INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr.Pのダンシングスシバー", "Mr.Pのダンシングスシバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「あふれる熱い涙」の田代廣孝の監督第２作。前作同様、“異国”における夫婦の絆にスポットを当て、繊細にして味わい深い人間ドラマに仕上げている。ロスで寿司屋をかまえる元ベトナム帰還兵の黒人男性と、彼の妻である日本人女性。苦楽をともにしてきた中年夫婦が、店の成功と引き換えに失ったもの、そして発見したものを見つめていく。戦争の悪夢から逃れられないでいる男の心情を託したタイトルの意味を明かすラスト・シーンも感慨深い。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO OF US", "TWO OF US", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "ロサンゼルスのLACCで映画製作を学んだ千葉誠治が、会話劇を意識して演出した16ミリ作品。数年前、留学という名目で恋人を置き去りにし、突然旅立った渋谷。帰国した彼が親戚の墓参りに訪れると、昔の恋人の友人だという女に出会う。渋谷に姿を消した本当の理由を教えろと迫る女。初めは煮え切らない答えを繰り返す渋谷だったが、女が自分の正体を明かした時、自分も本心を明かし始める。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Oh！マイブラザー　誰の心に届く… TWILIGHT FILE VII", "Oh！マイブラザー　誰の心に届く… TWILIGHT FILE VII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "オムニバスシリーズ「TWILIGHT FILE」第7弾の1作。無骨な刑事の風間亮と高校時代に傷害事件を起こして家を出た弟・隼は、父親の葬儀で10数年ぶりに再会するが、母親に罵倒された隼は再び姿を消してしまう。社会から脱落した隼は犯罪に手を染め、亮はそんな弟を刑事として追うことになる。元ボクサーで俳優、タレントとして幅広く活躍する赤井英和が主演。", "2010-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HAUNTING", "ホーンティング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "1963年のホラー映画「たたり」を、「スピード」のヤン・デ・ボン監督がリメイク。彼ならではの陽性のストーリー展開で、幽霊屋敷の恐怖の出来事を描いていく。不眠症に悩まされる女性ネルは睡眠障害研究の被験者に選ばれ、実験場の豪邸ヒル・ハウスへ。この不気味な屋敷は130年前に建てられたもので、不吉なウワサが耐えなかった。その夜から、３人の被験者と実験を仕切るマロー教授は怪事件に遭遇。とりわけ、霊感の強いネルは霊の存在を自覚する。やがて密室と化したヒル・ハウスで、恐ろしい常超現象が４人を襲う……。様々な建築様式を混合したヒル・ハウスの異様な造形や、この建物を生き物のように見せたILMによるSFX映像も見どころ。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Italianetz", "この道は母へとつづく", "", "", "http://image.eiga.k-img.com/images/movie/34216/315x210.jpg?1576", 99, "母を探すために孤児院を脱走したという少年の実話を基に製作され、05年ベルリン国際映画祭の少年映画部門グランプリを受賞したドラマ。ロシアの辺境の孤児院で育ち、イタリアの夫婦に引き取られることが決まった6歳のワーニャだが、ある日、先に養子にもらわれていった友達の本当の母親が孤児院に現れ、まだ見ぬ母への思いが募る。自分が養子にいった後に本当の母親が現れたらと不安になったワーニャは、孤児院を抜け出して母親を探す旅に出るが……。", "2007-10-27", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hellraiser: Deader", "ヘルレイザー　ワールド・オブ・ペイン", "", "", "http://ecx.images-amazon.com/images/I/31T201KKH3L._SL210_.jpg", 89, "", "2005-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A COUCH IN NEW YORK", "カウチ・イン・ニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "パリとニューヨークでアパートを交換した男女の恋を綴るラブ・コメディ。ニューヨークの精神分析医ヘンリーは、患者の愚痴を聞く毎日に嫌気がさし、期間限定でパリとのアパート交換の新聞広告を出した。それに応じたのが、パリで天真爛漫に生きるダンサーのベアトリスで、二人は顔を知らないまま住まいを交換。ニューヨークにやって来た彼女は、事情を知らない患者たちのカウンセリングをするはめになるが、意外なことに好評で、学会出席のため急遽帰国したヘンリーはこれに興味を抱く。ヘンリーは患者を装ってベアトリスに面会し、二人は互いに惹かれ合うが……。", "1996-01-01", "2,37,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EST−CE BIEN RAISONNABLE？", "ミュウ＝ミュウのスラップスティックはお好き？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「読書する女」で知られるミュウ・ミュウ主演のコメディ映画。囚人ジェラルドの裁判の日。彼はこの日を脱走するチャンスと心待ちにしていた。準備は整っており、脱獄は見事に成功した。ところがスクープをつかんで命を狙われるジャーナリストに判事と間違えられ、とんだ騒動に……。", "1981-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEW CENTURIONS", "センチュリアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "いわゆる警官ものとは少し趣の異なった作品。ロサンゼルス警察の外勤課のポリスマンの日常生活をリアリスティックに描いている。真面目一筋のベテラン警官に扮するG・C・スコットの渋い演技と、新人に扮したS・キーチらの手堅い芝居はなかなかの見ものである。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道〈やくざ〉血風録・無頼の紋章", "極道〈やくざ〉血風録・無頼の紋章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "暴力団、鉄心会の幹部、秋治はヤクザの息子ながら、カタギから傭兵となって極道の世界に入った変わりダネ。敵対組織が差し向けた愚連隊の凶暴な長、立花の罠によって鉄心会は窮地に追いこまれ、秋治はプライドを懸けて立花と１対１の決闘に挑む。男としての生きざまを貫くヤクザ者の死闘を、バイオレンスの醍醐味たっぷりに描いたアクション。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEDICINE MAN", "ザ・スタンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「ダイ・ハード」「レッド・オクトーバーを追え！」の２作でその力量を一躍、世界に知らしめたJ・マクティアナンがT・シュルマンのシナリオを得て、環境破壊問題をテーマに描くアドベンチャー・ロマン。アストン財団の援助のもとで、アマゾン奥地で極秘の研究に励む植物学者ロバート。本国との連絡を３年も絶っていた彼が突然、助手と機器を要求してきた。財団はその謎を解明すべく一人の女性科学者を送り込むが……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母三人", "母三人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "新派悲劇の代表作を映画化した母子もの。昭和15年の浅草。レビューのコーラスガール、時子は恋人、三郎が良家の令嬢と結婚することを知る。それでも彼の子、良太郎を産み育てる時子。彼女は良太郎を連れての旅の途中、空腹のため行き倒れたところを農民夫婦に助けられ、彼らに良太郎を預けるが……。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES VISITEURS", "おかしなおかしな訪問者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「グレート・ブルー」「ニキータ」等、一連のリュック・ベッソン作品の常連として、特異な存在感を見せつけてきたJ・レノが、12世紀から現代へとタイムスリップしてきた騎士を演じる爆笑コメディ。1123年のフランス。騎士のゴットフロワは、魔女の呪いによって、誤って婚約者の父親を殺してしまう。彼は、歴史を変えるために、魔術師に頼んでタイムトラベルを計画。だが手違いがあって、彼は現代へやって来る……。現代へ来たゴットフロワと、その従者ジャックイユは、さながらドン・キホーテとサンチョ・パンサ。ジャックイユ役のC・クラヴィエが、下卑た男を怪演している。なお、1998年には続編として、「ビジター」が作られた。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖星ゴラス", "妖星ゴラス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "重力が地球の6000倍の黒色妖星・ゴラスが地球に接近し、地球と衝突するまで、あと２年。道は二つ、“ゴラスの軌道を変えるか、地球が逃げるか”だ。人類は後者の道をとり、南極に巨大なジェット噴射口を作って、40万キロ地球を移動させようとするが……。壮大なストーリーを映画化したSF大作だ。この映画が製作された前年、ガガーリンを乗せた初の有人人工衛星が打ち上げられ、世は宇宙時代に突入。それを反映してか、物語もハードSFとして緻密に練りあげられている。東宝お得意の怪獣は、南極の基地建設を妨害するマグマが登場するが、全編のなかでは添え物といった感じで、この作品の見どころは、異常気象によって水没する世界の国々や、500坪にわたる壮大な南極セットなど、そのミニチュア特撮にある。“怪獣プロレス”ものと違い、日本では珍しい破天荒なストーリーとSFマインドを併せ持った特撮映画として、再評価が望まれる作品である。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Attack Force", "沈黙の激突", "", "", "http://image.eiga.k-img.com/images/movie/34306/315x210.jpg?3174", 94, "「沈黙」シリーズでおなじみのスティーブン・セガール主演によるアクション映画。麻薬組織の無差別攻撃で部下を失ったローソン司令官。組織を壊滅させることを決意した彼は、生化学者である恋人ティアの助けを借りて調査を開始する。そんな彼の前に、軍が極秘で開発した新薬CTXにより殺人マシーンと化した最強の敵が現われ……。「沈黙の奪還」でもセガールと組んだミヒャエル・ケウシュ監督がメガホンを取る。", "2007-11-10", "1,3,56", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("川中島合戦", "川中島合戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "日華事変に従軍し、中国戦線を視察してきた衣笠貞之助が、時の国策映画の風潮に乗って作り上げた時代劇大作。脚本に『分隊長の手記』で売り出した新進作家・棟田博を起用したのもそのあたりに事情がある。大河内伝次郎の武田信玄と、市川猿之助の上杉謙信との対決を核に、小者に扮した長谷川一夫と旅の女・山田五十鈴の恋までも描こうとしたが、仕上がりは散漫なものに終わった。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES ENFANTS DE LUMIE｀RE", "リュミエールの子供たち　フランス映画100年の夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "映画誕生100周年を記念して、映画の父リュミエール兄弟の母国であるフランスでこの100年間に作られた作品を集めて、編集した作品。1895年のリュミエール兄弟「リュミエール工場の出口」から1994年のリュック・ベッソン「レオン」、クシシュトフ・キェシロフスキ「トリコロール」３部作まで307作品が、“歌”“ギャグ”“キス”などのテーマに分けて登場する。フランス映画史上の傑作がほぼ網羅され、フランス映画ファンならずとも必見と言える作品。J・ペランの企画製作のもと、９人の監督が２年以上の時間をかけて製作。全編にわたって流れる音楽を担当したのは、これもまたフランスを代表する作曲家であるM・ルグラン。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIDIER", "ディディエ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「カンヌ映画祭殺人事件」に主演したフランスの人気お笑いトリオ、レ・ニュルの一員A・シャバが脚本、主演も兼ねて監督デビューしたコメディ。サッカー・チームのマネージャー、ピエールが友人から預かった犬が、ある日突然人間に変身。抜群の運動能力を発揮して、サッカー選手として大活躍するが……。言葉もうまくしゃべれず、やたら人なつこい主人公を、シャバが愛きょうたっぷりに好演。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE FEMME FIDE｀LE", "華麗な関係", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ラクロの恋愛心理小説の傑作『危険な関係』を、R・ヴァディムが映画化。主演は「エマニエル夫人」で世界中の男たちを魅了したS・クリステル。19世紀初頭、社交界でドン・ファンの異名をとるシャルル伯爵は、伯母の別荘に身を寄せていた。この別荘で彼は、美しい人妻に心を奪われるが、昔なじみのフローラ伯爵夫人が二人の仲をねたみ罠を仕掛ける……。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEX　THE　ANNABEL　CHONG　STORY", "SEX／アナベル・チョンのこと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "1995年１月19日に、ポルノ女優のアナベル・チョンは10時間で251人の男性とセックスをするという記録を樹立する。一躍ポップ・カルチャーのスターとなった彼女の記録への挑戦と、大学に通いながらポルノの世界に身を投じた背景に迫る異色のドキュメンタリー。", "1999-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼火", "鬼火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「悲しきヒットマン」「シャブ極道」の原作者としても知られる、山之内幸夫の原案をもとにしたハードボイルドな人間ドラマ。かつて“火の玉”と異名をとり恐れられていたすご腕のヒットマン国広が、５年の刑期を終え出所する。弟分の谷川は極道への復帰を切望するが、国広は堅気になる決意を固めていた。ある日、高級クラブのピアニスト麻子に殺したい人がいると打ち明けられた彼は、心に傷を負った彼女に安らぎを見い出していくが……。極道の世界でしか生きられない男の悲しい性を、原田芳雄が哀感をにじませて好演。彼と恋に落ちるヒロインを演じる、片岡礼子のみずみずしい魅力も印象的だ。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FIVE HEARTBEATS", "ファイブ・ハートビーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "「ハリウッド夢工場　オスカーを狙え!! 」で製作・監督・脚本・主演を務め“ポスト・エディ・マーフィ”として注目を浴びたR・タウンゼントが、1960年代モータウン全盛期を舞台に作った音楽映画。スラム出身の５人の黒人青年が同じ夢を追い、音楽にすべてを賭ける。ある日舞台上での思いがけないアクシデントが観客に受け、一躍スターに踊り出た５人だったが、人気とは裏腹に悪の世界へも溺れていく。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAPANTSULA", "マパンツラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "“マパンツラ”とはズールー語で“チンピラ”の意味をさす。この作品は南アフリカのソウェト（黒人居住区）に住むマパンツラたちの実態を通して、製作当時の南アフリカの姿を描いたもの。政府の検閲を逃れるためにB級ギャング映画として製作されたが、1990年７月ようやく本国での上映が許可された作品だ。", "1988-01-01", "67,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORNING GLORY", "勝利の朝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "K・ヘプバーンの二作目の主演映画で、舞台女優志望の芝居好きの女性を演じる。あらゆる努力を惜しまない個性あふれる人物像を彼女は見事に演じきる。素晴らしい脚本と厚みのあるキャスティングが魅力。スーザン・ストラスバーグ主演の「女優志願」（1958年、シドニー・ルメット監督）はこの作品の再映画化。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GHOST IN THE SHELL 攻殻機動隊", "GHOST IN THE SHELL 攻殻機動隊", "", "", "http://ecx.images-amazon.com/images/I/51x2WIVXaCL._SL210_.jpg", 80, "士郎正宗のSFコミック「攻殻機動隊」を押井守監督がアニメーション映画化。西暦2029年、高度に発達したネットワーク社会において多発するコンピューター犯罪、サイバーテロなどに対抗するため結成された非公認の超法規特殊部隊「公安9課」（通称「攻殻機動隊」）の活躍を描く。ある日、某国情報筋から、国際手配中の凄腕ハッカー・通称「人形使い」が日本に現れるとの情報が9課に寄せられる。隊長の草薙素子と9課の面々は人形使いの痕跡を追うが……。全米ビルボード誌のビデオチャートで週間1位を獲得するなど海外でも人気が高く、押井守の名を一躍世界に広めた代表作。ウォシャウスキー兄弟の「マトリックス」（19999）など、後のハリウッドSF大作へも影響を与えたとされる。", "1995-11-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DES MORTE", "ジ・エンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "メキシコ、ブラジル、アメリカ、イタリア、韓国、タイ、ネパールなど世界14ヵ国に一大ロケーションを敢行し、“死体処理場”“フェルナンデスの大手術”“恐怖のミイラ”“メキシコ闘牛場の惨事”など、顔をそむけたくなるような“死の儀式”のシーンで構成したショック・ドキュメンタリー。", "1981-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLINDFOLD", "目かくし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「わが谷は緑なりき」などの脚本家としても知られるP・ダン監督が描いたロマンティック・コメディ。国家保安局より理論物理学者の治療を秘密裏に依頼された精神分析医のスノーは、兄の容体を知ろうと彼に近づいて来た理論物理学者の妹といつしか恋に落ちるが、そこへ敵側のスパイが現れ、すったもんだの大騒動へと発展していく……。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("881", "881 歌え！パパイヤ", "", "", "http://image.eiga.k-img.com/images/movie/53706/315x210.jpg?3659", 109, "シンガポール伝統のステージパフォーマンス「ゲータイ（歌台）」。伝説的ゲータイ・シンガー、チェン・ジン・ランに憧れるリトル・パパイヤとビッグ・パパイヤは、ゲータイの歌手になることを夢見て、パパイヤ・シスターズと名乗り活動を始めるが……。2007年シンガポールのアカデミー賞外国語映画賞代表作品。監督は2004年「TIME」誌アジア版の「アジアのヒーロー20人」にも選ばれた鬼才ロイストン・タン。", "2008-08-09", "54", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Step Up", "ステップ・アップ", "", "", "http://image.eiga.k-img.com/images/movie/33886/315x210.jpg?1987", 100, "ダンスに打ち込む若者たちの恋や友情を描き、アメリカで大ヒットを記録した青春ダンス・ムービー。貧しい家庭で育ち、夢のない生活を送っていた高校生タイラーは、ある事件をきっかけに有名な芸術学校に通うバレエ・ダンサーのノーラと出会う。彼女のパートナーが負傷したことから、タイラーはその代役としてダンスを学ぶことになるが……。監督は「チアーズ！」「ブギーナイツ」などの振り付けを担当したアン・フレッチャー。", "2007-03-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Troy", "トロイ", "", "", "http://image.eiga.k-img.com/images/movie/1206/315x210.jpg?1932", 163, "トロイの王子パリスが、スパルタ王の妻ヘレンと恋に落ちて彼女を略奪。スパルタ王の兄アガメムノンは、それを口実にトロイを征服するべく、ギリシャ諸国の戦士からなる大軍隊を率いてトロイに向かう。その戦の中でギリシャ軍最強の戦士アキレスは、パリスの兄ヘクトル王子と対決することになる。古代ギリシャの詩人ホメロスの叙事詩「イリアス」を「U・ボート」のウォルフガング・ペーターゼン監督が映画化。", "2004-05-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四万十川", "四万十川", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "笹山久三原作の同名小説の中から第１部『あつよしの夏』と第２部『とおいわかれの日々に』の部分を、恩地日出夫が映画化。昭和30年代前半の高知県。自然と戦いながら四万十川流域に住む人々や家族の姿を通して、主人公の少年・篤義の成長を温かく描いた。樋口可南子が力強く生きる母親役を演じている。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("群龍戯鳳／PEDICAB DRIVER", "ペディキャブ・ドライバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "車夫たちが巻き起こす恋やケンカの騒動を描いた痛快アクション。自転車で人力車を引く車夫（ペディキャブ・ドライバー）たちが、庶民の足として活躍していた1940年代のマカオ。車夫の親分タンは、仲間から持ち込まれるもめごとの解決や恋の手助けに大忙しの毎日を送っていた。そんなある日、友人のフォンが恋したピンをギャングの親玉ヤオから守ってやり、ピンとフォンはカップルに。一方、弟分マーの恋人シウチョイがヤオの経営する娼館の娼婦だったことから、再びタンはヤオと対決することになるが……。ビデオタイトルは「帰って来たデブゴン／昇竜拳」。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LO STRANGOLATORE DI VIENNA", "ザ・ブッチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1930年、いにしえのウィーン。精神病院から退院したオットーは肉屋を再開するが、口論から殺してしまった妻の死体処理に困り果て、ソーセージにして売ったら大当たり。どうせなら古女房より、若い娘のお肉がうまかろうと……。イタリアの艶笑グルメ的ホラー・コメディ。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INVENTING　THE　ABBOTTS", "秘密の絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1957年、イリノイ州ヘーリーの町。富豪アボット家の３人姉妹はいずれも美人と評判で、貧しい家庭に育ったホルト家の次男ダグは、純朴なアボット家の三女パメラに思いを寄せていた。一方、ダグの兄ジェーシーは、セクシーな次女エレノアと関係を持っている。またジェーシーは、自分の家が貧しいのはアボット家のせいだという屈折した敵意を秘めており、そのことで彼とダグは対立することになる。イギリス人監督P・オコナーが手掛けた青春ロマンス。ハリウッド期待の若手スターが華々しく共演した作品だが、主人公の兄弟らの鬱屈した感情が反映されたトーンにはハリウッドらしからぬ陰りがにじみ、それが本作をいっそう魅力的にしている。ダグ役のJ・フェニックスのナイーブな個性も印象的。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラFINAL　WARS", "ゴジラFINAL　WARS", "", "", "http://ecx.images-amazon.com/images/I/313RG7933BL._SL210_.jpg", 125, "日本のみならず世界を魅了し続けてきた、大怪獣“ゴジラ”。そのシリーズ50年の歴史の幕引きを請け負ったのは、「あずみ」の北村龍平監督。豪華なキャストと精鋭スタッフが集結し、過去にリスペクトを捧げながらも、まったく新しいパワーがみなぎる作品になった。全編が見せ場といえる、有無を言わせぬアクション・シーンと怪獣たちの壮絶なバトルがとにかく圧巻だ。20XX年、X星人と名乗る異星人が友好を求め、地球に出現。だが彼らの目的は地球の征服だった。圧倒的なX星人の軍事力になす術もない地球防衛軍は、最終手段としてゴジラを蘇らせる。", "2004-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COUSIN　BETTE", "従妹ベット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "フランスの文豪バルザックの大作『人間喜劇』の一部を映画化。19世紀のフランスを舞台に、愛欲と金の絡んだ複雑な人間模様と復讐の物語がブラックなユーモアで描かれる。貴族ヒューロット家のベットは、思いを寄せていたヘクターを従姉のアドリーヌにとられ、おまけに貧乏画家の年下の恋人ウェンセスラスまでも彼女の娘に奪われてしまう。逆上した彼女は、ヘクターのかつての愛人である歌姫ジェニーを仲間に引き入れ、ウェンセスラスを誘惑させることに成功。さらに、娘に言い寄っていた市長を巧みに操り、自分を裏切った者たちへの復讐を着々と進めていく……。残酷な恋愛ゲームを仕掛けるヒロインを、J・ラングが熱演。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子連れ狼・冥府魔道", "子連れ狼・冥府魔道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "“子連れ狼”拝一刀は、筑前黒田藩より刺客の依頼を受ける。黒田藩の存続にかかわる重大な秘密を知った公儀探索方隠密が、事実を暴く密書を持って江戸へ向かったという。一刀の仕事は、隠密を殺し密書を奪い返すことであった。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADVENTURES OF PINOCCHIO", "ピノキオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "古典的名作を、「ベイブ」のSFXスタッフがアニマトロニクスを駆使して実写化したファンタジー。あやつり人形ピノキオが繰り広げる冒険を通して、家族の真の意味を問う。“若さ”を忘れた人形作りの名人ゼペットは、退屈な日々を送っていたが、ある時、魔法の谷で見つけた丸太で最高傑作の人形ピノキオを作り出す。ピノキオはあやつり糸なしで歩き、話しもでき、木でできている以外は人間の子供と変わらなかった。そんなピノキオに金もうけをたくらむロレンジーニが目をつけたことから、ピノキオはゼペットの元を離れ、とてつもない冒険に巻き込まれていく……。人形から人間に変身するラストシーンは感動的。人形製作をジム・ヘンソンズ・クリエーチャー・ショップが担当している。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gappie！ ぼくらはこの夏　　ネクタイをする！", "Gappie！ ぼくらはこの夏　　ネクタイをする！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "一流会社のOLに一目惚れした落ちこぼれ大学４年生が、その会社に就職すべく一大作戦を開始する青春サクセス・ストーリー。諸々の作戦に誇張の多いコミカルな展開ながらも、主人公の真摯で純粋な姿勢がリアルな共感を生む、魅力的な快作に仕上がっている。「がんばっていきまっしょい」「雨鱒の川」の磯村一路監督、初の一般映画。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABY’S DAY OUT", "赤ちゃんのおでかけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「ホーム・アローン」で子供を主役にしたJ・ヒューズが、赤ちゃんを主人公に選んだコメディ。ベイビー・ビンクは生後11ケ月。大富豪コットウェル家に誕生し、パパとママのあふれる愛情に包まれ、もうすぐ１歳の誕生日を迎えようという時に誘拐されてしまう。犯人はドジな３人組。中の一人がうたた寝しているすきに、ビンクは大都会シカゴの街中へ文字通りはい出してしまう。赤信号の横断歩道、ゴリラの檻の中、超高層ビルの建築現場……危険な場面もニコニコ笑ってくぐり抜けるビンクにとって、大都会は巨大な遊園地。FBIも巻き込んで、赤ちゃんの冒険が続く。ビデオ発売時タイトル、DVDタイトルは「ベイビーズ・デイアウト／赤ちゃんのおでかけ」。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MISSOURI BREAKS", "ミズーリ・ブレイク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "西部の開拓末期のモンタナを舞台に、馬泥棒の一群、牧場王国の独裁者、それに雇われたガンファイターたちの生きざまを描く。ブランドとニコルソンという二大スターの文字通りの激突が見もの。ひねりの効いた演出が光る異色西部劇。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤と黒の熱情 Passion", "赤と黒の熱情 Passion", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "組の資金を強奪した弟分、矢崎を組の命令で殺したヤクザ松浦。兄の死で廃人同様になり記憶喪失になった妹、沙織。自らの罪を償うべく松浦は、彼女のために美しい思い出作りごっこを始めるのだった……。「泣きぼくろ」に続き、アウトローの人生を工藤栄一がホロ苦いタッチで描く。悪役に扮した古尾谷雅人が怪演。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DAY THE WORLD ENDED", "原子怪獣と裸女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "B級映画の神様R・コーマンによる破滅ものSFの古典。核戦争後、奇跡的に７人の男女が生き残った。谷間に集まって共同生活をする彼らを、放射能で突然変異した怪物が襲い始める……。低予算ながら、過去のSF映画のアイデアを効果的に借用してまとめあげる手腕は、まさにコーマンならでは。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠山の金さん捕物控　影に居た男", "遠山の金さん捕物控　影に居た男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "江戸・本所横綱町の勘兵衛長屋の家主・勘兵衛が何者かに殺される。勘兵衛は長屋中に憎まれており、疑わしい連中が多すぎて捜査にならない。目明かし・八五郎は遊び人の文吉が怪しいと踏み、奉行所に長屋連中全員を出頭させたところ、奉行姿の文吉＝南町奉行・遠山左衛門尉が現れる。同じマキノ雅広監督の佳作「昨日消えた男」のリメイク版だが、推理劇としての面白さもある時代劇。。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Star Wars: Episode II - Attack of the Clones", "スター・ウォーズ　エピソード2 クローンの攻撃", "", "", "http://image.eiga.k-img.com/images/movie/1186/315x210.jpg?2555", 142, "ジョージ・ルーカスが壮大なスケールで描くSFシリーズ「スター・ウォーズ」新3部作の第2部。前作から10年後。数百もの惑星に広がった分離主義運動によって共和国が混乱に陥る中、パドメ・アミダラ元老院議員の暗殺未遂事件が発生。オビ＝ワンのもとで修行を積んだアナキン・スカイウォーカーがアミダラの護衛を任されるが、2人はやがて禁断の恋に落ちていく。後にダース・ベイダーへと転じるアナキン役は、「海辺の家」でも注目を集めた若手ヘイデン・クリステンセンを起用。", "2002-07-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使の火遊び", "天使の火遊び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ウサギは東京の24番目の区“桃宿”でミニFM局のDJをしている女の子。ある日、“ここではないどこか”へ連れて行ってくれる白いタクシーが走っているという噂が街に流れた。そして友達は次々にそのタクシーに乗り、違う世界へと旅立ってしまう。デジタルカメラの映像をパソコンで加工したフルデジタル合成ファンタジー。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Penny Pinchers", "ちりも積もればロマンス", "", "", "http://image.eiga.k-img.com/images/movie/57946/315x210.jpg?3560", 114, "", "2012-05-12", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Overboard", "潮風のいたずら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "金持ちで傲慢なある夫人がヨットから転落し、記憶喪失に陥った。彼女に嫌な思いをさせられた大工の男は、復讐とばかりに自分が夫だと偽って彼女を家に引き取る。女は疑問を抱きながらも家族の面倒を見ていくが……。記憶喪失をきっかけに性格美人に変わっていく女と大工一家の交流を描いたマーシャル監督の職人技が冴えたコメディ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEPPING RAZOR—RED X", "ステッピング・レイザー ～レッドX～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "“ステッピング・レイザー（歩くカミソリ）”と呼ばれ、過激なメッセージをレゲエのビートにのせて歌い続けたP・トッシュ。1987年、ジャマイカの自宅で、３人の強盗(？)に射殺された、彼の死の謎に迫るドキュメンタリー。死後に発見された、トッシュが“自叙伝”を吹き込んでいたマイクロ・カセットテープを手がかりに、様々な関係者の証言によってトッシュの素顔を浮き彫りにする。監督は「裸のランチ」の俳優としても知られるN・キャンベル。", "1992-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("敗家仔／THE PRODIGAL SON", "ユン・ピョウin ドラ息子カンフー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ジャッキー・チェンに代表される一連の香港カンフー・アクションの一つ。ブルース・リーの「死亡遊戯」（1978）でアクション場面の替え玉を演じ、日本映画「落陽」への出演で話題となったユン・ピョウ20代の主演作。Y・ピョウ演じるチャンは名門ワン家のドラ息子。父親の命令でカンフーの家庭教師やケンカ相手がわざと負けているとも知らずに本人は名人気取り。ところがカンフーの達人ルンに出会い、自分の弱さに気付いたチャンは、ルンを追いかける。", "1981-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sharkwater", "シャークウォーター　神秘なる海の世界", "", "", "http://image.eiga.k-img.com/images/movie/53656/315x210.jpg?4310", 89, "人々から恐れられるサメの真実の姿に迫ったドキュメンタリー。8歳の時にサメのパワフルな姿に魅せられて以来研究を続けてきたロブ・スチュワートが、自らカメラを手にしてサメが生息する世界各地の海に潜り、その生態を美しい映像で撮りあげた。さらにフカヒレのために密漁が後を絶たず過去50年間でその数が9割も減少したという厳しい現実を明かし、海の生態系の中心であるサメが絶滅することの危険性を訴えかける。", "2008-06-28", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANGMEN ALSO DIE", "死刑執行人もまた死す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "物語の舞台は戦時下のチェコ、プラハ。“死刑執行人”と異名をとるナチスの司令官ハイドリッヒが、ある日暗殺された。ナチスは、犯人捜しのために無差別報復を始める……。自らもナチスを逃れて亡命した経験をもつ、巨匠F・ラングのアメリカ時代の幻の傑作。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Iris", "アイリス", "", "", "http://image.eiga.k-img.com/images/movie/1456/315x210.jpg?1640", 91, "アカデミー賞助演男優賞をジム・ブロードベントが受賞、女優賞2賞にノミネート（主演ジュディ・デンチ、助演ケイト・ウィンスレット）されたドラマ。イギリスの小説家アイリス・マードックとその夫ジョンの40年間に渡る日々を、50年代のオックスフォード大学生時代から、アルツハイマーに冒された晩年までを描く。監督、共同脚本は、英国演劇界の重鎮、リチャード・エア。彼の母親も同じ病気を患っていた経験を持つ。", "2002-12-07", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("脱獄広島殺人囚", "脱獄広島殺人囚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "敗戦直後の混乱期、植田正之は仲間と共謀、闇屋と女を殺してモルヒネを強奪、逮捕され投獄されたもののすぐに脱獄。以後、脱獄入獄を繰り返し刑期は合計41年７ヵ月までふくれあがった。それでもなお脱獄にエネルギーのすべてを賭ける植田。執拗なまでの男の自由への戦いぶりを描いた痛快脱獄アクション。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剃頭匠", "胡同の理髪師", "", "", "http://image.eiga.k-img.com/images/movie/53326/315x210.jpg?2782", 105, "中国・北京の旧城内に散らばる細い路地・胡同（フートン）。これまで古き良き街並みを保ち続けてきた当地にも、北京オリンピックを機についに近代化の波が押し寄せようとしていた。変わりゆく時代の中で、昔ながらの生活を貫こうとする93歳の現役理髪師チンさんと、彼を取り巻く老人たちの日常をドキュメンタリー・タッチで綴った人間ドラマ。実在するチンさん本人が主演を務め、実生活をほぼそのままに映し出していく。", "2008-02-09", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNCLE BUCK", "おじさんに気をつけろ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "J・ヒューズ監督とJ・キャンディが「大災難P.T.A.」に次いで再びコンビを組んだコメディ。親戚の子供たちの世話をすることになった40歳にして独身男のおじさんが子供相手に奮闘する。J・キャンディが図々しいけれど何故か憎めない男を、またまた快演。そのキャンディ相手にM・カルキンも名演技を見せる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("疵", "疵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "昭和30年代の渋谷。生涯一度も銃や刃物を身につけず、常に相手を素手で叩きのめし、ヤクザの間で“死神”と恐れられた男・花形敬。33歳で散った、この破天荒なヤクザの生涯を、「ちょうちん」「極道〈やくざ〉渡世の素敵な面々」で、1980年代のやくざスターに踊り出た陣内孝則主演で描く。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La luna", "ルナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "　近親相姦を感じさせる、過剰なまでの愛情を息子に注ぐオペラ歌手の母親と不安定な思春期をおくる息子。ニューヨークとイタリアを舞台に、義理の父の死や実父との再会を通して親子の葛藤が描かれる。原題にあたる「月」が、劇中シンボリックな存在としてたびたび映し出される。撮影はベルナルド・ベルトルッチ作品には欠かせない、名カメラマン、ビットリオ・ストラーロ。脚本のジュゼッペ・ベルトルッチはベルナルドの弟。", "1980-04-12", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("七夜待", "七夜待", "", "", "http://image.eiga.k-img.com/images/movie/53646/315x210.jpg?2691", 90, "「殯の森」で第60回カンヌ国際映画祭審査員特別大賞を受賞した河瀬直美が、初めて故郷の奈良以外を舞台にした作品。長谷川京子が映画単独初主演を飾る。30歳を過ぎた彩子は、人生のリセットを求めてタイへと旅立つが、ホテルへ行くはずのタクシーがたどり着いたのは鬱蒼とした森だった。そこで暮らすタイ人の母子やフランス人の青年と出会い、タイ古式マッサージで戸惑いや苛立ちを沈めた彩子は、見知らぬ人々と7つの夜を過ごすことで新しい自分に出会う。", "2008-11-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まぼろし天狗", "まぼろし天狗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“やみのごぜん”と言い残して息絶えた女の謎を追ううちに、何者かに銃で襲われ、旗本・浅川喬之助にかくまわれた町奉行与力・守屋周馬。喬之助は周馬と自分が瓜二つなのを利用し、彼の代わりとなって江戸の大掃除をすることになるが……。美剣士スター・大川橋蔵が一人二役を演じるアクション時代劇。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MOLLY MAGUIRES", "男の闘い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "S・コネリーが、「007」シリーズに続いて出演した作品。舞台は1876年のアメリカ、資本家が権力をふるうペンシルバニアの無煙炭鉱。苛酷な労働条件に耐えてきた炭坑夫たちは資本家に対抗するため、アイルランド系移民の秘密結社“モリー・マガイア”を結成する。リーダー、ジャック・キーオと資本家側のスパイとして結社に潜入した男ジェームズを中心に、男たちの友情、裏切り、激烈な闘いを描く。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彼岸花", "彼岸花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "小津安二郎が手掛けた初めてのカラー作品。田中絹代・有馬稲子・山本富士子の豪華な女優陣を配して、相変わらずの小津調ホーム・ドラマで楽しめる。がんこな父・佐分利信は、父に相談なしで結婚相手を決めてしまったことに腹をたて、娘の結婚を許さないと言いだす。一方、知人の娘から自由な恋愛結婚についての意見を求められ賛成してしまう。そんな矛盾だらけでありながら、自分の娘のことになると冷静になれない父親像がおかしく、その妻・田中絹代が夫と娘を和解させようとひと苦労。里見惇の原作を小津安二郎・野田高梧の名脚本コンビが脚色した佳作である。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STREET WITH NO NAME", "情無用の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ギャング組織にFBIが潜入捜査した実話に基づく、ドキュメンタリー・スタイルのドラマ。後の潜入捜査映画にも影響を与えた一作だ。刑事側以上にR・ウィドマークの悪役ぶりが光る。第一次大戦後のセンターシティでは、銃撃殺人事件が次々に発生していた。同一の銃弾と見たFBIは、ギャング集団に二人の刑事を送り込む。一人はボスの部下になり、組織の秘密を知ることに……。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POPIO´L I DIAMONT", "灰とダイヤモンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "アンジェイエフスキの同名小説の映画化で、「世代」「地下水道」とともにワイダのレジスタンス３部作を構成している。戦争中は対独レジスタンス運動に青春を燃焼させ、戦後はテロリストとなって悲惨な末路を遂げる青年の姿を描いた傑作。1945年、ワルシャワ反乱の生き残りで反ソビエト派のテロリスト、マチェクはソビエトから来た共産党地区委員長、シュツーカ暗殺の指令を受ける。だが、マチェクは人違いで二人の男を殺してしまう。シュツーカの息子は反ソビエト反乱者として逮捕され、銃殺の判決を受けていた。マチェクは夜、息子に会いに行こうとするシュツーカの暗殺に成功し、その晩、ホテルでクリスティナという女と一夜をともにする。翌朝、マチェクは町を発とうとするが、衛兵に発見されて撃たれ、ゴミ捨て場で悶え死ぬ……。長い間、地下水道に潜んでいたために目を痛めたというマチェクの黒眼鏡が印象的である。", "1958-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANOTHER STAKEOUT", "張り込みプラス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "スマッシュ・ヒットとなったコミカルな刑事ドラマ「張り込み」の続編。組織犯罪の目撃者を保護する任務を負ったおとぼけ刑事コンビ。彼らの間にパワフルな女性検事補が加わり、家族を装った珍妙な張り込みが始まった……。「プリティ・リーグ」でも活躍したコメディエンヌ、R・オドネルの参加でお笑い度もアップ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("甲野善紀身体操作術", "甲野善紀身体操作術", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "人の身体の仕組みに逆らわないことで負担を極力減らすという独自の身体操作術を追求する武術研究者・甲野善紀を追ったドキュメンタリー。桑田真澄投手や宇宙飛行士の野口聡一氏への指導でも知られる甲野の身体操作術は、楽器演奏や演劇、介護など様々なジャンルに取り入れられている。ドキュメンタリー作家・藤井謙二郎が、甲野本人へのインタビューや甲野の身体操作術を支持する人々の証言を通して、その魅力に迫る。", "2006-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("It's a Wonderful Life", "素晴らしき哉、人生！", "", "", "http://ecx.images-amazon.com/images/I/51F9XV-XljL._SL210_.jpg", 130, "名匠フランク・キャプラ監督がジェームズ・スチュワート主演で贈る、アメリカの良心を描いたメルヘンチックなヒューマンドラマの傑作。子どもの頃からずっとツキに見放されてきた主人公ジョージは、それでも希望を捨てず、家族や町の人々に囲まれながら幸せな日々を送っていた。ところがクリスマスの日、彼は人生最大のピンチに追い込まれる。絶望した彼はついに自殺を図ろうとするが、そこに見習い天使と名乗る男が現れ……。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プリンセス　トヨトミ", "プリンセス　トヨトミ", "", "", "http://image.eiga.k-img.com/images/movie/55456/315x210.jpg?2907", 119, "「鴨川ホルモー」「鹿男あをによし」で知られる人気作家・万城目学の直木賞候補になったベストセラーを映画化。1615年の大阪夏の陣で断絶したはずの豊臣家の末裔（まつえい）が今も生きつづけ、大阪の男たちは400年もの間その秘密を守り続けていた。国家予算が正しく使われているかを調査する会計検査院の精鋭3人は、ふとしたことからその真実を知ってしまい、大阪の公共機関や商業活動など、あらゆる機能が停止する一大事件に巻き込まれていく。", "2011-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地球が動いた日", "地球が動いた日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "1995年１月17日、平和な日本を震撼させた阪神淡路大震災。その惨状の悲しみから希望を見い出していく子供たちの成長を感動的に描いた長編アニメーション。一流中学校を目指す小学６年生のツヨシは、勉強に忙しい毎日を送っていた。しかし１月17日未明、大地震がツヨシの住む神戸市を襲った。倒壊寸前のマンションから命からがら抜け出したツヨシ一家は、小学校の体育館に避難することになるのだが……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("世界最後の日々", "世界最後の日々", "", "", "http://image.eiga.k-img.com/images/movie/57786/315x210.jpg?2800", 96, "「BLUE」「ビリーバーズ」など大胆な性描写や暴力、社会問題を描いた問題作を発表し続けている漫画家・山本直樹の同名短編コミックを実写映画化。授業中に居眠りをしていた高校生・嘉納は、夢に現れた小さな「カミサマ」に世界滅亡を予言される。世界が滅ぶ前にやりたいことをやろうと、あこがれの学園のマドンナ、ユミとのセックスを目論んだ嘉納は、ユミの彼氏を撲殺。そのままユミを誘拐して盗んだ車で旅に出る。監督は「地球でたったふたり」の内田英治。", "2012-02-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミニミニ突撃隊", "ミニミニ突撃隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "“西野バレエ団五人娘”と呼ばれ人気を博した金井・由美・原田・奈美・江美が揃って共演した、はつらつ青春ストーリー。寄宿生活を送る女子大生５人組が舎監の目を盗んでゴーゴー喫茶へボーイハントに出かけたり、宝石強盗の一味を一網打尽にしたり。ちょっと危険な大活躍をするという他愛のないお話に、健康的なお色気が振りまかれる。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIDE OUT", "ビーチバレーに賭けた夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ビーチバレーを題材にした、恋と友情の青春ドラマ。大学生のモンローは、授業料を稼ぐためにアルバイトをしようとカリフォルニアにやって来る。しかし、仕事がうまくいかずにビーチをぶらついていると陽気な若者ウィリーに声をかけられ、ビーチバレーの賞金マッチに出場することに。初試合は惨敗となったが、対戦相手にほめ言葉をもらった二人はその気になって特訓を開始する。ビデオタイトルは「サイドアウト　ビーチバレーに賭けた夏」。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAY", "STAY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "人気漫画家・西炯子の「STAY ああ今年の夏も何もなかったわ」を実写映画化した青春学園ドラマ。美しい自然の広がる鹿児島県を舞台に、5人の女子高生たちが過ごすひと夏のエピソードをみずみずしいタッチで綴る。県立川中高校の演劇部で部長を務めるよーぴんは、母子家庭の長女として小さな弟妹の世話を焼いているしっかり者。そんなよーぴんに恋心を抱く顧問の松木は、よーぴんの家庭が抱えている複雑な事情を知る。", "2007-09-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Histoire(s) du cinema", "映画史", "", "", "http://image.eiga.k-img.com/images/movie/1836/315x210.jpg?2925", 268, "ジャン＝リュック・ゴダールが、10年以上の歳月をかけて作り上げた、全8章、のべ4時間半にわたる「映画の歴史」に関する考察。古今東西の無数の映画フィルムを集めてビデオに変換し、膨大な数の美術や文学、音楽からの引用をまじえてモンタージュしたデジタル作品。", "2000-05-13", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くろねこルーシー", "くろねこルーシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心魔", "心の魔", "", "", "http://image.eiga.k-img.com/images/movie/54976/315x210.jpg?4605", 93, "マレーシア映画界の注目株ホー・ユーハン監督が、同国で実際に起きた事件にヒントを得て映画化したサスペンス・ドラマ。母親と2人きりで暮らしてきた23歳の青年タッチャイは、15歳の女子高生インと付き合っている。ある日、2人の交際を知ったインの両親は、未成年と性交渉した罪でタッチャイを訴えると騒ぎだす。息子を愛するタッチャイの母は苦労して示談金を支払うが、それでもインの両親の怒りは収まらず……。", "2009-10-20", "50,6,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA RE｀GLE DU JEU", "ゲームの規則", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "巨匠J・ルノワールがミュッセの戯曲『マリアンヌの気まぐれ』に想を得て脚本を書き、自ら出演した恋愛喜劇の傑作。ラ・シュネイ家のパーティー。クリスチーヌは当主ロベールの妻だが、飛行家アンドレと愛人関係にあった。ロベールにもジュヌヴィエーヴという愛人がいたが、今は別れようと思っている。パーティーでクリスチーヌはド・サン・トーバンと親しくしたため、アンドレは嫉妬に狂う。一方、密猟監視人シュマシェールは使用人のマルソーが妻リゼットに色目を使ったため、彼を追い回す……。多くの人々が入り乱れる恋愛騒動の中に、ブルジョワ社会を風刺したラブ・コメディ。1959年に完全版に復元された。", "1939-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社長道中記", "社長道中記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "太陽食料社長・三沢英之助が新製品開発のため大阪へ出張することになり、女好き社長の浮気を封じ込めるためマジメ社員・桑原武が随行することになった。だがそこは社長のこと、浮気の虫がさっそくムズムズ……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEVERLY HILLBILLIES", "ビバリー・ヒルビリーズ じゃじゃ馬億万長者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1950年代の人気TV番組『じゃじゃ馬億万長者』の映画版。石油を掘り当てて大富豪になった、全員そろって変人の田舎者クランペット一家が、高級住宅街ビバリーヒルズに転居。彼らがそこで巻き起こす騒動を描くコメディ。やもめの父ジェドが、女結婚詐欺師に引っかかって財産を奪われそうになるハラハラドキドキのクライマックスは見もの。D・コールマンとL・トムリンほか、芸達者な脇役も映画好きには見逃せない。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋するイノセントマン", "恋するイノセントマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "舞台俳優のいずみよしはるが主演・監督・脚本を務めるナンセンス・ラブコメディ。何の目的も持たずに上京し、いつしか家に引きこもるようになった中年男・操。ある日、彼は盲目の女性・志乃と出会い、彼女に恋心を抱く。操の献身的な愛に志乃も応えようとするが……。一青窈の実姉で舞台女優として活躍中の一青妙がヒロインに、また「タナカヒロシのすべて」「けものがれ、俺らの猿と」で個性的な演技を披露した鳥肌実が主人公の父親に扮する。", "2006-11-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私の調教日記", "私の調教日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "障害を抱える観客が官能映画を映画館で楽しめるよう、女性による副音声や日本語字幕を付けたバリアフリー仕様の映画を製作・上映する「エロバリ」プロジェクトの第2作。恋人の吾郎との約束で“調教”を受けることになった銀杏は、怪しい洋館に連れていかれ、痩せぎすの女や赤い目出し帽をかぶった男たちに身を任せる。さまざまな辱めを受けるうちに、銀杏は次第に別の感情に目覚めていく……。", "2010-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hop", "イースターラビットのキャンディ工場", "", "", "http://image.eiga.k-img.com/images/movie/56106/315x210.jpg?4771", 95, "「怪盗グルーの月泥棒 3D」を成功させたプロデューサーのクリス・メレダンドリが、同作に続き米ユニバーサル・スタジオとタッグを組んだファミリーアドベンチャー。世界中の子どもたちのためにキャンディを作っているイースター島のウサギの王子イービーは、ミュージシャンになりたいと王国を飛び出しハリウッドへ。国王は部下のウサギたちにイービーを連れ戻すよう命令を下す。しかし、その混乱に乗じてヒヨコたちのボス、カルロスが反乱を起こし、ウサギたちから王国を乗っ取ろうとする。", "2011-08-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニホンザル物語・家族", "ニホンザル物語・家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "長野県志賀高原にある野猿公園のニホンザル社会を９年にわたって撮り続けた力作ドキュメンタリー。原因不明の四肢障害にかかっている母ザルが健康な子を産むシーンは感動的。確固たる秩序を持つ母系社会の姿が見えてくる。西田の優しい語りも特筆もの。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕の彼女はサイボーグ", "僕の彼女はサイボーグ", "", "", "http://image.eiga.k-img.com/images/movie/53396/315x210.jpg?2337", 120, "ひとり寂しい誕生日を送る大学生ジロー（小出恵介）の前に不思議な魅力の女の子（綾瀬はるか）が現れ、一緒に楽しい1日を過ごすが、彼女はすぐに姿を消してしまう。1年後、再びジローの前に姿を現した彼女はは、どこか以前と様子が異なっていたが、なんと彼女は、未来の自分が送り込んだサイボーグだった。「猟奇的な彼女」「僕の彼女を紹介します」のクァク・ジェヨン監督が、サイボーグの“彼女”と頼りないジローの時空を超えた恋模様を描くラブストーリー。", "2008-05-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長と小天狗 殴り込み甲州路", "次郎長と小天狗 殴り込み甲州路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "上州大前田の栄次郎は、小天狗と呼ばれる痛快な若者。彼は、旅に出された先で海老屋甚八の世話になることに。が、彼が留守中、武井のドモ安一味に甚八が殺され、栄次郎は責任を感じてドモ安一味を倒そうとはやる。それを知り、清水の次郎長一家が駆けつけるのだった……。マキノ雅広の痛快娯楽時代劇。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喧嘩鳶", "喧嘩鳶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "邦枝完二の新聞連載小説を、石田民三が前後編２部構成で映画化した作品。江戸を舞台に、美貌の持ち主であったばかりに男衆から狙われ続けるおもんと、ふとしたことから彼女を救い次第に彼女に恋心を抱いていく加賀鳶吉五郎との、波乱万丈の道程を描く。おもん役は花井蘭子。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La battaglia d'Inghilterra", "空爆大作戦　決死のエアーバトル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "第二次大戦のさなか、イギリス本土に潜入したスパイのもたらす情報により、ドイツ空軍は敵国の中枢部を正確に爆撃していく。日増しに激化する攻撃に耐えかねたイギリス軍は全機に出動命令を下し、大佐自ら決死の攻撃戦の先頭に立った。", "1970-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE OF BAMBOO", "東京暗黒街・竹の家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "戦争ものやアクション映画で、その独特なキレのある演出力によって多くの快作を残したS・フラーがオール日本ロケで撮った作品。富士山麓を走る列車がギャングに襲われ、アメリカ軍の兵器が奪われた。東京警視庁とアメリカ憲兵隊は協力して捜査に乗り出す。アメリカから派遣されたスパニアという男がギャング団にスパイとして送り込まれるが、彼は一味の仲間、マリコと恋に落ちてしまう……。デタラメに建てられた日本家屋のセットやタタミの上を靴を脱がずに歩きまわる人々など、日本文化に対する理解がメチャクチャなため“国辱映画”とまでいわれたが、内容は手に汗握る緊迫したものでフラーの代表作の一つ。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARCH", "ボンデージ・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "麻薬絡みの犯罪に手を染めて逮捕された異母兄のディランの裁判費用を稼ぐため、ある秘密組織に入ることを決意したアンディ。そこで彼女は、完璧なコールガールになるための専門的な訓練を受けることに……。近親相姦、SMといったアブノーマルな要素を散りばめたエロティック・サスペンス。加藤雅也が盲目のマッサージ師を演じている。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天若有情III", "ファイナル・ロマンス／天若有情III", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「アンディ・ラウの逃避行」、「風よさらば　天若有情II」に続く悲恋シリーズ第３作。「インファナル・アフェア」のエディソン・チャンを主役に据え、様々な運命にほんろうされながら愛を貫く男女の姿を描く。最愛の姉の自殺という悲しみを癒すため、日本にやってきた富豪の娘ジェーン。スキー場で雪崩にあったジェーンは、自動車整備工エディソンに助けられ、お互い惹かれ合うようになる。結婚を誓い香港に戻った二人だが、ジェーンの父親の度重なる妨害にあい、駆け落ちを約束するが……。日本の苗場温泉でロケを敢行。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA DIVINA CREATURA", "悦楽の闇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "浮かれ騒ぐ1920年代のローマの社交界を背景とした三角関係のドラマ。イタリア映画ならではの絢爛豪華なコスチューム映画である。名うてのプレイボーイのバニャスコ伯爵は、マノエラという美女を一目見て、彼女の虜となる。マノエラはバニャスコの愛人となるが、マノエラには二重三重の生活があることが分かってくる。そして、かつてマノエラの処女を奪った男、ミケーレの出現によってバニャスコは、激しい嫉妬に苦しむ。やがてバニャスコとミケーレの板ばさみになったマノエラは、二人の前から姿を消した……。豪華な美術と衣装、ロトゥンノのカメラは特筆に値する。アントネッリのヴィーナスのようなヌードも見所の一つだ。", "1975-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE FAUVE EST LACHE´", "野獣は放たれた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "やくざ稼業を引退し、今は平穏な生活を送っているポールに、昔の知人で秘密警察のポーランが訪ねてくる。かつてのポールの仲間レーモンが国家機密を盗み出し、逃亡中だという。それでポールに手を貸してほしいというのだ。最初は断っていたポールだが、警察は汚い手を使って捜査に協力させる。そしてポールはレーモンを追い詰めるが、ひょんなことで逆に追われる立場になってしまう……。M・ラブロ監督によるフィルム・ノワールの佳作。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Patriot Games", "パトリオット・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "大ヒットした「レッド・オクトーバーを追え！」と同一のCIAアナリスト、ジャック・ライアンを主人公にした作品。休暇で妻子とロンドン旅行中のライアンは、テロ襲撃事件に巻き込まれ、持ち前の行動力で皇族一家を救うが、テロ集団の復讐の標的として狙われることになる……。ライアン役は、前作のアレック・ボールドウィンから、H・フォードに交代。家族愛を前面に出したアクション映画になっている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BANKOMATT", "バンコマット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "庭師をしているブルーノは昔は銀行の重役だった。仕事熱心だった彼は、銀行のために必死に働いていた。しかし、ある投機に失敗した彼は銀行を追われてしまったのだ。ある日、ブルーノは窃盗事件に巻き込まれた若者を警察から救い出し、彼の恋人も加え、復讐のための巨大な犯罪計画を実行し始める姿を描くサスペンス。主演は「ベルリン・天使の詩」でも知られる名優B・ガンツ。", "1988-01-01", "4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHANGHAI 1920", "上海1920　あの日みた夢のために", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "少年時代に上海で知り合ったアメリカ人のドーソンと中国人のビリー。互いの置かれていた環境は違ったが、二人は深い友情で結ばれていく。しかし、ドーソンの父の死、迫りくる戦争の波といった歴史のうねりの中で、互いの夢は違う方向を向いて歩き始めた。それから25年後、二人は上海で再会する。1900年代初頭の情景が巧みに再現された上海の街並みと、２年ぶりにスクリーンに登場したローンの力強い演技は見ごたえがある。喜多郎の艶やかなメロディーがドラマをいっそう盛り上げる。", "1991-01-01", "1,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nick & Norah's Infinite Playlist", "キミに逢えたら！", "", "", "http://image.eiga.k-img.com/images/movie/54136/315x210.jpg?3383", 89, "高校生のニックは元彼女のトリスを忘れられず、自分が作った音楽CDをトリスに送り続けていた。しかしニックのCDは聞かれることもなく捨てられ、そのCDをゴミ箱から拾い続けていたノラは、見ず知らずの男性が作った音楽に惹かれていき……。音楽を通じて出会った男女のハートウォーミングラブストーリー。主演は「JUNO／ジュノ」のマイケル・セラ。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大盗賊", "大盗賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "三船敏郎扮する堺の豪商が、無実の罪で火刑に処せられそうになるが、危機一髪、国外に脱出。途中、嵐で難破し明国近くの孤島に漂着し、そこで王妃を助けて悪宰相を倒す。東宝が特撮の新路線として企画した冒険大活劇の第１作。妖婆に扮する天本英世が怪演。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("開港・風雲録 YOUNG JAPAN", "開港・風雲録 YOUNG JAPAN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "開国か攘夷かで揺れる幕末。海岸沿いの小さな村に過ぎなかった横浜は一躍注目の的となり、多くの若者たちが開港準備に追われていた。そんな横浜にやって来た攘夷派の若者・重伍は、そこで働く魅力的な人間たちとふれあい、自分の考えを変えていく、青春時代劇による人間ドラマ。人気TVドラマを数多く演出した大山勝美の初監督作。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OFICIR R RUZOM", "士官と未亡人 禁じられた官能", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ナチス・ドイツが敗れ、共産主義に沸くヨーロッパ・ユーゴスラビア。ブルジョワ階級の未亡人と共産主義者の少女、そしてその恋人である解放軍中尉との三角関係を描くラブ・ストーリー。東欧ペレストロイカの波に乗って製作された“解放”映画の１本として受けとれる官能ロマンだ。", "1988-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Revenant", "ゾンビ処刑人", "", "", "http://image.eiga.k-img.com/images/movie/57226/315x210.jpg?2001", 117, "思わぬ事態から街のヒーローになったゾンビの活躍を描く異色ホラーコメディ。不慮の死を遂げた青年バートは、なぜかゾンビとなって復活。食糧となる生き血を探していたところを強盗に襲われるが、すでに死んでいるため撃たれてもビクともせず、あっさりと返り討ちにする。これをきっかけに「夜回りゾンビ」として夜な夜な街に繰り出し、悪人を成敗するバートは、たちまち正義のヒーローとして話題になるのだが……。", "2011-11-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢の中へ", "夢の中へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "インディーズ界の異端児と呼ばれ、独自の路線をひた走る園子温監督による異色ファンタジー。自暴自棄気味になった役者が、奇妙な夢に凌駕されていく様が描かれる。園監督の下、日本映画界の第一線で活躍する役者が集結。村上淳、オダギリジョーなどが仰天の配役で登場する。すべてがうまくいかない二流役者のムツゴロウ。現実逃避からか不思議な夢を見るようになった彼は、次第に現実があやふやになり……。", "2005-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イラクニ接近ス", "イラクニ接近ス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "イラクに関するマスコミの報道に不満を抱いた当時20歳の谷澤壮一郎が、単身で同国へ。イラクで暮らす市民にインタビューを試み、彼らの視点から今回の戦争を斬る。登場する人々の意見は、戦争賛成派からアメリカの横暴を訴えるものまでさまざま。だが、いずれの発言も今回の戦争の真実を雄弁に物語る。手掛けた谷澤は自己責任のもと企画を立て、不安定な社会情勢にあるイラクへの取材を強行。その渾身のリポートに脱帽だ。", "2006-02-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MERMAIDS", "恋する人魚たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "セクシーでおシャレ、自分勝手で他人とのしがらみを極端に嫌う母親と、ユダヤ系でありながらカトリックの尼さんを夢見る15歳の長女、そして水泳のチャンピオンを目指す９歳の次女の３人が織り成すコミカルな物語。時代背景となる1963年を、音楽や風景、アンティーク・カーなどで見事に浮かび上がらせた。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風花", "風花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "女優の岸恵子のために木下監督が書き下ろして監督した、純愛モードのメロドラマ。信州の田園地帯をバックに二世代にわたる身分違いの恋を、差別などの社会問題を取り入れながら描いた秀作。大地主の次男と小作人の娘が心中して娘だけが生き残った。男の子供を妊娠中の彼女は相手方の家に渋々迎えられ子供を産んだ。後にその子が長男の年上の娘に淡い恋心を抱くことに。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Death of a President", "大統領暗殺", "", "", "http://image.eiga.k-img.com/images/movie/34136/315x210.jpg?1479", 93, "現職の合衆国大統領が暗殺されたら、という仮定に基づくフィクション。07年10月19日、アメリカ合衆国第43代大統領ジョージ・W・ブッシュが何者かの凶弾に倒れる。狙撃の瞬間はメディアを通して瞬く間に世界に広がり、衝撃を与える……。既存のニュース映像などをたくみに使用し、フィクションでありながらも、大統領暗殺の瞬間をリアリティ溢れる映像で表現。全米では公開規模を縮小されるなど、議論を呼んだ一作。", "2007-10-06", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("United 93", "ユナイテッド93", "", "", "http://image.eiga.k-img.com/images/movie/1726/315x210.jpg?2179", 111, "2001年9月11日の全米同時多発テロの混乱を、全米中の飛行機を監視する管制センターや米軍基地、そして、ハイジャックされた4機の内ただ1機だけ目的地に届かず、ペンシルバニア州に墜落したユナイテッド93便の内側から描く問題作。監督・脚本は「ブラディ・サンデー」「ボーン・スプレマシー」のポール・グリーングラス。キャストは、管制官や陸軍司令官など多数の本人出演の他、ほとんど無名の俳優を起用。", "2006-08-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンデスの花嫁", "アンデスの花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "タンガニーカにオール・ロケした「ブワナ・トシの歌」に続き、アンデスにオール・ロケを敢行した羽仁進のドキュメンタリー・タッチのドラマ。写真見合いで単身アンデスに渡って結婚したタミ子は、財宝探しに没頭する夫を尻目に地道に農業に励む。左幸子が土にまみれて働く女性を熱演。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOXING HELENA", "ボクシング・ヘレナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "セクシーな美女ヘレナに求愛し続けるものの、冷たく断られる青年医師のニック。思い余った彼は、偶然、車に轢かれたヘレナの両手、両足を切断し、彼女を監禁して危険な愛の生活を始めるが……。デヴィッド・リンチの愛娘ジェニファーが初監督に挑んだサイコなラブ・ストーリー。当初、主役のヘレナに予定されていたキム・ベイシンガーの降板をめぐる裁判も話題に。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A NIGHT AT THE OPERA", "マルクス兄弟　オペラは踊る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "長くパラマウントで作品を発表してきたマルクス・ブラザースが興行的に低迷し、心機一転をはかってMGMの伝説的な名プロデューサー、サルバーグの下で製作した作品。それまでの４人組からゼッポが抜けて３人組となった。舞台はイタリアのミラノ。ドリフトウッドは大金持ちのクレイブール未亡人に、ニューヨークのオペラへの投資を勧める。うまく口説いて、せしめた20万ドルで歌手を雇い、一路アメリカへ。密航したハーポ、チコも交えての船上でのギャグの数々、ニューヨークのオペラ舞台での大混乱がとてもおかしい。数ある彼らのコメディなかでも傑作としての誉れ高い一級品。DVDは「マルクス・ブラザーズ コレクターズ・ボックス」に収録。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕は人を殺しました", "僕は人を殺しました", "", "", "http://image.eiga.k-img.com/images/movie/57996/315x210.jpg?2240", 80, "附属池田小事件の宅間守や北九州連続監禁殺人事件の松永太死刑囚をモチーフに、理由や背景を一切描かず、欲求のまま人が人を殺すまでを映し出した異色作。2011年1月に劇場公開された「悪魔がきた」をはじめ、「壊したいおもちゃ」「太陽は知らない」「怪物はここにいる」など、人間の動機なき暴力や犯罪を描く短編を手がけてきた坂井田俊監督の初長編作。主演は俳優の川瀬陽太。", "2012-05-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Survival of the Dead", "サバイバル・オブ・ザ・デッド", "", "", "http://image.eiga.k-img.com/images/movie/55016/315x210.jpg?4438", 90, "「ナイト・オブ・ザ・リビングデッド」（68）、「ゾンビ」（78）などで知られるゾンビ映画界の巨匠ジョージ・A・ロメロ監督が手がけたゾンビ映画。死者たちが突如よみがえり、人々を襲い始める世界が舞台。秩序を失った世界で、強盗に成り下がった元州兵のサージは、安全な場所を求めてさ迷い続けていた。ある日、デラウェア沖に安全な島があるという情報を耳にしたサージらは、わずかな望みにかけ、その島へと向かうが……。", "2010-06-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kak ya Provyol Etim Letom", "夏の終止符", "", "", "http://image.eiga.k-img.com/images/movie/56806/315x210.jpg?3799", 124, "カンヌ、ベルリン、ベネチアの世界3大映画祭の受賞作を中心に、各地の映画祭で話題になりながらも日本未公開だった作品を一挙上映する「三大映画祭週間2011」にて上映。ロシア北極圏辺境の島にある気象観測所で放射能の数値を測定し、本庁にデータを送る仕事を黙々とこなすセルゲイとパベル。ある日、セルゲイはパベルに観測をまかせて釣りにでかけるが、未熟なパベルが観測時間を逃した上に、その埋め合わせのためにウソのデータを報告してしまい……。2010年・第60回ベルリン国際映画祭で2つの銀熊賞（男優賞・芸術貢献賞）を受賞。", "2011-08-13", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HE STANDS IN A DESERT COUNTINGTHE SECONDS OF HIS LIFE", "時を数えて、砂漠に立つ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "戦後アメリカ実験映画の推進者であり“アンダーグラウンド・シネマの守護神”と呼ばれたメカスが1969～84年に撮った“日記映画”。メカスは1922年リトアニア生まれ、詩人として出発したがナチスの侵攻を逃れて故国を離れ、ドイツでの難民生活の後、1949年弟アドルファスとともにニューヨークに移住。その時以来、16ミリ・ボレックスカメラで日記を撮り続けている。周囲の友人知人や折々の風景を撮りためたその膨大なフィルムを、年代やエピソードに従って「リトアニアへの旅の追憶」（1972）「ロスト・ロスト・ロスト」（1975）などにまとめている。本作は1969～84年に撮影されたフィルムからウォーホル、マチューマス、セラ、レノン＆ヨーコ、リヒター、ラングロワ、ジャクリーン・オナシスら幅広い交友を125の字幕でつなぐ。独特のコマ撮りを主体に短いスケッチの連続、メカスの愛情ある視線、観察眼、感受性。彼自身はこれを様々な人間の観察記録である“人類学的スケッチ”と呼んでいる。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMUALD ET JULIETTE", "ロミュアルドとジュリエット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "現代版“ロミオとジュリエット”の障害は、人種と階級の違い。フェミニストの女流監督、C・セローが社会の固定観念をユーモアたっぷりに覆した現代のおとぎ話。「愛と宿命の泉」での名演以後すっかり売れっ子になったD・オートゥイユと、天性の才能が光るまったくの素人P・リシャールの演技が素晴らしい。ロミュアルドは35歳の会社の社長。美しい妻と子供たちに恵まれて順風満帆の人生を歩んでいる。一方43歳のジュリエットは、ロミュアルドの会社で掃除婦をして父親の違う５人の子を育てている。接点のまったくなかった二人だが、重役たちの陰謀に巻き込まれて……。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("БЛОКАЛА／BLOCKADE", "レニングラード攻防戦 II 攻防900日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 200, "戦史に残るレニングラード攻防戦をセミドキュメンタリー・タッチで描いた、スペクタクル戦争巨編の第２部。1941年９月、レニングラードはナチス・ドイツ軍により完全包囲された。物資補給ルートは断たれ、苦境に追い込まれたソ連国民たちは……。", "1977-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THREE LIVES OF THOMASINA", "トマシーナの三つの命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "P・ギャリコのファンタジー小説『トマシーナ』をもとにした一編。ネコのトマシーナが引き合わせてくれた妻を亡くした獣医と一人の女、そして獣医の娘をめぐる物語を、愛情を込め描いている。原作ほどファンタジー色は強くないが、ディズニー作品らしい優しさに満ちた物語となった。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大阪の宿", "大阪の宿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "原作に描かれた大正から、この映画が製作された1950年代に時代を移し、主人公の目に映った大阪の庶民の人間模様を情緒豊かに描く。東京から大阪に左遷された主人公の会社員は、酔月荘という安旅館に下宿する。この旅館で働く３人の女中や、“うわばみ”というあだ名の大酒飲みの芸者といった人々の喜怒哀楽が、いつくしみのこもったタッチで綴られていく。中年の女中を演じるのは水戸光子と川崎弘子、芸者役の乙羽信子、若い女中の左幸子といった配役の妙と大阪の風俗とが、五所平之助のきめ細い演出のなかで見事に解け合い、「煙突の見える場所」にならぶ五所平之助の戦後の秀作の一つとなっている。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEST YEARS OF OUR LIVES", "我等の生涯の最良の年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 170, "同じ町に帰還した中年銀行員のアル、デパート勤務のフレッド、戦場で両腕を失い鉄の義手をはめているホーマーの３人の家族や恋人たちをめぐるドラマで、第二次大戦から帰還した軍人の社会復帰を描いたゴールドウィン製作の大作。戦勝国アメリカが、戦後に抱えた苦悩を、勇気と希望を謳歌して描いた国民的な作品で、アカデミー賞を８部門で受賞、H・ラッセルは助演男優賞のほかに特別賞まで受賞している。ワイラーの手堅い演出力が長さを感じさせない。ホーマーの恋人を演ずるK・オドンネルも清楚な感じで印象的。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Home", "HOME 空から見た地球", "", "", "http://image.eiga.k-img.com/images/movie/54476/315x210.jpg?2603", 120, "", "2009-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("40CARATS", "エーゲ海の旅情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "エーゲ海を旅行中、見知らぬ若者と一夜をともにした中年女性アン。そしてニューヨークでの偶然の再会は、親子ほど年の離れた二人に恋の炎を燃えさせた……。ブロードウェイのヒット作を映画化したラブ・コメディ。少女のような恥じらいを見せるウルマンが魅力的。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸の春 遠山桜", "江戸の春 遠山桜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "山中貞雄ファミリーといえる作家たちで構成された鳴滝組。彼らのペンネーム・梶原金八原作・脚色によるコメディ・タッチの時代劇で、“遠山の金さん”の若き日々を描いている。山中作品でおなじみの名コンビ、高勢実乗と鳥羽陽之助が、本作でも、おでん屋の茂十、そば屋の当八として縄張り争いを繰り広げるのも見どころ。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIAMOND SKULLS", "ダイヤモンド・スカル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "現代のイギリス上流階級。財界の若きリーダーであるヒューゴは何不自由ない暮らしを送っていたが、ある晩酔って運転し、女性をはねてしまう。同乗の上司の手で事故は秘密裏に処理されるが、警察の捜査が迫るとともに、ヒューゴの苦悩は妄想へとエスカレートしていく……。「白馬の伝説」のG・バーンが悩める主人公に扮する。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しあわせになろうね", "しあわせになろうね", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "大騒動に直面したヤクザ事務所の人間模様を、ペーソスたっぷりに綴るコメディ。不景気の影響による解散を翌日に控えた山室組に、恐ろしいニュースが飛び込んでくる。若い組員ヒロシが、ライバル組織のボスを殺害したというのだ。小心者の山室組長は、抗争を避けるためには自分の首か１億円の現金が必要だと聞かされ、右往左往するばかり。やがて方針を決める期限が迫る中、ただ“幸せになりたい”だけの山室や組員たちの運命は……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIMER", "プライマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "'03年のサンダンス映画祭でグランプリを受賞した知的なSFスリラー。あるエンジニアがタイムマシンを開発したことから、さまざまな異常事態が発生する。まったくの無名だった監督のカルースは製作・脚本・音楽・主演などほとんどのパートを一人でこなし、並々ならぬ才能を発揮している。新製品の開発に勤しむアーロンとエイブは、偶然にもタイムマシンを発明する。金儲けをもくろむ二人はタブーを犯してしまい……。", "2005-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忠臣蔵 花の巻・雪の巻", "忠臣蔵 花の巻・雪の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 206, "東宝創立30周年記念映画で、殿中刃傷の背景を歪曲して描き新味を出した八住利雄のシナリオと、手持ちカメラでブレも構わず写実的効果を出した稲垣浩の演出が光る“忠臣蔵”。松本幸四郎（８代目）以下、東宝の映画・演劇の手持ちスターを総動員。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VERY　BAD　THINGS", "ベリー・バッド・ウェディング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "結婚を控えたカイルは、悪友たちが企画したバチェラー・パーティーに招いた娼婦が事故死してしまったことから、悪夢のような日々を過ごす羽目になる。全編にわたって毒を撒き散らすブラック・コメディ。異様に結婚願望の強いフィアンセ役にC・ディアスが扮し、クライマックスでは恋人のためというよりも結婚式のために大活躍!?", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAKOTO", "MAKOTO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「踊る大捜査線」シリーズの人気脚本家、君塚良一の監督デビュー作。郷田マモラの同名コミックを基に、死に隠された切ない愛のドラマを描き出す。霊の無念の思いに耳を傾けることでさまざまな真実に直面する監察医・真言に、これが14年ぶりの映画出演となった東山紀之が扮する。現世に未練を残して事故死した人の霊が見える監察医が、彼らの代弁者となって事件を解明する。そんな彼の前に亡き妻の霊が現れ……。", "2005-02-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブホテル", "ラブホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "相米慎二が監督した唯一のロマンポルノ。経営する会社が倒産し、妻をヤクザに犯されてしまう村木。身も心もボロボロになり、自殺を決意して、最後の金で女を買う。しかし、ホテルに呼んだ名美をいたぶる村木は、その官能的な姿勢に生きる希望を見出して、タクシー・ドライバーとして再起。２年後、再び名美と出会う……。相米流長回しが心地良いリズムを刻む、感度抜群の大人のメロドラマに仕上がった。山口百恵の『夜へ』をBGMに使った音楽的センスも光っている。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILLIAM−S−BURROUGH’S　WIFE", "バロウズの妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1950年代後半から1960年代前半のアメリカで流行した文学運動“ビート・ジェネレーション”において、アレン・ギンズバーグやジャック・ケルアックとともに圧倒的な影響を与えた作家ウィリアム・バロウズ。彼の妻ジョーンに焦点を当て、バロウズの作家以前の姿と生活を綴ったドラマ。バロウズとジョーンの出会い、友人たちとの関係、結婚後のメキシコでの生活、そしてジョーンが夫バロウズにピストルで殺される、という悲劇に至るまでを克明に描いている。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FINAL HEIST", "ザ・ファイナル・ゲーム～盗まれた48時間～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "カナダのモントリオール、メトロポリタン美術館に秘蔵されている『ゴッホの肖像画』は、その価値数百万ドルといわれる名画である。娘の命と引き換えに、その名画を48時間以内に盗むことになった男の物語。「血のバレンタイン」で知られるG・ミハルカ監督が、華麗なる盗みのテクニックをスリル満点に映し出す。", "1991-01-01", "10,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOREST WARRIOR", "ザ・サバイバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "自然破壊から森林を守る不死身の男“フォレストウォリアー”が５人の子供たちと協力して、悪徳企業と戦うアクション・アドベンチャー。美しい大自然の姿を残すタングルウッド・マウンテン。この山に法の目をかいくぐり、暴利をむさぼる悪徳伐採企業が踏み込んで来た。森を守り続ける伝説の男マッケンは、親に内緒でこの森林のトゥリーハウスにキャンプにやってきた子供５人と出会い、彼らとともに開発を阻止しようと戦いを挑む。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JURIAN　PO", "ジュリアン・ポーの涙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "C・スレーターが見知らぬ町で数奇な体験をする青年を、誠実感たっぷりに演じた人間ドラマ。人生に疲れを感じたごく平凡な男ジュリアン・ポーが旅に出る。途中小さな田舎町に立ち寄るが、そこは長い間来訪者がいない閉鎖的な町だった。人々に注目され戸惑う彼は、冗談交じりにある言葉を口にする。それ以来、人々はポーに好奇心をむき出しにして、崇拝さえする者が現れる始末。そんな彼らを受け入れるポーは、やがてある決断を迫られるのだった……。世間から孤立した町に足を踏み入れたため人々にほんろうされていく主人公。彼の不条理な運命を、やわらかいタッチで綴った異色作だ。ビデオタイトルは「愛しのジュリアン」。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OFF LIMITS", "サイゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「プラトーン」のエリアス軍曹役で脚光を浴びたW・デフォーが、再びベトナム戦争下のサイゴンを舞台に、連続殺人事件を追う私服警官に扮するアクション・ミステリー。1968年、売春街で６人の売春婦が残忍な手口で殺されるという事件が発生。主人公バックと相棒のアルベビーは、現場の証拠から犯人がアメリカ人将校であることを確認し、最前線に乗り込んでいくが、事件は意外な方向へと展開する。現地の人々の反米感情や主人公と尼僧との恋を絡めながら、戦争犯罪の現場が決して最前線ばかりではないことを描く問題作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅の途中で−FARDA−", "旅の途中で−FARDA−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「ブリキの勲章」「セイリング 海にはばたく」などの中山節夫監督が、異国の地イランを訪れた日本人が様々な人と出会い、自分を見つめ直す姿を描いた人間ドラマ。イランそのものの描写に関しては、イラン映画の巨匠アッバス・キアロスタミが監修に当たっている。大手自動車会社から部品メーカーに出向しいている井沢は、町工場の倒産に当たって強制送還されたイラン人に未払いの給料を渡すためイランへ飛ぶ。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　IDIOTS", "イディオッツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "映画監督集団“ドグマ95”の２番目の作品として製作されながらも、日本では長らく公開が見送られていたトリアーの問題作。幼い子供を失ったばかりのカレンは葬式の日、社会的な利益を生まない人々を、可哀相と言いながら排除する人々の反応を挑発的なやり方で暴こうとする“イディオッツ”という集団に偶然出会う。あえて白痴のふりをする彼らに最初は怒りをあらわにしたカレンだったが、次第に心惹かれ共同生活を送るようになる。ある日、グループのリーダー、ストファーは結束を形にして示そうと、自分が最も愛する人の前で白痴になることを提案。愛する者の前で白痴になれない脱落者は消え、最後にカレンがテストを受ける番になった。カレンは家族が住む家へと戻り、家族の前で白痴となる……。「奇跡の海」「ダンサー・イン・ザ・ダーク」で、無垢で一途なヒロインを描いたトリアーが、無垢であることの異常さに焦点を当てているのが興味深い。", "1998-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("127 Hours", "127時間", "", "", "http://image.eiga.k-img.com/images/movie/55846/315x210.jpg?4537", 95, "登山家アーロン・ラルストンが体験した実話を監督ダニー・ボイル、ジェームズ・フランコ主演で映画化したサスペンスドラマ。03年、当時27歳だったアーロン（フランコ）は一人でユタの険しい谷へロッククライミングに行くが、誰も通りそうにない谷間で落下し、右手を岩に挟まれてしまう。そこから5日間、身動きの取れなくなったアーロンは必死に脱出をはかるが……。ボイル監督とともに脚本を手がけたのは「スラムドッグ＄ミリオネア」のサイモン・ビューホイ。", "2011-06-18", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The People Under the Stairs", "壁の中に誰かがいる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "　スラム街で育った家族思いの少年フールは悪人リロイとともに不気味な屋敷に忍び込み、盗みをはたらこうとする。しかしその屋敷には恐ろしい秘密が。異常な家主は娘アリスを虐待し、さらに牢獄と化した部屋には、誘拐されたあげく、生ける屍のような容貌に変わり果てた者たちが幽閉されていた。フールはアリスを救出し、家主と対決することに……。カラクリ屋敷の中で、子供たちの恐怖に満ちた冒険が展開される。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Red Corner", "北京のふたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "米国人のビジネスマン、ジャックが米中初の衛生事業契約の交渉のため北京を訪れる。彼は案内されたナイトクラブで出会った女性と一夜をともにするが、翌朝彼女は殺害されていた。殺人の容疑者として逮捕された彼は、無罪を主張したくても言葉が通じない。女弁護人シェンも、この国ではすべてを受け入れなくてはならないと、有罪を申し立てる。だが、ジャックの必死な姿に心を動かされ、事件に不審を抱いた彼女は、彼とともに闘う決意をする……。殺人犯に仕立て上げられた米国人男性と弁護士の中国人女性が、巨大な敵を相手に闘いながら強い絆で結ばれていくサスペンスフルなラブ・ストーリー。異国の地で身の潔白を証明するため奮闘する主人公をR・ギアが熱演。DVDタイトルは「レッド・コーナー　北京のふたり」。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOAQUIN MURRIETA（MURIETA）！", "荒野の群盗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "19世紀のカリフォルニアを舞台に、伝説的な反逆児ムリエタの生涯を綴った骨太なウエスタン。やっと掘り当てた金を持ち逃げされ、妻を殺されたムリエタは一年後、賭博師となって犯人たちを追っていた。そしてようやく捜し出した犯人たちを射殺するが、ムリエタの人生はすでに血塗られたものとなってしまっていた……。", "1965-01-01", "1,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　CLOSER　YOU　GET", "クローサー・ユー・ゲット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「フル・モンティ」の製作者U・パゾリーニのプロデュースによるハートフル・コメディ。舞台はアイルランドののどかな田舎町。ハリウッド映画の女優の美しさに魅せられた独身男たちが、アメリカから嫁をとろうと奮闘。村祭をお見合いパーティーに変えて、彼らは米国からの客を待つが……。不器用な男たちの姿が笑いを誘う。ビデオ発売時タイトルは「アメリカン・ウーマン」。", "2000-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAKED", "ネイキッド　快楽に満ちた苦痛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "ロンドンの街で、飢えたように女性の肉体を追い続ける二人の若者、ジョニーとジェレミー。精神的な愛よりも肉体的なセックスを信じる二人の女性遍歴から、終末論的な人間の姿が浮かび上がる……。イギリス映画界注目のM・リーが描く現代の黙示録。カンヌで主演男優賞に輝いたD・シューリスの狂気の演技に注目。", "1993-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東遊侠伝", "関東遊侠伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "松尾昭典監督、小林旭、宍戸錠主演による任侠アクション。元渡世人の主人公が、二つのヤクザ組織にはさまれて青息吐息の小さな組を救うために、再び渡世人の道に戻り一家の再建に乗り出す。小林旭と宍戸錠のコンビは、“渡り鳥”シリーズのノリで演じている。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Perhaps Love", "ウィンター・ソング", "", "", "http://image.eiga.k-img.com/images/movie/33736/315x210.jpg?4724", 109, "「ラブソング」の名匠ピーター・チャン監督が、国際派俳優・金城武を主演に迎えて贈るラブ・ストーリー。女優になるという夢を叶えるために愛を捨てた孫納と、別れてからもずっと彼女のことが忘れられない映画監督志望の見東。突然の別れから10年後、夢を実現した孫納と映画俳優になった見東は、ミュージカル映画で共演することになり再会を果たす。2人の愛の行方を、美しいミュージカル・シーンを織り交ぜながら描く。", "2006-11-11", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPIDERS", "スパイダーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "不時着したスペースシャトルから奇跡的に救出されたパイロットを救う為、NASAの極秘施設に呼び出されたマーチは、パイロットの口から巨大なクモが這い出てくる光景を目撃。そこにはエイリアンのDNAを注入した新種の生物の開発を進める政府の極秘プロジェクトが絡んでいた……。アニマトロニクスによる巨大クモが大暴れするクライマックスが見もの。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LIVING DESERT", "砂漠は生きている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "ディズニーの“自然の冒険”シリーズ第１作目のドキュメンタリー。従来の“自然の驚異”シリーズ（「水鳥の生態」など）は短編であったが、本作よりシリーズ名を変え長編化した。多くの西部劇の背景となった北米各地の砂漠の生物の生態記録。教育的価値に加えて娯楽映画として新分野を開拓した。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hulk", "ハルク", "", "", "http://image.eiga.k-img.com/images/movie/1086/315x210.jpg?2678", 138, "映画版ではドラマの重要なポイントとなる主人公の父親役をニック・ノルティ、主人公の恋人である科学者役をジェニファー・コネリー、軍人である彼女の父親役をサム・エリオットと、実力派俳優が共演。変身後のハルクのCGI映像はILMが担当。視覚効果スーパーバイザーのデニス・ミューレンは「スター・ウォーズ　エピソード2／クローンの攻撃」を途中で降りて本作に参加。ハルクの動きには、アン・リー監督自身の動きもキャプチャーされて使われた。", "2003-08-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タイマグラばあちゃん", "タイマグラばあちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "戦後の食糧難から若者たちによって開拓された岩手県の早池峰山の麓にあるタイマグラ地区。最後の１軒となった向田夫妻の自然に囲まれた日常を15年間記録したドキュメンタリー。昭和63年には電気が引かれたり、若者が隣に移り住んできたり、素朴な中で日々変化する暮らしぶりを穏やかに映し出す。岩手県の早池峰山の小さな開拓地。10軒ほどあった農家の人々は山を去ったが、その後も向田夫妻だけは、二人で静かに暮らしていた。", "2004-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELDLA DE LA BEASTA", "ビースト 獣の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "破天荒なSF映画「ハイル・ミュタンテ！ 電撃XX作戦」でデビューしたスペインの新鋭監督A・デ・ラ・イグレシアの第２作。黙示録の研究家であるアンヘル神父が、クリスマスに誕生する悪魔を倒すためマドリッドに乗り込む。悪魔に接近するには自分も悪行を積むべきだと考えた彼は、巨漢のヘヴィメタ野郎ホセ・マリアの協力を得て、悪の限りを尽くすのだが……。何とか人類を救おうとする神父のあまりに大マジメな暴走ぶりに、思わずアゼンとしてしまう異色ホラー・コメディ。しかも本国スペインのゴヤ賞で６部門を独占しただけあって、ドラマ運びや映像作りも実に丁寧な仕上がり。悪魔呼び出しの儀式をしているマンションにおもむろに現れるヤギ、建設中のビルに出現する悪魔といった現実か妄想かわからないイメージも随所に盛り込まれ、観る者を“楽しく”混乱させてくれる。ビデオ発売時タイトルは「ビースト　悪魔の黙示録」。", "1995-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("情非得巳之生存之道", "ビバ！監督人生!!", "", "", "http://image.eiga.k-img.com/images/movie/54016/315x210.jpg?3000", 96, "台湾シネマ・コレクション2008上映作品。演出家、俳優として活躍するニウ・チェンザーが監督・主演する半自伝的映画。共演に「闘茶／tea fight」のチャン・チュンニンら。トレンディ・ドラマの演出家兼俳優として時代を築いたドウズは、社会や政治の腐敗を描く映画を撮りたいと奮起する。しかし主演俳優に逃げられ、資金調達にも失敗。さらに自身の浮気癖のせいで恋人にも逃げられ、全てに行き詰ってしまう。", "2008-09-06", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴールデンスランバー", "ゴールデンスランバー", "", "", "http://image.eiga.k-img.com/images/movie/54556/315x210.jpg?1576", 139, "山本周五郎賞を受賞した伊坂幸太郎の逃亡劇を、「アヒルと鴨のコインロッカー」「フィッシュストーリー」でも伊坂作品の監督を務めた中村義洋が映画化。主演に堺雅人、共演に竹内結子、吉岡秀隆、香川照之、大森南朋、柄本明ほか。野党初となる首相の凱旋パレードが行われている仙台で、ラジコンヘリ爆弾を使った首相暗殺事件が起きる。時を同じくして、久々に旧友・森田と再会を果たしていた青柳は、突然現れた警官から発砲され、追われる身となってしまう。", "2010-01-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("結婚案内ミステリー", "結婚案内ミステリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "結婚相談所でアルバイトをしている女子大生が、大企業コンツェルンの女実業家から奇妙な結婚話を持ちかけられ……。トラブルに巻き込まれていくヒロインを姿を、ユーモアたっぷりに描いたサスペンス・ミステリー。当時、人気上昇中のアイドル女優・渡辺典子とうまくマッチした。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　PALMES　DE　M.SCHUTZ", "キュリー夫妻・その愛と情熱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "J・N・ファンウィックの戯曲『シュッツ氏の勲章』を、「ラ・ブーム」の職人監督C・ピノトーが映画化。19世紀末、パリ物理化学学校にやって来たポーランド人女性のマリーが、優秀な研究員ピエールと深い愛で結ばれラジウム発見という快挙を達成するまでを、当時の研究室を再現したセットを背景に綴る。シリアスなイメージの強いI・ユペールが、伝説的なヒロインを軽妙なユーモアを交えて体現。お目付役の校長シュッツに扮したP・ノワレのコメディ・リリーフも楽しい。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMA DELLA FINESTRA", "窓からローマが見える", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "報道カメラマンのカルロは、不思議な日本人の娘Oに出会い惹かれるが、一方で彼の留守中、黒人に強姦された妻の幻影に悩まされる……。世界的な版画家にして芥川賞作家、池田満寿夫の「エーゲ海に捧ぐ」に続く監督第２作。夫人の佐藤陽子がジプシー女で登場。", "1982-01-01", "76,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガンヘッド", "ガンヘッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "2039年の廃墟と化したロボット工場を舞台に、人類支配をたくらむ巨大コンピュータに挑む青年と重装甲機動ロボット“ガンヘッド”の活躍を描くSFアクション。ハリウッドからB・バーキをヒロインに据えて、ハリウッド派の原田眞人が撮り上げた野心作だ。特撮監督は川北紘一。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'avion", "チャーリーとパパの飛行機", "", "", "http://image.eiga.k-img.com/images/movie/34156/315x210.jpg?1502", 100, "亡き父が最後にくれたおもちゃの飛行機が、父の死を受け入れられない少年に奇跡をもたらす感動のファンタジック・ドラマ。クリスマスにパパからおもちゃの飛行機をプレゼントされた少年チャーリー。ところがその数日後、パパが事故でこの世を去ってしまう。ママは悲しみに暮れ、チャーリーも大好きだったパパの死に戸惑うばかり。そんなある日、パパからもらった飛行機が、まるで生きているかのように動き出し……。", "2007-09-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴルゴ13・九竜の首", "ゴルゴ13・九竜の首", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "人気劇画『ゴルゴ13』の映画化。世界的な殺し屋ゴルゴ13は、アメリカの麻薬組織からシンジケートの香港支部長暗殺の依頼を受け香港に潜入した。香港警察の敏腕刑事に邪魔され、再三チャンスを逃すが、そこは鋼鉄の意志を持つゴルゴ。最後は見事、暗殺に成功する。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我愛紋柴／NOW YOU SEE LOVE……NOW YOU DON’T！", "恋のトラブルメーカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "片田舎の村の若き村長サンソイのもとに婚約者のフェインがイギリスから帰ってくる。だが、都会生活にかぶれたフェインは彼とケンカをし、都会へと出て行ってしまう。サンソイも後を追って都会へと向かうが、二人は顔を合わせるたびにケンカを繰り返すばかりで……。「誰かがあなたを愛している」の監督メイベル・チャンと脚本アレックス・ロウのコンビが手掛けた心温まるラブ・コメディ。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONSTER DOG", "モンスター・ドッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "異色ロック・ミュージシャン、A・クーパーが主演したホラー映画。ロック・シンガー、ビンスがプロモーション・ビデオ製作のため20年ぶりに訪れた故郷で、野良犬による惨殺死が相次いだ。住人たちはビンスが原因だと思い込み襲ってくるが……。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEAVE ALL FALL FAIR", "マンスフィールドの追憶 孤独な果実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "20世紀初頭の女流作家キャサリン・マンスフィールドのひたむきな生き方と愛を浮き彫りにする。女性の地位がまだ低かった時代に渡英、愛を求めてさすらいながら、書くことをやめなかった作家の素顔を、夫との愛の姿を中心に描き出していく。", "1985-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("よい子と遊ぼう", "よい子と遊ぼう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 44, "“J・MOVIE・WARS”第２期作品の一編。ごく普通の中学生であるビンゴ、原田、くじら、本屋、並夫ら５人の退屈しのぎは盗み。ある日、並夫が、学校で噂の謎の覆面男に襲われたことから、彼らは覆面男との対決を決意する。盗みによってエネルギーを発散する彼らの日常と、話題の都市伝説的コワイ話を重ね合わせ、いまどきの中学生の世界をシャープかつドライに描く。彼らが交わすビビッドな会話、通常の言語感覚からかけ離れた言葉とリズムが、そのまま作品のリズムに連なり、一種のトリップ感覚をもたらすとともに、従来の少年映画とはひと味違った趣きを醸し出している。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Loser", "恋は負けない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "田舎町で育った青年ポールは大学に通うため、ニューヨークへ行くことに。だが、あまりにも純朴なポールは学校で浮きまくり、その上、ルームメイトからも馬鹿にされる始末。そんな彼はドーラという女子学生に心奪われるが、恋は実らず、そのあげくに彼女が文学部教授オルコットの恋人であることも発覚。にもかかわらず、お人よしのポールはドーラに優しく接するが……。都会へ出た青年の成長と恋を綴ったラブ・コメディ。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おしどり駕籠", "おしどり駕籠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ある城下町、藩主が急逝した後の世継ぎ問題で家老の黒木と次席家老の松坂は自分の息子を推してもめ合っている。が、当の本人たちは女遊びをしたり、そんな問題はどこ吹く風。しかしそれぞれに刺客がさし向けられて事態は急展開……。オペレッタ仕立てでテンポよく撮り上げられた明るい時代劇。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SATREE−LEX", "アタック・ナンバーハーフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "タイの女子バレーボール大会で、メンバーのほぼ全員がオカマというチームが優勝!?　そんな実話をもとにしたスポ根コメディ。彼女(？)たちが勝利を手にするまでの悲喜こもごもをコミカルに描く。トンコントーン監督は、数ヵ月に及ぶトレーニング(オカマとしての心得も含む)をキャストに積ませてから撮影に入ったとか。", "2000-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蘇る玉虫厨子　時空を超えた技の継承", "蘇る玉虫厨子　時空を超えた技の継承", "", "", "http://image.eiga.k-img.com/images/movie/54146/315x210.jpg?4452", 64, "飛鳥時代に作られ、法隆寺にて保管されている国宝の厨子（仏像を安置する両扉の箱）“玉虫厨子”の復元プロジェクトを追いかけたドキュメンタリー。かつては玉虫色に光り輝く仏具だった玉虫厨子も、歳月を経たことでその美しさが徐々に失われつつあった。国宝再建のため、現代の宮大工たちが「玉虫厨子復元チーム」を結成、資金調達や玉虫の羽の収集にまでいたる情熱と苦悩の日々が始まった。三國連太郎がナレーションを務める。", "2008-11-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天皇ごっこ　見沢知廉・たった一人の革命", "天皇ごっこ　見沢知廉・たった一人の革命", "", "", "http://image.eiga.k-img.com/images/movie/57036/315x210.jpg?3099", 115, "「天皇ごっこ」「囚人狂時代」などを残し、2005年に46歳で自殺した反体制の作家・見沢知廉に迫ったドキュメンタリー。教育批判と破壊活動で早稲田高等学部を退学処分となった見沢は、78年・三里塚闘争で成田空港占拠闘争の最前線に出た後、新右翼に転向しゲリラ活動を指揮。82年にスパイ疑惑のあった同志を殺害した容疑で懲役12年となる。獄中で記した小説「天皇ごっこ」で文壇にデビューし、出所後の「囚人狂時代」はベストセラーとなるが、05年、自宅マンションから投身自殺してしまう。見沢を知る関係者などのインタビューや記録映像を交え、見沢が求めたものを探っていく。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE FEMME EST UNE FEMME", "女は女である", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "このシナリオは「勝手にしやがれ」以前に書かれており、ゴダール自身は“これが本当の意味での処女作”と語っている。まさにその言葉にふさわしい、映画を“撮ること”と“観ること”の喜びが全編を駆け抜ける、幸福感あふれる作品になっている。物語は実にシンプルで、ある同棲カップルの痴話ゲンカの顛末。“どうしても子供が欲しい”女と、“結婚するまで欲しくない”男が、共通の知り合いであるもう一人の男を巻き込んで、ドタバタ騒動を繰り広げる。白を基調としたポップな色彩、生々しくもたおやかなカメラワーク、レイアウト感覚とポエジーが冴える字幕の挿入と、後年のゴダールの“登録商標”が若々しい雰囲気でひしめき合う様は圧巻。映像、音響、音楽が同一方向を目指さず、互いを高め合うような作用を働かせる独自のスタイルは、既にこの時点で完成していたことがわかる傑作だ。", "1961-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHO’S THAT GIRL？", "フーズ・ザット・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「上海サプライズ」に続くマドンナの初主演作。「恋におぼれて」のG・ダンを共演に迎え、無実の罪で投獄されたヒロインが仮出所後、弁護士とともに真犯人を探す旅模様を快活に演じている。「コンフィデンス」のJ・フォーリーの演出も小粋。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("投奔怒海／BOAT PEOPLE", "望郷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ベトナム戦争終結から３年、一見平和なベトナム、なおも続く悲惨さがあった。日本人フォト・ジャーナリスト、芥川は、そんな中でも明るくたくましく生き抜こうとする14歳の少女チンと出会う。彼は危険を承知で少女を国外に脱出させようと試みるが……。「客途秋恨」の女流監督アン・ホイのヒューマニスティックな感動に満ちた傑作。", "1982-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE VIE INDE´PENDANTE", "ひとりで生きる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "V・カネフスキーの長編第２作で、前作「動くな、死ね、甦れ！」の続編。第二次世界大戦直後の極東ソビエトの収容所地帯、スーチャンに暮らす少年ワレルカの心の葛藤を描く。悲劇的な様相を呈する大人たちの生活、日本人捕虜との友情や死んだガーリャの妹ワーリャとの感情のすれ違いなど、彼をめぐって様々な人間模様が展開される。日に日に生活のバランスを失っていくワレルカは、ついにスーチャンを離れる決意をするのだが……。主演は前作同様、ワレルカにP・ナザーロフ、そして少女ワーリャにD・ドルカーロワを起用、成長期の少年少女の感情を見事に表現している。", "1991-01-01", "24,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霧の港の赤い花", "霧の港の赤い花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ヤクザ同士の縄張り争いが激烈をきわめている横浜。大親父と呼ばれる松前は、最近勢力を伸ばしてきた岩瀬組をたたこうと、刑務所から出たばかりの立花組の尻押しをする。松前としては、岩瀬組と立花組を対立させ、共倒れを狙っているのだ。だがいち早くそれに気づいた岩瀬は、ひそかに一の子分浩に松前を消すように命じるが……。横浜を舞台にしたアクションもの。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ガンダム　第08MS小隊　ミラーズ・リポート", "機動戦士ガンダム　第08MS小隊　ミラーズ・リポート", "", "", "http://ecx.images-amazon.com/images/I/51ZVZZSNYBL._SL210_.jpg", 50, "「機動戦士ガンダム」で描かれた一年戦争を舞台に、名もなき一般兵士たちの戦いを描いたOVA「機動戦士ガンダム　第08MS小隊」の8話までをまとめた総集編。連邦軍の新米小隊長シロー・アマダは、戦いのなかでジオン軍の女性パイロット、アイナ・サハリンと出会い、恋に落ちる。シローはやがて、軍人として戦うことに疑問を抱きはじめ、そんなシローを監査するため情報官アリス・ミラーが派遣される。", "1998-08-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAKING CARE OF BUSINESS", "ファイロファックス トラブル手帳で大逆転", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "一時はジョン・ベルーシの弟としてばかり注目を浴びたが、今は独自の道を歩み始めたJ・ベルーシが主演するコメディ。出所を二日後に控えた囚人ジミーは、野球狂の虫がおさまらず刑務所を抜け出し野球観戦に。そこで偶然一冊のシステム手帳（ファイロファックス）を見つけ、持ち主になりすましカードや小切手を使いたい放題。一方持ち主のスペンサーは仕事の鬼。命より大事なシステム手帳を失い、おまけに刑務所にまで入ることになる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("7 ans", "待つ女", "", "", "http://image.eiga.k-img.com/images/movie/34236/315x210.jpg?1600", 86, "囚人と妻とその愛人の愛と葛藤を描いた官能ドラマ。7年の刑を受け服役中の夫バンサンのため、週2回の面会に欠かさず通うメイテ。ある日、面会を終え刑務所を出た彼女に見知らぬ男が声をかける。ジャンと名乗るその男に車で送ってもらうことになったメイテは、成り行きから車の中でジャンと関係を持ってしまう。しかしジャンにはある秘密があり……。愛を求め葛藤するヒロインを「マルタ…、マルタ」のバレリー・ドンゼッリが熱演。", "2007-10-06", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUDGMENT IN BERLIN", "ジャッジメント・イン・ベルリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "“壁”が崩壊する以前の東ベルリンを舞台に、自由を勝ち取ろうとした東ベルリン市民の苦悩の姿を描く社会派ドラマ。西側へ亡命しようとする二人の男が偽造した身分証明証を受け取り、ポーランド国境へと向かう。だが、仲間が捕まり動揺した彼らは、ポーランドの旅客機をハイジャックし、西側へ着陸させようとする。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILL　ME　LATER", "キルミー・レイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "上司との不倫に嫌気がさし、屋上からの飛び降り自殺を決意した銀行員ショーン。ところが、そこに銀行強盗に失敗したドジな青年チャーリーが転がり込んでくる。ショーンは“後で殺される”ことを条件に、チャーリーの人質となって逃走するのだが……。個性派の若手女優S・ブレアがニヒルなヒロインに扮した初主演作。人生に行き詰まった男女の逃避行と奇妙な恋の成り行きを描くコメディだ。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TERRORISTS", "オスロ国際空港 ダブル・ハイジャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ノルウェーの首都オスロで、過激アナーキストの一味がイギリス大使官邸を乗っ取り、３人を人質に取る。要求はロンドンの刑務所に収容されている仲間の釈放と、彼らをオスロ空港に運ぶこと。そして一味をオスロから脱出させる飛行機を用意すること。オスロ保安庁の保安部長、タールビクは、流血の惨事を引き起こさずに、あくまでテロリスト一味を逮捕しようとするが……。若かりし頃のS・コネリーが、テロリストに立ち向かう主人公を熱演したサスペンス・アクション。", "1974-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聖職の碑", "聖職の碑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 153, "大正2年の夏。小学校の先生と生徒たちが中央アルプス登山中に暴風雨に見舞われ、ゴザで囲われただけの山小屋に避難することに。だが、ある生徒がパニック状態に陥り逃げ出してしまう。連鎖反応から生徒全員が危険な状況にも関わらず下山を試み、校長までが命を落とす惨事に……。本作公開前年の「八甲田山」と同じ原作者と監督が組んだ作品だが、こちらはよりヒューマニズムに焦点を当てた仕上がり。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pink Subaru", "ピンク・スバル", "", "", "http://image.eiga.k-img.com/images/movie/56046/315x210.jpg?3344", 93, "数年前に妻に先立たれたズベイルの願いは、愛する妹アイシャの幸せとあこがれのスバル車を購入すること。しかし、妹の結婚式が近づくある日、こつこつと貯めたお金でようやく購入したスバル・レガシィが盗まれてしまう。この悲劇にアイシャも結婚を取りやめようとするが、ズベイルは家族の幸せのためにレガシィ奪還を誓う。車泥棒が頻発するイスラエルの街タイベを舞台に、日本の新人監督・小川和也が描くヒューマンドラマ。", "2011-04-16", "4,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブワナ・トシの歌", "ブワナ・トシの歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "学術調査のため、タンガニーカに派遣されて来た青年は、先発隊と合流することができず様々な体験をする。青年役を渥美清が好演。この作品がきっかけで羽仁も渥美もアフリカにとりつかれた。アドベンチャー・人間ドラマとでも呼びたい１作。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kinky Boots", "キンキーブーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「フル・モンティ」「カレンダー・ガールズ」の系譜に連なるイギリス発の笑って泣ける感動コメディ。倒産寸前の田舎の靴工場を継ぐことになった優柔不断なチャーリーは、工場の再起を賭けて、街で偶然出会ったドラッグクイーンのローラと共に、ドラッグクイーンご用達のセクシー・ブーツ（＝キンキーブーツ）を作ろうと思い立つが……。「インサイド・マン」のキウェテル・イジョフォーがローラを好演。", "2006-08-26", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOM＆VIV", "愛しすぎて 詩人の妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "『荒地』で知られるノーベル賞詩人T・S・エリオットと、彼の最初の妻ビビアンの知られざる愛と悲しみの物語。1914年、イギリス・オックスフォードで、もの静かなアメリカ人の文学青年トムと、活発で才気あふれる上流階級の娘ビビの二人はたちまち恋に落ち、結婚する。だが、文壇でトムが名声を獲得していく一方で、ビビは誤った薬の服用から精神を病んでいき、彼女の異常な行動は次第に手に負えないものになっていく……。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若親分千両肌", "若親分千両肌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "“若親分”シリーズの最終作で、第１作の池広一夫監督が演出を担当した。南条武は、栄吉という男に間違われて何者かに襲われた。栄吉は宇島の青柳組親分の行方不明の息子であったが、その青柳組が建設中の海軍秘密工場が爆破され、秘密兵器の設計図が盗まれてしまう。その容疑は武に……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY GIRL", "マイ・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "勝ち気で元気いっぱいの11歳の女の子ベーダ。悩みなんか全然ありそうにもない彼女だが、独身のパパの前に美しい美容師が出現したことから心は揺れ動き始める。そんな時、彼女の支えになってくれたのは幼なじみのトーマス。やがて彼に対する感情は、淡く大きな恋へと発展して……。少年少女の初恋物語を、せつなくユーモラスに綴る心温まる作品。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("官能小説", "官能小説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "漫画家・藤井みつるの同名ベストセラーを、グラビアアイドルの大久保麻理子主演で映画化した官能ラブストーリー。商社で経理を担当する27歳のOL・彩は、その真面目すぎる態度から“小局”とあだ名をつけられて周囲から疎まれていた。そんなある日、彩は営業部の冴えない新人・椎野が副業として密かに官能小説を執筆していることを知ってしまう。やがて彩と椎野は恋に落ちるが……。共演は「仮面ライダー剣」の北条隆博。", "2007-02-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Die Frau mit den 5 Elefanten", "5頭の象と生きる女", "", "", "http://image.eiga.k-img.com/images/movie/57276/315x210.jpg?1704", 93, "ウクライナ出身の老翻訳家スベトラナ・ガイヤーの半生をたどりながら、文学によって高められる人間の尊厳を静かに描き出すドキュメンタリー。ウクライナに生まれ、第2次世界大戦初期にドイツへ移住したガイヤーは、ドフトエフスキーの長編5作をロシア語からドイツ語に翻訳し、世に知られるようになる。しかし、それ以降もガイヤーは自身の過去への問いかけを続け、ドイツ移住後に初めて訪問した故郷への旅の中で、ウクライナの激動の歴史と向き合う。", "2012-12-31", "26,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fanboys", "ファンボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/55336/315x210.jpg?1512", 90, "「スター・ウォーズ」の熱狂的ファンが繰り広げる冒険を描くコメディ。「スター・ウォーズ　エピソード1／ファントム・メナス」の公開直前の1998年、「スター・ウォーズ」オタクの高校生エリックとその仲間たちは、末期ガンで余命わずかの友人ライナスの「死ぬ前に『エピソード1』を見たい」という願いを叶えるため、ジョージ・ルーカス監督の本拠地スカイウォーカー・ランチに侵入し、「エピソード1」のフィルムを盗み出そうとする。本家シリーズから、キャリー・フィッシャー、レイ・パークらがカメオ出演。日本では、ファンによる公開署名活動により劇場公開が決定した。", "2010-04-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DARK ANGEL", "ダーク・エンジェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "第一次大戦下で、二人の男性から求愛されその間で揺れる女性の苦悩と、真実の愛を描いたラブ・ロマンス。彼女は、一方の男性を愛していたが、彼は前線で行方不明となってしまう。やがて戦争が終わり、彼女はもう一方の男性と婚約するが、愛する彼は盲目の身となって生きていた。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("W.", "ブッシュ", "", "", "http://image.eiga.k-img.com/images/movie/53996/315x210.jpg?3177", 130, "「JFK」（91）でアメリカ第35代大統領ジョン・F・ケネディを、「ニクソン」（95）で第37代大統領リチャード・ニクソンを描いたオリバー・ストーン監督が、現職（映画製作時）の第43代大統領ジョージ・W・ブッシュを描いた伝記映画。父親で元大統領のジョージ・H・W・ブッシュとの確執の他、パーティ三昧だった青年時代にも焦点を当てている。主演は「ノーカントリー」のジョシュ・ブローリン。", "2009-05-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("王様ゲーム", "王様ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/56826/315x210.jpg?4054", 82, "「ハロー！プロジェクト」の人気アイドルグループ「Berryz工房」と「℃-ute」のメンバーが総出演するケータイ小説発のホラー。ある日、智恵美と幼なじみの伸明のもとに、「王様」と名乗る者から「指示に従わなければ罰を与える」というメールが届く。メールはやがてクラス全員に届くようになり、内容は次第にエスカレート。ついに命令に従わない生徒に罰が下され、それを皮切りに次々と生徒たちが抹消されていく。智恵美役の熊井友理奈（Berryz工房）とクラスメイトの謎めいた少女・莉愛役の鈴木愛理（℃-ute）がダブル主演。", "2011-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("未来への伝言", "未来への伝言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "1960年頃から世界中を襲った小児マヒ“ポリオ”から子供たちを救おうとする母親の姿を描いた日ソ合作のヒューマン・ドラマ。ポリオによって長男を死なせてしまった圭子は、ソ連でそれを阻止する生ワクチンが生産されていることを知り、やはり同じポリオにかかっている次男を連れてモスクワのグーセフ博士のもとへ行くが……。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POSTCARDS FROM THE EDGE", "ハリウッドにくちづけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "映画の都ハリウッドを舞台に、昔日の栄光に浸りきった元大スターの母と、頭打ちでドラッグに溺れる娘という二人の女優のリアルな人生像を描いた作品。往年の女優デビー・レイノルズを母に持つC・フィッシャーが、麻薬に溺れていた自らの恥部をさらけ出し、絶望の淵からの回復を綴った小説『崖っぷちからのはがき』の映画化。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裸の十九才", "裸の十九才", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "世間を震撼させた“連続射殺魔事件”に材をとった新藤兼人監督の代表作。犯人である19歳の青年の貧しい生いたちや、集団就職で上京してからの生活がさりげなく描かれる一方、幼くして別れた母の存在がきわめて強調されている。青年役の原田大二郎が好演。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブレター　蒼恋歌", "ラブレター　蒼恋歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "人気バンド「ザ・ブルーハーツ」の名曲「ラブレター」から生まれた青春ラブストーリー。福祉を学ぶため東京の大学への進学を考えている由衣は、大学の下見をしようと東京へやって来る。一方、バンドマンの良太は、本気でやりたいことが見つからず、無為な毎日を送っていた。バンド「ザ・サリンジャー」のライブで出会った2人は互いにひかれあい、再会を約束するが……。主演は、「あずみ」の石垣佑磨と「スウィングガールズ」の本仮屋ユイカ。", "2006-11-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NUMBER ONE FAN", "ポイズン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ハリウッドの人気アクション・スター、ゼーンは婚約者のホリーと一緒に出かけたパーティーでブレアーという女性に出会う。少女の愛らしさと妖しい色気を併せ持つブレアーに、ゼーンは惹かれてしまう。数日後、ブレアーがゼーンの前に現れ、二人は体を重ねる。ゼーンにとってはゆきずりの遊びのはずだった。だが、彼のファンであるブレアーは“愛している”という彼の言葉を盾に結婚を迫り、セックスを強要するがゼーンはそれを拒絶する。その時、彼女の中で狂気がはじけるのだった。真綿で首を締めるようにジワジワと恐怖を感じさせるサスペンス・アクション。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Casino Royale", "007／カジノ・ロワイヤル（1967）", "", "", "http://ecx.images-amazon.com/images/I/519J57YT2AL._SL210_.jpg", 134, "\"007\"パロディ満載の番外編。各国のスパイ暗殺を画策する国際陰謀団スメルシュを壊滅すべく引退した名スパイ、ジェームズ・ボンド卿宅に集まる各国諜報機関の幹部たち……。カジノを舞台に何人ものボンドが登場するアクション・コメディ。ニーブンの起用は原作者イアン・フレミングの希望によるもの。", "1967-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMMA IN PARIS", "不倫への招待", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "韓国のグラマラスな売れっ子女優が、旅先のパリで体験する官能の日々。シルヴィア・クリステルが演じて大ヒットした、あの「エマニエル夫人」の東洋版ともいえる異色作。主人公エマを演じるのは、韓国のセクシー女優、ヘリー・オー。彼女の熱く激しい演技に注目。", "1988-01-01", "2,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続組織暴力", "続組織暴力", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "一匹狼のヤクザ・兵頭五郎は、表向きは正当な法の手続きをとって銀座商事を設立。手広く仕事を始め、古くから銀座を縄張りにしているヤクザと何度か流血事件を起こす。北川警部は兵頭を逮捕するが、彼のバックには政界の大物がついていた……。刑事もまた暴力団ぽく、逆にそれがリアルさを増している。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT’S ALWAYS FAIR WEATHER", "いつも上天気", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「雨に唄えば」の名コンビ、G・ケリーとS・ドーネンによるミュージカル映画。無事に除隊した３人のGI。10年後に同じ酒場の同じテーブルでの再会を約束し、それぞれの生活に戻った。ボクサーのマネージャー、TV会社の重役、食堂経営者と三人三様の道を歩んだ彼らは無事に再会を果たすが……。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DAY OF THE LOCUST", "イナゴの日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "「真夜中のカーボーイ」の製作・監督・脚本トリオが、黄金時代の“夢の都”ハリウッドの実態と、その中で空しい夢にほんろうされる人間たちの姿を、赤裸々に描き出した重厚で辛らつな人間ドラマの秀作。1930年代末のハリウッド。撮影所の美術部に就職したトッドは、安アパートの向かいに住むエキストラ女優フェイに夢中だ。彼女は大スターを夢見る奔放な娘。ひょんなことから知り合った内気な男ホーマーと同棲をはじめた彼女だったが、たちまち浮気。意気消沈のホーマーは、故郷に帰ろうと、大劇場の前を通りかかって……。イナゴさながらに殺到するラストの群衆シーンが圧巻だ。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("全然大丈夫", "全然大丈夫", "", "", "http://image.eiga.k-img.com/images/movie/53276/315x210.jpg?1557", 110, "マイペースで不器用な3人の男女が織り成す恋愛模様を、荒川良々、岡田義徳、木村佳乃共演で描いた脱力系ラブコメディ。古本屋の長男・照男の夢は、世界一恐いお化け屋敷をつくること。もうすぐ30歳だというのに遊んでばかりいる冴えない照男は、同じく人生の壁にぶち当たっている幼なじみのサラリーマン・久男に説教され、夢の実現に向けて一念発起する。そんな2人の前に、信じられないほど不器用な女性・あかりが現われ……。", "2008-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浪人街（第１話～第３話）", "浪人街（第１話～第３話）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "無声映画末期を飾る不朽の時代劇。1928年、マキノ映画は所属のスターが大量脱退するという大ピンチを迎えていた。苦肉の作としてスターの出ない群衆劇を作ることになり、若きマキノ雅広（20歳）が気鋭のシナリオ作家・山上伊太郎（23歳）の協力を得て第１話「美しき獲物」を作り上げた。希望のない浪人たちがごろごろ寝ころんで日々を過ごしており、悪い旗本に立ち向かう時だけ団結するという、エゴイズムとニヒリズムに満ちた内容のユニークさに加えて、奔放に移動しパンするカメラ（撮影はこちらも気鋭の三木稔）の驚くべき尖鋭さ、そして何よりも全編にみなぎる若々しさによって一躍注目されることとなり、当時の青年層の心情とマッチした本作はキネマ旬報ベストワンも獲得してしまった。続いて第２話「楽屋風呂」、第３話「憑かれた人々」が作られ、マキノの長いキャリアの中で第一期黄金時代を築き上げた。ただし現存するフィルムは第１話・第２話の編集短縮版のみなのが残念。", "1928-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lilacs", "ラフマニノフ　ある愛の調べ", "", "", "http://image.eiga.k-img.com/images/movie/53426/315x210.jpg?3157", 96, "ピアニストとしても成功を収めた天才作曲家、セルゲイ・ラフマニノフの数奇な人生を描いた伝記ドラマ。故郷ロシアでの波乱に満ちた日々と革命によるアメリカへの亡命、アメリカでの成功とその陰に潜んだ苦悩、さらに人生を変えた3人の女性との愛など、不滅の名曲誕生の裏側に隠されたドラマを美しい旋律と共に紡ぎだす。メガホンを取るのは、「タクシー・ブルース」でカンヌ国際映画祭最優秀監督賞を受賞したパーベル・ルンギン。", "2008-04-19", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道黒社会 レイニードッグ", "極道黒社会 レイニードッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "台北を舞台にアウトローの闘いを描くハードボイルド・アクション。台湾マフィアのボス、ホウに雇われ、ヒットマンとして暮らす優児は、ある日、昔の女から息子だと言われ、一人の少年チェンを引き取る。そんな折、彼はホウから対立組織のクーの暗殺を依頼された。娼婦リリーの部屋で敵の動向をうかがっていた彼はついにクーの殺害に成功する。だが、ホウの裏切りによって追われる身となった優児はチェンとリリーとともに逃避行を始めることに……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IS PARIS BURNING？", "パリは燃えているか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 173, "第二次大戦末期、ドイツ軍占領下のパリ。パリ市民側では、ドゴール将軍の部下たちと過激派が街の防衛計画をめぐって対立していた。そこへ双方とも期待していた連合国軍が、パリを通らないという情報が入り……。パリ市民によるレジスタンス活動から、連合国軍によるパリ解放までの最後の２週間を、当時の映像も交えたドキュメンタリー・タッチで描いた超大作。英米仏独から、まさに“超豪華”と呼ぶにふさわしい俳優陣が結集している割に、誰も個性を発揮できなかったきらいがあるが、それがかえって中心となるヒーローがいない戦争の現実を表している。なお脚本には若きF・F・コッポラが参加している。", "1966-01-01", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上訪", "北京陳情村の人々", "", "", "http://image.eiga.k-img.com/images/movie/55046/315x210.jpg?4712", 124, "土地の強制収用や役人の腐敗といった地方政府への不満を中央政府に陳情するため、中国各地から北京へとやって来た多くの人々。様々な想いを胸に北京に長期滞在することになった彼らの、1996年から2008年の北京オリンピック前までの姿を捉え、中国における急激な経済発展の裏に潜む深刻な問題を浮き彫りにしたドキュメンタリー。監督は本作が長編デビューとなるチャオ・リャン。第62回カンヌ国際映画祭にて上映された。", "2009-11-26", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Volcano High", "火山高", "", "", "http://image.eiga.k-img.com/images/movie/1736/315x210.jpg?3771", 108, "韓国の映画会社と「プレミア」誌の脚本コンクールの応募作を、本作が3作目の60年生まれ、俊英キム・テギュン監督が映画化。高校を舞台に、生徒と教師が＜気＞と武術で過激な闘いを繰り広げる学園VFXアクション。秘伝書「師備忘録」を巡って教頭や生徒らの争奪戦が展開中の火山高。超人的なパワーを持つ転校生キムは、そのパワーのせいで8つの学校を退学処分になり、今度こそ卒業するため、その力を自ら封じていたのだが。", "2002-12-14", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Parade", "パラード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「ぼくの伯父さん」シリーズのジャック・タチが、スウェーデンのテレビ局に招かれて撮影したコメディで、タチの遺作となった。タチ扮するロワイヤル氏一座のサーカスは、家族連れで大賑わい。劇、演奏、手品、曲芸、さらにタチが得意とするパントマイムなど、様々なパフォーマンスが繰り広げられる。やがて裏方の職人や観客たちもショーに参加し始め、人々が一体となって盛り上がりを見せる。1975年度フランス映画大賞を受賞。", "1974-01-01", "2,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Anne of Green Gables", "赤毛のアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "　1908年の出版から、今なお世界で愛読されているルーシー・M・モンゴメリーの小説を自然情景たっぷりに映像化。年老いたマリラとマシューの兄妹は、孤児院から男の子の養子をもらうことに。しかし迎えに向かった駅舎にいたのは、赤毛のおさげを結わえたやせっぽちの少女アンだった。アン役のオーディション当時、16歳だったミーガン・フォローズはこの作品で一躍人気スターに。マリラ役はカナダを代表する大女優、コリーン・デューハースト。", "1986-01-01", "10,1,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SEVEN−UPS", "重犯罪特捜班　ザ・セブン・アップス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "題名になっている“セブン・アップス”とは、刑期が７年以上の重犯罪者のことである。そういう犯罪者たちがかかわる特別犯罪のみを手掛ける、非公式捜査班の刑事を主人公にしたポリス・アクションの正統派といった作品。実在の刑事S・グロッソーの体験をもとに脚本が作られただけに、リアリティーあふれる緊迫したドラマが展開する。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES ANGES GARDIENS", "俺たちは天使だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "善良な神父と強欲な男の前に、本人たちとは人格正反対の守護天使が登場して騒動を巻き起こすコメディ。香港の旧友から、子供と大金の持ち出しを頼まれたパリのミュージック・ホールの支配人カルコ。だが、香港に到着するとすでに旧友はギャングに殺され、カルコも彼らから追われるハメに。そこで彼は、パリに帰るところだったカトリック教団の神父ラタンに助けを求める。神父のおかげで無事パリに到着したカルコだが、神父に旧友の息子バオを押しつけ大金だけ奪おうとすると、突如二人と性格が正反対の守護天使が現れ……。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("完全なる飼育", "完全なる飼育", "", "", "http://ecx.images-amazon.com/images/I/21VR1T3D21L._SL210_.jpg", 96, "「ハリマオ」以来10年ぶりにメガホンを取る和田勉が、実在した事件を映画化。中年男と彼に誘拐された女子高生の間に芽生える奇妙な愛情を、肉欲的場面をふんだんに織り込みながら描く。女子高生のクニコがある日、中年男性の岩園に誘拐される。岩園は心と身体が結ばれた完全な愛が欲しいと、彼女を部屋に監禁する。初めは激しく抵抗したクニコだったが、かいがいしく世話をする彼の優しさに触れて次第に心を開き、彼に“飼育”されることを受け入れるのだった……。", "1999-01-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Global Metal", "グローバル・メタル", "", "", "http://image.eiga.k-img.com/images/movie/53766/315x210.jpg?2870", 93, "メタル音楽のルーツに迫ったドキュメンタリー「メタル／ヘッドバンガーズ・ジャーニー」のサム・ダン、スコット・マクフェイデン監督が、再びメタルを題材にしたドキュメンタリーを製作。本作では、日本を始めアジア・南米・中東へと足を運び、アイアン・メイデン、メタリカ、スレイヤー、YOSHIKI（X-JAPAN）ら各国メタルシーンを代表するアーティストやそのファン達への取材を敢行。KISS、ディープ・パープルなどの音楽が映画全編を飾る。", "2008-08-23", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GENDARME ET LES GENDARMETTES", "ルイ・ド・フュネスの大奪還", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "おなじみ、サントロペ分署の巡査長クリュショが登場する爆笑のポリス・コメディ第６作。お色気ムンムンの魅力的な４人の婦人警官がサントロペ分署に配属された。ところがそのうちの二人が誘拐されてしまい……。ブラックな笑いをひっさげて名優フュネスが珍活躍。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOURIR D’AIMER", "愛のために死す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "高校の女教師ダニエルは30歳、夫と離婚し二人の子持ち。彼女は17歳の教え子と愛し合うようになり、周囲の反対にも負けず情熱は燃え上がるばかり。ついに彼女は未成年誘拐の罪で告訴されてしまう。裁判にまで発展したセンセーショナルな実話を映画化した問題作。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN WITH THE GOLDEN ARM", "黄金の腕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "シカゴの麻薬療養所を退院したフランキーはカタギの仕事に就こうとするが、以前の仲間の罠にはまり、再びトランプ博賭師をすることになる。“黄金の腕”とまで呼ばれたカードさばきの技術を持つフランキーだったが、手が震えて思うようにカードを配ることができない。仲間には麻薬を打てと勧められても、フランキーは断固として断わる。しかし手の震えは日に日にひどくなり、やがて彼は死の苦しみにのたうちまわるようになる……。麻薬中毒者の陰惨な生活を、残酷なまでに冷めたタッチで描いた鬼気迫る心理サスペンス。特に主演のF・シナトラの、にこやかな歌手姿からは想像もできない暗い演技は印象的だ。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FOURTH PROTOCOL", "フレデリック・フォーサイス 第四の核", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "1987年、晩冬のモスクワ。タカ派で知られるKGB議長ゴボルシンは、西側世界転覆を狙う危険な野望——“オーロラ計画”を打ち立てた。それはモスクワ内部でも秘密裡に進行し、作戦の実行者、KGB工作員ペトロフスキーは議長直々に指令を受けた。同じころ、英国MI−5に所属するプレストンは度重なる上司との衝突から港湾担当へと左遷の憂き目を見る。だが、そこで彼は東側船籍の船員の事故死に遭遇し、恐るべき物質を発見する。ポロニウム、それは核爆弾の起爆装置となる。この物質は英国に潜入したペトロフスキーに渡るはずの物だった。フォーサイスのベストセラー小説の映画化で、ケインとブロスナンの対決も見もの。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIRLS！GIRLS！GIRLS！", "ガール！ガール！ガール！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "プレスリー＆N・タウログ監督コンビによる娯楽映画の快作。この作品でもプレスリーのヒット曲が全13曲フィーチャーされている。亡くなった父とともに造ったヨットを買い戻すため、漁船の雇われ船長をしているロス。ところが目当てのヨットが売りに出されることになり、焦ったロスはナイトクラブの歌手として契約し、歌いまくる……。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAST DANCE", "ラストダンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "死刑執行まであと30日の女性死刑囚シンディの前に一人の男性が現れる。それは、恩赦の申請をするために派遣された若き弁護士リチャードで、形式だけの仕事をするはずだった。しかし彼は、シンディの深い絶望に心動かされ、周囲の反対を退けて事件を再調査する。不自然な状況で死刑判決が下されたということを知った彼は、刑の執行を中止するために闘うのだった……。過去の裁判の不正を暴くというサスペンスとしての醍醐味に加え、死刑の是非を問う真摯なテーマ性は観る者の心を打つ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bruce Almighty", "ブルース・オールマイティ", "", "", "http://image.eiga.k-img.com/images/movie/1316/315x210.jpg?1493", 101, "地方TV局のレポーター、ブルースは仕事に不満、神に悪態をつくと神が出現、「私の仕事に不満があるなら君がやれ」と言われて全能の存在に。そこでブルースがやることは……。「エース・ベンチュラ」で共にブレイクしたジム・キャリーとトム・シャドヤック監督が「ライアー・ライアー」に続き3度目の顔合わせ。 撮影は「ダンス・ウィズ・ウルブズ」でオスカー受賞のディーン・セムラー。", "2003-12-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AK ドキュメント黒澤明", "AK ドキュメント黒澤明", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "フランスの映画監督、クリス・マルケルが、黒澤監督の「乱」の撮影風景を収めたドキュメンタリー映画。「乱」の製作過程をつぶさに追い、監督の指示のもと、情熱を傾けて取り組むスタッフや出演者を捉えている。黒澤のイメージがいかにして具現化されるかを浮き彫りにしている。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Body of Lies", "ワールド・オブ・ライズ", "", "", "http://image.eiga.k-img.com/images/movie/53176/315x210.jpg?2014", 129, "レオナルド・ディカプリオ＆ラッセル・クロウ主演のスパイ・スリラー。中東で対テロ殲滅作戦を展開する米CIA中東局の主任ホフマン（クロウ）と現地工作員フェリス（ディカプリオ）。ヨルダン情報局GIDとともに爆破テロ組織のリーダー、アル・サリームを追う2人は、反目し合いながらも協力し、中東に架空のテロ組織をでっち上げ、アル・サリームをおびき出そうと試みるが……。脚本は「ディパーテッド」のウィリアム・モナハン。監督は巨匠リドリー・スコット。", "2008-12-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超劇場版ケロロ軍曹", "超劇場版ケロロ軍曹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "子どもから大人まで幅ひろい世代から人気を集めるテレビアニメ「ケロロ軍曹」の劇場版。ケロン星から地球侵略に来たものの、居候先の日向家ですっかり地球暮らしをエンジョイしていたケロロ軍曹と4人の部下たち。そんなある日、ガンプラを買いに行った帰り道に不思議な祠（ほこら）を見つけたケロロと冬樹は、中にまつられていた壺（つぼ）をうっかり壊してしまう。実はその壺には、古代ケロン人が開発した最終兵器キルルが封じられており……。", "2006-03-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Rouge et Le Noir", "赤と黒　デジタルリマスター版", "", "", "http://image.eiga.k-img.com/images/movie/54936/315x210.jpg?1854", 189, "フランスの文豪スタンダールの小説をクロード・オータン＝ララ監督が映画化した「赤と黒」（54）のデジタルリマスター版。主演はジェラール・フィリップ、共演にダニエル・ダリュー、アントネラ・ルアルディほか。1820年代、フランスの小都市べリエールに暮らす聡明な青年・ジュリアンは、貧しい家庭に育つが、シェラン司祭の推薦で町長レナル家の家庭教師となる。そこで夫人と恋に落ちたジュリアンだったが、スキャンダルの発覚を恐れて神学校へと進む道を選ぶ。", "2009-11-28", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飛鷹", "シルバーホーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "アジアを代表するアクション女優ミシェル・ヨーが、「東京攻略」のジングル・マ監督とタッグを組んだSFアクション。近未来都市ポラリス・シティー。大富豪のルル・ウォンには、メタリックスーツに身を包んで悪をなぎ倒す正義のヒロイン「シルバーホーク」という別の顔があった。一方、シルバーホークの活躍を快く思わないリッチマン警視は、その正体を暴くべく躍起になっていた。そんな彼らの前に、世界征服を企む悪の組織デスクルセイダーが立ちはだかり……。", "2005-12-10", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ROMANTIC ENGLISH WOMAN", "愛と哀しみのエリザベス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "ドイツで休暇を過ごすエリザベス。夫のルイは人気作家で生活は満たされているが、彼女の心には虚しさが残る……。名匠J・ロージー監督と、M・ケイン、H・バーガーら国際派オールスター・キャストが出演したミステリー・ロマン。", "1975-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三つの竜の刺青", "三つの竜の刺青", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "久々の陸で、船長とともに飲んだ錠次。何者かによって翌朝、船長は殺され錠次はピストルを握って昏睡していた。なんとしても自分で犯人を見つけたい錠次は、かつての船長の言葉をヒントに捜査を始める……。宍戸錠主演のアクション作。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOLIDAY", "素晴らしき休日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "G・キューカーが戦前に撮り上げた、ソフィスティケイトされた作風が魅力のロマンティック・コメディ。遺産で大金持ちになった男女が結婚し、新婚旅行に旅立つが……。若き日のK・ヘプバーンとC・グラントの二人によるアメリカ映画黄金時代の作品。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELL DRIVERS", "地獄特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "通称ヘル・ドライバーと呼ばれている、ジャリ運搬車運転手たちが、会社側の不正に立ち向かい、社長に対し果敢に戦っていく。S・ベイカーが正義感に燃えるトラック運転手を熱演。D・マッカラム、S・コネリーらも脇で若い姿を見せてくれる。", "1957-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Svet-Ake", "明りを灯す人", "", "", "http://image.eiga.k-img.com/images/movie/55196/315x210.jpg?2683", 80, "純朴な人柄で村人たちから愛される電気工の夢は、風車を作って村中の電力をまかなうことと、息子を授かること。しかし、国の政治が乱れるある日、都会から私腹を肥やそうと企む者がやってきて……。キルギス出身のアクタン・アブディカリコフ監督が、キルギス名の“アクタン・アリム・クバト”名義で「旅立ちの汽笛」（2001）以来7年ぶりに手がけたドラマ。ソ連からの独立を経て、昔ながらの生活習慣が残るキルギスの田舎町を舞台に、政治的革命に翻弄されながらもつつましく生きる人々の姿を描く。", "2011-10-08", "77,2,7,4,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緋牡丹博徒・花札勝負", "緋牡丹博徒・花札勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "渡世修行を積むお竜は、名古屋の西之丸一家へワラジを脱ぐ。対立する金原一家は国会議員と結託し、名古屋一の貸元の座を狙っていた。お竜は若い男女の恋を叶え、たちの悪い金原一家に殴り込む。ニセお竜を演じる沢淑子が好演。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tinker Tailor Soldier Spy", "裏切りのサーカス", "", "", "http://image.eiga.k-img.com/images/movie/57526/315x210.jpg?3466", 127, "スパイ小説の大家ジョン・ル・カレの代表作を「ぼくのエリ 200歳の少女」のトーマス・アルフレッドソン監督、ゲイリー・オールドマン主演で映画化したスパイスリラー。1960年代のロンドン。ある作戦の失敗でイギリスの諜報機関サーカスを引責辞職したジョージ・スマイリーに、ある日特命が下される。それは、いまもサーカスに在籍する4人の最高幹部の中にいる裏切り者＝2重スパイを探し出せというものだった。共演にコリン・ファース、トム・ハーディ、ジョン・ハートほか。", "2012-04-21", "2,3,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STROGOFF", "栄光への戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ヴェルヌ作『ミシェル・ストロゴフ』の映画化。19世紀末、タタール人の侵入により戦場と化したシベリアを舞台に、皇帝の密命を受け愛国心に燃える中隊長と若き人妻の悲恋を、スペクタクルな場面を織り交ぜて描く。監督は、名匠ルキノ・ヴィスコンティの甥。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが命の唄・艶歌", "わが命の唄・艶歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "五木寛之の『艶歌』を映画化した舛田利雄監督作品。ヒット競争に明け暮れるレコード界の内幕を暴きながら、艶歌に生涯を賭けた男たちの人間像を描き出し、艶歌と日本人の心情との関係を考えさせる。売り出し中の新人歌手に扮した水前寺清子が好演。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SINGER NOT THE SONG", "黒い狼～ローン・ウルフの決闘～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "メキシコ、カンタナの町を舞台に、D・ボガードが残忍非情な無法者アナレクトに扮し、西部劇アクションを繰り広げる娯楽作品。善玉と悪玉の対決という定石通りの設定だが、たっぷり詰め込まれた理屈抜きの痛快さと語り口のうまさで観る者を飽きさせない。", "1960-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガラスの中の少女", "ガラスの中の少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1960年、吉永小百合と浜田光夫のゴールデンコンビでも映画化された有馬頼義の原作を現代的にアレンジし、当時のアイドル、後藤久美子と吉田栄作のニューコンビで再映画化。国務大臣を父に持つお嬢様育ちの靖子と、危険な仕事に手を染めている不良っぽい少年、陽一の汚れなき純愛を、大人たちの思惑を絡めて描く。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sweet November", "スウィート・ノベンバー", "", "", "http://ecx.images-amazon.com/images/I/512KRKHZNSL._SL210_.jpg", 120, "一流広告会社のエリート、ネルソンはある日、正体不明の不思議な女性サラと出会う。不幸な男性を救うのが得意と言うサラは11月の１ヵ月間だけ一緒に暮らそうと奇妙な提案をし、その間は仕事を一切しないという条件を付ける。最初は相手にしないネルソンだったが、思いがけないトラブルから会社をクビになり、結局は彼女と暮らすことに。仕事と成功しか頭になかったネルソンは、サラと過ごすことによって生きる喜びを見つけていくと同時に、本気で彼女を愛し始める。しかし、自由気ままに見える彼女にはある秘密が……。1968年製作の「今宵かぎりの恋」の舞台設定を現代に置き換え、K・リーヴス、C・セロンという美男美女の組み合わせで描く悲しい愛の物語。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緋鯉大名", "緋鯉大名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "毎年１回将軍が松坂家にやってきて鯉を賞味する“包丁式”のために、“お鯉様御養育”係を命じられ、松坂藩江戸屋敷に赴任した檜権之助。藩では人情派の家老が実権を握っていたが、次席家老がその失脚を狙っていた。いよいよ包丁式という日、池の鯉が何者かによって毒殺される。鯉を使った、ユニークな視点の人情料理(!?)時代劇。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("指導物語", "指導物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "熊谷久虎が戦時下に撮った国策映画。軍用列車を運転するため３ヵ月にわたる訓練を受ける鉄道隊の青年兵と、その指導にあたる老機関士の姿をドキュメンタリー・タッチで描いていく。本作を撮ったあとの熊谷は国粋主義指導団体“すめら塾”を結成、1953年まで新作のメガホンを取ることはなかった。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE REAL McCOY", "ブロンディー 女銀行強盗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "足を洗った凄腕の女金庫破り、カレンの一人息子が、ギャングのジャックにさらわれた。彼の要求は、800万ドルの強奪計画に手を貸すこと。子供の命を救うため、また過去に恨みのあるジャックに復讐するため、カレンは危険な賭けに挑んでいく。K・ベイシンガーのクールな魅力が光るサスペンス・アクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SR サイタマノラッパー", "SR サイタマノラッパー", "", "", "http://image.eiga.k-img.com/images/movie/54376/315x210.jpg?2906", 80, "埼玉県の田舎町を舞台に、ラッパーを目指す冴えない若者たちの奮闘を描いた青春映画。レコード屋もライブハウスもない田舎町で結成されたヒップホップグループ“SHO-GUNG”の仲間たち。彼らは地元の先輩たちの協力を得て初ライブを実現させようとするが、東京でAV女優として活躍していた同級生・千夏が戻ってきたことから、グループ内ですれ違いが生じるようになり……。監督は「JAPONICA VIRUS」の入江悠。", "2009-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FEMME D’A｀ CO＾TE´", "隣の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "かつて愛し合い、今は別れてしまった男女が偶然、隣人同士になってしまう。すでに互いに家庭を持っている二人が、戸惑いつつも再び恋の炎に身を焦がす。男女の心の機微を描き続けてきたトリュフォーが、最後のパートナーとなったアルダンと出会い、激しく狂おしい大人の恋を見事に映像化。", "1981-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("肉食系女子。", "肉食系女子。", "", "", "http://image.eiga.k-img.com/images/movie/55216/315x210.jpg?1345", 0, "2009年に流行した勝気で積極的な女性を形容する言葉「肉食系女子」をモチーフにしたコメディ。主演は加護亜依。広告代理店に務める狩子は、物怖じしない性格で次々と狙った男を落とすキャリアウーマン。そんなある日、部下として配属された穏やかな青年・山口に、今までに経験したことのない戸惑いを感じ始める。", "2010-02-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("首領の道", "首領の道", "", "", "http://ecx.images-amazon.com/images/I/51SnQwV-brL._SL210_.jpg", 79, "土山しげる、観月壌原作の同名コミックを小沢仁志主演で実写化した任侠アクション。連城組2代目の次男・恭次は、現在は広告代理店の社員として堅気の生活を送っていた。そんなある日、狩野組との抗争の中で兄・浩一が命を落とし、さらに父である組長までもが殺されてしまう。かつて「ハマの狂犬」と恐れられていた恭次は、復讐を胸にひとり立ちあがるが……。監督は「修羅の群れ」の辻裕之。共演は中野英雄、哀川翔ほか。", "2011-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極悪坊主 念仏人斬り旅", "極悪坊主 念仏人斬り旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "若山富三郎主演の“極悪坊主”ものの第３作。今までの悪行を悔いて、四国八十八ヶ所の旅に出た、僧・真海。彼は立ち寄った漁村で、３年前に自分が殺した室道鬼の未亡人と弟に出会う。復讐を誓う二人をなんとか説き伏せたものの、そこに政治結社、血友社がやって来る。村の人々を食いものにしようとする血友社に、真海の怒りが爆発して……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pola X", "ポーラX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "「ポンヌフの恋人」のレオス・カラックスが、「白鯨」で知られるアメリカ人作家ハーマン・メルビルの「ピエール」を映画化。才能に溢れた小説家ピエールは、豊かなノルマンディの自然に囲まれた城に住み、美しい婚約者を持つ青年。しかし、ある日姉と名乗る長い黒髪のジプシー女に惹かれた彼は、すべてを失っていくのだった。出演は、ギョーム・ドパルデュー、カトリーヌ・ドヌーブ、カテリーナ・ゴルベワほか。", "", "2,7,26,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN THE ARMY NOW", "イン・ザ・アーミー　こちら最前線、異常あり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "生まれながらのお調子者ボーンズは、オーディオ・ショップをクビになり失業してしまう。そこで、給料をもらえて、兵役は年間たったの二週間しかないという陸軍予備兵に志願する。ところが、入隊してみればそこには地獄の訓練が待っていた。なんとか訓練はクリアしたものの、今度は前線基地へと召集されて……。MTVの人気パーソナリティーとして知られるコメディアン、P・ショアがスキンヘッドに大変身。ノーテンキなソルジャー・ボーイ役に挑戦している。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MADE IN JAPAN こらッ！", "MADE IN JAPAN こらッ！", "", "", "http://image.eiga.k-img.com/images/movie/56966/315x210.jpg?3168", 84, "「プロと学生が共同で映画を企画・製作する」という京都造形芸術大学映画学科のプロジェクト「北白川派映画芸術運動」の理念にのっとり製作されたホームドラマ。同学科教授でもある「禅 ZEN」「BOX 袴田事件　命とは」の高橋伴明監督がメガホンをとり、同学科1回生の大西礼芳が、女優の松田美由紀とダブル主演を務めた。何事にも無関心な18歳の雛子は会社員の父・完治とホームヘルパーをしている母・春子と暮らしていた。しかし、祖母・妙の死をきっかけに完治が家に引きこもるようになり、次第に家族はバラバラになっていく。", "2011-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録ヒットマン・妻その愛", "実録ヒットマン・妻その愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "実在の人物をモデルにした山之内幸夫の小説を映画化。大山組系若宮組組長、若宮敬三は自分を襲ったヒットマンを射殺するが、現場に現れて動揺したその男の姉、啓子に肩を撃ち抜かれる。以来、若宮は啓子の世話をすることになるが、そんな若宮に彼女は惹かれていった。若宮にヒットマンを送り込んだ松波組組長は彼をつぶそうと新たな策を展開。怒り心頭に達した若宮は周りに迷惑をかけないようにと組を解散、自らがヒットマンとなって松波を狙い始める……。ヒットマンの道を選んだ若宮と彼を支え続ける妻、啓子が二人三脚で任侠道を歩む姿を描く。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かぶりつき人生", "かぶりつき人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "田中小実昌の同名小説を映画化した神代辰巳監督のデビュー作。母親への反抗心から自らも母と同じストリッパーの道を歩んだ娘が、すでに盛りを過ぎた母親を次第に追い越していく姿を描く。雑草のように強く愛おしいヒロインを、のちの神代夫人、殿岡ハツエが熱演。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　NOMI　SONG", "ノミ・ソング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "エイズによって世を去った天才アーティスト、クラウス・ノミのドキュメンタリー。エイリアンのような風貌、ニューウェイヴ風のポップサウンドに乗ったオペラ歌唱で知られる彼の強烈な個性に、関係者の証言を通じて迫る。デヴィッド・ボウイとの共演など、貴重な映像も見ものだ。N.Y.のアンダーグラウンドから飛び出したアーティスト、クラウス・ノミ。彼は独自の音楽や、奇抜なヴィジュアル・センスで名声を手にするが……。", "2005-06-25", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PALOMA", "ラ・パロマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "シュミットならではのノスタルジーとドイツ・ロマン主義の耽美性が感じられるメロドラマ。富豪の息子イジドールは、余命いくばくもない高級キャバレーの歌姫ラ・パロマと結婚する。次第に病気が回復したパロマは、イジドールの友人ラウルと激しい恋に落ち、ラウルが去った後、彼女は衰弱して死ぬ。パロマが死んで３年、イジドールは彼女の遺言を実行に移そうとするが……。様式的な演技と耽美的な映像でシュミットの名を高めた傑作。", "1974-01-01", "26,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロックアウト", "ロックアウト", "", "", "http://image.eiga.k-img.com/images/movie/55326/315x210.jpg?1495", 82, "記憶の一部を失ってしまった広は、行く当てもなく車を走らせていた。ある日、広が車から離れた隙に、見知らぬ少年が間違えて広の車に乗り込んでいた。偶然出会った少年と奇妙なロードトリップをするはめになった広は、少しずつ本来の自分を取り戻していくが……。新鋭・髙橋康進の長編デビュー作。2009年のニューヨーク国際インディペンデント映画祭で優秀長編映画賞、最優秀監督賞、最優秀スリラー賞を受賞。", "2010-02-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ソラニン", "ソラニン", "", "", "http://image.eiga.k-img.com/images/movie/54566/315x210.jpg?4004", 126, "累計70万部を突破した浅野いにおの人気コミックを、宮崎あおい主演で映画化した青春ドラマ。音楽で成功する夢を諦めきれずにフリーター生活を送る彼氏・種田と暮らす芽衣子は、OL2年目で突然会社を辞めてしまう。不安定な生活を2人で支え合いながら、種田は「ソラニン」という曲を書き上げ、レコード会社に持ち込むが……。共演に高良健吾、桐谷健太、ロックバンド「サンボマスター」の近藤洋一ほか。監督は多数のミュージックビデオを手掛け、本作が長編劇映画デビューとなる三木孝浩。", "2010-04-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夕笛", "夕笛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "昭和初期のある城下町を舞台に、父によって家柄の良い相手に嫁がされてしまった美しい娘と、純粋な学生との悲恋物語を情感たっぷりに描き出した純愛ものの佳作。心労が重なって盲目になった娘が学生の墓標を抱いたまま息絶えるシーンは感きわまる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Heartbeeps", "ハートビープス／恋するロボットたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "「マン・オン・ザ・ムーン」でジム・キャリーが演じた人気コメディアン、アンディ・カウフマンと、ブロードウェイの女王バーナデット・ピータース主演のSFコメディ作品。お互いに惹かれ合い、恋に落ちた家庭用ロボットのバルとアクア。彼らは主人の家を逃げ出し、自分たちの家庭を築こうと奮闘するが……。劇中の音楽を担当するのは、「スター・ウォーズ」シリーズ等の作品で知られる映画音楽の巨匠ジョン・ウィリアムズ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bobbie Sue", "ボビー・スー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("豊山犬", "プンサンゲ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/57476/315x210.jpg?2191", 121, "", "2012-12-31", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO　MAN　OF　HER　OWN", "心の青空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "自堕落な日々を送っていた若者の、人生の再生を見つめた人間ドラマ。賭博と女遊びに明け暮れるN.Y.のギャング、デーブは身辺に危険が及んだことから田舎町に身を隠す。そこで出会った純情な娘コニーと結婚した彼だったが、昔の情婦の密告によって逮捕され、服役を余儀なくされてしまい……。若き日のC・ゲーブルが主人公を好演。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("湾岸ミッドナイト THE MOVIE", "湾岸ミッドナイト THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/54616/315x210.jpg?3581", 0, "楠みちはるがカーレースに熱中する若者を描いた人気コミック「湾岸ミッドナイト」を映画化。車をこよなく愛する高校3年のアキオは、ある日、かつて「悪魔のZ」と呼ばれた「フェアレディS30Z」のスクラップを車の解体所で見つける。破壊的なスピードと人間のように意思を持つ車のため、これまでのオーナーたちは全員が事故によってZを手放していた。一瞬にしてZの虜となったアキオは、愛車と引き換えにZを手に入れるのだが……。主演はD-BOYSの中村優一。", "2009-09-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEATBALLS", "ミートボール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アイバン・ライトマン初監督作品。ライトマンがプロデューサーを務めた大ヒット作「アニマルハウス」のサマー・キャンプ版ともいえるハチャメチャコメディ。キャンプ場にやってきた指導員が仕事そっちのけで騒動を巻き起こす。ビル・マーレイの映画デビュー作。", "1979-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VALE ABRAAO", "アブラハム渓谷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 187, "フローベールの名作『ボヴァリー夫人』を下敷きにして書かれた小説をもとに、ポルトガルの巨匠M・デ・オリヴェイラが描く愛に生きた女性の半生の物語。かつて神が人間の傲慢さと恥を戒めたとされるアブラハムの谷に住む医者カルロスは、裕福な家の娘エマと結婚する。足が不自由だが恐るべき美貌を秘めたエマは、留守がちな夫の目を盗んで他の男たちを誘惑するようになる。貞操とか家族の絆という概念のまったく欠如したエマは、自由な愛に生きるゆえに、その愛に苦悩し、傷ついていく……。静かな場面の連続ながら、ぐいぐいと物語を引っぱっていく演出力はまさに非凡。欲望を超えた愛を崇高に描いた力作である。", "1993-01-01", "25,2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRUG，IT WILL BLOW YOUR MIND", "N.Y.ドラッグコネクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "R・ベンソンが深刻化する麻薬問題に監督・主演と自ら取り組んだ犯罪ドラマ。ニューヨークに住むヤッピーのカップルが麻薬密売にかかわり、自分たちも麻薬漬けになる。やがてマフィア社会の殺りくの渦へ巻き込まれるのだった。マフィアのボスを演じるD・アイエロが渋い演技でいい味を出している。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・初詣列車", "喜劇・初詣列車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "国鉄の車掌・上田新作は少々抜けているが純情な国鉄マン。妻と平凡だが幸福な毎日を送っている。ある日列車で幼なじみの美和子が何ごとか悩んでいる姿に接し……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香港製造／MADE　IN　HONGKONG", "メイド・イン・ホンコン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "F・チャン監督がわずか８万ドルの製作費で完成させた青春映画。街でスケボーに興じているところをスカウトされたS・リーは、この１本で一躍人気スターになった。低所得者向けアパートで母親と二人暮らしの少年チャウは、知的障害の弟分を引き連れてケチな借金取りの仕事をしていた。重病に冒された女の子ペンと心を通わせた彼は、手術代を調達するため殺しの仕事を引き受けるが……。主人公の性的な夢のイメージがしばしば挿入され、過酷な社会状況を生き抜く等身大の若者の心象風景を浮き彫りにする。あまりにも救いのないラスト・シーンは、香港映画界に対する痛烈なアンチテーゼとも言えるだろう。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("疾風／HAYATE・BASEMENT FIGHT", "疾風／HAYATE・BASEMENT FIGHT", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "前作「烈風ACTION!?」で、アクション演出を手掛けた竹田直樹監督作。“Basement Arena” と呼ばれる格闘場を舞台に、クンフー、剣術、棒術、カラテなど、あらゆるジャンルの格闘家同士の激しいサバイバルが繰り広げられるアクション映画。TVシリーズ『仮面ライダー龍騎』の松田悟志が披露する切れ味鋭いアクションが見もの。武闘家として大成するための大会を控え、練習に余念のない相川。彼の他に４人の格闘家たちが、謎の主催者が作った格闘場に集められる。そこには超人的な破壊力を持つ敵が待っていた。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五人のあばれ者", "五人のあばれ者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "三十石の小旗本・比村大吉を中心に、ウダツの上がらぬ生活をする５人組。この５人が無頼旗本の集まり“鬼神組”の向こうを張り、賭場を開こうとしたことから両者が対立。窮地に立った鬼神組は、５人組の仲間、おいちとその赤ん坊を人質にワナを仕掛けるが……。千恵蔵主演の痛快時代劇。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ART OF DYING", "惨劇の週末", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "廃墟で週末パーティーに戯れていた若者たちが、酔い潰れて寝ていた仲間のナッチョをイタズラで溺死させてしまう。そして４年後、その死を隠しながら生活を続けていた若者たちの前に、ナッチョが現れた……。怨念や復讐、罪悪感といった要素をモチーフにしながら、斬新な映像センスで繰り広げられる、スペイン産ホラー映画。", "2000-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イキガミ", "イキガミ", "", "", "http://image.eiga.k-img.com/images/movie/53546/315x210.jpg?1518", 133, "間瀬元朗の同名コミックの映画化。1000人に1人の確率で若者を選び命を奪う「国家繁栄維持法」が存在する世界では、国民の生命価値を高めることで社会の生産性を向上できると信じられている。政府より発行される死亡予告証は通称「逝紙（イキガミ）」と呼ばれ、それを受け取った者は24時間以内に死ぬ。厚生保険省の国家公務員である藤本（松田）はイキガミの配達職務を名誉ある仕事として遂行しようとするが……。", "2008-09-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dr.T & the Women", "Dr.Tと女たち", "", "", "http://image.eiga.k-img.com/images/movie/1376/315x210.jpg?3099", 122, "金も名誉も手に入れた、人気産婦人科医、Dr.Tの信仰の対象は「女性」。周囲の女性たちに惜しみなく愛を注ぐ日々に満足していた。が、ゴルフクラブで魅力的な女性に出会い、平穏な日々に波風が立ち始める。「M★A★S★H」から「ショート・カッツ」まで常に話題作を世に送り出してきたロバート・アルトマン監督の最新作。主演のリチャード・ギアはアルトマン作初出演。ケイト・ハドソン、リブ・タイラーら若手人気女優も共演。", "2001-12-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙人東京に現わる", "宇宙人東京に現わる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "大映初のカラーSF映画。突如出現した新天体“R”が、地球と正面衝突しそうなことを知り、パイラ星人は地球に警告に来る。その奇怪な姿に人間たちは最初、恐れおののくが、やがてパイラ星人と力を合わせて“R”を破壊する。ヒトデ型の体の中央に大きな目を持つパイラ星人の造型デザインは名画家の岡本太郎。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Traitor", "トレイター　大国の敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADIOS，SABATA", "大西部無頼列伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「西部悪人伝」に続く“サバタ”シリーズ第２作。動乱のメキシコでオーストリア軍のスキメル大佐に腕を見込まれた賞金稼ぎのサバタ。その一方でサバタは革命軍ともひそかに手を組み、軍資金の強奪計画を成功させるが、まんまとスキメルに金を横取りされてしまう。Y・ブリンナーが黒ずくめのコスチュームで悠然とサバタを演じている。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BULLET PROOF", "サンダー・ブラスト 地上最強の戦車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "メキシコの山村でゲリラ軍団に襲われた米軍の最新鋭戦車サンダー・ブラストを奪還すべく、元CIA捜査官で不死身の刑事が命懸けで敵陣に挑む。体に受けた38発の弾丸を自分で取り出すなど、G・ビジーの“ランボー”ばりの肉体アクションが見もの。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版NARUTO ナルト　木ノ葉の里の大うん動会", "劇場版NARUTO ナルト　木ノ葉の里の大うん動会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 10, "人気コミックが原作のアニメーション劇場版第1作「劇場版NARUTO ナルト　大活劇！雪姫忍法帖だってばよ!!」と同時上映の短編。", "2004-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWILIGHT FILE II", "TWILIGHT FILE II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「時」をテーマにした3本の中編からなるオムニバス映画。亡き母との思い出の場所である丘を探す少年の冒険を描いた「僕はこの丘で、君を愛したい…」、若き青年画家が少年時代の初恋を回想する「あのころ…summer memories」、現代にタイムスリップした幕末志士たちの奮闘を描く「矜持 KYOJI」というバラエティ豊かな作品がそろう。プロレスラーの藤波辰爾がストーリーテラーとして全作品に登場。", "2006-06-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版NARUTO ナルト　疾風伝　絆", "劇場版NARUTO ナルト　疾風伝　絆", "", "", "http://image.eiga.k-img.com/images/movie/53306/315x210.jpg?2138", 93, "週間少年ジャンプ連載の人気コミックをアニメ化した忍者アクション「NARUTO　ナルト　疾風伝」の劇場版第2作（「NARUTO　ナルト」シリーズとしては第5作）。監督・脚本は前作に引き続き亀垣一と武上純希。謎の忍者集団の奇襲を受けた木の葉隠れの里。駆けつけたナルトたち一行の前に、一族を裏切った兄・イタチへの復讐のため里を離れていたかつての仲間サスケが現れ……。", "2008-08-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAL", "キャル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "宗教対立の続くアイルランド。カトリックゆえに孤立する青年キャルは、ある日同じ境遇の年上の女マルチェラと出会う。彼女は夫をテロ組織IRAに殺されていたが、実はキャルもまたIRAに加わっていた。愛し合う二人には哀しい過去がつきまとう……。英国映画界の俊英オコナー監督のデビュー作。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪徳の栄え", "悪徳の栄え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "時は昭和10年。不知火侯爵家で開かれたマルキ・ド・サドの『悪徳の栄え』の芝居稽古中に殺人事件が発生する。隔離された屋敷内で、やがて人間たちの愛と狂気が露わになって……。「帝都物語」の実相寺昭雄監督によるエロス・サスペンス。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blue Labyrinth ブルーラビリンス", "Blue Labyrinth ブルーラビリンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "俳優としても活躍する中村英児がメガホンを取ったファンタジック・ドラマ。10代で華々しい作家デビューを果たしたものの、その後はスランプに陥って自殺未遂を繰りかえすようになってしまった鳴瀬航は、再起をはかるべく旅に出た末に、大学時代の友人が住む伊勢の小島を訪れる。しかし、島の住民たちは長年にわたって人魚のたたりにおびえながら暮らしていた。やがて、鳴瀬は自分を人魚だと言い張る少女・凪に出会う。", "2007-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUELLO　NEL　TEXAS", "赤い砂の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "テキサスに農場を構えるドン・ディエゴの息子が、牧童たちからメキシコ人であることを罵られ、ディエゴが砂金を貯めていることをしゃべってしまう。その夜、何者かが農場に侵入し、砂金を強奪したあげくにディエゴを殺害。ディエゴの義理の息子ウィルソンは、悪徳保安官が事件の黒幕だとにらむ。エンニオ・モリコーネが初めて西部劇のスコアを書くなど、マカロニ・ウエスタンの原点と見なされている一編。", "1963-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花つみ", "花つみ", "", "", "http://image.eiga.k-img.com/images/movie/57126/315x210.jpg?4730", 90, "青春と性をテーマに監督たちが独自の個性で自由に製作する「青春H2（セカンド）」シリーズの5作目。監督は、佐藤寿保、瀬々敬久、佐野和宏と並んで「ピンク四天王」と称される一方、「ジャイブ　海風に吹かれて」（2008）など一般映画も幅広く手がけるサトウトシキ。工場勤めの健太は、妻が死んでから記憶が1日しか保てなくなってしまう。そんな健太を気にかける同僚のあさこも暗い過去を引きずっており、誰かに殺してほしいという他殺願望を抱えていた。いつしかあさこは健太の部屋に通うようになるのだが……。", "2011-09-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("張り込み", "張り込み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "団地に住む主婦の部屋に、ある日刑事と名乗る男が張り込みと称して、ずかずかと入り込んでいく。傍若無人な言動を繰り返す刑事に、彼女はやがて不信感を抱き始めるが……。シリーズ企画“ラブ・シネマ”第４作のサイコ・サスペンス。ヒロインを演じた若林しほと刑事役の小市慢太郎の迫真の演技合戦が怪しく光る逸品。全編デジタル・ビデオで撮影された。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("No Regret", "後悔なんてしない", "", "", "http://image.eiga.k-img.com/images/movie/53716/315x210.jpg?2252", 114, "韓国ではまだまだタブー視されている同性愛を真正面から描き、第57回ベルリン国際映画祭パノラマ部門に正式出品された新鋭監督イ＝ソンの意欲作。田舎の孤児院から上京して工場で働くスミンは、苦労知らずの御曹司ジェミンに出会う。2人はたちまち恋に落ちるが、スミンはジェミンが解雇された工場の経営者一族だと知り激昂する。ゲイのホストバーで働き始めたスミンを追い続けるジェミンだが……。", "2008-07-19", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIROSHIMA MON AMOUR", "二十四時間の情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「君は広島で何も見なかった…何も」反戦映画の撮影で広島に来たフランス人の女優と建築技師らしい日本人の男のゆきずりの情事が、彼女の過去の禁じられた愛の記憶を甦らせる。「夜と霧」の戦争と忘却というテーマが劇映画に発展したようなレネ監督の長編第1作で、文学作家だったデュラスが初めて手掛けた映画作品。", "1959-06-17", "2,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門一番手柄 南蛮幽霊", "右門一番手柄 南蛮幽霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "江戸中の与力と同心、岡っ引きの面々をあざ笑うかのように殺人事件が発生。一日中寝そべっている同心の近藤右門、人呼んでむっつり右門が捜査に乗り出す。", "1929-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太平洋の鷲", "太平洋の鷲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "本多猪四郎監督が特殊技術監督・円谷英二と組んで作った、戦後初の本格的戦争スペクタクル。のちにこのコンビは、特撮怪獣映画を数多く生み出していく。連合艦隊司令長官・山本五十六の悲劇を中心に、太平洋で空母赤城、加賀などを次々に撃沈され、敗戦の色が濃くなっていく日本軍を描く。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOURNEY TO THE PRIMEVAL AGE", "前世紀探検", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "４人の少年たちは、ある日、太古の世界へと時間をさかのぼる河を見つけた。彼らはボートに乗り旅に出る。それも恐竜の時代への驚異と謎のタイム・アドベンチャーに。マンモス、恐竜、古代ワニ、食人鳥など人形アニメ技術の動きがリアルで迫力があり、有史以前の世界を見事に映像化している。", "1954-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜をひらく 女の市場", "夜をひらく 女の市場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "夜の銀座の引き抜き屋こと槌田昭。経営に苦しむクラブシャトウに狙いを定めた彼は、デマを流して大量引き抜きをかけ、シャトウのNo.1ホステスを獲得。続けて美人姉妹にアタックするのだったが……。1960～70年、夜の銀座を舞台にした小林旭の主演シリーズ“女の警察”の流れに連なる作品。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマンコスモス THE FIRST CONTACT", "ウルトラマンコスモス THE FIRST CONTACT", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "円谷英二生誕100年、ウルトラマン誕生35周年に当たる2001年に製作された作品。バルタン星人との戦いでエネルギーを喪失し、地球に舞い降りたウルトラマンコスモスは少年ムサシに助けられ、謎の輝石を彼に渡して飛び去っていく。一方、謎の宇宙信号の調査を続けていたSRC（科学調査サークル）はムサシに協力を求めるが、そんな時、バルタン星人の仕業で新怪獣が暴れだす…。同時期に放映が始まったテレビ版『ウルトラマンコスモス』では映画版から数年後、成長したムサシがSRC内の精鋭隊チームEYESに入り、コスモスとなって活躍する姿が描かれた。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAXi3", "TAXi（3）", "", "", "http://image.eiga.k-img.com/images/movie/1686/315x210.jpg?2711", 87, "97年の第1作、00年の第2作に続く第3作。今回の敵は、8カ月もマルセイユ警察を悩まし続ける銀行強盗集団。巨大な車でパトカーを踏み潰し、インラインスケートで戦車部隊を出し抜く知能犯たちに、おなじみスピード狂のダニエルとエミリアンのコンビが立ち向かう。ジベール署長が一目ボレする中国系美人ジャーナリスト役で「アンナと王様」のバイ・リンが出演。監督は「TAXi2」「WASABI」のジェラール・クラブジック。", "2003-05-17", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Runaway Bride", "プリティ・ブライド", "", "", "http://ecx.images-amazon.com/images/I/413V0WM2SPL._SL210_.jpg", 115, "結婚式当日に何度もドタキャンする女性のことを記事にした辛口コラムニストが、当人の抗議でメリーランド州の田舎町に取材に赴く。始めは反発する彼女だが、次第に惹かれあい……。「プリティ・ウーマン」の監督、主演コンビが再結集したロマンチック・コメディ。", "1999-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛しきソナ", "愛しきソナ", "", "", "http://image.eiga.k-img.com/images/movie/55996/315x210.jpg?4387", 82, "前作「ディア・ピョンヤン」がベルリン国際映画祭最優秀アジア映画賞を受賞するなど、高い評価を受けた在日2世のヤン・ヨンヒ監督によるドキュメンタリー。帰国事業により1970年代に北朝鮮に移住したヤン監督の3人の兄と姪のソナにフォーカスをあて、日本と北朝鮮という近くて遠い2つの国をつなぐ家族の強いきずなと愛情を描く。", "2011-04-02", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HUNTER", "ハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ハリウッドの代表的スターとして華々しく活躍したマックィーンが賞金稼ぎに扮した遺作。保釈中に逃亡した容疑者を再度収監するため、大都会の地下鉄から片田舎のコーン畑まで、体を張って熱演する最後の勇姿が見もの。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("塀の中の懲りない面々", "塀の中の懲りない面々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "元ヤクザで通算８年の刑務所暮らしという経験を持つ安部譲二が、刑務所内の囚人たちの日常を描いた自伝的同名ベストセラー小説の映画化。“塀の中の文豪”として刑務所中にその名を馳せる安部直也は、恐喝罪、銃刀法違反で懲役３年３ヵ月の刑を受けて服役中である。ここで安部は、塀の外では接することのないような様々な面々と生活をともにしながら、また看守の暴圧に耐えながらも、今も愛する別れた妻の待つシャバヘ出る日を待ち望む……。ニセ医者や、革命の闘士、オカマなど一癖も二癖もある囚人たちがいきいきと描かれ、いわゆる“刑務所もの”という暗さのないところに好感が持たれる。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCARECROW", "スケアクロウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "1970年代、ニューシネマと呼ばれる若々しい感性にあふれた作品が数多く発表されて、映画ファンに衝撃を与えた。この作品もカメラマン出身で、これがちょうど、３本目の作品になるシャッツバーグ監督が、政治的な意味を込めて発表したものである。物語は二人の男が国道で車を止めようとしているところから始まる。マックスとライオンが初めて出会ったのがこの南カリフォルニアの道路であった。マックスは刑務所から出てきたばかり。店を開くためピッツバーグへ向かう途中、妹に会うためデンバーに行くつもりだった。ライオンは船員生活をやめてデトロイトの妻のところに帰るところ。この二人はすっかり意気投合して旅を一緒にすることになる。久しぶりに会った妹と大騒ぎをしたり、けんかをしたり、強制労働に就かされたり、ロード・ムービーならではの手法で、知的な脚本、美しいカメラ、そして主演二人のイキの合ったコンビぶりが素晴らしい。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録外伝・武闘派黒社会", "実録外伝・武闘派黒社会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ヤクザ社会を舞台に、二人の若者の生き様を描くアクション。中国の黒社会を追われたリュウは父の故郷である日本に渡り、台湾マフィアの一員に。一方、彼のアパートの向かいに住むユウジはヤクザ殺しの罪で少年院に送られた過去を持ち、現在は無目的に日々を過ごしていた。そんな彼らが覚醒剤の取引をめぐる騒動に巻きこまれ、壮絶な争いに身を投じていく。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ATALANTE", "アタラント号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "29歳の若さで夭逝しながら、フランス映画界の新人に与えられる賞にその名を残す伝説的な監督J・ヴィゴが、唯一残した長編作品。若き船長とその妻の新婚生活における歓喜と戸惑い、別れと再会を鮮烈なタッチで生き生きと描き出していく。全編に満ちあふれる、みずみずしい詩情を湛えた映像が素晴らしい。", "1934-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Killing Room", "実験室KR-13", "", "", "http://image.eiga.k-img.com/images/movie/54866/315x210.jpg?4449", 94, "「テキサス・チェーンソー　ビギニング」のジョナサン・リーベスマン監督が、アメリカで実際に起きたマインドコントロール計画「MKウルトラ」にヒントを得て製作した密室サスペンス・ホラー。小さな実験室に閉じ込められ、次々と出される問題に正解しないと殺されるという恐怖の実験に参加してしまった人々の運命を描く。出演は「ボーイズ・ドント・クライ」のクロエ・セビニー、「普通の人々」のティモシー・ハットンほか。", "2009-11-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON'T LOOK DOWN", "アクロフォビアー 高所恐怖症", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「スクリーム」の監督W・クレイヴンが製作総指揮を務めたサイコ・サスペンス。山の頂上でカーラは夫や妹と楽しく遊んでいたが、妹が不慮の転落事故で死んでしまう。以来、高所恐怖症に陥り、生活にも支障をきたすようになったカーラは精神科医ポールのところに通い、グループ・セラピーに参加する。しかし、病気を克服した患者たちが次々と殺される事件が起き……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サンデイドライブ", "サンデイドライブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「フレンチドレッシング」で劇場デビューを果たした斎藤久志が、俳優としても活躍する監督、塚本晋也を主演に据えて綴ったストレンジな味わいのロード・ムービー。レンタルビデオ屋でバイトしている女の子、結衣は同棲相手の真二が浮気していることを知り逆上。頭から血を流して横たわる真二を置き去りにして、彼女に思いを寄せていたビデオ屋の店長、岡村とワゴン車で逃亡を図るのだが……。切羽詰ったシチュエーションとは裏腹に、行き当たりばったりの逃走劇には独特の停滞感と不思議な不発感が伴う。塚本のひょうひょうとした佇まいもさることながら、唯野未歩子が見せるとりとめのない存在感も印象的。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドリフト2", "ドリフト2", "", "", "http://image.eiga.k-img.com/images/movie/33506/315x210.jpg?3839", 80, "「きょうのできごと」「血と骨」の柏原収史を主演に迎え、公道を駆け抜ける“走り屋”たちの激しいバトルを描くカー・アクション第2作目。事故で最愛の妻子と黄金の拳を失ったプロボクサーの南は、全ての元凶である黒い車の走り屋を見つけて復讐を遂げるべく、自らS-13を操る走り屋として名を上げていく。共演は「あずみ2」の小橋賢児、「テニスの王子様」の載寧龍二。", "2006-07-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEZEBEL", "黒蘭の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "19世紀半ば、南北戦争前の南部ニューオリンズ。一人の情熱的な娘が、愛する男がいながらもほかの男と結婚する……。当時売り出し中だった、B・デイヴィスの出世作ともなったメロドラマの傑作。南部の女らしい熱情を内に秘めた演技が印象深い。また、彼女が着るゴージャスな衣裳も見ものだ。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グラッフリーター刀牙", "グラッフリーター刀牙", "", "", "http://image.eiga.k-img.com/images/movie/57136/315x210.jpg?2644", 124, "板垣恵介による人気格闘漫画「グラップラー刃牙」を題材とした劇団「FEELD STAGE」の舞台「グラッフリーター刀牙」の映画化。町の商店街を舞台に、地上げ屋と結託した悪の組織から、熱狂的な刃牙（バキ）ファンの母から英才教育を受けて育った少年・刀牙（トキ）と商店街の人々が力を合わせて町を守る姿を描くヒューマンコメディ。主人公・刀牙役は舞台版にも出演した須藤凌汰。ヒロイン役で人気アイドルグループ「AKB48」の北原里英が映画初出演を飾る。", "2012-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リーガル・エイリアン", "リーガル・エイリアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "日本に住む外国人たちの日常にスポットを当てた、全編英語の和製ヒューマン・ドラマ。日本在住の米国人ピーターはブロードウェイの舞台に立つことを目指しながらも、実際は英語教師として働く毎日。自己嫌悪を解消できず、恋人にも去られた彼はある日、友人の自殺未遂事件に遭遇し……。アイデンティティーを模索する異邦人の姿を通して、国際化社会の中の日本が見えてくる。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STORMY MONDAY", "ストーミー・マンデイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "イギリスの工業都市ニューカッスルで、定職を持たない町の若者ブレンダンと、ダイナーのウェイトレスをしているアメリカ女ケイトが出会った。しかしケイトは秘密組織とかかわりがあり、ブレンダンのバイト先のオーナー、フィニーの不動産をおどし取ろうとしていたことを知る。ブレンダンは組織からケイトを必死で守ろうとするのだが……。ミュージシャン出身のフィギスが、監督・脚本・音楽の３役を一人でこなし、味わい深い魅力的な作品に仕上げている。全編を彼自身によるオリジナルのジャズで通し、主題曲としてB.B.キングの表題曲を使用。さながら英国版フィルム・ノワールといった趣の作品。S・ビーンとM・グリフィスの絡みが美しい。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌舞伎役者・片岡仁左衛門", "歌舞伎役者・片岡仁左衛門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 488, "'94年、90歳でこの世を去った歌舞伎役者、13代目片岡仁左衛門。名人として多くのファンを魅了した彼の晩年を追った6部構成の傑作ドキュメンタリー。どの作品も仁左衛門の素顔が垣間見える貴重なショットが多数。彼が身振り手振りを交えて語る芸談も興味深い。生涯現役を貫いた名歌舞伎役者、13代目片岡仁左衛門。彼の88歳から亡くなるまでの舞台とその普段の生活を見つめる。（「登仙の巻」）", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天涯・明月・刀／THE MAGIC BLADE", "マジック・ブレード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "香港の人気作家クー・ロンの原作に基づくファンタジー活劇。武芸者の傅紅雪と燕南飛は宿命のライバル同士だが、謎の刺客の出現によって一時休戦を余儀なくされる。究極の武器を求める争奪戦に巻き込まれ、そこで刺客と死闘を演じる彼らのまえに、やがて強大な敵が立ちはだかる。マカロニ・ウエスタン風の衣装や、ダイナミックなワイヤーアクションなど見どころはつきない。", "1976-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヤーチャイカ", "ヤーチャイカ", "", "", "http://image.eiga.k-img.com/images/movie/53636/315x210.jpg?2676", 70, "「千と千尋の神隠し」主題歌の作詞を手掛けたことで知られる覚和歌子と日本を代表する詩人・谷川俊太郎が共同でメガホンを取り、覚による詩\"ヤーチャイカ\"をモチーフに作りあげたフォト・ムービー。自然に囲まれた小さな村の天文台を舞台に、それぞれ心に傷を負った男女がお互いとの関わりを通して再生していく姿を静止画とナレーションのみで綴っていく。主演は「ゆれる」の香川照之と「殯の森」の尾野真千子。", "2008-05-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セックス・チェック／第二の性", "セックス・チェック／第二の性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "名匠・増村保造監督自らの企画による異色作。中年男のコーチがある女子スプリンターを特訓するが、セックス・チェックの結果、半陰陽とわかり、大会出場の資格を得るため今度は女に戻すべく性の訓練に励むというもの。安田と緒形の個性のぶつかり合いが圧巻。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIVATE WARS", "プライベート・ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "巨大なビル建設のため住人たちを追い払おうとする悪徳不動産王が、凶悪ギャングを送り込む。善良な市民たちは、元刑事マニングを用心棒として雇った。落ちぶれた酔っぱらい私立探偵となっていたマニングだったが、卑劣なギャング相手に再起する。スタントマンたちの体当たりアクションが多数見られる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mujeres al borde de un ataque de nervios", "神経衰弱ぎりぎりの女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "一緒に暮らしていた恋人イバンが留守電にメッセージを残して突然姿を消し、大ショックのペパ。自分のことだけで精一杯の彼女の元に、テロリストの恋人を持つ友人やイバンの息子とその婚約者、さらにイバンの元妻などなど、悩める女たちが次から次へと現れて……。悩みながらもたくましく生きていく女たちを、鬼才ペドロ・アルモドバル監督がコメディタッチで描く。アルモドバル監督の初期作品の常連、アントニオ・バンデラスも出演。", "", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Claim", "めぐり逢う大地", "", "", "http://image.eiga.k-img.com/images/movie/1696/315x210.jpg?3594", 121, "19世紀英国の文豪トマス・ハーディの「カスターブリッジの市長」を原案に、「日陰のふたり」（96）でもハーディの「日陰者ジュード」に挑んだマイケル・ウインターボトム監督が、今度は舞台をアメリカに移し、各国俳優の共演で映画化。主演は英国のベテラン、ピーター・ミュラン。元妻役でナスターシャ・キンスキー、愛人役でミラ・ジョボビッチらが共演。音楽は「ピアノ・レッスン」などの名匠マイケル・ナイマンが担当。", "2002-12-14", "3,2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森繁の新入社員", "森繁の新入社員", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "長い失業生活をはなれ、保険会社の外務員となった豊臣三太郎は、次から次へと女難におそわれ、メチャクチャな新入社員生活が始まる。新東宝の売りものの一つだった森繁久彌主演のコメディ・シリーズの一編。まだ若い高島忠夫が初々しい演技を見せる。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOPO　GIGIO，　LA　GUERRA　DEL　MISSILE", "トッポ・ジージョのボタン戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "巨匠、市川昆監督が1967年に製作したパペット映画。今も多くの人々の心を掴む人気キャラクター、トッポ・ジージョが謎のボスの悪巧みから世界を救うため大活躍する。市川監督のもと、イタリアと日本の精鋭スタッフが集結。なかでもジージョの生みの親であるM・ベレゴの操演が見事。そのまるで生きているようなジージョの動きには驚かされるはずだ。小さなアパートで一人暮らしをしているトッポ・ジージョは、二匹のノミに悩まされていた。我慢できずに家を飛び出したジージョは、街で赤い風船に出会う。二人で夜の街を歩いていると、銀行ギャングの事件に巻き込まれ……。", "1967-01-01", "76,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレーマー case1", "クレーマー case1", "", "", "http://image.eiga.k-img.com/images/movie/53626/315x210.jpg?4452", 81, "過剰なクレーマーによる恐怖を描いたサスペンス。主演は「カミュなんて知らない」の柏原収史。製菓会社のお客様相談室でクレーム処理を担当する継村尚人（柏原）は、日々かかってくる理不尽なクレームにも毅然と対応し、社内でも一目置かれる存在。しかしある日、ナイトウと名乗る男からクレームを受け、れまで通りの対応をするが全く通用しない。やがて、彼の怒りの矛先は継村の生活圏内へ入り込み、悪質なストーキング行為が始まる……。", "2008-04-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELP！　I’M　A　FISH", "とび★うぉーず", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "魔法の薬のために魚になってしまった少年たち。48時間以内に人間に戻る薬を飲まなければ、彼らは一生元に戻れないのだ……。アンデルセンを生んだデンマークが製作。ポップで色鮮やかな流線型をベースにした映像美の数々がファンタスティックな情緒を否が応にも誘う、おとぎばなし感覚に満ちあふれた冒険アニメーション映画。", "2000-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BATTLE OF THE RIVER PLATE", "戦艦シュペー号の最後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "第二次世界大戦中、Uボートと並び高性能機能を発揮したドイツの小型戦艦シュペー号の悲劇をドラマティックに綴った作品。敵国海域で損傷し、ついに自爆するまでの戦艦に集う人間たちのドラマが、一般の戦争アクションとは一味違った重厚な演出で語られる。", "1956-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kurtlar vadisi - Irak", "イラク　狼の谷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "イラク戦争の矛盾やアメリカ軍への不満を痛烈に描き、イスラム諸国で大ヒットを記録したトルコ発の反米アクション。2003年7月、イラク北部のクルド人自治区で、同盟国であるはずのアメリカ軍がトルコ秘密司令部を襲撃。アメリカ軍によって拉致されたスレイマン将校は、屈辱的な扱いに耐えきれず自ら命を絶ってしまう。スレイマン将校の友人である元秘密諜報員ポラットは、復讐を果たすべくイラクへと向かう。", "2007-06-23", "32", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋の門", "恋の門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "現代演劇のカリスマであるだけでなく、映画界でも活躍する松尾スズキの初監督作。シュールさが人気の松尾ワールドだが、今回は舞台で描いたことのないストレートな恋愛ドラマが繰り広げられる。童貞貧乏漫画家青年と、コスプレ大好きOL。価値観が異なる男女がいかに結ばれるかを、デフォルメの効いたポップな演出でえぐり取ってみせる。独特のセリフ回しや小ネタ満載の松尾節で、デタラメな筆致に見せつつ、恋愛事の深淵を華麗に直撃する技には、新人監督離れした冴えが感じられる。石で漫画を描く自称“漫画芸術家”の門。初バイトの出勤中、ハート型のいい石を見つけ拾おうとした時、OLにヒールで指を踏まれケガをする。遅刻し向かった会社にはそのOLがいて……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE EHE DER MARIA BRAUN", "マリア・ブラウンの結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ニュー・ジャーマン・シネマの旗手ファスビンダーの代表作。第二次大戦のさなかに結婚し、翌日には夫を戦場に送り出したマリア。終戦を迎え、夫の戦死を知らされた彼女は黒人と親しくなり彼の子を宿すが……。一人の女の生きざまを通し、戦後10年間の旧・西ドイツの社会復興の真実を問う力作。", "1978-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("牡丹と竜", "牡丹と竜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "人斬りの竜は妻に死なれ、残された子・竜夫を連れ旅に出る。彼は道中出会ったテキヤの親分・平蔵に侠気を惚れられ、身を預けることになる。マキノ雅広監督の熱血任侠映画だが、同監督が1966年に撮った「日本侠客伝・斬り込み」のほぼ忠実なリメイクとも言える内容である。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カクト", "カクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "俳優、伊勢谷友介が監督デビューを飾った、逞しさに満ちあふれた青春群像ストーリー。若者たちの一夜の逃亡劇を追いながら、焦燥と疾走、混沌と軽快を一体化した映像文体で酔わせる。クスリや女の子の調達をしながら、満たされぬ毎日を送る大学生・リョウ。生まれて初めて彼女に裏切られたマコト、田舎から家出してきたナオシと酒を飲んでいたリョウは、ある気晴らしを思いつく。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きらきらひかる", "きらきらひかる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "フリーの翻訳家、27才、香川笑子。医師、30才、岸田睦月。二人はお見合いの席で本当の姿をそれぞれみせたことがきっかけとなり入籍。情緒不安定でアルコール依存症気味の妻と同性愛者の夫という奇妙な夫婦が誕生したのだった。夫から彼の恋人である大学生の紺を紹介されて親しくなった妻。奇妙の上に奇妙が重なった３人の不思議な関係に、やがて第３者が介入していく……。デビュー作「バタアシ金魚」で一途な恋を描いた松岡錠司が、第２作となる本作品では、都会という激しい荒波の世界で精いっぱい、正直に生きている人間の不器用な生き方を温かい視線で描き上げている。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前茶釜", "喜劇・駅前茶釜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "呑福茶釜の伝説で名高い“呑福寺”は観光バスで大繁盛。住職は笑いが止まらない。住職にウラミをもつ古道具屋と境内のショバ代を値上げされた記念写真屋は別の茶釜の伝説をデッチあげ、新しい寺をつくって対抗することになった。住職は伴、古道具屋は森繁、写真屋は堺。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH PENALTY", "デッドリー・リベンジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "筋金入りの敏腕刑事ジェームズは、連続殺人の犯人を追っていた。捜査が進むうちにジェームズは、彼のフィアンセを殺したのは、事件捜査を命令した上司だったと知る。ジェームズは刑事であることを忘れ、復讐鬼と化した。1993年、アメリカのホームビデオ・アカデミーでベスト・アクション・アドベンチャー賞を獲得。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ORPHANS OF THE STORM", "嵐の孤児", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "「国民の創生」「イントレランス」と並び、“映画の父”D・W・グリフィス監督が残した映画史に輝く金字塔ともいえる大作。サイレント期最大の女優リリアン・ギッシュとその妹ドロシーの共演によって描かれる、壮大なスケールの歴史ドラマである。舞台は貴族の横暴と腐敗で市民の不満が爆発寸前のフランス。パリ郊外にヘンリエッタとルイーズという美しい孤児の姉妹がいた。治療のためパリへ名医を求めて向かう二人だが、その途中ヘンリエッタが貴族に誘拐される。ルイーズは物乞いに身をおとすが、折しもフランス革命が勃発。別れ別れになった姉妹は再会できるだろうか……。", "1921-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蘇える金狼", "蘇える金狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "大藪春彦・村川透・松田優作のトリオで次々ヒットを生み出したハードボイルド・シリーズの口火を切った作品。昼間は平凡なサラリーマンを装いながら、夜は身体を鍛えて巨大資本を乗っ取る企みを持つ男の姿を描く。都会を冷たく、また迫力ある映像で切り取った仙元誠三のカメラワークも光る。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHAT ABOUT BOB？", "おつむてんてんクリニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "潔癖症から高所恐怖症まで、ありとあらゆる恐怖症を兼ね備えたボブ。良識あるドクター・レオと出会い、すっかり気に入ったボブは、あろうことか、バカンス中のレオの別荘にまでおしかけて……。人の迷惑をかえりみず助けを求める患者と、彼を追い払おうとする医者の“攻防”を描いたハチャメチャなコメディ。B・マーレー、R・ドレイファスという個性派二人が、抱腹絶倒のギャグの応酬を見せる。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鍵穴通りでサヨウナラを", "鍵穴通りでサヨウナラを", "", "", "http://image.eiga.k-img.com/images/movie/54576/315x210.jpg?4020", 108, "アルコールを手放せない和宏は、経営している下請の編集プロダクションの仕事もうまくいかず、妻・直子との関係悪化にも悩んでいた。直子もまた夫に苛立ちを覚えていた。1年前、和宏の友人の島村という男が、妻・霞を殺害し自身も自殺を図った事件があったのだが、この霞と不倫していたのが和宏だったのだ。直子にとっては寝耳に水の事件であった。和宏の過去に疑惑を抱いた直子は、ある日、口論の末に家を飛び出す。", "2009-04-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRANGER THAN PARADISE", "ストレンジャー・ザン・パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ニューヨークでヤクザな暮らしをしているウィリーが、ハンガリーから渡米してくる従妹エヴァをしばらく預かるハメに。最初は邪険にしていたウィリーだったが、賭博仲間エディともどもだんだん彼女が気になり始めて……。ジャームッシュ監督が、ベンダース監督から「ことの次第」の端尺フィルムを譲り受けて1/3を仕上げ、短編として出品したロッテルダムでの受賞を受けて完成させたカメラ・ドール受賞作。独特のオフ・ビート感覚が一世を風靡した。", "1986-04-19", "1,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PASSAGE　A｀　L’ACT", "見憶えのある他人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "J・P・ガッテーニョの処女作『悪魔の囁き』を映画化したミステリー。「地獄の貴婦人」「華麗なる女銀行家」などのF・ジローが、患者に惑わされる精神科医の姿をスリリングなストーリー展開の中に描く。富と名声を得た精神科医アントワーヌは、同じく精神科医の別れた妻と今も信頼関係を保っており、公私ともに順調だった。そんなある日、恐ろしく冷静で怪しげな雰囲気のエドワールという男性患者がやって来る。“妻を殺害した”と告白する彼は、その時の様子を詳しく話し始め……。ビデオ発売時タイトルは「悪魔の囁き」。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Faces in the Crowd", "フェイシズ", "", "", "http://image.eiga.k-img.com/images/movie/57656/315x210.jpg?4234", 102, "ミラ・ジョボビッチ主演で連続殺人犯を目撃してしまった女性に襲いかかる恐怖を描いたサスペンス。恋人と順風満帆な生活を送っていた小学校教師のアンナはある日、女性ばかりを狙う連続殺人鬼の犯行現場を目撃してしまう。犯人に追いかけられ川に転落したアンナは一命を取り留めるが、その時のショックで人の顔が判別できない「相貌失認」という状態に陥ってしまう。目撃したはずの男の顔もわからなくなってしまったアンナに、犯人の影が忍び寄り……。", "2012-05-12", "1,2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GENESIS", "グレート・ビギン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "「ミクロコスモス」のスタッフが手がけた壮大なドキュメンタリー。自然の誕生、動植物の進化の変遷、そして人類の誕生へ。さまざまな生命が形作られる、その過程を映し出していく。16年の歳月を費やして収められた映像は、驚異のひとこと。とりわけ、超音波装置で撮影された、母親の胎内で多彩な表情を見せる胎児の映像は見逃せない。", "2006-01-14", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すべては夜から生まれる", "すべては夜から生まれる", "", "", "http://ecx.images-amazon.com/images/I/41EP5A2W3SL._SL210_.jpg", 79, "「Dolls〈ドールズ〉」の西島秀俊の主演作。新鋭、甲斐田祐輔監督が、関係の交差した２組のカップルの感情をストイックなまでに鋭利に描き出す。売れない役者の山形は、同棲する優子に仕事と嘘をついてはカフェで時間を潰していた。そんなある日、彼はカフェで画家の上原と彼の恋人、綾と出会う。やがて山形は綾に惹かれていくが……。", "2003-01-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Kid Stays in the Picture", "くたばれ！ハリウッド", "", "", "http://image.eiga.k-img.com/images/movie/1486/315x210.jpg?2240", 93, "「ゴッドファーザー」「ある愛の詩」などのプロデューサー、ロバート・エバンズの自伝「くたばれ！ハリウッド」が原作のドキュメンタリー。原題「その若造は映画にとどまる」は、駆け出し俳優だったエバンズが「日はまた昇る」を降ろされそうになったときに名プロデューサー、ダリル・F・ザナックがそれを救ったときのひと声。この経験からエバンズは“映画はプロデューサーのものである”という信念を持ち、プロデューサーを目指していく。", "2003-09-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春秋一刀流", "春秋一刀流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "当時の時代劇に新風を巻き起こし、山中貞雄の再来とまで騒がれた、菅沼完治門下の俊英・丸根賛太郎のデビュー第１作。丸根の脚本を気に入った大スター・片岡千恵蔵の後押しで実現した。千葉道場を破門となり、今ではしがない用心棒稼業に身をやつした浪人・平手造酒が、史上名高い飯岡の助五郎と笹川の繁蔵の抗争に巻き込まれていく。丸根は造酒のつける日記により話を進めるという手法を用い、主人公が繁蔵一家で勇名を轟かす一方で、人並みの生活を望むという人間的な側面を描いている。話術巧みにたたみかけるような才気煥発の演出に、丸根の真骨頂がうかがえる。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Uncle Boonmee Who Can Recall His Past Lives", "ブンミおじさんの森", "", "", "http://image.eiga.k-img.com/images/movie/55726/315x210.jpg?3481", 114, "「トロピカル・マラティ」で国際的に高く評価されたタイのアピチャッポン・ウィーラセタクン監督が、カンヌ映画祭でタイ映画に初のパルムドールをもたらしたファンタジー・ドラマ。タイの山間で暮らす余命わずかなブンミは、自分の農園に妻の妹ジェンを呼び寄せる。すると、19年前にこの世を去ったブンミの妻の霊が現れ、さらに行方不明の息子も姿を変えて現われる。やがて、ブンミは彼らとともに深い森へ入っていく。", "2011-03-05", "3,23,7,2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌行燈", "歌行燈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "泉鏡花の同名小説を映画化した成瀬巳喜男初期の傑作。演劇界の重鎮・久保田万太郎が脚色、出演者に新派劇の名優たちを得て、芸の道に生きる男の波乱の生涯を描く。早朝の林の中、花柳章太郎の教えを受けて山田五十鈴が“松風”を舞う場面は圧巻のひと言である。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Twins", "ツインズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "遺伝子操作をした受胎実験で生まれてきた双子。その後、生き別れた2人は35年ぶりに再会する。すべてにおいて優れた弟と、すべてにおいてサエない兄というデコボココンビが母を探す旅に出る爆笑コメディ。シュワルツェネッガーがコメディに初挑戦した意欲作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUZZLE OF A DOWNFALL CHILD", "ルーという女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ファッション業界で泥まみれになって生きる一人のモデルの姿を冷酷に見つめたドラマ。モデルとしてセンセーショナルな成功を収めるが、華やかな行動とは裏腹に心は満たされないルー。不幸な結婚をしてしまった彼女は、仕事の面でも売れっ子モデルとしての面影は次第に色褪せ、やがて酒と薬に溺れていく。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新唐山大兄／THE BIG BOSS", "ドラゴン危機一発'97", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ブルース・リーの「ドラゴン怒りの鉄拳」のTVリメイク版で人気を博したドニー・イェン主演のカンフー・アクション。記憶を失った謎の格闘家が恋人と再会を果たし、過去を思い出す。彼は盗賊の仲間だったが、罪もない少女を犯そうとした首領ビッグ・ウルフの弟を殺し、盗賊一味に追われているのだ。やがて、彼は自分を探し当てたビッグ・ウルフと死闘を繰り広げることになる。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼくはうみがみたくなりました", "ぼくはうみがみたくなりました", "", "", "http://image.eiga.k-img.com/images/movie/54746/315x210.jpg?4271", 103, "ひとりの自閉症の青年と、彼を取り巻く人々をユーモラスに描いたヒューマンドラマ。人生に行き詰まった看護学生の明日美は、ある日、自分の車をじっと見つめている青年・淳一に出会う。明日美は淳一を誘って海を目指すことにするが、淳一の言動が少しおかしいことに気付き……。主演は「いま、会いにゆきます」の大塚ちひろ。", "2009-08-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN AFFAIR TO REMEMBER", "めぐり逢い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ハリー・ウォーレン作曲の甘いテーマにのって展開するラブ・ロマンスは、ハリウッドが最も得意とするジャンルである。この作品は戦前に「邂逅〈めぐりあい〉」という題名でアイリーン・ダン、シャルル・ボワイエで映画化されたものの再映画化。ヨーロッパへ向かう豪華客船のデッキで知り合った画家と歌手が恋に落ち、エンパイアステートビルの屋上での再会を約束するが、思いがけない事故のため二人は逢うことができなかった……。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABRE　LOS　OJOS", "オープン・ユア・アイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「テシス　次に私が殺される」で脚光を浴びたスペインのA・アメナーバル監督が、第11回東京国際映画祭でグランプリに輝いた長編第２作。裕福なハンサム青年セサルが、冷たくあしらった元恋人によって無理心中に巻き込まれ、顔面が醜く崩壊してしまう。生きる希望を失った彼は、やがて悪夢の迷宮に足を踏み入れていくのだった。夢と現実の境界が曖昧なストーリー構成と、スタイリッシュな映像で見せきるサスペンス・ミステリー。テクノロジーの発達に関心を寄せているという監督が、意表を突いたSF的なドンデン返しを盛り込み、未来への警鐘を鳴らしている。後にトム・クルーズが、「バニラ・スカイ」というタイトルでリメイクした。", "1997-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLAY　DIRTY", "大侵略", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "活劇を得意とした娯楽派A・ド・トスによる戦争アクション。第二次世界大戦下のアフリカ戦線で、砂漠地帯に建てられた独軍の石油貯蔵所を爆破すべく、前科者ばかりで結成された連合軍特殊部隊の暗躍を描く。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕等がいた　前篇", "僕等がいた　前篇", "", "", "http://image.eiga.k-img.com/images/movie/56126/315x210.jpg?3337", 123, "累計発行部数1000万部を突破する小畑友紀の人気少女漫画を、前後編の2部作で実写映画化。主演は生田斗真と吉高由里子。「ソラニン」の三木孝浩監督がメガホンをとる。クラスの3分の2の女子が一度は好きになる、非の打ちどころのない男子高校生・矢野は、過去に恋人を交通事故で失い心を閉ざしていた。しかし、明るく前向きで無邪気な七美の存在が次第に矢野の心を開かせていく。やがて2人はさまざま葛藤（かっとう）を乗り越えて恋を実らせるが……。共演に高岡蒼甫、本仮屋ユイカ。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIKI／THE WILD EAST：THE LAST SOVIET MOVIE！", "ワイルド・イースト ソビエト最後の映画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "黒澤明監督の「七人の侍」をベースにしたウエスタン風活劇。20世紀の終わり、“太陽の子供たち”と名乗る小人たちは、内乱を避け安住の地を求めて旅を続けていた。そこに狂暴なオートバイ集団が襲いかかり、リーダーを殺害。彼らは平和主義を捨て、護衛のためにプロの戦士を雇うことを決意する。しかし、彼らのもとに集まったのは、流れ者のカウボーイや金髪の美女、クンフーの達人、アル中のガンマンと、ひと癖もふた癖もある者ばかりだった……。", "1993-01-01", "51", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRE DOWN BELOW", "海の荒くれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "当時、華麗なるハリウッド・スターとして人気を博していたヘイワースと、同じく次々と主役をこなしていた個性派R・ミッチャム、さらにコメディ畑のJ・レモンの３人が見事な調和を奏でるアンサンブル劇のヒューマン・ドラマ。船で密輸の仕事をしていた二人の男の元に、ある日素晴らしい美女が現れ密航を頼まれる。憂いある彼女に魅了された彼らの友情にヒビが入り……。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROSPERO’S BOOK", "プロスペローの本", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "シェイクスピアの最後の戯曲『テンペスト』をモチーフにした荘厳なる映像絵巻。鬼才P・グリーナウェイがハイビジョン技術を駆使し、美しく怪しげな“魔法”の世界を作り上げている。主人公は絶海の孤島で一人娘と暮らす元ミラノ大公プロスペロー。友人から託された24冊の魔法の書を読み、偉大なる魔力を身につけた彼が、自分を陥れた者たちへの復讐のシナリオを書き、やがてそれが現実となっていくという筋書き。グリーナウェイだからこそ作り出せる計算され尽くしたバロック調の画面、それを流麗に捉えた名手S・ヴィエルニーの映像、そんな映像をより際立たせるM・ナイマンのスコア。すべてが相まって“芸術の極み”とも言うべき世界が構築されている。", "1991-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRINCE OF EGYPT", "プリンス・オブ・エジプト", "", "", "http://ecx.images-amazon.com/images/I/31Xp0q-YntL._SL210_.jpg", 99, "ドリームワークスが４年の歳月をかけ、初めて手掛けた大作アニメーション。セシル・B・デミル作品でおなじみの「十戒」をもとに、旧約聖書『出エジプト記』の聖者モーセの伝説が描かれる。古代エジプト。王子として育ったモーセは、自分が捨て子であり、奴隷の子供であったことを知り苦悩する。以来、兄ラメセスとの確執は深まり、固い絆で結ばれていた二人は別々の道を歩み始める。放浪の旅に出たモーセは、勇敢な女性ツィッポラの愛に支えられ、奴隷解放のために立ち上がるのだった……。アニメ初のクレーン・ショットを用いた馬車の疾走シーンや、有名な紅海が二つに割れるシーンなど、臨場感あふれる迫力映像が圧巻。ホイットニー・ヒューストンとマライア・キャリーが歌う主題歌『ホエン・ユー・ビリーブ』も感動を盛り上げる。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLEO DE ５ A｀ ７", "5時から7時までのクレオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "自分はガンではないか……という不安に取り憑かれている金髪のシャンソン歌手クレオ。彼女は病院の検査の結果が出る７時を待っている。今は５時……。ヒロインの心の二時間の流れをほぼ時間通りに描いた実験的作品。この素材をいや味なくさらりと描いたA・ヴァルダの演出も秀逸で、主演女優C・マルシャンの魅力も印象的。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔の刻", "魔の刻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "受験の失敗に悩む息子を愛するがあまり、彼を男として受け入れてしまった母が、息子との愛を清算し自立していくまでを描く、禁断の母子相姦をテーマにした社会派のドラマ。岩下志麻自らが母親役を希望、母としての微妙な心理を見事に演じきる。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Away We Go", "お家（うち）をさがそう", "", "", "http://image.eiga.k-img.com/images/movie/54716/315x210.jpg?2715", 98, "米コロラド州に住むバートとベローナのカップルは、妊娠を機に“幸せな家庭”をどこに築くべきかを探すため、友人や家族の家庭を訪ね歩く旅に出る。「アメリカン・ビューティー」「レボリューショナリー・ロード　燃え尽きるまで」のサム・メンデス監督が、本当の幸せと未来を模索していくカップルの姿を描いたロードムービー。", "2011-03-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REBECCA HORN", "レベッカ・ホルン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "パフォーマンス、映像、インスタレーションなど多岐にわたって活躍するドイツ人アーティスト、レベッカ・ホルンへのインタビューと作品紹介で彼女の実像に迫ったドキュメンタリー。既成のアートの範疇を超えた彼女にしか作れないエロチシズムと、私的な想像力に満ちた作品世界を知ることができる。", "1993-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MALE AND FEMALE", "男性と女性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "ハリウッド・サイレント期の絶頂期に活躍したC・B・デミル監督による恋愛劇。ヨット航海に出たイギリスの貴族たち。だが、嵐に襲われ難破。無人島に漂着するが、下男たちが今度は力を握るようになり、貴族の令嬢メラニーも元下男の一人との結婚を夢見る。そして、再びイギリスに帰り着くと、主従の関係は元に戻り……。絢爛豪華なセット、スワンソンに体現された新しい女性像など、サイレント期のデミル映画の神髄が凝縮された一編。", "1919-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("！［ai-ou］", "！［ai-ou］", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "秋元康が企画を手掛けたオフビートな犯罪ドラマ。ヤクザの下請けで生計を立てる不良中年・修次。ある日、ひょんなことから出会った元銀行マン・圭介とアウトサイダー・元と組み、一獲千金を狙ってヤバイ橋を渡るが……。彼らのマドンナ役で岡部まりが映画初出演。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GO", "GO", "", "", "http://image.eiga.k-img.com/images/movie/1406/315x210.jpg?1455", 122, "高校3年の杉原の国籍は韓国だが、普段はまったく気にしない。桜井という少女とつきあうようになり、いつか自分の国籍を告白しなくてはならないと思っていたある日、同じ国籍をもつ親友に悲劇が起こる。原作は金城一紀の直木賞受賞作「GO」。監督は、昨年の釜山映画祭で国際批評家連盟賞を受賞した「ひまわり」の行定勲。脚本はTV「池袋ウエストゲートパーク」の宮藤官九郎。同番組に出演した窪塚洋介、「バトル・ロワイアル」の柴咲コウら若手人気俳優が共演。", "2001-10-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UP WORLD", "グノーム　ボクの素敵なパートナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "“ルーメン”という宝石を10年に一度地上の太陽にさらす任務を受けて地上に上がってきた地底人のグノーム。彼はそこでおとり捜査の現場に遭遇、ひょんなことからルーメンは刑事の手に渡ってしまう。やっとのことで刑事の家に潜入したグノームだが、そこで刑事とコンビを組むことになって……。グノームのキャラクターが愉快なファンタジー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("九一神鷲狹侶／SAVIOUR OF THE SOUL", "アンディ・ラウ 神鳥伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "A・ラウが、自ら設立した製作会社“チームワーク(天幕)”の第１回作品。それぞれ特殊技能を持つ、男二人女一人のシティハンター。彼らの三角関係と、圧倒的強さを誇る敵との戦いを描いた、ファンタスティックなアクション映画。刀身が自由に曲がる剣を持ち、空に陸にとアンディが大活躍。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("市川崑物語", "市川崑物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "「スワロウテイル」「リリイ・シュシュのすべて」の岩井俊二監督が、日本映画界が誇る巨匠・市川崑の軌跡をたどったドキュメンタリー。これまであまり語られることのなかったアニメーター時代の様子などを貴重な映像資料を交えながら振り返るほか、公私に渡るパートナーとして知られる脚本家・和田夏十とのエピソードなどを、市川監督から多大な影響を受けたという岩井監督ならではの視点で描いていく。", "2006-12-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花", "花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "数々の国内映画賞を総ナメにした「GO」の原作者・金城一紀の短編集に収録された同名小説を映画化。脳に腫ようが見つかり不安に駆られる青年と、末期ガンの初老の男が、東京から鹿児島まで車でひた走る旅へ。その旅を通し、二人が自身を見つめ直していく。主人公を演じる大沢たかおと柄本明の好演、クラシックギタリスト・村治佳織の奏でる音楽が印象的。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAINT−EXUPE｀RY ： LA DERNIE｀RE MISSION", "サン＝テグジュペリ 星空への帰還", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "名作『星の王子さま』で知られる作家サン・テグジュペリ。作家であり飛行家でもあった彼のロマンに満ちた半生と、謎に包まれた“最期の２日間”を描く。自転車に翼をつけて空を飛ぼうとしたわんぱく少年から成長した彼は、兵役中に飛行機操縦の技術を身につけ、航空会社に就職。この頃に数々の作品を発表。その後、祖国解放のためにアメリカの北アフリカ基地の偵察機隊に所属するが、９度目の偵察で帰らぬ人となる。", "1994-01-01", "2,7,77,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVELINES", "Rock'n'Rollラブライン はいすくーるH白書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ロック・バンドの人気ボーカリスト同士が恋に落ちる。しかしお互いの高校がライバルであるため、恋の行方はままならない。そこで二人は、高校生のための電話相談室“ラブライン”に助けを求める。女子高生の水着が裂けたり、教材用テープがポルノにすり替えてあったりとエッチなシーン満載。テーマ曲“ラブライン”ほか12曲のロックが映画を盛り上げている。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CURSE OF THE PINK PANTHER", "ピンク・パンサー5 クルーゾーは二度死ぬ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ピーター・セラーズ亡きあとに作られたシリーズ第６作で、セラーズことクルーゾー警部が突如失踪、彼の行方を追うというお膳立てが用意された。ラストに、ロジャー・ムーア扮する“整形したクルーゾー”が登場するあたり、おかしいやら哀しいやら。はからずも怪盗役ニーヴンの遺作となる。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はぐれ刑事・純情派", "はぐれ刑事・純情派", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "捜査にはことのほか厳しいが、やむなく犯罪を犯した者への温情は忘れない山手中央署の人情刑事・安浦。家に帰れば二人の娘と暮らす良き父でもある彼の活躍を描いた人気TVドラマの映画化。娘に想いを寄せる若者とその妹が関係する殺人事件の捜査を進める彼の姿を、エリート刑事との対立も交えて描く。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若さま侍捕物手帖 鮮血の晴着", "若さま侍捕物手帖 鮮血の晴着", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "江戸の深夜に暗躍する強盗団に窮した与力・佐々島に相談を持ちかけられた若さまは事件の夜に古寺の境内で殺された質屋・阿波屋六左衛門の身元を洗い始める。その裏に潜む旗本と御家人の陰謀を知り若さまは……。巧みに構成された推理ものと大川橋蔵の立ち回りが楽しめる娯楽時代劇。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Another Happy Day", "アナザー・ハッピー・デイ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Un Prophete", "預言者", "", "", "http://image.eiga.k-img.com/images/movie/55346/315x210.jpg?3548", 150, "「真夜中のピアニスト」のジャック・オーディアールが現代の刑務所を舞台に描く作品。第62回カンヌ国際映画祭でグランプリを受賞した。刑務所で6年の刑に服す19歳のアラブ青年マリクは、所内を仕切るコルシカ人のマフィア・グループに奴隷のように扱われていた。そんな環境の中、マリクは徐々に読み書きを覚え、刑務所での独自のサバイバル方法を身につけていく……。日本ではフランス映画際2010にて初上映。12年、劇場公開。", "2012-01-21", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SONG OF BERNADETTE", "聖処女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 156, "ナポレオン３世統治下のフランス、スペイン国境近くの寒村が舞台。村はずれの洞窟で聖女を見たという美しい娘が魔女裁判にかけられながらも、最後は聖女として認められるまでを描く。舞台、人物ともフランスの設定だが、100パーセントのアメリカ映画。聖女を演じたJ・ジョーンズはオスカーを獲得した。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秦・始皇帝", "秦・始皇帝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 200, "「敦煌」に先立つこと26年前、すでに大映が中国を舞台に作り上げていた超大作。「釈迦」に次ぐ日本の70ミリ映画第２作でもある。奏の始皇帝の波乱万丈の半生を大映オールスター・キャストで描く。始皇帝を演じているのは、勝新太郎。中華民国陸軍および中央電影公司の協力を得ている。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の妻たち", "極道の妻たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "粟津組組長の妻・環と妹の真琴を主人公に、対照的なタイプながらそれぞれ“強い女”である姉妹の情念の葛藤を、五社英雄監督が職人芸の冴えを見せて演出した異色実録ヤクザ映画。原作は『週刊文春』誌上に掲載された家田荘子の長編ルポルタージュ。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOGORA OUVRONS LES YEUX", "パトリス・ルコントのドゴラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "パトリス・ルコント監督がカンボジアロケを敢行して撮り上げた映像詩。ルコントは、フランスの音楽家エティエンヌ・ペルションの曲「DOGORA」に感動し、弟が住むカンボジアで映画を撮ることを決意。彼はプノンペンの通りをトラックやモーターバイクで移動する人々や、湖畔の美しい街並みなどにカメラを向けていく。同国の豊かな自然と人々のあふれる生命力を「DOGORA」の音色に乗せて映し出す。", "2004-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Canal Zone", "パナマ運河地帯", "", "", "http://image.eiga.k-img.com/images/movie/57296/315x210.jpg?4648", 174, "1967年のデビュー以降、現代アメリカ社会を独自の視点で見つめ続けてきたドキュメンタリー作家フレデリック・ワイズマン監督が、長年にわたりアメリカの事実上の植民地だったパナマ運河地帯に暮らす人々の社会を描きだしたドキュメンタリー。1977年、ジミー・カーター米大統領は将来的に運河地帯を返還することを決め、米パ両国で運河を共同管理する新運河条約を締結する。2011年、ワイズマン監督作を一挙上映する「フレデリック・ワイズマンのすべて」にて日本初公開。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勢揃い東海道", "勢揃い東海道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "次郎長が初めて開く花会に詰めかけた諸国の親分衆。吉良の仁吉は兄弟分神戸の長吉から、荒神山の縄張りを安濃徳に奪われたとの報告を受ける。仁吉は義理を尽くして安濃徳に頼み込むが、卑怯な鉄砲で仁吉は倒れ、いよいよ次郎長が立ち上がる……。オールスター・キャストによる股旅もの。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴れん坊街道", "暴れん坊街道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "馬方の三吉は、大名の姫に従って江戸へ上る途中、近江路の宿で浪人くずれの与作と出会う。実は与作は三吉のまだ見ぬ父。おまけに、姫の乳母は三吉の実母。親と子の因果話がやがて悲劇へと突き進んでいく。原作は近松門左衛門の『丹波与作』。依田義賢の脚色を内田吐夢がリアルに描いている。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼくたちと駐在さんの700日戦争", "ぼくたちと駐在さんの700日戦争", "", "", "http://image.eiga.k-img.com/images/movie/53356/315x210.jpg?3475", 110, "人気ブログ小説を原作に、7人の男子高校生と駐在さんが繰り広げる奇想天外なイタズラ合戦を描いた青春コメディ。70年代のとある田舎町。イタズラに明け暮れていた“ママチャリ”ら7人は、町の駐在さんから大目玉をくらってしまう。仕返しに新たなイタズラを仕掛ける彼らだったが、なんと駐在さんもイタズラで反撃してきて……。主人公ママチャリを市原隼人、駐在さんを佐々木蔵之介、駐在さんの妻を麻生久美子が演じる。", "2008-04-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOOLIN’ AROUND", "フーリング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "社長令嬢に恋した青年が繰り広げる愉快な青春物語。科学実験のアルバイトをしたことから研究助手のスーザンと知り合ったウエス。彼女と御曹司を強引に結びつけようとする母親の邪魔にもめげず、愛し合う二人の恋の炎は燃え上がる……。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hafez", "ハーフェズ　ペルシャの詩", "", "", "http://image.eiga.k-img.com/images/movie/53256/315x210.jpg?4490", 98, "「ダンス・オブ・ダスト」「少年と砂漠のカフェ」などで知られるイランのアボルファズル・ジャリリ監督が、古代ペルシャに実在した詩人ハーフェズの詩句にヒントを得て作りあげたラブストーリー。コーラン暗唱者の称号“ハーフェズ”を持つ青年と、彼にコーランを教わることになった宗教者の娘の許されぬ恋を描く。テレビドラマ「時効警察」の麻生久美子がヒロインに抜擢され、初の海外進出を果たしたことでも話題に。", "2008-01-19", "15,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Wedding", "ウエディング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "　富豪コレリ氏の御曹子ディノと運送会社のオーナー、ブレナー氏の娘マフィンの結婚式が行なわれ、その後、コレリ邸でパーティが開かれる。コーディネーターの仕切りで進行していく宴は波乱の連続。花婿ディノの祖母ネティの急死、さらにマフィンの姉パフィがディノの子供を妊娠していた事実も人づてに伝わり始める。花嫁の母チューリップの不倫、突然の大嵐の襲来などてんやわんやに……。ロバート・アルトマン監督がお得意の集団劇スタイルで、個性的な両家の人間模様をユーモラスに描く。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新妻二人組／KILLING　WIVES　SOFTLY", "殺したい妻たちへ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "妻が自分を殺そうとしていると思い込んでしまった男二人が意気投合。逆に協力して妻たちの殺人計画を立てるが……。チョウ・ユンファとケニー・ビーの競演で描いた都会派ラブ・コメディ。ケニーは監督も兼務してブラック・ユーモアに冴えを見せる。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIGARO STORY", "フィガロ・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "“都市”“恋愛”“車”をキーワードに、林海象、C・ドゥニ、A・アグレスティという日米仏の新進映像作家の３人が独自の視点で描いた３部構成のオムニバス。東京、ニューヨーク、パリを舞台に、それぞれ若く多感な男女を主人公に、不思議な恋物語が繰り広げられる。", "1991-01-01", "2,76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PASSAGE DU RHIN", "ラインの仮橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "パリの下町に住むパン屋の娘婿ロジェーと、自由主義の新聞記者のジャン。ともに応召され、ドイツ軍の捕虜となった二人は、ライン河の軍用仮橋の上で知り合う。ジャンは使役を命じられた農村の娘をだまして脱走に成功、パリに潜入してレジスタンスに参加するが、娘に片思いするロジェーの心は深く沈む。", "1960-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZEBRA FORCE", "ゼブラ軍団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ジョンソン中尉率いる黒人ゲリラ部隊“ゼブラ軍団”は、ベトナム戦争帰還後、麻薬の売買でひともうけをたくらんでいた。麻薬シンジケートとの縄張り争い、さらには、警察を加えての抗争は、ますます激化する。ラストの大どんでん返しが見もののB級アクション映画。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHOSE LIFE IS IT ANYWAY？", "この生命〈いのち〉誰のもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "交通事故によって、四肢が麻痺し、生涯を病院のベッドで過ごさなければならなくなった彫刻家のハリスン。彼は、もはや自分は“生きてはいない”と判断し、死ぬ権利を求めて裁判を起こすのだったが……。生と死の問題を重厚なタッチで描いた、B・クラークの同名戯曲の映画化。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAIR DE POULE", "めんどりの肉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "名匠デュヴィヴィエによる、フィルム・ノワールもの。錠前屋のポールとダニエルは、金庫破りの二人組。彼らは、ドライブ・インを経営するトーマと知り合うが、この男の妻マリアがクセ者。トーマの持つ金庫の金を狙い、マリアは二人を誘惑し始めて……。妖艶な悪女、C・ルーベルが魅力的なサスペンス。", "1963-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sunchaser", "心の指紋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "　末期ガンを宣告されたナバホ族出身の凶悪犯ブルーは担当のエリート医師マイケルを人質にして脱走を企てる。ブルーは先住民に伝わる聖なる山を目指そうとしていた。兄をガンで亡くしていたマイケルはブルーに同情し、ふたりは次第に心を通わせていく。途中、強盗まではたらいてしまったマイケルとブルーは警察に追われるが……。西部の荒野で繰り広げられる男たちの逃避行と友情を描く。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLASS OF NUKE’EM HIGH PARTII ： SUBHUMANOID MELTDOWN", "悪魔の毒々ハイスクール２ ヒューマノイド・パニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "人類制覇をもくろむ女博士の遺伝子結合実験によって、下等生物の遺伝子と結合させられてしまった、人間そっくりだが感情を持たない“２級市民(サブ・ヒューマノイド)”。だがこの実験には欠点があり、ヘソだけは悪態をつく口の役割を果たしていたのだ。やがて地獄のヘソマウスたちが、市民権を求めて人類と対決する。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JULES ET JIM", "突然炎のごとく ジュールとジム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "トリュフォーが、心酔するロシェの小説をもとに映画化。モローに捧げた、“女優映画”の最高傑作だ。第一次大戦前夜。モンパルナスで知り合ったオーストリア青年ジュールとフランス青年ジムは、文学青年同士だったこともあり無二の親友となる。ある日二人はカトリーヌという美しく、神秘的な娘に出会い、ともに魅了される。ジュールは彼女に求愛、結婚し祖国に帰った。戦争を経て数年ぶりに、ライン河畔に住む二人を訪ねたジムは、ジュールからカトリーヌと結婚してくれと頼まれる……。男二人と女一人の複雑な愛の心理を、繊細で洗練された演出によって克明に映し出す。とりわけ、自由奔放で少々コケティッシュなモローのこぼれるような微笑を捉えるカメラ・ワークが素晴らしい。それだけでもモローは、“最も愛すべき女性”としてその名を永遠のものにしたといえよう。これは、トリュフォーが、モローを通して全女性に贈る“愛の賛歌”なのだ。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOW AND FOREVER", "情事のあとで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "幸福な夫婦が思いがけない事件をきっかけに大きく揺れ動くロマンティック・サスペンス。ジェシーとイアンは結婚６年目の夫婦。作家のイアンはまだ無名だったが、ジェシーはブティックを経営し、二人はうまくいっていた。そんな時、突如イアンは強姦事件の被告として訴えられてしまい、入獄することになる。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("湖畔の人", "湖畔の人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "源氏鶏太の同名小説を「昭和残侠伝」シリーズの佐伯清が映画化。12歳のとき父が娼婦を追って家出するなど、暗い影を引きずってきた三七子だが、今は恋人との結婚を間近に控え、幸せの絶頂にあった。だが彼女の家庭環境から恋人の両親が結婚に反対、恋人も両親に屈したため彼女の幸せは一挙に崩れてしまう。そんな折、敬愛する課長が交通事故で入院する。三七子が見舞いに行ったのを機に、二人は惹かれ合うが……。佐久間良子がしっとり見せるメロドラマ。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Double Man", "ダブルマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ヘンリー・S・マックスフィールドの小説「スパイの遺産」を映画化したスパイ・アクション。CIA局員のスレイターは、息子がスキー事故で死亡したとの電報を受け取りオーストリアへ向かう。しかしこの知らせは実は東ベルリンの秘密情報機関が仕組んだ偽の情報。外見から癖までスレイターに瓜ふたつの偽者をスパイとしてCIAに忍び込ませ、機密情報を盗み出そうという罠だった。ユル・ブリンナーが主人公とその偽者の2役を演じる。", "1967-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("House of Tolerance", "メゾン　ある娼館の記憶", "", "", "http://image.eiga.k-img.com/images/movie/57596/315x210.jpg?3827", 125, "20世紀初頭パリの娼館で生きる女性たち姿を華麗な映像美で描き、2011年・第64回カンヌ国際映画祭コンペティション部門にも出品されたドラマ。20世紀初頭の華やぐパリ。ひときわきらびやかに輝く高級娼館「アポロニド」の裏側には、女たちの人知れない孤独や苦悩が渦巻いていた。娼館一の美人だったマドレーヌは男にだまされて顔に傷を負い、常連客に本気でほれ込んだジュリーはいつか外に連れ出されることを夢見ている。しかし、娼館と女たちを取り巻く状況は次第に変化していき、アポロニドは閉館を余儀なくされてしまう。", "2012-06-02", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRST LOVE", "銀の靴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「オーケストラの少女」のD・ダービンとTVシリーズ『アンタッチャブル』で有名なR・スタックが共演したシンデレラ・ストーリー。叔父の家に身を寄せた孤児の少女コニーが、舞踏会で片方の靴を脱ぎ忘れてしまう。社交界の若者テッドは、その靴の持ち主を探し出そうとするが……。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TURNER ＆ HOOCH", "ターナー＆フーチ すてきな相棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "異常なまでの潔癖症で知られる捜査官が、殺人事件の犯人を嗅ぎわけることのできるブス犬の面倒を見るハメになったために起きる数々のトラブルを描くコメディ。とてつもなくだらしなく、卑しく、行儀の悪い無愛想な犬フーチが、観ている者にとってはとても可愛らしい。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOSFERATU A VENEZIA", "バンパイア・イン・ベニス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "18世紀末のベニスを舞台に、処女の純愛を求めさまようバンパイア。“彼”は世をはかなみ死を望んでいたが、処女に愛されることによってのみ死ぬことができるという運命を背負っていた。怪優キンスキーが、ヘルツォークの「ノスフェラトゥ」に続いて演じた吸血鬼役で、まさにはまり役。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE OF USHER", "アッシャー家の惨劇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "本作を皮切りにその後エドガー・アラン・ポーの一連の作品を手掛け、一時代を築くことになるR・コーマンのゴシック・ホラー。不気味な館、アッシャー家に伝わる邪悪な血を絶つために起こる惨劇。崩れかけているアッシャー家の雰囲気が巧みに映し出され、実に幻想的だ。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FEARDOTCOM", "フィアー・ドット・コム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "死を呼ぶサイトの謎をスリリングに描いたオカルト調のホラー。日本の怪談を思わせるテイストに加え、サディスティックなイメージを詰めこんだ映像にゾッとさせられる。N.Y.で変死事件が続発し、刑事マイクと保険省調査員テリーは調査を開始する。やがて死亡者がネット上で同じサイトを見ていたことが判明。真相を探るためマイクはそのサイトにログインする。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METAMORPHOSIS", "恐怖の生体実験", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "遺伝子操作によって“不老不死”を実現させようとした科学者が実験に失敗、逆に遺伝子に隠されていた太古の遺伝情報をよみがえらせてしまい、自らは退化の一途をたどる。老人から恐竜へ、そして……。バイオテクノロジーの発展にいささかの警鐘を鳴らした異色のSF恐怖映画。変身シーンが強烈。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Doremifasolrasido", "ドレミファソラシド", "", "", "http://image.eiga.k-img.com/images/movie/56746/315x210.jpg?3399", 105, "高校生の男女3人の恋を描く青春ラブストーリー。バンドでボーカルを担当しているウンギュは、ジョンウォンと出会い恋に落ちる。しかしジョンウォンはバンドメンバー、ヒウォンの幼なじみで、彼らが背負っている過去が明らかなり……。ドラマ「美男（イケメン）ですね」（2009）、「メリは外泊中」（10）などでブレイクした韓国の人気俳優チャン・グンソクの出演作を上映する「チャン・グンソク祭」で日本劇場初公開。", "2011-07-23", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("立つどうぶつ物語", "立つどうぶつ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "2本の足で立つ姿をTVで報じられ、話題となったレッサーパンダ・風太くんの誕生秘話と共に、日本全国の立つ動物を探し回り、動物の進化の本能に迫ったドキュメンタリー。撮影時間780時間をかけ、ミニ豚、ラッコ、シマリスなど100種あまりの動物を追っている。動物たちの立ち姿がとにかく可愛い。立つレッサーパンダ・風太くんの誕生の秘密に迫るべく、祖父のロンロンじいさんを訪ねた。そして明るみになった複雑な家族事情とは……。", "2005-08-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナチュラル・ウーマン", "ナチュラル・ウーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "アルバイトをしながらマイナー雑誌に漫画を発表している25歳の容子。彼女はバイト仲間の由梨子に惹かれているが、かつての恋人、花世の死と、それによって決着のつけられなかった葛藤から自分を解放できないでいた。由梨子はそんな容子に興味を抱き、接近するが……。過去と現在、女性３人の想いが交錯するストーリーが静かに、かつ情熱的に展開する。原作者自らが、佐々木監督とともに脚本も担当し、アンバランスで官能的な世界を作り出している。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ПРЕВРАЩЕНИЕ", "変身", "", "", "http://ecx.images-amazon.com/images/I/51hPSfyxC5L._SL210_.jpg", 87, "カフカの『変身』を、ロシア演劇界の鬼才V・フォーキンが完全映像化。朝起きたらなぜか毒虫に変身していた……という超有名なストーリーを、CGや特撮ではなく、主演俳優ミローノフのパントマイムだけで表現してしまう力技が圧巻。パジャマ姿で指先をもごもごと動かしながら、床や壁を這いずり回るビジュアルが、気持ち悪いやら可笑しいやら。ワケがわからないけれど不思議と笑えるカフカ・ワールドの酩酊感を表現している。失業中の父親に代わって一家の大黒柱として働くグレーゴルは、ある朝目を覚ますと１匹の巨大な虫に変身していた。困り果てて部屋に閉じこもっていると、家族や上司が部屋の扉をこじ開けようと騒ぎ出し……。", "2004-11-13", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Phone Booth", "フォーン・ブース", "", "", "http://image.eiga.k-img.com/images/movie/1596/315x210.jpg?2669", 81, "「タイガーランド」（00）で批評家の絶賛を浴びたジョエル・シュマッカー監督が、同作で注目を集めて今や出演作続々のコリン・ファレルを主演に描く異色サスペンス。自称一流パブリシストのスチュが、公衆電話の受話器を置いた途端にベルの音が。思わず受話器を取ると、正体不明の人物が彼を脅迫、電話ボックスをライフルで銃撃する。いったい誰が何の目的で？　臨場感溢れる撮影はアロノフスキー作常連のマシュー・リバティック。", "2003-11-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("片翼だけの天使", "片翼だけの天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "韓国籍のソープ嬢とハードボイルド作家の愛の成就を描いた生島治郎の告白的恋愛小説の映画化。アクション派の舛田利雄が演出したユーモラスな大人のラブ・ストーリーで、この役に相当入れ込んでいた秋野暢子の“愛しているからチラいんよ”のセリフが公開時流行した。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEART OF MIDNIGHT", "ハート・オブ・ミッドナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "急死した伯父の遺産を相続するため、彼の経営していたナイトクラブに移り住んだ情緒不安定な娘キャロル。静かに暮らそうとしたのもつかの間、彼女に次々と奇怪な現象が襲いかかる。ドキュメンタリー出身のチャップマン監督がスリリングなタッチで心理サスペンスに挑戦した作品。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刑事〈でか〉まつり", "刑事〈でか〉まつり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "上映時間10分以内、１分に最低１回のギャグ、主人公は刑事。そんなルールの下で12人の監督が短編を競うショートフィルム・オムニバス。「アカルイミライ」の黒沢清をはじめ、青山真治、篠崎誠、廣木隆一、万田邦敏、佐々木浩久、西山洋一、山口貴義、奥原浩志らが登板するほか、「リング」の脚本家、高橋洋が待望の監督デビューを果たす。", "2003-01-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Un secret", "ある秘密", "", "", "http://image.eiga.k-img.com/images/movie/57666/315x210.jpg?3733", 110, "フランスでベストセラーとなった同名小説をクロード・ミレール監督が映画化。第2次大戦下のフランスで、あるユダヤ人一家に起こった悲劇を描く。内気で病弱な少年フランソワはある日、屋根裏部屋で古いぬいぐるみを見つけたことをきっかけに、両親の知られざる過去を知る。父マキシムは母タニアと一緒になる前、アンナというユダヤ人女性と結婚していた。しかし、ナチスの存在がアンナの情緒を不安定にさせ、その一方でマキシムは結婚式で出会った美しいタニアに心ひかれていき……。セシル・ドゥ・フランス、リュディビーヌ・サニエ、マチュー・アマルリックら現代仏映画界を代表する豪華キャストが共演。", "2012-04-21", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents うそ　「離さないで」", "桃まつり presents うそ　「離さないで」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 26, "", "2010-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("檻 Prison Girl", "檻 Prison Girl", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「プラトニック・セックス」の加賀美早紀主演によるエロティック・サスペンス。惨殺された妹の仇（かたき）を討つために殺人を犯した麗華は、絶海の孤島に建てられた女子刑務所レディースガーデンに収監される。そこは、サディストの所長による陵辱や女囚たちによるリンチがはびこる地獄のような場所だった。やがて麗華は、妹を殺害した人物の正体を知る。メガホンをとるのは、「集団殺人クラブ　最後の殺戮」の石川均。", "2006-11-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月山", "月山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "山形県の庄内平野の奥にあり、古くから修験道の霊場として知られる月山の寺に、特に目的もなくひと冬を過ごしにやって来た青年の心の内側を描いた作品。外面的な動きに乏しい題材を、村野監督が朴訥な語り口と骨太の造形力でみせてくれる。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふくろう", "ふくろう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "大竹しのぶが2003年モスクワ映画祭主演女優賞を受賞した、新藤兼人監督による異色の人間ドラマ。国の失政で餓死寸前にまで追い込まれた母娘が、生き抜くために男たちをたぶらかし、次々と毒殺していく顛末をブラック・ユーモア満載で描く。単純なプロットの連続で繋いだ密室劇の中に、人間の本質を鋭く見つめる新藤監督の演出が光る。不毛な土地となった東北の山奥にある希望ヶ丘開拓村。村人は次々と去り、最後まで取り残された荒屋に住む母と娘は、激しい飢えに苦しんでいた。限界に達していた二人は、生きるためにある計画を思いつく。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEMORIAL　DAY", "ディープ・ハザード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "悪魔的な兵器をめぐる攻防がスリリングに展開するパニック・アクション。最新鋭の衛星レーザー砲“TARON”のコードナンバーを共産主義復活を目論む秘密結社が奪取し、米国の潜水艦やロシアの宇宙ステーションを爆撃。精神病院に軟禁されている元敏腕スパイ、エドワードは一味に操られ、何も知らぬまま、この危機に拍車をかけてしまう。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("つむじ風食堂の夜", "つむじ風食堂の夜", "", "", "http://image.eiga.k-img.com/images/movie/54726/315x210.jpg?2290", 0, "映像（Cinema）と音楽（Music）のコラボレーションを目指す「CineMusica（シネムジカ）」シリーズ第7弾。人気作家・吉田篤弘の小説を、「地下鉄（メトロ）に乗って」「真夏のオリオン」の篠原哲雄が映画化。主演に八嶋智人、月船さらら、下條アトム、田中要次、スネオヘアー、生瀬勝久ら個性派キャストが脇を固める。雨降り先生の「私」、古本屋の「デニーロの親方」、イルクーツクに行きたい果物屋の青年、不思議な帽子屋・桜田さん、背の高い舞台女優・奈々津さんなど個性的な常連客が集う「つむじ風食堂」のさりげない日常を描く。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十代の性典", "十代の性典", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "思春期映画のはしりとなったシリーズの第１作。17歳の高校生房江、同級生の英子、上級生のかおるらを中心に彼女のボーイフレンドたちと織り成す、愛と性を描く。他愛ない内容ではあるが、当時にしてはショッキングで、学生に見ることを禁ずる学校もでたほどだ。不幸な結末を迎える女学生たちの姿が悲しい。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("似水流年／HOMECOMING", "ホームカミング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1985年の第一回東京国際映画祭ヤングシネマ部門に出品された人間ドラマ。香港で働く若いキャリアウーマンが、中国南部の故郷の村に帰ってくる。そこで彼女は、農夫の妻となった幼なじみの女性と再会するが、今はまったく異なる環境で暮らす二人の友情は、もはや成立しなかった。中国と香港の生活様式や価値観の違いをさりげなく際立たせ、後の香港の中国返還という問題にいち早く迫った野心作だ。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Iron Lady", "マーガレット・サッチャー　鉄の女の涙", "", "", "http://image.eiga.k-img.com/images/movie/57446/315x210.jpg?2796", 105, "イギリス史上初の女性首相で、その強硬な性格と政治方針から「鉄の女」と呼ばれたマーガレット・サッチャーの半生をメリル・ストリープ主演で描いたドラマ。父の影響で政治家を志すようになったマーガレットは1975年、50歳で保守党党首に選出され、79年にはイギリス初の女性首相となった。国を変えるため男社内の中で奮闘するマーガレットは「鉄の女」と呼ばれるようになるが、そんな彼女にも妻や母としての顔があり、知られざる孤独と苦悩があった。マーガレットを支えた夫デニス役にジム・ブロードベント。監督は「マンマ・ミーア！」のフィリダ・ロイド。第84回アカデミー賞ではストリープが主演女優賞を受賞。ストリープは史上最多17回目のノミネートにして、「クレイマー、クレイマー」（79）、「ソフィーの選択」（82）に続く3つ目のオスカー像を手にした。", "2012-03-16", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Duplicity", "デュプリシティ　スパイは、スパイに嘘をつく", "", "", "http://image.eiga.k-img.com/images/movie/54456/315x210.jpg?4310", 125, "「フィクサー」でオスカー候補となったトニー・ギルロイ監督が、ジュリア・ロバーツ＆クライブ・オーウェン主演で放つ犯罪ドラマ。エクイクロム社の社長ディックは、業界トップシェアを誇るライバル会社B＆R社が驚異的な新製品を開発したとの情報を得る。エクイクロム社は情報を入手するために元MI6のレイ、B＆R社は情報を守るために元CIAのクレアをスパイとして雇うが、実はレイとクレアもある計画を企んでいた……。", "2009-05-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金瓶隻艶／GOLDEN　LOTUS", "金瓶梅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "中国の古典「金瓶梅」を映画化したエロティック・ドラマ。明の時代、豪族の西門慶に乱暴された潘金蓮は、性の快楽に目覚めて不能の夫を毒殺。西門慶の第５夫人となり、多くの男性を誘惑していく。無名時代のジャッキー・チェンの出演も見どころ。", "1974-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAFE PASSAGE", "運命の絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "家族の絆を問いかける、豪華キャスト競演のヒューマン・ドラマ。７人の子供に恵まれたマグとパトリックの夫婦は現在別居中。パトリックはヨリを戻そうとしているが、気丈なマグは慈善活動に生きがいを求め始める。そんなある日、息子パーシバルの所属する中東の海兵隊基地がテロリストに襲撃される。彼の身を案じる家族は、それぞれの思い出をよみがえらせていく。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Questione di cuore", "ハートの問題", "", "", "http://image.eiga.k-img.com/images/movie/55426/315x210.jpg?1618", 104, "脚本家として成功したおしゃべりなアルベルトと、自動車修理工場を経営する堅実なアンジェロは、同じ頃に心臓発作を起こし、たまたま同じ集中治療室に運び込まれる。それまでの人生に何の共通点もなかった2人だが、病室で意外なほど意気投合し、退院してからも友人として交流を深めていく。主演にアントニオ・アルバネーゼとキム・ロッシ・スチュアート。イタリア映画祭2010にて上映。", "2010-04-29", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("What Just Happened", "トラブル・イン・ハリウッド", "", "", "http://image.eiga.k-img.com/images/movie/55566/315x210.jpg?2764", 104, "「アンタッチャブル」「ヒート」「ファイト・クラブ」などで知られるハリウッドの名プロデューサー、アート・リンソンの回顧録を「ワグ・ザ・ドッグ／ウワサの真相」のバリー・レビンソン監督＆ロバート・デ・ニーロ主演で映画化した群像コメディ。映画プロデューサーのベン（デ・ニーロ）はショーン・ペン主演のアクション映画を製作するが、モニター試写で観客、製作陣の双方から酷評される。2週間後に控えたカンヌ映画祭への出品のため、ベンは芸術家気質の監督にラストシーンの再編集を命じる一方で、新作にも着手するが、主演のブルース・ウィリスとの間にトラブルが起こり……。", "2010-09-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふ・し・ぎ・な BABY", "ふ・し・ぎ・な BABY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "1980年代後半に、一世を風靡したアイドル・グループ、光GENJIが二つに分かれてそれぞれの主演作を競いあった青春映画の“GENJI”主演編。GENJIのメンバーが偶然手にした赤ちゃんは、なんと超能力の持ち主。不思議な力でメンバーに次々と幸運をもたらす。しかし怪しい３人組が赤ちゃん奪取を目的に、GENJIに近づき始めた。脚本を「NIGHT HEAD」の飯田譲治が手掛けている。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("色即ぜねれいしょん", "色即ぜねれいしょん", "", "", "http://image.eiga.k-img.com/images/movie/53786/315x210.jpg?2897", 114, "「アイデン＆ティティ」でもタッグを組んだみうらじゅんの同名小説を、個性派俳優の田口トモロヲが映画化。京都の仏教系男子高校に通う純は、ボブ・ディランに憧れる平凡な文科系男子。初恋の女子に告白できずに悶々としていた純はある日、童貞仲間に誘われるがままフリーセックス主義者が集まると噂される島へ行くことに。そこでオリーブと名乗る美少女に一目ぼれする純だったが……。ロックバンド「黒猫チェルシー」の渡辺大知が、映画初出演で主人公の純を好演。", "2009-08-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROVOCAZIONE", "青い挑発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "オマセな姉妹キキとヴィヴィは、父の死後、継母と３人で海辺の別荘暮らし。姉妹の家庭教師ロベルトは、なかなかの好男子で、彼女たちは彼を誘惑しては面白がっていた。ところが、ロベルトが継母の愛人だと知って……。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版カルテット", "劇場版カルテット", "", "", "http://image.eiga.k-img.com/images/movie/56766/315x210.jpg?2171", 150, "直木賞作家・大沢在昌の原作を、福田沙紀と松下優也のダブル主演でドラマ化。全9話を劇場公開版として再編集した。最愛の弟を謀殺されたシュンと、幼いころに家族を惨殺されたタケルは、ナゾの公安関係者クチナワの依頼を受け、秘密潜入捜査を手伝うことに。違法ドラッグの密造が行われているという不法滞在者の居住区ミドリ町に潜入し、危険なミッションに挑む。", "2011-06-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("(500) Days of Summer", "（500）日のサマー", "", "", "http://image.eiga.k-img.com/images/movie/54686/315x210.jpg?2789", 96, "建築家を夢見つつもグリーティング・カード会社で働くトムは、社長秘書として入社してきたサマーに一目ぼれをする。運命の恋を信じるトムは果敢にアタックし、遂に一夜を共にするのだが、サマーにとってトムは運命の人ではなく、ただの「友だち」でしかなかった。そんな、トムとサマーの500日の出来事を軽快に描くビター・スウィートなラブコメディ。主演は「G.I.ジョー」のジョセフ・ゴードン＝レビットと「ハプニング」のズーイー・デシャネル。監督はCMやミュージック・クリップなどで活躍する映像作家のマーク・ウェブ。", "2010-01-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE BELVE", "猛獣大脱走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ヨーロッパの大都市の動物園。ここではすべてがコンピュータで管理されている。ところがこの最新装置が故障。猛獣たちが脱走し、町はパニック状態に陥る。巨象の群れが空港に、地下鉄にはトラが……。人喰いシーンや象が美女の顔面をグチャリと踏みつぶす場面など、残酷な描写が強烈。", "1983-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("卒業", "卒業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「13階段」の長澤雅彦による、ちょっとビターな父と娘の愛情物語。映画初主演となる内山理名がけなげなヒロインを等身大で好演。その頑張りが印象的だ。突然の雨の日、傘を手渡すことで実の父親である真山と接触した娘の麻美。それからというもの、彼女は娘ということを伏せながら真山と会う。やがて彼女は彼が未だに母を愛し、見たことのない娘に対する責任で深く思い悩んでいることを知る。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Next Three Days", "スリーデイズ", "", "", "http://image.eiga.k-img.com/images/movie/55876/315x210.jpg?3683", 133, "「クラッシュ」「告発のとき」のポール・ハギス監督が、2007年の仏映画「すべて彼女のために」をラッセル・クロウ主演でリメイクしたサスペンスドラマ。大学で教鞭をとるジョン・ブレナン（クロウ）の妻ララが、ある日突然殺人罪で逮捕され刑期3年を言い渡される。以降、ブレナンは妻の無実を証明するために奔走するが、彼らの上告が棄却されると、妻の脱獄を計画する。共演にリーアム・ニーソン、エリザベス・バンクス。", "2011-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HISTOIRE　DE　MARIE　ET　JULIEN", "Mの物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "「美しき諍い女」でカンヌ映画祭のグランプリに輝いた名匠J・リヴェット監督が、E・ベアールと再タッグを組んで描いた恋愛劇。1975年に企画、撮影を開始しながらも、３日目で放棄されたという幻の作品。28年もの年月を経て、リヴェットは初めて官能シーンを描くとともに、緻密に構成された極上のミステリーとして、長年の念願を昇華してみせた。時計技師のジュリアンはある日、一年前に知り合った女性マリーと再会する。二人は翌日あらためて会う約束をするが、約束の場所にマリーは現れなかった。その後、突然ジュリアンの家を訪れたマリーは、彼を夕食に招待する。それぞれに秘密を抱えながらも、激しく互いを求め合う二人だったが、やがてジュリアンは受け入れがたい衝撃の事実を知る。", "2003-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BERGE｀RE ET LE RAMONEUR", "やぶにらみの暴君", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "生来のやぶにらみであるシャルル16世は暴君としてタキカルディ国を独裁していた。捕らえた鳥をピストルの練習台に射ち殺し、肖像画を描かせては気に入らないと画家を殺す。そんな国王の寝室に飾ってあった絵の中の煙突掃除の少年と羊飼いの少女は、ある夜駆け落ちしようと絵から抜け出す。しかし肖像画の国王が気付き、二人の邪魔をする。鳥の助けで煙突から逃げ出した少年と少女だが、本物の国王を殺した肖像画の王様は国中の警察をあげて二人の捜索を始める……。表情豊かなキャラクターと美しい色彩、そして想像力あふれる場面展開は、世界のアニメ映画史上に残る大傑作。", "1952-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOME CAME RUNNING", "走り来る人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "放浪の末、帰るつもりのなかった故郷へ帰ってきてしまったデイブは、酒びたりの日々を過ごす。やがて彼は正体なく酔っぱらい、何となくこの町まで連れてきた女ジニーと結婚しようとする。それは彼女を愛しているからではなく、すべての希望を失ったからだった。つかの間の幸せに酔う二人の背後に、ジニーと以前関係のあった男レイモンドの銃口が向けられる。名匠V・ミネリの見事な演出がそれぞれの俳優の個性をうまく引き出している愛憎ドラマ。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハート・オブ・ザ・シー", "ハート・オブ・ザ・シー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ミュージシャン、杉山清貴と2001年、他界したプロ・ボディボーダー、四方田冨士子との逸話をきっかけに出来たハートフル・ドラマ。舞台となる千葉県和田浦の美しい海の風景が素晴らしい。東京から美しい海の広がる故郷の千葉、和田町に戻った典子。あるプロの女性ボディボーダーに憧れる彼女はボディボードを始めると、やがて知人らとビーチ・コンサートを開こうと奔走する。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STORMY　WEATHER", "陽のあたる場所から", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "エロディ・ブシェーズが医師として人としての立場に苦悩しながらも、凛と生きる研修医を好演した人間ドラマ。自閉症のロアを演じたのは映画初出演のアイスランドの詩人。真実の愛情を求めた二人の女性の深い絆が力強く描かれている。研修医のコーラは身元不明で心の病を抱える女性患者ロアと出会った。心を通わせ始めた頃、祖国に強制送還された彼女をコーラは無心に追いかけていく。", "2005-01-22", "2,77,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CITE´ DE LA PEUR", "カンヌ映画祭殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "舞台はヨーロッパ映画界最大のイベント、カンヌ国際映画祭。ここに出品されたホラー映画そのままに連続殺人事件が起こり……。男好きの宣伝ウーマン、オツムの弱い主演俳優、下ネタ大好きのボディガードの３人から成る爆笑トリオ“レ・ニュル”が大暴れする、パロディー続出のフレンチ・コメディ。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沈黙の神拳 TRUE JUSTICE PART6", "沈黙の神拳 TRUE JUSTICE PART6", "", "", "http://ecx.images-amazon.com/images/I/515xPXlMeyL._SL210_.jpg", 90, "大都会シアトルを舞台に、犯罪者たちと戦う特別捜査班「SIU」の活躍を、アクション俳優スティーブン・セガール主演で描いたクライム・アクション・シリーズ「TRUE JUSTICE」の第6弾。連続レイプ殺人事件の捜査に乗り出したSIU。そんな中、リーダーのケインが事件現場付近で何者かに襲撃される。さらに、ケインのオフィスからは盗聴器が見つかった。一方、ロシア人組織による美人モデル殺害事件が発生し……。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAMMETT", "ハメット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ハード・ボイルドの元祖ダシール・ハメットを描いたジョー・ゴアズのベスト・セラーをコッポラが製作総指揮で映画化、ベンダースが監督を務めた。探偵小説をタイプし終えたハメットの許に、探偵社時代の仲間が訪ねてくる。消えた中国娘を探す彼に頼まれチャイナタウンを案内するハメットだったが、やがて巨大な陰謀に巻き込まれていく……。", "1985-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クラシコ", "クラシコ", "", "", "http://image.eiga.k-img.com/images/movie/55966/315x210.jpg?2215", 97, "日本サッカーのプロリーグ「Jリーグ」の下部にあるアマチュアリーグ「JFL」、そのさらに下に位置づけられる「地域リーグ」を題材にしたドキュメンタリー映画。地域リーグのなかでも激戦区と言われる北信越リーグで長年にわたってライバル関係にあるAC長野パルセイロと松本山雅FCとの因縁の対決（＝クラシコ）にスポットを当て、信州ダービーでの決戦が行われるまでの選手とサポーターたちの1年間を追う。タレントの安めぐみがナレーションを担当。", "2011-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Miller's Crossing", "ミラーズ・クロッシング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「赤ちゃん泥棒」のコーエン兄弟が、暗黒街でしのぎを削る男たちの友情と裏切りを斬新な映像で描いたギャング映画。イタリア系ギャング団とアイルランド系ギャング団の抗争が渦巻く、禁酒法時代のアメリカ東部の町。アイルランド系のボスであるレオとその片腕として働くトムは固い友情で結ばれていたが、ある女性が原因で仲違いする。やがて博打で借金を負ったトムは、敵対していたイタリア系のボスの下で働くことになるが……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Twilight Saga: Eclipse", "エクリプス　トワイライト・サーガ", "", "", "http://image.eiga.k-img.com/images/movie/55256/315x210.jpg?4496", 124, "バンパイアと人間の禁断の恋を描いたステファニー・メイヤーのベストセラー小説の映画化「トワイライト」シリーズの第3作。前作で禁断の壁を乗り越え結ばれたベラとエドワードに、凶暴なバンパイア集団の魔の手が迫る。エドワードはベラを守るため、恋敵のオオカミ男・ジェイコブと手を組むことに。バンパイアとの全面戦争を前に、ベラの恋心は2人の間で大きく揺れ動く。主演は前2作同様クリステン・スチュワート＆ロバート・パティンソン。監督に「30デイズ・ナイト」のデビッド・スレイド。", "2010-11-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("約束の地", "約束の地", "", "", "http://image.eiga.k-img.com/images/movie/54246/315x210.jpg?4072", 93, "「ニュースター・シネマ・コレクション」として、エイベックスが同社の新人を起用し製作した青春映画の第2弾。九十九里の乗馬クラブで働く優は若くして両親を亡くし、心を許せるのは父から引き継いだ愛馬エリザベスだけ。しかし、とある少年がエリザベスに興味を持ち、毎日のようにクラブにやってくるようになると、その少年に対し、優は次第に閉ざしていた心を開き始める。主演はTVドラマ「四つの嘘」の伊藤ゆみで、本作が劇場デビュー作。", "2009-01-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吉祥天女", "吉祥天女", "", "", "http://image.eiga.k-img.com/images/movie/34036/315x210.jpg?1369", 116, "「BANANA FISH」などで知られる人気漫画家・吉田秋生の同名作品を、「富江」シリーズの及川中監督が映画化。呪われた天女の血を継ぐ叶家の少女・小夜子が、12年ぶりに故郷に戻ってきた。小夜子と同じクラスになった平凡な女子高生・由以子は、ミステリアスな雰囲気の彼女に憧れを抱きはじめる。叶家の土地を狙う遠野家の兄弟・暁と涼もまた、小夜子に惹かれてゆくのだが……。鈴木杏が妖艶な魅力を持つヒロイン役に挑戦し新境地を開拓。", "2007-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大往生", "大往生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "名言を集めた永六輔の同名エッセイに基づく“劇団1980”の演劇を映画化。養老院、天神荘に住む10人の老人たちは、ボケ防止の新薬“DEM30”の話題で大騒ぎ。日本では2002年に売り出されるらしく、現在は簡単には入手できないとか。そんな噂の中、一人の老人がDEM30を入手しようと、悪戦苦闘を繰り広げる。“恋文の里”として有名な秋田県二ツ井町でロケを敢行。風景ののどかさが絶妙の効果を上げている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COBRA MISSION ２", "殺戮軍団 マッド・コブラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "数年前に退役していたロジャーに、再び軍の極秘任務の依頼がくる。CIAが南米某共産国家の独裁者暗殺を計画しているのだという。アメリカ国家のためならと重い腰をあげたロジャーは南米へ赴き、現地のゲリラ部隊と合流し、独裁者暗殺に成功する。しかしこの計画はソ連側が仕組んだ巨大な罠だった。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Quantum of Solace", "007 慰めの報酬", "", "", "http://image.eiga.k-img.com/images/movie/53186/315x210.jpg?4191", 0, "007シリーズ第22作。前作「カジノ・ロワイヤル」の続編にあたり、恋人ベスパーを失ったジェームズ・ボンドが復讐と任務の狭間で苦悩する姿を描く。ベスパーの死の真相を探るためハイチに飛んだボンドは、奇しくも知り合った美女カミーユを通じ、ベスパーを死に追いやった謎の組織の幹部ドミニク・グリーンに接近する。彼の巨大な陰謀を知ったMI6はボンドに任務を課すが……。監督は「チョコレート」「ネバーランド」のマーク・フォースター。", "2009-01-24", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AMOUR EN DOUCE", "優しく愛して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "有能な弁護士だが女性には目のない浮気者のマルクは、妻ジャンヌに離婚を言いわたされている。彼女には新しい恋人アントワーヌがいた。ある日、マルクは高級コールガールのサマンタと知り合い夢中になるが、彼女はジャンヌと意気投合してしまい、４人の男女の奇妙な共同生活が始まる。ベアールの初主演作で、E・モリナロが監督したラブ・コメディ。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小さな沼の物語　ベニイトトンボの詩", "小さな沼の物語　ベニイトトンボの詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "山々に囲まれた小さな沼。そこに住むトンボのリベラ、スリム、そしてリュウの３兄弟が、美しくも厳しい自然の中で成長していく過程を描いたもの。声優を使い、記録映画にドラマの要素を加えている。製作は、これまでに動物映画の佳作「キタキツネ物語」も作った実績のあるサンリオ。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SINNERS", "シナーズ・　背徳街〈ニューヨーク〉の銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "N.Y.のイタリア人街を舞台に繰り広げられる。背徳と狂気を描いた異色作。娼婦とのSMプレイの妄想に悩まされる牧師、アル。実の妹ソフィに恋をしたアルの兄、ジノ。妻を持ちながら、ソフィと関係してしまう従兄弟のニノ。異常な人間関係はやがて、狂気の暴力へと彼らをかき立てていく……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長意外伝 灰神楽木曾の火祭", "次郎長意外伝 灰神楽木曾の火祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "金貸しの銭屋太郎衛門の妾、お滝の罠にはまって銭屋殺しのぬれ衣を着せられた三太郎。汚名を晴らすためにお滝捜しの旅に出た彼は、銭屋が故郷の木曽の財宝を隠していたことを知り、現地へと向う。それを聞きつけた次郎長一家も木曽へ。だがお滝はすでに殺されてしまっていた。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PART IV 破産−乗っ取り", "難波金融伝 ミナミの帝王　劇場版PART IV 破産−乗っ取り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "バブル経済崩壊後の日本を代表するヒーローとなった高利貸し・萬田銀次郎の生きざまを描いたシリーズ第４作。九州の水島ビル興業の一人息子・光はミナミの街でブルセラバーを経営し成功を収めていた。だが、店舗買い取り問題などで大金が必要となり、銀次郎の元を訪れる。そんな折、銀次郎は光の継父が妻の死をきっかけに光を禁治産者に仕立てあげ、水島家の財産を乗っ取ろうとしている事実を知るのだった……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAN OF ARAN", "アラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "アイルランド西方に位置するアラン島。この厳しい自然の支配下にある孤島にも人間の生活がある。荒れ狂う海に船を出す漁夫のキングと岩だらけの土地を耕す妻のマギー。現地の人々の出演によって、四季を通しての荒々しい自然との闘いを描いた人間ドラマ。", "1934-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Heidi", "ハイジ", "", "", "http://image.eiga.k-img.com/images/movie/33556/315x210.jpg?3352", 0, "日本でもアニメが大流行したヨハンナ・スピリの名作を実写化。幼い頃に両親を亡くした少女ハイジは、アルプスの山奥でたったひとりで暮らしている変わり者の祖父のもとに預けられることになる。2人は徐々に心を通わせるようになるが……。主人公ハイジ役には「イン・アメリカ／三つの小さな願いごと」のエマ・ボルジャー、祖父アルムに扮するのはスウェーデンの巨匠I・ベルイマン作品や「エクソシスト」で知られる名優マックス・フォン・シドー。", "2006-07-15", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE JAZZ SINGER", "ジャズ・シンガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "家業を継ぐことを嫌った青年と父との対立を背景に、ミュージカル・スターとして成功した青年と父との和解をしみじみと描く。主演は後の伝記映画「ジョルスン物語」でこの当時のことが描かれることになる、A・ジョルソン。トーキー映画第１作としても有名。", "1927-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOPPING", "ショッピング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "限りなく現代に近い近未来、イギリスのとある瀕死の工業地帯で繰り広げられる、スリルとスピードに青春を懸ける不良少年少女たちの物語。自動車窃盗グループのリーダー、ビリーは３ヵ月の刑期を終え、刑務所から出て来て、恋人のジョーとすぐに自動車泥棒に戻るのだった。ヒロイン役は、ジュード・ロウの元夫人S・フロスト。", "1993-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MCBAIN", "マクベイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "個性派C・ウォーケンが、「ディア・ハンター」を思い出させるベトナム帰還兵に扮したバイオレンス・アクション。捕虜時代に命を救われた男の妹から、亡くなった兄の遺志を継ぐべく南米の内戦に参加してくれと頼まれたマクベイン。民衆のために再び銃をとった彼の熱い戦いぶりを、渋いタッチで描出する。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("濡れた唇", "濡れた唇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "神代監督の日活ロマンポルノ第１作。性的に満たされない男や、外人相手のコールガールら４人の男女が一緒に逃亡し、タバコ屋を強盗するに至る。男の“屈辱を受けました。腹でも切りますか”というシラけたモノローグが時代の雰囲気を伝えている。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЖЕСТОКИЙ РОМАНС", "持参金のない娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "ロシア国民演劇の創始者と仰がれている劇作家A・N・オストロフスキーの同名戯曲を「ふたりの駅」などで知られるE・リャザーノフが映画化した悲劇的な愛の物語。舞台は19世紀後半、ボルガ河畔にある大きな町ブリャヒモフ。そこに住む没落した貴族の娘ラリーサが、持参金なしで彼女を良家に嫁がせようとする母親や、周囲の身勝手な男たちにほんろうされ、やがて悲しい最期を迎えるまでを描いている。ラリーサを演じるのは、本作がデビュー作となるR・グゼーエワ。彼女の心をもてあそぶ伊達男には、「黒い瞳」の監督N・ミハルコフが扮している。", "1984-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エコエコアザラク　WIZARD OF DARKNESS", "エコエコアザラク　WIZARD OF DARKNESS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "聖華学園高校に一人の美少女が転校してきた。名前は黒井ミサ。真の姿は、黒魔術界で絶大な力を持つ魔術師だ。担任の白井響子の噂を告げようとした学級委員長のみずきが、突然苦しみ出したのを見て、ミサは、学園の中に黒魔術を使う魔術師がいることを察知する……。昭和50年代前半に支持を集め、連載復活により再び人気を博した同名ホラーコミックの映画化。強大な黒魔術パワーを秘めた黒井ミサと、永遠の生命を得るため人間を次々と血祭りにあげていく魔術師の壮絶な戦いを、最新SFXを投入して描く。イギリス映画「ヴァージニア」でデビューした佐藤嗣麻子の監督第２作。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YELLOWSTONE KELLY", "イエローストン砦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1876年のモンタナを舞台に、白人とスー族インディアンの激戦を描くウエスタン。主人公のC・ウォーカー演ずる開拓者は、両者の争いをなんとかして中止させようと苦悩していく。脚色に、コミカルな西部劇演出で知られるB・ケネディ。リメイク版「駅馬車」の監督G・ダグラス作品。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白髪魔女傳／THE　BRIDE　WITH　WHITE　HAIR", "白髪魔女伝　キラーウルフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "明朝末期、乱世の中国を舞台にした伝奇アクション。中原八大門で武道を学んだ一航は、ある日、森で謎めいた美女、狼女と出会い恋に落ちる。だが、彼女は邪教団に養成された殺し屋だった。やがて八大門と邪教団の戦いが始まり、一航と狼女は敵同士として再会。傷を負った狼女を助けた一航は永遠の愛を誓うが……。ワイヤー・ワークによる縦横無尽のアクションは圧巻。ビデオタイトルは「キラーウルフ　白髪魔女伝」、DVDは「永遠的明星レスリー・チャン　メモリアル DVD BOX Vol.２」に収録。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆笑・水戸黄門漫遊記", "爆笑・水戸黄門漫遊記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ウナギ屋の金兵衛、左官の助松、角造の３人は、旅に出て、水戸黄門の一行だと名乗ると誰もが信じてしまう。ところが道中、もう一組の黄門一行が現れてしまったから、さぁ大変。最後には本当の黄門様まで巻きこんで大騒動に発展する。金語楼主演のドタバタ時代劇でアチャコ、トニー谷らのゲスト出演も楽しい。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rise of the Planet of the Apes", "猿の惑星：創世記（ジェネシス）", "", "", "http://image.eiga.k-img.com/images/movie/56076/315x210.jpg?3897", 106, "人気SFシリーズの第1作「猿の惑星」（1968）のプリクエル（前章）。現代の米サンフランシスコを舞台に、遺伝子操作によって高い知能をもち人間たちを支配しようと企むサルたちと、それに対抗する人間たちの戦いを描く。主人公の若き科学者役に「127時間」のジェームズ・フランコ。共演に「スラムドッグ＄ミリオネア」のフリーダ・ピント、「ロード・オブ・ザ・リング」のアンディ・サーキスら。監督は英国の新鋭ルパート・ワイアット。", "2011-10-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Libera Me", "リベラ・メ", "", "", "http://image.eiga.k-img.com/images/movie/1496/315x210.jpg?1869", 119, "スター総出演で描く韓国初のファイアー・アクション大作。CGもミニチュアも使わず、すべて実写で火災シーンに挑戦し、300台の消防車と延べ1580人の消防隊員を動員。製作費は韓国映画史上最高の約4億5000万円。消防署隊員サンウは、かつて同僚を救えなかったという自責の念をもちながら、連続火災事件の犯人を追う。犯人は幼少時の心の傷から事件を起こしていた。題名は劇中のフォーレの鎮魂歌のラテン語題「我を救いたまえ」。", "2001-11-17", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I REMEMBER MAMA", "ママの想い出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "1910年のサンフランシスコ。ノルウェー移民のハンソン一家は、貧しいながらも、しっかり者のママを中心に楽しい日々を送っている。K・フォーブスの小説『ママの銀行預金』とジョン・ヴァン・ドルーテンによるその戯曲化に基づくホーム・ドラマ。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEASTMASTER", "ミラクルマスター 七つの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "D・コスカレリ版『桃太郎の鬼退治』的な作品。太古を舞台に、動物の心を操れる青年ダールが悪徳司祭を打ち倒すヒロイック・ファンタジー。波乱万丈の展開に加え、水浴びするT・ロバーツの色気もピリリと効いた、冒険アクションものの佳編である。", "1982-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHEYENNE SOCIAL CLUB", "テキサス魂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "カウボーイをしていたオハンランは、死んだ弟が経営していた社交クラブを相続することになり、さっそくクラブのある場所へと出かける。ところが下宿だろうと思い込んでいたオハンランの予想に反して、そこは売春クラブで……。G・ケリーが製作・監督を務めた大西部劇。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEGEND OF THE GOLDEN GUN", "ラスト・ガンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "殺された両親の復讐に燃える若者が主人公の西部劇。時代は南北戦争のさなか。クアントリル率いる一味が、わがもの顔でのさばる町で、若者ジョン・コルトンは両親を惨殺され、復讐の機会を狙っていた。コルトンは伝説のガンマン、ジョン・ハマーを見つけ出し、彼の元で腕を磨こうと決心する。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("烈風ACTION!?", "烈風ACTION!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ブルース・リー作品など、60年代から70年代のアクションを意識した和製カンフー活劇。CGやワイヤーワークを一切使わず、あくまで生身の肉体で勝負する、スピード感満点の立ち回りが見もの。ブルース・リー好きの敏幸と香璃は将来に不安を抱くカップル。ある日、香璃は敏幸に、誕生日プレゼントとして古道具屋で買ったヌンチャクを贈る。このヌンチャクには意外な秘密が隠されていた。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霊幻先生／MR.VAMPIRE III", "霊幻道士３ キョンシーの七不思議", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "元祖キョンシー・ホラーのシリーズ第３作。今回は、霊界と現実世界の間の霊幻界から襲いかかるキョンシーたちの摩訶不思議な七変化と、霊幻道士が繰り出す７つの秘術がパワーあふれ、そこに悪霊軍団まで現れて、三つ巴の戦いを見せてくれる。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The World", "世界", "", "", "http://image.eiga.k-img.com/images/movie/1636/315x210.jpg?3998", 133, "「プラットホーム」「青い稲妻」で世界的に注目を集める中国監督ジャ・ジャンクーの最新作。北京郊外にあるアミューズメント・パーク“世界公園”は、パリのエッフェル塔やイタリアのピサの斜塔など世界の名所を縮小して再現したテーマパーク。そこで働くダンサー、タオの毎日の不安と希望を、08年のオリンピック開催に向け変貌していく北京の街を背景に細やかに描く。タオ役はジャ・ジャンクー作品の常連、チャオ・タオ。", "2005-10-22", "76,2,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr. Bean's Holiday", "Mr.ビーン　カンヌで大迷惑?!", "", "", "http://image.eiga.k-img.com/images/movie/53126/315x210.jpg?4479", 89, "ローワン・アトキンソン扮する人気キャラクター“Mr.ビーン”が10年ぶりにスクリーンに復活。イギリスからフランスへと飛び出したビーンが、映画祭真っ只中のカンヌで大騒動を繰り広げる。くじ引きで南仏1週間の旅を当てたビーンは列車に乗り込みカンヌを目指すが、いつものごとくトラブルが続出。偶然出会ったロシア人映画監督を駅のホームに置き去りにしてしまい、列車内に取り残された息子ステパンと一緒に行動することに。", "2008-01-19", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大喧嘩", "大喧嘩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "山下耕作が、「博奕打ち・総長賭博」など“ヤクザ映画”の名手として評価される以前に、東映時代劇路線の中で撮った初期作品。敵の親分を斬った榛名の秀次郎が、将来を誓い合った仲のおかよに別れを告げ、旅に出る。そして３年ぶりに戻ってみると、おかよはかつての弟分の妻に、自分の組は新興勢力に苦しめられる弱体組織になっていた。やがておかよに新興勢力の手が迫り……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Roman Spring of Mrs. Stone", "ローマの哀愁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "T・ウィリアムズの小説『ストーン夫人のローマの春』を下敷きにしたメロドロマ。かつては人気を誇った女優カレン。容色の衰えは隠すことができず、ついに彼女は引退を決意する。やがて彼女は、一人ローマへと旅立ち、そして伯爵夫人のツバメと抜き差しならない関係に陥るが……。名女優V・リー最晩年の作品でもある。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ULTRAMAN", "ULTRAMAN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1966年の誕生以来、40年近くに渡って愛され続けるヒーロー特撮の金字塔「ウルトラマン」が、最新SFX技術によって生まれ変わった。その原点を紐解くかのように、ウルトラマンとしての宿命を背負った男が、次第に完全なるそれへと変貌を遂げていく過程を、感情面を繊細になぞりながらリアルに描いていく。やはり見せ場は苦悩のモンスター“ザ・ワン”との対決。新宿副都心を舞台に、全長40nに巨大化した我らがウルトラマンが、人類の運命、そして自身の家族のために壮絶な死闘を展開する。海上自衛隊の有働は太平洋沖で“青い光”に遭遇。凶悪怪物“ザ・ワン”に変わってしまう。一方、航空自衛隊の真木は退職日のラストフライトで“赤い発光体”と空中衝突してしまうのだった。", "2004-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　仮面ライダーカブト GOD SPEED LOVE", "劇場版　仮面ライダーカブト GOD SPEED LOVE", "", "", "http://image.eiga.k-img.com/images/movie/33596/315x210.jpg?2714", 66, "仮面ライダー生誕35周年記念作品。宇宙空間を舞台に繰り広げられる、ライダー史上最大スケールの戦いを描く。巨大隕石の衝突によりすべての海が干上がってしまった地球に、ワームと呼ばれる生命体が侵入。その脅威から人類を守るため、秘密機関ゼクトによって選ばれた仮面ライダーカブトらが立ちあがった。しかし7年後、ゼクトの中から反乱分子ネオゼクトが出現。ゼクト、ネオゼクト、ワームの3つ巴の戦いが始まる……。", "2006-08-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帰って来た木枯し紋次郎", "帰って来た木枯し紋次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1972～73年にTV放映され、絶大な人気を集めた『木枯し紋次郎』。市川崑監督、中村敦夫主演という、当時のコンビを復活させた、20年ぶりの作品（中村敦夫主演では、1977年にTV『新・木枯し紋次郎』がある）。当初、TVのスペシャル版として作られたが、完成度の高さから、劇場公開された。有名な渡世人、木枯し紋次郎が死んだと言われて５年が過ぎた。だが、彼は木曽山中に木こりとなって生きている。その木こりの頭から、今は渡世人となっている息子の小平次をつれ戻して欲しいと頼まれた紋次郎は、再び渡世の道へ……。笹沢佐保が書き下ろしたオリジナル・ストーリーをもとに、市川監督が映像美タップリに紋次郎を描く。オール・ロケの効果、時代考証にこだわった画作りは、往年のまま。ただかつての躍動感よりも“美”が勝ちすぎた感がある。しかし、現在の時代劇の状況を見ると、その作りの確かさは評価されていい。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チベット犬物語　金色のドージェ", "チベット犬物語　金色のドージェ", "", "", "http://image.eiga.k-img.com/images/movie/57376/315x210.jpg?1581", 91, "「時をかける少女」「サマーウォーズ」のマッドハウスと、中国の映画会社・中国電影集団がタッグを組んで製作した日中合作アニメーション。中国のベストセラー小説を原作に、チベットの大草原でたくましく成長していく少年の姿を描く。漢民族とチベット民族の血を引く少年テンジンは、母親を病気で亡くし、離れて暮らしていた父ラクパのいるチベットにやってくる。父との確執や慣れない土地の生活に戸惑うテンジンは、ある日、クマに襲われたところを金色のチベット犬に助けられる。テンジンはそのイヌをドージェヨンジーと名付けて一緒に暮らし始め、絆を深めていく。キャラクター原案を「20世紀少年」の浦沢直樹が担当した。", "2012-01-07", "76,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD IN BLOOD OUT", "ブラッド・イン ブラッド・アウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 180, "メキシコ系アメリカ人の若者３人の青春と生き様を、エネルギッシュに描いた作品。兄弟のように育ったストリート・ギャングのパコ、クルス、ミクロ。やがて。パコは警官に、クルスはドラッグに溺れながらも画家に、そしてミクロは刑務所内でギャングとなり、３人の間の溝は次第に深まっていく。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Orphan", "エスター", "", "", "http://image.eiga.k-img.com/images/movie/54386/315x210.jpg?3763", 123, "赤ん坊を死産で失い、悲しみに暮れていた夫婦ケイトとジョンは、養子を迎えようとある孤児院を訪れる。そこで出会った少女エスターに強く惹きつけられた2人は、彼女を引き取ることに。しかし、日に日にエスカレートするエスターの不気味な言動に、ケイトは不安を覚え始める。原作はアレックス・メイスの小説で、主演は「ディパーテッド」のベラ・ファーミガ。", "2009-10-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD ORANGE", "血のオレンジドレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "ロンドンのファッション・ハウスで“ブラッド・オレンジ”と名付けられた新作ドレスを身につけたモデルと、顧客たちが次々殺される。私立探偵トムは、時を同じくして起こった宝石窃盗事件とかかわりがあるとみて、調査を開始した。映画“セイント”シリーズで一世を風靡した俳優T・コンウェイをハリウッドから招いて作った作品。", "1954-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dune", "砂の惑星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "映像化不可能といわれたフランク・ハーバートの大長編SF小説を、前作「エレファント・マン」で成功をおさめた鬼才デビッド・リンチ監督・脚本により映画化。“デューン”と呼ばれる砂の惑星アラキスを舞台に繰り広げられる勢力争いを壮大なスケールで描く。後にテレビ放映用にナレーションと未公開シーンを加えた長尺版も製作されており、日本では「デューン／スーパープレミアム［砂の惑星・特別篇］」というタイトルで紹介されている。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Max mon amour", "マックス、モン・アムール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「愛のコリーダ」以来、活動の拠点を海外におく大島監督が、スタッフ・キャスト全員を外国人で製作した問題作。共同脚本をルイス・ブニュエル作品の脚本で有名なジャン・クロード・カリエールが、撮影をゴダール作品を数多く担当したラウル・クタールが担当している。チンパンジーと愛し合ってしまった人妻とその家族の生活を、実話にもとづいて描いた作品。幸せに見える家庭にヒトと動物の中間の猿という際どい座標を設けることで、夫婦間、家族間の隔たりを浮き彫りにする。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東兄弟仁義 任侠", "関東兄弟仁義 任侠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "東京、深川の賭場で出会った、名勝負師の清吉と勇三。勝負は熱戦の末、勇三に軍配があがるが、清吉は勇三のイカサマを見抜いていた。イカサマには理由があった。３年前、イカサマ師を斬った勇三は、その男の女房、お桂を案じ、捜した結果、彼女は幼い息子を預けて遊廓で女郎をしており、その身受金をつくるためだったのだ。薄幸な母子の存在を知った清吉はひと肌脱ごうとするが……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京上空いらっしゃいませ", "東京上空いらっしゃいませ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "新人、榎祐平のオリジナル脚本を映画化したファンタジー。交通事故で死んでしまった新人モデルの女の子が、天国に行くのを嫌がり、幽霊のままこの世の生を謳歌しようとする。彼女のときめきとはかなさを溌刺と描いていく。主演の牧瀬里穂の伸び伸びとした好演をはじめ、彼女をかくまう中井貴一、二役を演じる笑福亭鶴瓶などキャストが充実。長回しショットが役者の肉体を躍動させていく相米演出の妙もうれしい。４人の歌手が別々に歌う『帰れない二人』が、主人公の心情を切々と表現する。クライマックスのジャズ・バーでのミュージカル的演奏シーンの映画的高揚など見どころも多彩だ。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学園広場", "学園広場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "とある街に東京から転校してきた修二。彼はその学校に伝わる、校内一勇敢な男に“勇者の帽子”を授けるという風習がイタズラ半分の悪習と化していることに義憤を抱く。反抗的な修二に制裁を加えようとする猛者たちと渡り合い、帽子は誰の手に渡るのか……。若き倉本聰が脚本に参加した青春熱血もの。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nim's Island", "幸せの1ページ", "", "", "http://image.eiga.k-img.com/images/movie/53216/315x210.jpg?2065", 96, "海洋生物学者の父ジャックと共に孤島で暮らす少女ニムは、冒険小説の主人公アレックス・ローバーの大ファン。ある時、ジャックが行方不明になってしまったニムは、アレックス・ローバーの作者アレクサンドラに助けを求めるのだが……。児童文学作家ウェンディ・オルーの小説を映画化。ニム役は「リトル・ミス・サンシャイン」のアビゲイル・ブレスリン。対人恐怖症で引きこもりの作家アレクサンドラをジョディ・フォスターがコミカルに演じる。", "2008-09-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赫い髪の女", "赫い髪の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "にっかつロマンポルノが生んだ名作中の名作であり神代辰巳の最高傑作の一つ。雨の中、ダンプカーの運転手光造は自動販売機のカップめんをすすっている女を拾う。いたずらをするつもりの光造だったが、その日から女は彼のアパートに住みついてしまう。女は自分のことは何も言わないし、光造もあえて聞いたりはしない。ただ来る日も来る日もセックスを繰り返すだけ。彼女は突然何かにおびえたりするが、過去を持ち込むのはセックスの体位くらいで、光造と一緒にいることをただ喜んでいる。もう若くもない男と女の交接の日々を描きながら、その頽廃感から発せられるのは粉飾ぬきの性愛の姿だ。神代は例によって長回しを多用し、役者の息づかいまでも映し出している。また頻出する雨のシーンに象徴される湿度の高い映像空間を作りあげ、日本の映画が持つ独自の色彩と質感をフィルムに定着させている。その全編からエロスが匂い立つ。性愛の化身ともいうべき女を演じきる宮下順子が素晴らしい。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WENDY CRACKED A WALNUT", "ウェンディの見る夢は", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "主人公のウェンディは平凡な主婦兼OLだが、少々ロマンス小説にかぶれ気味。夫との仲にも微妙な不協和音がきしみ出した頃、彼女は奇妙な男ジェイクとロマンス小説そのままの成り行きに心奪われていく……。小説に熱中するあまり、物語の世界に迷い込んでしまう主婦をコミカルかつファンタジックに描いたラブ・ロマンス。", "1990-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十九歳の地図", "十九歳の地図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ドキュメンタリー「Black Emperor ゴッド・スピード・ユー！」でデビューした柳町光男監督の初の劇映画で青春映画の傑作。新聞配達をしながら予備校に通う19才の主人公の日常から湧き上がる社会への敵意を鮮烈に描いて、従来の青春映画の枠を打ち破った。住み込みの新聞配達店での無為な生活、相部屋の中年男と足の悪いバケモノじみた娼婦とのみじめな恋愛、配達先で受ける屈辱。そんな生活からくるやり場のない怒りに、主人公は脅迫電話をかけ続け、また右翼の宣伝カーに乗った自分を夢想する。配達先の地図に向って各家庭を○×で採点するシーンは絶賛された。青春期のエネルギーの昂まりを実によく捉えている。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wallace & Gromit in A Matter of Loaf and Death", "ウォレスとグルミット　ベーカリー街の悪夢", "", "", "http://image.eiga.k-img.com/images/movie/54546/315x210.jpg?2111", 29, "イギリスの人気クレイアニメ「ウォレスとグルミット」の（長編を除く）シリーズ4作目。奇想天外な発明品でいつも大騒動を巻き起こすウォレスと、そんな主人を守るためいつも大活躍の愛犬グルミットは、今度は街のパン屋になったものの、パン職人だけが狙われる連続殺人事件に巻き込まれて……。シリーズ過去3作「チーズ・ホリデー」「ペンギンに気をつけろ！」「危機一髪！」も新字幕・新吹替・デジタルリマスター版で同時上映。", "2009-07-18", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い牝豹M", "黒い牝豹M", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "横浜の裏の世界を支配する右翼実業家の速水は、広域暴力団・国栄会を敵にまわし着々と実力を蓄えていった。女殺し屋・Mは国栄会会長の密命を受け、速水暗殺に乗り出す。池玲子の日活初主演作で、公開時のカラテ・ブームに悪ノリしてカラテの達人という設定の殺し屋に扮している。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("善魔", "善魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "某ブルジョア・マダムが離婚。それを二人の新聞記者が追いかけていくが……。岸田国士の原作小説を、小津とのコンビで不朽の名作群を生みだした野田高梧が脚色。三國連太郎のデビュー作で、彼の芸名は本作の役名をそのままいただいたもの。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHE−DEVIL", "シー・デビル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ブスで器量なしのヒロインが、美貌の女流作家に夫を奪われたために“悪魔”のごとき復讐を開始するブラック・コメディ。ヒロインのR・バーは、復讐が次々とうまくいくにしたがって可愛らしく映り出し、反対に作家役のM・ストリープは、どんどん化けの皮がはがれていくという凝った趣向である。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Machinist", "マシニスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1年間にも及ぶ常軌を逸した不眠症を患う青年が、悪夢のような事件に巻き込まれるミステリー映画。カフカ的ともデヴィッド・リンチ風ともいえる不条理な映像世界を紡ぎ上げたのは、「セッション9」の気鋭ブラッド・アンダーソン。主演俳優クリスチャン・ベールの激ヤセぶりにも驚愕必至だ。眠れない日々を過ごす機械工トレヴァーが、自宅や勤務先の工場で奇妙な出来事に遭遇。やがて彼はアイバンという大男を怪しむのだが……。", "2005-02-12", "9,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談 鬼火の沼", "怪談 鬼火の沼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "戸畑敬助は、叔父にあたる御数寄屋坊主頭に遊びの金を無心していたが、次第に財布のヒモが固くなるのにいら立ち、殺しを決意する。ところが、実行に移してみると、間違って侍女の八重を殺してしまう。そして敬助は、妹の安否を気づかって訪れた八重の兄・清蔵をも殺害する……。罪もなく殺された兄妹の亡霊の復讐を描いた怪談もの。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いたいふたり", "いたいふたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "斎藤久志監督が「フレンチドレッシング」「サンデイドライブ」に続き三度、唯野未歩子を主演に据えて贈るファンタスティックな恋愛映画。なぜか互いに痛みを共有することになった若い夫婦。片方が殴られれば、片方も痛い。妻が妊娠したことによって、夫は死の恐怖を感じ、尋常ではいられなくなる。夫への思いだけでかろうじてバランスをとっている精神の不安定な妻と、妻を愛しながらも女にはだらしのない夫を、唯野と西島秀俊が即興的な芝居も取り入れながら体現。「由美香」などで知られる平野勝之が撮影を担当、AVクリエイターらしいフレキシブルなカメラワークを見せている。これまでは長回し撮影が多かった斎藤だが、ここでは積極的にカットを割るという新境地を披露。原一男と廣木隆一の両監督が印象的な役どころで好演したり、篠原哲雄や緒方明らが特別出演するなど、映画監督大集合といった趣もある。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バロー・ギャングBC", "バロー・ギャングBC", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "10代の男女が持っている受験や恋愛の悩みを分かち合おうと、海賊放送を引き受けた４人組の男女が、放送を通じて知り合った仲間とともに2,000人のダンス・フェスティバルを成功させるまでを描いた、シブがき隊３本目の主演作。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VOICI LE TEMPS DES ASSASSINS", "殺意の瞬間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "フランス映画界の名匠J・デュヴィヴィエ監督と名優J・ギャバンが、「望郷」以来、実に20年ぶりにコンビを復活させた“悪女もの”の傑作。レストラン経営者アンドレのもとを訪れた若い女性の魂胆とは……。J・ギャバンが男の悲壮感を漂わせて魅力的。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("破戒尼僧YUKI", "破戒尼僧YUKI", "", "", "http://image.eiga.k-img.com/images/movie/56286/315x210.jpg?4094", 32, "さすらいの女性僧侶ユキは、ひょんなことからアイドル志望の貧しい少女リコと出会う。ユキは知り合いのホームレスたちにリコを紹介するが、時を同じくして街には黄色いユニフォームを着たナゾの集団が出現。激しい抗争が勃発する。16ミリ長編作「殺し屋たちの挽歌」（2003）で米ロードアイランド国際ホラー映画祭観客賞を受賞し、「カクトウ便　そして、世界の終わり」（07）で劇場映画デビューを果たした山本俊輔監督が描く異色のガールズ・ソード・アクション。", "2011-08-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BUCCANEER", "大海賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "1812年、アメリカはイギリスとの戦いによって戦略上の要衝を失おうとしていた。そこでこの地を死守するため、海賊のジャン・ラフィットと取引しようと画策する……。自身、俳優として数々の作品に主演しているA・クインが監督を務め、ブリンナーほかオールスター・キャストで描いた海洋ロマン。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女優", "女優", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "新派の女優として人気を博し、映画でも溝口の名作「残菊物語」などに出演した森赫子。彼女が、自らの半生を赤裸々に描いた自叙伝『女優』を新藤兼人が映画化。新藤作品で様々な女性像を演じ続けてきた乙羽信子が、自分の信念を貫くために、あらゆる困難に勇敢にも立ち向かっていく強い女を熱演している。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Callas e Onassis", "マリア・カラス　最後の恋", "", "", "http://image.eiga.k-img.com/images/movie/53266/315x210.jpg?2137", 117, "20世紀を代表するオペラ歌手マリア・カラスとギリシャの海運王アリストテレス・オナシスとの情熱的な愛を中心に、絶頂期のマリア・カラスの波乱に満ちた私生活を映画化した伝記ドラマ。1957年、世界中の賞賛を集める歌姫マリアと大富豪オナシスが運命の出会いを果たした。2人にはそれぞれパートナーがいたが、瞬く間にマリアに魅せられたオナシスは猛アタックを開始する。マリアは全てを捨てる覚悟でオナシスとの恋にのめりこんでいき……。", "2008-01-26", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NUIT ET BROUILLARD", "夜と霧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 32, "アウシュビッツ強制収容所をめぐるレネ監督のドキュメント傑作。忘却され荒廃した現在のアウシュビッツのカラー映像と、戦争を再現するモノクロ映像を対比させて戦争を糾弾、その美しく厳しい映像と詩的なナレーションが胸に迫る1作。", "1961-10-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("友子の場合", "友子の場合", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "『ビッグコミック・スピリッツ』で連載され、TVでも２度ドラマ化されたノンストップ災難コメディ。旅行の途中で友だちに置き去りにされた女子高生が、必死に友だちを追う姿を描く。高校２年生の田村友子は、夏期講習をさぼって男女４人ずつのクラスメートと１泊の温泉旅行に出かけた。途中停車の橋田駅で駅弁を買いに降りた友子は、珍味“たこばしら”に気をとられ電車に乗り遅れてしまう。仲間に追い付こうと温泉宿を目指すがロープウェイは営業が終わっていたり、はたまた仲間とはすれ違いばかりで……。TVに続いて、ともさかの表情豊かな演技、本広克行の凝った映像処理がそれぞれ光る。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LURKING PEAR", "地底人アンダーテイカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "怪奇幻想文学のH・P・ラヴクラフトの短編小説『潜み棲む恐怖』を映画化したモンスター・ホラー。無実の罪で刑務所に入れられたジョンは出所後、父親が大金を埋めた墓地の地図を手に入れる。だが、そこには人間を地中に引きずり込み、血肉を食らう怪物が潜んでいたのだ……。キャスティングも「フレンジー」のJ・フィンチ、「ネクロノミカン」のJ・コムズらマニア好みだ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEARCHING　FOR　DEBRA　WINGER", "デブラ・ウィンガーを探して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "個性派女優R・アークエットの初監督作。プライベートでは母親でもある彼女が、俳優業との両立について悩み、同業者の女優たちへのインタビューを実施したドキュメント。シャロン・ストーンら34人もの有名女優が出演し、自らの人生について誠実に語っている。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Twilight's Last Gleaming", "合衆国最後の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "　刑務所を脱獄した男たちが軍のミサイル基地に侵入し、コントロール・センターを占拠。主犯は、基地の設計者でもある元・空軍大佐デル。彼らはホワイトハウスに対し、ベトナム戦争当時の機密文書の公開、逃亡資金1000万ドル、そして人質として大統領の身柄引き渡しを要求する。マッケンジー将軍らタカ派の高官は犯人一味を抹殺しようとするが……。クセ者キャストを揃えて展開される重厚なサスペンス。アメリカでの撮影が不可能となり、西ドイツにてロケを決行した。", "1977-01-01", "1,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In the Mirror of Maya Deren", "鏡の中のマヤ・デレン", "", "", "http://image.eiga.k-img.com/images/movie/55096/315x210.jpg?4786", 104, "ダンサー、詩人、人類学者、映画作家などさまざま顔を持つ才女マヤ・デレンのドキュメンタリー。1917年の革命の年にロシアのユダヤ人家庭で生まれたマヤは、5歳でアメリカに渡り、26歳でビジュアル・アート史に残る「午後の網目」を発表して一躍脚光を浴びる。その後、ブードゥー教の研究に傾倒し、ハイチでは芸術の女神と崇められるようになるが、44歳の若さで謎の死を遂げる。数々の神話に包まれたマヤの人生を丁寧に紐解いていく。", "2010-01-09", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOLLAR", "ドル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "スキー場にやって来た３組の夫婦と、億万長者のアメリカ女性ミス・ジョンンストン。ジョンストンは、あらぬ噂を立てて夫婦関係を混乱させようとしていたが、彼女自身も許婚に婚約破棄を言い渡されてしまう。一つの受難がいくつものトラブルに発展していくコメディ。バーグマンのコミカルな演技が注目を浴びた。", "1938-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すももももも", "すももももも", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“すももパック”が大好きな18歳の相原小桃は、性に関してオープンな妹・栗子や親友・涼子の価値観にはついていけず、付き合って半年になる直樹とは、まだキスもしていない。小桃がいちばん好きなのは家族でもなく、直樹でもなく、“すももパック”。そんな小桃がある日、事故に遭い、時空を超えて不思議な旅をする。少女から女へと成長する時期に、一人だけの“城”を守っているひたむきさを少女の視線で描き出していく。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EUROPE‘５１", "ヨーロッパ一九五一年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "夫の転勤で、アメリカからイタリアにやってきた中年女性。夫は出世し、彼女はイタリア駐在総代理人夫人として多忙な日々を送る。だが、家族とのスレ違いから我が子が自殺、彼女は孤独に苛まれるようになる……。イタリア映画界の巨匠、R・ロッセリーニ監督が、女性の目を通してイタリア社会の現実と矛盾を見据えた問題作。", "1951-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二十才の微熱", "二十才の微熱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "金が目的でも、女性が嫌いな訳でもないが、ゲイバーで体を売っているクールな大学生、樹。常に他人と距離を置いて生きている、そんな彼の微妙な心情を、周囲を取り巻く男女とのかかわりの中に繊細に綴る。現代の若者たちの感性を、優しい眼差しで捉えたみずみずしい青春映画だ。1989年度PFFアワード・グランプリを受賞した橋口亮輔の劇場映画デビュー作。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("菊次郎の夏", "菊次郎の夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "カンヌ映画祭で高い評価を得た北野武の監督第８作。小学３年生の正男は、夏休みに写真でしか知らない母に会いに行くことを決意。近所のおじさん、菊次郎が保護者として同行することになるが、正男は自分勝手な菊次郎に振り回されてばかり。そんな二人の気ままな旅は、寄り道をしながら何とか目的地にたどり着くのだが……。これまでの作品とは一転、暴力描写の一切ない、少年と中年男の交流をほのぼのと綴るロード・ムービーになっている。少年の孤独感や中年男の優しさを、ぬくもりあふれる眼差しで綴り、笑いと涙のさわやかな感動作となった。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The U.S. vs. John Lennon", "PEACE BED アメリカVSジョン・レノン", "", "", "http://image.eiga.k-img.com/images/movie/53116/315x210.jpg?3225", 99, "全世界に向けて反戦を訴えかけたミュージシャン、ジョン・レノンの真実に迫るドキュメンタリー。その絶大な影響力からニクソン政権に危険視されたジョンは、FBIによる監視や盗聴、さらに国外退去命令まで受けながら、それに屈することなく平和への想いを発信し続けた。オノ・ヨーコへのロングインタビューや関係者らの証言、さらに貴重な未公開映像などを通し、アメリカ政府とジョン・レノンの闘いの真相を明らかにしていく。", "2007-12-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和残侠伝・唐獅子仁義", "昭和残侠伝・唐獅子仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "風間重吉は花田秀次郎との果たし合いで左腕を失った。出所した秀次郎は林田一家の世話になる。樺島一家は採石の入札をめぐって林田と老舗“浅野屋”を圧迫していた。樺島の客人となった風間は、秀次郎に果たし状を渡すが、女房が凶弾に倒れ、秀次郎とともに死地に向かう。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dragon Wars: D-War", "D-WARS　ディー・ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/34206/315x210.jpg?1564", 90, "ロサンゼルスを舞台に人類壊滅の危機を描く韓国映画アクション。主演は「THE JUON／呪怨」のジェイソン・ベア。広範囲にわたる謎の地面の陥没が発見され、現場に駆けつけたリポーターのイーサンは、幼い頃の記憶に残るある人物がその惨劇に関係することに気づく。その女性を探し出すイーサンだったが、街は未知の怪物に襲撃され、やがて米軍の最新兵器とモンスターの壮絶な戦いへと発展していく……。", "2008-11-29", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白昼の通り魔", "白昼の通り魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "昭和32年頃、関西各地で起きた凶悪事件をもとにした短編の映画化。信州の農村。村長の息子に体を代償にして資金提供を受けて事業をはじめた村娘シノだが、ある日息子に無理心中を迫られる。しかしシノだけは生き残り、彼女を案じてあとをつけてきた英助に気絶しているところを発見される。しかし、シノに思いを寄せる英助はその場で彼女を犯す。その快感が元で、彼は“白昼の通り魔”となってしまう。全編を通し、男と女が激しく心の火花を散らす修羅場のみで構成される挑戦的な構成。ショッキングな犯罪とエロチシズムの中で、人間の心の奥底に潜む悪魔と生命力を描く大島渚監督の問題作。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Splendor", "アメリカン・スプレンダー", "", "", "http://image.eiga.k-img.com/images/movie/1256/315x210.jpg?3795", 101, "オハイオ州クリーブランドで病院事務員として働きながら、自分を主人公に日々日常を描くカルト・コミック「アメリカン・スプレンダー」を76年以来、年1回自費出版で刊行し続けた実在のコミック作家ハービー・ピーカー。彼の半生を、俳優が演じるドラマと本人たちも登場するドキュメンタリー、彼のコミックの画家によるアニメーションなどを交えて描写。アカデミー脚本賞にノミネート、全米批評家協会賞の作品賞、脚本賞を受賞。", "2004-07-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("波影", "波影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "豊田四郎監督が脚本に八住利雄、撮影に岡崎宏三、美術に伊藤熹朔と不動のパートナーを配して水上勉の原作に取り組んだもの。娼家“征木家”でただひたすら他人に尽くし、無償の愛を与え続けて死んでいく雛千代という女の姿を、この娼家を営む主人の娘の眼を通して描く。雛千代役は「雁の寺」「越前竹人形」でも水上作品のヒロインを演じた若尾文子。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蘇りの血", "蘇りの血", "", "", "http://image.eiga.k-img.com/images/movie/54356/315x210.jpg?3709", 83, "「青い春」「空中庭園」の豊田利晃が、歌舞伎や浄瑠璃の演目として知られる「小栗判官」をモチーフに描く寓話。天才按摩のオグリは闇の世界を支配する大王の砦に招かれるが、忠誠を誓わなかったため大王に殺されてしまう。生死の境をさまよったオグリは、身体の自由を奪われた「餓鬼阿弥」の姿として再び現世へと戻ることになる。そこで少女テルテに出会い、テルテは「蘇生の湯」でオグリを蘇らせようとするが……。主演は元「BLANKEY JET CITY」のドラマー・中村達也。", "2009-12-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("楓ニュータウン", "楓ニュータウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 28, "漫画家、イラストレーター、PV監督など多彩な分野で活躍するクリエイター、いわきりなおとが手がけた短編アニメーション。大阪郊外のニュータウンで暮らす小学5年生のミキオは、クラスメイトのハルコに恋心を抱いていたが、恥ずかしがり屋の性格が災いして話すこともできずにいた。そんなある日、ミキオは友達のメガネ君から、ハルコが遠くへ引越してしまうと知らされる。悩む彼の前に、おばけのキュピオが姿を現わし……。", "2007-12-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EPIDEMIC", "エピデミック～伝染病", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「ヨーロッパ」「キングダム」などで知られるデンマークの鬼才L・V・トリアーが創造したモノクロームの幻惑的な映像世界。トリアー自身が演じる映画監督と脚本家が書きためたシナリオが、コンピュータから消失してしまう。そんな彼らが新たに書き出した脚本『エピデミック』の中で繰り広げられるウイルスの悪夢と、ヨーロッパ全域に忍び寄るもう一つのウイルスの恐怖が不安げにヴィジュアル化される。日本では「奇跡の海」の封切りに合わせて、1997年に初公開された。", "1987-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DAY OF THE JACKAL", "ジャッカルの日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "フォーサイスの出世作となった同名小説の映画化。フランスのドゴール大統領暗殺をもくろむ殺し屋“ジャッカル”の、謀略決行と失敗までの行動を描く。付随的ドラマをそぎ落とした、あくまでも簡潔で客観的なカットの積み重ねが、息詰まるような緊迫感を盛り上げる社会派サスペンスの秀作。あくまでも冷徹に狙撃の準備をすすめる“ジャッカル”をE・フォックスが見事に演じきり、物語をさらに引きしめたものにしている。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Per un pugno di dollari", "荒野の用心棒", "", "", "http://ecx.images-amazon.com/images/I/317BpZr01QL._SL210_.jpg", 97, "ニュー・メキシコ国境の町サン・ミゲル。この町ではロホ一家とモラレス一家が血生臭い争いを続けていた。そこにフラリと現れた風来坊のガンマンは、町民が彼らの犠牲になっていることを知り、ワナを仕掛けて無法グループを同時に壊滅させようとする……。黒澤明の「用心棒」を西部劇に翻案したこの作品は、そのすさまじい殺りく描写と奇想天外なアイデアで、レオーネ自身が予想もしなかったほど世界中で大ヒットし、マカロニ・ウエスタン・ブームの先鞭をつけた。ひげ面に葉巻をくわえたポンチョ姿のイーストウッドにとっては、大スターへのきっかけを作ったエポック・メイキングな作品となった。", "1964-01-01", "4,7,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAGNIFICENT AMBERSONS", "偉大なるアンバーソン家の人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "デビュー作「市民ケーン」で後の映画に多大な影響を与えた天才、O・ウェルズが監督した第２作。19世紀後半、地方の町が都市へと移り変わっていくなかで、名家といわれたアンバーソン家が没落していくさまを、明暗を強調した照明、テンポの速い編集など、ウェルズ独特の手法を駆使して描いた大河ドラマ。父の死後、“小さな暴君”と呼ばれたアンバーソン家の息子ジョージは、旧家特有の身勝手な当主ぶりを発揮、やがて人々の信頼を失っていく……。編集にロバート・ワイズが参加しているのも興味深い。現在普及しているのはオリジナルの131分を縮めたカット版である。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOING HOLLYWOOD", "虹の都へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "名匠R・ウォルシュがハリウッドを舞台に描くミュージカル。女学校の教師シルヴィアは、ラジオから聴こえてきた歌手ビルの歌声に魅了され、彼に会いたい一心でハリウッドへ向かう。彼女は映画の仕事に向かうビルに同行するが、彼はスター女優リリに夢中。やがて、降板したリリの代役に起用されたシルヴィアは、女優として開花し、ビルとも急接近。しかし、ビルはシルヴィアの元に走り……。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUFF STORIA DI TRUFFE E DI IMBROGLIONI", "ブラッフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ベテラン、A・クインとカンツォーネ歌手、A・チェレンターノがコンビの詐欺師に扮し、1930年代のフランスを舞台にカジノの女ボスから金をまき上げる様子をコミカルに描く。", "1976-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("透明人間現わる", "透明人間現わる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "中里博士は人間を透明にする薬品を密かに完成させるが、秘密をかぎつけた謎の男が薬を奪い透明人間になって、希代の宝石“アムールの涙”を強奪しようとする。円谷英二が大映京都撮影所で、「虹男」に続いて特撮を担当した話題作である。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("What Happens in Vegas", "ベガスの恋に勝つルール", "", "", "http://image.eiga.k-img.com/images/movie/53566/315x210.jpg?4129", 99, "「メリーに首ったけ」のキャメロン・ディアスと、「ジャスト・マリッジ」のアシュトン・カッチャーが主演するラブコメディ。恋人にフラれた完璧主義者のジョイと、父親の経営する工場を解雇された道楽息子のジャックは、それぞれ憂さ晴らしにやってきたラスベガスで出会い、意気投合。飲み明かした勢いで結婚してしまう。翌朝、正気に戻った2人は結婚を解消しようとするが、その時、ジャックがジョイのコインで遊んだスロットマシーンで300万ドルが当たり……。", "2008-08-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不確かなメロディー", "不確かなメロディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ジャパニーズ・ロックのカリスマ、忌野清志郎が、バンド“ラフィータフィー”を引き連れて、およそ１ヵ月をかけて行った全国ライブハウス・ツアーを収録しつつ、彼の足跡を追う音楽ドキュメンタリー映画。ライブの合間に温泉に繰り出す彼らの飾らない姿が心地よい。ナレーションは、清志郎の高校時代からの盟友でもある三浦友和。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANGMAN'S KNOT", "ネバダ決死隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "R・スコット主演のウエスタン・アクション。騎馬戦をメインに襲撃と迎撃活劇が次々と繰り広げられる爽快な仕上がり。1865年の南北戦争終結後のネバダ州が舞台。北軍の金塊輸送部隊を襲い金塊を手中に収めた南軍の少佐率いる部隊だが、その時初めて戦争が終わっていることを知り、金塊を南部復興の資金にしようとする。そこへ自警団崩れのアウトロー集団が襲ってきた。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zwartboek", "ブラックブック", "", "", "http://image.eiga.k-img.com/images/movie/33896/315x210.jpg?1259", 144, "「氷の微笑」の鬼才ポール・バーホーベン監督が、23年ぶりに故郷のオランダでメガホンを取ったサスペンス・ドラマ。1944年、ナチス・ドイツ占領下のオランダ。ユダヤ人の女性歌手ラヘルは、オランダ南部への逃亡中に、何者かの裏切りによって家族をドイツ兵に殺されてしまう。復讐を胸に誓った彼女は、名前をエリスと変えてレジスタンスに身を投じる。彼女はスパイとしてドイツ将校ムンツェに近づき、彼の愛人になるが……。", "2007-03-24", "70,7,3,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BROTHER FROM ANOTHER PLANET", "ブラザー・フロム・アナザー・プラネット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「ピラニア」「ハウリング」の脚本家（ロジャー・コーマンの門下生）として知られたセイルズの監督第４作。日本初登場にして一躍インディーズ・ブームを巻き起こした話題作。異星から逃げて、ニューヨークのハーレムに迷い込んだ奴隷異星人“ブラザー”。ブラザーというのも、外見はまったくの地球の黒人そのままだからだ。ハーレムの人間たちとの愉快なかかわり。そこへブラザーを追って、一見白人風の賞金稼ぎ異星人の二人組が現れる……。SFのスタイルを借りて、人種問題をオシャレに描いたインディペンデントの快作。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奥菲斯小姐／WHITE COLLAR BELLES", "オフィスガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "発展著しい上海を舞台に、３人の女性の恋と仕事を描いた青春映画。学生時代から仲良しのアモン、ミミ、イェンイェン。上海でキャリアウーマンを目指していた３人だが、それぞれに仕事や恋の悩みが降り掛かる。アモンは広告代理店から独立したことで恋人に仕事の協力者との仲を疑われ、ホテル勤務のミミは海外赴任した夫に恋人がいたことを知り、腹いせに上司の誘惑に応じてしまう。そしてイェンイェンは、恋人を見限って華僑の愛人になってしまうのだった……。", "1994-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シコふんじゃった。", "シコふんじゃった。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「変態家族・兄貴の嫁さん」や「ファンシイダンス」で一種のカルト的な人気を持つ周防正行が実力を見せつけた傑作“相撲”映画。本木雅弘がマワシ姿の主人公を演じることでも話題になった。その本木扮する大学生、秋平は卒業するために必要な単位をもらうため、教授が監督する相撲部の試合に出場することになる。その相撲部は部員一人きりで潰れかけていたのだ。秋平をはじめ素人を集めて何とか出場した大会では当然のごとく惨敗。試合後、相撲部OBにさんざんにコケにされた秋平は、一度試合をすれば退部してよいという約束を忘れて、次は絶対に勝ってみせると豪語する。翌日から練習を始める相撲部だが、すぐに強くなるはずはない。小学生の腕白相撲の力士たちにさえ軽く投げ飛ばされる秋平たちを見て、教授がついに本腰を入れた……。スポ根ものの王道でも邦画初の相撲を取り上げた物語を、笑いを交えながら見せていく演出力は見事。柄本明、竹中直人らの脇役陣も光る。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGEL", "天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "軽妙洒脱な都会的コメディを得意とした巨匠E・ルビッチが、“妖花”M・ディートリヒを主演に据えて撮り上げた作品。英国外交官の妻マリアは、夫があまりかまってくれないので、一人パリへ旅立つ。そこである紳士と知り合い、同じ思いの二人は意気投合するのだが……。上流社会の既婚男女の機微を洒落たタッチで描いていく。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Make It Happen", "メイク・イット・ハプン！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "名門ダンススクールに入学するため、故郷のインディアナ州から大都会シカゴへ向かったローリンは、オーディションに落ちた挙げ句に車をレッカー移動され、路頭に迷っていた。そんな彼女を気の毒に思ったダナの計らいで、ローリンはあるナイトクラブで働き始める。そんなある日、クラブのトップダンサーが遅刻し、急遽ローリンがステージに立つことになる。「ステップ・アップ」「セイブ・ザ・ラストダンス」など多くのダンス映画を手がけるデュエイン・アドラーが脚本を担当。主演は元ストリップダンサーのメアリー＝エリザベス・ウィンステッド。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mirrors", "ミラーズ", "", "", "http://image.eiga.k-img.com/images/movie/53976/315x210.jpg?3147", 0, "人気TVシリーズ「24」のキーファー・サザーランドが主演するサスペンス・スリラー。監督は「ハイテンション」「P2」のフランスの新鋭アレクサンドル・アジャ。ニューヨーク6番街にある大火災で廃屋と化したデパートの夜警を依頼された元刑事のベンは、警備中にその建物の中にある不気味な鏡に触れてしまう。それをきっかけに原因不明な死亡事件が続発し、最愛の家族まで危険にさらしてしまうベンは、鏡をめぐる忌まわしい秘密を解き明かさねばならなくなる。", "2008-12-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Taxi Driver", "タクシードライバー", "", "", "http://ecx.images-amazon.com/images/I/51nU0bet9%2BL._SL210_.jpg", 114, "大都会ニューヨークを舞台に鬱屈した一人のタクシー運転手の狂気を描いた問題作。高く評価され、監督のM・スコセッシ、主演のR・デ・ニーロの名前を世界的に知らしめた。孤独なタクシー運転手トラヴィスは、大統領候補者の運動員ベッツィに一目惚れし、選挙運動の協力を申し出る。そしてデートすることになるが、彼は彼女をポルノ映画に誘い、怒らせてしまう。ベッツィに無視されるようになったトラヴィスは、ますます閉じこもりがちになる。ある日、彼の車に13歳の売春婦アイリスが逃げ込んでくるが、追って来たポン引きに連れ去られる。その後、アイリスと再会したトラヴィスは足を洗うように説得するが聞き入れられず……。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rookie of the Year", "がんばれ！ルーキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "運動オンチのヘンリーは、大ケガが治ったとたん、いきなり超豪腕投手に生まれ変わっていた。12歳にしてプロにスカウトされた彼は、低迷中のシカゴ・カブスを救うべく大活躍を始める……。「ホーム・アローン」の泥棒役で知られるD・スターンが出演を兼ねて初監督した心温まるスポーツ・コメディ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WE’RE BACK！ A DINOSAUR’S STORY", "恐竜大行進", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "1986年の「アメリカ物語」から始まったS・スピルバーグ製作総指揮の、劇場用アニメーション第４作。帆船型のタイム・マシンを操る科学者ニューアイズ船長は、ティラノザウルスなど４種・各１頭の恐竜に頭脳活性食を与え、人間なみの知能を持たせた。言葉もしゃべれるようになった彼らを、船長は子供たちの夢をかなえようと、太古の時代から現代のニューヨークに送りこむ。目的地のアメリカ博物館に向かう途中、家出少年のルイ、そして孤独な少女セシリアと友だちになった４頭。だが船長の弟スクリューアイズ博士が、４頭を彼の経営するサーカスの見せ物にしようと、悪の罠を仕かけ……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEVER A DULL MOMENT", "怪盗大旋風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "コメディものを得意とするJ・パリスの、記念すべき監督第１回作品。チンピラのボスが所有する美術館から名画を盗み出す計画を立てたチョイ役専門の俳優ジャックは、ボスが雇った殺し屋になりすまして計画を実行する。ところが、間違えてボスの妻の寝室に侵入してしまったりとドタバタの連続で……。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DE´CLIC", "レディ・ドール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "満たされない日々を送っている人妻クラウディアは、フェズ博士の発明した催淫マシーンで色情狂の女に変身。デパートの下着売場、パーティー会場、映画館のトイレなどで次々と男をむさぼる……。主演のF・ゲランが美しい裸身で官能的に迫る。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVY", "君に逢いたくて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「アルマゲドン」のL・タイラーが人気ブレイク前に主演したインディーズ・ムービー。彼女の役どころは、大学を中退してニューヨーク郊外の冴えないレストランのウエイトレスをしている少女キャリー。そんなヒロインの揺れる心情と並行して、彼女に片思いしている店の一人息子の青年ヴィクターの鬱屈した心理が濃密に描かれていく。デリケートな人物描写と、ソニック・ユースのS・ムーアが奏でるアコースティックなギター音楽が相まって、不思議な叙情性を醸し出す青春映画。これが長編処女作となるJ・マンゴールドは、サンダンス映画祭の審査員大賞と監督賞を獲得し、豪華キャスト作品「コップランド」の監督に大抜擢された。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("純ブライド", "純ブライド", "", "", "http://ecx.images-amazon.com/images/I/41zr4Uk6uiL._SL210_.jpg", 78, "人気漫画家・吉田聡の同名コミックを、「テニスの王子様」の城田優と「あさってDANCE」の川村亜紀主演で映画化した青春ラブストーリー。問題ばかり起こしている不良の“茶ボー”こと茶稿夏美は、失恋からなかなか立ち直れずすさんだ日々を送っていた。そんなある日、茶ボーの家に中学時代の同級生・純子が訪ねてくる。純子が家を失ったことを知り放っておけなくなった茶ボーは、純子と一緒に暮らしはじめるが……。", "2006-05-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ネムリユスリカ", "ネムリユスリカ", "", "", "http://image.eiga.k-img.com/images/movie/56926/315x210.jpg?3494", 96, "性犯罪により生まれた少女とその家族の絶望と再生を描くドラマ。母の琴野が15歳のときに男にレイプされ生まれた17歳の少女・夏芽は、母を犯した男を見つけ出すため、言葉が話せなくなった琴野と介護が必要な祖父とともに1台のバンで放浪生活を送っていた。探偵に払う費用をねん出するため琴野と夏芽は体を売ることも辞さなかったが、そんなある日、ついに目的の男が見つかり……。ひきこもりの青年の自立を描いた「青の塔」（2000）、加害少年の罪の行方を描いた「カタルシス」（03）の坂口香津美監督の第3作。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Heart", "アメリカン・ハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "刑務所を出たばかりの父親ジャックと、たった一人の肉親である彼を頼っておばの家を飛び出してきた息子ニック。夢を追ってばかりいる父と、愛に飢えた息子の愛情のやりとり、そして悲しい結末が、都会のやるせない風景の中で綴られていく。異色ドキュメンタリー「子供たちをよろしく」を手掛けたM・ベルらしい、シビアな人間ドラマ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUEEN OF HEARTS", "ラッキー・カフェ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ロンドンのイタリア街で“ラッキー・カフェ”を営むルカ一家。ある日、一家の母親のかつての婚約者が、借金のカタにコーヒーマシンを持っていってしまう。ギャンブル好きの父親は、母親をカタに起死回生の大バクチをするが……。J・アミエルが描くほのぼのタッチのコメディ。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("薔薇の葬列", "薔薇の葬列", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "映画理論、及び前衛映画の分野で精力的な活動を続ける松本俊夫監督の長編劇映画第１作。オイディプス神話をもとに、ゲイの少年と父の倒錯した性の悲劇を描く。主人公のインタビューを挿入するなどの実験的な手法が新しく、ピーターの妖しい美しさも印象的。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Inception", "インセプション", "", "", "http://image.eiga.k-img.com/images/movie/54466/315x210.jpg?4642", 148, "「ダークナイト」のクリストファー・ノーラン監督が、オリジナル脚本で描くSFアクション大作。人が眠っている間にその潜在意識に侵入し、他人のアイデアを盗みだすという犯罪分野のスペシャリストのコブは、その才能ゆえに最愛の者を失い、国際指名手配犯となってしまう。そんな彼に、人生を取り戻す唯一のチャンス「インセプション」という最高難度のミッションが与えられる。主人公コブにレオナルド・ディカプリオ、共演に渡辺謙、ジョセフ・ゴードン＝レビット、マリオン・コティヤール、エレン・ペイジほか。", "2010-07-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DREAMSCAPE", "ドリームスケープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "超能力を持った青年アレックスは、ある研究所から協力を依頼される。それは悪夢に悩まされている人の夢の中に入り、その根源を探って悪夢を断つというものだった。だが研究所の本当の目的が、大統領の暗殺であることを知ってしまう……。D・クエイド主演によるSFサスペンス。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Code of Silence", "野獣捜査線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "　シカゴ市警の部長刑事エディはコマチョ率いるマフィア組織の麻薬取引現場に踏み込もうとする。しかしそこに敵対組織が乱入し、麻薬と金が強奪されてしまう。コマチョ一味は強奪をはたらいたルナの手下や家族を殺害。唯一生き残ったルナの娘はエディに助けられるが、その後、誘拐されてしまう。救出に乗り出そうとするエディだったが、思わぬことから彼は署内で孤立無援状態に……。チャック・ノリスの空手技が炸裂する刑事アクション。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Flock", "消えた天使", "", "", "http://image.eiga.k-img.com/images/movie/1966/315x210.jpg?3802", 105, "18年間、性犯罪登録者の監察を続けてきた公共安全局のバベッジ（リチャード・ギア）は、退職間際、後任となるアリスン（クレア・デーンズ）の指導を行っていた。そんな時、1件の誘拐事件が発生し、バベッジは自身が監察している者の中に犯人がいると確信するが……。「インファナル・アフェア」がヒットした香港のアンドリュー・ラウ監督が、ハリウッドで製作したサイコ・サスペンス。歌手アブリル・ラビーンが事件の鍵を握る少女役で出演。", "2007-08-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君へのメロディー", "君へのメロディー", "", "", "http://image.eiga.k-img.com/images/movie/55686/315x210.jpg?3505", 0, "ミュージカル「テニスの王子様」で人気を集めた佐藤永典と、「赤い糸」の岡本玲が共演する青春ラブストーリー。「イヌゴエ」の横井健司監督がメガホンをとる。記憶喪失の少女・ユウと出会ったトキヤは、病院の窓から外ばかり眺めている彼女が気になり街へと連れ出す。ところが、ユウは車から流れてきたあるメロディを耳にすると、突然涙を流して動けなくなってしまう。数日後、ユウが別の施設に移ったことを知ったトキヤは、迷った末に訪ねることにする。", "2010-10-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COME´DIE D’E´TE´", "夏のアルバム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "第一次大戦直前の夏、故郷のブルジョワ社会に帰ってきた青年アドリアンと、ブルジョワの古い価値観を嫌う年上の女性ビッキーとの、美しくも短い愛の日々を描いていく。時代の変革期を強く美しく生きるヒロインをM・デートメルスが好演。原作はE・V・カイザリングの『南斜面』。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTENSIVSTATION DEUTSCHLAND／TERROR2000", "テロ2000年 集中治療室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "エログロ、アナーキーな作風で異彩を放つC・シュリンゲンズィーフのシュールなバイオレンス。実際に起きた少女誘拐殺害事件とネオナチの外国人排斥運動にヒントを得て、外国人を惨殺し続けるギャング一味の凶行と、難民キャンプの人々の反撃が呼び起こす大騒動を描く。アバンギャルドなタッチはもちろん、演技派として定評のあるU・キアーの狂気的な怪演にも驚かされる。", "1992-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN CHIEN ANDALOU", "アンダルシアの犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 17, "1920年代に最高潮に達したアバンギャルド映画の頂点を飾る傑作。1960年に、28年の完成当時上映に際してブニュエル自らが蓄音機を回していた音楽を選び、自らサウンド版を作成した。目玉を切り裂くカミソリと月を遮る一筋の雲のほか、手のひらから這い出してくる無数の蟻などが有名なシーンが多いが、ダリとの共同脚本は、非合理的な、衝撃的なイメージのみという規則に基づいて1週間足らずで書き上げられた。", "1928-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ocean's Eleven", "オーシャンズ11", "", "", "http://image.eiga.k-img.com/images/movie/1136/315x210.jpg?4530", 117, "フランク・シナトラ主演の「オーシャンと十一人の仲間」（60）をリメイク。ラスベガスの地下に眠る金庫から1億5000万ドルを盗み出すという、前代未聞の現金強奪計画のために、11人のプロフェッショナルが集結した！　監督を務めるのは「エリン・ブロコビッチ」「トラフィック」のスティーブン・ソダーバーグ。ジョージ・クルーニー、ブラッド・ピット、ジュリア・ロバーツ、アンディ・ガルシア、マット・デイモンといった、超豪華な出演陣が話題。", "2002-02-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Big Easy", "ビッグ・イージー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "デニス・クエイド、エレン・バーキン共演のサスペンス。ギャングの抗争による殺人事件が続くニューオリンズに赴任してきた刑事マクスウェインは、警察内の汚職を捜査する為にやって来た女性検事アンと出会う。捜査を続けるうち、マクスウェインが追う連続殺人と、アンが追う汚職事件の関連性が浮かび上がってくる……。監督は「ブレスレス」のジム・マクブライド、脚本は「ビバリーヒルズ・コップ」のダニエル・ペトリ・Jrが手掛ける。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LORD OF THE RINGS", "指輪物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "地球がまだ“中つ国”と呼ばれていたころ、ホビット族のフロドは、自分の持つ指輪が地上のすべてを支配できる危険な魔力を持つと知り、永遠に葬り去るため、仲間とともに火の山へと旅立つ。指輪を狙う闇の魔王と、その支配下にある様々な種族の陰謀が渦巻く中、彼は灰色の魔法使いガンドルフや、妖精エルフの助けを借りて数々の危機をくぐり抜けていく……。幅広い世代に読まれている、トールキンの壮大なファンタジーの前半をアニメ化。１コマずつ実写フィルムをトレースしていくという、ロトスコーピング技法を使い、戦闘場面などに生々しい効果を生み出している。構成、登場人物の性格描写など、複雑で繊細な原作の持ち味を見事に再現した長編アニメの秀作。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Back to the Future Part II", "バック・トゥ・ザ・フューチャーPART2", "", "", "http://ecx.images-amazon.com/images/I/510270Y1Y2L._SL210_.jpg", 107, "ドクの誘いで1985年から2015年の未来へタイム・スリップしたマーティ。そこでは自分の子供と対面することになる。なんと子供は双子。しかも男の子と女の子。自分とそっくりの子供を目の前にして、ビックリ仰天のマーティ。未来のハイスピードで動きまわるスケボーも、難なく乗りこなして、拍手喝采を受けるマーティだったが、再び事件に巻き込まれて、事実と反することをしてしまう。そして、1985年へ戻ったマーティとドクは、驚くべき時代を体験することになる……。大ヒットシリーズの第２作で、今回は過去に加え未来へもタイム・スリップしたマーティの冒険が描かれる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Combien Tu M'aimes?", "ダニエラという女", "", "", "http://image.eiga.k-img.com/images/movie/33786/315x210.jpg?2700", 95, "「マレーナ」「アレックス」のモニカ・ベルッチ主演のエロティック・ラブストーリー。宝くじで大金を当てた男が、多額の手当を払って娼婦と同居を始める。娼婦は妖艶で、心臓の弱い男には、刺激が強すぎると主治医が心配するほど。だが、ある日娼婦は男の元を去る。彼女には別の愛人がいたのだった……。監督は「美しすぎて」のベルトラン・ブリエ、モニカ・ベルッチの相手役にベルナール・カンパンとジェラール・ドパルデュー。R-18。", "2006-12-09", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白衣のアマゾネス", "白衣のアマゾネス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "良心的な医療活動を続ける美人女医、迦楼羅聖湖（かるらせいこ）と３人の看護婦には、法では裁けない巨悪を抹殺する裏の顔があった。医学界に巣食う巨大犯罪組織の陰謀を知った彼女たちは、敢然と戦いを挑むが……。同名劇画を、元C.C.ガールズの藤森夕子の単独主演で映画化したセクシー・アクション。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Whiteout", "ホワイトアウト", "", "", "http://image.eiga.k-img.com/images/movie/53806/315x210.jpg?2925", 101, "グレッグ・ルッカによるグラフィック・ノベルを、「アンダーワールド」シリーズのケイト・ベッキンセール主演で映画化したサスペンス・スリラー。過酷な冬の到来を目前にした南極を舞台に、南極初の猟奇殺人事件を捜査することになった女性FBI捜査官が、地上で最も危険な自然現象といわれるホワイトアウトの恐怖に直面しながらも犯人を追い詰めていく姿を描く。メガホンを取るのは「ソードフィッシュ」のドミニク・セナ。", "2009-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The President's Last Bang", "ユゴ　大統領有故", "", "", "http://image.eiga.k-img.com/images/movie/53136/315x210.jpg?1821", 104, "1979年10月26日、韓国で18年間に渡る軍事独裁政権を敷いてきたパク・チョンヒ大統領が、腹心の1人であった中央情報部長キム・ジェギュにより殺害された。韓国を揺るがせたこの歴史的事件を、韓国映画界の異端児イム・サンス監督が映画化した社会派ドラマ。当時の捜査記録や証言記録といった膨大な量の資料を基に、事件の前後24時間を描き出す。主演は「シュリ」「二重スパイ」のハン・ソッキュ。", "2007-12-15", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EIGHT MILLIMETER", "8mm", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "本物の殺人現場を捉えた“スナッフ・フィルム”の謎を追う、私立探偵の奔走を描いたサスペンス・スリラー。議員の未亡人が夫の遺品からスナッフ・フィルムらしき８ミリを発見。映画の中で死を遂げる少女は本当に殺されたのか？　探偵のウェルズは、フィルムとその消息をたどるうちに、アンダーグラウンドのポルノ業界の存在にたどりつく。ところが、そこには危険な罠が彼を待ち受けていた……。「セブン」の脚本家A・K・ウォーカーによるシナリオを、サイコな空気とともに映像化。アブノーマルな性の世界を知り、神経をすり減らす主人公ウェルズを、N・ケイジが好演している。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("城市猟人／CITY HUNTER", "シティーハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "北条司の人気コミックを、ジャッキー・チェンと後藤久美子の顔合わせで映画化したアクション・コメディ。香港で失踪した令嬢、清子を連れ戻すよう依頼されたリョウ。彼女が豪華客船に乗っているという情報を得て、リョウもまた船内に潜入するが、その船にはテロリストの集団も乗り込んでいた……。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("於宇同／ER WOO DONG", "哀恋妃〈あいれんひ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "韓国では、魔性の女の代名詞で知られる実在の女性“於宇同（オウドン）”の生涯を描いた映画。王族に嫁いだが、召使いと姦通したため、離縁された於宇同。一度は死を決意したものの、妓生(遊女)に助けられ、妓生として人生を再出発、生まれながらの美貌を武器に、ついには王様までも虜にしてしまう。だが、政治的に利用された於宇同は、狂人の罪を着せられ、刺客に追われる身となる。", "1985-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CANDYMAN: FAREWELL TO THE FLESH", "キャンディマン2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "モダン・ホラーの鬼才C・バーカーが生んだ異色のキャラクター、“キャンディマン”が暗躍する第２作。小学校教師であるアニーは、数年前に原因不明の殺人事件に巻き込まれ惨殺された父のことが頭から離れないでいる。この事件は、古くから言い伝わる伝説の復讐魔“キャンディマン”の仕業であると噂され、未だ解決されないまま。しかし、アニーが担任を務めるクラスの生徒が描いたある男の絵をきっかけに、恐ろしくも意外な事実が明らかになり……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ninja Cheerleaders", "ニンジャ★チアリーダー", "", "", "http://image.eiga.k-img.com/images/movie/53686/315x210.jpg?1409", 81, "コートニー、エプリル、モニカの3人は、天使のような顔に、プレイメイツも顔負けの完璧ボディを持ったスーパー女子高生。学校ではチアリーディング部で活躍し、学園のアイドル的な存在だが、放課後は柔術の達人Mr．Hiroshiの下で修行を重ねた“ニンジャ★チアリーダー”として、数々の難事件を解決しているのだった。そんなある時、Mr．Hiroshiが何者かに誘拐され……。", "2008-05-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCK’N ROLL HIGH SCHOOL FOREVER", "ハイスクール・ドリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "C・フェルドマンがロック大好き少年に扮して大暴れする学園コメディ。ロックンローラーにあこがれるジェシーと彼のバンドは、校内コンサートで騒ぎを起こし演奏を禁じられてしまう。ジェシーたちは、ナチのように残酷な女副校長と優等生グループと対立しながらロックを復活させようと奮闘する。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAYERLING", "うたかたの戀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "３度映画化された有名な悲恋物語のオリジナル版。19世紀末のオーストリア。皇太子のルドルフは宮廷生活に疑問を持ち、社会主義運動に身を投じる。彼はある日、遊園地で美しい男爵令嬢マリーと出会い、恋に落ちるが、二人の関係は許されないものだった。皇室の断固たる反対にあった彼は、ある決心を抱く……。", "1935-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("起きて転んでまた起きて", "起きて転んでまた起きて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "正明と修は大学の同級生。正明の家業はカツラ店で左前、修の家はクラッカー製造業で景気がいい。ところが、輸出用カツラが大ヒットして正明の家はガッポリ稼ぐ。一方クラッカーの注文がぴたりと止まって修の父親はショック死した。浮き沈みの激しい二人の珍奇な生き方と恋人争奪戦を描くコメディ。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宣戦布告", "宣戦布告", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "麻生幾の同名小説を映画化したポリティカル・サスペンス。200X年、北陸の敦賀半島海岸に国籍不明の潜水艦が座礁。その乗組員たちが武装して国内に上陸していることが判明。やがて日本は未曾有の危機にさらされることになる……。明日にも現実に起こりかねない現代日本の危機的状況が、政府を主軸として実にリアルに描かれた問題作。首相役の古谷一行をはじめ、ベテラン演技陣の白熱した演技合戦も見ものである。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TALLY BROWN，NEW YORK", "タリー・ブラウン、ニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ベルリンの同性愛監督プラウンハイムが、ニューヨークの異色女性芸人タリー・ブラウンの人生とショーをドキュメント。ボストンのストリップ酒場の歌手を皮切りに、ブロードウェイ、ハリウッド、そしてアンダーグラウンドの女神へ。猥雑なディヴァインの舞台も収録。", "1978-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("54", "54〈フィフティ★フォー〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1970～80年代にかけて、N.Y.のナストライフの中心地であった伝説のディスコ、スタジオ“54”。選ばれた者のみが入場できる、このディスコを舞台に、郊外の一青年から“54”の売れっ子バーテンダーとなっていく若者の栄光と挫折を、スタイリッシュに描いた青春映画。華やかな時代の熱気とともに、その裏側に潜む退廃的で虚無的な面を浮かび上がらせる。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガールフレンド", "ガールフレンド", "", "", "http://ecx.images-amazon.com/images/I/31D8MY0EACL._SL210_.jpg", 105, "女性の性を描いた「ヴァイブレータ」の廣木監督が、さらに女性映画を深く追求しようと企画した“ラブコレクション”プロジェクトの一本。女性同士の友情に焦点を当てたヒューマン・ドラマの佳作。彼氏にフラれて以来、酒に酔って誰とでも寝るようになったヒロイン。ある日、そんな男の一人からヌード・モデル撮影の依頼がくる。彼女はモデルを探し一人の女を発掘する。", "2004-11-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐を呼ぶ男", "嵐を呼ぶ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "1957年、石原裕次郎主演で大ヒット、その後1966年には渡哲也主演で再映画化され、これが３度目の映画化。ライバル同士の人気ドラマーに、近藤真彦と田原俊彦がそれぞれ扮し、迫力あるドラム合戦を展開、物語も製作当時の視点でアレンジされている。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空を飛んだオッチ", "空を飛んだオッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "直木賞作家、海老沢泰久の同名小説を映画化。空を飛ぶ力を持った少年が、その能力の特殊さゆえに思わぬ騒動に巻き込まれていく。約20年前の原作になるが、いじめの根本を見据えた物語は、むしろ現在の方が痛切に胸に迫りくる。大人にも子供にも大切なメッセージを届けるファンタジーだ。空を飛べるようになった小学生のオッチは、祖母の忠告でその能力を隠し続ける。ところが同級生にバレ、学校や村の大問題になり……。", "2005-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("City of God", "シティ・オブ・ゴッド", "", "", "http://image.eiga.k-img.com/images/movie/1336/315x210.jpg?1643", 130, "原作は、初めてスラムの住民の視点からスラムの現実を描いた小説としてブラジルでベストセラーになった、パウロ・リンスの同名小説。これが第3作となる監督フェルナンド・メイレレスは、55年、ブラジル生まれ。主要登場人物60人、脇役150人、エキストラ2000人の群像劇で、60年代後半から80年代までのある街の変貌を描く。実際の街でロケ、主要人物のほとんどは、舞台となった街の近隣に住むアマチュア俳優たちが演じている。", "2003-06-28", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arirang", "アリラン", "", "", "http://image.eiga.k-img.com/images/movie/57506/315x210.jpg?3226", 100, "ほぼ1年に1本のペースで新作を撮り続けていた韓国の鬼才キム・ギドクが、「悲夢」（2008）以来、映画界から遠ざかり、山中の一軒家で隠遁生活を送っていた自らの姿を捉えたセルフドキュメンタリー。カンヌ、ベルリン、ベネチアの世界3大映画祭でそれぞれ受賞を成し遂げたギドク監督が、国内での低い評価とのギャップや、仲間から受けた裏切りについてなど、内に秘めていた心情を吐露。「自らに疑問を投げかける自分」「それに答える自分」「それらを客観的に分析する自分」と3人の自分を演じながら心の内をさらけ出していく。", "2012-03-03", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人が人を愛することのどうしようもなさ", "人が人を愛することのどうしようもなさ", "", "", "http://image.eiga.k-img.com/images/movie/34176/315x210.jpg?1527", 115, "「天使のはらわた」「花と蛇」の石井隆監督が、これまで描き続けてきたヒロイン“名美”役に喜多嶋舞を迎えて放つ愛憎劇。女優として活躍する名美は、同じく俳優の夫・洋介の不倫に悩まされていた。そんな中、彼女は新作映画「レフトアローン」で洋介と共演することに。さらに洋介の不倫相手である若手女優までキャスティングされ、マスコミの注目を集めるが……。永島敏行、津田寛治ら実力派俳優が脇を固める。", "2007-09-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JULIETTE OU LA CLEF DES SONGES", "愛人ジュリエット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "牢獄の中でミシェルは一睡もせずに、一人の乙女のことを思い詰めていた。彼がさまよい込む夢の世界は、誰も彼も記憶を持たない忘却の国であった。カルネ監督がG・フィリップ主演で作った幻想的な映画。", "1951-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私は猫ストーカー", "私は猫ストーカー", "", "", "http://image.eiga.k-img.com/images/movie/54606/315x210.jpg?4061", 103, "イラストレーターの浅生ハルミンの同名エッセイを、俳優・脚本家としても活動する鈴木卓爾が映画化。古本屋でアルバイトをするハルの趣味は、道端や路地裏で見つけた野良猫を時間が許す限り追いかけること。そんなある日、古本屋の店長が昔恋人に送った本が偶然出てきて……。主演は「さよならみどりちゃん」の星野真理。", "2009-07-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・男の腕だめし", "喜劇・男の腕だめし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "戦後のミナト横浜、とあるストリップ小屋。ついつい特出しが過ぎるこの店を取り締まるべく、若い警官・湯原昌幸がやってくる。ところがこの警官、実は店のオヤジの実の息子だということがわかって……。松竹プログラム・ピクチャーの大笑いできる快作。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOTHER TERESA", "マザー・テレサ 母なることの由来", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "貧しい人々や障害者に救いの手を差し伸べ続けた修道女マザー・テレサ。彼女の生い立ちと、献身的な活動を５年間にわたって記録した感動のドキュメンタリー。カルカッタの“死を待つ人の家”やN.Y.でのボランティア、戦火をくぐってのベイルートでの救援作業など、その活動をリアルに綴る。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEENAGE HOOKER BECAME KILLING MACHINE IN DAEHAKROH", "テハンノで売春していてバラバラ殺人にあった女子高生、まだテハンノにいる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "韓国で若者に熱狂的な支持を得たカルト・ムービー。ホラー、エロス、SFなど、何でもあり奇想天外なストーリーが展開する。新鋭ナム・ギウン監督の凝った映像手法と演出も見逃せない。小遣い稼ぎで売春に走る女子高生がいつも通りテハンノで客をつかまえていたころ、担任教師と鉢合わせ。彼女は教師に売春を強要されたあげく、彼に雇われた３兄弟に殺されてしまう。だが、彼女は謎の男に新たな生命を与えられてよみがえる。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Prisoner of Paradise", "ナチス、偽りの楽園　ハリウッドに行かなかった天才", "", "", "http://image.eiga.k-img.com/images/movie/56086/315x210.jpg?3720", 96, "マレーネ・ディートリッヒの「嘆きの天使」にも出演した、ドイツ系ユダヤ人のコメディ俳優で監督でもあるクルト・ゲロンに光をあてたドキュメンタリー。第75回米アカデミー長編ドキュメンタリー賞にノミネートされた。1920年代から30年代にかけ、ベルリンで活躍したゲロンは、ナチスの台頭とともにユダヤ人迫害を逃れてフランス、オランダを渡り歩く。やがてオランダで捕えられたゲロンは、“楽園”と称される強制収容所に入れられ、ナチスの宣伝映画の監督を命じられる。", "2011-04-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しかしそれだけではない。　加藤周一　幽霊と語る", "しかしそれだけではない。　加藤周一　幽霊と語る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "文学をはじめとする芸術全般、文明、社会、政治など、幅広い分野で作家・評論家として活動し、2008年12月にこの世を去った加藤周一。彼は晩年、源実朝や神田盾夫、戦死した友人など、この世を去った人々と対話することを試みていた。生前最後のインタビューも収録した貴重なドキュメンタリー。", "2010-02-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("書かれた顔", "書かれた顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "歌舞伎の女形・坂東玉三郎を、映像の魔術師D・シュミットがドキュメンタリーとフィクションの融合という得意のスタイルで描いた異色の作品。玉三郎が演じる３つの演目『大蛇』『積恋雪関扉』『鷺娘』のほか、名女優、杉村春子が女形や玉三郎について語る場面や、現代舞踏の第一人者・大野一雄との対比、地唄舞の武村はんや芸者の蔦清小松朝じらの映像も収められている。ほかに、玉三郎演じる年増の芸者を二人の若者が奪い合うというフィクションのパートなど、貴重な事実の記録であると同時にファンタジックな現代の童話ともいえる、夢と現実が混然となった幻想的な作品に仕上がっている。日本とスイスの合作。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Plague", "プレイグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "カミュの長編小説『ペスト』の映画化。南米の街オランで、ペスト発生をいち早く発見した医師のリウーは、事実を隠すように要請する行政部に反対し、街を封鎖すべきだと主張する。一方、別の取材で街を訪れていたフランスのTV局のカメラマンとキャスターの二人もその事実に気付のだが……。W・ハート、R・デュヴァルら演技派俳優の共演で、人間とウイルスとの戦いを描いている。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ICE PIRATES", "スペース・パイレーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "地球からはるかに隔たった星雲。そこでは慢性的に不足する水を掌握するテンブラー帝国が極悪の限りをつくしていた。命知らずの勇者がそろった宇宙海賊軍団と帝国の戦いを、SFX技術を駆使して描写。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺人犯", "殺人犯", "", "", "http://image.eiga.k-img.com/images/movie/55296/315x210.jpg?3784", 121, "「風雲・ストームライダーズ」「父子」などで知られるアーロン・クォック主演のサスペンス・スリラー。「グリーン・デスティニー」「HERO」のビル・コンが製作を担当。ある連続猟奇殺人事件の捜査を担当していた香港警察のクォン警部は、通報を受け駆けつけたビルで何者かに襲われ、気絶してしまう。目覚めたときには記憶を失っており、一緒にいた同僚は瀕死の重傷を負っていた。そして、犯人は自分だと示す状況証拠が次々と見つかり、混乱するクォンは狂気に取りつかれていく……。", "2010-06-05", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トキメキ☆成均館スキャンダル　劇場編集版", "トキメキ☆成均館スキャンダル　劇場編集版", "", "", "http://image.eiga.k-img.com/images/movie/56276/315x210.jpg?4217", 128, "韓国の人気アイドルグループ「東方神起」「JYJ」で知られるユチョンが主演のドラマ「トキメキ☆成均館スキャンダル」（2010）を劇場用に再編集して公開。18世紀の朝鮮王朝時代、やむにやまれぬ事情から男装して名門校「成均館（ソンギュンガン）」に入学し、男子学生の中で寄宿舎生活を送ることになったユニは、ソンジュンやコロ、ヨリムと出会い友情を深めていく。やがてソンジュンがユニにひかれはじめ……。", "2011-05-06", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビリィ★ザ★キッドの新しい夜明け", "ビリィ★ザ★キッドの新しい夜明け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ギャングたちの襲撃から１軒の店を守るために、６人の用心棒が店のマスターとともに戦う。高橋源一郎の原案をもとに、自主映画出身の山川直人が脚本を共同執筆し、監督した。三上博史が、用心棒、ビリィ・ザ・キッドとなって好演。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLEAMING THE CUBE", "ローリングキッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "スケートボードを駆使して、殺された兄の復讐に立ち上がる少年の活躍を描く青春アクション。スケボー大好き少年ブライアンの義兄ヴィンがモーテルで首吊り死体として発見される。その死に不審を感じたブライアンは、彼が武器密輸組織を発見したために殺されたことに感づく。組織の魔の手は徐々にブライアンに忍び寄るが……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kalifornia", "カリフォルニア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「ソードフィッシュ」のD・セナがメガホンを執ったロード・ムービー。連続殺人を研究する作家が恋人を伴い、“希望の地”カリフォルニアを目指して有名な殺人現場を巡礼しようと計画する。二人は旅費を安く抑えるためにドライブの同乗者を募るが……。インテリ肌の二人の車に乗り込んでくる若いカップルが野生児のように無軌道で、取材旅行は死を賭した荒野の体験に様変わりする。その旅の果てまでを、乾いたシャープな演出で描く。動物的な男を盲目的に愛する娘の、無垢な愚かさをJ・ルイスが好演。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血斗水滸伝 怒濤の対決", "血斗水滸伝 怒濤の対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "大利根の流れにそって激しく対立する飯岡助五郎一家と笹川繁蔵一家。国定忠治が間に入り、仲直りを強引に承知させて手打ち式を行うが、その後助五郎は繁蔵一家への嫌がらせをますます繰り返し、繁蔵が忠治のもとに身を寄せるべく去ると、繁蔵一家に非道の限りをつくす。オールスター出演の娯楽時代劇。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Harry Potter and the Sorcerer's Stone", "ハリー・ポッターと賢者の石", "", "", "http://image.eiga.k-img.com/images/movie/1076/315x210.jpg?4801", 152, "J・K・ローリングの同名ベストセラー・シリーズの映画化第１弾。闇の魔法使いのために、偉大な魔法使いだった両親を失い、魔法嫌いの叔母夫婦の家庭で育った11歳のハリー。彼は魔法魔術学校に入学し、自分の能力に目覚めていくが、闇の魔法使いが彼を狙っていた……。", "2001-12-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARTHA GRAHAM ： THE DANCER REVEALED", "マーサ・グラハムの生涯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "20世紀を代表するとまでいわれる世界的舞踏家マーサ・グラハムの生涯に迫ったドキュメンタリー。踊る姿やリハーサル、そしてインタビューを通して、彼女の芸術観、人間関係などのプライベートな側面が紹介されていく。また、1930～50年代の貴重なダンス・シーンの映像も多く観ることができる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若ノ花物語 土俵の鬼", "若ノ花物語 土俵の鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "初代・若ノ花が主演した伝記映画。北国の貧しい家に生まれた若ノ花は、家計を助けるために力士になり、メキメキと頭角を現す。やがて妻を得るが、愛児が死にショックでスランプに陥る。やがて、周囲の励ましで見事に立ち直るという立身出世ストーリー。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODA SECRETA", "シークレット・ウェディング 待ちすぎた恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ブエノスアイレスの路上を真っ裸の男が走り抜ける。やがて警察に捕まった男は尋問を受けるのだが、自分が誰なのかさえわからない状態だった。釈放された男は次第に記憶を取り戻し、13年前に将来を誓い合った女トタが住む町へと出かける。ようやくトタを捜し当てた男だったが、待ち過ぎたトタは男を識別できなかった。男は真実を明かさず、偽名を使って彼女のそばで暮らし始めるが……。離れ離れになった恋人たちの悲劇と愛の再生をせつなく描きながら、反社会分子を次々と投獄し粛正していったアルゼンチン軍事政権の歪みが浮き彫りにされる。「タンゴ−ガルデルの亡命」や「オフィシャル・ストーリー」などとともに記憶される、アルゼンチンの過酷な過去をめぐる一編といえよう。", "1989-01-01", "22,70,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRAY LADY DOWN", "原子力潜水艦浮上せず", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "アメリカ海軍の原子力潜水艦が事故により沈没、浮上不能に陥る。艦内に残された酸素は36時間分。彼らを救うため海軍は総力を上げて救助を開始する……。現実の世界情勢でもしばしば発生する原潜事故を題材に、息詰まるサスペンスで見せるスペクタクル映画。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハラがコレなんで", "ハラがコレなんで", "", "", "http://image.eiga.k-img.com/images/movie/56326/315x210.jpg?1640", 109, "「川の底からこんにちは」「あぜ道のダンディ」の俊英・石井裕也監督が、粋に生きることをモットーとする妊婦の光子と、彼女を取り巻く人々の悲喜こもごもを描く人情劇。妊娠9カ月で恋人と別れ、所持金もつきてしまった光子は、幼少期を過ごした時代遅れの長屋に戻ってくる。いまは閑散とした長屋に暮らしているのは、幼なじみの陽一とその叔父・次郎だけ。義理人情を重んじる光子は、2人の営む食堂が経営難と知るやすぐさま助けの手をさしのべ、食堂は活気を取り戻していくが……。主演は「時をかける少女」の仲里依紗。共演に中村蒼、石橋凌。", "2011-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le moine", "マンク　破戒僧", "", "", "http://image.eiga.k-img.com/images/movie/57796/315x210.jpg?2199", 101, "1796年イギリスで発表され物議をかもした、悪行の限りを尽くす破戒僧の姿を描いたマシュー・G・ルイスの小説「マンク」を、バンサン・カッセル主演で映画化。17世紀スペイン、マドリッド。修道院の前に捨てられ孤児として育てられたアンブロシオは、立派な僧侶となるが、自身の出生の謎に苦悩する日々を送っていた。そしてある時、修道士を偽って近づいてきたミステリアスな美女の誘惑にかかり、戒律を破ってしまう。女の意のままになったアンブロシオは、黒魔術や強姦、窃盗、殺人などあらゆる悪徳に手を染めていくが……。監督は「ハリー、見知らぬ友人」のドミニク・モル。", "2012-03-24", "2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("父戀し", "父戀し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "離ればなれの父母のかすがいとなる、子役の美空ひばりのけな気さが光るメロドラマ。ギターの流し・雄二は、かつて別れた早苗との思い出が残る温泉町にやって来る。早苗は、娘の恵美子と父を養うため、東京へ働きに出て、今は町にいない。だが、雄二は恵美子の歌を偶然に聞き、それが自分が早苗に作ってやった曲だと知る。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("炭鉱〈ヤマ〉に生きる", "炭鉱〈ヤマ〉に生きる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "日本の近代化を支え続けながら、石油の登場と共に消え入った炭鉱について振り返るドキュメント。炭鉱画を描き続けた一人の坑夫の足跡をたどりながら、かつての生活を見つめる。明治から昭和にかけ、筑豊で炭鉱夫として生きた山本作兵衛。炭鉱夫として働く一方で、絵筆をとり炭鉱絵画を数百枚も残している。そんな彼の絵を通し、炭鉱に生きた人々を見つめる。", "2006-11-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Goodfellas", "グッドフェローズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "M・スコセッシが、実在の人物をモデルに描いた、30年にわたるマフィア・ストーリー。とはいえ、組織の抗争劇ではなく、一人の青年が“ファミリー”の中でどう生きていくかに焦点をあてている。手持ちカメラを多用した前半のバルハウスによる移動撮影、後半の主人公ヘンリーが捕まる長い一日を描いた「アフター・アワーズ」ばりの細かいキレのあるカット割りなど、演出的に見せる部分が多い。また、さながらロック年代史ともいえる1950～70年代のロック・ナンバーも魅力的だ。ヘンリーは、幼い頃から“グッドフェローズ”と呼ばれるマフィアの世界に憧れ、12歳で地元のボス、ポーリーの一家に身を投じる。兄貴分のジミーや仲間のトミーとヤバイ仕事も経験をしながら、カレンと結婚、子供ももうけた。だが、1978年、ジミーの指揮による600万ドルの空港からの強奪事件に参加。FBIに目をつけられ、麻薬の容疑でヘンリーは捕まってしまい……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私を抱いてそしてキスして", "私を抱いてそしてキスして", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "平凡なOL・圭子はある日、自分がエイズであると知る。一時は気落ちした圭子だが、マスコミを通じてエイズに対する偏見と戦おうと決意。しかし彼女はまだ、お腹の中に子供がいるのを知らなかった……。日本で初めてエイズを正面から取り上げた作品として話題になった一作。好感の持てる社会派メロドラマに仕上がった。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIGHTY APHRODITE", "誘惑のアフロディーテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ギリシャ悲劇『オイディプス王』をモチーフとしたラブ・コメディ。養子縁組をきっかけに出会った男女の愛をウィットの効いたエピソードで描く。斡旋業者から生まれたばかりの赤ん坊を引き取ったワインリブ夫妻。幼稚園では天才クラスを薦められ、ルックス、性格も抜群のマックスに大満足の二人だったが、その頃、夫婦間には亀裂が生じ始めていた。夫レニーはマックスを生んだ女性は素晴らしい人に違いないという思い込みから、母親探しを開始。だが意外にも、マックスの母親はポルノビデオに出演経験のあるコールガールのリンダだった……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LIGHT ATATHE EDGE OF THE WORLD", "カーク・ダグラスと　ユル・ブリンナーの　　世界の果ての大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "『海底二万哩』『地底人間の謎』など、冒険科学小説の第一人者J・ヴェルヌの原作からなる、海洋アクション。南米の最南端ホーン岬の灯台守が、残忍な海賊に単身立ち向かう。豪華二大スター、K・ダグラスとY・ブリンナーの堂々たる対決が見ものの一編。", "1971-01-01", "1,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sell Out!", "セルアウト！", "", "", "http://image.eiga.k-img.com/images/movie/55036/315x210.jpg?4697", 110, "2006年の短編映画「Adults Only」がベネチア国際映画祭で注目を集めたマレーシアのヨ・ジュンハン監督の長編デビュー作となるブラック・コメディ。ライバルに負けてばかりの女性TVレポーターと、クビを宣告された家電開発者。仕事に限界を感じる2人が出会ったことから巻き起こる騒動を、ミュージカル・シーンを織り交ぜながら描く。ベネチア国際映画祭でオルタナティブ・ビジョン賞を受賞。第10回東京フィルメックス上映作品。", "2009-11-22", "50", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZAPPA", "子供たちの城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「ペレ」で1988年カンヌ映画祭グランプリに輝いたB・アウグスト監督が、思春期の少年たちの成長していく姿を鋭い視点で捉えた作品。1961年コペンハーゲン郊外の町に住む３人の少年たちは、ギャングまがいのいたずらにふけっていた。そんな彼らにも、ひと夏の経験を機に心境の変化が訪れる……。", "1983-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mozart and The Whale", "モーツァルトとクジラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「レインマン」でアカデミー脚本賞に輝いたロナルド・バスが紡ぎだす、アスペルガー症候群の男女のラブ・ストーリー。発育障害の一種で、一般的に“知的障害のない自閉症”と言われるアスペルガー症候群。予測不可能な相手の行動に振り回され、時には傷つけあいながらも、共に困難を乗り越えることで成長していく2人を「ラッキーナンバー7」のジョシュ・ハートネットと「サイレントヒル」のラダ・ミッチェルが熱演。", "2007-02-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT OF THE RUNNING MAN", "エスケープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ジェリーは人生に希望のかけらも持ち合わせていないタクシー・ドライバー。ある夜、彼はギャングから追われる男を乗せるが、その男は100万ドルの強奪金が入ったブリーフケースを車に残したまま殺されてしまう。ジェリーが金を持ち逃げしたのを知ったギャングたちは、冷酷な殺し屋エックハートを雇う。ジェリーは、逃亡を続けるうちに、ここ何年かで初めて生きたいという感情を抱くのだった。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PICTURE BRIDE", "ピクチャーブライド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "日系３世のK・M・ハッタ監督の家族のルーツをもとに、アジア系アメリカ人の女性スタッフが描く愛の物語。20世紀初頭、見合い写真だけで結婚を決め、ハワイに渡った日本人花嫁（ピクチャーブライド）の愛と苦難の日々を綴る。1918年、両親を亡くした16歳のリヨは、ハワイのサトウキビ畑で働くマツジと見合い写真のやりとりだけで結婚を決め、ハワイへと渡った。だが、彼女を待っていたマツジは父親ほども年齢が違っていた。その日から夫と過酷な労働が始まる。日本に帰りたい一心で畑仕事をこなすリヨだが、同じ畑で働くカナと親しくなり次第に頑なな心もいやされていく。そんなある日、農園の現場監督の不注意で発生した火事にカナが巻き込まれてしまう。困難にも前向きに立ち向かう主人公を工藤夕貴が熱演。1995年サンダンス・フィルム・フェスティバルでは、ドラマ部門観客賞を受賞した。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("御誂次郎吉格子", "御誂次郎吉格子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "伊藤大輔、大河内伝次郎、唐沢弘光カメラマンのトリオは、サイレント末期に時代劇の傑作を次々と生み出していたが、これもその中の一編。江戸を逃れて上方へ向かう次郎吉は、船の中で出会った女・お仙といい仲になる。次郎吉は貧しく美しい娘・お喜乃を、お仙の兄・仁吉の毒牙から救い出し、決着をつけるべく御用の網の中を仁吉の家に乗り込むが……。映像と字幕のリズミカルな構成に、伊藤大輔の映画話術の一端をうかがうことができる。「血煙高田馬場」「新版大岡政談」「素浪人忠弥」「興亡新撰組」といった傑作のほとんどが失われた今、伊藤＝大河内＝唐沢トリオの仕事を知る上で貴重な作品。", "1931-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wall-E", "ウォーリー", "", "", "http://image.eiga.k-img.com/images/movie/53346/315x210.jpg?1844", 103, "ゴミだらけになった地球を人類が捨て去り、700年が過ぎた29世紀。地球にひとり置き去りにされたゴミ処理ロボットのウォーリーは、ただひたすらゴミ集めを続ける長い年月のうちに、感情が芽生えていた。そんな地球にある日、ピカピカのロボット、イヴがやってきてウォーリーは彼女に一目惚れ。しかし、イヴはある重要な任務を背負っていて……。ロボットを主人公にしたピクサーアニメーション。監督は「ファインディング・ニモ」のアンドリュー・スタントン。", "2008-12-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高校生と女教師 非情の青春", "高校生と女教師 非情の青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "恩地日出夫監督が高校を舞台に教育のあり方を問う社会派ドラマ。大学を出たての新米教師・山岸佐智子は副担任として生徒たちの生活指導を行う。バイクを盗む裕福な家庭の秋山、性に目覚めて生活が乱れていく由子、彼らを励ます佐智子だったが、ある日彼女はチンピラたちに暴行されてしまう……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Defiance", "ディファイアンス", "", "", "http://image.eiga.k-img.com/images/movie/53846/315x210.jpg?2973", 136, "「ブラッド・ダイヤモンド」のエドワード・ズウィック監督が、6代目007のダニエル・クレイグを主演に迎え、第2次世界大戦中に実在した1200人ものユダヤ人を救ったビエルスキ兄弟の物語を映画化。ナチス・ドイツに両親を殺されたユダヤ人のビエルスキ3兄弟は、復讐を胸にベラルーシの森へと逃げ込む。やがて森にはナチスの迫害から逃れてきたユダヤ人が続々と集まり始め……。共演にリーブ・シュライバー、ジェイミー・ベル。", "2009-02-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("２H", "２H", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "東京に一人で暮らすおよそ100歳になる中国の老人の孤独と死を２時間（2H）に集約したドキュメント。1998年冬、かつて孫文の参謀や中国軍の将軍として激動の中国を生きた老人、馬晋三。間もなく95歳になる彼は東京で人生の終わりを迎えようとしていた。彼の周囲の人々は、彼が孤独な死を迎えることを案じている。彼の元に東京を拠点に活動する中国女性芸術家、熊がやってくるのだが……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サマーウォーズ", "サマーウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/54346/315x210.jpg?4656", 114, "「時をかける少女」の細田守監督が、同作に続いて脚本・奥寺佐渡子、キャラクターデザイン・貞本義行とともに描くオリジナル長編アニメーション。数学が得意だが気弱な高校2年生の健二は、憧れの先輩・夏希に頼まれ、夏休みの間、彼女の実家で夏希のフィアンセとして過ごすことに。そんな時、健二はネット上の仮想空間OZで起きた事件に巻き込まれ、その影響が現実世界にも波及。夏希の一家ともども、世界の危機に立ち向かう。", "2009-08-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瀬戸内ムーンライト・セレナーデ", "瀬戸内ムーンライト・セレナーデ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「瀬戸内少年野球団」でも阿久悠の小説を取り上げた篠田正浩が、彼の『飢餓旅行』を映画化した力作。大震災直後の現在の神戸から、終戦直後の淡路島を回想するという卓越した視点で、ある家族の旅行を綴っていく。昭和21年、淡路島で駐在をしている恩田一家は、戦死した長男の遺骨を故郷の九州・宮崎に納めるため、家族全員で旅行をすることになる。神戸から瀬戸内航路の船に乗り込む両親と３人の子供たち。船の中で、気っ風のいい闇屋が中心となって宴会が始まり、父親の幸三も闇屋と盃を交わしている。次男の光司は美少女・雪子と知り合い、惹かれていく。恩田家の人々は、様々な人と触れ合いながら、新しい人生へと向かう旅をするのだった。長塚京三の頑固親父ぶりも見事だが、「僕は勉強ができない」の鳥羽潤と、これが映画デビューとなった吉川ひなのの初々しさも印象的。DVDは「瀬戸内三部作 メモリアルDVD-BOX」に収録。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大江戸の侠児", "大江戸の侠児", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "その日暮らしのチンピラヤクザが、武士の乗った馬にはね殺された子供の恨みを支えに、義賊・ねずみ小僧次郎吉となるまでを描いた物語。季節感を盛り込むのがタブーとされていたこの頃の時代劇に、堂々と夏の暑い日や雪のシーンを見せた意欲作として有名。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OLD ENOUGH", "オールド・イナフ としごろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ニューヨークに住む11歳の少女ロニーは、サマーキャンプで不良っぽい娘カレンと出会う。カレンの自由奔放な考えや振る舞いにロニーは面くらいながら、やがてその大人っぽさに魅力を感じ、憧れを抱くようになる……。思春期の少女のひと夏の日々を、飾らない語り口で描いてさわやかな感動を残す好編。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GINGA：THE SOUL OF BRASILIAN FOOTBALL", "GiNGA／ジンガ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "サッカーを愛するブラジルの国民性と、その懐の深さを垣間見ることができるスポーツ・ドキュメンタリー。レアル・マドリードで活躍中のロビーニョやプロ選手をめざす若者など、サッカーをめぐる登場人物10人の人間模様が描かれる。 プロ・クラブのトライアルに落選し続けているパウロ・セザール、事故で片足を失いながらもパラリンピック出場をめざすウェスクレイ。そのほかサッカーを愛する人々の日常を映し出す。", "2006-04-15", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIGHT UP NIPPON", "LIGHT UP NIPPON", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROUBLE ALONG THE WAY", "勝負に賭ける男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "金詰まりに悩む大学の学長パークは、フットボール・チームを育ててもうけようと名コーチのスティーブを迎える。だが彼は離婚した妻との娘の奪い合いで大騒動に巻き込まれて、チームどころではなく……。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ПРО　УРОДОВ　И　ЛЮДЕЙ／OF　FREAKS　AND　MEN", "フリークスも人間も", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ロシアの鬼才A・バラバノフの本邦初登場作。20世紀初頭のサンクトペテルブルクを舞台に、悪魔的なSM写真家ヨハンとその手下たちの策謀によって、二つの裕福なブルジョワ家庭が崩壊していく様を映し出す。人間の倒錯した欲望をセピアカラーの映像でえぐり出した異色作。主人公ヨハンが、当時開発されたばかりのシネマトグラフィーを用いて鞭打たれる女性の姿を撮ろうとするエピソードが、異様なエロティシズムを醸し出している。", "1998-01-01", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("七小福／PAINTED FACES", "七小福", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "京劇の伝統を伝える北京戯学院に集まった問題児たち。厳しい訓練に耐えて、彼らは少しずつ一人前に成長していく……。ジャッキー・チェンやユン・ピョウを輩出した学校を舞台にした実話に基づく物語。ジャッキーらにそっくりの子供も登場し、現在の香港映画界を支える知られざる歴史を描いた感動の一編。ビデオ発売時タイトルは「七小福 夢に生きた子供達」。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GSワンダーランド", "GSワンダーランド", "", "", "http://image.eiga.k-img.com/images/movie/53816/315x210.jpg?2275", 100, "1960年代後半にブームとなったGS（グループ・サウンズ）をテーマに、音楽に夢をかける若者を描いた青春映画。「キル・ビル」の栗山千明、「蝉しぐれ」の石田卓也、水嶋ヒロ、浅利陽介ら人気若手俳優が出演している。日劇の舞台に立つ夢を抱くマサオ、シュン、ケンタ、ミクは、白タイツにフリルの王子様ファッションがトレードマークの“ザ・タイツメン”で不本意ながら女の子たちを中心に大ブレイク。しかしミクは本当は女で……。", "2008-11-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("101 DALMATIANS", "101匹わんちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "ディズニーの長編アニメーション第17作。ダルメシアン犬のボンゴはご主人のロジャーのため、アニタとの仲を取り持つ。ボンゴもアニタの愛犬パティータと結ばれ、15匹の子犬が生まれた。ところがアニタの知人クルエラは、子犬の毛皮でコートを仕立てようと手下たちに誘拐を命じた。事件はムク犬の大佐の耳へと届き、部下の軍曹の捜索により丘の屋敷に連れ去られた99匹の子犬がいることが判明。軍曹は99匹の大脱走を手引きするが、悪いやつらに見つかり大ピンチ。子供たちを救うため、ボンゴは屋敷へと走る。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SMILE 人が人を愛する旅", "SMILE 人が人を愛する旅", "", "", "http://ecx.images-amazon.com/images/I/31riHbdcYQL._SL210_.jpg", 89, "日本を代表するスカバンドとして海外でも注目を集める「東京スカパラダイスオーケストラ」が、2006年7月から半年間にわたって敢行したワールドツアー「WILD PEACE」の模様を記録したドキュメンタリー。ヨーロッパ諸国やベトナムなど国内外で70本以上もの公演を行なった過酷なツアーを中心に、メンバー10人の半年間を追う。監督は、2003年にも同バンドを題材にした作品「CATCH THE RAINBOW」を手がけた牧野耕一。", "2007-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRITTERS4", "クリッター4　ファイナル・ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "コワくてカワイイ、人喰い毛玉怪物クリッターが、今度は宇宙へ飛び出す。1992年、バウンティ・ハンターのチャーリーは、激闘の末クリッターを退治。残った卵二つを、宇宙ポッドに積み込み輸送しようとするが、手違いで自分も一緒に乗ったままポッドは発進。50年後、一隻の宇宙船がポッドを発見。何も知らずに扉を開けた乗組員に、生まれたばかりのクリッターが襲いかかる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARRY AND WALTER GO TO NEW YORK", "ニューヨーク一獲千金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ムショ入りして、金庫破りの名人ワースの召使にさせられた芸人のハリーとウォルター。脱獄した二人は、出所したワースと女性新聞記者とともにワースの計画した銀行破りを実行して、見事大金を手に入れる。金庫破りのプロと素人が金庫をめぐって争う痛快コメディ。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虹の橋", "虹の橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "江戸時代、京都の貧しい長屋を舞台に、10歳で社会へ飛び出した若者たちの青春群像を哀歓を込めて描く。遊郭に売られそうになった妹の千代を助け、非情な両親を殺害して死罪となった富士太。その弔いに集まった千代ら長屋の若者たちは、ほかにも遊郭に売られた仲間がいることを知り、救出に向かうが……。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("中乗り新三・天竜鴉", "中乗り新三・天竜鴉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "美声とキップの良さが身上の伊那の町育ちの旅鴉“中乗り新三”。世話になっていた材木問屋のために木曽へ木材の買い付けに赴いた彼は、そこで御用林伐り出しで悪業を働く代官に出会うが……。山田達雄が松竹での第１作として監督した股旅もの。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dieci Inverni", "テン・ウィンターズ", "", "", "http://image.eiga.k-img.com/images/movie/54966/315x210.jpg?4589", 100, "冬のベネチアとレニングラードを舞台に、1組の男女の10年間に渡る友達以上恋人未満の関係を爽やかに綴ったラブストーリー。1999年冬、大学でロシア文学を学ぶためベネチアへとやって来たカミッラは、同じくベネチアに到着したばかりの青年シルベストロとめぐり合う。互いに惹かれあう2人だったが、やがてカミッラはロシアへ留学することになり……。監督は本作が長編デビューとなるイタリアの新鋭バレリオ・ミエーリ。", "2009-10-18", "4,24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUARTERBACK", "クォーターバック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "アメリカンフットボールの全米チャンピオンを目指す大学の有力選手の希望と悩みを描く青春映画。ジョーは大学フットボールのクォーターバックとして活躍していたが、毎年、いま一歩のところでチャンピオンになれないでいた。最後の年、有力選手のジョーには学内や地元のサポーターから熱い期待がかかるが、ジョーはそれがプレッシャーとなり酒場でウサを晴らすようになる。そんな時、ジョーは同じ大学のカミーユと出会い、彼女に惹かれていく……。監督は、「メジャーリーグ」で大リーグを描いたD・S・ウォード。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDEAD", "アンデッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "オーストラリア映画界の新鋭、ピーター＆マイケル・スピエリッグ兄弟による長編監督デビュー作。隕石の直撃によって不死者（アンデッド）化した人々と、生き残った人間たちといった、“人間VSゾンビ”の構図を軸に、マカロニウエスタンやSFなどの要素を絡め、サバイバル・バトルがパワフルに描き出されていく。低予算もなんのその、特殊効果も手掛けたスピエリッグ兄弟はその類い希なるアイデア性と、自宅のパソコンを最大限に駆使し、300を超えるCGエフェクトや、オリジナリティーあふれる数々の血まみれシーンなど、ハリウッド大作顔負けの映像を完成させた。", "2003-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PERFECT", "パーフェクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1970年代のシングルズ・バーに匹敵する男女の社交場と化した、現代のヘルスクラブ。ローリング・ストーン誌の記者アダムは、人気クラブで取材を続けるうちに美人インストラクター、ジェシーと結ばれるが……。お色気とスリルが交錯する娯楽作。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("午前中の時間割り", "午前中の時間割り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "８ミリを携えて旅に出た二人の女子高生の夏休みを描いた羽仁進監督の野心作。脚本はあくまでも素材とされ、生まれて初めてカメラを手にした主演の少女たちによる映像が、全編の半分以上に使われている。35ミリに引き伸ばされた８ミリ画面の粒子の荒さが新鮮な魅力。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROOTS OF EVIL", "セクシャル・サベージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "交錯する二つの殺人事件を追う二人の刑事の活躍を、エロティックな味たっぷりに描いたサスペンス。夜の街で、次々と起こるパラノイア（偏執狂）殺人が、コールガールたちを怖がらせていた。事件担当となった刑事ジェークとブレンダは、一方で街の有力者殺人事件の犯人も追っていた。絡み合う二つの事件の真相をつかむため、ジェークは危険を覚悟でブレンダをおとり捜査に投入するが……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("深い河", "深い河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「海と毒薬」に続き、またしても遠藤周作の世界に熊井啓が挑んだ１本。成瀬美津子は、満たされぬ心を埋める“何か”を求めてインド・ツアーに参加した。美津子は自由奔放な学生時代に、クリスチャンである大津と出会い、誘惑して信仰を捨てさせたことがあった。“神に勝った”ものの心の空虚さは埋められず、結局、美津子は大津を捨てる。今回の聖地ベナレス行きは、傷心のまま美津子の前から姿を消した大津を探すための旅でもあった。ベナレスの町で、大津との再会を果たした美津子は、雄大なガンジスの流れの中で自分の人生の意味を理解していく……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カルメン純情す", "カルメン純情す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「カルメン故郷に帰る」の主人公リリィ・カルメンとマヤ朱実の後日談を描く続編。浅草のストリッパー、カルメンのもとに男に捨てられた朱実が赤ん坊を抱いて転がり込んでくる。二人は泣く泣くこの赤ん坊を捨てるが、折からの出火騒ぎで急に心配になる。もとの場所へ戻ってみると、パリから帰ったばかりの芸術家が捨て子は元の情婦の仕業だと思い、電話をかけて相手に怒っているところ。この芸術家と知り合ったカルメンは、彼の描いたシュールな作品に感激し芸術家に恋心を抱くようになる。ストリップを芸術と信じ、それゆえに芸術家まがいの男には、まるで弱いカルメンという女の天真爛漫ぶりが好評を博した。前作に続き、松山善三、川頭義郎が助監督についている。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sea Monsters: A Prehistoric Adventure", "シーモンスター", "", "", "http://image.eiga.k-img.com/images/movie/53576/315x210.jpg?2594", 40, "今から8200万年前の白亜紀後期。浅瀬で生まれた海竜“ドリコリンコプス”の兄妹は、安全な浅瀬からエサを求めて内海へと旅立つが、内海には体長14メートルに及ぶ獰猛な“ティロサウルス”、や無気味な顔を持つ怪魚“シファクテイヌス”、サメの祖先とされる“クレトクシリナ”など、巨大な敵が待ち構えていた。古代の海を舞台に、激しい生存競争を繰り広げる海の生物たちを描く立体3Dアニメ。", "2008-04-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEVEN　DOLLARS　TO　KILL", "地獄から来たプロガンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "サンチョ率いる山賊団に妻を殺されたあげく、息子ジェリーをさらわれたジョニー。その20年後、サンチョに育てられたジェリーは荒くれガンマンになっていた。サンチョが銀行強盗を計画したことをきっかけに、ジョニーとジェリーは再会を果たすことに……。あまりにも非情な運命の悪戯によって対決するはめになった、父子の悲劇を描くマカロニ・ウエスタン。", "1966-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹下左膳餘話 百萬兩の壷", "丹下左膳餘話 百萬兩の壷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "29歳の若さで戦死した山中貞雄監督の、現在も残っている３本の作品の一つ。夭折の天才として今もなお語り継がれる山中貞雄は、サイレント期からトーキー初期にかけて、短い間ではあったが多くの素晴らしい映画と脚本を手掛け、日本の映画史に大きな足跡を残している。この「丹下左膳餘話・百萬兩の壷」は山中の最高傑作の一つ。大河内伝次郎の丹下左膳といえば、戦前は伊藤大輔によって、戦後もマキノ雅広によって手掛けられたチャンバラものを代表するシリーズ。その殺気あふれる剣劇ものを山中はハリウッドの人情喜劇を頭において時代劇ホームドラマの喜劇へとすり込み、見事にアレンジしてしまった。ちょっとのんきな左膳とその女房のお藤はひょんなことからみなし児のチョビ安を育てることになる。そのチョビ安が金魚入れにして持ち歩いているコケ猿の壷は、実は百萬両の謎を秘めたものだった……。実にテンポのよい演出と随所に散りばめられたユーモアで、今もその輝きを失わない必見の名作である。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED HARP BLUES", "RED HARP BLUES", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ブルースバンドで名をなそうとする陽児は、人々を魅了する音色を持つ伝説のブルースハープの存在を知る。だが、そのブルースパープの持ち主は飲んだくれの路上生活者だった……。夢を求めて迷走する青年を描いた青春物語。ミッキー・カーチス、木村充揮、桑名正博、宮田和弥らミュージシャンも多数出演。劇中の迫力あるライブやセッションシーンが大きな見どころ。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ばかのハコ船", "ばかのハコ船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「どんてん生活」の山下敦弘がダメなカップルの道行きを豊潤な感性で綴った監督第２作。故郷で再起を図る健康飲料販売の青年と、彼の恋人。二人のさらなる失敗と挫折感を見つめる。健康飲料“あかじる”の販売に失敗し、借金500万円を抱えて東京を後にした酒井大輔は恋人・島田久子を連れて里帰りする。人脈を駆使して大成功するはずが、事はうまく運ばない。やがて大輔は元彼女の風俗嬢・小林マドカのもとに雲隠れする。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゆずり葉", "ゆずり葉", "", "", "http://image.eiga.k-img.com/images/movie/54636/315x210.jpg?4108", 103, "全日本ろうあ連盟創立60周年を記念して製作された人間ドラマ。最愛の恋人・早苗の死をきっかけに、ろうあ運動の記録映画製作を断念した敬一。それから30数年後、大工として暮らしていた彼の元に、記録映画を完成させてほしいという依頼が舞い込む。悩みながらも製作を再開した彼は、俳優の吾朗や薬剤師を目指す尚美ら若い世代のろう者との交流を通し、自らの過去と向き合う決意をする。早苗役にSPEEDの今井絵理子。", "2009-06-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Little Miss Sunshine", "リトル・ミス・サンシャイン", "", "", "http://image.eiga.k-img.com/images/movie/33796/315x210.jpg?3384", 100, "サンダンス映画祭で絶賛され、第19回東京国際映画祭でも最優秀監督賞、最優秀主演女優賞、観客賞など最多3部門を受賞したロード・ムービー。アリゾナからカリフォルニアまでのバス旅行を通じて、崩壊寸前だった家族の再生を描く。監督はこれまでジャネット・ジャクソンやREMなどのPVを手がけ、本作で劇場映画デビューを飾ったジョナサン・デイトンとバレリー・フェリス夫妻。", "2006-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Men in Black II", "メン・イン・ブラック2", "", "", "http://image.eiga.k-img.com/images/movie/1536/315x210.jpg?3428", 88, "トミー・リー・ジョーンズ＆ウィル・スミスの主演で、地球に住まう異星人を監視する秘密組織「MIB（メン・イン・ブラック）」の活躍を描いたSFアクションコメディのシリーズ第2作。MIBのエージェントになって5年が経つJは、いまやエリートエージェントとしてさまざまな事件に対処していた。そんなある日、かつてザルタ星人が隠したとされる強大な力をもつ「ザルタの光」を求めて、凶悪なカイロシアン星人のサーリーナが地球にやってくる。セクシー下着モデルに化けたサーリーナはやすやすとMIB本部を占拠。唯一難を逃れたJは、かつての相棒でいまは記憶を消されて郵便局員として平和に暮らしていたKを呼びもどし、名コンビを復活する。監督＆メインキャストはもちろん、製作総指揮のスピルバーグ、音楽ダニー・エルフマン、特殊メイクのリック・ベイカーら前作のスタッフ陣も集結して製作された続編。", "2002-07-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("X-Men Origins: Wolverine", "ウルヴァリン：X-MEN ZERO", "", "", "http://image.eiga.k-img.com/images/movie/53236/315x210.jpg?2966", 109, "大ヒットアクション「X-MEN」シリーズの人気キャラクター、ヒュー・ジャックマン扮するウルヴァリンの過去を描く物語。驚異の肉体再生能力と戦闘力を持つ兄ビクターと弟ローガンは、150年以上に渡り共に生きてきたが、ある事件が引き金となり袂を分かつ。ローガンは自らの体に超金属アダマンチウムを移植し、ウルヴァリンとしてビクター打倒を誓うが……。監督は「ツォツィ」でアカデミー外国語映画賞を受賞したギャビン・フッド。", "2009-09-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私たちの結婚", "私たちの結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "松山善三と松竹ヌーヴェル・ヴァーグの旗手として活躍していた篠田正浩が共同で脚本を執筆、篠田が監督した青春恋愛事情。川崎の漁網会社に勤める圭子と冴子の姉妹は、二人とも職工の駒倉に好意を寄せていたが、冴子は姉のために身を引こうと決意する。しかし妹の思いをよそに、姉は金持ちの松本に惹かれていく。倍賞千恵子の新鮮な演技が初々しい。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Spy Who Loved Me", "007／私を愛したスパイ", "", "", "http://ecx.images-amazon.com/images/I/51XoimlTEeL._SL210_.jpg", 125, "「007」シリーズ10作目の記念作品として製作。そのため、今までの同シリーズのパロディーが随所に散りばめられて、ファンにはうれしい一編となっている。東西両陣営の原子力潜水艦が次々と行方不明になる。さっそくボンドとソ連の女スパイ、ソニアが一致協力して捜査にあたる。彼らは潜水艦を武器に世界征服を狙う男、ストロンバーグの秘密基地に潜入するが……。内容としては、もはやイアン・フレミングの原作の面影もないが、ボンドと対決する鋼鉄男ジョーズのキャラクター、あるいは水中に潜るロータス・エスプリ車など、映画ならではの出色の面白さを見せる。", "1977-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雲がちぎれる時", "雲がちぎれる時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "バスの運転手・三崎は、暗い過去を持つ女・市枝と再会する。三崎は過去を忘れて一緒になろうと市枝にせまるが、三崎の幸福な結婚を願って市枝は去っていく。田宮虎彦の『赤い椿の花』を、ラブロマンスの名手でもある五所平之助が映画化。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("曖昧な未来、黒沢清", "曖昧な未来、黒沢清", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "世界が注目する映画監督、黒沢清の創造の源を探るドキュメント。「≒〈ニア・イコール〉森山大道」が話題を呼んだ新鋭、藤井謙二郎監督が「アカルイミライ」の撮影現場に密着、このメイキングに黒沢監督が目を通し、その時々での考えや意図を語る。「アカルイ…」に込めた思いや本音を意外にも包み隠さず語る彼の姿が印象的だ。オダギリジョーや浅野忠信ら出演者が語る黒沢清像も興味深い。ビデオタイトルは「曖昧な未来」。DVDは「アカルイミライ スペシャルBOX」に収録。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCORCHERS", "セックスの義務と権利", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "結婚の時を迎えたセックス恐怖症の女の子スプレンディッド、夫が全く抱いてくれないことに不満を募らせる娘タルボット。そんな彼女らを中心に、平和なルイジアナの田舎町に巻き起こった珍騒動を綴る。娼婦役のF・ダナウェイほか実力派キャストが集結し、セックスにまつわる現代的な悩みを風刺したラブ・コメディ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋の片道切符", "恋の片道切符", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "篠田正浩のデビュー作。題名は当時、爆発的人気のあったロカビリーのヒット曲から採っている。人気歌手のマネージャー・衛子は、彼を自分のプロダクションにつなぎとめておくため、彼女を頼ってやって来た彼氏がいる女を彼に世話する……。軽薄でエゴイストな歌手を平尾昌章が好演。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bone Collector", "ボーン・コレクター", "", "", "http://image.eiga.k-img.com/images/movie/1846/315x210.jpg?1800", 117, "ニューヨーク市警の刑事ライムは優秀な犯罪学者だったが、4年前に負った脊髄の損傷により寝たきりの身体となっていた。一方、青少年課への異動を控えた警官アメリアは、最後のパトロール中に受けた無線で一人現場へ急行、無惨な男の死体を発見し、現場に残された意味不明な証拠を素早くカメラに収める。アメリアの的確な判断に感心したライムは彼女を助手に任命。以後彼女はベッドから無線で指示を送るライムの手足となり、事件の捜査にあたる。", "2000-04-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二等兵物語・あゝ戦友の巻", "二等兵物語・あゝ戦友の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "昭和19年10月、最前線の古山二等兵はケガの功名で二階級特進の軍曹となり、内地の堀部中隊に転属、そこで親友・柳一等兵と再会する。かつての上官・森下伍長は、古山を陥れようと画策するが失敗。やがて二人は沖縄戦線へ送られ……。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青の瞬間〈とき〉", "青の瞬間〈とき〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "島根県・西ノ島に住む達夫は、高校受験を控えた15歳の中学生。単調で退屈な日々を送っていた達夫だが、ある日車椅子の少女・加奈恵と出会い、深夜の散歩に出かけるようになる。ごく普通の少年が恋愛や親友とのけんかを通して一歩ずつ大人になっていく姿を描くホロ苦い青春ストーリー。草野陽花監督第一回監督作品。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIO−KILL", "BIO-FILE ハッカーを消せ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "西暦2004年、最先端の医療研究が行われているレッド・フォーク研究所の中で、天才科学者プロクター大佐は極秘の研究を進めていた。それは、人間の血液から精製された血清によって寿命を延ばす研究であリ、大佐は永遠の命を手に入れ、世界を征服しようとしていたのだ。同じ研究所に勤務しているプログラマーのレースは、偶然大佐のコンピュータに侵入し、彼の恐るべき野望を知ってしまう。レースは相棒のロボットとともに、大佐の野望を阻止するべく立ち上がる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHENANDOAH", "シェナンドー河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "南北戦争から３年目の1863年、シェナンドー河で農場を営むチャーリー一家は、中立を守って暮らしていたが、近くの渓谷が激戦地になるにおよんで否応なく戦争に巻き込まれていく。娘の婿は南軍に、妻が死と引き換えに生んだ末っ子は北軍に……。最後の本格西部劇作家、A・V・マクラグレンの代表作。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VENGEANCE VALLEY", "復讐の谷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "大牧場主の放蕩息子が、義兄の牧童頭に、自分の捨てた子持ち女を押しつける。おかげで女の兄二人に狙われるはめになった牧童頭は怒り心頭、ついに義兄弟対決となる……。1950年代製作の典型的西部劇の一つ。ランカスターの雄姿が抜群。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANNIE HALL", "アニー・ホール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "“アレンの最高傑作。上質な情感を備えたラブ・ストーリーであり、同時に涙のコメディでもある。必見の娯楽映画だ”とチャップリンにいわしめ、アカデミー賞主要４部門を独占した異才W・アレンの代表作。漫談家としてニューヨークのTVやナイトクラブで活躍するアルビーは、テニスクラブでアニーと知り合い、意気投合。二人はすぐに同棲を始めるが、お互いに嫌な部分が目につくようになる。二人はそれぞれ精神科医に相談をし、どうにか治まるかに見えた。そんな時、アニーはプロ歌手のトニーに歌をほめられ、カリフォルニアに行くことを勧められる。そしてアニーは、アルビーを残してカリフォルニア行きを決心する。歌手のトニー役でP・サイモンが出演している。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("砂の小舟", "砂の小舟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "砂の中に埋もれていた古い小舟に導かれて洞窟の奥深く入った17歳の少女と、口がきけない16歳の少年が体験する妖幻な世界を描いたメルヘン・タッチの作品。のちの丹波の活動を示すかのように、作品のなかに冥府への関心が現れている。別題「丹波哲郎の地上より大霊界」。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alien", "エイリアン", "", "", "http://ecx.images-amazon.com/images/I/41A6ht%2BvpuL._SL210_.jpg", 116, "　宇宙船ノストロモ号の乗組員はある惑星で異星人の宇宙船を調査する。だがその時、卵の中の生き物が乗組員に寄生。やがてそれは腹を食い破り、ノストロモ号内に潜伏する。その後、宇宙生物は人間をひとりずつ抹殺。生き残った航海士リプリーはたった独りで戦いを繰り広げることに！　未知の生命体の恐怖を描いたSFホラー。主演シガニー・ウィーバーはシリーズ全4作に出演することに。03年に、再編集された「エイリアン／ディレクターズ・カット」も公開された。", "1979-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flight of Fury", "沈黙のステルス", "", "", "http://image.eiga.k-img.com/images/movie/34276/315x210.jpg?2835", 98, "スティーブン・セガールが米軍パイロットに扮し、ステルス戦闘機を盗んだテロリストたちと対決する本格アクション。米軍の最新鋭ステルス戦闘機が訓練中に何者かによって盗まれた。奪還を命じられた凄腕パイロットのジョンは、懲罰免除を条件に任務に就く。ところが、事件には巨大な陰謀が隠されており……。「沈黙の奪還」に続きミヒャエル・ケウシュ監督がメガホンを取る。共演は「ブラック・ダリア」のアンガス・マッキネスほか。", "2007-10-27", "1,3,56", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少女たちの日記帳　ヒロシマ・昭和20年4月6日—8月6日", "少女たちの日記帳　ヒロシマ・昭和20年4月6日—8月6日", "", "", "http://image.eiga.k-img.com/images/movie/55226/315x210.jpg?3579", 109, "昭和20年の広島県立広島第一高等女学校1年生の日記帳には、学校生活、友人関係、家族、恋愛など、平凡ながら幸せにあふれる日々が綴られていた。同年8月6日に原子爆弾が投下されることを知らなかった少女たちの日常を再現し、当時の日本を振り返る。NHKで放送されたドキュメンタリー番組を劇場で公開するプロジェクト「This is REAL／NHK-BSドキュメンタリー・コレクション」にて上映。", "2010-02-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CLEARING", "二重誘拐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ビジネスでの成功と家族に恵まれ、アメリカン・ドリームを体現したかのような主人公が、一人の平凡な男に誘拐され、極限状況に引きずり込まれていく……。R・レッドフォードとW・デフォーの二大実力派スターが火花を散らすサスペンス映画。そこに主人公の妻の存在を絡め、３人の愛憎や人生の皮肉を浮き彫りにする大人の味わいの心理劇。アメリカン・ドリームを絵に描いたような生活を送っているウェインとアイリーンの夫婦。ところが、ウェインが、突然仕事を失うという不幸に見舞われたごく普通の男アーノルドに誘拐されたことで、彼らの幸せは崩れていく。", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陸軍中野学校・密命", "陸軍中野学校・密命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "イギリス諜報機関極東キャップ“キャッツ・アイ”の正体を暴くために中野学校より密命が下る。それを受けた椎名次郎が発見した意外な人物とは……。サスペンス・タッチで描かれた本作は、犯人探しの面白さが加味され、良質のミステリーとなっている。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オカンの嫁入り", "オカンの嫁入り", "", "", "http://image.eiga.k-img.com/images/movie/55236/315x210.jpg?1299", 110, "咲乃月音原作の小説「さくら色　オカンの嫁入り」（宝島社文庫）を、 宮崎あおい＆大竹しのぶのダブル主演で映画化。陽子（大竹）と月子（宮崎）は母一人子一人で大阪の下町で暮らしていた。ある晩遅く、陽子は若い金髪の男を連れて帰宅し「この人と結婚することにしたから」と言い放つ。いきなりのことに困惑し、怒る月子は、隣に住む大家のところに居候することにするが、やがて陽子の事情を知り……。監督は「酒井家のしあわせ」の呉美保（オ・ミポ）。", "2010-09-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Out of Sight", "アウト・オブ・サイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "人気作家E・レナードの原作を映像化した犯罪ドラマ。脱獄した銀行強盗犯ジャックが、逃走中に偶然出会った女性保安官カレンに一目惚れし、大胆なアタックを開始。カレンは任務と恋の狭間で悩みつつも、ジャックを追跡する。一方、ジャックは足を洗う前の大仕事としてダイヤの原石の強奪計画に着手するが……。拳銃も持たずに巧みな話術で銀行強盗をやってのける主人公像など、軽妙でスタイリッシュなテイストは、まさにレナードの世界。G・クルーニー＆J・ロペスの美男美女の共演もイキなムードを盛り立てる。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONCE UPON A TIME IN THE WEST／C’ERA UNA VOLTA IL WEST", "ウエスタン", "", "", "http://ecx.images-amazon.com/images/I/618UVuwoeXL._SL210_.jpg", 165, "西部の土地の利権を狙う悪党フランクが、所有者マクベインを殺害した。ところが土地の権利はマクベインの婚約者ジルに受け継がれ、彼女の命までも狙うフランクの前に謎の男が立ちはだかる。「荒野の用心棒」「夕陽のガンマン」でマカロニ・ウエスタンの一大ブームを巻き起こしたS・レオーネ監督が、ハリウッドの大物スターと組んで撮り上げた大作。ロングショットを巧みに織り交ぜたレオーネの堂々たる演出、ハーモニカをフィーチャーしたE・モリコーネの忘れえぬ音楽、H・フォンダの不敵な悪役ぶりなど見どころは尽きず、レオーネの最高傑作と見なすファンも少なくない逸品である。", "1968-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナンパの定石", "ナンパの定石", "", "", "http://image.eiga.k-img.com/images/movie/54856/315x210.jpg?4436", 100, "プロのナンパ師を自負する男女が繰り広げる恋愛バトルを描き、韓国で300万人以上の動員を記録したラブコメディ。ナンパの腕に並々ならぬ自信を持つジウォンとミンジュン。そんな2人が運命的な出会いを果たし、お互いを次のターゲットに定めるが……。「私の頭の中の消しゴム」のソン・イェジンと、ドラマ「朱蒙」のソン・イルグクがこれまでのイメージを覆すコミカルな演技で新境地を開拓。「ラスト・プレゼント」のオ・ギファンがメガホンを取る。", "2009-11-28", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冒険大活劇 黄金の盗賊", "冒険大活劇 黄金の盗賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "沢島忠監督が、特撮アクションを駆使して描いた娯楽時代劇。牢の中で軍用金300万両の夢を見た二人の怪盗が、奉行との山分けを条件に放免され、その探索を命じられる。やがて軍用金が埋められている沖の島に漂着した二人は、石田三成の残党が徳川幕府転覆のために軍用金を狙っていることを知った。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ME ＆ HIM", "ミー＆ヒム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ある日、自分の“下半身”がコトバをしゃべるようになったら……。ちょっとエッチながら迷える現代人の心理をシリアスに突いた風変わりなコメディ。イタリアの鬼才モラヴィアの小説を原作に、ともすれば下品でいやらしくなりがちな題材を、明るく愛嬌ある作品に仕上げた演出には好感が持てる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GHOST STORY", "ゴースト・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "共通の忌まわしい過去を持つ４人の男が、ニューイングランドの古ぼけた屋敷に集まった。昔の嫌な思い出を忘れ、気ままな日々を送ろうとするのであるが、次々と謎の死を遂げていく。果たして、姿なき殺人者の恐ろしい正体は……。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カードキャプターさくら　封印されたカード", "カードキャプターさくら　封印されたカード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "CLAMP原作による人気TVアニメの劇場版第２作で、TV版の後日談的ストーリー。さくらは、自分に告白したまま香港に帰っていったシャオランに、まだ返事ができないままでいる。そんなある日、シャオランが町に戻ってきた。今度こそ返事をしようと焦るさくら。その頃、町の風景が少しずつ欠けていくという不思議な現象が発生する……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はたらく一家", "はたらく一家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "プロレタリア作家・徳永直の同名小説を成瀬巳喜男が映画化。親の手助けをして働かざるを得ない子供たちが、進学したいと発言したことにより、一家に引き起こる様々な問題を描く。原作を貫く強い問題意識や社会批判に比べ、成瀬演出は日常描写に重きを置かれており、紋切型ではない優れた家庭劇となっている。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUSSICUM", "ルシカム KGB・赤い狙撃者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ローマの市街で、一人のアメリカ人女性が殺された。そのころ、ローマ教皇のソビエト訪問に向けて全世界中のスパイが、ローマに結集していた。殺された女もスパイだったのか？ ローマのKGBの拠点ルカシムをめぐって、CIA対KGBのスパイ合戦が繰り広げられるサスペンス・アクション。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／THREE", "THREE／臨死", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "韓国・タイ・香港の３人の監督による、３話構成のホラー・オムニバス。第１話「memories」を「クワイエット・ファミリー」で知られる韓国のヒットメーカー、キム・ジウン。第２話「the wheel」を世界的にも評価が高い、タイのN・ニミブット。そして第３話「going home」を、本プロジェクトのプロデューサーでもある香港映画界の名匠P・チャンが手掛けた。“死の向こう側にあるもの”といったテーマのもと、それぞれの文化に根ざす死生観から紡ぎ出された、恐怖と愛の物語が展開していく。ビデオ、DVDタイトルは「THREE　死への扉」。", "2002-01-01", "5,23,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バツ＆テリー", "バツ＆テリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "横浜・海峰高校野球部の一年生バッテリー、バツこと抜刀軍とテリーこと一文字輝は、超高校級のスラッガーであるにもかかわらず、ケンカ大好きのナンパ・コンビ。二人はバイク事故で恋人を失った少女・アンと知り合うが、彼女はなかなか打ち解けない。やがてバツとテリーに恨みを持つ辰夫がアンをさらい、不良たちとの激闘の末に救い出した二人は、甲子園予選大会の決勝大会に傷らけの体で駆けつけるのだった……。痛快青春マンガを奔放なタッチでアニメ化した劇場用オリジナル作品。主人公二人の軽い性格を生かして全編を押し切り、そのノリに加えて（原作と同じ出版社の雑誌から）猫のマイケルやその他の人気キャラクター、ついでに原作者自身も登場するなど遊びもたっぷり。ただしそのはしゃぎっぷりに、作画が追いついていないところも……。アニメ声優初挑戦の有森也実が、アンの声を演じている。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jarhead", "ジャーヘッド", "", "", "http://image.eiga.k-img.com/images/movie/1096/315x210.jpg?2216", 123, "「アメリカン・ビューティー」「ロード・トゥ・パーディション」のサム・メンデス監督が、一歩兵の手記を原作に“地上にいた兵士”の視点から見た湾岸戦争を描く話題作。「デイ・アフター・トゥモロー」「ブロークバック・マウンテン」のジェイク・ギレンホール、「フライトプラン」のピーター・サースガードを中心に、オスカー俳優2人、「Ray／レイ」のジェイミー・フォックスと「アダプテーション」のクリス・クーパーが脇を固める。", "2006-02-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rhapsody in Blue", "アメリカ交響楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "ワーナー・ブラザース映画が、トーキー誕生20周年を記念して製作した豪華なミュージカル大作。不滅の作曲家ジョージ・ガーシュインの生涯を骨子に、アメリカン・ジャズの変遷を描く。表題曲のほか『スワニー』『ス・ワンダフル』『パリのアメリカ人』『ポギーとベス』など名曲が次々と堪能できる。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門捕物帖 卍蜘蛛", "右門捕物帖 卍蜘蛛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ご存じむっつり右門の痛快捕物編。江戸で連続殺人事件が発生、その死体のいずれの腕にも卍蜘蛛の刺青があった。現場に残された手拭いなどから犯人は浪人・石川弥太郎と推定されたが、当の石川弥太郎は３年前に死んでいたのだった。右門は虚無僧に化け、石川が働いていた下田奉行所へと向かう。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("借王〈シャッキング〉THE MOVIE VOL.5沖縄大作戦", "借王〈シャッキング〉THE MOVIE VOL.5沖縄大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "莫大な借金を背負った安斉満、水沼正三、森下怜子の３人が大胆な詐欺計画を実行し、悪党をやっつける痛快マネー・ゲーム映画シリーズの第５作。悪徳政治家と癒着し、政府の仕事を受注して私腹を肥やす悪徳企業の存在を知った３人は、裏金を奪取するため沖縄を舞台にして壮大な詐欺計画を実行する。だが、そこに裏金の横取りをたくらむ女詐欺師軍団が現れ……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オバアは喜劇の女王　仲田幸子　沖縄芝居に生きる", "オバアは喜劇の女王　仲田幸子　沖縄芝居に生きる", "", "", "http://image.eiga.k-img.com/images/movie/57456/315x210.jpg?3747", 90, "沖縄芝居（沖縄方言＝ウチナー口で演じる芝居）の劇団「でいご座」を率いる沖縄の喜劇女優・仲田幸子の全ぼうに迫ったドキュメンタリー。全盛期は40以上の劇団があったとされる沖縄芝居も、若者の方言離れやテレビの普及により多くが解散していった。そんな中、芸暦64年を迎える76歳の幸子が率いる「でいご座」は、いまも沖縄の文化を伝え続けている。幸子本人や家族、劇団仲間、ファンのインタビューも交え、太平洋戦争を体験した幼少期から、終戦後に沖縄芝居に魅せられ芝居一筋で人生を駆け抜けてきた幸子の生きざまを紐解いていく。", "2011-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("検事霧島三郎", "検事霧島三郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "高木彬光の推理小説の映画化。腕利きの検事・霧島三郎はフィアンセ・恭子との結婚を前にして殺人事件を担当することになった。麻薬ルートを探る三郎の忙しさに、恭子の心は徐々に離れていく……。ミステリーとラブロマンスが交差する探偵映画。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Non-Stop New York", "ノンストップ紐育", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "「メリー・ポピンズ」等の名作で知られるロバート・スティーブンソン監督の初期作品。ニューヨークで殺人現場を目撃してしまったイギリス人ジェニーは、彼女の口を封じようとする犯人の陰謀により、イギリスに帰国したとたん身に覚えのない窃盗罪で投獄されてしまう。その頃、ニューヨークでは殺人犯として無実の男性が捕らえられ、死刑判決を受けていた。ジェニーは彼を救うため、密かにアメリカ行きの旅客機に乗り込むが……。", "1937-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GRAND CHEMIN", "フランスの思い出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "風光明眉なフランスの田舎を舞台に、９歳の少年ルイのひと夏の体験を綴った、情感あふれる思春期譚。パリ育ちのルイが、母に連れられブルターニュ地方の小村ルーアンスにやって来た。生まれて初めて親元を離れて過ごす３週間のバカンス。不安におののくルイの心を引き立ててくれたのが、隣家のおしゃまな少女マルティーヌだ。都会にはない田舎ならではの冒険の数々に目を輝かせるルイ……。ルイを預かったプロとマルセル夫婦の愛憎、実は離婚寸前のルイの両親の内情など、大人たちの思い悩みや悲しみが、ルイの目を通してナイーブに語られて共感を呼ぶ。ルイ役を演じる監督の実子アントワーヌの澄んだ瞳が印象的。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Big Blue", "グレート・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "リュック・ベッソン監督の長編3作目となる出世作「グラン・ブルー」のインターナショナルバージョン。素潜りで深度を競うフリーダイビングにかける男たちの物語。自身もダイビングの経験を持つリュック・ベッソンが、実在するフリーダイビング選手、ジャック・マイヨールをモデルに描き大ヒットを記録、日本ではのちに完全版やオリジナル・バージョンも公開された。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DANGEROUS", "ザ・デンジャラス／地獄の銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "日本人の女性レポーターが麻薬取引業者に惨殺される事件が起こった。殺された日本人の兄妹は復讐を誓い、街を徘徊するギャングたちを次々に殺していった。事態を重視した警察当局は若手刑事ランダムと元傭兵ビリーに事態の早期解決を要請する。追跡しながらも、この兄妹の家族への深い愛情と誇り高さに敬意を感じるランダムとビリー。二人は心の葛藤を抱えつつ、兄妹を追い詰めていく。M・パレ主演のB級ミステリー。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEIN　LIEBSTER　FEIND", "キンスキー、我が最愛の敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「アギーレ・神の怒り」など５つのヘルツォーク作品で怪演を見せた、クラウス・キンスキー。彼の日常生活や撮影現場での傍若無人ぶりを、ヘルツォーク自身が暴き出すこのドキュメンタリーには、二人の複雑な愛憎関係と、撮影者ヘルツォーク自身の変人ぶりも透けて見える。キンスキーの狂気と暴力の逸話には、思わず失笑。", "1999-01-01", "7,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nicholas and Alexandra", "ニコライとアレクサンドラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 189, "帝政ロシア最後の王朝であるロマノフ家滅亡の悲劇を「猿の惑星」のフランクリン・J・シャフナー監督が描いた歴史スペクタクル。20世紀初頭、日露戦争の影響で政情不安が広がるロシア。皇帝ニコライ2世と皇后アレクサンドラの間に生まれたアレクセイは重い病を患っていたが、怪僧ラスプーチンの不思議な力によって奇跡的に回復する。これがきっかけでラスプーチンは皇后の寵愛を受けるようになり、次第に政治にまで干渉を始める。", "1971-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("煉獄エロイカ", "煉獄エロイカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "吉田喜重が松竹を退社して設立した現代映画社とATGの提携による１千万円映画。原子力機構の研究員の庄田力弥は、平和だが、頼りなく漂う時間に生きている。しかしある時を境に、彼は破滅への道を歩まざるをえなくなる……。明確に連続するストーリーというものはなく時に応じて性・政治・革命などの様々なイメージが割り込んでくる。賛否両論に分かれた前作「エロス＋虐殺」をしのぐ難解な迷宮映画。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUDINI", "魔術の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "アメリカを代表する実在のマジシャン、ハリー・フーディニを採り上げた実話映画。魔術に命を懸ける熱気溢れる場面に加え、妻とのロマンスのサブ・ストーリーが泣かせてくれる。見世物小屋の奇術師と女性客として知り合った二人は結婚したが、生活は楽にならなかったものの、彼は奇術に情熱を傾け欧州興行に出てブレイクの時を迎える。だが、危険な脱出奇術に魅せられて……。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPERMAN IV ： THE QUEST FOR PEACE", "スーパーマン4 最強の敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "“スーパーマン”を生み出したDCコミック誌の創刊50周年を記念して製作された、映画化シリーズ第４作。悪の天才レックス・ルーサーが三たび登場し、核エネルギーから作り出した最強の敵“ニュークリアーマン”を差し向けてくる。地上や摩天楼上空、そして月面で行われる善と悪の死闘が最大の見せ場となっている。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間の條件 第３部・望郷篇／第４部・戦雲篇", "人間の條件 第３部・望郷篇／第４部・戦雲篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 176, "関東軍に初年兵として召集された梶は、上官らの不条理な命令には従わず、あえて自分を曲げなかったために、様々なリンチを加えられる。それでもなお自己の信念と主張を貫こうとする梶の目に映った軍内の生活は、戦争によって醜く歪められた世界だった……。上官たちのリンチに耐えに耐えぬく梶の姿は大衆の共感を呼び、第１・２部を上回る大ヒットを記録。宮島義勇によるカメラも、画面に空を広くみせる独特のアングルで強く印象に残る。梶の妻がはるばる夫を訪ねて現れ、二人が久しぶりに再会する雪の夜のラストシーンは胸をしめつけられるほど美しい。DVDは「人間の條件DVD-BOX 全６巻」に収録。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ultraviolet", "ウルトラヴァイオレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「バイオハザード」シリーズのミラ・ジョボビッチ主演による近未来アクション。21世紀末、新種のウィルスが世界中に広がり、感染によって超人的な能力を身につけた「ファージ」たちと、彼らの存在に危機感を抱いた人間政府との間で激しい戦いがはじまる。ウィルスに感染したために夫と子どもを政府に奪われた女性ヴァイオレットは、政府が開発したという最終兵器を奪うべく立ちあがるが、その正体が9歳の少年だと知る。", "2006-06-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("楽園／流されて", "楽園／流されて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ソリの合わない男女が無人島に流れ着いて、愛と性に目覚めるという70年代のイタリア産官能映画「流されて…」のパターンを踏襲した作品。局アナの恵利香は、参院選出馬の選挙活動中にボートで遭難してしまい、漁師の洋平と無人島に流れ着く。二人は飲み水すら手に入らない島でなんとか生き延びようとするが……。 あらゆる文明から切り離されて、飲み水すらままならない過酷な環境で生き抜こうとする姿が、現代人の矛盾を浮き彫りにする問題作。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江湖龍虎門／FLAMING　BROTHERS", "チョウ・ユンファのマカオ極道ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "香港マフィアの義兄弟、ウィンとファはギャングの大物になろうと野望を持ち、次第にのし上がっていく。それを阻止しようとするギャングの親玉インは二人を狙い、やがて対決の日がやってくる。恋人も巻き込んで展開する、チョウ・ユンファ主演の香港ノワール。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE 5TH DAY", "オペレーション・インポッシブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "中米のある国の独裁者ロドリゲス大統領に、アメリカの医学チームが逮捕される緊急事態が勃発。CIAの高官ハシュレイは、特殊任務専門家のロバートに呼びかけ９人の精鋭チームを招集し、５日間という限られた時間内での奪回作戦を命令する。ロバートたちはジャングルの基地を急襲、運よく人質の救出に成功するが、ロドリゲスの軍隊に追われ、ロバートと女医のデニスはジャングルにとり残されてしまう。奇想天外な設定で描いたB級アクション。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACE VENTURA WHEN NATURE CALLS", "ジム・キャリーの　エースにおまかせ！　エース・ベンチュラ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "J・キャリーの当たり役となった、ペット探偵エース・ベンチュラの活躍を描く第２作。地上4000メートルの山岳地帯に向かったエースは、岩山に取り残されたアライグマの救出に失敗。そのショックでチベットの僧院にこもる。そんなある日、英国大使のフルトンからワチャティ族の守り神である白いコウモリの捜索を依頼され、エースはアフリカへ飛ぶ。ワチャティ族の王子アウダは、対立する部族との和解を図り、その族長の娘と結婚しようとしていた矢先に、コウモリが盗まれたのだ。フルトンやアウダを助手に従えて、エースの調査が始まった。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桂子ですけど", "桂子ですけど", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "中野区のアパートに一人暮らしをしている鈴木桂子は、数年前に亡くなった父親の遺骨を火葬場からこっそり盗みだし、自分の部屋に置いている。１日が24時間であり、確実に１日は終わり、明日がやって来ると改めて考えた桂子は、21歳最後の３週間を着実に生きようとするのだった。「男の花道」で1986年PFFグランプリを受賞した園子温が、若い女の子の日常を静かに描いている。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無敵のハンディキャップ", "無敵のハンディキャップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "東京都世田谷区に住む身体障害者とボランティアの集団“ドッグレッグス”が主催する“身障者プロレス”にカメラを構え、障害者と健常者が越え難い壁を肉体の痛みを通して乗り越えようとする試みに迫る異色ドキュメンタリー。不自由な身体をよじってリングに立つ脳性マヒ障害者たちの荒ぶる姿は、彼らを“安全地帯”に囲い込んで事足れり、とする健常者の“良識”を震撼させるものがある。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Changeling", "チェンジリング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "交通事故により、愛する妻と娘を一瞬にして失ってしまった作曲家のジョン。心の傷を癒すため、別荘を借りて休養する彼を待ち受けるものとは？　ポルターガイストという異常現象に焦点を当てた、サスペンス・タッチのオカルト映画。", "1980-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鷲宮☆物語　商工会の挑戦", "鷲宮☆物語　商工会の挑戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "人気アニメ「らき☆すた」の舞台となっていることから町おこしで盛りあがる埼玉県久喜市の鷲宮町で製作された青春映画。高校時代の先輩から町おこしの映画制作を命じられ、地元の鷲宮町へ帰ってきた宮田隆。ここ数年でオタクの聖地となった故郷の急激な変化に戸惑いながらも、大学の映画仲間を集めて映画づくりを開始する隆だったが……。出演は「ごくせん THE MOVIE」の上鶴徹、人気アイドルグループ「AKB48」の増田有華ほか。", "2010-04-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺人蜂 キラー・ビー", "殺人蜂 キラー・ビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "毎年恒例の夏キャンプにやってきたガールスカウトのメンバーが、殺人蜂に襲われる恐怖を描いた絶叫ホラー。「プラトニック・セックス」で鮮烈な演技をみせた加賀美早紀をはじめ、7人のアイドル女優たちが群れをなして攻撃を仕掛ける殺人蜂と格闘。彼女たちの恐怖に慄く熱演も見逃せない。ガールスカウトたちの有志で行われる毎年恒例の夏キャンプに参加した7人が、キャンプファイアーの準備に取りかかっていると、恐るべき出来事が……。", "2005-04-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パイナップル・ツアーズ", "パイナップル・ツアーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "琉球大学出身の３人の新人監督が“沖縄の本当の姿を撮りたい”という思いから作り上げたオムニバス映画。謎の病で声を失ったオペラ歌手が占い師に占ってもらうために里帰りする「麗子おばさん」。土地の言葉もわからぬまま島に流れ着いて住み始めた若者が、島の年寄りに見込まれ、本人もわけがわからぬままに島の娘と結婚させられそうになる「春子とヒデヨシ」。東京のリゾート開発会社が島に眠る米軍の不発弾に１億円の懸賞をかける「爆弾小僧」の３つのエピソードが、地元の役者とコトバによっていきいきと描かれる。沖縄以外では標準語字幕付きで上映。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stereo Future", "Stereo Future", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "異才、中野裕之による劇場長編第２作。中野監督が温めていた4つのエピソード（“Stereo Future”“Silent Female”“Samurai Fighter”“Sounds Funky”）を織り交ぜながら恋愛ドラマを軸に人々の近未来を描いていく。売れない役者の圭介は、恋人と別れたばかりだったが時代劇の主役に抜擢される。しかし現場でベテラン俳優にほんろうされ、失敗ばかりして落ち込んでいた。一方、元恋人のエリは別れたショックで言葉を発することができなくなっていた。そんな折、エリは姉に連れられ参加した環境問題シンポジウムで樹医のダニーと出会い、次第に心いやされていく。圭介を永瀬正敏が演じるほか、桃生亜希子、風間杜夫、竹中直人ほか豪華な俳優陣が顔をそろえた。音楽にも、清水靖晃、TOWA TEI、FANTASTIC PLASTIC MACHINEらが参加し、ミュージック・クリップを多く手掛けた中野監督の映像と音の融合に対するこだわりが感じられる。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すかんぴんウォーク", "すかんぴんウォーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "監督・大森一樹、主演・吉川晃司による青春の夢と挫折をテーマにした“民川裕司３部作”の第１作。吉川晃司の映画初出演作。彼はこの作品の主題歌『モニカ』で同時に歌手デビューも果たした。広島の高校３年生・民川裕司は演劇を志して上京、六本木のスナックに勤める。ある日、ロック歌手志望の同僚と穴埋め出演したディスコのステージが評判を呼び、一躍人気者となってプロダクションにスカウトされる。同僚の方も毒舌タレントとして売り出すが、彼は裕司のスキャンダル話をネタにして……。冒頭の水泳シーンでは、水球のオリンピック候補選手だったという吉川がその実力を披露。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泥だらけの純情", "泥だらけの純情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "チンピラの次郎は、不良学生に絡まれている外交官の令嬢・真美を助けた。ところが乱闘のさなか、相手は誤って自分のナイフを刺して死に、次郎もケガをしてしまう。次郎は自首するが、真美の尽力ですぐに釈放される。それから二人は毎週デートを重ね、次郎はデートの資金を捻出するため悪事を繰り返す。組長は、次郎が真美を助けた時にヤクを落としたことを警察が嗅ぎつけ、次郎を泳がしていると疑って、彼に自首を勧める。一方、真美は父の仕事で外国へ行くことに。追いつめられた二人は雪山へ逃がれ、睡眠薬を飲んで心中を遂げる。ヌーヴェル・ヴァーグに影響を与えた才人・中平康が、身分の違う若い男女の純愛を描いた作品。心中を決意した二人が、雪の中でじゃれ合うシーンが印象的で、許されない愛の悲しみが痛切に描かれる。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’HOMME A｀ L’IMPERMEABLE", "殺人狂想曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "フランスの人気喜劇役者フェルナンデル主演のブラック・コメディ。J・H・チェイスの原作を、名匠J・デュヴィヴィエが監督。平凡なクラリネット奏者が殺人事件に巻き込まれ、次から次へと死体に付き合わされたりの大混乱。原題は“レインコートの男”の意。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死亡写真／AB−NORMAL BEAUTY", "アブノーマル・ビューティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "香港や台湾で人気を博している美人アイドル・ユニット、“2R”のウォン姉妹が主演したサイコ・サスペンス。悲惨なトラウマの影響で“死”に魅了されてしまった美大生の恐怖体験を描写。「the EYE【アイ】」のパン・ブラザースの兄オキサイドが、巧みな撮影&編集テクニックを駆使してスリルを盛り上げている。芸術的才能と美貌に恵まれた女子大生ジン。交通事故現場でカメラのシャッターを切って興奮した彼女は、異様な行動を取り始める。", "2006-02-25", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sophie's Choice", "ソフィーの選択", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "第２次大戦後のニューヨークを舞台に、作家志望の青年の目を通して、アウシュビッツから生き延びたポーランド人女性ソフィーとユダヤ人の恋人の過酷な愛のドラマが綴られる。反ユダヤ主義の環境に育ちながらナチに人生を踏みにじられた女性をメリル・ストリープが熱演し、アカデミー賞主演女優賞を獲得。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAS BUECHSE DER PANDORA", "パンドラの箱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "出生不明で男出入りの激しい、博士の愛人ルル。耐えかねた博士が自殺を勧めると、逆に彼を撃ち殺す。逃亡の果て、ロンドンで夜の女となったルルは、娼婦殺しの殺人鬼ジャックを客に拾う。ドイツ・サイレント映画末期の傑作の一つ。ベーデキントの同名小説を、常に社会問題で先鋭的な作品を発表しているパプストが映画化。", "1928-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅情", "旅情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "双葉流の家元として、生け花を紹介するため海を渡った杉田真弓。しかし、本当の目的は、かつて別れた恋人三上俊一に会うためかもしれなかった。だが、異国の地で三上は日系３世の娘と婚約していた。ふんだんなハワイ・ロケで、南国ムードたっぷりに仕上げた菊村到原作のメロドラマ。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODY PUZZLE", "ボディパズル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "最愛の夫を失ったばかりのトレイシーは、悲しみと孤独の日々を送っていた。そんな彼女に新たな恐怖が襲いかかる。残虐な連続殺人事件の死体の一部が、犯人から送られてくるのだ。その肉体がパズルのように完成していく……。J・パクラが追い詰められていく美しい未亡人を演じた猟奇スリラー。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("義蓋雲天／A　HEARTY　RESPONCE", "傷だらけのメロディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ジョイ・ウォンとチョウ・ユンファの共演によるサスペンス・アクション。密入国者の女性と彼女を追う刑事たちの姿を通し、中国からの密入国者問題も視野に入れた意欲作。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ストーンエイジ", "ストーンエイジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "社会に適応できず、心に傷を背負った青年たちの不思議な交流を描いたスピリチュアルな感動作。元ひきこもりの誠也は、ひきこもりの人たちが社会復帰するための場所“ひまわりの家”のスタッフになる。自分と同じ境遇の人たちを助けたい誠也だったが、なかなか心を開いてもらえず……。", "2006-05-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON THE COMET", "カレル・ゼマンの彗星に乗って　空想と冒険のファンタジア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "チェコスロバキアの巨匠ゼマンが、J・ヴェルヌの同名小説をもとに描いた、夢あふれるファンタスティックな色彩トリック・フィルム。1888年のフランス領アルジェリア。フランス軍の若き中尉は、町で見かけた絵葉書の中の黒髪の美人に一目惚れ。ある日、誤って海に落ちた彼を助けてくれたのは、何とあの絵葉書の美人だった。その時謎の彗星が地球に急接近。大地震とともに、町全体が宇宙へ飛び出した。遠ざかっていく地球の影。ここはいったいどこ!? 前世紀の恐竜や翼手竜も登場する楽しさいっぱいの冒険ロマン。1979年テヘラン国際児童映画祭グランプリほかを受賞。", "1970-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Land and Freedom", "大地と自由", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "第2次世界大戦直前のスペイン内戦に身を投じる若者達の姿を、イギリスのケン・ローチ監督が描いた戦争ドラマ。1936年、スペインで誕生した新政府に対してフランコ将軍が反乱を起こし、スペイン内戦が勃発。リバプールに住む失業中の青年デビッドは、スペインをファシズムから守るためバルセロナへと向かう。激しい戦いが繰り広げられる中、仲間として共に戦っていたスターリン主義者たちに対し、彼は次第に反感を持ち始め……。", "1995-01-01", "3,9,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("СТАРЫЙ НАЕЗДНИК／THE OLD JOCKEY", "騎手物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "引退を決意したベテラン騎手トロフィーモフは、ライバルのパーベルに挑発され再出発を誓い、孫娘マリヤの励ましで競走馬の調教に専念する。それから一年後、パーベルの馬との運命のレースが始まった……。娯楽映画の巨匠B・バルネットのみずみずしい感性が冴えわたる、平和で心温まるストーリー。レース場面での迫力満点のカメラワークは必見。", "1940-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE SAMOURAI", "サムライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "一匹狼の殺し屋、ジェフ。死地へ赴く男の胸中は、一本の刀に生命を懸ける武士の心持ちだ。彼は空虚なアパートを出て、今日も仕事へ向かう。アリバイ工作は情婦ジャーヌに任せた。殺しはうまくいくが、逃走の際にピアニストのヴァレリーに顔を見られる。ジェフをかばったヴァレリーの証言に不審を抱いた警部は、ジェフに尾行をつける。一方、依頼人もジェフをねらい、消そうとするが失敗。そしてジェフのもとに新たな殺人の依頼がくるが、その標的はジェフをかばったはずのヴァレリーだった……。メルヴィルのタイトな演出によるフィルム・ノワールの傑作。H・ドカエの端正なカメラワークが絶品である。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RED PONY", "赤い子馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "J・スタインベックの小説の映画化で、大自然をバックに少年トミーと赤い子馬との交流が描かれる。T・ゴーディオによるテクニカラーの撮影がひときわ美しい。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン　天空の難破船（ロスト・シップ）", "名探偵コナン　天空の難破船（ロスト・シップ）", "", "", "http://image.eiga.k-img.com/images/movie/55166/315x210.jpg?2319", 0, "青山剛昌による人気コミックシリーズの劇場版14作目。大富豪・鈴木次郎吉は、世界中の宝石を狙う怪盗キッドに向け、「世界最大の飛行船ベルツリー号に収めた宝石・天空の貴婦人を盗んでみよ」という挑戦状を叩きつける。キッドから宝石を守るため、コナン一行も飛行船に招待されるが、突然謎のテロリスト「赤いシャムネコ」が飛行船を襲う。", "2010-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Baader Meinhof Complex", "バーダー・マインホフ　理想の果てに", "", "", "http://image.eiga.k-img.com/images/movie/54316/315x210.jpg?4357", 150, "1960年代後半から70年代の10年間に渡るドイツ赤軍の闘争を克明に描き、第81回アカデミー外国語映画賞候補になった社会派ドラマ。1967年、西ドイツ。女性ジャーナリストのウルリケ・マインホフは、ベトナム戦争に対する反米運動で逮捕された活動家アンドレアス・バーダーらの思想に共鳴。“バーダー・マインホフ”グループと呼ばれた彼らは、やがてドイツ赤軍として武装闘争を繰り広げるが……。主演は「善き人のためのソナタ」のマルティナ・ゲデック。", "2009-07-25", "7,2,43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野狐笛 花吹雪一番纏", "野狐笛 花吹雪一番纏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "東千代之介が、出生の謎を持つイキな町火消しを演じた、娯楽時代劇。三次は、野狐の絵をあしらった横笛と一緒に捨てられていたという男。今は、町火消の“に組”の初五郎に引き取られている。彼には、おきぬという幼なじみがいたが、そのおきぬに旗本の尾形玄蕃が目をつけて……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OPPOSITE OF SEX", "熟れた果実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ティーンのセックス、妊娠、殺人を描いたブラック・ユーモア漂う青春ドラマ。16歳の家出娘ディーディーは異母兄のゲイの恋人マットをいたずら半分で誘惑して妊娠。異母兄の金を持ち逃げして駆け落ちした彼らだったが、ディーディーは浮気相手を見つけ、しかもその相手を弾みで殺してしまい……。「アダムス・ファミリー」では子役だったC・リッチがヒロインを体当たりで演じ、成熟した姿を披露。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TURBULENCE", "乱気流　タービュランス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "クリスマス・イヴのニューヨークからロサンゼルス行きの旅客機が離陸する。数少ない乗客の中には、連邦保安官によって護送中の連続殺人鬼ライアンと強盗犯スタッブスが含まれていた。するとオハイオ州上空で、スタッブスが保安官の銃を奪って乱射。スチュワーデスのテリーは、生き残ったライアンと闘いながら、パイロットを失った機を操縦することに……。誘拐サスペンスの秀作「ジャグラー　ニューヨーク25時」で記憶されるR・バトラー監督が、久々にその手腕を発揮した快作。操縦者の不在、サイコキラーの恐怖という二重の極限状況をたたみかけるような展開で描くことで、他の航空パニック映画との差別化を図っている。あわやジャンボ機がロサンゼルス市街に墜落、というクライマックスもスリル満点だが、それ以上に汗と涙でメイクもグシャグシャの大熱演を見せたL・ホリー嬢に拍手を贈りたい。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("寝盗られ宗介", "寝盗られ宗介", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "1980年に初演されたつかこうへいの同名戯曲をベテランの若松孝二が映画化。ドサ回り一座の座長・宗介と妻の愛の葛藤を中心に、座員たちの面白おかしい人間模様が展開する。藤谷美和子が尻軽女房を好演、原田芳雄と絶妙なコンビを見せる。宇崎竜童のポップな音楽も魅力。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APT PUPIL", "ゴールデンボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "S・キングの小説を「ユージュアル・サスペクツ」のB・シンガーが映画化した心理サスペンス。善良な少年が自分自身の中に眠る悪の心に支配されていく様をじっくりと映し出す。ロサンゼルス郊外に住む高校生トッドは成績優秀、スポーツ万能の優等生だった。そんな彼がホロコーストについて勉強を進めるうちに、アーサーという一人暮らしの孤独な老人が、アウシュビッツの副所長で“吸血鬼”と呼ばれた戦争犯罪人クルトであることを知る。トッドはそれをネタにクルトを手玉に取り、収容所の実態を聞き出す。それは単に探求心から生まれた行為だったが、クルトが語る残虐な話によって彼は悪の魅力に取り憑かれていく。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRUEL INTENTIONS", "クルーエル・インテンションズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "これまでに３度映画化されたラクロの「危険な関係」を現代のティーンエージャーを主人公にして映画化したセクシャルかつサスペンスフルな青春ラブ・ストーリー。マンハッタンの豪壮なタウンハウスに住む義理の姉弟キャスリンとセバスチャン。キャスリンは一見優等生だが、裏では様々な陰謀を張りめぐらす悪女。セバスチャンは女を落とすのが趣味のプレイボーイ。彼が校長の娘できまじめな少女アネットを落とせるかどうかで姉弟は賭けをする。だが、アネットに接近したセバスチャンは彼女の純粋さに本気で惹かれてしまい……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH WARRANT", "ブルージーン・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "空手全欧チャンピオンのタイトルを引っさげてスクリーンにデビュー、シュワルツェネッガーに継ぐ1990年代の肉体派ヒーローとして登場したベルギー出身、ヴァン・ダムのアクション快作。刑務所内で起きた連続殺人の謎を解くため、囚人に身をやつして覆面捜査する刑事の活躍を描く。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOU　AND　ME", "真人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アメリカ時代のF・ラング監督が「暗黒街の弾痕」「激怒」に続いて手掛けたハードな人間ドラマ。ニューヨークの百貨店に勤めるジョーとヘレンが結婚。しかし仮出獄中の身であるジョーは、ヘレンも自分と同じ前科者だと知ってショックを受ける。二度と悪事に手を染めないと誓ったジョーだったが、勤務先の百貨店を襲撃する計画を実行に移していく。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Henry's Crime", "フェイク・クライム", "", "", "http://image.eiga.k-img.com/images/movie/56956/315x210.jpg?3893", 108, "キアヌ・リーブスが主演を務めるクライムサスペンス。平凡な人生を歩んでいたヘンリーはふとしたことから銀行強盗の片棒を担がされ、自分だけが刑務所に入れられてしまう。仮出所したヘンリーは舞台女優のジュリーが運転のはねられてしまうが、そのことをきっかけにジュリーが出演する劇場と銀行との間に地下トンネルが掘られていることを知る。ヘンリーは銀行から大金を奪う計画を練り、獄中で知り合った詐欺師マックスが出所すると、一緒に劇団員に紛れ込むが……。ジュリー役に「マイレージ、マイライフ」のベラ・ファーミガ、マックス役に「ゴッドファーザー」のジェームズ・カーン。", "2011-11-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLING ZOE", "キリング・ゾーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "エイズにかかって自暴自棄なプロの犯罪者、エリック。働きながら娼婦の顔も持つ女子美大生、ゾーイ。そして双方から愛されるアメリカ人の金庫破り、ゼッド。パリ祭の日、ある銀行を襲ったエリックとゼッドら７人のギャングたちは、警官隊に取り囲まれて逃げ場を失う。偶然そこで銀行員として働いていたゾーイを人質にして、彼らは逃走をもくろむが……。監督のR・エイヴァリーはQ・タランティーノが無名時代からの盟友であり、その関係からタランティーノが製作総指揮としてクレジットされているが、実際には何もしていないらしい。ビデオ発売時タイトルは「キリング・ゾーイ 破滅への銃弾」。", "1993-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("優駿 ORACI○N", "優駿 ORACI○N", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "1987年吉川英治文学賞を受賞した宮本輝の小説を映画化。北海道の小さな牧場で生まれた１頭の馬。“オラシオン（祈り）”と名付けられたその馬が、競走馬として日本ダービーに出場するに至るまでを描く。監督はTV「北の国から」のフジテレビ・ディレクター杉田成道で、感動的な一編に仕上げた。クライマックスの日本ダービーでは、同年の実際のレース映像が使用されている。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生きていた野良犬", "生きていた野良犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "５年前、兄・太郎の身代わりとして逮捕された武部次郎は、入獄中に何者かの手により太郎が殺されたことを知った。出獄後、兄の仇を討つべく調べを始めた次郎は、かつての太郎の弟分で一家を仕切っている木村と坂崎が犯人という情報を得た。事実を確認すべく木村の邸に向かうのだが……。藤原審爾の『くたばれ』を水の江滝子が企画し映画化した。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月島狂騒", "月島狂騒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 36, "PFFアワードの1994年度グランプリを受賞した佐藤信介による中編。東京は月島の長屋。ガンの疑いで父親が入院してしまった一組の家族の一員それぞれが抱くきわめて個人的で微妙な思惑を、叙情あふれる演出で浮き彫りにしていく上質のヒューマン・ドラマ。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("先生、おなか痛いです", "先生、おなか痛いです", "", "", "http://image.eiga.k-img.com/images/movie/58016/315x210.jpg?2540", 71, "青春と性をテーマに監督たちが独自の個性でオリジナル作品を手がける「青春H」シリーズの通算21作目。監督は、同シリーズ初の女性監督となる「梅田優子の告白」の深井朝子。おっぱいパブで働く美雨は「帰りたい」が口癖で、店に出勤するなり「帰りたい」、家に帰っても「帰りたい」と言ってしまう。そんなある日、学生時代の同級生・涼馬が客と来店。その再会をきっかけに美雨は学生時代の出来事を回想する。もともと幼なじみだった美雨と涼馬だが、美雨のある行為を目撃したことから涼馬は美雨を追いかけまわすようになり……。", "2012-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HISTOIRE D’O", "O嬢の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "フランスのエロチシズム文学の古典を、J・ジャカン監督が映画化。恋人のルネに連れられて“O”が入った館で、彼女は全裸にされ首輪と腕輪をかけられたうえ、犯され鞭打たれる。だが、やがてそれが甘美な快楽に……。", "1975-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽のあたる坂道", "陽のあたる坂道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 214, "石坂洋次郎の原作を田坂具隆監督と石原裕次郎のコンビで映画化した青春映画の大作。上流家庭・田代家の次男は妾の子で、常に自分を非難される立場に置こうとし、幼い頃、長男の不注意から妹に軽いケガをさせた罪をかぶっていた。この家庭に、妹の家庭教師として美しい女子大生が訪れたのを機に、家族は次第に真実の自分をさらけ出していく。「乳母車」で石坂洋次郎のユートピア的な人間関係を淡々と描いて、ある種のリアリティーを持たせた田坂具隆監督が、ここではさらに複雑な人物設定を得て、より深く３時間の長尺をじっくりと見せる。石原裕次郎が「乳母車」に続き巨匠のもとで、のびのびとした演技を披露。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bee Movie", "ビー・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/53056/315x210.jpg?1829", 91, "一度選んだ仕事は死ぬまで変えられないハチの世界に嫌気がさした若きミツバチのバリーは、初めて巣の外の世界に飛び出し、ニューヨークの街へ。危うく人間に潰されそうになったバリーは、その危機から救ってくれた花屋の女性バネッサと友情を育むが……。「シュレック」シリーズのドリームワークス・アニメーション製作。人気コメディアン、ジェリー・サインフェルドがバリーの声のほか、製作・脚本も担当。バネッサ役はレニー・ゼルウィガー。", "2008-01-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Not Without My Daughter", "星の流れる果て", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "夫が突然イランへ帰国したため、政情も不穏なこの国の首都テヘランで暮らすことになった妻と幼子。危険なうえに閉鎖的な因習のはびこるこの国では、子供は夫の持ち物。どうしてもアメリカに帰るなら、かけがえのない一人娘を置いていかなければならなかった。母としての一心な愛だけを武器に、勇気ある国外脱出に挑んだアメリカ女性の実話物語。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージーの大爆発", "クレージーの大爆発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "197X年、日本は世界一の金塊保有国となり経済成長の絶頂をきわめていた。某国の秘密組織GIBが、女スパイX14号を使って大木健太郎をはじめとする７人のプロを味方に引き込み、3000億円の金塊を強奪。しかしGIBに裏切られた７人は逆襲に転じる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("待って居た男", "待って居た男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「昨日消えた男」の続編ともいうべき推理時代劇映画。南伊豆の“柊屋”という温泉場で次々に起こる怪事件を、ちょうどそこに骨休めの遊山旅で泊まっていた文吉とお光の夫婦が、アメリカ映画でいえばさしずめウィリアム・パウエルとマーナ・ロイのコンビばりに事件を解決していく娯楽編。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stranger Than Fiction", "主人公は僕だった", "", "", "http://image.eiga.k-img.com/images/movie/1816/315x210.jpg?4735", 112, "「チョコレート」「ネバーランド」のマーク・フォースター監督が、新人脚本家ザック・ヘイムのオリジナル脚本を映画化。几帳面に毎日同じ生活を送る国税庁の会計監査官ハロルド（ウィル・フェレル）は、ある日、自分の人生がある小説家によって執筆されている物語だと知る。その小説家は、物語の結末で必ず主人公が死ぬ悲劇作家として知られているが……。共演はマギー・ギレンホール、ダスティン・ホフマン、エマ・トンプソンら。", "2007-05-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIMON BIRCH", "サイモン・バーチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「ラブリー・オールドメン」の脚本家M・S・ジョンソンが、J・アーヴィングの小説『オーエンのために祈りを』をもとに手掛けた初監督作。1952年、メイン州の田舎町。生まれつき体が小さい12歳のサイモン・バーチは身長わずか96センチ。彼と、私生児として生まれた親友のジョーは野球をしたり野山を駆け回ったりしていつも一緒に過ごしている。やがて、ある事件をきっかけに、ジョーは実の父親を、サイモンは自分の使命は何かを探し始める。知的で快活な主人公を自然体で演じるI・M・スミスと、繊細な心優しい親友役J・マゼロの好演がさわやかな感動を与える。J・キャリーのカメオ出演も話題となった。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANKIE STARLIGHT", "フランキー・スターライト　世界で一番素敵な恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "第二次世界大戦の混乱時、身重のフランス女性ベルナデットが、アイルランドに流れ着く。生まれた男の子フランキーは、小人症だった。移民管理局のジャックは、彼女に同情し、愛人となり親子の面倒を見る。ジャックは大きな愛で親子を見守ってくれたが、転勤により離れていく。代わって、船の中でベルナデットを見初めたアメリカ人青年テリーが現れ、彼女に告白。ベルナデット親子は、テリーと一緒にアメリカに渡るが、そこにテリーの妻と娘が現れるのだった。A・パリローが、薄幸な女性を健気に演じるほか、共演のM・ディロンとG・バーンが味わい深い演技を見せる愛のドラマ。", "1995-01-01", "3,2,33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジェネラル・ルージュの凱旋", "ジェネラル・ルージュの凱旋", "", "", "http://image.eiga.k-img.com/images/movie/54266/315x210.jpg?1235", 123, "「チーム・バチスタの栄光」に続く海堂尊原作の同名小説を映画化。東城大学医学部付属病院に勤務する田口は、救命救急センター部長で“ジェネラル・ルージュ（血まみれ将軍）”の異名を取る速水に収賄の疑惑ありとの匿名の告発文書を受け取る。院長の依頼を受け、疑惑の真相を巡る調査に乗り出す田口だったが……。前作に続き、主演は竹内結子と阿部寛、監督は中村義洋。物語のキーとなる速水医師に「クライマーズ・ハイ」の堺雅人。", "2009-03-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("∀ガンダムII 月光蝶", "∀ガンダムII 月光蝶", "", "", "http://ecx.images-amazon.com/images/I/51xQMknZ8GL._SL210_.jpg", 128, "ガンダム20周年を記念し、シリーズの生みの親である富野由悠季が新たに手がけた「∀（ターンエー）ガンダム」（1999～2000）の総集編2部作の後編。地球と月の民との争いが続くなか、月では女王ディアナの留守を預かる政治家アグリッパ・メンテナーと軍人ギム・ギンガナムが結託。ギンガナムは最強のモビルスーツ、ターンXを操り、ロランの∀ガンダムに戦いを挑んでくる。そのなかで、∀ガンダムに隠された秘密と強大な力が解放されてしまう。", "2002-02-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("九ノ一金融道", "九ノ一金融道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「ちょうちん」「疵」など硬派の男性映画を撮り続ける梶間俊一監督が女性を主人公に撮りあげた正統派コメディ。十一（といち）と呼ばれ、10日で一割の高金利よりもさらに高い利子で金を融資するその会社は、通称、九ノ一金融と呼ばれていた。が、しかし、すべての金融会社から見放された客に高い利子で金を貸すにはわけがあった……。清水美砂がヒロインの女社長役をコミカルに演じている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRIDE AND THE PASSION", "誇りと情熱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "19世紀、ナポレオンの侵攻に抵抗するゲリラ部隊の戦いを描いた叙事詩スペクタクル。戦いに敗れて撤退するスペイン北方軍が捨てていった強力な大砲をめぐって、ミギュエルという男の率いるゲリラ部隊、イギリス海軍、フランス軍が三つ巴の戦いを繰り広げる。豪華キャストと圧倒的なスペクタクルが見もの。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮本武蔵", "宮本武蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "吉川英治の原作を、内田吐夢が中村錦之助を武蔵に起用して監督した５部作の第１部。関ヶ原の戦いから禅僧・沢庵の教示を得て修行の旅に出発するまでが、内田吐夢のダイナミックな演出で描かれる。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い橋の下のぬるい水", "赤い橋の下のぬるい水", "", "", "http://image.eiga.k-img.com/images/movie/1246/315x210.jpg?3831", 119, "リストラされ妻にも愛想をつかれた笹野は、ある港町にたどりつく。その町の橋のたもとで祖母とふたり和菓子をつくってひっそりと暮らすサエコには「秘密」があった。それは、男性と性的に交わらないと体内に大量の「ぬるい水」がたまり、官能の極みに達するとそれが大量に溢れ出すことだった。原作は辺見庸の同名短編小説。監督は「楢山節考」（83）、「うなぎ」（97）で2度のカンヌ映画祭パルムドール受賞の今村昌平。", "2001-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CELIA", "セリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "オーストラリアの女性監督A・ターナーのデビュー作。メルボルンで実際に起きたウサギ没収事件をもとに、少女のひと夏の幻想的な体験を感性豊かに綴っていく。天才子役とうたわれたR・スマートが、自分の大切なものを一度に失い、それを魔物のしわざだと思い込む少女を熱演している。", "1988-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE HISTOIRE DE VENT", "風の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ドキュメンタリー映画界の巨匠J・イヴェンスが妻マルセリーヌ・ロリダンとともに中国ゴビ砂漠にロケし、４年がかりで完成させた遺作。イベンス本人が砂漠で風を待つ老人として画面に登場するが、風はなかなか吹いてくれない。やがて彼は倒れ、その胸中に夢とも記憶ともつかない心象風景が去来する。フィクションとドキュメンタリーの境界線上に展開された実験性に富む作品である。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("希望の乙女", "希望の乙女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "昭和の歌姫・美空ひばりの芸能生活10周年を記念した豪華な映画。スター誕生の物語を綴るサクセス・ストーリーと、高倉健を相手に紡がれるゴージャスなラブ・ストーリーが一体となった作り。ひばりの実弟・小野透も出演。歌手を目指して田舎から上京した少女は、作曲家の先生を訪ね、近所の商店街の若者たちと楽団を結成した。彼らが幾多の試練を乗り越え成功するまでを描く。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Armored", "アーマード　武装地帯", "", "", "http://image.eiga.k-img.com/images/movie/54006/315x210.jpg?4692", 87, "マット・ディロン、ジャン・レノ、ローレンス・フィッシュバーン、コロンバス・ショート共演の犯罪アクション。輸送警備会社の屈強なドライバーたちが、自社を裏切り、銀行から請け負った現金約4200万ドルの強奪を試みる。だが、思わぬ目撃者の登場に予定が狂い、完全犯罪が悲劇へとなだれ込んでいく……。監督は「モーテル」「プレデターズ」のニムロッド・アーントル。製作にサム・ライミ。", "2010-03-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER FIRE", "アンダー・ファイア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "中南米、ニカラグアを舞台にサンディニスタ解放戦線と、ソモサ独裁政権下の軍隊との衝突に巻き込まれた３人のジャーナリストの姿を描く骨太な人間ドラマ。シチュエーションは「キリング・フィールド」と似ているが、感傷に流されず、きっちりと現実を捉えた秀作。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELL’S ANGELS", "地獄の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "映画製作者としてのみならず、実業家、飛行家としても知られる伝説の人物H・ヒューズの初監督作品。第一次大戦下のロンドンを舞台に、オックスフォード大学出身のエリート空軍隊員と、彼を誘惑するはすっぱ娘との奇妙なメロドラマ。この作品で主役デビューしたJ・ハーローの強烈な個性が見もの。", "1930-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWISTED", "ツイステッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「存在の耐えられない軽さ」の名匠P・カウフマンが殺人事件の容疑者にされた女刑事の孤独な闘いを描く。タフな外見に弱さを隠し持つヒロインの苦悩をA・ジャッドが熱演。サンフランシスコで連続殺人事件が発生。被害者はタフな女刑事ジェシカが関係を持った男たちだった。無実を証明するために真犯人探しに奔走するジェシカだが、次第に彼女の心に抱えた傷や苦しみが露呈されていき……。", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香港クレージー作戦", "香港クレージー作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "とんかつ屋の安吉を始めとする“飲ん平横丁”の連中は、中国人資本による立ち退き問題で大騒ぎ。この横丁に住むC調男は、単独で中国人社長・汪に会い、香港の汪のビルと交換に立ち退きを決める。香港に移住した７人は、日本レストランを開始するが……。香港ロケが効果をあげる。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hot Fuzz", "ホット・ファズ　俺たちスーパーポリスメン！", "", "", "http://image.eiga.k-img.com/images/movie/1776/315x210.jpg?4633", 120, "ジョージ・A・ロメロの「ゾンビ（原題：ドーン・オブ・ザ・デッド）」をパロディ化したホラーコメディ「ショーン・オブ・ザ・デッド」が絶賛され、カルト的人気を誇るイギリスの俊英エドガー・ライト監督が、同作の主演サイモン・ペッグらと再度タッグを組んで作り上げたアクションコメディ。平和な田舎に左遷されたエリート警官エンジェルは、そこで起こった不気味で残虐な事件の捜査を開始。しかし、村人たちは揃って能天気で、どこかおかしく……。", "2008-07-05", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANCERS", "ダンサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "失ってしまったバレエへの情熱を、再び取り戻そうと苦悩する演出家兼ダンサーの物語。そんな彼の前に、17歳のリサが現れ、彼女を愛することにより、熱い思いをよみがえらせていく。当代一のバレエ・ダンサー、M・バリシニコフの魅力がいっぱい。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUEBEARD", "リチャード・バートンの青ひげ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "亡き母の面影を忘れられず、妻を迎えては理想とのギャップを感じて殺す、ということを繰り返していた男爵の伝説を、現代に置き換えて描いた異色作。豪華なスタッフ・キャストが最大の魅力で、特に有名女優陣は、ヌードも辞さない演技合戦を展開している。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("311", "311", "", "", "http://image.eiga.k-img.com/images/movie/57726/315x210.jpg?2044", 94, "東日本大震災発生から2週間後の被災地の様子と、惨状を目の当たりにした4人の映像作家の姿をありのままにとらえたドキュメンタリー。森達也（映画監督・作家）、綿井健陽（映像ジャーナリスト）、松林要樹（映画監督）、安岡卓治（映画プロデューサー）の4人は「震災をその目で確認すること」を目的に、岩手、宮城を縦断。被災地の様子とともに、惨状を目の前に恐怖で高揚してしまう自身たちの姿や、遺族を前にしてもカメラを回すことを止めず批判を浴びる様子など、マスメディアでは扱われない「撮る側の素顔」も映し出す。", "2012-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANCIN’ THRU THE DARK", "ブライダルシャワー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ビートルズの生まれ故郷リバプールを舞台にしたダンサブルな青春グラフィティー。結婚前夜に昔の恋人と再会してしまった女の子リンダの揺れる心をリズミカルに綴っている。英国演劇界で活躍し、「旅する女／シャーリー・バレンタイン」などの原作者としても知られるW・ラッセル原案によるドラマ。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("よさこい旅行", "よさこい旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "坂本竜太と古賀三平は、たった二人の土佐大原駅の駅員。毎日をのんびりと過ごしていたが、ある日、新駅長・山下吉五郎が赴任してきた。山下はだらけた坂本と古賀をビシビシしごきはじめる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("АГОИНЯ／AGONY", "ロマノフ王朝の最期", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 149, "全ソ映画人同盟第一書記として知られるクリモフ監督の歴史超大作。帝政ロシア最後の王朝ロマノフ家のニコライ２世とその一族の最期を、当時の記録フィルムも挿入しながら、重厚かつ壮大に描く。1916年、ロシアは第一次大戦に敗退し、国家経済は破綻。各地で、帝政打倒を唱える民衆暴動が勃発した。対するは政府軍の弾圧と虐殺……。“流血のニコライ”と呼ばれたニコライ２世は実は気弱な趣味人で、重臣たちの献言に圧迫され、皇后アレクサンドルの忠言に思い悩む。皇后の背後で糸を引く者こそ、神がかりの修道僧ラスプーチンであった。農民出身の文盲だが、霊感を持つ彼は、皇后とその取り巻きの女連中を巧みに抱き込み、国家の命運を分けるほどの絶大なる権力を手中に収める。古都ペテルスブルグに保存されている宮殿や貴族の館を使った撮影が、セットでは味わえない現実感を醸し出す。ソビエト屈指の作曲家シニトケの叙情あふれる音楽が、ドラマを盛り上げる。", "1981-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("その人は昔", "その人は昔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "北海道の百人浜で出会った青年と少女。二人は貧しかったが、次第に愛し合うようになっていた。やがて、幸福な生活を求めて東京に出る二人。だが、少女は“あいつ”と呼ばれる金持ちの男に夢中になり、捨てられ、自殺してしまう。ミュージカル仕立ての美しくも哀しい青春映画。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歯科医", "歯科医", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "「花と蛇」「IZO」などで異彩を放つ個性派、遠藤憲一主演によるエロティック・サスペンス。マザーコンプレックスを抱え、母親の死以来インポテンツに陥っていた歯科医、松永。ふとしたきっかけで妻、紀子を殴った彼は性的興奮を味わい、二人の間にはセックスをめぐる危険な絆が生まれる。徐々に行為をエスカレートさせていく松永は、SMプレイを踏み越え、今度は小説を書くことで紀子を凌辱するが……。取り返しのつかない領域に深く沈みこんでいく夫婦の官能を、「コンセント」の中原俊が生々しいタッチで描く。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIVORCING JACK", "ディボーシング・ジャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "北アイルランドの二流タブロイド紙のコラムニスト、ダン・スターキーは頭は切れるが、飲んだくれのダメ男。ある日、一夜限りの不倫相手が殺されるという事件に直面。彼女が死に際に残した“ディボーシング(離婚する)・ジャック”という言葉を頼りに、独自に調査を始めるが……。原作者C・ベイトマン自らが脚色も担当。北アイルランドの揺れる政治情勢を背景に、ダン・スターキーの活躍をコミカル・タッチに描いたポリティカル・サスペンス。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新サラリーマン専科", "新サラリーマン専科", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "三宅裕司演じる平凡なサラリーマン、石橋万作が繰り広げる騒動と人間ドラマを描く喜劇シリーズの第３作。妻、たか子の父・庄助と同居を始めた石橋家は、破天荒な彼に振り回されっぱなし。一方、総務部課長となった万作は取引先のデパートから中身を知らぬままに、 200万円分の商品券が入った袋を受け取ってしまう。賄賂トラブルに巻き込まれた彼は悩んだあげく、意を決して商品券と直訴状を添えて社長への目安箱に入れる。そんな時、家では庄助が失踪するという事件が起きていた。庄助に扮した森繁久彌の怪演が圧倒的。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCROOGE", "クリスマス・キャロル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "ロンドンに住むスクルージは、徹底したケチで思いやりのない老人だった。クリスマス・イヴの夜、７年前に死んだかつての共同経営者マーレイの亡霊が現れ、“今夜、過去、現在そして未来の亡霊が訪れる”と言い残して姿を消すが……。英国の文豪ディケンズの小説を慈愛に満ちたミュージカルに仕立てた大作。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CAMERAMAN", "キートンのカメラマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "喜劇王キートン、MGM時代の幕開けを告げる中編無声コメディ。冴えない街頭写真家バスターは、ある映画会社の受付嬢サリーに“ニュース映画のカメラマンになったら”と言われて、すっかりその気に。ところが、サリーにこっそり聞いた抗争事件を撮ってみたもののまったく写っていない始末で、毎日失敗ばかり。あげくはサリーを恋敵にとられそうなるが、相棒のサルがとても賢い奴で……。", "1928-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAMP", "キャンプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「ヘドウィグ・アンド・アングリーインチ」の製作会社が手掛けた、歌と踊りが満載のキュートで切ない青春群像ドラマ。ミュージカルが大好きなせいで変わり者扱いされる子供たちが、演劇を教えるサマーキャンプに参加。彼らのひと夏の友情と恋模様を劇中劇のミュージカル・シーンを絡めて描く。子役たちが披露するプロ顔負けのパフォーマンスが最高。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pocketful of Miracles", "ポケット一杯の幸福", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "名匠フランク・キャプラ監督が「一日だけの淑女」を自らリメイクした人情コメディ。ニューヨーク暗黒街の大物デイブは、アニーという老婆が売るリンゴが自分に幸運をもたらしてくれると信じていた。ある日アニーのもとに、スペインに留学中の娘が婚約者を連れて帰国するという便りが届く。彼女は娘に、自分は社交界の貴婦人だと偽っていた。その話を聞いたデイブは、いつも世話になっているアニーのためにひと芝居打つことに……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Two Friends", "ルイーズとケリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "「ピアノ・レッスン」の女流監督ジェーン・カンピオン監督が、出世作「エンジェル・アット・マイ・テーブル」以前に撮った長編デビュー作。日本では1997年にロードショー公開された。パンクルックで不良っぽいケリーと、おとなしそうで優等生タイプのルイーズ。思春期を生きる彼女たちの友情と痛みを、時間を逆行させるという斬新な手法で映し出す。まるで他人の日記をひもとくようなドラマの流れの中に、カンピオンの細やかな感性が光る一編。", "1986-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Premonition", "シャッフル", "", "", "http://image.eiga.k-img.com/images/movie/54156/315x210.jpg?2871", 96, "「スピード」「評決のとき」のサンドラ・ブロック主演のスリラー。監督はドイツの新鋭メナン・ヤポ、脚本を「魔法にかけられて」のビル・ケリーが手がける。愛する娘たちと幸せにくらしていたリンダのもとに、夫のジムが交通事故で死亡したという連絡が入る。その翌日、悲しみにくれるリンダの前にジムが何事もなかったかのように現れ……。", "2009-01-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おんな刺客卍", "おんな刺客卍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "幕末の京洛。芸者お京は勤皇派の結び文を持っていたことから、新選組に辱めを受け、結婚も破れた。復讐に燃えるお京は、新選組から受けた傷の上に“卍”の刺青を入れる。そして刺客の修行を積み、あらゆる女の手管を使って新選組の一味を暗殺していく。恐るべき女のリベンジを描いた時代劇アクション。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sabrina", "サブリナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "大富豪のララビー家の長男ライナスは家の財産を守ることにしか興味がない堅物。一方、次男のデビッドは、仕事より女の子に夢中のプレイボーイ。一家のお抱え運転手の娘サブリナは子供の頃からデビッドに片想いをしていたが相手にされず、思いを断ち切るためにパリへ。二年後、美しく成長したサブリナが兄弟の前に現れ……。オードリー・ヘップバーン主演の名作「麗しのサブリナ」(1954)のリメイク。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE JFK ASSASSINATION", "JFK 最後の真実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "忌まわしいケネディ暗殺の真相に迫るドキュメンタリー。オリヴァー・ストーンの「JFK」での主人公、J・ギャリソン元地方検事や、大佐のモデルとなった元空軍大佐など、実在の人物が登場。衝撃的な証言で、ケネディ暗殺事件を克明に再現していく。既成事実を徹底して打破しようとする製作者の意図は、ドラマ以上にドラマティックなダイナミズムを現出することに成功している。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ERLEUCHTUNG　GARANTIERT", "MON-ZEN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「愛され作戦」などで知られるドイツの女流監督D・デリエによるコメディ。人生に疲れたドイツの中年兄弟が、禅の心を求めてニッポンへ。異国の地で二人はヘマを連発し、あっという間に無一文になるが、日本在住のドイツ人女性と知り合い、怪しいパブでバイトを始める。しかし最初の目的は“禅”だったような？　やがて二人はある禅寺にたどりつくが、そこでも未知の世界が待っていた。右往左往する中年男の姿が微笑ましく、ささやかながら愛おしい作品。お寺のシーンは曹洞宗の本山・総持寺で実際に撮影され、同寺の現役住職らが出演している。", "1999-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スイッチを押すとき", "スイッチを押すとき", "", "", "http://image.eiga.k-img.com/images/movie/56916/315x210.jpg?4260", 110, "「リアル鬼ごっこ」の人気作家・山田悠介のもうひとつの代表作で、WEBドラマ化や舞台化もされた同名小説を映画化。2026年、多発する自殺の原因を探るという国家プロジェクトのため、自殺装置のスイッチを持たされた10歳の子どもたちが監禁され、自殺に至る過程が観察されていた。多くの子どもがスイッチを押して命を絶っていくなか、7年間生き続けた6人は、ナゾめいた監視員・南が赴任してきたことにより、生きたいという心が揺り動かされていく……。", "2011-09-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Star Wars: Special Edition", "スター・ウォーズ　特別篇", "", "", "http://ecx.images-amazon.com/images/I/510aww6Ba2L._SL210_.jpg", 129, "ジョージ・ルーカス監督が1977年に発表した傑作SF映画「スター・ウォーズ」の20周年を記念して製作された特別篇。最新デジタル技術によって画質や音響が大幅に改善されたほか、モス・アイズリー宇宙港の景観をはじめとする新たなシーンの追加やCGキャラクターの追加、さらにオリジナル版公開時にカットとなってしまったジャバ・ザ・ハットとハン・ソロのシーンが復活するなど、数々の変更がなされている。", "1998-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TROJAN WOMEN", "トロイアの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "エウリピデスのギリシャ悲劇の本格的な映画化。紀元前416年、10年間の戦いの後、トロイア戦争はギリシャ軍の勝利に終わり、トロイアの男は、ことごとく殺された。生き残った女たちはギリシャに送られ、老いた者には奴隷としての運命が待っていた。時代を超えた戦争の悲劇がリアルに描き出される。４大女優の熱演も見どころ。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Exiled　放・逐", "エグザイル　絆", "", "", "http://image.eiga.k-img.com/images/movie/53986/315x210.jpg?4465", 109, "「エレクション」「PTU」などで知られる香港の巨匠ジョニー・トーが、裏社会に生きる男たちの熱い絆を描いたハードボイルド・アクション。主演は、「ザ・ミッション／非情の掟」でも共演したアンソニー・ウォンとフランシス・ン、「エレクション」のニック・チョンほか。中国への返還間近のマカオ。かつて仲間だった5人の男は、組織を裏切った仲間を殺すよう命じられた者と、男を守る者と真逆の立場に置かれていた……。", "2008-12-06", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREAKY　FRIDAY", "フォーチュン・クッキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1977年のジョディ・フォスター主演作「フリーキー・フライデー」のリメイク。現代的要素を加味しつつ、いつの時代も変わらない母と娘の微妙な関係を、コメディ・タッチでユーモラスに描いている。コメディエンヌとしての才能抜群の母役J・L・カーティスのコミカルな演技も見ものだ。精神科医の母テスと、ロックバンドに夢中の娘アンナは、いつもケンカばかり。この日も中華料理店で口論に。仲裁に入った店のオーナーがくれたクッキーを割ると大地震が！　気付くと二人の体が入れ替わっていた。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL CICLONE", "踊れトスカーナ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "イタリアののどかな田舎町を舞台に、平凡な会計士とフラメンコ・ダンサーのロマンスを描くラブ・ストーリー。トスカーナ地方の農家に、道に迷ったフラメンコ・ダンサーの一行がやって来る。セクシーな美女たちの登場に、会計士の長男レバンテやレズビアンの妹、素人画家の弟は戸惑うばかり。そんな中、レバンテはダンサーのカテリーナに恋をするが、彼女の恋人が現れ……。突然訪れた恋の行方を、素朴で奇妙な人々の人間模様を絡めてユーモアいっぱいに描く快作。陽気な音楽に乗せて抜群のプロポーションのダンサーたちが踊るダンス・シーンにも心が弾む。", "1996-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青い夜霧の挑戦状", "青い夜霧の挑戦状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "“無責任”ものなど、一連の喜劇で知られる古沢憲吾監督のアクション・ドラマ。若杉竜治は、悪の道に染まりそうなところを、果樹園主・仙石に救われて更生する。だが、仙石は権藤組と川北組の利権争いに巻き込まれて殺され、竜治は復讐に乗り出した……。竜治のかつての恋人に扮した水野久美の美しさも見もの。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fire！", "Fire！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "音楽に情熱を注ぐ若者たちの姿を描いた青春ドラマ。メジャー・デビューを夢みるロック・バンドと、女性ボーカルの奮闘の日々を、荒々しくも優しい視線で紡ぎ出した感動作。メインストリートでさえも人影まばらな沖縄・コザ市。龍二、たまき、カズトの３人は成功を夢みてバンドを組んでいるが、客の反応は鈍い。見かねたライブハウスのオーナーが、店で演奏を継続する条件として、女性ボーカルの加入を言い渡す。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Suspect Zero", "サスペクト・ゼロ", "", "", "http://image.eiga.k-img.com/images/movie/1676/315x210.jpg?1474", 99, "死体のまぶたを切り取り、現場に不可解な記号を残す連続猟奇殺人事件が発生。FBI捜査官マッケルウェイの捜査の中で、元FBIの名プロファイラーだった男の存在が浮かび上がる。だが、彼の目的は？　この謎の男役を名優ベン・キングスレーが熱演。マリリン・マンソンなどのクリップ出身、「シャドウ・オブ・ヴァンパイア」のE・エリアス・マーヒッジ監督が独自のダークな映像を展開。音楽は「π」「穴」のクリント・マンセル。", "2005-02-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Inju: The Beast in the Shadow", "陰獣", "", "", "http://image.eiga.k-img.com/images/movie/54736/315x210.jpg?4254", 105, "江戸川乱歩が1928年に発表した同名ミステリー小説を、「バーフライ」「死の接吻」のバーベット・シュローダーが映画化。主演を「ピアニスト」のブノワ・マジメル、ヒロインにパリ在住のモデル・源利華が抜擢された。作家・大江春泥を敬愛するフランスの推理小説家アレックスは、新作のプロモーションのため日本へやって来る。京都を訪れたアレックスは、流暢なフランス語を話す芸妓の玉緒に出会い……。", "2009-08-22", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAUTIFUL　PEOPLE", "ビューティフル・ピープル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ボスニア出身でイギリスに帰化した新人監督J・ディズダーによる、笑いと涙たっぷりの群像ドラマ。いきなり街なかで大ゲンカを始めたセルビア人とクロアチア人、英語は理解できないが純真な心を持つボスニア難民の若者、誤って戦場に空輸されてしまうジャンキー青年など、愛や戦争にまつわる幾つものエピソードがロンドンを舞台に同時進行していく。ボスニア紛争を直接描くことなく、劇中にキーワードとして登場する“LIFE”というテーマをうたい上げたユニークな視点が光る。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BERLIN EXPRESS", "ベルリン特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "パリからベルリンへ向かう特急列車には、各国の要人がある任務のために乗り合わせていたが、車内で殺人事件が発生。調査のため急停車したフランクフルトの町で、乗客の一人ベルンハルト博士が行方不明になった……。大戦直後のドイツを舞台に陰謀渦巻くサスペンスを、名匠J・ターナーが手掛けた佳作。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Evolution", "エボリューション", "", "", "http://image.eiga.k-img.com/images/movie/1386/315x210.jpg?4779", 103, "アリゾナ砂漠の真ん中に隕石が落下。そこには地球の生命体が46億年かかった進化を、たった1カ月で成し遂げる未知の生命体が付着していた。地元の生物学者、消防隊員、FBIらがそれぞれの思惑で現地に殺到、大混乱に。監督は「ゴーストバスターズ」（84）のアイバン・ライトマン。奇妙な生物たちのVFXスーパーバイザーは「ジュラシック・パーク」などの大御所フィル・ティペット。「ハンニバル」のジュリアン・ムーア、「アメリカン・パイ」のショーン・ウィリアム・スコットらが共演。", "2001-11-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rece Do Gory", "手を挙げろ！", "", "", "http://image.eiga.k-img.com/images/movie/55006/315x210.jpg?4652", 83, "ポーランドの巨匠イエジー・スコリモフスキ監督が1967年に発表した長編第5作で、スコリモフスキ自身が主人公の青年を演じるアンジェイ3部作の最終章。医師になったアンジェイが、列車の中で共産主義について考察する様子を描く。本作がポーランドでの公開当初にスターリン批判の作品とみなされて上映禁止処分を受けたことから、これ以降スコリモフスキは祖国を離れて制作活動を続けることとなった。", "2009-10-20", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄の掟に明日はない", "地獄の掟に明日はない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "山崎組代貸の滝田は、権藤組が組んだ八百長競艇をやめさせたため、命を狙われる羽目になる。滝田は、巧みに権藤を暗殺し、裏切り者も始末して足を洗おうとするが……。降旗＆高倉コンビの初顔合わせ。長崎で原爆の洗礼を受け、いつ死ぬか分からないという健さんのキャラクターがすごい。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WIZARD", "スウィート・ロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "自閉症で施設に入れられた弟を連れ出し、カリフォルニアへ向かう少年フレッド。弟のTVゲームの才能を駆使して小銭を稼ぎながら、兄弟は奔放な少女と道中をともにする。やがて３人は、TVゲームの全国大会が開催されることを知る……。子供たちのファンタスティックな旅路を描いたロード・ムービー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拳銃無頼帖 抜き射ちの竜", "拳銃無頼帖 抜き射ちの竜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "決して相手を殺さず、その肩を射ち抜いて利き腕を失わせることで有名な“抜き射ちの竜”こと剣持竜二。彼は麻薬密輸団のボスの用心棒になり同業者、コルトの銀と対決する。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LET’S　DO　IT　AGAIN", "一発大逆転", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ペテン師二人組がギャングをカモにする、コンゲーム感覚の痛快コメディ。監督兼俳優のS・ポワチエが大活躍する。また、C・メイフィールドが手掛けたファンキー・グルーヴあふれる主題歌が、ドラマを一層盛り上げる。教会の礼拝所の建設資金を捻出するため、ある策略を立てるペテン師コンビ。彼らは催眠術を使って弱いボクサーを凄腕に仕立て上げ、大儲けに成功する。しかし、儲け損なったギャングが彼らの前に現れ……。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paid to Love", "雲晴れて愛は輝く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "地中海の小国の皇太子が女にまるで興味がないことを心配した父王とアメリカ人銀行家が、パリで怪しげな女を雇い、皇太子にあてがおうとするうちに、いつしか若い二人は本当の恋におちてしまい…。ホークス映画の常連ライター、S・I・ミラーが初めてホークスと組んだ作品。", "1927-12-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("家光と彦左と一心太助", "家光と彦左と一心太助", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "江戸の名物男・一心太助と次期将軍・徳川家光は身分こそ天と地なれど、顔立ちは瓜二つ。弟・忠長を偏愛する北の方による家光暗殺計画が露顕し、二人は彦左の入れ知恵で入れ替わり、互いにトンチンカンな騒動を引き起こす。彦左役をシリーズ５作中、本作のみ月形龍之介にかわり進藤英太郎が演じている。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄男", "鉄男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "「電柱小僧の冒険」でPFFアワード1988グランプリを獲得した塚本晋也が、一人で７役をこなして作り上げた異色作。鉄に侵食されていく男の姿を圧倒的なパワーで綴っている。ストーリー性の希薄さは否めないが、映像の迫力は特筆に値する。ローマ国際ファンタスティック映画祭グランプリ。DVDタイトルは「鉄男 TETSUO THE IRON MAN」。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あぜみちジャンピンッ！", "あぜみちジャンピンッ！", "", "", "http://image.eiga.k-img.com/images/movie/55946/315x210.jpg?2461", 85, "ある地方都市に暮らす女子高生・優紀は、耳が聞こえないというハンディキャップを抱えながらも、ダンスチーム「Jumping girls」に加わる。チームのキャプテン・麗奈とは言葉を交わせないが良きライバルとなり、夏のダンス大会を目指す。しかし大会の直前、麗奈は足を負傷してしまい欠場が決定。優紀が麗奈の代理を務めることになるが……。耳が不自由な少女が、ダンスを通じて同年代の少女たちと友情を築いていく姿を描く青春ストーリー。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無警察", "無警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "“無警察”と呼ばれるある地方都市。町のドン、松崎とキャバレーで会見した市会議員、今泉は、松崎からゴルフ場建設を持ちかけられる。市民の生活を考えた今泉は申し出を拒絶するが、その直後、彼は何者かによって殺害されてしまった。事件を不審に思った新聞記者の浩一は、独自の調査を進めていくが、その背後には複雑な陰謀が絡み合っていた。ある意味で無法地帯の場所に展開するサスペンス・ミステリーの快作。主演は新東宝の看板スター、天知茂。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD　CARD", "ワイルド・カード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "韓国で同時期に公開された傑作サスペンス「殺人の追憶」と共に大ヒットした、ヒューマニズムあふれる刑事アクション・ドラマ。殺人課の刑事たち６人の日常と殺人事件の真相を丹念に描き出す。ラッパー兼俳優のヤン・ドングンが正義感に満ちた刑事役を好演。地下鉄の駅で女性の無惨な死体が発見され、３人の刑事が現場へ急行した。若手の刑事は犯人逮捕を固く決意し、厳しい捜査の中へ飛び込むが……。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARKED WOMAN", "札つき女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "N.Y.のナイトクラブを舞台に、大都会の裏世界を描く法廷ドラマ。財を築くためには手段を選ばないギャングのバニングが所有するクラブで客が殺される。店で働く５人の女性が殺人の容疑で拘束されるが、すべてはバニングの仕組んだワナだった。実在の検事とギャング、ラッキー・ルチアーノをモデルにしたリアリズムあふれる作品。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀河鉄道999", "銀河鉄道999", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "宮沢賢治の『銀河鉄道の夜』をヒントにした松本零士の同名のヒット作。そのアニメ化劇場版第１作。銀河鉄道999号が発着する、地球のメガロポリス。そのスラム化したダウンタウンに生きる15歳の少年・星野鉄郎は、母親を殺した機械人間たちを操る、機械伯爵を倒したいと願っていた。そのためにはアンドロメダに行き、機械の体を得なければならない。鉄郎は謎の美女・メーテルから贈られたパスで、彼女とともに999号に乗り込み、念願のアンドロメダへと出発する。999号が立ち寄る星々で、様々な試練を経てついに機械伯爵を倒したあと、鉄郎はメーテルの意外な秘密を知るのだった。りんたろう、小松原一男、椋尾篁といった演出・作画・美術の実力派スタッフを起用。壮大な宇宙の広がりを実感させる幻想的なイメージの表現とともに、少年の成長の旅を描ききった傑作といえる。1979年度邦画配収第１位。ゴダイゴによる主題歌も、ミリオンセラーの大ヒットとなった。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love Phobia", "とかげの可愛い嘘", "", "", "http://image.eiga.k-img.com/images/movie/33776/315x210.jpg?4757", 117, "「マラソン」のチョ・スンウと「トンマッコルへようこそ」のカン・ヘジョン共演によるラブ・ストーリー。嘘ばかりついている不思議な女の子アリと、幼い頃からずっと彼女を思い続ける青年ジョガンの20年間に渡る純愛を描く。ジョガンのクラスに転校してきたアリは、いつも黄色のレインコートを着て、ポケットにトカゲを忍ばせている変わった女の子。ジョガンはそんなアリに恋心を抱くが、彼女はある日突然姿をくらましてしまう。", "2006-12-16", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEVEN BRIDES FOR SEVEN BROTHERS", "掠奪された七人の花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1850年代。オレゴンの山奥に住むポンティピー家７人兄弟の長男が、町から花嫁を迎えたことから弟６人も兄にならえと町へ出かけ、娘たちを掠奪してくる……。シネスコ画面いっぱいにマイケル・キッド振り付けの躍動的な群舞が映える、MGMミュージカルの傑作。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LONG GRAY LINE", "長い灰色の線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "J・フォードが初めてシネマスコープに取り組んだ作品。ウエストポイント陸軍士官学校を舞台に、そこに50年勤め、ともに歩み続けた男の涙と喜びに満ちた栄光への軌跡を感動的に描いている。この役をT・パワーが好演し、妻役のM・オハラの優しさや、フォード得意のアイリッシュ気質も見事に表現されている。DVDは「コロムビアトライスター ウォー・ムービー・コレクションBOX(3)」に収録。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KANGAROO JACK", "カンガルージャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ヒットメイカー、ジェリー・ブラッカイマーが贈るファミリー・コメディ。人間サマを徹底的にコケにしてしまう、ファンキーなカンガルーが大活躍する。N.Y.に住むサエない若者チャーリーとルイスは、大金輸送をマフィアに任されオーストラリアへ飛ぶ。ところが、この大金をいたずら好きなカンガルーに持ち逃げされてしまう。二人は異国の荒野で、必死にカンガルーを追いかけるのだが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fight Club", "ファイト・クラブ", "", "", "http://ecx.images-amazon.com/images/I/51RyrB291mL._SL210_.jpg", 139, "心の中に問題を抱えるエグゼクティブ青年ジャックはタイラーと名乗る男と知り合う。ふとしたことからタイラーとジャックが殴り合いを始めると、そこには多くの見物人が。その後、タイラーは酒場の地下でファイト・クラブなる拳闘の秘密集会を仕切ることに。たくさんの男たちがスリルを求めて集まるようになるが、やがてそのクラブは恐るべきテロ集団へと変貌していく……。「セブン」のコンビ、ブラピとフィンチャー監督が再び組んだ衝撃作。", "1999-12-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE JANUARY MAN", "乙女座殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "大晦日の深夜、ホロ酔い気分で帰宅した女性が絞殺された。ニューヨークっ子を震え上がらせる、連続殺人事件の11人目の犠牲者だ。特別捜査を任された刑事ニックは、一見理由なき殺人事件の意外な関連性を発見する。P・オコナーがハリウッド・デビューを果たした、粋なラブ・サスペンス。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MALLI", "マッリの種", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1991年に起きた少女の自爆テロによるラジブ・ガンジー首相暗殺事件に基づいて作られた作品。1998年のカイロ映画祭で上映され、衝撃を受けた審査委員長ジョン・マルコヴィッチが自らパトロンとなってアメリカ公開に結び付けた。インド南部のゲリラキャンプで育った19歳の少女マッリは組織の命令に従い、自らの命を犠牲にすることを当然と思っていた。そして、ある日、彼女はVIPを暗殺するための“人間爆弾”に志願する。任務の実行場所に移動するマッリは、家族を失い地雷におびえる少年や、寝たきりの妻の面倒を見ながら暮らす老人など、普通の人々と出会い、今まで経験したことのない感情に襲われていくのだった……。ビデオ、DVDタイトルは「ザ・テロリスト　少女戦士マッリ」。", "1998-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMMENT JE ME SUIS DISPUTE´…（MA VIE SEXUELLE）", "そして僕は恋をする", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 178, "「魂を救え！」に続くA・デプレシャン監督の長編第２作にして、日本初登場作。３人の女性の間を、きわめつけの優柔不断ぶりでフラフラと揺れ動く29歳の男心を、饒舌に語り切った傑作だ。モノローグや長めのセリフが頻出するが、騒々しさや退屈は一切なし。澄んだ映像は決して弛緩することなく、３時間近くの長尺をよどみなく的確に紡いでいく。舞台は大学周辺、主人公は講師という設定だが鼻につくインテリ臭も漂わず、むしろトレンディー・ドラマさえ想起させる軽快なタッチ。監督の意をくんだ常連俳優たちの好演もあり、複雑にしてシンプルな味わいの、かつてなかったタイプの恋愛映画に仕上がっている。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("番頭はんと丁稚どん", "番頭はんと丁稚どん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "もとは花登筐原作による同名の連続TV番組で、その丁稚・崑松を演じた大村崑のトンチンカンな様とトボけた感じが茶の間を沸かせた。脇を固めるのは芦屋３兄弟、浪花千栄子、森川信、若水ヤエコなどの喜劇陣が中心になっている。物語は大阪道修町の薬種問屋“七ふく堂”を舞台に、田舎から丁稚奉公に出て来た少々頭の弱い少年・崑松が、七ふく堂の娘との色恋沙汰に巻き込まれ、ドタバタを展開するというもの。その娘を別府まで追いかけていった崑松が折から公開中の「番頭はんと丁稚どん」の舞台へ上がり、その娘の名を呼ぶという楽屋落ちなども用意されている。このシリーズ第１作のみ酒井欣也が演出を担当している。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キッドナップ・ブルース", "キッドナップ・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ジャズ・ミュージシャンの中年男と鍵っ子の女の子が日本列島を旅し続ける姿を淡々と撮った、浅井慎平のフォト・メルヘン。ストーリー性や劇的効果をいっさい排除した作品で、全編に無機的な雰囲気が漂う。山下洋輔が音を手掛け、出演もしている。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲゲゲの鬼太郎　鬼太郎の幽霊電車3D", "ゲゲゲの鬼太郎　鬼太郎の幽霊電車3D", "", "", "http://image.eiga.k-img.com/images/movie/54806/315x210.jpg?2766", 0, "1999年に製作された「ゲゲゲの鬼太郎」シリーズ初の3Dアニメーション。テストで赤点を取ってしまった三太郎は、時間を戻してテストをやり直したいと思っていた。すると三太郎のもとにねずみ男が現れ、「願いを書いた手紙を鬼太郎に出せ」と助言する。ねずみ男の言葉通り、鬼太郎手紙を出した三太郎は、過去を旅する幽霊電車に乗ることになる。短編3Dアニメーションを集めた「とびだす！3D東映アニメ祭り」で上映。", "2009-10-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ILE MYSTE´RIEUSE", "神秘の島 地底人間の謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "SFの古典的名作J・ヴェルヌの『海底二万哩』の後日譚『神秘の島』の映画化。前作で潜水艦ノーチラス号に乗ったまま北極海の渦の中に消えたネモ艦長は、実は大西洋の孤島で暮らしていた。そこへ南北戦争下の南軍から気球で脱出した５人の人々がやって来る。一行はやがて海底世界を目撃する。", "1973-01-01", "4,2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("越後つついし親不知", "越後つついし親不知", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "原作は『雁の寺』以降発表される作品が次々に映画化された水上勉の同名の小説で、監督の今井は持ち前のリアリズムで名をなしたわけだが、ここではむしろロマンティックに題材を扱っている。一見すると二人の男の確執の物語のようだが、その実、おしんという女の幸少ない人生が情感たっぷりに描かれた。冬の間、主人公が伏見の造酒屋へ出かせぎで働きに出ている時に同郷者の仲間に妻を犯され、その結果、妻が身ごもってしまうところから起こる悲劇を、神々しいばかりに美しい佐久間良子、お人好しの小沢昭一、憎々しい三國連太郎らが見事に演じきる力作である。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASSANDRA", "カサンドラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "夜ごと少女を苦しめる悪夢が、次第に現実のものとなって彼女の周囲の人々を襲った。その謎を追ううち、少女の呪われた出生の秘密が明らかになっていくサイコ・スリラー。", "1987-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聖闘士星矢 天界編 序奏～overture～", "聖闘士星矢 天界編 序奏～overture～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "『週刊少年ジャンプ』に掲載され、漫画のみならずアニメーション作品としても絶大な人気を誇った作品の劇場版。2003年漫画家生活30周年を迎えた作者、車田正美が“ハーデス編”の続編としてあたためていた幻のエピソード“天界編”をアニメ化した話題作だ。15年ぶりに蘇った聖なる闘士、星矢のアグレッシブな活躍が、躍動感みなぎる演出で描かれる。冥王ハーデスとの死闘によって力を失った星矢のもとに、アテナの姉のアルテミスが天闘士を送り込んだ。抜け殻になってしまっている星矢は為す術もなかったのだが、彼を守るべくアテナが現れて地上世界と交換で命を救うが……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Grown Ups", "アダルトボーイズ青春白書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "アダム・サンドラー、ロブ・シュナイダー、クリス・ロック、デビッド・スペード共演のファミリーコメディ。子供時代のバスケットボールのコーチが亡くなり、30年ぶりに再開を果たしたかつての親友5人組。彼らは独立記念日の連休にそれぞれの家族を連れて、郊外の湖畔の一軒家で過ごすことになるが、そこで次々と騒動が巻き起こる……。監督は「ビッグ・ダディ」「エージェント・ゾーハン」などサンドラーとのコンビ作で知られるデニス・デューガン。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Beach", "ザ・ビーチ", "", "", "http://ecx.images-amazon.com/images/I/51rI%2B36fgjL._SL210_.jpg", 119, "「トレインスポッティング」「普通じゃない」の監督＆脚本家が、イギリスの新進作家A・ガーランドのベストセラー小説を映画化したシニカルな青春ドラマ。刺激を求めてバンコクにやって来たアメリカ人青年リチャードが、手に入れた地図を便りに秘密の島にたどり着く。“ビーチ”と呼ばれるその美しい孤島では、様々な国の人々がコミュニティーを形成し、自由を謳歌していた。リチャードは快く受け入れられ、社会と断絶した島で解放的な気分を味わうが、次第にコミュニティー内は嫉妬や憎悪、秘密を守ろうとする異常な執着によって対立が激化。不穏な空気は、やがて思いがけない事態を引き起こす。退屈な日常から抜け出そうとした好奇心から内なる凶暴性に目覚めていく主人公をL・ディカプリオが鋭いまなざしで熱演。青い海と白い砂浜に囲まれた楽園のような景色と人間のエゴを暴く辛口のドラマ、そのミスマッチ感に異様な緊張感が漂う。", "2000-04-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OLIVER！", "オリバー！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "チャールズ・ディケンズの孤児物語『オリバー・ツイスト』をライオネル・バートが舞台ミュージカル化したものを巨匠C・リードが映画化。“最後の華麗なるミュージカル映画”ともいわれるテクニカラー大作。レスター坊やとJ・ワイルドが好演。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("百万両五十三次", "百万両五十三次", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "徳川治世300年、世に勤皇の火の手があがりはじめた頃、幕府は３万両の金を京都に送り、討幕の企てを阻止する計画を立てる。だがそれをかぎつけた薩摩藩士やその手先の女スリお蓮、怪盗の牛若の金五らが虎視眈眈とこれを狙う。老中は、芝居小屋の用心棒で実は公儀隠し目付の馬場蔵人にこの大金輸送を申しつける。大金争奪をめぐって繰り広げられるロード・ムービー的時代劇。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El Truco Del Manco", "クアホ、逆手のトリック", "", "", "http://image.eiga.k-img.com/images/movie/54796/315x210.jpg?4348", 87, "バルセロナを舞台に、身体の不自由な青年エル・クアホがラッパーを目指して奮闘する姿を描いた青春ドラマ。主人公を演じたのはスペインの人気ヒップホップ・グループ「La Excepcion」のメンバーとして活躍し、自らも手足に障害を持つラッパーのエル・ランギで、本作でゴヤ賞新人男優賞を獲得。また本作が長編監督デビューとなるサンティアゴ・A・サンノウも同新人監督賞を受賞している。", "2009-09-18", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おこげOKOGE", "おこげOKOGE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "“おこげ”とは、“おかま”にくっつく女性、すなわちゲイの男性に惹かれる女の子のこと。男性恐怖症の女、小夜子がホモセクシャルの人々と接したことから、彼らの愛すべき姿に惹かれていく様が描かれる。脚本家、中島丈博の「郷愁」に続く監督第２作。清水美砂が主人公に扮し、ヌードも披露。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガメラ対大悪獣ギロン", "ガメラ対大悪獣ギロン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "冒険好きな二人の少年が、宇宙船に閉じ込められ第十惑星に連れ去られ、宇宙人に食べられそうになる。そこにガメラが現れて二人を救う。宇宙人はギロンをけしかけるが、ガメラの敵ではなかった。第３作に登場したギャオスが再登場するが、ギロンにあっさりやられてしまう。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PATCH ADAMS", "パッチ・アダムス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "R・ウィリアムズが得意の演技で笑わせて泣かせる感動編。彼が演じるのは実在の医師パッチ・アダムス。1969年、アダムスは自殺未遂の果てに自分の意思で精神科に入院。だが、ここで彼は医学の道を志すことを決意し、二年後ヴァージニア大学の医学部に入学する。患者に接することを望み、規則を無視して病院に出向いては、病に苦しむ人々をパフォーマンスで楽しませるパッチ。医者は特別な存在と決めつける学部長との対立や、同級生カリンとの恋を経験しながら、学生でありながら夢見ていた無料治療院を開設するが……。パッチのバイタリティーと優しさあふれる生き方を描くとともに、現代医療の問題点をさりげなく突いたヒューマン・ドラマ。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARAVAN OF COURAGE ： AN EWOK ADVENTURE", "イウォーク・アドベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ルーカスの生んだ「スター・ウォーズ」シリーズの番外エピソード。緑の星エンドアに地球の宇宙船が不時着、乗っていた家族のうち両親が怪物にさらわれる。残された幼い兄妹は、小熊に似たイウォーク族の助けを借りて、怪物退治の冒険に旅立つことになった……。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN WITHOUT A FACE", "顔のない天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "M・ギブソンが初めてメガホンを執った感動的なヒューマン・ドラマ。1960年代のメイン州にある海辺の町を舞台に、自動車事故で顔に大やけどを負い、人目を避けるように暮らしている元教師と、彼に個人教授を請う少年の友情と別れをドラマティックに描いていく。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代性犯罪絶叫篇　理由なき暴行", "現代性犯罪絶叫篇　理由なき暴行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "若松監督の初期の作品はピンク映画タッチだと目されていたが、70年代の“にっかつロマンポルノ”の大攻勢を前にして、本作はピンク映画ジャンルでいかに作家性を示すかを提示してみせた渾身作だ。大都会をバックに、下層階級と見なされる若者たちの破壊的な衝動を描いている。DVDは「理由なき暴行」のタイトルに変更され、「若松孝二　初期傑作選DVD-BOX3」に収録。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bandits", "バンディッツ", "", "", "http://image.eiga.k-img.com/images/movie/1276/315x210.jpg?2841", 124, "ウィリス、ソーントン、ブランシェットと、ノッてる俳優3人が競演。ウィリスの娘2人も出演。監督は「レインマン」でオスカー受賞のバリー・レビンソン。撮影は「L.A.コンフィデンシャル」のスピノッティ、脚本は「レス・ザン・ゼロ」のペイトンが務める。州立刑務所で出会ったジョーとテリーは、正反対の性格なのに意気投合、協力して脱獄に成功する。が、逃亡中に出会った平凡な人妻ケイトがふたりを好きになり、妙な展開に。", "2001-12-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たまたま", "たまたま", "", "", "http://image.eiga.k-img.com/images/movie/57106/315x210.jpg?2306", 53, "オールアイルランドロケで撮影された蒼井優主演作。たまたま＝偶然の出会いや人と人とのつながりによって生まれる日常の小さな奇跡や希望を、アイルランドの美しい風景にのせて描く。300本以上のCMやミュージックビデオ、ショートムービーを手がけてきた映像クリエイターの小松真弓が原案・脚本を手がけ、監督デビュー作として撮り上げた。", "2011-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOLLOW POINT", "バッドマックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "軽妙なユーモアとハードな見せ場が融合した痛快アクション。ロシアン・マフィアを追う女性FBI捜査官ダイアンと元麻薬捜査官マックスが、互いの利益のために渋々手を組んだ。マックスはマフィアの大金を横取りし、その金で捜査官に復職しようとしていた。彼らは任務に失敗した殺し屋を利用して、組織の壊滅を図るが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WITHOUT　A　PADDLE", "トレジャー・ハンターズ／　進め！笑撃冒険王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "全米で６週連続トップ10入りのロングヒットを記録した爆笑アドベンチャー。今の生活に満たされない都会で暮らす30歳の男が３人揃って宝探しの旅へ。子供みたいな大人たちの珍道中に抱腹絶倒。「マトリックス」「インディ・ジョーンズ」などの名作パロディが、さらに笑いを誘う。幼なじみのダン、ジェリー、トムは親友の葬式で再会。大人になっても本気で財宝を探していた彼の後を継ぎ、３人は宝探しの旅へ。", "2005-11-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄金の法 エル・カンターレの歴史観", "黄金の法 エル・カンターレの歴史観", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "25世紀の未来から神話の時代までを、タイムトラベルで駆け抜ける壮大なスケールの冒険アニメーション。タイムマシンに乗った少年少女が、時の激流に押し流され、いろんな時代で歴史上の偉大な人物に次々と遭遇していく。偉人たちが引き起こした数々の奇跡を体験する少年少女たちの成長過程は見逃せない。人類史の秘密を明かした書『黄金の法』を読んでいたサトルの元に、謎の少女アリサが出現。彼女が乗ってきたタイムマシンに同乗したサトルは、ギリシャ神話ヘルメスやキリストなどの歴史上の偉人たちに遭遇する時間旅行に出る。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひぐらしのなく頃に", "ひぐらしのなく頃に", "", "", "http://image.eiga.k-img.com/images/movie/53406/315x210.jpg?3796", 0, "昭和58年初夏。かつて鬼が住んでいたといわれる雛見沢村に東京から引っ越してきた圭一は、クラスメイトたちとのどかな日々を送っていた。しかし村では最近、毎年夏祭りの日に1人が死に、1人が消えるという怪事件が発生しており、そして今年も夏祭りの日がやってきた……。原作者の竜騎士07がコミックマーケットで発表した後、口コミで人気が広がり、コミック、アニメなど様々なメディアに展開したサウンドノベルの実写映画化。", "2008-05-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サヨンの鐘", "サヨンの鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "第二次大戦下、満州映画協会と台湾総督府の全面的協力のもと、李香蘭（現・山口淑子）主演により製作された作品。満州の高砂族の住む蕃社に、明るくて誰からも好かれる娘、サヨンが住んでいた。内地から帰って来た婚約者サブロとともに楽しい日々を過ごしていたが、やがて義勇軍の召集が……。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("呪戒／JUKAI", "呪戒／JUKAI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "すほうれいこ、金子昇らの若手キャスト共演によるミステリー・ホラー。一人の女子大生の自殺をキッカケに、同級生たちを次々と襲う死の恐怖。生前関わった人間を6人殺すと死んだ人間が甦る、という恐ろしい\"呪戒\"の儀式を巡って、復讐心と憎悪に彩られた驚愕の真実が明らかになる。同級生の夏樹の悪夢で目覚めた小夜。すると次の日、学校で夏樹が自殺してしまう。残された彼女のパソコンには“呪戒”の儀式が打ち込まれていた。", "2005-02-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明月照尖東／ WITH OR WITHOUT YOU", "新・愛と復讐の挽歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "青年刑事と、暗黒街の冷酷なヒットマン・プリンスの二人が、同時に妖艶なコンパニオンに恋をする。やがて二人は避けられない死闘に火花を散らす。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠くはなれて", "遠くはなれて", "", "", "http://image.eiga.k-img.com/images/movie/57436/315x210.jpg?2448", 15, "無名の若手監督たちが作り上げた15分の短編10作品を特集上映する「NO NAME FILMS」の1本。無気力で行き当たりばったりな生活を続ける藤川は、ある日、同僚と口論になり、車から無理やり降ろされてしまう。知らない街に置き去りになった藤川は、そこで1匹のヤギと出会うが、そのヤギはとある研究機関のもので……。監督は、「世界グッドモーニング!!」（2009）が第32回ぴあフィルムフェスティバルで審査員特別賞を受賞した廣原暁。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFGHANISTAN DAYS OF HILL", "アフガニスタン・地獄の日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "戦乱が続くアフガニスタンを襲う毒ガス兵器。その生き証人を救うべく組織されたコマンド・チームが出動。だがその裏には陰謀が……。3000人の軍隊に挑む勇敢な男たちを描いたアクション映画。", "1986-01-01", "4,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING’S WHORE", "仮面の愛 マスカレード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "17世紀後半、サヴォイア公国。孤独にさいなまれていた王ヴィットリオは、ある日、拝謁に来た伯爵夫人ジャンヌの清らかさに魅了される。愛する夫からむりやり引き離し、彼女を自分のものにする王。ふがいない夫と横暴な君主への復讐を決意したジャンヌは、宮廷を意のままに操るが……。歴史に隠れた宿命の恋をゴージャスなムードで描いた大河ロマン。ビデオ発売時タイトルは「マスカレード 仮面の愛」。", "1990-01-01", "3,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AVVENTURIERO", "残虐の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "18世紀後半のフランスのツーロン港を舞台に、海に生きる荒くれ男の情熱的な生涯を描いた海洋冒険活劇。ラブ・シーンあり、スペクタクルありのストーリーで、風光明媚なロケーションを利用したエンターテインメントが堪能できる。「ロード・ジム」「地獄の黙示録」の原作者、J・コンラッドの小説を映画化したもの。", "1967-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NESTO IZMEDU", "３人でスプリッツァ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "東西文化のまじわる地として様々な歴史を持つユーゴ。この国を舞台にニューヨークの女性ジャーナリストが、性格の異なる二人の男性の間で揺れる姿を描く。原題は“どっちつかず”の意で、主人公の心理とユーゴの国情を象徴している。", "1982-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DERNIER DOMICILE CONNU", "最後のアドレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ギャングが口封じを狙っている重要な証人を捜し出し、保護した敏腕刑事マルソー。だが警察組織は証人を法廷に送り込むと同時に彼を放り出す。深いうらみを持った凶悪なギャングは、証人とマルソーを殺そうと襲いかかった。「冒険者たち」の原作者で、「暗黒街のふたり」などの監督としても知られるJ・ジョヴァンニが、元ギャングという特異な経験を生かし、警察機構の矛盾を批判した意欲作。L・ヴァンチュラのほか、M・ジョベールらの魅力あふれる演技によって描かれたフィルム・ノワールの名編。", "1969-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間", "人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "真夏。船長の亀五郎、船頭の八蔵、海女の五郎助ら４人を乗せて小さな荷役船・海神丸は船出した。たった２日の旅には十分の食料を積んで。しかし海神丸は、嵐に巻き込まれ、難破。太陽が照りつけるなか、あてもない漂流生活が始まる。やがて水は底をつき、乗組員たちは、自分の命を守るため互いに敵意を抱き、果ては精神に異常をきたしはじめるのだった……。野上弥生子の『海神丸』を新藤兼人が脚色、監督した人間ドラマの秀作。食料はおろか水までも失ってしまった難破船という極限状態のなかで、次第に狂い始めていく人間たちの姿を新藤兼人は冷徹な視点で淡々と描写、息づまる作品に仕上げている。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RONIN POP", "RONIN POP", "", "", "http://image.eiga.k-img.com/images/movie/56616/315x210.jpg?4705", 82, "韓国の9人組男性アイドルグループ「ZE:A」が初主演を務めた近未来アクション。世界が核戦争により荒廃し、暴力で権力が争われる弱肉強食の時代が到来。少女アニタの住む街でも、「マットドラゴン」と「フライングスネーク」という2大勢力の抗争が激化していた。アニタの兄リーや親友ジャッキーも戦いに巻き込まれていく中、トニーと名乗る流れ者に出会ったアニタは、トニーの剣の実力を知り、リーやジャッキーを助けてほしいと頼むが……。", "2011-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あなたと私の合言葉　さようなら、今日は", "あなたと私の合言葉　さようなら、今日は", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "市川崑が、久里子亭のペンネームで『平凡』に連載していた小編を、自ら映画化した恋愛喜劇。OLの和子は大阪に半次郎という恋人がいたが、父や妹の面倒を見ているため結婚に踏み切れず、大阪に住む友人・梅子に半次郎に会って結婚を断ってくれと頼む。ところが、何とその梅子が彼に惚れてしまい……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sleepy Hollow", "スリーピー・ホロウ", "", "", "http://image.eiga.k-img.com/images/movie/1856/315x210.jpg?1358", 98, "1799年、ニューヨーク。市警の捜査官イカボッドは身の毛もよだつ事件の捜査に向かう馬車に揺られていた。行き先は郊外の村“スリーピー・ホロウ”。異様な雰囲気が漂うこの村で、人々を恐怖に陥れている“首なし”連続殺人事件が起きていたのだ。イカボッドは村に着くなり長老たちに呼び出され、この殺人事件が“首なし騎士”——かつて独立戦争の最中この村で殺されたドイツ人騎士の幽霊——によるものだと聞かされる。", "2000-02-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("童貞物語４　ボクもスキーに連れてって", "童貞物語４　ボクもスキーに連れてって", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「櫻の園」の脚本家、じんのひろあきがシナリオを提供したシリーズ中でも異色の１本。主人公は、アルバイト仲間の高校生カップル３組。あるOLの誘惑をきっかけに、そのうち二組の男女が、互いの相手を交換してしまうという展開が新鮮だ。監督はこれが初の一般映画となったピンク出身の広木隆一。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベアテの贈りもの", "ベアテの贈りもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "日本国憲法に“男女平等”の条文を加えたアメリカ人女性ベアテ・シロタ・ゴードンの講演を収録しながら、彼女の功績によって、その後日本人女性がどのように活躍してきたかを検証したドキュメンタリー。市川房枝や緒方貞子など歴史的、世界的に誇るべき女性の姿が勇ましく綴られている。81才のアメリカ人女性ベアテは、日本国憲法に“男女平等”を記した女性としての講演をするため、久しぶりに日本の地を踏む。", "2005-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arthur et la guerre des deux mondes", "アーサーとふたつの世界の決戦", "", "", "http://ecx.images-amazon.com/images/I/61VtyizfWLL._SL210_.jpg", 100, "", "2010-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("岡山の娘", "岡山の娘", "", "", "http://image.eiga.k-img.com/images/movie/53856/315x210.jpg?2988", 92, "岡山を舞台に、ある少女とその周りの人々のひと夏を描く物語。地元のスタッフとキャストで製作され、「急にたどりついてしまう」の福間健二がメガホンを握る。大学生のみづきは母親との2人暮らしだったが、その母親も借金を残して他界する。みづきは大学を辞めて自己破産の手続きをしようとするが、会ったことのない父親がヨーロッパから帰ってきて……。", "2008-11-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAVE　THE　GREEN　PLANET！", "地球を守れ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "モスクワ国際映画祭で最優秀監督賞ほかを受賞した、驚きの展開で贈るSFサスペンス・コメディ。韓国映画界の若手演技派と言われるシン・ハギュンが怪演して魅せる。主人公はネクラでフツーの若者だが、地球で起こる災難や不幸は全て宇宙人のせいだと信じている。そんなある日、会社の社長がエイリアンだとにらんだ彼は、社長を誘拐し監禁してしまう……。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUNGO 日本文学シネマ　高瀬舟", "BUNGO 日本文学シネマ　高瀬舟", "", "", "http://ecx.images-amazon.com/images/I/51sI5r4%2BE2L._SL210_.jpg", 30, "日本が誇る文豪たちの名作短編を映像化する「BUNGO」シリーズの1作として、森鴎外の「高瀬舟」を成宮寛貴主演で描いた時代劇。流刑を言い渡された京都の罪人を乗せて高瀬川を下る舟に、弟を殺して裁かれた青年・喜助が乗せられる。護送役を務める同心・羽田庄兵衛は、喜助が晴れやかな表情を浮かべていることを不思議に感じ、その訳を尋ねてみることに。すると、喜助は事件の切ないてん末を語りはじめる。", "2010-05-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHAT THE BUTLER SAW", "執事の眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "役人のエールは、植民地の南の島での任務を終えて、執事のベンブリッジとともに英国の屋敷へ戻ってきた。ところが、持ち帰った荷物の中に思いもかけない珍客が！　なんとベンブリッジに一目惚れした、島の首長の一人娘が荷物に入って英国までついて来たのだった。このチャーミングなプリンセスをどうするか、二人は頭をかかえる。やがて“屋敷内に不思議なことが起こる”との噂が流れ始め……。恐怖映画の代表ハマー・フィルムが製作した「ローマの休日」の裏バージョンといった趣向の作品。", "1950-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Whatever Works", "人生万歳！", "", "", "http://image.eiga.k-img.com/images/movie/55716/315x210.jpg?4019", 91, "ウッディ・アレンの監督第40作となるコメディ。現代のニューヨーク。かつてノーベル賞候補だった天才物理学者のボリスも、今では社会から孤立し自堕落な日々をおくる気難しい初老の男。ある夜、ボリスの家に南部の田舎町から家出してきた娘メロディが転がり込み、そのまま居座ってしまう。2人は生活をともにするうちにひかれあい、年の差を乗り越えて結婚するが……。主演は人気コメディアンのラリー・デイビッド。ヒロインのメロディに「レスラー」のエバン・レイチェル・ウッド。", "2010-12-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グッドバイ", "グッドバイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "学園闘争も末期にさしかかった1970年初頭。映画好きのノンポリ学生がアジビラを配る女性活動家に気持ちがなびき、彼女の姿を求めて集会に顔をだす。集会場での内ゲバ騒ぎから二人は急速に恋愛関係に。しかし、初めてのデモで男は頭に大ケガを負い、入院中に彼女にも去られて……。精神的な傷痕から、過ぎていく時代に取り残されていく男の痛恨の思いにあふれた青春挽歌。森田童子の歌が印象的に使われている。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents 真夜中の宴　「granite グラニテ」", "桃まつり presents 真夜中の宴　「granite グラニテ」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 15, "", "2008-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA GLOIRE DE MON PE｀RE", "プロヴァンス物語 マルセルの夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "フランスの国民的な戯曲家にして映画人、M・パニョル。彼の少年時代の回想記から、第１部を映画化。19世紀末の、主人公マルセルの誕生から、幸福だった幼年時代、そしてプロヴァンス地方の別荘で過ごした、忘れられない少年時代の夏の日々が描かれる。牧歌的なユーモアを交え、すべてが輝いていた少年の時を、みずみずしく映し出している。DVDは「マルセル スペシャルエディション」に収録。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CRYING GAME", "クライング・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "IRAのテロリスト、ファーガスは人質に取った黒人兵士と奇妙な友情で結ばれる。そして彼の死に立ち会い、彼の恋人に会う約束を交わす。ファーガスは身分を隠してその女性に近づくが、ミステリアスな魅力を秘めた彼女にやがて心を奪われていく……。意表を突く展開と独特の映像美で根強いファンを持つN・ジョーダン監督のラブ・サスペンス。", "1992-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("7級公務員", "7級公務員", "", "", "http://image.eiga.k-img.com/images/movie/55796/315x210.jpg?2124", 112, "「映画は映画だ」のカン・ジファンと「青春漫画」のキム・ハヌルが共演するアクション・ラブコメディ。国家情報院の国内産業保安チームでミッション成功率100％を誇るスジは、その特殊な職業上、彼氏のジェジュンにさえ正体を明かせずにいた。そんなスジのウソに疲れ果ててしまったジェジュンは、一方的に別れを告げてロシアへ留学。3年後、国家情報院海外部門所属要員となって帰ってきたジェジュンは、任務中にスジと再会を果たす。", "2010-12-18", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pirates of the Caribbean: Dead Man's Chest", "パイレーツ・オブ・カリビアン　デッドマンズ・チェスト", "", "", "http://ecx.images-amazon.com/images/I/51tBIfGnCvL._SL210_.jpg", 151, "03年に大ヒットを記録したジョニー・デップ主演の海洋アドベンチャー第2弾。前作で恋人同士になったウィル（ブルーム）とエリザベス（ナイトレイ）は婚約して結婚式の準備を進めていたが、スパロウ船長（デップ）は海賊デイビー・ジョーンズから借金の取り立てに遭っていた。監督は前作同様、ゴア・バービンスキー。敵役デイビー・ジョーンズには「ナイロビの蜂」のビル・ナイ。", "2006-07-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IZO", "IZO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "「着信アリ」の三池崇史監督が、ビートたけしや松田龍平をはじめ、人気ファイターのボブ・サップまでを起用し、ニ度と実現できないほどの豪華キャストで製作した異色作。現代の東京に怨念として甦った“人斬り以蔵”の異名をとる岡田以蔵が、傍若無人の血の殺りくを繰り広げる。タイトル・ロールを演じる中山一也の体当たり演技にも注目を。時は幕末、土佐勤皇党首領、武市半平太に唆され、天誅の名のもとに数々の幕府要人を暗殺してきた岡田以蔵が処刑された。壮絶な最期を遂げたはずの以蔵だったが、彼の怨念は消えることなく、時空を超えて現代の東京に甦った。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O MELISSOKOMOS", "蜂の旅人", "", "", "http://ecx.images-amazon.com/images/I/41CY7ERAVWL._SL210_.jpg", 122, "アンゲロプロス監督が「シテール島への船出」の後にマストロヤンニを主演に迎えて描いたかつての活動家の最後の旅。教師のかたわら、父を継いで蜂飼いの旅を続けてきた初老の男が、娘の結婚式を家族との別れに旅立った。蜜蜂を連れて昔の仲間を訪ねて廻る途中、ひとりの少女に出会うのだが……。ギリシャ語をひとことも話せない大スター、マストロヤンニが全編吹き替えで絶望のギリシャ男に挑んだ。アンゲロプロス監督とはこの後「こうのとり、たちずさんで」で再び組む。", "1996-06-22", "27,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("何か面白いことないか", "何か面白いことないか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「憎いあンちくしょう」とほぼ同じスタッフとキャストによる青春ドラマの異色作。平凡な恋人との退屈な幸福に疑問を持ちはじめた倉橋典子は、父のセスナを売り払うことになるが、喫茶店で会った型破りの青年、早坂次郎に出会う。元パイロットだった彼は、何か突飛なことをやりたくて典子のセスナを手に入れる。典子は彼に協力するうちに次第に生きがいを感じ始め……。「憎いあンちくしょう」で明確に示された人間の真摯な生き方が、姉妹編的なこの作品では、結局不明瞭なままに終わってしまった感がある。しかし、商業映画の枠の中にあってテーマをしっかり見据える蔵原惟繕監督の前向きの姿勢は大いに評価される。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("柘榴館", "柘榴館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "山崎洋子の同名小説を映画化したエロティックな愛憎劇。壮麗な洋館を舞台に、近親相姦、不倫、殺人などのおどろおどろしい事件が交錯する。人里離れた邸宅に暮らす立花家の住み込み介護士として雇われた沙季。気難しくも妖艶な女主人の久代の世話をするうちに、彼女はこの家の人々に宿る呪われた運命を知るのだった。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PE´DALE　DOUCE", "ペダルドゥース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1996年度フランス映画界の興行成績No.1に輝いた小粋なコメディ。昼間は優秀なビジネスマンだが、夜になるとゲイ・クラブで元気いっぱいに人生を謳歌するアドリアン。そんな二つの顔を持つ彼の日常が、自由に生きるクラブの女性オーナーと彼女に恋した中年エグゼクティブの関係を絡めて描かれる。アドリアン役で女装を披露するP・ティムシットは、フランスの人気マルチ・コメディアン。1970年代のディスコ・ナンバーが効果的に使用されている。ビデオ発売時タイトルは「ペダルドゥース／淫らな夜　パリ・マレ地区編」。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アッシイたちの街", "アッシイたちの街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "1970年代後半からの自動車・家電などの華やかな輸出ブームの影で、京浜工業地帯で働く若者達の姿をダイナミックに描いた、巨匠・山本薩夫監督の青春群像劇。“アッシイ”とは、パックに入った組立部品のことで、ここではアッシイを下請けしながら生活している一家の物語を主軸にして、ドラマが繰り広げられていく。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名一番勝負", "悪名一番勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "マキノ雅広監督によるシリーズ第15作。新設される停車場の利権を狙う悪徳ヤクザが、長屋をつぶそうとたくらむ。用心棒の朝吉は、組に単身乗り込んで悪玉一味を叩きのめす。マキノ雅広らしい賑やかな顔ぶれで、江波杏子が得意の女賭博師役で共演している。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十面埋伏", "LOVERS", "", "", "http://image.eiga.k-img.com/images/movie/1116/315x210.jpg?1531", 120, "「HERO」のチャン・イーモウ監督が、同作のアクション監督チン・シウトン、衣装のワダ・エミと再び組んで描く古代中国アクション大作。唐の大中13年。朝廷の捕吏2人、劉と金は、最大の叛乱勢力集団、飛刀門の討伐の命を受けて、その集団の前首領の娘といわれる盲目の踊り子、小妹を捕らえ、彼女を救うふりをして金が集団に潜入、劉が密かに2人を追うという作戦に出るのだが。第57回カンヌ国際映画祭特別招待作品。", "2004-08-28", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VILLA RIDES！", "戦うパンチョ・ビラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "革命の動乱に明けくれる1912年のメキシコを舞台に、実在の英雄パンチョ・ビラの半生を描いたアクション映画。ランスフォードの原作を、ペキンパーとR・タウンという一流どころが共同で脚色した注目作。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　TIGGER　MOVIE", "ティガームービー／プーさんの贈りもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "世界中で愛されている“くまのプーさんと仲間たち”が繰り広げる、友情と冒険の物語。主人公はプーさんの友だち、トラのティガー。冬支度の森で急に寂しくなったティガーは、自分と同じように遊んでくれる“家族”を探すことに。ルーの提案で手紙を出したが、返事は来ない。ついに彼は、プーさんや仲間たちの心配をよそに吹雪の中へと旅立ってしまい……。『メリー・ポピンズ』などで知られるシャーマン兄弟の音楽が全編を流れ、物語をより盛り上げている。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い季節", "赤い季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルパン三世 DEAD OR ALIVE", "ルパン三世 DEAD OR ALIVE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "シリーズでは初めて原作者モンキー・パンチが監督し、原作のキャラクターが持つ“ワルでかっこいい”ルパンをハードボイルド・タッチで描いている。首狩り将軍の独裁国家・ズフ共和国の沖合に浮かぶ漂流島に、王政時代の膨大な財宝が眠っているとの情報を入手したルパンは、さっそくズフに潜入し、将軍が国を乗っ取るために謀殺したパニシュの恋人・オーリに出会う。しかし、死んだと思われていたパニシュは、ズフの独裁を倒そうとするレジスタンスを組織していた。そして彼こそ、財宝の秘密を握る男だったのだ。一方、何者かがルパンの首に100万ドルの賞金を賭け、世界中の殺し屋たちがズフに集まって来る……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Manta, Manta", "バニシングストリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「ノッキン・オン・ヘブンズ・ドア」でブレイクし、その後ハリウッドでも活躍しているドイツ人俳優ティル・シュバイガー主演の、コミカルなカー・アクション映画。自慢の愛車、オペルのマンタGTEに乗る主人公バーディは、ベンツに乗るライバル、アクセルと決闘の約束をする。そんなある日、バーディの恋人アスチが妊娠してしまい……。オペル、ベンツ、フェラーリ等ドイツの名車がずらりと登場。日本では2005年にDVD化。", "1991-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("25th Hour", "25時", "", "", "http://image.eiga.k-img.com/images/movie/1226/315x210.jpg?3722", 136, "ニューヨークで生きるモンティは、今から24時間後に７年の刑期で収監される。刑務所で自分を待つ運命に怯え、服従か逃亡か、それとも自殺かと思い悩む彼は、残された時間をどう過ごすのか。同名原作小説を書いたデビッド・ベニオフが脚本を書き、「マルコムX」「サマー・オブ・サム」のスパイク・リーが監督。ジャズ・ミュージシャンとしても有名なテレンス・ブランチャードの音楽がゴールデン・グローブ賞にノミネート。", "2004-01-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЗЕМЛЯ", "大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "エイゼンシュテインの「戦艦ポチョムキン」と並んでソ連の無声映画を代表する名作。人々が大地に根づいて暮らしているウクライナの農村に労働の集団化の運動がやってくる。青年ワシーリーはその運動の先頭に立ち、馬をトラクターに替えて農作業の機械化を進める。しかし、集団化に反対する富農の一人がある夜ワシーリーを襲った。ワシーリーの非業の死に農民たちは悲しみにくれるが、彼らの絆は以前にも増して強くなっていった……。明らかに社会主義礼讃の物語だが、内容はイデオロギー的というよりはむしろ、農民たちの泥くさい生命力を描いたもの。素朴で力強い映像が時代を超えた感動を呼ぶ。", "1930-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸川乱歩の一寸法師", "江戸川乱歩の一寸法師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "東京の夜を捉えたダークな映像（本当に暗い!?）、江戸川乱歩ならではの奇態かつ救いのないストーリー、そして役者たちの鬼気迫る演技。美しい人妻とその富豪の一家に襲いかかる誘拐と殺人事件の行方を描いたこの作品は、そんな“見せ物”に徹した新東宝独自のプログラム・ピクチャーの魅惑に満ちた傑作怪奇スリラー。ラストのオチには、ただただ驚くほかない。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Being There", "チャンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "　ワシントン郊外。主人が亡くなり、行き場のなくなった中年の庭師チャンスは町をさまようことに。彼は屋敷の外を知らず、草花をいじり続け、テレビだけを楽しみに生きてきた男だった。やがてチャンスは政治をも左右する財界の大物ベンジャミンと知り合う。無垢な心を持つチャンスはベンジャミンや彼の妻といった人々を次々と虜にしていくが……。天真爛漫な庭師を通じ、社会を風刺したコメディ。ピーター・セラーズが名演を見せる。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HAUNTED PALACE", "怪談・呪いの霊魂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "忌まわしい過去を持つ、祖先の建てた古城へ妻とともに引っ越してきたチャールズ・ウォード。やがて彼は、代々悪魔崇拝者であった祖先の魂が乗り移り、性格も容貌も醜く変化していく……。ラストのショッキングなシーンは見もの。ビデオ発売時タイトルは「呪いの古城」。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版 SPEC 天", "劇場版 SPEC 天", "", "", "http://image.eiga.k-img.com/images/movie/56856/315x210.jpg?3810", 119, "戸田恵梨香と加瀬亮のダブル主演で、未解決事件の捜査のため公安部に設立された「未詳事件特別対策係」＝通称「未詳（ミショウ）」の捜査員が、特殊能力（SPEC）を持った犯人と対決する姿を描いた人気ドラマ「SPEC 警視庁公安部公安五課未詳事件特別対策係事件簿」の劇場版。未詳の特別捜査官・当麻紗綾と瀬文焚流のもとにある日、謎めいたミイラ死体殺人事件の報せがもたらされる。その事件はやがて国家を揺るがす事態へと発展していき……。監督はドラマ版の演出も手がけた堤幸彦。", "2012-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wall Street", "ウォール街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "壮絶なマネー戦争の最前線、世界経済の中心地であるウォール街を舞台に、現代の花形職業、証券マンの夢と野望、まさに生死を賭けた熾烈な戦いをスリリングに描出。O・ストーンのスピーディーで切れ味鋭い演出が絶妙だ。若き証券マンのバドは、持ち前の頭脳と行動力のみを元手に、一獲千金を狙っていた。目標は、アメリカ出世物語を地でいく富豪成金ゲッコー。ひょんなことからゲッコーに気に入られたバドは、彼から様々な成功のコツを教えられ、見るまに実績を上げていく。いつしかバドは、違法行為すら辞さないまでになるが……。バド役をC・シーンがハードに熱演。非情だがカリスマ的魅力を持つゲッコーには、M・ダグラスが扮し演技派ぶりを発揮。1988年アカデミー賞はじめ、ゴールデン・グローブ賞などで、主演男優賞をものにした。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おじさん天国", "おじさん天国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "「たまもの」「かえるのうた」のいまおかしんじ監督によるエロティック・ファンタジー。とある港町の水産会社で働くハルオは、ダイオウイカを釣ることを夢見ていた。ハルオには同じ会社で働くリカという恋人がいるが、リカは同じく同僚の岩田とも交際中だ。そんなハルオの元に、おじさん・高山たかしがやって来たことから、ハルオとリカは不思議な世界へと迷いこんでいく。おじさん役にベテラン俳優の下元史朗。", "2006-12-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rio, 40 Graus", "リオ40度", "", "", "http://image.eiga.k-img.com/images/movie/55866/315x210.jpg?1694", 100, "ブラジルの巨匠ネルソン・ペレイラ・ドス・サントスが1955年に発表した長編監督デビュー作。リオデジャネイロのスラム街で暮らす人々の1日にスポットを当て、登場人物それぞれのエピソードを散文的に交錯させたドラマ。60年代に盛り上がったブラジルの映画運動「シネマ・ノーボ」の先駆けとなった作品で、トリュフォーやゴダールに絶賛されて世界的脚光を浴びた。", "2010-12-04", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女必殺五段拳", "女必殺五段拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "京都西陣の織物問屋の一人娘・菊は、空手に熱中し結婚話には耳をかさない。道場の妹弟子ミッチーの兄ジムは、レストランを開くための資金ほしさに、悪の手先となっていた。菊は悪の根城・極東映画京都撮影所にエキストラとして潜入する。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2011-2012 グノー「ファウスト」", "METライブビューイング2011-2012 グノー「ファウスト」", "", "", "http://image.eiga.k-img.com/images/movie/57166/315x210.jpg?3525", 233, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を映画館で上映する「METライブビューイング」2011～12年シーズンの6作目。ドイツの文豪ゲーテの戯曲を、フランスの人気オペラ作曲家シャルル・グノーが脚色したオペラ。16世紀ドイツ。悪魔メフィストフェレスに魂を売りわたしたファウスト博士は、マルグリットと恋に落ち、妊娠させてしまう。怒ったマルグリットの兄バンレティンはファウストに決闘を挑むが、逆に命を落とし、ファウストはわが身をさいなむマルグリットを捨てるが……。", "2012-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ROAD TO EL DORADO", "エル・ドラド／黄金の都", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "かつて、ディズニーで「美女と野獣」「ライオン・キング」などを製作したJ・カッツェンバーグが、製作総指揮を手掛けたドリームワークス製の胸躍る冒険ファンタジー。お尋ね者の詐欺師トゥリオとミゲールが、いかさまダイスで黄金の都エル・ドラドへの地図を手に入れたことから珍道中が始まる……。アカデミー賞を受賞した「ライオン・キング」のミュージカル・チームと人気ミュージシャンのE・ジョンが協力し、壮大な物語にふさわしい歌と音楽を提供。「ワイルド・ワイルド・ウエスト」では敵同士だったK・クラインとK・ブラナーが手を組み、声で息の合った共演を披露しているのも興味深い。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISSING　JESSICA　STEIN", "kissingジェシカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "働くシングル女性の共感を呼ぶ都会派ラブ・コメディ。微妙な愛情関係に陥った、実は“ストレート”の独身女性二人。彼女たちが手探りしながら、真実の愛をつかむまでを見つめる。ニューヨークの有名新聞社に勤めるジャーナリストのジェシカ。恋にご無沙汰の彼女は、大好きなリルケの詩を引用した新聞の恋人募集広告を見つける。ところがその広告主は“彼女”を求める女性のヘレンで……。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAN’T　HARDLY　WAIT", "待ちきれなくて…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "J・L・ヒューイット主演の青春ラブ・ストーリー。高校の卒業式が終わり、ごく普通の青年プレストンは、３年間片思いだったアマンダに気持ちを伝えようと卒業パーティーへ向かう。だが、アマンダの恋人でフットボールの花形選手マイクは、そこで彼女に別れを告げようとしていた。一方、マイクにいじめられていた優等生ウィリアムは彼への復讐を画策。さらに、悪ガキたちが押し寄せ、パーティーは警察も駆け付ける大騒動に発展してしまう。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Przypadek", "偶然", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "父の希望で医師を目指し、医大に通う青年ビテク。父の死の知らせを受けた彼は、首都ワルシャワへ向かう決心をしたが、ワルシャワ行きの列車は今まさに出発しようとしていた。偶然によって変わるビテクの3つの運命を、ドキュメンタリー監督として長く活躍してきたポーランドの巨匠クシシュトフ・キエシロフスキー監督が描いた画期的な作品。", "", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地球交響曲　ガイアシンフォニー　第七番", "地球交響曲　ガイアシンフォニー　第七番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「地球はそれ自体がひとつの生命体である」というガイア理論に基づき、賢人たちの言葉を美しい映像や音楽と共につづったドキュメンタリー「地球交響曲」シリーズの第7作。世界各地の伝統医療と西洋近代医学を統合する「統合医学」の第一人者アンドルー・ワイル、環境教育活動家の高野孝子、全身に散弾銃を浴び再起不能とされながらもツール・ド・フランスのチャンピオンになったグレッグ・レモンらが、この世のあらゆる生命体に宿る「自然治癒力」について語る。", "2010-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TONITE！ LET’S ALL MAKE LOVE IN LONDON", "TONITE！ LET'S ALL MAKE LOVE IN LONDON", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "“スウィンギング・ロンドン”と呼ばれた1960年代後半の、ロンドンのポップ・カルチャーの熱気を伝えるドキュメンタリー。ミニスカートをまとったティーンのファッション、ローリング・ストーンズ、ピンク・フロイドらが先導した新しいロックの波、マイケル・ケイン、ヴァネッサ・レッドグレーヴなど若者に支持される映画スターの出現など、時代の波をポップな視点で切り取っていく。バックを彩る音楽も、当時の人気ロックバンドによるサイケデリックなナンバーをそろえている。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLER　CONDOM", "キラーコンドーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ケーニッヒのベストセラー・コミックを映画化した、過激なシニカル・コメディ。ニューヨークで男性が性器を噛み切られるという事件が続発。犯人は凶暴化したコンドームの怪物！　ホテルで危機を逃れたゲイの刑事マカロニは、人間を襲う“キラーコンドーム”の陰に、科学者ボリスの存在を突き止める。ところが、その矢先、アメリカ大統領候補も犠牲となり……。「エイリアン」のH・R・ギーガーがキラーコンドームのデザインを担当。", "1996-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冷たい血", "冷たい血", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「WiLD LIFE」に続く青山真治の監督第４作。「チンピラ」でも組んだ石橋凌を主演に起用し、ある刑事の内面を純文学を思わせる繊細かつ骨太なタッチで綴っていく。殺人犯を追跡中、銃弾を受けて意識不明になり、何者かに拳銃を奪われた主人公。やがて、警察を辞めて単独で犯人を追う彼に、ある青年が“愛を証明するために”連続殺人を予告する。オウム真理教事件を思わせる衝撃的な幕開け、唐突に現れては消える謎のガスマスク部隊。日本の現実を映画に照射する一方で、片肺を失って文字通り“胸がからっぽ”になっていく男の行方を不思議な情感とともに、しっかり見届ける。確かな手腕で多様な解釈を誘発する刺激的な作品だ。「3-4X10月」の柳ユーレイが、キーポイントとなる男を不気味な存在感で体現。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Ought to Be in Pictures", "わたしは女優志願", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "サイモンの同名舞台劇を彼自身が脚色したヒューマン・コメディ。かつてはハリウッドの売れっ子ライターだった男のところに、女優志願の娘が訪れる。16年ぶりに再会した父娘だったが、娘の思惑は……。サイモンとロスの名コンビが綴る、笑いと涙のドラマ。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母の居る場所／台風一過", "母の居る場所／台風一過", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 37, "ネット配信されたWEBドラマを「母への想い」をテーマに劇場用に再編集した３話のオムニバス。沖縄で人生の再出発をはじめた親子の衝突を描く「第一話・美智子と小百合」。スキューバダイビングのインストラクターである娘と、台風のたびに電話をかけてくる母を描いた「第二話・美咲」。稼業の定食屋を継がずに、宅配弁当屋を始めた息子と心配する母を描いた「第三話・睦男」。各作品、すべて舞台は沖縄で、大型台風接近という状況が設定されている。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("別れのタンゴ", "別れのタンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "佐々木康＝高峰三枝子＝万城目正トリオの「懐しのブルース」に続く戦後第２作の歌謡映画。大スターを目指す歌手・美恵子と、かつての名バイオリニストで戦争で指を痛めた売れない作曲家のロマンスに、万城目メロディが香りを添える。主題歌の『別れのタンゴ』『しのび泣くブルース』はともに大ヒットとなった。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("目下の恋人", "目下の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "TVドラマ『愛の手前　恋のとなり』に未放映シーンを追加、再編集。辻仁成が「フィラメント」に次いで井川遥を主演に迎えたラブ・ストーリーだ。シュウという恋人がいながら、妻子ある男性バードとも付き合っているアカリ。二人にはそれぞれ魅力を感じているが、男たちにとってもアカリは目下の恋人でしかない。やがて、アカリはどちらが父親か分からない子供を妊娠。二人の男の間で揺れ動きながら、アカリは本当の愛を模索する。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("同じ星の下、それぞれの夜", "同じ星の下、それぞれの夜", "", "", "http://image.eiga.k-img.com/images/movie/57956/315x210.jpg?3936", 0, "2012年・第4回沖縄国際映画祭の企画「地域発信型プロジェクト」で製作された作品。「パビリオン山椒魚」の冨永昌敬、「イエローキッド」の真利子哲也、「サウダーヂ」の富田克也という注目の若手監督3人が、それぞれフィリピン（「ニュースラウンジ25時」／冨永監督）、マレーシア（「FUN FAIR」／真利子監督）、タイ（「チュンライの娘」／富田監督）を舞台に描いたオムニバス。", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("里見八犬伝第１部／妖怪の乱舞／八剣士の凱歌", "里見八犬伝第１部／妖怪の乱舞／八剣士の凱歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "河野寿一監督、東千代之介、中村錦之助主演で1954年に東映が映画化した滝沢馬琴の原作を、東映時代劇の看板監督・内出好吉が再び映画化したもの。前回は５部作の大作であったが、今回は３部作構成。東映若手スターの顔見世として製作されたもので、犬の八房の身体から８つの珠が四散し八犬士の生まれるところから、彼らの大活躍で里見の城が守られるまでが描かれている。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博徒対テキ屋", "博徒対テキ屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "昭和４年の浅草を舞台に、デパート進出を図る資本家と伝統的テキ屋との対立、さらにはテキ屋同士の内紛に巻き込まれる博徒の姿を描く。菊家一家の息子・竜太郎は、自分が母親の密通からできた不義の子であることを知り、実の父親を殺して一家を飛びだしたものの、浅草のために我が身を投げうっていく。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sid and Nancy", "シド・アンド・ナンシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "セックス・ピストルズのベーシストで、21歳という若さでこの世を去ったパンク・ヒーロー、シド・ビシャス。彼とその恋人ナンシーの破滅的な愛を「レポマン」で衝撃のデビューを飾ったアレックス・コックス監督が映画化。パンクを地で行く男シドをゲイリー・オールドマンが熱演。タイトル曲をジョー・ストラマーが、またオリジナル挿入歌を、ピストルズのギタリストだったスティーブ・ジョーンズや、ザ・ポーグスらが提供している。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星くず兄弟の伝説", "星くず兄弟の伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "映画にTVにビデオにと、ジャンルを超えて活躍するビジュアリスト・手塚眞と、意欲的なアプローチを続けるミュージシャン・近田春夫が組んだ異色の作品。無名ロック・シンガーの栄光と悲惨、上昇と失墜の年代記を、長い回想の物語として展開させていく。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PART XVI 借金セミナー", "難波金融伝 ミナミの帝王　劇場版PART XVI 借金セミナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "大阪ミナミの高利貸し、萬田銀次郎の活躍を描く人気シリーズ劇場版第17作。旅館経営に苦しむ遠山は、“開運セミナー”で住職・前島廣心から水晶玉を購入したとたん、外資系ホテルとの提携が決まり、すっかり信者に。ところが、これは仕組まれた罠で……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHENOMENA", "フェノミナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "昆虫と交信できる超能力を持った14歳の美少女が、スイスの郊外にある寄宿舎にやって来た。由緒あるその建物は何やら不気味な雰囲気。さらに最近、連続して起きている殺人事件にも関連がありそうで……。イタリアン・ホラーの鬼才アルジェントが、美少女J・コネリーを起用して作り上げたサスペンス・ショッカー。DVDは劇場公開版より４分長い完全版で発売。", "1984-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美空ひばり・森進一の花と涙と炎", "美空ひばり・森進一の花と涙と炎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "日本舞踊家元の跡継ぎ娘・霞はバンド・マスターに熱をあげ家を出る決心をする。一方、弟子の喜三郎は霞と結婚して家元を乗っ取る計画をたてていた。バンド・マスターにふられた霞はある日、流しの歌手に出会う……。美空ひばり＝森進一のコンビによる歌謡映画。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("闇の中", "闇の中", "", "", "http://ecx.images-amazon.com/images/I/41PNA7KZ8RL._SL210_.jpg", 89, "元人気お笑いコンビ「パイレーツ」の西本はるかが映画初主演に挑戦し、男たちを翻弄する魔性の女を体当たりで演じたエロティック・サスペンス。ある日、精神科医・新見の元に、美貌の女性患者・麗がやって来る。「人を殺した」という麗の告白に半信半疑のままカウンセリングを続ける新見だったが、ミステリアスな麗の魅力に次第に溺れていき……。メガホンを取るのは、「集団殺人クラブ」シリーズの石川均。", "2006-08-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KID BROTHER", "ケニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "生後６ヵ月で下半身切断というハンディキャップを負ったケニー少年。しかし彼は二本の腕を自由自在に使い、普通の少年以上に明るく元気に成長する。そんなケニーと、大きな愛で彼を見守る家族の真実の姿を描いた感動のドラマ。主人公を演じるのはケニー本人。特に、一人旅をする彼の勇姿は感動的だ。モントリオール映画祭グランプリ受賞作品。日本で「Keiko」を撮ったC・ガニオンの日米合作映画。", "1986-01-01", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON SE CALME ET ON BOIT FRAIS A｀ SAINT TROPEZ", "恋のサントロペ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "フランスのサマー・リゾートのメッカ、サントロペ。陽光まばゆいこの地を舞台にした青春映画。ブルターニュの叔母の家に行くのをいやがり、ボーイフレンドのルノーがいるサントロペへやって来たジュリエット。彼女はここで、秘書と秘密のバカンスを楽しみに来た父親と鉢合わせしてしまう……。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nuovo cinema paradiso", "ニュー・シネマ・パラダイス", "", "", "http://ecx.images-amazon.com/images/I/516X1jgWzoL._SL210_.jpg", 124, "　映画監督として成功をおさめたサルバトーレのもとに、老いたアルフレードの死の知らせが届く。彼の脳裏に、“トト”と呼ばれた少年時代や多くの時間を過ごした「パラダイス座」、映写技師アルフレードとの友情が甦ってくる。シチリアの小さな村の映画館を舞台に、映画に魅せられたサルバトーレの少年から中年に至るまでの人生を3人の役者が演じる。アカデミー外国語映画賞やカンヌ映画祭審査員特別グランプリなど、各国で賞賛を浴びた。", "1989-12-16", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROJECT SHADOWCHASER", "シャドーチェイサー 地獄の殺戮アンドロイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "巨大総合病院のビルが、軍事用殺りくアンドロイドが率いる武装テロリスト軍団に占拠され、大統領の娘が人質にとられる。FBIは急拠囚人を蘇生させ、いたる所に爆弾が仕掛けられ要塞と化したビルへの潜入を試みるが……。無敵の凶悪アンドロイド対タフなスーパーヒーローというおなじみの図式の近未来“ジェットコースター”アクション。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FIRST POWER", "ペンタグラム 悪魔の烙印", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ロサンゼルスで起きた儀式めいた連続殺人事件を追う若い刑事は、女性霊能者から悪霊が事件に関与していると告げられる。その霊とは刑事が死刑台に送り込んだサイコ・キラーのものだったのだ！　ルー・ダイヤモンド・フィリップスを主役に配し、息つく暇もないアクションで見せる異色ホラー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Road to Perdition", "ロード・トゥ・パーディション", "", "", "http://image.eiga.k-img.com/images/movie/1146/315x210.jpg?3692", 119, "30年代、大恐慌下のシカゴ。両親の死後、育ててくれたギャングの親分の下で、殺し屋として働くマイケル。が、親分の息子に妻と幼い次男を殺害され、12歳の長男とともに逃亡して、組織への復讐を企てる。しかし彼は長男には自分と同じ道を歩ませたくなかった。監督第1作「アメリカン・ビューティー」でオスカー監督賞＆作品賞を獲得したサム・メンデス監督の第2作。主演は2度のオスカー主演男優賞に輝くトム・ハンクス。", "2002-10-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どんずまり便器", "どんずまり便器", "", "", "http://image.eiga.k-img.com/images/movie/57736/315x210.jpg?3002", 81, "今回が初の長編作品となる女性監督・小栗はるひが、過去のトラウマから抜け出せず、不器用な愛情表現しかできない姉弟の姿を描く。凶暴で男にだらしのない女ナオミは同僚を刺して服役していたが、刑期を終えて生まれ故郷に戻ってくる。幼い頃に両親をなくしたナオミにとって、たった一人の弟ケイの存在だけが生きがいだったが、ケイは恋人のカナと幸せな日々を送っていた。そんなケイを見たナオミは、カナに幼い頃の姉弟のある秘密を告げる。", "2012-04-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("思い出の指輪", "思い出の指輪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "当時の人気グループ・サウンズ、ヴィレッジ・シンガーズを主役に据え、映像派の斎藤監督が撮影も兼ねて撮り上げた青春もの。城南学園にはその年の最も素敵な男子生徒にナルシスの指輪を贈るという伝統行事があった。全校女生徒たちの熱烈な推薦を受けた音楽好きな５人が、最終選考テストの標的に……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMORE E CHIACCHIERE", "恋はいそがず", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "町の名士の息子パオロと掃除夫の娘マリアは恋に落ちるが、マリアの父が二人の交際を認めず、二人は小さな逃避行に出る。事前に息子から相談を受けていたパオロの父は、多忙を理由に助言しなかったことを後悔し始めるが……。名匠デ・シーカが俳優として出演した人情喜劇。", "1957-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOM HORN", "トム・ホーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "S・マックィーンにとって最後の西部劇となった作品で、実在の名ガンマン“トム・ホーン”の生涯を、自ら自伝を調査するほどの熱の入れ方で完成させた。ホーンの老いとマックィーンのそれが重なり合い、一種悲壮な色調を帯び、枯れた味わいの余韻を残した。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIGGING TO CHINA", "ウィズ・ユー", "", "", "http://ecx.images-amazon.com/images/I/510TB1KMYZL._SL210_.jpg", 99, "俳優のT・ハットンが初めて劇場映画を監督したファンタスティックなドラマ。舞台は1960年代後半、ペンシルバニアの田舎町。森に囲まれたモーテルを経営する家族との生活にウンザリし、周囲から変人扱いされている10歳の少女ハリエットが、宿泊者の知的障害者リッキーと意気投合。ところが、二人の仲を周囲の大人たちは反対するのだった……。リッキーに扮するのは演技派K・ベーコン。子役E・R・ウッドのおマセな振る舞いも可愛らしい。", "1999-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les fils du vent", "MAXX!!! 鳥人死闘篇", "", "", "http://image.eiga.k-img.com/images/movie/33616/315x210.jpg?4212", 94, "2001年の主演作で世界中の注目を集めたフランスのアスリート集団YAMAKASIによるアクション・ムービー。タイのバンコクを舞台に、YAMAKASIのメンバーたちがチャイニーズ・マフィアと日本人ヤクザの抗争に巻き込まれていく姿を描く。監督は「YAMAKASI ヤマカシ」の脚本とアクション監督を手掛けたジュリアン・セリ。驚異的な身体能力を駆使し、スタント・CGなしで繰り広げられるアクションは圧巻。", "2006-08-26", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("功夫無敵", "カンフー無敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "台湾の人気アイドルユニット「F4」のバネス・ウー主演による本格カンフーアクション。繁栄の影で闇の勢力が抗争を繰り広げる1940年代の上海。自分と同じ超人的な力を持つ父を探しにやって来た青年リクは、賭博拳法場で知り合ったファッが起こした騒動に巻き込まれ、マフィアのボス、イーに追われる身となってしまう。ようやくイーの手下から逃げのびたリクは、偶然逃げ込んだ店にいたヤーが自分の父親ではないかと思いはじめる。", "2007-10-13", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEYOND DARKNESS", "バトル・ヘルハウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "グロテスクなB級ホラーには定評のあるイタリアで生まれたスプラッタ・ムービー。500年の時空を超えた恐怖が炸裂する。ニューイングランドのとある田舎町。新任のピーター神父が移り住んだ家は、引っ越したその夜から皿やグラスが飛び交い、不気味な雰囲気が漂っていた。その頃、かつて女囚刑務所で働いていたジョージは町で信じられないものを見る。魔女の生まれ変わりだと言いながら処刑されていった女が歩いていたのだ。彼は恐怖の幕明けを予感するが……。", "1990-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE AUSSI LONGUE ABSENCE", "かくも長き不在", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "アラン・レネ監督の「二十四時間の情事」などでフランス映画界の名フィルム編集者として知られたH・コルピの監督デビュー作。パリ郊外で“古い教会”という名のカフェを営むテレーズ。ある日、テレーズは店に立ち寄った中年の浮浪者を見て驚愕の表情を浮かべる。戦争中にドイツ軍に連れ去られたまま戻ってこない夫のアルベールにそっくりだったからだ。だが、この浮浪者は記憶を喪失していた。それから彼の記憶を呼び戻そうとするテレーズの努力が始まる。この浮浪者は夫なのか、それとも……。今なお人々の心に残る戦争の傷跡を、男女の愛とその記憶を通して描いた心理ドラマの名編。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SMITHEREENS", "スミサリーンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「マドンナのスーザンを探して」でメジャーに進出したシーデルマン監督のインディペンデント・デビュー作。ロック歌手を夢みる19歳のマンハッタン・パンク少女のハジケぶりを軽快に描く。リジンはインディーズではおなじみの俳優。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泣きながら生きて", "泣きながら生きて", "", "", "http://image.eiga.k-img.com/images/movie/54896/315x210.jpg?4491", 108, "ある家族の10年間を追ったTVドキュメンタリーを見て感銘を受けた大学生が、関係各社に働きかけて劇場上映を実現。1996年の東京、7年前に妻と娘を上海に残して来日した丁尚彪（ていしょうひょう）は、1度も中国に戻ることなく日本で働き続けていた。娘に一流の教育を受けさせたい一心で朝から晩まで休むことなく働き、家族への送金を続けた結果、娘はニューヨーク州立大学に合格し、渡米する。製作期間10年、撮影したテープは500時間を超える。", "2009-11-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEPHISTO", "メフィスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "K・マンの同名小説の映画化。メフィスト役で一時代を作った俳優兼演出家グスタフ・グリュンドゲソスをモデルに、コミュニストでありながら、芸術に生きようとしてナチ権力に利用されていく男の姿を描く。政治と芸術の関係を追究しつつ、小国ハンガリーの苦悩をも象徴させた問題作。", "1981-01-01", "7,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("零戦燃ゆ", "零戦燃ゆ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "太平洋戦争のさなか、兵器技術の精華として戦艦大和と並ぶ伝説的な存在となった帝国海軍の“零式戦闘機”。その試作された時から戦後に飛行場で燃やされてしまうまでを、零戦にかかわった人たちの様々なドラマを織りまぜて描いた作品。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新諸国物語 紅孔雀　第四篇 剣盲浮寝丸", "新諸国物語 紅孔雀　第四篇 剣盲浮寝丸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "“時代劇は東映”のキャッチコピーで、東映が大いなる興行的成功を収めた娯楽活劇。この第４部では、ミステリアスな盲目の若者・浮寝丸が突如、悪鬼に変貌するなど意外な物語が展開していく。DVDは「新諸国物語　紅孔雀(2)」に収録。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT DON’T PAY TO BE AN HONEST CITIZEN", "映画を探して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ニューヨーク・インディーズ・フィルムの１本。若い映画作家が、盗まれた現金とフィルムを取り戻そうと、必死の努力を続けるが……。バークハート監督自身の体験から作られた異色編。今は亡きギンズバーグ、バロウズの二人の作家の出演にも注目。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INDIA SONG", "インディア・ソング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "フランスの女性文学者M・デュラスが、自作を自ら監督して映画化。劇映画というより“映像と音で書かれた詩”とでもいうべき独特の耽美的作品。1930年代、カルカッタのフランス大使館を舞台に、登場人物の“オフの声”が静かに物語を語り、画面には建物の外観や植民地での西洋人の夜会がゆっくりと映し出される。そこに浮き上がってくるのは、大使夫人アンヌ・マリー・ストレッテルに横恋慕したラホールの副領事の純粋で狂暴な熱愛だ。彼はほんろうされ、夫人も入水自殺する。ニュイッテンの流麗なカメラ、タンゴを生かしたダレッシオのレトロ的音楽も素晴らしい。", "1974-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Unter Kontrolle", "アンダー・コントロール", "", "", "http://image.eiga.k-img.com/images/movie/57086/315x210.jpg?1713", 98, "ドイツの原子力発電関連施設を3年間にわたり取材したドキュメンタリー。原子炉建屋の内部や精密機器であふれた操作室をはじめ、放射性廃棄物の貯蔵施設、巨大な廃墟と化した原発とその解体作業の様子、原子力にかかわる人々の日常などを捉え、“原子力のゆくえ”を冷静に見つめていく。", "2011-11-12", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wide Awake", "リターン", "", "", "http://image.eiga.k-img.com/images/movie/54126/315x210.jpg?3367", 113, "「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。手術中に意識が戻ってしまう“手術中覚醒”を経験した男の復讐劇。「ファム・ファタール」のキム・ミョンミン、「カル」のユ・ジュンサン、「浜辺の女」のキム・テウらが出演。手術中の覚醒を経験した少年が、別の子供を殺害して精神病院に搬送される。そこで催眠治療を施された少年は出所し普通の生活を取り戻したかに見えたが、事件の25年後、少年の恐怖の記憶が蘇ってきて……。", "2008-10-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山参道", "山参道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "南洋から10年ぶりに故郷の磐梯に帰った男。その許嫁は彼をずっと信じていた。けれども、ほかの村人たちは男をうさんくさく思っていた。男は小学校に二宮金次郎像を寄付したり、山参道を修復したりと、村のために骨を折るのだが……。日活を離れた島耕二の初の大映作品。現存するフィルムは不完全版。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Bronzés Font du Ski", "人間模様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「髪結いの亭主 」「イヴォンヌの香り」で知られるパトリス・ルコント初期のコメディ作品で、フランスの演劇集団「スプランディド」と取り組んだ「レ・ブロンゼ／日焼けした連中」の続編にあたる。アルプスのスキーリゾートを舞台に、集まった人々をコミカルに描く。のちに「レ・ブロンゼ／スキーに行く」のタイトルでリバイバル上映された。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GODZILLA", "GODZILLA／ゴジラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "日本が生んだ特撮映画の名キャラクターを、ハリウッドが“新種ゴジラ”としてリメイクしたモンスター・ムービー。N.Y.に巨大怪獣が出現した。目撃者の証言からゴジラと命名されたこのナゾの生物の生態に興味を持った生物学者ニックは、接触してきたフランス諜報部員フィリップと捜査に乗り出す。一方、政府は軍を出動させ、あらゆるものを破壊し尽くすゴジラに攻撃を開始。街中で暴れ回り地下に潜ったゴジラの行方を追うニックとフィリップは、そこで恐るべき光景を目にする……。最新のSFXで生まれ変わったゴジラは、手足が長くなってスマートに変身。時速480キロの速さと、驚異的なジャンプ力で米軍をものともしない。高層ビルの谷間で繰り広げる戦闘ヘリとの攻防戦や水中での潜水艇との闘いなど、迫力満点の見せ場が満載だ。", "1998-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD　GUY", "悪い男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "売春街を仕切るヤクザと、彼によって娼婦に堕とされた女子大生の葛藤を描く“純愛”映画。一作ごとに物議を醸す韓国の鬼才K・ギドクの第７作。結ばれようのない男女が、心身ともにズタズタに傷つきながら新たな感情に目覚めていく人物描写が圧巻。ギドク監督の分身といわれる主演男優チョ・ジェヒョンが、一度しかセリフのない難役を演じきっている。純真な女子大生ソナに一目惚れした、ヤクザのハンギ。冷たいソナに強引に口づけしたため、彼女は激怒し、彼に唾を吐きかけた。侮辱された怒りと所有欲に燃えたハンギは、自分が仕切る売春宿にソナを娼婦として売り飛ばす。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAW2", "ソウ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "斬新なアイデアで構築した巧みなプロットを、スタイリッシュに映像化して話題を呼んだ密室スリラーの続編。前作で世間を騒がせた殺人鬼“ジグソウ”の逮捕に成功した刑事が、思わぬ事態に巻き込まれていく。あらゆる謎が氷解していくラストの展開は衝撃的だ。殺人事件の現場にやってきた刑事のエリックは、その残虐すぎる手口から、世の中を騒がせた連続殺人犯“ジグソウ”の仕業だと確信する。やがてエリックは“ジグソウ”を拘束するが、そのときすでに新たな殺人ゲームが始まっていた……。", "2005-10-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハゲタカ", "ハゲタカ", "", "", "http://image.eiga.k-img.com/images/movie/54296/315x210.jpg?1940", 134, "2007年に放送され、企業買収という斬新なテーマを扱い話題となったNHKドラマの映画化。徹底した合理主義で多くの企業の買収を成功させてきた敏腕ファンドマネージャーの鷲津は、日本の閉鎖的なマーケットに絶望し、海外にその活躍の場を広げていた。ある日、鷲津のもとを盟友の芝野が訪れ、中国系ファンドによる日本の大手自動車メーカー買収を阻止してほしいと依頼する。主演はドラマに引き続き大森南朋、敵対する“赤いハゲタカ”を玉山鉄二が演じる。", "2009-06-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("尻啖え孫市", "尻啖え孫市", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "戦国時代、大名たちが恐れおののいた日本最強の鉄砲集団“雑賀衆”。その頂点に立つ若大将・孫市の痛快な半生を描いた大型時代劇。クローズ・アップを多用した三隅演出は役者の個性を際立たせ、人間関係のわかりやすい群衆劇を作り上げた。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちびまる子ちゃん", "ちびまる子ちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "当時、人気絶頂だったTVアニメを長編らしい趣向を凝らして映画化。そのためノスタルジーをユーモアでくるんだ小学生物語というより、普遍的な感動を呼ぶ優れた学園ドラマとして完成している。小学３年生のまる子のクラスメート、大野と杉山は名コンビ。友情で結ばれた男の子二人の仲が運動会で危機を迎え、合唱コンクールで復活するまでを描く。主人公まる子はあくまでも狂言回しだが、等身大の視線で彼らを応援する彼女の姿勢にはTVシリーズ同様の“ボヤキ節”が全開しており、やや生真面目な物語を解きほぐし、安心して泣かせるムードを作り上げている。DVDタイトルは「劇場用映画　ちびまる子ちゃん」。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT　CAME　FROM　BENEATH　THE　SEA", "水爆と深海の怪物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "南太平洋の水爆実験は海を汚染し、その生態系をも変えてしまった。やがてミンナダオ海溝から巨大なタコが這い出し、航行中の船を次々に襲撃。ついにはサンフランシスコにも出現し、ゴールデンゲート・ブリッジを破壊し、市街にも侵入を開始する……。レイ・ハリーハウゼンのストップ・モーション・アニメ特撮が冴え渡る快作。日本初公開時は41分に短縮され、２本立ての添え物扱いであったが、後にファンの間で評価が高まっていった作品でもある。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNCONQUERED", "征服されざる人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "女奴隷としてアメリカに送られる船上にいるアビイは、豪商ガースと軍人クリスの二人から見初められ、接戦の末クリスの手におちた。それは、奴隷として彼女を扱うのではなくインディアンと通じている悪人ガースから守るためだったが……。巨匠デミルがG・クーパーを主演に描く作品。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dick", "キルスティン・ダンストの大統領に気をつけろ！", "", "", "http://ecx.images-amazon.com/images/I/21T7JGVCH9L._SL210_.jpg", 117, "ウォーターゲート事件の真実を目撃した女子高生が繰りひろげる騒動を描いた青春コメディ。1972年、社会科見学でホワイトハウスを訪れた女子高生アーリーンとベッツィーは、ひょんなことからニクソン大統領の犬の散歩係に就任する。毎日のようにホワイトハウスに通いはじめた2人は、そこでニクソン政権の裏側を目の当たりにし……。主演は「スパイダーマン」のキルステン・ダンスト＆TVシリーズ「ドーソンズ・クリーク」のミシェル・ウィリアムズ。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOKYO-GA", "東京画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ベンダース監督が敬愛する監督小津安二郎の墓を鎌倉に訪ねる旅を綴ったドキュメンタリー。空から見る街、駅の構内、原宿の竹の子族、パチンコ屋、食品サンプル……旅人ベンダース自身の好奇心の赴くままに東京の姿を切り取り、撮影監督、厚田雄春や俳優・笠智衆との対話を通して小津映画の世界に分け入る、映像による私的日誌。", "1989-06-17", "45,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旺角黒夜／ONE　NIGHT　IN　MONGKOK", "ワンナイト・イン・モンコック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "香港の繁華街モンコックで起こる、36時間の出来事を切り取った人間ドラマ。殺し屋や娼婦、刑事らの運命の交錯を描きつつ、それぞれに孤独を抱えた人々のドラマを浮かび上がらせる。香港の街の猥雑な空気感を切り取ったリアルな語り口や、「80デイズ」のダニエル・ウーによる殺し屋の好演が光る。二大組織の抗争に揺れる街。出稼ぎの娼婦と、組織に雇われた殺し屋、組織の一網打尽を図る刑事らが出会い、そこで思わぬ悲劇が起こる。", "2005-06-25", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Winnie the Pooh", "くまのプーさん", "", "", "http://image.eiga.k-img.com/images/movie/56146/315x210.jpg?3998", 66, "A・A・ミルンの児童文学を原作に、ウォルト・ディズニー生誕110周年を記念してシリーズ35年ぶりに製作された新作劇場用映画。プーとクリストファー・ロビンら100エーカーの森の仲間たちは、ある日、しっぽをなくしたイーヨーのために「イーヨーのしっぽを探すコンテスト」を開催。ところがクリストファー・ロビンがナゾの怪物にさらわれてしまったことから、プーたちは予想外の救出劇を繰り広げることになる。", "2011-09-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("第三の悪名", "第三の悪名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "朝吉は粟津一家の女親分・お妻に頼まれ、悪徳ヤクザ・カポネ一味の上前をはねようとする戦友・修を堅気にしようと奮闘。しかし修はカポネに捕まりリンチを加えられる。かけつけた朝吉と清次が、カポネ一味を一網打尽。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Der ganz große Traum", "レッスンズ・オブ・ア・ドリーム（英題）", "", "", "http://image.eiga.k-img.com/images/movie/58006/315x210.jpg?3495", 0, "", "2012-10-31", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄色い涙", "黄色い涙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "「タッチ」の犬童一心監督が、嵐の5人を主演に迎えて贈る青春群像劇。1960年代、高度経済成長期の東京・阿佐ヶ谷を舞台に、それぞれの夢を追いながらその日暮らしの生活を送る4人の芸術家の卵と、彼らを見守る1人の勤労青年の青春の日々を描く。原作は1974年にテレビドラマ化もされた永島慎二の同名コミック。少年時代にドラマ版を観て強く惹きつけられたという犬童監督が、その脚本を手掛けた市川森一を迎え、念願の映画化を実現させた。", "2007-04-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　POLIZIA　INCRIMINA，LA　LEGGE　ASSOLVE", "死神の骨をしゃぶれ　超犯罪ハイクライム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "マルセイユと北イタリアのジェノヴァを結ぶ麻薬ルートの撲滅に執念を燃やす敏腕警部ベルリ。しかし捜査に没頭するあまり、組織の恨みを買った彼は、身近な愛する者まで危険にさらしてしまう。イタリア版「フレンチ・コネクション」というべきハードボイルド調のポリス・アクション。F・ネロが正義を貫こうとする孤高の警部に扮し、渋い魅力を放っている。", "1973-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENEMY MINE", "第5惑星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "21世紀末、宇宙へ進出した地球人はドラコ星人と戦争状態にあった。敵を爆破した地球人ダビッジは第５惑星に不時着。だがそこにはドラコ星人も不時着していた……。異星人との敵対と相互理解、そしてなんと異星人の“雌雄同体出産”までも描いたSF異色作。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("島田陽子に逢いたい", "島田陽子に逢いたい", "", "", "http://image.eiga.k-img.com/images/movie/55676/315x210.jpg?1902", 70, "6人の監督が新しい性愛の可能性を探る特集「Love＆Eros CINEMA COLLECTION」の1作。「白日夢」（2009）のいまおかしんじ監督がメガホンをとり、島田陽子が本人役で主演を務める。病院で撮影をしていた島田は、共演俳優の急な降板により、不本意な相手と濃厚なラブシーンを演じるはめになる。現場を逃げ出した島田は、ちょうど退院しようとしていた五郎の家に居候することになるが、彼は末期のガンに侵されていた。", "2010-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("竹取物語", "竹取物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "古来から伝わる“かぐや姫”の物語を、誕生から月の世界へ戻っていくまで、ロマンスと特撮をたっぷりと織り交ぜて描いたSFメルヘン。沢口靖子の可憐な十二単衣姿と、スピルバーグ映画ばりの宇宙船のSFXが話題に。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三浦和義事件～ロス疑惑・もう一つの真実～", "三浦和義事件～ロス疑惑・もう一つの真実～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "かつて日本中を騒がせた“ロス疑惑”の真相に切り込んだ意欲作。海外で妻を殺された夫として同情を集めながら、一転して妻殺しの疑いをかけられマスコミの集中砲火を浴びた三浦和義。彼がその後裁判で無罪を勝ち取るまでの苦難を本人の証言を基に描く。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADY L", "レディ L", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "一連のアガサ・クリスティ原作の映画化作品のポワロ探偵役で有名なアカデミー俳優、P・ユスチノフが監督・脚本・出演の３役を果たした作品。80歳になるレディ・レンディルという女性の生涯、それは洗濯娘とおたずね者の義賊との愛の物語でもあった。S・ローレンの老け役も見もの。", "1965-01-01", "1,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("БИТВА　ЗА　МОСКВУ／THE BATTLE FOR MOSCOW", "モスクワ大攻防戦 第１部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 170, "135個師団、300万人に及ぶ兵力を投入して、ナチス・ドイツがソビエトに侵攻した“バルバロッサ作戦”を、ドキュメンタリー・タッチで描いた壮大なスケールの戦争映画。首都モスクワまで目前に迫ったドイツ軍の行く手を阻むものは何か……。", "1985-01-01", "12,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎子守唄", "男はつらいよ・寅次郎子守唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "旅先で赤ん坊をおしつけられて帰京した寅さん。結局、“とらや”一家が育てることになる。熱を出した赤ん坊を連れて行った病院には美しく親切な看護婦がいた。一目惚れの寅さんは、彼女が参加している合唱サークルへ乗り込んでいって大騒動を繰り広げる。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪夢探偵2", "悪夢探偵2", "", "", "http://image.eiga.k-img.com/images/movie/53366/315x210.jpg?3846", 102, "塚本晋也監督の「悪夢探偵」（2007）の続編。主演は前作に引き続き「御法度」「恋の門」の松田龍平、共演に市川実和子、光石研、内田春菊ほか。悪夢探偵と呼ばれる影沼京一は他人の夢に入ることができる特殊能力を持っているが、その仕事をいつも嫌々こなしている。そんなある日、同級生にいたずらをしてから悪夢にうなされ始めたという女子高生の雪絵が京一に助けを求めてくるが……。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("細路祥／LITTLE　CHEUNG", "リトル・チュン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "F・チャン監督が「メイド・イン・ホンコン」「花火降る夏」に続いて、香港の中国返還をモチーフにした３部作の最終編。ハードな社会派映画の側面が強かった前２作とは異なり、好奇心旺盛な下町の男の子の日常を温かい視線で見つめている。９歳にして“お金こそすべて”と悟り、“たまごっち”を買うために両親の食堂を手伝うチュン少年。ファンという不法移民の少女に初めての恋心を抱いた彼のささやかな冒険が、香港の大スター、ブラザー・チュンが1997年に死去した現実のエピソードを盛り込んで綴られる。", "1999-01-01", "6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BACK TO BACK，FACE TO FACE", "王さんの憂鬱な秋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 148, "地方文化センターの万年副館長のワンさんは、今回も村長上がりの無能なマーに館長の座を奪われてしまう。何としても館長になりたいワンさんはマーをいじめ始める。それが功を奏したのかマーは退任し、今度こそ自分の番と期待するワンさんだったが……。主役のニウ・チェンホアが東京国際映画祭最優秀主演男優賞を受賞した。", "1994-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／THE　SEOUL　RAINBOW", "ソウルの虹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "その美貌と肉体で、アッという間に芸能界のスターとなった女。やがて彼女は政界の最高権力者の豪邸に囲われの身となり、そこから逃げ出そうとすると拷問を受け、廃人寸前にされるのだった……。かつて、権力者が女優や歌手を性的奴隷にすることが度々あったという本国政界スキャンダルを鋭くえぐり、韓国で記録的ヒットを飛ばした問題作。", "1989-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sliver", "硝子の塔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "シャロン・ストーン主演のスキャンダラスなサスペンス。キャリアウーマンのカーリーが引っ越して来た、ガラス張りの高級マンション。そこはかつて殺人事件が起きた場所であり、各部屋には何者かが隠しカメラを仕掛けていた。そうとは知らぬカーリーの前で、第２、第３の殺人が起きる……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE HARD2", "ダイ・ハード2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "大ヒットを記録したアクション映画の第２作。6000万ドルの製作費をかけた大作で、監督には、「エルム街の悪夢４」で注目された新鋭R・ハーリンが抜擢された。前作では、B・ウィリス扮するマックレーン刑事が、ハイテク高層ビルでテロリストと戦ったが、今回は空港でテロリスト集団と対決する。前作から二年後のクリスマス・イヴ。妻のホリーを迎えに、ワシントンのダレス空港へ赴いたマックレーンは、不審な人物を発見して不吉な予感が走る。案の定、空港ではテロリストが空港の管制機能を制圧し、すべての旅客機を降りられなくしてしまう。ホリーの乗る機の燃料が刻々と減っていくのを知り、マックレーンはテロリストに対決を挑むが……。前作以上のバイオレンス・シーンが満載。ただし、マックレーンがスーパー・ヒーローになりすぎたきらいはある。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La brujula la lleva el muerto", "羅針盤は死者の手に", "", "", "http://image.eiga.k-img.com/images/movie/57356/315x210.jpg?2752", 111, "", "2011-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Laundry[ランドリー]", "Laundry[ランドリー]", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "窪塚洋介が知的障害を持った純真無垢な青年を演じた優しいタッチのヒューマン・ドラマ。テルの仕事は、祖母の経営するコインランドリーで洗濯物を盗まれないように見張ること。ある日、忘れ物を届けたことがきっかけで、ワケありの女性、水絵と出会う。そんな折、コインランドリーが人手に渡ってしまう。居場所を無くしたテルは、つらい過去を抱え自殺未遂を起こして帰郷した水絵を追って旅に出る。水絵はそんなテルに癒されている自分に気付くのだが……。現代の寓話とも言える物語。水絵を演じた小雪の透明感に満ちた佇まいも印象的。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もどり川", "もどり川", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "1981年度日本推理作家協会賞受賞の『戻り川心中』の映画化で、自らの歌に命を吹き込もうと、たびたび心中未遂事件を起こす歌人と、彼に魅せられて堕ちていく女たちの、心理的かけひきを描いたもの。萩原のデカダンでアナーキーな演技に魅了される。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すべての風景の中にあなたがいます", "すべての風景の中にあなたがいます", "", "", "http://image.eiga.k-img.com/images/movie/54676/315x210.jpg?2608", 74, "演劇集団キャラメルボックスによる短編演劇、ハーフタイムシアターをデジタル記録した作品。4月、イラストレーターの浩一は、熊本と宮崎の県境にある白鳥山を登山中に雨宿りした洞窟で、ある女性に出会う。女性が忘れていった手帳に気付いた浩一は、手帳に記されていた名前・藤枝沙穂流をたよりに、手帳を届けに行くが……。原作は「黄泉がえり」の梶尾真治。", "2009-07-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　サンシャインデイズ", "劇場版　サンシャインデイズ", "", "", "http://image.eiga.k-img.com/images/movie/53606/315x210.jpg?3572", 106, "70年代の湘南に実在したカフェを舞台に若者たちの青春を描いた同名テレビドラマの劇場版。名家として知られる神宮寺家の娘・ひかりは、ミュージシャンを目指す兄弟デュオ\"BB\"との出会いをきっかけに\"カフェBB\"をオープンさせる。ひかりが作るクロックムッシュとBBのライブでカフェは大繁盛、多くの若者たちが集う場となる。ヒロインを演じるのは人気ドラマ「花より男子」の西原亜希。共演に「タッチ」の斉藤慶太ほか。", "2008-05-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KEATON’S COP", "刑事ゲーブル マフィアの標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "TV『600万ドルの男』で知られる、L・メジャース主演の刑事アクション。マイアミのゲーブル刑事は、病院で起きた殺人事件の捜査を担当する。彼は犯人に狙われた元ギャングのボス、キートンを警備しながら、犯人を追い詰めていく。刑事と元ギャングの間に芽生える友情も見もの。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女狐風呂", "女狐風呂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "黒澤映画などで知られる小国英雄のオリジナル脚本を、市川雷蔵の主演で映画化した明朗推理時代劇。マキノ雅広監督の「待って居た男」（1942）のリメイク。場所を南伊豆のある温泉宿に限定し、男前の目明かし、芸妓、大工、旅の絵師、宿屋の人々など、多種多様な登場人物が推理合戦を繰り広げる……。演技派たちが顔をそろえた、小気味良い好編。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STARS ARE SINGING", "楽しき我が家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "鉄のカーテンをくぐり抜けてアメリカに密入国したポーランド人少女カトリの美しい声をめぐって繰り広げられるミュージカル。いっこうに芽が出ない芸人たちは、カトリの歌声を利用して自分たちとカトリをともに売り出そうとするが、ひょんなことから彼女の素性がばれてしまい、大騒ぎとなってしまう。カトリに扮するのは、ジャズ・シンガーとしても有名なR・クルーニー。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Step Up 2 the Streets", "ステップ・アップ2 ザ・ストリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逢いたくて逢いたくて", "逢いたくて逢いたくて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "部長が授業料に窮して楽器を質入れ、運営も行き詰まってしまった某大学サークル。部員の道子は部の再建のため偶然催された“園まりそっくりショー”に応募、見事優勝して賞金をせしめる。だが、本物の園まりが病気で声が出なくなったことから道子にその代役が回ってきて……。歌手の園まりが二役を演じる青春歌謡もの。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("素浪人罷通る", "素浪人罷通る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "伊藤大輔監督の戦後第１作。講談ネタの『徳川天一坊』の物語を題材にしている。八尋不二の脚本では、天一坊は実際に将軍の落としダネだが、封建制の政治的圧力によって殺される悲劇の男に書き変えられた。この時期はアメリカ軍による規制が厳しく、立ち回りは禁止。それに変わって独特の悲愴美でドラマを盛り上げた伊藤大輔の演出が素晴らしい。DVDは「阪東妻三郎傑作選　DVD-BOX」に収録。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聖母観音大菩薩", "聖母観音大菩薩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“禁断の人魚の肉を食べたものは、永遠の時を苦しみながら生きねばならない”という伝説を生きた女を描く。主役に「愛のコリーダ」の松田英子を起用、ファンタスティックな脚本とともに、若松監督の演出力が光る。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("復讐・運命の訪問者", "復讐・運命の訪問者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "“勝手にしやがれ!!”シリーズでコンビを組んだ黒沢清監督と哀川翔が、一転してシリアスな復讐劇に挑んだ作品。幼い頃、家族を惨殺された刑事、安城。覚醒剤事件を捜査する過程で、その犯人を見つけ出した彼は、逆に妻、冴子を惨殺されてしまう。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LO SCEICCO BIANCO", "白い酋長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "フェリーニが一人立ちして撮った第１作。イバンとバンダは、田舎からローマへ新婚旅行にやって来る。フォト・ストーリーのヒーロー“白い酋長”に憧れるバンダは、ホテルから抜け出し“白い酋長”に会いに行く。ところが、彼は卑怯で臆病な男にすぎないことを知って幻滅。風刺的な視点と幻想性という点で後年のフェリーニを彷彿とさせる。", "1951-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香魂女／OILMAKER’S　FAMILY", "香魂女−湖に生きる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "香魂湖という名の湖畔にあるゴマ油工場の女主人を通して、中国の封建的慣習に振り回される人々の悲劇を描いている。シアン・アルサオは、アル中の夫が仕事をしないため、23歳の知恵遅れの息子と従業員のチンメイとの３人で小さな工場をやっていた。彼女は、息子の嫁取りのことで悩んでおり、金を貸している漁師の娘ホワンホワンを借金の代わりに嫁にもらうことにする。シアンの20年来の愛人が別れを言いに来たところを夫に見つかりそうになるが、どうにかばれずに済む。しかし、ホワンホワンに目撃されてしまうのだった。そのことがきっかけで、かつてホワンホワンと同様に金のために結婚させられたシアンは、ホワンホワンの未来を思うようになる。", "1993-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　PE´RIL　JEUNE", "青春シンドローム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "庶民的なユーモア感覚に定評のあるC・クラピッシュ監督が手掛けた、ノスタルジックな青春コメディ。高校の同級生だった４人の男たちが病院で再会したことから、物語は始まる。彼らは、今は亡きリーダー格のトマジと過ごした、1970年代の思い出を回想。ひやかし半分に参加していた学生運動、イギリスから来た英語講師への淡い思い、ドラッグ体験、そして進路の悩み……。そんな彼らの青春の日々を、甘く切ないエピソードを連ねながらスケッチしていく。ジミ・ヘンドリックスやピンク・フロイドらのロック・ナンバーが、時代の熱気を演出。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UND DIE BIBEL HAT DOCH RECHT", "歴史としての聖書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "聖書の足どりを、発掘された数多くの遺跡と文献によって追ったドキュメンタリー。メソポタミアから発掘された石像や、モーゼが十戒を受けたシナイ山、ソロモン王の遺跡、クムランの洞窟、最古の旧約聖書といわれる死海文書、大洪水のあったことを示す地質図、4000年前のリンゴの木と蛇の絵の入った印章、十二使徒を見出した地、ガラリヤの湖、シュメール王の墓から発掘された宝石細工など、数々の遺跡や出土品、美術品を歴史の史料として綴る。バルテル・ケラーの原作を、記録映画出身で、怪奇映画やアクション映画が得意なラインル監督が手掛けた異色作。", "1977-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雲のむこう、約束の場所", "雲のむこう、約束の場所", "", "", "http://ecx.images-amazon.com/images/I/51gp4Xp5TpL._SL210_.jpg", 91, "単独作業による短編「ほしのこえ」でアニメーションの新たな可能性を示した新鋭、新海誠の初長編作品。北と南に分断された架空の日本を舞台に、高くそびえる北の塔へと飛ぶことを夢見る少年二人と少女。数奇な運命にほんろうされる彼らの友情と愛、葛藤を描く。厳しい選択を迫られ、現実を乗り越えようとする若者たちの成長に、夢の終焉とさらに続く人生を浮き彫りにした。そのSF風の設定ながら地に足のついたドラマに、胸を揺さぶられるに違いない。新海監督の持ち味である美しい風景の描写も印象的だ。青森の中学生、ヒロキとタクヤは、北の塔に飛ぶことを夢見て飛行機作りに励む。しかし、彼らに共感していた同級生サユリの転校により挫折。やがてサユリが原因不明の昏睡に陥り……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボクサー", "ボクサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "足の悪い天馬は、チャンピオンになることを夢見てジムに通っているが、初試合に敗れたためジムは彼を見捨てる。天馬はあきらめきれず元東洋チャンピオンの隼にコーチを頼む。寺山修司初の商業作品だが、随所に寺山らしさを見せる。具志堅用高、白井義男、ファイティング原田、ガッツ石松などが特別出演し、ガッツあふれるファイティング・シーンで魅了する。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("War Horse", "戦火の馬", "", "", "http://image.eiga.k-img.com/images/movie/55976/315x210.jpg?1609", 147, "1982年にイギリスで発表され、舞台化もされて成功を収めたマイケル・モーパーゴの小説を、スティーブン・スピルバーグ監督が映画化。第1次大戦下、農家の少年アルバートは毎日を共にしていた農耕馬のジョーイを軍馬として騎馬隊に売られてしまう。フランスの戦地に行くことになったジョーイを探すため、アルバートは徴兵年齢に満たないにもかかわらず入隊し、激戦下のフランスへと向かう。主人公アルバート役にイギリスの若手俳優ジェレミー・アーバイン。そのほかエミリー・ワトソン、デビッド・シューリス、ピーター・ミュランら名優が脇を固める。", "2012-03-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大怪獣バトル　ウルトラ銀河伝説 THE MOVIE", "大怪獣バトル　ウルトラ銀河伝説 THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/54696/315x210.jpg?4500", 0, "地球の遥か彼方にあるウルトラマンたちの故郷、M78星雲「光の国」で、悪の戦士ウルトラマン・ベリアルが復活してしまう。凶暴な怪獣を操り破壊を楽しむベリアルを止めようと、歴代のウルトラ戦士たちが立ち上がるが、ベリアルの猛攻撃に耐え切れず次々と倒れていく。負傷したウルトラマンメビウスは、リベラルと同様に怪獣を操れるレイを連れて戦いに挑むが……。", "2009-12-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHALE RIDER", "クジラの島の少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ニュージーランドのマオリ族が信じ続ける伝説をもとにした感動ドラマ。祖先と言い伝わる勇者の血を継いだ少女の不思議な運命を描く。スタッフ、キャストの多くがマオリ族という点も注目だ。ニュージーランドで暮らすマオリ族の少女、パイケア。族長の血を引く彼女は、人一倍マオリの伝統を大切にしながら成長する。だが代々、マオリの族長を務めるのは男と決めつける祖父は彼女を後継者と認めず、村の若者から後継者を選ぶことに。その矢先、パイケアは一つの奇跡を起こす。主演のK・C・ヒューズは最年少でアカデミー主演女優賞にノミネートされた。", "2003-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAMBONE AND HILLIE", "ハンボーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "主人のヒリーとロサンゼルスからニューヨークへやって来た犬のハンボーンは、何気ない子供のいたずらから、乗るはずの飛行機に遅れることになる。見知らぬ街に一匹残されたハンボーンは5500キロの旅をする。現代版「ラッシー」といった趣の冒険ドラマ。ビデオ、DVDタイトルは「わんワンロード」。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝・ミナミの帝王スペシャルVer．５０／金貸しの掟", "難波金融伝・ミナミの帝王スペシャルVer．５０／金貸しの掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "Vシネマの帝王・竹内力が演じる大阪の高利貸し・銀次郎の活躍を描く人気シリーズ通算50作目。非道な手口で暗躍する男・堂本に、銀次郎が怒りの制裁を下す。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大人になった夏", "大人になった夏", "", "", "http://image.eiga.k-img.com/images/movie/55516/315x210.jpg?1372", 0, "エイベックスが同社の新人俳優やアーティストを起用し製作する青春映画シリーズ「ニュースター・シネマ・コレクション」の第2弾。陸上部の藍は、なかなか結果が出せずスランプに陥っていた。そんなとき、同級生の桃子とさくらに誘われ、桃子の祖母が営む民宿を手伝うことに。料理、掃除、洗濯など、初めて経験することに戸惑う藍たちだったが、おばあちゃんが倒れ窮地に立たされると、力を合わせて民宿を切り盛りしていく。雑誌「ピチレモン」のモデル・江野沢愛美らが思春期の女の子を熱演している。", "2010-06-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浅間の鴉", "浅間の鴉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "長谷川伸の名作『沓掛時次郎』を映画化。人情時代劇の隠れた名作。一宿一飯の恩義から旅鴉の沓掛時次郎は、六ツ田の三造を斬ってしまう。虫の息の三造から、女房のおきぬと子供の太郎吉の行末を頼まれた時次郎は、それを受けた。彼は、ヤクザな稼業から足を洗って、おきぬと太郎吉のために生きようとするのだが……。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("方世玉與煕官／HEROES　TWO", "嵐を呼ぶドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "少林寺焼き討ちという清朝時代の実話をもとにした武術アクション。清朝兵士によって焼き討ちされた少林寺から、重傷を負いながらも逃げ延びた修行者フン。将軍は執拗に追跡し、ついには捕らえる。逮捕に図らずも協力してしまった同門のフォンは、処刑寸前のフンを救おうとするが、そこには将軍の罠が……。フンは洪煕官、フォンは方世玉という、中国に実在したのちの英雄。", "1972-01-01", "6", 0, NOW(), NOW());
