INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOWARDS END", "ハワーズ・エンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "中流階級だが理想的に生きる知的なシュレーゲル家と、資産家で現実的なウィルコックス家。正反対な両家はドイツ旅行中に親しくなり、ウィルコックス家の別荘“ハワーズ・エンド”に、シュレーゲル家の次女ヘレンが招かれる。ヘレンは次男ポールに一目惚れし、二人は恋に落ちるが、やがて崩壊。そして運の悪いことにロンドンのシュレーゲル家の向かいにウィルコックス家が引っ越してくる。閉塞的なイギリスの階級社会に生きる人々の愛憎を描き出した作品。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生きる", "生きる", "", "", "http://ecx.images-amazon.com/images/I/51BKlLo%2BrgL._SL210_.jpg", 143, "市役所の市民課長・渡辺は30年間無欠勤、事なかれ主義の模範的役人。ある日、渡辺は自分が胃癌で余命幾ばくもないと知る。絶望に陥った渡辺は、歓楽街をさまよい飲み慣れない酒を飲む。自分の人生とは一体何だったのか……。渡辺は人間が本当に生きるということの意味を考え始め、そして、初めて真剣に役所の申請書類に目を通す。そこで彼の目に留まったのが市民から出されていた下水溜まりの埋め立てと小公園建設に関する陳情書だった。この作品は非人間的な官僚主義を痛烈に批判するとともに、人間が生きることについての哲学をも示した名作である。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUINEVERE", "写真家の女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "親子ほども年の違うカメラマンに魅かれた女性が肉体的に開花させられるうち、自らの写真家としての才能にも気付いていく。アブノーマルでありながら、どこか普遍的な男女の関係を描いたミステリアス・ロマンス。ジーナ・ガーションが少ない出番ながら鮮烈な印象を残す。1999年サンダンス映画祭最優秀脚本賞、ドービル映画祭審査員特別賞受賞。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Double indemnity", "深夜の告白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "　ビリー・ワイルダー初期の作品にして、フィルムノワールの傑作といわれるサスペンス映画。保険外交員のウォルターは、訪問先の人妻から夫に内緒で、彼に傷害保険をかけたいと相談される。ただならぬ様子に一旦は拒絶するが、美しい妻の魅力にとらわれ夫殺害の共犯者に堕ちてゆく。殺人シーンでは、現場に居合わせる人物の表情を長くとらえ、間接的なリアリズムを演出。2人を取り巻く脇のキャラクターも秀逸である。", "1953-12-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖怪大戦争", "妖怪大戦争", "", "", "http://image.eiga.k-img.com/images/movie/1756/315x210.jpg?3833", 124, "「着信アリ」の三池崇史監督の妖怪アドベンチャー。少年タダシは、怪人・加藤保憲が妖怪と機械を融合させた怪物によって世界を支配しようとしていることを知り、日本古来の妖怪たちといっしょにその陰謀を阻止しようとする。忌野清志郎のぬらりひょん、根岸季衣の砂かけばばあ、竹中直人の油すまし、岡村隆史の小豆洗い、荒俣宏の山ン本五郎佐衛門、京極夏彦の神ン野悪五郎、水木しげるの妖怪大翁ら、ユニークな妖怪たちが大挙登場。", "2005-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE YOUNG RACERS", "ヤングレーサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "レーサーあがりの記者スティーブは、婚約者のモニカが売れっ子レーサーのジョーと浮気しているところを目撃してしまった。怒りに燃えた彼は再びレーサーに身を転じ、ヨーロッパ各地のレースに出場するジョーに男同士の勝負を挑んでいく。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蔵の中", "蔵の中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "京都の旧家の蔵の中で暮らす、肺炎で隔離された聾唖（ろうあ）の姉と、彼女を慕って蔵の中へ入ってきた美少年の弟。横溝正史が戦前に発表した作品のおどろおどろしい退廃美と、監督・高林の資質がぴったりと符合した幻想エロチシズム映画。ニューハーフ・松原留美子が話題に。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎真実一路", "男はつらいよ・寅次郎真実一路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "“とらや”の面々とケンカして飛び出した寅さんが上野の焼き鳥屋で出会った一流証券会社の課長。彼の家で美しい妻と知り合ったと思うや否や、夫の課長さんが家出蒸発してしまった。寅は奥さんと一緒に鹿児島まで夫探しの旅に出る。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH WISH II", "ロサンゼルス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "犯罪都市ロサンゼルスが舞台。数年前チンピラに妻を殺された設計技師が再び襲われ、娘を強姦されたあげく死に追いやられる。復讐の鬼となった技師が一人でチンピラ５人組に立ち向かう……。1974年製作の犯罪アクション「狼よさらば」の続編で、“デスウィッシュ”シリーズ第２作。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POSTMAN BLUES　ポストマン・ブルース", "POSTMAN BLUES　ポストマン・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「弾丸ランナー」で初めて監督業に挑戦し、好評を得た俳優出身のサブが、再び原案と脚本を兼任して挑んだ監督第２作。若き郵便局員を主人公に、ロマンティックな空気も漂わせた、疾走感たっぷりのユニークなハードボイルドだ。一介の郵便局員として暮らしていた沢木龍一は、ある日、高校時代の同級生、野口のアパートへ荷物を届ける。そこで目にしたのは、ヤクザとなって指をツメていた野口の悲痛な表情だった。彼に“ガキの頃みたいにドキドキすることはないのか？”と問われた沢木は、自宅に戻ってから未配達の郵便物を次々と開封し始める。その一通に、ガンに冒された少女の存在を知った彼は、翌日、病院を訪ね、彼女と文通を始めることにする。そんな彼が指名手配の犯人として警察に追われることになってしまう……。殺し屋、野口を巻き込んでの一大自転車レースとなるクライマックスが見もの。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シティハンター　ベイシティウォーズ", "シティハンター　ベイシティウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "劇場版シリーズ第2弾。美男美女の迷コンビが、ケンカを繰り返しながらも協力して都会の悪を退治する。シンプルなストーリー展開と洒脱なセリフの応酬も魅力の快作だ。女に弱いがスナイパーの腕は金メダル級の冴羽と、彼の元相棒で死んだ兄に代わり任務をこなす妹の槙村香が、危険な任務を遂行していく。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どチンピラ", "どチンピラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "人気劇画のビデオ・シリーズを映画版にしたもの。“どんな女性でも天国にエスコートしますよ”をうたい文句に、コマシ屋稼業に精を出す海藤。“どチンピラ”と呼ばれる彼はある日、城西組に追われる由奈を助け一夜をともにする。一方、港北組の情婦・弥生とも関係を持ってしまい、奇妙な三角関係が続くなか、組同士の抗争に巻き込まれていくのだった……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VASSA", "ワッサ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "ロシアが世界に誇る文豪ゴーリキーの戯曲『ワッサ・ジェレンズノワ』を映画化した作品。第一次世界大戦前夜の1913年。裁判係争中の夫に代わり、会社と邸宅を切り回すワッサ。革命家である養女が屋敷に戻ってくるが、ワッサには養女の思想と行動をまったく受け入れることができず対立するが……。", "1983-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HENRY: PORTRAIT OF A SERIAL KILLER", "ヘンリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "300人以上の女性を殺した連続殺人鬼ヘンリー・リー・ルーカス。実在のこの人物の伝記をもとに、この作品がデビューとなるJ・マクノートンが監督した異色作。もともとは低予算のホラー映画として製作されたが、“血も流れずセックスシーンもない”と映画会社がオクラ入りを決め、４年間も日の目を見ることがなかったいわくつきの映画だ。シカゴに住むオーティスのもとに妹が結婚先から出戻ってくる。オーティスはヘンリーという男と同居していたが、妹はヘンリーのどこか暗いが紳士的な態度に惹かれ始める。しかしヘンリーは女性を無差別に殺しまわっている殺人鬼だった……。殺人の動機をまったく語らず、殺人シーンさえほとんど見せないにもかかわらず、その衝撃は圧倒的だ。ビデオ発売時タイトルは「ヘンリー　ある連続殺人鬼の記録」。続編に「ヘンリー もう一つの連続殺人鬼の記録」がある。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EROTIC　TALES", "アート・オブ・エロス／　監督たちの晩餐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 167, "欧米の著名監督がエロスをテーマに撮った個性あふれる短編映画集。ビデオ＆DVD化の際には劇場公開された６作品に加え、未公開の６編も収録された。中でもアカデミー短編実写賞候補になった「マリッジブルーの愉しみ」(S・シーデルマン監督)、田舎の美少女が謎めいた男から性の手ほどきを受ける「悪魔のレッスン」(J・マイェフスキ監督)、アイスランドの幻想的な氷の空間を舞台にした「氷の愛撫」(F・T・フリドリクソン監督)の出来ばえが秀逸。", "1993-01-01", "1,3,20,17,42,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お葬式", "お葬式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "伊丹十三の初監督作品。自らの体験をもとに１週間で書き上げたシナリオを、ユーモアとアイロニーを織りまぜて軽妙な演出で映画化した。「お葬式」というタイトルは、公開当初は暗くて誰も観にこないと懸念されたが、予想外の大ヒットを記録し、各映画賞を総ナメにした。妻の父が急死したため、俳優の井上佗助はビデオで葬式の時の挨拶を勉強し、式に臨む。式には親戚をはじめ実に多彩な人物たちが集まっていた。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOCA A BOCA", "電話でアモーレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "恋と陰謀の騒動が展開する、陽気なラブ・コメディ。アントニオ・バンデラスに憧れる俳優志望のヴィクトルは、生活のためにテレフォン・セックスの仕事を開始。ところが、最初の客であるゲイの外科医に惚れられたことから、騒動に巻き込まれる。ハリウッド・デビューを懸けた大事なオーディションの日、ヴィクトルは外科医の妻とその愛人に命を狙われるハメに……。「ゴールデン・ボールズ」でラテン系のエロティック・ギャグを連発したJ・バルデムが、ヴィクトルをパワフルに怪演。", "1995-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EASY　VIRTUE", "ふしだらな女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "巨匠A・ヒッチコックの映画手法が冴えを見せる、監督第５作目に当たるサイレント作品。当時の人気作家カワードによる戯曲の映画化。華やかなリヴィエラを舞台にしたり、オーバーラップの効果的な使い方など、映画としてのオリジナリティーを提示する。離婚が厳禁だった頃の話。恋に落ちた末、恋人を自殺に追いやってしまったヒロイン。人々から非難される彼女だが、さらに悲惨な事態が巻き起こる。", "1928-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MODERATO CANTABILE", "雨のしのび逢い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ボルドーに近いブレの町で殺人事件が起きた。夫が製鉄所を経営するアンヌは、偶然耳にした殺された女の最期の悲鳴が気になり、若い工員のショーバンに事件のことをたずねる。だが、そのうちに……。単調な生活の流れのなかに生じた心の亀裂を描くメロドラマ。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボールZ　激突！！　100億パワーの戦士たち", "ドラゴンボールZ　激突！！　100億パワーの戦士たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 46, "SFモードも加わってパワー・アップした劇場版シリーズの第9作。地球と友好的なナメック星が機械惑星に襲われたため、孫悟空たちが助けに向かう。そこで待ち構えていたのは、かつて悟空に殺された後、サイボーグとしてより強靭になって再生した戦士メタルクウラだった。激闘の末、悟空は深い傷を負ってしまう。シリーズ屈指のヒットを記録した。DVDは『DRAGON BOX THE MOVIES』に収録。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　CHIMP", "旅立ちの汽笛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ブランコ」「あの娘と自転車に乗って」に続く、A・アブディカリコフ監督の少年時代の記憶に基づいた三部作の最終編。主人公は兵役を間近に控えた17歳の少年チンプ。そんな彼が酒浸りの父親との葛藤、ひそかに思いを寄せている少女との触れ合いなど様々な出来事を経験し、町を汽車で旅立っていくまでを綴る。キルギスタンの自然のざわめき、線路沿いの風景などを捉えた叙情的な映像の中に、多感な少年の揺れる思いがみずみずしく刻み込まれている。", "2001-01-01", "2,77,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MESCHUGGE", "ショコラーデ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "現在も残るナチスの影にラブロマンスを絡めた、ドイツ製のサスペンス。N.Y.で起きたユダヤ人老婆の殺人事件。事件の発見者であるドイツ出身の女性と、被害者の息子は、事件の背景にある戦時中の秘密を探る。そんな彼らの間に芽生える愛と、運命のいたずらをスリリングに描写。実話をもとにしているだけに、見ごたえもひとしおだ。大富豪の孫娘レナは、ある日、ホテルで瀕死の老婦人を助け、その息子デイビッドと出会う。一目で惹かれあった二人だが、やがて訪れた老婦人の死によって、二人は愛し合ってはいけない数奇な運命を背負っていることを知る。", "1998-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARLEM NIGHTS", "ハーレム・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "E・マーフィが15歳の時から抱いていた企画を、主演のみならず製作総指揮、監督、脚本まで手掛けた野心作。1938年の禁酒法下のニューヨーク、大繁盛のクラブを乗っ取ろうとギャングが乗り出した。オーナーの養子クイックは勇敢にもギャングに立ち向かうが……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO LOOKING BACK", "ノー・ルッキング・バック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ニューヨーク州の小さな田舎町に暮らすクローディアは、幼なじみのマイケルから結婚を迫られていた。そこに、かつての恋人チャーリーが町に戻ってきたことから、彼女の心は揺れ動き始める……。二人の男の間で苦悩しながらも、やがて成長していく女の旅立ちを描いた作品。全編寂しげな映像が、ヒロインの心の切なさを増幅させている。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VAMPYR", "吸血鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "映画史上の古典的名作の１本であるとともに、C・T・ドライエルの名を世界中に知らしめることとなった作品。不気味な城に宿をとったアランという男が、城の中で人間の生き血を吸う吸血鬼の女と対決し、ついには彼女を退治する。観客の創造力を重視した、高貴な雰囲気のホラー映画。ビデオ、DVDタイトルは「ヴァンパイア」で発売されているものもある。", "1931-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SINBAD AND THE EYE OF TIGER", "シンドバッド虎の目大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "人形アニメーション特殊効果の第一人者、ハリーハウゼン製作の冒険映画。魔法使いに呪いをかけられた王子を救うべく、謎の秘境へ旅立ったシンドバッド。彼の活躍や、怪物たちとの戦いなどが、多重合成の画面によってスリリングに展開される一編。", "1977-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("19〈ナインティーン〉", "19〈ナインティーン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "実話をもとにした青春劇。大学生の宇佐美は突然３人組の青年に拉致される。３人は目的もなく、ただ車で旅をするだけだったが逃げようとすると暴力を振るう。痛い目に遭いながらも宇佐美は彼らの奔放さに奇妙な共感を抱き始め……。これが初監督作となった渡辺一志が、現代の若者の冷めた心情をリアルに描写。俳優としても３人組の一人に扮し、カリスマ的な存在感を放っている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Heart of the Game", "女バス", "", "", "http://image.eiga.k-img.com/images/movie/54186/315x210.jpg?4291", 98, "アメリカ・シアトルの高校女子バスケットボール部に7年間密着したスポーツ・ドキュメンタリー。本作が劇場デビューとなるウォード・セリル監督は、ワシントン州のスポーツ名門校の弱小女子バスケ部“ラフライダーズ”が州チャンピオンに輝くまでの7年間を撮り続けた。試合の模様だけでなく選手の日常もカメラが捉え、アメリカのハイティーンにおける人種や格差までも問題提起している。", "2008-12-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誰も死なない", "誰も死なない", "", "", "http://image.eiga.k-img.com/images/movie/54226/315x210.jpg?4365", 34, "海外でも幅広く活躍するクリエイターの村上隆が製作総指揮を務める、サバイバルゲームに熱中する少女たちを描いた青春ショートムービー。監督は、村上の1番弟子でありオタク文化をメインに表現してきたアーティストのMr.（ミスター）。中学2年生の仲良し5人組が結成するサバイバルゲームのチーム“うさぎ組”は、1度は敗北したライバルチームにいつものお好み焼き屋でリベンジを誓い、それぞれのトレーニングに励み、再戦の時を待つ。", "2008-11-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("快傑まぼろし頭巾", "快傑まぼろし頭巾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "この作品のみ加藤泰の原作で、黒頭巾の正体は、薩摩藩士・村木弦之介になっている。だが、設定、ストーリーから見て、まぎれもなく“黒頭巾”もの。鳥羽伏見の戦いに敗れた幕府軍は、起死回生を願い、科学者・香川春山が発明した強力火薬を手に入れようとする。これを阻止するため、黒頭巾が大活躍。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLARETTA", "クラレッタ・ペタッチの伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ムッソリーニの愛人で、彼とともに銃殺され、逆さ吊りにされた悲劇のヒロイン、クラレッタ・ペタッチの半生を描いた作品。一人の女性ジャーナリストが、クラレッタの実在の妹に会ってインタビューをする回想形式で構成されている。主演のカルディナーレはこの役を演じたことで、イタリア映画界のひんしゅくを買った。", "1984-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEYOND THE MAT", "ビヨンド・ザ・マット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "プロレスの世界に生きる男たちの素顔、心情に迫ったドキュメンタリー。エディ・マーフィの“ナッティ・プロフェッサー”シリーズなどを手掛けた脚本家B・W・ブラウスティンが監督。子供のころからプロレス・ファンと言う彼が５年の年月をかけて、人気団体WWFの裏側、引退を考える伝説の人テリー・ファンク、かつてのWWFのスターで、今は地方の独立リーグでリングに上がるジェイク・ロバーツの姿を追う。特にWWFのミック・フォーリーが対戦相手のザ・ロックと試合前に打ち合わせをする様子はショッキングだが、試合で大流血して控え室に戻ったミックが傷口を縫合している姿にプロレスに命を懸ける男の意地を見ることができる。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火焼島之横行覇王／THE JAIL IN BURNING ISLAND", "炎の大捜査線2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "絶海の監獄島“火焼島”を舞台にしたハード・アクションの続編。麻薬組織の罠に落ちて逮捕された刑事ヤンは、孤島の監獄、火焼島に送られる。そこは暴力に支配された地獄のような場所。苛酷なリンチに耐えながら、ヤンは同房のリンとともに所内のボクシング大会の騒ぎに乗じて、脱獄を図る……。金城武の硬派なタフガイぶりが見もの。", "1997-01-01", "6,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LET’S MAKE LOVE", "恋をしましょう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "M・モンローとY・モンタンという二大スターが共演し、名匠G・キューカーが監督したミュージカル映画。モンローのキュートなお色気が見ものである。億万長者で色ごとも達者な青年が、身元を隠して自分を皮肉った芝居のオーディションを受け、そこでセクシーに踊る若く美しい女に一目惚れしたことから、やがて二人の間に恋が芽生えて……。サミー・カーン作詞、ジェームズ・バン・ヒューゼン作曲の『恋をしましょう』など５曲のほか、コール・ポーターの名曲『私の心はパパのもの』も歌われる。撮影中、モンローとモンタンが映画を地でいくかのような恋に落ちたというエピソードもあった。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SURVIVING PICASSO", "サバイビング・ピカソ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "天才芸術家パブロ・ピカソと彼を愛したフランソワーズの話をJ・アイヴォリーが映画化した作品。1943年、画学生のフランソワーズはパリのビストロでピカソと出会い、いつでもアトリエに遊びに来るように言われる。それ以来、二人は師弟関係となるが、それが愛に変わるのは時間の問題だった。周囲が反対すればするほど38歳年上のピカソへの思いは高まっていくフランソワーズ。その頃、ピカソには妻と二人の愛人がいて、女同士を張り合わせて楽しんでいた。そのことはフランソワーズを傷つけるが、ピカソと付き合うことで芸術家としても成長し続けていく。やがて、長男のクロードと長女のパブロが誕生。しかし、それでもピカソは変わらず、新しい女と関係を持つ。ついにフランソワーズは、ピカソの元を離れパリで暮らすことに。数年後、お互いに落ち着きを取り戻したピカソとフランソワーズは、愛よりも深い友情で結びついていたのだった。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("被告山杠爺／THE ACCUSED UNCLE SHANGANG", "正義の行方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "四川省の山奥にあるトイ村を治めている党支部書記、チャオじいさんの家の前で女が首吊り自殺をする。女の夫のハンはチャオが殺したと叫ぶが、じいさんは無視をする。可愛がってた孫のフーから、じいちゃんはハンの奥さんを自殺に追い込んだのかと問われても、自信は揺るがなかった。そこへ、“ハンの女房の自殺について調べろ”という匿名の手紙を受けた検察院が調査に乗り出してくる。女検察官のスーは村の人たちに話を聞いていくうちに、チャオじいさんが皆に慕われていることを知る……。", "1994-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXIT IN RED", "欲情の媚薬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "M・ローク主演のエロティックなフィルム・ノワール。患者の不可解な死をきっかけに、ロサンゼルスからパームスプリングスに流れてきた精神科医エド。彼がそこで出会ったミステリアスな人妻との情事をきっかけに、殺人事件の罠にはめられていく姿を、荒涼たる砂漠をバックに描き出す。ロークと熱い絡みを見せる二人の美女は、モデル出身のA・スコフィールドと「蘭の女」のC・オーティス。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Good Morning President", "グッドモーニング・プレジデント", "", "", "http://image.eiga.k-img.com/images/movie/55496/315x210.jpg?2646", 132, "ロトで多額の賞金を手にした任期終了間近の大統領、男手ひとつで息子を育てる若き大統領、多忙から夫とすれ違う日々をおくる韓国初の女性大統領。3代にわたる大統領の素顔を、韓国のホワイトハウス・青瓦台の料理人の視点で描く。「トンマッコルへようこそ」のチャン・ジン監督がメガホンをとり、「ブラザーフッド」のチャン・ドンゴンが4年ぶりにスクリーンに復帰した。", "2010-07-24", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・男はつらいよ", "新・男はつらいよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "映画の前身であったTV版『男はつらいよ』のディレクター・小林俊一を監督に迎えた一編。競馬で当てた大金で、おじさん夫婦をハワイ旅行に招待しようとする寅次郎の珍騒動。“とらや”に下宿した美しい幼稚園の先生への恋ごころは高まるが……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAIKIKI　WEDDING", "ワイキキの結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「愉快なリズム」に続いて、B・クロスビー、B・バーンズ、M・レイが共演したロマンティック・コメディ。ハワイのパイナップル缶詰会社の宣伝部員トニーとジャッドが、旅行客の女性二人組ジョージアとマートルと知り合う。だが、彼女たちが持っていた黒真珠が原因で、４人は原住民に離れ島へ監禁されてしまう。度重なる災難の末に、二組のカップルが誕生するまでを、軽快なタッチで描く。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("徳川の女帝 大奥", "徳川の女帝 大奥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "徳川十一代将軍家斉の頃、住職の娘から将軍付のお手付中臈になったお美代と、それを取り巻く人々の策謀を描いた大奥もの。女同士が火花を散らす凄絶な戦いを描いている。主演のお美代を演じるのは「キャバレー日記」以来、にっかつには久々の竹井みどり。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLITHE SPIRIT", "陽気な幽霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "霊媒を題材にスリラーを書こうとした小説家が術師を呼んで実験したところ、５年前に死んだ妻の幽霊が戻ってきて、現在の妻と３人の間にとんでもないヤキモチ騒ぎがもち上がる。小品ながらさすが巨匠リーンの演出。皮肉で鋭い笑いにたっぷり堪能させられる。", "1945-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本暴力団・殺しの盃", "日本暴力団・殺しの盃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "鶴田浩二扮する主人公のヤクザは、戦中派の臭いが全身から漂い、生き方よりも死に方を常に考えている。鶴田浩二が、疲労感に満ちた戦中派ヤクザを熱演する。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THIS ISLAND EARTH", "宇宙水爆戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "1950年代ハリウッドSF映画の代表作の一つ。惑星間戦争で形勢が不利になり、地球に助けを求めにやって来た惑星メタルーナの科学者エグゼターは、原子力科学者のカップルを強引に自分の星へ連れて行く。二人はメタルーナで、彼らの侵略的な地球移民計画を知り協力を拒否、危うく精神改造をされかかるが、エグゼターのおかげでメタルーナを脱出することができる。宇宙の彼方で繰り広げられる水爆戦、メタルーナの地下都市、円盤型宇宙船、そしてメタルーナ・ミュータントなど、にぎやかな小道具と荒涼としたイメージが素晴らしい。セット・デザインと色彩(テクニカラー)の美しさも特筆に値する。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’INTESSA", "エロスを弾く女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「ソドムの市」でアブノーマル・エロスの第一人者となったP・P・パゾリーニ原案の作品。大胆なセックス描写を絡めながら、若い男女の性に対する欲望を様々な角度から表現したという本作は、手錠プレイ、トイレファック、覗きプレイ、SMプレイなど、まさにアブノーマル・セックスのオンパレード。エロス文学の背徳性を徹底的に追究している。", "1995-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("May 18", "光州5・18", "", "", "http://image.eiga.k-img.com/images/movie/53376/315x210.jpg?2304", 121, "80年5月に韓国で多くの死傷者を出した“光州事件”を題材とする衝撃の人間ドラマ。両親を早くに亡くして以来、高校生の弟ジヌの親代わりを務めてきた心優しい青年ミヌ。ある日彼は、想いを寄せる女性シネとジヌとの3人で映画を観に行くことに。ところが、映画館の外で民主化を求める学生たちと軍との衝突が起こり……。主人公ミヌを「殺人の追憶」のキム・サンギョン、その弟を「王の男」のイ・ジュンギが熱演する。", "2008-05-10", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Conan the Barbarian", "コナン・ザ・バーバリアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "", "2012-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("This Boy's Life", "ボーイズ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "暴力的な恋人から逃げて、息子のトビーと新しい生活を夢みるキャロラインは、社交的な男ドワイトと結婚。だが、多感なトビーは威圧的なドワイトになじめない。その嫌悪感はやがてピークに達し……。青年期の苦い思い出を綴ったT・ウルフによる同名の自伝的小説を、豪華なスタッフ、キャストで映像化。", "1993-11-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　KINGDOMII", "キングダムII・　第３章／第４章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 291, "カルト的支持を得た異色ホラー第２作。病院内をさまよう霊魂、医師たちの確執、さらに秘密組織の発覚で異様な雰囲気が充満していた前作に比べ、今回はブラック・ユーモアがエスカレート。予測不可能な展開と、エキセントリックな映像感覚で再び観客をグイグイ引き込む怪作となった。巨大病院キングダムでインターンの女性ユディットが男児を生んだ。創設者クルーガーとウリ二つの顔をしたその赤ん坊は、異常な速さで成長していく。一方、少女モナの脳外科手術に失敗したヘルマー医師は、敵対するクロウスホイを秘薬でゾンビに仕立てようと悪戦苦闘。院内に悪魔が存在していることに気付いた霊媒士のドルッセ夫人は、心霊術でコンタクトを取ることに成功し、ユディットの赤ん坊との恐るべき関係を突き止めるのだが……。", "1997-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WALTZ KING", "ウィーンの森の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ワルツ王ことヨハン・シュトラウス一世と息子たちの確執を物語の軸に描く音楽映画。息子たちが音楽家になることに反対だった父ヨハン。しかし懸命に音楽に打ち込む長男シャニの姿に心打たれた彼は、やがてすべてを許す……。『ラデッキー行進曲』『こうもり』はじめ名曲の数々が聞きどころ。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チャカ・LONELY HITMAN", "チャカ・LONELY HITMAN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "竹内力が従来のギラギラした個性を封印して挑んだ、やさしいヤクザの物語。主人公は“チャカ”と恐れられるスゴ腕の殺し屋だが、体が弱く持病持ち。病院で出会った気丈な看護婦と恋に落ち、家庭を持つ夢を見るが、皮肉な運命が彼を待ち受ける。展開はオーソドックスだが、いつもスウェットスーツ姿のヒットマンが日常的な哀感を漂わせ、異彩を放つ。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ISOLA DEGLI UOMINI PESCE", "ドクター・モリスの島／フィッシュマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "バミューダ海域に近いカリブ海を漂流するボートに乗っていた軍医クロードらは、何者かに襲われ、気付くと孤島に漂流していた。島には不気味な原住民と白人夫婦が住んでいた。この島に隠された謎とは……。迫りくるモンスターがユニークなホラー映画。", "1979-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUTUREWORLD", "未来世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「ウエストワールド」の続編。世界一の娯楽施設、デロス・ランドが再開した。各国高官らとともに招待された敏腕記者チャックは、オーナーの陰謀に気付く。高官をロボットにすり替え、世界征服を企てていた。本物と偽者、二人のチャックの対決がスリリング。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EARLY SPRING", "アーリー・スプリング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "詩を書くのが好きな少女エスターは、隣りのアパートに越してきた女性の美しさに憧れるようになる。だがその女性は娼婦だった。理想と現実の残酷なギャップにショックを受けながら、エスターは大人の世界に一歩ずつ踏み出していく……。女流監督A・H・イェンセンが思春期の少女の姿をみずみずしく描いた佳作。", "1986-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スタア", "スタア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "スター同士の男女が結婚、その新居披露パーティーに招かれた評論家・作曲家・芸能記者たちを筒井康隆的ブラック・ユーモアで鋭く風刺したスラップスティック映画。原作者の筒井自身が地震研究所の博士役で怪演、タモリのヒトラーなども笑わせる。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KING AND I", "王様と私", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "Y・ブリンナーの代表作。舞台は、絢爛豪華なシャム王家の宮殿。ここに家庭教師としてやって来たイギリスの女性が、封建的な王家に文明の息吹を持ち込み、さらに王からも愛されるようになる。ドラマティックな音楽に優美なセット、そして素晴らしいキャストなど、品位と感動を兼ねそなえた名作。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パッチギ！", "パッチギ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "「ゲロッパ！」の井筒和幸監督が60年代の京都を舞台に若者たちの成長を描いた青春群像劇。日本人と在日朝鮮人の高校生が巻き起こす事件を中心に、若者の普遍的な純粋さや、恋愛、葛藤をダイナミックに描く感動作だ。複数のエピソードとシンクロする音楽の使い方も印象的。1968年、京都。高校2年生の康介は親友の紀男と一緒に、敵対する朝鮮高校へ親善サッカー試合を申し込みに出かけるが……。", "2005-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自尊を弦の響きにのせて 96歳のチェリスト　青木十良", "自尊を弦の響きにのせて 96歳のチェリスト　青木十良", "", "", "http://image.eiga.k-img.com/images/movie/58036/315x210.jpg?3396", 93, "1915年に生まれ、戦前からチェロ奏者として活躍している青木十良の90歳からの6年間を追ったドキュメンタリー。80代になってから始めた念願のバッハ「無伴奏チェロ組曲」の録音を10年間かけて成し遂げた姿や、児童福祉施設「ねむの木学園」の園長として知られる宮城まり子との60年ぶりの再会、40年ぶりに再会したバイオリニストの森悠子と、森が音楽監督を務める長岡京室内アンサンブルとの初共演を果たした姿などが映し出される。", "2012-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALI　G　INDAHOUSE", "アリ・G", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "英国のTVで人気を呼んだお笑いキャラ、アリ・Gを主人公にしたコメディ。ヒップホップのノリで下ネタと不道徳ギャグを連発する、主人公のおバカな活躍がくだらなくも痛快だ。お気楽に過ごしているヒップホップ・グループのリーダー、アリ・G。街の娯楽施設の閉鎖を防ごうとしていた彼は英国の副首相に認められ、議員選に立候補して当選してしまう。だが、その裏には政界トップの座を狙う副首相の陰謀が隠されていた。", "2002-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Grand Bleu: Version Longue", "グラン・ブルー　完全版　デジタル・レストア・バージョン", "", "", "http://image.eiga.k-img.com/images/movie/56596/315x210.jpg?3366", 0, "1988年の初公開時にフランスで社会現象を巻き起こし、以降も世界中で親しまれてきたリュック・ベッソン監督の名作「グラン・ブルー」を、デジタル処理によってフィルムの傷や汚れを修復した「デジタル・レストア・バージョン」。フリーダイビングにすべてを賭ける2人の青年の友情と対立、そんな彼らに魅せられてしまった女性の葛藤を、ジャン・レノ、ロザンナ・アークエット、ジャン＝マルク・バール共演で描く。", "2010-08-07", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONAN THE DESTROYER", "キング・オブ・デストロイヤー／　コナンPART２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "人気コミック『コナン』シリーズの映画化第２作。冒険児コナンが女王の依頼で、富と権力をもたらす“ダゴスの角”を手に入れるため、危険な旅に出る。前作「コナン・ザ・グレート」に続きコナン役シュワルツェネッガーが、精悍な肉体美と怪力を存分に披露。彼が次々と困難を乗り越えていく姿が痛快な大活劇ファンタジー。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴力教室", "暴力教室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "松田優作の初主演作にあたり、のちに続く“遊戯”シリーズなど、バイオレンス・アクションの先がけとなった作品。不良暴力学生集団・クールスが支配する学園に新任教師・溝口勝利がやって来る。溝口は校内の悪を一掃すべく暴力と陰謀に立ち向かう。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の舞妓はん", "花の舞妓はん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "祇園随一の評判の美人舞妓・雪路と、若いトラック運転手・浩太郎の運命的な出会いと別れを描いた、橋幸夫と倍賞千恵子の顔合わせによる歌謡メロドラマ。“しきたり”のために好き合った者たちが互いに会うことも出来ぬ花柳界。華やかさの裏で、夜桜の舞う公園を去って行く橋幸夫のうしろ姿が心に残る。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三匹の牝蜂", "三匹の牝蜂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "人の波に埋まった万博会場を舞台に荒稼ぎをするズベ公３人組が、暴力団と争いながらもしぶとく生きていく。新人時代の大原麗子が、イキのいいズベ公を演じる。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VALERIE AND A HER WEEK OF WONDER", "闇のバイブル／聖少女の詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "1960年代後半の怪奇映画ブームの象徴的作品。謎の宣教師が少女ヴァレリーの前に現れたころから、町に吸血鬼が出現し始める。すべては謎に包まれているが……。妖しげなムードが全編に漂うなか、悪夢の世界をさまよう少女の怪奇体験を描くカルト・ムービー。", "1969-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lay the Favorite", "レイ・ザ・フェイバリット（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Still Life", "長江哀歌", "", "", "http://image.eiga.k-img.com/images/movie/34126/315x210.jpg?1465", 113, "06年のベネチア国際映画祭で金獅子賞に輝いた、中国の若き名匠ジャ・ジャンクー監督による人間ドラマ。ダム建設によって水没することが決まっている三峡の街。16年前に別れた妻子を探しにこの街を訪れた炭鉱夫サンミンは、かつて妻が住んでいた場所がすでに水没してしまったことを知る。一方、音信不通の夫を探しにやって来たシェン・ホンは、夫が働いていた工場を訪れるが、そこに彼の姿はなく……。", "2007-08-18", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Apocalypse Now", "地獄の黙示録", "", "", "http://image.eiga.k-img.com/images/movie/45186/315x210.jpg?2701", 147, "ジャングルが美しく燃え、ドアーズの『ジ・エンド』が流れてくる1960年代末のベトナム。暑苦しいサイゴンのホテルから指令部に呼び出されたウィラード大尉は、ジャングル奥地に失踪し、現地人のカリスマとなって王国を築いたと噂されるカーツ大佐の暗殺密令を受け、部下４人とナング河を逆上る。その途上、ナパームの匂いを偏愛し戦争をエンジョイするキルゴア大佐、ジャングルの特設ステージで兵隊たちを挑発するプレイメイト、ドラッグに狂う兵隊、迫力ある戦場を撮るTV取材班など無秩序で神経症的な現実を次々と目撃する。やがてカンボジア奥地でカーツの異様な王国を発見、丸坊主のカーツが闇の中から現れ“恐怖”について語り出す。広場では牛を蛮刀で殺す儀式。ウィラードも任務に取りかかった。ポスト・ベトナム映画の総決算。評価をめぐり賛否両論が起こった。2001年にはコッポラ自身の手により、56分も長い特別完全版が製作された。", "1980-02-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TALES OF HOFFMANN", "ホフマン物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "19世紀末、パリで数々のオペレッタを発表したドイツの作曲家オッフェンバックの代表作を、「赤い靴」で成功したパウエル＆プレスバーガーのコンビが映画化。詩人ホフマンの幻想的な３話を、オペラ、バレエの手法を生かして豪華な作品に仕上げた。", "1951-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ASSASSINATION OF TROTSKY", "暗殺者のメロディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "スターリンによってソ連から追放された革命家トロツキーを暗殺すべく命令を受けたフランク・ジャクソンは、彼を追ってメキシコに向かった。任務へのプレッシャーからフランクはやがて自分を失っていく……。トロツキー暗殺事件をもとにJ・ロージーが、人間の内面に潜む不安と暗黒を鋭くえぐり出した異色作。", "1972-01-01", "1,3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海の情事に賭けろ", "海の情事に賭けろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "赤木圭一郎が二役を演じる、アクション映画。房州に出かけた大学生、栗谷剛一は、自分と瓜二つのヤクザ者、加東勇二と間違われ、ピストルで負傷する。東京へ戻った剛一は、真相を突きとめようと、勇二のいた水野組の根城に乗り込んだ。やがて、剛一は、勇二が双生児の兄弟だと知るが……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIRANDA", "ミランダ 悪魔の香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "T・ブラス監督が、男たちの間を渡り歩く戦争未亡人、ミランダの姿をショッキングなセックス・シーンとともに描く。戦後間もない北イタリアの田園地帯で古いホテルを経営するミランダ。グラマラスな肉体で次々と男を誘惑し、愛欲に溺れていく彼女は、束縛されることを嫌い……。", "1985-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昇り竜やわ肌開帳", "昇り竜やわ肌開帳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "関東桜一家の二代目組長を継いだ桜勝美の活躍を描く女任侠映画。鈴木清順門下の葛生雅美の監督デビュー作でもある。昇り竜の刺青を肌に刻む勝美の前に、流れ者の哲次郎という男が現れた。彼女の許婚は哲次郎の身代りになって殺されたのだという。勝美はやがて哲次郎に惹かれていくのだが……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASSASSINATION", "続・殺しのテクニック 人間標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "極右犯罪組織を摘発するため、反逆罪で死刑にされた形をとったCIAの諜報部員が、実際には存在しない自分の弟になりすまして組織に潜り込む。しかし妻の再婚相手に見破られ、組織の首領にも疑われはじめて窮地に陥る。シルヴァがスパイの非情さを好演。", "1967-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Windtalkers", "ウインドトーカーズ", "", "", "http://image.eiga.k-img.com/images/movie/1216/315x210.jpg?4245", 134, "第2次世界大戦のアメリカ海兵隊は、ナバホ族の言語を暗号として使用。ナバホの通信兵ヤージーは、ガダルカナルで負傷したベテラン兵エンダーズとペアを組みサイパンに派遣される。が、エンダーズの任務は暗号が日本軍の手に渡るのを防ぐこと。通信兵ヤージーが日本軍の捕虜となる場合には、彼を抹殺することを命じられていた。ジョン・ウー監督が戦争実話を元に「ブローン・アウェイ／復讐の序曲」の脚本コンビで映画化。", "2001-08-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LET’S SCARE JESSICA TO DEATH", "呪われたジェシカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "当時32歳のJ・ハンコック監督が若いスタッフを率いて撮り上げた、世にも恐ろしい怪奇ファンタジー。神経障害を持つ若妻ジェシカが、夫と友人に連れられてコネチカットの湖畔の別荘に引っ越してきた。その別荘にはエミリーという若い娘が勝手に住みついており、４人の共同生活が始まる。やがて謎の幻聴にうなされていたジェシカの周囲で次々と不可解な出来事が起こり、ついにおぞましい惨劇が……。“ジェシカを死ぬまで怖がらせよう”という原題さながらにジェシカに襲いかかる幻聴、アコースティック音楽や効果音をコラージュした音響処理が、得体の知れない不安感を増幅。さらに墓場や丘の上に出没する少女、湖の水中にゆらめく白い影といった不気味なイメージがおもむろに挿入され、観る者の恐怖に拍車をかける。物語的には一種の吸血鬼映画なのだが、すべての出来事が現実か妄想か判然としない語り口によって、特定のカテゴリーに収まりきらない異色の叙情派ホラーとなった。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mothers' Way, Daughters' Choice", "母の道、娘の選択", "", "", "http://image.eiga.k-img.com/images/movie/56296/315x210.jpg?4187", 85, "米ニューヨークでロイターの記者として働く我謝京子監督が、日本人女性の生き方や母と娘をテーマに撮り上げたドキュメンタリー。職場の同僚が執筆した「ユダヤ人の罪悪感」というエッセイ集に触発された我謝監督は、「あこがれていた外国へやってきた一方で日本を飛び出したことへの罪悪感」をテーマに、ニューヨークに住む日本人女性たちに話を聞いてまわる。日本を出た理由や故郷へ抱く罪悪感、日米の働き方の違い、育児と仕事の両立など多岐にわたる話題はいつしか親世代との比較となり、監督のなかで母と娘との姿が明確になっていく。", "2011-05-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獄門島（総集編）", "獄門島（総集編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "推理小説史上、十指に入る傑作と言われる同名小説を映画化。金田一の良き相棒とも言える磯川警部に大友柳太朗が扮している。獄門島という瀬戸内海の孤島を舞台に、古い因習によって三姉妹が次々と殺されていく事件の謎に、金田一が挑む。東京映画配給会社（東映）の第１回配給作品。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAN OF THE WEST", "西部の人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "町を代表して学校教師を迎えに行く途中のリンクは列車強盗に襲われ、いかさま師のサム、歌手のビリーとともに西部の荒野に取り残される。途方に暮れた二人をリンクはある廃屋に連れていく。そこには列車強盗たちがたむろしていた。実はリンクは以前この一味の仲間だったのだ……。西部劇のヒーローは普通は颯爽としてカッコいいものだが、この映画ではそんなイメージを覆すかのように、老いて疲れ果てた男が主人公。物語も暗く陰惨だが、そんな中にウエスタンというジャンルの刷新を図ろうとしたA・マンの意欲が見てとれる。「大砂塵」と並ぶ1950年代ウエスタンの異色傑作。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("路上", "路上", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 42, "脚本家、助監督として長年、神代辰巳監督に師事した鴨田好史が、神代に捧げた作品。大きすぎるコートを着た女が新宿の町を歩いている。路上生活者と出会い、高架下の絵描きや突然大声を出す男と擦れ違う。変わり続ける新宿を大きなコートの女が徘徊する。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本暗黒史 情無用", "日本暗黒史 情無用", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "工藤栄一監督と安藤昇のコンビによる“日本暗黒史”もの第２作。真の任侠道を信じる男として生きようとする一匹狼が、不良どもを集めて組を設立。地方歓楽街の浄化のために暴力団組織の統合をはかるが……。安藤昇の生の迫力が生かされた一編。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幻想・アンダルシア", "幻想・アンダルシア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "スペイン・アンダルシア地方を舞台に、何不自由ない生活を送る平凡な主婦が、失踪した作家を探し幻想的な旅をする様を描く。ヒロイン・庸子は、スペイン料理店で夫と外食中に、作家の志水幹朗が失踪したという話を耳にする。庸子は自ら名乗り出て、志水を探すために単身スペイン・アンダルシア地方へ。途中、自転車で異国を旅する青年・遊、謎めいた女性・秋と知り合い、３人は志水が部屋に残した『偽りの血』という原稿と“プラテーロ”という言葉を頼りに足取りを追う。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Iron Man", "アイアンマン", "", "", "http://image.eiga.k-img.com/images/movie/53456/315x210.jpg?1660", 125, "「スパイダーマン」「ハルク」などを生み出したマーベル・コミックの人気作品をロバート・ダウニー・Jr.主演で映画化、全米で大ヒットを記録したアメコミ映画。監督は「ザスーラ」のジョン・ファブロー。巨大軍事企業の社長トニー・スタークはアフガニスタンでテロ組織に捕われ、新兵器の開発を強制されるが、敵の目を盗んで戦闘用パワードスーツを開発し、敵地から脱出。さらに改良を加えたパワードスーツを装着し、“アイアンマン”となってテロ撲滅のため戦うことを決意する。", "2008-09-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("寄性獣医・鈴音 EVOLUTION", "寄性獣医・鈴音 EVOLUTION", "", "", "http://image.eiga.k-img.com/images/movie/57246/315x210.jpg?3810", 72, "1500万ダウンロードを記録した春輝原作の人気ケータイ配信コミック「寄性獣医・鈴音」を2編で実写映画化した「寄性獣医・鈴音 GENESIS×EVOLUTION」の後編。鬼頭財閥の研究所で死闘を繰り広げた鈴音だったが、何者かに拉致監禁されてしまう。同じ建物のどこかに仲間も捕らえられていると考えた鈴音は脱出を試みるが、そこへメイド服を身にまとった謎の少女が出現。さらには発情した女たちや凶暴化した男たちが次々と襲いかかってくる。", "2011-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Elizabeth: The Golden Age", "エリザベス：ゴールデン・エイジ", "", "", "http://image.eiga.k-img.com/images/movie/34266/315x210.jpg?3232", 114, "若きエリザベスがイングランド女王に即位するまでを描いた「エリザベス」の続編となる歴史スペクタクル。主演のケイト・ブランシェットやシェカール・カプール監督ら前作のキャスト・スタッフが再集結。さらにクライブ・オーウェンやサマンサ・モートンといった実力派キャストも加わり、スペインとの宗教対立や宮廷内に渦巻く陰謀を乗り越えてイングランド黄金時代を築いていくエリザベス女王の苦悩や葛藤を描き出す。", "2008-02-16", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴト師株式会社スペシャル", "ゴト師株式会社スペシャル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ゴト師株式会社のリーダー、君島はパチンコ業界を食い物にする警視庁に戦いを挑む。マスコミを使い、警察バッシングをするゴト師株式会社だったが、警視庁生活保安局の女性警視、天地が、代議士の力を借りて反撃に出るのだった。主人公・君島と瓜二つの男（根津の二役）と、敵方のキャラクターもユニーク。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かあちゃん", "かあちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "天保末期、飢饉による米価の高騰と苛酷な税の徴収により、庶民たちの暮らしは困窮していた。とある町の貧乏長屋に住む、おかつとその子供たちは、近所でも評判のしまり屋。家族全員で働いているのにつつましく暮らし、長屋の付き合いにも参加しない。ところが、そのケチぶりには深い理由があった……。本作が75本目の監督作品となる名匠・市川崑が現代の世相を思わせる不景気な江戸の町を舞台に“家族”の在り方や人間関係を優しくユーモラスに問う人情時代劇。人情味に厚いおかつを演じた岸恵子はフランスから永住帰国して、本作に出演した。原作は山本周五郎の同名小説。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECRET WAR OF HARRY FRIGG", "脱走大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "1943年のイタリア戦線。捕虜となった連合軍士官を救出しようと、アメリカ人の“脱走名人”が敵地に潜入。しかし、敵地と思っていたその場所は戦争とはまったく無縁の伯爵の豪邸で、彼もすっかりそこに腰を落ち着けてしまう……。P・ニューマンの個性を生かした戦争コメディ。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほらふき丹次", "ほらふき丹次", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "主演の藤田進が自ら映画化権を手に入れた、念願の企画の映画化。皆からホラ吹きと馬鹿にされる丹次が、ふとしたことから強盗を捕え、被害者の娘を引き取る。二人の間に親子を越えた感情が芽ばえるが、娘は犯され、あげくに自殺。丹次は仇を討ち、警官に射殺される。中川信夫の演出が冴えた佳編。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あした輝く", "あした輝く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "昭和20年、満州の夏樹病院の一人娘・今日子は、彼女に想いをよせる加賀中尉のプロポーズを断り、衛生兵の速水との愛を実らせていく。やがて終戦を迎え、飢えと寒さのなか、今日子は速水の子を身ごもるが、速水はソ連兵に連れ去られてしまう。里中満智子のマンガを基に作られたティーン向けのメロドラマ。当時人気アイドルだった浅田美代子が主演している。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.SATURDAY NIGHT", "ミスター・サタデー・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "「恋人たちの予感」などで人気のコメディ・アクター、B・クリスタルが製作、監督、脚本、主演の４役に挑んだ意欲作。ドサ回りを続ける落ちぶれたコメディアンが、華やかな絶頂期を回想。毒舌を武器にのし上がり、それゆえ落ちぶれた彼の姿を、マネージャーである兄との葛藤を交えて哀切なタッチで描く。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間失格　ディレクターズカット版", "人間失格　ディレクターズカット版", "", "", "http://image.eiga.k-img.com/images/movie/55056/315x210.jpg?4729", 0, "太宰治の名作「人間失格」を、「デスノート」などで知られる人気漫画家・小畑健がキャラクター原案を手がけたTVアニメの劇場版。アニメーション制作はマッドハウス、主人公の声を堺雅人が担当する。裕福な家庭で育った葉蔵は、真似事の左翼運動で貧しい人々から金銭を搾取し、気ままに暮らしていた。ある日、反社会的な活動をしたとして警察に追われていた葉蔵は、恒子という女の営むカフェに逃げ込む。", "2009-12-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南太平洋の若大将", "南太平洋の若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "京南大学柔道部主将・田沼雄一の実家が経営するすきやき屋が、ハワイに支店を出すことになる。そこでハワイの日本料理屋の娘に熱を上げられ、恋人・澄子はプンプン。雄一は柔道の選手権試合でオランダの留学生に大苦戦となるが、駆けつけた澄子の声援で見事に勝利する。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPECIAL BARDOT", "今宵バルドーとともに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 47, "1967年、当時世界的にセクシー・アイドルとして人気のあったB・バルドーの魅力を紹介したTV番組で、バルドーの歌と踊りが堪能できる。S・ゲンズブールが相手役として登場し、歌を歌っている。日本では1996年、ゲンズブール特集の時に、劇場で上映された。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風、スローダウン", "風、スローダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "タレントの島田紳助の映画監督デビュー作。大阪を舞台に、プロのオートバイレーサーを夢見るオサムと、ヤクザ組織に加わったチンピラ・ショージ、そして何不自由なく父親の事業を継ぐことを約束されているボンボン・ヤスヒロ。生き方はそれぞれ違うものの中学時代の同級生であるこの３人の夢とロマン、そして挫折を描いた青春映画。10年前の「ガキ帝国」で映画デビューを飾った紳助が、それ以来の仲間でもある井筒和幸を総監修に迎え、自分自身のエピソードなども織り込み哀愁込めたタッチで綴っていく。それぞれの主人公に扮する吉本興業の若手タレント石田靖、長原成樹、西川忠志（西川きよしの長男）が持ち前のキャラクターを生かして三者三様の若者像を好演。彼らの前に現れるマドンナ役の五十嵐いずみのアンサンブルも見事で、一人の新人監督の入魂の一作として、大いに好感の持てる作品である。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NATURAL CAUSES", "ザ・ターゲット 暗殺へのプレリュード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "キッシンジャー暗殺計画の陰謀に巻き込まれた男女の姿を描いたポリティカル・サスペンス。タイの首都バンコク。ベトナム難民援助の第一人者レイチェルが射殺された。ロサンゼルスから10年ぶりに帰国した娘のジェシーは、ソムチャイ少佐から母が人身売買の黒幕だったことを知らされショックを受ける。その夜、何者かに殺されそうになった彼女は、母の秘密を探ることを決意するが……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Number 23", "ナンバー23", "", "", "http://image.eiga.k-img.com/images/movie/53076/315x210.jpg?4448", 99, "「オペラ座の怪人」のジョエル・シューマッカー監督がジム・キャリー主演で放つサスペンス・ミステリー。動物管理局に勤めるウォルターは、誕生日に妻から“ナンバー23”という本をプレゼントされる。それは、呪われた数字“23”に翻弄される刑事を主人公とした殺人ミステリーだった。主人公と自分の人生が酷似していることに気付いたウォルターは、自身も“23”という数字に取り憑かれるようになり……。", "2007-11-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NUMBER SEVENTEEN", "第十七番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "ヒッチコックがイギリス時代に監督した、スラップスティック調のコメディ。宝石泥棒を追う刑事が、“十七番”と呼ばれる廃屋で、ベンという謎の男と出会う。その後、宝石争奪戦が展開。貨物列車で逃走する３人の泥棒を追う刑事とベン。そして冒険好きの若い女を巻き込み、話は意外な方向へと向かう。", "1932-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("為鐘情", "レスリー・チャン　君の瞳に恋してる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "2003年に自殺という形で惜しまれつつこの世を去った香港の人気俳優L・チャンが、若かりし頃に主演した青春恋愛アタック・ムービー。自らカヴァーした吉川晃司のポップナンバー『MONICA』をはじめ、ゴキゲンな曲がドラマを彩る。DJの主人公は悪友の二人と共に、ナンパのことばかり考える毎日を送っていた。そんなある日、看護婦として働く箱入り娘の女にひと目ボレし、悪友二人も協力するが……。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("王将", "王将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "将棋界の奇才・坂田三吉の将棋に賭けた半生を描いた秀作。時代劇を得意とした伊藤大輔監督が、太平洋戦争後、占領軍の方針で時代劇の製作が禁止され、題材に困っていたなかで見出した舞台劇を映画化し、良質の人間ドラマに仕上げた。大阪・天王寺の貧民街に住む坂田三吉は、本業の草履作りの収入まで将棋につぎ込む大の将棋狂い。そのおかげで家計は火の車。女房の小春の苦労は絶えない。負け知らずの三吉が、東京から来た花形棋士の関根七段に負けたことから、関根に追いつき追い越すことだけが三吉の生涯の目標となった。血を吐く努力の末に三吉は、ついに関根を破ったが、無学文盲のゆえに名人位にふさわしくないとされ、位には関根がつくことになる……。阪妻が「無法松の一生」の車夫・松五郎に匹敵する名演を披露。勝負師魂を見事に描いているのもさることながら、献身的な内助の功や、父親の鬼手による勝利を娘が泣きながら非難するところなどは、ホームドラマの頂点をきわめ、涙を誘わずにはいられない。DVDは「阪東妻三郎傑作選 DVD-BOX」に収録。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mesrine: Part 1 - Death Instinct", "ジャック・メスリーヌ　フランスで社会の敵（パブリック・エネミー）No.1と呼ばれた男 Part.1 ノワール編", "", "", "http://image.eiga.k-img.com/images/movie/54836/315x210.jpg?2650", 113, "1970年代のフランスに実在した銀行強盗ジャック・メスリーヌの生涯を、前後編に分け2部作として映画化した実録犯罪ドラマ。第1部ではメスリーヌの青年時代に焦点を当て、彼がいかにして“社会の敵 No.1”と怖れられる存在となったかが描かれる。タイトルロールにバンサン・カッセル。共演にジェラール・ドパルデュー、セシル・ドゥ・フランス。監督は「アサルト13　要塞警察」のジャン・フランソワ・リシェ。", "2009-11-07", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bourne Identity", "ボーン・アイデンティティー", "", "", "http://image.eiga.k-img.com/images/movie/1306/315x210.jpg?3490", 119, "原作は人気作家ロバート・ラドラムのポリティカル・サスペンス3部作の第1作「暗殺者」（新潮文庫）。海上で発見された記憶喪失の男は、なぜか戦闘能力や語学が堪能。自分の身元を探ろうとするが、暗殺者が次々に現れる。監督は「スウィンガーズ」「go」では撮影も兼任したニューヨーク出身の俊英ダグ・リーマン。「ラン・ローラ・ラン」のヒロイン役、フランカ・ポテンテが主人公に協力する女性役で共演。", "2003-01-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIB", "りんご", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "イランの名匠モフセン・マフマルバフの娘である、当時18歳のサミラ・マフマルバフが初演出に当たったメルヘン・ドラマ。生まれてからずっと父親に監禁されていた12歳の双子の少女が福祉事務所によって解放されたという実話に基づき、外の世界に接した少女たちの驚き、友達との交流、初めての買い物などの冒険を、ほのぼのと描き出す。ドキュメンタリーを思わせる素朴な視点は、いかにもイラン映画風。少女たちの真っ直ぐな瞳が印象に残る。", "1998-01-01", "15,2,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎 魔性剣", "眠狂四郎 魔性剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "狂四郎がふとした因縁で知り合った大工の孫・鶴松は実は岩代藩主の妾腹であった。世継ぎのできぬ岩代藩は一度は殺そうとした鶴松を誘拐するが……。他に第２作で死んだむささびの伴蔵の妹が登場、狂四郎の行手をさえぎる。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUBU", "愛すれど哀しく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "純真な少女ベルタはパン職人ブブに引っかけられ売春で稼がされることになる。ブブは勤めを辞め彼女のヒモとなるが、やがて刑務所入り。ある日、ベルタはピエーロという青年と出会い、人生をやり直そうとするが、出所したブブは彼女を放そうとしない……。20世紀初頭のミラノを舞台に「わが青春のフロレンス」のスタッフが描く佳編。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅するパオジャンフー", "旅するパオジャンフー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "台湾で、歌や芸などを披露しながら薬を売り歩くパオジャンフーの姿を描いたドキュメンタリー映画。３つのグループのパオジャンフーの物語を交錯させ、詩情あふれる映像で綴る。家族で構成された新威殿一座をメインに物語は展開。新威殿の長男アホンが、月琴で弾き語りをする老人の元パオジャンフーや、蛇使いのグループと出会い、一座を継ぐ決心をするまでが描かれている。「さらば愛しき大地」などで知られる名匠・柳町光男が、全編中国語により骨太く演出している。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bangkok Dangerous", "バンコック・デンジャラス", "", "", "http://image.eiga.k-img.com/images/movie/54066/315x210.jpg?3284", 100, "「the EYE」のオキサイド＆ダニー・パン兄弟監督が、自身の出世作「レイン」をハリウッドでセルフリメイクしたサスペンスアクション。自らのルールを忠実に守ることで完璧な仕事をこなしてきた暗殺者ジョーは、能力の限界を迎える前に引退することを決意。最後の仕事として、とある犯罪組織から4件の暗殺依頼を受けるが……。「ナショナル・トレジャー」シリーズのニコラス・ケイジが主人公ジョーを熱演。", "2009-05-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUIEN SABE？", "群盗荒野を裂く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "メキシコ革命のさなか、政府軍を襲って武器を奪い、革命軍に売りつける群盗集団がいた。あるとき、彼らの所へビルというアメリカ人青年がやって来て……。派手な銃撃戦とともに、イタリア・アクション映画お得意の残虐な大量殺りくシーンが展開。", "1967-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若者はゆく −続若者たち−", "若者はゆく −続若者たち−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "両親を幼い頃に亡くした５人兄妹の家族ドラマの続編。青春時代から大人の世界へと一歩踏み出す５人の姿を中心に、60年代の昭和の若者と社会を捉えたビビッドな群像劇だ。佐藤太郎、次郎、三郎、オリエ、末吉の５人はそれぞれの仕事や勉強にいそしんでいたが、そんな中、オリエの職場の同僚ミツが上司と揉めて解雇される。ミツは不当解雇だとしてオリエに相談するが……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カフカ　田舎医者", "カフカ　田舎医者", "", "", "http://image.eiga.k-img.com/images/movie/53066/315x210.jpg?1845", 21, "「頭山」でオスカー候補となったアニメーション作家・山村浩二が、フランツ・カフカの不条理な世界に挑んだ短編アニメ。人間国宝である狂言師・茂山千作ら茂山一家が声優を務める。吹雪の夜に急患の知らせを受けた田舎医者。10マイル離れた患者の家へ行くための手段がなく困っていると、突然見知らぬ馬子が姿を現す。馬子に馬を借りた田舎医者は、瞬く間に患者の元へと辿り着く。ベッドに横たわった少年は健康そうに見えたが……。", "2007-11-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NAKED CITY", "裸の町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ファッション・モデル殺人事件を捜査する刑事ハローラン。モデルの女友だちの愛人が質入れした宝石が、ある医師の家から盗まれたものであることを知った彼は、背後に宝石盗賊団が暗躍していることを突き止める……。J・ダッシン監督が、オール・ニューヨーク・ロケで描いたセミ・ドキュメンタリータッチの作品。緊張感あふれる映像とスリリングなストーリー展開が注目される。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飛び出す冒険映画 赤影", "飛び出す冒険映画 赤影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "琵琶湖にほど近い村で、金目教という新興宗教が勢力を拡大。騒乱の元となる彼らを根絶するために出陣した赤影、青影、白影の前に、天下支配をもくろむ甲賀幻妖斎率いる七人衆が立ちはだかる。横山光輝原作のTVシリーズ“仮面の忍者　赤影”のうち４話を劇場向けに再編集した特撮時代劇。一部の特撮シーンが３D化されている。DVDタイトルは「仮面の忍者　赤影　THE MOVIE」。DVDには日本未公開の海外長編版「Ninjyascope」「WATARI THE CONQUEROR」「WATARI AND FANTASTICS」を収録している。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE POPPPY IS ALSO A FLOWER", "悪のシンフォニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "欧米に流れ込む阿片の根絶を目指し、その密売ルートを追って活躍する捜査官の任務をアクション仕立てで描く。原案はイアン・フレミングで、監督は彼と「007」シリーズでコンビを組んだT・ヤング。豪華キャストが見もの。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野蛮人のように", "野蛮人のように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "天才少女と騒がれてデビューした人気女流作家が、偶然から暴力団の抗争に巻き込まれていくサスペンス。女流作家に角川から独立したばかりの薬師丸ひろ子、暴力団に追われるチンピラに柴田恭兵が扮し、川島透が小気味よい物語に仕上げた。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASCADEUR", "カスケーダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "スタントマン出身のH・マーティンス監督、主演による痛快アクション。妻を亡くして以来、一人で暮らしている元スタントマンのヴィンセントが、古代遺跡研究生の女性クリスティンと出会う。ヒトラーがロシアから奪取した伝説の秘宝“琥珀の部屋”を探し求める彼女は、犯罪組織に追われる身。二人は追っ手をかわしながら秘宝を目指して旅に出る。ヘリコプターのトラック激突や、飛行機での格闘、迫力のカーチェイスなど、CGを一切使わない豪快なアクション・シーンが見もの。", "1998-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAT BALLOU", "キャット・バルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ナット・キング・コールのバラッドが印象的な痛快西部劇。1894年ワイオミング、父を殺し屋に殺され、復讐のためにガンマンを集め大姐御“山猫バルー”となったキャサリンは、大騒動の末に復讐を果たすのだが……。一人二役のL・マーヴィンが好演。DVDは「コロムビア・トライスター ザ・ウェスタン・ムービーズBOX(1)」に収録。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Red Badge of Courage", "勇者の赤いバッヂ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "　1862年、南北戦争の戦場。北軍の新兵ヘンリーは戦友たちの前では強がっていたものの、初めての実戦を前に怯え、ついには逃亡を図る。だが北軍の疾病兵たちを目のあたりにした時、ヘンリーの心に変化が……。スティーブン・クレインの小説「赤い武功章」を映画化。監督ヒューストンの意思に反し、スタジオ側によって20分以上を編集・削除されてしまった。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀の男・青森純情篇", "銀の男・青森純情篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "漫画家、本宮ひろ志の『銀の男』をモチーフにした青春群像劇の第２作。店のプレーヤーの一人、透はある日、高校時代の同級生で憧れの女性だった美和と再会する。そんな彼女から母の手術費で500万円が必要と打ち明けられた透はお金を工面するため店の仕事以外にもバイトを始めた。民俗学の研究のため店に通う倫子は彼が心配になり、美和のことを調査するが、すべては美和のウソだった……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シンプルライフ・シンドローム", "シンプルライフ・シンドローム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "10代のリアルな心情を描いたことで話題となった同名小説を、作者自ら映画化。姉の死の真相を探る少女、自殺した少女に共感を覚える少年、恋人を亡くして死に魅了されていく女子高校生の、それぞれのドラマがオムニバス形式で展開する。自分を“透明な存在”と思いこんでしまう若者たちの、生の実感の薄さを浮き彫りに。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サムライプリンセス　外道姫", "サムライプリンセス　外道姫", "", "", "http://image.eiga.k-img.com/images/movie/54706/315x210.jpg?4213", 82, "「からくり」と呼ばれる機械人間が氾濫し、盗みや殺しが絶えない時代。ある日、武装した野武士が旅芸人の女たちを惨殺する事件が起こり、その光景を目の当たりにした僧侶の蓮情と科学者の狂楽は、成仏できない彼女達の魂を宿した不死身のからくり、外道姫を誕生させる。外道姫を演じるのは、本作が映画初出演となるグラビアアイドルの希志あいの。", "2009-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い潮", "黒い潮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "行方不明の国鉄総裁が、轢死体となって発見されたという事件を背景に、一人の新聞社社会部記者の、煮えきらない社の方針に対する憤りと、かたくなな正義感を描いた社会派作品。下山事件にヒントを得た井上靖の小説の映画化。滝沢修ら新劇のベテランが出演。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("莫逆家族　バクギャクファミーリア", "莫逆家族　バクギャクファミーリア", "", "", "http://image.eiga.k-img.com/images/movie/55986/315x210.jpg?3330", 0, "累計発行部数2550万部を突破する田中宏の人気ヤンキー漫画を、「海炭市叙景」「青春☆金属バット」の熊切和嘉監督が実写映画化。主演は人気お笑いコンビ「チュートリアル」の徳井義実が務める。17歳にして関東一の暴走族「神叉」のトップを張っていた火野鉄は、30歳を過ぎた今は建設作業員として平凡な生活をおくっていた。しかし、ある事件をきっかけに理不尽な現代社会に異を唱えることを決意。かつての不良仲間たちを再集結させる。", "2012-09-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HENRI CARTIERーBRESSON：POINT D’INTERROGATION", "アンリ・カルティエ・ブレッソン　疑問符", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 37, "世界的に著名な写真家であるH・カルティエ・ブレッソンを、自身も高名なファッション写真家であるS・ムーンがインタビューした作品。“決定的瞬間”という言葉を作ったブレッソンの写真観と数多くの作品を見ることができる、写真史的に貴重な映像である。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODY＆EARTH", "イブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "“アダムとイブ”のイブをヒロインに、彼女の愛を探す旅を通して自然と人間の結びつきを映像と音楽で表現。北米、カリブなどで撮影された美しい映像とそれに呼応するサウンドが印象的だ。地球の奥深く分け入った地で長い眠りから覚めたイブ。自然と完全に調和した無拓な世界で生きる彼女はある日、自己に目覚める。“愛”の存在を知った彼女は何かに突き動かされるように旅へ。やがて旅先で彼女はソウル・メイトを見つける。ビデオ、DVDタイトルは「イブ　ボディー・アンド・アース」。", "2002-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORO　NO　BRASIL／SOUND　OF　BRAZIL", "モロ・ノ・ブラジル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "アキ・カウリスマキの兄であるミカ・カウリスマキ監督が手掛けたドキュメンタリー。フィンランド人である監督が第二の故郷というほど愛してやまないブラジルの音楽について取材。カメラを抱えてリオ・デ・ジャネイロや地方を旅し、サンバとボサノバだけではないこの国の音楽の多様性をすくい取っていく。ブラジルの歴史や生活様式も取り込んだ興味深い一作だ。", "2002-01-01", "7,20,17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAU GESTE", "ボー・ジェスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "“コールマン髭”を流行させた無声映画。宝石泥棒とうしろ指をさされ、アフリカの砂漠で外人部隊に入った英国の若き３兄弟と意地悪い軍曹の確執を軸に、兄弟の悲しい運命を描く。トーキー以後、二度リメイクされたが、それも本作の大ヒットあればこそ。なお、ビデオは澤登翠の活弁版で収録されている。", "1926-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last Godfather", "ラスト・ゴッドファーザー（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2010-01-01", "1,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("のれんと花嫁", "のれんと花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "一人息子が経営する江戸時代からののれんを誇るカステラ屋“長崎開花堂”は大繁盛。そのため、長崎で“元祖開花堂”を営む父親の方では客足がバッタリ。これではまずいと、父親は東京の長崎開花堂に文句を言いに上京するのだが……。軽く明るく清らかに展開する楽しいコメディ。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どっちもどっち", "どっちもどっち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "明石家さんまと松田聖子のコンビで贈るラブ・コメディ。TVディレクターの村田は浮気症が原因で、恋人の慶子にフラれてしまう。彼は心機一転、半年間の女絶ちを決意し新しいマンションに引っ越す。ところがその部屋には女の幽霊が住んでいて彼を付け回し……。ビデオ発売時タイトルは「大迷惑トラブルコメディ　どっちもどっち」。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wall Street: Money Never Sleeps", "ウォール・ストリート", "", "", "http://image.eiga.k-img.com/images/movie/55186/315x210.jpg?3172", 133, "オリバー・ストーン監督＆マイケル・ダグラス主演による名作「ウォール街」の23年ぶりの続編。前作の最後に逮捕され収監されたウォール街のカリスマ投資家ゴードン・ゲッコー（ダグラス）が長い刑期を終えてニューヨークに帰ってきた。疎遠になっていた娘ウィニーとの関係修復にとりかかるゲッコーだったが、ウィニーは強欲な犯罪者の父親を毛嫌いしていた。そんな中、ゲッコーはウィニーの婚約者でウォール街で一旗揚げようとしている野心家の青年ジェイコブと出会う。共演はシャイア・ラブーフ、キャリー・マリガン、ジョシュ・ブローリン。", "2011-02-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASSAI～LES　GUERRIERS　DE　LA　PLUIE", "マサイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アフリカに住む有名な部族マサイを主人公にしたユニークな冒険ドラマ。雄大なアフリカの自然をバックに、伝説の獅子を追う若きマサイ戦士たちのドラマが繰り広げられる。演技初体験ながら、堂々たる存在感を見せつけるマサイの若者たちの好演に注目。興味深いマサイの風習も見どころだ。村を干ばつから救うための貢物の獅子のたてがみを求め、旅立ったマサイの若き戦士9人。彼らの行く手に次々と危機が立ちはだかり……。", "2004-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　ロールとローラ　　うきぐも城のひみつ", "それいけ！アンパンマン　ロールとローラ　　うきぐも城のひみつ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "絵本の主人公として生まれたキャラクター、アンパンマン。TVアニメ版も2002年秋に15周年を迎えた人気シリーズの劇場版第14作。雲の中に浮かぶ不思議な城で、たった一人で空の汚れと戦うローラ姫。宿敵ばいきんまんと戦いながら、ローラ姫を助けるアンパンマンとロールパンナの活躍を描く。黒木瞳がローラ姫の声でゲスト出演している。キノコ村のきのこちゃんをめぐる騒動を描いた「鉄火のマキちゃんと金のかまめしどん」が同時上映された。", "2002-07-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君は僕をスキになる", "君は僕をスキになる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "構成作家や作詞家として活躍するトレンドメーカー、秋元康が企画したラブ・コメディ。毎年クリスマスをたった一人で過ごしていた二人のOLが、今年こそ一緒に過ごそうと決めた一人の男。ところが、その相手が同一人物だったことから恋のスクランブル・ゲームが巻き起こる……。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("平成無責任一家 東京デラックス", "平成無責任一家 東京デラックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「月はどっちに出ている」に続く、崔洋一、岸谷五朗コンビ作品。飴屋一家の家業は詐欺師。結婚と離婚を繰り返す母のもとには父親の違う兄弟が５人。故郷、土城市の市長選トトカルチョで大損した一家は、一発逆転を夢見て、花の東京にやって来る。行く先々で様々なトラブルの種をまき散らしながらも、なんとか大金を騙し取るが、いちばん信頼していた家族の一人にハメられ……。ホームドラマというよりも、珍道中という呼び名がふさわしい、前進あるのみの詐欺師一家の物語。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOTEL SPLENDIDE", "ホテル・スプレンディッド", "", "", "http://ecx.images-amazon.com/images/I/2134M4BG6DL._SL210_.jpg", 98, "孤島にひっそりと立つホテル・スプレンディッドは、完璧なる健康と美容、長寿を求める人たちが集う場所。今は亡き先代の経営者ブランチェ夫人の編み出した厳格な規約を滞在者とスタッフが守ることで、この世界の調和が保たれていた。しかし、過去に副料理長として勤めていたキャスが舞い戻ったことから、欲望をもたないことで安定していたホテルの規律が乱れ始める……。随所に、刺激的でブラックな笑いと斬新なアイディアを盛り込んだ、ちょっと風変わりな愛の物語。撮影のG・パドス、美術のA・ドミニッツ、衣裳のM・ペルネッタと監督T・グロスとのコラボレーションは、テリー・ギリアムの世界を彷彿させる。", "2001-02-10", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Marksman", "ザ・マークスマン", "", "", "http://image.eiga.k-img.com/images/movie/33646/315x210.jpg?1878", 94, "「ブレイド」シリーズのウェズリー・スナイプス主演のミリタリー・アクション。チェチェンの原子力発電所が、元米軍最高司令官という経歴を持つテロリストのザイザンにより占拠された。核を用いた大規模テロを阻止するため、アメリカ軍は敏腕エージェントのペインター率いる特殊部隊を現地へ送り込む。「デトネーター」「7セカンズ」と併せ、スナイプス主演のアクション映画を3連発で公開する特別企画“スナイプスの大運動会”の第1弾。", "2006-09-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　コキンちゃんとあおいなみだ", "それいけ！アンパンマン　コキンちゃんとあおいなみだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "やなせたかし原作の人気アニメーション劇場版第第18作「それいけ！アンパンマン　いのちの星のドーリィ」と同時上映の短編。", "2006-07-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MODS 夜のハイウェイ", "THE MODS 夜のハイウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "上京してきた二人の少年が体験する恋と冒険を縦糸に、ロック・ミュージシャンとその恋人の別離を横糸に、東京の週末の夜を描き出した青春グラフィティー。THE MODSが同名アルバムとカップリングさせてプロデュースしたプロモーション的な作品。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンのちゃっきり金太", "エノケンのちゃっきり金太", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "1930～40年代にかけて最大の人気を誇ったコメディアン、エノケンと山本嘉次郎監督とのコンビ映画の代表作。ジョンストン・マッカレイの小説『地下鉄サム』を下敷きに、幕末日本を舞台にしたドジなスリと岡っ引きの追いつ追われつのドタバタ喜劇が展開する。財布と一緒に勤皇派の密書をスッてしまった金太は、薩摩の侍と岡っ引きに追われるハメに……。追っ手を逃れて江戸から西へと旅立った金太の行く手には数々の事件が巻き起こる。時代劇の中で突如としてナンセンスなコミック・ソングの合唱が始まったりと、かなり自由奔放な遊びが見られ、今見ても新鮮さを少しも失っていない。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADULT 24歳の恋", "ADULT 24歳の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "無職で親のスネをかじって生活している24歳の多恵子は、自分を変えようと出会い系サイトで知り合った誠とホテルに入るが、誠は同性愛者で肉体関係を拒否されてしまう。さらに、ひょんなことから誠が尊敬する同性愛者の小説家・和也を探すはめになるが、多恵子は次第に誠にひかれていることに気がつく。オタク系アイドル「中野腐女子シスターズ」の一員として活動していた金井あやが、芸名をあんりにあらためて初主演を務めた。", "2011-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’HOMME QUI AIMAIT LES FEMMES", "恋愛日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "生涯、女とその脚線美を追い続けて死んだ男を描くフェミニスト、トリュフォー監督の風変わりな女性探求物語。集まるのはすべて女性という奇妙な葬式が行われる。棺の中に眠るのは、生涯独身を通した中年男ベルトラン。彼は生前、女好きで、特に女性の脚線美をこよなく愛し脚が美しければ腰から上はどうでもよいというほどの入れこみよう。そんな彼の豊富で珍奇な女性遍歴を綴った手記が出版され、パリに出かけた彼は、ここでも道ゆく足・足・足に目を奪われる……。出版社の女性編集者をはじめ、初秋のパリの街をかっ歩する女性たちの腰から上も素晴らしい!? 女を愛する男（＝男たち）の永久願望を執拗に鮮明に再現した作品。", "1977-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARIS VU PAR…", "パリところどころ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ヌーヴェル・ヴァーグ全盛の頃、当時のフランス映画界の先鋭監督６人が顔をそろえ、パリの街の点景をそれぞれの視点から描いたオムニバス映画。J・L・ゴダールはパリの外縁の街を舞台にした恋愛悲喜劇を、E・ロメールは凱旋門のあるエトワール広場を利用したシニカルなコメディを、C・シャブロルは高級住宅地に住むブルジョワ一家の悲劇を……といった具合に、各作品ごとの味わいを比較できるのが楽しい。必見なのは“シネマ・ヴェリテ”の提唱者J・ルーシュによる第２話「北駅」編。夫婦喧嘩して家を出た妻が道で男に声をかけられる約15分のエピソードを、ほぼワンカットで撮ったカメラワークは圧巻。", "1965-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誰か夢なき／総集編", "誰か夢なき／総集編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "かつてラグビー部の主将だったインターンの川路竜之助は、看護婦の多喜子に惹かれるが、彼女はやがて病院を追い出されてしまう。竜之助は彼女を追って病院を飛び出し、必死に探し求めるのだが……。「姿三四郎」で知られる富田常雄の原作を“早撮りの天皇”渡辺邦男が監督した青春映画。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COMPUTER WORE TENNIS SHOES", "テニス靴をはいたコンピューター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "“エレクトロニクスの時代”を反映した、ディズニー・プロ製作の爆笑コメディ。貧乏大学ゆえに購入できなかったコンピュータを、町の実力者に頼んで寄付してもらった学生デクスター。ある雨の日、ヒューズを取り換えようとして配線がショート、その瞬間、コンピュータに蓄積されていたすべての記憶が、そっくりそのままデクスターの頭に引っ越した。監督は「乱気流／タービュランス」のR・バトラー。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IVANHOE", "黒騎士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ウォルター・スコットの有名な騎士道小説『アイヴァンホー』の映画化。12世紀イギリス。腹黒いジョン親王に監禁された獅子王リチャードを救出するため、サクソン人のアイバンホーが立ち上がる。馬上槍試合や城の包囲攻略戦などスペクタクル・シーンは壮絶。E・テイラーの美しさとR・テイラーの好演が光る。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAST A GIANT SHADOW", "巨大なる戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "実在の人物ミッキー・マーカスの伝記を素材にした、戦場秘話。イギリス軍撤退を機に、ユダヤ人を追い出そうとアラブ諸国が圧力をかけているパレスチナ。ニューヨークに住む退役大佐マーカスは、民族独立のために戦うユダヤ人を応援するためパレスチナに向かい、独立運動の組織づくりに取りかかる。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市牢破り", "座頭市牢破り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "座頭市はヤクザ同士の喧嘩で、朝五郎という百姓に味方する親分の人柄に惚れるが、数ヵ月後に再会した朝五郎は百姓たちを苦しめる悪徳ヤクザになっていた……。三國連太郎が屈折したヤクザの親分役を巧演する。「戦争と人間」(1970～73)、「不毛地帯」(1976）などで知られる巨匠・山本薩夫が演出にあたった一編。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("疾走", "疾走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "それまでオリジナル脚本で疾走してきたSABU監督が初めて手がけた原作もの。重松清の同名大作小説を基に、少年期の“影”を凝視し、新境地を見せる。坂道を転がるように、怒涛と混沌が交錯する運命に巻き込まれていく主人公の姿を、透徹した光景を捉えた映像の数々で紡ぎ出す。主演は、NEWSの手越祐也。西日本のある町。兄シュウイチが放火事件で逮捕されたことから、中学生シュウジの日常は暗転する。東京に去った同級生エリを追って、単身旅を始めるが、それは果てしない道のりだった。", "2005-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Broken", "ブロークン", "", "", "http://image.eiga.k-img.com/images/movie/53876/315x210.jpg?3019", 88, "「フローズン・タイム」のショーン・エリス監督の長編第2作。主演は「ブラザーズ・グリム」のレナ・ヘディ、共演に「ぼくを葬る」のメルビル・プポー、「バーバー」「キングダム／見えざる敵」のリチャード・ジェンキンスら。ロンドンに暮らすX線技師のジーナが家族や恋人と父親の誕生日を祝っていると、突然大きな鏡が割れる。彼らは「鏡が割れると7年間不幸が続く」と冗談でやり過ごすが、その翌日、ジーナは自分と瓜二つの人間が自分と同じ車に乗っているところを目撃してしまい……。", "2008-11-15", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幡随院と白柄組 男の対決", "幡随院と白柄組 男の対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "おなじみ幡随院長兵衛と水野十郎左衛門の葛藤を描いた娯楽時代劇。家光治世下、ことごとく対立する幡随院と白柄組。そんな中幡随院一家にシンパの塩問屋阿波屋をはじめ、ほとんどの江戸の塩問屋が、勘定奉行松坂、老中戸田と組む相模屋によって暖簾をおろす寸前に追い込まれる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ありふれた愛に関する調査", "ありふれた愛に関する調査", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "関川夏央の小説『名探偵に名前はいらない』を榎戸耕史が映画化。うだつのあがらない探偵の日常を通して、都会に生きる人々の孤独をユーモアの中に描く。奥田瑛二がアンチ・ヒーローを好演、自ら企画にも名を連ねている。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・拝啓天皇陛下様", "続・拝啓天皇陛下様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "北支戦線に招集されたお人好しの山口善助は民間からの献納犬・友春号の飼育係を命じられる。ある日、善助に元の飼い主・久留宮ヤエノから慰問袋が届く。やがて友春号は戦死、そして終戦。善助は友春号の死を知らせにヤエノの家へ行く……。善助の友人に扮する小沢昭一が中国人・王万林を怪演。DVDは「渥美清メモリアル　渥美清・もうひとつの世界」に収録。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名無しの十字架", "名無しの十字架", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Grissom Gang", "傷だらけの挽歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "　1930年代、ミズーリ州。名士の娘バーバラが数名の男たちによって誘拐される。その後、スリム・グリソム率いるギャングが銃撃戦の末に彼女を奪い去った。スリムはバーバラに一目惚れ。拉致されたバーバラはスリムの優しさを知り、やがて彼に体を許す。一方、スリムの母親はバーバラの父親に身代金100万ドルを要求。それを手に入れた彼女はクラブの経営に乗り出す。その間、探偵の捜査も進んでいたが……。誘拐事件の顛末を描く犯罪ドラマ。同じ原作が1948年に「黒い骰子」として製作されている。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("S.O.S.　TITANIC", "失われた航海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "史上最大の海難事故として知られる豪華客船タイタニック号の悲劇を再現した映画。イギリスの超豪華客船が、その処女航海で巨大な氷山にぶつかり沈み始める……。信じがたい事故に遭遇した乗員、乗客たちのパニック状態と、その助け合う姿を見事に描出している。", "1979-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REMO WILLIAMS：THE ADVENTURE BEGINS…", "レモ　第１の挑戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "アメリカのベストセラー冒険小説『デストロイヤー』を映画化。平凡な警察官レモが東洋古武術を習得し、巨大な敵に立ち向かう。ユーモアを交え、歯切れのいいストーリー展開で描くアクション。中でも、修理中の自由の女神での攻防がエキサイティングだ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHALAKO", "シャラコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "19世紀末のアメリカ、ニューメキシコを舞台として、狩猟にやってきたヨーロッパ貴族女性と、インディアンと白人の混血児シャラコとが織り成すラブ・ロマンスを中心として描かれたドミトリク監督の異色西部劇。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ときめき", "ときめき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "歌手を志す美代子と水泳部の重要メンバーである杏子、久子、早苗の仲良し４人組。ある日、杏子は人形劇団で各地を巡演している青年に好意を寄せるが、青年は早苗に愛を告白する。杏子の気持ちを知っている早苗は悩むが……。市村泰一監督による青春ドラマ。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DILWALE　DULHANIA　LE　JAYENGE", "シャー・ルク・カーンのDDLJラブゲット大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 183, "「ラジュー出世する」で日本でも知られるインドのトップ・スター、S・R・カーン主演のラブ・ストーリー。まだ見ぬ許婚との結婚を控えた女子学生シムランは友達とヨーロッパ旅行に出かけるが、その初日、彼女はお調子者のプレイボーイ、ラージと出会う。ラージの軽さについていけないシムランは彼を避け続けるが、やがて二人は恋に落ちて……。親の決めた結婚のためにインドに帰ったシムランと周囲に自分を認めさせるために奮闘するラージ。二人の恋の行方を、歌あり踊りありのダイナミックな展開で見せていく。", "1995-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロマンチック・アイランド", "ロマンチック・アイランド", "", "", "http://image.eiga.k-img.com/images/movie/54846/315x210.jpg?4022", 105, "ドラマ「コーヒープリンス1号店」のイ・ソンギュン、「タチャイカサマ師」のイ・スギョンら若手韓流スターが集結したラブコメディ。クビの危機に立たされたイケメンCEOジェヒョク、職場でも家庭でも嫌な思いばかりしているOLスジン、面接で失敗続きの就職浪人ジョンファン、嫌われ者のスーパースター、カヨン。クリスマス直前、辛い現実を忘れようとフィリピンのボラカイ島に出かけた4人は、そこで夢のようなロマンスに身をゆだねる。", "2009-11-21", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TENBATSU", "TENBATSU", "", "", "http://image.eiga.k-img.com/images/movie/55636/315x210.jpg?3718", 60, "“学校の物置き部屋にある絵馬掛けに嫌いな相手の名前を書いた石を吊るすと、呪いの執行人ホーリー様が現われて天罰を下す”という噂が伝わる黒川高校。ホラー文学研究部に所属する結季は、部員の綾美の作品がホラー文学大賞の佳作に選ばれたことを知り、しっと心から呪いの儀式を行ってしまう。キャストには「君と歩こう」の吉谷彩子、「うた魂♪」の吉川まりあ、グラビアアイドルの小泉麻耶らフレッシュな顔ぶれがそろう。", "2010-09-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ULTIME GRIDA DALLA SAVANA", "グレートハンティング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "公開当時、ショッキングなシーンの連続で話題を集めたドキュメンタリー映画。すべての歴史を狩るもの狩られるものの闘争の歴史として捉え、人間がライオンに襲われるシーンをはじめ、“人間狩り”の一部始終など、目を覆うばかりの残酷な場面が収められている。", "1975-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナミイと唄えば", "ナミイと唄えば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　9歳で那覇のお座敷に身売りされて以来、85歳になる今日まで、三線（さんしん）を手に放浪の人生を送り、03年にはCDデビューも果たした元気な“おばぁ”、通称ナミイこと新城浪（あらしろなみ）と、“人間ジュークボックス”と呼ばれる彼女の歌に魅了されていく人々の姿に迫るドキュメンタリー。監督は、「アレクセイと泉」でチェルノブイリ原発事故後のベラルーシを描き、国際的にも高い評価を得た本橋成一。", "2006-03-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABC　AFRICA", "ABCアフリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "イランの巨匠A・キアロスタミが国連の国際農業開発基金からの要請を受け、アフリカのウガンダで撮影したドキュメンタリー。エイズや内戦によって無数の子供たちが孤児としての暮らしを強いられているこの国の現状を、デジタルカメラで記録している。悲惨な状況の下でも、たくましく、時に無邪気に生きる子供たちの生命の輝きを捉え、そこにかすかな希望を見いだそうとするキアロスタミの視線が観る者の胸を打つ。", "2001-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Night of the Living Dead : 30th Anniversary Edition", "ナイト・オブ・ザ・リビング・デッド　最終版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　甦った死者たちが生者を襲撃。襲われた者もやがてゾンビと化していく！　ジョージ・A・ロメロ監督の代表作であり、恐怖の代名詞となったゾンビ（リビングデッド）・シリーズ第1弾のリニューアル・バージョン。脚本を担当したジョン・A ・ルッソの演出による新たな導入部と神父の姿を描いたエピローグが追加・再編集された。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暁の脱走", "暁の脱走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "田村泰次郎原作の『春婦伝』を谷口千吉と黒澤明が脚色し、谷口千吉が監督した反戦映画の力作。舞台は戦争末期の中国中部戦線。敵の捕虜となり軍へ送還されて来た三上上等兵と慰安婦の春美は、いつしか激しい恋に落ちる。自由を求めて二人は脱走を計るが、機関銃の銃弾を浴び、折り重なって息絶えてしまう……。「銀嶺の果て」や「ジャコ万と鉄」で日本人離れしたダイナミックなアクション・タッチを見せ、黒澤明の好敵手として注目を集めていた谷口千吉の代表作。非人間的な日本軍の内部機構を、真正面から批判した最初の作品で、その後の反戦映画に大きな影響を与えた。同じ原作を鈴木清順監督が1965年日活で「春婦伝」という題で再映画化した。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ステキな金縛り", "ステキな金縛り", "", "", "http://image.eiga.k-img.com/images/movie/55546/315x210.jpg?1603", 142, "「ザ・マジックアワー」の三谷幸喜監督が同作以来3年ぶりにメガホンをとり、法廷サスペンスやファンタジーの要素も盛り込んで送り出すオリジナル長編コメディ。三流弁護士のエミが、担当する殺人事件の弁護のため、被告人のアリバイを唯一証明できる落ち武者の幽霊・更科六兵衛を法廷に引っ張り出そうと奮闘する姿を描く。主演は深津絵里、西田敏行。共演に竹内結子、浅野忠信、篠原涼子、佐藤浩市ら豪華キャストが集う。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("馬鹿息子", "馬鹿息子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "金融界の大物の息子バーディは、あまりの頼りなさゆえに父から勘当されるが、やがて株の仲買人の資格を取って父の危機を救い、愛しき恋人との結婚にまでこぎつける……。チャールズ・チャップリン、ハロルド・ロイドと並ぶ映画黎明期の喜劇王バスター・キートンによる、記念すべき長編映画主演デビュー作。", "1920-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOCK−UP", "ロックアップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "S・スタローンが挑んだ、パワフルで感動あふれるヒューマン・バイオレンス・アクション。６ヵ月後に出所を控えたフランクは、ある夜突然移送される。行き先は“地獄の館”といわれるゲートウェイ刑務所。殺意をむき出しにしてフランクを迎えるドラムグール所長。そして彼の残忍なイビリが始まった……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fly", "ザ・フライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　科学者のセスは記者のベロニカに開発中の物質転送装置を公開する。生物の転送実験で失敗が続くが、やがてセスは自らの体を転送することに成功。しかもその後、彼の体には驚異的な活力が備わる。セスは、転送装置に一匹のハエが紛れ込んでいたこと、そしてそれが転送後にセスの体と遺伝子レベルで融合したことを知る。彼の肉体はみるみる変化し、ついには惨たらしい姿に……！　58年作「蝿男の恐怖」をリメイク。おぞましくも悲痛なドラマが展開する。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Longest Yard", "ロンゲスト・ヤード", "", "", "http://ecx.images-amazon.com/images/I/51pelnwX1VL._SL210_.jpg", 121, "　かつてアメフトの花形選手だったポールは車を盗んだ罪で刑務所に送られる。看守たちのフットボール・チームを育成することにやっきの所長へイゼンはポールにコーチ役を命ずる。やがてポールは看守チームの相手となる囚人チームの結成に同意し、荒くれ者たちを次々とメンバーにしていく。かくして看守チーム対囚人チームが激突することに！　男たちの闘いを描いた痛快スポーツ・アクション。05年にはリメイクも製作された。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裏町のお転婆娘", "裏町のお転婆娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "東京に家出してきた娘・エミーが、ひょんなことから興行界の大御所・大山の孫娘と勘違いされ、ジャズ・ショーのスターとして売り出されるハメに……。裕次郎もので有名な井上梅次によるミュージカル・コメディ。「オーケストラの少女」の日本版ともいえる作品。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Il papa di Giovanna", "ボローニャの夕暮れ", "", "", "http://image.eiga.k-img.com/images/movie/55206/315x210.jpg?3367", 104, "イタリアの名匠プピ・アバティが、第2次世界大戦前後の故郷ボローニャを舞台に描く家族ドラマ。17歳の女子学生ジョバンニは、心優しい父ミケーレと美しい母デリアと、慎ましくも平穏に暮らしていた。だがある日、ミケーレが勤務する学校で殺人事件が起こり、被害者と同級生だったジョバンニに容疑がかけられてしまう。さらに事件に追いうちをかけるように、戦火は激しさを増していく。父親役のシルビオ・オルランドが第65回ベネチア国際映画祭主演男優賞を受賞。", "2010-06-26", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JE VOUS AIME", "ジュ・ヴ・ゼーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "35歳の彼女は、未婚の母のキャリアウーマンで、常に何人もの恋人がいる。しかし、なぜか恋は実らず、いつも破局を迎えてしまうのだった。人気絶頂期のC・ドヌーヴ主演にもかかわらず、長い間公開されず、1996年ゲンズブール特集の時に初めて公開された。", "1980-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE FLEW OVER THE CUCKOO’S NEST", "カッコーの巣の上で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "1960年代アメリカのキャンパス・カルト小説の映画化。刑務所の強制労働を逃れて精神病院に入った男が、ラチェッド婦長のもと、無気力に管理される患者たちを生き返らせるが……。人間の自由と尊厳をテーマに、一人の男の悲劇を描いた傑作だ。原作とは相当ニュアンスは違うが、痛切な人間ドラマとして見ごたえ十分。作品賞をはじめアカデミー主要５部門受賞。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAR’S　KISS", "ベアーズ・キス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「コーカサスの虜」で知られるロシアの名匠セルゲイ・ボドロフが撮り上げた温かくファンタスティックな物語。サーカス団の少女と、人間に姿を変えられる熊が、ヨーロッパをめぐるサーカスの道程でひそかに愛を紡いでいくラブ・ストーリーだ。カラフルな色彩で画面を彩りつつ、ロード・ムービー仕立てで展開し、少女の成長や心の動きを丁寧に描いている。", "2002-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Inside Man", "インサイド・マン", "", "", "http://image.eiga.k-img.com/images/movie/33566/315x210.jpg?3054", 128, "「マルコムX」「ラストゲーム」のスパイク・リー監督とデンゼル・ワシントンのコンビが、共演にジョディ・フォスターとクライブ・オーウェンを迎えた犯罪サスペンス。マンハッタンの銀行で強盗事件が発生。警察、銀行強盗、両者とも譲らず神経をすり減らすような心理戦を展開する中、銀行の女性顧問弁護士が交渉人として現場にやってくる……。", "2006-06-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火のように", "火のように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "妊娠中の妹と、妻に先立たれ、故郷で静かに暮らす男を軸に、仏教的な死生観を含めた人間ドラマ。監督を筆頭に、はじめにきよしなど、関西で活躍するアーティストが多数参加している。故郷の村で映画の製作を終えた作家の由紀は妊娠中だが、出産の決心が着かない。そのせいか、夫の元へ戻りづらい彼女はそこに暮らす兄と生活を始める。そんなとき、村で殺傷事件が起き……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Antonio das Mortes", "アントニオ・ダス・モルテス", "", "", "http://image.eiga.k-img.com/images/movie/42326/315x210.jpg?3302", 100, "領主に歯向かう山賊たちを討伐するため雇われた殺し屋のアントニオ・ダス・モルテスは、残虐非道な支配者たちの姿を目の当たりにし、いつしか虐げられている農民を解放するために立ち回るようになる。ブラジルの新しい映画＝シネマ・ヌーボの旗手として賞賛されたグラウベル・ローシャ監督の代表作。1970年日本公開。2011年、ローシャ監督没後30年を記念した「グラウベル・ローシャ・ベスト・セレクション」にてリバイバル。", "2011-06-18", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Butterfly Effect 2", "バタフライ・エフェクト2", "", "", "http://image.eiga.k-img.com/images/movie/34246/315x210.jpg?2341", 92, "過去に戻り現実を変える能力を手に入れた青年の切ない運命を描き話題を呼んだSFサスペンス「バタフライ・エフェクト」の第2弾。26歳のニックは、恋人ジュリーの誕生日パーティの帰り道に自動車事故に遭い、ジュリーと親友を亡くしてしまう。それから1年後、失意の底にいたニックは、懐かしい写真を見つめている内に激しい頭痛に襲われる。次の瞬間、彼はその写真を撮った場面にタイムスリップしており……。", "2007-10-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　空の境界　第六章　忘却録音", "劇場版　空の境界　第六章　忘却録音", "", "", "http://image.eiga.k-img.com/images/movie/54216/315x210.jpg?3074", 0, "全7章からなる奈須きのこの小説「空の境界」を原作に、万物の死を見ることのできる能力「直死の魔眼」を持つ少女・両儀式の戦いを、全7部作でアニメ映画化した人気シリーズの第6弾。黒桐鮮花は兄である黒桐幹也に想いを寄せていたが、幹也は両儀式に惹かれていた。鮮花は式を駆逐するため、蒼崎橙子に入門し魔術師の見習いとなるが……。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　FOG　OF　WAR", "フォッグ・オブ・ウォー／マクナマラ元米国防長官の告白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "元米国国防長官ロバート・マクナマラが、戦争そのものの在り方に物申した異色のドキュメンタリー。マクナマラはハーバード大学院卒のエリートとして第二次大戦の東京大空襲にかかわり、フォード自動車の社長に抜擢され、ケネディ大統領に請われて閣僚入りしたという政財界の超大物。かつて“戦争屋”と非難された男が語る“戦争への疑問”は、短絡的な反戦論ではないだけに説得力十分。ベトナム戦争やキューバ危機など世界史を揺るがす裏話も飛び出し、イメージ映像の洪水と相まって異様な迫力を感じさせる。一体なぜ東京大空襲で民間人が虐殺されたのか？　ベトナム戦争の泥沼化の原因は？　キューバ危機を回避できた理由とは？　近代史に影を落とす国際紛争の最前線にいた元国防長官マクナマラが、戦争の裏側を回想する。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彩恋", "彩恋", "", "", "http://image.eiga.k-img.com/images/movie/34106/315x210.jpg?1442", 91, "「ハチミツとクローバー」の関めぐみ、「スウィング・ガールズ」の貫地谷しほり、「フラガール」の徳永えりら注目の若手女優が共演する青春ラブ・ストーリー。のどかな地方都市に暮らす高校3年生のナツ、ココ、マリネは、いつも一緒の仲良しトリオ。性格も悩みもバラバラの3人だったが、互いに支えあいながら18歳の多感な時期を過ごしていた。そんな彼女たちの等身大の姿を、ユーモアを交えつつさわやかに描き出す。", "2007-08-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人生劇場・飛車角", "人生劇場・飛車角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "横浜の遊女・おとよと逃げ延びてきた飛車角は、小金親分の計らいで深川の裏町に住むことになる。しかし結局、罪の償いに飛車角は警察に自首。ところが、飛車角の留守中、おとよは宮川と恋に落ちて……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鍵がない", "鍵がない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "鍵を失くして町をさまよう女性の一夜を、CMプランナーの山田英治が繊細に描いたハートフル・ドラマ。さまざまな悩みを抱えた人々と出会う中で、別れた恋人への未練を断ち切る彼女の心の揺れ動きを、温かいまなざしで見つめる。つぐみ、大森南朋ら、演技派陣が微妙な心情を表現している。家の鍵を失くした美沙子が、元カレの良介に鍵を預けていたことを思い出す。彼女は良介に電話をするが、彼の隣には新しい彼女がいて……。", "2005-10-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WOLVES OF WILLOUGHBY CHASE", "ウィロビー・チェイスのおおかみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "J・エイケンの児童文学を映画化したファンタスティック・アドベンチャー。狼の群れが生息する地方に住むウィロビー卿の娘ボニーと従兄弟のシルヴィアは、両親の留守の間、家庭教師スライカープとともに生活することになった。しかしこのスライカープ、実は卿の財産を乗っ取ろうと企んでいたのだ。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BARON　PRASIL", "ほら男爵の冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "トルンカ、ティールロヴァーと並び称されるチェコ・アニメの巨匠K・ゼマン監督が、1961年に発表した名作。通常の概念にまったく囚われないゼマンのイマジネーションあふれる世界観は今見ても新鮮だ。日本では2004年に初めて公開された。月面に降り立った宇宙飛行士のトニーク。人類初の快挙に興奮する彼だが、そこにはすでにほら男爵、ミュンヘンハウゼンがいた。", "2004-12-25", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARTNERS", "パートナーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "“ゲイ”をネタにした、少々アブナイ警察アクション・コメディ。殺人事件を捜査するため、二人の警官がゲイの夫婦を演じるハメに、ところが片方が本物のゲイ。ホモでない相棒は大弱り……。恋愛騒動にまで発展していく珍捜査ぶりがおかしい。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雷電", "雷電", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "傑作「東海道四谷怪談」などで知られる中川信夫監督が、尾崎士郎の原作をもとに映像化した作品。浅間山麓の庄屋の屋敷に奉公する16才の少年・太郎吉。ひょんなことから屋敷にやって来た大相撲・浦風部屋の親方に、その体格のよさと力士としての才能を見いだされる太郎吉だったが……。大関・雷電為右衛門の青春を情感豊かに描き出す。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("路傍の石", "路傍の石", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "山本有三の同名小説を戦後３度目の映画化。総代として学校を卒業した吾一は将来の夢を思い描いていたが、飲んだくれの父のために進学できずに呉服屋に奉公に出る。つらい日々を耐えながら、同じ境遇の友人たちとの語らいに吾一は勇気づけられていく……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PERILS OF PAULINE", "ポーリンの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "サイレント映画時代、連続活劇の女王として有名な名女優パール・ホワイトの半生をB・ハットンが演じる伝記映画。舞台女優を目指しつつも、肌に合わない舞台演技に見切りをつけてハリウッドにやって来たパールの生きざまを、G・マーシャル監督はミュージカル仕立てでのびのびと描いていく。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("この空の花　長岡花火物語", "この空の花　長岡花火物語", "", "", "http://image.eiga.k-img.com/images/movie/57866/315x210.jpg?2137", 160, "2004年の新潟県中越地震から復興をとげ、11年の東日本大震災発生時には被災者をいち早く受け入れた新潟・長岡市を舞台に、ひとりの女性新聞記者がさまざまな人と出会い、不思議な体験を重ねていく姿を大林宣彦監督が描く。11年夏、熊本・天草の地方紙記者の玲子が新潟・長岡を訪れる。目的は、中越地震を乗り越え復興し、東日本大震災の被災者をいち早く受け入れた同地を取材すること。そして、長年音信不通だった元恋人からの「長岡の花火を見てほしい」という便りに心ひかれたためだった。", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nanny McPhee Returns", "ナニー・マクフィーと空飛ぶ子ブタ", "", "", "http://image.eiga.k-img.com/images/movie/55366/315x210.jpg?4362", 0, "人気作家クリスティアナ・ブランドの小説「ふしぎなマチルダばあや」を、アカデミー賞女優エマ・トンプソンが脚色し、自ら主演した「ナニー・マクフィーの魔法のステッキ」の続編。戦地に行っている父親に代わって牧場をなんとか切り盛りしようと試みる若い母親を助けるため、田舎にやってきた乳母のマクフィーだったが、ここでも、魔法を使って3人の子どもたちと、2人の甘やかされた従兄弟を躾けることになる。共演にマギー・ギレンホール、リス・エバンス、マギー・スミス、ユアン・マクレガー。", "2011-07-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESERT BLOOM", "デザート・ブルーム～キノコ雲と少女～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "義父の浮気の現場を目撃し、ショックを受け家出したローザは、核実験が予定されるネバタ砂漠に迷い込んでしまう。彼女が無事帰ったあと、核実験は行われるが……。13歳の少女の目を通して、核実験の不安を家庭内の不安と対比させて描いた佳作。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("13GHOSTS", "13ゴースト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "妻を失った悲しみから立ち直れずにいるアーサーとその子供たちは、大富豪の叔父サイラスの遺産として巨大な屋敷を譲り受けることになる。彼らは早速その屋敷を見に行くが、それは鋼鉄と強化ガラスで作られた奇妙な屋敷だった。実はサイラスは生きており、ここに悪霊を集めて伝説の儀式を行い、最強のパワーを得ようとしていたのだ……。恐るべき計画を知らず屋敷に足を踏み入れたアーサーたちが体験する数々の恐怖を描いたギミック・ホラー。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Planet B-boy", "プラネット B-BOY", "", "", "http://image.eiga.k-img.com/images/movie/55136/315x210.jpg?1299", 95, "ブレイクダンスの世界大会「バトル・オブ・ザ・イヤー・ファイナル」の2005年大会に出場したダンサーたちのドキュメンタリー。本大会で準優勝を勝ち取った日本代表チーム・一撃、厳格な家庭の中でダンス活動を認めてもらおうとする韓国のダンサー、人種的偏見を持った家族と戦うフランスの少年ダンサーなど、さまざまな事情を抱えるダンサーたちの素顔に迫る。", "2010-01-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風のかたみ", "風のかたみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "日本の古典文学『今昔物語』をベースにした福永武彦の小説を映画化、妖しく、あでやかな王朝ロマンが展開される。平安時代後期、信濃の若者・大伴次郎親信は、京の都に向かう途中、妖艶な女陰陽師・杜女と出会い、謎めいた笛を与えられる。笛を手に上京した次郎は、身を寄せた中納言の家でその娘・萩姫に恋をする。しかし、姫は後宮への入内が決まっている上に、色事師・安麻呂に心を奪われていた。ある日、安麻呂と萩姫が盗賊に襲われるという事件が起こり、姫の気持ちを知った次郎は姫の文を安麻呂に届けることになるが……。シナリオを読んだ岩下志麻が出演を希望した作品で、岡本喜八監督の夫人である岡本みね子が製作している。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("この天の虹", "この天の虹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "九州の八幡製鉄所とその社員住宅の人々の生活を描いたホームドラマ。一帯が製鉄所の社員が住む街で、正社員と下請け企業の家庭の格差、そこに起こるあつれきを映し出した社会的なテーマ性が見もの。題名の「この天の虹」とは、工場の煙突が生み出す多様な煙のこと。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLAYERS", "ウィンブルドン愛の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "テニス界の最高の名誉、ウィンブルドン世界選手権を目指す若きテニス・プレーヤーと、謎に満ちた美女が織り成す、激しくも切ないラブ・ロマンス。ビラスをはじめ、有名プレーヤーが出演。観客席のマーガレット王女を捉えた実写を使うなど、臨場感も抜群。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男たちの大和 YAMATO", "男たちの大和 YAMATO", "", "", "http://image.eiga.k-img.com/images/movie/1426/315x210.jpg?4393", 143, "東映と角川春樹が、戦後60年記念作品として世界最強最大と謳われた「戦艦大和」に乗り込んだ乗組員たちの悲劇を壮大なスケールで描いた超大作。05年4月6日、鹿児島県枕崎の漁港を訪れた1人の女性が、老漁師に北緯30度43分、東経128度4分まで船を出してほしいと懇願する。しかし、そこはかつて老漁師が新兵として乗り込んでいた戦艦大和が60年前の昭和20年4月7日に沈んだ場所だった。監督・脚本は「新幹線大爆破」の佐藤純彌。反町隆史、中村獅童、渡哲也、鈴木京香、仲代達矢ら豪華キャストが共演。", "2005-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファンキー・モンキー・ティーチャー", "ファンキー・モンキー・ティーチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "『ヤングマガジン』に連載された、もりやまつる原作の人気コミックを映画化した痛快作。吉本新喜劇のスーパースター・間寛平が主人公のハチャメチャ教師・戸沢役を快演、出来の悪い生徒を相手に葛藤し、奮闘していく姿をコミカルに演じている。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEON EMPIRE", "ネオン・エンパイア バグジーの野望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "1930年代後半、まだ小さな田舎町にすぎなかったラスベガスに、一大カジノを備えた超豪華ホテルを建てようとしたギャングの物語。バリー・レビンソン監督の「バグジー」とはまた違った視点で、実在の大物ギャング“バグジー”をモデルに、金と欲にもてあそばれる男たちの闘争を描いていく。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fakers", "FAKERS フェイカーズ 4日間で借金を返す法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "", "2007-03-26", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ANIMATRIX", "アニマトリックス", "", "", "http://ecx.images-amazon.com/images/I/51GQ8HDVJVL._SL210_.jpg", 101, "「マトリックス」の世界をトップ・アニメーターたちが自由な発想と手法で描いた９つのオムニバス・アニメ。“マトリックス”世界の成り立ちから、ネオたちとは異なる時空での物語、「…リローデッド」へと繋がる物語など、単なるサイド・ストーリーには留まらない広がりを見せる。マッドハウスやスタジオ４℃といった日本のアニメスタジオによるクオリティーの高い映像も圧巻。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Life of Pi", "ライフ・オブ・パイ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ASSASSINATION BUREAU", "世界殺人公社", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1906年、奇怪な殺人事件がヨーロッパで次々と起こる。調査記事を書くことになった女性記者ソーニャは殺人プロ協会会長のイヴァンと接触。彼は殺人をただのゲームと考え、殺人協会の会員が別の会員を殺すゲームを堪能していたのだった。動機がないのだから捕まるはずもなく、イヴァンは彼女を連れて会員暗殺の旅に出る。コミカル・タッチの冒険劇。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女", "女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "オール・ロケ、登場人物は男女二人だけという、木下惠介ならではの意欲的な趣向の作品。意志が弱くヤクザな仕事しかできない一人の男と、彼にふがいなさを感じながらもついていく一人の女の道行きを追っていく。木下組カメラマンとして知られる楠田浩之は、木下惠介の妹で脚本家でもある楠田芳子の夫。外景を生かした撮影が優れている。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pickpoket", "スリ　ニュープリント版", "", "", "http://image.eiga.k-img.com/images/movie/56676/315x210.jpg?1930", 74, "1999年に他界したフランス映画界の名匠ロベール・ブレッソンの名作をニュープリント版で上映する「映画の國名作選III ロベール・ブレッソンの芸術」にて上映。生まれつき手先が器用な貧しい大学ミシェルは、いつしかスリをして生計を立てていくようになる。最初は警察に捕まりもしたが証拠不十分で釈放され、次第に犯行は組織だったものになっていく。一方、母親のアパートの隣室に住むジャンヌはミシェルにひかれていくが……。", "2011-06-25", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2:37", "明日、君がいない", "", "", "http://image.eiga.k-img.com/images/movie/33936/315x210.jpg?2085", 99, "オーストラリアの新鋭ムラーリ・K・タルリ監督が、友人の自殺や自らの自殺未遂といった実体験を基に、思春期の若者たちが抱える心の闇を描き出した衝撃作。ハイスクールに通う6人のティーンエイジャー。様々な悩みや問題を抱え、その繊細な心は今にも押しつぶされそうになっていた。そしてある日の午後2時37分、そのうちの1人が自殺を図る。彼らのリアルな現実をそれぞれの視点で捉えながら、事件が起こるまでの1日を描いていく。", "2007-04-21", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIREHAWK", "ファイアー・ホーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ベトナム戦争の最前線で戦うヘリコプター部隊６人のサバイバル・アクション映画。ある時、６人の乗るヘリコプターがジャングル上空で故障し、墜落した。故意に部品が取りはずされていたことを知った隊長は、仲間に裏切り者がいると判断。疑惑の中でジャングル脱出のサバイバル戦が始まる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("舞妓Haaaan!!!", "舞妓Haaaan!!!", "", "", "http://image.eiga.k-img.com/images/movie/1946/315x210.jpg?2926", 120, "人気脚本家・宮藤官九郎が舞妓の世界を舞台に書き下ろした脚本を、阿部サダヲ、堤真一、柴咲コウ共演で映像化したハイテンション・コメディ。サラリーマンの鬼塚は“舞妓との野球拳が夢”というほどの舞妓好き。京都への転勤が決まり念願のお茶屋デビューを果たしたものの、野球拳まであと一歩という時に突然乱入してきたプロ野球選手・内藤に邪魔されてしまう。一方、鬼塚に捨てられた恋人の富士子は、舞妓になって彼を見返そうと決意する。", "2007-06-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("激動の1750日", "激動の1750日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "日本最大の暴力組織の三代目組長および若頭が相次いで急死。組長の残した“組織”という巨大な遺産をめぐり、組は現状維持を望む古参幹部と、現状に飽き足らぬ若手武闘派の真っ二つに分かれる。ヤクザ映画の集大成ともいえる実録タッチの集団抗争劇。本格的ヤクザに初挑戦の中井貴一が迫真の演技。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋子の毎日", "恋子の毎日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ジョージ秋山の同名コミックを原作にした青春ヤクザ映画。二枚目ヤクザのサブと、底抜けに明るい新妻・恋子の波乱に満ちた楽しい(？)生活を描いていく。松村雄基の男っぷりが見事にサマになっている。和泉監督の前作「この胸のときめきを」の高校生たちが一転してヤクザを演じるのも見どころ。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いばらの王 King of Thorn", "いばらの王 King of Thorn", "", "", "http://image.eiga.k-img.com/images/movie/54906/315x210.jpg?4280", 109, "岩原裕二による人気コミックを、「スチームボーイ」の製作チームがアニメーション映画化。致死率100パーセントの奇病メドゥーサの脅威にさらされた世界。人類は治療法の開発が期待される未来に向け、一部の人間にコールドスリープ処理を施す。その資格者に選ばれた少女カスミは、深い眠りののち、不気味なイバラと怪物が支配する世界で目を覚ます……。", "2010-05-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIN　ICH　SCHO¨N？", "アム・アイ・ビューティフル？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "「愛され作戦」のデリエ監督が自作短編集『あたし、きれい？』を自ら映画化。奇麗な人生なんてどこにもない。それでも“自分は奇麗に生きているかな？”と問いかけながら、一歩ずつ前進していく人々の日常をユーモアたっぷりに描いた人間ドラマ。「ラン・ローラ・ラン」のF・ポテンテが喪失から再生へと向かってたくましく生きるリンダを好演している。", "1998-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("七人の野獣 血の宣言", "七人の野獣 血の宣言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「七人の野獣」の続編。丹波扮する元刑事が、５人の仲間を集めて競馬場の売上金を強奪しようと画策する。前作では悪役だった宍戸錠が、逮捕された６人が出所してくるラストにちょっと出てきて、初めてタイトル通り“七人の野獣”になるあたりなかなか人を食った作品である。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Benda Bilili!", "ベンダ・ビリリ！　もう一つのキンシャサの奇跡", "", "", "http://image.eiga.k-img.com/images/movie/55486/315x210.jpg?1845", 87, "手作りの楽器でバンドデビューを果たしたコンゴの路上生活の姿を描く音楽ドキュメンタリー。内戦による混乱と貧困にあえぐキンシャサに、家がなく動物園で寝泊りする障害者たちがいた。彼らのバンド「スタッフ・ベンダ・ビリリ」による演奏を聞いたフランス人映像作家ルノー・バレとフローラン・ドラテュライはその音楽に魅了され、彼らのドキュメンタリー映画とアルバム制作を決意する。それから5年、数々の困難を乗り越えてアルバムは世界中で発売され、ついにはヨーロッパ・ツアーが実現する。", "2010-09-11", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thumbsucker", "サムサッカー", "", "", "http://image.eiga.k-img.com/images/movie/1706/315x210.jpg?4036", 96, "17歳になっても親指を吸う癖が直らない少年の心の成長を、ミュージッククリップやCFで活躍するマルチクリエイター、マイク・ミルズが独特なタッチで描いた青春映画。自分の親指をしゃぶる癖が直らずに悩んでいるオレゴン州の高校生ジャスティンは、悩みを解消するために催眠術、薬物などありとあらゆる方法を試すが……。「ナルニア国物語」のティルダ・スウィントンやキアヌ・リーブス、ビンス・ボーンらスター俳優が脇を固めている。", "2006-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("That's the Way of the World", "ザッツ・ザ・ウェイ・オブ・ザ・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/33926/315x210.jpg?1284", 100, "ソウル・ミュージックの帝王アース・ウィンド＆ファイアーの絶頂期の映像を満載した音楽ドラマ。敏腕音楽プロデューサーのコールマンは、デビューを控えた新人R＆Bグループ“グループ”のレコーディングに力を注いでいた。ところが、会社の上層部から白人ファミリー・トリオ“ペイジズ”のプロデュースを命じられ、コールマンは自分が望む音楽と会社が求める音楽との間で決断を迫られる。主演は若き日のハーベイ・カイテル。", "2007-04-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花祭底抜け千一夜", "花祭底抜け千一夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "東京の下町“いろは横丁”の八百屋一家を舞台に、肉親の預かりっ子で歌の才能のある末娘・トモ子が、皆の愛情に包まれて明るく元気に育っていく様子を描いたジャズ喜劇。当時、少女歌手として人気を集めていた松島トモ子が末娘役を演じている。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自由", "自由", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 37, "インディペンデントの俊英、福島拓哉監督が、自身が体験した不思議な出来事をもとに、監督・脚本・主演の三役をこなした短編作品。前半は、ありきたりな毎日を送る女子高生、ミカ、後半は、ミカのメル友のサラリーマン、テルを主人公にした二部構成で、全編を通して、二人のメールでの会話が表示される特殊な手法でストーリーが展開され、どうにもならない現実を綴っている。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Iron Man 3", "アイアンマン3（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("倩女雲雨情", "チャイニーズ・ゴースト・ウェディング 霊幻花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "モデル出身の香港人気女優エミリー・チュウ主演。日本からもAV女優、伊藤さやかが出演。ある夜、町の青年が美女と出会って一目惚れ。二人は恋に落ちてしまうが、実は彼女は妖怪だった。青年が何も知らぬ間に時は経っていくが、彼の親友が彼女の正体を見破る。ついに彼女の背後にいる魔王との闘いが……。", "1989-01-01", "6,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おみすてになるのですか　傷痕の民", "おみすてになるのですか　傷痕の民", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "太平洋戦争の敗戦から65年がたった2010年。戦争は過去の出来事、歴史になりつつあったが、空襲などで負傷し後遺症に苦しむ多くの民間人が、国から補償の手も差しのべられずにいまだ取り残されていた。戦後65年を生きてきた戦災傷害者たちの現実に迫ったドキュメンタリー。", "2010-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NETTOYAGE A｀ SEC", "ドライ・クリーニング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1997年度ヴェネチア映画祭で、最優秀脚本賞を受賞。女性監督A・フォンテーヌが、軽快なコメディ「おとぼけオーギュスタン」から一転、繊細で官能的な愛の物語を作り上げた。パリ郊外の小さな町。ジャン・マリーとニコルの夫婦が経営するクリーニング店に美青年ロイックが現れる。彼は昨夜ナイトクラブのショーで見たドラッグクィーンの青年だった。唯一の肉親である姉とコンビを組んで各地を巡業しているという彼と親しくなるにつれ、妻ニコルはとうとう一線を越えてしまう。それを目撃した夫ジャン・マリーもまた、魅惑的なロイックに心を惑わされていた。奇妙な関係で結ばれた３人の間に、やがて衝撃的な事件が起こる……。", "1997-01-01", "2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("McQ", "マックQ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "警察の上層部も絡んだ、シアトルの組織犯罪を一掃するため、すご腕の初老の刑事が決然と立ち上がった……。西部劇のスター、J・ウェインがウインチェスターと馬を、特製ピストルと新型車に変えて奮戦する刑事アクション。ラストの海岸線での銃撃戦は、スピード感にあふれて痛快きわまりない。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NUTTY NUTS", "12人のイカれた男たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "精神病院で育ち“12人格”になってしまったネーサンは、生き別れになった双子の兄フィルバートを探し出す。フィルバートは、セクシーな愛人と豪邸に住み、次期大統領を狙っているという超エリート。そんな兄のパーティーで、ネーサンの12人の人格が現れたから、さあ大変！　全米で人気のコメディアン、S・カーニーが特殊メイクや顔芸を駆使して、エルヴィスやモンローなど、12人の人格を演じ分けるドタバタ・コメディ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("藤十郎の恋", "藤十郎の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "元禄時代の京都南座の役者・坂田藤十郎は、近松の『大経師昔暦』の濡れ場をいかにしてリアルに演ずればよいか悩む。女を自殺させてまでもその芸をきわめようとする残酷非情な坂田の芸道精神を描く大作。林長二郎改め長谷川一夫の東宝移籍第１回作品。助監督は黒澤明。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺たちの明日", "俺たちの明日", "", "", "http://image.eiga.k-img.com/images/movie/53496/315x210.jpg?2476", 93, "韓国の新鋭ノ・ドンソク監督が、デビュー作「マイ・ジェネレーション」に続き、社会の底辺で生きる若者たちの無軌道な青春をリアルに切り取った長編第2作。レンタカー屋で働く青年ギスは、子供の頃に誤って性的障害を負わせてしまった友人ジョンデに対し、いまだに罪悪感を抱いていた。一方、思い通りにいかない日常に嫌気がさしたジョンデは、ギスの制止も聞かず怪しげな風俗店で働き始める。ある日、ジョンデは偶然銃を手に入れ……。", "2008-03-22", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO KILL A DRAGON", "ドラゴンを消せ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "刑務所に収監されている空手の達人ボルトが、100万ドルの現金を香港からロサンゼルス経由でメキシコシティに運ぶ約束で釈放される。ロスでは謎の殺し屋一味に狙われ、ラスベガスでは、持っているアタッシュケースの中身がすべてニセ札であることに気付いたボルトは、事件真相の謎を探る決意を固める。当時の空手映画ブームに便乗して製作された、アクション・ドラマ。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTERMISSION", "ダブリン上等！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "アイルランドの首都ダブリンを舞台にした活きのいい群像コメディ。街を徘徊するダメ人間たちが、ひょんなことから銀行強盗騒ぎを巻き起こす。企画に惚れ込んだコリン・ファレルがどうしようもないチンピラ役に扮しただけでなく、エンドクレジットで歌声まで披露する。恋人デイドラの気持ちを試そうと別れ話を持ち出し、フラれてしまったジョンは、チンピラのレイフの話に乗って銀行強盗に参加する。", "2005-02-19", "3,33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hobbit: There and Back Again", "ホビット　ゆきて帰りし物語", "", "", "http://image.eiga.k-img.com/images/movie/56936/315x210.jpg?3192", 0, "「ロード・オブ・ザ・リング」の前章となる「ホビットの冒険」を映画化する2部作の後編。", "2013-12-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER　TIGER　VON　ESCHNAPUR／DAS　INDISCHE　GRABMAL", "大いなる神秘　第１部　王城の掟　第２部　情炎の砂漠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 198, "「条理ある疑いの彼方に」でハリウッドを去った巨匠F・ラングが、それまで撮っていた数々のフィルム・ノワールから一転、おおらかなタッチで描き上げた冒険ロマン。インドを訪れたドイツ人建築家ハロルドが、シータという美貌の踊り子と恋に落ち、彼女に偏執的な愛情を抱くチャンドラ王の元を逃げ出すまでが第１部「王城の掟」。チャンドラに監禁されたハロルドが捨て身の反撃に出るクライマックスが、第２部「情炎の砂漠」で描かれるという構成になっている。眩い色彩で映像化されたエキゾチックなオリエンタリズムが見どころだが、第２部で主人公が迷宮のごとき地下世界を延々とさまよう描写が、いかにもラングらしい強烈な印象を残す。", "1958-01-01", "7,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("短い労働の日", "短い労働の日", "", "", "http://image.eiga.k-img.com/images/movie/54496/315x210.jpg?3910", 74, "1976年、ポーランド中東部の工業都市ラドムの労働者たちが、食料品の高騰を理由に暴動を起こした。やがてポーランド中に広がった暴動の様子を、ポーランドが生んだ名匠クシシュトフ・キエシロフスキー監督がドラマ化。1981年に製作されたが、事件の真相を浮き彫りにした同作は政府の検閲によって上映禁止となり、その後も劇場公開されることはなかった。キエシロフスキー監督死後の1996年にテレビ放映された。", "2009-06-25", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALEX＆EMMA", "あなたにも書ける恋愛小説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「恋人たちの予感」のR・ライナー監督によるラブ・ストーリー。借金の取り立て屋にパソコンを壊された新進作家アレックス。30日で借金を返済しなければ命の保証はない。慌てた彼は小説を書き上げるために速記係エマを雇う。二人は恋愛観を対立させながら恋愛小説の執筆に取りかかるが……。 現実のラブ・ロマンスと恋愛小説の中の恋がシンクロしながら進展するユニークなスタイルで、いつの時代になっても変わることのない男と女の恋模様を描き出す。K・ハドソンが作中劇のパートで４役に挑戦。彼女のキュートな魅力が満載の一編だ。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVIL SENSES", "ベレッタの女 最後の誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "殺し屋マヌエルはふとしたことから組織幹部の名簿を手に入れ、その日から刺客に追われることになる。ロンドンからローマへと逃亡するなか、そこで出会った美しく妖しげな高級娼婦ヴィットリアに心惹かれるが、実は彼女は組織が送った最後の殺し屋だった。いつしかヴィットリアも恋に落ちるのだが……。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブラックスクール　黒白 KOKUHAKU", "ブラックスクール　黒白 KOKUHAKU", "", "", "http://image.eiga.k-img.com/images/movie/56566/315x210.jpg?4729", 82, "刑事・田代の裏の顔が暴かれナゾの組織に一歩近づいた警察は、田代とルール違反を犯したイレイザーたちをかくまい、捜査を続ける。警察は組織から命を奪われることになった田代をおとりにするが、新しい刺客たちが現れ……。「イレイザー」と呼ばれる高校生の殺し屋たちの戦いを描く「ブラックスクール　裏黒 RIGURO」に続くシリーズ第2作。監督は自身もアクション俳優として活動する佳本周也。", "2011-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年期", "少年期", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "心理学者・波多野勤子と息子との往復書簡をまとめた“カッパブックス”シリーズの同名ベストセラーを映画化した作品。戦時中、自由主義者で読書ばかりしている学者を父に持った少年が、自らが受けた軍国主義教育や周囲の人間の愛国的行動との狭間で悩む姿が描かれている。脚本は本作が２作目の田中澄江。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPECTER〈スペクター〉", "SPECTER〈スペクター〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 41, "最新のCG技術と特殊造形を駆使して作り上げた、特撮アクション・ムービー。悪の生命体の侵略が日常と化した地球を舞台に、地球防衛局の最強を誇るヒーロー、スペクターの活躍を描く。ゲーム業界屈指の映像制作集団として知られる“ビルドアップ”のハイクオリティなSFX映像が見どころ。'05年、地球侵略を目論む悪の生命体“インヴェーダー”が日本に出現。この危機に地球防衛局最強の男、スペクターが立ち上がる。", "2005-07-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌９", "釣りバカ日誌９", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "ハマちゃんとスーさんの、おなじみ釣りバカコンビが繰り広げるにぎやかな騒動を描いた、人情コメディのシリーズ第10作。万年平社員のハマちゃんが勤める営業３課に、同期の馬場が新任の部長として配属されてきた。生マジメで仕事一筋の馬場だが妻とは別れ息子と二人暮らし、親子の関係もギクシャクしていた。そんな馬場にある時、５年も通っているバーのママに惚れていることを告白されたハマちゃんだったが……。仕事先の“お得意さま”への挨拶まわりに出かけたハマちゃんの、八面六臂の“釣り談議”がなんともおかしく、お約束の宴会芸も怪しくエスカレート。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLUE BUTTERFLY", "天国の青い蝶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "余命わずかの少年が、昆虫学者と一緒に、世界一美しいといわれる青い蝶“ブルーモルフォ”を探しに行ったという実話がベースの感動物語。少年の最後の願いが奇跡的な出来事を巻き起こす。「翼をください」で国際的に評価を得た女性監督L・プールが、きめ細やかな演出で、難病に悩まされながらも希望に満ちた少年の心を愛情深く描いた。脳腫瘍で余命数ヵ月と宣告された10歳の少年ピートには、幻の青い蝶が見たいという夢があった。それを叶えるため、有名な昆虫学者アランの所へ行く。最初はそっけない態度のアランだったが、ピートの熱意に負け、一緒に熱帯雨林へ向かう。", "2004-01-01", "10,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BED OF ROSES", "マンハッタン花物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ニューヨークを舞台に、心に孤独を抱える男女の恋を描いたラブ・ストーリー。仕事熱心で重役に抜擢されたキャリアウーマンのリサ。そんな彼女が、ただ一人の身内の死の知らせを聞いて悲しみにくれていた時に、送り主不明の素敵な花束が届く。差出人が配達人のルイスと知った彼女は、とまどいながらも心優しいルイスに惹かれる。やがて二人は恋に落ちるが、複雑な生い立ちから人を愛することを恐れるリサは、目の前の幸せに躊躇するのだった。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CREATOR", "クリエイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ノーベル賞科学者ハリー博士は、亡き妻の細胞を培養し、妻をよみがえらせようとしていた。実験に必要な若い女性の卵子を提供するのはメリー。一方、シド博士はハリーの行動に不審を抱き、ハリーを大学から追い出す。そしてメリーは……。奇妙な味わいのSFロマン。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONSIEUR VERDOUX", "チャップリンの殺人狂時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "30年間実直に勤めた銀行をクビになったベルドー氏は、金持ちの中年女と結婚しては殺し、保険金を奪う。トレードマークのチョビひげとステッキを捨て、戦争による大量殺人を痛烈に批判したブラック・ユーモア。マッカーシーの“赤狩り”旋風で「独裁者」と同様、上映中止の憂き目にあった。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alpha and Omega", "Alpha and Omega", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fur: An Imaginary Portrait of Diane Arbus", "毛皮のエロス　ダイアン・アーバス　幻想のポートレイト", "", "", "http://image.eiga.k-img.com/images/movie/1876/315x210.jpg?1554", 122, "「セクレタリー」のスティーブン・シャインバーグ監督が、ポートレイト写真に革命を起こした女性写真家ダイアン・アーバスの生涯を幻想的なタッチで映画化。50年代後半～60年代のニューヨークを舞台に、ファッションカメラマンの妻でアシスタントとして働くダイアンが自らの嗜好に目覚め、独創的な写真家へと変貌する姿を描く。ダイアン役にオスカー女優のニコール・キッドマン。", "2007-05-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EARTHLING", "アドベンチャー・ロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "不治の病にかかり、死に場所を求めてオーストラリアの山奥へ帰って来た男と、この地を旅行中、両親を不慮の事故で失っていた少年の出会いと交流。ホールデン扮する初老の男が少年に“サバイバル”の方法を教えていくさまが感動的な一編。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES DAMES DU BOIS DE BOULOGNE", "ブローニュの森の貴婦人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「罪の天使たち」に続くR・ブレッソンの第２作。フランスの哲学者ディドロの小説『運命論者ジャック』を題材に、二つの恋愛をめぐる４人の男女の心理を描く。衣装、表情、台詞、演技など日常性を極力避けたロマネスクで、古典的な世界は「ラルジャン」など後年の作風と一見違うようにみえるが、人々の心の中を外側から絞り込んでいくブレッソンの姿勢は変わることがない。", "1945-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌19 ようこそ！鈴木建設御一行様", "釣りバカ日誌19 ようこそ！鈴木建設御一行様", "", "", "http://image.eiga.k-img.com/images/movie/53336/315x210.jpg?3966", 0, "1988年にスタートした国民的人気シリーズの通算第21作目で、シリーズ初の社員旅行を描く。会長になったものの会社が心配で多忙な日々を送るスーさんと、相変わらず仕事そっちのけで釣りが命のハマちゃん。暴飲暴食のせいで会社の健康診断に引っかかるハマちゃんだったが、無事に社内旅行へ参加することに。旅行先の大分県でも釣りのことしか頭にないハマちゃんだったが……。ゲスト出演は常盤貴子、山本太郎ほか。", "2008-10-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北辰斜にさすところ", "北辰斜にさすところ", "", "", "http://image.eiga.k-img.com/images/movie/53166/315x210.jpg?2003", 111, "「草の乱」の神山征二郎監督が名優・三國連太郎を主演に迎え、旧制高校生たちの青春とその後の人生を描いた群像劇。戦前、鹿児島の旧制第七高等学校造士館で学友たちと青春を謳歌した上田勝弥は、卒業後に大学で医学を学び、戦時中は軍医として従軍。戦後に東京で開業した病院も現在は息子に任せ、悠々自適の生活を送っていた。しかし彼は、ある理由から鹿児島にも故郷の熊本にも行くことができずにおり……。", "2007-12-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逮捕しちゃうぞ the MOVIE", "逮捕しちゃうぞ the MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ビデオシリーズ、TVシリーズでも人気を博した藤島康介原作のアニメの初映画化作品。警視庁きっての問題児、墨東署交通課所属の夏実と美幸が、東京に襲いかかる危機に立ち向かっていく。工場跡地で見つかった戦争中の不発弾の処理作業が行われているころ、あるビルから密輸銃火器が発見された。その後、都内各地で連続する不可解な事件。それらが一点に結ばれたとき、大規模な爆弾テロ事件が勃発し……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃える闘魂アントニオ猪木50年の軌跡", "燃える闘魂アントニオ猪木50年の軌跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "「アントニオ猪木デビュー50周年DVD-BOX」発売を記念し、DVDの発売に先がけて特別編集版を劇場公開。1960年9月30日、東京・台東区体育館でプロレスラー・猪木寛至（現：アントニオ猪木）の戦いが始まった。伝説の名勝負や秘蔵映像、インタビューを交えて猪木の激闘の数々を振り返る。", "2010-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHIMES AT MIDNIGHT", "オーソン・ウェルズのフォルスタッフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "シェイクスピアの『ヘンリー４世』『ウィンザーの陽気な女房たち』などに出てくる巨漢の憎めない悪党、騎士のくせに大酒飲みで女好き、大ボラ吹きのフォルスタッフを肥満しきったウェルズが怪演。その即位をめぐって、政情不安の高まるヘンリー４世治下1400年代の英国。悪名高いフォルスタッフは放蕩の日々を送る皇太子ハルと娼婦ドルを交えて痛快な無頼生活をやり過ごしていた。国王に呼び戻されシュルーズベリーの合戦に参加、フォルスタッフはちょこまか逃げ回るが、ハルの倒した敵将の息子をずる賢く自分の手柄に。やがて病床の王のもとにハルは戻り父の死後ヘンリー５世に即位。戴冠式にはせ参じたフォルスタッフは旧交を温め昇進を期待するが、若き国王は旧友を拒絶し厳しく追放の命を下す。傷心の老フォルスタッフは真夜中の鐘を聞いた日々を懐かしみつつ息を引き取る。スピーディーなカッティング、誇張された空間と肉体、少年のような悲しみの具現は、ウェルズならでは。", "1966-01-01", "9,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ULTIMATE WARRIOR", "SF最後の巨人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "核戦争後の荒廃した2012年のニューヨークが舞台。生き残った、対立する二つのグループが壮絶な闘いを繰り広げる、デッド・テック・フューチャー（近未来派）SFの一編である。個性派Y・ブリンナーとM・V・シドーの共演が新味。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋するナポリタン　世界で一番おいしい愛され方", "恋するナポリタン　世界で一番おいしい愛され方", "", "", "http://image.eiga.k-img.com/images/movie/55416/315x210.jpg?3832", 105, "相武紗季、人気グループ「EXILE」の眞木大輔、塚本高史が主演のラブストーリー。イタリア料理店のシェフ・武（塚本）は、密かに恋心を寄せていた幼なじみの瑠璃（相武）に会いに行く途中、ピアニストの佑樹（眞木）が起こした事故で命を落としてしまう。佑樹は奇跡的に一命を取りとめるが、なぜか記憶が武のものにすり替わっていた。瑠璃は慣れ親しんだ武の料理を作る佑樹にいらだちを感じるが、佑樹のひたむきな思いに次第に心を開いていく。", "2010-09-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ХАРЕБА И ГОГОИ", "ハレバとゴーギ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "グルジアの山林に立てこもり、帝政に対抗する男たちの活躍を描いた力作。第一次ロシア革命後のカヘティア地方。ハレバとゴーギを中心とする“人民の復讐者”たちは、村人たちの英雄として地元の貴族や官吏たちからも一目置かれていたが、皇帝政府から派遣された軍隊により絶滅させられる。", "1987-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PREACHING　TO　THE　PERVERTED", "プリーチング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ロンドンのセクシュアル・アンダーグラウンドを舞台に描く異色のラブ・ストーリー。アメリカのレズビアン・カルチャーを代表するG・ターナーが、SMの女王様に扮して、強烈な個性とカリスマ性を発揮している。保守派の国会議員ハーディングのスタッフに採用されたクリスチャンのピーターが、SMクラブの潜入調査を命じられる。猥雑行為の証拠をつかむため、秘密クラブに入会した彼は奴隷の男女がひしめく異様な光景にがく然とする。ところがセックスの女王タニヤに気に入られたピータは、彼女に導かれるままSMの世界にのめり込んでいき……。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男嫌い", "男嫌い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "豪華な邸宅に住む４人の独身女性。いずれ劣らぬ美人で生活力もあり、彼女ら目あての男たちが数多く訪れる。しかし彼らのほとんどが欠点を握られ、むしり殺されてしまう。一緒に住む男の子、修は気が気ではない……。肉食魚のように自由に、かつ残酷に生きる女たちの姿を描いた風刺劇。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("望楼の決死隊", "望楼の決死隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "日中戦争直前の鮮満国境における国境警察官たちの労苦と活躍を描いた戦意高揚アクション映画。新任国境警察官の目を通してドラマが語られていく。酷寒の国境のもと、原節子までもが銃を持ち、さながら西部劇のタッチで迫るアクション・シーンは圧巻だ。監督は、「ひめゆりの塔」の今井正。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cheri", "わたしの可愛い人　シェリ", "", "", "http://image.eiga.k-img.com/images/movie/55596/315x210.jpg?3958", 90, "フランスの女流作家コレットの代表作「シェリ」を、「危険な関係」のスティーブン・フリアーズ監督がミシェル・ファイファー主演で映画化。20世紀初頭の華やかなパリを舞台に、美貌と知性で富と名声を手に入れた40代後半の元高級娼婦レアと、母子ほども年齢の離れた19歳の青年シェリの恋愛を描く。共演に「ヴィクトリア女王　世紀の愛」のルパート・フレンド、オスカー女優キャシー・ベイツら実力派がそろう。", "2010-10-16", "3,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Smart People", "賢く生きる恋のレシピ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPERATION PACIFIC", "太平洋機動作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "第２次世界大戦下の太平洋戦線で活躍したアメリカ軍潜水艦サンダーフィッシュ号乗組員たちの愛と勇姿を描く戦争映画。先任仕官デューク少佐は、別れた妻メアリーに求婚しているジョンの兄でもある艦長の命令に従い、彼の命を犠牲にして艦の危機を救う。だがそのためジョンから激しく憎まれる。そんな中、艦は日本軍の艦隊を捕捉し……。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fados", "ファド", "", "", "http://image.eiga.k-img.com/images/movie/54056/315x210.jpg?3266", 85, "第5回スペイン・ラテンアメリカ映画祭上映作品。「フランメンコ」「タンゴ」のカルロス・サウラ監督が、ポルトガル発祥のファド・ミュージックを追ったドキュメンタリー。ポルトガル領だったアフリカ、ブラジルの黒人たちがファドに与えた影響や、ファドが生まれた文化的、歴史的背景を探る。ファドの女王、アマリア・ロドリゲスを偲び、マリサ、カルロス・ド・カルモ、カマネなど名だたるファド・ミュージシャンが素晴らしいパフォーマンスを披露する。", "2008-09-11", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あらかじめ失われた恋人たちよ", "あらかじめ失われた恋人たちよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "劇作家・清水邦夫と当時まだTVディレクターだった田原総一朗の共同脚本・共同監督による鮮烈な一編。田原も清水も同じ大学から岩波映画社に入社した先輩と後輩同士である。桃井かおりがろうあ者の少女役で本格的デビューを飾り、その美しくふくよかな裸体は当時の若い観客たちをワクワクさせた。また、当時新進気鋭の写真家・加納典明や歌手のカルメン・マキが出演し、話題をまいた作品でもある。オリンピック候補として挙げられた棒高跳びをあきらめて、次のスポーツとしてかっぱらいを選んだ主人公。彼が、北陸の地をさまよい歩きながら出会った身体障害者の兄弟との心の交流をみずみずしいタッチで描いた、一種のロード・ムービー。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美しい暦", "美しい暦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "高校３年生の矢島貞子は、良い先生と友人に囲まれ幸せな高校生活を送っていた。ところが１ヵ月前、登山で知り合った男子高校生をはじめとするグループがひょんなことから自分の高校にやってきた。女の先生が彼らを不良呼ばわりしたため、抗議にやってきたのだ。彼女は次第に彼に好意を抱き始めるが……。学園ものの青春ラブ・ストーリー。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pursuit of D.B. Cooper", "ハイジャックコネクション クーパーの大仕事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "現金輸送中の飛行機から20万ドルを奪いパラシュートで逃亡したD・B・クーパー。彼はこの計画を見事成し遂げ、喜び勇んで家に帰るが、そこには軍隊時代の上官が待ち受けていた。かくして大金をめぐる二人の壮絶な争奪戦の火ぶたが切られる。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新宿やくざ狂犬伝・一匹灯", "新宿やくざ狂犬伝・一匹灯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "３年間の刑期を終えた春日洋一は新宿のソバ屋に就職するがある日、ヤクザのいざこざに巻き込まれた際に藤吉組の幹部・元木と出会い、やがて彼の舎弟としてヤクザ社会に踏み込んでいく。そのころ、関西最大の勢力を誇る大野組が新宿に触手を伸ばしてくる。そして大野組の殺し屋が元木を襲ったことから、キレた洋一は大野組の組長を射殺してしまう……。自分の感情を抑制することができず、狂犬のように突っ走っていく男、春日洋一。的場浩司のはまり役とも言えるこの男の暴走ぶりと、彼の周囲で起こる抗争を描いた現代任侠伝。監督の和泉聖治が大野組のヒットマンを、脚本の香月秀之が刺客を演じている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Looking for Eric", "エリックを探して", "", "", "http://image.eiga.k-img.com/images/movie/54996/315x210.jpg?1955", 117, "「麦の穂をゆらす風」などで知られるイギリスの名匠ケン・ローチによるコメディ・ドラマ。何をやってもうまくいかず不満だらけの毎日を送っていた郵便局員エリック。そんな彼の前に、憧れの存在だったサッカー選手エリック・カントナが突然現われた。それ以来、カントナはエリックに様々なアドバイスをするようになり……。サッカーのフランス代表として活躍した名選手エリック・カントナが本人役で出演したことで話題に。2009年東京国際映画祭ワールドシネマ部門で上映。2010年劇場公開。", "2009-01-01", "3,2,37,4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("壊音／KAI−ON", "壊音／KAI−ON", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "第77回文學界新人賞を最年少で受賞した篠原一の同名小説を、新人・奥秀太郎が映画化。中学生のタキ、トト、ハジメは、好奇心からドラッグを始める。しかし、思春期の不安定な精神状態も手伝って次第に服用がエスカレート。そんなある日、タキが授業中に倒れ病院に運ばれる……。極力セリフを排除し、退廃的な音楽とヴィヴィッドな映像で思春期のゆらめきを描く。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Foreign Affair", "異国の出来事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "　廃墟と化した戦後のベルリンを舞台に、プレイボーイなアメリカ人将校の恋のさやあてを描く。ドイツ視察に向かうアメリカ人女性議員は、ある女性からプリングル大尉への贈り物を託される。女性は大尉のガールフレンドのひとりだったが、彼は受け取るとすぐ、それを闇市でマットレスと交換する。熱をあげているドイツ人女性といちゃつくために。マレーネ・デートリッヒがナチスの歌手を演じ、音楽を担当したフレデリック・ホランダーがピアノマンとして出演している。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勇闖天下／REBEL FROM CHINA", "反逆の絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "旧資本家であるコウは、文化革命が吹き荒れる中国で、職を失い、弟ミンと二人香港へ渡って来る。貧しいながらも地道に生きようとする兄コウに対し、香港での成功を夢見る弟は悪の道へと引き込まれようとしていた。近年、ハリウッドでもヒット作を連発する人気監督J・ウーが、兄コウ役で主演した作品。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLESSED", "エヴァンジェリスタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "イギリスの新鋭サイモン・フェローズ監督がH・グラハムを主演に撮り上げたホラー。「ローズマリーの赤ちゃん」を彷彿させる、赤ん坊と悪魔の怖い関係が再び映画に。不妊治療に訪れた夫婦は体外受精の結果、赤ん坊を授かるが、実は悪魔の子で……と恐ろしい展開の本作。体外受精の横行や子供を作らないアメリカ社会への風刺を効かせている。", "2004-12-25", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN AMERICAN TAIL: FIVEL GOES WEST", "アメリカ物語2 ファイベル西へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "前作から２年。ネコの復讐から逃れ、成功を求めてアメリカにやって来たマウスクビッツ一家だったが、そこにも自由はなく、毎日ネコの恐怖にさらされながら過ごしていた。そんなある日、西部ではネコとネズミが力を合わせて、楽しく豊かな街ができつつあるという噂を聞き、一家をはじめ多くのネズミが次々と列車で西部へ向かった。だがそれは、ネコのボス、ミャオの罠だった。スピルバーグのアニメ映画第２作。ハートウォーミングな冒険ファンタジーに仕上がっている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHRONIK DER ANNA MAGDALENA BACH", "アンナ・マグダレーナ・バッハの日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "バッハの伝記映画であり、ファンにはこたえられないバッハ音楽の映画である。彼を演じるのは、当時“現代のバッハ”といわれたG・レオンハルトだ。レオンハルト演奏による『ブランデンブルグ協奏曲』から始まるこの映画は、２番目の妻アンナと再婚し、その死に至るまでの30年近くにわたる年代記となっている。映像は絶えずバッハの音楽の演奏によって占められ、ナレーションはアンナの声で、二人の生活を語り続ける。そして“ミニマル＝シネマ”と呼ばれるストローブ＝ユイレの新しい映像感覚。ただの伝記映画にとどまらない傑作である。DVDタイトルは「アンナ・マクダレーナ・バッハの年代記」。", "1967-01-01", "7,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ariel", "真夜中の虹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "南を目指す男の波乱万丈な旅を、アキ・カウリスマキ監督らしい淡々としたタッチで描く。失業したカスリネンは、自殺した父のキャデラックではるか南を目指す旅に出た。ところが途中で強盗に全財産を奪われ、日雇い仕事に出た帰りにイルメリという女性と知り合う。ある日彼は、金を奪った強盗と再会し捕まえようとするが、逆に警察に連行されそのまま投獄。イルメリの助けを借り、刑務所仲間のミッコネンと共に脱獄するのだが……。", "1988-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shadowboxer", "サイレンサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「クィーン」のヘレン・ミレン、「ザ・エージェント」のキューバ・グッディング・Jr.という2人のオスカー俳優が競演を果たしたアクションサスペンス。末期ガンに冒された殺し屋ローズは、仕事の相棒でもある恋人マイキーと共に穏やかな余生を送るつもりでいた。ローズは最後の仕事として、犯罪組織の親分クレイトンから彼の妻ビッキーの殺害を依頼されるが……。「チョコレート」のプロデューサーを務めたリー・ダニエルズの初監督作。", "2007-04-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金田一耕助の冒険", "金田一耕助の冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "今やマスコミの売れっ子となり、等々力警部とのコンビでCMにも出たりしている金田一耕助が、唯一真犯人を突きとめることができなかった事件に再び挑む、という“金田一耕助”シリーズの番外編。テレビで同役をやった古谷一行が金田一耕助を演じる。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天才詐欺師物語 狸の花道", "天才詐欺師物語 狸の花道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "舌先三寸で人をだますことに長けていた競輪狂の男が、恋女房に何とか気に入られようと、大掛かりな詐欺を思いつき、大金をせしめようとするのだが……。町田浩二の原作『詐欺の天才奮闘記』をもとにした喜劇。調子のいい詐欺男を小林桂樹が熱演する。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ISLE", "魚と寝る女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "韓国の異端児監督キム・キドクの第４作。孤独な男女の絡みつく情念を描く衝撃の愛の物語。人里離れた山奥。濃い霧がたちこめる湖で釣り場を管理している女ヒジンは、一言も話すことなくただ黙々と仕事をこなす毎日。小屋舟に釣り客を運び、彼らに体も売っている彼女は、思い詰めた表情の男ヒョンシュクに興味を抱く。浮気した恋人を殺害し、死に場所を求めてやってきたヒョンシュクと孤独なヒジンは、心の隙間を埋め合うかのように情欲に溺れていく。ヒロイン、ソ・ジョンは、セリフも一切なしに、ねっとりとした視線のみで、嫉妬や欲望を表現。追い詰められた彼らが自らに課す残酷なシーンが強烈。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘密", "秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "メッキ工場に勤める恵一は、昔の恋人の夫に泣きつかれて会社の金を貸す。だが、奔走もむなしく金の工面はつかず、恵一は思いあまって銀行帰りの女を襲う。それを目撃した保母のみや子は、彼に自首を勧めるが……。若者向き週刊誌に連載された早乙女勝元の原作を、青春ものに定評のある家城巳代治監督が映画化。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE FEU AUX POUDRES", "火薬に火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「現金に手を出すな」の原作者A・シモナンが脚本を書き、H・ドコワンがメガホンを執ったギャング映画の快作。武器の密輸で稼いでいるペドロのもとへ、画家と称する男が訪ねて来た。だが、男は警察のおとり捜査官だった。ペドロは男が仕掛けた罠にはまり、次第に追い込まれていく……。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Look", "LOOK", "", "", "http://image.eiga.k-img.com/images/movie/53746/315x210.jpg?2838", 102, "「デトロイト・ロック・シティ」の鬼才アダム・リフキンの全編監視カメラによる映像で構成された異色サスペンス。決定的瞬間の数々を構成し、監視社会に生きる人々のリアルな日常をドキュメンタリータッチに描く。アメリカ某都市。逃亡中の2人組の強盗、幼い子供に付きまとう男、職場の女性にセクハラを行うデパートのマネージャー、同性愛の恋人との情事を楽しむ弁護士、ロック・スターを夢見るコンビニ店員、早熟な女子高生と高校教師の情事。それら全てを監視カメラが捕らえていた……。", "2008-09-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Forbidden", "たそがれの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "政治家ボブ・グローバーの愛人になり私生児を産んだルル。ロベルタと名づけられた赤ん坊は、やむを得ない事情でグローバーの養女として育てられることになる。時がたち、グローバーは知事として成功する。彼の娘が実は愛人に産ませた子であることを、以前からルルに想いを寄せていた新聞記者ホーランドが嗅ぎつけ、記事にして公表しようとするのだが……。監督は後に「或る夜の出来事」等の作品を遺した名匠フランク・キャプラ。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hors de Prix", "プライスレス　素敵な恋の見つけ方", "", "", "http://image.eiga.k-img.com/images/movie/53386/315x210.jpg?3623", 104, "「アメリ」「ダ・ヴィンチ・コード」のオドレイ・トトゥ主演によるラブコメディ。リゾート地で若さと美貌を武器に玉の輿の相手を探すイレーヌ（オドレイ）は、南仏の高級ホテルで出会った男ジャンを金持ちと勘違いして誘惑し、一夜を共にする。その一年後、2人は同じホテルで再会するも、イレーヌはジャンの本当の姿を知り、彼の前から姿を消してしまう。イレーヌのことが忘れられないジャンは彼女を追いかけニースへ行くが……。監督は「砂の売人」「お先にどうぞ」のピエール・サルバドーリ。", "2008-03-08", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春の門", "青春の門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 189, "五木寛之の大河小説『青春の門』の第１部『筑豊篇』の映画化。大正７年、北九州の筑豊炭坑。米騒動が全国に吹き荒れる時、坑夫の伊吹重蔵は、暴動の先頭に立って男をあげる。重蔵の妻は幼い信介を残して他界し、重蔵はカフェの女給・タエを後妻に迎える。だが重蔵は炭鉱内の事故で、坑夫たちを助けに行き死んでしまう。戦後、かつて重蔵とタエを張りあった竜五郎が、タエと信介の面倒をみることになる。映画は劇中の脇役の一人・小沢昭一を語り手とし、信介の成長を通して、大正・昭和史を描く。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マリヤのお雪", "マリヤのお雪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "原作は川口松太郎となっているが、実はモーパッサンの作品の『脂肪の塊』を翻案したのがこの作品。明治の初め、官軍と西郷軍の戦火が近づいてきたのを知り、町を出た１台の馬車があった。名士たちを乗せたこの馬車には娼婦のお雪たちもいて、軽蔑の視線をうけている。やがて馬車は官軍に捕まり……。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CORSA DI PRIMAVERA", "春のかけ足", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "G・カンピオッティ監督の幼少時代をベースに、田舎町に引っ越してきた少年の新しい生活を、子供たちの素朴で生き生きとした日々の中に描いていく。両親の別居で８歳のイサッコは、母親に連れられてイタリア北部の田舎町カシアーゴにやって来た。新しい学校に通い始めたイサッコは、優等生のガブリエとガキ大将のフィオレといつも一緒に遊ぶようになる。ある日、３人は家出を計画し、ヴェネチアに行くことを決めるが、決行当日駅に来たのはイサッコだけだった。ところがやって来た電車からは意外な人が降りてきて……。イサッコの両親以外は監督の友人や親戚など素人を起用した、温かみのある作品。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あいつに恋して", "あいつに恋して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "北海道・日高の牧場で道産子の馬を育てている青年が、サラブレットに押され気味の道産子馬の優秀性を示そうと、全国行脚の旅に出るという青春映画。ムービー・キャラバン隊を編成し、全国各地を移動しながら撮影するという珍しい製作過程が話題になった。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の勲章", "夜の勲章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "数日前から姿を消した姉の行方を捜して欲しい、と看護婦から依頼を受けた探偵の阿久津純一。調査を続けるうち、姉は麻薬中毒患者で、異国で死んだ父の遺産1000万円を持って行方をくらましていることがわかる。そして事故発生後７日目、遺産の入った預金通帳とともに姉の死体が発見される。失踪事件から始まる、ハードボイルド・スタイルのサスペンス。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャコ万と鉄", "ジャコ万と鉄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "北海道のニシン漁場に片目の無法者ジャコ万が流れてきて、無法の限りをつくすが、網元は弱味を握られていて手が出せない。そこへ正義感の強い網元の息子、鉄が帰ってきて、ジャコ万と対決する。「銀嶺の果て」「暁の脱走」と並ぶ谷口千吉監督の代表作。当時、新人だった三船敏郎が鉄を好演。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死なない子供　荒川修作", "死なない子供　荒川修作", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "2010年5月に急逝した芸術家、荒川修作と作品をテーマにしたドキュメンタリー。荒川が05年に手がけた死なないための住宅「三鷹天命反転住宅」で暮らす人々の様子を通し、死の運命から決して逃れることができない人間の限界に挑み続けた荒川の軌跡をたどる。自身も「三鷹天命反転住宅の住人」である山岡信貴監督がメガホンをとり、浅野忠信がナレーションを務める。", "2010-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANSEN MED REGITZE", "追想のワルツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "デンマークのとある田舎町に住む、ごく平均的な老夫婦を主人公に、彼らの戦前、戦後を通じた45年にわたる愛の人生讃歌を、現在と過去を奔放に行き来する回想＝絶妙のカットバックと、見事なドラマ展開で見せる、大ベストセラー、クリステンセンの同名小説の映画化。全編にしみわたるノスタルジックな叙情が、デンマーク映画の質の高さを物語っている。", "1989-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("永山裕子　人形と百合を描く", "永山裕子　人形と百合を描く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "みずみずしい独特の色彩の作品で、多くの水彩画ファンの支持を得る画家・永山裕子の創作の過程に密着したドキュメンタリーシリーズ第3作。アンティークドールとユリの花を中心に、モモや水差し、鉄瓶などのモチーフを描く制作過程を収録。白いモチーフを描く上でのポイントや、透明水彩ならではの白を塗り残した表現などを解説し、永山流水彩画法を掘り下げていく。", "2010-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌17　あとは能登なれ　ハマとなれ！", "釣りバカ日誌17　あとは能登なれ　ハマとなれ！", "", "", "http://image.eiga.k-img.com/images/movie/33586/315x210.jpg?2694", 107, "釣りバカサラリーマンのハマちゃんが活躍する人情コメディ、シリーズ第17作。金沢や能登半島を舞台に、お馴染みのメンバーが騒動を繰り広げる。ハマちゃんが勤める鈴木建設営業3課に、かつて社内のマドンナ的存在だった沢田弓子が再雇用される。3課の仲間たちは大喜びだが、弓子の再就職には何やら事情がありそう。心配して声を掛けたハマちゃんに、弓子は相談を持ちかけるが……。ゲスト出演は石田ゆり子、片岡鶴太郎、大泉洋ほか。", "2006-08-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZIPANG ジパング", "ZIPANG ジパング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "伝説の黄金国ジパングを巡り、盗賊に賞金稼ぎ、加えて徳川幕府にジパング王国などが入り乱れて展開されるエンターテインメント時代劇。林海象監督の突拍子もないアイデアがノンストップで繰り広げられる前半は楽しめるが、ジパングに乗り込んでからの後半が失速気味だ。成田三樹夫の遺作となった。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　MYSTE｀RE　PICASSO", "ミステリアス　ピカソ・天才の秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "20世紀を代表する天才画家、パブロ・ピカソ。彼の作品製作過程を、フランス・サスペンス映画の巨匠H・G・クルーゾーが、緊迫感あふれる演出で追ったドキュメンタリー。ここでピカソが描くのはこの映画のための新作。途中画面がスタンダードからシネスコに拡大され、力強いタッチの作品が大画面で迫力たっぷりに映し出されていく。初公開時のタイトルは「ピカソ　天才の秘密」、1998年に「ミステリアス　ピカソ・天才の秘密」と題してリバイバル上映された。", "1956-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アジアンタムブルー", "アジアンタムブルー", "", "", "http://image.eiga.k-img.com/images/movie/33746/315x210.jpg?3908", 110, "大崎善生の同名小説を、人気俳優・阿部寛と映画初出演の松下奈緒共演で映画化。日本とフランスのニースを舞台に、悲劇的な状況にありながらもひたむきに愛し合う男女の姿を描く。成人男性向け雑誌の編集者として働く隆二は、仕事で出会ったカメラマンの葉子と恋に落ちる。2人は同棲を始め幸せな毎日を送るが、ある日葉子が突然倒れ、医師から余命1ヵ月を宣告されてしまう。現役音大生でもある松下が劇中でピアノ演奏を披露している。", "2006-11-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RACING STRIPES", "レーシング・ストライプス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「ベイブ」のスタッフが手がけた、夢あふれるアニマル・ファンタジー。競走馬になりたいと願うシマウマが、サラブレッドを向こうに回して大奮闘。不可能を可能にするべく努力を重ねる姿がユーモラスかつ感動的に描かれる。人間の言葉を話す動物キャラがずらりと登場。人間サマ顔負けの“好演”に加えて、デジタル技術による愛嬌あふれる表情もチャーミングで夢中にさせられる。サーカスの一座に置き去りにされたシマウマの子。農場に拾われ、ストライプスと名付けられた彼は、競馬場で走ることを夢みて奮闘。その才能に牧場主も注目し、彼に調教を施すが……。", "2005-03-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("迷走地図", "迷走地図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "改憲党第２派閥の寺西と、彼を支えてきた妻、それに４年来の私設秘書を主人公に、保守党の政権をどの派閥が握るか、裏側に潜む個人的なスキャンダルや金銭の授受、さらには殺人事件まで絡めて描いた社会派サスペンス・ミステリー。助演陣が好演。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOS　SIN　NOMBRE", "ネイムレス・無名恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "モダンホラー作家R・キャンベルの小説『無名恐怖』を映画化したスリラー。出版社で働く中年女性クラウディアの元に、５年前何者かに惨殺されたはずの娘アンヘラから、助けを求める電話が入る。驚いたクラウディアは元刑事マセラの協力を得て調査を進めるうちに、“ネイムレス”というカルト集団がアンヘラ失踪事件にかかわっていることを突き止める……。スペインの新人監督J・バラゲロが、絶対悪を標榜する暴力集団の暗躍を描写。“闇”にこだわったリアルな映像感覚が、ただならぬ不安と戦慄をもたらす異色作だ。", "1999-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRUST", "えびボクサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "体長２メートル以上のエビが人間と格闘するという、なんとも奇想天外なコメディ。思わず苦笑してしまうチープなエビの造型、エビと人間の愛も芽生える脱力必至のストーリーに注目だ。パブを経営するアル中の中年男ビルは一念発起し、市場で巨大エビを購入する。これをボクサーの甥っ子と戦わせ、見世物にして一攫千金を狙うという腹づもりだったが、企画を持ち込んだTV局にことごとく断られ……。", "2002-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("首都高速トライアル", "首都高速トライアル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "現役F1レーサーが持つ７つの高速道路の区間記録。これをすべて破ればプロに、一つでも記録をぬり替えられなかったらスッパリとあきらめる。そんな走りに夢と青春を懸けた若者たちの姿を、カー・アクションを織り交ぜて描いた青春映画。唐十郎の長男・大鶴義丹がこの作品でスクリーン・デビュー。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウィンター・ソルジャー　ベトナム帰還兵の告白", "ウィンター・ソルジャー　ベトナム帰還兵の告白", "", "", "http://image.eiga.k-img.com/images/movie/55466/315x210.jpg?1672", 95, "1971年1月31日から2月2日までの3日間、米デトロイトでベトナム帰還兵たちが悲惨な戦争を止めるために勇気を出して証言を行った“ウィンター・ソルジャー公聴会”の模様を収めたドキュメンタリー。公聴会の模様は、そのあまりに衝撃的な内容に全米マスコミが黙殺したが、記録映画として72年1月にニューヨークで限定公開され、その年のベルリン国際映画祭では、フォーラム部門インターフィルム賞を受賞。イラク戦争最中の05年にはニューヨークで再上映され、大絶賛を浴びた。", "2010-06-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KAOS", "カオス・シチリア物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 187, "ノーベル賞作家の短編集を原作にタビアーニ兄弟が描く、圧倒的に美しく壮大な抒情詩傑作。<もう一人の息子><月の病><甕><レクイエム>……鈴をつけられたカラスが真っ青なシチリアの空を飛び、かの地に暮らす人々の物語を結んでいく。そして、人生に疲れて故郷シチリアに戻った作者ピランデッロに、亡くなったはずの母が少女の頃の思い出を語る、まばゆいばかりに美しい<母との対話>がエピローグを飾る。", "1985-08-10", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Renaissance Man", "勇気あるもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "会社をクビになったエリート広告マンのビルは、職安で陸軍の国語教師の職を見つけ、８人のおちこぼれ新兵たちに教え始める。彼らはそれぞれ事情を抱え、軍隊に入る以外の道はなかった若者たちだった。読書の時間、生徒たちがシェイクスピアに興味を示す。彼らの好奇心を馬鹿にしていたビルだが、教官である軍曹に“生徒をばかにする前に自分が真剣になれ”と罵倒され、初めて目が覚める。翌日からビルは、『ハムレット』の授業を始める。新しい世界を知る喜びにあふれる若者たちの姿が、ビル自身にも希望を与え始めた。人生の目的を見失った中年男が、再び情熱を取り戻す姿をD・デヴィートが好演。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠き落日", "遠き落日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "1987年に公開され、大ヒットを記録した「ハチ公物語」のメイン・スタッフが再結集して製作された大作。自分の不注意から息子に大ヤケドを負わせてしまった母シカとそのハンデをバネに世界的な医学者となった野口英世の美しい母子の愛情を描いている。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ratatouille", "レミーのおいしいレストラン", "", "", "http://ecx.images-amazon.com/images/I/51VA0tVHTkL._SL210_.jpg", 110, "「アイアン・ジャイアント」「Mr.インクレディブル」のブラッド・バード監督によるディズニー／ピクサーの最新作。パリの5つ星レストラン“グストー”を舞台に、料理が苦手な見習いシェフのリングイニと、“グストー”に流れ着いた料理が得意なネズミ、レミーの友情と成長を描く。声の出演にはパットン・オズワルト、ルー・ロマーノの他、イアン・ホルム、ブライアン・デネヒー、ピーター・オトゥールら名優が名を連ねる。", "2007-07-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Wife's Relations", "バスター・キートンの華麗なる一族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "キートンはパン職人。町でガラスを割ったことをとがめられた彼は、変なオバサンに裁判所へ連れていかれてしまった。しかし裁判官たちはなぜかポーランド語しかしゃべれず、二人を結婚させてしまう。ケチで荒くれた妻の親戚たちは、彼を資産家と決め込んでさっそく成金生活を始めるが……。日本未公開とされることが多いこの作品、実は大正12年に「キートン半殺し」なるタイトルで、公開されている。また昭和に入ってからのリバイバル時には、ヒット作「セブン・チャンス」の旧題名「キートンの栃綿棒」にあやかって、「キートンの飴ン棒」なるタイトルで上映されたことがある。", "1922-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("First Sunday", "悪党（ワル）にもラブソングを！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("影武者", "影武者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 179, "黒澤明監督が「デルス・ウザーラ」以来5年ぶり（日本映画としては「どですかでん」以来10年ぶり）に製作した、戦国スペクタクル巨編。製作総指揮としてフランシス・F・コッポラとジョージ・ルーカスが参加、アメリカでも公開され独創的な様式美と壮麗な合戦絵巻が評判を呼んだ。時は戦国時代、あやうく処刑をまぬがれた盗人が武田信玄の影武者となり、信玄の幻に威圧されながらも敵をあざむいていく。だが男にとって戦国の雄・信玄として生きることはあまりにも過酷だった……。カンヌ国際映画祭でグランプリを受賞。", "1980-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い蕾と白い花", "赤い蕾と白い花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "互いに片親だけで育てられた高校３年生のとみ子と重夫。彼らが、親同志の交際を経て、結ばれるまでを描いた青春ドラマ。主演は、吉永小百合と浜田光夫の日活ゴールデンコンビ。石坂洋次郎の『寒い朝』を池田一朗が脚色し、吉永小百合のデビュー作も手掛けている西河克己がメガホンを取った。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Svetat e golyam i spasenie debne otvsyakade", "さあ帰ろう、ペダルをこいで", "", "", "http://image.eiga.k-img.com/images/movie/57756/315x210.jpg?4231", 105, "事故で両親と記憶を失った青年が、再会した祖父とともに祖国へ戻る自転車の旅の過程で、家族の絆を取り戻していく姿を描いたロードムービー。幼少期に両親とともに共産党政権下のブルガリアからドイツへ亡命した青年アレックスは、交通事故で両親と記憶を失ってしまう。ブルガリアからアレックスを迎えにきた祖父バイ・ダンは、アレックスと2人でタンデム自転車に乗り、祖国を目指して欧州横断の旅に出る。アレックスはかつて祖父に習ったバックギャモンを再び教わりながら旅をすることで、記憶から失われた自身の人生を見つめなおすことになる。", "2012-05-12", "65,7,30,64,44", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("略奪愛", "略奪愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "京都府下で実際に起きた事件をモデルに、親友の夫を奪い取る女の激しい愛をスリリングに描いた愛憎ドラマ。高校時代の先輩、由美の婚約者に横恋慕したヒロイン妙子は、二人が結婚したあとも日に日にその思いを強くしていき……。ニューウェーブやくざ路線に定評の梶間俊一が久々に“女”を描いた異色作。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お遊さま", "お遊さま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "谷崎潤一郎の小説『芦川』を巨匠・溝口健二が映画化。美しい未亡人・お遊さまをめぐって展開する妖しく耽美的な世界を溝口の永遠のヒロイン・田中絹代主演で描く。晩年の溝口芸術に大きく貢献した撮影監督・宮川一夫と初めてコンビを組んだ記念碑的作品。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WELL", "女と女と井戸の中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "オーストラリアの新人監督S・ラングが確かな手腕を発揮した心理サスペンス。長らく父親と二人暮らしの中年女性ヘスターと、彼女の家にメイドとしてやって来た若い娘キャスリン。人里離れた田園で共同生活を始めた彼女たちの友情は、キャスリンが空き巣の男を車で轢いたことから崩壊していく。広大な大地を背景にしながらも、密室的な息苦しさに満ちた世代の異なる女二人だけのドラマ。冷ややかなブルートーンで統一された映像、登場人物の心のメタファーとなる“井戸”の用い方など、注目すべき点は多い。", "1997-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霹靂火／THUNDERBOLT", "デッドヒート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "自らカーレースを主催するほどのカーマニア、ジャッキー・チェンが、企画・主演を務めたカー・アクション。日本の自動車メーカーでの研修留学を終え、香港に戻ったジャッキーは、父親が経営するレーシングショップを手伝っていた。その頃、香港ではテロ事件が多発しており、ジャッキーは警察に腕を買われハイウェイでの検問に駆り出される。ある夜、検問を猛スピードで突破する車が出現。壮絶なカーチェイスの末、捕まえた犯人は一流レーサーで国際テロリストのクーガーだった。復讐に燃える彼は刑務所を脱獄、ジャッキーの妹二人をさらい、日本で開催されるカーレースで勝負することを要求する。日本でもロケを行い、加山雄三らが出演している。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONSPIRAY OF FEAR", "陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "大学生のクリスは、生物学者の父とともにエレベーター事故に巻き込まれ、父を亡くしてしまう。葬儀の場に、父の親友と名乗る男が現れ、彼が持っていた重要書類を探しているとクリスに告げる。その後、クリスは記者を名乗る男からも呼び出され、誘拐されそうになる。しかし、偶然知り合った少女ジミーに助けられ、難を逃れる。果たして、彼らは誰なのか？ なぜ狙われているのか？　何も知らないクリスとジミーは、いつしか巨大な陰謀の渦に巻き込まれていく。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PROFESSIONALS", "プロフェッショナル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "メキシコ革命の革命派くずれの山賊に妻を誘拐された、油田の持ち主グラントは、妻をとり返すため４人の戦争専門家を雇った。射撃、馬、弓矢、爆弾とそれぞれ特技を持ったこの４人組は山賊の待ち受ける岩山と砂漠の地に踏み込んでいく……。主役４人の個性が存分に発揮された痛快な西部劇アクション。DVDは「コロムビア・トライスター ザ・ウェスタン・ムービーズBOX(1)」に収録。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SERIES　７", "シリーズ７／　ザ・バトルロワイアル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "視聴者参加のTVドキュメンタリー番組を風刺した異色作。無作為に選ばれた視聴者同士の殺し合いを生中継するTV番組“シリーズ7”。２週勝ち抜いた妊婦ドーンは、最後の決戦の場である故郷へと向かう。敵は女子高校生や中年看護婦、策略家の老人など。その中にはドーンの高校時代の恋人の姿もあった……。視聴率至上主義のメディアの病巣に警鐘を鳴らしている作品だ。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("資金源強奪", "資金源強奪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "欲に取りつかれた男たちが、大金の強奪戦を繰り広げるアクション映画。チンピラ・武司が８年ぶりにシャバに戻る。しかし、彼に対する組の対応は冷たく、武司はムショ仲間と組の金を強奪する。これに悪徳刑事がからみ、欲の張り合いが展開。ムショ仲間に扮した川谷と室田がいい味を出している。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ばかもの", "ばかもの", "", "", "http://image.eiga.k-img.com/images/movie/55706/315x210.jpg?4427", 120, "芥川賞作家・絲山秋子の小説を、「デスノート」シリーズの金子修介監督が映画化。内田有紀と成宮寛貴が主演を務める。19歳の大学生ヒデは、27歳のおでん屋の娘・額子に映画に誘われ、その夜、童貞を奪われる。男勝りでぶっきらぼうな額子には強くひかれ、アパートに通いつめるようになるが、ある日突然別れを告げられてしまう。呆然としたままヒデは大学を卒業し、新たな恋も経験するが、額子のことを忘れられず、やがてアルコールに依存するようになる。", "2010-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の中にいる他人", "女の中にいる他人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "E・アタイヤの小説『細い線』を翻案した成瀬巳喜男監督の心理ミステリー。殺人を犯して自首しようとした夫が、子供の将来を重んじる妻に事故死に見せかけて殺されるまでを描く。本来のスタンダード・サイズに戻った成瀬の緻密で緊張感あふれる演出が秀逸。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABOUT LAST NIGHT…", "きのうの夜は…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "束縛されない、自由な恋愛をエンジョイしたいというのは、男性なら、誰もが思うところ。この映画の主人公、ダニーもその一人で、ふと知り合った女性デビーと、合意の上、共同生活を始める。しかし現実は、なかなかクールにはいかず、様々なトラブルが二人を襲っていき……。当時、若手スターの一人だったR・ロウがダニーに扮し、ちょっぴりアダルトな役に挑戦した。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紫右京之介 逆一文字斬り", "紫右京之介 逆一文字斬り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "父・伝右衛門が残した巡察覚え書きが、幕府の権力を一手に握ろうとする大老・酒井の手にあると知った右京之介。彼は酒井邸に忍び込むが、隻腕の侍・剣之助に斬りつけられて危機一髪で脱出することに。幼なじみを酒井の配下に殺され、父の死も酒井に関係することを知った右京之介は単身、酒井に挑んでいく。大川橋蔵が主役になって大活躍する時代劇アクション作。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒミズ", "ヒミズ", "", "", "http://image.eiga.k-img.com/images/movie/56356/315x210.jpg?3430", 129, "ギャグ漫画「行け！稲中卓球部」で人気を博した古谷実が、ギャグを封印して若者の心の暗部を浮き彫りにしたコミック「ヒミズ」を、「冷たい熱帯魚」「恋の罪」の鬼才・園子温監督が実写映画化。ごく普通に生きることを願っていた祐一と、愛する人と守り守られ生きていくことを夢見る景子。ともに15歳の2人の日常が、ある事件をきっかけに絶望と狂気に満ちたものへと変わっていく様子を描く。主演は「パンドラの匣」の染谷将太と、「劇場版　神聖かまってちゃん　ロックンロールは鳴り止まないっ」の二階堂ふみ。2011年・第64回ベネチア国際映画祭では、染谷と二階堂がそろってマルチェロ・マストロヤンニ賞（新人俳優賞）を受賞した。", "2012-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄眼", "鉄眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "一切経全巻の刻版に命を懸けた傑僧・鉄眼禅師の生涯を写実した、「月山」に続く“櫂の会”製作の作品。青森から鹿児島まで、全国40数カ所を踏破した彼の姿をと通して、人生の意味を世に問うている。鉄眼没後300年を記念して作られ、彼の徳を賛えている。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミッシェル・ガン・エレファント“THEE MOVIE” LAST HEAVEN 031011", "ミッシェル・ガン・エレファント“THEE MOVIE” LAST HEAVEN 031011", "", "", "http://image.eiga.k-img.com/images/movie/55066/315x210.jpg?4140", 0, "2003年に解散したロックバンド「ミッシェル・ガン・エレファント」のドキュメンタリー。2009年7月に急逝したバンドメンバーのアベフトシを追悼するプロジェクトの一環で製作された。2003年10月に幕張メッセで4万人を動員して行われたラストライブとそのバックステージ映像を収録。監督はミュージックビデオを数多く手がける番場秀一。", "2009-12-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Airplane II: The Sequel", "フライングハイ2 危険がいっぱい月への旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ジェット機が宙返りして笑わせた「フライング・ハイ」の続編。今回は宇宙時代にふさわしく人類初の月世界旅行へ出発するスペース・シャトルが舞台。いきなり軌道をはずれ、月ならぬ太陽へ向かって動き出したからさあ大変、乗客たちの運命は……。ひたすら会話のジョークで笑わせるSFコメディ。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざの墓場・くちなしの花", "やくざの墓場・くちなしの花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "はみだし刑事と韓国人ヤクザの友情、その妹との恋愛が、「くちなしの花」のテーマ曲にそってセンチメンタルに描かれている。最初の深作＝渡コンビによる「仁義の墓場」の硬質さと比較して、評価の分かれるところ。大島渚監督が警察署長役で特別出演。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GALIA", "恋するガリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "浮気性の夫に悩む中年女性に同情したガリアは、ひそかに彼女の夫の尾行を始める。しかし彼の行動を追ううちに恋心を抱いてしまい……。若い女性が恋愛の苦しみを知るサスペンス・ドラマ。1960年代ファッションに身を包んだM・ダルクがキュートな魅力をみせる。", "1965-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("首領を殺った男", "首領を殺った男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "1963年の「人生劇場・飛車角」から30年。興行的不振から、一時撤退へと追い込まれた東映ヤクザ映画。その“最後の１本”とうたわれて製作された作品。“東映ヤクザ映画”とりあえずの最終作。18年ぶりに刑務所から出所した筋金入りのヤクザ・宝来と、彼の兄弟分で今やヤクザ界の首領に上りつめた大木戸との対立を描いている。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KRONIKA WYPADKOW MILOSNYCH", "愛の記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "A・ワイダが政治的なテーマを離れて撮り上げた作品。作家であり映画監督でもあるコンビッキの原作を基に、ワイダの再生と復活を願う人々の協力を得て完成した本作品は、二人の若い男女の愛の物語であり、また、戦乱のたびに戦場となる祖国ポーランドへの、ポーランド人のアイデンティティーと愛の記録でもある。1939年、ポーランドの地方都市リトアニア。大学進学を控えたビテクは、久しぶりの帰省中、近所に住む少女アリーナに一目で恋をする。その頃、人々は戦争の噂で持ちきりだった……。ワイダの生まれ故郷リトアニアを映し出す映像が見事なほど美しい。", "1986-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奈緒ちゃん", "奈緒ちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "横浜市に住む重度のてんかんと知的障害を持つ少女、奈緒ちゃんとその家族の12年間にわたる生活を記録したヒューマン・ドキュメンタリー。奈緒ちゃんの叔父である記録映画作家の伊勢真一が彼女が８歳の時から撮り始め、成人を迎えるまでを追った。明朗快活で誰にでも親しまれ、街の人気者である奈緒ちゃんの個性と、家族や地域の人々の温かい視線が作品を福祉映画に終わらせず、風通しのよいものにしている。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PSYCHO II", "サイコ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "23年の時を経て製作されたヒッチコック監督の名作「サイコ」の後日譚。裁判で無罪となった精神異常者ノーマンが、再びあの忌まわしいベイツ・モーテルに戻ってくるところから、物語は始まる。果たしてノーマンは、悪夢から解放されるのだろうか……。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUNERAL IN BERLIN", "パーマーの危機脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "英国諜報部員パーマーは、東ドイツから亡命したロシアの軍人を連れ出すためにベルリンに向かったが、その裏にはナチ戦犯の大金をめぐる陰謀が隠されていた。「国際諜報局」に続く“ハリー・パーマー”シリーズ第２作のスパイ・アクション。", "1966-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ただ、君を愛してる", "ただ、君を愛してる", "", "", "http://image.eiga.k-img.com/images/movie/33706/315x210.jpg?4695", 116, "「いま、会いにゆきます」の市川拓司原作の小説「恋愛寫眞 もうひとつの物語」を、玉木宏と宮崎あおい主演で映画化。クリスマスのニューヨーク。自分の前から突然姿を消した静流からの手紙を手に、誠人は1人この地にたたずんでいた。6年前に大学の入学式で出会った誠人と静流。誠人にひと目で恋をした静流は、写真が趣味の彼に近づきたいばかりにカメラを手にする。しかし誠人は同級生のみゆきに片思いをしていた……。", "2006-10-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Buried", "リミット", "", "", "http://image.eiga.k-img.com/images/movie/55616/315x210.jpg?3234", 90, "地中の箱に閉じ込められた男の脱出劇がほぼ全編暗闇の中で描かれる異色シチュエーション・スリラー。イラクでトラック運転手として働くアメリカ人ポールは、ある日突然何者かに襲撃され、気づくと地中深くに埋められた棺の中にいた。手元にあるのは携帯電話、ライター、ナイフ、ペン、酒、そして残り90分の酸素のみ。タイムリミットが迫るなか、ポールは必死に脱出を試みるが……。主演は「ウルヴァリン／X-MEN ZERO」のライアン・レイノルズ。", "2010-11-06", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hostel: Part II", "ホステル2", "", "", "http://image.eiga.k-img.com/images/movie/1926/315x210.jpg?4141", 94, "クエンティン・タランティーノ製作総指揮の下、「キャビン・フィーバー」のイーライ・ロス監督がメガホンを取った衝撃ホラー「ホステル」の第2弾。ヨーロッパ旅行中の女子大生ベス、ホイットニー、ローナは、列車で出会った美女から天然スパの情報を聞いてスロバキアを訪れる。ホステルにチェックインし、街の祭でハメをはずす3人。拷問殺人組織“エリート・ハンティング”によって、自分たちが闇のオークションにかけられているとも知らずに……。", "2007-09-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CABIN　FEVER", "キャビン・フィーバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "閉ざされた森の小屋で、死に至る病原菌に襲われた若者たちの恐怖を描く戦慄のホラー。体を蝕まれていくショッキングな描写に加えて、感染疑惑をぬぐえない若者たちの極限心理がスリルを加速させる。助けの声に応えず感染者を忌み嫌う田舎町の閉鎖的な空気も、恐怖を盛り立てるに十分。ヴァカンス気分で森の中の小屋を訪れた若者5人組。一人が謎の病原菌に感染したことから、彼らのヴァカンスは悪夢へと急変する。", "2005-04-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mulholland Drive", "マルホランド・ドライブ", "", "", "http://image.eiga.k-img.com/images/movie/1126/315x210.jpg?4725", 146, "マルホランド・ドライブで起きた車の衝突事故で記憶を失った女は、近所の家に迷い込み、女優の卵ベティと出会う。同居することになった2人は、いっしょに彼女の素性を調べはじめるが……。音楽はいつものA・バダラメンティ。編集は公私ともに彼のパートナーであるM・スウィーニー。撮影は「ロスト・ハイウェイ」のP・デミング。美術は「イレイザーヘッド」で惑星の男を演じたリンチの高校時代からの親友、J・フィスク。", "2002-02-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸川乱歩猟奇館 屋根裏の散歩者", "江戸川乱歩猟奇館 屋根裏の散歩者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "江戸川乱歩原作の同名小説を原作として、猟奇趣味いっぱいに独特の世界を展開した異色作。下宿屋・東栄館の屋根裏を夜ごと散歩しては他人の生活をのぞき見ている男・郷田三郎は、今日は貴婦人・清宮美那子の部屋へとやって来た。郷田は、美那子と道化師や、中空の椅子の中に潜む彼女の運転手らとの情事をのぞき見ながら孤独と幻想の快楽にひたる。やがて、ただののぞき見にあきたらなくなった郷田は、天井の穴からモルヒネをたらして部屋の住人を殺害するという妄想にかられ始める……。『人間椅子』など、他の乱歩作品を取り込みながら、単なるポルノの域を越えたデカダンな美学を追求した意欲作。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEMESIS ２", "ネメシス２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "A・ピュンによる、バイオレンス・アクションの続編。秘密捜査官アレックスとの死闘によって滅ぼされたかにみえたサイボーグ軍団。しかし、かろうじて生き延びたその残党は、人間たちとの戦いの中で次第にその能力を進化させていた。彼らは“ネビュラ”と呼ばれる特殊生命体に生まれ変わり、荒野と化した地球を餌食にしようとしているのだ。しかし、アレックスの子孫たちが、驚異のDNAを開発し、ネビュラに対抗できる戦士“ネメシス”を誕生させる。生まれ変わったネメシスたちは、ネビュラに最後の戦いを挑む。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STING II", "スティング2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ニューマン＆レッドフォードによる名作「スティング」の続編で、脚本のD・S・ワード以外はキャスト・スタッフを一新。天才詐欺師コンビがボクシングの八百長をネタにひと芝居をうって、再び大仕事を。だが復讐に燃える仇敵が強力なワナを仕掛けてきて逆転、また逆転のだまし合いが繰り広げられる。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD CERT", "大本命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "D・フランシスの競馬小説を、T・リチャードソンが映画化した競馬犯罪ドラマ。障害レース馬アドミラルをめぐる陰謀と策略をサスペンスフルに描く。５台の車を相手にしたアドミラルと車の追跡劇、そして英国最大の障害レース、グランド・ナショナルなど見せ場もたっぷり用意されている。", "1974-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネ響「マエストロ6」　グスターボ・ドゥダメル　シモン・ボリバル・ユース・オーケストラ・オブ・ベネズエラ", "シネ響「マエストロ6」　グスターボ・ドゥダメル　シモン・ボリバル・ユース・オーケストラ・オブ・ベネズエラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "映画館でクラシック音楽のコンサートを上映する「シネ響」の6作目。2009年に28歳の若さで世界的名門ロサンゼルス・フィルの音楽監督になったグスターボ・ドゥダメルが、自身が育ったオーケストラ、シモン・ボリバル・ユース・オーケストラと共演し、ラベルの「ダフニスとクロエ」やバーンスタインの「マンボ」を披露する。", "2011-02-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOOST", "ジェームズ・ウッズのコカイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "実力派ハロルド・ベッカー監督が、アメリカにおける深刻なコカイン汚染の実態を鋭く告発した作品。J・ウッズ扮する主人公が、幸福なエリートサラリーマンから、やがてふと手にした“白い粉”によって落ちていく様をリアルに描いていく。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アジャパー天国", "アジャパー天国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "キャバレーで皿洗いをするゆきは幼い娘を抱え、夫の引き揚げを待っていた。しかし、実は夫はすでに大陸から引き揚げており、今は悪の道に入り恐喝をやっている。ゆきの勤めるキャバレーを舞台に拉致事件が起こったのがもとで、その事件にかかわっていた夫とゆきは再会する。斎藤寅次郎得意の喜劇。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FACE", "フェイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "昔カタギの強盗団のボス、レイが４人の仲間とともに造幣局を襲撃。だが、奪った金は予定額を下回り、仲間内で不満の声が持ち上がった。その夜、何者かが彼らの分け前を強奪するという事件が発生。仲間を再召集したレイは、意外な裏切り者の正体を突き止める……。貧しさゆえに犯行におよぶ強盗団の、欲望の人間模様を描いた犯罪劇。スリルを抑えたストイックなタッチが、ハードボイルドな空気を醸し出している。「フル・モンティ」のR・カーライルがレイに扮して渋みを発揮。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もうひとつの人生", "もうひとつの人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "アルコール依存症を抱えた人々とその家族から成る自助グループを捉えた記録映画。断酒歴２年の夫妻、７年前入院中に知り合った３人の仲間、断酒歴12年の女性、断酒歴17年の夫妻と病院の医師という４組に取材し、それぞれのエピソードが紹介される。“この人さえ消えてくれたらと殺意さえ覚えたこともある”とのすさまじい言葉も語られるが、アルコール依存症の害を説いたり、警鐘を鳴らす作品とはなっていない。一人一人が自分を見つめ、自分を語り、自分を取り戻す姿が描かれる人間讃歌となっているがゆえに感動的である。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONFIDENCES TRAP INTIMES", "親密すぎるうちあけ話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "パトリス・ルコント監督が描く大人のラブ・ストーリー。愛に傷ついた男女が新たな関係を築いていく。フランスの実力派女優、サンドリーヌ・ボネールが愛に苦悩するヒロインを体現。その円熟味を増した演技が味わい深い。愛する人に去られて落ち込む税理士のウィリアムは、精神分析医の診察室と間違えて自分のオフィスを訪れたアンナに興味を抱く。彼が真実を打ち明ける間もなく、彼女は赤裸々な話を始めるが……。", "2006-06-10", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BRASIER", "赤と黒の接吻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "1931年、北フランスの炭鉱町。父とともにポーランドから出稼ぎに来たビクトルは、パーティーで出会ったフランス女性アリスと、一目で恋に落ちる。だが、二人に嫉妬した炭鉱夫エミールが、アリスを強引に犯してしまう。同じ頃、幼い弟をも墜落事故で失ったビクトルは、自暴自棄になり、暴動に駆り立てられていくが……。民族問題や貧富の差といった、シリアスなテーマを追った大河ドラマ。「グレート・ブルー」の直後、殺到する企画を断り続けてきたJ・M・バールは脚本に惚れ込み、出演を即決したという。", "1991-01-01", "2,42,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("本日休診", "本日休診", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "本日休診の札を掛け、のんびりと昼寝しようと思った八春先生。ところが勇作の戦争後遺症による発作が出て、兵隊ごっこに付き合わされたり、飲み屋の女が流産したので駆け込んできたり、といった具合に問題が次々に起こり、テンヤワンヤとなる群像喜劇。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOY FROM ANDROMEDA", "ド★ロ★ム アンドロメダから来た少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "遠くアンドロメダから来た少年と、３人のティーンエイジャーの心の交流を描いたSFファンタジー。謎の物質“ファイヤーキー”の破片を見つけたジェニーたち３人は、導かれるまま、ある街にやってきた。そして３人は、200万光年の彼方から来た少年ドロムと出会う。ドロムは銀河大戦を逃れ、地球にたどり着いたものの、恐るべき暗黒の力をもつエイリアンに狙われていた。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cento Chiodi", "ポー川のひかり", "", "", "http://image.eiga.k-img.com/images/movie/54366/315x210.jpg?1927", 94, "「木靴の樹」などで知られるイタリアの巨匠エルマンノ・オルミ監督が、自身の映画人生最後の長編劇映画として撮りあげた作品。夏期休暇中のボローニャ大学で、大量の古文書に釘が打ち込まれているのが発見された。容疑者として浮かび上がったのは、学年末の授業を最後に姿を消した若き哲学教授。全てを捨ててポー川上流にたどり着いた彼は、そこで見つけた廃屋で暮らし始める。牧歌的な風景の中で織りなされる教授と村人たちの交流を通し、人生の豊かさとは何かを問いかける人間ドラマ。", "2009-08-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREAKING POINT", "ブレーキング・ポイント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ギャング組織による殺人の現場を目撃、そのことを法廷で証言したために、ギャングに命を狙われるハメに陥った一市民の抵抗を描くアクション映画。妻は報復を恐れ反対したが、正義のために法廷に立ったマイケル。引っ越し先のトロントまで魔の手は忍び寄り、やがてマイケルの妹の婚約者が組織に殺される。", "1976-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DETECTIVE STORY", "探偵物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ニューヨークの第21分署。犯罪を心底から憎んでいる鬼刑事ジムは、まじめな若者の出来心による横領事件に深入りするうちに、妻の忌まわしい過去を知ってしまう。人を許すことのできない男のジレンマ。こうして、頑固な正義漢は、破局のレールを直進する。落ち着きのない万引き女、わめき散らすギャング、ジムを詰問する弁護士。刑事部屋という限られた空間にいくつもの人間模様が絡み合い、サスペンス豊かな犯罪絵図が展開。舞台空間を限定した、緻密な人間ドラマの演出に定評のある、名匠ワイラーの面目躍如だ。ブロードウェイで上演された時の出演者たちが脇を固め、ダグラスの熱演を助けている。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どぶ川学級", "どぶ川学級", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "非行に走った少年少女たちが、青年労働者とのふれ合いを通して自立に向かっていく姿をつぶさに見つめた作品。自主製作だが、本当の教育とは何かを改めて考えさせられる真摯な教育映画であり、噛み応えのある社会派ドキュメンタリーである。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Brazilian Job", "ブラジリアン・ジョブ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「ミニミニ大作戦」（03）の続編。", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dumbland", "ダムランド", "", "", "http://image.eiga.k-img.com/images/movie/54286/315x210.jpg?3601", 35, "「ワイルド・アット・ハート」「インランド・エンパイア」で知られる鬼才デイビッド・リンチの会員制ウェブサイト“DAVIDLYNCH.COM”のために、リンチ監督が自ら描きおろした8本のアニメーションを上映する。", "2009-02-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SHUTTERED ROOM", "太陽の爪あと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "怪奇小説の大家、H・P・ラヴクラフトの短編小説『ダンウィッチの怪』をもとにした怪奇サスペンス映画。ニューイングランドの人里離れた孤島。新婚早々のケルトン夫婦がこの島にある屋敷を相続するためやって来た。ところが屋敷には恐ろしい怪物が住んでいるという噂が……。", "1966-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コドモのコドモ", "コドモのコドモ", "", "", "http://image.eiga.k-img.com/images/movie/53676/315x210.jpg?4723", 122, "小学生の妊娠・出産を描いたさそうあきらの同名コミックの映画化。監督は、前作「神童」に続いてさそう作品の映画化を手掛ける萩生田宏治。性に対する意識もないまま、幼なじみの男子と“くっつけっこ”をした小学5年生の春菜が妊娠。しかし、周囲の大人たちは春菜の言うことなど信じてくれず……。主人公・春菜を演じるのは新人の甘利はるな。その他、麻生久美子、宮崎美子、谷村美月ら実力派女優が揃い、主題歌は初めて劇映画への楽曲提供となる奥田民生。", "2008-09-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("疾風！鞍馬天狗", "疾風！鞍馬天狗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "幕末の京都には、嵯峨面をかぶった暗殺団・謎の古寺の怪僧や新選組が跋扈し、正義の剣士・鞍馬天狗の剣がうなりをあげる。嵐寛寿郎はこの後、大蔵貢が社長に就任した新東宝へ移り、本作品がシリーズ最終作となった。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バトル・ロワイアル", "バトル・ロワイアル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "暴力的な内容ゆえに論議を呼んだ高見広春の同名ベストセラー小説を映画化。失業率が15パーセントを超えた近未来の日本を舞台に、法の下で殺し合いを強いられる中学生たちのサバイバルを描く。子供を恐れるようになった大人が見せしめのために制定した法律BR法。全国の中学３年の中から無作為に選ばれた１クラスの生徒に殺し合いを強要する、この法の対象になった城岩中学３年B組の生徒が無人島に連行された。３日の制限時間を与えられた彼らは級友を殺すことにためらいを覚えつつ、それぞれ生き残る術を模索する……。原作のブラックユーモアを抑え、不条理な状況に置かれた子供たちのやるせない心情を描出。深作欣二監督ならではの感情的なバイオレンス描写が切迫感を伝え、せつない青春映画の色合いを濃くしている。狂気をはらんだ冷徹な教師にふんするビートたけしの怪演も見もの。興行の成功を受けて、2001年には追加撮影と再編集の施された特別編が公開された。DVDは初公開時版と特別版がある。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人情紙風船", "人情紙風船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "第二次世界大戦は、内外の多くの映画作家の命を奪ったが、戦火に消えた最大の才能の持ち主の一人は間違いなく山中貞雄であっただろう。若い頃から多くの作品のシナリオを手掛け、23歳で処女作「抱寝の長脇差」を撮ってから、28歳で遺作となったこの「人情紙風船」まで、約５年間で日本映画史にその名を深く刻んだ。物語は、出征を前にした山中貞雄の厭世的な心理状態が反映されている。長い間コンビを組んできた脚本家の三村伸太郎のシナリオを得て、貧乏長屋に住む人々の日常を苦悩に満ちたタッチで描いた。髪結の新三は、町の財閥である白子屋の箱入り娘を誘拐する。新三と同じ長屋に住む海野又十郎は、妻と二人で仕官する日を夢見て貧しい暮らしに耐えている。だが又十郎は新三の誘拐に一役絡んでいたのだった……。山中貞雄本人は“こんな作品を遺作にしたくはない”と戦地に赴いたというが、“キネマ旬報ベスト・テン”では、第４位に堂々選ばれた。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夫婦合唱", "夫婦合唱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "週刊誌記者の雄吉と電気会社の社長秘書・敦子は共稼ぎの若夫婦。だが敦子に思いを寄せる真山が雄吉のスキャンダルを吹聴して回ったことから夫婦間に亀裂が生じる。ある日、社長命令で妾に手切れ金を持っていった敦子は逆にヤクザに脅され危険な目に遭い、雄吉はその事件をスクープする。源氏鶏太のサラリーマン小説を中井貴一の亡父・佐田が快演する軽サスペンス。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("良人の貞操", "良人の貞操", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "姉妹のように仲良く女学校を卒業した加代と邦子。邦子の婚約者・水上は、加代にも心の奥で惹かれるものを感じていた。一方、加代は親友の幸福を祈り、自分はごく平凡な男と結婚。ところがある時、この平凡だが次第に凶暴になっていた夫が事故死して……。愛とモラルの狭間で揺れる女心を描いた三角関係悲恋物語。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINAL EQUINOX", "ファイナル・コンタクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "宇宙からの謎の物体をめぐり、CIA、犯罪組織、刑事が三つ巴のバトルを展開するSFミステリー。南米アステカの遺跡から発見された謎の物体“ライフ・ボム”。地球上の物質すべてを植物に変えるこの物体が、元物理学者シャイローと国際犯罪組織のトーマスによって強奪され、CIAのドレッグ指揮官は、長年トーマスを追うルガー刑事に捜査を依頼する。だが、捜査を進めていくうちに政府の最高機密を知ったルガーは、家族ともどもトーマスの悪辣な陰謀に巻き込まれていく。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMAMI", "パパまた脱いじゃった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "イタリアの小さな村を出てローマに行った娘アンナ。映画スターになって成功したと聞いた彼女の父は、娘を訪ねてローマに向かうが、とあるポルノ映画館のポスターに目をやるとそこにはアンナの姿が……。チチョリーナの後継者として政治活動に励んでいた、人気ポルノ女優M・ポッツィ主演の自伝的ピンク・コメディ。", "1992-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREASE2", "グリース２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "前作から２年後。ステファニーは、ライデル高校の女つっぱりグループの新リーダー。彼女好みの男になろうと転校生のマイケルは黒ずくめの覆面ライダーとなり、彼女の危機を救う。現代っ子二人の恋物語を軽快なロック・ナンバーにのせて綴る学園ミュージカル。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Glove", "ホームランが聞こえた夏", "", "", "http://image.eiga.k-img.com/images/movie/56606/315x210.jpg?3202", 144, "天才ピッチャーと賞賛されながらも聴覚を失ってしまったミョンジェは、聴覚障害者学校へ転校し、二度と野球はしないと誓う。その高校の野球部は中学生レベルの弱小チームだったが、ある日、暴行事件を起こして謹慎処分になった国民的スター投手キム・サンナムがコーチとしてやってくる。サンナムは練習を見ているだけのミョンジェに、一緒に野球をやろうと説得するが……。実在する聴覚障害者学校野球部の実話にもとづいた青春ドラマ。監督は「シルミド SILMIDO」「黒く濁る村」のカン・ウソク。", "2011-08-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Guns of Navarone", "ナバロンの要塞", "", "", "http://ecx.images-amazon.com/images/I/51LnliisNnL._SL210_.jpg", 144, "マクリーンの戦争アクション小説の映画化。1943年第二次世界大戦中、連合軍はケーロス島の南の小島ナバロンにある、ドイツ軍の巨大な大砲を爆破する計画を立てた。一見、不可能とも思える難攻不落の要塞島爆破のため、全軍から著名な登山家キースをはじめとする６人の特殊隊員が選ばれる。トンプソン監督は、彼ら特攻隊が無事目的を達成するまでのドラマをダイナミックな演出で描ききった。とりわけ目的地に着くまでの地理的・時間的問題を提示するくだりは説得力もあり、映画自体の緊迫感を盛り上げる。ティオムキンの勇壮な音楽、パノラマ的効果を生んだモリスの撮影も見事。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORBIDDEN ZONE", "フォービデン・ゾーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "アメリカン・インディーズから飛び出した、キッチュでアブノーマルな味わいを持つミュージカル風コメディの大傑作。監督のR・エルフマンはフランスに赴いて、前衛アーティストとして高い評価を得ていたが、この作品で映画に初挑戦。全編が書き割りという意表を突いたセットと、実写とアニメとを融合させた奇妙な手法で、スタイリッシュな演出を展開した。ヘラクレス家の地下には６次元の世界に通じる入口があり、娘のフレンチーはある日その禁断地帯に入り込む。そこは小人の王様（小人のカルト・スターであるエルベ・ビレチャイズ）や、異常に嫉妬深い王女などに支配されたアブノーマルな世界。あのオインゴ・ボインゴの珍妙で絶妙な音楽もかぶさり、まったく類をみないユニークな作品が完成した。悪魔の一団が、突如『ミニー・ザ・ムーチャー』の替え歌を歌い出すあたりのノリはその頂点。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BASIC INSTINCT", "氷の微笑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "元ロック・スターが全裸のままベッドに縛りつけられ、アイスピックで刺し殺された。容疑者として浮かび上がったのは、被害者の恋人で作家のキャサリン。数ヵ月前に発表した小説には、今回の事件そっくりの殺人事件が描かれていた。尋問やウソ発見器ではシロと出ながらも多くの謎を残す彼女に近づく刑事ニックは、キャサリンにレズビアンの女友だちがいることを突き止めるが、彼自身も危険にさらされていく。「ロボコップ」「トータル・リコール」などで今やハリウッドきってのヒットメーカーとなったオランダ出身のP・ヴァーホーヴェン監督による衝撃作。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bellissima", "ベリッシマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　娘をスターにしようと奔走する母親の姿と映画界の内幕を、風刺の効いたテイストで描いた作品。子役に起用されるにはコネが必要と聞いた母親は、早速撮影所に出入りする若者に取り入ろうとするが……。闊達な母親を見事に演じているのは、戦後のイタリア映画界を代表するアンナ・マニャーニ。原案は 、ネオリアリズムの代表作である「自転車泥棒」の脚本を手掛けたチェーザレ・ザバッティーニ。", "1981-08-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL DOGS GO TO HEAVEN", "天国から来たわんちゃん チャーリーのお話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "天国からよみがえってきた犬チャーリーと、動物と自由に話せる純真な少女アン・マリーとの友情を描いたファンタジー・アニメーション。主人公チャーリーの声をB・レイノルズが担当、美しいメロディーにのせて自慢の美声を随所で披露してくれる。「アメリカ物語」で一躍有名になった監督のD・ブルースはディズニー時代「眠れる森の美女」「ロビン・フッド」などを手掛けたベテラン。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿金／STUNT　WOMAN", "スタントウーマン　夢の破片〈かけら〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「トゥモロー・ネバー・ダイ」でハリウッドに進出したミシェル・ヨー（公開当時ミシェール・キング。後に改名）の主演作。スタントウーマンに扮し華麗なアクションを披露する一方で、仕事と恋に全力を尽くす女性像を好演している。香港映画界でスタントウーマンを目指すカンは、代理で出演した映画で認められ、スタント・チームに迎えられる。仕事も増え、仲間との友情が芽生えた頃、青年実業家サムと恋に落ちた彼女は、それまで意識したことのない女としての喜びに目覚めていく。やがて仕事を辞めてサムとの結婚を決意、バーの経営に乗り出したカンを、厳しい現実が待ち受けていた。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE PIECE ワンピース　呪われた聖剣", "ONE PIECE ワンピース　呪われた聖剣", "", "", "http://ecx.images-amazon.com/images/I/51cGOiJgQeL._SL210_.jpg", 90, "尾田栄一郎原作による人気アニメの劇場版第5弾。伝説の宝刀・七星剣が眠るというアスカ島を訪れたルフィ海賊団。ところが、船番をしていたゾロが行方不明になってしまう。ゾロを探して島の奥へと向かったルフィたちは美しい少女マヤと出会うが、ゾロのことを尋ねようとすると怯えて逃げだしてしまう。マヤを追ってとある村にたどり着いたサンジは、そこでゾロと遭遇。しかし、ゾロはなぜか海軍の剣士たちと行動を共にしていた。", "2004-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IBERIA", "イベリア／魂のフラメンコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "スペインの巨匠カルロス・サウラ監督が、自国の名作曲家イサーク・アルベニスの組曲「イベリア」から想を得て作り上げた贅沢なダンス映画。その組曲を中心にした演奏を背景に、スペインを代表する舞踏家や音楽家がパフォーマンスを繰り広げる。スペイン音楽と舞踏の世界に酔いしれる1本だ。世界中に愛されるスペインの音楽と舞踏“フラメンコ”。その世界の頂点に君臨するサラ・バラスやアイーダ・ゴメスらがステージに集う。", "2006-02-04", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Walk with Love and Death", "愛と死の果てるまで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "　英仏百年戦争時のフランスの農村を舞台に、戦乱に巻き込まれた若い男女の牧歌的なロマンスを描く。青年ヘロンは旅の途中、若い娘クラウディアと出会い、恋に落ちる。そんなふたりに悲劇が待ち受けていた……。ジョン・ヒューストン監督の愛娘アンジェリカが抜擢され、見事にヒロイン役を務めた。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In Darkness", "ソハの地下水道", "", "", "http://image.eiga.k-img.com/images/movie/57836/315x210.jpg?4260", 145, "第2次大戦時、ナチスが支配するポーランドでユダヤ人を地下水道にかくまっていた男の実話をもとに映画化したドラマ。監督は、「太陽と月に背いて」「僕を愛したふたつの国　ヨーロッパ　ヨーロッパ」などで知られるポーランド人女性監督アニエスカ・ホランド。1943年3月、ナチス占領下のポーランド。地下水道で働くソハは、迷路のように入り組んだ地下に盗品を隠しながら日々をしのいでいた。そんなある日、ゲットーからトンネルを掘って逃げてきたユダヤ人たちを見つけたソハは、最初は金目当てで彼らをかくまうが、次第に気持ちを通わせていき……。", "2012-09-30", "7,42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サウスバウンド", "サウスバウンド", "", "", "http://image.eiga.k-img.com/images/movie/34226/315x210.jpg?1404", 114, "直木賞作家・奥田英朗のベストセラー小説を、「間宮兄弟」の森田芳光監督が映画化。小学6年生の上原二郎は、仕事もせずに騒動ばかり起こしている元過激派の父・一郎の存在を恥ずかしく思っていた。ある日、上原家は母・さくらの提案で、一郎の故郷である西表島へ引っ越すことになる。地元の人々に歓迎される上原家だったが、やがて一郎は開発業者との戦いを開始し……。破天荒な父・一郎を豊川悦司が演じる。", "2007-10-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCANNERS III：THE TAKEOVER", "スキャナーズ３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "生まれながらのスキャナーである姉弟の運命を描いたシリーズ第３作。隔離されたスキャナーたちに残された道は、たび重なる頭痛に耐えながら、ひたすら己のスキャナー・パワーを隠し続けて生きていくことだけだった。だが試験段階の新薬を開発した博士の娘ヘレナは、あまりの頭痛に耐えかねて、無断で試薬を使ってしまう。次の日からヘレナの人格は変わり、世界の支配を企み始めた。やがて弟との対決の日が訪れる。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Al-massir", "炎のアンダルシア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "エジプト映画界の重鎮Y・シャヒーンの長編第32作となる歴史大作。舞台は12世紀ヨーロッパの文化都市アンダルシア。イスラム帝国君主カリフ・マンスールへのクーデターを謀る原理主義者グループが暗躍し、自由な精神を持つ哲学者アベロエスとその家族が陰謀に巻き込まれていく。いくつかのエピソードが重層的に絡み合う構成に、ラブ・ストーリー、サスペンス、ミュージカルなどの要素を詰め込んだ娯楽色豊かな仕上がりは、まさにエネルギッシュ。社会的な問題提起によって何度も上映禁止などの処分を受けてきたシャヒーンの面目躍如というべき骨太な快作だ。", "1997-01-01", "77,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪猫有馬御殿", "怪猫有馬御殿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 49, "「怪談佐賀屋敷」に続く入江たか子主演の“化け猫”もの。有馬侯頼貴の側室・おこよは侯の寵愛が新参のおたきに移ったのを嫉み、嫌がらせの末、ついに殺害してしまう。しかしその夜から有馬御殿では怪異が相次ぐ……。刀で切られた化け猫の首が宙を飛んで、おこよのノドに食らいつくシーンは一見の価値あり。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GETAWAY", "ゲッタウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "スティーヴ・マックィーン主演で有名なバイオレンス・ロマンのリメイク。罠にはめられた男女が黒幕を裏切り、メキシコ国境を目指す絶望的な逃避行の物語。主役は実生活でも夫婦のA・ボールドウィンとK・ベイシンガー。二人の絡みが強調されすぎて、ドライだった前作よりずいぶん甘ったるくなった。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桜田門外ノ変", "桜田門外ノ変", "", "", "http://image.eiga.k-img.com/images/movie/54986/315x210.jpg?2697", 0, "1860年、水戸藩と薩摩藩の浪士が開国をおし進めていた徳川幕府の大老・井伊直弼を暗殺した事件「桜田門外ノ変」の真相と背景を、吉村昭の同名小説をもとに描く時代劇。主演に大沢たかおを据え、長谷川京子、北大路欣也、柄本明、伊武雅刀ら豪華俳優陣が脇を固める。水戸藩士の関鉄之介は、妻と息子に別れを告げ、井伊直弼を討つため江戸へと向かう。鉄之介は１8人の襲撃部隊の指揮をとり、井伊の暗殺に成功するが、多くの仲間が犠牲となる。その後、鉄之介は薩摩藩による幕府制圧に参戦すべく京都へと向かうが……。", "2010-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春", "プライベートレッスン青い体験", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "10代の乱れたセックスの実態を描いた禁断のコリアン・エロス。転校生のジャヒョは、早熟な同級生のハラと念願の初体験を済ませる。一方、高校の女教師ジョンに思いを寄せるスインは彼女の部屋へ押し入り……。「深い悲しみ」のクァク・チギュン監督が青春時代の性を大胆に演出した。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2010-2011 ヴェルディ「イル・トロヴァトーレ」", "METライブビューイング2010-2011 ヴェルディ「イル・トロヴァトーレ」", "", "", "http://image.eiga.k-img.com/images/movie/56476/315x210.jpg?1291", 196, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を劇場のスクリーンで上映する「METライブビューイング」2010～11年シーズンの11作目で、ジュゼッペ・ベルディ作曲の傑作オペラ。吟遊詩人（トロバトーレ）の騎士マンリーコは、敵方のルーナ伯爵の女官レオノーラを愛し、幾度かの戦いを経てルーナからレオノーラを勝ち取る。ルーナは復しゅうを誓い、マンリーコの母アズチェーナを処刑すると脅してマンリーコをおびき出すが……。", "2011-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEN WORLDS COLLIDE", "地球最後の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "空想科学映画の大家G・パルが製作した近未来スペクタクル。新星ザイラが９ヵ月後に地球に激突することを予期した科学者たちは、国連に信じてもらえずに、百万長者の力を借りてロケットを完成。男女19人を選んで新しい星へと旅立っていく。SF版“ノアの箱舟”ともいうべき趣向の作品である。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dead and the Damned", "カウボーイ＆ゾンビ", "", "", "http://image.eiga.k-img.com/images/movie/57586/315x210.jpg?4573", 86, "西部劇とゾンビを組み合わせた異色のSFウェスタン・アクション。ゴールドラッシュに沸くアメリカ西部のとある町に、ある日、謎の隕石が飛来。その影響で町の住人たちが次々とゾンビと化し、人間を襲い始める。腕利きの賞金稼ぎと先住民族の戦士、酒場で働く美人給仕の3人は、ゴーストタウンと化した町でゾンビたちとの壮絶なサバイバルを繰り広げる。", "2012-03-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かわいいひと", "かわいいひと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "“グリコポッキー”のCMの世界を発展させたファンタスティックな青春オムニバス。同CMを手掛けた相米慎二が総監督にあたり、３人の新人監督に演出を任せている。電話ボックスで出会いを果たしたティーンのカップル、恋人の不在中に謎の女に転がり込まれた営業マン、大好きな男の子の危機を察知した少女の物語が、若手アイドルのみずみずしい魅力とともに展開。第３話のヒロイン、吉川ひなのが連発する奔放なラブラブ演技が楽しい。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幽霊VS宇宙人（2007）", "幽霊VS宇宙人（2007）", "", "", "http://image.eiga.k-img.com/images/movie/53446/315x210.jpg?2408", 106, "「呪怨」シリーズの清水崇監督と「怪談新耳袋」の豊島圭介監督が“幽霊”と“宇宙人”を題材に競作し、オムニバス形式で上映する人気シリーズの第3弾。\"四谷怪談\"を新解釈で現代に甦らせた清水監督の「ロックハンター伊右衛もん」と、宇宙人の女と人間の男たちの三角関係を描いた豊島監督による「略奪愛」の2本立て。キャストには「松ヶ根乱射事件」の山中崇ら個性派が顔を揃え、女性お笑いコンビのハリセンボンがナビゲーターを務める。", "2008-03-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IMAGINARY CRIMES", "詐欺 イマジナリークライム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "女子高校生のソニアは、父親のレイと妹のグレダとの３人暮らし。レイは母親の生前から、一獲千金を夢見て鉱山の投資に明け暮れていた。グレダの母親代わりとなったソニアは、高校の先生の勧めで、カリフォルニア大学への進学を決意、進学反対のレイと対立する。そんな時、レイが詐欺罪で逮捕される。いつまでも見返りのない投資に腹をたてた資本家に訴えられたのだ。グレダは施設に預けられ、家族はバラバラに。一人ぼっちになったソニアは高校の卒業式を迎えるが……。個性派H・カイテルが、奔放で身勝手な父親役をシブく演じている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROUGH　DRAFT", "ラフドラフト　殺人実況報道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "猟奇殺人犯と彼の依頼でその行動を追うジャーナリストの駆け引きを描くサイコ・サスペンス。常に真実を追及するジャーナリスト、ネルソンが偶然殺人の現場を目撃する。ネルソンの正体を突き止めた殺人鬼は、彼に自分のことを記事にしないかと持ち掛ける。動揺しながらもピュリッツァー賞に値するネタだと確信したネルソンは、殺人鬼へのインタビューを繰り返す。だが彼は、殺人を見せつけたり、濡れ衣を着せる殺人鬼に、次第に追い詰められていく。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN BELLISSIMO NOVEMBRE", "さらば恋の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "シシリー島を舞台に、17歳の少年の甘くせつない初恋と性体験をみずみずしく描く。少年ニーノは、都会的に洗練された魅力的な叔母チェッティーナに恋をし、その熟れた肉体の虜となる。だが少年にとっては唯一の恋も彼女にとっては数ある浮気の一つにすぎず、やがてそのことに気付いた少年は大人への一歩を踏み出していく。", "1969-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LEGEND OF WISELY", "飛竜伝説 オメガクエスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "S・ホイ、T・ロン、J・ウォンの顔合わせが魅力の秘境アドベンチャー。伝説の秘宝“ドラゴンパール”をめぐり、冒険家、大富豪、暗黒街のボス、密教僧たちが争奪戦を繰り広げるスペクタクル・アクション。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Now Pronounce You Chuck and Larry", "チャックとラリー　おかしな偽装結婚!?", "", "", "http://ecx.images-amazon.com/images/I/514OEJmCyyL._SL210_.jpg", 115, "男同士の偽装結婚をめぐって巻きおこる騒動を、人気コメディアンのアダム・サンドラーと「最後の恋のはじめ方」のケビン・ジェームズ主演で描いたコメディ。亡くした妻を思い続けながら子どもたちを育てているラリーとプレイボーイのチャックは、消防署の同僚で親友同士。子どもたちを年金の受取人にするために再婚しなければならなくなったラリーは、ゲイのふりをしてチャックと偽装結婚することを思いつくが……。", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hidden", "ヒドゥン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "次々と人間をボディ・スナッチし残虐な行為を重ねるエイリアンと、それを追う刑事。そしてやはり人間を装いFBI捜査官として“悪人”を追うエイリアン。三者の壮絶な戦いが、ロサンゼルスの街を舞台にバイオレンス・タッチで描かれる。悪のエイリアンがハードロック好きでスピード狂、非道の限りをつくすという設定や、撃たれても簡単に死なずボロボロの体で立ち向かってくる不気味さなどにJ・ショルダー監督のセンスが光る。話題になったSFXは「エルム街の悪夢２」でもJ・ショルダーとコンビを組んだケヴィン・イェーガー。彼は「チャイルド・プレイ」の人形も手掛けている。アヴォリアッツ国際ファンタスティック映画祭グランプリ受賞。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("５ PER L’INFERNO", "戦場のガンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "奇抜なマカロニ・ウエスタン“サバタ”シリーズで知られるF・クレイマー監督の戦争アクション。米軍レンジャー部隊の５人が、ドイツ軍の重要機密を奪うため敵の司令部への潜入を試みる。やがて５人は女ゲリラのヘルガと合流し、機密が収められた金庫に迫るが、すんでのところで敵との乱戦になって……。", "1968-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ICE　RAIN", "氷雨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "2005年、徴兵のために俳優活動休止を余儀なくされた人気俳優ソン・スンホン。彼の熱演が光る山岳を舞台にした愛のドラマ。「ほえる犬は噛まない」のイ・ソンジェも好演。アラスカ山脈を舞台に若者たちのドラマがミステリー風に展開する。尊敬する先輩ジュンヒョンらと共にパーティを組んで登山に挑む主人公ウソン。しかし途中で吹雪に遭い、二人だけが一行からはぐれてしまう。洞窟に避難した後、意識がもうろうとする中、ジュンヒョンは過去にあった愛の思い出を語り始める……。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真心・My name is MAGOKORO", "真心・My name is MAGOKORO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "真心ブラザーズが歌う『流れ星』『橋の上で』ほか全４曲に乗せて、永瀬正敏扮する売れない役者の青春を描く短編。30歳を迎えた真心タロウは、独身の冴えない役者。相棒の下心ジロウとオーディションを受けながら、昔の彼女と出会ったり、風俗嬢と朝までカラオケを歌ったりといった毎日を過ごしている。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD CHANNELS", "エイリアン・コレクター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ロックンロールと美女を愛する“コスモ”なるエイリアンが地球に襲来。彼らは謎の怪電波を使って、人間をミニチュア化し、自分のコレクションにしてしまうのだ。放送局を占拠された人気DJのダン・オデアーは、ミニチュア化されたリスナーたちや恋人を助けるため、エイリアンに戦いを挑む。P・ヒップ主演によるコメディ・タッチのSFホラー。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KNOCK OFF", "ノック・オフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ダブルチーム」でハリウッドに進出を果たしたツイ・ハーク監督が、その主演俳優ジャン＝クロード・ヴァン・ダムを香港に招いて撮り上げたハードアクション。舞台は返還直前の香港。そこから密輸出される模造品ジーンズをめぐり、不審な事件が起こった。事件に巻き込まれたジーンズ会社のレイはCIAの要請を受け、香港の警官とともに事件の背後にいるマフィアと戦うことになる。サモ・ハン・キンポーがアクション監督を務め、ヴァン・ダムのハードな立ち回りに、香港らしい軽妙さを盛り込むことに成功している。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POUR SACHA", "熱砂に抱かれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1967年に起こった第３次中東戦争を背景に、イスラエルのキブツ（集団開拓農場）で働くフランスの元リセ教師サーシャと、彼のパートナーであり教え子でもあるヒロイン、ローラの姿を綴った大人の恋愛ドラマ。かつてのリセの仲間との恋のさや当て、生々しい中東戦争の爪痕が映画の様々な場面に現れては消えていき、一人の男に愛を捧げた女の鮮烈な生き様が中心主題として浮き立つ。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHANTASM", "ファンタズム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "両親のいない少年マイクが兄の友人の葬儀を双眼鏡で見ていて、埋葬するべき柩を葬儀屋が再び霊柩車に積みこむのを目撃した。兄に話しても信じてもらえず、一人で霊廟へさぐりに出かけたマイクが見たものは、生きている死者たちの異様な群れ。おまけに気の狂った墓守に襲われ、驚くほどの速度で獲物を狙うシルバー・スフィア（短剣のとび出た銀色のボール）に追われることに。かろうじて危機は脱出できたが、証拠にと切り落として持って帰った墓守の指は奇妙に変形し、それ自体エネルギーを持った生き物に変わっていた。あらゆる物体を吸収してしまう異次元の魔力と少年たちとの闘いが、特撮技術を駆使した映像の中で始まる。アヴォリアッツ国際ファンタスティック映画祭で審査員特別賞を受賞。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE JOLSON STORY", "ジョルスン物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "20世紀初頭のアメリカの人気ポピュラー・シンガー、アル・ジョルスンの伝記映画。19世紀末、少年ジョルスンは舞台への思いを胸に家を飛び出す。そして、当時大流行だったミンストレル・ショーの黒人歌手のまねをして顔を黒く塗り、地方巡業で大当たりをする。たまたま客席にいたオスカー・ハマースタインに認められニューヨークに出て大成功。トーキー第１作の「ジャズ・シンガー」に主演するチャンスを得て、全米を熱狂させるまでになる。『スワニー』『マミー』などの名曲を、楽しげに朗々と歌うジョルスン・スタイルを、パークスが気持ち良さそうに好演。ドラマのツボを心得たスイング感あふれる佳作。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Open Water 2: Adrift", "オープン・ウォーター2", "", "", "http://image.eiga.k-img.com/images/movie/34086/315x210.jpg?3145", 94, "低予算ながら大ヒットを記録した海洋サバイバル・パニックの第2弾。前作よりさらに恐ろしい実話を基に、自分たちの不注意から広大な海のど真ん中で命の危険にさらされる若者たちの恐怖体験を描く。大型ヨットでメキシコ湾沖にクルーズに出た6人の男女。泳ぎを楽しもうと海に飛び込んだ彼らは、ヨットに上るための梯子を出していなかったことに気付く。ヨットに戻る方法を見つけられないまま、6人は徐々に体力を失っていき……。", "2007-07-28", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蝶々雄二の夫婦善哉", "蝶々雄二の夫婦善哉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "1955年からラジオ、1963年からはテレビで放送され、高聴取率・視聴率を稼いだお化け番組「夫婦善哉」の司会者コンビの原案・主演による人情映画。大阪ミナミの盛り場でぜんざい屋を営む利子と忠太郎。だが忠太郎は商売に気乗りせず、飲み屋の仲居に手を出す始末。利子は忠太郎を思い、店を洋食屋に改造する。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APARTMENT ZERO", "アパートメント・ゼロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "フォークランド紛争終結後のブエノスアイレス。名画座を経営する映画青年エイドリアンは、部屋代節約のためにルームメイトを募集する。やがてやって来たジャックとの共同生活は順調に始まったかに見えるが、次第に彼の恐ろしい秘密が明らかになる……。神経質な主人公を「アナザー・カントリー」のC・ファースが熱演。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHAKESPEARE WALLAH", "インドのシェイクスピア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "シェイクスピア劇を演じながらインドを旅する旅芸人たち。一座のヒロインが、若い色男と恋仲になるが、彼は歌謡映画のスターとも関係をもっていた……。J・アイヴォリーが、女性の自立をメインテーマに描き、その名を知らしめた初期の傑作。インド映画界の巨匠S・レイが音楽を担当しているのにも注目。", "1965-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARNOSAURII", "ダイナソーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "電力関係のトラブルが続く砂漠の鉱山施設に派遣された調査員たちが遭遇する、恐ろしいバイオ・モンスターとの闘いを描いたSFサスペンス。鉱山施設で調査員たちを待ち構えていたのは、軍の極秘実験から生まれた、想像を絶する生命体であった。通信機能を断たれ、逃げ場を失い、成す術もなく次々と殺されていく調査員たち。さらに、施設の恐るべき事実を聞かされ、脱出までに残された時間がわずかしかないことが判明する……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("稲村ジェーン", "稲村ジェーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "波乗り野郎のヒロシ、音楽野郎のマサシ、チンピラ野郎のカッチャン、そしていい女・波子の４人を中心とするナサケないヤツらが、“稲村ジェーン”と呼ばれる伝説の波が来る日を待つ。湘南、稲村ヶ崎を舞台にしたミュージシャン桑田佳祐の初監督作品。脚本は作詞家の康珍化。もちろん、桑田率いるサザンオールスターズの歌の数々が全編を彩る。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蒼き狼　地果て海尽きるまで", "蒼き狼　地果て海尽きるまで", "", "", "http://image.eiga.k-img.com/images/movie/1006/315x210.jpg?4023", 136, "史上最大の帝国・モンゴルを築き上げた英雄チンギス・ハーンの半生を描く歴史大作。部族の長の息子として生まれたテムジン（のちのチンギス・ハーン）は、彼らの始祖“蒼き狼”の生まれ変わりとしてたくましく成長する。しかし敵対する部族に父親が殺されると、部下たちから見捨てられてしまう……。「男たちの大和／YAMATO」の角川春樹が製作費30億円を投じ、オールモンゴルロケを敢行。監督は「Wの悲劇」の澤井信一郎。", "2007-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀嶺の王者", "銀嶺の王者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "当時日本全国に“ザイラー・ブーム”を巻き起こした、オーストリアの天才スキーヤー、トニー・ザイラーを主演に据えたスキー映画。アルプスの雪崩で友人を失ったザイラーが、傷心をいやすためはるばる日本へ。そこで彼は、日本娘と美しい恋の花を咲かすのだった。「黒い稲妻」(1958)で、ザイラーの華麗なスキー・シーンを捉えた名カメラマン、ラウテンフェルトがわざわざ来日して担当したスキー・シーンが見もの。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMILE AND THE DETECTIVES", "エミールと探偵たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "E・ケストナーのジュブナイル小説を、ディズニー・プロが映画化した冒険ストーリー。ベルリンに住むおばあさんの家まで、400マルクのお金を届けることになったエミール少年。ところが、バスでスリにそのお金を盗まれてしまい、少年探偵団の仲間とともに大追跡を開始。その頃スリの仲間たちは、銀行から大金の強奪を計画していた……。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鋼鉄の巨人／怪星人の魔城", "鋼鉄の巨人／怪星人の魔城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "惑星カピアから来た怪星人・カピアは地球征服のため東京周辺に原因不明の奇病をまきちらし、子供たちは疎開。スーパー・ジャイアンツは子供たちに、ジャイアンツの助けを呼べる不思議な玉を渡す。ジャイアンツとカピア星人の対決シーンは、ミュージカル調に処理されている。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DARK MIRROR", "暗い鏡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "著名な社会学者が殺され、容疑者として浮かび上がったのは美しい双子の姉妹。捜査陣はどちらか一人が犯人であるということまでは突き止めるのだが、そのどちらかが断定できない。そこで心理学者に二人をテストしてもらうことに……。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NIGHT OF THE WARRIOR", "ナイトウォリアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ギャングが賭けを仕切り、都市の片隅でひそかに行われる“格闘試合”。主人公マイルズは類まれなテクニックで試合に勝ち続けてきたが、恋人のために引退を決意するのだった。だが彼を試合に出させたいプロモーターは、彼の恋人を誘拐、マイルズは再び死闘のリングに上がる。あらゆる武術を駆使した、主人公の格闘アクションが見もの。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CHIAVI DI CASA", "家の鍵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "イタリアの名匠ジャンニ・アメリオによる人間ドラマ。障害を抱えた我が子と初めて対面した父親の戸惑いとその心の変化を、繊細かつ力強く描き出す。主人公に助言を与える年上の女性を名女優シャーロット・ランプリングが演じている。15年前、恋人を出産時に亡くしたショックで、子供を手ばなしたジャンニ。彼は障害を持つ息子パオロをベルリンのリハビリ施設に連れていくため、初めて対面することになる。", "2006-04-08", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thomas & Friends: Misty Island Rescue", "きかんしゃトーマス　ミスティアイランド　レスキュー大作戦!!", "", "", "http://image.eiga.k-img.com/images/movie/56156/315x210.jpg?1919", 65, "人気児童絵本「きかんしゃトーマス」の劇場版。海に落ちそうになったディーゼルを助けたトーマスは、ご褒美としてメインアイランドに招かれることになる。しかし、その途中、けん引されていた船との鎖が切れてしまい、乗っていたイカダごと海を漂流。見知らぬナゾの島ミスティアイランドにたどりつく。トーマスはそこで出会った3台の機関車ダッシュ、バッシュ、ファーディナンドの力を借り、元いたソドー島を目指して冒険に出る。", "2011-04-23", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すっぽんぽん", "すっぽんぽん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "何をやってもうまくいかず、やることなすことすべてがみみっちいことばかりの岩清水鶴之助。借金取りのヤクザに追われ、妻にも逃げられた彼は、ある日何十年ぶりに一人の男と再会する……。群馬県の片田舎を舞台に不器用に生きる男の姿を描くおかしくも哀しい物語。主演は森崎東監督作品などの名バイプレーヤー、河原さぶ。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEAN COCTEAU ： AUTOPORTRAIT D’UN INCONNU", "ジャン・コクトー　知られざる男の自画像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "天性の詩人コクトーの生涯を、生前の様々なインタビュー・フィルム、コクトー自身が撮った16ミリのプライベート・フィルム、若い時代の写真集、コクトー映画の抜粋などを織り交ぜてまとめ上げた異色のドキュメンタリー。詩、小説、戯曲、映画、デッサンと、多岐に及んだ自らの芸術を語り続けるコクトーの言葉は、エスプリと含蓄に富んで、観る者を想像界の迷宮に導いていく。さらに、前衛精神のあふれる1920年代のパリに端を発する、めくるめく交遊関係の紹介。ピカソ、ストラヴィンスキー、ダリ、サティ——なかでも、親友ラディゲの死のショックで始めた麻薬体験の告白が、詩人の苦渋を垣間見せて圧巻だ。コクトーをはじめとする芸術家たちの姿を見るだけでも価値がある。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRAIGHT TALK", "ストレート・トーク こちらハートのラジオ局", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "カントリーのシンガー・ソングライターであり、女優としてもアカデミー賞にノミネートされたことのあるD・パートンが主演するコメディ。職を失い、シカゴへ出てきたシャーリーは、やっとのことでラジオ局での職にありついた。初出勤日に、慣れないオフィスをうろつくうちに間違ってラジオ相談の部屋へ。心理学者に間違われたシャーリーが訳のわからないまま放送は流れてしまう。ところがこれが大ヒット。途端に彼女は人気者に。一方ラジオ局側は、彼女がニセ心理学者であることを隠すのに必死だった。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Laughing Policeman", "マシンガン・パニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "スウェーデンの犯罪小説の白眉“マルティン・ベック”シリーズ第４作の映画化で、舞台はストックホルムからサンフランシスコに移された。バスの大量殺人事件を追う主人公、殺人課の警部マルティンを中心に、都市型犯罪の恐怖を描く。ビデオ発売時タイトルは「笑う警官／マシンガン・パニック」。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("National Treasure 3", "ナショナル・トレジャー3（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2014-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NUTCRACKER", "くるみ割り人形", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "世界中で愛され続けている名作バレエ劇を映画化。E・T・ホフマンの童話をもとに書かれた、クリスマス・イヴの夜に繰り広げられる夢あふれるファンタジックな物語。イヴの日にくるみ割り人形をもらったマリーは、その夜、人形が変身した王子様に魔法の島に連れられて行くのだった……。J・バランシンの振付けによるニューヨーク・シティ・バレエ団の華麗な踊りはため息の出る素晴らしさ。小さなヒロイン、マリーを夢へいざなう王子役にM・カルキンを配している。G・ルーカス率いるSFX集団、ILMが特殊効果を担当。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢みるように眠りたい", "夢みるように眠りたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "昭和初期の東京。私立探偵・魚塚に、月島桜という老女から娘を探してくれとの依頼がくる。調査を進めるうちに魚塚は依頼主はいにしえの名女優で、彼女がかつて主演した映画「永遠の謎」はラスト・シーンを残して未完に終わったことを知る。魚塚はさらに意外な事実を発見する……。林海象のデビュー作で、探偵ものの定番を破った怪作。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今日もまたかくてありなん", "今日もまたかくてありなん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "湘南の海の近くに家を建てたサラリーマンが、借金返済のため会社の部長にその家を貸す。サラリーマンの妻と子供は軽井沢の実家に帰るが、そこではヤクザが暴力事件を起こしていて……。歌舞伎役者・中村勘三郎が現代劇に出演した作品で、ヤクザと対決する男を熱演している。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Possible Changes", "不機嫌な男たち", "", "", "http://image.eiga.k-img.com/images/movie/33856/315x210.jpg?4824", 114, "エリート会社員から映画作家へと転身を遂げた韓国アートフィルム界の新星、ミン・ビョングク監督のデビュー作。30代半ばを迎えた男性2人のミドルエイジ・クライシスを、純愛や不倫といった要素を織り交ぜながらミステリアスなタッチで描く。ストーリーは2部構成で展開し、冷たいブルートーンの画面では彼らが直面する現実を、暖かみのあるレッドトーンの画面では彼らが夢見る“もうひとつの現実”を映し出す。", "2007-02-17", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虹色ほたる　永遠の夏休み", "虹色ほたる　永遠の夏休み", "", "", "http://image.eiga.k-img.com/images/movie/57766/315x210.jpg?3877", 105, "著者の川口雅幸が自身のWEBサイトで連載をはじめ、好評を博して出版化に至った小説「虹色ほたる　永遠の夏休み」をアニメーション映画化。1970年代の自然豊かな田舎の村を舞台に、少年のひと夏の不思議な体験と成長を描く。1年前に父親を亡くした小学6年生のユウタは、夏休みに父親とカブトムシをとりにきた思い出の山奥へ出かける。ふとしたはずみで足を滑らせ転倒したユウタは意識を失い、目を覚ますと30年以上前にタイムスリップしていた。ユウタをいとこと呼ぶ少女さえ子や、同い年の少年ケンゾーと出会ったユウタは、そこで1カ月以上の時間を過ごすことになるが……。", "2012-05-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VEIN 静脈", "VEIN 静脈", "", "", "http://image.eiga.k-img.com/images/movie/56376/315x210.jpg?2759", 58, "等身大人形を用いた一人芝居の人形演劇で独特の世界観を確立しつつも、2010年7月に62歳で他界した岡田芳一が、自らの舞台演目を映画化した遺作。岡本演じる顔に包帯を巻いた「私」と血管がむき出しの身体を持つ人形の「少女」が、お互いをいたわりあいながらも、決して相手を救うことができないことから生じる心の痛みを描く異色のドラマ。", "2011-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖艶毒婦伝・人斬りお勝", "妖艶毒婦伝・人斬りお勝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "宮園純子主演の“妖艶毒婦”シリーズ第２作。演出はベテラン中川信夫があたっている。甲源一刀流の娘・お勝の受難と復讐の物語。東映は同じく藤純子の“緋牡丹お竜”シリーズとともにドル箱シリーズ化を狙ったが、３作で打ち止めとなった。第１作の監督・石川義寛は中川の弟子で、「東海道四谷怪談」のシナリオにも参加している。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本一のショック男", "日本一のショック男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "“日本一の～男”シリーズの最後の作品。東北の田舎町・霜焼村に押しよせた過疎の波を懸命に防ごうとする村の巡査、日本一作。一作はある日、東京からやって来た少女の自殺を止める。少女の恋を成就させるために、彼は少女と東京に向かうのだった……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEVA ZAN", "DEVA ZAN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("センス・オブ・ワンダー／レイチェル・カーソンの贈りもの", "センス・オブ・ワンダー／レイチェル・カーソンの贈りもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "著書『沈黙の春』の中で、早くから化学物質の危険性を訴えてきた科学者レイチェル・カーソン。本作は、彼女の死後、友人たちの手によって出版されたエッセイ『センス・オブ・ワンダー』をもとに制作された長編ドキュメンタリーだ。彼女が愛した別荘周辺の森や海辺を背景に、彼女のメッセージ＝“自然の神秘や不思議に目を向ける感性の大切さ”を翻訳者・上遠恵子の朗読によって綴る。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOONLIGHT AND VALENTINO", "ムーンライト&ヴァレンチノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "突然の事故で夫を亡くした未亡人レベッカを慰めようと集まった人たち。夫に愛されていないと思い込んでいる人妻、義娘や周りの人間に疎まれている継母、そしてボディ・コンプレックスを持つ少女。彼女たちはそれぞれの問題を温め、ぶつけ合いながら、徐々に相手と自らを癒していく。ニール・サイモンの愛娘エレン・サイモンの戯曲を映画化。彼女のまわりで実際に起きた出来事をもとに、個性豊かな女優陣が印象的なシーンを生み出している。ボン・ジョヴィのリーダー、J・B・ジョヴィが、沈みがちな４人の女性を華やげるセクシーなペインター役を好演。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DO　NOT　DISTURB", "小さな目撃者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "口の不自由な少女と殺人犯の戦いを描いたサスペンス活劇。アムステルダムに観光旅行中だった、口のきけない10歳の娘メリッサが、路地裏で殺人事件を目撃。口封じを図る犯人に追われながらも、彼女は見知らぬ街を逃げまわりながら反撃に転じようとする。アムステルダムの迷宮のような町並みの描写が効果的で、チェイスなどの見せ場とともにスリルをあおる。", "1999-01-01", "70,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAIR OF THE WHITE WORM", "ケン・ラッセルの白蛇伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "B・ストーカーの怪奇小説『白蛇の巣』を原作に、鬼才K・ラッセル監督が独特のセンスで映画化したゴシック・ホラー。イギリスの片田舎に伝わる白蛇伝説をめぐり、悪夢の世界が展開されていく。腰をくねらせながら壷の中から出てくる蛇女役のA・ドノホーと、ロック調の白蛇伝説民謡が愉快。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HALLOWEEN４：THE　RETURN　OF　MICHAEL　MYERS", "ハロウィン４　ブギーマン復活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "今は亡き名優D・プレゼンスが出演した、人気ホラーのシリーズ第４作。前作で焼死したはずの殺人鬼ブギーマン。しかし、ハロウィンの夜、再びよみがえり冷酷な殺人を繰り広げる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KIDS", "KIDS", "", "", "http://image.eiga.k-img.com/images/movie/34186/315x210.jpg?1540", 0, "幅広い作風で人気の作家・乙一による短編「傷-KIZ／KIDS-」を原作とする青春ファンタジー。他人の傷を自分の体に移す能力を持つ少年と心に傷を抱える青年との友情を、小池徹平＆玉木宏主演で描く。寂れた街で荒んだ生活を送る青年タケオ。街にやって来たばかりの内気な少年アサトと出会った彼は、アサトが持つ不思議な力を目の当たりにし……。監督を務めるのは、乙一作品の映像化は「きみにしか聞こえない」に続き2度目となる荻島達也。", "2008-02-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Endangered Species", "真夜中の極秘実験", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "　退職した元ニューヨークの刑事ルーベンはひとり娘を連れて、アメリカ西部の田舎町へ休暇に向かう。そこで彼は女性保安官ハリエットが担当していた牛の連続惨殺事件の調査を手伝うことに。だが、その背後には軍の陰謀が……。キャトル・ミューティレーションの真相を描く異色サスペンススリラー。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三億円をつかまえろ", "三億円をつかまえろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "“３億円強奪事件”の時効成立を間近に控えてのタイムリーな企画だったが、中味はこの事件とはまるで関係ない前田陽一の強盗喜劇。農協の大金庫に眠る３億円をムショ仲間の３人と地主のドラ息子、赤ん坊という５人組が襲う。金庫を守るガードマンとのダマシ合いが笑わせる。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENDLESS LOVE", "エンドレス・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "17歳のデビッドと15歳のジェイド。親から交際を禁じられてもお互いを想う気持ちは募るばかり……。大きな障害にぶつかり、悩み傷つく思春期の二人の姿を捉えたラブ・ロマンス。ライオネル・リッチーとダイアナ・ロスが歌うテーマ曲もヒットした。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雨情", "雨情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "明治末期から大正・昭和にかけて人々に親しまれた童話詩人・野口雨情の放浪生活を描いた時雨音羽の原作を、久松静児監督と森繁久彌のコンビで映画化した純愛ドラマ。結婚しながらも、惚れた女のあとを追って家出するダメ男を森繁久彌が好演。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE CHRISTMAS", "ホワイト・クリスマス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ビスタビジョンの記念すべき第１作。ボブとフィルの芸人コンビは、知人が経営するホテルが破産寸前と知って、景気回復を図ってショウを企画するが……。ラストのショー・シーンが楽しい心温まるメルヘン。主題曲『ホワイト・クリスマス』も大ヒットした。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("With A Girl of Black Soil", "黒い土の少女", "", "", "http://image.eiga.k-img.com/images/movie/53506/315x210.jpg?2490", 89, "世界各地の映画祭で高い評価を受ける韓国の鬼才チョン・スイル監督が、家族を支えて生きる少女の孤独な日常を詩情豊かに綴った人間ドラマ。廃鉱寸前の鉱山で働く父や情緒不安定な兄と暮らす9歳の少女ヨンリム。父と兄の面倒を一生懸命に見ている彼女は、一家にとって母親のような存在だった。ある日、父が病気で仕事をクビになってしまう。もっと重い病気にかかれば保障を受けられると知ったヨンリムは、あるアイデアを思いつき……。", "2008-03-08", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花と竜", "花と竜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1954年東映版（佐伯清監督・藤田進主演）に次ぐ再映画化で原作は火野葦平の小説。明治末に沖仲士から身を起こし九州一帯の港湾荷役を手中に収めた大親分、玉井金五郎の一代記。裕次郎は無鉄砲でロマンチストの金五郎を演じた。ラストの雪中の決闘が見どころ。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("[Rec]3 Genesis", "REC レック3 ジェネシス", "", "", "http://image.eiga.k-img.com/images/movie/57856/315x210.jpg?3720", 80, "ドキュメンタリータッチの主観映像で登場人物が遭遇する恐怖を描くスペイン製ホラー「REC レック」のシリーズ第3作。晴れて挙式の日を迎えたコルドとクララは幸せの絶頂にいたが、感染すると人を襲う謎の伝染病に侵された新郎の叔父が発病し、披露宴の出席者を襲い始める。混乱のなか離ればなれになってしまった2人は、それぞれ愛する人と再会するため、感染し変わり果てた友人や親族に立ち向かうが……。前2作でジャウマ・バラゲロとともにメガホンをとったパコ・プラサ監督が手がけた。", "2012-04-28", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLIPPER", "フリッパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "都会育ちの思春期の少年が、イルカとの交流を通して大人へと成長していく姿を描く。両親が離婚したばかりのサンディは、フロリダの小さな島に住む変わり者の伯父ポーターの下でひと夏を過ごすことになった。ある時彼は、密漁者ディックに母親を殺された子供のイルカと出会う。フリッパーと名づけて友情をはぐくむが、その頃、島では原因不明の不漁が続き、フリッパーに疑惑がかかる……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばり捕物帖 自雷也小判", "ひばり捕物帖 自雷也小判", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "老中・阿部伊予守の妹姫である、捕物好きのお七と、そのお守り役・佐々木兵馬のコンビが活躍する「ひばり捕物帖 かんざし小判」（1958）の続編。今回は、お七の名をかたる女が、伝馬町牢屋敷に火をつけ、大塩平八郎一派の残党を逃がしてしまう。疑われたお七は、謎の女が残した“散らし小判”を手掛かりに、事件究明に乗り出し……。美空ひばりの十変化、彼女の歌を生かした、ミュージカル調の演出が見もの。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・ギャンブラー", "ザ・ギャンブラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "アンソロジー・フィルム「アゲイン」などを手掛け、日活アクション・ファンを自認してきた作家、矢作俊彦が思い入れたっぷりに撮り上げた無国籍アクション。ガン・アクションこそ少ないものの、ハードボイルド感覚あふれた作品に仕上げている。舞台は無法地帯と化した町。ここに流れ着いたおたずね者の若いギャンブラーと、かつてはスゴ腕と恐れられ、落ちぶれた中年ギャンブラーの対決を描く。日活アクション時代に一世を風靡した宍戸錠が、往年の名キャラクター“エースのジョー”として、アル中を克服して勝負を挑む中年ギャンブラーを好演。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東幹部会", "関東幹部会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "５年の刑期を終えて出所した寺田組の組長・次郎は、東上してくる大場組に対する鉄砲玉となり、自分の生まれ故郷の決戦場、富士市へ向かう。快作「斬り込み」(1970)を思わせるチンピラたちの群像が、強烈。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWITCHING CHANNELS", "スイッチング・チャンネル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "B・ヘクトとC・マッカーサーの原作『THE FRONT PAGE』は、1940年代、H・ホークスの「ヒズ・ガール・フライデー」、1970年代、B・ワイルダーの「フロント・ページ」など過去３回映画化されているが、本作は主役を、従来の新聞記者からTVのニュースキャスターに、男性から女性にと時流に合わせたJ・レイノルズの脚色が小気味良い。人気TVキャスター、クリスティはこのところハードワークで限界にきている。そんな時、短い休暇で出逢ったハンサムな青年実業家と恋に落ち、局のボスで元夫のサリーにキャリアを捨てると告げにいく。辞めさせまいと必死に画策するサリー。折も折、一大スクープが……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mogura", "Mogura", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "高村薫の『奏でる女、踊る男』を映画化。将来に絶望し、閉ざされた世界で暮らす元ミュージシャンの男・Mogura。ある日、雑誌で見た女性チェリスト・チェロに魅かれ、彼女に手紙を送り始める。チェロ奏者として生きていくことに行き詰まり、孤独な日々を送っていた彼女は、迷ったあげくMoguraに会い、彼の耳が聞こえないことを知る。自分の分身であるチェロを感じられない男に愕然とする女、開いてしまった心の扉に戸惑う男……。「鉄拳」「南京の基督」など数々の映画音楽を手掛けてきた梅林茂の初監督作。セリフによる説明を排除し、映像と音楽で紡ぎ出す映像詩を作り上げている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST OUTLAW", "ラスト・アウトロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "南北戦争直後のアメリカを震撼させた７人の銀行強盗団に起きる対立と裏切りを描くハードなウエスタン。北軍に妻子を殺された南軍元大佐グラフが率いる強盗団は手段を選ばぬ冷酷さで、全米中の銀行を襲い続けていた。しかし、団の一人ユースティスは、人質を簡単に殺してしまうグラフの非情さに反発する。ユースティスの気持ちは、グラフが重傷を負った仲間を見捨てようとした時に爆発、グラフに向かって発砲してしまう。崖から落ちたものの一命を取り留めたグラフは、ユースティスへの復讐のため強盗団を追うのだった。M・ロークが冷酷無比なグラフを演じる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京オリンピック", "東京オリンピック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 170, "1964年10月10日から24日まで東京で開催された東京オリンピックの全貌を収めた長編記録映画である。この撮影には103台のカメラを使用し、レンズは232本を数え、録音済テープは240時間にも及んだ。レンズは18ミリから世界で初めて使われた2000ミリまでの幅広いレンズを用意し、人間としての選手を捉えるという市川崑の演出意図に見事に応じている。実際この作品はまず東京の変貌を示すビルの改体工事を冒頭に据えるユニークさを示し、全編が人間性を探求するオリンピック映画となった。それゆえ、映画には無理解な方面から各種の批判があったが、100メートル競争のヘイズ、80メートル障害の依田、体操のチャスラフスカ、そしてマラソンのアベベなどを捉える映像の迫力は、かつてないオリンピック映画を創出した。なお、本作品はオリンピック映画史上初のスコープサイズ（テクニスコープ）の作品で、映画館はもちろん、全国の自治体・学校を隈なく巡回して、公式記録にはないが日本映画史上最も観客動員をした映画である。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武蔵野夫人", "武蔵野夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "当時ベストセラーとなった大岡昇平の同名小説の映画化。復員した青年・勉を引き取ることになった宮地家の妻・道子は、やがて勉を一人の男として意識し始める。勉の兄嫁の富子や嫉妬深い道子の夫などが入り乱れて、愛憎の人間模様が展開する……。複雑な心理描写を溝口の独特の演出力で撮り上げた名編。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EQUILIBRIUM", "リベリオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "感情を抑制させられた近未来を舞台に、体制に立ち向かう男の戦いを描いたSFアクション。銃と武術を融合した本作オリジナルの格闘術“ガン＝カタ”のスピード感あふれる描写が見もの。第三大次戦後の荒廃した近未来。人類は惨劇を繰り返さないため、感情抑制を義務づけられていた。ある日、感情犯罪取締官プレストンは抑制薬を飲み忘れたことから、美しい音楽に涙してしまう。それ以来、彼の胸に体制への反感が芽生え……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブリキの勲章", "ブリキの勲章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "東京都足立区の中学教師による実話の著書をもとに、教育問題一筋の中山節夫監督が熱っぽく映画化。一人の不良中学生を改心させようとする担任教師の奮闘ぶりが描かれる。決して甘ったれず、現実の厳しさに直面した姿勢で貫かれているのがいい。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRZEZINA", "白樺の林", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "日本では社会派監督の印象が強いワイダ。しかし彼は芸術色の濃い作品も多く手掛けており、これはその代表的な一編。ポーランド中央部の森林地帯。森林監視人として暮らす男と、胸を病んで帰ってきたその弟。肉体的な欲求から二人の間を揺れ動く、隣家の若い娘。この３人の関係のなかに、生きることと死ぬこと、そしてその間にある性への問いかけを織り込み、叙情性の強い内省的な作品に仕上げている。", "1970-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("意気に感ず", "意気に感ず", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "サラリーマンを描く源氏鶏太の同名小説を、斎藤武市が映像化した企業内幕もの。神保物産営業部員・英吉は、神保物産から独立した西田物産に引き抜きの誘いを受けていた。それを蹴って、彼は神保物産立て直しをはかるべく、４年前退職した東沢を連れ戻す。が、社内にも西田物産派がおり、様々な妨害が……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わたしたちの夏", "わたしたちの夏", "", "", "http://image.eiga.k-img.com/images/movie/56686/315x210.jpg?2036", 89, "アラフォー世代と20歳前後の世代の異なる2人の女性が交流する姿を描いたドラマ。主演は「汚れた女（マリア）」の吉野晶。東京郊外の自然雑貨店で働きながら写真を撮っているアラフォーの千景は、ある日、かつて一緒に暮らしていた男・庄平の娘サキと出会う。そこから庄平とも再会し、再び親しくなる2人だったが、大学生のサキには納得してもらえずにいた。その1年後、別々の道を歩んでいた千景とサキだったが……。", "2011-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HER FATAL WAYS", "彼女はシークレットエージェント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "中国公安局の女性捜査官チェンが、麻薬組織の一人を護送して香港にやって来る。接待役となったのは香港警察のウー。四川省から来たチェンは、都会を知らないばかりかガチガチのかたぶつ。カルチャー・ギャップにとまどうなか、犯人が脱走。ウーの心配を無視し、チェンはガムシャラに乱暴なやり方で犯人を追う。古くは「ニノチカ」「レッドブル」といった東西のカルチャー・ギャップを見せるタイプの映画だが、天安門事件後、中国政府公安局に対し風刺の刃を向けられるのは、やはり香港映画ならではだろうか。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最強兵器女子高生 RIKA", "最強兵器女子高生 RIKA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "人気グラビアアイドルの工藤里紗が主演を務めるゾンビアクション。2年前に家を出た祖父・隆平を訪ねるため、電車に乗りこんだ女子高生のリカ。しかし、隆平の暮らす街は無数のゾンビがうごめく地獄と化していた。ゾンビに襲われてしまったリカに、天才外科医の隆平はとある“手術”を施す。最強の戦士「RIKA」となった彼女は、ゾンビのボスである最凶ゾンビ・グロリアンを倒すべく立ちあがるが……。監督は「イズ・エー」の藤原健一。", "2008-02-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT SHIFT", "ラブINニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「バニシング IN TURBO」に続いて発表された俳優出身のR・ハワードの監督第２作。ニューヨークを舞台に死体置場で働く気弱で真面目な青年と、ヒモを殺され困っていた売春婦との恋を描いたコメディ。この後ハワードは「スプラッシュ」「コクーン」へと躍進。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("硫黄島", "硫黄島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "名優として知られる宇野重吉の監督作品。1956年に「あやに愛しき」で監督デビューした宇野としては５作目に当たる作品で、日米戦争の激戦地・硫黄島から生きて帰った男が、戦友を死なせたことで苦悩し、その罪悪感ゆえに再び硫黄島に渡って自らの命を絶つという、ミステリー仕立ての厳しい人間ドラマである。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の見える街", "風の見える街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "東京にありながら山深い五日市でホームヘルパーを始めた里美。ただの気まぐれで始めた仕事だったが、痴呆の進む老女イクや地元を愛する青年・孝志、その恋人で都会志向の早紀らと触れ合ううち、里美は目まぐるしい都会の流れで失っていた自分の時間を取り戻し始める……。現役の五日市町役場職員である監督の小林仁以下、撮影機材に触ったこともないスタッフたちによって作られた地域密着型映画。主演のつゆき由美を除き出演者も素人ばかりだが、何ともいえない温かさに満ちた愛すべき作品となっている。地元在住の映画監督、斎藤耕一が旅館のおやじ役で渋い演技を見せている。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遊☆戯☆王　デュエルモンスターズ　　劇場版「光のピラミッド」", "遊☆戯☆王　デュエルモンスターズ　　劇場版「光のピラミッド」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "2004年に全米で公開されて週間興行収入ランキング最高位４位を記録した大ヒット・アニメ。日本では未公開という非常に珍しいケース。今までのTVアニメやOVAには登場しなかった新カード、新モンスターが現れるオリジナル・ストーリー。美術館を訪れた主人公・遊戯は不思議なビジョンを見て、新たな戦いの渦中へ飛び込んでいく。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こまどり姉妹がやって来る　ヤァ！ヤァ！ヤァ！", "こまどり姉妹がやって来る　ヤァ！ヤァ！ヤァ！", "", "", "http://image.eiga.k-img.com/images/movie/54826/315x210.jpg?4388", 71, "昭和30年代に一躍スターとなり、71歳となった現在も精力的に日本各地を飛びまわっている双子デュオ「こまどり姉妹」の知られざる半生を辿るドキュメンタリー。生きるために歌い続けた幼少時代、絶頂期にステージ上で起きたファンによる凶行、時代の流れによる低迷期など、栄光の影に隠された真実が次々と明かされていく。メガホンを取るのは「歌謡曲だよ、人生は」の一遍「ラブユー東京」を手掛けた片岡英子。", "2009-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウィーン物語 ジェミニ・YとS", "ウィーン物語 ジェミニ・YとS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "芸術の都ウィーンを舞台に、国籍の違う双児の兄弟の謎をサスペンス・タッチで描き出した、一連の“たのきん”主演映画の第５作。田原俊彦が一人二役という難しい役どころに挑戦、助演にまわった近藤と野村がリラックスした演技をみせる。ウィーンの風景が美しい。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋する日曜日", "恋する日曜日", "", "", "http://image.eiga.k-img.com/images/movie/33576/315x210.jpg?2181", 89, "BS-iで2003年と2005年に放送された1話完結の恋愛ドラマシリーズを「ヴァイブレータ」「やわらかい生活」の廣木隆一監督が映画化。舞台は長野県松本。転校を間近に控えた晶は幼なじみの直への思いが募る毎日。だが、直が夢中になっているのは共通の友人である環。そんな中、晶の送別会が行なわれるが、晶の気持ちを知ってか知らずか、直は環を送別会に呼んでしまう……。脚本はTVドラマ「夜王」などのいずみ吉絋。", "2006-07-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HALF PAST DEAD", "奪還DAKKAN・アルカトラズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "数多くの映画で描かれてきたアルカトラズ刑務所を、ハイテク要塞に見立てたバトル・アクション。S・セガールが謎の囚人に扮し、武装グループとの大乱戦を演じる痛快作だ。銃弾を浴びて生死の境をさまよい、アルカトラズ刑務所に送り込まれたスゴ腕の車泥棒サーシャ。ある日、２億ドル相当の金塊を狙う傭兵集団が乱入。サーシャは囚人仲間たちと協力し、一味との激闘を繰り広げるが……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真剣勝負", "真剣勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "「飢餓海峡」「血槍富士」などで知られる巨匠・内田吐夢の遺作となった作品。代表作の「宮本武蔵・五部作」のあとに作られた、宮本武蔵・番外編という感じで、武蔵とくさりガマの名手、宍戸梅軒の死闘をたたみかけるようなスピードで描いた作品。もう以前のように大作を撮れなくなった内田吐夢が、低予算ながらもその力を発揮し、一般的なチャンバラ時代劇の枠には収まらないような特異な作品を作り上げ、高い評価を得た。チャンバラそのものよりも戦う男の気迫と哲学を描いた内容で、５部作に展開された武蔵の生きざまが凝縮されたような迫力。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Aliens of the Deep", "エイリアンズ・オブ・ザ・ディープ", "", "", "http://ecx.images-amazon.com/images/I/51PDRQ5HXEL._SL210_.jpg", 47, "「タイタニック」のジェームズ・キャメロン監督が、「ジェームズ・キャメロンのタイタニックの秘密」に続いて手がけた深海ドキュメンタリー。光も音も存在しない深度3000メートルの世界にスポットを当て、そこに広がる神秘的な光景や不思議な生きものたちの生態を、高度な技術で撮影された美しい映像で描きだす。まるで宇宙のような深海の謎を紐解いていくことは、地球外生命体の解明にまでつながっていく。", "2005-09-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("9+1 ナインプラスワン", "9+1 ナインプラスワン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "人気作家・新堂冬樹が製作総指揮を手がけ、「富江」シリーズなどホラー作品に定評のある及川中監督がメガホンを取ったサスペンス・ホラー。とあるペンションで行なわれたミュージカルのオーディションで、最有力候補だった女性が謎の失踪を遂げた。それから1年後、オーディションに参加した残り9人の女性たちに、差出人不明の手紙が届く。その手紙に導かれ、9人の女性たちは再びペンションに集まることになるが……。", "2008-08-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はさみ hasami", "はさみ hasami", "", "", "http://image.eiga.k-img.com/images/movie/55526/315x210.jpg?4800", 112, "「大阪ハムレット」（08）で脚光を浴びた光石冨士朗監督が、理容美容専門学校を舞台に描く青春ドラマ。美容専門学校の教員・久沙江が、自信が持てずに不登校ぎみの学生、複雑な家庭環境に悩み授業に身が入らない学生など、さまざまな事情を抱えた未来の美容師たちと奮闘し、夢を追いかける姿を描く。主人公の新人教員役に池脇千鶴、学生に扮するのは「春との旅」の徳永えり、「十三人の刺客」の窪田正孝ら。", "2012-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISS　CONGENIALITY", "デンジャラス・ビューティー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "男勝りでお色気ゼロのFBI捜査官グレイシーが、美人コンテストに出場するはめに。連続爆弾犯逮捕のための潜入捜査が目的なのだが、美容コンサルタントの厳しい指導を受けるうちに、いつしか彼女は素敵なレディーへと変身していく……。S・ブロックの魅力をフルに活かしたセクシー＆ゴージャスなアクション映画。オスカー俳優M・ケインほか脇役陣も充実。", "2001-06-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("九ちゃんのでっかい夢", "九ちゃんのでっかい夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "山田洋次が監督したドタバタ・コメディ。坂本九演じる喜劇役者が、誤診でガンの宣告を受ける。その彼に莫大な遺産が転がり込んだことから、彼の身辺が騒がしくなっていく。坂本九の役者としての才能は、その軽妙な持ち味からもっと評価されるべき。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEOREMA", "テオレマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1960～70年代に数々の古典文学の斬新な映像化で既成の権威に激しく挑戦したパゾリーニが、自らの原作をもとに発表した問題作。ミラノで大企業を営む有産階級の家族のもとに、ある日、謎の青年が来訪する。それまで平穏に暮らしていた家族は、おのおのが青年と接するうちに理性を失い、家庭の均衡が乱れはじめ、ついに一家は離散する。スタンプ扮するエキセントリックな美青年に、常識を超えた力を持つ超越者という神話的寓意を仮託。そして彼によって有産階級の人間関係が家庭の内部から崩壊していく過程を、一般の時間空間の通念を打破した映画構成で描いた。", "1968-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RIVER", "ザ・リバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "５世代続いている先祖伝来の農地を守るため、貧困にも雨による洪水にも耐えて闘う農民の姿を描いた力作。アメリカの農業が直面する問題を正面から取り上げ、「カントリー」や「プレイス・イン・ザ・ハート」とともに、現在の農業経営危機をダイレクトに伝えている。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("屍家重家／MORTUARY BLUES", "オカルト・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "2000年前に死んだ始皇帝のミイラが、不死身の悪霊として復活。その厄払いの儀式の最中に男が謎の死を遂げる。彼は宝の隠し場所を記した暗号文を持っていた。宝探しに出発した人間たちと、彼らが麻薬の運び屋だと勘違いしたドジな刑事が、悪霊と対決することになる。ジョン・カーペンター監督の「ゴースト・ハンターズ」の香港版ともいえるホラー・コメディ。ワイヤーワークによる空中チャンバラ・アクションがいかにも香港映画らしい。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SKIN GAME", "スキン・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "新興成金のホーンブローワ家の息子ロルフは、名家ヒルクリスト家の令嬢ジルを愛してしまう。ロルフの父は息子の望みを叶えるため、結婚を申し込みにヒルクリスト家を訪れるが断られる。実はヒルクリスト家では、ロルフの義理の姉の過去を知っていて、それをネタに土地を手に入れようとしていたのだ。文豪ゴールズワージーの戯曲をヒッチコックが映画化したサスペンス。", "1931-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CINQUE GIORNATE", "ビッグ・ファイブ・デイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ナポレオン没落後、革命の嵐が吹き荒れるミラノが舞台。スリとパン焼き職人の二人が体験する奇想天外な出来事をコミカルに描いた作品。", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROB　ROY", "ロブ・ロイ　ロマンに生きた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "18世紀スコットランドの実在の英雄ロブ・ロイの勇気ある生き様を描いた闘いのドラマ。貴族の陰謀に巻き込まれ、愛する家族の命までも危険にさらしてしまうロブ・ロイは、自らの名誉を回復するため、そして、家族の安全を守るために、冷酷な独裁者に反旗を翻す。広大なスコットランドの大自然を背景に、彼とその妻メアリーとの力強い愛の絆や、様々な人間模様が綴られていく。特殊効果は一切使用せず、ロケ地であるハイランド地方の美しさをダイレクトに映し出す映像も見逃せない。ビデオ発売時タイトルは「レジェンド・オブ・ヒーロー／ロブ・ロイ」。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("につつまれて", "につつまれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "「萌の朱雀」の女性監督、河瀬直美が８ミリで撮り上げたプライベート・ドキュメンタリー。一度も会ったことのない父親を探し出そうとする河瀬自身の心情を、カメラは淡々と写し出していく。父の所在が明らかになるにつれて深まる不安や、ようやく父と会話を交わした時の涙声など感情をさらけ出すかのような映像の連続が、痛切に観る者の胸に響く。山形国際ドキュメンタリー映画祭では、国際映画批評家連盟特別賞を受賞。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOGS", "ドッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "犬が人間を襲うという動物パニック映画の一編。南カリフォルニアのある町で犬が人間や牛を襲い始める。犬が狂暴になった原因をハーラン教授が調査するが……。身近な動物である犬を扱った狙いは良かったが、今一つ恐怖感に欠ける。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武闘派刑事２ HEART CRASH", "武闘派刑事２ HEART CRASH", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "警視庁人事課のコンピュータ入力ミスで、防犯課家事相談係という地味なセクションに異動になった犬宮のところに突然、従姉妹のカオルが助けを求めに来た。ガクラン姿の何者かに狙われている留学生を助けて欲しいという。一方、銀座警察管内では、脈絡のない殺人事件が発生し、カオルも人質にとられてしまう。実は一連の事件はバンダル王国の皇太子を狙うテロリストの仕業だった。高瀬将嗣監督、寺脇康文主演の「武闘派刑事」第２作。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アザーライフ", "アザーライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "まったく接点のなかった12人の男女が、運命に導かれるように1つの結末にたどりつくまでを描いた愛憎ミステリー。38歳の若さで大手商社の経営者を務める大杉航一郎は順調な人生を歩んでいるように見えたが、私生活では愛娘・遥を亡くし心身喪失状態にあった。ある日、航一郎は街で遥にそっくりな売春婦のマキと出会い、互いにひかれあうが……。監督・脚本を手がけるのは、現役サラリーマンという異色の肩書きを持つ新鋭・赤地義洋。", "2006-10-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD CITY", "マッド・シティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "ダスティン・ホフマン＆ジョン・トラヴォルタの演技派共演による社会派ドラマ。博物館をクビになった警備員サムの抗議行動が、彼の意志に反して人質ろう城事件に発展。偶然現場に居合わせた地方局のTV記者マックスは、ネットワークへの帰り咲きを狙い、犯人サムへの単独取材を敢行。世論も最初はサムに同情的だったが、騒ぎが大きくなるに連れてマックスもサムも立場が悪くなり……。興味本位なメディアの報道と、それに踊らされる世論を痛烈に風刺。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("橋のない川（一部・二部）", "橋のない川（一部・二部）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 267, "1961年に出版されて大きな反響を呼んだ住井すゑの長編小説の映画化。被差別部落に生きる人々が水平社を結成するまでの長く苦しい闘いを、２部構成で描く。重い主題を扱うにあたり、今井の演出はむしろ日常的な描写を重視している。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HUCKLEBERRY FINN", "ハックルベリーの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "トゥエインの同名傑作小説をもとに、シャーマン兄弟が脚本と音楽を担当。19世紀末、広大なミシシッピー河流域を舞台に、ハックルベリー少年と、様々な人々との交流が描かれていく。ミュージカル仕立てが、原作のファンタスティックな内容にピタリとマッチした佳品。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2010-2011 ワーグナー「ニーベルングの指環」　序夜「ラインの黄金」", "METライブビューイング2010-2011 ワーグナー「ニーベルングの指環」　序夜「ラインの黄金」", "", "", "http://image.eiga.k-img.com/images/movie/56466/315x210.jpg?1805", 183, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を劇場のスクリーンで上映する「METライブビューイング」2010～11年シーズンo1作目。ワーグナーの全4部からなる大作オペラ「ニーベルングの指環」の第1部。神々の長ボータンは居城バルハル建設の報酬のことで巨人兄弟ともめ、火の神ローゲの知恵で地底のアルベリヒの元へ宝を求めに行く。そこで無限の権力が得られるという「ラインの黄金」から作られた指環の存在を知ったボーゲンは、アルベリヒから指環を奪うが……。", "2010-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MALICE", "冷たい月を抱く女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "男女３人の葛藤に殺人事件の謎を絡めたサスペンス・スリラー。生徒の連続殺人に頭を痛める大学の学長補佐アンディの前に現れた、旧友の外科医ジェッド。アンディの妻トレイシーの手術の際に彼が医療ミスを犯したことから、夫妻の間に深い溝が生じる。だが、医療の舞台裏には思いもよらぬ陰謀が……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL　MULINO　DELLA　DONNA　DI　PIETRA", "生血を吸う女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "カール・ドライヤー監督のクラシック作「吸血鬼」をトリビュートした、ホラー映画の名作。ホラー・ジャンルで傑作を残したフェローニ監督の真骨頂。20世紀初めのオランダが舞台。血を入れ替えないと死んでしまうという美女のために、若い学者が取った手段とは？　女を拉致しては生血を入れ替え、死体はロウ人形に埋め込み人形館に展示していた……。", "1961-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アリーナロマンス", "アリーナロマンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "アイドルオタクの男子高校生と、アイドルを目指す同級生の美少女が繰り広げる青春ストーリー。アイドル歌手の追っかけをしているミツルは、歌番組の観覧会場でクラスメイトの舞華を見つける。実は舞華も男性アイドルの熱烈な追っかけをしており、自らも歌手になることを夢見ていた。意気投合した2人は、お互いの夢を励ましあうように。いつしか舞華に淡い恋心を抱きはじめたミツルは、彼女をアイドルにしようと決意するが……。", "2007-10-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナナとカオル", "ナナとカオル", "", "", "http://image.eiga.k-img.com/images/movie/55936/315x210.jpg?2192", 80, "高校生のSMという衝撃的な内容が話題を呼んだ甘詰留太によるコミックを実写映画化。エッチなことばかりもう想してしまう冴えない童貞高校生カオルは、隣に住む美形でグラマラスな優等生ナナにボンデージの衣装を着てもらうことを夢想していた。そんなカオルの前に、なぜか突然ボンデージ姿のナナが現れる。主演は「龍馬伝」「仮面ライダー響鬼」で注目を集めた栩原楽人と、本作が映画初出演となるグラビアアイドルの永瀬麻帆。", "2011-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beautiful Sunday", "ビューティフル・サンデー", "", "", "http://image.eiga.k-img.com/images/movie/54116/315x210.jpg?3351", 117, "「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。「甘く、殺伐とした恋人」のパク・ヨンウ、「卑劣な街」のナムグン・ミンが共演し、悪の道に進んだ2人の男の葛藤を描く犯罪スリラー。麻薬密売組職を検挙した刑事のカンは、植物状態の妻の入院費用を工面するため、押収した麻薬を対立組織に売り、出所したボスのチョ・サンテの復讐を受ける。一方、純情で内気な青年ミムは初恋の女性スヨンと結婚するが、スヨンは妊娠中にミムの秘密を知り家を飛び出してしまい……。", "2008-10-11", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("稀人", "稀人", "", "", "http://ecx.images-amazon.com/images/I/21QBBAS84PL._SL210_.jpg", 92, "日本映画界の第一線で活躍する監督に新人監督たちが挑む「ホラー番長」シリーズの1作として、「呪怨」の清水崇監督がメガホンを取った異色ホラー。男が自らの眼をナイフで貫いて自殺する現場を偶然にも撮影した映像カメラマンの増岡は、死の直前、男がある一点を凝視して怯えていたことに興味を持つ。事件現場にあった奇妙な通路から地下に広がる異界へと迷い込んでいった増岡は、そこで鎖につながれた1人の少女を発見し……。", "2004-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃える秋", "燃える秋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "父娘ほども年齢の差がある画廊経営者と、愛人関係にある27歳のグラフィック・デザイナーが、その呪縛から離れて、旅立っていくまでを描いた“女の自立”ドラマ。デパートによる映画製作進出が話題になった頃の作品で、本作は三越の製作。イラン・ロケが美しい。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REGARDING HENRY", "心の旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ニューヨークを代表するほどの有能な弁護士が、ある日、一発の銃弾を浴びてすべての記憶を失う。妻さえもわからぬ彼に、周囲の人々は温かい手をさしのべ、やがて彼は徐々に記憶を取り戻す。記憶がよみがえった時、彼は生まれて初めて、愛のぬくもりを知るのだった。愛を求めてさまよう現代人の心を鮮やかに描いたヒューマン・ドラマ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歓喜の歌", "歓喜の歌", "", "", "http://image.eiga.k-img.com/images/movie/53156/315x210.jpg?1987", 112, "落語家・立川志の輔の同名新作落語を映画化した人情喜劇。小さな地方都市、みたま町の文化会館に勤める飯塚主任（小林薫）が、大晦日に開催されるママさんコーラスグループのコンサートをダブルブッキングしてしまったことにより起こる騒動をユーモラスに描く。監督は「東京タワー／オカンとボクと、時々、オトン」の松岡錠司。共演は安田成美、伊藤淳史、由紀さおり、浅田美代子ら。", "2008-02-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST PAGE", "密告", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ふとした出来心から、人生を狂わせていく男の、追い詰められていく様を描いた心理サスペンス。格式ある書店に勤めるジョン・ハーマンは、体の不自由な妻の手術のため、保険証書を現金化する。それほどまでに、妻を愛しているジョンだったが、ふとしたはずみで従業員のルビーと過ちを犯してしまう。ルビーは、恋人ジェフにそそのかされて、ジョンを脅迫してくる。やがてジョンは寝たきりの妻が急死したことを知るのだが……。", "1952-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白鷺", "白鷺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「湯島の白梅」同様、泉鏡花原作の悲恋物語を衣笠貞之助が監督した作品。明治の末の浜町河岸を舞台に、料亭“辰巳屋”の一人娘・お篠を主人公として、白鷺のようにはかなく、白鷺のようにしとやかな一人の女性の悲しい恋を描く。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RHINESTONE", "クラブ・ラインストーン　今夜は最高！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ニューヨークのナイト・クラブのスター、ジェイクは、店のオーナーに新しいスターを育てればフリーにしてやると持ちかけられる。たまたま出会ったタクシー・ドライバーに白羽の矢を立てた彼女は、全身全霊を傾けて特訓に没頭するが……。S・スタローンが脚本にも参加した陽気な音楽コメディ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("壊したいおもちゃ", "壊したいおもちゃ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 29, "", "2007-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大病人", "大病人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "伊丹十三が、死というテーマを見つめた意欲作。映画監督兼俳優の向井は、新作の撮影中に倒れて入院。彼の体はもって１年というガンに冒されていた。そうとは知らぬ向井は、不倫相手の女優と病室で情事を楽しんだり、看護婦をナンパしたりと、妻の万里子や担当医の緒方を困らせる。やがて病状を知った向井は、ショックから自殺を図るが……。これまでの伊丹作品のマニュアル的な要素は稀薄で、彼流のユーモアはそのままに、嫌味のない娯楽作として見事に完結している。主人公が前向きに死を迎えるラストは感動的。SFXを駆使した死後の世界の幻想的な描写も印象深い。DVDは「伊丹十三　DVDコレクション“ガンバルみんな”BOX」に収録。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La caduta degli dei　The Damned（英語）", "地獄に堕ちた勇者ども", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "　ルキノ・ビスコンティ、ドイツ3部作の第1作。鉄鋼会社を手中に収めようと、血で血を洗う一族の争いと、ナチスを背景にした権力闘争が複雑に絡み合う。1933年、ドイツで鉄鋼会社を経営する男爵の誕生日パーティが開かれる。宴の後、男爵が死体で発見され、一族の1人が逃亡。経営者の死後、孫のマルティンに株が譲渡されるが、実権を握ったのは母親とその愛人だった。ヘルムート・バーガー演じるマルティンの倒錯的なキャラクターが、一族の悲壮感をあおる。", "1970-04-11", "4,26,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拳撃／DUEL OF FIST", "フィスト・バトル 拳撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "武侠映画を得意とするチャン・チェ監督には珍しい現代劇。死の間際の父から、異母兄弟がタイにいることを知らされた青年。香港からタイへと向かった彼は、そこでムエタイの選手と出会い友情を結ぶ。実は、このムエタイ・ファイターこそ青年の兄弟だったが、彼は八百長試合を強いられ苦境に立たされていた……。ムエタイのアクションに加えて、1970年代のサイケなファッションも見もの。", "1971-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Life ライフ", "Life ライフ", "", "", "http://ecx.images-amazon.com/images/I/41CmgM%2BpvRL._SL210_.jpg", 93, "ぴあフィルムフェスティバルで注目を集めた新鋭・佐々木紳監督の劇場長編デビュー作。地方でキャンドルアーティストとして暮らす青年・勇の家の留守番電話に、ある日、聞き覚えのない女性の声で、一方的に待ちあわせの場所と日時を告げるメッセージが残されていた。その日は、勇の高校の同窓会が行われる日。待ちあわせ場所へと向かった勇は、電話の声の主である少女・茜と出会う。テレビドラマ「イヌゴエ」の綾野剛が主演を務めるほか、音楽も担当。", "2007-01-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不良番長・出たとこ勝負", "不良番長・出たとこ勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "暴力団・大滝組の圧迫からカポネ団は住みなれた新宿を離れるハメになり、磐梯熱海に大量スケコマシ作戦を展開する。折しも、大滝組は土地の金融業者・黒柳と結託し、一大観光チェーン建設に野望を燃やしていた。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHO IS KILLING THE GREAT CHEFS OF EUROPE？", "料理長〈シェフ〉殿、ご用心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "料理雑誌の出版社を経営するマックスは、大の美食家として知られたが、医師から減食を命じられる。彼は自らの選んだベスト４シェフを集めて王室晩餐会を開いたが、その夜以来、ベスト４シェフが一人また一人と殺されていくのだった……。サスペンス色よりコメディ色の濃厚な作品。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛を乞うひと", "愛を乞うひと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "モントリオール映画祭で国際批評家連盟賞を受賞。娘を折檻する母親と、そんな母親を愛した娘の二役に、原田美枝子が迫真の演技で挑んだ感動作。高校生の娘と二人暮らしの照恵が、幼い頃に亡くなった父の遺骨を探し始める。その旅の中で、彼女の脳裏に蘇る少女時代のつらい思い出の数々。照恵はホステスの母に、来る日も来る日も殴る、蹴るの虐待を受けていた。母はなぜ冷たく当たったのか……。やがて母と父の出会いのいきさつを知った彼女は、母を一人の人間として受け入れ、自分自身の複雑な思いと向き合う決心をする。母との再会シーンは壮絶。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOR　ZUM　HIMMEL", "ゲート・トゥ・ヘヴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "国際空港の無国籍な雰囲気を生かした、ファンタスティックな恋愛劇。フランクフルト空港で清掃係をしているインド人女性と、不法入国を図るロシア人青年。スチュワーデスに憧れる前者と、パイロット志望の後者の間に芽生えたロマンスを温かく描く。「ツバル」の俊英V・ヘルマーが演出を手掛け、メルヘン調の語り口でアウトサイダー同士の恋を紡ぎ出す。", "2003-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love Letter", "Love Letter", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「Undo」「花とアリス」の岩井俊二監督の長編第1作。事故で婚約者の樹を亡くした博子は、国道になってしまったという彼が昔住んでいた住所に届くはずのない手紙を出した。しかしその手紙は、婚約者と同姓同名の女性のもとへ届き、2人の不思議な文通が始まるのだった。中山美穂が博子と手紙を受け取る女性、樹の2役を演じ話題となった。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEE NO EVIL，HEAR NO EVIL", "見ざる聞かざる目撃者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "耳の聞こえない男にG・ワイルダー、目の見えない男にR・プライヤーが扮し、二人が殺人現場に遭遇したために巻き込まれる騒動を描いたコメディ映画。監督のA・ヒラーと主演の二人は、かつて「大陸横断超特急」でもトリオを組んでいるだけあって、息の合った笑いが連発される。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOUNG GUNS", "ヤングガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "アメリカ西部開拓史上でも名高いビリー・ザ・キッドと５人の仲間たち。彼らがまだ無名のチンピラだったころからの青春時代を忠実に描いたウエスタン。当時注目の若手スターたちが総結集するという豪華キャスティングで、全米公開時、一週目で興行成績一位となった。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　おかっぱちゃん旅に出る", "劇場版　おかっぱちゃん旅に出る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "小学館文庫から出版されているイラストエッセイを、原作者のBoojil自らが主演を務めて映画化。都内で暮らす21歳のイラストレーターが、自堕落な生活に終止符を打つべくひとり旅に出ることを決意する。髪型を変えて「おかっぱちゃん」となり、バックパックとスケッチブックを手にタイへと向かい、旅先で出会ったさまざまな人々との交流を通して本当の自分を見出していく。", "2010-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RASPUTIN， THE MAD MONK", "白夜の陰獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "帝政ロシア時代に生きた怪僧ラスプーチンの物語を描いた一編。帝政ロシアの片田舎で、ラスプーチンと名乗る怪僧が不思議な力で瀕死の女を助けた。だがその神通力が邪道として教会から破門される。そして首都ペテルブルクに乗り込んだ彼は、宮廷に出入りし、皇后の寵愛を得るようになる……。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LADIES’　MEN", "底抜けもててもてて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「底抜けてんやわんや」に続く、喜劇王J・ルイスの監督第２作。大学の卒業式の日、恋人にフラレたハービーは女難を忘れて仕事に打ち込もうとするが、うまくいかない。転職を重ねた末に学生寮のボーイとなるが、そこは男に飢えた女学生でいっぱい。逃げようとするハービーは寮長に引き止められ、やむなくそこに居座ることになり……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGEL　BABY", "エンジェル・ベイビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "オーストラリアのアカデミー賞に輝いた、鮮烈なラブ・ストーリー。精神を病んだ男女の不安定な恋の行方を描く。精神科の集団セラピーで知り合ったハリーとケイト。恋に落ちた彼らはともに暮らし始める。やがてケイトの妊娠を機に、二人は薬物治療を拒絶してクリーンな体を目指すが、ケイトは次第に精神のバランスを崩し……。ピュアで壊れやすい、愛の絆が哀切な後味を残す。", "1995-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHICAGO JOE AND THE SHOW GIRL", "ハマースミスの6日間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "実際に起こった事件をもとにした人間ドラマ。1944年、第二次大戦中のロンドン。しがない軍人のリッキーは、売れないショーガールのジョージナと出会う。男は女にハリウッドの女優を、女は男にシカゴのギャングをダブらせる。本当の自分を隠した二人は、犯罪ゲームに溺れるが……。前半の夢物語と後半の愛憎劇。ガラリと変わる雰囲気が面白い。", "1990-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE＾N BIE＾N PHU", "愛と戦火の大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "1954年３月13日、インドシナ。フランス軍の重要拠点ディエン・ビエン・フーで、フランスからの独立を目指すベトナム軍の総攻撃が開始される。ハノイではアメリカ人ジャーナリスト、ハワードが兵士たちと親交を温めていたが、そんな雰囲気もベトナム軍の攻撃により、一変するのだった……。実際に従軍し捕虜になった経験を持つP・シェーンドルフェル監督が、インドシナ戦争の実態を浮き彫りにした問題作。ビデオ発売時タイトルは「スカイミッション　空挺要塞DC3」。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COCOON", "コクーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "かつてメキシコ湾の海底に隠した秘密のカプセルを取り戻すため、地球にやって来た地球外生物と、そのカプセルを偶然引き揚げてしまった養老院の老人たちとの、心温まる愛の交流を描いた一編。カプセルの力で若返った老人たちの姿が、何とも素敵でユーモラスだ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Simone", "シモーヌ", "", "", "http://image.eiga.k-img.com/images/movie/1656/315x210.jpg?3611", 117, "「ガタカ」の脚本＆監督、「トゥルーマン・ショー」の脚本も書いたアンドリュー・ニコルが描くSFハリウッド舞台裏コメディ。落ち目の監督タランスキーは、ワガママ女優に苦労させられてうんざり。CG製のバーチャル女優シモーヌ主演で映画を作るが、彼女は大人気になり、こんどは彼女の正体を隠すために大苦戦。しかし、シモーヌがアカデミー賞にノミネートされてしまい……。シモーヌ役はモデル出身のレイチェル・ロバーツ。", "2003-09-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超新星 LIVE MOVIE in 3D “CHOSHINSEI SHOW 2010”", "超新星 LIVE MOVIE in 3D “CHOSHINSEI SHOW 2010”", "", "", "http://image.eiga.k-img.com/images/movie/56026/315x210.jpg?2125", 0, "2007年に韓国デビュー、09年に日本へ進出した韓国の6人組男性アイドルグループ「超新星」が、2010年12月21日に横浜アリーナで行ったライブ「CHOSHINSEI SHOW 2010」の様子を3D映像で劇場公開。全員が身長180センチ以上というモデル並のスタイルと甘いルックスで人気のメンバーが、貴重な素顔を見せるオフショット映像も交える。", "2011-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あなたへ", "あなたへ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "高倉健が「単騎、千里を走る。」（2006）以来の映画出演を果たした人間ドラマ。監督は、高倉とともに「夜叉」（1985）、「あ・うん」（89）、「鉄道員」（99）などを生み出してきた降旗康男。北陸にある刑務所の指導技官・倉島英二は、最愛の妻を53歳で亡くし、「故郷の海に散骨してほしい」と記された絵手紙を受け取る。生前には口にしなかった妻の真意を知るため、英二は自家製キャンピングカーで妻の故郷・長崎へと向かう。「夜叉」「あ・うん」のプロデューサーで08年に死去した市古聖智が遺した原案を、降旗監督と脚本家の青島武が再構築したオリジナルストーリー。高倉とは「夜叉」以来27年ぶりの共演となるビートたけしのほか、田中裕子、佐藤浩市、草なぎ剛、綾瀬はるか、余貴美子ら、ベテランから若手まで日本映画界を代表する顔ぶれがそろう。", "2012-08-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　SIRE｀NE　ROUGE／RED　SIREN", "レッド・サイレン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "リュック・べッソンに認められたオリヴィエ・メガトン監督が、７年の歳月をかけて映画化した斬新な映像で描き出すサスペンス・アクション。実の母親の殺人に気付いた少女が、トラウマを抱える元傭兵の力を借り、連れ戻そうとする母親からの逃避行を決行する。母親一味の差し向けたマフィアとの暗闇のモーテル内での銃撃戦は、息を飲むほどの緊張感。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUMPIN’ JACK FLASH", "ジャンピン・ジャック・フラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「カラーパープル」で名演を見せたゴールドバーグが、今度は口八丁手八丁でCIAやKGBを向こうにまわし、東欧圏に捕らわれた英国情報員救出に大車輪の活躍を見せる。タイトルはもちろん、ローリング・ストーンズの大ヒット曲からの引用で、イギリス情報員の暗号名として登場。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROUNDERS", "ラウンダーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "カード・ゲームを題材にしたスリリングでクールな青春劇。ポーカーの得意な法学生マイクは全財産を賭けた勝負に敗れ、勉学に専念していた。しかし悪友との再会によって、彼は勝負の世界に引き戻され、やがてポーカーに自分の進むべき道を見いだしていく。主人公の成長劇に加えて、彼を囲む様々なギャンブラーたちの人間ドラマにも味がある。主演のM・デイモンはもちろん、脇を固める演技派陣の好演も見どころ。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霍元甲", "SPIRIT", "", "", "http://image.eiga.k-img.com/images/movie/1666/315x210.jpg?4298", 103, "19世紀末から20世紀にかけて活躍した実在の中国武闘家、フォ・ユァンジアを描く格闘映画。優れた武術家を父に持つ少年フォ・ユァンジアは、父に拒まれても鍛錬を積み、やがて地元一の格闘家となる。だが、弟子の仇を討とうとして相手を絞め殺してしまい、家族が報復を受けてから、強さとはなにかについて悩むようになる。主演はこの役を熱望したジェット・リー。監督は「フレディVSジェイソン」などハリウッドで活躍するロニー・ユー。", "2006-03-18", "6,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPLENDOR", "スプレンドール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "映画の黄金期を生きたイタリアの小さな町の映画館が、テレビやビデオの台頭で閉館の危機に。映画一筋の人生を歩む館主のジョルダン、その彼と恋に落ち、劇場の案内嬢になったシャンダル、映画館に通ううちついには映写技師になってしまった青年ルイジ。３人はありったけの努力で映画館を守ろうとするが……。「メトロポリス」や「アメリカの夜」など、劇中に登場する作品も見逃せない、映画を愛する人々の、映画を愛する人々に捧げる佳作。", "1989-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("USED CARS", "ユーズド・カー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "アリゾナ州フェニックス市の郊外にある二軒の中古車販売会社を舞台に、そのすさまじい競争ぶりと、一人のモーレツ社員の法律や常識を無視した破天荒な行動と恋を描くハチャメチャ・コメディ。250台をこえる中古自動車軍団が暴走するシーンが見せ場。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIDAY THE １３TH PART V：A NEW BEGINNING", "新13日の金曜日", "", "", "http://ecx.images-amazon.com/images/I/51091ls2nLL._SL210_.jpg", 92, "“13金”シリーズ第５作。前作で殺人鬼ジェイソンを倒したトミー少年が成長。６年後18歳になったが、その時のショックから立ち直れず、社会復帰のため施設に送られる。が、またしても惨劇に巻き込まれてしまう。いったい誰の仕業か……。", "1985-11-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOUR WEDDINGS AND A FUNERAL", "フォー・ウェディング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "４度にわたる知り合いの結婚式と、一度の葬式の度に出会っては、恋心を募らせる英国人男性と米国人女性。彼らの迷走する恋模様をユーモラスな会話を交えて描く。H・グラントが自分の気持ちを素直に表せない主人公を好演。全米では英国映画としては異例のナンバーワン・ヒットを記録した。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DNA", "D.N.A.II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「ブラック・ダイヤモンド」のアクション・スター、M・ダカスコス主演のSFアクション。禁断の魔境で繰り広げられる、DNA合成によって産み出された未知の生物と人間たちの死闘をスリリングに活写。なお、J・フランケンハイマー監督作「D.N.A.ドクター・モローの島」とは関連はない。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLIRTING", "ニコール・キッドマンの恋愛天国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "舞台は1965年、オーストラリアの田舎町。湖を挟んで建つ全寮制の男子校と女子校。男子校の生徒ダニーは内気で友人もなく、変人扱いされていた。ある日彼は、女子校の生徒タンデイウェと知り合う。アフリカ系である彼女もまた、肌の色のせいで仲間からのけ者にされていた。N・キッドマンの出世作。", "1990-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INVADERS FROM MARS", "惑星アドベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ある夜、奇妙な音で目が覚めたデビッドが窓の外を見ると円盤がすぐそばに着陸していた。調べに行って帰ってきた父は人が変わったようだった。相談しに行った警察署長も様子がおかしい。デビッドは恐怖にかられていく……。大胆なテクニカラーが美しいSF傑作。1986年に「スペースインベーダー」のタイトルでリメイクされた。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROTHER", "BROTHER", "", "", "http://image.eiga.k-img.com/images/movie/1026/315x210.jpg?4802", 114, "ヤクザの抗争の最中、組を追われ、ロサンゼルスに留学している弟・ケンを頼って渡米した山本。ケンは、黒人デニーたちとヤクの売人をしていた。そんな彼らが起こしたひとつの事件が、縄張り抗争に発展。ヒスパニックのチンピラからチャイニーズ、イタリアン・マフィアをも巻き込んだ、絶体絶命の戦争が始まった……。", "2001-01-27", "76,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("多摩川少女戦争", "多摩川少女戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "近未来、荒れ果てた多摩川沿いの街。川をはさんで激しく対立する二つの不良少女チームの抗争がついに全面対決の時を迎えていた。そんな折、親友の復讐のために３年ぶりに街に舞い戻ったナオと、義理の父親を撃ったケロッピの出現が、少女たちの運命を急展開させていく。次世代アイドル候補が多数出演した美少女版「極道の妻たち」の趣。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPACEWAYS", "人間ロケット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "恐怖映画の老舗ともいうべきハマー・プロは、ホラー色をその内容に取り入れながらも、SFやコメディ、人間ドラマなど様々な作品を作っていった。本作も、宇宙時代の黎明期である1953年の映画でありながら、ロケットから見た地球のカメラワークなど、迫力ある仕上がりになっている。科学者スティーブは、妻と研究所員クレンショーが抱擁している現場を目撃。その後二人は消息を絶った。スミス博士は、スティーブが二人を殺してロケットに乗せたのではと疑う。スティーブは、自らの潔白と打ち上げの成功という使命を負ってロケットに乗り込む。", "1953-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIDNIGHT STING", "ミッドナイト・スティング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "悪事で財を成した田舎町の実力者ギデオンと、彼をハメるために周到な罠を仕掛ける詐欺師のケイン。賭けボクシングで対決する、切れ者同士の壮絶なだまし合いを描き出す。二転三転するスリリングな展開と、迫力のボクシング・シーンが見もの。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COMPANY OF STRANGERS", "森の中の淑女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "バスが突然故障したため、森の中の廃屋で過ごさねばならなくなった７人の老女とバスの運転手。すべて女性ばかり、しかも格別親しいわけでもないこの奇妙な一行の姿を、ほとんど即興的演出で描いた一編。それぞれの老女たち（全員演技経験なし）が抱える人生の辛苦や過去の思い出、あるいは未来への憂いと希望が、降って湧いたような人生の休暇のなか、会話と身振りによってじわじわとにじみ出る。ラスト、奇妙な状況下で熟成された彼女たちの友情を、さらりと見せたC・スコット監督の演出も冴えている。1990年のマンハイム国際映画祭にてグランプリを受賞。", "1990-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE VAGRANT", "バーグラント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "将来有望な若きエリート青年が、夢のマイホームを手に入れた。だが引っ越したその日から奇妙な出来事が連続し、住所不定の怪人“バーグラント”が家の周りをうろつき始める……。監督のC・ウェイラスは、特撮などでオスカーの受賞経験もあり、サスペンスフルな演出やSFXはお手のもの。その技術が随所に表われている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHURCH", "デモンズ３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "前２作のランベルト・バーヴァ監督から、アルジェントのもう一人の愛弟子M・ソアヴィに演出をバトンタッチしてのシリーズ第３作。教会の中に封印されていた悪魔が約900年ぶりによみがえり、聖堂に閉じ込められた人々を次々と餌食にしていく。ムードある美的恐怖演出が見ものだ。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イタズ", "イタズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "マタギの掟で禁じられている子連れ熊を撃ってしまった銀蔵は、罪ほろぼしもあってその子熊ゴン太を自分で育てることにする。しかし、成長して大人になったゴン太とは、いつか闘わねばならぬ運命にあった……。丹念な自然描写で綴った動物映画。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦雲アジアの女王～ 悲運のラストクイーン", "戦雲アジアの女王～ 悲運のラストクイーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "棋本捨三原作の『或る女の終幕』を新人・高倉みゆき主演で映画化したスペクタクル映画。清朝の王族・粛親王の第14王女として生まれた川島芳子の波乱の生涯が描かれる。“東洋のマタ・ハリ”と呼ばれた男装の麗人・川島芳子を高倉みゆきが演じる。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HEAD HUNTER", "真夜中のヘッドハンター 殺人の報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ベトナム戦争からの帰還兵アディは、その経験と手腕を買われて、ある巨大秘密組織のボス、トングのボディガードに雇われた。そんな彼がある日、トングの姪ビッキーと恋に落ち……。秘密組織を背景に様々な人間模様が交錯するサスペンス・アクション。", "1982-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE JUNKMAN", "ジャンクマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ロサンゼルスでも有名な自動車解体業者、H・B・ハリッキーのワンマン映画第２作。カー・アクション映画を撮り終えた“ジャンクマン”が、何者かに命を狙われる。200台近い高級車を破壊したという、派手なカー・アクションが売りもの。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いちばん美しい夏", "いちばん美しい夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "今が楽しければいい、という典型的“ギャル”の高校生・直美は、両親の別居がもとで夏休みを田舎の親戚の家で過ごすことに。最初は田舎暮らしに嫌気がさしていた直美だが、ある日、軽い痴呆を患う老婦人・小出さんに出会い、互いに秘密や夢を分かち合うようになる。ひと夏の経験を通して成長していく少女の姿を押し付けがましくなく描いたハート・ウォーミングな人間ドラマ。ベテラン南美江が品のある凜としたたたずまいで小出さんを演じ、年輪を重ねた日本女性の美しさを見せてくれる。上智大学などで教鞭をとるイギリス人監督のJ・ウィリアムズの35ミリ劇場デビュー作。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHINOBI", "SHINOBI", "", "", "http://image.eiga.k-img.com/images/movie/1646/315x210.jpg?4004", 101, "「弟切草」の下山天監督が、山田風太郎の「甲賀忍法帖」を原作に描く忍者アクション。1614年、徳川家康からの指令により、忍者の2大勢力である甲賀忍者と伊賀忍者のうち片方のみを存続させることが決定。その存続を掛けて代表者各5人が対決することになる。一方、伊賀の朧と甲賀の弦之介は互いの素性を知らずに出会って恋に落ちるが、2人はこの5人のうちの1人に選ばれていた。主演はオダギリジョー、仲間由紀恵。", "2005-09-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オールナイトロング", "オールナイトロング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "東京の京浜地区、羽田。ここに３人の平凡な10代の少年たちがいる。旅客機の整備士を目指すシンジ、ガリ勉のテツヤ、お坊ちゃまのケンスケ。それなりの人生を歩むかに見えた彼らは、ある通り魔殺人を目撃したことから小さな運命共同体となる。そしてシンジの女友達が不良たちに暴行され、植物人間になる悲劇が。彼らはにわかに精神の均衡を失い、猟銃を盗み出して血の報復を開始するのだが……。記録映画出身の29歳の新鋭監督、松村克弥が、社会問題化する“ハト型暴力”を描いた劇場映画デビュー作。残酷描写で映倫R指定を受けた異色の青春編だが、話題性とは裏腹に作りがきわめてオーソドックスな点が評価の分かれるところ。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狂走情死考", "狂走情死考", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "若松孝二が「金瓶梅」に続いて監督した作品。新宿の騒乱罪で東京を追われた全学連の学生が女とともに北海道に逃れてくる。全編雪のシーンで、新潟を皮切りに東北・北海道とオールロケを敢行。北海道の支笏湖の零下10度の厳寒の中を主演女優が素っ裸で走りまわるシーンが話題になった。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雨が好き", "雨が好き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "劇団に所属する若い女優が、中年の男と知り合って愛し合い、その男に裏切られて小さな仕返しをするまでの姿を淡々と描いたシティ・ロマン。1981年度中央公論新人賞受賞の原作を、作者である高橋洋子がふくらませて脚色・監督・主演を兼ねている。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジェニファ／涙石の恋", "ジェニファ／涙石の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「MISTY」「あさきゆみめし」に次ぐ、三枝健起の監督第3作。原案者でもあるアメリカ人モデル・ジェニファー・ホームズと、人気俳優・山田孝之が、悲しみを抱えた少年少女のピュアな恋を繊細な雰囲気で好演。幻想的な美しい映像も印象的な切ないラブ・ストーリーだ。心を閉ざした少年、隆志が寄宿する長野県の由緒正しき寺にある日、外国人少女ジェニファがホームステイでやって来る。陽気に振る舞いながらも心に悲しみを抱えたジェニファと、暗い過去を持つ隆志は、次第に惹かれ合うが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHAT DID YOU DO IN THE WAR，DADDY？", "地上最大の脱出作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "「ピンク・パンサー」シリーズのB・エドワーズ監督によるハチャメチャな戦争コメディの傑作。J・コバーンのいつにないひょうひょうとした好演も見どころ。第二次大戦末期、イタリアのパレルモ。激しい戦いが繰り広げられたこの地で、戦史にも載らぬとんでもない戦いがあった。アメリカ軍、イタリア軍、そしてナチス・ドイツも入り乱れての大混戦。ワインが飛び交い、スカートが舞い踊る大騒ぎが展開する……。エドワーズ監督らしいお色気シーン、そして戦争を徹底的に笑いのめしたストーリーと、彼のコメディ・センスが遺憾なく発揮されている。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RIGHT HAND MAN", "ライトハンド・マン 禁じられた抱擁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "19世紀末のオーストラリア。21歳の誕生日に右腕を失った名家の若き領主ハリーは、今また新たな悲運を宣告される。遺伝病によって余命いくばくもないと知った彼は、友情で結ばれた馬丁のネッドに恋人セーラと契り、世継ぎを授けてほしいと頼むのだが……。美男俳優R・エヴェレットの魅力いっぱいの好編。", "1986-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トワイライトシンドローム　デッドゴーランド", "トワイライトシンドローム　デッドゴーランド", "", "", "http://image.eiga.k-img.com/images/movie/53756/315x210.jpg?3346", 70, "人気ゲームをモチーフに、死のゲームに巻き込まれた若者たちの恐怖を描くアクション・ホラー。ホラーゲーム\"トワイライトシンドローム\"のイベントに参加するため遊園地に集まった7人の若者たち。彼らは進行役のピエロに案内されるままに、知力や体力が試される体験型ゲームを開始する。しかし、ゲームオーバーになった者には罰ゲームとして\"死\"が待ち受けており……。「独立少女愚連隊」の安里麻里がメガホンを取る。", "2008-08-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES CHEMINS DE KATMANDOU", "カトマンズの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "学生運動に挫折したオリヴィエは、母と別れた父から慰謝料を受け取るため、カトマンズに向かった。旅の途中で知り合ったヒッピーの娘ジェーンと愛し合うようになるが、麻薬と金銭をめぐる悲劇が二人を待っていた。バーキンと、当時の夫ゲンズブールが共演している。", "1969-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅の群れ", "修羅の群れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "父の仇をとるため横浜の親分の門下に下った一人の若い衆が、戦後の混乱期を経てやがて巨大組織の首領になっていく過程を、ヤクザ社会の義理人情、道義などを交えて描いた実録任侠映画。北島三郎が主題歌を歌い、松方の子分・モロッコの辰役でも好演。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KID FROM BROOKLYN", "ダニー・ケイの牛乳屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "いつも配達成績ビリの牛乳屋が、妹にからんできたチンピラ二人を思いがけなくノックアウトしたおかげで、ボクシングの選手に仕立て上げられ、八百長試合に勝ち続け、ついに世界チャンピオンに……。戦前にハロルド・ロイドが演じた作品のリメイクだが、こちらはダニーの主演だけにミュージカル的楽しさもたっぷり。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARMIN PROFOND", "深紅の愛・DEEP CRIMSON", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "少し太めだが気のいい二児の母で看護婦のコラルは、シャルル・ボワイエの大ファン。彼女は文通相手募集の記事で自称、ボワイエ似のニコラスと知り合う。ある日、ニコラスがコラルを訪ねてくる。彼女は彼に一目惚れしてしまうが、実は彼は結婚詐欺師で元妻を殺害していたのだった。監督はメキシコのベテラン、A・リプステイン。ビデオ、DVDタイトルは「ディープ・クリムゾン／深紅の愛」。", "1996-01-01", "2,19,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続 忍びの者", "続 忍びの者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "織田信長の伊賀攻めによって伊賀の砦はつぶされ、その後も執拗な忍者狩りが続いていた。信長必殺を誓う石川五右衛門は、徳川方からの情報により本能寺で念願を果たすが、今度は秀吉に命を狙われる。権力者の覇権の道具でしかない忍者の悲劇が痛烈に描かれた力作。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA HIJA DEL ENGANO", "賭博師の娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "しがないセールスマンのドンは、ある日、妻の浮気現場に出くわし、銃口を突きつけて彼女を家から追い出す。彼女は“娘はあなたの子供ではない”と言い残して去っていった。その後人間不信に陥ったドンは、冷酷な金の亡者になろうと決心する。歌あり、踊りあり、抱腹絶倒のドタバタありの、巨匠ブニュエルが手掛けた痛快コメディ。", "1951-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Barton Fink", "バートン・フィンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "コーエン兄弟が個性派俳優ジョン・タトゥーロを主演に迎え、カンヌ国際映画祭でグランプリ、監督賞、主演男優賞の3冠に輝いた異色のサスペンス作品。1941年、ニューヨークで活躍する新進気鋭の社会派劇作家バートン・フィンクは、その実力を認められ、ハリウッドに招かれることに。不気味なホテルにチェックインした彼は、薄暗い部屋で早速脚本の執筆に取りかかる。ところが、彼の周囲で次々と奇怪な出来事が起こりはじめ……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("У САМОГО СИНЕГО МОРЯ", "青い青い海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "天才的なストーリーテラーであるソ連の名匠B・バルネットの最高傑作の一つに挙げられる青春映画。若き機関士アリョーシャとユスフが、カスピ海で嵐に見舞われながらも派遣先の小さな島にたどり着く。そこでマーシャという天真爛漫な娘に心を奪われた二人だったが、彼女は大波に飲まれてしまい……。たわいのない三角関係のドラマを、きらめく詩情とユーモアの連続で語り明かすバルネットの演出が、ひたすらみずみずしい。モノクロ映画でありながら、ダイナミックに“青い海”を切り取った映像は眩しいばかり。ヒロインに扮したE・クジミナも、誰もが魅了されてしまうほどの可憐さ。", "1935-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ILL MET BY MOONLIGHT", "将軍月光に消ゆ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "第二次世界大戦中における英国将校の体験記を映画化した冒険ドラマ。独軍占領下のクレタ島に上陸した英軍大尉は、パルチザンの少佐とともに独軍司令官の将軍を捕らえ、カイロへ連行しようとする……。アクション・シーンを排し、英国人特有のユーモアが痛快な、一味違った戦争映画。", "1956-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROZEN ASSETS", "ベイビーバンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "次期社長も夢じゃないと言われ、田舎町に転勤した若手銀行マンのザック。ところがそこは、銀行は銀行でも“精子バンク”だった。しかしヘコたれないザックは、成績を上げるため10万ドルの懸賞を争う“最優秀精子コンテスト”を開き、町は大混乱に……。C・バーンセンとS・ロング共演によるコメディ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Five Graves to Cairo", "熱砂の秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　ビリー・ワイルダー作品には珍しいアクション活劇で、生き残るために戦うイギリス人兵士を描いている。ロメル将軍率いるドイツ軍に敗北を期したイギリス軍。死を免れたブランブル伍長は、前日に命を落としたホテルの給仕に変装し、ドイツ兵との接触を試みる。幸運なことに、亡くなった給仕がドイツ軍のスパイだったため、機密情報を連合軍に漏らすことに成功。ワイルダーが長年ファンだった監督エリッヒ・フォン・シュトロハイムが、将軍役で出演している。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BELLE ET LA BE＾TE", "美女と野獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "詩人J・コクトーが監督した作品で、主演のJ・マレーはこの映画で日本初登場となった。原作はボーモンの童話により、鬼才コクトーがアレンジして、見事な幻想ファンタジー・ロマンに仕上げた。C・ベラールの美術（衣装およびセット）が素晴らしい。", "1946-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UTAKATA／女王・中村うさぎ 「愛のショック療法」", "UTAKATA／女王・中村うさぎ 「愛のショック療法」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "買い物依存症の日々を描いたエッセイ『ショッピングの女王』で知られる人気作家・中村うさぎの、仕事からプライベートまでの一部始終を追ったドキュメンタリー。本人へのインタビューを通して、彼女のセックス観、恋愛観に迫ると共に、豊胸手術を決断した、その果てなき美意識の根幹を探っていく。買い物依存症に加え、ホストクラブ通いにふける中村うさぎが、美容整形に挑戦。仕事場や病院など、カメラは彼女のありのままの姿を映し出す。", "2005-01-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O.HENRY’S FULL HOUSE", "人生模様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "O・ヘンリーの５つの短編を、それぞれ異なったスタッフ、キャストによって描いた、オムニバス作品。留置場暮らしを望む男を描く『警官と聖歌』、幼なじみの刑事と犯人の物語『クラリオン・コール新聞』、病に伏した娘がツタの葉を自分の命に例える『残った葉』、腕白小僧を誘拐してしまった男たちの顛末を綴る『酋長の身代金』、時計を売ってクシを買った夫と髪を売って時計入れを買った妻の、美しい夫婦愛を描く『賢者の贈物』の全５話を収録。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Something's Gotta Give", "恋愛適齢期", "", "", "http://image.eiga.k-img.com/images/movie/1176/315x210.jpg?2120", 128, "若い女性との恋愛遍歴を重ねる中年男性が、交際相手の母親に心惹かれ、そんな自分にとまどう大人のラブ・コメディ。主人公は主演、助演合わせて3度のオスカー受賞歴を持つジャック・ニコルソン。彼が恋する同世代の女性役のダイアン・キートンは、本作でゴールデングローブ賞主演女優賞を受賞、オスカーでも同賞にノミネート。監督は「ハート・オブ・ウーマン」でも大人の恋愛をコミカルに描いたナンシー・メイヤーズ。", "2007-03-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("でかんしょ風来坊", "でかんしょ風来坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "当たり役“マイトガイ”シリーズで一世を風靡した小林旭主演。コメディの度合いを増した銀座の次郎長“暴れん坊”シリーズの第２弾。もちろん、アクションもふんだんに盛り込まれている。元首相の初恋相手が銀座一帯を買い占める？　ハチャメチャな展開でストーリーが進行していく面白さに加え、不良少女グループ、老け役が似合う北林谷栄の怪演も要チェック。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VENDETTA", "シシリアン 虐殺の地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "シチリアからニューオリンズにやってきた移民たちを快く思わない地元マフィアの大物ヒューストン。悪辣な彼の策謀により、数名の移民が警察署長殺しの容疑者として逮捕されるが、悲劇はそれだけで終わらなかった。これが1890年代に起こった実話と聞けば、誰もが衝撃を受ける壮絶な社会派歴史劇。無実の罪を着せられたイタリア人移民が、プロパガンダされた普通の民衆の手で無惨に“公開処刑”されていくクライマックスは、しばらく脳裏に焼きついて離れない。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1969", "1969", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ケネディとキング牧師が暗殺された1960年代。ベトナム戦争がもたらした混沌とした時代の気運が、若者たちを暗い世界に閉じこめていた。無二の親友であるラルフとスコットは、大学の春休みを利用して故郷に帰る。しかし、二人はそこで様々なベトナム戦争の影響に直面する。スコットの兄は、厳格な父の希望通り出征し、そして二度とは戻ってこなかった。時代からも現実からも逃避しようとする二人。だが彼らにも暗い影は容赦なく迫ってきていた。ベトナム戦争の泥沼化により揺れるアメリカで、苦悩する若者たちの姿を真摯な視点で見つめた佳作。全編に流れているのはクリームやジミ・ヘンドリックスほかの名曲15曲と、プリテンダーズによるオリジナル１曲。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Atlantis", "アトランティス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「グラン・ブルー」「レオン」のリュック・ベッソン監督による、構想10年、撮影38カ月の歳月をかけた海洋ドキュメンタリー作品。自身もダイビングの経験を持つリュック・ベッソン監督自ら撮影も行っている。「最後の戦い」から数々のベッソン作品に参加しているエリック・セラの音楽にのせて、世界の様々な海に生息する神秘の生態を写す。", "2010-09-04", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THING CALLED LOVE", "愛と呼ばれるもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "カントリーの本場、ナッシュビル。たくさんのカントリー・シンガーを輩出したブルーバード・カフェのオーディションに、一人の男と一人の女がやって来た。やがてオーディションで知り合う二人。ところがそこにもう一人の男が……。カントリー・ソングをバックに始まる恋のトライアングル。夭折した青春スター、R・フェニックス最後の主演作にして彼の歌声もたっぷりと収録された一編。P・ボグダノヴィッチらしい温かな演出も見もの。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beethoven 9 The Complete Symphonies: Thielemann & Wiener Philharmoniker", "ウィーン・フィル＆ティーレマン　ベートーヴェン全交響曲　シネコンサート", "", "", "http://image.eiga.k-img.com/images/movie/57366/315x210.jpg?3992", 0, "現代クラシック界で注目を集めるドイツ出身の指揮者クリスティアン・ティーレマンとウィーン・フィルハーモニー管弦楽団が、ムジークフェラインザール（ウィーン楽友協会大ホール）で2008年から3年の歳月をかけて行ったベートーベンの交響曲第1番～第9番の全演奏会の様子を一挙上映。Aセット（第1番・第3番「英雄」）、Bセット（第2番・第6番「田園」）、Cセット（第4番・第5番「運命」）、Dセット（第7番・第8番）、Eセット（第9番合唱付き）の5セットに分けて上映される。", "2011-12-10", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("驚天龍虎豹／LETHAL PANTHER", "リーサル・パンサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "全編、銃撃戦と戦闘シーンが連続するハデなアクション映画。二人の女スナイパーが、マフィアの首領暗殺の依頼を受ける。完璧に仕事を完了したものの、二人のうち一人が依頼主に殺されてしまう。残された一人は、逆襲を開始する……。", "1990-01-01", "6,1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ココニイルコト", "ココニイルコト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "岩井俊二の「Undo」「Love Letter」などでプロデューサーを務め、「はつ恋」で脚本を書いた長澤雅彦の監督デビュー作。上司との不倫が原因で、東京から大阪の営業局に飛ばされたコピーライターの志乃は、目標も希望も失っていた。そんな彼女を支えたのが、同僚の前野だった。彼の楽観的な考え方と明るい性格に励まされ、志乃は次第に心癒されていくのだが……。真中瞳が映画初主演とは思えない落ち着いた演技で、揺れ動くヒロインの微妙な心の動きを表現。ゆっくり生き生きとしていく彼女の表情の変化が清々しい。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古惑仔之人在江湖／YOUNG AND DANGEROUS", "欲望の街　古惑仔I 銅羅湾〈コーズウェイベイ〉の疾風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "香港の若者を熱狂させた青春アクション・シリーズの記念すべき第１作。“古惑仔（こわくちゃい）”と呼ばれる、クールに生きる若きチンピラたちの姿を、「恋する惑星」のカメラマン、A・ラウがエネルギッシュに描く。幼い頃からつるんでいた５人の不良少年たちが、暴力団、洪興社の組員の舎弟に。危ない橋を渡りながら、彼らは組織内で勢力を伸ばしていく。そんなある日、別組織のボスの抹殺を命じられた５人はマカオへと飛ぶが、組織内の卑劣な裏切り行為によって破門の身に。さらに彼らの友情にも亀裂が生じてしまう。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LONESOME　COWBOYS", "ロンサム・カウボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ポップ・アートの中心的人物A・ウォーホルが手掛けた映画の一つで、映画を作り始めた当初はアングラ色強い作品を作っていた彼が、より一般劇場映画に近い作品を作るようになった変節点と言える怪作ウエスタン。アリゾナのゴーストタウンを舞台に一人の女とゲイのカウボーイたちが繰り広げる騒動を、エロチシズムとフェチシズムを織り交ぜて描き出す。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GROTESQUE", "グロテスク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "恐竜の研究に明け暮れる没落貴族のヒューゴ・コールと、夫に相手にされず欲求不満の妻のハリエットの暮らす館に、新しい執事夫婦フレッジとドリスがやって来た。その日以来、コール家の生活は一変する。忠実に仕事をするフレッジだったが、動物的な本能で、コール家の人々の弱点を嗅ぎ出していく。欲求不満のハリエットを誘惑し、ヒューゴがゲイだと気付くとそれをネタに彼を脅し始めるのだった。おまけに、娘のクレオに相手にされないと、その恋人と同性愛の関係を持ってしまう。果たしてフレッジの狙いは何か？　不気味な執事をロック・スターのスティングが演じ、主題歌も歌っている。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("病院へ行こう", "病院へ行こう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "間男した男とされた男が、階段から落ちて一緒に入院。しかも、ベッドは隣り同士。二人の担当医は研修医の女性で……。この３人の病院生活でのドタバタを描いたコメディ。入院生活を斜めからのぞいた一色伸幸の脚本が、テンポの良いドラマを生みだした。女医に扮した薬師丸は、キャラクターを生かした演技で好演している。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海猫", "海猫", "", "", "http://ecx.images-amazon.com/images/I/21EVK33GP4L._SL210_.jpg", 129, "原作は谷村志穂の小説。日本とロシアのハーフである薫は、一目ぼれされて漁師・赤木邦一の元へ嫁ぐ。子供にも恵まれ、夫の仕事を手伝い、海の女になるはずだった。ところが怪我をして入院した邦一は看護師の啓子に心惹かれ、薫もまた自分に秘かに想いを寄せる義弟の広次に惹かれていき……。薫は二つの愛に苦悩し、やがて心身ともに壊れていく。伊東美咲が体当たりの演技を見せている。", "2004-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bad News Bears", "がんばれ！ベアーズ　ニュー・シーズン", "", "", "http://image.eiga.k-img.com/images/movie/1016/315x210.jpg?4397", 113, "「スクール・オブ・ロック」のリチャード・リンクレイター監督が「バッドサンタ」のビリー・ボブ・ソーントンを主演に、76年の大ヒット作「がんばれ！ベアーズ」をリメイク。元プロ野球選手のモリスは、今は害虫駆除の仕事をしながら酒びたりの日々。彼は弁護士のリズに依頼されて子供たちの弱小チーム、ベアーズのコーチを引き受けるが初戦で惨敗。モリスはチームの建て直しを決意し、やがて野球への情熱を取り戻していく。", "2005-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEARSKIN", "ベアスキン 都会の夜の一幕寓話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "訳ありな過去を背負った得体の知れないアメリカ人シルバ。彼が生活の糧としている人形劇の一座に、ある日、殺し屋に追われている少年ジョニーが転がりこんできて……。ヨーロッパの架空の都市を舞台に、T・ウェイツがそのアウトローな魅力を遺憾なく発揮した現代の寓話。", "1990-01-01", "3,25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いかレスラー", "いかレスラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ロングラン・ヒットを記録したイギリス産おバカ映画「えびボクサー」。“えび”と肩を並べる人気食材“いか”に、ボクシングよりメジャーなプロレスをさせてはどうか、という突飛なアイデアから誕生したのがこの作品だ。不治の病を患い失踪した人気レスラーが、“いか”となることで病を克服し、再びプロレス界に殴り込みをかけるといった破天荒な物語をナンセンスな笑いと本格アクションで彩っていく。だが、この映画の魅力はいかだけじゃない。いかレスラーに勝つため、たこに生まれ変わった“たこレスラー”や甲殻類最強の戦士“しゃこボクサー”も登場。史上最大のシーフード・バトルが展開する！　田口浩二VSクラッシュ火山によるIMGP王座決定戦。得意技で火山を下し、コミッショナーの鴨橋からチャンピオン・ベルトを受け取ろうとした田口だったが、突如現れた巨大ないかがリングに乱入。ベルトを奪われてしまう。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE FAKE", "BLUE FAKE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "女探偵の綾香は同居する家出少女のエチカとケンカ別れしてしまう。そんな中、知り合いの探偵事務所から綾香に仕事が入ってくる。依頼人はラジオ番組のDJ・郷田。イヤがらせ電話をしてくるリスナーの美奈子を見つけてほしいというものだった。だが、間もなく美奈子は事故に遭って死んでしまう。釈然としない綾香は郷田に近づくが、危険な匂いを感じながら次第に彼に惹かれていく……。見せかけのような現代社会を背景に、奔放に生きる女性の姿を通して、大人になりきれない都会人の人間模様を描くヒューマン・ドラマ。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("按摩と女", "按摩と女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "温泉場の按摩は、東京から来た女に恋をしてしまう。彼は女がスリだと勘違いして逃がしてやろうとするのだが……。様々な人物が入り乱れ、交錯しながら展開する軽いタッチのコメディで、リラックスした一編。即興演出のためであろうか、どこかホーム・ムービー的な感じの作品。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Assault on Precinct 13", "アサルト13 要塞警察", "", "", "http://image.eiga.k-img.com/images/movie/1266/315x210.jpg?4464", 110, "ジョン・カーペンター監督の「要塞警察」（76）のリメイク。デトロイトの13分署に凶悪犯が輸送されて来た夜、謎の武装集団が襲ってくる。「トレーニング・デイ」のイーサン・ホーク、「マトリックス」のローレンス・フィッシュバーンらが共演。監督は自国フランスで撮った手持ちカメラによるアクション映画で注目を集め、本作でハリウッド・デビューのジャン・フランソワ・リシェ。脚本は「交渉人」のジェイムズ・デモナコ。", "2006-02-18", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("8 Femmes", "8人の女たち", "", "", "http://image.eiga.k-img.com/images/movie/1236/315x210.jpg?3784", 111, "舞台は50年代のフランス。クリスマス・イブの朝、雪のため密室となった大邸宅で、主人の死体が発見される。邸宅にいるのは8人の女たち。果たして犯人は誰なのか。フランソワ・オゾン監督の最新作はフランス映画界が誇る新旧8人の名女優の豪華共演も話題のミュージカル。17年生まれの「うたかたの恋」のダニエル・ダリューから、79年生まれの新星リュデビーヌ・サニエまで、全員に歌って踊るシーンがあるサービスぶり。", "2002-11-23", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROMWELL", "クロムウェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "イギリス近代史上、最大の事件である市民革命を、製作に５年かけて緻密な時代考証で描くスペクタクル映画。共和制を樹立した主人公オリヴァー・クロムウェルにR・ハリス、戦いに敗れて斬首された国王チャールズ１世にA・ギネスが扮する。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エンジェル・ダスト", "エンジェル・ダスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "大都会の死角を突き、群衆の中で若い女性ばかりを狙った連続殺人が発生。抜擢された異常犯罪性格分析官・須磨節子は、捜査線上にかつての恋人・阿久礼の存在を知って愕然とする……。洗脳（マインド・コントロール）、ラブロマンス、サスペンスの要素を混ぜ合わせた、異色極まりないメロドラマ。「逆噴射家族」以来10年ぶりの長編映画となる奇才・石井聰亙監督作。劇団「ブリキの自発団」の生田萬が脚本に参加している。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カミナリ走ル夏", "カミナリ走ル夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "三池崇史作品で助監督を経て、「１−ICHI−」で監督デビューを果たした丹野雅仁による青春ドラマ。空しさを抱え、惰性で生きていた青年が、一人の少女との出会いによって輝きを取り戻していく。理想と現実とのギャップの中で葛藤しながら生きる若者の姿を、スタイリッシュな映像と音楽でみずみずしく描かれている。バッティングセンターで働く20歳の青年・雷は、目的もなく空虚な毎日を送っていた。常連客のヤクザとともにやって来る女子高生・夏生も孤独な心を抱えていた。そんな二人が次第に心を通わせ、やがて雷の中で何かが弾ける。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hours", "めぐりあう時間たち", "", "", "http://image.eiga.k-img.com/images/movie/1196/315x210.jpg?2799", 115, "マイケル・カニンガムの同名小説（集英社刊）を「ダメージ」の脚本家であり「パリスbyナイト」「ストラップレス」などの脚本・監督兼任作を持つデビッド・ヘアが脚色。1923年のロンドンで精神的な病を患いながら「ダロウェイ夫人」を書くバージニア・ウルフ。51年のロサンゼルスで「ダロウェイ夫人」を読む家庭の主婦。01年のニューヨークで詩人の友人のためにパーティを開こうとする女性編集者。3つのドラマが交錯していく。", "2003-05-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONTE WALSH", "モンテ・ウォルシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "開拓時代も終わりに近いある西部の町。牧場の経営も大企業の進出で先細りだ。ガンマンのモンテの仲間ショーティは牧場をクビになり、無法者となって強盗三昧。モンテはやむなくショーティと対決することになる……。ジャンヌ・モローをはじめ個性派俳優をそろえ、死にゆく西部のロマンに捧げた挽歌。監督は「ローズマリーの赤ちゃん」「ミスター・グッドバーを探して」などの撮影監督W・A・フレイカー。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Happy Gilmore", "俺は飛ばし屋　プロゴルファー・ギル", "", "", "http://ecx.images-amazon.com/images/I/51G886ME6DL._SL210_.jpg", 95, "サタデー・ナイト・ライブ出身の人気コメディアン、アダム・サンドラー主演によるスポーツコメディ。プロのアイスホッケー選手を目指す青年ハッピー・ギルモアは、シュート力は抜群なのにスケートが大の苦手。ある日、差し押さえられた祖母の家を取り戻すために大金が必要になったギルモアは、賞金目当てでゴルフトーナメントに出場することに。持ち前のシュート力でミラクルショットを連発し、順調に勝ち進んでいくが……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　GOU＾T　DES　AUTRES", "ムッシュ・カステラの恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "セザール賞で４部門を受賞するなど、様々な映画賞を受賞したロマンティック・コメディ。中堅会社社長のカステラは、仕事への熱意も妻への愛も冷めてしまい冴えない毎日。そんな中出会った英語教師クララの舞台女優としての一面を知り、彼女に恋をしてしまう。クララに近づくために、芝居に興味を抱いたり芸術家たちと交流したりと、無縁だった世界で大奮闘。やがて、芸術の素晴らしさに目覚めていく彼の一途な姿は、クララの心を動かしていく。中年男性の純愛と周囲の人間模様を、ユーモアとペーソスを交えて描く。ときに切なく、ときにドキリとさせる鋭い人間洞察がさりげなく光るさわやかな人間讃歌となった。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Misadventures of Merlin Jones", "ツツぬけですピタリ一発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "読心装置を発明した青年が繰り広げるドタバタ騒動を描いた、ウォルト・ディズニー製作による奇想天外なコメディ作品。メガホンを取るのは、「メリー・ポピンズ」等の作品を手掛けた、ディズニー映画の常連監督ロバート・スティーブンソン。超脳波を研究する変わり者の学生マリーン・ジョーンズは、人の心を読むことのできる装置を発明する。ある日、判事が詐欺や強盗の計画を考えているのを知った彼は、慌てて警察に通報するが……。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE APRIL FOOLS", "幸せはパリで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "会社と家庭との往復だけで、人生をすり減らしている平凡な会社員が、パーティーで知り合った美しい女性と意気投合し、すべてを投げ捨て一緒にパリへ。ちなみに彼女は彼の会社の社長夫人、まったくウソのようなコメディを、実に楽しく見せてくれる。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALICE’S RESTAURANT", "アリスのレストラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "現代の吟遊詩人アーロ・ガスリーが実名で登場し、自らの青春と彷徨を演じていくホロ苦いヒューマン・ドラマ。ヒッピーのアーロは学校を中退すると仲間とともに廃墟になった教会跡に住み、おんぼろギターを抱えて日々を過ごしていた。だがある日、彼のもとに徴兵検査の通知が届き、彼はなんとか逃れようとする。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DILEMMA", "ジレンマ 秒の追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "警官殺しの凶悪犯サラザールは、腎臓移植手術を必要とする少女にとって唯一の適合ドナーであることが分かる。腎臓提供を条件に減刑された彼だったが、病院に移送される途中に脱走してしまう。サラザールを追うことになったのは彼に相棒を殺された刑事クイン。何度となく彼を追い詰めるが、少女への移植を成功させるために銃を撃つことができず……。刑事と犯人の息づまる追跡劇を描いたサスペンス・アクション。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Another Earth", "アナザープラネット", "", "", "http://ecx.images-amazon.com/images/I/51-fI6lWPZL._SL210_.jpg", 93, "", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAINED HEAT", "チェーンヒート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "女囚刑務所ものはB級アクションの定番の一つだが、これは強烈な拾い物の一編。麻薬密売や売春など悪の限りをつくす刑務所長派と女看守長派が対立する所内で、ブレアが暴動を呼びかける。当時24歳のブレアが、肢体もあらわに体当たりで熱演している。", "1983-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CORVETTE SUMMER", "コルベット・サマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "アメリカの名車“コルベット・スティングレー”に憧れ、青春の情熱を傾ける一人の若者の物語。高校生ケニーは、ある日ポンコツ同様のコルベットを手に入れ、見事新車同様に改造するが、何者かに盗まれてしまう。消えた愛車を探して、ケニーは旅に出る……。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悲しき別れの歌", "悲しき別れの歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "おなじみ小百合・光夫のコンビによるラブ・ストーリー。北国から上京し洋裁学校に通う娘が、医者を志す青年と出会い、恋をする。母にそのことを打ち明けるが、母はその青年が、かつて自分を愛した男の息子であることを知って大きなショックを受け、病床に伏してしまう。母の死の間際、娘はことの真相を聞かされるが……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おまえうまそうだな", "おまえうまそうだな", "", "", "http://image.eiga.k-img.com/images/movie/55536/315x210.jpg?4615", 89, "人気絵本作家・宮西達也の大ヒット作「ティラノサウルス」シリーズをアニメ映画化。草食恐竜に育てられたティラノサウルスのハートは、自分が肉食恐竜だと知り、ひとりで生きていくことを決心する。ある日、ハートは卵から生まれたばかりの草食恐竜の赤ちゃんを食べようとするが、赤ちゃんはハートを父親だと思い込んでしまう。やがて2人の間には父と子のような愛情が芽生えはじめ……。赤ちゃん恐竜の声を演じるのは人気子役の加藤清史郎。", "2010-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ET DIEU… CRE´A LA FEMME", "素直な悪女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ヴァディム監督の処女作。当時22歳の愛妻バルドーを主役に起用して話題を呼んだ。孤児の娘ジュリエットをめぐって酒屋の主人、プレイボーイたちが繰り広げる恋のさや当てゲーム。バルドーがセクシーな魅力をふりまき世界中でセンセーションを巻き起こした。", "1956-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TARZAN　FIND’S　A　SON", "ターザンの猛襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "J・ワイズミュラー主演の“ターザン”シリーズ第４作。英国貴族の夫妻を乗せた飛行機が墜落し、奇跡的に夫妻の赤ん坊だけが密林で命をとりとめた。チータによってターザンとジェーンのもとに連れてこられたこの赤子は“ボーイ”と名づけられ、すくすくと成長する。やがてボーイが６歳になったとき、彼の遠縁が捜索にやってくるのだが……。DVDは「ジョニー・ワイズミュラー ターザン・フィルムズ」に収録。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギブリーズepisode２", "ギブリーズepisode２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "架空のアニメーション製作会社“スタジオギブリ”で働く面々の日常の生活ぶりを、面白おかしく描いたオムニバス短編アニメーション。「猫の恩返し」の同時上映として公開された。CGをダイナミックに用いたものや、ノスタルジックなパステル風の作画など、パートごとに手法を変えており、実にバラエティー豊かで楽しめる。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Concile de Pierre", "ストーン・カウンシル", "", "", "http://image.eiga.k-img.com/images/movie/34006/315x210.jpg?1348", 103, "「クリムゾン・リバー」の原作者ジャン＝クリストフ・グランジェの同名小説を映画化したアクション・スリラー。モンゴルから迎えた養子リウ＝サンとパリで暮らすローラ。リウ＝サンの7歳の誕生日が近づいた頃、母子の周囲では不可思議な出来事が次々と起こり始め……。息子を守るために立ち上がる母を「マレーナ」のモニカ・ベルッチがほぼ全編ノーメイクで熱演。フランスの大女優カトリーヌ・ドヌーブとの競演も見どころ。", "2007-06-09", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Music from the Inside Out", "オーケストラの向こう側　フィラデルフィア管弦楽団の秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "世界屈指の名門オーケストラ、フィラデルフィア管弦楽団の演奏家たちにスポットを当てたドキュメンタリー。アカデミー賞ノミネート経験を持つドキュメンタリー作家ダニエル・アンカーが5年間に渡ってオーケストラに密着し、ステージの上のみならずメンバーひとりひとりの日常風景まで丹念に取材。それぞれが歩んできた人生や音楽との向き合い方を丁寧に掘り下げ、彼らが紡ぎだす美しいハーモニーの源に迫る。", "2008-05-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CANNONBALL RUN", "キャノンボール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“キャノンボール”とは東海岸のコネチカットから西海岸のL.A.まで、5000キロにわたるカーレースのこと。B・レイノルズを中心にジャッキー・チェンやファラ・フォーセット、R・ムーア、D・マーティンら、豪華スターが多数登場する痛快アクション・コメディ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATAL BEAUTY", "危険な天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "W・ゴールドバーグが女刑事に扮したアクション。“危険な天使”とは、若者たちの命を30秒で奪う麻薬のこと。自らも麻薬に溺れ、最愛の幼子を失った経験をもつ女刑事、リタ・リゾーリは、この恐るべき麻薬を根絶するため巨大な組織を相手に立ち上がる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'ennemi Intime", "いのちの戦場　アルジェリア1959", "", "", "http://image.eiga.k-img.com/images/movie/54206/315x210.jpg?1848", 112, "フランス近現代史のタブーとされるフランスとアルジェリアの植民地独立戦争を、「ピアニスト」「王は踊る」のブノワ・マジメルが立案・主演した戦争映画。「スズメバチ」のフローラン・エミリオ・シリが監督を務める。1959年、アルジェリアのカリビア地方ではフランスの支配からの独立を求めゲリラと仏軍との熾烈な戦闘が続いていた。新しく赴任してきたテリアン中尉は、常態化する拷問や虐殺など殺戮の泥沼化を懸念するのだが……。", "2009-02-28", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今ひとたびの", "今ひとたびの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "五所平之助の戦後第１作にして、戦後初の純愛映画。第二次大戦前、左翼の運動家・野上と恋人・暁子との仲は引き裂かれるが、戦後めでたく結ばれる。龍崎一郎、高峰三枝子のカップルが、ニコライ堂の前で逢瀬を重ねるロマンティックなシーンに、五所らしい抒情的なタッチが垣間見える。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("連弾", "連弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "夫は専業主夫、妻はバリバリのキャリアウーマン。こんな逆転夫婦が突如、妻の不倫発覚によって家庭崩壊の危機に……。竹中直人が監督・主演を手掛けたホームドラマ。シリアスなテーマながら、登場人物たちがコミカルな鼻歌を歌うなど、竹中ならではのユニークかつユーモアあふれる演出によって軽妙な味わいの人間ドラマかつ家族ドラマとなった。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Warrior and the Wolf", "ウォーリアー＆ウルフ", "", "", "http://image.eiga.k-img.com/images/movie/57196/315x210.jpg?3528", 106, "井上靖の短編小説「狼災記」を、オダギリジョー主演、「青い凧」「春の惑い」のティエン・チュアンチュアン監督で映画化したアクションロマンス。秦の皇帝の命を受け、反乱部族を制圧するため北方の国境警備にあたっていた軍指令の陸は、退却の最中に猛吹雪に襲われる。呪われたハラン族の村に避難した陸は、そこで出会った謎の美女と恋に落ちるが、2人には過酷な運命が待ち受けていた。ハラン族の美女役に「ダイ・ハード4.0」のマギー・Q。", "2011-10-22", "8,6,54,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神様のカルテ", "神様のカルテ", "", "", "http://image.eiga.k-img.com/images/movie/55556/315x210.jpg?2050", 128, "現役医師の作家・夏川草介によるベストセラーで、2010年本屋大賞第2位にも選出された同名小説を映画化。主演は、人気アイドルグループ「嵐」の櫻井翔と宮崎あおい。「60歳のラブレター」「白夜行」の俊英・深川栄洋がメガホンをとる。夏目漱石を愛読し、信州の地方病院に内科医として勤務する栗原一止が、写真家の愛妻・榛名や同僚医師、看護師、患者、アパートの個性的な住人らに支えられ、医師として成長していく姿を温かなまなざしで描く。", "2011-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LIE", "鏡の向う側", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "優雅に暮らす３人家族。妻は夫にセックスを拒まれ身もだえる。夫は娘への性愛の衝動に苦しむ。娘は無邪気に微笑みながらも女の魔性を隠し持っている。この家族の性の苦悩とその崩壊を描いた心理ドラマ。原作はA・モラヴィアの小説『関心』。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・タイガース 華やかなる招待", "ザ・タイガース 華やかなる招待", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ステージ演奏を夢見て東京へ出てきた５人組が、かつて無銭飲食をしたレストランのウェイトレスとともに、様々な難関をくぐり抜けてステージ演奏を実現させていくまでを描いたサクセス・ストーリー。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORTAL KOMBAT ANNIHILATION", "モータル・コンバット2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "コンピュータ・ゲームから生まれたバトルアクションの続編。前作で究極の格闘トーナメント戦に敗れた、邪悪な魔界の戦士たちが復活。リュー・カンをはじめとする凄腕の格闘家たちが、再び地球を守るための戦いに乗り出していく。ハードコア・テクノのデジタルなサウンドとCGを駆使したSFX映像の融合が刺激的。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MATRIMONIO　ALL’ITALIANA", "あゝ結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "離婚が法的に認められていないイタリアでの結婚をモチーフにした、名匠V・デ・シーカ監督のコミカルな人間ドラマ。無学な商売女S・ローレンと、彼女が初めてとった男M・マストロヤンニとの奇妙な関係が綴られていく。それまで虐げられ続けてきた女が、ラストで一発逆転をかますという痛快な一編である。", "1964-01-01", "4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOOD MOTHER", "情熱の代償", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "役者であり、また監督としても「スリーメン＆ベビー」を手掛けたL・ニモイが描く人間ドラマ。夫と別れ、一人娘との新生活を始めたモリー。ある日、彼女は自由に生きる彫刻家と出会い、人生で初めてともいえるような激しい恋をしてしまう。そんな彼女に様々な問題が起きる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おはん", "おはん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "大正時代の関西の田舎町。ほとんど商いのなくなった古道具屋を営む幸吉は、昔なじんだ芸者のおかよに引き取られて暮らしている。ところが、ある夏の日、７年前に別れた妻のおはんに出会う。おはんはまだ幸吉を思い続けているといい、幸吉は再び二人で暮らす決心をするのだが……。市川崑監督が幸吉、おはん、一人息子の悟、おかよとごく少数の主要人物に焦点を合わせてドラマを構成し、細やかな演出をみせた男と女の愛の物語。おはんに吉永小百合、芸者のおかよに大原麗子が扮し、対照的な役柄を演じる。別れた妻と芸者との間で揺れる微妙な心理状態を、石坂浩二が好演。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAT", "BEAT", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "舞台の演出家、宮本亜門が映画監督デビューを飾った人間ドラマ。米軍統治下の1960年代の沖縄を舞台に、この地に流れ着いて住み続ける男タケシと、黒人兵にレイプされ、その時の子供マリアを育てる女ミチを描く。かつては恋人同士だった二人の心が悲しい事件に遭遇しながら葛藤し、もがき苦しみながらも再び交錯していくまでを、沖縄に生きる人々のバイタリティーあふれる生き様を交えながら鮮烈に映し出す。ミチを演じた内田有紀の熱演が印象に残る。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WATERLOO", "ワーテルロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "1815年。英雄ナポレオンと知将ウェリントンが、ヨーロッパの運命を懸けてワーテルローの地で激突した戦いを描くスペクタクル歴史劇。ソビエト版オリジナル・バージョンは４時間に及ぶ大作。", "1970-01-01", "4,12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CRI DU HIBOU", "ふくろうの叫び　偏愛殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "アルフレッド・ヒッチコック「見知らぬ乗客」、ルネ・クレマン「太陽がいっぱい」などの原作者であるP・ハイスミスの1962年の同名小説をC・シャブロルが映画化した心理サスペンス。ジュリエットの家の周りを毎晩、ロベールという男がうろついている。彼は一年前から妻と別居中で、ジュリエットの美しさに魅せられ、彼女を見ることだけを生きがいにしていた。ある晩、ロベールは思い切ってジュリエットの前に姿を現すが、自分のことについては嘘をついてしまう。ジュリエットには婚約者のパトリックがいたが、幸せな気持ちになれず、次第にロベールに気持ちが移ってしまう。ところが不器用なロベールは彼女を受け入れることができなかった。「サム・サフィ」などの監督であり、監督デビュー以前の女優時代のV・テヴネが、ベロニカ役で出演している。1987年の作品だが、1997年に初めて公開された。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA DANZA MACABRA", "幽霊屋敷の蛇淫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "日本の映画監督、黒沢清も惚れ込んでいるイタリアン・ホラーの隠れた傑作。70年代に誕生する「エクソシスト」や「オーメン」のような悪魔の話ではなく、本作は日本の怪談的な幽霊ものという異色の設定だ。幽霊が間違いなく出没する館に取材気分で入った新聞記者が、その余りの美しさにひと目惚れ。恋の行方はいかに？", "1964-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREENDALE", "ニール・ヤング／グリーンデイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ロック界のカリスマ、ニール・ヤングが「Human Highway」以来、20年ぶりに劇映画を製作。アルバム『グリーンデイル』で歌われた架空の町の物語を、１曲毎にエピソード化したロック・ミュージカルだ。環境問題や政府批判、若者への希望など、様々な思いが交錯した彼のアメリカ観が展開。平和な町“グリーンデイル”で突如発生した警察官殺人事件。それを発端にして、現代社会の様々な問題が人々の身に降りかかっていく。", "2004-12-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MCDULL、 PRINCE DE LA BUN", "マクダル・パイナップルパン王子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "本邦未公開の「My Life as Mcdull」に続く、香港の大人気アニメの映画化第２弾。キュートなブタやウシたちの日常を通じて、大人にしかわからない哀愁や苦悩が炸裂するシュールな世界観が強烈。子供も大人も魅了したこの作品は、子豚マクダルの父親の若き日が、ほとんど母親の妄想として語られるというディープ＆メルヘンな怪作だ。シングルマザーのミセス・マクは息子マクダルの“貧乏ゆすり”を直すために、今はいない夫パイナップルパン王子について語り始める。", "2006-03-11", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CIRCLE OF TWO", "レッスン・イン・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "60歳のエキセントリックな画家は、こともあろうに15歳の女学生と恋に落ちてしまった。愛しているがゆえに抱かれたいと思う少女と、愛しているがゆえに抱いてはいけないと思う男。二人の微妙な心の揺れ動きととまどいを、みずみずしい映像で綴ったラブ・ストーリー。T・オニールのオール・ヌードも話題になった。", "1980-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NUTTY PROFESSOR", "底抜け大学教授", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "さえない大学教授ケルプは、自分の発明した特殊薬の力でたくましい男性に変身。昼はさえない大学教授、夜はカッコいいプレイボーイの二重生活を続けるケルプ。やがて薬が切れて正体がバレるが、教え子の愛を得て元の自分を取り戻す。1950～60年代にかけて活躍したJ・ルイス18本目の爆笑コメディ。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("檸檬のころ", "檸檬のころ", "", "", "http://image.eiga.k-img.com/images/movie/33906/315x210.jpg?1267", 115, "若手作家・豊島ミホの小説を原作に、高校生の男女の甘く切ない日々を爽やかに綴った青春ドラマ。高校3年生の加代子は成績優秀で、吹奏楽部の指揮者も務めている。野球部の西は中学校時代から彼女に好意を抱いているが、その気持ちを伝えられずにいた。一方、音楽ライターを夢見る恵は軽音楽部の辻本と親しくなり、文化祭で演奏する曲の作詞を依頼される。「僕は妹に恋をする」の榮倉奈々と「カナリア」の谷村美月が2人のヒロインを瑞々しく演じる。", "2007-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GHOSTS …OF THE CIVIL DEAD", "亡霊の檻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "近未来の最新鋭の刑務所。一見パラダイスに見える物資と待遇に恵まれたこの刑務所で、ある日突然、見えないシステムによるロックダウン（囚人監房内への厳重な監禁）が始まった……。90人の出演者のうち50人が本物の囚人という、ミュージック・ビデオ出身のスタッフが送り出す近未来もの。", "1988-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大奥　浮絵悲恋", "大奥　浮絵悲恋", "", "", "http://image.eiga.k-img.com/images/movie/54026/315x210.jpg?1317", 0, "若手俳優集団D-BOYSが主演を務める大河ロマンシリーズ第3部作。主演は「アシンメトリー」「夏休みのような1ヶ月」の荒木宏文。正徳2年、6代将軍徳川家宣が死去し、裏大奥では正室と側室の壮絶な権力争いが繰り広げられていた。そんなある日、正室に突き飛ばされ転んでしまった大奥女中の絵島は、通りすがりの美青年に助けられる。しかし彼は正室が足しげく通う男遊郭の花魁で……。", "2008-11-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOUCHETTE", "少女ムシェット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "フランスの片田舎に暮らす14歳のムシェット。アルコール中毒の父親と病気の母親に代わって家を切り盛りさせられている上に、学校では同級生や教師から酷い扱いを受けるばかりの薄幸の少女だ。ある雨の夜、森の中で密猟者と出会ったことがきっかけで、彼女の人生はさらに悲惨な破滅へと向かって転がり落ちていく……。ブレッソン監督が「バルタザールどこへ行く」に続いてアナトール・ドーマンと組み、ストイックでリアルな表現で語りきった絶望の物語。", "1974-09-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦国群盗伝", "戦国群盗伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "戦前、P・C・Lで滝沢英輔が監督した作品の再映画化。山中貞雄の脚本を、本作では新しく黒澤明が潤色した。北条氏政が関東管領になった永禄、元亀の頃を背景に、天城山を根城に暴れまわる野武士の一団を西部劇タッチで描いた大作だが、杉江敏男監督では荷が重すぎた。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fast Five", "ワイルド・スピード MEGA MAX", "", "", "http://image.eiga.k-img.com/images/movie/55916/315x210.jpg?4833", 130, "前科者のドミニクと彼を脱獄させた元捜査官ブライアンは、張りめぐらされた捜査網をかいくぐり、ブラジルの裏社会で身を隠していた。2人は逃亡生活を終わらせて自由を得るため、裏社会を牛耳る黒幕から1億ドルを強奪する計画を立案。世界中から凄腕のドライバーを集める。人気カーアクションシリーズ第5弾で、前作「ワイルド・スピードMAX」のジャスティン・リン監督がメガホンをとる。ポール・ウォーカー、ビン・ディーゼルらシリーズのオリジナルキャストに加え、ドウェイン・ジョンソン（ザ・ロック）が出演。", "2011-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZORRO，THE GAY BLADE", "ゾロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "G・ハミルトンが怪傑ゾロの兄と、弟の二役を演じるパロディー色の強いコミカルな活劇。かつて父が怪傑ゾロであったことを知ったドン・ディエゴは、父の遺志を継いで二代目ゾロとして暴君総督の前に立ちはだかる。しかし、彼の双子の弟がオカマだったことから話はややこしくなり……。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CAZA", "狩り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "C・サウラ監督初期の代表作。内戦終結後のスペイン。ホセは３人の仲間とともに狩猟場へうさぎ狩りに出かけた。だが、猟が進むにつれ、彼らは平常心を失っていき、猟は思わぬ惨劇へと転化する。スペイン内乱の爪痕を色濃く残した作品であり、スペイン映画史にその名を残す傑作として誉れ高い一作。", "1965-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUS DEN LEBEN DER MALIONETTEN", "夢の中の人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "のぞきスタジオで女が殺されるという事件が起きる。精神分析医イェンスンは、犯人からの電話を受けたということで事情聴取に応じる。しかしそれは昔からの知人のもので、とても殺人を犯すような人物ではなかった。ベルイマン晩年の作品で、罪を犯すに至るまでの心理を独特の映像で描いている。", "1980-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOOK", "フック", "", "", "http://ecx.images-amazon.com/images/I/51S9FC8EZ3L._SL210_.jpg", 142, "S・スピルバーグが、かねてから希望していた『ピーターパン』の実写映画化を果たした意欲作。40歳になるアメリカ人弁護士ピーターは妻と子供を連れて祖母ウェンデイの待つロンドンに休暇に出かける。しかし深夜に子供たちは何者かに誘拐されてしまう。残されたのはフック船長からの手紙。そして子供を救えるのは、かつて“空飛ぶピーターパン”であったピーターだけなのだ。自分がピーターパンだと信じることのできないピーターだが、妖精ティンカーベルの導きでネバーランドへと旅立つことになる……。R・ウィリアムズのピーターパン、D・ホフマンのフック船長、J・ロバーツのティンカーベルなどキャスティングも絶妙だ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("珠拉的故事", "草原の女", "", "", "http://image.eiga.k-img.com/images/movie/53946/315x210.jpg?3104", 91, "「胡同の理髪師」のハスチョロー監督が、2000年に自身の故郷である内モンゴルを舞台に撮り上げた長編デビュー作。幼い息子アヨールを抱え、都会に行ったまま何年も帰らない夫ラシを待ち続けていたゾルの前に、見知らぬ男ガルが現われる。羊追いの仕事をガルに任せることにしたゾルは、熱を出したアヨールを病院へ運んでくれたことをきっかけに彼との距離を縮めていく。ところが、そこへ思いがけずラシが帰って来て……。", "2008-10-18", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO　WAY　BACK", "NO WAY BACK／逃走遊戯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "豊川悦司がニューヨークのヤクザに扮し、R・クロウと共演した日米合作映画。FBI捜査官ザックは、マフィアの首領サラーノの息子を殺した重要容疑者としてニューヨークのヤクザ組織のボス、ユウジを逮捕する。しかし、サラーノは復讐のため、ザックの息子を誘拐し、代わりにユウジの引き渡しを要求してくる。ザックはFBIと連絡を絶ち、ユウジを連れてロサンゼルスへと向かうが、途中、ユウジがスチュワーデスを人質に取り、砂漠に飛行機を不時着させる。逃げるユウジをサラーノ、ザック、そしてFBIも追うのだった。ビデオ、DVDタイトルは「逃走遊戯／NO WAY BACK」。", "1995-01-01", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOHNNY　ORO", "リンゴ・キッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "イタリアの鬼才S・コルブッチが「続・荒野の用心棒」と同年に撮り上げた遊び心あふれるマカロニ・ウエスタン。主人公は黄金をこよなく愛するキザなサウスポー・ガンマン、ジョニー。頑ななまでに拳銃の携帯を許さない保安官がいる町に足を踏み入れた彼は、かつての恨みを晴らそうとする男の策略によって窮地に追い込まれていく。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEETINGS WITH REMARKABLE MEN", "注目すべき人々との出会い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "20世紀の“魂の教師”として大きな影響を残した神秘思想家G・I・グルジェフの伝記映画。アルメニアに生まれ、幼い頃から超自然的なものに惹かれたグルジェフは、生の神秘を解き明かさんと各地を放浪し、ついに古代秘密教団のサルムング教団と出会う。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLUES BROTHERS", "ブルース・ブラザース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "ムショあがりのジェイクと相棒のエルウッドは、世話になった孤児院が資金難で税金を払えず、差し押さえ寸前なのを知り、昔の仲間を集めてバンドを再結成、コンサートを開いて金を集めようとするが……。怪優ベルーシが歌に踊りにその怪物的パワーを発揮、脚本も兼ねるエイクロイドとともに繰り広げるハチャメチャ・ミュージカル・コメディ。二人を殺そうとする謎の女のエピソード、惜しげもなく大量のパトカーをブチ壊していくカーチェイスなど、ドタバタ本来の面白さが味わえる。数々のヒット曲に合わせて顔を出すミュージシャンたちも見逃せない。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京失格", "東京失格", "", "", "http://image.eiga.k-img.com/images/movie/33606/315x210.jpg?4765", 90, "友人を亡くした2人の男たちが東京を巡る姿を描いたロードムービー。大学でバンドを結成して以来10年のつきあいのタックン、たかちゃん、エイジ。しかし、ある日突然エイジが他界してしまう。親友の死を受け入れることができず、酒に溺れてそのまま東京中をさまよい続ける2人。そんな彼らの元に、エイジの恋人だったカンナからメールが届く……。これまでカメラマンとして活躍してきた井川広太郎が監督・脚本・撮影を手掛ける。", "2006-08-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Papillon", "パピヨン", "", "", "http://ecx.images-amazon.com/images/I/51CE2MDTVML._SL210_.jpg", 151, "無実の罪で13年間の刑務所生活を強いられたアンリ・シャリエールの実話に基づく小説を、スティーブ・マックィーン、ダスティン・ホフマンの2大スターで映画化。監督は「猿の惑星」「パットン大戦車軍団」のフランクリン・J・シャフナー。胸に蝶の刺青があることからパピヨンと呼ばれる男。身に覚えのない殺人罪で終身刑を言い渡された彼は、自由を求めて脱獄を繰り返した末、親友のドガと共に脱獄不可能とされる孤島に送られる。", "1974-12-31", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("音楽人", "音楽人", "", "", "http://image.eiga.k-img.com/images/movie/55146/315x210.jpg?1264", 0, "メジャーデビューを夢見るバンド「ミ・センティー」のギター兼ボーカルを務める蒼は、ギタークラフトを学ぶ幼なじみの詩音と再会する。11年前の事故で記憶の一部を失った蒼と、病気のため歌手になることを諦めた詩音は、互いに惹かれ合うが、なかなか関係を発展できずにいた。そんな中、2人はメジャーデビューを賭けた大会に向け、新曲を完成させる。原作はケータイ小説作家ERINAの「大切な君へ　音楽人2008」と、森綾の「音楽人1988」。", "2010-05-15", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("永遠の法", "永遠の法", "", "", "http://image.eiga.k-img.com/images/movie/33666/315x210.jpg?4659", 114, "発明王エジソンが研究していた“霊界通信機”を完成させた少年たちが繰り広げる異次元への旅を描いた冒険ファンタジー。大川隆法の「永遠の法」を原作に、テレビアニメ「キャプテン翼」「カウボーイ・ビバップ」等で活躍してきた今掛勇監督がアニメ映画化。科学技術を学んでいる高校生の隆太は、仲間と共に訪れたニューヨークで、霊媒師から謎のメッセージを受け取る。それは、エジソンが考案した“霊界通信機”の設計方程式だった……。", "2006-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銭ゲバ", "銭ゲバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "『少年サンデー』に連載されたジョージ秋山の漫画はあまりにも反倫理・反社会的とされ、反響を呼んだ。金銭欲のためなら殺人をはじめとするあらゆる非道を辞さない銭ゲバのニヒルな生き方を描く。その銭ゲバに唐十郎、ヒロインには緑魔子と横山リエが起用されている。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEI DONNE PER L’ASSASSINO", "モデル連続殺人！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ファッションモデルの女性が仮面の男に惨殺された。彼女は他人のことまであれこれと日記帳に書く癖があったことから、モデル仲間はこの日記の奪い合いを始める。が、日記を手にしたモデルは次々と狙われていく……。イタリア耽美ホラーの鬼才バーヴァのモダン・ホラー。", "1964-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KING AND FOUR QUEENS", "ながれ者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "隻眼の巨匠R・ウォルシュ監督の豪快なウエスタン。お尋ね者のダン・キーホーは、追っ手を逃れて西部の町ダッチ・ストンにやって来た。そこで彼は強奪した10万ドルの金貨を隠したまま殺された４人兄弟の話を聞き、その在りかを探ろうと４人の未亡人たちの家に乗り込んでいく。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SPLIT", "汚れた七人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ロサンゼルスのフットボール試合場の売り上げ金強奪を計画した７人の男たちの、その息詰まるアクションを描いた好編。R・スタークの“悪党パーカー”シリーズ『七番目の男』の映画化。豪華な出演者のなかでも、D・サザーランドがイイ味を出している。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐来たり去る", "嵐来たり去る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "石原裕次郎、浅丘ルリ子が共演した舛田利雄の人情アクション。時は明治37年、日露戦争たけなわの頃。出征していった親友との約束を守り、その弟と妹の相談役になった板前・富坂の英五郎。彼は、親友の弟、妹、そして将来を誓い合った芸者・小春を守るため、悪どい小松川一家に戦いを挑んでいく。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虹をわたって", "虹をわたって", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1970年代前半を代表する人気スター、天地真理が主演し、喜劇作家として名高い前田陽一が監督した青春映画。横浜の労務者街に迷い込んだ金持ちの娘が騒動ののち、家に帰るまでを軽快に描く。天地の明朗な個性と、まわりを囲む人物たちの愛すべきキャラクターが魅力。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON JUAN", "ドンファン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "周囲の人に愛する気持ちを呼び起こしていく青年の愛の冒険を描いた寓話。現代のニューヨークに、伝説上の愛の貴公子ドンファンを名乗る男が現れた。21歳の彼は最愛の人に失恋したといい、ビルの屋上から投身自殺を図ろうとするのだが、精神科医に説得され精神病院で観察されることになる。そこで担当医ジャックの奇妙な診療が始まり、彼は、1500人に及ぶ女性たちとの愛の遍歴を語り始めるのだった……。異色なキャラクターで独自の道を開拓するJ・デップが現代のドンファンを好演。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO TO TANGO", "殺しのタンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "仏像を愛する殺し屋ジェームズは、新たな依頼を受けブエノスアイレスへと飛ぶ。彼はこの仕事を最後に引退するつもりでいた。さっそく、最後の標的の身辺調査を始めるが、次第に標的の情婦シシリアに惹かれていく。一緒にネパールへ行こうと彼女に告げるジェームズ。だが彼の知人が次々と殺され……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRAW DOGS", "わらの犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ペキンパー初の現代劇。平和主義を信奉する若き数学者デビッドは、暴力が横行するアメリカを捨て、妻エミーの故郷スコットランドに行く。だが、そこも安住の地ではなかった……。自衛のために戦ううち、自己の内に潜む暴力性に目覚め、その虜になっていく気弱な若者をホフマンが熱演。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRATEGIC　COMMAND", "エグゼクティブコマンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "副大統領を乗せた飛行機がテロ・グループにハイジャックされた。彼らは投獄されている組織のボスの釈放を要求、応じなければ細菌兵器を上空でばらまくと政府を脅迫する。タイムリミットは４時間。政府の指令を受けた軍の情報部顧問リックは、機内に潜入してテロリストたちとの戦いを開始する……。特殊部隊と武装テロ集団の対決を描くパニック・アクション。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SE SEI VIVO SPARA", "情無用のジャンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "北軍の輸送車から奪った金塊をめぐって、泥棒一味から足を洗った“ジャンゴ”が彼ら相手に大活躍する西部劇。イタリア国内でも西部劇としては初めて成人指定を受けたほどの残酷シーンが売り物で、インディアンの頭の皮を剥ぐシーンなどが話題となった。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("結び目", "結び目", "", "", "http://image.eiga.k-img.com/images/movie/55446/315x210.jpg?4132", 0, "絢子と啓介は、生徒と教師という立場でありながら激しくひかれあい、禁断の関係を結んでしまう。それから14年が経ち、互いに家庭を築いた2人だったが、ある日、運命的な再会を果たす。そして2人は、かつての感情を抑えきれずに再び関係を持ってしまう。演出・脚本・役者とマルチに活動する赤澤ムックと、「ポチの告白」の川本淳市が主演を務め、「童貞放浪記」の小沼雄一監督がメガホンをとる。", "2010-06-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラヴ・ノーツ　十二の果実。", "ラヴ・ノーツ　十二の果実。", "", "", "http://image.eiga.k-img.com/images/movie/55266/315x210.jpg?3390", 0, "人気ボーカルグループ「ゴスペラーズ」のミリオンヒットを記録したラブソング・コレクション・アルバム「Love NotesⅡ」の収録曲を映像化。ケータイ音楽ドラマ「DOR＠MO」で配信され、後に劇場公開される。阿部力と英玲奈がそれぞれ1人2役を演じる。国語教師の田所と音楽教師の佐藤は、音楽室の倉庫で眠るピアノから、古い原稿用紙と楽譜、交換日記を見つける。そこにはある男女の美しくもはかない恋愛の物語がつづられていた。", "2010-02-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼新娘／THE PHANTOM BRIDE", "ファントム・ブライド 鬼新娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "心が優しすぎるため、世渡りが下手で失意の日々を送っている朴。ある日、古道具店で机を買う。その引き出しの中にはある女性の遺書が入っていて、彼は返事を書く。そのことから起こる人間と幽霊の奇妙な三角関係を描いたSFXホラー。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチバンIV 最狂戦争", "ガチバンIV 最狂戦争", "", "", "http://ecx.images-amazon.com/images/I/61TwoKqyRjL._SL210_.jpg", 69, "", "2009-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tenacious D: In the Pick of Destiny", "テネイシャスD 運命のピックをさがせ！", "", "", "http://image.eiga.k-img.com/images/movie/53486/315x210.jpg?1778", 93, "「スクール・オブ・ロック」のジャック・ブラック率いる実在のバンド、テネイシャスDが大暴れする爆笑ロック・アドベンチャー。ロックンロールを求めてハリウッドにやって来たJBは、海岸でギターを演奏していたKGと意気投合してバンドを結成する。ビッグになることを誓い合った2人は、偉大なアーティストたちが手にしたという伝説のピックの存在を知り……。ベン・スティラー、ティム・ロビンスら豪華ゲスト陣にも注目。", "2008-07-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("屋根の上の赤い女", "屋根の上の赤い女", "", "", "http://image.eiga.k-img.com/images/movie/34196/315x210.jpg?3582", 30, "「トロイの欲情」でぴあフィルムフェスティバル2005準グランプリを獲得した岡太地監督が、文化庁による“若手映画作家育成プロジェクト”に選出され手掛けた短編映画。文具卸会社でアルバイトをすることになった青年・護摩は、そこで働く事務の女性・西山と出会う。独特な雰囲気を持つ西山に恋心を抱く護摩だったが、行動には移せずにいた。そんな中、彼女のミスをきっかけに問屋で大乱闘が発生。西山と護摩は屋根の上へと逃げ出し……。", "2007-09-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学忍", "学忍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "現代の高校を舞台に、細々と生きながらえてきた忍者たちの活躍を描くアクションコメディ。南忍学校に転校してきた吹雪美月はクラスになじめず、もんもんとした日々を送っていた。そんなある日、授業中に何者かによりクラスメイトが襲われ、それを機に次々と生徒たちに魔の手が伸びる。監督は「ビーオン」の佳本周也。", "2010-09-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEYOND", "ビヨンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "30年ぶりに実家に帰ってきたアーサー。だがその地下室には一つの腐乱死体が……。そして次々に起こる残虐な事件。また屋根裏部屋で見つかった古文書の謎とは？　L・フルチがリアルな特殊メイクを駆使して描くイタリアン・ホラー。", "1980-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヨムトシヌ DEATH COMIC PART1", "ヨムトシヌ DEATH COMIC PART1", "", "", "http://image.eiga.k-img.com/images/movie/54506/315x210.jpg?2358", 91, "とある漫画喫茶にある赤色のコミックについての都市伝説を描いたホラー。謎の赤い表紙のコミックには読む者にとって都合のいいことばかりが描いてあり、手にした者は続きをどんどん読み進めていく。しかしそのコミックに描いてあることが全て現実となり、血塗られた恐怖が待ち受けているという。親友の死をきっかけに赤いコミックの存在を知ったシズカとリョウコは、そのコミックの謎を追うが……。", "2009-03-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PLEASURE OF KILLING", "凶弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "偶然、出会った二人の殺し屋。一人は犯罪地区で育ち、麻薬のディーラーをしているルイス。もう一人は大学教授の職にありながら、退屈な日常に嫌気がさし、殺人を犯すアンドレス。二人は、意気投合し次々に殺人を犯していく。アンドレスの恋人、教え子、娼婦と、獲物を求めて街をさまよい歩くが、犯行が露見する。警察に追い込まれた二人は、バーで無差別な殺人を繰り広げたあげく、互いに銃を向けあう。そして、意外な結末が……。A・バンデラスが人気を得たことにより、ビデオ化された未公開のスペイン映画。", "1988-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダンプ渡り鳥", "ダンプ渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "「トラック野郎」に代わる“ダンプ野郎”たちの男気を哀愁たっぷりに描いた好編。子犬を轢くまいとして事故死した友人の遺骨を持って北海道にやって来た14トン・トレーラー乗りの男と、周辺の人々の人生模様が綴られていく。遺骨を海に流す友人の妻・宮下順子と失恋女子大生・原田美枝子が印象的だ。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ITINE´RAIRE D’UN ENFANT GA＾TE´", "ライオンと呼ばれた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "幼くして母に捨てられ、サーカスのきらびやかな世界に育ったサム・リヨン。清掃会社を設立し、裸一貫から成功を手にした彼は、いつも何かにチャレンジしていなくては気がすまない性格だった。50代を迎えたある日、彼はたった一人で大西洋横断に乗り出す。そして遭難したふりをして、最愛の娘がいるにもかかわらず、まったく新しい生活を築こうとするのだが……。J・P・ベルモンドが58歳にして取り組んだ一人の男の生きざま。彼の渋い演技に加え、ルルーシュとは「男と女」以来のコンビとなる、F・レイの流麗な音楽が映画を盛り上げている。", "1988-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("必殺！主水死す", "必殺！主水死す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "“必殺”シリーズの完結篇。仕事は惰性の連続、そろそろ今までの生活に区切りをつけようと考えていた主水。そんな中、町絵師の葛飾北斎が懐に十両の小判を持ったまま卒中で死に、立て続けに北斎なじみの彫師、摺師が謎の死を遂げる。お役所は３人を病死と判断するが、北斎の娘・お栄は摺師の家にあった１枚の似顔絵が、３人の死に何か関係があるとにらみ、主水に仕事を頼む。主水は街で似顔絵そっくりの捨蔵に出会うが、捨蔵は実は女で、その育ての親のお千代は、主水の昔の仕事仲間だった。一方、大奥では元老中の水野忠邦が、将軍の世継・家定の双子の片割れを探し出し、お家乗っ取りを企てていたが、その片割れこそ捨蔵だったのだ。同じ頃、120両という大金の仕事が舞い込むが、武家の跡目争いに関わる人殺しだったため、主水たちは仕事を断る。そして、その標的も捨蔵だった。捨蔵をめぐってうごめく陰謀の真相を探るべく主水は立ち上がるが……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユメノアリカ", "ユメノアリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "俳優としても活動する中村英児監督の青春映画。少年時代に埋めたタイムカプセルを掘り起こした５人の幼なじみが体験する出来事を、瑞々しい感性でファンタジックに描く。400人のオーディションを勝ち抜いた主演の佐藤広佳をはじめ、若手俳優たちのフレッシュな魅力も印象深い好編だ。純ら幼なじみの５人は、少年時代に遊んだ裏山がゴルフ場になる噂を聞く。最後の思い出に昔埋めたタイムカプセルを掘りにいくが……。", "2005-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUCK AND THE PREACHER", "ブラック・ライダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "南北戦争直後のテキサスが舞台。西部に移住しようとする黒人たちが白人の偏見や暴力と戦いながら進む姿を、元北軍軍曹の黒人ガイドを軸に、派手なガンファイトやインディアンとの関係などを絡ませて描く。S・ポワチエの初監督作品。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いさなのうみ", "いさなのうみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "高知県室戸の水産高校に通う廉次は代々、遠洋漁業を営む一家に育った。漠然と漁師になることを考えていた廉次は、鯨が来ると言った祖父が一人、船を出してしまうという一件を経験してから、急速に鯨にのめり込んでいく。そんな彼の前に、京子という不思議な魅力を持った転校生が現れる。彼女は鯨に関して博識だったが、捕鯨を考える廉次と鯨の保護を訴える彼女とは大きく意見が食い違った。ある日、廉次は訓練のために一人で船を出すが、誤って海に転落してしまう。意識もうろうとなった彼の目に、海の中を泳いでくる京子の姿が映る……。廉次と彼に恋する同級生、美智子、そしてひと夏だけの転校生、京子が紡ぎ出す青春模様を、過疎、生態系破壊といった社会問題を織り込みながらファンタジックに描き出す。主演は高知県内のオーディションで選ばれた二宮哲也。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("De Battre Mon Coeur S'est Arete", "真夜中のピアニスト", "", "", "http://image.eiga.k-img.com/images/movie/1526/315x210.jpg?2388", 108, "「天使が隣で眠る夜」「リード・マイ・リップス」のジャック・オディアール監督が、78年製作のハーベイ・カイテル主演フィルム・ノワール「マッド・フィンガーズ」をリメイク。主演は「スパニッシュ・アパートメント」「ルパン」のロマン・デュリス。28歳のトムは、犯罪すれすれの不動産の裏ブローカーをしながら、今は亡き母のようなピアニストになる夢を抱いている。彼はオーディションのためピアノのレッスンを再開するが。", "2005-10-05", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仁義なき戦い・完結篇", "仁義なき戦い・完結篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "本来は前作で終了する予定だったが、ヒットが続くので「完結篇」として本作が作られた。舞台背景になるのは昭和41年から46年まで。警察の頂上作戦でいちおう終結したかに見えた広島ヤクザ抗争は、組員の出所により再燃する。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dunya & Desie", "ドゥーニャとデイジー", "", "", "http://image.eiga.k-img.com/images/movie/54766/315x210.jpg?4301", 102, "オランダの人気TVドラマを映画化した女の子のロード・ムービー。厳格なイスラム教徒の家庭で育ったモロッコ出身のドゥーニャと、天真爛漫で自由奔放なオランダ娘デイジーは、性格は対照的ながらも大の親友同士。ある日、ドゥーニャは親が決めた親戚との見合いのため一時モロッコに帰郷することになる。一方、ボーイフレンドの子供を妊娠してしまったデイジーは、自分を置いてモロッコに行ってしまった父親に会うためドゥーニャの後を追うが……。", "2009-11-07", "70,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARK UNIVERSE", "ダークユニバース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "汚染された地球から人類の未来をかけ、探査船ノーチラス号が宇宙へ旅立った。しかし船は何千ものエイリアンの胞子に浸食され、宇宙飛行士スティーブの肉体も、それに冒されていた。ある夜、フロリダの湿地帯で住民が次々姿を消す事件が起こる。そこには、ノーチラス号の残骸と、変わり果てたスティーブの姿があった。「死霊の棲む館」をはじめ「ダークマン」「エルム街の悪夢／ザ・ファイナルナイトメア」の特撮を担当したS.O.T.A.FX工房が迫力ある映像をみせる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KG カラテガール", "KG カラテガール", "", "", "http://image.eiga.k-img.com/images/movie/55896/315x210.jpg?2082", 91, "「ハイキック・ガール！」の武田梨奈主演による空手アクション。伝説の空手家・紅宗次郎の家系に生まれた彩夏（武田）には、幼いころにナゾの集団によって父親を殺され、妹を連れ去られてしまったつらい過去があった。時は流れ、横浜で普通の女子高生として暮らしていたが、ある出来事をきっかけに妹と再会を果たす。しかし、妹はナゾの集団の手で殺人マシンに育てあげられていた。彩夏は妹を取り戻すため、たったひとりナゾの集団に立ち向かうことを決意する。", "2011-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("われらの時代", "われらの時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "兄はパリ留学の懸賞論文に入賞することを、弟は仲間たちと一緒に日本中を演奏してまわることを夢見て生きている兄弟。しかし、弟は総理大臣襲撃の計画に参加、仲間が爆死して警察に追われる身となる。そんな折り、兄に論文入賞の通知がくる。大江健三郎の書き下ろし長編小説の映画化。長門裕之が好演。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが青春のとき", "わが青春のとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "第二次大戦下の朝鮮半島の小都市を舞台に、現地人の信望厚い日本人青年医師と、金と権力志向のかたまりのような夫との生活に疲れはてた人妻との悲恋を描いた反戦色濃いメロドラマ。名門大映の破産から３年の歳月を経て新スタートを切った新生大映映画の第１回作品。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛇姫様", "蛇姫様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "城主が病に伏しているのにつけ込み悪事をたくらむ家老・佐伯佐衛門の次男を斬り、以後、旅役者一座にかくまわれながら佐衛門一味と対決する千太郎の姿を、一座の三味線弾き・お島との恋を絡めつつ描いた作品。これまでに衣笠貞之助監督、河野寿一監督らによって映画化された川口松太郎の原作を、早撮り、多作で知られる渡辺邦男が再映画化、講談調の面白い作品になっている。謎の剣豪に扮している黒川弥太郎が好演。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arthur et les Minimoys", "アーサーとミニモイの不思議な国", "", "", "http://image.eiga.k-img.com/images/movie/1906/315x210.jpg?1634", 104, "リュック・ベッソン監督が実写と3Dアニメを融合させた映像で描くファンタジー・アドベンチャー。冒険家のおじいさんに憧れる10歳の少年アーサーが、おじいさんが残した宝の地図を手掛かりに、体長2ミリの“ミニモイ族”の世界で大冒険を繰り広げる。「チャーリーとチョコレート工場」の名子役フレディ・ハイモアが主人公に扮するほか、マドンナ、ロバート・デ・ニーロ、デビッド・ボウイら超豪華キャストがミニモイたちの声を担当する。", "2007-09-22", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COMFORT OF STRANGERS", "迷宮のヴェニス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ベニスを舞台に、若い恋人たちが危険な男の罠に落ちていく悲劇を描いたエロティック・サスペンス。謎の紳士をC・ウォーケンが演じ、R・エヴェレットとN・リチャードソン（名女優ヴァネッサ・レッドグレーヴの娘）扮する若いイギリス人カップルを、倒錯した性の世界へ引きずり込んでいく。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花のあと", "花のあと", "", "", "http://image.eiga.k-img.com/images/movie/54816/315x210.jpg?4373", 107, "「たそがれ清兵衛」「武士の一分」で知られる藤沢周平による短編時代小説を、「青い鳥」の中西健二監督が映画化。自由恋愛が許されない江戸時代、東北の小藩で育った以登は、腕の立つ下級剣士・孫四郎に恋をしてしまう。しかし、以登には家が定めた許嫁がおり、孫四郎への想いを断ち切ろうとするが……。主演に北川景子、共演に甲本雅裕、バレエダンサーの宮尾俊太郎、歌舞伎役者の市川亀治郎、柄本明、國村隼ほか。", "2010-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　ばいきんまんと3ばいパンチ", "それいけ！アンパンマン　ばいきんまんと3ばいパンチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "やなせたかし原作の人気アニメーション劇場版第8作「それいけ！アンパンマン　空とぶ絵本とガラスの靴」と同時上映の短編。", "1996-07-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA NOTTE", "夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "結婚10年を迎えた作家のジョヴァンニと妻リディアは、病床の旧友トマゾを見舞う。リディアはかつてトマゾに深く愛されていたがジョヴァンニとの結婚の方を選んでいたのだった。トマゾに回復の見込みがないとわかったこの日以来、すべてがうまくいっていたジョヴァンニとリディアの間も歯車が狂い始める……。もはや取り返しようもない愛の孤独の中に沈んでいく二人の姿を乾いた筆致で綴っていく、アントニオーニの有名な“愛の不毛”３部作の１本。", "1961-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("谷岡ヤスジのメッタメタガキ道講座", "谷岡ヤスジのメッタメタガキ道講座", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "谷岡ヤスジのマンガを下敷きに、ナンセンスなギャグを散りばめたコメディ。朝から夫婦ゲンカが絶えないオラ山家からは、今日もどなり声が聞こえる。両親の姿を見て冷笑する息子・ガキ夫は学校へ出かけていくが、登校途中にクラスメイトのアサ子に立ち小便しているところを目撃され、学校でも騒動が……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METEORMAN", "スーパーヒーロー メテオマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "エメラルド色に輝く流星のおかげで突如、スーパーパワーを授かった平凡な教師が、人助けに、そして世界の正義を守るためにと、ハチャメチャな活躍を見せるB級SFアクション。第２のエディ・マーフィとの呼び声も高い、R・タウンゼントが監督・主演に大活躍。ILMが特撮を担当しているのも見どころの一つ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("老虎田鶏／DIRTY TIGER，CRAZY FROG！", "燃えよデブゴン カエル拳対カニ拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "デブゴンとクレイジー・フロッグは、いつも身に着けている、“どんな武器をも寄せつけない魔法の鎧”をある日盗まれてしまい、小悪党のダーティ・タイガーとともに捜索の旅に出る。やがて二人の前に現れたのは……。", "1980-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE MAN TATE", "リトルマン・テイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "生後数ヵ月にして字を読み、４歳で詩を書き、７歳になった今ではピアノでモーツァルトを完璧に弾きこなし、ゴッホも顔負けの油絵を描く天才少年フレッド。シングル・マザーの母親ディディにとってこの息子は自慢だったが、ある日児童心理学者のジェーンが現れたことから、際立った天才児フレッドの心が揺らぎ始める。自らシングル・マザーのもとで育ち、天才子役といわれ、早くから芸能界の表裏を見て来たフォスターの初監督作。自由闊達な作風が冴えており、監督としても一流のセンスを見せている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATHER OF THE BRIDE", "花嫁のパパ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "この世で最も大切な存在である娘に突然結婚を宣言され、とまどう父親。そんな父親の揺れ動く心と、周囲の人々との交流をユーモアとペーソスを交えて描いた作品。建築の勉強をするためにローマに留学していた娘が帰郷、父娘は久しぶりの対面をするが、娘は突然思いがけないことを口にする……。S・マーティンとD・キートンが絶妙な夫婦像を見せる。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Take Flight", "フットワーキングス", "", "", "http://image.eiga.k-img.com/images/movie/54596/315x210.jpg?4049", 70, "シカゴ発祥のダンス“フットワーク”を極めた若者キング・チャールズと、彼が率いるダンスチーム“フットワーキングス”のドキュメンタリー。犯罪の多い貧民地区で育ったチャールズは、過酷な戦いとハードな練習を積み重ね、弱冠19歳でシカゴのダンス界でフットワークのキングと呼ばれるまで登りつめる。ダンスを武器に世界に立ち向かう若者たちをカメラが捉える。", "2009-04-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ツンツン節だよ全員集合!!", "ツンツン節だよ全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "自称一流の手配師・碇田長吉は、“セントラル移動食品”の依頼により自分の故郷から４人の男を集めてくる。長吉は12人分のギャラをピンハネ。４人は豚小屋同然の部屋に押し込められ屋台を引いて奴隷のようにこき使われるが、ついに反撃に転じる。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE REPLACEMENT KILLERS", "リプレイスメント・キラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "C・ユンファのハリウッド進出１作目となったバイオレンス・アクション。製作総指揮には彼の主演作「男たちの挽歌」などで香港ノワールの世界を確立し、一足先にハリウッドで活躍する監督J・ウーが参加。スローモーションのガン・アクションなど、J・ウーのテイストが散りばめられている。極悪非道のチャイニーズ・マフィアのボス、ウェイに家族を狙われ、命じられるままに殺しを繰り返すヒットマン、ジョン・リー。だが、刑事スタンへの報復のため彼の７歳の息子を射殺しろという命令を聞くことはリーにはできなかった。組織を裏切ったリーは中国に住む母と妹を安全な所に逃がすように手配し、自らも国外逃亡を図るが、組織の追っ手と激しい戦いを繰り広げることに。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The “Reichsorchester”　The Berlin Philharmonic and the Third Reich", "帝国オーケストラ　ディレクターズカット版", "", "", "http://image.eiga.k-img.com/images/movie/53966/315x210.jpg?4647", 97, "創立125周年を迎えたベルリン・フィルハーモニー管弦楽団のヒトラー政権時代に注目したドキュメンタリー。第2次世界大戦後、指揮者フルトベングラーはナチスのプロパガンダに協力したとして戦犯会議にかけられた。この事実を、オーケストラの演奏家メンバーの視点から検証していく。当時を知る証言者として96歳となったバイオリニストのJ・バスティアン、86歳のコントラバス奏者E・ハルトマンら。", "2008-11-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("独立機関銃隊未だ射撃中", "独立機関銃隊未だ射撃中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "昭和20年８月のソ連国境。重機関銃トーチカ“キの３”の守備兵たちは、絶えず敵のすさまじい砲撃を受けながらも陣を死守していた。しかし、付近のトーチカは全滅し、連絡も途絶え、ついに戦力も失くした彼らは戦場の死角を求めて逃げ回る。“独立愚連隊”シリーズより連なる東宝戦争アクションの１本。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALWAYS 続・三丁目の夕日", "ALWAYS 続・三丁目の夕日", "", "", "http://image.eiga.k-img.com/images/movie/34056/315x210.jpg?1497", 146, "昭和30年代の東京下町に暮らす人々の姿を描き、日本中に感動を巻き起こした「ALWAYS　三丁目の夕日」の続編。監督の山崎貴をはじめ、吉岡秀隆、堤真一、小雪ら前作のスタッフ・キャストが再結集。昭和34年春。貧乏作家の茶川は、姿を消したヒロミを想い続けながら淳之介と暮らしていた。そんなある日、淳之介の実父である川渕が、息子を連れ戻しに来る。人並みの生活を条件に淳之介を預かった茶川は、再び純文学の執筆を始め……。", "2007-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUT OF BOUNDS", "ランナウェイ・18才の標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "アイオワの田舎で退屈な毎日を送る18歳のダリル。ある日、生きがいを求めてL.A.に向かった彼は、偶然にもヘロイン強奪犯のバッグを自分のものと取り違えてしまう。何も知らぬまま凶暴な犯人に追われ、ダリルの命懸けの逃避行が始まった。「すてきな片想い」「ブレックファスト・クラブ」のA・M・ホールが走り回るサスペンスフルなアクション。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談新耳袋殴り込み！劇場版　関東編", "怪談新耳袋殴り込み！劇場版　関東編", "", "", "http://image.eiga.k-img.com/images/movie/56486/315x210.jpg?4743", 92, "日本全国から集めた実話怪談集「現代百物語　新耳袋」を原作とした人気TVシリーズ「怪談新耳袋」のスピンオフ企画「怪談新耳袋殴り込み！」の劇場版。原作に収録された心霊スポットを探検する「新耳Gメン」のメンバーが幽霊を挑発し、心霊現象をカメラに収めるドキュメンタリー。「関東編」では、100％幽霊が現れるという群馬県の心霊スポットや東京・八王子のスポット、日本最大の自殺の名所・富士の樹海などに突撃する。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Proposal", "あなたは私の婿になる", "", "", "http://image.eiga.k-img.com/images/movie/54446/315x210.jpg?2626", 108, "ニューヨークの出版社に務めるカナダ出身の女編集長マーガレットは、ビザの申請を忘れて国外退去を命じられてしまう。そこで彼女は、気弱なアシスタントのアンドリューと結婚して何とか事態を乗り越えようとするが……。主演はサンドラ・ブロック、ライアン・レイノルズ。監督は「幸せになるための27のドレス」のアン・フレッチャー。", "2009-10-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新天龍八部／DORAGON　CHRONICLES", "シスター・オブ・ドラゴン　天女武闘伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "国際的に活躍するコン・リーが香港で活動していたときに主演したカンフー・アクション。武術界の最高峰、天山派の総裁ユーチィには、長女ハンワンと双子のチョースィとチョンホイの３姉妹がいた。だが、ハンワンがチョンホイに許されぬ愛情を抱いたために、ユーチィは彼女を連れて山にこもる。家族を破滅に追いやったハンワンと彼女に憎しみを募らせるチョースィの対立は、武術を駆使した壮絶な闘いへと展開し、さらに不老不死の秘術をめぐる争いにも巻き込まれていく。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eye of the Storm", "アイ・オブ・ザ・ストーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "両親を殺されたレイは、盲目の弟と砂漠のさびれたモーテルを経営している。そこを訪れた酔いどれの中年男と美しい妻。兄弟二人の静かな暮らしを脅かすこのカップルに、レイは殺意を抱くが……。「サイコ」を彷彿とさせる、乾いたタッチの心理サスペンス。D・ホッパーが脇役ながら、強烈な個性を発揮。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Killer Inside Me", "キラー・インサイド・ミー", "", "", "http://image.eiga.k-img.com/images/movie/55816/315x210.jpg?1527", 109, "スタンリー・キューブリック監督作「現金に体を張れ」（1956）の脚本にも参加したノワール小説の名手ジム・トンプソンによる小説「おれの中の殺し屋」を、「ひかりのまち」のマイケル・ウィンターボトム監督が映画化。1950年代の米テキサスを舞台に、好青年の保安官が自らの内に目覚めた衝動にかられ、冷徹な殺人鬼に変ぼうしていく姿を描く。「ジェシー・ジェームズの暗殺」のケイシー・アフレックが主演。ケイト・ハドソン、ジェシカ・アルバが共演する。", "2011-04-16", "1,14,3,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOYEUSES PA＾QUES", "恋にくちづけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "南フランスのコート・ダジュールを舞台に、中年実業家と若きパリジェンヌの甘い恋を軽快なテンポで描いたラブ・コメディ。大スター、ベルモンドを相手にマルソーが奮闘。上半身ヌードも見せて、ヤング・アイドルからの脱皮を図った意欲作。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("髪結いの亭主　デジタルリマスター版", "髪結いの亭主　デジタルリマスター版", "", "", "http://image.eiga.k-img.com/images/movie/55836/315x210.jpg?2296", 82, "フランスの名匠パトリス・ルコント監督の名を一躍世に知らしめたラブストーリー「髪結いの亭主」を、高画質＆高音質でよみがえらせたデジタルリマスター版。少年時代に女性理髪師に憧れを抱いて以来、理髪師との結婚を夢見てきたアントワーヌ。やがて中年を迎えた彼は、美しい理髪師マチルドに出会い、彼女を射止めることに成功する。それから10年間、マチルドとの愛に溢れた平穏な日々が過ぎていくが……。", "2011-01-08", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ストロベリーショートケイクス", "ストロベリーショートケイクス", "", "", "http://image.eiga.k-img.com/images/movie/33406/315x210.jpg?1450", 127, "魚喃キリコの同名コミックを、「三月のライオン」の矢崎仁司監督が映画化。大失恋を乗り越えて恋の訪れを待つフリーター里子、学生時代の男友達を思い続けるデリヘル嬢の秋代、過食症のイラストレーター塔子、恋に生きるOLちひろ。仕事も性格もまったく違う彼女たちの日常をリアルに描く。「ジョゼと虎と魚たち」の池脇千鶴をはじめ、中越典子、中村優子、岩瀬塔子が4人の主人公を演じる。人気恋愛小説家の狗飼恭子が脚本を担当。", "2006-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Herb & Dorothy", "ハーブ＆ドロシー　アートの森の小さな巨人", "", "", "http://image.eiga.k-img.com/images/movie/55666/315x210.jpg?3349", 87, "ニューヨークの小さなアパートで暮らすアート収集家夫婦を追ったドキュメンタリー。郵便局員のハーブと図書館司書のドロシー夫妻は、自分たちの給料で買える値段であること、そしてアパートに収まるサイズであることを条件に、約30年間で2000点以上ものアート作品を収集してきた。そのコレクションには名だたるアーティストたちの無名時代の作品が多数含まれており、ある日、アメリカ国立美術館から寄贈の依頼が舞い込む。", "2010-11-13", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jack and Jill", "ジャックとジル", "", "", "http://image.eiga.k-img.com/images/movie/57186/315x210.jpg?4033", 91, "アダム・サンドラーが双子の兄妹を1人2役で演じたコメディ。妻子に恵まれ広告代理店のビジネスマンとしても成功したジャックにとって、悩みの種は双子の妹でいつもお騒がせなトラブルメーカーのジルの存在だけ。そんなある日、ジルに付き合ってバスケの試合観戦にでかけたところ、会場に居合わせた名優アル・パチーノがジルに一目ぼれしてしまう。さらにジャックは、そんなパチーノにCM出演の依頼をしなければならなくなり……。ジャックの妻役でケイティ・ホームズが共演するほか、アル・パチーノが本人役で出演。監督は「ビッグ・ダディ」「エージェント・ゾーハン」などサンドラー主演作を多数手がけるデニス・デューガン。", "2012-01-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CIOCIARA", "ふたりの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "第二次世界大戦下のイタリア。夫を亡くし、女手一つで食料品店を切り盛りするチェジラは、娘を連れて田舎へ疎開する。間もなく戦争は終結。疎開先をあとに、ローマへと引き返す母娘だったが……。一人の男をめぐる母と娘の愛を絡めながら、戦争の混乱の中を生き抜く女たちの姿を力強く描いている。何より母親を演じるS・ローレンの力演が注目される作品で、彼女はアカデミー、カンヌの両方で主演女優賞に輝いた。", "1960-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("えんどうの花", "えんどうの花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「えんどうの花」など多くの名曲を残した沖縄の音楽家・宮良長包の人生を描いたドキュメンタリー・ドラマ。大正10年、38歳で沖縄県師範学校の音楽教師となった長包は、斬新な作品を次々と発表する一方で、全県各地への音楽行脚を開始する。やがて長包のメロディーは沖縄中で愛されるようになるが、次第に沖縄にも軍国主義の波が押しよせてくる。長包を演じるのは、西表島出身のシンガーソングライター、池田卓。", "2007-07-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("影法師・寛永寺の血闘／続・影法師 龍虎相博つ", "影法師・寛永寺の血闘／続・影法師 龍虎相博つ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 149, "賊に倒された幕府御金蔵の蔵番士の仇をとろうとする同僚の仙波龍之介が、途中で知りあった黙然と酒を飲み、興おこれば横笛を吹く浪人の天堂左近とともに犯人を追いかけていくという時代劇。阪東妻三郎が一人二役で、龍之介と左近を巧みに演じ分ける。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rak haeng Siam", "ミウの歌 Love of Siam", "", "", "http://image.eiga.k-img.com/images/movie/54646/315x210.jpg?3422", 154, "「レベル・サーティーン」で国際的に注目を集めたタイ人監督チューキアット・サックウィーラクンによる青春ドラマ。少年時代は親友同士でいつも一緒に遊んでいたミウとトン。姉の失踪をきっかけにトンが引越したことから2人は離れ離れになるが、高校生になったある日、思わぬ場所で再会を果たす。それぞれ悩みを抱えて生きてきたミウとトンにとって、お互いの気持ちを理解できる親友はかけがえのない存在となっていき……。", "2009-06-20", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PVC-1", "PVC-1 余命85分", "", "", "http://image.eiga.k-img.com/images/movie/54306/315x210.jpg?4423", 85, "南米コロンビアで実際に起きた事件をヒントに、ある一家を襲った誘拐事件の顛末を85分ワンカットで描いた衝撃サスペンス。人里離れた山間部で農園を営むバルデス家を武装集団が襲撃。犯人たちは一家全員を縛り上げ、妻の首にリモコン式のドーナツ型爆弾を装着して姿を消す。残されたカセットテープには、多額の身代金を払わなければ爆弾を爆発させて一家全員を殺害するというメッセージが吹き込まれており……。", "2009-03-14", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姐御", "姐御", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "藤田五郎の『女侠客』をもとに、目の前で愛する夫を殺された女が、自らも極道の世界に身を落とし、復讐をする姿を描き出した女性ヤクザ映画。主演は「化身」で映画デビューした黒木瞳。監督は「冒険者カミカゼ」以来、７年ぶりにメガホンを取った鷹森立一。個性的な役者陣が脇を固めたが、特に黒木の脱ぎっぷりが際立った。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プッチーニに挑む　岡村喬生のオペラ人生", "プッチーニに挑む　岡村喬生のオペラ人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "", "2012-05-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アラブの人々から見た　自衛隊イラク派兵", "アラブの人々から見た　自衛隊イラク派兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "シリア、レバノン、ヨルダン、エジプトの中東４ヵ国を巡り、日本の自衛隊の海外派遣についてアラブの人々にしたインタビューや、パレスチナ難民キャンプに暮らす人々を取材したドキュメンタリー作品。監督は、水俣病の発生した川でもある、新潟県・阿賀野川の川筋に生きる人々の姿を追ったドキュメンタリー「阿賀に生きる」で、日本を代表するドキュメンタリー監督の仲間入りを果たした佐藤真。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRUE BELIEVER", "トゥルー・ビリーバー はぐれ弁護士の執念", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "落ちぶれた弁護士が、過去に葬られていた正義と法への情熱を取り戻すプロセスをスリリングに描くハードボイルド・サスペンス。弁護士役に、個性派J・ウッズ、彼の助手に演技派の若手俳優R・ダウニーJr.が扮する。８年前の殺人事件から、麻薬取引の黒幕が浮かび上がってくる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("港の乾杯 勝利をわが手に", "港の乾杯 勝利をわが手に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "鈴木清太郎こと清順の処女作。競馬の八百長事件に巻き込まれた騎手の弟を元船乗りの兄が身を捨てて救う、というストーリー展開。典型的な歌謡映画ではあるが、後年につながる清順独自の奇妙なつなぎが随所に見られる。初期の清順はこの手の歌謡映画の量産を強いられ、雌伏の時を過ごす。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JACK", "ジャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "巨匠コッポラが手掛けた感動のヒューマン・コメディ。人の４倍の早さで成長する少年ジャックは10歳なのに、見かけは40歳の大人。両親は彼を守るため家から出さないように育ててきたが、ジャックは普通の子供と同じように学校に行って、友達と遊ぶ生活を願うようになる。小学校に入学した彼が次第にクラスメイトと仲良くなり、担任の女教師への初恋を経験したり、落ち込んだりしながらも精神的に成長する姿を、笑いと涙で綴っていく。少年の仕草、話し方、心の有り様を演じたR・ウィリアムズがさすがの好演。ジャックの家庭教師を演じたB・コスビーも出番は少ないものの、抑制の利いた演技で好印象を残す。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOOLAADE", "母たちの村", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "アフリカに残る風習で、母体を傷つける女子割礼に反対した女性の闘いを、力強いタッチで描いたシリアス・ドラマ。主演のファトゥマタ・クリバリは、実際に割礼を受けた女性のひとり。心の痛みを体現した彼女の迫真の演技に魅せられる。アフリカの村に住む女性コレの元に、割礼を嫌がる4人の少女が逃げ込んできた。因習にこだわる男たちは激怒し、村に混乱が広がる。やがてこの騒動はコレの娘の結婚話にも影を落とし……。", "2006-06-17", "2,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　LA　VERTICALE　DE　L’E´TE´", "夏至", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「青いパパイヤの香り」「シクロ」のトラン・アン・ユンが５年ぶりに手掛けた人間ドラマ。不倫中の長女、新婚早々倦怠期を迎えた次女、兄を敬愛し恋人とうまくいかない三女。何でも話し合う仲のいい三姉妹は亡き母の秘密の恋を知り、戸惑いの中で自分自身と向き合っていく。40、30、20代の三姉妹の愛に揺れ動く微妙な心情を、セリフを抑え、漂う雰囲気で物語る。香りや質感まで伝わってきそうなベトナムの日常風景を映し出す、みずみずしく官能的な映像が印象的。", "2000-01-01", "2,60", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こわい童謡　表の章", "こわい童謡　表の章", "", "", "http://image.eiga.k-img.com/images/movie/1936/315x210.jpg?2430", 74, "誰もが知っている“童謡”をモチーフに、名門女子高の合唱部で繰り広げられる惨劇を描いたサスペンス・ホラー。聖蘭女学院に転校してきた彩音は、奇妙な幻聴に悩まされていた。やがて、彼女の周囲で変死や失踪が相次ぎ……。民俗学者の長野隆之監修の下、「渋谷怪談」シリーズの福谷修監督がメガホンを取る。主演は「夜のピクニック」の多部未華子。事件の謎を解く後日談「こわい童謡／裏の章」が続けて公開される。", "2007-07-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CAPER OF THE GOLDEN BULLS", "宝石を狙え ゴールデン・ブル作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "かつて国際的銀行泥棒として鳴らしたピーターも、今は恋人グレイスとともに平穏に暮らしていた。ところが昔の仲間アンジェラが現れ、銀行襲撃の計画を打ち明ける。協力しなければ昔の悪事をばらすと脅迫されたピーターは仕方なく参加するが、危ういところをグレイスが助けるのだった。マッギバーンのベストセラー小説をもとにしたアクション。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー×仮面ライダー　フォーゼ＆オーズ MOVIE大戦 MEGA MAX", "仮面ライダー×仮面ライダー　フォーゼ＆オーズ MOVIE大戦 MEGA MAX", "", "", "http://image.eiga.k-img.com/images/movie/57236/315x210.jpg?3646", 95, "「仮面ライダー」誕生40周年となる2011年に放送を開始した最新作「仮面ライダーフォーゼ」とシリーズ前作「仮面ライダーオーズ」がタッグを組む劇場版。世界征服を企む謎の強敵に対しフォーゼ、オーズのほか歴代のライダーも集結し、激闘を繰り広げる。「ハロー！プロジェクト」の人気アイドル・真野恵里菜が「仮面ライダーなでしこ」に変身する女子高校生の美咲撫子役で出演する。", "2011-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAN", "ビーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "TV放映やビデオで人気を呼んだ『ミスター・ビーン』の劇場版。イギリスの美術館から買い戻された名画がアメリカに展示されることになった。美術館員のビーンはひょんなことから、その除幕式で挨拶をすることになり、米国へと飛ぶ。ビーンを迎えた現地の美術館員は彼を高名な美術学者と思い込み、丁重にもてなすが、ビーンは行く先々で騒動を巻き起こし……。TV版では、自己中心的な行動で笑わせたビーンだが、ここでは世話になった美術館員との友情の逸話が盛り込まれ、意外な“いい人”の面をのぞかせる。普段は無口なビーンの、除幕式で披露する苦し紛れの演説が笑える。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA TENTATION D’ISABELLE", "イザベルの誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "スイスのとある田舎町。青年ブルーノには愛する妻イザベルがいる。イザベルは貞淑で、穏やかな両親にも恵まれて幸福な家庭を築いていた。ところがある日、ブルーノはイザベルの昔の恋人アランを自宅へ招待してしまう。アランにはリオという妻がいて、一つ屋根の下で４人の男女が複雑な関係を呈するようになる。いったいブルーノは何を考えているのだろうか？　やがてブルーノの異様な愛の情熱は、彼の周囲の人間にも伝染しはじめる……。破滅的な男の愛の探求を、「ラ・ピラート」に匹敵する官能的なカメラワークで見せる魅力的なドワイヨン作品。彼の初期の作品群に見られるような“救いがたい悲劇性”はここでは見られず、放浪した愛は映画のラストで回帰することになる。音楽は常連のP・サルド。C・ゲンズブールがほんの数分間だけ顔を見せている。", "1985-01-01", "2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANKENSTEIN MUST BE DESTROYED！", "フランケンシュタイン 恐怖の生体実験", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「逆襲」（1957）に始まり、「復讐」（1958)、「怒り」（1963)、「死美人の復讐」（1967)、本作、「Horror Of Frankenstein」（1970)、「地獄の怪物」（1973）まで、全７作（ただし1970年作品は別キャスト＆スタッフ）が作られた、英ハマー・プロの“フランケンシュタイン”もの第５作。警察に追われる狂気の科学者フランケンシュタイン博士。彼は宿屋の女主人アナとその恋人カールを脅して、恐ろしげな人体実験を手伝わせる。目的は、脳外科の権威ブラント博士の脳を、生きたまま他人の肉体に移植すること。成功したかに見えた恐怖の生体実験は、モンスターと化した患者の逃走を機に、血みどろの死闘となった……。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("英雄伝説　空の軌跡 THE ANIMATION Vol.1", "英雄伝説　空の軌跡 THE ANIMATION Vol.1", "", "", "http://image.eiga.k-img.com/images/movie/57066/315x210.jpg?3197", 40, "日本ファルコムの人気RPGシリーズ「英雄伝説　空の軌跡」をアニメーション化。全2巻のOVAを劇場で上映する。さまざまなエネルギーを生み出す「導力器（オーブメント）」を用いた兵器開発に各国がしのぎを削り、世界は覇権を狙う各国の思惑がうずまき混迷としていた。そんな中、民間人の安全と地域の平和を守るギルドの「遊撃士（ブレイサー）」として活動するエステルとヨシュアは、世界の裏で暗躍する謎の結社「身喰らう蛇」と対峙することとなる。", "2011-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トウキョーノワール", "トウキョーノワール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "東京の街で痛みを抱え、愛を求める女性たちの姿を綴る短編集。女性としての生き方に悩む30代半ばのキャリアウーマン、恋人を奪われた悲しみから風俗嬢になった女子大生、まったく違った生き方をする同姓同名の奈緒とナオ。「Pain／ペイン」の石岡正人、「HOBOS」の熊澤尚人が、“二面性”をテーマに、悩みながらも前進していく女性たちの性と心理を繊細に描く。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Texas Chainsaw Massacre 2", "悪魔のいけにえ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "電動ノコギリが人間を襲う恐怖で、観客を狂気の世界へと導いたT・フーパーの出世作「悪魔のいけにえ」の続編。前作での忌まわしい事件から13年後、あの殺人鬼一家が生贄を求めて、再び暗躍する。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2 OU 3 CHOSES QUE JE SAIS D'ELLE", "彼女について私が知っている二、三の事柄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "出演 解説１ 「メイド・イン・USA」と同時に製作されたゴダール監督作品。4千世帯を収容するパリ郊外の団地で夫と子供ふたりと暮らすジュリエット。夫にとって自慢の妻だが、昼間は売春をして買い物を楽しんでいる主婦のひとりだった。アメリカがパリにもたらす夢の虚像、68年の革命への予感を孕んだ本作は、大島渚監督「絞死刑」との作品交換で日本初公開された。", "1970-10-03", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("反逆児", "反逆児", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "無声時代からの時代劇の巨匠・伊藤大輔の会心作。群雄割拠の戦国時代、徳川家康の子として若くして名将の誉れ高い三郎信康が、今川義元の血を継ぐ母・築山御前と、織田信長の娘・徳姫との板挟みの中で苦悩し、ついには信長に疎んじられ死ぬまでを、悲壮美あふれる映像で描いた。織田を憎み、信康を盲愛する築山御前と、気位の高い徳姫との葛藤、信長に命じられて家名を守るため、築山御前と信康を死に追いやる家康の苦悩など、戦国時代の非人間的な厳しさの前で、力関係と宿命に苦悩する人間の心理を的確に描き出した。信康を演ずる中村錦之助、築山御前の杉村春子、徳姫の岩崎加根子の演技も見ごたえ十分。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("豚の報い", "豚の報い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "芥川賞を受賞した又吉栄喜の同名小説を崔洋一が映画化。沖縄の伝説を軸に、独特のユーモアを漂わせながら、家族の絆、人と人の触れ合いを温かく綴る。沖縄の小さなスナックに突然豚が飛び込んできて、襲われた従業員の若い女性が失神してしまう。偶然居合わせた豚小屋で生まれた大学生の正吉は、豚がもたらすという厄を落とすため、神の島、真謝島に行くことを提案する。彼はそのついでに、12年前に風葬された父の骨を拾いに行こうと考えていた。正吉とスナックのママ、従業員の女性二人は、それぞれの心の傷を癒す旅に出る……。名指揮者、小澤征爾の息子、征悦が初主演ながら、旅を通して成長していく青年を好演し、存在感を放つ。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAUVE QUI PEUT（LA VIE）", "勝手に逃げろ／人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1970年代に政治へ走り商業映画から遠ざかっていたゴダール監督が、復活を告げた快作。主人公は都会を離れ田舎で生活を始めようとしているドゥニーと、その愛人でテレビ局に勤めるポール、田舎から都会に出てきた娼婦のイザベル。彼ら３人のそれぞれの人生が、“勝手に逃げろ”と“人生”をモチーフに、『想像界』『不安』『商売』『音楽』の４つの章の中で描かれている。ゴダールの1970年代の作品を総括するほか、1980年代の作品への基礎的要素を持つ重要な作品。コマ落としによるストップモーションやスローモーションを多用した、躍動感あふれる映像も印象的だ。日本では長い間、未公開であったが1995年に公開された。", "1979-01-01", "2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オトシモノ", "オトシモノ", "", "", "http://image.eiga.k-img.com/images/movie/33676/315x210.jpg?4668", 94, "駅で落し物を拾った人間が次々と姿を消してしまうという怪奇現象を人気絶頂の沢尻エリカ主演で描くサスペンスホラー。小学生の孝は駅のホームで定期券を拾った後、行方不明になる。その後、孝と最後まで一緒にいた友達の範子まで、行方不明になり、範子の姉である奈々は妹を捜すため孝の家にいくが、そこで、目撃したのは恐ろしい形相に変わり果てた孝の姿だった……。監督は黒沢清らに師事した新鋭古澤健。", "2006-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BETRAYAL OF THE DOVE", "殺人病棟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "離婚女性の心のスキをついた恐怖の出来事を描くショッキング・スリラー。一人娘を連れて夫と別れたエリーは、ジェスというハンサムな青年医師との出会いによって離婚の傷を癒す。ところが、その後、エリーの周りで謎めいた事件が続発。疑惑に憑かれた彼女は、愛するジェスをも疑い始め……。ミステリアスな医師ジェスに扮したB・ゼーンの研ぎ澄まされた存在感が、何とも不気味。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Like Father Like Son", "ハモンド家の秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ジャック・ハモンドは有能な心臓外科医。その息子クリスは学校の成績はイマイチだが、スポーツマンの明るい高校生。こんな二人が、ヒョンなことから、姿かたちはそのままで中身だけが入れ替わってしまう。クリスは優等生になり、かたやジャックはメスも持てぬ外科医となり、クレジットカードを使って乱痴気騒ぎ、という大変な毎日を送ることになる。入れ替わって知った世界で、二人とも精いっぱい生きようとするが、失敗を重ねるばかり。当時、ブームとなった入れ替わりコメディ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大虐殺", "大虐殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "関東大震災の混乱の中で起きた“甘粕事件”及び、社会主義者や朝鮮人たちを虐殺した“亀戸事件”に憤慨した大杉栄の舎弟たちが、テロリストとなって軍部に復讐しようとする姿が描かれる。アナクロ戦争大作からピンク映画まで手掛けた小森白監督の代表作。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents すき　「LATE SHOW」", "桃まつり presents すき　「LATE SHOW」", "", "", "http://image.eiga.k-img.com/images/movie/57886/315x210.jpg?1310", 26, "若手女性監督による映画製作・上映集団「桃まつり」が、「好き」をテーマに女性監督たちが撮り上げた短編作品を上映する「桃まつり presents すき」の1本。あこがれていた叔父の3回忌の夜、まもるは旧友のひろしとともに、叔父の所有していたビルの屋上で、叔父が集めたアメリカ映画を鑑賞していた。そこへ地元のバイカーたちとたむろする少女が現れ、まもるは少女に興味を抱くが……。監督は調布映画祭で入選した「ポンの氾濫」（2009）の佐藤麻衣子。「参のすき」として「さめざめ」（星崎久美子監督）、「SAI-KAI」（名倉愛監督）と同時上映。", "2012-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("骨壺", "骨壺", "", "", "http://image.eiga.k-img.com/images/movie/58116/315x210.jpg?1735", 0, "若者から支持を集める作家・山田悠介の同名小説（角川文庫刊「8.1 Horror Land」所収）を、「AKB48」の松原夏海、「アイドリング!!!」の横山ルリカ、「SUPER☆GiRLS」の宮崎理奈、「AeLL.」の篠崎愛の人気アイドル共演で映画化。地味で目立たない女子高生の絵里は、幼なじみでクラスの人気者の美津子を慕っていたが、いまだ口をきいてもらうこともできずにいた。美津子が教師からストーカー行為を受けていると知った絵里は、美津子を守るため「その中の遺灰を飲み込んだ人間は3日以内に必ず死ぬ」といわれている呪いの骨壺に手を出すが……。", "2012-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こちら葛飾区亀有公園前派出所 THE　MOVIE", "こちら葛飾区亀有公園前派出所 THE　MOVIE", "", "", "http://ecx.images-amazon.com/images/I/312MD9E8RML._SL210_.jpg", 94, "人気コミックに基づくTVアニメの劇場版。葛飾の名物巡査長、両津勘吉の型破りな活躍を描いたアクション・ムービー。両さん役のラサール石井はもちろん、伊東四郎やヒロインを演じるともさかりえなど、豪華なキャスティングにも注目。平和な東京・葛飾の町で爆破事件が発生。犯人は、なぜか同じ商会のビルだけをターゲットにしていた。両津巡査はFBIの爆弾処理エキスパート、星野リサとともに捜査に乗り出すが……。", "1999-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DAWNING", "青い夜明け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "大戦直後の南アイルランド。伯母や祖父とともに大きな屋敷で暮らすナンシーは、ある日、父かもしれない謎の男キャシアスに出会い、二人の間には不思議な友情が生まれる。だがある痛ましいテロ事件に遭遇した彼女は、祖国アイルランドが抱える切実な問題に直面していく。少女の成長、そして揺れるアイルランドの運命を、確かな演技陣と力強い自然の見事なアンサンブルで描く。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gunfight at the O.K. Corral", "OK牧場の決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "トゥームストンの町はずれにあるOK牧場。末弟ジムを闇討ちにされたワイアット・アープとその弟バージルは、肺病に冒された賭博師ドク・ホリディの助っ人を得て、クラントン一味と対決する。アメリカ開拓史上で名高い決闘を描いたアクション西部劇。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents うそ　「迷い家」", "桃まつり presents うそ　「迷い家」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "", "2010-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER IN THE FIRST", "告発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "若きエリート弁護士ジェームズが担当することになったのは、アルカトラズ刑務所で起きた殺人事件。25年の刑で服役中のヘンリーという若い囚人が脱獄計画を密告した仲間を殺害、死刑は、ほぼ確実だという。そんな勝ち目のない裁判を任された彼だが、極度の精神的ダメージから言葉を失い、怯え切ったヘンリーに会いショックを受ける。面会を繰り返していくうちに、言葉を取り戻したヘンリーから、アルカトラズでの悪夢のような日々が明かされていく。その真相に激しい憤りを感じたジェームズは、ヘンリーの無罪を主張し、逆に刑務所そのものを告発するのだった。1941年に実際に起きたヘンリー・ヤング裁判をもとに作られた問題作。ジェームズ役のC・スレーター、ヘンリー役のK・ベーコンがともに渾身の演技を見せている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAMILY PRAYERS", "家族の祈り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ギャンブル好きの父のため、12歳の少年アンドリューの家庭は取り立て屋に脅かされる毎日。当然のごとく両親の仲も悪くなっていった。なんとか家族をまとめようと努力するアンドリューだったが、とうとう父が家を出ていってしまう……。家族の幸せを取り戻そうと苦悩しつつ成長していく少年の姿を描いた、静かな感動を呼ぶファミリー・ドラマ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　とべ！とべ！ちびごん", "それいけ！アンパンマン　とべ！とべ！ちびごん", "", "", "http://ecx.images-amazon.com/images/I/61XCaMxKI0L._SL210_.jpg", 40, "「それいけ！アンパンマン」の劇場版シリーズ第3弾。雨の守り神ドラゴンが住むというみどりの島。ある日ばいきんまんは、この島にあるという「緑の珠」を狙ってやって来るが、そこにいたのは空も飛べない子どものドラゴン・ちびごんだった。ばいきんまんから逃げようとして海に流されてしまったちびごんは、アンパンマンに助けられる。島を守るため、アンパンマンたちと一緒にばいきんまんに立ちむかうことを決意するちびごんだったが……。", "1991-07-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bugsy", "バグジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "カジノの街ラスベガスをつくりあげた実在のギャング、ベンジャミン“バグジー”シーゲルの半生を、「レインマン」のバリー・レビンソン監督がウォーレン・ビーティを主演に迎えて映画化した作品。1930年代のニューヨークで暗躍していた殺し屋バグジー。ボスの命令でハリウッドへ乗り込んだ彼は、そこで出会った女優バージニアと恋に落ちる。彼は2人で訪れたネバダ州の砂漠に、巨大なカジノ付ホテルを建設しようと考えるが……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ペンタの空", "ペンタの空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "日本で生まれたペンギンのペンタと、ペンタを故郷に帰すべく南極まで旅立つ少年亮太との交流を中心に、その心温まる冒険の旅を感動的に描いたファミリー・ピクチャー。TV『わくわく動物ランド』などで知られる永田貴士による、ペンギンの生態をこまやかに捉えた映像が見どころ。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Echelon Conspiracy", "エネミーオブU.S.A.", "", "", "http://image.eiga.k-img.com/images/movie/55316/315x210.jpg?1476", 95, "アメリカの国家機密といわれる軍事情報システム「エシュロン」をテーマにしたサスペンス・アクション。ある日突然、マックスのもとに携帯電話が送られてきた。その携帯電話の指示に従って行動すると、次々に大金を手にすることができる。だが、ある日マックスは、カジノで大儲けをしている最中に、カジノの警備員・ジョンとFBI捜査官のグラントに捕まってしまう。2人はマックスを利用し、携帯電話に届く情報の発信源を突き止めようとするが……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECOND CIVIL WAR", "セカンドインパクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "内戦の危機に立たされたアメリカ国内の緊張感を描くサスペンス・アクション。インド、パキスタンで核戦争が勃発。大量の難民がアメリカへと向かうが、移民にかねてから反対していたアイダホ知事ファーレイはこれを拒否する。彼の態度は政治問題にまで発展し、ファーレイは州境を閉鎖。これに対し、大統領は72時間以内に封鎖を解かなければ軍事行動に出ると言い出し……。危機的状況の中で不倫相手のTVリポーターに振り回される知事や、頼りない大統領など、様々な人間模様がブラックな笑いを交えて展開する。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VANISHING POINT", "バニシング・ポイント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "車こそ生きがいという男が、デンバーからサンフランシスコまでの車の陸送の仕事を引き受ける。限られた時間と警官の執拗な追跡のなか、時速200キロという猛スピードで飛ばしていくが、行く手にはバリケードが！　スピード感満載のカー・アクション映画の快作。1997年、ロックバンド、プライマル・スクリームが本作にインスパイアされた同名アルバムと主人公の名前を冠したシングル『コワルスキ』を発表。それに伴い、ビデオも再発売された。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝ野麦峠", "あゝ野麦峠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 154, "明治から昭和初期にかけ、長野県の中小製糸工場で低賃金と長時間労働に耐えながらも、日本の輸出産業を支えた女工たちの悲話を描いた、山本茂実の同名ルポルタージュの映画化。けなげで純情な娘役を大竹しのぶが好演、原田美枝子がそれと対照的な役柄をこなす。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SO I MARRIED AXE MURDERER", "ハネムーンは命がけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "好きになった女が殺人鬼ではないかと疑う男の姿を描くサスペンス・コメディ。女運の悪さを嘆いていた詩人のチャーリーは、ある時、一人で肉屋を経営するハリエットと知り合い、恋に落ちる。彼女を紹介するため、両親の家に訪れたチャーリーは、ふと目にした新聞の連続殺人の記事に驚く。そこに書かれていた３人の夫を続けて殺した犯人の特徴がハリエットにぴったりだったのだ。一度は別れることを決めたものの友人の警官トニーにたしなめられ、ハリエットと結婚したチャーリーだったが……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MESSENGER OF DEATH", "メッセンジャー・オブ・デス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アメリカの田舎町で起きた新興宗教の教徒９人の惨殺にまつわる謎を解くべく、調査を開始する新聞記者の活躍を描くハードボイルド・ミステリー。製作のパンチョ・コーナー、監督のJ・リー・トンプソン、そして主演のC・ブロンソンのトリオが手掛ける６本目の作品でもある。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("友へ・チング", "友へ・チング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "1970年代から80年代の釜山を舞台に、幼なじみ4人の少年がたどる哀しくも激しい青春を、ドラマティックに描き出す感動作。腕っ節が強く頼れるジュンソク、ケンカッ早い葬儀屋の息子ドンス、そして真面目な優等生サンテク、お調子者のジュンホは仲良し４人組。別々の中学に進むも高校で再会した４人だったが、不良に絡まれたサンテクを助けようとしたジュンソクとドンスが大乱闘を起こし退学処分に。そしてジュンソクとドンスは黒社会に足を踏み入れていく。友情、嫉妬、葛藤やコンプレックスなど、成長過程で誰もが経験した感情を、ノスタルジックな描写をふんだんに盛り込みながら繊細にすくいとっている。韓国では社会現象になるほどの大ヒットを記録した。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Diary of the Dead", "ダイアリー・オブ・ザ・デッド", "", "", "http://image.eiga.k-img.com/images/movie/53736/315x210.jpg?4454", 95, "「ナイト・オブ・ザ・リビングデッド」（68）「ゾンビ」（78）などで知られる“ゾンビ映画の父”ジョージ・A・ロメロ監督が、主観撮影法を用いて描く新たなゾンビ映画。山奥でホラー映画を撮影していた学生たちは、ラジオで“世界各地で死者が蘇り、人々を襲っている”という報道を聞く。彼らは真実を残すため、カメラで惨劇の記録を試みるが……。出演は米人気TVドラマ「Lの世界」のミシェル・モーガン、「エミリー・ローズ」のジョシュ・クローズら。", "2008-11-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.P’S　DANCING　SUSHI　BAR", "Mr.Pのダンシングスシバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「あふれる熱い涙」の田代廣孝の監督第２作。前作同様、“異国”における夫婦の絆にスポットを当て、繊細にして味わい深い人間ドラマに仕上げている。ロスで寿司屋をかまえる元ベトナム帰還兵の黒人男性と、彼の妻である日本人女性。苦楽をともにしてきた中年夫婦が、店の成功と引き換えに失ったもの、そして発見したものを見つめていく。戦争の悪夢から逃れないでいる男の心情を託したタイトルの意味を明かすラスト・シーンも感慨深い。", "1998-01-01", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN　TO　BOOGIE", "ボーン・トゥ・ブギー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "70年代の英国ロックを代表するアーティスト、マーク・ボラン率いるT.REXのパフォーマンスを中心に、セッションを織り込んだ音楽映画。監督も務めたリンゴ・スターとボランのやり取りや、ジョン・レノン邸で演じた寸劇にも注目。'72年、T.REXはウェンブリーでライヴを敢行。いたってシンプルなステージに1万人のファンが詰め掛け、ヒット曲に熱狂する。", "2005-07-30", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野狼／COLOR　OF　PAIN", "カラー・オブ・ペイン／　野狼〈オオカミ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "一匹狼の殺し屋、龍也が暗殺の依頼を遂行中に重傷を負う。一発の銃弾が脳に留まったまま生きるはめになった彼は、街で偶然出くわした銀行強盗団の一員になる。そんな龍也の前に、彼と同じく怖いもの知らずの若い警官ジョーが現れ……。癒されぬ心の傷を抱えた男たちの友情と葛藤を、キレ味鋭い銃撃シーンを盛り込んで描き上げたハードボイルド・アクション。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beowulf", "ベオウルフ　呪われし勇者", "", "", "http://image.eiga.k-img.com/images/movie/53086/315x210.jpg?3346", 113, "「バック・トゥ・ザ・フューチャー」「フォレスト・ガンプ／一期一会」のロバート・ゼメキス監督が英語最古の英雄叙事詩を、前作「ポーラー・エクスプレス」同様、モーションキャプチャー技術を駆使して映画化。6世紀のデンマークを舞台に勇者ベオウルフと呪われた巨人グレンデル、そしてその母親との死闘を描く。ベオウルフに「ディパーテッド」のレイ・ウィンストン。その他、アンソニー・ホプキンス、ジョン・マルコビッチ、アンジェリーナ・ジョリーら豪華キャストが共演。", "2007-12-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マジック・ツリーハウス", "マジック・ツリーハウス", "", "", "http://image.eiga.k-img.com/images/movie/56116/315x210.jpg?2065", 105, "1992年から発行されているアメリカの人気児童書を日本でアニメーション映画化。本と勉強が大好きで気が弱い兄のジャックと、冒険好きでどんな動物とも仲良くなれる妹のアニーは、ある日、森の木の上に不思議な小屋を見つける。そこは時空を超えてあらゆる場所へ移動できる魔法のツリーハウスで、ネズミの姿に変えられてしまったモーガンと出会った2人は、モーガンを救うためさまざまな時代や場所をめぐる冒険に出る。女優の北川景子と人気子役の芦田愛菜が主人公兄妹の声優に挑戦。", "2012-01-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スリーピングフラワー", "スリーピングフラワー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "これが劇場監督デビュー作の柿本ケンサクが、悩める姉妹の自分探しの旅を綴った感動ドラマ。心の病を抱えた姉の静養に付き添った妹が、緑豊かな田舎町の人々の愛情に触れ、自らも癒やされていく。グラビア出身の工藤里紗が、映画初出演とは思えないナチュラルな存在感で印象を残している。都会で暮らす姉妹、物静かな千春と活発な深雪。ある日、千春が田舎での休養を医者に勧められ、深雪は姉と共に静養先へ向かうが……。", "2005-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIXIE LANES", "想い出のデキシー・レーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "1945年、第二次大戦が終結して皆の気持ちがウキウキしている時、ある小さな町に家族からも町の人々からも厄病神扱いされていた密造酒作りの名人が復員してきた。男と家族、そして町中を巻き込んでの奇想天外な泥試合が始まり……。古き良きアメリカの人情を描いたコメディ作品。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("廟街故事／THE MEAN STREET STORY", "男たちの挽歌−外伝−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "黒社会の巣くつで生まれ育った青年ミュウ。父親がヤクザの闘争で殺され、その姿が脳裏に焼き付いて離れない彼は、ヤクザからの甘い誘いを拒み続けていた。だが、魔手は次第に彼をヤクザ世界へと巻き込み、闘争心に目覚めた彼は親友と手を組み故郷を牛耳るボスに闘いを挑む。黒社会の抗争を描いたハード・アクション。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT AND DAY", "夜も昼も", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "アメリカが生んだ偉大な作曲家コール・ポーターの半生を描いた伝記作品。『アメリカのいとこ』でブロードウェイ・デビューを果たした後、第二次大戦、結婚と経ていく間に次々とヒット曲を作り出していく姿をドラマティックに描き出す。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLI INDESIDERABILI", "狼たちの鎮魂歌〈レクイエム〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "実話を基にした硬派なギャング映画。'51年に米国からイタリアに強制送還されたマフィアたちの人生を、回想を織り込みつつ描く。悪になりきれなかった者たちの人間ドラマが味わい深く、哀切な後味を残す。チョイ役ながら重要なキャラとして出演しているヴィンセント・ギャロの好演にも注目。米国を追放され、イタリアに戻ってきた伝説のマフィアたち。ひとりのジャーナリストが彼らを追跡。やがて意外な事実が判明し……。", "2005-07-09", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEEL DAWN", "スティール・ドーン 太陽の戦士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "L・ホール監督が製作を兼任する近未来格闘映画版「シェーン」。師を殺され、復讐の旅に出た戦士ノーマッド。やがて彼は近未来戦争で夫を失った未亡人が営む農場に身を置き、迫り来る悪と対決することになる。黒澤映画の影響を受けたという作品世界にもご注目。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jude", "日蔭のふたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "イギリスの文豪トーマス・ハーディによる小説「日陰者ジュード」を、マイケル・ウィンターボトム監督が映画化。19世紀イギリス。大学進学を目指す青年ジュードは、アナベラという娘に誘惑され、妊娠を理由に結婚させられてしまう。だが当然その結婚はうまくいくはずもなく、すぐに破局。働きながら勉強を再開したジュードの前に、ある日いとこのスーが現れる。2人は激しい恋に落ちるが、彼らには過酷な運命が待ち受けていた……。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽戦隊サンバルカン", "太陽戦隊サンバルカン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 28, "機械帝国ブラックマグマから世界を守るため結成された地球平和守備隊の特殊部隊・サンバルカンの活躍を描いた人気テレビシリーズの劇場オリジナル版。ブラックマグマのヘルサターン総統が、サンバルカン分断作戦を発動させた。攻撃の指揮をとることになったアマゾンキラーは、誘拐した子供たちを餌にバルイーグルひとりを浜名湖へとおびき出すことに成功。サンバルカンのそろわない東京に、ブラックマグマの魔の手が伸びる。", "1981-07-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHAPLIN PUZZLE", "チャーリー・チャップリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "偉大なる喜劇王チャールズ・チャップリンの本質を探るべく作られた映画。PART1「チャップリンの秘密」と題したドキュメンタリーと、PART2「警察官」（短編）から構成されている。イングリッシュ・ミュージック・ホールのパフォーマーとして舞台で成功した後に、1913年に映画を作るためカリフォルニアにやって来たチャップリン。彼はいかにして、映画におけるアイデンティティーを探していったか。すべてが一夜にして生まれたものでなく、絶え間ない努力と工夫がなされたことを明らかにしていく。", "1992-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("捨て犬", "捨て犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "Vシネマの帝王・竹内力による劇場用映画初プロデュース作品。暴力と裏切りに満ちた裏社会を舞台に、本来の職務と極道の仁義との狭間で揺れ動く潜入捜査官の葛藤を描く。1000発を超える銃弾を使用した銃撃アクションの凄まじさに加え出演者が全員男という硬派なドラマにも圧倒される。潜入捜査官としてヤクザ組織に潜り込んだ谷原は、同じ組織の男・神崎の信頼を得、しだいに極道の世界へ足を踏み入れていくが……。", "2005-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名高きろくでなし", "悪名高きろくでなし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "竜巻は週刊誌のトップ屋だったが、仕事をほされていた。そんな彼がニセ札事件に巻きこまれ、警察に追われるはめに……。ストーリーは次から次にとんでもないことが巻き起こり、マンガチックな展開となる。宍戸錠が黒づくめの衣装で、らしからぬ雑誌記者を怪演。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PAPER CHASE", "ペーパー・チェイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ハーバード大学のエリート学生のキャンパス・ライフ、とりわけ試験を全面的に描いた異色の青春映画。想像を絶する試験勉強ぶり、教室での教授とのやりとり、そして教授の娘との恋愛。そのすべてが戦いであるというキャンパス・ハードボイルド。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Myway Highway", "Myway Highway", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "2009年8月のウルフルズ活動休止後、ソロミュージシャンとして活躍するトータス松本を追ったドキュメンタリー映画。2010年春、楽曲制作のため渡米したトータス松本と共同プロデューサーの須藤晃。2人がロサンゼルスのアパートに部屋を借りて暮らした約1カ月間を密着取材し、ミュージシャンとプロデューサーが限られた時間のなかで作品をつくりあげていく過程の一部始終を映し出していく。", "2010-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秒速5センチメートル", "秒速5センチメートル", "", "", "http://ecx.images-amazon.com/images/I/51EtYpp9RUL._SL210_.jpg", 60, "互いを思いあっていた貴樹と明里は、小学校卒業と同時に離れ離れになってしまう。それから時が過ぎたある雪の日、貴樹は明里に会いにいくことを決意する。貴樹と明里の再会の日を描いた「桜花抄」、その後の貴樹を別の人物の視点から描いた「コスモナウト」、2人の魂の彷徨（ほうこう）を描いた表題作の3本からなる連作アニメーション。監督・原作は自主制作の「ほしのこえ」が高く評価された新鋭・新海誠。", "2007-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELTER SKELTER", "ヘルター・スケルター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "女優シャロン・テイトがチャールズ・マンソン率いる新興宗教集団に惨殺された事件をドキュメンタリー・タッチで描いた異色作。ハリウッドのビバリー・ヒルズでS・テイトの惨殺死体が発見されて二ヵ月後、別の事件で逮捕されたヒッピーの証言から、事件の背後に異端の教義を説く男の姿が浮かび上がる。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇之王／KING　OF　COMEDY", "喜劇王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "香港のコメディスターのチャウ・シンチーが、さえない役者に扮し、香港映画界で奮闘する姿を面白おかしく描いたラブ・コメディ。映画のエキストラにさえなれない役者のワンは、ホステスのピュウピュウに演技指導をする。二人はいつしかイイ感じになるのだが、ワンのプロ根性がスター女優の目に留まり、映画主演に抜擢されることになって……。ジョン・ウー監督の「狼／男たちの挽歌・最終章」の銃撃戦などをパロディー化し、笑いを誘う。冒頭にジャッキー・チェンが合格するエキストラ役でカメオ出演しているが、これは本作とジャッキー主演の「ゴージャス」の撮影現場が近かったという理由で、主演俳優同士が互いの映画に特別出演したという経緯から実現したもの。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニワトリはハダシだ", "ニワトリはハダシだ", "", "", "http://ecx.images-amazon.com/images/I/51Y1K8E90NL._SL210_.jpg", 114, "処女作「喜劇・女は度胸」以来、庶民の悲喜こもごもをパワフルに描き続けてきた名匠・森崎東の６年ぶりの監督作。知的障害を持つ少年を中心に、家族の絆、人と人の心の触れ合いを、警察の汚職事件を交えながら描き出す。丹念に積み重ねられた気取りのない日常描写。人間の真の姿を丸裸にするような、そのユニークな視点と鋭い洞察力は今もなお健在で、観終わった後にはすがすがしい感動が広がる。粗野だが人情味あふれる父親役の原田芳雄、包容力があり気丈な母親役の倍賞美津子ら、個性的な俳優陣の競演も絶妙だ。重度の知的障害者である15歳のサムは父親と二人暮らし。別居中の在日韓国人の母と妹に会うことが何よりの楽しみだ。ある日、抜群の記憶力を持っているサムは、そのために警察の汚職事件に巻き込まれ……。", "2004-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'Elisir d'Amore", "World Classics＠CINEMA 愛の妙薬", "", "", "http://image.eiga.k-img.com/images/movie/55106/315x210.jpg?2726", 123, "オペラやバレエの名舞台を映画館で上映する「World Classics＠CINEMA」の1作として、世界的に知られるイギリスのオペラハウス、グラインドボーン音楽祭で2009年に上演されたドニゼッティ作曲のオペラ「愛の妙薬」をデジタルシネマ化。美しい村娘アディーナが純朴な青年ネモリーノの真実の愛に気づくまでの顛末をコミカルに描く。テノール・アリアの傑作「人知れぬ涙」や、場面ごとに着替えるアディーナの衣装も見どころ。", "2010-04-03", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAWS ３−D", "ジョーズ3−D", "", "", "http://ecx.images-amazon.com/images/I/41JCCAESNFL._SL210_.jpg", 98, "“ジョーズ”シリーズ第３作。フロリダの海上公園シーワールドのレジャー施設“海底王国”に巻き起こる白いサメの恐怖を描いた作品。今回は“アイビジョン3D”という立体映画方式を駆使し、突如、出現する恐怖のサメの姿が、よりリアルに映し出され話題となった。DVDタイトルは「ジョーズ３」。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABOVE THE WAR", "ブルドッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "戦時下のベトナムで機密事項のマイクロフィルム奪取に向かう５人の特殊部隊。アメリカ軍と共産国軍による激戦のなか、両軍を敵にまわして任務にあたる荒くれ男たちの勇気と友情の物語。香港などアジア諸国で活躍する倉田保昭が、隊員の一人として出演。ふだんは板前というユニークな役柄だ。", "1992-01-01", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE　NOTE", "BLUE NOTE／ハート・オブ・モダン・ジャズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ジャズ界を代表する名門レーベル“ブルーノート”の軌跡を追ったドキュメンタリー。1939年にドイツ系移民、A・ライオンとF・ウルフによって設立されたレコード会社が、いかにして“ジャズの代名詞”的存在となったかを、ジャズの興亡史とともに検証していく。セロニアス・モンクやアート・ブレイキー、ソニー・ロリンズ、オーネット・コールマン、ハービー・ハンコックら、老舗レーベルにゆかりのある超有名プレーヤーたちの演奏シーンやコメントも数多く収録。", "1998-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブレイクブレイド　第六章「慟哭ノ砦」", "ブレイクブレイド　第六章「慟哭ノ砦」", "", "", "http://image.eiga.k-img.com/images/movie/56006/315x210.jpg?4429", 52, "吉永裕ノ介の人気コミックを劇場版アニメ化したシリーズ全6部作の最終作。ボルキュス大隊の脅威が迫るなか、シギュンはライガットの生還を信じ、ゴゥレム・デルフィングの新兵器を鍛え上げていく。新型装甲をまとい、最終形態へと変ぼうしたデルフィングを操り、ライガットはボルキュスとの最後の戦いへと赴く。", "2011-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beginners", "人生はビギナーズ", "", "", "http://image.eiga.k-img.com/images/movie/56786/315x210.jpg?2839", 105, "ユアン・マクレガー主演で人生を前向きに生きようと変化してく人々の姿を繊細に描いた人間ドラマ。38歳独身で奥手なオリバーは、母に先立たれ5年がたったある日、ガンの宣告を受けた父からゲイであることをカミングアウトされる。衝撃を受けたオリバーは事実をなかなか受け止められず臆病になってしまい、運命的な出会いを果たした女性アナとの関係も自ら終わらせてしまう。しかし、真実を告白した父は残された人生を謳歌し、その姿を見たオリバーは自分の気持ちに正直に生きることを学んでいく。監督は「サムサッカー」のマイク・ミルズ。主人公の父親を演じたクリストファー・プラマーが、第84回アカデミー賞で助演男優賞を受賞。受賞時82歳で演技賞史上最高齢でのアカデミー賞受賞となった。", "2012-02-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIRACLE ON 34TH STREET", "34丁目の奇跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1947年に製作された同名のクリスマス・ムービーのリメイク。ニューヨークのメイシー百貨店がクリスマス前に行う街頭パレードは、市民が毎年楽しみにしている名物イベント。しかしサンタ役の男が酔いつぶれて、イベントの責任者は大弱り。そこにクリスと名乗る白ヒゲの太った老人が現れ、自分は本物のサンタクロースだと言う。パレードで見事サンタ役を務めたクリスはクリスマス・セールの会場でも大人気。その一部始終を苦々しく見ていたライバル・デパートは、クリスを精神異常者に仕立てようとする。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("落下する夕方", "落下する夕方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「幻の光」のプロデュースを手掛けた合津直枝の監督デビュー作。若い女性の圧倒的な支持を得た江國香織の同名小説を原作に、恋人を失ったヒロインの魂の再生を静謐なタッチで綴っている。恋人、健吾と一緒に暮らし始めて４年目のリカがある日突然、彼から別れ話を切り出された。そして独りマンションに残されたリカの元に、健吾の心を奪った張本人の華子が押し掛けてくる。奔放な言動を連発する華子との奇妙な同居生活を続けるうちに、リカの心は不思議と癒されていく。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火龍／THE　LAST　EMPEROR", "火龍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「ラストエンペラー」でも描かれた、清朝中国の最後の皇帝、溥儀の生涯をたどる伝記ドラマ。３歳で皇帝に即位し、清朝崩壊後は満州国の皇帝となり、終戦後は戦犯として服役した後、53歳で初めて平民となった溥儀。生計を立てるために庭師となり、愛妻とともに試練に耐える、そんな数奇な人生のドラマを「西太后」のリー・ハンシャン監督が描き出す。", "1987-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルーズ・ソックス", "ルーズ・ソックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "「タイムリープ」の監督・今関あきよしが自らの小説を脚色、演出した青春ストーリー。万引きが見つかり、逃げる４人の女子高校生、PiNK、ピース、スケちゃん、カクちゃん。コンビニ店員に追われ、ひたすら走る彼女たちに起こる出来事をコミカルに描きながら、ヒロインPiNKの心の葛藤をヴィヴィッドに映し出していく。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カスタムメイド 10.30", "カスタムメイド 10.30", "", "", "http://ecx.images-amazon.com/images/I/51PE93NWB1L._SL210_.jpg", 121, "2004年10月30日に広島市民球場で奥田民生のコンサートが開催された。熱狂の渦に巻き込まれた単独ライブのドキュメントと、ある姉妹のドラマを交えて描いた異色の音楽ムービー。映画初主演となる人気シンガー、木村カエラの瑞々しい演技と、奥田民生の音楽が心地よい体験をさせてくれる。マナモとみなもの姉妹が10年ぶりに一緒に暮らすことに。昔から仲の悪い二人の前に自称“天使”が現れ、奥田民生のライブに行けと告げる。", "2005-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大津波のあとに", "大津波のあとに", "", "", "http://image.eiga.k-img.com/images/movie/57386/315x210.jpg?1580", 74, "2011年3月11日に発生した東日本大震災の発生から2週間後。被災地の仙台、東松山、石巻を中心に、未曾有の大津波が残した傷跡を記録したドキュメンタリー。全校児童108人中74人が津波に飲まれてしまった石巻市大川小学校を中心に撮影された。同じく震災直後の被災地を記録した「槌音」と同時上映。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悲夢（ビモン）", "悲夢", "", "", "http://image.eiga.k-img.com/images/movie/53476/315x210.jpg?2445", 93, "「サマリア」「うつせみ」などで海外で高評価を受けるキム・ギドク監督が、「ゆれる」「蟲師」のオダギリジョー、「私たちの幸せな時間」のイ・ナヨンを主演に描くラブストーリー。別れた恋人を忘れられないジンと別れた恋人を憎むランは、夢を共有しあうという不思議な関係で結ばれていた。精神科医は、「2人が愛し合えば夢は消える」とアドバイスするが……。", "2009-02-07", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SADGATI", "遠い道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "10億もの人口を擁するインドにあって近代化を大きく妨げているのが、“カースト制度”。なかでもバラモン（僧侶）を崇める“バラモン絶対主義”は深刻で、いまだに解放を求める過激な運動は絶えない。S・レイ監督は、こうしたインド社会の矛盾を題材にしたプレームチャンドの短編小説『サドガディ』をほぼ忠実に映画化。バラモンの命令によって重労働を課せられ、過労のために死んでしまう農夫の姿を描く。バラモン絶対主義に対する痛烈な皮肉を込めた一編。", "1981-01-01", "21,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("48 Hrs.", "48時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　サンフランシスコの刑事ジャックは脱獄犯ガンツとその仲間を追うが、彼らに逃げられた上、同僚を殺害されてしまう。怒りに燃えるジャックは捜査のために、かつてガンツの仲間だった服役囚レジーを48時間だけ仮釈放させる。切れ者の悪党レジーとタフな刑事ジャックは喧嘩を繰り返しながらも、次第に友情を深め、犯人に迫る……。男たちの追跡行を描くバディ・ムービーの傑作。映画デビューを飾ったエディ・マーフィはここからスターダムにのし上がった。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("静かな生活", "静かな生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "1994年のノーベル賞に輝いた大江健三郎の同名タイトル作品を義兄でもあり、高校の同窓生でもある伊丹十三が映画化。脳に障害を持ちながら、美しい音楽を作るイーヨ。妹のマーちゃんは、彼の音楽が美しいのは、イーヨの魂がこの世で一番美しいからだと信じ、一人では生きていけない兄に一生寄り添って生きていこうと決心している。そんな二人を核に、両親の長い留守中に起こる波瀾万丈の冒険物語が繰り広げられていく。イーヨ役の渡部篤郎とマーちゃん役の佐伯日菜子が好演。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2000MANIACS", "2000人の狂人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「血の祝祭日」に続くルイスのスプラッタ・ムービー第２作。南北戦争中、北軍によって虐殺された町の亡霊が、100年に一度よみがえり、通りかかった北部出身の人間を血祭りにあげる。車でその町を通りかかった３組のカップルは、歓迎を受けホテルに泊まるが、その晩から“祭り”が始まるのだった。女の腕を笑いながらナタで切り、クギを打った樽に男を入れて転がし、大岩で女を圧死させる……。演出の明るさが一種の狂気を逆に強調して背筋をゾッとさせる。ホラー史上に残る傑作の１本だ。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THIEF OF BAGDAD", "バグダッドの盗賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "「イントレランス」などのグリフィス映画で助手を務めたあと、監督になったR・ウォルシュが注目されるきっかけとなったヒット作。アラビアンナイトをもとに、D・フェアバンクスが盗賊に扮して胸のすく活躍をみせる。", "1924-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽を抱け", "太陽を抱け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ミュージシャンとして成り上がろうとする青年たちの姿を、恋を交えて描いた青春喜劇。解散したチューリップ座のもとメンバー、森山と千葉は、強引にオリオン・レコードに売り込み、“オリオン・スターズ”を結成。新曲もヒットし、千葉はレコード会社の社長令嬢・洋子に惹かれていくが……。当時、歌にドラマに活躍中の朝丘雪路が魅力的。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乱熟女子大生", "乱熟女子大生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "マッチの炎を見ると淫乱になってしまうという、特異体質の女子大生を主人公にしたエロティック・ドラマ。不倫旅行に出かけた姉マリの代役として、スナックでバイトすることになった地味めのミキが、不思議な性癖を利用されて複数の男性と関係を持つ。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Heaven’s Gate", "天国の門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 149, "　1890年、ワイオミング。東欧からの移民が押し寄せる中、家畜業者の会議が開かれ、牛泥棒という名目で移民125名の死刑執行が決定される。これに立ち向かう保安官のアベリル。町には傭兵たちが集結し、ついに戦争の火蓋が切られる……。「ディア・ハンター」で名声を得たマイケル・チミノ監督がアメリカ史の恥部と言われた“ジョンソン郡戦争”を題材にして作り上げた西部劇超大作。興行的失敗で製作元のユナイト映画は倒産。219分の完全版も88年に日本公開された。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Joan of Arc", "ジャンヌ・ダルク", "", "", "http://image.eiga.k-img.com/images/movie/1796/315x210.jpg?3046", 157, "「レオン」のリュック・ベッソン監督が、フランスの国民的英雄として知られる女性ジャンヌ・ダルクの生涯をミラ・ジョボビッチ主演で映画化。百年戦争下のフランス。小さな農村で暮らす信仰心のあつい少女ジャンヌは、イギリス軍に家族を殺され親戚のもとへ身を寄せる。やがて17歳になったジャンヌは、教会で神の声を受け、神の使者としてシャルル王太子に謁見することに。軍を率いて戦場へと向かったジャンヌは、イギリス軍を相手に劇的な勝利をおさめるが……。", "1999-12-11", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NANNY", "妖婆の家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "名女優B・デイヴィスが謎めいた老婆に扮して、観客に恐怖を募らせる心理サスペンス・スリラーの会心作。表向きはホームドラマのスタイルを採りつつも、館を舞台にしたホラー作品のような怖さを引き出している。実業家夫妻とその子供が住む自宅には、老婆の乳母がいたが、息子は乳母を嫌っていた。ある日、父が出張し母が病気で入院して乳母と二人だけに。やがて、恐ろしい事態が発生する。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("芳草碧連天／THE GREEN GREEN GRASS OF HOUSE", "ジョイ・ウォンの緑の大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "昔ながらの米作りを続ける貧しい村に、リンユンという女性が訪れる。彼女は花の栽培で村をたて直すことを提案するが、村長はじめ村の年長者たちに拒否される。一人ツオウヨンという若者が、彼女の提案に耳を傾けた。二人はともに村を花で埋めつくそうと働き、いつしか恋が芽生える。初々しいJ・ウォンが美しいラブ・ストーリーを見せてくれる。", "1987-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mon petit doigt m'a dit...", "アガサ・クリスティーの奥さまは名探偵", "", "", "http://image.eiga.k-img.com/images/movie/33636/315x210.jpg?2108", 105, "ミステリー作家アガサ・クリスティによる人気シリーズ“おしどり探偵トミー＆タペンス”の中の1作「親指のうずき」を、舞台をフランスに移して映画化。のどかな田園地帯で優雅な生活を送るおしどり夫婦が、1枚の風景画を手がかりに、老婆失踪事件の捜査に乗り出す。好奇心旺盛な奥さまプリュダンスを「女はみんな生きている」のカトリーヌ・フロが、おっとりした性格の夫ベリゼールを「アメリ」のアンドレ・デュソリエが演じる。", "2006-09-09", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Way We Were", "追憶", "", "", "http://ecx.images-amazon.com/images/I/51KhGPWXCbL._SL210_.jpg", 118, "バーブラ・ストライサンドとロバート・レッドフォード共演による恋愛ドラマ。1937年、大学の創作クラスで出会ったケイティ（ストライサンド）とハベル（レッドフォード）の、その後20年にわたる愛の変遷を激動の時代を背景に描き出す。監督は「雨のニューオリンズ」「ひとりぼっちの青春 」のシドニー・ポラック。共演はロイス・チャイルズ、ブラッドフォード・ディルマン。", "1974-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヤングおー！おー！・ 日本のジョウシキでーす！", "ヤングおー！おー！・ 日本のジョウシキでーす！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "「ヤングおー！おー！」は桂三枝がホストを務めた大阪の人気公開テレビ番組。その題名をいただいた吉本興業の人気お笑いタレント総動員のドタバタ喜劇。日本を脱出して世界旅行を計画する二人の男が資金かせぎに失敗、ヤケを起してモーターボートで脱出、やがて孤島に漂着する。藤本義一、ドクトル木崎国嘉も出演。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いとしのエリー", "いとしのエリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "皮ジャンにバイク姿というハイセンスなルックスの新任女教師・串田枝里子こと通称“エリー”と、落ちこぼれ生徒・晋平の恋の行方を描いた学園ラブ・ロマンス。監督の佐藤雅道、脚本の藤長野火子、主演の国生さゆりとも、これがデビュー作である。サザンオールスターズの同名曲にインスパイアーされた作品。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POCIAG DO HOLLYWOOD", "モンローに憧れて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "長距離列車のウエイトレスをしているメリリンは女優志望の女の子。「お熱いのがお好き」を見て以来、マリリン・モンローに憧れ、彼女のようになりたいと思い続けている。しかし映画アカデミーの試験では、歯並びが悪いせいで落とされる始末。そんな彼女のもとに、ある朝ビリー・ワイルダー監督から、電話がかかってくる……。ポーランドを中心に活躍するR・ピポバルスキー監督が、ヨーロッパ生まれのB・ワイルダー監督への熱いオマージュを込めた作品。", "1987-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A TALE OF WANDERINGS", "テイル・オブ・ワンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "19世紀より昔の話。孤児のマイとマルタ姉弟の家が悪党に襲われ、弟マイが誘拐されてしまう。彼には隠された金の場所を感じとる超能力があったのだ。マルタは友人とともにマイを救出する旅に出るが……。道中マルタが遭遇するドラゴンや魔女など、ファンタスティックなキャラクターがいっぱいのソ連映画。", "1983-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUTH　BOSTON", "サウス・ボストン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "元ニュー・キッズ・オン・ザ・ブロックのD・ウォールバーグが主演した硬派な青春映画。かつて抗争でサウス・ボストンを出た青年ダニーが、久々に故郷に戻ってくる。しかし病弱の母親、借金を抱えた弟、ワガママな妹は、もはや家族崩壊寸前だった。元恋人に無視され、就職もままならないダニーは、図らずも破滅的な運命をたどることになる。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大江戸喧嘩纏", "大江戸喧嘩纏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "美空ひばりの艶のある歌声が全編を彩る痛快時代劇。め組の頭領として名を馳せる辰五郎、女だてらの纏振りのお雪、美男の纏持ちの新三が、鉄火の喧嘩場へと命賭けで飛び込んでいく様を描く。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トラック野郎・突撃一番星", "トラック野郎・突撃一番星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "原田美枝子がマドンナを演じる一編。イルカの調教師・えり子に首ったけの桃次郎は、彼女の幼なじみのトラック野郎・玉三郎を助手に使って接近。だがえり子には養殖アワビの研究家・駿介という意中の人がいたのだった。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COP AND A HALF", "コップ・アンド・ハーフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "B・レイノルズが４年ぶりに主演した刑事ものコメディ。監督は1970年代の人気TV『ハッピー・デイズ』でスターとなったH・ウィンクラー。レイノルズ扮するマイアミの刑事が、殺人現場の唯一の目撃者である８歳の少年とコンビを組んで事件を解決していく。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌姫魔界をゆく", "歌姫魔界をゆく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "16ミリのデビュー作「喜談・南海變化玉」で一部ファンの熱狂的な支持を得たカルト・ムービー作家、長嶺高文の第２作。二人の歌姫ミッキーとドナルド、そして彼女らのマネージャーは魔界に迷い込んでしまう。彼女らを待ち受けるのは人喰い魔女や巨大怪獣たち。しかし実は歌姫らもそれぞれ吸血鬼の子孫であり、オオカミ男の末裔だった。歌姫一行と魔女たちの追いつ追われつの死闘が始まる……。キッチュでけばけばしいセットのなかでのスラップスティックなドタバタアクションも発表当時は一般にはまったく無視されたが、物語よりキャラクターを重視したこの怪作、一見の価値あり。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLOATING　LANDSCAPE", "恋の風景", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "恋人を失い、人生の喜びを失ったヒロインの葛藤と再生を、香港の女性監督キャロル・ライが繊細なタッチで描き出す。国際的にも評価の高い台湾の絵本作家ジミーが、本作のためにオリジナルのイラストを書き下ろし、劇中ではアニメ化されて登場。作品を象徴する、重要な役割を果たしている。病死した恋人が遺した絵の風景を求めて、恋人の故郷にやってきたマンは、絵本作家を目指す郵便配達の青年、シャオリエと心通わせる。", "2005-03-26", "6,8,76,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猫耳 neko-mimi", "猫耳 neko-mimi", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "男二人女二人の虚ろな若者が朽ちかけた部屋の中で、いつ果てるともなく角砂糖を積んでいる。カメラという機械と、そのアナロジー＝眼球の解剖。そこへ第３の女が現れ、彼らの生活は乱される。つり下げされた第３の女の葬送の儀式の果てに、彼らは入水自殺する……。「NOW HERE」「スピノザのレンズ」など８ミリの実験的な短編でも知られるノイズミュージシャン黒澤潤が、16ミリで初めて撮った長編映画。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE BEAU SERGE", "美しきセルジュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ヌーヴェル・ヴァーグの最初期を飾る、C・シャブロルの記念すべき長編デビュー作。華やかな都会から一転、雪に閉ざされた故郷に帰ってきた青年を軸に、息詰まるような４人の男女の愛憎を鮮烈な映像表現の連続で描く。J・C・ブリアリ以下、ヌーヴェル・ヴァーグの３大スターの魅力が弾ける。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE MUSE´E GREVIN", "グレバン蝋美術館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "昼下りのカフェテラスで一人の男が居眠りを始める。その男の夢の中に蝋人形館が現れ、人形たちが動き出す。人形たちは自分のモデルとなる人物を探し出そうとパリの街をさまよい始めた……。J・ドゥミが、コクトー、バローの出演協力で作り上げた異色短編。", "1958-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Drive", "ドライヴ", "", "", "http://image.eiga.k-img.com/images/movie/57216/315x210.jpg?1795", 100, "「きみに読む物語」「ブルーバレンタイン」のライアン・ゴズリング主演で、昼はハリウッド映画のカースタントマン、夜は強盗の逃走を請け負う運転手として働く孤高の天才ドライバーが、愛する女性を守るため裏社会を相手に命がけの戦いを繰り広げる姿を描いたクライムサスペンス。デンマーク出身の新鋭ニコラス・ウィンディング・レフン監督が手がけ、2011年・第64回カンヌ国際映画祭で監督賞を受賞した。", "2012-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くれないの盃　完結編", "くれないの盃　完結編", "", "", "http://image.eiga.k-img.com/images/movie/54076/315x210.jpg?3675", 72, "お笑いコンビ品川庄司の庄司智春、スピードワゴンの井戸田潤、バットボーイズの佐田正樹が主演するヤクザ映画。前編と完結編の2部にわけて公開される。雷轟一家の組長・牧原総一郎は自分の体調不良から跡目を選ぶことにするが、若頭の門倉勇三、幹部の柿崎、実子の牧原翔一、3年の勤めを終えて一家へ帰ってきた景山晃の候補4人の誰を選ぶかで悩む。そんなある日、新興勢力の桜田総業が土地の利権問題にからみ、雷轟一家の縄張りに足を踏み入れてきて……。", "2008-10-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH ANXIETY", "メル・ブルックス 新サイコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ブルックスの喜劇は一種のパロディーの面白さがある。例えば「ブレージング・サドル」は西部劇、「ヤング・フランケンシュタイン」は怪奇映画、「メル・ブルックス　珍説世界史Part１」は史劇、といった具合にそれぞれが笑いのネタになっている。そしてこの作品は、サスペンスの巨匠ヒッチコックの映画をネタに撮り上げたものだ。様々な事件に巻き込まれる高所恐怖症の精神病療養所長を主人公に、「めまい」「サイコ」「北北西に進路を取れ」、さらには「鳥」「マーニー」などの有名なシーンの数々がユーモラスに再現される。あくまでパロディーに徹した爆笑コメディで、主演ブルックスはもちろんのこと、リーチマン扮する婦長もオカシイ。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛はクロスオーバー", "愛はクロスオーバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "F3レースの世界を舞台に、７年前、夫を事故で失った女、女の夫を死なせたレーサー、死んだ夫のレース仲間だった男という３人が織りなすラブ・ロマンス。本編ではあらかじめ脚本の中に音楽のイメージが組みこまれ、音楽と映像が溶け合うように配慮された。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRAND CANYON", "わが街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "ロサンゼルスに住むマックとクレアの夫婦。家族や隣人と様々な問題を抱えノイローゼ気味になっていたクレアだったが、ある日捨て子を見つけ家に連れ帰ったことから、彼女の人生に大きな変化が起こる……。平凡な街の平凡な人々の生き様を厳しく、かつ優しいタッチで描いた人間讃歌ともいえる感動作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("荷車の歌", "荷車の歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "全国に広がる農業共同組合の婦人部が手をつなぎ、一人10円のカンパで3200万円を集め製作された山本薩夫監督の力作。広島県の山村の貧農の娘として生まれた主人公が結婚し姑にいじめられ、夫に裏切られながらも苦労に耐えて生き抜いていく姿を描く。こういう役をやらせたら他の追随を許さない望月優子の力演が印象に残る。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MINOES", "ネコのミヌース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "オランダで100万人を動員する大ヒットを記録し、世界各国の児童映画祭で賞に輝いているファンタジー。児童文学者A・M・G・シュミットの同名小説に基づき、ある日突然、人間の女の子に変身してしまった猫ミヌースの心温まる冒険が展開。ミヌース役の主演女優C・V・ハウテンの愛らしい魅力、本物の猫たちが披露する名演技も見どころ。引っ込み思案の新聞記者ティべの前に突如現れたミヌースは、実は人間の女の子になったネコ。ティベの住み込み秘書となり、ネコ・ネットワークで極秘ネタを収集していた彼女は、街の名士であるエレメートの裏の顔を掴み……。", "2001-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CUBE2 HYPERCUBE", "CUBE2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "異例のヒットを記録し、亜流作品も多数製作された「CUBE」の正式な続編。無機質な立方体の部屋に閉じ込められた男女が、決死の脱出劇を繰り広げる。遂に明かされるCUBEの正体にも注目を。目覚めると立方体の部屋に閉じ込められていた女医のケイト。同様に閉じ込められた７人の男女と合流した彼女は、わずかな手掛かりをもとに脱出の糸口を懸命に探す。ところが突破口はまったく見えずじまい。やがて部屋全体が凶器となって、彼らを襲い始める。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・女は度胸", "喜劇・女は度胸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「男はつらいよ」の第１作をはじめ、山田洋次のいくつかの作品の脚本共作者であった森崎東が、山田洋次の原案に基づいて撮り上げた監督デビュー作。働かずに酒に酔っては寝転がっている父、不機嫌な顔で黙々と内職を続ける母、荒っぽいダンプカーの運転手の兄、気弱な自動車修理工の弟からなる桃山家で、兄と弟のそれぞれの恋人が取り違えられることから起こる、下品だが活力あふれる騒動。ささいな言い争いが一気にエスカレートして、怒りを爆発させてドタバタに至る親子ゲンカの場面はもちろんのこと、そんな男たちの怒声を吸い込んでも動じない女たちのバイタリティーも印象的。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　仮面ライダーオーズ WONDERFUL 将軍と21のコアメダル", "劇場版　仮面ライダーオーズ WONDERFUL 将軍と21のコアメダル", "", "", "http://image.eiga.k-img.com/images/movie/56236/315x210.jpg?4445", 65, "平成仮面ライダーシリーズ12作目「仮面ライダーオーズ」の劇場版。ヨーロッパの森で発見された「失われたメダル」の封印が解かれたことで、強力なメダルを作る能力を持った錬金術師ガラが復活。ガラは現在の世界を終わらせ、新たな世界の王＝オーズになるため使い魔を使って人間たちの欲望を集め始める。映司らはその影響から江戸時代にタイムスリップしてしまい危機に陥る。松平健が“暴れん坊将軍”こと徳川吉宗役で出演し、江戸時代にタイムスリップしたオーズとの共闘を果たす。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HELICOPTER SPIES", "0011ナポレオン・ソロ スラッシュの要塞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「0011ナポレオン・ソロ」シリーズの第７作。地球を廃墟にすることもできる恐ろしい熱反射プリズムを盗み出し、世界支配を画策するセバスティアン。彼の計画を阻止するべく、おなじみソロとイリアの名コンビが、猛然と悪に立ち向かう。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAREFREE", "気儘時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "アステアとロジャースのコンビによるRKOミュージカル８作目。ダンサーになりたかった精神科医というのがアステアの役どころ。友人の恋人でダンサーのロジャースに恋心を抱いて……という物語。DVDタイトルは「気まま時代」。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER　BY　NUMBERS", "完全犯罪クラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "町の有力者の息子で高校の人気者リチャードと、クラス１の頭脳を持ちながら暗くて友人もいないジャスティン。正反対の性格の二人が完全犯罪を計画。殺人を実行に移す……。プロファイリング、犯罪学に精通し、警察の裏をかきながらゲームのように犯罪を楽しむ二人と、直感から二人を犯人と確信した女刑事キャシーの対決を描く犯罪スリラー。犯罪心理学、行動科学の基礎を押さえた物語構成がサスペンスを盛り上げる。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TENDER FICTIONS", "テンダー・フィクションズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "「ナイトレイト・キス」で注目を集めた、レズビアン・カルチャーのゴッドマザーといわれる映像作家B・ハマーの自伝的ドキュメンタリー。30歳でカミングアウトした彼女のプライベート・フィルムや、歴史的なニュース映像を織り交ぜたユニークな構成で展開。ブラックパンサーや反ポルノ闘争など、レズビアンとして精力的に活動してきた彼女の人生をたどると同時に、友人や恋人、家族も登場し、ハマーの素顔を浮き彫りにする。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOPIO STIN OMICHLI", "霧の中の風景", "", "", "http://ecx.images-amazon.com/images/I/41CY7ERAVWL._SL210_.jpg", 125, "ドイツにいると聞かされた見知らぬ父を求めて、12才の少女と5才の弟が夜行列車に飛び乗った。オートバイの青年への報われない初恋、死んだ馬が引きずられていった雪の夜、疲れきった旅芸人たちが休む海辺、港から吊り上げられる巨大な手、そしてフィルムの切れ端の中に浮かぶ1本の樹……痛切に美しい詩のような風景の中を進むふたりが旅の果てに見るものは……。アンゲロプロス監督が娘たちのための寓話として作った人気作、ヴェネチア映画祭銀獅子賞最優秀監督賞受賞。", "1990-03-24", "27,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KID FROM SPAIN", "カンターの闘牛士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "大目玉の表情と、軽妙な歌が売り物だったコメディ俳優E・カンターが主演のドタバタ・コメディ。善良な青年が、銀行強盗の疑いをかけられ、メキシコへ逃亡。だが、今度は有名な闘牛士に間違われ、悪漢に目をつけられて追いまわされるハメに……。一難去ってまた一難という、受難を描く。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Footloose", "フットルース　夢に向かって", "", "", "http://ecx.images-amazon.com/images/I/51eaUjyYhZL._SL210_.jpg", 0, "ケビン・ベーコン主演の青春ドラマ「フットルース」（1984）のリメイク。監督は「ハッスル＆フロウ」のクレイグ・ブリュワー。オリジナル版でケビン・ベーコンが演じた主人公レン役には、ジャスティン・ティンバーレイク、クリスティーナ・アギレラなどの振り付けを担当していたダンサーのケニー・ウォーマルドが抜てきされた。", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Eagle", "第九軍団のワシ", "", "", "http://image.eiga.k-img.com/images/movie/56016/315x210.jpg?3606", 114, "イギリスの歴史小説家ローズマリー・サトクリフ原作の「第九軍団のワシ」を、「消されたヘッドライン」のケビン・マクドナルド監督が映画化した歴史アドベンチャー。西暦120年、ローマ帝国の支配下だったカレドニア（今のスコットランド）で起きた内乱を平定するためローマ帝国最強の第9軍団が出撃するが、そのまま消息を絶ってしまう。その20年後、第9軍団の副司令官だった父親を捜すため軍人のマーカスが冒険の旅に出る。出演はマーカスに「G.I.ジョー」のチャニング・テイタム。共演にジェイミー・ベル、ドナルド・サザーランド。", "2012-03-24", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女犯破戒", "女犯破戒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "江戸中期文化爛熟の頃、妖しき悪の殿堂・延命院を舞台に、無理心中で一人生き残り仏門に入った元人気役者の日当。彼は戒を破って次々と女を抱き、大奥にまで色祈祷を施して大僧正の地位を狙う大悪僧となる。あげくの果てに心中で死んだ女と瓜二つの女が現れて破滅する、というお話。工藤栄一の爬虫類的な冷たい感触の映像が印象的だ。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虹男", "虹男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "角田喜久雄が新聞に連載した推理小説を基に、幻想表現に一部カラーを使うなど実験的な手法を使って映像化したサスペンス映画。大映スリラーらしいスリリングかつ画期的な特撮シーンで魅せる。別々の新聞社に所属する新聞記者の男女コンビが活躍する。女記者の女友達がある一家連続殺人事件の容疑者に。その無実を晴らすため、彼らは事件の真相を調べていくと……。“虹の幻影”を見て人が死ぬ怪事件の真相とは？", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIKI DE SAINT PHALLE ： WER IST DAS MONSTER−DU ODER ICH？", "ニキ・ド・サンファル／美しい獣〈ひと〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1930年パリに生まれてモデルとして活躍し、1960年代初頭に絵の具を仕込んだ拳銃や小さな大砲でキャンバスを撃つという“射撃絵画”で脚光を浴びたニキ・ド・サンファル。1978年にはイタリア・トスカーナ地方で、タロットカードにインスピレーションを得た壮大な彫刻庭園“タロット・ガーデン”の建設に着手した彼女の軌跡をたどるドキュメンタリー。ガラス、鏡、陶器の色鮮やかなモザイクで彩られた神話的な彫刻作品が紹介され、過激な中にも豊かな包容力を秘めた稀代のアーティスト、ニキの人間像が浮かび上がる。", "1995-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プライド 運命の瞬間〈とき〉", "プライド 運命の瞬間〈とき〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 161, "伊藤俊也監督・津川雅彦主演で新たな東條英機像を描き出し、様々な論争を呼んだ歴史大作。終戦後、国際軍事裁判に出廷した元内閣総理大臣・東條英機は極刑を覚悟していたが、戦争の原因をすべて敗戦国に押し付けようとする連合国の意図を知り、裁判を戦い抜くことを決意するのだった……。津川雅彦、裁判長を演じる「ロボコップ」のR・コックス、主席検事を演じた「G.I.ジェーン」のS・ウィルソンら俳優陣の好演。さらに完全再現された法廷セットで行われる緊迫感あふれる裁判シーンとで、見ごたえのある壮大な人間ドラマとなっている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遥かなる甲子園", "遥かなる甲子園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "聴覚障害というハンデにもめげず、甲子園出場を目指し奮闘する沖縄のろう学校生徒たちの姿をいきいきと描く実話の映画化。この手の作品にありがちな感傷や押しつけのメッセージを避け、生徒たちの前向きな姿勢をサラリと打ち出し、すべての登場人物に好感の持てる気持ちの良い佳作に仕上がっている。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狼", "狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "新藤兼人のオリジナル脚本を自ら監督した近代映協の自主製作作品。貧しい戦争未亡人、自動車修理工、映画脚本家、元銀行員らが、互いに励まし合って懸命に働くがうまくいかず、やむなく銀行強盗に手を染める姿を描く。冷徹な客観描写が貫かれ、出口なき状況に置かれた人間たちの絶望的な心理をリアルに伝えている。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Perfect Storm", "パーフェクト・ストーム", "", "", "http://image.eiga.k-img.com/images/movie/1586/315x210.jpg?3915", 130, "1991年10月。マサチューセッツの港町グロースターを、ビリー・タイン船長率いる漁船アンドレア・ゲイル号が出航しようとしていた。最近釣果が振るわなかったビリーの船だが、漁師としての誇りと生活のため、ビリーと仲間たちは今回の漁で何としても大きな成果をあげるつもりでいた。沖に出た船は順調に操業を続けていたが、その頃地元のテレビでは気象予報士が観測史上例のない大嵐の発生を伝えていた……。", "2000-07-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Grand Alibi", "華麗なるアリバイ", "", "", "http://image.eiga.k-img.com/images/movie/53926/315x210.jpg?2647", 93, "「ミステリーの女王」ことアガサ・クリスティの名作小説「ホロー荘の殺人」を映画化。監督は脚本家としても活躍するパスカル・ボニゼール。のどかで美しい風景が広がるフランスの田舎の大邸宅。9人の上流階級男女による華やかなパーティは、1発の銃声と女の悲鳴で突然打ち切られた。パーティにはホストである上院議員夫妻、ゲストの医師夫妻、彫刻家、作家、女優などが参加していたが、殺された精神分析医のピエールに対し、それぞれが愛情や憎しみを抱えていた……。", "2010-07-17", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ДОН КИХОТ", "ドン・キホーテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ソ連初のシネスコ版で贈る騎士冒険映画。セルバンテスの文芸大作小説を、「ハムレット」や「リア王」などソ連史劇の巨匠コージンツェフ監督が、ロシアの大地へ一大ロケーションを敢行し撮り上げた。悲しくもおかしいドン・キホーテの物語が壮大なスケールで進行する。騎士に憧れ自らが騎士だと称するドンは、姫に見立てた田舎娘と百姓サンチョ・パンサを連れて旅に出る。", "1957-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EGGS", "卵の番人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ノルウェーの森に住む老いた兄弟、モーとファーは完璧なルーティンの中で生活していた。同じ時間にとる質素な食事や午睡、時折訪れる客人など、まるで変わることなく続く穏やかな毎日。ところがファーの隠し子コンラードが現れたことで、二人の生活に異変が起こる。鳥の卵のコレクションを抱え、車椅子に乗り、雌鳥のような奇声を発するコンラード。やがてモーは、不気味な甥によって平穏と自分の居場所が失われつつあることに気付くのだった……。美しい映像で綴る寓話的作品。", "1995-01-01", "36", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLAYING　BY　HEART", "マイ・ハート,マイ・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "幸福を探し続ける11人の人間模様を豪華キャストのアンサンブルで描く心温まる群像ドラマ。ポールとハンナは結婚40年目を迎える熟年夫婦だが、夫には今も大切に想っている女性がいることに妻は気付いている。一方、彼らの３人の娘たちも、悩み、傷つき、孤独を抱えていた。奔放な末娘ジェーンはなかなか本物の恋に出会うことができず、次女メレディスは結婚に失敗して恋に臆病になっている。そして長女グレイシーも夫との仲は冷え切っている。そんな彼女らと夫や愛人、恋人たちの心のさまよいを、それぞれの視点から見つめ、新たな人生を手に入れるまでを丁寧な演出で綴っていく。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Julie & Julia", "ジュリー＆ジュリア", "", "", "http://image.eiga.k-img.com/images/movie/54436/315x210.jpg?2122", 123, "実在の料理研究家ジュリア・チャイルドと、ジュリアに憧れる現代のOLジュリー。50年の時を隔てた2人の女性を「めぐり逢えたら」「ユー・ガット・メール」のノーラ・エフロン監督が描いた人間ドラマ。食べることが大好きなパリ在住のアメリカ人ジュリアは、名門料理学校で習得した誰でも簡単に作れる家庭料理の本を出版し、本は大ベストセラーとなる。50年後のニューヨーク、作家になる夢をあきらめたOLのジュリーは、憧れのジュリアのレシピに挑戦し、それら全てをブログに綴ることを思いつく。", "2009-12-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A River Runs Through It", "リバー・ランズ・スルー・イット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "「普通の人々」で監督としても高い評価を得た名優ロバート・レッドフォードが、ブラッド・ピットを主演に迎えて贈るヒューマン・ドラマ。20世紀初頭のモンタナの壮大な自然を背景に、フライ・フィッシングを通して交流する家族の絆とそれぞれの葛藤を描く。厳格な牧師の家庭で育てられた、真面目な兄ノーマンと自由奔放な弟ポール。父に習ったフライ・フィッシングで結ばれる彼らは、やがてそれぞれの道を歩み始めるが……。", "1993-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホワイトアウト", "ホワイトアウト", "", "", "http://image.eiga.k-img.com/images/movie/1746/315x210.jpg?4143", 129, "日本最大の貯水量を誇る新潟県奥遠和ダムが白銀に覆われた2月。新潟県警に突然「奥遠和発電所およびダムを占拠した」という無線が入った。テログループ“赤い月”はダムの職員を人質に取り50億円を要求、24時間以内に要求が達成されない場合ダムを爆破すると通告する。ダムが決壊すれば下流域の20万世帯が激流に飲まれることに。一方、テロリストの見張りを逃れたダムの運転員・富樫は、猛吹雪と豪雪で閉ざされた外部との連絡手段を探っていた。", "2000-08-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SALVADOR", "サルバドル 遥かなる日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "「プラトーン」でアカデミー監督賞を獲得したストーンが手掛けた、シリアスで情念のこもった社会派ドラマ。政情不安定で、内戦の絶えないエルサルバドルに入国したフォト・ジャーナリストのボブ。彼の見た現実は、胸がムカつくほどの凄惨だった……。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トップをねらえ！　劇場版", "トップをねらえ！　劇場版", "", "", "http://ecx.images-amazon.com/images/I/517mRDe-okL._SL210_.jpg", 95, "庵野秀明監督が1988年に手がけた全6話のOVAを再編集して劇場公開。続編「トップをねらえ2！」の劇場版とあわせて上映された。人類が宇宙に進出した未来、地球はナゾの宇宙怪獣（STMC）の侵略を受けていた。沖縄女子宇宙高校に通い、宇宙に強いあこがれを抱く少女タカヤ・ノリコは、持ち前の努力と根性を武器にパイロットとして成長。最終決戦兵器「ガンバスター」のパイロットとして人類の未来のため戦う。", "2006-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crank", "アドレナリン", "", "", "http://image.eiga.k-img.com/images/movie/34046/315x210.jpg?1376", 94, "「トランスポーター」シリーズでアクション・スターとしての人気を確立したジェイソン・ステイサムが殺し屋に扮するノンストップ・アクション。フリーの殺し屋シェブは、睡眠中に宿敵リッキーに毒を盛られてしまう。毒の作用を止めるには体内にアドレナリンを出し続けなければならないと知った彼は、解毒剤の入手とリッキーへの復讐のため奔走する。ところが、今度は恋人のイブがターゲットになっていることを知り……。", "2007-07-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パンダフルライフ", "パンダフルライフ", "", "", "http://image.eiga.k-img.com/images/movie/53536/315x210.jpg?2533", 100, "ぬいぐるみのような愛らしさで子供から大人まで多くの人々を魅了する動物界のアイドル、パンダの生態に迫ったドキュメンタリー。約60頭のパンダたちが暮らす中国・四川省の成都パンダ繁育研究基地での子育ての様子や、和歌山県にあるレジャー施設アドベンチャーワールドで生まれ育った4歳の双子パンダ、リュウヒンとシュウヒンの別れと成長をドラマチックに描き出す。女優・菅野美穂がナレーションを務める。", "2008-08-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MACCHIE SOLARI／THE VICTIM", "炎のいけにえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "真夏のローマを舞台にした連続殺人事件を描くイタリアン・ミステリーの傑作。死体のカットを積み重ねるオープニングからグッと引き込まれ、モリコーネの哀切なサウンドトラックもサスペンスを募らせる。変死体を鑑定する女医の隣人が海辺で銃殺された。彼女は女医の父の愛人だったため女医自身が疑われる。一方、父は事故で半身不随となり、やがて毒殺される。犯人の正体は？", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅がゆく３ ～九州やくざ戦争", "修羅がゆく３ ～九州やくざ戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "哀川翔主演の極道もの第３作。本郷組組長・本郷流一の恩人である博多の久須本組組長が殺される。岸田組の伊能が大磯組のチンピラに襲わせたのだ。本郷は兄弟分の京本とともに博多に乗り込み、久須本組を助ける。久須本組に本郷のいることを知った伊能は、博多に総攻撃を命令、ここに本郷と伊能の因縁の対決が始まるのだった。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STREET HUNTER", "狼が眠る街 ストリートハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "アメリカ最大の麻薬市場であるニューヨーク。残虐非道なコロンビア人麻薬組織と、たった一人で組織に立ち向かう一匹狼の賞金稼ぎが繰り広げる凄絶なドラッグ代理戦争をスリリングに描いたアクション作。ガン・マニア必見の名銃・珍銃が画面に次々と登場するなど、これまでになく銃器類へのこだわりを見せている点に注目。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("制服サバイガールII", "制服サバイガールII", "", "", "http://image.eiga.k-img.com/images/movie/54236/315x210.jpg?2919", 70, "「制服サバイガール I」の続編。テーマパークで起こったゾンビ化した女子高生たちの壮絶な殺し合いから数日後、無事に生き残り政府に保護された葵だったが、クラスメイトたちをテーマパークに誘ったことに激しく責任を感じ、ゾンビを絶滅させるために単身テーマパークへと乗り込む。監督は「クレーマー」シリーズの金子大志、出演にグラビア女優の仲村みう、“JUNONスーパーボーイコンテスト2007”の白石隼也ほか。", "2008-12-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOE’S　WEDDING", "ジョー・ウェディング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "結婚式を４日後に控えたジョーはバーでミステリアスな美女ユタに誘惑され、彼女について行ってしまう。だが、気付くと彼は全裸で首に鎖をつけられ監禁されていた。結婚式が刻々と迫るなか、ユタはジョーにある復讐をするというが、身に覚えのないジョーは誤解を晴らそうとする……。とんでもない災難に見舞われたジョーの奮闘を描いたコメディ。", "1996-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花くれないに", "花くれないに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "まだ男女共学の許されていないみちのくの高校に赴任して来た教師の朝田恵太郎。そこでは教師や町の有力者が二派に分かれて共学論争が展開されていた。共学反対派のPTA会長や教頭は、男女合同の運動場の予定地を競輪場にしようと図っていて……。NHKで放映された連続テレビドラマの映画化。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRUSH　LANDING", "怪鳥 フライング・デビル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "中国映画には珍しい、本格派のパニック・アクション。上海から北京へ向けて飛び立った一機の旅客機。やがて機長は着陸装置の故障に気付き、空港に引き返そうとする。しかし着陸を試みるもののうまくいかず、燃料だけが減っていく。残る手段は胴体着陸だが、この着陸法は乗客の犠牲を伴うものだった……。機内の人々のパニック描写も緊迫感たっぷり。", "2000-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MIRACLE", "奇蹟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "1812年のスペイン・ミラフローレス村。聖母マドンナに仕える美貌の見習い尼のテレサは、信仰を捨て愛する英国兵を追っていた。ジプシーや闘牛士との悲しい恋を経て彼女はやっと英国兵と再会。しかし、彼の罪の深さを知り故郷へ戻る。K・フォルメーラの世界的ヒットとなった舞台劇の映画化。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD END CITY", "デッド・エンド・シティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "21世紀初頭のアメリカ。政府の無謀な都市再開発計画のために、街は次々と荒廃の一途をたどる。そんな街で暴れまわるギャング集団“ラッツ”に闘いを挑む正義の味方、若き戦士マーフィーの活躍を描く近未来アクション。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Collateral Damage", "コラテラル・ダメージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ロサンゼルスの消防士ゴーディは街で起こった爆弾テロ事件によって妻子を亡くしてしまう。現場で不審人物を目撃していたゴーディの協力で、犯人がコロンビア解放軍のウルフであることが判明するが、政府は政治的な事情から彼を制裁しようとしなかった。怒りに燃えるゴーディは逃亡したウルフを自力で捕まえることを決意し、コロンビアに潜入するが……。9・11テロ事件の影響で公開が延期されたいわくつきのサスペンス・アクション。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MAN CALLED HORSE", "馬と呼ばれた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1825年、アメリカに探検狩猟に来た英国貴族がダコタのスー族に捕まり、厳しい儀式と掟のなかで苦難の末、首領の娘と結婚し地位を得る。他部族との戦いで妻子を失い、故国に帰るまでの数奇な体験を描く冒険活劇。1976年に続編「サウス・ダコタの戦い」も製作された。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ローレライ", "ローレライ", "", "", "http://image.eiga.k-img.com/images/movie/1506/315x210.jpg?4554", 128, "平成ガメラシリーズの特技監督として知られる樋口真嗣が長編監督デビュー。45年8月、広島に原爆を投下され、窮地に陥った日本軍は、ドイツから極秘裏に入手した潜水艦・伊507に特殊兵器「ローレライ」を搭載し、米原爆搭載機の発信基地の奇襲を目論む。だが乗組員たちの思いはさまざまだった。原作は福井晴敏の「終戦のローレライ」。同じ原作者による大作「戦国自衛隊1549」「亡国のイージス」が続々公開されるのも話題。", "2005-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("腰辨頑張れ", "腰辨頑張れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 28, "うだつの上がらない保険勧誘員の奮闘を描いた成瀬巳喜男監督の第８作で、現存する最古の成瀬作品。小市民映画の範ちゅうに入りながら、同期の小津や五所のような“松竹蒲田調”の温かいユーモア精神や人情の世界にひたることなく、リアリズムへの志向が見られるのが、のちの作風に通じていて興味深い。", "1931-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DENNIS THE MENACE", "わんぱくデニス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "J・ヒューズが製作、脚本を手掛けた爆笑のキッズ・ムービー。1930年代にひとコマ漫画として発表され、1970年代にはアニメにもなった人気キャラクター、デニスを映像化。イタズラ好きの彼と、その標的にされる隣家のウィルソン氏との交流を、コミカルかつほのぼのと描く。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IDENTIFICAZIONE DI UNA DONNA", "ある女の存在証明", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "ニコロは映画監督。彼は一人の女性の映画を構想しているが、そのイメージがまだつかめない。実生活ではマービと愛し合っているが、その彼女が彼のもとを去っていく。そして、その後知り合ったイダも……。一人の女のイメージを求めて、現実と幻想が交錯していく。アントニオーニはこの作品の後、「愛のめぐり逢い」まで長い沈黙に入ることになる。", "1982-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SANTA CLAUSE2", "サンタクロース・リターンズ！／クリスマス危機一髪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「ギャラクシー・クエスト」などで知られる名コメディアン、T・アレンが主演した「サンタクローズ」の続編。前作と同じキャストが集結し、またまたクリスマスに騒動を巻き起こす。８年前、ひょんな事からサンタクロースとなったスコット。これまで彼は“史上最高のサンタ”として日夜頑張ってきた。しかし、サンタとしての条件をたった一つだけ満たしていなかった彼は、その職を追われようとしているのだ。“サンタクロースは結婚していなければならない”という条件を満たすために、バツイチ、息子ありのスコットは残された１ヵ月でミセス・サンタクロースを探すことになるが……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北の岬", "北の岬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "辻邦生の原作を熊井啓が妻・桂明子とともに脚色、自らメガホンを執った恋愛ドラマ。ヨーロッパから日本へ向かう船の中で出会った自動車エンジニアの光雄と修道女、テレーズの悲劇的な恋の行方を綴っている。テレーズを演じるのは、C・ジャドだが、彼女の声は、岩崎加根子が吹き替えている。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bad Teacher", "バッド・ティーチャー", "", "", "http://image.eiga.k-img.com/images/movie/56066/315x210.jpg?4606", 92, "教育に対する熱意はゼロ、玉の輿にのることばかり考えている女性教師が、裕福な男性をつかまえようと奮闘する姿をキャメロン・ディアス主演で描いたコメディ。お金も夢も熱意もないアラサーの中学教師エリザベスは、玉の輿こそが女の幸せと信じ、結婚相手を探す日々。そんなある日、職場にお金持ちの代理教師スコットが赴任してくる。エリザベスはスコットを捕まえるための武器として豊胸手術を受けようとするが……。スコット役はプライベートでディアスの元恋人だったジャスティン・ティンバーレイク。", "2012-05-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pajaros de papel", "ペーパーバード　幸せは翼にのって", "", "", "http://image.eiga.k-img.com/images/movie/55736/315x210.jpg?3253", 123, "フランコ独裁政権下のスペイン・マドリード。内戦で妻子を失った喜劇役者のホルヘは、相方のエンリケと、エンリケが引き取った孤児ミゲルと3人で生活を始める。ホルヘは軍部に反体制派の容疑をかけられるが、それでも仲間たちと舞台に立ち、観客に笑いを届けようとしていた。やがて自分を父親のように慕い、必死で芸を覚えようとするミゲルとも打ち解け、生きる希望を失っていたエンリケの心は癒されていくが……。2010年モントリオール世界映画祭で観客賞を受賞したヒューマンドラマ。", "2011-08-13", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談 雪女郎", "怪談 雪女郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "田中徳三が日本の古典的な伝説『雪女』を、格調高く描いた意欲作である。怪談的要素よりも雪女と人間の恋愛物語が中心に置かれ、雪女ものの一つのクラシックとなった。藤村志保が雪女に扮し、抑制のきいた演技で難役を見事に演じきった。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("晴れ姿 伊豆の佐太郎", "晴れ姿 伊豆の佐太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "戦後、一時人気が低迷した高田浩吉は、ヒット曲『伊豆の佐太郎』で復活。この曲のヒットにあやかり、久し振りに主演したのがこの作品。土地の親分、雷魚の儀十の片腕を斬り、許嫁のお美代とも別れて伊豆を去った佐太郎。彼が旅芸人一座にかくまわれて、３年振りに故郷へ帰るとお美代は儀十の毒牙にかかろうとしていて……。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙で1番ワガママな星", "宇宙で1番ワガママな星", "", "", "http://image.eiga.k-img.com/images/movie/55586/315x210.jpg?2893", 100, "宇宙旅行に当選した5人の男が、宇宙センターを舞台に繰り広げる騒動を描くサスペンス・コメディ。大倉孝二、きたろう、「ガレッジセール」のゴリ、マイケル富岡、佐野和真らバラエティに富んだキャストが出演する。2020年、カップラーメンの懸賞で宇宙旅行に当選した5人は、スタジオにあるロケットに乗り込み、突拍子もない宇宙訓練に参加させられる。そこには宇宙旅行企画会社の陰謀が隠されていた……。第2回沖縄国際映画祭の長編プログラム“Laugh部門”に出品。", "2010-08-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Seven Swords", "セブンソード", "", "", "http://image.eiga.k-img.com/images/movie/1166/315x210.jpg?3466", 153, "「男たちの挽歌」シリーズ、「ワンス・アポン・ア・タイム・イン・チャイナ」シリーズのツイ・ハークが、レオン・ライ、ドニー・イェンはじめ香港、中国、台湾、韓国からアジアの新旧人気俳優を集めて描くVFX武侠アクション。舞台は17世紀の中国。明王朝から覇権を奪った清王朝は、旧勢力の反乱を防ぐため武術者を次々に死刑に処していく。この冷酷な圧政に怒った武術の達人7人が、7つの剣＝セブンソードを手に立ち上がる。", "2005-10-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("電送人間", "電送人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「透明人間」（1954）に始まる東宝SF路線“変形人間”ものの第３作。戦時中に起きたある犯罪をめぐる復讐を描き、科学映画というよりは推理劇の面が強い。電気となって瞬時に空間を移動する“電送人間”に中丸忠雄、彼を追う警部に鶴田浩二という配役が、日本的な陰湿さを体現している。特撮は円谷英二。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ride", "ザ・ライド　ハワイアン・ビーチ・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "100年前のハワイにタイムスリップしてしまったサーフィンのワールドチャンピオンの青年が、そこでの出会いを通して成長していく姿を描いた青春サーフィン映画。若くしてワールドチャンピオンになったサーファーのデビッドは、大会中に波に飲まれて意識を失い、1911年のワイキキ・ビーチで目を覚ます。彼を海中から救ったのは、後に“サーフィンの父”と讃えられる伝説のサーファー、デューク・カハナモクだった……。", "2007-04-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ballets Russes", "バレエ・リュス　踊る歓び、生きる歓び", "", "", "http://image.eiga.k-img.com/images/movie/53146/315x210.jpg?1796", 118, "20世紀の芸術やエンタテインメントに多大な影響を与えたロシアのバレエ団“バレエ・リュス”の活動の軌跡を辿ったドキュメンタリー。20世紀初頭のパリで花開いたバレエ・リュスは、興行主ディアギレフの死を受けて解散したものの、その後すぐに再結成されて世界中にバレエの魅力を伝え続けた。元団員たちへのインタビューや貴重なアーカイブ映像を通してその歴史を振り返るほか、2000年にニューオリンズで開催された“同窓会”の模様も収録。", "2007-12-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("B−５２", "B−52", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "アメリカが誇る最先鋭戦闘機B−52の全貌を明かしたドキュメンタリー。パイロットを始めとする関係者が、同戦闘機の歴史や能力について語る。メカニックな美しさを捉えつつ、その裏側にある殺りく兵器としての脅威を浮き彫りにしていく。", "2001-01-01", "7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春グラフィティ　スニーカーぶる～す", "青春グラフィティ　スニーカーぶる～す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "働きながら定時制高校に通う昌也・洋一・敏夫の仲良しトリオは、それぞれに夢を抱いていたが、敏夫に仕事を頼まれた昌也が交通事故を起こしてしまったことから、その夢が崩れはじめる。サービス精神旺盛で“見せる”ことに徹した“たのきんトリオ”映画初主演作。ビデオタイトルは「スニーカーぶる～す」。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("隣りの八重ちゃん", "隣りの八重ちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "1930年代は松竹蒲田の“小市民映画”の黄金時代であり、小津安二郎の「生れてはみたけれど」、清水宏の「人生のお荷物」、成瀬巳喜男の「腰辨頑張れ」などが、都市サラリーマンの日常生活をソフトに、そして少々ペシミスティックに描いていた。その代表選手が島津保次郎であり、とりわけ「隣の八重ちゃん」はその代表作。ストーリーは単純で、隣り合った２軒の平凡な庶民の生活スケッチが淡々と綴られる。八重子は隣の恵太郎・精二兄弟と仲が良く、いつも一緒に遊んでいる。ところが姉の京子が嫁いだ先から出戻ってきたばかりか、どうやら恵太郎に気があるらしい。ほのかに恵太郎を思っていた八重子は気が気でなくなって……。「私の兄さん」「兄とその妹」などで庶民の心理スケッチの名人とうたわれた島津監督の一つの頂点であると同時に、逢初・岡田・大日方・磯野ら当時の松竹蒲田の演技陣のチームワークの良さがうかがえる。また、スタッフの中に助監督として豊田四郎と吉村公三郎が、撮影助手として木下惠介が名を連ねている。", "1934-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猿田彦土中神社", "猿田彦土中神社", "", "", "http://image.eiga.k-img.com/images/movie/54756/315x210.jpg?4286", 61, "美術家・鈴木寅二啓之の独特な手法で描かれる「土中絵画」を、伊勢・猿田彦神社の祀に奉納するプロジェクトを記録したドキュメンタリー。天と地をつなぐことに深いこだわりを持つ鈴木を通し、神社というコミュニティの存在意義や、自然と人々の調和について考察する。", "2009-08-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE VOICE", "リトル・ヴォイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "M・ハーマン監督が「ブラス！」に続いて発表したファンタスティックな音楽ドラマ。英国のさびれた港町で、やかましい母親と二人暮らしの女の子エルヴィ。父親の死後、まったく口をきかなかった彼女が、ずば抜けた歌唱力の持ち主だと判明したことから、周囲の人々が大騒ぎを始める。ライブ・シーンでジュディ・ガーランドやマリリン・モンローの鮮やかな物まねを披露するのは、この映画に先立つ舞台版でも主演を務めたJ・ホロックス。ヒロインの心を開かせる若者に扮するE・マクレガーも素朴な魅力を見せている。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIS MAJESTY O’KEEFE", "白人酋長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1870年代の南太平洋・ヤップ島。乗組員の反乱によって船を追われた船長のオキーフが、この島に流れ着いて来た。真珠貝を採取して大もうけをたくらむオキーフだったが……。B・ハスキンが監督した海洋アドベンチャー。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アークエとガッチンポー THE MOVIE チェルシーの逆襲", "アークエとガッチンポー THE MOVIE チェルシーの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 15, "台湾発の人気アニメ「アークエとガッチンポー」の劇場版「アークエと魔法のハンマー」と同時上映された短編作品。世界崩壊を企む悪の組織パイプスの天才少女チェルシーが、最終兵器メカプリオを完成させた。一方、UNCOエージェントのタケシ、アークエ、ジャスミンは美味しいショーロンポーを食べるため出撃するが、チェルシーの激しい妨害を受ける。そしてついにメカプリオと元祖プリプリクラゲの死闘が幕を開けるのだった。", "2005-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAVANDEH", "駆ける少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ペルシャ湾岸の村で、打ち捨てられた船で一人暮らしの少年アミロは、ごみためを漁り、靴磨きや水売りなどをして何とか生きていた。彼の仲間たちもみな同じ境遇だ。しかし彼らは、エネルギーを持て余すかのように、列車を追いかけたり、砂漠を駆け抜けたり、毎日をたくましく生きているのだった。ペルシャ湾沿岸の美しい風景をバックに、身寄りのない少年が厳しい社会のなかで生き抜いていく姿を描く。", "1986-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA COULEUR DU VENT", "誰かが知っている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "「愛を弾く女」でエマニュエル・ベアールと共演したE・ブールジーヌが主演したラブ・ミステリー。パリの大手出版社に勤めるルイーズが、一冊の本と出会う。彼女はその作者に魅せられ、顔も知らぬまま、手紙による恋愛を始める。とうとう、作者の住むニューヨークへ旅立つルイーズだったが、そこには罠が仕掛けられていた。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("駱駝祥子／RICKSHAW　BOY", "駱駝の祥子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "旧時代の北京。農村からやって来た祥子は人力車を借り、車引きとして懸命に走り回る。そして自分の車を持ち自由な生活を送ろうとするのだが……。祥子役は「さらば、わが愛／覇王別姫」のチャン・フォンイー。その妻役で「ホームカミング」「フルムーン・イン・ニューヨーク」などの香港映画で活躍のスー・チンガオワが出演。", "1982-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Two Rabbits in Osaka", "大阪のうさぎたち", "", "", "http://image.eiga.k-img.com/images/movie/57346/315x210.jpg?2884", 70, "", "2011-01-01", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ИТЕРДЕЬОУКА／INTERGIRL", "令嬢ターニャ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 149, "昼は看護婦だが、夜は娼婦をして外貨を稼ぐ、レニングラードの娘ターニャ。国際結婚をし、祖国ソ連からの脱出を図るのが彼女の目的だった。やがてスウェーデン人にプロポーズされた彼女は、ストックホルムへと向かうが……。社会の矛盾のなかでも、力強く生きる女の姿を捉えた辛らつな人間ドラマ。", "1989-01-01", "12,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREAT TRAIN ROBBERY", "大列車強盗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 8, "史上初の西部劇とされる貴重なサイレント短編。同じ年、初めてストーリーのあるアメリカ映画「ある消防夫の生活」を撮ったポーターの大ヒット作で、“映画”を大衆の娯楽にする引き金となった。特にラストで観客を撃つ(！)場面は今見ても衝撃的。", "1903-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大醉侠／COME DRINK WITH ME", "大醉侠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "香港武侠アクションの父キン・フーの監督第２作にして、武侠映画史に残る傑作。総督を父に持つ女剣士が誘拐された兄を救うため、男装して旅に出る。宿屋で危機に見舞われた彼女を助けたのは、酔いどれの酔猫と名乗る男。彼は伝説的な武術の達人だった……。華麗な武術アクションは、まさにキン・フーの真骨頂。後の「グリーン・デスティニー」に多大な影響を与えた作品としても知られており、ヒロインのチェン・ペイペイは同作で客演を果たしている。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("発禁本「美人乱舞」より 責める！", "発禁本「美人乱舞」より 責める！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "昭和初期、SMを追究することに異常なまでの執念を燃やした責めの研究の大家・伊藤晴雨の数奇な生活を描いたロマンポルノ。「実録阿部定」「屋根裏の散歩者」とともに、田中登監督の昭和史３部作の１本としてみなされている。晴雨に拾われ、愛され、責められ、狂わされるヒロインを宮下順子が演じている。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("安重根と伊藤博文", "安重根と伊藤博文", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "明治政府の要人、伊藤博文は、ハルビンで朝鮮人、安重根によって暗殺された。大陸進出を狙う日本と、その猛威にさらされる朝鮮の人々の“思い”を、北朝鮮の史観によって描く歴史ドラマ。実際にハルビンロケを敢行。重厚な演出、演技が歴史の“重み”を感じさせる。", "1979-01-01", "48", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HACKERS", "サイバーネット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "わずか11歳で、ウォール街のコンピュータをクラッシュさせ、FBIのブラック・リストに挙げられた天才ハッカーのデイド。マンハッタンの高校に転校してきた彼の元には、コンピュータを自由自在に操る、才能豊かな連中が集まり始めていた。そんな仲間の一人が、あるとき、自称“ハッカー大王”プラハの陰謀の証拠を掴んだ。するとプラハは卑怯にも、自らの罪を彼らにかぶせようと画策し始める。デイドらは、サイバーネットを使って、世界中の優秀な頭脳に協力を求め、プラハに対抗、ネットワーク上での、激しいバトルが高速展開する！", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リュック・フェラーリ／ある抽象的リアリストの肖像", "リュック・フェラーリ／ある抽象的リアリストの肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 46, "前衛音楽の大家、故リュック・フェラーリの晩年の日常をスケッチしたドキュメンタリー。75歳にして自由奔放な創作を続けるリュック・フェラーリ。人気DJのエリックMとの共演や愛妻とのやりとり、作曲、スタジオでのリハーサルやレコーディング、パフォーマンスを映し出す。庭の木々に吊るされたCDの光の反射が美しい映像や、生前最後の日本公演からの抜粋映像も見逃せない。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RESURRECTED", "ヘルハザード 禁断の黙示録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "古典的ホラー作家ラヴクラフトの原作をホラー映画界の鬼才D・オバノンが映画化した作品。精神病院から一人の男が脱走した。彼の病室にはメチャクチャにつぶされた看護人の死体が。しかしこの事件の背後には恐るべき200年前の暗黒の神話が隠されていた……。原作の格調高さとスプラッタを見事に融合。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHANGHAI KNIGHTS", "シャンハイ・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「シャンハイ・ヌーン」の続編にあたるコミカル・アクション。舞台は19世紀末のロンドン。父を殺された主人公チョンが相棒のお調子者ロイとともに敵を追ううちに、王室を巻き込んだ巨大な陰謀に行き当たる。主人公を演じるジャッキーのスピーディーなアクションはもちろん、名コンビとなったO・ウィルソンとの絶妙の掛け合いも見ものだ。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mi ricordo Anna Frank", "アンネの追憶", "", "", "http://image.eiga.k-img.com/images/movie/56946/315x210.jpg?4782", 99, "世界中で読み親しまれている「アンネの日記」には書かれなかった、アンネ・フランクがゲシュタポによってアムステルダムの隠れ家から連れ出され、強制収容所に送られてからたどった過酷な運命を描いたドラマ。アンネの親友だったハネリ・ホスラーのインタビューをまとめた「もうひとつの『アンネの日記』」（アリソン・レスリー・ゴールド著）を原作に、アンネの家族の中で唯一ホロコーストを生き延びた父オットーによる回想でつづられる。音楽を「ニュー・シネマ・パラダイス」「海の上のピアニスト」のエンニオ・モリコーネが担当。", "2012-04-14", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRASS　ON　FIRE", "炎のジプシーブラス／地図にない村から", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ルーマニア在住のジプシー・ブラスバンド“ファンファーレ・チォカリーア”の世界ツアーを記録した音楽ドキュメンタリー。地図にも載っていないルーマニアの小村から生まれた音楽＝ジプシー・ブラスが、世界中を席巻して多くのファンを生んでいく。その経緯が、叙情的な映像と世界最速といわれる白熱のライブシーンを巧みに織り交ぜながら描かれる痛快な一編。", "2002-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUBLE TEAM", "ダブルチーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "“香港のスピルバーグ”の異名をとるツイ・ハーク監督のハリウッド進出第1作となったハード・アクション。元CIAの秘密工作員クインと長年の宿敵であるテロリスト、スタブロスの対決が、激しい銃撃戦や迫力の爆破シーン、また激しくぶつかり合う肉弾戦の連続でスピード感たっぷりに描かれていく。NBAのスーパー・プレイヤー、デニス・ロッドマンがクインに味方する闇の兵器ブローカー役で初の映画出演を果たしたのも話題となった。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("てなもんや三度笠", "てなもんや三度笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "当時、TVで大人気だった『てなもんや三度笠』を映画化した“てなもんや”シリーズの第１作。喧嘩は弱いがハッタリをきかすのがうまいヤクザ・あんかけの時次郎は、ひょんなことから口八丁手八丁の小坊主・珍念と知り合い、二人の珍道中が始まる。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAR NO EVIL", "デンジャーサイン／あなたが聞こえない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「愛は静けさの中に」でオスカーに輝いたM・マトリンが自身と同様の耳の不自由なヒロインに扮して、沈黙の恐怖を体現したサスペンス。ハンディを克服してジムのインストラクターとして自立する女性ジリアン。生徒が何者かに殺された日から、彼女の周囲にはその身を脅かす謎めいた事件が起こり始める……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN A SHALLOW GRAVE", "哀しみのラストダンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "M・ビーンが主演した哀切のラブ・ロマンス。彼が扮するのは、第二次世界大戦で大ヤケドを負い、見るも痛々しい姿となった青年ガーネット。彼は自分の容姿へのコンプレックスから、愛する恋人ジョージナにすら会おうとせず、かたくなに心を閉ざしてしまう。やがて、ひょんなことから知り合った気のいい青年ポッターに、彼女への手紙を託すようになるガーネット。しかし、次第に彼はポッターとジョージナが愛し合っているのではと疑うようになる……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使のはらわた 赤い眩暈", "天使のはらわた 赤い眩暈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "夜勤の夜、強姦された看護婦・名美と、彼女を車ではねてしまった村木。二人のつかの間のふれあいと悲劇的結末を描き出す。これまで同シリーズの原作・脚本を担当してきた劇画作家・石井隆の監督デビュー作。村木の死に託して、やがて終焉を迎えるにっかつロマンポルノの挽歌のようだ。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRAYSTOKE ： THE LEGEND OF TARZAN，LORD OF THE APES", "グレイストーク　類人猿の王者 ターザンの伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "バロウズのベストセラー『類猿人ターザン』を忠実に映画化。ターザンを単に英雄にした従来の作品とは異なり、シリアスな人間ドラマとなっている。名門グレイストーク家の子ながら、アフリカで猿に育てられたターザンは、やがて密林の王者となった。しかし、一人の男を助けたことからグレイストークの家へ帰ることになる。文明や社会の規律と本能の間で苦悩するターザン。だが自分を育ててくれた父猿が射殺され、ここは自分の住む世界ではないと気付くのだ。愛する者に背を向け、密林に帰っていくターザンの姿は悲しくも美しい。リック・ベイカーによる表情豊かな猿のメイクも、野性の純粋さを強く訴えている。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マジでガチなボランティア", "マジでガチなボランティア", "", "", "http://image.eiga.k-img.com/images/movie/55756/315x210.jpg?2976", 0, "“ギャル”や“ギャル男”と呼ばれる若者たちのカンボジアでのボランティア活動を記録したドキュメンタリー。合コンやナンパに明け暮れていた医大生たちが、ひょんなことからボランティア活動と出合い、カンボジアに小学校や診療所を建てることを決意する。多額の借金を抱えながらも本気でボランティア活動に取り組む彼らの姿を3年間に渡って追う。「ソラニン」「おにいちゃんのハナビ」の高良健吾がナレーションを担当する。", "2010-12-04", "76,52", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Grbavica", "サラエボの花", "", "", "http://image.eiga.k-img.com/images/movie/53096/315x210.jpg?1668", 95, "ボスニア内戦から10数年の時を経たサラエボを舞台に、戦争の犠牲となった女性の再生と希望を描いた人間ドラマ。女性監督ヤスミラ・ジュバニッチがメガホンを取り、デビュー作にして06年度ベルリン国際映画祭でグランプリに輝いた。母娘2人でつましい生活を送るエスマと12歳のサラ。シャヒード（殉教者）と聞かされていた父親の死に疑問を持ち始めたサラは、真相を話そうとしないエスマに反感を募らせていき……。", "2007-12-01", "39,40,77,7,41", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シュアリー・サムデイ", "シュアリー・サムデイ", "", "", "http://image.eiga.k-img.com/images/movie/54666/315x210.jpg?2199", 122, "小栗旬が小出恵介を主演に初監督に挑んだ青春群像劇。勝地涼、ムロツヨシ、妻夫木聡、竹中直人ら豪華俳優陣が脇を固める。高校でバンドを組む5人組が文化祭中止に抗議するため、爆破予告をして高校を占拠するが、仲間の1人が本物の爆薬を仕掛けたことで大爆発を起こしてしまう。事件から3年後、責任を取って学校を中退した5人は、行き詰った人生をやり直すためにある作戦を思いつく。", "2010-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ア・ホーマンス", "ア・ホーマンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "異様な無国籍的ムードに彩られた新宿を舞台に、過去の記憶を持たない男とハミダシヤクザの友情、闇の世界に生きる人間たちの内幕を描いた作品。演技派性格俳優としての地位を固めつつあった松田優作が久々にアクションに復帰、自ら監督もこなした。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ghost Town", "オー！マイ・ゴースト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("錆びたペンダント", "錆びたペンダント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "渡哲也扮する一流ナイトクラブのバーテンが、無実の罪で服役。刑期を終えて出所後、真犯人を捜し始める……。江崎実生が随所に情感のこもった演出を見せるが、ヒロイン役の山本陽子が力不足で、作品のバランスがくずれてしまった。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢二", "夢二", "", "", "http://image.eiga.k-img.com/images/movie/39916/315x210.jpg?3968", 128, "「ツィゴイネルワイゼン」（1980）、「陽炎座」（81）に続く鈴木清順監督の“浪漫3部作”の完結編で、大正から昭和にかけて活躍した画家・竹久夢二の半生を幻想的に描き出す。恋人の彦乃と駆け落ちするため夢二は金沢の湖畔へやってくるが、そこに彦乃は現れず、さらには静かで小さな村に突然の銃声が鳴り響く。妻を寝取られた男・鬼松が、妻と妻を寝取った男を殺して山に逃げ込んだようなのだが……。夢二役は「カポネ大いに泣く」（85）に続き清順監督作の主演となった沢田研二。2012年、「ツィゴイネルワイゼン」「陽炎座」とあわせてニュープリントでリバイバル上映。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BE FREE！ビー・フリー", "BE FREE！ビー・フリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "『コミックモーニング』に連載された江川達也の劇画の映画化で、脚本の一色伸幸はこれでデビューを飾った。舞台となる高校はABC……Z組と能力別にクラス編成されているが、さらにその下に落ちこぼればかりを集めて隔離した“さくら組”が作られる。そこにドジで助平で、おまけにおっちょこちょいの教師・笹錦が左遷されてきたことからトラブルが始まる。化粧のうまいオカマの少年、色情狂の女生徒、すぐ自殺したがるブスなど問題児が入り乱れて目茶苦茶な物語が展開。学歴社会を皮肉った作品だが説教じみたところが微塵もなく、主人公たちの自由奔放に生きる姿は実に気持ちがいい。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL　ALAMEIN　LA　LINEA　DEL　FUOCO", "炎の戦線／エル・アラメイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「エーゲ海の天使」などの脚本で知られるE・モンテレオーネが、第二次世界大戦における北アフリカ戦線エル・アラメインの戦いを描いた戦争ドラマ。ドイツの同盟国として参戦していた伊軍にスポットを当て、同軍が英軍の猛攻撃と補給不足で前線に孤立してしまった惨状を、一人の学徒志願兵の目を通して冷徹に描いた力作。1942年の北アフリカ戦線エル・アラメイン。その最南端のイタリア歩兵陣地に、一人の学生志願兵セッラが赴任する。祖国への忠誠心を胸に秘めて着任したセッラだったが、過酷な戦時下の惨状を目の当たりにし、愕然とする。", "2002-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("近藤勇 池田屋騒動", "近藤勇 池田屋騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "戦前の日活で活躍した池田富保監督が菁穂と改名、新東宝で発表した戦後第１作。夜ごとの遊興、はては町民からの金品強奪。乱行が続く新撰組の局長・芹沢を、次長の近藤勇は深く憂えた……。“アラカン”演じる近藤勇を中心に、仇討ち騒ぎ、池田屋騒動、美男剣士・沖田総司の恋などを描いた娯楽時代劇。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが愛", "わが愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "井上靖の小説『通夜の客』の映画化。敗戦後４年たったある秋の夜、新津礼作の通夜の席に見知らぬ女性が現れ、水島きよとだけ名乗って姿を消す。実は、きよは17歳の頃から新津を思い続けてきたのだった。きよの回想から二人の悲痛な愛の軌跡が明らかになる……。見事な撮影で描かれたメロドラマの名編。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版イナズマイレブン　最強軍団オーガ襲来", "劇場版イナズマイレブン　最強軍団オーガ襲来", "", "", "http://image.eiga.k-img.com/images/movie/55606/315x210.jpg?3475", 0, "ニンテンドーDSの大ヒットゲームを原作に、サッカー日本一を目指す弱小チームの奮闘を描いたテレビアニメ「イナズマイレブン」の劇場版。熱血キャプテンの円堂によってめきめきと実力を上げた雷門中サッカー部は、中学サッカー日本一を決める大会「フットボールフロンティア」で決勝戦に進出する。しかし彼らの前に現われたのは対戦相手の世宇子中ではなく、ヤミの勢力によって未来から送り込まれた最強軍団「オーガ」だった。", "2010-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEVIL'S TREASURE", "デビルズ・パイレーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "18世紀中頃のある夜、ボーンズ船長から“宝島”の地図を受け取った少年ジムは、仲間とヒスパノーラ号に乗り込み島へ向かう。だが、片足の船長シルバーが、海賊の本性を現わし始めた。奴らより早く宝を見つけ出すために、ジムたちは大冒険へと飛び出す——。R・L・スティーヴンソンの小説『宝島』をベースにした、海洋冒険活劇。船長にO・リード、片足シルヴァーにC・ヘストン、少年役に「太陽の帝国」のC・ベールといったスターがずらり。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Down and Out in Beverly Hills", "ビバリーヒルズ・バム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　ビバリーヒルズの一角にある豪邸で妻子とともにリッチな生活を送る実業家のデイブ。しかし家族はそれぞれ問題を抱えていた。そんなデイブの邸宅のプールで、ホームレスの男が入水自殺を図る。デイブは彼を助け、邸宅に住まわせることに。やがてジェリーと名乗るホームレスの男は富豪一家に思わぬ影響を与えていく。デイブはジェリーが疎ましくなるが……。フランス映画「素晴らしき放浪者」をリメイク。アメリカ富豪層の生活を風刺したコメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くれないものがたり", "くれないものがたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "官能とバイオレンスを追求する池田敏春監督が、赤江瀑の『千夜恋草』を映画化。紅葉にむせかえる京都を舞台に、香道の名人とその妻の倒錯した性の世界を、典雅な映像で描く。ヒロイン・竹井みどりの妖しい美しさ、彼女に魅了される高校生を演じる三上祐一のナイーブな存在感が魅力。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METISSE／CAFE´ AU LAIT", "カフェ・オ・レ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「クリムゾン・リバー」のM・カソヴィッツの監督デビュー作。18歳の少女ローラの部屋で、白人の恋人フェリックスと黒人の恋人ジャマルが鉢合わせしてしまう。混乱する二人に彼女は、どちらが父親か分からない子供を妊娠していると告白。ローラと赤ん坊をめぐって二人の対立が始まる……。敵対しながらも不思議な絆で結ばれていく二人の青年と、一人冷静な少女の奇妙な関係を、軽やかに綴ったラブ・コメディ。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤城の子守唄", "赤城の子守唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "おなじみ、天保の頃の侠客、国定忠治の物語を、忠治の子分・板割浅太郎を中心に描いた時代劇。農民たちの窮乏を見かねた国定忠治とその子分たちは、代官所を襲って貯蔵米を放出。彼らは、赤城山に立てこもる。追われる忠治の身を何とかしようと浅太郎は、東奔西走。しかし、その努力も空振りに終わり、赤城山に戻ってみると、忠治一党に政吉という、密偵が入り込んでいて……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅粉／BLUSH", "べにおしろい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "1950年、人民政府が成立して街の娼館はすべて閉鎖された。港南地方の小都市で働いていた娼婦チウイーとシャオも労働改造所に連行される。チウイーは脱走してなじみの客だった金持ちのプーの元に身を寄せるが、結局尼寺へ駆けこむ。一方シャオは、工場労働者として不本意な生活を送っていたが、プーと再会。結婚して子供が生まれるが、共産党がプー家の財産を没収してしまう。男に頼らずに独りで主体的に生きようとするチウイーと、男に頼らなければ生きていけないシャオ。激動の時代を生きた女二人と一人の男の生き方を、水の都、蘇州を舞台に華麗に描いていく。", "1994-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Night of the Living Dead 3D", "超立体映画　ゾンビ3D", "", "", "http://image.eiga.k-img.com/images/movie/34316/315x210.jpg?4549", 80, "ジョージ・A・ロメロ監督の傑作ゾンビ・ホラー「ナイト・オブ・ザ・リビングデッド」を3D立体映像でリメイクしたアトラクション・ムービー。兄と一緒に叔母の埋葬に参列したバーブ。ところが、墓地ではゾンビとなって蘇った死者たちが、生きた人間を襲ってその肉を喰らっていた。混乱の中で兄とはぐれてしまったバーブは、バイクに乗った青年ベンに助けられる。2人は近くの農家に避難するが、無数のゾンビたちに包囲されてしまい……。", "2007-11-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MALLRATS", "モール・ラッツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ショッピング・モールにたむろする若者たちが巻き起こす珍騒動を描いた青春コメディ。気ままな学園生活を送るTSとブローディはある日、二人そろって彼女に捨てられてしまう。たまり場のショッピング・モールで、何か憂さ晴らしはないかと考える二人。ところがそこに、ブローディの元の彼女ルネッサが新しい恋人と現れたことから……。『スパイダーマン』や『超人ハルク』を生み出した伝説の漫画家スタン・リーが実名で登場。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HANGING TREE", "縛り首の木", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "金鉱地スカルクリークにやって来た医師ジョーは、ケガをした若者ルーンを救う。ジョーの召使となったルーンは過去を明かさぬジョーの身の上を不思議に思うが、そのころ、強盗に襲われた馬車から生き残った女がジョーのところに運び込まれ、彼の過去が暴かれる……。人間心理の暗部をえぐり出した異色ウエスタン。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男前　泣いて笑って泥まみれ", "男前　泣いて笑って泥まみれ", "", "", "http://ecx.images-amazon.com/images/I/51RpS7M11DL._SL210_.jpg", 110, "元極道という経歴を持つ画家・山本集が、智弁学園高校野球部監督時代の部員たちとの熱い交流をつづった同名ノンフィクション小説を映画化。新設されたばかりの奈良五弁学園野球部の監督に就任した山本は、甲子園出場を目指して猛特訓を開始する。そんな山本の熱い指導に部員たちが信頼を寄せはじめた矢先、山本は体罰をふるったとして退職を迫られてしまう。主演を務めるのは、Vシネマ「実録・なにわ山本組」でも山本集役を演じた風間貢。", "2005-02-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMANCE", "ロマンスX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "性について追及し続ける女流監督C・ブレイヤが、男女間における愛情と性欲の問題を真正面から見つめる問題作。小学校教師のマリーは、同棲中の恋人に相手にされず、孤独と不満を募らせている。やがて彼女は行きずりの男たちとの一夜の関係に身を任せ、性欲を満たすようになる。名匠テオ・アンゲロプロス作品で知られるヨルゴス・アルヴァニティスによる詩的な映像で、セックスレスに悩む女性の姿を赤裸々に映し出す。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Source Code", "ミッション：8ミニッツ", "", "", "http://image.eiga.k-img.com/images/movie/56216/315x210.jpg?3777", 94, "デビュー作「月に囚われた男」が評判となったダンカン・ジョーンズ監督の第2作。シカゴで乗客全てが死亡する列車爆破事件が発生。犯人捜索のため政府が遂行する極秘ミッションに、米軍エリートのスティーブンスが選ばれる。事故犠牲者の事件発生8分前の意識に入り込み、その人物になりすまして犯人を見つけ出すという作戦で、必ず8分後には爆破が起こり元の自分に戻るスティーブンスは、何度も「死」を体験するうちに次第に作戦への疑惑を抱きはじめる。", "2011-10-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kinsey", "愛についてのキンゼイ・レポート", "", "", "http://image.eiga.k-img.com/images/movie/1106/315x210.jpg?3868", 118, "1948年、セックスについての統計をまとめた「キンゼイ・レポート」を出版して全米にセンセーションを巻き起こしたインディアナ大学の助教授キンゼイ博士。著作はベストセラーになるが、キンゼイには苦難の日々が待っていた。実在したキンゼイ博士と妻クララの絆を描く人間ドラマ。監督・脚本は「ゴッド・アンド・モンスター」のビル・コンドン。ゴールデン・グローブ賞の作品賞、主演男優賞、主演女優賞にノミネート。", "2005-08-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BURNT　BY　THE　SUN", "太陽に灼かれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "「黒い瞳」「ウルガ」等、叙情的な作品で高い評価を得ているN・ミハルコフ監督が、製作、脚本、主演を兼ねた意欲作。1936年夏、モスクワ郊外の別荘地で家族と休暇を過ごすロシア革命の英雄コトフ大佐のもとに、ドミトリという男が訪れる。彼は10年間消息を絶っていた、大佐の妻マルーシャのかつての恋人だった。再会し、過去を悔やむ二人。そんな中、ドミトリがコトフの娘ナージャに話した『おとぎ話』は、コトフによって巧妙に引き裂かれたドミトリとマルーシャの悲恋物語だった。真実を知ったマルーシャは夫を責めるが、彼女の愛を失いたくないコトフは心から謝り、次第に彼女の心は安らいでいく。だがドミトリの目的はそれだけでなく、秘密諜報員として大佐の粛正のためだった……。スターリン大粛正の下、運命に引き裂かれた男女の悲劇がロシアの広大な草原を舞台に、透明感あふれる映像で描かれている。監督の愛娘ナージャも好演。", "1994-01-01", "24,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天保水滸伝 大原幽学", "天保水滸伝 大原幽学", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "大飢饉が相次いだ江戸・天保年間。厳しい年貢の取り立てや、新興ヤクザの勢力拡大で疲弊する農民たちを見て、農民の結束を呼びかけて立ち上がった、農協の始祖・大原幽学をクローズ・アップした作品。農協のキャンペーン映画でもある。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hunger", "ハンガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　不死の生命力を持つ吸血鬼のミリアムは生きた時代ごとに愛する者を見つけ、自分の仲間にしていた。ニューヨークに暮らす今の相手は、18世紀のイギリスで見つけたジョン。ミリアムは吸血鬼になることを条件に彼にも生命力を与えていたが、200年以上も若さを維持することは不可能だった。衰えを感じ始めたジョンは老化を研究している女医サラに会うが……。美男・美女の吸血鬼の末路を華麗かつ斬新な映像美で描いた退廃的スリラー。", "1983-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お六櫛", "お六櫛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "陽気とともに幹と幹とが摩擦して火を発する樹木を人々は木賊と呼んだ。この木の生い茂る信州・蓼科山麓の小さな農村では、秋になると持ち場を割り当てて木賊の刈り取りが行われた——。父親が木賊刈りを怠って山火事を起こしたために、村人の迫害を受けながらひっそりと暮らす兄妹の悲劇を描いた正木不如丘の代表作『木賊の秋』を、ストーリーテラ−の伊藤大輔監督が映画化。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOKYO 10＋01〈トーキョーイレブン〉", "TOKYO 10＋01〈トーキョーイレブン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "近未来のTOKYOを舞台に、11人の若者が殺人ゲームを繰り広げるアクション。謎の組織が開催するゲームに参加するために、無作為に集められた11人の若者たち。勝者には賞金３億円と、過去の悪事がすべて精算されるという特典が付くという。だが、11時間以内にゴールできなければ“死ぬ”という恐るべき追加ルールがあった。スタイリッシュな映像と斬新なサウンドで描かれる壮絶なバトルが見もの。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOSSAFER", "トラベラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "A・キアロスタミの初めての長編劇映画。落ちこぼれの10歳のガッセムは、サッカーに夢中で、テヘランで行われる大事な試合は何としても見に行くつもりだ。実際彼は、親から金を盗み、先生に嘘をつき、友だちを裏切って、スタジアムにやって来る。しかし彼は疲れ果てていて、試合の始まる前に眠ってしまう。彼が起きた時には、すでに試合は終わっていた。DVDは「パンと裏道り」が併録されている。", "1974-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妻として女として", "妻として女として", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ある中年の大学教授をめぐり、長い間愛人として尽くしてきた女と、子供ができない身体のため、その愛人の子を引き取って育てた妻との対立を描いた成瀬巳喜男作品。高峰、淡島の火花散る演技合戦はすさまじく、どっちつかずの男を演じる森雅之の圧倒的な存在感も強い印象を残す。三者が対面するクライマックスは圧巻。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("巡城馬", "ポストマン・ファイツ・バック／巡城馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "郵便配達人の青年とその仲間は、ある品物を運んでくれるよう頼まれた。そこから事件に巻き込まれ……。チョウ・ユンファがペテン師を演じる痛快現代アクション。", "1982-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WINNIE　THE　POOH　AND　THE　BLUSTERY　DAY", "プーさんと大あらし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "ディズニー・アニメ「くまのプーさん」の続編。くまのプーさんと仲良しのピグレットが、大風でフクロウの家まで飛ばされてしまう。ところが、大雨が降り始め、家が崩壊。プーさんをはじめ、動物たちはクリストファーの家に避難するが、トラブルはまだまだ続き……。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東方三侠／THE HEROIC TRIO", "ワンダーガールズ 東方三侠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "香港３大トップ女優が活躍する男顔負けのスーパー・アクション。世界征服をたくらむ悪の大王の秘密訓練場に集められた３人の美少女、ツン、チャット、チンは、敵を倒すためのパワーとテクニックの英才教育を施され、その美しさとは裏腹に、凄まじい破壊力を持つ人間兵器として完成していく。ツンとチャットは、途中で大王の悪に気付き、脱走するが、残されたチンは、大王の手下として赤ん坊連続誘拐犯となってしまう。それを知ったツンとチャットは苦悩する。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mariｎes Go Hone／辺野古・梅香里・矢臼別", "Mariｎes Go Hone／辺野古・梅香里・矢臼別", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "米軍基地に暮らす人々の知られざる戦いを見つめたドキュメンタリー。北海道・矢臼別にある自衛隊演習場の真ん中に40年以上も住み続ける川瀬氾二、米軍の演習に苦しんできた韓国・梅香里のチョン・マンギュ。米軍と自国の対応に、異を唱える彼らの戦いを追う。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUS LE SOLEIL DE SATIN", "悪魔の陽の下に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1987年５月、カンヌ映画祭グランプリを得ながらも凄まじい賛否の渦に巻き込まれたいわくつきの作品。フランス映画の鬼才M・ピアラが渾身の力を込めて描いた、聖性と魔性との葛藤の物語である。どこかブレッソンを思わせる禁欲的なスタイルの中で、北フランスの農村を審美的ともいえる映像で見事に捉えた力作。北フランス・カンパーニュの司祭ドニサンは、並はずれた苦行の中に神への信頼と懐疑への抉別を求めていた。しかしある時、彼は悪魔と出会う。彼は悪魔によって人の心を読み取る力を授けられるが、恋人を殺害し絶望にうちひしがれる少女をついに救うことができない。数年後、彼はある少年の死に立ち会うが、“奇跡を起こせ”との心の声を聞き……。鈍重な主人公が真の聖性へと肉薄しながらも悪魔との戦いにもろくも敗れていく。原作の枝葉を刈り取った脚色も見事である。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("羅生門", "羅生門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "世界にクロサワの名を知らしめた歴史的作品。原作は芥川龍之介の短編「藪の中」。平安時代、都にほど近い山中で貴族女性が山賊に襲われ、供回りの侍が殺された。やがて盗賊は捕われ裁判となるが、山賊と貴族女性の言い分は真っ向から対立する。検非違使は巫女の口寄せによって侍の霊を呼び出し証言を得ようとする、それもまた二人の言い分とは異なっていた……。豪雨に浮き立つ羅生門の造形美、立ち回りシーンの迫力、生き生きとした役者たちの演技などすべてが印象深い作品。ベネチア国際映画祭でグランプリを受賞した、黒澤明の出世作である。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dilemma", "僕が結婚を決めたワケ", "", "", "http://image.eiga.k-img.com/images/movie/55826/315x210.jpg?2420", 0, "「アポロ13」「ビューティフル・マインド」の名匠ロン・ハワードが、結婚に揺れ動く40代の男性の心理を軽妙なタッチで描くロマンティック・コメディ。自動車関連の会社を経営する独身男ロニーは、一緒に暮らしている恋人ベスにプロポーズすることを決意する。そんな矢先、親友ニックの妻が若い男と浮気している現場を発見してしまい、結婚というものに疑問を抱くようになる。主演は「ウェディング・クラッシャーズ」のビンス・ボーン。", "2011-01-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瞳の中の訪問者", "瞳の中の訪問者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "手塚治虫の人気マンガ『ブラック・ジャック』の“春一番”というエピソードの映画化。事故で失明した美少女が、角膜移植手術によって再び見えるようになるが、新しい目には他人には見えない男の姿が映る。移植された角膜は、湖で殺された若い女のものであった。2001年のタイ映画「the EYE」などに影響を与えた。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("元始、女性は太陽であった・　平塚らいてうの生涯", "元始、女性は太陽であった・　平塚らいてうの生涯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "ベテラン女流映像作家、羽田澄子が、明治から昭和にかけて女性解放運動に貢献した平塚らいてうの生涯を追ったドキュメンタリー。女性だけの手による文芸誌『青踏』を25歳で刊行、日本で最初の女性運動団体を結成、婦人の参選権獲得への道を開いたらいてう。彼女の動く映像はわずかながら、その精力的な活動の情熱が感じられる作品となっている。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すばらしい蒸気機関車", "すばらしい蒸気機関車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "明治時代から100年以上にわたり、日本の美しい風景の中を走り抜けてきた全国の蒸気機関車を捉えたドキュメンタリー。大ヒットした本作の公開より5年後に、北海道で運行していた蒸気機関車を最後にその姿を消すことになる。全国津々浦々のローカル線で当時、活躍していたその雄姿を高林監督は執念と愛着を込めて追いかける。また、後の大物監督と知られる大林宣彦が、劇中音楽を担当している。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四谷怪談", "四谷怪談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "おなじみ鶴屋南北の『東海道四谷怪談』を、ベテラン小国英雄が新人の田辺虎男と共同で脚色している。伊右衛門に殺されたお岩が、無念をはらすため夜な夜な化けて出る。相馬千恵子が不気味なメイクアップでお岩を演じる。原作に忠実でオーソドックスな怪談に仕上がっている。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バブルと寝た女たち", "バブルと寝た女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "バブル経済絶頂期、実家の父が家出をしたため、学費が払えなくなった美大生みどりは銀座のホステスのアルバイトを始める。やがて、彼女は月 200万円で客の青年実業家、須賀の愛人となった。23歳で銀座の高級クラブを任されたみどりはリッチな生活を手に入れるが、ついにバブル経済が崩壊する時が来る……。家田荘子の同名ノンフィクションをもとに、バブルに躍らされた女の破滅的な愛を哀切を込めて描く。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　てのひらを太陽に", "それいけ！アンパンマン　てのひらを太陽に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "劇場版登場10周年、シリーズ第10作を記念した、やなせたかし原作のファンタジー・アニメ。大根役者の旅一座のリナちゃんがブラック大魔王に襲われ、アンパンマンが危機一髪で救い出す。しかし、大魔王の吐いた黒い息によってリナちゃんの仲間たちは石になってしまう。みんなを助けるためにアンパンマンは大魔王が住む、くらやみ谷に向かうのだが……。", "1998-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新男の紋章・度胸一番", "新男の紋章・度胸一番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "第４作で監督は松尾昭典からベテランの滝沢英輔にかわった。竜次は、軍医として中国戦線に赴き、いちだんと成長して帰国する。戦友の実家の組の窮地を救うために竜次は、母の義弟で自分には叔父にあたる男と闘うことになる。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNTAMED HEART", "忘れられない人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "“1990年代版「ある愛の詩」”と評された切ないラブ・ストーリー。ウェイトレスのキャロラインは暴漢に襲われかけたところを、同じ店で働く孤児院育ちの寡黙な青年アダムに助けられた。やがて恋に落ちる二人。だが、心臓を病むアダムは、彼女を襲った暴漢の復讐に遭って重傷を負ってしまう……。アクションからコメディまで演じわけるC・スレーターが誠実な青年を好演している。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カモとねぎ", "カモとねぎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "３人の詐欺師が競艇の八百長でひともうけした。その金を狙って女が現れるが、３人の詐欺師は女と手を組む。この４人が「スパイ大作戦」さながらに、あの手この手で詐欺をくり返すコメディ。邦画には数少ない犯罪喜劇の良質の１本。硬派の作品が多い森雅之が珍しく笑わせる。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("象物語", "象物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "アフリカ・ケニアの大自然の中、病気で死んだ母象に代わって、生まれたばかりの弟象を密猟者たちから必死で守ろうとする兄象の姿と、彼らの集団生活ぶりをきめ細かく描いたドキュメンタリーの動物映画。ナレーションを岡田英次が務めている。監督の実兄でもある蔵原惟繕が総監督にあたっている。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わすれなぐも", "わすれなぐも", "", "", "http://image.eiga.k-img.com/images/movie/57976/315x210.jpg?2636", 25, "アニメーションの制作環境の問題解決や振興を図ることを目的とした文化庁の若手アニメーター育成プロジェクト「アニメミライ」2011年度制作の4作品のうちの1本。古書店を営む硯は、かつて陰陽師が大蜘蛛を封印したという一冊の本を手に入れるが、そこから身長20センチほどの小さな娘蜘蛛が出現する。母親を捜し求める娘蜘蛛に同情した硯は、娘蜘蛛をあるべき場所へ返そうとするのだが……。古代と現代が結びついて巻き起こる騒動を、ユーモアを交えて描く伝奇アクション。制作はプロダクションI.G。監督は、同社が手がける「テイルズ・オブ」シリーズなどに参加してきた海谷敏久。", "2012-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE ROUGE EST MIS", "赤い灯をつけるな", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "フランス暗黒小説の重鎮A・L・ブルトンが自らの原作を脚本化。J・ギャバンと、デビュー間もないL・ヴァンチュラが共演したフィルム・ノワール。運命の輪の中で友情を守り、あるいは裏切りながら、悲劇の道をたどるというギャングたちの挽歌。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLOUR　BLOSSOMS", "桃色", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "香港の高級アパートの一室を舞台に、男と女の欲望が絡み合う官能ドラマ。松坂慶子ほか台湾や韓国など東アジア各国から集まった俳優陣が妖しい魅力を披露し、時空を超えた愛と欲望のファンタジー・ワールドをかたどっていく。退廃美を打ち出したスタイリッシュな映像もムードを高める1本だ。日本人女性から高級アパートの仲人を頼まれたメイライ。彼女はその部屋の虜となり、目の前に現れた二人の男の間を揺らめくことに。", "2005-05-14", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARCTIC BLUE", "ルトガー・ハウアー　処刑脱獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "酷寒のアラスカで、狩猟によって暮らしている男たち。そのリーダーのベンは街からやって来たハンターといさかいを起こし、逮捕されてしまう。ベンは環境調査官のエリックの操縦するセスナで刑務所に送られるが、その途中、ベンは保安官を殺し、セスナを不時着させる。ベンとエリックは雪の中を街へと帰ることになる。いがみ合いながらも、やがて友情が芽生えた二人だったが、街へ戻るとベンの弟分たちがエリックの家を襲撃していた……。決死のサバイバルを描いたアクション作品。", "1994-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AQUARIUS", "アクエリアス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ホラー・ミュージカルをリハーサル中の劇団員たちが、嵐の夜、突如劇場内に閉じ込められ、フクロウ姿の殺人鬼の手によって次々と惨殺されていく……。イタリアン・ホラーの鬼才ダリオ・アルジェントの門下生、M・ソアヴィの監督デビュー作。奇想天外なアイデアと粘っこい残酷描写の数々でまとめ上げられたスプラッタ・ホラー。アヴォリアッツ国際ファンタスティック映画祭では最優秀恐怖映画賞を受賞。", "1986-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MARRIAGE CIRCLE", "結婚哲学", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ドイツからアメリカに渡り、その洗練された映画技法によって数多くの名作を生んだエルンスト・ルビッチ監督のサイレント期の最高傑作の一つ。上流社会の男と女の物語を、しゃれたユーモアたっぷりに描いていく。“ソフィスティケイテッド・コメディ”はルビッチお得意のジャンルで、後代のビリー・ワイルダーらに大きな影響を与えたが、その元祖ともいえる作品だ。ウィーンに住むシュトック教授は夫婦の間に倦怠期を迎えていた。一方、妻のミッツィは親友のシャルロットの夫フランツに心を寄せはじめていた。妻の浮気に気付いたシュトック教授が探偵を雇ったことから、二組の夫婦のドタバタ劇が始まる。", "1924-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Tourist", "ツーリスト", "", "", "http://image.eiga.k-img.com/images/movie/55376/315x210.jpg?2740", 103, "アンジェリーナ・ジョリーとジョニー・デップが初共演を果たしたミステリードラマ。「善き人のためのソナタ」のドイツ人監督フロリアン・ヘンケル・フォン・ドナースマルクが、フランス映画「アントニー・ジマー」（2005）をリメイクする。イタリアへ傷心旅行にやって来たアメリカ人のフランクは、ナゾの美女エリーズと運命的な出会いを果たす。しかし、彼女と恋に落ちたフランクは、いつしか巨大な陰謀に巻き込まれていく。", "2011-03-05", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎春の夢", "男はつらいよ・寅次郎春の夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ビタミン剤のセールスマン・マイケルは遠くアリゾナからやって来たが、品物がまったく売れず行き倒れ寸前。“とらや”の人々が面倒をみることになったが寅さんはアメリカ嫌い。そこへ美しい英語の先生が顔を出したから事態は一変……。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kansas", "カンザス　カンザス経由→N.Y.行き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "同じ列車に偶然乗り合わせた二人の若者。片や友人の結婚式に向かう途中の好青年、片や生まれ育った町に帰って何か大きなことをやってやろうという前科者。好青年は誘われるままに前科者の故郷、カンザス州チェロキーで列車を降りるのだが……。M・ディロンとA・マッカーシーの共演による青春映画。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／恣女", "マーニム 愛の嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "なかなか子供に恵まれない夫婦。夫の母親は妻をなじり、ある女を雇って子供を生ませようとするがそれも失敗。やがて夫の方に原因があるとわかり、母親は雇った女と下男をまじわらせて子を生ませ、孫として育てようとするのだが……。儒教の道徳観と現代の倫理を鋭くえぐる、韓国大鐘賞受賞作。", "1984-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SAMURAI ザ・サムライ", "THE SAMURAI ザ・サムライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "袴と日本刀を身につけサムライ言葉を駆使する超アナクロ高校生・血祭武士をめぐる、アクションとお色気の熱血青春爆笑編。マドンナ山口敦子の争奪を賭けた、超モダン財閥御曹子との破天荒な対決が痛快だ。風来坊役で菅原文太が友情出演。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジューンブライド ６月19日の花嫁", "ジューンブライド ６月19日の花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "人気作家・乃南アサの小説を映画化したラブ・サスペンス。事故に遭った池野千尋は前田一行という男に救われるが、今までの記憶をすべて失っていた。やがて彼女は自分が６月19日に結婚する予定であることを思い出す。その後、彼女は心当たりの人物や場所を歩き回る。客とも寝るやり手のホステス、劇団員、結婚式直前になって失踪した女。記憶を取り戻していくたびに、彼女は様々な顔を持つ自分を知り、混乱するばかり。そんな時、彼女は誰かに命を狙われていることに気づく……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOMENT BY MOMENT", "年上の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "TVドラマのライターとしてエミー賞受賞の実績を持つ女流監督、ワグナーが、中年の女心を巧みに描いた力作。夫の浮気に心悩ます人妻と、行きずりに出会った街の若者との激しい恋の物語。トラヴォルタがひたむきな若者を熱演。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE　OF　DRACURA", "ドラキュラの屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "ドラキュラ、狼男、フランケンシュタインの三大モンスターが揃い踏みした怪奇映画。彼らに加え狂人博士も登場して怖さは超ド級もの。博士の城を陽光に弱いドラキュラが訪れ、昼間でも行動できるように治療してくれと頼まれる。次には狼男がやって来て、取り憑く狼の呪いを解いてほしいと懇願する。さらにフランケンシュタイン博士が作った怪物が現れて……。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACROSS THE SEA OF TIME", "遥かなる夢・ニューヨーク物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "アイマックス社が作り上げた3D立体映画であり、東京と大阪に専門映画館が作られ、そこで上映された作品。11歳のトーマス少年がロシアからニューヨークへ密航して来る。彼の手には、先祖でありアメリカに移民したレオポルドが撮影したニューヨークの立体写真が握られていた。トーマスはこの写真を手掛かりに、自分の肉親でもあるレオポルドの妻、ジュリアを探そうとしているのだった。ウォールストリート、チャイナタウン、エンパイアステート・ビルなどニューヨークの観光地が数多く登場するのも見もの。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAADASSSSS！", "バッドアス！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ブラック・ムービーの元祖となった伝説の映画「スウィート・スウィートバック」の製作の舞台裏をドラマとして描いた力作。メルヴィン・ヴァン・ピーブルズ監督がたどった苦難の道のりを、その息子マリオの監督・主演によって再現する。逆境を跳ね返す、創作意欲の熱さに胸を打たれる。保守的なハリウッドに背を向け、自主製作で黒人映画の製作に着手した映画監督メルヴィン。そんな彼に次々と難題が降りかかり……。", "2005-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN KICKBOXERII", "リアル・キックボクサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "父親の跡を継いで会社社長となったリリアンは、６歳の娘を誘拐され身代金200万ドルを要求される。彼女は二人の男に助けを求めた。一人は別れた夫で刑事のマイク。もう一人は元恋人で武道家のデビッド。二人の男は衝突しながらも、誘拐犯を追う。元WKAミドル級チャンピオンのD・A・クックの主演作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALIGULA: LA STORIA MAI RACCONTATA", "カリギュラII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "残虐な暴君として知られるローマ帝国３代目・皇帝カリギュラの非道な行為を描いた、エロティック・ドラマの続編。権力を極限まで振るい、レイプや処女狩り、乱交、獣姦など、連夜のように性の宴を繰り返していたカリギュラ。人間性を失った彼は、次第に狂気の底へと落ちていく……。", "1981-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOSTLY MARTHA", "マーサの幸せレシピ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "仕事一筋で生きてきた30代女性が、周囲の人々との交流を通して本当の幸せに気付くまでを描くハートフル・ドラマ。色鮮やかな料理の数々が五感を刺激する。独身の女性シェフ、マーサが、事故死した姉の娘リナを引き取ることになる。だが、自慢の料理をふるまっても、リナは食事を口にしようとしない。落ち込むマーサは、彼女とは対照的に陽気なイタリア人シェフ、マリオとの出会いによって、自分の人生と料理に足りなかったものに気付くのだった。", "2001-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋は五・七・五！", "恋は五・七・五！", "", "", "http://ecx.images-amazon.com/images/I/5162nJ5wSfL._SL210_.jpg", 105, "ロングラン・ヒットを記録した「バーバー吉野」の荻上直子監督の第2作。俳句をポップな存在として取り上げ、“俳句甲子園”を舞台に、17音で想いを表現する高校生たちの不安や恋の悩みを切なさとたっぷりの笑いで描いた青春群像劇。前作同様に、荻上監督の独特のユーモアが炸裂している。クラスになじめない帰国子女の治子は、漢字練習のため、半強制的に俳句部へ入れられる。しかし、部員のほとんどが素人で……。", "2005-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BE＾TE", "邪淫の館 獣人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "没落寸前の貴族一家を救うために嫁いだ貴婦人。だが相手は、先代の妻が獣と交わって生まれた獣人だった。馬の種つけ場面や、森の中での貴婦人と獣人との性交場面など、全編に異様なムードが漂うが、どこかキッチュな趣もある異色ムービー。", "1975-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISSING LINK", "エイプマンひとりぼっち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "100万年以上前の物語。石器を使う新人類に家族を皆殺しにされ、たった一人生き残ったエイプマン（猿人)。彼は一人ぼっちでアフリカの大平原をさまよっていたが、やがて多くの動物たちとの交流が始まる。特殊メイクの第一人者リック・ベイカーが造形したエイプマンが見事。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WR：MYSTERIES OF THE ORGANISM", "WR：オルガニズムの神秘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "WRとはウィルヘルム・ライヒのこと。ナチスに迫害されたこのドイツ人精神分析学者はアメリカにわたって性エネルギーの研究を続けるが、やがてアメリカの学会からもはじき出されるようになる。映画はこのライヒのアメリカでの生涯を追いながら、性革命を実践しようとしている若い女性の生活を描いた物語と交錯させている。世界の若者たちが精神的にライヒと深く結びついていること、政治体制がいかに人間の性を抑圧するかを語っていく。“呪われた作家”D・マカヴェイエフがその名を国際的にとどろかせた記念碑的作品。", "1971-01-01", "34,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLIGHT TO MARS", "火星超特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "宇宙飛行士と新聞記者の一行を乗せた宇宙船が流星雨に襲われ、火星に緊急着陸する。火星人に捕らえられた一行は危機に直面するが、火星政府高官の娘の手助けにより、ロケットを修理し、無事地球への帰途に着く。スリリングなタッチで描かれた宇宙冒険ストーリー。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD MAX BEYOND THUNDERDOME", "マッドマックス サンダードーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "シリーズ第３作は第１作から20年後の近未来。人間も物と同様に売買されるバータータウンを舞台に、正義と悪との闘いが繰り広げられる。荒涼で殺伐とした大地とメカニカルな武器の取り合わせが異様な空間を作り出すことに成功。ギブソンの人気シリーズとして定着。", "1985-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ministry of Vengeance", "ミニストリー・オブ・ベンジェンス 炎の復讐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ベトナムの泥沼で戦い、戦争の残酷な面をいやというほど知らされ、自分自身その時の記憶に悩まされる主人公。復員して信仰の道に入り、牧師になった彼は、妻子をテロリスト・グループに惨殺され法衣を脱ぎ捨てて復讐に立ち上がる。ベトナム復員兵を主人公にしたアクション作品。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Platinum Blonde", "プラチナ・ブロンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "名門スカイラー家のスキャンダルを嗅ぎつけた新聞記者スチューは、記事の掲載を阻止しようとやって来たスカイラー家の娘アンと恋に落ち結婚する。しかし贅沢な生活しかしたことのないアンとの仲はうまくいかず、不満は募るばかり。そこへ、かねてからスチューに想いを寄せていた女性記者ギャラガーが現れ……。フランク・キャプラ監督による恋愛ドラマ。ヒロインのアンを演じるのは、美しいブロンドでアメリカ中を虜にしたジーン・ハーロウ。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WSRO´D NOCNEJ CISZY", "夜の沈黙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "殺人事件を追う刑事とその息子、そして犯人の駆け引きを描いた硬質のサスペンス。クリスマスの近づく街で、人々を震撼させる少年連続殺人事件。捜査の責任者ヘルマン刑事は、犯人探しに全力を傾けていた。だが、その息子のビクトルは厳格な父に反抗するため、事件の捜査を混乱させようと殺人を犯し……。", "1979-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺たちの交響楽", "俺たちの交響楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "労働者たちだけで構成されている川崎のアマチュア合唱団が、ヴェートーヴェンの『交響曲第九番』の合唱をやろうという計画をたて、様々な問題を乗り越えながら公演を成功させていく。ラストの“第九”合唱シーンは圧巻だ。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Son of Babylon", "バビロンの陽光", "", "", "http://image.eiga.k-img.com/images/movie/56136/315x210.jpg?2189", 90, "2003年、イラク北部クルド人地区。サダム・フセイン政権の崩壊から3週間後、戦地に出向いたまま戻らない息子を探すため、老いた母は3歳の孫アーメッドを連れて旅に出る。2人は道中でさまざまな出会いと別れを繰り返し、過酷な現実に押しつぶされそうになりがならも前進していく。幼い少年と年老いた祖母の900キロに及ぶ旅路を通し、度重なる戦争で多くの行方不明者を出しているイラクの実情を描くヒューマンドラマ。", "2011-06-04", "77,3,2,70,77,77,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLADE", "ブレイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "人間とヴァンパイアのハーフとして生まれたダーク・ヒーローが活躍する、アメリカの人気コミックを映画化したホラー・アクション。母親が臨月のときにヴァンパイアに襲われたため、その血を引くことになったブレイドは、逆にヴァンパイア・ハンターとして人間たちを救っていた。そんな彼の前に、世界征服をたくらむ若きヴァンパイア、フロストが出現。古代予言書を解読し、12人のヴァンパイアを生け贄にして全能の力を得ようとするフロストとブレイドの壮絶な闘いが始まる……。SFXマン出身のS・ノリントンがCGを駆使して、スピード感あふれすスタイリッシュな映像でコミックの世界をビジュアル化。日本刀やマシンガンを武器に暴れ回る過激なアクションも見ものだ。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hunter", "ハンター", "", "", "http://image.eiga.k-img.com/images/movie/57466/315x210.jpg?2661", 100, "「プラトーン」「スパイダーマン」のウィレム・デフォー主演で、タスマニアの自然の中で、現地の母子との触れ合いを通じて自らの生き方を見つめなおしてく孤高のハンターの姿を描いたドラマ。凄腕ハンターのマーティンは、とあるバイオテクノロジー企業の依頼を受け、絶滅したとされるタスマニアタイガーを狩りにオーストラリアのタスマニア島を訪れる。それまでは他人とのかかわりを拒絶して生きてきたマーティンだったが、ベースキャンプ代わりの民家に暮らす女性とその子どもたちと交流するうちに忘れていた感情を取り戻していき、同時に自身が引き受けた依頼に倫理的疑問を抱くようになる。", "2012-02-04", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANASTASIA", "アナスタシア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "帝政ロシア末期の皇女アナスタシアをめぐる謎を、ロマンティックに解き明かしたファンタジー・アニメ。ロシア革命によって家族と引き裂かれ、孤児となった皇女アナスタシア。10年後、当時の記憶をなくしたまま成長した彼女は、若い詐欺師ディミトリと出会う。アナスタシアの消息を、その祖母が探っていることを知ったディミトリは賞金目当てで、彼女を本物とも知らずに引き合わせることに。そんなアナスタシアに、ロシア帝国に恨みを持つ妖術師ラスプーチンの魔の手が迫る……。ミュージカル仕立ての群舞シーンや当時の建築美術など、シネマスコープの大画面いっぱいに広がるCG映像が圧巻。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAVISKY", "薔薇のスタビスキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "1930年代フランスで暗躍した実業家にして大詐欺師のアレクサンドル・スタビスキー。ニセ公債を発行し派手な生活を送るが、彼の成功を疑う検察官に追われ、スイスの山荘へ逃亡する。スタイリッシュなレネの他作品とは作風を異にするシリアス・ドラマ。", "1974-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("草の上の仕事", "草の上の仕事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 42, "「月とキャベツ」「洗濯機は俺にまかせろ」などの秀作を手掛けた篠原哲雄による初の16ミリ作品。燃えるような夏の１日、草刈り仕事を一緒にやることになった草刈り職人と、バイトの学生との出会いと別れを、ヌーヴェル・ヴァーグの夏を思わせるような光のなかに閉じこめた佳作。サボろうとするバイト学生と、それをたしなめる職人。次第に心を開いていく二人の周りを光が飛び跳ねる川のシーンが美しく印象に残る。1993年神戸国際インディペンデント映画祭においてグランプリを受賞。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乙女ごころ三人姉妹", "乙女ごころ三人姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "歓楽の街・浅草六区の盛り場で、夜ごとネオンの軒をつたう門付け女たちの悲哀を描いた、川端康成の『浅草の姉妹』の映画化。松竹を離れた成瀬巳喜男監督のP・C・L第１作であり、初のトーキー作品である。ナレーションの挿入をはじめ、トーキーならではの巧みな音響効果が用いられ、もの悲しい三味線の音色が、場末の芸人のわびしさを象徴する。", "1935-01-01", "76", 0, NOW(), NOW());
