INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スカイ・クロラ The Sky Crawlers", "スカイ・クロラ The Sky Crawlers", "", "", "http://image.eiga.k-img.com/images/movie/1956/315x210.jpg?2330", 121, "過去の記憶がない戦闘機乗りの函南優一は、新たに着任した基地で上官となる女性・草薙水素に出会う。2人はやがて惹かれていくが……。「GHOST IN THE SHELL／攻殻機動隊」「イノセンス」の押井守が、菊地凛子、加瀬亮ら実力派俳優を声優に起用し、森博嗣の同名小説を映画化した長編アニメ。思春期の姿のまま永遠に生きる“キルドレ”と呼ばれる青年たちが、大人たちによって作られた“ショーとしての戦争”を戦う姿を通し、生きることの意味を問う。", "2008-08-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Azur et Asmar", "アズールとアスマール", "", "", "http://image.eiga.k-img.com/images/movie/34076/315x210.jpg?1408", 99, "「キリクと魔女」「プリンス＆プリンセス」のミッシェル・オスロ監督による冒険ファンタジー・アニメ。中世イスラムを舞台に、人種も身分も違う2人の青年の冒険と成長を描く。領主の子である青い瞳のアズールと彼の乳母の子である黒い瞳のアスマールは、まるで兄弟のように育てられた。やがて成長したアズールは、幼い頃に乳母に聞かされた子守歌の国を訪れるが、そこでは彼の持つ青い瞳は不吉なものとされており……。", "2007-07-21", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕は天使ぢゃないよ", "僕は天使ぢゃないよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "異端派ミュージシャンとして知られるあがた森魚が、駆け出しアニメーターと、その恋人との切ない恋を描いた林静一原作の『赤色エレジー』をもとに、監督・脚本・音楽・主演を手掛けた青春映画。桃井かおり、大瀧詠一、横尾忠則、岡本喜八など、脇を固める異色の顔ぶれも見どころ。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒の天使 Vol.1", "黒の天使 Vol.1", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "６歳の時、暴力団組長の父とその愛人である母を目の前で殺された少女、一光は“黒の天使”と呼ばれる魔世の手引きでアメリカに逃亡する。それから10数年が経ち、銃と格闘技の腕を磨いた彼女は美しい女となって帰国した。両親を殺した組の幹部・野木を倒すために……。葉月里緒菜が石井隆ワールドに挑み、復讐に燃える冷徹な女を演じたハードボイルド・アクション。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗黒航路", "暗黒航路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "荒れ模様の東シナ海を渡り日本へ向かう昭南丸。その中には、それぞれ複雑な過去を持つ３人の男女が乗り合わせていた。やがて昭南丸は漂流船を発見、救助するが、彼らは実は密輸団であり、船を乗っ取ってしまう。無線機を壊され、救命具も流されてしまった昭南丸乗員の運命を描くパニック・サスペンス。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("広島仁義・人質奪回作戦", "広島仁義・人質奪回作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "８年の刑期を終えて出所した広島のヤクザが、足を洗おうと東京に出て総会屋になるが、結局ヤクザ渡世に舞い戻り、兄弟分に殺されるハメになる。残酷描写はなかなかのものだが、盛り上がりに欠ける。「人質奪回作戦」というサブタイトルは不当表示のきらいあり。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天地明察", "天地明察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "2010年第7回本屋大賞を受賞した冲方丁の小説を、「おくりびと」の滝田洋二郎監督が映画化。江戸時代前期、800年にわたり使用されてきた暦の誤りを見抜いた囲碁棋士で天文暦学者の渋川春海が、20年以上の歳月をかけて日本独自の太陰暦を作り上げていく姿を描く。主人公・春海役は「V6」の岡田准一、春海の妻となるえん役に宮崎あおい。その他、中井貴一、松本幸四郎らが共演。", "2012-09-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヘイフラワーとキルトシュー", "ヘイフラワーとキルトシュー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "フィンランド国内はもとより各国の映画祭で数々の賞を受賞した一編。しっかり者のヘイフラワーとわがままっ子のキルトシューのキュートな姉妹を主人公に、心温まるファミリー・ドラマが展開する。全編を通して映し出されるフィンランドの美しい風景も見どころだ。母が頼りなく7歳で家庭を切り盛りするヘイフラワー。小学校入学間近の彼女は、この機会に家族にちゃんとしてもらおうと考えるが……。", "2002-10-15", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらばラバウル", "さらばラバウル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "本多猪四郎の、「ゴジラ」の前作にあたる戦争メロドラマで、当時流行した戦争歌謡、『ラバウル小唄』に絡めた企画。太平洋戦争末期、ラバウル駐在の航空隊で鬼隊長と呼ばれた若林大尉を中心に、彼と看護婦すみ子のロマンスなどを織り込んで、敗色濃い戦地の悲惨さを描いている。特殊技術は、円谷英二。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀座カンカン娘", "銀座カンカン娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "引退した落語家宅には、身内の青年、画家の卵、音楽家志望の女が同居している。映画のエキストラで知り合った青年と４人組で銀座裏の流しとなり、落語家の窮地を救う。主題歌『銀座カンカン娘』『わが夢わが歌』ともに大ヒット。終盤の、落語家に扮した名人・五代目志ん生の落語は、まさに圧巻。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前番頭", "喜劇・駅前番頭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "大学を出て旅館“一心亭”の番頭になった次郎。新しがりやの主人・徳之助と古風な頑固者の支配人・孫作とが対立している。徳之助を尻に敷く女房の圭子も含めて、次郎は３人から期待されることとなった。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・ウーマン", "ザ・ウーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "江戸時代後期の文化年間、中村座の大名題・坂東三津五郎の後妻で、美貌と男好きによって江戸中にスキャンダラスな評判をふりまいた女義太夫・竹本小伝の、自由奔放な生き方とエロスの世界を描いた異色時代劇。佳那晃子がこの作品でデビューを飾った。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人生ごっこ!?", "人生ごっこ!?", "", "", "http://image.eiga.k-img.com/images/movie/53466/315x210.jpg?1717", 80, "ある日あっけなく死んでしまった若い男が、死後の世界でも斜に構えていたために、生前の自分の人生を追体験する“人生ごっこ”の旅に出される。その旅の中で、恋人との幸せな記憶、痛恨の別れ、そして、あるはずのない死後の未来などを通じて、生きることの意味を考えるようになるが……。監督は黒沢清、和田誠、北野武らの下で助監督を務め、03年に「らくだ銀座」でデビューした林弘樹。", "2008-02-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青空のゆくえ", "青空のゆくえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "15歳の少年少女が、仲間の突然の転校や初恋に揺れるひと夏を、切なくも爽やかなタッチで綴った青春群像ドラマ。TVドラマ、CM、映画などで活躍するティーンエイジャー９人が織り成す、フレッシュな競演も見もの。男子バスケ部のキャプテン、正樹がアメリカに転校することになった。突然の発表に、親友や正樹に思いを寄せていた少女たちの心が揺れる。", "2005-09-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おとうと", "おとうと", "", "", "http://image.eiga.k-img.com/images/movie/54336/315x210.jpg?3679", 126, "「十五才・学校IV」以来10年ぶりとなる山田洋次監督の現代劇で、吉永小百合と笑福亭鶴瓶が姉弟を演じる家族ドラマ。共演に蒼井優、加瀬亮。東京郊外のとある商店街で薬局を営む吟子は、夫を早くに亡くし、女手ひとつで娘の小春を育てていた。ある日、エリート医師との結婚が決まった小春の結婚式に吟子の弟・鉄郎が突然現れ、泥酔して披露宴を台無しにしてしまう。親族に責められる鉄郎をかばう吟子だったが、鉄郎の恋人だと名乗る女が借金返済を求めて吟子の薬局に現れ……。", "2010-01-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("だまされて貰います", "だまされて貰います", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「日本一のワルノリ男」に続く植木等と加藤茶のコンビによる作品。ホラ吹き男・伊賀は東北の田舎町から花子を東京へ連れ帰る。その花子を追って忠作も上京する。一方伊賀は、水をガソリンに変える機械の発明に熱中する早川源内と知り合い、一世一代の詐欺を企てる。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ええじゃないか", "ええじゃないか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "慶応２年、突然日本各地に起こった、欲求不満を踊りに爆発させながら“伊勢まいり”に向かうという“ええじゃないか”騒動。今村監督は、この下層庶民騒動をクライマックスに、江戸の歓楽街・両国に集う種々雑多な人間たちの群像ドラマを描こうとしている。弾圧する側に女たちが尻をまくってみせるシーンが話題になった。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／SPRING，SUMMER，FALL，WINTER，AND　SPRING", "春夏秋冬そして春", "", "", "http://ecx.images-amazon.com/images/I/51qV4g2yNhL._SL210_.jpg", 101, "あまりに過激なテーマと描写が、常に賛否を呼ぶ韓国の鬼才K・ギドク監督。多彩な才能がひしめく韓国映画界でも特異な存在感を放つ彼の、これまでとは一味違う快作。物語の舞台となるのは、森を深く分け入った先の湖上に浮かぶ古寺。この神秘的な佇まいの寺を背景に、人間の一生が四季の移ろいに重ね合わせられながら描かれる。その圧倒的な映像美と、仏教をベースにした慈悲深い物語は、同監督の新境地を感じさせるもの。彼自身が主人公を演じている点も注目だ。人里離れた山寺で老僧とたった二人で暮らす幼子。ちょっとした悪戯から彼は“罪”というものを知る。やがて青年になった彼は、寺に心の傷を癒しにきた女性と恋仲に。二人は駆け落ち同然で寺を後にする。", "2004-10-30", "7,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　神聖かまってちゃん　ロックンロールは鳴り止まないっ", "劇場版　神聖かまってちゃん　ロックンロールは鳴り止まないっ", "", "", "http://image.eiga.k-img.com/images/movie/55956/315x210.jpg?1316", 0, "口コミでロングランヒットを記録した「SR サイタマノラッパー」の新鋭・入江悠監督が、人気バンド「神聖かまってちゃん」の名曲群をモチーフにオリジナル脚本で撮り上げた青春群像劇。プロの棋士になるという夢をもつ女子高生・美知子は、周囲の今時の高校生たちと自分とのギャップに悩んでいた。ある時、彼氏に「神聖かまってちゃん」のライブに誘われるが、その日はなんとか出場を勝ち取った将棋のアマチュア王座決定戦の決勝と重なっており……。", "2011-04-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天下第一", "天下第一", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "時は五代十国時代。天下第一の皇帝が道士の策略で毒を呑まされ天下第一の病にかかり、それを治すのに天下第一の名医を呼び寄せるが名医が国境を越えるのに役人が天下第一の名画を要求する。天下第一の絵師は天下第一の美女でなければ絵は描けないと言い、探し当てた美女は天下第一の翡翠が欲しいと言い出し、その翡翠を盗むため天下第一の大泥棒を雇い……と物語は果てしなくエスカレートしていく。キン・フー作品としては珍しく評価の分かれる作品。", "1983-01-01", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博多どんたく", "博多どんたく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "博多どんたくの祭礼をクライマックスに、恋あり、笑いあり、喧嘩ありといったいかにも日本人の好みを考慮したさわやかな人間ドラマ。阪東妻三郎が「無法松の一生」にも通じる博多の町人役で、どんたくの日に武士の町、福岡へ乗り込んで行く。丸根賛太郎監督の若々しい才気あふれる作品。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GUERRE DU FEU", "人類創世", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "紀元前８万年、地球が最後の氷河期を迎えようとしていた時代に、人間の祖先が、“火”を求めて困難な旅に出る姿を映像化した作品。セリフはなく有史以前の“ヒト”の行動が、学術的裏づけをもとに正確に描写される。猿と人間の中間のメイクがユニーク。", "1981-01-01", "10,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Underworld: Evolution", "アンダーワールド：エボリューション", "", "", "http://ecx.images-amazon.com/images/I/51sxVXUZ-3L._SL210_.jpg", 106, "バンパイア（吸血鬼）族とライカン（狼男）族の数世紀に及ぶ闘争を描いた、ケイト・ベッキンセール主演のゴシック・サイバー・アクション「アンダーワールド」の続編。バンパイアの女戦士セリーンは、自分の家族を殺した一族の長・ビクターへの復しゅうを果たし、同族からも追われる身となってしまう。唯一の味方であるバンパイアとライカンの混血青年マイケルと共に行動していたセリーンは、両種族の創世にまつわる驚がくの事実を知る。", "2006-04-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JE　RENTRE　A｀　LA　MAISON", "家路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "巨匠M・ド・オリヴェイラが、人生の晩年にさしかかった老俳優の姿を静かに見つめる感動作。娘夫婦と妻を交通事故で失った老俳優ヴァランスは、残された幼い孫とともに暮らし始める。家族を失った悲しみ、俳優として限界を感じる戸惑いの中で、彼は自分の人生に思いをめぐらせる……。主人公の悲哀をにじませる名優M・ピコリの温もり豊かな存在感、みずみずしい映像と軽やかなタッチが、深刻なテーマを陰うつにすることなく、心に染みる味わい深い作品に仕上げた。", "2001-01-01", "25,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PUBLIC ENEMY", "民衆の敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "トーキー初期のギャング映画を代表する名作。シカゴの暗黒街で、酒の密売をきっかけにのし上がっていく幼なじみの二人組、トムとマットの栄光と失墜を描く。J・キャグニーとE・ウッズが、まさに直情径行型の与太者を喜々として演じて観る者を圧倒する。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOW　TO　KILL　YOUR　NEIGHBOR'S　DOG", "舞台よりすてきな生活", "", "", "http://ecx.images-amazon.com/images/I/5177N89TYML._SL210_.jpg", 98, "子供嫌いの戯曲作家が、新作で子供をリアルに描けず、そのうえ愛妻に子供をせがまれて四苦八苦し……。大人になり切れない、そんな男の受難をユーモアを込めて描いたヒューマン・ドラマ。向かいに住む少女に対して抱き始める父性的な愛、不安定ながら確かな妻との愛情、ストーカー男との奇妙な友情など、主人公と周囲の人々とのつながりのエピソードがハートを温めてくれる。憎めないダメ男といえる主人公に扮した演技派K・ブラナーの好演も光る。共感度の高い愛すべき一本だ。", "2004-12-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PALLBEARER", "ハッピィブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "幸福でも不幸でもない、モラトリアム青年の気分を切り取った青春コメディ。就職先の見つからない25歳のトムは記憶にない友人の葬式に参列し、柩を担ぐことに。これが縁で、故人の母親ルースとベッドイン。その一方、トムは高校時代の憧れの女性ジュリーと再会して恋心を再燃させる。いいムードとなるも、そんな彼らの前にルースが現れ……。G・パルトロウがジュリーに扮して、現代女性の苦悩をさわやかに体現している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LORD JIM", "ロード・ジム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 154, "J・コンラッドが1900年に書き上げた名作の３度目の映画化。船員ジムは航海中に乗客と船を見捨てて逃げたため資格をはく奪されるが、流れ着いた東南アジアの島で献身的に働き、過去を償おうとする。豪華キャストによる70ミリ大作。伊丹一三とは改名前の若き伊丹十三である。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サマーヌード", "サマーヌード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ある日の午後から翌日の朝まで。夏の終わりの一コマを、総勢20名を超える登場人物の交錯とすれ違いによって描き出したさわやかな群像劇。若干、24歳の飯塚健がメガホンを執った。８月31日、南の島。帰省していた女子大生、夏樹は青年プーと出会う。そのころ、葬儀屋の五郎は、結婚20周年記念日用の妻への贈りものとして用意した花火玉が消え、途方に暮れていた。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INN OF THE SIXTH HAPPINESS", "六番目の幸福", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 157, "日本軍の侵攻に揺れる中国を舞台に、中国への伝道を目指してやって来たイギリス人女性グラディスの困難に満ちた宣教の日々を描いた大作。カンチェンという村にやって来た彼女はそこで“六番目の幸福”という宿を経営、中国軍の混血将校リナンが帰国を勧めるにもかかわらず、そこにとどまり様々な困難に立ち向かう。やがて日本軍がこの村を爆撃しに来て……。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mrs Palfrey at The Claremont", "クレアモントホテル", "", "", "http://image.eiga.k-img.com/images/movie/55786/315x210.jpg?3999", 108, "英女流作家エリザベス・テイラーの小説を、英ベテラン女優ジョーン・プロウライトと「わたしの可愛い人　シェリ」のルパート・フレンド主演で映画化。ロンドンの街角にある長期滞在型ホテル「クレアモントホテル」に、パルフリー老婦人がやってくる。ホテルの住人たちは誰が婦人を訪ねてくるかと関心を抱いていたが、彼女を訪ねてくる家族はひとりもいなかった。そこで小説家志望の青年ルードと出会った夫人は、彼に孫のふりをしてもらうことにする。", "2010-12-04", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mixed Nuts", "ミックス・ナッツ　イブに逢えたら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "人生相談所を経営するフィリップは、クリスマスイブだというのに恋人に振られ、家賃の支払いも滞る始末。そんななか同僚とのもめ事や不運が重なり……。ロマンスを軸に、キャラクターが織りなすひと騒動をユーモアたっぷりに描く。個性派俳優スティーブ・マーティンをはじめ「ケープ・フィアー」のジュリエット・ルイス、「シックス・センス」のハーレイ・ジョエル・オスメントなど演技派が勢揃い。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浮草の宿", "浮草の宿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "当時の日活の新人スター、二谷英明を鈴木清太郎（現・清順）が演出したB級アクション。本編は殺人の濡れ衣を着せられたヤクザが、５年後に真犯人を求めて横浜に帰ってくるといったハードボイルドと、春日八郎のヒット曲を素材とする歌謡映画という二つの顔を持つ。春日の役は流しの歌手、その正体は正義の税関吏だ。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・団地親分", "喜劇・団地親分", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "伴淳三郎以下、関西喜劇人総出演による喜劇映画。北大阪一帯の大親分・大川重五郎は一人娘が検事の息子と恋仲と知り、組を解散し団地に引っ越す。そこで詐欺師グループの悪だくみを知ってこれを叩きつぶし、団地の人気者となる。斎藤寅次郎の愛弟子・市村が多彩な出演者をソツなくさばいた。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("潜水艦ろ号未だ浮上せず", "潜水艦ろ号未だ浮上せず", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "叙情派・野村浩将監督による新東宝の戦争大作。太平洋戦争末期、還らざる出撃をした潜水艦呂号作戦を悲壮感あふれるタッチで描く。藤田進が十八番の軍人役を貫禄たっぷりに演じ、中山昭二以下、新東宝の若手俳優が大挙出演。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊豆の踊子", "伊豆の踊子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "川端康成の名作の２度目の映画化。五所作品と同じ伏見晁が再び脚色にあたり野村芳太郎が演出した。ヒロインの踊り子役は当時歌手としてもブレイクしていた美空ひばりで、文芸作品の映画化というよりは、文芸作品を素材としたアイドル映画の感が強い。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BATTLE OF BRITAIN", "空軍大戦略", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "1940年夏。和平交渉をチャーチルに拒否されたヒトラーは、突如、英国本土の爆撃を敢行。迎え撃つ英国空軍は、スピットファイアーやハリケーンで激しく応戦する……。全編の３分の２が空中戦シーンという戦争航空映画の巨編。イギリス映画界の豪華スター競演も話題を呼んだ。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京の暴れん坊", "東京の暴れん坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "俗に小林旭の“銀座の次郎長”ものといわれる作品の第１作。旭扮する主人公は、銀座のレストラン“ジロー”の若旦那。彼の恋人は、“松の湯”の一人娘で、この松の湯が悪玉一味に乗っ取られそうになり、主人公がやっつける。銀座の商店街の面々と力を合わせて事件を解決するという、ローカル性と明るくコミカルなタッチがなかなか面白い。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("曼荼羅〈MANDARA〉 若き日の弘法大師・空海", "曼荼羅〈MANDARA〉 若き日の弘法大師・空海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "これまで謎とされていた、弘法大師・空海が唐に渡る前後７年の青年時代を描く、日中国交正常化20周年を記念した日中合作映画。山岳修行中に大日教の存在を聞き、大和に赴いた真魚は、大日教を得度し、名を空海と改めて遣唐留学生として唐に渡る。長安へ向かった彼は、そこで密教の奥義を会得する。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Seventh Anniversary", "Seventh Anniversary", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "「きょうのできごと」など話題作を連打する行定勲監督が手掛けた、ガーリーなラブ・ファンタジー。失恋する度に身体から小さな石が出てくる女の子を主人公に、“失恋のカケラ”が巻き起こす珍騒動を、DVカメラを駆使した軽やかでポップな映像で描き出す。全篇出ずっぱりで好演した小山田サユリの、等身大の可愛らしさが光る。失恋の度に身体の中から石が出てくるルルは、７番目の石が気に入って“セブンス・アニバーサリー”と名付ける。知人の紹介でルルの石が雑誌に取り上げられ、女性の間で“失恋の石”が大流行。たちまち高値で取引されるようになり……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女体", "女体", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "浅丘ルリ子を高く評価していた名匠・増村保造監督が彼女を想定して、池田一朗とともに書いたオリジナル脚本の映画化。自らの肉体を武器に生きる貧しい娘のエネルギッシュな生き方とその破滅を冷めた目で凝視する。主人公に扮した浅丘のパンチの効いた演技が素晴らしい。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOCH NESS", "永遠の夢　ネス湖伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "神秘的な自然に囲まれたスコットランドのネス湖にロケを行い、現代のネッシー伝説を映像化したファンタジー・ロマン。雪男の調査に没頭したあまり、学者としての信用とプライドを失ったデンプシー。ネス湖に怪獣などいないことを証明する仕事を請け負った彼は渋々現地入りするが、調査中に謎の生物の存在を探知し、忘れかけていた情熱を取り戻していく。宿泊先の美しい女主人やその幼い娘と交流を持つ主人公に、“スリーメン＆ベビー”シリーズのT・ダンソンが扮している。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KIKOE", "KIKOE", "", "", "http://image.eiga.k-img.com/images/movie/54656/315x210.jpg?4140", 99, "音楽家・大友良英の活動を追ったドキュメンタリー。大友は世界を拠点に様々なジャンルの作品を製作し続け、カヒミ・ カリィ、浜田真理子らのプロデューサーとして活動する傍ら、数多くの映画音楽も手がける人気作曲家である。大友本人、彼と関わりのある音楽家、評論家、アーティストなど100名を越える人物へのインタビューや記録映像を濃縮し、大友のひととなりに迫る。", "2009-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Enchanted April", "魅せられて四月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "新聞広告を見て、陽光まばゆいイタリアの古城を借りることになった４人の女性たち。階級も生活レベルもそれぞれに違う彼女たちが、一緒に暮らす過程で理解を深めていく模様を、美しい映像で綴っていく。M・リチャードソンやJ・プローライトらが織り成す、繊細な演技は秀逸。", "1992-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CARDINAL", "枢機卿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 176, "聖職の身である青年ファーモイルは、人種差別、階級差別、そして妹の堕胎にまで遭遇し、自身も禁じられた愛に悩んでいた。そんな時、彼は司教としてウィーンに派遣される。折しもヨーロッパはナチズムの嵐が吹き荒れており、ファーモイルは再び苦境に立たされる。オールスター・キャストで描く感動作。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダブルス", "ダブルス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "社長の座を追われた若きオタク実業家が、元カギ師を誘い込んで自分の会社の現金強奪を企てた。計画は成功したかに思えたが、逃走に使用したエレベーターが止まってしまい、二人は中に閉じ込められてしまう……。低予算を逆手にとったアイデアが光るクライム・サスペンス。ショーケンと鈴木一真のコンビネーションもいい。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RETURN OF SWAMP THING", "怪人スワンプシング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「スーパーマン」「バットマン」で知られる全米で人気の『DCコミック』誌の新ヒーロー、全身緑の植物に覆われた正義の怪物“スワンプシング”の活躍を描くモンスター映画。以前ウェス・クレイヴン監督により一度映画化されているが、日本に紹介されるのはこれが初めて。モンスターの造型が見もの。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROSS OF IRON", "戦争のはらわた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "男の理想とする“闘い”と“夢”をひたすら追求してきたペキンパー監督が、1943年のロシア戦線において死と対峙する兵士たちのなかにそのテーマを求めた力作。同年、ドイツ軍はソ連軍の反攻に遭い、次第に敗色を濃くしていった。ドイツ軍第２小隊を率いる人間味あふれるスタイナー伍長と、名誉欲に溺れた中隊長との激しい確執のドラマが展開する。凄絶な戦闘に男たちの求めるものは何か。ペキンパー監督は戦場の男たちの生きざまを、スローモーションを効かせたダイナミックな演出で鮮やかに描写。ゴールド作曲のエモーショナルなテーマ音楽も胸をえぐる。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dead Snow", "処刑山／デッド卍スノウ", "", "", "http://image.eiga.k-img.com/images/movie/55176/315x210.jpg?1297", 91, "冬のバカンスを楽しもうと雄大な雪山を訪れたノルウェーの医学生8人は、山小屋の床下から偶然ナチスの隠し財産を見つけ、こっそり着服しようとする。すると凍土の中からナチス兵のゾンビ軍団が現れ、彼らに容赦なく襲いかかってくる。生き残った医学生らは無残に殺された仲間の復讐を誓い、ゾンビに熾烈な戦いを挑む。監督はノルウェーの鬼才、トミー・ウィルコラ。", "2010-02-13", "36", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四畳半襖の裏張り・しのび肌", "四畳半襖の裏張り・しのび肌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "「四畳半襖の裏張り」の好評から、企画された作品。大正時代後期、花街に生まれ育った少年の性の遍歴を描く。中島丈博の脚本により、前作とは趣を異にする物語性が強くなった。ラストで芹明香が言うセリフ“男と女はアレしかないんよ”が有名に。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹下左膳", "丹下左膳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "大河内伝次郎とマキノ雅広監督のコンビによる“丹下左膳”。江戸・小野塚鉄斎のもとにある名刀・乾雲、坤竜の二振を、刀剣蒐集狂・饗庭侯の命を受けた丹下左膳が、鉄斎を斬って奪い取る。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT", "あれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "元祖セックス・シンボルといっても過言ではないC・ボウ主演のお色気コメディ。平凡なデパート・ガールが社長と結ばれるまでを、当時最先端の風俗を交えながらテンポよく描いている。原作はこの映画で女優デビューも果たしたE・グリン。主演のボウはこの作品で爆発的な人気を得、新時代の女“イット・ガール”として一世を風靡した。", "1927-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOUNTY TRACKER", "バウンティ・ハンター 死の報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "プロの殺し屋に兄を殺された賞金稼ぎの弟が復讐に燃える。殺し屋エリックは会計事務所を襲って職員を皆殺しにした。何とか一命を取り留めたポールは、警察に保護されるが、エリックの魔の手は警察内にまでも及んでくる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初姿丑松格子（暗闇の丑松より）", "初姿丑松格子（暗闇の丑松より）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "長谷川伸の原作を島田正吾以下、新国劇総出演で映画化した時代劇。深川の料理人・丑松は、女中・お米と祝言を挙げる。お米に横恋慕する巳之助は丑松の留守中お米に迫るが、帰宅した丑松に殺される。丑松はお米を知人に預け、旅に出る。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スピードマスター", "スピードマスター", "", "", "http://image.eiga.k-img.com/images/movie/34146/315x210.jpg?1490", 96, "スピードに魅せられた走り屋たちが壮絶なバトルを繰り広げる本格カーアクション。凄腕の走り屋・颯人は、バトル中の事故で友人を亡くして以来、速さを追求することに虚しさを感じるようになる。あてのない放浪を続けていた彼は、病に倒れた父の修理工場を立て直そうと奮闘する少女・まひろと出会う。修理工場で整備士として働くことになった颯人は、そこで1台のチューンナップカーと巡りあい、再びバトルの世界へと舞い戻っていく。", "2007-08-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五星戦隊ダイレンジャー", "五星戦隊ダイレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "東洋の伝説獣の力と気功術を使って悪のゴーマ族に立ちむかう5人の若き戦士の奮闘を描いたスーパー戦隊シリーズ「五星戦隊ダイレンジャー」の劇場版。ゴーマ族のトランプ公爵の陰謀により、子どもたちが次々とトランプに姿を変えられてしまった。子どもたちを危機から救うためトランプ公爵に闘いを挑むダイレンジャーたちだったが、公爵が魔法のトランプでよみがえらせた強力な怪人たちに苦戦を強いられる。", "1993-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若くて、悪くて、凄いこいつら", "若くて、悪くて、凄いこいつら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "柴田錬三郎の現代小説を、中平康が監督した異色活劇。事業の失敗と孫娘の自殺で傷心の佐倉は、死ぬつもりで軽井沢へ向かう途中、３人の若い男女と知り合う。やがて佐倉の孫娘は生きていて、財界の大ボスに監禁されていることを知った３人は、孫娘奪還に乗り出す。眼狂四郎なる殺し屋が登場する、という楽屋オチも。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEINER DAS EISERNE KREUZ ２.TEIL／BREAK THROUGH", "戦場の黄金律 戦争のはらわた II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "第二次大戦下のドイツ軍の抗争を描いた作品。ホフマン将校をはじめとする反ヒトラー派幹部は、ヒトラーを暗殺し戦争を終結させる計画を立てていた。将校のスタイナーはホフマンの命を受け、アメリカ軍大佐に計画を告げるため最前戦へ赴くのだが……。ビデオ発売時タイトルは「戦争のはらわた II」。", "1983-01-01", "7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野獣刑事", "野獣刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "はみ出し刑事と、犯人として捕えた男、そしてその妻との三角関係を軸に、工藤監督がハードに描くアクション・ドラマ。ふだんは気が弱いが、覚醒剤を打つと人が変ったように暴力をふるう男を泉谷しげるが怪演している。後半のたたみかけるような展開に、息をのまされる。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺し屋茶村 LUCKY KILLER", "殺し屋茶村 LUCKY KILLER", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "腕は確かだが間抜けな殺し屋・茶村。金に困った彼は、サラ金を襲い10万円を手にした。ところがその直後から、謎の男に付け回されたので始末してしまう。帰宅した茶村のもとに、ある殺しの依頼が届く。それは、今殺した男を始末するものだった。難なく殺しの報酬を得たラッキーな殺し屋。これは現実なのか……？　「心臓抜き」で、サスペンスの新領域を作り上げた高橋玄の短編作品。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマン 怪獣大決戦", "ウルトラマン 怪獣大決戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「ウルトラマン」「ウルトラ６兄弟VS怪獣軍団」に続く、1979年３本目の劇場用映画。バルタン星人など人気怪獣が登場するTV版「ウルトラマン」の５話を再編集し、一部に撮り下ろし映像を加えて、12人のウルトラ家族VS10大怪獣の対決を描く内容に変更されている。アニメ版「ザ・ウルトラマン」の登場が異彩を放つ。", "1979-07-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moulin Rouge", "赤い風車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "　19世紀末のパリ。貴族の出身ながら、子供時代の事故が原因で体が不自由だった画家ロートレック。孤独な貧乏生活を続けていた彼はキャバレー“ムーラン・ルージュ”に毎夜訪れ、ダンサーたちをスケッチしていた。その帰り道、彼はある女性と知り合い、恋に落ちる。しかし彼女との関係は長く続かなかった……。画家を志していたジョン・ヒューストン監督が印象派の巨匠ロートレックの短い生涯を美しい映像で綴る。", "1953-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Edtv", "エドtv", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "ケーブルTV局のディレクター、シンシアが打ち出した新企画は、普通の人を主役にして24時間その人間の生活をカメラで追うというものだった。そして彼女が白羽の矢を立てたのは、31歳でビデオショップの店員をしているエド。契約を交わしたエドの生活は『エドtv』という番組となって、カメラを通して視聴者に目撃されることになる。兄レイと別れたシャリーとの交際や実の父親の登場まで、エドの私生活の放送は高視聴率を稼ぎ、彼自身も有名人となっていくが……。プライバシーを侵していくTVの暴力を、R・ハワードがシニカルな笑いを込めて描いた社会派ヒューマン・ドラマ。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVING YOU", "さまよう青春 ラヴィング・ユー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "歌手として人気急上昇中だったプレスリーが主演した２作目。孤児院出身でビール配達の青年歌手プレスリーの実力に目をつけたバンド・リーダーが、彼をスターダムにのせようとプロモート、成功するまでを描く。新人D・ハートが清純な美しさを見せた青春音楽映画。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　NEXT　BEST　THING", "2番目に幸せなこと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "名匠J・シュレシンジャーが、世間体にとらわれることなく独自の幸せを追求する男女の姿を描く感動ドラマ。男運のないアビーとゲイのロバートは、何でも相談し合える親友同士。そんな二人が酔った勢いで一線を超え、息子が誕生する。二人は未婚のままで互いに干渉をしない共同生活を始めるが、アビーが新しい恋人との生活を望み、養育権をめぐって争うことになる。自由奔放なヒロインを演じたマドンナが歌う主題歌『アメリカン・パイ』も大ヒットした。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IKKA：一和", "IKKA：一和", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ぴあフィルムフェスティバルで注目を浴びた川合晃監督の長編デビュー作。ファミレスを舞台に、ひょんなことから人質を取って立てこもった一家の運命を、ユーモアたっぷりに描き出す。関西ネイティブの役者たちによる本物の関西弁のノリが楽しめるご当地ムービーだが、特に愛情過多なおかんに扮した秋野暢子と、渋い魅力のおとん役の國村隼が素晴らしい。記念日には必ずファミレスで食事をするのが決まりの太田一家。今日は次男・勇の20歳の誕生日で、久しぶりに家族四人が集まる日。ところが父親でピザ屋の慎一郎が、身体中に武器を忍ばせていたことから店内がパニックに陥る。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bridget Jones's Diary", "ブリジット・ジョーンズの日記", "", "", "http://image.eiga.k-img.com/images/movie/26856/315x210.jpg?2829", 97, "出版社勤務の32歳独身女性ブリジットは、セクシーな上司に関心を持つが……。恋と仕事に奮闘する彼女の毎日を等身大に描く。原作は日本をはじめ世界23カ国でベストセラーになった同名小説。原作者フィールディングが共同脚本と製作総指揮を担当。脚本には「ノッティングヒルの恋人」のリチャード・カーティスも参加。TVのドキュメンタリー監督出身のシャロン・マグワイアが映画初監督。ヒロイン役のレニー・ゼルウィガーが、役作りのため6キロ太ったもの話題。", "2001-09-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飛天舞／OUT LIVE", "アウトライブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "剣劇とワイヤーワークを融合した韓国製の伝奇アクション。本国で大人気のコミックを基に、争いにほんろうされる愛をドラマティックに描く。後半に進むほど壮絶さを増す剣のバトルは圧倒的だ。元朝末期の中国。両親を亡くし、伯父に育てられた若き剣士ジナと、蒙古将軍の娘ソルリは恋に落ちる。だがジナの持つ秘伝の書“飛天神記”をめぐり、争いが激化。悲劇を呼ぶこの戦いの中で、彼らの恋は皮肉な道のりをたどり……。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い賭博師・悪魔の左手", "黒い賭博師・悪魔の左手", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "回教国パンドラ王国の若き国王アブダラ三世は、賭博大学の技術部長“教授”にあやつられ、日本のギャンブル市場獲得に邪魔なギャンブラー・氷室浩次を亡きものにしようと３人の凄腕ギャンブラーを送り込む。ストーリーをデフォルメしすぎて散漫な印象に。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I'm Here", "アイム・ヒア", "", "", "http://image.eiga.k-img.com/images/movie/57336/315x210.jpg?2137", 32, "「マルコヴィッチの穴」「かいじゅうたちのいるところ」のスパイク・ジョーンズが初めて手がけたラブストーリーで、近未来のロサンゼルスを舞台にロボットの切ない恋を描いたショートフィルム。ドキュメンタリー映画「みんなのしらないセンダック」（2009）とあわせた「INVITATION from SPIKE JONZE」として11年12月に劇場公開。", "2011-12-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Sister's Keeper", "私の中のあなた", "", "", "http://image.eiga.k-img.com/images/movie/54166/315x210.jpg?3426", 110, "白血病の姉・ケイトを救うために、ドナーとして作られて産まれた11歳の妹アナは、ある日突然、「自分の体のことは自分で決める」と臓器提供を強いる両親を相手に訴訟を起こすが、その裏にはある思いが隠されていた……。ジョディ・ピコーのベストセラーを、「きみに読む物語」のニック・カサベテス監督が映画化。主演は自身初の母親役となったキャメロン・ディアス、アナ役に「リトル・ミス・サンシャイン」のアビゲイル・ブレスリン。", "2009-10-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲレンデがとけるほど恋したい", "ゲレンデがとけるほど恋したい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "若者に人気のスノーボードを取り上げ、全編ニュージーランド・ロケで描いた恋と友情の物語。学生時代からのスキー仲間であるミナ、マサル、アンノの３人は、それぞれの思いを胸にニュージーランドにやって来た。そこへかつての仲間、シュウスケが姿を現し、マサルとの結婚を決めていたミナに動揺を与える。スノーボードによるニュージーランドの難所アイス・ブルー・ホーン滑降に失敗していたシュウスケは、ミナと再会したことで再び挑戦する心が芽生えるのだった。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本侠客伝 雷門の決斗", "日本侠客伝 雷門の決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "浅草の興行街。堅気となった聖天一家の持ち小屋・朝日座は、仇敵・観音一家の手に渡った。聖天一家改め平松興業の二代目・信太郎は、日本一の浪曲師・梅芳を招き挽回を図る。が、再起の機会をことごとく観音一家に潰され、信太郎の辛抱も限界に……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サイドカーに犬", "サイドカーに犬", "", "", "http://image.eiga.k-img.com/images/movie/34026/315x210.jpg?1363", 94, "「雪に願うこと」で国内の映画賞を総なめした根岸吉太郎監督が、芥川賞作家・長嶋有のデビュー作を映画化。80年代初頭のある夏の日、10歳の薫の母が家を出た。数日後、薫の家に突然“ヨーコさん”という若い女性が現われる。男勝りで天真爛漫なヨーコは、神経質な母とは正反対の性格。そんな彼女に始めのうちは戸惑う薫だったが、やがて憧れのような感情を抱いていく。2年ぶりの映画出演となる竹内結子が自由奔放なヒロインを好演。", "2007-06-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Safe House", "デンジャラス・ラン", "", "", "http://image.eiga.k-img.com/images/movie/56726/315x210.jpg?2828", 0, "デンゼル・ワシントン、ライアン・レイノルズ主演で元CIAエージェントの逃亡劇を描くアクション。CIAを裏切り10年間逃亡を続けていた元工作員トビンが南アフリカにあるCIAの収容施設に収監されるが、謎の傭兵部隊が施設を襲撃。新人工作員のマットはトビンを連れて逃走することになるが……。", "2012-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スケバン刑事", "スケバン刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "和田慎二原作の同名マンガに設定を借りた、TVシリーズの映画化。南野陽子の映画初主演作。ヨーヨーを武器とするスケバン刑事サキは、不良生徒を兵士に変えクーデターをたくらむ一味と対決する。南野のひたむきな表情が素晴らしい。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shall we ダンス？", "Shall we ダンス？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "2004年にリチャード・ギア主演でハリウッド・リメイクもされた、周防正行の監督作。各映画賞も軒並み独占し、1996年を代表する日本映画となった。これまでも“修行僧”や“学生相撲”など、一見地味なニッポン文化にスポットを当て、ニュアンス豊かな独自の作風を見せてきた監督だが、本作はその総決算ともいうべき充実作。知られざる“社交ダンス”の世界を舞台に、従来の作品とは一線を画する親しみやすさが際立った快活なコメディを完成させている。主人公は平凡な家庭を持つサラリーマン、杉山。電車の中から、ビルの窓際にたたずむ美女を見かけた彼は、彼女を追いかけるようにして岸川ダンス教室を訪れる。美女の名は舞。彼女からダンスを習うことを夢見て、杉山は社交ダンスを始めることを決意する……。個性豊かなダンス仲間たち、コンプレックスを抱えるヒロイン、そして夫の熱中ぶりに不安を感じ探偵を雇う妻。雑多な登場人物に囲まれながら、浮き彫りになっていく中年男の物語は悲哀よりもむしろ希望に満ちている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE　IS　THE　DEVIL／STUDY　FOR　A　PORTRAIT　OF　FRANCIS　BACON", "愛の悪魔／フランシス・ベイコンの歪んだ肖像", "", "", "http://ecx.images-amazon.com/images/I/51TWZ5FXAXL._SL210_.jpg", 90, "英国が生んだ天才画家フランシス・ベイコンと、そのモデルとなった恋人ジョージ・ダイアーの同性愛にスポットを当てた愛憎劇。1960年代、ベイコンの屋敷に盗みに入ろうとした若者ジョージは、天井から転落。ベイコンは、そんな彼をベッドに誘い愛人にしてしまった。だが、学のないジョージはベイコンと仲間たちの嘲笑を浴び、次第に酒とドラッグに溺れていく……。ベイコンの絵を思わせるダークで閉塞的な映像が、精神の闇を鮮烈に浮かび上がらせる。", "1999-02-28", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新桃太郎", "新桃太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "桃太郎伝説をSF的に映画化したファンタジー作品。桃太郎がなぜ桃の中に入って川を流れてきたのかという出生の秘密が明かされる。おなじみの鎧に身を包み、キビダンゴを持ち、ノボリを立て、犬・キジ・猿を連れての鬼退治だ。", "1987-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vaghe stelle dell'Orsa", "熊座の淡き星影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "　ルキノ・ビスコンティが、前作「山猫」に引き続きクラウディア・カルディナーレをヒロイン役に迎え、ユダヤ人家族の愛憎を描いた作品。アメリカ人の夫を持つサンドラは、長い間父の死に疑問を抱いていた。夫と共にイタリアの故郷へ戻った彼女は、近親相姦的愛情を秘めた弟からの不穏な眼差しと、義理の父からの非難を浴びる。クラウディアは文学作品への出演も多く、グラマラスな体型を武器に、1960年代トップスターの地位を築いた。", "1982-11-13", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE MILLION", "ル・ミリオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "「巴里の屋根の下」に次ぐR・クレールのトーキー第２作。芸術家の卵ミシェルと同じ屋根の下で暮らす相棒の画家プロスペルは文無し。たまたま、二人が割り勘で買った100万フランの宝くじが当たるが、プロスペルがそのくじをなくしてしまう……。", "1931-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIGHT LIGHTS，BIG CITY", "再会の街　—ブライトライツ・ビッグシティ—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "原作者のマキナニーの実体験をもとに描いた、大ベストセラー小説の映画化。ニューヨークの出版社に勤める作家志望の青年ジェミーは、母を亡くし、モデルをしている妻のアマンダに家出され、失意の日々を過ごしていた。悪友に誘われ酒とドラッグに溺れ、ついには職を失ってしまう。ある日、街のショーウィンドーで、アマンダの顔に似たマネキンを見つけ、仲むつまじかった頃の二人を思い出す。ジェミーはなんとか彼女に接近しようと試みるのだが……。M・J・フォックスが初めてシリアスなドラマに挑戦し、イメージ・チェンジをはかった作品。なお、彼はこの撮影後に、共演のT・ポランと結婚した。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大勝負", "大勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "博徒渡世人に十手を預けたために、政道が乱れに乱れてしまった関八州を舞台に、博徒同士の争いに巻き込まれていく隠密たちの活躍を描く任侠時代劇。真岡の二大勢力、新田の常吉と虎五郎はそれぞれ用心棒を雇ったが、この二人、実はお互い隠密であり、ともにヤクザ撲滅のために暗躍していく。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN TO DANCE", "踊るアメリカ艦隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "女性タップダンサーとして名高いエリノア・パウエルが「踊るブロードウェイ」に続いて出演したミュージカル映画。ニューヨークで出会ったダンサーと内気な船乗りの恋物語を、コール・ポーターの名曲で綴っていく。壮大なセットを組んでのラスト・シークエンスは見ものである。名優J・スチュアートにとっても出世作となった。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅に出た極道", "旅に出た極道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "のちに「敦煌」などの大作で知られる佐藤純彌がメガホンを執った一編。天野屋一家六代目・島村清吉は、子分の保とジョージを連れて、妻のみね子を探しに香港へやって来る。ところが、ひょんなことから麻薬取引に巻き込まれ、世界のギャング団を相手に大暴れすることに。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秋葉の火祭", "秋葉の火祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "若き日の清水次郎長を、チャンバラ・スターの大御所、市川右太衛門が演じた作品。1936年に自らの右太プロを解散した彼は、この当時、新興キネマにおいて年５、６本のペースで出演していた。法印大五郎に扮した光岡龍三郎は、マキノ時代の右太衛門に拮抗する東亜キネマのスターだった。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LISBON STORY", "リスボン物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "録音技師ビンターの許に、友人の映画監督から1枚の絵葉書が届く。国境のなくなったEUの道路を通ってリスボンに彼を訪ねるビンターだったが、当人はビデオ映像を残して消えていた。ガンマイクで街の音を拾って歩く彼の耳に、美しい女性テレーザの歌声が届く……。リスボン市の依頼を受けたベンダース監督が、映画百年にあたって世界最高齢のオリベイラ監督を迎えて軽やかに綴った映画への愛の賛歌。", "1995-08-26", "7,25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桂小五郎と近藤勇 龍虎の決戦", "桂小五郎と近藤勇 龍虎の決戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "新選組のメンバーに焦点を当てた時代活劇。幕末は慶応２年。近藤勇率いる新選組が、倒幕を策す薩長の勤皇派を強圧する京洛の港。志士、桂小五郎は密談中の近藤勇一派を箱屋に化けて立ち聞き中、発見され今にも斬られようとするところを芸者に救われる。一方、新選組の隊士・吉松忠三郎が勤皇派の密偵であることがバレ、彼は拷問を受ける。知らせを受けた桂は救いに行くのだが……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("БРАТ／BRAT", "ロシアン・ブラザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "映画監督セルゲイ・ボドロフの息子を主演に、現代ロシアン・マフィアの姿を描いたバイオレンス・アクション。２年間の軍役を終えた青年ダニーラは、兄を頼ってサンクトペテルブルグを訪れる。その兄はプロの殺し屋になっており、ダニーラもまた組織の暗殺者に身を投じていく。1997年、本国で大ヒットとなった。", "1997-01-01", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Amants du Pont-Neuf", "ポンヌフの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "アレックスという名の青年を主人公にしたレオス・カラックス監督の「ボーイ・ミーツ・ガール」「汚れた血」に続く3部作完結編。巨額の制作費を投じ、ポンヌフ橋をセットで再現したことで話題になった。天涯孤独の大道芸人、アレックスは、失明の病にあることに絶望した画学生ミシェルとパリ、ポンヌフ橋で出会い、恋に落ちる。しかしミシェルの両親が眼の治療法を見つけたことを知ったミシェルは、彼の元を去るのだが……。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLAGUE OF THE ZOMBIES", "吸血ゾンビ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "怪奇映画の宝庫、ハマー・プロの製作によるゾンビ映画の古典。イギリスの片田舎に不思議な疫病が発生し、死者が激増。フォーブス教授がこの謎の原因を突き止めるため、墓を掘り返して死体を解剖しようとするが、どの墓もすべて空っぽだった……。ハマー・プロらしいおどろおどろしい演出と背景設定がうまく効いている。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLYANNA", "ポリアンナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "何にでも喜びを見いだす楽観的な少女・ポリアンナ。そんな彼女の日常と周囲の人々との交流をみずみずしく描いたファミリー・ドラマ。不幸にもめげずに元気はつらつに生きる少女を、H・ミルズが生き生きと演じている。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紀子の食卓", "紀子の食卓", "", "", "http://image.eiga.k-img.com/images/movie/33656/315x210.jpg?4649", 159, "2002年に製作され大きな反響を呼んだ「自殺サークル」の続編。退屈な日常や家族との関係に嫌気がさしていた女子高生・紀子。ある日“廃墟ドットコム”というサイトを発見した彼女は、家出して東京へ行き、サイトを通して知り合ったクミコが運営するレンタル家族の一員となる。一方、紀子と“廃墟ドットコム”の関連に疑いを持った妹ユカも東京へと向かうが……。前作に引き続き、鬼才・園子温監督がメガホンを取る。", "2006-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕と妻の1778の物語", "僕と妻の1778の物語", "", "", "http://image.eiga.k-img.com/images/movie/55086/315x210.jpg?2873", 0, "「なぞの転校生」「ねらわれた学園」といった作品で知られるSF作家・眉村卓と、2002年にがんで死去した悦子夫人の実話をもとに、余命宣告を受けた妻のために5年間にわたって1日1篇の短編小説を書き続けた夫の姿を描く。「黄泉がえり」の草なぎ剛と竹内結子が夫婦役で再共演を果たす。テレビドラマ「いいひと。」「僕の生きる道」など多くの草なぎ主演作を手がけてきた星護監督がメガホンをとる。", "2011-01-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOIN’ TIME", "プリズナーズ やめられないボクたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "訪問販売のセールスマン、ジャレットは、セールス先の家で奥さんと関係を持ってしまう。ところがそこへ州知事のダンナが帰って来る。ジャレットは凶悪な暴行魔として刑務所へ送られてしまう。しかしこの刑務所、支離滅裂＆酒池肉林のハチャメチャなところだった……。かの「監獄ロック」「暴力脱獄」などの刑務所映画や、「ロッキー」ほかの名作のパロディーがふんだんに詰め込まれたコメディ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Percy Jackson & the Olympians: The Lightning Thief", "パーシー・ジャクソンとオリンポスの神々", "", "", "http://image.eiga.k-img.com/images/movie/54946/315x210.jpg?4741", 121, "「ホーム・アローン」のクリス・コロンバス監督が、ベストセラー児童書を映画化したファンタジー。主演は「3時10分、決断のとき」の新星ローガン・ラーマン。ピアース・ブロスナン、ユマ・サーマンらが脇を固める。寄宿学校に通う17歳の少年・パーシーは、ある日突然、予言の神に自分がギリシャ神話の神々の息子であると告げられる。パーシーは盗まれたゼウスの雷撃を探すため、仲間のアナベスとグローバーとともにアメリカ横断の旅に出る。", "2010-02-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Army of Darkness", "キャプテン・スーパーマーケット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "　死霊と闘い、中世の世界に飛ばされてしまったアッシュは現代に戻るため、“死者の書”を探し出そうとする。ところが呪文を間違えたばかりにゾンビの群れが復活。アッシュと騎士たちは死闘を展開することに。実はスーパーマーケットの店員だったアッシュの冒険を描く「死霊のはらわた」シリーズ第3弾。悪ノリもますます加速し、コミック色とファンタジー色も強くなった。2種類のエンディングが存在する。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("破片のきらめき　心の杖として鏡として", "破片のきらめき　心の杖として鏡として", "", "", "http://image.eiga.k-img.com/images/movie/54196/315x210.jpg?2911", 0, "第14回ヴズール国際アジア映画祭で観客賞を受賞した、心の病を抱える人々の創作活動を見つめたドキュメンタリー。精神科病院の中にある造形教室には、様々な心の病を患う人々がやってきては創作に励んでいる。絵画、詩の朗読、ギターなど彼らの表現する作品郡、10年以上の交流を経てその魅力に迫る。吉行和子がナレーションを務める。", "2008-12-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("寝ずの番", "寝ずの番", "", "", "http://ecx.images-amazon.com/images/I/51Y0B04091L._SL210_.jpg", 110, "上方落語界の重鎮・笑満亭橋鶴が今まさに臨終しようというとき、最後に謎のお願いの言葉をつぶやいた。それを聞いた弟子たちは、師匠の願いを叶えんと慌てふためくのだが……。果たして師匠の願いとは？　ベテラン俳優の津川雅彦が、日本映画界の創始者である祖父・牧野省三と、早撮りの名手としてならした伯父・マキノ雅弘の名跡マキノ性を継いだ初監督作で、故中嶋らもの同名短編小説を映画化した人情喜劇。", "2006-04-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gamer", "GAMER ゲーマー", "", "", "http://image.eiga.k-img.com/images/movie/53906/315x210.jpg?4182", 105, "「アドレナリン」シリーズを手がけるマーク・ネベルダイン監督とブライアン・テイラー監督のコンビが、ジェラルド・バトラーを主演に迎えて描くSFアクション。マイケル・C・ホール、ローガン・ラーマン、アンバー・バレッタら豪華キャストが脇を固める。西暦2034年、オンライン・ゲーム「スレイヤー」では、脳手術を施された生身の囚人が、プレイヤーたちに遠隔操作され激戦を繰り広げていた。そんななか、無実の罪で投獄されたケーブル（バトラー）は、愛する妻と娘に会うため「スレイヤー」の世界から抜け出す。", "2010-12-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hindsight", "青い塩", "", "", "http://image.eiga.k-img.com/images/movie/57536/315x210.jpg?2314", 122, "「イルマーレ」のイ・ヒョンスン監督が、元ヤクザの男と暗殺者の少女が料理をきっかけにひかれあっていく姿を描いたラブストーリー。ヤクザ稼業から足を洗ったドゥホンは、夢だったレストランを開業するため料理教室に通い始める。そこで出会った少女セビンと距離を縮めていくドゥホンだったが、セビンの正体はドゥホンを殺すよう命じられた暗殺者だった。セビンが自分の命を狙っていると知ってもなお、ドゥホンはセビンを裏社会から守ろうとするが……。主演は「殺人の追憶」の名優ソン・ガンホと、ドラマ「善徳女王」などで人気の若手女優シン・セギョン。", "2012-03-17", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POWDER", "パウダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "テキサス州ホイートンの農家で、ある老人男性が自然死を迎えた。駆けつけた保安官が地下室を探ると、その闇の中から一人の少年が見つかる。色素も体毛も全くなく、透き通るような白い肌。その名もパウダーは、落雷によって瀕死の重体となった母親から生まれた運命の子だった。身寄りを失った彼は青少年更生施設へ送られるが、ある日、物理の授業中で実験機の電気エネルギーを引き寄せてしまい、異常な電解質の体であることが分かる。また同時に、人の心を読み取ることもできてしまう能力も備えていた。その特異な風貌と体質に、周囲の目は冷たい。ほのかな思いを寄せた少女からも引き離された彼は……。不思議な能力に加え、高い知性と繊細な心を併せ持った少年が、周囲の人間たちと心を通わせていく姿を綴ったヒューマン・ドラマ。アッと驚くファンタスティックなラストが、さわやかな感動を与える。特殊メイクを駆使してパウダーに扮するのは、TV『インディ・ジョーンズ／若き日の大冒険』で注目を浴びたS・P・フラナリー。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLTERGEIST", "ポルターガイスト", "", "", "http://ecx.images-amazon.com/images/I/51R5dQk7JAL._SL210_.jpg", 115, "かつて墓地だった場所に家を建てたため、その家に住む家族が亡霊たちにおびやかされるという物語。娘を亡霊に奪われてしまった両親が、その娘を奪回するため立ち上がるという、親子の愛がテーマになっている。リチャード・エドランドの手による、様々なSFXが見ものの一編。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("欲", "欲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "尾崎士郎の戯曲小説『ホーデン侍従』を五所平之助監督・伴淳三郎主演で映画化した風俗喜劇。出雲の回天堂医院の新山博士は、不老不死の妙薬の研究に打ち込み、犬や猫や狸の生殖ホルモンを材料にして研究を進めるが、やがて人間の生殖ホルモンが必要になる。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はやく起きた朝は…オンステージ THE MOVIE", "はやく起きた朝は…オンステージ THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/56056/315x210.jpg?2926", 0, "1994年から放送されている朝のトーク番組「はやく起きた朝は…」の舞台「はやく起きた朝は…オンステージ」15回目の公演を記念して製作された劇場版。2011年の「オンステージ」の模様を中心に、過去15回の舞台を振り返る秘蔵映像、舞台裏の模様、稽古中の様子、劇場版オリジナルの映像も交える。出演はテレビ、舞台同様に松居直美、磯野貴理、森尾由美。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAN ON A TIGHTROPE", "綱渡りの男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "N・パターソンの短編小説『国際事件』を原作に、チェコスロバキアの小サーカス団が国境を突破して西ドイツに脱出するまでをスリリングに描くサスペンス映画。一座はチェコでは本当のサーカス芸ができないと考えて、客寄せのパレードを利用して白昼堂々の脱出作戦を開始していく。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("", "鯨の中のジョナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "1942年、ホロコーストの嵐が吹き荒れていたアムステルダムに住む４歳のジョナ。彼にとってユダヤ人、ナチスという概念はなく、怒鳴る兵士、吠える犬、銃、鉄条網、そして身近に死が存在するだけで、今までの暮らしと何も変わりはなかった。優しい父と母がいてくれさえすれば……。ホロコーストの嵐の中で健気に生きる少年ジョナの姿を寓話のように優しい視点で綴った作品。", "", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BESAT", "ポゼスト・狂血", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ラース・フォン・トリアー監督が設立した映画会社ゼントローパの製作による異色スリラー。ルーマニア人の旅行者がデンマークで不審死を遂げ、その死体から、まったく新しい伝染病菌が見つかった。周囲の反対をよそに感染元の調査に乗り出した医師ソーレンは、やがて謎めいた占星術師に遭遇。その口から、ウイルスの驚くべき出どころが明かされる。", "1999-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シェイディー・グローヴ", "シェイディー・グローヴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「冷たい血」に続く青山真治の監督第５作。自分を捨てたかつての恋人を追い続けるストーカーまがいのOLと、彼女のイタズラ電話に偶然出くわした寡黙な青年。二人の出会いから愛の結実までを、寓話性の高い物語と飛躍力のある描写で紡ぐ。ヒロインに雇われた探偵が狂言回し的な役どころで登場。彼の視点も交えた多様なナレーションの混在ぶりが、ピュアな映像を深化させ、エモーションとイマジネーションを誘発する。「Helpless」で青山と組んだカメラマン、田村正毅が全編の８割にデジカムを使用。タイトルの意味する“陰のある森”のシーンでのフィルム撮影と、デジカムによる日常描写の鮮やかなコントラストを見せている。", "1999-07-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冬の河童", "冬の河童", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "川のほとりの古い一軒家を舞台に、若い男女が恋に悩みながら成長していく姿を描く。引っ越しを１週間後に控えたある日、川のほとりにすむ一太郎、タケシ、ツグオの３人兄弟のもとに、従姉妹のサケ子が手伝いにやってくる。サケ子と初対面のツグオと、彼女が来ることを知らなかったタケシは戸惑う。幼い頃タケシに恋心を抱いていたサケ子は、彼に気持ちをぶつけるのだが……。1995年のロッテルダム国際映画祭新鋭監督賞を受賞。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Canadian Bacon", "ジョン・キャンディの大進撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ドキュメンタリー映画「華氏911」「ボウリング・フォー・コロンバイン」で知られるマイケル・ムーアのブラック・コメディ。冷戦終結後、軍事産業を支えるために反カナダキャンペーンを行った米国首脳陣。お調子者の保安官ブーマーは、義勇軍を結成しカナダに乗り込むのだった。本作は、2度のエミー賞を受賞し、心臓発作で急逝したコメディアン、ジョン・キャンディにとって最後に公開された作品となった。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天空の城ラピュタ", "天空の城ラピュタ", "", "", "http://ecx.images-amazon.com/images/I/51mvIqLeiCL._SL210_.jpg", 124, "ジョナサン・スウィフトの「ガリヴァー旅行記」に登場する天空の島、ラピュタを題材に、宮崎駿が監督、脚本を手がけた、スタジオジブリの劇場用アニメーション作品。鉱山町で、見習い機械工として働く少年パズーは、ある日、空から降ってきた不思議な少女シータと出会う。2人は、シータの身に着けていた不思議な「飛行石」を狙う様々な陰謀に巻き込まれていく。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大海原を行く渡り鳥", "大海原を行く渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "“渡り鳥”滝伸次は九州の雲仙高原に流れつき、観光事業にからむ悪玉の企みからヒロインを助けて大活躍。ダイヤモンドライン入りした宍戸錠に代わって藤村有弘がライバル“ツブテの竜”に扮し、得意の無国籍語を駆使して怪演！", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUTTERFLIES ARE FREE", "バタフライはフリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ブロードウェイで大ヒットした同名舞台の映画化。サンフランシスコの安アパートに住む女優志願の娘と、作詞・作曲で売り出し中の全盲の青年との恋の行方を描いたラブ・ストーリー。この作品でゴールデン・グローブ新人賞を獲得したホーンの個性が光る。青年の母親役、ヘッカートの“姑”ぶりも一見の価値あり。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・スパイダースの大騒動", "ザ・スパイダースの大騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "大阪巡業を終えて次のコンサート開催地に向かうザ・スパイダースの７人組。だが途中で彼らの車は衝突事故を起こしてしまう。しかも被害者の女性は絶世の美女。正章と順たちは次の公演のことなどすっかり忘れて……。日活青春映画路線で花開いた森永健次郎作品。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SPECIALIST", "スペシャリスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "元CIAの爆破工作員レイは、謎の美女メイから彼女の両親を殺した３人の犯人、マイアミを牛耳るキューバ系犯罪組織のボスとその手下を爆殺するという仕事を依頼される。レイが鍛え抜かれた爆破技術を駆使して次々と標的を仕留めるなか、組織は警察と組んで厳重な捜査網を敷く。捜査を指揮するのはレイのCIA時代の上司で、10年前に起こったある作戦上のトラブルから永遠の宿敵となっていたネッドだった。彼はなぜか一連の爆破事件がレイの仕業だということを知っていた。すべての仕事を終えたレイは、メイと激しい恋に落ちる。しかし一方でネッドは、復讐に燃えるメイを利用してレイを巻き込み、爆破犯人に仕立て上げたうえ、自分の手で彼への恨みを晴らそうと企んでいたのだった。S・スタローンとS・ストーンが初共演、ダイナミックな爆破シーンが見もののハード・アクション。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("甲賀屋敷", "甲賀屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "甲賀隠密の世阿彌は阿波藩の陰謀を探ろうとして逆に幽閉されてしまう。甲賀屋敷の財宝を狙う旅川は、そのすきに世阿彌の娘・お千絵を幽閉。すると、虚無僧の法月弦之丞が彼女を救うべく暗躍する。彼はお千絵と愛し合う仲でもあった……。女形俳優出身の衣笠貞之助による本格時代劇。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN TO BE WILD", "ケイティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "サンフランシスコに実在する手話のできるゴリラをモデルにした物語。両親が離婚し、母親に反抗する14歳の不良少年リックは、ゴリラの言語研究をしている母親の関係で、手話のできるゴリラ、ケイティと出会う。リックとケイティは手話を通して意思を通わせるようになり、深い“友情”で結ばれる。そんな時、ケイティの本来の持ち主が現れ、ケイティをショッピング・モールの見せ物にしようとする。ケイティが自由に暮らせるカナダを目指して、二人の旅が始まった。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREEK TYCOON", "愛はエーゲ海に燃ゆ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ケネディ大統領未亡人ジャクリーンと、ギリシャの海運王オナシスとのラブ・ロマンスを描く。船舶富豪トマシスは、美しく貞淑な妻を顧みず、大女優を愛人にしているが、あるパーティーで合衆国上院議員の妻と出会い、その美貌に一目惚れ……。クインの好演が光る、男気あふれるドラマ。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GRATEFUL DEAD MOVIE", "ザ・グレイトフル・デッド・ムーヴィー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "1967年のデビュー以来、コンスタントに活動を続けていたロック・バンド、ザ・グレイトフル・デッド。サイケデリックなサウンドでカリスマ的人気を誇る彼らの、1974年10月サンフランシスコでのステージの模様を追ったドキュメンタリーだ。当時、彼らが使用した巨大PAシステム“ウォール・オブ・サウンド”は641個のスピーカーを組み合わせもので、迫力あふれるサウンドを生み出している。年間100回のコンサートをこなす彼らの演奏も素晴らしく、まさにバンドが質量ともに充実した時期のライブといえよう。バンドのリーダー、J・ガルシアがL・ガストとともに監督し、編集に1500時間を費やしたという。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OS　OLHOS　DA　A’SIA", "アジアの瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "17世紀、長崎での非人間的な宗教弾圧事件を通して、現代への平和の祈りを込めた日本・ポルトガル合作のヒューマン・ドラマ。16世紀末、キリスト教伝道の一環として、４人の日本人少年がローマに派遣される。しかし日本では政治と宗教のあつれきによる禁教令が出され、帰国した４人はそれぞれ悲運の人生をたどることになった。そして現代、EUの文化官ジェーンが、この４人の少年使節団をテーマとしたオペラを紹介する目的で、長崎の街を訪れるが……。", "1996-01-01", "76,25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SYNGENOR", "シンジェノア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "次の国際的軍事紛争は必ず中東で起こると予測した合衆国およびその同盟諸国は、来るべき砂漠戦に勝つため、究極の秘密兵器“シンジェノア（人造統合有機体）”を開発する。だが脱走を試みるシンジェノアも出始めて……。半魚人型の近未来モンスターが暴れまくるSFホラー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONSIEUR KLEIN", "パリの灯は遠く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "1942年、ドイツ軍占領下のパリ。美術商のロベールは同姓同名のユダヤ人と間違われ、身の潔白を証明するため、自力でもう一人のロベールを探し出す。だが、彼はすでに検挙されたあとだった……。有無をいわせぬ横暴な国家権力と、無力な個人の関係を象徴的に描いた問題作。", "1976-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日雇い刑事〈デカ〉", "日雇い刑事〈デカ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "劇団大人計画、HIGHLEG JESUSの面々が特異なキャラを演じ、両劇団の劇中映像や音楽などを担当する映像作家、奥秀太郎が初監督を務めた異色の犯罪ドラマ。警察がパートタイマー制を導入し、日雇い刑事なるものが誕生。そんな刑事の一人、通称“日雇い”は普段は鳶職人だが、事件が起きると呼び出される日々が続いていた。一方、東大出身のヤスが組員となった長坂組はコンピュータ事業に成功し、羽振りのいい生活を送っている。そんなやさぐれ刑事たちと、ハイテク＆ゴージャスなヤクザたちの争いが始まった……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE FURY", "ホワイトフューリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "豪快なスキー・チェイスに、流行のスノーボードを採り入れたサスペンス・アクション。４人の若者がウィンター・スポーツで週末を楽しむログキャビンに、FBIに追われてきた凶悪犯が押し入る。若者たちはブリザードが吹き荒れる雪原に逃げ出したが……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIAMOND HEAD", "ダイアモンド・ヘッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ハワイを舞台に新旧の世代の対立を描いた、ピーター・ギルマンのドラマティックな作品の映画化。カウアイ島はリチャードの王国であった。彼の望みは上院議員に立候補、力の拡大を図ることと妹に自分の後継者を選んでやることだった。力強いメロドラマ。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏のページ", "夏のページ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "大人でもなく、子供でもない。子供から少年への微妙なバランスの中で揺れる仲良し３人組のひと夏の冒険を、大自然の雄大な美しさのなかに描き出した佳編。原作者、みなみらんぼうの少年や自然を見る目の“やさしさ”が随所に現れ、誰もが経験した少年時代のことを思い起こさせてくれる。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARTEMISIA", "アルテミシア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "17世紀のローマに誕生した17歳の伝説の女流画家アルテミシア。欲望のままに愛と芸術に生きた彼女の波乱の半生を鮮やかに描き出す。女性が封建的な制度に縛られていた1610年のローマ。画家である父の手伝いをしながら、独自に絵の勉強を重ねていた17歳のアルテミシアは、若き画家アゴスティーノに弟子入りする。情熱的に絵を探求するうち、二人は激しく愛し合うようになる。しかし、二人の関係を知ったアルテミシアの父親が激怒、アゴスティーノを強姦罪で訴えてしまい……。奔放で純粋なヒロインを演じるのは「ある貴婦人の肖像」の美少女V・チェルビィ。社会の掟の犠牲となり、精神的屈辱を受けてもなお、信念を貫く女性を体当たりで熱演している。", "1997-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK COBRA ３", "ブラック・コブラ３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "S・スタローンの「コブラ」の黒人版として制作された“ブラック・コブラ”シリーズの第３作。シカゴ警察のすご腕刑事マローン、通称“ブラック・コブラ”が挑むのは、CIAから大量に武器・弾薬を強奪した武装犯罪組織。マローンは、捜査の先々に仕掛けられた罠を突破し、組織の秘密基地に肉薄する。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("弥太郎笠", "弥太郎笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "子母沢寛の原作を、大映時代劇の名匠・森一生が監督した股旅任侠映画。旗本直参の家柄に生まれながら、武士を嫌ってヤクザになった、りゃんこの弥太郎の旅と恋心を描く。かつて親分だった虎太郎の娘を慕う弥太郎は、虎太郎を暗殺して悪徳をつくす大八一家に挑戦し、娘を守りぬいていく。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("第一次約會／FIRST DATE", "ファースト・デート　夏草の少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "日本の支配を脱し、アメリカの影響を強く受け始めた1950年代後半の台北。社会の変動の中で、とりわけアメリカ文化の強烈な影響を受けながら揺れ動く青年たちの青春群像を、一人の先生とのかかわりとともに描き出したノスタルジックな青春ドラマ。思春期を台北で過ごした当の本人、ワンが監督している。", "1989-01-01", "53,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Highlander III: The Sorcerer", "ハイランダー3 超戦士大決戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "永遠の生命を与えられ、果てしない闘争を運命づけられた超人“ハイランダー”ことマクラウドを主人公とするSFアクション・アドベンチャーの完結編。ハイランダーを倒し世界最強の支配者になることをもくろむ魔術師ケインは、戦国時代の日本でのマクラウドとの対決で霊山の洞窟に封じ込められてしまう。しかし400年の時が流れた現代、ハイテク工場の建設工事が洞窟を崩し、ケインはよみがえる。時を超え、スコットランド、日本、モロッコ、ニューヨークと空間を超えて、再びハイランダーと魔術師の壮絶な戦いが繰り広げられていく。", "1994-01-01", "3,2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE JARDIN DES PLANTES", "陽だまりの庭で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "アクション喜劇の名手として知られるP・ド・ブロカが、ナチ占領下のパリを舞台に作り上げた感動作。父親を英雄視している純朴な少女と、そんな彼女を哀れんで父がナチスに銃殺されたことを告げられない祖父。レジスタンスもどきの大冒険に繰り出した二人の交流が、おとぎ話を思わせるファンタスティックなエピソードを交えて展開される。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THING　TO　COME", "来るべき世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "SF小説の大家ウェルズが第一次大戦後に未来図として描いた原作を基に、「風と共に去りぬ」で名声を馳せた美術監督W・C・メンジーズがメガホンを取ったモダニズムあふれる近未来SF映画。ドイツの名作「メトロポリス」以上にひねった展開が見られる。1940年、イギリスの都市エヴリタウンを敵機が襲撃、街が破壊された。戦争は長々と続き、夢遊病にかかる人々が増え続け、独裁者が圧政を敷く。その先にある世界とは？", "1936-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギャング対ギャング 赤と黒のブルース", "ギャング対ギャング 赤と黒のブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "東京オリンピックの射撃選手候補・加納信之はささいなケンカから人を殺して将来を踏み誤る。出所後横浜を仕切るヤクザ組織・菊栄会の凄絶な跡目争いに利用された彼は……。鶴田浩二が孤高の暗殺者を演じた、実録タッチの任侠アクション。ハードボイルド風の筋立てだが、描写はかなりウェット。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月と嘘と殺人", "月と嘘と殺人", "", "", "http://image.eiga.k-img.com/images/movie/55116/315x210.jpg?2519", 82, "「アニと僕の夫婦喧嘩」の八神蓮と滝口幸広が再び共演するサスペンス。警察学校で同期の相田と奥寺が勤務する警察署管轄内で、男子高生3人組によるひったくり事件が発生する。事件の被害者は、かつて、巡査だった相田の父を殺害した容疑のかかった男で、鞄には大量のドラッグが入っていた。犯人の1人・ツカサは仲間の制止を振り切って、ドラッグを捌こうとするが……。", "2010-02-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Don't Come Knocking", "アメリカ，家族のいる風景", "", "", "http://image.eiga.k-img.com/images/movie/1366/315x210.jpg?3856", 124, "「パリ、テキサス」のビム・ベンダース監督とサム・シェパードが84年以来、約20年ぶりにコンビを組んだロードムービー。落ち目の西部劇スター、ハワードは、30年ぶりに母親に会いに行き、自分に子供がいるかもしれないと聞かされる。彼は自分の子供を探す旅に出る。主人公のかつての恋人役はシェパードの実生活の妻でもあるジェシカ・ラング。共演は「ドーン・オブ・ザ・デッド」のサラ・ポーリー、「北北西に進路を取れ」のエバ・マリー・セイントら。", "2006-02-18", "7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("祝福／NEW YEAR SACRIFICE", "祝福", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "中国の文豪、魯迅の原作を映画化したもの。清朝末期の中国。若くして夫を亡くした人妻がやがて大金で賀老六という男のもとに嫁がされるが、彼もまた死亡。女は皆から不吉がられ、一人街をさまよう……。薄幸な女の半生を描いた悲劇で、カルロビバリ映画祭特別賞に輝いた。監督は、とりわけエルンスト・ルビッチの影響を最も濃厚に受けていた人物として知られるサン・フー。ニューウェーブ世代（第五世代）と並んでもっとも充実した作品群を生み出した第二世代に属する1916年生まれの監督で、中国初のカラー映画「梁山泊與祝英台」（1953）の監督としても有名。", "1956-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("百合星人ナオコサン", "百合星人ナオコサン", "", "", "http://image.eiga.k-img.com/images/movie/57326/315x210.jpg?1689", 0, "アニメーターが「絵にしてみたい」「動かしてみたい」と感じる企画を、TVシリーズでも劇場用映画でもない短編・中編作品として自由にアニメーション化していく「アニメ文庫」第1弾3作品の1つ。「月刊コミック電撃大王」（アスキー・メディアワークス）で連載されているkashmirのコメディ漫画をアニメ化。ある日突然、少女みすずの前にメイド姿の宇宙人・ナオコサンが現れ、みすずの家に居候することに。しかし、ナオコサンの正体は地球を“百合”化しようと企む百合星人だった。", "2011-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMIN ： THE RISE AND FALL", "食人大統領アミン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1971年から８年間の独裁政治を続けたウガンダの大統領アミンの実話に基づく一編。虐待や虐殺はもちろんのこと、人肉を食べたり、若い娘をレイプしたりと、数限りない悪名を数えた彼の姿が綴られている。しかし、描写にまったく迫力がなく、演出や物語の展開も非常に平坦。", "1982-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUPPETS FROM SPACE", "ゴンゾ宇宙に帰る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1976年にスタートした人気TV番組『マペット・ショー』のキャラクターが活躍するSF冒険コメディ。マペット下宿屋に住む鼻の長いゴンゾが、お告げによって自分は宇宙から来たエイリアンだということを知る。TVショーに出演するなどして宇宙の家族を探し始めるゴンゾ。だが、そんな彼を政府の秘密組織が狙っていた……。A・マクダウェルやR・リオッタら有名スターたちがカメオ出演。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOOD SAM", "善人サム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "“善人サム”とアダ名されるデパートの支配人はたいへんな慈善家。家では怠け者の義弟を養い、隣人には車を貸して事故を起こされその尻ぬぐいも自分でする。いつも損な役まわりばかりのサムにある日、とんでもない幸運が……。名匠L・マッケリーが描く心温まる人情ドラマ。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE BATTANT", "鷹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "宝石強盗で10年の刑を受けたジャックが出所した。だが事件は依然、謎に包まれていた。警察と昔の仲間の双方から追われる彼の前に美しい女が現れる。A・ドロンが監督、主演ほか４役をこなし、フィルム・ノワールの復権を目指して渾身の力で描くサスペンス。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INVASION U.S.A.", "地獄のコマンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "全米で同時多発テロが発生。今は引退し悠々自適の生活を送っていたスーパー・コマンド、マット・ハンターは、国家存亡の危機に一人立ち上がる。裏でテロを指揮する東側工作員ロストフとの宿命の対決は……。全編、手に汗握る凄絶な戦闘シーンが展開するアクション・ムービー。DVDは「チャック・ノリス アクションDVD−BOX」に収録。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ghost Rider: Spirit of Vengeance", "ゴーストライダー　スピリット・オブ・ベンジェンス（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-02-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Too Late the Hero", "燃える戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "　太平洋戦争下の南太平洋上ニューヘブリデス島。膠着状態にある戦局を打開すべく、連合軍はジャングルの奥地にある日本軍の無線通信所の破壊作戦を決行する。アメリカ軍のローソン中尉、イギリス軍ホーンスビー大尉といったメンバー成る工作隊は犠牲者を出しながらも任務を遂行。しかし兵たちの軋轢は絶えず、さらに日本軍の追跡隊も彼らを動揺させる……。激しい戦闘、そして極限状態での兵たちの姿を描いた戦争映画。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まむしの兄弟", "まむしの兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "菅原文太主演の往年のアクション・シリーズを、1990年代風にリメイクした痛快ヤクザ映画。バイオレンスとユーモア、男気が入り交じるアツいドラマが、柳葉敏郎の硬派な魅力とともに描かれる。“まむし”の異名をとる凶暴なヤクザ、ゴロ政が刑期を務めて出所。若い女組長に惚れた彼が、弟分の勝次とともに敵対組織に殴り込み……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の妻たち・赤い殺意", "極道の妻たち・赤い殺意", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ヒットを連発してきた“極道の妻たち”が、主演に高島礼子を据えて完全リニューアル。脚本は“極妻”シリーズで３作品の監督を務めた中島貞夫。大阪ヤクザ一家の跡目を争う内部抗争で組長が殺される。そのカタギの息子は妻、由紀と平穏な新婚生活を送るはずだったが、組長の座に着いた直後に暗殺されてしまう。怒りに燃えた由紀は復讐を誓って背中に不動明王の刺青を彫り、犯人捜しを始めるのだった……。シリーズを支えてきたかたせ梨乃が抗争の犠牲になった夫の仇を討つために由紀とともに戦う極妻を演じている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女囚さそり・けもの部屋", "女囚さそり・けもの部屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "前２作とは異なり、今回は都会（けもの部屋）が舞台となる。刑務所を脱獄したナミは、売春婦のもとに身を寄せる。ナミは町のヤクザの情婦カツからリンチを受ける。ナミは夜の女たちの“怨”を背追い、ドスを手にする……。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MIRACLE WORKER", "奇跡の人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1962年に製作された感動の名作をリメイク。目、耳、口の不自由な、三重苦に苦しむ少女時代のヘレン・ケラーと、彼女に生きる喜びを教えた盲目の女教師サリヴァンの交流を描く。1962年版でヘレン・ケラー役を演じてアカデミー賞の助演女優賞に輝いたパティ・デュークが、ここではサリヴァン先生に扮し、好演している。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GENTLEMEN PREFER BLONDES", "紳士は金髪〈ブロンド〉がお好き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ナイトクラブの踊り子、モンローとラッセルは大の仲良し。モンローの“玉の輿”をめぐって、二人の珍道中は始まるが……。モンローが『ダイヤモンドは女性のベスト・フレンド』を歌えば、ラッセルは“モンロー・ウォーク”を披露。二大女優のお色気競演が楽しいミュージカル・コメディ。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DISPARA", "愛よりも非情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "サーカス一座に生まれ育ったアンナは射撃の名手。ある日彼女は、サーカスの取材にやって来た新聞記者マルコスと出会い、やがて心から愛し合うようになる。しかし運命の夜、マルコスの留守中にアンナは３人の男に暴行されてしまう。誰にも打ち明けられず深い孤独と絶望の中、アンナはたった一人で復讐を決意する……。マルコス役をA・バンデラスが好演している。", "1993-01-01", "9,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Et si on vivait tous ensemble?", "アンド・イフ・ウィ・オール・リブド・トゥゲザー？（英題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "", "2012-12-31", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心跳墨脱", "紅い鞄　モォトゥオ探検隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "モンゴル族出身のハスチョロー監督が、中国最後の秘境といわれるチベット奥地の墨脱（モォトゥオ）を舞台に撮りあげた作品。数千キロメートルの危険な山を歩いて学校へ通っているという墨脱の子どもたちのため、私財を投じて墨脱に学校を建てた上海のとある老人。そんな老人の行動に感銘を受けた新聞記者ワンは、現在は墨脱で暮らす老人に取材するため、老人を診察しに来た女医や地元の校長らとともに墨脱への険しい道のりを進んでいくが……。", "2007-03-24", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Beckoning Silence", "運命を分けたザイル2", "", "", "http://image.eiga.k-img.com/images/movie/54886/315x210.jpg?4478", 74, "実在の事件をもとに、ペルーのアンデス山脈で遭難した登山家たちを描いた03年のイギリス映画「運命を分けたザイル」の続編。前作の遭難事故後、厳しいリハビリを経て奇跡的な復活を遂げたジョーは、敬愛する伝説の登山家トニー・クルツが挑んだ「妄執の塊」と呼ばれるスイス・アイガー北壁へ挑む。", "2009-11-07", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("テラ戦士ΨBOY", "テラ戦士ΨBOY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "超能力を持つ16歳の女子高生MOMOKOが、宇宙から地球上に墜落してきた謎の異星人BOYを、彼を使って超能力の悪用をたくらむマッド・サイエンティストたちから救い出すまでを描いたファンタジー。ふんだんな特撮シーンと、当時の人気アイドル、菊池桃子の演技が魅力。DVDは「菊池桃子プレミアムコレクション“BRAND”」に収録。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIEF SEASON", "さよならを言わないで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "証券会社の社員、ジョニーと国連のタイピスト、ルイサは、燃え上がるような激しい恋に落ちた。ジョニーは二人で一緒に住む家がほしいために、預かった証券を無断で操作し、警察に捕まり、二人の生活は長くは続かなかった。短い間に激しく燃えた青春のひとときを描いた感動作。", "1970-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花とアリス", "花とアリス", "", "", "http://image.eiga.k-img.com/images/movie/1416/315x210.jpg?2795", 135, "明るく素直なハナと、おてんばでいつもハナを振り回しているアリスは幼なじみで、これまでなんでもいっしょにしてきた親友。高校生になったハナの宮本センパイへの初恋にアリスが協力、恋を成就させようとするのだが。記憶喪失に端を発した奇妙な三角関係と、少女たちの成長が並行して進行。その中に短編映画のようなエピソードが散りばめられていく。「リリイ・シュシュのすべて」の岩井俊二監督が、脚本、音楽、編集も担当。", "2004-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLICE ACADEMY: MISSION TO MOSCOW", "ポリスアカデミー777　モスクワ大作戦!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ご存じ、人騒がせな警官集団がロシアで大暴れするシリーズ第７作。ロシア・マフィア潰滅のためモスクワに乗り込んだ“ポリアカ”の面々。勲章のつけ過ぎで一人では立っていられない署長ら、負けず劣らずヘンな現地の警官と組んだ彼らが、コンピュータを利用して大金強奪を企てるマフィアに戦いを挑む。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LADY CRAVED EXCITEMENT", "ふるえる女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "クラブ“カルロ”のダンサー、パットとジョニーは人気の名コンビ。だが平凡な毎日が物足りないパットはいつも刺激を求めている。そんな折、彼女は新聞記事に載っている殺人犯の顔が、客の一人ではと疑う。その日から、二人の迷探偵としての活躍が始まった。ホラー作品を数多く手掛けたハマー・フィルムが制作したコメディ感覚のライトな映画。", "1950-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天下を取る", "天下を取る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "源氏鶏太原作による石原裕次郎の一連のサラリーマンものの一編。裕次郎扮する商事会社の新入社員が、同期の社員を参謀役にして困難な契約を獲得するために奔走する。石原裕次郎の華やかなスター性は、サラリーマン役などにはとても収まるものではなかった。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHERE SIDEWALK ENDS", "歩道の終わる所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「帰らざる河」などで知られる名匠O・プレミンジャーが手掛けたフィルム・ノワールの傑作。脚本を務めたB・ヘクトは「暗黒街の顔役」で有名な作家としても知られる。非情な刑事を演じるD・アンドリュースの存在感と、そんな彼を変えていく美女役ジーンの震えるほどの美しさ。絶妙なアンサンブルも見もの。ギャングのボスから大金を巻き上げた男が殺された。刑事はボスが罪を着せようとする容疑者を拷問の末、殺してしまい死体を隠してしまうが……。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CHAMBRE VERTE", "緑色の部屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "若くして妻を失った男ジュリアンは40歳になっても独身を守り、ひっそりと暮らしている。彼の家には秘密の部屋があり、そこで彼は死んだ妻の霊を祭っていた。トリュフォー自らが主演し、死について問題提起した作品で、ロウソクを使った“緑色の部屋”の映像が印象的。", "1978-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kung Fu Dunk", "カンフー・ダンク！", "", "", "http://image.eiga.k-img.com/images/movie/53526/315x210.jpg?2519", 100, "ジェイ・チョウ、チェン・ボーリン、バロン・チェンらアジアのスターと、「少林サッカー」「HERO」を手がけたスタッフが作り上げたスポーツ・アクション・コメディ。超人的な身体能力を持つシージエは、ひょんなことから大学のバスケットチームに入部する。バスケの初心者だったシージエだが、「気」を駆使してダンクシュートを極め、チームを勝利へと導いていく。やがて大学バスケ・リーグの決勝戦で邪悪な対戦チームに命懸けで挑むが……。", "2008-08-16", "53,6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HUNCHBACK OF NOTRE DAME", "ノートルダムのせむし男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "サイレント時代に恐怖映画のスターとして絶大な人気を誇ったR・チャニーの代表作。ルイ11世統治下のパリを舞台に、美しいジプシー娘に恋したせむしの鐘つき男の純情を描く内容は、その後の３度の映画化と同じものだが、メイクの醜怪さにかけてはこの作品が群を抜いているといえるだろう。", "1923-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Wedding Campaign", "ウェディング・キャンペーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "「トンマッコルへようこそ」のチョン・ジェヨンと「ファミリー」のスエ主演で贈るハートウォーミングなラブ・コメディ。韓国の田舎町で暮らす38歳の独身男マンテクは、同じく婚期を逃した親友ヒチョルとともに、ウズベキスタンへ花嫁探しの旅に出る。ところが、純情すぎて女性とまともに話すこともできないマンテルは、お見合いに失敗してばかり。通訳の女性ララは彼のお見合いをなんとか成功させようと手を尽くすが……。", "2006-12-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("惑星大怪獣ネガドン", "惑星大怪獣ネガドン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "「ゴジラ」シリーズなどに参加したCGアーティストが、たった一人で作り上げたフルCGの本格的怪獣映画。往年の特撮映画にオマージュを捧げたというだけあって、50年代のフィルムの質感を忠実に再現。CGとは思えぬアナログ感覚がほとばしる、ノスタルジックで新鮮なヴィジュアルに注目。昭和100年を迎えた架空の近未来。墜落した宇宙船から宇宙怪獣ネガドンが現れ、東京に向けて侵攻を開始。日本人は滅びてしまうのか？", "2005-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　MIDSUMMER　NIGHT’S　DREAM", "真夏の夜の夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "シェイクスピアの戯曲を、19世紀初頭のイタリア、トスカーナ地方に舞台を設定して映画化したロマンティック・コメディ。愛し合うカップル、ハーミアとライサンダーは駆け落ちして森に入り込む。ハーミアの婚約者ディミトリアスは彼女を捜しに出かけ、そのあとを彼を愛するヘレナが追いかけていった。それを見た妖精の王オベロンはヘレナの愛を成就させようと、いたずら好きの妖精パックに愛の媚薬を使うように命じるが、相手を間違えてしまい……。真夏の一夜、森の中で繰り広げられる愛の騒動をファンタスティックに描いていく。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Presents うに煎餅", "Presents うに煎餅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "直木賞作家・角田光代の短編集「Presents」の映画化第2弾。文具メーカーに就職して1年になる羽月。留年を続けて未だに大学に通っている恋人・悟との関係が上手くいかなくなった彼女は、合コンで知りあった年上の男性・安田にひかれはじめる。彼女はバレンタインデーに安田に本命チョコを渡すが、ホワイトデーに安田からピアスをプレゼントされた時、ようやく大切なことに気づく。主演は「デスノート the Last Name」の戸田恵梨香。", "2007-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠れる美女", "眠れる美女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "川端康成原作の『眠れる美女』と『山の音』を融合し、新解釈を施して映画化。初老のクラシック音楽評論家の江口は、薬で眠った少女たちが老人たちの玩具となる“館”に通ううちに、息子の嫁・菊子の寝顔を彼女たちに重ねるようになる。一方、夫の不倫に悩む菊子は義父に想いを寄せ、そんな「館」に通う義父の姿を知った時、眠れる美女になりすます決心をする……。初のフケ役に臨んだ原田芳雄と、アイドルの殻を破りヌードも披露した大西結花が、微妙な父娘を好演している。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新不了情／C’EST LA VIE，MON CHERI", "つきせぬ想い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "不治の病に冒され、余命いくばくもない少女と売れない青年作曲家のラブ・ストーリー。作曲家のキットは妥協することができず鬱屈とした日々を送っていた。人気歌手のトレーシーと同棲をしていたが、ある日、口論をし、キットは部屋を出てしまう。新しく借りたアパートで、少女ミンと出会い、やがて愛し合うようになる。明るく積極的なミンだったが、いつ再発するかわからないガンを持病に持っていた。ミン役のA・ユンがみずみずしい演技を見せている。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cover Up", "デスロック 戦略ガス兵器を追え！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "イスラエルの米海軍基地で起きた爆弾テロ事件を取材する特派記者のマイク。激動の地で事件の真相を追う彼は、やがて数万人の巡礼者抹殺をたくらむ神経ガス強奪事件に巻き込まれていく。肉体派スター、ラングレンが新たな領域に挑んだスペクタクルなサスペンス・アクション。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("They", "インプラント", "", "", "http://image.eiga.k-img.com/images/movie/1446/315x210.jpg?3412", 89, "大学院生ジュリアに、突然、幼なじみのビリーからの呼び出しが。数年振りに再会したビリーは身体に奇妙な傷があり、「暗闇に気をつけろ。俺は助からない」という言葉を残して、彼女の目の前で自殺する。その日以来、ジュリアの身の回りで奇妙な出来事が起こり始め、彼女の身体にも彼と同じ謎の刻印が浮かび上がってくる。彼女は幼なじみたちと協力して、謎の正体を探っていくが。監督は「ヒッチャー」のロバート・ハーモン、主演は「アンブレイカブル」「恋する遺伝子」のローラ・レーガン。", "2003-03-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スワロウテイル", "スワロウテイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 148, "「Love Letter」に続く岩井俊二監督長編第2作。舞台は、円都と呼ばれる架空の都市。仲間のフェイホンらとともに偽札作りを始めた娼婦グリコは、ライブハウスを買取、歌手として成功していく。Chara演じるグリコがボーカルを務めるバンドYEN TOWN BANDの楽曲が、実際にYEN TOWN BAND名義で発売され話題となった。96年の日本アカデミー賞において、話題賞、新人俳優賞（Chara、伊藤歩）を受賞。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON DEADLY GROUND", "沈黙の要塞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "S・セガールが、製作・主演に加えて念願の初監督も兼ねたアクション大作。アラスカのイヌイットの聖地で、無茶な開発に着手した巨大石油会社エイジス。石油火災消火のエキスパートとして知られるタフガイ、フォレスト・タフトは、利権のためなら手段を選ばない社長ジェニングスに友人を殺され、一人敢然と立ち上がる……。日本では“沈黙”シリーズとうたわれたが、「沈黙の戦艦」「沈黙の断崖」とは無関係の１本。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("源氏物語　千年の謎", "源氏物語　千年の謎", "", "", "http://image.eiga.k-img.com/images/movie/55656/315x210.jpg?3664", 135, "「人間失格」「ハナミズキ」の生田斗真を主演に、作家・高山由紀子の「源氏物語　悲しみの皇子」を映画化。「愛の流刑地」の鶴橋康夫監督がメガホンをとり、“物語の中の光源氏の世界”と“物語を書いた紫式部の世界”が交錯する世界を描く。共演に窪塚洋介、東山紀之ほか、中谷美紀、真木よう子、田中麗奈、多部未華子ら豪華女優陣が結集する。", "2011-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK BEAUTY", "黒馬物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "汚れなき心を持つ少年と、黒い子馬ブラック・ビューティーとの心のふれあいを美しく描いた感動作品。名子役として、かつて全世界のファンを魅了したM・レスターの、「小さな恋のメロディ」以前の主演作である。原作は、児童文学の名作として名高いA・シュウエルの同名小説。", "1970-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAMILA", "カミーラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "19世紀半ばのブエノスアイレス。上流家庭の令嬢カミーラと、イエズス界の神父が恋に落ちる。だが権力と教会は、自由のシンボルである彼らの愛を認めず、やがて二人は人々の同情を集めながら処刑される……。“血の独裁”といわれた悪名高きファン・マヌエル・デ・ローサスの政権下にあった実話をもとにした作品。この物語は長く政府によって製作が禁止されていたが、民主政権の到来をうまく捉えたベンベルグが映画化に成功した。", "1984-01-01", "22,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ANGLAISE　＆　LE　DUC", "グレースと公爵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "フランスの名匠E・ロメールが、油絵に実写の人物をCGで重ね合わせるという新しい技法を駆使して描く歴史ドラマ。18世紀末、フランス革命で旧体制が崩壊したパリは混沌とした状態だった。そんな中で、国王ルイ16世を敬愛するグレース・エリオットと革命派に傾倒するオルレアン公爵は、強い絆で結ばれた友人関係にあった。やがて、改革が進まず、生活が改善されないことに不満を募らせた民衆による蜂起が始まる。グレースと公爵は、信条の違いから仲違いをするが……。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うれしはずかし物語", "うれしはずかし物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "家庭を離れ、一人になれる空間を求めてワンルーム・マンションを買った中年男と、週に１度の約束と割り切って愛人契約を結ぶあっけらかん女子大生との恋模様を、ペーソス豊かに描いた作品。川上麻衣子が均整のとれたプロポーションを披露する。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POSLENDNIE KANIKULI", "ラスト・ホリデイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "カザフスタンの映画作家A・カラクーロフが、アフガン紛争勃発直前の時代の真実をシンプルな映像で追求し、第９回東京国際映画祭ヤングシネマ部門で東京ゴールド賞を受賞した青春映画。舞台は1979年のカザフ。カザフ人のカリム、グルジア系ユダヤ人のジェーカ、ロシア人のワレールカは、どこにでもいる不良高校生だった。ある日、出来心でエレキギターを盗んだために、ワレールカが警察で拷問的なリンチを受ける。やがて彼をかくまうカリムとジェーカも悲劇的な運命をたどっていく。", "1996-01-01", "51", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUILLS", "クイルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "文豪マルキ・ド・サドの晩年を描いた戯曲の映画化。幽閉され、自由を奪われながらも執念で執筆し続けるサド侯爵の姿を描く。精神病院に収容されたサドは、小間使いの協力を得てひそかに自分の作品を出版。しかしその事実が発覚し、彼はペンや家財道具を没収される。それでもサドは、文字通り我が身を削って小説を書き続ける……。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月光仮面・第２部／絶海の死斗", "月光仮面・第２部／絶海の死斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "“HOジョー発爆弾”を手に入れんとするどくろ仮面は、発明家・中山博士を誘拐し、設計図のある一本松の洞穴に向かうが、すでに月光仮面が先回りしていた。追いつめられたどくろ仮面は海中に墜落する。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロックンロールミシン", "ロックンロールミシン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「GO」で在日韓国人少年の青春を描き2001年度の各映画賞を総なめにした行定勲が、今度は日本の若者たちの夢と挫折をテンポ良く描く。仕事にも恋愛にも行き詰まっていた会社員の賢司は、高校の同級生だった凌一たちのデザイナーズブランドの立ち上げに加わる。自分たちが着たいと思う服を作る凌一たちを見ているうちに、こここそが自分の居場所だと思えるようになった賢司だったが……。原作は、第12回三島由紀夫賞を受賞した鈴木清剛の同名小説。原作とはあえて変えたというラストシーンが壮快。青春映画らしい小気味いい後味を残す結果となった。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RHYME　＆　REASON", "Rhyme＆Reason〈ライム＆リーズン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ヒップホップ・シーンの現在を、人気アーティストの証言を中心にして紹介する音楽ドキュメンタリー。韻を踏んだエネルギッシュな詞をビートに乗せて表現するラッパーたちの胸の内や、業界の内幕に鋭く迫る。Qティップやローリン・ヒルらが語る創作の秘密はファンならずとも必見だ。人気アーティストがヒップホップを取り巻く環境について激白。また業界の裏事情や創作の背景にある社会のシビアな現実を浮き彫りにする。", "2005-08-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血煙高田馬場", "血煙高田馬場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "講談でおなじみの中山安兵衛の高田馬場18人斬りを、マキノ雅広・稲垣浩の両監督が共同で撮り上げた痛快この上ないチャンバラ映画。不満から新発田藩を出奔して江戸にやって来た中山安兵衛は、毎日酒をあおり、ケンカをしてヤクザな生活を送っている。ある日、伯父が、18人を相手にして高田馬場で決闘をはじめた。それを聞いた安兵衛は八丁堀から決闘場の高田馬場まで韋駄天に走りぬけ、息絶えた伯父の仇を討つべき斬り込んでいく……。カット割のテンポの良さや、セリフの歯切れの良さで実にスピード感ある作品に仕上がっており、特に安兵衛が高田馬場に駆けつけるシーンからの一気にたたみかけるような圧倒的なリズムは他に類をみない。有名な韋駄天走りのシーンは実に20数カットも土手を走る安兵衛の姿を素早いパンで重ね、その実験的ともいえる手法は未だに語り草となっている。また、18人斬りのシーンでは撮影中に音楽を流し、ジャズダンスをまねながら殺陣を振りつけたという、エピソードも有名。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Once Around", "ワンス・アラウンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「マイライフ・アズ・ア・ドッグ」で世界中にその名を知らしめた、スウェーデンの名匠ラッセ・ハルストレム監督のハリウッド進出第1作。リチャード・ドレイファスとホリー・ハンターを主演に迎え、家族愛をテーマに描くコメディタッチのヒューマン・ドラマ。主人公レナータがようやく見つけた結婚相手は、腕利きのセールスマン。ところが、がさつな彼はレナータの両親とそりが会わず、彼女は彼らの間で板ばさみになってしまう……。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("17歳の風景／少年は何を見たのか", "17歳の風景／少年は何を見たのか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「完全なる飼育・赤い殺意」で復活を果たした鬼才、若松孝二が実際の事件にインスパイアされて創作した、シンプルにして力強い一作。母親を殺害し、一人自転車で旅を続ける少年の姿を、一切の内面描写を排して冷徹に見据える。少年の顔面に叩きつけられる風、そして風景が何よりも雄弁だ。富士山を眺めながら、北へと少年は自転車をこぎ出した。三国峠から六日町、柏崎、象潟、男鹿半島と、行路はどんどん過酷になり……。", "2005-07-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南海の狼火", "南海の狼火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "四国の宇和島。須賀真珠会社は、新興の宇高真珠に借りた借金を、闘牛の賞金で返そうとする。だが、須賀真珠の乗っ取りをたくらむ宇高真珠は、陰謀を巡らしていた。そこに流れ者の野村浩次がやって来て……。ライバル役の宍戸錠は、常に数珠を離さない“坊主の政”に扮し、相手を射った後に念仏を唱えるという、とぼけた殺し屋を好演している。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("情事の履歴書", "情事の履歴書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "過激な政治メッセージと暴力的な性描写で“ピンク映画の巨匠”といわれた若松監督の大ヒット作。集団就職で上京してきた少女が、様々な男性遍歴の果てに、たくましく生き抜いていく姿を描く。田舎で男に犯され、千草みどりが全裸で雪原を走るシーンは、当時マスコミの話題となった。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Morocco 横浜愚連隊物語", "Morocco 横浜愚連隊物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "戦後の横浜に実在したモロッコハットをかぶり、映画「モロッコ」を愛した陽気なならず者“モロッコの辰”の生き様を描いたアクション映画。昭和20年、進駐軍のはびこる横浜の街を、辰と彼が率いる愚連隊が闊歩する。辰は愚連隊を引き連れ、進駐軍の物資を奪い、警察をも巻き込んで気勢を上げる。そんな彼の生き方にあこがれた若者たちが次々と集まり、彼らは自由のために戦う。だが、次第に辰はハマ中のヤクザを敵に回し、抗争に巻き込まれていく……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天云山伝奇／LEGEND OF TIANYUN MOUNTAIN", "天雲山物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "「芙蓉鎮」「最後の貴族」のシェ・チンが、文革終了後間もない1980年に発表した作品。主人公は1950年代末の反右派闘争で、右派とのレッテルを貼られ苦境に陥る羅群という男。彼をめぐってその恋人だったものの結局は彼を捨て幹部夫人となった馮薇という女と、逆境の中で彼に同情し、ともに生活苦に耐えていく宋晴嵐という女の運命をヒューマニスティックな視点で描き出していく。", "1980-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Der amerikanische Freund", "アメリカの友人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "白血病で死を宣告されているドイツの額縁職人ヨナタンを1度限りの殺し屋に仕立てようとアメリカ人の詐欺師が近づく。彼に第2の殺人依頼が舞い込んだと知った詐欺師は、不思議な友情に駆り立てられて助けに向かうが……。パトリシア・ハイスミスの主人公“アメリカの友人”トム・リプリーをデニス・ホッパー、額縁職人をブルーノ・ガンツが演じるほか、レイ、フラー、シュミット、ユスターシュといったベンダースの敬愛する監督たちが特別出演も豪華な1作。", "1987-06-27", "45,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Martyrs", "マーターズ", "", "", "http://image.eiga.k-img.com/images/movie/54486/315x210.jpg?3894", 100, "1970年初頭のフランス、行方不明となっていた少女リュシーが路上を彷徨っているところを発見される。何者かに廃墟に監禁され、長期に渡って拷問と虐待を受けていたリュシーは事件の詳細を語らないため、捜査は難航を極めていた。養護施設に収容されたリュシーは、少女アンナの献身的な介護で平穏な生活を取り戻してゆくが……。監督はフランス・ホラー界の新鋭パスカル・ロジエ。", "2009-08-29", "2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冒険者たち　ガンバと7匹のなかま", "冒険者たち　ガンバと7匹のなかま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「あしたのジョー２」「ゴルゴ13」など多種多彩な感覚で演出を手がける出崎統監督が、1975年にTV放映された連続アニメ『ガンバの冒険』を映画化した傑作だ。ディズニーの動物アニメに匹敵するような、愛と勇気と元気をくれる作品だ。ドブネズミのガンバが、イタチに脅える島の仲間のネズミたちを励まし、イタチと対決する。最後には、ジーンとした感動に包まれる。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MYSTE｀RE ALEXINA", "女教師アレクシーナ　白い肌の秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "禁じられた性遍歴を重ねてきた実在の人物をモデルに描く、エロティック・インモラル・ムービー。寄宿制の女学院に赴任してきた女教師のアレクシーナは、ある日、ルームメイトのサラに、自分が幼少のころに経験した女性との恋愛について告白する。サラはとっさに身を引くが、そのことにショックを受けたアレクシーナはひどく落ち込み、生徒にもつらく当たるようになるのだった。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラバーズ　覆う女", "ラバーズ　覆う女", "", "", "http://image.eiga.k-img.com/images/movie/55576/315x210.jpg?4003", 77, "ラバー（ゴム）素材の衣服を身につけることで快感を覚える“ラバーフェチ”の沙夜子は、誰にもその秘密を明かすことなく、弁当工場で淡々と働く日々を送っていた。そんなある日、新しく主任として配属された木下と意気投合し、互いにひかれあっていくが、なかなか自分の性癖を打ち明けられずにいた。「バカは2回海を渡る」の渡邊貴文監督がメガホンをとり、「サムライプリンセス　外道姫」の希志あいのが主演を務める。", "2010-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大暴れ東海道", "大暴れ東海道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "遠山の金さん、鼠小僧、弥次・喜多など、東海道を駆けめぐったおなじみの面々が登場する娯楽時代劇。遠山金四郎は、岡崎・水野監物家に起こった家老・黒木一派によるお家乗っ取り騒動に巻き込まれ、命からがら逃げて来た千代姫を助ける。そして大目付代理として城中へ乗り込み黒木一派の悪事を暴露する。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODY COUNT", "ボディ・カウント／ヤバい奴ら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "５人組の強盗団が、ボストン美術館から高額の絵画を盗み逃走した。警官の銃弾に倒れたリーダーを失い、４人だけで取引先のマイアミへと向かうことになる。ところが、それぞれの敵意と疑惑が沸き上がり、４人は仲間割れをしてしまい……。ふとしたことで亀裂が生じた犯罪者一味の転落の一途を、強盗シーンの過去と逃走シーンの現在を交錯させて描いていく。仲間の中で最もイカれた男を怪演するJ・レグイザモと、冷静沈着なD・カルーソーの緊張感あふれる一触即発の駆け引き、意外な展開が衝撃的な犯罪サスペンスだ。ビデオ発売時タイトルは「ボディ・カウント」。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桐島、部活やめるってよ", "桐島、部活やめるってよ", "", "", "http://image.eiga.k-img.com/images/movie/57626/315x210.jpg?3615", 0, "早稲田大学在学中に小説家デビューし、第22回小説すばる新人賞を受賞した朝井リョウの同名小説を、「腑抜けども、悲しみの愛を見せろ」の吉田大八監督が映画化した青春群像劇。田舎町の県立高校で映画部に所属する前田涼也は、クラスの中では静かで目立たない、最下層に位置する存在。監督作品がコンクールで表彰されても、クラスメイトには相手にしてもらえなかった。そんなある日、バレー部のキャプテンを務める桐島が突然部活を辞めたことをきっかけに、各部やクラスの人間関係に徐々に歪みが広がりはじめ、それまで存在していた校内のヒエラルキーが崩壊していく。主人公・前田役に神木隆之介が扮するほか、前田があこがれるバトミントン部のカスミを「告白」の橋本愛、前田同様に目立たない存在の吹奏楽部員・亜矢を大後寿々花が演じる。", "2012-08-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANSIKTET", "魔術師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "19世紀スウェーデン。魔術師の旅芸人一座が、魔術はインチキだという領事や警察署長、医師の前でレパートリーを演じさせられる。彼らは神秘を信じない俗人たちに、したたかなしっぺ返しをする……。後年のベルイマン作品と違ってラストはにぎやか。", "1958-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心霊２", "心霊２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "“霊界案内人”稲川淳二が２話オムニバスで贈る恐怖映画の第２作。第１話「踏切に立つ少女」は、母が倒れたという知らせを聞いて帰郷したOLの物語。踏切で不思議な少女を見るようになってから彼女は奇妙な体験をすることに。第２話「まちぼうけ」は稲川淳二自身が15年前に体験した話。レポーターとして温泉街にやって来た彼はその夜、一軒の宿に泊まる。開かずの間があると聞いた稲川とスタッフはその部屋に向かうのだが……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LOST　SON", "ロスト・サン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "フランスの名優D・オートゥイユが、英語映画デビューを飾ったスタイリッシュ・ミステリー。異国で暮らす孤独な私立探偵が、行方不明の息子の依頼を受け捜索するうちに事件に巻き込まれていく……。ロンドンの裏社会を舞台に少年売春、幼児虐待という衝撃的なテーマで新境地を切り開いたC・メンゲス監督の問題作。", "1999-01-01", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ART　MUSEUM　BY　THE　ZOO", "美術館の隣の動物園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "兵役休暇を利用して青年が恋人のアパートを訪ねると、恋人とは正反対のズボラな性格の女が住んでいた。彼女の夢は、シナリオライターになること。やがて二人はお互いに恋する相手をモデルに共同で一本のシナリオを書き上げる。青龍賞シナリオ公募部門で入選した自らの脚本を、韓国映画界では数少ない女性監督が映画化したラブ・ストーリー。「八月のクリスマス」「カル」のシム・ウナがコミカルな演技に初挑戦し、キュートな魅力を振りまいている。", "1998-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEPTUNE FACTOR", "深海征服", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "大西洋の海底に設けられた海洋ラボが地震で研究員ごと海溝に転落。原子力潜水艦に救助を依頼するが深すぎてだめ。最後の手段として深海用潜水艇ネプチューン号が潜航を開始、様々な危機に直面しながら、救助に成功する。カナダを中心とした海洋科学陣の協力によって製作された海底SF映画。", "1973-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鈍獣", "鈍獣", "", "", "http://image.eiga.k-img.com/images/movie/53896/315x210.jpg?3049", 106, "週刊誌編集者の静は、失踪した作家・凸（でこ）やんこと凸川の行方を探るため、全てが相撲中心の田舎町ときわにやって来る。そこで静を待ち受けていたのは、町でトップを目指す江田と、その腰巾着の警官・岡本、江田の愛人・順子に、ホステスのノラという怪しげな面々だった。岸田國士戯曲賞を受賞した宮藤官九郎の舞台「鈍獣」を映画化。浅野忠信が殺しても死なない鈍い男を演じる。", "2009-05-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("George Michael: A Different Story", "ジョージ・マイケル　素顔の告白", "", "", "http://image.eiga.k-img.com/images/movie/1066/315x210.jpg?3910", 100, "「ウキウキ・ウェイク・ミー・アップ」「ラスト・クリスマス」「ケアレス・ウィスパー」といったヒット曲で80年代のUKミュージック・シーンのアイドルとなりながらも、レコード会社との契約訴訟問題で活動を休止したワム！のジョージ・マイケルが、自らのゲイ疑惑についてなど、実生活を語るドキュメンタリー。ワム！解散以来、表舞台から消えていた相棒アンドリュー・リッジリーも登場。", "2005-12-23", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほえる犬は噛まない", "ほえる犬は噛まない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "妊娠中の妻に家計を支えてもらいながら大学教授を目指すユンジュは、マンションに響き渡る犬の鳴き声にイライラしていた。一方、管理人室に勤める女性ヒョンナムは、失踪した子犬を探すことになり……。日常に潜む笑いや恐怖、アクションにファンタジー、すべてを盛り込んだ傑作ブラック・コメディ。さえない女子のチャーミングな青春映画としても楽しめる。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("scope", "scope", "", "", "http://image.eiga.k-img.com/images/movie/55356/315x210.jpg?1541", 84, "インディーズ界の新鋭・ト部敦史が、犯罪加害者のしょく罪を描く長編デビュー作。強かん罪で6年間服役したアツオが出所すると、そこには元性犯罪者を監視する“スコープ法”が制定された社会が待ち受けていた。監視の目から逃れるように都会を去り、離島の工場に就職したアツオは、そこでろうあの少女ナギと恋に落ちる。しかし、アツオの素性が知れわたると、職場の人々は不信感から態度をひょう変させていく……。", "2010-05-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILENT　TONGUE", "アメリカンレガシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "作家、演出家としても活躍するS・シェパードの「ファー・ノース」に続く監督第２作。荒涼とした大地を背景に、ネイティブ・アメリカンと白人の間に残る、怒りと苦悩を浮き彫りにする。青年タルボットは出産の際に亡くなった妻アウバニーの側を片時も離れようとせず、深い悲しみに暮れていた。アウバニーはタルボットの父プレスコットが、馬と引き換えに買い取ったインディアンと白人の混血児だった。プレスコットは息子の心の慰めにと、今度はアウバニーの妹を連れ去る。そんな彼らを、激しい怒りに満ちたアウバニーの癒されぬ魂が、幽霊となって苦しめるのだった……。亡くなる前年に出演したR・フェニックスが、亡妻の死に心を病む青年を迫真の演技で熱演。第６回東京国際映画祭での上映時タイトルは「サイレント・タン」。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("馬", "馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "いねは、東北の片田舎に生きる農家の娘。この映画は、そんな彼女が新しく生まれた仔馬をひたむきな愛情で育て上げ、立派な軍馬として世に送り出していくまでを描いた写実的作品である。撮影は１年がかりの長期ロケで行われ、春・夏・秋・冬とそれぞれ別のカメラマンが担当。少女と馬の厳しくかつ優しい交流を四季折々の美しい風景や風俗と絡めながら描き出していく。ちなみに夏のパートを担当したのは、山中貞雄の「人情紙風船」できらめく才能を発揮した三村明。動物映画のはしりともいえる作品ではあるが、当時の山本嘉次郎は“エノケン”ものなど、ミュージカル・コメディ路線の作品を中心に演出。本作は、当時のそうしたライトな路線を逸脱した渾身の力作であり、原案・脚本・演出のすべてを一人で担当。なお製作主任は、当時山本の愛弟子として行動をともにしていた黒澤明である。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THUMB　TANIC", "親指タイタニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "おなじみのヒット作をオール“親指”キャストで再現したパロディー作品。豪華客船の沈没事故で散った愛の悲劇が、一転して喜劇と化す。親指セリーヌ・ディオンも登場して熱唱を披露してしまうほどのバカバカしいギャグ・センスが魅力。「ナッシング・トゥ・ルーズ」のS・オーデカークが演出を手掛けている。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばりの山を守る兄弟", "ひばりの山を守る兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "美空ひばりが、男装の麗人に扮した、大佛次郎原作の時代劇。甲州の山岳地帯。そこにある台ヶ原と呼ぶ一帯で、豪族の玉置家と角倉家が争っていた。角倉家では、主人の病気をよいことに黒川銃次郎が勢力を伸ばし、玉置家の伊織、大三郎兄弟を亡き者にしようとする。玉置兄弟は、その謀略に感づいて……。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("闇金ウシジマくん", "闇金ウシジマくん", "", "", "http://image.eiga.k-img.com/images/movie/56996/315x210.jpg?3835", 0, "闇金融の世界をリアルに描いた真鍋昌平の同名人気漫画を、山田孝之主演で実写化したドラマの劇場版。10日で5割という違法な金利で金を貸し付ける闇金業者・丑嶋馨が、弱肉強食の社会の底辺で力強く生き抜く姿を描く。借金の回収のためにセレブたちのホームパーティに訪れた丑嶋は、イベント系サークル代表のジュンと出会う。数日後、丑嶋の経営する「カウカウ・ファイナンス」に現れたジュンは、イベントの資金調達のための借金を懇願するが……。映画版のヒロインで、母親の借金を肩代わりしたことで受け追い詰められていく女性・鈴木未來を「AKB48」の大島優子が演じる。", "2012-08-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古墳ギャルのコフィー　桶狭間の戦い", "古墳ギャルのコフィー　桶狭間の戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「菅井君と家族石」「秘密結社　鷹の爪」などで知られるFROGMANと蛙男商会が手がけた短編FLASHアニメで、さまざまな古墳たちが通う学園を舞台に繰り広げられるナンセンスコメディ「古墳ギャルのコフィー」の劇場版。前方後円墳の女子高生コフィーと幼なじみの四隅突出型墳丘墓のダニエルは、学校の裏にある開かずの部屋に入り戦国時代にタイムスリップしてしまう。そこで、コフィーたちの担任で落ち武者の桶狭間先生の意外な過去が明らかになり……。「秘密結社 鷹の爪 THE MOVIE 総統は二度死ぬ」と同時上映。", "2007-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TORCH SONG TRILOGY TOECH", "トーチソング・トリロジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "まだまだゲイがハリウッド映画でタブー視されるなかで、男性同士の恋愛関係を自然に描いた秀作。もともとは主人公アーノルド役のH・フィアスタインが脚本を書き、オフ・ブロードウェイで上演していた芝居の３部作を一つにまとめたもの。題名は“三つの悲恋歌”の意味である。アーノルドは、ナイト・クラブで働く女装のエンターテイナーだ。もちろんゲイなのだが、アーノルドももういいかげんな年、一夜の関係よりもわかり合えるパートナーがほしい。ある夜、彼はハンサムな高校教師エドと出会い、深い仲になる。だが、アーノルドがいくら尽くしても、どうもエドは彼のことを恥ずかしいと思っている様子。おまけにエドはバイ・セクシュアルで、女の方の恋人と結婚してしまう。それから数年後、アーノルドは若くてハンサムなアランに出会う。優しくて理解のあるアランとの幸せな日々、二人は養子をもらうことまで相談していたが……。A・バンクロフトがユダヤ人の母親を見事に演じている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社長紳士録", "社長紳士録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "大正製袋の新社長・小泉礼太郎は、鹿児島で契約破棄との知らせを受け、九州へ飛ぶ。南国澱粉との交渉だが、ライバルの赤羽製袋も同じことを狙っているのだ。そんな中、社長は芸者とホテルへしけ込もうとするが、部下にばったり出くわして……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELSTREE CALLING", "エルストリー・コーリング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "A・ヒッチコックをはじめ４人の監督による共作で、ヒッチコック自身はワン・シークエンスを担当しているだけ。TVが珍しかった頃、映らなくなったTVと悪戦苦闘するオヤジ（この部分がヒッチコック）と、テレビ中継されているミュージック・スタジオの模様を同時に描いていく。", "1930-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELISO", "エリソー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "帝政ロシアを糾弾する、スターリン体制初期の啓蒙映画。19世紀末のグルジア。トルコとの国境に近い山岳地帯に住む少数民族が、コサックの策謀によって強制的に国外移住させられる悲劇を、村長の娘エリソーの恋に絡めて描く。「26人のコミッサール」の監督による貴重な無声映画。", "1928-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("べらんめえ芸者罷り通る", "べらんめえ芸者罷り通る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "美空ひばり主演“べらんめえ芸者”シリーズの第４作。折からのアフリカ・ブームで来日した、世界屈指のダイヤの産地コロンダ国の国王・アジバ６世に惚れられた売れっ子芸者の小春。だがダイヤ鉱山発掘の権利を虎視眈眈と狙う山中一味が小春を利用、外務省の裏をかいた周到な作戦を企てる。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAJOR LEAGUE", "メジャーリーグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "T・ベレンジャー、C・シーンという二大スターをあてて全米で大ヒットした野球映画。実在のチーム、インディアンズをモデルに作られたといわれるが、当事者たちが苦笑いしそうな珍プレイが続出してわかせる。チームの売却をもくろむ新オーナーの策略で落ちこぼればかりが集められたインディアンズ。当然、まともなチームには勝てるはずもないが、そんなこととは知らないプレーヤーたちは優勝を目指して一致団結する。ノーコン・ピッチャーを演じるC・シーンの３枚目ぶりなど、意外な演技が楽しい。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLESH AND BONE", "フレッシュ・アンド・ボーン 渇いた愛のゆくえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "当時夫婦だったM・ライアンとD・クエイドの共演で描いた辛口の恋愛劇。父親が、とある農夫の一家を惨殺する犯行現場を見てしまった少年アーリス。30年後、心の傷が癒えない彼は酔いどれの女性ケイと出会い、恋に落ちる。だが、アーリスの父ロイが彼らの前に出現したことによって、ケイの意外な過去が明らかになる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("柳生旅ごよみ女難一刀流", "柳生旅ごよみ女難一刀流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "懐中無一文で気ままな武芸旅を続ける柳生十兵衛は、ひょんなことから羽鳥城に乗り込むことに。城主・羽島弾正は明晩に控えた豊年祭に領内の処女20人を城中に留め、慰みものにする計画だという。十兵衛は城内に乗り込むが卑劣な水野刑部の短銃で谷に落ちてしまう。切れ味鋭い明朗快活な時代劇。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("又四郎喧嘩旅", "又四郎喧嘩旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "市川雷蔵が一人二役を演じ、とぼけた味わいも見せた時代劇。諏訪八万石では、多恵姫、琴姫の二人をめぐり、いずれを世継ぎの姫とするかで藩内が割れていた。婿として決まっている松平源三郎に会うため、江戸に向かった多恵姫は、琴姫一派に襲われる。そこを助けたのが、謎の浪人、笹井又四郎で……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眼の壁", "眼の壁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "松本清張のベストセラーを映画化した社会派推理劇。奥湯河原山中で、昭和電業の会計課長・関野の自殺死体が見つかる。残された遺書からパクリ屋一味の詐欺にかかり、手形を奪われた責任をとったものだと判明。部下の萩崎は、パクリ屋一味の足どりを追う……。アッと驚く展開が待ち受けている。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("侍戦隊シンケンジャー　銀幕版　天下分け目の戦", "侍戦隊シンケンジャー　銀幕版　天下分け目の戦", "", "", "http://image.eiga.k-img.com/images/movie/54396/315x210.jpg?1639", 0, "外道衆に立ち向かう5人の侍たちの活躍を描くスーパー戦隊シリーズ「侍戦隊シンケンジャー」の劇場版。脂目マンプク率いるクサレ外道衆の猛攻を受け、敗北寸前に陥ったシンケンジャー。やがて彼らは、シンケンレッドの祖先である志葉烈堂がかつて脂目マンプクを封印するために使った初代秘伝ディスクの存在を知る。しかし、現在そのディスクは敵陣真っ只中に建つ神社にあり……。日本映画初のフルデジタル3D実写作品としても話題を呼ぶ。", "2009-08-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美女放浪記", "美女放浪記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "男まさりの“みにくいあひるの子”・生駒ひらめを主人公にした“美女”ものの第３作。例によって美しい姉妹たちから疎外され、恋人にもふられたと早合点したひらめが、“自殺してやる”とばかりに旅に出た東北での珍騒動を描く。美女役・研ナオコが絶好調。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Missile", "ミサイル", "", "", "http://image.eiga.k-img.com/images/movie/57306/315x210.jpg?4619", 115, "1967年のデビュー以降、現代アメリカ社会を独自の視点で見つめ続けてきたドキュメンタリー作家フレデリック・ワイズマン監督が、カリフォルニア州バンデンバーグの空軍基地にカメラを入れ、空軍将校の訓練に密着。核戦争に対する議論やミサイルの装備・発射、暗号や通信、テロ防衛対策の模様などが映し出されていく。2011年、ワイズマン監督作を一挙上映する「フレデリック・ワイズマンのすべて」にて日本初公開。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rise: Blood Hunter", "ブラッド", "", "", "http://image.eiga.k-img.com/images/movie/34116/315x210.jpg?1454", 98, "「チャーリーズ・エンジェル」シリーズのルーシー・リュー主演によるバンパイア・アクション。ジャーナリストのセイディーは、あるカルト教団の取材中に襲われ、バンパイアたちのアジトに連れ去られる。そこで惨殺されたはずの彼女だったが、やがて目を覚まし、自分もバンパイアになってしまったことに気付く。セイディーは彼らに復讐するべく戦いを挑むが……。監督は「スネーク・フライト」の脚本を手掛けたセバスチャン・グティエレス。", "2007-08-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SERGEANT RYKER", "裏切り鬼軍曹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "朝鮮半島の38度線。南北に分かれ陰惨な戦闘が繰り広げられているなかを、ライカー軍曹は上官の命令で敵地に潜入していた。彼は共産軍側から米軍基地へと戦火を突破して戻るが上官はすでに死亡しており、反逆者として逮捕されてしまう……。戦場での不可解な事件をめぐって展開する軍事サスペンス。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN OF THE KILLER TOMATOES", "リターン・オブ・ザ・キラートマト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "殺人トマトの恐怖を描いたカルト作品「アタック・オブ・ザ・キラートマト」の続編。人々の心に恐ろしい記憶を残した巨大トマトとの戦争から25年が経った。だが、あの恐怖を再びと、ひそかに研究に精を出している科学者がいた。彼は、トマトを人間の男女に変えてしまう変換機を発明し、計画を進めようとする。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロッカーズ", "ロッカーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "高速チューンで鳴らした伝説のロックバンド、THE ROCKERSのデビューまでの道のりを描いた青春劇。今や俳優として知られる同バンドのボーカリスト、陣内孝則が初監督に挑み、悪戦苦闘していたころの姿をコミカルにとらえていく。メンバーに扮する中村俊介や岡田義徳、塚本高史らの好演に加え、エネルギッシュなライブ・シーンも光った。博多のアマチュア・バンド、ロッカーズはサエない４人組。そんなバンドも名ギタリスト、タニの加入で活気づき、次第に地元で人気を高めていく。やがてタニの失明の危機が判明し、ボーカルのジンは苦渋の選択を迫られる。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAKAMINE アメリカに桜を咲かせた男", "TAKAMINE アメリカに桜を咲かせた男", "", "", "http://image.eiga.k-img.com/images/movie/56346/315x210.jpg?3662", 105, "米ワシントンD.C.のポトマック川沿いに桜並木を植え、日米の懸け橋となるべく奔走した化学者・高峰譲吉の半生を描いた伝記ドラマ。若くして渡米した高峰は、タカヂアスターゼの発見や止血剤アドレナリンの結晶化などで成功を収める。しかし1904年、日本はロシアとの戦争に突入。米国内では日本が敗戦するとの声が多く、高峰は複雑な心境にあった。そんな折、アメリカに桜を咲かせたいと願う旅行家兼文筆家のE・R・シドモアと知り合った高峰は、彼女の夢を実現させるべく日本から2000本の桜の苗木を取り寄せるが……。", "2011-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホストタウン／エイブル２", "ホストタウン／エイブル２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "知的障害を持つ選手による世界最大のスポーツイベント“スペシャルオリンピックス”を取り上げたドキュメンタリーの第２作。2003年にアイルランドの首都ダブリンで開催された第11回大会を舞台に繰り広げられる、人々の交流、人間の機知と勇気、そして限りない可能性を温かい眼差しで見つめていく。日本人アスリートを受け入れるホストタウンとなった、ダブリン郊外のニューブリッジ。そこには知的障害を持った少女エイミーが暮らしていた。交流を通じて親しくなった選手たちの応援に出かけ、一瞬一瞬に心から喜びを感じる彼女の姿は、“普通に生きる”ことへの強い感動と、明日への希望に満ちた活力を与えてくれる。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の警察", "女の警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "銀座にバーやキャバレーを持つ暁興業の人事兼保安部長・篝は、ホステスたちの信望も厚く“女の警察”とも呼ばれていた。不可解な自動車事故を起こして死んだ元ホステスの夫・玖島がひそかに取材していた事件に、彼も巻き込まれていく。全編を青江三奈の歌が彩る。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("洗濯機は俺にまかせろ", "洗濯機は俺にまかせろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "下町商店街の中古電器店で働く青年の恋と成長を描いた、しみじみと心温まる一編。実直に、そしてマイペースで生きる主人公、バツイチで奔放な店の娘、彼女が想いを寄せる中年タクシー運転手の三角関係が、穏やかなタッチで綴られる。３人に扮した筒井道隆、富田靖子、小林薫のアンサンブルも心地よく、洗濯機修理が得意という青年の生活描写も丹念。人物関係やサブ・キャラクターの個性など、監督、篠原哲雄の次作「きみのためにできること」との関連部分も多く、見比べてみるのも一興だ。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIRTUOSITY", "バーチュオシティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "仮想現実の世界から逃亡した犯罪者と、その犯罪者を追う捜査官の戦いを描いたSFスリラー。1999年のロサンゼルス。LETEC(警察技術研究所)は、バーチャル・リアリティーで警察官の犯人追跡技術を訓練するシュミレーターを開発した。だが、過去の犯罪データを合成して作られた仮想の犯罪者“シド6.7”が、サイバースペースから現実世界へと逃亡。“シド6.7”と訓練で唯一互角に戦った元警官で囚人のパーカーは、自由の身になることを条件に追跡を開始する。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MON HOMME", "私の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "自分の生き方や仕事にも満足している娼婦マリーは、ある日、街角でホームレスの男ジャノンと出会う。彼に興味を持ったマリーは、ジャノンに寝る場所と食事を与え、やがて二人は愛し合うようになる。彼を自分の男にする決意をしたマリーはジャノンに愛情を注いでいくが、そんな彼女とは反対にジャノンは他に女を作り、マリーのヒモへと変わっていく。ホームレスの男にほんろうされる娼婦の姿を描いた愛のドラマ。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wisdom of Crocodiles", "クロコダイルの涙", "", "", "http://image.eiga.k-img.com/images/movie/1346/315x210.jpg?2929", 95, "美貌の医師スティーブンは、ハンサムで聡明で寛大で、成功した男。しかし彼は孤独だった。自分を愛してくれる女の血を吸わないと死んでしまうのだ。彼は次から次へと女を誘惑しては完全犯罪を重ねていた。ある日、彼の新たな犠牲者マリアが漁船に引き上げられる。しかし、積極的に捜査へ協力するスティーブンを、警察もクロとは思いかねていた。そして、スティーブンの目の前に知的で情熱的な女性、アンが現れる。", "2000-11-18", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鯨捕りの海", "鯨捕りの海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "和歌山県太地町の漁港を拠点に、日本近海で操業する沿岸小型捕鯨船、第三十一純友丸。この船に乗り込んだ６人の船員の日常を６ヵ月に渡って密着取材し、そのチームワークも鮮やかな仕事ぶりと日本の捕鯨の現況を収めたドキュメンタリー。欧州の捕鯨国ノルウェーの現状も記録されている。監督は東陽一作品の助監督を務めてきた梅川俊明。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dear Friends ディアフレンズ", "Dear Friends ディアフレンズ", "", "", "http://image.eiga.k-img.com/images/movie/33836/315x210.jpg?2819", 115, "「Deep Love」が女子中高生の間で大ブームを巻き起こしたベストセラー作家Yoshiの同名小説を映画化。“友達なんか必要ない。必要なときにだけ利用するもの”と考える女子高生リナ。そんな彼女を、突然の病魔が襲う。絶望し自ら命を絶とうとする彼女の前に、小学校時代の同級生マキが現われ……。主演は「ワイルドスピードX3／TOKYO DRIFT」の北川景子と、「スウィングガールズ」の本仮屋ユイカ。", "2007-02-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KOMITAS", "コミタス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "高名な修道士であり、アルメニア音楽の巨匠であったコミタスの生涯を、同じアルメニア出身のD・アスカリアン監督が西ドイツ亡命中に描いた作品。修道士として教会合唱隊の指揮者を務めていたコミタスは、その才能を認められ、ドイツへの留学などを経て音楽と美学の学位を取得する。故郷に戻った彼は、教会合唱隊を率いながらアルメニア音楽の研究に励み、民謡の収集や創作活動などで民族の音楽に多大な功績を残した。しかし1915年、トルコ政府による200万人にものぼるアルメニア人の大虐殺が、コミタスの生活と精神に大きな傷を残し、国を追われた彼は人生最後の20年間を精神病院で過ごすことになる。", "1988-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUNFIGHT AT COMANCHE CREEK", "連邦保安官", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1875年のコロラド。強盗団の横行に業を煮やした秘密探偵局は、ギフォードを無法者に仕立てて潜入捜査を開始する。彼は、強盗団の手先として活躍を始めるが、その懸賞金が上がったことから強盗団の中に裏切りが生じ、危険を感じた彼は保安官に救いを求めるが……。F・マクドナルドが演出した傑作西部劇。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("筆子・その愛　天使のピアノ", "筆子・その愛　天使のピアノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "明治時代、夫と共に日本初の知的障害児教育を実践した石井筆子の半生を常盤貴子主演で映画化。幕末に長崎県大村藩士の娘として生まれた筆子。成人後は華族女学校で教鞭をとるかたわら津田梅子らと女性の地位向上を目指し動き出す彼女だったが、知的障害を持つ長女を障害児教育施設・滝乃川学園に預けたことから、園長の石井亮一と運命の出会いを果たす。監督は「石井のおとうさんありがとう」で“児童福祉の父”石井十次を描いた山田火砂子。", "2007-01-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ne Touchez Pas La Hache", "ランジェ公爵夫人", "", "", "http://image.eiga.k-img.com/images/movie/53416/315x210.jpg?1250", 137, "19世紀初頭。パリ社交界の華ランジェ公爵夫人は、舞踏会で出会ったモンリボー将軍に興味を持つ。まるでゲームを楽しむように思わせぶりな態度で将軍を翻弄する公爵夫人だったが、ある事件をきっかけに本気で彼を愛しはじめてしまい……。フランスの巨匠ジャック・リベット監督が、文豪オノレ・ド・バルザックの同名小説を映画化。主演は「恋ごころ」のジャンヌ・バリバールと「ポーラX」のギョーム・ドパルデュー。", "2008-04-05", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEN OF BOY'S TOWN", "感激の町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "'30年の少年もの「スキピイ」でアカデミー監督賞を受賞したN・タウログが、「少年の町」の続編として撮り上げたお得意の少年映画の名作。30年代にアカデミー主演男優賞を2度受賞した名優トレーシーも、人情味溢れる演技で魅了する。前作に引き続き、フラナガン神父と少年たちの物語が孤児院を舞台に展開。少年刑務所で看守を殺した少年を神父が裁判所から連れ帰ってきたが……。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Malena", "マレーナ", "", "", "http://image.eiga.k-img.com/images/movie/1516/315x210.jpg?3079", 92, "1940年、第二次大戦下のイタリア。12歳の少年レナートは、村で一番美しい女マレーナに一目惚れする。結婚したばかりの夫を徴兵された彼女は、海岸沿いの家に1人で住んでいた。戦地にいる夫を想い、目の見えぬ父親の世話をする彼女を影のように追うレナート。やがて、敗戦とともにマレーナの夫の戦死が伝えられ、彼女にとっての悲劇が幕を開ける。長ズボンをはくようになったレナートは、そのすべてを胸張り裂ける想いで見つめ続けていた。", "2001-06-09", "4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("哭きの竜", "哭きの竜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "人気麻雀劇画の映画化。広域暴力団・桜道会の若頭、甲斐正三は“哭きの竜”を探し求めていた。竜は神がかり的な運の強さと不敗の伝説を持つ雀士。他組織との抗争を控えた甲斐は、その魔性の強運を求めていたのだ。しかし竜を縛ることはだれにもできない。やがて抗争の火ぶたは切られ、戦いの渦は竜を慕う夏枝やテツまでもを巻き込んでいく。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAY OF THE WARRIOR", "ピカソ・トリガー　デイ・オブ・ザ・ウォリアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "『プレイメイト』『ペントハウス』のグラビアを飾った美女たちが活躍するセクシー・アクション・シリーズの一編。屈強な美女ばかりで構成された政府直属の極秘戦闘部隊“リーサル・レディーズ”。彼女たちは、元CIAエージェントのウォリアーが組織する犯罪ネットワークを壊滅させるため、潜入捜査を進めていた。だが、ウォリアーがリーサル・レディーズのデータを入手。彼女たちはたちまちピンチに陥る。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おやすみアンモナイト", "おやすみアンモナイト", "", "", "http://image.eiga.k-img.com/images/movie/55126/315x210.jpg?3133", 100, "東京・高円寺にあるリサイクルショップ「素人の乱5号店」の経営者・松本哉と、彼を取り巻く若者をたちを描いた青春群像劇。主演は辻岡正人、疋田紗也。経済的に窮地に立たされている劇団員の成子は、六本木で夜の仕事を探すことにする。一方、破天荒な男・登は、友人たちと高円寺の街を盛り上げようと、過激なデモ運動を計画していた。", "2010-01-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("流れる雲よ", "流れる雲よ", "", "", "http://image.eiga.k-img.com/images/movie/56816/315x210.jpg?3779", 115, "脚本家の奈美木映里（ペンネーム草部文子）によるラジオドラマ「ひこうき雲」を演劇集団アトリエッジが舞台化した「流れる雲よ」を、“演劇シネマ”として映画館のスクリーンで上映。太平洋戦争下の昭和20年夏、戦況が刻々と悪化するある日、鹿児島の航空隊に所属する光太郎と正人は、ラジオから未来の放送が流れてくるのを聞く。2人は、昭和20年8月15日に日本が負けて戦争は終わるということを知るが、そんな矢先、光太郎にも特攻の命令が下り……。", "2011-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Syrian Bride", "シリアの花嫁", "", "", "http://image.eiga.k-img.com/images/movie/54046/315x210.jpg?3252", 97, "67年の第3次中東戦争以降、イスラエルの占領下となった中東・ゴラン高原。“無国籍者”の境遇にあるイスラムの少数派とされるドゥルーズ派の花嫁モナが祖国のシリア側へ嫁いでいく。1度境界線を越えたら2度と家族のもとへは帰れない複雑な中東情勢を背景に、揺れ動く家族の姿が描かれる。監督は「Cup Final」「Lemon Tree」のエラン・リクリス。04年のモントリオール世界映画祭ではグランプリを受賞した。", "2009-02-21", "2,7,46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("皇室と戦争とわが民族", "皇室と戦争とわが民族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "神武天皇の東征から始まり、昭和12年の蘆溝橋事件による日中戦争、太平洋戦争、敗戦、米軍占領、独立、皇太子の結婚、親王殿下の誕生まで、日本と皇室との運命を描いた新東宝お得意の戦争・歴史大作。神武天皇を演じる嵐寛寿郎を筆頭に、新東宝おなじみの男優たちが総登場している。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林三十六房／SHAOLIN　TEMPLE　THE　３６　CHAMBER", "少林寺三十六房", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "武術アクター、リュー・チャーフィの代表作。清朝時代、家族を殺された青年リューは復讐を決意し、少林寺の門を叩く。厳しい修業を経て、２年で少林寺の三十五房での試練を突破し、武道の真髄な目覚めた彼は、やがて三十六番目の房の創設を志し……。武術家ラウ・カーリョンの演出は武道の精神性をも捉えており、欧米でも熱烈な支持を受けている。", "1977-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREEWAY2 CONFESSIONS OF A TRICKBABY", "トリック・ベイビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "凶暴な少女コンビの逃避行を、ブラックユーモアを交えて描いた犯罪劇。前科38犯の強盗犯クリスタルと連続殺人犯のアンジェラ。療養施設で出会い意気投合した彼女たちは脱走し、アンジェラが崇拝するカルト教祖に助けを求めてメキシコへ。ところが、そこで教祖の意外な素顔が明かされ……。謎めいた教祖に扮する個性派V・ギャロの怪演も見もの。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bunraku", "BUNRAKU ブンラク", "", "", "http://image.eiga.k-img.com/images/movie/57406/315x210.jpg?2627", 125, "日本の伝統芸能の文楽から着想を得て、漫画やTVゲーム、演劇、マカロニ・ウェスタンなどさまざまなジャンルの要素を盛り込んで描いたアクション。銃の使用が禁じられた世界を舞台に、ある目的を秘めた流れ者のドリフターと誇りと名誉を重んじる侍のヨシが出会い、街を牛耳る組織のボス・ニコラと、ニコラの側近で「キラー」と呼ばれる9人の達人たちと死闘を繰り広げる姿を描く。ドリフター役でジョシュ・ハートネットが主演。歌手で俳優のGACKTが準主役のヨシを演じ、自身初の海外作品への出演を果たした。共演にウッディ・ハレルソン、ロン・パールマン、デミ・ムーアら。", "2012-01-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FA¨NGELSE", "牢獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "“経費を低く抑えられるなら、好きなように撮っていい”とプロデューサーに言われたベルイマンがヒッチコックの「ロープ」にならってカット数を減らし、その結果独特な長回しの手法のヒントともなった作品。それまで商業監督としてメロドラマなどを手掛けていたベルイマンが“作家”としてのスタイルを確立した手掛かりとなったともいえ、後のベルイマン映画に繰り返し登場する木馬や劇中劇などのモチーフや愛の崩壊というテーマなどがはっきりとした形で表われている。売春婦ビルギッタが経験していく一種幻想的な“地上の地獄”を描きながら神について問いかけ、賛否が激しく分かれた異色作。", "1949-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hella W", "マダム・ヘッラ", "", "", "http://image.eiga.k-img.com/images/movie/57256/315x210.jpg?4072", 82, "1920～40年代のフィンランドで、劇作家・実業家・活動家など多方面で活躍した女性ヘッラ・ブオリヨキの波乱の半生を描いた歴史ドラマ。実業家として成功を収めていたヘッラは、大恐慌の煽りを受けて資産を失い、再び劇作家として活動。ソ連とフィンランドの間で起こった冬戦争では、幅広い交友関係をいかして両国の和平にも貢献したが、その現実とは裏腹に、フィンランド当局から国家反逆罪の疑いをかけられてしまう。最新のフィンランド映画5作品を一挙上映する「フィンランド映画祭2011」にて上映。", "2011-10-02", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUTO　FOCUS", "ボブ・クレイン／　快楽を知ったTVスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "一度は頂点を極めるも、ヒット作に恵まれず、最後は他殺死体となって発見されたアメリカのTVスター、ボブ・クレイン。社会派作品に定評のあるポール・シュレイダーが、彼の半生をウィットに富んだ切り口と鋭い視点で描き出した。クレインの異常なまでの性癖を通して浮かび上がる1960～70年代のアメリカ・ショービジネス界の裏側がユニークだ。TVコメディ『0012捕虜収容所』の主役でお茶の間のスターとなったボブ。急に周りからチヤホヤされ始めた彼はスタジオの出入り業者、ジョンと組み、女の子を引っ掛けては乱交を続ける。だがそのことが妻にバレてしまい……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HENNESSY", "怒りの日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "紛争が絶えないベルファストで、市街戦の巻き添えをくって妻子を亡くした元IRAのヘネシーは、復讐のためイギリスへ向かう。国会開会式の当日に、エリザベス女王ら政府要人を爆死させようとするが……。ビデオ発売時タイトルは「黒の復讐」。", "1975-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギャング同盟", "ギャング同盟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "深作欣二監督が、シリアスなハートボイルド調に徹して撮り上げたギャング映画。刑務所帰りのやくざ者、風間は、かつての縄張りを奪われたことから、組織の大物を誘拐して身代金をせしめようと画策。かつての仲間を集めて計画を遂行し、身代金を要求する。だが、風間らのアジトは、たちまち一味の殺し屋たちに包囲され、壮絶な銃撃戦が起こる。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUONGIORNO，NOTTE", "夜よ、こんにちは", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "イタリアの巨匠監督マルコ・ベロッキオが、'78年に起きたアルド・モロ元首相誘拐殺害事件を、犯人グループの女性の視点から描いた社会派ドラマ。フィクションを織り交ぜながら、人間の内面からテロリストの矛盾と葛藤に迫った力作だ。過激派組織“赤い旅団”のキアラは、モロ元首相暗殺計画に参加し、モロの身柄を匿う役割を担っていた。ところがモロの高潔な人柄に触れるうちに、自分たちの大義を疑い始める……。", "2006-04-29", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("相棒シリーズ　鑑識・米沢守の事件簿", "相棒シリーズ　鑑識・米沢守の事件簿", "", "", "http://image.eiga.k-img.com/images/movie/53866/315x210.jpg?4204", 105, "人気シリーズ「相棒」に登場する鑑識官・米沢守を主人公にしたスピンオフ。監督は「あぶない刑事」の長谷部安春、原作は監督の息子ハセベバクシンオー。米沢は別れた妻の死亡を伝えられるが、死体は同姓同名の別人で、所轄の刑事・相原の元妻であった。自殺という死因の断定に納得のいかない米沢と相原は、協力して彼女の死の真相を探るが……。シリーズ初登場の萩原聖人が米沢の相棒役を務めるほか、本家「相棒」の水谷豊、寺脇康文も出演。", "2009-03-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NAKED CELL", "肉体の魔性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "イギリスの人里離れた田園地帯に送り込まれた無実の女囚が、そこでOL時代の赤裸々な体験を回想していくうちに現実と妄想が交錯し、性の奈落へと堕ちていく姿を描く実話の映画化。やがて彼女は恐るべき女監視人のもとで、人間性を奪われていく。主演は1000人の中から選ばれたV・ジェフリー。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トワノクオン　第五章　双絶の来復", "トワノクオン　第五章　双絶の来復", "", "", "http://image.eiga.k-img.com/images/movie/57116/315x210.jpg?4817", 0, "「機動戦士ガンダム　第08MS小隊」などで知られ、2010年に他界した飯田馬之介監督が生前に制作を進めていた全6話のオリジナルアニメーションを映画館で上映する第5話。ファンタジアム・ガーデンから脱出したイプシロンとベスティア（能力者）たちを追って、サイボーグ部隊が出動する。そして、能力者たちの集団「アトラクター」の中心人物だったクオンの行方が知れないなか、思わぬ真実が明らかになる。", "2011-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GABBEH", "ギャベ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "イランの遊牧民カシュガイ族の女たちが織る素朴な絨毯（ギャベ）に、一人の娘ギャベの恋模様を重ね合わせて描いた、色鮮やかな美しき幻想譚。A・キアロスタミと並ぶイラン映画界の二大巨匠として人々に愛され、最大の動員力を持つM・マフマルバフ監督による、ファンタスティックな野心作である。", "1996-01-01", "15,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA NOTE BLEUE", "ソフィー・マルソーの愛人日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "鬼才A・ズラウスキーによる異色の人間ドラマ。ピアノの詩人ショパンとその愛人ジョルジュ・サンド、そしてショパンに惹かれる彼女の娘の関係を描く。S・マルソーは、母親の愛人を奪おうとする娘ソランジュを情熱的に演じる。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本沈没（2006）", "日本沈没（2006）", "", "", "http://image.eiga.k-img.com/images/movie/1566/315x210.jpg?4049", 135, "73年にもオールスターキャストで映画化された小松左京によるSF小説を「ローレライ」（05）で長編劇場映画デビューを果たした樋口真嗣監督がリメイク。日本海溝直下の大規模な地殻変動により日本列島の大部分が海中に沈むという予測が発表され、その発表を前後してマグニチュード8クラスの大地震が連続発生する……。", "2006-07-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちんじゃらじゃら物語", "ちんじゃらじゃら物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "パチンコの機械を扱わせたら日本一という男が、その底抜けの人の好さから女房に逃げられたり、名古屋の市長選挙に立候補させられたりと、てんやわんやの人情喜劇。パチンコ屋50軒の首をくくらせたというパチプロ“なだれのサブ”と主人公・太助の丁々発止の戦いが見もの。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KID", "キッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "イメージ・コンサルタントとして成功を収めているラスのもとに、８歳のころの自分と名乗る少年が現れた。少年を消すために様々な方策を練るうちに、自分は子供のころの夢を何一つ叶えていないことに気付き……。忘れていた過去との出会いによって、今の自分を再発見するファンタスティックな感動ドラマ。映画初出演のS・ブレスリンが、B・ウィリスに一歩もひけをとらない演技を披露している。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN SINGE EN HIVER", "冬の猿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ノルマンディー地方の寒村を舞台に、人生の冬を迎えた男と若く奔放な男との心のふれあいを、センスのよいユーモアを交え、詩情豊かに描き上げた珠玉の名作。「ヘッドライト」「地下室のメロディー」など数多くの名画を生んだH・ヴェルヌイユ監督の最高傑作といわれる作品。なぜか日本では長い間公開されないでいたが、1996年に初公開された。フランス映画界の大御所J・ギャバンと、当時ヌーヴェル・ヴァーグの代表的スターとして売り出し中だったJ・P・ベルモンドが共演した唯一の作品であることも、フランス映画史上に残る価値を持っている。名手L・パージュの撮影、M・マーニュの音楽も絶品。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ERASER", "イレイザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "重要事件の証人を守るため、彼らの過去を消し去る“イレイザー”が、新兵器密売計画を知った女性を黒幕から守るために巨大組織と戦うアクション。大手武器製造会社の社員リー・カレンは偶然、最新鋭のスーパー兵器の密売計画を知り告発。その計画には政府高官が絡み、リーにはこれまでにない危険が付きまとう。彼女の保護を命じられた政府特別情報局員、イレイザーのクルーガーだが、事件の背後には彼の上司の姿も見え隠れし、孤立無援の戦いを強いられる。上空１万メートル、時速400キロのジェット機からパラシュートなしでの脱出劇など、体当たりのアクションが満載。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hunted", "ハンテッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "　オレゴン州の山中で連続殺人事件が発生。犯人は、元・特殊部隊の精鋭として活躍したハラム。彼は優秀な兵士として功績を収めながらも、過酷な戦闘によって心を病み、殺人者と化してしまった。神出鬼没のハラムを捕まえるべく、かつて彼に戦闘術を教えた男L.T.が駆り出されることに。決死の追跡の果て、ハラムは逮捕されるが、その後、護送中に逃亡を図り……。サバイバルと殺人術を身につけた男たちが激突。アカデミー賞俳優ふたりが火花を散らす！", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("京化粧", "京化粧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "旦那と絵の勉強をしている三野村、さらに翻訳を生業とし、東京から遊びに来たという男・山岡との間で揺れる祇園の芸妓・園を主人公に、女心の深層を巧みに描き出したメロドラマ。大庭秀雄が斎藤良輔と共同で脚本を書き、自ら監督して哀愁漂う作品に仕上げた。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WESELE", "婚礼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "20世紀を目前にひかえた1900年。オーストリア領ガリツィア地方（現ポーランド）の旧都クラクフにある教会で若い詩人が結婚式を挙げた。やがて彼は同じように農民の娘を妻にした友人の画家の家へと披露宴を行うために向かう。そこにはクラクフのあらゆる階層の人々が集まり、一夜の乱痴気騒ぎの中で新郎新婦たちを祝福していた。夢ともうつつとも区別のつかぬ騒ぎの中に紛れ込んでくる歴史的人物の亡霊たち。館の主人は、亡霊の一人から蜂起の命令を出すことを指示される……。ポーランドの苛酷な近・現代史を婚礼の一夜に象徴的に浮かび上がらせるという構図が映画を貫く。A・ワイダ監督ならではの政治的問題作。", "1973-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Battle: Los Angeles", "世界侵略：ロサンゼルス決戦", "", "", "http://image.eiga.k-img.com/images/movie/55276/315x210.jpg?4352", 116, "1942年、米ロサンゼルス上空に未確認飛行物体（UFO）の編隊が出現し、空軍が対空砲火を行うも撃墜は失敗。それ以降、世界各国で不気味な円盤の目撃情報が相次ぐ。そして2011年、ついに宇宙からの地球侵略が開始される。人類最後の砦（とりで）となったロサンゼルスを舞台に、エイリアンの侵略に立ち向かう米海兵隊員たちの戦いを描くSFアクション。主演にアーロン・エッカート。「テキサス・チェーンソー　ビギニング」のジョナサン・リーベスマンがメガホンをとる。", "2011-09-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("衝撃天子門生／HONG KONG GODFATHER", "暗黒英雄伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "A・ラウが、お得意のスピーディーなアクションを披露するハードボイルド活劇。３大マフィアの一つ“洪興社”の首領の息子マークは、抗争を治めるという役目を負った。だが、彼を陥れようとする謎の一団が出現。これに警官隊が加わり、三つ巴の壮絶な戦いが繰り広げられる。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Back Street", "裏街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ファニー・ハーストによる同名小説を、「キング・ソロモン」のロバート・スティーブンソン監督が映像化。激しい恋に落ち、結婚を誓い合ったウォルターとレイ。しかしウォルターに一方的に思いを寄せる女性の妨害により、2人は離れ離れになってしまう。数年後、偶然ニューヨークで再会を果たした彼らは、再び愛し合うように。しかしウォルターは既に別の女性と結婚してしまっていた。レイは彼の地位を守るため、ある決断をする……。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD OR ALIVE FINAL", "DEAD OR ALIVE FINAL", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "哀川翔、竹内力、三池崇史監督による人気シリーズの第３作にして完結編。物語上のつながりはないが、前２作のダイジェスト的な映像も挿入され、大がかりなエンディングが用意されている。舞台は、2346年の横浜。独裁者ウーによって再建されたこの都市に現れた放浪者リョウは、やがて革命家たちに力を貸す存在に。ウーに服従する警官ホンダは、否応なくリョウと対決することになる。もちろん、ホンダとリョウの激突がクライマックスだが、実は両者ともにロボットという点がミソ。とりわけ、そのことを知らずに家族と一緒に暮らしているホンダのエピソードには「ア・ホーマンス」や「A.I.」を思わせる哀切さがある。全編香港ロケを敢行しており、無国籍ムードとともにどこかのんびりとしたリズムで映画は進行する。テーマ曲と呼ぶべきサックスによる『G線上のアリア』も印象的。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ten Commandments", "プリンス・オブ・アーク", "", "", "http://ecx.images-amazon.com/images/I/51XfwTQuUhL._SL210_.jpg", 89, "", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エンジェル・スノー", "エンジェル・スノー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "結婚６年目を迎えるソギュンとジヌォンに長い不妊治療の末、待望の子供が産まれることになった。ロマンティックな新居に引越し、喜びを噛み締める二人。だが医者から、お腹の子は不治の病で、産まれても一日の命だと宣告されてしまう。幸せの絶頂から絶望のどん底へ突き落とされた若夫婦の強い絆と愛情を綴るラブ・ストーリー。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATAL SKIES", "スカイ・ウォリアーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "実話に基づいた政治がらみの事件に、スカイ・ダイビングの醍醐味をミックスさせたアクション映画。登場人物が高校生ということで、単純明快な青春もの的要素も加味されている。スカイ・ダイビングの着地点を外してしまった４人の高校生。そこは産業廃棄物の違法処理現場だった。秘密を知った彼らを抹殺しようと何者かが迫ってくる。警察も買収されており、４人は自力で敵に向かっていく。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蜘蛛の糸", "蜘蛛の糸", "", "", "http://image.eiga.k-img.com/images/movie/57046/315x210.jpg?3926", 70, "「銀河鉄道の夜」「斜陽」など多数の日本文学を映画化してきた秋原正俊監督が、芥川龍之介の「蜘蛛の糸」を新たな解釈で映画化。現世で悪事を働き地獄（黄泉の国）に落ちたカンダタは、さまざまな人物に出会い翻弄される。黄泉の国では生前の行いによりそれぞれ異なる地獄が待ち受けており、とりわけ特異な地獄に落ちたカンダタが生前に行ってきた悪事の理由が明らかにされる。名優・平幹二朗が34年ぶりに映画主演。", "2011-09-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Robots", "ロボッツ", "", "", "http://image.eiga.k-img.com/images/movie/1156/315x210.jpg?3744", 90, "「アイス・エイジ」をヒットさせたクリス・ウェッジ監督とブルースカイ・スタジオが送り出すファンタジー冒険アニメーション。中古のパーツをつなぎあわせて生まれたロドニーは、発明家を夢見て大都会ロボット・シティにやってくるが、そこでは、大企業の経営者ビッグウェルドが、中古品で出来たロボットを排除しようと企んでいた……。ユアン・マクレガー、ハル・ベリーら豪華キャストが声優を務める。日本語吹替え版は草なぎ剛、矢田亜希子ほか。", "2005-07-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪獣大戦争", "怪獣大戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ゴジラ、ラドン、キングギドラが、超科学力で地球侵略を計るX星人の手でコントロールされ地球各地を暴れ回る。前作「三大怪獣　地球最大の決戦」で最大限の効果を上げた怪獣バトルロイヤルを宇宙規模まで拡大し、スケール・アップを狙った意欲作。", "1965-12-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("B　COMME　BEJART", "ベジャール、バレエ、リュミエール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "オリジナリティーあふれる試みで、バレエ界に革命をもたらしたと称賛される天才振付師、モーリス・ベジャールに迫るドキュメンタリー。新作舞台『リュミエール』の出来上がる過程をたどりながら、挑戦し続ける彼の素顔に迫りつつ、その創作の源を紐解いていく。まったく衰えることない彼のバレエに対する情熱と愛情には脱帽だ。2001年２月、ベジャール・バレエ団の新作『リュミエール』の準備が開始。振付師モーリス・ベジャールは会見でも意欲を語り、熱心にリハーサルに取り組む。やがてアクシデントに見舞われながらも、６月19日、ついに初演の幕が上がる。", "2002-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林寺拳法 ムサシ香港に現わる", "少林寺拳法 ムサシ香港に現わる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "少林寺拳法の達人・山中ムサシが拳法修行のためにやって来た香港で、事件に巻き込まれ大活躍する。全編香港ロケで主役以外は、現地の役者を使って作られた、松竹初の空手映画。風間健のアクションはなかなかだが、演技の方は今一つ。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バーバー吉野", "バーバー吉野", "", "", "http://ecx.images-amazon.com/images/I/51TB9PCNEDL._SL210_.jpg", 96, "ベルリン国際映画祭児童映画部門で特別推薦作に選ばれた新鋭、荻上直子の長編デビュー作。少年たちがみんな同じ髪型をしている田舎町。保守的な大人たちに対抗し、子供たちがやがて反乱を企てる。淡い初恋、性への興味、秘密基地……。そんな思春期の多感な日々を素朴なタッチで描出。大人への第一歩を踏み出す少年の成長を優しく見つめた青春ドラマだ。のどかな風景が広がる田舎町。少年たちは、町に一軒しかない床屋バーバー吉野のおばちゃんによって“吉野ガリ”にすることが習慣だ。ところが、東京からきた転校生が“髪型は自由だ”と言い出したため、騒動が巻き起こる。", "2004-04-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水戸黄門漫遊記・御用御用物語", "水戸黄門漫遊記・御用御用物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "福田晴一監督、伴淳三郎、花菱アチャコ主演という“二等兵物語”シリーズのトリオによる黄門漫遊記。水戸黄門には沢村国太郎が扮し、伴淳とアチャコは、捕手の淳八と亜茶吉に扮して、無念の死を遂げた同僚の仇を討つ。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIQUID DREAMS", "リキッド・ドリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ブロンドの美女イヴは、妹が部屋で殺されているのを発見する。犯人を見つけようと自ら乗り出したイヴは、妹の住んでいた閉鎖的なビルの異様な世界に足を踏み入れる。そこでは暴力とセックスが支配する奇妙な“儀式”が行われていた……。アブノーマルなSF世界を独特の映像感覚で描く近未来スリラー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Friday the 13th", "13日の金曜日（2009）", "", "", "http://image.eiga.k-img.com/images/movie/53936/315x210.jpg?3322", 95, "「テキサス・チェーンソー」のマイケル・ベイ製作＆マーカス・ニスペル監督が再タッグを組み、79年の名作「13日の金曜日」を新たな解釈で甦らせたホラー。行方不明の妹を探すためクリスタル・レイクを訪れたクレイ。地元警察や住民の警告にも耳を傾けず捜索を始めるクレイだったが、いつしか彼は殺人鬼ジェイソンが潜む領域に足を踏み入れてしまい……。主演はTVシリーズ「スーパーナチュラル」でブレイクしたジャレッド・パダレッキ。", "2009-02-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴッホ", "ゴッホ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "　19世紀後期、オランダ・アムステルダム。画家ビンセント・バン・ゴッホは貧困に苦しみながらも、独特のタッチで自分の世界を描いていた。そんな彼を支え続ける弟テオ。兄ビンセントはテオ宛ての手紙にあらゆることを綴る。一方、兄の存在は、テオの恋愛や結婚にも影響を及ぼす。ビンセントも弟に頼らざるを得ないことに苦悩する。その後、心を病んだビンセントは自らの耳を切り落としてしまうが……。画家ゴッホと弟テオの交流を描く伝記ドラマ。", "1990-01-01", "3,2,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRISONERS OF HONOR", "逆転無罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "1894年、ドイツに機密情報を流したかどでドレフュス大尉がスパイ行為・反逆罪に問われる。しかし、事件の背後には恐るべき陰謀が……。当時、フランス全土を震撼させた実話“ドレフュス事件”を、法廷サスペンス仕立てにしてK・ラッセルが映画化。隠れた謎に挑む軍人ピカール大佐をR・ドレイファスが重厚に演じている。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぱいかじ南海作戦", "ぱいかじ南海作戦", "", "", "http://image.eiga.k-img.com/images/movie/57556/315x210.jpg?3970", 0, "「現代版ロビンソン・クルーソー」とも評された椎名誠の同名小説を映画化。失業、離婚と悪いこと続きな佐々木は、人生をリセットするためやってきた西表島で4人組のホームレスに手持ちの財産を盗まれ、途方に暮れる。そんな佐々木を偉大なサバイバル生活者と勘違いした都会風な若者オッコチと、関西弁の女の子アパ、キミと4人で海辺の共同生活を始めた佐々木は、ある日、財産を奪って逃げたホームレスたちの噂を耳にし、リベンジのためにある作戦を練る。佐々木役の阿部サダヲを筆頭に、永山絢斗、貫地谷しほり、佐々木希と豪華キャストが集う。", "2012-08-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　法界坊", "シネマ歌舞伎　法界坊", "", "", "http://image.eiga.k-img.com/images/movie/55026/315x210.jpg?3239", 150, "歌舞伎の舞台公演をスクリーンでデジタル上映する「シネマ歌舞伎」シリーズ第13弾。金と女に目がない法界坊は、永楽屋のお組に恋をしていた。ある日、盗まれた吉田家の重宝「鯉魚の一軸」をお組の恋仲の要助が探していることを知った法界坊は、ある悪巧みを思いつき……。主演は中村勘三郎、共演に中村扇雀、中村橋之助、中村勘太郎、笹野高史ほか。", "2009-12-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Human Nature", "ヒューマンネイチュア", "", "", "http://image.eiga.k-img.com/images/movie/1436/315x210.jpg?3474", 96, "ビョークの「ヒューマン・ビヘイビア」などのクリップで知られるゴンドリー監督は、スパイク・ジョーンズとNYのチボマットのライブで出会い、カウフマンを紹介され、彼に見せられた脚本が気に入って本作が誕生。ちなみに、撮影のティム・モーリス＝ジョーンズはゴンドリーとはクリップで組んできた仲だが、ガイ・リッチー監督の「ロック、ストック＆トゥー・スモーキング・バレルズ」と「スナッチ」のカメラマンでもある。", "2002-03-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛のお荷物", "愛のお荷物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "受胎調整を訴える厚生大臣。しかし彼の一家は、その言葉とは裏腹に次々と子供を生んでいくという喜劇。アンドレ・ルッサンの『あかんぼ頌』をもとにしているが、原作権が得られず、風刺喜劇の鬼才・川島雄三の演出にも、他の作品にみられるようなキレが不足。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エイブル", "エイブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "知的障害者がアメリカにホームステイするという、鮮やかな視点によるドキュメンタリー。ダウン症の19歳、渡辺元と自閉症の17歳、高橋淳が、アリゾナ州のキャサリン＆マークのルビ夫妻のもとで３ヵ月間暮らす様を写し撮る。英語を解さず、言葉自体がほとんど出てこない二人を前に、文字通り人間そのものとして向き合い、つかみとるしかなくなる夫妻。その根気強く温かいまなざしに同化しながら、観る側も二人の少年の唯一無二のキャラクターを発見することになる。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談新耳袋・異形", "怪談新耳袋・異形", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD FOR DRACULA", "処女の生血", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「アンディ・ウォーホルのドラキュラ」としても知られる、アンディ・ウォーホルが監修として名を連ねるポップ・ホラー。3D版フランケンシュタイン映画「悪魔のはらわた」と併せて製作された。実際にはウォーホルは製作にタッチせず、P・モリセイに名前を貸した、いわば“ウォーホル・レーベル”の商業映画。怪優U・キアー演じる老残のドラキュラ伯が執事に引き立てられ処女を求めてカトリックの国イタリアへ行脚。しかし没落貴族の娘たちは下男とデキていて、その不純な血にドラキュラは……。ドラキュラを正統ホラーからはずし、悲劇の主人公にした異色作。", "1974-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガマの油", "ガマの油", "", "", "http://image.eiga.k-img.com/images/movie/53796/315x210.jpg?2911", 131, "1日に何億円も稼ぐデイトレーダーの拓郎は妻と息子の拓也と幸せな日々を送っていたが、ある日、拓也が交通事故により意識不明の重体に陥ってしまう。拓郎は事故を隠すため、拓也の恋人からの電話で拓也になりすましてしまい……。「バベル」「シルク」など国際的に活躍する俳優・役所広司が主演も務める初監督作品。瑛太、小林聡美、八千草薫、益岡徹ら豪華共演陣が揃う。", "2009-06-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American History X", "アメリカン・ヒストリーX", "", "", "http://image.eiga.k-img.com/images/movie/3846/315x210.jpg?4015", 120, "カリフォルニアの高校に通うダニーは、尊敬する兄デレクが刑務所を3年振りに出所する日、校長に呼び出される。「兄弟をテーマに作文を書け。タイトルは『アメリカン・ヒストリー X』……」デレクは地元のスキンヘッド（白人至上主義グループ）のリーダーとしてカリスマ的な存在だった。彼がその道に足を踏み入れたのは、父が黒人のドラッグ・ディーラーに殺されたことがきっかけだった。", "2000-02-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブース", "ブース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "若手監督＆俳優のコラボで新しいホラー映画を目指す“絶対恐怖”シリーズの第２弾は、深夜ラジオの人気DJが生放送中に体験する恐怖を描いたサスペンス・ホラー。密室で次第に狂気に追い込まれていく主人公を、佐藤隆太が迫真の演技で体現している。深夜のラジオ番組の人気パーソナリティー、勝又真吾は、生放送中に“嘘つき”と囁く、正体不明の薄気味悪い女の声を聞くのだが……。", "2005-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秋雨　Autumnal Rain", "北京の恋　四郎探母", "", "", "http://image.eiga.k-img.com/images/movie/34296/315x210.jpg?1668", 98, "京劇を学ぶために中国を訪れた日本人女性と、京劇俳優の青年との恋を綴ったラブストーリー。「北京ヴァイオリン」のシュエ・シャオルーが脚本を手掛ける。かつて京劇の女形スターだったカの元に、京劇を習いたいという日本人女性シコが現われる。同じ日の夜、芝居から遠ざかっていたカの息子ミンが8年ぶりに戻って来る。稽古を通して次第に惹かれあっていくシコとミン。しかし2人の間には祖父の代の悲しい因縁があり……。", "2007-11-03", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん　のび太のねじ巻き都市冒険記", "ドラえもん　のび太のねじ巻き都市冒険記", "", "", "http://ecx.images-amazon.com/images/I/21E3PH7X3BL._SL210_.jpg", 98, "ドラえもんとのび太、その仲間たちが活躍する人気アニメ・シリーズの第18作。脚本を1996年に他界した藤子・F・不二雄が執筆している。ドラえもんは22世紀の商店街福引で小惑星をもらえる権利を獲得。喜んだのび太たちはさっそく、その星に行き、生命のネジを使ってオモチャに命を与え、町を作り始める。だが、脱走中の凶悪犯が現れたり、湖から金色の怪物が現れたりと大騒ぎの連続。やがて、その怪物と話したのび太は彼がこの星に命を与えた“種まく者”であることを知り、彼から星を楽園に変えるように言われるのだった。", "1997-03-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風雲天満動乱", "風雲天満動乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "天保８年、数年来続いた飢饉にあえぐ、摂河泉大阪三郷の住民は、為政者の非道な弾圧によって死の直前まで追い込まれていた。この圧政に、公正を尊ぶ陽明学を身をもって実践する男、大塩平八郎が立ち上がる。豊国観音教と名乗る邪教の存在や火あぶりの刑など、娯楽作品としての見せ場もたっぷり。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLD HEAVEN", "コールド・ヘブン 悪夢の再会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "N・ローグが、輪廻転生という特異なテーマに取り組んだ作品。超常現象をめぐる奇怪な物語がローグ独自の映像美によって映し出される。ローグ作品ではおなじみ、監督夫人でもあるT・ラッセルが、M・ハーモンと共演。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青島〈チンタオ〉アパートの夏", "青島〈チンタオ〉アパートの夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "デビュー作「黒砲事件」で痛烈な官僚批判を行ったホアン・チェンシン監督が、市場経済の導入で影響を受ける人々の奔走をユーモアたっぷりに描いた風刺コメディ。作家の高文とその妻が引っ越してきた青島のアパートでは、トラブルメーカーの張と党幹部の劉が激しく対立していた。やがて張が熱帯魚販売の事業で成功したことをきっかけに、高文は人騒がせな彼らの意地の張り合いに巻き込まれていく。", "1992-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КРЕЙЦЕРОВА　СОНАТА", "クロイツェル・ソナタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "トルストイの同名小説を原作に、男女の愛をめぐって人間の内に潜むエゴや残酷さを赤裸々に描く文芸大作。嫉妬のために妻を殺して服役していた男が、刑期を終えて故郷へ帰る道中、当時を回想しながら、退廃的な性道徳の批判と結婚生活の欺瞞を暴き出していく。監督は文芸映画の巨匠M・シュヴァイツェル。", "1987-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CACTUS FLOWER", "サボテンの花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "エイブ・バロウズの人気舞台劇の映画化。ゴールディ・ホーンはこのデビュー作でオスカーを受賞。ニューヨーク５番街で歯科医を営む独身プレイボーイが若い恋人と堅物の年増看護婦の間で珍騒動を繰り広げるコメディ。バーグマンは久々にアメリカ映画に出演。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あこがれ", "あこがれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "黒澤明監督の「赤ひげ」（1965）に起用されたあと、TVドラマの『氷点』で人気が沸騰したアイドルスター・内藤洋子の初主演作。興行的に大成功し、以後、内藤や酒井和歌子、加山雄三、黒沢年男らを主演にした一連の作品が東宝青春路線として次々に作られた。この作品で内藤は、養護施設で育ち、酒飲みの父親の面倒をみながらラーメン屋で働くけなげな娘に扮する。始終暗い表情をたたえた彼女だが、施設時代の男友だちとのデートの場面では明朗な個性を十分に発揮。シネスコ画面が草原を走る内藤のクローズ・アップを捉え続ける長いショットは、その額の輝きを甘い青春の記憶として永遠化している。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("舞妓はん", "舞妓はん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "倍賞千恵子の舞妓姿が初々しいメロドラマ。祇園の舞妓・小美知は８年ぶりに小学校の同級生・瀬川孝夫と再会、深く愛し合うようになっていた。しかし周囲の人間は、製薬会社の専務を小美知の旦那にしようと二人の仲を引き裂く。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Tale of Despereaux", "ねずみの騎士デスペローの物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "", "2008-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トワノクオン　第二章　混沌の蘭舞", "トワノクオン　第二章　混沌の蘭舞", "", "", "http://image.eiga.k-img.com/images/movie/56396/315x210.jpg?1919", 45, "「機動戦士ガンダム　第08MS小隊」などで知られ、2010年に他界した飯田馬之介監督が生前に制作を進めていた全6話のオリジナルアニメーション。「ベスティア」と呼ばれる特殊能力に目覚めた少年少女たちの戦いを描く。人々をおびえさせる連続殺人事件が発生し、その現場にはいつも花の絵が残されていた。能力者がかかわっていると考えたクオンたちは事件を追うが、同時にサイボーグ部隊のイプシロンも事件の核心に迫っており……。", "2011-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("船を降りたら彼女の島", "船を降りたら彼女の島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「がんばっていきまっしょい」の磯村一路監督が再び愛媛を舞台に撮り上げたヒロイン・ドラマ。両親に結婚を告げるために帰郷した女性が自身の初恋を回想する。恋人との結婚を報告するため、東京から瀬戸内海の瀬ノ島に帰郷した久里子。父親にそれを言い出せないでいる彼女は、幼なじみの健太とともに初恋相手の男の子、隆司の消息を追うことで気持ちに踏ん切りをつけようとする。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("闇のカーニバル", "闇のカーニバル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「ロビンソンの庭」の山本政志監督が、高い評価を受けるきっかけとなった意欲的な16ミリ作品。子連れの女性ロック歌手が子供を一晩男友だちに預けて、新宿の街にトリップしていく。映画は彼女の行動を追いながら、都市の欲望と闇の部分にスポットを当てる。早朝のカラスの群れも印象的だ。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("娘十六ジャズ祭", "娘十六ジャズ祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「青春ジャズ娘」に続いて新東宝が製作した“ジャズ娘”もの音楽映画の第２作。あるキャバレーのジャズ・バンドが一人の浮浪少女を拾い、彼女を元オペラの指揮者のもとに預ける。しかし、彼女はジャズの天分を持っており、やがてジャズ・バンドに加わって得意の歌を披露する。当時の人気ジャズ・バンドが大挙出演。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初笑い底抜け旅日記", "初笑い底抜け旅日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "町人姿に身を変えた黄門様に漫遊の予備知識を得意げに教える風来坊の金太。後日、彼はデンスケ賭博で女スリに負け、おまけにその女スリが黄門様の財布を盗んで金太の傘の中に入れたものだからさあ大変……。エノケン、トニー谷、金語楼らコメディアン総出演の時代喜劇。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUNDAY BLOODY SUNDAY", "日曜日は別れの時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "J・シュレシンジャー監督が、二度の日曜日を含む10日間に二人の男と一人の女の間で起こった出来事を描き、現代人の孤独を照射した作品。ユダヤ人の中年開業医ダニエルは、若い芸術家ボブと同性愛の関係。一方、女経営コンサルタント、アレックスはボブに惹かれていた。ボブはダニエルとの生活に区切りをつけようとするが……。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT MOVES", "ナイトムーブス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "家庭不和にある探偵ハリーのもとに転がり込んできたあるもうけ仕事とは、元女優の家出した娘を捜し出すことだ。娘のボーイフレンドから居場所を聞き出し、無事に連れ戻したものの、密輸が絡む殺人事件に巻き込まれる。ハックマンの孤独な中年探偵がいい。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ランブリングハート", "ランブリングハート", "", "", "http://image.eiga.k-img.com/images/movie/54786/315x210.jpg?3839", 0, "恋に奔放な葵と現実主義な翠は、対照的な性格をした双子の姉妹。優しい青年・克也にプロポーズされた翠は平凡ながらも幸せな日々を送っていたが、ある日突然、好きな人を追いかけて葵がマンションに転がり込んでくる。迷惑がる翠をよそに、葵はマンション向かいのラブホテルでバイトを始め……。「色即ぜねれいしょん」の臼田あさ美が一人二役に挑戦したロマンチック・コメディ。監督は「ヘイジャパ！」「アリア」の村松亮太郎。", "2010-01-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本列島　いきものたちの物語", "日本列島　いきものたちの物語", "", "", "http://image.eiga.k-img.com/images/movie/56796/315x210.jpg?3127", 95, "日本列島で暮らす生き物たちの知られざる家族愛をテーマに、2年半にわたる密着撮影を敢行したネイチャードキュメンタリー。日本各地の美しい四季の移ろいを背景に、ヒグマ、ニホンザル、ウリボウ、キタキツネ、アザラシ、ニホンジカなど30種類におよぶ生き物が登場。NHKスペシャル「プラネットアース」など、自然科学番組に定評のあるNHKエンタープライズが製作した。人気アイドルグループ「嵐」の相葉雅紀がナビゲーターとして映画ナレーションに初挑戦。相葉のほか、長澤まさみ、ゴリ（ガレッジセール）、黒木瞳もナレーションを担当。", "2012-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr. Bug Goes To Town", "バッタ君町に行く", "", "", "http://image.eiga.k-img.com/images/movie/54776/315x210.jpg?3409", 0, "マックス・フライシャー（監督）とデイブ・フライシャー（製作）の兄弟が、ロトスコープという技法を使用して描く長編アニメーション。1941年にアメリカで公開された。音楽は「スターダスト」「わが心のジョージア」などの名曲で知られるホーギー・カーマイケル。都会の真ん中に佇む草むらには、さまざまな虫たちが平和に暮らしていた。しかし、囲いが壊れたために草むらに人間が侵入し、虫たちは平穏な生活を失う。長旅から戻ったバッタのホピティは、安全な土地への引越しを提案し、虫たちは一斉に引越しを始めるが……。", "2009-12-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セカンドチャンス", "セカンドチャンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "20代、30代、40代の女性３人が人生の再出発を誓うまでの３話からなるオムニバス。第１話は、別れた恋人の結婚を知り動揺する28歳の勝ち気なスタイリスト。第２話は、５年前に別れた不倫相手との予期せぬ再会に平静を保とうとする32歳の女性ドライバー。第３話は、娘の結婚式で離婚した夫と再会してペースを乱す47歳の女性。三者三様のキュートでちょっぴり切ない恋模様と、苦悩を乗り越えて経験する人生の転機を軽やかに描く。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女賭博師さいころ化粧", "女賭博師さいころ化粧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "女賭博師・大滝銀子は、死んだ父のイカサマの汚名を晴らすために、父の対戦相手だった緋桜のお秋に弟子入りして真相を究明する。緋桜のお秋に扮した久保菜穂子がはまり役。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ネガティブハッピー・チェーンソーエッヂ", "ネガティブハッピー・チェーンソーエッヂ", "", "", "http://image.eiga.k-img.com/images/movie/53106/315x210.jpg?1907", 109, "無気力に日々を送る高校生・陽介の前は、ある日、謎の美少女・絵里と出会うが、彼女は夜な夜な出現してはチェーンソーを振り回す不死身の男との戦いを続けていた。何故戦うのか理由はわからないが、平凡な日常の何かが変わることを期待して、陽介は絵里の戦いに加勢することに……。「NHKにようこそ！」などで人気の若手小説家・滝本竜彦のデビュー作を市原隼人、関めぐみ主演で映画化した青春活劇。監督はCM界で活躍する北村拓司。", "2008-01-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("落陽", "落陽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "にっかつが80周年記念作品として製作した構想10年、製作費50億円の超大作。第二次世界大戦期の満州に生きたある男の一生を、戦争の経済的側面を照らしながら描いていく。D・レイン、D・サザーランドなど外国の第一級のスターの出演、またジャズ界の女王、エラ・フィッツジェラルドが主題歌を歌うことで話題を集めた。ビデオタイトルは「落陽　THE SETTING SUN」。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本戦歿学生の手記　きけ、わだつみの声", "日本戦歿学生の手記　きけ、わだつみの声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "戦没学生の手紙や手記を集めて出版された『きけわだつみの声』に想を得た反戦映画。戦争に巻き込まれていった若者たちの真摯な訴えが全編を貫いている。監督の関川秀雄は“東宝大争議”で東宝を去った映画人の一人。この後、反戦思想を盛りこんだ作品を連作した。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("つぐみ", "つぐみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "山本周五郎賞を受賞した吉本ばななの大ベストセラー小説を映画化。市川準にとっては、初めて東京以外を舞台にした作品となった。西伊豆の小さな港町。病弱なために小さな頃から甘やかされて育ち、わがままな性格になった18歳の少女、つぐみ。幼なじみのまりあが夏休みで東京の大学から帰って来て、つぐみの姉・陽子と３人に穏やかな夏が訪れる。ある日、つぐみは美術館に勤める恭一と出会い、恋に落ちた。しかし、つぐみに横恋慕する不良たちが恭一にケガを負わせる。つぐみの命を懸けた復讐が始まるが……。牧瀬里穂の存在感ある演技が魅力。脇役ながら、あがた森魚の好演も光る。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Morvern Callar", "モーヴァン", "", "", "http://image.eiga.k-img.com/images/movie/1546/315x210.jpg?3661", 97, "スコットランド作家アラン・ウォーナーの、サマセット・モーム賞受賞の同名小説を、英グラスゴー出身33歳の女流監督、「ボクと空と麦畑」のリン・ラムジーが映画化。スコットランドの港町、スーパーマーケットの店員モーヴァン。彼女のボーイフレンドが自殺し、彼の書いた小説と音楽テープを彼女に残すが……。劇中のテープ収録曲として、リー・ヘーゼルウッド＆ナンシー・シナトラ、カン、ベルベット・アンダーグラウンド、ウイーン、ボーズ・オブ・カナダ、エイフェックス・ツインなどが流れる。", "2003-03-08", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Boogie Woogie", "ギャラリー　欲望の画廊", "", "", "http://ecx.images-amazon.com/images/I/517fsTBFsCL._SL210_.jpg", 94, "", "2009-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("40 GUNS TO APACHE PASS", "無法のライフル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "南北戦争後の米アリゾナ州。アパッチの襲撃に備えて砦を防衛するため、騎兵隊は40挺の新式ライフル銃を用意するが……。A・マーフィを主人公に騎兵隊とアパッチの激闘を迫力のタッチで描く大型ウエスタン・アクション。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市千両首", "座頭市千両首", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "代官の陰謀で千両箱強奪事件の犯人という汚名を着せられた座頭市は、真相を解明し代官一味と用心棒の十四郎を斬る。城健三朗扮する用心棒が馬に乗り鞭を振り回して市と対決する、西部劇タッチのアクションは迫力十分。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOLLYWOOD ENDING", "さよなら、さよならハリウッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "映画業界をネタにウディ・アレンらしい皮肉が冴えるコメディ。落ちぶれた天才監督が、復帰作のクランクイン前日に極度のプレッシャーで盲目になるという設定から、映画作りのデタラメさとアートをめぐる曲解が滝のようにあふれ出る。自由奔放なセルフ・パロディの演技も絶好調だ。オスカー2度受賞は遠い過去。すっかり落ち目の映画監督ヴァルは、プロデューサーで元妻エリーから新作のオファーを受けるが……。", "2005-04-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダーV3", "仮面ライダーV3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 24, "人気特撮テレビシリーズ「仮面ライダーV3」の第2話「ダブルライダーの遺言状」を劇場用に再編集し、「東映まんがまつり」の1編として公開。仮面ライダー1号＆2号のダブルライダーによる改造手術を受けて仮面ライダーV3となった風見志郎は、ダブルライダーの指導のもと、家族を殺したデストロンの怪人ハサミジャガーに立ちむかう。やがて志郎とダブルライダーは、デストロンが原子爆弾で東京の壊滅を目論んでいることを知り……。", "1973-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AU REVOIR，LES ENFANTS", "さよなら子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "1977年以来、創作活動の場をアメリカに移していたマルが、10年ぶりにフランスに戻って撮った自伝色の濃い作品。1944年、ナチス占領下のフランス。パリからカトリックの寄宿学校に疎開している12歳の少年ジュリアンは、転校生のジャン・ボネと親しくなるが、ある時、実はボネがユダヤ人であり、学校でかくまわれていることを知る。ジュリアンとボネの友情は、学校の料理番の密告でゲシュタポがやって来た時に終わりを告げる。ボネはゲシュタポに連行され、二度と戻ってこなかった。二人の少年の間に芽ばえた友情と別離を淡々と描いた感動作。", "1987-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KICKBOXER2", "キックボクサー2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "前作の主人公カート・スローンは、怪物キックボクサー、トン・ポーに殺されていた。その弟デビッド・スローンが、兄の師だったジアンの教えを受けて、トン・ポーと死闘を繰り広げる。前作はJ・C・ヴァン・ダムをスターダムにのし上げたが、そのヒット・キャラクターに頼らず、あくまでストーリーとしての続編を重視した作品。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の怒りをぶちまけろ", "男の怒りをぶちまけろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "21歳の若さで事故死した“和製ジェームス・ディーン”こと赤木圭一郎主演のアクション映画。圭一郎扮する新聞記者が、時価２億円のダイヤをめぐるギャング団同士の抗争に巻き込まれる。小品ながら彼のキビキビしたアクションで楽しめる一編に仕上がっている。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南極物語", "南極物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "1958年２月、やむなき事情により日本の南極探検隊の飼っていたカラフト犬15匹が、昭和基地に残された。この作品は、その時食糧のない厳寒の南極でひと冬を乗り切った２匹の犬、タロ、ジロの実話をもとにした動物映画。彼らが厳しい自然条件のなかをどう生き抜いたのか？　そんな犬たちのサバイバル生活が、南極の雄大なる風景の中に描かれる。ある犬は氷の海に落ち、ある犬はシャチと闘って命を落としと、実話とドラマティックなフィクション部分をうまく融合させ、ある種感動的な美談として、蔵原監督が仕上げた。当時、それまでの邦画興行収入記録を塗り替える大ヒットとなった。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Oliver Twist", "オリバー・ツイスト", "", "", "http://image.eiga.k-img.com/images/movie/1576/315x210.jpg?2064", 129, "「戦場のピアニスト」でオスカー監督となったロマン・ポランスキーが、英国の文豪ディケンズの名作「オリバー・ツイスト」を原作に忠実に映画化。撮影は「戦場のピアニスト」のパベル・エデルマン。舞台は19世紀の英国。孤児院で育った9歳のオリバーは、院を脱走してロンドンへ。そこでフェイギン老人の元に集まって盗みの修行をする孤児たちの集団の一員となって暮らすが、ふとしたことから富裕な紳士ブラウンロー氏と出会う。", "2006-01-28", "3,2,43,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デコトラの鷲（しゅう）　其の五　火の国熊本親子特急便", "デコトラの鷲（しゅう）　其の五　火の国熊本親子特急便", "", "", "http://image.eiga.k-img.com/images/movie/53726/315x210.jpg?2314", 108, "幼い頃に生き別れた父親が九州にいると聞いて熊本を訪れたデコトラ運転手の鷲一郎。再会を果たせないまま帰途についた彼は、帰り道の阿蘇で珠代という女性が営む牧場に立ち寄り、珠代の娘・茜が家出中であることを知る。その後偶然にも茜と出会った鷲一郎は彼女を牧場へ送り届けようとするが……。哀川翔主演による人気シリーズの第5弾。シリーズ全作を手掛ける香月秀之がメガホンを取る。共演は梅宮辰夫、水前寺清子ほか。", "2008-06-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Boogie Nights", "ブギーナイツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "1970年代後半から1980年代にかけて、ポルノ映画作りに情熱を燃やした人々の栄華と衰退をたどる群像劇。イチモツのサイズだけは誰にも負けない高校生エディが、ポルノ映画の理想を追求するジャック・ホーナー監督のファミリーに加入する。やがて“ダーク・ディグラー”なるイカす芸名を得てヒット作を連発するエディだったが、ドラッグの快楽に溺れジャックとの決別の時を迎える……。リック・スプリングフィールドらのヒットナンバーを全編にフィーチャーし、当時のアメリカン・カルチャーをよみがえらせた、撮影時26歳の新鋭監督P・T・アンダーソンの演出は軽快そのもの。実在の巨根ポルノ・スター、ジョン・C・ホームズをモデルにしたとされる主人公ディグラーと、仲間たちの波乱に満ちた人生を哀感たっぷりに描いていく。骨のあるポルノ監督に扮したB・レイノルズは、ゴールデン・グローブ賞などの助演男優賞に輝いた。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROTHERS IN ARMS", "悪魔のジャンクハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ロッキー山脈のはずれの広大な雪原に棲む殺人鬼教祖集団に襲われる、若者たちの運命を描くサバイバル・ホラー。主人公たちが逃亡と戦いの果てに、人間の弱さや卑怯さ、残酷さなどを露呈していくところが面白い。音楽をジョン・カーペンター監督との名コンビで知られるA・ハワースが担当している。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("憲兵とバラバラ死美人", "憲兵とバラバラ死美人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "元憲兵大尉・小坂慶助の原作『のたうつ憲兵』を映画化した、大蔵貢体制まっただ中の新東宝の代表的ゲテモノ映画。昭和12年10月、主力が満州に出動したあとの仙台歩兵第４連隊の井戸の中から首と四肢がなく、しかも５ヵ月の胎児を身ごもった女の腐乱死体が発見される……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMONG GIANTS", "マイ・スウィート・シェフィールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「フル・モンティ」の脚本家S・ボーフォイのシナリオに基づく人間ドラマ。失業中の中年男レイは仲間とともに、24キロにわたる鉄塔群のペンキ塗りの仕事に着手。バック・パッカーの女性ジェリーを仲間に加えて、彼らは陽気に仕事をこなしていく。やがてレイはジェリーと惹かれ合うようになるが、この恋はレイと仲間との友情に波紋を投げかける……。社会からはみ出した愛すべき男たちの友情と葛藤、せつない愛を時に温かく、時に物悲しく描き出す。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAT STREET", "ビート・ストリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1980年代のヒップホップ・カルチャーを背景にダンス・ビート・バトルをフィーチャーした青春映画。ブレイクダンス、ラップ、DJスタイルなど、クールかつノリノリのダンス・ムービーとしても楽しめる一作だ。荒れ果てたニューヨーク・ブロンクスを舞台に、一人のアーティストの生き様と、彼をサポートする仲間たちとの熱い友情が綴られていく。監督はこの後、音楽ドキュメンタリーの秀作「THE SHOW」を撮り上げたS・レーザン。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD THE LAST VAMPIRE", "BLOOD THE LAST VAMPIRE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "「老人Z」の北久保弘之監督によるフルデジタルアニメーションのアクション映画。企画には「アヴァロン」の押井守が参加している。1966年、米空軍・横田基地周辺では不審な自殺が相次いでいた。そんな中、基地のアメリカンスクールにセーラー服の美少女、小夜が現われた。彼女は人間社会に身を潜めた吸血鬼“翼手”を倒す為に、“組織”が送り込んだ救世主だった。", "2000-11-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラ", "ゴジラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ゴジラ生誕30周年を記念して「メカゴジラの逆襲」以来実に９年ぶりに製作された。怪獣トーナメントを繰り返し、すっかり子供のアイドルと化したゴジラのキャラクター。生誕30周年を機に、第１作で凶暴な怪獣として、初登場したゴジラのキャラクターが今回再び採用された。しかし、ドラマ部分のテーマに核戦争の恐怖を設定したのはいいが、橋本幸治の演出は陳腐な人間ドラマの域を出ていない。また、肝心の中野昭慶の特撮はあまりにメカニックに頼りすぎて、ゴジラ本来の原始的な凶暴さが全く生かされなかった。", "1984-12-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BERESINA　ODER　DIE　LETZTEN　TAGE　DER　SCHWEIZ", "ベレジーナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "鬼才D・シュミットがスイス政財界を風刺したブラック・コメディ。“もしもスイスでクーデターが起こったら”というアイデアをもとに、一人のロシア人コールガールがたどる運命を綴る。スイス国籍を取るためにロシアからスイスにやってきたイリーナは、権力者たちの寵愛を一身に受ける高級売春婦。無垢な彼女は、客たちの“優しさ”を将来スイスに呼び寄せるつもりの家族に手紙で知らせていた。そんな彼女は、いつしか国家の陰謀に巻き込まれ現実を目の当たりにすることになる。一人の女性と、汚れきったキャリア・政治家らの姿を通し、現代社会のミゾを寓話的かつ皮肉を込めて描きあげる。本国での公開当時、作品内容と酷似した事件が起きたことも話題になった。", "1999-01-01", "26,7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("色ごと師春団治", "色ごと師春団治", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "大阪ミナミの寄席“花月”で前座を務める桂春団治は、型破りの落語と独特の話術で人気があったが、生来の女好きと酒好きという欠点の持ち主。小料理屋の女中、ひいき筋の若後家、京都の宿屋の娘と、次々に手を出すが、一方で彼は“花月”の一枚看板となっていく。松竹新喜劇のスター、藤山寛美主演の人情喜劇。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京フィスト", "東京フィスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「鉄男　TETSUO」などでカルト映画ファンの人気を持つ塚本普也が監督と主演を兼ねた恋愛格闘技映画。二人の男と一人の女との三角関係を絡ませながら、都会の男の生きざまを描く。サラリーマンの義春はかつての友人拓司に恋人のひずるを誘惑され、殴り込むがボクサーの拓司にあっさりとやられてしまう。彼への憎しみを募らせた義春は、拓司の通うジムに入門。トレーニングに励む中、二人の対戦の時が訪れるのだが……。DVDタイトルは「TOKYOフィスト」。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブレイクタウン物語", "ブレイクタウン物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "風俗産業の最前線、新宿・歌舞伎町を舞台に、金のために性を売ることにほとんど抵抗を感じない、地方から出てきた少女たちの目を通して、そこに生きる人々の様々な人間模様を綴った風俗映画。一般公募の高校生・橘、木元、前川が揃って好演。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("賭星／ALL　FOR　THE　WINNER", "ゴッド・ギャンブラー 賭聖外伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "チョウ・ユンファ主演のヒット作「ゴッド・ギャンブラー」のパロディー版。叔父を頼って大陸から香港に渡った若者が、持ち前の透視能力を活かしてギャンブルで連勝。やがて国際大会で、香港の天才ギャンブラーと対決するハメになる。主人公に扮したC・シンチーは、本作のヒットで大ブレイクし、香港を代表するコメディ・スターとなった。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ride with the Devil", "楽園をください", "", "", "http://image.eiga.k-img.com/images/movie/1616/315x210.jpg?2014", 138, "1861年、アメリカ南部ミズーリ州。親友同士であるドイツ移民の青年ジェイクと裕福な農園主の息子ジャックは、南北戦争の勃発により希望に満ちた青春の道を絶たれる。父を北軍派に殺されたジャックと、本来なら北軍のドイツ人グループに加わるべきジェイクも、南部の男として南軍派のゲリラ隊に加わった。しかしやがて、この戦いは戦う意味すら見えない狂気の殺戮へと変貌していく。そして、歴史的な事件“ローレンスの大虐殺”が幕を開けた……。", "2001-02-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("残酷おんな情死", "残酷おんな情死", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "睡眠自殺を図った女・万代子を助け、彼女のマンションに居候することになった千恵。病弱な万代子に代わって千恵が彼女の仕事を請け負うが、その仕事とはサド・クラブのショー・ガールで、やがて千恵はレズの味を知り始める。レズビアンを素材に“情死”の世界を描いた衝撃作。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社長えんま帖", "社長えんま帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "マルボー化粧品社長の大高長太郎は個性的な部下たちの掌握に苦労している。親会社の大社長の古稀祝賀会に出席、“攻撃は最大の防御なり”と叩き込まれたが、一方で浮気の虫もムズムズしてきて……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スウィングガールズ", "スウィングガールズ", "", "", "http://ecx.images-amazon.com/images/I/517VNAFVZPL._SL210_.jpg", 105, "「ウォーターボーイズ」の矢口史靖監督が、今度は女子高生×ジャズというドッキングで笑い泣かせる、ほのぼの痛快作。のほほんと脱力系の東北弁を話す女の子たちが、なんとはなしにビッグバンド・ジャズの世界で、ココロとカラダのセッションを謳歌していく姿を、相変わらず肩ひじ張らず汗もかかない、伸びやかなスマイル演出でさらりと見せていく。東北地方のとある田舎町。食当たりで倒れたブラスバンド部の代わりに、落ちこぼれの女子高生たちがビッグバンドを結成。嫌々始めた彼女たちだったが、次第にジャズの魅力にとり付かれ、学生音楽祭出場を目指して練習に励む。", "2004-09-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Rum Diary", "ラム・ダイアリー", "", "", "http://image.eiga.k-img.com/images/movie/57416/315x210.jpg?3049", 120, "2005年に自殺したアメリカのジャーナリスト、ハンター・S・トンプソンの自伝的小説を映画化。1960年代のプエルトリコで、弱小新聞に執筆する酔いどれフリージャーナリストのポール・ケンプの破天荒な人生を描く。トンプソン原作の「ラスベガスをやっつけろ」にも主演したジョニー・デップが製作・主演。ニューヨークの喧騒に疲れたジャーナリストのポール・ケンプは、自然に囲まれたプエルトリコにやってくる。個性豊かな仲間と毎日ラム酒をあおる生活にすっかりなじんだケンプだったが、ある日、アメリカ人企業家のサンダーソンと知り合い、サンダーソンの婚約者シュノーの魅力に夢中になってしまう。共演にアーロン・エッカート、アンバー・ハードら。", "2012-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY LIFE", "マイ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "ロサンゼルスに住む36歳の広告マン、ボブ・ジョーンズ。仕事も順調で、妻ゲイルは初めての子供を妊娠中、誰から見ても幸せの絶頂にいる男。しかし彼は、末期ガンに冒されていた。残されたわずかばかりの命。彼は妻にも告げず、一人生まれてくる子のためにビデオレターを撮り始める。パパはどんな男だったのか、どんなにママを愛していたかを……。「ゴースト／ニューヨークの幻」の脚本家B・J・ルービンが初監督を務めた感動作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Carla's Song", "カルラの歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "イギリスの巨匠ケン・ローチ監督が、内戦下のニカラグアで心に深い傷を負った女性の姿を通して、ニカラグア内戦の真実を批判を込めて描いた作品。グラスゴーのバス運転手ジョージは、ある日無銭乗車しようとした外国人女性カルラを助け、どことなく陰のある彼女に惹かれるようになる。最初のうちは頑なに心を閉ざしていた彼女だったが、彼の優しさに次第に心を動かされ、ついに故郷ニカラグアでのつらい過去を語り始める……。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人斬り観音唄", "人斬り観音唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "捨て子で盲目の小坊主林太郎の母・お蔦を探すため、旅をする怪僧・了達。ところがお蔦は娼婦になっており、紀州から泉州・堺、そして四国へと、彼らの旅を先回りして次から次へと売られていく。一方、了達は旅の途中の堺で、軍需物資の集積を謀るため悪どい手段を展開する陸軍少将らの横暴に出くわし……。「座頭市」を意識した任侠活劇。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAIGRET ET L’AFFAIRE SAINT−FIACRE", "サン・フィアクル殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "殺人予告の脅迫状を受け取ったサン・フィアクル伯爵夫人。彼女が助けを求めたのは、夫人に幼い頃憧れていたメグレ警視。古物商として館に潜入したメグレだが、予告したその日、ミサのさなかに夫人は突然心臓発作を起こす。自らの名誉を懸けた敏腕警視の厳しい追及が、秘書や息子らの関係者に向けられていく。演ずるのは、いぶし銀の魅力たっぷりのJ・ギャバン。まさにはまり役といえる渋い演技で、シムノンの描くミステリーの世界を見事に体現した作品。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUMUMBA", "ルムンバの叫び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "今なお語り継がれるコンゴの英雄ルムンバの生涯を、ドキュメンタリータッチで描く伝記映画。1960年にベルギーの植民地支配下にあったコンゴを独立に導き、初代大統領に就任したルムンバ。間もなく列強各国の利害関係の渦に巻き込まれ、悲劇的な結末を迎えてしまう彼の死の真相に迫る。ビデオタイトルは「暗殺前夜　ルムンバの叫び」。", "2000-01-01", "2,37,7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("淫種／THE　RAPE　AFTER", "死霊の受胎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "写真家モーは女と骨董品が大好き。彼は撮影のあと、古寺から人面怪像を持ち帰る。その夜、モーは、モデルのシューユーと酒を飲み泥酔。実は人面怪像には呪われた秘密があり、シューユーは悪霊にレイプされてしまう……。グロテスクなパワーを持った香港ホラー。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新選組始末記", "新選組始末記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "子母沢寛の原作を三隅研次監督、市川雷蔵主演のコンビで映画化した幕末もの。浪人・山崎蒸は恋人・志満の反対にもかかわらず、当時京都で活動し始めていた壬生の新選組に入るが、内部には策謀が渦巻いていた。近藤勇に扮する城健三朗（＝若山富三郎）が好演。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MAN CALLED SLEDGE", "スレッジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "V・モローの監督デビュー作でもある西部劇。荒野の真っ只中の監獄に隠匿されている30万ドルの砂金に狙いをつけて、お尋ね者スレッジは仲間と組んで、自ら囚人として乗り込み、まんまと砂金を奪い取るが……。", "1969-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本の青春", "日本の青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "遠藤周作の小説『どっこいショ』の映画化。戦争中に上官からリンチを受けて耳が不自由になった男。今は特許事務所を開いて平凡に生きている彼が、たまたま元上官に再会したことから、心の古傷が甦り……。戦争がトラウマになった者の哀感のドラマ映画。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOT DOG…THE MOVIE", "ホットドッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "B級映画ならではの拾いもの。“ホットドッグ”とはルール無用のスキー・チェイスの勝利者に与えられる栄光の称号。いうなれば「キャノンボール」のスキー版映画なのだ。34シーンに及ぶスキー・アクションが見ものだが、ほどよいお色気とラブ・チェイスも楽しい。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELLO MARY LOU：PROMNIGHTII", "プロムナイト II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "30年前のプロムの夜、ボーイフレンドの嫉妬心から焼死したメリー・ルーが復活。美しい女生徒ビッキーに乗り移り、復讐を始めるというサイコ・ホラーのシリーズ第２作。木馬やシーツ、黒板がビッキーを襲う恐怖シーンを手掛けたのは「エルム街の悪夢」のジム・ドイル。", "1987-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALPHAVILLE", "アルファヴィル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "電子指令機の命じるまま、記憶も思考も感情もなしに人々が暮らす未来都市アルファヴィル。ブラウン教授救出のため、都市に入った探偵レミーは、教授の娘ナターシャを救い、電子指令機を破壊する。ゴダールは後に「新ドイツ零年」にも、この探偵を登場させた。", "1965-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Black Stallion", "ワイルド・ブラック　少年の黒い馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "中近東を航海旅行中、嵐に遭遇して海に投げ出された少年を、同じく投げ出された馬が救った。孤島に流れ着いた少年と馬は、それから６ヵ月間生き延びて救出され……。コッポラの指揮によるファミリー・ピクチャー。後に続編「ワイルド・ブラック２　黒い馬の故郷へ」も製作された。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COUSINS", "今ひとたび", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "1975年に大ヒットしたフランス映画「さよならの微笑」のリメイク編。お互いの夫婦関係にすき間風が吹きはじめた二組のカップルが、親戚の結婚式で出会い、恋に落ちてしまう。いとこ同士の恋人たちの愛と苦悩を、ユーモアとロマンティックなムードの中に描いた作品。ロッセリーニの美しさがまぶしい。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファイナル・ジャッジメント", "ファイナル・ジャッジメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-06-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SMILE OF THE FOX", "殺しへの微笑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "18歳のフレッシュなボディーに96センチのバストという、セクシー女優D・カプリオリオが主演したサスペンス。罠にはまり、職はおろか妻子まで失った刑事マーク。狙撃の腕を買われた彼は、地下組織に要人暗殺を依頼される。指定された部屋で連絡を待つうちに隣人マリーナと恋に落ちた彼は、彼女のパトロンが自分の狙う相手と同じ人物だと知り……。", "1992-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO WEEKS IN ANOTHER TOWN", "明日になれば他人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "映画製作の舞台裏を描くベストセラーの映画化。一時は巨匠監督クルーガーと組んでアカデミー賞まで獲得したほどの男ジャックは、妻のカルロッタと離婚して以来転落していった。酒に溺れ、精神障害まで起こし療養所にいた彼に、６年ぶりにクルーガーから仕事が舞い込む。しかしそれは、録音監督という彼の予想もしない仕事だった。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NUMBER ONE WITH A BULLET", "弾丸刑事／ニック＆フランク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ロサンゼルス市警の二人の刑事が、市の名士の仮面を被った大物麻薬ディーラーを追い詰める。別れた女房に未練ありのハミダシ刑事ニックとダンディな黒人刑事フランクのキャラクターの描き分けが面白い。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Spy Kids: All the Time in the World in 4D", "スパイキッズ4D：ワールドタイム・ミッション", "", "", "http://image.eiga.k-img.com/images/movie/56636/315x210.jpg?1242", 88, "ロバート・ロドリゲスが手がける「スパイキッズ」シリーズ第4作。時間を止めて過去にさかのぼり、世界を征服しようと企む悪党タイムキーパーの野望を阻止するため、母親が元敏腕スパイという秘密を知った双子の姉弟が、新スパイキッズとして戦う姿を描く。双子の母親の元スパイにジェシカ・アルバが扮するほか、シリーズ過去3作でスパイキッズを演じたアレクサ・ベガ、ダリル・サバラも引き続き出演。", "2011-09-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢影童年／ELECTRIC SHADOWS", "玲玲〈リンリン〉の電影日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "文化大革命という激動の時代に、映画と共に生きた人々を描く珠玉の感動作。ノスタルジーあふれる美しい映像は、厳しい生活ながらも豊かな心があった、古き良き時代を鮮やかに映している。主演は中国映画界の実力派、シア・ユイ。元女優志望の母を持つリンリンは、親子二人の貧しい暮らしの中、映画好きの少女に成長する。転校生のシャオビンと親友になるが、父親に虐待を受けていた彼は、祖父母に引き取られてしまう。", "2006-05-13", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺は、君のためにこそ死ににいく", "俺は、君のためにこそ死ににいく", "", "", "http://image.eiga.k-img.com/images/movie/33966/315x210.jpg?1317", 141, "東京都知事の石原慎太郎が製作総指揮と脚本を手掛け、特攻隊員たちのはかない青春を切り取った群像劇。太平洋戦争末期、不利な状況に陥った日本軍は、最後の手段として特別攻撃隊を編成。多くの若者たちが大空へと散っていった。そんな中、軍指定の食堂を営む鳥濱トメは、複雑な思いを胸に彼らを見守り続けていた……。“特攻の母”として知られる実在の人物トメを岸恵子が演じるほか、特攻隊員を徳重聡、窪塚洋介、筒井道隆らが熱演。", "2007-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RACHEL PAPERS", "レイチェル・ペーパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "名門オックスフォードに通う大学生チャールズが、紆余曲折を経て憧れのアメリカ娘レイチェルと同棲にこぎつけるものの、だんだん熱が冷めて……という、ちょっとリアルな恋物語。J・プライス、J・スペイダーなど脇役たちの好演が拾いもの。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELGA UND MICHAEL", "続・女体の神秘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "大ヒットした教育的医学映画「女体の神秘」の続編。前作で主人公ヘルガになったガスマンの実生活に基づいて描かれていく。動物とのセックスを描くことにより、健康で満ち足りた夫婦生活とは何かを、映像を通して訴えていく。", "1968-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帝銀事件 死刑囚", "帝銀事件 死刑囚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "熊井監督の処女作品。1948年に起こった銀行員12人毒殺の帝銀事件を、取材した新聞記者の目を通してドキュメンタリー・タッチで追っている。熊井監督の“真実とは何か”の訴えが、リアリズムの画面を通して観る者の心を揺さぶる。平沢死刑囚役の信の抑えた演技が印象深い。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Purple Butterfly", "パープル・バタフライ", "", "", "http://image.eiga.k-img.com/images/movie/1606/315x210.jpg?3847", 128, "戦乱の緊張が漂い始める1931年の上海。日本軍の敏腕諜報部員・伊丹は、かつて恋人だったシンシアに再会し、もう一度愛を育もうとするが、シンシアは抗日テロ組織パープル・バタフライの一員となっていた。「SAYURI」も楽しみなチャン・ツィイーと、国際派スター、仲村トオルが共演。監督・脚本は「ふたりの人魚」でロッテルダム映画祭でグランプリを受賞した中国映画界の新鋭ロウ・イエ。撮影は「たまゆらの女」のワン・ユー。", "2005-11-12", "8,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vertigo", "めまい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "　原作の「死者の中から」は、ヒッチコックの映画のために書かれた小説。映画のタイトルは、主人公が高所恐怖症であることから監督によりつけられた。ジョンは妻の行動を心配する友人のエルスターから、彼女の尾行を頼まれる。自殺した曾祖母の霊に取り憑かれている妻は、重ねて自殺を図るが……。彼女を救えなかった罪悪感から、悪夢に陥るシークエンスやヒロインによる観客への真実の告白など練り上げられた演出が冴える。", "1958-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にっぽん泥棒物語", "にっぽん泥棒物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "土蔵破りに入ろうとして不気味な集団を目撃する男。その直後に列車転覆事故が勃発する。21世紀になっても謎とされている松川事件を思わせるような犯罪を、うまく絡ませた恐ろしくも楽しい喜劇。刑事役の伊藤雄之助のとぼけた味わいと、三國連太郎の純情素朴な心情が笑いを増幅させる。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RAILWAY CHILDREN", "若草の祈り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "20世紀初頭。父親がスパイ容疑で連行されたため、残された３人の子供たちは母親に連れられ、ヨークシャーの田舎に引っ越す。子供たちはそこで一日一本だけ走るSLに夢を託し、父の帰りを待っている。エディス・ネスビット女史の名作をすがすがしいタッチで描いた一編。", "1970-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cradle 2 the Grave", "ブラック・ダイヤモンド", "", "", "http://image.eiga.k-img.com/images/movie/1296/315x210.jpg?2186", 101, "監督バートコウィアクは撮影監督出身、「リーサル・ウェポン4」の撮影でジョエル・シルバーに認められ、「ロミオ・マスト・ダイ」で監督業に進出。DMXは刑務所収監経験をもつ人気ラッパー。自伝も出版、自己レーベルも持ち、近年は俳優活動も盛ん。本作のテーマ曲も自作、サントラにはあのエミネムとの共作も収録されている。また、「スコーピオン・キング」で注目され、「X-MEN2」の悪役ミュータント、レディ・デスストライカーに抜擢されたアジア系美女ケリー・フーも共演。", "2003-03-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使が降りた日", "天使が降りた日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "家族や恋人とのすれ違いや、社会のしがらみの中で、自分を見失いかけている人々をそっと救ってくれる、“天使”の存在を描いたオムニバス映画。「スカイハイ」の麻生学監督ら5人の気鋭作家による、小さな幸せに溢れたファンタジックな世界観が、混迷の現代に安らぎのひと時を与えてくれる。祖父のホテルで働く美咲は、1ヵ月も宿泊費を滞納している客に悩まされている。そんな中、彼女は奇妙な老婆に出会う。（「気まぐれ天使」）", "2005-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nine", "NINE", "", "", "http://image.eiga.k-img.com/images/movie/53226/315x210.jpg?4417", 118, "フェデリコ・フェリーニ監督の「8 1/2」のミュージカル版「ナイン」を、「シカゴ」のロブ・マーシャル監督が映画化。ダニエル・デイ＝ルイス、マリオン・コティヤール、ペネロペ・クルス、ジュディ・デンチ、ニコール・キッドマン、ソフィア・ローレンら豪華キャストが集結。映画監督のグイドは、クランクインを目前に控えながら脚本を書くことができず、プレッシャーと女性たちとの複雑な関係に追い詰められていた。そんなグイドは妻にも愛想を尽かされ、いつしか自分の幻想世界へと溺れていく……。", "2010-03-19", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.MAGOO", "Mr.マグー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「裸の銃〈ガン〉を持つ男」のL・ニールセン主演のコメディ。彼が扮する大富豪マグー所有の高価な宝石をめぐって美人宝石泥棒とその間抜けな相棒が暗躍。マグーはありとあらゆる邪悪な陰謀にさらされるが、彼はなぜか傷一つ負うことなくピンチを逃れていく。それでも事件は間抜けなFBI捜査官らを巻き込んでますます大きくなってしまうのだった……。監督はジャッキー・チェン主演の「レッド・ブロンクス」「ファイナル・プロジェクト」を撮った香港映画界の俊英S・トン。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PART10 待つ女", "難波金融伝 ミナミの帝王　劇場版PART10 待つ女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "銀次郎に金を借りていた小野寺が詐欺に遭い、行方をくらました。銀次郎は小野寺を捜索するために、彼の妻である芸者・夢千代の下に仲間の涼子を送り込む…。螢雪次朗らがゲスト出演した、高利貸し・銀次郎の活躍を描いた金融ドラマシリーズ第11作。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PENTATHLON", "ソルジャーゴールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "マラソン、水泳、乗馬、フェンシング、射撃の５種を競い合うペンタスロンの旧東ドイツ代表、エーリッヒは、1984年ロサンゼルス・オリンピックの金メダル獲得後、アメリカに亡命していた。そのことで、彼を手塩にかけて育てた元コーチで秘密警察のクリントはエーリッヒを恨んでいた。そしてベルリンの壁崩壊後アメリカに渡り、ネオ・ナチのテロ集団の総統となり復讐を開始するのだった……。D・ラングレン主演のアクション。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANFAN", "恋人たちのアパルトマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "婚約者ロールとの所帯じみた関係に嫌気がさしていたアレクサンドルは、自由奔放でバイタリティーにあふれたファンファンに出会い一目で恋に落ちる。夢のような時間を過ごす彼らだったが、アレクサンドルは彼女への愛の情熱を保ち続けるために肉体関係を持とうとしなかった。そんな折、ロールの存在を知ったファンファンは、後ろ髪引かれながらアレクサンドルに別れを告げて去ってしまう。ファンファンを失うことを恐れたアレクサンドルは、ロールとの婚約を解消することを決意し、ファンファンのアパルトマンの隣の部屋を借りる。彼女の留守中に壁をぶち抜き、マジックミラーを取り付けたアレクサンドルは、何も知らないファンファンと密やかな同棲生活を始めるのだった……。フランスの人気作家A・ジャルダンが、1990年に発表した小説を自ら映画化した作品。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEAN　GIRLS", "ミーン・ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "全米で人気爆発のティーンアイドル、L・ローハン主演の学園コメディ。面白いのは、ハッピーな恋愛話よりも、女同士の権力争いや足の引っ張り合いに主軸が置かれていること。ブラックでリアルな学園生活の裏側と、爽やかなシンデレラ・ストーリーを融合させた、青春ドラマの怪作だ。アフリカからアメリカの高校に転校したケイディは、なぜか学園の女王レジーナのお気に入りに。ところがレジーナは最低の悪女で……。", "2005-03-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SINBAD， THE SAILOR", "船乗りシンバッドの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "D・フェアバンクスJr.が剣さばきもあざやかに大活躍するアドベンチャー。あまりにも有名な『アラビアンナイト』の物語に材をとった海の英雄シンドバッドの冒険活劇で、M・オハラが華を添える。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DV2", "DV2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 160, "", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆発！暴走遊戯", "爆発！暴走遊戯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "下町の暴走族“ブラック・パンサー”と山の手の暴走族“レッドバロン”の抗争を描く、岩城滉一の暴走族もの第２作。「ウエスト・サイド物語」風の筋立てで、多岐川裕美が可憐な岩城の妹役で登場。中島ゆたか、芹明香といった脇を固める女優陣が魅力的。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Amator", "アマチュア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "巨匠クシシュトフ・キエシロフスキー監督の長編第2作。平凡だが幸せな生活を送っていたフィリップ・モスは、娘の誕生日を記録するために2カ月分の給料をはたいて8ミリカメラを購入。アマチュア映画協会の人物と知り合ったことがきっかけでカメラにのめりこんでいった彼は、仕事場や家庭で徐々に孤立していく。本作でモスクワ映画祭グランプリを受賞したキエシロフスキーは、ポーランド国外でも高い評価を受けるようになった。", "", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPEED ２ ： CRUISE　CONTROL", "スピード2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "全世界で大ヒットを飛ばした「スピード」の続編。主演は前作でスターとなったS・ブロックと「アラモ」のJ・パトリック。そして狂気に満ちた犯人をW・デフォーが演じている。前作で事件に巻き込まれたアニーは新しい恋人であるSWAT隊員のアレックスと、一週間のカリブ・クルージングを楽しむため豪華客船に乗り込む。だが、そこにはシー・ジャックを企むガイガーも紛れ込んでいた……。彼の計画によって各所で爆破が起こり、暴走する船の中で、アニーとアレックスがタンカーとの衝突を回避し、ガイガーを追い詰めていく様をスピーディーな演出で見せていく。エンディング・テーマ曲を小室哲哉が手掛けたことも話題となった。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SNAKE PIT", "蛇の穴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "記憶を失い、精神病院に入院する妻と、彼女の記憶を取り戻させようとする夫と医者。この３人の愛情のもつれを描いたサスペンス・ラブ・ストーリー。エロール・フリンと共演した剣劇もので名を馳せた美人女優、O・デ・ハヴィランドの主演作。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一万年王国　青森県の縄文文化", "一万年王国　青森県の縄文文化", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "青森県六ヵ所村の海岸で見つかった１万２千年前の縄文時代の遺跡へ取材を敢行、その様子や出土品などから縄文人たちの深くて豊かな生活を描き出していく。作家の立松和平がナレーションを担当している。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La religieuse", "修道女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "修道院の腐敗を告発したディドロの原作を、数年にわたる紆余曲折の末にリベットが実現させた渾身の問題作。アンナ・カリーナが見事な主演を見せ、教会からの反対運動を受けながらカンヌ映画祭でパルムドールにノミネートされた。没落貴族の娘シュザンヌは意に反して修道院に送られた。行く先々で出会う聖職者たちの腐敗を目にし、迫害の日々の中で自由への抵抗を続けるのだが……。", "1996-09-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love Happens", "わすれた恋のはじめかた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("From Hell", "フロム・ヘル", "", "", "http://image.eiga.k-img.com/images/movie/1396/315x210.jpg?2276", 124, "デトロイト生まれの黒人兄弟アレン＆アルバート・ヒューズ監督が、UKコミック作家アラン・ムーアの同名作品を原作に、19世紀イギリスの連続殺人鬼、切り裂きジャックの映像化に挑む。ヴィクトリア朝末期ロンドンで台頭する新しい科学と古い伝統の相克、そこから生じたオカルティズムの流行など、史実に基づく時代描写のもと、アヘン中毒の警部（ジョニー・デップ）が娼婦メアリ（ヘザー・グラハム）の協力を得て事件を追う。", "2002-01-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEVER CRY WOLF", "ネバー・クライ・ウルフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "カナダの動物作家F・モワットのベストセラー小説の映画化。カナダ北部のツンドラ地帯を舞台に、オオカミとその生態調査をする生物学者の青年を通じて、大自然が持つ厳しさ、美しさを見事にうたい上げる。心が洗われるような映像美が素晴らしい。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL BUONO，IL BRUTTO，IL CATTIVO", "続・夕陽のガンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "南北戦争末期のニューメキシコとテキサスを舞台に、盗まれた20万ドルをめぐって3人の流れ者が活躍するマカロニ・ウエスタン。「夕陽のガンマン」の続編で、音楽は前作同様E・モリコーネがあたり、前作よりさらにスピード感を高めた。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("孤剣は折れず・月影一刀流", "孤剣は折れず・月影一刀流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "剣の師である小野が暗殺されたのを知った神子上源四郎は江戸に戻り、小野道場四天王の挑戦を受け、彼らを葬り去る。養父の松平伊豆守より、暗殺の黒幕が柳生一門と春日局であることを知らされた源四郎は、彼に春日局暗殺を依頼されるが、彼は逆に柳生但馬に捕らえられてしまう。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BETTER LATE THAN NEVER", "コート・ダ・ジュール・ドリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "二人の老名優と子役の少女が繰り広げる小粋なコメディ。かつて、一人の女性をめぐって火花を散らしたことのある二人の老人が、その女性からの遺言で、今度は彼女の孫娘の新しい祖父になろうと再び対決する。イギリス紳士、D・ニーヴンとA・カーニーの演技対決も見もの。", "1982-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SISTER ACT", "天使にラブ・ソングを…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "殺人現場を目撃して警察に駆け込んだ場末の歌手デロリスは、修道院に身を隠すことにする。ハレルヤの合唱は熱いロック調ゴスペルになってしまうなど、厳格なシスターたちの日常はデロリスのおかげで大わらわ。W・ゴールドバーグの個性が爆発したハートフルなコメディ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女刑事RIKO・聖母の深き淵", "女刑事RIKO・聖母の深き淵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "柴田よしきのミステリー小説を、「［Focus］」の井坂聡が映像化。殺人犯を追う女刑事の奔走を描く。ラブホテルで起こった殺人事件を追う女刑事・リコは、３年前との誘拐事件との関連に注目。次第に真相に近づくものの、そんな彼女に危険が迫る……。スリリングなドラマはもちろん、刑事でありシングルマザーでもある主人公など登場人物の二面性を見つめた、人物描写の奥行きにも注目したい。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マルドゥック・スクランブル　燃焼", "マルドゥック・スクランブル　燃焼", "", "", "http://image.eiga.k-img.com/images/movie/56336/315x210.jpg?1551", 61, "第24回日本SF大賞を受賞した冲方丁の小説を3部作で映画化する第2部。ボイルドとの激闘で傷ついたバロットとウフコックは、ドクターに連れられて生命の研究施設「楽園」を訪れる。「楽園」の技術で回復したバロットは、創始者の1人、フェイスマンと出会い「楽園」の過去を知ることになる。そして、追い求めているシェルの記憶がシェルの経営するカジノの100万ドルチップの中に隠されていることを突き止めたバロットらは、チップを手に入れるためカジノに潜入する。", "2011-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どんぐりっ子", "どんぐりっ子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "森昌子の芸能生活５周年記念映画で、山形を出て、東京の中流家庭で女中となって働く娘の姿を素朴にうたい上げた佳作。山形の風景のみずみずしさ、娘の生きる力強さが印象的。原作の『女中っ子』は1955年に田坂具隆監督、左幸子主演で１度映画化された。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Moderns", "モダーンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "　1920年代、モダン・アートの時代を迎えようとしていた頃のパリ。腕はいいが売れない画家のハートは、彼のもとを去り、若き実業家ストーンと再婚したレイチェルと再会する。レイチェルとのよりを戻すため、ハートは贋作を描くことを決意するが……。成功を夢見る画家と、彼を取り巻く男女の愛憎をロマンティックかつシニカルに描いたドラマ。「トラブル・イン・マインド」に続いて撮影を担当した栗田豊通の映像も美しい。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DELICATESSEN", "デリカテッセン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "最終戦争後のパリ郊外にポツンと建つ精肉店。そこの店主と上に住む住民たちは、滅多に手に入らなくなった“肉”を分け合う仲。どうも様子がおかしいこの店にある日、心優しい一人の男が住人としてやって来て……。仏映画界の鬼才ジュネ＆キャロが手掛けた傑作ブラック・コメディ。“おいしそうな人間”を手ぐすね引いて待つ店主と、犠牲者にされそうな男の必死のサバイバルが見もの。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last King of Scotland", "ラストキング・オブ・スコットランド", "", "", "http://image.eiga.k-img.com/images/movie/33876/315x210.jpg?1241", 125, "1970年代にウガンダに君臨した悪名高き独裁者、イディ・アミンの実像に迫る社会派サスペンス。強いカリスマ性で民衆の支持を集め、クーデターによって大統領の地位を得たアミンが、次第に残虐な独裁者へと成りかわっていく姿を、彼に重用される架空の青年医師の視点で描く。鬼気迫る演技で見事アミンを演じきったフォレスト・ウィテカーは、第79回アカデミー賞をはじめ数々の映画賞で主演男優賞を総なめにした。", "2007-03-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPRINGTIME", "春が来れば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "「オールド・ボーイ」などで知られる韓国映画界屈指の演技派、チェ・ミンシクが主演した心温まる人間ドラマ。子供たちへの音楽指導を通じて人生を悟る中年音楽家の心の再生劇を、美しい四季の移り変わりになぞらえて描き出した感動作だ。交響楽団のトランペッターになる夢を半ば諦めかけているヒョヌは、ある時、都心から遠く離れた炭鉱町の中学校で音楽の指導者を募集していることを知って、その街へと赴くのだが……。", "2006-03-25", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FLAME AND THE ARROW", "快傑ダルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "12世紀のイタリア・ロンバルディアを舞台に、外敵に領土を侵略され、圧政に苦しむ民衆を救い、捕らえられた息子を救出すべく立ち上がる野人ダルドの活躍を描く時代劇。B・ランカスターがサーカス時代の盟友クラヴァットと組んで画面せましと大活躍する。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無問題２／NO　PROBLEM　２", "無問題〈モウマンタイ〉２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "ナインティナインの岡村隆史が香港で大暴れするコメディ第２作。香港映画ファンの青年ケンが、念願の香港に旅行にやって来る。ところが、殺し屋に間違われて、巨大企業の陰謀に巻き込まれるハメに。ケンは彼らが狙う資産家令嬢を救うために騒動の渦中に飛び込んでいく。ギャグを連発しながら、二丁拳銃やワイヤーアクション、カンフーを駆使して本格的なアクションを披露する岡村隆史のハチャメチャな活躍に注目。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES ENFANTS DU DE´SORDRE", "無秩序な少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "売春とドラッグにより、刑務所送りとなった20歳のマリー。受刑者のための慈善の小劇団、フィル一座と出会ったことが、彼女の運命を変えていく。仮釈放され、保護監察処分つきながら自由の身となったマリー。さっそくフィル一座へとやって来た彼女は、ピリピリと張り詰めた警戒心をなかなかぬぐい切れないでいたが、演劇を通して次第に心を開いていき、彼女自身の娘とも、コミュニケートできるようになっていく……。主演のE・ベアールが素顔の熱演を見せているほか、重くなりがちなテーマを最後まで描き切った、女性監督Y・ベロンのきめ細かな演出が見事だ。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太の恐竜", "ドラえもん のび太の恐竜", "", "", "http://ecx.images-amazon.com/images/I/51SV233F1RL._SL210_.jpg", 93, "人気アニメ『ドラえもん』の記念すべき劇場版映画第一作。のび太が恐竜のたまごの化石を発見。卵からかえったのは首長竜の赤ちゃんだった。のび太は恐竜をピー助と名付け、育てることに。しかしナゾの男がピー助をつけ狙い、のび太は赤ちゃんを元の世界に返すことにするが……。第一作にふさわしく、感動的なストーリーが展開。定番から変わりネタまで、15種類にもおよぶ秘密道具の登場にも注目だ。", "1993-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私のなかの8ミリ", "私のなかの8ミリ", "", "", "http://image.eiga.k-img.com/images/movie/54416/315x210.jpg?3792", 65, "「となりのボブ・マーリィ」で初脚本＆監督を務めた俳優・大鶴義丹が、写真家の桐島ローランドを撮影監督に迎えて監督したバイク・ロードムービー。突然の会社の倒産で日本に帰国したミハルは、久々に帰った空虚な部屋で１通の手紙を見つける。ミハルは昔の恋人との思い出を胸に、手紙を頼りにバイクで北へ向かう。バイクを走らせていたミハルは１人の男と出会い、その男はかつての恋人も持っていた8ミリカメラを持っていた。", "2009-04-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUNRISE", "サンライズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "サイレント芸術の金字塔「最後の人」で名高いドイツのF・W・ムルナウ監督が、フォックス社に招かれて撮った渡米第１作。音楽と効果音のみのサウンド版無声映画である。静かな田舎の村に純朴な若夫婦がいた。ある夏、都会から美しい女が避暑に来て、この夫を誘惑する。色香に迷った彼は女に誘われるまま、妻を殺して逃避行することに同意。湖に妻を沈めるべく小舟に乗り込むのだが……。市電まで走らせた都会の大通り、葦に囲まれた湖など、信じられぬことに全シーンがセット内で撮影されており、カメラは人と物の間を流麗に動き回って観る者を魅了。愛の復活までを見事に描いてみせた。また、本作と「第七天国」の演技で第１回のアカデミー賞を得たJ・ゲイナーが、コケティッシュな魅力をいかんなく発揮している。", "1927-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFGHAN BREAKDOWN", "レッド・ストーム アフガン侵攻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "アフガニスタン侵攻の実態を、旧ソ連軍の全面バックアップによって、リアルに描いた戦争アクション。ソビエト陸軍のバンデュラ少佐は、アフガニスタンでの作戦に携わりながらも、自軍の行動に疑問を抱いていた。その疑問とは、部下を見殺しにはできないという思いであった。そんな折、ソビエト全軍に対して、撤退命令が下される。しかも平和的停戦交渉は決裂し、兵たちは危険を冒しての退却を強いられるのだった。", "1991-01-01", "12,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハイブリッド刑事", "ハイブリッド刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "「秘密結社　鷹の爪」シリーズを手がける蛙男商会による劇場版アニメ第6弾。予算削減と作業効率化のため警視庁に創設された特殊捜査チーム・ハイブリッド課。さまざまな官庁や役所から派遣された公務員が刑事を兼任するこの部署には、消防士、裁判官からネコまで総勢2800人もの職員が在籍している。警視庁の花形・捜査一課からハイブリッド課の課長へと異動させられた小泉鈍一郎は、ハイブリッド課の生みの親である大岩行政改革大臣の娘の警護を任されるが……。", "2011-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Home Coming", "ウォッチャーズ", "", "", "http://image.eiga.k-img.com/images/movie/54876/315x210.jpg?4464", 95, "米TVシリーズ「The OC」でブレイクしたミーシャ・バートンが病的な悪女を熱演するサイコスリラー。高校時代にフットボールの花形選手として活躍したマイクは、恋人シェルビーに会うため大学の冬休みを利用して地元へと帰ってくる。一方、ボウリング場を営むマイクの高校時代の恋人シェルビーは、今でもマイクを病的なほど愛していた。シェルビーの気持ちは徐々にエスカレートしていき、遂にはマイクを取り戻すためにエリザベスを拉致してしまう。", "2009-09-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chasing Legends", "チェイシング・レジェンド", "", "", "http://image.eiga.k-img.com/images/movie/56776/315x210.jpg?2571", 95, "2008年、09年シーズンで年間最多勝利を果たしたロードレースチーム「チーム　コロンビア・HTC」に迫ったドキュメンタリー。2009年ツール・ド・フランスに挑んだチームに密着。ジョージ・ヒンカピー、マーク・カベンディッシュら主力選手やエディ・メルクスら往年の名選手へのインタビュー映像、ハイビジョンカメラでとらえた迫力のレース映像などを交える。", "2011-07-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("42ND STREET", "四十二番街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "富豪の男とミュージカル・スターを目指す女、そしてその女が恋する男という３人の三角関係を物語の軸に置きながら、ミュージカル・ショーの舞台裏を描いたバックステージものの傑作。様々なトラブルに巻き込まれ、なかなか思うようにことが進まない演出家の苦悩と起死回生の大ヒットという、お決まりのストーリー展開ながら、ミュージカルの名匠L・ベーコンの演出が冴えわたり、豊かなミュージカル・シーンに彩られた一編となった。振り付けは「フットライト・パレード」のバズビー・バークレー。挿入曲『シャッフル・オフ・バッファロー』でのダンス・シーンは伝説的。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ant Bully", "アントブリー", "", "", "http://image.eiga.k-img.com/images/movie/33686/315x210.jpg?4677", 89, "アリと同じサイズになってしまった少年が虫たちの世界で繰り広げる冒険を描いた3DCGアニメ。いじめられっ子のルーカスは自分がいじめられた悔しさをアリたちにぶつけていた。そんなルーカスに腹を立てたアリたちは、魔法使いアリの力でルーカスをアリと同じ大きさに縮めてしまう。ルーカスはアリとの生活を通して友情の大切さや本当の勇気を学んでいくが……。製作は「ポーラー・エクスプレス」のトム・ハンクスとゲイリー・ゲッツマン。", "2006-10-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAR！", "スター！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 194, "ブロードウェイの大女優ガートルード・ローレンスの半生を、J・アンドリュース主演、R・ワイズ監督の「サウンド・オブ・ミュージック」のコンビで映画化したミュージカル。20世紀初頭に大ヒットしたジョージ・ガーシュインやコール・ポーターの曲が使用されている。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("札幌・横浜・名古屋・雄琴・博多 トルコ渡り鳥", "札幌・横浜・名古屋・雄琴・博多 トルコ渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ソープランドが、まだトルコ風呂と呼ばれていた時代。日本各地を渡り歩く男女二人を主人公に、各地のトルコ風呂を紹介するというドキュメンタリー・タッチの物語。そこに登場するのは、読経流れる尼寺トルコや女学校トルコ、スチュワーデス・トルコなどなど。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATHSTALKER III：DEATHSTALKER AND　THE WARRIORS FROM HELL", "デス・ストーカー 誓いの剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "中世ヨーロッパ。勇者ストーカーの住む静かな村が襲撃を受ける。戦いの中で偶然美しい王女カリッサと出会った彼は、ある宝石を守ってくれと頼まれる。それが伝説の財宝の町への手掛かりになると知った彼は、王女を守り財宝を手に入れようと決心する。人気シリーズの第３作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Titicut Follies", "チチカット・フォーリーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hills Have Eyes Part II", "サランドラII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "　モトクロスの大会に出場するため、トレーラーで道を急ぐ若者たちの一行。彼らは近道をするため荒地を横断しようとするが、車のトラブルで立ち往生する。そんな一行に、恐るべき殺人鬼が襲いかかった。前作に引き続き、ウェス・クレイブンが監督を務めた残虐ホラー第2弾。", "1984-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん　のび太とブリキの迷宮〈ラビリンス〉", "ドラえもん　のび太とブリキの迷宮〈ラビリンス〉", "", "", "http://ecx.images-amazon.com/images/I/218DEKCQ8BL._SL210_.jpg", 100, "のび太たちは、不思議な門を通り抜け、ブリキン島のブリキンホテルにやってきた。おもちゃが暮らすその島でリゾート気分を満喫する中、ブリキの飛行機に襲われ、ドラえもんも連れ去られてしまった。のび太は、ロボット軍と戦う少年とともに、ドラえもんを救う旅に出る。名優・森山周一郎が反乱軍のナボギストラーを演じている。", "1993-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON GOLDEN POND", "黄昏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "トンプソンの舞台劇の映画化。ニューイングランドを舞台に、ここで毎年夏を過ごす老夫婦と、久しぶりにやって来た娘一家との心の触れ合いが温かく繊細に描かれる。名優フォンダとヘプバーンの老練な演技、ライデル監督の手堅い演出が光る名編。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("波の数だけ抱きしめて", "波の数だけ抱きしめて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1982年の湘南を舞台に、学生最後の夏休みを本格的なFM局設立という夢にかける５人の男女の恋の行方を描く青春ドラマ。「私をスキーに連れてって」「彼女が水着に着がえたら」に続く“ホイチョイ・ムービー”３部作の最終章で、音楽は１作目に続いて松任谷由実が担当。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Vie Moderne", "モダン・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/55436/315x210.jpg?4022", 90, "ピュリッツァー賞を受賞した写真家レイモン・ドゥパルドンが、10年以上の歳月をかけて南仏セベンヌ村の農家の人々の暮らしを撮影したドキュメンタリー。気難しいが仕事には強いこだわりを持つ酪農家の老人、都会から農家に嫁いだ娘、自分の羊牧場を持つことを夢見て移住してきた夫婦など、自然や動物と共生する人々の素朴な生活を通し、人間本来の生活を描き出す。", "2010-06-26", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("或日の干潟", "或日の干潟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "鳥類写真の専門家であった下村兼史が、干潟を舞台に自然界の現象をありのままにフィルムに収めて高く評価された生物記録映画。カニがエサを捕る瞬間や、トビハゼやムツゴロウのとぼけた表情がほほえましかったり、また主役が鳥に移ると平和な干潟に弱肉強食のドラマも繰り広げられる。今ではすっかり様変わりしてしまった千葉県行徳海岸や佐賀県の有明海に、２年がかりのロケーションをして完成された。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2012", "2012", "", "", "http://image.eiga.k-img.com/images/movie/53616/315x210.jpg?3620", 158, "「インデペンデンス・デイ」「デイ・アフター・トゥモロー」のローランド・エメリッヒ監督が手がけるディザスター・ムービー。古代マヤ文明の暦が終わる2012年12月21日に地球が滅亡するという予言を題材に、世界中で地震や津波などの大災害が起こる中、必死に生き延びようとする人々を描く。ジョン・キューザックが家族を守ろうと奮闘する主人公に扮し、キウェテル・イジョフォー、ダニー・グローバー、ウッディ・ハレルソンらが脇を固める。", "2009-11-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chaos", "カオス", "", "", "http://image.eiga.k-img.com/images/movie/33726/315x210.jpg?4714", 107, "「トランスポーター」のジェイソン・ステイサムと「ブレイド」のウェズリー・スナイプスが、不可解な事件を追う刑事と事件を仕掛ける知能犯に扮し対決するサスペンスフルなクライム・アクション。シアトルの銀行で、武装強盗団による立てこもり事件が発生。強盗団のリーダー、ローレンツから交渉人として指名されたコナーズ刑事は、新人刑事デッカーと共に事件解決に乗り出すが……。共演は「父親たちの星条旗」のライアン・フィリップ。", "2006-11-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ渡り鳥・悪党稼業", "やくざ渡り鳥・悪党稼業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "小林旭・宍戸錠・渡哲也の三大スター揃い踏みのアクション映画。ある温泉町で、二組のヤクザが対立している。小林旭扮する流れ者のヤクザ・立原が、二つの組の間をうまく立ち回り、麻薬取り引きの金をせしめようとするが、脇から渡哲也扮する香港ジョーが割り込んでくる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("One Day in Europe", "ワン・デイ・イン・ヨーロッパ", "", "", "http://image.eiga.k-img.com/images/movie/34166/315x210.jpg?1515", 95, "世界中のサッカーファンが熱狂するヨーロッパのチャンピオンズ・リーグ決勝戦の日に起こった4つのエピソードからなるオムニバス・ドラマ。試合が行われるロシアのモスクワ、チームの地元であるトルコのイスタンブールとスペインのサンティアゴ・デ・コンポステラ、さらにドイツのベルリンを訪れた4組の外国人旅行者が様々なトラブルに巻き込まれる姿を、文化の違いやサッカーへの情熱を交えながらユーモアたっぷりに描き出す。", "2007-09-01", "7,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAIDOS DEL CIELO", "豚と天国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "南米ペルーの首都リマを舞台に、３つの世代と３つの異なる階級の人々の、３つの人生模様をクロスオーバーさせた、悲痛な笑いに富んだブラック・ユーモア映画。自分たちのために大理石の豪華な墓を作ろうと切磋琢磨する老夫婦の物語など、３つの現代の寓話はすべて、ペルー社会の危機的状況の現実的メタファーであるといえる。", "1989-01-01", "59,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アフリカの鳥", "アフリカの鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "中学受験を控えた仲良し５人組の対立と和解を描いた児童映画。多摩川の河原で、珍しい鳥を見つけた少年、強。愛鳥家の青年から、それがはるばるアフリカから飛んできたことを聞いた彼は、独力で鳥のことを書いたミニ新聞を発行する。ところが息子の勉強のじゃまになると、母親が怒り出して……。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN　TO　FIGHT", "七人のマッハ!!!!!!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "テコンドーやサッカー、ラグビー、体操などの各界のアスリートたちとテロ組織との死闘を描いたタイ製アクション。「マッハ！」のアクション指導者パンナー・リットグライが監督を務め、スポーツと格闘が融合した驚異の見せ場を演出する。車上のバトルなどの生身のスタントも迫力満点。 スポーツ選手たちの慰問で賑わう小村を、武装兵団が占拠して政府を脅迫。一度は人質になった慰問団の面々は、やがて反撃に転じ……。", "2005-12-03", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵ホームズ2　海底の財宝の巻", "名探偵ホームズ2　海底の財宝の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 24, "「名探偵ホームズ」は、宮崎駿が監督、演出などを務め、1984年からテレビ朝日系で放送されたテレビアニメシリーズ。本作と「青い紅玉の巻」がテレビ放送に先駆け「風の谷のナウシカ」と同時上映された。コナン・ドイルの「シャーロック・ホームズ」をもとにしているが、登場人物はすべて犬になっている。ホームズのライバル、モロアッチ教授が財宝が狙っていることを知ったホームズは、ドーバー海峡へと向かうのだった。", "", "76,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長い散歩", "長い散歩", "", "", "http://image.eiga.k-img.com/images/movie/1556/315x210.jpg?3958", 136, "06年度のモントリオール国際映画祭で最優秀作品賞を受賞した「少女～an adolescent」「るにん」に続く奥田瑛二監督の第3作。厳格すぎるゆえに家族とバラバラになってしまった松太郎は、質素なひとり暮らしを始める。しかし、隣室の少女が虐待されていることを知り、少女を救うため、自身の過去を清算するため、少女を連れて旅に出るが……。主演は日本を代表する名優・緒形拳。", "2006-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBBERY UNDER ARMS", "武装強盗団　～俺たちに明日はないII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "開拓の意気揚がる南半球の大地オーストラリア。世界各地から様々な人種がどっと押し寄せてくる中で金塊強奪、そして牛泥棒と悪業をつくして生き急ぐアウトロー兄弟がいた。彼らの短くも激しく燃えた青春をクールに描いたアクション編。", "1957-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I WANT YOU", "我が心の呼ぶ声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "第二次大戦後、アメリカ東部の小さな町に住む兄弟に届いた朝鮮戦争への召集令状。二人は妻、恋人と引き離されて戦場へと向かう。アメリカの典型的な一家を舞台に、徴兵される兄弟、苦悩する家族の心理を感動的に描いたホームドラマの佳作。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL LADRO DI BAMBINI", "小さな旅人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "母親に売春を強要されていた11歳の少女ロゼッタは、弟とともに孤児院に送られることになる。若い警官アントニオに護送され、姉弟のイタリア縦断の旅が始まる。それは彼女たちが人を信じることを学ぶ旅でもあった……。子供同士のロード・ムービーというありがちな設定だが、飾らない語り口が確かな感動を誘う。", "1992-01-01", "4,2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cast Away", "キャスト・アウェイ", "", "", "http://image.eiga.k-img.com/images/movie/1326/315x210.jpg?3955", 144, "チャックは速さを誇る宅配便“フェデックス”のシステム・エンジニア。世界中を駆け回り、システム上の問題解決に明け暮れる日々。一秒も無駄にしないことが信条の彼は、恋人ケリーとのデートも秒刻みだった。そんな彼はある時、飛行機事故に遭い、1人無人島に流れ着く。彼は恋人ケリーとの面影と新しい友達に見立てたバレーボールを支えになんとか生き延びる。そして4年が経った……。", "2001-02-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の講道館", "花の講道館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "岡山の起倒流道場の一番弟子・三島は、ある日道場を訪れた講道館の馬場に敗れる。場主の野田は時流を察して、三島を講道館へ送る決意をする。長谷川一夫が初めて出演した柔道映画だが、どっしりした構えなど意外とハマリ役であった。またミス日本に選ばれたばかりの山本富士子が出演したことでも話題となった。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PEOPLE I KNOW", "ニューヨーク最後の日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アル・パチーノ主演によるヒューマン・ドラマ。政治家やセレブリティに顔が利く華やかな職業に就きながらも、自らの生き方を見つめ直そうとする男の悲哀に満ちた心情を綴る。重要な慈善パーティーを控えるマンハッタンの敏腕パブリシスト、イーライ。過去30年間、第一線で活躍してきた彼は人生に疲れきっていた。死んだ弟の未亡人ヴィクトリアとの未来を夢みる彼だったが、やがて思わぬ怪事件に巻き込まれていく。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREESTYLE: THE ART OF RHYME", "フリースタイル／　アート・オブ・ライム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "DJとしても活躍する監督のK・フィッツジェラルドが、7年以上の歳月をかけて完成させたヒップホップ・ドキュメンタリー。現在のシーンを代表するアーティストたちへのインタビュー映像などを交え、即興のラップ・スタイルとして知られる“フリースタイル”の全容を浮き彫りにする。リズムに乗せて即興で言葉を紡ぎだすヒップホップのMCにスポットを当て、これまで映像化されにくかったヒップホップの一面に迫る。", "2005-04-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAMMA３", "ガンマー第３号 宇宙大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "日米合作SF作品。地球に突進する惑星を爆破するために、宇宙ステーション・ガンマー３号の隊員が派遣される。だが、任務を終えて基地に帰ってきた彼らは宇宙生物を持ち帰ってしまった。不死身の宇宙生物は人間を襲いはじめる。深作欣二監督がオール外国人キャストを迎えて撮り上げた異色のSFスリラーだ。", "1968-01-01", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金玉満堂／THE CHINESE FEAST", "金玉満堂 決戦！炎の料理人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "中華料理の最高峰“満漢全席”での対決に向けて奔走する料理人たちの姿を描いたグルメ・エンターテインメント。名のあるレストランに勝負を挑んでは乗っ取り、自社チェーンに加える“料理の超人集団”は、新たな狙いを名門“満漢楼”に定めた。挑戦を受けた満漢楼だったが、名料理人兼オーナーのオウが倒れ、料理人も逃げ出してしまい、娘のガーウェイと料理人見習いのサンが立ち向かうはめになる。二人はかつて中国一の名料理人として知られたリウ・キットを助っ人に迎えようとするが、やっと探し出したリウはアル中に身を持ち崩していた……。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWIST＆SHOUT", "ツイスト＆シャウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1964年のデンマーク。恋人の妊娠中絶という試練を乗り越え、本当の愛を見いだす高校生ビヨンと、親との葛藤に悩むビヨンの親友エリックを描く心理ドラマ。「子供たちの城」の続編にあたる作品で、主人公ビヨンの二年後の姿を追っている。L・シモンセンがモスクワ映画祭最優秀男優賞を受賞した。", "1984-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STUDENT BODY", "キル・ミー・テンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "代々、医者の家系のゲイブリエルにとって、有名医大合格は至上命令。落ちることなど絶対に許されない。ところが、ケアレスミスを犯して補欠の６番目になってしまう。そこで、補欠１番目から５番目のリストを手に入れ、ひそかに買収していくが、接触した彼らが次々と謎の急死を遂げ始めたから大変！　おまけに恋するカービーに買収がばれ、絶交を言い渡されるはめに。ゲイブリエルは、恋と自立と名誉挽回をかけて大奮闘する。ポップなセンスとドタバタのノリをミックスさせたサスペンス・ラブ・コメディ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔剣", "魔剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "殺気も見せず、ただ相手が切りかかると反射的に鞘走って敵を倒す必殺“夢想剣”の使い手、瀬波幻雲斎の悲劇の半生を描く。天涯孤独の身の上から俗世を離れて修行に生きてきた彼は、同情が仇となって豊臣秀次と石田三成の争いに巻き込まれる。さらに自らをかたきと狙う青年剣士・稲葉哲郎太に“夢想剣”を伝授し、果てていく。原作は五味康祐の芥川賞受賞作『喪神』。関西歌舞伎出身の中村扇雀が哲郎太を好演。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　FILS", "息子のまなざし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「ロゼッタ」のダルデンヌ兄弟が、濃密なサスペンスを込めて撮り上げた問題作。職業訓練所で働く測量技師の男が、かつて自分の息子を殺した少年と対面。怒りや戸惑いなど様々な感情に突き動かされる主人公の姿を、被写体に食らいつくような手持ちカメラで直視していく。職業訓練校で少年たちに大工仕事を教えるオリヴィエ。彼の前に現れた新入りのフランシスは、かつてわが子を殺した少年だった。オリヴィエはフランシスを自分のクラスに迎え入れるが、どう対処すべきか分からず苦悩することに。", "2002-01-01", "37,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トリック劇場版", "トリック劇場版", "", "", "http://image.eiga.k-img.com/images/movie/1716/315x210.jpg?1444", 119, "テレビ朝日系金曜ナイトドラマ枠史上最高のヒット作となった人気TVシリーズの劇場版。自称売れっ子天才奇術師奈緒子と、天才物理学者上田のコンビが、超常現象を解き明かすという、シリーズの設定はそのまま。300年に一度、大きな災いに見舞われるという言い伝えのある村で、奈緒子は神を演じて欲しいと依頼されるが、そこにはすでに自ら神だと名乗る3人の男がいた。彼らは神なのか、それともトリックなのか？", "2002-11-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レッスン LESSON", "レッスン LESSON", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "年上の謎めいた未亡人に心惹かれる、気鋭の若手モーター・ジャーナリスト。誘われるまま彼女とイタリア旅行に出かけた彼は官能的な愛の日々の中で、大人の男へと成長を遂げるが……。五木寛之の同名小説を映画化したラブ・ロマンス。大胆なラブ・シーンにも挑んだ秋吉久美子の体当たり演技も話題に。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪の渡り鳥", "雪の渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ヤクザの世界から足を洗い、船大工として地道に暮らしていた銀平だったが、悪業の限りをつくす丑松に再び刀を抜いた。雪の降りすさむなか、すさまじい斬り合いが始まる。様式美にのっとった大殺陣シーンが見もの。長谷川一夫お気に入りの作品。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SECRET AGENT", "こちらジェームズ・ブラウン探偵社", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "元CIAのエージェント、ジェームズは今は私立探偵を営んでいる。次々に舞い込む珍事件を息子のジェームズJr.と正体不明の美女アレックスとの３人で解決していた。ある日ジェームズは、以前関与していた国家機密に関する情報をつかんでしまう。渋々ながらも再びCIAの任務についたジェームズだが、失敗の連続、おまけに女スパイに誘惑までされて……。主役の探偵を演じているのは「スター・ウォーズ／帝国の逆襲」「バットマン」のバイプレイヤー、B・D・ウィリアムズ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROSEBUD", "ローズバッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "パレスチナ・ゲリラによる誘拐事件を描くサスペンスもの。“黒い九月”と名乗るゲリラが、リビエラに停泊中の大富豪のヨットを襲い、名門の娘５人を誘拐。彼らは娘たちを裸にして写真を撮り、その父親たちへ様々な要求をする……。CIA情報員に扮するオトゥールの活躍が見もの。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AIRPORT'77", "エアポート'77　バミューダからの脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「大空港」「エアポート1975」に続く「エアポート」シリーズ第３作。大富豪の所有するジャンボ・ジェット機がハイジャックされ、魔のバミューダ海域に不時着。絶望とパニック状態のなか、機長らの決死の努力で救援隊が到着、乗客たちは奇跡的に救助されるのだった。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギプス", "ギプス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "平凡な人生を歩んでいた和子は、ある日ギプスをはめた奇妙な女・環に出会い、その不思議な魅力に取り憑かれてか、日々彼女の部屋へ通うようになるが……。全編デジタル・ビデオで撮影された“ラブシネマ”第５作のアブノーマルなラブ・サスペンス。女同士の不可思議な愛憎が、倒錯しつつも奇妙な透明感を与えている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOSTAGE", "ダーティ・ゲーム 堕ちた工作員", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "原作は第二次大戦中、実際の情報活動に従事していたイギリスの作家T・オールビュリーの『堕ちた工作員』。命令とあらば、どんな汚い任務もこなしてきた英国秘密情報員ジョン・レニー。今回の指令は、クルド人ゲリラに拉致された防衛分析家を救出することだった。いつものように冷淡に任務を遂行していくジョンだったが、任務を超えて愛し合う女性と出会う。そこから彼の苦悩が始まった。任務終了後、疑問を抱いたジョンは、辞任を申し出る。しかし、秘密を知り過ぎた彼に政府の魔の手が……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Train", "テラートレイン", "", "", "http://image.eiga.k-img.com/images/movie/54406/315x210.jpg?3778", 94, "東ヨーロッパを訪れたアメリカ人大学生グループが、偶然乗り込んだ列車内で想像を絶する恐怖に襲われるサバイバルホラー。レスリングの国際大会に参加するため、チームメイトたちと東ヨーロッパへやって来た女子大生アレックス。リトアニアで列車に乗り遅れてしまった彼女たちは、そこに居合わせた女性医師の案内で別の列車に乗車。しかし、列車内には不気味な空気が流れており……。ヒロイン役に「ゴーストワールド」のソーラ・バーチ。", "2009-05-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　SCARLET　LETTER", "スカーレットレター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "「八月のクリスマス」「シュリ」などで誠実なイメージの役柄を演じてきた韓国の大スター、ハン・ソッキュが、妻と愛人の二股をかける刑事役で新境地に挑んだフィルム・ノワール。そんな主人公が、写真館で発生した奇怪な殺人事件の捜査を担当したことをきっかけに、破滅への道を転がっていく様を官能的なラブ・シーンを盛り込んで描写。「ブラザーフッド」などで知られる美人女優イ・ウンジュがジャズシンガーである愛人役に扮し、情感豊かな演技を披露。これが惜しくも自殺した彼女の遺作となった。写真館店主が殺され、被害者の美人妻ギョンヒが容疑者となる。しかし刑事ギフンの捜査は混迷し、貞淑な妻がいながら愛人カヒとの関係を引きずる彼の私生活も不安定になっていく。", "2005-05-14", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAST GASP", "超常殺人者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "連続殺人事件が起き、死体はいずれもアキレス腱が切られていた。犯人の手がかりは一向に掴めない。建設業者のレスリーも、殺人現場に呼び出され、アキレス腱を切られてしまい、更に不思議なことに、それ以降の記憶をなくしてしまっていた。一方、行方不明の夫を探しているノラに雇われた探偵レイが、ある情報を元にレスリーの屋敷を訪ねたまま消息を断つ。そして、新たな殺人が……。事件のカギはレスリーが握っていると確信したノラは単身、レスリーの屋敷に乗り込むが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAR TREK VI：THE UNDISCOVERED COUNTRY", "スター・トレック VI 未知の世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "惑星連邦の宿敵クリンゴン帝国は絶滅の危機に瀕し、助けを求めて和平交渉を申し込んできた。エンタープライズ号は救助のためクリンゴン軍と合流するが、誰も命令しないのにエンタープライズ号は相手に攻撃を開始した……。スタートから25周年を迎えた人気TVシリーズ劇場用第６作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOOGEYMAN", "ブギーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「スパイダーマン」のサム・ライミ監督が製作したホラー。アメリカの有名な架空の怪物をモチーフにして、現実と幻想が交錯するドラマが展開する。ライミに見出された新鋭スティーブン・ケイ監督が、悪夢の物語を緊迫感たっぷりに演出。クローゼットから出てきた怪物に父を連れ去られた幼少期の記憶に、今も悩まされるティム。母の葬儀のために久しぶりに実家に戻った彼は、因縁の生家でさらなる悪夢に襲われる。", "2006-06-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とりたての輝き", "とりたての輝き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1980年度城戸賞佳作入選のシナリオを作者・浅尾政行が自らメガホンを取った意欲作。サラ金の取り立てを仕事とする若者と中年男、この二人の姿を通じて社会の一面を浮きぼりにする。世相を反映した脚本の切り口は面白いが、演出は平板で新しさに欠けた。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎の告白", "男はつらいよ・寅次郎の告白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「…ぼくの伯父さん」「…寅次郎の休日」に引き続き後藤久美子が３連続でマドンナ登場。日本海沿岸の鳥取を旅する寅次郎だったが、そこで偶然泉と再会。母親の再婚を素直に喜べず、その寂しさを紛わすために鳥取に来たのだった。一方、それを泉からの絵ハガキで知った満男も鳥取へと向うが……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MATA HARI", "マタ・ハリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "G・ガルボが、第一次世界大戦下の悲劇の女スパイを演じた作品。パリで人気のダンサーは、実は連合国の工作員。しかし、秘密文書を手に入れるために近づいたロシアの将校と、彼女は燃えるような恋に落ちてしまう……。スパイ・サスペンスではなく、メロドラマというのがいかにもガルボの主演作らしい。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMAZING GRACE AND CHUCK", "サイレント・ボイス 愛を虹にのせて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "リトル・リーグのエース、チャックは父が働くミサイル基地で核兵器の恐ろしさを目撃、核廃絶の日まで大好きな野球をやめると宣言した。その新聞記事を見たプロ・バスケットのスーパースター、アメイジングがチャックの仲間入りをしたのをきっかけにスポーツ界のスターたちが次々と続き、一大ムーブメントとなっていく……。大御所G・ペックも米大統領役で登場する感動のドラマ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREE WILLY", "フリー・ウィリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "狭い水族館に閉じ込められたオルカのウィリーと、孤児院を脱走した少年ジェシーは出会ったとたん、目に見えぬ絆で結ばれた。ウィリーで金もうけをたくらむ大人から自由になるため、ジェシーはウィリーを海に帰す決心をする。全米で動物保護運動を再燃させたエコロジー・ムービー。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の哀しみ", "夜の哀しみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "港町の水産加工工場で働く主婦・登世は、二人の子供を育てながら東京に出稼ぎに行っている夫の留守を守る平凡な主婦。地道でつましい暮らしは一見、平穏に見えたが、登世は夜ごとに感じる激しい性衝動を抑え切れずにいた。これまで貞淑な女性役が多かった平淑恵が、不倫に走ってしまう主婦を熱演した家庭崩壊劇。原作は三浦哲郎の同名小説。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("満月 MR.MOONLIGHT", "満月 MR.MOONLIGHT", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "大森一樹が原田康子の原作をもとに、現代の札幌にタイムスリップしてしまった江戸時代の武士・小弥太と、ひょんなことで彼と出会った高校教師・まりとの素敵な恋と冒険を描いたファンタジー。高校の生物学教師というヒロインを演じる原田知世は彼女のデビュー作「時をかける少女」のラストを思い起こさせる。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("扉を開けて", "扉を開けて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "女子大生で超能力者の峰岸美弥子は、テレポート能力を持つ斉木沓、ライオンに変身できる山岸桂一郎とともに、満月の夜、異次元へと運ばれてしまう。そこで美弥子は、“中の国”の伝説の女勇者の生まれかわりであるとされ、西の国との戦いに巻き込まれる。東の国の王女・ディミダも彼女に味方し、ついに西の国の城に攻め込んだ美弥子たちだったが、そこで知った敵の大将軍の正体とは……。女流SF作家・新井泰子の同名ジュニア小説を映画化した、初めてのアニメ化作品。女性を主人公としたヒロイック・ファンタジーで、作画も女性スタッフが中心となって健闘を見せた。ただ、原作のデリケートな雰囲気は、あリがちなイメージに置きかえられてしまい、やや受ける印象にちぐはぐなものがある。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MASCHERA", "ラ・マスケラ　仮面の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "18世紀のイタリア。貴族青年レオナルドは、旅芸人一座のマドンナ、少女イリスに一目惚れ。ところが、気位の高い彼女は、にべもなく彼を拒絶する。悩んだレオナルドは、仮面（マスケラ）をつけ、再びイリスの前に現れる。仮面の神秘性漂う、幻想味豊かなラブ・ロマンス。「眺めのいい部屋」のH・B・カーターが、魅力たっぷりに好演。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コント55号 世紀の大弱点", "コント55号 世紀の大弱点", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "“欽ちゃん”と“二郎”さんというそれぞれ愛称で親しまれたお笑いコンビ、コント55号。人気絶頂期だった当時、彼らが初の本格的な映画に進出したシリーズの第1弾。笑い過ぎに注意したいギャグ＆アドリブ連発、時代を彩ったユニークな出演者たちとの共演で魅せるウルトラ・コメディだ。週刊誌の記者とカメラマンのコンビがある日、偶然から傑作小説の原稿を拾い、架空の名前で雑誌に発表したところ大評判を呼ぶ……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRACULA’S WIDOW", "ドラキュラ・ウイドー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ドラキュラ伯爵が最も愛した未亡人ヴァネッサが、400年の眠りから覚めて現代によみがえった。夫の死を信じきれず、現代の街をさまよい歩くのだが……。彼女に愛される若者と、ドラキュラに渡すまいとする若者の恋人、さらには伯爵の宿敵ヘルシング教授の子孫まで登場する騒動を描く。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　BARON　DE　L’ECLUSE", "ギャンブルの王様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ジョルジュ・シムノンの原作をもとに、プレイボーイ男爵の華麗な生活をコミカルに描く。ギャンブル好きのアントワーヌ男爵は、その日暮らしの気ままな男。ある日、大金を勝ち取った彼は、元恋人を亭主から奪ってオランダへと旅に出るが……。自由奔放でユーモアのある主人公を、名優J・ギャバンが大人の男の余裕を漂わせて魅力たっぷりに好演。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("祈り　サムシンググレートとの対話", "祈り　サムシンググレートとの対話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Feast II: Sloppy Seconds", "フィースト2 怪物復活", "", "", "http://image.eiga.k-img.com/images/movie/54526/315x210.jpg?2193", 97, "全世界で大ヒットを記録したパニック・ホラー「ザ・フィースト」の続編。謎の人喰いモンスターと人間たちが死闘を繰り広げたテキサスのバーに、ハーレーに乗ったバイカー・クイーンが出現。双子の妹がモンスターをおびき寄せるために命を落としたと知った彼女は、生存者たちに復讐するため街へと向かう。しかし、街では増殖したモンスターが人間たちを襲い続けており……。前作に続き新鋭ジョン・ギャラガー監督がメガホンを取る。", "2009-06-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLAMDANCE", "スラムダンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アートの世界が現実になってしまうような、新しいフィルム・ノワールの香りが漂う作品。売れない漫画家C.C.ドルードは、約束を守らない男。友人の経営するアートクラブのパーティーで、彼はヨランダと知り合うが、ある日彼女が殺され、ドルードは殺人事件の容疑者にされる。彼は自分を守るため、素人探偵になるが……。監督は「スモーク」のW・ワン。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Telitalalat", "ウィニングチケット　遥かなるブダペスト", "", "", "http://image.eiga.k-img.com/images/movie/55076/315x210.jpg?4097", 104, "1956年、フォークリフトの運転手ベーラは、家族と下宿人の美女・ロージカと慎ましく暮らしていた。そんなある日、ベーラはサッカーくじで大穴を当て大金を手に入れるが、その同日に労働者や農民が厳しい生活への不満を爆発させた「ハンガリー動乱」が勃発する。大金を家に持ち帰るはめになったベーラは、その使い道に困り果て……。第16回東京国際映画祭コンペティション部門へ出品。劇場公開は2010年。", "2010-02-13", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兩個只能活一個／THE ODD ONE　DIES", "パラダイス！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "その日暮らしの不良少年モーは、大金欲しさに面倒な殺しの仕事を引き受けてしまう。そんな時、偶然モーに大金が舞い込み、彼は自分で別の殺し屋を雇うのだが、やって来たのは刑務所から出所したばかりの女、カルメンだった。二人は一緒に過ごすうちに、次第に愛し合うようになるのだが、指定された殺しの日も近づいていた……。金城武がナチュラルな持ち味を生かした、風変わりな青春ラブ・ストーリー。これがデビュー作となる監督P・ヤウはバイオレンスな設定をポップな味わいで活写。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンの弥次喜多", "エノケンの弥次喜多", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "中川信夫演出による、エノケンの喜多八、二村定一の弥次郎兵衛によるおなじみ珍道中物語。武田勝頼の五十万両の埋蔵金をめぐり、お姫様や泥棒を巻き込んでの大騒動。面白さ満点の出来ばえで、戦後にリバイバルされた。なお、脚本を実際に書いたのは、当時助監督の市川崑であった。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SKYGGEN AF EMMA", "エマ EMMA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "裕福な家庭で、何不自由なく育てられている女の子、エマ。しかし、建前ばかりで実は両親が自分に対して本来の愛情を示していないことを察知した彼女は、狂言誘拐を企み、親に自分の心情をアピールする。美しい映像と叙情的な音楽、ヨーロッパの文化的な薫りが漂う優しさに満ちあふれた作品。", "1988-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユダ", "ユダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("溺れる人", "溺れる人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "平凡な日常生活に突如入り込んできた悪夢を、シュールに描いたラブ・サスペンス。冷めた風呂の底に沈んでいる妻クミコを発見したトキオ。だが119番には連絡せずに、死体を叩いて“蘇れ！”と叫んだ後、酒を飲んで寝てしまう。翌朝、目を覚ましたトキオはクミコに介抱されている自分に驚く。一度死んだ人間がなぜ生き返ったのか？　その日を境に、トキオの生活は少しずつ歯車が狂い始める。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA LOI DU SURVIVANT", "生き残った者の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "2004年に他界したフィルム・ノワールの第一人者、J・ジョヴァンニのデビュー作。コルシカ生まれの彼が、自らの生きざまを鮮烈に描き出した一編。男二人と女一人の愛と友情を謳ったロベール・アンリコの監督作「冒険者たち」は、同じ原作をもとにした物語の前日譚。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLASH GORDON: THE DEADLY RAY FROM MARS", "フラッシュ・ゴードン 宇宙レーザー戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "謎の洪水、地震、ハリケーンが地球を襲う。この現象を探るためゴードンはモンゴ星へ向かうが、途中火星にある兵器が地球の大気から空気を抜き取っていることに気付く。本編は「火星地球を攻撃す」という題名で日本初公開された後、手塚治虫の『ロック冒険記』のヒントとなった。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二人の武蔵", "二人の武蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "五味康祐原作の同名小説を映画化。武蔵と名のる二人の剣豪の姿を描いている。その二人とは、作州の平田武蔵と藩州の岡本武蔵。彼らは、同じ道場を訪れたことから知り合い、それぞれ剣をきわめてから雌雄を決することを誓い、修行の旅に出た。だが彼らの動向を見守る一人の男、佐々木小次郎がいた……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DISH", "月のひつじ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1961年、アポロ11号が人類初の月面歩行に成功。この歴史的瞬間を衛星中継で世界に伝えたのは、オーストラリアの片田舎の天文台だった……！ 突然、世界的規模の任務を命じられたオーストラリアの科学者たちの奮闘を描く実話の物語。アポロ11号を見失ってしまうハプニングや、内気な青年の初々しい恋物語、一大イベントに舞い上がる町の混乱ぶりなど、素朴な田舎町のほのぼのとした人間模様がユーモラスに綴られる。", "2000-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関雲長", "三国志英傑伝　関羽", "", "", "http://image.eiga.k-img.com/images/movie/57496/315x210.jpg?1495", 109, "「三国志演義」に登場する劉備の部下の武将・関羽が主人公のエピソード「過五関、斬六将」を映画化した歴史アクション。劉備と敵対する曹操の捕虜になってしまった関羽だったが、曹操軍と袁紹の率いる軍勢が刀を交えた「白馬の戦い」で曹操を助け、勝利に貢献する。曹操は関羽を部下に引き入れようとするが、劉備の元へ戻ることを譲らない関羽の願いを聞き、6つの関所を手形なしに通れるよう手配する。晴れて劉備の元へ戻ろうとした関羽だったが、皇帝が関羽殺害の命を出したため、道中で幾度となく命を狙われることになる。主演は「イップ・マン」「孫文の義士団」など主演作が続くドニー・イェン。監督は「インファナル・アフェア」シリーズを手がけたフェリックス・チョンとアラン・マック。", "2012-01-14", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("kasanegafuti", "kasanegafuti", "", "", "http://image.eiga.k-img.com/images/movie/58046/315x210.jpg?3802", 27, "トラッシュ・カルチャーの専門誌「TRASH-UP!!」と3人の映画監督がコラボレーションした上映プログラム「コラボ・モンスターズ!!」の1作。真は恋人の豊に結婚をせまるが、「真と結婚すると父が不幸な死に方をする」と言われ、その一方で豊の妹の園に「父は既に殺されている」と言われる。姉妹の食い違う話に混乱する真は、育ての親である叔父から驚くべき事実を知らされて……。監督は「完全なる飼育　愛の40日」「INAZUMA 稲妻」の西山洋市。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chicken Little", "チキン・リトル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "何をやっても失敗ばかりの小さな男の子チキン・リトルは、ある日、街に現れたエイリアンの宇宙船に潜り込んでしまい、迷子になったエイリアンの子供と出会うのだが……。ピクサー製以外のディズニー・アニメとしては初のフルCGとなった作品。監督は「ラマになった王様」のランディ・フルマー。主人公の声は、監督作「Garden State」がサンダンス映画祭で話題となった俳優ザック・ブラフ。", "2005-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE SORELLE", "姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "結婚した妹と二年ぶりで再会した姉は、それまで抑えていた感情があふれ、自らの本当の欲望に気付く。それはやがて殺意に変わって……。N・ドロンとS・ストラスバーグが、深くデリケートな姉妹愛の世界を、快楽や背徳のムードたっぷりに繰り広げる官能ドラマ。", "1969-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DESPERATE HOURS", "必死の逃亡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "インディアナ郊外に住む平凡な家庭に、突然３人の脱獄囚が押し入って来た。仲間の情婦が高飛びの金を届けるまで家族を人質にするという脱獄犯たち。警察の捜査網はじわじわと迫って来る。彼らの運命は？　極限状態に置かれた犯人と家族との息詰まるようなドラマを緊密な演出で描いたサスペンス。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋人たちは濡れた", "恋人たちは濡れた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "日活ロマンポルノ草創期に花開いた神代辰巳監督の“濡れた”シリーズの一編。うらぶれた漁村を舞台に、名前を捨て、自らの過去も未来も放棄した映画館のフィルム運びの青年の虚無に彩られた日々を描く。陰鬱な海の光景と浜辺の砂のざらついた質感が印象的。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Annie", "アニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "　1930年代、孤児院で暮らすアニーは明るくて元気な女の子。つらいことも多いが、いつか両親が迎えに来てくれると信じていた。そんなある日、アニーは億万長者ウォーバックス氏の豪邸に招かれることに。金儲けしか興味のなかったウォーバックスは無邪気なアニーと過ごすうちに次第に変わっていくが……。ブロードウェイの大ヒットミュージカルを映画化した感動作。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬、走る／DOG RACE", "犬、走る／DOG RACE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "故・松田優作の主演が予定されていた1980年代の幻の企画を、崔洋一監督が1990年代のテイストで映画化。無国籍な街と化した新宿・歌舞伎町を舞台に、本能のままに生きる人々の姿をエネルギッシュに描き出す。マフィアに情報を流しては金をせしめる悪徳刑事・中山は、犯罪に手を染める中国人女性の桃花と恋人関係にある。相棒の情報屋、秀吉は中山と行動をともにしつつも、彼には内緒で桃花に手を貸していた。そんなある日、桃花が何者かに殺害された。復讐を誓った中山と秀吉は、凶暴な韓国系マフィアを敵に回して大芝居を打つが……。善悪では割り切れない複雑な人物像を捉えつつ、街の混沌を浮き彫りに。新宿中を駆け回るクライマックスの疾走感が、強烈な印象を与える。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE AFFAIRE DE FEMMES", "主婦マリーがしたこと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ナチ占領下のフランス。主婦のマリーは生計のために法律で禁じられている“妊娠中絶”を女たちに施していた。だが夫に密告され逮捕された彼女を待っていたのはギロチン台だった……。ヌーヴェル・ヴァーグの鬼才C・シャブロルが久々に往年の冴えた演出力をふるった彼の最高傑作とも呼ばれている力作。自分の幸福と自由のために、“仕事”として中絶を行った無垢なマリーを、I・ユペールが抑えた演技で好演している。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hang 'Em High", "奴らを高く吊るせ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1889年のオクラホマ。無実の罪で無法者たちから私刑を受けた男が、その復讐のために彼らを皆殺しにする。「荒野の用心棒」など、当時イタリアで名を馳せたイーストウッドの凱旋帰国第１作。本場アメリカで主演した、マカロニ・ウエスタン調の作品。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逆襲！スケ番☆ハンターズ　地獄の決闘篇", "逆襲！スケ番☆ハンターズ　地獄の決闘篇", "", "", "http://image.eiga.k-img.com/images/movie/55386/315x210.jpg?4600", 75, "長い戦いで傷つき疲れ果てたアサミは、ヤクザハンターの師匠・猪熊が営むバーに帰ってくる。しかし、店は地元ヤクザ・小龍会の横行により、昔の面影もないほど荒れ果てていた。賭博場を作るため、冷酷無比の殺し屋アキラを用心棒に、反対する者を一掃していく小龍会。初代ヤクザハンターの美樹も惨殺され、愛する者を失ったアサミは復しゅうを誓う。監督はショートフィルム「やくざハンター」で注目を浴びた奥田真一、主演に「片腕マシンガール」の亜紗美。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸福号出帆", "幸福号出帆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "無意識のうちに兄弟愛以上の愛情を感じ合うようになったオペラ歌手を目指す三津子と兄の敏夫。ある日新聞で母の昔の愛人であるイタリア人の莫大な遺産が正妻に渡ることを知り、遺産を分けてもらおうと邸に乗り込むのだが……。風俗的に古びた部分を巧みに現代化した三島由紀夫作品の映画化。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代やくざ・新宿の与太者", "現代やくざ・新宿の与太者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ムショ帰りの一匹狼・勝又五郎は、同じ与太郎仲間の秀男と組んで、ユスリ、タカリなどをしながら新宿の街で次第にのし上がっていく。新宿のくろがね興行からライバルの郷原組への挑発を請け負う五郎の、狂犬ぶりが見どころ。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARD MEN", "ハードメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "チンピラ３人組の一夜の葛藤をユーモラスに描いた犯罪映画。借金の取り立て屋トーンは、子供の誕生を機にヤクザから足を洗うことに。だが、ボスはトーンの仲間二人に彼の暗殺を命じた。二人組はトーンと夜遊びをしながら暗殺の機会をうかがうが、友情と任務の狭間で思い悩み手を出せず……。おどおどしたチンピラたちのユーモア、凄まじいバイオレンスが混在し、観る者の目を惹きつける。1990年代後半のイギリス映画界の勢いを伝える、パワフルな一編。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bank Job", "バンク・ジョブ", "", "", "http://image.eiga.k-img.com/images/movie/53586/315x210.jpg?2915", 110, "1971年のイギリスで実際に起きた王室スキャンダルに関わる銀行強盗事件を「13デイズ」「世界最速のインディアン」のロジャー・ドナルドソン監督が映画化。借金取りに追い立てられる毎日を送るテリーのもとに、昔の恋人マルティーヌが現れ、銀行強盗の話を持ちかける。テリーは愛する家族のためマルティーヌの計画に乗るが、その話には裏があった……。主演は「トランスポーター」シリーズのジェイソン・ステイサム。共演にサフロン・バロウズ、デビッド・スーシェら。", "2008-11-22", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユキがロックを棄てた夏", "ユキがロックを棄てた夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "新鋭の長崎俊一が、日大芸術学部映画学科在学中に撮り上げ、その独自のスタイルで注目を集めた16ミリ作品。ロック・シンガーのユキは、仲間とロックを棄て、メジャー歌手を目指す。だが仲間たちは、そんな彼女を引き戻そうとし、争いが勃発。やがて、それはカーチェイス、銃撃戦へとエスカレートして……。アクション仕立てだが、仲間がゴロ寝するアパートの描写など、細部へのこだわりに才気がうかがえる。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAND AND DELIVER", "落ちこぼれの天使たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "実話に基づいた感動の学園もの。荒廃したハイスクールに赴任して来たエスカランテ。彼はまず無気力な生徒たちに、週30時間勉強するという約束をさせる。そして彼は時には優しく、時にはおどして教えていき、ついに生徒たちに大学修了試験を受けさせるが……。E・J・オルモスは、本編の演技で1988年度米アカデミー主演男優賞にノミネートされた。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上海バンスキング", "上海バンスキング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "劇団“自由劇場”のヒット・ミュージカルを、深作欣二が松坂慶子、風間杜夫という、「蒲田行進曲」のキャストを集めて映画化。1936年、上海にあるダンスホール“セントルイス”に集うジャズマンたちの、悲喜こもごもの人間模様。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANNY PELOPAJA", "ファニー～紫の血の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "暴力的でサディスティックな警部ガレゴと関係を続けるファニー。彼女には、獄中の恋人を脱獄させる目的があった。だが、脱獄した恋人をガレゴに殺され、彼女の胸には復讐の炎が燃え上がる。３年後、ファニーとガレゴは再会するが……。男と女が愛と憎しみの中で繰り広げるバイオレンス。", "1983-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les neiges du Kilimandjaro", "キリマンジャロの雪", "", "", "http://image.eiga.k-img.com/images/movie/57686/315x210.jpg?4666", 107, "「マルセイユの恋」「幼なじみ」のロベール・ゲディギャン監督が、生活苦にあえぎながらも隣家の孤児を引き取る漁師夫婦を描いたビクトル・ユゴーの長編詩「哀れな人々」に着想を得て描いた人間ドラマ。マルセイユの港町に暮らすミシェルは、労働組合の委員長として組合員のため戦い、妻マリ＝クレールもそんな夫を長年にわたり支え続けてきた。しかし、ミシェルはついにリストラされてしまい、さらに自宅に強盗が押し入るという事件まで起こる。ミシェルは、自分と一緒に解雇された元同僚の青年クリストフが犯人であることを突き止めるが、クリストフが幼い弟と借金を抱えて苦しんでいることを知り……。", "2012-06-09", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EDIPO RE", "アポロンの地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ギリシャ神話で有名なオイディプス王の伝説を、現代的な視点から鮮烈な映像美で描いた秀作。不吉な運命の予言を背負ったオイディプス王が、そうとは知らずに父親を惨殺し、母親と関係。地獄に落とされ、真実を知ったオイディプス王は、自分の両眼をえぐるという異常な物語だ。", "1967-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きみが輝くとき", "きみが輝くとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "先天性四肢障害で生まれつき左腕のない実在の少年をモデルに、その周辺に様々なエピソードを配して本人が主演したサッカー少年物語。手を使わなくてもいいからと始めたサッカーで、少年が全国大会で優勝するまでを、自然な動きで捉えた感動作。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPERATION LEOPARD", "レッド・ベレー 史上最強の傭兵部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "中央アフリカのコルクシという町で突如反乱軍が決起したため、300名ものヨーロッパ人が幽閉されてしまった。そこで人質救出のために特別組織された傭兵部隊が現地へ向かう。激しい戦闘の末、果たして任務遂行はなるか……。アクションと哀感にあふれた戦争映画。", "1979-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REACH FOR THE SKY", "撃墜王ダグラス　～殴り込み戦闘機隊～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "両脚を失いながらも、第二次世界大戦の空の英雄になったダグラス・バーダーの伝記映画。大空の魅力に取り憑かれた男の生きざまをさわやかに描き上げた。「007」シリーズなどを監督しているL・ギルバートが、迫力あふれる飛行シーンを盛り込んで男性的ロマンチシズムを追求した佳作。", "1956-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死にぞこないの青", "死にぞこないの青", "", "", "http://image.eiga.k-img.com/images/movie/53776/315x210.jpg?2885", 95, "小学6年生のマサオは、新任教師の羽田やクラスメイトから壮絶なイジメを受けていた。そんな彼の前に、全身真っ青で傷だらけの少女・アオが出現。アオにそそのかされて羽田殺しを決意するマサオだったが……。人気作家・乙一のホラー小説を「ALWAYS／三丁目の夕日」の名子役・須賀健太主演で映画化。不気味な少女・アオに「神様のパズル」の谷村美月、マサオの担任・羽田に「ヒートアイランド」の城田優。", "2008-08-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BETHUNE", "黄土の英雄 −軍医ベシューンの生涯−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "最前線の野戦病院で働く医師の心の葛藤を通して、戦争の愚かさを描く人間ドラマ。1938年、日本と戦争中の中国にカナダ人の医師がやって来る。酒と女を愛し、激しい個性を持つ理想家シューン。彼は、病院のずさんな衛生状態に激怒、医療レベルの向上に乗り出す。そして多くの人命を救いながら、医師や看護婦の育成をし、中国人に医学を教えていく。そんな彼も患者の傷から伝染病に感染。ついに息を引き取る。中国人たちの手によって行われた葬儀は盛大なものであった。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仇討選手", "仇討選手", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "のちに名作「飢餓海峡」などで知られる豪快、骨太の巨匠、内田吐夢が撮った初の時代劇。市井の平凡な男が殿様に無理やり侍に仕立てられ、仇討ち計画に参加させられるのだが、最後は逆に仇討ちされて死んでいく……。武士道を題材に、喜劇と悲劇を体現する主人公の姿を一種の皮肉を込めた視点から映し出す。内田監督とのコンビで知られる名脚本家、小林正のユーモア・センスも冴えわたる傑作。", "1931-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE UNNAMABLE", "ヘルダミアン／悪霊少女の棲む館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "アメリカの代表的な幻想ホラー作家H・P・ラヴクラフト原作を映像化。カルトにして今観ても鮮烈なホラー作品。学生たちがある館に忍び込んだが、そこは呪われた家だった。屋根裏に棲む得体の知れない怪物が、じわりと彼らに襲いかかり、一人また一人と血祭りに上げていく。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("網走番外地・荒野の対決", "網走番外地・荒野の対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "西部劇タッチの豪快なアクションをメインにした一編。北海道の馬相場を一手に握ろうとして、手段を選ばぬ権田一味のやり口に、橘真一は鬼寅・栗田らとともに権田牧場に殴り込む。嵐寛寿郎が見事な手綱さばきを見せる。さすが鞍馬天狗！", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Invasion of the Body Snatchers", "ボディ・スナッチャー／恐怖の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "　カリフォルニア州の小さな町。医師マイルスの周辺で異変が。人々は肉親が別人になってしまったと訴え始める。やがて明らかになる驚愕の事実。マイルスは、巨大な豆のサヤの中で人間が複製され、それが本物と入れ替わっていたことを知る！　未知の生命体による侵略の恐怖を描く。78年、そして93年にもリメイクされた。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAINS", "ストリート・ウォリアーズ 戦え！血の一滴まで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "サウス・シカゴの無法地帯に迷い込んだ４人の男女。そこは二大ギャング組織が対立する戦争ゾーンだった。自らの生命を守るため４人の若者は、武器を手にして決死の逃走を開始する。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last Sunset", "ガン・ファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "　退役軍人のジョンは牛を引き連れ、妻のベル、そして娘のミッシーとともにメキシコの牧場を後にし、テキサスを目指すことに。それをベルのかつての恋人ブレンダン、そしてお尋ね者の彼を追う保安官ダナが手伝う。旅の途中、ジョンが射殺されるなど危機が続く。そんな中、ブレンダンはミッシーに想いを寄せるようになるが……。男たちの闘い、そして悲劇的な愛を描いた西部劇。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES CAPRICES D’UN FLEUVE", "川のうつろい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「リディキュール」「女優マルキーズ」などに出演している名優B・ジロドー監督、主演による美しい愛の物語。1787年、貴族ジャン・フランソワは、決闘でルイ14世の友人を殺したため国外追放となる。西アフリカの小さな植民地の総督となった彼は、献上された黒人少女アメリーを我が子のように可愛がる。やがてその思いは深い愛情へと変わっていき……。祖国を離れアフリカの大地へやって来た一人の男が、真実の愛を見つけるまでを詩的な映像で綴る。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Little buddha", "リトル・ブッダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "　1990年代シアトル、9歳のジェシーの家にラマ僧が訪ねてくる。少年がチベットの高僧の生まれ変わりだと言い、ブータンにある僧院へ来ることを諭す。初めは信用しなかった父親だが、ある事件をきっかけに息子と旅することを決意する。少年の旅と2500年前のシッダールタ王子の物語が交錯する、壮大な歴史絵物語。古代インドのシークエンスに登場する王子には、キアヌ・リーブスが扮している。", "1994-12-31", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GERMANIA ANNO ZERO", "ドイツ零年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "「無防備都市」「戦火のかなた」に続いてR・ロッセリーニが、ベルリン・ロケで撮影したネオ・レアリスモを代表する１作。第二次大戦直後、焼けあとのベルリンの廃墟に生きのびた一家がいた。父は病に伏し、娘は外人相手に体を売って家計を助ける。ナチ党員の生き残りの長男は毎日ゴロゴロしている。末っ子のエドムント少年はヤミ屋の手先として働いていたが、ある日昔の小学校の教師に出会い、弱い者は死ぬべしという思想を吹き込まれ、病床の父に毒を盛る……。オールロケ、俳優は全員素人で作られ、作りものの感情を排してあくまで冷徹な目で荒廃した人間性を見つめ、後の世代に大きな影響を与えた作品。", "1948-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Der Freischutz", "魔弾の射手", "", "", "http://image.eiga.k-img.com/images/movie/57546/315x210.jpg?2053", 142, "ウェーバーによるドイツ・ロマン派の名作オペラ「魔弾の射手」を映画化。ナポレオン戦争をともに戦った2人の狩人マックスとカスパールは、そろって森林官クーノーの娘アガーテに恋していた。アガーテはマックスを愛していたが、2人が結ばれるには領主の御前で開かれる射撃大会でマックスが優勝しなければならない。スランプ気味で射撃に自信をなくしているマックスに、カスパールは悪魔に魂を売って手にした百発百中の魔弾を使うように話をもちかけるが……。", "2012-03-10", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Witness for the Prosecution", "情婦", "", "", "http://ecx.images-amazon.com/images/I/516gcdVQcmL._SL210_.jpg", 117, "　アガサ・クリスティの短編「検察側の証人」の映画化。主演のマレーネ・デートリッヒたっての希望で、監督にはビリー・ワイルダーに白羽の矢がたった。ワイルダーにとっては初の法廷劇だが、彼の作品特有の悪女は今作でも健在。ロンドンで、裕福な未亡人の刺殺事件が起きる。容疑の疑いがかけられたレナードは、ロンドンきっての敏腕弁護士ローバーツ卿に弁護を依頼する。しかし、唯一のアリバイを証言する妻が思いもよらないことを口にし……。", "1958-03-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はなれ瞽女おりん", "はなれ瞽女おりん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "篠田監督が滅びいく“瞽女”を北陸の美しい四季を背景に、哀歓を込めて描いた作品。病気などの原因で失明、もしくはそれに近い視力に陥った少女たちが、厳しい訓練を受けて“門付け”の旅に出るという瞽女は、日本の庶民の伝統芸能を細々と守っていると同時に、厳しい生活態度を強いられる。ある男と一夜をともにしたことから“はなれ瞽女”となったおりんは、脱走兵の男と知り合い、一緒に旅を続けることになる。これは1967年製作、篠田・岩下コンビによる「あかね雲」の物語ともほぼ一致していて、作者の反戦への願いを感じずにはいられない。宮川一夫の美しいカメラワークも印象深い。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スレイヤーズぷれみあむ", "スレイヤーズぷれみあむ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "新たな男性キャラも登場して、よりパワー・アップしたコミカル・ファンタジー・アニメの劇場版第5作目。今回は人気声優、林原めぐみが演じるリナにスポットを当てた構成だ。“タコ語”しか話すことができない病気に罹る人間が急増。その裏には、タコ族が人々のパニックをエネルギーに変え、彼らの守護神を復活させようとしていたのだ。リナはそれを阻止するために立ち上がる。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラッシュライフ", "ラッシュライフ", "", "", "http://image.eiga.k-img.com/images/movie/54426/315x210.jpg?3806", 122, "東京芸術大学映像研究科の生徒らが企画プロデュースから製作、配給までを一貫して行うプロジェクトで、人気作家・伊坂幸太郎の同名小説を映画化。堺雅人、寺島しのぶ、柄本佑、板尾創路己ほか豪華俳優が出演する。己のポリシーを貫く泥棒・黒澤、神に救いを求める青年・河原崎、不倫相手とお互いの伴侶を殺害する計画を進めるカウンセラー・京子、仕事と家族を失ったサラリーマン・豊田、そんな4人の人生が交錯し……。", "2009-06-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHOENIX", "スター・コンバット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "アンドロイドと精鋭コマンド部隊の壮絶なバトルを描いたアクション。惑星タイタスの採鉱コロニーが、突如アンドロイド軍に占拠された。製造元のライデル社社長キルゴアは、タイラー率いる精鋭チームを派遣し、アンドロイドの指導者ミロの連行を任命する。だが、激しい銃撃戦により負傷したタイラーはミロに助けられ、キルゴアの謀略とアンドロイドたちの悲運を知る。そしてタイラーは、キルゴアを倒すべく立ち上がる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使", "天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "桜沢エリカの同名コミックを映画化したラブ・ファンタジー。恋に臆病なコンビニ店員や恋人と娘の間で揺れるシングルファーザーなど、人生につまずいた人々に天使が寄り添って勇気を与える。ままならない人生の前で立ち止まり気味の人々が、物言わぬ天使と出逢ったことから、ほんの少し前を向いて歩き始める姿を描き出す。深田恭子がセリフのない天使という難しい役どころに挑戦。原作から得たインスピレーションで、深田自身が描いたイメージ画を基に作られた天使の衣装にも注目。", "2006-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("インスタント沼", "インスタント沼", "", "", "http://image.eiga.k-img.com/images/movie/53886/315x210.jpg?3035", 120, "担当雑誌が休刊になって出版社を辞めた沈丁花ハナメは、泥沼のようなジリ貧人生をやり直そうと思っていた矢先に、自分の父親が“沈丁花ノブロウ”なる見知らぬ男だと記された手紙を発見する。事実を確かめようと手紙の住所を訪ねると、そこには“電球”と名乗る骨董屋の店主がいた……。監督は「転々」の三木聡、主演は深夜ドラマ「時効警察」でも三木とタッグを組んだ麻生久美子、共演は風間杜夫、加瀬亮、松坂慶子。", "2009-05-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISS OF DEATH", "死の接吻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "宝石強盗に失敗し、ただ一人逮捕され、20年の刑期を科せられた前科者のニック。地方検事補のダンジェロは、ニックが共犯者の名前を白状すれば、減刑し、仮出所させてやると持ちかけたが、彼はそれを拒む。一年後、ニックの一味が彼の妻に分け前を与えなかったので、妻は生活に困って自殺。そのため、二人の子供は孤児院に入っていることを知り、ニックは彼らに復讐を誓う。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女王蜂と大学の竜", "女王蜂と大学の竜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "終戦直後の新橋の露店を舞台に、関東桜組にワラジを脱いだ“大学の竜”こと広岡竜二が、新興ヤクザや悪党どもを相手に大活躍。“大学の竜”に扮する吉田輝雄が小気味よく、桜組組長のアラカンが見事な殺陣をみせる。組長の娘・珠美に扮するバンプ女優・三原葉子の二代目“女王蜂”ぶりも秀逸。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夕映え少女", "夕映え少女", "", "", "http://ecx.images-amazon.com/images/I/51f2RIiWggL._SL210_.jpg", 100, "文豪・川端康成の初期短編集の中から、女性をテーマに描いた4作品を映像化したオムニバス映画。第2次世界大戦を目前にした昭和初期の日本を舞台に、現代にも通じる若い女心の不思議さを描きだす。メガホンを取るのは、東京藝術大学大学院映像研究科に在籍する4人の若手監督。キャストには、「蛇にピアス」の吉高由里子、「転がれ！たま子」の山田麻衣子、「恋空」の波瑠らフレッシュな顔ぶれが揃う。", "2008-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("糊塗妙賊小神偸", "キー・ホイ・クァンのドロボーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「インディ・ジョーンズ／魔宮の伝説」のショート役で一躍脚光を浴びた天才子役、J・キー（以前の名前はキー・ホイ・クァン）主演の台湾製アクション・コメディ。少年スリと美人のサギ師、そしてマヌケな強盗がトリオを結成、街を牛耳る悪徳ボスを敵に回して大活躍をするが……。スクリーン狭しと暴れ回るキーのやんちゃぶりが見もの。", "1986-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くりいむレモン", "くりいむレモン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "「リアリズムの宿」「ばかのハコ船」などで知られる山下敦弘監督が、伝説のアダルトアニメ・シリーズ“くりいむレモン”を実写映画化。腹違いの兄妹が繰り広げる微妙な恋愛関係を軸に、思春期の少女が抱く淡い恋心を絶妙に捉えた、ちょっとエッチな青春映画だ。恋心に揺れるヒロインを演じたのは、映画初主演の村石千春。大胆なベッド・シーンにも果敢に挑戦している。17歳の女子高生、亜美は、血の繋がらない兄、ヒロシのぐうたらぶりにいつも呆れていた。ある日、留守中の両親に代わり亜美の進路相談に出席したヒロシは、担任教師の乱暴な態度に激怒。亜美はそんな兄の想いを嬉しく受け止める。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL FIORE DELLE MILLE E UNA NOTTE", "アラビアンナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "美少年ヌレディンの流転の遍歴を描いた、「デカメロン」「カンタベリー物語」に続く、パゾリーニの艶笑３部作の１本。ヌレディンは、女奴隷ズルムードを買い、二人で幸福な生活を送っていた。しかし、ズルムードは青い目の男バルムスに奪われてしまい……。ヌレディンのズルムード捜しの旅のなかに、『アラビアンナイト』の挿話が綴られる。", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神坂四郎の犯罪", "神坂四郎の犯罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "石川達三のベストセラー小説を映画化。女の持つダイヤ目当てに偽装心中したという疑いをかけられた男・神坂四郎。犯行について４人の証人が現れるが真実はヤブの中で終わる。不敵な男・神坂四郎に森繁が扮して、新境地をみせた異色の推理サスペンス。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ME，NATALIE", "ナタリーの朝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "学生運動で大学を中退したナタリー。彼女は自分の容姿に悩むが、グリニッジ・ビレッジで一人暮らしをするうちに様々な経験をし、一人前の女に成長していく。ミニスカートやサイケデリックな風俗を織り込み、激動の1960年代の中で恋をした女性が、人の美しさは内面的な輝きであると悟っていく姿を描く。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香港淪落／1941 GENOCIDE IN HONG KONG GENO", "香港大虐殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1941年の冬。日本軍に完全侵略された香港の街では、至るところでレイプ、虐殺、拷問が行われていた。モンダイ、オンダイ、ソンダイ３姉妹の父親は生き伸びるために日本軍の手下として働き、モンダイとオンダイを軍人に奴隷として差し出してしまう。そんな父が許せないソンダイは、愛国心の強いサム・フォンの野戦病院で働き始め、やがて二人は恋に落ちる。だが、病院にも非道な日本軍が押し寄せて来るのだった……。第二次大戦時の“暗部”に材を得た、骨太なドラマ。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOOD THIEF", "ギャンブル・プレイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "N・ジョーダン監督が1955年の傑作「賭博師ボブ」にヒントを得て映画化したハードボイルド。フランスのリゾート地・リビエラを舞台に、ツキに見放されたギャンブラーが、カジノの大金と有名絵画の強奪を計画する。主演のN・ノルティが、本来の持ち味であるタフガイの魅力を生かし熱演。", "2003-01-01", "3,2,10,33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG TIME", "ビッグ・タイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "俳優としても注目される異色シンガー・ソングライター、トム・ウェイツが、1987年11月、サンフランシスコとロスの二カ所で行ったコンサートの模様を収める。３枚のオリジナル・アルバムの曲を中心に、オープニングの『ベッドに横たわる男』をはじめ、パフォーマンス性たっぷりに展開。T・ウェイツの一人舞台とも言える１本。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANNA", "アンナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "祖国チェコスロバキアを捨てアメリカに渡った元大女優アンナ。かつての栄光と厳しい現実のギャップにさいなまれ、若いチェコ娘に嫉妬するアンナの心理的葛藤を克明に綴った、良質の女性映画。ゴールデン・グローブ賞最優秀主演女優賞ほか受賞。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社長三代記", "社長三代記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "このあとおなじみになる松林宗恵監督が、初めてメガホンを取った。福富電機工業は創立10年を据えて、二代目社長・浅川啓太郎以下、純情な営業部長・大場太平も張り切っている。啓太郎は妻のお守を部下の長谷川に命じて、自分は新橋芸者・梅千代とイチャイチャ。ところがこの二組の男女づれ、街頭でばったり出くわしてしまったから大変。やがて啓太郎はニューヨーク支店長になり、大場が三代目社長に就任してめでたしめでたしと相なる。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLEEPWALK", "スリープウォーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "真夏のニューヨーク。タイピストのニッキーは、ある日黒人の大男から中国語で書かれた中国のおとぎ話を手渡され、３日間で英訳するよう頼まれる。それからというもの、ニッキーの身の回りで不思議な現象が起こり始める。ジャームッシュのよき協力者、ドライヴァーがメガホンを執ったミステリアスな一編。", "1986-01-01", "7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAR OF THE BUTTONS", "草原とボタン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「炎のランナー」の製作者D・パットナムと脚本家C・ウェランドによる青春ドラマ。アイルランド南西部の小さな町。ファーガス率いる“バリーズ”は、隣町のジェロニモ率いる“キャリック”とケンカの毎日。勝利の印は相手の洋服のボタンだった。ある日、ボタンを奪われたファーガスは、継父に叱られボタンを買うために資金稼ぎに奔走する。そんな中、対立は次第にエスカレートし、大騒動に発展。ファーガスとジェロニモは警察や大人たちに追われるハメに……。わんぱくな少年たちがケンカに明け暮れる日々の中で、友情や裏切り、家族間のトラブルなどに心揺れる姿を、繊細なタッチで活写。少年たちを見守る少女の温かいまなざしが印象的。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香港厨神　大阪撻一餐", "大阪プロレス飯店", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "香港と大阪を舞台に展開するコメディ。香港で料理人修業中のキーは、大阪にいる、いい加減な兄マイのアイデアに乗り、プロレスとレストランを融合させた店の経営に着手。そこで巻き起こる珍騒動がナンセンスなギャグとともに描かれる。 サモ・ハン・キンポーの息子ティミー・ハンが主人公キーに扮し、コメディ・センスを発揮。父親との共演も見ものだ。香港で料理人を目指すキーは、父の遺産として金の延べ棒を受け取る。自分勝手な兄マイは、遺産を元手に“大阪プロレス”のテーマレストランを開店。ところが、ライバル店の妨害に遭い、地球征服を目指す謎の組織までが現れる……。", "2004-01-01", "6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林寺／SHAOLIN TEMPLE", "少林寺列伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "少林寺の英雄、方正玉、洪煕官の活躍を描いた武術アクション。熱意が認められて少林寺への入門を許された方正玉、洪煕官、胡恵乾らは厳しい特訓を積み、ついに秘伝の拳術を学ぶにいたる。しかし、清国による少林寺の焼き打ちが、刻一刻と迫り……。著名な武術家がアクション指導で参加しており、本格的なカンフーの見せ場を満喫できる。", "1976-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("絵里に首ったけ", "絵里に首ったけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "関西風のベタなギャグを盛りこんだ青春コメディ。亡き恋人の遺志を継いで、彼の母校のラクビー部顧問となった高校教師の絵里。だが部員はわずか４人で、いずれも落ちこぼれの問題児ばかり。意を決した彼女は色仕掛けで生徒たちを奮い立たせ、部員を集めてライバル校との勝負に挑む。ヒロイン、大河内奈々子の快活な好演が魅力。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARK PASSAGE", "潜行者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "H・ボガート、L・バコールの顔合わせが見ものの犯罪映画。無実の罪を着せられた男バリーが、殺人事件の真犯人を捜すため刑務所を脱獄する。彼はサンフランシスコに向かう途中でアイリーンという美しい娘と心を通わせ、整形手術によって別人になりすます。そんなバリーの前に謎めいた脅迫者が現れて……。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("解夏", "解夏", "", "", "http://ecx.images-amazon.com/images/I/41IHcJCP03L._SL210_.jpg", 113, "「精霊流し」が映画化されたさだまさしの、もう一つの小説世界に、繊細なタッチの作品をてがける磯村一路監督が挑戦。長崎を舞台に、いつ失明するかも分からない奇病に冒された青年の帰郷を見つめる。恋人との別離と和解を繰り返し、絶望と悟りを行き来する主人公の心情を大沢たかおがリアルに体現し、大いに泣かせる。東京で小学校の教師をしていた隆之は、徐々に視力を失っていくという病気に冒される。教師をやめ、故郷の長崎に帰り美しい風景を目に焼き付けることで失明する恐怖を紛らわそうとする隆之だったが、恋人の陽子がやって来て……。", "2004-01-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太と夢幻三剣士", "ドラえもん のび太と夢幻三剣士", "", "", "http://ecx.images-amazon.com/images/I/61S86K57MGL._SL210_.jpg", 100, "自分が英雄になった夢を見たのび太は、自分の思い通りに夢を見る事ができる道具を出してもらう。夢見ることに夢中になったのび太だったが、ある日謎の男から“夢幻三剣士”の夢を見るように言われる。その夢は、侵略の危機に面した王国で、のび太は伝説の剣士となって王国を救う旅に出ることになるが……。", "1981-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Born to Raise Hell", "沈黙の復讐", "", "", "http://image.eiga.k-img.com/images/movie/55766/315x210.jpg?4083", 0, "「沈黙」シリーズで知られるスティーブン・セガールが、復しゅうに燃える麻薬捜査官を熱演するアクション。銃と麻薬密輸を捜査する国際麻薬捜査部隊“IDTF”に配属されたボビーはある日、抗争に巻き込まれ、同僚を失ったことを契機に、たった1人で巨大な悪に立ち向かっていく。俳優やスタントマンとしても活躍するラウロ・チャートランド監督がメガホンをとり、セガール自らが脚本を手がける。", "2010-12-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西鶴一代女", "西鶴一代女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "敗戦後低迷を続けていた溝口健二が西鶴の『好色一代女』をもとに、全身全霊を打ち込んで完成させた彼の最高傑作。奈良の荒れ寺へ客にあぶれた街娼たちが集まり、グチを言いあっている。その中に厚化粧でも年は隠せないお春という女がいた。その夜、巡礼帰りの百姓たちの前に引きだされ、“こんな化け猫をおまえたちは買いたいのか”とさらし者になった彼女は、我知らず寺の羅漢堂に入っていく。居並ぶ五百羅漢を眺めやるうちに、羅漢像の一つ一つが過去の男たちに見えてくるのだった。こうして時代は一気にさかのぼり、彼女が御所づとめに出ていた13歳の時からの流転の生涯が綴られていく。様々な男たちと出会い、別れていくたびに不幸になっていくお春を通して、封建制度下に自我を通そうとした女の悲劇を優しく、そして苛烈に描ききっている。溝口一流の長回しが随所で効果を上げており、また巨大なセットの空間の広がりに圧倒される。田中絹代も一世一代といえる名演を見せた。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Puss in Boots", "長ぐつをはいたネコ", "", "", "http://image.eiga.k-img.com/images/movie/55156/315x210.jpg?2887", 90, "ドリームワークスアニメーションの大ヒットシリーズ「シュレック」に登場する人気キャラクター「長ぐつをはいたネコ」を主人公にしたスピンオフ。シュレックに出会う以前の長ぐつをはいたネコこと主人公のプスが、幼なじみのハンプティ・ダンプティとメスネコのキティとともに繰り広げる冒険を描き、捨てネコだったプスの生い立ちやお尋ね者になった過去、長ぐつに隠されたエピソードなどが明かされる。プスの声は「シュレック」シリーズに引き続きアントニオ・バンデラスが担当。そのほかサルマ・ハエック、「ハングオーバー」のザック・ガリフィアナキスらが声優を務める。", "2012-03-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録・夜桜銀次", "実録・夜桜銀次", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 175, "日本のヤクザ抗争史にその名を刻む伝説の鉄砲玉“夜桜銀次”こと平尾国人の壮絶な人生をリアルに描いた任侠映画。大ヒットした1974年の映画「山口組外伝・九州進攻作戦」で一躍有名になった“銀次”。その謎に包まれた生涯と独特の美学がこれまで語られたことのなかった別府の組長との義兄弟愛を中心に大胆な新解釈で綴られる。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGEL　EYES", "エンジェル・アイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "J・ロペス主演の都会派ラブ・ストーリー。警官としてのタフな外見の裏側に深い心の傷を持つシャロン。彼女はある日、ハイウェイの交通事故現場に駆けつけ、瀕死の重傷を負った被害者を必死に励まし続けた。それから１年半後、シャロンの同僚がストリート・ギャングに銃撃され、現場にいた彼女は犯人を追い詰めるが、逆にピンチに陥ってしまう。それを救ったのはキャッチと名乗る男性だった……。過去につながる記憶の扉を閉ざした彼と、そんな彼に惹かれていくシャロン。二人の恋の行方と“過去”という試練、そして心の再生を感動的に綴っていく。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Huit fois debout", "エイト・タイムズ・アップ", "", "", "http://image.eiga.k-img.com/images/movie/54956/315x210.jpg?4574", 104, "職も家も失ってしまった女性が前向きに奮闘する姿をユーモアたっぷりに描く、フランスのヒューマン・ドラマ。バスの清掃やベビーシッターのアルバイトで生計を立てながら就職活動を続けるエルザ。なかなか定職が見つからずアパートの退去勧告まで受けてしまった彼女は、自分と似たような境遇の隣人マチューと知り合い、彼がキャンプ生活を送っている森を訪れる。主演は「ぼくの大切なともだち」のジュリー・ガイエ。", "2009-10-21", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Transformers", "トランスフォーマー", "", "", "http://image.eiga.k-img.com/images/movie/1916/315x210.jpg?4136", 144, "自動車、ヘリコプター、戦闘機など地球上のあらゆるマシンの姿をトレースし、人型ロボットへ変形することができる未知の金属生命体が地球に襲来。人類に対して侵略を開始する。そんな時、先祖の探検家が南極で発見した彼らの“秘密”を、そうとは知らずに保管していた16歳の少年サムは、初めて買った自分の車が巨大ロボットに変形することを知り……。監督は「アルマゲドン」のマイケル・ベイ。スティーブン・スピルバーグが製作総指揮を担当。", "2007-08-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・ふしぎな國　日本", "喜劇・ふしぎな國　日本", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "山口県の田舎町を舞台に、反対を押し切って愛を貫こうとする若い男女の姿を中心に、夫婦や親子の会話、町ぐるみのカラオケ大会、一徹な老人たちの時事放談など“愛すべき日本人”たちの滑稽な現象、言動を追った喜劇。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピュ～ぴる", "ピュ～ぴる", "", "", "http://image.eiga.k-img.com/images/movie/55906/315x210.jpg?2108", 93, "男性としての体に違和感を覚えていたピュ～ぴるは、自ら制作した奇抜なコスチュームを身にまとってクラブへ通うことで、その違和感を解消していた。やがてそのコスチュームがアートとして注目を浴びるようになり、芸術家として創作活動を始める。世界的な評価を受ける日本人コンテンポラリーアーティスト、ピュ～ぴるが、性同一性障害、失恋、去勢手術などを経て成功していく8年間の軌跡を、長年の友人である松永大司監督が追ったドキュメンタリー。", "2011-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボディガード牙・必殺三角飛び", "ボディガード牙・必殺三角飛び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "千葉真一主演の“ボディガード牙”ものの第２作。他流派から挑戦を受けた牙直人は、三角飛びで相手を殺してしまい刑務所に送られ、そこで沖縄空手の名手・南条と知り合う。出所後、南条に米軍輸送襲撃の罪をかぶせた悪玉相手に、牙の必殺三角飛びが爆発する。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベっぴんの町", "ベっぴんの町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "軒上泊の“アマチュア・オプ”シリーズの同名小説を映画化。主人公は、元少年院の教官という経歴を持つ素人探偵。行きつけのバーのホステスの依頼で、失踪した女子高生を捜し始めた彼が、やっかいな殺人事件に巻き込まれていく姿をハードボイルドな雰囲気たっぷりに描き出している。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アワモリ君西へ行く", "アワモリ君西へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「アワモリ君乾杯！」に続く坂本九の主演作。前作で予備校生だったアワモリ、大学のコンビも、社会人になっている。今回は、大阪支社に転勤を命じられた大学とともに、商売のコツを覚えるため大阪に向かったアワモリが、大学のいる轟商事の課長、高田の縁談に首をつっこんだことから起きる大騒動を描く。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沈黙的姑娘／THE WRASH OF SILENCE", "金城武 香港犯罪ファイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ストレートヘアの女性だけを狙った強姦犯罪が香港で続発。障害を持つ妹がその被害に遭ったことから、死体の美容技師をしているメイは妹の専属医クーに疑惑の目を向ける。一方、これに目をつけた真犯人は、クーを犯人に仕立てようと罠を仕かけ……。自分の無実を証明しようと奔走する医師を、金城武が熱演したサイコ・サスペンス。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MARIE DU PORT", "港のマリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "フランス映画の巨匠カルネと名優ギャバンコンビの代表的作品。中年の金持ち男が、港町の娘マリィに恋をする。彼女には若い恋人がいて、男はいつもフラれっぱなし……。若い娘に振り回される中年男のとまどいぶりをギャバンが好演。港町の描写も歯切れ良く、映画の妙味を堪能できる。", "1949-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　ホラーマンとホラ・ホラコ", "それいけ！アンパンマン　ホラーマンとホラ・ホラコ", "", "", "http://image.eiga.k-img.com/images/movie/34066/315x210.jpg?2157", 21, "「それいけ！アンパンマン」に登場するガイコツ姿の人気キャラクター、ホラーマンにそっくりなホラ・ホラコが、愉快なホラ話で大騒動を巻き起こす短編アニメーション。同シリーズの劇場版第19作「それいけ！アンパンマン　シャボン玉のプルン」の同時上映作。ほら貝型のUFOから飛び出してきたホラ・ホラコが、ホラーマンの娘だと聞いたアンパンマン。2人は一緒にホラーマンを探すことになり……。ホラコの声を榎本加奈子が演じる。", "2007-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINDERS KEEPERS", "太陽をつかもう！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "観光地サン・カルロスでは、近くの海にアメリカの爆弾が不発のまま墜落したため、観光客が来なくなってしまった。ホテルに呼ばれた楽団は客がいないので商売あがったり。そこで楽団員たちは観光客を呼び戻すために爆弾が見つかったと噂を流す。そのころ、本物の爆弾が民家で発見され、またも大騒ぎに……。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽とバラ", "太陽とバラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "生まれ育った環境の貧しさゆえに不良グループの仲間入りをしていく青年を、社会的な視線で描き出した木下惠介ならではの作品。同年の日活作品「太陽の季節」に代表される太陽族、愚連隊などの流行や神聖化に対する木下の批判的な姿勢が明確に示されている。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jonah Hex", "ジョナ・ヘックス", "", "", "http://ecx.images-amazon.com/images/I/51x8utSAN9L._SL210_.jpg", 80, "1970年代に誕生したDCコミックスの人気グラフィック・ノベルを「ノーカントリー」のジョシュ・ブローリン主演で映画化したアクション西部劇。顔の半分が焼けただれたジョナ・ヘックス（ブローリン）は西部各地で荒稼ぎする凄腕の賞金稼ぎ。ある日、彼は軍隊に捕らえられ「テロリストのターンブルを殺せば身柄を自由にする」という提案を受け、それを快諾。ターンブルはヘックスの宿敵で、彼の顔を傷つけた張本人だった。共演にジョン・マルコビッチ、ミーガン・フォックス。監督は「ホートン／ふしぎな世界のダレダーレ」のジミー・ヘイワード。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUSIC FROM ANOTHER ROOM", "ミュージック・フロム・アナザー・ルーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "一途な青年の恋の行方と彼の不思議な魅力で優しさを取り戻す人々の人間模様を描くロマンティックなラブ・ストーリー。５歳のダニーはアンナの誕生に立会い、彼女が運命の恋人だと直感した。やがて、成長した彼はアンナと運命的な再会を果たすが、彼女にはすでに婚約者が。それでも、ダニーは彼女への思いを断ち切れず、彼の純粋な思いは責任感から自分に素直になれないアンナの心を解きほぐしていく。J・ロウが明るく無邪気な青年をナチュラルに好演。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHILD'S PLAY3", "チャイルド・プレイ3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "冷酷な殺人鬼の魂を宿す殺人人形チャッキーが、その秘密を知る少年アンディをつけ狙う、人気ホラー・シリーズの第３作。16歳になり陸軍学校の訓練生となったアンディのもとに現れたチャッキーが、今度は年下の士官候補生タイラーの命を狙おうとする。大量の兵器を駆使した壮絶な死闘が展開。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クルタ The adventure of a GOLDEN DOG", "クルタ The adventure of a GOLDEN DOG", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "オーストラリアの大自然を舞台に、ゴールデン・レトリバーの子犬クルタの冒険を描く日豪合作ファミリー動物映画。好奇心がもとで、風船つきのバスケットに乗って遠くの街まで飛ばされてしまったクルタ。モモイロインコのディンプルを相棒に、わが家を目指す旅が始まった。オーストラリア特有の動物たちやスケールの大きな自然を織り込みながら、旅によって成長を遂げる子犬の姿を描く。牧瀬里穂がクルタの声で吹き替えに挑戦。日本語版の監督・脚本は畑正憲が担当する。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月あかりの下で　ある定時制高校の記憶", "月あかりの下で　ある定時制高校の記憶", "", "", "http://image.eiga.k-img.com/images/movie/55646/315x210.jpg?4517", 115, "高校の非常勤教師というキャリアをもつ太田直子がメガホンをとり、夜間の定時制高校に通うさまざまな生徒たちの姿を記録したドキュメンタリー。埼玉県立浦和商業高校定時制クラスを舞台に、2002年の入学から2006年の卒業までの4年間に密着する。派手な身なりで教師に暴言を吐く生徒、家庭内暴力が原因で登校できなくなった生徒、自傷行為を繰り返す生徒など、家庭や社会生活の中で問題を抱えながらも支え合う若者たちを映し出す。", "2010-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COFFY", "Coffy", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "知る人ぞ知るB級映画の異才J・ヒルのバイオレンス活劇。ヘロイン中毒となった妹の仇を討つため、看護婦のコフィは娼婦になりすまして麻薬組織に潜入。しかし、その正体がバレたことから、彼女は絶体絶命の危機に見舞われ……。ヒロイン、コフィを演じたブラック・ビューティー、P・グリアーのパワフルな熱演が見もの。クエンティン・タランティーノの助力によって1990年代に本国で再公開され、カルトな人気が高まった。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In the Shadow of the Moon", "ザ・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/53556/315x210.jpg?2562", 100, "1969年7月20日、アポロ11号が月面に着陸。ニール・アームストロング船長によって、月に初めて人類の足跡がしるされた。以降、72年までの間に12人の宇宙飛行士が月に降り立ったが、いまだ地球外の地に立った人間はその12人しかいない。NASA提供による初公開の映像や、アポロ11号のバズ・オルドリン、マイク・コリンズ、奇跡的な生還を果たしたアポロ13号のジム・ラベルら、アポロ計画で月へ向かった10人の宇宙飛行士たちが当時の体験を語るドキュメンタリー。", "2009-01-16", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pickup on South Street", "拾った女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ニューヨークの地下鉄を舞台にスリを働くスキップは、キャンディという女の財布をスリ取る。ところが中に入っていたのは共産国スパイの機密フィルムだった。キャンディの雇主ジョーイはそれを取り戻すべく行動を開始する……。S・フラーが1953年に撮ったハードボイルド・アクション。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Felon", "プリズン・サバイブ", "", "", "http://image.eiga.k-img.com/images/movie/54586/315x210.jpg?4033", 104, "スティーブン・ドーフ、バル・キルマー、サム・シェパードらが共演するクライムアクション。家族と平凡で幸せな日々を送っていたウェイドだったが、ある晩、自宅に押し入った強盗を誤って殺害してしまい、過剰防衛の罪で刑務所に入れられてしまう。そんなウェイドを待ち受けていたのは、凶悪犯たちによる制裁や看守たちの嫌がらせだった。始めは耐え忍んでいたウェイドだったが、とうとう怒りが爆発し……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シンガポールスリング", "シンガポールスリング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ミュージシャン、徳永英明が企画・原案をはじめ、４役をこなしたハード・アクション。新婚旅行先のオーストラリアで、殺人事件に巻き込まれた日本人カップル。犯人に仕立て上げられた彼らが、困難な状況のなかで眠っていた闘争本能をよみがえらせていく。若松孝二による、シャープな演出が光る。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志・殴込み甲州路", "次郎長三国志・殴込み甲州路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "甲州にある大熊の賭場が猿屋の勘助に荒らされ、物見役を買って甲州へ潜入した投げ節お仲は、正体がばれて捕らえられる。次郎長一家は甲州へ殴り込み、勘助を斬ってお仲を救い出す。次郎長役の小堀明男が父・小堀誠と親子共演。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("G.I. Joe 2: Retaliation", "G.I.ジョー　バック2リベンジ", "", "", "http://image.eiga.k-img.com/images/movie/57486/315x210.jpg?1373", 0, "ハズブロ社のアクションフィギュアをもとに映画化した「G.I.ジョー」（2009）のシリーズ第2作。パキスタンで極秘任務にあたっていた国際機密部隊「G.I.ジョー」は、米大統領の裏切りにより組織解体の危機にさらされる。「G.I.ジョー」の弱体化を受け、テロ組織「コブラ」が再び勢力を拡大。ロンドンをはじめとした大都市が陥落していく。孤立無援に追い込まれた「G.I.ジョー」は初代司令官ジョーをリーダーに迎え、世界の危機に立ち向かう。デューク役のチャニング・テイタム、冷徹な暗殺者ストームライダー役のイ・ビョンホンら前作から続投のキャストに加え、伝説の初代司令官ジョーにブルース・ウィリスが扮する。", "2012-08-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("土砂降り", "土砂降り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "東京・南千住の連れ込み旅館の女主人阿部たねは、初枝という女中を使い、３人の子供たちと暮らしていた。長女の松子は同僚の一夫と結婚話が進んでいたが、一夫の母の反対で破談に。自暴自棄となった松子は家出し、キャバレーのダンサーに身を落とす。新派で上演された北条秀司の原作を、中村登が演出した異色編。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DOCKS OF NEW YORK", "紐育の波止場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "社会の底辺にいる人々の生活を温かな視点で描く、サイレント末期のメロドラマ。油まみれで働くしがない貨物船の火夫、ビルは、波止場で身投げしようとした売春婦メイの命を救う。ビルの優しさに触れ、生きる希望を取り戻した彼女は、酒場で元気に働き始めた。だが水夫たちにメイが次々と口説かれるのに耐えられない彼は、酔った勢いで彼女との結婚を宣言。その夜、救世軍の士官の立ち合いで彼は形ばかりの夫となった。翌朝、酔いがさめると、彼は昨夜の軽率さを何たることかと思い悩む。そしてただの飲んだくれにすぎない自分を恥じた彼は、ひそかに姿を消すことを決めたのだが……。職人と天才が同居する巨匠J・フォン・スタンバーグ監督が、メリハリ巧みな脚本を得て手掛けた初期の傑作の一つ。また、島津保次郎監督の松竹映画「上陸第一歩」が、本作の翻案であることも広く知られている。", "1928-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Da Vinci Code", "ダ・ヴィンチ・コード", "", "", "http://image.eiga.k-img.com/images/movie/1046/315x210.jpg?2350", 150, "ダン・ブラウンの世界的ベストセラー小説を「ビューティフル・マインド」のオスカー監督、ロン・ハワードが映画化。ルーブル美術館で館長が殺害される事件が発生。調査を依頼されたハーバード大宗教象徴学教授ラングドンは、暗号解読官ソフィーとともに死体に隠された暗号を解読してゆく中で、カトリック教会が隠蔽してきたキリスト教史上最大の謎に行き当たる。「ターミナル」のトム・ハンクス、「アメリ」のオドレイ・トトゥらが出演。", "2006-05-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("隠し剣　鬼の爪", "隠し剣　鬼の爪", "", "", "http://image.eiga.k-img.com/images/movie/1466/315x210.jpg?3540", 131, "人気時代小説作家、藤沢周平の剣豪小説「隠し剣」シリーズの「隠し剣鬼の爪」と、人情時代小説「雪明かり」を原作に、「たそがれ清兵衛」の山田洋次監督が映画化。幕末の東北の小藩。秘剣を身につけた下級武士、片桐宗蔵は、かつて好意を抱いていた奉公人きえが病に倒れたと知って引き取り、心を通わせていくが、藩の江戸屋敷で謀反が発覚し、お家騒動に巻き込まれる。共演は高島礼子、小林稔侍、田中邦衛、倍償千恵子ら。", "2004-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE，SO　DIVINE", "恋する神父", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "人気韓国ドラマ『天国の階段』でブレイクした若手スター、クォン・サンウが生真面目な神父に扮するラブ・ストーリー。女性と信仰の間で揺れ動く新米神父の切ない恋の行方が描かれる。神父役で新境地に挑むクォンもさることながら、破天荒なヒロインを体現したハ・ジウォンがこれまた魅力的。生涯独身で神に仕えることを選んだ神学生のギュシク。ところが彼は神父の姪に当たる自由奔放な娘、ボンヒに心を奪われてしまい……。", "2005-07-16", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新網走番外地・吹雪のはぐれ狼", "新網走番外地・吹雪のはぐれ狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "第９作「悪への挑戦」とよく似た設定の作品。看守と囚人の豆の横流しの事実を知った末広勝治は、襲撃を受け負傷。牧師に救われ不良少年たちの更生を手伝うが、悪徳ヤクザの悪業に怒りが爆発する。岡田真澄が巡回牧師を好演。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すべては「裸になる」から始まって", "すべては「裸になる」から始まって", "", "", "http://image.eiga.k-img.com/images/movie/57576/315x210.jpg?1760", 80, "2007年に発表された元AV女優・森下くるみの自伝的小説を、元「AKB48」の成田梨紗の主演で映画化。秋田から上京した森下は18歳でAV女優としてデビューし、たちまちAVクイーンと呼ばれるほどの人気を博す。10年間の活動を経てAVを引退し、自伝本を出版すると、今度は映画化の話が舞い込む。脚本作りの過程で当時を振り返る森下だったが、AV女優という職業に対する偏見などから、脚本の内容は思わぬ方向に進んでいく。そんな中、森下は憎んでいた父親と上京以来初めて再会するのだが……。映画初主演となる成田が森下役を体当たりで演じ、「仮面ライダー剣」の椿隆之、漫画家の江川達也らが共演する。監督は「ADULT 24歳の恋」の中町サク。", "2012-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A NIGHTMARE ON ELM STREET ３：THE DREAM WARRIORS", "エルム街の悪夢3 惨劇の館", "", "", "http://ecx.images-amazon.com/images/I/515DcO8rdQL._SL210_.jpg", 97, "C・ラッセルが監督を手掛けたシリーズ第３作。ドラマは、最初の惨劇から６年後という設定。パート１のヒロイン、ナンシーが心理学の研究生として登場、あの悲劇が再び起こり……。今回は、殺人鬼フレディの忌まわしい出生の秘密が明らかになる。", "1988-01-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画クレヨンしんちゃん　ちょー嵐を呼ぶ　金矛（きんぽこ）の勇者！", "映画クレヨンしんちゃん　ちょー嵐を呼ぶ　金矛（きんぽこ）の勇者！", "", "", "http://image.eiga.k-img.com/images/movie/53296/315x210.jpg?2720", 93, "人気アニメ「クレヨンしんちゃん」の劇場版第16弾。闇の世界“ドン・クラーイ”で、地球にいる“選ばれし勇者”に地球とドン・クラーイを結ぶ\"闇の扉\"を開けさせる計画が進められていた。実はその勇者とはしんのすけで、彼は突然現われた美女にそそのかされ、知らないうちに闇の扉を開けてしまう。このままでは地球が闇に支配されると知ったしんのすけは、勇者を守るという少年マタの協力を得て闇に立ち向かうが……。", "2008-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　MUJER　MAS　FEA　DEL　MUNDO", "世界で一番醜い女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ブラック・ユーモアに満ちたスペイン製のスリラー。2010年、マドリッドで老女が殺された。犯人は、醜い顔ゆえに悲惨な体験を重ねてきた女性ローラ。革新的な整形手術で美女となった彼女は“美”とされるものへの復讐を誓い、次々と女性を殺害していた。そのころ、殺人事件を追うベテラン刑事アリバスは、被害者たちの意外な共通点に気付き……。シーンによってミステリー、ホラー、コメディなど様々な要素が垣間見えるジャンル・ミックスの妙に注目。", "1999-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DESPERADOS", "ならず者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "南北戦争下、西部をわがもの顔に荒らしまくっていたガルト一家。息子デビッドは一人一味から逃れたが、戦争が終わって平和な日々が訪れる頃、再びガルト一家に出会う……。V・エドワーズとJ・パランスの二人の個性派俳優が、宿命的な父と子の役を演じるバイオレンス・アクション。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIFE　IS　CHEAP…，BUT　TOILET　PAPER　IS　EXPENSIVE", "命は安く、　トイレット・ペーパーは高い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "大学入学とともにカリフォルニアに移住し、「スラムダンス」などを手掛けたW・ワンが、初めて祖国の香港で撮った作品。“カウボーイ”と呼ばれる男の視点を通して伝えられる香港の表と裏の表情を、ドキュメンタリーとフィクションを交えた実験的映像で浮き彫りにしたもので、日本では1997年７月、香港の中国返還に合わせて初公開された。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白い指の戯れ", "白い指の戯れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "伊佐山ひろ子が演じるヒロインは、街角でふと知り合った男に処女を捧げても、何の屈託もなく男から男へとわたり歩き、現行犯の罪を一人背負っても悲痛な印象を与えない、新しい感覚の人物として造型されている。1970年当時、隆盛を誇っていたアメリカン・ニューシネマとも雰囲気的に似た印象を残し、ポルノというよりも新しいタイプの青春映画として見れる作品である。舌足らずなセリフ回しも可愛い伊佐山ひろ子の新鮮な魅力が光っている。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN INDIEN DANS LA VILLE", "僕は、パリに恋をする", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "本国フランスで大ヒットを記録したファンタスティック・コメディ。妻の住むアフリカを訪ねた証券マン、ステフは初めて対面した息子のミミをパリへ連れ帰るハメになる。帰国後、仕事上のトラブルによって、ステフはパニックに。そんな父をよそに、野生児ミミはパリの街で大騒動を巻き起こし……。ユーモラスなドラマからにじみ出る、人間同士の絆の大切さが温かく伝わってくる好編。アメリカ映画「ジャングル２ジャングル」は本作のリメイク。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUNSHINE PART II", "続・サンシャイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "妻の死後、５歳になる娘を抱えて、タクシー運転手のバイトをしながら歌手の道を目指すサムだが、男手一つの子育ては大変だった……。前作同様ジョン・デンバーの曲が重要な使われ方をしている。TVムービーだったが日本では劇場公開された。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NUMBER ONE", "ナンバーワン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ハリー・フラッシュはロンドンでも指折りのハスラーとして知られていたが、生来のお人好しの性格のため、どうしてもある一線を超えられないでいた。ある日、彼はノミ屋のビリーの手助けでビリヤード世界選手権に出場することになり、チャンピオンとなるための特訓を受けるが……。主演はアフリカ救済の“バンド・エイド”の仕掛け人、B・ゲルドフ。彼が俳優としての才能を発揮した作品である。扱われているゲームがナインボールではなく、スヌーカー・ゲームであることも話題に。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沈黙 SILENCE", "沈黙 SILENCE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "遠藤周作の人気文学の映画化。江戸時代、キリシタン弾圧が激しさを増してきた長崎にポルトガル人司祭ロドリゴが到着した。数々の試練ののち、彼が踏絵をして転ぶまでを篠田監督は冷徹に見つめる。武満徹の音楽が複雑な情趣をよく表現している。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUN WILD RUN FREE", "野にかける白い馬のように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「バニシング・ポイント」などのアクション映画や、日本映画「クライシス2050」の演出などで知られるR・C・サラフィアン監督の出世作。言語障害の孤独な少年と一頭の白い野生馬との心の交流を詩情あふれるタッチ描いていく。", "1969-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE BON ET LES ME´CHANTS", "レジスタンス／反逆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "C・ルルーシュが全編をセピア・カラーで描いたサスペンス・アクション。チンピラのシモンとジャック、コールガールのローラは３人で毎日気楽な生活を送っていた。やがてドイツ軍の侵攻が始まるとともに、３人は当然のようにレジスタンスに参加、ゲリラ活動に身を投じていく。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST SUPPER", "最後の晩餐　平和主義者の連続殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "一軒の家を借りて共同生活を営む男女５人の大学院生。その中のピートが車の故障で困っていたところを救ってくれたトラック運転手ザックを連れてくる。５人は彼に感謝し、夕飯をともにするが、極端な愛国主義者であるザックと平和主義者の５人は意見が合わず対立。ナイフを振りかざして脅すザックと揉み合ううちに彼を殺してしまう。一旦は落ち込む５人だったが、“もし、時間をさかのぼり第二次大戦前のヒトラーに出会ったら、彼を殺すのが正義だ”という考えを持つ彼らは、危険思想のザックを殺したことも正当化してしまう。以来、５人はタカ派、差別主義者、保守派の人間を招いては毒入りワインで殺すという行為を繰り返す……。自分たちの勝手な理論で殺人を犯す５人の行為とその破綻をシニカルに描いたブラック・コメディ。最初に殺されるザックを演じるのは、「ツイスター」のB・パクストン。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺の血は他人の血", "俺の血は他人の血", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "筒井康隆の長編スラップスティック調SFハードボイルド活劇をベテラン舛田利雄が映画化。地方都市を舞台に凶悪なマフィアのボスの血を輸血し強くなった若者が、二つの敵対するヤクザ組織を壊滅させる。のちに乱作される筒井原作映画の１本目で、この作者の映像化は難しいということを証明した作品。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rumble Fish", "ランブルフィッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アメリカの地方都市を舞台に、かつてそこの不良グループのリーダー格であった兄に憧れ、自分もそうなろうと焦る弟の成長を描いた青春映画。モノクロ映像の中で、彼らの象徴といえる“闘魚”だけを色づけしたり、高速度撮影を用いたりと、映像的実験も随所に。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALIVE", "ALIVE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "週刊ヤングジャンプに連載された人気コミックを「あずみ」の気鋭、北村龍平が映像化。狂気の人体実験の中で、次第に明かされていく事実は衝撃的だ。死刑囚の天周は刑の回避と引き換えに、ある実験に参加することに。隔離された実験室には、もう一人の凶悪な死刑囚、権藤が待ち受けていた。閉ざされた空間で、互いに憎悪を募らせる彼らの前に、やがて謎めいた女性が現れ……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キスより簡単２ 漂流篇", "キスより簡単２ 漂流篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「キスより簡単」の第２作。誰にも束縛されない自由な恋愛を楽しむヒロインの設定は生かしているが、話は前作とつながっていない。今回はまだ見たこともない父親のイメージを追いながら個性豊かな中年男たちとの出会いと別れを経験し、自立していく女性像を描く。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STORIA DI RAGAZZI E RAGAZZE", "いつか見た風景", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1936年、イタリアにファシズムの影が忍び寄る暗黒の時代。その世情をよそに、北イタリアの田舎、ポルリッタ・テルメの農家では、農夫の娘シルビアと、都会の青年アンジェロの婚約祝いが開かれる。両家の集いは、様々なドラマを生み……。P・アバティ監督による、まったくタイプの違うふたつの家族が巻き起こす人間模様。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABDULLADZAN，OR　DEVOTED　TO　STEVEN　SPIELBERG", "UFO少年アブドラジャン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "中央アジア、ウズベキスタンで製作されたSF映画。とある村で未確認飛行物体が着陸。農民のバザルバイは少年のような異星人を発見し、アブドラジャンと名づけて面倒をみる。アブドラジャンは動植物の成長を早めたり、村人たちをクワで飛ばせたりと、不思議な出来事を起こし……。UFOの出現が当たり前のように受け止められる、のどかな村の描写が、ほのぼのとしたユーモアを醸し出す。", "1992-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COURS DU SOIR", "ぼくの伯父さんの授業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 27, "J・タチが「プレイタイム」製作中に作った短編。J・タチ扮する「プレイタイム」のユロ伯父さんが、他の出演者たちにパントマイムの授業をするという内容で、パントの名手タチによる、たばこを吸う、階段につまづく、テニスや乗馬をするなどの演技を見ることができる。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARCELLINO PANE E VINO", "マルセリーノ・パーネ・ヴィーノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1955年の名作「汚れなき悪戯」をリメイクしたファンタジー。11人の修道僧たちに育てられた、いたずらの好きな孤児マルセリーノ。母親に会いたいという彼のせつなる願いが、やがて静かに奇跡を呼び起こす。「天使の詩」のL・コメンチーニが演出を手掛け、少年のピュアな思いを、イタリアの美しい風景の中に描き出す。", "1991-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOODLINE", "華麗なる相続人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "世界的に有名な製薬会社の社長が事故死する。娘のエリザベスが大株主として財産を受け継ぐが、株主たちは会社乗っ取りや莫大な財産を虎視眈々と狙っていた……。人気作家S・シェルダンのベストセラーの映画化で、豪華キャストが話題になった。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rien ne va plus", "最後の賭け", "", "", "http://image.eiga.k-img.com/images/movie/56196/315x210.jpg?1998", 105, "2010年に他界した仏ヌーベルバーグの巨匠クロード・シャブロルの日本未公開作品を連続公開する「映画の國名作選II クロード・シャブロル未公開傑作選」にて上映。マフィアの金に手をつけた詐欺師コンビのてん末を、スイスの雪山や南国アンティル諸島など多彩なロケ撮影で描く。主人公の詐欺師コンビを「ピアニスト」のイザベル・ユペールと、「クリクリのいた夏」のミシェル・セローが演じる。", "2011-05-28", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUSSIAN DOLLS（LES POUREES RUSSES）", "ロシアン・ドールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "留学生の群像を描いた青春映画「スパニッシュ・アパートメント」の続編。5年を経てさらなる人生の壁に行き当たる、若者の苦悩の物語が展開。前作に続いて監督を務めたセドリック・クラピッシュのユーモラスかつリアルな語り口は絶品だ。留学から5年、グザヴィエはパリで作家として奮闘しつつも、煮え切らない日々を過ごしていた。そんなある日、彼はロンドンに住む留学生仲間のウェンディと仕事をすることになり……。", "2006-05-20", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOLLANDS　LICHT", "オランダの光", "", "", "http://ecx.images-amazon.com/images/I/41534SHCFBL._SL210_.jpg", 94, "タイトルの“オランダの光”とは、フェルメールやレンブラントらオランダ絵画の巨匠たちが描いた傑作に見られる、独特の陰影を持つ同地の自然光のこと。だが、1950年代に行われたザイデル海の干拓の影響により、その光はすでに失われたと指摘する人もいる。本作はこの“光”についての真意を紐解くドキュメンタリー。オランダ国内をはじめ、世界各地で採取した“光”の映像と数々の絵画を対比させながら、“光”の存在を探究していく。普段何気なく見ている日の光が、不思議と違うものに見えてくるユニークな切り口の作品だ。", "2004-11-03", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAGIA VERDE", "緑の魔境", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "文明が発達し、全世界を交通機関が網羅する現代社会において、いまだに訪れる人間を拒み続ける南米の密林。４人の取材チームは、ブラジルを出発点として、アマゾン川を遡行。ピラニアや様々な猛獣、そして苦難と戦いながら、イグアスの滝などを取材する。人類最後の秘境に挑んだ驚異の世界。M・クラヴェーリのカメラが秀逸。", "1953-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母に捧ぐる歌", "母に捧ぐる歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "夫を病で亡くした女給上がりの紀子は、昔気質の姑に娘を奪われ、亡夫の薬代の返済のため、上海に流れていく。母を慕う娘は、生前、父がコンクールに応募して入賞した曲「母に捧ぐる歌」を発表会の舞台で歌い上げる……。母子の愛を描いた少女哀話。主題歌の吹き替えは、歌上艶子と中村淑子が担当。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Child is Waiting", "愛の奇跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "　精神科医のクラーク博士が校長を務める精神薄弱児の施設に、音楽教師のジーンが赴任してくる。心を閉ざす少年と仲良くなったジーンはその子を全く面会に来ない両親に会わせようとするが……。障害を抱える子供とそれを見守る大人たちの心情を描いた物語。実際に障害を持つ子供をキャスティングし、この映画を撮り上げたカサベテス監督だったが、製作のスタンリー・クレイマーと対立。結果、カサベテスはハリウッドから干されてしまう。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIND FIELD", "頭脳警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "モントリオール警察のオレイリーは、犯人逮捕の時に過剰なほどに銃を使い、必ずといっていいほど射殺してしまう危険な刑事。彼は最近悪夢に悩まされていた。それはベッドに縛りつけられ、電気ショックを与えられるというもの。そんな彼が、国際陰謀に発展するような強盗事件に遭遇する。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPHERE", "スフィア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "太平洋の沖合で 300年近く海底に沈んでいた巨大な宇宙船が発見された。政府の要請を受けた心理学者ノーマン、彼の古い知り合いの生化学者ベス、ニヒルな数学者ハリーら数人の学識者は謎の指揮官バーンズとともに、その調査にあたることになる。深海居住施設を拠点にリサーチを進める彼らは宇宙船内で巨大な球体“スフィア”を発見、その機能を解明しようとしていた矢先、海上との連絡が途絶えてしまう。そして彼らの潜在意識に眠っている恐怖が、次々と現実となって彼らを襲うのだった……。M・クライトンの小説をもとに、B・レヴィンソンが初めて挑んだSFドラマ。海底で起こる恐怖の事件、そして生き残った人間たちの間に生まれる葛藤が、スリリングに描かれる。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヨコハマメリー", "ヨコハマメリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "“ハマのメリー”と呼ばれた伝説の娼婦の行方を追いかけたドキュメンタリー。親交の深いシャンソン歌手・永登元次郎らの証言から、戦後50年に渡って街頭に立ち続けた女の面影が深く静かに浮かび上がる。1995年、冬。メリーさんが姿を消した。白塗りのメイクでドレスに身を包み、知る人ぞ知る存在であったあの老婆は何者だったのか？ 彼女の消息を追うと共に、彼女の過去を探っていく。", "2006-04-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO　GILLIAN　ON　HER　37TH　BIRTHDAY", "逢いたくて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "M・ファイファーが美しいゴーストになって登場する切ないラブ・ロマンス。デビッドの妻ジリアンの37歳の誕生日に、彼女の姉夫婦が訪ねて来る。この日は事故で亡くなったジリアンの命日でもあった。彼女の死から２年たってもふさぎこみ、亡妻のゴーストに語りかける父を、娘レイチェルは寂しく見つめていた。そんな彼らの関係を心配した姉夫婦は、レイチェルを引き取りたいと申し出る。娘役のC・デインズが傷つきやすい少女をみずみずしく好演している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Man on a Ledge", "崖っぷちの男", "", "", "http://image.eiga.k-img.com/images/movie/57259/315x210.jpg?3001", 102, "「アバター」のサム・ワーシントン主演で、ある計画のため飛び降り自殺をしようと見せかける男と、その男と対峙する女性刑事の駆け引きを描いたサスペンス。30億円のダイヤモンドを強奪した罪で収監された元刑事のニックが脱獄。ニューヨーク・ルーズベルトホテル高層階から身を乗り出し飛び降りようとする。制止しようと説得する警察に対しニックは、最近の任務に失敗して後がない女性刑事リディアを唯一の交渉役として指名するが……。共演にエリザベス・バンクス、エド・ハリス、ジェイミー・ベルら。監督はドキュメンタリー出身で本作が長編デビュー作のアスガー・レス。", "2012-07-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’E´TUDIANTE", "スチューデント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "フランスを代表する女優S・マルソーが、デビュー作「ラ・ブーム」のC・ピノトー監督と久しぶりに組んだ青春映画。ソルボンヌ大学で教員資格取得のため学んでいるヴァランチーヌと、浮気がちなボーイフレンドとの愛の行方を繊細なタッチで描く。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TEMP", "派遣秘書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "公私ともに難問を抱えている若きエリート社員ピーターのもとに、ある日クリスと名乗る秘書が派遣されてきた。強烈な個性で、上昇志向の強いクリスは、仕事でも社長の前で抜け目なく自己アピールし、ピーターに対しては色仕掛けで迫る。その頃、社内では自殺や事故の怪事件が続き、さらにライバル社への情報漏れも判明し……。出世欲に取り憑かれた人間たちの醜悪な様と、連続殺人の恐怖を描いたサイコ・サスペンス。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花咲く港", "花咲く港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "南九州のある港町に、昔ある男が漂然とやって来て造船所を造ろうとしたが失敗。しかしその男は豪胆な性格のため、人々から尊敬をもって迎えられる。ある日、男は突然南方へ旅立って行く。それから15年が過ぎ、その男の遺児と名乗る男が港町に出現。それに続いてまた一人、遺児と名乗る男がやって来る。二人の遺児は兄弟だった、と思いきやそれは真赤な嘘。彼らペテン師が純朴な村人たちの中で繰り広げる騒動を描いたユーモラスな作品。木下惠介の第１回監督作である。木下は最初松竹蒲田の現像部に入社、その後撮影部に転じ、入社３年目にして念願の演出部に移った。やがて書き続けた脚本が認められ監督に昇進した。DVDは「木下惠介　DVD-BOX 第1集」に収録。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グッバイ・ママ", "グッバイ・ママ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "作詞やTVドラマのプロデュースなどで活躍するヒット・メーカー、秋元康の初監督作品。証券会社で働くキャリアウーマン、かな子は優雅な独身生活を送っていたが、そんな彼女のもとへ、かつての恋人の子供・健が転がり込んでくる。最初は迷惑するかな子だったが、いつしか健に心を開いていく。そんなある日、かな子はニューヨークへ行くことになり……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROM BEYOND", "フロム・ビヨンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "脳の可能性を追求する実験を重ねた結果生まれてしまった怪物が、研究所を訪ねた二人の男女に襲いかかる……。「ZOMBIO 死霊のしたたり」で一躍脚光を浴びた奇才S・ゴードンが、再びH・P・ラヴクラフトの原作に基づき撮り上げたホラー映画。奇想天外なアイデアとSFXを駆使した独自の映像世界を構築。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLAYING GOD", "不法執刀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "TVシリーズ“X−ファイル”のD・ドゥカブニー主演によるサスペンス。手術で用いる麻薬に手を出し、医師免許をはく奪された元外科医ユジーンは、禁断症状に苦しみながらすさんだ生活を送っていた。ある日ユジーンは、バーで襲撃された犯罪組織のボス、レイモンドの応急手当てをし、その手腕を見込まれて彼の専属医となる。多額の報償金につられ裏の医師として活動し始めた彼は、やがてレイモンドの愛人と恋に落ちるのだが……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦国無頼", "戦国無頼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "時は戦国。織田信長によって滅ぼされた浅井長政の家臣・佐々疾風之助、立花十郎太、鏡弥平次は、落ちのびて後、それぞれ流転の人生を歩む。時は流れ、十郎太は織田方に、疾風はその敵方に分かれ、弥平次を加えた３人は、再び戦場で出会うが……。山口淑子が十郎太と疾風之助の間で揺れる野武士の娘、おりょうを可憐に演じている。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("斬り込み", "斬り込み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "長谷部安春、小沢啓一とともにニュー・アクションの一翼をになった澤田幸弘監督のデビュー作。大組織、関東連合会の傘下に入り、自分たちの組に裏切られたチンピラたちの、はけ口のない怒りをエネルギッシュに描く。チンピラに扮した藤竜也、岡崎二朗が熱演。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おかえり", "おかえり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "恋愛の末に結ばれ、これといった不満も問題もないはずだった平凡な若い夫婦。ある日、妻の不審な行動から、彼女が精神分裂病にかかっていると知らされた夫は、彼女自身が作り上げた妄想から解放しようとする……。誰もが潜在的に抱える孤独や焦燥感を主題に、愛することのはかなさや切なさを綴った愛の物語。映画批評家・篠崎誠の劇場用映画デビュー作。エキセントリックな表現を排除した演出はごくシンプルだが、力強さに満ちている。加藤泰との名コンビで知られるベテラン・カメラマン、古谷伸が篠崎を好サポートし、小津安二郎作品の名子役、突貫小僧こと青木富夫が特別出演している", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("放課後", "放課後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "森谷司郎監督による東宝青春路線の一編。栗田ひろみを主演に、大人の世界を垣間見た一人の女子高生の揺れ動く心と、無垢ゆえの残酷さを描く。栗田の愛くるしい笑顔と鋭いまなざしが印象的。彼女の初恋の対象となる地井武男の渋い中年男ぶりも魅力。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHILADELPHIA EXPERIMENTII", "フィラデルフィア・エクスペリメント II　超時空決戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "タイムスリップをテーマにヒットしたSF「フィラデルフィア・エクスペリメント」の続編。第二次世界大戦中、海軍の秘密実験の失敗により、駆逐艦に乗っていたデビッドは1984年へとタイムスリップしてしまった。それから10年後、彼は海軍から離れて幼い子供と平和に暮らしている。一方、軍は秘密プロジェクトを再開し、核を搭載したステルス戦闘機を瞬間移動させる実験を進めていた。しかしステルスは時空間のひずみに吸い込まれ、精鋭パイロットを登場させたまま1943年のナチスドイツへとタイムスリップしてしまう。ステルスを手に入れたナチスは第二次大戦に勝利し、歴史は大きく変わってしまった。ナチスが支配する“もう一つの1993年”へと飛ばされたデビッドは、愛する世界を取り戻すために独裁国家と巨大な時空の流れに挑む。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUYS AND DOLLS", "野郎どもと女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "ニューヨークの下町を書かせては右に出る者がいないデイモン・ラニヨンの原作によるミュージカル大作。博徒のスカイ、ネイサン、救世軍の娘セーラ、ナイトクラブの女、この４人の恋物語などがフランク・レッサーの佳曲にのせて繰り広げられる。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH RING", "ファイナルフォース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "大富豪が暇と金にあかして主催する殺人ゲームに、誘拐され連れられてきた男マット。元アメリカ陸軍中尉である彼が、殺人ゲームの場となる島の中で、武器も無くたった一人で闘うことになる。マット役のM・ノリスは生身アクションのベテラン、チャック・ノリスの息子であり、自身もスタントなしの過激なアクションを演じている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADVENTURERS", "冒険者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 171, "革命の機運高まる1950年代の中南米の小国とヨーロッパをまたにかけ、波乱万丈の生涯を送った、ダックス・ゼノスの一代記。幼いころに革命騒ぎで母と姉を殺された彼が、やがて革命軍に身を投じ革命を成功させ、今度はヨーロッパで名を成す。だが故国に再び戦雲が……。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCENT OF A WOMAN", "セント・オブ・ウーマン 夢の香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 157, "全寮制名門校の生徒チャーリーは、休暇中のアルバイトで全盲の元陸軍中佐フランクの世話をすることになる。人間嫌いとも思えるほど頑迷なフランクは、チャーリーを道先案内役としてニューヨークに旅に出る。最高のホテル、最高のレストラン、最高の車、そして最高の美女を求め歩くフランクに引きずり回されるチャーリーは面くらうが、実はこの旅には、一つの隠された目的があった……。年の差を超えた二人の男の心の触れ合いを描き、生きることの素晴らしさを謳い上げた感動作。盲目の退役軍人を演じたA・パチーノの複雑な人間心理を表現していく様は圧巻で、念願のアカデミー主演男優賞を射止めた。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PINOCCHIO", "ピノッキオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "世界中で親しまれている、おなじみの童話『ピノキオ』を原作に忠実に映画化。「ライフ・イズ・ビューティフル」のロベルト・ベニーニが監督、主演を務め、夢とユーモアあふれる世界を創造した。妖精の魔法を秘めた丸太から作られた、命を持つ人形ピノッキオ。作り主の老職人ジェペットは、いたずら好きの彼を良い子に育てようと、学校に通わせる。だが、詐欺師のネコとキツネにだまされたピノッキオは刑務所に入れられてしまい……。", "2002-01-01", "4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ICE STATION ZEBRA", "北極の基地 潜航大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 148, "北極の平原にあるイギリスの気象観測基地からSOSが発せられた。さっそく米・ソが生存者救出に乗り出すが、実はそこでは、ソ連の人工衛星が撮影したミサイル基地のフィルムをめぐって米・ソで激しい戦いが繰り広げられていたのだ。生存者救出に向かったアメリカ原子力潜水艦の艦長ファラディは、その争いに否応なく巻き込まれる。北極圏で撮影されたスパイ・アクション。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flashback", "フラッシュバック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "FBI捜査官のジョンは、典型的な現代のエリート青年。その彼が、20年間地下に潜っていたヒッピーの教祖的存在ヒューイの護送を命じられる。1960年代にどっぷりつかったヒューイとジョンの珍道中が始まる。“ハリウッドの反逆児”“アメリカン・ニューシネマの旗手”と讚えられたあと、ハリウッドを追われ、ドラッグに溺れ、再び監督としてカムバック。見事復活したD・ホッパーが、ヒッピーの教祖という、まさにハマリ役で登場。K・サザーランドとの共演も異色の顔合わせである。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRIUMPH OF THE SPIRIT", "生きるために", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "「プラトーン」の製作者A・コペルソンが、第二次大戦下のアウシュビッツを通して極限下の人間の愛を描いた衝撃のドラマ。収容所の将校たちの屈折した欲望を満たすために、死と背中合わせのボクシングを課せられるバチカン市国のミドル級チャンピオン。アウシュビッツで様々な葛藤と戦いながら200回もの試合を強いられた男の実話を映画化。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富嶽秘帖・完結編", "富嶽秘帖・完結編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "谷底へ転落した亜矢姫は富士根の里に住むお雪に救われ、又四郎に再会する。やがて能面から財宝のありかが富士根の里の洞窟と判明し、又四郎は謎の浪人・嶽十郎の加勢を得て勘兵衛一味を粉砕。若山富三郎が主人公の危機を救う謎の浪人・嶽十郎に扮する時代活劇。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月曜日のユカ", "月曜日のユカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "横浜を舞台に、数々の噂に彩られつつ、男たちの憧れをくすぐってやまぬ18歳の娘・ユカ。初老のパトロンと同年代の恋人を持ち、男を喜ばせるのが最大の生きがいという彼女だが、身勝手なロマンと打算に生きる男たちは彼女の奔放さにあたふたするばかり。ユカをめぐる恋愛ゲームは、とうとう皮肉な結末に……。奇抜な構図、シャープなライティング、定石破りのカット割り、ドタバタ・コメディ調の処理など、異能派・中平康は技巧を尽くして、刹那の男女関係をドライに描出。当時、20歳を越えたばかりの加賀まりこがシネスコ・サイズいっぱいに若い姿態を躍動させて、愛すべき小悪魔ぶりを発揮している。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("急凍奇侠／ICEMAN COMETH", "タイム・ソルジャーズ 愛は時空〈とき〉を超えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "非道の限りを尽くし、17世紀の明王朝時代から、タイム・マシンを使って300年後の現代に逃亡してきた衛兵隊長フォン。それを追って、やはり現代にやってきた副隊長のチェン。二人の宿命の戦いを描いたアクションが繰り広げられる。チェンと現代の女性、ポーラとのロマンスを絡めた明王朝時代と現代のカルチャー・ギャップも楽しい。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZULU", "ズール戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "イギリス史上に名高いズール戦争を、現地アフリカ・ロケによって再現した戦争スペクタル巨編。わずか100名のイギリス軍守備隊が、4000名のズール族の襲撃を退けるまでを勇壮に描いていく。イギリス映画界を代表する大物スターが総出演。M・ケインはこれが出世作ともなった。", "1963-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・浪花の恋の寅次郎", "男はつらいよ・浪花の恋の寅次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "瀬戸内の小島で大阪の芸者・ふみと出会った寅さん。生き別れの弟に会うのをためらうふみをけしかけて会いに行く二人。しかし弟はもはやこの世にはいなかった。柴又へ帰った寅のもとへ、ふみが訪ねて来たから大変。寅はその気になるが……。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POU−VOIR INTIME", "パーソナルパワー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "カナダのフランス語圏に属するケベック州出身の監督E・シモノーが、実際に起きた事件を取材して撮った犯罪映画。出所間近の囚人テイオウの元に、ある日、警察上層部の男が現れ、ある依頼を持ちかけるのだが……。", "1985-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vampires Suck", "ほぼトワイライト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "全米大ヒットを記録した人気シリーズ「トワイライト」などの青春バンパイア映画をパロったコメディ。10代の少女ベッカが、青年2人の間で揺れ動くという「トワイライト」のストーリーを、「アリス・イン・ワンダーランド」、レディーガガ、ツイッター、そしてiPhoneといった若者文化を絡めて描く。監督は「最終絶叫計画」シリーズを手がけてきたジェイソン・フリードバーグとアーロン・セルツァー。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEAST", "レッド・アフガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ソ連軍がまだアフガン侵攻を続けていた頃。ソ連の戦車部隊はある村を急襲、壊滅に追い込む。しかし非道なダスカル隊長と、理想家のカバチェンコは対立。ダスカルは部下に命じ、カバチェンコを岩に縛りつけ、置き去りにする。やがてアフガン・ゲリラに助けられたカバチェンコは苦悩し、彼らの味方となる。「ウォーターワールド」などで知られるK・レイノルズ監督が手掛けたスリリングな戦争ドラマの傑作。DVDは「コロムビアトライスター ウォー・ムービー・コレクションBOX(2)」に収録。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tangled", "塔の上のラプンツェル", "", "", "http://image.eiga.k-img.com/images/movie/54469/315x210.jpg?2353", 92, "グリム童話の「ラプンツェル（髪長姫）」を映画化した、ウォルト・ディズニー・スタジオの長編アニメ第50作。逃亡中の盗賊フリン・ライダーは、山奥の谷にそびえ立つ高い塔を見つける。好奇心から塔に入ったフリンは、髪が驚くほど長い不思議な少女ラプンツェルと出会う。彼女は18年間、塔の中だけで生活してきたがフリンとともに外の世界へ冒険の旅に出ることになる。", "2011-03-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCHLOCK", "シュロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "「ブルース・ブラザース」のJ・ランディス監督の長編デビュー作となったパニック映画。ピクニックに出かけた人々を惨殺した類人猿に近い謎の生物が、街にやって来てさらなる惨劇を巻き起こしていく。スリリングなストーリー展開の中で、随所にのぞかせるB級感覚が楽しい。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間の約束", "人間の約束", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "佐江衆一原作の『老熟家族』をもとに、吉田喜重が「BIG1物語・王貞治」以来８年ぶりにメガホンを執った骨太社会派人間ドラマ。東京の新興住宅地で寝たきりの老母・タツが死ぬが、他殺の形跡が発見される。タツの夫・亮作は自分が絞殺したと自首するが、彼は取調室で失禁するほどボケていた……。吉田喜重監督は、当時ちょっとしたブームになっていたボケ老人の問題を決してちゃかすことなく真摯な態度で見据え、８年間のブランクを感じさせぬ確かな演出をみせた。全編を通じて透明感あふれる水のイメージが描かれ、この暗く重い映画にとって、一種の清涼剤的な役割を果たしている。外光の射し込む日本家屋の空間を生かした、撮影の成果も特筆に値する。DVDは「吉田喜重全集［86-03］炎を映す水」に収録。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("皇家戦士／ROYAL　WARRIORS", "皇家戦士 Royal Warriors", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "殺し屋を護送中の飛行機がハイジャックされそうになる。これを救ったのが日本人の退職刑事ヤマモトと香港の美人刑事ミシェルら３人。彼らはそのため“殺人兵団”と戦うことになる。真田広之の魅力が冴えるバイオレンス・アクション映画。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イノセントワールド", "イノセントワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "桜井亜美の同名ベストセラー小説を『CUTE』の下山天が繊細なタッチで映画化。17歳の少女アミは、実の父である精子ドナー・307を探す旅に出る。知的障害児の腹違いの兄タクヤを連れて、青森の竜飛岬を訪れた彼女は、そこで診療所を営む父、高森を発見。彼の妻と４人で奇妙な共同生活を始めるが……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE MEN AND A BABY", "スリーメン＆ベビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "セザール賞を受賞した秀作フレンチ・コメディ「赤ちゃんに乾杯！」をハリウッドがリメイクし、1987年のクリスマス・シーズンに大ヒットを記録した作品。ピーター、マイケル、ジャックの３人は、共同で豪華なマンションを借りている独身貴族。そんな彼らが、ジャックが間違って作ってしまった赤ちゃんの世話をすることになり、慣れない手つきで四苦八苦。やがて、ひょんなことから麻薬事件の容疑もふりかかり、独身アパートは大混乱に……。いかにもアメリカ映画らしく、ライトでポップな笑いに満ちたほのぼのコメディ。監督は“ミスター・スポック”役でおなじみのL・ニモイ。その才人ぶりがうかがえる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兩公婆八條心／STRANGE BEDFELLOWS", "香港トワイライト・ゾーン 摩訶不思議物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "香港版「トワイライト・ゾーン」とも言えるオムニバス形式のミステリー・ムービー。男が赤ん坊を生むようになった近未来をSFタッチで描いたものや、離婚を迫った夫を妻が呪術で殺すという猟奇的なものなど、３編から構成されている。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPEN　WATER", "オープン・ウォーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "大海原に置き去りにされたダイバーの恐怖を描くスリラー。CGや視覚効果を使わず、海洋での撮影を繰り返して収められた迫真の映像が、取り残された不安や恐怖をリアルに伝える。休日を利用して3年近くかけて撮り上げたという新鋭クリス・ケンティスの根気と、濃密な演出に脱帽させられる。カリブ海でのダイビング中、海に置き去りにされた夫婦。やがて体調悪化やサメの襲来によって、彼らの極限状態はピークに達し……。", "2005-06-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SNAKE EATER", "コブラ・キラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "刑事ケリーは海兵隊のエリート・コマンドー部隊“スネーク・イーター”の出身。ある日、休暇中の彼の両親が惨殺され、妹が誘拐されるという事件が起きた。怒りに燃えるケリーはならず者たちへの復讐に立ち上がる。敏腕刑事の復讐劇を描いたハード・アクション。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビッグ・ショー！ハワイに唄えば", "ビッグ・ショー！ハワイに唄えば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "TV『雷波少年』や映画「のど自慢」に登場した、室井滋扮する売れない演歌歌手、赤城麗子の、その後を描いたコメディ。営業と慰安旅行を兼ねてハワイにやって来た麗子は、偶然現地で公演を開こうとしていた都はるみの前座を務めることに。ところが、初日のステージは散々。すっかり自信をなくした麗子は、現地の日系人青年のプロポーズに揺れ動き、歌を捨てようとするが……。「のど自慢」同様、室井滋が不器用な人間の奮闘をコミカルに妙演。彼女の歌う『大阪しぐれ』、都はるみの『好きになった人』など、おなじみのメロディーがドラマをホットに盛り立てる。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひろしま", "ひろしま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "関川秀雄が、原爆体験者の手記『原爆の子』の一編を映画化した社会派ドラマ。広島A高校に通う三年のみち子はクラスで原爆当時のラジオを聴いていて、恐怖心から突然倒れてしまう。しかしそれは、原爆によって白血病を発症していたのだった……。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEMORIES OF MURDER", "メモリー 血ぬられた殺意", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「殺しのドレス」「ミッドナイトクロス」とサスペンスの似合うN・アレンが主演するサイコ・スリラー。共演は元プリンス・ファミリーのヴァニティ。ジェニファーは幸せな結婚生活を送っていたが、ある日一人の女の顔を目撃したことで記憶喪失となる。その女とは、ジェニファーにとって忘れることのできない過去の事件の鍵を握っていたのだった。夫との幸せな日々も忘れてしまったジェニファーの周囲に様々な謎がふりかかる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アキレスと亀", "アキレスと亀", "", "", "http://image.eiga.k-img.com/images/movie/53659/315x210.jpg?2709", 119, "「HANA-BI」「座頭市」の北野武監督の長編14作目。出演は北野武（ビートたけし）のほかに、樋口可南子、柳憂玲、麻生久美子ら。売れない画家の真知寿は、幼い頃に両親を亡くしてからも画家になることだけを夢見てきた。そんな純朴な真知寿に惹かれた幸子と真知寿はやがて結ばれ、夫婦で創作活動に没頭していくが……。真知寿のかなわぬ夢に立ち向かう苦悩や、彼を陰から支える幸子の夫婦愛を描く。", "2008-09-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Soudain le vide", "エンター・ザ・ボイド", "", "", "http://image.eiga.k-img.com/images/movie/55349/315x210.jpg?2564", 143, "「アレックス」で物議を醸したギャスパー・ノエ監督が、東京を舞台に固い絆で結ばれた兄弟を描く作品。東京にやってきたばかりのドラッグ・ディーラーの兄オスカーと、ナイトクラブでストリッパーとして働く妹リンダ。ある日、オスカーは警察の捜査に遭い、拳銃で撃たれてしまう。遠のく意識の中、リンダのことを強く思ったオスカーの魂は現世にとどまり、東京の街をさまよい出す。", "2010-05-15", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PURSUED", "ヘッドハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "画期的な技術を開発したベンチャー企業の男が、大企業の引き抜きの標的となり、悪夢のような運命をたどるサスペンス劇。悪役の“ヘッドハンター”に扮するのはクリスチャン・スレーター。巧みな話術と情報収集で獲物に近づき、必要とあらば殺人も辞さないその非情な手口が戦慄を呼ぶ。画期的な追跡システムを開発したベンの前に、ヘッドハンター、ヴィンセントが現れる。それを拒絶したベンの周囲で異常な出来事が……。", "2005-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("皇家女将／SHE SHOOTS STRAIGHT", "98分署 香港レディ・コップス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "警官一家のウォン一家は、父と二人の兄が殉職したものの、残された息子ツンパオと４人の妹たちもすべて警官として活躍している。やがてツンパオはエリート警官ミナと結婚。その頃、香港の街をベトナム人のギャングが荒らし始めていた。任務に就いたツンパオは殺され、ミナと妹たちは復讐戦を開始する。エキゾチックな魅力をもつJ・コウ主演の、女性版カンフー・アクション。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONEY", "女神がそっと微笑んで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "実業家であった父親の遺産で、勝手気ままな生活を送っていたフランク。その遺産をすべて使い切った彼は、目的もなくアフリカに向かった。そこで偶然から始めたツアー斡旋業が当たり、さらに恋人も得て幸福な日々を過ごしていた。そんなある日、見知らぬ人物から父の遺産についての意外な事実を手紙で知らされる……。若手スターのE・ストルツがビジネス界のドロドロとした世界に立ち向かう青年役に挑んだ作品。", "1991-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD LOVE", "マッド・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "傷つきやすい少女との恋愛に悲劇的な結末を選ぶ青年の恋を描く恋愛物語。高校生のマットは転校生のケイシーの型にとらわれない自由に生きる姿に強く惹かれる。二人はすぐに恋仲になるが、時に非常識な行動をとるケーシーとつき合うことを親に禁止され、ケーシーはそのことで自殺未遂を起こしてしまう。二人は、なけなしの金を持って旅に出る。その途中で様々なトラブルが起き、最後にはケーシーの感情は爆発するのだった……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAMMY MARKET", "マミー・マーケット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "12歳のエリザベスのママは、パパのいないマーティン一家の大黒柱。がんばり屋だけど、すごく厳しいママのお小言にすっかりうんざりしたエリザベスは、もっと違うママが欲しいと願ってしまう。すると、隣のカブールおばさんが魔法の呪文を教えてくれた。その呪文を唱えると“マミー・マーケット”で今のママの代わりに理想のママを選べるのだ。気に入らなければ返品、交換もOK。ただし魔法は３回まで。エリザベスはさっそくママをとり替えるが……。主演は、「マイ・ガール」の美少女スター、A・クラムスキー。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sympathy for Delicious", "シンパシー・フォー・デリシャス", "", "", "http://image.eiga.k-img.com/images/movie/56169/315x210.jpg?2963", 101, "かつて伝説的DJとして名をはせたディーンは、事故で体がまひし、スラム街で車上生活を送っていたが、ある日、不思議な治癒能力に目覚めたことから人々の注目を集める。そうして得た富と名声を再び音楽界への成功につなげようと考えたディーンは、カリスマ的リーダーのステインが率いるロックバンドに加わるが……。俳優マーク・ラファロの初監督作で、オーランド・ブルームがパンクロックのリーダーを熱演。", "2011-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Danse, Le Ballet de L'Opera de Paris", "パリ・オペラ座のすべて", "", "", "http://image.eiga.k-img.com/images/movie/54719/315x210.jpg?4228", 160, "フランス国王ルイ14世が権力を尽くして作り上げた世界最古のバレエ団「パリ・オペラ座」の舞台裏を記録したドキュメンタリー。監督はドキュメンタリー界の巨匠、フレデリック・ワイズマン。公演では見られない、「エトワール」と呼ばれる最高位のダンサーたちの練習風景や、ダンサーたちを支えるスタッフの日常を映し出す。", "2009-10-10", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飛ぶ夢をしばらく見ない", "飛ぶ夢をしばらく見ない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "山田太一の同名小説を映画化。中年の田浦修司は、北陸の病院で入院中に一晩だけ睦子という老婆と相部屋になる。回復した彼が東京で働いていると、睦子から電話が。会ってみると、睦子は妖艶な中年女性の姿になっていて……。愛する女性が急速に若返っていく。その中で、中年の姿のまま取り残される男。悲劇が待っていると知りながら、すべてを捨てて睦子を愛する田浦を細川俊之が好演。睦子に扮した石田えりも70歳から10代の少女までを演じ分けている。日本には数少ない大人のためのファンタジーといえよう。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VALLEY OF THE DOLLS", "哀愁の花びら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "大学を出てニューヨークの芸能プロダクションの秘書として働くことになったアンは、芸能界の裏側の人々の駆け引きや愛憎劇を目のあたりにする。そして自分もまた、その舞台裏に引き込まれて……。B・パーキンス、S・テイト、S・ヘイワードの３女優の競演が見どころ。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大木家のたのしい旅行　新婚地獄篇", "大木家のたのしい旅行　新婚地獄篇", "", "", "http://image.eiga.k-img.com/images/movie/55909/315x210.jpg?2903", 121, "新婚の大木信義と咲は、ケンカも絶えず早くもけん怠期を迎えていた。そんなある日、近所のスーパーで出会った怪しい占い師に勧められ、1泊2日の温泉付き新婚旅行へ出かけることに。2人は旅先で次々と不思議な出来事や奇妙な人々に遭遇し……。人気劇作家・前田司郎による同名小説を映画化。信義役の竹之内豊がコメディに初挑戦。妻の咲には水川あさみが扮する。共演に樹木希林、荒川良々、橋本愛、柄本明ら。", "2011-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RADIO DAYS", "ラジオ・デイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "第二次世界大戦が始まったばかりの頃、テレビではなく、ラジオが家族団らんの中心だった時代を背景に、ニューヨークの海辺の町に住む少年ジョーの一家の生活と、マンハッタンのナイトクラブでシガレット・ガールをしながらラジオのパーソナリティーを夢見ているサリーの生活とを、織り交ぜながらコミカルに描き、古き良きアメリカへの郷愁を、軽妙なタッチでうたい上げた作品。今回はアレン自身は出演していないが、少年の目を通して見たお人よしの両親、伯母夫婦、結婚に憧れながらいつも男運の悪い叔母などに扮する俳優たちはみんな“アレン一家”と呼んでもいい面々。ラジオやクラブのステージから流れる音楽は往年のヒット曲ばかり43曲で、音楽映画を見ているような楽しさも同時に観客に与えてくれる。アレンの自伝的色彩も濃厚な作品である。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO BE OR NOT TO BE", "メル・ブルックスの大脱走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1942年のエルンスト・ルビッチ監督作品「生きるべきか死ぬべきか」の再映画化。ナチ政権下のドイツ。ある演劇一座が、ユダヤ人狩りの魔手を逃れ国外脱出を図る……。ブルックスならではのミュージカルとコメディの妙味を生かした佳作。特に彼のヒトラーぶりが圧巻だ。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blindness", "ブラインドネス", "", "", "http://image.eiga.k-img.com/images/movie/53429/315x210.jpg?2384", 121, "ノーベル文学賞作家ジョゼ・サラマーゴの「白の闇」を、「シティ・オブ・ゴッド」「ナイロビの蜂」のフェルナンド・メイレレス監督が映画化。突然視界が真っ白になり視力を失う奇病が世界中に蔓延。隔離された感染者たちは不安と恐怖から次第に人間の醜い本性をむき出しにしていく……。混乱する世界の中で唯一目が見える主人公をジュリアン・ムーアが演じるほか、マーク・ラファロ、ダニー・グローバー、ガエル・ガルシア・ベルナル、伊勢谷友介、木村佳乃ら国際色豊かなキャストが顔を揃える。", "2008-11-22", "10,17,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRAVE HEART", "ブレイブハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 177, "13世紀のスコットランドに実在し、その独立と解放を目指して戦った男の半生を描いたヒストリー・ロマン。幼い頃、イングランド国王エドワード一世の侵略によって家族を皆殺しにされたウィリアムは異郷の地で成長し、帰ってきた故郷で幼なじみのミューロンと再会する。二人は激しい恋に落ち結婚するが、その直後に駐屯中のイングランド兵にミューロンを目の前で殺害されてしまう。愛する人を殺されたウィリアムは復讐を誓って抵抗軍を組織、スターリング・ブリッジの戦いで英国軍を撃破するまでになるのだが……。M・ギブソンが製作・監督・主演を務めて、見事アカデミー賞５部門を制した大作。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RITEN", "夜の儀式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "あるヨーロッパの小国で男二人女一人の小劇団が起訴される。彼らの“祭式”という出しものがワイセツ罪にひっかかったのだ。やがて予審判事のもとに一人ずつ出頭した彼らは、審問の間にそれぞれの過去を明かしていく……。ベルイマン初のTV用作品で日本では劇場公開された。彼独特の人間ドラマが緊密に描かれている。", "1969-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KHARAEJ AZ MAHDOUDEH", "オフ・ザ・リミット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ハリミが建てたしい家に、ある晩、泥棒が入る。ハリミは近所の人たちの協力で泥棒を捕まえ、警察に突き出すが、管轄が違うと警察は相手にしてくれない。翌日、違う警察に行くが、ここでも追い返されてしまう。役所に行くと、ハリミの住むカオス・シティーが地図に載っていないことがわかる。仕方がなく、ハリミは隣人たちと町の中に仕切りを作り、他の地区を追い出してしまうのだった。テヘランを舞台にした滑稽な社会風刺劇。", "1988-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PALAIS ROYALE", "パレ・ロワイヤル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1950年代の終わりのカナダ・トロント。時代に名を残そうと、若く、野望に満ちた一人の男がこの町にやって来る。ふとしたことから彼はモデルをしている女と知り合い、恋に落ちるが、彼女はギャングの娼婦でもあった。男は女のそばにいたいがために、危険な仕事に手を染めていく……。ノスタルジックなムードの中に、危険な恋愛と組織内のすさまじい抗争を描いたギャング・ストーリー。", "1988-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本のいちばん長い夏", "日本のいちばん長い夏", "", "", "http://image.eiga.k-img.com/images/movie/55529/315x210.jpg?1492", 0, "「佐賀のがばいばあちゃん」の倉内均監督が、1度も戦争の体験を語ることなくこの世を去った父への思いから、1963年に行われた歴史的座談会「日本のいちばん長い日」を再現する文士劇。ポツダム宣言に対する日本政府の対応、原爆の投下、ソ連の参戦、終戦へ至る経緯などについて、現代日本を代表するジャーナリストや文学者が熱い議論を交わす。", "2010-08-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREDDY’S DEAD：THE FINAL NIGHTMARE", "エルム街の悪夢 　ザ・ファイナルナイトメア", "", "", "http://ecx.images-amazon.com/images/I/51H971E98GL._SL210_.jpg", 89, "“エルム街の悪夢”シリーズの完結編。殺人鬼フレディ誕生の秘密が明かされ、そして、フレディは真に永遠の眠りにつく……。ラスト15分を3D−SFXで見せる凄絶なクライマックスも話題となった。また、少年時代のフレディの義父役で登場するロック・ミュージシャンのA・クーパーの怪演にも注目だ。", "1992-02-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PIRATE", "踊る海賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ミネリ、ガーランド、ケリーのトリオによるテクニカラーMGMミュージカル。19世紀のカリブ海、市長と愛のない結婚を強いられた娘は、海賊マココとの恋を夢みるが、サーカスの男に恋をして……。C・ポーターの歌が魅力的。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("卍", "卍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "弁護士を夫に持つ人妻が、偶然知り合った独身の若い女の魅力の虜となり、同性愛の関係に陥る。そこに若い女の恋人である青年が絡んできて、夫を交えて奇妙な人間関係と倒錯したセックスの世界が、人妻の告白という形で描き出されている。女同士の同性愛という、その頃の日本映画ではタブー視された題材であるが、増村監督は、“恥も外聞もなく欲望を表現する狂人”たちが繰り広げる饗宴を肯定的に力強く描き、増村流美学をみなぎらせている。なお、この小説の映画化は市川崑監督が長年温めていた企画であるが、市川監督が「東京オリンピック」にかかったために、増村監督が演出を務めたという経緯がある。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HONG KONG 1997", "ラスト・バトル　HONG KONG1997", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1997年の中国返還まであと１日に迫った香港。ヒットマンのレッジはシャーウッド社に依頼された中国軍のウー将軍の暗殺を実行する。しかし、彼の首に1000万ポンドの賞金が懸けられてしまい、次々と暗殺者の魔の手が伸びる。国際陰謀が渦巻く混沌の香港を舞台に、英国人ヒットマンの決死の脱出を描いたハードボイルド・アクション。主演は「ターミネーター２」のT−1000役で一躍注目を浴びたR・パトリック。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L' ANTICRISTO", "レディ・イポリタの恋人／夢魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「エクソシスト」にお色気を盛り込んだ伊製オカルトホラー。幼い頃の事故で、両足が不自由になった美女イポリタ。最愛の父が再婚することを知り、孤独を募らせる彼女は、悪魔の力に取り憑かれ……。レトロな特撮を用いた超常現象シーンが、ファンタスティックに描かれる。", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DMZ", "DMZ非武装地帯／追憶の三十八度線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "韓国と北朝鮮を隔てる軍事境界線で発生した非常事態を、イ・キュヒョン監督が自ら発表したベストセラー小説に基づいて映像化。朴大統領暗殺事件の混乱に乗じて侵攻してきた北朝鮮軍と、それを迎え撃つ韓国特殊部隊の激闘が展開。若き兵士の叫びを通して、朝鮮半島分断の悲劇を描き出す。", "2005-07-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("집으로...", "おばあちゃんの家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "　わがままに育てられたソウル生まれの孫と、山村で素朴な生活を送る祖母のひと夏の交流を「美術館の隣の動物園」で鮮烈なデビューを飾ったイ・ジョンヒャンがユーモアあふれる視点で描く。おばあちゃん役のキム・ウルブンは、実際にロケ地の村で暮らし、「生涯1度も映画を見たことがない主演女優」という点でも話題に。孫のサンウとその母親役のほかは、ロケ地の村人たちがキャスティングされている。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Red Dawn", "若き勇者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "　コロラド州の田舎町に降下したパラシュート兵が人々を虐殺。町はキューバ、ソ連、ニカラグアの連合軍によって占拠されてしまう。そんな中、ジェドや弟のマットといった高校生の男女数名が山に逃げ延びる。やがて彼らは銃を手にゲリラ戦を開始。占領軍に徹底抗戦を繰り広げる若者たちだったが、彼らの中からも犠牲者が……。タカ派ジョン・ミリアス監督がアメリカを侵略した敵軍に立ち向かう少年・少女たちの死闘を描く。当時の新進若手俳優たちが共演。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PERSONAL CHOICE", "宇宙への選択", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "アポロ計画最後の宇宙飛行士が、宇宙への無謀な情熱を傾ける少年とその恋人に一通の手紙を残して死亡。そこには彼が月から持ち帰った石を少年の手で宇宙に戻してもらいたいと記されていた。隠された石を見つけ出した二人の目の前に、信じられない光景が広がる。宇宙をめぐる人々の愛と友情を描く、SFファンタジー・ロマン。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("原振侠與衛斯理／THE SEVENTH CURSE", "セブンス・カース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "新薬開発のためタイの秘境に赴いた香港医師団。冒険好きの若き医師クアンは原住民クー族の祭典を見に行くが、いけにえの娘を助けたことから“血の呪い”をかけられて……。香港映画がそのパワーと神髄を見せつけるミステリー・アドベンチャー。コメディからシリアスまで多彩な役どころをこなすユンファが主演した冒険もの。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECRET GARDEN", "秘密の花園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「小公子」「小公女」で知られるF・H・バーネットの名作を、女流監督A・ホランドが繊細なタッチで映像化。両親を失い、叔父の陰気な屋敷に引き取られた孤独な少女が、美しい花々を育て上げた秘密の花園。その自然の息吹が、妻の死を嘆き悲しむ叔父、わがままで病弱ないとこの心を解きほぐしていく。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("舞姫", "舞姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "森鴎外の原作を篠田正浩監督が郷ひろみを主演に迎え、ドイツ・ロケを中心に映像化。明治18年、国費留学生としてドイツに渡った太田豊太郎は、ある日、悲しみに沈む少女エリスと出会う。二人はやがて愛し合うようになるが、苛酷な運命が待ちうけていた。流麗なカメラワークが美しく、「ラストエンペラー」で知られるコン・スーの音楽が限りなく切ない。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUMMER IN THE CITY／DEDICATED TO THE KINKS", "都市の夏　キンクスに捧げる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "W・ヴェンダースがミュンヘン映画学校の卒業製作として撮影した長編第１作。後の「都会のアリス」「さすらい」「パリ、テキサス」に通じるあてどなく放浪する男たちというテーマと、物語の起状を追わず、人々の表情や日常を静かに見つめるというスタイルは、この作品で確立されている。ミュンヘンの刑務所から釈放されたハンスは出迎えた元相棒の車に乗り込む。相棒はハンスから情報を得ようとしているが、ハンスは“金のありかは知らない”と言うだけ。しかしかつての仲間は彼の言葉を信じようとせず、ハンスのあとを追う。ハンスはまるで迷子のようにミュンヘンの街を逃げまどう……。", "1970-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Daisy", "デイジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "「インファナル・アフェア」3部作などで知られるアンドリュー・ラウ監督が、韓国映画界に招かれ、「猟奇的な彼女」のチョン・ジヒョンと「私の頭の中の消しゴム」のチョン・ウソンを主演に迎えて製作したラブ・サスペンス。全編オランダ・ロケを敢行し、異国の地でめぐりあった画家の卵のヘヨン、インターポールの捜査官ジョンウ、暗殺者のパクウィという3人の男女の数奇な愛の軌跡をみつめる。", "2006-05-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EYEWITNESS", "小さな目撃者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "地中海の島を訪れた大統領の歓迎パレードを見に行ったレスター少年は、大統領狙撃犯を目撃する。だが空想好きな彼の話を誰も信じてくれない……。『狼と少年』の話を題材にしたこの作品は、歯切れのよいハフの演出とレスターの好演がかみ合った拾い物の佳作。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姉妹", "姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "昭和を代表する歌姫・美空ひばりが、ティーンエイジャーの頃に主演した姉妹愛のドラマ。ひばりが扮する三女が上の姉たちの恋のキューピッドを演じるという設定は当時としてはとても珍しい快作。三女が次女とその彼の仲立ちをし、めでたく婚約までいったが、長女もその彼を愛しているとは知らなかった。やがて現れた金持ちの紳士も次女と仲良くなっていき……。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("百万円と苦虫女", "百万円と苦虫女", "", "", "http://image.eiga.k-img.com/images/movie/53379/315x210.jpg?2309", 121, "「さくらん」の脚本家タナダユキが監督、「ニライカナイからの手紙」「フラガール」の蒼井優主演で送る青春ムービー。短大を卒業後、就職もできずフリーター生活を送る鈴子。ひょんな事件から家族の元を離れ、100万円を貯めるごとに引越しを繰り返す生活を始める。様々な人との出会いを通じ、鈴子は少しずつ人の心の温かさに触れて成長していく。共演に森山未來、ピエール瀧ら。主題歌はクラムボンの原田郁子が担当。", "2008-07-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・与太郎戦記", "続・与太郎戦記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "与太郎は、履歴書の特技欄に“落語”と書いて提出、ところが軍の上司らはこれを“露語”と誤読。与太郎は、スパイ訓練生として陸軍中野学校に送り込まれるはめに。またまたドジの連続が見もの。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCHO¨NER GIGOLO−ARMER GIGOLO", "ジャスト・ア・ジゴロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "アメリカ製のジゴロ映画とはまったく異なり、時代の激しい流れの中ではかなく消えていった一人のジゴロを、デカダンな雰囲気が漂う映像の中に綴った作品。監督は政治映画「パワー・プレイ」で見事な演出ぶりを見せたヘミングス。俳優としても活躍している彼は、演出を手掛けると同時に謎の地下組織の男に扮し、不思議な存在感を出している。", "1978-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("22才の別れ／Lycoris　葉見ず花見ず物語", "22才の別れ／Lycoris　葉見ず花見ず物語", "", "", "http://image.eiga.k-img.com/images/movie/34129/315x210.jpg?1469", 119, "大林宣彦監督が、「なごり雪」に続く“大分3部作”の第2弾として手掛けた恋愛ドラマ。伊勢正三の名曲「22才の別れ」をモチーフに、母娘2代に渡る切ない恋を綴る。44歳のサラリーマン・川野俊郎は、同僚の有美と煮え切らない関係を続けていた。ある日彼は、コンビニで働く少女・花鈴が「22才の別れ」を口ずさんでいるのを聞き、ふと懐かしさを覚える。やがて花鈴と親しくなった俊郎は、彼女から驚くべき事実を告げられる……。", "2007-08-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("警察日記", "警察日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "磐梯山麓の小さな町の警察署を舞台にして、その町に暮らす人々の引き起こす様々なエピソードをスケッチ風に描く久松静児監督作品。中年のお人好しの吉井巡査が捨て子の姉妹の世話をしたり、若い花川巡査がもぐり周旋屋にひっかけられた娘を出発寸前に押さえ、その娘に淡い恋心を抱いたり、またこの周旋屋の女をめぐって所長が職安の連中とやり合ったり。その他、万引きと無銭飲食の女の哀しい話、あるいは気のいい馬車屋の話、捨て子が料理屋の女将に引き取られたあと実の母が現れるといったエピソードが盛り込まれた人情喜劇の佳編。若き宍戸錠、三國連太郎、二木てるみなどの好演が印象的である。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRON", "トロン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "世界で初めてデジタル・コンピュータ・グラフィックスを導入した、ディズニー・プロ製作の話題作。有能なエンジニア、フリンは、ひょんなことからミクロの電子世界に迷い込むが……。CG映像がスリリングでファンタスティックなSF冒険譚。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVERYBODY WINS", "もうひとつのラブストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "残酷な殺人事件の犯人として逮捕された無実の青年を救ってほしい、と美しい女から依頼を受けた私立探偵。女の話は謎と矛盾だらけだったが、やがて探偵は彼女に惹かれていく。製作はJ・トーマス、脚本はA・C・ミラー、監督はK・ライスという、豪華なスタッフによるセンセーショナルなミステリー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・夫婦善哉", "喜劇・夫婦善哉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "織田作之助の同名小説を映画化。1955年の豊田四郎版に比べ、藤山寛美と野川由美子を主演にすえたことで、大阪喜劇の雰囲気が出た作品になっている。女にも金にもだらしない、大阪の化粧品問屋の息子、柳吉が、良い仲になった芸者、蝶子の一途な愛情で、人間的に目覚めていく様を描く。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("玻璃之城", "玻璃（ガラス）の城", "", "", "http://image.eiga.k-img.com/images/movie/1849/315x210.jpg?4078", 110, "1996年、大晦日のロンドン。新年を祝う人々が湧く広場の中、車が猛スピードで路肩に激突し車中のラファエルとビビアンは息絶える。翌日、ラファエルの息子デイビッドは遺体の確認のためロンドンへ。香港にいるはずの父がなぜロンドンに、しかも女性と一緒だったのか？　疑問に思いながらもデイビッドは、ビビアンの娘スージーと共に香港へ行き、ラファエルとビビアンの共同名義となっている家を訪れる。そこで2人は初めて父母の青春時代を知るのだった。", "2000-04-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fimpen", "サッカー小僧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ロッタちゃん」シリーズや「マイライフ・アズ・ア・ドッグ」の製作者ワルデマル・ベルゲンダールと、「みじかくも美しく燃え」のボー・ウィデルベルイ監督による痛快ちびっこサッカー映画。6歳の天才サッカー少年ヨーハンは、プロチームに入団して大活躍。ついにスウェーデン代表にまで選出され、エースストライカーとしてワールドカップ出場を目指し奮闘する。当時の代表監督エリクソンをはじめ、代表選手が多数出演している。", "1974-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君を忘れない FLY BOYS, FLY！", "君を忘れない FLY BOYS, FLY！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1945年夏。九州南部の前線基地の海軍航空隊のエリート、望月大尉は特攻作戦への反発から、あえて302特別飛行隊を編成。超一流の航空技術の持ち主から落ちこぼれまで、様々な略歴を持つ７人の若者たちが集められた……。日本映画の伝統といえる、特攻隊員として大空に散った若者たちの物語だが、若手人気俳優たちの共演で、戦争映画というより悲壮感の少ない青春映画となった。渡邉孝好が７人の個性の描き分けも的確な手堅い演出を見せている。伊丹十三の長男、池内万作が本作でデビューした。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽あたり良好！ KA・SU・MI 夢の中に君がいた", "陽あたり良好！ KA・SU・MI 夢の中に君がいた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "あだち充の同名青春コミックを原作とするテレビアニメ「陽あたり良好！」の後日談を描いた劇場版。叔母が営む下宿屋「ひだまり荘」で暮らすかすみは、国際的なグランプリレーサーを目指す年上のボーイフレンド克彦と、下宿人で同級生の勇作との間で揺れ動いていた。そんな中、克彦が「2年後、君と結婚するために帰ってくる」という言葉を残して渡米。2年後、一流レーサーとなって帰国した克彦は、約束どおりかすみにプロポーズするが……。", "1988-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STEEL HELMET", "鬼軍曹ザック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "S・フラーの長編第３作で、彼の名が一躍有名になった出世作。朝鮮戦争のさなか、分隊が全滅しザック軍曹は一人生き残った。負傷した彼は戦争孤児に助けられ、戦線を後退するが、古寺に立てこもった彼らを狙撃兵が襲う……。朝鮮戦争開戦直後に製作された力作で子役のW・チャンが印象的。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本女侠伝・侠客芸者", "日本女侠伝・侠客芸者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "石炭ブームにわく明治末期の博多。馬賊芸者と評判の高い信次は、男まさりの気っぷと度胸で人気を呼んでいた。鉱業会社社長の大須賀は、土地のヤクザと手を組み炭鉱主にのし上がろうとたくらむが、清吉や信次によって阻まれる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUSTIN　POWERS　THE　SPY　WHO　SHAGGED　ME", "オースティン・パワーズ：デラックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1990年代に現れた1960年代のオバカ・スパイ、オースティン・パワーズの活躍を描く、コメディの第２作。冷凍冬眠中、宿敵ドクター・イーブルによって精力源“モジョ”を抜き取られたオースティン。弱体化したオースティンはモジョを取り戻すべく、タイムマシンで1969年に戻り、イーブルと宿命の対決に挑む。前作同様シックスティーズのオシャレ・テイストを残しつつ、下品なギャグは更にパワーアップ。男性器を型どったロケットが飛ぶクライマックスをはじめ、下ネタが敷き詰められている。新キャラクターで登場する、イーブルの８分の１サイズというクローン人間“ミニ・ミー”の、かわいらしくも凶暴な暴れっぷりが笑いを呼ぶ。", "1999-08-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風来坊探偵 岬を渡る黒い風", "風来坊探偵 岬を渡る黒い風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "「赤い谷の惨劇」と２本立で上映された、中編アクション映画。スタッフはすべて「赤い…」と同じで、キャストも主演の千葉真一以外はみな役柄を変えて登場する。海難事故が相次ぐ房総半島の江藤漁業は、事故の原因に不審を抱き、風来坊探偵こと西園寺五郎に調査を依頼したが……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベルサイユのばら", "ベルサイユのばら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "大ベストセラーとなった池田理代子の同名劇画の映画化。監督は「シェルブールの雨傘」（1964）などで知られるフランスの映画作家J・ドゥミで、フランス革命を生きた一人の男装の女性の半生をオール外人キャスト、大規模なフランス・ロケで壮麗に描いた。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャッカー電撃隊VSゴレンジャー", "ジャッカー電撃隊VSゴレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "子どもたちに大人気の戦隊ヒーロー、「ジャッカー電撃隊」と「秘密戦隊ゴレンジャー」が共演を果たした劇場オリジナル作品。ある日、クライムによって大量のウランが奪われた。クライムの秘密基地にはUFO軍団、鉄仮面軍団、機械化軍団、拳闘士軍団が集結。鉄の爪は盗んだウランで水爆をつくり、世界中を攻撃しようと企んでいた。基地が奇怪岬にあると突き止めたジャッカーは、ゴレンジャーと力をあわせて強敵たちに立ち向かう。", "1978-03-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メス", "メス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "打算的な結婚のために、帝王切開の手術を装って妻を殺害し、医学連盟理事長に治まっている医師・菊川。その時生まれた息子・式根は外科医となり、復讐の機会を狙っていた。やがて、菊川病院と式根の勤める葛城病院で患者の怪死事件が起こり始める……。柿沼宏と松森正による劇画をもとに、医学界の暗部を描いた貞永監督得意のサスペンス作品。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銃殺 ２・26の叛乱", "銃殺 ２・26の叛乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1954年の「叛乱」と同じ立野信之の『叛乱』を原作とした昭和史問題作。丹波哲郎扮する相川中佐の軍務局長暗殺から２・26事件の勃発、そして青年将校たちの処刑までを描いていく。慎重派の安東大尉を鶴田浩二が熱演。撮影の仲沢半次郎は、のちに同テーマの「動乱」も手掛けている。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO　CODE　OF　CONDUCT", "フル・ブラント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "マーティン＆チャーリーのシーン親子が、「ウォール街」以来12年ぶりに共演したポリス・アクション。アリゾナ州フェニックスの熱血刑事ジェイクが、大量のヘロインを取り引きする犯罪組織と対決。別居中の妻を人質にとられた彼の奮闘を、カーチェイスや銃撃シーンをふんだんに盛り込んで描き出す。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市喧嘩太鼓", "座頭市喧嘩太鼓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ヤクザの熊吉への一宿一飯の義理から、やむなく若いヤクザ・宇之吉を斬った座頭市だが、熊吉の狙いが宇之吉の姉・お袖にあると知った市は、彼女を連れて逃げる。佐藤允が市のライバル役で登場。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ANIMAL", "アニマルマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "『サタデー・ナイト・ライブ』出身のコメディアン、ロブ・シュナイダー主演のコメディ。警察署のさえない事務職員マービンが交通事故に遭遇。目覚めると彼は警察犬並みの嗅覚と馬の脚力、イルカの遊泳力が備わっていた。スーパーパワーを駆使して念願の警察官となるマーヴィン。しかし実は彼の体には、知らぬ間に動物の臓器が移植されていた。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES SEPT PE´CHE´S CAPITAUX", "七つの大罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 148, "フランスとイタリアの監督が手を組んで作ったオムニバス。テーマは、聖書に説かれている七大罪で、狂言回し役としてG・フィリップ扮する若者が登場する。以下は各話のテーマ。「第１話・欲ばりと怒り」「第２話・怠けもの」「第３話・色好み」「第４話・ねたみ」「第５話・大食らい」「第６話・見えっぱり」「第７話・第八の罪」。", "1952-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・サイレンサー　MAGNUM357", "ザ・サイレンサー　MAGNUM357", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ニューオリンズのマフィアのボスが殺され、市警のクックはマコトを逮捕した。しかし、マコトは脱獄し、自分をはめた奴らに復讐するためニューオリンズに戻って来る。クックは、FBIの捜査官のジャネットとともにマコトを追うが、事件の裏にはある陰謀が隠されていた。千葉真一が出演している日米合作アクション。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くノ一忍法帖 柳生外伝　～江戸花地獄篇～　～会津雪地獄篇～", "くノ一忍法帖 柳生外伝　～江戸花地獄篇～　～会津雪地獄篇～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "山田風太郎の人気小説を「SCORE」の小沢仁志が、監督・脚本・主演の３役をこなして映画化した時代劇活劇。悪徳大名に殺された家老一族の生き残りである７人のくノ一が復讐を決意。天下の剣豪・柳生十兵衛が彼女らに力を貸すことになった。だが、彼らの前に恐ろしい忍術を操る暗殺集団が立ちふさがる。SFXやワイヤーアクションをふんだんに盛り込んだ立ち回りが迫力満点。公開時のタイトルは「くノー忍法帖 柳生外伝」だが、ビデオ・DVDでは「同・江戸花地獄篇」（前篇）「同・会津雪地獄篇」（後篇）に分けられている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sorcerer's Apprentice", "魔法使いの弟子", "", "", "http://image.eiga.k-img.com/images/movie/55209/315x210.jpg?2963", 110, "「ナショナル・トレジャー」シリーズの製作ジェリー・ブラッカイマー＆ニコラス・ケイジ主演のアクション・ファンタジー。800年にわたり繰り広げられてきた魔法大戦争が、現代のニューヨークでぼっ発。魔法使いのバルサザール（ケイジ）は悪の勢力を撃退するため、今は亡き大魔法使いの後継者として気弱な物理オタクのデイブを無理やり自分の弟子にする。しかし、魔法の存在すら信じないデイブの修行は難航し、史上最強の魔女モルガナはその勢力を次第に拡大させていた。共演はジェイ・バルチェル、アルフレッド・モリーナ、モニカ・ベルッチ。", "2010-08-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("梅田優子の告白", "梅田優子の告白", "", "", "http://image.eiga.k-img.com/images/movie/53979/315x210.jpg?4360", 50, "「学生による商業映画製作」がコンセプトのトリウッドスタジオプロジェクト作品で、企画・制作・宣伝など全て現役の学生の手による。昼は牛丼屋、夜はセクシーキャバクラで働く勝ち気で負けず嫌いな梅田優子は、出会う男と次々に関係を持ち、その積み重ねを密かに楽しむ日々を送っている。まともな恋愛とは無縁にみえる優子だが、ある時、牛丼屋にやってくる男のことが気になりだした彼女は必死にアプローチするのだが……。", "2008-10-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺は園子温だ！", "俺は園子温だ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "1995年の監督作「桂子ですけど」のもとになり、園子温が監督デビューを果たすきっかけとなったPFF入選作品。自分の誕生日までの３週間を、エッセイ風に綴っていく。１日24時間。この限られた時は確実に過ぎ去っていく。間もなく迎える自分の誕生日までのこの“時間”を、着実に生きようと決心した主人公の姿を描き出す。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もうひとりいる", "もうひとりいる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "都内の廃校でグラビアを撮影中の美少女３人と編集者、カメラマンらが突然の惨劇に見舞われるホラー映画。ドッペルゲンガー現象をモチーフに、“もう一人の自分”に命を狙われる登場人物の不条理な運命が、低予算ながらも巧みに練られた恐怖演出で描かれていく。ティーン・アイドル３人が主演を務める一方、「呪怨」の清水崇監督が監修で参加し、新人の柴田一成をバックアップしている。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BELLY OF THE BEAST", "沈黙の聖戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「LOVERS」のアクション監督C・シウトンの演出のもと、S・セガールがダイナミックな立ち回りをみせる。テロリストにさらわれた娘を救うため、タイに乗り込んだ元CIA工作員が巨大な陰謀に立ち向かう。シウトンお得意のワイヤーワークを活かした、驚異の格闘アクションが見もの。元CIA捜査官のジェイクの愛娘が、タイ旅行中にテロリスト集団に誘拐された。元同僚で今は僧侶となったスンティらの協力を得て、娘の奪還に乗り出すのだが、テロリストだけでなくタイ特殊部隊やCIAを敵に回すハメに……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("髑髏銭（総集編）", "髑髏銭（総集編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "元禄５年、秘宝の謎を解くカギ・髑髏銭をめぐって暗躍する者は、盲目の妖釼士・銭酸漿、老中・柳沢、凶賊・銀座の赤吉だったが、この事件に巻き込まれたのは、薄幸の美女・お小夜、神奈三四郎、そして怪盗・念仏三十郎であった。柳沢の娘・桧も絡んで、登場人物の印象もあざやかに、正邪入り乱れての宝探しが始まる。銭酸漿がもうけ役で、1962年東千代之介、1956年月形龍之介も好演だったが、原健作はまさに適役。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ペインテッド・デザート", "ペインテッド・デザート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "オリジナル・ビデオ“タフ”シリーズの原田眞人監督と木村一八のコンビによる、オールド・アメリカン・スタイルのハードボイルド。砂漠の町で寂れたカフェを営む日系人女性と中年のタフガイ、流れ者の日本人青年の友情を、ギャングの抗争と絡めて描く。奥山和由プロデューサーが世界マーケットを想定した作品ということで話題になった。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARUKO", "HARUKO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "フジテレビの番組『ザ・ノンフィクション』で放映され、大反響を呼んだドキュメンタリー作品を、カットされたエピソードを加えて再編した劇場映画。現在87歳、在日朝鮮人の金本春子さんが歩んだ苦難の日々を、朝鮮総連のカメラマンで息子のソンハさんがフィルムに記録。ヤミ商売で37回も逮捕された母親の波乱万丈の半生が克明に明かされる。在日一世の金本春子さんは、母親として７人の子供を抱えながら怒涛の人生を送ってきた。家庭を省みない夫が蒸発し、一家離散にまで追いこまれる。春子さんは、その極貧生活を乗り越えるために、ヤミ商売に手を出すが……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森と湖のまつり", "森と湖のまつり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "北海道・阿寒の雄大な自然を背景に、アイヌの風俗をふんだんに取り入れ、アイヌ民族の存亡と愛をめぐるドラマを、内田吐夢が正攻法の演出で描く。任侠映画のスターの座を確立する前の高倉健が、亡びいくアイヌのために闘う主人公を野生味豊かに演じている。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEA　WITH　MUSSOLINI", "ムッソリーニとお茶を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "1930～40年代のイタリア。第二次世界大戦前後という緊迫したムードのフィレンツェで、自分らしく生きた英国人やアメリカ人の女性５人の誇りを見つめた感動作。元大使の未亡人、芸術家、考古学者ら個性豊かな面々の姿が、彼女たちと交流を持つことになった一人の少年の視点を通して描かれる。富豪との結婚を繰り返す奔放な元踊り子に扮したシェールが、恋に破れてもなおゴージャスな気品を失わない女の魂を体現しているのが印象的。ゼフィレッリの演出もテンポが乱れず快調だ。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOHNNY　SKIDMARKS", "モンタージュ・証拠死体", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "犯罪現場専門のカメラマン、ジョニーは、恐喝ネタのセックス写真を撮影して金を稼ぐ最低の男だった。何者かによって恐喝仲間４人が惨殺され、身の危険を感じたジョニーは、自分が撮った写真を手がかりに犯人を割り出そうとするが……。二転三転するストーリーと、ショッキングな映像が見どころのサスペンス・ミステリー。「レリック」の脚本家J・ラフォーが、かつてカメラマンとして活動した経験からアイデアを得た監督デビュー作。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAHARA", "サハラ／死の砂漠を脱出せよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "C・カッスラーの世界的なベストセラー小説『死のサハラを脱出せよ』を映画化したアドベンチャー活劇。カッスラーが生んだ人気キャラクターである敏腕エージェント、ダーク・ピットがスクリーンで活躍を繰り広げる。広大な砂漠を舞台に、秘宝を追うピットの冒険がスリリングに展開。危機また危機の展開に加えて、次第に明らかになる巨大な陰謀の全貌が緊迫感を盛り立てる。タフでクールなピットを演じきった「10日間で男を上手にフル方法」のマシュー・マコノヒーによる熱演も魅力的だ。古い金貨を発見したことから、伝説の甲鉄艦の存在を確信した海洋機関調査員ピット。サハラに飛んだ彼は、伝染病の発生源を探る研究員エヴァと砂漠を進むうちに陰謀に行き当たり……。", "2005-06-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAVENOUS", "ラビナス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "雪山で遭難した移民が仲間の肉を食べて生き残ったという、実際に起こったドナー・パス事件に材を得て描く奇妙な味わいの問題作。1847年、メキシコ・アメリカ戦争で死体の山から生還したボイド大尉が、雪に閉ざされた砦へ赴任して来る。そこには原住民の姉妹や数名の兵士たちなど一癖ある者たちがいた。その夜、重度の凍傷を負った男コルホーンが救いを求めて彼らの前に現れ、山で遭難した仲間たちが、飢えで常軌を逸したガイドに次々と殺されていると語り始める。ボイドら一行は、早速現地に向かうが、コルホーンの態度が一変、凶暴性を露にして彼らの人肉を求めて襲い掛かってくる。人肉を食べることに取り憑かれた男の狂気を「司祭」「フェイス」に次いでA・バードと組むR・カーライルが怪演。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("笑う大天使（ミカエル）", "笑う大天使（ミカエル）", "", "", "http://image.eiga.k-img.com/images/movie/33119/315x210.jpg?4549", 92, "人気少女漫画家、川原泉の代表作として知られる同名コミックを、「スウィングガールズ」の上野樹里主演で映画化した学園コメディ。母を亡くし、生き別れになっていた金持ちの兄と暮らすことになった史緒。兄の勧めで超お嬢様学校として名高い“聖ミカエル学園”に転入した庶民派の彼女は、ひょんなことから意気投合した和音や柚子と共に、完璧なお嬢様を演じる。そんなある日、お嬢様ばかりを狙った誘拐事件が発生し……。", "2006-07-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANDANGO.", "CLUB ファンダンゴ．", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "街で一番ホットなクラブ“ファンダンゴ”を舞台に、そこに集まる３人の若者のエピソードを交差するように描くスタイリッシュなブラック・ムービー。キュートな魅力でベルリンのクラブ・クイーンとして名を馳せているシャーリーの夢はモデルになること。有名カメラマンの目に留まったシャーリーは、クラブのオーナーで恋人のルポの金庫から有り金全部と愛車のシトロエンを奪って逃げ出す。一方、盲目を装い、トラブルに巻き込まれないようにしていた人気DJのサニーは、オーナーのルポに盲目でないことがばれ、店を解雇されてしまう。ルポは、麻薬の取引のことで頭がいっぱい。そんな３人が、地下駐車場でバッタリ出くわして……。ビデオタイトルは「略奪者」、DVDタイトルは「CLUBファンダンゴ.／略奪者」。", "2000-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLANCHE ET MARIE", "ふたりの女～ブランシュとマリー～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1941年のドイツ軍フランス侵攻から1943年のパリ解放まで、レジスタンス時代のフランス人の生活を淡々としたドキュメンタリー・タッチで描いたフランス映画。第二次大戦のさなか、夫の身代わりになってゲシュタポに逮捕され、獄中で出産するヒロインをミュウ・ミュウが熱演。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZHOU　YU’S　TRAIN", "たまゆらの女〈ひと〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "二人の男の間で迷う女心を描き出した恋愛ドラマ。遠く離れた恋しい男と、側で見守り愛してくれる男。彼らのそれぞれの愛し方に触れながら、自身の愛を模索するヒロインのデリケートな心情が、同性の共感を誘う。「きれいなおかあさん」のスン・チョウ監督とコン・リーのコンビが再び顔を合わせて、繊細なドラマを紡ぎ出す。無名の詩人と恋に落ちた女性チュウヨウは、汽車で10時間を費して彼の家に通い、遠距離恋愛を始める。だが、詩人はそんな彼女の強い愛を重荷に感じ始めていた。そんなある日、チュウヨウは無骨だが優しい同郷の獣医と出会い……。", "2002-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真夜中の河", "真夜中の河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "俳優、南雲佑介が、自ら監督と脚本を担当した16ミリの自主製作青春映画。沖縄から上京した、ある俳優志願の青年は下水掃除のバイトで生計を立てる毎日。彼は、インテリ、オカマ、チンピラなど、工事現場の個性豊かな飲み友だちと触れ合いながら、夢を捨てずに生きていく。南雲の俳優仲間が大挙出演。南雲自身も親方役で顔を出す。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("River's Edge", "リバース・エッジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "アメリカの片田舎で、無感動でエキセントリックな日々を送る高校生たち。ある時、仲間の一人が同級生の女の子を殺してしまった。この事件をきっかけに、彼らは一人の風変わりな中年男と出会う。その男フェックはダッチワイフと暮らす、片足が不自由な元バイカー。若者たちとフェックの間に奇妙な連帯感が生まれて……。今でも、アメリカの若者の間でカリスマ的存在であるD・ホッパーがそのテーマと脚本に惚れ込んで取り組んだという作品。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あの丘越えて", "あの丘越えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "当時最高のアイドルだった美空ひばりと鶴田浩二の共演による青春映画。山の牧場で育った万里子はおてんばの自然児。東京へ出た彼女は家庭教師・大助にいろいろ教わりながら都会的に美しくなっていくのだった……。万城目正作曲の歌が散りばめられた明るい娯楽映画。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LASSIE’S GREAT ADVENTURES", "名犬ラッシーの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "S・ロングのベストセラー小説「名犬ラッシー」の映画化。気球に乗ったラッシーとティミー少年がカナダの大森林にたどり着き、そこで火をおこしたり、弓矢を作ったりのサバイバル生活を始めるが……。広大な自然を背景に、コリー犬のラッシーと少年の心温まる友情と大冒険が描かれる。DVDは「名犬ラッシー DVD−BOX」に収録。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGHWAY　OF　HEARTACHE", "恋はシリアル・キラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ブラック・ユーモアを満載したナンセンス・コメディ。カントリー歌手を夢みる35歳の主婦ワイノナは夫を殺して、夢を実現するための旅に出る。コンクールでもライバルを殺害し、見事に(？)優勝した彼女だったが、その後も逆上すると人を射殺する悪癖はおさまらず……。破天荒なヒロインの行動、趣味の悪い極彩色の映像、体当たりのギャグなど、型破りな笑いが楽しめる怪作。", "1994-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しあわせになれない　悲しい花　呪いのドライブ", "しあわせになれない　悲しい花　呪いのドライブ", "", "", "http://ecx.images-amazon.com/images/I/51QxERraSuL._SL210_.jpg", 87, "「MEATBALL MACHINE」の山本淳一監督が、怪談師・ファンキー中村の原案をもとに描くサスペンスホラー。女の怨霊が取り憑いた1台の青いカマロ。その車に乗った者は、恐怖と絶望に襲われて自殺へと追い込まれてしまうのだった。別れた妻に未練を抱き続ける中古車ディーラーの木村は、ある女性から格安で青いカマロを譲り受けるが……。出演は「怪談・牡丹燈篭」の大沢樹生、グラビアアイドルの愛川ゆず季ほか。", "2010-08-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("禅 ZEN", "禅 ZEN", "", "", "http://image.eiga.k-img.com/images/movie/53889/315x210.jpg?2158", 127, "「光の雨」「火火」の高橋伴明監督が、乱世の鎌倉時代を生きた孤高の僧侶、道元禅師を描く歴史ロマン。歌舞伎役者の中村勘太郎が道元を熱演。共演に内田有紀、藤原竜也、村上淳、勝村政信、西村雅彦、笹野高史ほか。幼い頃に母を亡くした道元は、人々の心の救いとなる悟りを得るため、仏道の正師を求め入宋するが……。", "2009-01-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOT SNOW", "HOT SNOW", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ジャニーズJr.内の人気グループ「Mis Snow Man」が主演する青春映画。何よりもダンスが大好きな4人の高校2年生が、25年前の1986年にタイムスリップ。そこで出会ったダンス好きな4人と意気投合する。時を超えて出会った8人は、ひとつの目標に向けて力を合わせ、友情やきずなを育んでいく。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG", "ビッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "12歳の少年が、ある朝突然35歳の中年男に大変身!? という奇想天外なアイデアが大ウケしヒットを飛ばした、心温まるロマンティック・コメディ。チビが悩みのジョッシュは、カーニバルの晩“ゾルダー・マシーン”に願いをかけた。翌朝鏡に映った自分の姿にビックリ。一夜にして20歳以上も“ビッグ”になってしまったのだ。家を追い出されN.Y.にやって来たジョッシュは、玩具会社に就職。自由で天真爛漫な発想が認められ大昇進を果たすが……。大人と子供のアンバランスな魅力をフルに発揮するのはT・ハンクス。女性監督マーシャルの、細やかで機知に富んだユーモア・センスが最高。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("柳生武芸帳・双竜秘剣", "柳生武芸帳・双竜秘剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "同じスタッフ、キャストによる東宝版「柳生武芸帳」の続編。柳生武芸帳をめぐって、柳生一門と肥前の兵法者・山田浮月斎、そして永井家の隠し姫・清姫を頭領とする虚無僧の一団が、三ツ巴の争奪戦を展開する。稲垣浩が前作に引き続いて、スピーディーなタッチで一級の娯楽映画に仕上げた。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の紋章・喧嘩街道", "男の紋章・喧嘩街道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "竜次の大島組は母の村田組に吸収合併され、母は村田組三代目の跡目を竜次にゆずり引退する。竜次は襲名披露後、修行の旅に出かける。留守中に悪玉一味がのさばり、村田組は危機に陥るが、かけつけた竜次は悪玉を斬り、また修行の旅に出る。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL", "エル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "敬虔なカトリック信者で、品行方正、40歳を過ぎてなお女性を知らない大金持ちのフランシスコは、教会で出会った素晴らしい足の持ち主グロリアに一目惚れ。婚約者のいる彼女と強引に結婚した。しばらくして再会したグロリアの元婚約者は、彼女からフランシスコが並外れた嫉妬心の持ち主で困っている、という話を聞かされる。新婚旅行に出かけた列車の中から早くも嫉妬が始まった。彼女の友人、訴訟のために雇った弁護士、相談役の神父と、近づく男たちとの仲を次々に疑ったあげく、寝ている彼女をロープやハサミで脅した。さらにピストルで脅し塔から突き落とそうとした夫に愛想をつかした彼女はついに家出。それを知ったフランシスコは半狂乱になり、彼女を追って入った教会で幻覚に襲われ、神父に飛びかかる。時が過ぎ、精神病院から出た主人公だが……。ジャック・ラカンの心理学講義にも使われたという伝説的映画。", "1952-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("舞妓はんだよ全員集合!!", "舞妓はんだよ全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "京都祇園町、新選組の末裔と自称する近藤長介は置屋“伊川家”の婿養子だが、女房に逃げられ芸妓が一人もいなくなる。長介はそれぞれ江戸末期に活躍した人物の子孫だと名のる４人を使って、芸妓・ヒナ菊に言い寄る吉良平造をやっつける。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MOINDRE DES CHOSES", "すべての些細な事柄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ろうあの人々の姿をいきいきと捉えた「音のない世界で」などで知られるドキュメンタリー作家、N・フィリベールが精神病患者にスポットを当てた作品。舞台となるのは独特の治療法を行っている精神科クリニック、ラ・ボルド。緑に囲まれた広大な土地を持つこの医院で暮らす患者たちの日常、観客を呼んで行われる毎年恒例の上演会で戯曲『オペレッタ』をやることになった患者たちの練習風景などが綴られていく。監督の温かい視点で捉えられた患者たちの自由な姿が静かな感動を呼ぶ。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Carne tremula", "ライブ・フレッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "イギリスの推理作家ルース・レンデルの小説「引き攣る肉」を、スペインの鬼才ペドロ・アルモドバル監督が映画化。思いを寄せる女性エレナの家に強引に上がり込んだ青年ビクトル。彼は駆けつけた警察官サンチョともみ合いになり、はずみでサンチョの相棒ダビドを撃ってしまう。下半身不随になったダビドはやがて車椅子バスケットボールの選手になり、エレナと結婚。4年の刑期を終えて出所したビクトルは彼らへの復讐を誓うが……。", "1997-01-01", "9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEACHERS", "りんご白書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "いじめ、自殺と、荒廃を極める現代アメリカのハイスクール。その内情をリアルに捉え、そこに生きる教師や生徒の姿をドラマティック、かつヒューマニズムあふれるタッチで綴っていく。女生徒の妊娠、裁判沙汰、拳銃の不法所持といった問題を抱えた高校で、孤軍奮闘する教師の姿を描いた作品。N・ノルティがユニークな教師に、そしてR・マッチオが生徒の一人に扮している。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("間宮兄弟", "間宮兄弟", "", "", "http://image.eiga.k-img.com/images/movie/1519/315x210.jpg?1379", 119, "「家族ゲーム」「海猫」の森田芳光監督が、江國香織の同名小説を映画化。下町のマンションで同居生活をする間宮兄弟は、2人ともオタクで30代が近づく今も大の仲良し。兄はビール会社に勤務、弟は小学校の用務員をしているが夜はいっしょにDVD鑑賞の日々。ある日、ガールフレンドがほしい2人は、弟の働く学校の女教師と、2人が通うDVDレンタル店でバイトする女子大生を招いてホームパーティを開くことを思いたつ。", "2006-05-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Windstruck", "僕の彼女を紹介します", "", "", "http://image.eiga.k-img.com/images/movie/1749/315x210.jpg?2703", 123, "「猟奇的な彼女」の監督＆主演コンビのラブ・ストーリー。人気女優チョン・ジヒョンが、前作同様のキツくてキュートなヒロインを演じる。誤認逮捕がもとで出会った高校教師ミュンウと、熱血女性警察官ギョンジン。2人は愛し合うようになり、ミュンウは無鉄砲なジョンジンをハラハラしながら見守るが、そんな2人を過酷な運命が待ち受けていた。共演は「火山高」のチャン・ヒョク。製作は「HERO」「LOVERS」のビル・コンが担当。", "2004-12-11", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN AMERICAN MURDER", "長く熱い夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "３歳の時に自分と母親を捨てた父親から手紙が届き、ミックはカリフォルニアの小さな町へ向かう。そこで彼を迎えたのはサラという謎めいた女だった。父と息子、そしてサラをめぐる三角関係が、思わぬ殺人事件を呼び起こすことにまでエスカレートするサスペンス。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後的貴族／THE LAST ARISTOCRATS", "最後の貴族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「芙蓉鎮」の大ヒットで知られるシェ・チン監督が、中国映画史上初の大規模ニューヨーク・ロケを展開した話題作。1940年代、新中国成立前後の激動の時代を背景に、国民党官僚の令嬢として育った李彫という娘が夢に燃えてニューヨークへと留学するものの、時代の流れとともに零落の人生を歩み始める様を描いている。", "1989-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The American", "ラスト・ターゲット", "", "", "http://image.eiga.k-img.com/images/movie/55719/315x210.jpg?2584", 106, "マーティン・ブースの小説「暗闇の蝶」を「コントロール」のアントン・コービン監督、ジョージ・クルーニー主演で映画化したサスペンスドラマ。スウェーデンで仕事を終えた殺し屋ジャック（クルーニー）は、最後の仕事としてイタリアの小さな村へとやってくる。カメラマンとして生活しながら村の神父ベネデットや美しい女性カルラと交流するジャックだったが、やがて彼の命が狙う殺し屋が村に現れ……。", "2011-07-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROUTE58", "ROUTE58", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "人気アイドル・ユニット“RUN&GUN”のメンバー４人が主演を務めた青春ロード・ムービー。「月のあかり」でデビューした倉持健一監督が、少年たちの恋と友情を鮮やかに描き出す。沖縄で生まれ育った17歳の潤、和也、勇基、貴志。親友同士の彼らは幼馴染みの凛の“一度電車に乗りたい”という願いを叶えようと、彼女を連れ電車のない沖縄から九州へと向かう。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美しさと哀しみと", "美しさと哀しみと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "原作は『婦人公論』に連載された川端康成の異色作。20年前に自分を捨てた作家・大木と再会した女流画家・音子。大木は彼女の弟子・けい子に惹かれるが、女二人ともまた愛し合う仲であった。1985年にフランスの女流監督ジョイ・フルーリーが再映画化。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ANGE　DE　LA　NUIT ANGEL OF THE NIGHT", "泣きぬれた天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "彫刻家のジャックら、貧しくも希望に燃えた若い芸術家たちが共同生活を送っている下宿があった。しかし戦争が勃発し、下宿の面々も次々と出征し、残された者は無事を祈りながら不安な日々を送る。やがて、戦場で盲目となってしまったジャックが帰還してきて……。光を失った若者が、本当の愛を知るまでを描いた感動作。", "1942-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("清水港代参夢道中（続清水港）", "清水港代参夢道中（続清水港）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "マキノ雅広監督・片岡千恵蔵主演によるご存じ森の石松・金比羅参りの一席。ある劇場の演出家・石田は“森の石松”の舞台稽古がうまく進まずふて寝するが、その夜ふと目を覚ますと石田は清水港の森の石松になっていた……。物語の最初と最後に、現代のエピソードを持ってくるというシャレた構成になっており、さらに当時の浪曲界で人気ナンバーワンであった広沢虎造を、片岡千恵蔵と共演させて浪曲をたっぷり聞かせる。まさにお盆映画にふさわしい娯楽性十分の快作である。なお、この映画は戦後、沢島忠監督・中村錦之助主演で「森の石松鬼より恐い」という題で再映画化された。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("John Q", "ジョンQ・最後の決断", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "最愛のわが子が心臓病に冒され、保険がきかず高額の手術を受けられないことを苦にしたジョンQは、病院の救急病棟を占拠し、子供の手術を要求するが……。現代アメリカの保険制度の矛盾を取り上げながら進行するヒューマン・サスペンス。まるで心優しき「狼たちの午後」とでもいった趣向で、犯罪者でありながらいつしか大衆の支持を得ていく主人公を、D・ワシントンがいつもながらに好演。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UOMINI SI NASCE，POLIZIOTTI SI MUORE", "バニシング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "凶悪犯罪のはびこるローマを舞台に、警察特捜部のあぶない刑事アルフレッドとトニーのハンサム・コンビの活躍を描く。麻薬シンジケートのボス逮捕に血まなこの二人は、常識外れの捜査をものともせず、ようやく彼を追い詰めるが、事件の裏には意外な事実が。", "1976-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MA VIE EN ROSE", "ぼくのバラ色の人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "７歳の少年リュドヴィックの夢は女の子になること。度々女装してはママを困らせていた。そんなある日、パパの上司の息子と結婚式ごっこをしたり、学芸会で白雪姫の格好をしたことで周囲の親たちの反感を買ったリュドヴィックは、両親にしかられショックを受ける。引っ越し先では、仕方無く男の子らしく振る舞おうとする彼の前に、男の子になりたい少女が現れ……。長髪がよく似合う主人公に扮するG・D・フレネの、やさしい笑顔が魅力的。彼が空想するカラフルに彩られたおとぎ話風の世界に、少年の純粋な夢が映し出される。1998年ゴールデン・グローブ最優秀外国語映画賞受賞。", "1997-01-01", "37,2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOU ONLY LIVE ONCE", "暗黒街の弾痕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「俺たちに明日はない」でも描かれたボニーとクライドをモデルにした最初の映画であり、当時のフィルム・ノワールに大きな影響を与えた名作。刑務所を出たばかりのエディーは希望に燃え、ジョーと結婚するが、新生活を心ない人々の偏見でぶち壊されていき、無実の罪で投獄される。真犯人が判明したのは、脱獄しようとするエディーの説得に当たった神父を彼が殺してしまった時だった。今度は本当の犯罪者になってしまい、追い詰められた彼は犯罪に走り、妻のジョーとともに悲劇的な運命をたどる。アメリカに渡り「激怒」に続いてこの映画を発表したF・ラングは、この若いカップルを息詰まるようなタッチと、霧や影を効果的に使った映像美で見事に映し出している。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shaolin", "新少林寺 SHAOLIN", "", "", "http://image.eiga.k-img.com/images/movie/56869/315x210.jpg?2460", 131, "ジェット・リーの代表作として知られるカンフー映画の金字塔「少林寺」（1982）でも題材となった少林拳法を、アンディ・ラウ主演で描くカンフーアクション。20世紀初頭の中国。辛亥革命により各地で争いが絶えない中、登封市にある少林寺の僧侶たちは傷ついた人々の救助にあたっていた。そんなある時、粗暴で傲慢な将軍・候杰（こうけつ）は部下・曹蛮（そうばん）の裏切りにより全てを失い、追われる身となる。少林寺にかくまわれた候杰は心を改め出家し、僧侶たちを率いて戦火に追われる民衆を守る戦いに身を投じていく。共演にニコラス・ツェー、ファン・ビンビン、ジャッキー・チェン。", "2011-11-19", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dust to Glory", "ダスト・トゥ・グローリー", "", "", "http://image.eiga.k-img.com/images/movie/33569/315x210.jpg?2066", 97, "メキシコのバハ・カリフォルニアで毎年開催される世界一過酷なオフロードレース“バハ1000”を題材にしたドキュメンタリー。たった1人で全コースに挑むプロライダーや、オフロードの伝説といわれる62歳のライダーなど、多彩な顔ぶれの参加者たちを追う。監督は「ステップ・イントゥ・リキッド」で高い評価を受けたデイナ・ブラウン。90人のクルーを引き連れ、50台以上のカメラを駆使して撮影された映像は迫力満点。", "2006-07-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブ・レター", "ラブ・レター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "浅田次郎の短編集『鉄道員〈ぽっぽや〉』の中に収められた一編『ラブ・レター』を映画化。新宿歌舞伎町で裏ビデオ屋の店長を任されている高野吾郎は知り合いのヤクザに頼まれ、中国人女性の白蘭と偽装結婚をする。彼女に日本の国籍を取らせるためだ。それから数ケ月後、吾郎は彼女が病死したことを知る。彼女の遺体を受け取るために彼は、勤め先のクラブがある千葉の千倉まで出かけるが……。渋々出かけた彼が異国の地で死んだ白蘭に対して、次第に感情移入していく様を、森崎東が繊細な演出で浮き彫りにしていく。白蘭が吾郎に残した１枚の手紙 “吾郎さんのお嫁さんのままで死んでいいですか？”は否応無しに涙を誘う。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あいつ", "あいつ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "生まれつき体が弱く、幼なじみの貞人からいじめの対象にされている光。ある日、ひょんなことから超能力を身に着けた光は……。NHKのニューウェーブ・ドラマで注目された同局の若手ディレクター、木村淳の劇場映画デビュー作で、「女がいちばん似合う職業」の岡本健一が風変わりなエスパー少年を好演した異色の青春ドラマ。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHARLESTON", "縄張〈シマ〉はもらった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "時代は1930年代、ニューヨークのリトル・イタリーへ乗り込んだチンピラ・ヤクザのソニー。マフィアのボスに見込まれ、謎の人物に牛耳られた縄張（シマ）をまかされ奔走するが、密造酒作りの牧師一味を助けたことから、ギャングたちとわたり合うことに。「くたばれカポネ」の続編。", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市関所破り", "座頭市関所破り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "座頭市は妙義山への道中、笠間の宿場町で芸人たちから法外な場所代をむしり取る島村の甚兵衛と結託する悪代官に怒りをおぼえ……。TV時代劇『三匹の侍』で人気上昇中だった平幹二朗が、悪玉側の用心棒役で座頭市と対決。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("内海の輪", "内海の輪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "四国・松山で呉服の老舗を営んでいる店主の妻は、大学のエリート・コースを歩む男と関係を持っていた。だが男は自分の立身出世のため、女が邪魔になっていた。やがて男の内部に殺意が芽生えていく……。男のエゴと女の情念を、サスペンス豊かに描いた一編。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SUPER", "ニューヨークのいたずら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ニューヨークのオンボロアパートを舞台に、悪徳家主が住人たちとの交流の中で真の人間性に目覚めていく姿を描いたハートフル・コメディ。環境改善を訴える住民の声などには耳を貸そうともしない家主のルーイは、ある日告訴される。そして判決の結果は、自分のアパートの一室に住むというものだった。住んでみて初めて、そのあまりのひどさに気付くルーイだが……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コアラ課長", "コアラ課長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「えびボクサー」「いかレスラー」に続いて名物宣伝マン、叶井俊太郎が放つ動物映画の第３弾。今度はコアラを主人公に、ある猟奇殺人事件に絡む国際的陰謀が描かれる。サイコ・スリラー、韓流的純愛、格闘アクションなど、着ぐるみの脱力感を遙かに凌ぐ怒濤の展開に圧倒させられる。韓国キムチ業者との契約を控えた漬物会社のコアラ課長。ある日、彼の恋人が惨殺され、コアラ課長に容疑がかけられるのだが……。", "2006-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YENTL", "愛のイエントル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "B・ストライサンドが共同製作・監督・共同脚本・主演・主題歌を兼任した人間ドラマ。男だけに学問の自由が与えられた20世紀初頭の東欧。無類の勉強好きの少女イエントルは、男装して神学校へ入学することに成功するのだが……。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪獣大奮戦 ダイゴロウ対ゴリアス", "怪獣大奮戦 ダイゴロウ対ゴリアス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "みなしご怪獣のダイゴロウを国が養うことになった。だが大食いのダイゴロウの食費を国税ではまかなえない。“ダイゴロウを救え！”と、子供たちは立ち上がる。一方宇宙から凶悪怪獣ゴリアスが飛来して……。東宝の“怪獣”もののユーモラスな一編。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MAISON", "薔薇色のロレーヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "古い館に住む孤独な老人と若者たちの交流を軸に、人生の喜びと悲しみを不思議なメルヘン・タッチで描いた秀作。ただ老いていくだけの日々を過ごす老科学者ルイの館に、ロレーヌという娘が滞在することになった。彼女はある晩、ボーイフレンドや友達を連れてきて誕生パーティーを開くが、彼らの若々しさを見ていると、ルイは自分の老いた肉体がいたたまれなくなり、やがて自殺を図る。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN THE DEEP WOODS", "殺人愛好症〈マニア〉の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "N・コンデの同名小説を映画化したサイコ・スリラー。女流作家ジョアンナの親友が森の奥深くで惨殺死体で発見された。次々と同じ手口での殺人が続くある日、探偵と名乗る謎の人物ミラーが、ジョアンナの前に現れる。ミラーがいうには、この連続殺人犯の正体は、彼女の兄トミーであるというのだ。1992年秋、エイズで亡くなったA・パーキンスの遺作となった。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHORT HOPE", "SHORT HOPE", "", "", "http://image.eiga.k-img.com/images/movie/55319/315x210.jpg?1485", 0, "下町の商店街を舞台に、3組の男6人が繰り広げる3本のストーリーから構成される人情劇。女子高生と肉体関係を持ったことから淫行罪で留置所送りになってしまった勇が、面会に来た悪友の真治に助けを求める「勇＆真治」。俳優志望のジョーとみちるが、銭湯でヤクザの芝居をしていたところ、そこで本物のヤクザに出くわしてしまう「ジョー＆みちる」。親の文房具店を手伝いながら趣味のバンド活動に精を出す潤が、友人の竹田に久々に会う田舎の父親との食事に同席を求められる「潤＆竹田」。一見別々に見える話は、1本の群像劇としてつなげられている。", "2010-02-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STILETTO", "USAブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ベストセラー作家H・ロビンスの同名小説に基づく、美女と暴力を詰め込んだスケールの大きなアクション・スリラー。エキゾチックな舞台を背景に、カジノやパーティーといった華やかな陽の部分と殺人という陰の部分とがうまくブレンドされている。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富江・最終章／禁断の果実", "富江・最終章／禁断の果実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "伊藤潤二の人気ホラーコミック『富江』を映画化したシリーズの第４作。安藤希が何度殺してもよみがえってしまう、美しい化け物、富江の４代目を演じる。いじめられっ子の高校生・登美恵は、ある日、富江と名乗る美少女と友達になる。だが、彼女は25年前に登美恵の父親が恋をして、いまだに忘れられずにいる少女その人だった……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かくて神風は吹く", "かくて神風は吹く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "元寇の役における博多湾での海戦を、オールスター・キャストで描いたスペクタクル時代劇。台風シーンは、東宝特撮陣を招いて撮影された。もはや神風にでも頼らなければ、日本は戦争に勝てないと思って製作されたような気もする、挙国一致の気概をあおる愛国映画である。日蓮役には市川右太衛門。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Island of Dr. Moraeu", "D.N.A.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　太平洋上を漂流していたダグラスはモンゴメリーという怪しい男の船に救助され、彼の住む孤島へ連れていかれる。そこでは遺伝子学者モロー博士が半獣半人の生命体を生み出していた。やがて獣人たちは反乱を起こし、モロー博士を殺害。ダグラスは生きてそこから脱出できるのか？　「獣人島」「ドクター・モローの島」に続く三度目の映画化となったSFアドベンチャー。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXILS", "愛より強い旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "自身のルーツであるロマ文化に根ざした作品を作り続けてきたトニー・ガトリフ監督が、自らの生い立ちを基にした半自伝的なロード・ムービー。パリでの生活に違和感を覚えた青年が、恋人を連れて少年時代を過ごしたアルジェリアへと旅立つ。一見無軌道な旅の過程からは、世界の現状が見え隠れ。音楽通のガトリフらしく、民俗音楽や現代的なトランス音楽が満載され、目と耳を通じて旅を体感させる。第57回カンヌ映画祭では最優秀監督賞に輝いた。主演は新作が相次ぐフランスの人気俳優ロマン・デュリス。少年時代にフランスに移住したザノは、祖国であるアルジェリアを目指して恋人のナイマと旅に出る。無賃乗車と野宿を続けながらアフリカに近づくが、乗る船を間違えてしまい……。", "2005-12-17", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マッスルヒート", "マッスルヒート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "肉体派ケイン・コスギが、その優れた身体能力を駆使して挑んだアクション。舞台は2009年の東京。公安局の特殊任務に就いた元特殊部隊員のジョーは、香港マフィアの黒幕、建仁を追って敵地に潜入。しかし、相棒の桂木は捕えられ、みずからも危険な状況に追いこまれた。意を決したジョーは、建仁の賭博場に単身乗りこみ、死のバトルに挑む……。ジャッキー・チェン作品のスタッフとして知られるサム・ウォンがアクション監督を務め、肉弾戦の見せ場をダイナミックに演出。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hollywood Boulevard", "ハリウッド・ブルバード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「グレムリン」のジョー・ダンテ監督と「ロックンロール・ハイスクール」のアラン・アーカッシュ監督の長編デビュー作。製作総指揮は、カルト的人気を誇るB級映画の巨匠ロジャー・コーマン。スターになることを夢見てハリウッドにやって来た女優志望の少女。しかしどこからも相手にされず、やっとのことでたどり着いたのは、ポルノからSFまで何でも手掛けるB級専門の映画会社だった……。映画製作の舞台裏を描くB級コメディ。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jimmy Hollywood", "ジミー・ハリウッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「レインマン」でアカデミー監督賞に輝いたバリー・レビンソン監督によるコメディ・ドラマ。主人公ジミーは、オーディションに毎回落ち続ける売れない役者。ある日、車上荒らしに腹を立てた彼は、友人で短期記憶障害を持つウィリアムと共に自警団を結成、見事車上荒らし犯を捕まえる。ところが、警察のちょっとした勘違いによって、事態は思いがけない方向へ進み始める……。ハリソン・フォードが本人役でカメオ出演している。", "1994-01-01", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新SOS大東京探検隊", "新SOS大東京探検隊", "", "", "http://image.eiga.k-img.com/images/movie/33979/315x210.jpg?1327", 40, "大友克洋の短編コミック「SOS大東京探検隊」に大胆なアレンジを加え、続編として映画化した3Dアニメ。父親が小学生の頃に書いたノートを発見した少年が、そこに記されていた秘密の宝物を探そうと仲間たちと探検隊を結成。マンホールから地下に下りて大冒険を繰り広げる。監督の高木真司をはじめ、大友監督作「スチームボーイ」のスタッフが再び集結。伝統的なセルアニメの手法を取り入れ、従来の3Dアニメをさらに進化させた。", "2007-05-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BATMAN THE MOVIE", "バットマン オリジナル・ムービー", "", "", "http://ecx.images-amazon.com/images/I/51wvOD-B2pL._SL210_.jpg", 105, "コミックスやTVシリーズで人気の“バットマン”シリーズを映画化した作品。人体から水分を奪う殺人機械を発明したペンギン、ナゾラーらの悪人が人類に挑戦してきた。迎え撃つは正義のヒーロー、バットマン！　1989年のリメイク作のハードな雰囲気とは違って、カラフルでキッチュな娯楽映画だ。", "1967-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CEDDO", "チェド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "17世紀中頃の西アフリカの某王国を舞台に、民衆を改宗させようとするイスラム教の尊師たちと、彼らに対抗して伝統を守り続けようとする人々の対立を描いた作品。タイトルは、西アフリカのウォロ語で“抵抗者”の意味。監督のセンベーヌは、アフリカ映画界の第一人者で小説家でもある。", "1976-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN FREE", "野生のエルザ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ファミリー映画の古典。ケニアの草原に生まれた野生のライオン、エルザと、東アフリカの狩猟家アダムソン夫妻の心温まる交流の物語。原作は世界的なベストセラーとなったJ・アダムソンの同名ノン・フィクション。主題曲『ボーン・フリー』もヒット。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Cat's Meow", "ブロンドと柩の謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1924年に実際にあった事件“オネイダ号の謎”を題材にしたミステリー。「スパイダーマン」のK・ダンストがヒロイン役で新たな魅力を放つ。1924年、新聞王のハーストが豪華客船でパーティーを開催、ハリウッドのセレブ15名を乗せて船が出航する。パーティーが盛大に進む中、ハーストは愛人のマリオンとチャップリンの親密そうな姿に嫉妬、怒りが頂点に達して……。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORE", "モア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "ステファンは、不思議な縁で結ばれたエステルを追って、パリからスペインのイビザ島までやって来た。ギラギラと照りつける太陽、そして紺碧の海。ここは別天地だ。だが、魔性のようなけだるい音楽が二人を麻薬の世界に引き込んでいく……。斬新な映像感覚とピンク・フロイドのテーマ曲が話題を呼んだシュローデルのデビュー作。", "1970-01-01", "58", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUTOSTOP ROSSO SANGUE", "ヒッチハイク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "キャンピングカーでアメリカを旅する夫婦が、見知らぬ男のヒッチハイクに応じる。ところが、その男は、200万ドルの殺人強盗犯だった……。「O嬢の物語」でデビューしたクレリーのすさまじいレイプ・シーンと、ラストのあっと驚く結末が話題になったサスペンス・アクション。", "1976-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふたりだけのアイランド", "ふたりだけのアイランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "東京に住むOLの律子は、自然を愛する男・俊晴と結婚。二人は南海の無人島でハネムーンを過ごすことになるが、律子の想像とはかけ離れたサバイバル生活によって、互いの生活にズレが生じ始め、二人の仲は危うくなるが……。中川安奈が、ハネムーンによって大自然の素晴らしさに目覚めていくヒロイン役を好演。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEN IN WAR", "最前線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "TVシリーズ“コンバット”の原形であるこの作品は、ハリウッドの第一級のスタッフ、キャストによって作られた戦争佳作。朝鮮戦争下のアメリカ軍小隊が最前線で孤立し、敵の幾多の攻撃を受け、内部対立しながら撤退していくさまを、タイトなストーリー・ラインで描く。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大菩薩峠・第１部", "大菩薩峠・第１部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "中里介山の死で未完のままとなった長編小説『大菩薩峠』を内田吐夢監督と片岡千恵蔵のコンビで映画化したシリーズの第１部。剣をとっては天下無敵の“音なしの構え”の机龍之助だが、彼の心の底には絶えず自分さえ信じ得ぬ虚無の嵐が吹き荒れていた……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paju", "坡州　パジュ", "", "", "http://image.eiga.k-img.com/images/movie/56769/315x210.jpg?2128", 110, "ソウル西北に位置し、北朝鮮とも対峙する街パジュ。数年間インドで放浪生活を送っていたウンモは故郷のパジュに帰ってくるが、そこでは事故死した姉ウンスの夫、ジュンシクが以前と変わらぬ生活を送っていた。かつて学生運動の活動家だったジュンシクに対し、姉の生前から複雑な感情を抱いていたウンモは、次第にジュンシクを異性として意識し始める。一方で、姉の死に疑問を抱いたウンモはジュンシクを責めるが……。2011年「真！韓国映画祭」にて上映。", "2011-05-28", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヱヴァンゲリヲン新劇場版：序", "ヱヴァンゲリヲン新劇場版：序", "", "", "http://image.eiga.k-img.com/images/movie/34139/315x210.jpg?3308", 98, "1995～96年に放送され、社会現象を巻き起こしたTVアニメ「新世紀エヴァンゲリオン」を、新たに4部作の劇場版として構成する「新劇場版」の第1部。原作・総監督はTV版に続いて庵野秀明が務め、「日本沈没」の樋口真嗣が画コンテで参加。14歳の少年・碇シンジは、10年ぶりに再会した父ゲンドウが司令官を務める特務機関NERV（ネルフ）に呼び出され、人型決戦兵器“エヴァンゲリオン”に搭乗し、“使徒”と呼ばれる謎の敵と戦えと命令されるが……。", "2007-09-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渋谷区円山町", "渋谷区円山町", "", "", "http://ecx.images-amazon.com/images/I/51fcWFNjNVL._SL210_.jpg", 110, "おかざき真里の同名コミックを映画化した青春ラブストーリー。友達と渋谷に遊びに来た女子高生・由紀江は、若い臨時教師・ヤマケンが円山町のラブホテル街で女性と歩いているのを目撃する。それ以来、由紀江はなぜかヤマケンのことが気になりはじめる。一方、吹奏楽部に所属する糸井は、学校でいつも一緒に行動している瀬田たちから陰湿なイジメを受けていた。無理して明るく振舞う糸井のことが気になって仕方がない有吉は、彼女を渋谷へと誘いだす。", "2007-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN THE SPIRIT", "霊感コメディ イン・ザ・スピリッツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "勤務先のビバリーヒルズで、夫が突然会社をクビになったため、慌ててニューヨークに引き返したマリアンヌ。なんとか親切な未亡人リーバの家に居候することになったが、隣家の売春婦が事故死。その日からマリアンヌたちは、恐ろしい殺人鬼に狙われる。P・フォークなどのベテランが脇を固めるコメディ・タッチのサスペンス。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARIA", "アリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "イギリスの異色プロデューサー、ドン・ボイドが、世界の映画界から独自の映像主義で有名な10人の監督を指名し、古今のオペラのアリアの中から最もインスピレーションを触発されたものを一つ選ばせて、それを自由にイメージさせた数分間のフィルムを、J・ハート演じる狂言まわしの登場でつなげ１本にまとめた作品。たとえばヴェルディの『運命の力』(C・スターリッジ)は路傍のメルセデスを盗んだ３人の子供たちの悲劇、ワーグナーの『トリスタンとイゾルテ』(F・ロッダム)はラスベガスのホテルの一室で肉体を交える若い男女の愛の高揚と死、ルイーズの『シャルパンティエ』(D・ジャーマン)は年老いた女優が舞台の紙吹雪と喝采の中で回想する初恋の日々——といったぐあいにイメージが置き換えられている。各監督の個性と才気が競い合うオムニバス作品。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gargandi snilld", "スクリーミング・マスターピース", "", "", "http://image.eiga.k-img.com/images/movie/34049/315x210.jpg?1380", 87, "ビョーク、シガーロスら才能溢れるミュージシャンを次々と輩出するアイスランドの音楽シーンにスポットを当てたドキュメンタリー。ヨーロッパとアメリカの間に位置し、それぞれの影響を受けながら独自の文化を発展させてきたアイスランド。世界的アーティストはもちろん、日本では紹介されることの少ないインディーズ・ミュージシャンらのライブ映像やインタビューを交えながら、その独創的な音楽の世界を紹介していく。", "2007-07-07", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANTO＾MAS", "ファントマ 危機脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "大胆奇抜な犯罪で、フランス全土を恐怖に陥れる怪人ファントマとそれを追う警部、そしてファントマを慕う美女の追いつ追われつを描く人気活劇シリーズの第１作。スタント・マンに頼らず、アクション・シーンに挑んだマレーの奮闘が見もの。", "1964-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOT BUBBLEGUM（LEMON POPSICLE III）", "グローイング・アップ3　恋のチューインガム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "人気シリーズの第３作。せっかくできたステディ、ドリスをほったらかして、ベンジーは今日もナンパに精を出す。美人のニキとベッドインで、ベンジーはすっかりのぼせ上がるが、ニキの本性を知った彼は再びドリスのもとへ……。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOP MODEL", "トップ・モデル 魔性の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "男女の愛欲をテーマにした体験小説を書いてヒットさせた女が、次回作のネタさがしに高級コールガールになることを思いつく。女性編集者がそのアイデアにのって、開いたクラブは大繁盛となるが……。イタリア製のエロチシズム謳歌作品。ヒロイン役ムーアのトランジスタ・グラマーぶりが見もの。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КОТО ТАТО ЛЕЬА", "歌っているのはだれ？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "1941年、セルビアの田舎から首都ベオグラードに向かう木炭バスに、ジプシーや農夫、ハンターら様々な人々が乗り合わせる。続出するアクシデントの数々を人々とともに滑稽に詩的に綴っていく。ユーゴの俊英シヤン監督が34歳の時に手掛けた劇場用映画のデビュー作。", "1980-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REDLINE", "レッドライン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ベテラン、R・ハウアーと「クライング・フリーマン」のアクション俳優、M・ダカスコスが共演した近未来アクション。1930年代のシカゴを彷彿とさせる腐敗したロシア。幻覚シリコンでひともうけをたくらんだ密輸業者ウェイドはパートナーのメリックと恋人カチャの裏切りにあい、射殺される。だが、ロシア当局に発見された彼は大脳皮膚蘇生手術を受けて生き返り、メリックとカチャへの復讐を誓う……。監督は「ハードカバー　黒衣の使者」でアヴォリアッツ国際ファンタスティック映画祭グランプリを受賞したT・タカクス。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チェスト！", "チェスト！", "", "", "http://image.eiga.k-img.com/images/movie/53439/315x210.jpg?2397", 110, "鹿児島の“錦江湾横断遠泳大会”に挑戦する小学生たちの奮闘を描いた青春ドラマ。小学6年生の隼人には、クラスの皆に言えない秘密があった。それは、自分がカナヅチだということ。毎年夏に開催される錦江湾横断遠泳大会も、いつも仮病を使って休んでいた。ところが小学校最後の年である今年は逃げ切ることができず、強制的に参加することになってしまう。仕方なく仲間たちと共に練習を開始する隼人だったが……。", "2008-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拳銃無頼帖 明日なき男", "拳銃無頼帖 明日なき男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "抜き射ちの竜は、ライバルのコルトの譲が病気で入院しているという噂を聞いて見舞いにやってくる。実はこれがかつて竜に取引の邪魔をされた暴力団清流会会長・辻堂の罠であった。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("その後の仁義なき戦い", "その後の仁義なき戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "題名に“仁義なき戦い”と銘打っているものの、本シリーズとは直接関連のない、いわば番外編。監督は深作欣二から工藤栄一にバトンタッチ。暴力組織の内部抗争という設定の中で、ほんろうされる若者たちに焦点を合わせ、友情、裏切り、激烈な生きざまを描く。大阪の大暴力団・石黒組の若頭の地位を巡り、傘下の浅倉組と花村組の対立が深まる。そんな中で、浅倉組系津川組の相羽と、同じ浅倉組系竜野組の根岸、水沼は意気投合、友情を深める。だが浅倉は対立する花村を蹴落とすために、竜野組をつぶし、そのいざこざに花村を巻き込もうとした。相羽は竜野殺しの手引きを命じられる。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スキヤキ", "スキヤキ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "双子の姉妹を主人公に、家族や社会の様々な問題に取り組んだ意欲作。郵便局に勤める三原理香は、仕事のほかにも家業の洋食屋の手伝い、家事と大忙しの毎日。家には老人性痴呆症の祖母がいて、その面倒だけでも大変だ。双子の姉・由香が持病のため家にこもりがちなのも気にかかる。ある日、外洋貨物船のコックである豊田達也が店に現れ一家に深くかかわり始めたことから、理香を取り囲む問題が一気に噴出する。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWINKY", "おませなツインキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ロンドンに住むおませな少女の恋と冒険の日々を描いたコメディ。16歳の少女ツインキーには22歳も年上で、エロ小説家の恋人スコットがいた。ある日ツインキーが突如結婚宣言、驚いたスコットは一瞬とまどうが彼女の純粋な愛に気付き、結婚を決意する。だが当然のごとく様々な難関が待ち受けていて……。", "1969-01-01", "3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Terminator 3: Rise of the Machines", "ターミネーター3", "", "", "http://image.eiga.k-img.com/images/movie/1189/315x210.jpg?1407", 110, "ジェームズ・キャメロン監督の人気シリーズ第3作を「U-571」のジョナサン・モストウ監督が映画化。ドラマは前作の10数年後、20代の青年ジョン・コナーの前に前2作のT-800そっくりのターミネーターT-850と、その進化型である女性型ターミネーターT-Xが出現。未来から彼らを送ってきたのは誰なのか？　その目的は？　シリーズの設定はもちろん、お約束セリフもすべて登場する正攻法の続編。ILMがリアル系VFXで実力を発揮。", "2003-07-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("土と兵隊", "土と兵隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "原作は火野葦平の有名ないわゆる兵隊３部作『麦と兵隊』『土と兵隊』『花と兵隊』の一作。監督は「五人の斥候兵」が大ヒットしたため起用となった田坂具隆。軍部の意向で製作された超大作であり、中国大陸で大々的ロケーションを敢行している。「五人の斥候兵」以上に本作品はドキュメンタリー・タッチにあふれ、その淡々とした筆致には詩情すら感じられ、素晴らしい出来ばえ。派手な戦闘場面も少なく、部隊が転進していく様子をただひたすら捉えている。嘉善城攻略の任務を帯び、清水隊は出発した。難行軍が続く中で、兵隊たちは疲労困憊しきっている。嘉善城を目前にしての攻防戦の末、ついに城を陥落させる。それもつかの間、清水隊は嘉善入城もせず、再び前進の行軍を開始する。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天與地／TAIN DI", "アンディ・ラウ 天與地〈てんとち〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "数々の妨害工作に遭いながらも、麻薬撲滅のために一人で組織に戦いを挑む、監察官の姿を描く。1926年、アヘン禁止令の初代監察官として上海に赴任したチャン・ヤッパンは、警察が麻薬組織とグルである腐敗した光景を目のあたりにする。チャンを買収しようと組織の大物タイと警視総監の二人はやっきになるが、チャンは応じることなく、徹底的に取り締まりを行う。だが、組織の魔の手は愛妻スーに襲いかかり……。当時、香港のアイドル四天王の一人といわれたA・ラウ主演で、巨額の3000万香港ドルを投じた香港版「アンタッチャブル」。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hero", "靴をなくした天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "マスコミが作り上げた虚像に振り回される人々の姿を描いた風刺コメディ。飛行機の墜落現場に遭遇したコソ泥のバーニー。彼は嫌々ながらも乗客たちを助け出し、そのままその場を立ち去った。その飛行機に偶然乗り合わせていた女性テレビリポーターのゲイルは、現場に残った靴を手がかりに、テレビで”謎のヒーロー”の公開捜査を開始。そしてある男性が名乗りを挙げるが……。主人公バーニーを、名優ダスティン・ホフマンが好演。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Deep", "ザ・ディープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "バミューダ海域を舞台に、若い新婚カップルが、沈没船に残された大量の麻薬をめぐる争奪戦の渦中に巻き込まれる海洋サスペンス。見どころは、全体の半分以上を占める水中撮影。海底の美しい映像はもちろん、Tシャツ姿で潜るビセットのセクシーな肢体も楽しめる。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Prima della rivoluzione", "革命前夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "　1960年代、パルマに住む若者が、自らのイデオロギーのあり方に苦悩する姿を描いた青春映画。マルクス主義に傾倒するファブリツィオは、ブルジョア階級の婚約者との別れを決める。しかし、友人の死や若い叔母との近親相姦、希望の見えない没落地主を目の当たりにし、思想が揺らぎ始める。フランス人作家の小説にアイデアを得ているが、ベルナルド・ベルトルッチの半自伝的作品といわれている。", "1964-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fantastic Four", "ファンタスティック・フォー　超能力ユニット", "", "", "http://image.eiga.k-img.com/images/movie/1059/315x210.jpg?1442", 106, "天才科学者リードたち4人は、宇宙実験の失敗が原因で、人間を超えた能力を得る。同様の能力を得た実業家のビクターは、実験の失敗により富も名声も失ったことから、邪悪な計画を実施し、人々を恐怖に陥れる。4人は“超”能力を正義のために使う決意をし、かつての友、ビクターとの戦いに挑んでいくのだった……。「スパイダーマン」や「X-MEN」を生んだマーベル・コミックスのスタン・リーが原作者のSFファンタジー・アクション。", "2005-09-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CREEPSHOW 2", "クリープショー２／怨霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "キング＝ロメロのコンビによる大ヒット・ホラー「クリープショー」の第２作。今回ロメロは脚本のみを担当している。強盗に殺された雑貨店の夫婦の仇を討つインディアン人形、ひき逃げをして死んだはずの被害者にいつまでも追いかけられる加害者の話など、全３話のオムニバスは全体的に前作に劣る。前作同様、特殊メイクをトム・サヴィーニが担当。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SALSA", "サルサ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ラテン音楽に魅了されたフランス人天才ピアニストのレミは、輝かしい未来を捨てサルサの世界へ。ところが、キューバ人に“サルサはチョコレート（黒人）じゃなきゃダメ”と門前払いをくらってしまう。諦めきれないレミは、肌と髪の色を変え身も心もキューバ人になりすまし、魅力的な恋人ナタリーも出来るのだが……。偽りの姿に苦悩するレミを映画初出演のヴァンサン・ルクールが熱演。終始鳴り響くキューバ音楽が、心と体を熱くさせるチャーミングなラブ・ストーリー。", "1999-01-01", "2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SITUATION NORMAL ： A.F.U.", "シシリー要塞異常なし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "第二次世界大戦下。シシリー島上陸命令を受けた、５人のアメリカ軍生え抜きの戦争プロフェッショナルの活躍を描くアクション・コメディ。このテの戦争映画としてはJ・リー・トンプソンの「ナバロンの要塞」などが有名だが、ドキュメンタリー出身のN・ロイ監督は、笑いの中に、ファシズムへの抵抗をリアルに浮かび上がらせている。P・フォーク、J・ロバーズなど、ひとクセある俳優たちの競演も見もの。", "1972-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブラックキス", "ブラックキス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "『白痴』の手塚眞が構想６年をかけて練った新感覚サイコ・スリラー。俳優陣には犯人を明かさないという演出により、リアルな恐怖心を煽っている。歌舞伎町で芸能プロデューサーが惨殺された。神出鬼没の犯人の魔手は、殺人現場を偶然目撃したモデルの卵、アスカの身にも忍び寄る……。妖しいムードを漂わせたスタイリッシュな夜のシーンと、リアリズムにこだわった刑事の捜査シーンを交互に描出、特殊メイクを駆使したギミックも随所に炸裂する。", "2006-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NICK OF TIME", "ニック・オブ・タイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "税理士のジーン・ワトソンは、娘思いの平凡な男。ところが、ロサンゼルスのユニオン・ステーションで、暗殺者たちに目をつけられたばっかりに、とんでもない事件に巻き込まれてしまう。娘を人質に取られ、“90分以内にカリフォルニア州知事を暗殺しなければ、娘の命はない”と、脅迫されたのだ。誰かに助けを求めようにも、暗殺者の厳しい監視のもと、なすすべもなく時間だけが過ぎていく。娘を救うためには、知事を殺すしか方法はないのか？　「シザーハンズ」や「エド・ウッド」などアクの強い役柄が多い個性派ジョニー・デップが、極限状況で右往左往する平凡なサラリーマン役に挑戦し、緊張感あふれる演技を見せている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("原野の子ら", "原野の子ら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "「ブリキの勲章」など、教育映画のベテランとして知られる中山節夫が手掛けた人間ドラマ。阿蘇の雄大な自然とともに生きる人々の姿を、若い女性教師の奮闘劇を中心に綴る。熊本市内から阿蘇の小さな分校に赴任してきた淑子。１、２年生を担当することになった彼女は、様々な悩みを抱える生徒の家族たちと出会う。そんなある日、痛ましい事件が起きる……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妻の貌", "妻の貌", "", "", "http://image.eiga.k-img.com/images/movie/54699/315x210.jpg?4202", 114, "広島在住の82歳の映像作家・川本昭人が半世紀に渡って撮影した妻キヨ子の人生の記録。原爆の被爆により甲状腺がんと診断されたキヨ子は、大病を患いながらも寝たきりの義母の世話をし、2人の子どもを育てあげる。子どもの成長や孫の誕生など、平凡な日常の記録が広島の暗い歴史を物語る。", "2009-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("つぶより花舞台", "つぶより花舞台", "", "", "http://image.eiga.k-img.com/images/movie/54319/315x210.jpg?3654", 78, "最高年齢69歳の “シニア劇団かんじゅく座”に7カ月間密着したドキュメンタリー。演技未経験の14人の高齢者が集まり、2007年に旗揚げしたかんじゅく座。子供のころの夢、認知症の予防など様々な動機で集まった劇団員達が初舞台を目指し奮闘する様子をカメラが記録していく。監督は俳優・作家・演出家としても活躍する鯨エマ。", "2009-01-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サルベージ・マイス", "サルベージ・マイス", "", "", "http://image.eiga.k-img.com/images/movie/56479/315x210.jpg?1784", 81, "若手実力派女優の谷村美月が初のアクションに挑戦。オール広島ロケを敢行し、神出鬼没の怪盗マイスの活躍を描く。地元・広島を心から愛する美緒は、連続する美術品窃盗事件を追う最中、謎の怪盗マイスと出会う。溢れる正義感から当初はマイスに憤りを覚えた美緒だったが、やがてマイスが正義のために盗みを重ねていることを知り、協力して事件の解決に奔走することになる。監督は「仮面ライダー」シリーズの田崎竜太。「ハイキック・ガール！」の西冬彦がプロデュースとアクション監督を担当。", "2012-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BANG THE DRUM SLOWLY", "バング・ザ・ドラム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "白血病に冒されたメジャー・リーグのキャッチャーと、同じチームの投手との男の友情のドラマ。主人公に、若き日のデ・ニーロが扮し、実在のメジャー・リーグの選手も顔を見せている。また、監督役のガーディニアはアカデミー助演男優賞にノミネートされた。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛇娘と白髪魔", "蛇娘と白髪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "楳図かずおの恐怖漫画『ママが怖い』と『赤ん坊少女』を原作に、“ガメラ”シリーズで知られる湯浅憲明が監督した少女向け恐怖映画。孤児の小百合はふとしたことから南条家で暮らすことになるが、その家の天井裏にはタマミという娘が住んでいる。ある日小百合は風呂場でタマミの恐ろしい秘密を見てしまう。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("栄光へのシュプール・猪谷千春物語", "栄光へのシュプール・猪谷千春物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "昭和31年の冬季オリンピック大会で銀メダリストに輝いた天才スキーヤー、猪谷千春の半生を、感動的なエピソードを重ねて描いた長編伝記アニメーション。幼い頃から父にスキーの基本を教え込まれた千春。小学生になり競技会に出場しては抜群の成績を収めていた彼は、いつしかオリンピック出場を夢見るようになっていた。しかし、昭和20年に戦争が終わり敗戦国の日本は国際大会に参加できず、千春は自分を見つめ直すため両親のもとを離れていく。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD OR ALIVE・犯罪者", "DEAD OR ALIVE・犯罪者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「日本黒社会・LEY LINES」における“静”とは好対照を見せる、三池崇史ならではの“動”の演出が縦横無尽に疾走する一大傑作。Vシネマ界の両横綱、哀川翔と竹内力の初顔合わせという話題さえもかすんでしまう怒涛の映像世界を構築している。新宿署のはぐれ刑事、城島は歌舞伎町路地裏の中華料理店でチャイニーズ・マフィアと日本ヤクザの激突に遭遇。捜査を開始すると、仲間を率いて銀行を襲撃している中国残留孤児三世、本上龍一の存在が浮かび上がってくる。大物二人の対決という古色蒼然とした図式を極限まで押し進めることによって生まれた問答無用のラスト・シーン。誰もが言葉を失うに違いない結末は掟破りと呼ばれかねないものだが、そこに至る時空の変容、変質は緻密な編集によってしっかり計算されている。図太さと精妙さを兼ね備えた破格の構成に加え、非情なバイオレンス・シーンなど、三池は活劇の新たな地平を完全に切り開いたといえるだろう。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE BUTTANE", "シチリアの娼婦たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "「永遠のマリー」と同様、監督A・グリマルディが自身の同名小説を映画化。７人の娼婦たちのそれぞれのエピソードを断片的につなげ、彼女たちの生活をユーモラスに、時には哀しく描いた作品。イタリア標準語とは異なるシチリア方言をそのまま使い、説明的なシーンを省いたモノクロの映像が印象的だ。", "1994-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shoot 'Em Up", "シューテム・アップ", "", "", "http://image.eiga.k-img.com/images/movie/34199/315x210.jpg?1555", 86, "ニューヨーク暗黒街で生きる凄腕ガンマンのスミスは、ある日ひとりの妊婦が悪党たちに追われている場面に遭遇する。助けに入ったスミスが悪党たちと銃撃戦を繰り広げる中、突然産気づいた妊婦はその場で赤ん坊を出産。流れ弾に当たって絶命した彼女に代わり赤ん坊を守り抜こうとするスミスだったが、悪党たちは執拗に赤ん坊を狙う。その背後には巨大な陰謀が潜んでおり……。主演はクライブ・オーウェン。", "2008-05-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ストロベリーロード", "ストロベリーロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1960年代のベトナム戦争に揺れ動くアメリカ・カリフォルニア州を舞台に、広大ないちご畑にアメリカン・ドリームを託し、渡米した日本人の兄弟・久と明。二人は葛藤しながらも、それぞれ恋をし、様々な人たちと出会っていく……。第20回大宅荘一ノンフィクション賞を受賞した石川好原作の映画化。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY FAMILY／MI FAMILIA", "ミ・ファミリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "アメリカでの成功を夢みて、1920年代にメキシコからロサンゼルスにやって来た青年ホセが60年かかって築き上げたサンチェス家の親子３代にわたる物語。不法入国や家族の不和、犯罪や死など、様々な試練や犠牲、失望の中でも懸命に生きるサンチェス家の姿を、いくつものエピソードを織り交ぜながら、生き生きと描き出している。“ゴッドファーザー”シリーズで、印象深い家族ドラマを作り上げたF・F・コッポラが製作総指揮にあたり、アメリカン・ドリームを求めたメキシコ系アメリカ人の家族の歴史と文化を余すとこなく語り尽くしている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四谷怪談・お岩の亡霊", "四谷怪談・お岩の亡霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "おなじみの“四谷怪談”の映画化。大映の鬼才・森一生が鮮やかなカラー画面の中に描く。原作と違い映画では、お岩と一緒に伊右衛門に殺される按摩が伊右衛門と組んで、自殺したお岩を不義の心中にみせかける……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京伝説／蠢〈うごめ〉く街の狂気", "東京伝説／蠢〈うごめ〉く街の狂気", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "シリーズ化されている平山夢明の人気小説を、「富江」の及川中監督が映画化したホラー。都市に住む女性の恐怖の体験を、実話に基づいて描く。明け方に鳴らされるチャイム、隣家の幼児虐待の気配、見知らぬ男からの不気味な手紙など、身近な事件を連ねた物語でドキドキさせられる。グロテスクな映像も強烈で、夢見が悪くなるかも!?　東京のとあるマンションでデザイン事務所を開業している大沢裕美子の周囲では、些細だが奇妙な出来事が立て続けに起こっていた。そんなある日、裕美子の元に差出人不明の手紙が届き、そこにはおぞましい内容が書かれていた。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Conan the Barbarian", "コナン・ザ・グレート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "　有史前のハイボリア時代。タルサ率いる一味に両親を殺された少年コナンは囚われの身となり、労働を強いられる。やがて雄々しく成長した彼は剣闘士として戦い抜き、自由の身になることを許される。両親を殺したタルサへの復讐を胸に旅に出たコナンは数々の苦難を乗り越えていくが……。剣と魔法の世界で繰り広げられる勇者の冒険を描いた骨太のアドベンチャー・ロマン。アーノルド・シュワルツェネッガーの出世作でもある。84年には続編も製作された。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ENFORCER", "脅迫者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ボギーお得意の推理サスペンス。殺人請負業の首領を追いかけ続けていた地方検事補が、首領の悪業を証言するという子分の自首で、翌日の裁判まで彼を保護することになる。しかしその夜、狙撃されかけた子分は窓から脱出を試みて墜落死。別の証人を捜して検事補が開始した新たな追跡行が意外な結末を呼ぶ。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マダムと女房", "マダムと女房", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "何よりもまず日本映画史上初の本格的トーキー作品として記憶されるべき作品であろう。この作品で使用されたのは、松竹の土橋武夫・晴夫兄弟の開発したいわゆる土橋式トーキーで、全編同時録音で撮影され、カットの変わり目で音が途切れぬよう３台のカメラを同時に回した。郊外の静かな住宅地に引越して来た劇作家は、予想外に隣家のジャズがうるさく原稿書きがはかどらないため、隣家へどなり込む。ところが、その家のマダムの美しさにすっかり参り、一緒になって騒ぎ始める、という喜劇。生演奏のジャズ、ラジオから聞こえる声、猫の鳴き声、目ざまし時計の鳴る音などが次から次へと聞こえる、まことににぎやかな作品。", "1931-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЛΕΤЯТ　ЖУРАВЛИ／THE　CRANES　ARE　FLYING", "戦争と貞操", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1956年のスターリン批判による“雪解け”を象徴するソ連映画の新しい波の代表作。建築士ボリスとベロニカは恋人同士。モスクワの夜が明けるまで幸福に酔いしれる。だが、ボリスに出征命令がくる。ベロニカに会えないほどあわただしく出征していくボリス。残されたベロニカは空襲で両親を亡くしたうえに、いとこのマリクに犯され、いやいやながらマリクと結婚する。そこにボリスの戦死の知らせ。ベロニカはそれを信じられず、ひたすらボリスの帰還を待つ……。流動的で叙情的なカメラワークによって恋する女の心情を美しく謳い上げながら、戦争の悲惨さを鋭く描いた感動的な名作。ビデオ、DVDタイトルは「鶴は翔んでゆく」。", "1957-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘花", "秘花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "“ピンクの黒澤明”こと若松孝二がこの頃、金のために配給会社の要請で監督した一連の商業ピンク映画の一編。主演の横山リエはこのあと同監督の「天使の恍惚」にも出演。若松孝二はこの作品のあと、足立正生とともにパレスチナへ旅立ち、長編記録映画「赤軍−PELP、世界戦争宣言」を撮る。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE ETC.", "ラブetc.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "もつれた恋の三角関係を、リアルに綴ったラブ・ストーリー。不器用だが誠実な青年ブノワは、新聞の恋人募集欄で知り合った女性マリーと結婚。式の当日、ブノワの友人ピエールは初めてマリーへの恋心に気付いた。彼が抑え切れない思いをあらわにしたことから、３人の関係に亀裂が生じ始め……。“愛してる”と言うことのない、選択のない愛を選んだヒロイン、C・ゲンズブールの好演が印象的。彼女が歌う主題歌も聴きどころ。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUSMAN", "敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "1984年に47歳で早世したギュネイが、「群れ」に引き続き獄中からの指示で製作、完成させた作品。ストーリーは、都会を舞台に、失業した男の内面の葛藤を、家族、特に妻とのあつれきを軸に描いている。人間にとって“真の敵”とは何かを深く問いかけた力作である。", "1984-01-01", "32", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("America's Sweethearts", "アメリカン・スウィートハート", "", "", "http://image.eiga.k-img.com/images/movie/1679/315x210.jpg?3640", 103, "わがままな人気女優グウェンの妹、キキは彼女の付き人。別居中のグウェンの夫、大物俳優のエディにひそかに思いを寄せているが、彼は彼女の気持ちに気づかない。グウェンとエディが共演した映画の公開が決まり、宣伝マンのリーは2人を復縁させようとする。ハリウッドの内幕をからめて描くラブ・コメディ。20世紀フォックスやディズニーの責任者を経たジョー・ロス、久々の監督作。宣伝マン役ビリー・クリスタルが脚本に参加。", "2002-03-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京物語", "東京物語", "", "", "http://ecx.images-amazon.com/images/I/41Akj%2B34s5L._SL210_.jpg", 136, "黒澤明、溝口健二とともに日本を代表する映画作家・小津安二郎の代表作といえばこの「東京物語」につきるだろう。小津がサイレント期から描き続けてきた親子関係のテーマの集大成ともいえる作品である。地方から老いた夫婦が上京し、成人した子供たちの家を訪ねる。子供たちははじめは歓迎するが、やがて両親がじゃまになって熱海に行かせたりして厄介ばらい。戦死した息子のアパート住まいの未亡人だけが親身になって面倒をみてくれるという皮肉。やがて老夫婦は田舎に帰るが、その直後、妻は急死してしまう。一人残された老人は、静かに海を見つめて……。戦後の日本における家族生活の崩壊を描いた、と監督本人が語るこの作品は、人間の孤独感、死生観といったテーマまでをも取り込み、味わい深い作品となった。志賀直哉を深く愛した小津監督は、『暗夜行路』にちなんで尾道市をラスト・シークエンスの舞台に選んだが、その尾道の寂れて、どこか温かい風景が、この厳しいテーマを繊細に包み込み、忘れることのできない画面を生み出した。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monolith", "モノリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "人間から人間へと寄生し、強烈な破壊エネルギーを放出する、宇宙からの未知生命体。このエイリアンが政府の研究機関から逃亡し、怪事件を巻き起こしていく。若い男女の刑事コンビがこれを追い、同時に謎の政府機関の陰謀に立ち向かう。SF的なシチュエーションにミステリーの要素を加えた異色の娯楽活劇。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Horsemen", "ホースメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "　アフガニスタンの山奥。騎士たちが最高の栄誉を賭けて戦うブズカシ（騎馬戦）に各地から闘士が集まる。ウラズは勇敢な騎士として知られた父親トルセンに指名され、試合に出場するが、敗北を喫してしまう。誇りを失い、名誉も傷つけられた彼は苦難の旅に出ることに……。騎馬の民として生まれた男の試練と成長を描く冒険叙事詩。アフガニスタンでロケを行い撮影された騎馬戦は大迫力。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("故郷〈ふるさと〉", "故郷〈ふるさと〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "高齢化社会の到来を見据え、老人パワーを元気いっぱいにうたい上げたロード・ムービー。沖縄県名護市の名物マラソン・ランナーである76歳の時子が、故郷の北海道根室市までのマラソン行脚を決意。この無謀な日本列島縦断マラソンはマスコミに報道され、時子は一躍脚光を浴びることになるが……。過酷なロケを乗り切った淡島千景の走りっぷりも感動を呼ぶ１本。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Ley del deseo", "欲望の法則", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「トーク・トゥ・ハー」等の作品で知られるスペインの鬼才ペドロ・アルモドバル監督が、男同士の危うい三角関係を描いた作品。ゲイの映画監督パブロにはファンという恋人がいたが、2人の関係は倦怠期に突入していた。ファンに対して物足りなさを感じ始めていたパブロは、ある日出会った青年アントニオと関係を持ってしまう。ところが、ファンがパブロに宛てた手紙を見て嫉妬に狂ったアントニオが、ファンを殺してしまい……。", "1987-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOMBAY", "ボンベイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "複合的なジャンルの横断をパワフルに見せ切る、インド映画らしい醍醐味に満ちた大作。イスラム教徒の娘とヒンドゥー教徒の青年が禁じられた恋を成就させ結婚するが、やがて混迷・過激化の一途をたどる宗教戦争の真っ只中に放り込まれていく。『ロミオ＆ジュリエット』風のロマンスが現実を告発する社会派ドラマへ変貌する様が、とにかくパワフル。「ラジュー出世する」「ムトゥ　踊るマハラジャ」に較べるとグッとシリアスだが、美しい音楽とゴージャスなダンス・シーンは期待を裏切らない。", "1995-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪異宇都宮釣天井", "怪異宇都宮釣天井", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "時は江戸時代。三代将軍・徳川家光は日光東照宮参拝の折、宇都宮城に一泊することになった。しかし、宇都宮藩の城主と家老は、豪商と結託して家光の暗殺を目論んでおり、その不穏な空気を察知した将軍家の隠密・利根は独自に暗躍を始める……。史上名高い宇都宮釣天井事件を、怪談映画の巨匠・中川信夫監督が描いた時代劇。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅がゆく６ 東北激闘篇", "修羅がゆく６ 東北激闘篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "会津・郡山を舞台に、土地利権をめぐる東西極道たちの争いを描いたアクション・シリーズの第６作。新宿・本郷組は、会津に組を構える滝沢一家と共同でリゾート開発に乗り出していた。一方、郡山では関東木曜会の春田組が駅前の再開発プロジェクトに着手。そんな折、本郷を目の敵にする関西最大の岸田組が動き出し、抗争の火種を巻いたことからついに一触即発の巨大抗争に発展し……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("復讐の歌が聞こえる", "復讐の歌が聞こえる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "石原慎太郎の原作を、貞永方久と山根成之が助監督籍のまま共同監督した異色アクション。城所の悪どい手口に竹中は会社と恋人を奪われ、そのために父と兄は自殺する。竹中は城所を殺そうとして失敗。10年後出所した彼は再び復讐を開始する……。この作品でデビューした原田芳雄が熱演。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AZ EN XX. SZAZADOM／MY 20TH CENTURY", "私の20世紀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "エジソンが発明した電球の灯によって、新しい時代が到来した19世紀末から20世紀初めのブタペストを舞台に、運命のいたずらから別々に育てられた双子の姉妹の人生をファンタスティックに描いていく。カンヌ国際映画祭新人監督賞ほか多数の賞を受賞した、20世紀の光と影のオマージュ。", "1989-01-01", "30,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　スペシャル劇場版 ローンシャーク", "難波金融伝 ミナミの帝王　スペシャル劇場版 ローンシャーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1990年代の不況を背にコゲついた借金を次から次へときりとっていく浪花の高利貸し・萬田銀次郎を主人公とするシリーズのスペシャル劇場版。アメリカで事業に失敗、マフィアに追われる千脇と、ホスト遊びに入れ込み、500万の借金を抱える笙子は、銀次郎から1000万円を借りてエステサロンを開店。100名を越える会員の契約金を持ち逃げ、借金を踏み倒された銀次郎の怒りが爆発する。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELECTRIC SANSHIN", "沖縄・文化のゴーヤチャンプルー／ Electric Sanshin", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 49, "イタリア人のロイク・ストラーニ監督が、沖縄を題材に作り上げたユニークな記録映画。版画家の名嘉睦稔や、映画監督の中江裕司ら沖縄のさまざまなアーティストのインタビューに、アニメによるナレーションを加えた構成で、沖縄の文化を見つめる。沖縄の真の姿が見えてくる、興味深い一作だ。いろいろなことが混ざることを意味する沖縄の言葉“ちゃんぷるー”。この言葉を共通項に、沖縄の表現者たちがさまざまなことを語り明かす。", "2005-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TRUMAN SHOW", "トゥルーマン・ショー", "", "", "http://ecx.images-amazon.com/images/I/514jhuQVCQL._SL210_.jpg", 103, "離れ小島の町シーヘブンで生まれ育ったトゥルーマンは妻や友人たちに囲まれ、保険会社のセールスマンとして何不自由ない平穏な暮らしを送っていた。だが、彼の生活は生まれた時から24時間年中無休で全世界にテレビで放送されており、シーヘブンは作り物で彼以外の人間は全員、プロデューサー・クリストフの指示で動く役者であった。徐々にこの秘密に気付き始めたトゥルーマンは……。“もし、自分の人生が演出されたものだったら？”そんなオリジナリティーあふれる脚本を執筆したのは「ガタカ」で監督デビューも飾ったA・ニコル。それをP・ウィアーが叙情性とブラック・ユーモアあふれる人間ドラマとして映像化したユニークかつ心温まる作品。秘密を知ったことから自分の運命を自らの手で切り開こうとする主人公をJ・キャリーが好演し、1999年ゴールデン・グローブ主演男優賞を受賞した。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fixer", "フィクサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "　帝政ロシアの末期。反ユダヤ運動の激しいウクライナ・キエフで暮らすヤコフはユダヤ人の部落を抜け出したのちに、秘密警察に逮捕され、少年刺殺事件の犯人に仕立て上げられてしまう。民衆の怒りの矛先をユダヤ人に向けようとする政府当局は無実のヤコフに自白を迫る。事件が国際的関心事となる中、ヤコフはあくまで潔白を主張し、裁判を要求し続けるが……。ピューリッツァー賞に輝いた小説を映画化。政府による迫害に立ち向かった男の姿を感動的に描く。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pedal", "ペダル　ピストバイク・ムーブメント in NY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "写真家として注目を集めるピーター・サザーランドが手がけた、ニューヨークのバイク・メッセンジャーたちの日常を追ったドキュメンタリー。もとは競技用として日本の競輪などで使用されていたブレーキのない自転車「ピストバイク」の世界的流行の火付け役となったメッセンジャーたちにスポットを当て、華麗な乗りこなしテクニックをはじめ、メッセンジャーという仕事に誇りを持って生きる彼らの素顔や苦悩を映しだしていく。", "2007-03-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SEARCHERS", "捜索者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1987年４月、ハリウッド100年を記念して選ばれたアメリカ映画ベスト作品の中で、西部劇部門のベスト１に選ばれた不朽の名作。南北戦争の３年後、イーサン・エドワーズは放浪の末に兄の家に帰ってきた。久しぶりの再会を喜ぶのもつかの間、彼の留守中に兄一家がコマンチ族に襲われ虐殺されてしまい、末娘のデビーだけが殺されずに連れ去られていた。イーサンは、インディアンの混血青年マーチンとともにコマンチ追跡の果てしない捜索行に出発する。何年もの間、二人はインディアンの居留地を捜し続け、ついにデビーを発見するが……。ここには、「荒野の決闘」や「黄色いリボン」に描かれたフォード本来の持ち味のヒューマニズムはない。全編を通して浮き彫りにされるのは、主人公のインディアンに対するすさまじい憎悪と飽くなき執念だ。かくしてフォードは新境地を拓き、ウェインも会心の演技を見せた。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風と女と旅鴉", "風と女と旅鴉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "加藤泰監督による股旅時代劇。島帰りの中年ヤクザ・仙太郎とチンピラヤクザ・銀次が、奇妙な友情を感じ合い、銀次の故郷へ戻ってきて巻き起こす騒動を描いている。銀次はその故郷の村で、かつて父母を悲惨な死に方で亡くし、追われるように村を去ったのだが、帰ってきた銀次を迎える村人たちの目は冷たい……。中村錦之助をはじめ出演者全員がノー・メイクの素顔で登場し、全場面がアフレコいっさいなしの同時録音。加藤監督のありのままの自然さを追求しようとする意気込みがうかがえる。仙太郎に扮する三國連太郎が、いつも銀次に村の人たちと仲良くすることを諭す、苦労性の渡世人を渋い味で好演。錦之助も、ひねくれた若者の姿を見事に演じていて、白塗りのない素顔で、いたずらっぽくにやける姿が、妙にかわいらしい。加藤監督得意のロー・アングルのカメラが効果を発揮し、叙情的で心温まる作品になっている。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Shootist", "ラスト・シューティスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "　1901年、ネバダ州カーソンシティ。老ガンマン、ブックスは旧友の医師から末期ガンの宣告を受ける。その医師の勧めで、ブックスはロジャース夫人の宿に身を寄せることに。夫人の息子ギロムはブックスがかつて30人も撃ち殺した伝説的なガンマンであることを見抜く。やがてブックスは最後の対決に臨むことに……。79年にガンで亡くなった名優ジョン・ウェインがその3年前に撮影した遺作。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Hired a Contract Killer", "コントラクト・キラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "フィンランドのアキ・カウリスマキ監督によるサスペンスコメディ。ロンドンの水道局で働くフランス人レオ。突然解雇された彼は自殺を図ろうとするが死にきれず、新聞広告で知った殺し屋コントラクト・キラーに自分の殺害を依頼する。しかしその夜、彼はカフェで出会った花売り娘マーガレットに恋をしてしまい……。主演はフランスの名優ジャン＝ピエール・レオ。元ザ・クラッシュのジョー・ストラマーによるバーでの演奏シーンも必見。", "1990-01-01", "20,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("婚前特急", "婚前特急", "", "", "http://image.eiga.k-img.com/images/movie/55679/315x210.jpg?1777", 107, "「蛇にピアス」以来3年ぶりの主演を務める吉高由里子が、恋に奔放な女性を演じるラブコメディ。新鋭・前田弘二監督がメガホンをとり、共演に加瀬亮、青木崇高ら人気俳優がそろう。24歳のOLチエは、時間を有効活用しようと5人の男性に“5マタ”をかけていた。しかし、親友（杏）の結婚に触発され、5人の恋人の査定を開始すると、本当に自分を愛してくれている人が誰なのかわからなくなっていく。", "2011-04-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A DEMON IN MY VIEW", "わが目の悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "孤独な日々を送るアーサー・ジョンソンのアパートの隣室にアントニー・ジョンソンというよく似た名前の男が引っ越して来たことから、何かが狂い始める。ある日、アーサーは間違えて、アントニー宛ての手紙を開封してしまうのだが、このことがアーサーの隠れていた狂気を引き出してしまう。次第に肥大していく彼の狂気は、やがて犯罪へと向かうのだった。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄海", "哀しき獣", "", "", "http://image.eiga.k-img.com/images/movie/57219/315x210.jpg?1908", 140, "「チェイサー」のナ・ホンジン監督とハ・ジョンウが再びタッグを組んだクライムサスペンス。罠にはめられ追われる身となった男が、闇に潜む真実を暴き出していく姿を描く。中国・延辺朝鮮族自治州でタクシー運転手を営むグナムは、借金返済のために賭博に手を出すが、失敗して逃げ場を失う。殺人請負人のミョンに韓国へ行ってある人物を殺せば借金を帳消しにすると言われたグナムは、韓国へ出稼ぎにいったまま音信の途絶えた妻に会うためにもミョンの提案を受諾。黄海を渡り韓国へたどり着くが、そこで罠にはめられ、警察や黒幕すべてから追われる身となってしまう。", "2012-01-07", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRZESLUCHANIE", "尋問", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "1951年、スターリン主義下のポーランド。キャバレーで歌うアントニーナは、まったく理由のわからないまま秘密警察に拘留される。当局の狙いは、アントニーナにニセの証言をさせ、彼女の知り合いの活動家を、政治的な謀略に陥れることにあった。その日から、彼女の真実を守るための戦いが始まった。過酷な運命に敢然と立ち向かったヒロインの壮絶なドラマで、製作当時は上映禁止、９年後のカンヌでようやく桧舞台に登場した。", "1982-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらば愛しき人よ", "さらば愛しき人よ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "原田眞人がヤクザ映画をハードボイルド・タッチに味付け。郷ひろみがクールでありながら優しさを捨てきれない殺し屋、修史を演じている。物語は、修史が幼なじみの女と再会し、甘い夢を見るが、ヤクザ渡世のしがらみから彼女と決別するというもの。チョイ役ながら、内藤陳の怪演が光る。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　DI＾NER　DE　CONS／THE　DINNER　GAME", "奇人たちの晩餐会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ヨーロッパで大ヒットしたブラック・コメディ。編集者ピエールと友人たちは、それぞれに“奇人”を連れてきて笑い者にするというパーティーが週に一度の楽しみ。今回はまれに見る変わり者、税務署勤務のフランソワを同伴することにしたのだが、腰を痛めて出席できなくなってしまう。フランソワと二人きりでかみ合わない会話に疲れるピエールは、さらに彼の勘違いのせいで妻や愛人、税務監査のトラブルに巻き込まれていく。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狐の呉れた赤ん坊", "狐の呉れた赤ん坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "戦後時代劇の幕開けを飾った作品。GHQは日本の時代劇に顕著な“封建道徳”の表現を抑えるよう指示、その結果、このような明るい人情喜劇ができあがった。大井川の川越え人足・張子の寅は乱暴者だがお人好し。街道筋に狐が出ると聞き、退治してやろうと出かけて行くと、そこには赤ん坊が寝ている。寅はこの赤ん坊を育てるハメになるが、実はこの捨て子、ある大名の妾の子であった……。子育てと別れのストーリーはチャップリンの「キッド」を思わせる。主演の阪妻の戦後第１作。1971年に勝新太郎主演のリメイク版がつくられた。当時、脂ののりきっていた丸根賛太郎の傑作。DVDは「阪東妻三郎傑作選　DVD-BOX」に収録。", "1945-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コント55号 宇宙大冒険", "コント55号 宇宙大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "当時の地球と人類へのアイロニーが込められた、東宝のコメディ・シリーズ第4作。SFテイストにあふれた設定と、宇宙に飛び出したコント55号による唯一無二のアクションが爆笑を誘う。坂本竜馬ならぬ坂本桂馬に扮する欽ちゃんと、新撰組の一員に扮する二郎。彼らは、ひいきの芸者と共に宇宙人にさらわれて異星へ。平和過ぎるその星では、好戦的な地球人の刺激を求めていたのだ！", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("命", "命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "芥川賞作家・柳美里のベストセラー小説、『命』『魂』『生』『声』から成る『命』シリーズの映画化。生と死をじっくりと見つめながら、元恋人同士の絆を描く、ほぼ実話をベースにしたラブ・ストーリー。1999年初夏、妻帯者の子供を妊娠した作家・柳は産むべきかどうかを迷っていた。アドバイスを求めて元恋人で劇団主催者・東のもとを訪れた柳は、東が救いようのないガンに冒されていることを知る。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.ROBINSON CRUSOE", "ロビンソン・クルーソー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "サイレント時代からアクション映画の大スターとして大活躍したD・フェアバンクスが、現代のロビンソン・クルーソーに扮した冒険コメディ。プレイボーイのダグラスは船旅中に無人島を発見。ロビンソン・クルーソー気取りで、愛犬を連れて一人きりの生活を楽しむことにしたが……。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　PEUPLE　MIGRATEUR", "WATARIDORI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "世界20ヵ国以上で３年に及ぶ撮影を敢行したドキュメンタリー大作。100種類以上の“渡り鳥”が様々な経路で北極を目指し、また元の場所へ戻っていく旅の模様を克明に映し出す。大自然をバックに群れを成して飛ぶ鳥の美しさ、多くの困難を乗り越えて目的を達成しようとする姿が感動を呼ぶ。総監督を務めたのは俳優としても活躍するフランスのプロデューサー、ジャック・ペラン。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・社長忍法帖", "続・社長忍法帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "北海道で東西組との戦いに勝った岩戸建設は、業界Aクラス入り目指してバク進中。北海道のバー・まりものマダムが上京して岩戸社長に店を持たせてくれとねだる。泣き落とされた社長は徹夜の設計図づくり。でも奥さんに知られて……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЧАЙКОВСКИЙ／TCHAIKOVSKY", "チャイコフスキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 154, "世界的作曲家チャイコフスキーの生涯を、彼の人間的側面から描いた作品。離婚、富豪の未亡人メックとの秘めたる交際など、名声の陰に隠された彼の実生活と精神的苦悩にスポットを当てている。サン・セバスチャン映画祭の主演男優賞を受賞した、スモクトゥノフスキーの熱演が見もの。", "1970-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("river", "river", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "北海道テレビ放送で大人気を博したバラエティー番組『水曜どうでしょう』の企画・構成・出演を担当していた鈴井貴之の長編第２作目。暗い過去に悩む４人の同級生が、記憶を操作できる薬品の強奪計画に参加するサスペンス。登場人物の人生を交互にバランスよく描きつつ、時間軸を巧みに前後させながら一つのドラマとして結びつける鈴井監督のアイデアが冴え渡る。取り逃がした通り魔に人質を殺され、自責の念に苦しむ警察官の佐々木は、ある時小学校の同窓会に出席し、旧友と再会する。過去を忘れてしまいたいと話していると、見知らぬ男が現れて、記憶を操作できる薬品があると言い出す……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Straw Dogs", "わらの犬（2011）", "", "", "http://ecx.images-amazon.com/images/I/51ImAn2iKRL._SL210_.jpg", 0, "サム・ペキンパーの名作「わらの犬」（1971）を「ザ・コンテンダー」のロッド・ルーリー監督がリメイクしたサスペンス。舞台をオリジナルのイギリスからアメリカ南部に、主人公の設定を数学者からハリウッド映画の脚本家に置き換え、田舎町に引越してきた夫婦が地元民から嫌がらせを受け、最後に怒りを爆発させる姿を描く。主演はジェームズ・マーズデンとケイト・ボズワース。", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1997古惑仔戰無不勝／YOUNG　AND　DANGEROUS４", "新・欲望の街II　'97古惑仔最終章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "暗黒街の熾烈な抗争に揉まれながら青春を送る、チンピラたちの生き様を描く香港発大ヒット・シリーズ第４作。裏社会を暗躍する結社、洪興社は、対立する東星社にボスを殺されたため、組織を再編成することに。新たなボスの候補、サンカイとサンファンは後の選挙に勝つべく業績争いを始める。だがサンカイは、恋人を殺されヤクザ生活に疑問を抱き始めたナンと衝突。その間サンファンは、ひそかに東星社の後ろ盾を得て、着実にボスとしての力量をアピールする。一方、自ら主催したパーティーも散々な結果に終わり雲行きの怪しいサンカイ。この不穏な空気を利用して、東星社は洪興社のチンピラを殺害、勢力拡大の下地作りを。しかし、サンファンの背後に東星社の存在を察知した洪興社の古惑仔たちは、陰謀をたくらむ一味に挑みかかる。「天使の涙」のK・モクとM・リーの共演も見どころ。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふりむけば愛", "ふりむけば愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "百恵＝友和コンビの出演８作目で、初のオリジナル作品。単調な生活から抜け出したいと願う一人の女の愛、裏切り、絶望、変身を描き、女の自立をうたっている。百恵＝友和コンビ初のベッド・シーンが当時のマスコミで大いに話題になった。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Breaking Away", "ヤング・ゼネレーション", "", "", "http://ecx.images-amazon.com/images/I/51Kmz3hHyYL._SL210_.jpg", 101, "若者はみな大学へ、というインディアナ州ブルーミントンの町で、自転車レースに青春をたくす高卒４人組の愛と友情、父と子の絆を描くさわやかな青春映画。ちょっぴりユーモラスで温かいイエーツ・タッチが快調。アメリカで大ヒット、1980年にはTVシリーズも製作された。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIENS CHEZ MOI，J’HABITE CHEZ COPINE", "恋の邪魔者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ドジな失敗のせいで、勤めていたガソリンスタンドをクビになったギイは、収入がなくなり、下宿からも追い出されてしまう。見かねた友人のダニエルは、恋人フランソワーズと一緒に住み始めた部屋の一つをギイに提供する。それ以来、ギイは手当たり次第に女性を家に連れ込んでいた。ある日、ギイとダニエルは街中で、ギイが連れ込んだ女で、ダニエルがひそかに目をつけていた女性カトリーヌと出会う。すっかり意気投合したダニエルとカトリーヌはデートの約束を交わす。そして、ギイと結託して、ダニエルはまんまとカトリーヌの家で一夜を過ごすが……。人気監督P・ルコントが「仕立て屋の恋」などでも組んでいるM・ブラン主演で撮り上げた初期作。長い間未公開だったが、1996年に初めて劇場公開された。", "1980-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金田一少年の事件簿　上海魚人伝説", "金田一少年の事件簿　上海魚人伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "同名ベストセラー・マンガをもとにした、人気TVシリーズの完結編。名探偵、金田一耕助の孫、一（はじめ）が、上海で起こった怪事件に挑む。美雪の元に、文通相手の中国雑技団の少女レイリーから、助けを求める手紙が届く。団長である父親が殺され、兄に容疑がかけられたのだ。さっそく上海に飛んだはじめと美雪は捜査を開始。事件現場の舞台裏は密室状態、団員にはアリバイがあり、死体に魚人の仮面が被せられていたという。やがて、はじめは雑技団内の複雑な人間関係、過去の忌まわしい事件とのかかわりを疑うが、その矢先、事件当時と同じ『魚人遊戯』を上演中、大勢の観客が見守る中で第２の殺人事件が発生する……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リバイバル・ブルース", "リバイバル・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "下半身切断の少年がたくましく生きる姿を描いた「ケニー」や「Keiko」などで知られる、カナダのクロード・ガニオン監督が手掛けた友情ドラマ。ブルース・バンドを組んでいた男女３人が26年ぶりに再会。人生の岐路にいた３者が自らを再生させていく。監督が出演者にセリフから演技まですべてアドリブを指定。それに見事に応える桃井かおり、奥田瑛二、内藤剛志の演技が見もの。1970年代に１曲のブルースを残し、解散したバンド・メンバーたちは、別々の人生を歩んでいた。26年後、メンバーの一人、健は沖縄に住む洋介を訪ね、バンド復活を持ちかける。しかし、洋介は末期ガンに冒されていた。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子育てごっこ", "子育てごっこ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "自由主義者の作家のもとで学校教育も受けずわがままいっぱいに育った娘を、岩手県の山の中の小学校分校教師夫妻が預かり、自然のなかで人間らしさをしつけていく様子を描いたドラマ。同名の直木賞受賞作をベースに、素朴な山村の人情を、今井監督が手堅く描き出している。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひめゆりの塔", "ひめゆりの塔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "太平洋戦争で国内唯一の戦場となった沖縄を舞台に、勤労奉仕で最前線へと駆り出された女学生たちを描いた今井正監督の名作。悲惨な状況を客観的な視点で捉えた演出スタイルが、反戦平和の主張を痛烈に訴え、内容の重さにかかわらず大ヒットとなった。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAST LIGHT", "要塞監獄 プリズナー107", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "犯罪人たちの収容所、アルカトラズ刑務所。なかでも、一般監房とは区別された、通称“デス・ロー”と呼ばれる棟には死を待つ凶悪犯ばかりが収容されていた。その中に一人、デンバー・ベイリスという、反抗的な態度と凶悪さで監房中に名をとどろかせた男がいた。刑務所の主任は彼を目の敵のように扱うのだが……。K・サザーランドが監督・主演に挑戦した迫真のバイオレンス・ドラマ。クリント・イーストウッド主演の「アルカトラズからの脱出」のオリジナル・セットがそのまま使われているのにも注目したい。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浪華悲歌", "浪華悲歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "溝口健二、戦前の代表的傑作。社会的な弱者である女性の精神の自立を描いて、従来のメロドラマ的女性映画のカラを打ち破った画期的な作品。大阪の薬種問屋に電話交換手として勤めるアヤ子は、会社の金を横領した父親のために、問屋の主人の妾となって借金を肩代りしてもらう。しかし、いつしか主人の妻が知ることになり追い出されてしまった。おまけに兄の学費稼ぎのため、好色な株屋相手につつもたせを仕組むことになる。逮捕され、恋人にも裏切られたアヤ子は家に帰るが、誰も温かく迎えようとしない……。全体に暗いトーンの中でロングショットが多用され、その中に浮かび上がる山田五十鈴の美しい横顔が印象的。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH ART", "殺しのアーティスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "リオで親しくしていたコールガールが殺され、自ら捜査に乗り出したアメリカ人の写真家マンドレーク。マフィア絡みのこの事件に身の危険を感じた彼は、ナイフの使い手から護身と攻撃の術を教わり、組織に挑むが……。P・コヨーテが、ナイフの魅力に魅せられ、危険な変貌を遂げる男を好演。", "1991-01-01", "1,17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Rebound", "理想の彼氏", "", "", "http://image.eiga.k-img.com/images/movie/54689/315x210.jpg?2279", 95, "オスカー女優キャサリン・ゼタ＝ジョーンズ主演のラブストーリー。主婦として平穏に暮らしていた40歳のサンディは、夫の長年にわたる浮気を知って離婚を決意する。2人の子どもを連れてニューヨークへ引っ越し、人生の再スタートを切った彼女は、あるときアラムという24歳の青年に出会う。サンディは彼をベビーシッターとして雇うことになり、2人は徐々に距離を縮めていく……。共演は「ナショナル・トレジャー」のジャスティン・バーサ。", "2009-11-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽炎", "陽炎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「226」の五社＝奥山コンビが再び組んで放つ任侠アクション。昭和３年、熊本の花街。料亭八雲にかつてそこの娘だった女胴師おりんが帰って来るが、八雲も今や宿敵岩船一家に乗っ取られ、色と欲に満ちた場所になっていた。そして、おりんは岩船一家を相手に大立ち回りを繰り広げるが……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("流れ者図鑑", "流れ者図鑑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「由美香」に続く平野勝之のビデオ・ドキュメンタリー作品。再び愛人関係にある女性を伴って決行した北海道自転車旅行の模様をカメラに収めている。“私小説”ともいうべき独自の映像文体は健在。「由美香」との違いは、相手役がカメラに不慣れで女優としては素人に近い自主映画作家であるという点で、そのため先の読めない展開が前作よりスリリングな効果を挙げている。平野は映像制作の共犯者として彼女を選んでいるにもかかわらず、相手は自己の発露に懸命すぎるあまり心と体の言行不一致にもがき苦しむばかり。映像に対して無防備すぎるヒロインの姿勢が、現実と虚構の境界線に一種の極限状況を派生させている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAGED FURY", "プリズン・エンジェル —女囚たちの夜—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "女優を夢見てハリウッドにやって来た女が罠にはめられ女性刑務所送りに。だが服役することになった刑務所は地獄のような所で、彼女はレズの所長や殺しをなんとも思わない看守長から、奴隷同然の扱いを受けて……。B級作品御用達“女囚残酷もの”映画。エロチシズムにアクションの要素をプラス。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("激安王 通天の角", "激安王 通天の角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "バブル崩壊後の大阪で、急激な成長を続けるディスカウント業界。しかしその背後には、現金問屋、バッタ屋、並行輸入業者などがしのぎを削る価格競争が勃発していた。激安戦争に敗れて失意のうちに死んだ父の仇を討つため、岩下春男は持ち前の度胸と気っぷで流通革命に挑む。同名ヒット漫画の映画化で、「月はどっちに出ている」の製作・脚本コンビが再び顔を合わせ、崔組出身の祭主監督が長編デビューを飾った注目作。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REGARDE LES HOMMES TOMBER", "天使が隣で眠る夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "しがない中年賭博師のマルクスは、ヒッチハイクの途中でジョニーという青年と出会う。ジョニーの純粋さに安らぎを感じ、次第に心を開いていくマルクス。ある日、賭博で大損をしたマルクスは組織に多大な借りを作り、借金取り立ての仕事をさせられるようになる。ついには殺しの仕事も引き受けてしまう二人。一方、人生の目的を見失っていた中年セールスマンのシモンは、若い刑事のミッキーと知り合う。年の差にもかかわらず、不思議な親近感を覚えるシモン。しかしミッキーはある事件の捜査中、撃たれて植物人間になってしまう。刑事を撃った二人組と、彼らを追い詰める刑事の友人。それぞれの男たちの二つの物語が複雑に絡み合い、意外な結末を迎える。「バルジョーで行こう！」などの脚本家J・オーディアールの監督デビュー作。1995年セザール賞新人監督作品賞、新人男優賞（M・カソヴィッツ)、編集賞の３部門を受賞した。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paranormal Activity 3", "パラノーマル・アクティビティ3", "", "", "http://image.eiga.k-img.com/images/movie/57039/315x210.jpg?4348", 84, "製作費わずか1万5000ドルの低予算ながらも、全米興行収入が1億ドルを超える大ヒットとなったホラー「パラノーマル・アクティビティ」（2009）のシリーズ第3作。1作目から18年前の過去に舞台を移し、ある平凡な一家を襲う怪奇現象を描く。1988年9月24日、ある家庭で姉妹と思われる2人の少女が部屋を暗くし、鏡の前でホームビデオを回していた。しかし、彼女たちのカメラには不可解な映像が次々と映し出されていき……。", "2011-11-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Star Wars: The Clone Wars", "スター・ウォーズ　クローン・ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/53689/315x210.jpg?4064", 98, "「スター・ウォーズ」シリーズの新3部作「エピソード2／クローンの攻撃」と「エピソード3／シスの復讐」の間に位置する“クローン戦争”を描いた長編CGアニメーション。ドゥークー伯爵率いる分離主義勢力とジェダイを擁する共和国との間に勃発したクローン戦争が激化する中、ジェダイのアナキン・スカイウォーカーは彼のパダワンであるアソーカと共に重要な任務へと向かう。やがて彼らは犯罪組織の首領ジャバ・ザ・ハットに出会い……。", "2008-08-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE JUROR", "陪審員", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "女流彫刻家のアニーは11歳の息子と暮らすシングルマザー。ある日、彼女に陪審員としての出頭要請がかかる。それは、マフィアのドンとその孫が殺害された事件で、ファミリーのボス、ボファーノが殺人の罪で裁かれる裁判だった。市民の義務をごく軽い気持ちで全うしようとしたアニーに謎の男が近づき、裁判で無罪を主張しなければ子供の命はないと脅迫する。男は“ザ・ティーチャー”と呼ばれるシンジケートのヒットマンで、ボファーノ・ファミリーの意を受けて裁判を無罪に持ち込もうと暗躍していた。私生活のすべてを監視されてしまったアニーは、有罪に傾きかけた他の陪審員の心証を覆していく。それでもザ・ティーチャーの脅迫は止らず、アニーの親友を殺してしまう。怒りに燃えるアニーは、命を懸けて反撃に出るのだったが……。D・ムーアが母の強さと弱さを凛とした美しさで演じたサスペンス・スリラー。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THOMAS L’IMPOSTEUR", "山師トマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "第一次大戦下のパリ。負傷兵の看病に没頭するド・ボルム侯爵夫人の前に、陸軍学校の青年トムが現れた……。戦乱のパリを舞台に美貌の詐欺師トマの虚飾に満ちた青春と死を描いたシリアス・ドラマ。コクトーが自らの原作を脚本化し、フランス映画界の鬼才G・フランジュが演出。", "1965-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サラリーマン一心太助", "サラリーマン一心太助", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "一心太助の23代目にあたる石井太助が活躍するサラリーマンもの。太助が勤める葵食品の社長、光男は新しもの好きで、大久保彦左衛門の子孫である大久保営業課長は苦々しく思っていた。だが光男はそんな反対を押し切って電子計算機を導入。おかげで営業部はセールス・ノルマに追われて大わらわに。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Falling Down", "フォーリング・ダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "渋滞するロサンゼルスの高速道路に車を乗り捨てた男がいた。近くの商店で電話をかけるための両替を拒否された男は怒りを爆発させ、店内を破壊。彼はやがて狂気をエスカレートさせ、街中を震え上がらせる凶悪犯へと変貌していく……。現代の都市生活者に激しい警鐘を鳴らした異色バイオレンス・アクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("T−FORCE", "ターミナルフォース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "2007年、激増する凶悪犯罪に対抗するため、武装サイボーグ軍団“ターミナル・フォース”が開発された。そんな中で特捜刑事ジャックは、一般市民を犠牲にしてでも目的遂行のため手段を選ばない彼らの犯罪処理能力に危機感を抱く。人類に反逆し暴走するサイボーグ軍団と特捜刑事との新兵器を駆使した戦いを描いた近未来SFハード・アクション。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Quest for Camelot", "キャメロット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "英国のアーサー王伝説をもとに、一人の少女の愛と冒険の物語を描いたアニメ。王国キャメロットに住む少女ケイリーの望みは父のような騎士になることだった。だが、その父が裏切り者の騎士ルーバーによって殺され、アーサー王の剣が盗まれるという事件が起きる。ケイリーは剣を取り戻すため武器を取り、道中で出会った盲目の騎士ギャレットとともに、ルーバーに戦いを挑むのだった……。悪魔の力を身につけて国を奪おうとするルーバーの声をG・オールドマン、アーサー王の声をP・ブロスナンが演じている。ビデオ、DVDタイトルは「魔法の剣　キャメロット」。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Che: Part Two", "チェ 39歳　別れの手紙", "", "", "http://image.eiga.k-img.com/images/movie/54029/315x210.jpg?3917", 133, "「トラフィック」でオスカー監督賞＆助演男優賞を受賞したスティーブン・ソダーバーグとベニチオ・デル・トロが、キューバ革命を率いた指導者エルネスト・チェ・ゲバラの半生を描いた2部作の第2部。キューバでの革命を成功させたゲバラだったが、地位や市民権を全て放棄し、再び革命の旅に乗り出す。1966年、独裁政権下にあるボリビアに入国したゲバラは、ゲリラとして政府軍と戦うが……。", "2009-01-31", "9,2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JINGLE ALL THE WAY", "ジングル・オール・ザ・ウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "家庭より仕事第一の運動器具会社の社長ハワード・ラングストンは、クリスマス・イヴの前日、８歳になる息子ジェイミーの空手帯授与式に出席する約束を守れなかった。ハワードは汚名返上にテレビヒーロー、ターボマン人形をプレゼントすることを約束するが、イヴの当日まで忘れてしまう。あわてて買いに行くが、人形は大変な人気でどこの店でも完売状態。ハワードは息子との約束は破れないと、人形を求めて走り回るのだが、同じく息子のために人形を探すマイロンが現れ、その結果、思わぬハプニングに遭遇する。人形は見つからないまま、ジェイミーと一緒に見るはずだったクリスマスのパレードが、始まってしまう。そして、ハワードはパレードのターボマン役のスタントマンと間違えられて、パレードに参加してしまうのだった。プレゼント探しに奔走する父親の姿を描いたハートフル・コメディ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ココロとカラダ", "ココロとカラダ", "", "", "http://ecx.images-amazon.com/images/I/212X93M0GSL._SL210_.jpg", 83, "廣木隆一監督が企画した“ラブコレクション”プロジェクトの一作。モスクワ映画祭で市川実日子を主演女優賞に導いた「blue」の安藤尋監督が、微妙な女性心理を巧みに描き出す。学生時代、知美はレイプされかけた友人の恵子を救った。その後、恵子は上京し売春で生活している。そんな時二人は再会。やがて二人は奇妙な友情で結ばれていく……。", "2004-12-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PICNIC AT HANGING ROCK", "ピクニック・アット・ハンギングロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "1900年２月14日、ピクニックに出かけた名門女子学園の生徒たちのうち数名が忽然と姿を消してしまった……。オーストラリアで実際に起こった謎の女生徒失踪事件を「いまを生きる」のP・ウィアーが、華麗な色彩美にのせ、神秘的に描いていく。", "1975-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("翔んだカップル", "翔んだカップル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "この作品の後、若者から大きな支持を獲得した相米慎二の監督デビュー作。『週刊少年マガジン』に連載されていた同名マンガの映画化である。公開当時は、アニメとの併映であったため大きな話題にならなかったが、一部の観客と批評家から相米監督の長回し多用による演出が絶賛を浴びた。ひょんなことで同じ家に住むことになった高校一年生の男女と、二人を取り巻く友人たちの心の揺れが描かれている。田代勇介は弁護士を目指し、九州から東京の名門校に入学した高校生。叔父の家に住むことになった勇介は間借人を頼んだが不動産屋の手違いで、美少女・山葉圭がやって来る。１ヵ月の条件で一緒に住むようになる二人。お互い十分意識しているのに好きだと言えない。そんな二人にクラス１、２の成績を争う杉村秋美と中山わたるが絡んでくる。杉村は勇介に、中山は圭に引かれて……。４人それぞれの成長と別れを、一抹の痛みとともに克明に浮かび上がらせた相米演出が冴え、それを受けた４人の好演が光る一編。のちに全長版も公開された。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Prestige", "プレステージ", "", "", "http://image.eiga.k-img.com/images/movie/1909/315x210.jpg?2701", 130, "「メメント」「バットマン・ビギンズ」のクリストファー・ノーラン監督が、クリストファー・プリーストの小説「奇術師」を映画化。19世紀末のロンドンを舞台に、ライバル関係にある2人の天才マジシャンが、お互いの意地とプライドを賭けて戦いを繰り広げる。主演のマジシャン2人にはヒュー・ジャックマンとクリスチャン・ベール。マジック監修はデビッド・カッパーフィールドが担当。", "2007-06-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DREAM DEMON", "ドリームデーモン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "女優ヴァネッサ・レッドグレーヴの姪、ジェマの映画デビュー作。この映画でパリ国際ファンタスティックSF映画祭最優秀女優賞を受賞している。美しき社交界の華、ダイアナ。結婚をひかえた彼女は、自宅の地下室で不気味な人形を発見、以来悪夢に悩まされる。その彼女の前にジェレミーという娘が現れて……。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パコと魔法の絵本", "パコと魔法の絵本", "", "", "http://image.eiga.k-img.com/images/movie/53309/315x210.jpg?3501", 105, "後藤ひろひと原作の舞台「MIDSUMMER CAROL　ガマ王子vsザリガニ魔人」を、「下妻物語」「嫌われ松子の一生」の中島哲也監督が役所広司、妻夫木聡ら豪華キャストで映画化。奇妙な人ばかりが集まるとある病院に、偏屈な大富豪・大貫がいた。嫌われ者の大貫はある日、交通事故で両親を失い、後遺症で1日しか記憶が持たなくなってしまった少女パコと出会う。パコとの出会いで自分の人生を見つめ直す大貫は、彼女にとある提案をする。", "2008-09-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はるか、ノスタルジィ", "はるか、ノスタルジィ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "青春時代を過ごした北海道、小樽を10年振りに訪れた人気少女小説家の綾瀬は、そこで自分のファンだという少女、はるかと出会う。彼女の案内で、現在の小樽を巡り始める綾瀬。そんな二人の前に、やがて高校生時代の綾瀬が姿を現したことから、彼は忌しい昔の記憶を呼び覚ます。３人で過去の記憶をたどるうち、綾瀬は次第に純真な心を取り戻して……。忘れていた自分の過去と対峙することになった中年男の心の葛藤を、幻想的に描いた好編。「転校生」「さびしんぼう」を手掛けた大林宣彦監督、山中恒原作のコンビによる詩情豊かな傑作ファンタジーだ。「ふたり」で大林と組んだ石田ひかりが、さわやかな演技で二役をこなしている。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裸の大将放浪記 —山下清物語—", "裸の大将放浪記 —山下清物語—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "精薄の天才画家・山下清の生涯を、新たに編集された清の日記をもとに描いた作品。乱暴な子として施設に入れられ、そこで絵の才能を開花させた話や、気ままな生活に憧れて旅に出ていくエピソードなど、興味深い内容が淡々とした映像で綴られていく。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜宴", "ウェディング・バンケット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「推手」、「恋人たちの食卓」とあわせて“父親3部作”といわれるアン・リー監督のヒューマン・ラブ・ストーリー。アメリカに帰化した台湾出身のウェイトンはビジネスでも成功を収め、ゲイの恋人サイモンと幸せに暮らしている。しかし彼がゲイだとは思いもよらぬ両親が台湾からやって来ることになった。ウェイトンは両親を安心させるために、グリーンカード目当ての中国人女性と偽装結婚することになるが……。主人公たちの奇妙な三角関係のなかに、中国の伝統的な考え方とゲイ、国籍問題、親子の絆などを見事に表現した斬新なタッチの人間ドラマ。", "1993-01-01", "53,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALIEN NATION", "エイリアン・ネイション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "20世紀末、ロサンゼルスでは、偶然の事故で地球にやって来た多数のエイリアンが居住区を作り、人間と共存していた。サイクス刑事はそこのエイリアン刑事フランシスコと組んで、エリア内で起きた殺人事件を捜査することになった。人種(？)の違いのため初めは反目し合っていた二人だったが、捜査が進むにつれ、二人の間にはいつしか友情が芽ばえてくる。やがて事件の背後に、麻薬製造の黒幕ハーコートの影が浮かび上がってくる。二人は力を合わせ、ハーコートを追い詰めていく。これまで白人と黒人といったコンビで見せていたスタイルを、地球人とエイリアンという形に置き換えた視点がユニークな作品。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("世界は彼女のためにある", "世界は彼女のためにある", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "新人・保坂大輔監督が映画美学校の卒業制作として手がけた作品ながら、ゆうばり国際ファンタスティック映画祭のオフシアター部門で審査員特別賞を受賞した異色のSF青春ドラマ。高校生の坂ノ下は恋人のカヲリがいじめにあっていることを知りながらも、カヲリを助けることができずにいた。そんなある日、坂ノ下は謎の男・タカナシから、自分が国家のためにつくられた戦闘用のクローン人間だという衝撃の事実を聞かされる。", "2005-12-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE OF HEARTS", "スリー・オブ・ハーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "W・ボールドウィンらがフレッシュな演技を見せるモダンな恋愛ドラマ。同棲相手のエレンに去られ、悲しみに暮れるレズビアンのコニー。思い余った彼女は出張ホストのジョーにエレンを誘惑して捨てるように頼む。そうすれば彼女は戻って来ると思ったのだ。しかし、ジョーは本気でエレンに夢中になってしまうのだった……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AWAY ALL BOATS", "全艦発進せよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "第二次大戦の太平洋における上陸作戦を扱ったベストセラー小説の映画化。上陸用母艦ベリンダ号の艦長に着任した歴戦の勇士ホウクス大佐。だが副艦長以下、乗組員はほとんどが未経験者で、次々と失敗を繰り返す。そんな中で戦線は沖縄へ移り、ベリンダ号は日本軍の特攻隊の激しい波状攻撃を受けるが……。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOREVER FEVER", "フォーエバー・フィーバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "シンガポールから飛び出したイキのいい青春コメディ。1977年、世界を席巻するディスコ・ブームがシンガポールにも飛び火。ブルース・リーとバイクを愛する若者ホックは、つきあいで観た「サタデー・ナイト・フィーバー」にハマってしまう。かくして、彼はバイクを買うための賞金目当てでダンス大会を目指し、幼なじみのメイとダンス教室に通うが、ライバルに嫌がらせを仕掛けられ……。職場でも家庭でも厄介者扱いされ、ダンスにはけ口を見いだす主人公像は「サタデー…」を踏襲しているが、タッチは陽気でコミカル。銀幕のトラヴォルタ（もちろんニセモノ）に声をかけられたり、登場人物の意外な秘密が明かされるなどの、突拍子もない展開が笑いを誘う。", "1998-01-01", "54", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("走らなあかん 夜明けまで", "走らなあかん 夜明けまで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "東京者のサラリーマンが大阪という異文化に遭遇しながら、盗まれた鞄を巡ってヤクザと対立する姿を描いたハードボイルド。出張で初めて大阪にやって来た食品会社のサラリーマン・坂田勇吉は、通天閣で企業秘密の新製品が入ったアタッシュケースを何者かに盗まれ大あわて。翌日の会議までに取り戻さなければならず、土地勘のない生粋の東京者の坂田はひたすら大阪を奔走する。やがてケースを盗んだのはヤクザだと分かり、さらに唯一の頼みの綱だったホステスの真弓まで人質にとられてしまうのだった……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paris Trout", "パリス・トラウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "黒人差別主義者トラウトは、小さないざこざから黒人青年を逆恨みし、彼の幼い娘を射殺する。裁判にかけられ、有罪濃厚となったトラウトはやがて正気を失い、攻撃のホコ先を家族に向け始めた……。D・ホッパーの狂気の演技に注目。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カタクリ家の幸福", "カタクリ家の幸福", "", "", "http://image.eiga.k-img.com/images/movie/1469/315x210.jpg?4288", 113, "「殺し屋1」の三池崇史監督が、韓国映画「クワイエット・ファミリー」を豪華キャスト共演で、ミュージカル映画にリメイク。丹波哲朗、忌野清志郎なと個性派俳優も出演。脱サラして人里離れた山中で、ペンション経営を始めるカタクリ家の6人。しかし、やっと来た客が、原因不明の死を遂げてしまう。それでなくても経営難に悩む一家は、死体を隠し事件を隠蔽しようとするが、そのせいで次から次へと困った事態が生じてしまう。", "2002-02-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ENEMY BELOW", "眼下の敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "南大西洋を舞台に展開する、アメリカ駆逐艦とドイツ潜水艦の激しい攻防戦。まず米側が大量の爆雷を落とし、独軍を悩まして先手を打つ。これに対抗して独軍は深く潜水して脱出の機会をうかがっていた。しばらくして独軍は反撃に出る。米軍駆逐艦の胴腹に魚雷をぶつけたのだ。米軍は絶体絶命の危機に。しかし艦長のとっさの判断で駆逐艦を独軍の潜水艦に体当たりさせ、とも倒れとなる。R・ミッチャムとC・ユルゲンスのシーソー合戦が一種のゲーム感覚で描かれており、98分間を一気にみせる。特筆すべきは米・独どちらの側の描写もフェアである点で、気持ちのいい展開となっている。", "1957-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KLASSENVERHALTNISSE", "階級関係　−カフカ「アメリカ」より−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "F・カフカの文学『アメリカ』(または『失踪者』とも翻訳される)を原作にして、S・ユイレ監督が映像化した純粋文学映画。純粋というのは、原作のセリフをそのまま本編で使うという野心的試みから名付けられたもの。よって、カフカの文学的世界観が忠実に再現されている。古今東西のカフカ作品を原作にした作品群の中でも、ひと際異彩を放つ傑作。ドイツ人青年カールは、年上の女性を妊娠させてしまい、怒った両親によってアメリカに追放されてしまう。果たして、放浪を続ける彼が行き着く先は……？", "1984-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("世界の終わりという名の雑貨店", "世界の終わりという名の雑貨店", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "嶽本野ばらの小説集『ミシン』に収録されている同名の短編を映像化したナイーブな青春ドラマ。情報誌ライターの雄高は現実とうまく折り合えず、世間の目から逃れるように雑貨店を開く。そこを訪れた内向的で心を開けない少女、胡麻。彼らは交流を重ねるうちに、奇妙な連帯感で結ばれ……。ヒロイン、高橋マリ子のピュアな存在感が光る。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Georgia Rule", "幸せのルールはママが教えてくれた", "", "", "http://ecx.images-amazon.com/images/I/51HuPfnsndL._SL210_.jpg", 110, "「プリティ・ウーマン」のゲイリー・マーシャル監督が、ジェーン・フォンダ、リンジー・ローハン、フェリシティ・ハフマン主演で描くハートフルドラマ。自由奔放で反抗的な娘レイチェルは、母リリーに連れられてアイダホで暮らす祖母ジョージアの家でひと夏を過ごすことに。様々なルールを決めて生活しているジョージアは、レイチェルにもそのルールを守るよう強制する。そんな祖母に反感を覚えつつも、徐々に心を開いていくレイチェルだったが……。", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("テルマエ・ロマエ", "テルマエ・ロマエ", "", "", "http://image.eiga.k-img.com/images/movie/56149/315x210.jpg?4691", 108, "「マンガ大賞2010」「第14回手塚治虫文化賞短編賞」を受賞したヤマザキマリの同名コミックを阿部寛主演で実写映画化。古代ローマ帝国の浴場設計師ルシウスが現代日本にタイムスリップし、日本の風呂文化を学んでいく姿を描くコメディドラマ。生真面目な性格で古き良きローマの風呂文化を重んじる浴場設計師のルシウスは、ふとしたきっかけで現代日本にタイムスリップ。そこで出会った漫画家志望の女性・山崎真実ら「平たい顔族（＝日本人）」の洗練された風呂文化に衝撃を受ける。古代ローマに戻りそのアイデアを用いた斬新な浴場作りで話題となったルシウスは、時の皇帝ハドリアヌスからも絶大な信頼を寄せられるようになるのだが……。映画オリジナルのヒロイン・真実を上戸彩が演じる。監督は「のだめカンタービレ」の武内英樹。", "2012-04-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パイレーツによろしく", "パイレーツによろしく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "自分の好きなものばかりに熱中し、自閉的な生活をともにする３人の男女が、様々な人々との出会いによって変わっていく過程を描いた青春映画。みずみずしい感覚と独特の会話体で、若い層の指示を得た川西蘭の小説が原作。石黒賢、三上博史がさわやかな印象を残す。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COOK，THE THIEF，HIS WIFE ＆ HER LOVER", "コックと泥棒、その妻と愛人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "フランス料理店に３人の客がやって来る。傍若無人で残忍な“泥棒”と美しい“その妻”。そして教養人である彼女の“愛人”。寡黙な“コック”を交えてそれぞれの複雑な思惑が交錯しはじめる……。ブラックなユーモアと独特の映像美で綴るP・グリーナウェイのスキャンダラスな話題作。ゴルチエによる衣装の色彩設計も実に鮮やか。", "1989-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("「分校日記」　イーハトーブの赤い屋根", "「分校日記」　イーハトーブの赤い屋根", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "男先生は村人の火葬や団体保険の集金、そして学校は、地区の売店や電話の取次店さながらの状態という、生徒数わずか21人の山あいの分校を舞台に、都会からやって来た女先生と村人を交えた子供たちとの、型破りでほのぼのとした交流を描く。熊谷勲の第１回監督作。ビデオ発売時タイトルは「イーハトーブの赤い屋根」。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほんとうにあった怖い話・怨霊", "ほんとうにあった怖い話・怨霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "投稿された実話をもとに、「深夜の警備員」「首坂」「葬儀屋が見たもの」「鼓膜移植」「エレベーターの少女」という５編をオムニバス形式で綴るホラー。熊切和嘉監督作品「アンテナ」のメイキングなどを手掛けた新鋭・山本清史監督の劇場デビュー作でもある。身近な出来事の中に起こる怪奇現象だからこその臨場感と真実味は十二分に恐ろしい。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("築地魚河岸三代目2", "築地魚河岸三代目2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents うそ　「テクニカラー」", "桃まつり presents うそ　「テクニカラー」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "", "2010-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WITH HONORS", "きっと忘れない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "エリート大学生とホームレスの男の交流を、コミカルかつせつないタッチで描く青春映画。ハーバード大学の学生モンティが、大学の地下室で出会った初老の浮浪者のサイモン。自由で博識な彼から、モンティは学業以上に大切なものを学んでいくが……。マドンナが歌う主題歌『アイル・リメンバー』も大ヒット。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猫ラーメン大将", "猫ラーメン大将", "", "", "http://image.eiga.k-img.com/images/movie/53699/315x210.jpg?2765", 0, "そにしけんじ原作の4コマ漫画を、「ギララの逆襲／洞爺湖サミット危機一発」の監督・河崎実が映画化。「機動戦士ガンダム」のアムロ・レイや「巨人の星」の星飛雄馬で知られる声優の古谷徹と、「巨人の星」の飛雄馬の父、星一徹役の加藤精三が、猫で再び親子役を演じる。キャットアイドルになるための父のスパルタ教育に嫌気がさして家を飛び出した大将。寿司屋、外科医、タクシー運転手などに挑戦するが、結局どれもうまくいかず……。", "2008-11-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMITYVILLE A NEW GENERATION", "真・悪魔の棲む家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1979年の「悪魔の棲む家」を第１作に、次々に作られてきた“アミティヴィル”ものの１本。とはいえ、今回は家ではなく、悪霊と交信できる鏡によって惨劇が起きる。主人公キイズは、この鏡を手に入れてから、毎夜悪夢にうなされる。鏡を他人に貸すと、その人物は謎の死を遂げる。ホラーの定石通りの展開で、あまり新味はない。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まほろ駅前多田便利軒", "まほろ駅前多田便利軒", "", "", "http://image.eiga.k-img.com/images/movie/55629/315x210.jpg?4813", 123, "「風が強く吹いている」の三浦しをんによる直木賞受賞作を、「ケンタとジュンとカヨちゃんの国」の大森立嗣監督が映画化。主演は瑛太と松田龍平。東京・町田市をモデルとした架空の地方都市“まほろ市”を舞台に、便利屋を営む多田と同級生の仰天が、一筋縄ではいかない顧客たちからの無理難題を痛快に解決する日々を描く。2人は、ある事件にかかわることで自らの抱える忘れられない過去と向き合うことになる。", "2011-04-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIS−MOI OUI…", "世界でいちばん好きな人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "青年医師に思いを寄せる少女の一途な愛を描いたピュアなラブ・ストーリー。無軌道な生活を送る30歳の青年医師ステファンはある夜、家の前で12歳の家出少女エヴァと出会う。彼女を仕方なく泊めたステファンだが、それ以来エヴァの積極的なアプローチにほんろうされっぱなし。実は、脳に障害がある彼女は、６歳の時から病院で優しくしてくれたステファンのことを想っていたのだった。エヴァの病状が悪化する中、ステファンはすべてをなげうって彼女を助けようとするが……。J・H・アングラードが、持ち前の優しさと繊細さを遺憾なく発揮し、少女に振り回されるプレイボーイ役を好演。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SALAAM BOMBAY！", "サラーム・ボンベイ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "インドの女性監督M・ナーイルの長編第１作。大都市ボンベイ。サーカス団から一人取り残された少年クリシュナは、チャイを売りながら、ストリート・チルドレンの仲間入りをする。彼の住む一帯を取りしきるバーバー、その愛人のレーカー、レーカーの娘マンジュなどと親しくなったクリシュナはそこでの生活になじんでいく。彼はある日、娼婦宿で出会ったネパール娘に一目惚れ。だが、その娘が娼婦としてバーバーに鍛えられることになって……。ドキュメンタリー映画出身のナーイル監督らしく、ボンベイの雑踏で暮らす人々の息づかいが聞こえるような演出が見もの。主演の子供たちも、素人ながら存在感のある演技を披露している。", "1988-01-01", "21,4,2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARTHA．．．MARTHA", "マルタ…、マルタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "デビュー作「クリスマスに雪はふるの？」では、７人の子供を抱える母親の奮闘を、第２作「ヴィクトール・小さな恋人」では娼婦と少年の擬似母子の関係を描いたフランスの女性監督サンドリーヌ・ヴェッセが、三たび“家族”を題材に撮り上げたエモーショナルなドラマ。両親から十分な愛情を注いでもらえず母になった女性マルタ。家庭生活を放棄するほど幼少時のつらい思い出に囚われている彼女の苦悩、そして家事や娘リーズの世話を一手に引き受け、彼女を支え続ける幼馴染みの夫レイモンの深い愛情を、厳しくも温かい視線をふんだんに注いで綴っていく。自分でもどうにもならない心の痛みに支配され、精神のバランスを崩していくマルタの姿が痛々しい。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BYE BYE BABY", "バイ・バイ・ベイビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "実業家パオロと病院勤務の女医サンドラは結婚して３年。理想的なカップルのように見えたが、ちょっとしたことが原因で離婚した。それから一年。互いに新しい恋人とめぐりあい暮らしていたが、どしゃぶりの雨の日、二人は再会する。愛が再燃し、いつしか複雑な四角関係になっていき……。元スタローン夫人ブリジット・ニールセンの肢体がまぶしいエロティック・ドラマ。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("200X年・翔", "200X年・翔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "200X年、白鳥座X-1のブラックホール爆発で放出された素粒子が地球人の胎児の細胞に入り込んだ。その結果、遺伝子を操作してしまい、異常に成長の早い子供たちが生まれた。放っておけば生存本能だけが発達した恐ろしい存在になる彼らを救うためには、太陽の放出する中間子を浴びねばならないという。直接浴びることのできる街・清流へと聖夫妻は子供たちを連れて向かうのだった……。小規模公開ながら、特異な設定が光る青春SF作品。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gloria", "グロリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　組織を裏切り、命を狙われていたジャックは同じアパートに住む女性グロリアに息子フィルを預ける。その後、ジャック一家は皆殺しに。一方、グロリアはフィルをつれてアパートを脱出。子供嫌いのグロリアは、一時はフィルを手放そうとするが、結局その子を連れて逃走を続けるハメに。やがて彼女は組織に乗り込む決意をするが……。タフなヒロインの活躍を描くハードボイルド・ドラマ。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチバン MAXIMUM", "ガチバン MAXIMUM", "", "", "http://ecx.images-amazon.com/images/I/61AddjTRG%2BL._SL210_.jpg", 65, "", "2011-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("或る女", "或る女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "文豪・有島武郎の同名小説を豊田四郎監督が映画化。自我にめざめ、時代に先んじて生きようとした一人の近代女性の数奇な半生を描く。京マチ子が自由奔放なヒロインを熱演、彼女の気性の激しさにも似た、動きの多い誇張した演出スタイルが特徴的。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　天元突破グレンラガン　螺巌篇", "劇場版　天元突破グレンラガン　螺巌篇", "", "", "http://image.eiga.k-img.com/images/movie/54079/315x210.jpg?3451", 127, "ガイナックスによる人気ロボットアニメの劇場版「天元突破グレンラガン／紅蓮篇」に続く第2弾。大グレン団は螺旋族王四天王を撃破し、王都テッペリンにて螺旋王ロージェノムとの壮絶な一騎打ちを制したシモン。王都攻略後、瞬く間に文明を構築した人類だったが、宇宙にまでその手を伸ばしたとき、正体不明の敵が現れる……。", "2009-04-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Liste de Carla", "カルラのリスト", "", "", "http://image.eiga.k-img.com/images/movie/34309/315x210.jpg?1684", 95, "逃亡中の戦争犯罪人を探し出して告発する国連検察官カルラ・デル・ポンテの活動に密着したドキュメンタリー。オランダのハーグに設置された旧ユーゴ国際刑事法廷。ここで検察官を務めるスイス人女性カルラは、500万ドルの賞金が賭けられながらも未だ逃げ続けている戦犯たちを追い求め、世界各国を飛び回っている。国連裁判所の舞台裏に初めてカメラが入り込むと同時に、虐殺によって家族を失った人々の悲痛な叫びを捉える。", "2007-11-10", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本一の若大将", "日本一の若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "京南大学の田沼雄一はマラソン部のキャプテン。ある日、カミナリ族から女店員・中里澄子を救い、やがて二人は好感を持つようになる。水上スキー大会で優勝を果した雄一は、スポンサーの社長令嬢に気に入られ、悪質な週刊誌に二人の仲をでっちあげられてしまう。雄一と澄子の仲は気まずくなりマラソン大会に出場した雄一は絶不調になる。しかし、誤解と知った澄子の声援で見事に優勝を飾る。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dungeons & Dragons: Wrath of the Dragon God", "ダンジョン＆ドラゴン2", "", "", "http://image.eiga.k-img.com/images/movie/33699/315x210.jpg?4689", 105, "1974年に誕生し、世界中のゲームファンを魅了したロールプレイングゲーム「ダンジョンズ＆ドラゴンズ」の映画化第2弾。人間と魔術師が力を合わせて治める平和なイシュミール国に、邪悪な魔術師ダモダールが蘇った。3000年前にファラジュアという地獄を支配した黒竜の復活を企むダモダールを倒すため、選ばれし5人の勇者たちが旅に出る。モンスターがはびこる剣と魔法の世界をスクリーンで再現したファンタジーアドベンチャー。", "2006-10-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("事件屋稼業", "事件屋稼業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "関川夏央と谷口ジローの代表作である同名人気漫画を、全編フィリピン・ロケで映画化。うだつの上がらぬ探偵が、人生の転機を賭けて臨んだ人身売買組織との闘いを、南国のエキゾチックな風景の中に描く。ピンク映画界で、磯村一路らと“ユニット５”を結成し、意欲的な活動を続ける福岡芳穂が監督。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY　TUTOR　FRIEND", "同い年の家庭教師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "韓国で大ヒットを記録した、クォン・サンウ主演のラブ・コメディ。日本でもTVドラマ『天国の階段』で絶大な人気を得たクォン・サンウと、清純派を脱却した演技で魅せるキム・ハヌルが共に壮絶な演技バトルを繰り広げる。すぐキレるため家庭教師のバイトが続かない女子大生スワン。そんな彼女が母親の友達の息子ジフンを教えることに。しかし、彼は札付きの不良で高校留年生だった……。原作は「猟奇的な彼女」と同じくインターネットに連載されていたエピソード。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Harry Potter and the Goblet of Fire", "ハリー・ポッターと炎のゴブレット", "", "", "http://image.eiga.k-img.com/images/movie/1079/315x210.jpg?2617", 157, "ホグワーツの4年生になった14歳のハリーは、3大魔法学校対抗試合に出場することになるが、その背後にはついに復活しようとする闇の魔法使いヴォルデモート卿の陰謀があった。ヴォルデモート卿役で「シンドラーのリスト」のレイフ・ファインズ、ゴシップ記者役で「めぐりあう時間たち」のミランダ・リチャードソンが出演。監督はシリーズ初の英国人、「フォー・ウエディング」「モナリザ・スマイル」のマイク・ニューウェル。", "2005-11-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユメ十夜", "ユメ十夜", "", "", "http://image.eiga.k-img.com/images/movie/33829/315x210.jpg?2354", 110, "文豪・夏目漱石の短編集「夢十夜」を豪華監督・キャストで映像化したオムニバス・ムービー。「犬神家の一族」の市川昆、「姑獲鳥の夏」の実相寺昭雄といった巨匠から、「呪怨」の清水崇、「ゆれる」の西川美和ら新鋭まで日本映画界を代表する10人の監督たちが集結。漱石自身が“この作品が理解されるには100年もの年月がかかるだろう”と予言した不条理で幻想的な世界に、アクション、サイレント、CGなど様々な手法で挑む。", "2007-01-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRAVELLING AVANT", "巴里を追いかけて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1948年パリ。戦後の荒廃した中で、若者たちは映画に熱中した。そんな中で、映画を何よりも愛するニーノ、金持ちの息子ドナルド、死んだ恋人の影響で映画に惹かれるようになった美しいバルバラの３人が知り合い、シネマクラブ創設を夢見る。有名なシーンの引用で散りばめられた映画マニアへのオマージュ。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SERVANTS OF TWILIGHT", "スティグマ 邪神降臨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "人気ホラー作家クーンツの原作を映画化したサスペンス・ホラー。とある新興宗教の教祖によって突然、“悪魔の化身”と決めつけられた６歳の少年ジョーイ。彼を抹殺しようとやって来た信者たちと、息子を守ろうとする母クリスティーンの凄絶な戦いが展開する。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Seven Year Itch", "七年目の浮気", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "　ビリー・ワイルダーが「第十七捕虜収容所」に続きヒット舞台劇を映画化した、セクシーコメディ。蒸し暑いニューヨークのアパート。妻子がバカンスに出かけたため、一時の独身気分を満喫しているリチャードのもとに、上の階に住む美女がクーラーを求めてやってくる。地下鉄の通風口に立ったマリリン・モンローの白いスカートが巻き上げられるシーンはあまりに有名。舞台でも主演を務めたトム・イーウェルが、映画版でもリチャードを演じている。", "1955-11-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Animal Farm", "動物農場", "", "", "http://image.eiga.k-img.com/images/movie/54099/315x210.jpg?3331", 74, "イギリスの老舗アニメーション・スタジオのハラス＆バチュラー・カートゥーン・フィルムズ設立者、ジョン・ハラスとジョイ・バチュラーが、社会風刺に満ちたジョージ・オーウェル原作小説をアニメ化。残忍な農場主に虐げられてきた家畜の動物たちが、豚のスノーボールとナポレオンをリーダーに革命を起こし、人間を追い出して動物による農場経営に乗り出す。しかし一致団結したかのように見えた動物たちの間にも、確執や権力争いが生まれ……。", "2008-12-20", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死亡遊戯／GAME OF DEATH", "ブルース・リー 死亡遊戯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "超人的な武闘で、一躍世界のスターになったB・リーが、完成を見ることなく急逝し、吹き替えを使って作り上げられたもの。人気スターが国際シンジケートに狙われて事件に巻き込まれるというアクション映画。ラストの壮絶な決闘シーンが話題となった。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREEWAY", "連鎖犯罪　逃げられない女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「ガンクレイジー」の脚本家M・ブライトが童話『赤ずきん』をモチーフに書き上げたシナリオを、O・ストーン製作総指揮の下で映画化したサイコ・サスペンス。売春婦の母と麻薬中毒の父を逮捕され、独りぼっちになった15歳の少女が祖母の家に向かうフリーウェイで変質的殺人鬼に追われることになる。主演は若手女優のL・ウィザースプーン。K・サザーランドが彼女を執拗に狙い続ける犯人のボブを怪演している。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("非行少年・若者の砦", "非行少年・若者の砦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "かつて非行少年だった家庭教師と、非行しかかっている高校生を描いた異色青春映画。時代の青春を鋭く切り取ることで定評のある藤田敏八の監督作品である。純粋であるがゆえに、大人たちの醜さが我慢できない少年・祐一の行動が共感と同時に激しく胸を打つ。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水になった村", "水になった村", "", "", "http://image.eiga.k-img.com/images/movie/34109/315x210.jpg?1446", 92, "ダム建設予定地に住む人々の姿を追い続けている写真家・大西暢夫によるドキュメンタリー。1957年、岐阜県徳山村に日本最大のダムが建設されるという話が持ち上がった。約1600人の住民たちが続々と移転地に引越す中、何家族かの老人たちは、できる限り徳山村で暮らし続けたいと戻ってきた。徳山村と同じ揖斐郡で育った大西は、そんな老人たちの暮らしに魅せられ、村がダムの底に沈むまでの15年間、彼らの姿をカメラに収め続けた。", "2007-08-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TOXIC AVENGER", "悪魔の毒々モンスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ひ弱ないじめられっ子メルヴィンが、不良グループにはめられて有害廃棄物資のドラム缶に飛び込んだ途端、化学変化によって醜悪なモンスターに変身。街にはびこる悪を徹底的に根絶しようと闘いを開始した彼は……。全編ブラック・ユーモア満載のホラー・コメディ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Na Putu", "サラエボ，希望の街角", "", "", "http://image.eiga.k-img.com/images/movie/55789/315x210.jpg?1657", 104, "「サラエボの花」（2006）でベルリン映画祭金熊賞に輝いたヤスミラ・ジュバニッチ監督の長編第2作。ボスニア・ヘルツェゴビナの首都サラエボを舞台に、ひと組のカップルがたどる愛の行方を、戦争や宗教問題を背景に描き出す。客室乗務員ルナと空港管制官アマルは、サラエボで同棲するカップル。ある日、アマルは勤務中の飲酒が原因で停職処分を受けてしまう。停職中にかつての戦友と偶然再会したアマルは、触発されてイスラム原理主義に傾倒していく。", "2011-02-19", "39,40,77,7,41", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギミー・ヘブン", "ギミー・ヘブン", "", "", "http://image.eiga.k-img.com/images/movie/1069/315x210.jpg?3187", 121, "ある刺激を受けると、本来とは別の感覚も同時に発生するという“共感覚”を持つ新介は、誰にも理解されない孤独な日常を送っていた。一方、両親を早くに亡くした少女・麻里もまた、共感覚を持つがゆえに、誰とも分かり合えないという孤独に苦しんでいたが、そんな2人がある日、運命的に出会う……。江口洋介、安藤政信、宮崎あおい、松田龍平らが共演する新感覚サスペンス。監督はCM界で活躍する松浦徹。", "2006-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE　OF　FRANKENSTEIN", "フランケンシュタインの館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "フランケンシュタイン男爵が創り上げた怪物はもちろん、ドラキュラ、狼男ほかおなじみモンスターが揃い踏みの人気シリーズの一作。刑務所から脱走した博士が手下を連れて男爵の城を目指した。旅の途中で彼はドラキュラに出会い、自分を刑務所に送った村長を殺すように仕向けるがドラキュラは陽光に当たって死んでしまう。次に、氷漬けの狼男と怪物を発見し……。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fright Night", "フライトナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「スター・ウォーズ」などで知られるSFX界の第一人者R・エドランドが手腕を発揮したホラー映画。17歳の高校生チャーリーは、隣の住民がヴァンパイアに変身するのを目撃。以来、命を狙われるハメに……。特殊メイクを駆使した“ウルフヴァンパイア人間”の、３段階変身が見もの。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CYBORG", "サイボーグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "暴力に明け暮れる混乱の近未来を舞台に、人類復活の鍵を握る女サイボーグを護衛する戦士ギブソンの活躍を描くSFバイオレンス・アクション映画。J・C・ヴァン・ダムが主人公に扮して、重量感あるアクションを披露する。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACTA GENERAL DE CHILE", "戒厳令下チリ潜入記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1973年、社会主義路線をとるチリのアジェンデ政権は、ピノチェト将軍による軍部クーデターで倒され、アジェンデ大統領をはじめ多くの人々が殺され、また亡命した。チリの映画作家M・リティンもそんな一人である。その後彼が亡命先のヨーロッパから戒厳令下のチリに変装して潜入し、その後のチリの表情をフィルムに収めたのが、このドキュメンタリー映画である。監督自身のサンチャゴ到着から始まるチリ潜入の姿と多くの人々に対するインタビューを、チリの歴史や1973年のクーデター当時を記録する古いニューズ・リールとともに構成しながら、チリの表情が緊迫感をもって浮き彫りにされていく。", "1986-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thor", "マイティ・ソー", "", "", "http://image.eiga.k-img.com/images/movie/54259/315x210.jpg?1605", 115, "「スパイダーマン」「X-MEN」のスタン・リーによるマーベル・コミックを実写映画化。神々の王の息子ソーは、ごう慢な性格をとがめられ、謙虚さを学ぶために人間界へ送り込まれる。医師ドナルドとして人間界で暮らしていたソーは、ある事件をきっかけに記憶を取り戻し、魔法のハンマー「ムジョルニア」でソーに変身。ヒーローとして人類の敵と戦う。監督はケネス・ブラナー。主演は「スター・トレック」にも出演したオーストラリアの新鋭クリス・ヘムズワース。神々の国の戦士ホーガン役で浅野忠信がハリウッド映画に本格初出演。", "2011-07-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHIRLEY VALENTINE", "旅する女 シャーリー・バレンタイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "シャーリーは家庭の主婦であり母親として一所懸命、夫のため子供のために生きてきた。今、中年の域にさしかかった彼女は自分の人生を見つけようと一人でギリシャ旅行に出かけ、そこで第二の青春を迎えるのだが……。英国の有名な舞台劇の映画化で、人生を振り返る女性を繊細に描いた佳作。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES PORTES DE LA NUIT", "イヴ・モンタン 枯葉 ～夜の門～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "第二次大戦直後のパリを舞台に、戦中のレジスタンス運動の同志だった鉄道員たちと、彼らを裏切り、密告した男たちの生活を描く。後年人気シャンソン歌手となったY・モンタンのヒット曲『枯葉』は、この映画の主題歌としてJ・コスマが作曲したもの。ビデオ発売時タイトルは「夜の門」。", "1946-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲキ×シネ「メタルマクベス」", "ゲキ×シネ「メタルマクベス」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 201, "「劇団☆新感線」による演劇を映画館で上映する「ゲキ×シネ」第4作。「劇団☆新感線」が人気脚本家・宮藤官九郎とタッグを組み、シェイクスピアの戯曲「マクベス」をヘビーメタルに乗せて演じた新感覚ミュージカルを映像化。西暦2206年。繰り返される戦争により、世界は荒野と化していた。ESP王国の将軍ランダムスターは、城へ帰る途中に不気味な3人の魔女と出会う。魔女はナゾめいた予言と共に、1980年代に活躍したヘビーメタルバンド「メタルマクベス」のCDをランダムスターに手渡すが……。", "2007-02-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAINED TO YESTERDAY", "雨の日にふたたび", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ベトナム戦争で捕虜になったり行方不明になったりした兵士たちの３人の妻の苦悩を描いたメロドラマ。結婚してわずか２週間で夫を戦場に送り出したサンディ、７年間も捕虜になったあげく夫の病死の知らせを受けたメリー、いまだ夫は行方不明のままのシャロン。苦しさをともにした３人が向かった空港で見たものは……。シナリオ・ライター、J・シルヴァーの出世作。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SWIMMER", "泳ぐひと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "暑い夏の午後、会社重役のネッドは、友人や近隣のエリートたちの家のプールを泳ぎながら家路をたどり始める。だが、満ちたりた生活を送っているはずの彼らは、皆生活に疲れ、人生への情熱を失っていた。自らの成功の虚しさを痛感したネッドは、絶望のうちに家にたどり着く……。病める現代の断面を鋭く描いたアメリカン・ニューシネマの傑作。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUMMERTIME", "旅情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アメリカ人のオールド・ミスが、孤独な心で過ごすベニスの街で知り合った銀髪の中年紳士に恋をする。水の都の美しい風景をみずみずしい映像感覚で捉え、名曲『サマータイム・イン・ベニス』の流れが生む切ない恋が、陶酔感を誘い、愛のはかなさを思わせてくれる。名匠リーンはこの作品をきっかけに世界を舞台にした大作・名作を続々発表。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("D.C. CAB", "D.C.キャブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ワシントンD.C.のオンボロタクシー会社を舞台にしたコメディ。会社がボロなら、運ちゃんもモヒカンのミスター・T以下強烈な個性の集まりで、これもバラバラ。が、彼らがある事件をきっかけに、経営不振でつぶれかかったこの会社を見事に立て直す。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・コテージ", "ザ・コテージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "怨霊伝説の残る島で正体不明の恐怖に襲われる若者たちの運命を描いた青春ホラー。サークルの仲間6人で、南の島へ卒業旅行にやってきた志保。美しい自然の中で自由を満喫していた志保らは、島に残る恐ろしい言い伝えを知る。それは、恨みを持つ死者の魂がよみがえり、人々を呪い殺すというものだった。やがて、ショッキングな出来事が次々と彼女たちを襲いはじめる。主演は人気グラビアアイドルの安田美沙子。", "2006-09-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("傷だらけの山河", "傷だらけの山河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 152, "人の犠牲を意に介さない非情な事業欲で成り上がっていった大事業家が、それゆえにやがて妻にも子どもにも妾にも見捨てられ、孤独になっていく様を描いた痛烈な社会派ドラマ。“信念に生きるのみ”と捨てゼリフをはく、山村聰の表情が印象的。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ICI ET AILLEURS", "ヒア＆ゼア・こことよそ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "70年に製作を開始しながら72年以降3年間の沈黙を挟んで、新しい同士である妻ミエビルと共に75年に完成した作品。80年代に向けてビデオを駆使したゴダール監督の新しい映画づくりの第1作となった。「勝利」と題された<ジガ・ベルトフ集団>作品の企画が、なぜ新しい題名を冠した新プロダクション<ソンイマージュ>作品となったのかという疑問自体がまずテーマとなる。ヨルダン、レバノン、シリアで撮影されたイメージと、フランス帰還後のゴダールの再考をたどる問題作。", "1978-07-22", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("象の背中", "象の背中", "", "", "http://image.eiga.k-img.com/images/movie/1959/315x210.jpg?4158", 124, "秋元康による同名小説を「g＠me」の井坂聡監督が映画化した人間ドラマ。充実した毎日を送っていたサラリーマンの藤山は、ある日突然がんと診断され余命半年の宣告を受ける。しかし彼は延命治療を拒否。残されたわずかな人生を有意義に過ごすため、自分が今まで関わった人々に直接別れを告げようと決意する。その一方で妻には病気のことを話せずにおり……。主人公・藤山を役所広司、彼の妻を、20年ぶりの映画出演となる今井美樹が演じる。", "2007-10-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("V.I.WARSHAWSKI", "私がウォシャウスキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "男顔負けの空手と射撃の腕前、機知に富んだ才覚、怖いもの知らずの行動力……。正義と真実のシカゴの女私立探偵V・I・ウォシャウスキーの活躍を描く。決して男に媚びないが、男の良さもきちんと心得ている主人公のキャラクターが主演のターナーと見事に融合している。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TENNESSEE NIGHTS", "テネシー・ナイツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "出張先のナッシュビルで週末を過ごしていたイギリス人弁護士レイトン。だが彼は、宿泊先のモーテルで銀行強盗一味の仲間割れに巻き込まれてしまう。夢中で逃げ出した彼は、やがて黒人の少女ミニーと出会い……。見知らぬ土地で窮地に陥ったエリート弁護士と、彼を救おうとする少女の絆を描くサスペンス。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Flamingo Kid", "フラミンゴキッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1963年、夏。ジェフリーはロングアイランドのビーチクラブでアルバイトをしながら青春を謳歌していた。秋には大学進学を控えていたが、大物の外車ディーラーと出会い、自分の進路を疑い始め……。年上の女性との恋、父との切ない交流を通して、大人へと成長していく若者をさわやかに描いた青春映画。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOULEVARD DU RHUM", "ラムの大通り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "アメリカの禁酒法時代、カリブ海にあったラム酒密輸入ルート“ラムの大通り”を舞台に、密輸船のいかつい船長と、彼が憧れた映画女優とのロマンスや冒険が伸びやかなタッチで描かれる。少年のような男心の純情をヴァンチュラが好演、バルドーとのコンビもいい。", "1971-01-01", "2", 0, NOW(), NOW());
