INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("加哩辣椒／CURRY ＆ PEPPER", "超アブない激辛刑事　カリー&ペッパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "香港映画界のコメディ王C・シンチーと香港歌謡界四天王の一人、J・チュンが共演した痛快アクション。二人が演じる凸凹刑事の活躍を、ハラハラドキドキあり、笑いあり、ラブ・ストーリーあり、本物のカンフーありとエンターテイメント精神たっぷりに見せていく。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sanctum", "サンクタム", "", "", "http://image.eiga.k-img.com/images/movie/55949/315x210.jpg?2813", 109, "「アバター」のジェームズ・キャメロン製作総指揮による3Dアクションアドベンチャー。南太平洋パプアニューギニアにある孤島の洞窟を探検していたベテランダイバーたちは、探検中に巨大サイクロンに遭遇。チーム一丸となって洞窟からの脱出を試みるが……。主演は「M:I-2」「ムーラン・ルージュ」のリチャード・ロクスバーグ、「ファンタスティック・フォー」のヨアン・グリフィズ。監督は豪映画「男たちの戦場」のアリスター・グリアソン。", "2011-09-16", "1,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents 真夜中の宴　「emerger」", "桃まつり presents 真夜中の宴　「emerger」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 42, "", "2008-03-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROOKIES 卒業", "ROOKIES 卒業", "", "", "http://image.eiga.k-img.com/images/movie/53939/315x210.jpg?1806", 137, "森田まさのりのベストセラー漫画をTVドラマ化して高視聴率を記録した「ROOKIES」の劇場版。3年生に進級したニコガク野球部の面々が、熱血教師・川藤と共に最後の夏の甲子園を目指す。川藤役に佐藤隆太、生徒役に市原隼人、小出恵介らおなじみのキャストが再結集したほか、「ハンサム★スーツ」の山本裕典、「グミ・チョコレート・パイン」の石田卓也ら新たなメンバーが参加。監督はTVドラマ版の演出も手掛けた平川雄一朗。", "2009-05-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("（本）噂のストリッパー", "（本）噂のストリッパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "浦安の小屋のストリッパー・グロリアに憧れる青年、洋一の純情ぶりを描いた青春映画。TVドラマ“ケンちゃん”シリーズの宮脇康之が純情青年を好演。森田監督がロマンポルノに初挑戦し、それまでの情感に訴えるポルノとは一味違う、ライト感覚あふれる作品となっている。", "1982-09-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LONG RIDE HOME", "大いなる砲火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "南北戦争末期のメキシコ国境付近の荒野を舞台に、南北両軍兵士たちの死闘と、男女の激しい愛憎を描いた本格ウエスタン・アクション。沈着冷静で正義感あふれる北軍少佐のG・フォードと、反逆者で荒くれ者の南軍大尉のG・ハミルトンの二大名優が対照的な魅力を見せる。H・フォードが冒頭脇役で出演している。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWIST AGAIN A MOSCOW", "モスクワの追跡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "社会主義時代のロシアを舞台に、KGBに追われる反体制ロック歌手の一派の逃避行を描いたアクション・コメディ。ホテルを経営するブルジョワ男イウリのもとに、パンク歌手のマネージャーをしていることから当局にマークされた甥っ子が転がり込んできた。保守的なイウリは妻に尻をたたかれ、嫌々ながら甥の国外逃亡の手助けをするハメに……。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Spy Kids 2: The Island of Lost Dreams", "スパイキッズ2 失われた夢の島", "", "", "http://image.eiga.k-img.com/images/movie/1669/315x210.jpg?2795", 100, "アメリカ最年少のスパイ、カルメンとジュニの姉弟に、同世代のライバル、ゲイリーとガーディが出現。ジュニは彼らの妨害でスパイの資格を剥奪される。2人は汚名返上のため、大統領の機密情報を盗んだ犯人を追って謎の島へ。そこは奇妙な生物たちで溢れていた。「ハリーハウゼンに捧ぐ」との献辞通りに、彼の作品を連想させる生物が大挙出現。フループはじめ前作の登場人物も多数登場。今回はカルメンとジュニの母方の祖父母も登場。", "2002-09-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SO WHAT", "SO WHAT", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "大友克洋のコミック『GOOD WEATHER』をベースに、ある田舎町でロックバンドを組んでいる４人の高校生の姿を追った青春ドラマ。自主映画作家から1986年「ビリィ・ザ・キッドの新しい夜明け」で劇場用映画デビューを果たした山川直人の第２作。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEET REVENGE", "恋人たちのパリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "夫ジョンとの10年間の結婚生活を終え、離婚を決心した女性弁護士のリンダ。結婚前に取り交わした契約から、彼女はジョンに対し慰謝料を支払わねばならなくなる。しかし、それがいやな彼女は、“ジョンが別の女と再婚した場合、慰謝料は払わなくてもいい”という条項を思い出し、なんとかジョンを再婚させようと、女優の卵ケイトを彼の前に差し向けるのだが……。パリを背景に、恋の陰謀と、それを超えて愛し合うようになる恋人たちの姿を綴ったロマンティック・コメディ。R・アークエットが、女優の卵ケイト役を華麗に演じている。", "1990-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("攻殻機動隊 S.A.C. SOLID STATE SOCIETY 3D", "攻殻機動隊 S.A.C. SOLID STATE SOCIETY 3D", "", "", "http://image.eiga.k-img.com/images/movie/55879/315x210.jpg?2056", 108, "大ヒットアニメ「攻殻機動隊 STAND ALONE COMPLEX」のシリーズ第3作として制作された長編アニメ「攻殻機動隊 S.A.C. Solid State Society」を3Dアニメとして新たによみがえらせた劇場版。難民蜂起事件から2年後の西暦2034年。“梵”の刺青を入れた男たちが相次いで不審な自殺を遂げる。新たなメンバーを増員した公安9課は、自殺した男たちがシアク共和国の特殊工作員で、テロを計画していたことを突き止める。", "2011-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユー★ガッタ★チャンス", "ユー★ガッタ★チャンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "吉川晃司のデビュー作「すかんぴんウォーク」に続く第２作。監督も前作と同じ大森一樹で、今回はアイドル・スターの地位を手にした主人公・民川裕司が、悩みながら自分をみつめ直し、新たに出発するまでを描く。映画の中と同様、当時人気絶頂の吉川が好演。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Z.P.G.（ZERO POPULATION GROWTH）", "赤ちゃんよ永遠に SFロボットベイビーポリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "“人口増加のため、今後30年間は出産を禁止する！”—ラスとキャロルの夫婦は、それでも子供を持ちたいと願う……。赤ん坊を産めない異常な社会を、淡々としたタッチで描いていく異色SF。暗い未来を暗示するラスト・シーンが不気味だ。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOREVER AMBER", "永遠のアンバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "イギリスの小さな村で育った孤児の少女アンバーは、富と名声を求めて次から次へと男を変え、波瀾万丈の人生を送っていく。しかし彼女の周囲には常に憎悪と嫉妬が渦巻いていた……。ベストセラーの大河小説をL・シャムロイの幻想的な撮影によって一種のおとぎ話のように映画化した。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌３", "釣りバカ日誌３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "人気も定着した、釣りバカ・浜崎伝助と彼の会社の社長・鈴木一之助コンビの３作目。TVのCMに出演した浜崎夫婦。しかし、できたフィルムには奥さんと子供だけが写っていてハマちゃんはむくれる。気晴らしに伊豆・星の浦に旅行したハマちゃんにスーさんが道連れに。そこでスーさんは、かつて愛した女の娘・雪子と出会うが……。マドンナ役は五月みどり。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嗚呼!!花の応援団", "嗚呼!!花の応援団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "南河内大学応援団親衛隊長・青田赤道はケンカに強く女にはまるでだらしがない。新入隊員が団旗をタバコの火でこがしてしまい青田は責任をとって腹を切らねばならない。そこで青田は仲の悪い浪華大学応援団にケンカを売り、旗はそれで破れたことにしてうまくのがれることに成功する。やがて野球や駅伝の応援に親衛隊のウルトラ珍妙な活躍が始まった……。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Devil in a Blue Dress", "青いドレスの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1948年、ロサンゼルス。イージーは街全体が活気にあふれるなか、一人ドン底状態。失業中でマイホームのローンに追われる彼は、どんな仕事でも引き受ける気分だった。そんな時、彼の元に人捜しの依頼が舞い込んでくる。捜す相手はダフネという白人女。依頼人にうさん臭いものを感じながらも引き受けたイージーだったが、案の定、彼の悪い予感は的中する。殺人事件に巻き込まれ、悪徳白人警官に痛めつけられ、あげくの果て命まで狙われるハメに。しかし、イージーはついに“青いドレスの女”ダフネの存在をつかむ。D・ワシントンが主演した小粋なミステリー。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ☆ドラえもんズ　ゴール！ゴール！ゴール!!", "ザ☆ドラえもんズ　ゴール！ゴール！ゴール!!", "", "", "http://ecx.images-amazon.com/images/I/31DQE0CGA8L._SL210_.jpg", 5, "世界各地からやって来た7人のネコ型ロボットからなる「ザ・ドラえもんズ」を主人公にした短編アニメーションのシリーズ第7弾。悪の科学者アチモフのチームとサッカー対決をすることになったドラえもんズ。しかし、試合会場のアチモフスタジアムでは、数々の罠が彼らを待ち受けていた……。登場人物の台詞を一切排し、音楽と効果音のみで描かれている。「のび太とロボット王国」「ぼくの生まれた日」と同時上映。", "2002-03-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A FINE MESS", "ブレイク・エドワーズのファイン・メス!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "B・エドワーズ監督によるドタバタ・コメディ。ふとしたことで八百長が仕組まれた競馬レースの情報を聞いてしまった俳優志望の男が、友人の金で馬券を買い、まんまと大金をせしめるが、恐ろしいヤクザに狙われるハメに。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Away from Her", "アウェイ・フロム・ハー君を想う", "", "", "http://image.eiga.k-img.com/images/movie/53289/315x210.jpg?2170", 110, "「スウィート・ヒアアフター」「あなたになら言える秘密のこと」のサラ・ポーリーが、アリス・マンローの短編小説「クマが山を越えてやってきた」を自ら脚色し、長編監督に初挑戦した人間ドラマ。44年間連れ添った仲睦まじい老夫婦が、妻のアルツハイマー病発症をきっかけに変化していく様を描く。妻のフィオナにオスカー女優のジュリー・クリスティ。夫グラントにカナダの名優ゴードン・ピンセント。製作総指揮はアトム・エゴヤン。", "2008-05-31", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINAL APPROACH", "ファイナル・アプローチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "最高飛行速度マッハ3.5のハイテク戦略偵察機“SR−71”で低空飛行を行っていたテスト・パイロットのジェイソン大佐は、エンジン故障のために緊急脱出する。見慣れない部屋で目が覚めたジェイソンは、ゴッドリーブと名乗る精神科医から心理テストのような質問を浴びる。一時的な記憶喪失状態だったジェイソンは、ゴッドリーブの巧みな誘導で断片的な記憶を取り戻してくる。“自分は何か特別な任務を負っていたのでは？ ”そう思い始めたジェイソンは、ゴッドリーブに対しても不信感を抱き始める。果たしてゴッドリーブの正体は？ そしてジェイソンの運命は……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Three Burials of Melquiades Estrada", "メルキアデス・エストラーダの3度の埋葬", "", "", "http://image.eiga.k-img.com/images/movie/1229/315x210.jpg?3596", 122, "「逃亡者」（93）でアカデミー助演男優賞を受賞、「メン・イン・ブラック」シリーズなどで活躍する名優トミー・リー・ジョーンズが劇場用長編映画を初監督。テキサスとメキシコの国境。カウボーイのピートは、友人のメキシコ人カウボーイ、メルキアデス・エストラーダの死体を発見。彼を故郷の町に埋葬するため、彼を殺した新任の国境警備員マイクと共に遺体を運ぶ旅に出る。昨年のカンヌ映画祭でトミー・リーが主演男優賞、ギジェルモ・アリアガが脚本賞を受賞。", "2006-03-11", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents kiss!　「たまゆら」", "桃まつり presents kiss!　「たまゆら」", "", "", "http://image.eiga.k-img.com/images/movie/54479/315x210.jpg?1247", 22, "", "2009-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BELLE NOISEUSE", "美しき諍い女（いさかいめ）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 237, "日本公開にあたり、モデルを演じるエマニュエル・ベアールの“ヘア論争”が巻き起こったリベットの問題作。老画家の屋敷を、新進画家が訪ねてきた。彼の恋人を見た老画家は、10年間中断していた野心作“美しい諍い女”の制作再開を決意する。かつて“美しい諍い女”のモデルを務めた妻、創作に悩む老画家、最初はモデルを拒んでいたが次第に積極的に老画家に挑み始める娘、そしてその恋人。二組のカップルの関係にも緊張が走りはじめる……。", "1992-05-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Graffiti", "アメリカン・グラフィティ", "", "", "http://ecx.images-amazon.com/images/I/51ppg0xuOmL._SL210_.jpg", 112, "青春映画の、いわば原点ともいうべき作品。当時弱冠29歳のG・ルーカスが、自らの思い出を重ね合わせて、ドラッグもベトナム戦争もなかった1962年の青春像を、あざやかにスクリーンによみがえらせている。カリフォルニア北部の小さな地方都市。若者たちの気晴らしはカスタム・カーをぶっ飛ばして、ガールハントすることだ。カート、スティーブ、テリー、ビッグ・ジョンの４人はいつも連れだっている悪友仲間。高校を卒業し、東部の大学へ行くカートとスティーブにとって最後の寄るがやってくる。町に繰り出す４人。ケンカ、カーレース、恋人との別離。やがて夜が明け、新たな旅立ちが始まる……。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fat City", "ゴングなき戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "　元ボクサーのタリーは青年アーニーの才能を見抜き、彼にボクシング・ジムへ行くことを勧める。やがてボクサーとなるアーニー。敗戦が続く中、彼はガールフレンドと結婚することに。一方、酒びたりのタリーはどん底の生活を送っていた。そんな彼は、アーニーとの再会を経て、カムバックを決意するが……。かつてボクサーでもあったジョン・ヒューストン監督が栄光なき男たちの生き様をリアルに描く。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tracks", "トラックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "S・キング自らが監督した「地獄のデビルトラック」でも知られる短編小説『トラック』を、現代的なアレンジを加えて再び映画化したホラー・サスペンス。“エリア51”にほど近い小さな田舎町で、トラックたちが意志を持って行動を始め、突如人々を襲い出す。生き残った数人の人間たちはドライブ・インに立てこもって反撃を試みるのだが……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帝都大戦", "帝都大戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "荒俣宏原作の『帝都物語』第11巻“戦争（ウォーズ）篇”を映画用に大胆にアレンジ。ハリウッドから“超現実視覚効果”としてスクリーミング・マッド・ジョージが招かれ、SFXの現場を指揮している。戦争末期の帝都・東京の崩壊をたくらむ加藤保憲と二人の霊力者とのサイキック・ウォーズが展開されていく。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GRAND JEU", "外人部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "愛人フロレンスのために悪事をはたらいたうえに彼女に捨てられたピエール。彼は自暴自棄となり、外人部隊に入って、酒と女の日々を過ごす。だが、ある日、彼はフロレンスに瓜二つの女性と出会う……。有名な外人部隊を舞台に男女の運命を描いた古典的作品。", "1933-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("惜春鳥", "惜春鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "東京の大学に通っている岩垣、旅館の息子・峯村、工場で働いている手代木、サロンの息子・牧田。郷里・会津で久しぶりに再会した彼らが、現在の境遇の違いによる微妙な感情の行き違いを経て、ある事件をきっかけに再び一致団結し友情を確認し合うまでを描いている。木下監督に育てられた若い俳優たちが、一同に出演した作品。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クローズド・ノート", "クローズド・ノート", "", "", "http://image.eiga.k-img.com/images/movie/34209/315x210.jpg?1916", 138, "人気ミステリー作家・雫井脩介による恋愛小説を「世界の中心で、愛をさけぶ」の行定勲監督が映画化。教育大に通う香恵は、引越先のアパートで前の住人が忘れていったノートを発見する。それは小学校の女性教師・伊吹の日記で、生徒たちとの交流や恋の悩みが綴られていた。バイト先で出会った画家・石飛への恋心を持て余していた香恵は、罪悪感を抱きながらも日記を読み進めていくが……。2人のヒロインを沢尻エリカと竹内結子が演じる。", "2007-09-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWAMP WATER", "スワンプ ウォーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ナチスに追われ、アメリカに渡ったJ・ルノワールが、プロデューサーのD・F・ザナックに招かれ、アメリカで撮った作品。アメリカ南部を舞台に展開する鮮烈なサスペンス・ストーリー。殺人犯と疑われ南部の沼地に逃げ込んだ男を、何が何でも私刑にしようと偏見に満ちたディープ・サウスの村人たちが追う。そんな中、彼の容疑に疑いを持つ二人の人間がいた……。果てしなく続く底無し沼と、奇妙な植物が這う南部の映像が印象深い。J・ルノワールは、当時の南部の人々の偏見の強さと、祖国フランスの現状への猜疑心との二重写しに成功している。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺たちの時", "俺たちの時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "“ふれあいコンビ”中村雅俊・檀ふみ主演の青春絵巻第３作。人生に何の目的もないような無気力人間の大学生が、一人の老人と一人の少女に出会い、生きるための光明を見出して自分の道を歩き出していく姿を描く。助演者のキャラクターが光る。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("virtual trip 美しい惑星 The Beautiful Planet", "virtual trip 美しい惑星 The Beautiful Planet", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "「TAJOMARU」「RED SHADOW 赤影」の中野裕之監督が、ハワイやタヒチの自然を捉えたドキュメンタリー。オアフ島、ハワイ島、タヒチ島といった南太平洋に浮かぶ島々の空撮からはじまり、地上での美しい時間の流れや水中に生息する野生動物の姿など、その魅力をあますところなく撮影。中野監督が数年を費やして撮りあげた圧倒的な映像美と、世界的ミュージシャンのテイ・トウワらが手がけた癒しの音楽により、地球の絶景を体感することができる。", "2010-07-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CANNIBAL APOCALYPSE", "地獄の謝肉祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ベトナム戦争中、人肉を食らって生き延びた帰還兵のカニバリズム・シンドローム。コンチネンタル・スプラッタの面目躍如。女の乳房にかぶりつくわ、太ももに食らいつくわの大騒ぎ。この“カニバリズムは伝染する”という設定も面白く、興奮を増す。", "1980-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD　LEAVES", "DEAD　LEAVES", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "「キル・ビル」のアニメパートも担当したアニメスタジオ、Production I.G.が手掛けたノンストップ・アクション。気鋭アーティスト・今井トゥーンズが手掛けたキャラクターが、スクリーン狭しと大暴れ。過去の記憶がまったくない二人組・PANDYとRETROは、欲望のおもむくままに都市で食べ物と武器を強奪した末、月面刑務所“DEAD LEAVES”に送還されてしまう。そこでは看守が囚人たちを暇つぶしに殺すことが日常化しており……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ring", "ザ・リング", "", "", "http://image.eiga.k-img.com/images/movie/1199/315x210.jpg?1524", 116, "大ヒット和製ホラー「リング」をスピルバーグのドリーム・ワークスがリメイク。基本的なストーリーは同じ。新聞記者レイチェルは、それを見ると7日目に死ぬという謎のビデオを入手するが、幼い息子がそのビデオを見てしまい、彼を救うために奔走する。監督は「ザ・メキシカン」のゴア・バービンスキー、脚本は「スクリーム3」のアーレン・クルーガー。特殊メイクはアカデミー賞を6度受賞のリック・ベイカーが担当。", "2002-11-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空かける花嫁", "空かける花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "原作は藤沢桓夫の『花粉』という小説でこれを笠原良三が脚色、番匠義彰が演出にあたる大船調下町喜劇。東京下町の繊維問屋の主人・七兵衛と孫娘の女子大生・まるめが繰り広げる、それぞれの惚れたりフラれたりのラブ・ロマンス。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLESH GORDON", "フレッシュ・ゴードン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "1930年代に流行した漫画『フラッシュ・ゴードン』のポルノ版パロディー。宇宙のかなたにあるエキサイト惑星から発射された光線は人の理性をなくしセックスの虜としてしまう。フレッシュは、ペニス型ロケットで人類を救うべく飛び立つ。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FEDS", "マドンナ★コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「ゴーストバスターズ」「ツインズ」のI・ライトマンが製作総指揮を担当したアクション・コメディ。FBI養成所のルームメイト、エリーとジャニスの女性二人。銃は得意だが学科はだめなエリー、逆に頭脳明晰だが腕力はからっきしのジャニス。彼女たちは男なんか何するものぞ、とはりきるが……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボクラの島を忘れない", "ボクラの島を忘れない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "瀬戸内海の小さな島を舞台に、休所が迫る保育所の子供たちを１年間に渡って追いかけたドキュメンタリー。淡々とした日々の中で、思い切りの笑顔で駆け回る子供たちと、島のおばあちゃんとの心の交流が丹念に描かれる。香川県丸亀市沖、瀬戸内海に浮かぶ小さな島、広島。この島の保育所で元気いっぱい遊ぶ６人の子供たちは、島のおばあちゃんとキアゲハの青虫を飼育中。しかし、保育所はもうすぐなくなることになっていて……。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Scream 4", "スクリーム4：ネクスト・ジェネレーション", "", "", "http://image.eiga.k-img.com/images/movie/56349/315x210.jpg?3196", 110, "ウェス・クレイブン監督が手がける人気ホラーミステリーのシリーズ第4作。10年前に発生したウッズボロー連続殺人事件で生き残ったシドニーは、いまでは作家となり成功を収めていた。しかし、シドニーが本の宣伝のため故郷へ戻ったのを機に2人の女子高生が惨殺される事件が発生。シドニーの周囲の人々が次々と狙われていく……。ネーブ・キャンベル、デビッド・アークエット、コートニー・コックスらシリーズの主要キャストが再結集したほか、エマ・ロバーツ、ヘイデン・パネッティーアら若手陣も参加。", "2011-10-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILENCE　OF　BODY", "奴隷貴婦人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "美貌の精神科医マリアンヌがある絵画に魅了され、その作者である東洋画家ヘリョンと親しくなる。しかし二人は暴漢にレイプされてしまい、ヘリョンの脳裏に少女時代の忌まわしいトラウマがよみがえる。韓国のグラマー女優Y・ヘリが、「エマニエル夫人」でおなじみのS・クリステルと共演した官能ドラマ。", "1992-01-01", "1,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イッツ・ア・ニューデイ", "イッツ・ア・ニューデイ", "", "", "http://image.eiga.k-img.com/images/movie/53149/315x210.jpg?2818", 70, "心の病で耳が聞こえなくなったサラリーマンと、美人で有能な派遣OLが繰り広げるラブストーリー。東大卒なのに不器用で失敗ばかりしている甲斐は、上司に怒鳴られている最中に突然耳が聞こえなくなってしまう。医師から心因性の難聴と診断され途方に暮れる甲斐の元に、派遣社員の沙織が配属される。甲斐は何故か彼女の声だけは聞くことができ……。ヒロインの沙織を演じるのは、テレビドラマ「逃亡者おりん」で注目を集めた青山倫子。", "2007-12-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CANDIDATE", "候補者ビル・マッケイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "カリフォルニア州の上院議員選に出馬した若き弁護士ビル・マッケイは、高い理想を持って政界に乗り込もうと意欲満々だった。しかし、建前論ばかりの選挙のメカニズムに取りまかれ、自分が単なる操り人形にすぎないと知る……。シニカルな題材だが、さわやかな語り口で青春ドラマ風に仕立てた好編。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学校の階段", "学校の階段", "", "", "http://image.eiga.k-img.com/images/movie/33959/315x210.jpg?3040", 75, "学校内の階段を駆け巡る“階段部”に青春を捧げる高校生たちの姿を、ワイヤーアクションやCGを駆使して描く異色の青春群像劇。天栗浜高校に転校してきた里美は、学校非公認の階段部にスカウトされ入部を決意する。ある日、階段部を目の敵にする生徒会長のちづると、部の存続を賭けて対決することになり……。ヒロインの里美に「ケータイ刑事」シリーズで人気の黒川芽以。監督は「発狂する唇」などでカルト的人気を誇る鬼才・佐々木浩久。", "2007-04-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海軍病院船", "海軍病院船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "疾病兵を収容した海軍病院船。生死をさまよう兵士たちの悲痛な叫びや、故郷への想いが狭い船内に渦巻く。力尽きて息を引き取る兵士も少なくなかった……。様々なドラマを乗せて進む海軍病院船“氷川丸”の航海が描かれている。山本五十六の貴重な映像も収められている。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kill Bill: Vol. 1", "キル・ビル", "", "", "http://image.eiga.k-img.com/images/movie/1099/315x210.jpg?3594", 113, "史上最強と言われた女エージェントのザ・ブライドは、結婚式当日にかつてのボス、ビルの襲撃を受け、夫や身ごもった子供まで殺されてしまう。4年後、昏睡状態から目覚めた彼女は、ビルへの復讐を決意する……。クエンティン・タランティーノ監督6年ぶりの最新作でヒロインを演じるのは「パルプ・フィクション」のユマ・サーマン。宿敵ビルには70年代のテレビ・シリーズ「燃えよ！カンフー」などで活躍したデビッド・キャラダイン。伝説の剣豪・服部半蔵を演じた千葉真一は、剣術指導にもあたった。本作は2部作の前編にあたる。", "2003-10-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBO C.H.I.C.", "ロボ・ウーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "近未来の犯罪都市を舞台に、ロボコップならぬロボ・ウーマンが活躍するアクション・コメディ。全男性憧れの美貌と、グラマラス・ボディを持つ正義の戦士に扮するのは、TVシリーズ“バットマン”のロビンことB・ウォードが扮している。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Young and Innocent", "第3逃亡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "　犯人の汚名を着せられた男が、チャーミングな女性の手を借り共に真犯人を探す、ヒッチコックお得意のサスペンスムービー。映画女優が浜辺で死体となって見つかる。凶器のベルトなどから第1発見者であるロバートが疑われるが、彼は無実を証明しようと逃亡を謀る。ロバートを助けるエリカ役は、ヒッチコック1934年の作品「暗殺者の家」で娘役を演じたノバ・ピルブーム。原題の「若さと無邪気」が意味する通り、彼女の成長と信頼の強さも描かれている。", "1977-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グーグーだって猫である", "グーグーだって猫である", "", "", "http://image.eiga.k-img.com/images/movie/53359/315x210.jpg?2276", 116, "人気少女漫画家・大島弓子が飼い猫との日々を綴った自伝的エッセイ漫画を、「ジョゼと虎と魚たち」「メゾン・ド・ヒミコ」の犬童一心監督が映画化。吉祥寺に暮らす漫画家の麻子は、愛猫サバが死んだ悲しみで漫画が描けなくなり、空虚な日々を送っていた。そんなある日、麻子はペットショップで運命的に出会った子猫を、グーグーと名付けて飼うことに。主演の小泉今日子が5年ぶりの新曲で主題歌も担当。共演に上野樹里、加瀬亮、お笑い芸人の森三中ら。", "2008-09-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮本武蔵　双剣に馳せる夢", "宮本武蔵　双剣に馳せる夢", "", "", "http://image.eiga.k-img.com/images/movie/54169/315x210.jpg?3676", 0, "剣豪・宮本武蔵が晩年に金峰山・霊巌洞で執筆したとされる兵法書「五輪書」に基づき、武蔵の剣法“二天一流”の謎やその生涯に迫る歴史アニメ。「GHOST IN THE SHELL／攻殻機動隊」「イノセンス」のプロダクションI.Gがアニメーションを手がけ、押井守が原案と脚本を担当する。地、水、火、風、空の5つの巻から成る「五輪書」に新しい解釈を加え、無敵の男・武蔵をドキュメンタリータッチで描く意欲作。", "2009-06-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HEIRESS", "女相続人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "H・ジェームズの中編小説『ワシントン広場』を名匠ワイラーが映画化。19世紀半ば。富豪の医者の娘キャサリンは、死んだ母に似ず、器量が悪く内向的。そんな彼女を父親は今一つ愛せない。ある日、従妹の婚約パーティーに出席したキャサリンは青年モリスと出会い、熱烈な恋に落ちた。ところが父親は、彼が定職もない貧乏青年であることを理由に結婚を許さない。彼女は駆け落ちを決意するが、約束の時間に彼は現れなかった……。財産をめぐる父と娘、男と女の愛憎ドラマを、シリアスに格調高く描いたワイラーの傑作。自分をことごとく裏切った父と男への復讐を遂げたラストで、キャサリンの顔に現れる毅然とした美しさが印象的だ。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Punk: Attitude", "PUNK：ATTITUDE パンク：アティテュード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1970年代の誕生以来、世界各地で進化を続けてきたパンクロックの歴史を辿ったドキュメンタリー。「パンク」という言葉が使われる以前のバンドから、オリジナルパンクやハードコアパンク、そしてグランジや現代パンクに至るまで、パンクロックの30年間にわたる軌跡を、各シーンの当事者へのインタビューや貴重なアーカイブ映像とともに振りかえっていく。監督は「クラッシュ：ウェスト・ウェイ・トゥ・ザ・ワールド」のドン・レッツ。", "2008-07-12", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DESERT FOX", "砂漠の鬼将軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "第二次大戦中、勇将と謳われたロンメル将軍の悲劇を描いた戦争ドラマ。第二次大戦下のアフリカ。“砂漠の狐”と連合軍より恐れられたロンメル将軍は、戦況不利のなかにあって、最前線の悲惨な状況を顧みようともしない上層部に対し憤りを感じていた。やがて彼は、ヒトラー暗殺計画に加担するが……。J・メイスンがロンメル役を熱演。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN JULY", "太陽に恋して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ベルリン映画祭金熊賞受賞作「愛より強く」のファティ・アキン監督が贈るロマンチックなロード・ムービー。冴えない教育実習生が一目惚れの女性を追って旅に出る中で大胆に成長を遂げていく姿がユーモアたっぷりに描かれる。トルコ人の女性に恋した冴えないダニエルは彼女を追ってイスタンブールへ旅立つ。彼に思いを寄せるユーリを伴いさまざまな経験をする中で、偏屈だった彼の性格は徐々に変わっていく。", "2000-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二十一歳の父", "二十一歳の父", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "女マッサージ師と結ばれた盲目の青年は、若くして父親になり幸福な日々を送っていた。しかし交通事故で妻と子を一度に亡くし、失意の彼も服毒自殺を計る。成島東一郎のカメラ、武満徹の音楽が見事。後年、シリーズでTVドラマ化されたバーニング・ラブ系の純情青春映画。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関白宣言", "関白宣言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "さだまさしの同名ヒット曲をヒントにしたサラリーマン喜劇で、実弟のさだ繁理が主演した珍品。23歳の宇田まさしは、うだつのあがらない会社員。恋人なしの生活を見かねた悪友から紹介された美女・久美が、鬼部長の実の娘だったことから大騒動が……。いかにも“演技勉強中”の名取裕子が初々しい。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONTE　D’AUTOMNE", "恋の秋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "E・ロメールの連作“四季の物語”の最終編。誤解と思い込みの恋騒動がユーモラスに描かれる。ぶどう園を経営する勝ち気な中年女性マガリは息子と二人暮らし。そんな彼女に恋人を見つけてやろうと、親友のイザベルは新聞の交際欄で相手を見つける。一方、マガリの息子の恋人ロジーヌも、かつて交際していた大学講師をマガリに紹介し、彼らの恋を実らせようとしていた。やがてイザベルの娘の結婚パーティーの日、彼らは一堂に会するが……。軽妙な会話を通して物語を展開させていく、ロメールの洗練されたタッチは絶品。緑豊かな風景が恋の騒動をのどかに彩り、スッキリとした後味を残す。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京ハレンチ天国・　さよならのブルース", "東京ハレンチ天国・　さよならのブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "本田隆一監督が大阪芸術大学の卒業製作として完成させた長編デビュー作。憧れの職業である殺し屋になったものの失態を犯してしまった黒川。マフィアの取引に乗じて一攫千金をもくろむGSバンド・ドンキーズの面々。そんなまるで関連のない二つの物語が交錯していく様が、1960年代の風俗に対する監督の愛着を反映したエネルギッシュな演出で描かれる。ゆうばり国際ファンタスティック映画祭のオフシアター部門でグランプリを受賞。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMOK！", "アモク！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1976年南アフリカ共和国のソウェトで起きた虐殺事件をもとにしたアンチ・アパルトヘイト映画。ストーリー自体はフィクション仕立てだが、白人シンパの盗み撮りによるヨハネスブルグ市のロケなど、緊迫感に満ちた構成になっている。", "1981-01-01", "38,77,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN THE NAME OF FATHER", "父の祈りを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "「マイ・レフトフット」の監督、主演コンビによる、実話に基づいた人間ドラマ。1974年にロンドン郊外で起きた爆弾テロ事件。現場近くに居合わせたヒッピーくずれのアイルランド人青年ジェリーはIRAのテロリストと間違えられ、いわれなき罪に問われる。後に彼の父もまた協力者とみられ投獄された。必死に無実を主張する父に対して、冷めた感情を抱くジェリー。だが、父が非業の死を遂げた時、彼は自由を求めて戦うことを誓う。警察の捜査と司法制度のずさんな実態にメスを入れた社会派ドラマながら、父子の葛藤に焦点を絞った物語は感動的。拷問に近い尋問シーンで見せるD・デイ・ルイスの熱演も光る。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母", "母", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "新藤兼人監督の永遠のテーマである“女性”と“核”を同時に扱った作品。脳腫瘍に冒された子を持つ娘が再婚、夫が殺され、子供も失ったあと、新しい命を宿していることで希望を見いだすまでを乾いたタッチで描く。主演の乙羽が母のエネルギーを見事に表現。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　RIVIE｀RES　POURPRES　２：LES　ANGES　DE　L’APOCALYPSE", "クリムゾン・リバー2／　黙示録の天使たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "J・レノ主演によるフレンチ・サイコスリラーの続編。レノ扮する敏腕刑事が、新たな若い相棒とともに、再び猟奇殺人の謎に挑む。今回の発端は、死体を壁に埋め込むという異様な事件。黙示録を信じる宗教団体が絡んだ、そのミステリアスな真相を解き明かす。レノとは旧知の仲であるL・ベッソンが脚本を手掛けている。 修道院のキリスト像から血が流れる怪奇事件が起こり、捜査現場に来たニーマンス警視は壁に埋められた死体を発見。一方、麻薬捜査中のレダ刑事はイエスを名乗る男と出会う。二つの事件は絡み合い、七つの封印の謎が浮かび上がる", "2004-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フレンチドレッシング", "フレンチドレッシング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "PFF出身の斉藤久志が、やまだないとの同名コミックを映画化した青春ドラマ。ドライな感覚の高校生の男女と男性教師の奇妙な三角関係を描く。飛び降り自殺をしようとしたいじめられっ子の17歳の男子生徒・岸田が、教師の村井に救われる。ところが、村井は彼を押し倒しレイプ。岸田は村井が妻を殺したというウワサを聞き、証拠をつかむため、彼の庭を掘り返す。そんな岸田に興味を抱く女生徒のマリィも加わり、いつしか３人は共同生活を始める。付かず離れずの心地よい関係を楽しんでいた彼らに、やがて変化が生じる。", "1998-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUMBO", "ダンボ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "サーカスの象ジャンボの赤ちゃん象、ダンボ。大きな耳をみんなからバカにされるが、ねずみのチモシーに励まされ、空飛ぶ象としてサーカスの人気者へと成長していく。アカデミー音楽賞を受賞した心温まるディズニー・アニメの傑作。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DELIVERED", "配達人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「17 セブンティーン」のG・ファーランド監督が手掛けたサスペンス・スリラー。何事にもツキがない若者が、ピザの宅配で殺人行為が行われている家を訪問。何も知らないまま殺人鬼と接触した彼が、巧妙な罠にはまり連続殺人犯に仕立てられ、元恋人の命まで狙われていく過程を描き出す。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Breach", "アメリカを売った男", "", "", "http://image.eiga.k-img.com/images/movie/53389/315x210.jpg?2326", 110, "20年以上に渡り祖国アメリカを裏切り続けた実在のFBI捜査官が逮捕されるまでの2カ月間を描いたサスペンス。FBIの若手捜査官オニールは、上司から新たな任務を言い渡される。それは、トップ捜査官ハンセンの行動を逐一報告することだった……。「アダプテーション」のオスカー俳優クリス・クーパーと「父親たちの星条旗」のライアン・フィリップがハンセンとオニールに扮し、激しい心理戦を繰り広げる。監督は「ニュースの天才」のビリー・レイ。", "2008-03-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜あけ朝あけ", "夜あけ朝あけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "鬼怒川べりの農村。親を亡くした奥山家の子供たちが、農作業に、出稼ぎにと精いっぱい一家を支えていく様子を、四季の美しい風景の中で描いた作品。劇団民芸の第８回自主製作作品で、子役には地元から選ばれた６人の少年少女が扮した。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("球形の荒野", "球形の荒野", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "第二次世界大戦末期、祖国を救うため妻子を捨てて連合軍側と極秘に和平交渉を進め、表向きは任地で死んだことになっていた父と娘の再会までの過程を描きながら、その姿を通して戦争告発に迫った社会派サスペンス。松本清張の原作を忠実に映像化。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("からっ風野郎", "からっ風野郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "主演に三島由紀夫を起用して話題を呼んだ増村保造監督作品。非情なヤクザの世界に身を投じた一人の若者をダイナミックに描く。銃弾を受けてエスカレーターに倒れるラストシーンの三島の熱演が見もの。なお彼は、劇中で自ら作詞した主題歌を歌っている。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La doppia ora", "重なりあう時", "", "", "http://image.eiga.k-img.com/images/movie/55429/315x210.jpg?1623", 95, "ホテルの清掃係として働くスロベニア移民のソニアと、元警察官で今はガードマンのグイドは、あるカップリング・パーティーで知り合い、すぐさま恋に落ちる。しかしある日、グイドがソニアを仕事場の別荘に誘い、そこで2人の人生を一転させる事件が起きてしまう……。新鋭ジュゼッペ・カポトンディの長編デビュー作。ソニア役のクセニア・ラパポルトが2009年のベネチア国際映画祭で主演女優賞を受賞した。イタリア映画祭2010にて上映。", "2010-04-30", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("White Light/Black Rain: The Destruction of Hiroshima and Nagasaki", "ヒロシマ　ナガサキ", "", "", "http://image.eiga.k-img.com/images/movie/34089/315x210.jpg?1424", 86, "「待ちわびる日々」でアカデミー賞ドキュメンタリー短編賞を受賞した日系3世のスティーブン・オカザキ監督が、広島と長崎に投下された原爆をテーマに、25年の歳月をかけて完成させたドキュメンタリー。14人の被爆者と原爆投下に関わった4人のアメリカ人の証言を通し、世界の多くの人々が未だ知らずにいるヒロシマ・ナガサキの真実を映し出すとともに、核拡散の危機が高まりつつある世界に対してその恐ろしさを訴える。", "2007-07-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPIDERSII", "スパイダーズ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "巨大グモの暴走を描いたパニック・スリラー第２作。嵐に遭ったクルージング中の夫婦がタンカーに助けられる。だが、このタンカーは人体を使った生物実験場で、驚異的なスピードで成長する巨大グモが養殖されていた。やがてクモは人間のスキを突いて檻から脱出し、乗組員に次々と襲いかかる。グロテスクなヴィジュアルや限定空間の緊迫感がスリルをあおる。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MIRROR CRACK’D", "クリスタル殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ロンドン郊外の町で起こる謎の連続殺人事件。当局の捜査は袋小路に迷い込んでしまうが、名探偵ミス・マープルの推理は徐々に犯人を追い詰めていく……。「オリエント急行殺人事件」「ナイル殺人事件」に続いて、豪華なキャストが話題となったアガサ・クリスティ原作の一編。", "1980-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROBLEM CHILD2", "プロブレム・チャイルド2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "キッズ・ムービー大流行の中で作られた第２作。今回はジュニアに加えて、新たな問題児トリキシーが登場、大騒動を巻き起こす。ようやく心を通わせたジュニアと養父ベンは、モートビルという町で新たな生活を始める。ところが、やたらと“女”が多い町らしく、彼女たちの色仕掛けにベンはすっかりその気に。学校へ行っても自分より上手のイタズラ娘に悩まされ、ジュニアは何もかも気に入らない。そこで再び得意のイタズラを……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ULTIMO SQUANO", "ジョーズ・リターンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "サマーシーズンを目前にした港町で、ウィンドサーフィンをしていた若者が行方不明になる。作家のピーターはサメの襲撃ではないかと推測するが、町の有力者は耳を貸そうともしない。やがてシーズンが到来し、ウィンドサーフィン・レースが開幕。大会がクライマックスを迎えた時、サメが現れ、血塗られた惨劇が始まる。巨大なサメと人間の戦いを描いた動物パニック映画。", "1980-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOCES DE SOUFRE", "アルプス山麓殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "風光明媚なアルプス山麓の村で、金庫係を殺した現金強奪事件が起こる。ほどなく犯人は事故死するが、盗まれた札束は消えていた。犯人の妻と愛人に疑いがかけられるが、二人は逆に自分たちを追う刑事に疑惑を持ち、探偵を雇う……。犯人の妻と愛人役の女優の美形ぶりが拾いもの。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ポケットモンスター　ダイヤモンド＆パール　アルセウス　超克（ちょうこく）の時空へ", "劇場版ポケットモンスター　ダイヤモンド＆パール　アルセウス　超克（ちょうこく）の時空へ", "", "", "http://image.eiga.k-img.com/images/movie/54309/315x210.jpg?2204", 0, "人気アニメ「ポケットモンスター」の新シリーズ「ダイヤモンド＆パール」の劇場版第3弾。旅を続けるサトシたちは、大昔の神殿の遺跡が残る美しい町・ミチーナにたどり着く。遺跡を訪れたサトシたちは、湖から突然現れた竜巻に襲われそうになるが、ポケモンと心を通わすことのできる遺跡のまもりびと・シーナに助けられる。シーナは、幻のポケモン・アルセウスに祖先が返さなかった“命の宝玉”を返そうとしていたが……。", "2009-07-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・社長太平記", "続・社長太平記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "女性下着メーカー・錨商事は九州支社開業のため、牧田社長がじきじきにやって来た。しかし元来九州はライバル、さくら商会の本拠地。そのうえ、地元の業者を招待した宴会で大森支社長が大失敗を演じ、取引は難航する。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WIZARD OF SPEED AND TIME", "マイク・ザ・ウィザード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "腕は抜群ながら、いまだ無名のSFX映画作家マイクは、TVのプロデューサーとディレクターから、特別番組の制作を依頼される。“一大旋風を巻き起こすような大傑作”のために与えられた時間は、わずか３週間……。コマ撮りを駆使したハンドメイドなSFXが楽しく、圧巻。映画作りに情熱を燃やす人々に捧げたい、愛すべき１本。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アトランタ・ブギ", "アトランタ・ブギ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "無国籍人種のるつぼと化したニッポンの、とある町・轟町。ことの起こりは“毎年恒例の町内運動会に、除け者にされてきた３丁目も参加させよう”という２丁目からの提案だった。そもそも３丁目は、不法滞在者あり、フーゾクあり、いつ何があっても不思議じゃない“はきだめ”の町。町内運動会なんて、見向きもしていなかったが、運動会をネタに賭けをやることを思いつき、その金欲しさに猛特訓を始める。一方、これを機会に３丁目を追い出したい１丁目も、金の力で選手を集めていく。「てなもんやコネクション」以来となる山本政志監督の破天荒な“運動会ムービー”。岸谷五朗、富田靖子、福山雅治などアミューズ所属の50人を超えるゲストも、作品に彩りを添えている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hush… Hush, Sweet Charlotte", "ふるえて眠れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "　ルイジアナの豪邸にひとりきりで暮らす年老いた女性シャーロット・ホリス。邸の取り壊しを頑なに拒む彼女は37年前に起こった猟奇的な殺人事件の犯人と目されていた。その事件以来、シャーロットが精神に異常をきたし、ホリス邸には死体が埋まっているという噂も立つ。そんなホリス邸に、シャーロットの従妹ミリアムが37年ぶりに訪ねてくるが……。「何がジェーンに起こったか？」のスタッフと主演ベティ・デイビスが再び組んだサスペンス・スリラー。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A FISH CALLED WANDA", "ワンダとダイヤと優しい奴ら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "イギリスを代表する笑劇集団モンティ・パイソンの、ブラックで乾いたユーモアと、アメリカらしいスピーディでパワフルなドタバタがドッキング。前例を見ないまったく新しいタイプの無国籍コメディができあがった。世界有数の宝石商業地区ロンドンのハットン・ガーデンズ。ゴージャスな宝石店が強盗に襲われ、1300万ポンドのダイヤが盗まれた。犯人はボスのジョージを筆頭に、ケン、オットー、紅一点ワンダの４人組。ところが、ワンダに惚れ込んでいるオットーが、彼女のためにとダイヤの独り占めを企み、ボスを密告。が、さすがはボス。ダイヤをどこかへ隠して知らぬ顔。ワンダは、ボスの弁護士マーチーに色気で迫り、宝石の在りかを聞き出そうとするが……。美しくセクシーで“翔んでる”女泥棒ワンダを、J・L・カーティスが好演。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEVER TALK TO STRANGERS", "ストレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "若く美しい有能な犯罪心理学者サラは、マックスという猟奇殺人鬼の“鑑定”に張り詰めた日々を送っていた。そんな彼女の前に、見知らぬ男トニーが現れた。危険なものを感じながらも、トニーの妖しさに虜になっていくサラ。めくるめく恋の始まりは、サラにとって恐怖の序奏だった。届けられた不吉な枯れ花。愛猫の箱詰め惨殺体。壁に殴り書かれた死のメッセージ。恐怖の中で、トニーとの愛に燃えるサラだったが、一番疑わしいのもトニーだった。エロティックな要素もふんだんに取り入れた、ヒッチコック映画の伝統を踏まえたサイコ・スリラー。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ethan Frome", "哀愁のメモワール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1９世紀末、マサチューセッツ州の田舎町。病弱の妻との生活に縛られたイーサンは、妻の親戚であるお手伝いの娘と恋に落ちるが、悲劇が待っていた……。イーディス・ウォートンの長編小説「イーサン・フローム」の映画化。", "1993-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLANK GENERATION", "ザ・ブランク・ジェネレーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "ニューヨークのインディペンデント映像作家、A・ポーと、パティ・スミス・グループの一員だったI・クラールの手による、1976年当時のニューヨーク・パンクスのライブドキュメント。当時点在したアンダーグラウンドなロックカフェ“CBGB”やマクシズ・カンザスシティでの、オリジナルのハートブレイカーズの映像などは超貴重なもの。ニューヨーク・パンクスの息吹が聴こえる。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMSTERDAMNED", "アムステルダム無情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「悪魔の密室」でアヴォリアッツ映画祭のグランプリを獲得したD・マース監督の第３作。網の目のように運河の張りめぐらされたオランダの首都、アムステルダムで発生する恐怖の連続殺人事件をミステリアスなサスペンスとして描いていく。ビデオ発売時タイトルは「アムステルダム／怒りの追撃」。", "1988-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUSA", "MUSA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "14世紀に実際に起きた事件をモデルに、歴史から忽然と姿を消した９人の武士たちの行方をフィクションとして描いた歴史スペクタクル。使節団としての任務が失敗し、明から故郷・高麗へ戻る途中の武士たちが、偶然にも明の姫を救ったことで、その運命が大きく変わってしまう。激動の時代に生きた明の姫役チャン・ツィイーの熱演にも注目したい。14世紀、高麗は明との友好関係を築くため、使節団を派遣した。しかし、逆にスパイ容疑をかけられ、交渉は失敗に終わる。使節団のチェ将軍は、故郷・高麗を目指し、残された水と食料を携えて決死の行軍を開始するが……。DVDタイトルは「MUSA—武士—」。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAUF VOTRE RESPECT", "レプスキー危機一髪 ロシア皇帝の秘宝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "製作者S・ゴッビが、英国のハードボイルド作家J・H・チェイスの作品４本を映画化する“チェイス・プロジェクト”の１本。ニースの博物館から、多額の保険金がかけられた聖母像が盗まれた。国際保険会社の調査員レプスキーは、盗んだ犯人、怪盗ブラッドレーの手から聖母像を取り戻そうとするが……。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMMANDER　HAMILTON", "ハミルトン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "奪われた核兵器の謎を追う軍人の奔走を描いたサスペンス・アクション。ロシアの核兵器が過激派テロリストに盗まれた。CIAの特命を受けて、その行方を追うスウェーデン軍特殊部隊の中佐ハミルトンは、核兵器の奪還に成功。すべての証拠を消して任務を終えた彼だったが、本国に戻ると、事件は意外な方向に転がり出していた……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOODY　MALLORY", "ブラッディ・マロリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "赤毛の美しき魔物ハンター、マロリーが怒濤の大活躍を見せつけるバイオレンス・ホラー。日本のアニメや漫画をこよなく愛する新人監督ジュリアン・マニアのこだわりのディテールに注目したい。悪魔崇拝者アバドンの一味が、ローマ教皇を誘拐するという事件を起こす。魔物退治に励む赤毛の美女マロリーは、個性的な仲間を率いてアバドンのアジトに乗り込むが、様々なモンスターの攻撃を受けるハメになってしまい……。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・男の泣きどころ", "喜劇・男の泣きどころ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "仕事のしすぎで男が立たなくなった中年男が、美容体操をしているストリッパーに若き日のまぶしい女学生のブルマー姿を見出し、見事回復していくまでを描いた奮闘物語。女房・春川ますみによるあの手この手の回復作戦が涙ぐましい。喜劇一筋、瀬川の39本目。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霧丸霧がくれ 南海の狼", "霧丸霧がくれ 南海の狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "角田喜久雄の原作による「霧丸霧がくれ」ものの第２部。追手を逃れた二出川霧丸と浪江は轟右門によって捕らえられてしまう。霧丸は浪江を奪い返すため、敵方の高柳九十郎の命令に従い黒観音を盗みだすが、そのために浪江は、逆に危機にさらされることに……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMANOFF AND JULIET", "びっくり大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "政治や博愛主義よりも恋愛と笑いを信じる欧州の小国コンコーディアの大統領。彼の国は独立以来492年もの間、他国の援助なくしては存続し得なかった超貧しい国だったが、突然、米国、ソ連の二大大国が援助を申し入れてくる。それは国連で真っぷたつに割れた票を自国に有利にするための誘惑だった。ユスチノフの自作コメディの映画化。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お好み八ちゃん", "お好み八ちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "広島でお好み焼き屋を切り盛りしている八ちゃんはドジでのろまで要領の悪い青年。勝ち気でわがままな恵美はそんな人の良い彼を自分の楽しみのために利用しようとする。周囲の人間は二人の関係をハラハラしながら見守るが……。『はだしのゲン』で知られる漫画家、中沢啓治が自作の同名漫画をもとに、生まれ故郷の広島を舞台にして初監督に挑んだ人情劇。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Linewatch", "バトル・ライン", "", "", "http://image.eiga.k-img.com/images/movie/54589/315x210.jpg?4037", 90, "キューバ・グッティング・Jr.が主演するアクションドラマ。テキサスで国境警備員を勤めるマイケルは、真面目で家庭的な人柄で周囲から親しみと尊敬を集めていた。しかし、マイケルはギャングの一味だった過去を持っており、昔の仲間から麻薬密輸に手を貸すよう脅迫される。家族を人質に取られ、自身も殺されかけたマイケルは、組織を壊滅すべく反撃に出る。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAGNIFICENT SEVEN", "荒野の七人", "", "", "http://ecx.images-amazon.com/images/I/51AcNkMzF2L._SL210_.jpg", 128, "黒澤明監督の「七人の侍」に惚れ込んだブリンナーが翻訳権を買いとり、舞台をメキシコに設定して映画化した西部劇の快作。メキシコの寒村イストラカンは、毎年野盗に襲われ続けている。そこで村の長老は助っ人を雇うことにした。７人のガンマンたちが集まり、彼らは村人たちに射撃の練習をさせ、隠し棚を作り、道には溝を掘らせて野盗一味の襲撃にそなえた。やがて野盗は40人の手下を率いて現れるが、思わぬ農民たちの反撃にあい、部下の半数の死体を残して命からがら逃げ帰った。その後、何度かの攻防が続き、結局３人のガンマンが生き残った。リーダーのクリスがつぶやく、“勝ったのは俺たちじゃない。百姓だよ”と。７人の個性あふれるガンマンぶりが話題を呼び、なかでもマックィーンのカッコ良さは群を抜いていた。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANDINGO", "マンディンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "奴隷問題の真実に迫るカイル・オンストットのベストセラーを映画化した社会派ドラマ。1808年の奴隷輸入禁止令により、奴隷を牧場で飼育して売買し始めたアメリカ南部。そのなかの、ある一家を舞台にして斜陽化する白人と権利に目覚める黒人階級の葛藤、人種を超えた男と女の愛や憎しみなどを、刺激的な場面の連続の中に大スケールで描いている。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ落語　落語研究会　昭和の名人", "シネマ落語　落語研究会　昭和の名人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2010-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("半分の月がのぼる空", "半分の月がのぼる空", "", "", "http://image.eiga.k-img.com/images/movie/54629/315x210.jpg?2492", 112, "「60歳のラブレター」の深川栄洋が、橋本紡のベストセラー小説を映画化。主演は「砂時計」「ダイブ!!」の池松壮亮、共演に「守護天使」の忽那汐里、大泉洋ほか。ある日、平凡な高校生・裕一は心臓病を患う少女・里香と出会う。長期入院のためうまく人とコミュニケーションがとれない里香に振り回される裕一だったが、2人は互いに惹かれていく。一方、里香が入院する病院の医師・夏目は、医者でありながら里香と同じ病気だった妻を救えなかったことで、立ち直れずにいた。", "2010-04-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE FROM THE HEART", "ワン・フロム・ザ・ハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「地獄の黙示録」に次ぐコッポラ作品で軽いタッチのハートフルなミュージカル。ストーリーはボーイ・ミーツ・ガール、撮影はすべてセットとハリウッド回帰を思わせる一方、ビデオをフィルムのサブとして使うなど実験的な面も。ディーン・タブラリスによるラスベガスのセットが美しい。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LANGITKU RUMAHKU", "青空がぼくの家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ジャカルタのスラム街に住むグンポルは貧しいながらも心優しい人々に囲まれて暮らしている。一方、高級住宅街に住むアンドリは母がなく、会社社長の父も仕事に明け暮れるばかり。異なる環境で育ったこの二人は、ふとしたことから知り合い、次第に友情を深めていく。ある日、グンポルの住むスラム街が不法占拠という理由で撤去され、グンポルの両親も収容されてしまう。家も学校も嫌になったアンドリは、ぼう然とするグンポルと二人、グンポルの祖母を訪ねる旅に出るが……。貧富の差を越え友情を結ぶ二人の少年を描いた心温まる物語。ベルリン映画祭児童映画祭ユニセフ賞、ナント三大陸映画祭ジャック・ドゥミ賞などを受賞したインドネシアのS・R・ジャロット監督作品。", "1989-01-01", "73", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("てんやわんや", "てんやわんや", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "当時の新聞連載小説の人気作家・獅子文六と渋谷実監督との初顔合わせ。気の弱い会社員が社長の命によって四国へ出張するが、そこでは四国独立運動が起こっていた……。新人・淡島千景がキャリアウーマン的魅力を発揮。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Supercross", "スーパークロス", "", "", "http://image.eiga.k-img.com/images/movie/33669/315x210.jpg?4131", 82, "アメリカで人気の屋内型バイク競技“スーパークロス”の世界チャンピオンを夢見る兄弟レーサーの奮闘を描いた青春バイク映画。モトクロス選手の経験もあるスティーブ・ボーヤム監督が、スーパークロスの魅力をスクリーンに再現。タイラー・エバンス、ジェイムス・スチュワートをはじめ、第一線で活躍するライダーも多数出演している。実際のレース会場で撮影された、トップ・ライダーたちによる豪快で華麗なテクニックの数々は迫力満点。", "2006-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Vacances De Monsieur Hulot", "ぼくの伯父さんの休暇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ジャック・タチの長編第2作で、タチの代表的キャラクター、ユロ氏が初登場する風刺コメディ。日本では本作に次ぐシリーズ第2作「ぼくの伯父さん」の方が先に公開されたため、この邦題がつけられた。大勢の人々が海へと繰り出すバカンスの季節。ポンコツ車で海辺のホテルへやって来たユロ氏が、ホテルの従業員や他の宿泊客たちを巻き込んで、行く先々でドタバタ騒動を繰り広げる。カンヌ国際映画祭国際批評家大賞を受賞。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・あゝ軍歌", "喜劇・あゝ軍歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "無駄死にしたくないので、気のふれた真似をして陸軍の野戦病院に入り、そこで終戦を迎えた勝造とカトやんは、今も同じ会社に勤める良き友だった。その勝造の部屋に婆さん、未亡人、少女、ヒッピー風の男などが転がり込んでテンヤワンヤとなる。随所に軍歌を挿入しながら、世代間の断絶をコメディ・タッチで描く。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("音楽座ミュージカル　メトロに乗って", "音楽座ミュージカル　メトロに乗って", "", "", "http://image.eiga.k-img.com/images/movie/53709/315x210.jpg?3416", 171, "2000年に初演された浅田次郎原作の音楽座ミュージカルを高画質映像でスクリーン上映。戦後の闇市から大企業を起こした独裁的な父を嫌い、会社を継がずに家を飛び出した中年営業マンの真次は、母と妻と子どもと生活は苦しかったが幸せな暮らしを送っていた。そんなある日、地下鉄で元教師の野平に出会い……。2008年5月10日に劇場初公開。", "2008-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD DOG COLL", "マッド・ドッグ・コール 暗黒街の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "実在のギャングをモデルに暗黒街を鮮烈に描いたアクション映画。禁酒法時代、ヴィンセント・コールと弟ピーターは10代の頃から、マフィアのボス、ダッチ・シュルツのもとで働いていた。やがて“マッド・ドッグ”の名で実力をつけていったヴィンセントは、シュルツの縄張りを荒らし始める。怒ったシュルツはヴィンセント殺害を計画。それに対してコール兄弟はボスからの独立を決心するが……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キネマの天地", "キネマの天地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "松竹が大船撮影所50周年記念として、全力をあげて取り組んだ大作。松竹蒲田撮影所が全盛だった大戦前後の時代を背景に、映画製作に夢を燃やす人々の熱い情熱を山田洋次監督がオールスター・キャストで映画化。特にこの作品で幸運なデビューを飾った有森也実が、初々しい魅力を放っている。浅草の活動小屋で売り子をしていた小春は松竹の監督に見出され、大部屋女優となった。演技経験のない小春を助監督の島田は一生懸命盛りたて、一人前の女優に育てようとする。大作の主役に抜擢された小春は監督の厳しい指導に耐え女優として成功するのだった。激動の時代を生きた映画人の讃歌ともいえる作品。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REVENGE OF DEATH", "リベンジ・オブ・デス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「キックボクサー」で格闘技映画ブームを作り、ジャン・クロード・ヴァン・ダムを世に放ったD・ワース監督が作った女性版のマーシャルアーツ・アクション。主演のC・ラスロックは、本場の香港映画界で活躍した元全米女子空手チャンピオン。本作では、夫の敵に復讐をするCIAの秘密諜報員という、“いかにも”それらしい役どころ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昼下りの情事 古都曼陀羅", "昼下りの情事 古都曼陀羅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "銀行の新入社員・峰雄は上司のすすめで見合いをした。相手は老画家の養女・みな子で不思議な魅力のある女だった。峰雄はたちまち一目惚れしてしまい、誘われるままに彼女を抱いてしまう。父娘相姦と若者の恋を美しい京都の風景の中にしっとりと描く。サド・マゾ性倒錯の世界が、小沼監督独自の耽美センスのもとに結実している。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BACK IN ACTION", "バック・イン・アクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ロス市警は、最も凶悪な麻薬組織カサジャン・ギャングを潰そうと、潜入捜査を行っていたが、激しい銃撃戦の末、警察側に多数の死傷者を出して作戦は失敗。この時にパートナーを失った刑事ロッシは復讐を誓う。一方、特殊部隊の元メンバー、ビリーは抗争に巻き込まれて行方不明となった妹を捜し出すため、組織に近づこうとしていた。二人の男がともに、巨大組織に立ち向かっていく姿を描いたハード・アクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青空娘", "青空娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "増村保造の監督第２作。源氏鶏太の連続ラジオ・ドラマをもとに、祖母の死によって初めて知らされた実の両親の家へ向かった娘が、様々な虐待にあいながらも明るく生きていく姿を描く。若尾文子の、さわやかでみずみずしい魅力がひときわ光る、はつらつとした青春映画の佳作。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セイジ　陸の魚", "セイジ　陸の魚", "", "", "http://image.eiga.k-img.com/images/movie/56119/315x210.jpg?2540", 108, "俳優・伊勢谷友介の「カクト」（2003）以来8年ぶりとなる監督第2作。大学最後の夏休みに自転車で一人旅をしていた“僕”は、国道沿いの寂れたドライブイン「HOUSE475」で不器用だが純粋に生きる男セイジに出会う。成り行きで店を手伝うことになった僕は、物静かなセイジの話す言葉に影響を受け、人生について考えをめぐらせていく。しかし、そんなある日、小さな町で凄惨な事件が発生し……。ドライブインの雇われ店長セイジを西島秀俊、大学4年生の僕を森山未來が演じる。", "2012-02-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CIRCULEZ，Y’A RIEN A｀ VOIR！", "愛しのエレーヌ　ルルーとペリシエの事件簿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "凸凹刑事コンビのルルーとペリシェは、主に小切手帳の盗難事件を担当している。そんな二人はある日、画廊を経営している未亡人エレーヌ・デュベルネと出会う。瞬間的にエレーヌに一目惚れしたルルーは、何かと口実を見つけては彼女に接近するが、なかなかうまくいかない。それでもあきらめきれないルルーだったが、そのうち、彼女が仕事のパートナーのマルクと共謀して、絵画の不正売買をしていることを知ってしまう。人気監督P・ルコントが撮り上げた作品で、1996年に日本初公開された。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("からっぽ", "からっぽ", "", "", "http://image.eiga.k-img.com/images/movie/57989/315x210.jpg?3300", 117, "群馬県桐生市を舞台にしたご当地映画。監督は同市出身で、NHK-BSなどでドラマ演出を手がけた経験のある弱冠27歳の若手・草野翔吾。存在感のまったくない高校生・加藤小判は、誰からも存在を意識されていないときのみテレポートできる能力を持っている。しかし、その能力には、テレポートした先で全裸になってしまうという欠点があった。ある日、家を追い出された小判は、誰もいないはずのテレポート先でシーナという女性に出会う。小判の能力に興味をもったシーナもまた帰る家がなく、2人の奇妙な共同生活が始まる。主人公の小判を演じるのは、「告白」などに出演経験のある清水尚弥。ヒロインのシーナ役に「20世紀少年」の平愛梨。", "2012-06-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Show Must Go On", "優雅な世界", "", "", "http://image.eiga.k-img.com/images/movie/54119/315x210.jpg?3356", 112, "「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。「シークレット・サンシャイン」のソン・ガンホ主演の暴力団員の葛藤を描いた人間ドラマ。愛する家族との平穏な暮らしを夢見るヤクザのイングだが、仕事に精を出すほどに妻と娘から疎まれてしまう日々を送っていた。そんなとき、組織にナンバー2であり幼馴染のサンムと関係が悪化し……。", "2008-11-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT RHYTHMS", "ナイト・リズム セクシーDJ殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "人気DJが、オンエア中に殺人事件の犯人に仕立てあげられ、仕組まれた罠を解明していくセクシー・サスペンス。ラジオのSEXトークを担当するニックに、ある日災難がふりかかる。放送中に訪れた女性がニックを誘惑。DJブースの中で快楽に酔いしれるが、なんとオンエア中。リスナーたちに届く甘美なあえぎ声は、殺気を帯びた苦しげな声に変わる。ニックが気付いた時には、その横には女の死体が倒れているのだった。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暖春", "暖春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "故・小津安二郎が生前親しかった里見惇と共作した原作を、大船調の後継者・中村登が脚色・監督した。京都・南禅寺界隈で小料理屋を営む母と、婚期に差しかかった娘をめぐるホームドラマ。母の望む男と結婚するまでの様々な葛藤と和解を描く。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("先生あした晴れるかな", "先生あした晴れるかな", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "私立中学受験のため塾に通い始めた５年生の太と、両親が離婚し母親と二人暮らしの圭二が突然荒れだす。担任の加藤先生は二人の心をつかもうと努力するのだったが……。教育映画を撮り続ける中山節夫の監督作品。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージーだよ・奇想天外", "クレージーだよ・奇想天外", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "谷啓をメインにしSF的要素を加味。α星長官は、地球の原水爆実験やロケット打ち上げをやめさせるべく、SFと監視役の零八を地球に送り込む。M7は日本人・鈴木太郎に乗り移るが、失敗の連続。谷啓が小市民的な宇宙人を怪演する。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bug", "BUG バグ", "", "", "http://image.eiga.k-img.com/images/movie/1899/315x210.jpg?2025", 102, "息子を亡くし心に深い傷を負ったアグネスは、仮釈放中の元夫ジェリーによる暴力から逃れるためモーテル暮らしをしていた。そんなある日、彼女は友人からピーターという男を紹介される。お互い辛い過去を持つアグネスとピーターは次第に心を通わせていくが、やがて部屋の中に存在する“バグ（虫）”に悩まされるようになり……。「エクソシスト」「フレンチ・コネクション」の名匠ウィリアム・フリードキン監督が放つ衝撃スリラー。", "2008-07-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Land of the Dead", "ランド・オブ・ザ・デッド", "", "", "http://image.eiga.k-img.com/images/movie/1109/315x210.jpg?4823", 93, "68年の「ナイト・オブ・ザ・リビング・デッド／ゾンビの誕生」、78年の「ゾンビ」、85年の「死霊のえじき」で＜ゾンビ＞という存在を世に産み出したジョージ・A・ロメロ監督による、ゾンビ映画。ゾンビたちの存在があたりまえになった世界。街の支配者に雇われて食料を調達してきたライリーとチョロは、街に溢れるゾンビたちにある異変が生じたことに気づく。特殊メイクはトム・サビーニの弟子、グレッグ・ニコテロが担当。", "2005-08-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彼女について知ることのすべて", "彼女について知ることのすべて", "", "", "http://image.eiga.k-img.com/images/movie/57719/315x210.jpg?1292", 105, "「永遠の1/2」「リボルバー」などの映像化作品でも知られる作家・佐藤正午の小説を、「行旅死亡人」の井土紀州監督が映画化。とある地方都市を舞台に、性や暴力を交えて男女の切ない絆を描くラブストーリー。何不自由ない平凡な暮らしを送り、同僚の美千代との結婚も考えていた教師の鵜川の前に、美千代の学生時代の後輩メイが現れる。美千代はメイに対して嫌悪感をあらわにするが、逆に鵜川はメイに興味を抱く。しかし、何もかもが美千代とは正反対で、暗い影が付きまとい謎めいたメイにひかれたことから、鵜川の日常は狂い始めていく。元「ブラン娘」（TBS系「王様のブランチ」レポーター）の笹峯愛が映画初主演。", "2012-05-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刺青〈SI−SEI〉", "刺青〈SI−SEI〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "耽美主義を貫いた作家・谷崎潤一郎の同名小説を映画化。これまで２度映画化されているが、今回は舞台を現代に置き換え、めくるめく倒錯した愛と美の世界が展開する。刺青に魅せられ、数奇な運命に翻弄される男女の物語を“ピンク四天王”の佐藤寿保監督が官能的で繊細な色合いに仕上げている。彫り物師の来栖は美妙という美しい女性の背中に、最高の作品を彫ることを決意する。彼女を誘拐・監禁した来栖は、見事な八重垣姫を美妙の背中に彫り上げるが……。", "2006-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無宿者", "無宿者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "５年前、笹子峠で父を惨殺され、その形見のドスを持って犯人を探して旅をする一本松と、同じく５年前、笹子峠で御用金を持って姿を消した父を探す黒木弥一郎。佐渡金山で二人が出会い、真犯人を探し出していくまでを描いたヤクザものの時代劇アクション。スピード感と映像美が秀逸な三隅監督の傑作。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Osterman Weekend", "バイオレント・サタデー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "R・ラドラム『オースターマンの週末』の映画化。TVキャスター、タナーの友人３人がソ連KGBと組んでスパイ活動をしているとにらんだCIAは、その実態を探るため、彼に協力を依頼するが……。現代のエレクトロニクス・テクノロジーを駆使した米・ソのスパイ戦争をテンポよくまとめたペキンパーの遺作。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Toy Story 2", "トイ・ストーリー2", "", "", "http://ecx.images-amazon.com/images/I/51GOQi6eOZL._SL210_.jpg", 92, "アンディの部屋のおもちゃ−カウボーイ人形のウッディとスペースレンジャーのバズは、今日も仲間と楽しい時間を過ごしていた。ところがある日、バザーに売りに出されたペンギン人形のウィージーを助けに行ったウッディが、通 りすがりの男に盗まれてしまう。ウッディ略奪犯、それはおもちゃ屋のアルという男だった。バズたち仲間は親友の救出を計画、アルのおもちゃ屋に向かう。一方アルのマンションに連れてこられたウッディは、自分がプレミア人形であることを知る。", "2000-03-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POTOMOK BYELOGO BARSA", "白い豹の影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "旧ソ連邦キルギス共和国の雄、オケーエフが、祖国の民族色豊かな作風を貫きながら、自然と人間の共存を鋭く突きつけた話題作。白銀の天山山脈の偉容、人間を飲み込む大雪崩の迫力、大地を暴走するサイガ（カモシカの一種）の大群など、息をのむスペクタクルが随所に織り込まれている。", "1984-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD FEAST", "血の祝祭日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "自らを高僧の生まれ変わりと信じるエジプト料理の材料店主は、次々と若い女を惨殺し、その臓物を捧げる。スプラッタ・ムービーは、まさにH・G・ルイスのこの一作から始まったとされている記念すべき作品。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OTELLO", "オテロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "シェイクスピアの４大悲劇の一つに数えられる有名な脚本を作曲家G・ヴェルディが歌劇化した『オテロ』。オペラ史上の名作として名高いこの歌劇をF・ゼフィレッリ監督が映像化。物語は、15世紀、ヴェネチア共和国のキプロス総督オテロが、部下の奸計にはまって愛する妻を殺してしまうというもので、南イタリアの城やクレタ島の要塞など、全編の９割がロケーションとあって、スケールの大きな映像も見どころの一つとなっている。オーケストラとコーラスはミラノ・スカラ座、指揮をロリン・マゼールが務め、オテロ役はゼフィレッリ監督の「トラヴィアータ−1985・椿姫」にも出演していた世界的テノール歌手、P・ドミンゴ。", "1987-01-01", "4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青い春", "青い春", "", "", "http://image.eiga.k-img.com/images/movie/1259/315x210.jpg?1798", 83, "阪本順治監督の「ビリケン」で脚本を担当、「ポルノスター」(98)で監督デビューした豊田利晃の新作は、漫画家、松本大洋の短編集「青い春」からの4編をもとに、漫画誌編集者時代から原作者と交流のあった監督が脚本も執筆、男子高校の2年生4人のタイトル通りの日々を描く。故・松田優作を父に持つ松田龍平、「バトル・ロワイアル」の高岡蒼佑、人気モデル、大柴裕介ら新鋭俳優が集合。挿入歌はミッシェル・ガン・エレファント。", "2002-06-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXOTICA", "エキゾチカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "欧米ではすでに高い評価を得ているカナダの鬼才A・エゴヤンの本邦初公開作。ナイトクラブに集う人々の人間模様が、サスペンスフルな語り口で描かれる。登場人物は、クラブのDJと、その元恋人のダンサー、彼女を指名する常連の税務調査員、そしてペットショップを経営するゲイの青年。悲しい過去で結ばれた彼らの関係が、映画の進行にしたがってパズルを埋めるように浮かび上がっていく。", "1994-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武士道残酷物語", "武士道残酷物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "自己を犠牲にしてまで主君に仕える日本人の被虐的精神構造を、江戸時代から現代までの７つのエピソードで描くオムニバス映画。第13回ベルリン映画祭で金熊賞を獲得した。物語は現代に生きるサラリーマン・飯倉が、ダムの入札をめぐって上役から競争会社の情報を盗むように言われ、スパイをしたその恋人が自殺未遂をするところから始まる。映画はそこから飯倉の先祖にさかのぼり、主君や国家のために犠牲になって死ぬ飯倉家七代の残酷な歴史を綴っていく。藩主の落度を被っての切腹に始まり、殉死、不義密通の濡れ衣で男根を切られたり、老中に娘を献上したり、戦争で死んだりと、異様なエピソードの数々に日本人の原形が重ね合わされる。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Anges Exterminateurs", "はじらい", "", "", "http://image.eiga.k-img.com/images/movie/53159/315x210.jpg?1992", 100, "前作「ひめごと」のオーディションを巡るセクハラ訴訟で有罪判決を受けたフランスの異才ジャン＝クロード・ブリソー監督が、官能映画オーディションの内幕を明かした問題作。映画監督のフランソワは、“タブーと歓び”をテーマにした新作のオーディションを行う。集まった女優たちにフランソワが出した要求は、カメラの前で興奮を感じること。あまりにも大胆な要求に呆然とする者が多い中、3人の女優がフランソワの目に留まり……。", "2007-12-22", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聖家族　大和路", "聖家族　大和路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "堀辰雄の代表作「聖家族」と「大和路」を、お笑いコンビ「ラーメンズ」の片桐仁主演で映画化。監督は「二重心臓」「斜陽」の秋原正俊。画家の河野は、師匠・九鬼を亡くしたショックで自暴自棄になり、絵が描けなくなってしまう。そんなある日、新作のために滞在していた奈良で、九鬼の内縁の妻・細木と娘の絹子に出会う。九鬼の聖域に踏み込んだことでますます苦悩する河野は、さまざまな女性との出会いで人生を見つめ直していく。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WOMAN ON TOP", "ウーマン・オン・トップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「オール・アバウト・マイ・マザー」のP・クルス主演のキュートなラブ・コメディ。料理が得意な美しい人妻イザベルは、夫の浮気現場を目撃したことにショックを受け故郷を離れることに。親友のいるサンフランシスコで料理学校の講師として働き始めるが、テレビの料理ショーに出演した彼女は一躍、人気者に。そんな時、イザベルを追って夫がやって来てしまい……。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("楳図かずお恐怖劇場　まだらの少女", "楳図かずお恐怖劇場　まだらの少女", "", "", "http://ecx.images-amazon.com/images/I/41M4TXQX5VL._SL210_.jpg", 60, "2005年にデビュー50周年を迎えたホラー漫画家・楳図かずおの短編6作品を実写映画化したオムニバス「楳図かずお恐怖劇場」の1編として、「恋する幼虫」の井口昇監督が手がけたサスペンスホラー。連休に母の故郷・美土路村にやって来た弓子は、同じ年のいとこ・京子と久々に再会する。しかし、なぜか村人たちは弓子を「ヘビ女」と忌み嫌い……。主演は「下弦の月　ラスト・クォーター」の中村有沙と、テレビドラマ「瑠璃の島」で注目を集める成海璃子。", "2005-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER SCHARLACHIROTE BUCHSTABE", "緋文字", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ホーソンの名作小説『スカーレット・レター』を、ドイツの名匠ヴェンダース監督が緻密に撮り上げた文芸ヒューマン・ドラマ。監督の才気がほとばしる初期の問題作。17世紀のアメリカ西海岸へ、イギリスから移住してきた若きヒロイン。そこは清教徒の町で戒律が重んじられていた。彼女は姦通を意味する字を縫った服を着せられ不義の子を育てたが、住民の非難が集中する。", "1972-01-01", "7,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COSI", "ハーモニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "モーツァルトのオペラ『コシ・ファン・トゥッテ』をモチーフとしたエキセントリックなコメディ。大学を中退してブラブラとしている青年ルイスが、精神病院の患者が上演する舞台の演出を請け負うことに。患者たちの奔放な振る舞いに四苦八苦するルイスだったが、オペラを上演したいという無謀な希望を聞き入れるうちに、彼らを本気でサポートしようと決意するが……。脚本のL・ノーラが自らの体験をもとに書いた舞台劇の映画化。突飛な行動を連発する患者たちがもたらすユーモアと、彼らと主人公がはぐくむ友情が丁寧に描き込まれ、観る者の共感を呼び起こす。クライマックスの演劇シーンでは、きらびやかな美術と照明が魅惑的な映像の“ハーモニー”を奏でており、特にT・コレット扮する麻薬中毒者ジュリーが『スタンド・バイ・ミー』を歌うシーンは感動的。", "1996-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRETTY MAIDS ALL IN A ROW", "課外教授", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ある女子高を舞台に、次々と発生する殺人事件を描いたロマンティック・ミステリー。犯人は意外なことに、学校で生徒指導を担当するハンサム・コーチだった……。R・ヴァディムがアメリカに渡って撮った、お色気たっぷりの作品。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鼠小僧次郎吉", "鼠小僧次郎吉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "大佛次郎の原作を新藤兼人が脚色し、三隅研次が監督した、正義の味方的な時代劇。怪盗鼠小僧は大名や豪商を襲い、奪った金品を貧乏人にばらまき、庶民の英雄として人気を集めていた。江戸の暗黒街を牛耳る安五郎は鼠小僧に煮え湯を飲まされたので復讐を企てる。林与一が二役で活躍。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("残菊物語", "残菊物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "軍国主義への協力を嫌った名匠・溝口健二が活路を見出した“芸道もの”の佳作。明治初期の歌舞伎界を舞台に、芸の未熟な御曹子が、彼を愛する女性の献身的な内助と、旅回りの苦労の末に栄光をつかむ。この作品で溝口はワンシーン＝ワンカットを完成させた。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L.A. BAD", "吐きだめの疾走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "不法入国者のため、警察からも迫害される不良少年ニコス。麻薬売買や盗みを繰り返した後、覆面パトカーの無線を盗もうとして暴行され、病院送りになるが……。「ラ★バンバ」でリッキー・バレンスの兄役を演じたE・モラレス主演。ロサンゼルスを舞台に描いた衝撃の青春映画。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUMP INTO HELL", "地獄へ突込め", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ベトナム戦争前夜のインドシナ奥地を舞台に、フランス軍とホー・チ・ミン軍との戦いを描いた戦争アクション。1954年３月、ディエンビエンフーのフランス軍は赤軍の応援を得たホー・チ・ミン軍によって包囲されていた。歴戦の勇者を抱えるフランス軍だったが、必死の応戦もむなしくディエンビエンフーは陥落してしまう……。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青い鳥", "青い鳥", "", "", "http://image.eiga.k-img.com/images/movie/53949/315x210.jpg?4482", 105, "重松清の同名短編小説を、阿部寛主演で映画化した人間ドラマ。いじめによる自殺未遂があった中学校を舞台に、臨時派遣教師として赴任してきた吃音の村内先生（阿部）と、自殺未遂の生徒がいたクラスの生徒たちとの心の交流が描かれる。監督は吉田喜重、原田眞人、長崎俊一らの作品で助監督を務め、本作が長編映画デビュー作となる中西健二。共演は伊藤歩、「シルク」「テニスの王子様」の本郷奏多。", "2008-11-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOTEL FLOWERS ホテル・フラワーズ", "HOTEL FLOWERS ホテル・フラワーズ", "", "", "http://image.eiga.k-img.com/images/movie/57119/315x210.jpg?3931", 0, "人気グラビアアイドルの原幹恵と森下悠里がダブル主演を務める異色ドラマ。お嬢さま育ちの美薗が1人で管理するリゾートホテルにある日、ヤクザ風の男、山下とその愛人ヨーコがやってくる。山下は2階の全フロアを借り切って身代金誘拐を画策していたが、その途中でヨーコが高熱を出して倒れてしまう。美薗がヨーコを看病し、それがきっかけで2人の仲は急接近。ヨーコは美薗を大胆に変身させようとするが……。", "2011-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Seems Like Old Times", "昔みたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "エリート検事の現夫と、どこか憎めない売れない作家の前夫の間で、ドタバタと大騒ぎを繰り広げる、G・ホーン演じる女性弁護士。N・サイモンの筆先もなめらかなロマンティック・コメディ。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lars and the Real Girl", "ラースと、その彼女", "", "", "http://image.eiga.k-img.com/images/movie/53409/315x210.jpg?2356", 106, "「きみに読む物語」「16歳の合衆国」のライアン・ゴズリングが主演。第80回アカデミー脚本賞にノミネートされた、内気な青年とその周りの人々を温かく描くハートフルドラマ。監督は新進気鋭のCM作家、クレイグ・ギレスピー。雪が降り積もる田舎町、優しくて純粋なラースは町の人気者だが、ずっと彼女がいないために家族は心配していた。そんなある日、ラースが「彼女を紹介する」と兄夫婦のもとにやってくる。しかしラースが連れてきたのは等身大のリアルドールだった……。", "2008-12-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OVER THE EDGE", "レベルポイント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "カリフォルニアの新興都市を舞台に、大人に反抗する少年たちを描く辛口の青春映画。中学生たちによるパトカー射撃事件を機に、警察は少年たちの溜り場である娯楽センターを閉鎖し、反抗的な彼らを力で押さえ込もうとするが……。この作品で映画デビューを飾ったM・ディロンが、ひねた不良少年を好演。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪魔がきた", "悪魔がきた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "", "2011-01-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tales of the Rat Fink", "ラット・フィンク　ボクのビッグ・ダディ", "", "", "http://image.eiga.k-img.com/images/movie/53969/315x210.jpg?4625", 76, "アメリカでカルト的人気を誇るねずみのキャラクター“ラット・フィンク”の生みの親、エド・ロスの伝記映画。エド・ロスは俗にビッグ・ダディとも呼ばれる、1950～60年代のアメリカ西海岸を代表するアメリカン・ポップ・カルチャーのアーティストである。彼の刺激的な人生とその作品群を、当時の時代背景を交えてポップに描く。2006年トロント国際映画祭正式出品。", "2008-11-22", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRIMES OF PASSION", "クライム・オブ・パッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "現代のロサンゼルス。流行の最先端をいくファッション・デザイナー、ジョアンナには昼と夜の、まったく違う二つの顔があった。様々な客とあらゆるスタイルのセックスにふける娼婦“チャイナ・ブルー”が彼女の夜の姿である。雇い主の誤解から、ファッションの重要企業秘密を外部に漏らしていると疑われた彼女は、調査に来た私立探偵に、秘密の二重生活を知られてしまう。妻との不仲から、ジョアンナを抱く私立探偵ボビー。二人の間に流れる感情は、いつしか愛に変わっていくのだが……。K・ターナーがジョアンナに扮し、都会の闇に潜む男女の欲望を大胆に描いた異色のラブ・ストーリー。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FINAL CONFLICT", "オーメン 最後の闘争", "", "", "http://ecx.images-amazon.com/images/I/51teuGllxuL._SL210_.jpg", 108, "ダミアン・ソーンは32歳になっていた。今やソーン産業という大企業のトップにいるばかりか、合衆国大統領からも一目置かれるようになった彼は、大統領の直々の指名で駐英大使としてイギリスに赴く。実はそこでは『ヘブロンの書』に基づく救世主再臨のときが、今まさに訪れようとしていた。それを察知したダミアンは救世主を亡きものにしようと、再臨の日に生まれたロンドンの赤ん坊をすべて抹殺しようとするが……。大ヒット・シリーズの第３作では、これが長編映画デビューとなったG・ベイカーが演出を担当。どこか英国風の格調をたたえながら、悪魔の子と救世主の最後の戦いを描いた。ダミアンを演じたサム・ニールも撮影当時32歳。ジェームズ・メイスンの推薦で大役に抜擢され、以後、国際スターの道を歩んだ。大編成の管弦楽と合唱を盛り込んだJ・ゴールドスミスの音楽もシリーズ最高のスケール感を醸して迫力いっぱい。", "1981-05-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("侍戦隊シンケンジャーVSゴーオンジャー　銀幕BANG!!", "侍戦隊シンケンジャーVSゴーオンジャー　銀幕BANG!!", "", "", "http://image.eiga.k-img.com/images/movie/55139/315x210.jpg?1532", 0, "「ガンマンワールド」でガイアークの害統領・バッチードと激戦を繰り広げていたゴーオンジャーは、バッチードの罠により、それぞれ異次元ワールドへと飛ばされてしまう。「ヒューマンワールド」へ戻ってきたゴーオンレッドとボンパーは、そこで侍戦隊シンケンジャーに出会う。初めは対立しながらも、すべてのワールドの征服をもくろむバッチードの野望を阻止するため、シンケンジャーとゴーオンジャーは最強タッグを組む。", "2010-01-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Meaning of Life", "人生の意味", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 12, "", "2005-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裸女と拳銃", "裸女と拳銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "鈴木清太郎（清順）監督による“裸女”白木マリの肉体を前面に押し出した悪女ものの和製フィルム・ノワール。麻薬王を追うカメラマン、槙は女の罠にはまり、殺人犯として逮捕される。やがて釈放された槙の前に現れたのは殺し屋と謎の新興宗教団体、そして彼を罠にかけた女と瓜二つの女だった。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘密結社　鷹の爪 THE MOVIE 3 http://鷹の爪.jpは永遠に", "秘密結社　鷹の爪 THE MOVIE 3 http://鷹の爪.jpは永遠に", "", "", "http://image.eiga.k-img.com/images/movie/54839/315x210.jpg?2977", 104, "人気のFLASHアニメ「秘密結社　鷹の爪」を映画化したシリーズ第3弾。シリーズ全作を通じ、監督・脚本をFROGMANが務める。実家で羽を伸ばしていた鷹の爪団のメンバーだったが、休暇から戻ってくると秘密基地が何者かの手で荒らされており、レオナルド博士の姿も忽然と消えていた。博士の発明で家賃を払おうと思っていた総統は大慌てで、博士の故郷・テキサスへと向かうことにする。しかしそこで待ち受けていたのは博士ではなく、「24」のジャック・バウアー似のジョン・ジョロリンと名乗る男だった……。「古墳ギャルのコフィー／コフンデレラ」も同時上映。", "2010-01-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渡世人", "渡世人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "東映任侠路線の変化球的極道映画。昭和初期、満州での利権をめぐって正宗一家３代目の塚本は、上毛組組長・藤井の放った刺客に殺され、代貸の新太郎も襲われるが逆に相手を殺してしまい、服役することに。刑務所で刺客の名前を知った彼は、出所後藤井の陰謀のすべてを察知。一方刺客の一人、三上も藤井のやり方に嫌けがさして……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドキュメンタリー・インドの星　マザー・テレサとその世界", "ドキュメンタリー・インドの星　マザー・テレサとその世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "世界最悪の居住地区といわれるインド・カルカッタのスラム街に身を投じ、死を待つ人々のために尽力し続けたマザー・テレサ。インド政府から異例の撮影許可を得て、献身的な活動を続けた彼女とシスター、ブラザーたちの生き方を追いかける。人間の尊厳や愛、生と死、富と貧困を見つめた重厚なドキュメンタリーだ。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NATTVARDSGA¨STERNA", "冬の光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ベルイマンはプロテスタントの上級僧職者の息子だが、この「冬の光」は、僧職に就かなかった彼が、もし家業に就いていたらという仮定のもとに作られた、自伝的要素を含んだ作品。“神の沈黙”といわれる３部作の第２編にあたる。ベルイマン自身が“神”を常に意識しながらも、“僧職”におさまり、偽善的な教えを反復するには至れなかった苦悶が感じ取れる。細心の注意を払ったルンドグレンによる教会内部のセットと、超高感度フィルムを使用して映し出した光の明暗の美しさが出色。", "1962-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DINNER WITH THE VAMPIRE", "バンパイア最後の晩餐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ホラー映画のオーディションに合格した男女４人は、監督の住む古城への招待を受けた。晩餐会の席上、監督は自分が吸血鬼であることを明かし、巨大なコウモリに変身して飛び去った。彼を倒す方法はあるのだろうか？　イタリアン・ホラーの気鋭L・バーヴァが、新解釈の吸血鬼ものに挑戦した意欲作。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOSTRADAMUS", "ノストラダムス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "疫病の蔓延と宗教弾圧に揺れる中世フランスに生まれ、1999年に地球が滅亡するとも読み取れる不気味な予言を残したノストラダムス。多くの謎に包まれた彼の生き様を、大胆な解釈とスペクタクル映像を盛り込んで描いた異色作。悪夢的な未来のヴィジョンに苦悩するノストラダムスを、「ニキータ」のC・カリョが熱演している。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博奕打ち 一匹竜", "博奕打ち 一匹竜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "東映プログラム・ピクチャーで、当時高倉健と二枚看板だった鶴田浩二主演で贈る、大人気シリーズ第2弾。ヤクザ映画の中でも毎回趣向を変え、本作では刺青大会に命を張る渡世人という主人公が登場する。大阪が主舞台。東京で刺青師として修業を積んできた相生宇之吉が、5年ぶりに師匠がいる大阪に帰ってみると、師匠はヤクザ組の非道な仕打ちにあえいでいた。宇之吉は師匠のために一肌脱ぐ。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHALK GARDEN", "ドーヴァーの青い花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "娘が再婚したことが許せず、孫娘ローレルを自分の意のままになるよう育てていた老婦人の屋敷に、ローレルの家庭教師としてマドリガルがやって来る。ローレルは反発心の強い子供だったが、やがてマドリガルの努力は実を結び、ローレルは彼女に対して好意を寄せてくるようになる。やがて彼女は、今まで祖母に聞かされていた、母が自分を捨てた、という話が嘘であることを知る。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("直撃！地獄拳", "直撃！地獄拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "千葉真一主演の空手もの。甲賀忍法の嫡子・甲賀、一匹狼の殺し屋・隼、元合気道師範・桜の３人が国際麻薬シンジケートの壊滅に乗り出す。石井輝男がツボを心得た演出で一気に見せる。元ボクシング世界チャンピオンの西城正三が用心棒役で出演。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BEAUTE´ DU DIABLE", "悪魔の美しさ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「巴里祭」などで知られるフランス映画の名匠クレールが、“ファウスト伝説”を映画化。19世紀初頭、大学教授ファウスト博士は、悪魔の使いであるメフィストに、魂を売る契約書のサインを迫られる。OKした彼は、青年に姿を変え、宮廷に迎え入れられるが……。", "1949-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Little Princess", "マイ・リトル・プリンセス（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　HITLERS　A　FAMILY　PORTRAIT", "ヒトラー家の人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "世紀の独裁者ヒトラーの人物像を、両親、兄弟などの家族・親戚関係に加え、青春時代の様子などを捉えた貴重なドキュメンタリー・フィルム。自らを神だと思い込んだ若かりしヒトラーは家族を必要としなくなりやがて、縁を切ってしまう。若い時分は挫折続きだった。そんな彼を知る人たちの証言はもちろん、当時の数多くの写真や映像を基に、ヒトラー一家の実像と謎に迫っていく。", "2005-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SKETCHES", "ホールド・オン・ミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "３人の青年が若かりし頃の自分たちを語りながら旅をするという、ハートウォーミングな物語。22歳の夏、別々の道を歩んでいた３人は、仲間の一人の婚約パーティーで再会することに。だが婚約とはウソで、白血病に冒された彼が、残り少ない時間を親友たちと過ごすため、二人を呼んだのだった。３人は少年時代の憧れだったカリフォルニアへと旅立つ……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A LEAGUE OF THEIR OWN", "プリティ・リーグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "男たちが戦場に出払った1943年のアメリカで、全米選りすぐりの美女たちで構成された史上初のプロ野球ウーマンリーグが誕生。その前向きさとガッツあふれるプレイで、彼女たちは次第に野球ファンの注目を集め始めるが……。マドンナの盗塁、G・デイヴィスのパワフルな打撃など、ダイヤモンドいっぱいに艶やかなプレイを繰り広げるヒロインたちの姿が美しい。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO MUCH", "あなたに逢いたくて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "画商のアートはグラマーな富豪の美女と成り行きで婚約。だが、彼女の知的な妹リズこそ、アートにとって理想の女性だった。気持ちを抑え切れないアートは髪型を変え、双子の弟を名乗ってリズに接近するが……。A・バンデラスのコミカル演技が楽しめる恋愛劇。軽いプレイボーイと芸術家肌の男という、二つの人格を渡り歩くハメになった、主人公の悪戦苦闘ぶりが笑いを誘う。", "1995-01-01", "1,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIGADOON", "ブリガドーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "アカデミー作品賞に輝いた「巴里のアメリカ人」の監督・主演コンビによるファンタジックなミュージカル・ラブ・ロマンス快作。1950年代のハリウッド・ミュージカル映画の勢いを感じさせる好編。100年に一度だけスコットランドに現れるという伝説の村があった。アメリカからやって来た二人の男トミーとジェフが、この村に迷い込んでしまう。そして、トミーは美しい娘と恋に落ちるが……。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUNBURN", "サンバーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ファラの人気絶頂時に製作されたアクション・ミステリー。保険調査員がモデルのエリーの協力を得て、謎の事故を洗ううちに次々と危機が振りかかる……。舞台となるアカプルコの観光的要素に加え、ファラのムッチリした水着姿が最大の見もの。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE END OF EVANGELION 新世紀エヴァンゲリオン劇場版 Air／まごころを、君に", "THE END OF EVANGELION 新世紀エヴァンゲリオン劇場版 Air／まごころを、君に", "", "", "http://ecx.images-amazon.com/images/I/51M3KJWEHYL._SL210_.jpg", 87, "1995～96年に放送され社会現象を巻き起こしたTVアニメーション「新世紀エヴァンゲリオン」の劇場版で、TV放送時に物議をかもした最終2話を完全新作で描きなおした完結編。TV版24話から分岐する、もうひとつのエンディングとなる第25話「Air」と第26話「まごころを、君に」で構成される。最後の使徒であった渚カヲルは倒されたが、カヲルを自らの手で殺したことで碇シンジは心を閉ざしてしまう。一方、「人類補完計画」をすすめるゼーレはNERV（ネルフ）司令のゲンドウと決別。NERV本部を押さえるため戦略自衛隊や量産型エヴァンゲリオンを次々と投入し、本部は壮絶な戦場と化していく。第25話「Air」に相当する部分は、同年春に公開された劇場版「シト新生」内の「REBIRTH」編で公開済みであったが、アフレコや画面の修正などが若干行われている。", "1997-07-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEVIL’S ISLAND", "精霊の島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "アイスランド映画界の第一人者F・T・フリドリクソンが、北欧の最優秀映画に与えられるアマンダ賞を「ムービー・デイズ」に続いて獲得したヒューマン・ドラマ。アイスランドの史実に基づく映画の舞台は、1950年代レイキャビクの荒野。予知能力を持つ老婆カロリナとその夫トマスを長とする４世代の大家族が、駐留米軍が残したバラックと呼ばれる粗末な住居で織り成す波瀾万丈の人間模様を追いかけていく。「春にして君を想う」のような美しい風景がほとんど出てこないにもかかわらず本作が感動的なのは、相次ぐ身内の死などの悲劇や米国文化の流入による生活の変化を、何とか乗り越えていく人々の力強い生命感が伝わってくるから。また一家に忍び寄る“悪魔”という要素が、この不思議な味わいの叙事詩に不安と神秘性を吹き込んでいる。", "1996-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("反逆のメロディー", "反逆のメロディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "澤田幸弘が「斬り込み」に続いて監督したニュー・アクション。一匹狼のヤクザ・塚田は、ゲバ作、滝川らとともに関東のヤクザ組織が支配するK市に乗り込み大暴れの末、殺される。原田芳雄、佐藤蛾次郎、藤竜也らが、フーテンヤクザに扮し熱演する。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花と蛇２／パリ・静子", "花と蛇２／パリ・静子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "SM小説の傑作として名高い団鬼六の『花と蛇』を、セクシー女優の杉本彩主演で映画化した官能ドラマの続編。日本古来の春画の世界をスクリーンに再現し、危険な三角関係に堕ちていく男女のディープな愛憎劇を映像化。極限の愛の形を描く。前作より激しさを増した強烈なSMシーンも見もの。美術評論家の遠山には、美しい妻の静子がいた。静子は夫の前では貞淑な妻だったが、それは性的不能な夫への気遣いからだった……。", "2005-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITY BENEATH THE SEA", "海底都市", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "海底に造られた巨大な都市を舞台に、スリル満載のストーリーで展開するSF大作。2051年。海底都市パシフィカの創設者マシューズは、大統領の要請で再び海底都市に赴く。今度の任務は、強力なエネルギー源を海底都市に集めることと、その爆発防止用の金の貯蔵庫の建造であった。しかしその金の強奪を計画する者が現れたり、海底地震が起こったり……。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDERCOVER BLUES", "アンダーカバー・ブルース 子連れで銃撃戦!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "スパイというハードボイルドな世界が、幼い赤ん坊が加わることで一変するアクション・コメディ。ニューオーリンズで産休を満喫するジェフとジェーンのブルー夫妻は、これまでに数々の修羅場をくぐり抜けてきたスゴ腕のスパイ。しばしの平和な日々を過ごす彼らに、無情な指令は突然やってきた。しつこいギャングに命をつけ狙われながら、再び銃を手に、なぜか最愛のベビーまで連れて、国際テロ組織との戦いに世界を飛び回る。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あしたのジョー", "あしたのジョー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 153, "『週刊少年マガジン』に連載されて人気を呼んだボクシング漫画のTVアニメ版を、劇場用作品として再編集したもの。流れ者の少年・矢吹丈と元ボクサーの丹下段平との出会いから、彼が少年院で出会った宿命のライバル・力石徹と、のちにプロ・ボクサーとなって対決するところまでを描いている。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Other Side of Midnight", "真夜中の向う側", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "S・シェルダンの超ベスト・セラーの映画化。第二次大戦の勃発をスタートに、洋装店の売り子からファッション・モデル、映画スター、そしてギリシャの富豪の愛人となって波乱の人生を送った女性の愛の遍歴を描く。彼女が自ら堕胎するシーンが衝撃的。ビデオ発売時タイトルは「真夜中は別の顔」。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("涯てへの旅", "涯てへの旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "自主制作映画の草分けとして知られ、「金閣寺」などの名作で国内外から高い評価を受けてきた映像作家・高林陽一が、デジタルビデオを用いて撮りあげた短編作品。唯一の肉親である祖母を探すため、父の生まれ故郷である海辺の町にやって来た男。記憶と現実が入り乱れる旅のなかで、男は生きる術を学んでいき……。主人公の男を演じるのは、「ベンチのある風景」に続き2度目の高林監督作への出演となる高城ツヨシ。", "2008-03-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("食卓のない家", "食卓のない家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "1972年２月に起きた連合赤軍浅間山荘事件を素材にした原作をもとに、過激派を長男に持った一家庭を、1973年の早春・晩春・梅雨・初秋・晩秋と五つの時節にわたってたどった社会派ドラマ。ヴェネチア映画祭でも好評を博した。小川真由美が母親役を熱演。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケン・笠置の極楽夫婦", "エノケン・笠置の極楽夫婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "新婚旅行に行っている間に恩師に留守宅を占拠されてしまった漫画家夫婦と、新婚早々アパートから追い立てを食った小説家夫婦。やっと貸家を見つけた漫画家夫婦だが、そこは独身の娘に限るという。妻が娘に化け、夫を老人に扮装させて父だと称して乗り込もうとした漫画家夫婦、だがそこには同じようなかっこうをした小説家夫婦がいた。コメディアン、エノケンこと榎本健一が大活躍するドタバタ・コメディ。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("牛乳屋フランキー", "牛乳屋フランキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "中平康が「狂った果実」と同じ年に発表した、スラップスティック・コメディの傑作。元ネタはキノトールと小野田勇の連続放送劇で、人情喜劇なのだがこれをギャグ満載の映画に作り変えた。主演はフランキー堺とブーチャンこと市村俊幸で、ともにドラマー、ピアニストとミュージシャンあがりの二人が、リズミカルな軽いフットワークで動き回る。この二人は1955～58年にコンビを組み、フランキーとブーチャンものとして親しまれた。物語は、牛乳配達員の堺六平太が、ライバルのブルドック牛乳店と張り合って、お得意先を増やしていくというもの。“理性がなんだ、道が何だ、要するに俺たちは退屈なんだ”と太陽族作家に扮したブーチャンが「狂った果実」のセリフを言ったり、牛乳に睡眠薬を入れて女を犯す妄想をする「処刑の部屋」を思わせるシーンがあったりと、当時の世相を取り込んだ描写も面白い。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Big Mommas: Like Father, Like Son", "ビッグママ・ハウス3", "", "", "http://ecx.images-amazon.com/images/I/51sM64VLlbL._SL210_.jpg", 107, "マーティン・ローレンス主演による人気コメディシリーズの第3作。マフィアの取引現場を張り込みしていたFBI捜査官マルコムの前に、なぜか息子のトレントが姿を現わす。そこで殺人現場を目撃してしまったトレントは、マフィアから命を狙われる身に。マフィアの犯罪の証拠が記録されたUSBが、とある女子高に隠されていると知ったマルコムとトレントは、それぞれビッグママと女子生徒に変装して潜入捜査を開始する。", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IMPROMPTU", "即興曲 愛欲の旋律", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ショパンと人気女流作家、ジョルジョ・サンド、この歴史上あまりに有名な二人の出会いを描いたラブ・ストーリー。作曲家のリスト、画家のドラクロワ、詩人のミュッセなど、ほかにも著名な芸術家たちが次々に登場する。劇中には、ショパンやリストの名曲が流れ、19世紀前半の芸術家たちの生活をいきいきと見せてくれるのもうれしい。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE　AFFAIRE　DE　GOU＾T", "趣味の問題", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "高級レストランのウエイター、ニコラが、高名な実業家でもある美食家ドゥラモンの味見役として雇われる。高級スーツに送迎用の運転手を与えられ、夢のような日々を満喫するニコラだったが、彼を自分の分身に仕立てようとするドゥラモンの歪んだ欲望を知り……。B・ラップ監督が「私家版」に続いて撮り上げた濃密な心理サスペンス。激しい愛憎に揺れ動く二人の男の駆け引きを、あくまでクールなタッチで描き、観る者の想像をかき立てる。両者の関係が破滅に至ったことを序盤で示す回想形式になっており、名優J・P・レオーが事件の真相を追求する判事を厳格に演じている。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴーグル", "ゴーグル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "父親からの虐待に苦しむ小学生が、暴力の影響から脱皮しようともがく姿を、真摯なタッチで描いた人間ドラマ。『日本テレビシナリオ登竜門』で大賞に輝いた若き才能・桜井剛が、若干27歳で監督デビューを飾る。苦い現実から目をそらすことなく希望に満ちたラストを創り上げた監督の姿勢がいい。父から受けた虐待の痕を隠すため、ゴーグルを着けて登校を始めたハルキは、両親の不仲に悩むイジメられっ子のカツキと仲良くなる。", "2005-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPER FORCE", "スーパーフォース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "2020年、凶悪な犯罪が横行するアメリカ、マイアミ市を舞台に、最終兵器を身につけたスーパー警官が活躍するアクション。元宇宙飛行士という経歴を持つ主人公が、“スーパースーツ”と“スーパーサイクル（バイク）”を駆使して戦うというのがミソ。「ロボコップ」を思わせるメカニカルなヒーローもの。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フレッシュマン若大将", "フレッシュマン若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "日本自動車に就職、サラリーマンとなった若大将・田沼雄一は、サービス課に配属され、持ち前のファイトで商売仇の陰謀をはね返し、見事に得意先と契約を締結。そのうえ、高橋節子という恋人も手に入れる。酒井和歌子が２代目ヒロインとして登場。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Guy Was Cool", "あいつはカッコよかった", "", "", "http://image.eiga.k-img.com/images/movie/54799/315x210.jpg?4220", 113, "韓国TVドラマ「夏の香り」で大ブレイクしたソン・スンホンが、熱血不良青年を演じる学園コメディ。平凡な女子高生ハン・イェウォンは、ネット上に書き込んだある文章が原因で、ガラの悪い商業高校の不良ウンソンに目をつけられてしまう。ある日、学校まで押し寄せてきたウンソンから逃げるため、学校の外壁を飛び越えたイェウォンは運悪くウンソンの上に落下してしまい、彼の唇を奪ってしまう……。", "2009-09-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Boneyard", "ボーンヤード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "奇怪な死を遂げた３人の中国人少年が、ゾンビとなってよみがえった。その場に居合わせた７人の男女は、力を合わせてゾンビを撃退する。ところが、一匹のプードルがゾンビの血をなめてしまってから状況は一変。新たな恐怖が７人を襲う……。ゾンビ映画数あるなかでも、この作品に登場する全長３メートルのゾンビ犬は異色中の異色。「遊星からの物体X」「コクーン」などのSFXを担当したジェームズ・カミンズがメガホンを執っている。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THANK GOD. IT’S FRIDAY", "イッツ・フライデー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "金曜の夜のディスコを舞台に、そこに集まる若者たちを通して、現代アメリカの風俗をあざやかに描き出す青春映画。ゲストのコモドアーズもいいが、何といってもD・サマーの熱唱する『ラスト・ダンス』が絶品である。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THIS IS SPINAL TAP", "スパイナルタップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "R・ライナー監督の出世作。伝説のロック・バンド“スパイナルタップ”の全米ツアーの模様を、関係者のインタビューを交えて綴る架空のドキュメンタリー。実在のバンドのエピソードがパロディーで多数登場、音楽ファンを笑わせてくれる。後に続編も作られた。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIVER", "RIVER", "", "", "http://image.eiga.k-img.com/images/movie/57339/315x210.jpg?2019", 89, "2008年6月に起こった秋葉原無差別殺傷事件をモチーフに、大切な人を失った女性が喪失感から再生していく姿を描いたドラマ。秋葉原で起こった通り魔事件で恋人の健治を失ったひかりは、家にひきこもる生活を続けていたが、優しく見守る母や再会した高校時代の同級生の助けもあり、徐々に落ち着きを取り戻していく。やがて事件が起こった秋葉原を訪れたひかりは、それぞれの事情を抱えながらも懸命に生きている人々と出会いを果たしていく。監督は「ヴァイブレータ」「余命1ヶ月の花嫁」の廣木隆一。主演は「転校生　さよならあなた」の蓮佛美沙子。", "2012-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("World War Z", "ワールド・ウォー・Z（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刑事／DUELIST", "デュエリスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "王朝時代を舞台にした刑事ドラマという風変わりな体裁を取りながら「ロミオとジュリエット」さながらの悲恋ドラマが展開する。挑戦王朝時代。武術の達人の女刑事ナムスンは、偽金事件の捜査中に仮面の刺客と出会う。彼女は男と交戦した際、割れた仮面からのぞいた彼の“悲しい目”に心奪われてしまう。二人は互いに惹かれ合いながらも、１対１の戦いを繰り返し……。スタイリッシュな映像と流麗なアクションで魅せる歴史活劇。", "2006-04-22", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一刀斎は背番号６", "一刀斎は背番号６", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "奈良の山奥で一刀流の修行を積んだ剣豪がプロ野球チームに入団、ホームランを打ちまくる。当時の人気プレーヤー、稲尾や田宮らも実名で登場し、漫画的キャラクターの主人公を盛り上げている。荒唐無稽な原作をホーム・ドラマとして料理した木村演出も的確だ。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HO！", "オー！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ギャングのお抱え運転手から、ついには“ルパン＋カポネ＝オー”とまで新聞に書かれるようになったレーサーあがりの青年の、絶頂と悲惨な最期を綴ったアンリコのフィルム・ノワール。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flamenco, Flamenco", "フラメンコ・フラメンコ", "", "", "http://image.eiga.k-img.com/images/movie/56389/315x210.jpg?2631", 101, "「カラスの飼育」（1975）、「カルメン」（83）で知られるほか、「フラメンコ」（95）、「サロメ」（2002）、「イベリア　魂のフラメンコ」（05）などフラメンコを題材にした映画を多数撮り続けてきたスペインの名匠カルロス・サウラが、人の誕生から晩年、再生までを全21幕の構成で描き出したフラメンコ映画。カリスマ的人気のダンサー、サラ・バラスや、名フラメンコ歌手のホセ・メルセー、フラメンコ・ギターの第一人者パコ・デ・ルシアら一流のフラメンコ・アーティストが共演。撮影は「地獄の黙示録」「ラストエンペラー」のビットリオ・ストラーロが担当。音楽は「フラメンコ」のイシドロ・ムニョス。", "2012-02-11", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魚介類　山岡マイコ", "魚介類　山岡マイコ", "", "", "http://image.eiga.k-img.com/images/movie/56369/315x210.jpg?1278", 82, "海からやってきた“魚介類”女子高生・山岡マイコが巻き起こす騒動を描くファンタジックコメディ。ある日、海から制服に身を包んだ女の子・山岡マイコが浜辺にあがってくる。ネコのぬいぐるみが手放せず、常に体を水で濡らしている天然キャラのマイコと、マイコの第一発見者で心配性の鴨子は共同生活を始めるが、ナルシストのカリスマ美容師や怪しいカウンセラー、マイコを調理したい料理人など2人の周囲に奇妙な人々が次々現れる。やがて絶体絶命の危機を迎えたマイコは“出世魚”として覚せいし……。主演はガールズユニット「9nine」の佐武宇綺。", "2011-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほしのふるまち", "ほしのふるまち", "", "", "http://image.eiga.k-img.com/images/movie/55419/315x210.jpg?4373", 120, "「週刊ヤングサンデー」（小学館）で2006～08年に連載された原秀則の人気漫画を、「ひゃくはち」「BECK」の中村蒼主演で実写映画化。尾崎豊や浜田省吾らの音楽制作パートナーとして、数々の名曲を手がけてきた須藤晃がプロデュース。「花ゲリラ」「書の道」の川野浩司監督がメガホンをとる。東京の進学校で進級できず、遠縁の親類を頼って富山の高校に転校してきた堤恒太郎が、地元の少女・渚との出会いを通して自らと向き合い、苦悩しながらも成長していく姿を描く。", "2011-04-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN ＆ OUT", "イン&アウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アカデミー賞のスピーチで、教え子である俳優に“先生はゲイです！”と言われたことから、結婚を間近に控えた教師、ハワードの幸福な日々は一変、周囲に白い目を向けられる。慌てたハワードは、次第に“自分は本当にゲイなのでは？”という疑念にとらわれ……。お笑い演技の得意なオスカー俳優K・クラインが、本領を発揮したコメディ。ゲイに見られないために不自然に男っぽく振る舞う、その姿がおかしい。パロディー化されたアカデミー賞授賞式も笑える。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("АЛЕКСАНДР НЕВСКИЙ", "アレクサンドル・ネフスキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "プロコフィエフの音楽を使って戦闘シーンを音楽の盛り上がりと呼応するように映像をモンタージュした、野心的作品。監督のエイゼンシュテインは、音楽と映像の相関性を初めて理論的に実践し、この歴史的な一大ページェントを作り上げた。13世紀に実在したアレクサンドル・ネフスキー将軍が、豊富な資源に恵まれたロシアに侵攻しようとするスウェーデン軍やゲルマン軍を勇猛果敢に粉砕し、故国の危機を救うさまを描いている。一説には、製作当時ソビエトに圧力をかけていたナチス・ドイツをゲルマン軍になぞらえてエイゼンシュテインが製作に着手したともいわれている。", "1938-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Watch with Me 卒業写真", "Watch with Me 卒業写真", "", "", "http://image.eiga.k-img.com/images/movie/34009/315x210.jpg?3171", 125, "「千年火」の瀬木直貴監督が“生と死”をテーマに描いたヒューマン・ドラマ。ガンに侵された男性が、妻や友人たちと共に残された日々を生きるさまを情感豊かに綴る。余命半年を宣告された元報道カメラマンの上野。残された時間を故郷の福岡県久留米市で過ごすことにした彼は、妻・由紀子の協力を得て、故郷の風景や友人たちの姿を収めた写真集を作ることを決意する。主人公を「模倣犯」の津田寛治、妻を「人でなしの恋」の羽田美智子が好演。", "2007-06-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("記憶の音楽—Gb—", "記憶の音楽—Gb—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "人気バンド、SOPHIAのヴォーカル、松岡充主演によるサスペンス・ストーリー。幼少時、高熱のため聴覚を失った少年サイ。音楽を知らないはずなのに、ふと触れたピアノで、あるメロディーを弾き始めた彼はそのとき受けたショックで気を失ってしまう。サイはピアニストのハツネに救われ、やがて惹かれ合うようになる。そのころ、街では連続殺人事件が起こっていた。現場に残されているのは決まって“Gb”という謎の記号。このキーワードは、やがてサイの過去と彼が紡いだメロディーに関連性があることが判明してくるが……。ミュージック・クリップ出身の川村ケンスケの劇場デビュー作。ハツネ役は「ISOLA　多重人格少女」の黒澤優。彼女と松岡の電撃入籍も話題を呼んだ。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MADAME SOUSATZKA", "マダム・スザーツカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "ロンドンを舞台に、才能あるインド人少年を立派なピアニストに育てあげようと、全情熱を傾けて指導する老ピアノ教師マダム・スザーツカの姿を、名匠J・シュレシンジャーが丹念に描き出す。S・マクレーンはこの作品のために体重を12キロも増やしたといわれている。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニッポン無責任時代", "ニッポン無責任時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "『スーダラ節』の大ヒットで人気者となったクレージー・キャッツの植木等主演による“無責任”シリーズ第１作。群を抜いたお調子もの、平均（たいらひとし）は洋酒会社にもぐり込み、アレヨアレヨと出世して社長にまでのしあがる。東宝が自社の人気女優トリオの“お姐ちゃんトリオ”（重山規子・中島そのみ・団令子）を絡ませたお盆映画であったが、予想を上回る大ヒットを記録し、急きょシリーズ化が決定した。植木等の人気はこれで決定的となり、1960年代の日本喜劇映画界をリードしていくことになる。めったに日本映画を誉めない大島渚が絶賛したという、一見の価値のある一編。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIABET", "悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "製作当初以来、残忍かつ暴力的な内容で公開禁止処分を受けていた、いわくつきの作品。18世紀末、祖国ポーランドの現状を憂い政治活動を行っていたヤコブは、ある日、逮捕され精神病院に入れられる。そこで彼は、奇妙な小男に助けられ脱走に成功する。だがこの小男こそ、人の姿を借りた残忍な悪魔だったのだ。", "1972-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　FAMILY　WAY", "ふたりだけの窓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "映画館に勤めるアーサーとレコード店員のジェニーは、祝福されて結婚するが、友達のいたずらで新婚初夜はおあずけ。そのうえ、詐欺にあい新婚旅行にも行けなくなる。夫の両親との同居で、親密になれない二人の苛々が募り、爆発寸前！ポール・マッカートニが音楽を担当し、M・ロードが全米評論家協会賞を受賞した、イギリスの下町を描いたファミリー・ドラマの佳作。", "1966-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANITA：TANZE DES LASTERS", "アニタ 背徳のダンサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "1920年代のベルリンに実在していたという背徳のダンサー、アニタ・ベルバーをモチーフに、現代のベルリンに現れたアニタと名乗る老女のスキャンダラスな振る舞いと、かつてのアニタの姿を交錯させ描いた実験的な作品。精神病院へ送られた老女が登場する現在(モノクロ)と、アニタ・ベルバーの生涯のいくつかの場面を綴った過去(カラー)が交互に現れ、まるで相互に反響し合うように展開していく。ドイツ表現主義のスタイルを模した、R・V・プラウンハイム監督の映像感覚、そして音楽は、フリッツ・ラング監督の「ドクトル・マブゼ」のサウンド版を使っていることも話題の一つだ。", "1987-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESIGNING WOMAN", "バラの肌着", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "宝クジを当てて祝いの酒で酔ったスポーツ記者は、マリラという女性と知り合い恋に落ちる。さっそく結婚しニューヨークに向かう二人だが、マリラは実はトップ・ファッションデザイナーだった。まったく違う世界に生きる二人の行方は？　G・ペックとL・バコールの絶妙な掛け合いが見ものの傑作ラブ・コメディ。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE VIOLENT MEN", "欲望の谷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "南北戦争後の西部を舞台にしたアクション・ウェスタン。横暴な牧場主リューは付近の牧場を買い占めていた。主人公のカウボーイ、ジョンも売ろうとしたが、自分の牧童がリューの暴力的な弟コールに殺されて怒りが爆発。一方、コールは兄の妻と不倫し、保安官とつるんで残虐な行為を繰り返すのだが、そのアウトローぶりが徐々にエスカレートしていく。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAGONIA", "マゴニア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "オランダから飛び出した新鋭女性監督、イネケ・スミツの長編デビュー作。わけあって離れ離れに暮らす父と子の切れることのない深い絆が描かれる。映画はこの父子物語に、父が語る寓話的エピソード3編が加わる形で進展。まったく別の逸話が、不思議と1本の線でつながる構成がユニークだ。毎週末、海に囲まれた島で暮らす父親を訪ねる少年。そこで少年は父親からマゴニアという架空の国を舞台にしたおとぎ話を聞かされる。", "2005-03-19", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jeremiah Johnson", "大いなる勇者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1850年代、戦争からの逃亡者ジェレマイア・ジョンソンはロッキー山中で猟師となり、インディアンと交易し、インディアンの娘と結婚し、白人の孤児を引き取り、幸せに暮らし始める。だが、彼には困難が待ち受けていた……。雄大な自然を背景に、孤高のフロンティア・スピリットを描く。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MILOVNIK ZVIRAT", "動物の好きな男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 15, "ひとりぼっちの老人が様々な動物を飼い始める。その動物たちは成長してゾウやクジラになる。ところがある日、老人が亡くなってしまい……。チェコアニメ映画祭2006で上映された一作。", "1984-01-01", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES PRE´DATEURS DE LA NUIT", "フェイスレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ヴィスコンティの死後、失意のドン底にあったH・バーガーが久しぶりに主演したフランス製ホラー映画。主人公フラマンはパリで整形外科医を開業している。ひょんなことから彼の妹が硫酸を浴びてふた目と見れない顔に。以来、フラマンは美女を誘拐し、その顔を妹に移植しようとするのだった。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花と龍／青雲編・愛憎編・怒涛編", "花と龍／青雲編・愛憎編・怒涛編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "火野葦平原作の同名小説を５度目にして初の完全映画化。若松の港を舞台に、一介の沖仲士から身を起こし、九州一帯を支配する大親分にのし上がった男を壮大なスケールで描く。ローアングルを多用した独特のスタイルが魅力。DVDタイトルは「花と龍」。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新吾十番勝負・第三部", "新吾十番勝負・第三部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "享保11年、新吾は晴れて親子対面の儀がかない江戸へ上る途中、何者かに発砲される。旅の一座・藤井一家に紛れるものの、かつて新吾に敗れた宗田豊之進の仇と名乗る多羅尾平八が、やがて挑戦を仕掛けてくる。多羅尾平八に扮する戸上城太郎が風格をみせる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グミ・チョコレート・パイン", "グミ・チョコレート・パイン", "", "", "http://image.eiga.k-img.com/images/movie/53129/315x210.jpg?1943", 127, "大槻ケンヂの半自伝的小説を、大槻の盟友にして劇団ナイロン100℃主宰のケラリーノ・サンドロビッチが映画化した青春ドラマ。1986年、高校生の賢三は、平凡なクラスメイトたちを軽蔑しながらアンダーグラウンドなロックや映画にのめり込んでいた。しかし実際は冴えない仲間たちと悶々とするだけの日々。そんなある日、賢三は名画座でクラスの人気者・美甘子を見かける。実は彼女も映画やロックが大好きで……。", "2007-12-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太王四神記", "新編集版　太王四神記　第3章：別れのきざし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "2007年にテレビ放映と劇場公開がほぼ同時に行なわれたペ・ヨンジュン主演の歴史ファンタジードラマ「太王四神記」の新編集版4部作の第3章。王の座に就き連戦連勝を続けるタムドクは、謎の城主によって守られている難攻不落のカンミ城へと兵を進める。一方、キハはタムドクへの思いに背き、ホゲをさらなる陰謀へと導いていく。密かにタムドクを思い続けてきたスジニは、自らが背負っている運命に気づき……。", "2010-05-22", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Octopussy", "007／オクトパシー", "", "", "http://ecx.images-amazon.com/images/I/51G66nqKDsL._SL210_.jpg", 131, "アメリカの威信を失墜させようと、NATO基地の核爆弾爆破をもくろむインドの王族カマル。インドと西独を舞台にボンドとカマルの闘いが展開する。スリル満点の派手なアクションがテンポよく続くシリーズ第13作。ムーア主演作の中では好編。", "1983-07-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勝手にしやがれ!! 脱出計画", "勝手にしやがれ!! 脱出計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「同・強奪計画」に続き、“勤労ハンパ者”コンビの活躍を描いたコミカル・アクション。雄次と耕作の二人が暴力団組長から娘の交際相手を調べることを依頼されたことから、組長の娘とその恋人をめぐるトラブルに巻き込まれる。前作に続いて黒沢清が監督に当たり、エンターテインメントに徹した演出ぶりを見せる。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さそり", "さそり", "", "", "http://image.eiga.k-img.com/images/movie/54069/315x210.jpg?1570", 100, "篠原とおるのコミックを梶芽衣子主演で映画化した70年代の名作アクション映画「女囚701号　さそり」を水野美紀主演でリメイク。恋人の父親殺しの罪を着せられて投獄された松島ナミ。獄中での過酷な日々の末、瀕死の状態で刑務所の外に捨てられた彼女は、謎の老人によって助けられ一命をとりとめる。彼女は老人の元で刺客になるための訓練を受け、自分を陥れた者たちへの復讐を開始する。監督は「雨音にきみを想う」のジョー・マ。", "2009-08-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STRAWBERRY BLONDE", "いちごブロンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "グラマー美人のガールフレンド、ヴァージニアを友達に横取りされたブルックリンの青年歯科医ビフ・グリムス。落胆した彼だったが、恋人の友だちのおとなしい娘と結婚してかえって幸せになる……という恋愛コメディ。技巧派ぶりを発揮するJ・キャグニーとO・デ・ハヴィランド、そして妖花 R・ヘイワースが、それぞれ秘術を尽くして渡り合う様が何とも楽しい。1933年のゲイリー・クーパー主演作「或る日曜日の午後」の再映画化だが、R・ウォルシュ監督はこの題材が気に入ったと見えて、1948年に再び「On Sunday Afternoon」(主演はデニス・モーガン)としてリメイクしている。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Apollo 18", "アポロ18", "", "", "http://image.eiga.k-img.com/images/movie/56959/315x210.jpg?2236", 87, "アポロ計画にまつわる陰謀説を背景に、公式には存在していない「アポロ18号」の乗組員が月面で遭遇した事件を描くSFサスペンス。1961年から72年にかけて計6回の有人月面着陸を成功させたアポロ計画は、アポロ17号を最後に突然打ち切られる。しかし、極秘に記録された映像フィルムの中には、存在しないはずのアポロ18号が月面に着陸し、乗組員が恐るべき事件に直面する様子が映し出されていた……。「ウォンテッド」のティムール・ベクマンベトフが製作を担当。", "2012-04-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRACULA HAS RISEN FROM THE GRAVE", "帰ってきたドラキュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「吸血鬼ドラキュラ」(1958)、「凶人ドラキュラ」(1965)に続く、英ハマー・プロ製作、C・リー主演のシリーズ第４作。前作のラストで川に落ち、氷漬けにされたドラキュラ伯爵が復活。若い娘が次々と行方不明になっているある村で、無神論者の若者が吸血鬼と対決するという一編。ドラキュラが初めて胸に杭を刺されて死ぬ場面が登場した。「砂の惑星」の名カメラマン、F・フランシスの監督作。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Johnny English", "ジョニー・イングリッシュ", "", "", "http://image.eiga.k-img.com/images/movie/1459/315x210.jpg?3165", 87, "Mr.ビーンで人気のローワン・アトキンソンが英国秘密諜報員を演じるスパイ・コメディ。監督は「英国映画でウケるのは007とコメディだけだから」と本作を引き受けた「スライディング・ドア」「サベイランス／監視」のピーター・ハウイット。脚本は「ワールド・イズ・ノット・イナフ」「ダイ・アナザー・デイ」の本家007映画を描いたニール・パービス＆ロバート・ウェイドに「ツインズ」のウィリアム・デイビスが参加。", "2003-10-04", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐を呼ぶ友情", "嵐を呼ぶ友情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「嵐を呼ぶ男」同様バンドマンの世界を背景とした男たちのドラマ。ジャズ喫茶“ジャズボート”では岡辺と川添のトランペットが人気を呼び、連日満員であった。二人は名トランペッター・旗から演奏を一から叩きこまれたのだった。そんなある日、恩師・旗の息子がチンピラとなっているのを知った二人は、彼をジャズの力で更生させようと誓う。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゼイラム ２", "ゼイラム ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "宇宙を股にかけるマイス星の女バウンティ・ハンター（賞金稼ぎ）イリアは、地球に逃亡した盗賊カヌートから骨董品カマライト奪回に成功し、次の作戦の到着ポイントに赴く。しかし、パートナー、フジクロの裏切りで、カヌートとその無数の配下どもに包囲されてしまう。すると、そこへマイス星当局がイリア支援のために送り込んだ高性能武装ロボットが飛来する。しかし、この支援者ロボットの正体は、不死身のエイリアン、ゼイラムのユニットが組み込まれた不良試作品だった。イリアとゼイラムの死闘が始まった。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREAKY FRIDAY", "フリーキー・フライデー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "13歳の少女と母が大ゲンカ。互いに相手の立場になりたいと願うと、不思議なことに、外見はそのままに心だけが入れ代わってしまう。入れ代わりコメディの先駆けともなった一編。「ダウンタウン物語」「白い家の少女」に続いて出演した、J・フォスターの名子役ぶりが楽しめる。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PUNISHER", "パニッシャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アメリカのマーベル・コミックスの人気キャラクターをベースにしたバイオレンス・アクション巨編。妻子を殺された元刑事が、処刑人（＝パニッシャー）と化して、イタリア＆ジャパン・マフィアを壊滅させていく。D・ラングレンのアクションと、ジャパン・マフィアの女首領K・ミヨリがド迫力。", "1989-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO RETREAT，NO SURRENDER", "ノーサレンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "空手で暴力団に立ち向かおうと、日夜修行に励む少年の前にブルース・リーの亡霊が現れる、というユニークな設定の青春アクション。「ロッキー」や「ベスト・キッド」と同様にテンポよく作られている。ビデオ発売時タイトルは「シンデレラ・ボーイ」。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花と兵隊", "花と兵隊", "", "", "http://image.eiga.k-img.com/images/movie/54559/315x210.jpg?3993", 106, "タイ・ビルマ国境付近で敗戦を迎えたにも関わらず、祖国に帰らず未帰還兵となった日本兵6人の生涯に迫るドキュメンタリー。監督は本作が劇場デビューとなる若手映像作家の松林要樹。1941年、日本軍はビルマ侵攻作戦に着手し、およそ19万もの将兵を失った。敗戦後、自らの意思で所属部隊を離れ、現地に残った兵士たちはその地で家族を作り定住している。一見幸せそうに暮らす彼らから、長年の沈黙を経て壮絶な戦争の記憶と故郷への思いが語られる。", "2009-08-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("БАЛЛАДА О СОЛДАТЕ", "誓いの休暇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "一人の兵士の帰郷旅行を軸に、戦争の悲惨さをうたい上げたG・チュフライ監督の秀作。17歳の少年兵アリョーシャは、二台の戦車を炎上させて英雄になり、６日間の休暇を与えられる。故郷へ帰る列車の中で彼は様々な人と出会う。戦地で足を失い、妻に会うのを嫌がる兵士、負傷した婚約者を病院に訪ねるために列車に乗り込んだ少女……。少年兵の遭遇する人々とのエピソードの一つ一つが、観る者の胸をさす作品だ。", "1959-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TANK GIRL", "タンク・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "イギリスの人気コミックを映像化したSFコメディ。砂漠化が進んだ近未来の世界。愛用のタンク（戦車）を乗り回すモヒカン・ヘアのキュートなヒロイン、レベッカは飛行機エンジニアのジェット・ガールとともに、資源を独占するウォーター＆パワー社の悪徳社長ケスリーに闘いを挑む……。コミックの原画を挿入したポップな映像、全編に流れるオルタナティヴ・ミュージックなど、MTV感覚で楽しめる作品。ヒロインのファンキーなファッションも見逃せない。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彼のオートバイ 彼女の島", "彼のオートバイ 彼女の島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "オートバイで旅する青年の前に、風のように現れた女。二人はいつしか結ばれるが、やがて女は島に帰ってしまう。原田知世の姉、原田貴和子のデビュー作で、片岡義男の同名小説を、物語以上に感性をくすぐる大林流の演出で見せた軽やかな青春ファンタジー。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESTINY TURNS ON THE RADIO", "ジョニー・デスティニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "その特異なキャラクターで、俳優としても活躍しているQ・タランティーノの俳優デビュー作。銀行強盗で服役中だったジュリアンはまんまと脱獄し、相棒から金を受け取り、恋人と国外逃亡するためラスベガスへと向かう。真っ赤なロードランナーをヒッチハイクし、ラスベガスへ着くと思わぬ事態が。恋人には裏切られ、金は謎の男に奪われたという。そして驚くべきことに、金を強奪したのは真っ赤なロードランナーに乗った男だというのだ！　それを聞いたジュリアンは、謎の男を探して街を奔走する。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUCK THE WORLD", "フリーライド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "10年の刑期を終え、殺人罪で服役していた刑務所から出所したフランク。ロデオ・カウボーイでもある彼は、チャンピオンになって社会復帰を果たそうとする。ロデオ大会を追いかけて旅に出た彼は、ひょんなことから銀行襲撃で５人を殺害し逃走中のスカーレットに出会う。やがて二人は愛し合うようになり、破滅への旅を続けていく……。原作者のE・クックはM・ロークのペンネーム。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAITING TO EXHALE", "ため息つかせて", "", "", "http://ecx.images-amazon.com/images/I/51BRRNYCqHL._SL210_.jpg", 121, "現代女性が直面する恋愛、離婚、仕事などの悩みを浮き彫りにしたベストセラー小説の映画化。様々な愛に悩みながらも、励ましあっていく４人の女性の友情を描く。仕事の成功と理想の男を求めてサバンナは、親友のバーナディンのいるフェニックスにやって来た。11年間連れ添った夫と離婚訴訟中のバーナディンは、ヘア・サロンを経営するグロリアと、保険会社に勤めるロビンをサバンナに紹介し、４人は友情を深めていく。そんなある日、サバンナの前に昔の恋人ケネスが現れる。一方、バーナディンはバーでジェームズという男性と出会い一夜をともにするが……。「ボディガード」に続く人気シンガー、W・ヒューストンの主演映画第２作。本作でもテーマ曲を歌い、ビルボードをにぎわした。個性派俳優F・ウィテカーの初監督作品でもある。", "1996-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千両獅子", "千両獅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "江戸市中に幕府と結んで暴利をむさぼる御用商人から、財宝をかすめとる怪盗・葵太郎が出没。旗本・松平鶴太郎は、葵太郎とそっくりだったことから疑いの目を向けられ、自ら捜査に乗り出す。やがて鶴太郎の前に、葵太郎が出現。彼が行方不明の兄、美代太郎とわかる……。立場違いの兄弟が闘う、捕物活劇。原作は、山手樹一郎の『江戸群盗記』。名匠・内田吐夢がメガホンを執っている。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shelter", "シェルター", "", "", "http://image.eiga.k-img.com/images/movie/54919/315x210.jpg?4328", 112, "スウェーデン出身の新鋭マンス・マーリンドとビョルン・ステインが手がけるスーパーナチュラル・スリラー。主演はジュリアン・ムーア、ジョナサン・リース＝マイヤーズ。精神分析医のカーラが、解離性同一性障害を患う青年デビッドを診察していると、彼の中からアダムとウェスという全くの別人格が現れる。カーラはそれぞれの人格の過去を調べていくうちに、人間の魂を隔離する“シェルター”という超常現象の存在を突き止めるが……。", "2010-03-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キディ・グレイド　劇場版　第二部：KIDDY GRADE MAELSTROM 氾濫篇", "キディ・グレイド　劇場版　第二部：KIDDY GRADE MAELSTROM 氾濫篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "特殊能力を持つ少女たちの奮闘を描いたSFアニメ「キディ・グレイド」のテレビ版全24話に新作カットを追加して再構成した劇場版3部作の第2弾。支配階級ノーブルズに逆らったことからGOTTの登録を抹消されてしまったエクレールとリュミエール。逃亡した彼女たちは、GOTTが送り込んだ追手をかわしながら本部へと乗り込んでいくが……。監督は、「機動戦艦ナデシコ」でキャラクターデザインを担当した後藤圭二。", "2007-06-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FULLTIME　KILLER", "フルタイム・キラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "反町隆史とA・ラウという異色の共演が実現した話題作。反町とアンディが演じるのは世界屈指の殺し屋。このライバル二人が、日本、香港はじめアジア中を股に架け、頂上争いを繰り広げるハードボイルド・エンターテインメント。冷静沈着で影のあるNo.１の殺し屋“O”に対して戦いを挑む男がいた。トクと言うその男は殺しを刺激剤と考える新進の殺し屋。“O”を倒すため、まずは家政婦に近づこうとするトクだが、家政婦は“O”と深いかかわりを持つ仲で……。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODY SHOTS", "ボディ・ショット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "過激なカクテル“ボディ・ショット”を飲んだ末に起きたレイプ事件をめぐる衝撃のドラマ。ジェーンとリックはジェーンの家の同じベッドで目を覚ました。彼らは前夜のクラブで初めて出会い、泥酔したあげく、一夜をともにしたのだ。そこに、前夜のクラブに同席していたジェーンの友人、サラがボロボロの下着姿で現れる。彼女はリックの友人マイクにクラブから連れ出されレイプされたのだと言う。果たして、真相は？", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVEN AND EARTH", "天と地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "「プラトーン」「７月４日に生まれて」に続く、O・ストーン監督によるベトナムもの。今回は、実在のベトナム女性、L・リー・ヘイスリップの、波乱に富んだ半生を、一大叙事詩として描いている。1949年、ベトナムの村キーラに生まれた彼女の、ゲリラのスパイ、娼婦、アメリカ軍人の妻と、様々な経験を経ながらたくましく生きていく姿を映し出す。主演は、これが映画初出演という、H・T・リー。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEY JAPANESE! Do you believe PEACE, LOVE and UNDERSTANDING? 2008 2008年、イマドキジャパニーズよ。愛と平和と理解を信じるかい？", "HEY JAPANESE! Do you believe PEACE, LOVE and UNDERSTANDING? 2008 2008年、イマドキジャパニーズよ。愛と平和と理解を信じるかい？", "", "", "http://image.eiga.k-img.com/images/movie/53459/315x210.jpg?2349", 104, "東京郊外を走る国道16号線沿いの街を舞台に、万引き・援交に走る女子高生や親の仕送りを受けながら合コンに励むニートなど、\"まともに見えてどこか変\"な現代日本人の姿をブラックユーモアたっぷりに描いた群像劇。長編監督デビュー作「LOVEHOTELS／ラヴホテルズ」で注目を集めた村松亮太郎監督がメガホンを取り、キャストには神田沙也加、いしだ壱成、川合千春、桜塚やっくんら総勢58人の個性派が集結した。", "2008-03-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渡り川", "渡り川", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "高知県四万十川流域の幡多地区に住む高校生と教師による“幡多ゼミ”の活動を描いたドキュメント。地元の鉄道、土手、ダムが戦時中多くの朝鮮人たちによって築かれたことを知った彼らが、韓国人たちとの交流を深めていく中で、アジアの人々とともに生きていくために、自分たちに何ができるのかを探る姿を追う。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初恋・地獄篇", "初恋・地獄篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "失語症的な疎外の時代に生きる、少年と少女の不安定な愛を、シネマ・ヴェリテ的手法を導入して描いた作品。主人公のシュンが７歳の時、父が死に、母は再婚した。救護院に入ったシュンは、彫金師に引き取られ、そこで成長する。シュンはある日、ナナミという少女と知り合う。彼女は集団就職で上京して、今はヌードモデルをやっている。二人は安ホテルで抱き合うが、シュンは当惑し不成功に終わった。映画はシュンの過去と現在を交互に描きながら、グロテスクに肥大した現実をインタビュー。８ミリ映画、隠し撮りなどを取り入れながら多層的に捉えていく。性の萌芽のおののきが痛々しい。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Infernal Affairs", "インファナル・アフェア", "", "", "http://image.eiga.k-img.com/images/movie/1089/315x210.jpg?2835", 102, "警察に潜入することを命じられたマフィアの青年と、マフィアに潜入して囮捜査をすることを命じられた警察官青年は、ともに18歳。その10年後、ある覚醒剤取引きをめぐってお互いの存在を知った2人に、避けられない対決の時がやってくる。人気俳優トニー・レオンとアンディ・ラウが共演。香港で02年の年間興行成績第1位はじめオープニング興収、上映館数など数々の歴代記録を更新した大ヒット作。", "2002-10-11", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wedding Party", "御婚礼／ザ・ウェディング・パーティー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "　結婚式の前日、チャーリーはフィアンセのジョセフィンが暮らす邸宅を訪ねる。未来の家族や友人たちの意見から、彼は様々な思いをめぐらせることに……。ブライアン・デ・パルマ監督の日本未公開コメディ。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若き日の次郎長 東海道のつむじ風", "若き日の次郎長 東海道のつむじ風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "仲間の危機に甲府へかけつけた次郎長一家は、甲府勤番支配・平垣三郎兵衛を抱き込んだ猿屋勘助の罠にはまり、召し捕らえられる。留守を預かる大政は、子分一党を引き連れて甲府へ乗り込む。マキノ雅広が若手をうまく使い、楽しい一編となった。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EYEWITNESS", "目撃者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ビルの清掃人ダリルは、TVの美人レポーターを見ることで、孤独な生活をまぎらわしていた。ある日、恐ろしい事件に巻き込まれた彼は、これをきっかけにレポーターと知り合い……。殺人事件を軸に展開する、大人の恋物語。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASCHERA DI CERA", "肉の鑞人形", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "イタリアン・ホラーの巨匠L・フルチ、D・アルジェントが1933年のクラシック・ホラー「肉の蝋人形」のリメイク版として、小説『オペラ座の怪人』をモチーフに共同で企画。だが、1996年にL・フルチが他界したため、D・アルジェント作品「フェノミナ」などのF/Xアーチスト、S・スティヴァレッティが監督に抜擢されたホラー作品。犯罪シーンばかりを再現した邪悪な蝋人形博物館で働くヒロインの恐怖の体験を描く。", "1997-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AIR DE PARIS", "われら巴里っ子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "パリの下町、中央市場の近くに練習所を開く元拳闘家。自分が果たせなかったチャンピオンへの夢を捨てきれず、希望を託して若者を育てているが、ある日有望な選手が弟子入りして……。パリっ子気質と哀歓を描く下町情緒豊かなカルネの佳作。", "1954-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東海水滸伝", "東海水滸伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "浪曲の“すし食いねェ”で有名な、ご存じ森の石松の金比羅参りの一席。この作品は終戦間近の昭和20年に海軍航空本部の委嘱映画として製作されたが、戦争末期で人がおらず清水の二十八人衆になる役者が、同時に敵方も演じた。", "1945-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はやぶさ HAYABUSA", "はやぶさ HAYABUSA", "", "", "http://image.eiga.k-img.com/images/movie/56399/315x210.jpg?1547", 140, "通信途絶やエンジン停止など幾度もの危機を乗り越えて小惑星イトカワへ到達し、微粒子を採取。2010年6月13日、奇跡的に地球に帰還した小惑星探査機「はやぶさ」を陰で支えたプロジェクトチームの7年間の苦闘を描くドラマ。宇宙科学研究所（現・JAXA）のスタッフとして働き、自らの生き方を見つめなおしていく主人公の女性研究員・水沢を竹内結子が演じ、その上司に西田敏行が扮する。監督は「20世紀少年」シリーズの堤幸彦。", "2011-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Observe and Report", "オブザーブ・アンド・レポート", "", "", "http://ecx.images-amazon.com/images/I/51gbPFAi7JL._SL210_.jpg", 86, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The World of Henry Orient", "マリアンの友だち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ノラ・ジョンソンの原作小説を、ジョージ・ロイ・ヒル監督がピーター・セラーズ主演で映画化したコメディ・ドラマ。主人公マリアンは、転校してきたバルという少女と大親友になる。バルは富豪の娘だが、彼女の両親は旅行ばかりでいつも寂しい思いをしていた。ある日2人は、公園でオリエントというプレイボーイのピアニストと出会う。それ以来オリエントは少女たちにつきまとわれ、女性を口説こうとするたびに邪魔されるようになる。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋文", "恋文", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "日本人女優で初めて役者から監督へ転向した、田中絹代の初演出作品。外国兵相手の娼婦たちに、ラブレターの代筆をしてやる男と、彼を取り巻く女たちのふれあいを描く人間ドラマ。成瀬巳喜男の「あにいもうと」で助監督を務めたこともあるという田中絹代は、女性監督らしい、繊細なタッチをうまくいかしている。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Unthinkable", "4デイズ", "", "", "http://image.eiga.k-img.com/images/movie/56009/315x210.jpg?3906", 97, "かつてイラク戦争に従軍し祖国のために戦った米国民のヤンガーは、アメリカに対する憎しみからテロリストとなり、ある3都市に核爆弾を仕掛けたことを告白。FBI捜査官のヘレンと尋問のプロ“H”は、それぞれの方法でテロを阻止しようと奔走する。尋問のエキスパート、FBI捜査官、テロリストという立場の異なる3人の緊迫した心理戦を描くサスペンス。監督は「戦争のはじめかた」で注目を浴びた社会派グレゴール・ジョーダン。", "2011-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Did You Hear About the Morgans?", "噂のモーガン夫妻", "", "", "http://image.eiga.k-img.com/images/movie/55079/315x210.jpg?4395", 103, "サラ・ジェシカ・パーカー＆ヒュー・グラント主演のラブコメディ。ニューヨーク・マンハッタンの高級アパートに住み、人も羨む生活を送るモーガン夫妻だが、すでに結婚生活は崩壊し、離婚の準備中。そんな2人が街角で殺人事件を目撃。FBIの目撃者保護プログラムによりワイオミングの田舎で過ごすことになるが……。監督は「トゥー・ウィークス・ノーティス」のマーク・ローレンス。", "2010-03-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Duchess", "ある公爵夫人の生涯", "", "", "http://image.eiga.k-img.com/images/movie/54159/315x210.jpg?3415", 110, "故ダイアナ妃の祖先デボンシャー公爵夫人のスキャンダラスな実話を映画化。「つぐない」のキーラ・ナイトレイが、波乱に満ちた人生を駆け抜けた公爵夫人を熱演。18世紀後半のイギリス、貴族の娘ジョージアナ・スペンサーは、世界的に裕福なデボンシャー公爵のもとへ嫁ぐことになる。新生活に大きな期待を抱くジョージアナであったが、結婚後まもなく厳しい現実に直面することになる。第81回アカデミー賞衣装デザイン賞を受賞したコスチュームにも注目。", "2009-04-11", "3,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤胴鈴之助", "赤胴鈴之助", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "両親の顔を知らぬまま鉄斎老人に育てられた鈴之助は、修行のため亡き父と同門であった千葉周作の道場に入るが、朝から晩まで雑用ばかりで大くさり……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANTRAP", "罠！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "殺人罪で刑務所に入れられたマーヴィンは、無実を証明するために脱走する。その知らせを聞いて、マーヴィンの妻テルマは仕事先の上司ジェルハートとともに激しく動揺していた。一方、マーヴィンの依頼を受けた探偵ビショップは、２年前の事件を再調査する。ジェルハートが怪しいと疑ったビショップは、ある作戦を立てる……。", "1952-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flypaper", "フライペーパー！　史上最低の銀行強盗", "", "", "http://image.eiga.k-img.com/images/movie/58029/315x210.jpg?4153", 88, "「ハングオーバー！　消えた花ムコと史上最悪の二日酔い」も手がけたジョン・ルーカス＆スコット・ムーアの脚本家コンビが、密室を舞台に銀行強盗たちが繰り広げる騒動を描いたアクションコメディ。主演はパトリック・デンプシーとアシュレイ・ジャッド。「ホーンテッドマンション」「ドラゴン・キングダム」のロブ・ミンコフ監督がメガホンをとった。とある銀行に勤めるケイトリンは、客としてやってきたハンサムだがちょっと変わった男性トリップに応対していたところ、2組の強盗が同時に銀行を襲い、職員や他の客とともに人質として閉じ込められてしまう。銀行強盗たちはトリップの仲介でそれぞれ金庫とATMの金を分け合うことになるのだが、トラブルが続出し……。", "2012-06-16", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NICHOLAS AND ALEXANDORA", "ニコライとアレキサンドラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 171, "帝政ロシア最後の王朝ロマノフ家滅亡の悲劇を描く歴史スペクタクル。ニコライ二世、アレクサンドラ皇后、怪僧ラスプーチン、レーニンといった歴史上の人物が織り成すドラマが骨太に描かれている。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CON　AIR", "コン・エアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "敏腕プロデューサーのJ・ブラッカイマーが、「ザ・ロック」に続いてN・ケイジを主演に据えたサスペンス・アクション。不運な事件に巻き込まれて投獄された元軍人キャメロン・ポーが、８年の刑期を終えて出所の日を迎える。一刻も早く愛する妻子の許に帰りたいポーだったが、彼が乗った連邦保安局の空輸機（コン・エアー）は凶悪犯サイラス率いる囚人グループにハイジャックされてしまう。ポーの連絡を受けた連邦保安官ラーキンや警察隊も駆けつけ、砂漠の飛行場で死闘の幕が切って落とされる。空と陸を舞台に、囚人VS囚人による壮絶なバトルが展開。爆破、銃撃のスペクタクルのみならず、個性派俳優が演じる囚人たちのキャラクターを描き込んで、“男の魅力”をエネルギッシュに発散させている。なかでも恐怖の快楽殺人鬼との触れ込みで機に乗り込んでくるものの、結局は何もしないに等しい怪人ガーランド役、S・ブシェーミの存在が面白い。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PEAU　D’ANGE", "天使の肌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「花咲ける騎士道」のヴァンサン・ペレーズが、長編初監督に挑んだラブ・ストーリー。衝撃的な出会いをした孤独な男女を「ポーラX」のギョーム・ドパルデューと、映画初主演のフランス期待の新星モルガン・モレが演じる。貧乏な家を出て、住込みで働くアンジェル。ある日、彼女は街に偶然やって来たグレゴワールと出逢う。互いの心が通じ合った二人は一夜をともにすることに。翌朝、グレゴワールは去って行くが、彼を追うようにアンジェラも街を出る。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAT AND DUST", "熱砂の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "1920年代の英国支配下のインドへ行ったオリビアは、インドの大守と恋に落ちてしまう。そして半世紀後、自分の大叔母でもあるオリビアの軌跡をたどるため、アンはインドへ赴く。オリビアのミステリアスでドラマティックな人生とインドの持つ抗しがたい魅力に取り憑かれた彼女の軌跡を追ううちに、アンもまた、オリビアと同じような生き方をしている自分を見いだしていく……。1975年にブッカー賞を受賞した原作を、著者自身が脚本化したこの作品は、1983年の英国アカデミー賞脚本賞と英国映画批評家協会脚本賞を受賞。J・アイヴォリー監督の代表作でもある。", "1982-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("辣手神探", "ハード・ボイルド／新・男たちの挽歌", "", "", "http://ecx.images-amazon.com/images/I/51UMQSY-ldL._SL210_.jpg", 127, "「男たちの挽歌」のジョン・ウーとチョウ・ユンファが再び手を組んだハードボイルド・アクション。中国返還直前の香港を舞台に、刑事に扮するユンファと、麻薬捜査官に扮するトニー・レオンが武器密輸団相手に繰り広げる壮絶な戦いを描く。クライマックスのセキュリティシステムで密閉された病院内での銃撃戦では、チョウ・ユンファとトニー・レオンが絶妙のコンビネーションを見せる。ジョン・ウー監督、ハリウッド入り直前の傑作。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAMAYANA", "ラーマーヤナ　ラーマ王子伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "インドでは知らぬ者のいない世界的な叙事詩『ラーマーヤナ』を、日本のスタッフが中心となってアニメ化。ラーマ王の若き日の逸話や英雄行為を、神話的な世界観の中に写し出す。深遠さより、単純明快なアクション・エンターテインメントとしての側面を強調。「ムトゥ　踊るマハラジャ」のヒットに便乗して公開が実現した。", "1993-01-01", "21,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUBLE JEOPARDY", "ダブル・ジョパディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "“誰もが同一の犯罪で二度有罪にはならない”という合衆国憲法で定められた法律を逆手に取った一人の女の復讐劇を描くサスペンス。ある日、夫と出かけたセーリングで妻リビーが目覚めると夫が忽然と姿を消していた。夫殺しの濡れ衣を着せられ有罪を言い渡された彼女に、さらなる悪夢が襲いかかる。死んだはずの夫が、息子を預けた親友と暮らしていることが発覚。絶望と怒りに震えるリビーは、刑務所の仲間から二重処罰を禁ずる法律の存在を教えられ、夫の殺害を誓う。６年後出所した彼女は我が子を取り戻すため夫への復讐に燃えるが、そんな彼女の前に保護監察官トラヴィスが立ちふさがる。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トラック野郎・故郷特急便", "トラック野郎・故郷特急便", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "高知へ向かうフェリーでドサ回り演歌歌手に一目惚れした桃次郎は、高知のドライブ・インで働く風美子にも入れ揚げて忙しい。これにトラック野郎とヤクザ興行主との闘犬による対決も絡む。果たして桃次郎は両手に花といくのか……。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Girlfriend Experience", "ガールフレンド・エクスペリエンス", "", "", "http://image.eiga.k-img.com/images/movie/55499/315x210.jpg?1696", 77, "「オーシャンズ11」「トラフィック」のアカデミー賞監督スティーブン・ソダーバーグが、主演に人気ポルノ女優サーシャ・グレイを迎えて製作した恋愛ドラマ。リーマン・ショック直後の08年秋のニューヨーク。エリート相手に恋人と過ごすようなひとときを提供し、1時間2000ドルを稼ぐ高級コールガールのチェルシーは、理解ある恋人クリスとともに充実した人生を送っていた。そんな彼女の前に心惹かれる男性客が現れ……。", "2010-07-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Spider-Man 2", "スパイダーマン2", "", "", "http://image.eiga.k-img.com/images/movie/1179/315x210.jpg?1367", 127, "大学やアルバイトに加え、スパイダーマンとして闘うピーターは、憧れのメリー・ジェーンとすれ違うばかり。スパイダーマンへの復讐を誓う親友のハリーともうまくゆかず、人間関係に悩む。そんな時、優秀な科学者であるDr.オクタビウスに会うが、彼は実験中の事故で凶暴な怪人ドック・オクに変身してしまう。監督、キャストのほか、音楽、特殊効果スタッフも続投。撮影は「マトリックス」シリーズのビル・ポープに交代。", "2004-07-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRENCH CANCAN", "フレンチ・カンカン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ベル・エポックのパリを舞台に、ムーラン・ルージュの創始者で、女性には移り気な興行師ダンクラールの生き様と、フレンチ・カンカンの誕生を描いたルノワールの佳作。父ルノワールの印象派絵画を思わせる色彩美あふれるシーンの中に、人生の華やかさと苦しみが鮮やかに描かれている。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RETURN OF THE FLY", "蝿男の逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "前作「蝿男の恐怖」で悲惨な最期を遂げた科学者の息子フィリップ。そんな彼もまた父と同じ物質転送の研究を開始。しかし、フィリップの才能をねたむ何者かの罠により、あの忌まわしい事件が再び……。前作同様、主演はV・プライス。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hurricane", "ザ・ハリケーン", "", "", "http://image.eiga.k-img.com/images/movie/1829/315x210.jpg?4245", 145, "その驚異的な強さで“ハリケーン”の異名を持つボクサー、ルービン・カーター。1966年、彼は故郷パターソンで白人3人を殺害した容疑で逮捕され、終身刑を宣告される。無実を訴えて獄中で書いた自伝を出版し反響を呼ぶが、再審で再び有罪判決を受け、カーターの存在は次第に世間から忘れられていった。レズラ少年が古本市でカーターの本を見つけたのは、まさにそんな時だった。カーターの生きざまに胸を打たれたレズラは、その思いを手紙に託しカーターへ送る。", "2000-06-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲゲゲの鬼太郎", "ゲゲゲの鬼太郎", "", "", "http://image.eiga.k-img.com/images/movie/33949/315x210.jpg?2802", 103, "1960年代の連載開始以来、5度に渡ってアニメ化され、世代を越えて愛され続ける水木しげるの名作妖怪漫画を、最先端のVFX満載で初の劇場版実写映画化。恐るべき魔力を持つ“妖怪石”を巡り、鬼太郎や仲間たちが史上最大の戦いを繰り広げる。鬼太郎を人気デュオWaTのウエンツ瑛士が演じるほか、ねずみ男に大泉洋、猫娘に田中麗奈、子なき爺に間寛平が扮するなど、漫画やアニメでおなじみの妖怪のキャスティングにも注目したい。", "2007-04-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぱぴぃオールドマン", "ぱぴぃオールドマン", "", "", "http://image.eiga.k-img.com/images/movie/57729/315x210.jpg?4819", 72, "青春と性をテーマに監督たちが独自の個性で自由に製作する「青春H2（セカンド）」シリーズの9作目。監督した自主映画「脚の生えたおたまじゃくし」（2009）でゆうばりファンタスティック国際映画祭審査員特別賞を受賞した個性派俳優の前野朋哉が、親子の絆を描く。親の遺産を食い潰しながら生活している茂と、漫画家を目指すと言いながら漫画が描けない茂の息子とおる。ある日、2人の前に韓国人留学生キム・ヘソンが現れる。美人のヘソンを前にして急に張り切りはじめた茂をとおるは冷めた目で見ていたが、2人のヘソンに対する気持ちは日に日に高まって……。ヘソン役に人気AV女優の沖田杏梨が扮する。", "2012-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミスター・ルーキー", "ミスター・ルーキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "2002年のプロ野球、阪神タイガースの春の快進撃と歩調を合わせるかのように劇場でヒットしたスポーツ・ドラマ。阪神タイガースに突然、現れた覆面リリーフ・エース。甲子園でしか登板せず、インタビューも受けない、この男の正体は、昼はビール会社で働いている妻子持ちのサラリーマンだった。彼の活躍によってタイガースは優勝争いに食い込むが……。一度は諦めた夢を追う主人公の奮闘、妻子との愛情のドラマが人間味豊かに描かれる。ランディ・バースをはじめ新旧の実在する選手の出演も話題を呼んだ。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒心鬼／THREE WISHES", "ツイ・ハークのゴーストホーム —13日の金曜日の妻たちへ—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ツイ・ハーク監督がSFXを担当したロマンティック・コメディ・ホラー。妻と弟の不倫関係を疑う主人公チャン。だが弟の恋の相手は、実は妻の妹の幽霊だった。そこにあの世から黒心鬼と名乗る悪魔が現れたから、さあ大変。アニタ・ムイほかキャストも充実。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eternal Summer", "花蓮の夏", "", "", "http://image.eiga.k-img.com/images/movie/53109/315x210.jpg?1912", 95, "お調子者のショウヘンの面倒を見てきた優等生のジェンシン。小学校の頃からの幼なじみで今は高校に通う2人の前に、転校生のホイジャがやってくる。周囲に上手くなじめないでいたホイジャは、心優しいジェンシンに惹かれていくが、ある時、ジェンシンが密かに抱くショウヘンへの想いを知ってしまう。一方、ショウヘンはホイジャへの恋心を募らせ……。台湾東部の花蓮県を舞台に、同性愛を含む男女3人の三角関係を描いた青春ストーリー。", "2007-11-10", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alatriste", "アラトリステ", "", "", "http://image.eiga.k-img.com/images/movie/53989/315x210.jpg?3166", 145, "「ロード・オブ・ザ・リング」「イースタン・プロミス」のビゴ・モーテンセンが全編スペイン語の台詞に挑戦した17世紀のスペインを舞台にしたスペクタクル・ドラマ。スペインの人気作家、アルトゥーロ・ペレス＝レベルテの同名小説の映画化で、共演に「バンテージ・ポイント」のエドゥアルド・ノリエガ、「パンズ・ラビリンス」のアリアドナ・ヒルら。孤高の剣士アラトリステは、戦死した友との誓いを守ってマドリードに戻り、友の息子を引き取るが、奇妙な暗殺の依頼が舞い込み……。", "2008-12-13", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Haunting Lover", "ホーンティング・ラヴァー　血ぬられた恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "中国の広州。学歴のない無職の青年リャンクァンは、恋人シウチャンの叔父の紹介で老舗製薬工場の面接に向かう。しかし、数年前に失踪した工場の従業員チャンクァンにうり二つのリャンクンは、工場長から気味悪がられ不採用となってしまう。落胆するリャンクァンは、帰り道に工場内の有力者を助けたことがきっかけで仕事に就けることになるが……。「カンフーシェフ」で共演したバネス・ウーと加護亜依が再共演する香港のホラー・ファンタジー。", "2010-08-07", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONTINENTE PERDUTO", "失われた大陸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「緑の秘境」で南米大陸の神秘を追い求めたL・ポンツィ伯爵が、東南アジアの秘境に挑んだドキュメンタリー。1953年の夏より17ヵ月にわたって撮影され、最後の首狩り族といわれるダヤク族の姿など貴重なシーンが収められている。", "1955-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOCTOR DOLITTLE", "ドリトル先生 不思議な旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "H・ロフティングのベストセラー初の映画化。西イングランドの港町に住み、動物の言葉を理解する不思議な医師ドリトル先生が、動物たちと一緒に帆船で冒険旅行に出かける……。主人公の人間味あふれるキャラクターが印象的。人間と動物の競演が楽しいミュージカル・ファンタジー。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沈睡的青春", "午後3時の初恋", "", "", "http://image.eiga.k-img.com/images/movie/54019/315x210.jpg?3209", 92, "台湾シネマ・コレクション2008上映作品。「花蓮の夏」のジョセフ・チャンと、モデル出身のグォ・ビーティンが主演するノスタルジックな恋愛映画。午後3時に必ずチンチンの古びた時計店を訪れるハンは、メガネをかけた無口な青年。チンチンはそんなハンに惹かれていくが、やがてハンが10年前に亡くなっていることを知る。時計が止まったかのように同じ日課を繰り返す2人が、新しい1歩を踏み出していく姿を描く。", "2008-08-30", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les adieux a la reine", "フェアウェル・マイ・クイーン（英題）", "", "", "http://image.eiga.k-img.com/images/movie/58009/315x210.jpg?3392", 0, "", "2012-12-31", "2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROOFTOPS", "ルーフトップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "２度のオスカー受賞や、「ウエスト・サイド物語」で、それまでのミュージカルの概念を破ったといわれるR・ワイズ監督が描いた青春グラフィティ。ニューヨークの下町にたむろする少年たち。彼らは廃墟と化したビルの屋上に住み、仲間同士で独特の世界を作っている。一匹狼のTは、仲間同士のパーティーが最高の楽しみ。彼はそこで出会ったエレナと惹かれ合うが、エレナはTと敵対するロボの仕事仲間だったのだ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラVSモスラ", "ゴジラVSモスラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "シリーズ第19作。前２作では監督、脚本を手掛けた大森一樹は、ここでは脚本のみに専念。黒澤明や浦山桐郎らの下に師事し、「超少女REIKO」でデビューした大河原孝夫が監督を務め、スケールの大きい娯楽作に仕上げた。見どころは、シリーズ中１、２を争う人気を誇るモスラの復活。そしてその宿敵となる新怪獣、黒いモスラ“バトラ”の登場だ。この２大怪獣とよみがえったゴジラが、名古屋、横浜を舞台に三つ巴の壮絶な死闘を繰り広げる。また過去に、“ザ・ピーナッツ”が演じて人気を集めた小美人も、新人二人を起用して復活。懐しいあのモスラの歌も披露し話題となった。森林伐採によって起きた土砂崩れ現場で巨大な卵が発見される。その卵から誕生したモスラは、隕石の影響で目覚めたゴジラと対決。そこへ、黒いモスラが現れ……。", "1992-12-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CROWDED SKY", "翼の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "混雑した空で海軍のジェット機と、62名の乗客を乗せた民間旅客機が接近遭遇。ジェット機を操縦する中佐には、空中衝突で部下を死なせた苦い経験があった。二つの飛行機に乗る人々はそれぞれの過去を振り返る。死の淵に追い込まれた人々の様々な人間模様を、スリルいっぱいの展開の中に描き出したパニック・ドラマ。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCROOGED", "３人のゴースト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "IBCテレビの若社長フランクは、常軌を逸したスクルージ(＝守銭奴)。金のためなら、人の命も二の次、三の次。そんな彼の前に、クリスマス・イヴの晩３人のゴーストが現れ、過去、現在、未来の彼の姿をまざまざと見せつける。チャールズ・ディケンズの『クリスマス・キャロル』を下地に愛の大切さを謳ったクリスマス・メルヘン。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGHWAY TO HELL", "地獄のハイウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "極秘結婚を挙げるため、ラスベガスへ向かう途中のチャーリーとレイチェルは、真夜中のハイウェイで、パトカーに止められ、レイチェルが連れ去られてしまう。このパトカーに乗っていたのは、地獄から来た“ヘルコップ”だった。あとを追うチャーリーは、途中のガソリンスタンドでサムという老人から、車と武器をもらい、地獄へ向かう。サムも40年前、婚約者をヘルコップに誘拐されていたのだ。時空を越え、チャーリーとヘルコップのノンストップチェイスが始まる。ロブ・ロウの実弟C・ロウがチャーリーに扮した、コミカルなSFアドベンチャー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PADRE PADRONE", "父／パードレ・パドローネ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "原作者ガビーノが登場して語る生い立ち。6歳から山で羊飼いの修行を強いられたガビーノ。恐い父と厳しい自然に育てられ無口な羊飼いに成長した彼だったが、音楽との出会い、軍隊での学問との出会いを経て、やがて強権的な父と対決する時がやってくる……。沈黙からコミュニケーションへという主題を壮絶に美しく描く、タビアーニ兄弟の日本での劇場公開第1作。", "1982-07-31", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VILLAGE OF THE DAMNED", "未知空間の恐怖 光る眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ウィンダムのSF小説『呪われた村』の映画化。イギリス南部の小さな村が、奇妙な霧に包まれ一時仮死状態となる。二ヵ月後、若い女性12人がすべて妊娠し、そして生まれてきた12人の子供たちは、異常に高い知能と、光る眼を持ったエイリアンであった……。リラの演出が冴える恐怖映画の名作。本作のヒットを受け続編「続・光る眼／宇宙空間の恐怖」が、後にリメイク版も製作された。DVDは「続・光る眼／宇宙空間の恐怖」も収録。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真田風雲録", "真田風雲録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "日本ではジャンルとして成立しなかったミュージカル映画だが、何本かは快作が作られている。マキノ雅広の「鴛鴦歌合戦」という今ではよく知られた時代劇ミュージカルもあるが、この「真田風雲録」はそのメチャクチャさかげんにおいて「鴛鴦歌合戦」にひけをとらない大爆笑の和製ミュージカル・コメディ。猿飛佐助をはじめとする真田十勇士の破天荒な暴れぶりを、忍術とギャグと歌で綴った痛快きわまりない一編。もとは舞台で上演されていた作品の映画化で、加藤泰は面白いと即座に引き受けて完成。あまりに突飛な出来で興行的には大失敗。紅一点・お霧（霧隠才蔵）を演じる渡辺美佐子が印象的。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL SUR", "エル・スール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1957年秋、15歳の少女エストレリャは、父がもう帰ってこないと予感する。ただ父と一緒にいるのが嬉しかった幼い頃には、彼の過去にも内戦にも考えは及ばなかった。だんだんと離れていった父との最後の記憶は、初聖体拝受式でのダンス。南にはなにがあるのか……。名優オメロ・アントヌッティを迎え、少女の目を通して暗いスペインの歴史を描く、ビクトル・エリセ監督の珠玉の秀作。", "1985-10-12", "9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Intolerable Cruelty", "ディボース・ショウ", "", "", "http://image.eiga.k-img.com/images/movie/1359/315x210.jpg?4565", 102, "離婚訴訟専門の腕利き弁護士マイルズ。彼が次に手掛ける依頼人は、妻に浮気の証拠を握られた夫レックスロス氏。離婚の条件を少しでも有利にするため、依頼人の美しい妻マリリンに会ったマイルズは、彼女が一筋縄ではいかない女性だと知りつつ、彼女に惹かれていく。コーエン兄弟映画にジョージ・クルーニーとキャサリン・ゼタ＝ジョーンズが新たに顔合わせ。カメラはコーエン作品の常連、名手ロジャー・ディーキンスが担当。", "2004-04-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZABRISKIE POINT", "砂丘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "“愛の不毛の作家”M・アントニオーニがアメリカを舞台にして現代社会の病巣を描き上げた作品。学園紛争の嵐吹き荒れる時代、警察の手を逃れた学生マークと、仕事から逃避して放浪中のダリアは、砂漠地帯で出会い愛し合う。いつものアントニオーニ作品同様、ストーリーよりも心象風景に重きが置かれた作品。", "1970-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sunset Boulevard", "サンセット大通り", "", "", "http://ecx.images-amazon.com/images/I/51j7kkLtrJL._SL210_.jpg", 110, "　映画の都ハリウッドのサイレント時代と50年代、2つの時代がシンクロする作品。「ハリウッドでハリウッドを描く作品が作りたかった」とビリー・ワイルダーは製作意図を語っている。売れない脚本家ギリスは、無声映画時代の大女優ノーマの家へ迷い込む。華やかな世界への返り咲きを願う彼女は、ギーリに脚本の手直しを頼むが……。ノーマ役のグロリア・スワンソンの緊迫感あふれる演技は秀逸。「クレオパトラ」などの監督として知られるセシル・B・デミルが、本人役で登場している。", "1951-10-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シャッフル2 エクスチェンジ", "シャッフル2 エクスチェンジ", "", "", "http://image.eiga.k-img.com/images/movie/55939/315x210.jpg?3054", 85, "サンドラ・ブロックが、シャッフルされた1週間に翻ろうされるヒロインを演じた「シャッフル」のシリーズ第2弾。「ラストサマー」「THE JUON 呪怨」のサラ・ミシェル・ゲラーが主演を務める。弁護士のジェスは、愛する夫ライアンと幸せに暮らしていた。しかし、夫の弟・ローマンが保護観察付きで出所してきたことをきっかけに、2人の完ぺきな生活が崩れ始める。", "2011-01-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太の大魔境", "ドラえもん のび太の大魔境", "", "", "http://ecx.images-amazon.com/images/I/21TNRBEV49L._SL210_.jpg", 92, "夏休みのある日、のび太の家に子犬が紛れ込んでくる。ペコというその子犬とともにアフリカのジャングルへ行くことになったドラえもんたちは、その地に伝わる巨神像のナゾを解くことに。そこは犬の王国で、ペコはその国の王子だったのだ。世界征服をもくろむ悪大臣、ダブランダーにのび太たちが挑んでいく。", "1993-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Cantante de Tango", "タンゴ・シンガー", "", "", "http://image.eiga.k-img.com/images/movie/54999/315x210.jpg?4641", 105, "恋人に捨てられた女性タンゴ・シンガーの孤独と再生を、切ないタンゴの調べに乗せて描いたアルゼンチン発の人間ドラマ。タンゴ・シンガーとして順調な道を歩んでいたエレナは、ある日突然恋人から別れを告げられてしまう。失恋を受け入れられない彼女は元恋人を執拗なまでに追い続けるが……。本作のために1年半に及ぶタンゴのレッスンを受けたという主演女優エウヘニア・ラミレス・ミオリの美しい歌声が話題となる。", "2009-10-21", "37,22,2,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANNEKEN PIS", "小便小僧の恋物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ブリュッセルの観光名所、小便小僧をモチーフにした甘く切ないラブ・ストーリー。子供の頃に、家族を目の前の電車事故で失った孤独な青年ハリー。ブリュッセルにやって来た彼は、路面電車の運転手ジャンヌに出会い恋をする。同じアパートの住人である二人の気持ちは次第に近づいていき、ある日ハリーはジャンヌに金色の靴をプレゼントする。だが、彼女が“愛してる”とささやいたことで二人の関係はぎくしゃくとなり、彼女を愛しながらも、子供の頃受けた傷が原因で、愛してるの一言がハリーは言えず、はかない恋は悲劇へと向かう。", "1995-01-01", "37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天若有情２.之天長地久／A MOMENT OF ROMANCEII", "風よさらば／天若有情 II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "香港の人気スター、A・クォック主演のラブ・ストーリー。無実の罪で刑務所にいる弟を救うため、売春組織に身を落とす覚悟で大陸からやってきたセリアは、組織の殺人事件に巻き込まれマフィアと警察に追われるはめに。逃走中、バイクレーサーのフランクと出会い、いつしか二人は心惹かれ合い、愛を育んでいくのだが……。マフィアに捕らわれた恋人を救うために命を懸ける、バイクレーサーの死闘を描く。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本侠客伝 絶縁状", "日本侠客伝 絶縁状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "東京に大きな勢力を持つ天盟会。その傘下の浜田組組長・浜田勇吉は、獄中にある会長の釈放を条件に組を解散し、堅気になった。一方、天盟会会長の座を狙う上野は、浜田組を制圧しようと企んでいた。義兄の平井が上野組に襲われ、憤った浜田は上野組に斬り込む。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仁義 JINGI", "仁義 JINGI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "立原あゆみ原作の人気コミックの映画化。“１発の弾丸”によって結ばれた情に厚く女好きなチンピラヤクザ・仁と、東大卒のエリート崩れ・義郎。性格も環境もまったく違う二人の無頼漢を軸に、膨脹するバブル経済の真っ只中でヤクザ渡世をのし上がっていく姿を痛快に描いていく。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("当選確実", "当選確実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "12年間、まったくの無風であった桐野市市長選挙。３期に渡って対立候補のいない現職・錦本は裏では地上げ業者と手を組んでいる悪徳政治家だった。今回の選挙もまた、無風状態であった。投票日まで、あと３日と迫ったある日の午後、地上げ業者を人質に一人の男がビルに立てこもった……。森田健作の参議院出馬のため、社会的影響を考え完成以来６ヵ月以上も公開が遅れた政治ドラマ。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("木石", "木石", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "肺を病んで長い闘病生活を続けていた五所平之助監督の再起第１作。追川初は有島伝染病研究所に25年間勤務し、40歳を越すまで独身を通していたが、はからずも襟子という年頃の娘がいることがわかる……。襟子役には病気の高峰三枝子に代わり、木暮実千代が出演し、大役をこなした。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ポケットモンスター　ベストウイッシュ　ビクティニと白き英雄　レシラム", "劇場版ポケットモンスター　ベストウイッシュ　ビクティニと白き英雄　レシラム", "", "", "http://image.eiga.k-img.com/images/movie/56039/315x210.jpg?1332", 98, "2010年から始まった人気アニメ「ポケットモンスター」の新シリーズ「ベストウイッシュ」の劇場版。1本の物語をベースに、登場するポケモンやシーン、セリフの異なる「ビクティニと黒き英雄　ゼクロム」「ビクティニと白き英雄　レシラム」の2作品を同日公開する。かつて「大地の民の王国」として栄えたアイントオークにやってきたサトシらは、不思議なパワーをもつ幻のポケモン、ビクティニに出会う。しかし、王国復活を願う大地の民の末えいがビクティニの力を利用しようと企み、その影響で街に異変が発生してしまう。", "2011-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bloodrayne: The Third Reich", "ブラッドレイン　血塗られた第三帝国", "", "", "http://image.eiga.k-img.com/images/movie/57589/315x210.jpg?2251", 79, "「ハウス・オブ・ザ・デッド」のウーベ・ボル監督が手がけるバンパイアアクション「ブラッドレイン」シリーズ第3作。1943年、ナチス・ドイツが台頭するヨーロッパのとある強制収容所で、捕えられたバンパイアを使った人体実験が行われていた。ハーフバンパイアのレインはレジスタンスとともにナチスと戦うが、収容所の司令はレインの血を手に入れ、最強のバンパイア・デイウォーカーに変身。その血をヒトラーに捧げて不滅の総統を生み出そうと企む。主演は前作「ブラッドレインII」に続きナターシャ・マルテが務める。", "2012-03-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A DOG’S LIFE", "犬の生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "チャップリンが活動の場をミューチュアル社から新会社ファースト・ナショナルに移して撮った第１作。ドタバタ喜劇からストーリー作品への、彼の移行期に位置する。浮浪者チャーリーは職探しに失敗したあと、一匹の小さなノラ犬を野犬の群れから助け出す。それが縁で一人と一匹の共同生活が始まるが……。酒場の娘とのロマンスの味つけも効いて、涙と笑いを共存させる喜劇スタイルがここに誕生した。原題には“みじめな生活”という意もある。DVDは「ラヴ・チャップリン！　短編集」に収録。", "1918-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ME AND YOU AND EVERYONE WE KNOW", "君とボクの虹色の世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "'05年のカンヌ映画祭でカメラドール（新人監督賞）を受賞。多彩な活動を続ける女流アーティスト、ミランダ・ジュライが監督・主演を務めた群像劇だ。アーティストを目指すクリスティーンが、靴屋の店員リチャードと出会う。互いに心引かれる彼らだったが、素直に一歩を踏み出せない。一方、彼らの周囲の人々もそれぞれの問題に直面し……。色彩感覚あふれる映像が、孤独で不器用な人々のドラマをほのぼのとした空気で包み込む。温かい視線や、ツボを心得た音楽の使い方も魅力的だ。", "2006-04-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BRIDES DRACULA", "吸血鬼ドラキュラの花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ハマー・プロの十八番とも言うべきドラキュラもので、「吸血鬼ドラキュラ」に続いて製作された。しかし、ドラキュラ自身は登場せず、ヘルシング教授とドラキュラの弟子マインスター男爵との戦いが展開する。教授役のP・カッシングをはじめ、監督のT・フィッシャーなどのスタッフは、前作の「吸血鬼ドラキュラ」とほとんど同じ布陣。", "1960-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人形芸人ドント＆ノット", "人形芸人ドント＆ノット", "", "", "http://image.eiga.k-img.com/images/movie/54729/315x210.jpg?4245", 60, "深夜バラエティ番組「バミリオン・プレジャー・ナイト」からスピンアウトした、フナビキアキ監督によるクレイアニメーション。TV放映されなかった未公開エピソードや、ドント＆ノットのコンビ結成の秘密に迫る「スクープ！　ドントノット誕生秘話」を収録。声優に友近やおかけんたなど、吉本の芸人を多数起用している。三十路間際の女人形芸人ドントとノットが、真のお笑いと真の友情を求めて劇場、TVを駆け回り、夢と恋に揺れながら、漫才に全力投球する姿を描く。", "2009-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Camellia", "カメリア", "", "", "http://image.eiga.k-img.com/images/movie/56919/315x210.jpg?2472", 143, "タイ、日本、韓国の3人の監督が過去、現在、未来の釜山を舞台にそれぞれ描いたオムニバスラブストーリー。「快盗ブラックタイガー」のウィシット・サーサナティヤン監督による、1970年代の釜山にやってきたタイのスパイの悲恋を描いた「アイアン・プッシー」、少女とカメラマンのほのかな恋を描く行定勲監督の「カモメ」、韓国のチャン・ジュナン監督による、記憶の売買がビジネスとして横行する未来都市で恋人の記憶と取り戻そうと奮闘する男の姿を描く「ラブ・フォー・セール」の3編で構成される。", "2011-10-22", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八月拾五日のラストダンス", "八月拾五日のラストダンス", "", "", "http://ecx.images-amazon.com/images/I/519Q9YJEM2L._SL210_.jpg", 92, "太平洋戦争末期を背景に、激戦地で出会った男女が織りなすはかない恋をつづった戦争ドラマ。死の行軍を続けていた陸軍烈部隊の山瀬孝雄一等兵は、その途中で従軍看護婦の市川春香と出会う。仲間たちが次々と死んでいき、ついに2人きりになってしまった山瀬と春香は、逃げこんだ洞窟の中で夢を語りあうが……。山瀬役に「忍風戦隊ハリケンジャー」の山本康平。「零　ゼロ」の井出良英が監督・脚本を手がける。", "2005-08-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONAMOUR", "ティント・ブラスの白日夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "御年70歳を超えても濃厚なエロスを追究し続けるイタリアの大家ティント・ブラスが、ブロンド巨乳女優を起用して放つ官能映画。夫との性生活に満たされず妄想に耽る美人妻のアバンチュールを、これでもかと奔放なセックス・シーン満載で描く。夫への欲求不満を抱く美女マルチナは、エッチな妄想に耽る日々を送っていた。旧友シルビアから“夫を挑発しなさい”と忠告された彼女は、とどまることを知らない大胆な行動を連発し……。", "2006-06-10", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Game Plan", "ゲーム・プラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅の豚", "紅の豚", "", "", "http://ecx.images-amazon.com/images/I/51ZZauffo-L._SL210_.jpg", 91, "宮崎駿監督、原作、脚本、スタジオジブリ制作による劇場用アニメーション作品。第一次大戦後のイタリア、アドレア海。暴れまわる空賊相手に賞金稼ぎをしていた1匹の豚、ポルコ・ロッソは、自分に魔法をかけて豚になってしまった飛行機乗りだった。ポルコを倒すため、空賊たちは彼のライバル、ドナルド・カーチスを雇うのだった。本作の主題歌を歌う加藤登紀子は、声優も務めた。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PERSONNE　NE　M’AIME", "だれも私を愛さない！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "B・ラフォン、B・オジェが姉妹役に扮するなど、ヌーヴェル・ヴァーグ世代のベテラン俳優が味わい深い演技を披露する家族コメディ。絶縁中の母娘、夫の浮気現場を押さえようとする女らワケありの登場人物たちが、旅先でばったり出くわし、悲喜こもごもの人間模様を織り成していく。 1967年生まれのフランスの女流監督M・ヴェルヌの劇場デビュー作。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOLOCH", "モレク神", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "A・ソクーロフ監督の“ファシスト３部作”の第１作。映画は、ナチス総統ヒトラーが腹心を引き連れ、愛人エヴァが待つ山荘にやってくるところから始まる。そこでエヴァと二人きりになって子供のように振る舞ったり、自分を極度に蔑んでみたりするヒトラーのエキセントリックな行動を描き、一人の弱い人間としての独裁者の姿を浮き彫りにしていく。題名の“モレク神”とは、旧約聖書で戦火のシンボルとして記される神の名前のこと。", "1999-01-01", "24,7,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Speak", "スピーク", "", "", "http://image.eiga.k-img.com/images/movie/56909/315x210.jpg?3239", 83, "呪われた場所として恐れられる廃きょのホテルに乗り込んだ映画撮影クルーが体験する恐怖を、リアリティあふれる主観映像で描くフェイクドキュメンタリーホラー。売れない若手映画監督のシェリーは呪われたホテルで恐怖ドキュメンタリー映画を撮影し、名を上げようと目論む。管理人の忠告を無視してホテルに乗り込んだシェリーと撮影クルーらはカメラを回し始めるが、想像を絶する怪奇現象に見舞われ館内に閉じ込められてしまう。", "2011-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("БОРЕЦ И КЛОУН", "レスラーと道化師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "栄光の道を目指す、ロシアサーカス界に実在した二人のスターの愛と友情の物語。レスラーのポドゥブヌイと道化師のドゥーロフはサーカスに一緒に入団する。そこでは団長の娘の駈け落ち、ドゥーロフの活躍をねたむ先輩ピエロ、ポドゥブヌイと恋に落ちたブランコ乗りの少女の死など、彼らの前には恋人や家族との別れ、挫折、裏切りなど数々の試練が待ち受けていた……。そして、ポドゥブヌイはレスリング世界選手権へ出場することになる。", "1957-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星と俺とできめたんだ", "星と俺とできめたんだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "星川健次はナイトクラブ“地中海”のアルバイト歌手。健次の兄はアメリカ帰りの歌手マリを残して謎の事故死を遂げる。その背後には高速モーター・ボートのエンジンをめぐる企業間戦争があった。健次は兄を犠牲にした陰謀に立ち向かうが……。主役の西郷を、マリを愛する剣道家として渡哲也が脇で支えるアクションもの。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長崎の歌は忘れじ", "長崎の歌は忘れじ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "広島で被爆した田坂具隆が長い闘病生活の間に構想した物語を映画化したもの。長崎生まれで音楽家志望だったヘンリー・グレイは戦後再び長崎の地に足を踏み入れる。彼はハワイの日本人捕虜収容所で、瀕死の日本兵から未完の交響曲の楽譜を託されていた。ヘンリーはさっそく遺族を探し始めるが……。ヘンリー役は現役オペラ歌手だったA・ロールマン。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うる星やつら３ リメンバー・マイ・ラブ", "うる星やつら３ リメンバー・マイ・ラブ", "", "", "http://ecx.images-amazon.com/images/I/211YYYZ7SCL._SL210_.jpg", 90, "TVシリーズ『うる星やつら』の作画監督・演出を数多く手掛け、劇場版第２作の作画監督を務めた、やまざきかずおの初の劇場用アニメ監督作品。“友引メルヘンランド”の開園日にそこを訪れた諸星あたるは、ルウという魔術師の少年に、カバに変えられてしまう。そしてルウは想いを寄せるラムを、呪いの玉の力で異空間に閉じこめる。それを知ったあたるはラム救出のために、異世界に突入するが……。異世界の描写は前監督の押井守の影響が色濃いが、ラムとあたるのラブ・ストーリー的な面と、ウエットな雰囲気とが強調された映画作りがなされている。それなりのレベルで見せる作画に対して、物語のあいまいさが気にかかる。DVDタイトルは「うる星やつら　劇場版　リメンバー・マイ・ラブ」。", "1985-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真昼の暗黒", "真昼の暗黒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "“八海事件”に基づく、弁護士・正木ひろしの『裁判官』を原作として橋本忍が脚本を担当、今井正が演出した問題作。えん罪の恐ろしさをリアルでサスペンスあふれるタッチで描ききった作品である。当時、八海事件は裁判継続中だったため、最高裁が製作側に圧力をかけたというエピソードを残す。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gangster Squad", "ギャングスター・スクワッド（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("売春暴力団", "売春暴力団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "『週刊アサヒ芸能』に連載された同名コミックの映画化。舞台は大阪。資金源の大半を売春によるアガリに頼る弱小暴力団“生駒組”の組長の息子、金太郎は、極悪非道で無類の女好きだが、どこか憎めない男。彼の溺愛する妹、桜子が敵対する組織“鳴門組”の若頭、佐々木と恋仲になったことから金太郎は怒り心頭。抗争が始まるかに思われたが……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MOGILE PIU BELLA", "シシリアの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "イタリアの美人女優O・ムーティの映画デビュー作で、少女時代の彼女のみずみずしくも妖しい魅力を堪能できる１本。封建的な風習の残るシチリア島を舞台にしたハードなメロドラマだ。農民の娘フランチェスカが、マフィア一家のヴィトに見初められ強引に結婚を申し込まれる。ヴィトは事あるごとに反発するフランチェスカを服従させようとするが、強固な意思を持つ彼女はまったく屈服せず……。", "1970-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WELCOME　TO　DEATH　ROW", "ウェルカム・トゥ・デス・ロウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ヒップホップ史を塗り変えた“デス・ロウ・レコーズ”レーベルの裏側を追いかけた音楽ドキュメンタリー。映画は、悪名高き創始者シュグ・ナイトの実像から、同レーベル移籍後、凶弾に倒れることになるトゥパック・シャクールの悲劇までを網羅。辣腕プロデューサー、ドクター・ドレやラッパー、スヌープ・ドッグらの貴重なコメントをはじめ、見どころが盛りだくさんだ。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダーク・ラブ Rape", "ダーク・ラブ Rape", "", "", "http://image.eiga.k-img.com/images/movie/53369/315x210.jpg?3695", 0, "不動産業者がレイプを繰り返すというショッキングな内容で話題を呼んだ坂辺周一のコミック「レイプ」を、「オールナイトロング」の松村克弥監督が実写映画化。ひとり暮らしを始めた22歳の楓は、留守中に何者かが侵入した形跡を発見する。その犯人は、部屋を仲介した不動産屋の御子柴だった。親切そうに見える御子柴には連続レイプ犯という裏の顔があり……。「生きない」など映画監督としても活躍するダンカンがレイプ魔を怪演。", "2008-03-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Va Savoir", "恋ごころ", "", "", "http://image.eiga.k-img.com/images/movie/1729/315x210.jpg?1604", 155, "ご存知、ロメール、ゴダール、トリュフォーらの盟友、彼らが結集した「カイエ・デュ・シネマ」誌編集長だったリベットの長編第17作。撮影のリュプチェンスキーはじめ常連スタッフが集合、ルイジ・ピランデッロ作の劇中劇「あなたのお望みのまま」も挿入。劇団の主演女優カミーユは、舞台のために3年振りにパリに行く。彼女は、かつての恋人ピエールとの関係に決着をつけようと彼のもとを訪れるが、彼には新たな恋人がいた。", "2002-02-09", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブレイブ・ストーリー", "ブレイブ・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/1309/315x210.jpg?3825", 111, "宮部みゆきのベストセラー小説を、世界屈指のアニメーションスタジオGONZO、フジテレビ、ワーナー・ブラザース映画の3社が手を組んで作り上げたアニメーション大作。父親が家出をしたために、家庭崩壊の危機に直面した11歳のワタルが、運命を変えることが出来るという世界「幻界（ヴィジョン）」へと旅立つ。声の出演は、主人公ワタルに松たか子。監督は「LAST EXILE」「フルメタル・パニック！」などを手掛けた千明孝一。", "2006-07-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("保持通話", "コネクテッド", "", "", "http://image.eiga.k-img.com/images/movie/54639/315x210.jpg?4114", 110, "キム・ベイシンガー主演、デビッド・R・エリス監督によるハリウッド映画「セルラー」を香港でリメイクしたサスペンススリラー。ある日突然、理由も分らぬまま誘拐、監禁された女性グレイスは、部屋にあった壊れた電話を必死で直し、助けを求める。その電話を受けた冴えない男アボンは、否応なしに事件に巻き込まれ……。監督は「香港国際警察」のベニー・チャン。主演に「エレクション」のルイス・クーと、台湾の人気女優バービー・スー。", "2009-08-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋谷橋", "恋谷橋", "", "", "http://image.eiga.k-img.com/images/movie/55979/315x210.jpg?3477", 113, "東京のデザイン事務所で働いていた朋子は不景気でリストラされ、鳥取県の温泉街で老舗旅館「大橋」を経営する実家に戻ってくる。そんな折、父・和夫が病気で倒れてしまい、母・祥子は自分が旅館の旦那として役割を果たすかわりに、朋子に若女将として働いてほしいという。デザイナーの夢をあきらめきれない朋子だったが、温泉街に失われた活気を取り戻そうと奮闘する。ダンス＆ボーカルグループ「SPEED」の上原多香子が映画初主演。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("半斤八両／THE PRIVATE EYES", "Mr.Boo！ ミスター・ブー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "おなじみホイ３兄弟の記念すべき日本公開第１作。(“Mr.BOO！”のシリーズくくりは、日本公開時に付けられたもの。）長兄マイケルの探偵事務所に、弟たちが助手として参加し、次々と事件を解決？　していくのだ。サメ（ジョーズ）の頭がい骨と腸詰めヌンチャクの対決など、スッとぼけたギャグが全編にギッシリ。", "1976-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ざ・鬼太鼓座", "ざ・鬼太鼓座", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "加藤泰が佐渡を拠点とする鬼太鼓座の活動内容を紹介したドキュメンタリー。メンバーの鍛えられた肉体による太鼓演奏や日常のトレーニングの様子などを、ロケとスタジオ撮影を織り交ぜて追い求める。佐渡、福島、静岡、鹿児島と各地の四季の美しさも映し出していく。見事なドキュメンタリーであると同時に、まさに加藤泰の世界がそこに存在している。1981年に完成しながら、事情により長年公開されなかったが、1994年に加藤泰の特集の際に上映された。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緑山高校　甲子園編", "緑山高校　甲子園編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "『週刊ヤングジャンプ』連載で評判を呼んだおバカ野球漫画を原作に、OVAリリースをはさんで登場した映画版。９人のナインのハイ・テンションとミラクルぶりがある種、爽快でもあるスポ根コメディ・アニメだ。実写映画の「地獄甲子園」や「逆境ナイン」のノリだといえる。新設された緑山高校にさっそく野球部ができ、メンバー全員が甲子園を目指して信じられない活躍を見せる。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dave Chapplle's Block Party", "ブロック・パーティー", "", "", "http://image.eiga.k-img.com/images/movie/33739/315x210.jpg?4726", 103, "アメリカの人気スタンダップ・コメディアン、デイブ・シャペルの発案により2004年9月18日にニューヨーク・ブルックリンの路上で開催された一夜限りの無料シークレットライブの模様をとらえたドキュメンタリー。アカデミー脚本賞を受賞した「エターナル・サンシャイン」のミシェル・ゴンドリー監督が、カニエ・ウェストやローリン・ヒルらヒップホップ界を代表するアーティストたちのライブシーンから舞台裏まであますことなく映し出す。", "2006-11-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Typhoon", "タイフーン", "", "", "http://image.eiga.k-img.com/images/movie/1719/315x210.jpg?4551", 124, "「友へ／チング」のチャン・ドンゴン主演、クァク・キョンテク監督のコンビによるアクション大作。韓国映画史上最高の製作費180億ウォンを投じ、北はロシア・ウラジオストックから南はタイ南部のクラビにまで及ぶ壮大なロケを敢行、南北分断が生み出した悲劇に核弾頭の争奪戦を絡めた一大エンターテインメントを描き、韓国では歴代最多観客動員数を記録した「ブラザーフッド」が公開1週目に達成した177万人を超える180万人を、1週間で動員。韓国興行成績の新たな歴史を作った話題作。", "2006-04-08", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAITING FOR THE MOON", "月の出をまって", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "作家であるガートルード・スタインとその秘書アリス・B・トクラス。実在した二人の女性の友情を、ヘミングウェイ、ピカソ、アポリネールなどの若い芸術家たちとの交流を通して描き出した作品。といっても伝記でもなく、フィクションでもない。二人の著書から設定をとり出し、そこに虚構を織り交ぜて、ありし日の二人の姿を想起させるものとなっている。物語は、アリスの語りで過去に遡る。二人はアメリカからフランスに移住し、多くの芸術家たちが集うサロンに出入りする。夏は南フランスの別荘で暮らす二人の姿を詩的に映像化していく。1987年のアメリカン・フィルム・フェスティバルでグランプリ受賞。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋空", "恋空", "", "", "http://image.eiga.k-img.com/images/movie/34269/315x210.jpg?1794", 129, "中高生から圧倒的な支持を獲得し、出版後わずか1カ月で100万部を突破したケータイ小説を映画化。主演はテレビドラマやCMで大ブレイク中の新垣結衣。平凡な女子高生の美嘉は、同級生のヒロと恋に落ちる。2人は数々の障害を乗り越えながら絆を強めていくが、ある日突然、ヒロは美嘉に一方的に別れを告げる。やがて、自分に思いを寄せる大学生・優と付き合いはじめた美嘉の元に、衝撃的な知らせが届き……。", "2007-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITY STREETS", "市街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "「喝采」に続くR・マムーリアンの監督第２作で、暗黒街から足を洗おうとする若い男女の姿を描いた流麗なメロドラマである。またハードボイルド小説の鬼才D・ハメットが、唯一映画のために書き下ろしたオリジナルストーリーという点もファンなら見逃せないところ。血で血を洗うギャングたちに嫌気がさし、平凡な明日を夢見る青年キッドと、大組織の幹部の養女であるナンの恋。それをマムーリアンは相似形を活かした場面転換、映像と音声の対位法、象徴的な小道具のクローズアップなど鮮烈な演出で描き出している。名手L・ガームスの撮影もすばらしい。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PINK CADILLAC", "ピンク・キャデラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "失敗ばかりの頼りない中年賞金稼ぎと標的のはずなのになぜか道連れになってしまうニセ札保持の人妻との珍道中。ハードで真面目イメージのイーストウッドがイメチェン、二枚目半のズッコケぶりを披露するアクション・コメディ。ピンクのキャデラックの豪快な走りっぷりが痛快そのもの。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不知火海", "不知火海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 153, "有機水銀によって汚染された不知火海に棲む魚を食べれば、水俣病になることがわかっていても漁を続ける漁師たち。そして患者たちは……。水俣病に冒された人々の生活を冷徹な視点で見つめ続けたドキュメンタリー。「水俣—患者さんとその世界」に始まる土本典昭の“水俣”シリーズの集大成だ。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAROSELLO NAPOLETANO", "ナポリの饗宴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "第二次世界大戦後のナポリを舞台に、大道芸人とその家族を通して、この街の歴史とナポリ気質を生き生きと描き出す。あざやかな色彩、あふれんばかりに盛り込まれた歌と踊りが、歓楽の街ナポリの雰囲気を十二分に伝える娯楽作。", "1954-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪の中のしろうさぎ", "雪の中のしろうさぎ", "", "", "http://image.eiga.k-img.com/images/movie/57499/315x210.jpg?4700", 32, "吉本興業が主催する2011年・第3回沖縄国際映画祭の企画「地域発信型プロジェクト」で製作された6本の短編作品を、「東」と「西」の3本ずつに分けて劇場公開する「沖縄国際映画祭　地域発信型映画」の「東」の1本。新潟県・十日町市の有志メンバーが、芸術で町を盛り上げようと世界的芸術家の山田太郎を招く。しかし、山田は絵を描くパフォーマンスをする「エアドローイング」の世界選手権優勝者で、本当の芸術家ではなく……。", "2011-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エコファン", "エコファン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "これまで数多くのヒット広告を世に送りだしてきたクリエイター、内藤まろが、“ほのゆるエコ”をテーマにつくりあげたコマ撮りアニメ。滅亡まであと26日となった“ちきゅう”。ぐーたらで能天気なサル科のヒト、モモちゃんは、ずっと家に引きこもったまま“ちきゅう”に優しくない生活を送っていた。そんなモモちゃんと“ちきゅう”を救うため、2046年の未来からブリキのロボット、エコファンがやって来るが……。", "2009-06-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スウィートホーム", "スウィートホーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "伊丹十三が、「ドレミファ娘の血は騒ぐ」で再び組んだ監督・黒沢清、SFXメイクアップの第一人者ディック・スミスと組んで製作した本格的ホラー映画。亡くなった画家・間宮一郎の屋敷に取材で入りこんだTV局のスタッフが、謎の悪霊に襲われ次々に命を落としていく。伊丹十三が入念なメイクで老人に変身、悪霊に立ち向かう自動車修理工を快演している。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダウンタウン・ヒーローズ", "ダウンタウン・ヒーローズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "脚本家・早坂暁の自伝的小説を「男はつらいよ」の山田洋次が映画化。昭和23年松山、バンカラ調の松山高校に入学した志麻浩介と、県立高校のマドンナ房子との淡い恋愛を、山田洋次流の人情味あふれるセンスで描く。房子を演じるのは、映画出演は「紳士同盟」以来１年半ぶりとなる薬師丸ひろ子。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Push", "PUSH 光と闇の能力者", "", "", "http://image.eiga.k-img.com/images/movie/54439/315x210.jpg?2842", 111, "超能力者を育成する政府の秘密機関ディビジョンから身を隠し、香港で暮らしていた念動力の持ち主ニックは、ある時、キャシーと名乗る未来予知能力を持つ少女に、ディビジョンの重大な秘密を持って逃亡したキラという女性の捜索を頼まれたことから、超能力者同士の壮絶な戦いに巻き込まれていく。「ギャングスター・ナンバー1」のポール・マクギガン監督が、全編香港ロケで描くアクションサスペンス。クリス・エバンス、ダコタ・ファニングらが出演。", "2009-11-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEVIL IN THE FLESH", "レイプ・コマンドー 女体の黙示録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "サミーとクラウスは、中近東の某国首相が傷つき反乱軍に追われているところを助ける。３人は、ある村の中に淋しく建つ女医カトリンの病院へたどり着いた。首相をベッドに入れた二人は、次々と女たちを犯していく。そこへ反乱軍が姿を現し、隠れるサミーたちの目の前で、またもや女医と看護婦たちをレイプしていくのだった。イタリア映画得意のエロスにバイオレンスを加えた過激映画。", "1991-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUATRE NUITS D'UN REVEUR", "白夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ある夜、画家の卵ジャックはセーヌ河に身投げしようとする少女を助ける。1年前にアメリカ留学に発ち、その夜に再会を約していた恋人が現れないのだという。苦しむ彼女に恋心を抱きながら、同時に罪悪感に苛まれるジャックはひとりテープに思いを語りかける……。「やさしい女」に続きドストエフスキーを翻案したブレッソン監督が描く、美しい夜のセーヌと狂おしい愛の姿。", "1978-02-25", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER　TUNNEL", "トンネル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 167, "冷戦下のベルリンで実際にあった驚異の実話をもとに映画化し、各映画祭で絶賛を浴びた感動作。1961年、ベルリンの壁建設によってドイツの人々は東西に分断された。西側へ逃れた人々は、共産圏の東側から家族や恋人を救い出そうと奔走する。それは壁の下に145メートルものトンネルを掘るという無謀な計画だった……。スパイと分かれば容赦なく拷問され、壁の向こうで恋人が息絶えようとしていても成す術もない。仲間を救うために我が身を犠牲にする者もいる。そんな数々の悲劇を生んだ実話ならではの重みと、主人公と東ドイツ国家保安局のサスペンスフルな攻防戦を巧みに織り交ぜたドラマ構成が秀逸。スリルと感動で観る者を圧倒する力作だ。", "2001-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Gathering of Old Men", "ルイジアナの夜明け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "アメリカ南部ルイジアナのサトウキビ農園で、白人が黒人労働者マツーの家の庭で撃たれるという事件が起こる。農園の女主人キャンディーは、自分のもとで働く黒人たちを白人の差別から守ろうと、他の黒人たちを集合させ、自分が撃ったことにすると話す。しかし、保安官メープスがやって来た時、集まっていた18人の黒人老人たちはそれぞれに自分がやったと言い出す。老人たちはマツーを守り抜こうと決意し、黒人のリンチを叫ぶ白人たちに立ち向かう。シュレンドルフの、ドイツ人の目から見た“人種差別”映画で、その視点が新鮮である。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЦВЕТ ГРАНТА", "ざくろの色", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "1964年に発表した処女作「火の馬」で世界的に注目された、パラジャーノフ監督の長編第２作。18世紀に生きた宮廷詩人、サヤト・ノバの詩世界を、映像に置き換えたと言い得るような、美しく、詩的な映像世界で展開される。セリフを極限まで排し、イメージの積み重ねによって語られる詩人の生涯。様式的かつ儀式的な構成が、ゴダールなどに少なからぬ影響を及ぼした。パラジャーノフは、この映画を撮るにあたってアルメニアに古くから伝わる手法を用いたという。青年時代の詩人サヤト・ノバとその恋人を、ソフィコ・チアウレリがともに演じているほか、同一人物でも年齢によってまったく違った風に描かれるイコンに倣い、サヤト・ノバを４人の役者が演じている。しかしながら、現存する「ざくろの色」は、1969年のオリジナルではなく、1971年にセルゲイ・ユトケーヴィッチが編集した短縮版であるため、どこまでパラジャーノフの意に沿ったものなのか、不明なままである。", "1971-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄飛鴻之西域雄獅／ONCE UPON A TIME IN CHINA　＆　AMERICA", "ワンス・アポン・ア・タイム・イン・チャイナ＆アメリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "伝説の武道家、ウォン・フェイフォンの活躍を描くシリーズの一編。シリーズ第３作「ワンス・アポン・ア・タイム・イン・チャイナ／天地争覇」で決別したツイ・ハークとジェット・リーのコンビが復活。19世紀のアメリカ西部を舞台に、チャイナタウン誕生を背景に新たな冒険物語を展開している。渡米したウォンは、激流にのまれて記憶を喪失。ネイティブ・アメリカンに救われた彼は、中国人を虐待する悪党どもに敢然と立ち向かっていく。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海の花火", "海の花火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "北九州、呼子港の遠洋漁業組合長・神谷は、不正を犯し組合を赤字化させた二人の船長をクビにして、若くて有能な兄弟を雇う。しかし組合の赤字は回復せず、しかもクビにした二人は、彼らに次々と嫌がらせを仕掛けてきた。木下惠介監督が海に生きる男たちの誇りを問うヒューマン・ドラマ。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADY IN WHITE", "汚れなき瞳の中に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ハロウィンの日、級友のいたずらで学校のロッカールームに閉じ込められてしまった少年フランクは、真夜中に美しい少女メリッサが何者かに殺害されるのを目撃。そして彼自身も首を絞められるが……。想像力豊かな少年の目を通して綴った、現実と幻想が交錯するファンタジー・サスペンス。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beaufort", "ボーフォート　レバノンからの撤退", "", "", "http://image.eiga.k-img.com/images/movie/53489/315x210.jpg?2145", 127, "第57回ベルリン国際映画祭で銀熊賞（監督賞）を受賞、第80回アカデミー賞外国語映画賞にノミネートされたイスラエルの戦争ドラマ。00年、レバノンのイスラエル軍前哨基地ボーフォートに、22歳の若き指揮官リラズが赴任するが、爆弾除去の作戦失敗や見えない敵からの爆撃に次々と戦友たちが倒れていく。基地の少年兵たちは精神を病んでいき、リラズの恐怖と苦悩の日々が続く。やがてレバノンからの撤退が近づく中、彼らに最後の任務が与えられるが……。", "2008-02-16", "46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガーダ／パレスチナの詩", "ガーダ／パレスチナの詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "フォトジャーナリストの古居みずえが自らカメラを携え、混迷を極めるパレスチナで生き抜く女性を追ったドキュメンタリー。ガザ地区の難民キャンプで生まれ育ったガーダは、持ち前の自立心で古い因習に逆らってきた。しかし抵抗運動で親戚が殺されたことを契機に、民族が追われた故郷の歴史をたどり始める。主人公ガーダの結婚や出産など人生のイベントを通じて、難民キャンプで生きる厳しさをもリアルにあぶり出した。", "2006-05-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の生涯・彦根篇／江戸篇", "花の生涯・彦根篇／江戸篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 180, "井伊直弼の波乱にとんだ生涯を、舟橋聖一の原作をもとに描いた大河ドラマ。彦根藩主から江戸へ下り、のちに大老となって開国条約の処理にあたり、桜田門外で暗殺されるまでを、３時間の長尺だが、力のある演出で見せる。歌舞伎役者の松本幸四郎（当時）が映画初出演。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・ディフェンダー", "ザ・ディフェンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "秋葉原の路地裏で働くしがないエンジニア、孝のもとにある日、良美と名乗る少女がCDデータの修理を頼みにやって来た。しかし、それ以来、孝は突然の頭痛に悩まされたうえ、謎の暗殺者の襲撃に見舞われる。やがて病院に運ばれた孝は、そこで衝撃的な事実を知ることに……。突如、自らの恐るべき出生の秘密を知ってしまった男が、自己のアイデンティティーを懸けて戦う姿を描いた異色SFドラマ。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SATAN BUG", "サタンバグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "アメリカ全土を一週間で死滅させるほどの細菌“サタンバグ”が何者かによって盗まれる。米国CIAは、さっそく極秘裏に調査隊を組織。ところが、アメリカだけでなく全地球の征服を夢見るその犯人とは、意外な人物だった。市民は恐怖のどん底に落とされるが……。J・スタージェスが演出するアクション・ミステリー。原作者のI・スチュアートは、アリステア・マクリーンの別名。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死刑台のエレベーター（2010）", "死刑台のエレベーター（2010）", "", "", "http://image.eiga.k-img.com/images/movie/55309/315x210.jpg?1515", 0, "仏ヌーベルバーグの鬼才ルイ・マル監督による同名映画を吉瀬美智子＆阿部寛主演でリメイクしたサスペンスドラマ。横浜の総合医療グループの社長夫人・手都芽衣子は、グループ内の病院に勤務する魅力的な医師・時籐と不倫関係にあった。2人は社長の手都を自殺に見せかけて殺す計画を立て、実行するが、病院のエレベーターが週末の電源解除により止まってしまったことからから、事態は思いも寄らない方向へ進んでいく……。共演に柄本明、津川雅彦、玉山鉄二、北川景子。監督は「のんちゃんのり弁」の緒方明。", "2010-10-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MURDER’S SECRET", "新・ゾンゲリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ルチオ・フルチ監修によるお得意のスプラッタ・ムービー。伯母が精神病院で悲惨な死を遂げた事件を唯一知るリチャードは、その30年目の命日に亡き伯母の別荘で過ごすことにした。そして惨劇が始まる。血のしたたる逆さづり死体。ギロチンにかけられた生首が飛び、ウジ虫がはいまわる腐乱死体にキスをする男……。", "1990-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bright Star", "ブライト・スター　いちばん美しい恋の詩（うた）", "", "", "http://image.eiga.k-img.com/images/movie/55369/315x210.jpg?1543", 119, "「ピアノ・レッスン」「ある貴婦人の肖像」のジェーン・カンピオン監督が、夭折した英国ロマン派詩人ジョン・キーツと、その若き恋人ファニー・ブローンとのロマンスを綴った恋愛ドラマ。1818年ロンドン郊外のハムステッド。詩人として世に出始めたばかりのキーツは親友の編集者ブラウンの家に居候する。キーツは、その家の隣人ブローン家の長女ファニーに惹かれ恋に落ちるが、結核を患ってしまう……。出演はキーツに「パフューム」のベン・ウィショー、ファニーにアビー・コーニッシュほか。", "2010-06-05", "3,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PERDITA　DURANGO", "ペルディータ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "デヴィッド・リンチ監督作品「ワイルド・アット・ハート」でイザベラ・ロッセリーニが演じたキャラクター、ペルディータ・ドゥランゴを主役に据えたバイオレンス・アクション。メキシコ国境の町で出会った炎の女ペルディータとマッチョな悪党ロメオが、野獣のようなセックスに溺れながら、若い白人カップルを誘拐。やばい荷物をラスベガスに運ぶ仕事を請け負った彼らは、次々と麻薬捜査官の追跡をかわしていく。", "1997-01-01", "9,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DECONSTRUCTING　HARRY", "地球は女で回ってる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "アカデミー賞脚本賞で13回目のノミネートを受けたW・アレンの監督第27作。自分の性生活をネタにしている小説家が、別れた妻や恋人たちと繰り広げる悲喜劇を軽妙なセリフとともに綴るラブ・コメディ。自分の私生活を書いてベストセラー作家になったハリーは、３回の離婚歴がある独身男。最近スランプに陥り、精神分析医の元を訪れている。３番目の妻の妹で愛人関係にあったルーシーに怒鳴り込まれたり、別れた恋人フェイが結婚すると聞いて嫉妬したりと周囲に振り回されてばかり。悩みが増えるなかで、彼は次第に虚構と現実の区別がつかなくなっていき……。空想の中で“ピンボケ俳優”として登場するR・ウィリアムズをはじめ、D・ムーアやB・クリスタルら脇役陣のキャラクターもユニーク。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ДВОРЯНСКОЕ ГНЕЗДО／A　NEST　OF　GENTRY", "貴族の巣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "後にハリウッドに渡るソ連のA・コンチャロフスキー監督が、ツルゲーネフの同名小説を原作に、19世紀ロシアのインテリ貴族の悲恋物語を詩情豊かに綴った文芸ドラマ。ヒロインに抜擢された新人、I・クプチェンコのみずみずしい美しさにも目を奪われる。パリでの社交生活と奔放な妻に愛想を尽かして、単身祖国に戻ったラブレツキー。久しぶりに親戚のカリーニン家を訪ねた彼は、美しく成長した同家の長女リーザに魅了される。そんな折、妻の突然の訃報が届き、ラブレツキーはリーザに愛を告白するが……。", "1970-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GO TRABI GO", "ゴー・トラビ・ゴー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "東独のライプチヒ郊外で慎ましく暮らすシュトゥルーツ一家は、ある日“長期休暇”を取ることを決意。それは資本主義国家の産物として、以前は考えられないものだった。一家は愛車トラビ・ジョージで、いざイタリアへ。しかしこの車、物資不足の状況下で作られたダンボールとプラスティック製のオンボロ車なのだ。資本主義のシステムにほんろうされる一家の姿をドタバタ風に描きながら、社会風刺を織り込んだ異色のコメディ。", "1990-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COUNTRY GIRL", "喝采", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "いわゆる“バックステージもの”の傑作でブロードウェイのヒット戯曲の映画化。かつては名エンターテイナーで今はアル中の夫と、思いを寄せる若き演出家との三角関係に揺れる女心を、G・ケリーが好演。心理描写を主とした難役を見事にこなしている。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天空小説／OUT OF THE BLUE", "天空小説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "香港で人気のラップ・グループ軟硬天使（ソフト＆ハード）のメンバーが主演し、その一人であるジャン・ラムが監督した中編。香港のショッピング・アーケード。他人の店の電話回線を使い恋人と話すコピー・ショップの青年、バービー人形を愛するオモチャ屋の青年、アーケードを徘徊するファッション・フェチの青年。この３人の動きをある時はバラバラにある時は交差してカメラは追っていく。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moonrise Kingdom", "ムーンライズ・キングダム（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELECTRIC DRAGON 80000V", "ELECTRIC DRAGON 80000V", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "少年時代の事故で体に電流パワーを溜め込んだ竜眼寺盛尊は、自ら帯電体質を生かしてエレキ・ギターをかき鳴らし、爬虫類と心を通わせるペット探偵。そんな彼の前に、仏像の仮面をつけて電波ジャックを行う謎の男、雷電仏像が現れ、二人の宿命的な闘いの幕が開く。浅野忠信、永瀬正敏がいずれ劣らぬ怪人キャラクターに扮し、劇画調のバトルを繰り広げるSFアクション。メタリックな質感のモノクロ映像、劇場での“ライヴ体験”を重視した音響効果など、石井聰亙監督のこだわりが存分に発揮された。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FIRST WIVES CLUB", "ファースト・ワイフ・クラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "オリヴィア・ゴールドスミスの同名ベストセラー小説をもとに、夫の浮気に怒った３人の妻たちの復讐劇を描くコメディ。大学時代に親友だった３人の女性が、元同級生の葬儀で20年ぶりに再会する。整形手術を繰り返す落ち目の女優エリース、夫の経営する電気チェーン店を陰ながら支え続けたブレンダ、性的不能になった夫と精神科医に通うアニー。夫が若い女性と浮気中という共通の悩みを抱えた彼女らは意気投合。“ファースト・ワイフ・クラブ”なるものを結成して、夫の財産を奪い取るためあの手この手で反撃を開始する。パワフルな妻たちに扮するG・ホーン、B・ミドラー、D・キートンらベテラン女優たちの活躍ぶりが痛快。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひとり旅", "ひとり旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "悪党どもが密輸取り引きをするたびに現れては現ナマを奪う男、“エースのジョー”と呼ばれる男がいた。日ごろの復讐のため罠を張る悪党どもに、ジョーは銃弾を浴びて車ごと海に沈むが、恋人・美佐の危機に不死身のジョーは再び立ち上がるのだった。渡り鳥シリーズの斎藤武市が描く痛快アクション。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAMPION", "チャンピオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ボクシングの手ほどきを受け、金を稼ぐためリングに立つ一人の青年が、八百長試合を乗り越え、やがて人々を踏み台にしてチャンピオンとしてのし上がっていく様を描くボクシング映画の秀作。拳闘シーンのすさまじいまでの描写、一人の男の執念がほとばしるような演出もそうだが、それまでのハリウッド映画に見られなかったようなアンチ・ヒーロー像が鮮烈な印象を残した。主人公を演じたK・ダグラスはこの作品で一躍名を売り、脚本のC・フォアマンは“赤狩り”の犠牲となってイギリスに逃れたといういわくつきの作品でもある。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLUNKETT　AND　MACLEANE", "プランケット＆マクレーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "リドリー・スコットの息子、J・スコットが初メガホンを執った痛快バディ・ムービー。18世紀半ばのロンドンに実在した紳士強盗の逸話をもとに、アップテンポなアクションシーン、男たちの友情、そしてロマンティックな恋の成り行きを加え娯楽要素たっぷりのエンターテインメント作品に仕上げた。ダークな画面にエネルギーみなぎる演出センスは、父親譲り。「ロード・オブ・ザ・リング…」のL・タイラーが反抗的で意志の強い女性を快活に演じ、作品に華を添えている。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("川上哲治物語 背番号16", "川上哲治物語 背番号16", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "打撃の神様・川上哲治の半生を描いたスポーツ映画。熊本の豪球投手時代から巨人軍入団、太平洋戦争出征を経て、戦後の大スターとして昭和31年に2000本安打を達成するまでを綴る。川上役は少年期、青年期、現在（川上自身）を３人が演じ分けている。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夕暮れ", "夕暮れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "「花の袋」の新鋭・戸田彬弘監督が、奈良を舞台に現代の家族の在りようを描いたヒューマン・ドラマ。東京で働く長谷川勝雄は、祖母の三回忌に出席するため、奈良で民宿を営む実家に帰省する。いつもはすぐに東京へ戻る勝雄が今回はしばらく滞在すると知り、違和感を覚える家族。そんな中、祖父の一平が貧血で倒れた女性・リサを連れて帰る。こうして長谷川家では、勝雄とリサを加えた6人の共同生活がはじまり……。", "2010-06-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不散", "楽日", "", "", "http://image.eiga.k-img.com/images/movie/33619/315x210.jpg?4169", 82, "閉館間際の映画館で繰り広げられる人間模様を、台湾の名匠ツァイ・ミンリャン監督が映画への愛をこめて描き出した人間ドラマ。楽日（最終日）を迎えた台北の古い映画館“福和大戯院”では、最後の上映作としてキン・フー監督の傑作「血闘竜門の宿」が上映されていた。場内には、かつての映画スターや、男を求めてうろつく男たちの姿が。一方、受付係の女は、長い間ともに働いてきた映写技師の男に、思いを込めた最後のプレゼントを届けに行く。", "2006-08-26", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WEEKEND AT BERNIE'S", "バーニーズ あぶない!? ウィークエンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "会社内の陰謀を暴いたお手柄により、週末をリゾート地ロングアイランドで過ごすことになった二人組。ところがそこで殺人事件が発生、せっかくの休暇を邪魔されてたまるかと、二人はあの手この手で事件を隠そうとするが……。ヨーロッパ喜劇の要素にアメリカのユーモアセンスを盛り込んだコメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASOCH", "作家マゾッホ・愛の日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "マゾヒズムという言葉を生み出すもとになった作家レオポルド・フォン・ザッヒェル・マゾッホと、彼の妻ルメリンとの凄絶な愛と性の相克を描いたのがこの作品である。ザッヒェル・マゾッホは、自らの肉体と精神を痛めねば執筆できなかった……。", "1980-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Torinoi lo", "ニーチェの馬", "", "", "http://image.eiga.k-img.com/images/movie/57189/315x210.jpg?2955", 154, "「ヴェルクマイスター・ハーモニー」「倫敦から来た男」で知られるハンガリーの名匠タル・ベーラが、ドイツの哲学者ニーチェの逸話を題材に荒野に暮らす男とその娘、一頭の馬のたどる運命を描く。1889年、イタリア・トリノ。ムチに打たれ疲弊した馬車馬を目にしたニーチェは馬に駆け寄ると卒倒し、そのまま精神が崩壊してしまう。美しいモノクロームの映像は「倫敦から来た男」も担当した撮影監督フレッド・ケレメンによる。2011年・第61回ベルリン国際映画祭で銀熊賞（審査員特別賞）を受賞。", "2012-02-11", "30,2,26,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄プロレス", "地獄プロレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「八月のかりゆし」の高橋巖監督によるプロレスを題材にしたスポ根喜劇。プロレス界の闇の養成機関“サイの穴”に、最弱レスラーに改造されてしまった男が戦いを挑む。レスラー役に元K-１チャンピオンのニコラス・ペタス、義手のオーナーに我修院達也が扮するなど、個性的なキャストが揃った。“愛育プロレス”の所属レスラー淳は、かつての花形レスラー、松戸三四郎と出会う。彼は悪の集団によって最弱レスラーに改造されており……。", "2005-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Human Centipede (First Sequence)", "ムカデ人間", "", "", "http://image.eiga.k-img.com/images/movie/56219/315x210.jpg?3733", 90, "狂気に満ちた医師が、複数の人間の口と肛門とをつなぎ合わせ“ムカデ人間”を作ろうとする姿を描くショッキングサスペンス。ヨーロッパを旅行中、ドイツの森の中で立ち往生してしまった2人のアメリカ人女性が、助けを求めて一軒の邸宅にたどりつく。翌朝、目を覚ました2人は病室のベッドの上におり、隣には同じように日本人男性が寝かされていた……。日本人男性役で人気ドラマ「HEROES ヒーローズ」にも出演している北村昭博が出演。", "2011-07-02", "70,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gone for a Dance", "ゴーン・フォー・ア・ダンス（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "", "2007-01-01", "37,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Match Point", "マッチポイント", "", "", "http://image.eiga.k-img.com/images/movie/1119/315x210.jpg?4514", 124, "「アニー・ホール」「マンハッタン」のウッディ・アレンがニューヨークを離れ、イギリスで作り上げたラブ・サスペンス。ロンドンの上流社会を舞台に、元プロテニス・プレイヤーの野心家クリスが、成り上がり、そして転落していく様を、結婚や仕事の成功など人生における様々な決断を通して辛辣に描く。主演クリスには「M:i:III」のジョナサン・リース・マイヤーズ。クリスの運命を狂わせるノラにはスカーレット・ヨハンソン。", "2005-08-19", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEISE FLEHEN MEINE LIEDER", "未完成交響楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "オーストリアが生んだ世界的大作曲家シューベルトの伝記映画。名曲『未完成交響曲』のメロディーにのせて、彼と二人の娘のロマンスが語られる。音楽と映像が見事に溶けあった新感覚映画として特に日本では評価が高い。ソプラノ歌手エゲルトの歌声が満喫できるのもうれしい。", "1933-01-01", "7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄の剣豪 平手造酒", "地獄の剣豪 平手造酒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "おなじみ天保水滸伝の悲運の剣豪・平手造酒の破滅へ向かう人生を描く。造酒に辰巳柳太郎、千葉周作に島田正吾と、戦後製作再開後、日活と手を結んだ新国劇スターが総出演。黒澤映画で頭角を現した菊島隆三の脚本を得て、舞台とは違った味わいの意欲作となっている。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐の中のイチゴたち", "嵐の中のイチゴたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "校内暴力、イジメ、落ちこぼれと現代の中学校が抱える様々な問題を真っ向から捉えた学園ドラマの問題作。対立する不良グループが抗争を繰り広げる公立中学を舞台に、面白半分に選出された生徒会長が暴力一掃のために孤軍奮闘するさまを描く。“イチゴ”とは15歳のこと。オーディションで選ばれた本物の“イチゴ”たちが出演。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふり袖太平記", "ふり袖太平記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "名家・里見家の流れをくむ旗本・菅谷家の一人娘・小浪のひばり屋敷で突如お家騒動が勃発した。その背後には菅谷家を狙う悪徳家老の陰謀があった……。美空ひばりが美声を聞かせ、大川橋蔵が颯爽と剣をふるう娯楽時代劇。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　MENTEURS", "君が、嘘をついた。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "J・H・アングラード扮する映画監督と、脚本家志望のパリジェンヌが織り成すロマンティックなラブ・ストーリー。映画の脚本家を夢見る20才のディジーは、ちょっとした嘘をきっかけに映画監督ザックと大女優エレーヌのカップルに近づくのだが、その夜、あこがれのザックが姿を消してしまい……。映画制作の現場と同時に進行していく恋の駆け引きが、“嘘”と“真実”を絡めながらスリリングに展開。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("踊りたい夜", "踊りたい夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "元々ミュージカル映画指向の強かった井上梅次監督が、原案・脚本も手掛けたミュージカル・ドラマ。東京のナイトクラブ“白馬車”で人気者の３姉妹、マリ、ユリ、ミッチーはショーダンサーを目指していたが、マリは偽りの恋に走り、ユリは作曲家と結婚。バラバラになった３人だが、やがてショービジネスの世界に戻ってくる……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SEA WOLVES", "シーウルフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "第二次大戦も佳境に入った1942年後半。インド洋戦線ではドイツのUボートが、ひと月の間に総重量25万トンにも及ぶ連合軍の戦艦を撃沈、インド国内に駐留するイギリス軍の大きな驚威となっていた。この窮状を前にして、連合軍はUボート破壊極秘作戦をたて、18人の強者を集結させた。年齢も高く、体調も万全とはいえぬ18人の“シーウルフ”。しかし彼らをおいてこの作戦を遂行できる者はいない……。R・ムーア、G・ペック、D・ニーヴンらが大活躍。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COLLECTOR", "コレクター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "蝶の収集が唯一の趣味の銀行員。ある日、大金を手にしたことから恐ろしい考えに取り憑かれる……。人間を収集する異常な性格を通して人間の独占欲と異常性を描いたW・ワイラーの佳作。T・スタンプが異常性格の男を好演している。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISTRESS", "ミストレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "デ・ニーロ率いる製作プロ、トライベッカの作品。かつて将来を期待されながら今は料理番組の演出に甘んじている映画監督を主人公に、彼の脚本に魅せられたプロデューサー、愛人をその映画に出演させることしか考えないスポンサーたちなど、様々な人間が入り乱れる映画界の裏事情を鋭く描く。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("或る夜の殿様", "或る夜の殿様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "衣笠貞之助監督の戦後第１作。明治の開化期、箱根の温泉旅館を舞台に、鉄道施設の利権をめぐる騒動を、みずみずしい映像と大らかなユーモアで描く。長谷川、山田らが軽妙な演技を見せ、飯田蝶子、志村喬、進藤英太郎ら充実した脇役陣とのアンサンブルも楽しい。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウインディー", "ウインディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "10代にして天才と呼ばれた男が、グランプリ・チャンピオンを獲得して頂点に立つが、突然の事故により挫折。娘との交流、女性メカニックとの愛を支えに再度ヨーロッパ・サーキットに挑む、というストーリー。戦後初の日本・西独合作映画となったレーシング・ドラマのそう快作。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WOMAN OBJECT", "欲望という名の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "結婚生活２年目を迎えた美しい人妻が、理由のわからぬセックス恐怖症に陥る。彼女はその現実を避けるため空想にふけり、その中でセックスをするのだった。他人にはうかがい知れない、女性の意識下に潜む想像を超える性幻想を、強烈なタッチで描いたブラジル製エロティック・ファンタジー。", "1981-01-01", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALEXA", "官能と背徳の谷間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "アレクサはニューヨークの金持ちたちの欲求を満たす高級コールガール。ある日友人の紹介で、売春婦についての戯曲を執筆中の劇作家トニーと知り合う。決して恋をしてはいけないコールガールの世界。しかし、彼女は売春という行為を嫌う彼を愛してしまい……。エロティックな見せ場を絡ませたラブ・ストーリー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les contes de la nuit", "夜のとばりの物語", "", "", "http://image.eiga.k-img.com/images/movie/57959/315x210.jpg?3740", 84, "「キリクと魔女」「アズールとアスマール」で知られるフランスのミッシェル・オスロ監督が、少年少女の愛をテーマに美しい色彩で描いた影絵アニメーション。異なる歴史や世界、文化の中で生きる少年少女の6つの愛の物語がつむがれる。日本語吹替えに声優の坂本真綾、アニメ声優初挑戦の西島秀俊ら。", "2012-06-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NINJA CONDOL13", "ドーベルマン・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "幼いころ、目の前で父を殺され、忍者の師に育てられた青年は、その道のプロとなり、今は殺し屋として生きていた。そんなある日、彼の恩人である警官を殺せという命令が下る。非情な組織の掟に、一人敢然と戦いを挑む男の活躍。“香港ニンジャ映画”が生んだヒーロー、A・ルーのアクションに注目。", "1987-01-01", "1,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Un couple parfait", "不完全なふたり", "", "", "http://image.eiga.k-img.com/images/movie/34039/315x210.jpg?1372", 108, "「パリ、ジュテーム」に唯一の日本人監督として参加し注目を浴びた諏訪敦彦監督が、全編フランス語かつオール・パリロケで撮りあげた日仏合作の恋愛ドラマ。友人の結婚式に出席するためパリを訪れたマリーとニコラ。結婚して15年になる2人は周囲からは理想の夫婦と見られていたが、実は離婚することを決めており……。主演は「ふたりの5つの分かれ路」のバレリア・ブルーニ・テデスキと、「情痴　アヴァンチュール」のブリュノ・トデスキーニ。", "2007-06-30", "2,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANY GIVEN SUNDAY", "エニイ・ギブン・サンデー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "トニーがコーチを務めるプロのアメフト・チーム“マイアミ・シャークス”は、目下のところ連敗続きで士気も低下中。そんな折、無名選手のビーメンがふとしたことからスター・プレーヤーとなり、やがてチームも活気づいていくのだが……。O・ストーン監督が、試合のフィールドを戦場に見立てたかのようにワイルドに描いたスポーツ映画の快作。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Big Carnival(Ace in the Hall)", "地獄の英雄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "　ビリー・ワイルダーが長年の脚本パートナーであったチャールズ・ブラケットと離れ、新たな脚本家を迎えた作品。ニューヨークの新聞社から閉め出されたジャックは、洞窟内で生き埋めになっている男を目撃する。是が非でも一流紙に戻りたい彼は、スクープを利用し情報操作に手を染める。おのれの野心のためなら手段を選ばない新聞記者が、世論に裁かれる姿は見応え十分。世間の評価が芳しくなかったため、スタジオ側が「Ace in the Hall」から「The Big Carnival」にタイトルを変更したという逸話も。", "1952-09-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あかね雲", "あかね雲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "昭和12年。ダークブルーの能登の海を背景に、薄幸の中で必死に生きる土地の女・まつのと、逃亡中の脱走兵・小杉の間に悲劇的な愛の炎が燃えさかる。二人の運命を象徴するかのように、真赤なあかね雲だけがカラーで挿入されている。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の渡り鳥", "花の渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "旅ガラス榛名の清太郎は、権現山の崖下で瀕死の七兵衛から娘のおみねへの伝言を頼まれる。おみねは佐原の悪親分・岩井屋音蔵のもとにいたが、子分の惣吉が連れて逃げ出した。追っ手に囲まれて危機一髪のその時、清太郎が二人を助ける。若き日の勝新太郎と雷蔵が好演した、明るい人情時代劇。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING KONG LIVES", "キングコング２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "前作で世界貿易センタービルから墜落、植物人間さながら眠り続けていたコング。その彼が人工心臓移植によってよみがえる……。レディ・コングとの恋、そしてベビー・コングの誕生と、世界最大(？)のラブ・ストーリーが画面いっぱいに展開。コングをよみがえらせるアイデアの奇抜さが新味。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("It's Alive", "ダニエル　悪魔の赤ちゃん", "", "", "http://image.eiga.k-img.com/images/movie/54509/315x210.jpg?2392", 83, "70年代ホラーブームの中心となったラリー・コーエンの「悪魔の赤ちゃん」（74）をリメイク。レノアは異常な陣痛で病院に運び込まれ、何とか無事に赤ちゃんを産むが、その直後に医師と看護師が何者かに惨殺される。レノアはダニエルと名付けた赤ちゃんを家に連れて帰るが、その日からレノアの周りで不気味な出来事が起こり始める。", "2009-05-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIGNE DE VIE／LINIYA ZHIZNI", "ラヴィアン・ローズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "処女作「タクシー・ブルース」でカンヌ映画祭最優秀監督賞に輝いた、P・ルンギンによる異色のラブ・ストーリー。フランス人の作曲家フィリップが、旅行で訪れたモスクワ空港でロシア人女性オクサナに声をかけられる。天使のように美しい彼女の魅力に惹かれ、夜の街へと出かけていった彼は、途中で意識を失ってしまう。目覚めると、そこは見知らぬ屋敷。彼はマフィアのボスの娘であるオクサナに誘拐されたのだった。厳重な監視下に置かれた彼は、架空の織物工場をアジアに売り飛ばす計画の片棒を担がされるハメに。そんな中、父親の溺愛から逃れたいと切望していたオクサナと恋に落ちたフィリップは、危険な逃避行を決意する。ビデオ発売時タイトルは「ロシアン・ゴッドファーザー」。", "1996-01-01", "2,24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("續三等重役", "續三等重役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "社員のことを心配してやれと前社長に叱られた桑原現社長は、浦島人事課長に命じて模範社員を探すことになり、第一候補として秘書の若原が出張先から呼び出される。この年、社長役の河村黎吉が死去。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD GIRL", "DEAD GIRL　狂気の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "売れない俳優で誇大妄想癖(分裂症)があるアリは、精神分析医に夢の話を実際のごとく話し、すでに夢と現実の境目がわからなくなっていた。そんな彼の目の前に夢で見た女、ヘレンが姿を現す。だが、実際の彼女は妄想通りの女でなく、アリは思い余って彼女を絞め殺してしまう。死体となったヘレンを自室に連れ帰り、彼女との“恋愛”を楽しむうちに、現実と妄想が交錯する狂気の世界へと陥っていく……。クセ者キャストが顔をそろえる未公開サスペンス。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使に見捨てられた夜", "天使に見捨てられた夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "38歳の女性探偵、村野ミロは友人の依頼でAV女優、一色リナの捜索を始める。やがてミロにふりかかる嫌がらせの数々。リナの出演作にはレイプ容疑がかけられており、リナ自身“消されている”可能性もあったが……。桐野夏生の同名小説を、かたせ梨乃主演で映画化。隣人であるゲイのバー主人と穏やかな交流を持つ一方、いわくありげなAV監督との一夜の情事にも溺れる主人公。生々しく、もろい彼女の視点を通して、新宿の闇にうごめく謎が解き明かされていく。かつて「極道の妻たち・決着」でかたせと共演した大杉が、同性愛者の哀感を体現し、作品に和みと深みを与えている。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花はどこへいった", "花はどこへいった", "", "", "http://image.eiga.k-img.com/images/movie/53669/315x210.jpg?2721", 71, "ベトナム戦争中にアメリカ軍が散布した枯葉剤の被害にスポットを当てたドキュメンタリー。フォト・ジャーナリストの夫グレッグ・デイビスを肝臓がんで亡くした坂田雅子が、彼の死の原因がベトナムの戦場で浴びた枯葉剤かもしれないと知り映画制作を決意。戦後30年以上を経た現在でも未だ多くのベトナム人を苦しめている枯葉剤の実態に迫ると同時に、被害者とその家族の深い愛情や平和への想いを描き出していく。", "2008-06-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUPPET ON A CHAIN", "デンジャー・ポイント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "イギリス冒険小説の人気作家A・マクリーンが自らの作品を脚色したサスペンス・アクション。運河の都アムステルダムを舞台に、オランダの巨大な麻薬組織に敢然と挑むインターポールの麻薬捜査官の大活躍。壮絶なモーターボートによるチェイス・シーンが展開される。", "1972-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mauvaise Graine", "悪い種子（たね）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "　「麗しのサブリナ」「アパートの鍵貸します」など、数々の名作を世に送り出したビリー・ワイルダーのデビュー作で、アメリカに渡る以前にフランスで製作された。20代後半で既に脚本家として10数本のキャリアを積んでおり、処女作ながら凝ったカメラワークや魅力的なヒロイン像など天才の片鱗がうかがえる。自動車泥棒のグループと若きパリジェンヌの物語を、カーチェイスシーンたっぷりに映像化した。", "1933-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("警察故事續集／POLICE　STORYII", "ポリス・ストーリー２ 九龍の眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "ジャッキー・チェンの監督、主演第10作目に当たる人気シリーズの第２作。一本気な性格が災いして、何かと問題を起こすことの多いチェン刑事を主人公に、悪質な爆弾組織との壮絶な闘いを描いたアクション映画。ジャッキーの恋人役には、これが３度目の共演となるマギー・チャン、人間味豊かな上司には、常連トン・ピョウがそれぞれ扮し脇を固めている。公開時タイトルは「九龍の眼　クーロンズ・アイ」。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤ひげ", "赤ひげ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 185, "原作は山本周五郎の「赤ひげ診療譚」。江戸時代の小石川養生所を舞台に、そこを訪れる庶民の人生模様と通称赤ひげと呼ばれる所長と青年医師の心の交流を描く。長崎で医学を学んだ青年保本は、医師見習いとして小石川養生所に住み込む。養生所の貧乏臭さやひげを生やした無骨な所長赤ひげに反発する保本は、養生所の禁を犯して破門されることすら望んでいた。しかし、赤ひげの診断と医療技術の確かさに触れ、また彼を頼る貧乏人に黙々と治療を施すその姿に次第に心を動かされていった……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("因幡の白うさぎ", "因幡の白うさぎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "萩原聖人、江口のりこ共演によるファンタジック・ドラマ。河原で暮らすホームレスの隆は、かつて高校球児として甲子園に出場したこともあるが、試合でのミスがトラウマとなり、すっかり自信を失っていた。そんな隆の運命が、1匹のウサギとの出会いをきっかけに大きく変わりはじめる。メガホンをとるのは、本作が監督デビュー作となる李相國。ペットと人間が織りなすドラマを6人の監督が競作した「PETBOX ペットボックス」シリーズの1作。", "2006-12-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("細雪", "細雪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "谷崎潤一郎の名作の３度目の映画化で、名匠・市川崑がそのテクニックとセンスを駆使して撮り上げた芸術娯楽作品である。原作に絶妙な脚色を施したのは市川監督と日高真也。撮影は“金田一耕助”もののベテラン・長谷川清。現代的感覚による音楽処理も、そのモダンなイメージを強める役目を果たした。昭和13年春、芦屋。旧家・蒔岡の四姉妹が花見の宴で一同に会した。長女・鶴子と次女・幸子は、まだ未婚の妹・雪子と妙子の結婚を気遣う毎日だ。おとなしい三女の雪子は、姉や義兄たちの勧めに従い次々と見合いをするが、いずれも雪子本人の気が進まず断わってしまう。おてんばな四女・妙子は、愛し合っていた写真家を病気で亡くしてしまい、酒びたりとなるが、酒場のバーテンと知り合い結ばれる。鶴子の夫の東京転勤が決まり、一方で雪子の新しい縁談もあっけなくまとまり、姉妹は散りぢりとなっていく。雪子をひそかに想っていた幸子の夫・貞之助は黙々と酒を飲む。季節は折しも細雪の降る初冬を迎えていた。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Max", "アドルフの画集", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ナチスドイツの総統として歴史上に悪名を残したアドルフ・ヒトラー。画家を目指していたというその青年時代に焦点を当て、虚実を織り込みながら歴史の裏側に迫る人間ドラマ。「トゥームレイダー」のN・テイラーが若き日のヒトラーを熱演、エキセントリックな演説口調を英語ながら完璧にコピーしてみせる。またヒトラーと意気投合する画商をJ・キューザックが好演している。", "2002-01-01", "30,10,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXCEPTION　TO　THE　RULE", "エクセプション　殺意のダイヤモンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "一夜かぎりの情事から殺人ゲームにまで発展していく、悪女のすさまじい執念を描いたサスペンス・ドラマ。妊娠中の妻をよそに、ミステリアスな美女カーラと一夜をともにした宝石商のティム。しかし、カーラはティムとの情事を撮ったビデオテープをネタに彼を脅して……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INVASION OF THE BODY SNATCHERS", "SF／ボディ・スナッチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "サンフランシスコで、住人の人格が急変する事件が相次ぐ。同じ頃、粘着性のマユ状物質が見つかり、その中から人格の変わった住人の死体が……。実は宇宙からの侵略者が地球人の肉体を乗っ取っていたのだ！　派手なSFXこそないが、どこか不気味なクセ者俳優がそろい、ジワジワと背筋の寒くなるSFホラー。ドン・シーゲル監督の「ボディ・スナッチャー／恐怖の街」のリメイク。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビーオン", "ビーオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "毎日ケンカばかりの不良学校・蘭城高校で、高校生のケンカとは思えない悪質な問題が多発。刑事・不動文太は調査のため教師になりすまして学校へ潜入する。蘭城の生徒たちは、突然あらわれた教師に反発するが……。ケンカに明け暮れる不良高校生たちの戦いを描いた学園アクション。", "2010-09-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHO FRAMED ROGER RABBIT？", "ロジャー・ラビット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1947年のハリウッド。その撮影所のすぐそばに漫画の登場人物たちの住む街、トゥーン・タウンがあった。ロジャーは新作の撮影に身が入らず悩んでいた。原因は愛妻の浮気で、その現場を探偵エディがつかんだまではよかったが、その直後、妻の愛人が何者かに殺されてしまう。容疑は当然、ロジャーにかかってきた。ミッキー・マウスやバックス・バニーなど懐かしいアニメ・キャラクター総出演の楽しいファンタジー。ディズニーとスピルバーグが手を組んで、実写とアニメをうまく合成して作った娯楽作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UCCELLI ASSASSINI", "キリング・バード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "バードウォッチングをしようとある森へやって来た大学生一行。彼らが踏み入れた古い洋館は、実は20年前に鳥類学者ブラウンが家族を惨殺した場所だった。何も知らない学生たちは一人また一人と怨念が操る怪物に殺されていく。動物パニックものとスプラッタを合体させたバイオレンス・ホラー。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JLG／JLG　AUTOPORTRAIT　DE　DE´CEMBRE", "JLG／自画像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "鬼才J・L・ゴダールが、自身のイニシャルをタイトルに冠して描いたセミ・ドキュメント風の一編。スイスのレマン湖畔で暮らす彼の生活を捉えながら、国立映画局の査察官の介入などのフィクションを織り込んで映画は展開。さらに鮮烈なイメージ、アラゴンの『断腸詩集』などの引用、映画論や芸術論、政治論など、ゴダールの思想が散りばめられている。", "1995-01-01", "2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Witness", "刑事ジョン・ブック／目撃者", "", "", "http://ecx.images-amazon.com/images/I/41iAK1gkH5L._SL210_.jpg", 112, "「誓い」「危険な年」のピーター・ウィアー監督が、ハリソン・フォードとコンビを組んだサスペンスドラマ。アメリカ・ペンシルバニアの片田舎で、文明社会から距離を置き、17世紀の生活様式を守って暮らすアーミッシュ。その村出身の子供サミュエルがフィラデルフィア駅のトイレで殺人事件を目撃する。捜査を担当する刑事ジョン・ブックが、サミュエルと母親のレイチェルを署に連れて聴取を行うと、サミュエルはジョンの同僚刑事のマクフィーが殺人事件の犯人だと告白する。共演にケリー・マクギリス、ダニー・グローバー。", "1985-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRINCE AND THE PAUPER", "放浪の王子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "M・トウェインの名作『王子と乞食』の映画化。瓜二つの王子と乞食が、ふざけて衣服を取り替えたばかりに、立場が入れ代わり思わぬ人生を送るが、王子の忠実な騎士に助けられ王位に復するという時代活劇。1962年と1977年にも再映画化されている。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STANLEY ANDA LIVINGSTONE", "スタンレー探検記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ヘンリー・スタンレーによる1869年・第１回アフリカ冒険の旅を映画化。４年前に行方不明になったリヴィングストン博士を探すため、ニューヨークの新聞記者スタンレーはアフリカに赴き、熱病や大自然の脅威など困難を乗り越えながら、ようやく博士を見つける。しかし、帰国を拒む博士の意を汲み、一人でロンドンへ着いた彼を待ち受けていたものは……。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A NIGHT IN CASABLANCA", "マルクス捕物帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ドタバタ喜劇の王者マルクス兄弟の主演作品。ナチが財宝を隠したカサブランカのホテルで、支配人が次々と殺される。新しい支配人におさまったグルーチョは、チコ、ハーポと助け合い、足を引っぱり合いながらナチの残党と闘う。ビルを使った迫力満点のギャグが必見。DVDは「マルクス・ブラザーズ コレクターズ・ボックス」に収録。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("侠盗高飛／FULL CONTACT", "フル・コンタクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "バンコクのナイトクラブで、用心棒として働くジェフは、借金にあえぐ相棒のサムのため、武器輸送車の襲撃に参加する。しかし、リーダーのジャッジとサムの裏切りで、ジェフは窮地に立たされる。九死に一生を得、香港に帰ったジェフ。そこではあのジャッジがマフィアのボスに成り上がり、かつての恋人がサムのものになっていた。すべてを失ったジェフは、ジャッジとサムに孤独な復讐を挑む……。チョウ・ユンファ独壇場のハードアクションが展開。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春の門 自立篇", "青春の門 自立篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "五木寛之の自伝的大河小説・第２部の再映画化。大学に進学した主人公・伊吹信介の、青春の彷徨を力強いタッチで描く。ボクシング部のコーチ・渡瀬恒彦と娼婦・桃井かおりの恋、渡瀬に捨てられた女・中島ゆたかの存在など、信介をめぐる大人たちの描写が見事。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love of the Dead", "コリン LOVE OF THE DEAD", "", "", "http://image.eiga.k-img.com/images/movie/55889/315x210.jpg?2690", 97, "世界中で死者がよみがえり、生きた人間を襲い始めてロンドンの街はパニックに陥る。ナゾのウイルスに感染し、ゾンビになってしまった青年コリンは、わずかに残る記憶をたよりに、恋人ローラと暮らしたアパートへと向かう。クリエイターのマーク・プライスが、製作・監督・脚本・撮影・編集をひとりで手がけ、製作費わずか45ポンド（約5800円）という超低予算ながらも、その完成度の高さから各国の映画祭で話題となった。", "2011-03-05", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Like a Virgin", "ヨコヅナ・マドンナ", "", "", "http://image.eiga.k-img.com/images/movie/53839/315x210.jpg?4552", 116, "女の子になることを夢見るぽっちゃりめな高校生ドングは、性転換手術のため力仕事のバイトに明け暮れていた。ある日、高校生シルム（韓国相撲）大会の優勝者へ贈られる奨学金の存在を知ったドングは、弱小シルム部に入部し練習に励むが……。主演に「トンマッコルへようこそ」のリュ・ドックァン、監督は本作がデビュー作となるイ・ヘジュンとイ・ヘヨン。SMAPの草なぎ剛がチョ・ナンカンとして担任教師役で出演している。", "2008-09-13", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fearless", "フィアレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "飛行機事故から生還し、恐怖心を失った男と、同じ事故で娘を死なせた失意から立ち直れない女。心を寄せ合い事故の後遺症を乗り越える彼らの姿を、男の妻との葛藤を織り交ぜて描く。安易な感傷に溺れないウィアーならではの硬派なタッチが光る。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BENT", "ベント　堕ちた饗宴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "世界中で上演され、好評を呼んだM・シャーマンの戯曲を映像化。ナチス政権下で、ユダヤ人よりも下等とみなされた同性愛者たちの、苦難の愛を描く。目を合わすことすら許されない苛酷な収容所生活の中で、悪政に屈しない確かな愛情をはぐくむ男たちの姿が、鮮烈な印象を与える。女装のゲイの役で出演したスーパースター、M・ジャガーの妖艶な(？)歌声が聴きどころ。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デコトラ・ギャル奈美", "デコトラ・ギャル奈美", "", "", "http://image.eiga.k-img.com/images/movie/54189/315x210.jpg?3459", 80, "伝説のトラック野郎だった亡き父のデコトラ（派手な絵やイルミネーションなどで飾り付けたトラック）を受け継いだ奈美は、威勢のいい性格と確かな腕前で、荷主たちにも大人気だった。そんな奈美を慕って、トラッカー相手に身体を売って生きている桃香は「まじめに生きたい！」と、奈美のトラックに乗り込んでくる。しかしある日、思いも寄らない事件に2人は巻き込まれてしまい……。監督・脚本は「ガチバン」の城定秀夫。", "2008-10-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロスト★マイウェイ", "ロスト★マイウェイ", "", "", "http://ecx.images-amazon.com/images/I/516UQXjyTBL._SL210_.jpg", 76, "名監督と新人監督が作品を競う「ワラ（＾O＾）番長」シリーズの1作として、「ドッペルゲンガー」の脚本家・古澤健が初メガホンを取り、アレックス・コックス監督のカルト的名作「レポマン」にオマージュを捧げたスローライフ・パンク・ムービー。郊外の中古自動車店・田宮モーターズに集まってバンド活動を行なっている吾郎、信平、亮介の冴えない中年男3人組。ある日突然、亮介の目から破壊ビームが出るようになり……。アレックス・コックス本人が特別出演。", "2004-04-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("[Rec]2", "REC レック2", "", "", "http://image.eiga.k-img.com/images/movie/54869/315x210.jpg?2897", 0, "登場人物が持つビデオカメラの視点による映像が話題となり、2007年に世界的ヒットを記録したスペインのホラー「REC／レック」の続編。前作で謎の伝染病による惨劇が起こったバルセロナ郊外のアパートへ調査に訪れた専門家たちと、彼らに同行するSWATチームが辿る壮絶な運命、さらに前作では明かされなかった感染の真相に迫る。前作に続きジャウマ・バラゲロ＆パコ・プラサが監督・脚本を担当。", "2009-10-24", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("×ゲーム2", "×ゲーム2", "", "", "http://image.eiga.k-img.com/images/movie/57609/315x210.jpg?1459", 0, "幼い頃にいじめを受けた者が企てる復讐を描いた、山田悠介の同名小説の映画化「×ゲーム」（2010）の続編。前作に参加した「AKB48」とその派生ユニット「渡り廊下走り隊」の菊地あやか、仲川遥香に代わり、同じく「AKB48」「渡り廊下走り隊」の多田愛佳が主演。共演に元「AKB48」の平嶋夏海。何者かにより密室状態の教室に監禁された女子学生・佐伯美鈴とネイリストの萩原裕子ら5人の男女は、「授業」と称したサバイバルゲームに強制的に参加させられ、復讐劇に巻き込まれる。同じ頃、いじめの復讐代行組織の噂を聞きつけた記者・尾藤が組織の謎に迫っていたが……。", "2012-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伽耶子のために", "伽耶子のために", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "在日韓国人二世の青年と、日本人ではあるが、在日韓国人の男と日本人の女という夫婦の養子として育った少女との出会いと別れを描く。純度の高い硬質のリリシズム。夜の白樺並木を二人がリヤカーを引きながら歩いていく場面の美しさは、恋する者たちの充足と悲哀を見事に表現している。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOWN PERISCOPE", "潜望鏡を上げろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "海軍のはみ出し者の乗組員たちが、原子力潜水艦撃沈のために巻き起こす騒動を描いた抱腹絶倒のコメディ。アメリカ海軍の最高幹部ウィンスロー提督は、アメリカ東部海岸線が敵国に侵略されたという設定のもとで、海軍に秘密作戦の訓練を命じる。作戦実行には40年前の旧式ディーゼル潜水艦が担ぎ出され、艦長には出世コースからはずれ、人並みはずれたユーモアのセンスを持つトーマス・ドッジ少佐が選ばれる。おまけに乗組員たちも海軍のはみ出し者ばかりだった……。ビデオ発売時タイトルは「イン・ザ・ネイビー」。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EIN STERN FALLT VOM HIMMEL", "空から星が降ってくる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "製作公開当時に大人気を博していたフィギュア・スケーター、イナ・バウアーが主演したアイドル青春映画。ラブ・ストーリーやコメディの要素も入った心地良い作品だ。引退し別の職業に就いている元女子フィギュア選手を、アイスショーに出演させようと二組のイベンター屋がスカウト合戦をする。一方の組は何と、彼女を誘拐してしまい……。", "1961-01-01", "7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くそガキの告白", "くそガキの告白", "", "", "http://image.eiga.k-img.com/images/movie/57899/315x210.jpg?2060", 94, "映画監督を夢見る不細工な青年と売れない女優が、かなわない夢と非情な現実にもがく姿を描いた青春映画。容姿に強いコンプレックスを抱く32歳の大輔は、映画監督になることが夢だった。しかし、格好つけてばかりで映画を撮ろうとはせず、周囲からあきれられる日々。一方、女優を目指す25歳の桃子は、オーディションに落ち続け、まもとな役がもらえない。ある日、そんな2人が撮影現場で出会い……。主演はお笑いコンビ「キングオブコメディ」の今野浩喜と、グラビアアイドルで女優の田代さやか。今回がデビュー作の鈴木太一監督が、自らの境遇と経験をもとに執筆した脚本で撮り上げ、ゆうばり国際ファンタスティック映画祭で審査員特別賞ほか4冠を達成した。", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍風カムイ外伝 月日貝の巻", "忍風カムイ外伝 月日貝の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "孤独な剣士カムイが大活躍する時代劇アニメの名作。原作は漫画家・白土三平の『サスケ』と並ぶ代表作。TVシリーズはフジテレビ系で1969年に放送され好評を博した。忍者グループの冷酷さが嫌で足を洗った主人公カムイだが、“抜け忍”と呼ばれて組織から追われる身に。しかし、そんな抜け忍たちが共に協力して追っ手たちと戦いを繰り広げる。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京島", "東京島", "", "", "http://image.eiga.k-img.com/images/movie/55019/315x210.jpg?4160", 129, "谷崎潤一郎賞を受賞した桐野夏生のベストセラー小説を、木村多江主演で映画化。共演に窪塚洋介、福士誠二、鶴見辰吾ら。メガホンをとるのは「犬と歩けば　チロリとタムラ」の篠崎誠監督。清子と隆の夫婦は旅の途中で嵐に遭い、太平洋に浮かぶ無人島に漂着する。隆が衰弱していくなか、さらに島に16人の若い男たちと、密航に失敗した6人の中国人の男が漂流してくる。やがて男23人と清子の奇妙な共同生活が始まるが、隆がナゾの死を遂げたことで、島での人間関係が崩れ始める。", "2010-08-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LJUBAVNI SLUCAJ ： ILI TRAGEDIJA SLUZBENICE PTT", "愛の調書、又は電話交換手失踪事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "電話交換手イザベラは衛生検査官アーメッドと出会い、結ばれ、そして彼に井戸に落とされて殺される……。実在の事件を基に、性犯罪の専門家のインタビューを交えながら多層的に描かれていく実験的意欲作。これが長編第２作のマカヴェイエフ監督は、この作品で各国に紹介され世界的な名声を得た。", "1967-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一万年、後…。", "一万年、後…。", "", "", "http://image.eiga.k-img.com/images/movie/34179/315x210.jpg?1531", 77, "テレビアニメ「まんが日本昔ばなし」の脚本家としても知られる沖島勲監督が8年ぶりにメガホンを取り、1万年後の日本に迷い込んだ男の運命を描いた異色ドラマ。とある民家に、光とともに突然1人の男が出現した。ところが、その場に居合わせた住人の少年・正一と妹の淳子は全く動じない。男は正一との会話を続けるうち、自分が1万年後の世界に来たことを知る……。主演は映画初主演となる個性派俳優・阿藤快。", "2007-09-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POIL DE CAROTTE", "にんじん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "フランスの小説家、J・ルナール原作の映画化。夫婦間の愛情が冷めてしまった時期に生まれ、兄たちと差別されて育っていく薄幸の少年フランソワの物語。デュヴィヴィエ監督が正確な自然主義タッチをもって、逆境の中を生き抜く主人公の姿を感動的に描いた名作。", "1932-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIVATE BENJAMIN", "プライベート・ベンジャミン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "二度の結婚に失敗した金持ちのお嬢様ベンジャミンは、ヒョンなことからアメリカ陸軍に入隊。みるみる出世して女の自立に目覚めるが、まだ結婚の夢は捨てきれない……。製作、主演を兼ねる人気コメディエンヌ、ホーンが女性の微妙な立場をコミカルに見せ、キュートな魅力をふりまく。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レオニー", "レオニー", "", "", "http://image.eiga.k-img.com/images/movie/55599/315x210.jpg?4256", 0, "世界的な彫刻家イサム・ノグチの母親レオニー・ギルモアの生涯を 「ユキエ」「折り梅」の松井久子監督が映画化した伝記ドラマ。1901年、女子大を卒業し、ニューヨークで教鞭をとっていたレオニーは日本から来た詩人・野口米次郎（ヨネ・ノグチ）と出会う。お互いに惹かれあい恋に落ちる2人だったが、レオニーの妊娠を知るとヨネは一方的に帰国してしまう。カリフォルニアに移りシングルマザーとなったレオニーだったが、日系に対する差別を感じ、ヨネの誘いにのって渡日を決意する。主演は「シャッターアイランド」のエミリー・モーティマー。共演に中村獅童、原田美枝子、竹下景子。", "2010-11-20", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("王手", "王手", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「どついたるねん」「鉄拳」と、パワフルな作品を作り続ける阪本順治が、これまでのボクシングの世界から一転した“真剣師（かけ将棋）”の世界に挑んだ意欲作。大阪・新世界に住む真剣師の飛田と、プロの棋師を目指す香山は幼なじみで、対照的な性格でありながらも二人は将棋が取り持つ腐れ縁の仲だった。そんな二人の前に伝説の老真剣師・三田村が現れる。三田村を演じた若山富三郎は、本作が遺作となった。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("電撃BOPのセクシーマザーファッカーズに!!", "電撃BOPのセクシーマザーファッカーズに!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「家族ロック」の島田角栄監督が放つ異色バイオレンス・コメディ。ケンカ500戦無敗のパンクロッカー、ヤクザ狩りに励む世直し活動家、暴力とロックンロールを愛するヤクザという3人の男が繰りひろげる破天荒な追跡劇を描く。第2次世界大戦の不発弾を見世物にして日銭を稼ぐパンクス岡島の前に、3年前に別れた元恋人・智子が現れる。智子の頼みで、ヤクザに拷問されているという彼氏を助けに向かうハメになる岡島だったが……。", "2007-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BULLETS OR BALLOTS", "弾丸か投票か", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ニューヨークの敏腕刑事ブレイクが、警察部長に昇進した親友マクラレンに免職されてしまう。衆人環視の前でマクラレンを殴ったブレイクは、ギャングのボス、クルーガーに拾われる。しかしそこには、ギャングと悪徳政治家の癒着を暴こうとするマクラレンの計略があった。社会悪に捨て身の闘いを挑んでいく男たちの運命を描いた硬派な社会派ドラマ。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銭形平次捕物控・からくり屋敷", "銭形平次捕物控・からくり屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "おなじみ長谷川一夫の平次と花菱アチャコの初代八五郎が、怪しげな新宗教の教祖のまわりで起きる殺人事件を解決する。悪党に踊らされて、教祖にまつりあげられていたお琴を、平次が幼なじみだったことから、事件の解決後に叔父の住む故郷へ帰してやる、という人情的なオチがきいている。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スチャラカ社員", "スチャラカ社員", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "大阪朝日放送製作の同名の人気テレビ番組の映画化作品。倒産寸前の“何でも貸します業”都田物産の社員が、会社再建のために一丸となって立ち回るドタバタ喜劇。当時の上方売れっ子喜劇人たちが総出演する。香川登志緒、沢田隆治という上方のお笑い界のドンが名を連ねていることに注目したい。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONTE DE PRINTEMPS", "春のソナタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "仏映画の名匠E・ロメールによる“四季の物語”シリーズの第１作。高校教師のジャンヌは、自分の部屋をいとこに貸し、恋人も不在で行き場がない。彼女は、パーティーで偶然ピアニスト志望のナターシャと出会い、彼女の部屋へ。父親の今の恋人エーブを快く思わないナターシャは、知的なジャンヌを父の恋人にと思うが……。偶然と誤解が紡ぎ出す男女の微妙な心理を、しゃれたセリフ回しの中に描き出すロメールの手腕が見事。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIN CUP", "ティン・カップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "しがないゴルファー仲間とのつきあい、酒、そして女たち……。いい加減な暮らしをそれなりに楽しんでいたロイの前に、ある日女性精神分析医モリーが現れる。頭脳明晰なエリートのモリーに恋するロイ。彼女の愛を勝ち取り、もう一度生き直すために最後のチャレンジ、全米オープントーナメントの参加を決意する。挫折した一人の男のサクセスストーリーであるとともに、愛のために再生しようとする男とそれを支える女のラブ・ストーリー。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマンゼアス", "ウルトラマンゼアス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "ウルトラマン生誕30周年を記念して３部構成で製作された「ウルトラマン　ワンダフルワールド」の第３部。赤いマスクのニュー・ヒーロー、ウルトラマンゼアスが登場し、地球を守る。ガソリンスタンド“MYDO”は、実は地球の平和を守る超宇宙防衛機構の本部。そして、スタンドの見習い店員・朝日勝人こそ、Z95星雲からやって来たウルトラマンゼアスだった。しかし、隊員たちはそのことを知らずにいた。そんな中、世界各地で金がなくなる怪現象が続出。調査を進めていくと金を食べる大怪獣コッテンポッペの仕業と分かり、その裏には地球支配をたくらむベンゼン星人のフォトジャーナリスト悪神亜久馬の姿があった。勝人をゼアスと知った悪神は、隊員の透と子供たちを誘拐し、戦いを仕掛けてくる……。DVDは「ウルトラマンゼアス　１＆２」に収録。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GHOST IN THE SHELL 攻殻機動隊2.0", "GHOST IN THE SHELL 攻殻機動隊2.0", "", "", "http://image.eiga.k-img.com/images/movie/53769/315x210.jpg?4023", 85, "西暦2029年。凶悪化していくコンピュータ犯罪を食い止めるため、政府は非公認の超法規特殊部隊である“攻殻機動隊”を結成する。国際手配されているテロリスト“人形使い”が日本に現われるという情報を掴んだ彼らは追跡を開始するが……。映像クリエイター・押井守の名を一躍世界に轟かせた近未来SFアニメ「GHOST IN THE SHELL／攻殻機動隊」を、押井自らの監修の下、最新鋭の技術でフルリニューアル。", "2008-07-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE", "BLUE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "エイズとの闘いに力尽き、1994年２月に亡くなった鬼才D・ジャーマンの遺作であり、画家イヴ・クラインにオマージュを捧げた異色作。タイトル通り全編青一色の映像に、ナレーションがかぶさるというもの。失明の危機にさらされる恐怖や、病を克服した“愛”について語る、ジャーマンの悲痛な声が印象的。", "1993-01-01", "3,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Creation", "クリエイション　ダーウィンの幻想", "", "", "http://image.eiga.k-img.com/images/movie/55009/315x210.jpg?4573", 108, "「種の起源」で進化論を唱えた19世紀イギリスの自然科学者チャールズ・ダーウィンを題材にした伝記ドラマ。ダーウィンの子孫にあたるランドル・ケインズが著した伝記「ダーウィンと家族の絆」を原作に、科学とキリスト教信仰との間で葛藤するダーウィンとその家族の姿を描き出す。ダーウィンとその妻を演じるのは実生活でも夫婦として知られるポール・ベタニーとジェニファー・コネリー。「ザ・コア」のジョン・アミエルが監督を務める。", "2009-10-20", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ekiden／駅伝", "ekiden／駅伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "駅伝に情熱を賭ける青年の姿を通して、人と人との心の交流や友情を笑いを交えて描く感動作。岬と早川は大学駅伝部で活躍した親友同士。マネージャーのさおりの応援が二人の励みになっていた。卒業後は、実業団チームで活躍する早川に対し、岬は造船会社に就職し、即席の駅伝チームで奮闘する毎日。純粋に走ることが好きな岬の清々しい姿は、やる気のなかった部員たちに刺激を与えていく。やがて迎えたオリンピック予選の日、さおりが見守る中、岬と早川は敵同士となって走ることに……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("No Blood No Tears", "血も涙もなく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「相棒　シティ・オブ・バイオレンス」のリュ・スンワン監督による痛快クライム・アクション。かつてスゴ腕の金庫破りだった女性キョンソンは、現在は足を洗い、借金返済に追われながらもタクシードライバーとして生計を立てていた。一方、歌手を夢見る元ラウンド・ガールのスジンは、暴力的な彼氏と縁を切ることができずにいた。ある日偶然出会った2人は、闘犬場から持ちだされた大金入りバッグをめぐる騒動に巻き込まれていく。", "2007-03-10", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("画魂／SOUL OF A PAINTER", "画魂 愛、いつまでも", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "19世紀末に中国で生まれ、中国人として初めてパリの画壇で成功を収めた実在の女流画家パン・ユィリャンの波瀾万丈の生涯を描いたドラマ。1920年代初頭の中国・安徽省の娼館に売られたユィリャンは、“水揚げ”の日に逃げ出し、官使のパンの元に身を寄せる。しかし官使と娼婦の恋はスキャンダルとなり、二人は上海へと移り住む。彼女はそこで初めて西洋絵画と出会い、美術学校に通うようになる。", "1992-01-01", "8,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD MEN DON’T WEAR PLAID", "四つ数えろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "マーティンが主演したパロディー３部作の２作目（１作目は「ペニーズ・フロム・ヘブン」、３作目は「二つの頭脳を持つ男」)。私立探偵が、ある美人に科学者である父親の行方を捜してほしいと頼まれる。捜査が進むうち、様々な邪魔が入り、事件は次第に大がかりなものへと発展。ついにはナチスの陰謀へとたどり着く……。物語はいたって単純明快だが、モノクロの映像、タッチとともに完全に1940年代探偵映画の世界を再現し、パロディー化。加えて、1940～50年代の本物の探偵映画のシーンを使用、実に巧みな編集でつないでいき、マーティンがボギーから情報を貰ったりする場面も飛び出す。共演スターは「殺人者」のバート・ランカスター、「汚名」のイングリッド・バーグマンなど18名に及ぶ。「シャーキーズ・マシーン」のウォードが1940年代の大スターに負けない美女ぶりを発揮し、色を添えている。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和残侠伝・吼えろ唐獅子", "昭和残侠伝・吼えろ唐獅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "花田秀次郎は黒田一家の女房と駆け落ちした子分を追って金沢へ。金沢では三州一家と稲葉一家が築港工事の入札をめぐって一触即発の状態にあった。秀次郎の昔の恋人・加代は三州の女房になっていた。駆け落ちした二人の命と引換えに、秀次郎は三州を斬るはめに……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRAGUE", "プラハ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "亡くなった母を映した幻のフィルムを求めてプラハにやって来た青年アレキサンダー。資料館の秘書エレナと出会った彼は恋に落ちるが、彼女は館長とも愛人関係にあった。エレナの奔放な行動に惑わされ、また母の過去が明らかになるにつれて彼は本当の自分に目覚めていく。幻想の街プラハを舞台に、繊細で官能的な映像で描くラブ・ストーリー。", "1991-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ENFER", "愛の地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ポールは、念願かなって南仏のリゾートホテルのオーナーとなり、街一番の美女ネリーと結婚する。至福の興奮と繁忙のあまり幻聴の兆しさえ見えていたポールは、やがて現実と妄想の境界線を見失っていく……。C・シャブロルが、偏執狂の夫の堕落を描く。E・ベアールとF・クリュゼの精神の均衡ぎりぎりの演技が見もの。ビデオ発売時タイトルは「エマニュエル・ベアール　愛の地獄」。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEKROMANTIK", "ネクロマンティック〈特別編〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "セックスと殺人の芸術性を追求した一連の作品で、良識派の評論家たちには無視されながらも、熱狂的な支持者層を持つユルグ・ブットゲレイトが、ネクロフィリア（死体愛好趣味）をテーマに撮った作品。死体とのセックスに快感を求める人間を、グロテスクに、ユーモラスに、アーティスティックに描く。本国ドイツでは、国内の上映禁止ばかりか、作品のネガはもちろん映画の製作にかかわるすべての素材の廃棄処分という裁判所命令が出された。この〈特別編〉は日本でのビデオ発売のために極秘に再編集されたもの。死体の一部をコレクションしているロベルトにとって、交通事故でバラバラになった死体の処理という仕事は趣味と実益を兼ねていた。ある日、死体をまるごとくすねるチャンスに遭遇、家に持ち帰って、同じ趣味を持つ妻と３人でベッドに入る。が、その快楽の虜となった妻は死体と駆け落ちしてしまい……。", "1995-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KNIGHTS OF THE ROUND TABLE", "円卓の騎士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "イギリスのアーサー王伝説に基づく史劇スペクタクル。MGM映画のシネマスコープ第１作として製作された作品でもある。６世紀のイギリスを統治していたアーサー王の忠実な部下ランスロットは、王妃グエナビアと愛し合うようになり、王の支配を覆えそうと企む異母妹モーガンの罠に落ちてしまう……。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("団地妻　昼下がりの情事（2010）", "団地妻　昼下がりの情事（2010）", "", "", "http://image.eiga.k-img.com/images/movie/55329/315x210.jpg?3331", 75, "1971～88年のわずか17年間で1133本を製作した「にっかつロマンポルノ」が、「ロマンポルノ・RETURNS」として22年ぶりに復活。記念すべき平成版ロマンポルノ第1弾として製作されたのが「団地妻　昼下がりの情事」。71年に誕生したロマンポルノ第1作「団地妻　昼下りの情事」を基に、「櫻の園」の中原監督が現代の団地妻の姿態に迫るべく“古巣”に復帰した。劇団「東京乾電池」で舞台を中心に活躍する高尾祥子が初ヌードに挑戦している。", "2010-02-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朝を呼ぶ口笛", "朝を呼ぶ口笛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "1958年度の全国小中学校生作文コンクールで文部大臣賞を受賞した吉田稔の『新聞配達』を基にした青春映画。父と母、幼い弟と妹の一家にあって、定時制高校に進学するため新聞配達をしてお金を貯める吉田稔少年を中心に、周りの人々の様々な人間模様を描く。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("国士無双", "国士無双", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "伊丹万作の傑作時代劇のリメイク版。浪人にそそのかされ、剣豪・伊勢伊勢守を名乗った青年が本物の伊勢守と遭遇。にせ物と本物の対決となるが、にせ物があっけなく勝ってしまう。権威主義の武家社会を風刺した伊丹の作品には及ばないが、中井貴一は好演。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("これがシノギや！", "これがシノギや！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「悲しきヒットマン」の原作者でもある山口組顧問弁護士の山之内幸夫ら11人のライターによるルポタージュ集をベースにした、ユーモラスなヤクザ映画。バブルの崩壊によって株を売ったヤクザににらまれる、証券マンの真一。やむを得ず見習いとして組に入った彼は、アタリ屋やビラ貼りといった“シノギ”の数々に手を貸すことになる。失敗続きの彼だが、実は伝説のヤクザの息子である彼は、酒に酔うと人が変わったように強気になり……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MILCZACA GWIAZDA", "金星ロケット発進す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "核の脅威を訴えた、東独とポーランドの合作SF。金星と同じ成分で、内部に磁気録音コイルがある隕石が発見された。さっそく世界中から集められた科学者８人が、ソ連の提供したロケットで金星に向かう。が、すでに金星は、核爆発を起こして自滅していた……。", "1960-01-01", "7,42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BROTHER McMULLEN", "マクマレン兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "カトリックの戒律に縛られて成長した、アイリッシュのマクマレン３兄弟がそれぞれの恋や人生に悩む姿をユーモラスに描いた人間ドラマ。父の死後、マクマレン３兄弟の母は、35年の間、自分を待ち続けた恋人の元へと去っていく。それから５年後、幸せな家庭生活を送る長男ジャックの家に、シナリオ作家を目指す次男のバリーと宗教の違う恋人のことで悩む３男のパトリックが転がりこんでくる。やがて３人は、離婚、セックス、結婚問題と人生の難題に直面していく。米国の新人監督の登龍門、サンダンス・インスティテュートの1995年度グランプリ作品。27歳のE・バーンズが製作・監督・脚本・主演を務め、２万ドルという低予算ながら高い評価を受けた。ビデオ発売時タイトルは「ブラザー・マクマレン」。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURIEL'S WEDDING", "ミュリエルの結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「ベスト・フレンズ・ウェディング」のP・J・ホーガンによるラブ・コメディ。町の有力者を父に持つミュリエルは、高校を中退して今は職もない。友人たちからはダサいと笑い者にされている。そんな彼女が、新しい自分になるため、名前をマリエルに変えてシドニーへと出発。幸せになるためには結婚しかないと信じる彼女の、大奮闘が始まる。ABBAの軽妙な音楽にのせて、結婚を夢みる女の子の姿をユーモアとペーソスを交えて描く。本国のみならず米国でも大ヒットし、20キロも体重を増やして主役に挑んだT・コレットは1995年度ゴールデン・グローブ賞のコメディ部門で主演女優賞にノミネートされた。", "1994-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIGNS OF LIFE", "サインズ・オブ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "米・メイン州の漁村イーストハセットを舞台に、時の流れに逆らえず消えていくボート工場で働く人々の、袋小路に入った人生からの再生を描く。A・ケネディ、B・ブリッジスら、個性豊かな演技派を配し、“苦悩”に絡めとられた人々の思いを的確に表現した、新鋭J・D・コールズの演出が光る。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸福な食卓", "幸福な食卓", "", "", "http://image.eiga.k-img.com/images/movie/33819/315x210.jpg?4795", 108, "人気作家・瀬尾まいこの同名小説を原作に、ある家族の崩壊と再生を中学生の娘・佐和子の目を通して描く人間ドラマ。3年前に父親が自殺未遂してから、少しずつ家族の歯車が狂い出してきた中原家。兄は大学進学をやめて農業を、母は家を出て1人暮らしを始めた。そして父は、家族全員が揃う朝食の席で“父をやめる”ことを宣言。そんな中、佐和子は転校生の男の子・大浦と親しくなるが……。主人公・佐和子に抜擢されたのは新人の北乃きい。", "2007-01-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("希望ヶ丘夫婦戦争", "希望ヶ丘夫婦戦争", "", "", "http://image.eiga.k-img.com/images/movie/54619/315x210.jpg?2801", 88, "1979年に日活ロマンポルノとして映画化された実相寺昭雄の短編小説を、現代の視点で再び映画化。さとう珠緒が欲求不満の人妻を体当たりで熱演。東京郊外に住む猫田千吉は仕事のストレスからEDに陥り、妻・弘子の欲求を満たすことが出来ずにいた。弘子は夫の愛情を取り戻すため必死に奔走するが、リアルドールに性的快楽を求め始めた千吉との溝は深まるばかりだった。", "2009-06-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天装戦隊ゴセイジャー　エピック ON THE ムービー", "天装戦隊ゴセイジャー　エピック ON THE ムービー", "", "", "http://image.eiga.k-img.com/images/movie/55539/315x210.jpg?1670", 0, "“護星界”から地球を守るためにやって来た5人の見習い“護星天使”たちが、不思議な力“天装術”で悪の軍団と戦う姿を描く「天装戦隊ゴセイジャー」劇場版。宇宙から2つのナゾの隕石が降ってきたことで、地球の山や海に不気味な異変が生じ始める。その2つの隕石を合体させた笛が吹き鳴らされると、地球が滅亡するという。そんなとき、最悪の宇宙人軍団でありゴセイジャーの因縁の敵”ウォースター“たちが5人の前に立ちふさがる。", "2010-08-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHERE THE BOYS ARE 1984", "ボーイハント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1961年製作の同名MGM映画の現代版。フロリダ州の常夏のリゾート地フォート・ローダーにやって来た、東部名門校の女子大生４人が巻き起こす、てんやわんやのボーイハント騒動を描く。軽快なロックサウンドにのって、女の子たちの水着姿が躍動する、ライトな青春コメディ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Aspen", "アスペン", "", "", "http://image.eiga.k-img.com/images/movie/57309/315x210.jpg?4591", 146, "1967年のデビュー以降、現代アメリカ社会を独自の視点で見つめ続けてきたドキュメンタリー作家フレデリック・ワイズマン監督が、スキーリゾート地としても有名なコロラド州アスペンの街のさまざまな側面をとらえたドキュメンタリー。19世紀には銀鉱山として栄え、現在は風光明媚な観光リゾート地として知られるアスペンは、その成り立ちゆえに自然と人工物、泥まみれの鉱夫と洒落たウェアで滑走するスキーヤーなど、さまざまな対立した文化が存在していた。2011年、ワイズマン監督作を一挙上映する「フレデリック・ワイズマンのすべて」にて日本初公開。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNA MUJER SI´N AMOR", "愛なき女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「女の一生」などで有名なG・D・モーパッサンの『ピエールとジャン』を原作とした作品。資産家一家の次男にある時、外国の見知らぬ人物から遺産がころがり込んでくる。それを見た兄は疑惑を抱いた。彼は病院を建てる夢をもっていただけでなく、弟が自分の昔の恋人と結婚してしまっていたため、嫉妬にも突き動かされた。しかも両親の仲は冷えきっている。彼は弟の出生の秘密を調べ、口論からあやうく殺し合いになりかかる。それを見た母は今までの苦労と忍従の過去を語って、兄弟の不仲に終止符を打つ。母ロサリオの過去を中心に据え、女の一生を描くメロドラマ。", "1951-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vidal Sassoon: The Movie", "ヴィダル・サスーン", "", "", "http://image.eiga.k-img.com/images/movie/57979/315x210.jpg?3809", 91, "「サスーン・カット」と言われる斬新なカットで、1960年代スウィンギング・ロンドンのファッションシーンを牽引し、美容界に大きな影響を与えたヘアスタイリスト、ビダル・サスーンのドキュメンタリー。ロンドンの孤児院で育ち、戦争や貧困などさまざまな苦難を経験したサスーンが、情熱と努力で旧態依然とした美容界に革命を起こし国際的な名声を得た軌跡を、本人や友人、関係者らのインタビューでたどる。", "2012-05-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狼の眼", "狼の眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "とある郊外の街で、ゴルフ場建設をめぐり推進派と反対派が対立する。スナックの雇われマスターとしてこの土地にやって来た寡黙な青年、亮は、両親が誰かも知らない孤児だった。そんなある日、23年前に生き別れたヤクザな兄、勝が彼の前に現れる……。二大勢力の抗争に巻き込まれる兄弟の運命を描く異色のハードボイルド。映画は乾いた緊迫感と兄弟の情愛をにじませながら、クライマックスの荒々しい銃撃シーンにたどり着く。内なる野性に目覚める主人公に扮したモデル出身の鈴木一真、酒浸りの兄を生々しく演じた大和武士の存在感もともに圧巻。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　MUMMY", "ハムナプトラ／失われた砂漠の都", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "3000年前の古代エジプト、国王の愛人と恋に落ち、国王を殺害した高僧イムホテップはその罰として生きたままミイラにされてしまう。そして1926年、傭兵上がりの囚人リックは美人エジプト学者エヴリンやその兄ジョナサン、刑務所所長らと失われた都ハムナプトラを目指し、ついに黄金が眠る都を発見する。だが、彼らの前に、長年恨みを抱きながら地底で眠っていたイムホテップがミイラとなってよみがえり……。古典ホラー「ミイラ再生」のリメイクだが、最新のSFXを駆使した超陽性のアドベンチャー映画となっている。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sunset Heat", "ミッドナイトヒート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ロサンゼルスを舞台にした、本格派ハードボイルド・ストーリー。麻薬のディーラーから足を洗ったエリックは、４年ぶりにロスの土を踏む。だが彼を待ち受けていたのは、組織の金の強奪事件だった。犯人は彼の親友だったが、エリックを共犯と思い込んだ組織のボスは警察を操り、彼を殺人犯に仕立てる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOHAMMAD MESSENGER OF GOD", "ザ・メッセージ —砂漠の旋風—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 180, "西暦624年。イスラム教の開祖マホメットと、その教えに従う戦士たちの戦いを描くスペクタクル。マホメットは邪悪像を否定し、唯一絶対の神アラーの使徒として、現状が世界の終末を招くと予言する。リビア軍の全面的支援のもとで撮影された戦闘シーンは圧倒的迫力。以前発売されたビデオには、未公開シーン35分も収録。", "1976-01-01", "1,47", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESIRE ＆ HELL AT SUNSET MOTEL", "サンセット・モーテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "嫉妬深く神経質な夫は、愛人の部屋に通い詰める妻を、前科者のデッドパンに張らせる。だが、そのデッドパンも妻の官能の虜になってしまう。そんな中、愛人の死体が発見された。うだるような暑さの中、愛欲渦巻く事件は、意外な結末を迎える……。1950年代のアメリカを舞台に、セクシーな人妻をS・フェンが演じるミステリー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELL ON WHEELS", "マイヨ・ジョーヌへの挑戦／ツール・ド・フランス100周年記念大会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "３週間以上かけて、フランス一周約3500km、高低差200nを超えるコースを走りぬく世界最大の自転車レース、ツール・ド・フランス。記念すべき開催100周年を迎えた2003年の大会の模様を、ドイツの\"チーム・テレコム（現T-モバイル）\"に密着しながら追ったドキュメンタリーだ。歴史と伝統を誇るツール・ド・フランスは、いかにして発展したのか？　レース中のアクシデント、サポート側の心情など、フランスの雄大な景色の中に描く。", "2006-06-17", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PASSIONE D’AMORE", "パッション・ダモーレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "19世紀のイタリア。人前には決して顔を見せない“醜女”フォスカは、ハンサムな若き将校ジョルジュに激しい恋心を抱く。それを知った彼は、なんとか彼女から逃れようとするのだが……。恋愛映画の定説を破った設定と演出だが、観る者を妖しく魅了する異色ラブ・ストーリー。", "1980-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　CAVEMAN’S　VALENTINE", "ケイブマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "かつてジュリアード音楽院の天才作曲家として脚光を浴び、妻子にも恵まれたロミュラス。今はマンハッタンの片隅の洞窟で寝起きする世捨て人となった彼は、ある冬の日、若い男の死体を発見し、事件の真相に迫っていく。アメリカ探偵作家クラブ選定のエドガー賞に輝いた同名ミステリー小説の映画化。原作に惚れ込んだS・L・ジャクソンが、奇怪な妄想に取り憑かれて社会をドロップアウトした男の孤独な闘いを熱演している。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("河流", "河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「青春神話」「愛情萬歳」に続き、ツァイ・ミンリャンが、都市に生きる人々の孤独と愛への渇望を淡泊なほど静かに映し出した監督第３作。街をぶらついていたシャオカンは２年ぶりに女友達と再会。映画の撮影隊である彼女に頼まれ、死体役で汚れた川に浮かぶ仕事を引き受けた。だが、翌日シャオカンは首が曲がったままになる奇病に取り憑かれてしまう。ゲイ・サウナに通い続ける父、愛人との密会を続ける母。そんなバラバラの家庭が、シャオカンの首を心配することでかすかな結び付きを見つけたかに思えたが……。", "1997-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DR.JEKYLL ＆ MR.HYDE", "ジキルとハイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1908年以来、何度も映画化されてきた恐怖小説の古典『ジキル博士とハイド氏』。人間の中に秘められた善と悪の姿を映すこの傑作を今回は大胆にアレンジ。善良な医師ジキルは新開発の麻酔薬を吸い、別人に変身。彼は少年期の性的な精神障害のため、夜ごと娼婦をいたぶり、そのノドをメスでかき切るようになる。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Se Eu Fosse Você", "逆転夫婦!?", "", "", "http://image.eiga.k-img.com/images/movie/54779/315x210.jpg?2989", 108, "体が入れ替わってしまった夫婦が巻き起こす騒動を描き、本国ブラジルで大ヒットを記録したコメディ・ドラマ。広告会社で働く夫クラウディオと合唱団の教師を務める妻エレーナは、お互いを理解することができずにいつもケンカばかりしている。そんなある日、いつものように目覚めた2人は、それぞれの体が入れ替わってしまったことに気づく。2人はそのことを周囲に隠したまま、お互いの役割を演じて生活するのだが……。", "2009-10-03", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・ドッキリ大逃走", "喜劇・ドッキリ大逃走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "大阪万国博覧会の会場を舞台に繰り広げられるコメディ。世界を牛耳る財界の黒幕に移殖するための頑丈な心臓を提供するよう指令が飛び、マラソン選手の高井庄介に白羽の矢が立った。危うし庄介……。心臓強奪組織員のてんぷくトリオが懐しい。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死づえ／噂霊", "死づえ／噂霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "噂で話していた霊が現実のものとなり、惨劇を巻き起こす恐怖を描いたホラー作品。病気を苦に首を吊った女の子と車に轢かれて死んだ母親の霊が出る空き家をモチーフに、誰もが聞き覚えのある身近なエピソードでリアルな恐怖を演出。噂話ならではの恐怖感に着目したセンスが秀逸な力作だ。音楽プロデューサーの宮崎と歌手デビューを目指す妹の友香は一軒家を借りるが、その家は亡くなった母娘の霊が出る噂話があり……。", "2005-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUS LES MATINS DU MONDE", "めぐり逢う朝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "妻の死後、隠遁生活を送るビオールの名手サント・コロンブと、その弟子であるマラン・マレとの確執と音楽への愛情を18世紀を舞台に描く。ビオール奏者になることを決心し、サント・コロンブを訪れた若きマレ。だが名声を求めるマレと、音楽にすべてを捧げたサント・コロンブの間には、埋めようもない隔たりがあった。やがて宮廷音楽家になったマレは、将来を誓ったサントの娘マドレーヌをも捨て、栄華をきわめていく。絢爛豪華な18世紀宮廷風俗、そして古楽器ビオールの演奏シーンが見事に再現される。谷崎潤一郎の『陰翳礼讃』に傾倒しているという、A・コルノー監督の陰影に富んだ画面作りも見もの。1992年のセザール賞監督賞、助演女優賞ほか、７部門を受賞。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("First Love", "ファースト・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "", "2012-12-31", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("本陣殺人事件", "本陣殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「素晴しい蒸気機関車」など、自主製作作家として知られる高林陽一が、盟友・大林宣彦を音楽に迎えて挑む横溝正史の世界。“宿場の本陣”という日本的空間を舞台に起きた密室殺人。そのトリックと動機をあばくのは、中尾彬扮するジーンズ姿の金田一耕助。トリックが解明されるシーンは圧巻。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレヨンしんちゃん　嵐を呼ぶアッパレ！戦国大合戦", "クレヨンしんちゃん　嵐を呼ぶアッパレ！戦国大合戦", "", "", "http://ecx.images-amazon.com/images/I/61dxPyMdOAL._SL210_.jpg", 95, "スーパー幼稚園児・野原しんのすけが活躍するアニメの劇場版シリーズ第10作。なぜか戦国時代の春日部あたりにタイムスリップしてしまったしんのすけは、命を狙われる春日家の家臣、又兵衛を助けたことから彼の家にお世話になることに。しかし、そのころ、春日家の当主、康綱には重大な危機が迫っていた。この国を狙う大蔵井高虎は康綱の娘、廉姫との政略結婚を要求してきたのだ。しかし、康綱はこれを断り、やがて春日家と大蔵井の軍勢が合戦が始まることに……。しんのすけのおバカパワーは今作でも健在。そこに又兵衛と廉姫の恋物語、野原一家の家族愛などを盛り込み、笑いあり、涙ありのエンターテインメント作品となった。", "2002-04-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春雪", "春雪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "東横線で渋谷の会社に通う孝子と、東横線の運転手・木島は婚約を交わした間柄。ところが、孝子の父の失業や木島の経済状態を顧みれば、結婚もままならない。そんな時、孝子の妹に良縁がまとまってしまう。孝子はふさぎ込んでしまうが、木島の転勤話をきっかけに彼への愛情を再確認、いつまでも待とうと決心するのだった……。主演に“アーニーパイル劇場”出身の新人・藤田泰子が大抜てきされた。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAINT RALPH", "リトル・ランナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "奇跡を信じる少年の純粋さが、忘れていた心を思い出させてくれる珠玉の感動作。昏睡状態の母を目覚めさせるため、14歳の少年ラルフが、ボストンマラソンで優勝するという奇跡を起こそうと奮闘。ひたむきにがんばる姿に胸が熱くなる。14歳のラルフは、昏睡中の母を救うため、ボストンマラソン優勝という無謀な誓いをした。皆にバカにされても練習に励む彼に、次第に周囲も応援し始め、ついに彼の才能が開花する。", "2006-03-04", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE YEAR OF LIVING DANGEROUSLY", "危険な年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "スカルノ政権末期のインドネシアを舞台にオーストラリアTV特派員とイギリス大使館秘書との燃えるような恋愛を描く。といっても単なる恋愛ものではなく、当時の複雑な政治状況や東洋思想をじっくりと描き込むことに成功。娯楽映画に終わらぬ重厚さを作品にもたらしている。", "1983-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SKY PIRATES", "スカイ・レイダース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "第二次大戦末期、アメリカ＝オーストラリア混合の空軍チームが秘密の司令を受けてワシントンへ向かった。しかし途中で原因不明の事故に遭遇し、編隊は霧深い夜の海に不時着する。そこで彼らが見たものは……。オーストラリアのスタッフによって撮られたアクション・アドベンチャー。", "1986-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生ける人形", "生ける人形", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "『朝日新聞』に連載された片岡鐵兵の同名小説を、内田吐夢が映像化したサイレント作品。大志を抱いて都会へ乗り込んで来た田舎者の主人公が、複雑な社会機構に打ちひしがれていく様を、“モダン・ガール”との恋を交えながら、鋭い世相風刺とともに描いていく。", "1929-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パンドラの匣", "パンドラの匣", "", "", "http://image.eiga.k-img.com/images/movie/54289/315x210.jpg?3605", 94, "結核のため出兵することもかなわず太平洋戦争終結を迎えた少年ひばりは、「健康道場」と称する風変わりな結核療養所に入所。気まぐれで明るい看護士のマア坊や、美人看護士長の竹さん、個性的な療養患者たちとの日々を通して、次第に生きる活力を取り戻していく。太宰治の同名青春小説を、「パビリオン山椒魚」の冨永昌敬監督が映画化。主演は「フレフレ少女」の染谷将太。芥川賞作家で歌手の川上未映子が美人看護士の竹さんを演じ、女優デビュー。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("半支煙", "わすれな草", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "香港の下町を舞台に繰り広げられるハートフルな人間ドラマ。30年間もブラジルで逃亡生活を送っていたチンピラのヒョウは、忘れることの出来ないある女を探し出すために香港へ戻ってくる。同時に、宿敵・九龍への復讐も果たそうと考えていた。そんな彼の前に、若いチンピラのスモーキーが現れる。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHILDREN OF A LESSER GOD", "愛は静けさの中に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "M・メドフのトニー賞受賞舞台戯曲『小さな神の子ら』の映画化作品。大学を卒業し、片田舎のろう学校に赴任したジェームズは早々に７人の子供たちのクラスを受け持った。野心に燃えて、彼らの心に飛び込んでいこうとする彼の前にサラという名の美しい女性が現れる。彼女はみんなから鼻つまみ者に。その閉ざされた心を開かせようと必死になるジェームズに、サラは淡い恋心を抱くようになる。この作品でオスカーを手にしたM・マトリンは、映画を通じて実生活でもハートと恋に落ちたが、後に別れている。女性監督ヘインズのナイーブな感性が観る者の心を打つ佳作。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Chaser　追撃者", "チェイサー", "", "", "http://image.eiga.k-img.com/images/movie/54419/315x210.jpg?1915", 125, "04年に韓国で実際に起きた連続殺人事件をベースに、殺人犯と元刑事の追跡劇を緊張感たっぷりに描き出した犯罪スリラー。元刑事ジュンホが経営する風俗店から、女たちが相次いで失踪を遂げる。やがて店の客だった青年ヨンミンが容疑者として逮捕されるが、証拠不十分で釈放されてしまい……。韓国では観客動員数500万人以上を記録し、大鐘賞の作品、監督、主演男優賞など6部門に輝いた。L・ディカプリオ製作によるハリウッドリメイクも決定している。", "2009-05-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Present", "トーマス・キャンベル　ザ・プレゼント", "", "", "http://image.eiga.k-img.com/images/movie/54659/315x210.jpg?3404", 74, "多方面に渡って活躍する西海岸のカリスマ・アーティスト、トーマス・キャンベルが、「The　Seedling」「スプラウト」に続いて手掛けたサーフ・ムービー。カリフォルニア、オーストラリア、西アフリカ、ハ ワイ、インドネシアといった世界各地のビーチを約5年の歳月をかけて巡り、幅広い層のサーファーたちによる新しいサーフィン体験と、その可能性を追求していく。16ミリフィルムにこだわったアーティスティックな映像美にも注目。", "2009-08-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SGT.KABUKIMAN N.Y.P.D", "カブキマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "カブキマントで空を飛び、日本刀を振りかざす。扇子を広げ、ゲタを飛ばし、割りばし、手裏剣でなみいる敵をバッタバタ……。いったい日本が好きなのか嫌いなのかよくわからない、R・カウフマンの手による悪ノリ・ヒーローもの。その痛快なまでのムチャクチャさが気持ちいい。日本のナムコ、ギャガ、アメリカのトロマという３社の共同プロジェクト作品。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIRVANA", "ニルヴァーナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "アカデミー外国語映画賞に輝いた「エーゲ海の天使」のG・サルヴァトレスが手掛けた近未来ドラマ。2050年、売れっ子ゲーム・クリエイターのジミーは失恋のショックから立ち直れずにいた。そんなある日、準備中の新作ソフト“ニルヴァーナ”にウイルスが侵入したことから、ゲームの主人公ソロが自我に目覚め、リセットで繰り返される行為に嫌気を抱くように。苦しむソロを助けるため、ジミーは女性ハッカーらの協力を得て危険な冒険に臨む……。怪事件の真相の究明に迷える主人公の自分探しの旅が重なり、スリルとともに人間ドラマとしての歯応えを感じさせてくれる。", "1996-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEATLES AS THEY WERE", "ザ・ビートルズ・グレイテスト・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "20世紀最大のポピュラー・グループ、ザ・ビートルズの軌跡を数々のフィルムで綴ったドキュメンタリー映画。わずか８年間の活動期間のうちに、前人未到の記録を次々とうち立て、後の音楽界に計り知れない影響を与えた彼らの魅力の一端をうかがい知ることができる。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Snakes on a Plane", "スネーク・フライト", "", "", "http://image.eiga.k-img.com/images/movie/1659/315x210.jpg?3833", 107, "「数千匹の蛇がジャンボジェット機を制圧する」という奇抜なアイデアを元に「デッド・コースター」「セルラー」のデビッド・R・エリス監督が作り上げたB級パニック・アクション。組織犯罪の重要証人となった高校生の暗殺目的のために飛行中の旅客機に数千匹のヘビが放たれる。証人護送のため、その旅客機に同乗していたFBI捜査官（サミュエル・L・ジャクソン）がパニック状態の機内で対応を迫られる。", "2006-10-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Tempest", "テンペスト（2010）", "", "", "http://image.eiga.k-img.com/images/movie/55739/315x210.jpg?3486", 112, "ウィリアム・シェイクスピアが単独では最後に創作した同名戯曲を、「アクロス・ザ・ユニバース」のジュリー・テイモア監督が映画化。第83回米アカデミー衣装デザイン賞にノミネートされた。肉親の謀略により国を追われ、絶海の孤島で暮らす前ミラノ女王プロスペラが、島で会得した魔術を使って嵐を起こす。原作のミラノ王プロスペローの設定を女王に置き換え、新たな解釈で壮大な復しゅう劇を描き出す。", "2011-06-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAGIC CHRISTIAN", "マジック・クリスチャン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「イージー・ライダー」などで知られるT・サザーンの原作を映画化したもので、ハムレットのストリップティーズ、チャンピオンボクサーのゲイプレイなど、現代社会を痛烈に風刺したブラック・ナンセンスの怪作。主題歌をポール・マッカートニーが作曲、特別ゲストの顔ぶれも異色。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOMEZ　＆　TAVARES", "マルセイユ・ヴァイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "南仏のマルセイユを舞台に、モテたい、豪邸に住みたいなど、とにかく身勝手な欲望のためにがんばるマックス＆カルロスの凸凹コンビが大活躍。派手な銃撃戦、カークラッシュ、セクシー美女など、なんでもそろった娯楽精神に感服。刑事マックスは、パリから来た堅物刑事カルロスとコンビを組まされることに。ところがカルロスが汚職容疑で内偵調査されていると知り……。", "2004-11-13", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リリイ・シュシュのすべて", "リリイ・シュシュのすべて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "「Love Letter」「スワロウテイル」の岩井俊二がインターネットで展開した実験的な同名小説を、岩井自ら映画化。中学2年の雄一は、かつては親友だった星野からのいじめを受け、つらい日々を過ごしていた。彼は、唯一の救いである歌手、リリイ・シュシュのファンサイトを運営し、そこで仲間を見つけるのだが……。出演は市原隼人、忍成修吾 、伊藤歩、蒼井優ほか。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SERIAL MOM", "シリアル・ママ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "一見ごく普通の主婦ビバリーは、ホラー映画マニアの息子に精神科医のセラピーを受けるように忠告した高校の担任をひき殺し、娘をふった男を火かき棒で殴り殺してしまう。ビバリーは、平然と生活していたが、警察は彼女を疑い始め、捜査は次第に迫ってきた。それでも彼女は、気にくわない人たちを殺し続けるが、やがて警察に逮捕される。しかし、それで大人しくなるようなビバリーではなかった。彼女は弁護士を解雇し、無罪を主張。全米の注目を集めるのだった。犯罪者でさえ人気者になってしまうアメリカの現実を、カルト・ムービーの帝王、J・ウォーターズが皮肉たっぷりに描いたブラック・コメディ。K・ターナーがビバリーを快演。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪獣総進撃", "怪獣総進撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "昭和29年の「ゴジラ」でスタートした東宝の怪獣映画シリーズは、「モスラ」や「妖星ゴラス」などの傑作を生み出し、また「大怪獣ガメラ」や「大巨獣ガッパ」など他社の怪獣ものにも影響を与え、海外にも多くのゴジラ・フリークを生み出した。しかしそのマンネリ化は防ぎようもなく、昭和40年代に入ったあたりから初期の頃のヒューマンな味わいが薄れ、怪獣同士の戦いがメインの子供向け作品に移行していく。この作品はそれまでの東宝怪獣キャラクターが勢ぞろいして怪獣島に管理され、平和な生活を送っているという状況で物語が始まる。そこに宇宙から凶悪怪獣キングギドラが飛来し、地球怪獣との死闘が……。", "1968-08-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fast Food Fast Women", "ファストフード・ファストウーマン", "", "", "http://image.eiga.k-img.com/images/movie/1389/315x210.jpg?3019", 98, "マンハッタンのカフェで働くウェイトレスのベラは、恋愛も人生もちょっと停滞気味の35歳。彼女の不倫相手ジョージの情けないさまに業を煮やしたベラの母は、知り合いの青年ブルノとの見合いを勧める。出会ったベラとブルノは次第に惹かれあっていくが、前妻との間の2人の子供を育てているブルノには、心とは裏腹に「子供は嫌い」と口走ったベラの言葉がネックになり、あと一歩踏み込めずにいた。そんな折、ベラが老女をかばって暴漢に襲われてしまう。", "2001-02-03", "1,2,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GONE　FISHIN′", "ゴーン・フィッシン’", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「リーサル・ウェポン」シリーズでおなじみのJ・ペシ＆D・グローヴァーのコンビが主演の痛快コメディ。幼なじみで大の釣りバカ二人組のガスとジョーは、ひょんなことからフロリダで行われるあこがれのフィッシング・トーナメントに参加することに。舞い上がる彼らは家族の忠告も馬耳東風、早速、愛用のボートをけん引して一路、“聖地”に向かうのだが……。行く先々でトラブルを巻き起こす男たちのズッコケぶりと、意外な活躍(？)が笑える。困った表情もチャーミングなR・アークエットが魅力的。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Rules of Attraction", "ルールズ・オブ・アトラクション", "", "", "http://image.eiga.k-img.com/images/movie/1159/315x210.jpg?3172", 108, "85年発表の処女作「レス・ザン・ゼロ」「ルールズ・オブ・アトラクション」「アメリカン・サイコ」と80年代を描くベストセラーを3連発した米作家ブレット・イーストン・エリス。その第1作と第3作はすでに映画化済みだが、第2作が遂に映画化。監督・脚本を担当したのは「パルプ・フィクション」の共同脚本を担当、「キリング・ゾーイ」の監督でもあるロジャー・エイバリー。有名私立大学に通う生徒たちの群像劇が展開。", "2003-11-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pascali's Island", "パスカリの島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「危険な情事」でアカデミー脚色賞にノミネートされたJ・ディアデンが監督し、「ガンジー」でアカデミー主演男優賞を受賞したB・キングスレーが主演した異色ミステリー。20世紀初頭、滅亡しかけたオスマン帝国のスパイとして、ギリシャの孤島でひそかに暮らしていたバジル・パスカリ。そこに現れたボウルスと名乗る謎の考古学者。土地転がしまがいの方法で権力者から金を巻き上げる彼の行動に、心ならずも協力するはめになるパスカリ。しかし最愛の人リディアをボウルスに奪われた時、パスカリは最後の選択を迫られることになる……。男と女の愛憎、友情と嫉妬など人間の弱さをテーマにした秀作。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・道場破り 問答無用", "続・道場破り 問答無用", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "人格者の武人・大炊介高央は、ある日妹の許婚を無礼討ちにし、以来人が変わったように乱暴狼藉を繰り返すようになる。息子の乱心を嘆いた父は、ついにその命を奪う決意をし、高央の親友・柾木兵衛がその役を買って出るが……。「道場破り」が好評だったため、その主演コンビを再度起用して内川清一郎監督が撮りあげた時代活劇だが、前作とはまったく話の共通項はない。原作は山本周五郎の『大炊介始末』。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("傷城", "傷だらけの男たち", "", "", "http://image.eiga.k-img.com/images/movie/1889/315x210.jpg?4122", 111, "「インファナル・アフェア」シリーズの監督・スタッフが、トニー・レオン＆金城武を主演に迎えて放つクライム・サスペンス。元刑事の私立探偵ポンは、恋人の自殺が原因で酒びたりの生活を送るように。一方、彼の元上司であるベテラン刑事ヘイは、大富豪の娘と結婚し幸せの絶頂にいた。そんな2人の運命が、ある殺人事件をきっかけに再び交錯していく。「ディパーテッド」に続き、レオナルド・ディカプリオ主演でハリウッドリメイクが決定した話題作。", "2007-07-07", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("No Man's Land", "ノー・マンズ・ランド", "", "", "http://image.eiga.k-img.com/images/movie/1569/315x210.jpg?1973", 98, "アカデミー外国語映画賞、カンヌ映画祭脚本賞などを受賞。93年、ボスニア紛争下、ボスニアとセルビアの中間地帯“ノー・マンズ・ランド”に取り残された2国の兵士チキとニノの駆け引きに、彼らの周囲の国連防護軍や、マスコミの動きをユーモアを交えて描く。監督はボスニア戦争の最前線でドキュメンタリーを撮影した経験の持ち主。キャストもスタッフも、クロアチア、ボスニア、セルビア、英国、ベルギーなど各国の人材が集合。", "2002-05-25", "2,4,37,3,64", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖婆", "妖婆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "元大映社長の永田雅一のプロデューサー復帰第２作。オカルト・ブームに乗って製作された映画だが、むしろ平凡な女性が“妖婆”となっていく様に、女の執念と嫉妬を描き出した。少女から妖婆までを演じた京マチ子の熱演ぶりが話題を呼んだ。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Model", "モデル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本の首領 完結篇", "日本の首領 完結篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "片岡千恵蔵が、政財界の黒幕役で登場する。政財界の黒幕・大山は任侠団体の統一合体を提唱するが、佐倉も大石も乗ろうとしない。そこで……。片岡、佐分利、三船という三大スターの顔合わせが迫力満点。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("倩女幽魂III之道・道・道／A CHINESE GHOST STORYIII", "チャイニーズ・ゴースト・ストーリー３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "封印に閉じ込められた妖魔がよみがえろうとしている中国郭北県の蘭若寺。その寺に一夜の宿を得ようと立ち寄った二人の僧侶は古木の妖怪ロウロウ、美しい妖魔ロータスらと対決することになる……。おなじみの華麗なワイヤーアクションを駆使したSFXに加えて、エロティックな場面も盛り込んだ娯楽作。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КАВКАЗСКЙ　ПЛЕННИК／PRISONER OF THE MOUNTAINS", "コーカサスの虜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「自由はパラダイス」などで知られるS・ボドロフ監督がトルストイの小説を、現代のチェチェン紛争に背景を置き換えて映画化。コーカサス地方のロシア駐屯地。初めて戦闘に出かけたロシア兵のイワンは、チェチェン人の待ち伏せにあい、サーシャ准尉とともに捕虜となる。彼らは息子がロシアの捕虜となっているムラットという男に売り渡されるが、待遇は悪くなかった。イワンはムラットの娘のジーナとも親しくなる。イワンとサーシャは脱走を企てるが、サーシャは殺され、イワンは再び捕らえられる。穴蔵に足枷をされているイワンにジーナが鍵を渡すが、ムラットに見つかり、イワンは山の上まで連れていかれ……。", "1996-01-01", "51,24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("任侠木曾鴉", "任侠木曾鴉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "工藤栄一監督、大川橋蔵主演による股旅もの。橋蔵扮する主人公は、窃盗の濡れ衣を着せられて投獄されるが、出所後真犯人を捜して旅に出る……。工藤栄一監督には珍しい明朗時代劇で、おんな女衒に扮した丘さとみが新境地を見せた。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("House of Sand and Fog", "砂と霧の家", "", "", "http://ecx.images-amazon.com/images/I/21MZ07CT6EL._SL210_.jpg", 126, "夫に逃げられた孤独なアメリカ人女性とイランから亡命してきた元軍人一家が、一軒の“家”の所有権をめぐって激しく対立していくヒューマン・ドラマ。空しくも切実な争いを繰り広げる登場人物の思いをきめ細やかに描き、人間の心の拠り所は何かというテーマを観る者に投げかける。J・コネリー、B・キングスレーというオスカー俳優二人が、見ごたえたっぷりの演技合戦を披露。霧の立ちこめる美しい情景を挿入しながら、ずしりと重いドラマを仕上げたCM界出身の新人監督V・パールマンの手腕も見事だ。", "2004-11-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("闇の中の魑魅魍魎", "闇の中の魑魅魍魎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "榎本滋民原作の『血みどろの絵金』を中平康監督が自らの独立プロで映画化。幕末の土佐で活躍した異端の絵師・金蔵の若き性と、嵐のような内面を描く。主役の金蔵には、当時、状況劇場に属していた麿赤児が扮し怪演。この作品はカンヌ映画祭に出品されたが、不評に終わった。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紙人形春の囁き", "紙人形春の囁き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "1923年に監督デビューした溝口健二が、独自の作風を確立して一躍、注目を集めた記念碑的な無声映画。東京の下町・両国界隈を舞台に、古い商家に育った娘がたどる悲しい運命と一家の末路を流麗な映像のなかに描き出したメロドラマ。柳橋の料亭、浅草仲見世、歳の市などの下町風俗や、大正末期の人情世界を彩り豊かに再現するなど、その後の溝口作品を語るうえで興味深い演出スタイルが散見できる。", "1926-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NAVIGATORS", "ナビゲーター・ある鉄道員の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "イギリス映画界反骨の巨匠ケン・ローチ監督が、英国人労働者たちの苦悩の日々を描いた問題作。1995年、英国鉄道再編のあおりを受け、リストラの嵐が吹き荒れるヨークシャー州南部の保線会社に勤務しているポール。彼は離婚後、同僚の家に転がり込み、子供たちともろくに会えない日々が続いている。そんなある日、脱線事故が発生し……。", "2001-01-01", "3,7,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DECODER", "デコーダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ノイズ・サンプリングによって大衆をコントロールしようとする反逆的な青年など、音が人間に及ぼす影響をテーマに、アインシュトゥルツェンデ・ノイバウテンやサイキックTVのメンバーが中心となって製作した異色SF。『裸のランチ』などで知られる、作家バロウズの出演も話題となった。", "1983-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボトムズファインダー", "ボトムズファインダー", "", "", "http://image.eiga.k-img.com/images/movie/55839/315x210.jpg?3029", 0, "ロボットアニメ「装甲騎兵ボトムズ」の新作を3連続上映する「ボトムズフェスティバル」の第2弾。崖の上の世界“トプ”にあこがれながら、深い崖の底にある世界“ボトムズ”に暮らす少年アキ・テスノ。天性の人型兵器AT操縦技術をもつアキは、人々から“ダンブラー（曲芸師）”と呼ばれていた。ある日、ディアハルトという男から「トプからボトムズへ誘拐された少女を探してほしい」との依頼が舞い込み、アキは相棒のエイビィとともに“島”と呼ばれる廃墟へ向かう。", "2010-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUTANT WAR", "ミュータント・ウォー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "SFXを駆使した冒険スペクタクル。たび重なる核戦争とエイリアンの襲撃で、地球人は死滅しかけていた。汚染による変異体“ミュータント”は正常な人間を脅かしていたが、一匹狼ハリーが立ち上がり、彼らに戦いを挑んだ。グロテスクなクリーチャーが見もの。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IPHIGENIA", "イフゲニア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "ギリシャ映画界の巨匠カコヤニス監督の手による、“エウリピデス３部作”の映画化の完結編である。トロイア戦争のさなか、薄幸な運命をたどるアルゴスの若く美しい王女イフゲニアの悲劇を軸に、戦争の無慈悲、権力への欲望、愛憎の葛藤といった人間の宿業を描く。", "1978-01-01", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REFLECTION／呪縛の絆", "REFLECTION／呪縛の絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "富豪の時田が惨殺され、その莫大な遺産が養女の久美に転がりこもうとしていた。警察に不審がられながらも、隣家の秋山の目撃証言で助けられた久美は、やがて似た者同士の彼と愛し合うようになるが……。勝目梓の官能サスペンス小説を映画化。充電期間に入っていたグラビア・クイーン、木内あきらの復帰作となり、濡れ場も辞さない体当たりの熱演を見せた。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("float", "float", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "神奈川県の人造湖・相模湖畔で暮らす三梨朋子監督が、相模湖建設にまつわる実話にヒントを得て手がけた詩的な人間ドラマ。かつてダム建設によって小さな村が湖底に沈んだ。その後、土地を離れていた数人の村人たちが戻り、湖畔で暮らしはじめる。そんな村人の1人を祖父に持つ圭介は、祖父が営んでいた小さなボート小屋を継ぎ、たった1人で細々と生活している。ある日、ボート小屋に見知らぬ女性が迷いこんできて……。", "2004-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RAGE", "デストラクション 制御不能", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "敏腕FBI捜査官と殺人集団の死闘を、銃撃戦やチェイスの見せ場とともに描いたダイナミックなアクション・ムービー。FBIのニックは新任の女性捜査官ケイトと反目しながらも手を組んで、凶悪な暗殺集団を追跡。敵の手に落ちたケイトを救出し、壮絶な戦いの末に一味を壊滅に導いたニックだったが、さらなる罠が彼らを待ち受けていた……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Funny People", "素敵な人生の終り方", "", "", "http://ecx.images-amazon.com/images/I/51NVaImHvLL._SL210_.jpg", 140, "「40歳の童貞男」のジャド・アパトー監督が、アダム・サンドラーを主演に迎えたヒューンマンコメディ。スタンダップコメディアンとして成功を収めたジョージ・シモンズは、不治の病に冒され余命1年未満の宣告を受ける。絶望の淵に立たされながらもコメディアンとしての人生をまっとうしようと決意したジョージは、コメディクラブで出会った駆け出しのコメディアン、アイラの才能を見ぬいてアシスタントとして雇うことにするが……。", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マリンバアンサンブル", "マリンバアンサンブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "ネット配信で好評を博し、劇場公開された人間ドラマ。確かなテクニックを持ちながら認められないマリンバ奏者など、人生の分岐点に立った音楽家たちが、それぞれの道を模索していく姿を描く。深い悩みを抱えたヒロインを等身大で表現した新進女優、紺野まひるの好演が光る。世間に認められないマリンバ奏者のユリは、指揮者の加藤の誘いで、ある島を訪れる。そこで彼女は同じ悩みを持つ音楽家たちと出会い……。", "2005-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lakeview Terrace", "レイクビュー・テラス　危険な隣人", "", "", "http://image.eiga.k-img.com/images/movie/53619/315x210.jpg?4465", 110, "サミュエル・L・ジャクソン主演のバイオレンス・サスペンス。監督は「ベティ・サイズモア」のニール・ラビュートで、ウィル・スミスが製作を担当。白人と黒人の新婚夫婦・クリスとサラは、念願のマイホームを手に入れ、ロサンゼルスの閑静な住宅街に引っ越してくる。しかし越してきて早々、隣に住む警官エイブルに執拗な嫌がらせを受け始める。不条理な嫌がらせは次第にエスカレートしていき……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リアリズムの宿", "リアリズムの宿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「どんてん生活」「ばかのハコ船」で熱狂的な支持を得ている山下敦弘監督が、つげ義春の漫画世界に挑戦。常連俳優、山本浩司に加え、劇団“阿佐ヶ谷スパイダース”主宰の長塚圭史を起用して、冴えない男二人の当てのない旅模様を、しみじみとした風情と純朴な叙情が織りなす独特の描写の数々で紡ぎ出す。くるりの音楽も絶妙。駆け出しの脚本家・坪井と映画監督・木下は、共通の友人が遅刻したため、ともに温泉街を旅することになる。だが、顔見知り程度でしかないため、ぎこちない雰囲気。やがて、彼らは海で出会った少女と行動をともにすることになる。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mapa De Los Sonidos De Tokio", "ナイト・トーキョー・デイ", "", "", "http://image.eiga.k-img.com/images/movie/55129/315x210.jpg?4328", 98, "「エレジー」のイザベル・コイシェ監督が、菊池凛子を主演に描くラブストーリー。共演に「パンズ・ラビリンス」のセルジ・ロペス、「たそがれ清兵衛」の田中泯ら。リュウは築地の魚市場で働きながら、裏で殺し屋家業に手を染めている孤独な少女。ある日、娘の自殺を嘆く実業家の手下から、娘の夫の暗殺を依頼される。「スペイン映画祭2009」で上映され、2010年9月に劇場公開される。", "2009-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL MOMENTO DELLA VERITA｀", "真実の瞬間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "F・ロージ、初のカラー映画。アンダルシア地方の片田舎。貧しい村で職にありつけない青年ミゲールは、金欲しさに闘牛士養成所に入る。厳しい訓練に耐え、闘牛士としてデビューした彼は、名声と金を手にするが、それとともに過密スケジュールがミゲールを襲う……。本当の闘牛士ミゲールによる迫真の闘牛シーンが見もの。", "1965-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍の子太郎", "龍の子太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "昔々、貧しい村に暮らす少年・太郎は、彼が龍になった母から生まれたことを祖母から聞かされる。その龍がいるという北の湖に向かった太郎は、赤鬼にさらわれた少女・あやを怪力で助け、彼女とともに空飛ぶ白馬に乗って、湖に辿り着く。龍の姿の母と再会した太郎。下の貧しい土地に湖の水を流したいと、母に頼む太郎だったが、湖のふちを打ち砕いた龍は、力つきて死んでしまう。その時、龍の体の中から……。原作は民話をもとに書かれた、松谷みよ子の同名児童文学。実写映画のベテラン・浦山桐郎がこれを淡々とした描写で綴りながらも、骨太の感動作に作り上げた。民話の世界を描いて、民話を生んだ人間の“生命力”まで掘り起こしているところに、その感動の源がある。また演出に応えた作画も秀逸で、最後に龍の体の中から太郎の母の姿が現れるところなど、むせかえるような生命の息吹きを感じさせる。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LINGUINI INCIDENT", "ニューヨーク恋泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "グリーンカード欲しさに結婚相手を必死で探すイギリス人のプレイボーイと、魔術師フーディニに憧れるウェイトレス。ニューヨークのバーで一緒に働くことになった二人は、バーの売り上げ金を狙って強盗を計画し始める……。サスペンスと笑いがブレンドされた、おしゃれなラブ・ロマンス。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Van Helsing", "ヴァン・ヘルシング", "", "", "http://image.eiga.k-img.com/images/movie/1209/315x210.jpg?1353", 133, "「ハムナプトラ」シリーズのスティーブン・ソマーズ監督が、ユニバーサル・ホラーの人気モンスター大集合で描くアクション・アドベンチャー。モンスターハンター、ヴァン・ヘルシングが、ドラキュラ伯爵、フランケンシュタインの怪物、狼男と激闘。特殊効果はスコット・スクワイア率いるILM。主演は「X-MEN」のヒュー・ジャックマン。彼に協力する僧侶役で「ロード・オブ・ザ・リング」のファラミア役デビッド・ウェンハムが共演。", "2004-09-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍虎兄弟", "龍虎兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "小沢仁志が監督(監督名＝OZAWA)・主演で撮り上げたアクション。昇龍の刺青をした兄・昇と、猛虎の刺青をした弟・猛。三国人の指導者として恐れられた王海東の血を引く二人は、武田組組長の下、漢民街でヤクザとして生きていた。やがて、二人は組長から組の跡目を言い渡される。しかし、若頭の本田は不満を抱き、黒社会のボス、王烈と組んで漢民街と武田組の乗っ取りを開始。昇、猛は壮絶な戦いを繰り広げていく…。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒョンジェ", "ヒョンジェ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "70年代の大阪を舞台に日本人と在日朝鮮人の関係を描いた人間ドラマ。1968年、川崎でシージャック事件が発生し、警視庁の狙撃隊によって犯人が射殺された。それから11年後、大阪で暮らす高校生・純は、バンド活動とケンカに明け暮れていた。そんなある日、純は河原でドラムの練習をしていた在日朝鮮人ヨンチョルと仲良くなる。やがて2人は、お互いの家族がシージャック事件に関わっていたことを知り……。", "2008-01-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FORGOTTEN", "極秘指令　グリーン・ベレーを消せ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "K・キャラダイン主演のアクション作品。ベトナム戦争終結17年目に、やっと捕虜の身を解かれ、帰国した６人のグリーン・ベレー隊員。しかし彼らはすぐに西ドイツのボンに連れていかれ、敵地での戦いぶりを尋問される。しかしその謎の出来事の裏には、ある陰謀が……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL TETTO", "屋根", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "第二次世界大戦後の混乱期は脱したものの、依然、庶民にとっては貧しい暮らしが続くイタリア。父親の家に同居することになった若い夫婦の姿を通して、庶民の哀歓を綴った人情喜劇。一夜にして家を建て、屋根を葺けば居住権を認められるという法律を逆手にとり、一晩で家を建てようとする夫婦の悪戦苦闘ぶりを軽妙な語り口で描いている。", "1956-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌うエノケン捕物帖", "歌うエノケン捕物帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "エノケン主演の歌謡コメディ。エノケンが江戸の街で唄を歌いつつ仕事に励む篭かきに扮し、ふとしたことで目撃した犯罪捜査に協力する。自分の相棒が想いを寄せる女を脅していた人物が、悪玉の親分だと分かり……。エノケンの相棒役を歌謡界の大スター・藤山一郎が演じたことでも話題となった。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SISTA　KONTRAKTET", "暗殺の瞬間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1986年２月28日、当時のスウェーデンの首相オロフ・パルメが路上で暗殺された。この迷宮入りしてしまった事件の舞台裏を、大胆な解釈を盛り込んで映像化した本格派のポリティカル・サスペンス。主人公はストックホルム市警の腕利き刑事ニューマン。いち早く姿なき暗殺者のにおいをかぎ取った彼が、執念の捜査を繰り広げながらも、不可解な上層部からの指示によって孤立していく姿がリアルに描かれる。", "1998-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORT APACHE，THE BRONX", "アパッチ砦ブロンクス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "犯罪発生率が異常に高い街、ニューヨークのサウス・ブロンクス。その犯罪多発地区にある唯一の安全地帯、ニューヨーク市警41分署は“アパッチ砦”と呼ばれる市民のよりどころだ。ここで働く二人の警官の活躍を描いた社会派アクション。ニューマンは初の警官役。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MACKENNA'S GOLD", "マッケンナの黄金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "アパッチ族の老酋長が隠した黄金の在りかに群がる、様々な人間のむきだしの欲望を西部の広大な岩山地帯を背景に、J・リー・トンプソンが描いた豪快なアクション西部劇。地震の中を脱出するクライマックス・シーンはシネラマならではの大迫力。DVDは「コロムビアトライスター ザ・ウェスタン・ムービーズBOX(2)」に収録。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("この胸のときめきを", "この胸のときめきを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "数々のオールディーズ・ナンバーをバックに、修学旅行で京都に集まった高校生たちと現地の女子高生二人、さらには大人たちまで交えて繰り広げられる、ときめき青春映画の快作。登場人物のすべてが“がんばりや！”と声をかけたくなるほどに魅力的だ。特に、畠田理恵扮する文学少女メロンの近過去と近未来の間をさ迷っているような、夢みる乙女像が素晴らしい。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILENCE LIKE GLASS", "ガラスの中の私", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "将来を嘱望されるバレリーナが、ステージで突然倒れ、病院へ担ぎこまれる。しかもそこは、がんセンターだった。華やかな生活から一転して苛酷な入院生活へ。一度は人生に絶望する彼女だが、やがて意志の力でがんを克服していこうとする……。実話をもとに、主人公の揺れ動く心理を見事に映像化したドイツ出身のシェンケルの感動作。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIDING THE BULLET", "ライディング・ザ・ブレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ホラーの帝王、スティーヴン・キングの短編を、キングの盟友ミック・ギャリス監督が映画化。母親を見舞うため病院までヒッチハイクの旅に出た青年が、死神や悪霊、不気味な死者に襲われる恐怖を描く。キング自身の臨死体験を基にした、リアルな世界観に圧倒される一作。どこか陰のある雰囲気を持つ画学生のアランは、倒れた母を見舞うために、ヒッチハイクをして病院へ向かおうとするが……。", "2005-07-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白馬童子 南蛮寺の決斗（完結編）", "白馬童子 南蛮寺の決斗（完結編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "当時“第２の大川橋蔵”と噂された白面の貴公子・山城新伍の“風小僧”ものに続く主演作。長崎を舞台に白馬にまたがり、何処からともなくやって来た白馬童子が、連続殺人で世情を騒がす黒蜘蛛党と闘う。白馬童子は当時の子供たちのアイドルであった。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("≒〈ニア・イコール〉会田誠", "≒〈ニア・イコール〉会田誠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "各分野のカルト的人気を誇るアーティストをとりあげるドキュメンタリー“ニア・イコール”シリーズの森山大道に続く第２作。今回は絵画を皮切りに小説、漫画と様々な表現方法で作品を発表する異端の現代美術家、会田誠の実像に迫る。枠にとらわれない彼の活動及び制作過程から私生活に至るまでカメラが密着。彼の創造の源と人柄が見えてくる作品だ。『切腹女子高生』などで知られる美術家の会田誠。2002年２月、彼は広島市現代美術館の依頼で200号の大作に挑む。また、村上隆氏の主宰するカルティエ現代美術財団美術館の展覧会にも誘われ、フランスのパリを訪れる。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BELLE CAPTIVE", "囚われの美女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“アンチ・ロマン”の旗手として知られる作家A・ロブ・グリエの監督作品。情報の運び屋ヴァルテルと彼の前に現れた謎の美女をめぐる物語は、観る者を現実と幻覚が交錯する迷宮に誘い込む。また、H・アルカンのカメラ・ワークが、マグリットのオマージュと相まって幻想的な世界を構築している。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The City of Violence", "相棒　シティ・オブ・バイオレンス", "", "", "http://image.eiga.k-img.com/images/movie/33879/315x210.jpg?1244", 94, "「クライング・フィスト」のリュ・スンワン監督が製作・監督・主演・脚本の4役を果たしたノワール・アクション。幼なじみのワンジェの訃報を受けて帰郷した刑事テス。ワンジェを殺害した犯人が逃亡中だと知らされたテスは、同じく少年時代からの仲間であるソックァンと共に捜査を開始する。ところが、事件の裏には衝撃的な事実が隠されていた……。スンワン監督自らが身体を張って挑んだ豪快なアクションに注目。", "2007-03-10", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WARNING SHOT", "消えた拳銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "張り込み中、拳銃を向けられていると思い人を射殺してしまったバレンス警部。しかも射殺したのは、その界隈では慈善家として通っている医学博士だった。バレンスは、自らの嫌疑を晴らすため必死の捜査を開始する。D・ジャンセンが罠にはめられた警部役を好演したサスペンス。名女優L・ギッシュが特別出演している。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺は上野のプレスリー", "俺は上野のプレスリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「俺は田舎のプレスリー」の姉妹編ともいうべき作品。青森から東京に出てきた青年が、苦労しながらもひたむきに生きていく姿をコミカルに描く。都会生活が肌に合わない青年の下宿に転がり込む同郷の後輩に、吉幾三が扮する。東北訛りがかえって新鮮な味を出す。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Killing", "現金に体を張れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "　ライオネル・ホワイトの処女小説「見事な結末」を原作にした犯罪活劇。前科者のジョニーは競馬場から200万ドルの強盗を企てる。競馬場の会計担当やバーテンといった内通者の協力で綿密な計画を立て、金を奪うことに成功。しかし仲間の妻の画策によりアジトが襲撃される。50年代の人気ハリウッドスター、スターリング・ヘイドンが追いつめられていく主人公を熱演。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("家族", "家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "九州・長崎の小さな島を出て、北海道の開拓部落へ向かう５人家族の姿を描く異色のロード・ムービー。途中子供が急死する事件以外、あくまで日常的な、ドラマ性を排除したシナリオ。そして、まるでドキュメンタリーのような冷めたカメラワークが秀逸だ。1970年に開催されていた万国博覧会を観光映画的な描き方ではなく、高度経済成長の象徴としてストーリーに組み入れ、主人公一家の貧しさと対比させた演出も実に的確。山田洋次の力量が感じられる。ラストで、北海道にたどり着いた一家が祖父の死にもめげずたくましく生きていく姿が、この作品のテーマを十分に伝えている。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHEHERAZADE", "シェーラザード 新・千夜一夜物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "中世バグダッド。ジミー・ジニアスという中年男は、アラーの神へ悪態をついたため罰を下される。その罰とは、現代のイギリスへ飛ばされること。元の時代に戻るためには、魔法のランプを手にした人物の手助けをしなければならない。一方、ジミーのいないバグダッドでは、美しい王女シェーラザードが、断頭台の露と消えかけていた……。娯楽文学の古典『千夜一夜物語』を題材に、ジミーとシェーラザードの時を超えた冒険を描くSFアクション。", "1990-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUL　SURVIVORS", "エントランス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "交通事故で死の淵から奇跡的に生還した女子大生キャシー。しかしそれから彼女の周りで次々と不可思議な出来事が起こり、いつしか彼女は恐ろしい幻覚に囚われていく……。ティーン・ホラーのヒット作「ラストサマー」のプロデューサーによるサスペンス・スリラー。忍び寄る謎と恐怖の中、これが映画初出演となるヒロイン、M・セイジミラーの清楚な存在感が引き立つ。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE AMONG THIEVES", "おしゃれ泥棒２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "誘拐されたフィアンセを救い出すため、身代金代わりに要求された秘宝“ファバージュの卵”を盗み出したピアニスト、キャロライン。ところがすぐに謎の人物に追跡され、途中妙な冒険野郎につきまとわれ、おまけに得体の知れない山賊の大親分にはプロポーズされ……。手に汗握る愉快なロマンティック・ミステリーがリズミカルに展開する。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("荒木栄の歌が聞こえる", "荒木栄の歌が聞こえる", "", "", "http://image.eiga.k-img.com/images/movie/54449/315x210.jpg?3838", 0, "労働者階級の人々への音楽を中心に数々の名曲を残した作曲家・荒木栄の歌の魅力に迫る音楽ドキュメンタリー。栄が終生を過ごした大牟田、夕張、与論島など日本縦断ロケを敢行し、38歳の若さでこの世を去った荒木の軌跡を、「がんばろう」「星よおまえは」「夜明だ」「仲間のうた」などの名曲と共に追う。", "2009-03-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CABOBLANCO", "太陽のエトランゼ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "舞台は第二次大戦直後のペルーの小さな漁村、カボ・ブランコ。戦争の傷を癒し、静かに余生を送ろうとするギフの前に、一人の美女が現れる。彼女は戦争末期に輸送船とともに海に沈んだ恋人の本当の死因を調べるためにやって来たのだった。ブロンソンのタフガイぶりが光るサスペンス活劇。ビデオ発売時タイトルは「灼熱のカボ・ブランコ 狼たちの野望」。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEAR MRS.", "浮気はパリで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "二人の妻と一人の愛人を平等に愛し続けてきたヤリ手のビジネスマン。海兵予備隊への入隊には部下を替え玉に使って、自分は愛人とひそかにアカプルコで休暇としゃれこんだ。ところがその替え玉がソ連大使館に拉致され、話は米ソのスパイ合戦にまで発展して……。妻たちの“ニアミス”を軸に、様々な人間が入り乱れて展開する爆笑コメディ。", "1991-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIT THE DUTCHMAN", "ダッチ・シュルツ 仁義なき戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「モブスターズ」「バグジー」など、実在のギャングを描いた映画の流れにのって登場した実録ギャング映画。「ビリー・バスゲイト」でダスティン・ホフマンの憧れたギャング、「コットンクラブ」でリチャード・ギアを手下にした伝説のギャング、ダッチ・シュルツ。彼が、1929年に24歳で刑務所を出たところから、1935年ラッキー・ルチアーノの手下に射殺されるまでを生々しく描く。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Teenage Paparazzo", "ティーンエイジ・パパラッチ", "", "", "http://image.eiga.k-img.com/images/movie/55849/315x210.jpg?4346", 95, "米人気テレビシリーズ「アントラージュ★オレたちのハリウッド」で知られる俳優エイドリアン・グレニアーが監督を務め、13歳の少年パパラッチの姿を追うドキュメンタリー。パパラッチに追いかけ回される日々にうんざりしていたエイドリアンはある日、パパラッチの群れの中にオースティンという少年を見つける。少年に興味をもったエイドリアンは、彼の日常にカメラを向け、パパラッチとセレブリティの関係性を見つめ直していく。", "2011-02-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CADDY", "底抜けやぶれかぶれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“底抜け”コンビによるゴルフ・コメディで、シリーズ第９作。N.Y.パラマウント劇場に出演中のジョーとハービー。大人気を誇るコンビの楽屋で、取材に来た記者の求めからジョーの父が二人の生いたちを語りはじめるが……。当時のトップゴルファー、サム・スニードとベン・ホーガンが顔を出している。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blood Sweat and Gears: Racing Clean to the Tour de France", "ブラッド・スウェット＆ギアーズ", "", "", "http://image.eiga.k-img.com/images/movie/56499/315x210.jpg?3098", 93, "世界最高峰の自転車ロードレース「ツール・ド・フランス」に出場するプロチームに密着したドキュメンタリーを一挙公開する特集上映「サイクルロードレースの世界」にて上映。2007年冬、米コロラド州ボルダーを拠点とする無名の小さなサイクリングチーム「スリップストリーム」が、ツール・ド・フランス挑戦に向けて一歩を踏み出す。レースのゴール地点、パリ・シャンゼリゼに到達するまでに密着し、選手たちの等身大の姿を捉える。", "2011-06-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wasabi", "WASABI", "", "", "http://image.eiga.k-img.com/images/movie/1739/315x210.jpg?1993", 95, "ベッソンが設立したヨーロッパ・コープの第3弾。彼が製作と脚本を手がけ、監督は「TAXi2」のジェラール・クラブジック。主演は「レオン」以来7年ぶりにベッソン映画に登場するジャン・レノと、「秘密」を見たベッソンが惚れ込んだ広末涼子。強引な捜査で謹慎を命じられた刑事ユベール。そんな彼の元に1本の電話が入った。それは19年前に日本に住んでいたときに愛した女性、ミコの死を知らせるものだった。そして、日本に着いたユベールを待ち受けていたのは、初めて会う自分の娘ユミだった……。", "2002-02-02", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シャカリキ！", "シャカリキ！", "", "", "http://image.eiga.k-img.com/images/movie/53599/315x210.jpg?4122", 106, "曽田正人の自転車ロードレースをテーマにした人気コミックの映画化。監督は今村昌平、北野武らの助監督を努めてきた大野伸之。メインキャストに若手俳優集団D-BOYSの遠藤雄弥、中村優一、鈴木裕樹ら。自転車バカの高校生テルは廃部寸前の自転車部に入部するが、チームプレーを無視してレースで失格となり、部を廃部に追い込んでしまう。それでもレースを諦めないテルに、部員たちも心を動かされ、チームは再び強豪ライバルへ挑む。", "2008-09-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tandem", "タンデム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「髪結いの亭主 」「イヴォンヌの香り」のパトリス・ルコント監督作品。20年以上続くラジオのクイズ番組で司会者を務めるミシェル・モンテーズ。ある日、ディレクターのリブトは番組打ち切りの知らせを受けるが、その知らせをミシェルに伝えることができずにいた。ミシェルを「髪結いの亭主 」のジャン・ロシュフォール、リブトを「バティニョールおじさん」のジェラール・ジュニョが演じる。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JETSONS THE MOVIE", "宇宙家族〈スペース・ファミリー〉ジェットソンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "日本でも『宇宙家族』のタイトルで放映された劇場版。21世紀後半、新設工場の副社長になった父親とともに、工場のある小惑星に移住したジェットソン一家が、迫害されている先住種族の存在を知るが……。初期からのTV版の雰囲気を残しながらも、CG映像の使用や人気歌手ティファニーの声優起用など、現代的な味付けが加えられたSFコメディになっている。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREAKER MORANT", "英雄モラント／傷だらけの戦士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "南アフリカでイギリスが、オランダ系移民ボーア人に対して起こしたボーア戦争（1899～1902）で、当時イギリスの植民地であったオーストラリアは英連邦軍の一員として軍事的な協力を強いられた。そんなオーストラリア軍のある部隊が小競り合いの末、ボーア人を捕らえる。ゲリラか一般住民かの区別がつかず、捕虜の処置をイギリス軍総司令官に仰ぐが、玉虫色の返答が戻ってくる。そこで捕虜を銃殺にすると、事件が漏れ、人道上の非難が巻き起こる。やがて将校３人が軍法会議にかけられてしまい……。本国に存在をアピールしようと焦り、冷酷に利用されていく植民地人の心理を客観的に分析している。", "1980-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ORA　DI　RELIGIONE", "風の痛み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "恋愛映画「ベニスで恋して」が日本でもヒットしたS・ソルディーニ監督が撮り上げた、フェルメールの絵画タッチの幻想的な映像詩。その美しい映像で、イタリア・アカデミー賞の撮影賞に輝いた。父を殺して東欧からスイスに逃亡してきた男は、偽名で工場で働いていた。そんなある日、男は異母兄妹で人妻の妹に出会うが、自らの素性を知られていないのをいいことに接近する……。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILENT NIGHT DEADLY NIGHT ５：THE TOY MAKER", "キラー・ホビー　オモチャが殺しにやって来る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "クリスマスが近づいた夜、外に人の気配を感じたデレクは玄関を開けてしまう。そこには“クリスマスまで開けないで”とメッセージ付きの箱が置いてあった。待ち切れないデレクが箱を開けようとするのを父親が止め、箱を取り上げた。その時、箱の中のサンタ人形が恐ろしい形相になり……。製作・脚本を「死霊のしたたり」「ミクロキッズ」のB・ユズナが、SFXを「エルム街の悪夢」「ポルターガイスト２」などのスクリーミング・マッド・ジョージが担当したホラー作品。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HALLELUJAH TRAIL", "ビッグトレイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "J・スタージェス監督によるアクション西部劇。時は1867年。デンバー市の酒蔵が底をつき、市議会は40台の駅馬車を使って酒を輸送することを決定。さっそく輸送作戦が開始されるが、これに興味を持った禁酒主義同盟とスー族のインディアンたちが、駅馬車を狙って攻撃を仕掛けてくる……。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京大空襲 ガラスのうさぎ", "東京大空襲 ガラスのうさぎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "50万部のベストセラーとなった主婦が書いた戦争体験記を、国際児童年にふさわしい企画として映画化した。３月10日の東京大空襲で母と妹を亡くし、父も機銃掃射で殺されて兄と二人だけになった少女の悲しみが、観る者の心を打つ。風化していく戦争の記憶への警鐘として意義深い。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Io sono l'amore", "ミラノ、愛に生きる", "", "", "http://image.eiga.k-img.com/images/movie/56969/315x210.jpg?3892", 120, "ティルダ・スウィントンの主演で、イタリア・ミラノの上流社会を舞台に、富豪の夫人が息子の友人との情事を通して押し殺していた自分を解放していく姿を描いたドラマ。良妻賢母として富豪の一家を支えてきたエンマは、子どもたちもそれぞれ独り立ちし、自分を見つめなおしていた。そんな時、息子の友人と恋に落ちてしまい……。2010年・第83回アカデミー賞では衣装デザイン賞にノミネートされた。", "2011-12-23", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あーす", "あーす", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "大阪に住む小学校３年生の良平は、パッカー車（ゴミ回収車）に興味を持ちはじめ、そのあとを追うようになり、やがて、作業員たちと親しくなっていく。そして彼はゴミ収集を手伝い始めるのだが……。「君は裸足の神を見たか」で思春期のゆれる心を描いた金秀吉が、子供の優しい視点から見たゴミ焼却現場を通して、環境問題を訴えていく社会派ドラマ。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聖白百合騎士団", "聖白百合騎士団", "", "", "http://image.eiga.k-img.com/images/movie/54549/315x210.jpg?2639", 43, "帝都郊外のお嬢様学校に入学した幸子は、憧れの先輩・有希恵が部長を務める聖書黙読部に入部し、平和な学園生活を楽しんでいた。しかし聖書黙読部が生徒会から無期限活動停止命令を受けると、部員の本性が露になり……。AKB48の秋元才加が過激にマシンガンを発砲する女子高生を熱演。", "2009-05-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あにいもうと", "あにいもうと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "東京近郊、多摩川べりの田舎町。荒っぽい性格だが妹への愛は人一倍の兄と、いったん東京に出たものの年下の学生の子をはらんで再び兄のもとへ帰ってきた妹の、深い兄弟愛の世界を描いた作品。1936年、江口又吉脚本、木村荘十二監督のコンビで映画化された、室生犀星の同名小説の２度目の映画化。今回の脚本は成瀬とのコンビは定評のある水木洋子。成瀬の全盛期に撮られた傑作で、彼にとっては最後の大映映画でもある。登場人物の小さなしぐさや軽い目配せを緻密に組み立てて、ドラマの情緒をつかんでいく演出はまさに名人芸の域。成瀬の1953年作品に見られる、「夫婦」「妻」そして「あにいもうと」といったタイトルは、彼の作品世界のよって立つ場所を、これ以上ないほど明確に教えてくれる。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRANDED", "烙印", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "名撮影監督として知られる、R・マテがメガホンを執った西部劇。殺人を犯し、警察から追われているチョヤは、財産横領を狙って、失踪している息子になりすまし南テキサスの牧場主のところに乗り込んでいくが……。西部劇俳優、A・ラッドがチョヤ役を熱演している。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("薄化粧", "薄化粧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "女に溺れ、殺人を繰り返す野獣のような男と、その逮捕に執念を燃やす刑事たち。逃走途上の男にかかわった女たちの人生をダイナミックに描く、五社のリアリズム映画。次々に女をものにする緒形の演技が面白い。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Melancholia", "メランコリア", "", "", "http://image.eiga.k-img.com/images/movie/57249/315x210.jpg?1961", 135, "「ダンサー・イン・ザ・ダーク」「アンチクライスト」の鬼才ラース・フォン・トリアーが、巨大惑星の接近で終末を迎えつつある地球を舞台に、人々の孤独と絶望、魂の救済をワーグナー作曲の「トリスタンとイゾルデ」の壮大なメロディにのせて描き出していくドラマ。姉夫婦の豪華な邸宅で盛大な結婚パーティを開くジャスティンは、皆から祝福され幸福感に満たされる一方、どこかでむなしさも感じていた。そんなとき、巨大な惑星「メランコリア」が地球に向けて近づいていることが判明。それは同時に地球滅亡の知らせでもあったが、それを聞いたジャスティンの心はなぜか軽やかになっていく。主人公ジャスティン役のキルステン・ダンストが2011年・第62回カンヌ国際映画祭で女優賞を受賞した。", "2012-02-17", "18,14,2,7,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAIT−ON−JAMAIS", "大運河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "水の都ヴェネチア。老男爵の養女ソフィは、品行不良な許婚者との仲を清算して、彼女を愛するジャーナリストのもとへ……。ヴェネチアの風光にMJQが演奏するモダンジャズが流れる。ヴァディム流の艶のある演出も健在。アルヌールの美しさが花を添える。", "1956-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Walking With Dinosaurs 3D", "ウォーキング・ウィズ・ダイナソー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A BETTER WAY TO DIE", "ハード・トゥ・ダイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "暴力に嫌気を覚え、シカゴ警察を辞職したブーマーは、恋人の住む町へと向かう。ところが、その矢先に政府のスパイに間違えられ、FBIやマフィアから追われるハメに。追い詰められたブーマーは銃をとり、壮絶な逃避行を繰り広げる……。逃亡者の戦いを描くサバイバル・アクション。散りばめられたユーモアや、銃撃戦などの迫力あるアクションが見もの。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAW DEAL", "アーノルド・シュワルツェネッガー　ゴリラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "シュワルツェネッガーが主演する暗黒街アクション。わけあって田舎の交通パトロールに勤務する元FBI捜査官が、特命をおびてシカゴの巨大暗黒街組織に立ち向かう。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NATIONAL LAMPOON’S VACATION", "ナショナル・ランプーン　ホリデー・ロード4000キロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「アニマル・ハウス」や「ボールズ・ボールズ」でおなじみの、いわゆる“ナショナル・ランプーン”チームによるロード・コメディ。原作は、ユニークな風刺にあふれる『ランプーン』誌に掲載された短編。アメリカ中産階級の家族４人が、シカゴからロサンゼルスの大レジャーランドへ行くまでの爆笑珍旅行と、着いてからのひと騒動を描いた。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blue Cha-Cha", "深海 Blue Cha-Cha", "", "", "http://image.eiga.k-img.com/images/movie/33609/315x210.jpg?4681", 108, "東京国際映画祭で正式上映され高い評価を得た台湾発のラブ・ストーリー。刑務所から出所したばかりの娘アユーは、刑務所で知り合ったアンが経営するバーで働くことに。アユーは店の常連客チェンと恋に落ちるが、心に病を抱える彼女が彼を愛しすぎてしまった為に、2人の関係は破綻していく。デビュー作「夢幻部落」で世界の注目を浴びたチェン・ウェンタン監督の長編第3作目。主人公アユーを、台湾のトップ歌手ターシー・スーが演じる。", "2006-08-19", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pedro", "ペドロ", "", "", "http://image.eiga.k-img.com/images/movie/54859/315x210.jpg?2364", 90, "22歳でこの世を去ったHIV啓蒙活動家の青年ペドロ・サモラを描いた伝記映画。「ミルク」でアカデミー賞最優秀脚本賞を受賞したダスティン・ランス・ブラックが脚本を手掛ける。1972年にキューバで生まれ、8歳でマイアミへ移住したペドロは、17歳の時にHIV感染の診断を受ける。それからペドロはTV番組への出演を重ね、ゲイのHIV感染者あることを率直に語り、あっという間に民衆の心を捉えていく。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東おんなド根性", "関東おんなド根性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "江島組二代組長を継いだ江島美樹の任侠渡世を描く“関東おんな”ものの第３作。出所して久しぶりに故郷・川崎に帰ってきた美樹は、関東明和会会長選挙のいざこざに巻き込まれ、当選できなかった佐川組組長の逆恨みを買ってしまう。やがて佐川は、美樹に江島組解散を強要してくるのだった。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBBERY", "大列車強盗団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1963年にイギリスで実際に起きた30億円強奪事件をもとにしたアクション映画。ロンドン市内の大通りで、宝石商の車が事故を起こす。折よく通りかかった救急車が意識不明の宝石商を運び出すが、実はこの救急車はニセ物でダイヤを狙うギャングの車だった……。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN OF THE SECAUCUS 7", "セコーカス・セブン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "1960年代のベトナム反戦運動の渦の中で大学生活を送り、その後散り散りになってしまった７人の仲間たち。ニュージャージーの片田舎で、彼らは10年ぶりに再会する。職業も恋愛も別々の道を歩んできた彼らが、語り合い、酒を酌みかわし、バレーボールに興じて共通の体験をよみがえらせる。そして、また別々の生活へと帰っていくのだった……。1980年代のアメリカ独立プロ系映画隆盛の火付け役となった記念碑的作品。セイルズ監督特有の巧みなセリフ回しと、てらいのない演出で、熱き日々を共有した仲間の変わらぬ友情が、苦しみと慈しみを込めて綴られる。手作りのぬくもりを持つ気鋭監督の処女作で、ロサンゼルス映画批評家協会最優秀脚本賞を受賞している。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オボエテイル", "オボエテイル", "", "", "http://image.eiga.k-img.com/images/movie/55859/315x210.jpg?4605", 0, "直木賞作家の高橋克彦が盛岡を舞台に描いた3本の短編ミステリーを、「免許がない！」の明石知幸監督らが映画化したオムニバス作。新進作家がナゾの女性との出会いをきっかけに幼少時の記憶をよみがえらせる「遠い記憶」、頭痛に悩まされる女性が精神科での退行睡眠治療によって前世の恐怖へと導かれていく「前世の記憶」、故郷の古い住宅地図から男性の衝撃の過去が明らかになる「緋い記憶」で構成される。本編完成後、諸事情により公開されずにいたが2010年に劇場公開となる。", "2011-01-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In the Cut", "イン・ザ・カット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "「ピアノ・レッスン」のJ・カンピオン監督が、ニューヨークを舞台に撮り上げた官能的なミステリー。大学で文学を教えている知的なヒロインが図らずも殺人容疑者を目撃し、事件を担当する刑事とただならぬ関係に陥っていく。これまでのラブ・コメのヒロイン役から一転、女性の“性”を生々しくさらけ出す役柄に挑んだメグ・ライアンの新境地が話題を呼んだ。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("９ Songs", "９ Songs", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "髪や肌の感触、昼下がりのまどろんだ雰囲気、ライブでの高揚感……。マイケル・ウィンターボトム監督が、愛した人との思い出を断片的につなぎ合わせて描いた切ない愛の物語。カンヌ映画祭で話題となったラブ・シーンは、官能的であると同時にナチュラルでロマンチックだ。南極の荒野で、マットは元恋人リサを思い出す。愛を確かめ合い、ライブで興奮した束の間だが、輝いていた日々が彼の脳裏に甦る。", "2005-04-29", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エンディングノート", "エンディングノート", "", "", "http://image.eiga.k-img.com/images/movie/56519/315x210.jpg?1698", 89, "ガンの宣告を受けた一家の父が自らの人生を総括し、家族へあてた“エンディングノート”を残すまでの姿を軽妙なタッチでつづったドキュメンタリー。2009年、何事も「段取りが命」をモットーに高度経済成長期を支えてきた熱血営業マンの砂田知昭は、67歳で会社を退職。第二の人生を歩み始める。しかし、その矢先に重度の胃ガンが発見され、自身に「自らの死の段取り」を課すことになる。監督は知昭の娘で、是枝裕和監督らの映画制作現場に従事してきた砂田麻美。", "2011-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN COEUR EN HIVER", "愛を弾く女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ヴァイオリンの工房を共同経営するマクシムとステファン。陽気なマクシムは新進ヴァイオリニストのカミーユと恋仲にあったが、物静かなステファンも彼女の美しさに魅了されていた。やがてステファンの熱い視線にカミーユも気付き……。大人の男女の三角関係をクールに見つめた、味わい深い恋愛ドラマ。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOLDWYN FOLLIES", "華麗なるミュージカル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "何を作っても不入り続きの映画プロデューサーが、典型的なアメリカ娘を雇い入れ、彼女の意見を“大衆の声”にして新作映画にとりかかる。その映画作りの舞台裏を、様々な人気芸人たちを登場させながら描くミュージカル・コメディ。ジョージ・ガーシュインの名曲の数々がドラマを彩る。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love and Other Impossible Pursuits", "水曜日のエミリア", "", "", "http://image.eiga.k-img.com/images/movie/56179/315x210.jpg?1808", 102, "ニューヨークで働く新人弁護士のエミリアは、既婚者の上司と恋に落ちる。波乱を乗り越え離婚の成立した上司と晴れて結婚したエミリアだったが、8歳になる夫の連れ子には懐いてもらえず、周囲からは“略奪女”のレッテルを貼られ、さらなる困難が続く。次第に追いつめられていくエミリアだったが、ある時、意外な人物が手を差しのべる……。ナタリー・ポートマンが主演・製作。「偶然の恋人」のドン・ルース監督がメガホンをとる。", "2011-07-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あの橋の畔で　第一部", "あの橋の畔で　第一部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "記憶喪失、悲恋などのキーワードを散りばめ、現代の韓流映画にもその影響が見られる、日本産メロドラマの決定打的大作の第１部。後に「砂の器」で大ヒットを飛ばす野村芳太郎と山田洋次のコンビが強力。父の介護のため長崎へ帰った光晴の恋人の葉子だが、数年後に政略結婚を迫られていて再会できなくなってしまう。一方、東京で暮らす光晴は事故で入院、退院後は北海道へ。葉子はその後を追うが……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅の長脇差 伊豆の佐太郎", "旅の長脇差 伊豆の佐太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「花笠椿」に続く高田浩吉の旅の長脇差ものの第２作。縞の合羽に三度笠が板についた佐太郎はイカサマ師、素っ飛びの弥次と意気投合、二人で下田に向かう。その途中、井伊大老の密書を狙う塚越源兵衛たち10数人を相手に闘いを続けている宇津木伝之丞、数馬の兄弟の悲壮な姿に助太刀を買って出るのだが……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOMANI ACCADRA", "イタリア不思議旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "19世紀半ばのイタリアを舞台に演じられる西部劇風のロード・ムービー。当時28歳のルケッティのデビュー作で、人気監督モレッティが製作を担当している。1948年、イタリア中部のトスカーナ地方。人のいい牧童のルーポとエドは、病気の仲間を助けようと、地主の集金人を襲い、わずかな金のためにおたずね者となる。その二人を追う地主の息子と３人の賞金稼ぎ。そのほか盗賊の一味や風変わりな貴族、理想郷(調和の園)を創設した発明家といった不思議な人々が次々に現れる。牧童エドとルーポが遭遇する事件と冒険を、コミカルに描いた好編。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こどもたちの時間", "こどもたちの時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "英才教育などの特殊な保育は一切せず、ただ普通に“人間らしく”育てる保育を理念に掲げる東京都武蔵村山市の村山中藤保育園の１年余りにわたる記録。生後３ヵ月の赤ちゃんがハイハイを始め、歩けるようになり、言葉を発し始める……。そんな自立へと向かってゆっくりと、しかし確実に歩む子供たちの姿を捉えている。幼児教育期から受験のためのカリキュラムが組まれる時代、“人間が人間らしく育つとは？”と問うドキュメンタリー。ナレーションを担当するのは室井滋。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い波止場", "赤い波止場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "組織の保護の下、神戸に潜入している殺し屋は、彼に首ったけの情婦がありながら、兄を組織の手で殺されて食堂を女手一つで切り盛りするインテリ娘に惚れてしまう。組織に裏切られ、彼は情婦と国外脱出を企てるが、その娘がボスの手下に傷つけられたというデマに惑わされて、単身引き返してボスを射殺し警察に捕らえられ……。エキゾチックな神戸の港のムードと、ジャン・ギャバン主演の「望郷」を下敷きにしたセンチメンタルな物語がうまくはまった。裕次郎の魅力もさることながら、主人公に付きまとう刑事役の大坂志郎、脱出の手引きをするマダムの轟夕起子と脇役陣の充実ぶりも作品を魅力あるものにしている。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOUONGBLOOD", "栄光のエンブレム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ホッケー少年がジュニア・リーグ決勝に出場するまでの、恋と友情を描いた青春映画。R・ロウがホッケー選手役に挑戦し、熱演をみせた。P・スウェイジ、K・リーヴスらその後活躍する若手スターが出演しているのも注目。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("≒草間彌生　わたし大好き", "≒草間彌生　わたし大好き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "世界的に高い評価を受ける前衛芸術家・草間彌生の創作活動と日常を1年半に渡って記録し、多くの人々を魅了する草間ワールドの真髄に迫るドキュメンタリー。彼女の最新作であるF100号のモノクロ作品シリーズ“愛はとこしえ”50作の制作風景に密着し、真っ白なキャンパスから作品が完成するまでの貴重な瞬間を克明に映し出す。個性的なアーティストたちにスポットを当てたドキュメンタリー映画シリーズ「≒（ニアイコール）」第5弾。", "2008-02-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Transporter 3", "トランスポーター3 アンリミテッド", "", "", "http://image.eiga.k-img.com/images/movie/54089/315x210.jpg?4285", 103, "ジェイソン・ステイサム主演の人気カー・アクション「トランスポーター」のシリーズ第3弾。リュック・ベッソンがシリーズを通して製作＆脚本を務める。凶悪な男・ションソンから「赤い代物」の運搬を依頼された運び屋フランクだったが、それを断ると、愛車から20メートル離れると爆死するという罠を手首に仕掛けられてしまう。依頼品を運ばざるをえなくなったフランクは、同じ罠を仕掛けられた謎の美女ヴァレンティーナと依頼品に隠された陰謀に挑む。", "2009-08-15", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fear Is the Key", "爆走！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "妻と子供、弟、そして膨大な貴金属を積んだ飛行機が、カリブ海に沈む。貴金属を専門に運ぶ運送業を営む夫は落胆し酒に溺れるが、数年後ふとしたことから飛行機を墜落させた犯人の手がかりをつかむ。メキシコ湾に沈んだ金貨と宝石をめぐって展開するアクション・アドベンチャー。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fred Claus", "ブラザーサンタ", "", "", "http://image.eiga.k-img.com/images/movie/53059/315x210.jpg?1833", 116, "サンタクロースの兄フレッドは、弟とは大違いのダメ人間。一時は努力したものの弟の足元にも及ばず、ついには悪事を働いて刑務所へ……。兄想いのサンタクロースはクリスマスが差し迫る中、妻の反対を押し切り、そんな兄を引き取るのだが……。ビンス・ボーン扮する出来損ないサンタの兄フレッドが引き起こす騒動を描いたコメディ。サンタクロース役にポール・ジアマッティほか、レイチェル・ワイズ、ケビン・スペイシーら豪華スターも出演。", "2007-12-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE PIECE ワンピース THE MOVIE デッドエンドの冒険", "ONE PIECE ワンピース THE MOVIE デッドエンドの冒険", "", "", "http://ecx.images-amazon.com/images/I/21VBK14FHFL._SL210_.jpg", 90, "ルフィ海賊団が繰り広げる大冒険を描いた人気テレビアニメの劇場版第4弾。航海の途中で小さな港町ハンナバルに立ち寄ったルフィたち。この町では、海賊たちが命がけで競いあう危険なレース「デッドエンド」が開催されようとしていた。レースへの参加を決めたルフィたちの前に、優勝候補のガスパーデ海賊団や海賊処刑人シュライヤといった強敵たちが立ちはだかり……。監督はテレビ版のシリーズディレクターを務めた宇田鋼之介。", "2003-03-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("のんちゃんのり弁", "のんちゃんのり弁", "", "", "http://image.eiga.k-img.com/images/movie/53809/315x210.jpg?2928", 107, "子持ちシングルマザーの奮闘を描いた入江喜和のコミックを、「いつか読書する日」の緒形明が映画化。主演は小西真奈美。31歳専業主婦の小巻はだらしない亭主に愛想をつかし、娘ののんちゃんを連れて母親の暮らす京島へと出戻る。のんちゃんを幼稚園に通わせて仕事を探すが、キャリアも社会常識もない小巻は次々と面接に落ち、生活のため水商売のバイトを始めるが……。", "2009-09-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIVA KNIEVEL！", "ビバ・ニーベル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "スタント・オートレーサー、ニーヴェルが本人役で登場し、スタント・ショーや悪者退治に大活躍を見せる映画。暗い過去を引きずる整備士やシャッター・チャンスを狙ってつきまとう女流写真家などが絡んで、快調なテンポで進行する。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOLDIER’S FORTUNE", "ドッグ・イン・フォース 地獄の特攻野郎たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "アメリカに帰還したベトナム戦争の英雄ジョーンズは母国の変貌した姿に傷つき、戦争のプロとして再び母国を離れる。ところが娘が何者かに誘拐されてしまった。彼は屈強なメンバーを集め、娘の奪還に向かうが、相手の狙いが自分の命であることに気付く……。背景にベトナム戦争を置き、悲劇的な男の姿を描くアクション映画。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白線秘密地帯", "白線秘密地帯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "売春禁止法により消えた“赤線”“青線”に代わって登場した“白線”と呼ばれる秘密売春組織に若き刑事が潜入する。ロケを多用したセミドキュメンタリー・タッチの演出が生々しい迫力を出している。モデル出身の菅原文太が正式デビューを飾った。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KATE　＆　LEOPOLD", "ニューヨークの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "しっかり者のキャリアウーマンと、19世紀から現代にタイムスリップした公爵の、運命の恋を描くラブ・コメディ。広告会社勤務のケイトは、元ボーイフレンドの部屋で、クラシックな衣装を着込み、流暢な王立英語を喋るやたらと礼儀正しい男レオポルトに出会う。最初は不審がるケイトだったが、レディ・ファーストかつロマンチストのレオポルトの優しさに触れ、心が癒されていくのだった……。現代と19世紀のギャップが生むコミカルな描写が楽しい。恋に仕事に疲れ気味の女性を演じたメグ・ライアンのキュートさもさることながら、オーストラリア人俳優ヒュー・ジャックマンの究極のジェントルマンぶりがハマっている。DVDではディレクターズカット版（128分）もある。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WHOLE WIDE WORLD", "草の上の月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "女流作家N・B・エリスの自叙伝を映画化した、実話に基づく愛のドラマ。エリスの恋人であり、「コナン・ザ・グレート」の原作者として知られる1930年代の小説家ロバート・ハワードとエリスの関係を見つめつつ、手に追えないほどの自由奔放さを持っていたハワードの生を捉えていく。「ザ・エージェント」のL・ゼルウィガーがヒロイン、エリスを情熱的に演じている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊賀忍法帖", "伊賀忍法帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "角川映画が薬師丸ひろ子に続いて売り出した、渡辺典子のデビュー作。山田風太郎原作の持つ忍法帖の味を生かし、SFXを駆使した多彩な忍術が登場する。松永弾正が主家の妻に惚れ、その心をつかもうと陰謀をめぐらし、真田広之扮する忍者・城太郎がそれを阻止しようとするという物語。弾正に入れ知恵をする果心居士を演じた成田三樹夫の怪演が、最大の見もの。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はだしのゲンが見たヒロシマ", "はだしのゲンが見たヒロシマ", "", "", "http://image.eiga.k-img.com/images/movie/56729/315x210.jpg?1720", 77, "漫画「はだしのゲン」の作者・中沢啓治が、自身の被爆体験や「はだしのゲン」を描くまでの半生を語るドキュメンタリー。1945年8月6日、当時6歳だった中沢は広島市内の学校で被爆。自身は奇跡的に助かるも、父、姉、弟を同時に亡くし、直後に生まれた妹も4カ月で他界する。漫画家として東京での活動も軌道にのりかけた22歳のとき、広島の原爆病院に入院していた母も亡くなり、自分にできることは何かを考えた中沢は以降、戦争と原爆をテーマにした漫画を数多く発表していく。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUANA　LA　LOCA", "女王フアナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "スペイン映画界の名匠V・アランダの監督作で、本国では190万人を動員する国民的大ヒット作。大航海時代のスペインを統一したイサベルの娘にして、ハプスブルク家神聖ローマ帝国皇帝カール５世の母であるフアナ。多くの謎や伝説に満ちた彼女の激動の生涯を、女王としてではなく一人の女性としての、一途な愛の物語として綴っていく。本作が主演デビューとなるP・L・デ・アジャラはゴヤ賞とサンセバスティアン映画祭の主演女優賞を受賞した。", "2001-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The People vs. George Lucas", "ピープルVSジョージ・ルーカス", "", "", "http://image.eiga.k-img.com/images/movie/57449/315x210.jpg?4297", 92, "世界中の「スター・ウォーズ」ファンへの取材を敢行し、ファンならば誰もが抱く「スター・ウォーズ」の創造主ジョージ・ルーカスへの疑問をあらわにしていくドキュメンタリー。北米、南米、ヨーロッパから日本、そして南極まで、世界各地のファンを訪ね歩き、寄せられた映像素材もあわせて構成。「オリジナルの3部作で終わりにしておくべきだったか？」「『エピソード1』はそれほどひどい作品だったか？」「“遥か彼方の銀河系”は創造主のものか、ファンのものか？」といった論争を繰り広げていく。", "2012-03-10", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮本武蔵・一乗寺の決斗", "宮本武蔵・一乗寺の決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "武蔵は吉岡清十郎の弟・伝七郎から呼び出しを受け、三十三間堂で対決しこれを斬る。面目を失った吉岡一門は総力をあげて復讐を画策し、一乗寺下り松に武蔵を呼び出す。武蔵対吉岡一門73人の対決シーンは、内田吐夢の殺気みなぎる演出が冴えわたる名殺陣シーンである。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Karate Kid, Part III", "ベスト・キッド3／最後の挑戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "　たくましく成長した空手ボーイ、ダニエルの最後の闘いを描くシリーズ第3弾。カラテ大会で敗退したコブラ会はダニエルとミヤギへの復讐の機会をうかがっていた。ふたりの仲を引き裂こうとするコブラ会はバッド・ボーイと呼ばれるマイクを送り込み、ダニエルを挑発する。一方、ミヤギはコブラ会との決着は既についたと言って、ダニエルに大会出場を認めなかったが……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("面影", "面影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "老博士と、親子ほど年の違う若い夫人はむつまじく暮らしていたが、博士の弟子は夫人が自分の亡き妻と生き写しであることに気づき、驚く。ある日、子連れの夫人と遠出した弟子は大雨のため、ホテルで一夜を過ごすことになるが……。グラマー女優として売り出した浜田百合子を起用した耽美的な作品。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野山／IN THE WILD MOUNTAINS", "野山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "山間の農村が舞台。新しい耕作事業を次々と手掛けるものの何をやってもうまくいかない禾木は、妻から離婚され灰灰の家に転がりこむ。灰灰の妻は夫の保守的なところに愛想を尽かしていた。ついに禾木と灰灰は妻を交換し……。中国映画には珍しい題材の作品で、三大陸映画祭グランプリを受賞。", "1985-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Knight's Tale", "ROCK YOU! ロック・ユー！", "", "", "http://image.eiga.k-img.com/images/movie/1619/315x210.jpg?3175", 132, "「L.A.コンフィデンシャル」の脚本でアカデミー脚色賞受賞のヘルゲランドが「ペイバック」に次ぎ監督・脚本を兼任。音楽はコーエン兄弟作品の全作を手がけるカーター・バーウェル。「過去と現在を切れ目なく繋ぐ」という監督の意図のもと、14世紀の物語にクイーンやデビッド・ボウイの音楽を投入。「パトリオット」のメル・ギブソンの息子役で注目のヒース・レジャーを主人公に、ポール・ベタニーら英国の新鋭若手俳優が競演。", "2001-10-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泥の河", "泥の河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "宮本輝の太宰治賞受賞小説を映画化した、小栗康平の監督デビュー作。昭和31年、日本が戦争の傷跡から立ち直り、高度成長期に入りかけたころ。大阪・安治川の河口で食堂を営む夫婦とその息子・信雄、そして川の対岸に船を停めている、売春婦の母を持つ喜一と姉の銀子。これら世間の流れから取り残されたような貧しい人々の生活を、きめ細やかな演出で描いている。信雄が喜一と出会うことによって、少年としての悪さや、喜一の母が男に抱かれるところを見て、衝撃を覚えていく様が、みずみずしいタッチで綴られる。大阪の一時代を少年ドラマとして捉えた傑作で、アカデミー外国語映画賞にノミネートされた。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛇形刀手／SNAKE IN THE EAGLE’S SHADOW", "ジャッキー・チェンの蛇拳", "", "", "http://ecx.images-amazon.com/images/I/517rXovyvLL._SL210_.jpg", 97, "蛇形派挙法と鷹爪派挙法は宿敵同士。鷹爪派に押され気味の蛇形派を守る老人に偶然出会った青年が、蛇形派の誇りをかけて戦うことに……。それまでストイックなタッチのものばかりだったカンフー映画に新風を吹きこんだ、笑いあり、スリルありのコミカル・アクション。DVDタイトルは「蛇拳」。", "1976-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Broadway Bill", "其の夜の真心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "名馬ブロードウェイ・ビルを取りまく競馬の世界の人々を描いたコメディ・ドラマ。大富豪ヒギンズ家の娘婿ダンは、一家の当主と衝突し、愛馬ブロードウェイ・ビルを連れて家を飛び出す。彼は仲間と共にブロードウェイ・ビルを競走馬として育てあげ、大規模なダービーに出走させるためにあの手この手で奮闘するが……。監督は「或る夜の出来事」のフランク・キャプラ。彼は後に本作を「恋は青空の下」というタイトルで自らリメイクしている。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTINO PROFONDO", "ランジェリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "エロティックなランジェリーを着せられるのを嫌がる妻にあき足らない夫は、美人のモデルとの浮気に走る。一方、残された妻にも誘惑の手が伸びる。全編に散りばめられたエロティック・シーンと、様々なランジェリーが見もののセクシャル・ラブ・ストーリー。A・サルビィのコミカルな音楽が楽しい。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RICH IN LOVE", "リッチ・イン・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "母親が“第二の人生を始める”と置き手紙をして突然の家出をした。ショックで自分を見失ってしまった父、問題の多い姉夫婦、そんななか、17歳のルシールは恋も勉強も捨て、一家の主婦になりきろうとする。B・ベレスフォード監督が、繊細なタッチで綴った、哀しくもおかしいファミリー・ドラマ。フランス出身の名作曲家ジョルジュ・ドルリューの遺作でもある。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Curious Case of Benjamin Button", "ベンジャミン・バトン　数奇な人生", "", "", "http://image.eiga.k-img.com/images/movie/53529/315x210.jpg?3336", 167, "「セブン」「ファイト・クラブ」のデビッド・フィンチャーとブラッド・ピットが、F・スコット・フィッツジェラルドの短編小説を映画化。80代の年老いた姿で生まれ、歳をとるごとに若返っていき、0歳で生涯を終えたベンジャミン・バトンの奇妙な人生を、数々の出会いと別れを通して描く。共演にケイト・ブランシェット、ティルダ・スウィントン。08年度アカデミー賞では最多13部門にノミネート。", "2009-02-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("つづく", "つづく", "", "", "http://image.eiga.k-img.com/images/movie/56949/315x210.jpg?4748", 110, "小さな設計事務所を営む笠原信明のもとにある日、帝都ビル開発の裕香が訪れ、大型プロジェクトへのコンペに参加するよう呼びかえる。一方、帝都ビル開発内では、裕香とプロジェクト担当役員の澤木の動きに不審を抱いた井上と次期社長候補の木場が、2人の動向を調査。ライバルの澤木を失脚させたい木場は、信明に近づき裕香の動きを報告させようとする。思わぬ事態に巻き込まれた信明だったが、次第に事態が自身の過去と関係していることが明らかになっていき……。", "2011-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER SIEGE2", "暴走特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「沈黙の戦艦」の“最強のコック”が活躍するS・セガール主演のシリーズ第２作。元ネイビー・シールズの秘密戦闘員でスゴ腕料理人のケイシー・ライバックが、豪華列車を舞台に息詰まるゲリラ戦を展開。ロス行きの豪華列車グランド・コンチネンタルは、ロッキー山脈を走行中、狂暴なテロリスト集団に襲われる。狂気に憑かれた天才ハイテク技術者のトラヴィス・デインが優秀な傭兵ペンと手を組み、自分が開発した超高性能レーザー砲搭載のグレイザー１号の乗っ取りを企てたのだった。姪のセーラと休暇旅行のため列車に乗り合わせたライバックは、乗客の命を懸けて一人反撃を開始するが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mighty Uke", "マイティ・ウクレレ", "", "", "http://image.eiga.k-img.com/images/movie/57029/315x210.jpg?2383", 76, "ハワイアン・ミュージックに欠かせない民族楽器ウクレレの魅力をひも解くドキュメンタリー。1880年代にポルトガル移民によってハワイに持ち込まれ、音楽シーンの主流にのったウクレレだったが、1950年代にはエレキギターの普及によりその存在が忘れられていく。しかし、21世紀に入り新世代のウクレレ奏者たちが次々と登場。再び脚光を浴びるようになる。ジェイク・シマブクロ、ジェームズ・ヒル、タイマネ・ガードナーら現代の注目アーティストや、ビル・タピアら往年の名プレイヤーなど、多数のウクレレ奏者のインタビューを交えながらウクレレの魅力に迫る。", "2011-12-10", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PETER　PAN　IN　RETURN　TO　NEVERLAND", "ピーター・パン2／ネバーランドの秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "第二次世界大戦下のロンドン。かつてピーター・パンに導かれてネバーランドを訪れた少女、ウェンディは優しい母親に成長していた。しかしその娘ジェーンは戦争という厳しい時代に暮らす中で夢見る心を無くし、ピーター・パンの存在すら信じていない。ところがそんな彼女こそ、ネバーランド存亡のカギを握る人物だった！　ディズニー映画不朽の名作が約半世紀ぶりに蘇った続編。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COPS", "キートンの警官騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "“偉い社長になったら結婚するわ…”と言う市長の娘のハートを得るため、貧乏青年は詐欺師に売り付けられたガラクタを積んだ荷馬車で街を行く。ところが警官たちのパレードに紛れ込んだばかりに爆弾魔と間違われ、彼は街中の警官に追われるはめに……。無声映画の楽しみ満載、自主プロ時代の傑作短編。", "1922-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私の心はパパのもの", "私の心はパパのもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "交通事故で母を亡くした美々子は、15年ぶりに別れていた父と再会し一緒に暮らすことになった。典型的な中年のイメージである父・陽平との暮らしに疑問を感じ始めた時に陽平から突然、婚約者を紹介され……。日本テレビの２時間ドラマとして、大林宣彦が撮り上げた作品で、劇場でも公開された。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUNGLE CAT", "ジャングル・キャット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "ディズニー・プロ製作の「自然の冒険」シリーズの一編。広大なアマゾン上流の密林に撮影隊が入り、それまで知られなかったネコ族のジャガーをはじめとして、爬虫類、鳥類などの生態を紹介した記録映画。生存をかけた動物たちの死闘がすさまじい。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A ZED ＆ TWO NOUGHTS", "ZOO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "動物園で働く動物学者のオズワルドとオリヴァーの兄弟。最近、二人は同時に交通事故で妻を亡くした。自動車を運転していたアルバという女性も片脚を切断した。兄弟はこの事故をきっかけに生について考えるようになり、動物が腐敗していく過程や生物の進化に興味を抱く。やがて、兄弟とアルバの間に愛が生まれ、３人の生活が始まる。兄弟は次第に互いに見分けがつかないほど瓜二つとなっていき、またアルバは双子の男の子を出産する。だが、アルバは残った片脚の切断手術による衰弱で死に、兄弟も自らの腐敗の実験のため命を絶つ。シンメトリカルな映像構成の中で息づく、シュールでデカダンな世界。グリーナウェイがその名を決定的なものにした、初期の傑作。DVDは「ピーター・グリーナウェイ　DVD−BOX(1)」に収録。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gone Baby Gone", "ゴーン・ベイビー・ゴーン", "", "", "http://ecx.images-amazon.com/images/I/515epUmR-fL._SL210_.jpg", 114, "デニス・ルヘイン（「ミスティック・リバー」）原作のミステリー小説（「愛しき者はすべて去りゆく」角川書店刊）を映画化したベン・アフレックの監督デビュー作。ボストンの私立探偵パトリック＆アンジーが4歳の少女の誘拐事件を捜査する中で、職業的な危機と、個人的な危機的状況に陥っていく。パトリック＆アンジーにベンの弟、ケイシー・アフレックとミシェル・モナハン。共演はモーガン・フリーマン、エド・ハリスら。", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エデンの海", "エデンの海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "瀬戸内海に臨む女子高に、東京から青年教師・南条がやって来た。彼の明るい人柄はたちまち生徒たちの人気を集めるが、勝気な少女・巴だけは彼に反抗的。しかし、いつしか二人の間には、師弟愛以上の想いが芽ばえていく。西河克己監督は、後年、山口百恵の主演で同作をリメイクしている。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘密潜入捜査官ワイルドキャッツ in ストリップロワイヤル", "秘密潜入捜査官ワイルドキャッツ in ストリップロワイヤル", "", "", "http://image.eiga.k-img.com/images/movie/53729/315x210.jpg?4157", 88, "これまで数々の難事件を解決に導いてきた秘密潜入捜査官“ワイルドキャッツ”のハニーとバニー。清純派アイドル・かおりがイベント中に突然フルヌードになって引退に追い込まれたという怪事件を担当することになった2人は、アイドルになりすまして潜入捜査することに。そんな彼女たちの前に、暗黒結社ブラックホークスの女ボス・ベニタカが現われ……。人気グラビアアイドルの森下悠里＆かでなれおんがW主演を務めるセクシー・アクション。", "2008-06-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君こそ次の荒鷲だ", "君こそ次の荒鷲だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "少年飛行兵学校に入学した良平は、父の死の悲しみを乗り越えて、一人前の飛行兵になるべく猛訓練を続けていく。当時の兵学校勧誘のキャッチフレーズをそのままタイトルとし、若き少年たちに次期兵士としての夢や希望を抱かせようとする軍部の意図に呼応して製作された戦意昂揚映画。脚本は、小津安二郎作品で知られる野田高梧が担当している。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SEVEN VEIL", "第七のヴェール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "人間の潜在的心理に鋭く迫った異色作。自動車事故で手を火傷、絶望して自殺を図った女流ピアニスト。彼女の治療にあたった精神分析医の手により、その過去がベールをはぐように徐々によみがえっていく……。既成の音楽を巧みに利用した心象描写が見事。", "1945-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROUGE BAISER", "15才の恋／ルージュ・ベーゼ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1950年代パリ、社会運動にうち込み、恋を知って大人になっていく少女と、周囲の人々を描く。愛と革命の青春を、レトロ風景のなかに映し出した秀作ドラマ。主演のC・バランドレイが、ベルリン映画祭の女優賞に輝いている。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Blackout", "ブラックアウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "鬼才A・フェラーラが手掛けた、サスペンスフルな心理劇。麻薬と酒の日々を送る人気俳優マティは、恋人に去られ自暴自棄に。麻薬に溺れた彼は、ビデオ・アーティストに誘われるまま退廃的なセックスに身を任せ、そのまま気を失ってしまった。一年半後、当時の記憶がないまま静かに暮らすマティの胸に、衝撃的な記憶の断片がよみがえる……。欲望にもたれがちな人間の深層心理を鋭い切り口で浮き彫りに。狂言回し的な存在であるビデオ・アーティストをD・ホッパーが怪演している。", "1997-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("正義派", "正義派", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "志賀直哉原作の『正義派』と『清兵衛とひょうたん』をもとに、渋谷実が監督した意欲作。しがないバスの運転手・藤田は、ある日過って人をはねてしまう。バスに乗り合わせていた藤田の後輩は、藤田の過失だと証言する。渋谷実はドライな演出に徹し、志賀直哉の文体を再現した。ラストの清兵衛が川に投げ捨てるひょうたんが、プカプカと浮きながら流れていくシーンが印象的。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Quarter TWILIGHT FILE VI", "Quarter TWILIGHT FILE VI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "オムニバスシリーズ「TWILIGHT FILE」第6弾の1作。「初恋」の小嶺麗奈主演による人間ドラマ。都会での生活に追われ、かつて故郷で思い描いていた理想の生活をすっかり見失ってしまったOLの小夜。ある朝、ふと“何か”が足りないと感じた彼女は、いつもと違う電車に乗って故郷へと足を運ぶ。懐かしい風景の中を歩いているうちに、偶然にも学生時代の友人たちと再会した小夜は、さまざまな思いを抱えながら生きている友人たちとの交流を通して、大切な“何か”を思い出していく。", "2009-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE UNHEARD MUSIC", "アンハード・ミュージック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "一つのバンドの記録に留まらず、新しいアメリカン・ミュージックの発信源であるL.A.アンダーグラウンド・ミュージック・シーン全体を検証したドキュメンタリー。アンハード・ミュージック、つまり未だ聴かざる音楽が、音とイメージの洪水で紹介される異色作だ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REDLINE", "REDLINE", "", "", "http://image.eiga.k-img.com/images/movie/54769/315x210.jpg?4580", 102, "「鮫肌男と桃尻女」の石井克人監督による原作を、「アニマトリックス」の小池健監督が手描きにこだわって映画化したアニメ。木村拓哉、蒼井優、浅野忠信ら豪華俳優陣が声優として参加する。リーゼントに革ジャンを着こなすクールな主人公・JPは、実はシャイで純情な男。初恋の女性ソノシーを振り向かせるため、軍事国家「ロボワールド」で開催される何でもありのカー・レース「REDLINE」に出場を決める。幼なじみの天才メカニック、フリスビーがカスタムした最強の車でレースに参加するが、その車には八百長のためのワナが仕掛けられていた。", "2010-10-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nobody's Fool", "ノーバディーズ・フール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "雪に閉ざされた小さな町ノース・バスで土木作業員として働いているサリーは、家族とも別れて一人退屈な日々を送っていた。他人からも心を閉ざす彼に対して、大家であるミス・ベリルだけが温かいまなざしを向けている。ある日、偶然息子の家族と出会うものの、しっくりとはしないサリーだったが、孫のウィルと心を通わすことによって、サリーのかたくなな心に小さな変化が起こり始める……。R・ベントンがP・ニューマンを主人公に想定して脚本化、小さなコミュニティーでの人と人とのふれあいを優しく描き出している。ミス・ベリルを演じたJ・タンディにとって遺作となった作品でもある。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君といつまでも", "君といつまでも", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "山口静美は、ボンデージクラブの屋上で出会った男、加倉井に監禁される。加倉井は、静美を鎖で繋ぎ、その身体に剃刀を走らせ、バスタブに入れて、ていねいに洗う。そんな加倉井に、静美は“昔、父親に犯された私を慰撫してくれた兄が知ったら許さないだろう”と言いつつも抵抗できない。静美の夢をモチーフに、人間の欲望、孤独、愛を描くサイコティック・エロス・ファンタジー。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Religulous", "レリジュラス　世界宗教おちょくりツアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Under the Hawthorn Tree", "サンザシの樹の下で", "", "", "http://image.eiga.k-img.com/images/movie/56079/315x210.jpg?2682", 114, "都会育ちの女子高生ジンチュウは、文化大革命の再教育のために送られた農村で青年スンに出会う。ジンチュウは、エリートでありながらも明るく誠実なスンに恋心を抱き、やがて2人はひかれあうが、身分違いの2人の愛には過酷な試練が待ち受けていた。文化大革命下の中国を舞台に、「初恋のきた道」のチャン・イーモウ監督が描く純愛ストーリー。主演はイーモウ監督が新たに見出した新鋭チョウ・ドンユィ。", "2011-07-09", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("流転の海", "流転の海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "宮本輝が全５部作を想定している大河小説の第１部を映画化。終戦後の大阪、廃墟と闇市の世界で、再起を誓う型破りの商人・松坂熊吉。だが、58歳にして初めての子供ができ、商売一途の男にうれしい苦労の種がふえた。裏切り、ヤクザとの渡り合いなどをしながら熊吉は妻と子を守っていくのだが……。原作者が熊吉は森繁をイメージして書いたというだけあって、これは彼のはまり役。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さすらいの賭博師", "さすらいの賭博師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "小林旭主演で大ヒットし、多くの続編を生み出した人気シリーズの第１作。ダイスを振らせれば右に出る者はいないと言われるほどの黄金の腕を持つ男・氷室浩次は、神戸の高級アパートで恋人・信子と楽しい生活を送っていたが、ある夜、氷室の兄・健一が血だるまで部屋に転がり込んできた。暴力団柏田組のしわざだと知るや、氷室は単身で殴り込みをかける。しかし、氷室がアパートに戻ってみると、兄は息絶え、恋人の信子までも柏田組の者に殺されていた。氷室は復讐の鬼と化し……。シリーズを通して繰り返されるダイスのトリックが楽しく、スピード感あふれる作品である。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("R246 STORY", "R246 STORY", "", "", "http://image.eiga.k-img.com/images/movie/53589/315x210.jpg?2153", 0, "原宿・渋谷といった繁華街を沿道に持つ\"国道246号線\"を題材に、ジャパニーズ・カルチャーを牽引する6人の著名人がメガホンを取ったオムニバス・ムービー。浅野忠信によるSF、中村獅童による時代劇、ユースケ・サンタマリアによるラブストーリー、音楽ユニット・m-floのVERBALによるヒップホップのドキュメンタリーなど、様々なジャンルで活躍する彼らがそれぞれの個性をスクリーンに昇華させた。", "2008-08-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("土徳・焼跡地に生かされて", "土徳・焼跡地に生かされて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "広島の浄土真宗のお寺、真光寺に生まれた映像作家、青原さとしのプライベートなドキュメンタリー。お寺の古い因習にこだわり続けて生きてきた父親が病に倒れたことをきっかけに、ビデオカメラを通して郷土の探索にとりかかった青原監督。11年分の記録映像に基づき、監督の父親の壮絶な原爆体験、戦前から戦後にかけての広島の歴史や文化などが多角的に映し出されていく。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WRITTEN FACE", "書かれた顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "歌舞伎の女形・坂東玉三郎を主演に、映像の魔術師D・シュミットがドキュメンタリーとフィクションの融合という得意のスタイルで描いた異色作。玉三郎が演じる３つの演目『大蛇』『積恋雪関扉』『鷺娘』のほか、名女優、杉村春子が女形や玉三郎について語る場面や、現代舞踏の第一人者・大野一雄との対比、地唄舞の武村はんや芸者の蔦清小松朝じらの映像も収められている。ほかに、玉三郎演じる年増の芸者を二人の若者が奪い合うというフィクションのパートなど、貴重な事実の記録であると同時にファンタジックな現代の童話ともいえる、夢と現実が混然となった幻想的な作品に仕上がっている。", "1995-01-01", "26,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRIN BLANC", "白い馬", "", "", "http://image.eiga.k-img.com/images/movie/45649/315x210.jpg?4070", 40, "「赤い風船」などで漸新なアイデアによる撮影技術を導入、そこに詩情豊かな童話的世界を創造したラモリス監督の初期の代表作。フランスの湿原に棲む一頭の白い馬と、それを馬飼いの一団から助けようとする少年の友情をみずみずしい映像で綴った作品。ジャン・ビゴ賞受賞。", "1952-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本敗れず", "日本敗れず", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "第二次世界大戦末期。沖縄戦での敗戦、米・英・ソのポツダム会談、そして広島・長崎への原爆投下と敗戦の色合いが濃くなった日本で強引に本土決戦を訴える陸相の川浪と天皇の玉音版を亡きものにしようとする将校たちの姿を描く敗戦裏面史。岡本喜八の「日本のいちばん長い日」同様、動乱の人間たちの姿を描いている。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("All I Want for Christmas", "クリスマスに万歳！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "クリスマスを機会に、離婚した両親を再び結びつけようと奮闘する兄妹を描く、ハートウォーミングな作品。L・ニールセンが、デパートの客寄せサンタクロースとなって特別出演。また、L・バコールが兄妹の祖母役で登場、パーティー・シーンで歌までも聞かせてくれる。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lebanon", "レバノン", "", "", "http://image.eiga.k-img.com/images/movie/55779/315x210.jpg?2309", 90, "1982年に起きたレバノン戦争を題材に戦争の恐怖と人間の狂気を描き、2009年ベネチア国際映画祭金獅子賞を受賞した戦争ドラマ。自らイスラエル兵としてレバノン戦争に従軍したサミュエル・マオス監督の実体験をもとに、レバノンに侵攻したイスラエル軍の戦車に乗る4人の若き兵士たちが目撃する生々しい戦場の実態を、閉塞した戦車内の映像とスコープ越しに見える外部の光景のみでスリリングに描き出す。", "2010-12-11", "46,2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUAI DES ORFE´VES", "犯罪河岸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「恐怖の報酬」「悪魔のような女」などサスペンス映画の巨匠として知られるH・G・クルーゾー監督が、S・A・ステーマンの推理小説『正当防衛』を映画化した作品。戦後間もない1946年のパリで、夫モーリスのピアノ伴奏で歌うジェニーは、次第に人気者になる。ジェニーは映画会社を経営する財閥のブリニョンからモデルの仕事をもらい、喜んで彼の自宅に行く。心配したモーリスが家に行くと、ブリニョンは死んでいた。その頃、ジェニーは、旧友で写真師のドラに犯されかかったので酒瓶で殴ったとすべてを打ち明けていた。ドラは証拠隠滅をし、嘘をつくようにジェニーに言うが、そのことがかえってモーリスを追い詰めていく。事件はパリ警視庁の敏腕警部アントワーヌが担当することになり、彼は謎を一つずつ分析して、事実を明らかにしていくのだった。最後に大ドンデン返しがあるなど、意外性の演出で見せる力は、さすがH・G・クルーゾーである。", "1947-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE JUICE", "ブルー・ジュース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "英国コーンウォール地方を舞台に、サーファーたちの友情と愛をコメディ・タッチで描いた青春ドラマ。30歳を目前に控えたサーファーJCの元に、ロンドンから幼なじみのディーン、テリー、ジョシュの３人がスタッグナイト(結婚を前にし、男友だちだけが開くパーティー)のサーフィン・トリップに訪れた。恋人クロエから結婚を迫られ、彼女との溝が深まるJCは、その悩みを打ち消すかのように友人たちとサーフィンに興じる。ある時JCは、ディーンから“墓場”と呼ばれる危険なサーフポイントに誘われるが、恋人の気持ちを取り戻すために断る。だが、ディーンが一人危険な波に立ち向かったことを知り……。DVDタイトルは「キャサリン・ゼタ＝ジョーンズのブルー・ジュース」。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢遊夏威夷／HOLIDAY DREAMING", "夢遊ハワイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ノスタルジックな視線がまぶしい青春映画。“青春”から連想させるはじけた若々しさよりも、どこか不安を秘めた佇まいが心地よい余韻をもたらす。「僕の恋、彼の秘密」のトニー・ヤンをはじめ、台湾注目の若手俳優が自然体の演技を見せている。軍隊を脱走した同僚を探すという命令を受けたアーチョウとシャオグェ。台北で休暇を満喫していた二人は、精神を病んだアーチョウの幼なじみシンシンを連れ、海へと向かう。", "2006-09-02", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ご縁玉　パリから大分へ", "ご縁玉　パリから大分へ", "", "", "http://image.eiga.k-img.com/images/movie/54269/315x210.jpg?3832", 72, "大分で“いのちの授業”を続けてきた元養護教論の山田泉と、ベトナム孤児としてフランス人の養父母に育てられたパリ在住のチェリスト、エリック＝マリア・クテュリエの交流を描いたドキュメンタリー。エリック＝マリアはパリを旅行中だった乳ガンを患う山田に出会い、その時に「ご縁がありますように」と手渡された5円玉に引き寄せられるように山田の住む大分へと旅立つ。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ごろつき犬", "ごろつき犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "白浜でキャデラックを乗り回す女とともに正月を迎えた大介は、その女からハジキに関係のある仕事を依頼される。喜々として大阪のドヤ街に戻り、さっそく仕事に取りかかる大介は、しかしすぐに軟禁されてしまう。すべては大金を狙う女が仕組んだ罠だったのだ。大介の怒りの拳銃が火を噴く。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明日なき街角", "明日なき街角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "北方謙三の同名小説をベテラン、若松孝二が映画化したハードボイルド・アクション。昼は工員として働き、夜はモルヒネを売りさばく哲二の仲間、佐々木が取引に失敗し、殺される。すべてに折り合いをつけ、クールに生きてきた哲二が、佐々木を罠にはめた相手を知り、次第に復讐心に駆られていくさまを描く。主演は「キッズ・リターン」の金子賢。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋のたそがれ", "恋のたそがれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "マイナーなレディースコミック誌を出している雑誌社に勤める25歳の編集者・加津山と現在の恋人・早川京子、そして加津山が担当となっているマンガ家の妹・渡辺エミとの三角関係を淡々と描いたインディーズ作品。クライマックスらしいものはまったくなく、ただ日常会話のやりとりによって、青春の、恋の時間が移ろうような映像となって切り取られていく。監督の山口貴義は８ミリによる学生映画出身で、これが長編劇場用映画第１作となる。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Secret World of Arrietty（英題）", "借りぐらしのアリエッティ", "", "", "http://image.eiga.k-img.com/images/movie/55169/315x210.jpg?4295", 94, "イギリスの女流作家メアリー・ノートンの児童文学「床下の小人たち」を、スタジオジブリが映画化。監督は「千と千尋の神隠し」「ハウルの動く城」「崖の上のポニョ」で原画、「ゲド戦記」で作画監督補を務めた米林宏昌。企画・脚本に宮崎駿。身長10センチの小さなアリエッティ一家は、人間が住む家の床下で、さまざまな生活品をこっそり借りて暮らしていた。彼らの掟は「決して人間に見られてはいけない」ということ。しかし、アリエッティはその家に引越してきた少年・翔に自分の姿を見られてしまう。", "2010-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春の底", "春の底", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "新鋭・松村真吾による人間ドラマ。脚本を一切用意せず、出演者に設定だけを伝えて即興演技をさせるという大胆な手法を採用し、その即興性により引き出された生々しい感情のぶつかり合いが強烈なインパクトを残す。恋人の妊娠が発覚した西本。幼い頃、父に失踪された彼は、自分が父親になる自信がなく、とりあえず中絶費用を稼ぐことにするが……。", "2005-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("元祖パチンコ物語 温泉珍道中", "元祖パチンコ物語 温泉珍道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ビデオ化された際に大ヒットした「パチンコ物語」。以後、この作品はオリジナル・ビデオとしてシリーズ化され、８作目にあたる本作から劇場で公開された。パチンコ・チェーンの経営を息子に委ねて悠々自適の生活を送るオーナー・国松の浮気旅行を描いたもの。１作目に続いて、財津一郎が笑わせてくれる。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎夕焼け小焼け", "男はつらいよ・寅次郎夕焼け小焼け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "寅さんが、飲み屋からヘンな老人を“とらや”に連れてきた。ところがこの老人、日本画の大先生だった。寅さんと老先生、意気投合して兵庫へ旅に出るが、地元の美人芸者・ぼたんに寅さんは一目惚れ。だが彼女は大レストランの社長にだまされて、200万円を巻き上げられていたのだった。寅さんは老先生に“彼女のために絵を描いてくれ”と頼むが“金のために絵は描けない”と断わられ、悪態をつく……。キネマ旬報ベストテンの２位に入り、太地も助演女優賞を獲得した。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUSH", "ラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1975年、テキサス州の田舎町キャタレー。新米婦人警官のクリスは、パートナーのレイシーとともに、麻薬のおとり捜査に挑む。しかし、麻薬中毒者とかかわり合ううちに、次第に二人とも麻薬に溺れていく……。実話小説をもとに、ショッキングなストーリーが展開。音楽にはE・クラプトンを起用、劇中曲の『ティアーズ・イン・ヘブン』はビルボード誌の第１位を飾るなど大ヒットした。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT WAS THEN，THIS IS NOW", "BAD ～傷だらけの疾走～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "『アウトサイダー』で青春文学の旗手となったS・E・ヒントンのベストセラーに、E・エステヴェスが惚れ込み、自ら脚本を執筆し主演した意欲作。兄弟のように育った親友同士が、ドラッグの常用によって崩壊していくという、アメリカの青春を映したドラマ。「リバー・ランズ・スルー・イット」などで知られるC・シェーファーは本作でデビュー。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地上最強のカラテ PART2", "地上最強のカラテ PART2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "一撃必殺をモットーとする極真空手の世界をとらえたドキュメンタリーの第２作。極真会館会長、大山倍達と門下生たちの空手に打ちこむ日々を、海外支部の特訓風景を交えて紹介。五人掛け、空中ブロック割りなどの荒技や、さらに巨大グマとのデスマッチなど、目を見張るカラテ・アクションを、様々な角度の映像で迫力たっぷりに捉える。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　海神別荘", "シネマ歌舞伎　海神別荘", "", "", "http://image.eiga.k-img.com/images/movie/57389/315x210.jpg?4427", 0, "歌舞伎の舞台公演を映画館のスクリーンでデジタル上映する「シネマ歌舞伎」シリーズ第16弾。1914年に発表された泉鏡花による戯曲が原作で、海底にある宮殿「琅かん殿（ろうかんでん）」の公子と地上の美女の恋を描く。ひとりの美女が海中の宮殿・琅かん殿（ろうかんでん）の公子に輿入れし、彼女の美しさに惚れた公子は優しくもてなす。しかし、美女は地上への未練を捨てきれず……。2009年7月に歌舞伎座で上演された舞台をデジタルシネマ化。", "2012-02-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すばらしき臨終", "すばらしき臨終", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "葬儀屋の営業マンの実態を捉えたナンセンス・コメディ。突然、会社をリストラされた村上は月収 100万円という宣伝文句に釣られ、葬儀屋に就職。病院にとり入って死体の葬儀権を獲得する営業の仕事を任される。優柔不断な社長、会社の金を横領する先輩社員、変態の若手社員らに囲まれる村上はろくでもない日々を送ることに。それでも美人女医の青山といい関係になりそうだったが、葬儀屋に勤めていることが妻、歌子にばれて大ゲンカ。家庭崩壊の危機が迫る……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ガンダムUC episode5「黒いユニコーン」", "機動戦士ガンダムUC episode5「黒いユニコーン」", "", "", "http://image.eiga.k-img.com/images/movie/57519/315x210.jpg?4398", 0, "人気作家・福井晴敏による小説「機動戦士ガンダムUC」を全6話でアニメーション化する第5話。オーストラリア・トリントンでの攻防の最中に現れた黒いMS「バンシィ」の急襲を受けたバナージ・リンクスは、身柄を拘束されてしまう。しかし、バナージに歴代のガンダムパイロットたちと同じ可能性を見出したロンド・ベル隊司令のブライト・ノアが助けの手を差し伸べる。一方、連邦議会の重鎮ローナン・マーセナスからビスト財団のマーサ・カーバインへと身柄を引き渡されたミネバ・ザビは、バナージに対する切り札として利用されることになってしまう。", "2012-05-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAN TURNER，HOLLYWOOD DETECTIVEIN THE RAVEN RED KISS−OFF", "ダン・ターナー 探偵ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "パルプ・マガジンから生まれたハードボイルド・ヒーロー、ダン・ターナーがスクリーンに登場。物語の舞台は1949年のハリウッド。女優のヴェラ・デュヴァルの行動の調査を依頼された私立探偵ダン。だが、調査中突然起きた殺人事件に巻き込まれる。警察はダンを犯人だと誤解し追跡を開始した……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A DANGEROUS MAN LAWRENNCE AFTER ARABIA", "ロレンス1918", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "映画「アラビアのロレンス」で描かれたT・E・ロレンスの、第一次世界大戦後の姿を描く。アラブの民族に英雄とうたわれながらも、無冠の帝王とも呼ばれるロレンスは、戦争終了後の1918年、アラブの独立を目的にパリの講和会議へと向う。理想を掲げ、夢を追うロレンスだったが……。彼の人間性とその裏側をじっくりと見つめた重厚な人間ドラマになっている。TVフィーチャー作品。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽の傷", "太陽の傷", "", "", "http://image.eiga.k-img.com/images/movie/33649/315x210.jpg?1821", 117, "三池崇史監督が哀川翔を主演に迎えて放つ、衝撃の社会派ドラマ。近年増え続ける少年犯罪や少年法が抱える問題をテーマに、凶悪な少年犯罪により愛する娘を失った男の苦悩を描く。少年グループによる浮浪者への暴行を止めた片山は、主犯格の神木の恨みを買い、愛娘を殺されてしまう。神木はすぐに警察に連行されるが、マスコミは事件の原因が片山にあると騒ぎ立てる。追いつめられた片山は、仮釈放された神木に会おうとするが……。", "2006-09-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Long Pants", "初恋ハリイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "白塗りの無表情なキャラクターで人気者となったハリー・ラングドン主演のコメディ。後の名匠フランク・キャプラが、「当たりっ子ハリー」に続きメガホンをとる。ようやく大人になり、長ズボンを履くことが許されたハリー。自分の姿を町の人々に見せようと自転車に乗って出かけた彼は、町で偶然見かけたビービーというならず者の女性にひと目惚れしてしまう。彼は婚約者プリシラの存在も忘れ、ビービーを追って都へ行くのだが……。", "1927-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FRIENDS OF EDDIE COYLE", "エディ・コイルの友人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "アクション派のP・イエーツ監督が、暗黒街に生きる男たちの姿をリアルに描いたハードボイルドな犯罪アクション。一見、平穏な町。だがその裏側では暗黒街の男たちと司法当局との間で、汚い取引が行われていた。初老の運び屋エディ・コイルは、懲役刑の判決を待つ間に、最後の大仕事を画策するが……。エディ・コイルを演じるR・ミッチャムはじめ、クセ者ぞろいのキャストが、作品にリアルな陰を加えている。D・グルーシンによる音楽も秀逸。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Countdown", "宇宙大征服", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "　ソ連は有人宇宙船を月に向けて打ち上げることに成功。アメリカはこれに対抗し、計画を早め、旧式のマーキュリー宇宙船で月への有人飛行を決行しようとする。そんな中、飛行士が空軍のチャイズ大佐から民間人のリーに代わることに。やがて打ち上げの時がやってくるが……。鬼才ロバート・アルトマン監督のリアルなSF冒険ドラマ。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cobra", "コブラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "　ナイトスラッシャーと呼ばれる狂信的な虐殺集団による殺人事件が続発。その凶行を偶然目撃したモデルのイングリッドは命を狙われるハメに。そんな彼女の警護任務に、ロス市警の刑事コブレッティが就く。サブマシンガンを携行し、カスタムメイドされた改造車を乗り回すその男はコブラとあだ名される凄腕刑事だった。迫り来る敵の群れに、コブラの銃弾が炸裂する！　スタローンが人気絶頂期に放った痛快刑事アクション。彼と当時の夫人ブリジット・ニールセンが共演。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いつか来るさよなら", "いつか来るさよなら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "弘は父の病院の看護婦・由里子をひそかに想っていた。が、運命のいたずらか、由里子は弘の父と結婚してしまう。弘は酒とバクチで生活が荒れ、由里子を呪う。そこに由里子との過去の関係から彼女をゆすろうとする悪徳医師が現れて……。笹沢左保の小説『廃墟の周囲』を、舟木一夫を主役にすえて映画化。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("午後の遺言状", "午後の遺言状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "杉村春子と乙羽信子が共演し、劇映画としては二人の遺作となった味わい深い人間ドラマ。毎夏、蓼科高原に避暑にやって来る老女優、蓉子と彼女の世話をしている農婦の豊子。豊子の娘、あけみが実は蓉子の亡き夫の子だという告白から、互いの心中が変化していく様を描く。女性の老いがテーマだが、それぞれの持ち味を遺憾なく発揮して独特の芝居空間を現出させた二大女優の存在感が、なにより素晴らしい。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARD HUNTED", "グラマラス・ハンターズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "元プレイメイトたちが、女スパイとなって活躍する、露出度満点のエロティック・アクション。表向きは豪華クルーザーで優雅な生活を送る若き富豪ケインは、裏世界では中東の某独裁者との取引を行う武器商人だった。彼の陰謀を察知したアメリカ当局は、女情報部員たちを送り込んだ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イルカに逢える日", "イルカに逢える日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "島発見400年、小笠原返還25年を記念して作られた、人間とイルカとの触れ合いを絡めたラブ・ストーリー。クリスマスも間近に迫った頃、小笠原では“エコロジー・コンサート”が開かれることになった。そのイベントの参加者、桂は溺れそうなところをイルカに助けられる。彼は島民の青年、達哉に心ひかれていき……。イルカが、桂と達哉との愛の縁結びとして活躍。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE NIGHTS", "ホワイトナイツ／白夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "東京に向かうジャンボジェットに、ソ連からアメリカに亡命したバレエ・ダンサーが乗っていた。突然、機体が変調をきたし、ソ連の空軍基地に不時着。再び自国に戻ってしまった彼を待っていたのは過酷な運命だった……。バリシニコフの華麗なバレエがシリアスなドラマに花を添える。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GRAPES OF WRATH", "怒りの葡萄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "文豪J・スタインベックのピュリッツァー賞受賞小説の映画化。仮出所で刑務所を出て４年ぶりに故郷のオクラホマの農場に帰ってきたトム・ジョード。そこで彼が見たのは、荒れ果てた大地と飢えに苦しむ農民たちの姿だった。久しぶりに再会したトムの家族も同じ有様だったが、母親のマアのたくましさのおかげで貧しいながらも元気でいた。だが、土地はすでに人手にわたっていた。そこでジョード一家はオンボロ車でカリフォルニアに向かう。だが、希望の土地カリフォルニアで彼らを待っていたのは……。凶作と資本主義の歪みの中でたくましく生きるアメリカ農民の姿を描いた社会派ドラマの名作。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bride Wars", "ブライダル・ウォーズ", "", "", "http://ecx.images-amazon.com/images/I/51lulxsOfjL._SL210_.jpg", 90, "結婚式をめぐって繰りひろげられる女同士の過激な争いを、ケイト・ハドソン＆アン・ハサウェイの人気女優競演で描いたコメディ。幼い頃からいつも一緒に過ごしてきた親友同士のリブとエマ。偶然にも同じ日に婚約した2人は同じホテルでの結婚式を計画するが、ホテル側の手違いで同じ日の同じ時間にダブルブッキングされてしまう。お互い譲れないリブとエマは、ライバル心をむきだしにして壮絶なバトルを開始する。", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道罷り通る", "極道罷り通る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "代紋を持たない島村清吉は、子分を連れ、地方のつぶれそうな一家の代紋を買収すべく旅に出た。鷲羽市では、コンビナート建設をめぐる地元の岩橋組と漁師たちが争いのさなかで、清吉は漁師に味方して悪どい岩橋組を相手に大暴れする。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT CLUB", "ナイトクラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "正気を失った男が、夢の中で見た美しい女と現実に出会い、やがて彼女との生活が、すべてとなっていく姿を描く、サスペンス・タッチのドラマ。若い夫婦が、ロスにある工場の廃墟に移り住み、そこで念願のナイトクラブを開店させようとする。だが、夫はギャングの抗争に巻き込まれ、次第に正気を失っていく……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Who's That Knocking at My Door", "ドアをノックするのは誰？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "M・スコセッシ監督の最初の長編劇映画。またH・カイテルが映画初出演にして主人公を演じている。J.R.は、ニューヨークのリトル・イタリーに住むイタリア系青年。フェリーの上で知り合った女性に強く惹かれながらも自分の階級に劣等感を持ち、なかなか最後の告白ができないでいた。だが彼女にも人には話せない過去があって……。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハミングライフ", "ハミングライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "人気ロックバンド「GOING UNDER GROUND」の同名曲をモチーフにベストセラー作家・中村航が書き下ろした短編小説を映画化。上京したばかりの藍は、慣れない都会での生活に気分が沈みぎみ。そんなある日、藍は不思議な犬に導かれて見つけた木のウロに、1通の手紙を発見する。それは、深夜の託児所で働く智宏が書いたものだった。手紙に元気づけられた藍が返事をウロに入れると、今度は智宏から返事が届き……。", "2007-01-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIED DRAGON FISH　THOMAS EARWING'S AROWANA", "FRIED DRAGON FISH　THOMAS EARWING'S AROWANA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "「Love Letter」「花とアリス」の岩井俊二監督がフジテレビの深夜ドラマ用に制作、のちに同監督作品「PicNic」と共に劇場公開された。探偵事務所に派遣されたプーは、トーマス・アーウィング氏の魚を探すという探偵の仕事に関わっていくうちに不思議な少年ナツロウと出会う。プーを芳本美代子、ナツロウを芳本美代子が演じる。主題歌はCharaの「Break These Chain」。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AMITYVILLE HORROR", "悪魔の棲む家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "1974年、アメリカはロングアイランドのアミティヴィル。実際に起こった出来事をルポした大ベストセラーをもとに、悪霊に取り憑かれた家の中で次々と起こる超常現象と、住みついた家族に襲いかかる悪霊の恐怖を描く、ノンフィクション・ホラー・ムービー。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("懐しのブルース", "懐しのブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "“古い日記の頁には、涙のあともそのままに——”主題歌『懐しのブルース』のメロディーに乗って物語は展開する。没落華族の娘が、病気の妹や生活のためにキャバレーの歌手となり、ある男と恋愛、ドライブに食事にと楽しい時を過ごす。しかし彼には療養中の妻がいることが判る。“帰らぬ夢の懐しさ”と思い出を胸に秘め、涙をこらえ歌うのであった。主題歌『懐しのブルース』『燃える瞳』は発売と同時に大ヒット。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THUMBELINA", "サンベリーナ おやゆび姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "不朽のアンデルセン童話のアニメ化作品。親指ほどの大きさしかない少女サンベリーナは、自分と同じ大きさの人間がいないことから孤独な日々を送っていた。ある日、同じくらいの大きさの男の子コーネリアスが彼女の前に現れる。彼女と違うのは、彼は妖精の王子で背中に羽が生えていることだけ。二人は会った瞬間に恋に落ちるのだった……。ディズニー方式の１枚１枚手描きの手法に加え、コンピュータ・グラフィックスなどの技術を用いた、きめの細かい映像が美しい。B・マニロウが音楽を担当、挿入歌も歌っている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("胸さわぎの放課後", "胸さわぎの放課後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "同級生に比べ、かなり奥手の部類に属する高校１年の女子バレーボール部員と、先輩のバスケットボール部員との、幼稚だが、さわやかで明るい恋模様を描いた学園ラブロマンス。自分も好きなのに、友人に頼まれてラブレターを渡すくだりなど、実に現実的。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AKIRA", "AKIRA", "", "", "http://ecx.images-amazon.com/images/I/51YlSFLyDNL._SL210_.jpg", 124, "第三次世界大戦から31年後の2019年。舞台は東京湾上に建設された新都市・ネオ東京。職業訓練学校生の鉄雄は、逃走中の超能力開発実験体の子供と遭遇したことから、ともに軍の研究所へ運ばれる。一方反政府ゲリラは、かつて東京を壊滅させたあと、カプセルの中で眠り続けているとされる“アキラ”を覚醒させようとしていた。ゲリラの一員の少女と知り合った鉄雄の兄貴分の金田は、鉄雄救出のため研究所に潜入する。しかし超能力に目覚めた鉄雄と対決することになり……。大友克洋が、その長編大作を自らの指揮でアニメ化、壮大なスケールのサイバー・パンクSFに仕立て上げている。ほとんど実写を思わせるリアルな作画は、圧巻の一語。特に未来都市の崩壊を細密に描いたクライマックスは、これまでのアニメ映画にはない興奮に満ちている。民族音楽をベースにした芸能山城組の音楽も、映像とよく溶け合い、音と映像による近未来の黙示録が誕生。海外の映画ファンにも深く愛されるジャパニメーションの名作。", "1988-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SECRET SOCIETY", "恋はハッケヨイ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "相撲に生きがいを見いだした女性の成長を描く奮闘劇。ヨークシャー地方の田舎町。夫の失業を機に缶詰工場で働き始めたデイジーが、秘密クラブのメンバーに迎え入れられる。それは相撲に魅せられた巨体の女たちの集団。相撲の稽古に励むデイジーは、太っているというコンプレックスを克服し、輝きを取り戻し始める。ヒロインが初めて見る不思議なスポーツにのめり込み、夫を支えるため、自信をつけるために健気に努力する姿がほほ笑ましい。相撲を神秘的な儀式のように捉えた描写もユニーク。", "2000-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("功夫皇帝／THE　KUNG　FU　EMPEROR", "カンフーエンペラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「男たちの挽歌」で人気を呼んだティ・ロン主演のカンフーもの。清朝を舞台に四代皇帝康煕（こうき）亡きあとの王位継承をめぐる争いを描く。王子の一人、禎は武術の達人だが野心を持たない男だった。しかし、敵方の陰謀を知り、ついに立ち上がる。", "1981-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西蔵之王／THE　EMPEROR’S　STORY", "ザ・エンペラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "７世紀のチベット王朝をめぐる陰謀と愛の大河ロマン。西暦633年、近隣の部族を征圧したチベット皇帝が毒殺され、息子・松賛が23代皇帝に即位した。権力を握りそこねた側近は娘を使って皇帝を骨抜きにしようとたくらむが、娘は本気で皇帝を愛してしまう。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIRDCAGE", "バードケージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ショークラブ“バードゲージ”のオーナー兼演出家のアーマンドと、店のトップスターであるアルバートの二人は、私生活の上でも最高のパートナーだった。ある日、アルバートの息子のバルが超保守派の上院議員キーリーの娘と結婚することになるが、性別を超えた夫婦の関係を、四角四面のキーリーに理解してもらえるわけない。困っているうちに、いよいよ両家ご対面の時がやってきた！　常識的ではないが本物の愛で結ばれたカップルの喜劇を、R・ウィリアムズとN・レインが洗練された笑いに変えて表現している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MULTIPLE MANIACS", "マルチプル・マニアックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ピンク・フラミンゴ」のウォーターズ監督のバッドテイスト・ムービー。夫とその愛人を殺害し、死体の内臓をむさぼり喰うディヴァイン。その後、彼女は発狂し、ついには15フィートの巨大エビにレイプされる。題名はスプラッタ映画「2000人の狂人」のもじり。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BRADY BUNCH MOVIE", "ゆかいなブレディー家　我が家がイチバン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1969年から10年にわたって放映されたTVドラマ『ゆかいなブレディー家』を、1990年代風にリメイクしたホーム・コメディ。３人の娘を持つ女性と３人の息子を抱える男性が結婚。しっかり者のメイドも加わって大家族となった賑やかなブレディー家に、税金未納による家の競売問題が持ち上がる。折しも、不動産業者が一家の屋敷を狙って動き出し……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHAT　A　GIRL　WANTS", "ロイヤル・セブンティーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ニューヨークで母親と二人暮らしの女の子が、一度も対面したことのない父親と会うため単身ロンドンへ。そんな庶民派ヒロインがイギリス社交界に新風を吹き込んでいく姿を、躍動感たっぷりに綴るガールズ・ムービー。本国アメリカでは子供向けTV番組のホストをこなすなど、すでに大人気を得ているキュートな主演女優アマンダ・バインズが好演を見せた。17歳のアメリカ人少女ダフネが、まだ見ぬ父親ヘンリーと会うためロンドンへ旅立つ。選挙を控えた政治家であるヘンリーと対面したダフネは、イギリス社交界にデビュー。しかしトラブルを起こしてパパを困らせてしまい……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stuart Little", "スチュアート・リトル", "", "", "http://image.eiga.k-img.com/images/movie/1819/315x210.jpg?1605", 84, "リトル家はやさしいパパとママ、一人息子のジョージ、猫のスノーベルの幸せな4人家族。ある日、弟を欲しがるジョージのために養護施設に訪れたリトル夫妻は、人間の言葉を話すネズミ、スチュアートに出逢い、気に入って養子にもらい受ける。家族を持つという夢が叶って大喜びのスチュアートだが、人間の弟を欲しがっていたジョージは不満顔。スノーベルも自分がネズミのペットになるのが気にくわないのだった……。", "2000-07-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Crazies", "クレイジーズ", "", "", "http://image.eiga.k-img.com/images/movie/55359/315x210.jpg?2358", 101, "「ゾンビ」の巨匠ジョージ・A・ロメロ監督が1973年に手掛けた同名ホラーのリメイク。アメリカ中西部の小さな田舎町で、普通の人々が突然発狂し、人間を襲い始める。町の保安官のデビッドと身重の妻ジュディは生き残るために町からの脱出を図るが……。出演はデビッドに「ダイ・ハード4.0」「ヒットマン」のティモシー・オリファント、ジュディに「サロゲート」ラダ・ミッチェル。監督は「サハラ／死の砂漠を脱出せよ」のブレック・アイズナー。", "2010-11-13", "1,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MURDEROUS AFFAIR", "魔性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "小学校教師ポールは、妻に隠れて元同僚のキャロラインという愛人を囲っていた。キャロラインはグラマラスなボディーと小悪魔のような雰囲気を持つ魅惑的な女。彼女との激しい情事を終え、何食わぬ顔で帰宅したポールを待っていたのは、数発の銃弾を受け横たわる妻の亡骸だった。キャロラインの証言でポールのアリバイが成立するが……。1989年に実際に起きた事件を映画化したサスペンス。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APARAJITO", "大河のうた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "前作「大地のうた」を受けた続編。新天地ベナレスでのオプー一家の苦しい生活と、念願の大学に受かり憧れのカルカッタへと旅立つオプーの青春が綴られる。父母の死にあいながら苦学に懸けるオプーに、レイ監督は未来を仮託する。河のほとりの生活描写が秀逸。ヴェネチア映画祭で国際批評家連盟賞もあわせて受賞している。", "1956-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEW AGE", "ニュー・エイジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "タレント・エージェントのピーターと、グラフィック・デザイナーのキャサリンは、ビバリーヒルズの豪邸に住むカップル。愛情は冷えきっていたが、プライベートに干渉しないことで二人の関係は微妙なバランスを保っていた。しかし、不況による経済的な不安がもたげると、破局寸前に。起死回生を狙い、二人で始めた高級ブティックも失敗。キャサリンは虚飾に満ちた生活に決着をつけるため、ピーターとの心中を計画する。男と女、それぞれのプライドが崩壊する様を、現代風俗とともに活写したシニカル・コメディ。ダナ・キャラン、ロメオ・ジリらによるJ・デイヴィスの衣装も見どころ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春とはなんだ", "青春とはなんだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "主演の石原裕次郎のキャラクターがこれまでのものとはうって変わり、きわめて“指導者”的色彩の濃いものとなっている。そうした意味でターニング・ポイントとなった作品であろう。型破りの授業で生徒の信頼を得た高校教師が、自分が指導をしているラグビー部に、同校の不良グループを勧誘し入部させ、“更生”させる、というお話。翌年東宝は返答映画として「これが青春だ」を作った。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALRIMENTI CI ARRABBIAMO", "サンド・バギー／ドカンと３発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "遊園地をつぶし、高層ビルを建設しようとする悪徳ボスに、サンド・バギーを壊されたキッドとベスのコンビが闘いを挑み、プランを叩きつぶしてしまう、ドタバタ・アクション・コメディ。ヒル＆スペンサーのコンビで連作されている作品の１本。泥臭さが身上。", "1974-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE ARREST", "パーフェクト・ファミリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "J・L・カーティス、K・ポラック共演によるファミリー・コメディ。ジェネットとネッドの夫婦は行き違いも多く、別居を決意するが、14歳の息子グローヴァーと10歳の娘ステイシーは大反対。二人を元通り仲良くさせようと地下室に誘い込み、反省するまで監禁することにする。それを知ったグローヴァーの友人たちも自分の親を一緒に閉じ込めてしまう。こうして４組の親VS子供の戦いが始まった……。親を改心させようとする子供と、なんとか脱出を試みる親が次第にお互いを理解し合うまでを、爆笑の攻防戦を交えながら軽快なテンポで見せていく。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hunger Games", "ハンガー・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/57839/315x210.jpg?3545", 0, "全米で人気のスーザン・コリンズによるヤングアダルト小説を映画化したアクション。独裁国家が支配する近未来を舞台に、殺し合いのゲームに参加させられた16歳の少女の活躍を描く。主演は「X-MEN：ファースト・ジェネレーション」「ウィンターズ・ボーン」のジェニファー・ローレンス。", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr.Deeds", "Mr.ディーズ", "", "", "http://image.eiga.k-img.com/images/movie/1549/315x210.jpg?1375", 96, "アメリカの片田舎のピザ店主ディーズは、大会社オーナーの叔父が急死して持ち株を彼に残したために、会社オーナーに。TV番組のレポーター、ベイブは、彼を取材するため職業を隠して彼に接近。一方、会社重役チャックは、会社の乗っ取りを企んでいた。全米の人気コメディアン、アダム・サンドラー主演＆製作で36年製作の名作「オペラハット」を大胆にリメイク。彼が恋するリポーター役で、ウィノナ・ライダーが共演。", "2003-02-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I.Q.", "星に想いを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "整備工のエドは、ある日、車の修理に立ち寄った数学者のキャサリンに一目惚れしてしまう。しかし彼女は新進気鋭の心理学者と婚約したばかりで、しかも叔父はあのアインシュタイン博士。エドの前にI.Q.の厚い壁が立ちはだかるが、意外にも二人の恋を取り持ってくれたのはなんとアインシュタイン叔父さんだった……。M・ライアンとT・ロビンスによる心温まるラブ・ストーリー。アインシュタイン役のW・マッソーが優しさあふれる愛すべき老人を演じている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デルス・ウザーラ", "デルス・ウザーラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "シベリアのウスリー地方に暮らす天涯孤独の猟師デルス・ウザーラ。地誌調査のためにウスリー地方に入ったロシアのアルセーニェフ隊は、厳しい自然に直面し窮地に陥ったところをデルスに助けられる。大自然を愛するデルスの生き方にアルセーニェフは感嘆を覚え、次第にふたりは強い友情に結ばれていく。しかし、過ぎ行く年月は残酷な別れを容赦なく突きつける。オール旧ソ連ロケの映し出す大自然は厳しく美しく、役者たちの静かな演技も心を打つ。黒澤監督初の70ミリ作品。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEILY BA MANN AST", "夢がほんとに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "借金を抱えているテレビカメラマンのサデグは、戦争の前線の取材をすれば大金がもらえると聞き、面接に行く。彼の熱意に感激した隊長は、最前線に行くように言うが、サデグにとってはとんでもないこと。何とか前線から離れようと小細工をするが、すればするほど前線に近づいてしまうサデグ。ついには最も危険な地域に出てしまったサデグは、敵を砲撃し、英雄になってしまうのだった。イランでは珍しい戦争コメディ。", "1996-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRAMMA DELLA GELOSIA", "ジェラシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ひょんなことから花売り娘と恋に落ちてしまったレンガ職人のオレステ。妻子ある身ながら、不倫の愛に走ってしまった男の悲喜劇を描いたイタリアン・エロティック・コメディの快作。M・マストロヤンニとM・ヴィッティというコンビを、E・スコラが手慣れた演出で料理している。", "1970-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リンダリンダリンダ", "リンダリンダリンダ", "", "", "http://image.eiga.k-img.com/images/movie/1499/315x210.jpg?3066", 114, "とある地方都市の女子高校生たちの高校生活最後の夏を描く青春群像劇。バンドからギタリストが脱け、3日後に迫る文化祭で何を演奏するか困った響子、恵、望の3人は、オリジナル曲を断念。ブルーハーツのコピーをすることに決めて、韓国人留学生ソンをバンドに引き入れて練習を開始するが……。監督は「ばかのハコ船」「リアリズムの宿」の山下敦弘。音楽は元スマッシング・パンプキンズのジェームズ・イハが担当。", "2005-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Irreversible", "アレックス", "", "", "http://image.eiga.k-img.com/images/movie/1249/315x210.jpg?2758", 98, "パーティからの帰り道で、若い女性アレックスがレイプされる。それを知った彼女の婚約者はその復讐をしようと犯人を探し、彼女の元恋人はそんな彼を落ち着かせようと彼に同行する。監督は「カルネ」「カノン」のギャスパー・ノエ。「マレーナ」のモニカ・ベルッチと「ジェヴォーダンの獣」のバンサン・カッセルが共演。音楽はDAFT PUNKのトマ・バンガルテルが担当。", "2003-02-08", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GUNRUNNER", "ケビン・コスナーのガンランナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "「アンタッチャブル」の後にK・コスナーが主演した、アクション＆ラブ・ロマンス。1906年の中国を命からがら脱出したテッドは銃の密輸で生計をたてるが、田舎の母親や弟への仕送りもあり、どんどん深みにはまっていく。彼の波乱に富んだ生きざまを、ナイト・クラブの女性オーナーとの恋を絡めて描く。", "1989-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門捕物帖 地獄の風車", "右門捕物帖 地獄の風車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "一人、二人と殺されていく札差たち。むっつり右門は、事件の裏に強大な権力の存在をかぎつける……。嵐寛寿郎主演によるシリーズ終了後、東映に場を移して再開された大友柳太朗主演の一編。脚本の鷹沢和善は、沢島忠とその夫人・高松富久子の共同ペンネーム。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THREE MUSKETEERS", "三銃士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "A・デュマ原作による娯楽史劇『三銃士』の史上初めての映画化。「バグダッドの盗賊」など、当時最高の剣劇スターとして鳴らしたD・フェアバンクス製作・主演した無声大活劇である。腹黒い重臣の陰謀から国王と国家を守るべく立ち上がった近衛兵隊の三銃士と、その盟友である正義の若者ダルタニアンの活躍を、痛快無比な見せ場の連続で描く。1929年にはフェアバンクスが再びダルタニアンを演じた姉妹編「鉄仮面」も製作された。", "1921-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチバン　アルティメイタム2", "ガチバン　アルティメイタム2", "", "", "http://image.eiga.k-img.com/images/movie/56929/315x210.jpg?1924", 75, "ヤンキー映画「ガチバン」シリーズ第12作で、「ガチバン　アルティメイタム」の続編。バイカー集団「ゴールドスカル」の極悪非道な頭・金次が出所し、チームのきずなと誇りを汚した「BBBD団」を探し始める。一方、銀次との死闘で傷ついた紋児と、紋児を見守る勇人は息を潜めていたが、ゴールドスカルの急襲で紋児の仲間がさらわれてしまう。「ガチバン」シリーズを支えてきた佐野和真、窪田正孝がダブル主演。", "2011-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KISS", "キッス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "キッスをすることで悪魔の呪いが叔母から姪に代々伝わる、という恐ろしいテーマに基づいた、古典的な超自然ホラー。叔母アイリーンからキッスを受け、呪いを引き継いだフェリス。25年後、悪魔の呪いを引き継がせるために、フェリスは妹の娘エイミーに近づくのだった。悪魔の呪いは叔母から娘に代々引き継がなければならなかったのだ……。SFXは「ザ・フライ」でアカデミー賞に輝いたクリス・ウェイラスが担当。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おねがいマイメロディ　友＆愛", "おねがいマイメロディ　友＆愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の素顔", "夜の素顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "新藤兼人脚本・吉村公三郎監督の名コンビによる女性ドラマ。舞踊界の第一人者になろうという野望のために、自らの持てるものをことごとく提供して名声と栄光をかち得ようとする一人の女を描く。京マチ子と若尾文子らが演技バトルを展開。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SANSARA", "サンサーラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "写真家、映像作家、音楽家など多彩な顔を持つフランスの新鋭ジークフリート。彼の長編第2作は、世界14ヵ国でロケを行った自由奔放なロードムービーだ。放浪の旅に出た主人公サンサが訪れる東京のパートには、藤谷文子、安部譲二が出演。ジークフリート自身が手がけたサントラも魅力の一編だ。パリから放浪の旅に出た青年サンサ。世界各地を訪ねる彼は、遙か遠いアジアの日本にも立ち寄り、不思議な光景を目の当たりにする。", "2005-01-29", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INSIGNIFICANCE", "マリリンとアインシュタイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "マリリン・モンロー、アルバート・アインシュタイン、ジョー・ディマジオ、ジョゼフ・マッカーシーの４人が、「七年目の浮気」を撮影中のある一夜、偶然にも出くわして展開する、アメリカの悪夢。ローグ監督のめくるめく演出が素晴らしい。原題は“無意味”だが、ラストはこの世で最高の無意味、核爆発で締めくくられる……。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人致中年／A MIDDLE AGE", "人、中年に至る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "眼科の女医・陸文亭は、患者だった夫と結婚し、二人の子供を育てていた。生活は苦しかったが彼女はひたすら仕事に励む。しかし次第に疲労が重なり、彼女の健康はむしばまれていくのだった……。百花奨優秀作品賞のほか、主演のパン・ホンが金鶏賞優秀主演女優賞を受賞している。", "1982-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Lune dans le Caniveau", "溝の中の月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "フランソワ・トリュフォー「ピアニストを撃て」の原作で知られるアメリカ人作家、デビッド・グーディスのサスペンスを、「ベティ・ブルー」のジャン＝ジャック・ベネックス監督が映像化。レイプされたことを苦に自殺した妹を持つジェラールは、妹の復習を誓い、犯人を追い続ける。「ベティ・ブルー」「コールドマウンテン」のガブリエル・ヤーレが音楽を担当。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("靖国 YASUKUNI", "靖国 YASUKUNI", "", "", "http://image.eiga.k-img.com/images/movie/53499/315x210.jpg?2081", 123, "日本在住の中国人ドキュメンタリー監督リ・インが、靖国神社にまつわる様々な事象を10年間に渡って取材して完成させた日中合作のドキュメンタリー。毎年狂乱の様相を呈する8月15日の靖国神社を記録したほか、靖国神社のご神体として敗戦までの12年間で8100振りも作られたという“靖国刀”の鋳造を再現する刀匠の姿を通し、その歴史的意味を明らかにしていく。ベルリン国際映画祭をはじめ世界各地の映画祭で大反響を呼んだが、日本国内では上映を自粛する映画館が相次ぐなど物議を醸す。", "2008-05-03", "76,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Puccini e la fanciulla", "プッチーニの愛人", "", "", "http://image.eiga.k-img.com/images/movie/56129/315x210.jpg?2203", 84, "1909年、世界的作曲家のジャコモ・プッチーニは、新作オペラ「西部の娘」の作曲に取り組んでいた。しかし、そんなある日、プッチーニとの浮気を疑われていたメイドのドーリアが自殺。作曲活動を中断せざるをえなくなってしまう。イタリア、トスカーナ地方の風光明媚な土地トッレ・デル・ラーゴを背景に、実際に起こった「ドーリア・マンフレーディ事件」の真相を描き、プッチーニの創作の秘密に迫る。イタリア映画祭2009にて「プッチーニの娘」のタイトルで日本初上映。11年劇場公開。", "2011-06-18", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千と千尋の神隠し", "千と千尋の神隠し", "", "", "http://image.eiga.k-img.com/images/movie/1639/315x210.jpg?3874", 125, "10歳の千尋は、両親と地方都市に引っ越す途中、異世界に迷い込む。そこでは人間は、魔女＝湯婆婆が経営する神さまのための銭湯で働かない限り、ブタや石炭に変身させられてしまうのだった。ブタになった両親を助けようと、千尋はそこで働きはじめる。作画監督の安藤雅司、美術監督の武重洋二、音楽の久石譲など、宮崎アニメの常連が結集。前作「もののけ姫」に続き、日本古来の土着神たちがユニークなデザインで登場する。", "2001-07-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秋菊打官司", "秋菊（しゅうぎく）の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "　美しさと色気を兼ね備えた女性を演じることに定評のあるコン・リーが、「紅いコーリャン」「菊豆」とは打って変わり、身重の田舎娘を演じる。夫が村長との言い争いから、股間を蹴られてケガをする。妻の秋菊は村長のもとを訪ねるが、彼はまったく詫びる気がない。怒りの収まらない妻は警察に訴え、いつしか裁判沙汰へと発展していく。チャン・イーモウ作品は2年連続ベネチア映画際に出品され、本作で見事、金獅子賞、主演女優賞を獲得した。", "1992-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君の心臓の鼓動が聞こえる場所 ", "君の心臓の鼓動が聞こえる場所 ", "", "", "http://image.eiga.k-img.com/images/movie/54739/315x210.jpg?3480", 127, "演劇集団キャラメルボックスで演出・脚本を担当する成井豊が17年ぶりに書き下ろした小説を舞台化し、約3万人を動員した人気公演をデジタル映像化。40代にして独身の脚本家・根室典彦には、大学卒業後すぐに結婚しわずか6年で離婚した過去がある。クリスマスを目前にしたある日、作家を目指すため家出してきたという実娘・いぶきが突然、彼の元を訪れ……。主演は本作が初舞台となる黒川智花と同劇団の看板俳優として活躍する西川浩幸。", "2009-10-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の妻たち・情炎", "極道の妻たち・情炎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "高島礼子主演による人気ヒロイン活劇“極妻”新シリーズ第5作。跡目争いの渦中にいる夫を殺された極妻が怒りの戦いに乗り出すという、凄まじい情念のドラマが展開。威勢の良いタンカをスカッと決める高島の熱演に加え、杉本彩との“競艶”も見もの。殺された夫に代わり、組を切り盛りする姐御、波美子。亡き夫が参入していた大組織の跡目争いが激化し、彼女もそれに巻き込まれ……。", "2005-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("智恵子抄", "智恵子抄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "詩人・高村光太郎とその妻・智恵子との愛と死別を、中村登が叙情的に描いた作品。油絵画家を目指す智恵子が文選に落選、実家が倒産するなど不幸が続き、精神に変調をきたしていく様を、智恵子役の岩下志麻が巧演。彼女を一途な愛情で見守る光太郎を丹波哲郎が演じている。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たとえ世界が終わっても", "たとえ世界が終わっても", "", "", "http://image.eiga.k-img.com/images/movie/34149/315x210.jpg?1494", 98, "短編映画「演じ屋」シリーズや深夜ドラマ「駄目ナリ！」でカルト的人気を誇る野口照夫監督の劇場長編デビュー作。余命数年という宣告を受けた真奈美は、自殺サイトで出会った人々と集団自殺を試みるが失敗に終わる。1人で自殺しようとした彼女の前に、自殺サイト管理人の男性・妙田が現れ、“ある人を助けたら、苦しまずに死ねる薬をあげる”と提案。迷いながらもこれを受け入れた真奈美は、がんに侵された青年・長田を紹介される。", "2007-08-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIXON", "ニクソン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 191, "アメリカの第37代大統領、リチャード・M・ニクソンの激動と波乱に満ちた半生を描いた大河ドラマ。エリートのJ・F・ケネディとは対照的に、貧しい少年時代から大統領にまで昇り詰めてアメリカンドリームを成し遂げた男、ニクソン。彼は国内経済の立て直し、ソ連との力の均衡化、中国との国交回復、ベトナム戦争の終結への尽力など、数々の政治的功績をあげたにもかかわらず“ウォーターゲート事件”で辞任に追い込まれた。ニクソンは罠にかけられたのか、それとも何者かに利用されただけなのか。O・ストーンのニクソン周辺の人々への徹底した取材により、複雑な人物であったニクソンの素顔が描き出される。歴史的な事実だけでなく、ケネディ暗殺事件の黒幕をニクソンであると決めつけるなど、大胆な解釈、憶測を織り交ぜた手法に、賛否両論が渦巻いた問題作。主人公ニクソンにA・ホプキンスが扮し、若き日から晩年に至るまでをダイナミックに演じている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cold Creek Manor", "コールド・クリーク　過去を持つ家", "", "", "http://ecx.images-amazon.com/images/I/411ZTE7YMYL._SL210_.jpg", 119, "", "2003-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LIFE　OF　DAVID　GALE", "ライフ・オブ・デビッド・ゲイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "ハリウッド屈指の演技派ケヴィン・スペイシーが謎めいた役柄に挑んだミステリーの佳作。人生を踏み外し、死刑囚にまで転落した大学教授の軌跡が、女性記者との対話によって明かされていく。同僚をレイプして殺した罪に問われた元大学教授デビッド・ゲイルの死刑が確定した。最後の３日間、彼にインタビューを行うことになった女性記者ビッツィーは、事件の意外な真実を知り、えん罪ではないかと疑い始める。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALIFORNIA DOLLS（ALL THE MARBLES）", "カリフォルニア・ドールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "プロレスの本場アメリカでは、選手層も厚く、世に認められるまでは多くの巡業サーキットとトレーニング、観客へのアピール、そしてさらに大きな幸運が必要。この映画はその若さのすべてをプロレスに賭けた二人の美女、その名も“カリフォルニア・ドールズ”が、全米を転戦しながらアメリカン・ドリームを手にするまでを、華麗なテクニックと笑いで綴った娯楽作品。ズブの素人の女優たちをトレーニングしたのは、20年間女子世界チャンピオンだった名レスラー、ミルドレッド・バーク。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怒る西行", "怒る西行", "", "", "http://image.eiga.k-img.com/images/movie/55029/315x210.jpg?4687", 97, "「ニュー・ジャック・アンド・ヴェティ」「一万年、後…。」など独自の作風で知られる沖島勲監督が、玉川上水の久我山から井の頭公園に至る沿道を散歩する姿を追ったドキュメンタリー。東京都の放射5号道路建設計画によってその一部が取り壊されることになっている緑豊かな道を歩きながら、風景が変わっていくことに対する想いや自身の幼少期の体験、さらにモーリス・ド・ブラマンクや西行法師に至るまで、様々な話題を語り尽くしていく。", "2010-01-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("California Split", "ジャックポット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "　雑誌編集者のビルはポーカークラブで出会ったチャーリーと意気投合する。ギャンブル三昧のチャーリーは夜の仕事をしている女性ふたりのヒモだった。ビルはチャーリーと行動を共にし、次第にギャンブルにのめり込んでいく。ふたりは大勝ちする一方、しばしば災難にも遭うハメに。そんなある時、突然チャーリーは姿を消し、ビルは借金を抱え込むことに……。ふたりの男の交流、そして大勝負の顛末をシニカルに描いたコメディ。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOMEBOY", "ホームボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "主役のM・ローク自身が長年温めてきたという企画。主人公は愛を求めて町から町を渡り歩く孤独なボクサー。刹那的な幸せに生きる彼の心を支えるのは、これもどこか寂しげな一人の女。演じるのはロークの私生活のパートナーだったD・フューアー。E・クラプトンのせつないブルース・ギターがバックに流れる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青木ヶ原", "青木ヶ原", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-04-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TICKLE ME", "いかすぜ！この恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "西部を舞台にしたプレスリー主演の音楽映画。観光牧場で出会ったロニーとパム。パムは祖父の遺産の隠し場所を書いた手紙を持っており、何度か暴漢に襲われていたが、そのたびにロニーが助けてくれていた。しかし、ロニーと観光牧場の女社長との仲を疑ったパムは牧場を飛び出し、一人遺産捜しの旅に出かける。ロニーらは懸命にあとを追うが、彼らを何者かがつけ狙い……。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Mujer Sin Cabeza", "頭のない女", "", "", "http://image.eiga.k-img.com/images/movie/54059/315x210.jpg?3271", 87, "第5回スペイン・ラテンアメリカ映画祭上映作品。アルゼンチンで歯科医として裕福に暮らすベロニカは、ある日一瞬のわき見運転で何かを轢き逃げしてしまう。夫や親族はベロニカを庇い、全て無かったことにしようとするが、ベロニカの心には“誰かを殺したかもしれない”という大きなトラウマが根深く残る。アルゼンチンの保守的な富裕層の人々と、その背景に見え隠れする多くの混血の貧者が暮らす社会をシビアに描く。", "2008-09-13", "22,9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("櫛の火", "櫛の火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "恋人を病気で失った青年に残されたのは、彼女が使っていた１本の櫛だけだった。櫛とともに空白の日々を送る青年だが、新しい年上の女性との出会いを機に、生きることの意味を見つけ出していく。草刈正雄が現代青年のとめどない解体感、喪失感を好演。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("竜二", "竜二", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "子分もいるし、町では少し顔を知られたヤクザ者・竜二がある日、妻と娘のために足を洗ってカタギの世界に入ろうと決心する。実際にヤクザの世界にいたことがある金子正次の原作・主演による温かくてホロ苦いドラマ。監督は新人の川島透で、その若々しい映像は金子正次の演技とともに高い評価を受け、その年の映画賞の話題をさらった。ワキ役に、元フォーリーブスの北公次やコメディアンの桜金造などが意外な面を見せて好演。竜二の妻を演じた永島暎子も印象に残る。金子正次は残念ながらこの作品が完成直後にガンで死去。撮影中も病状をおしての執念の熱演であったという。なお、脚本の鈴木明夫は、金子正次のペンネームである。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("演劇1", "演劇1", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「選挙」「精神」の想田和弘監督が、劇作家の平田オリザと平田が主宰する劇団青年座を描くドキュメンタリー。2部作の前編。", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAGE TO KILL", "バトル・ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "“ランボー”を思わせるスーパー・ヒーローが大活躍するB級活劇。カー・レーサー、ブレイン・ストライカー。彼は、セント・ヘロン島で起きた軍事クーデターに、留学している弟が巻き込まれたことを知り、急きょ島へ潜入した。そこで、彼を待ち受けていたのは、クーデターの首謀者、ターナー少将に仕える武装兵たち。捕らえられたストライカーは、すさまじい拷問を受けた後、弟が軟禁されている医科大学の寮に移されるのだが……。ターナーを演じるのは、個性派オリヴァー・リード。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猫の恩返し", "猫の恩返し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "宮崎駿が企画し、「耳をすませば」の姉妹編として柊あおいが書き下ろした原作をもとに、新進の森田宏幸が監督したファンタスティックな女の子の青春アニメーション映画。車に轢かれそうになった猫を助けた高校生のハルは、その恩返しを受けることになる。猫の国へ招待されたまでは良かったが、彼女はそこで猫の王様から息子の嫁にと強要されてしまう……。 ビデオ、DVDタイトルは「猫の恩返し／ギブリーズ　episode 2」。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KINGFISH", "キングフィッシュ　大統領への挑戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "民主党上院議員にしてルイジアナ州知事のヒューイ・P・ロングは、カリスマ的魅力で一気に中央政界に躍り出る。彼は次期大統領選で現職のルーズベルトを打ち破るとまでいわれた政界の“キングフィッシュ(最重要人物)”となるが、ルイジアナ州議事堂で一発の銃弾に倒れた。犯人は前途有望な青年医師カールだったが、ボディガード３人から60発を超える銃弾を撃ち込まれ即死する。そして議員も30時間後に他界。この暗殺はカールの単独犯なのか、それとも陰謀なのか？　ケネディ大統領暗殺事件と並ぶ政治ミステリーであり、幻の大統領とまでいわれた“ロング上院議員狙撃事件”を映画化。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAGE DOOR", "ステージ・ドア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1930年代、ソフィスティケイテッドな作風で、多くの女優の魅力を引き出したラ・カーバ監督の代表作の１本。ニューヨークの演劇の世界を舞台に、女優たちの喜びと悲しみ、成功と失敗、羨望と嫉妬を鮮やかに描き出す。若き日のヘプバーンの熱演が印象深い佳作。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRICK OR TREAT", "ハロウィン1988　地獄のロック＆ローラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "平凡な若者エディが、焼身自殺したロック・スター、サミーのレコードを入手した。エディがそのレコードをかけると、なんとサミーが不気味な姿で復活。彼はオカルト・パワーを使って街で暴れ始める。「ハロウィン」とは全く無関係の、パワフルなホラー。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED　PLANET", "レッド・プラネット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "2050年、環境破壊によって人類の滅亡も間近となった地球では火星への移住計画が進行。その第一歩として火星で大量の藻を栽培し、酸素を確保することになる。ところが、酸素の観測数値を知らせる装置が故障。宇宙飛行士や科学者ら６名が火星に送られた。しかし、火星に接近したところで宇宙船は損傷を受け、女性船長ボーマンだけが母船に残り、ほかの５名のクルーが着陸船で火星に降り立つ。生命体は存在しないはずの火星で彼らは無数の甲虫に襲われ、自分たちの活動を助けるはずのロボットAMEEの攻撃を受けることに……。火星を舞台に繰り広げられるクルーたちのサバイバルを、スリルとサスペンスたっぷりに描いたSFアクション。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武闘派仁義 完結篇", "武闘派仁義 完結篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "経済ヤクザが幅を利かす世の中に、侠気の道を貫こうとする武闘派ヤクザの乱れ咲きの人生を描くシリーズ完結編。極道社会のはぐれ者・児島武士は、自分が求める“任侠道”を実現すべく組を旗上げする。が、警察の目がうるさくなるのを恐れ、児島を目障りとする旧来の組が結束してこれを潰そうと、刺客を送り込む……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VUKOVAR POSTE RESTANTE", "ブコバルに手紙は届かない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ユーゴスラビアの民族戦争を背景に描き、数々の映画賞を獲得した力作。異民族ゆえに引き裂かれていく男女の悲劇を軸に、戦争の無意味さ、人間の愚かさを訴えかける。ユーゴスラビアの美しい町ブコバル。クロアチア民族籍のアナは幼なじみのセルビア民族籍のトーマと結婚し、幸せの絶頂にあった。ちょうどその頃ベルリンの壁が崩壊、ユーゴからの独立を叫ぶクロアチア人と残留派のセルビア人の対立が始まる。トーマが召集され、実家に戻ったアナは妊娠していることに気付く。しかし、抗争はますます激化。アナが留守の間に家の火事で両親は死亡、さらに“戦争の犬”と称する男たちによって乱暴され、彼女は深い絶望感に襲われる……。実際に戦争の起きたブコバルで撮影し、戦争の痛ましさを生々しく映し出す映像が異様な緊迫感を醸し出す。ビデオ発売時タイトルは「ブコバル」。", "1994-01-01", "1,4,34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅櫻夢／RED　CHERRY", "レッドチェリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "1940年のモスクワ、イワノフ国際学院に編入して来た中国人の少年と少女の二人は、ドイツ軍の侵攻で始まる戦争に巻き込まれてしまう。少女チュチュはドイツ軍将校のメイドとして働き始めるが、刺青好きの将校により、背中に悪魔の紋章を入れられてしまうのだった。戦争に運命を変えられた実在の少女たちの悲しみをオール・ロシアロケで描いている。ビデオタイトルは「処女の刺青　レッドチェリー」。", "1995-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love's Labour's Lost", "恋の骨折り損", "", "", "http://image.eiga.k-img.com/images/movie/1509/315x210.jpg?3021", 93, "時は1939年。フランス近隣の小さな王国ナバールの若き国王は、学問のため親友3人と“女人禁制”の誓いをたてる。そこへ、病床にある父王の代理としてフランスの王女がお付きの美女3人を伴い交渉にやってきた。固い誓いもどこへやら、男たちは一目で恋に落ちてしまう。男女8人はそれぞれ恋した相手にラブ・ゲームを開始。果たして恋の駆け引きの勝者は誰なのか……？", "2000-12-16", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガラスの使徒〈つかい〉", "ガラスの使徒〈つかい〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "日本演劇界の重鎮、唐十郎が原作・脚本・出演を兼ねた意欲作。「夜を賭けて」で監督デビューを果たした、新宿梁山泊の金守珍がメガホンを執り、演劇色の濃いファンタジーとなっている。巨大なレンズを磨き続ける伝説のレンズ研磨職人と、ガラスの化身ともいうべき謎の美少女との交流を、幻想的に紡ぎ出す。天体望遠鏡用の直径60センチのレンズを磨き続ける職人、池谷佐七。ある日突然現れた少女、葉子は彼のために特別な研磨剤“化粧砂”を取りに行くのだが……。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("石中先生行状記", "石中先生行状記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "石坂洋次郎文学の映画化。「隠退蔵物資の巻」「仲たがいの巻」「千草ぐるまの巻」の３話からなる。東北のある城下町に住む有名な小説家・石中先生が、村の老若男女の御意見番としてユーモラスに問題を解決していく。第３話に登場する三船敏郎の農夫は笑える。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEXAS", "掠奪の町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "W・ホールデン、G・フォードという当時最高の人気を誇った美男俳優の顔合わせによるウエスタン。混乱する南北戦争直後のテキサスを舞台に、二人の男が一人の女と恋のさやあてをしたり、強盗事件に巻き込まれたりという古き良き時代の西部劇。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("好人好日", "好人好日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "笠智衆と岩下志麻が父娘に扮し、ほのぼのとした情愛を見せる人情ドラマ。奈良の大学の数学教授・尾関は、数学以外には無関心の変人。その娘・登紀子には竜二というフィアンセがいるが、彼女は父が竜二を気に入るかどうかが気掛かりで……。随所にユーモアを交えながら、娘を温かく見つめる父親を演じた笠智衆の好演が光る。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Abrazos: Tango En Buenos Aires", "タンゴ・イン・ブエノスアイレス　抱擁", "", "", "http://image.eiga.k-img.com/images/movie/54039/315x210.jpg?4200", 86, "「ブエノスアイレス・タンゴ・フェスティバル」に密着したドキュメンタリー。アルゼンチンタンゴ発祥の地であるブエノスアイレスで開催されたタンゴ・フェスティバルを中心に、演奏者やダンサー、観客を通してタンゴ文化の奥深さに迫る。2004年に亡くなった伝説のバンドネオン奏者、ホセ・リベルテーラの貴重な演奏も収められている。", "2008-11-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Treasure of the Sierra Madre", "黄金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "　メキシコの町に流れてきたドブスとその相棒カーティンは老人ハワードと出会う。彼らは黄金探しの夢を追って、旅に出ることに。シェラ・マドレ山で砂金を見つけたものの、猜疑心のとりことなったドブスは独り占めを企み……。三人の男たちの旅を通じて人間の欲望を描き出す。ジョン・ヒューストン監督の実父ウォルターが好演し、アカデミー助演男優賞を受賞。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHAKEDOWN", "シェイクダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "J・グリッケンハウスが、P・ウェラーと組んで、警察内部の麻薬汚職を描くポリス・アクション。主人公は、おとり警察官殺害容疑で捕まった麻薬売人を担当する弁護士ローランドと、ニューヨーク市警のはみ出し刑事リッキー。売人の証言から、事件の背後には麻薬密売組織と癒着する“ブルージーン・コップ”なる刑事たちの存在があることを知った二人は、不正を暴くために協力して巨悪に立ち向かっていく。監督によれば「フレンチ・コネクション」の1980年代版を作りたかったとか。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジェフ・ベック　ライヴ・アット・ロニー・スコッツ cine sound ver.", "ジェフ・ベック　ライヴ・アット・ロニー・スコッツ cine sound ver.", "", "", "http://image.eiga.k-img.com/images/movie/54749/315x210.jpg?3033", 100, "2007年11月27日から12月1日までの5日間、ロンドンにあるロニー・スコッツ・ジャズ・クラブにて行われたジェフ・ベックのライブ映像を上映。1965年のデビュー以来、世界のロック・ギター・シーンの頂点に君臨し続けるジェフ・ベックとエリック・クラプトンの共演、客席にはロバート・プラント、ジミー・ペイジの姿が映し出されるなど、ロックの歴史を凝縮した映像作品に仕上がっている。", "2009-09-26", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1980（イチキューハチマル）", "1980（イチキューハチマル）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "1980年代にカルトバンド“有頂天”のリーダーとして活躍し、現在は劇団ナイロン100℃の主宰として知られるケラリーノ・サンドロヴィッチが、待望の映画監督デビュー。1980年の東京を舞台に先生、教育実習、生徒として同じ学校に通う三姉妹の姿を時事ネタ満載で描き出す。ケラ独特のすっとぼけたユーモアが冴えわたる一方で、オーソドックスな人間ドラマとしても高水準な佳作。元アイドルの一之江キリナが、星隆高校に教育実習にやってきた。大騒ぎする生徒たちをよそに、教師として働く姉のカナエとキリナに教わることになった妹のリカは困惑顔。しかもキリナはイケメンの生徒に一目惚れしてしまい……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TERROR ON TAPE", "テラー・オン・テープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ホラー・ムービー20作品のハイライト・シーンを収録したサービス精神旺盛な一編。ホラー・ビデオ専門店の主人が、次々とやって来る客にお勧めのテープを紹介していくという設定で、マニアが大喜びする凄絶な鮮血シーンが続出する。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後的瘋狂／DESPERATION", "追跡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "西安映画製作所に所属する第五世代の監督のなかでも、サスペンス・アクションを得意とする異才として知られるC・シャオウェンの第２作。殺人犯として指名手配された宋沢は大連市内に潜入。青年警視・何磊は偶然彼の姿を発見し、彼を追いつ詰る。何磊は宋沢に自分の過去と同じ暗い影を発見し始めていた……。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IRON EAGLE", "アイアン・イーグル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "アメリカ空軍機が中東上空でテスト飛行中、領空侵犯で撃墜された。不運にも敵の捕虜となったパイロットに死刑が言いわたされる。同じパイロットを目指していたその息子は、父を助けるために、友人の力を借りて、救出作戦を開始する。F−16など最新鋭機が登場するスカイ・アクション。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE　ME　TONIGHT", "今晩は愛して頂戴ナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "J・マクドナルドとM・シュヴァリエの名コンビによるミュージカル・コメディの傑作。パリに住む貧乏だが陽気な仕立て屋のモーリスは、公爵の令嬢ジャネット姫に一目惚れしてしまう。身分を隠してジャネットの住む城に入り込み、二人は恋仲になるが……。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OTHELLO", "オセロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "シェイクスピアの４大悲劇の一つ。ムーア人の将軍オセロが、イアーゴの奸計にはまって、貞淑な妻を殺害し、自らも悲劇的な最期を遂げるさまを名優オリヴィエがドラマティックに熱演している。", "1966-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モンスターズクラブ", "モンスターズクラブ", "", "", "http://image.eiga.k-img.com/images/movie/57209/315x210.jpg?4370", 72, "「青い春」「ナイン・ソウルズ」の豊田利晃監督が、アメリカを18年間にわたり震え上がらせた天才数学者で爆弾魔のユナボマーに着想を得て製作したドラマ。1978～95年の間、合計16回にわたって航空業界や金融関係者に爆発物を送りつけ、3人の犠牲者と29人以上の怪我人を生んだ「ユナボマー事件」の犯人で大学助教授のセオドア・ジョン・カジンスキーを、現代日本の雪山にこもり企業やテレビ局に爆弾を送り続ける主人公・垣内良一に置き換えて描く。黙々と爆弾を送り続ける良一は、森で奇妙な怪物と遭遇して以来、怪物や死んだ家族の幻想に悩まされる。そして最後の爆弾を日本の首相に送ろうとした夜、自殺した兄ユキが目の前に現れ……。主演は豊田監督と4度目のタッグとなる瑛太。兄ユキを窪塚洋介が演じる。", "2012-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNA SRINA NEL CUORE", "心の棘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "イタリアの地方都市で暇を持て余した青年ウィルは、キャシーと一夜を過ごす。だが、彼女のすべてを手にしたかと思うと、次の瞬間には彼女を失ったことに気付く。キャシーには、ある過去が……。アラン・ドロンの息子アンソニーのデビュー作。", "1986-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLIND DATE", "ブラインド・デート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "多忙のため恋人をつくる暇もないウォルターが、会社の重要取引先とのディナーで“女性同伴”を義務づけられた。兄に相談したところ、紹介してくれたのはすごい美女。ただし“絶対に酒を飲ませてはいけない”という忠告つきだったのだが……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOCK’N LOAD", "殺しのファイナル・コール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "米軍のトップ・コマンドたちの脳に埋め込まれた特殊指令が20年の歳月を経て実行されるというサスペンス仕立てのアクション。ベトナム戦争後、米軍は催眠を利用した極秘の訓練を行った。その訓練とは秘密コードによってコマンドたちが殺人マシンに変身するというもの。ところが平時では厳守されるべきコードがもれてしまう。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("369のメトシエラ　奇跡の扉", "369のメトシエラ　奇跡の扉", "", "", "http://image.eiga.k-img.com/images/movie/55829/315x210.jpg?4004", 105, "公的記録のない独居高齢者や育児放棄された無国籍児など、現代社会の隙間で生きる人々の姿を通して“都会の中の孤独”を浮き彫りにするファンタジー。区役所に勤める俊介は、一カ所に定住することなく引越しを繰り返していた。古びたアパートに引越した俊介は、毎日のように隣室から聞こえてくる奇妙な歌声に興味を持ちはじめる。やがて引き寄せられるように隣室を訪れた俊介は、そこにいた老婆から思いがけない話を聞かされる。", "2011-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER CAPRICORN", "山羊座のもとに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1830年代オーストラリアのシドニー。土地の名士サムとその妻ヘンリエッタのもとへ、故国アイルランドから友人がやって来る。二人には暗い過去があったが……。ヒッチコックには珍しいコスチューム・プレイ。長回しを多用した独特のスタイルはヒッチコックならではのもの。名手カーディフの撮影も見逃せない。", "1949-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("源氏九郎颯爽記 秘剣揚羽の蝶", "源氏九郎颯爽記 秘剣揚羽の蝶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "美剣士・源氏九郎の人気シリーズ第３作。和歌の巻き物の行方を追って、中村錦之助扮する源氏九郎が、祖先・源義経の悲願が宿る水煙、火焔の両刀をひっさげて大暴れするという娯楽時代劇。ある時は神出鬼没の道中ヤクザ“初音の鼓”に身をやつす、源氏九郎の“遠山の金さん”ばりの活躍が面白い。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("図鑑に載ってない虫", "図鑑に載ってない虫", "", "", "http://image.eiga.k-img.com/images/movie/34029/315x210.jpg?1365", 103, "高視聴率を記録した深夜ドラマ「時効警察」の三木聡監督による脱力系サスペンス・コメディ。「月刊黒い本」の美人編集長から、謎の生物“死にモドキ”を探し出して死後の世界をルポするよう依頼されたフリーライターの“俺”。嫌々ながらもアル中のオルゴール職人エンドーやリストカットマニアのサヨコらと共に捜索の旅に出るが……。主人公に「嫌われ松子の一生」の伊勢谷友介、サヨコに「バベル」の菊地凛子、エンドーに松尾スズキが扮する。", "2007-06-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Perfect Getaway", "パーフェクト・ゲッタウェイ", "", "", "http://image.eiga.k-img.com/images/movie/54819/315x210.jpg?1671", 98, "ハワイを舞台にしたミラ・ジョボビッチ主演のアクション・サスペンス。監督は「ビロウ」「リディック」のデビッド・トゥーヒー。ハネムーンでハワイを訪れていたクリフとシドニーは、1組のカップルが殺害される事件に遭遇する。現地で仲良くなった2組のカップルに次第に疑いを抱き始めたシドニーたちだったが、犯人の動機など一切不明なまま、謎の殺人事件に巻き込まれていく。", "2010-01-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("８月の約束", "８月の約束", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "夏休みの最後、恋人から盗んだマリファナ自生地の地図を手にした美大生の今日子は、二人の友人とともに人里離れた田舎の森にやって来た。ヒッチハイクをして、自殺願望者の岡井の車に乗せてもらい目的地に向かうのだが、途中ゲイのカップルと出会い……。今どきの若者たちの奇妙な旅を描いた異色ドラマ。1995年度ゆうばりファンタスティック映画祭ビデオ部門でグランプリを受賞した。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／KUMIHO", "KUMIHO／千年愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "東洋に古くから伝わる千年を生きる九尾の狐（九尾狐＝KUMIHO）が現代によみがえるというファンタジー・ロマン。すでに999年生きているKUMIHOは、本当に愛し合える男と出会い、その日から100日目に愛を交わすことができれば人間に生まれ変わることができる。しかし残された時間は一年しかなかった。そんなある日、ソウルの町でチンピラに殴られていたヒョギという若者を助け、彼こそ真実、愛し合える男ではないかと思うのだった……。公開されるや大きな話題を呼び、当時の韓国映画史上最大のヒットとなった作品。", "1994-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゼロファイター・大空戦", "ゼロファイター・大空戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "黒澤明の愛弟子・森谷司郎の監督デビュー作。太平洋戦争の激戦地・ブイン基地へ新任の若い隊長が赴任して来て、暴れ者ぞろいの航空隊を腕と度胸でまとめあげる。“若大将”シリーズで人気急上昇の加山雄三が、若い新任隊長に扮し熱演する骨太の空戦ドラマ。円谷英二が特撮を担当。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Terminator Salvation", "ターミネーター4", "", "", "http://image.eiga.k-img.com/images/movie/53229/315x210.jpg?1362", 114, "西暦2018年。スカイネットに抵抗する人類の戦いを導くはずのジョン・コナーだが、記憶喪失の謎の男マーカス・ライトとの出会いにより、その決意が揺らぎ始める。果たしてマーカスの正体は……。ジョンはマーカスとともにスカイネットの中枢へ侵入するが、そこで恐るべき秘密を目にすることに。ジェームズ・キャメロンが生み出した大ヒットシリーズの第4作で、新たな主演に「ダークナイト」のクリスチャン・ベール、監督は「チャーリーズ・エンジェル」のマックG。", "2009-06-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続々々番頭はんと丁稚どん・　チャンポン旅行", "続々々番頭はんと丁稚どん・　チャンポン旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ペテン師の情婦の色仕掛けにまんまとひっかかって500万円の商品を盗まれてしまった雁七は、九州にたまっている大口の集金200万円を５日間で集めることで責任をとるよう命じられる。崑松らとともに九州での仕事を始める雁七だが……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大魔神逆襲", "大魔神逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "雪深い山里での物語。隣国の領主は兵器作りのために村の男たちを地獄谷に連れ去る。父や兄を助けるため、４人の少年が魔神の山を越え地獄谷に向かう。少年たちは雪山で睡魔に襲われ、彼らの命もこれまでと思われた時、金色の光とともに大魔神が現れ……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドリフターズですよ！ 盗って盗って盗りまくれ", "ドリフターズですよ！ 盗って盗って盗りまくれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "今回ドリフの面々が扮するのは、コソ泥。獄中で出会った５人は、出所後の再会を誓い、それぞれ街へ出ていく。そして再会した５人は徹底した泥棒学訓練を経て、いよいよその成果を試すのだが……。シリーズ唯一の原作もの。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤裸特工", "レディ・ウェポン", "", "", "http://ecx.images-amazon.com/images/I/51JYCXVJENL._SL210_.jpg", 92, "10代で誘拐され、殺し屋として養成された少女たちの過酷な闘いの日々を描く本格派レディース・アクション。厳しい訓練、仲間との殺し合いを強いられる最終試験を経て、殺し屋となってからも、なお襲いかかる危機の連続。そんな極限状態を、二人のヒロインの固い友情を通してエモーショナルに描く。ワイヤー・ワークと格闘に体当たりで挑んだ新進女優マギー・Qとアンヤの熱演に注目。暗殺者候補生として謎の女性マダムMによって誘拐された少女たち。その中の二人、シャーリーンとキャットは励まし合い厳しい訓練を乗り越え、さらなる非情な世界に飛び込んでいく。", "2004-12-11", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EMERALD FOREST", "エメラルド・フォレスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "アマゾンでダム建設に従事するアメリカ人技師の家族が、ピクニックの途中でインディオに襲われ、一人息子がさらわれる。以来10年、１日も休むことなく捜索を続ける父親の前に、次代を担うリーダーとしてインディオに育てられた息子が現れた。文明社会への帰還を訴える父親に、“ボクの家はここだ”と答える息子。父親は断腸の思いでジャングルをあとにするが、ある日、部族の危機を救うべく立ち上がった息子が、助けを請いに来る。悪の巣窟に乗り込む父子。二人の間に、熱い絆は消えることがなかった。南米で起きた類似事件をもとに、鬼才ブアマンが描くアドベンチャー大作。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("重慶森林／CHUNGKING EXPRESS", "恋する惑星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「欲望の翼」「楽園の瑕」と大作の続いたW・カーウァイがそれまでの制作スタイルを一新して、短期間で撮影し、素早く仕上げたターニングポイントともいうべき一編。失恋した刑事の心を、謎の金髪女とのプラトニックな一夜が癒やすことになる『重慶マンション』。そして恋した男の部屋に忍び込み、模様替えしていく女の姿を活写した『ミッドナイト・エクスプレス』の２本のエピソードで構成されている。カーウァイのモチーフ“すれ違い”が軽快な語り口で、さらに洗練された情景として浮かび上がり、日本では大ヒット。歌姫フェイ・ウォンの起用、ママス＆パパスのヒット曲『夢のカリフォルニア』の効果的な挿入が目を引く。後に姉妹編「天使の涙」にも出演する金城武の独特の風情もたまらない。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The White Ribbon", "白いリボン", "", "", "http://image.eiga.k-img.com/images/movie/55299/315x210.jpg?1517", 144, "「ピアニスト」「ファニーゲーム」などで知られるオーストリアの鬼才ミヒャエル・ハネケによるミステリー。第1次世界大戦直前の北ドイツを舞台に、教会や学校の指導でプロテスタントの教えを守って暮らしてきた小さな村の住人たちが、次々と起こる不可解な事故によって不穏な空気に包まれていく様子をモノクロ映像で描きだす。カンヌ国際映画祭パルム・ドール、ゴールデングローブ賞外国語映画賞をはじめ多数の映画賞を受賞。", "2010-12-04", "7,77,2,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅雪姫 怨み恋歌", "修羅雪姫 怨み恋歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "梶芽衣子主演・藤田敏八監督による「修羅雪姫」の続編。前作で見事仇を討ったお雪は、警察に捕まり死刑判決を受けるが、革命家の手紙を盗むことを条件に釈放される。しかし、革命家の家に女中として入り込んだお雪は、彼の人徳に魅せられ、警察を裏切る。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE　VALENTINE", "ホワイト・バレンタイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「リベラ・メ」のヤン・ユノ監督による恋愛ドラマ。最愛の恋人を亡くした悲しみから立ち直れない男と、天真爛漫だが恋にはちょっと臆病な少女。不思議な縁でめぐり合った二人の、胸に抱く切ない思いが綴られる。ヒロインに扮するのは「僕の彼女を紹介します」のチョン・ジヒョン。韓国を代表する女優となった彼女が、本格女優デビューを飾った記念すべき１本。", "1999-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PART XV　　商工ローン・保証人の落し穴", "難波金融伝 ミナミの帝王　劇場版PART XV　　商工ローン・保証人の落し穴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "若くしてベンチャー企業を立ち上げた青島は、銀次郎から借金するが、その後新たに金が必要になり、別のローン会社から融資を受ける。ところが事業は失敗し、青島は失踪。借金を肩代わりさせられた婚約者の由利子に、銀次郎が救いの手を差し伸べる。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南国太平記・前篇", "南国太平記・前篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "幕末の乱世のなか、片岡千恵蔵が相対立する二役を演じた時代劇。島津斉興の側室・お由羅は嫡男・斉彬を排し、我が子の久光を嗣子に立てようとして牧仲太郎に斉彬の３人の子を呪殺させる。一方、斉彬の家臣・益満は牧を怪しいとにらみ、彼ら一味を殺そうと計画する……。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DELIRIOUS", "ジョン・キャンディの夢におまかせ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "シナリオ・ライターのジャックは、自作のドラマでレイチェル役を演じているローラに夢中。ところが見事彼女にフラれてしまった。傷心のジャックは交通事故を起こしてしまう。そして彼が目を覚ましたのはなんと彼が創作したドラマの中の架空の町“アッシュフォード”の病院だった。しかも彼がタイプを叩いた通りにコトが運ぶとなれば……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EYE OF THE WIDOW", "プリンス・マルコ 地中海の標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "紺碧の地中海。恐るべき戦略細菌兵器をめぐって謎の武器商人やブローカー、テロリストらが暗躍するなか、武器商人の美しい未亡人を救い出し、細菌兵器を焼却して黒幕を抹殺するためCIAの敏腕エージェント、プリンス・マルコが派遣される。フランスの人気作家ビリエの代表的スパイ小説の映画化。地中海のロケは、派手なアクションとともに優雅なリゾート気分をも満喫させてくれる。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADY CHATTERLEY’S LOVER", "チャタレイ夫人の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "戦争で不能になった夫を捨て、野卑な庭番のもとで性に目覚めていく若妻コニーを描く。ロレンスの高名な原作を、J・ジャカン監督が映画化。イギリス中部のみずみずしい田園風景の中に浮かぶコニーの白い肌が悩ましい。", "1982-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CIAO FEDERICO！", "フェリーニ サテリコン日誌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "圧倒的なエネルギーにあふれたフェリーニの大作「サテリコン」のメイキング・フィルム。有名なトゥリマルキオの大宴会、将軍リーカの軍船などのシーンで、フェリーニが、いかに俳優を演出するかが見もの。撮影現場にポランスキーがシャロン・テートと訪ねてくるシーンも収録されている。E.F.L.A.アメリカ・フィルム・フェスティバルのブルーリボン大賞受賞。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("手塚治虫のブッダ　赤い砂漠よ！美しく", "手塚治虫のブッダ　赤い砂漠よ！美しく", "", "", "http://image.eiga.k-img.com/images/movie/55609/315x210.jpg?1317", 111, "手塚治虫が10年の歳月をかけて描きあげた名作コミック「ブッダ」をアニメ映画化した全3部作の第1弾。声優陣には吉岡秀隆、吉永小百合、堺雅人ら豪華俳優が結集する。舞台は2500年前のインド。世界の王になると予言されたシャカ国の王子シッダールタは、思春期を迎え、厳しい階級社会に疑問を抱くようになる。そんなとき、強大なコーサラ国がシャカ国に侵攻してくる。そこでシッダールタは、コーサラ国一の勇者と呼ばれる青年チャプラと出会う。", "2011-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOM AND DAD SAVE THE WORLD", "スペース・エイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "地球を破壊せんと惑星スペンゴから望遠鏡をのぞいていた皇帝スペンゴは、地球上に理想の女性を発見。それは、まったくの平凡なアメリカの主婦マージであった。スペンゴは、夫と旅行に出かけたマージを超引力光線で自分の星へ連れて来る。何とか一人脱出できた夫ディックは、マージ奪還と地球の平和のために戦うのであった。ブルドック型エイリアンやら、金魚型エイリアンなど奇妙な生き物がウヨウヨ登場するSFアドベンチャー。J・ジョーンズ、T・ガー、E・アイドルといった喜劇役者が登場している。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flyboys", "フライボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/53069/315x210.jpg?1850", 138, "第1次世界大戦時、中立の立場をとっていたアメリカから志願兵としてフランス空軍に入隊した青年たちの実話を映画化したフライト・アクション。テキサスに住む青年ローリングスは、ニュース映画で戦闘機パイロットの活躍を見てフランス行きを決意する。ラファイエット戦闘機隊に参加した彼は、同じくアメリカからやって来た仲間たちと共にパイロットとしての訓練を受け始めるが……。主演は「スパイダーマン」シリーズのジェームズ・フランコ。", "2007-11-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミュージックビデオ・ノイローゼ　スギモトコウスケ×ハンサムケンヤ MV集", "ミュージックビデオ・ノイローゼ　スギモトコウスケ×ハンサムケンヤ MV集", "", "", "http://image.eiga.k-img.com/images/movie/57859/315x210.jpg?2185", 45, "2011年にミニアルバムを発表してデビューし、音楽誌などから注目を集めている京都在住の新鋭シンガーソングライター・ハンサムケンヤのミュージックビデオ（MV）を一挙上映するプログラム。CG、実写、アニメーションなどを自在に合成した映像表現が話題のMVは、ケンヤ自らの依頼で映像作家の椙本晃佑が一手に引き受けている。「これくらいで歌う」「蟲の溜息」「決心速度」「アラハラ」「この街の歩き方」の5本を上映し、オープニングと曲間にも椙本による新たな映像が加えられている。", "2012-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CHOC", "最後の標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ドロン、ドヌーヴというフランス映画界きっての大物スターの共演で描かれた犯罪ドラマ。足を洗おうとした殺し屋が、組織の手を逃れて、とある農家に身を寄せた。そこで彼は人妻と激しい恋に落ち、やがて彼女と新たな人生を歩むため、マフィアとの最後の戦いに挑む。ドロンの渋みを増した存在感、ドヌーヴの美貌が印象的。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEN WANTED MEN", "十人のならず者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1860年代のアリゾナ州タクスンシティは、 “十人のならず者”と呼ばれる無頼漢たちの支配下にあった。苦心の末ついにこの地を家畜王国に築き上げた先駆者の一人、スチュアートは平和な暮らしを望み、無関心を装っていたが、ついに怒りを爆発させる。悪に敢然と立ち向かう正義の男を描く娯楽西部劇。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HUSBAND’S AND LOVERS", "金曜日の別荘で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "イタリアの文豪モラヴィアが、最初の妻のエルサ・モランテとルキノ・ヴィスコンティの三角関係をもとに描いたといわれる原作『金曜日の別荘』の映画化。アリーナとステファーノは理想的な夫婦に見える。だが、夫はセックスよりも彼女自身を賛美し、妻は肉体的にも激しい愛を求めていた。やがて妻は平日を夫と過ごし、週末を愛人と過ごすことを夫に申し出る。ジョルジオ・アルマーニが担当する主演の二人の衣装にも注目。", "1991-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Grand Bleu", "グラン・ブルー／オリジナル・バージョン", "", "", "http://ecx.images-amazon.com/images/I/51DTt2auaxL._SL210_.jpg", 132, "「レオン」「フィフス・エレメント」のリュック・ベッソン監督の出世作「グレート・ブルー」公開10年を記念して日本公開されたフランスオリジナル・バージョン。幼い頃からイタリア少年エンゾにライバル扱いされていたフランス少年ジャック。大人になったエンゾは、ジャックを探し出し再び勝負を挑むのだった。素潜りで深度を競うフリーダイビングにかける男たちの物語。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Payback", "ペイバック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「殺しの分け前　ポイント・ブランク」のタイトルで映画化されている、R・スタークの『悪党パーカー／人狩り』を再映画化したハードボイルド活劇。チャイニーズ・マフィアから大金の強奪に成功したものの、仲間の裏切りに遭い、重傷を負ったプロの強盗ポーター。落とし前をつけるべく裏切り者の行方を追う彼は巨大組織や悪徳警官、チャイニーズ・マフィアらを相手取り、危険な戦いに挑む。悪党たちの思惑が交錯する中、７万ドルという決して多くない分け前のために体を張る主人公のこだわりが、男気を感じさせる。敵の脅迫にも決して屈しない硬派な主人公を、M・ギブソンがワイルドに熱演。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛妻物語", "愛妻物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "駆け出しのシナリオライター・沼崎敬太は、日本映画を代表する坂口監督の下で厳しい修業に励んでいる。だが、やっと成功への一歩を踏み出した時、彼を陰で支えてきた妻が病でこの世を去る……。溝口健二に師事した新藤兼人監督が、自らの下積み時代を描いたオリジナル脚本を映画化した、第１回監督作品。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SMOKE JUMPERS", "ファイヤーインフェルノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "森林火災の際、パラシュートで現場に降下して消火活動を行うアメリカ消防組織特殊班、通称スモーク・ジャンパーズ。その隊員であるドン・マッケイの命知らずの活躍と、彼の身を案じる妻との愛と葛藤を、実際に起きた1994年のコロラドの大森林火災をクライマックスに描く。主演は「インデペンデンス・デイ」のA・ボールドウィン。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AMBUSHERS", "サイレンサー第3弾／待伏部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ハミルトンの小説をもとにした、プレイボーイなスパイ、マット・ヘルムの活躍を描くスパイ・アクション・シリーズの第３作。世界初の電磁波推力で動く円盤が完成し、女性パイロットのシーラが搭乗したが、彼女は円盤もろとも行方不明になる。そんなある日、マットの前に記憶を喪失した彼女が現れる。円盤は権力狂の一味に奪われたというが……。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン／世紀末の魔術師", "名探偵コナン／世紀末の魔術師", "", "", "http://ecx.images-amazon.com/images/I/511p3aoiboL._SL210_.jpg", 100, "小さな名探偵、コナンが活躍するアニメ・シリーズの劇場版第３作。ロマノフ王朝から伝わるロシアの秘宝“インペリアル・イースター・エッグ”の一つが日本で発見された。これを狙って、コナンの宿敵、怪盗キッドが参上。ところが、キッドは何者かの銃弾に倒れる。謎を追って古城にやってきたコナン一行に、さらなる危険が迫ってくる……。ライバル、キッドはスクリーン初登場。", "1999-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERIKA，TERRA INCOGNITA", "あめりかてらいんこぐにた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "16世紀ヨーロッパの某国王室。カリブ海を探検し、多くの財宝を略奪してきた一行がインディオの酋長を連れて帰ってきた。異人種を目の前にして好奇の視線を送る王侯貴族たちのなかにあって王女だけが彼を違う目で見ていた。セリフを排し、原色による強烈な色彩で描くエスニックかつ幻想的な一編。", "1988-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DEUXIE｀ME SOUFFLE", "ギャング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 147, "終身刑の宣告を受けて服役している戦前の大物ギャング、通称ギュことギュスターブ・マンダが脱獄した。彼はかつての情婦マヌーシュのところに身を隠そうとする。ちょうどその頃、彼女たちは、マルセイユの大物ギャング、ポールと彼の弟でパリを縄張りとするジョーともめていた。脱獄してきたギュに危機を救われたマヌーシュは、彼を外国に逃がすためマルセイユ近郊の隠れ家に連れていく。ところが、彼は金がないためポールが計画していたプラチナ強奪に加わり、計画は見事に成功。だが、パリ警視庁のブロ警部は、ギュが事件に加わっていたとみて、罠をかける。警察の卑劣なやり方で、プラチナ強奪事件のあらましを漏らしてしまったギュは再び脱獄し……。非情な暗黒街の世界をリアルな映像感覚で描いたフィルム・ノワールの傑作。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みかへりの塔", "みかへりの塔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "200人以上の特殊学級の児童たちを教化する学校を舞台にして、様々な問題の末、山から学校までの水路を力を合わせて完成させる子供たちの姿を、横山準（爆弾小僧の別名）はじめ松竹の名子役を総動員して撮り上げた感動の名作。特にラストの移動撮影は、いまだに語り継がれる必見のシーンだ。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("濡れた二人", "濡れた二人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "若尾文子主演、増村保造監督という黄金コンビによる不倫ドラマ。若尾演じる万里子は、夫との生活に不満を感じている人妻。その不満は彼女を一人旅へと駆り立てた。そして彼女は、田舎町の青年、繁男と出会う。彼の素直さに惹かれた万里子は青年と結ばれる。しかし、彼女を待っていたのは……。不倫の果てに自立の道へ投げ出される女を、カメラはドライに見つめ抜く。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("堀川中立売", "堀川中立売", "", "", "http://image.eiga.k-img.com/images/movie/55039/315x210.jpg?4638", 123, "世界各地の映画祭で上映されて話題を呼んだ「おそいひと」の柴田剛監督が、陰陽師・安倍晴明をモチーフに撮りあげた異色の妖怪アクション。京都でヒモ生活を送っていた信介は、ホームレスのツトムと出会う。ひょんなことから式神にされてしまった彼らは、街を跋扈する妖怪たちと闘うハメになり……。主演は関西インディロック界の重鎮「オシリペンペンズ」のボーカリスト・石井モタコと、「ばかのハコ船」の山本剛史。", "2010-11-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("窓から飛び出せ", "窓から飛び出せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "俳優の大日方伝が製作を手掛ける第１回作品で、原作も兼ねている他、彼の家族が揃って出演している。大家族の徳山家は農業を営み、家畜を飼って生活していた。隣の藤枝家は主人が死に、母と二人の子供の３人で暮らしていたが、母は宗教に金をつぎこんでいた。二つの家族の対立と心の交流を描く。新人時代の香川京子が可愛い。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1000年刻みの日時計・牧野村物語", "1000年刻みの日時計・牧野村物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 222, "東北地方の一農村に惹かれた小川紳介監督による「ニッポン国古屋敷村」に続く長編ドキュメンタリー作品。とはいうものの単なる記録映画ではない。山形県上山市牧野村に13年間もこもり続け、稲の成長や自然との闘いをつぶさに捉え、また土地に伝わる民話をドラマ仕立てで再現。そこに住む人々の大らかな生き方を愛情を込めて描いた、画期的エッセイ映画である。日々野良仕事に携わるものの目とリズムで、土の表情・稲の表情を克明に捉えた前半の撮影成果。職業俳優に村の人々を交えた配役に牧野村伝承の物語が、遠い記憶の原石のように荒々しくシンプルなスタイルでよみがえる後半の演出成果。土とともに生きるという空間軸に“1000年刻み”の時間軸を掛け合わせていくという、ロング・レンジで対象と取り組む小川監督ならではの壮大な試みだ。真のユーモアあふれる人間讃歌。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渋谷怪談２", "渋谷怪談２", "", "", "http://ecx.images-amazon.com/images/I/21WVJKK30ZL._SL210_.jpg", 72, "同時公開された「渋谷怪談」のその後を描いた心霊ホラー。第１作の主人公リエカを姉のように慕っていた女子高生、綾乃が、渋谷の呪われたコインロッカーの存在を知ったことで、悪夢のような出来事を体験していく。「自殺マニュアル」で劇場映画監督デビューを果たした福谷修が、有名な都市伝説をモチーフに恐怖シーン満載の脚本を執筆している。綾乃は、奇妙な事件によって廃人となった家庭教師のリエカから、ロッカーキーを受け取った。その頃、女子高生の間で、あるコインロッカーを使うと恋が実るという伝説が噂されていたが、それを使った者たちは次々と失踪し……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　RE´SIDENCIA", "象牙色のアイドル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "カルト・ホラー「ザ・チャイルド」のナルシソ・イパニエス・セラドールによるサスペンス。森の奥深くにある女生徒ばかりの寄宿学校。厳格な女院長により、校内は異様に緊迫した空気が張り詰めている。院長には溺愛している一人息子ルイスがいたが、女生徒たちは彼と会う機会がほとんどなかった。そんな中、女生徒たちが次々と失踪する……。", "1970-01-01", "2,3,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　ああっ女神さまっ", "劇場版　ああっ女神さまっ", "", "", "http://ecx.images-amazon.com/images/I/51FWMT8WB7L._SL210_.jpg", 111, "", "2000-10-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人生とんぼ返り", "人生とんぼ返り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "マキノ雅広が、1950年の「殺陣師段平」を自らリメイクした作品。物語は、新国劇の盟主・沢田正二郎から“リアルな”殺陣の創造を頼まれた殺陣師の段平が、無学なためにリアリズムの精神をつかめず、それでも女房のお春や娘のおきくの助けを借りて、殺陣の工夫に没頭していくというもの。今回も当時の五社協定の枠を越え、お春役を山田五十鈴が買って出て名演を見せている。また、同年「夫婦善哉」で自らの新生面を切り開いた森繁が段平を好演。浪花のガンコな職人気質の男を、人情味タップリに演じている。他に、娘役の左幸子が死の床に付きながらも殺陣への夢を捨てない段平になり代わり、段平からの口伝えで殺陣をつける、健気な娘に扮し、涙を誘う。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月の出の接吻", "月の出の接吻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "百合子は経済的な理由から愛する作曲家の俊彦との結婚をあきらめ、金持ちの栗本と婚約するが、実家の火事から破談になってしまう。俊彦と百合子は互いに愛し合いながらすれ違いばかりを繰り返す。俊彦が東京で作曲家として大成功した頃、百合子はすっかり彼のことをあきらめてしまったが……。古賀メロディーに乗せて展開するメロドラマ。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("What's Your Number?", "運命の元カレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A TIME TO KILL", "評決のとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "ミシシッピー州のカントンで、10歳の黒人少女が二人の白人青年に暴行を受け、父親のカールは自分の手で犯人を射殺する。新米弁護士として働くジェイクはカールの弁護を務めることになるが、経験の浅い彼にはあまりにも手ごわい事件。南部の小さな街で起きた殺人事件は彼の周りに多くの犠牲者を生み、やがて白人と黒人をめぐる大きな社会問題へと発展していく……。人種差別問題をテーマに描く法廷サスペンス。ベストセラー作家J・グリシャムの小説の映画化で、自身も製作を担当している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AMAZING PANDA ADVENTURE", "リトル・パンダの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "父親が働く中国にやって来た少年ライアン。しかし、２年ぶりの再会を喜ぶ間もなく、事件が起こる。密猟者たちが生まれたばかりのパンダを罠にかけ、次々にさらっていくのだ。そのうえ、父親も銃で撃たれてケガをするが、パンダを救うため、ライアンは次々と襲ってくる危機に果敢に立ち向かう。中国大陸の美しい自然を舞台に、勇気ある少年とパンダの心の交流を描き出す動物アドベンチャー・ストーリー。主演のライアンはクリスチャン・スレーターの弟。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EAGLE AND THE HAWK", "鷲と鷹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "ヨーロッパ戦線の勃発と同時にジェリー、ヘンリー、マイクの３人の友人は英国空軍に入隊、ジェリーとヘンリーは偵察機に同乗することになる。戦いの中で残忍性を見せるヘンリーをジェリーは嫌悪するものの、二人は数々の武勲を立てていく。そんな中で、神経をすり減らしたジェリーは休暇をとるが、その間にヘンリーと組んで偵察機に乗ったマイクが戦死。前線に戻ったジェリーはヘンリーの同乗を拒否して彼の仇を取るために空に飛び立つが……。戦争の悲惨を描いた航空映画。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("離婚しない女", "離婚しない女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "倍賞姉妹が本格的に映画共演、一人の男をめぐり、女同士火花を散らすというラブロマンス。女二人には家庭があり、両者の間を泳ぐように愛をむさぼる男に扮した萩原健一がはまり役。積極的な女に千恵子、男となかなか一線を越えられない女に美津子とこれまでの二人のイメージを逆転させたようなキャスティングが興味深い。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幕末・てなもんや大騒動", "幕末・てなもんや大騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "時次郎と珍念は、今や人気絶頂の勤王の株を先買いしようと、新選組と勤王党の争いが絶えない京都へ向かうが、ひょんなことから坂本竜馬に桂小五郎の密書を届ける役を仰せつかる。かくて二人の珍道中が始まる……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Enemies: A Love Story", "敵、ある愛の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "　ナチスによって妻子を失ったユダヤ人のハーマンは、第2次大戦後に命の恩人でもあった女性ヤドウィガと再婚し、アメリカ・ニューヨークに渡って暮らしていた。彼にはマーシャという愛人もいたが、そんな折に収容所で死んだはずの元妻タマラが出現。3人の女性との間で揺れ動き、苦悩するハーマン。やがてヤドウィガの妊娠が発覚して……。女性たち、そして選択を迫られる男の葛藤を描いた物語。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みんな～やってるか！", "みんな～やってるか！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「その男、凶暴につき」から「ソナチネ」まで、いずれも北野武の死生観を反映させたといわれる前４作とはうって変わって、初めてビートたけしとして監督したお笑い映画。セックスすることしか考えていない男が、妄想を抱き、幼稚だけれども大胆な行動を繰り返すうちに、いつしか自分の意思とは関係なく妙なものに巻き込まれ、とんでもない結末を迎える。好色＆アホ一代男のナンセンスな物語。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RETURN OF THE LIVING DEAD PART II", "バタリアン２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "人を喰ったような冗談感覚とシャープな演出で大ヒットとなったホラー・コメディ「バタリアン」の続編。前作で水爆投下により全滅したと思われていたバタリアン軍団。しかし超生命体である彼らは、やっぱり生きていた。放射能の影響によって、バイオ菌の大気感染力は倍増、人々は次々とバタリアンに……。大量に出現したゾンビたちと、最新兵器で彼らに挑む人類との戦いが、恐ろしくもコミカルに繰り広げられていく。前作のD・オバノンに代わって監督、脚本をこなしたのは、化学者出身の変わりダネ、K・ウィダーホーン。監督は代わっても、冗談感覚は健在である。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使の楽園", "天使の楽園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "ゲイだったアダルトビデオ俳優タカチの追悼パーティーに出席した玲子とシンペイはタカチの思い出を回想していく。美少年ソラオと同棲していたタカチ。だが、ソラオが消えたとき、タカチは誰にも告げず故郷の高知に帰っていったのだが……。タカチと彼を取り巻く人々の記憶と感情の動きを、８ミリ、16ミリ、デジタル・ビデオなど様々な映像素材を組み合わせ、時間や空間を自由に構築して描写。愛を求めて生きる人々の姿を、優しい視点でとらえた青春映画。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PASSION FLOWER HOTEL", "レッスンC", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "N・キンスキーが17歳の頃に主演した青春ラブ・ストーリー。スイスの名門寄宿学校に転入することになったアメリカ娘のデビーは、列車の中で知り合った青年フィリップスと意気投合。彼は隣の名門男子校に通っていたが、お互いの校則が厳しく、自由に会うことが出来ない。そこで、デビーは友人たちと“自由恋愛クラブ”を結成、男子校と連絡を取り合い、男女のデートのチャンスを作る。", "1978-01-01", "7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　GOD MUST BE CRAZY PART ２", "コイサンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "コイサンマンとはブッシュマンの現地語での呼び名。日本でも人気を博したあのニカウが７年ぶりに再登場した「ミラクル・ワールド　ブッシュマン」に続く“ブッシュマン”シリーズ第２作。今回は行方不明になった二人のわが子を捜して大活躍する。これに飛行機の不時着事故で砂漠に迷い込んだ文明人、そして密猟者が入り乱れ、ユーモアたっぷりの物語が展開する。ビデオ発売時タイトルは「ブッシュマン２　コイサンマン」。DVDタイトルは「コイサンマンII」。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青年の椅子", "青年の椅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "源氏鶏太原作による石原裕次郎主演のサラリーマンもの。日東電機の高坂虎彦は、湯浅営業部長を失脚させて社を食いものにしようとする陰謀があることを知る。高坂と取引先の畑田商会社長は湯浅を守ることを誓い合う。一方、取り引き先の社長令嬢、美沙子とタイピストの十三子は互いに高坂に好意を寄せていた。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hello My Love", "Hello, My Love", "", "", "http://image.eiga.k-img.com/images/movie/56939/315x210.jpg?2959", 95, "男女の三角関係と同性愛を描くラブストーリー。快活なトークで人気のラジオDJホジョンには、付き合って10年になる恋人ウォンジェがいる。パリに料理留学に出ていたウォンジェが帰国し、あとはプロポーズを待つだけだったが、ウォンジェは一緒に帰国したイケメンの後輩とワインバーの開店準備に忙しい。しかも2人の仲がただならぬ雰囲気で……。韓国映画の名作や最新作を一挙上映する「K-MOVIEセレクション2011」にて上映。", "2011-10-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WATCHER IN THE WOODS", "呪われた森", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "森の中の古い邸宅へ引っ越してきた17歳のジャンの身辺に不思議な現象が次々と起こる。それは30年前に姿を消した少女カレンのSOSのサイン。そのカレンを呼び戻すためにジャンは……。ハフ監督のオカルト・ミステリー。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワースト☆コンタクト", "ワースト☆コンタクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "筋金入りのヤクザの前に現れた、宇宙人を名乗るオッサン。“地球を滅ぼしにきた”と語る彼は、果たしてホンモノなのか。哀川翔と板尾創路の異色コンビで贈る、シュールなギャグが満載のちょっと切ないイイ話。監督は「Believer〈ビリーバー〉」で長編デビューを果たした俊英、多胡由章。生垣幸太郎は“死神”の異名をとる筋金入りの極道。麻薬の取引を控えた大切な時に、ホームレスにしか見えない自称“宇宙人”が現れ……。", "2005-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITY ON FIRE", "シティ・オン・ファイア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "大火災の恐怖を描いたB級パニック映画。職場を追われ復讐心に燃える男が、石油精製所のタンクから精製ずみの石油を下水道に放流。市内を網の目のように流れる下水道を伝って、石油が都市の隅々に行きわたる。そこへ工場現場の火花が引火し、町は炎に包まれ人々はパニックに陥る。", "1979-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀河鉄道の夜 I carry a ticket of eternity", "銀河鉄道の夜 I carry a ticket of eternity", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "宮沢賢治の名作童話を、舞台を現代に置き換えて実写映画化したファンタジックドラマ。病気の母を支え、アルバイトをしながら高校に通うジョバンニ。ある日、彼の元に小学校の同窓会の知らせが届く。小学校時代、クラスメイトからいじめられていたジョバンニは、いつも友人のカムパネルラに助けられていた。同窓会には行きたくないがカムパネルラとは再会したいと悩むジョバンニの前に、銀河鉄道に乗った小学生のカムパネルラが姿を現わす。", "2006-11-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRISONER OF ZENDA", "ゼンダ城の虜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "A・ホープの同名小説の２度目の映画化。19世紀末期。ヨーロッパの某国は、領主ルドルフ大公の戴冠式を控え、熱気を帯びていた。だが、大公の異母兄マイケルが、王位を奪おうと画策。たまたま釣りにやって来た英国紳士ラッセンディルは、大公と瓜二つだったため、麻酔薬で眠らされている大公の代わりに戴冠式に出席することになる。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A LIFE IN THE THEATER", "ライフ・イン・ザ・シアター 栄光と喝采の日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "ニューヨーク・ブロードウェイを舞台に、やる気に満ちた若手のハンサム俳優ジョンと、キャリア豊富な初老俳優ロバートの演劇人としての人生の表舞台とバックステージでの生活を描いた、笑いと感動のヒューマン・ドラマ。「摩天楼を夢見て」でピュリッツァー賞を受賞するなど、第一級のハリウッド映画を手掛けているD・マメットが原作・脚本を担当。TVフィーチャー作品。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Warriors", "ウォリアーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "　夏の夜、ブロンクス公園にニューヨーク各地からストリートギャングたちが集結。彼らは休戦協定を結んでいたが、その頂点に立つサイラスが何者かによって射殺されたことで事態は一変する。サイラス殺害の罪を着せられたウォリアーズのメンバーは、グラマシー・リフス、ベースボール・フューリーズといったギャングから命を狙われながらも、自分たちのシマであるコニーアイランドを目指し、決死の逃亡を試みる……。ウォリアーズの死闘を描いた緊迫のサスペンス・アクション。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コンクリート", "コンクリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1988年に発生した女子高生拉致監禁殺害事件の内情を描いたノンフィクション小説を映画化。当初、劇場公開が予定されていたが、内容を巡って一部で激しい公開阻止運動が起こったため、一度は公開が中止になったことでも話題となった作品。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLORUM", "白タク", "", "", "http://image.eiga.k-img.com/images/movie/54979/315x210.jpg?4610", 109, "フィリピン・インディーズ映画界で活躍する新鋭ジョビン・バレステロスが監督・脚本を手掛け、同国内で高い評価を受けたロードムービー。前途有望な警官でありながら、非合法な公益車のドライバーのアルバイトをしている青年シモン。ある日、運転中に人をはねてしまった彼は、事件の目撃者で刑務所から出所したばかりの老人ペドロを連れ、フィリピン東海岸を縦断する自動車旅行へ出かけることになるが……。", "2009-10-19", "35", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺殺人・跳跳舞", "ドニー・イェン／COOL", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「HERO」のD・イェンが監督・主演したハード・アクション。目にもとまらぬ速度で炸裂する肉弾バトルや銃撃戦とともに、徹底的にスタイリッシュに仕上げた映像センスも見逃せない。ニューヨーク市警時代に同僚ウェズリーに裏切られ、殺し屋に転身した孤独な男キャット。因縁のウェズリーと思わぬ再会を果たし、復讐に燃える彼だったが、自分を追う女刑事と恋に落ちてしまい……。", "1998-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("7鉱区", "第7鉱区", "", "", "http://image.eiga.k-img.com/images/movie/55479/315x210.jpg?3140", 101, "洋上の石油採掘船の乗組員たちが、謎の巨大生物と死闘を繰り広げる姿を描いた3Dアクション。1970年代、石油ボーリング船エクリプス号が東シナ海の日韓共同開発区域「第7鉱区」で調査作業を行っていた。しかし、ある日突然、本部との通信が途絶え、海の上で孤立した乗組員たちが次々と何者かに襲われていく。やがて巨大な謎の深海生物が姿を現すが、その正体には驚くべき秘密が隠されていた。「光州5・11」のキム・ジフンがメガホンをとり、「TSUNAMI ツナミ」のユン・ジェギュンがプロデュース。主演は「デュエリスト」「TSUNAMI ツナミ」のハ・ジウォン。", "2011-11-12", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("果てしなき情熱", "果てしなき情熱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "作曲家の三木は三文キャバレーで働いていた。歌手の福子とウェイトレスのしんは彼に想いを寄せていたが、彼には忘れられない女がいた。服部良一のヒット曲『湖畔の宿』『夜のプラットホーム』『雨のブルース』などがストーリーの展開につれて流れ、主人公の心の内を物語る。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デスノート the Last name", "デスノート the Last name", "", "", "http://image.eiga.k-img.com/images/movie/33729/315x210.jpg?4298", 140, "死神が落とした“死のノート＝デスノート”で、キラとして犯罪者たちを裁く夜神月と名探偵Lとの対決は、死神レムともう1冊のデスノートの登場により、予想のつかない事態へと発展してゆく。「週刊少年ジャンプ」に連載され、圧倒的な人気を巻き起こしたカリスマ・コミックの実写版第2弾。前作に引き続き主人公に藤原竜也、名探偵Lに松山ケンイチ、第2のキラとなる戸田恵梨香、その他に新たなキャストが加わって驚愕のラストを迎える。", "2006-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("本気〈まじ〉！", "本気〈まじ〉！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "『週刊少年チャンピオン』に連載されていた同名コミックの映画化で、任侠の世界で本物の男へと成長していくチンピラの生き様をさわやかに描いた青春ヤクザ映画。渚組に属するチンピラ・本気は、純粋な心を持つ駆け出しのヤクザ。そんな彼が、清純な女子校生・久美子に恋し、仲は深まっていくが……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブファイト", "ラブファイト", "", "", "http://image.eiga.k-img.com/images/movie/53749/315x210.jpg?2843", 126, "まきの・えりの小説「聖母少女」を、「油断大敵」「ミッドナイトイーグル」の成島出監督が映画化。主演に「バッテリー」「ダイブ!!」の林遣都、「幸福な食卓」「ゲゲゲの鬼太郎／千年呪い歌」の北乃きい、共演に大沢たかお、桜井幸子。大沢たかおがプロデュースとしても参加。いじめられっ子の稔は幼馴染の亜紀に助けられてばかりの軟弱な少年。才色兼備な亜紀に憧れる男子たちに追い回される稔は、密かにボクシングを習い、亜紀よりも強くなろうとするが……。", "2008-11-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人形のいる風景　ドキュメント・オブ・百鬼どんどろ", "人形のいる風景　ドキュメント・オブ・百鬼どんどろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "自作の等身大人形を使った妖艶なパフォーマンスで世界的に高く評価されてきた人形劇団「百鬼どんどろ」の主宰・岡本芳一を追ったドキュメンタリー。2006年、下北沢の映画館における公演の記録を引き受けた渡邊世紀監督が、これをきっかけに密着取材を開始。岡本や関係者へのインタビューをはじめ、満開の夜桜の下で上演された長野公演の模様、そして信州伊那谷にあるアトリエ“どんどろハウス”の風景などを通し、その独特の魅力に迫る。", "2008-09-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLEEPING WITH THE ENEMY", "愛がこわれるとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ジュリア・ロバーツ主演のスリラー。周囲からは理想のカップルに見られたマーチンとローラの夫婦。しかし、マーチンは異常なまでの潔癖症でサディスティックな性格だった。夫の仕打ちに耐えかねたローラは、ヨットでの海洋事故を擬装してマーチンのもとを去り、名前を変えて他の街で暮らしはじめるが……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ORGY OF THE DEAD", "死霊の盆踊り", "", "", "http://ecx.images-amazon.com/images/I/31K1FK0VXCL._SL210_.jpg", 90, "そのあまりにもバカバカしい内容が、逆にマニアの間で話題となった“幻のカルト・ムービー”。物語は、ある墓場に迷い込んだ一組のアベックが闇の帝王に捕まり、死んだ美女たちの延々と続く裸踊りを無理矢理見せられるというもの。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　夢猫の国のニャニイ", "それいけ！アンパンマン　夢猫の国のニャニイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "アンパンマンと魅力的な仲間たちが愉快な冒険を繰り広げる、人気TVアニメの劇場版第16作。100年に１度だけ生まれる特別な夢猫ニャニイを、故郷のふしぎな夢猫の国へ連れ戻そうと奮闘するアンパンマンの活躍を、宿敵・ばいきんまんとの戦いを交え、夢いっぱいの世界観で描いていく。クライマックス、魔法の光線によって猫に変えられてしまうアンパンマンたちの変身シーンは、必見。ドリーム彗星がアンパンマンたちの世界に近付くある日、不思議な卵の夢を見たメロンパンナちゃん。目覚めるとそこには、夢に出てきた卵が！　中から飛び出した子猫のニャニイは、100年に１度だけ生まれる特別な夢猫だった。", "2004-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLIFFHANGER", "クリフハンガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "S・スタローンがR・ハーリン監督と手を組んだ山岳アクション。救助ミスで同僚ハルの恋人を死なせてしまったレスキュー隊員のゲイブは、ショックから山を去る。そんなある日、ロッキー山脈からSOSが。ハルは一人で救出に向かうが、それは雪山に散った３個の現金入りのトランクを回収しようとする犯罪者集団の罠だった。元恋人のジェシーに叱咤激励されてゲイブはハルの後を追うが、ともに一味に捕らえられてしまい……。特撮を最小限に止め、ライブ感覚にこだわった臨場感たっぷりの映像にハラハラさせられる。筋骨隆々のロッククライマーに扮するスタローンも、久々のハマリ役。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パルムの樹", "パルムの樹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "女戦士から謎の卵を地底世界まで届けるよう頼まれた人形のパルムは、仲間を増やしながら旅を続けていく。「幻魔大戦」や「AKIRA」の原画を担当したなかむらたかし監督が満を持して発表したファンタジー・アニメ。単なる冒険物語ではなく、主人公パルムの心の戸惑いや残酷さなども隠さず描くことで、魂の孤独や弱さをも描出していく画期的な意欲作に仕上がっている。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COLOR PURPLE", "カラーパープル", "", "", "http://ecx.images-amazon.com/images/I/213F5ZQ4T0L._SL210_.jpg", 153, "ピュリッツァー賞を受賞した原作を、スピルバーグが壮大なスケールで映画化した力作。ある黒人姉妹の強い絆と不滅の愛で彩られた40年の歴史を、一大叙事詩として描きながら、人間が自分自身に目覚める精神的成長の道程を深く追究した作品である。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジェームス山の李蘭", "ジェームス山の李蘭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "公金を横領した日系アメリカ兵ポールとそのハウスボーイ、葉介。罪から逃れるために神戸に流れてきた彼らは、そこでクラブを開き、盛況を得る。気をよくし、ギャンブルに情熱を注ぐ葉介は、あるポーカー大会で美貌の中国人女性と出会うが……。男女のサスペンスノリの愛を含んだギャンブル・ドラマ。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シルクハットの大親分", "シルクハットの大親分", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "“緋牡丹博徒”シリーズの人気親分・熊虎を主人公に仕立てた任侠シリーズ第１作。お竜さんこと藤純子も特別出演している。日露戦争から帰ってきた熊虎親分は、大陸で軍の高官と結託して金品をピンハネしていた鎮台一家と対立。多大な犠牲を払ったあと、お竜と二人で鎮台に斬りかかっていく。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEAVING LAS VEGAS", "リービング・ラスベガス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ラスベガスを舞台に、死ぬことを決めたアル中の男と娼婦との絶望的な愛の物語を、実話をもとに描く。ハリウッドの脚本家として活躍していたベンは、酒のために会社をクビになり、妻も子も彼の元を離れてしまっていた。絶望したベンは死ぬまで酒を飲むことに決め、全財産を持ち、ラスベガスへやって来る。モーテルに部屋を借りたベンは、町で拾った娼婦のサラを部屋に連れ込む。サラは、どこか優しいベンに惹かれるものを感じていた。暴力的なユーリと怯えて暮らしていたサラには、ベンの優しさが忘れられなかったのだ。やがて、身の危険を感じたユーリが町を離れ、自由となったサラはベンの部屋を訪れる。ベンはサラの家に暮らすことに同意するが、「決して酒をやめろと言うな」という条件を出す。その言葉通りベンは酒をやめなかった……。アカデミー賞とゴールデン・グローブ賞の主演男優賞をはじめ、数々の賞に輝いたN・ケイジが、ベンを熱演。また、E・シューがケイジに負けない熱演を見せる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOOD NEIGHBOR SAM", "ちょっとご主人貸して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "ファンタジー小説の第一人者J・フィニーの『良き隣人サム』の映画化。広告代理店に勤めるサムが隣に引っ越してきた美女をめぐり、トラブルに巻き込まれていく。笑いを散りばめた脚本と軽妙なタッチの上質のアメリカン・コメディである。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Notorious", "ノトーリアス・B.I.G.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Constantine", "コンスタンティン", "", "", "http://image.eiga.k-img.com/images/movie/1039/315x210.jpg?3208", 121, "鬼才アラン・ムーアのコミック「ヘルブレイザー」を原作に、キアヌ・リーブスがオカルト探偵を演じるVFXアクション。悪魔や天使を見る特殊能力を持つ探偵コンスタンティンは、女性刑事アンジェラと共に彼女の妹の死の真相を探るが……。堕天使役でティルダ・スウィントンが共演。監督はMTV出身で本作が映画初挑戦となるフランシス・ローレンス。撮影は「リバー・ランズ・スルー・イット」でオスカー受賞のフィリップ・ルスロー。", "2005-04-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・レイプ", "ザ・レイプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "強姦されたことを告訴した女性が、論告求刑までの裁判の過程で受ける屈辱的な苦しみと、傷つきながらも人間として成長していくさまを描いた、東陽一監督の社会派系の女性映画。主演の田中裕子が硬軟両用の演技で魅せるほか、恋人役の風間杜夫も好演。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("口裂け女2", "口裂け女2", "", "", "http://image.eiga.k-img.com/images/movie/53569/315x210.jpg?2980", 98, "1978年に岐阜県で起こった連続殺人事件。その事件後に流れた噂をモチーフに口裂け女の誕生秘話を描いたノンフィクションホラー。岐阜県で養鶏場を営む沢田家の三姉妹。結婚を控える長女の幸子（川村ゆきえ）、自立して美容室で働く雪枝（岩佐真悠子）、陸上部に所属し活発に学生生活を送る三女・真弓（飛鳥凛）はそれぞれに幸せな日々を過ごしていたが、雪枝の昔の恋人・鈴木が雪枝と間違え真弓に硫酸をかけたことから、沢田家の運命が狂っていく。", "2008-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MADNESS OF KING GEORGE", "英国万歳！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "A・ベネットの大ヒット舞台劇の映画化。舞台と同じく、N・ホーソーンが錯乱していく国王を好演し、アカデミー主演男優賞にノミネートされた。舞台の演出も手掛けた「クルーシブル」のN・ハイトナーが、英国王室で起こった悲喜劇を痛烈な皮肉を込めて描き出し、華麗な監督デビューを飾った。1788年、国民から親しまれていた国王ジョージ三世に異変が起こり始める。早朝に侍従たちをたたき起こしたり、突然女官長に抱きついたりする国王は周囲を混乱に陥れ、とうとう隔離されてしまう。王妃は悲しみに暮れ、王位を狙う皇太子はひそかに一計を案じ始め、まさに王室は混乱状態。そんな中、一人の医師が国王を回復させるため強行手段に出る。", "1994-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERO？天使に逢えば…", "HERO？天使に逢えば…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ツイてない男女と、ツキまくってるカップル。二組の出会いを通して、運、不運が織りなす不思議な人間模様を描く作品。負け続けのボクサーと失敗続きのOL。そんな二人が付き合い始めるものの、自分たちの運のなさに拍車がかかる？ と疑心暗鬼になり、旅先で知り合ったノリノリの恋人たちに運命を託すようになる様が、楽しく綴られていく。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Offside", "オフサイド・ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/34159/315x210.jpg?1506", 92, "女性のサッカー観戦が法律で禁止されているイランを舞台に、男装してスタジアムに潜り込んだ少女たちの姿をユーモラスに綴るハートフル・ストーリー。ドイツ・ワールドカップへの出場を賭けた大事な試合に忍び込もうとした1人の少女。入り口であえなく兵士に見つかってしまった彼女は、同じように捕らえられた少女たちと一緒にスタジアム内に勾留されるが……。監督は「チャドルと生きる」のジャファル・パナヒ。", "2007-09-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長血笑記 殴り込み道中", "次郎長血笑記 殴り込み道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "偽者の石松が起こした騒動がもとで旅に出ることになった次郎長一家。道中、立浪一家に襲われる雪太郎を救ったりしながら彼らは追分の三五郎の家にたどり着く。一家をもてなす金を作ろうとした三五郎は、知らずに立浪一家に縁のある賭場に出かけてイカサマに遭う。それを知った次郎長らは殴り込むのだが……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("While you were sleeping", "あなたが寝てる間に…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　「スピード」でタフなヒロインを演じたサンドラ・ブロックが憧れの男性の名前さえ聞けない、純情な女性を演じる。地下鉄の改札で働くルーシーは、毎日決まった時間に改札口を通る男性にときめいていた。クリスマス・イブの日、憧れの彼が不良にからまれ、ホームに落ちてしまう。列車が滑り込む寸前、彼を救ったルーシーだったが……。誤解が誤解を呼び、真実を告げられずに右往左往するサンドラがキュート。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春ばかちん料理塾", "青春ばかちん料理塾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "人気アイドル・後藤真希が主演するヒューマン・コメディ。エプロン姿のゴマキが料理に悪戦苦闘する姿がキュートだ。目標が見つからず、高校を中退した瞳。不安な毎日を過ごす彼女は、顔も知らないメル友“あゆみちゃん”とのメールが心の支えになっていた。そんな折、あゆみちゃんのメールがきっかけで料理教室に通い始めた瞳は、中年サラリーマンや元スッチーなど、ちょっと変わった面々のグループに入る。やがて、グループ対抗の料理コンテストが開かれることになり、瞳はメンバーと一緒に挑戦する。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Triangle", "トライアングル", "", "", "http://image.eiga.k-img.com/images/movie/55409/315x210.jpg?1906", 92, "日本人脚本家と韓国のスター俳優がタッグを組む、日韓共同ドラマプロジェクト「テレシネマ7」シリーズ。人気ドラマ「特命係長・只野仁」の尾崎将也が脚本を手がけた一作。かつて大企業の社長夫人だったジヨンは、亡き夫との思い出の場所になったスキー場を訪れていた。そこで美術展などを企画する小さな会社を経営するサンウと出会い、夫が美術品のコレクターだったジヨンは意気投合する。そんなとき、ジヨンの幼なじみだと名乗る女性ソンヘが現れるが、ジヨンはどうしても思い出せずにいた。", "2010-05-29", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水戸黄門海を渡る", "水戸黄門海を渡る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "松前藩の御用船から蝦夷地の測量図が奪われた。仙台に立ち寄っていた水戸黄門の一行は、真相を求めて蝦夷に渡る。そこでは幕府転覆の陰謀が……。長谷川一夫が水戸黄門とアイヌの大酋長の二役を演じている。馬に乗ったアイヌの軍勢など西部劇調の娯楽時代劇。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIRL’S TOWN", "非情の青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "非行少女たちの様々な行為を描く青春映画。過失致死を犯した妹の身代わりとなって感化院“少女の町”に入れられたシルバーは、院内で何かにつけて問題を起こす。彼女の唯一の友であるセラフィナは、歌手のジミーに恋するあまり自殺を図る。そして、シルバーの妹も、悪事に誘われようとしていた。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏祭り", "夏祭り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "夢破れた男とある女性演歌歌手が、旅先で知りあったちんどん屋の一座や、地元の人々と行動する様を描いた異色作。大衆食堂でのバカ騒ぎ、現役の音頭取り一派による河内音頭、300人のエキストラを導入した盆踊り大会など盛りだくさんな見せ場に、関西人の独特な感性が如実に反映していく。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("K○YA　澄賢房覚え書", "K○YA　澄賢房覚え書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "現代女性の眼を通して、聖僧、破壊僧、それぞれの姿を描いた伝奇ロマン。“女人禁制と女犯”というテーマの論文を書くため、美術史家・秋月洋子は、高野山を訪れる。彼女は、資料から、女犯の戒に触れて山を追われた、破壊僧・澄賢の存在を知った。洋子は、澄賢が書いた、“理趣経”に興味を覚えて……。名取裕子が、時空を超えて澄賢と対面する洋子をはじめ、４役を演じている。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A PRAYER FOR THE DYING", "死にゆく者への祈り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "恋人との愛に安らぎを見いだしていた元テロリストのファロンは、彼女を救うために最後の戦いに出る。『鷲は舞い降りた』で知られるベストセラー作家J・ヒギンズの同名小説の映画化。重い運命を背負った孤独な元テロリストの役をM・ロークが好演。S・デイヴィス、A・ベイツら彼を支えるキャスティングも魅力的。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("流氓醫生／MACK　THE　KNIFE", "裏街の聖者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "日本のコミック『Dr.くまひげ』をベースに、「不夜城」のリー・チーガイがメガホンを執った人情喜劇。主人公は、貧しい人のために小さな診療所を営む心優しい町医者マック。大病院絡みの騒ぎに巻き込まれた彼の災難や、彼のもとに集まる若き研修医や娼婦、重病の娘や刑事ら下町の人間模様を温かく描き出す。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Un condamné à mort s'est échappé ou Le vent souffle où il veut", "抵抗　死刑囚の手記より", "", "", "http://image.eiga.k-img.com/images/movie/55069/315x210.jpg?4182", 97, "「バルタザールどこへ行く」「スリ」で知られる巨匠ロベール・ブレッソンの脱獄ドラマ。1943年、ドイツ占領下のフランス・リヨン。仏軍レジスタンスのフォンテーヌ中尉は、独軍に捕らえられ、モントリュック刑務所に入獄する。拷問され、独房に入れられたフォンテーヌは即座に脱獄を決意。囚人と情報交換をし、スプーンで作ったナイフを武器に着々と準備を進めるが、仲間のオルシニが脱獄に失敗し銃殺されてしまう……。", "2010-03-20", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STACY’S KNIGHTS", "ギャンブラーズ／最後の賭け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "新進俳優時代のK・コスナー28歳の主演作。ウィルは才能のある内気な女性ギャンブラー、ステイシーにギャンブルの実践術をコーチする。ウィルのリードによって彼女はカジノで敵なしのギャンブラーに成長するが、カジノのボスはウィルを殺す指令を出した。スリリングで痛快な娯楽作。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Prezít svuj zivot", "サヴァイヴィング・ライフ　夢は第二の人生", "", "", "http://image.eiga.k-img.com/images/movie/56449/315x210.jpg?4697", 108, "シュルレアリスムの巨匠ヤン・シュバンクマイエルが、前作「ルナシー」以来5年ぶりに手がけた長編。うだつのあがらない中年サラリーマン、エフジェンの楽しみは寝ることくらい。ある日、夢の中でエフジェニエという若く美しい女性と出会うが、そこに息子が現れて気まずい思いをしてしまう。夢の内容が気になるエフジェンは精神分析医のカウンセリングを受け、さらには自分の意思で夢の世界に入っていく方法を見つけるが……。", "2011-08-27", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Antichrist", "アンチクライスト", "", "", "http://image.eiga.k-img.com/images/movie/55809/315x210.jpg?3607", 104, "「ダンサー・イン・ザ・ダーク」のラース・フォン・トリアー監督が、ウィレム・デフォーとシャルロット・ゲンズブール主演で描くサイコスリラー。セックスの最中に幼い息子を事故で亡くした夫婦。愛する息子の死をきっかけに心を病んでしまった妻を療養させるため、セラピストの夫は彼女を森の山小屋へと連れて行くが……。2009年カンヌ国際映画祭コンペティション部門に出品され、過激な性描写などで物議を醸した問題作。", "2011-02-26", "18,7,2,14,4,42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・影の軍団 序章", "新・影の軍団 序章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1980年から85年にかけて放送された人気シリーズが18年ぶりに復活。本作では服部半蔵の弟子である忍者を主人公に据え、お家騒動を描く。豊臣太平の時代、河内家の篠忠は和平のため、ライバルの実葛家に妹、藤姫を嫁がせようとする。しかしその直前に篠忠が病に倒れてしまった。藤姫の身に迫る危機を防ごうと、半蔵らが立ち上がる。", "2003-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("麻雀飛翔伝　哭きの竜　外伝1", "麻雀飛翔伝　哭きの竜　外伝1", "", "", "http://image.eiga.k-img.com/images/movie/56719/315x210.jpg?4164", 68, "能條純一の人気麻雀コミック「麻雀飛翔伝　哭きの竜」シリーズの「外伝」を2部作で映画化した前編。関東最大の組織・桜道会が内部抗争を繰り広げていた時代。「哭きの竜」と呼ばれる伝説の雀士・竜は、ヤクザたちの抗争に巻き込まれ凶弾に倒れてしまう。主演は「仮面ライダー響鬼」ほか多くの平成仮面ライダーシリーズに出演している松田賢二。", "2011-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZAPPED！", "超能力学園Z", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "科学の実験中に、ふとしたはずみで超能力を身につけてしまった少年が、その念力を使って巻き起こすドタバタ大騒動。念力パニックのシーンでは、「スター・トレック」「キャリー」「アニマル・ハウス」などのパロディーも楽しめるSF学園コメディ。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　MAGIC　TAUCH", "マジック・タッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「Mr.BOO!」シリーズで名を上げた伝説のコメディアン、M・ホイが主演し自ら監督もこなしたジェットコースター的展開が楽しい超絶コメディ。香港四天王レオン・ライが笑いを誘う妙演を披露する。祖父は清の最後の皇帝、父は毛沢東のそれぞれ骨を占う高名な占い家系に育った三代目の青年。しかし、全く当たらないと不評だった。そこで、税務局員を利用する起死回生の方法を思い付く。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("懲役十八年", "懲役十八年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "名匠・加藤泰監督が時代劇から任侠路線に移る過渡期に撮った「男の顔は履歴書」「阿片台地・地獄部隊突撃せよ」に続く安藤昇主演の３部作の一編。敗戦後の混乱期、戦没者の遺族を救済することで自らの戦争責任を全うしようとする川田と同じ志を抱きながら、次第に彼らを食い者にして戦後を生き延びようとする塚田の二人の復員兵の生き方とその対決が、戦中世代の表裏一対を成す精神として鮮烈に描かれている。独特のローアングル、長回しを主体とする綿密な画面設計が見事で、女たちの割烹着と、ヤミ米の白で埋まる遺族会の集会所や、真紅の囚人服と吹き出す血に染まる刑務所など色彩処理も秀逸。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トップドッグ", "トップドッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1987年のロードレース世界グランプリの熱戦から、平忠彦、W・ガードナー、R・マモラ、E・ローソンらを中心に、チャンピオンを目指すライダーたちの姿を追ったドキュメンタリー。「プライド・ワン」で製作総指揮を務めた高木巌が、本作では製作・監督にあたった。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRATAN", "少年、機関車に乗る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "土を食べる奇癖を持つ７歳のアザマットと、彼を叱りながらも優しく見守る17歳のファルー。そんな兄弟が遠く離れて暮らす父に会うため貨物列車に乗って繰り広げる、旅の道程をほのぼのと綴った、素朴なタッチのロード・ムービー。中央アジア、タジキスタン（旧ソ連）の映画としては、日本初の劇場公開作となった。", "1991-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EISENSTEIN", "エイゼンシュテイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ロシアの巨星セルゲイ・エイゼンシュテインの生涯を描いた伝記映画。若くして「戦艦ポチョムキン」で並々ならぬ才能を知らしめたエイゼンシュテインが、スターリン政権下で苦悩し、心臓発作に倒れるまでの波乱に満ちた生き様をたどる。明らかに低予算で作られたとわかる作品だが、「アレクサンドル・ネフスキー」などの撮影風景を再現するなど、エイゼンシュテインの型破りな人物像を掘り下げようとした作り手の熱意が伝わってくる。", "2000-01-01", "7,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Haute Tension", "ハイテンション", "", "", "http://image.eiga.k-img.com/images/movie/23349/315x210.jpg?2973", 91, "あまりにも残虐な殺戮シーンのため日本での公開が長い間見送られていたフランス産スプラッター・ホラー。親友アレックスの実家で週末を過ごすことになった女子大生マリー。ところがその夜、突然訪ねてきた謎の男がアレックスの両親と弟を惨殺、さらにトラックでアレックスを連れ去ろうとする。マリーはなんとか彼女を救おうとするのだが……。殺人鬼に立ち向かう主人公マリーを「ロシアン・ドールズ」のセシル・ドゥ・フランスが熱演。", "2006-08-26", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Absolute Beginners", "ビギナーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "デビッド・ボウイ、シャーデーら多数の人気ミュージシャンが出演したことで話題になった青春映画。1950年代ロンドンのソーホーを舞台に、“ビギナーズ”と呼ばれる若者たちの姿をミュージカルタッチで描く。監督は、映画「セックス・ピストルズ／グレート・ロックンロール・スウィンドル」や、ローリング・ストーンズ、デビッド・ボウイ等多くのミュージック・クリップを手掛けたことで知られるジュリアン・テンプル。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Captivity", "キャプティビティ", "", "", "http://image.eiga.k-img.com/images/movie/34189/315x210.jpg?1544", 96, "「24　TWENTY FOUR」で主人公ジャック・バウアーの娘キムを演じたエリシャ・カスバート主演による密室サバイバル・スリラー。何者かに誘拐され、見知らぬ部屋で目を覚ましたトップモデルのジェニファー。自分が密室に監禁されていることを知った彼女は、何とか逃げ出そうとするものの失敗してしまう。やがて隣室にも1人の青年が閉じ込められていることに気づいたジェニファーは、彼と協力して再び脱出を図るが……。", "2007-09-15", "1,24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダーアマゾン", "仮面ライダーアマゾン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 24, "南米アマゾンからやって来た仮面ライダーアマゾンの戦いを描いた特撮テレビシリーズ「仮面ライダーアマゾン」の第16話「ガランダーの東京火の海作戦!!」を、「東映まんがまつり」の1編として劇場公開。ガランダー帝国のゼロ大帝が、東京火の海作戦を発動させた。実行を命じられたゲンゴロウ獣人は、火薬工場やガソリンスタンドを次々と爆破していく。アマゾンは計画を阻止するべく立ちむかうが……。", "1975-02-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冬の日", "冬の日", "", "", "http://image.eiga.k-img.com/images/movie/56289/315x210.jpg?2911", 27, "国内外で活躍するクリエイターら10人が次世代のクリエイターを発掘・育成するプロジェクト「コ・フェスタPAO」で選出された、期待の若手監督3人による中編映画を一挙上映する「movie PAO」の1本。カメラマンになる夢をあきらめ、実家の写真館に帰ってきたリサは、ある雪の日に現れた1人の女性と出会う。彼女が抱える秘密を知ったリサは……。長澤まさみ、風雪ジュンが主演。監督はNHKで「わかば」「火の海」「セカンドバージン」などドラマ演出を手がけてきた黒崎博。", "2011-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪のコンチェルト", "雪のコンチェルト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "元フォーリーブスの江木俊夫が初プロデュースした青春映画で、スキーに熱中するあまり、定職も持たずに雪を求めて世界中を放浪する男たち“スキー・バム”の友情と恋を描く。トレンディ・ドラマで人気の出た風間トオルと元プロ野球選手の異色タレント亜仁丸レスリーが主人公のスキー・バム・コンビを演じている。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・幸福の青い鳥", "男はつらいよ・幸福の青い鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "筑豊で昔なじみの旅役者の娘・美保と知り合った寅さん。上京した美保の就職を世話するなどハッスルするが、美保に思いを寄せる画家志望の青年が現れて……。長渕剛が挿入歌も歌っている。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Toy Story in 3D", "トイ・ストーリー 3D版", "", "", "http://image.eiga.k-img.com/images/movie/54939/315x210.jpg?2861", 81, "「トイ・ストーリー」（95）のデジタル3D版。木製カウボーイ人形のウッディは、アンディの宝物だった。しかし、6歳の誕生日に最新式のアクション人形バズ・ライトイヤーがやって来ると、アンディはバズに夢中になってしまう。アンディを取られたことで、バズに執拗な対抗心を抱くウッディ。しかし、ひょんなことから危険な人間の世界へ飛び出してしまった2人は、アンディのもとへ帰るため、協力しながら友情を深めていく。", "2010-02-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pirates of the Caribbean 5", "パイレーツ・オブ・カリビアン5（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2014-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A STAR IS BORN", "スタア誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 176, "1937年に、フレデリック・マーチ、ジャネット・ゲイナーで作られた「スター誕生」のリメイク。前作は、ハリウッドの愛と栄光と挫折の物語をシリアスに描き、味のある作品だった。今回は、ガーランドの持っているエンターテイナーとしての資質を存分に生かした構成で、基本的な話は変わりないが、よりミュージカルの要素が強くなっている。製作費600万ドルをかけたシネマスコープ・サイズの超大作で、上映時間２時間34分であったが、公開当初は20分近くカットされた。1983年に監督了解のもとに復元された完全バージョンを公開。ミュージカル・ナンバーが追加され、フィルムの紛失部分はスチール構成になっている。前座歌手エスターはある劇場のショーで、アル中で落ち目のスター、ノーマンと知り合い、映画入りを勧められる。ノーマンの指導を受け、彼女は一気にスターとなり、二人は結婚。しかし、エスターの人気と裏腹に、落ちぶれていくノーマンは、入水自殺する……。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("玄海つれづれ節", "玄海つれづれ節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "吉永小百合が“耐える女”からイメージ・チェンジを図り、八代亜紀とのコンビで人情コメディに挑戦した作品。失踪した夫を追って九州へ向かう吉永と、借金取りの八代、これに吉永の幼友だちの風間杜夫が絡む。吉永はソープランド嬢に扮したりと意欲的だったが、いきのよさに欠けた。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひとごろし", "ひとごろし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "コント55号の「初笑い・びっくり武士道」（1972）と同じ原作の映画化。前回、無頼剣豪に坂上二郎、臆病な旗本に萩本欽一が扮したのに対して、今回はそれぞれ丹波哲郎、松田優作が演じる。誰もが恐れた上意討ちを、臆病者が特異な方法で成し遂げる。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勸楽五福星／FIVE HAPPY LUCKY STARS", "キョンシー VS 五福星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "子供たちが大活躍する児童向けカンフー映画。仲の悪い二軒の料理店の子供たちが、得意のカンフーでマフィアと日本人産業スパイをやっつけて、憧れの美人姉妹を守る。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONTENEGRO OR PIGS AND PEARLS", "モンテネグロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "激しい性描写と痛烈な政治批判が、本国のみならず全世界で大きな話題となった異色作。「スウィート・ムービー」以来７年ぶりに発表した鬼才D・マカヴェイエフ監督作。ストックホルムで裕福に暮らすアメリカ生まれの人妻。ある日、ひょんなことから移民たちの酒場で二晩を過ごすことになる。彼女は、その体験がもとで、自分の中に秘められていた性を目覚めさせていく……。主題歌を伝説のディーバ、マリアンヌ・フェイスフルが歌っている。", "1981-01-01", "14,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("About Schmidt", "アバウト・シュミット", "", "", "http://image.eiga.k-img.com/images/movie/1239/315x210.jpg?3972", 125, "リース・ウィザースプーンがやり手女子高生を演じるブラック・コメディ「ハイスクール白書／優等生ギャルに気をつけろ！」（99）の監督＆共同脚本コンビが、ジャック・ニコルソンを主演に66歳の男の自分探しを描き、アカデミー賞主演男優＆助演女優部門にノミネート。定年退職したシュミットは、妻を失い、婚約者が気に入らず娘とも大ゲンカ。キャンピングカーで故郷に向かう旅の中で、これまでの人生を振り返っていく。娘の婚約者の母親役でキャシー・ベイツが共演。", "2003-05-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふたつのウーテル", "ふたつのウーテル", "", "", "http://image.eiga.k-img.com/images/movie/57439/315x210.jpg?1769", 15, "無名の若手監督たちが作り上げた15分の短編10作品を特集上映する「NO NAME FILMS」の1本。母親を亡くした女が自分を捨てた父親に会いにいこうとするが、そこで父親を見捨てた異母弟に偶然出会い、女のトラックに同乗する。2人は2昼夜のドライブをともにすることになるが……。監督は、「アンナと二階の部屋」（2009）で第32回ぴあフィルムフェスティバルのエンタテインメント賞、「ハイランド」（09）で第22回東京学生映画祭グランプリなどを受賞してきた田崎恵美。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHRONIQUE D’UN E´TE´", "ある夏の記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "“シネマ・ヴェリテ”と呼ばれる新しいドキュメンタリー形式で現実を生々しく描いたJ・ルーシュの意欲作。フランスが様々な社会的問題を抱えていた1960年夏、パリで街頭インタビューが行われる。そしてその光景を編集したフィルムが被質問者に試写され、彼の反応から現代フランスの諸相が浮き彫りにされる。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KWAGGA STRIKES BACK", "アフリカンダンディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "自然を愛し、動物と戯れることが大好きなサバンナの勇者、クワガの前に、治安維持を名目に近代兵器を装備した国連軍が立ちはだかった。地元の人間に少しも敬意を払わない彼らによって平和な村は崩壊寸前、クワガの復讐作戦が開始される。ナミビアの独立選挙を監視する目的で国連軍が駐屯した実際の出来事をモチーフとした作品。", "1991-01-01", "67", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海底軍艦", "海底軍艦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "日本SF小説界の草分け的存在である押川春浪の同名小説を原作とした作品。１万２千年も前に地球上で繁栄を誇ったムウ帝国は、地殻変動のために海底に沈没した。しかし彼らはその科学力で海底に都市を築き上げて世界征服を企んでいるのだ。旧日本海軍の知能と技術を集結した“海底軍艦”は、ムウ帝国の野望を阻止すべく、海底深く戦いを挑んでいく。しかし、行く手には全長150メートルもある大怪竜のマンダが待ちかまえていた……。艦首に穿孔器をつけた海底軍艦のデザインは、まるでブリキのおもちゃのようにゴツゴツしていて楽しい。東宝の“怪獣”ものが最もノッていた時期の佳作である。特技監督は円谷英二。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HENRY V", "ヘンリー五世", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "W・シェイクスピアの歴史劇を、イギリス演劇界の新鋭K・ブラナーが映画化。15世紀前半、百年戦争中のイングランド。28歳で国王になり、王位継承権をめぐってフランスへ渡ったヘンリー５世の大遠征が描かれる。ブラナーは、原典に自らの解釈を加え、ヘンリー五世の内面的な人間性を表出した。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("豪傑児雷也", "豪傑児雷也", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 14, "日本映画の父・牧野省三が1909年以来コンビを組み続けてきた千本座の舞台役者・尾上松之助主演で撮った、このコンビの晩期に位置する作品。当時多産された忍術妖術トリックものの一つで、かつては舞台劇をそのままカメラ据えっぱなしで撮っていたこのコンビの作品も、この頃になると様々な映画的表現技法が見られる。", "1921-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花のお江戸の釣りバカ日誌", "花のお江戸の釣りバカ日誌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "舞台を江戸時代に置き換えて描かれる「釣りバカ日誌」の時代劇バージョン。妹の美津と長屋で暮らす浪人・浜崎伝助は釣りだけが得意ののんきな男。ある日、夜釣りに出かけた彼は寂しげな隠居・鈴木一之助と出会い、意気投合する。実は一之助は庄内藩の江戸家老で、伝助は彼の希望によって江戸屋敷で働くことに。そこで彼は一之助の遠縁にあたる美しい奥女中、小浪に一目惚れしてしまう……。庄内藩のお家騒動に巻き込まれた一之助を助ける伝助の活躍(？)、伝助と小浪の恋、そしておなじみの釣りシーンと、盛りだくさんの内容が織り込まれている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヴァンパイア・ストーリーズ BROTHERS", "ヴァンパイア・ストーリーズ BROTHERS", "", "", "http://image.eiga.k-img.com/images/movie/56279/315x210.jpg?1551", 78, "大学の仲間が殺人事件に巻き込まれてしまったセイと、血のつながらない妹ミドリは、仲間の死を悲しんでいた。そんな2人の前に、5年前に失踪したセイの兄アイが突然現れる。ミドリを愛し、バンパイアの血を引く自身の運命に抗うセイと、セイを守るためバンパイアになることを受け入れたアイの兄弟はすれ違っていくのだが……。人気若手俳優を起用し「BROTHERS」「CHASERS」の2本同時に製作されたオリジナルのバンパイア映画。「BROTHERS」主演は「D-BOYS」の柳下大。ヒロインにアイドルグループ「℃-ute」の鈴木愛理。", "2011-10-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浮雲日記", "浮雲日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "明治21年、九州から上京してきたばかりの信介は、悪いポン引きに引っかかり、危うく警察に捕まるところを、牛肉屋の娘・お龍に救われる。彼女の助力を得て、信介が東京で自分の生きる道を見いだす姿を描く。主人公をとり巻く女性陣に花柳小菊、日劇ダンシングチームの谷さゆりと意外なキャスト。料亭のおかみに扮した沢村貞子が好演。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON Your Mark", "ON Your Mark", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 7, "「耳をすませば」の併映作品として公開された、“CHAGE&ASKA”の同名曲のプロモーション・フィルム。わずか10分にも満たない短編だが、宮崎駿の作家性が存分に発揮された見ごたえのある逸品に仕上がった。近未来を思わせる舞台で繰り広げられる、翼を持った少女と彼女を大空へ帰そうとする青年二人の逃避行。「もののけ姫」などとはまた趣を変えた映像のダイナミズムが前面に出ており、とりわけ落下シーンにおける震えるような陶酔感は、比類なきアニメ作家としての風格に満ちあふれたものになっている。また、この年に日本を襲った不幸な出来事（阪神淡路大震災、オウム真理教事件）をダイレクトに反映させた世界観の今日性も見逃すべきではない。DVDは「ハウルの動く城／ジブリがいっぱいSPECIALショートショート」ツインBOXに収録。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野良猫ロック ワイルド・ジャンボ", "野良猫ロック ワイルド・ジャンボ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "シリーズ中、唯一原作の映画化で、もとになった船知慧の『破れても突っ込め』はジャズ文体で書かれた小説。無目的に日々を送る非行グループ、ガニ新、C子、タキら５人の前に、ある日、正教学会の幹部の２号・アサ子が現れる。アサ子は彼らに、学会の大祭に信者たちから多額の寄付金が集まることを告げ、それを強奪しようと持ちかけた。さっそく彼らは海辺にキャンプを張って強化合宿をする。そして実行の日がきた……。目的もなくメンバーがたむろする描写、合宿の悪ふざけなどのシーンにアナーキーな無常感が漂う映画となった。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アマカラ珍騒動", "アマカラ珍騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "失業中の元警官・金太郎は、ひょんなことから映画撮影所の守衛として働くことになる。金太郎の娘ハル子も同じ頃、映画会社にスカウトされ、撮影所に通うことになった。ある日、金太郎が映画に出演中の柳家金語楼と瓜二つなのが原因で人違いの大騒動になり……。異才・中川信夫が撮ったB級コメディの佳作。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CAINE MUTINY", "ケイン号の叛乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "駆逐艦ケイン号に着任した新艦長クイーグは、強度の偏執狂で航海中に異常な行動を繰り返していた。そんな折、ケイン号は台風に襲われ、副官マリク大尉は艦を救うため艦長から指揮権を奪い危機を切り抜ける。だが、港では反逆罪の容疑で軍法会議が待っていた。果たして叛乱事件の真相は……。艦上の迫力あるスペクタクルと、息詰まる法廷場面で見せるサスペンス・ドラマ。ピュリッツァー賞を受賞し、世界的なベストセラーとなった同名小説を、名匠ドミトリクが監督。H・ボガートが人間の深層心理に鋭く迫る名演技を見せるほか、J・フェラー、L・マーヴィンなど個性派の芸達者が脇を固めている。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("cfガール", "cfガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "妥協を許さないその仕事ぶりからトラブルメーカーとしても有名なCFディレクター・爽太郎は、ある日一人の魅力的な少女と出会う。彼は気の合う仲間たちを集め、彼女をメインに据えたCMを作り始めるが、そこには様々な妨害が。スターへの道を歩み出す少女に夢を託す男たちの姿を描く。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キスとキズ", "キスとキズ", "", "", "http://ecx.images-amazon.com/images/I/21CFK1DZNRL._SL210_.jpg", 91, "俳優として活躍する一方、「渋谷怪談」などを手掛け、監督としての才覚も評価される堀江慶監督が、“生きる意味は何か”という普遍的なテーマに挑み、「IZO」の高野八誠と「コンセント」の市川実和子を主演に迎えた青春ラブ・ストーリー。主人公は小説家を夢見てフリーター生活を送るサエない青年。周りには、政治家の父、不法滞在中国人の親友、ヤクザの叔父、という至極特異な環境。そんな中で常に自身の存在価値を問い続けてきた彼が、一人の女性との運命的な出会いを通して人生の転機を見いだしていく。と、物語自体は一見ありきたりのようであるが、真の価値観を見失いがちの現代人にとっては、これ以上なく強烈なメッセージとしてストレートに響く。小説家を目指しバイトを続ける俊は、彼女に捨てられ酒浸りの毎日。そんなある日、彼は二人組の男に襲われかけている女性、夏希を助けるが……。", "2004-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛍火の杜へ", "蛍火の杜へ", "", "", "http://image.eiga.k-img.com/images/movie/56089/315x210.jpg?4218", 44, "「夏目友人帳」で知られる漫画家・緑川ゆきの同名コミックをアニメーション映画化。夏休みに祖父の家に遊びにきていた少女・蛍は、妖怪たちが住むといわれる「山神の森」で迷子になってしまう。途方に暮れる蛍の前にキツネの面を被った少年ギンが現れ、ギンに助けられた蛍は、毎年夏にギンのもとを訪れるようになる。いつしか2人はひかれ合っていくが、ギンは人でも妖怪でもなく、触れると消えてしまうという不思議な存在だった……。大森貴弘監督ら「夏目友人帳」のアニメ版を手がけたスタッフが集う。", "2011-09-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CABARET", "キャバレー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "ファシズムの暗雲漂う1930年代初頭のベルリン、すべての政治状況に背を向けた人々が、キャバレーに集う。フォッシーはここに、順応するでもなくコミットするでもない、最もよくファシズムを支えた人間像を描写。異色のデカダン・ミュージカルの傑作を作り上げた。アカデミー助演男優賞を受賞したMC役のジョエル・グレイが出色。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UDON", "UDON", "", "", "http://image.eiga.k-img.com/images/movie/33349/315x210.jpg?3858", 134, "「踊る大捜査線」「交渉人　真下正義」のプロデューサー亀山千広と香川県出身の本広克行監督が日本のソウルフードである“うどん”をテーマに製作したコメディドラマ。BIGになるために讃岐を出てニューヨークに飛び出した香助（ユースケ・サンタマリア）だったが、夢破れ借金を抱えて帰国する。父親には一喝されるも、とりあえず借金を返すためにタウン誌の編集として働くことにする。", "2006-08-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELE´NA ET LES HOMMES", "恋多き女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "“ファンタジー・ミュージカル”と名付けられた優雅できらびやかなジャン・ルノワール後期の名作。20世紀初め、ベル・エポックの香りの残るパリに亡命してきたポーランドの公爵夫人エレナがいた。彼女は愛した男性に偉大な仕事を成し遂げる力を授け、その男性が成功すると去っていく、そんな女性だった。パリ祭の日、閲兵式に出た英雄ロラン将軍とその友人の伯爵はエレナに心を惹かれていくが、彼女にはすでに求婚者が……。国際色豊かなキャスティングも魅力で、シャンソン歌手のジュリエット・グレコも出演し歌声を聴かせてくれる。", "1956-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("童謡物語", "童謡物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "季節の移り変わりとともに、花を求めて日本中を旅する養蜂家。そんな彼らが信州の田舎町で出会った人々との触れあいを描いた作品。信州の山里のひっそりとした美しい自然と、バックに流れる郷愁を誘う懐しい童謡がほのぼのとした印象を与えている。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生きてゐる孫六", "生きてゐる孫六", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「花咲く港」で華麗にデビューした木下惠介の第２作。オリジナル脚本を演出したものとしてはこれが第１作となる。農村に代々名家を構える旧家の小名木家と、その所有する土地の開放を要求する村の若者たちの間で繰り広げられる騒動、そして周辺であたふたと動き回らされる人々の姿を、喜劇タッチで描いた作品。DVDは「木下惠介　DVD-BOX 第1集」に収録。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WESTWORLD", "ウエストワールド", "", "", "http://ecx.images-amazon.com/images/I/51HMX8A0FRL._SL210_.jpg", 88, "本物そっくりの人間や動物のいるデロス・ランド。そこは、人間の欲望を満たす一大レジャーランドだ。が、ある日、コンピュータが故障しロボットが反乱を起こす。SF作家クライトンが、自らの脚本で初監督した作品。ロボット役のブリンナーが不気味だ。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シナの夜（蘇州夜曲） 総集編", "シナの夜（蘇州夜曲） 総集編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "大陸に侵攻した当時の日本の国策に沿って製作された“大陸親善映画”。長谷川＝李香蘭コンビによる同種の「白蘭の歌」に続く第２作で、再び神話的な大ヒットを記録した。日中間を船行する貨物船の乗組員・長谷に助けられた中国娘・桂蘭は、彼に惹かれて次第に親日派になっていく。だが、彼女の親戚には抗日派がいた……。渡辺はま子が歌う主題歌も有名。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸っ子肌", "江戸っ子肌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "女をめぐって戦う、正統派時代劇の佳品。火消し加賀鳶の小頭、吉五郎は旗本・向井にさらわれそうになっている娘を助ける。彼女はおもんといい、加賀鳶と対抗する、は組の次郎吉の妹だった。一時は心を通い合わせる吉五郎と次郎吉だったが、火事場のいさかいからまた嫌悪なムードに。そんな時、おもんに再び向井の魔の手が伸びてくる。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Backtrack", "バックトラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "D・ホッパーが1989年に監督・主演したものの、製作会社が勝手に編集し、それに激怒したD・ホッパーが監督のクレジットを拒否、裁判まで起こし、アメリカでは公開されなかった作品（日本では「ハートに火をつけて」というタイトルで公開された）をホッパー自身が編集し直し、また音楽を差し替えてまったく別の作品として発表したもの。才色兼備のポップアーティストのアンは、ある日、殺人を目撃し、マフィアに命を狙われる。しかし、アンの魅力に惹かれた殺し屋のマイロは命を助ける代わりに自分の言いなりになるように強要。助かりたいアンは、その条件をのむのだが……。「ハートに火をつけて」はラブ・ストーリー的内容だったのが、ここでは男と女の関係を深く掘り下げた官能的な世界が描かれている。また、J・フォスターのヌードが見られるのも話題。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Schizopolis", "スキゾポリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "アメリカの平和な郊外を舞台に、思想団体に勤める男とその妻など、奇妙な人々が絡み合うシュールなスラップスティック・コメディ。ソダーバーグが監督・脚本・撮影・主演をこなし、前衛的な映像手法を駆使した実験映画的な作品。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太夫さんより 女体は哀しく", "太夫さんより 女体は哀しく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "1955年に劇団新派によって上演され、テアトロン賞を受賞した北条秀司の戯曲『太夫さん』を名匠・稲垣浩が映画化。300年の伝統を誇る京都・島原遊廓の老舗、宝生楼を舞台に女主人とそこで働く遊女たちの生態を描く。太夫・玉袖に扮する乙羽信子が好演、田中絹代、浪花千栄子ら名優が脇を固める。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゾロ目の三兄弟", "ゾロ目の三兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "小林旭主演・東映第１作のアクション映画。昭和28年頃の大阪を舞台に、小林・田中・渡瀬扮する助平でケチでけんか早い３兄弟が、故郷への工場進出をめぐって大資本と結託したヤクザ組織を敵にまわし、大立ち回りを演じる。脚本は当時任侠路線から実録路線への推移のなか、本領を発揮しつつ量産し続けていた高田宏治。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　アンパンマンとゆかいな仲間たち", "それいけ！アンパンマン　アンパンマンとゆかいな仲間たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 31, "やなせたかし原作の人気アニメーション劇場版第4作「それいけ！アンパンマン　つみき城のひみつ」と同時上映の短編。「あかちゃんまんの大冒険」「カレーパンマンとSLマン」「うたえ！夢のアンパンマン号」の3話構成。", "1992-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("XXX: State of the Union", "トリプルX ネクスト・レベル", "", "", "http://ecx.images-amazon.com/images/I/51DyDcXYk9L._SL210_.jpg", 101, "型破りなシークレットエージェント「トリプルX」の活躍を描くスパイアクションの第2弾。前作のビン・ディーゼルに代わり、人気ラッパーのアイス・キューブが主演を務める。NSA支局が何者かに襲撃され、16人のエージェントが殺害された。間一髪で生き延びたギボンズは、軍刑務所に服役しているかつての部下ダリウスを脱獄させ、新たな「トリプルX」に任命。捜査を開始したダリウスは、事件の黒幕が国防長官デッカートであることを知る。", "2005-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OKINAWAN BOYS オキナワの少年", "OKINAWAN BOYS オキナワの少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "沖縄本土復帰の決まった1970年。軍政下で少年時代を過ごした青年が東京に出てシナリオの勉強をしている。その少年期の回想と東京での生活を、詩情豊かに描く。沖縄出身の新城監督が第66回芥川賞受賞作を原作に、監督自身の過去と現在を投影させたデビュー作。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ムーミン谷の彗星", "ムーミン谷の彗星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "みんなの人気者ムーミンが活躍するファンタジック・ストーリーの劇場版アニメ。ムーミン声優で有名になった高山みなみの声は、かなりの好感度を呼ぶ。大塚や子安の男性声優陣も健在。彗星が地球に近づきつつある危険な状況の中、少しはしょんぼりすることもあるけれど、いつもの明るいムーミンがここにいる。彼はみんなと協力し合、い励まし合いながら冒険を繰り広げていく。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大阪物語", "大阪物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "「東京兄妹」「東京夜曲」などで東京の風景にこだわってきた市川準監督が、大阪に舞台を移して撮り上げた青春映画。売れない漫才師コンビを両親に持つ14歳の少女が、両親の離婚、父の失踪、その捜索などの体験を通して大人へと成長していく姿を、優しい眼差しで描き出している。大阪の下町描写のぬくもりはもちろん、市川監督に“大阪弁の美しさに惹かれた”と言わせたヒロイン、池脇千鶴のみずみずしい好演が印象的。ロクデナシの父親に扮した沢田研二、気丈な母を演じる田中裕子の実の夫婦共演による、夫婦漫才シーンも見どころ。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEHRE　MADARI", "テヘラン悪ガキ日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ベルリン映画祭こども映画祭のグランプリ受賞をはじめ、各国のこども映画祭で好評を得た作品。一人の少年が現実を知り、成長する姿をコメディ・タッチで描いていく。少年院にいるメヘディは母の顔を知らず、たまたま新聞で見た女性の写真を母親だと思い込んでいた。そこにソ−シャル・ワーカーの女性が新しい先生としてやってくる。メヘディは写真に似た先生をすっかり自分の母親と信じ、住所を突き止めるや少年院を脱走し、先生の家に向かう。そして自分の母親になってほしいと、先生に訴えるのだが……。", "1998-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shaun of the Dead", "ショーン・オブ・ザ・デッド", "", "", "http://ecx.images-amazon.com/images/I/51vdK0tPqaL._SL210_.jpg", 100, "突如として街に出現したゾンビの大群から恋人を救うべく立ちあがったダメ男の奮闘を描き、低予算ながらイギリスで大ヒットを記録したゾンビコメディ。ロンドンの家電量販店で働く冴えない青年ショーンは、親友のエドとつるんで自堕落な毎日を送っていた。ついにガールフレンドのリズに愛想を尽かされたショーンはようやく更正を決意するが、その矢先、街に大量のゾンビが発生。愛するリズを助けるため、エドと共に行動を開始するショーンだったが……。", "2004-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブルックリン橋をわたって", "ブルックリン橋をわたって", "", "", "http://image.eiga.k-img.com/images/movie/56779/315x210.jpg?2702", 80, "ニューヨークを舞台に、日本人少女が現地の青年たちとの交流を通して成長していく姿を描いた日米合作の青春ドラマ。父親の転勤でニューヨークで暮らすことなった小野寺早紀は、環境の変化にとまどい、学校でも周囲に馴染めず孤独な日々を送っていた。そんなある日、父親から渡ることを禁じられていたブルックリン橋を渡ったことから、早紀の人生が大きく転換する。「婚前特急」「マイ・バック・ページ」の石橋杏奈が主演し、大半がニューヨークでロケ撮影され英語のセリフにも挑戦。父親役は芸能リポーターとして知られる阿部祐二。", "2011-12-17", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paragraf 78", "バイオソルジャー", "", "", "http://image.eiga.k-img.com/images/movie/53269/315x210.jpg?2142", 130, "ロシア流格闘術コマンドサンボとガン・アクションを融合させた超絶アクション“コマンド・ガン・サンボ”に、生物兵器というSF的要素を盛り込んだ新感覚のバトル・ムービー。雪山の奥深くに建つ実験施設で事故が発生。グッドウィン隊長の指揮の下、かつての部下で5年前に退役したはずの6人が集められ、調査のために現地へと向かう。そこで彼らを待ち受けていたのは、生物兵器と化した研究者たちだった……。", "2008-01-26", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Oil City Confidential", "ドクター・フィールグッド　オイル・シティ・コンフィデンシャル", "", "", "http://image.eiga.k-img.com/images/movie/56099/315x210.jpg?4352", 106, "1970年代初頭からイギリスのパブ・ロックシーンを牽引し、後のパンク・ロックムーブメントの火付け役となったバンド「ドクター・フィールグッド」に迫った音楽ドキュメンタリー。「NOFUTURE A SEX PISTOLS FILM」「LONDON CALLING ザ・ライフ・オブ・ジョー・ストラマー」に続く、ジュリアン・テンプル監督による“70年代イギリス音楽3部作”の3作目。ドクター・フィールグッドが、後に続くセックス・ピストルズやジョー・ストラマーらによるパンク・ロックの文化に多大な影響を与えた真実を紐解く。", "2011-04-09", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　HARDER　THEY　FALL", "殴られる男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「波止場」の原作者B・シュールバーグの小説を映画化した硬派なヒューマン・ドラマ。失業中のスポーツライターのエディが、トロという巨漢ボクサーの売り込みを依頼される。ところがその背後には、大もうけをもくろむ悪徳マネージャー、ニックの八百長が仕組まれていた。終盤、悪に反旗を翻すエディ役に名優H・ボガート。これが彼の遺作となった。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE　FEMME　MARIEE", "恋人のいる時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ゴダールの監督第８作。不倫に溺れた人妻の２日間を、実験的な手法を取り入れながら繊細なタッチで描く。前夫との間に生まれた息子をつれ、小型機のパイロット、ピエールと再婚したシャルロットは、３ヵ月前から浮気をしている.。相手は地方巡業劇団の役者ロベール。不審を抱いたピエールは、私立探偵にシャルロットを尾行させている。シャルロットは尾行を警戒しながら、ロベールのアパートに通っている。やがて、彼女の妊娠が発覚するが、彼女にも父親がどちらか分からなかった……。", "1964-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌麿・夢と知りせば", "歌麿・夢と知りせば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "浮世絵師・歌麿を花鳥山水図はうまいが美人画は下手という設定にし、彼が女房を浮浪者に襲わせたりしながら、美人画に目覚めていくさまを描いている。実相寺昭雄の耽美なエロティシズムが全編に横溢する作品。吸血鬼役で名をはせた歌麿役の岸田森の怪演が心に残る一編である。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("On Your Mark", "On Your Mark", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 7, "宮崎駿監督、原作、脚本、スタジオジブリ制作による短編アニメーション。CHAGE and ASKAのコンサートツアー用のプロモーションフィルムとして制作され、同監督作品「耳をすませば」の同時上映として劇場公開された。CHAGE and ASKAのシングル曲「On Your Mark」に合わせ、未来都市を舞台に警察官2人が羽の生えた少女を救出するSFアクション。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠らない街 新宿鮫", "眠らない街 新宿鮫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "大沢在昌の傑作ハードボイルド小説『新宿鮫』シリーズの第１弾を映画化。新宿歌舞伎町界隈で“新宿鮫”の異名をとる刑事、鮫島。彼は、暴力団の抗争に使われた銃が、３年前に自分が逮捕した木津の手による改造銃だと知る。その後も改造銃による事件が起こり、鮫島は木津の足取りを追うが……。オール・ロケによる新宿の無国籍化した生々しい街並みが効果的。鮫島と因縁で結ばれた木津を、奥田瑛二が、不気味な存在感を漂わせたアウトロー演技で好演している。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不良番長・手八丁口八丁", "不良番長・手八丁口八丁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "神坂以下カポネ団の面々は、暴力団・中西興業に睨まれ食いつめていたが、社長・中西から土地買収の話が持ち込まれて上州・国定村へと旅立つ。しかし逆に中西の悪どさを知り、善良な市民のためにカポネ団は立ち上がるのだった。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("War", "ローグ　アサシン", "", "", "http://image.eiga.k-img.com/images/movie/34169/315x210.jpg?1518", 103, "ジェット・リー、ジェイソン・ステイサムという東西のアクションスター2人が壮絶な戦いを繰り広げるアクション大作。チャイニーズ・マフィアとジャパニーズ・ヤクザの戦いが激化するサンフランシスコに謎の殺し屋ローグ（リー）が再び姿を現す。マフィア抗争を担当するFBI捜査官のジョン（ステイサム）は、3年前、逮捕寸前まで追い詰めたローグに家族と同僚を殺されていた……。監督はこれまでエミネムや50セントのPVなどを手がけ、本作で長編映画デビューを飾ったフィリップ・G・アトウェル。", "2007-10-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("True Grit", "トゥルー・グリット", "", "", "http://image.eiga.k-img.com/images/movie/55149/315x210.jpg?2281", 110, "「ノーカントリー」のコーエン兄弟が、ジョン・ウェイン主演の名作西部劇「勇気ある追跡」（1969）をジェフ・ブリッジス主演でリメイク。父親を殺された14歳の少女マッティは犯人を追跡するため、隻眼の凄腕連邦保安官コグバーン（ブリッジス）を雇う。だが、コグバーンは元泥棒で大酒飲みの自堕落な男で、彼を信用できないマッティはコグバーンに同行して犯人を追うことになる。共演にマット・デイモン、ジョシュ・ブローリン、バリー・ペッパー。", "2011-03-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PREMINCUKUNDAM　RAA！", "愛と憎しみのデカン高原", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 157, "インドでヒンドゥー語映画と同じく古い歴史を持ち、最多生産本数を誇るデルク語による波乱万丈のラブ・ストーリー。嫁いだ姉を訪ねて隣町にやって来たギリが、カーヴェリという娘と恋に落ちる。だがカーヴェリは、ギリには親が決めた結婚相手がいることを知り、深く傷ついてしまう。やがて二人は互いに心から愛し合っていることを確認し合うが、カーヴェリの父はとんでもない荒くれ者で……。感情をストレートに表現する歌や踊り、地方色豊かな荒涼とした風景も見もの。", "1997-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やっちゃ場の女", "やっちゃ場の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "築地の青果市場、通称“やっちゃ場”の仲買い店のちゃきちゃき娘ゆき子。母が死に、父が家出してからは男勝りのゆき子が若い衆を使って一人で商売を仕切っていた。ゆき子の将来を心配した叔母は縁談の話を持って来るが、彼女は若い衆の一人、精一にひそかに思いを馳せていて……。快活な中にも、切なさを垣間見せる若尾文子のヒロインに注目。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユーリ", "ユーリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "TVドラマ『東京ラブストーリー』『ラストクリスマス』などのTVの脚本家として活躍する、坂元裕二の初監督作品。現実離れしたシュールな青春像が描かれる。８月の終わり、することもなく時間を持て余していた雅之は、町で七美という不思議な少女に出会う。七美は、イラン人のアミールとユーリという名の冷蔵庫を運んでいた。その中には、七美が誤って殺してしまった恋人の死体が入っていた。七美に急速に惹かれていく雅之は、七美、アミールとともに冷蔵庫を抱えた旅に出るのだった……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女のみづうみ", "女のみづうみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "愛人・北野と情事を重ねる宮子は、ある夜自分の裸体を北野に撮らせたが、そのフィルムを暴漢に奪われる。以来、宮子は見知らぬ男の脅迫を受け、ついに男に指示された列車に乗るのだった……。川端康成の小説『みづうみ』より着想を得て、自由に翻案した吉田喜重作品。写真の中の女をモチーフに、虚像と実体の関係を問いかける。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("B．MONKEY", "Bモンキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「イル・ポスティーノ」のマイケル・ラドフォードによる犯罪劇。ロンドンを舞台に犯罪に酔う男女３人の奔走を描く。美少女ベアトリスは裏社会のちょっとした顔。相棒ブルーノや、ゲイであるその恋人ポールと家族のように暮らしている。そんな彼らに別れの時が訪れ、ベアトリスは足を洗おうとするが……。「トリプルX」のアーシア・アルジェントがヒロインを好演。", "1998-01-01", "3", 0, NOW(), NOW());
