INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Maiden Heist", "ザ・バッド", "", "", "http://ecx.images-amazon.com/images/I/51T4Yy1npzL._SL210_.jpg", 90, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("'G'　MEN", "Gメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "J・キャグニーがGメンを演じ、Gメン映画の先駆けとなった作品。暗黒街の顔役、マッケイに息子のように愛され育った弁護士ブリックは大学の友人に誘われてGメンとなる。やがて、マッケイは隠居し、Gメンたちは残る大物であるコリンズの検挙に躍起となるが、卑怯にもコリンズはブリックの上司の妹を人質にとって国外逃亡を図る……。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("21 HOURS AT MUNICH", "テロリスト 黒い九月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1972年９月５日、ミュンヘン・オリンピック開催中の選手村で起こった恐怖のアラブ・テロ事件。テロリスト集団の名は“黒い九月”。11人のイスラエル選手を人質に捕虜仲間の解放を当局に迫ったゲリラたちの犯行を、ドキュメンタリー・タッチで再現してみせたTVムービー。日本では劇場公開された。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バイオハザード　ダムネーション", "バイオハザード　ダムネーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "人気ゲームをフルCGで映像化した「バイオハザード　ディジェネレーション」（2008）の続編。", "2012-08-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私たちの幸せな時間", "私たちの幸せな時間", "", "", "http://image.eiga.k-img.com/images/movie/34059/315x210.jpg?1392", 124, "死刑囚の男と自殺願望の女の交流を描き、韓国で300万人を動員した感動のラブ・ストーリー。3回自殺未遂をした元歌手のユジョンは、3人を殺害した死刑囚のユンスと、週に1度、3時間だけ面会をすることに。始めのうちは敵対心を抱いていた2人だったが、やがてお互いが似ていることに気付き、本当の自分をさらけ出していく。「デュエリスト」のカン・ドンウォンと「小さな恋のステップ」のイ・ナヨンが2人の主人公を熱演。", "2007-07-14", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女真珠王の復讐", "女真珠王の復讐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "絶海の孤島を舞台にしたサスペンス映画。会社重役の陰謀によって恋人を逮捕されたヒロインが、女の武器を駆使して復讐を果たしていく様を描く。主演女優、前田通子が大胆なヌードを披露するほか、若き日の丹波哲郎が殺し屋を演じている。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("復活の朝", "復活の朝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "人員不足、激務、高い離婚率……過酷な職場環境で、患者の命を守る“縁の下の力持ち”として心を砕く看護師たちの実像を熱っぽく描いたヒューマン・ドラマ。とある外科病棟。婦長、清原の下に十数人の看護婦が昼夜なく働いている。ある新人看護師は、彼女にとって初めての患者、10歳の脳腫瘍少年の死に号泣する。あるベテラン看護婦は、過重勤務と恋愛の板ばさみになって苦悩する。そんな部下たちを励ましながら、清原は連日入院してくる重症患者に立ち向かう。彼女たちの闘いの中からガン告知、ターミナル・ケア等の医療がかかえる課題が浮かび上がる。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Endhiran", "ロボット", "", "", "http://image.eiga.k-img.com/images/movie/57279/315x210.jpg?4112", 177, "「ムトゥ　踊るマハラジャ」のラジニカーントが1人2役で主演し、心を与えられたロボットが失恋の痛手から暴走する姿を描いたインド製SFアクションコメディ。バシー博士は10年の歳月をかけ、自分とそっくりで感情をも理解するロボット、チッティを開発する。心をもったチッティは博士の恋人サナに恋をするが、サナにふられた上に博士の怒りを買い廃棄されてしまう。このことがきっかけで冷酷なターミネーターと化したチッティは、量産した自分のレプリカでロボット軍団を組織し、破壊の限りを尽くす。サナ役は94年ミス・ワールドで世界一の美女に輝いたアイシュワリヤー・ラーイ。音楽は「スラムドッグ＄ミリオネア」のA・R・ラフマーン。", "2012-05-12", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Je M'appelle Elisabeth", "ベティの小さな秘密", "", "", "http://image.eiga.k-img.com/images/movie/53789/315x210.jpg?2901", 90, "フランスの田舎町に暮らす10歳のベティは、想像力豊かで動物好きな女の子。両親の不仲に不安を抱いていたある日、父親の働く精神病院から逃げ出してきた青年イヴォンに出会う。ベティはイヴォンを自転車小屋に匿い、必死に守ろうとするが……。監督は「デルフィーヌの場合」のジャン＝ピエール・アメリス、原作はゴダールの元妻で女優のアンヌ・ビアゼムスキー。主演は「僕を葬る」で劇場デビューしたアルバ＝ガイア・クラゲート・ベルージ。", "2008-09-20", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルナの子供", "ルナの子供", "", "", "http://image.eiga.k-img.com/images/movie/54929/315x210.jpg?4539", 100, "「天使の楽園」の鈴木章浩監督が、子供の生まれない関係をテーマに製作したオムニバス映画。堕胎した過去に苦しむ女、傷つくのを恐れて目の前の愛を受け入れられないレズビアンの女、セックス抜きで愛されたいと望む女を描いた3部で構成される。主演は「純喫茶磯辺」の佐伯佳奈杷、声優の奥真紀子、「火火」の岩元英理。", "2009-11-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　リリカル☆マジカルまほうの学校", "それいけ！アンパンマン　リリカル☆マジカルまほうの学校", "", "", "http://ecx.images-amazon.com/images/I/31S215G7HGL._SL210_.jpg", 60, "やなせたかし原作の人気アニメ「それいけ！アンパンマン」の劇場版シリーズ第6作。空に浮かぶ魔法の島を訪れたアンパンマンは、魔法の学校に通う見習い魔女リリカと出会う。アンパンマンと一緒にジャムおじさんたちの元を訪れたリリカは、そこで楽しい仲間たちとすっかり仲良くなる。今度はリリカがアンパンマンたちを魔法の島に招待するが、先にやって来たばいきんまんとドキンちゃんによって島は大パニックになっていた。", "1994-07-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arizona Dream", "アリゾナ・ドリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "それぞれのアメリカン・ドリームを胸に秘めた個性的な登場人物たちが織りなす人間模様を、ユーゴスラビア出身のエミール・クストリッツァ監督が、ジョニー・デップ主演で描いたブラック・コメディ。叔父の結婚式に出席するためにアリゾナへやって来たアクセル。叔父が営む自動車販売の仕事を手伝うことになった彼は、夫を射殺した過去を持つ未亡人エレインと、彼女の義理の娘で自殺願望のあるグレースが暮らす家に転がり込むが……。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古都", "古都", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "川端康成の同名小説を映画化。京都の呉服屋の娘・千恵子と早苗という離ればなれになっていた双子の姉妹が再会し、一人の男性を巡って愛の波紋が広がっていく。二役を演じた岩下志麻の好演と、成島東一郎の見事な色彩映像が映える、叙情的な恋愛映画。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Battlefield Earth", "バトルフィールド・アース", "", "", "http://image.eiga.k-img.com/images/movie/1279/315x210.jpg?3286", 117, "西暦3000年、文明を9分間で壊滅させた異星人・サイクロ人の侵略により荒廃した地球では、生き残った僅かな人間たちは山奥に隠れ住むか、奴隷として生きるしかなかった。山間の村に住む勇敢な青年ジョニーは家族を振りきり冒険に出るが、サイクロ人の司令隊長タールに捕らえられて学習マシンにかけられる。タールは彼にサイクロ人の知識を教え込み、意のままに操ろうとしていたのだ。", "2000-10-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("荒神", "荒神", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "“限られた空間でのDUEL（対決の意味）”を同一テーマに、堤幸彦監督と北村龍平監督が競作で対決したコラボレーション企画。こちらの北村作品は山奥の古寺を舞台に、戦いの神である“荒神”と侍の雌雄を決する果たし合いが描かれる。北村監督ならではの斬新で、スタイリッシュなアクションはここでも健在。主役の二人に扮する大沢たかおと加藤雅也の熱演も必見だ。戦い敗れ、山奥の古寺に逃げ込んだ侍。瀕死の重傷を負う彼だが、寺にいた戦いの神“荒神”を名乗る男の介護で一命を取り止める。やがて彼は傷も癒え、山寺を出ることに。ところが荒神に生死を賭けた真剣勝負を申し込まれ……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAILWAYS 愛を伝えられない大人たちへ", "RAILWAYS 愛を伝えられない大人たちへ", "", "", "http://image.eiga.k-img.com/images/movie/56019/315x210.jpg?3445", 123, "鉄道にまつわる人々の人生をつづったヒューマンドラマで、中井貴一主演「RAILWAYS 49歳で電車の運転士になった男の物語」（2010）に続くシリーズ第2作。富山を舞台に、第二の人生を目前にした一組の夫婦の姿を描く。運転士として42年勤めた富山地方鉄道を1カ月後に定年退職することになっている徹は、ガン患者の在宅ケア業務に就きたいと話す妻・佐和子ともめてしまう。佐和子は徹と距離を置くため家を出てしまうが……。主人公の徹役に三浦友和、妻・佐和子役に余貴美子。", "2011-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("a piece of PHANTASMAGORIA", "a piece of PHANTASMAGORIA", "", "", "http://ecx.images-amazon.com/images/I/410PA50XFFL._SL210_.jpg", 78, "絵本作家のたむらしげるが、これまでも絵本やデジタル原画集などのメディアで描いてきた幻想的な架空の惑星ファンタスマゴリアをアニメーションで描いた短編集。プラネタリウム技師の告白やガラスの海の生活、スノーマンの旅、巨大電球が輝く日など、ファンタスマゴリアのさまざまな場所で起こる日常の出来事を、手使海ユトロのやわらかな音楽にのせてつづっていく。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ROAD　TO　MEMPHIS", "ロード・トゥ・メンフィス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ブルースの聖地として知られるアメリカ、メンフィスにスポットを当てたドキュメンタリー。ブルースの王様B.B.キングを筆頭に、ボビー・ラッシュやリトル・ミルトンなど大御所アーティストが続々登場。彼らのエキサイティングなステージはもちろん、ツアー中の風景も収められ、ブルースマンの“生”を伝える内容となっている。貴重な記録映像もファン必見。かつて黒人文化の中心地として栄えたビール・ストリートに、老いたブルースマンたちが帰ってきた。成功を手にした者、一度は音楽を捨てた者、未だにドサ回りを続ける者。それぞれの思惑を超えて、彼らの音楽が一つになる。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不夜天／KILLER’S NOCTURNE", "復讐の夜想曲〈ノクターン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "チーフェンは、麻雀の名手だった父親を冷酷なナイトクラブ経営者イエンのため死に追いやられ、仇討ちにいくが袋叩きに合う。そこをイエンの愛人オータムに助けられ、恋に落ちる二人だったが……。1930年代の上海を舞台に、殺された父と恋人の復讐に燃える青年を描いたアクション。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("貸間あり", "貸間あり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "喜劇映画に独特の作風を築いた川島雄三の監督作品。井伏鱒二の同名小説を原作とし、大阪のとあるアパートに住む風変わりな人間たちのエネルギッシュな群像を描く。川島の代表作「幕末太陽傳」の主役で名を上げたフランキー堺の怪演が見もの。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠離戦争年代／FAR　REMOVED　FROM　WAR", "戦争を遠く離れて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1956年生まれの第五世代に属する女流監督、フー・メイの独立第１作。かつて八路軍兵士だった顧蒙はすでに老年に達し、悠々自適の生活を送っていた。しかし、抗日戦で死んだと思われていた恋人・雲花がまだ生きているとわかり、彼女を捜して旅に出る……。第10回タシケント映画祭審査員特別賞受賞作。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STADT DER VERLORENEN SEELEN", "ベルリンブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ベルリンの下町にある猥雑なハンバーガーショップ“バーガー・クィーン”。そこを訪れる黒人ダンサーやストリッパー、ゲイなどの一風変わった人物たちが繰り広げるハチャメチャなミュージカル。ただし、ミュージカルといっても、これはパンク版。人種も、性別も様々で、社会からはみ出して生きる彼らの夢と挫折の日々が、激しいパンク・ロックにのせて描かれていく。監督は、「タリー・ブラウン、ニューヨーク」以来これで二度目の登場となる西ドイツの異色作家、プラウンハイム。", "1983-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹下左膳・乾雲の巻", "丹下左膳・乾雲の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "妖刀に呪われ無益の殺生を重ねる左膳は、今や越前守にも追われる身となってしまった。彼は岩城藩・下屋敷に行き、斬りかかる藩士と闘いながら、乾雲を求めて主水正を追い詰める。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POTOP", "遠雷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 183, "17世紀、スウェーデンの侵略に抗するポーランドを舞台に、一人の青年騎士の恋を描いたスペクタクル大作。シェンキヴィッチの小説をもとに壮大なスケールで映画化されており、戦闘シーンや、策略と野望が交わる緊迫感あふれる場面が見どころとなっている。主演のD・オルブリフスキの熱演も捨て難い。", "1974-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CENTO GIORNI A PALERMO", "100日の凶弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1982年、カラビニエリ軍司令官の英雄ダラ・チェサは、パレルモに旅立った。そこで政府要人がマフィアに暗殺されていたからだ。ダラ・チェサはすぐにマフィアを弾圧する。が、彼の赴任100日目、敵は反撃を開始した……。", "1984-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続次郎長富士", "続次郎長富士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "次郎長を長谷川一夫、石松を勝新太郎が演じたおなじみの娯楽時代劇。反次郎長派の親分・平親王の勇蔵の謀略で石松を殺された次郎長一家。勇蔵は、石松の死骸と引き換えに次郎長の身柄を要求してきたため、次郎長はわずか４人で敵陣へ。だが、親分と一緒に死のうと決めた子分たちがその跡を追う……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本の黒い夏 冤罪", "日本の黒い夏 冤罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "1994年、長野県松本市で発生した『松本サリン事件』を社会派、熊井啓が描く社会派ドラマの衝撃作。「帝銀事件・死刑囚」でも歴史的な事件を映画化した熊井監督の骨太な演出と冷静な観点が光る。高校の放送部員二人が、１年前に起きた『松本サリン事件』でのえん罪報道を検証するドキュメンタリーを製作することに。二人が取材のために訪れたテレビ局で、報道部長の笹野と部下たちが当時の様子を語り始めた……。情報と憶測が飛び交い、マスコミによって犯人に仕立て上げられていく被害者の姿が、マスメディアの立場にいる人々の視点から描かれる。えん罪を作るのも晴らすのもマスコミという、報道のあり方に一石を投じる問題作だ。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("貴族の階段", "貴族の階段", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「安城家の舞踏会」など、数々の名作を生み出した巨匠、吉村公三郎監督、新藤兼人脚本のコンビによる人間ドラマ。軍部が台頭してきた昭和初期、進歩的貴族、西の丸秀彦は軍部から強力な誘いを受けていた。一方、その息子である近衛士官・義人はひそかに節子という女に恋していたが、実は節子は秀彦を慕い続けていて……。３人の織り成す人間模様と、その背景にある二・二六事件を、義人の妹の日記という形で描いている。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パレード", "パレード", "", "", "http://image.eiga.k-img.com/images/movie/54649/315x210.jpg?2890", 0, "第15回山本周五郎賞を受賞した吉田修一の青春群像劇を、「世界の中心で、愛をさけぶ」の行定勲監督が映画化。主演は藤原竜也、香里奈、貫地谷しほり、小出恵介、林遣都。都会の片隅でルームシェアをする、健康オタクの会社員・直輝、自称イラストレーターの未来、恋愛依存症の琴美、大学生の良介の4人のもとに、男娼をしている謎の少年・サトルが転がり込んでくる。時を同じくして、町では女性連続暴行事件が多発していた。", "2010-02-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE WOMEN", "若草物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "オルコットの名作『若草物語』を、G・キューカーが映画化したドラマ。マーチ家の４人姉妹を主人公に、隣の富豪の息子とその家庭教師をめぐる恋愛騒動と、４人のその後が綴られていく。“死”を中心軸にした演出はキューカーらしい堅実なもので、なかでも次女ジョーを演じるK・ヘプバーンの熱演が光っている。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帰ってきた！刑事〈でか〉まつり", "帰ってきた！刑事〈でか〉まつり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "主役は刑事、上映尺は10分以内、１分に１回のギャグを入れる。そんな３つのルールの下に12人の気鋭監督が製作費自腹で競い合った“刑事〈でか〉まつり”企画の第２作。今度のルールは女刑事を主役にすること。第１作からは総入れ替えとなる参加監督も、「誰も知らない」の是枝裕和、「黄泉がえり」の塩田明彦、「肌の隙間」の瀬々敬久ほか錚々たる顔ぶれが集結している。", "2003-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ERNESTO", "エルネスト −美しき少年−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "エルネストはバイオリニストを目指す17歳の美少年。ある日、彼はたくましい男に誘われ倉庫で異様な体験をする。その後、双子の兄弟に同時に愛され……。同性愛と異性愛の間で揺れる思春期の少年心理を描く、S・サンペリ監督作。", "1979-01-01", "4,9,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLORADO TERRITORY", "死の谷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ともに人生の裏街道を行く、お尋ね者とあばずれ女の悲劇の恋を描いた西部劇の佳作。不幸な絆に結ばれた男女が、誰一人祝う者もない荒野の結婚式につかの間の喜びを感じあい、一つの希望にすがりながら生きていくが……。万策つきてシェリフの一撃に倒れるラスト・シーンが印象的。1941年にウォルシュ自身が監督した「ハイ・シェラ」のリメイク。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOWN", "ダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "オランダの鬼才D・マースが、1983年に製作した自身の出世作「悪魔の密室」をリメイクしたホラー・サスペンス。ニューヨークのマンハッタンにそびえるハイテクを装備したビルで、エレベーターが誤作動し死傷者が出るという事件が発生。エレベーター技師マークは、取材にやってきた女性記者ジェニファーとともに独自に調査を始める。二人が目撃した驚くべき真相とは？　N・ワッツが恐怖に敢然と立ち向かう女性を熱演している。", "2001-01-01", "1,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE INFORMER", "男の敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "フォードは通算４回のアカデミー賞を獲得したが、その最初の受賞作品。大男で力持ちのジポーは、アイルランド独立運動の秘密組織員だが、思慮が浅く規約を破ることが多かったため組織を除名され、金のない生活を強いられていた。そんなとき、指名手配されている独立運動の同志フランキーが町に潜入し、その首に賞金がかけられていることを知る。恋人ケイティのために金の欲しいジポーは、警察に密告。フランキーは家族の目の前で殺され、ジポーは同志たちに不審がられ狼狽する……。名手オーガストによる撮影が素晴らしく、夜霧の美しさと不気味さが、追われる男の物語をサスペンスフルに盛り上げた傑作。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕らはあの空の下で", "僕らはあの空の下で", "", "", "http://image.eiga.k-img.com/images/movie/54609/315x210.jpg?4066", 68, "漫画家を目指す男子高校生たちの友情を繊細なタッチで描いた青春ドラマ。明るく人気者の生徒会長・神崎浩紀は、漫画家を夢見てコンクールに作品を応募しては落選するという日々を送っていた。副会長の吉永直や後輩の山本鉄平は、そんな浩紀に振り回されながらも温かく見守り続けていた。ある日浩紀は、アメリカからやって来た転校生・沢田修が同じく漫画家志望であることを知り、コンクール入選を目指して漫画を共作することになるが……。", "2009-08-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の門 実録・大阪頂上戦争", "極道の門 実録・大阪頂上戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "高度成長期の大阪を舞台に、一人のチンピラが本物のヤクザになっていくまでを描く。大政組の組員・尾形とその舎弟・村田が敵対する義道会の刺客５人に襲われた。尾形は殺されてしまうが、村田は５人を切り倒す。この事件で大政組と義道会の対立は深刻さを増していく。そんな中、組長の鹿島と若頭の赤垣が警察で取り調べ中の村田を訪れ、親子の杯をガラス越しに酌み交わすのだった。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ilusiones Ópticas", "見まちがう人たち", "", "", "http://image.eiga.k-img.com/images/movie/54959/315x210.jpg?4579", 107, "南米チリの郊外都市バルディビアに建つショッピングモールを舞台に繰り広げられる群像劇。手術で盲目から回復したスキー選手、万引癖のある美しい人妻と、彼女に恋してしまう警備員、失業に備えて訓練中の従業員、豊胸手術に憧れる女性といった個性的な登場人物たちの人生が交錯していく様子を、ユーモアと皮肉を散りばめながら描く。チリで俳優や脚本家として活躍するクリスチャン・ヒメネスの長編初監督作品。", "2009-10-20", "77,25,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Black Gold", "おいしいコーヒーの真実", "", "", "http://image.eiga.k-img.com/images/movie/53559/315x210.jpg?2567", 78, "今や私たちの生活にとって欠かせない存在となったコーヒー。しかしその原産国であるエチオピアでは多くのコーヒー農家が貧困にあえいでおり、人々は教育を受けられないばかりか食べることさえままならない状況に陥っている。彼らを救おうと奔走するコーヒー農協連合会の代表者タデッセ・メスケラの活動を追いながら、1杯のコーヒーが消費者に辿り着くまでの道のりに潜む不公正な貿易システムの実態を明らかにするドキュメンタリー。", "2008-05-31", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花嫁隠密", "花嫁隠密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "花嫁にして隠密、柔弱な夫にして勤皇の志士という二人を主人公にした時代劇。正月映画らしいにぎやかな設定で、その点ではのちの東映時代劇を思わせる。大友柳太朗は、1937年に新興キネマから映画デビュー。1939年「元禄女大名」で注目された中堅のチャンバラ・スター。のちの東映での活躍は周知の通りである。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドッグス", "ドッグス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "初期作ではモノクロを数多く手掛けていた長崎俊一監督が、全編ビデオ撮影で久しぶりにモノクロ作品に挑んだ犯罪サスペンス。殺人犯を追跡中、いわくありげな男・寺西の自殺行為に出くわした刑事・美樹。やがて何者かに脅迫されるようになった寺西を救うべく美樹は、刑事の道を踏み外していく。意思を持って犯罪に加担していく女刑事の物語。「犬、走る DOG RACE」の遠藤憲一がとりとめのない魅力を放つ不思議な男を、想像力を喚起する見事な演技で見せている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON THE RIVIERA", "夫〈ハズ〉は僞者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ナイトクラブに出演する芸人ジャックは、世界一周飛行家アンリが自分にそっくりなのを利用して、彼に扮して舞台に立ち大喝采を浴びた。一方資金繰りに困り、ロンドンへ飛んだアンリのパートナーたちも、ジャックをアンリに仕立ててレセプションに出席させる。ジャックは見事アンリの妻を射止め、スポンサーと飛行の契約もとりつけるが……。D・ケイが一人二役を演じるミュージカル・コメディ。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒノマル♪ドリーム", "ヒノマル♪ドリーム", "", "", "http://image.eiga.k-img.com/images/movie/57949/315x210.jpg?2943", 103, "2012年・第4回沖縄国際映画祭の長編プログラム「Peace」部門にて上映。", "2011-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The King of Fighters", "ザ・キング・オブ・ファイターズ", "", "", "http://image.eiga.k-img.com/images/movie/56489/315x210.jpg?3786", 93, "日本の人気格闘ゲームをマギー・Q主演で実写映画化。「THE KING OF FIGHTERS」の称号をかけ戦うファイターたちのトーナメントに、かつて出場禁止処分を受けたルガールが復しゅうにやってくる。美しい女ファイターの不知火舞は、ルガールに力を与えている「オロチ」を封印するため「草薙剣（くさなぎのつるぎ）」を探しはじめる。しかし、その間にもトーナメントはルガールに支配され、ファイターたちが次々と犠牲になっていく。", "2011-07-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・拝啓車寅次郎様", "男はつらいよ・拝啓車寅次郎様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "就職して半年が過ぎた満男は、大学の先輩から誘われ、祭でにぎわう長浜を訪れる。案内役は先輩の妹、奈穂。二人は急速に打ち解け、満男が思わず“付き合っている人いるの？”と尋ねた時、背後から“いたっていいじゃないか。そいつと勝負するんだ”という寅さんの声……。日本三大山車祭りの一つ、湖国近江の“長浜曳山祭り”を舞台に、またも伯父と甥の恋が燃え上がる。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOMEONE AT THE DOOR", "扉の人影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "サリーと弟ロニーは親戚の残した不気味な豪邸に住んでいた。ニュース記者のロニーはスクープ記事を取りたいがため、サリーとその恋人ビルとの３人でサリーの偽装殺人を計画。ところが彼らの思惑とは裏腹に、豪邸の隠し部屋から宝石が発見されるやら、召使の死体が湖に上がるやら、３人は本当の特ダネ事件に巻き込まれるハメになる。全編に散りばめられたギャグと、軽妙なストーリー展開、そして３人のキャラクターが楽しい。", "1950-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ISRAEL VIBRATION", "イズラエル・ヴァイブレーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "ジャマイカの伝説的なレゲエ・ヴォーカル・グループとして世界的に知られる“イズラエル・ヴァイブレーション”のライブ・ドキュメント。彼らの聖地であるイズラエルで'93年に行われた公演の模様を収録。荒んだ生活を送りながらも音楽を続けてきた彼らの秘話も明かされる。", "2005-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARK CIRCLE", "ダークサークル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "1980年代、世界的規模で保有されるプルトニウムの驚異を、女性作家のアーヴィングが綿密な取材と多くの資料をもとに作り上げた異色のドキュメンタリー。タイトルは核廃棄物の処置を危険視した“暗黒の循環”という意味。主にアメリカの放射能汚染に関係した民間人や反核団体のグループが出演している。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOULOU　GRAFFITI", "パリの大泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "J・レノがスゴ腕の大泥棒に扮したコミカル・アドベンチャー。ピクラルンは巧みな話術と大胆な計画でパリを騒がせる天才的な泥棒。ある日、相棒の少年ルルが女性発明家ジュリエットと知り合った。子供に夢を与える発明をした彼女だったが、その発明品が悪徳企業に盗まれようとしていた。ルルに説得されたピクラルンは渋々、ジュリエットを救うためにひと肌脱ぐが……。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Home of the Brave", "勇者たちの戦場", "", "", "http://image.eiga.k-img.com/images/movie/53249/315x210.jpg?2111", 107, "「五線譜のラブレター／DE-LOVELY」のアーウィン・ウィンクラー監督が、イラク戦争から帰還したアメリカ人兵士たちの苦悩を描いた戦争ドラマ。イラクで人道的任務に就いていた兵士たちが、帰国直前に反米勢力による襲撃を受けた。熾烈な戦いの末に1人が死亡し1人が重傷、残る面々も心に大きな傷を負う。その傷は故郷へ帰っても癒えることはなく……。出演はサミュエル・L・ジャクソン、「ステルス」のジェシカ・ビールほか。", "2008-01-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BANQUIE｀RE", "華麗なる女銀行家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "一介の貧しい帽子売りから社交界のヒロインとなり、フランス初の“女銀行家”として絶頂を迎えたエルマ。しかし、敵対勢力による恐るべき陰謀が着々と進行していた……。第一次大戦後の混乱期に名をはせた、実在の女性銀行家マルト・アノーの生涯を豪華キャストで映画化。", "1980-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビー・バップ・ハイスクール 高校与太郎哀歌", "ビー・バップ・ハイスクール 高校与太郎哀歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "城東工業との喧嘩に明け暮れるトオルとヒロシ。そんな時、今日子が愛徳高校に戻って来た。二人は俄然張り切るが、城東のワルに徹底的に叩きのめされる。１度は自信を失くした二人だが、今日子を人質にとった城東勢が待ち受けるドライブ・インに敢然と乗り込んだ。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hills Have Eyes", "サランドラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "　トレーラーで旅していたボブ・カーターとその家族一行はかつて核の実験場だった民間人立ち入り禁止エリアに迷い込む。しかもそこで車が故障。荒野の真ん中で立ち往生するボブたちはやがて思わぬ恐怖に遭遇する。彼らは異常な殺人鬼一家に襲われ、次々と犠牲に。しかも赤ん坊も連れ去られてしまい……。ウェス・クレイブン監督の初期傑作ホラー。06年にクレイブン製作によるリメイク版も公開された。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラビット・ホラー3D", "ラビット・ホラー3D", "", "", "http://image.eiga.k-img.com/images/movie/55729/315x210.jpg?2633", 83, "Jホラー界を代表する清水崇監督が、香川照之と満島ひかりのダブル主演で描く3Dファンタジー・ホラー。「恋する惑星」「花様年華」などウォン・カーウァイ監督作品で知られるクリストファー・ドイルが撮影監督を務める。3D映画を鑑賞していた姉と弟が、スクリーンから飛び出してきたウサギのぬいぐるみを手にしたことで、巨大な遊園地のようなワンダーランドに迷い込む姿を描く。", "2011-09-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Roots of Heaven", "自由の大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "　戦後フランス領アフリカへ渡ったモレルはアフリカ象保護を立法化するための運動を起こす。全アフリカ連邦結成を夢見る黒人の民族主義運動家と関わりながら、モレルの活動は続くが……。アフリカを舞台に、運動家の闘争を描く人間ドラマ。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い画集・ある遭難", "黒い画集・ある遭難", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "松本清張の原作を石井輝男脚色、杉江敏男監督で映画化。江田をリーダーとする浦橋、岩瀬の３人のパーティーは鹿島槍で遭難。岩瀬は、黒部渓谷に転落死するが、岩瀬の妹は兄の死に疑問を抱く……。鹿島槍のロケーションが臨場感を盛り上げた。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("（ON THE BOAT）", "（ON THE BOAT）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "世界を一周するクルーズ船を舞台に、一般公募で集められた６人の監督が、素人俳優を起用して撮り上げた異色作。世界各地での一期一会を切り取った、味のあるドラマが繰り広げられる。海賊映画の撮影が行われているクルーザーの船上。行き当たりばったりの撮影にウンザリした助監督の西川は降板。彼は自分でカメラを持って船上の人々を追いかけることに。そして西川の代わりに乗客の池田が助監督を務め……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Factory", "ザ・ファクトリー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAND OF STEEL", "片腕サイボーグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "近未来。利権を妨害する“尊師”を暗殺するため悪徳企業家に雇われたサイボーグ、パコ。体の70％は機械だが、残りの30％の人間性が暗殺を未遂に導くのだった。逆に依頼人から追われる身となったパコは故郷に向かうのだが……。「ターミネーター」もどきのサイボーグの活躍と特殊メイクで見せるSFアクション。", "1986-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES DIABOLIQUES", "悪魔のような女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "パリ近郊の私立学校。校長のミシェルは、妻クリスティナの財産のおかげで地位を得たが、女教師ニコルと公然の愛人関係にある。しかし、彼があまりにも利己的な暴君であるため、妻と愛人の二人は共謀して彼を殺すことに……。ドンデン返しがショッキングなサスペンス・ムービー。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うたかた", "うたかた", "", "", "http://image.eiga.k-img.com/images/movie/54279/315x210.jpg?3590", 100, "少女の青春をテーマに、大人と子供の中間で揺れ動く少女を描いた短編5話で構成されるオムニバス映画。主演は、アイドルグループ「アイドリング !!」の長野せりな、人気グラビアアイドルの渡辺万美、「うた魂♪」「トワイライトシンドローム／デッドゴーランド」の吉川まりあほか。", "2008-12-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パッチギ！ LOVE＆PEACE", "パッチギ！ LOVE＆PEACE", "", "", "http://image.eiga.k-img.com/images/movie/1809/315x210.jpg?3621", 115, "大ヒットを記録した前作から2年、舞台を68年の京都から74年の東京へと移して製作された井筒和幸監督の青春群像劇第2弾。難病を抱えた息子の治療のために京都から上京してきたアンソンとキョンジャの世間との“格闘”と、太平洋戦争末期、生き残ることに必死だったアンソン兄妹の父親たちの戦いを並行して描く。主演の2人には2200人のオーディションから選ばれた井坂俊哉と中村ゆり。共演に藤井隆、西島秀俊ら。", "2007-05-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SERGEANT RUTLEDGE", "バファロー大隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "砦の司令官の娘が暴行され殺された。現場で目撃された黒人兵に嫌疑がかかるが、法廷での取り調べで事件は二転三転し、真犯人は意外なところに……。J・フォード得意の騎兵隊ものではあるが、基本的な構成は法廷を舞台としたフラッシュバックの回想劇。心理描写が見事な社会派スリラーともいえる。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT COULDN’T HAPPEN HERE", "ペット・ショップ・ボーイズ 夢色の幻想", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "日本でも数多くのファンの支持を得たイギリスの人気デュオ“ペット・ショップ・ボーイズ”の二人が、初めて映画に挑戦した一風変わったロード・ムービー。盲目の僧侶や逃亡中の殺人鬼など、様々な人々と出会うスリルと驚きに満ちた彼らの冒険の旅を、おなじみのヒット曲を全編に散りばめながら描く。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("絶世好BLA／LA BRASSIRE", "恋するブラジャー大作戦(仮)", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ランジェリー・メーカーを舞台に、最高の下着を作ろうと試行錯誤する社員たちの恋と仕事を、軽やかに描いて大ヒットした香港産ラブ・コメディ。色鮮やかで美しい下着の数々や、スタイル抜群の女性陣にも目を奪われるオシャレな1作だ。究極のブラを目指して、日本の下着メーカーが香港へと乗り込む。そこで抜擢されたのが2名の男性社員。しかし彼らをめぐって社内の恋愛事情が活性化。果たして究極のブラの完成は？", "2006-06-24", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("き・れ・い？", "き・れ・い？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「オールナイトロング」の異才、松村克弥監督が、直木賞作家、唯川恵の短編小説『めまい』を映画化したサイコ・ホラー。 美容整形に異常な執念を燃やす女性が、その念を“夢を叶えた”美人整形外科医に向かって放射していく様を描き出す。「六月の蛇」で高い評価を得た黒沢あすかが、女性心理の異常なねじれを大胆に体現して、観る者を驚かせる。美人整形外科医の野口庸子は、独立開業したクリニックが好調で、多くの患者が訪れていた。そんなある日、吉江と名乗る風変わりな女性が現れ、300万円を差し出し、診療時間外に手術をしてほしいと庸子に迫るのだが……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHIME｀RE", "シメール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "愛し合い、同棲して一年になるアリスとレオ。アリスの妹ミミはこのカップルを愛し、またうらやんでもいた。しかしアリスは妊娠し、それを知ったレオは彼女を避けはじめる。幸せな関係が崩れ、二人の突然の別れにショックを受けたミミは自殺を図る。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL GATTO A NOVE CODE", "わたしは目撃者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "染色体の研究で知られるテルジ研究所の学者が言い争っているのを偶然耳にした盲目のアルノ。元新聞記者だった彼は事件の気配を感じて、後輩記者のジョーダンに応援を求め調査を開始するが、関係者は次々と殺されていく……。ホラー味がにじむアルジェントのスリラー。", "1970-01-01", "4,7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("完全なる飼育・秘密の地下室", "完全なる飼育・秘密の地下室", "", "", "http://ecx.images-amazon.com/images/I/5156284D65L._SL210_.jpg", 111, "シリーズ通算４作目となる今回は著者である松田美智子が書き下ろした同名小説を映画化し、装いも新たになった。毎回、注目を集めるヒロイン役にCFで活躍していた、したらひさこが挑む。恋人に暴力を振るわれ、公園で気絶した梨里は目覚めるとタケルという男に監禁されていた。初めは逃亡を考えた梨里だが、優しく接する彼に興味を抱くようになる。やがて彼女は彼がある女性とのことでトラウマを抱えていることを知る。", "2003-08-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超劇場版ケロロ軍曹　撃侵ドラゴンウォリアーズであります！", "超劇場版ケロロ軍曹　撃侵ドラゴンウォリアーズであります！", "", "", "http://image.eiga.k-img.com/images/movie/53639/315x210.jpg?1617", 0, "地球侵略を企てるカエル型宇宙人が大騒動を繰り広げる人気ギャグアニメの劇場版第4弾。謎の巨大物体“竜のしっぽ”が世界中に突如出現し、宇宙の彼方ケロン星から地球侵略にやってきたケロロ軍曹はその正体を探るため、ニューヨーク、パリ、シドニー、タンザニア、奥東京市へと向かう。シリーズ誕生10周年を記念し、原作・吉崎観音により明かされたエピソード・ゼロ「ケロ0（ゼロ） 出発だよ！全員集合!!」を同時上映。", "2009-03-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REVENGE OF THE NERDSII：NERDS IN PARADISE", "ナーズの復讐II ナーズ・イン・パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "落ちこぼれ集団“ナーズ”はいつも優等生集団“アルファー”の迫害を受けてばかり。ついに怒りが頂点に達したナーズたちはミリタリー・ルックに身を包み、装甲車で大逆襲を開始する。リゾート地フロリダを舞台に展開する痛快な学園コメディ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMMANUELLE L’ANTIーVIERGE", "続・エマニエル夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "前作でめくるめく愛と性の喜びに目覚めたエマニエル夫人。今回は香港を舞台に、彼女の新しいボーイフレンドとなる青年パイロットや、彼女に性の手ほどきを受ける上流階級の娘などが登場。有名なテーマ曲にのせ、エキゾチックで甘美な世界が展開される。", "1975-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUNK FOOD／ジャンク フード", "JUNK FOOD／ジャンク フード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "海外でも注目を浴びる俊英、山本政志の鮮烈なストリート・ムービー。パン屋に行くことを日課とする一人暮らしの老女のエピソードで始まり、ドラッグ漬けのOLの大波乱の１日など、無国籍化が進む都市の混沌とした出来事を点描。また実際のチーマー、暴走族、在日外国人などの素人が大挙出演し、それぞれの世界を荒々しくも自由な感性の映像で活写されていく。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOOD OLD BOYS", "ワイルドメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "その独特の雰囲気で、一躍スター俳優の座についたT・L・ジョーンズが、20年来の夢だった監督業を実現させ、加えて脚本、主演の３役をこなした作品。殺伐としたアメリカ西部。新時代20世紀の狭間に生きるアウトローのヒューイは、２年間の放浪生活の後、兄のウォルター一家を訪ねる。しかし彼らは銀行と不動産屋から牧場の借金返済を迫られ、窮地に立たされていた。そんな折、ヒューイは女教師のスプリングと出会い、彼女に好意を持ち始める。スプリングへの思いは募る一方、返済期限も迫る。文明社会の一員として生きるか、再びアウトローとなるか、ヒューイは人生の決断を迫られる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("左右", "我らが愛にゆれる時", "", "", "http://image.eiga.k-img.com/images/movie/56809/315x210.jpg?2285", 115, "カンヌ、ベルリン、ベネチアの世界3大映画祭の受賞作を中心に、各地の映画祭で話題になりながらも日本未公開だった作品を一挙上映する「三大映画祭週間2011」にて上映。余命わずかの娘ハーハーを救うため骨髄移植が必要なメイ・チューだったが、自分も前夫のシアオ・ルーも適合検査で一致しなかった。焦ったメイ・チューは人工授精でもう1人子どもをもうけ、臍帯（さいたい）血移植でハーハーを助けようと決意するが……。「北京の自転車」「上海ドリーム」などで知られ、中国第6世代を代表するワン・シャオシュアイの監督作。2008年・第58回ベルリン国際映画祭で銀熊賞（脚本賞）を受賞。", "2011-08-13", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Palindromes", "おわらない物語　アビバの場合", "", "", "http://image.eiga.k-img.com/images/movie/1269/315x210.jpg?3758", 100, "「ハピネス」「ストーリーテリング」のトッド・ソロンズ監督の新作は、監督第1作「ウェルカム・トゥ・ドールハウス」のヒロインであるいじめられっ子ドーンの従姉妹、アビバの物語。12歳のアビバは母親になって子供を愛したいと思って妊娠するが、母親に中絶させられ、家を出てさまざまな人々に出会っていく。主人公アビバの役を、6歳から成人まで、年齢も人種も性別さえも異なる8人の俳優たちが演じるというユニークな演出法。", "2005-06-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若い人", "若い人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "石坂洋次郎の原作を石原裕次郎・浅丘ルリ子・吉永小百合の三大スター競演で映画化した青春ドラマ。特殊な生活環境からひねくれた育ち方をした思春期の少女の多感な心理を描く。原作は今度が３度目の映画化で、前２作では原作通り北国を舞台にしているが、ここでは舞台が南国に移っている。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREAKHEART PASS", "軍用列車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "伝染病で全滅しかかった山岳砦救援のため、白銀のロッキーを爆走する軍用列車。それを待ち受ける狂暴なインディアンと魔の急勾配“ブレイクハート・パス”。名作「ナバロンの要塞」の冒険作家マクリーンの原作を、アメリカ建国200年を記念して映画化。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天皇・皇后と日清戦争", "天皇・皇后と日清戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "「明治天皇と日露大戦争」の大ヒットによって息を吹き返した新東宝が、二匹目のドジョウを狙って製作した姉妹編的戦争絵巻スペクタクル。日清戦争がどのようにして勃発し、そして終結していったのかを、明治天皇と昭憲皇后を主軸に据えながらパノラマティックに描出。浪花節的な語り口で、戦前ではおなじみだった様々なエピソードが披露されていく。別題「明治大帝と日清戦争」。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Un homme et une femme", "男と女", "", "", "http://ecx.images-amazon.com/images/I/41cK0sVwLrL._SL210_.jpg", 104, "当時無名だったフランスのクロード・ルルーシュ監督が自ら資金を調達して製作した恋愛ドラマ。主演はアヌーク・エーメとジャン＝ルイ・トランティニャン。スタントマンの夫と死別した脚本家アンヌ（エーメ）と、妻に自殺されたカーレーサーのジャン・ルイ（トランティニャン）による大人の恋愛がフランシス・レイの音楽と美しい映像にのせて綴られる。66年の第19回カンヌ国際映画祭ではパルム・ドールを、同年度のアカデミー賞では外国語映画賞を受賞した。", "1966-12-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STORYVILLE", "ストーリービル 秘められた街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "何代も続いた名家に生まれ、将来を約束された青年クレイ。だが彼は、父の死に疑惑を抱いたことから自らも殺人事件に巻き込まれ、次第に権力と金、怨恨と陰謀が渦巻く猥雑なダークゾーンへと足を踏み入れていく。デヴィッド・リンチとともに「ツイン・ピークス」をプロデュースした、M・フロストの劇場映画初監督作。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARIANNE DE MA JEUNESSE", "わが青春のマリアンヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "霧の立ち込める湖の彼方にそびえ立つ謎の古城。対岸のインゲンシュタットの館に住む少年たちは、この古城に興味を惹かれ、冒険に出発する。一行は大きな犬に襲われて逃げ帰ってくるが、バンサンだけ取り残される。彼はそこで美しい乙女マリアンヌと出会い、彼女に心を奪われる。それ以来、バンサンの心にはマリアンヌの面影しかなかった。ある日、マリアンヌから助けを求める手紙がきて、バンサンは夢中で湖を泳いで古城に向かうのだが……。そして２日後、湖の岸で血まみれになったバンサンの姿が発見される。青春の美しい記憶の一頁をドイツ・ロマン主義の神秘的な幻想の中で描いた珠玉の一編。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRAIN DONORS", "大錯乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "「裸の銃を持つ男」「フライング・ハイ」などの超ナンセンスのドタバタ・コメディで知られるザッカー兄弟が製作総指揮を担当した爆笑コメディ。「バートン・フィンク」で脚光を浴びたJ・タトゥーロが、口八丁手八丁のいい加減な弁護士に扮し50万ドルの報酬でバレエ団設立に東奔西走。バレエ界のスーパースター、ボラーレ獲得に乗り出すが……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sergeant York", "ヨーク軍曹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "第1次大戦の西部戦線で、わずか7人の部下と共に132人のドイツ兵を捕虜にし、数々の名誉を得て、一躍全米のヒーローとなった゛テネシーの山男゛アルビン・カロム・ヨークの実話を映画化した。映画化までの道のりは険しく、撮影も予定を超過、製作費も大幅に増えたものの、主演のゲイリー・クーパーはオスカーを受賞し、熱狂的な反響を呼ぶ作品となった。", "1950-09-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いろは若衆 花駕籠峠", "いろは若衆 花駕籠峠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "老中水野越前守の隠し子お雪は、浮浪児の三太を伴って旅に出る。途中、稲葉小僧新助という男に出会い、一通の封書をあずけられる。封書は林肥後一味の連判状で、大阪米問屋と結託して米の買い占めによる値上がりを図り、米騒動を起こして越前守の失脚を狙うというものだった。美空ひばりの歌謡股旅もの。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超新星フラッシュマン　大逆転！タイタンボーイ", "超新星フラッシュマン　大逆転！タイタンボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "スーパー戦隊シリーズ「超新星フラッシュマン」の劇場版第2弾。巨大ロボ・フラッシュキングが倒れ、今こそフラッシュマンを倒すチャンスだと考えたメスが行動を開始した。獣戦士ザ・ジライカに襲われたバスを救いに来たフラッシュマンの前に謎の巨大暴走車が出現し、中からバラキという男が姿を現わす。かつてフラッシュ星の英雄タイタンに破れて彼と友情を結んだというバラキは、タイタンから託されたフラッシュタイタンを地球に届けに来ていたのだった。", "1987-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雲の上団五郎一座", "雲の上団五郎一座", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "菊田一夫の舞台劇の映画化。ドサ回り劇団・雲の上団五郎一座の座長・団五郎は大劇場で公演する夢を抱いている。四国で出会った酒井という青年と二人三脚で大興行を打とうとするが……。エノケンとフランキーの新旧コンビが絶妙。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUST FOR GOLD", "秘境", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "40年代から50年代にかけてハリウッドで作られた、それまでとは違うタイプの西部劇の一作。金塊を巡って凄まじい抗争が勃発するという、争奪アクションものだ。西部劇俳優たちの中でもアウトローなどを含め、性格俳優ぶりを遺憾なく発揮したG・フォードが渋い。テキサスと並ぶ西部劇舞台の聖地・アリゾナをバックに、貴重な金鉱石資源を探して男たちが争いを繰り返す。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eraserhead", "イレイザーヘッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "鬼才デビッド・リンチ監督、衝撃のデビュー作。悪夢のような出来事に見舞われ正気を失っていく男を、全編モノクロ映像で綴る。消しゴムのような髪形をしていることからイレイザーヘッドと呼ばれるヘンリー。ガールフレンドから奇形の赤ん坊を産んだことを告白された彼は、彼女との結婚を決意する。ところが、彼女はおぞましい形相で金切り声をあげる我が子に耐え切れず、家を出てしまう。残されたヘンリーは1人で赤ん坊を育てるが……。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("返事はいらない", "返事はいらない", "", "", "http://image.eiga.k-img.com/images/movie/57349/315x210.jpg?3049", 94, "", "2011-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARMED FOR ACTION", "ガンシティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "J・エステベス主演の刑事アクション。タワーズ刑事は、ベトナム帰りで、有名なマフィアのヒットマン、モンテルの護送中に武装集団に襲われ、モンテルを連れ去られる。モンテル奪還のため、タワーズとマフィアの壮絶な戦いが始まる……。5000発の弾丸を使用し、マシンガンやバズーカの発砲など全編が爆発に彩られている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古嶺街少年殺人事件／A BRIGHTER SUMMER DAY", "古嶺街〈クーリンチェ〉少年殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 188, "“台湾ニューウェーブ”の旗手と呼ばれ注目されていたエドワード・ヤンの日本初公開作。1991年の東京国際映画祭で絶賛され、審査員特別賞ほかを受賞。1960年代の台北で、一人の中学生の少年が同級生の少女を殺す事件が起きた。実際に起こったその事件に至るまでの二人の姿と彼らをめぐる人々の物語を、当時の台北の歴史的背景を浮き彫りにしながら描いていく。青年ギャング団の抗争、プレスリーに憧れバンドで歌う少年の夢、大陸に帰りたいと願う少年の親たちの世代の焦りと不安。そしてそんな時代の中で出会った少年と少女の愛はなぜ悲しい結末を迎えたのか……。出口を失った青春群像を力強い映像で見せる入魂の一作である。出演者の半分以上は映画初出演の素人俳優。E・ヤン自身が長期間にわたりみっちりと演技を仕込んで撮影に臨んだという。なお、日本では４時間のロング・バージョンも公開された。", "1991-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("れっしゃだいこうしん　ザ☆ムービー", "れっしゃだいこうしん　ザ☆ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "日本中の列車を特集するDVD「日本列島列車大行進2010」のキッズナレーション版をベースに製作された劇場版。新幹線や特急など最新の列車から、ローカル線を走る鈍行列車やSL、新幹線区間の線路を点検する特殊車両「ドクターイエロー」など日本の列車を幅広く紹介。子どもたちが列車をわかりやすく学べるためのクイズコーナーなども取り入れられている。", "2010-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUICK", "銃弾の微笑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ギャングに狙われた女殺し屋の逃避行を描くサスペンス・アクション。賭博好きの愛人に貢ぐため殺し屋稼業に精を出す美女クイック。ギャングの金を横領した会計士ハーシェルの護送を引き受けた彼女だったが、この仕事の影には罠が。一緒に逃げ回るクイックとハーシェルの間に、やがて恋心が芽生え……。1993年ヒューストン映画祭で最優秀作品賞受賞。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心臓破りの丘", "心臓破りの丘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ヘルシンキ・オリンピックに惜敗した栄三は、母の一言で一念発起。ランナーとしての再起を懸け、ボストン・マラソン優勝のため、心臓破りの丘をひた走る。マラソンに青春のすべてをぶつけた青年ランナーの姿を、感動的に描いたスポーツ・ドラマ。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自由な女神たち", "自由な女神たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "男のために整形美女となった、内気でちょっぴり頭の軽い女・徳子と、その場しのぎの嘘ばかりついているお人好しの女・咲江のコンビが、田舎の温泉場を舞台に繰り広げるドタバタ・コメディ。松坂、桃井が初共演、息の合ったおトボケぶりをみせる。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Running Out of Luck", "ランニン・アウト・オブ・ラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ローリング・ストーンズのフロントマン、ミック・ジャガーのソロ・デビュー・アルバム「シーズ・ザ・ボス」に収録されている「ランニン・アウト・オブ・ラック」という曲を基に、ミック本人が主演をつとめたミュージカルタッチのコメディ作品。メガホンを取るのは、映画「セックス・ピストルズ／グレート・ロックンロール・スウィンドル」や、多くのアーティストのミュージック・クリップを手掛けたことで知られるジュリアン・テンプル。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("War of the Arrows", "ウォー・オブ・ザ・アロウズ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-31", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BORDER", "ボーダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "メキシコとの国境近くの町エル・パソ。国境警備の仕事に就く元警官は、身分不相応なぜいたくを望む妻にへきえきする一方、貧しいメキシコ人に同情を寄せていた。金と色と欲に汚れた現実をイギリス人監督が現代アメリカへの風刺を込めて描く社会派映画。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学校 II", "学校 II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "1993年の「学校」の姉妹編ともいうべき作品。主演、テーマ、物語、構成などは同じだが、舞台と主人公は変わっている。北海道の高等養護学校を舞台に、問題児二人の成長を見守る教師、青山竜平の姿を描く。山田洋次作品常連の吉岡秀隆が、社会に馴染めない少年の魂を見事に体現。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crooklyn", "クルックリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　70年代のブルックリン。そこに暮らすカーマイケル一家は、売れないミュージシャンの父親と働き者の母親、そして5人の子供たちからなる賑やかな家族。パパは子供たちに優しいが、仕事もなく、金にならない曲ばかり作っている。そんな夫に愛想を尽かしたママの怒りがついに爆発。これが原因でパパは家を出て行ってしまうが……。スパイク・リー監督が綴るノスタルジックな家族ドラマ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カナリア", "カナリア", "", "", "http://image.eiga.k-img.com/images/movie/1319/315x210.jpg?4170", 132, "「害虫」「黄泉がえり」の塩田明彦監督が、オウム真理教事件をモチーフに描く話題作。カルト教団に入信した母に連れられて、妹と共に教団の施設で育った12歳の少年、光一。教団はテロ事件を起こして解散し、母は行方知れずになり、光一は児童相談所に預けられるが、祖父母に引き取られた妹を取り戻すために施設を脱走。その道中、援助交際をする少女、由希と出会い、一緒に旅するようになる。", "2005-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Marvin's Room", "マイ・ルーム", "", "", "http://ecx.images-amazon.com/images/I/519o4KvfRUL._SL210_.jpg", 99, "R・デ・ニーロが製作を務め、豪華キャストで描いた感動のヒューマン・ドラマ。屈折した17歳の長男ハンクに手を焼くリーの許に、20年間交流のなかった姉ベッシーから電話が入る。たった一人で父と叔母の面倒をみている彼女は、白血病に冒され親族からの骨髄移植を望んでいた。久しぶりの再会を喜ぶベッシーだったが、リーは気まずさをぬぐえない。ところが、反抗的だったハンクは、ベッシーの温かい人柄に触れ、次第に心を開いていく。そんな中、ベッシーの病状は着実に悪化していき……。絆を修復していく対照的な性格の姉妹を、M・ストリープ、D・キートンの二大オスカー女優が熱演。息子役のL・ディカプリオも実力派俳優らと堂々と渡り合い、演技力を発揮した。", "1997-02-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACK SWAN", "海の征服者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "17世紀、カリブ海を荒らし回った海賊ヘンリー・モーガンが、英国王チャールズ二世によって罪を許され、カリブ海の海賊一掃に功を挙げるまでを描いた海洋アドベンチャー。R・サバティーニの小説をもとに、H・キングが活劇の醍醐味もたっぷりに映画化した作品。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ごろつき２", "ごろつき２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "有栖川組の組長が路上で襲撃され、跡目相続をめぐって抗争が勃発。親友同士である若き組員の涼と瞳も、否応なしに血で血を洗う壮絶な戦いに巻き込まれていく。やがて、抗争が激化するにつれ、二人の間に溝が生じ始める……。ノンフィクション作家、家田荘子の小説を映像化した青春ヤクザ映画の続編。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bridge", "ブリッジ", "", "", "http://image.eiga.k-img.com/images/movie/34019/315x210.jpg?1357", 93, "世界最大の自殺スポットして知られるサンフランシスコのゴールデンゲート・ブリッジにカメラを設置し、自殺者が後を絶たない橋の様子を1年間に渡って記録し続けた衝撃のドキュメンタリー。飛び降り自殺の瞬間を捉えた映像や、自殺者の家族や友人、奇跡的に助かった人物への証言などを通し、生きる勇気と命の尊さを訴える。監督は「救命士」「アンジェラの灰」のプロデュースを手掛けてきたエリック・スティール。", "2007-06-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("See You", "See You", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "", "2012-06-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EVERLASTING SECRET FAMILY", "仮面の告白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "公人としては、信頼の厚い政治家で、幸福な一家の長でもある上院議員。だが彼は、危険な快楽を求めてデカダンの世界に足を踏み入れる。22年にわたる彼の秘密の生活の相手となった美青年は、今度は彼の息子に危険な復讐を仕掛ける……。オーストラリア上流階級の禁じられた断面にメスを入れた、異形のラブ・ストーリー。", "1988-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIDING　GIANTS", "ライディング・ジャイアンツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "大波に挑んだ歴代のサーファーたちの人間像に迫りつつ、ビッグウェーブ・サーフィンの歴史をたどるドキュメンタリー。あらゆる世代の名サーファーたちの発言を通して、サーフィンへの愛と旺盛なチャレンジ精神を見つめる。絶妙のタイミングで危険を回避し、大波に乗る華麗な技に注目。ハワイで、カリフォルニアで、次々と発見される大波が起こる海域。サーファーたちはそこに集い、不可能とも思える波に挑む。", "2005-07-09", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAGING THUNDER", "レイジング・サンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ベトナムの国宝を持ち出し、バンコクへ亡命したグエン。彼を捕らえようとするソ連側の暗殺グループが、グエンの娘を誘拐。それを知った娘の婚約者スコットは、彼女を助け出そうと敵陣に乗り込むが……。韓国式、タイ式など、ありとあらゆる格闘技が入り乱れて繰り広げられるアクション映画。タイ・オールロケも見どころ。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺ら東京さ行ぐだ", "俺ら東京さ行ぐだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "吉幾三のヒット曲『俺ら東京さ行ぐだ』をモチーフにし、映画の中に巧みに取り込んだテンポの良い作品。東京で一生懸命働く息子と、その生活ぶりが理解できず田舎に帰ることを勧める両親との確執を描く。作品のキーとなる部分で、吉幾三本人が顔を出す。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カムイ外伝", "カムイ外伝", "", "", "http://image.eiga.k-img.com/images/movie/53339/315x210.jpg?2244", 120, "白土三平の名作コミック「カムイ外伝」を、崔洋一監督、松山ケンイチ主演で映画化したアクション時代劇。宮藤官九郎が脚本を手がけ、共演に小林薫、小雪、伊藤英明、佐藤浩市らが揃う。掟に縛られた忍の世界から自由を求めて抜け出した忍者カムイは、ある小さな村で漁師・半兵衛の家族として迎えられ、同じ抜忍のスガルらとともに一時の平穏を得る。しかし、村人の密告により再び追われる身となったカムイは……。", "2009-09-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("亜州警察之高壓線", "ドラゴン電光石火1998", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「HERO」のD・イェンが迫力のカンフー・テクニックを見せるアクション。妻を殺した極悪非道のギャング、ディックを追って、香港の刑事チャンはマニラに向かう。彼はフィリピン警察のエドゥと協力して捜査を続けるが、ディックは犯罪組織のナンバー２にのし上がっていて、なかなか手が出せない。やがて、二人はディックの犯罪の証拠をつかむが……。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUEN　AND　RID", "ムアンとリット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "19世紀のタイを背景に、女性の権利のために闘った女性ムアンの半生を描く感動作。川で溺れたところを救ってくれた僧侶リットの側にいたいがために、ムアンはお寺へ行き、当時女性には認められていなかった文字を習い始める。学ぶことに喜びを見いだす彼女は、やがて父親によって金持ちの男性プーの元に売られてしまうが、脱走してリットと再会し結婚する。しかし、プーに姦通罪で訴えられた際、女性の権利を主張したムアンは投獄され、屈辱に耐えながら闘う決意をする。愛のために意志を貫いたムアンを、タイのトップ女優スッカパットが熱演。リットには、彼女の夫で同じくトップ・スターのプロムシリが扮している。", "1994-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルパンの奇巌城", "ルパンの奇巌城", "", "", "http://image.eiga.k-img.com/images/movie/56189/315x210.jpg?3930", 90, "モーリス・ルブランによる「アルセーヌ・ルパン」シリーズの一編「奇巌城」の舞台を日本に翻案して映画化。ある日、地元の名士・面河家の秘書・佐久間が殺害される事件が発生。しかし、事件のカギを握る面河家の一人娘ジュンコと使用人レイナの証言は食い違い、ナゾは深まるばかり。そんな時、事件を捜査する刑事・柿田川と女子大生探偵・伊地知は、事件現場に残された不可解な暗号を発見する。実力派声優・俳優の山寺宏一がルパン役に扮する。監督は「斜陽」「聖家族　大和路」の秋原正俊。", "2011-05-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG CLOCK", "大時計", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "社長の愛人ポーリンをアパートまで送って行った雑誌編集者のストロード。その夜、社長からポーリンが殺されたという電話を受け取った彼は、探偵を雇って調査にあたらせるが、どの報告も、その日ポーリンが一緒にいた男が犯人だとするものだった……。ケヴィン・コスナーの主演で大ヒットした「追いつめられて」の元ネタになった作品。設定に若干の変更はあるが、リメイク版同様の濃厚なミステリーがかもし出されている。", "1947-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泰造", "泰造", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ベトナム戦争下、26歳という若さでカンボジア戦線で消息を絶った日本人カメラマン・一ノ瀬泰造の物語。彼の遺著『地雷を踏んだらサヨウナラ』をベースに、母・信子の『わが子泰造』も加えて、最前線を駆け回る泰造の幼少年期からの生の軌跡をたどる。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE MARQUISE VON O…", "O侯爵夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "仏ヌーヴェル・ヴァーグを代表する監督の一人、ロメールが、H・V・クライストの短編小説を映画化。現代的な解釈を加えずに、原作を忠実に再現している。フランス革命直後のイタリア北部、身に覚えのない妊娠をしたO侯爵夫人が子供の父親になる人を求め、広告記事をはり出した。果たして、彼女の身に何が起こったのか……？ その数ヵ月前、夫人の住む城がロシア軍に攻められ、ロシア兵に犯されそうになった夫人はあやうくロシア軍の中佐F伯爵に助け出される。F伯爵は夫人に求婚するが、夫亡き後、貞淑を誓った彼女はかたくなに拒否。そんなある日、婦人の妊娠が発覚する。夜の室内はローソクのみ、昼は自然光の照明のみで撮影された映像美が見事。", "1975-01-01", "7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("下女", "ハウスメイド", "", "", "http://image.eiga.k-img.com/images/movie/56059/315x210.jpg?4104", 107, "上流階級の家にメイドとして雇われたウニは、優しい主人のフンと双子を妊娠中の妻、6歳の娘、そして昔から働くメイドのビョンシクに囲まれ、日々を忙しく過ごしていた。ある日、ウニは求められるままにフンと肉体関係を結んでしまうが、それ以降、邸宅で次々と不可解な事件が起こりはじめる。韓国映画界の名作「下女」（1960）をリメイクした官能サスペンス。主演は「シークレット・サンシャイン」（2007）でカンヌ国際映画祭最優秀女優賞を受賞したチョン・ドヨン。", "2011-08-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レイチェル・カーソンの感性の森", "レイチェル・カーソンの感性の森", "", "", "http://image.eiga.k-img.com/images/movie/55969/315x210.jpg?2239", 55, "1962年に発表した著書「沈黙の春」で化学物質の危険性をいち早く訴え、世界中に環境保護運動を巻き起こした米生物学者レイチェル・カーソンの遺作「センス・オブ・ワンダー」を映画化。カーソンが余生をおくったメイン州の海岸と森を舞台に、豊かな自然と触れ合いながら過ごした日々をドキュメンタリータッチで描きだす。カーソンに扮するのは、同作をもとにしたひとり芝居で、18年間にわたってカーソンを演じてきた女優カイウラニ・リーが主演を務める。", "2011-02-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPERGIRL", "スーパーガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "スーパーマンの従妹のカーラが、崩壊したクリプトン帝国の生命源“オメガヘドロン”をめぐって、世界征服をもくろむ魔女セリーナと闘う。カーラの母役でM・ファローが顔を見せる。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Land of the Lost", "マーシャル博士の恐竜ランド", "", "", "http://image.eiga.k-img.com/images/movie/53849/315x210.jpg?4351", 101, "「俺たちフィギュアスケーター」のウィル・フェレルが、奇抜な科学者を演じるアクション・コメディ。監督は「キャスパー」「レモニー・スニケットの世にも不幸せな物語」のブラッド・シルバーリング。タイムワープと異次元世界の可能性を信じるマーシャル博士だったが、学会で学説を馬鹿にされ、落ち目の一途を辿っていた。そんなとき、彼の学説を支持するケンブリッジ大学の才女ホリーが現れ、彼女の励ましのもとタイワープ装置を完成させるのだが……。", "2009-09-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新宿Loft BOOTLEG 1999", "新宿Loft BOOTLEG 1999", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "日本のロックの最前線として、数多くの人気バンドを世に送り出した新宿ロフト。1999年３月に西新宿から歌舞伎町に移転した、このライブハウスの23年の歴史を、セミ・ドキュメンタリーとして描写。ゆかりのミュージシャンたちや関係者のインタビュー、ライブ・パフォーマンスに、ロフトを愛する人々のドラマをからめながら、ロックンロールの息吹きを捉えていく。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HEARTBREAK KID", "ふたり自身", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "レニーは一目惚れしたライラと結婚、新婚旅行に出かけるが、彼女のマナーの悪さに失望する。そんなある日、金髪グラマーの美女にまたもや一目惚れ。すったもんだのあげく離婚、グラマー美女との結婚にこぎつけるが……。ダメ女をシェパードが好演。女流監督メイが風刺的に描いたコメディ。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎無頼控 魔性の肌", "眠狂四郎無頼控 魔性の肌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "90年前の島原の乱の折りに、ポルトガルから天草四郎に送られた秘宝マリア像を京都に献上する役目を命じられた武士とその娘。娘の出生の秘密を知った狂四郎は彼女に同行するが、謎の教団・黒指党が行手を阻む。工夫を凝らした殺陣と快調なテンポで水準以上の出来。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SANTA CLAUSE", "サンタクローズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ディズニー映画が手掛けた、サンタクロースになる契約をした男の悲喜劇を描くハートフル・コメディ。おもちゃ会社に勤めるエリート・サラリーマンのスコットは、バツイチで一人暮らし。離れて暮らす息子のチャーリーにも疎まれ、親子関係は今一つ。そんなクリスマス・イヴの夜スコットは、屋根から落ちてきたサンタクロースと次のサンタを引き受ける契約を交わすはめになり、それ以来身も心もサンタと化していく。優しくなった父親に喜ぶチャリーだが、スコットは周りからは変人扱いされ、あげくのはてには誘拐犯にされてしまい……。タイトルは、サンタクロースとサンタ契約が掛けられている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WITHOUT SCRUPLE", "黒い炎・情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ある日、何者かにレイプされた人妻シルビア。彼女は復讐を誓い男の正体を突き止めるが、逆に彼の異常な性愛の虜になっていく……。「O嬢の物語・第２章」で大胆なセックスシーンをこなしたS・ウェイが、再び自慢の裸身を惜し気もなく披露。", "1985-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Carnage", "おとなのけんか", "", "", "http://image.eiga.k-img.com/images/movie/57619/315x210.jpg?1281", 79, "トニー賞演劇部門の作品賞やローレンス・オリビエ賞の新作コメディ賞を受賞したヤスミナ・レザの舞台劇「大人はかく戦えり」を、「戦場のピアニスト」「ゴーストライター」のロマン・ポランスキー監督が映画化。子ども同士のケンカを解決するため2組の夫婦が顔をあわせ、話し合いを始める。最初は理性的に進められていた話し合いも、時間がたつにつれ各々の本性がむきだしになり、やがてそれぞれの夫婦間にも不協和音が生じていく。登場人物は4人のみで、室内でリアルタイムに進行する会話劇。ジョディ・フォスターとジョン・C・ライリー、ケイト・ウィンスレットとクリストフ・ワルツが2組の夫婦を演じる。", "2012-02-18", "2,7,42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Carlito's Way", "カリートの道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "　1975年、ニューヨーク。麻薬ビジネスで名を馳せた暗黒街の大物カリートが刑務所から出所。彼は犯罪から足を洗うことを決意し、クラブの経営者に収まる。かつての恋人ゲイルとよりを戻すこともできたが、捜査当局からは執拗にマークされ、さらにブロンクスの新興マフィア、ベニーとのいざこざも絶えない。そんな折、カリートはマフィアに脅迫されていた弁護士デイブを助けるが、それがきっかけで命を狙われるハメに……。パチーノとデ・パルマ監督が「スカーフェイス」以来、久々に手を組んだアクション・ドラマ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彼とわたしの漂流日記", "彼とわたしの漂流日記", "", "", "http://image.eiga.k-img.com/images/movie/55469/315x210.jpg?2724", 116, "「ヨコヅナ・マドンナ」で脚光を浴びたイ・ヘジュン監督による奇想天外なラブストーリー。主演は「トンマッコルへようこそ」のチョン・ジェヨン、韓流ドラマ「私の名前はキム・サムスン」のチョン・リョウォン。人生に絶望した男がソウルを流れる川・漢江に飛び込む。目覚めると孤島に漂着していた男は、島を脱出することを諦め、そこでサバイバル生活を始めることに。一方、川岸に建つマンションの一室に3年もひきこもる孤独な女は、カメラの望遠レンズ越しに孤島の男を発見し、何とか男へメッセージを送ろうとする。", "2010-06-19", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まむしの兄弟・お礼参り", "まむしの兄弟・お礼参り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "本州で、一家をかまえる藤島組の親分が、レジャーランドを建設しようとする兵頭組の申し出を断り暗殺される。政は藤島の娘・あきに一目惚れ。惚れた勢いで、いやがらせを続ける兵頭組へ、マシンガンで武装したまむしの兄弟が殴り込みをかける。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TERESA", "女トラッカー 甘い暴走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "美人でセクシーなテレーザは、女トラック運転手。同じトラック仲間のジーノと知り合い、気のいい彼とコンビを組むことになるが、なぜかそれ以来トラブルが続発する。ソフィア・ローレンの再来ともいわれたS・グランディが、お色気たっぷりのアクションを披露。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Madagascar 3: Europe's Most Wanted", "マダガスカル3", "", "", "http://image.eiga.k-img.com/images/movie/55159/315x210.jpg?1810", 0, "都会育ちの動物たちが巻き起こす騒動を描くドリームワークス・アニメーション「マダガスカル」シリーズ第3作。ニューヨークの動物園で育ったライオンのアレックス、シマウマのマーティ、キリンのメルマン、カバのグロリアが、モンテカルロからローマ、スイス・アルプスを経てロンドンへとヨーロッパを横断する冒険を繰り広げる。", "2012-08-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRING ME THE HEAD OF ALFREDO GARCIA", "ガルシアの首", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "1960～70年代のアメリカB級活劇を代表するS・ペキンパーの後期の傑作。広大なメキシコを背景に、酒場のピアノ弾きがすでに死んでしまった男の首を手に入れるため、墓場をさまよい歩く。ペキンパーならではの渇いた演出と、賞金の懸かった首をめぐって変容する人間模様の描写が見もの。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moon Over Parador", "パラドールにかかる月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　ニューヨークの俳優ジャックは、カリブ海の小国パラドールで映画を撮影中に、死んだ独裁者の身代わり役を依頼される。パラドールで要職に就くストラウスマンは自らの手で国を操ることができるようになるまで、君主の死を秘密にしておきたかったのだ。しかし君主の情婦だったマドンナには別の考えがあった……。一世一代の大芝居に挑む俳優役にリチャード・ドレイファスが扮し、再びマザースキー監督と組んだコメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("麗霆”子 レディース！　総長最後の日", "麗霆”子 レディース！　総長最後の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "渡辺美奈代主演の“女暴走族＝レディース”もの第２作。３ヵ月後に引退する麗霆”子〈レディース〉初代総長さとみに、関東制覇をもくろむ“埼玉暴走連盟 羽衣天女”が戦いを挑んでくる。それに対抗するためさとみは、千葉の５チームで連帯するが、“羽衣天女”の攻撃は執拗を極め、さとみ以外の総長はみなチームを去るのだった……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陰日向に咲く", "陰日向に咲く", "", "", "http://image.eiga.k-img.com/images/movie/34069/315x210.jpg?3477", 129, "劇団ひとりのベストセラー小説を、岡田准一、宮崎あおい、西田敏行ほか豪華キャストで映画化した群像劇。借金苦からオレオレ詐欺に手を染めたもののカモの老婆に親近感を抱いてしまう青年、若い頃売れない芸人に恋をしていた母の青春時代を辿る娘、25歳の崖っぷちアイドルと、彼女を応援するアキバ系オタク、大嘘つきのカリスマホームレスに憧れるサラリーマン。年代も境遇もバラバラな彼らの人生が、ある台風の日にひとつに重なり……。", "2008-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAPPY　END", "ハッピーエンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "主演のチョン・ドヨンの大胆な全裸シーンが話題になっただけでなく、数々の女優賞に輝き高い評価を得た作品。失業中で妻の代わりに育児や家事に励む夫とキャリアウーマンの妻とその不倫相手、という三角関係。ありがちなメロドラマを想像してしまうが、巧妙な人間描写と緊迫感のある映像で描いた絶妙なラブ・スリラーに仕上がっている。失業中の夫と幼い娘を抱え、仕事に忙しいボラは、結婚後に再会した昔の恋人イルボムとひそかに不倫中。夫への愛が冷めたわけではなく、イルボムとの相性の良すぎるセックスから逃れられないだけなのだが、それに気付いた夫は……。", "1999-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kiss Kiss [Bang Bang]", "キス★キス★バン★バン", "", "", "http://image.eiga.k-img.com/images/movie/1479/315x210.jpg?1664", 101, "殺し屋フィリックスは組織に引退を宣言。が、父親の老人ホームの家賃を払うため、密輸業者の過保護息子で、33歳になるのに子供部屋を出たことがないババの子守りをすることに。一方、フィリックスの引退を許さない組織は、彼の弟子ジミーに彼の殺害を命じる。監督スチュワート・サッグはこれが2作目の英国の新鋭。音楽はモニカ・ビッティ主演のスパイ映画「唇からナイフ」(66)のジョン・ダンクワースが担当するなど60年代趣味が満載。", "2002-12-21", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIDAY THE １３TH PART VIII：JASON TAKES MANHATTAN", "13日の金曜日PART8　ジェイソンN.Y.へ", "", "", "http://ecx.images-amazon.com/images/I/51J0RR4W8NL._SL210_.jpg", 100, "スプラッタ・ムービーの元祖シリーズ８作目。クリスタル・レイクを出て、マンハッタン行きのクルーザーに乗り込み、ついにニューヨークへやって来たジェイソン。ポリスマンやストリート・ギャング相手に殺人を重ねていく“彼”に、ニューヨーカーがどんな反応を見せるのかお楽しみ。", "1989-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十一人の侍", "十一人の侍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「十三人の刺客」「大殺陣」などの集団時代劇に力作を数多く残す工藤栄一監督の「日本暗黒史・血の抗争」に続く1967年度作品。“トラック野郎”シリーズの監督・鈴木則文が脚本に参加している。館林藩主・松平齊厚の短気が原因で家老・阿部正由が殺された。次席家老・榊原帯刀はこれを提訴したが認められず、仙石隼人に齊厚暗殺を命ずる。隼人は同志９人を引き連れて江戸に上り、暗殺計画を練る。計画は一旦頓挫したものの、ついにチャンス到来。豪雨の中で悽惨な死闘が繰り広げられる。このラストの大殺陣場面によって、武家政治の暗黒のむなしさや無意味さがヒシヒシと伝わってくる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUGGERNAUT", "ジャガーノート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "２万5000トンの豪華客船ブリタニック号が航海する北大西洋を舞台にメガトン級の爆弾が７個仕掛けれられた。20数時間後に、それが爆発すれば1200人の命が奪われる。刻一刻と迫りくる危機に挑む爆薬処理班の男たち。スリリングな盛り上がりを見せる演出が鮮やかなサスペンス。", "1974-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林塔棚大師／RETURN TO THE 36 CHAMBER", "続・少林寺三十六房", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「少林寺三十六房」のヒットを受け、同じスタッフ・キャストで製作された続編。悪徳染物商人の暴力に立ち向かうも、逆に叩きのめされた修行僧が、強くなりたい一心で少林寺に弟子入り。厳しい修業を積みながら、独自の拳法を生み出していく。前作とのストーリー的なつながりはないが、修業シーンは前作以上にユニークで、縄や竹を駆使した拳法の見せ場も登場。", "1980-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Talladega Nights: The Ballad of Ricky Bobby", "タラデガ・ナイト　オーバルの狼", "", "", "http://ecx.images-amazon.com/images/I/51m%2BNbOEB7L._SL210_.jpg", 121, "「俺たちニュースキャスター」のウィル・フェレル＆アダム・マッケイ監督が再びタッグを組み、アメリカで人気のモーターカーレース「NASCAR（ナスカー）」を題材に描いたコメディ。NASCARのトップレーサーにのぼりつめたリッキー・ボビーは、富も名声も手に入れ順風満帆の毎日を送っていた。そんなある日、フランスのF1レーサー、ジャン・ジラールがNASCARに進出。ジャンとのレース中に起こした大事故により、リッキーの人生はどん底へと落ちていく。", "2006-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鍵（THE KEY）", "鍵（THE KEY）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "肉体の衰えとボケに怯え、その強迫観念から逃れるために、色香あふれる肉体の持ち主である妻との夜の営みに没頭しようとする、定年過ぎの元大学教授。病身になってもなお妻を求める異常ともいえる教授の性への執着ぶりを、より谷崎文学に近い形で映像化。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("安陽嬰児", "安陽の赤ちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "巨匠チェン・カイコーの助監督として「始皇帝暗殺」などに携わった実力派、ワン・チャオ監督による人間ドラマ。現代の中国・河南省の小さな町を舞台に、捨て子の赤ちゃんを巡る、立場も境遇も違う3人の男女の姿を描く。いまなお貧富の差が激しい中国の現状を反映した、痛烈な一篇だ。中国・河南省の小さな町、安陽を訪れた売春婦は、自分の赤ちゃんを捨ててしまう。その後、リストラされた男が赤ちゃんを拾うが……。", "2001-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高校生番長・深夜放送", "高校生番長・深夜放送", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "中学生の時、暴行された暗い過去を持つ女子高校生の名を何者かが騙り、ラジオの深夜放送に売春の告白をした。女子高校生は、犯人を見つけるため不良グループに近づく。旧大映末期の青春ものだが、ピンク映画に近い仕上がり。DJのアシスタント役で松坂慶子が出演。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOT THE I", "dot the i〈ドット・ジ・アイ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "恋人との結婚を決意した女性が、魅力的な青年と運命の恋に落ちていく……。愛憎入り乱れる三角関係が、やがて思いがけない物語へと発展。二転三転する大胆なストーリー展開で、観る者を魅了するラブ・サスペンス。ヒロインを惑わせる青年を「モーターサイクル・ダイアリーズ」のG・G・ベルナルがセクシーに好演。優しい恋人バーナビーとの結婚を控えたカルメンは、独身最後のパーティーで偶然居合わせたセクシーな男性キットとキスを交わす。その情熱的なキスで二人はたちまち恋に落ちてしまう。カルメンは心揺れ動きながらも、バーナビーを選ぶのだが……。", "2003-01-01", "3,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Living Proof", "希望のちから ", "", "", "http://image.eiga.k-img.com/images/movie/54829/315x210.jpg?4393", 91, "デニーは乳がんの治療にすべてを捧げる医師。彼は、乳がん患者とその家族のためにいろいろなものを犠牲にしていた。ある時は家族との団欒、ある時は治療薬開発の資金を得るために奔走し、多くの時間を乳がん治療の研究に時間を費やしていた。それはすべて患者の笑顔のためであった。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Guardian", "守護神", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "アメリカ沿岸警備隊のエリート養成学校を舞台に、それぞれに心に傷を負った教官と訓練生の絆と成長を描いたアクションドラマ。ベン（コスナー）はこれまでに200名以上もの遭難者の人命を救ってきた伝説的なレスキュースイマーだったが、事故で相棒を亡くしてからは第一線を退き、後進の育成にあたっていた。そこに高校水泳の王者で不遜な態度のジェイク（カッチャー）がやってくる。監督は「沈黙の戦艦」「逃亡者」のアンドリュー・デイビス", "2007-02-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AMOUR L’APRE｀S−MIDI", "愛の昼下がり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ヌーヴェル・ヴァーグの先駆者ロメールの、６作品からなる“教訓話”シリーズの最終話。仕事も家庭も安定し、人生に満足しているかのようにみえる主人公フレデリックが、奔放な女クロエと出会い惑わされ、やがては家庭に戻るまでの心の動きが丹念に綴られている。法律相談所を友人と経営する若手実業家のフレデリックは、美しい妻とかわいい子供に恵まれ、仕事も順調、何一つ不自由ない生活を送っていた。だが、彼は結婚してからというもの、すべての女性が美しく見えてしかたなかった。そんなある日、事務所にクロエという女性がやって来て……。DVDは「エリック・ロメール コレクション(2)」に収録。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN NINJA５", "マーシャルコマンダー 黒の攻襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "マーシャルアーツの達人ジョーは、誘拐された恋人を救うため、12才の少年ヒロと追跡を始めた。だがこの誘拐の裏には世界制覇をたくらむ、巨大な黒幕が隠れていた。ジョーとヒロは忍術の修行を開始。固い友情を深めながら敵に向かっていく。主人公ジョーの義父でニンジャの頭の役にN・P・モリタが出演。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五重塔", "五重塔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "幸田露伴の名作小説を、元プロボクサーのガッツ石松＆元「モーニング娘。」の飯田圭織共演で映画化。陶芸家・片岡十兵衛は、理想の陶芸の地を求めてたどり着いた青森県五所川原市で、東京の大学を卒業した娘・有衣と2人で暮らしはじめる。ある日、熊谷常光院の住職・老円上人から、五重塔の陶器を制作するという計画を聞いた十兵衛は、その仕事を引きうけるが……。監督は「富嶽百景　遥かなる場所」の秋原正俊。", "2007-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREAT RACE", "グレートレース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "20世紀初頭。新聞社の主催するニューヨーク・パリ間の大オートレースに参加したレスリーとフェイト、さらに取材をかって出た女記者のマギーを交えての競争が始まる。抜け目のないフェイトはレスリー以外の参加車に細工をしてスッ飛ばす。マギーはレスリーの車に同乗し取材を続ける。フェイトは中欧の小国の皇太子にそっくりなことから、王位争奪戦に巻き込まれるがマギーらの助力で事件は解決、再びパリへの疾走が始まる……。サイレント時代の手法を大画面に駆使した豪華喜劇。J・レモンがフェイトと皇太子の二役をこなし、マンシーニの音楽が最高。エドワーズ監督の代表作。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KENTUCKY FRIED MOVIE", "ケンタッキー・フライド・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「フライング・ハイ」「殺したい女」などを手掛けたJ・エイブラハムズ＆ザッカー兄弟が、鬼才ランディスと組んだ超フマジメ映画。名作「空中レビュー時代」の曲『カリオカ』を音痴に歌うオープニングに始まり、テレビ番組、CM、映画など、22のエピソードで構成されたパロディーの数々が綴られていく。例えば、オイル・ショックに立ち向かうため、若者のニキビやフライドチキンの空き箱から“油”を取り、精製するくだり、「燃えよドラゴン」のパロディーでズロース・リー主演の「燃えよ鉄拳（原題＝日本円の怒り)」など、当時の社会を背景にしながら、時代を超越したギャグが抱腹絶倒の笑いを提供してくれる。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋すれど恋すれど物語", "恋すれど恋すれど物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "同年の春、東京宝塚劇場で上演された菊田一夫のミュージカルの映画化作品。南蛮渡来の爆薬を唐津から江戸へ運ぶ任務を受けた有島一郎と三木のり平のコンビが繰り広げるドタバタ珍道中喜劇だ。出演は他に大河内伝次郎、雪村いづみ、榎本健一などの豪華キャストで、喜劇映画はお得意の斎藤寅次郎が監督する。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大学の暴れん坊", "大学の暴れん坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "赤木圭一郎の「素っ裸の年令」に続く２本目の主演作。赤木扮する大学の柔道部の選手が、ケンカをして先輩から柔道を禁じられ、キャバレーの用心棒として働くうちに、経営者の悪事の内幕を知り、得意の柔道で悪玉を退治する。赤木はこの作品の翌年、ダイヤモンド・ラインに参加し主演作を連発する。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("点と線", "点と線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "あまりにも有名な松本清張の同名の原作を、小林恒夫が演出した推理映画。北海道にいたはずの男が、福岡で殺人を犯すことができるだろうか。日本の北と南を結ぶ点と線を推理し、ついにそのアリバイ作りのトリックを発見するまでを描く。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青島要塞爆撃命令", "青島要塞爆撃命令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1914年から始まった第一次世界大戦の、中国を舞台にした日・独戦を描いた戦争アクション娯楽作。第二次世界大戦ものが多い戦争映画だが、本作は悲劇性よりも活劇の方に重点を置いた作りだ。中国にあるドイツの拠点地、青島要塞への日本軍による攻撃が開始された。だが、ドイツのビスマルク砲台を破ることができなかった。やがて空からの攻撃命令を受け、当時2機しかなかった戦闘機が大空へ飛ぶ。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本一のゴマすり男", "日本一のゴマすり男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ゴマをすれなかったばかりに万年課長で会社を定年退職した男は、息子にゴマをすらなければ出世はできないと教える。はじめは実力が第一と思っていた息子・中等も、やがて父の教えに気づき、上役にゴマスリ攻勢を始める……。植木等の調子のよさがよく出た作品。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SONG OF NORWAY", "ソング・オブ・ノルウェー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "“北欧のショパン”と呼ばれた、ノルウェーの代表的作曲家グリークの半生を、美しい北欧の自然をバックに描いたミュージカル。全編に流れる名曲『ピアノ協奏曲イ短調』。演奏はロンドン・シンフォニー交響楽団で、グリークの人間ドラマとしても見ごたえあり。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOKYO！", "TOKYO！", "", "", "http://image.eiga.k-img.com/images/movie/53419/315x210.jpg?2370", 110, "3人の鬼才監督が東京を舞台にした物語を競作。生きることに思い悩む女性の体が徐々に木になっていく「TOKYO！／インテリア・デザイン」（ミシェル・ゴンドリー監督／藤谷文子、加瀬亮主演）、下水道から現れる謎の怪人が街を恐怖に陥れる「TOKYO！／メルド」（レオス・カラックス監督／ドゥニ・ラバン主演）、10年間引きこもっていた男が、恋する女性を追って外界に足を踏み出す「TOKYO！／シェイキング東京」（ポン・ジュノ監督／香川照之、蒼井優主演）の3作で構成。", "2008-08-16", "2,76,5,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZUSJE", "リトル・シスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "低予算作品ながら世界中の映画祭で高い評価を得たオランダのインディーズ作品。恋人とアツアツの一人暮らしの女子学生と、ビデオカメラで彼女の姿をひたすら追いかける、その兄の葛藤をユーモラスに描く。終始、兄のカメラを通した一人称の視点で展開する、ドキュメント風の映像がユニーク。登場人物の感情をリアルに伝えている。", "1995-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("玉割り人ゆき・西の廓夕月楼", "玉割り人ゆき・西の廓夕月楼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "潤ますみ主演の“玉割り人ゆき”ものの第２作。北陸の廓を舞台にして、玉割り人・ゆきの娼妓たちを相手にした性訓練と、青年楼主との恋が描かれる。低予算映画ながら新鋭・牧口雄二監督の熱気みなぎる意欲作である。潤ますみが前作に引き続き好演。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜談・南海變化玉", "喜談・南海變化玉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "熱気球の実現に情熱を注ぐ老教授に協力する若者たちパノラマ団は、南洋の空への夢をふくらませるが、行く手には様々な妨害が……。まだ“カルトムービー”という言葉が一般に使われない頃に登場した、元祖長嶺高文のキッチュでスラップスティックな16ミリによるデビュー作。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若い狼", "若い狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "更生を誓って少年院を出た信夫だが、郷里の炭鉱は廃坑となり、出かせぎに行った父の消息も知れず。母は日雇いをしながら幼い弟妹を抱えて貧しく暮らしていた。信夫は恋人の道子を頼って上京するが職はなく、少年院時代の仲間に誘われヤクザ組織に入ってしまう。少年たちの出口なき状況を、ドキュメンタリー・タッチで描いた恩地日出夫監督の処女作。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雨のめぐり逢い", "雨のめぐり逢い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "盲目の妹の手術代を作るために銀行強盗の片棒をかついだ男と、その強盗の騒ぎに巻き込まれ、やはり失明してしまった女との紆余曲折を描いたサスペンス・ドラマ。市井の暮らしぶりをあざやかに演出した野村監督の手腕と、山城新伍の好演が光る作品。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALAN＆NAOMI", "ルーカス・ハースの14歳のメモリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「刑事ジョン・ブック／目撃者」での澄んだ瞳が印象的なL・ハースが、傷ついた少女との交流を通して大人になっていく過程を描く。第二次世界大戦中のニューヨークで、少年アランはフランスから逃れてきたユダヤ人の少女ナオミを紹介される。レジスタンスだった父親を目の前で殺されたナオミは、心に深い傷を受け、口をきくことさえしない。根気よくナオミの家へ通うアランに、次第に心を開いていくナオミ。学校へ通えるまでになった彼女だったが、ある時、反ユダヤ主義の級友が二人にケンカを仕掛けてきた。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRYSTAL VOYAGER", "クリスタル・ボイジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "サーフィン映画の名作「ビッグ・ウェンズデー」で知られる、水中撮影の天才にしてサーフィンの申し子、G・グリノーによる伝説的サーフィン・ドキュメンタリー。日本では2004年に劇場初公開となった。たった一晩で大型の水中撮影器具を自作したり、より奔放に波と戯れたいがために極端に短いボード“ニーボード”を開発したり……。何よりも自由を求めたグリノーが、残した伝説は数知れず。", "2004-12-04", "11,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博徒斬り込み隊", "博徒斬り込み隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "新宿淡野組幹部・相羽雄作が７年振りに出所した時、組はすでになく東日本最大の暴力団組織・盟友会傘下の陣野組が仕切っていた。相羽は死んだ子分の遺骨を持って故郷・飯坂を訪ねるが、ここでも盟友会の魔手が迫っていた……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUMMER SCHOOL", "サマー・スクール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ハワイで夏休みをと、ウキウキのハイスクールの体育コーチが、なんとサマースクールの教師に。かくして落ちこぼれ教師VS落ちこぼれ生徒のハチャメチャ騒動が展開されることに……。二枚目ハーモンのズッコケぶりが見もの。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("垂簾聴政／REIGN BEHIND A CURTAIN", "西太后", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "中国の長い歴史のなかでも、最も民衆に恐れられたという残忍な女性・西太后の半生を、中国映画界がその総力を結集して製作した、スケールの大きな伝記ドラマ。激動の時代を生きた一人の女性の姿を、史実を交えながら浮き彫りにする。", "1984-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニッポン国 古屋敷村", "ニッポン国 古屋敷村", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 210, "“三里塚”ものなど、ユニークなドキュメンタリーを撮り続ける小川プロの作品。小川プロがユニークなのは、撮影対象となる地に何年も根をおろし、農民とともに暮らし生活する中から、映画を撮っていくという点にある。1980年の夏、東北地方を冷害が襲った。山形県上山市で共同生活をしながら、稲作のドキュメンタリーを撮影中だった小川プロの一行は、古屋敷村に赴き、冷害の原因を究明しようとする。一行はミニチュアを作ったり、観察記録を作ったりして、冷害と稲の関係を科学的に検証していく。と同時にカメラは古屋敷村に生きる農民の姿を、いきいきと活写する。字幕スーパー付きの、おわり婆さんが語り出す、不思議な体験談は抱腹絶倒のおかしさだ。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時をかける少女（2010）", "時をかける少女（2010）", "", "", "http://image.eiga.k-img.com/images/movie/55049/315x210.jpg?4688", 122, "筒井康隆の原作小説4度目の映画化。主演は2006年の細田守監督によるアニメ版でも主人公の声優を務めた仲里依紗。本作が長編デビューの谷口正晃がメガホンをとる。2010年、18歳の女子高生・芳山あかりは、事故で昏睡状態に陥った母・和子のため、深町一夫という母の初恋の人を探しに70年代へタイムリープ。そこで出会った大学生・涼太に助けられながら深町を探すが……。共演に中尾明慶、安田成美ら。", "2010-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ringer", "リンガー！替え玉★選手権", "", "", "http://image.eiga.k-img.com/images/movie/33939/315x210.jpg?1295", 95, "知的発達障害者になりすましてスペシャル・オリンピックスに不正出場しようと企む青年の姿をブラックユーモアたっぷりに描いたコメディ。「メリーに首ったけ」「ふたりにクギづけ」など、監督として数々のコメディ映画をヒットさせてきたファレリー兄弟プロデュースの下、「ビヨンド・ザ・マット」のバリー・W・ブラウスタイン監督がメガホンを取る。主演は「jackass」シリーズの超過激パフォーマンスでおなじみのジョニー・ノックスビル。", "2007-04-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談累が淵", "怪談累が淵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "中川信夫の本格的怪談映画（毎年お盆の時期に封切られた）の第１作。死んだ父親同士が遺恨の間柄とは知らない、羽生屋の番頭・新吉と三味線の師匠・豊志賀は、情を交わす。やがて、豊志賀の顔が傷ついて新吉の心は羽生屋の娘・お久に移り、豊志賀は嫉妬にかられていく……。本作以後、中川信夫は「亡霊怪猫屋敷」「憲兵と幽霊」「女吸血鬼」「東海道四谷怪談」「地獄」と、経営不振の新東宝の末期を飾る傑作、佳作、話題作を連発することになる。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エバラ家の人々", "エバラ家の人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "“エバラ焼肉のたれ”のCFを映画化したファミリー・コメディ。主役は当時CFに出演していた女優・浅茅陽子。平凡なサラリーマン家族のエバラ家は狭い団地暮らし。しかしある日、夫の実家が裏山を売り、そのお金で郊外の一戸建てが手に入った。夢が叶ったエバラ家だが、やがてローン地獄で家庭は崩壊寸前になり……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("駆逐艦雪風", "駆逐艦雪風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "前年、TV『三匹の侍』で人気を得、スターの仲間入りをした長門勇主演による戦争映画。佐世保で駆逐艦雪風を製作した工員・木田勇太郎は、この船を愛するあまり、乗組員に志願。戦況の悪化するなか、雪風は無傷のまま戦果をあげる。1945年、雪風は戦艦大和最後の航海に、駆逐艦として同行することになるのだった……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ВОЛЕБНАЯ ЛАМПА АЛАДДИНА／ALADIN　AND　THE　MAGIC　LAMP", "アラジンと魔法のランプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "アニメでもよく知られる昔話を、実写で撮り上げたロシア製ファンタジー。バグダッドに住む青年、アラジンは、スルタン王女ブドゥル姫を一目見た瞬間、恋に落ちた。そんな彼に悪賢い魔法使いが、彼の願いをすべてかなえると嘘をつき、魔法のランプを探させる。だがアラジンはランプを奪い、魔法のおかげで姫と結ばれる。", "1967-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うみ・そら・さんごのいいつたえ", "うみ・そら・さんごのいいつたえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "作家・椎名誠が、1990年の「ガクの冒険」に続いてメガホンを取った監督第２作。雄大なサンゴ礁に囲まれた沖縄の島を舞台に、東京の生活に疲れ逃げるように故郷の島へ舞い戻った母娘を中心に、島に住む個性的な人々の生活模様を映し出していく群像劇。「ガクの冒険」と同じく“上映＆トークショー”という形でホール上映を中心に全国縦断公開された。DVDは「ホネ・フィルム式活動寫眞全記録 椎名誠 DVD-BOX」に収録。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREEN BOY グリーンボーイ", "GREEN BOY グリーンボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "元ボクサーで今は八百屋を営む大介は、妻と別れて10歳の息子・弘志と二人暮らし。ある晩、自分の命じた用事で、弘志が出先で交通事故に遭い、下半身不随となる。リハビリの苦しみを励ますため自分も再び４回戦ボーイを目指し、苦しみをともにしようとする父だが……。森田健作総指揮の青春映画第２作。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("透光の樹", "透光の樹", "", "", "http://ecx.images-amazon.com/images/I/2160Y7VMNYL._SL210_.jpg", 121, "「絆〈きずな〉」以来６年ぶりにメガホンを執った名匠・根岸吉太郎が、高樹のぶ子の同名ベストセラー小説を映画化。体だけの関係から始まり、永遠の愛で結ばれていく男女の行方を、秋吉久美子と永島敏行の共演で描き出す。香り立つような官能的なシーンの中に、狂おしい愛の物語を浮き彫りにする。寝たきりの父と高校生の娘と暮らす千桐は、25年ぶりに知人の郷と再会する。千桐は借金返済を条件に、郷の愛人になることを承諾し……。", "2004-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Senna", "アイルトン・セナ　音速の彼方へ", "", "", "http://image.eiga.k-img.com/images/movie/55649/315x210.jpg?4267", 108, "天才的なドライビング・テクニックと甘いマスクで世界中の人々を魅了しながらも、1994年のサンマリノGPで衝撃的な事故死を遂げたブラジルのF1ドライバー、アイルトン・セナの素顔に迫るドキュメンタリー。その34年の生涯を、レース映像や貴重なプライベート映像を交えながら振り返るほか、本人へのインタビューやチーム関係者・家族らの証言を通し、華々しい経歴の影に潜んだ苦悩や葛藤（かっとう）を浮き彫りにしていく。", "2010-10-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY　GENERATION", "MY GENERATION WOODSTOCK 1969,1994,1999", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ロック・イベント、ウッドストックの歴史を検証したドキュメンタリー。1969年、愛と平和をテーマにした、このビッグ・コンサートは40万人を動員して伝説となった。しかし1994年の25年周年記念イベントはスポンサーの意向などで商業主義に冒されたものとなり、1999年の30周年イベントは、より混沌化する……。コンサート映画ながら音楽を切り離し、あくまでイベントの在り方に焦点が絞られている。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A｀ LA RECHERCHE DE CHRISTIAN B.", "ボルタンスキーを探して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "フランスが誇る現代美術作家クリスチャン・ボルタンスキーの世界に迫ったドキュメンタリー。ユダヤ人であるC・ボルタンスキーの様々な作品や、テキストを読み上げたり、インスタレーションの準備をしたりする彼自身の姿など、様々なイメージの断片が綴られることによってアーティストの姿が浮かび上がってくる。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とっかえっ娘。", "とっかえっ娘。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "深夜TV番組発信の企画“つんくタウンFILMS”が製作した“モーニング娘。”の石川梨華、吉澤ひとみら総勢９人が出演したコメディ。きまぐれ魔法使いアイボンの魔法で、お金持ちの少女と貧乏な少女が生活を交換し、巻き起こすドタバタ珍騒動をファンタスティックに綴った物語だ。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きょーれつ！もーれつ!!古代少女ドグちゃんまつり！　スペシャル・ムービー・エディション", "きょーれつ！もーれつ!!古代少女ドグちゃんまつり！　スペシャル・ムービー・エディション", "", "", "http://image.eiga.k-img.com/images/movie/55259/315x210.jpg?3417", 0, "土偶の神様・ドグちゃんが妖怪と戦う姿を描くTVコメディ「古代少女ドグちゃん」を、「片腕マシンガール」の鬼才・井口昇が映画化。主演は谷澤恵里香。1万年の眠りから覚めたドグちゃんは、土偶の相棒ドキゴローとともに、現代に蘇った妖怪たちを次々と退治しながら、引きこもり少年・誠の心も開いていく。TVドラマを再編集し、幻のパイロット版が劇中劇として追加された構成となる。", "2010-02-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("独身アパート どくだみ荘", "独身アパート どくだみ荘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "金なし、職なし、女なし。東京阿佐ヶ谷の独身アパート“どくだみ荘”の鼻つまみ住人、堀ヨシオを主人公にした青春珍騒動記。ないないづくしで世間にもだまされっぱなしのヨシオだが、優しく自分を介抱してくれた看護婦のユミに一目惚れ。必死のアプローチ作戦でユミをものにしようとするが……。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハード・リベンジ、ミリー", "ハード・リベンジ、ミリー", "", "", "http://image.eiga.k-img.com/images/movie/53799/315x210.jpg?3328", 0, "押井守監修のオムニバス映画「真・女立喰師列伝」の一編「荒野の弐丁拳銃」でハードなアクションを披露した水野美紀と辻本貴則監督が、再びタッグを組んで送り出すバイオレンスアクション。20XX年、銃刀法撤廃により治安が悪化し、大都市砂漠化で無法都市となったヨコハマ。ミリーは悪党「ジャックブラザーズ」に赤ちゃんを惨殺される。復讐を誓うミリーは自らの肉体に刀と銃を仕込み、敵のアジトへと向かう。", "2008-08-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASAS DE CARTON", "イノセント・ボイス／12歳の戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "激しい内戦下の南米・エルサルバドルで少年時代を過ごした脚本家オスカー・トレスの実体験に基づく物語を、メキシコ出身のルイス・マンドーキ監督が感銘を受けて映画化。12歳で政府軍に徴収される少年の目を通して、死と隣り合わせの日常や、戦争の犠牲となる一般市民の悲しみと怒りを描き出す。内戦下の国で母親や姉弟たちと共に明るく暮らす11歳のチャバ。しかし、次第に戦火は激しさを増し、徴兵の日も近づいていた。", "2006-01-21", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pink Floyd The Wall", "ピンク・フロイド　ザ・ウォール", "", "", "http://image.eiga.k-img.com/images/movie/25839/315x210.jpg?3122", 95, "ピンク・フロイドの大ヒットアルバム「ザ・ウォール」の世界を、「ミッドナイト・エクスプレス」のアラン・パーカー監督が映像化した作品。脚本を担当するのは同バンドの中心メンバーであるロジャー・ウォーターズ。孤独と絶望にうちひしがれ、心の中につくりあげた壁にすがりながら狂気に溺れていくロックスターの姿を、幻想的で強烈な映像と音楽で描く。主演は本作で映画デビューを果たした、ミュージシャンのボブ・ゲルドフ。", "1982-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("食器を洗う男", "食器を洗う男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 46, "社会に出て数年がたつサラリーマンの井上さとしは、仕事にもなれ、恋人がいないという以外は順調な日々を送っていた。そんなある日曜日、いつものようにたまった洗濯物や食器を洗い、近所のスーパーへと買物に出かけた彼は、そこで偶然先輩の彼女・松本と出会い、多少の下心も手伝って彼女を夕食に誘う。そして食事も済み、彼女が帰ろうとした時……。TV放映を前提に作られた16ミリ作品で、北海道では上映前に放映された。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダイナマイトどんどん", "ダイナマイトどんどん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "昭和25年、北九州一円で昔堅気の岡源組と新興ヤクザの橋伝組の間での抗争がエスカレートし、民主的に抗争を解決しようという警察署長の提案で、野球大会が開かれる。愛する女をめぐる争いも絡んで、一筋縄ではいかない、まさに懲りない面々の戦い模様が笑わせる。背番号が花札のカードになっていたり、小指がないために誰も打つことのできない魔球できりきり舞いさせるなど……。次第にスパイクを尖らせ、バットに鉛を埋めた一人一殺の殺人野球に発展して、観る者はあっけにとられ、彼らのあくなき闘争心に脱帽させられるに違いない。岡本監督の変化球がコーナーいっぱいにキマった作品。DVDは「岡本喜八・邂逅編／STRANGERS　DVD-BOX」に収録。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("独身女人", "独身女性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "外資系ファッション業界で活躍するルオユンは、バツイチで子連れのキャリアウーマン。開放経済政策の潮流の中、新しい恋人のサポートで頭角を現した彼女は、次第に成功を収めていく。が、新作発表会を控え、恋も仕事も順調に思えた彼女の前に、前夫や恋のライバルが出現し……。様々な困難を乗り越え、自立した中国人女性の生き方を描く。", "1991-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LONE　RANGER", "西部の王者／ローン・レンヂャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "往年の人気TVドラマから生まれた西部劇。愛馬シルバーを駆って悪に挑む熱血漢ローン・レンジャーの活躍劇が展開する。ローン・レンジャーと相棒トントが、辺境の町にやって来た。そこにはインディアンの不穏の企みを知事に吹き込み、私腹を肥やそうとする実力者の陰謀が……。実力者と配下の荒くれに向けて、ローン・レンジャーの怒りの拳銃が火を吹く。DVDタイトルは「「ローン・レンジャー 劇場版 DVD−BOX」。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PACIFIC PALISADES", "ソフィー・マルソー　パシフィック通り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "S・マルソーが、フランスからアメリカに渡った女性のとまどいを、繊細な演技の中に見せた作品。パリの食堂“パシフィック・パリセーズ”のウエイトレス、ベルナデッドは退屈を持て余し、アメリカ青年グレッグの口車に乗って、アメリカに渡る。しかし、グレッグの話は、実は嘘で……。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四姉妹物語", "四姉妹物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "“グリコ・ポッキー”のCMキャラクター、すなみ、ちなみ、こなみ、えなみを主人公に据えた、赤川次郎原案による軽快なミステリー・コメディ。舞台は、長女すなみの親友・明日香の結婚披露パーティー。会場にたまたま四姉妹が顔をそろえるが、花嫁が殺されてしまい……。CMでおなじみの四姉妹に、父親、あわて者の刑事、陰のある青年、怪しげな大富豪、謎のピアニストらが絡んで、物語は展開。「バカヤロー２ 幸せになりたい」に続く映画３本目の本田昌広は、このCMを手掛けた売れっ子ディレクターでもある。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beautiful Islands ビューティフルアイランズ", "Beautiful Islands ビューティフルアイランズ", "", "", "http://image.eiga.k-img.com/images/movie/55199/315x210.jpg?2666", 110, "気候変動により沈没の恐れがある3島、南太平洋のツバル、イタリアのベネチア島、アラスカのシシマレフ島に暮らす人々の生活を記録したドキュメンタリー。「空気人形」の是枝裕和監督がエグゼクティブプロデューサーを務める。生まれ故郷を愛し、代々受け継がれてきた伝統文化や食文化を守って生きる島民の姿を通し、失われていく島の危機を訴える。", "2010-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOWLINGIII THE MARSUPIALS", "ハウリングIII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "人間から狼人間へと変身していくショッキングなシーンで話題を集めた「ハウリング」。シリーズ第３作で、監督は２作目同様P・モーラが担当。オーストラリアの田舎町に、あの狼人間の不気味な遠吠えが再び響きわたる。", "1987-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小屋丸　冬と春", "小屋丸　冬と春", "", "", "http://image.eiga.k-img.com/images/movie/55559/315x210.jpg?2318", 88, "フランスを代表する現代美術作家ジャン＝ミッシェル・アルベローラが、新潟県十日町市の山奥に位置する集落・小屋丸をテーマに撮りあげたドキュメンタリー。2003年の「大地の芸術祭　越後妻有アートトリエンナーレ」で小屋丸を訪れたアルベローラは、同地に理想の共同体を見出し、07年から2年間にわたり取材を敢行。里山の自然に包まれた人々の暮らしを、詩情豊かなモノクロ映像でつむぎ出していく。", "2010-10-02", "2,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Boy Meets Girl", "ボーイ・ミーツ・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ジャン＝リュック・ゴダールの再来といわれたレオス・カラックス監督の長編デビュー作。親友のトマに恋人を奪われたアレックスは、彼を殺す決意を胸に夜のパリに出て行くが、同じく失恋した少女、ミレーユと出会い恋に落ちる。のちに「汚れた血」「ポンヌフの恋人」へと続いていく、ドニ・ラバン演じるアレックスを主人公にした3部作の第1作。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聴かれた女", "聴かれた女", "", "", "http://ecx.images-amazon.com/images/I/21TeT7Kog3L._SL210_.jpg", 84, "「ジャンクフード」「リムジンドライブ」などの鬼才・山本政志監督が、セクシー女優・蒼井そら主演で放つポップなエロティック・サスペンス。新しいアパートに引越したばかりの青年リョウは、薄い壁越しに聞こえてくる隣室の女の生活音が気になりはじめる。リョウは次第に妄想を膨らませていき、見たこともない彼女に恋心まで抱くようになってしまう。一方、隣室の女・皐月は、毎日のようにかかってくる脅迫電話に悩まされていた。", "2007-02-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LOST　CONTINENT", "魔獣大陸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ホラーの名門ハマー・プロが製作したSFアドベンチャー。危険物を不法輸送する貨物船カリタ号には、スネに傷を持つ一筋縄ではいかない連中が乗り込んでいた。しかしカリタ号は暴風雨で、未知の海域に足止めをされる。人喰いザメや巨大クラゲの襲撃に遭い、何とか生き延びた人々が海底で見たもの。それは海に沈んだ伝説の大陸だった……。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アイリス THE LAST", "アイリス THE LAST", "", "", "http://image.eiga.k-img.com/images/movie/55869/315x210.jpg?2524", 0, "イ・ビョンホン主演の大ヒット韓国ドラマ「アイリス」を映画化したスパイアクション。韓国国家安全局の特殊要員ヒョンジュンは、ハンガリーでの任務中に深い傷を負ってしまう。ヒョンジュンは助けを求めた副局長のペク・サンの裏切りによって、親友チン・サウに命を狙われるはめになる。そのときから、ヒョンジュンは巨大な陰謀の渦に巻き込まれていた。", "2011-01-08", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠足・Der Ausflug", "遠足・Der Ausflug", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "テレビのドキュメンタリー演出家、五十嵐久美子による初映画監督作。ウィーン郊外の精神病院内に建っている芸術の家で暮らす10人の天才画家たちは、家から出ることを遠足と呼んでいた。天才芸術家たちが織りなす孤独な遠足を通して、一度傷ついて病んでしまった彼らの静かな交流を見つめる。公開時に世田谷美術館で開催された『グギングの芸術家たち』展も話題を呼んだ。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jal Aljido Motamyunseo", "よく知りもしないくせに", "", "", "http://image.eiga.k-img.com/images/movie/54969/315x210.jpg?4594", 126, "「女は男の未来だ」「アバンチュールはパリで」のホン・サンスが、とあるアート系映画監督の日常を綴った長編第9作。映画監督のギョンナムは、小さな町で開催される映画祭に審査員として招待される。そこで偶然にも旧友のサンヨンと再会を果たした彼は、サンヨン夫婦の家で夜通し飲み明かすが、ささいなことからサンヨンを怒らせてしまい逃げるように町を離れる。", "2009-10-23", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOKPO　IS　HARBOR", "木浦(モッポ)は港だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "韓国での大ヒットに続き『ゆうばり国際ファンタスティック映画祭』ヤング・シネマ・グランプリを受賞した、コミカルな潜入捜査アクション。韓国の正統派美男俳優チャ・インピョと、実力派チョ・ジェヒョンの共演が実現した。ソウルの刑事が麻薬犯罪の捜査のため、ヤクザの街ともいわれる港町・木浦を仕切る組織にヤクザになりすまし潜入したが、刑事はいつの間にか組長の人間性に惹かれていく……。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にっぽん美女物語", "にっぽん美女物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "神楽坂の老舗料理屋を舞台にした人情もの。４人姉妹のうち３人までは美しくおしとやかに育ったものの、一人男っぽくてガラッパチになった末娘・ひらめをメイン・ヒロインに物語は展開する。研ナオコが涙ぐましい活躍を披露。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Magic & Loss", "マジック＆ロス", "", "", "http://image.eiga.k-img.com/images/movie/57069/315x210.jpg?2286", 81, "女優兼プロデューサーとしてインディーズ界で活動する杉野希妃が、「息もできない」の主演コンビ、ヤン・イクチュンとキム・コッピを迎えて製作した幻想的なドラマ。香港のリゾート地・ムイウォの森にやってきた日本人女性のキキと韓国人女性のコッピは、森の傍らのホテルに宿泊する。そこには1人のベルボーイ以外は誰もおらず、世間から孤立したホテルで2人は徐々に時間の感覚を失っていく。やがて現実と幻想が入り混じっていく中で、2人は互いを求め合うようになるのだが……。メガホンをとったのは、本作が長編2作目のマレーシア人監督リム・カーワイ。", "2011-11-05", "76,5,50,6,8,2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Versailles", "ベルサイユの子", "", "", "http://image.eiga.k-img.com/images/movie/54349/315x210.jpg?3699", 113, "パリ郊外の世界遺産ベルサイユ宮殿のはずれの森で暮らす男ダミアンは、母親に置き去りにされた5歳の男の子エンゾを見つける。ダミアンはエンゾの世話をする羽目になるが、寒さと飢えをしのぐ生活を共にするうち、2人の間には親子以上の絆が芽生え始める。主演は2008年に急性肺炎で逝去した「ポーラX」「ランジェ公爵夫人」のギョーム・ドパルデュー。", "2009-05-02", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小花／XIAO　HUA", "戦場の花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「中華女児」(1949)の主演女優、チャン・チョンの監督作品。逃亡生活を送る革命家夫妻の娘として生まれた紅果は趙永生という男のもとに預けられた。彼女は永生の妹、小花として育てられる。年月がたち小花は解放軍の女医、周と会う。彼女こそは実の母で……。小花役は「ラストエンペラー」のジョアン・チェンことチェン・チョン。「西太后」の名優、リュウ・シャオチンの初々しい姿が見もの。", "1979-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽のきずあと", "太陽のきずあと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "若者のぶつけるところのないいらだちと暴力を描いた作品で、原点は中平康監督の「狂った果実」である。母とともに函館で暮らしていた弟の耕治が上京し、修平は10年ぶりに弟と再会する。この兄弟と、修平の同級生のかおり、仲間の唐沢を含めた４人で千葉の別荘へ遊びに行く。しかし、かおりが唐沢の方へなびいたため、耕治と修平は嫉妬に襲われる……。「夜をぶっとばせ」や「赤い暴行」など、1970～80年代にかけての怒れる若者を撮り続けた曾根中生が、東映で初めて監督した青春映画の佳作。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("米", "米", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "名匠・今井正監督が手掛けた初めてのカラー作品。霞ヶ浦の農村を舞台に、地方の因襲や生活状況を描く。農家の母を演じる望月優子の体当たりの演技が評判をとり“日本のお母さん”女優と呼ばれるようになった。中尾カメラマンが捉えた田園風景が美しい。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY FOOLISH HEART", "愚かなり我が心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "第二次大戦中、兵役にとられた婚約者の子を宿していた女が、婚約者の死で途方にくれたあげく、親友の恋人を奪って結婚。しかし生活はうまくいかず彼女は酒びたり。見かねた親友が真相を明かし、子供を連れての離婚となる。映画そのものよりも主題曲の大ヒットで有名になった作品。薄幸の女をS・ヘイワードが熱演している。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Il y a longtemps que je t'aime", "ずっとあなたを愛してる", "", "", "http://image.eiga.k-img.com/images/movie/54899/315x210.jpg?4590", 117, "6歳の息子を殺害した罪で15年間服役していたジュリエットは、出所して妹レアの家に身を寄せることになる。ジュリエットは福祉事務所に就職先を紹介してもらうが、重罪を犯したジュリエットの受け入れ先は簡単には見つからなかった。息子殺害の理由を一切語らないジュリエットだが、レアはジュリエットの部屋で事件の真相に迫る秘密を発見してしまう。主演は「イングリッシュ・ペイシェント」のクリスティン・スコット・トーマス。フランスの小説家フィリップ・クローデルの長編監督デビュー作。", "2009-12-26", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Narco", "ナルコ", "", "", "http://image.eiga.k-img.com/images/movie/34259/315x210.jpg?1627", 105, "「戦場のアリア」のギョーム・カネが、発作的に睡眠状態に陥る病気“ナルコレプシー”の青年に扮するフランス発のコメディ・ドラマ。何時でも何処でも突然眠ってしまう青年ギュスは、そのせいで仕事も妻との関係も上手くいかず、冴えない毎日を送っていた。そんなある日、彼は自分が見た奇想天外な夢をコミックに描くことを思いつく。ところが、ギュスの絵の才能を妬んだ精神科医ププキンが彼に殺し屋を差し向け……。", "2007-10-20", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NA¨RA LIVET", "女はそれを待っている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "臨月の妊婦セシーリアが病院に担架で運び込まれる。ベッドで目覚めたセシーリアはおなかの子を流産してしまったことを知る。夫との不和で離婚も考えていた彼女は、病院に出入りする家族や看護婦らとのふれ合いの中で生命と人生の尊さに目覚めていく……。産院オールロケによるドキュメンタリー的作品。", "1958-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大いなる完", "大いなる完", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "昭和初期、貧しい小作人の息子として生まれた完は地主の娘・高子に惚れ抜き、ついに犯してしまう。だが、完は彼女に“俺の嫁はお前しかおらん”と言い放ち、生まれついての金持ちには負けたくないと彼女の兄・新太郎にライバル宣言する。やがて第二次大戦が勃発、完はその中で地主にまで成り上がるが、それを小作人に分け与えてしまう。そんな彼の言動に惚れた博徒・滝川らに支えられ、完は新太郎を追って政治の世界に飛び込んでいく……。本宮ひろ志のコミックを鬼才・高橋伴明が映画化。激動の昭和史の中で、強い意志と情熱を持って昇り詰めていく主人公・完の半生を熱く描く。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEVADA SMITH", "ネバダ・スミス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "実在の西部の男、ネバダ・スミスの若き姿をマックィーンがクールに演じきった傑作。16歳の時、３人組の強盗に両親を殺された彼は復讐に燃え、旅に出る。その執念はすさまじく、自ら強盗を働き、刑務所の中までも仇を追うほどだ。復讐のために心がすさんだ男のすごさと影が見事に描かれている。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　WINTER　GUEST", "ウインター・ゲスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「ダイ・ハード」の悪役でも知られる英国の演技派俳優A・リックマンが、自身が演出した舞台劇を映画化、監督デビューを果たした人間ドラマ。冬のスコットランド。写真家のフランシスは夫を亡くして以来、喪失感をぬぐいきれずにいた。母エルスペスはそんな彼女が心配でならない。何かと世話を焼く母にフランシスはイラ立ち、二人は衝突を繰り返す。しかし、母はフランシスが一人でオーストラリアへ移住することを恐れ、フランシスも母の気持ちを察している。一方、二人の様子を見守るしかないフランシスの息子アレックスは初恋を体験し、大人の世界へ踏み出そうとしていた……。E・トンプソンと実母F・ローが、葛藤の中で絆を回復していく母娘を好演。凍った海が広がるスコットランドの、美しくも哀しい景色も深い余韻を残す。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I LOVE MY WIFE", "くたばれ！ダーリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "妻子がありながらも、美人と見ればちょっかいを出さなくてはいられない男バロウズ。今までとはケタ違いの美人の人妻に参ってしまった彼は、アノ手コノ手のアタックを開始するが、妻にバレ、そして愛する人妻にも愛想を尽かされて……。E・グールドが、このどうしようもない女好き男をホロ苦く演じている。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽雨／SUNSHINE AND SHOWERS", "太陽雨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "デビュー作「絶響」のリリカルなタッチが評価されたチャン・ツォーミンの第２作。開放都市深川を舞台に、図書館司書とそのボーイフレンドで広告会社を営む青年、そのかつての恋人の女、そしてファッション・モデル志望の17歳の少女の、それぞれの心理を繊細に描き上げていく。原作・脚本は1961年生まれの女性作家、ソウ・シーホン。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OTHER PEOPLE’S MONEY", "アザー・ピープルズ・マネー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "オフ・ブロードウェイにおいて人気を博していた同名舞台をN・ジュイソンが映画化した作品。金融界のカウボーイこと会社乗っ取り屋ラリー。狙った獲物は決してはずさない彼の新しいターゲットは、創業81年の優良企業ニューイングランド電信電話会社であった。決して妥協を許さないこの会社の会長ジョーギーとホットなマネーゲームが繰り広げられていく。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mississippi Burning", "ミシシッピー・バーニング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "1960年代のアメリカ南部における人種差別問題をテーマに、アラン・パーカー監督が実話に基づいて描いた社会派サスペンス。ミシシッピー州で3人の公民権運動家が消息を絶った。FBIは2人の捜査官を派遣するが、彼らを待ち受けていたのは、非協力的どころか敵意まで剥き出しにして捜査を妨害する住民達だった……。たたきあげのベテラン捜査官をジーン・ハックマンが、その相棒の若手エリート捜査官をウィレム・デフォーが熱演。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Good Woman", "理想の女（ひと）", "", "", "http://image.eiga.k-img.com/images/movie/1409/315x210.jpg?2940", 93, "人気女優スカーレット・ヨハンソン主演の人間ドラマ。南イタリアの避暑地にやってきたニューヨークの上流階級夫妻ロバートとメグ。これまで夫の愛を信じてきたメグだが、夫が奔放な恋愛遍歴を重ねてきたアメリカ女性アーリーンと密会しているという噂を耳にして動揺する。オスカー・ワイルドの「ウィンダミア卿夫人の扇」を、1930年代のイタリアのセレブが集う高級避暑地を舞台に映画化。監督は「完全犯罪」のマイク・バーカー。", "2005-09-10", "3,9,4,1,58", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画クレヨンしんちゃん　伝説を呼ぶ　踊れ！アミーゴ！", "映画クレヨンしんちゃん　伝説を呼ぶ　踊れ！アミーゴ！", "", "", "http://ecx.images-amazon.com/images/I/61Po5bIifnL._SL210_.jpg", 90, "人気テレビアニメ「クレヨンしんちゃん」の劇場版第14作。しんのすけが暮らすカスカベに、ナゾの「そっくり人間」がいつの間にか本物の人間とすり替わってしまう「世界サンバ化計画」の魔の手が忍び寄る。野原一家は国際秘密組織SRIの女性捜査官ジャッキーによって危ないところを救出されるが、すでにカスカベはそっくり人間で埋めつくされていた。前作「伝説を呼ぶ　ブリブリ3分ポッキリ大進撃」のムトウユージが引きつづき監督を務める。", "2006-04-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("音楽", "音楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "近親相姦とフロイト的精神分析を扱った三島由紀夫の名作の映画化。監督は女を描くことに定評のある増村保造で、性的快感を得られない女性の深層心理を乾いたタッチで解き明かしていく。西洋バサミのイメージを効果的に使った数少ない日本のニューロティック映画。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Aida", "World Classics＠CINEMA アイーダ", "", "", "http://image.eiga.k-img.com/images/movie/55109/315x210.jpg?2781", 171, "世界中から選りすぐったオペラやバレエの名舞台を映画館で楽しめる「World Classics＠CINEMA」シリーズの1作として、スペインのリセウ大劇場にて2003年に上演されたベルディ作曲によるオペラ「アイーダ」をデジタル映像化しスクリーン上映。エジプトの奴隷となったエチオピア王女アイーダと、彼女と恋に落ちてしまったエジプト軍の将校ラダメス、祖国への想いと恋人との間で揺れ動く2人の愛の行方を描きだす。", "2010-01-30", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緋牡丹博徒・お竜参上", "緋牡丹博徒・お竜参上", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "浅草の鉄砲久一家にワラジを脱いだお竜は、鉄砲久の娘婿が持っている興行権を鮫州政一家が狙っていることを知り、悪どい鮫州政のやり口を封じるために、渡世人・青山常次郎とともに殴り込む。加藤泰が監督した第３作「花札勝負」の後日譚的なストーリーで、藤純子の静と動の美しさが見事にスクリーンに焼きつけられた。特にお竜が菅原文太扮する流れ者のヤクザ・青山常次郎を見送り、そっとミカンを渡す雪の今戸橋のシーンは、圧巻。藤純子は何者にも代えがたい情緒を見せた。加藤泰の演出が、すみずみまで行き届いたシリーズ屈指の最高傑作である。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウンタマギルー", "ウンタマギルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "沖縄芝居の最もポピュラーな演目の一つ、沖縄版ねずみ小僧といえる義賊伝説をモチーフに、時代を沖縄の1969年“日本復帰”直前に移して描いた意欲作。豚の化身の妖艶美女と禁を破って関係を持った砂糖工場の労働者ギルーは、島民が恐れる森の中に追放され、そこで霊力を身につけて空飛ぶ義賊に。本土復帰派や米軍統治派の金持ち連中から金品を盗み出し、貧しい独立ゲリラにそれをばらまいていく……。妖精、動物占い、夢見の術など、生と死、夢と現実が未分化の沖繩の風土を伝える神話的イメージをふんだんに交えて、ゆったりとしたリズムで綴る聖俗混交ファンタジー。セリフが琉球語のため日本語字幕付き。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRST BLOOD", "ランボー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ベトナムでグリーンベレーとして活躍した男ランボーが、復員後とある町で不審者として警察から屈辱的な扱いを受ける。怒りを爆発させた彼は、数百人の警官と死闘を繰り広げるが説得を受け降伏する。闘う男の孤独とロマンを色濃く見せ、大ヒットしたアクション映画。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日野日出志の　ザ・ホラー怪奇劇場第ニ夜", "日野日出志の　ザ・ホラー怪奇劇場第ニ夜", "", "", "http://ecx.images-amazon.com/images/I/21VG3CNWMCL._SL210_.jpg", 175, "日本を代表するカルト・ホラー漫画家、日野日出志の代表６本を、６人の新進気鋭監督が映画化したオムニバス・シリーズの第２作。今回は、奇病に侵された少年の悲劇を描く「爛れた家～蔵六の奇病より～」、ある高校のオカルト研究会が女生徒の自殺事件を探る「オカルト探偵団・死人形の墓場」、悪夢と現実の間で困惑する女子高生３人の物語「恐怖列車」の３作品が登場。深遠なメッセージを含んだ衝撃的かつグロテスクな描写で、多くのアーティストを虜にした日野作品のエッセンスが、余すことなく抽出されている。ビデオ、DVDは単品での発売もされている。", "2004-11-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANKENSTEIN GENERAL HOSPITAL", "フランケンシュタイン／桃色病院", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "フランケンシュタイン博士の孫の孫の孫フランケンハイマー博士が、またも人造人間を創造してしまう。しかしこの怪物、異常なまでの食欲と性欲を持つプッツン・モンスターであった！　フランシス・F・コッポラ作品などの編集を務めていた元女優のD・ロバーツが初監督するセクシー・コメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sleep Is for Sissies", "エッジ・シティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "パンクでアナーキーな映画を作り続けるイギリスの鬼才アレックス・コックス監督が、カリフォルニアのUCLA在学中に製作した幻の処女作。ロサンゼルスに住むイギリス人芸術家ロイに降りかかる悪夢のような出来事を、ブラック・ユーモアをちりばめて描いた短編フィルム。主人公ロイを、コックス監督自らが演じている。また、本作の撮影を担当しているマイケル・マイナーは、後に「ロボコップ」の脚本を手掛け成功を収めた。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISTY〈ミスティ〉", "MISTY〈ミスティ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "池田敏春が監督したミステリー・ロマンの香り漂う傑作ハードボイルド。姉の死によって故郷に舞い戻った横浜のヤクザ・藤川。姉の自殺に疑問を抱くそんな彼に際限ない暴力と悲しみが襲いかかる。次々に殺されていく藤川の幼なじみたち。そして復讐を誓う藤川にも魔の手は襲いかかり、ついに友人を死に追いやった殺人鬼と対決するが、その裏で糸を引いていたのは……。かつてクリント・イーストウッドが「恐怖のメロディ」で使用した名曲『ミスティ』を劇中に巧みに使用、その流麗な映像にマッチした独特の世界を作り出している。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHE’S BEEN AWAY", "おかえりなさい、リリアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "英国演劇界の重鎮P・ホールが監督した、二人の女性の人間ドラマ。60年間精神病院にいた叔母リリアンの常軌を逸した行動は夫婦の悩みのタネになる。その頃、妻ハリエットが懐妊。情緒不安定になったハリエットは家を出るが、リリアンもついてきて……。女同士であることで気持ちをわかち合うハリエットとリリアンのふれあいが見もの。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alvin and the Chipmunks", "アルビン　歌うシマリス3兄弟", "", "", "http://image.eiga.k-img.com/images/movie/53239/315x210.jpg?3767", 92, "間違って都会にやってきたシマリス3兄弟のアルビン、サイモン、セオドアは、売れない作曲家のデイブの家に転がり込む。まるで家族のような新しい生活が始まり、やがてアルビンたちがデイブの曲を歌うと大ヒットする。3兄弟はたちまち大スターになるが、レコード会社の企みでデイブと引き離されてしまい……。デイブ役を演じる「ドリームキャッチャー」のジェイソン・リーと、CGで描かれるシマリスたちが共演する。", "2008-09-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不惑のアダージョ", "不惑のアダージョ", "", "", "http://image.eiga.k-img.com/images/movie/56839/315x210.jpg?1794", 70, "貞淑な人生を送ってきたひとりの修道女が、更年期を迎えたことから起こる心身の変化を叙情的に描いたドラマ。若き日から修道女として生きてきた真梨子は、40歳になり人よりも早く更年期にさしかかっていた。結婚や出産、家族など、それまで避けてきた話題が耳に入りはじめ、思い悩む日々を送っていたある日、教会に通う女性からバレエ教室のピアニストの代理を頼まれる。バレエ教室に足を運んだ真梨子は、清らかで厳粛に踊るバレエダンサーたちに次第に魅了され、自身もピアノの演奏に楽しみを見出していくが……。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ware Case", "ウエヤ殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "ジョージ・P・バンクロフトによる戯曲を基に、「キング・ソロモン」「メリー・ポピンズ」等の作品で知られるイギリスの名匠ロバート・スティーブンソン監督がメガホンを取ったサスペンスドラマ。ロンドンで、ある大富豪の男性が殺害されるという事件が発生。被害者の義理の兄弟にあたる男性がその容疑者として拘束された。彼の自宅にある池から遺体が発見されたのだ。審議の末、彼は無罪を勝ち取り釈放されるが……。", "1938-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRAINING DAY", "トレーニング・デイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "犯罪の絶えない街ロサンゼルスを舞台に、犯罪の最前線に立つ警察官の苦悩と戦いを活写するアクション。L.A.市警に13年間勤務する麻薬捜査官、アロンソの前に、理想に燃えた新人警官ジェイクが現れる。憧れのアロンソに認められようと躍起になる彼だったが、次第にアロンソの腐敗した論理に引きずり込まれていくことに……。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POISON IVY ２：LILY", "アリッサ・ミラノ　ボディヒート２ 挑発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "エロティックな作品が続くA・ミラノの主演作。ある美術大学に入学してきた純真無垢な美少女リリィは、新居で以前住んでいた女性の淫らな写真と性生活を赤裸々に告白した日記を見つける。それを読むうちに彼女は、自らの内側に潜んでいた性の本能が目覚めていく。外見までも淫らになっていくリリィの虜になっていったのは、同級生グレディンと教授ドナルドだった。彼らの愛欲が頂点に達した時、事態は悲劇を迎える……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST RUN", "ラスト・ラン 殺しの一匹狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "暗黒街のドライバー稼業を長年務め、老いを見せはじめた男が、人生最後の大仕事として脱獄囚をスペインに逃がすことにすべてを懸けた。脱獄囚を抹殺すべく組織の執拗な攻撃を受けたとき、運び屋は敢然と敵に立ち向かう……。カーチェイスも見ごたえ十分な渋いアクション。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("通貨と金髪", "通貨と金髪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「鬼火」「恋極道」など哀愁漂う男のロマンを描く望月六郎による、エロティックな異色ドラマ。一見平凡な大学教授、高塚は金髪フェチ。彼は、ある日現れたアメリカからの留学生アンナにたちまち魅了される。そんな彼をもてあそぶように、アンナは高塚を性の奴隷にし……。諏訪太朗が妄想と欲望に取りつかれる大学教授を怪演。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　CLOSE　SHAVE", "ウォレスとグルミット、危機一髪！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 31, "発明家ウォレスとインテリ犬グルミットの活躍を描いた、人気クレイ・アニメーション・シリーズの第３作。窓拭きサービスを始めたウォレスとグルミットは依頼を受け、町の毛糸店へ。そして、そこの女主人に熱を上げるウォレス。だが、この店は羊の盗難事件に関与していた。不審な動きを察知したグルミットは悪党の罠におち、羊盗難事件の容疑者として逮捕され……。ほのぼのとしたユーモアにサスペンスの要素を加味。スピード感のある見せ場でスリルを盛り立てる。なかでも、悪党のトラックをサイドカーで追うチェイス・シーンの奇想天外な展開は見もの。なお、劇場公開時には本作の制作スタジオ“アードマン”による短編６本と併せて上映された。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遊星王子（第１部・第２部）", "遊星王子（第１部・第２部）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "雑誌『少年』に連載され、TVでも人気を呼んだ伊上勝の同名小説を映画化した空想科学映画。地球征服をたくらむ悪の帝王・まぼろし大使と、地球の平和を守る正義の味方・遊星王子の死闘が展開される。新人時代の梅宮辰夫が、遊星王子に扮し大活躍する。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rocco e i suoi fratelli", "若者のすべて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 176, "　「ロッコと彼の兄弟たち」の原題通り、都会の生活に翻弄されるパロンディ兄弟の姿を描いた、ルキノ・ビスコンティの代表作。ミラノに住む長男を頼りに、南部から移住してきたパロンディ家。次男のシモーネはボクサーとして成功への糸口を見つけるが、娼婦ナディアに溺れ落ちぶれていく。彼女が次に愛を求めたのは、三男のロッコだった。公開当時は、ナディアへの暴行シーンや殺害シーンが問題視され、スキャンダルを巻き起こした。", "1960-12-27", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Departure", "Departure", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "沖縄・那覇のストリートを舞台に誰もが一度は経験する“青春の終わり”という普遍的なテーマを斬新な映像で描いた作品。高校卒業後、別の道を歩くこととなった一也、マサル、秀介。一也は恋人に別れを告げるため、秀介は最後の夜を過ごすガールフレンドを求め、マサルはカラオケボックスでバイトに勤しみながら最後の夜を過ごす。翌日、ロンドンへ留学する予定だった秀介が向かった先は……。1999年“サンダンス・NHK国際映像作家賞”優秀賞を受賞。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まいっちんぐマチコ！ビギンズ", "まいっちんぐマチコ！ビギンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "熱烈なファンが多いコミック『まいっちんぐマチコ先生』の実写版。アイドル主演作で異才ぶりを発揮する「オッパイ星人」の鈴木浩介監督が、人気アイドルの磯山さやかをヒロインに迎え、お決まりの“まいっちんぐポーズ”が誕生するまでのエピソード・ゼロにあたる秘話を描く。進学校こりゃま学園は、江戸川橋教頭と一部の生徒会によって、ほしいままに支配されていた。そこへ教育実習生のマチコが赴任するのだが……。", "2005-09-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT WATCH", "夜をみつめて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "夫と死別し現在は再婚していながらも、過去にこだわり不眠症に悩むエレン。そんなある夜、彼女は隣の廃屋にのどを切られた男の死体があるのを自室の窓から目撃、警察に知らせるが、死体などどこにも見当たらず……。L・フレッチャーのミステリー舞台劇を映画化した、E・テイラー主演作。", "1973-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOKYO RAIDERS", "東京攻略", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "消息を絶った日本人フィアンセの行方を捜しに、香港から東京へやってきたメイシーと、ひょんなことから彼女と行動をともにすることになったユン、そして東京で探偵業を営むリン。彼らが繰り広げるトラブルを描くサスペンス・アクション。新宿・渋谷など東京で大ロケーションを敢行し、仲村トオルほか日本人俳優も多数出演している。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flight", "空の王者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "アメフトの大学対抗試合で大失敗して故郷を去ったレフティ。空軍に入隊した彼は、そこで知り合った将校のウィリアムスと親友になる。エノリアという看護婦を好きになった時も、ウィリアムスが彼女に想いを寄せていることを知り潔く身を引いたが、エノリアはレフティのことが好きだと告白する。そんな中、激しい戦闘が始まり、敵地へ飛んだレフティが行方不明になる……。原作は本作に出演もしている俳優ラルフ・グレイブス。", "1929-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬神家の謎 悪魔は踊る", "犬神家の謎 悪魔は踊る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "人里離れた湖畔の旧家・犬神邸で、遺産相続をめぐって起こる連続殺人事件。３人の母と３人の子供の恐るべき暗闘の秘密、また事件の鍵を握る“顔の無い男”とは？　スタッフ陣が大幅に様変わりした第５作。この頃には、すでに金田一耕助の名を借りた“多羅尾伴内”ものになっていた。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女獄門帖 引き裂かれた尼僧", "女獄門帖 引き裂かれた尼僧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "女郎のおみのはつらい生活にたまりかねて逃亡する。彼女は現世と縁を絶つため噂に聞いた駆け込み寺、愁月院を探し出し、世話になることに。だがその寺は裏でアヘンを栽培する悪の巣窟だった……。島守俊夫の『女地獄獄門帖』をもとに、駆け込み寺に渦巻く陰謀と狂気を赤裸々に描き出していく。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心中天網島", "心中天網島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "篠田正浩が10年来構想を練っていた近松門左衛門の浄瑠璃を映画化。脚本に詩人の富岡多恵子と音楽家の武満徹が参加している。紙屋治兵衛は、女房子供のある身で女郎・小春と深いなじみになり、ついには女房を捨て小春と情死行に至る。前半の浮世絵の複写や、巨大な文字の壁などで構成されたユニークなセットが舞台となり、後半の道行きや心中の場面はロケ撮影となっている。墓地での濃厚なラブシーンや、最後の鮮烈な心中シーンに治兵衛と小春の情熱的な愛と性が象徴的に表現される。成島東一郎の白黒の映像と、武満の音楽が独特の雰囲気を作っている。女房と遊女の二役をこなす岩下志麻の演技も高く評価された。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PIGALLE", "ピガール　欲望の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "フランスの映画作家K・ドリディの長編デビュー作。風俗スポットが乱立するパリの歓楽街・ピガールに生きる男女の愛と孤独のドラマが、現地ロケによる臨場感の込もった映像で語られる。陽気なストリッパーのヴェラと深い友情で結ばれたフィフィは、ピガールのしがないチンピラ。フィフィの恋人はケバケバしい性転換者のディヴィンヌだった。そんな彼らの気ままな日常に、新興勢力のヤクザ者が現れたことから危険な事件が巻き起こる。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("情無用の刑事（でか）まつり", "情無用の刑事（でか）まつり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「主人公は刑事」「完成尺は10分以内」「本編中に最低5回ギャグを入れる」という3つの条件のもと制作されるオムニバスシリーズ「刑事（デカ）まつり」の第8弾。監督には「エコエコアザラク」「ケータイ刑事」シリーズなど映画・テレビで幅広く活躍する鈴木浩介、「ラッパー慕情」で注目を集めた藤原章、シリーズ全作のポスターデザインを手がけるアートディレクター・中平一史、ピンク映画界の俊英・城定秀夫ら、個性豊かな8人が顔をそろえた。", "2005-08-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クローズZERO II", "クローズZERO II", "", "", "http://image.eiga.k-img.com/images/movie/53719/315x210.jpg?1594", 133, "累計発行部数4200万部を超える高橋ヒロシ原作の人気コミック「クローズ」を、オリジナルストーリーで実写映画化した「クローズZERO」の続編。前作で鈴蘭男子高校の覇権争いを制した滝谷源治（小栗旬）たちと、ライバル校・鳳仙学園との間で起こる新たな抗争が描かれる。前作からの続投となる小栗旬、山田孝之の他、新メンバーとしてロックバンド「RIZE」の金子ノブアキ、三浦春馬らが出演。監督は前作同様、三池崇史。", "2009-04-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旗本退屈男 謎の蛇姫屋敷", "旗本退屈男 謎の蛇姫屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "おなじみ市川右太衛門主演の“旗本退屈男”もの。額には三日月傷を持つ通称旗本退屈男、直参旗本・早乙女主水之介の活躍を描く娯楽時代劇だ。大老・柳沢美濃守が、立花家の家宝、旗差物を奪ったことから、彼が旗差物に隠された家康ゆかりの財宝を狙っていることを知った退屈男の活躍を描く。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("容疑者 室井慎次", "容疑者 室井慎次", "", "", "http://ecx.images-amazon.com/images/I/51Ns0pBGUSL._SL210_.jpg", 117, "「交渉人 真下正義」に続いて、『踊る大捜査線』シリーズから派生したスピンオフ・ムービー。柳葉敏郎扮する寡黙な警視庁管理官、室井慎次を主人公にしたサスペンスが繰り広げられる。殺人事件の捜査ミスから、逮捕されてしまった室井。警察庁と警視庁の対立や、弁護士の執拗な追及がからみ、どんどん立場を悪くしてしまう彼に、無実を証明する機会はあるのか。室井の究極の決断はもちろん、彼の知られざる過去やおなじみの人気キャラの登場など、ファン必見のポイントはたっぷり。殺人捜査の責任を取らされ、逮捕された室井。警察の不正を暴くと豪語する弁護士は、彼への追及の手を緩めない。一方、室井の姿勢に心動かされた刑事たちが殺人事件の真相を探る。", "2005-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("へんりっく　寺山修司の弟", "へんりっく　寺山修司の弟", "", "", "http://image.eiga.k-img.com/images/movie/54759/315x210.jpg?2214", 117, "寺山修司の死後、戸籍上の弟となった森崎偏陸を題材にしたドキュメンタリー。高校時代に寺山主宰の劇団「天上桟敷」に入団した偏陸は、寺山亡き後に寺山籍に入り、その魂の継承者としてジャンルを越えた活動を続けている。「樹の上の草魚」の石川淳志監督が7年間にわたって偏陸を追い続け、寺山ワールドのトリックスターという運命を背負った偏陸の中に存在する“人間・森崎偏陸”の姿を浮かびあがらせていく。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VERSTECKTE LIEBE", "シークレット・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "クレタ島の一軒屋に一人こもって執筆を続けるドイツ人の作家ペーター。言葉の通じないこの世界で孤独を友としながら暮らしていた彼が、ある日一人の少女と出会い、二人は心を通わせていく……。セリフを極限までけずり、クレタの美しい自然の中に人生の喜びを謳い上げた佳作。", "1986-01-01", "27,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE SONG", "LOVE SONG", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1980年代に人気を博した故・尾崎豊のヒット曲に乗せて描くラブ・ストーリー。1985年の北海道。小さなレコード店で、高校生の彰子は、一枚のレコードを薦めてくれた店員の松岡に恋をする。だが、松岡はかねてからの夢であった自分のレコード店を開くために突然東京へ行ってしまう。高校生活最後の夏休みに松岡に会うため上京した彰子だが、そこには厳しい現実が待ち受けていた……。年上の男性への淡い恋心を募らせる少女と、夢に敗れ挫折から這い上がろうとする青年。つまずきながらも情熱に向かって突き進む二人の姿に浮かび上がる青春の痛みと輝きを、尾崎豊の歌が切なく包み込む。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("縮図", "縮図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "幼くして芸者置屋に身売りされた、銀子という女の半生記を描いた力作。芸者の世界という新藤兼人の得意とする題材であるだけに、粘質性の演出を前面に据えて、どこまでも銀子を追いつめていくという厳しさを見せている。その銀子を演じた乙羽信子は初の本格的汚れ役に挑む。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明治一代女", "明治一代女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "歌舞伎役者・沢村仙枝と相思相愛の芸者・お梅は、襲名披露のために箱根の巳之吉から金を借りるが、その金はお梅を想うあまり無理算段したものであった……。巳之吉に扮した田崎潤が好演し、伊藤監督の次作「下郎の首」の主役に抜擢される。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUR HOSPITALITY", "荒武者キートン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "日本では、これが最初に公開されたキートンの長編であり、キートン作品でも５本の指に入る傑作の１本。仇敵の娘とは知らずに、列車で乗り合わせた若い娘を愛してしまったウィリー。彼女の兄弟の襲撃を乗り越えて、二人は果たして結ばれるや否や……。", "1923-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SMOKE", "スモーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ニューヨーク、ブルックリンのタバコ屋に集まる人々の日常を、過去と現在、嘘と本当を織り交ぜながら描いた味わい深い人間ドラマ。14年間、毎朝同じ時刻に店の前で写真を撮り続けているタバコ屋の店長オーギーのなじみ客で作家のポールは、家に帰る途中、危うく事故に遭いそうなところを黒人少年ラシードに助けられる。その数日後、ラシードがポールの家にやって来る。彼を２、３日家に置くことにしたが、ラシードの語る自分の正体はどうやら嘘らしい。一方オーギーの店には18年前に別れた元恋人のルビーが現れ……。現代アメリカ文学を代表する作家P・オースターが、W・ワン監督のために書き下ろした作品。", "1995-01-01", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー×仮面ライダー×仮面ライダー THE MOVIE 超・電王トリロジー EPISODE YELLOW お宝 DE エンド・パイレーツ ", "仮面ライダー×仮面ライダー×仮面ライダー THE MOVIE 超・電王トリロジー EPISODE YELLOW お宝 DE エンド・パイレーツ ", "", "", "http://image.eiga.k-img.com/images/movie/55379/315x210.jpg?4097", 0, "「劇場版　超・仮面ライダー電王＆ディケイド　NEOジェネレーションズ　鬼ヶ島の戦艦」の続編第3弾。「EPISODE RED　ゼロのスタートウィンクル」「EPISODE BLUE派遣イマジンはNEWトラル」「EPISODE YELLOW お宝 DE エンド・パイレーツ」の3連作を、2週間ごとに連続上映する。モモタロスの鼻を頼りに、良太郎たちはナゾのイマジンを追いかけていた。やがて彼らの前に、イマジンが憑依した海東大樹が現れ、電王のパスと新幹線型の時の列車“デンライナー”を盗んでいく。一行は途方に暮れるが、そこに時間警察の黒崎レイジが現れる。", "2010-06-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベルセルク　黄金時代篇I 覇王の卵", "ベルセルク　黄金時代篇I 覇王の卵", "", "", "http://image.eiga.k-img.com/images/movie/55959/315x210.jpg?3672", 80, "戦乱で荒廃した架空の中世の世界を舞台に、屈強な剣士ガッツの活躍を描く三浦建太郎の同名人気コミックを映画化。原作の中でも人気の「黄金時代」を3部作で描く第1部で、アニメーション制作を「鉄コン筋クリート」「マインド・ゲーム」のスタジオ4℃が担当する。孤独な剣士ガッツは、身の丈を超える巨大な長剣を自在に操り、傭兵として各地を渡り歩いていた。傭兵集団「鷹の団」のグリフィスはそんなガッツに目をつけ、決闘でガッツを制して鷹の団に引き入れる。幾多の激戦を潜り抜けるうちに、ガッツとグリフィスは固い絆で結ばれていくが……。", "2012-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マルサの女", "マルサの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "マルサ（国税局査察部）の女性捜査官が、ラブホテルの経営者を脱税で摘発するまでを描く。伊丹十三監督の第３作であり、1987年度の国内の映画賞を総ナメにした。ヤリ手の税務調査官・板倉亮子は、ラブホテルのオーナー・権藤に脱税の疑いがあるとにらんで調査を始めるが、なかなか証拠がつかめない。そんな時、彼女は国税局査察部捜査官に任命される。摘発のプロたちの仲間入りをした亮子は調査経験を重ね、上司の花村とともに再度、権藤を調べることになる……。宮本信子は、ソバカスだらけの顔に“マルサ・カット”の異名をとるボサボサのオカッパ頭のカツラをつけ、プロの女捜査官を好演。対する権藤を演じた山崎努は、女捜査官の前で常に不敵な笑顔を絶やさないという食えない男に扮し、板倉の上司・花村役の津川雅彦が主役を食わんばかりの名脇役ぶりを見せる。室内撮影の妙ともいうべき前田米造のカメラワークについては、周防正行監督による本編のメイキングビデオ「マルサの女をマルサする」に詳しい。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Major and the Minor", "少佐と少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "　日本では劇場未公開であるビリー・ワイルダーの第2作目。待望のハリウッド初進出作品で、当時のスター、ジンジャー・ロジャースの起用に成功している。アイオワへ向かう列車代を浮かすため、スーザンは12歳の少女に変装する。偶然乗り合わせたフィリップ大佐との間に、ほのかな想いが芽生えるが……。少女愛をテーマにすえているが、ベストセラー小説「ロリータ」発刊の12年前の製作である。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Roman Holiday", "ローマの休日", "", "", "http://ecx.images-amazon.com/images/I/51-dxl3iHGL._SL210_.jpg", 118, "アメリカ映画初出演となるオードリー・ヘプバーンと名優グレゴリー・ペック共演のロマンティック・コメディ。ヨーロッパを周遊中の某小国の王女アン（ヘプバーン）は、常に侍従がつきまとう生活に嫌気が差し、滞在中のローマで大使館を脱出。偶然出会ったアメリカ人新聞記者ジョー（ペック）とたった1日のラブストーリーを繰り広げる。監督は巨匠ウィリアム・ワイラー。54年のアカデミー賞では主演女優賞、脚本賞、衣装デザイン賞を受賞した。", "1954-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西銀座駅前", "西銀座駅前", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "「盗まれた欲情」でデビューした今村昌平の第２作。人気絶頂のフランク永井の歌にのせて、男の弱さと女の強さが浮き上がる。女房に頭の上がらない薬局の主人が、浮気に走って失敗するまでの絶望的状況を、コメディ・タッチで描く。今村流・人間悲喜劇の初期作品。DVDは「今村昌平 日活作品全集(1)」に収録。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山田村ワルツ", "山田村ワルツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "嫁不足に悩む農村・山田村を舞台に独身の青年団が起こす集団見合い騒動を、ポップなタッチで描くユニークなコメディ。一色伸幸が、都会と田舎のギャップを独特の言語感覚で描いている。主題歌を担当した米米CLUBもゲスト出演。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十手舞", "十手舞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "町奉行の命を受け、悪の巨魁を秘密裏に葬り去る“影十手”となった花嵐のお蝶が、闇の殺し屋に堕とされた自分に気づき、奉行に復讐を挑むまでを描いた時代劇アクション。五社英雄自らの原作の映画化で、新体操のリボンを操った石原真理子の華麗さが見もの。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルビーフルーツ Ruby Fruit", "ルビーフルーツ Ruby Fruit", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "斎藤綾子の同名小説を「喪の仕事」の君塚匠が映画化。恋人を亡くし、傷心旅行でバリ島を訪れたまい子。彼女はそこで、不思議な魅力を持つ女性やを代と出会う。男を愛することしか知らなかったまい子は、やを代との愛に戸惑いながらも溺れていく。そして、本当の快楽を知るために、天国の味がする赤い果実を味わう。身体を求め、心を求め合う二人。青い空と美しい海、そしてガムランの音楽とバリダンスのきらびやかな衣装が醸し出す神秘の匂いのなかで、究極の愛が二人を極限の世界へと導いていく。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COMRADES OF SUMMER", "メジャーリーグをぶっ飛ばせ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "故障で大リーグをお払い箱になり、ロシアのナショナル・チームの監督に就任した名選手スパーキー。彼の指導の下で力をつけた弱小チームはアメリカに遠征し、スパーキーの古巣マリナーズにエキシビジョン・マッチを挑むが……。「がんばれ！ベアーズ」を思わせる、弱小野球チームのコミカルな奮闘記。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大酔侠", "大酔侠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "アジアの巨匠と呼ばれたキン・フーの初期作品で、武侠アクション映画のルーツとて映画史上に残る傑作。兄を人質にした山賊を追う女剣士・金燕子は、窮地を謎の酔っ払いに助けられる。彼こそは伝説の武人・酔侠だった。彼の協力を得た金燕子は再び山賊団に戦いを挑む。「グリーン・デスティニー」にも出演したチェン・ペイペイが女剣士役でキレのある立ち回りを見せる。", "1966-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版アニメ　忍たま乱太郎　忍術学園　全員出動！の段", "劇場版アニメ　忍たま乱太郎　忍術学園　全員出動！の段", "", "", "http://image.eiga.k-img.com/images/movie/55749/315x210.jpg?1448", 78, "忍術学園に通う忍者のたまご“忍たま”たちの成長を描くNHK教育の人気アニメ「忍たま乱太郎」の劇場版。夏休みを終えたばかりの忍術学園で、1年は組の喜三太が行方不明になっていることが判明する。手違いで6年生の宿題を当てられてしまった喜三太は、「オーマガトキ城主のふんどしをとれ」という宿題をこなすため、タソガレドキ城と合戦中のオーマガトキ城へと行ってしまったのだ。乱太郎たちは喜三太を救出するため、オーマガトキ城へ向かうが……。", "2011-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARTHUR", "ミスター・アーサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "大財閥の放蕩息子アーサーは政略結婚を命じられ、逆らえば相続権を取りあげると言い渡される。しかし貧しい女優志願の娘に恋をして……。小柄なムーアが、けたたましい笑い声と酔っぱらいぶりで笑わせる、ほのぼのロマンティック・コメディ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴力団", "暴力団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "小沢茂弘監督・鶴田浩二主演によるギャングもの。ギャング仲間の策謀で刑務所送りにされた主人公は、出所後二人の舎弟分とともに反撃に転じるが、二人の弟分は次々に殺され主人公は単身殴り込みをかける。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN PLAN D’ENFER", "ホワイ・ミー？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "そろそろ泥棒稼業から足を洗いたいと願う泥棒二人組は、これが最後のヤマと、とある宝石店に忍び込む。ところが盗み出した宝石の中に“ビザンチンの炎”と呼ばれるビザンチン帝国の秘宝が交じっていて……。登場人物のオーバーアクションが愉快な、ウェストレイク原作のアクション・コメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前音頭", "喜劇・駅前音頭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "小田急線沿線の商店街を舞台にした一編。駅前商店街で対立する森田屋呉服店の徳之助とBON洋装店の孫作。盆踊りを浴衣でやるかアロハにするかでまたまた大もめ。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("哦、香雪／SWEET SNOW", "少女香雪〈シャンシュエ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "香雪の暮らす村に駅ができ、都会の匂いを運んでくる汽車を少女たちは毎日心待ちにするようになる。都会から遠く離れた山村に育った少女たちの無垢な心を詩情豊かに描いた作品。主役の香雪を演じるのは「黄色い大地」で好演したシュエ・パイ。", "1989-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦火の果て", "戦火の果て", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "かつて軍港としてにぎわっていたものの、今ではさびれた港町を舞台に、元海軍大佐とかつての部下、そして一人の女性をめぐる愛憎を描くメロドラマ。二人の男の対決に吉村＝新藤コンビは戦争責任問題を追及するが、全体的に甘くご都合主義的な内容なので鋭さはない。吉村・新藤・山田典吾・殿山泰司らによって結成された近代映画協会第１回作品。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE FOR THE ROAD", "ハートビートで追いかけて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "政治家志望の青年ポール。彼は尊敬する上院議員クラークに、娘ロビンをサウスカロライナにある施設まで送ることを頼まれた。うまくいけば出世街道まっしぐらと、小説家志望の悪友T.S.を伴い、さっそく南への旅を開始。ところがロビンは一筋縄ではいかないツッパリ娘で、スキあれば逃げ出そうと、二人を散々な目に遭わせる。やがてポールは、ロビンが離れて暮らす母親ブランシェに会いたがっていることを知るのだが……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HALLELUJAH", "ハレルヤ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "サイレント映画時代の名匠K・ヴィダーが初めて手掛けたトーキー映画。当時としては異例のオール黒人キャストとしても注目を集めた秀作ドラマだ。綿花を栽培しているジークは、美しい踊り子チックと彼女の情婦ホットショットにだまされて金を奪われる。ホットショットとのいさかいの最中に、誤って自分の弟を殺害してしまったジークは、信仰生活に入った。だが、チックへの思いは断ち切れず、そのため激しい葛藤に苦しむ……。", "1929-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("XANADU", "ザナドゥ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "音楽の殿堂、ザナドゥの建設を目指す初老の男と青年、二人に力を貸す女神らが繰り広げるファンタジーあふれるミュージカル。ジャズとロックの融合が楽しめる一編。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CURSE OF CAT PEOPLE", "キャット・ピープルの呪い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "名匠R・ワイズのデビュー作としても見逃せない、「キャット・ピープル」（1942）の続編。今回はある少女に猫族が乗り移り、危険な幻想世界へと誘う。RKOの経営危機を救うヒットとなった。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙怪獣ガメラ", "宇宙怪獣ガメラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "地球征服をたくらむ宇宙海賊・ザノンが次々と送り込む怪獣を、平和星M38から派遣された３人の宇宙人が、ガメラを呼んで撃退する。“ガメラ”シリーズ全７作から、ガメラと怪獣たちの戦闘シーンをピック・アップしたアンソロジー。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WATER", "レゲエde ゲリラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "カリブ海に浮かぶ小さな島カスカラを舞台に、突然噴き出た天然良質のミネラル・ウォーターをめぐって、全世界の利権争いが展開する。製作総指揮のG・ハリスンをはじめ豪華絢爛たる出演陣。才能の戦略戦争といわんばかりのアクション・コメディ。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　CONTES　DE　KISH", "キシュ島の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "ペルシャ湾に浮かぶキシュ島を舞台にしたオムニバス。難破船から流出したダンボールを持ち帰り家に飾りつける男と、その装飾を恐れて落ち着きを失う妻の物語「ギリシャ船」、海辺の小屋に住み、あらゆる手段でお金を稼ぐ男の、その目的を描いた「指輪」、ドアを担いで砂漠を歩き続ける老人の姿を淡々と綴った「ドア」の３話で構成。イラン映画界の名だたる鬼才が結集し、幻想的な美しさをたたえた映像とシュールな物語を、それぞれ生み出している。", "1999-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチバン", "ガチバン", "", "", "http://image.eiga.k-img.com/images/movie/53829/315x210.jpg?1282", 75, "窪塚俊介主演のヤンキー映画。生まれながらに喧嘩の強い拓海は、古沢と三上の3人で“三羽カラス”として恐れられていた。中学を卒業し、別々の高校に進んだ3人は、それぞれの学校で番長になると誓いを立てるが、拓海の通う衣笠高校には停学中の最強の不良・城島が帰ってきて……。", "2008-07-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE AT FIRST BITE", "ドラキュラ都へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ドラキュラ伯爵が、ルーマニアの古城を追い出されてニューヨークにやって来た。そして憧れのカバー・ガール、シンディにアタックを開始するが……。G・ハミルトン扮するドラキュラがダンディーぶりを発揮。ホラー・コメディ・ブームの走りとなった一作だ。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喋血雙雄", "狼／男たちの挽歌・最終章", "", "", "http://ecx.images-amazon.com/images/I/51V%2B6sAkU%2BL._SL210_.jpg", 111, "引退を決意していた一匹狼の殺し屋ジェフリーは、クラブ歌手ジェニーを銃撃戦に巻き込んで失明させてしまった。彼はその償いのために最後の仕事を引き受けるが、警察の手が伸び、組織もジェフリーを裏切り、彼は窮地に立たされる……。ひとりの女性を傷つけたことで、本来の人間性に目覚めてゆくある殺し屋の姿を描いた香港ノワール。ウー監督独自の美意識にあふれた作品。特に欧米での評価が高く、この作品が数々のハリウッド映画人に影響を与えた。クエンティン・タランティーノらが、ウー監督ハリウッド誘致へのきっかけとなった作品。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Naked Lunch", "裸のランチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　1953年、ニューヨーク。害虫駆除員をしているウィリアムは謝って妻を射殺し、その後、麻薬の力に導かれるようにして“インターゾーン”へ逃げ込む。彼は不可思議な眩惑の町で次第に自分を見失い、得体の知れない陰謀に取り巻かれていく……。映画化不可能と言われた小説を、原作者ウィリアム・S・バロウズの半生の物語を織り込みながら脚色。幻想的なビジュアルを駆使し、小説家の内的世界を描き出す。", "1991-01-01", "10,3,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野獣の青春", "野獣の青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "白黒画面の中、コップに差された一輪の花だけが赤く浮かび上がっている。清順美学の真の誕生と、当時の映画ファンを躍り上がらせた作品で、この冒頭シーンから鈴木清順の特異な色彩感覚がきわ立ち、最後まで息もつかせぬドラマ（反ドラマ？）運びになっている。暴力団つぶしと殺人犯を追う元刑事の活躍を描いた大藪春彦の『人狩り』が原作で、ガラスを使った室内セット効果、あるいは映画館の裏にある暴力団事務所など、清順世界がたっぷりと楽しめる。“きわめて耽美的な暴力映画（石上三登志）”“日本映画史上もっともアメリカン・ハードボイルドに近づいた作品（小林信彦）”と絶賛されている。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼくらの七日間戦争２", "ぼくらの七日間戦争２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "大人と中学生との闘争をコミカルに描いた「ぼくらの七日間戦争」の第２作。今回は舞台を沖縄に移し、バカンスにやって来た悪ガキたちと、それを追って来た教師。さらに美しい無人島を破壊しようとする開発業者までもが入り交じって、豊かな自然を背景に彼らの闘いが繰り広げられる。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INDIANA JONES AND THE LAST CRUSADE", "インディ・ジョーンズ 最後の聖戦", "", "", "http://ecx.images-amazon.com/images/I/51mWkXeCBdL._SL210_.jpg", 127, "ハリウッド最高のヒットメーカー、G・ルーカスとS・スピルバーグが手掛けた人気シリーズ第３作。今回はナチス・ドイツが猛威を振るう1938年をバックに、キリストが最後の晩餐で使ったという“聖杯”をめぐって、我らがインディがナチス軍を相手に大活躍。1912年ユタ州。砂漠の真っ直中にある洞窟で、悪党３人組が、スペインの宝物を盗掘。それを偶然目撃した少年インディは、宝物の中にあった十字架をコッソリ奪って家に逃げ帰る。26年後。考古学教授で無類の冒険家インディは、何者かに命を狙われる。原因は、例の十字架。同じ頃、行方不明の父親から、“聖杯日誌”が送られてくる。父と聖杯の行方を追って、インディはベニスへと飛んだ……。インディの父親役でS・コネリーが共演。反目しながらも深い絆で結ばれる父子の情愛を、リアルにコミカルに妙演し、新しい味わいを添えている。父子を色じかけで罠にかける美人博士を新星A・ドゥーディが好演。少年インディをR・フェニックスが演じているのも見逃せない楽しみの一つだ。DVDは「アドベンチャーズ・オブ・インディ・ジョーンズ コンプリートDVDコレクション」に収録。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮 Love in Palace THE MOVIE 第3章", "宮 Love in Palace THE MOVIE 第3章", "", "", "http://image.eiga.k-img.com/images/movie/55619/315x210.jpg?1988", 0, "現代の韓国に王室が存在しているという設定で大ヒットした少女漫画が原作のTVドラマを、劇場版3部作として再編集した第3作。主演のユン・ウネ、チュ・ジフン、John-Hoonら若手人気俳優が多数出演した。皇太子の従兄弟ユルは皇太子妃チェギンに愛の告白をするが、チェギュンの気持ちは皇太子シンへと向かっていた。シンもチェギュンを愛し始めていたが、元恋人ヒョリンが学校で自殺未遂を図ったことで2人の関係は気まずくなる。一方、ユルとチェギョンは不倫疑惑をスクープされてしまう。", "2010-07-31", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢の女", "夢の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "お浪は借金のために娘を里子に出して洲崎遊廓に身を売った女。花魁として売れてきた頃、お浪は娘が里子先でひどい仕打ちを受けていると耳にして、子供を引き取ろうと決心する。お金を稼ぐため、身を粉にして働き始めたお浪にひいきの客は愛想をつかしてしまう……。玉三郎が描くきらびやかな女の情念の世界。女性の美を追求した映像に加え、お浪の世話をやく女郎屋の下働き・お松に扮した樹木希林の好演が光る。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE BONHEUR", "幸福〈しあわせ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "女流監督A・ヴァルダが夫婦間の愛情を美しくも残酷に洞察した秀作。日曜日に一家でピクニックに行く幸福な家庭に、一人の若く美しい女性が介入したことで波紋が生じる……。ソフト・フォーカスの画面の中に、モーツァルトの音楽が効果的に取り入れられている。", "1965-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEET BIRD OF YOUTH", "渇いた太陽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "劇作家T・ウィリアムズの『青春の甘い鳥』の映画版は、同じ作者の原作もの「熱いトタン屋根の猫」でタッグを組んだブルックス監督・主演男優ニューマンの再登板映画となった。善と悪の中間の半分アウトロー的な役が上手いニューマンのキャラクターが光る。かつてブロードウェイで有名だったが今は落ちぶれた青年は、今度は元大女優を利用して栄光を取り戻そうと狙う。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが道", "わが道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "佐藤不器、風見透編著による『ある告発—出稼ぎ裁判の記録』をもとに、新藤兼人監督が映画化した社会派ドラマ。青森出身の出稼ぎ労働者・川村芳造が東京で行き倒れ、身元不明人として勝手に医大の解剖実験材料として扱われる……。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VISIONS OF LIGHT：THE ART OF CINEMATOGRAPHY", "ビジョンズ・オブ・ライト 光の魔術師たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アメリカ撮影協会の監修の下、気鋭の撮影監督が次々に登場。ハリウッド映画の歴史がいかに見事にフィルムに光を定着してきたか、ビリー・ビッツァー、スタンリー・コルテス、グレッグ・トーランドら、優れた先達の技術的達成の驚異性を具体的におさらいしながら、現在に至る最先端の映像成果を検証していく。第一線の撮影監督のインタビュー集『マスターズ・オブ・ライト』を発展させた映像版として一気呵成に見せる。", "1992-01-01", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("番町皿屋敷・お菊と播磨", "番町皿屋敷・お菊と播磨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "岡本綺堂の戯曲『番町皿屋敷』を伊藤大輔が映画化。旗本の名門・青小播磨と愛人・お菊の愛と裏切りの物語が、劇的な見せ場たっぷりに描かれる。主演の長谷川・津島コンビはまぶしいほどの美しさ。クライマックスの屋敷をのんでいく、炎の中での抱擁シーンが圧巻。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACRI", "ACRI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「河童」（1994）に続く、カールスモーキー石井こと石井竜也の監督第２作。オーストラリアのカールセン海洋大学の佐古田教授は、人間は元は人魚だったという学説を立てていた。その学説を信じてオーストラリアにやって来たジャーナリスト羽岡は、ある青年が体験した不思議な出来事の謎を探っていた。その海原青年はかつて恋人と一緒に海で事故に遭い、自分だけが助かって恋人の阿久里は行方不明になった。その時海原は海の中である体験をしたのだが、それ以来、彼は記憶を失っていた。ファンタジー色をさらに強めた石井監督の独壇場。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("藩金蓮之前世今世／THE REINCARNNATION OF THE GOLDEN LOTUS", "ジョイ・ウォンの　リインカネーション—輪廻転生—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "中国古典『水滸伝』に登場する悪女、藩金蓮が成仏できずに現代によみがえり、自分を殺した男の生まれ変わりの男と恋に落ちる運命の物語。苛酷な運命を背負った美女には、悲恋や霊界といえばハマリ役のJ・ウォンが扮する。「あの愛をもういちど」のC・ローがファンタスティックなラブ・ストーリーに仕上げている。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beat the Devil", "悪魔をやっつけろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "　地中海のある港で船の修理を待つ一行がいた。アメリカ人ビリーとその妻、そしてビリーの仲間たちとイギリス人ハリー夫妻。ビリーたちはアフリカにあるウラニュウム鉱を目当てにし、一攫千金を夢見る。ところがやがて出航した船が故障。一行はボートで脱出するが、その後も予期せぬ災難が続き……。", "1953-01-01", "4,1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("出嫁女／FIVE GIRLS TO BE MARRIED", "五人少女天国行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "女は嫁入り前の人生の最も美しい刹那に自害すると、白い鳩になり天国へ飛んでいける、という中国の古い伝説に沿って、純粋無垢な少女たちが死を決意し遂げていくまでを描いた、今までの中国映画にはなかったファンタジー・ロマン。５人の少女たちの美しいきらめきが、映画に奥行きを与えている。", "1991-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КОСМИЧЕСКИЙ РЕЙС", "宇宙飛行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "製作の'35年より11年後の近未来に設定した月旅行SF映画。リアリティを出すため、宇宙飛行学者の権威であるコンスタンチン・ツィオルコフスキーを顧問に招いて作り上げた。'46年のソ連飛行研究所の博士が月旅行を計画していた。一方、有人飛行の反対派の教授が動物を乗せたロケットを発射したが、それが行方不明になってしまった。それでも博士たちはロケットに乗る……。", "1935-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ПОЛИГОН／PORIGON", "ポリゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "1949年８月以来、300回以上も原・水爆の実験場に使われた、カザフ共和国のセミパラチンスクにスポットをあてたドキュメンタリー。第１部と２部に分かれ、１部では核実験の放射能が人間の体に与えた影響を、２部では、核実験停止を唱える、故サハロフ博士らへのインタビューを収めている。", "1990-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SA　SON　I　HIMMELEN", "歓びを歌にのせて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "厳しい冬のスウェーデン北部を舞台にした感動のヒューマン・ドラマ。指揮者として大成功を収めた男が心臓を病み、田舎のコーラス隊の指導を通して新たな人生の喜びを見いだしていく。本国スウェーデンでは史上第3位の興行収入を叩き出す大ヒットとなり、アカデミー外国語映画賞候補にもなった。心身共に疲弊したダニエルは、故郷の小学校に移り住む。コーラス隊の指導を任された彼は、音楽の魅力を再発見し、彼らと友情を深めていく。", "2005-12-17", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Due Date", "デュー・デート　出産まであと5日！史上最悪のアメリカ横断", "", "", "http://image.eiga.k-img.com/images/movie/55399/315x210.jpg?4360", 0, "「ハングオーバー」のトッド・フィリップス監督が、主演にロバート・ダウニー・Jr.とザック・ガリフィアナキスを迎えて放つ爆笑ロードムービー。第一子の誕生を5日後に控えたピーター（ダウニー・Jr.）は、ロサンゼルス行きの飛行機をアトランタで待っていた。だが、ピーターは財布と荷物を盗まれ、飛行機にも乗れなくなってしまう。そこでピーターは空港で知り合った髭面の男イーサン（ガリフィアナキス）の誘いに乗り、車でロサンゼルスを目指すことにするが……。", "2011-01-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無頼無法の徒 さぶ", "無頼無法の徒 さぶ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "山本周五郎の同名小説を小林旭と長門裕之のコンビで映画化。敏捷で腕のたつ栄二と鈍重なさぶの二人は、ある日、10年前に知り合った女性・すえと再会した。すえは栄二を慕うが、人気者の彼に嫉妬してしまう。そんな折、綿文屋の家宝が紛失し、栄二が犯人として捕らえられてしまうのだが……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青い魚", "青い魚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "沖縄県那覇市のダウンタウンを舞台とした危ういラブ・ストーリー。美容院でアルバイトしている18歳の涼子が、マフィア絡みのトラブルに巻き込まれた和也という謎めいた青年と出会い、穏やかな日常から足を踏み外していく。人気モデル、大内まりが透明感あふれる魅力で映画デビュー。監督の中川陽介が、生活感あふれる沖縄独特のムードを刻み込んでいる。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さすらいのトランペット", "さすらいのトランペット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「嵐を呼ぶ男」のトランペット版といった感じの青春サクセス・ストーリー。トランペッター、明島秀夫は、敏腕プロモーター、矢崎竜二の力でスターになるが、作られた人気に嫌気がさし、一時トランペットを捨てる。北国で修行をする彼は、村祭りの太鼓を聞いて新たな音楽に目覚め、単身再起した。しかし、落ち目の矢崎は彼を逆恨みする。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOLDY III", "ゴールディとゆかいな仲間たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "おてんば娘のジェシーと熊のゴールディは大の仲良し。ゴールディはいろいろなサーカス芸を見せてくれる芸達者で、そんなゴールディをマジシャンのボージアは自分の手に入れようと狙っていた。ボージアの魔の手がいよいよ間近に迫ったとき、大自然のなかへとゴールディを連れて逃げ込んだジェシー。自然の様々な動物たちに助けられながら、ジェシーとゴールディの大冒険が始まる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("E´TOILE SANS LUMIE｀RE", "光なき星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "天才歌手E・ピアフと当時の若き恋人Y・モンタン。この魅力的な二人が顔を合わせた幻の名画。長年未公開だったが、1996年に初公開された。晩年には洒脱かつ重厚な風格を増したモンタンがここでは青臭いほどみずみずしく、またピアフの演技もほとんどお目にかかったことのないものだけに貴重だ。もちろん歌もたっぷり聴かせてくれる。", "1945-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beyond the Sea", "ビヨンド the シー　夢見るように歌えば", "", "", "http://image.eiga.k-img.com/images/movie/1289/315x210.jpg?4542", 118, "「アメリカン・ビューティー」「ユージュアル・サスペクツ」のオスカー俳優ケビン・スペイシーの「アルビノ・アリゲイター」に続く監督第2作で、今回は主演も兼任。50年代から60年代に活躍し37歳で夭折した実在のエンターテイナー、ボビー・ダーリンの生涯を音楽満載で描く。彼が結婚した人気女優サンドラ・ディー役は「ブルークラッシュ」のケイト・ボズワース。他にジョン・グッドマン、ブレンダ・ブレッシンら芸達者が共演。", "2005-02-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛と死の記録", "愛と死の記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "吉永小百合がレコード店で働く娘、渡哲也が将来性を嘱望された青年を演じた作品。二人は恋に落ちるが、渡が広島の原爆被爆者で、彼の死によって悲恋に終わる。小百合の「愛と死をみつめて」の逆パターンだが、オートバイに乗って雨の中を疾走するデートの場面などに蔵原監督の鮮烈なタッチがみられ、凡百の純愛映画とはひと味違う作品になっている。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADA　APA　DENGAN　CINTA？", "ビューティフル・デイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "インドネシアで大ヒットしたさわやかな青春ストーリー。初めての恋にときめき、友情との間で揺れ動く女子高生の姿を描き出す。軽快なテンポのエネルギッシュな映像に、思春期の輝きや痛み、繊細な心の機微をストレートに活写。インドネシアのバンドン映画祭で作品賞をはじめ8部門を受賞した。17歳のチンタは、趣味の作詩を通して大人びた少年ランガと恋に落ちる。だが、そのために親友4人との仲が気まずくなっていき……。", "2005-03-05", "73", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("和平飯店／CHOW YUN FAT IN PEACE HOTEL", "大陸英雄伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「狼たちの挽歌」のチョウ・ユンファ、ジョン・ウーのコンビが３年ぶりに復活。ユンファの推薦で、TV出身のワイ・カーファイ監督が初めてスクリーンのメガホンを執った。舞台は1920年代の上海。最大のギャング団をたった一人で壊滅し伝説となった男・ピンは、血生臭い世界からは身を引き、やむにやまれず事件を起こした者たちのために治外法権的な場所を作ろうとしていた。そのホテル“和平飯店”には噂を聞きつけ、連日、裏の事情を持つ男たちが逃げ込む。しかしある日、マンと名乗る女を受け入れたことから、彼女を追うギャングのボス、ムンとの間に危険な空気が流れ始める……。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("危険旅行", "危険旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "マスコミの女王と呼ばれる才女が、反逆を起こして突如旅に出る。旅の途中、写真家の男と知り合って、やがて二人の間にはロマンスが芽ばえ……。高橋貞二と有馬稲子が息の合ったところをみせる。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLEASE　TEACH　ME　ENGLISH", "英語完全征服", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "英会話学校を舞台にした韓国発のラブ・コメディ。極度の妄想癖を持つ地方公務員の女の子が、英会話教室で出会ったお調子者の青年にひと目惚れしたことから、英語完全マスターに燃えるというお話だ。誰もが習得したいと願いながらも、つい挫折してしまいがちな“英会話”をモチーフに、「MUSA−武士−」のキム・ソンス監督が遊び心溢れるポップな映像感覚を披露。キュートなメガネっ子のヒロインに扮した若手女優イ・ナヨンと、「僕の彼女を紹介します」のチャン・ヒョクの顔合わせも楽しい。思い込みの激しい女の子ヨンジュが、職場を代表して英会話学校に通うはめに。そこで彼女はデパート店員ムンスにひと目惚れ。ところがムンスはブロンドの美人教師にご執心で……。", "2005-04-16", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FASTER， PUSSY CAT！ KILL！ KILL！", "ファスター・プシィキャット！キル！キル！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "マーティン・スコセッシ、ジョン・ウォーターズらの映画作家に多大な影響を与えた、カルト中のカルト、R・メイヤーの代表作。ゴーゴー・クラブのダンサー、バーラ、ロージー、ビリーは、ドライブの途中で出会ったレーサーとケンカになり、背骨を折って殺してしまう。レーサーの恋人をムリヤリ道づれにした３人は、砂漠の真ん中に住む大金持ちの老人の財産を目当てに、色仕掛けの悪だくみをもくろむが……。主役３人の下品でダイナミックな肉体の躍動を、大胆なカメラワークで画面にはじけさせるその迫力は、今見ても新鮮でエキサイティングだ。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緑玉紳士", "緑玉紳士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "新進気鋭のクリエイター、栗田やすおが、たった一人で制作したパペット・アニメーション。露天商をしながら放浪の旅を続ける老舗メガネ屋の跡取息子の冒険を、４年半の歳月をかけた精巧なセットで映像化。とぼけた味わいのあるキャラクターたちが繰り広げるアクションも見もの。老舗メガネ屋“眼鏡紳士”の跡取息子グリーンピースは家出をする。メガネの露店商をしながら、大都会“テーブルスクウェア”へ向かうが……。", "2005-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREAT CARUSO", "歌劇王カルーソ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "イタリアオペラ界が生んだ最高のテナー、カルーソの伝記映画。主役を演じるランザもテノール歌手として名声を博した。全編に『アイーダ』『トスカ』『リゴレット』など27曲の歌が流れ、華々しい生涯を描いている。恋人役、ブライスの可憐な美貌が光る。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドーベルマン刑事", "ドーベルマン刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "深作欣二監督、千葉真一主演によるアクション映画。沖縄石垣島の刑事が、殺された家出娘の遺骨を受け取るために新宿にやって来て、芸能界のスキャンダルに巻き込まれる。人気劇画の原作を深作欣二は持て余し気味だが、千葉真一のアクションは衰えを知らない。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("だからワタシを座らせて。　通勤電車で座る技術！", "だからワタシを座らせて。　通勤電車で座る技術！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ネット上で大反響を呼び書籍化されたハウツー本「通勤電車で座る技術！」を映画化したラブコメディ。一流企業の秘書課に勤める貴子は、ひょんなことから同じ会社で働く大輔と出会う。「過敏性腸症候群」である大輔が電車に乗るだけで便意を催してしまうことを知った貴子は、大輔に協力して通勤電車で座る方法を探すことに。やがて、貴子は大輔にほのかな恋心を抱くようになり……。主演はハリウッドでも活躍する元アイドルの田村英里子。", "2006-10-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いちばんきれいな水", "いちばんきれいな水", "", "", "http://image.eiga.k-img.com/images/movie/33679/315x210.jpg?4671", 90, "8歳の頃から11年間眠り続け突然目を覚ました姉と、12歳の妹が過ごす奇跡の3日間を描いた感動作。古屋兎丸の同名コミックを原作に、これまでサザンオールスターズや大塚愛ら多数の人気アーティストのミュージック・ビデオを手掛けてきたウスイヒロシが初メガホンを取った。8歳の心を持つ無邪気な姉を、映画初主演の加藤ローサが、姉に振り回されながらも成長していく妹を「仄暗い水の底から」の菅野莉央がいきいきと演じる。", "2006-10-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヤッターマン", "ヤッターマン", "", "", "http://image.eiga.k-img.com/images/movie/53119/315x210.jpg?3181", 111, "1970年代のタツノコプロ製作の同名人気アニメを、「クローズZERO」「スキヤキ・ウエスタン　ジャンゴ」の三池崇史監督が実写映画化。タイトルロールのヤッターマン1号に櫻井翔、2号に福田沙紀。ライバルのドロンジョに深田恭子。4つ揃うとなんでも願いが叶うというドクロストーンめぐる、ヤッターマンとドロンジョ率いるドロンボー一味との戦いを描く。", "2009-03-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAT MAN AND LITTLE BOY", "シャドー・メーカーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "「キリング・フィールド」などの社会派監督、R・ジョフィが、原爆問題に取り組んだ衝撃作。1945年８月、広島と長崎に投下された原爆を“作った人々”の立場から描く。作らせた軍人の論理、研究に参加した科学者の論理、その家族たちや周囲の人々の思いなど、様々な思惑の絡み合いを追ったドラマ。原爆製造の命令を受けたグローブス将軍にP・ニューマン、J・キューザックの好演が光る。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゆれる", "ゆれる", "", "", "http://image.eiga.k-img.com/images/movie/1759/315x210.jpg?1867", 119, "「蛇イチゴ」（03）でデビューを飾った西川美和監督の第2作。東京で写真家として気ままに暮らす猛（オダギリジョー）が、母親の一周忌で久しぶりに帰郷。猛は家業を継いだ兄の稔（香川照之）と幼なじみの智恵子とともに近くの渓谷へ行くが、智恵子が吊り橋から転落してしまう。智恵子の近くにいた稔が逮捕され裁判となるが、そこで猛は今まで見たことのない兄の姿を目の当たりにする……。", "2006-07-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MINKY MOMO IN 旅だちの駅", "MINKY MOMO IN 旅だちの駅", "", "", "http://image.eiga.k-img.com/images/movie/56819/315x210.jpg?4751", 30, "「旅立ちの汽笛を鳴らす」というライオン像がある中央駅で偶然再会したモモとフェナリナーサのモモは、戦火にさらされた50年前の過去へタイムスリップする。そこで、戦災孤児でスリをしながら生きている少女と出会い、魔法の力で旅立たせてくれと願いを聞かされたモモだったが……。1994年に製作された「魔法のプリンセス　ミンキーモモ」のOVA作品。82年のTVシリーズ初放送から30周年目前を記念して、過去のOVA作品を映画館で上映する「魔法のプリンセス　ミンキーモモ PARTY」にて劇場初上映（「MINKY MOMO IN 夢にかける橋」との2本立て）。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("テコンV", "テコンV", "", "", "http://image.eiga.k-img.com/images/movie/55549/315x210.jpg?2316", 80, "1976年に韓国で公開され、大ヒットを記録した同国初の本格ロボットアニメ。世界大会で優勝するほどの実力をもつ若きテコンドーの使い手・フンは、ロボット工学の権威だった父キムを殺され、その意思を継いで巨大ロボット「テコンV」のパイロットとなる。副操縦士をつとめるガールフレンドのヨンヒ、フンを慕う少年チョリらと力を合わせ、世界征服をたくらむ謎の組織・アカ帝国と激闘を繰り広げる。", "2010-08-07", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAN TROUBLE", "お気にめすまま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "世間を騒がす切り裂き魔に脅える、美貌のソプラノ歌手ジョーン。彼女のSOSに応えるためにやって来たのは貸し犬業者のハリー。リッチな美女と冴えない中年男は、頼り頼られ恋に落ちるが……。E・バーキンとJ・ニコルソンの組み合わせが愉快でポップなラブ・コメディ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("惨劇館　ブラインド", "惨劇館　ブラインド", "", "", "http://image.eiga.k-img.com/images/movie/57089/315x210.jpg?3770", 60, "ホラーコミック界を代表する古賀新一、伊藤潤二、御茶漬海苔の3人が、各々の代表作を自らのメガホンで実写化した「古潤茶」プロジェクトの第3弾。「惨劇館」の御茶漬海苔が原作・脚本・監督を務め、自室のブラインドのすき間から殺人を目撃してしまった少女・桜に迫る恐怖を描いたサイコサスペンス。主演は「炎神戦隊ゴーオンジャー」「高校デビュー」などで活躍する逢沢りな。", "2011-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANIAC COP", "マニアック・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "突然N.Y.に現れた残虐非道の殺人警官。人間離れしたパワーで殺りくを繰り返す恐怖のコップ。市民は恐怖のどん底に陥り、無関係の警官まで射殺されてしまう。容疑者にされてしまった警官は恋人と真相を追及する。超ド迫力のホラー・バイオレンス。パリ国際ファンタスティックSF映画祭最優秀監督賞を受賞。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浪曲映画・新佐渡情話", "浪曲映画・新佐渡情話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "佐渡の港で、行き倒れ同然に拾われたお梅。拾った伊作は、お梅がふしだらな妹・お由の娘であることを知り、彼女を立派に育ててやろうと決心するが……。伊藤大輔の指導下でシナリオ技法を磨き上げた清瀬英次郎の浪曲トーキー。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT☆KING ナイトキング", "NIGHT☆KING ナイトキング", "", "", "http://image.eiga.k-img.com/images/movie/54239/315x210.jpg?4288", 0, "歌舞伎町にホストクラブ“愛”をオープンし、ホスト界のドンとなった愛田武をモデルにした漫画「破天荒」（原作：倉科遼）を映画化。「泪壺」「鎧　サムライゾンビ」のいしだ壱成が主演。高度成長期の東京に新潟から上京してきた武は、しがないセールスマンを続けていた。そんな折、叔父からホストの仕事を紹介され軽い気持ちでホストになった武だったが、そこは想像を超える厳しい世界だった。", "2009-01-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUNNY　GAMES", "ファニーゲーム", "", "", "http://image.eiga.k-img.com/images/movie/51409/315x210.jpg?2368", 103, "夏のバカンスを過ごすため、湖畔の別荘にやってきた平凡な親子三人と一匹の犬。そんな彼らの前に謎めいた二人組の若者が現れ、理不尽な死のゲームへの参加を強要してくる。M・ハネケ監督が暴力というテーマを、観客に対する確信犯的な挑発をこめて突き詰めた異色のサスペンス映画。何の理由もなく殺人を繰り返す若者たちと、その獲物にされた一家が織り成す一昼夜の出来事を、異様な緊迫感がみなぎる長回しの映像で語り明かす。カンヌ映画祭のコンペティション部門では“ショッキングな場面あり”との警告付きで上映され、賛否両論を呼び起こした。", "1997-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャズ娘乾杯", "ジャズ娘乾杯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "曲芸師タニマンの３人の娘は歌と踊りが大好きでレビュー女優志願。父と末娘が旅興行に出ている間に二人の娘は映画のエキストラの仕事をもらい、父を安心させるためにニュー・スターになったと知らせる。バック・ステージもののコメディ的サクセス・ストーリー。雪村いづみと江利チエミの初共演作。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANNA　KARENINA", "アンナ・カレーニナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「不滅の恋　ベートーヴェン」のB・ローズ監督が、トルストイの有名な悲恋小説に挑戦。文学史上に名高いヒロインにフランスの人気女優S・マルソーが扮し、青年将校との宿命的な恋をドラマティックに体現する。チャイコフスキーの『交響曲第６番悲愴』をテーマ曲とする音楽指揮を、名指揮者ショルティが担当。大がかりなロシア・ロケでロマノフ王朝時代の貴族社会を再現した映像と相まって、壮麗なムードを高めている。", "1997-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THIN PINK LINE", "ピンク・ピンク・ライン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "死刑囚を追いかける撮影隊の悪戦苦闘を、オフビート調で描いたコメディ。殺人罪で死刑を宣告された男チャンシーの無実を証明するため、ドキュメンタリー映画を撮ることになった撮影隊。ところが、取材をすればするほどチャンシーの“クロ”の可能性が強くなり、おまけに彼のゲイ疑惑も持ち上がって撮影は混乱する一方。やがて死刑執行の当日、映画は何とか完成するが……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カリスマ", "カリスマ", "", "", "http://image.eiga.k-img.com/images/movie/1859/315x210.jpg?2970", 104, "代議士を人質にたてこもる青年から謎のメッセージを受け取った刑事・薮池。犯人も人質も両方死なせるという結果 に終わったこの事件に関する責任問題で、薮池は身を潜めることになる。そして、彼は導かれるようにしてその森にやってきた。森にはひょろりと伸びる1本の“木”があった。実はこの“木”を囲む森では、植林した杉が萎え、根こそぎ倒れていくという奇怪な現象が起こり始めていた。", "2000-02-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブラック会社に勤めてるんだが、もう俺は限界かもしれない", "ブラック会社に勤めてるんだが、もう俺は限界かもしれない", "", "", "http://image.eiga.k-img.com/images/movie/54569/315x210.jpg?3210", 104, "2ちゃんねるの書き込みから生まれた黒井勇人のスレッド文学を、「キサラギ」の佐藤祐市が映画化。主演は小池徹平。高校中退のニート・マ男は、母の死をきっかけにプログラマーとなり、何とか小さなIT企業に就職する。しかし入社してみると、そこはサービス残業や徹夜は当たり前で、過酷な労働を社員に強いる「ブラック会社」だった。変わり者の同僚に囲まれ、徐々に成長していくマ男をコミカルに描く。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・社長学ABC", "続・社長学ABC", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "大日食品の新社長・丹波久はヤングパワーの起用をはかって大ハッスル。一方、会長とは名ばかりで仕事のない網野前社長。しかし福山への出張を命じられると、お得意の浮気の虫がまたまた動き出して……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天安艦沈没", "天安艦沈没", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "2010年3月26日、ペンリョン島近海で韓国海軍哨戒艦「天安艦」が沈没し、46人の兵士が命を落とした。事件翌日、韓国政府が「北朝鮮の魚雷攻撃による撃沈」と発表したことから南北間の軍事的緊張は一気に高まったが、韓国政府が提出した証拠のどれもが不可解で多くの疑惑が残された。韓国のキム・ドギュン監督が、いまだ解決していない同事件にまつわる様々な謎を検証し、その真相に迫るドキュメンタリー。", "2011-11-05", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCORPION", "スコルピオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "DIA（防衛情報機関）の諜報員でスコルピオンと呼ばれる主人公が、国際テロリストの一味と世界各地を舞台に闘うアクション映画。主演のタレナースは元カラテ・チャンピオン。監督のウィリアム・リードは“メイキングもの”を数多く作ってきた人で、これが初監督作。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サマー・ソルジャー", "サマー・ソルジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「砂の女」をはじめ、安部公房とのコンビで知られる勅使河原宏が、安部と離れて初めて手掛けた劇映画。米軍基地の街・岩国を舞台に、ベトナム戦争の悲惨な体験から軍に戻ることを拒否した脱走兵の、逃亡の日々を描く。脚本のネースンは著名な日本文学研究者。勅使河原は以後劇映画から離れていたが、1989年に「利休」を発表した。DVDは「勅使河原宏の世界DVDコレクション」に収録。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("April Snow", "四月の雪", "", "", "http://image.eiga.k-img.com/images/movie/1009/315x210.jpg?3676", 107, "照明監督のインスは、妻の交通事故の知らせを聞き、病院へ駆けつけた。そこにいたのは見知らぬ女性、ソヨン。実は、インスの妻とソヨンの夫が、不倫旅行の最中に事故を起こしていたのだ。複雑な想いでお互いの伴侶の看病をする2人。そして、いつしか同じ苦しみや悲しみを持つ者同士惹かれあっていくのだった……。「八月のクリスマス」「春の日は過ぎゆく」のホ・ジノ監督がペ・ヨンジュンを主演に迎えて送る切ないラブ・ストーリー。", "2005-09-17", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APRILI", "四月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "発表当時、不当にも上映禁止となったが、2000年のカンヌ国際映画祭で復刻版が上映され世界中の喝采を浴びたO・イオセリアーニ監督のデビュー作。音楽と映像だけで綴られるほとんどサイレント映画の趣ながら、時折、翻訳不能のセリフが発せられる遊び心に満ちた作品。物質的な豊かさを求めてケンカを繰り返す若い夫婦の悲劇編から、その後二人が愛が全てだと悟る喜劇編へと変わる構成。", "1962-01-01", "68", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鞍馬天狗 龍驤虎搏", "鞍馬天狗 龍驤虎搏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "幕末の京都の街でお札踊りという不思議な乱舞がはやり始めた頃、勤皇の志士たちを次から次へと暗殺する山嶽党の暗躍が続いた。鞍馬天狗は志士たちのために奮然と立ち上がる。嵐寛寿郎と月形龍之介というニ大スターの顔合わせが見もの。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　IDOLES", "アイドルたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "名女優ビュル・オジエが主演し、「ママと娼婦」のジャン・ユスターシュが編集を手がけたことでも伝説として語り継がれてきた、'68年製作のフレンチ・カルト・ムービー。3人組のアイドル・ユニットの虚像が、記者会見の場ではぎとられていくという内容で、現代にも通じるテーマを含んでいる。60年代のパリ。とあるライブ会場で、狂乱ジジと短刀のチャーリー、魔術師シモンによる3人組ユニットの結成がお披露目となり、観客を魅了するのだが……。", "2005-10-29", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOURS，MINE AND OURS", "合併結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "北カリフォルニアのある一家の実生活をもとにした、“ルーシー・ショー”でおなじみのボール主演のホーム・コメディ。夫を失い、残された８人の子供たちと新しい生活を始めるためカリフォルニアにやって来た女と、妻に先立たれ、これまた10人の子供の面倒を見なければならなくなった男。ひょんなことから出会った二人は、互いに子供のことを言い出せずにいた……。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANJIN イングリッシュサムライ", "ANJIN イングリッシュサムライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 184, "徳川家康に仕えたイギリス人、ウィリアム・アダムズ＝日本名：三浦按針（みうらあんじん）の激動の生涯を描いた舞台を映画館のスクリーンで上映。関が原の合戦からさかのぼること半年、オランダ船リーフデ号のイギリス人航海士アダムズは、苦難の末に豊後国にたどりつき、やがて家康を天下人に導く存在となっていく。主演はトニー賞受賞歴のある英国人俳優オーウェン・ティール。家康役に市村正親、アダムズと家康をとりもつ日本人宣教師役に藤原竜也。", "2010-06-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("One Train Later", "ワン・トレイン・レイター（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ABOMINABLE SNOWMAN", "恐怖の雪男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "雪山の雄大な風景をバックに、自然の恐ろしさと、雪男の恐怖を描くホラー映画。なかでも宗教的な匂いのする宮殿に住む、執事長役のA・チンの演技が、恐怖のムードを盛りあげている。ヒマラヤの村に住むジョンとヘレン夫妻のもとへ、友人の登山家たちがやってきた。ジョンと彼らは、雪男がいるという伝説がある山に向かう。そして、ある闇の夜、テントのそばで雪男らしき人影を発見する。", "1957-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAY", "ステイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "冒頭の交通事故シーンから意外な真実が明かされる結末まで、見る者を翻弄し続けるサスペンス劇。ユアン・マクレガー扮する精神科医に、不気味な言葉を残して失踪を遂げる患者を演じたライアン・ゴズリングの存在感に注目。ヘンリーという心を病んだ青年が、自殺することを予告して姿を消した。彼の行方を追う精神科医サムは、次々と奇怪な現象に見舞われ、現実と幻覚の区別がつかない迷宮に陥っていく。", "2006-06-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEVIL’S RAIN", "魔鬼雨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「猿の惑星」のスタッフによる特殊メイクが話題を集めたオカルト映画。平和なプレストン家を突如襲った邪教の集団に対し、両親を奪われた息子のマークは拳銃と十字架を盾に、単身敵地に乗り込んでいく……。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("網走番外地・決斗零下30度", "網走番外地・決斗零下30度", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "サガレン炭鉱をタコ部屋同然にして悪どくもうけている支配人・関野のやり口に、怒った橘真一は、網走刑務所のムショ仲間とともに関野を叩きつぶす。レギュラー陣が総出演するが、マンネリの感はぬぐえず、少々かげりが見えてきた。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOAN OF ARC", "ジャンヌ・ダーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "祖国フランスの危機を救ったにもかかわらず、国王や貴族たちの反感をかい、神への信仰を棄てなかったために教会から焚刑にされた聖女ジャンヌ・ダーク。その悲劇の生涯を当時としては破格の製作費460万ドル、出演者4000名で映画化したもの。バーグマンが憧れの役に挑戦。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACK PIRATE", "ダグラスの海賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "17世紀の地中海、海賊船に襲われ父を殺された若者は復讐を誓う。自らも海賊の一員となり、ブラック・パイレートと呼ばれ一目置かれるようになった彼だが、正義の心は失っておらず、卑怯な手段で姫を人質にした仲間に戦いを挑む……。アクション映画の元祖ともいえるフェアバンクス主演の痛快大活劇。", "1926-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長短脚之戀／FRACTURED FOLLIES", "パラダイス・パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "香港のスーパースター、チョウ・ユンファ主演の人情喜劇。世界中で大ヒットを記録した「チャイニーズ・ゴースト・ストーリー」の主演でトップ女優となったジョイ・ウォンを相手役に据え、しがないタクシー運転手と足の不自由な娘との恋が展開する。香港の市井の人々の生活が、ユーモラスに描かれて興味をそそる。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("至尊計状元才", "カジノ・シンジケート　組織犯罪覇権抗争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「ゴッド・ギャンブラー」シリーズのB・ウォンによるコメディ・タッチのアクション。世界屈指のギャンブラー、レイが、強豪のチュンとの対決のためマカオへ向かう。ところが、途中で立ち寄った香港で二人組の詐欺師に出くわし、予想外のトラブルに巻き込まれる。レイに成り済まして賭場場に繰り出した詐欺師たちとともに、彼は暗黒街の大物相手に一世一代の大勝負に挑むことになるが……。だまし、だまされの３人のコミカルなやり取りが笑いを誘う。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超劇場版ケロロ軍曹2 深海のプリンセスであります！", "超劇場版ケロロ軍曹2 深海のプリンセスであります！", "", "", "http://image.eiga.k-img.com/images/movie/33889/315x210.jpg?1547", 0, "カエル型宇宙人のケロロ軍曹とその部下たちが巻き起こす騒動を描き、子供から大人まで幅広い人気を誇るギャグアニメの劇場版第2弾。かつて地球侵略にやって来たマロン星人の末裔、メール王子と従者のマール。たった2人になってしまった国の再建を目論む彼らは、新たなプリンセス候補として、ケロロ軍曹が居候している日向家の長女、夏美を深海の王国に連れ去ってしまう。ケロロ軍曹たちは夏美救出大作戦を開始するが……。", "2007-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ先生", "やくざ先生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "石原裕次郎主演による学園もの。戦災孤児を収容する学園に裕次郎扮する補導員が赴任して来る。彼自身もこの学園出身の不良少年であった。彼は少年たちに素っ裸でぶつかっていくが、学園はジェット機基地建設のために取り壊される運命にあった。青春学園ドラマの先駆け的な一作。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE", "血と怒りの河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "幼い頃、両親がメキシコの革命家に殺され、盗賊の首領オルテガに育てられたアメリカ人のブルー。オルテガには４人の息子がいたが、ブルーもわが子同然に可愛がられた。だがある日、雑貨屋の店先で美しい娘ジョアンヌを見つけたオルテガの息子の一人は、彼女を犯そうとする。そこへ出くわしたブルーは、本能的に彼女を助け、息子を撃ち殺してしまう。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白銀城の対決", "白銀城の対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "伊庭俊介は、学生時代、スキー選手権に出場するために訪れたゲレンデに６年振りにやって来た。ロープウェイを建設するためだ。だが、そこにはかつてのライバル・達夫が、ロープウェイ反対派の馬方を引き連れ、待っていた……。白銀の世界を舞台に、石原裕次郎が活躍するアクション・ドラマ。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lethal Weapon", "リーサル・ウェポン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ドル箱コンビ、M・ギブソンとR・ドナー監督の超ハード・アクション映画。“リーサル・ウェポン（＝人間兵器）”と異名をとるロサンゼルス市警の殺人課刑事と相棒の黒人ベテラン刑事は、巨大な麻薬組織を壊滅せんと悪戦苦闘。興奮とバイオレンスみなぎる凄絶な“男の世界”が描かれる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子どもたちの夏　チェルノブイリと福島", "子どもたちの夏　チェルノブイリと福島", "", "", "http://image.eiga.k-img.com/images/movie/57479/315x210.jpg?3714", 75, "原子力発電所の事故に見舞われた旧ソ連・チェルノブイリと日本・福島の両地域に住み続ける母子の姿を追ったドキュメンタリー。チェルノブイリの事故発生時、子どもたちを救うため活動した女性議員や医師、教師たちが当時の惨状を語る一方で、これから母親になる若い世代や子どもたちが将来への前向きな気持ちを明かす。また、福島では行政の安全宣言に疑問を抱き我が子を守ろうと奔走する親たちや、そんな状況下でも無邪気に遊ぶ子どもたちの姿から未来への希望を見出していく。チェルノブイリ原発事故から25年の節目となる2012年4月にむけて製作されていたが、11年3月11日の東日本大震災による福島原発事故を受け、福島のパートが追加されて完成した。", "2011-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YEAR OF THE COMET", "イヤー・オブ・ザ・コメット　失われたワインを追え！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "スコットランドの古城に眠っていた幻のワイン、1811シャトー・ラフィット、通称“イヤー・オブ・ザ・コメット”を見つけ出したワイン・セラーのマーガレット。売り手も決まり、その代理人のオリヴァーとともにワインを持ち帰ろうとする。すべてが順調にいくかに見えたこの取引だが、古城で二人を待っていたのはワインと一人の男の死体だった……。幻のワインをめぐって繰り広げられる欲望と陰謀が渦巻く追跡劇を、名匠P・イエーツがスリリングに演出している。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I am Sam", "I am Sam アイ・アム・サム", "", "", "http://image.eiga.k-img.com/images/movie/1439/315x210.jpg?3394", 133, "7歳の知能しか持たないサムは、コーヒーショップで働きながら、ひとり娘ルーシーを育てるが、ソーシャル・ワーカーに娘を取り上げられ、敏腕女性弁護士に助けを求める。彼女は夫と息子との関係に悩んでいた。監督は「コリーナ、コリーナ」（監督・脚本）、「グッドナイト・ムーン」「ストーリー・オブ・ラブ」（脚本）と家族愛を描き続ける女流ジェシー・ネルソン。主人公の友人役で実際に知的障害を持つ人々も出演。", "2002-06-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHTMARES", "デビルゾーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "NBCのTVシリーズのパイロット版として製作された人気ホラー映画。「恐怖の錯覚殺人」「悪魔のビデオゲーム」「悪霊」「子を呼ぶ魂」の４話からなるオムニバス構成。舞台はいずれも現代で、日常的な環境に潜む恐怖を描いている。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("威龍猛探／THE PROTECTOR", "プロテクター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "チェンが初めて刑事役に挑んだ作品。新進デザイナー誘拐事件をきっかけに、香港とニューヨークの二大シンジケートに立ち向かう若き刑事の物語。ガン、ナイフ、電気ノコギリそしてカンフーと、アメリカ風活劇とオリエンタル・アクションを織り交ぜて暴れまわるチェンの活力に拍手。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG BULLY", "スクールウォーズ　もうイジメは懲りごり！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ミクロキッズ」のR・モラニスと「トゥルーライズ」のT・アーノルド、二人の喜劇俳優が共演したハートフル・コメディ。お互いに母校である小学校の教師となった、小学生時代のイジメっ子とイジメられっ子が30年ぶりに再会。それぞれの息子同士のイジメ問題をよそに繰り広げられる、彼らの大人げない対決の行方が、テンポ良く描かれる。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELLFIGHTERS", "ヘル ファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "テキサスに本社を持つ油田火災専門の消防会社に勤める、ベテラン消防隊員チャンス。仕事一筋の彼についていけずに妻は離婚を決意するが、ベネズエラでの革命騒ぎのさなかに起きた大油田火災現場での夫の活躍を見て、彼の仕事の偉大さがわかり……。J・ウェイン主演の珍しい現代アクションもの。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("（ハル）", "（ハル）", "", "", "http://ecx.images-amazon.com/images/I/2136GA5BM3L._SL210_.jpg", 118, "パソコン通信によって見知らぬ男女が出会い、恋が生まれるまでを描いたラブストーリー。（ハル）というネームでパソコン通信の映画フォーラムにアクセスを始めた速見昇は仕事も恋もうまくいかず鬱屈していた。そんな彼に（ほし）というネームで励ましのメールが届く。その日から、2人はメールを交換し始め、本音を伝え始めるようになる。そして（ハル）は会うことを提案するが……。スクリーンに映しだされたメール文字だけでなく、2人の距離感をみずみずしく描いた森田演出が際立つ。", "1996-03-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("СОЛЯРИС／SOLARIS", "惑星ソラリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "静かな池。水藻が揺れる水面をカメラがゆっくりと捉えていく。ここは21世紀の地球。世界中の科学者が宇宙の彼方の未知の惑星ソラリスに注目していた。惑星表面のプラズマ状の“海”が有機的頭脳を持つと推測されたが接触には失敗。心理学者クリス・ケルビンが調査に送り込まれる。だが軌道ステーションを支配していたのは静寂と狂気だった。ケルビン自身も10年前に自殺した妻ハリーの幻影をたびたび見る。ソラリスの海は、人間の潜在意識から記憶を引き出して実体化させ人間に働きかけていたのだ。やがて科学者たちは実験の後、ケルビンの意識をX線にして海に投射した。そこに現れたのは……。地球への架空の郷愁を息苦しいほど切々と描く、水と火と鏡と廃墟の映像詩人タルコフスキーの不思議なSFの世界。いわく“良心が突然物質化されて現れ、その〈所有者〉を苦しめる。私がレムの小説から読み取ったコンセプトは大体このようなものでした”。地上の未来都市シーンに東京の首都高速道路が出るのもお見逃しなく。", "1972-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雙瞳", "ダブル・ビジョン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "台湾版「セブン」ともいうべき超自然的なサイコ・スリラー。道教の“５つの地獄”になぞらえた猟奇事件を追う男たちの運命を描き、台湾でリピーター続出の大ヒットを記録した。台北の街で奇怪な連続変死事件が発生し、FBI捜査官のリクターが捜査協力することに。地元の刑事ホアンはリクターとコンビを組み、この一連の猟奇事件が道教の“５つの地獄”に合致することを突き止め、真相に迫っていく。", "2002-01-01", "6,53,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Suspiria", "サスペリア", "", "", "http://ecx.images-amazon.com/images/I/51AGoips6UL._SL210_.jpg", 98, "ホラー映画作家アルジェントの名を一躍高めた作品。アメリカの美少女スージーが、ドイツのバレエ学校に入った。が、そこは悪魔崇拝者たちのアジトで、彼女は連続殺人事件に遭遇する。夜ごと聞こえる靴音を数え、その通りに進んだスージーが見たものは……。隠しドアの向こうの部屋には、目に見えぬ魔女がいたのだ。雷でパッ、パッと浮かぶ魔女の輪郭。スージーは、力をふりしぼってナイフを突き刺す。ガラスの破片が顔面を割ったり、ナイフでメッタ刺しにしたり、痛々しい殺しの表現が恐ろしい。また、赤い部屋、赤い照明など、赤を基調にした映像もムードを高めている。惨劇の間に必ず流れる、ゴブリンの音楽も不気味だ。", "1977-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男前／日本一の画家になったる！", "男前／日本一の画家になったる！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ヤクザから画家へ転身した異色の経歴を持つ山本集の壮絶な人生を描いた任侠映画シリーズの第３作。ドスを絵筆に持ち変え、日本一の画家を目指して邁進する主人公の熱い生き様を映像化。Vシネマを中心に活躍中の注目俳優、風間貢が、前2作に続いて山本役を熱演し、主題歌もこなしている。昭和54年夏。京都の山科刑務所に服役中の山本は、刑務所の生活に耐えていた。2年後、山本は手厚い出迎えを受け、出所の日を迎える。", "2005-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moving Violations", "ドライブ・アカデミー 全員免停", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "免許停止者専用の自動車学校に入るハメになったトラック運転手の青年。生徒全員を不合格にしようとする鬼教官や女判事をめぐって、彼の身に次々と騒動が持ち上がる。全編ギャグとジョークの連発、抱腹絶倒のコメディだ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("將軍II", "英雄武闘伝説 将軍の息子II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "本国で記録的な大ヒットとなった大河ロマンの続編。日本の支配が強まる中、ヤクザから後に国会議員にまで登り詰めていく主人公キム・トゥハンの闘争をアクションを交えて描き出す。", "1991-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mars Needs Moms", "少年マイロの火星冒険記 3D", "", "", "http://image.eiga.k-img.com/images/movie/55929/315x210.jpg?4298", 89, "母親を何者かにさらわれた9歳の少年マイロは、ロケットで宇宙へと飛び立ち、高度な科学と文明を誇る火星にたどり着く。未知の土地で母親を探すマイロは、やがて火星に秘められたナゾを解き明かしていく。「バック・トゥ・ザ・フューチャー」のロバート・ゼメキスが製作、「タイムマシン」のサイモン・ウェルズが監督を務める3Dアニメーション。", "2011-04-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬夜叉／時代〈とき〉を越える想い", "犬夜叉／時代〈とき〉を越える想い", "", "", "http://ecx.images-amazon.com/images/I/216T25GX2DL._SL210_.jpg", 100, "TVシリーズも人気を博している、高橋留美子原作『犬夜叉』の初映画化。キャラクター総出演に加え、劇場用のオリジナルストーリーによって誕生した史上最強の敵キャラも登場。15歳の誕生日に戦国時代にタイムスリップしてしまった日暮かごめは、少年・犬夜叉と出会い、あらゆる野望を叶える妖力を持つといわれる“四魂の玉”をめぐり、邪悪な妖怪たちと戦う。", "2001-12-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("軽蔑　ディレクターズ・カット", "軽蔑　ディレクターズ・カット", "", "", "http://image.eiga.k-img.com/images/movie/57199/315x210.jpg?3366", 145, "芥川賞作家・中上健次の小説を、高良健吾と鈴木杏のダブル主演、廣木隆一監督で映画化した「軽蔑」のディレクターズ・カット版。鈴木扮する真知子がポールダンスでさらすヌードシーンや、高良扮するカズが蒼井そら演じる元恋人のかおりに誘惑されるシーンなど、劇場公開版にはなかった10分間を追加。ラストも劇場公開版とは異なるものになっている。", "2011-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIDE THE HIGH COUNTRY", "昼下りの決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ゴールドラッシュに沸くカリフォルニア。元保安官のスティーブは、かつての部下ジム、若いヘックと組んで、金を鉱山から町へ運ぶ仕事に就く。しかしジムとヘックが金を強奪しようとしたため、スティーブは二人の武器をとりあげてしまう。そこへ、同行していた若い娘を奪いとろうとする一味が襲いかかり……。男たちの裏切りと友情を、哀愁を込めて描いた西部劇。これが２作目のペキンパー監督は、老いが近づいた元保安官とガンマンを、スコットとマックリーという引退が近い二人の老スターに演じさせ、人生が終焉に近づくさまを、残酷なまでに描き出した。無名時代のW・オーツ、ジェームズ・ドルーリーらが出演。印象的なラスト・シーンも心に残る。日本公開時の原題は「Guns in the Afternoon」。スコットとマックリーはこの作品後、映画界を引退した。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT ON EARTH", "ナイト・オン・ザ・プラネット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "ロサンゼルス、ニューヨーク、パリ、ローマ、ヘルシンキの5つの都市で同時刻に走るタクシーで起きる物語をオムニバスで描くジャームッシュ監督作品。大物エージェントを乗せる若い運転手、英語の通じない運転手、盲目の女性客と口論する運転手、神父相手に話し出したら止まらない運転手、酔っ払い客に翻弄される運転手の挿話に豪華キャストが終結、日本で大ヒットした。", "1992-04-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バースデイプレゼント", "バースデイプレゼント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "誕生日に恋人から一方的に婚約を破棄されたスチュワーデスの秋子と、憧れの女性を同僚に奪われたさえないツアーコンダクター・正吉の二人がセーヌ川のほとりで出会う。そして、次のパリ祭で再会する約束をして別れる。しかし、惹かれ合う二人だが、互いについた小さなウソが恋の行方を阻むことになり……。エッフェル塔、シャンゼリゼをはじめパリの名所が随所に登場する、トレンディー映画。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents すき　「帰り道」", "桃まつり presents すき　「帰り道」", "", "", "http://image.eiga.k-img.com/images/movie/57879/315x210.jpg?3393", 15, "若手女性監督による映画製作・上映集団「桃まつり」が、「好き」をテーマに女性監督たちが撮り上げた短編作品を上映する「桃まつり presents すき」の1本。都会にあこがれて田舎の町を出た奈美が、やむを得ない事情で田舎に戻ってくる。その道中、幼い頃に川辺で出会った老人のことを思い出した奈美は、子どもの頃にはわからなかった老人の気持ちを理解し、故郷で生きていくことを決意する。監督は「桃まつり」常連の竹本直美。「壱のすき」として「フィガロの結婚」（天野千尋監督）、「the place named」（小森はるか監督）と同時上映。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丘を越えて", "丘を越えて", "", "", "http://image.eiga.k-img.com/images/movie/53509/315x210.jpg?2495", 114, "「真珠婦人」「父帰る」等で知られる作家であり、文藝春秋社を創業して社長を務め、芥川賞・直木賞を創設した菊池寛。その生誕120年、没後60年を期に製作された、菊池とその周辺の人々を描く人間ドラマ。舞台は昭和初期の東京。女学校を卒業し、知人の紹介で文藝春秋社の面接を受けた細川葉子（池脇千鶴）は社長の菊池寛（西田敏行）の目に留り、菊池の個人秘書として働き始める。監督は「光の雨」「火火」の高橋伴明。", "2008-05-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の妻たち 最後の戦い", "極道の妻たち 最後の戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "関西の広域暴力団が跡目相続で真っ二つに割れ、５年にも及ぶ激烈な長期抗争のさなか妻（おんな）たちにもその波が及んで……。人気シリーズの第４作。今回は妻同士が契りを結ぶ“盃”ごとや、拳銃を握っての妻の殴り込みなど大胆なシーンを盛り込んで、妻たちの心情と行動をダイナミックに描き出す。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GENGHIS KHAN", "ジンギス・カン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "アジアを征服したモンゴルの英雄、ジンギス・カンの生涯を描いた史劇スペクタクル。諸部族が互いに争いあっていた12世紀のモンゴル。若きテムジンは、部族をひとまとめにしようと野望を抱き、のし上がっていく。監督は「サイレンサー」シリーズのH・レヴィン。娯楽作には力を発揮する彼だが、スケールの大きなこの作品には力不足だったようだ。", "1965-01-01", "1,3,7,34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Harimaya Bridge", "The Harimaya Bridge はりまや橋", "", "", "http://image.eiga.k-img.com/images/movie/53739/315x210.jpg?3415", 120, "「リーサル・ウェポン」シリーズなどで知られるハリウッド俳優、ダニー・グローバーがエグゼクティブ・プロデューサーを務める日本、アメリカ、韓国の合作映画。サンフランシスコで暮らす写真家ダニエルは、日本の高知県で英語教師として働く一人息子のミッキーを突然の交通事故で失ってしまう。太平洋戦争で父を失った過去があるダニエルは、日本への嫌悪感と偏見を抱えつつも、息子との思い出を辿るように単身日本へと向かう。日本からは高岡早紀、清水美沙らが出演。", "2009-06-13", "76,1,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("追いつめる", "追いつめる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "広域暴力団壊滅に執念を燃やす退職警官の姿を描く辛口アクション・ドラマ。原作は直木賞を受賞した。７年間にわたって浜崎組を追い続けた刑事・志田はその捜査中、誤って仲間を撃ってしまい退職する。だが、その執念は消えず、出所したばかりの組員の協力を得て、浜崎組の幹部たちを捕えることに成功するのだが……。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("隠し砦の三悪人", "隠し砦の三悪人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "黒澤監督初のシネスコープ作品。戦国時代、敗軍の大将真壁六郎太が、世継ぎの雪姫と隠し置いた黄金200貫とともに敵陣を突破し、同盟軍の陣内へ逃亡するまでの脱出劇。難関につぐ難関、次々とと襲い来る絶体絶命の危機を間一髪で切り抜けるアイデアの数々は、黒澤ほか三人の脚本家により練り上げられたもの。また、六郎太一行に付き添う狂言回しのごとき百姓コンビが、後に「スターウォーズ」の『C-3PO』、『R2-D2』の原案になった逸話はあまりにも有名。スリルとサスペンスとユーモアにあふれた、痛快娯楽時代劇の傑作巨編。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後の贈り物", "最後の贈り物", "", "", "http://image.eiga.k-img.com/images/movie/54849/315x210.jpg?4384", 98, "組織のために愛する女性と人生を捨てて殺人を犯した無期囚テジュ。彼の旧友である刑事ヨンウは、重い病を抱える自分の娘セヒの肝臓移植の条件にテジュが合致していることを知る。移植に合意して10日間の帰休を得たテジュは、逃亡するチャンスを窺う中で驚くべき事実を突きつけられる。ドラマ「天国の階段」のシン・ヒョンジュン、「朱蒙」 のホ・ジュノが2人の主人公をそれぞれ演じる。「無影剣」のキム・ヨンジュンが監督を務める。", "2009-11-22", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　FAR　OFF　PLACE", "ラスト・ウインド　少年達は砂漠を越えた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「アビス」や「バックドラフト」などで知られる撮影監督M・サロモンが初メガホンを執った冒険物語。密猟者たちに親を殺された14歳の少女と16歳の少年が、追っ手から逃れるべく、アフリカ中西部カラハリ砂漠2000キロの横断に乗り出す。同行するのは道案内役のブッシュマンの若者と、少女の愛犬。彼らの決死の道行きを、雄大な自然描写を背景に描き出す。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRINCE THE SHOW GIRL", "王子と踊子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1911年、ロンドン。ジョージ５世の戴冠式に招かれたカルパチア王国の大公殿下は、その夜鑑賞したレビューの一座の踊子にいたく心を奪われ、二人きりの晩餐に彼女を招待する。しかしおかたい大公の愛の言葉は陳腐をきわめ、踊子はすっかり幻滅。二度目の口説きは音楽と光の効果も万全で大成功となり、彼女も本気で彼を愛し始めるのだが……。この身分違いの恋物語は、オリヴィエとモンローの好対照の個性と演技が、格調を忘れぬオリヴィエ自身の演出にうまく生かされて、類似の宮廷ロマンス映画の中では出色の出来。小品ながら、モンロー全盛時代の忘れがたい佳作である。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Saw", "ソウ", "", "", "http://image.eiga.k-img.com/images/movie/1629/315x210.jpg?2640", 103, "老朽化した巨大なバスルームで目覚めた互いに面識のない2人の男は「6時間以内に相手を殺すか、2人とも死ぬか」というゲームを強要される。だが誰が何の目的で？　一方で、この犯人が他の被害者たちにもカミソリワイアーを張り巡らした密室、顎を砕くヘッドギアなどを用いた究極のゲームを仕掛けていたことが判明していく。サンダンス映画祭で注目された異色サスペンス。監督は本作で監督デビュー、オーストラリア出身の28歳。", "2004-10-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クジラ　極道の食卓", "クジラ　極道の食卓", "", "", "http://image.eiga.k-img.com/images/movie/54229/315x210.jpg?3799", 120, "立原あゆみの人気コミック「極道の食卓」を映画化した大人の青春映画。濁組組長の雷蔵は、55歳で妻と熟年離婚する。これまで普通の人生を歩むことができなかった彼の夢は、学校で学ぶことと、1食ごとに心を込めて食卓に向かうこと。組員たちに内緒で高校の夜間部に入った彼は、クラスメイトの咲子らと共に第2の青春をスタートさせるが……。昼はヤクザ、夜は高校生という型破りな2重生活を送る主人公を松平健が好演。", "2009-02-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONSTRAINT PAR CORPS", "密室の渇き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1983年の夏、島で知り合った青年マルクと恋に落ちたクレール。だがマルクが去ったあと彼女は公然ワイセツ罪で捕らえられ、暴行を受けた末に刑務所送りとなる。そこですさまじい虐待を受けるクレール。無実の罪で捕らえられた女を主人公に、女子刑務所の暴力を描き出す衝撃作。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベルセルク　黄金時代篇III 降臨", "ベルセルク　黄金時代篇III 降臨", "", "", "http://image.eiga.k-img.com/images/movie/57529/315x210.jpg?3529", 0, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COVER GIRL", "カバーガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ナイトクラブのコーラス・ガールがカバー・ガールに選ばれたことをきっかけにブロードウェイのスターとなるが、最後は恋人のもとへ帰る。作詞作曲はジョージ・ガーシュインとジェローム・カーン。ケリー自身のほか、スタンリー・ドーネンらが振付を担当している。1950年代MGMミュージカルの先駆的佳作。DVDは「リタ・ヘイワース フィルム・コレクション」に収録。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("外科室", "外科室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "外科室のベッドで手術を待つ伯爵夫人は、どうしても麻酔はいやだという。うわごとを言ってしまうからだ。彼女の病気は、手術をしなければ回復の見込みはない。手術は彼女の主張通り麻酔なしで行われるが、彼女にはどんな秘密があったのか？　玉三郎初監督、50分一本立て1000円興行などで話題となったヒット作。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Woodstock", "ウッドストック　愛と平和と音楽の三日間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 185, "1969年の８月15日～17日にかけて、N.Y.にほど近いベセルの丘で開催された一大ロック・コンサート、ウッドストック。単なるコンサート・イベントではなく、愛と平和と音楽の祭典、激動の時代の若い魂の解放の宴だった。出演したアーティストたち——ジョーン・バエズ、ジョー・コッカー、カントリー・ジョー＆ザ・フィッシュ、クロスビー・スティルス＆ナッシュ、アーロ・ガスリー、ジミ・ヘンドリックス、サンタナなどなど。この作品はアーティストを軽快に活写しただけではなく、全米から押し寄せた40万人もの若者たちの、会場での自由を求める姿を感動的にドキュメントしている。死者３人、負傷者5000人、出産も２件を記録。後の音楽ドキュメント映画にも、多大な影響を与えた作品である。マーティン・スコセッシが共同監督と編集で参加している。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("羅生門　デジタル完全版", "羅生門　デジタル完全版", "", "", "http://image.eiga.k-img.com/images/movie/54199/315x210.jpg?3473", 78, "1951年にベネチア国際映画祭金獅子賞を受賞した黒澤明の名作を、米アカデミー協会の全面的バックアップを受け、映像とサウンドをデジタルリマスター。原作は芥川龍之介の短編「藪の中」、主演は三船敏郎。平安時代、とある山中で貴族女性が山賊に襲われ、供回りの侍が殺された。やがて盗賊は捕われ裁判となるが、山賊と貴族女性の言い分は真っ向から対立し……。", "2008-11-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLESS THE BEASTS ＆ CHILDREN", "動物と子供たちの詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "サマー・キャンプにやって来たコットンら６人の少年たちは、いずれも親に無視されたはみだしっ子たちだ。牧場で射的のマトにされるバッファローを見てショックを受けた彼らは、逃がそうとするが……。大人たちの無理解に抗議する純真な子供たちの姿を見つめた力作。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("国産凌凌漆／FROM　BEIJIN　WITH　LOVE", "0061 北京より愛をこめて!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1990年以降、香港のマネーメーキング・スターのトップを走り続けるC・シンチーがおとぼけのスパイを演じ、脚本も担当したコメディ。「007」シリーズのパロディーの数々とシンチーのアクションを交えたコメディ演技が笑いを呼ぶ。北京で、国宝級の恐竜の頭の化石が、謎の鉄仮面の男に盗まれる。人民警察司令官は、今は町で肉屋をやっている元スパイのチャイを呼び出しその捜索を命じた。だが、司令官こそが犯人であり、ドジなチャイに事件を担当させることで調査に動く“南”のスパイ活動をかく乱するつもりなのだ。目的地に飛んだチャイは、司令官の密命を受けた女スパイ、カムと行動をともにするが……。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魚影の群れ", "魚影の群れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "本州最北端、下北半島の漁港・大間を舞台に、ある一人の頑固な漁師を中心に展開される骨太の人間ドラマ。彼が男手一つで育てあげた娘と、一人前の漁師になろうとする青年との愛憎を軸に、家出した妻との再会など多彩なエピソードを盛り込んで描く。相米慎二監督がアイドル映画ではない、大人のドラマに初挑戦した作品で、巻頭の海辺の長回しから、撮る対象と粘り腰で格闘する相米ワールドに引きずり込まれる。オール・ロケで撮影されたマグロ漁の場面は、魚影を捕えてから釣り上げるまでが記録映画のようにリアルタイムで描かれていて、海の男のドラマに広がりを与えた。父に近親愛を抱く娘に扮した夏目が、全身にエモーションをたぎらせ熱演。サブ・エピソードながら家出妻・十朱の落ちぶれ役の好演も光る。DVDは「夏目雅子　出演名作集　DVD-BOX」に収録。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Before the Devil Knows You're Dead", "その土曜日、7時58分", "", "", "http://image.eiga.k-img.com/images/movie/53869/315x210.jpg?3008", 117, "「十二人の怒れる男」「狼たちの午後」の名匠シドニー・ルメットの監督45作目。優雅な暮らしを送る会計士のアンディは、娘の養育費に窮している弟ハンクに、両親が営む宝石店への強盗計画を持ちかけ、2人は計画を実行に移すが……。1つの誤算から家族の抱える闇が浮き彫りになっていくサスペンス・ドラマ。「カポーティ」のフィリップ・シーモア・ホフマンが自らの不正の発覚に怯える兄を、「ガタカ」のイーサン・ホークが甲斐性のない弱気な弟を演じている。", "2008-10-11", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TARGET", "ターゲット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "ハックマンとディロンが父子を演じたアクション映画の快作。ヨーロッパ旅行中に行方不明となった妻を捜すため、パリに飛んだウォルターと息子のクリスは、幾度となく殺し屋に命を狙われる。領事館の旧友や、今まで互いにしっくりいかなかった息子クリスは、父親がCIAのスパイだったという過去に驚くが、協力し合い父親を助ける。捜査を続けるうちに妻を誘拐したのは、16年前に家族を惨殺され、その犯人をウォルターだと固く信じる東ドイツのスパイ、シェローダであることがわかるが……。ラストの真犯人登場までスリリングに展開し、親子の情愛もほど良く加味された作品。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZIG ZIG", "恋のモンマルトル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "パリの下町モンマルトルの歓楽街ピガール。ナイトクラブで今夜も踊る二人組の美人ダンサーは、スイスでの豪華な山荘暮らしを夢見るパリジェンヌ。この二人を中心に恋の駆け引き、人生の喜怒哀楽をコメディ・タッチに描いたサスペンス・ロマン。主題歌『ジグ・ジグ』も楽しい。", "1975-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("双龍曾", "ツイン・ドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "幼い頃、生き別れとなった双子の兄弟マーとジャッキー。片やニューヨークの一流オーケストラ指揮者、片や香港暗黒街のボス。互いの存在を知らぬままそれぞれの世界で成功を収めた兄弟が運命の糸で再会した時、とてつもない陰謀が始まろうとしていた……。香港の国民的スター、ジャッキー・チェンがはじめて二役に挑み、香港映画界の中心的存在T・ハークと組んだ娯楽アクション巨編。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地球交響曲　ガイアシンフォニー 第四番", "地球交響曲　ガイアシンフォニー 第四番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "様々な人々の生き方をインタビューと映像で綴るオムニバス形式のドキュメンタリー映画のシリーズ第４作。地球環境の美しさと大切さを訴える同シリーズは、自主上映活動主体の映画ながら160万人以上の観客を動員するという隠れたロングラン作品でもある。本作では、『ガイア理論』の創始者として名高い生物物理学者のJ・ラブロック、チンパンジー研究家＆環境教育活動家のJ・グドール、レジャンド・サーファーのJ・ロペス、芸術家の名嘉睦年稔が出演。その活動とともに、地球の命の素晴らしさをカメラが余すところなく伝えている。", "2001-12-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dirty Harry", "ダーティハリー", "", "", "http://ecx.images-amazon.com/images/I/51rQHIWhDXL._SL210_.jpg", 102, "　サンフランシスコで無差別狙撃事件が発生。犯人は警察に対し、10万ドルを支払わなければ、次の犠牲者を狙うと通告してきた。殺人課の刑事ハリー・キャラハンは必死の捜査の果てにサソリと名乗る犯人を追い詰め、ついに逮捕する。しかしハリーの暴力行為が原因で、犯人は釈放されることに。その後、スクールバスがジャックされるという事件が……。　44マグナムをぶっ放す凄腕刑事ダーティハリー、シリーズ第1弾。クリント・イーストウッドの当たり役であり、ドン・シーゲルの代表作でもあるバイオレンス・アクション。", "1972-02-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我が恋は燃えぬ", "我が恋は燃えぬ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "明治の自由民権運動の女性闘士で、女性解放運動のさきがけともなった影山英子の『妾の半生涯』をもとにして構成されたメロドラマ。岡山の旧家に育った英子は、恋人の自由党員・早瀬を慕って上京するが、早瀬は実は藩閥政府のスパイだった。傷心の英子は弾圧にもくじけずに闘うが……。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あさき夢みし", "あさき夢みし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "13世紀、法皇の皇子・後深草天皇の求愛を受けた女・四条が、やがて様々な男たちと愛欲に溺れ、そこから脱して自由を求め、出家して全国を行脚していく。詩人・大岡信の脚本を得て、情念の作家・実相寺昭雄が、一人の女性の流転する半生を叙情的に描いた作品。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HOT ROCK", "ホット・ロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ニューヨークはブルックリン博物館に展示されている超高価なダイヤを盗み出すプロの泥棒の話。ダイヤはアフリカのA国のものだが、盗みの依頼はAの敵国Bからのものというアイデアも面白い。スケールも大きく、小技も効いてすべてがうまくできた犯罪アクション。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("異形ノ恋", "異形ノ恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "上京したものの働きもせず町をさまよっていた晃は、同性愛者の多菜子に拾われ彼女の部屋に転がり込む。多菜子に惹かれていくも、想いを伝えられない晃は、やがて精神のバランスを崩し、通り魔的な犯罪を繰り返すようになる。犯罪者の心理を描く人間ドラマ。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・奮闘篇", "男はつらいよ・奮闘篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "１年ぶりに生みの母親と再会した寅次郎は、沼津のラーメン屋で出会った少し頭の弱い少女から“あんたのお嫁になりたい”と言われて真剣に悩む。後半、少女の故郷・津軽に移っての展開が秀逸。ミヤコ蝶々が母親役で再登場。また田中邦衛が教師役で出演している。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一個死者対生者的訪問／QUESTIONS FOR THE LIVING", "死者の訪問", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「トンヤンシー・夫は六歳」のホァン・チェンチョンによる第２作。満員のバスの中、わき役俳優の葉肖肖はスリが財布を抜き取るのを見つけ、捕まえる。しかし葉肖肖は逆にスリの凶器によって命を失ってしまう。だが彼の魂はその死を受け入れずによみがえり、無念を晴らそうと生者への訪問を始める。", "1986-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THEY LIVE BY NIGHT", "夜の人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1930年代、不況下のアメリカ南部。刑務所を脱獄した若者ボウイは、仲間とともに銀行強盗を続けていた。やがて、“ボウイ・ザ・キッド”と呼ばれるようになった彼は、キーチという女性と恋に落ち、人生をやり直すための逃避行を開始するが……。犯罪映画としてのクールなスタイルと、追い詰められ、人目を避けるがゆえに燃え上がる愛を描いたロマンティックな部分を併せ持つ作品。N・レイの処女作で、ゴダールからカラックスまで多くの映画人に影響を与えたといわれる傑作。ロバート・アルトマンの「ボウイ＆キーチ」はこの作品のリメイク。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("eXistenZ", "イグジステンズ", "", "", "http://image.eiga.k-img.com/images/movie/1839/315x210.jpg?3707", 97, "未来、人々の娯楽は脊髄に生体ケーブルを直結してプレイする究極のヴァーチャルリアリティ・ゲームだった。ゲーム界のスターである美貌の天才ゲームデザイナー、ゲラーの新作ゲーム“イグジステンズ”の発表会で、ゲラーは小動物の骨でできた銃を持つ男に襲われる。たまたま居合わせた警備員見習いのパイクルは重傷のゲラーを託され、間一髪で混乱する会場から逃げ出した。脊髄の損傷を恐れゲームをプレイしたことがないパイクルだったが、ゲラーに説き伏せられ2人でゲームを始めることになる。", "2000-04-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五人の斥候兵", "五人の斥候兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "北支戦線を転戦していた岡田歩兵部隊に本隊から前方敵陣偵察の命令が伝えられ、部隊長は５名からなる斥候隊を送り出した。斥候隊は夜の草原をひそかに駆け抜けていく。やがて遂に敵陣に近づいた。丘に差しかかった時、前方に敵のトーチカを見つける。敵は機銃掃射を開始。５人は身を伏せるが、敵の大部隊に包囲されたことを知る。水流を頼りに退路を探すうちに、５人は離ればなれに……。煽動的な戦意昂揚映画が多かった中にあって、本作品はきわめて抑制された、いわばドキュメンタリー映画を観るような印象すら与える静的な作品となった。日本の戦争映画で芸術的映画として評価された最初の作品といわれている。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("利休", "利休", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "時は戦国、安土桃山時代。織田信長、明智光秀、豊臣秀吉と続く権力闘争のなかで、ひたすらお茶の道を追究し、自分の生き方を変えなかった男、千利休。この作品は、美と知の体現者である利休と、絶大な権力を持ちながらも粗野で、利休に羨望と嫉妬の目を向ける秀吉という対照的な男の確執を描いている。衣装は、時代劇衣装の第一人者であるワダエミ。監督は、自らも華道・草月流家元の免許を持ち、これが17年ぶりの劇映画となる勅使河原宏で、映画に登場する生花はすべて監督自身の手によるものである。役者陣では、重厚な演技の三國連太郎（利休）と、見てくれだけの権力者・秀吉に扮した山崎努のオーバーな演技が好対照。1989年、モントリオール映画祭では、芸術貢献賞を受賞。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Baby's Breath ママになろうよ", "Baby's Breath ママになろうよ", "", "", "http://image.eiga.k-img.com/images/movie/53609/315x210.jpg?4631", 87, "妊婦のマリは、母子ともに健やかに日々過ごしたいたが、ある日、通院先の産婦人科で、頭に包帯を巻いた見知らぬ少女と出会う。そして、彼女に導かれたマリが見たものは、夫・祐介の遺体だった……。マリはそこで目が覚めるが、記憶と現実が錯綜し……。妊婦の揺れ動く心情や同世代の女性たちの生き様を描くドラマ。主演は元グラビアアイドル、パイレーツの浅田好未で、実際に浅田の妊娠5カ月から出産までの間に撮影されている。", "2008-04-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちんなねえ", "ちんなねえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "「HEAVEN-６-BOX／天国の六つの箱」に続いて高知県立美術館が製作したアート・フィルム。「海ほおずき」の林海象がメガホンを執っている。一人の探偵が迷い込む幻想を捉えたフィクション・パートに、土佐の絵師、絵金が登場する麿赤兒の舞踏公演『トナリは何をする人ぞ』の模様を絡めて、マジカルな映像世界を紡ぎ出している。タイトルの“ちんなねえ”とは、高知の方言で“おかしなことだねえ”“奇妙だねえ”という意味。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("331/3r.p.m.", "331/3r.p.m.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1970年代の高円寺。高校を中退して上京してきた僕は、学生運動をちょっとかじって、喫茶店でたむろし、夜はキャバレーでバイトと、切なく、中途半端な毎日。それでも僕の周囲は変わっていく。喫茶店の常連の活動家は逮捕され、伝説のシンガーは焼鳥屋で酔いつぶれている。そんなある日、キャバレーに新人のホステスが入ってくる……。ジャックス、遠藤賢司など1970年代和製ロック22曲が全編に流れている。アンティーク玩具屋“ゴジラや”のオーナーである木澤雅博が企画・監督・原作・脚本を務めている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATHSTALKER II：DUEL OF THE TITANS", "勇者ストーカーの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ヒロイック・ファンタジー“勇者ストーカー”シリーズ第２作。嵐の晩、城から宝石を盗んだストーカーは、ジャフィア国の王女イビーと出逢う。王女の国は今、魔法師ジェラクの手にあり、イビーのニセ王女が支配していた。イビーを助けて、ストーカーが乗り込む。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISSION OF THE SHARK", "シーフォース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "第二次大戦中の実話をもとにした海上戦争アクション。手に汗にぎる緊迫感と意外な結末は、1981年西独映画「U・ボート」を思わせるものがある。1945年７月、米軍の誇る戦艦インディアナポリスは機密指令を受けて出港。船倉の積み荷は、太平洋上の弧島でB−29に搭載されるということ以外、すべての船員に秘密とされていた。通常ルートではなく最短直線距離を航行する艦の動きを、日本軍が見逃すはずはなかった。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恐山の女", "恐山の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "五所平之助後期の代表作。貧しい漁村。娘アヤ子は父が病気になり遊廓・七福へ売られていった。そこでの常客に勘助と勘二郎の親子がいたが、二人は時期を同じくして死んでしまう。男殺しの女として噂の的になったアヤ子に、勘一はあえて近づくのだが……。全編をドロドロとした怨念が貫く力作。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BULLET BALLET／バレット・バレエ", "BULLET BALLET／バレット・バレエ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "恋人を拳銃自殺で死なせてしまったCM制作会社のサラリーマン合田は、それがトラウマとなって拳銃の魅力に執着するようになっていく。そんなある日、彼は不良グループの少女・千里らに脅され、金をとられてしまう。グループへの復讐心に燃える合田は、自ら拳銃を作って乗り込んで行くのだが、計画は無残にも失敗。しかし、その後本物の拳銃を求めて街をさまよう彼は、死に急いでいるかのような千里に不思議なシンパシーを覚えるようになっていく……。世界的に注目され続けるインディーズ映画界の鬼才・塚本晋也監督が、硬質かつ生理的なモノクロ映像で、都市の闇と、世代間の衝突と共感、そして人間の死と生を描いたバイオレンス映画。かつて「鉄男　TETSUO」を製作していた頃はオヤジ狩りをする世代に属していた監督が、今ではオヤジ狩りをされる世代に回っていることを、強く自覚しながら演出しているかのような節も感じられる作品である。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dear Heart 震えて眠れ", "Dear Heart 震えて眠れ", "", "", "http://image.eiga.k-img.com/images/movie/54889/315x210.jpg?2076", 87, "心臓移植で殺人鬼の魂を宿されてしまった男が繰り広げる惨劇を、高島礼子主演で描いたサイコ・サスペンス。ミステリー作家である夫・信二とともに高原の別荘を訪れた美輪子は、心臓移植手術を受けたばかりの信二が奇妙な言動を取り始めたことに戸惑いを覚える。その原因を突き止めようと信二を別荘に残して東京へ引き返した彼女は、彼に移植された心臓が連続殺人犯のものであると知る。メガホンをとるのは「象の背中」の井坂聡。", "2009-12-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("本日の猫事情", "本日の猫事情", "", "", "http://image.eiga.k-img.com/images/movie/53099/315x210.jpg?1897", 62, "猫を題材にした漫画を得意とするいわみちさくらの同名コミックを、人気モデルの大桑マイミ主演で映画化。5匹の猫と飼い主の女性の日常を、天国から彼女たちを見守っている猫の視点で綴る。天国にいる猫のルパンは、地上で暮らす5匹の仲間たちとママの姿を温かく見守り続けている。やんちゃな猫たちはイラストレーターであるママの仕事を邪魔してばかりで……。ルパンの声を担当するのは、アニメ版「NANA」などの声優・平野綾。", "2007-12-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fright Night", "フライトナイト　恐怖の夜", "", "", "http://image.eiga.k-img.com/images/movie/56859/315x210.jpg?1519", 106, "トム・ホランド監督のカルト的人気を誇るホラーアクション「フライトナイト」（1985）をリメイク。ごく普通の高校生チャーリーの隣家に魅力的な男ジェリーが引越してきてから、夜ごと町の人々が姿を消していく。チャーリーはジェリーの正体がバンパイアであることを知るが、それを証明するすべもない。チャーリーは最愛の人を守るためにも、自らの手でジェリーを倒す決意をするのだが……。主演はチャーリー役に「ターミネーター4」のアントン・イェルチン、対するジェリー役にコリン・ファレル。", "2012-01-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユリシス", "ユリシス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "オーストラリアの雄大な自然を舞台に、3人の男女がたどる心の旅路を描いたロードムービー。オーストラリアのケアンズへ旅行にやって来た大学生サライとミチルは、ナンパ目的で訪れた空港で、美しい日本人女性・千景と出会う。2人はオーストラリアに生息する青い蝶“ユリシス”を探しに来たという千景を連れ、あてのない旅に出るが……。主演は「犬神家の一族」の奥菜恵。共演にミュージカル「テニスの王子様」の斎藤工と、「幸福のスイッチ」の林剛史。", "2006-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUX YEUX DU SOUVENIR", "思い出の瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「田園交響楽」の監督、主演コンビのJ・ドラノワとM・モルガンが、再びコンビを組んだメロドラマ。美貌のスチュワーデス、クエールと運命的な再会を果たした旅客機パイロット、ジャックの恋を描く。現代とは隔世の感ある航空事情や、J・マレーの美男ぶりも見もの。", "1948-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("堂堂たる人生", "堂堂たる人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "『週刊明星』に掲載された源氏鶏太の同名の小説を牛原陽一が監督。主演は石原裕次郎。オモチャの製作会社に勤める中部周平（裕次郎）と紺屋小助（長門）は新製品のテスト中に石岡いさみ（芦川）と知り合う。オモチャ会社は倒産寸前だったが、３人は知恵を絞り孤軍奮闘して会社を救う、というハッピーな作品。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新網走番外地・嵐呼ぶダンプ仁義", "新網走番外地・嵐呼ぶダンプ仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "刑務所で殺された兄弟分の妻の窮地を救うために、末広勝治は悪徳ボスと死闘を演ずる。結局この作品が最終作となり、７年間続いてきたロング・シリーズはここに終わりを告げたが、有終の美は飾れなかった。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUT　PRE｀S　DES　E´TOILES", "エトワール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "“エトワール”とはフランス語で“星”の意味を持ち、バレエ界の頂点に立つダンサーのこと。300年以上の歴史を持つバレエの殿堂、パリ・オペラ座の裏側を初めて映像で捉えた貴重な作品。“舞台は麻薬”と言い放つ者、血豆を隠してステージに立つ者、厳しい現実と実力の壁にぶち当たり挫折する者、チャンスを狙う代役たち。そして家庭との両立に悩み、引退の孤独に脅える者。過酷な練習風景や実際のステージを追いながら、ダンサーたちの素顔を赤裸々に映し出す。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASQUERADE", "マスカレード 甘い罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "貧乏なヨット青年が、金持ちに近づき結婚する。だがこれには思いがけない秘密が隠されていた……。当時のアイドル、R・ロウの魅力がいっぱいのサスペンス・ラブ・ストーリーで、ハードなベッド・シーンや二枚目ぶりをみせる彼のクールさも魅力的。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE ON HAUNTED HILL", "地獄へつゞく部屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "名優V・プライスが、幽霊屋敷パーティーを開催する不気味なキャラを演じて恐怖をあおる古典的ホラー。屋敷に宿泊すると一晩も持たずに絞殺死体で発見される伝説があった。主人公は屋敷を借りて人々を招待し、一夜を無事に過ごせば賞金を与えると言う。やがて戦慄のサバイバル劇が繰り広げられることに。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("走れ！ケッタマシンウエディング狂奏曲", "走れ！ケッタマシンウエディング狂奏曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ミュージカル劇団“ふるさときゃらばん”の製作によるミュージカル仕立てのファミリー・ドラマ。主人公は、田舎町に住む谷川家の人々。しっかり者の長女ユキが恋人を追って家出したことから、ガンコ者の父親、勇作は落ち込む一方。そのころ、落ちこぼれ中学生の長男、ター坊は悪友たちと自転車競争に熱中。これが商店街の人々にウケて、自転車競争は町起こしの一大イベントに発展するが……。にぎやかな歌と踊り、３千人のエキストラを動員した自転車レース・シーンが見もの。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRISONER OF ST.PETERSBURG", "ペテルブルグ幻想", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ドストエフスキーやゴーゴリなどのロシア文学を愛する少年がベルリンに降り立つ。彼の目に映るものはすべてロシア文学の中に出てくる風景であり、人物なのだ。ある日、二人の女性と知り合った少年はその一人に『罪と罰』のソーニヤを見いだすが……。現実と幻想がクロスオーバーする独特な映像世界。", "1988-01-01", "11,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ほんとうにあった怖い話3D", "劇場版ほんとうにあった怖い話3D", "", "", "http://image.eiga.k-img.com/images/movie/55689/315x210.jpg?3546", 75, "一般投稿で寄せられた恐怖体験を再現ドラマ化し、iPhone及びiPadアプリの「エンターテインメント」カテゴリで第1位を獲得したホラー体験集「ほんとうにあった怖い話」の劇場版。「モーニング娘。」の新垣里沙が映画初主演を務める。女性の飛び降り自殺を目撃してしまった女子高生の恐怖を描く3D作品「シセン」、心霊スポットを訪れたカップルの運命を描いた「心霊スポット」など全5話で構成される。", "2010-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一遍上人", "一遍上人", "", "", "http://image.eiga.k-img.com/images/movie/57319/315x210.jpg?1414", 0, "お笑いコンビ「キャイ～ン」のウド鈴木が映画初主演を果たし、鎌倉時代に各地で「踊り念仏」を行った時宗の開祖・一遍上人の姿を描くドラマ。監督は、「斜陽」「蜘蛛の糸」など文芸作品の映画化を多数手がけてきた秋原北胤（正俊）。「仏との結縁」を万人に説くため遊行を始めた一遍は、妻や娘らとともに旅に出る。その途中に立ち寄ったとある武家屋敷で念仏を唱えていると、妻の超一が突然踊り始め、これをきっかけに一遍は「踊り念仏」を始める。鎌倉を目指して旅を続ける一遍はさまざまな人との出会いを果たしていくが……。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃尻娘 ピンク・ヒップ・ガール", "桃尻娘 ピンク・ヒップ・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "活発で積極的なレナ、奥手で消極的タイプの裕子。二人の女子高生は固い友情で結ばれているが、男の子とのセックスをめぐり、ライバル意識の火花を散らし始める。小原監督の演出は、思春期にある女の子の心理をビビッドに表現し、これまでの日本青春映画が陥りがちな陰湿さを痛快に払拭。軽やかなタッチの作品に仕上げている。竹田かほりと亜湖の凸凹コンビも息がぴったりと合って、亜湖が“レナちゃ～ん”と甘えた声で何度もすり寄ってくるところは、奇妙なおかしさを誘う。このあと、1979年に「ラブアタック」、1980年に「プロポーズ大作戦」と２本の続編が製作されている。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUNDI MATIN", "月曜日に乾杯！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "2002年ベルリン映画祭銀熊賞(監督賞)と国際批評家連盟賞のダブル受賞。イタリアの名匠オタール・イオセリアーニが、繰り返される単調な日常にちょっと嫌気がさした、しがない中年男の自分探しの旅を温かい視線で綴った。片道１時間半かけて工場へ行くヴァンサン。仕事は単調極まりなく、家でも自分の居場所がない。そんな日常に嫌気がさした彼は、ある日突然、家族に何も告げず、ぶらりとヴェニスへ旅立つ。", "2002-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悶絶!!どんでん返し", "悶絶!!どんでん返し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "名匠、神代辰巳監督が第２回ロマンポルノ・シナリオ公募の入選作『あづき色の黄昏』を、乾いたタッチで描いた異色作。東大卒のエリート・サラリーマンがキャバレーのホステスと一夜の浮気をしようとしたことから、ヤクザに絡まれ、うしろの童貞を失い、オカマになって警察に追われる身にまで堕ちていく。トンでもない快作になった。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eddie and the Cruisers II: Eddie Lives!", "ストリート・オブ・ヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "M・パレの劇場映画デビュー作でもあった「エディ＆ザ・クルーザス」の６年後に製作された続編。前作で自動車事故に遭って行方不明となっていた人気ロック・バンドのリーダー、エディはカナダで別人として生きていた。しかし彼は再び、スターとしてカムバックする決意を固めていく。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん／　のび太のワンニャン時空伝", "ドラえもん／　のび太のワンニャン時空伝", "", "", "http://ecx.images-amazon.com/images/I/31HRN941STL._SL210_.jpg", 84, "記念すべき映画版第１作「ドラえもん　のび太の恐竜」が公開されてから通算25作目。本作ではのび太が一匹の子犬を助けたことから始まる壮大な冒険物語が展開する。個性的な犬人間やネコ人間なども登場して、ドラえもんファミリーと熱い友情で結ばれた冒険を繰り広げていく。川で溺れている仔犬を助けたのび太は“イチ”と名付けて可愛がろうとしたのだが、ママに反対されて他の捨て犬や捨て猫とともにタイムマシーンで３億年前まで連れていく。次の日、再びのび太たちはタイムマシーンに乗り込むが……。", "2004-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINDERS　KEEPERS，　LOVERS　WHISPERS", "ファインダー・ キーパーズ・ラヴァーズ・ウィーパーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "「ファスター・プシィキャット！キル！キル！」へと続く、異才R・メイヤーのバイオレンス路線の一編。我が物顔でハイウェイを行く、無軌道なバイカー３人組は、暴行に飽き足らず、ついには殺人をも犯す。彼らに妻をレイプされた獣医は復讐に燃え、３人の魔の手を逃れた人妻とともに、その行方を追跡する。エキゾチックな巨乳美女ハジが存在感を発揮。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／DEEP　BLUE　NIGHT", "ディープ・ブルー・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "韓国映画ニューウェーブの旗手、ペ・チャンホがオール・アメリカ・ロケで描くドライなタッチの大ヒット作。恋人を故国に残してアメリカにやって来たペクは、アメリカでの永住権を得るため、バー勤めの韓国女性と偽装結婚をする。恋人との幸福な生活を夢みて口八丁手八丁で移民局や女の裏をかき続ける韓国人の姿を爽快に、またペーソスとともに描く。偽装結婚を暴こうと厳しく追及する役人たちを前に行き詰まったペクがアメリカ国歌を歌い出すシーンは圧巻。アン・ソンギの二枚目半の持ち味に、都会的でシャープな語り口がミックスされた傑作に仕上がっている。", "1985-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kidnapped", "海賊船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「宝島」のスティーブンソンによる冒険小説を、ウォルト・ディズニー製作の下、「キング・ソロモン」等の作品で知られるイギリス出身の名匠ロバート・スティーブンソンの監督・脚本で映画化。18世紀のスコットランド。父の遺産を狙う叔父の企みで海賊船にさらわれてしまった青年デイビッド。船で働かされることになった彼は、ある日難破した青年アランを救う。2人は船長たちに戦いを挑み、なんとか船からの脱出に成功するが……。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Holy Smoke", "ホーリー・スモーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「ピアノ・レッスン」のジェーン・カンピオン監督が異色のテーマに取り組んだ問題作。魂と肉体をさらけ出して生きることの意味を探る男女の葛藤を、大胆な官能描写を交えて映し出す。インド旅行中の若いオーストラリア人女性ルースが、ヒンズー教の導師との結婚を決意。驚いた家族はルースを取り戻すため、P・Jという男を雇う。洗脳を解く専門家であるP・Jは、砂漠の小屋でルースと向き合うのだが……。", "1999-01-01", "11,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ICE CASTLES", "アイス・キャッスル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "オリンピックを目指すフィギュア・スケーターのレクシー。ふとした事故で彼女は視力を失ってしまう。周囲の人々の愛に支えられ、地区大会に出場を果たすが……。花形スケーター、ジョンソンの華麗なスケート・シーンが見もののラブ・ストーリー。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロマンス娘", "ロマンス娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「ジャンケン娘」に次ぐひばり・チエミ・いづみのトリオによるハッピーな和製ミュージカル。三人娘は、夏休みの自転車旅行を計画し、その旅費稼ぎのためにデパートでアルバイトを始める。３人はそのデパートのオーナーに見初められて邸に招待されるが、いづみはそこで実の父と名乗る男に出会う。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蒼穹のファフナー HEAVEN AND EARTH", "蒼穹のファフナー HEAVEN AND EARTH", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "人型兵器“ファフナー”に搭乗して未知の生命体“フェストゥム”に立ち向かう少年たちを描いた人気ロボットアニメの劇場版。2004年のテレビシリーズ放送から6年の時を経てスクリーンに登場する。テレビ版に続き、脚本を「マルドゥック・スクランブル　圧縮」の冲方丁、キャラクターデザインを平井久司、アニメーション制作をXEBECが手掛ける。", "2010-12-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANTWONE　FISHER", "きみの帰る場所／　アントワン・フィッシャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "オスカー俳優、デンゼル・ワシントンの初監督作。家族の愛情を知らずに育った若い軍人の心の再生を真摯に描く。アメリカ海軍の水兵、アントワンはキレやすい性格のため、軍の精神科に通うよう命じられる。性に関するトラウマを抱える彼は、担当医のダヴェンポートにそのことを打ち明けることで徐々に自らを取り戻していき、やがて初めて女性に恋をする。ビデオタイトルは「A.Q.／アントワン・Q・フィッシャー・ストーリー」、DVDタイトルは「アントワン・フィッシャー／きみの帰る場所」。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お父さんはお人好し", "お父さんはお人好し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "果物店を営む藤本阿茶太郎は13人の子宝者。気の弱い長男の結婚話に奔走するうち、不景気で商売は苦しくなり、借金地獄に陥って……。長沖一の人気連続ラジオ・ドラマを映画化した斎藤寅次郎監督のお得意の人情喜劇。ラジオと同じく名優、花菱アチャコが主演し、続編が連作された。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いとしのラハイナ", "いとしのラハイナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "自動車修理工の二人組が、日頃のストレス解消のためにと出かけたハワイ旅行で巻き起こす、珍騒動の数々を描いたライト・コメディ。怪しげな東北弁を使う外人青年にスーツケースを持ち逃げされたり、大金持ちの中国人に気に入られたりとエピソードも多彩。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DV〈ドメスティックバイオレンス〉", "DV〈ドメスティックバイオレンス〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「でらしね」の中原俊監督が、ドメスティックバイオレンスの渦中にある夫婦の姿を凝視した衝撃作。嫉妬と執着心から家庭内暴力がエスカレートしていく夫と、愛ゆえにそこから逃げ出すことができない妻の出口のない関係を、具体的な描写であぶり出す。演技派、遠藤憲一が荒れる男心を熱演。結婚記念日に夫、昭吾は妻の泰子との約束をキャンセル。その夜から彼の異常な行動が始まった。暴力は次第に激しさを増していき……。", "2005-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青蛇／GREEN SNAKE", "青蛇転生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "人間と妖精が共存していた南宋時代の中国を舞台に、妖精とそれを追う高僧の闘いを描いたSFXアクション・ファンタジー。二匹の青と白の大蛇は女性に姿を変え、人間界の許仙学士の元で修行を積んでいた。白蛇は許仙に恋をし、青蛇もまた彼を愛していたが、自分より優れている白蛇にはかなわず嫉妬していた。そんな折、各地で妖精を制圧している高僧の法海が現れ、二匹の正体を知る彼は闘いを迫ってくるのだった……。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版タッチ 背番号のないエース", "劇場版タッチ 背番号のないエース", "", "", "http://ecx.images-amazon.com/images/I/218M29RS45L._SL210_.jpg", 93, "幼なじみである双子の兄弟の達也・和也と浅倉南の３人が活躍する、ラブ・ストーリーとスポーツ・ドラマを調和よくブレンドさせた青春アニメの傑作。幼い頃からいつも一緒に遊んでいた彼らの合言葉は“目指せ! 甲子園”。高校生になると、和也と南は野球部の選手とマネジャーになったが、タッちゃんこと達也はボクシング部へ。だが、彼もやがて野球部に入ることに……。", "1986-04-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tactical Unit - Partners", "タクティカル・ユニット　機動部隊　絆", "", "", "http://image.eiga.k-img.com/images/movie/57749/315x210.jpg?2611", 91, "ジョニー・トー監督作「PTU」（2003）のスピンオフ作品で、香港警察の特殊機動部隊（Police Tactical Unit＝PTU）の戦いを描くアクション。現金輸送車の襲撃事件が発生し、PTUの小隊長サムの率いるチームと、サムとは何かと折り合いが悪い女性メイの率いる小隊が、山中に逃げ込んだ犯人グループの掃討作戦を担当することになる。2つの小隊はライバル心から功績を争うこともしばしばだったが、そんな2小隊の前に犯人グループが出現。激しい銃撃戦が始まるが……。ジョニー・トーがプロデュースを手がけ、トー作品の常連サイモン・ヤムが主演を務める。", "2012-02-25", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三國志／THREE GREAT KINGDOMS", "三国志　大いなる飛翔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "企画から完成までに８年６ヵ月、総製作費15億円、エキストラの数10万余と、中国映画界が総力を結集して製作したスペクタクル時代劇。後漢滅亡のあと三国時代の中国を背景に、諸葛孔明を擁する劉備玄徳と、冷徹の将・曹操との戦いを第１部では208年の赤壁の戦いまでに区切って描いていく。", "1990-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛のきずな", "愛のきずな", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "松本清張の『たづたづし』を原作としたミステリー・ロマン映画。専務の娘と結婚して皆からうらやまれている鈴木良平。けれども、父親や権威をかさにきる妻との生活は決して楽なものではなかった。そんなある雨の日、良平は清純な娘・雪子と出会い、愛し合う。しかし、雪子には服役中の夫がいた……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("音楽疆屍／THE MUSICAL VAMPIRE", "霊幻道士６ 史上最強のキョンシー登場!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "シリーズも６作目となって、お札も鏡も墨紐も効かない最悪のキョンシーが登場する。 1920年頃の中国では、キョンシーとなった者たちを迷わずに成仏させるため、故郷に送り届けるビジネスがあった。その道では知られた教祖マ先生は、ヤム老人なるキョンシーを送る途中、弟子たちのミスでそのキョンシーを盗まれてしまう。盗まれたキョンシーはイギリスの考古学者の手によって薬をうたれ、最強のキョンシーへと変身する。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GROUND　CONTROL", "グランド・コントロール 乱気流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アリゾナ州に冬の嵐が迫り、フェニックス空港の管制塔が大忙しとなる。非番の管制官を動員しても人手が足りず、飛行機墜落事故の過去を悔やむ元管制官ジャックも現場復帰することに。しかし彼を待ち受けていたのは、大惨事につながりかねない予期せぬトラブルだった。「乱気流　タービュランス」とは、内容的にも無関係なサスペンス・ドラマ。ほとんど全編、管制官が飛行機を誘導するコントロール・ルームを舞台にしているためスペクタクル描写は皆無だが、密室的な緊迫感を重んじた点に好感が持てる。ビデオタイトルは「乱気流／グランド・コントロール」。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忘れられない、あの夏", "忘れられない、あの夏", "", "", "http://image.eiga.k-img.com/images/movie/55759/315x210.jpg?1913", 0, "東シナ海に浮かぶ亜熱帯の小さな島で暮らす漁師の香織は、女友だちと秘密の島と呼ばれる無人島に遊びに行くことに。たまたま映画の撮影で島に滞在していた人気俳優のトムと下火の中年女優の奈々子も一緒に無人島へ上陸するが、船が流され遭難してしまう。やがて7人のサバイバル生活が始まるが、香織は自分勝手でわがままなトムと激しく対立する。「恋人はスナイパー」の六車俊治監督がメガホンをとる。", "2010-10-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クローンは故郷をめざす", "クローンは故郷をめざす", "", "", "http://image.eiga.k-img.com/images/movie/54209/315x210.jpg?3489", 110, "近未来。宇宙ステーション建設作業中に事故死した耕平は、クローン技術によって蘇る。しかし、双子の弟を亡くした幼少期の記憶しか持たずに再生し、オリジナルの自分の遺体を弟と思い込んだ耕平は、遺体を担いで故郷を目指して歩きはじめる。自主映画で高評価を受けてきた中嶋莞爾監督が、06年サンダンス・NHK国際映像作家賞を受賞した自身の脚本を、及川光博主演で映画化。同賞の審査員だったビム・ベンダースがエグゼクティブ・プロデューサーを務める。", "2009-01-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASKERADE", "たそがれの維納〈ウィーン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "20世紀初頭のウィーン。美男の誉れ高い流行画家ハイデネックと、彼を取り巻く３人の女の恋のさや当て。その危機的状況をまるく収めたのは聡明な田舎娘の計らいだった……。渋く苦い大人のロマンティシズムを香り高く描いた、ハイソサエティ・ドラマの古典的名作。", "1934-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレヨンしんちゃん　爆発！ 温泉わくわく大決戦", "クレヨンしんちゃん　爆発！ 温泉わくわく大決戦", "", "", "http://ecx.images-amazon.com/images/I/61W9FSqctdL._SL210_.jpg", 99, "人気アニメの劇場版シリーズ第７作。ひょんなことから騒動に巻き込まれたスーパー園児、野原しんのすけとその一家が、地球と家族の平和を守るために怪獣と戦ったり、ハチャメチャな奮闘を繰り広げる。悪の科学者アカマミレは地球を温泉で沈めるという途方もない作戦を思いつく。これを阻止するため、世界の温泉の平和を守る温泉Gメンは不思議なパワーを秘めた金の魂の湯＝きんたまの湯を探していたが、なんとそれは野原家の地下に眠っていた……。", "1999-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dead-Bang", "サンタモニカ・ダンディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "　警官殺害事件の調査に乗り出すロス市警・殺人課の刑事ベック。やがて彼は白人至上主義組織の一員バーンズにたどり着く。しかしそこにFBI捜査官のクレスラーが介入。ベックはクレスラーと対立しながらも、バーンズを追うことに……。実在する刑事の体験を基にしたサスペンス・アクション。テレビシリーズ「マイアミバイス」で人気絶頂だったドン・ジョンソンが、精悍だが、人間的な弱さを持つ刑事役に扮している。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　ルビーの願い", "それいけ！アンパンマン　ルビーの願い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "劇場シリーズ15周年の本作は、工藤静香が声優を担当するオーロラの国のルビーが登場。不思議な石を探してアンパンマンが大冒険を繰り広げる。オーロラの国のルビーはワガママが過ぎて国外に置いてけぼりにされてしまう。国に戻るため、願いの叶う石を探す彼女はアンパンマンたちと出会う。事情を聞いたアンパンマンは、彼女を手助けするが……。同時上映作品は「怪傑ナガネギマンとドレミ姫」。", "2003-07-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殯の森", "殯の森", "", "", "http://image.eiga.k-img.com/images/movie/1949/315x210.jpg?4153", 97, "「萌の朱雀」「沙羅双樹」の河瀬直美監督が、“生と死”をテーマに認知症の男性と介護士の女性の交流を描き、第60回カンヌ国際映画祭でグランプリに輝いた人間ドラマ。奈良県山間部のグループホームで暮らすしげきは、33年前に亡くなった妻の思い出と共に穏やかな毎日を過ごしていた。そこに介護福祉士としてやって来た真千子もまた、事故で我が子を失い大きな喪失感を抱えていた。ある日2人は、しげきの妻が眠る森を訪れるが……。", "2007-06-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Broken English", "ブロークン・イングリッシュ", "", "", "http://image.eiga.k-img.com/images/movie/53649/315x210.jpg?2696", 98, "「フェイシズ」「オープニング・ナイト」の名匠ジョン・カサベテスと、女優ジーナ・ローランズの実娘であるゾエ・カサベテスの劇場デビュー作品。主演は「バスキア」「クリスティーナの好きなコト」のパーカー・ポージー、「ぼくを葬る」「ブロークン」のメルビル・プポー。ニューヨークのホテルで働く30代独身のノラは、恋愛がうまくいかず、母親に将来を心配されながら仕事に追われる日々を送っていた。そんなある日、友人のホームパーティで情熱的なフランス人のジュリアンと出会う。", "2008-12-13", "1,2,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUEBERRY HILL", "ブルーベリー・ヒル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1950年代のベルギーの町。ハイスクール５年生のロビンや他の生徒たちは夏休みを目前にしていた。ある日、ロビンはかつての恋人が他の男と歩いているのを見かける。まだ彼女のことが忘れられないでいるロビンは、彼女の姿を求めて街をさまよう。青春の愛や友情、反抗を描くベルギー版「アメリカン・グラフィティ」。", "1989-01-01", "37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADY JANE", "レディ・ジェーン 愛と運命のふたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "舞台は16世紀、チューダー王朝時代のイギリス。王位継承の画策に自らの運命をほんろうされ、わずか９日間の女王となった16歳の少女、レディ・ジェーン・グレイと彼女の夫との短くも波乱に富んだ人生を描くコスチュームものの作品。ヘレナ・ボナム・カーターの映画デビュー作。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUNNING BRAVE", "ロンリーウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "高校陸上の花形、ビリーは、スポーツ奨学生として大学に迎えられたが、インディアンと白人の混血である彼は、人種を理由に学生クラブにも入会できなかった……。東京オリンピックの１万メートルで見事に優勝を飾ったビリー・ミルズの半生を描いた作品。", "1983-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Drawing Restraint 9", "拘束のドローイング9", "", "", "http://image.eiga.k-img.com/images/movie/56569/315x210.jpg?3840", 135, "アメリカの現代美術アーティスト、マシュー・バーニーが、約20年にわたって挑み続けている自身のライフワーク「拘束のドローイング」を映像化した異色のラブストーリー。実生活でのパートナーでもある歌手のビョークを共演に迎えて日本ロケを敢行。茶道や捕鯨といった日本文化をモチーフに、捕鯨船で西洋の男女が恋に落ちる様子を、鮮烈な映像美とビョークの生命力あふれる歌声によって描いていく。", "2006-02-11", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愉しき哉人生", "愉しき哉人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "落語界出身の喜劇俳優・柳家金語楼を主人公に配した成瀬巳喜男監督の喜劇。ある町に現れた父娘３人が、いがみ合う町内の人々に人の和を説き、隣組の重要性を訴え、いずこへともなく去っていく。戦時中らしく国策的な内容だが、あまりに楽天的なドタバタ喜劇であるため、逆説的な意味合いすら感じられる。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD女（ガール）", "BAD女（ガール）", "", "", "http://ecx.images-amazon.com/images/I/51rFBk28JhL._SL210_.jpg", 82, "", "2012-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FRESHMAN", "ドン・サバティーニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "名優M・ブランドが出演したコメディ。自らの当たり役「ゴッドファーザー」のビトー・コルレオーネをパロっているが、笑いの方はもう一つ。バーモントからニューヨーク大学映画科に入るためにやってきた青年クラークは、いきなり置き引きにあって無一文に。しかし、その彼をマフィアのドンが気に入ってしまい……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOING UNDERCOVER", "マイ・ディア・ボディガード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "破格のギャラにつられて富豪のデラハント夫人の依頼で、令嬢のヨーロッパ旅行のお目付け役を引き受けた二流探偵のヘンリー。ところが娘マリゴールドはブルジョワ特有のナマイキ娘で彼は大いにクサるが……。L・トンプソンのわがままぶりとベテラン女優J・シモンズの貫禄ある演技が見もののコメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("椅子／THE CHAIR", "宝物の椅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "地方の新聞社で40年間校正係を務めたクー老人は、定年退職となり、長年使った椅子をもらおうとするが、青年が座った時に壊れてしまう。帰る途中、クー老人は骨董屋で見た椅子を気に入り、退職金をはたいて買う。家に帰ると、二人暮らしの娘のミルが退職金を使ったことに怒りだす。次の日、骨董屋に行くが返品には応じてもらえず、青空市場で売りに出しても誰も買ってくれなかった。仕方がなく、椅子を庭に放って置くクー老人。そんな中、博物館で椅子が盗まれ、クー老人が疑われていることがわかるが、椅子はどこかへ消えていた。老人と椅子をめぐる出来事に人々が右往左往する様を描いた風刺喜劇。", "1996-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LOST WORLD：JURASSIC　PARK", "ロスト・ワールド／ジュラシック・パーク", "", "", "http://ecx.images-amazon.com/images/I/61wT5LmJNmL._SL210_.jpg", 129, "メガヒットを飛ばした「ジュラシック・パーク」の続編。前作同様、M・クライトンの原作をS・スピルバーグがスケール感豊かに映像化した。恐竜を現代に蘇らせた史上最大のテーマパークの失敗から４年。コスタリカ沖の孤島で家族とともに休暇を楽しんでいた少女が、奇妙な動物に襲われる。実はこの島は生産された恐竜たちが自然繁殖していた“サイトB”と呼ばれる島だった。４年前の生き残りの数学者マルコムは、かつて“ジュラシック・パーク”を計画したハモンドに調査を依頼されるがかたくなに拒否。しかし恋人の古生物学者サラがすでに島に向かったことを知り、カメラマンとメカニックを率いて後を追うのだが……。大群で襲いかかる小さな恐竜など、前作では見られなかった種も続々登場。暴利をむさぼる権力者たちとそれを阻止しようとする人間たちの確執、我が子を守ろうとする恐竜の姿など、ドラマ部分も濃密になっている。", "1997-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GROUND・地雷撤去隊", "THE GROUND・地雷撤去隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「SCORE」で脚光を浴びた室賀厚が監督を手掛けた異色アクション。東南アジアのリゾート開発地から地雷源が発見され、その撤去に囚人たちが駆り出された。保釈と引き換えに作業を志願した彼らは、死と隣り合わせの緊迫した状況のなか、懸命の作業に挑む。ゆうばり国際ファンタスティック映画祭98では、冒険映画賞を受賞。DVDは「JUNK・死霊狩り」を併録。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESK SET", "コンピュータとミス・ワトスン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "S・トレーシー＆K・ヘプバーンという、ハリウッドで名コンビと評判だったカップルによる恋愛コメディ。ネットワーク・テレビ局の調査部主任のバニー女史。ところが局は新しく、コンピュータの天才サムナー博士を雇い入れ、彼女の仕事を能率的にしようとしてきた。最初は仲が悪かったバニーとサムナー博士だったが、やがてお互いに恋心を抱くようになり……。IBMの全面協力により、当時の最先端をいくOA機器に加えて、L・シャムロイの撮影によるスコープサイズを生かした画面作りが見もの。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香港有個荷里活", "ハリウッド★ホンコン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "香港のフルーツ・チャン監督が、「小さな中国のお針子」のジョウ・シュンを主演に据えた不思議な味わいのドラマ。大都会の狭間の下町に暮らす人々と、大陸からやってきた美しい女の子の交流を綴る。香港のダイホム村。焼き豚屋を営むチュウ一家、チンピラ青年ウォンの前に、上海からやってきた美しい娘が現れる。彼らは上海娘の妖しい魅力の虜になるが、やがて彼女の意外な素性が明らかになる。", "2001-01-01", "2,6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　ペ・ヨンジュン3D in 東京ドーム 2009", "劇場版　ペ・ヨンジュン3D in 東京ドーム 2009", "", "", "http://image.eiga.k-img.com/images/movie/55449/315x210.jpg?1652", 103, "2009年9月、東京ドームで行われたペ・ヨンジュン来日イベントの模様を6台のハイビジョンデジタル3Dカメラで撮影し、厳選した映像を編集。イベント内容はアニメ「冬のソナタ　もうひとつの物語」、ペ・ヨンジュンが約1年間にわたり韓国各地の伝統文化を体験取材し執筆した著書「韓国の美をたどる旅」が軸になる。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狂熱の季節", "狂熱の季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "河野典生の小説『狂熱のデュエット』をもとに、虚無感から無軌道に生きる若者たちの姿をビート・ジャズの演奏に乗せて鮮烈に描く。少年鑑別所を出た明と勝は途中でユキを拾い海へと向かった。３人は海岸で明を鑑別所に送りこんだ新聞記者の柏木と恋人の文子を襲う。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sick House", "シックハウス", "", "", "http://image.eiga.k-img.com/images/movie/53999/315x210.jpg?3182", 90, "野心的な若い考古学者のアンナは、多数の幼児が犠牲になった中世の疫病に関する秘密が、今は閉鎖された古い病院にあることを突き止める。しかし、その病院で病原菌が発見されたという理由で、突然解体されることになる。なんとしても現場を調べたいアンナは、解体前夜に病棟へ忍び込むのだが……。廃墟寸前の病院を舞台に繰り広げられるシチュエーション・ホラー。主演は「ジーパーズ・クリーパーズ」のジーナ・フィリップス。", "2007-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEET THE FEEBLES", "ミート・ザ・フィーブルズ 怒りのヒポポタマス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「乙女の祈り」で一躍、世界的に注目されたニュージーランド出身のP・ジャクソンによる35ミリデビュー作。登場人物はすべてパペットという設定の中に、肉片や汚物が飛び散るスプラッタ・ムービー。ノスタルジックな音楽とパロディーで見せる異色娯楽作。", "1989-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ANNE´E DES ME´DUSES", "サロメの季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "南フランス、コート・ダジュール。18歳のクリスは母親のクロードと毎夏をここで過ごす。クロードはとても38歳には見えない美しさとけだるさを持っていた。クリスは男に魅力をふりまくことも、どうすれば男を思い通りに操ることができるかも心得ていた。彼女は大人たちと同じように十分、ビーチの社交生活を堪能していた。しかし、一人だけ思い通りにならない男、ロマン。クルーザーで生活するジゴロのロマンがつき合っているのは、母のクロード。いつしかロマンに愛とも憎悪ともつかぬ感情を抱いたクリスは……。フランス人らしい心理のあやを官能でえぐる異色作。スイスの名手R・ベルタのカメラがまぶしい。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("砂時計", "砂時計", "", "", "http://image.eiga.k-img.com/images/movie/53299/315x210.jpg?2629", 121, "両親の離婚により、母の故郷である島根県に引っ越した14歳の杏。同学年の大悟や藤と仲良くなり、ようやく自分の居場所を見つけることができた矢先、人生に疲れ果てた母が突然自殺を遂げ……。テレビドラマ化も話題を呼んだ芦原妃名子の大ヒットコミックを、「いぬのえいが」の佐藤信介監督のメガホンで映画化。少女時代の杏を「うた魂」の夏帆、成長した彼女を「未来予想図／ア・イ・シ・テ・ルのサイン」の松下奈緒が演じる。", "2008-04-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN　AFFAIR", "情事・an affair", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "建築家の夫、10歳になる息子とともに平穏な家庭生活を営んでいるソヒョン。そんな彼女の前に、アメリカに住む妹の婚約者ウインが現れる。仕事が忙しい妹の代わりに彼らの新居を探してやることになったソヒョンは、次第にウインと心惹かれ合うようになる。本作で10年ぶりに映画界に復帰した美人女優イ・ミスクが、年下の男性との情事に心揺れるヒロインを情感豊かに演じた不倫ドラマ。", "1998-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("そのときは彼によろしく", "そのときは彼によろしく", "", "", "http://image.eiga.k-img.com/images/movie/33999/315x210.jpg?2782", 114, "「いま、会いにゆきます」の原作者・市川拓司による同名小説を、長澤まさみ、山田孝之、塚本高史ら注目の若手俳優を主演に迎えて映画化したラブ・ストーリー。13年ぶりに再会を果たした幼なじみ3人の恋と友情を綴る。水草の店を営む智史の元に現われたトップモデルの鈴音。彼女が幼なじみの花梨だと知った智史は再会を喜びあい、もう1人の幼なじみである佑司に会いに行く。しかし佑司は事故で昏睡状態に陥っており……。", "2007-06-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天一坊と伊賀之亮", "天一坊と伊賀之亮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "日本映画の父と称されるマキノ省三が、グリフィス、チャップリンらによるユナイテッド・アーティスツ型の自由な芸術創作活動を目指して設立した、聨合映画芸術家協会製作の作品。監督はマキノ省三のもとで数多くの作品を演出してきた衣笠貞之助で、彼の代表作「狂った一頁」の前作にあたる。市川猿之助との契約条件の関係で、撮影日数10日で撮影された。", "1926-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BAD SEED", "悪い種子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "主婦クリスティーンは、娘ローダの同級生が死んだという知らせを聞く。その事件にどうもローダが関係しているらしい。以前ローダはガラス玉欲しさに老婆を殺したことがあったのだ。今回の事件もローダが犯人だとしたら……。ショックを受けるクリスティーンは、やがて家系に隠された血ぬられた事実を知る。一見純真無垢な少女の心の中に潜む邪悪さを不気味に描き出したサイコ・スリラーの傑作。「エクソシスト」や「オーメン」など子供が悪魔に変貌するホラーものの先駆け的作品で、主演のP・マコーマックが可愛らしい少女と冷酷な殺人鬼という二面性を見事に表現して、観る者をぞっとさせる。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ソフトボーイ", "ソフトボーイ", "", "", "http://image.eiga.k-img.com/images/movie/54909/315x210.jpg?2995", 0, "「ライアーゲーム」「ソラニン」の永山絢斗が初主演を務める、実話がベースの青春映画。共演に「武士道シックスティーン」の賀来賢人、「マリア様がみてる」の波瑠ほか。密かにフレンチシェフになることを夢見る佐賀の男子高生オニツカは、県内にソフトボール部がないため、創部するだけで全国大会に出場できるソフトボール部を作りたいという幼なじみのノグチに振り回され、部員集めに奔走していた。しかし、女子からもてたい一心で結成されたチームも次第に一丸となり、全国大会に向けての猛特訓が始まる。", "2010-06-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天羅地網／GUNMEN", "ガンメン 狼たちのバラッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "かつて戦地で戦った麻薬王ヘイを追う、上海警察隊のディン。敵の動きに手を焼く彼は、かつての戦友３人を集めて特別捜査部隊“ガンメン”を編成し、ヘイに宣戦布告する。香港アクションのパワーを見せつけるハードな一作。「愛人／ラマン」で世界的な俳優となったL・カーファイのワイルドな魅力も光る。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男じゃないか・闘志満々", "男じゃないか・闘志満々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "TVの青春もので台頭した森田健作が、国民的人気を得ていた時の映画主演作。大学でボクシング部に所属している武夫はある日少女剣士の京子に剣道で敗れ、自ら転部して剣道を始める。やがて学生選手権が始まって……。父親役のフランキー堺が笑わせる。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NIGHT WE NEVER MET", "ロマンスに部屋貸します", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ニューヨーク、グリニッジビレッジの古いアパート。エレン、サム、ブライアンの３人は、一室を週２日ずつシェアするという方法で、心休まる“隠れ家”を手に入れた。この３人の間にはやがて恋心が芽生えていくが、お互いの顔を知らないために、とんでもないハプニングが起こる。果たして３人の恋の行方は？ソーホーやセントラルパークなど、ニューヨーカー御用達のスポットで、スタイリッシュなロマンスが展開する。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("M−１グランプリへの道／　まっすぐいこおぜ！", "M−１グランプリへの道／　まっすぐいこおぜ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“お笑いブーム”の流れを受けて誕生した本作は、若手漫才師の祭典“M-１グランプリ”に懸ける芸人たちの姿を、笑いと涙で綴った青春映画だ。ダイノジやラーメンズ、エレキコミックのほか、現役で活躍中の芸人たちが一挙出演し、日常から舞台に至るまでをリアルに体現している。", "2004-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WRONG ARM OF THE LAW", "新・泥棒株式会社", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "表稼業はブティック経営、ウラに回ると泥棒稼業のオーソリティ。だが、恐れを知らぬオーストリアの泥棒３人組が出稼ぎにやって来た。察知したオーソリティは事業を守るため泥棒株式会社を結成する……。P・セラーズ主演のコメディの一編。", "1962-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA GRANDE VADROUILLE", "大進撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "パリで大ヒットしたフュネスとブールヴィルの喜劇映画。第二次大戦末期のパリ。ドイツ軍に追われている３人のイギリス軍パイロットをかくまう、オペラ座の指揮者とペンキ屋二人。このドタバタコンビが、奇想天外な活躍をする痛快娯楽作。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOCK WAVES", "ショック・ウェーブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "サーフィン映画の数々を世に送り出してきた名手ボブ＆ロンのコンドン・ブラザースの大作。なんと、３年の歳月をかけて製作したというフィルムには、世界のトップ・サーファーたちの迫力あふれる究極のテクニックが満載されている。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRACES OF RED", "赤い殺意", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "表と裏の顔を使い分ける人々の、様々な思惑が複雑に絡み合うサスペンス。フロリダの高級リゾート地を揺るがす連続殺人事件を縦糸に、女癖の悪い刑事と彼を殺人犯と疑うその相棒、彼らを魅了する妖艶な未亡人の駆け引きをスリリングに描く。L・ブラッコがミステリアスな悪女を熱演。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("炎のごとく", "炎のごとく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 147, "遺作「ざ・鬼太鼓座」がドキュメンタリーのため加藤泰にとっては最後の劇映画。男と女の情念をチャンバラや任侠の世界に描いてきた加藤の集大成とも言える名作である。会津の小鉄と異名をとる一人の博奕打ちが瞽女・おりんを愛しながらも新撰組の近藤勇と出会い、幕末の動乱の中に身を投じていく様をダイナミックに描いてみせた。ワンカットにつき、時には４時間もかけて粘って画面を作った加藤の演出は、TVのインスタント時代劇には見られない骨のあるものを生み出した。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("顔", "顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "松本清張の中編をベースに映画化した、サスペンスフルなミステリー。ファッション・モデルの栄光をつかみ取るために、汚れた過去を知る男を列車から落として殺した女。だが犯行の直前、列車の洗面所で一人の男に顔を見られていた。男はふとしたことから事件を知り、目撃者として警察に出頭する……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MULHOLLAND FALLS", "狼たちの街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1950年代初頭、ロサンゼルスは朝鮮戦争後の繁栄に沸き、全米中から人と金が流れ込む一方で、それを狙って組織犯罪が激増していた。ロス市警はその魔手から街を守るため、政治力にも屈しない対組織犯罪特別捜査班“ハット・スクワッド”を結成した。彼らが直面した最大最後の事件は、ある女の死から始まる。死んだのは特捜班のリーダー、マックス警部補の元愛人アリソン。その後、彼女と原子力委員会会長トーマスとの情事を盗撮したフィルムが届き、フィルムを撮影した男も殺される。特捜班はその存亡をかけ、国家的な謀略に挑むことに……。凶悪な組織犯罪に敢然と立ち向かった４人の実在の男たちを、豊かな映像で描き上げたクライム・ロマン。N・ノルティ、M・グリフィスをはじめ豪華スターによる競演も話題を呼んだ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WELCOME HOME，ROXY CARMICHAEL", "悲しみよさようなら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ハリウッドで大成功を収めた伝説の大女優が、故郷に里帰りすることになった。彼女は女優を志して、この町に夫と子供を残してきたのだった。自分が彼女の子だと信じてやまない15歳のディンキーは、彼女が自分をきっとこの町から連れ出してくれると、淡い期待を抱いていたが……。当時、19歳のW・ライダーが初々しい魅力を放つ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRUNO", "ぼくが天使になった日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "事故に遭い、臨死体験をした10歳の少年が悟りをひらき、自分の本当の居場所と真理をみつけようとするファンタジー。大女優で宗教家としても有名なS・マクレーンが初メガホンを執り、10年来温めてきたオリジナル・ストーリーを映画化した。マクレーンの宗教哲学的な色濃い作品ながら、しみじみとした感動を与えてくれる。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴーストシャウト", "ゴーストシャウト", "", "", "http://ecx.images-amazon.com/images/I/51eiiZnQ5bL._SL210_.jpg", 103, "この世には現世への未練を抱えた幽霊がいっぱい。そんな幽霊たちの通訳をするというユニークな職業、ゴーストネゴシエイターの活躍を描くロマンティック・コメディ。自分の恋はままならず、幽霊騒動に借り出されては他人の恋を取り持つヒロインを、ミス・ユニバースジャパンの準ミスに輝いた滝沢沙織が快活に好演。彼女をはじめ、フレッシュな若手スター陣が結集している。幽霊とコミュケーションを取り、彼らの通訳となるゴーストネゴシエイターのヨウコは、引退して恋人と結婚することを夢見ている。ところが、彼女の助けを求める急な仕事が入り……。", "2004-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pippi on the Run", "長くつ下のピッピ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「ロッタちゃん」シリーズでおなじみのアストリッド・リンドグレーンによる原作を基に、赤いおさげとそばかすがトレードマークの“世界一強い女の子”ピッピが仲間たちと繰り広げる大冒険を描く。お母さんに叱られて、ついに家出を決意したトミーとアニカ。彼らの隣家“ごたごた荘”で暮らすピッピも一緒に行くことになる。途中で会ったコンラッドさんから、世界一強力なノリをもらった3人は、楽しい家出旅行を続けるが……。", "1970-01-01", "14,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blown Away", "ブローン・アウェイ 復讐の序曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "ボストンの街を恐怖に陥れる連続爆破事件が起き、爆発物処理部隊のダブは、その巧妙な手口からプロの仕業であることを察知する。犯人は、アイルランドの刑務所から脱獄したテロリスト、ギャリティ。彼の狙いはダブへの復讐だった。ダブはアイルランドでテロ組織を裏切った過去をひた隠しにしていたのだ。彼の周囲の人々が次々と爆弾の餌食となり、危険はやがてダブの身にも迫る……。工夫を凝らした爆弾の設定と、迫力のある爆破シーンが見もののサスペンス・アクション。主人公と犯人の息詰まる攻防が緊迫したシチュエーションを盛り立てる。「逃亡者」でオスカーを受賞したT・L・ジョーンズの狂気の悪役ぶりも秀逸。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE WISSELWACHTER", "ポインツマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "汽車から、雪降る荒野に誤って途中下車した一人の美しい女。彼女はそこで列車のポイント切り替え係の男と知り合い、一緒に暮らし始める……。オランダ映画界注目の“映像詩人”J・ステリングが奏でる、孤独な男女の不条理な恋愛模様。ミシェル・ムルダーの旋律が印象に残り秀逸。", "1987-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHEYENNE AUTUMN", "シャイアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 158, "病気と飢えに苦しめられ、ついにインディアン居留地を出て故郷イエローストーンへの帰還を目指すシャイアン族。その中には、子供たちに読み書きを教えている白人娘デボラもいた。一方、インディアンの居留地脱出の報を聞き、合衆国騎兵隊は武力制圧の暴挙に出る。隊にはデボラとの結婚を望み、シャイアン族に同情を寄せるアーチャー大尉の姿もあった。白人たちの猛攻によって次第に追い詰められていくシャイアン族。やがて厳しい冬が訪れ、彼らは全滅の危機を迎えるが……。西部劇の巨匠J・フォード監督が晩年に撮った、オールスター競演の70ミリ超大作。部分的にセット撮影のE・G・ロビンソンと、ロケ撮影のR・ウィドマークが平然とカットバックされるなど、荒っぽい作りが目立つ。が、70ミリの画面いっぱいに展開されるスペクタクル描写には感動必至。滅びゆく民族を扱った社会派的な味もある佳編。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DU　RIFIFI　CHEZ　LES　HOMMES", "男の争い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "フランス産のフィルム・ノワール（＝犯罪映画）を代表する傑作。50年代のハリウッドの“赤狩り”旋風に遭ったJ・ダッシン監督が、再起を懸けて作り上げた。音だけによる30分間にわたる無言の金庫破りや、ギャング同士の凄まじい銃撃戦などのシーンが強烈。刑務所を出所したヤクザが、さっそく仲間と金庫破りの綿密な相談をし、実行へと移して成功。だが、ギャング団が彼らに目を付けていた。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROADWAY　BILL", "その夜の真心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「素晴らしき哉、人生！」の名匠キャプラによる恋愛ドラマ。妻の父が経営する会社で肩身の狭い思いをしていたダンが、好きだった競馬の世界に夢を馳せ、家を出てしまう。困難に直面しながらも彼の持ち馬が優勝を果たすが、レース直後に馬が急死し、彼は途方に暮れる。そんな彼を励ましたのは、妻の妹アリスの存在だった。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ノストラダムスの大予言 Catastrophe-1999", "ノストラダムスの大予言 Catastrophe-1999", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "先祖から３代にわたってノストラダムスの『諸世紀』を研究してきた若い科学者が、その予言通りの人類最後の日の状況のなかに投げ出され、娘や恋人とともに苦悩する姿を、特撮を駆使してSF的に描く。取り分け大豪雨と鉄砲水の大襲来のシーンは圧巻。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WARLOCK", "ワーロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "暴れ者の集団のマックオウン一家がわが者顔に横行する町、ワーロック。住人たちは、たまりかねて自衛のために保安官クライを呼ぶことにした。だが、町にはクライのほかに賭博師のモーガンやクライを夫の仇と狙うリリーなどもやって来た。果たして町の運命は……。個性あふれるキャラクターが勢ぞろいした大作ウエスタン。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アレクセイと泉", "アレクセイと泉", "", "", "http://image.eiga.k-img.com/images/movie/40479/315x210.jpg?3642", 104, "初監督作「ナージャの村」が、世界各国で高い評価を受けた写真家・本橋成一の長編第２作。1986年に起きたチェルノブイリ原発事故で被災した、ベラルーシ共和国東南部に位置する小さな村ブジシチェ。現在では55人の高齢者と一人の青年だけという住人たちの日常を、淡々と綴るドキュメンタリーだ。透明感のある映像と、坂本龍一による音楽が美しく印象に残る。", "2002-02-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CADENCE", "ミリタリー・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "軍刑務所を舞台に、一人の白人兵が、頑固な所長と黒人兵たちの中で成長していく様を描く。父のマーティンが監督と所長役で出演、息子チャーリーが主役の兵士ビーンを演じる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Man Who Was Superman", "星から来た男", "", "", "http://image.eiga.k-img.com/images/movie/54129/315x210.jpg?4349", 106, "「マラソン」のチョン・ユンチョル監督が、「猟奇的な彼女」のチョン・ジヒョン＆「ユア・マイ・サンシャイン」のファン・ジョンミン共演で描くドラマ。テレビ製作会社でドキュメンタリー番組を作っている女性ディレクターのスジョンは、ひょんなことから自称スーパーマンの男と出会う。男の奇想天外な言動に興味を持ったスジョンは、彼を主人公に番組を作ることに。やがてスジョンは、男の頭の中に何かが埋まっていることを知る。「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。", "2008-09-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Glee: The 3D Concert Movie", "glee グリー　ザ・コンサート 3Dムービー", "", "", "http://image.eiga.k-img.com/images/movie/56849/315x210.jpg?2419", 84, "2009年の全米放送開始から瞬く間に世界的人気となった学園ミュージカルドラマ「glee グリー　踊る♪合唱部!?」のキャストによるコンサートツアーの模様を3D映画化。番組の代名詞でもあるジャーニーの「Don't Stop Believin'」やブリトニー・スピアーズ、ビヨンセ、マイケル・ジャクソン、レディー・ガガらの人気ナンバーが次々と披露される。迫力のライブ映像のほかツアーの舞台裏の様子も収めた。", "2011-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A｀ LA FOLIE", "彼女たちの関係", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「ニキータ」のA・パリロー、「ベティ・ブルー　愛と激情の日々」のB・ダルが熾烈な演技合戦を見せるラブ・サスペンス。恋人と幸せな同棲生活を送っている芸術家のアリスと、彼女の許に転がり込んできた威圧的な態度の姉エルザ。そんな彼女たちの屈折した愛憎模様を、二人の間で揺れる青年との三角関係を絡め、アパートの一室を舞台にスリリングに描き出す。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cutie Honey キューティーハニー", "Cutie Honey キューティーハニー", "", "", "http://image.eiga.k-img.com/images/movie/1349/315x210.jpg?3140", 93, "アニメにもなった永井豪の同名人気コミックを「新世紀エヴァンゲリオン」の庵野秀明監督が実写映画化。普段は明るくドジな派遣OL如月ハニーは、天才科学者如月博士が作った無敵のパワーを持つアンドロイド。ハニーのパワーの秘密を狙う秘密結社パンサークローが、如月博士の後継者、宇津木博士を誘拐、ハニーは悪に立ち向かう。特撮監督は「ガメラ1」「ガメラ3」の神谷誠、特殊メイクは「ガメラ」や「さくや妖怪伝」の原田智生。", "2004-05-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("発熱天使", "発熱天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ドキュメンタリーとも、フィクションとも言い切れないボーダレスな１本。失踪した友人を捜すため、北京にやってきた一人の青年。１枚の写真を手がかりに捜索を続ける彼は、中国で若いアーティストたちと出会い、語らううちに、驚くべき真実を知る。画面に登場する画家、ミュージシャン、CFプロデューサー、そして性転換したダンサーはいずれも実在の人物。椎名桔平扮する主人公は、彼らと食事をともにし、酒を酌み交わす。単なるインタビューにとどまらない、あたかもセッションのようなコミュニケーションから、アーティストたちの本音が浮き彫りになっていく。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RISING STORM", "ライジング・ストーム 2099A.D.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "自ら“神”と名乗る独裁者が支配する恐怖の未来社会を相手に立ち上がる４人の男女の活躍を描く、近未来SFアクション。“ライジング・ストーム”とは、世の中すべてを覆す威力を持つ砂漠の大嵐のことである。Z・ギャリガンほか若手スターたちの共演も見どころの一つ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の視線", "風の視線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "松本清張の同名小説の映画化。愛のない結婚生活を続けている夫婦２組の物語。海外に赴任中の夫を待つ亜矢子と、彼女に惹かれつつも千佳子と見合い結婚をしてしまった新進カメラマン・奈津井。亜矢子の夫の帰国で事態は急展開をみせる……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ごくせん THE MOVIE", "ごくせん THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/54489/315x210.jpg?2014", 116, "型破りな熱血教師・ヤンクミを仲間由紀恵が熱演し、数多くの若手俳優を輩出した人気TVシリーズ「ごくせん」の劇場版。原作は森本梢子のコメディ漫画で、TVシリーズに引き続き亀梨和也、三浦春馬、小栗旬、速水もこみちら三代に渡る3年D組の面々が総出演。赤銅学院高校で教師を続けるヤンクミこと山口久美子のもとに、教え子の小田切が教育実習生としてやってくる。自分に憧れたのだと勘違いして喜ぶヤンクミだったが、ある日、3年D組に暴走族が乗り込んできて……。", "2009-07-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小梅姐さん", "小梅姐さん", "", "", "http://image.eiga.k-img.com/images/movie/53859/315x210.jpg?2993", 76, "日本を代表する芸者・赤坂小梅の生誕100周年を記念して製作されたドキュメンタリー。小梅は、大柄な体格と豪快な性格、芸者として鍛えた巧みな歌唱力で、昭和56年の引退公演まで第一線で活躍し、全国各地の数多くの民謡を歌った。小梅が生まれ育った筑豊・川崎町の風土や、芸者修行に明け暮れた北九州を訪ね、関係者のインタビューなどを交えながら、唄を愛し唄に尽くした女の生き様を描く。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Keeper", "ザ・キーパー／監禁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "監禁された若い女性と、監禁した初老の警部補の駆け引きを描くサイコ・サスペンス。個性派デニス・ホッパーの狂気的な怪演が、ドラマのスリルを見事に高めている。より狂気を帯びたキャラを登場させる意外性のある展開や、田舎の閉塞的な空気感も鮮烈な印象を残す。恋人を殺されたストリッパー、ジーナ。病的なまでにしつけにうるさい警部補クレブスは、奔放な彼女を更生させようと自宅に監禁し……。", "2005-02-05", "10,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ番外地", "やくざ番外地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "丹波哲郎主演によるヤクザ映画。野見組は、多摩市の高瀬組を傘下に入れるべく、大幹部・村木に高瀬組壊滅を一任する。村木は愚連隊を手下に高瀬組をいためつけるが、高瀬組の代貸・塚田はかつて村木と兄弟の盃をかわした仲だった……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("37°2 le matin", "ベティ・ブルー　愛と激情の日々", "", "", "http://image.eiga.k-img.com/images/movie/27389/315x210.jpg?3665", 121, "「ディーバ」のジャン＝ジャック・ベネックス監督が、1組のカップルがたどる衝撃的な愛の行方を鮮烈な映像で描き、本国フランスのみならず世界中に熱狂的ファンを生み出した名作ラブストーリー。海辺のバンガローで暮らす35歳の青年ゾルグは、セクシーな美少女ベティと出会い、瞬く間に恋に落ちる。一緒に暮らしはじめた2人は激しい愛欲の日々を送るが、情熱的すぎるベティの愛情表現は次第にエスカレートしていき……。1986年・第59回アカデミー賞では外国語映画賞にノミネート。2012年、日本初公開から25年を記念して監督自身の監修によるデジタル・リマスター版がリバイバル公開。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("深呼吸の必要", "深呼吸の必要", "", "", "http://ecx.images-amazon.com/images/I/51RD3CNZDVL._SL210_.jpg", 123, "沖縄でさとうきび収穫時に雇われる“きび刈り隊”。35日間できび７万本を刈り取らねばならない重労働だ。このアルバイトに応募してきた若い男女が、過酷な共同作業を通して、連帯感や達成感を感じ、人生の再スタートを切るまでを描く青春群像劇。篠原哲雄が登場人物の揺れ動く心情を、沖縄の豊かな自然の中に生き生きと映し出す。沖縄のとある離島。さとうきびの収穫を手伝う“きび刈り隊”に応募した若者５人がやって来る。それぞれ言いたくない事情を抱えた彼らは、お互いに心を開こうとしない。だが、雇い主の老夫婦の優しさに触れ、過酷な労働に明け暮れるうち、次第に連帯感が生まれていく。", "2004-05-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秋刀魚の味", "秋刀魚の味", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "巨匠・小津安二郎の遺作となった作品。小津自身は家庭を持たず、母と二人きりの生活を送ってきたのだが、その母をこの「秋刀魚の味」の構想中に失った。娘を嫁に出した父、あるいは母の孤独というのは小津作品に繰り返し現れるシチュエーションだが、真の孤独を味わった小津によって描かれたこの作品での父親役・笠智衆の、淋しさに震える背中は今までにないすご味がある。娘と暮らす初老のサラリーマンは、婚期の娘の結婚を心配する。娘には好きな相手がいるらしいが、はっきりしない。父は、同僚から娘の見合いを勧められる。縁談はもたつきながらやがてまとまり、娘は嫁いでいく……。どちらかといえば、軽いコメディ・タッチで作られているところが、逆に父親の孤独感を浮き彫りにして秀逸である。娘を演じた岩下志麻の快活さも新鮮で、原節子が演じてきたしっとりした感じとは違った味わいがある。この作品を最後に翌年、小津は60才の誕生日にその生涯を閉じた。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mystic River", "ミスティック・リバー", "", "", "http://image.eiga.k-img.com/images/movie/1129/315x210.jpg?3731", 138, "幼なじみの3人の少年は、ひとりが誘拐事件に遭ったことから、次第に離れていくことになる。だが、25年後、彼らは殺人事件を契機に再会する。ひとりは被害者の父、ひとりは容疑者、ひとりは刑事として。デニス・ルヘインの同名ベストセラー小説を「L.A.コンフィデンシャル」の名手ブライアン・ヘレゲランドが脚本化。撮影はイーストウッド組、「許されざる者」などの照明を経て「ブラッドワーク」を撮影したトム・スターン。", "2004-01-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2011-2012 ヴェルディ「エルナーニ」", "METライブビューイング2011-2012 ヴェルディ「エルナーニ」", "", "", "http://image.eiga.k-img.com/images/movie/57169/315x210.jpg?2980", 229, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を映画館で上映する「METライブビューイング」2011～12年シーズンの9作目。フランスの作家ビクトル・ユーゴーの戯曲に基き、イタリアの作曲家ベルディが手がけた傑作オペラ。16世紀初頭のスペイン。貴族出身の山賊の首領エルナーニは、スペイン大公シルバの許婚ドンナ・エルビーラと愛し合っていた。2人の結婚は国王により認められるが、これに傷ついたシルバ大公が復讐を企てる。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROZAC　NATION", "私は「うつ依存症」の女", "", "", "http://ecx.images-amazon.com/images/I/51KN9GWKH0L._SL210_.jpg", 99, "クリスティーナ・リッチが初めてプロデュースに乗り出した作品は“うつ病”というシリアスなテーマを扱った青春映画。1980年代の音楽シーンも再現されており、伝説のロッカー、ルー・リードも本人役で登場する。10代にして音楽ライターとして活躍するリジーは名門ハーバードに入学する。過度な期待をかける母親から逃れ、寮に入ってキャンパスライフを満喫する彼女は、セックス、パーティー、ロックンロールにハメを外すが、やがて持病のうつ病が悪化して……。", "2003-09-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I　MUVRINI", "イ・ムヴリーニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "「ベンゴ」「僕のスウィング」など、ドラマと音楽を見事に融合させた作品を手掛けるT・ガトリフによるドキュメンタリー。国際的に人気のあるバンド“イ・ムヴリーニ”に密着し、彼らのインスピレーションの源泉を探ね歩く。コルシカの伝統音楽ポリフォニーの起源を理解するため、グループの生まれ故郷である山頂の村にやって来たガトリフは、その地の魅力を肌で感じ、音楽に秘められたルーツを探る。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AIR AMERICA", "エア・アメリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ベトナム戦争を背景に、いつでも、どこへでも、運べるものなら何でもござれというアブない荷物専門の“謎の空飛ぶ宅配便会社”を主人公にした型破りなスカイ・コメディ。空撮アクションはもちろん、全編を彩る1960年代のロックンロールも聴きもの。M・ギブソンとR・ダウニーJr.が絶妙なコンビネーションを見せる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お坊主天狗", "お坊主天狗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "“いれずみ判官”片岡千恵蔵、“むっつり右門”大友柳太朗、“若さま侍”大川橋蔵に美空ひばりが花を添えた豪華時代劇。旗本を捨てた男と偏屈者の名研師が、悪代官を父の仇と狙う武家の出の芸者を助ける。悪代官の忠臣は迷いながらも、結局彼女に仇を討たせる。渡辺邦男監督の1954年作品のリメイク版。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("任侠秘録人間狩り", "任侠秘録人間狩り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "女性を誘拐、拉致して男に斡旋する犯罪組織に客として現れた一人の男。怪しい雰囲気の男に不審を抱きつつも、組織は彼を女の監禁場所へ案内する。徐々に明らかになる彼の素性と、その目的とは……!?　漫画家としてだけなく、俳優、ライター、レポーターなど多彩な活動をする杉作J太郎監督作。「怪奇！！幽霊スナック殴り込み！」と２本立てで公開された。", "2006-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("隣る人", "隣る人", "", "", "http://image.eiga.k-img.com/images/movie/57869/315x210.jpg?3285", 85, "「子どもたちと暮らす」ことを実践している児童養護施設「光の子どもの家」の日常に8年間にわたり密着したドキュメンタリー。埼玉県にある児童養護施設「光の子どもの家」は、可能な限り普通の建物で普通の暮らしを子どもたちに提供し、さまざまな事情で親と一緒に暮らせない子どもたちが、親代わりの保育士と生活している。親から愛情を受けることなく施設で暮らす生意気盛りのムツミと甘えん坊のマリナは、保育士のマリナさんを取り合ってケンカすることもしばしば。そんなある日、ムツミの母親が再び子どもと暮らそうという思いを胸に施設を訪れてくる。", "2012-05-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Trilogia: To Livadhi Pou Dhakrisi", "エレニの旅", "", "", "http://image.eiga.k-img.com/images/movie/1379/315x210.jpg?3864", 170, "「旅芸人の記録」のテオ・アンゲロプロス監督が、ロシア革命で両親を失ったギリシャ難民のエレニの半生を、ギリシャ現代史に重ねて描く映像叙事詩。少女エレニはロシア革命で両親を失い、ギリシャ人難民の一団を率いる男スピロの家族として育てられる。成長したエレニは、スピロの長男アレクシスと恋に落ちて妊娠するが、スピロスの叱責を恐れて彼に隠して出産するのだが……。ギリシャの20世紀を描く3部作のうちの第1作。", "2005-04-29", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paraiso", "Paraiso", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "小さな箱と、住所が書かれた紙。そんな父の“遺言”を携えてキューバにやってきたボーカリスト、栄子。気のいい日系三世のカルロスと出会った彼女は、手がかりもないまま、“MAKOTO”という謎の人物に箱を渡すため、奔走するが……。慣れない土地で、亡き父親の素顔を知り、成長するヒロインの心象風景を描いたロード・ムービー。全編キューバ・ロケを敢行。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROGOPAG", "ロゴパグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "1960年代に最も先鋭であった４人の監督によるオムニバス映画。“ROGOPAG”のタイトルは、４人の監督の名前の頭文字からとった。ロッセリーニの第１話「潔白」は、国際線のスチュワーデスに惚れ込んだ男の話。男の常軌を逸した惚れようが滑稽である。ゴダールの第２話「新世界」は、核爆発で一変してしまった世界の姿を描く。人類最後の男は、世界の変化のありさまを後世に伝えようと筆をとる。パゾリーニの挿話「意志薄弱な奴」は、撮影所でキリスト磔刑の映画を撮っている監督の話。撮影中、腹をすかした俳優が食べ過ぎて本当に十字架上で死んでしまう。この挿話は風刺がきいていていちばんの傑作。グレゴレッティの第４話「にわとり」は、マスコミに支配される現代のプチブル中年夫婦の話。", "1963-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WEDDING NIGHT", "結婚の夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "仕事に行き詰まった小説家が田舎へ行き、苦しみながら新作の執筆を始めるが、妻がニューヨークへ帰ると、隣家の娘と愛し合うようになる。しかし娘には婚約者がいた。都会から戻った妻は、夫の書く新作のヒロインに隣家の娘の面影を読み取る。そして愛の四角関係は悲劇的な結末へ……。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWILIGHT FILE・『影』『幻』『月光』", "TWILIGHT FILE・『影』『幻』『月光』", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "新進気鋭のクリエーターたちを迎えて贈るオムニバス作品集。ラブ・ミステリーという形式にこだわり、不思議な運命に翻弄される男女の物語を、3話構成でじっくり描いていく。金子昇や森下千里などフレッシュな若手俳優が集結。また、車だん吉が3話とも登場し、存在感のある演技を見せている。露天商をしている亮の前に小夜という謎めいた少女が現れる。惹かれあう二人だったが、彼女はある秘密を抱えていて……。(「月光」)", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("車夫遊侠伝・喧嘩辰", "車夫遊侠伝・喧嘩辰", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "義理と人情に厚く、ケンカっ早い車夫の物語。内田良平と桜町弘子の演技は最高で、それを受けた加藤泰の演出も冴えわたった。特にラスト近くの、雪の降る橋の上での結婚式のシーンは、オーバーラップを使った幻想的な画面で記憶に残る。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LISTA　DE　ESPERA", "バスを待ちながら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "大勢の人であふれかえる、キューバのある田舎町のバス停。しかし人々は何日も待たされ、たまに到着するバスは常に満席、ついにバス停は閉鎖されてしまう。そこで客の一人である若い技師が、故障したバスの修理を提案。残った客の間には、次第に奇妙な連帯感が芽生えてくる。不便な交通事情など、現代キューバの社会情勢をさりげなく盛り込んだ人情群像劇。決して豊かではないが、そんな現実を笑い飛ばすような、あっけらかんとしたキューバ人気質を、ユーモアあふれる描写で浮き彫りにする。", "2000-01-01", "49,9,2,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SAILOR WHO FELL FROM GRACE WITH THE SEA", "午後の曳航", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "三島由紀夫の小説を、舞台を外国に移し華麗な映像で映画化。海と船に憧れる少年ジョナサンと未亡人の母は、寄港した二等航海士ジムを理想の男と思う。しかしジムは海を捨てて母と結婚し、少年の夢は破れる。母と男のベッド・シーンを少年がのぞくところがいかにも淫び。", "1976-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("珈琲とエンピツ", "珈琲とエンピツ", "", "", "http://image.eiga.k-img.com/images/movie/57669/315x210.jpg?3776", 67, "静岡県湖西市にあるサーフショップ＆ハワイアン雑貨店「Surf House Ota」の店長で、ろう者の太田辰郎さんの姿を追ったドキュメンタリー。30年以上のキャリアをもつサーファーで、サーフボードを作る職人でもある太田さんは、店にやってきたお客さんに自身が愛飲するハワイのコーヒーを振る舞い、紙とエンピツで筆談を始める。大きな身振りや豊かな表情で人懐こく話しかける太田さんの姿を通して、「言葉を超えたコミュニケーション」の大切さを描き出していく。監督は自身もろう者の今村彩子。", "2012-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUTH PACIFIC", "南太平洋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "ロジャース＆ハマースタインII世のヒット・ミュージカルの映画化。南太平洋の小さな島を舞台に、従軍看護婦と島に住むフランス人の男の恋、海兵隊の若者と島の娘の恋を描く。A・ホールが歌う『バリ・ハイ』が、“魅惑の南太平洋”を見事に伝え、聞きごたえ十分。DVDは「ロジャース＆ハマースタイン コレクション」に収録。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("精神", "精神", "", "", "http://image.eiga.k-img.com/images/movie/54149/315x210.jpg?1307", 135, "“落下傘候補”や“どぶ板選挙”といった日本の地方選挙の内幕をとらえ、海外でも注目された「選挙」の相田和弘監督が、精神科を舞台にそこへ集う患者たちを真正面から捉えたドキュメンタリー。様々な人々が集まる精神科診療所“こらーる岡山”には、心を患い自殺未遂を繰り返す人や、病気と付き合いながら信仰や芸術を深めていく人もいる。心の傷を癒す精神科医療を通し、現代人の精神のありように迫る。", "2008-01-01", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PALEFACE", "腰抜け二挺拳銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "B・ホープとグラマー女優J・ラッセルの共演によるコメディ仕立ての西部劇。無痛治療を看板に掲げる歯医者のピーターと、西部にその人ありと謳われた女傑カラミティ・ジェーンとの珍妙な活躍を描く。挿入歌『ボタンとリボン』も大ヒット。シリーズとして作られたわけでないが、この作品がヒットしたため、B・ホープ主演のパラマウント映画には“腰抜け”の邦題がつけられることになった。なお、ビング・クロスビーと組んだ“珍道中”シリーズもある。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABE: PIG IN THE CITY", "ベイブ・都会へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "子ブタのベイブが活躍する大ヒット作の続編。前作でプロデューサーを務めたG・ミラーが監督に当たり、アクションとユーモア満載の大冒険を繰り広げる。不注意からホゲットおじさんに怪我を負わせてしまったベイブは、経営難に陥った農場を救うためイベントに出場することになる。おばさんに連れられて初めての大都会にやって来たベイブの胸は、不安と期待でいっぱい。いろいろな動物たちが暮らすホテルに滞在することになるが、そこでもベイブは様々なトラブルに巻き込まれ……。ベイブが猛犬に追いかけられたり、橋から宙吊りになったりと危険なシーンが続出。チンパンジーの家族や、個性的な犬たちなどユニークな動物の名演にも注目。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エレファントソング", "エレファントソング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "利重剛による＜J・MOVIE・WARS＞第２期の一編。33歳のウエートレス・水橋加奈子のもとに下月の死の知らせが入った。“死んだ時くらい何かの足しになりたい。せめて土に返してほしい”と言っていた彼との約束を果たすために、加奈子は、花屋の真鍋のトラックで伊豆山中へ向かう。山の中とはいえ、どこも所有者の立て札が立ち並び、人一人埋めるのも容易ではない。下月を背負い、雑木林の斜面を登る加奈子の肩に、死体の重みがのしかかる……。30代の人間が一つの約束を通して、自分の生の意味を問う姿を描く。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I STILL KNOW WHAT YOU DID LAST SUMMER", "ラストサマー2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "大ヒットしたショッキング・ホラーの続編。１年前の忌まわしい殺人事件の悪夢から立ち直れずにいる大学生のジュリーは、ルームメイトのカーラに誘われ、彼女の恋人と友人の４人でバハマへ旅行に出かける。ところが、島はハリケーンが接近中のため人影もまばらで、ホテルの従業員は怪しげな人ばかり。そんななか、ジュリーらに謎の殺人鬼が襲いかかる。前作で生き残ったJ・L・ヒューイットとF・プリンツJr.のほか、若手スターが勢ぞろい。逃げ場を失った孤島を舞台に、残虐な殺人鬼の恐怖をスリリングに描く。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Idade da Terra", "大地の時代", "", "", "http://image.eiga.k-img.com/images/movie/56299/315x210.jpg?1614", 151, "ブラジルの新しい映画＝シネマ・ヌーボの旗手として賞賛されたグラウベル・ローシャ監督の没後30年を記念し、日本初公開作を含む代表作を連続上映する「グラウベル・ローシャ・ベスト・セレクション」にて上映。バイーア、ブラジリア、リオデジャネイロを舞台に、夜明けの街並みやリオのカーニバル、政治状況の総括、ブラジリアの工事現場などブラジルの現在と過去を交錯させた映像と音でつづる。ローシャ監督の遺作。2011年日本初公開。", "2011-06-18", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミロクローゼ", "ミロクローゼ", "", "", "http://image.eiga.k-img.com/images/movie/56029/315x210.jpg?3187", 90, "マネキンを使った異色ドラマ「オー！マイキー」で人気を博した石橋義正監督が手がけるラブファンタジー。花屋に勤めるユリに一目ぼれしたタモンは、なんとかユリの気を引こうとするが、ある日突然ユリは強盗に連れ去られ姿を消してしまう。ユリを捜し求めるタモンはやがて時空を越え、波乱万丈の冒険を繰り広げる。主演の山田孝之が金髪おかっぱ頭の外国人、浪人、愛の伝道師という1人3役をこなし、3部構成でストーリーが展開。", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GONE WITH THE WIND", "風と共に去りぬ", "", "", "http://ecx.images-amazon.com/images/I/51F2aEmGpeL._SL210_.jpg", 232, "大物プロデューサー、セルズニックが製作費600万ドルを注ぎ込んで作り上げたアメリカ映画史上の金字塔。大農場主の娘スカーレットは、アシュレーを愛しているが、アシュレーは彼の従妹メラニーと結婚。勝気なスカーレットはあてつけで、メラニーの兄と結婚するが折から始まった戦争で戦死、今度は妹のフィアンセを横どりして結婚するが彼も亡くなってしまう。そんな彼女を激しい戦火の中、助けてくれたバトラーの強引ともいえる愛情に引きずられて結婚した。しかし、彼女はアシュレーへの思いが断ちきれず、二人の生活は破綻してしまう。バトラーが彼女の前から去った時、初めてスカーレットはバトラーを愛していることに気付くが……。スカーレットの波乱万丈の生き方を通して、女性の立場からアメリカン・スピリットをうたい上げた点に、時代を超えて輝く本作の原点がある。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXTREME MEASURES", "ボディ・バンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "マンハッタンにそびえ建つグラマシー病院。緊急医療室に勤務する若き医師ガイは、急患者の不可解な死の謎を探るうちに、ホームレスを使った人体実験を行う病院の裏の顔を知ってしまい……。陰謀に立ち向かう青年医師の姿を描きながら、医療倫理を問う衝撃のサスペンス。主人公に扮した好漢H・グラントの熱演も見もの。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Incredible Hulk", "インクレディブル・ハルク", "", "", "http://image.eiga.k-img.com/images/movie/53199/315x210.jpg?1313", 114, "実験中に大量の放射能を浴びた科学者ブルース・バナーは、感情が高まると緑色の超人ハルクに変身するという特異体質になってしまう。元の体に戻るべく治療方法を探すブルースだったが、その驚異的なパワーに目をつけた軍が彼の元に追手を送り込み……。これまでも数度に渡って実写化されてきたマーベル・コミックの人気シリーズを、エドワード・ノートン主演＆「トランスポーター」シリーズのルイ・レテリエ監督で新たに映画化。", "2008-08-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HORSE SOLDIERS", "騎兵隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "北軍のマーロー大佐は敵の補給を断つためニュートン駅破壊の任務を受けて騎兵隊を率いて出発した。軍医のケンドール少佐も配属されたが、マーロー大佐とは犬猿の仲。苦しい行軍が進むうちに二人のいがみ合いも激しさを増していくが……。戦場におけるヒューマニズムを謳った、J・フォード後期の傑作。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NITABOH・仁太坊／津軽三味線始祖外聞", "NITABOH・仁太坊／津軽三味線始祖外聞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "津軽三味線の始祖、仁太坊の半生を、フィクションを交えながら描くアニメーション。貧困、失明、両親との死別と、不幸に見舞われながらも三味線修行に精進した彼の屈強な生き方を描き出す。若手三味線演奏家の上妻宏光による、劇中の三味線演奏シーンの迫力は圧巻。主題歌と挿入歌を担当した加藤登紀子の娘Yaeが、仁太坊の母親役で声優に挑戦している。江戸時代末期、青森県に生まれた仁太郎は、生まれて間もなく母を失い、８歳の時に失明、11歳の時に父を亡くす。天涯孤独になった彼は三味線に打ち込むが、太棹による新しい奏法を身に付けるため厳しい修行に臨む。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ETERNITY", "エタニティ 永遠の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "強大なマスメディアの権力に対し愛と名誉を懸けて闘いを挑む男。彼はその中で、不思議なデジャ・ブを通じて自分の前世＝中世ヨーロッパの戦乱の時代の記憶を追体験する。前世で引き裂かれた愛が、時間と権力という二つの障害を乗り越えて、現世で再びよみがえることができるのか？　永遠の愛をテーマにしたファンタスティックなラブ・ストーリー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METEOR", "メテオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "地球に向かう巨大な隕石。武装人工衛星によってこの隕石を阻止しなければ１ヵ月後に地球は滅亡していまう。だが、そのすきに乗じた核攻撃を懸念する米・ソ両大国は、自国の武装人工衛星を使用することをためらうのだった……。大国エゴを加味したSFパニック。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("絶唱", "絶唱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "戦前の、まだ封建的な風土の残る山陰地方を舞台に、大地主の息子と貧しくも美しい山番の娘の悲恋を描いた作品。大江賢次原作の３度目のリメイクで、1958年の小林旭、浅丘ルリ子、1966年の舟木一夫、和泉雅子に次いで、百恵＝友和コンビが主演している。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH　ART", "ハイ・アート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ミロシュ・フォアマンやガス・ヴァン・サントらに師事した女性監督L・チョロデンコが、サンダンス映画祭で脚本賞を受賞した長編デビュー作。写真雑誌の編集アシスタントに昇格した24歳のシドが、かつて第一線で活躍していた写真家ルーシーと知り合う。元女優と同棲し、ドラッグに溺れているルーシーのつかみどころのない魅力に惹かれたシドは、彼女を再起させようと奔走。やがて二人は、深い愛情で結ばれるが……。アートの世界の退廃的でアンニュイな雰囲気のなかに、対照的な二人の女性の葛藤と狂おしい愛情を繊細かつリアルに映し出す。理想と現実の狭間で苦悩する写真家の複雑な心境をA・シーディが見事に表現している。ビデオタイトルは「プライベートフォト」。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花かご道中", "花かご道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "江戸屋の一人娘、お弥重と武蔵屋の一人娘、お喜久。ともに病弱な父を持つ二人は、彼らに代わって、商用の旅に出ることになった。旅慣れぬ彼女たちはなかなか進まない上、馬子にだまされ、山賊の巣に連れ込まれた。そこで一人の若い男三太郎と出会い助け出されるが……。美空ひばり主演、工藤栄一監督による青春時代劇。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西大后／THE EMPRESS DOWAGER", "続・西太后 暴虐の美貌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「西太后」(1984)のヒットにより製作された続編。史上に類を見ない富と栄華を誇った中国清朝を、わずか27歳で支配した西太后。最後の皇帝・溥儀をも生んだこの女帝が犯した恐ろしい所業の数々を、壮大な清朝滅亡の歴史の中で描いた大作。「芙蓉鎮」で日本にも知られたリウ・シャオチンの熱演が見もの。", "1989-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Herbie Rides Again", "続ラブ・バッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "人間のような感情を持つフォルクスワーゲン、ハービーの活躍を描く「ラブ・バッグ」シリーズ第2弾。超高層ビル建設のために大富豪ホーク氏から立ち退きを迫られているお婆さんを救おうと立ち上がったハービーと青年ウィルビー。激怒したホーク氏は、高級車軍団を差し向けてハービーを壊そうとするが……。「メリー・ポピンズ」等の実写版ディズニー映画を多数手掛けたロバート・スティブンソン監督が、前作に続きメガホンを取る。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Edward Scissorhands", "シザーハンズ", "", "", "http://ecx.images-amazon.com/images/I/510L-f93GgL._SL210_.jpg", 105, "「バットマン」のティム・バートン監督が描く、切ないファンタジー。丘の上の屋敷に住む発明家に作られた、人造人間のエドワード。未完成のまま主人に先立たれたエドワードは、ハサミの手を持っていた。セールスでやってきたペグに連れられ、町に出ることになったエドワードだが……。エドワードをジョニー・デップ、彼が恋をする、ペグの娘キムをウィノナ・ライダーが演じている。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("sur／FACE14人の現代建築家たち", "sur／FACE14人の現代建築家たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "近年、東京都心の開発や京阪神の大規模なプロジェクトなどで活躍が目立つ日本の建築家たち。在日オーストリア人のジャーナリストとドイツ人の映像作家の二人が、日本を代表する14人の現代建築家たちの実像に迫るドキュメンタリーだ。彼らが語る自作に対する“美意識”を、あくまで外国人の視点で切り取っていく。普段は作品を通して表現する建築家たちの、意外な一面が伺える１本だ。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("曖・昧・Me", "曖・昧・Me", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "伊藤尚子の第36回新人映画シナリオコンクール入選作を、「バナナシュート裁判」で注目を集めた佐藤闘介監督が映画化。なんとなく曖昧な日々を送る女子高校生の薫子が、曖昧なままに成長していく姿を描く青春期映画。“歩く好奇心”と化して周囲をあたふたさせる主演の裕木奈江のアンニュイな個性とあどけない瞳が素晴らしい。つたないピアノの調べとともに、彼女がボーイフレンドを誘導し、キスにまで至らしめる廃屋シーンは抜群。主題歌は山崎ハコ。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("激しい季節", "激しい季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "サラリーマンの英樹が、自分を車ではねた美しき人妻・京子に心を奪われる。英樹はすべてを投げ出して京子を愛するが、彼女の夫であるヤクザの大物が出所して命の危険にさらされる。他人をも傷つけかねない凶暴な愛の形を描くラブ・ストーリー。モデル、シンガーとして活躍する高橋理奈の映画デビュー作になった。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冷飯とおさんとちゃん", "冷飯とおさんとちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 178, "「冷飯物語」「おさん」「ちゃん」の３部からなるオムニバス映画。それぞれ同名の山本周五郎の短編小説の映画化。「冷飯物語」は古書収集を趣味とする独身の男がひょんなことから幸運に恵まれ続ける話。「おさん」は妻のもとを飛び出した男が再び戻ってみると、妻は殺されていたという話。「ちゃん」は貧しい火鉢職人がなおも火鉢造りにこだわり続ける話。いずれも主役の男は中村錦之助が演じている。「親鸞」「五番町夕霧楼」の田坂具隆がメガホンを執っている。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOOL’S FIRE", "フールズ・ファイア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "演劇界の才女J・テイマーが描く、中世を舞台にした寓話的な一編。王様と７人の大臣を楽しませる宮廷道化師のホップ・フロッグ。そんな彼が、憧れの踊り子にひどい仕打ちをした王に対して、人智を尽くした報復行為に走る。ハイビジョンや巨大なパペットを駆使して展開する、ミステリアスな映像も見もの。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LADY VANISHES", "レディ・バニッシュ／暗号を歌う女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ヒッチコックの代表作「バルカン超特急」のリメイク。第二次大戦勃発直前。ロンドンへ向かう急行列車の中で、初老の女性が突然姿を消す。同じコンパートメントにいたアマンダは、乗務員に失踪を告げるが……。E・L・ホワイトの小説の映画化。", "1979-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤毛", "赤毛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "岡本喜八の脚本・監督、三船主演による幕末もの。慶応４年、徳川から明治へと体制が大きく変わる革命期。百姓あがりの赤報隊士・権三は赤毛をなびかせて故郷に錦を飾る。彼は代官屋敷から年貢米を百姓に返したり、借金を棒引きにして女郎たちを解放したり、得意の絶頂にいるが、利用価値なしと判断した白毛の官軍によって抹殺される……。民衆に密着していたがために革命の先頭に立ち、それゆえに銃火を浴びる。赤報隊の一隊士に革命の論理を結晶させた異色作。三船敏郎が十八番の粗野だが誠実な主人公を力演し国際スターの貫禄を見せ、松竹の岩下志麻が東宝映画に初出演して花を添えた。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志・海道一の暴れん坊", "次郎長三国志・海道一の暴れん坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "実質上マキノ雅広のオリジナルによるシリーズの最高作。石松はお蝶の法事を終えた次郎長の愛刀を、讃岐の金比羅様へ納める役を仰せつかる。ある娼家で夕顔という娼婦に惚れた石松は、身愛山鎌太郎のはからいで夕顔を身受けすることになる。ところが、その帰途、ワラジを脱いだ都鳥の吉兵衛の闇討ちにあい、無念の死を遂げる。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲキ×シネ「蜉蝣峠」", "ゲキ×シネ「蜉蝣峠」", "", "", "http://image.eiga.k-img.com/images/movie/55089/315x210.jpg?2944", 167, "「劇団☆新感線」による演劇を映画館で上映する「ゲキ×シネ」の7作目。演出をいのうえひでのり、脚本を宮藤官九郎が担当。主演は古田新太、共演に堤真一、高岡早紀、勝地涼、木村了、梶原善ほか。自分の名前以外覚えていない男・闇太郎は、元旅役者の銀之助に誘われて蜉蝣峠を下っていた。やがて、極道者たちが勢力争いを繰り広げる荒廃した宿場町にたどり着き、闇太郎は自らの過去と対峙する。", "2010-02-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウォーターズ", "ウォーターズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "青年実業家、バスケ選手などさ、まざまなキャリアを持ちながら夢破れた７人の男たち。そんな彼らが心機一転、ホストになって人生の再出発を決意する。しかし保証金を騙し取られたあげく、店長に逃げられてしまった。素人の彼らだけで営業することができるのか!? それぞれに手痛い失敗を体験し、ひょんなことからホストクラブで人生をやり直すハメに陥った７人の男たちが、友情を深めながらホスト道を邁進していく姿を、笑いあり、涙ありで綴った痛快青春コメディ。", "2006-03-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SERGEANT CLARIN", "ゴリラ・コマンドー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "残虐なゲリラと、特殊コマンドー部隊の闘い、さらには軍を脱走した反逆者を追う海兵隊が、三つ巴となって暴れ回るバトル・アクション。フィリピン軍の協力によって、ジャングル用装甲戦車やジェットヘリ、バルカン砲などの兵器が登場する。", "1991-01-01", "1,4,35", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("What Planet Are You From?", "2999年異性への旅", "", "", "http://ecx.images-amazon.com/images/I/51C5K09AudL._SL210_.jpg", 104, "", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AMAZING CAPTAIN NEMO", "アトランチスの謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ノーチラス号のネモ艦長と米海軍の猛者の二人が体験する不思議な出来事を描いたSF映画。彼らは大冒険の末、ジブラルタル海峡に沈む、謎の大陸アトランティスを発見するが……。SFXを駆使した海底での壮絶な戦闘シーンは見もの。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あまっちょろいラブソング", "あまっちょろいラブソング", "", "", "http://image.eiga.k-img.com/images/movie/55659/315x210.jpg?3109", 0, "「バカバカンス」でデビューした新鋭・宮田宗吉監督による青春映画。イタリア料理店でバイトしながらバンド活動をするナオミは、恋人の小林ともマンネリの関係で、刺激のない日々をおくっていた。そんなある日、ドラムのケンジとベースのヒサオがバンドを脱退したいと言い出す。その翌日、小林から投げやりなプロポーズを受けたナオミは、音楽をやめて結婚を決意する。", "2010-09-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SOUTHERNER", "南部の人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "大地を愛し、土に生きる農民の姿を自然主義的なタッチで描いた佳作。「怒りの葡萄」よりも詩情にあふれ、ルノワール監督の力量が全編にみなぎる。アメリカ南部の農業地帯で、移住労働者として暮らすサム・タッカーは、かねてから独立農家として定住したいという夢があった。ある親方が荒地を貸してくれることになったが、厳しい風土、困難な労働、偏屈な隣人、子供の病気などサムの苦労は絶えない。努力の末、畑が実ったのもつかの間、折から襲った豪雨で畑は無残な姿と化した。一瞬くじけて工員になろうと考えたサムだったが、家族を見て再び気力を奮い立たせるのだった。以前、「炎のサウザナー」のタイトルでビデオ化されたこともある。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD ORCHID", "蘭の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "上司の求愛に気後れするばかりのまじめで純情な女エミリー。ところが灼熱の地ブラジルで謎の男ウィラーと出会ったことから、彼女は自分の中に潜んでいた激しい欲情に気付き始める。脚本＝Z・キング、中年男＝M・ロークという「ナインハーフ」のコンビが再び描き出す、愛と官能の世界。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Keep", "ザ・キープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1941年、独国防軍が駐屯したルーマニアの城塞。そこに封じ込められた魔物は、自らを解き放つため、城中深く埋められた十字架を取り出そうとするが……。善の力と魔物の戦いを描くオカルト映画。", "1983-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅衣少女／THE GIRL IN RED", "紅い服の少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "16歳になる思春期真っ盛りの少女・安然。彼女は小さい頃から何を見ても“なんで？”を連発し、母親を困らせていた。今でも自分の信念に忠実に生きようとするあまり、周囲と様々な摩擦を起こしていて……。思春期の少女の悩みを描く。若手女流作家ティエ・ニンの原作を、女流監督ルー・シャオヤーが映画化した。", "1984-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("茄子　アンダルシアの夏", "茄子　アンダルシアの夏", "", "", "http://image.eiga.k-img.com/images/movie/1559/315x210.jpg?3178", 47, "南スペイン、アンダルシアの酷暑の中、世界3大自転車レースに参加するぺぺの1日に、レースの駆け引きや熱狂に加え、青年の夢と焦燥をも盛り込んだ47分。「千と千尋の神隠し」で作画監督を務めた監督・脚本の高坂希太郎は、自身もアマチュアレースでトップクラスの成績を持つ自転車レーサー。原作は黒田硫黄の連作コミック「茄子」中の一編。エンディング・テーマは、やはり自転車好きで知られる忌野清志郎の「自転車ショー歌」。", "2003-07-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・昨日の敵は今日も敵", "喜劇・昨日の敵は今日も敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "城南大学の応援団と音楽部は犬猿の仲。応援団は箱根のホテルでアルバイトをすることになるが、そこへ音楽部が演奏旅行にやってきて、ハチ合わせになり大混乱。そんな騒ぎのなかで怪し気な客が列島改造を叫んで、ホテルを占拠する。当時の政府が唱えた日本列島改造論を風刺した喜劇。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Yurt", "ホーム", "", "", "http://image.eiga.k-img.com/images/movie/57359/315x210.jpg?3859", 85, "", "2011-01-01", "32", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRANGERS ON A TRAIN", "見知らぬ乗客", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ハイスミスの原作をハードボイルドの名匠チャンドラーが脚色。妻と別れたがっていた男が、列車に乗り合わせた見知らぬ乗客から、“交換殺人”の話を持ちかけられる……。ヒッチコック演出の妙味が堪能できるサスペンスの秀作。異常殺人者に扮するウォーカーの好演が恐怖を盛り上げる。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時代屋の女房", "時代屋の女房", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "原作は村松友視の第87回直木賞受賞作。舞台となるのは、東京・大井町界隈。そこで古道具屋“時代屋”を営む安さんの周りには、女から女へ渡り歩きしている喫茶店のマスターや、元女子プロレスの女房にイビられながらも楽しく暮らす飲み屋の亭主など、ユニークな面々がいる。ある日、安さんの所に日傘をさした女・真弓が女房気取りで住み込んだ。自然に二人は夫婦のように暮らすが、真弓は突然姿を消す……。森崎東お得意の人情喜劇。安さんをはじめとする人々をスケッチ風に淡々と描いた佳作。謎の女・真弓を演じる故・夏目雅子は、どこかはかなくカゲロウのような美しさと、圧倒的な存在感を併せ持ち、自らの魅力を発揮。惜しい逸材であった。DVDは「夏目雅子　出演名作集　DVD-BOX」に収録。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWILIGHT FILEII", "TWILIGHT FILEII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "新進気鋭の若手監督が集い、独自のテーマと切り口で中編作品を競作するオムニバス映画プロジェクトの第２弾。無垢な少年が主人公の「僕はこの丘で、君を愛したい…」、初恋の記憶が題材の「あのころ…summer memories」、異色のバトル・ムービー「矜持／KYOUJI」の全３話。３話すべてに登場する同じ名前の主人公を、各エピソードで違うキャストが演じ分けるなど凝った構成にも注目したい。母親を亡くして以来、心を閉ざしてしまった悟は、母を写した１枚の写真を手がかりに、母と生前一緒に行った秘密の丘を探そうとするが……。（「僕はこの丘で、君を愛したい…」）", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京兄妹", "東京兄妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "失われかけた東京の情景、そこに生きる兄妹の日常を、懐かしく、はかなく、時に厳しい視点を持って描く。古き良き東京、見ることの少なくなった日本式家屋、懐かしさ漂う小道具など画面を構成するディテールの随所に、市川監督の敬愛する小津安二郎作品へのオマージュが漂う。両親を亡くし、ひっそりと暮らす兄妹。妹は今日も兄のために好物の豆腐を買いに行く。兄は、そんな妹を残して結婚する決心がつかない。やがて高校を卒業した妹は、兄の親友と交際し始め、兄の心中は穏やかでない……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風花", "風花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "名匠・相米慎二の長編第13作にして、遺作となった人間ドラマの佳編。満開の桜の下で目覚めたキャリア官僚の澤城は、二日酔いで何も思い出せず、となりにいる女が誰かも分からない。やがて、ゆり子と名乗るその風俗嬢と北海道に向かうことになった澤城は、それぞれの思いを胸に広大な北海道で車を走らせていく……。しっとりとした空間の中に、自分の居場所を見失い、しかし立ち止まることもできない人々の痛々しい姿が映し出され胸に刺さる。また、ヒロインを演じた小泉今日子が一人娘と離ればなれに暮らす風俗嬢という役を自然体で好演し、新境地を開拓している。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渡り鳥いつ帰る", "渡り鳥いつ帰る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "戦禍の中に妻子を見失い、鳩の街“藤村”の主人になった伝吉と、そこで働く様々な境遇にある女たちの生活を描く久松静児監督作品。やがて伝吉は妻子を発見するが、自分の今の立場を考えて身を引き、自殺する。永井荷風の『にぎりめし』『春情鳩の街』『渡鳥いつかへる』を久保田万太郎が再構成したものを八住利雄が脚色。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROSSROADS", "ノット・ア・ガール", "", "", "http://ecx.images-amazon.com/images/I/21ME42WSB7L._SL210_.jpg", 93, "世界中で人気を誇るミュージシャン、B・スピアーズの映画デビュー作。優等生の高校生が、幼馴染の二人の友達とロサンゼルスへ向かう旅に出たことから、新しい自分を見つけていく青春ロード・ムービー。監督はビースティ・ボーイズのマイクDの妻で、ソニック・ユースやシェール等のミュージック・ビデオを数多く手掛けているT・デイヴィス。純粋なアイドル映画として楽しめる作りになっている。", "2002-05-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　空の境界　第二章　殺人考察（前）", "劇場版　空の境界　第二章　殺人考察（前）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "奈須きのこの同名小説を原作に、万物の死を見ることのできる能力「直視の魔眼」を持つ少女・両儀式の苦悩と戦いを描いた人気アニメ劇場版「空の境界」7部作の第2章。16歳の高校生・黒桐幹也は、あえて他者とかかわりをもとうとしない両儀式にひかれていく。やがてわずかながら同じ空間を共有するようになった2人だったが、そんなある日、街では連続猟奇殺人事件が発生する。", "2007-12-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大帝の剣", "大帝の剣", "", "", "http://image.eiga.k-img.com/images/movie/33409/315x210.jpg?4475", 110, "「陰陽師」の夢枕獏による同名小説を「トリック」の奇才・堤幸彦監督が独自のセンスとユーモアで映像化したSF伝奇時代劇。江戸時代の日本を舞台に、持ち主に絶大な力をもたらすという“三種の神器”を巡り、歴史上の人物や妖怪、宇宙人たちが壮絶な闘いを繰り広げる。主人公の大剣を背負う大男・万源九郎に阿部寛、豊臣の血を引く美しい姫・舞に長谷川京子が扮する。さらに竹内力、六平直政といったベテラン勢が特殊メイクで妖怪役に挑む。", "2007-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊豆の踊子", "伊豆の踊子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "川端康成の名作の５度目の映画化。出演者に若手スターをそろえた東宝青春路線の一編として製作された。内藤洋子の発らつとした魅力は、従来の踊り子像を覆す現代的なヒロインを作り出し、その額をきわ立たせる可憐なまげ姿とともに強い印象を残した。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERE WE GO ROUOND THE MULBERRY BUSH", "茂みの中の欲望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "17歳の高校生が女の子を求めて繰り広げるアバンチュールの数々を、当時流行の“サイケデリック”な映像で綴る青春映画の佳作。当時の人気バンド、トラフィックのテーマソングをはじめ、1960年代のロンドンをまるごと伝える風俗の数々が郷愁を誘う。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NADIE　CONOCE　A　NADIE", "パズル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「オープン・ユア・アイズ」で脚本＆助監督を務めたM・ヒルの長編監督デビュー作。クロスワード・パズルの作成で生計を立てているシモンに、ある言葉を入れろという脅迫めいた電話がかかり、指示通りにパズルを作成すると、その言葉に符号するかのような死亡事件が起こる。事件の真相を追ううちに浮上したのは、同居人のカエルへの疑惑。友人を疑うことに罪悪感を覚えたシモンが教会へ赴くとそこには神父の死体があった。後日、カエルへの疑惑は薄れたものの、街では第３の事件が起こる。倒すべき相手がわからぬまま、シモンは孤独な戦いに踏み込んでいく……。色彩豊かなスペイン映画のイメージを覆す色味を抑えた撮影は、透明感漂う映像でサスペンスフルなストーリーを引き立てることに成功し、見事、1999年ゴヤ賞最優秀特殊効果賞を受賞した。「オープン・ユア・アイズ」「アザーズ」の監督A・アメナーバルが音楽を担当し、親友マテオのために506曲もの楽曲を提供したことも話題に。", "1999-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITY LIGHTS", "街の灯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "この作品の製作中に、ハリウッドではサイレントからトーキーへと移っていった。しかしチャップリンは無声映画のスタイルを崩さず、最終的に音楽とサウンドエフェクトを入れた、いわゆるサウンド版として完成させた。街の放浪者チャーリーは、ふとしたことから盲目の花売り娘と知り合った。花を買ってやりたくてもお金がない。そこで掃除夫として働いたり、ボクサーになったり、彼女の眼を手術して治そうと一所懸命に頑張るが、思うように金は手に入らない。そんなある日、酔っぱらいの百万長者の男と仲良くなり、彼から大金をもらう。その金を娘の手術代とすることができたのだが、しらふになった百万長者は自分がくれてやったことをすっかり忘れてチャーリーを泥棒扱い。彼は牢獄につながれる身に……。刑を終えて出所したチャーリーは娘と再会する。が、目が見えるようになった娘は彼が自分の恩人であることがわからない。放浪者を逆にあわれむ彼女だったが……。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボール　魔神城のねむり姫", "ドラゴンボール　魔神城のねむり姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "今なお絶大な人気を誇る冒険アクション・アニメの劇場版シリーズ第2弾。TV版には登場しないオリジナル・キャラが大きな見どころの一つだ。ドラゴンボールを駆使し、“かめはめ波”の必殺技を持つ孫悟空たちの勇姿がダイナミックに描かれる。魔神城に閉じ込められた謎の“ねむり姫”を巡って、熾烈な戦いが展開。DVDは『DRAGON BOX THE MOVIES』に収録。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCKSHOW", "ROCK SHOW", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1976年６月10日に、シアトルのキングドームに７万人を集めたポール・マッカートニー＆ウイングスのコンサートを記録したドキュメンタリー。レーザーやフィルムを駆使した舞台を、13台のカメラが追い、ウイングスの魅力を十分に伝えている。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("またの日の知華", "またの日の知華", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「ゆきゆきて、神軍」など、斬新な切り口のドキュメンタリー映画を作り続けてきた、原一男監督。ドキュメントの鬼才として国内外から高い評価を集める彼が、ついに劇映画の分野へ進出。初の劇映画で彼が主題に選んだのは、一人の女性の生き様。僅か数年の間に豹変するヒロインの波乱に満ちた人生が描き出される。鬼才で鳴らす彼だけに、一人のヒロインを4人の女優に演じさせるなど、随所に斬新な発想を導入。野心的な一作に仕上がった。本作が遺作となった金久美子の演技も感慨深い。従兄の良雄と結婚した知華は子宝にも恵まれ、幸せに暮らしていた。だが暫くして夫が結核で入院。待つ身の辛さから彼女は、言い寄られていた同じ職場の和也と関係を結んでしまう。", "2005-01-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("祝（ほうり）の島", "祝（ほうり）の島", "", "", "http://image.eiga.k-img.com/images/movie/55459/315x210.jpg?3721", 0, "山口県上関町祝島（いわいしま）は、瀬戸内海に浮かぶ人口約500人の自然に囲まれた美しい小島。1982年、島の対岸4キロに原子力発電所の建設計画が浮上するが、島民は28年にわたり建設反対運動を続けている。島民の生活を通し、人間と社会のあり方を見つめるドキュメンタリー。「アレクセイと泉」の本橋成一監督がプロデュースを務め、纐纈（はやぶさ）あや監督が劇場公開作デビューを果たす。", "2010-06-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("17歳的天空", "僕の恋、彼の秘密", "", "", "http://ecx.images-amazon.com/images/I/31VGV0DBQTL._SL210_.jpg", 93, "台北を舞台にゲイの青年たちが織りなす恋の行方を爽やかに描き、低予算ながら台湾で異例のロングランヒットを記録した青春ラブストーリー。夏休みに台北へやって来た田舎育ちの純真な青年ティエンは、そこで出会ったイケメンのバイにひと目ぼれしてしまう。しかしバイの正体は、「ミスター・ラブマシーン」と噂されるほど有名なプレイボーイだった……。主演は本作が映画デビューとなるトニー・ヤンと、「セブンソード」のダンカン・チョウ。", "2005-12-10", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Resident Evil", "バイオハザード", "", "", "http://image.eiga.k-img.com/images/movie/1019/315x210.jpg?3479", 101, "古い洋館で目覚めたアリスは、それまでの記憶を失っていることに気づく。そこに武装した特殊部隊が登場。彼女を伴い、洋館の地下通路から、秘密研究所で起きた異変の調査に向かう。異変とは？　また彼女の正体は？　影は東ベルリンで建設中の新しい地下鉄駅を使用。特殊効果は同監督と「イベント・ホライゾン」で組んだリチャード・ユリシッチ。音楽は「スクリーム」のマルコ・ベルトラミと映画音楽初挑戦のマリリン・マンソン。", "2002-08-31", "1,7,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("C〈コンビニエンス〉・ジャック", "C〈コンビニエンス〉・ジャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "就職の研修のために、上京してきた地方大学生、浩。宿泊先として、あてにしていた幼なじみがギャングに拉致されたことから、仲間とともに救出すべく行動をとるのだった。コラムニストとして活躍する泉麻人の同名小説を映画化した作品。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Extraordinary Adventures of Adel Blanc-Sec", "アデル　ファラオと復活の秘薬", "", "", "http://image.eiga.k-img.com/images/movie/55439/315x210.jpg?1766", 107, "「ニキータ」「レオン」「フィフス・エレメント」のリュック・ベッソン監督が、新生ルイーズ・ブルゴワンを主演に迎えて描く冒険活劇。共演に「007 慰めの報酬」のマチュー・アマルリックほか。1911年のエジプト。ベストセラー作家のアデルは瀕死の妹を救うため、エジプトに伝わる“復活の秘薬”を求めてラムセス2世の墓を訪れる。アデルは黄金を狙う盗賊の攻撃や宿敵のデュールブーの邪魔立てをかわしながら、秘薬のカギを握るミイラを手に入れる。その頃パリでは、絶滅したはずの翼竜プテロダクティルスが現れ、人々を恐怖に陥れていた。", "2010-07-03", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIRTHDAY PARTY", "誕生パーティー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "イギリスの平凡な下宿屋で開かれたバースデー・パーティー。楽しくなるはずの宴も、謎の二人の紳士の登場で雰囲気がガラリと変わってしまう。その場の重い空気もやがてスリリングな様相を呈してきて……。ジョゼフ・ロージーの心理劇「召使」「できごと」「恋」の脚本家としても知られるH・ピンターの、劇作家としての名声を得た初期のシナリオを映画化した作品。監督のW・フリードキンにとっても長編第２作にあたり、３年後の「フレンチ・コネクション」で脚光を浴びることになる彼の力量を測る意味でも、貴重な作品。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROLLFLO¨JTEN", "ベルイマン／魔笛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "夜の女王から頼まれて、ザラストロに捕らえられている女王の娘パミーナを救出することになったエジプトの王子タミーナ。女王は彼に危難避けの魔笛を授けるが、実は女王が悪人でザラストロが高僧だとわかる。彼のもとで修行を積んだタミーナはめでたくパミーナと結ばれるのであった。モーツァルトの有名歌劇をベルイマン流に映画化。", "1974-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あたしンち", "あたしンち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "けらえいこ原作の大人気アニメが、今度はスクリーンに登場。些細なことにも一生懸命で、パワフルな母を中心に、父、ユズヒコ、みかんのお騒がせ４人家族、タチバナ家に巻き起こる珍騒動をコミカルに描く。今回の劇場版は、母とみかんが頭をぶつけたとたんに入れ替わるというオリジナルのストーリーだ。また、主題歌を人気アーティスト、矢野顕子が手掛ける。相変わらずパワフルな母に振り回されているタチバナ家。ある雨の日、歩道橋で足を滑らしたみかんと母が、お互いに頭をぶつけた瞬間に雷が二人を直撃。その途端、みかんの体と母の体が入れ替わってしまう珍現象が起こり……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ne change rien", "何も変えてはならない", "", "", "http://image.eiga.k-img.com/images/movie/55489/315x210.jpg?4283", 98, "「ヴァンダの部屋」「コロッサル・ユース」で知られるポルトガルの鬼才ペドロ・コスタ監督が、フランス人女優ジャンヌ・バリバール（「そして僕は恋をする」「恋ごころ」）の歌手活動を追ったドキュメンタリー映画。歌のレッスンからアルバムレコーディング、ライブリハーサル、そして本番のライブまで、舞台をフランスから東京に移しながら、 女優バリバールが見せる多彩な表情を美しいモノクロ映像で捉えている。", "2010-07-31", "25,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("川は流れる", "川は流れる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "仲宗根美樹の曲『川は流れる』のヒットから製作した作品。沖縄出身の亜紀はまだ見ぬ父を尋ねて上京するが、新しい家族と暮らす父を見て自活を決意。歌手への道を志すのだが……。桑野と仲宗根のフレッシュ・コンビが魅力的。特に桑野は薄幸の沖縄少女を好演。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ATTACK OF THE KILLER TOMATOES ： DIRECTOR’S　CUT　VERSION", "アタック・オブ・キラートマト　完璧版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1978年に作られた「アタック・オブ・ザ・キラー・トマト」をJ・デ・ベロが編集し直した“完璧版”。ある日、一人の主婦が台所でトマトに襲われる。一方、トマトジュースを飲んだ男も、もがき苦しんで死亡。海岸ではトマトの大群にビキニギャルたちが襲われ大パニック。政府は調査に乗り出すが、なぜか無能者ばかりが送り込まれ……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IO　NON　HO　PAURA", "ぼくは怖くない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1978年、辺り一面に黄金色の麦畑が広がるイタリア南部の村。この村で育った10歳の少年ミケーレが、奇妙な“穴”に監禁された同世代の男の子と心を通わせ、その背後に潜む大人たちの秘密に迫っていくサスペンス・ミステリー。想像力豊かな少年が厳しい現実と向き合い、図らずも成長を遂げていく様を、スリリングかつ感動的に描き上げた。", "2003-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GYPSY", "ジプシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 149, "ストリップの女王ジプシー・ローズ・リーの回想記をもとにした実話もの。ボードビル華やかなりし頃、ローズは二人の娘をスターにしようと懸命になっていた。ローズはなかなかのやり手だったが、不景気とトーキーの浸透で契約を取ることは次第に困難になり、やがて娘の一人、ルイーズはストリッパーとして脚光を浴びる。彼女は名前をジプシー・ローズ・リーと改名するのだった。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("安宅家の人々", "安宅家の人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "安宅家の頭主、宗一は精神薄弱者だったが美しい心の持ち主だった。元使用人の娘だった妻、國子は彼を心から愛し、広大な庭に養豚場を経営して家を守っている。そこに宗一の異母弟で放蕩者の譲二が妻を伴って転がり込んでくるが……。善意に満ちた夫婦の姿を捉えたヒューマニズムあふれる作品。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("結婚の条件", "結婚の条件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "前作「憎いあンちくしょう」で女優として華々しく開花した浅丘ルリ子が、ハンサムな義兄を慕うカラッとしたOLを好演した作品。まひるは、二人の男から結婚を申し込まれている美人のOL。しかし、彼女の夫の理想像は、義兄の史郎だ。その史郎に、実は愛人がいたことを知ったことで、彼女の心は微妙に揺れ始める。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREAKING　THE　WAVES", "奇跡の海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 158, "「エレメント・オブ・クライム」「ヨーロッパ」「キングダム」と、カルト支持の高い異色作を放ち続けるL・V・トリアーの長編第５作。物語の舞台は、1970年代初頭、スコットランド北西部の寒村。戒律の厳しいカルヴィン主義が支配するこの村で、敬虔な信者のベスは、よそ者のヤンと結婚する。油田の採掘現場で働く彼が留守になると、一人、神と対話を繰り返すベスだった。ところが、ある日、ヤンは現場で大けがを負い、全身麻痺となってしまう。“もうお前を抱くことはできない。愛人を見つけて、その時のことを話してくれ”というヤンの言葉を神との対話の中で“愛の証しを示す行為”と理解したベス。彼女は、村の男性と次々に関係を持とうとし、ついには教会から追放されてしまうのだった……。排他的な村社会で純粋に愛を貫いたヒロインの悲劇的な運命に、やがて“祝福の鐘”が鳴り響く、厳しくも感動的な愛のドラマ。手持ちのカメラで撮影された映像は絶えず不安定に揺れ、あたかもドキュメンタリーを見ているかのような生々しさを放っている。", "1996-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハリウッド監督学入門", "ハリウッド監督学入門", "", "", "http://image.eiga.k-img.com/images/movie/54359/315x210.jpg?3309", 73, "「ザ・リング2」でハリウッド進出を果たした中田秀夫監督が、巨大映画産業都市ハリウッドでの映画製作についてのノウハウを記録したドキュメンタリー。中田監督自らの経験をもとに、プロデューサーやカメラマン、編集やコンポーザーなど様々なスタッフの証言で構成し、華やかなハリウッド映画の裏側を明らかにする。", "2009-03-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("河童 kappa", "河童 kappa", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "文豪・芥川龍之介の名作「河童」を、「東京スカパラダイスオーケストラ」の谷中敦主演で映画化した不条理ファンタジー。「真昼の花」の秋原正俊監督がメガホンを取り、カッパの国に迷いこんでしまった男性の運命を描く。ある日、山の中で穴のようなものに落ちてしまった男。気を失った彼が目を覚ますと、そこにはなんとカッパがいた。カッパたちの生態を知るにつれて、カッパの国にはいられないと嘆く男だったが……。", "2006-05-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dream Concert ドリームコンサート New Generation'10", "Dream Concert ドリームコンサート New Generation'10", "", "", "http://image.eiga.k-img.com/images/movie/55769/315x210.jpg?2876", 0, "1995年に初開催され、毎年約7万人もの観客を動員する韓国最大規模の音楽フェスティバル「Dream Concert」。2010年5月22日に開催された第16回の模様を映像化し、大迫力のスクリーンで上映するライブ映画。「Super Junior」「KARA」「少女時代」「SHINee」など、日本でも人気のある“K-POP”アーティストたちが繰り広げる白熱のステージを映画館で体感できる。", "2011-01-22", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ガンダムUC episode2「赤い彗星」", "機動戦士ガンダムUC episode2「赤い彗星」", "", "", "http://image.eiga.k-img.com/images/movie/55639/315x210.jpg?4349", 59, "「ローレライ」「亡国のイージス」の福井晴敏が手がけた小説を全6話のオリジナルビデオアニメとして映像化。第2話「赤い彗星」をブルーレイ＆DVD発売に先行して劇場で上映する。宇宙世紀0096年、歴史を覆す秘密が眠る「ラプラスの箱」のカギとなるMSユニコーンを託された少年バナージは、その箱を狙う地球連邦軍に捕らえられてしまうが、赤いMSシナンジュを駆る“シャアの再来”フル・フロンタルが現れる。", "2010-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAFU CAGE", "MAFU マフ～悪魔の檻～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "人里離れた館に天文学者の姉エレンと精神異常の妹シシーが暮らしている。ペットを鎖につないだまま、次々に殺害していく狂気の妹と、彼女を深く愛する姉の戦慄の世界を描いた異色作。“マフ”とは妹に殺されるチンパンジーのこと。やがて姉の恋人までが殺りくの対象になっていく。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONTIES IMMORAUX", "インモラル物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "人間の永遠のテーマともいうべき性の問題を、４つの時代を背景に描いたオムニバス映画。第１話は満潮に合わせてエクスタシーに達する男女の話。第２話は19世紀末の性書の話。第３話は17世紀初めのハンガリーが舞台。そして最後にボルジア家の話が描かれる。", "1974-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ろくでなし稼業", "ろくでなし稼業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "過剰な気取りが独特のパーソナリティーとなり、無国籍アクションを量産する日活のカラーにマッチして、敵役ながら１作ごとに人気が上昇してきた宍戸錠の第１回主演作。宍戸錠の主演の話はかねてからあったが、石原裕次郎の事故、赤木圭一郎の急死という不測の事態によりダイヤモンドライン強化のため急きょ実現した。このため日活では宍戸錠に“エースのジョー”というニック・ネームをつけ積極的に売り出すことになり“渡り鳥”シリーズで小林旭を売り出した斎藤武市を監督にあたらせた。宍戸錠と二谷英明が息の合ったコンビを見せ、メリハリのきいた斎藤演出と相まってコメディ・アクションの佳作に仕上がった。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キリン POINT OF NO-RETURN！", "キリン POINT OF NO-RETURN！", "", "", "http://image.eiga.k-img.com/images/movie/56749/315x210.jpg?1563", 0, "東本昌平の人気バイク漫画「キリン」の「POINT OF NO-RETURN!」編を大鶴義丹監督、真木蔵人主演で実写映画化。バイク・カタナに乗るキリンと呼ばれる中年の男が、ポルシェ911と公道で繰り広げる熱い戦いを描く。バーで出会った女性トモミと一夜を過ごしたキリンは、立ち寄ったバイク屋でかつて自分が乗っていたバイク・カタナを見つける。事故に遭って廃車にしたはずだったが、最新のパーツが装備され、より戦闘的な雰囲気を漂わせるカタナをみたキリンの胸中にある思いがよぎる。", "2012-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("父よ母よ！", "父よ母よ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "いじめ、非行、登校拒否、家庭内暴力など、現代社会の問題点の縮図である学校では何が起ころうとしているのか。共同通信記者の斎藤茂男がまとめたルポルタージュの映画化。非行の実例を積み重ねながら、親たちに訴えるという形をとっている。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD SIDE", "ワイルド・サイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "銀行員のアレックスはコールガールのバイトで、金融業界の大物ブルーノと知り合う。ブルーノは運転手で実は覆面捜査官のトニーを使い、銀行にダミー口座を開く手助けをアレックスに命令する。ブルーノの元妻も加わって……。政府から大金を奪う計画の裏で男女４人の思惑と愛情が絡み合うサスペンス・ドラマ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A BLOODY TALE", "抵抗の詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ナチス占領化のユーゴスラビアの田舎町。そこに住む子供たちは、いつしか自然にドイツ軍に抵抗するようになり、ついに大人たちと一緒に虐殺されてしまう。詩的な描写の中に、激しいまでの反戦の意志と戦没者への鎮魂を描いた作品。主演の子供たちは、みんな無名の新人ばかりである。", "1969-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瘋狂英語", "クレイジー・イングリッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "実業家でもある若き英語教師リー・ヤンが、独自の教育法“瘋狂英語”を掲げて中国全土を駆けめぐる様を記録したドキュメンタリー。その教え方たるや実に明確で、大声を出すことを恥ずかしがらず、体全体を使って英語をマスターしようというパフォーマンス色の強い内容。“自信に満ちた中国人になろう”と無数の生徒たちを叱咤激励し、万里の長城などで熱血指導を行うリー先生の奮闘ぶりが何ともユニークだ。", "1999-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Live Free or Die Hard", "ダイ・ハード4.0", "", "", "http://image.eiga.k-img.com/images/movie/1929/315x210.jpg?4142", 132, "95年以来、12年ぶりとなるブルース・ウィリス主演の大ヒットシリーズ第4弾。7月4日のアメリカ合衆国独立記念日にサイバーテロが発生。アメリカ東海岸の交通、通信、金融の各システムが麻痺する中、ジョン・マクレーン（ブルース・ウィリス）はたまたま護衛をしたハッカーのマット（ジャスティン・ロング）とともにテロリストたちに立ち向かってゆく。監督は「アンダーワールド」シリーズのレン・ワイズマン。", "2007-06-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("中国の鳥人", "中国の鳥人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "椎名誠の同名小説を「岸和田少年愚連隊 血煙り純情篇」の監督・脚本コンビが映画化。中国の秘境に魅せられた日本人ビジネスマンの冒険を大人のファンタジー色で綴る。商社マンの和田が急な出張で中国・雲南省に出発。ひょんなことからヤクザの氏家と道中をともにすることになった彼は、ヘンな日本語を話す中国人通訳に連れられてミャンマー国境近くの少数民族の村にやって来る。“鳥人伝説”が存在するこの村には鳥人学校があり、子供たちは羽根を付けて飛ぶ練習をしていた。奇妙な光景に戸惑いつつも、二人は雄大な景色と神秘的な伝説に心を惹かれていく……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青いうた　のど自慢　青春編", "青いうた　のど自慢　青春編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "井筒和幸監督によるヒット作「のど自慢」から新たに生まれた青春ストーリー。豊かな自然が広がる青森県むつ市。問題を起こして中学を1年留年した達也は、一緒に卒業する1歳年下の弟・良太に誘われて、クラスメイトの恵梨香や俊介と一緒に卒業パーティで歌を披露することに。卒業後、それぞれの道へと進んだ4人は、時には挫折を経験しながらも成長していくが……。出演は、テレビドラマ「3年B組金八先生」の濱田岳、冨浦智嗣、寺島咲ほか。", "2006-05-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホームレスが中学生", "ホームレスが中学生", "", "", "http://image.eiga.k-img.com/images/movie/54109/315x210.jpg?3342", 75, "義務教育を受けていなかったホームレスの中年男性が、中学校に転校してくる騒動を描いた異色作。監督は「ガチバン」の城定秀夫、主演に現役ホームレスのうつのみや八郎、共演に「マリと子犬の物語」の蛭子能収、「愛の流刑地」の阿藤快ら。とある中学に、須崎という中年男性のホームレスが転校してくる。生徒たちは不潔な須崎を避けたが、映研の生徒たちはホームレスを密着取材しようと須崎に近づくが……。", "2008-10-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL FUTURO E｀ DONNA", "未来は女のものである", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "たくさんの男女がたむろするディスコで、一組のカップルが、チンピラにからまれている若い妊婦を助け出す。この夜から３人の奇妙な共同生活が始まった……。ムーティとシグラの対照的なキャラクター描写がユニーク。二人の女の生き方を通して、未来を語ろうとした鬼才フェレーリの意欲作。", "1984-01-01", "4,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・ローリング・ストーンズ　レッツ・スペンド・ザ・ナイト・トゥゲザー HDリマスター版", "ザ・ローリング・ストーンズ　レッツ・スペンド・ザ・ナイト・トゥゲザー HDリマスター版", "", "", "http://image.eiga.k-img.com/images/movie/56259/315x210.jpg?3267", 91, "世界的ロックバンド「ザ・ローリング・ストーンズ」の1981年全米ツアーから、アリゾナ州のサン・デビル・スタジアムでの屋外ショー、ニュージャージー州のメドーランズ・ブレンダン・バーン・アリーナで行われた屋内ショーの模様を収めたライブドキュメンタリー。20台のカメラやヘリコプターを使った上空からの撮影など、さまざまな手法が駆使されたライブドキュメンタリーの傑作を、ツアーから30周年となる2011年、HDリマスター版で新たに劇場公開。", "2011-07-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乱れからくり", "乱れからくり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ネジ仕かけの人形でひと財産を築いた玩具メーカーの社長一族が、正体不明の殺人鬼に追いまわされるという、1978年度日本推理作家協会賞受賞の原作をもとにしたミステリー。主人公の私立探偵役の松田優作が、傍若無人でふてぶてしく、しかしコミカルな味を出している。TVアニメ『ルパン三世』のテーマ曲で知られるピアニスト、大野雄二が音楽を担当。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Boys from Brazil", "ブラジルから来た少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "ナチス復興とクローン人間計画の恐怖を描いたアイラ・レビンの傑作サスペンス小説を「猿の惑星」「パピヨン」のフランクリン・J・シャフナー監督が映画化。アウシュビッツ収容所で死の天使と恐れられた遺伝学者ジョセフ・メンゲレ。彼は各地に潜伏中のナチス残党とともに、65歳の公務員94人を殺害するという奇妙な計画を企てる。ナチス残党を追跡していたリーバーマンは、この事件を追う内に彼らの恐るべき本当の狙いを知る。", "1978-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOTHING TO LOSE", "ナッシング・トゥ・ルーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "大手広告会社の重役ニックは、仕事と美しい妻アンに恵まれ順調そのもの。ところが、ある日、妻と上司の不倫の証拠を見つけてしまい、彼の中で何かが弾ける。すっかりキレてしまったニックは、銃を突き付けてきたおしゃべりの強盗ポールを道連れに旅に出発。人種も性格も正反対の二人は、行く先々でトラブルを引き起こし……。エリート・コースから外れたヤケクソの白人男T・ロビンスと、お調子者の黒人強盗犯M・ローレンスの凸凹コンビが繰り広げる騒動がアップテンポで展開する爆笑コメディ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE UNHOLY GARDEN", "国際盗賊ホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "熱砂の無法地帯を舞台に、人間たちの欲望が渦巻くサスペンス・アクション。愛する女性の父親が所有する財宝を守るため、美男俳優R・コールマン扮する青年が、無法者を相手に戦う。だが、彼は利用されていただけであった。果たして本当の悪者は、一体誰なのか？", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La strategia del ragno", "暗殺のオペラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "　ファシストによって暗殺された父の死の心真相を探るべく、アトスは北イタリアの田舎町を訪れる。この町で父は英雄的存在になっており、謎は少しずつ解明していくが、そこには意外な事実が待ち受けていた。過去と現実が錯綜する脚本を、ベルナルド・ベルトルッチが見事に演出している。ジュリオ・ブロージが若き日の父と息子の2役に挑戦し、「第三の男」のヒロイン役で知られるアリダ・バリが父の愛人役を演じた。", "1970-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Un ete brulant", "灼熱の肌", "", "", "http://image.eiga.k-img.com/images/movie/58059/315x210.jpg?2770", 95, "", "2012-07-31", "2,4,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宝塚歌劇映画版　幻想歌舞劇　太王四神記 Ver.II 新たなる王の旅立ち", "宝塚歌劇映画版　幻想歌舞劇　太王四神記 Ver.II 新たなる王の旅立ち", "", "", "http://image.eiga.k-img.com/images/movie/55249/315x210.jpg?2797", 152, "宝塚歌劇を映画館で鑑賞する「タカラヅカ　レビューシネマ」の第2弾。ペ・ヨンジュン主演の韓国歴史ドラマ「太王四神記」を、宝塚歌劇団がミュージカル化。主演は星組トップスターの柚希礼音。世界を統一し、平和をもたらす王が生まれるという高句麗の伝説の日。ソスリム王の弟ヤンの息子タムドク（柚希）と、王の義弟ヨン・ガリョの息子ヨン・ホゲは、同時にこの世に生を受ける。さまざまな野望が渦巻く中、タムドクは過酷な運命へと立ち向かっていく。", "2010-02-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フランケンシュタイン対 地底怪獣（バラゴン）", "フランケンシュタイン対 地底怪獣（バラゴン）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "東宝とベネディクト・プロ提携による初の日米合作怪獣映画。第二次世界大戦末期、ナチスドイツから秘密裡に日本に運び込まれたフランケンシュタインの怪物の心臓は広島に到着したが、原爆で行方不明となる。15年後、人間化して異常成長を遂げたフランケンシュタインは、折から出現した地底怪獣バラゴンと対決する。海外ホラーの古典的キャラクター“フランケンシュタイン博士の怪物”を主役に迎え、それを巨大化して怪獣と対決させるという着想が実にユニークである。しかし本家フランケンシュタインが科学の力によって作られた再生人間であるのに対して和製フランケンシュタインの方は、一個の心臓から巨大に成長するクローン人間であり、容貌と名前以外に共通性はほとんどない。ビデオ、DVDには初公開時のエンディングを収録。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NATURAL ENEMY", "同居人　背中の微かな笑い声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "一見、好男子ながら恐ろしい本性を持つ青年が、ある一家を破滅に追い込んでいくサイコ・スリラー。株式ブローカーのテッドが部下のジェレミーを自宅に間借りさせた。だが、ジェレミーの奇怪な行動は次第にテッドの家庭を破壊していく。やがてジェレミーの出生の秘密が明らかになり……。「コピーキャット」で殺人鬼を演じたW・マクナマラがジェレミーに扮して、ここでも怪演を披露。", "1997-01-01", "3,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グロヅカ", "グロヅカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "能の『黒塚』をモチーフに、人里離れた山荘で起こる惨劇を描いたサイコ・ホラー。出演には森下千里らフレッシュな顔ぶれが揃い、華やかさの裏に秘めたオンナの業を見事に体現している。休部中の映画研究部再会のため、アイら7人は山奥での合宿を企画する。その晩、アイが見つけたビデオに不気味な映像が映っていて……。", "2005-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAFARI EXPRESS", "サファリ特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "観光客のために野生の動物の写真を撮影するガイド会社、サファリ・エキスプレス。ここに勤めるジョンが巨大なウラニウム鉱山を独り占めしようとする悪人バン・ディアと火花を散らす。特にラストのセスナによる空中戦は迫力満点。", "1976-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボックス！", "ボックス！", "", "", "http://image.eiga.k-img.com/images/movie/55119/315x210.jpg?2733", 0, "元放送作家の百田尚樹による青春小説を、「デトロイト・メタル・シティ」の李闘士男監督が映画化。主演は市原隼人、高良健吾。高校のボクシング部に所属するカブ（市原）は、怠け者だが天才的なボクシングセンスを持っており、試合でも連戦連勝を収めていた。一方、カブの幼なじみで気弱な優等生のユウキ（高良）も、カブに憧れボクシングを始める。無敵を誇る天才ボクサーのカブと、地道に努力を重ねていくユウキは、やがて運命のライバルとなる。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／THE　QUIET　FAMILY", "クワイエット・ファミリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "舞台出身の新人監督キム・ジウンによる、ブラック・ユーモアたっぷりの不条理サスペンス。ソウル郊外でペンションを開くことになった６人家族。開業早々だというのに客足はさっぱり。ようやく訪れた第一の客は謎の自殺を遂げる。噂になったら誰もこなくなると死体を処理した家族だが、第二、第三の客も次々に死んでいき……。ロベール・ブレッソンやアキ・カウリスマキを敬愛しているというジウンの演出はどこまでも淡々として涼やか。どんなに事態が白熱しても、ひょうひょうとしたテンポは崩れない。唐突にストレイ・キャッツのロカビリー・ナンバーを流したり、キューブリックの「シャイニング」風のオチをつけたりと、ニヤニヤさせられる描写も多い。", "1998-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画ドラえもん　のび太の恐竜2006", "映画ドラえもん　のび太の恐竜2006", "", "", "http://ecx.images-amazon.com/images/I/51P9PEGCD8L._SL210_.jpg", 107, "2005年に声優陣を一新して生まれ変わった国民的アニメ「ドラえもん」の劇場版シリーズ第1作「のび太の恐竜」をリメイクした長編アニメ。ある日、恐竜の卵らしきものを発見したのび太。卵から生まれたのは、白亜紀の恐竜・フタバスズキリュウだった。のび太は恐竜をピー助と名づけ、ママに内緒で育てはじめることに。やがて、のび太は大きく成長したピー助を白亜紀へ戻す決心をするが……。", "2006-03-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRUCE LEE in G.O.D／死亡的遊戯", "BRUCE LEE in G.O.D／死亡的遊戯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ブルース・リーの死後、代役による追加撮影の末、完成した「死亡遊戯」（本当の原題は「死亡的遊戯」だったとか）には、生前に彼が残したフィルム120分のうち10分程度しか使われていなかった。ここでは、残されたクライマックスのアクション場面の未使用フィルムを復元し、さらに撮影が中断＆頓挫するまでのドラマ（「燃えよドラゴン」の撮影とリーの突然の死去）を前半に盛り込むことで、全く新しい「死亡遊戯」ならぬ「死亡的遊戯」の完成が目指されている。香港の若手俳優デヴィッド・リーをリー役に据えて「死亡遊戯」に懸けたリーの情熱、苦悩を描くドラマ部分もユニークだ。なお、G.O.Dとは「ゲーム・オブ・デス」という英語原題の略。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハワイ珍道中", "ハワイ珍道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "怪しい芸能ブローカーの半田は、歌自慢の娘チエミとハワイ公演の契約を結ぶ。現地のコンクールで優勝した川端も加え、彼女らの公演は見事大成功を収めた。ところがチエミと父の運命的な再会や、金に目がくらんだばかりに人喰い人種の島で大冒険を繰り広げる半田と川端など、事態は次第にハチャメチャなものになっていく……。イーストマンカラーによる新東宝初めてのカラー映画。ハワイ・ロケと芸達者たちの競演が見もの。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャプテン翼　危うし！全日本Jr.", "キャプテン翼　危うし！全日本Jr.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "本作に出会ってサッカー選手を目指した現役プロが多いという、人気アニメの劇場版シリーズ第2作。欧州遠征から全日本が凱旋して1年が経ち、今度は欧州選抜チームが来日して試合することに。しかし、全日本は二人のレギュラーを欠いている。大空翼の活躍で先制点ゴールを決めたものの、その後欧州勢の怒とうの反撃が始まった……。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A CHRISTMAS CAROL", "クリスマス・キャロル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "本作の後、2度映画化された、イギリスの文豪ディケンズ原作の名作。ディケンズ作品では『オリバー・ツイスト』と並ぶ同作品3度目の映画化。『オリバー・ツイスト』の少年主人公とは違って、こちらは老人が主役だ。人生の深みや滋味も加わって味わいある人間ドラマとなっている。クリスマス・イヴの夜、金持ちだが思いやりのない老人の前に過去の亡霊が現れて……。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TONKA", "裸足のトンカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「ベティ・ブルー 愛と激情の日々」の人気俳優J・H・アングラードが初監督に挑んだラブ・ストーリー。スランプのスプリント選手が出会った、奔放な少女トンカ。その駿足に目をつけたスプリンターは、彼女に陸上競技をコーチする。やがて二人は恋に落ちるが……。勝つためではなく走ることが必要だから走る、ヒロインのナチュラルな姿が心に残る。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝ決戦航空隊", "あゝ決戦航空隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 199, "第二次世界大戦で、自爆テロのような特攻作戦を生んだ実在の男・大西瀧治郎の生涯を描く実録ものの戦争映画。敗戦国となった日本側の視点から捉えられた。パールハーバーの奇襲作戦から始まった太平洋戦争以来、日本軍は連戦連勝を重ねていたが、遂に追い詰められる時がやって来た。サイパン島でのアメリカとの決戦に敗れた日本軍は、最後の切り札を繰り出すことに。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Preacher's Wife", "天使の贈りもの", "", "", "http://ecx.images-amazon.com/images/I/51dOwVJfa-L._SL210_.jpg", 124, "ケイリー・グラント主演の「気まぐれ天使」（1947）をリメイクした作品で、歌手のW・ヒューストンの主演第３作。クリスマスを一週間後に控えた聖マシュー教会の牧師ヘンリーは気分が重かった。父の後を継ぎ、牧師になったものの自分の力では何もできないという無力感に負けていた彼は、神に助けを祈るのだった。次の日、彼の前にダドリーと名乗る男が現れ、神様から派遣された天使だと言う。信用しないヘンリーにはおかまいなしに、ヘンリーの助手として家に入り込んだダドリーは、何をやってもうまく、家族たちとも仲良くなる。そして、ヘンリーも生きる喜びを感じるのだった。しかし、教会は順調とは言えず、ハミルトンという男の口車に乗り、ヘンリーは教会を手放してしまう。そのことに怒った妻のジュリアとヘンリーの仲にも冷たい風が流れ、反対にダドリーとジュリアの仲は親密になっていき、ダドリーはジュリアに恋をしてしまうのだった。しかし、天使にはそれは許されない恋だったのだ……。", "1996-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("半分処女とゼロ男", "半分処女とゼロ男", "", "", "http://image.eiga.k-img.com/images/movie/57139/315x210.jpg?4760", 105, "青春と性をテーマに監督たちが独自の個性で自由に製作する「青春H2（セカンド）」シリーズの6作目。異色のマネキン人形劇「オー！マイキー」の脚本や「平凡ポンチ」「東京ゾンビ」の監督として活躍する佐藤佐吉が、ブラックユーモアを満載に描く青春劇。童貞の警察官・桜木健一郎は、自分を含めた人間の額にその人のセックス経験数が浮かび上がって見えるようになる。かねてから処女を相手に童貞を捨てることを目標としていた健一郎は、額に「0.5」と浮かぶ女性・陽子と出会い、数字が「1」ではないことから陽子が処女であろうと信じ、陽子を相手に晴れて童貞を卒業するのだが……。", "2011-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bodyguard", "ダブルマックス", "", "", "http://ecx.images-amazon.com/images/I/3146YJ0Q7QL._SL210_.jpg", 95, "「マッハ！」のペットターイ・ウォンカムラオが監督・脚本・主演を務めたタイ発のアクションコメディ。大富豪のチョート・ペットパンタカム会長が、謎の殺し屋グループに殺害された。会長のボディーガードを務めていたウォンコムは、任務を果たせなかったことから会長の息子チャーイチョンに解雇されてしまう。ところが、今度はチャーイチョンに殺し屋たちの魔の手が迫る。さらにウォンコムもまた命を狙われ……。", "2005-11-26", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATHOM", "空から赤いバラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "60年代から70年代にかけて弾んだセクシー・ダイナマイト女優、ウェルチがまさに体当たりのアクション演技で魅せる快作だ。女ヒーロー映画的タッチも満載。スカイダイビングが上手な女にNATOから、東側から水素爆弾の点火器具を取り返してほしいとの依頼。危険な任務に飛び込んだヒロインは、敵側のグループと壮絶な戦いの中へ。東西冷戦下の時代が生み落とした娯楽作。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍びの者 続・霧隠才蔵", "忍びの者 続・霧隠才蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "大阪落城後、幸村を助け薩摩の島津家に落ち延びた才蔵は、徳川方の探索を逃がれ種ヶ島に渡るが、服部半蔵らに発見される。幸村は潔く自決し、復讐を誓った才蔵は駿府に潜入する。時代の流れに対してたった一人で戦いを挑む男のむなしさがよく出ている。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUT COLD", "アウト・コールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "自分が殺したと早とちりして死体隠しに奔走する男の騒動を描くブラック・コメディ。お人よしの肉屋デイブは冷凍室の中で、カチカチに凍っていた共同経営者アーニーの死体を発見、自分のミスで殺してしまったと思い込む。死体の始末に困っているデイブに協力を申し出たアーニーの妻サニー。だが、実は……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版 FAIRY TAIL 鳳凰の巫女", "劇場版 FAIRY TAIL 鳳凰の巫女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Down with Love", "恋は邪魔者", "", "", "http://image.eiga.k-img.com/images/movie/1369/315x210.jpg?1634", 101, "新進女流作家バーバラが書いた「恋は邪魔者」がベストセラーに。女性に恋は不要だと説くこの本の主張を否定するため、プレイボーイのジャーナリスト、キャッチャーがバーバラに恋の罠を仕掛ける。監督は「チアーズ」のペイトン・リード、脚本は「キューティ・ブロンド」の続編「キューティ・ブロンド／ハッピーMAX」原案のイブ・アラート＆デニス・ドレイク。100着に及ぶ50年代風の衣装をデザインしたのは「ミート・ザ・ペアレンツ」などのダニエル・オーランディ。", "2003-10-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FAREWELL TO ARAMS", "戦場よさらば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "ヘミングウェイの名作の映画化。第一次大戦時イタリアに従軍した米人将校フレデリックは、従軍看護婦のヘレンと出会い愛し合うが……。戦争批判を抑え、メロドラマに徹したボーゼイジ演出が成功。原作は『武器よさらば』だが、戦前の日本ではこのタイトルは使えず「戦場よさらば」の名で公開。1957年、R・ハドソン主演でリメイクされた。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タスマニア物語", "タスマニア物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "タスマニアに赴任した父を追った息子は、父が商社を辞めて自然保護運動をやっていることを知る。父の真意を量りかねる息子もやがてタスマニアの大自然の中で心を開いていく。タスマニア島の雄大な自然とウォンバット、ワラビーなどの動物たちの愛らしい姿を交えて心優しく描いたファミリー・ドラマ。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンのどんぐり頓兵衛", "エノケンのどんぐり頓兵衛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "山本嘉次郎とエノケンのコンビによる最高の１本とも称される。神社の祭礼でインチキ居合抜きの見世物を演じていた頓兵衛は当国の主国に召し抱えられるが、御前試合で見事にノサれ追放となる。しかし、頓兵衛は家老の娘・梢に惚れてしまい、あきらめきれず誘拐を企てるものの失敗、家老に梢を嫁にくれと頼む。怒った家老はあやまって自分で自分を斬り、死んでしまう。頓兵衛は追われる身となり、変装を重ね逃げていくが、梢たち仇討ちの一行にことごとく見破られる始末。ついには絶体絶命と相なって……。ラストのドンデン返しも笑わせる。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("憎いあンちくしょう", "憎いあンちくしょう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "愛が形骸化した男女が、ふとしたきっかけで日本縦断の旅に出ることになり、愛を取り戻すという物語。マスコミに生きる男女を描く一方、無医村で働く医師と遠く東京に離れて暮らす恋人の純愛を設定し、２組の愛を互いに照射させながら物語は展開していく。蔵原惟繕監督のスピーディーで歯切れのいいカッティングやカメラワークも冴え、時にスクリーン・プロセスを使ったラブ・シーンの処理も鮮やか。人気スター、北大作にはマネージャー兼恋人の榊典子がいるが、このところ多忙から二人の仲は倦怠気味。そこへジープを九州まで運ぶという仕事が舞い込んでくる。九州の無医村で働く恋人のために、美子が新聞広告を出したのだった。大作はこの話に共感して、その運転手を買って出る。一方、マスコミもこのニュースを聞きつけ、ジープを運転する大作のあとを追いかけまわす。典子はあわてて大作を連れ戻そうとするが……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ОБЛАКО　РАЙ", "君はどこにいるの？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "変わりばえのしない毎日が続くロシアの田舎町。周囲の人々から疎んじられてきたコーリャがホラのつもりで“ぼくは旅立つ”と言ったとたん、噂が噂を呼び、絶えて久しい事件到来と町中がお祭り騒ぎとなる。とうとう極東に旅立つはめになったコーリャと、まどろみから覚めて活気づく町の人々との交流を、おかしく切なく描き出す抒情編。1991年、ロカルノ国際映画祭で銀豹賞を獲得した。", "1991-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MARK OF ZORRO", "奇傑ゾロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "明るく楽天的な快男子ダグラスが、スクリーン狭しと大活躍する、痛快で楽しさいっぱいの剣戟映画。19世紀末のメキシコ領カリフォルニア。知事と軍の横暴に苦しむ住民を助けんと、黒装束の騎士が夜ごと飛びまわり悪漢退治。人々は彼をゾロと呼んだ。", "1920-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Deus e o Diabo na Terra do Sol", "黒い神と白い悪魔", "", "", "http://image.eiga.k-img.com/images/movie/44119/315x210.jpg?3656", 118, "生活に追いつめられ領主を殺害してしまった貧しい牛飼いのマヌエロは、熱烈な信望者を集める黒人神父と出会い、ともに政府軍と戦うようになる。彼らに所領を荒らされた領主や教会は、名高い殺し屋のアントニオ・ダス・モルテスを差し向ける。ブラジルの新しい映画＝シネマ・ヌーボの旗手として賞賛されたグラウベル・ローシャ監督の代表作「アントニオ・ダス・モルテス」と対をなす姉妹編。1985年日本公開。2011年、ローシャ監督没後30年を記念した「グラウベル・ローシャ・ベスト・セレクション」にて、118分の完全版としてリバイバル。", "2011-06-18", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOSTON STRANGLER", "絞殺魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "時にはB級と軽視されながらも、犯罪もの、アクション、SF、ファンタジーなどあらゆるジャンルにわたって、あくまで娯楽性を追求し、黒澤明監督をはじめ多くの信奉者を持つR・フライシャーの渾心の力作。ボストンのあるアパートの一室で年配の女性が何者かに絞殺され、半裸で犯されていた。警察側は捜査にあたるが何ら手掛かりは得られない。やがて第２、第３の同じ手口の犯行が警察を嘲笑うかのように繰り返される……。前半の大胆な分割スクリーンの手法は映画史上でも屈指のもの。後半は一転、密室の中の長回しで犯人の心理をえぐる実験的意欲作。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JENNIFER", "ジェニファー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "虐げられた少女の内に潜む超能力が、自らのコントロールを超えて爆発する、その悲劇と恐怖を描いた、サイキック・ホラー映画。内向的な少女ジェニファーは父とともに都会の名門校にやって来たが、そこで同級生のいじめに遭い……。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剣鬼", "剣鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "犬の子と言われ馬鹿にされて育った俊足の斑平が、初老の浪人から居合いを学び、妖剣の使い手として成長する。柴田錬三郎原作による三隅研次の“剣三部作”のうちの一つ。斑平が馬と駆けっこをしてぶっちぎりで勝つシーンは語り草に。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADDICTION", "アディクション 吸血の宴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "現代社会における“罪”を探究し続ける鬼才A・フェラーラが、吸血鬼伝説にその材を求めたモノクロ映画。謎の女に首筋を噛まれ、ヴァンパイアと化した女子大学院生のキャスリーン。彼女は恋人の大学講師の血さえ奪うようになるが、やがて吸血鬼の血しか吸わない男に出会う。彼の哲学と説教に目覚めたキャスリーンは、博士論文を完成させ、成功を収めるが……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHANGHAI SURPRISE", "上海サプライズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "当時、新婚ホヤホヤだったS・ペンとマドンナの共演で話題を呼んだサスペンス活劇。1930年代の上海を舞台に、アヘン絡みの騒動に巻き込まれたアメリカ人青年の活躍を描く。G・ハリスンが主題歌だけなく挿入曲まで手掛けている。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CIEL EST A｀ VOUS", "この空は君のもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "自動車修理工場を経営するピエールは、第一次世界大戦時の元パイロット。彼の飛行機に対する情熱は今も消えない。妻のテレーズは夫の飛行機熱を冷まそうとするが、ある日飛行機に乗せてもらい、自分もその魅力の虜になってしまう。そして女性飛行士のコンクールに参加したテレーズだが、彼女の乗った飛行機はマルセイユを発ったまま消息不明になった……。J・グレミヨン監督が戦時中の占領下という困難をものともせず、実話に基づいて撮り上げたフランス映画史上に輝く航空映画の白眉。女性解放のテーマをいち早くとり上げた点も注目に値するが、その詩情豊かな映像と語り口が最高に素晴らしい。", "1943-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROSS", "CROSS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "インターネットで10分×８回のスクリーミング配信後、劇場上映するというまったく新しいスタイルで公開された新感覚の近未来SF作品。主演はロックバンド“RISE”のヴォーカル＆ドラマー、金子統昭。監督は「インストール」の大森美香。近未来の日本では、政府が抜き打ちでクロスワードパズルを市民に出題し、選別するクロス制度が導入されていた。読み物依存症でひきこもりの青年、風間紘平はたまたま政府のコンピュータをハッキングしたことから警察に命を狙われることに。そんな彼を助けたのは“移動式ナース”をする黒沼兄妹。追われる身となった風間は黒沼のアドバイスで、読書で培った頭脳を生かし、クロスの解答の代行業を始めるが……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒社會／TRIADS ： THE INSIDE STORY", "黒社会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ヤクザ組織の体質を嫌い、一人N.Y.に住んでいた、香港最大のヤクザ組織組長の一人息子ホー。だが父が暗殺され、葬儀のために香港に帰ってきた彼はやがて組織の頂点に立つことになる。香港ノワールの立役者、チョウ・ユンファが見せる男の美学が魅力のハードボイルド・アクション。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE TESTAMENT D’ORPHE´E", "オルフェの遺言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "詩・小説・戯曲・絵画・彫刻と万能だったコクトーが作った最後の映画。彼自身扮する“詩人”が一人の青年に案内され時間と空間を無視して現代や古代、死の国や生の国を自在に旅する姿を、フィルムの逆回しなど映画トリックを駆使して描く異色の映像詩。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("港のロキシー", "港のロキシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「オートバイ少女」に続く、あがた森魚の監督第３作。再び函館を舞台に、ノスタルジックな肌合いの青春映画を繰り広げている。両親を失い、二人暮らしを続けている姉弟。特別な絆で結ばれた彼らの前に、カリスマ的な魅力を放つアイスホッケー選手が現れ、不思議な三角関係が奏でられていく。クライマックスとなるローラースケート大会を中心に、少年たちが路上を滑走する場面が映画のテンポを決定。叙情と疾走感が交錯する撮影が素晴らしい。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MA¨DCHEN IN UNIFORM", "制服の処女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "女流劇作家ウィンスローの『昨日と今日』の映画化。第二次世界大戦後、厳格な規律を重んじるドイツの寄宿舎を舞台に、そこで青春を迎える多感な娘たちの生活をリリカルに描く。教師を慕う生徒の感情の機微を、女性ならではの繊細な演出で映し出した名作。", "1931-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・中学教師", "ザ・中学教師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "８人一組の班を形成し、生徒たちの自治を促し社会のルールを厳しく教え込む教師、三上。自らの教育方針を徹底する彼の前に、生徒たちは次々と問題を起こすのだった。プロフェッショナルな教育理念を持つ教師ドラマの主人公を「宇宙の法則」と同様に、まさにハマリ役と思わせる名演で長塚京三が演じている。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("国道20号線", "国道20号線", "", "", "http://image.eiga.k-img.com/images/movie/34299/315x210.jpg?1672", 77, "「雲の上」で高い評価を受けた富田克也監督が、地方都市で荒んだ生活を送る若者たちの狂った日常を描き出した衝撃作。郊外型の大型店や消費者金融のATMが立ち並ぶ国道沿いの町。元暴走族の青年ヒサシは、同棲中の元ヤンキー娘ジュンコと共に、パチンコやシンナーに明け暮れる自堕落な毎日を過ごしていた。定職にも就かず借金ばかりが増えていく彼に、暴走族時代の友人で闇金屋の小澤がある話を持ちかけ……。", "2007-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("「粘土のお面」より かあちゃん", "「粘土のお面」より かあちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "山本嘉次郎監督の「綴方教室」の続編ともいえる作品。お人好しのブリキ職人と、その一家を中心にして、貧しいながらも明るく生きていく人々の姿を淡々としたスケッチ風な手法で描き上げた作品。子役の二木てるみのいきいきとした表情が忘れがたい印象を残す。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聊斉金瓶梅／GHOST STORY OF KIM FING MVI", "淫邪王 デーモン・ウォリアーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "香港特撮界のアクションにエロティックな要素を取り込んだ香港映画。深い眠りから目覚めた邪悪な魔獣が美女を襲う。悪魔の肉欲地獄から美女チン・リンを救うため、ゴースト導師が霊界よりよみがえる。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪割草", "雪割草", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "田坂具隆の戦後第２作として撮られたホーム・ドラマ。郊外の住宅地に暮らす平凡な夫婦の前に突如、夫と別の女との間にできた隠し子が現れる。夫婦の間には微妙な波風が立ち始めるが……。撮影は、田坂作品初期以来コンビを続ける伊佐山三郎。妻役の三條美紀は、大映の元経理課員。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CZLOWIEK Z ZELAZA", "鉄の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 152, "ポーランド映画界の鬼才、ワイダ監督が、グダニスクの造船所で起こったストライキに関心を寄せ、そこに働く一人の若者の孤独な闘争の日々と、彼の生活を抹殺せんとする体制との関係を鋭く追及した問題作。“連帯”に向ける監督の深い共感がうかがえる一編。", "1981-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女優A", "女優A", "", "", "http://image.eiga.k-img.com/images/movie/57369/315x210.jpg?4279", 9, "芸能事務所イトーカンパニー所属の俳優たちが出演するショートムービーをネット配信している「イトーチューブ」で公開された作品を映画館で上映する「イトーチューブ Shrot Movie Colleciton」の1本。蒼井優がトーク番組の司会に挑戦するが、ゲストに招いた駅売店のベテラン女店員から猛烈なダメ出しをされてしまい、インタビューの行く末に暗雲が漂う。蒼井優と片桐はいりが主演するシットコム。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIRLS’　NIGHT", "ガールズ・ナイト 天使がくれた瞬間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「秘密と嘘」の名演で観客の涙を搾り取ったブレンダ・ブレッシン主演作。脚本のケイ・メラーが、親友をガンで亡くした体験をもとに執筆した笑いと涙の友情劇だ。向こうっ気の強いジャッキーと控え目な性格のドーンは、40年来の親友同士。二人は田舎暮らしの唯一の娯楽であるビンゴ大会で大金を引き当てるが、ドーンが脳腫瘍に冒されていることが発覚。やがて彼女たちは、ドーンの夢だったラスベガス旅行で残された時間を過ごそうとする……。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宝石泥棒", "宝石泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "箱根レークサイドホテルに現れた美女二人組。財閥の令嬢とその女中というふれこみだが、実はこの二人宝石泥棒。有閑マダムの持つ50カラットのダイヤを狙って彼女への接近を試みるが、同じようにダイヤを狙って変装している男二人組の宝石泥棒がいて……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　INVASIONS　BARBARES", "みなさん、さようなら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「アメリカ帝国の滅亡」のD・アルカン監督がその続編的意味合いで撮り上げ、俳優たちも再集結した作品。女と書物とワインをこよなく愛し、人生を気ままに謳歌してきた父親が迎える最期。家族や友人に囲まれて過ごすその時がユーモアと哀愁の中で描かれている。破天荒で偏屈な父を軽蔑してきたセバスチャンの元に、父の余命わずかという知らせが入った。渋々故郷へ戻った彼は、最期の時も謳歌したい父のために友人や元愛人を集め、ヘロイン治療のため麻薬常習者のナタリーを雇い……。", "2003-01-01", "10,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カルテット！", "カルテット！", "", "", "http://image.eiga.k-img.com/images/movie/57179/315x210.jpg?4672", 118, "バラバラになりかけていた家族がカルテットを組み、再び絆を取り戻していく姿を描いた音楽ドラマ。音大出身の両親をもち、バイオリンの才能も豊かで将来を有望視される少年・永江開。しかし、フルートを習っている姉は弟の才能にコンプレックスを抱いてドロップアウト気味な上、父親がリストラされたことで家族はバラバラに。再び家族の笑顔を取り戻すため、開は家族カルテットを結成すべく奮闘する。主演はオーディションで選ばれた新人の高杉真宙。クランクイン直前に東日本大震災が発生し、舞台でもあり撮影地でもあった千葉県浦安市が液状化現象の被害を受け製作が危ぶまれたが、市民700人がエキストラで参加するなど協力して完成させた。", "2012-01-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("格闘技オリンピック　四角いジャングル", "格闘技オリンピック　四角いジャングル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1970～80年代に活躍した格闘家たちの試合を収めたドキュメンタリーシリーズの第３作。アントニオ猪木と“熊殺し”と恐れられた空手家のウイリー・ウィリアムスの激しい勝負や、タイガー・ジェット・シンなどの貴重な素顔に迫った姿を捉えている。DVDタイトルは「四角いジャングル 最終章 格闘技オリンピック」。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rabbit-Proof Fence", "裸足の1500マイル", "", "", "http://image.eiga.k-img.com/images/movie/1609/315x210.jpg?3247", 94, "1931年のオーストラリア。先住民アボリジニと白人の混血児は、白人社会に適応させるために、家族から隔離され、寄宿舎に収容される政策がとられた。この寄宿舎から脱走して我が家に帰った3人の少女のひとりが書いたノンフィクションを映画化。監督は「今そこにある危機」「ボーン・コレクター」のフィリップ・ノイス。撮影はオーストラリア出身のクリストファー・ドイル。", "2003-02-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love in the Time of Cholera", "コレラの時代の愛", "", "", "http://image.eiga.k-img.com/images/movie/53089/315x210.jpg?1881", 137, "「百年の孤独」のノーベル文学賞作家ガブリエル・ガルシア=マルケスによる同名小説を「フェイク」（97）、「ハリー・ポッターと炎のゴブレット」のマイク・ニューウェル監督が映画化。1897年のコロンビア、郵便局員フロレンティーノは配達先の令嬢フェルミナと恋に落ちるが、身分の違いを理由に引き裂かれてしまい……。フェルミナの結婚後、50年以上に及び彼女を待ち続けるフロレンティーノを、「ノーカントリー」でアカデミー賞を受賞したハビエル・バルデムが好演。", "2008-08-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彫る・棟方志功の世界", "彫る・棟方志功の世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "青森の貧しい鍛冶屋の息子が、ゴッホの絵に打たれ“わだば、ゴッホになる”と言って上京する……。彫刻家・棟方志功の人生と芸術の秘密を、彼の作品や故郷の風土を通して探求していく見事な作品。監督・柳川武夫以下、スタッフは16ヵ月にわたって棟方志功を追い続けたと言われる。そのかいあって、棟方に対する深い愛情が全編にみなぎっている。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名一番", "悪名一番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "人々の零細な資金を集めた預金を持ち逃げする大黒金融の一社員。朝吉と清次は彼を追って東京の本社にかけあいに行くが、軽くあしらわれる。しかし事件の黒幕が、社長の郡と関東の顔役・工藤と知り、二人は得意の殴り込みに……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("安藤組外伝・群狼の系譜", "安藤組外伝・群狼の系譜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "鬼才、工藤栄一が「赤と黒の情熱」以来５年ぶりにメガホンを執ったヤクザ映画。14年の刑期を終えて出所したヤクザの小諸は、ガラリと様変わりした組織の情勢に直面。かつて若衆の一人だった新庄は、自分の組を持つほどになっていた。そんな折、新庄の敵対組織との結託を知った小諸は、仁義を通すため敵地へと乗り込む……。全編にビヴァルディの曲が使用されている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乱反射", "乱反射", "", "", "http://image.eiga.k-img.com/images/movie/55999/315x210.jpg?2543", 71, "モデルで女優の桐谷美玲が主演し、「時をかける少女」（2010）の谷口正晃監督がメガホンをとる青春ストーリー。映像（Cinema）と音楽（Music）のコラボレーションを目指す「CineMusica（シネムジカ）」シリーズ9作目で、同じく桐谷主演、谷口監督による「スノーフレーク」との2本立て上映。高校時代に角川短歌賞を受賞し話題となった歌人・小島なおが、17～20歳に詠んだ歌を集めた同名歌集を原作に、早く大人になろうと背伸びをしながらも、本当の恋を知らずに誰かを思う気持ちが理解できない女子高生・志摩の苦悩を等身大に描く。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ANNIVERSARY　PARTY", "アニバーサリーの夜に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "女優と、まもなく監督デビュー予定の小説家。二人の６回目の結婚記念日を祝い、ホームパーティーが催された。そこに集まる人々の様々な人生模様のアンサンブルを綴ったグランドホテル形式の、ちょっと切なくも愛情に満ちた大人のヒューマン・コメディ。豪華かつ実力派キャストそれぞれの飾らない好演が素晴らしい。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Knight and Day", "ナイト＆デイ", "", "", "http://image.eiga.k-img.com/images/movie/55189/315x210.jpg?2283", 109, "「バニラ・スカイ」でコンビを組んだトム・クルーズとキャメロン・ディアスが再び共演するアクション・コメディ。監督は「17歳のカルテ」のジェームズ・マンゴールド。理想の男性を追い求める平凡な女性ジューンは、ある日空港でハンサムな男と運命的な出会いを果たすが、男の正体は重要な任務を帯びたスパイだった。男との出会いから、何度も危険な目に遭遇するジューンは、やがて男に疑いの念を抱きはじめる。", "2010-10-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEGEND　OF　UNJIN", "花嫁はギャングスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "奇想天外な設定がウケて、韓国で大ヒットを記録したヒロイン活劇。主人公は大の男をも黙らせるヤクザの女親分。“私が死ぬ前に誰かいい人と結婚して”という病床の姉の願いを叶えるため、彼女は花婿探しに奔走するハメになる。そんなヒロインの悪戦苦闘を、ハードなアクションを交えて描写。韓国の人気女優、シン・ウンギョンがワイルドな主人公を熱演。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAHARA", "サハラ戦車隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "サハラ砂漠を舞台に、H・ボガート扮するガン軍曹が活躍する戦争アクション。第二次大戦の北アフリカ戦線で、一台の戦車とともに生き残った米兵３人が、水不足に悩みながらドイツ軍との戦闘をくぐりぬけ、本隊に救援されるまでを描く。DVDは「コロムビアトライスター ウォー・ムービー・コレクションBOX(1)」に収録。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NIGHT THE LIGHTS WENT OUT IN GEORGIA", "さよならジョージア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "しがない歌手の兄とともに、ドサ回りの旅を続けるしっかり者の妹。兄はある町で恋に落ち、妹は夢をつぶされ兄のもとを去る……。純粋で優しく、しかし根っから芯の強い少女という役は、マクニコルの得意とするところで、主題歌のほか二曲も、マクニコル自身が披露。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shrek", "シュレック", "", "", "http://image.eiga.k-img.com/images/movie/1169/315x210.jpg?1839", 90, "5月に全米公開され、ドリームワークス最大のヒットを記録したフルCGアニメ。最新技術で、人物の表情、炎、風に揺れる木の葉などの微細な表現をリアルに映像化。マイク・マイヤーズとエディ・マーフィーが声の共演。巨人シュレックが暮らす静かな森に、身勝手な領主に追放されたおとぎ話の登場人物たちが大挙襲来。自分の暮らしをじゃまされたシュレックは、彼らをもとの住処に戻すため、領主にかけ合いに行くことにする。", "2001-12-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("There Will Be Blood", "ゼア・ウィル・ビー・ブラッド", "", "", "http://image.eiga.k-img.com/images/movie/53169/315x210.jpg?3257", 158, "「ブギーナイツ」「マグノリア」の鬼才ポール・トーマス・アンダーソン監督が、20世紀初頭のカリフォルニアの石油産業を背景に、家族、宗教、裏切り、そして欲望について描いた人間ドラマ。原作は社会派作家アプトン・シンクレアによる「Oil（石油）」（1927）。主演は「マイ・レフト・フット」「父の祈りを」「ギャング・オブ・ニューヨーク」のオスカー俳優ダニエル・デイ＝ルイス。第80回アカデミー賞では主演男優賞と撮影賞を受賞した。", "2008-04-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POWER RANGERS", "パワーレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "日本の特撮ドラマ『恐竜戦隊ジュウレンジャー』をもとに、最新のCGとSFXを駆使して描いたアクション・ヒーロー活劇。悪と戦うために超能力を与えられた６人の高校生が、地球を守るため悪の帝王とバトルを繰り広げる。ある日、町の工事現場から巨大なカプセルが発見され、6000年前に当時のレンジャーによって埋められた悪の化身アイヴァン・ウーズがよみがえる。レンジャーへの復讐に燃えるアイバンは、再び地球破壊の野望をたくらむが……。全米ではTVシリーズが放送されるや爆発的な人気を博し、一大ブームを巻き起こした作品の映画化。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HURRY SUNDOWN", "夕陽よ急げ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "第二次大戦直後の戦後景気に沸く、アメリカ南部ジョージア州。ジュリーは夫とともにこの地に産業王国を築こうとしていたが、黒人女のローズと白人のラッドはどうしても土地を売ろうとしない。ジュリーの夫は次第に強引な手を打ち始め……。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー・THE FIRST", "仮面ライダー・THE FIRST", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "'71年にTVに登場して以来、シリーズが続く国民的ヒーロー『仮面ライダー』の記念すべき第1作が装いも新たに復活。主人公、仮面ライダー1号の本郷猛と、仮面ライダー2号の一文字隼人の誕生から対決に至る、まさに初期の物語が描かれる。パワフルにして華麗なアクションが痛快。改造人間、仮面ライダー1号の本郷猛はショッカーと対決。そんな彼の前に一文字隼人が変身した仮面ライダー2号が敵として現れる。", "2005-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PERSECUTION AND ASSASSINATION OF JEAN-PAUL MARAT AS PERFORMED BY THE INMATES OF THE ASYLUM OF CHARENTON UNDER THE DIRECTION OF THE MARQUIS DE SADE", "マルキ・ド・サドの演出のもとにシャラントン精神病院患者たちによって演じられたジャン＝ポール・マラーの迫害と暗殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "演劇界出身の鬼才P・ブルックが、自らが演出を手掛けた舞台劇を実験的なアプローチで映画化。患者たちの治療手段として芝居を採り入れているシャラントン精神病院。ここに収監されたマルキ・ド・サドが、フランス革命で名を上げたジャン・ポール・マラーがシャルロット・コルディに暗殺された事件の芝居を演出するという設定の下、様々な問題提起をはらんだ異色の映像世界が繰り広げられる。", "1972-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("張込み", "張込み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "松本清張原作・橋本忍脚色・野村芳太郎監督という名トリオによる社会派推理ドラマの代表作。東京で起きたピストル強盗殺人犯が会いに行くと見られる佐賀市に住む愛人は、年長の銀行員の後妻となって細々と暮らしている。東京から派遣された二人の刑事は、安宿の２階で張り込みを始める……。冒頭、横浜駅から九州行きの列車にあわただしく乗り込む二人の刑事の緊迫した描写から１週間にわたる張り込み、そして犯人逮捕まで橋本忍の脚本は一分の隙もない。野村芳太郎の演出はロケーション効果をいかしたセミドキュメンタリー・タッチを駆使し、人間描写にも確かな腕を見せ、この種の推理ドラマの一つの到達点を示した。出演者はいずれも好演だが、犯人の愛人に扮する高峰秀子の演技は特に味わい深いものがあった。松本・橋本・野村のトリオは、この後「ゼロの焦点」「影の車」「砂の器」と話題作、力作を発表、数々の秀作を残している。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rocket Men", "宇宙（そら）へ。", "", "", "http://image.eiga.k-img.com/images/movie/54429/315x210.jpg?3811", 98, "1958年のNASA設立から50年、宇宙開発に全力を注いできた職員と宇宙飛行士たちの挑戦と奮闘を描き、NASAの壮絶な歴史の全貌を明らかにするドキュメンタリー。NASAの総プロジェクトを克明に記録したフィルムをもとに、打ち上げに失敗したロケットや船内火災など、死と隣り合わせのミッションに挑む人々に迫る。監督は実録ドラマに定評のあるリチャード・デイル。", "2009-08-21", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若さま侍捕物帖 紅鶴屋敷", "若さま侍捕物帖 紅鶴屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "若さまが居候している舟宿の近くにある紅鶴屋敷で夜な夜な起こる幽霊騒ぎ。そこを買い取った清吉のヤクザ仲間と伯父の越後屋清兵衛が殺害された。若さまは事件の解明に乗り出すとともに、紅鶴屋敷の謎も解いていく。やがて若さまは事件の裏にある悪徳商人らの抜荷買いの秘密に気づく。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("加藤隼戦闘隊", "加藤隼戦闘隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "第二次大戦中、新鋭戦闘機“隼”を乗りこなし、軍神といわれた加藤建夫の伝記映画。主演の藤田進にとっても当たり役となった。今見ると、加藤の旺盛なる研究心や部下への思いやりといったドラマ部分よりも、円谷英二による空中戦の特撮に目が引かれる。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH SHIP", "ゴースト 血のシャワー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "北大西洋を彷徨する、ナチス・ドイツの拷問船。ナチスの悪霊とユダヤの怨霊が、新たな生血をエネルギーとしてさまよう、スプラッタ仕立ての怪奇ドラマだ。美女が血のシャワーを浴びたり、ナチスの亡霊の恐怖ムード描写など見ごたえ十分。ビデオ発売時タイトルは「デス・シップ」。", "1980-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代人", "現代人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "渋谷実が、建設局の中に起こる収賄事件を題材に、戦後社会の暗黒面を描いた作品。社会風刺やコメディに特異な才能を発揮した渋谷だが、この作品では演劇的な手法も取り入れて、テーマ重視の硬質な一編に仕上げている。池部良自らが、演技開眼したと語る映画。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グロテスク", "グロテスク", "", "", "http://image.eiga.k-img.com/images/movie/54329/315x210.jpg?4202", 73, "会社の同僚で思いを寄せ合う和男とアキは初めてのデートを楽しんでいたが、突然何者かに拉致され、目が覚めると見知らぬ地下室にいた。手足を拘束され完全に監禁された2人は、「2人の愛に感動できれば解放する」と話す謎の医師と見られる男に数々の恥辱と虐待を受ける……。監督は「ノロイ」「口裂け女」の白石晃士、主演は「東京残酷警察」の長澤つぐみ。", "2009-01-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK NIGHT ブラックナイト", "BLACK NIGHT ブラックナイト", "", "", "http://ecx.images-amazon.com/images/I/51RMPMDV9XL._SL210_.jpg", 98, "香港、日本、タイ出身の気鋭のクリエイター3人が集結し、それぞれ自国の首都を舞台に描いたオムニバス・ホラー。「ツインズ・エフェクトII 花都大戦」のパトリック・レオン監督が「第一夜　隣人」、「HINOKIO」の秋山貴彦監督が「第二夜　闇」、「デッドライン」のタニット・チッタヌクン監督が「第三夜　記憶」をそれぞれ手がける。キャストには瀬戸朝香、華流スターのディラン・クォ、タイの実力派女優ピッチャナー・サクサゴーンら、各国から豪華俳優陣が参加。", "2006-06-24", "76,6,23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代やくざ・人斬り与太", "現代やくざ・人斬り与太", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "深作欣二監督のバイオレンスが爆発するエキサイティングな一編。川崎の売春街に育ち、感化院とシャバを往復していっぱしの愚連隊番長にのし上がった勇。街を二分するヤクザのボスたちにたてついて、がむしゃらに暴れまくった果てに、野良犬のように撃ち殺される様が壮絶。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AT THE MAX", "ローリング・ストーンズ・アット・ザ・マックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1989年８月から翌年まで続いたR・ストーンズの“復活ツアー”のファイナルを、ロンドン、ウェンブリー・スタジアムでの公演を中心に記録した、ド迫力のライヴ・フィルム。驚異的なサウンド・システムとともに、特殊な大面積フィルムに収められたステージは“網膜いっぱい（＝アイマックス）”に広がり、M・ジャガーにからまれそうな臨場感。感動的なラストまでエネルギッシュなプレイに脱帽してしまう。初来日公演の迫力を再確認するうえでもファン必見の作品。1997年に東京アイマックス・シアターで特別ロードショーとして公開された。", "1991-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女賭博師・花吹雪お涼", "女賭博師・花吹雪お涼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "流れの女賭博師お涼こと沢田涼子は、闇討ちされた父の敵を打つために、故郷の町に戻り、旧知の高品を訪ねる。再会を喜ぶ間もなく、涼子に新興勢力・望洋会が襲いかかるが、刑務所帰りの榊徹哉の手によって難を逃れた。数日後、徹哉が望洋会組長・西村が開いた花会に行くと賭場で札を操る涼子の姿があった。鈴木砂羽が、花札賭博の渡世に足を踏み入れた女賭博師を好演。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベッドかざりとほうき", "ベッドかざりとほうき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "人気児童文学作家メアリー・ノートンの「空とぶベッドと魔法のほうき」を、「メリー・ポピンズ」のスタッフが映像化したミュージカル作品。第2次世界大戦中のイギリスを舞台に、見習い魔女と3人の子供たちが繰り広げる冒険を描く。魔法学校の通信教育を受講中の魔女エグランタインの元に、戦争で孤児となった3兄弟が預けられることになった。魔法学校閉鎖の知らせを受けた彼らは、空飛ぶベッドに乗ってロンドンへ向かうが……。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOI　ALBINOI", "氷の国のノイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "アイスランドの俊英D・カウリによるシニカルでファンタスティックな異色の青春ドラマだ。倦怠感で窒息しそうになっている17歳の青年ノイ。彼の反抗的でやる気のない態度は、人々の神経を逆なでするばかり。彼と周囲のぎこちない関係を見つめた映画は、トボけたユーモアの中にも愛情あふれる温もりが漂う。村からの脱出を決意したノイの感情が爆発すると同時に、やがて発生した衝撃的な出来事とは!?　悲劇と喜劇が絶妙に渾然となった感覚が鮮烈なインパクトをもたらす１本。", "2003-01-01", "77,7,3,18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BATTERIES NOT INCLUDED", "ニューヨーク東8番街の奇跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ニューヨークの下町を舞台に、地上げ屋に立ち退きを迫られる安アパートの住人たちと、彼らの前に現れた宇宙からの小さな訪問者との交流を描いたファンタジー作品。ILMによる特撮と名優たちの共演が見ものだ。バックに流れるバンド演奏も良い。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Farewell to the King", "戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "　太平洋戦争末期、原住民を抗日ゲリラ兵に育てるという任務を負い、ボルネオに渡ったフェアボーン大尉はその地で王として崇められているアメリカ人脱走兵のリーロイドと出会う。ジャングルで平和な世界を築いていたリーロイドは、やがてフェアボーンに協力し、連合軍側のために日本軍と戦うことを決意する。しかしリーロイドの村を日本軍が攻め込み……。男たちの勇気と誇り、そして友情を描いた戦場ドラマ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Resident Evil: Extinction", "バイオハザードIII", "", "", "http://image.eiga.k-img.com/images/movie/34219/315x210.jpg?1579", 94, "ミラ・ジョボビッチ主演による大ヒット・アクションのシリーズ第3弾。前作から数年後、T-ウイルスが世界中に蔓延し、地上はアンデッドで埋め尽くされていた。アンブレラ社による追跡を避けながら1人で生き抜いてきたアリスは、離れ離れになっていたカルロスたちと再会を果たす。感染が及んでいないという安息の地・アラスカへ向かうことになった彼らは、燃料と食料を確保するために砂漠と化したラスベガスに立ち寄るが……。", "2007-11-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("萌えよ！ドラゴンガールズ", "萌えよ！ドラゴンガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "新人アイドル・福下恵美ほか、注目の若手キャストが出演するヒロイン・アクション。ネット型の格闘ゲームに没頭する中学生たちが、それぞれ自分の操るキャラクターに扮しバトルを展開する。大会優勝を目指し、腕を磨く彼らの入り組んだ人間模様も見もの。ほのかなロマンスを交え、爽やかに綴る。ネット型格闘ゲーム“武闘演舞”で美鈴を操る彰吾は、知世というキャラに出会い大会での対戦を誓い合う。しかし知世を操るのは意中の女子だった。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EUROPA EUROPA", "ヨーロッパヨーロッパ 僕を愛したふたつの国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ナチスの手から逃げ回るユダヤ人の少年、ソロモン。身分を隠し続ける彼は、ソ連では共産主義者として、連れ戻されたドイツでは純粋ドイツ人としてふるまい、生命の危機を乗り越えるが……。女流監督A・ホランドが、第二次世界大戦にアイデンティティーをほんろうされる少年の姿をユーモラスに描く。1991年、ゴールデン・グローブ賞外国映画賞など、数々の映画賞を受賞。これをきっかけにホランドは、「秘密の花園」でハリウッドに進出した。", "1990-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUZZLE", "アントニオ・バンデラス 最も危険な男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "アントレス、ペドロ、ディアゴの３人は銀行に押し入り、現金と二人の人質を奪って逃走した。アジトであるマンションの一室で、音と振動に感知する爆発物を人質に取り付け、監禁。警察に対し、逃走経路の封鎖を止めるよう要求する。そこまでは完璧だったのだが……。犯人たちの心の葛藤と緊迫感あふれる人質たちのドラマが見どころ。", "1987-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE´CALAGE　HORAIRE", "シェフと素顔と、おいしい時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "フランスを代表する人気スター、ジャン・レノとジュリエット・ビノシュ。意外にも初共演となるベテラン同士による大人の恋物語だ。シャルル・ド・ゴール空港のストで足止めを食らった二人はひょんなことから出会い、一夜をともにする。お互いに恋人との関係に問題を抱えて情緒不安定な上、価値観の全く違う二人が、次第に心を通わせていく……。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sabrina", "麗しのサブリナ", "", "", "http://ecx.images-amazon.com/images/I/51D5Kahdl5L._SL210_.jpg", 113, "　ビリー・ワイルダー作品の中で、最もおしゃれで気品に満ちたロマンチックコメディ。大富豪ララビー家に仕える運転手の娘サブリナが、富豪の次男に恋をする。父親や使用人仲間は不毛な恋を忘れさせるため、サブリナをパリへ送り出す。2年後、洗練された淑女に変身した彼女が帰国。その変貌ぶりに、次男やその兄は使用人の娘であることに気がつかなかった。オードリー・ヘプバーンが、「ローマの休日」の翌年に出演した作品で、若き日の魅力がたっぷり堪能できる。", "1954-09-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rocky V", "ロッキー5／最後のドラマ", "", "", "http://ecx.images-amazon.com/images/I/51JCEUmZ4pL._SL210_.jpg", 105, "　ソ連のリングで強敵ドラゴを倒したロッキー。しかし帰国後に脳障害が発覚し、彼はボクサー引退を決意。さらにその後、破産へと追い込まれ、トレーナーとして働くことになる。やがてロッキーは若手ボクサー、トミーの育成に乗り出し、彼を鍛え上げる。トミーは試合で勝利するが、次第に人が変わっていく。ロッキーはそんなトミーと路上で闘うことに……。シリーズ第1作を手がけ、名声を得たアビルドセンが再び監督を務めた感動作。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JASON X", "ジェイソンX・13日の金曜日", "", "", "http://ecx.images-amazon.com/images/I/51ABMOOUUuL._SL210_.jpg", 91, "第1作の監督であるS・S・カニンガムが総指揮を務め、８年ぶりに製作されたシリーズ第10作。クリスタル・レイクの地下研究所で冷凍保存されたジェイソンが、400年後の2455年に発掘される。やがて宇宙船内で復活したジェイソンは、若者や兵士たちを次々と抹殺していく。未来の宇宙空間に舞台を設定するという意表を突いたアイデアで、マンネリ感が否めなかったシリーズに新たなエネルギーを注入。デジタル技術を得ていっそうパワーアップしたジェイソンの大暴れが、これでもかと見せ場を連発する快作となった。ビデオ、DVDタイトルは「ジェイソンX」。", "2002-09-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Labor Day", "レイバー・デイ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NELL", "ネル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "J・フォスターが原作に惚れ込み、自ら製作を手掛け主演した、女性版「野生の少年」。医者のジュリーは、ノースカロライナの山の中で、母の元で文明から隔離され、今は一人で暮らしていた少女ネルを発見する。ジュリーは独特の言葉を話すネルを理解しようと努力するが、ネルを研究対象と考える精神科医のポーラが現れ、二人は対立。裁判所の判断で、二人は３ヵ月間、ネルのそばで観察をすることになる。ネルは次第に打ち解けていくが、事実を知ったマスコミが押し寄せて、ネルは心を閉ざしてしまうのだった。フォスターのなりきりぶりには、風格さえ漂う感動作。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUYVER", "ガイバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "日本の人気コミック『強殖装甲ガイバー』を、ハリウッドで映画化。アメリカを中心に活躍する、日本人の特殊メイク師S・M・ジョージの初監督作品でもある。ある科学組織から盗み出された生体兵器“ユニット”が、大学生ショーンの手に渡り、彼と合体。ハイパワーを持つ、ガイバーに変身し、組織が送り込んだゾアノイド軍団と戦う。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和元禄ハレンチ節", "昭和元禄ハレンチ節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "飲む、打つ、買うが大好きな製薬会社の営業部員とライバル会社の社員が、互いに対抗意識を燃やしながら研究所の博士の娘に接近。娘を間に骨肉の争いを繰り広げるが……。牧伸二、立川談志ら喜劇人たちがハレンチ社員に扮して熱演するドタバタ・ギャグ映画。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人生／LIFE", "人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "農村で教師をしていた高加林は、その職を生産大隊書記の息子に奪われ、しかたなく両親の下農作業に従事する。絶望の日々のなか、彼は劉巧珍という文盲の少女と恋仲になるのだが……。西安映画製作所の所長としてチェン・カイコー、チャン・イーモウらニューウェーブ監督を育てたウー・ティエンミンが所長就任直前に完成させた大作。", "1984-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEET CHARITY", "スイート チャリティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 153, "フェリーニの名作「カビリアの夜」をブロードウェイでミュージカル化したものを、舞台版の演出も手掛けたB・フォッシーが映画化。ニューヨークのダンサー、チャリティは、そのお人好しな性格のため、いつも損ばかりしている。最愛の人にも逃げられ、全財産をも失った彼女だったが……。後に「キャバレー」「オール・ザット・ジャズ」などを発表する天才振付家B・フォッシーの映画初監督作。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・空手バカ一代／格闘者", "新・空手バカ一代／格闘者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "梶原一騎の17回忌を記念して、彼の傑作『空手バカ一代』を時代設定を現代に置き換えて映画化。才能ある若き格闘家に扮する体育会系アイドル、永井大の本格派アクションが炸裂する。空手道狩野塾、塾長を父に持つ丈太郎は、稽古中に誤って父を殺してしまう。罪の意識に苛まれた彼は、やがて格闘技の裏社会で、多数の格闘家たちと闘いを繰り広げるようになり……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("After Hours", "アフター・アワーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "仕事を終えた独身サラリーマンが、殺人事件をはじめ次々に身に覚えのないやっかいごとに巻き込まれ、右往左往しながら異常な一夜を過ごす。スコセッシ監督が大学生の書いた脚本を映画化。ニューヨーカーの孤独と神経症的な面を鋭く捉えたブラック・コメディの傑作。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Heat", "ヒート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 171, "", "1996-05-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTOLERANCE", "イントレランス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 162, "1989年にオーケストラの伴奏付きでよみがえった映画史上の文字通りの超大作。古代バビロンの滅亡、キリストの処刑、聖バーソロミューの虐殺、そして現代と４つのエピソードが同時進行で絡まり合い、途切れ、再び現れては流れ出す。こうした試みは映画史上空前絶後といえる。まるで山の中から涌き出す細い流れのように始まり、次第に勢いを増し、やがて一つにまとまって大きな流れとなるように設計され、イントレランス(不寛容)から起こる悲劇を共鳴させる。４つのエピソードを結び付けるのは揺りかごを永遠に揺すり続ける母親のイメージで、象徴的なこのシーンに現れるのは、グリフィス作品に欠くことのできないL・ギッシュ。1914年のイタリア映画の超傑作「カビリア」から強烈なインスピレーションを受けて作られた映画だが、その大掛かりな撮影の様子はタヴィアーニ兄弟の「グッドモーニング・バビロン！」でチラっと描かれている。ビデオタイトルは「イントレランス(上・下)」。", "1916-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Haven", "ヘイヴン　堕ちた楽園", "", "", "http://image.eiga.k-img.com/images/movie/33689/315x210.jpg?4679", 98, "「パイレーツ・オブ・カリビアン」シリーズで人気を不動のものにしたオーランド・ブルームが、顔と心に深い傷を負いながらも恋人を一途に愛する青年を熱演し、製作も手掛けたサスペンスフルな人間ドラマ。世界中から富豪が集まるカリブ海のケイマン諸島を舞台に、脱税容疑でFBIに追われる男が島に持ち込んだ100万ドルを巡る物語と、裕福な家庭の娘と島で生まれ育った青年との悲劇的な恋の物語が交錯し、思わぬ方向へと展開していく。", "2006-10-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乳房を抱く娘たち", "乳房を抱く娘たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "高度成長下の農村。若者たちは皆都会へと出ていき、農民たちはやむなく労働力の機械化を受け入れるが、農村で粘り強く生き抜こうとする明るい青年の一団がいた……。当時の農村の悩みを反映した作品で、加藤嘉、伊藤雄之助らの脇役陣の好演もあって、決して暗くならぬ楽天的な青春映画といった一編。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あふれる熱い涙", "あふれる熱い涙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "日比混血の私生児としてフィリピンに生まれ、母親の命ずるままに日本の農家に嫁いだ一女性の姿を描き、現代の愛の在り方を問う意欲作。フィリピン人花嫁を演じる新人ルビー・モレノの初々しい魅力と、鋭い問題意識が話題になった。製作・監督・脚本の３役をこなす田代廣孝の第１回作品。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("益西卓瑪", "チベットの女・イシの生涯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "中国のシェ・フェイ監督がチベットの美しさに魅了され、オール現地ロケで撮った恋愛映画。壮麗な宗教建築や湖など、チベットのありのままの様子が季節の移り変わりとともに描かれていく。1950年、美しい歌声を持つチベットの村娘イシが、初恋の人である僧侶サムチュ、ならず者の行商人ギャツォら３人の男性とめぐり合う。しかし彼らはチベットを取り巻く時代の波にほんろうされ、波乱に満ちた人生を歩むことに。", "2000-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALIGULA", "カリギュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "アメリカの『ペントハウス』誌が初めて製作に乗り出したポルノ大作。実際に出演者がハード・コアを演じたわけではないが、局部のアップ、性器の結合がふんだんに出てくる。狂気に満ちたローマ皇帝カリギュラの暴政を描いた、一種の歴史劇。", "1980-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吸引", "少年の誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "1973年の中国。文化大革命真っ只中の北京は大人たちが田舎に下放され、子供たちだけの解放区となっていた。少年ダーシャオも両親が下放され、祖母との二人暮し。夏休みを迎えた彼は、同様に両親を下放された友人シャーダンと川で遊び、メンコをし、凧揚げに興じ、楽しい日々を送っていた。そんな中、ダーシャオはシャーダンと暮らす姉ウェンウェンに関心を抱く……。少年のひそかな思いと性の目覚めを、繊細なタッチで綴っていく。", "2002-01-01", "8,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIMPLE MEN", "シンプルメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ジム・ジャームッシュやスパイク・リーに続くニューヨーク・インディーズの雄H・ハートリーの長編第３作。一匹狼の兄と純真な弟が、23年前のペンタゴン爆破事件の容疑者として逃亡中の父を追い、ニューヨークからロング・アイランドへ。そこで二人の女と出会いやがて……。孤独だが人なつっこい、不思議な魅力あふれる登場人物たちの行動をコメディを基調にしたシンプルな語り口でテンポよく綴っている。間髪をいれず繰り出される磨きぬかれたセリフとシャープな色彩感覚を持った映像が光るハードボイルド・メルヘン。音楽のN・ライフルは、ハートリー監督のミュージシャン・ネーム。", "1992-01-01", "1,3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLOSET LAND", "クローゼット・ランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "作品が政治を批判しているとして逮捕された美貌の絵本作家。捜査官は様々な拷問を加えて、彼女の口を割らせようとするが……。M・ストウとA・リックマンが演技の火花を散らす密室劇。「ドラキュラ」でオスカーに輝いた石岡瑛子が衣裳と美術を担当している。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あぶく銭", "あぶく銭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "勝新太郎が演じるヒゲ松が、いつものように賭場荒らしをしていくうち、ヤクザ同士の集団抗争に巻き込まれていく。1972年の「座頭市御用旅」が遺作となる森一生の、晩年の１作。ヒゲ松を追い回す用心棒役に天知茂。撮影には「野菊の墓」の名手、森田富士郎が当たっている。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK RAINBOW", "ブラック・レインボウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "19世紀の世界に君臨した霊媒師たちの血を引く現代女性をめぐって繰り広げられるサイキック・ミステリー。霊媒師としてアメリカを渡り歩くマーサは、ある日、未来の惨劇を予知してしまい、やがて予言が的中したことから何者かに命を狙われるはめに。R・アークエットが悲劇のヒロインを好演。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワールドクラシック＠シネマ2011 バレエ「ロメオとジュリエット」　英国ロイヤル・バレエ　日本公演", "ワールドクラシック＠シネマ2011 バレエ「ロメオとジュリエット」　英国ロイヤル・バレエ　日本公演", "", "", "http://image.eiga.k-img.com/images/movie/56459/315x210.jpg?1647", 156, "世界各地の有名オペラハウス、バレエ団の上演を映画館のスクリーンで上映する「ワールドクラシック＠シネマ2011」（全8作品）の1作。世界屈指のバレエカンパニーでもある英国ロイヤル・バレエによる「ロメオとジュリエット」（セルゲイ・プロコフィエフ作曲）の日本公演の模様を上映。同バレエ団で長くプリンシパルを務めた日本人ダンサーの吉田都が、ロイヤル・バレエとの最後の共演としてジュリエット役を演じた。", "2011-02-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OL百合族", "OL百合族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "“百合族”コンビの美和子となおみが高校を卒業して、都心のOLとなるところから始まるシリーズ最終作。偶然に同じ会社のOLとなった二人はマンションで共同生活を始めるが、結婚願望を抱きはじめたなおみはまたしても、美和子に冷たくあたりはじめ……。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Trip", "白昼の幻想", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "1960年代にアメリカを中心に全世界に広がったドラッグ・カルチャー。LSDの服用により“トリップ”したP・フォンダらが見る幻覚をサイケデリックな画面で描く。怪奇映画を得意とするR・コーマン監督の作品。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Entre les Murs", "パリ20区、僕たちのクラス", "", "", "http://image.eiga.k-img.com/images/movie/54409/315x210.jpg?4513", 128, "生い立ちも出身国もさまざまな24人の生徒と1人の国語教師の交流を通じ、フランスの教育現場を赤裸々に描いた作品。演技経験のない24人の子供たちのリアルな芝居が注目を集め、第61回カンヌ国際映画祭パルムドールを受賞。監督は「ヒューマンリソース」のローラン・カンテ。原作「教室へ」の著者フランソワ・ベゴドーが教師役を演じる。移民や問題児の多い中学校を舞台に、傷つき反発し合いながらも、信頼を深めていく生徒たちの姿をドキュメンタリータッチで描く。", "2010-06-12", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING OF KICKBOXERS II", "キング・オブ・キックボクサー２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "格闘技のチャンピオンが総出演するハードなアクション。白人系と中国系が激しく対立するロスのキックボクシング・ジム。ある夜白人系のリーダーの妹ジュリーは、中国人のジョニーに出会い、恋に落ちるが、彼は中国系リーダーの身内で……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Infernal Affairs III", "インファナル・アフェアIII", "", "", "http://image.eiga.k-img.com/images/movie/1449/315x210.jpg?3378", 118, "アンディー・ラウ、トニー・レオン、レオン・ライなど香港の人気スター共演で描く大ヒット犯罪ドラマの完結編。潜入捜査官ヤンの死の10カ月後、警官として暮らす潜入マフィアのラウは、妻マリーとの離婚調停中。そんな彼の前にエリート警察官ヨンが現れるが、ラウはヨンもまた潜入マフィアなのではないかと疑う。第1作撮影中に3部作構想が始動し、監督・脚本・編集・音楽とも同じスタッフで全3作を一挙に製作。", "2005-04-16", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("でっかいでっかい野郎", "でっかいでっかい野郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "どこへ行っても厄介者で人並み外れた無鉄砲——“２代目無法松”気取りの風来坊を主人公にした野村監督の喜劇大作。とある娘に恋をするも、恋人がいると知ってスッパリあきらめ、逆に応援するくだりなど、いかにも渥美清主演らしい人情喜劇に仕上がっている。DVDは「渥美清メモリアル　渥美清・もうひとつの世界」に収録。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUDGEMENT NIGHT", "ジャッジメント・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "近道をして高速道路を降りたばかりに、スラムの犯罪多発地区に迷い込んだ４人組の若者たち。殺人事件を目撃し、街のギャングに追われる彼らの死闘を、緊張感たっぷりに描いたサバイバル・アクション。ラップとオルタナティヴ・ロックの融合による、斬新な音楽をフィーチャーしたサントラも話題に。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOBODY’S　BUSINESS", "知ったこっちゃない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "1970年代からインディペンデントの映像作家として活動してきたA・ベルリナーが、自らの父親オスカー・ベルリナーとの対話を通して、家族の歴史を見つめ直したドキュメンタリー。息子の映画作りの意図を一笑に付し、“わしは普通の人間だ。映画のネタにはならんぞ”とまくしたてるオスカーのシニカルな人物像が実にユニーク。多数の映像や公的な資料をパズルのようにコラージュした構成も個性的な作品だ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Scoop", "タロットカード殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/34279/315x210.jpg?1652", 95, "ウッディ・アレンが「マッチポイント」に続き、スカーレット・ヨハンソンをヒロインに据えて製作したミステリー・コメディ。ジャーナリスト志望の女子大生サンドラは、友人と訪れたマジックショーの最中に、新聞記者の幽霊からあるスクープを耳打ちされる。それは、現在ロンドンを騒がせている連続殺人鬼に関するものだった。サンドラは幽霊出現のきっかけをつくったマジシャンのスプレンディーニと共に、事件を調べ始めるが……。", "2007-10-27", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレヨンしんちゃん　ヘンダーランドの大冒険", "クレヨンしんちゃん　ヘンダーランドの大冒険", "", "", "http://ecx.images-amazon.com/images/I/61BEDiY4cJL._SL210_.jpg", 100, "しんのすけは、幼稚園の遠足でアミューズメントパーク“群馬ヘンダーランド”にやってきた。しかし、そのヘンダーランドこそ地球征服をたくらむオカマ魔女らが潜む本拠地だった。テント小屋に閉じ込められていたヘンダーランドの王女メモリ姫、連れ去られたひろしとみさえを助けようと、しんのすけがオカマ魔女に挑む。雛形あきこが本人役で登場している点も見ものだ。", "1996-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL’OMBRA　DI　UNA　COLT", "情無用のコルト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "G・グリマルディ監督が世代の異なるガンマン二人の友情と確執を哀感たっぷりに描き上げ、観る者の男泣きを誘ったマカロニ・ウエスタン。メキシコの村で用心棒の仕事を終えたデュークとスティーヴ。若いスティーヴはデュークの娘スーザンと平和に暮らすことを望んでいた。しかし、ある悪女がスティーヴを裏切り者とふれ回ったため、彼はデュークから決闘を申し込まれてしまう。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASPER", "キャスパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "キャスパーは心優しいゴースト。メイン州にある広い屋敷に３人の邪悪なゴーストとともに住んでいる。ある日、この屋敷に心霊学者のハーベイ博士と娘のキャットがやってくる。キャスパーはキャットこそが自分が探し求めていた友だちだと、あの手この手で仲良くなろうとするが……。作中のゴーストは、すべてCGで創作。TV出身のB・シルバーリングがメガホンを執っている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Company Men", "カンパニー・メン", "", "", "http://image.eiga.k-img.com/images/movie/55989/315x210.jpg?3935", 104, "リーマン・ショック後の不景気により会社をリストラされた3人の男たちが自らの人生を見つめ直し、再生していく姿を描いた人間ドラマ。主演にベン・アフレック、トミー・リー・ジョーンズ、クリス・クーパー。共演にケビン・コスナー、ローズマリー・デウィット。監督・脚本は「エデンより彼方に」「アイム・ノット・ゼア」の製作を手がけてきたジョン・ウェルズ。", "2011-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("路地へ・中上健次の残したフィルム", "路地へ・中上健次の残したフィルム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "46歳で他界した芥川賞作家、中上健次の一連の小説世界の原点というべき紀州の“路地”をめぐるドキュメンタリー。紀州出身の映画作家、井土紀州が現地を車で旅する姿を追いながら、中上自身が取り壊される前の“路地”を撮影した16ミリ・フィルムを挿入していく。『枯木灘』『千年の愉楽』『地の果て　至上の時』などの中上のテキストが井上によって朗読され、すでに失われたはずの風景がリアリティーをもって浮かび上がってくるかのような、ただならぬ気配の漂う映画になっている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("有話好好説", "キープ・クール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "　発展著しい90年代の中国都市部を舞台に、「紅いコーリャン」のチャン・イーモウが若者の恋愛と友情を描く。中国語タイトルは「有話好好説（ちゃんと話そうよ）」。露天商を営むシャオは、別れた恋人アンホンが未だに忘れられずつきまとっていた。ある日、彼は知らない男たちに痛めつけられる。その影にはアンホンの新しい彼氏ダーロンの存在が。反撃するため、シャオは通行人の男の鞄を投げつけるがその中のパソコンを壊してしまう。", "1997-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WONDERLAND", "ワンダーランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "伝説のポルノスター、ジョン・ホームズと、ハリウッドのナイトクラブを牛耳るエディー・ナッシュが関与していたことで注目を集めた'81年の“ワンダーランド殺人事件”。ホームズの妻と恋人の証言を基に事件の真相に迫りながら、ホームズと彼女らの複雑かつ純粋な愛の形を浮き彫りにする。'81年、L.A.で残虐な殺人事件が発生。ジョン・ホームズと彼のギャング仲間の男が取り調べを受けるが、それぞれの証言は食い違い……。", "2005-05-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SO　WEIT　DIE　FUSSE　TRAGEN", "9000マイルの約束", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 158, "発売されてから40年以上が経つ現在でも売れ続ける、ドイツの作家バウアーのベストセラーを映画化。戦犯として送還されたシベリアから脱出を図り、祖国を目指し9000マイルを歩き続けた実在のドイツ兵、クレメンス・フォレルの苦難のドラマを描き出す。６万キロを超えるロケにより撮り上げられた大自然の映像が圧巻。第二次世界大戦後、ドイツ軍の中尉クレメンスは、戦犯としてシベリアに送還された。収容所で待っていたのは過酷な労働だった。脱走は不可能と思われたが、妻子に会いたい一心で、9000マイル先の故郷を目指して脱走する。", "2001-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fool's Gold", "フールズ・ゴールド　カリブ海に沈んだ恋の宝石", "", "", "http://image.eiga.k-img.com/images/movie/53479/315x210.jpg?2300", 112, "トレジャーハンターのフィンは、18世紀に海底に沈んだスペイン王室の財宝を探し続けている。ついに有力な情報を得た彼は妻のテスが働く大富豪ナイジェルの船に乗り込んで財宝の追跡を開始。しかし元師匠やギャングたちも財宝を狙っており……。「10日間で男を上手にフル方法」のマシュー・マコノヒー＆ケイト・ハドソンが再共演したロマンティック・アドベンチャー。「最後の恋のはじめ方」のアンディ・テナントが監督を務める。", "2008-05-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER，MY SWEET", "ブロンドの殺人者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ハードボイルド小説が生んだ不滅のヒーロー、フィリップ・マーロウを主人公とするR・チャンドラーの名作の２回目の映画化（現在までに３度作られている)。昔の女を探してくれと出所したての男に頼まれたマーロウは、この事件をたどるうち宝石盗難事件や殺人事件に巻き込まれていく。1930年代のミュージカル・スター、D・パウエルがイメージ・チェンジをはかって成功、ファンを驚かせた。ハンフリー・ボガートのマーロウなどと違い、大都会の巷に沈むしがなさが色濃く出ている。RKO映画らしい暗いトーンで統一された沈んだ画面の中から思いがけない真相が浮かび上がり、フィルム・ノワールとしても一級の作品。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOLDIER BLUE", "ソルジャー・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "1864年、コロラド州サンドクリークで、600人のシャイアン族が騎兵隊によって虐殺された史実を映画化した異色ウエスタン。白人の都合のいいように解釈されてきた西部開拓史をネルソン監督は、ラスト15分間の凄絶な残虐シーンの中で痛烈に告発した。原作はオルソンの『太陽を射つ矢』。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAGNUM FORCE", "ダーティハリー2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "シリーズ第２作。警察内部の汚職を背景に、不正刑事たちを追い詰めるハリー刑事の活躍を描く。サンフランシスコの大都会で社会正義を守るため、暴力をもって立ち向かうハリー刑事が冷徹に職務を遂行していく。警察の腐敗構造をリアルに見せた演出が出色。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("えてがみ", "えてがみ", "", "", "http://image.eiga.k-img.com/images/movie/56759/315x210.jpg?2088", 95, "「ふゆの獣」（2010）が第11回東京フィルメックスで最優秀作品賞を受賞した内田伸輝監督が、02年に手がけたドキュメンタリー。出会った人々の似顔絵をハガキに描いて送るナベと俳優志望のヨシタケの共同生活を、いくつもの季節やロケーションを経て撮り続けた。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TESIS", "テシス 次に私が殺される", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "本国スペインでの公開当時、歴代興収記録を塗り替える大ヒットを飛ばしたサスペンス・スリラー。“映像における暴力”というテーマの卒論のリサーチに取りかかった女子大生アンヘラ。ところが担当教授は謎の死を遂げてしまい、彼女が死亡現場からひそかに持ち帰ったビデオには、おぞましい本物の殺人シーンが収められていた。残酷ビデオの収集家であるオタク学生チェマに相談を持ちかけ真相究明に乗り出すアンヘラだったが、大学内に潜む殺人鬼は彼女にも魔の手を伸ばしてくる……。闇社会で実際に取引されているというスナッフ・ビデオを題材に、当時、23歳だったアメナーバル監督が低予算を感じさせない緊迫のドラマを創造。「ミツバチのささやき」のかつての天才子役A・トレントが主演と聞いただけでも、そそられるファンは少なくないはず。ビデオ、DVD発売時タイトルは「殺人論文／次に私が殺される」。", "1996-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FASHION CRIMES", "偽りのジェラシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "トップ・モデルが、電話を借りに入った別荘で殺人の瞬間を目撃。警察署長はさっそく捜査を開始するが、別荘はここ20年間使われておらず、彼女が殺人を見たという部屋すらなかった。やがて彼女の周囲で奇怪な事件が相次ぎ……。超心理学的要素も取り入れたファッショナブルなミステリー。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hollow Man 2", "インビジブル2", "", "", "http://image.eiga.k-img.com/images/movie/33779/315x210.jpg?1827", 91, "透明人間になる力を手にした天才科学者が暴走するSFスリラー「インビジブル」の続編。前作でケビン・ベーコンが怪演した透明人間をクリスチャン・スレイターが演じる。軍が開発した兵器“姿なき戦士”の実験台として、元特殊部隊員マイケル・グリフィンが選ばれた。彼は透明人間になるが、自分がだまされていた事を知り、軍への復讐を開始する……。前作でメガホンを取ったポール・バーホーベンが製作総指揮を務める。", "2006-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CAGE AUX FOLLES", "Mr.レディ Mr.マダム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "パリで大ヒットしたジャン・ポワレの舞台劇を映画化した傑作コメディ。舞台は南フランス・サントロペ。人気のオカマ・クラブ“狂人の檻”を経営する中年のゲイカップル、レナートとアルバンは、クラブの階上に愛の巣を構えている。ある日、レナートの一人息子が結婚話を持ち込んできた。相手はこともあろうに、お堅いことで知られる政党書記長の一人娘。男だけの愛の巣に女が乗りこんでくる。あわてふためく二人の男。舞台でも同じ役を演じたセローのグロテスクな女装ぶりが、大いに笑いを誘い、世界中でも大ヒットした。", "1978-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月光仮面・幽霊党の逆襲", "月光仮面・幽霊党の逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "鉱脈の秘密を握る大岡は“幽霊党”と名乗る黒覆面の一団に狙われ、娘のはる子が誘拐された。鉱脈の眠る三ッ峰峡に向かった幽霊党の前に月光仮面が現れ、はる子を救い出し一味を撃滅する。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サオヤの月", "サオヤの月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "石井聰亙や瀬々敬久などの作品でキャリアを積んできた藤川佳三監督による私的ドキュメンタリー。1度は映像の世界を離れたものの再びカメラを手にした彼が、離婚した妻を追い、崩壊した家族を再生しようと試みる。'01年2月に協議離婚の末に別れた、藤川佳三とその妻の幸子。決別した二人がカメラを前に、離婚に至った経緯と過去を振り返る。", "2005-10-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL CASO MORO", "首相暗殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1978年に“赤い旅団”によって誘拐されたイタリアの元首相アルド・モロがその後暗殺されるまでの55日間を、克明に描いたドキュメンタリー・タッチの問題作。主演のヴォロンテは、この作品での演技で全世界から高い評価を得ている。", "1986-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Félix et Lola", "フェリックスとローラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "移動遊園地のオーナー、フェリックスは、遊園地でさびしげに過ごす女性ローラに一目ぼれをしてしまう。ローラの申し出を受け、フェリックスは謎の多い彼女を雇うことにするが、ローラはある男性を見て逃げ出してしまう。「仕立て屋の恋 」「橋の上の娘」のパトリス・ルコント監督による、「シャルロット・フォー・エヴァー」のシャルロット・ゲンズブール主演のラブ・ロマンス。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロボ☆ロック", "ロボ☆ロック", "", "", "http://image.eiga.k-img.com/images/movie/53079/315x210.jpg?1867", 92, "「ブリスター！」の須賀大観監督がメガホンを取った青春ロボットアクション。自称“天才便利屋”のマサルは、オタクな公務員ニラサワから奇妙な依頼を受ける。土星人の侵略から地球を守る幻の巨大ロボット“ランドツェッペリン”を動かすために、マサルの声が必要だというのだ。同じ頃、マサルと親友のコウは麻薬がらみの危ない仕事を引き受けるが……。突然地球の運命を託されてしまった冴えない青年マサルを演じるのは「パッチギ！」の塩谷瞬。", "2007-11-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I WALKED WITH A ZOMBIE", "ブードゥリアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "ホラー映画のメイン・キャラクター、ゾンビもののごく初期の傑作。1940年代を代表する怪奇映画製作者V・リュートンの指揮で「ジェーン・エア」が自由に脚色されている。ハイチの名家に就職口を見つけ、採用された美人看護婦は島にやって来たとき、患者が生ける死者ゾンビと知る。やがて彼女はこの症状が、土地の宗教ブードゥー教に発していることを突き止めるのだが……。ゾンビとなっている男の妻とヒロインが初めて出会うシーンや、夜中に眠っているヒロインが危機にさらされるシーンなどは必見。ジョージ・A・ロメロの作品とは違って、ゾンビが普通の人間と同じ外見をして、同じように歩くことなど、意外なところも多い。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Passion of The Christ", "パッション", "", "", "http://image.eiga.k-img.com/images/movie/1589/315x210.jpg?3945", 127, "人間イエス・キリストの死までの最後の12時間を写実的に描く問題作。メル・ギブソンが構想12年、製作費2500万ドルには私財を投じ、イタリアのチネチッタ・スタジオで撮影して完成。公開の可否を巡って長期に渡りキリスト教団体等との論争が繰り広げられたが、公開した途端に驚異の大ヒットを記録。観客には死者や警察に自首する犯罪者なども出現した。セリフはすべてラテン語とアラム語で語られ、全米でも英語字幕付きで公開。", "2004-05-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAB", "CAB", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "平凡な男が、ひょんな事から殺人鬼となっていく姿を描いた異色のサスペンス。心優しいタクシードライバーの木田良介は、ある晩、泥酔したヤクザの親分を乗せた。あまりの悪態から警察に駆け込む木田だが、そんな彼を見てヤクザは逆上。木田に襲いかかるが、もみ合ううちに木田が誤ってヤクザを刺し殺してしまう。正当防衛が成立しながらも報復を恐れて暮らす彼の前に、人を殺すことの喜びを語るマリコという謎の女が現れたことから、彼の中で何かが変わっていく。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shamo", "軍鶏 Shamo", "", "", "http://image.eiga.k-img.com/images/movie/53449/315x210.jpg?2956", 105, "両親殺しで少年院に送られた亮は、過酷な日々を生き抜くため空手家の黒川に師事する。相手が死ぬまで闘い続ける軍鶏のごとく鍛えあげられた彼は、刑期を終えて世界格闘技トーナメントを目指すが……。人気漫画「軍鶏」を、原作者の橋本以蔵が自ら手掛けた脚本と香港のソイ・チェン監督のメガホンで実写化。「インファナル・アフェア」シリーズのショーン・ユー、K-1ファイターの魔裟斗ら香港・日本から集った豪華な顔ぶれが熱い闘いを繰り広げる。", "2008-05-03", "6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREAKFAST AT TIFFANY’S", "ティファニーで朝食を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ニューヨークの超一流宝石店ティファニーを観光名所に仕立て上げた、ヘプバーンの代表作の一つ。猫と二人暮らしのホリーは、いわばコール・ガール。ティファニーのショーウインドーを眺めながらパンをかじるのが大好きだ。ある日、彼女のアパートに、何やら怪しげな若手小説家が引っ越してくる。金持ち中年女の世話を受ける彼は、妖精のように捉えどころがなく、時として少女のように純真なホリーに次第に心惹かれるようになるが……。ヘプバーンの魅力満開。その優雅で軽やかな身のこなし、細身の体にフィットしたファッションの数々、愛くるしい微笑は忘れられない。おしゃれで都会的センスあふれるラブ・ストーリーの佳品。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAN ON A HOT TIN ROOF", "熱いトタン屋根の猫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "アメリカ南部の旧家を舞台にした愛憎の人間ドラマ。ワンマンな当主ビッグ・ダディがガンに冒されていることがわかり、酒びたりの息子ブリック、ヒステリー気味のその妻マギー、そして遺産の分け前をもらいにきた兄夫婦らがすさまじい人間模様を繰り広げる。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リング２", "リング２", "", "", "http://ecx.images-amazon.com/images/I/21CPBXZC79L._SL210_.jpg", 95, "主要スタッフ＆キャストが再結集したモダン・ホラー。前作でチラリと登場した中谷美紀扮する高野舞を主人公に据えた映画用オリジナル・ストーリーになっている。恋人、高山竜司の不可解な突然死に納得できない舞。竜司の別れた妻・玲子の職場の同僚や医師とともに、呪いのビデオの謎に深入りしていく彼女は、非業の死を遂げた山村貞子の恐るべき邪悪な怨念と対決する。１週間というタイムリミットが強調されていた前作のようなストレートな恐怖感は薄れたものの、多彩な登場人物に拡散していく不安を、巧みな音響効果で盛り立てる。「らせん」で死んだはずの浅川玲子と幼い息子が身を潜めていたという設定になっており、あくまで「リング」の後日談として観賞すべき。", "1999-01-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キッスで殺せ！", "キッスで殺せ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "　私立探偵マイク・ハマーは深夜のハイウェイで謎めいた美女に出くわす。しかし男たちに襲われ、彼女は殺され、ハマーも重傷を負ってしまう。事件の真相を探るハマーは助手ベルダを人質に取られながらも、核開発をめぐる陰謀に迫っていく。名匠アルドリッチが過激な暴力描写で描く衝撃のフィルムノワール。ゴダールらヌーベルバーグ作家たちによって支持され、カルト化。55年版は上映時間104分。98年には106分のディレクターズ・カットが初公開された。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本海大海戦", "日本海大海戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "東宝が、この後、年１本のペースで製作していく戦記大作“８・15”ものの第１作。明治の日露戦争を、東郷平八郎大将をメインとして描いている。クライマックスはバルチック艦隊と連合艦隊の大海戦。これが事実上最後の映画となった円谷英二特技監督は、自らの特撮技術の、一つの集大成とも言うべき、実写さながらの迫力ある戦争シーンを作り上げている。ドラマとしては平板だが、戦争映画の特撮を語るうえでは、はずせない作品。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("御存じ快傑黒頭巾 危機一発", "御存じ快傑黒頭巾 危機一発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "戦艦ニューブリテン号を、持ち主の王一成から譲渡してもらおうとする、薩摩藩の小松帯刀と益満休之助は、新選組支配・松平主税介に妨害される。主税介は、王の息子・一松を誘拐。その一松を黒頭巾が助けるが……。おなじみの七変化に加え、大友柳太朗が“黒頭巾節”を歌っている。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大奥", "大奥", "", "", "http://image.eiga.k-img.com/images/movie/55179/315x210.jpg?3466", 116, "2009年度手塚治虫文化賞を受賞したよしながふみの人気コミックを実写映画化。「木更津キャッツアイ」シリーズの金子文紀監督がメガホンをとり、人気アイドルグループ「嵐」の二宮和也、柴咲コウが主演を務める。江戸時代、男だけに感染するナゾの疫病がまん延し、日本の8割の男が死に絶えた。女が要職に就き、男が体を売る男女が逆転した世の中に、ひとりの女将軍に3000人の美しい男たちが仕える女人禁制の大奥があった。武士道を追い求める青年・水野祐之進は、貧しい旗本の実家を救うため、またかなわぬ恋の相手・お信への思いを断ち切るため、大奥にあがることを決意する。", "2010-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大佛開眼", "大佛開眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "たび重なる大仏建立反対派の妨害で、天才彫刻師・楯戸ノ国人は窮地に立たされた。一時は中止の命が下ったものの、行基の遺言などによって建立再開が決定するが、またも反対派の策謀によって、妨害が加えられる。国人は捨て身で妨害を阻止する。天平時代を舞台とした大歴史絵巻物語。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROKEN MONEY", "ブロークン・マネー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ロサンゼルスの美人車泥棒フランチェスカは、リノで盗んだ車を調べてびっくり。なんと、270万ドルのアングラ・マネーとスーパーK（ニセ100ドル札）の原版が積まれていたのだ。自首か取引か？　何も知らずに彼女の道連れになってしまった新聞記者のジャックとともに、フランチェスカはメキシコへの脱出を図る。執拗に彼らを追い詰めるマフィアと警察。果たして、二人は国境突破できるのか？ カーチェイス、銃撃戦、ヘリの来襲など、ハデなアクションが目まぐるしく展開する。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美脚迷路", "美脚迷路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "年商数十億円というネット・ビジネスの成功者である竹内が焼身自殺した。彼の幼なじみである刑事の加藤は、悪質な手段でのし上がった竹内を追っていた本庁の刑事から、怪しげなクラブMAZEの存在を告げられる。MAZEに足を踏み入れる加藤。そこにはボンデージに身を包んだ女性ダンサーたちの美脚があふれ、男たちを性の迷路に誘い込んでいた。そこに加藤と竹内の共通の幼なじみである七緒子が現れ、ここが竹内がひそかにオーナーを務めていたクラブであることがわかるが……。廣木隆一が妖しい映像で、エロスに潜む男と女の青春の影をミステリアスに描いた切ないサスペンス。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VICTIM OF LOVE", "疑惑の行方　アンダー・サスピション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "セラピストのテスは、友人の紹介で魅力的な男性ポールと出会う。次第にポールに惹かれていくテスだが、実は彼は、テスの患者の一人、カーラの恋人だった。しかもカーラの精神的不安定の原因が、ポールの悪魔のように冷酷な仕打ちにあるというのだ。一方、テスとポールの関係に気付いたカーラは、嫉妬と憎悪からテスに執ような嫌がらせを始める……。P・ブロスナンが、セクシーで危険な男の魅力を存分に発揮するエロティック・スリラー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESPERATE MEASURES", "絶体×絶命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "大病院を舞台に、刑事と凶悪犯の戦いが繰り広げられるサスペンス活劇。骨髄移植を要する息子の適合ドナーが、服役中の凶悪犯罪者であることを知った刑事コナーは、その男マッケイブを説得して手術を承諾させる。だが、病院に運ばれたマッケイブは明晰な頭脳を働かせて脱走。手術のタイムリミットが迫る中、コナーは職権を超えて、悪賢く立ち回るマッケイブを追う。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大学のお姐ちゃん", "大学のお姐ちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "大学のキャンパスに繰り広げられる明朗青春映画。日南大学の女子大生・敏子、美津子、重子の三人組はいずれ劣らぬスーパー・レディだが、フランス語の授業は大の苦手だ。単位を落として退学を決意する３人。しかし病気の教授に代わってハンサムな講師が来ると知って、俄然猛アタックを開始する。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獅子王たちの最后", "獅子王たちの最后", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ヤクザ社会に足を踏み入れた、幼なじみである武闘派の和馬と知能派の智弘、臆病な弥生。和馬と智弘はともに組んでトップを目指すが、暴力団新法施行以後、二人の方向が微妙にズレ始める。一方、脱落した弥生はホストとして働きながら、煮え切らない日々を送るが……。哀川翔をはじめ、若手俳優陣のエネルギッシュな演技が光る、せつない青春群像劇。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("МАΤЬ", "母", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ゴーリキーの同名小説をプドフキンが映画化、ソ連サイレント時代の名作の１本に数えられている。貧困の中で生きる母が、革命運動に参加する息子の影響で、社会の矛盾、改革の意識に目覚めていく。1968年にサウンド版として再生された。", "1926-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誰よりも金を愛す", "誰よりも金を愛す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "唄で有名な小原庄助さんの十八代目・庄助は身上をつぶした先祖の汚名を挽回すべく、億万長者の夢実現を目指し“億万証券”に就職する。一向に芽の出なかった庄助も幽霊となった初代の助けを得て営業部長になるが……。有名コメディアンが幽霊に扮する楽しい一編。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TARAS BULBA", "隊長ブーリバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "16世紀、ウクライナの覇権を争うトルコとポーランドの戦いを背景にしながら、ポーランド軍に裏切られたコサックの隊長ブーリバの復讐劇を、アクション派のJ・L・トンプソン監督が描いた文芸スペクタクル。波乱万丈の物語もさることながら、迫力満点の戦闘シーンなど見どころも盛りだくさん。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN CONDAMNE A MORT S'EST ECHAPPE", "抵抗（レジスタンス）−死刑囚の手記より−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "レジスタンスで逮捕された中尉が独房にいれられた。手錠のはずし方を覚え、サジで作ったナイフで羽目板を削り、毛布で綱を作るうちに、死刑の宣告が下った。そんなとき、上半身は独軍服の少年が彼の独房に送り込まれる。彼は独軍のおとりなのか……。ナチスの傀儡政権ビシー政府時代の実在の軍人の脱獄手記を映画化した、ブレッソン監督の長編第4作。原題の<ひとりの死刑囚が脱獄した>のとおりのシンプルな物語を、徹底したクロースアップの連続で描ききる驚きの傑作。", "1957-12-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("B.A.P.S.", "バップス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ジョージア州の田舎町でウェイトレスをしているニシとミッキーは夢を達成するために、ロスで行われるミュージック・ビデオのオーディションに向かうが、あっさり落選。そんな二人に今度はイザックという男が声をかけてくる。初めて乗るリムジンに興奮する二人だったが、叔父の財産を狙う彼に利用されそうになり……。「エグゼクティブ・デシジョン」のH・ベリーが主演した、ポップなシンデレラ・ストーリー・コメディ。NBAのD・ロッドマン、ラップ・ミュージシャンのヘビー・Dらが特別出演している。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モスクワわが愛", "モスクワわが愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "白血病で余命いくばくもない日本のバレリーナが、素質を認められて正式にボリショイ・バレエ団へ入団。幸運にも次回公演作のプリマに抜てきされ、血のにじむようなレッスンを重ねるのだが……。彼女とソ連人彫刻家との国境を越えた愛を描いたメロドラマ。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NUN’S STORY", "尼僧物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 152, "ベルギーの医師の娘ガブリエラは、看護尼となり自国の植民地コンゴで原住民に対する医療活動に励む。だが、第二次大戦が勃発。ナチス占領下の祖国で父が敵兵に銃殺されたとき、真に生きるため、尼僧服を脱ぐのだった。ヒューマンな香り高い傑作。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗殺", "暗殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "司馬遼太郎の一連の幕末暗殺史を描いた“幕末”というシリーズの中に収められてる短編小説『奇妙なり八郎』を、篠田正浩監督が好漢・丹波哲郎を得て映画化したテロリズム時代劇の秀作。浪人・清河八郎は文武に優れていた。しかし、農村出身で何の門閥もないため、極端なまでに権謀術策を用い、ついに勤皇・佐幕の両派から敵としてつけ狙われ非業の最期を遂げる。篠田正浩の凝った画面構成と、シャープなアクション処理が見事なアンサンブルを見せ、特にラストの清河八郎暗殺シーンの息をのむ映像処理は、篠田のハッタリのきいた美学が見事に昇華した名シーンであった。丹波哲郎が清河八郎に扮し、豪快な中にも滑稽味を加えた演技をみせ、彼の代表作の１本となった。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗黒街最大の決闘", "暗黒街最大の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "アメリカ帰りの愚連隊と新興ヤクザが手を組み、旧勢力のヤクザ組織と壮絶な抗争を繰り広げるギャング映画。井上梅次の見せ場をタップリ盛りこんだ職人芸の演出が光る。この年、鶴田浩二は「人生劇場・飛車角」に出演。ヤクザ映画のスターとして彼が君臨する過度期に作られたのが本編だ。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lake House", "イルマーレ", "", "", "http://image.eiga.k-img.com/images/movie/1699/315x210.jpg?4032", 98, "01年製作の同名韓国映画を「スピード」以来12年ぶりとなるキアヌ・リーブス＆サンドラ・ブロックの共演でリメイクしたタイムトラベル・ラブストーリー。湖畔に建つガラス張りの一軒家に住む女医のケイトは研修を終えて新たな勤務先のあるシカゴへ引っ越すことになる。手紙の転送をお願いするために、次の住人に郵便ポストに置き手紙を残したケイトだったが、次の住人であるアレックスから奇妙な手紙を受けとる……。監督はアルゼンチン、オランダを経てハリウッドに辿り着いたベテラン映像作家アレハンドロ・アグレスティ。", "2006-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ACCIDENTAL TOURIST", "偶然の旅行者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "全米書評家協会賞に輝いたA・タイラーの同名ベストセラー小説をもとに「白いドレスの女」の黄金トリオ、カスダン監督、W・ハート、K・ターナーが再び組んだ、大人向けのロマンティック・コメディ。旅行ガイドブックのライター、メーコンは、一年前に不慮の事故で一人息子を亡くし、そのショックから立ち直れない妻にも去られて、今は一人暮らし。ある時、子持ちの犬の調教師ミュリエルと出会い、人生観が一変、恋に落ちた。メーコンの書く言葉の一つ一つが、さまよえる心の旅人への適確なアドバイスにもなっていくという、人生の哀歓に満ちた小粋な一編だ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女戦士与超人／AMAZONS AND SUPERMEN", "アマゾネス対ドラゴン／　世紀の激突", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "収穫の頃になると襲ってくる女戦士軍団アマゾネスから村人を守るため東洋の空手使い、怪力の黒人、若い武芸者が立ち上がる。「アマゾネス」に「七人の侍」を掛け合わせたような娯楽映画。", "1974-01-01", "4,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蟹工船", "蟹工船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "昭和の初め、折からの不況であぶれた鉱夫、農夫、労働者上がりの雑夫たちを乗せ、蟹工船博光丸が出港した。監督はむりやり作業を強制するがいっこうに生産高が上がらない。漁夫らの反抗を拳銃でおさえ、監督はソ連領海への侵入を企てる。昭和初期に書かれた小林多喜二のプロレタリア小説を原作に、俳優の山村聰が監督。群像劇のダイナミズムにあふれている。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hickey & Boggs", "殺人者にラブ・ソングを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "人気TVシリーズ“アイ・スパイ”の私立探偵ヒッキー＆ボッグスのコンビを、そのまま映画に移行させたアクションもの。メリー・ジェーンという名の女を捜す仕事を請け負った二人組は、やがて怪事件に巻き込まれていく。アクション映画の俊才W・ヒル監督の脚本デビュー作でもある。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rock of Ages", "ロック・オブ・エイジズ", "", "", "http://image.eiga.k-img.com/images/movie/56679/315x210.jpg?2398", 0, "1980年代を代表するロックナンバーを中心に構成された人気ミュージカルを、「ヘアスプレー」のアダム・シャンクマン監督が映画化。ライブハウスで働きながら歌手を目指す青年ドリューと少女シェリーは、互いの夢を語り合ううちにひかれあうようになる。一方、あこがれのロックバンド「アーセナル」で活躍するステイシー・ジャックスは、富や女におぼれた生活を送っていたが……。映画初出演のディエゴ・ボネータ、「バーレスク」のジュリアン・ハフをはじめ、ラッセル・ブランド、ポール・ジアマッティ、キャサリン・ゼタ＝ジョーンズらが共演。トム・クルーズが“ロックの神様”ステイシー・ジャックスを熱演。「デフ・レパード」「ジャーニー」「ボン・ジョヴィ」「ポイズン」「ホワイトスネイク」などのヒットナンバーが多数登場。", "2012-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("徳川女系図", "徳川女系図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“異常性愛路線”と呼ばれた石井輝男監督によるカルト・シリーズ第１弾。エロスとバイオレンスに徹し、当時の観客を驚かせヒットした問題作だ。江戸時代の五代将軍・綱吉のやりたい放題のセックス三昧と、それを平気で受け入れる大奥の女たちの生態を活写。将軍が二派に分かれた大奥の新人を要望すれば、嫉妬でその女はリンチに遭う。そんな状況を嘆いた将軍の性欲はさらに……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE VLIEGENDE HOLLANDER", "さまよえる人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "「レンブラント」「イリュージョニスト」などで知られるオランダの映像詩人J・ステリングが、７年の準備期間をかけて完成させた寓話風の歴史大作。16世紀後半、スペイン帝国に支配されたオランダ。フランドル地方の農村に生まれ、ダッチマンと呼ばれて成長した若者が、亡き父親が残した金の聖像をめぐる争奪戦に巻き込まれる。やがて彼は、自分を救ってくれた幼なじみの女性ロッテとともに逃亡。二人は世にも数奇な大冒険を繰り広げていく。", "1995-01-01", "70,7,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風船", "風船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "大佛次郎の新聞小説を、川島雄三監督が助監督の今村昌平とともに脚色した大人の愛の物語。かつて天才画家としてもてはやされた村上春樹は、今では写真工業会社の社長として成功していた。息子の圭吉は父の会社の部長を務め、久美子という愛人がいたが、圭吉の心が他の女性に傾いたことを知った久美子は自らの命を絶つ。圭吉に絡む３タイプの女を新珠三千代、北原三枝、左幸子が好演。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ДНИ ЗАТМЕНИЯ", "日陽はしづかに発酵し…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "中央アジア・トルクメニスタンの荒涼とした町に暮らすロシア人医師ジーマ。ある日、彼と若いアジア人の友人サーシャ宛てに、送り主不明の小包が届く。その時からジーマの周囲の歯車がズレ始め、彼は遠い世界の終末を感じ取るようになる……。多作で知られる現代ロシア映画の鬼才A・ソクーロフ監督がペレストロイカ後に製作した作品。複雑なロシア現代史を背景にソクーロフ独自の世界観、終末観を漂わせ、大胆なカメラワークとエキゾチックな音楽が広大な中央アジアの大地に不思議な色彩を加えている。", "1988-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PYRATES", "パイレーツ この恋、火気厳禁！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ディスコで出会って一目惚れした男女が、愛し合うたびに火事が起こってしまう!?　“アツアツ”のカップルの恋模様を描いたコメディで主演K・ベーコンのお相手は実際の妻であるK・セジウィック。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Star Wars: Episode I - The Phantom Menace 3D", "スター・ウォーズ　エピソード1 ファントム・メナス 3D", "", "", "http://image.eiga.k-img.com/images/movie/57409/315x210.jpg?4724", 136, "SF映画の金字塔「スター・ウォーズ」全6部作を3D化した第1弾。平和な惑星ナブーが銀河通商連合に襲撃され、事件の調査に向かったジェダイ騎士クワイ＝ガン・ジンとそのパダワンのオビ＝ワン・ケノービは、メカの不調で砂の惑星タトゥイーンに不時着する。そこで出会った9歳の少年アナキンの中に秘められたフォースの力を感じたクワイ＝ガンは、アナキンをジェダイ騎士団に入団させようとする。", "2012-03-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暖流", "暖流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "腐敗した病院の立て直しをめぐって院内で展開される権力争いを背景に、院長令嬢、事務局長、医師、看護婦らの恋模様を綴ったメロドラマ。劇作家・岸田國士の新聞小説を、「非常線の女」「浮草物語」などの戦前の小津作品の脚本で知られる池田忠雄が脚色。当時まだ名声を確立していなかった吉村公三郎の出世作となった。もともとは島津保次郎が映画化する予定だったが、彼が松竹を離れたため吉村にまわってきた企画である。オリジナル版は前後編計180分の大作だが、現存プリントは１本に短く再編集された124分版。助監督に木下惠介、中村登らがついている。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姦C（カシマシイ）", "姦C（カシマシイ）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "伊藤陽佑、林剛史、冨田翔ら、戦隊ヒーローとして活躍した俳優たちが集結したネットムービーを、劇場用にリメイクしたコメディドラマ。ニート青年の武部は、ヒーリングソウルパワーミュージック部の先輩・中沢から送られてきた奇妙なキノコを食べて以来、部屋の中でグラビアアイドルたちと一緒に暮らしはじめる。一方、中途半端なヤクザになった宮本は、誤解からアニキに殺されかけてしまい……。", "2007-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユニットバス・シンドローム", "ユニットバス・シンドローム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "自分はこの世界にとって存在に値するか？ ネットの普及などによって、以前にも増して深刻化している“個人にとって根源的な問い”を、映画なりの手法で突き詰めた、新人監督、山口智の意欲作。失恋のショックから再生していく青年の心象風景をロード・ムービーとして紡ぎ出す。別れた恋人を忘れられないフジモトは、友人ダイスケの家のユニットバスで幽霊に遭遇。“彼女”は現世に未練を残したまま自殺した少女だった……。", "2005-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京の休日", "東京の休日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "国際都市“TOKYO”に、謎のドラッグをめぐって老大富豪、パンク野郎、チャイナガールなど、各国から集まったアウトローたち。彼らによる争奪戦に巻き込まれたロッカー少女の不思議な冒険物語。CM出身の長尾直樹の劇場映画デビュー作で、「アルファヴィル」のE・コンスタンチーヌやアレックス・コックス作品の常連D・ルードなど国際色あふれる顔ぶれで描く。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sagan", "サガン　悲しみよこんにちは", "", "", "http://image.eiga.k-img.com/images/movie/54339/315x210.jpg?4014", 122, "デビュー作「悲しみよ　こんにちは」が世界的ベストセラーとなり、弱冠18歳で富と名誉を手にした作家フランソワーズ・サガンの波乱万丈な人生を描いた伝記映画。「エディット・ピアフ／愛の賛歌」のシルビー・テステューが自由奔放なサガンを好演している。気ままで贅沢な暮らしを謳歌するサガンだったが、恋愛や結婚はトラブル続きで、傷心を紛らわすための酒と浪費が増大していく……。", "2009-06-06", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニライカナイからの手紙", "ニライカナイからの手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "沖縄本島の南、八重山諸島の竹富島を舞台に、郵便局長の祖父とその孫、そして東京で暮らす母親の手紙を通した深い絆と、少女の成長が描かれた珠玉の感動作。時代は変わっても思いを伝える最良の手段である手紙と沖縄の離島の素朴な生活が、忘れかけていた大切なことを思い出させてくれる。沖縄、竹富島。オジィと暮らす風希は毎年誕生日に届く手紙だけが東京の母とのつながり。やがて母への思いを募らせ、成長した風希は……。", "2005-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハード・リベンジ、ミリー　ブラッディ・バトル", "ハード・リベンジ、ミリー　ブラッディ・バトル", "", "", "http://image.eiga.k-img.com/images/movie/54669/315x210.jpg?3338", 0, "水野美紀主演のバイオレンス・アクション「ハード・リベンジ、ミリー」の続編。死闘の末に悪党軍団ジャック・ブラザーズのジャックを葬り、家族のための復讐を遂げたミリーだったが、今度はジャックの仲間から命を狙われることになる。そんな中、ミリーのもとに恋人の復讐を手伝って欲しいという美少女・ハルが現れるが……。長澤奈央がシリーズ初出演。", "2009-07-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEAMBOAT ROUND THE BEND", "周遊する蒸気船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ミシシッピー河を蒸気船で旅するドク・ジョンの甥っ子デュークが、フリーティ・ベルという娘を救ったために殺人を犯してしまう。話を聞いて正当防衛だと判断したドクはデュークを自首させるが、なぜか裁判の判決は死刑。何とかデュークを救いたいドクとフリーティは知事への直談判を試みるが、悪友イーライ船長が彼らの前に立ちはだかる。J・フォードの“ウィル・ロジャース３部作”の最終編にあたる軽やかな人情喜劇。アメリカ南部の雰囲気を吸い込みながら、クライマックスの主人公とライバルの蒸気船レースまで、J・フォードのスラップスティックな話術が冴えわたる。主演の国民スター、W・ロジャースは、本作を撮り終えて間もなく飛行機事故で還らぬ人となった。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAYHEM", "メイハム 乱暴者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "“腐った街だぜここは”と、凶悪な犯罪都市で行き場と人生を見失った二人の若者が、犯罪者狩りを始めるが、社会は彼らを残忍な殺人者として追い詰める。そして悲劇的な運命が彼らを襲うのだった。殺りくシーンの連続で描くハード・アクション。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武林女大生", "最強☆彼女", "", "", "http://image.eiga.k-img.com/images/movie/54049/315x210.jpg?3098", 115, "「猟奇的な彼女」「僕の彼女はサイボーグ」のクァク・ジョエン監督が、怪力少女の恋を描くアクション・コメディ。「火山高」のシン・ミナが、片想いする武道家の女子大生を好演している。武道家一家の一人娘で怪力部のエースのソフィは、キャンバスでクールな美青年に一目ぼれしてしまう。女らしさを身につけるため、武術の道を捨てようとするソフィだったが……。", "2008-11-29", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　VENGEANCE　OF　SHE", "燃える洞窟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "H・ライダー・ハガード原作のヒット作「炎の女」の続編。朦朧とした意識の中で、何かの力に操られるかのように東へと旅する美女キャロル。彼女は医師フィリップの助けを得て、かつて不老不死の女王クーマが支配した古代都市へとたどり着く…。チェコ出身の女優O・ベローヴァの官能的な魅力も見どころのハマープロ製作による秘境ロマン。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三個相愛的少年／OH！　MY　THREE　GUYS", "ぼくたちはここにいる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "幼なじみのゲイ３人組の友情を、笑いと涙で綴ったハートフル・ドラマ。広告プランナーのホウと三流役者のガウ、脚本家のファーはマンションで同居している幼なじみ。ゲイである彼らは、それぞれに悩みを抱えている。ホウは気になる女性が現れて動揺し、ファーは恋人と別れて仕事もはかどらない。ガウはそんな二人を励まし、場を和ませている。互いに支え合い幸せに暮らしてきた彼らに、やがて一つの死が近づいていた……。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crazy Heart", "クレイジー・ハート", "", "", "http://image.eiga.k-img.com/images/movie/55239/315x210.jpg?4554", 111, "「ビッグ・リボウスキ」「サンダーボルト」で知られる名優ジェフ・ブリッジスが、第82回アカデミー賞で自身初のオスカーとなる主演男優賞を受賞したヒューマンドラマ。共演にマギー・ギレンホール、ロバート・デュバル、コリン・ファレル。酒に溺れ堕落した生活を送るシンガーソングライターのバッド・ブレイクが、ある女性ジャーナリストに見出され、音楽を通して再起しようと奮闘する姿を描く。", "2010-06-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我的兄弟姐妹", "再見〈ツァイツェン〉／また逢う日まで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "本国・中国では社会現象となったほどの大ヒット作。両親の突然の死で生き別れとなった兄弟姉妹の20年ぶりの再会が、幼き頃の回想シーンを織り交ぜながら情感豊かに綴られる。香港の人気女優、ジジ・リョンら俳優陣と子役の好演、ノスタルジックな物語が涙を誘うこと必至。デビュー作とは思えない心憎い演出を見せるユイ・チョン監督の才能にも注目だ。", "2001-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ballad of Mott the Hoople", "すべての若き野郎ども　モット・ザ・フープル", "", "", "http://image.eiga.k-img.com/images/movie/57909/315x210.jpg?3579", 101, "1972年に発表したアルバム「すべての若き野郎ども」が世界的ヒットを記録したグラムロックバンド「モット・ザ・フープル」のドキュメンタリー。69年の結成後、デビッド・ボウイ、T・レックスと並びグラムロックシーンを担った「モット・ザ・フープル」。ライブパフォーマンスで聴衆を魅了するもヒット作に恵まれず、72年には解散の危機に見舞われるが、デビッド・ボウイがプロデュースしたアルバム「すべての若き野郎ども」が大成功を収める。わずかなうちにキャリアのどん底と頂点を経験した同バンドの歴史を、イアン・ハンターらオリジナルメンバーはもちろん、ロジャー・テイラー（クイーン）、ミック・ジョーンズ（クラッシュ）ら、同バンドのファンを自認するアーティストたちが語る。", "2012-05-12", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄色いからす", "黄色いからす", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "小学生の清は母親と二人で暮らしていたが、そこへ父が中国から復員した。父はかつての後輩の下で働くことになった不満もあり、清にも冷たい。また子供も生まれて、清はますます疎外感を感じるようになる。しかしやがて一家のわだかまりは解けて……。家族愛ドラマの佳作。題名は清が情緒不安定な心理から描いた絵によっている。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shadow Man", "沈黙の奪還", "", "", "http://image.eiga.k-img.com/images/movie/33809/315x210.jpg?4786", 95, "スティーブン・セガール主演の“沈黙”シリーズ第12弾。セガール扮する元CIA捜査官が、さらわれた愛娘を救うために数々の敵に立ち向かうサスペンス・アクション。企業家として成功を収めた元CIA捜査官ジャック・フォスターは、娘のアマンダと共にルーマニアを訪れる。しかし到着した直後、アマンダが何者かによって連れ去られてしまう。その裏には、軍が極秘裏に開発していた細菌兵器を巡る巨大な陰謀が渦巻いていた……。", "2007-01-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FEMME DE MA VIE", "悲しみのヴァイオリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ヴァイオリン奏者のシモンは、芸術上の行き詰まりと妻への愛の狭間で苦悩し、アルコールに溺れていく。どうしたらいいのか分からない妻は、シモンを抱きしめ泣くだけだったが……。「インドシナ」のバルニエ監督が大人の愛を描く。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The One", "ザ・ワン", "", "", "http://image.eiga.k-img.com/images/movie/1579/315x210.jpg?1328", 87, "多次元宇宙捜査局の元捜査官ユーロウは、宇宙に125あるパラレル・ワールドの自分を殺して唯一の存在になろうとするが、最後の1人、ロサンゼルス保安官ゲイブは強敵だった。格闘振り付けはリーのハリウッド進出作「リーサル・ウェポン4」以来、彼と組んでいるコーリー・ユエンが担当。善玉リーの基本は八卦掌、悪玉リーの基本は形意拳と、2人のリーのキャラクターを、その格闘のスタイルで表現する演出を試みている。", "2002-06-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("How Do You Know", "幸せの始まりは", "", "", "http://image.eiga.k-img.com/images/movie/55279/315x210.jpg?4741", 116, "「恋愛小説家」のジェームズ・L・ブルックス監督が、「キューティ・ブロンド」シリーズのリース・ウィザースプーンを主演に描くロマンティック・コメディ。メジャーリーガーの恋人マニー（オーウェン・ウィルソン）と一緒に暮らす31歳のプロソフトボール選手リサ（ウィザースプーン）は、突然チームからクビを言い渡されてしまう。落ち込むリサの前に、父親と貿易業を営む青年ジョージ（ポール・ラッド）が現われ、マニーとの微妙な三角関係がはじまる。", "2011-02-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Goldfinger", "007／ゴールドフィンガー", "", "", "http://ecx.images-amazon.com/images/I/519vhTXKwaL._SL210_.jpg", 106, "シリーズ第3作。米国政府の金塊を放射能で汚染しようとする悪玉ゴールドフィンガーにボンドが対決を挑む。この作品で初めてボンドの愛車アストン・マーチンが登場。殺人帽、レーザー光線といった秘密兵器も次々と飛び出し、楽しさ倍増。主題歌も大ヒットした。", "1965-04-30", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HAUNTING", "たたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "霊感を持つ二人の女性を助手にして、呪われた家で心霊現象を研究している人類学者。だが次々にポルターガイスト現象が起こり、彼らにも姿の見えぬ危険が迫ってくる……。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRIZE OF PERIL", "バトルランナー2030", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "西暦2030年。某国某都市。人間狩りという恐ろしいTVショーが絶大な人気を集めていた。ハンターから逃げ切れれば大金を手にすることができるのだが……。ある日、失業者のジャックマンは、大金をつかみ恋人との幸福を得るために応募した。ついに殺人ゲームが始まったのだ。ジャックマンはそこで、ハンターの銃弾に傷つきながらも反撃を開始する。名優M・ピコリが、一味違う役を好演。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROGUE　TRADER", "マネートレーダー・銀行崩壊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1763年に創業されたイギリスの名門ベアリングズ銀行を破綻に追い込んだ28歳のトレーダー、ニック・リーソンの獄中手記を、E・マクレガー主演で映画化した社会派サスペンス。学歴もなく、労働者階級出身ながら巧みな話術でベアリングズ銀行に就職したニック。リサという愛妻を得た彼はシンガポール支店の先物取引部門の責任者に抜擢される。確実に利益を上げる彼に、本店からミスを処理する一時的な口座を作るよう指示があり、ニックはその口座番号を88888とする。しかし、これがすべての間違いの始まりだった。ミスを犯した彼はそれを88888口座に隠し、その損失を埋めるために無許可の取引や、巨額の不正取引を繰り返すようになる……。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ANGELIC CONVERSATION", "エンジェリック・カンヴァセーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ケン・ラッセル監督の「肉体の悪魔」などの美術を担当したこともあるD・ジャーマンの、日本初公開の監督作品である。本編の内容は、明確なストーリーがあるわけではなく、D・ジャーマンのイメージが綴られた実験映画風のもの。廃墟や岩場、泉や炎、ホモセクシャルな男たちといったものが、８ミリフィルムやビデオを駆使した斬新な映像の中に映し出されていく。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さようならCP", "さようならCP", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "昭和61年、茨城県新治郡のボロ寺の和尚が関東一帯のCP（脳性小児麻痺）者を寺に集めて、マハラバ寺と称するコロニーを建設。８年後に村が崩壊したあとも、和尚の思想を受け継ぐ急進的団体“青い芝”のCP者たちが運動を続けてきた。“働くことだけが正義の社会”に“オレたちの世界”を取り戻すため、CP者が街頭行動に出る。そんな彼らのギリギリの自己表現にカメラが真正面から対決している。「ゆきゆきて、神軍」でその名を馳せた原一男の出発点となった力作。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ERMO", "麻花〈マーホア〉売りの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "農村に夫と息子との３人暮らしをするアルモは、町まで揚げ菓子マーホアを売りに行く。アルモは仲の悪い隣家がテレビを買ったこがでおもしろくなく、町で見た大型のテレビをどうしても買う決心をするのだった……。ロカルノ映画祭で批評家大賞などを受賞した。", "1994-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE　FINE　DAY", "素晴らしき日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「卒業の朝」のM・ホフマンによるラブ・コメディ。建築家のメラニーと人気コラムニストのジャックは、ともに５歳の子供を持つバツイチ。そんな二人が子供の野外授業の出発時間に遅れたことから、交替で子守をしてそれぞれの仕事へと向かうことになる。携帯電話で頻繁に連絡を取り合うものの、急な仕事が入るなどトラブルが続出して……。対照的な性格の男女が仕事と子供の世話に追われながらも互いに惹かれ合っていく過程を、一日の出来事の中に軽妙なテンポで描く。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャングルハウス3ガス　林家三平", "ジャングルハウス3ガス　林家三平", "", "", "http://image.eiga.k-img.com/images/movie/54989/315x210.jpg?2575", 73, "「昭和の爆笑王」と称され絶大な人気を誇った落語家・林家三平の半生を追ったドキュメンタリー。「源平盛衰記」「源氏物語」などの高座を記録した貴重な映像資料をはじめ、妻の海老名香葉子ら家族へのインタビュー、さらに2009年3月に行われた2代目三平襲名イベントの模様などを通し、落語家と家庭人の両面から三平の真実の姿を捉えていく。メガホンを取ったのは「ISOLA　多重人格少女」の水谷俊之。", "2009-10-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sex Pistols: There'll Always Be An England", "セックス・ピストルズ　勝手にやったぜ!!", "", "", "http://image.eiga.k-img.com/images/movie/54219/315x210.jpg?3042", 71, "パンクロック界のカリスマ“セックス・ピストルズ”のオフィシャルライブ映像を収録した作品。監督は同バンドのドキュメンタリー「NO FUTURE：A SEX PISTOLS FILM」でもメガホンを取ったジュリアン・テンプル。アルバム“勝手にしやがれ!！”の30周年を記念したライブのため、5日間だけ再結成したバンドをカメラが追いかける。", "2008-12-20", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A HARD DAY’S NIGHT", "ビートルズがやって来る ヤァ！ヤァ！ヤァ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "1960年代のミュージック・シーンに登場し、またたく間に世界中の若者を虜にしたイギリス・リバプール出身の４人組、ザ・ビートルズ——ジョン・レノン、ポール・マッカートニー、リンゴ・スター、ジョージ・ハリスンは解散するまで数々のヒット曲を送り出した。その高い音楽性は多くのミュージシャンたちに影響を与え、今もなお信奉者を持つ永遠のアイドルである。彼らが出演した映画作品は４本あるが、これはその第１作。当時人気の頂点にいた彼らの一日半の生活をドキュメンタリー・タッチで描く。プロデューサーとシナリオライターの二人が彼らの公演に同行し、性格、行動などを詳しく取材して脚本にまとめ上げた。撮影はロンドンを中心に行われ、地下鉄、駅、TVショーへと目まぐるしく走り回る４人の姿を、スピード感あふれるカメラで捉えていて新鮮。演奏される曲は、タイトル曲のほかに『恋する２人』『恋におちたら』『すてきなダンス』など全部で11曲。2001年のリバイバル公開時のタイトルは「ハード・デイズ・ナイト」。現在DVDはこのタイトルでリリースされている。", "1964-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DELTA FORCE ２", "デルタフォース２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "南米、サンカルロスの要塞に君臨する麻薬帝国の王ラモンに捕らえられたDEA（米国法務省麻薬取締局）のエージェントを救出すべく、スコット・マッコイ大佐率いる世界最強のエリート集団デルタフォースが再び出撃する。おなじみ空手殺法でアクション映画の王道を歩むC・ノリス主演作。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Montparnasse 19", "モンパルナスの灯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "重い病と貧困に悩まされながら、世間に認められることなく36歳という若さでその生涯を終えた天才画家モジリアニ。彼の苦悩に満ちた人生と、彼をとりまく女性たちを描いた伝記映画の傑作。当初はマックス・オフュルスがメガホンを取る予定だったが、オフュルスの急逝によりジャック・ベッケル監督が後を引き継ぎ、彼の代表作となった。モジリアニを熱演したジェラール・フィリップは、奇しくも彼と同じ36歳でこの世を去っている。", "1958-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空港の魔女", "空港の魔女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "羽田空港で偽ドルが発見される。偽ドル事件になぜか激しい執着を見せる新聞記者が、事件の鍵を握る女に巧みな手段で近寄る。女は新聞記者に自分と組むことを申し出るが、彼の妹は、実は偽ドル事件で殺されていたのだった。一度は女を好きになりながら、妹のために正義を貫く新聞記者を高倉健が好演したサスペンス・ミステリー。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO OF A KIND", "セカンド・チャンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "堕落した人間世界に真実の愛があるかどうかを確かめるため、神様が二人の男女を生き返らせた。「グリース」に続きJ・トラヴォルタとO・ニュートン・ジョンの二大スターが共演したロマンティック・ストーリー。全編に流れるポップス・ナンバーも聴きもの。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("涙くんさよなら", "涙くんさよなら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "母親を捜しに日本にやって来た混血児のジュリー。慈善団体援助を受けるために、彼女は口がきけないふりをして来日したが、時ならぬ美談にマスコミは大騒ぎ。嫌けがさした彼女は一人で母親捜しの旅に出るが、途中出会った若者たちが彼女の母親を見つけようとする……。脚本に倉本聰が参加、元祖中国女優のジュディ・オングが主演した青春アイドル・ノリ映画。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダメジン", "ダメジン", "", "", "http://image.eiga.k-img.com/images/movie/33509/315x210.jpg?4532", 98, "読んで字のごとく「ダメな人」の長閑な日常を「イン・ザ・プール」「亀は意外と早く泳ぐ」TVドラマ「時効警察」の三木聡監督が描く脱力系コメディ。川崎に住むリョウスケ、ヒラジ、カホルの「ダメジン」3人組は、働かずに生きて行ける方法を真剣に考えている。だが、ある日近所の年寄りから「ダラダラしたいなら、インドへ行け」と言われ、インド行きを考えるようになる。", "2006-07-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仁義の聖戦　ジャックナイフ", "仁義の聖戦　ジャックナイフ", "", "", "http://image.eiga.k-img.com/images/movie/57829/315x210.jpg?1540", 70, "木村一八主演の任侠作品。岡山の町で、小さな組ながらも独立を貫いてきた木元組のシマに、関西共和会をバックにもつ宮本会が侵攻してくる。木元組の若頭・京極晃の兄貴分で組長代行の箕島は、宮本会幹部の暗殺計画を決行。京極も作戦に参加するが失敗し、箕島が警察に逮捕されてしまう。", "2012-02-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SF Short Films", "SF Short Films", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「SF・サムライ・フィクション」「Stereo Future」に続いて中野裕之監督が仕掛けるSFシリーズ第３作。これが監督デビューとなる安藤政信の「アダージェット」やピエール瀧の「県道スター」、そしてSFシリーズの助監督を務める芹澤康久の「ハナとオジサン」と、中野裕之が監督した３本を合わせた全６本の短編映画集だ。それぞれの監督の個性と感性が凝縮された短編はどれも見ごたえ十分。ビデオ、DVDタイトルは「Short Films」。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hoosiers", "勝利への旅立ち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "インディアナ州の田舎町にやって来た鬼コーチが、様々な障害を乗り越えて地元の高校のバスケットボール・チームを州選手権優勝にまで導いていく。臨場感あふれる試合のシーンに加え、多彩な人間模様も見どころ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Longshots", "奇跡のロングショット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "米アメリカンフットボールのユースリーグで活躍する、史上初の女性選手ジャスミン・プラマーをモデルにしたスポーツドラマ。メタルバンド「リンプ・ビズキット」のボーカル、フレッド・ダーストがメガホンをとる。イリノイ州のミンデン。元アメフトの花形選手カーティスは、義理の姉から頼まれ、学校でイジメにあっている彼女の娘ジャスミンの面倒をみることになる。最初は反発し合う2人だったが、カーティスはジャスミンにフットボールの才能があると見抜き、アメフトを教え始める。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EAGLES WING", "イーグルス・ウイング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "一頭の伝説の馬をめぐる、誇り高きコマンチ族の戦士と白人の男との、男と男の闘いを描くアドベンチャー・アクション。白人を嫌うインディアンの戦士が、白人と取引をしようとした酋長を殺す。取引のため旅をしていた白人の男は、彼に襲われ仲間を殺される。やがて二人は伝説の白馬をめぐり、対決のときを迎える。", "1979-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神偸次世代", "スパイチーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "香港版「ミッション：インポッシブル」というべきアクション。戦闘能力とハイテクを武器に奔走する、特殊工作チームの活躍が描かれる。任務中に仲間を死なせたことを心の傷として抱える敏腕工作員マック。新薬データの強奪という新たな依頼を受けた彼は、チームを率いて奔走する。だが、この仕事にはマックを陥れようとする罠が潜んでおり、意外な人物が裏で糸を引いていた。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("nude", "nude", "", "", "http://image.eiga.k-img.com/images/movie/55519/315x210.jpg?4060", 106, "2010年にAV女優を引退したみひろの私小説を、「童貞放浪記」の小沼雄一監督が映画化。「アウトレイジ」の渡辺奈緒子が映画初主演を務める。新潟の高校を卒業し、女優になる夢をかなえるため上京したひろみは、ある日ヌードモデルのスカウトを受ける。AVには出演しないことを条件にモデルの仕事を受けたひろみだったが、恋人や親友からは猛反対され、次第に孤立していってしまう。ひろみはひとり悩んだ末、長年の夢をかなえるためにAV出演を決意する。", "2010-09-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAR TREK: INSURRECTION", "スター・トレック／叛乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「スター・トレック」の劇場版新シリーズ第３作。エンタープライズ号の映像が、ミニチュアによるSFXからフル・デジタル化されたCGIに変わり、よりリアルな迫力が生まれた。不老不死のバクー人が暮らす惑星の秘密を探るため、ソーナ人が陰謀を画策。バクー人を別の惑星ヘの強制移住させようとするソーナ人に、エンタープライズ号が戦いを挑む。監督は前作に引き続き、副長役で出演もしているJ・フレイクス。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大拳銃", "大拳銃", "", "", "http://image.eiga.k-img.com/images/movie/57709/315x210.jpg?2773", 31, "新人の大畑創監督が、2009年ゆうばり国際ファンタスティック映画祭およびぴあフィルムフェスティバルで審査員特別賞を受賞したクライムアクション。郁夫と聡の兄弟が経営する倒産寸前の金属加工工場にある日、拳銃密造の依頼が舞い込む。背に腹は変えられず依頼を引き受けた兄弟は失敗を繰り返しながらも密造を続けていく。次第に兄の郁夫は拳銃を作ること自体にのめりこんでいき……。大畑監督の「へんげ」（11）劇場公開にあわせて同作と同時上映。", "2012-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tsotsi", "ツォツィ", "", "", "http://image.eiga.k-img.com/images/movie/33919/315x210.jpg?1277", 95, "アパルトヘイト廃止後も続く南アフリカの厳しい現状をリアルに描き、アフリカ映画として初のアカデミー賞外国語映画賞に輝いた感動の人間ドラマ。ヨハネスブルクのスラム街で暮らす少年ツォツィは、仲間たちと窃盗やカージャックを繰り返す日々を送っていた。そんなある日、彼は奪った車の中に生後間もない赤ん坊を発見する。仕方なく赤ん坊を育て始めたツォツィだったが、やがて命の価値に気づき、人間性を取り戻していく。", "2007-04-14", "3,67", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎物語", "次郎物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "島耕二版と同じく原作の第１部だけを扱っているが、清水版ではドラマティックな演出を排し、淡彩で叙情的な風情に包まれたものとなっている。田園風景の美しさや田舎の旧家のたたずまい、人々の生活感情などの風俗描写は、清水宏が得意とするものだけありうまい。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALIFORNIA MAN", "原始のマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "裏庭にプールを掘っていた高校生二人組は、なんと地面の下から氷結した太古の原始人を発見した。二人は原始人を生き返らせるとリンクと命名。現代風のクールガイに仕立て上げて留学生のふりをさせて高校に通わせるのだが……。MTVの人気者P・ショアが大活躍する珍妙なハイスクール・コメディ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Anaconda 4", "アナコンダ4", "", "", "http://image.eiga.k-img.com/images/movie/54579/315x210.jpg?4025", 88, "大蛇アナコンダの恐怖を描いたシリーズの第4弾。前作で逃げ出した2匹の大蛇を退治したアマンダだったが、ある研究者が蛇のお腹の中にいた子供たちを密かに持ち出していた。蛇は予想を上回る速さで成長を遂げ、遂には研究者たちを襲って脱走。次々と人々を襲い始めた蛇とアマンダが再び壮絶な戦いを繰り広げる。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'enfant Qui Voulait Etreun Ours", "白くまになりたかった子ども", "", "", "http://image.eiga.k-img.com/images/movie/1649/315x210.jpg?4467", 78, "デンマーク・アニメの巨匠ヤニック・ハストラップ監督が、イヌイットの神話を元に描く大人のためのファンタジー。シロクマにさらわれたイヌイットの子供は、氷の平原の中でシロクマの母親の愛情を注がれ、シロクマとして育てられる。やがて子供は人間の世界に戻ることになるが、彼の夢はシロクマになることだった。音楽は「キャラバン」「WATARIDORI」のブリュノ・クーレ。03年ベルリン映画祭児童映画部門準グランプリを受賞。", "2004-07-10", "2,18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃色身体検査", "桃色身体検査", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "滝田洋二郎監督が撮り上げたピンク映画の本作は、後の自作「病院へ行こう」などへのヒントになったと思われるシチュエーション・コメディの味わい。滝川真子の健康美あふれる初々しい演技をはじめ、名脇役・大杉漣がスラップスティック調の怪演技で魅せる。旅行資金を稼ぐため、院内の患者を相手に売春をするナースたちがいた。そんなある日、病院から死体が盗まれる事件が発生する……。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fisher King", "フィッシャー・キング", "", "", "http://ecx.images-amazon.com/images/I/517TEHgyTtL._SL210_.jpg", 137, "ニューヨークの超売れっ子DJジャックは、ある日ラジオで不用意な発言をし、それがもとで大量殺人事件が起こってしまう。事件の３年後、今はウツ病になりビデオ屋の店員に落ちぶれた彼は、例の殺人事件で妻を亡くしたホームレスのパリーに出会う。パリーは元大学教授だったが、今はキリストが最後の晩餐に使ったという“聖杯”を探している。そしてジャックこそがその“聖杯”を見つけ出す力になる人物だと言う。はじめは気味悪がったジャックだが、やがてパリーとつき合ううちにジャックの人生は変わっていく……。鬼才T・ギリアムの感動作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("強く　強く　バイオリニスト・神尾真由子 21歳", "強く　強く　バイオリニスト・神尾真由子 21歳", "", "", "http://image.eiga.k-img.com/images/movie/55229/315x210.jpg?1980", 109, "世界三大音楽コンクールの1つである、第13回チャイコフスキー国際コンクールで優勝したバイオリニスト・神尾真由子の素顔に追るドキュメンタリー。2007年夏、留学先のスイス・ジュネーブから帰国した神尾の１カ月に密着する。NHKで放送されたドキュメンタリー番組を劇場で公開するプロジェクト「This is REAL／NHK-BSドキュメンタリー・コレクション」にて上映。", "2010-02-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忌野清志郎　ナニワ・サリバン・ショー　感度サイコー!!!", "忌野清志郎　ナニワ・サリバン・ショー　感度サイコー!!!", "", "", "http://image.eiga.k-img.com/images/movie/57079/315x210.jpg?4675", 114, "2009年に他界した忌野清志郎を中心に、01年、04年、06年と3度にわたり大阪城ホールで行われてきたライブ「ナニワ・サリバン・ショー」（通称・ナニサリ）を新たに映画化。過去3度のナニサリにおける忌野の出演場面のほか、“4度目のナニサリ”のために集結した忌野を慕う豪華アーティストたちの歌声も披露される。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Coach Carter", "コーチ・カーター", "", "", "http://image.eiga.k-img.com/images/movie/1339/315x210.jpg?4010", 136, "「セイブ・ザ・ラストダンス」「ネゴシエーター」のトーマス・カーター監督が、99年に実際に高校のバスケットボール・チームで起こった出来事を元に描く感動の実話ストーリー。高校のバスケット・コーチとなったケン・カーターは、チームメンバーの将来を考えて、リーグ戦の2試合を失っても彼らの成績を向上させようとする。カーター役はサミュエル・L・ジャクソン。人気ミュージシャンのアシャンティの映画初出演も話題。", "2005-08-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bounty Hunter", "バウンティー・ハンター", "", "", "http://image.eiga.k-img.com/images/movie/55269/315x210.jpg?2495", 110, "ジェニファー・アニストン＆ジェラルド・バトラー共演のアクション・コメディ。賞金稼ぎのマイロ（バトラー）は、保釈中に行方をくらました元妻の記者ニコール（アニストン）を逮捕するために雇われ、あっさりとニコールを捕まえる。だが、ニコールが偽装殺人事件を追っていたために元夫婦の2人はともに犯罪組織から命を狙われることになる。監督は「メラニーは行く！」「最後の恋のはじめ方」のアンディ・テナント。", "2010-07-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Greyfriars Bobby", "ユアン少年と小さな英雄", "", "", "http://image.eiga.k-img.com/images/movie/33899/315x210.jpg?1262", 104, "19世紀のスコットランドに実在した忠犬ボビーの物語を基に描いた感動ドラマ。エジンバラの貧困地区に住む少年ユアンは、警察官のジョン・グレイが飼っている勇敢なテリア犬ボビーのことが大好きだった。しかしグレイが病死してからというもの、ボビーは埋葬された主人のそばを離れようとしない。数々の試練にも負けず主人の墓を守り続けるボビーだったが、町で登録犬制度が施行されると、野良犬として捕獲されてしまい……。", "2007-03-24", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COWBOY IN AFRICA", "野獣狩り カウボーイ・スタイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "アフリカを舞台に、主人公であるカウボーイの活躍を描いた異色作。アフリカのケニアに大きな農場を持つヘイスは、野生の動物を家畜のように飼育する計画を持ち、すでに政府から許可を得ていた。だがこの計画に反対する牧場経営者ベッカーは、動物学者を抱き込んでヘイスの農場で疫病が伝染したと政府に訴え出るのだが……。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　怪談　牡丹燈籠", "シネマ歌舞伎　怪談　牡丹燈籠", "", "", "http://image.eiga.k-img.com/images/movie/54679/315x210.jpg?3143", 155, "歌舞伎の舞台公演をスクリーンで上映する「シネマ歌舞伎」シリーズ第12弾。三遊亭円朝の同名原作を現代風にアレンジし、2007年歌舞伎座で上映された。新三郎に恋して焦れ死にしたお露は、自分の後を追って死んだ下女・お米と共に、夜になると牡丹燈籠を手に下駄の音を響かせながら、新三郎のもとへ通うようになる。新三郎の下働きの伴蔵とお蜂の夫婦は、お露を幽霊と見破るが……。", "2009-07-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("非誠勿擾", "狙った恋の落とし方。", "", "", "http://image.eiga.k-img.com/images/movie/55389/315x210.jpg?2828", 125, "「女帝　エンペラー」のフォン・シャオガン監督が、「ハッピー・フューネラル」のグォ・ヨウと「トランスポーター」のスー・チー共演で描いたラブ・コメディ。一夜にして大富豪になった独身男性チン・フェンは、理想の結婚相手を見つけるべく旅に出る。やがて彼は客室乗務員の女性シャオシャオに惹かれはじめるが、彼女は既婚男性に恋しており……。中国で記録的大ヒットを飛ばし、ロケ地となった北海道ブームを巻き起こした。", "2010-04-10", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIABOLICA", "デアボリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "悪魔が目をつけた妊婦ジェシカの胎児をめぐる、悪魔と超能力者の大闘争。胎児は異常発育を始め、ジェシカの形相は異常に変化し、緑色の粘液を口から吐き出す。「エクソシスト」で点火したオカルト・ブームに乗って製作。イタリアならではのゲテモノの味が魅力だ。", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("the hiding 潜伏", "the hiding 潜伏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "別れた恋人のストーカー行為に怯えるヒロインが、自宅に侵入してきた謎の女の血液を媒介とし、秘められた能力を覚醒させていく……。インディペンデント映画界の異才・福居ショウジンのサイキック・シチュエーション・スリラー。", "2008-05-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朱と緑", "朱と緑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "寝室で寝ているところを暴漢に襲われ、周囲の噂に堪えきれず大阪へ家出してしまった社長令嬢と、令嬢に勇気を与える男、その男を純粋に慕う下宿の娘らが繰り広げる愛情の喜びと哀しみ。その機微を複雑な現代社会のなかに描いたメロドラマ。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCORE", "SCORE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ビデオ会社でオリジナルビデオを作っていた室賀厚が奥山和由に自作を送り、それを気に入った奥山が無名の室賀に監督させたことで話題になった、ハード・アクション映画。組織の命令で宝石店を襲撃した４人組の強盗。彼らが逃げ込んだ郊外の廃墟に、獲物の横取りをたくらむ若いカップルが現れる。安全だったかくれ家が、逆に逃げ場のない壮絶な戦場と化す。組織との取引まであと数時間。この廃墟から脱出するのは……。3000万円という低予算ながら、フィリピン、ラスベガスでロケを敢行し、弾丸3000発を使用するなど派手な仕上がりになっている。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blue Chips", "ハード・チェック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "　大学バスケットボールのコーチ、ピートはチームを立て直すために才能のある新人選手を獲得しようとするが、相手が金品を要求し……。アマチュアスポーツ界の実情を描くドラマ。シャキール・オニールの他、有名選手やコーチが出演している。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Star Wars: Episode VI - Return of the Jedi", "スター・ウォーズ　ジェダイの復讐", "", "", "http://ecx.images-amazon.com/images/I/5158cHaAOHL._SL210_.jpg", 132, "SF映画の金字塔として名高い傑作シリーズ「スター・ウォーズ」の第3作にして、全シリーズを通しての完結編（エピソード6）。惑星タトゥイーンへと向かったルークとレイア姫は、大悪党ジャバ・ザ・ハットに捕らえられていたハン・ソロの救出に成功。修行を続けるため再び惑星ダゴバを訪れたルークは、ヨーダから驚くべき自身の出生の秘密を明かされる。一方、反乱軍は帝国軍が衛星エンドアに建設中の第2デス・スターの攻撃に乗りだし……。劇場公開当時の副題は「ジェダイの復讐」だったが、2004年のトリロジーDVDボックス発売時に、本来の原題「Return of the Jedi」を訳した「ジェダイの帰還」に改題された。", "1983-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の警察・乱れ蝶", "女の警察・乱れ蝶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "“女の警察”篝正秋は、悪どいホステスの引き抜きに怒り心頭。妹を人質にとられていた敵方の部下とともに悪の巣に乗り込む。無頼シリーズの小沢啓一が監督。なお、製作会社のアロー・エンタープライズとは小林旭のプロダクションだが、それほど入れこんだ割には作品の質は低い。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シャム猫・ファーストミッション", "シャム猫・ファーストミッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "21世紀初頭の東京。湾岸地区が国際的テロリスト“アジアの虎”こと海堂俊介に占拠される。内閣調査室直属のテロリズム殲滅チーム“シャム猫”のジュンとナオミが出動するが、開発中の次世代兵器に目をつけた海堂の恐るべき狙いが明らかになり……。モンキー・パンチ原作のアクション・アニメ。『ルパン三世』の人気キャラ、峰不二子の原型ともいわれるタフなヒロインたちの活躍を描く。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOOD　MORNING...AND　GOODBYE！", "草むらの快楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "モンド映画の鬼才R・メイヤーによるエロチックな愛憎劇。夫の性的不能に欲求不満を募らせる、奔放でグラマーな後妻エンジェルは偶然出会った、たくましい男と愛人関係に。彼らの間に、17歳の愛娘や、男性を回復させた夫が割り込んで……。泥沼化する人間関係を見つめながらも、カラッとしたタッチはメイヤー作品ならでは。ビデオ発売時タイトル、DVDタイトルは「グッドモーニング・グッドバイ」。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大撒把／AFTE　SEPARATION", "再見〈ツァイチェン〉のあとで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "中国で大ヒットした都会の男女のラブ・ストーリー。市場経済が導入された中国の首都、北京を舞台に、時代の波に揺られる男女のつかの間の恋を描く。カナダへ留学する妻を見送ったばかりのカメラマン、クーイェンが、見知らぬ男に頼まれて昏睡した彼の妻、チョウウンを病院に運ぶ。流産した彼女は、夫の後を追って渡米するのが夢だった。１年後、クーイェンは出国を間近に控えて喜ぶチョウウンと偶然再会、お祝いに日本料理店に招待する。さらに１年後の大晦日。互いに妻と夫に電話をして留守だったために落ち込んでいた二人は一緒に大晦日を過ごし、休日ごとに夫婦のように過ごすことになるが……。", "1993-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIORNI D’AMORE", "恋愛時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "南イタリアにある、掠奪された娘は掠奪者以外とは結婚できないという習慣。貧しい農家に育ったアンジェラとパスカーレは、人並みの結婚式を挙げようと懸命に働くが、焼け石に水。ついにパスカーレはアンジェラを掠奪するが……。サンティス監督が描く古き良き純愛物語。", "1954-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE FIELD", "ラブ・フィールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1963年、ダラスのケネディ暗殺事件を背景に、当時はまだタブーだった白人女性と黒人男性の愛が、緊迫した逃走劇のうちに描かれる。M・ファイファーはこの作品で、1992年度のアカデミー主演女優賞にもノミネートされた。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KARNABAL", "コメディアンツ★ムービー　カルナバル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "スペインの国民的演劇集団“コメディアンツ”。主人公のジョーメッツは、どこからともなく現れた男によって、不思議な祝祭空間に案内される。スペインのカーニバルに登場する頭デッカチの巨人、ドラゴン……。様々な歓喜とオブジェと色彩が全編に流れる。", "1985-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARFAIT　AMOUR！", "堕ちてゆく女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "実際に起こった事件をもとに、男女の愛憎を描いた、官能的な恋愛ドラマ。離婚歴のある中年女医フレデリックは、年下の若き事業家クリストフと恋に落ちた。だが、束縛を嫌うクリストフは一夜かぎりの情事を楽しみ、フレデリックは気を揉むばかり。罵り合い、それでも離れられない彼らの関係は、やがて悲劇的な結末へと向かう。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピノキオ√964", "ピノキオ√964", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1990年に劇場公開された短編８ミリ「ゲロリスト」で旋風を巻き起こした福居ショウジンが初めて手掛けた16ミリ作品。タイトルの“ピノキオ√964”とはダッチワイフならぬダッチハズバンドの名前で、プレイ中に捨てられた彼が、記憶喪失の女ひみこと出会ったことから思わぬ戦いに巻き込まれていく……。パワフルな音と映像で盛り上げる異色作。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばり捕物帖 折鶴駕籠", "ひばり捕物帖 折鶴駕籠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "折鶴のついた駕籠に乗った武士が殺された。その死に不審を抱いたお七は、勇敢にも武士の身元調査に乗り出すが、やがてそこに大きな陰謀がうごめいていることがわかってきて……。美空ひばりと東千代之介の人気捕物シリーズの一編。工藤栄一の初期作品。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wild Bill", "ワイルド・ビル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　アメリカ西部史において伝説的ガンマンとして知られたワイルド・ビル・ヒコックの生き様を描くウェスタン。ビルはカンザスで保安官を務めるが、自分の助手を誤射によって殺してしまう。その後、彼はサウスダコタ、デッドウッドの町へ。そこにはかつて彼を愛したカラミティ・ジェーンがいた。やがてビルは元恋人スザンナの息子ジャック・マッコルから命を狙われることになり……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOAP DISH", "ソープディッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "人気昼メロ番組『陽はまた沈む』。嫉妬、欲望、そして様々な愛を描いたその番組の舞台裏では、女優、男優、脚本家が入り乱れてドラマ以上にドラマティックな世界が展開されていた……。K・クラインら３大スターを中心に、豪華キャストで描く痛快コメディ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちちり", "ちちり", "", "", "http://image.eiga.k-img.com/images/movie/55339/315x210.jpg?1516", 77, "細野辰興、五十嵐匠、サトウトシキ、いまおかしんじ、窪田将治の5人の個性派監督が1つの脚本をベースに、演技ワークショップ「Workshop@FAITH」に参加した俳優を起用し、5通りの短編で紡ぐオムニバス。小説家志望の好子は、さまざまな文学賞に作品を応募し続けるも、なかなか芽が出ずに悩んでいた。そんなある日、好子は恋人からのプロポーズを受け結婚することになり、2人の結婚を祝うために友人たちが集まってくる。", "2010-03-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うめく排水管", "うめく排水管", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "「富江」でヒットを飛ばした及川中が、再び伊藤潤二の漫画を映画化。伊藤潤二作品の中で最もシュールな異色作と言われる原作を、哀切なムード漂うホラーに仕上げた。郊外の一軒家を舞台に、排水管の中に忍び込んだ男が、次々と人間を排水口の中に引きずり込んでいく恐怖を描き出す。若手女優、栗原瞳と岩佐真悠子の熱演にも注目。潔癖症の母と暮らす美人姉妹、令奈と真理。母と離婚した父が突然家の前に現れ、二人は困惑する。一方、令奈は、暴漢から助けてくれたオタク青年、滑井にストーカー行為をされていた。そんなある日、家の排水管が詰まり始め……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Secretariat", "セクレタリアト　奇跡のサラブレッド", "", "", "http://ecx.images-amazon.com/images/I/51FjiKbne6L._SL210_.jpg", 123, "1973年にアメリカで3冠を獲得した競走馬セクレタリアトの馬主ヘレン・チェナリーの半生をダイアン・レイン主演で映画化した伝記ドラマ。米バージニア州の主婦ヘレン（レイン）は父親が持つ競走馬の育成牧場メドウ・ステーブルを経営難であることを知りながら引き継ぐ。最初は戸惑うばかりだったヘレンだが、トレーナーのルシアンと出会ったことから、積極的に男性社会である競馬ビジネスの世界に足を踏み入れていく。監督は「ワンス・アンド・フォーエバー」のランダル・ウォレス。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PATRIOT", "沈黙の陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "S・セガールが製作総指揮も兼ねたパニック・サスペンス。免疫学者に扮する彼が猛威をふるうウイルスと過激派グループに立ち向かう。アメリカ北部の田舎町が、武装したネオナチ集団によってウイルスに汚染される。免疫学の権威マクラーレンは、その原因がかつて政府機関で働いていたときに研究用に開発したウイルスであることを突き止めるが、ウイルスが突然変異したためワクチンに効き目がない。ネオナチによる襲撃の中、彼は血清の開発に奔走する。S・セガールの実娘、藤谷文子がアヤコ・セガールの名で特別出演。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EAT A BOWL OF TEA", "夜明けのスローボート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "W・ワン監督による、コミカルなチャイナタウン・ストーリー。時は移民排斥法が廃止された1949年、舞台はN.Y.。初老の男、ワー・ゲイは、息子と中国大陸の娘メイを結婚させようとする。めでたく結婚した二人は、アメリカでレストランを経営するが……。香港映画界の才人、E・ツァンも出演している。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kramer vs. Kramer", "クレイマー、クレイマー", "", "", "http://ecx.images-amazon.com/images/I/51KXej1HGzL._SL210_.jpg", 105, "ダスティン・ホフマンとメリル・ストリープが夫婦役で共演し、79年度のアカデミー賞で作品賞、監督賞を含む5部門で受賞したファミリードラマ。毎晩深夜に帰宅する仕事人間の夫テッドに愛想を尽かし、自分自身を取り戻すために家出した妻のジョアンナ。その翌日からテッドは7歳の息子を抱え、仕事と家庭の両立に励むが、家出から1年後、ジョアンナが息子の養育権を主張し、テッドを提訴する……。監督・脚本はロバート・ベントン。", "1980-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHATEVER　HAPPENED　TO　HAROLD　SMITH？", "ハロルド・スミスに何が起こったか？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1970年代の英国を舞台に、超能力騒動を軽妙に描いたコメディ。ごく普通の一家スミス家は、父ハロルドが超能力を持っていることが発覚して騒然。一方、ディスコ好きの18歳の息子ヴィンスは同僚に恋するが打ち明けられずにいる。そんな折り、父の超能力が原因となった死亡事故が起こり……。ディスコやパンクなどの当時の流行をユーモラスに捉え、のどかな笑いを誘う。ビデオ、DVDタイトルは「ミラクル＊ショー　ハロルド・スミスに何が起こったか？」。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hostel", "ホステル", "", "", "http://image.eiga.k-img.com/images/movie/33709/315x210.jpg?4698", 93, "クエンティン・タランティーノが製作総指揮を手掛け「キャビン・フィーバー」のイーライ・ロス監督がメガホンを取った衝撃ホラー。アメリカ人バックパッカーのジョシュとパクストンは、スロバキアのとあるホステルに外国人男性を求める美女たちがいるという噂を聞きつける。彼らは意気揚々とそのホステルへ向かうが、そこで彼らを待ち受けていたのは想像を絶する恐怖だった……。監督の希望により三池崇史監督がカメオ出演している。", "2006-10-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海洋天堂", "海洋天堂", "", "", "http://image.eiga.k-img.com/images/movie/56159/315x210.jpg?2400", 98, "水族館で働くワン・シンチョンは妻との死別後、自閉症の息子ターフーを男手ひとつで育ててきたが、ある日、自分がガンで余命わずかだと知る。自分が亡き後も息子が普通に暮らせるようにと、世話をしてくれる施設はなんとか見つけ出したが、シンチョンにはまだ息子に伝えるべきことが残されていた。ジェット・リーが脚本にほれ込み、アクションを封印して主演した親子のきずなを描くドラマ。チェン・カイコー監督の「北京ヴァイオリン」などで脚本を手がけたシュエ・シャオルーがメガホンをとり、クリストファー・ドイルが撮影、久石譲が音楽を担当。", "2011-07-09", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Grudge 2", "呪怨　パンデミック", "", "", "http://image.eiga.k-img.com/images/movie/34119/315x210.jpg?2158", 102, "ジャパニーズ・ホラー「呪怨」を清水崇監督自らハリウッドでリメイクし、全米で大ヒットを記録した「THE JUON／呪怨」の第2弾。邪悪な怨霊“伽椰子”の呪いが海を渡って世界へと感染。さらに、日本版では語られなかった“伽椰子”誕生の秘密が明かされる。日本でインターナショナル・スクールに通うアリソンは、友人と共に幽霊屋敷と噂される一軒家を訪れる。そこ潜む怨念を呼び覚ましてしまった彼女たちは、それ以来呪われてしまい……。", "2007-08-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LISA", "LISA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "死を目の前にして登山へ夢をかける女性の前向きな姿を、高峰マッターホルンの雄大な景色の中に描く。ベルギー、ブリュッセル。自立した女性リサは、あこがれのマッターホルン登頂に失敗し、さらに恋人に裏切られて深く傷ついていた。そんなある日、若きピアニスト、サムと出会い、新しい恋に希望を取り戻す。だが、別れた恋人がHIV感染者だったことを告白。自身もポジティブであることが分かり、リサはサムとの別れを決意し、かねてからの夢だったマッターホルン登頂に再び挑戦する。", "1996-01-01", "37,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Millions", "ミリオンズ", "", "", "http://image.eiga.k-img.com/images/movie/1539/315x210.jpg?1395", 98, "12日後にイギリスの貨幣がポンドからユーロに変わるという架空の設定の下、イギリス北部に住む小学生の兄弟アンソニーとダミアンは、ポンド札がギッシリ入ったバッグを発見する。夢想家の弟ダミアンは、ポンド札が使えなくなる前にこのお金で貧しい人々を救おうと考え、一方、現実家の兄アンソニーは、クラスの人気者になろうと仲間にお金を分配するのだが……。監督は「トレインスポッティング」「28日後...」のダニー・ボイル。", "2005-11-05", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOLD", "ゴールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ベストセラー作家、スミスの同名冒険小説の映画化。南アフリカのリンダーディッチ金鉱の坑内で起こった事故の解明を依頼された男が、知らぬ間に巻き込まれた陰謀の渦……。坑内を襲った大洪水からの脱出シーンが迫力満点。劇画タッチのスペクタクル・アドベンチャー。", "1974-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Other Sister", "カーラの結婚宣言", "", "", "http://image.eiga.k-img.com/images/movie/1789/315x210.jpg?1427", 129, "サンフランシスコ郊外に邸宅を構えるテイト家。全寮制学校に通 っていた知的障害をもつ末娘、カーラが24歳の誕生日を前に数年ぶりで家に帰ってくることになった。家族は心からカーラを歓迎する。娘を家族から引き離したことに罪の意識を抱き「今度こそ彼女を自分の手で守る」と決意していた母エリザベスは、カーラに良家の子女に相応しい作法や服装を施そうとするが、カーラにとってそれは窮屈なだけだった。", "2000-01-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　TEXICAN", "テキサス群盗団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "名優F・クロフォードが主役を張り、凄まじい銃撃戦が展開するウエスタン娯楽活劇。西部劇の歴史の中でも、実に渋い作品群に位置する隠れた名作。早撃ちとして名を轟かせている主人公は、メキシコでの銃撃戦により実兄が死んだことを知る。兄のリベンジに燃える彼は、お尋ね者であるにも関わらず、米国に帰り兄の死の真相を探っていく。一人孤独な闘いが始まる。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロックよ、静かに流れよ", "ロックよ、静かに流れよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「ユキがロックを棄てた夏」などの自主映画で名をはせた長崎俊一の劇場用映画第２作。原作は同名の実話手記で、“ツッパリ”とレッテルを貼られた高校生４人が、仲間の死を乗り越えてロック・コンサートを開くまでを、みずみずしく描いている。俊介は母と妹とともに東京から松本に引っ越してくる。転校早々ツッパリのミネさとトンダからケンカを吹っかけられるが、それもいつしか友情に変わっていった。彼らの夢はロックバンドを結成し、コンサートを開くことだった。しかし、デビューを目前にして、ミネさが交通事故で死んでしまう。長崎監督はいきいきとしたシーンを積み重ね、青春の痛みとひたむきさを描いた。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前学園", "喜劇・駅前学園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "監督以下スタッフの顔ぶれが変わった第19作。かもめ学園の新任体育教師・次郎は女生徒たちのデモを鎮めて皆の信頼を得るが、スパルタ式授業で校長と意見が対立し、ついに全生徒立ち合いのもとナギナタと柔道の他流試合と相なった。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("横浜ばっくれ隊", "横浜ばっくれ隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "『月刊少年チャンピオン』に連載された同名漫画を映画化した、ツッパリ学園コメディのシリーズ第１作。横浜最強にして最低のバカ・成田常松は、醍醐が率いる横須賀の連中が横浜を狙っているという噂を聞く。ところが転校生の美奈に惚れた彼は、彼女がクラス委員の姫野と付き合っていること知って愕然。その間に次々とハマのダチどもは倒されていくのだが……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Omen", "オーメン（2006）", "", "", "http://image.eiga.k-img.com/images/movie/1139/315x210.jpg?4101", 108, "06年6月6日という絶好のタイミングに合わせて、30年ぶりにリメイクされた名作ホラー。実子を死産させてしまったソーン夫妻は、同じ病院で6月6日午前6時に誕生した母親不明の赤ん坊を引き取り、ダミアンと名付け養子として育てる。だが、5年後ソーン一家の周辺で次々と怪事件が起こる……。監督は「エネミー・ライン」のジョン・ムーア。脚本は前作と同じデビッド・セルツァー。", "2006-06-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泥の惑星", "泥の惑星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "インディペンデントで活動を続ける井土紀州が日本映画学校俳優科、映画美学校フィクションコースとコラボレーションして作り上げた一連の作品を公開する「映画一揆　井土紀州2011」にて上映。思春期の少年少女の恋や苦悩を瑞々しく描く青春劇。地方の農業高校に通うハルキはある日、天文部の転校生アキにひと目ぼれしてしまう。そんな矢先、校内の植物が原因不明のまま枯れはじめて……。", "2010-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Great Rock 'n' Roll Swindle", "セックス・ピストルズ／グレート・ロックンロール・スウィンドル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1976年に「アナーキー・イン・ザ・UK」でセンセーショナルなデビューを果たし、イギリスのパンク・ムーブメントを牽引した4人組セックス・ピストルズ。その仕掛け人で、マネージャーでもあったマルコム・マクラレンの視点から彼らを捉えたドキュメンタリータッチの作品。約2年間に渡るバンド活動とその後のメンバーの姿を、ライブ映像やアニメーションを交えながら描く。伝説となったシド・ビシャスの銃乱射シーンは必見。", "1979-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢 追いかけて", "夢 追いかけて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "パラリンピックで４個の金メダルを獲得した全盲のスイマー、河合純一の半生を描く人間ドラマ。ハンデを個性としてとらえ、真っ直ぐに夢を追う姿を、本人の主演でさわやかに描き出す。中３の時に視力を失ってしまった純一。教師や仲間に助けられ、彼は好きな水泳に打ち込む。やがて大学を出て教師となった彼は夢を追い続け、パラリンピック出場選手に抜擢される。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAITHLESS", "不実の愛、かくも燃え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 154, "巨匠I・ベルイマンが自らをモデルに執筆した脚本を、ベルイマンゆかりのベテラン女優L・ウルマンが映画化。人間の弱さやエゴイズムをえぐり出す深遠なドラマだ。荒涼とした島でひっそりと余生を送る老人の前に、マリアンという魅惑的な女性が幽霊のように姿を現す。この二人の対話を通して、かつてマリアンが指揮者の夫、愛くるしい娘がいながら、友人の映画監督ダーヴィッドと不倫関係に陥った過去が回想されていく。", "2000-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROADFLOWER", "ロードランナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ジャックは恋人のヘレンとスーパーモデルの娘アシュレーを連れて、休暇に出発する。父の恋人に気分を害したアシュレーは、ドライブインで知り合った男クリフに惹かれる。しかし、クリフの正体は、刑務所から出てきたばかりの狂暴な殺人者だった。クリフは、アシュレーを手に入れるため、ジャックや友人たちを襲い、ジャックは瀕死の重傷を負う。狂気と化したクリフはアシュレーとヘレンを拉致し逃走する。怒りに燃えたジャックは、クリフとの１対１の戦いに挑む。「ターミナル・ベロシティ」でも歯切れのいい演出を見せたD・サラフィアンによるアクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　CELLULOID　CLOSET", "セルロイド・クローゼット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ハリウッド映画における同性愛描写を検証したドキュメンタリー。膨大なフィルムの中から120作品のシーンをフィーチャーし、スタッフや俳優の証言を織り交ぜて同性愛の受け止められ方の歴史をたどる。ホモセクシャルを表現した意外な描写や、検閲でカットされたシーンなど、興味深い映像を満載。「ベン・ハー」「理由なき反抗」などの名作に秘められた、ゲイにまつわる逸話も明かされる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PREUVE D’AMOUR", "青い殺意", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "映画スタジオ建設の中心人物チャールスへのインタビューを終えたジャーナリストのマルタンは、帰りの電車でセクシーな女性ルーと知り合い、一夜をともにする。だがその日から、スタジオ関係者が次々と殺された。ルーに疑惑を抱きながらも愛し始めるマルタンは……。G・ダーモン主演のサスペンス・ミステリー。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PURE COUNTRY", "ピュア・カントリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "カントリー・ミュージックのスター、G・ストレートの映画初主演作。ダスティは、富も名誉も手に入れたカントリー・ミュージック界のスーパー・スターだった。ある時、彼は音楽への情熱をすっかり失ってしまった自分に気付く。そんな自分に嫌気がさし、旅に出たダスティは、美しい女性ハーリーに出会った。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もののけ姫", "もののけ姫", "", "", "http://ecx.images-amazon.com/images/I/51g1d2%2BuGhL._SL210_.jpg", 133, "当時の日本映画歴代興行収入第1位を記録した宮崎駿監督、原作、脚本の劇場用アニメーション作品。舞台は室町時代の日本。タタリ神にかけられた呪いを解くため西方へ旅立った少年アシタカは、人間でありながら神々の側につくもののけ姫と呼ばれる少女サンと出会う。アニメーション作品として初の日本アカデミー賞最優秀作品賞の受賞をはじめ、様々な国内の映画賞を受賞した。", "1997-07-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANIMAL CRACKERS", "けだもの組合", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "口髭に眼鏡という装いのグルーチョ、三角帽にギョロ目で女大好きのチコら、珍妙なキャラクターのマルクス４兄弟が繰り広げるスラップスティック・コメディ。彼ら扮する“名士”が上流社会の晩餐に招かれたためにパーティーは大混乱！", "1930-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("坊っちゃん", "坊っちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "夏目漱石の同名小説の最初の映画化。監督は、東宝の屋台骨を支えた監督の一人、山本嘉次郎で、彼にとっては東宝の前身P・C・Lに入社して３作目にあたる。内容は、松山の中学教師として赴任してきた江戸っ子の坊っちゃんが、野ダイコ、赤シャツといった権力に迎合する人々と反目し、我を通して去って行くというもの。この作品のヒットは、のちの東宝文芸路線の基盤を築いた。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BANDWAGON", "バンドワゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "アマチュア・ロックバンドの奮闘を描いた青春ドラマ。人前では歌えないシャイなボーカリスト、ドラッグ好きのギタリスト、すぐにキレるベーシスト、マザコンで甘え上手なドラマー。バンドを結成した彼らが伝説の敏腕マネージャーに見出され、ツアーに出発。そんな彼らのトラブル続きの道中を、気取らないユーモアとともに描出。軟弱だが憎めないメンバーの個性がいきいきと伝わり、好感度の高いドラマとなっている。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌13・ハマちゃん危機一髪！", "釣りバカ日誌13・ハマちゃん危機一髪！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "富山の富豪・黒部から美術館建設という大仕事をとってきたハマちゃんだが、そのデザインをめぐって現地に赴いた設計部の桂を黒部が気に入り、息子の嫁にとハマちゃんに相談したことから、大騒動が始まる。シリーズ３作目となる本木克英監督のスラップスティック感覚みなぎる演出が楽しい。富豪役の丹波哲郎が観客の期待を裏切らない怪演。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀座旋風児", "銀座旋風児", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "インテリア・デザイナーの二階堂卓也は、戦時中に国民から上納されたダイヤを横領し、中国人になりすまして銀座のボスになった男・堀田を追い詰める。ラストに“風ノ如ク又カエリ来ル！”という紙片を残し、消え去る卓也のキャラクターのキザッぽさ、これぞ小林旭の真骨項といえよう。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空の神兵　陸軍落下傘部隊訓練の記録", "空の神兵　陸軍落下傘部隊訓練の記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "“空の神兵”が落下傘部隊の代名詞として使われるようにまでなった、落下傘部隊の兵士たちの過酷な訓練風景を記録した迫力の戦争映画。降下していく落下傘を高速度カメラで追うなどの優れた撮影技術が駆使されている。 当時の映画評論家たちによって絶賛され、劇中で流れている植木三郎作詞・高木東六作曲の同名主題歌が大ヒットした。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シベリア超特急", "シベリア超特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "映画評論家の水野晴郎が長年の夢だった監督に初挑戦したサスペンス・ミステリーの怪作。1938年、モスクワと満州の間を走るシベリア鉄道の列車内部で連続殺人が起きる。推理を開始した山下奉文陸軍大将はスターリンによって送り込まれた暗殺者と遭遇するのだが……。名作からの引用もさることながら、スクリーンを独占する“水野大将”の暴れぶりが最高。あれよあれよという間に、驚愕のダブルどんでん返しの大団円を迎えることになる。カルト人気を得て、その後シリーズ化された。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Britannia Hospital", "ブリタニア・ホスピタル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "「ifもしも……」「オー！ラッキーマン」に続きリンゼイ・アンダーソン監督と怪優マルコム・マクダウェルのコンビが贈る“ミック・トラビス3部作”の第3作。創立500周年の記念式典を目前に控えたイギリスの大病院ブリタニア・ホスピタル。従業員たちのストライキによる大混乱の中、外科医ミラー博士は恐ろしい人体実験を決行しようとしていた。取材のため病院に潜入していたテレビリポーターのミックがその標的となり……。", "1982-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO—MOON JUNCTION", "トゥー・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "アメリカ南部の小さな町を舞台に、住む世界の異なる男女の愛を、新感覚で描いたエロティック・ラブ・ストーリー。名家の令嬢エイプリルは、エリートの座を約束された婚約者がいたが、ある日カーニバルで知り合った野性的な青年ペリーに惹かれていく。最後に彼女が選んだのは官能の世界であった……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOCTEUR POPAUL", "ジャン・ポール・ベルモンドの　交換結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "美女に飽きて、ブス口説きに夢中のプレイボーイが、理想の醜女と出会い、結婚するが、彼女にはセクシーな妹がいた。妹の結婚を妨害し、彼女に子供まで産ませてしまった夫に対し、妻は巧妙な復讐劇を仕組むのだった。シャブロル監督による異色のブラック・コメディ。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Naissance des Pieuvres", "水の中のつぼみ", "", "", "http://image.eiga.k-img.com/images/movie/53519/315x210.jpg?2509", 85, "15歳の少女マリーは、シンクロナイズド・スイミングの大会で高慢な美少女フロリアーヌに恋をしてしまう。フロリアーヌに近づきたい一心で彼女が通うスイミングスクールに出向くマリー。何とかフロリアーヌから見学の許可をもらった彼女は、やがてフロリアーヌからある秘密の願いを打ち明けられる。フランスの新人女性監督セリーヌ・シアマが、自身の体験を基に10代の少女たちが抱える思春期の痛みをみずみずしく綴った青春ドラマ。", "2008-06-28", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Teen", "American Teen アメリカン・ティーン", "", "", "http://image.eiga.k-img.com/images/movie/53879/315x210.jpg?2172", 95, "「くたばれ！ハリウッド」のナネット・バースタイン監督が、アメリカ中西部の地方都市ワルシャワの高校に通う10代の若者5人に1年間密着したドキュメンタリー。父親からの期待とスランプに悩むバスケ部エース、映画監督になりたい変わり者の少女、マーチングバンドとゲームにしか興味のないオタク少年、裕福な家庭に育ったいじわるな少女、変わり者に恋したイケメン青年など、どこにでもいるようなティーンたちの高校最後の1年間をカメラが追う。", "2008-10-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LIFE AND DEATH OF COLONEL BLIMP", "老兵は死なず", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "1902年のボーア戦争で知り合った、二人の男の友情のやり取りから、第二次世界大戦突入までの40年間を綴った大河ロマン。戦争下で製作された、いわゆる戦意高揚映画である。当時大人気だったD・カーが３役をこなしているのにも注目したい。", "1943-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オートバイ少女", "オートバイ少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "1973年に『ガロ』に掲載された同名漫画を映画化。幼い頃に父と生き別れた17歳の少女・みのる。バイクで北海道へ父親探しの旅に出かけ、様々な人々と出会う彼女の心の揺れをいきいきと描出。「僕は天使ぢゃないよ」以来、20年ぶりに演出を手掛けたミュージシャン・あがた森魚のロマンあふれる語り口が光る。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bring It On", "チアーズ！", "", "", "http://image.eiga.k-img.com/images/movie/1329/315x210.jpg?1379", 100, "高校のチアリーディング・チームの新キャプテン、トーランス（ダンスト）は、全国選手権大会目前に、チームの振り付けが盗作だと知って愕然、新たな振り付けの猛練習を開始するがはたして優勝は可能なのか？　リード監督は映画のメイキングやディズニーのTV番組、スーパーチャンクなど米インディバンドのクリップ出身。脚本のベンディンジャーは音楽誌「SPIN」やMTVニュースのライター、クリップ監督を経て脚本デビュー。", "2001-08-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOOK OF LOVE", "BOOK of LOVE　あの日の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "高校最後のプロム。ジャックはクラス一の美人リリーに憧れていたが、彼女はなんと不良グループのリーダー、アンジェロのガールフレンド。早くパートナーを決めたいジャックは、思いきってアンジェロの妹ジーナに声をかけてみるが……。青春、1950年代、ロックンロールという必須条件を満たしたフィフティーズ・ムービー。ビデオ発売時タイトルは「ブック・オブ・ラブ／あの日の恋」。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACROSS　THE　GREAT　DIVIDE", "アドベンチャー・ファミリー／　ロッキーを越えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1970年代を代表するファミリー冒険もの「アドベンチャー・ファミリー」シリーズのスピン・オフ的な３弾。19世紀のアメリカのフロンティア時代をバックに幼い姉弟と青年の３人が大冒険を繰り広げる。遺産相続のためロッキー山脈を越えてオレゴンへ向かう姉妹。途中で青年と知り合い、クマ、オオカミ、インディアン、馬泥棒一味に遭遇しながらも彼らは山を越え、川を下り目的地を目指す。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JACQUOT DE NANTES", "ジャック・ドゥミの少年期", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「シェルブールの雨傘」などで有名なフランスの名匠J・ドゥミの故郷ナントでの幼い日々を、彼の妻であるA・ヴァルダが限りなく優しいまなざしで撮った感動の一編。ディズニーの『白雪姫』が大好きだった少年がいかにして映画監督になる夢をふくらませていくかが、詩情豊かに描かれていく。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん　のび太のパラレル西遊記", "ドラえもん　のび太のパラレル西遊記", "", "", "http://ecx.images-amazon.com/images/I/61ExWye1zPL._SL210_.jpg", 90, "孫悟空に会おうとのび太が唐の時代の中国へ。孫悟空との対面で喜ぶのび太だったが、このタイムスリップがきっかけで現実世界が妖怪に支配されることになる。さらに、しずかが妖怪、羅刹女らにさらわれて……。三蔵法師など『西遊記』でお馴染みの人物、妖怪が出てくるのも見逃せない。また本作でドラミちゃんが劇場版に初登場する。", "1988-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("When You're Strange", "ドアーズ　まぼろしの世界", "", "", "http://image.eiga.k-img.com/images/movie/55589/315x210.jpg?4309", 82, "60年代を象徴するアメリカのロックバンド、ドアーズを題材にしたドキュメンタリー。本作のための新たな撮影は行わず、当時のオリジナル映像のみで構成。結成前の映像やエド・サリバンショー出演時の映像、さらにジャニス・ジョプリンやアンディ・ウォーホルとの交流といった貴重な資料を交えながらバンドの軌跡をたどり、その創作活動の裏側を明かしていく。監督は「ジョニー・スエード」のトム・ディチロ。ジョニー・デップがナレーションを務める。", "2010-10-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("木枯し紋次郎", "木枯し紋次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "人気を博した笹沢左保の股旅小説を、中島貞夫が映画化。TVでは中村敦夫の当たり役だった紋次郎を、菅原文太が演じている。内容は、身代わりの罪で島流しにあった紋次郎が島を脱け出したものの、兄弟分に裏切られ、あてのない旅に出るというもの。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SON　FRERE", "ソン・フレール／兄との約束", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "フランスの名匠パトリス・シェローが、死の悲しみと、そこから生まれる安らぎを描き、第53回ベルリン映画祭で銀熊賞（監督賞）を受賞した感動作。不治の病に冒された兄。そんな兄の看病をする弟。反目していた兄弟が、死を目前にして歩み寄っていくまでを繊細な映像の中に描き出す。血小板が破壊される難病が再発したトマ。死への不安に襲われた彼は、長く疎遠だった弟リュックに病院に付き添ってくれるように頼む。", "2005-02-12", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Am", "僕がいない場所", "", "", "http://image.eiga.k-img.com/images/movie/34249/315x210.jpg?1616", 98, "ポーランドの女性監督ドロタ・ケンジェルザブスカが、親の愛を得ることができない孤独な子供たちの姿を描いた衝撃のドラマ。孤児院で先生や他の子供たちと馴染めずにいる少年クンデルは、孤児院を抜け出し母の元へと向かう。しかし母は町の男たちと関係を持ち、乱れた生活を送っていた。1人で生きていこうと決意したクンデルは、川に捨てられた舟で暮らし始める。そんな彼の前に、裕福な家の娘でありながら孤独を抱える少女クレツズカが現われ……。", "2007-10-13", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCRATCH", "SCRATCH〈スクラッチ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "スクラッチのテクニシャンやDJバトルの世界チャンピオンなど、世界的に活躍するDJたちの様々な側面を映し出したドキュメンタリー。スクラッチテクニックの誕生から、近年のDJ哲学“ターンテーブリズム”に至るまでの進化の歴史を、DJたちによるダイナミックなパフォーマンスや貴重なインタビューで綴る。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANCCOIS TRUFFAUT PORTRAITS VOLE´S", "フランソワ・トリュフォー　盗まれた肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1984年に他界したヌーヴェル・ヴァーグの巨匠フランソワ・トリュフォーの生涯に迫ったドキュメンタリー。エリック・ロメール、クロード・シャブロルをはじめとする同志、家族や親友など、トリュフォーと親交の深かった30数名がトリュフォーとの思い出を語る。その他「大人は判ってくれない」のスクリーン・テストや「野性の少年」の演出風景など、トリュフォーの作品をめぐる、貴重な映像の数々を織り交ぜて彼の素顔を描いている。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("甦れ！ウルトラマン", "甦れ！ウルトラマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ウルトラマン生誕30周年を記念して３部構成で製作された「ウルトラマン ワンダフルワールド」の第１部。宇宙恐竜ゼットンとの戦いに敗れたウルトラマンのその後を、テレビ時代の映像を脚本通りに再編集して描く。ゼットンとの戦いに敗れたウルトラマンは、スランプに陥っていた。その一方で、ハヤタ隊員は怪獣総攻撃の白昼夢に悩まされていた。この白昼夢がウルトラマンの予知能力だとわかりながらも、誰にも言えないでいた時、イデ隊員が同じ夢を告白。戦いに備え新兵器を開発したという。そんな中、謎の円盤群が飛来して来る。なお再編集にあたっては、アフレコはすべて俳優本人たちが行っている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Time Machine", "タイムマシン", "", "", "http://image.eiga.k-img.com/images/movie/1709/315x210.jpg?3861", 96, "1899年のニューヨーク。事故死した恋人を救うためタイムマシンを開発した数学者アレックスは、80万年後の未来に不時着。そこには人類が異種族に進化した世界があった。監督は原作者H・G・ウェルズの曾孫、サイモン・ウェルズ。「プリンス・オブ・エジプト」などドリームワークス製アニメの助監督を経て、本作で監督デビュー。特殊視覚効果はデジタル・ドメイン他が担当。未来人の特殊メイクはスタン・ウィンストン・スタジオ。", "2002-07-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Journey 2: The Mysterious Island", "センター・オブ・ジ・アース2 神秘の島", "", "", "http://image.eiga.k-img.com/images/movie/57419/315x210.jpg?1473", 94, "東京ディズニーシーのアトラクションでも知られるジュール・ベルヌの冒険小説「地底旅行」を3D映画化した「センター・オブ・ジ・アース」（2008）の続編。ある日、存在するはずのない謎の島から発せられる救難信号をキャッチしたショーンは、新たな義父ハンクとともに冒険に旅立つ。巨大なハチや小さなゾウなど動物のサイズが逆転した不思議な「神秘の島」を見つけたショーンだったが、島には驚くべき秘密が隠されていた。主人公ショーンを演じるのは前作に続きジョシュ・ハッチャーソン。義父ハンクにドウェイン・ジョンソン（ザ・ロック）、ヒロインのカイラニに「ハイスクール・ミュージカル」のバネッサ・ハジェンズ。", "2012-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PIECES OF APRIL", "エイプリルの七面鳥", "", "", "http://ecx.images-amazon.com/images/I/31NM5W371HL._SL210_.jpg", 80, "自由奔放に生きてきた女の子と、彼女と絶縁状態にある家族との再会を綴る感動作。仲の悪かった母が余命わずかと知らされたエイプリル。そこで彼女は、感謝祭に疎遠だった家族を家に招待し、ご馳走をふるまおうとするが……。「ギルバート・グレイプ」の脚本家P・ヘッジズが監督デビューを果たし、重くなりがちな物語をユーモア満載の軽快なタッチで描出。初めての料理に悪戦苦闘する主人公に扮したK・ホームズが実にキュートだ。", "2004-10-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KICKED IN THE HEAD", "ステューピッド・イン・ニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "失業に火事など、不幸なことが一挙に相次いで起こり、とにもかくにもついてないレドモンド。それならば、いっそ自己の精神世界を充実させたいと願う彼に魂の救済はあるや否や？　若い才能を見込んだM・スコセッシが製作総指揮を買って出た、筋金入りのニューヨーク・インディーズ映画。不幸も連続して起きると、どこか陽気で楽しい!?", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("卒業プルーフ", "卒業プルーフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "俳優・鶴見辰吾が初プロデュースした日本版ブラット・パック・ムービー。卒業を控えた大学生２グループの青春群像を軽いタッチで描く。監督の牛山真一も、この作品で劇場用映画を初演出、登場人物の持つキャラクターの描き分けに手腕を発揮。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SGT.PEPPER’S LONELY HEARTS CLUB BAND", "サージャント・ペッパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ポピュラー音楽史上の最高傑作といわれるビートルズの同名LPをもとに脚色した、ロック・ミュージカル。当時のスーパースターが大挙出演し、各自がオリジナルのアレンジで、ビートルズのヒットナンバーを演奏したことが、音楽ファンの話題となった。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い荒野", "赤い荒野", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "中国地方の高原地帯が舞台。牧場主の留守中に、その妻が女手一つで切り回す牧場を悪玉一味が乗っ取ろうとするが、牧場主の親友である宍戸錠が現れて危機を救う。アメリカの雑誌にも紹介されて話題になった宍戸錠主演による一連の和製ウエスタンの一編。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Formula51", "ケミカル51", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "大学で薬理学を学んだエルモは、卒業式の日にマリファナを押収され、薬剤師の道を閉ざされた。ドラッグ精製人となった彼は、ある日究極のドラッグ“POS51”の開発に成功する。これを利用して足を洗おうと考えた彼は、リバプールの犯罪組織に売り込みを開始するが……。スカート姿のごっつい黒人、神経質そうなチンピラの主人公の2人をはじめ、間抜けな組織のボス、細い肢体で銃をぶっ放す美人ヒットマンなど個性派キャラクターが続々登場。ハイテンションな怪演ぶりを発揮する彼らが巻き起こす、ドタバタ・アクションコメディ。", "2003-02-01", "1,3,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富士見二丁目交響楽団シリーズ　寒冷前線コンダクター", "富士見二丁目交響楽団シリーズ　寒冷前線コンダクター", "", "", "http://image.eiga.k-img.com/images/movie/57659/315x210.jpg?1781", 83, "秋月こおの人気ボーイズラブ小説「富士見二丁目交響楽団」シリーズの映画化。守村悠季がコンサートマスターを務める富士見市民交響楽団（通称フジミ）に、芸大出身で留学帰りの二枚目指揮者・桐ノ院圭がやってくる。圭の指揮で楽団はみるみるレベルアップしていくが、それまで楽団を引っ張ってきた自負のある悠季は圭のやり方が気に入らず、楽団を辞めようとする。しかし、そんな悠季を圭が引き止めて……。主演はミュージカル「テニスの王子様」でも共演した高崎翔太と新井裕介。", "2012-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("盗まれた恋（I Love You）", "盗まれた恋（I Love You）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "浅草の踊子・良子は劇場の閉鎖で失業し、客であった銀行員・阿久津と結婚しようと試みるが、彼はとんだプレイボーイ。腹を立てた良子は、自分には無名の書家の婚約者があると口走り、阿久津の力で有名にしてほしいと頼む。ところが、阿久津が引き受けてしまったのでさあ大変……。鏡二郎の『恋人』を市川崑が翻案した恋愛コメディ。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Godfather", "ゴッドファーザー", "", "", "http://ecx.images-amazon.com/images/I/51X2WjhHd9L._SL210_.jpg", 175, "アメリカのマフィアの内幕を描いたマリオ・プーゾのベストセラー小説を、当時32歳のフランシス・フォード・コッポラ監督が映画化した壮大なファミリードラマ。イタリア・シシリー島からアメリカに移住し、巨万の富を築き上げたビトー・コルレオーネ一族の跡目相続や、世代交代を謀る周囲のマフィアとの間に起こる抗争が重厚なタッチで綴られる。主演はマーロン・ブランドとアル・パチーノ。72年度のアカデミー賞では作品賞を含む3部門を受賞した。", "1972-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆走！ムーンエンジェル 北へ", "爆走！ムーンエンジェル 北へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "工藤静香主演で贈る女性トラッカーの青春と友情を描いたロード・ムービー。女性トラッカーの葛西ルナは、カミオンのマドンナの異名を持つ業界では有名な女。愛車・ルナ号を転がし日本全国を駆けめぐるルナは、冬場の出稼ぎのため先輩の喜代子のいる釧路へと向かう。途中ライバルの夏子とデッドヒートしているところを、兵藤という警官に取り押さえられるが、トラック野郎の如月大五郎に助けられる。だが、今度は先輩のトラックが兵藤に没収されてしまい、ルナは警察に乗り込むが兵藤がニセ警官と分かり……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Patton", "パットン大戦車軍団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 172, "第2次世界大戦のアフリカ戦線でナチス・ドイツ軍を相手に闘ったアメリカの名将、ジョージ・S・パットンの波乱に満ちた半生を、「猿の惑星」のフランクリン・J・シャフナー監督が映画化した戦争スペクタクル。パットン将軍をジョージ・C・スコットが熱演しアカデミー主演男優賞に輝いたが、これを辞退したことで話題になった。他にもアカデミー作品賞、監督賞、主演男優賞、脚本賞など、1970年度の最多7部門を受賞。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT TRAIN TO VENICE", "処刑特急 テロリストの罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "オリエント急行を舞台に繰り広げられる戦慄のサスペンス・アクション。ジャーナリストのマーティンは、ネオ・ナチ・ムーブメントのスクープを書き上げ、出版社のあるベニスへオリエント急行で向かう。が、列車には、ネオ・ナチのテロリスト集団が同乗していた。不気味なスキンヘッドの一団によって乗客は次々と殺され、オリエント急行はさながら“処刑特急”と化して恐怖のどん底を疾走する。マーティンと恋人ベラがドーベルマンを操る謎の男の追撃をかわすなか、列車はベニスに到着。しかし、この美しい水の都では巨大な罠が二人を待ち受けていた。", "1992-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドカベン", "ドカベン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "水島新司の大人気コミックを原作とした野球映画。明訓高校に入学した山田太郎と岩鬼正美はともに弱小柔道部に入り、関東大会決勝戦まで進んだあと野球部に入部する。荒唐無稽な描写が続きつつも、兄妹の絆をクライマックスの見せ場に持ってくるあたり、やはり鈴木演出。永島敏行のデビュー作でもある。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("娼婦", "娼婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "売春禁止法が施行されても、女の肉体が存在し続ける限り“娼婦”性は永遠のものであるというテーマのもと、高林陽一が描く“娼婦・売春”に関するレポート。男と女の延々と続けられるファック・シーンの前半、幻想的なシーンが積み重ねられる後半、これといったストーリー、決まった話はないが、それゆえに痛烈に訴えかけてくる作品である。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鷲と鷹", "鷲と鷹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "井上梅次が石原裕次郎と三國連太郎の初顔合わせで監督した海洋アクションの力作。貨物船“海洋丸”に、船長を父の仇と狙う若者と、保険金目当ての偽装沈没計画をかぎつけた刑事が、ともに水夫に化けて乗り込んでくる。二人は反目しあいながらも最後は協力して危機を乗り越える。井上梅次監督がボクシング映画の秀作「勝利者」に続いて演出した石原裕次郎主演作である。井上監督は裕次郎と二人の女の三角関係、三國連太郎との重量感あふれる殴り合い、そしてクライマックスのロケ、セットを巧みに併用した大暴風雨シーンと盛りだくさんの内容で、一級の娯楽作品に仕上げた。特に当時、東洋一の設備を誇った新設ステージにおける日活特撮陣入魂のスペクタクル・シーンは迫力十分で、この後、日活でこの種の大規模な海洋アクション映画が作られなかったのは惜しまれる。裕次郎は名優・三國を相手に堂々たる主演ぶりを見せ、「俺は待ってるぜ」を経て井上監督による「嵐を呼ぶ男」のドラマー役で国民的大スターに成長する。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AFFAIR OF THE NECKLACE", "マリー・アントワネットの首飾り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "フランス王朝を崩壊させたといわれる実在の女性ジャンヌ・ド・ラ・モット。彼女が仕組んだスキャンダル“首飾り事件”を、新たな解釈で描くサスペンスフルな歴史ドラマだ。名家に生まれたジャンヌは、幼い頃に孤児となり全財産を奪われる。以来、名誉を取り戻すために生きてきた彼女は、男たちを巧みに操り、王室をも揺るがす一大計画を実行に移す。家名を取り戻すことに恐るべき執念を燃やしたジャンヌを、H・スワンクが迫真の演技で熱演。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひき逃げ", "ひき逃げ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "交通事故で子供を殺された女の復讐劇。主演の高峰秀子の夫である、松山善三のオリジナル脚本を成瀬巳喜男が演出。交通戦争という社会問題を告発した物語展開には、松山の道徳観が色濃く反映している。良心の呵責にさいなまれ自滅する加害者役の司葉子が好演。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SENSO’４５／BLACK　ANGEL", "ティント・ブラス／秘蜜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "「郵便屋」で知られるイタリアン・エロスの巨匠ティント・ブラス監督作。「髪結いの亭主」で脚光を浴びたアンナ・ガリエナが、フェロモンたっぷりの中年女性を体当たり演技で好演。第二次大戦末期のイタリア。一部の上流階級の人々は、戦火とは無縁の豪勢な日々を謳歌していた。そんな中、夫カルロと観劇に出かけたリヴィアは、劇場で出会った中尉と禁断の恋に落ちる。彼女は次第に深みにはまっていき……。", "2002-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新女囚さそり・特殊房X", "新女囚さそり・特殊房X", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "さそり役には夏樹陽子、演出は前作同様、小平裕。病院と政界の陰謀によって恋人を謀殺されたナミが、怨の化身となって復讐する。悪役のカリカチュアした性癖、簡略化されたセットの使用など、図式的な設定が随所に見られる。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カポネの舎弟・やまと魂", "カポネの舎弟・やまと魂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "米国シンジケートの依頼で、カポネは侠客・伊豆政を暗殺するのだが……。公害の元凶となっている日米合弁会社の悪らつな手段に、男カポネの怒りが爆発する。親分肌が似合う富三郎が貫ろくを見せる、銃撃アクションをメインにした爽快作品", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK SCORPION", "ブラック・スコルピオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "犯罪都市エンジェル・シティに現れた悪を葬る闇の処刑人。タフでクールな彼女の名はブラック・スコルピオン。コルベット・スティングレーを改造した走る武器格納庫“スコルピオン・モービル”を駆使して犯罪に挑むその正体は、元秘密捜査員のダーシー。正義の通用しない街に愕然とし、法に縛られない自らの分身を造り出したのだった。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("破れ傘長庵", "破れ傘長庵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "悪徳医師・村井長庵が主人公で、持ち前のず太さと賢さで“欲しいものは何でも手に入れる”とばかりに悪事を重ね、逆に悲業の死を遂げるまでが描かれている。森監督の、人間を冷めた目で見つめる演出の確かさと、勝のアンチ・ヒーローぶりが光る作品。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JASON AND THE ARGONAUTS", "アルゴ探検隊の大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ギリシャ神話『アルゴ船の遠征』を映画化したファンタジー・アドベンチャー。製作及び特殊撮影を担当している人形アニメーションの第一人者、R・ハリーハウゼンが、見事に神話世界を描いている。王位返還を要求したジェイソンに、ペリアス国王は交換条件としてある難題を要求。それは、コルキス国にある、幸福をもたらす黄金の羊の毛皮を取ってくることだった。勇者たちを伴い、アルゴ号に乗り込むジェイソン。だが、行く手には様々なモンスターたちが待ち受けていた。青銅の巨人タロスや７頭の巨竜ヒドラ、骸骨軍団など、自由奔放なイマジネーションの世界が広がる。なかでも、骸骨軍団との戦いのシーンは壮絶。骸骨の動きが驚くほどリアルだ。", "1963-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("京極夏彦／怪・七人みさき", "京極夏彦／怪・七人みさき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "京極夏彦のミステリー時代劇を原作にした人気TVドラマのシリーズ第１作を、劇場公開のために再編集したディレクターズ・カット版。魔よけの札を撒いて歩く御行の又市と、山猫廻しのおぎん、そして戯作家の山岡が、７人の娘が殺されるという呪われた伝説“七人みさき”の謎に挑んでいくさまがオカルティックに描かれていく。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bitter Tea of General Yen", "風雲のチャイナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "フランク・キャプラ監督による、中国を舞台にしたメロドラマ。宣教師である婚約者と結婚するため、革命真っ只中の中国へやって来たアメリカ人ミーガン。革命による混乱で婚約者とはぐれ怪我を負った彼女は、イェン将軍に助けられ、彼の豪邸へ連れて行かれる。そこで彼女は、将軍の愛人が敵に極秘情報を流そうとしているのを発見する。理想主義者のミーガンは、許すことによって愛人は考えを改めるのではないかと将軍に提案するが……。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOME GIRLS", "ダルク家の三姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "R・レッドフォードが製作総指揮した、三姉妹と一人の青年が繰り広げる青春ドラマ。大学生のマイケルは、突然大学を去ったギャビーのあとを追って、カナダのケベック市を訪れた。そこで彼は、彼女とその二人の姉妹と接していくうちに、真実の愛と生きる勇気に目覚めていく。", "1988-01-01", "1", 0, NOW(), NOW());
