INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sex and the City", "セックス・アンド・ザ・シティ", "", "", "http://image.eiga.k-img.com/images/movie/53179/315x210.jpg?2754", 144, "ニューヨークで暮らす4人の30代独身女性の日常をスキャンダラスに描き、日本でも大ブームを巻き起こしたTVシリーズ「セックス・アンド・ザ・シティ」がスクリーンに登場。サラ・ジェシカ・パーカーらおなじみのキャストが再結集し、TV版でそれぞれの“ハッピーエンド”を迎えた4人のその後を描き出す。人気スタイリスト、パトリシア・フィールドによる最新ファッションも注目。", "2008-08-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hospital", "病院", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MA FEMME S’APPELLE REVIENS", "夢見るシングルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "妻と離婚し、失意のどん底の医師ベルナールは、人生にすっかり絶望し、生活環境を変えるためにパリへやって来た。ある日、彼はアパートのエレベーターの中で、失神している女性を発見する。彼女の名はナディーヌ。彼の向かいの部屋に住むファッション・カメラマンだった。愛の幻滅を何度も味わった彼女は、哀しみを紛らわすためのヤケ食いの時期を終わらせ、現在は断食の時期に入っているのだという。お互い愛に傷ついている二人は、次第に心を通わせ、それぞれの愛を語り合うようになっていく。長い間未公開だったが、1996年に初めて劇場公開された。", "1981-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TOMB OF LIGEIA", "黒猫の棲む館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ポー作品の映画化で有名なコーマン監督の怪奇映画。奇妙な墓石の前で突然馬が暴れ出し、振り落とされたロウェナを助けたフェル。やがて、二人は結婚するが、新婚早々屋敷では不思議な出来事が次々と起こる。何かを暗示するように現れる黒猫の謎とは……。", "1964-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLARA’S HEART", "マイフレンド,クララ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "R・マリガン監督が描く、ハートウォーミングな友情物語。妹の死や、両親の別居など、悲しい出来事のため心を閉ざしてやって来た黒人女性との心の交流を通じて、明るさを取り戻していく。ユーモアあふれる心優しき家政婦をW・ゴールドバーグが好演。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Los Dioses Rotos", "壊れた神々", "", "", "http://image.eiga.k-img.com/images/movie/54789/315x210.jpg?4337", 90, "20世紀初頭にキューバの首都ハバナのサン・イシドロ地区を牛耳った伝説の女衒アルベルト・ヤリーニの物語をモチーフに、ハバナ出身のエルネスト・ダラナスがメガホンを取ったヒューマンドラマ。アルベルト・ヤリーニの伝説を研究することになった大学教授ラウラ。調査を進めるうち、彼女は現在のハバナに広がる闇の世界へと足を踏み入れていく。2008年新ラテンアメリカ映画祭で観客賞・映画文化批評賞を受賞。", "2009-09-21", "49", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BILLIE", "ビリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「奇蹟の人」の名演で知られるP・デュークが主演した、青春コメディ。とある学校の陸上競技部で、つねに一位の女性選手ビリー。彼女を中心に、万年二位のボーイフレンドや市長候補の父、婚前妊娠がバレて大騒動になる姉などの人間模様を綴る。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("菩提樹・リンデンバウム", "菩提樹・リンデンバウム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "『あしながおじさん』をベースにした大和和紀のマンガを原作に、名の知れぬ人の援助によって大学の医学部に入学した麻美の愛と苦悩を描く。南野陽子がこれまでのコミック調の主人公とは違った“普通”の女の子に扮している。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE　NIGHT　AT　McCOOL’S", "ジュエルに気をつけろ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "理想の家を探し求めて、男から男へと渡り歩くセクシーな娘ジュエル。幸か不幸か、そんな彼女とめぐり合ってしまったバーテン、刑事、弁護士の三者三様の運命を描く。L・タイラーが時に純真な、時に妖艶な魅力を放つヒロインをイキイキと演じるラブ・コメディ。一種のファム・ファタールものだが、３人の男それぞれの体験を順番に語り明かし、最後に意外なオチを設けている凝った構成が新味といえる。M・ダグラス主演の「フォーリング・ダウン」のパロディーもチラリ。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MENACEIISOCIETY", "ポケットいっぱいの涙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "スパイク・リー、アーネスト・ディッカーソンなどが切り開いてきたブラック・ストリート・フィルムの新しき旗手、弱冠20歳のアレン＆アルバート兄弟のデビュー作。1960年代後半に大規模な暴動が起こったことで知られる、ロサンゼルスのワッツ地区を舞台に、そこでサバイバルしていくしかない少年たちの生き方を鮮烈に描いている。銃による力だけが正義の街で、人を殺すことをなんとも思わず、そして死をもなんとも思わぬ少年たち。暴力だけがすべてではないことを知った時にはすでに……。ミュージック・クリップの演出で鍛えられた、斬新な映像が生み出すバイオレンス描写と、カッティング・ワークが魅力だ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VIE，L’AMOUR，LA MORT", "愛と死と", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "幸福な日々を送る恋人同士のフランソワとカロリーヌ。しかしある日、フランソワは尾行してきた警官に逮捕される。何も知らぬカロリーヌにやがてフランソワの犯した罪が明らかにされ、フランソワは死刑を宣告されるのだが……。C・ルルーシュが詩的な映像の中に死刑問題を鋭く提起する問題作。", "1969-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUT OF CONTROL", "ゲット・アウト 青春超特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "６人の男女高校生が夜間飛行に出かけ、嵐のなか、無人島に上陸する。が、そこは武器密輸グループの取引場所だった。「エンドレス・ラブ」でブルック・シールズの相手役に抜擢された美少年M・ヒューイットの、青春アドベンチャー・アクション。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天狗飛脚", "天狗飛脚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "鬼才・丸根賛太郎の脂のノリ切っていた当時の作品。新興と老舗、対立する二つの飛脚屋の企業戦争に、江戸を荒らす駿足の怪盗や、蔓延する熱病に命を賭ける蘭学医などを絡ませ、舞台は江戸から大阪へと走る……。往年の「右門捕物帖」の“アバタの敬四郎”を思わせる志村喬の演技が絶品の痛快時代劇。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Children...", "カエル少年失踪殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/57789/315x210.jpg?2712", 132, "「殺人の追憶」（2003）で描かれた華城連続殺人事件、「あいつの声」（07）で描かれたイ・ヒョンホ君誘拐殺人事件と並び、韓国三大未解決事件のひとつに数えられる少年の連続失踪事件を題材に描いたサスペンス。1991年3月26日、韓国・大邱（テグ）に暮らす少年5人が「カエルを捕まえにいく」と言って出かけたまま、行方不明になってしまう。スクープを狙うTV局のカン・ジスンや犯人像を分析するファン・ウヒョク教授、子どもたちを捜索する刑事パク・キョンシクは、それぞれの思惑を抱いて事件を追っていたが、そんなある日、ふとしたきっかけで被害者少年の父親に疑惑が向けられ……。", "2012-03-24", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOS SOBREVIVIENTES", "天国の晩餐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1959年、カストロの指導する革命が成功し、バチスタ独裁体制は崩壊した。多くのブルジョワたちは身の安全のためキューバを去った。だが、名門オロスコ家の人々は、祖国にとどまることを選んだ。当主セバスチャンは、すぐ旧体制が復活すると信じていたのだ。やがて反革命軍の侵攻や海上封鎖が行われ、オロスコ家の人々は時来たりと喜ぶが、すべて失敗に終わった。歳月がたつにつれて、オロスコ家の人々は邸内に閉じこもり、自給自足の生活を始める……。資本主義から封建制へ、さらに奴隷制からカニバリズムへと逆行するブルジョワたちの姿をブラック・ユーモアで描く。", "1978-01-01", "49", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とっとこハム太郎・ハムハムハムージャ！幻のプリンセス", "とっとこハム太郎・ハムハムハムージャ！幻のプリンセス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "人気のTVアニメの映画化第２作。ある日、ハム太郎は不思議な夢を見る。シャボン玉に閉じ込められたお姫さまが“助けて”と呼び掛ける夢だ。目が覚めても夢が気になっていたハム太郎は、仲間とともにお姫さまの救出に向かうが、たどり着いたハムージャ王国では夢の通り、お姫さまがナゾの猫にさらわれていた。王国の危機を知ったハム太郎たちは、お姫さまと王国を救うために奮闘する。", "2002-12-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN THE LINE OF FIRE", "ザ・シークレット・サービス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "ケネディ大統領が暗殺された瞬間、現場にいながら大統領を守れなかったシークレットサービス・エージェントのホリガンはいまや定年間近。その彼のもとに大統領暗殺予告の電話が入る。果たしてホリガンは今度こそ大統領を守りきれるのか？　暗殺者役のJ・マルコヴィッチの怪演が光るサスペンス・アクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道三国志", "極道三国志", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "８年前の大阪。殺された総長の仇を討つため東山一家の吉田龍二と勝一平は敵の組長、黒田の殺害を試みて失敗し、投獄される。そして現在、東京では老舗の深大寺一家と新興勢力の田村組が小競り合いを繰り広げていた。出所し、この地に流れ着いた龍二と一平は黒田への復讐を誓いつつ、この抗争に深くかかわるようになる。そして、組長が殺され、深大寺一家が窮地に陥ったとき、二人は組員の松岡と手を組み、田村組に切り込んでいく……。ヤクザ社会に生きる男たちの意地と義侠心を描いた任侠ロマン。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヤマトよ永遠に", "ヤマトよ永遠に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "西暦2202年、40万光年の彼方から暗黒星団帝国が地球を襲い、占領した。古代進は地球の森雪と別れて、小惑星・イカロスに脱出。そこでヤマトに乗船し、敵が地球に仕掛けた重核子爆弾を停止させるため、暗黒星団帝国の本星に向けて出撃する。敵は本星を未来の地球に擬装して、古代らをあざむこうとするが、聖総統・スカルダートと刺し違えたスターシャの娘・サーシャの犠牲によって、ヤマトはついに本星の破壊に成功する。最初TVフィーチャーとして製作された、「宇宙戦艦ヤマト 新たなる旅立ち」に続く形でドラマが展開。キャラクターデザインなどに松本零士調からの脱皮が見られ、美少女・サーシャに人気が集まった。森雪を愛する敵将校・アルフォンの登場など新味が加えられてはいるものの、ドラマ的には第１～２作を超えるに至っていない。公開当時は“ワープディメンション”方式と称して、画面がビスタからシネスコへと変化した。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("落語娘", "落語娘", "", "", "http://image.eiga.k-img.com/images/movie/53629/315x210.jpg?2667", 109, "幼い頃から落語に魅せられ、大学卒業後すぐに落語界へ飛び込んだ香須美は、差別や偏見につきまとわれながらも女前座として雑用に走り回る日々を送っていた。そんなある日、落語界の異端児とされる師匠・平佐が、語れば死ぬという禁断の噺“緋扇長屋”に挑むと言い出して……。永田俊也の同名小説を、「12人の優しい日本人」の中原俊監督が映画化。ヒロイン役に「着信アリ2」のミムラ、破天荒な師匠役に津川雅彦。", "2008-08-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA NOTTE DI SAN LORENZO", "サン★ロレンツォの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "流れ星に願いをかける聖ロレンツォの夜、母親が語る6歳だった夏の思い出。1944年の夏、トスカーナの小さな村がドイツ軍に爆破を予告された。村を出て連合軍を探す村人たちの道行も、少女にはウキウキする旅。やがて小麦畑の中で、パルチザンとファシストが対峙、元は同じ村の同胞たちの銃撃戦が始まる……。タビアーニ兄弟が自身の短編ドキュメンタリーを元に、内戦の悲劇を陽光あふれる美しい映像で綴った寓話的年代記。", "1983-02-11", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二重被爆", "二重被爆", "", "", "http://image.eiga.k-img.com/images/movie/33629/315x210.jpg?4285", 50, "1945年8月に広島と長崎の両市で被爆した“二重被爆者”に焦点を当てたドキュメンタリー。わずか300キロの距離にある2つの都市に投下された原子爆弾。現在も100名以上の二重被爆者がいるとされるが、その存在はほとんど知られていない。当時、長崎県で造船技師として働き、出張先の広島県でも被爆した山口彊さんをはじめ、7名の二重被爆者へのインタビューから、これまで明るみに出ることのなかった二重被爆の実態に迫る。", "2006-09-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪猫からくり天井", "怪猫からくり天井", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "囲碁の対局相手・鍋島備前守に手打ちにされ、許嫁さえ側女に召し出された鍋島家の囲碁師匠・又七郎。彼の母、秋篠は死体を前に鍋島家を呪いながら自害して果てる。そのとき又七郎の愛猫・玉簾が現れ、二人の血潮をなめつくすと、怪しく鳴いて消えた……。“鍋島騒動”を扱ったおどろおどろしい化け猫映画。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CEMENT GARDEN", "ルナティック・ラブ～禁断の姉弟～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "現代イギリス文学界の雄、I・マキューアン原作の問題小説『ザ・セメント・ガーデン』の映画化。父に続いて母が死に、４人の子供たちはその死体をセメント漬けにし庭に埋めた。子供だけが残された館では、17歳の長女と15歳の弟が近親相姦にふけり始め……。フランスの女優、C・ゲンズブールが全裸ヌードを披露し、ハードなラブ・シーンに体当たりで挑戦している。", "1992-01-01", "3,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泣き虫チャチャ", "泣き虫チャチャ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "チャチャこと小野寺千秋はミキサーの助手だが、ディスコでは有名なディスコキング。恋人のはるみやオカマバーで働く勝らと、楽しい日々を送っていたのだがある日、田舎に帰ることになり……。これが監督デビューの花輪金一が描いたさわやかな青春映画。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DROWNING BY NUMBERS", "数に溺れて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "原題の「DROWNING BY NUMBERS」は“次々に溺れて”という意味にもなる。物語は、同じシシーという名前を持つ祖母、母、娘の３人が、それぞれの夫を溺死させていくという、ブラック・ユーモアの効いた話を軸にしている。彼女たちの“殺人”を事故に見せかける検視官と、死にまつわる儀式的なゲームに夢中の彼の息子がそれに絡んで、言葉と数の遊びに彩られた不思議な映像世界が展開する。１から100までの数字が映画の中に散りばめられて、それを追っていくだけでも興味は倍増。「ZOO」「建築家の腹」同様、グリーナウェイ＆ヴィエルニー・コンビの不思議な映像が魅力的。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BATTLE OF BULGE", "バルジ大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "第二次大戦も終わり近い1944年12月、ベルギーのアルデンヌ地方にいる連合軍部隊には早くも戦勝気分が漂っていたが、その頃ドイツ陣営では多数の戦車を集めて反撃態勢を整えていた。こうして12月16日、米軍最前線の部隊は突然轟音とともに現れたタイガー戦車隊の猛攻に大打撃を受ける。その後もドイツ軍の進撃はすさまじく、米軍の燃料貯蔵庫さえもアメリカ兵に変装したドイツ兵に押さえられてしまう有様。しかし沈着冷静な米軍中佐がドイツ軍の大反撃作戦を失敗へと導く。オール男性スター・キャストによる戦争巨編で、莫大な製作費をかけた戦闘シーンの数々も話題になった。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イチかバチか", "イチかバチか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "奇才・川島雄三の遺作となった作品。大金持ちでありながらケチな生活を送っている製鋼会社社長と、豪放でいて小心の市長との工場誘致をめぐるやりとりに、美人スパイとのラブ・ロマンスを絡ませて描く。川島雄三らしい個性的な人物の造型が楽しめる。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談新耳袋 劇場版", "怪談新耳袋 劇場版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "怪異蒐集家の木原浩勝と中山市朗が、本当にあった怖い話を全国から集めた怪談集。BS-Iでロングシリーズとして放送された後、映画化となった。「発狂する唇」の佐々木浩久や「明るい場所」の豊島圭介、「人造人間ハカイダー」の雨宮慶太ら７人の監督が登板、８篇の“怪談”が並ぶ。100話を語り終えると怪しきことが起こると伝えられる「百物語」の作法にのっとって99話まで製作が続けられるので、作品を見るごとに恐怖心が増す。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2010-2011 ヴェルディ「ドン・カルロ」", "METライブビューイング2010-2011 ヴェルディ「ドン・カルロ」", "", "", "http://image.eiga.k-img.com/images/movie/56469/315x210.jpg?1679", 259, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を劇場のスクリーンで上映する「METライブビューイング」2010～11年シーズンの4作目。16世紀、フランドル地方を支配しているスペイン。父であるスペイン国王フィリッポ2世の妃エリザベッタと愛し合うカルロは、フランドルを救うために立ち上がるが、父により捕らえられてしまう。エリザベッタもまた、胸に秘めたカルロへの愛を暴かれてしまい……。イタリア・オペラの雄、ベルディによる一大悲劇。", "2011-01-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WOMAN OF DESIRE", "デッドボディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "資産家の双子の兄弟、テッドとジョナサンは船長のジャックとテッドの恋人クリスティーナとともに豪華ヨットでクルージングに出かける。しかし、嵐に遭遇し、ヨットは難破。救助されたジャックは“テッドが嵐のなか、誤って海に転落した”と説明する。しかし、一命を取り止めたクリスティーナは“ジャックがテッドを射殺し、自分をレイプした”と証言する。“世界のセックスシンボル”とうたわれたB・デレクが15年ぶりに復活、パーフェクトな肉体で、男たちをほんろうする熟女を演じている。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間魚雷回天", "人間魚雷回天", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "回天特別攻撃隊長・津村敏行の手記を松林宗恵監督、岡田英次主演で映画化した戦争映画。戦争末期、戦局挽回のため水中特攻艇・回天に乗り込み、太平洋に散っていった男たちの短い生涯を描く。冷静沈着な朝倉少尉を演じる岡田英次が好演。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Abduction", "ミッシング ID", "", "", "http://image.eiga.k-img.com/images/movie/57229/315x210.jpg?1736", 106, "「トワイライト」シリーズのジェイコブ役でブレイクしたテイラー・ロートナー主演のアクションサスペンス。ごく普通の高校生ネイサンは、たまたま見た失踪者サイトに載せられた1枚の写真が幼いころの自分であることに気づく。現在の家族やこれまでの人生はすべて偽りだったことを知ってしまったネイサンだったが、それと同時に周囲で不穏な影がちらつきはじめ……。共演はフィル・コリンズの実娘で女優のリリー・コリンズ。監督は「ボーイズ’ン・ザ・フッド」「フォー・ブラザーズ　狼たちの誓い」のジョン・シングルトン。", "2012-06-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCANDALO", "スキャンダル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "第二次大戦下の南フランス・プロバンスの田舎町。大学教授の夫人で薬局を経営しているエリアーヌは、彼女の性的欲求不満を手玉に取る雑役夫アルマンのセックスの奴隷と化す。暗い時代を背景とした倒錯したエロチシズム。ストラーロのカメラが美しい。", "1976-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Faerie Tale Theatre", "フェアリーテール・シアター", "", "", "http://image.eiga.k-img.com/images/movie/53779/315x210.jpg?2890", 0, "古今東西の名作童話をティム・バートンをはじめとした豪華監督が映像化し、1982年から87年までアメリカで放送された伝説のコメディTVシリーズ。全26タイトルの中から、「ティム・バートンのアラジンと魔法のランプ」「フランシス・フォード・コッポラのリップ・ヴァン・ウィンクル」「ミック・ジャガーのナイチンゲール」「クリストファー・リーとフランク・ザッパのこわがることをおぼえようと旅に出た男」の4タイトルを上映する。", "2008-08-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER JOKER", "ジョーカー ～復讐の切り札～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ハンブルグのマンションの一室で、若い男女の射殺死体が発見された。遺体のそばには“ハート”のカードが……。殺人課のジャンは捜査を始めた矢先に爆破事件に遭い、両足の自由を奪われてしまう。車椅子を使うことを余儀なくされたジャンは殺し屋を雇い反撃に出る。そして“ジョーカー”のカードを残しながら敵を殺していくが……。", "1986-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOISES OFF", "カーテンコール　ただいま舞台は戦闘状態", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "1982年、英国で初演後、ローレンス・オリヴィエ賞など多くの賞に輝いた舞台劇『NOISES OFF』を映画化。華麗なショー・ビジネス界にうごめく人たちの悲喜劇をM・カプランがアメリカに舞台を移し脚色。M・ケインが主演したコメディ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばり姫初夢道中", "ひばり姫初夢道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "城主が老齢に達した青空城。権力に心を奪われた家老は、城主の養子の兄で痴呆症の大之亮をまつりあげ、実権を得ようとする。それを知った城主の娘・ひばり姫は……。大曾根辰夫が、会社側からようやくその実力を信頼され始めた中期の作品。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Windhorse", "風の馬", "", "", "http://image.eiga.k-img.com/images/movie/54299/315x210.jpg?3621", 97, "チベットの首都ラサには至る所に監視カメラが設置され、そこで暮らす住民に自由はない。ある日、デビュー間近の歌手のドルカは、ダライ・ラマの肖像の掲示禁止に反対した尼僧が投獄されたことを知る。残忍な拷問で変わり果てた尼僧はドルカの従妹のペマだった……。海外のメディアが入ることの難しいラサで、実話を基に撮影された意欲作。", "2009-04-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("舞台姐妹／ACTRESS SISTERS", "舞台姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "シェ・チン監督全盛期の作品。女性だけによって演じられる越劇の劇団を舞台に、そこに逃げ込んで来た藍春花という女と劇団の師匠の娘の人生航路を描いていく。ともに人気スターとなった二人だが次第に対照的な人生を歩み始め……。ロンドン映画祭ブリティッシュ・フィルム・インスティテュート年度賞、ポルトガル・フィゲイラフォシュ映画祭批評家賞受賞。", "1965-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O", "O〈オー〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "シェイクスピアの名作『オセロ』を、アメリカのハイスクールに置き換えて描く痛切な青春ドラマ。唯一の黒人で成績優秀な学生、オーディンはバスケットボール・チームの天才プレーヤー。同じチームのヒューゴは、コーチであるヒューゴの父からも寵愛を受けるオーディンに激しく嫉妬、オーディンを破滅させるために次々と罠を仕掛けていく。嫉妬が憎悪に変わり、そして起こる悲劇。J・ハートネットが愛に飢え苦悩する少年の複雑な心情を、抑えた演技ながらも静かな迫力で体現している。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAUT BAS FRAGILE", "パリでかくれんぼ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 169, "リベット作品の常連女優たちが終結、ミュージカル・シーンを交えて軽やかに綴られたパリ物語。バイク便に転職した元恐喝犯、5年間の昏睡から醒めたら家を相続していた娘、母親を探している図書室の司書、3人の娘たちの冒険が始まる……。歌手に扮したアンナ・カリーナの久々の映画出演も話題に。", "1996-12-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE MILLION YEARS B.C.", "恐竜100万年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「アルゴ探険隊の大冒険」のスタッフが、流麗なストップモーション・アニメを駆使して、紀元前100万年の恐竜たちの世界に挑んだ。のし歩く恐竜、飛ぶ翼竜をしのぐ、グラマラス美女、R・ウェルチの存在感にドッキリ。", "1966-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("White Jazz", "ホワイト・ジャズ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ジェームズ・エルロイ（「L.A.コンフィデンシャル」）原作の「暗黒のLA四部作」最終章を「NARC／ナーク」「スモーキン・エース／暗殺者がいっぱい」のジョー・カーナハン監督が映画化。1950年代のロサンゼルス。マフィア一家の家宅侵入事件を捜査することになった警官クラインは、一家の秘められた秘密に行き当たる。", "2012-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Matando Cabos", "カクタス・ジャック", "", "", "http://image.eiga.k-img.com/images/movie/33579/315x210.jpg?1442", 103, "メキシコで大ヒットを記録したクライム・アクション。街の権力者で、恋人の父親でもあるカボスの元を訪れたジャック。ところが彼の目の前で、カボスが気絶してしまう。ジャックは慌てて助けを呼びにいくが、その間にカボスに恨みを持つ清掃係のチーノが現われ、カボスのスーツや時計を奪って逃走。一方、チーノの息子ボッチャはカボスの誘拐を企んでおり……。監督・脚本はポスト・タランティーノとの呼び声高いアレファンドロ・ロサーノ。", "2006-07-29", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DODGE　CITY", "無法者の群", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "無法者がはびこるダッジシティを舞台にした、E・フリン＆O・デ・ハヴィランド主演、M・カーティス監督の活劇トリオで描いたウエスタン。女性記者からの懇願によって新保安官になったウェイドは、町から暴力を一掃し、犯人をリンチから守るために、よその町まで護送することになるが……。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真夜中まで", "真夜中まで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "才人、和田誠の監督第６作となった大人のムードあふれるサスペンス。ジャズ・クラブの人気トランペッター、守山は憧れのジャズ・ミュージシャンが今夜、店に遊びに来ることを知り、落ち着かない。ところがワケありのホステス、リンダを助けたことから災難が。彼女は殺人事件を目撃してしまい、犯人の汚職刑事コンビに命を狙われていた。彼女とともに逃げるハメになった守山は次のステージの時間を気にしつつ、刑事たちの犯行を証明すべく夜の街を駆ける……。午後10時35分から深夜０時15分までのドラマが、上映時間とシンクロするかたちで展開するという凝った作り。追いつ追われつのスリルはもちろん、軽妙なやりとりが醸し出すユーモアやハードボイルドな空気も大きな魅力となっている。ジャズ・ファンの和田監督らしく『ラウンド・ミッドナイト』などおなじみの曲が使用されているが、なかでもスウィング調にアレンジされた『月の砂漠』は印象深い。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殿さま弥次喜多 怪談道中", "殿さま弥次喜多 怪談道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "偶然知りあった町人・弥次、喜多とはかつて、相方の身分をとりかえて気まぐれ旅に出た尾州の若殿・宗長と紀州の若殿・義忠。ひょんなことから知りあった巡礼娘・お小夜から尾州国家老・坂崎大膳の大陰謀を聞かされて……。中村錦之助・賀津雄兄弟が息のあった若殿コンビを演じる鮮度のいいミュージカル調時代劇。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メロドラマ", "メロドラマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "かつて渡哲也主演の日活ニュー・アクション“無頼”シリーズでならした小沢啓一監督によるハードボイルド・ロマン。15年前の犯罪が時効寸前に迫っている、二人の男と一人の女。過去を捨てて明日に生きようとする３人に、犯罪組織の魔の手が伸びていく……。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SWEET HOUSE OF HORROR", "ルチオ・フルチのホラーハウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "イタリアを代表するホラー映画の巨匠ルチオ・フルチが描くスプラッタ・ホラー。子供二人を残して、強盗に惨殺された夫婦の亡霊が館に取り憑き、様々な怪奇現象を引き起こす。子供たちの面倒を見る友人夫婦が、館を売り払おうとするが、死霊の力によって妨害される。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flaklypa Grand Prix", "ピンチクリフ・グランプリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1975年の公開以来、ノルウェーで観客動員数第1位の記録を保ち続けている人形アニメ。ピンチクリフ村で暮らす自転車修理工レオドルが、助手のあひるソランやはりねずみのルドビグと共に、自作のスーパーカーでレースに挑戦する姿を描く。ユーモア溢れる物語に命を吹き込んだのは、家具職人だったイボ・カプリノ監督。5年の歳月をかけ、サーキットに集まる観衆やスーパーカーの部品に至るまで緻密に作り上げた。", "2007-02-03", "36", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARTY GIRL", "パーティーガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ニューヨークを舞台に、大人になりきれないヒロインの挫折と成長をキュートに描いた青春映画。マンハッタンのダウンタウンでパーティー生活に明け暮れる24歳のメリーは、母親代わりのジュディから勧められ図書館で働き始める。しかし彼女は仕事そっちのけで毎晩クラブ通い。ジュディから生活ぶりに対する小言を言われ、心を入れ替えて最高の図書館員になろうと仕事に精を出すが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・名もなく貧しく美しく 父と子", "続・名もなく貧しく美しく 父と子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "松山善三が再び、ろうあ者の世界を描いた、自らの処女作にして最高傑作の続編。今回は前作で死別した妻・秋子と道夫の間に生まれた一郎を主人公に、障害者を親に持ったために差別を受ける若者の苦悩と救済を描く。ろうあ者を単に社会の犠牲者としてだけでなく、そのエゴイズムまで描いている点が秀れている。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女が階段を上る時", "女が階段を上る時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "菊島隆三のオリジナルの脚本を演出した成瀬巳喜男監督の名作。夫の死後、生活のために高級バーの雇われマダムになった女と、その周囲に生きる人間たちの生態を描く。身体を張ってのし上がっていく同僚たちを尻目に、貧しくとも堅気の生活に戻りたいと願う主人公を高峰秀子が気高く演じた。男たちに思いを踏みにじられても涙を見せない凛とした姿が美しい。仲代達矢扮する若いマネージャーが捧げる真の愛情にゆらめきながらもふと思いとどまる一種の禁欲性に、成瀬メロドラマの真骨頂が見出せる。なお、題名は高峰の勤めるバーがビルの2階にあることに由来する。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀河鉄道の夜", "銀河鉄道の夜", "", "", "http://ecx.images-amazon.com/images/I/414ZKSHJBAL._SL210_.jpg", 107, "宮澤賢治の原作をマンガ化したますむらひろしの作品を原案に採用し、主な登場人物は猫に置きかえられて描かれた。監督は「まんが日本昔ばなし」「タッチ」の杉井ギサブロー。また詩的なそのイメージを広げるため、脚本を別役実、音楽を細野晴臣という第一級の作家、アーティストが手掛け、映像と音を物語の深淵へと導いている。結果的に原作はかなり忠実に映像化され、薄明の世界の中に繰り広げられる幻想の情景は、アニメで初めてここまで視覚化されたといっても過言ではない。ただその解釈をめぐって評価は分かれたが、劇場用アニメを大人たちにも注目させた傑作ファンタジーとして、忘れがたい好編となった。星祭りの夜、病気の母を持つ少年・ジョバンニは、丘の上に現れた不思議な汽車に乗り込んだ。すでに座席にいた親友のカンパネルラとともに、銀河の旅が始まり、白鳥座ステーションなど通る様々な場所で、二人は神秘的な体験を重ねる。やがてジョバンニとカンパネルラに、別れの時が訪れるが……。", "1985-07-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Contender", "ザ・コンテンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "政敵に容赦ないスキャンダル攻撃を浴びせられながらも、毅然とした態度で自分自身の信念を突き通す女性議員の潔い闘いを描いた人間ドラマ。副大統領候補に指名されたことから、過去のセックス・スキャンダルを暴かれ窮地に立たされるレイン。しかし、レインはあくまでスキャンダルに取り合わない姿勢を崩さず、聴聞会でもノーコメントを貫き通す。議会が、そして大統領がレインに下した判定とは？　卑劣な妨害工作を行うラニヨン議員を演じるのは、製作総指揮も兼ねたG・オールドマン。“カメレオン俳優”の名は、ここでも健在で、その見事な変貌ぶりに驚かされる。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIME BANDITS", "バンデットQ", "", "", "http://ecx.images-amazon.com/images/I/21C7JST2T1L._SL210_.jpg", 111, "T・ギリアム監督の四次元ファンタジー。現代に生きるイギリス少年がベッドサイドに現れた６人の小びとに誘われ、時空を超えた冒険に出かける。元ビートルズのG・ハリスンが主宰するハンドメイド・フィルムズ作品。", "1981-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("よなよなペンギン", "よなよなペンギン", "", "", "http://image.eiga.k-img.com/images/movie/53539/315x210.jpg?3063", 88, "「銀河鉄道999」「メトロポリス」のりんたろう監督が、「時をかける少女」などのアニメスタジオ、マッドハウスとタッグを組んだCGアニメーション。いつか空を飛ぶことを夢見て、ペンギンの着ぐるみコートに身を包み夜の街を歩き回っている少女ココ。ある晩、ペンギンのカプセルに導かれてゴブリンの少年チャーリーと出会った彼女は、闇の帝王ブッカブーに狙われたゴブリン村を救うため立ち上がる。", "2009-12-23", "76,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEX", "テックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「レベルポイント」でデビューし、一躍人気スターとなったM・ディロン主演の青春ドラマ。兄と二人暮らしのテックスは、真面目な兄とは正反対の自由な性格だ。ある日、テックスは一人のヒッチハイカーを乗せたところ、これが指名手配中の男で、彼は銃を突きつけられながらも冷静に対処し、男は警察に射殺された。この事件でテックスは町の人気者となり、離れ離れになっていた父がニュースを見て帰ってきた。テックスはしばしの幸せに浸っていたのだが、ある夜、父から自分の出生の秘密を聞いてしまう……。後のM・ディロンの代表作「アウトサイダー」「ランブルフィッシュ」などの原点ともいえる作品。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LEGEND　OF　THE　EVIL　LAKE", "千年湖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "湖の底に眠る千年の想い。戦乱の世に命懸けで愛し合う男女の哀しい運命。時空を超えて、壮大なスケールで描かれた究極のラブ・ストーリー。“韓国のヒュー・グラント”ともいわれる、「大変な結婚」のチョン・ジュノが知的で成熟した男の魅力を見せつけている。恋人との平穏な生活を望む将軍ビハラン。しかし命を狙われた恋人が、呪われた千年湖へ身を投げ、その封印を解いてしまい……。", "2005-08-06", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Election", "ハイスクール白書　優等生ギャルに気をつけろ！", "", "", "http://ecx.images-amazon.com/images/I/21RP4yoN9BL._SL210_.jpg", 103, "高校の生徒会長選挙をめぐって巻き起こる騒動を描いたブラックコメディ。ネブラスカ州のとある高校。上昇志向の強い優等生トレイシーは、生徒会長を目指して熱心な選挙活動を開始する。彼女には、かつて不倫相手の教師を退職に追いこんだ過去があり、そんなトレイシーに危機感を抱いた教師ジムは、当選を阻止するべくフットボール部の人気者ポールを対立候補に仕立てあげる。トレイシー役に「クルーエル・インテンションズ」のリース・ウィザースプーン。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Traumschiff Surprise Periode 1", "ドリームシップ　エピソード1/2", "", "", "http://image.eiga.k-img.com/images/movie/33639/315x210.jpg?2074", 87, "ドイツ本国で「ロード・オブ・ザ・リング」を抜いて歴代1位のオープニング成績を記録したSFコメディ。300年後の地球から人類滅亡の危機を救うために現代へやってきたオカマたちの活躍を、「スター・ウォーズ」等ヒット作のパロディ満載で描く。監督は、ドイツでは人気コメディアンとして知られるミヒャエル・ブリー・ヘルビヒ。出演は、「キング・アーサー」等ハリウッドでの活躍も目覚ましいティル・シュバイガーら。", "2006-09-09", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版BLEACH ブリーチ MEMORIES OF NOBODY", "劇場版BLEACH ブリーチ MEMORIES OF NOBODY", "", "", "http://image.eiga.k-img.com/images/movie/33769/315x210.jpg?3358", 93, "「週刊少年ジャンプ」で連載中の人気コミックをアニメ映画化。死神の女性・朽木ルキアとの出会いから死神の力を手に入れた高校生・黒崎一護の闘いを描く。現世・空座町に大量発生した認識不能の霊生物“欠魂”と、尸魂界の空に映し出された現世の景色。世界崩壊の前兆とされるこれらの出来事は、厳龍率いる闇の勢力・ダークワンが引き起こしたものだった。一護と死神たちは世界の崩壊を阻止するために立ち上がるが……。", "2006-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アバウト・ラブ／　関於愛〈クワァンユーアイ〉", "アバウト・ラブ／　関於愛〈クワァンユーアイ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "東京・上海・台北を舞台に、国境を越えた男女の交流を描く3話のオムニバス。「イノセント・ワールド」の下山天をはじめ、アジア各国の俊英たちが1話ずつを演出。言葉を超えたコミュニケーションの過程を丹念に見つめるドラマは、それぞれに味があり、胸に迫る。漫画家を目指し東京に暮らす台湾人留学生ヤオは、交差点で美智子とすれ違う。彼女は恋人と別れたばかりで……。(「東京編」)", "2005-09-17", "76,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("感染", "感染", "", "", "http://ecx.images-amazon.com/images/I/21ZH8JT64AL._SL210_.jpg", 98, "ジャパニーズ・ホラーの新レーベル“Jホラーシアター”の第１作となる、ウイルス感染の恐怖を描くホラー。経営危機に直面した古い病院を舞台に、医療事故をきっかけに感染が広がってしまう謎のウイルスの恐怖を描写。佐藤浩市や高嶋政伸ら実力派俳優が、瞬く間に広がっていく感染の恐怖を体現していく。経営危機ながら、多くの患者を抱える病院で医療事故が発生。医師たちは事故をもみ消そうとしていた。その時、謎の症状の急患が運ばれてくる。", "2004-10-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOTEL　CACTUS", "モーテルカクタス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "新鋭パク・キヨンが自由な発想で撮り上げた新感覚の韓国映画。ソウルの片隅に立つモーテルの407号室に出入りする男女の愛と別れを、４つのエピソードとして切り取っていく。艶めかしくうごめく登場人物の肉体を、ウォン・カーウァイ作品でおなじみのC・ドイルが幻惑的なカメラワークで捉えている。第１話でカップルを演じるジン・ヒギョンとチョン・ウソンは、ともに韓国で人気の若手スター。", "1997-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("書を捨てよ町へ出よう", "書を捨てよ町へ出よう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "演劇実験室“天井棧敷”を主宰した寺山修司は数々の実験映画を発表してきたが、初めて劇場用長編として製作したのが本作だ。原作は寺山の同名のエッセイ集と“天井棧敷”が上演したパフォーマンス。よって確固とした物語は存在せず、主人公の青年（寺山自身がモデルか）を中心とした世界が、現実・過去・幻想といった様々なイメージとなって描写される。いつも家出を考える“私”は、人工飛行機を飛ばす幻想を見ている。私はまた、自分が出演している映画の中の現実世界からの逃避を夢見ている。私はスクリーンから外界の観客に語りかけることで、映画という虚構の世界から現実へ出て行こうとする……。万引き癖の祖母、ウサギを偏愛する妹、痴漢癖のある中年男 —— 奇妙だがどこにでもいそうなキャラクターたちの、幻想と現実が交錯するシネ・エッセイ。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tomorrow Never Dies", "007 トゥモロー・ネバー・ダイ", "", "", "http://ecx.images-amazon.com/images/I/518rCnCNsWL._SL210_.jpg", 119, "", "1998-03-14", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虹色ハーモニー　マイ・レインボウ・マン TWILIGHT FILE IV", "虹色ハーモニー　マイ・レインボウ・マン TWILIGHT FILE IV", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "オムニバスシリーズ「TWILIGHT FILE」第4弾の1作。姉妹のような関係の母子と、余命半年の男の交流を描いたハートフル・ドラマ。小学6年生の佐伯小夜は、キャバクラで働く母親・小町と2人暮らし。ある日、小町の友達だという亮が2人の家を訪れ、そのまま居座ってしまう。自分は小夜の父親だと言い張る亮に振り回されてばかりの小町と小夜。しかし小夜は亮の優しさに少しずつ心を開いていく。そして町内の音楽発表会の日、亮は子どもたちに近づくナゾの男を見つけ……。", "2007-10-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピンチランナー", "ピンチランナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "アイドルグループ・モーニング娘。が主演し、話題を呼んだ青春映画。ある女子高校の陸上部に集まった少女たちが友情に結ばれ、駅伝大会に出場するまでを描く。メンバーのうち安倍、飯田、矢口、市井、後藤が選手、保田がマネージャー、中澤が彼女たちの姉御的存在のアンテナショップの店長を演じている。また、最後の駅伝シーンは、ひたちなか全国少女駅伝大会に５人が参加し、力走したドキュメンタリー映像が使われている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の世界", "男の世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "恋人の復讐をするためにカナダから暗黒街に復帰した一匹狼が、組織に戦いを挑む姿を描く裕次郎主演のアクション。恋人を組織のボス・白石に殺された紺野が、白石の出所と時を同じくして５年ぶりに帰国。刑事・茂木は紺野をマークする。一方、紺野は自分の店を守ってくれた阿川に再会し……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新篇 丹下左膳 隻眼の巻", "新篇 丹下左膳 隻眼の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "中川信夫が演出した、東宝版第３作。大河内伝次郎が日活作品に続き当たり役を演じている。原作者が林不忘となっていないのは、川口松太郎が林の遺族から権利を譲り受けたため。川口はこれを“新篇”と名付け、新聞に連載した。第１作で名誉を失い、第２作で右腕を斬られた左市が、本膳では右目を潰され、ここに御存知“丹下左膳”が誕生する。しかし、この東宝版の評判は悪く、この３作目が公開されたのは２作目から１年後であり、ついに５作目となるはずであった“完結篇”は製作されなかった。１作目は渡辺邦男、２作目は山本薩夫、４作目は萩原遼が監督したが、フィルム・センターに収蔵されたのは唯一中川作品のみである。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PEPPERMINT　CANDY", "ペパーミント・キャンディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "「グリーン・フィッシュ」のイ・チャンドン監督による、一人の男の人生を20年にわたって遡る社会派人間ドラマ。何もかも失ったキム・ヨンホがすべてを捨てる決心をした時、彼の脳裏には様々な思い出が蘇る。５年前、自ら放棄してしまった家庭。15年前、互いに思いを寄せながら別れを決意した初恋の人。一人の男の人生を通して、韓国の政治状況も冷静に見つめながら、選ばなかったことで失ってしまった大切な時間を一つ一つたどっていく。主演のソル・ギョングが、ある男の人生を20年にも渡って演じるという難役に挑み、圧倒的な演技力を見せつけている。第37回 韓国大鐘賞の主要５部門を独占受賞。", "1999-01-01", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("if　‥‥", "if もしも‥‥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "カンヌ国際映画祭でグランプリを受賞した異色の青春映画。イギリスの名門パブリック・スクール。厳しい校則や威張り散らす上級生たちに嫌気がさした主人公ミックは、仲間のジョニーとウォレス、さらに下級生の美少年やコーヒーショップで働く少女とともにある計画を企てる。後に「時計じかけのオレンジ」で大ブレイクした怪優マルコム・マクダウェルがアナーキーな反逆児ミックを演じ、センセーショナルな映画デビューを果たした。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOHNNY 100 PESOS", "独りぼっちのジョニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "実話に基づく異色のバイオレンス・アクション。朝のバスの中、乗客に向かって銃を突きつけた少年ジョニーは、逃げるように街角のビデオショップへ。そこは非合法の外貨両替商であり、ジョニーは４人の仲間とその金を強奪するために押し入ったのだ。しかし、またたく間に周囲を包囲され、ジョニーたちはビデオショップの店長とその愛人グロリアらを人質にろう城するが……。ビデオ発売時タイトルは「明日なき狼たち」。", "1993-01-01", "1,77,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神さまこんにちは", "神さまこんにちは", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "韓国を代表する鬼才ペ・チャンホが描く、心温まるロード・ムービー。小学３年生の時から、急に体が動かなくなった青年ピョンテ。彼には、慶州の高台に登り、星の絵を描くという夢があり、それを果たすために旅に出る。途中、放浪詩人のミンウや、妊婦のチュンジャが旅の道連れになるが……。心に傷を持つ３人が助け合う姿が感動的だ。", "1987-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PECKER", "I love ペッカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「ピンク・フラミンゴ」などの奇才J・ウォーターズが、カメラ小僧の青春を自伝的要素を込めて描くコメディ。ボルチモアの高校生ペッカーは何よりも写真を撮るのが大好き。カメラを片時も離さず、異常に仕事熱心なガールフレンドや町の人々を撮りまくっている。そんな彼の写真がアート・ディーラーの目に止まり、ニューヨークで個展を開くことになる。成功を手に入れ、一躍ときの人となったペッカーだが、そのため自由に写真を撮ることができなくなり……。シリアスな役が多かったE・ファーロングが純粋なカメラ小僧をはつらつと演じ、新たな魅力を発揮。セックスや暴力シーンは一切ないがR−15に指定されている。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fool for Love", "フール・フォア・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "　ニューメキシコ州モハベ砂漠に立つモーテルでひっそりと暮らす女メイ。そんな彼女のもとに、カウボーイ姿の男エディが現れる。愛とともに憎悪をぶつけ合うエディとメイ。やがてキャンピング・カーに住む年老いた男から明かされるふたりの複雑な関係。男はエディとメイの父親であり、ふたりは異母兄妹だったのだ……。忌まわしい関係から抜け出そうとする兄と妹の愛憎劇。サム・シェパードが自らの戯曲映画化にアルトマン監督を指名し、そのアルトマンがシェパードを主演に逆指名したという意欲作。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIRD OF PARADISE", "南海の劫火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "文明国の見学を終え、故郷の南の島に帰って来た混血のポリネシア人テンガ。彼とともに島にやって来た白人のアンドレは酋長の歓迎を受け、その娘のカルアに心惹かれていくが……。「折れた矢」「避暑地の出来事」のD・デイヴィス監督が描く南海のロマンス。D・パジェットのエキゾチックな美しさも必見。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Double-D Avenger", "ダブルD・アベンジャー", "", "", "http://ecx.images-amazon.com/images/I/513SBR96HGL._SL210_.jpg", 77, "「ウルトラ・ヴィクセン」のキトゥン・ナティビダッド、「ファスター・プッシーキャット　キル！キル！」のハジら、セックス＆バイオレンス映画の巨匠ラス・メイヤー作品に華を添えた巨乳女優たちが集結したB級アクション。超人的な絶倫パワーを手に入れたヒロインが、「メガパイレンジャー」に変身して悪に立ちむかう姿を描く。メガホンを取るのはラス・メイヤーの熱狂的なファンである新人ウィリアム・ウィンクラー。みうらじゅんが字幕監修を担当している。", "2006-02-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLENN　GOULD　OFF　THE　RECORD　／ON　THE　RECORD", "グレン・グールド／27歳の記憶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "『ゴールドベルグ変奏曲』を始めとする名演の数々で、いまもなお熱狂的な支持者を生み続けている孤高のピアニスト、故グレン・グールドの若き日の姿を収めたドキュメンタリー。かつて原題通りのタイトルでビデオ発売されていたものを、日本では1999年に初めて劇場公開した。原題が示しているように作品は二部構成。前半の「オフ・ザ・レコード」では、故郷トロントの別荘でくつろぎ、友人相手に音楽家としての本音を語る姿を捉え、後半の「オン・ザ・レコード」ではニューヨークのスタジオで『イタリア協奏曲』の録音に没頭する様が写し出されていく。その後、コンサート活動を完全に停止し、レコーディングと映像制作参画によって独自の音楽表現を切り開いていった鬼才の一貫した姿勢に胸打たれる中編だ。", "1959-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルー＝ガルー", "ルー＝ガルー", "", "", "http://image.eiga.k-img.com/images/movie/55059/315x210.jpg?2081", 99, "京極夏彦のSF小説を原作とした樋口彰彦のコミックをアニメ映画化。TVアニメ「攻殻機動隊 STAND ALONE COMPLEX」の脚本を手がけた藤咲淳一が監督を務める。舞台は近未来の日本。全てを情報端末で監視された社会で生きていた子どもたちは、学校に行くことなく家庭の端末で学習を行うため、他者と物理的に交流することがほとんどなかった。しかし、少女ばかりを狙う連続殺人事件をきっかけに、4人の少女たちが閉鎖された世界に立ち向かう。", "2010-08-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MURDERS IN THE RUE MORGUE", "モルグ街の殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "パリのモルグ街にある屋敷で残忍な殺人事件が発生。しかし死体の見つかった部屋は、内側から鍵がかかった密室状態だったため捜査は暗礁に……。やがて第２、第３の犠牲者が出るに及んで、警察は引退した元名警視デュパンに協力を要請する。職人Y・シュワルツがポーの名作を現代風にリメイク。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAREWELL MOSCOW", "さらばモスクワ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "舞台は、1970年の反ユダヤ主義運動が激化するモスクワ。移民を許されずに、精神病院から労働キャンプに収容され、あげくに追放の身となってしまうユダヤ人女性の苦悩をリアルに描き出した力作。社会派の名匠ボロニーニがソ連のユダヤ人問題に真っ向から取り組んだ異色作である。", "1986-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUL MAN", "ミスター・ソウルマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "金にも女にも不自由しないマークはリッチな若者。憧れの名門ハーバード大学に合格するまではよかったが、両親が突然、“今後いっさい経済的援助をしない”と宣言。さて困った彼が考えた手段とは？　C・トーマス・ハウエルの変身術とソウル・サウンドが楽しいコメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOPE FLOATS", "微笑みをもう一度", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "S・ブロックが長編としては初の製作総指揮に挑み、主演を兼ねた愛のドラマ。TVの視聴者番組の生放送中に、夫と友人の不倫を知らされたバーディは大ショック。娘を連れて故郷に戻った彼女だったが、ハイスクールの女王だった過去と、みじめな現在の落差にめげるばかり。母親に勧められて再会した同級生と心を通わせていくものの、新たな恋に踏み切る勇気もない。そんなバーディに、娘が反抗心を募らせ……。愛をなくした女性の心の再生を、ユーモラスに描出。田舎町ののどかな風景がオフビート感を醸し出し、心地よいドラマを演出している。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Natural", "ナチュラル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "天才的な野球の才能に恵まれてプロ球団入りを決意したものの、ある女性の凶弾に倒れて道を閉ざされ、その後35才のルーキーとして復活を遂げたロイ・ホッブス。彼の半生を、1930年代の古き良きアメリカを舞台に描く。バーナード・マラマッドによる原作小説を、「ダイナー」のバリー・レビンソン監督が映画化した感動作。苦境に立たされながらも自分の信念を貫く主人公ロイを、ロバート・レッドフォードがさわやかに演じる。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新兵隊やくざ", "新兵隊やくざ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "脱走を繰り返し天津に辿り着いた大宮と有田が、女たちを集めて女郎屋を開くという痛快作。藤岡琢也、玉川良一など笑いの取れる役者を脇に配し、喜劇部分をクローズアップ。「兵隊やくざ・大脱走」で再び同じ役を演じる成田三樹夫が本作で登場。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Buffalo Bill and the Indians, or Sitting Bull's History Lesson", "ビッグ・アメリカン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "　1885年、西部開拓時代が終わりを告げようとしていた頃。ワイルド・ウェスト・ショーの一座を率いるバッファロー・ビルは優れたショーマンとして成功を収めていた。しかし彼は作家のネッドとスターの存在感を備えたスー族の族長ブルを疎ましく思うようになる。様々な要求をするようになったブルはある時、ビルの前から姿をくらましてしまうが……。実在したバッファロー・ビルの後半生をシニカルに描いた西部劇。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KAZUO OHNO", "ダニエル・シュミットの大野一雄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 15, "舞踏家として独特な世界を作る大野一雄の舞う姿を、スイス人監督のD・シュミットが収めた短編作品。大野のヨーロッパ公演を見たD・シュミットが日本での大野の舞踏を記録することを希望、R・ベルタとともに来日し、撮影した。レインボーブリッジをバックに晴海埠頭で踊るシーンは素晴らしい美しさである。", "1995-01-01", "2,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("必殺仕掛人", "必殺仕掛人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "原作の第１作『おんなごろし』の映画化。梅安は仕掛人の元締め、音羽屋から殺しを頼まれるが、その中には幼い頃、生き別れになった妹・お吉の名があった……。田宮二郎がニヒルな殺し屋として登場し、TVとは違った梅安を作り出している。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ghosts of Mars", "ゴースト・オブ・マーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　2176年、火星の植民地。MPF（火星警察）の警部補メラニーは囚人ジェームズの護送任務のため、鉄道でシャイニング・キャニオンと呼ばれる町に向かう。しかしそこは火星の先住民族の霊によって壊滅状態に。町の住人たちは霊に憑依され、殺戮を繰り返していた。そしてその集団は警察官たちにも襲いかかる。メラニーは地獄と化した一帯から脱出すべく、ジェームズら犯罪者と手を組んで壮絶な肉弾戦を繰り広げることに……。火星の死闘を描いたSFバトルアクション。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Trou", "穴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "ジョゼ・ジョバンニが自らの脱獄体験に基づいて書いたノンフィクション小説の映画化で、「現金に手を出すな」「モンパルナスの灯」の名匠ジャック・ベッケル監督の遺作となった作品。ラ・サンテ刑務所の一室に閉じ込められた4人の男たち。彼らは床に穴を掘って脱獄しようと、細心の注意を払って綿密な計画を練り上げる。そこに入ってきた新たな服役囚。迷った挙句、彼らは新入りに計画を打ち明け、5人で協力して実行するのだが……。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PROWLER", "ローズマリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "1945年６月、ブリッチャー女学校の卒業パーティーで一組のカップルが何者かに惨殺された。そして35年ぶりに復活したパーティーの夜、軍服を着た謎の男により再び血の惨劇が繰り広げられる。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("路地裏のコッペリウス", "路地裏のコッペリウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "Vシネマ「真田くノ一忍法伝」シリーズのかわさきひろゆき監督が、3組の母と娘の物語をつむいだオムニバス。売れない小説家・日暮の前に現れた目の見えない少女・織絵と、彼女の周りの不思議な人々の幻想を描く「織絵編」、廃屋で2体の人形と暮らし、人には見えないものが見える女・季江の過去が暴かれていく「季江編」、母・伸子の自殺の真相を探る佳代が季江と出会い、母との悲しい行き違いが浮き彫りになる「伸子編」の3編。", "2011-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Jaguar", "ジャガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「3人の逃亡者」のフランシス・ベベール監督によるコミカルなアドベンチャー作品。熱帯雨林の保護を訴えるためにフランスへやって来たアマゾンの霊媒師ワヌと通訳カンパナ。ワヌはなぜか、借金を抱えるいい加減な男ペランにつきまとう。そんな中、突然病に倒れたワヌは、自分の魂を取り戻すことができるのはペランしかいないと言い出し、彼をアマゾンへ連れて行くが……。アマゾンで育った野生児カンパナをジャン・レノが演じる。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やぎの冒険", "やぎの冒険", "", "", "http://image.eiga.k-img.com/images/movie/55919/315x210.jpg?3720", 84, "沖縄の中学生、14歳の仲村颯悟による長編監督デビュー作。那覇に暮らす小学6年生の裕人は、母の田舎の今帰仁村で冬休みを過ごすことにする。大自然のなかで優しい祖父母と従兄弟、2匹の子やぎ・ポチとシロと楽しい時間を過ごしていたが、ある日、ポチが地元の人々に食用のために殺される姿を見て大きなショックを受ける。そんなとき、売りに出されそうになったシロが逃げ出して村は大騒ぎになる。", "2011-01-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUTT　BOY", "トンケの蒼い空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "社会に適応できない危ういまでに無垢な青年の心にしみる青春物語。「私の頭の中の消しゴム」の演技派俳優チョン・ウソンが、これまでのヒーロー的イメージを覆して、目的のない人生を孤独に過ごす青年を好演し、一見ダメ男が持つ熱い魂を観る者に伝える。父と二人きりで暮らすチョルミンは、皆からトンケ＝野良犬と呼ばれている。高校を中退し、無目的に過ごすトンケだったが……。", "2005-11-12", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　TEABAEK　MOUNTAINS", "太白山脈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "「風の丘を越えて～西便制」のイム・グォンテクが、朝鮮分断の悲劇と激動の時代を生き抜いた人々の姿を浮き彫りにした一大叙事詩。1945年８月、左翼と右翼の対立に揺れる朝鮮半島。南朝鮮労働党道党所属のチョンは幼なじみのソフィアの家に身を隠すうち、二人の間に身分を越えた愛が芽生える。一方、同族同士のいさかいに胸を痛めていた中学教師のキムは、両方を中立な立場から批判し“アカ”のレッテルを貼られ、闇討ちに合ってしまう。原作は、チョ・ジョンレの同名小説。", "1994-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHTMARE", "友引忌〈ともびき〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「ボイス」で名を上げた韓国のA・ビョンギ監督がその一つ前に発表したホラー映画。大学卒業から２年後、自殺したサークル仲間の女性の怨霊らしき影につきまとわれる男女の恐怖を描く。凝った照明やカメラアングルで不気味なムードを盛り上げ、これでもかとエグいショック・シーンを連打。「ボイス」のハ・ジウォンが女幽霊を怪演している。大学時代のサークル仲間ソネと再会したヘジンは、ソネが何かに怯えている様子に気付く。ソネは、学生時代に自殺した共通の友人ギョンアがつきまとっていると告白する。ヘジンは当初、そのことを気にも留めなかったが……。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWITCH", "スウィッチ ボクと彼女の関係は…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "女たちの恨みを買って殺されてしまったプレイボーイのスティーブは、神から“本当にお前を愛する女を見つけたら天国へ入れてやろう”と告げられ、地上に送り返される。だが地上に戻った彼は、悪魔の陰謀によって姿を女に変えられていた……。悪女役の多かったバーキンがコメディに挑戦。外見は女性だが中身は男という難役を見事にこなす。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PILLOW BOOK", "ピーター・グリーナウェイの枕草子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "若い頃に清少納言の随筆を読み、深い感銘を受けたというP・グリーナウェイ監督が、満を持して取り組んだ現代版『枕草子』。京都の旧家に生まれたナギコが、美しいファッション・モデルに成長する。両親の影響で書と文学に興味を抱くナギコは、ジェロームというイギリス人の理想の恋人と出会い、至福の時を過ごすが……。ヒロインが人間の肉体に筆を走らせ、愛と復讐のドラマを織り成していくという着想がユニーク極まりない異色作。シーンによってスクリーン・サイズを変えたり、画面内に別の小さなフレームを挿入するなど、グリーナウェイらしい革新的な映像作りが随所に見られる。また語学力などの問題で適役の日本人女優が見つからず、中国人のV・ウーを主役に抜擢。後に「トレインスポッティング」で一躍スターとなるジェローム役のE・マクレガーが、たどたどしい日本語とオールヌードを披露している。", "1996-01-01", "3,2,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("艶恋師（いろこいし）", "艶恋師（いろこいし）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "「夜王」の倉科遼原作による同名コミックを映画化した官能ロマン。神楽坂の花街で、芸者の私生児として生まれた菊之介。18歳の時、芸者の牡丹の手ほどきにより“男”となった菊之介は、不幸な女性の身と心を救う“竿師”として生きていくことを決意する。ある日、そんな菊之介の前に孤独なキャバクラ嬢・奈美が現われる。奈美を絶頂へ導くことができず敗北感に打ちのめされた菊之介は、一流の竿師となるため旅に出るが……。", "2007-07-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Angus, Thongs and Perfect Snogging", "ジョージアの日記　ゆーうつでキラキラな毎日", "", "", "http://image.eiga.k-img.com/images/movie/53759/315x210.jpg?3333", 100, "恋や友情に悩める中学生ジョージア・ニコルソンの日常を描くルイーズ・レニソンのベストセラー「ジョージアの青春日記」を、「ベッカムに恋して」のグリンダ・チャーダ監督が映画化。イギリス、ブライトンの街に暮らすジョージアは、大きな鼻がコンプレックスで、まだキスもしたことがない14歳の女の子。そんなある日、ハンサムな転校生ロビーに一目惚れしたジョージアは、なんとか彼を振り向かせようと、親友ジェスらとともにあれこれ作戦を練るが……。", "2008-11-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("体験旅行", "体験旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "車掌の大作は勤務中車内に現れたコブラに噛みつかれ卒倒して、彼の助手の姉・令子に介抱される。気がついた大作は、とたんに彼女に一目惚れ……。フランキー堺の純情失恋物語。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUS LES TOITS DE PARIS", "巴里の屋根の下", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "クレール監督がトーキーに取り組んだ第１回作品。パリの下町、大道の艶歌師アルベールが歌声を響かせ、相棒が譜面を売り歩く。パリの庶民たちのほのぼのとした感情を伝えるクレールの演出も冴え、タイトルと同名のテーマ曲も効果的に使われている。", "1930-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浪人街の顔役", "浪人街の顔役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "正義の剣が放たれる、アクション時代劇。浪人仲間の妹を救うため、周防屋に乗り込んだ九十九半兵衛だったが、周防屋をはじめ般若一家、老中の水野の側用人、塚本の怒りを買うことになる。３人はグルになり、私利私欲のために町人を苦しめ始めた。半兵衛たち浪人はついに怒りに燃え立ち上がる。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ともだち", "ともだち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "気管支喘息にかかった少女を励ます少年の姿を通じて、現代社会で忘れられがちな人間の優しさ、思いやりを描く日活児童映画第２作。公害が最大の社会問題として取り上げられていた当時の世相がうかがえる。話の分かる“お兄さん”役で出演している松田優作に注目。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEATHERS", "ヘザース ベロニカの熱い日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "“ヘザース”という特権グループの存在により、ウッ屈した学園生活を送っている17歳のヒロイン、ベロニカ。だが反逆的な転校生J.D.の登場で、彼女は復讐を開始。ところがJ.D.の無軌道ぶりはベロニカの想像をはるかに超えており、いとも簡単にヘザースの面々を殺してしまうのだった……。ティーンたちの学校内部での死をめぐる先生や学生たち、さらにメディア側の反応をアイロニカルな笑いを込めて描くティーンエイジ・ブラック・コメディ。ダークな題材をポップにレイアウトした、この監督第１作で注目を集めたM・レーマンは、この後も「アップルゲイツ」など、アメリカ郊外を舞台にした異常な物語を連発していく。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EAST OF JAVA", "ジャワの東", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "地上最大の火山クラカトア島へ冒険に行くため、汽船バタビア・クイーン号に乗り込んだ冒険家たち。目的は乗客の一人の行方不明になった夫を捜し出すことと、高価な宝物の入った箱を捜し出すことだ。船長の的確な指示のもと、バラバラの冒険家たちは一致団結して宝物を見つけ出す。海洋活劇の古典。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　DEVIL’S　BACKBONE", "デビルズ・バックボーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "メキシコ出身の気鋭G・D・トロが、スペインに招かれて撮ったホラー映画。荒野の孤児院に出没する少年の幽霊をめぐる謎を、ダークな映像美でじっくりと語り明かす。「オープン・ユア・アイズ」のE・ノリエガが挙動不審な用務員に扮し、邪悪な存在感を放っているのも見ものだ。1930年代のスペイン内戦時代。両親を失ったカルロス少年は、サンタ・ルチア孤児院に預けられる。彼は、気性の激しい管理人のハチントや、同世代の仲間とも馴染めない日々を送るうち、奇妙な現象に悩まされはじめ……。", "2001-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHUCK＆BUCK", "チャック&バック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "27歳のバックは、母親の葬式で幼なじみのチャックに十数年振りに再会し大喜び。チャック会いたさに彼の住むロサンゼルスへ移り、彼に付きまとうバック。そんな彼を避けるチャック。次第にストーカーまがいの行動をとるバックは、子供時代の二人を芝居にして上演すると言うのだが……。オトナになれない男を描いた、痛くて切ない青春ドラマ。メインの登場人物を演じたのは現役の脚本家やプロデューサーたち。職業俳優の面々を使わず、寓話的なリアリズムを醸し出したこの作品は、2000年のシアトル国際映画最優秀脚本賞ほかアメリカ国内で数々の賞を受賞。脚本も担当した主人公バック役のマイク・ホワイトも本職はTVドラマの著名なプロデューサー。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Mighty Heart", "マイティ・ハート　愛と絆", "", "", "http://image.eiga.k-img.com/images/movie/1969/315x210.jpg?2567", 108, "02年にパキスタンで実際に起きた誘拐事件をアンジェリーナ・ジョリー主演、ブラッド・ピット製作で映画化。監督は「グアンタナモ、僕達が見た真実」で06年ベルリン映画祭銀熊賞を受賞したマイケル・ウィンターボトム。パキスタン最大の都市カラチでテロリストを取材中のウォール・ストリート・ジャーナル紙の記者ダニエル・パールが、何者かに誘拐される。彼の妻マリアンヌは妊娠5カ月の身重ながらも、夫の無事を祈り、必死の捜索にあたるが……。", "2007-11-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCHOOL TIES", "青春の輝き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "フットボールの腕を認められて、伝統校に転校した高校生のデヴィッド。持ち前の明るさで学校中の人気者となる彼は、自分がユダヤ人であることを隠し続けるが……。1950年代の封建的なアメリカを舞台に描かれる、辛口の青春ドラマ。B・フレイザー、M・デイモンをはじめとする、当時の若手スターの共演もお楽しみ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄土地", "黄色い大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "日中戦争の始まった1936年、中国中央部・陝西省を舞台に、八路軍の兵士と貧農の娘の淡い恋と農民たちの苛酷な生活ぶりを描くチェン・カイコー監督のデビュー作。荒涼とした大地の風景の中にこだまする少女の唄声、全編が素朴で哀切きわまりない古謡に彩られ、心洗われる映像詩に仕上がっている。85年金鶏賞最優秀撮影賞、ロカルノ映画祭銀賞を受賞し、広く海外でも名を知られる作品となった。", "1984-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NATIVE SON", "ネイティブ・サン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "黒人青年ビガーは、誤って自分の主人である裕福な白人家庭の令嬢を殺してしまう。証拠隠滅、発覚、そして息詰まるような逃亡と追跡……。恐怖、憎悪、差別の果てに彼がいき着いた先はどこか？　俳優陣の見事な演技と緻密な演出が、最後まで緊張感を持たせる。原作はベストセラー『アメリカの息子』。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四月怪談", "四月怪談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "８ミリ映画出身の小中和哉が、16ミリ作品「星空のむこうの国」に続いて初めて35ミリに挑戦したSFファンタジー。“特撮に頼らないSFを撮りたかった”ということで、人気少女マンガ家・大島弓子の原作を時にコミカルに、時にリリカルに描いている。生命の素晴らしさを素直に訴えかける作品は近年珍しく、さわやかさに好感が持てる。国下初子はふとした事故から自分が死んだと勝手に思い込み、魂が肉体から離れてしまう。青年幽霊の弦之丞は早く初子を生き返らせようと説得するが、自分の状況を楽しんでいる初子はなかなか戻ろうとしない……。公開時には中高生の共感を呼んでヒットした心優しい物語。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("餌食", "餌食", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "昔の同棲相手が芸能プロの社長によってヘロイン中毒にされたのを知ったアメリカ帰りのロック歌手。彼は、転がり込んだ先のアパートの住人から歩兵銃を借り、ヘロイン取引現場へと乗り込んでいく。ロック歌手・内田裕也を主人公に、P・トッシュやマトゥンピなどのレゲエのリズムでまとめた佳品。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMAZON WOMEN ON THE MOON", "アメリカン・パロディ・シアター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「ケンタッキー・フライド・ムービー」から10年。“パロディーの王様”J・ランディス監督が、J・ダンテほかの気鋭監督と組んで手掛けた、全20話からなるパロディー・オムニバス映画。とにかく、全編にあふれるギャグ、ギャグ、ギャグ……で、終始抱腹絶倒。S・グッテンバーグ、R・アークエットら、有名人気スターの勢ぞろいも楽しみの一つだ。 第１話「モンドー・コンドー」は、冷えたビールの爆発から始まって、次々とトラブルが発生し、ついにマンションの窓から転落死する男の話。第２話「ペットハウス・ビデオ」は、雑誌“ペットハウス”のセンター・フォールドを飾るタリン・スティールのとんでもない“素顔”を追跡。お次は……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Une Aventure", "情痴　アヴァンチュール", "", "", "http://image.eiga.k-img.com/images/movie/33909/315x210.jpg?1269", 107, "パリの街を舞台に、夢遊病の女性と彼女に魅了された男性との愛の顛末をサスペンスフルに描く官能ロマンス。パリのアパルトマンで恋人との新生活をスタートさせたジュリアンは、ある晩、道路を裸足でさまよい歩く女性を見かける。向かいのアパルトマンに住むその女性ガブリエルが夢遊病者であることを知った彼は、不安定な言動を繰り返す彼女を救おうとするが……。ヒロインを演じるのは「スイミング・プール」のリュディビーヌ・サニエ。", "2007-03-31", "2,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猛吹雪の死闘", "猛吹雪の死闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "カルトな人気を誇る石井輝男監督の初期作品。雪山を舞台に強盗犯と山岳ガイドの死闘を描く、スリリングなアクション快作だ。雪崩でフィアンセの千代子を死なせてしまった過去を持つ山岳ガイドの五郎。ある日、彼の前に千代子そっくりの女性、昭子が３人の男とともに現れ、五郎に山越えのガイドを頼む。だが、３人の男は逃亡中の宝石強盗で、昭子は人質だったのだ！強盗団の一人を演じた菅原文太の若さあふれる悪役ぶりも見もの。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tabunfire", "ロケットマン！", "", "", "http://image.eiga.k-img.com/images/movie/34229/315x210.jpg?1591", 103, "「マッハ！」「トム・ヤム・クン！」の製作陣による、タイ発のヒーロー・アクション。1920年代のタイ農耕地帯に、ロケット弾と自らの身体を武器に牛泥棒を退治する謎の男、ロケットマンがいた。幼い頃、両親を牛泥棒に殺され復讐を胸に誓っていた彼は、ある日ついにその犯人を発見する。しかしその男は強大な力を持つ妖術使いだった……。「七人のマッハ!!!!!!!」のダン・チューポンがロケットマンをスタントなしで熱演。", "2007-10-06", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わんぱく王子の大蛇退治", "わんぱく王子の大蛇退治", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "東映長編動画を代表する傑作であると同時に、日本のアニメ史上に残る記念碑的作品。森康二を東映動画初の作画監督に据え、そのシンプルで量感に満ちたキャラクターを平面的に表現。これに対応して背景を一種デザイン的に処理することで、日本の神話をまったく新しいイメージとともに再生することに成功している。イザナギ、イザナミの国生みに始まるスサノオの命の物語を、純粋なファンタジーへと結晶化させた力量は、スタッフが最も充実した当時の東映動画でのみ、可能であったといえるだろう。「ゴジラ」の映画音楽で知られる伊福部昭のダイナミックなサウンドを背景に、天早駒に乗ったスサノオが、大蛇を倒していく空中戦の壮絶さ。そのスピード感とスリルは、今なお、少しも色あせてはいない。黄泉の国へ去った母のイザナミを追って、海へと船出した少年・スサノオ。兄のいる夜のオス国を通り、火の神と戦い、高天原で大騒ぎを巻き起こしたスサノオは、出雲の国で出会ったクシナダ姫を救うため、八叉の大蛇と戦う決心をする。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTENT TO KILL", "地獄の女スーパーコップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ポルノから一般映画への転身を果たしたセクシー女優T・ローズが、女刑事に扮してハードな魅力を見せるアクション。おとり捜査官のビッキーは、麻薬組織から大量のコカインの押収に成功。だが、組織は分署を襲撃し、それを奪い返す。仲間を殺された彼女は復讐を胸に秘め、敵のアジトに潜入するが……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅蝙蝠", "紅蝙蝠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "上方役者の中村紫近はひいき客の愛妾と密会しているところを見つかり、花札勝負でことの始末をつけることになる。負けた方は質屋・分銅屋の南蛮時計を盗んでこなければならない。いかさまにより敗れた紫近は分銅屋に忍び込むが、そこには主人の死体が……。巨匠・衣笠貞之助が描く時代ミステリーの快作。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Princess Kaiulani", "プリンセス・カイウラニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "", "2012-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アニと僕の夫婦喧嘩", "アニと僕の夫婦喧嘩", "", "", "http://image.eiga.k-img.com/images/movie/54499/315x210.jpg?1629", 80, "若者たちが落語を通して成長していく様子を、ミュージカル「テニスの王子様」の八神蓮、滝口幸広らイケメン俳優共演で描いたハートウォーミングドラマ。大学に入ったばかりの亮は、落語研究会のたった1人の部員アニに強引にスカウトされ、落研に入部することに。はじめのうちは破天荒なアニの行動に戸惑うばかりの亮だったが、徐々に落語の世界にのめりこんでいく。そんな中、大学対抗の落語コンクールにアニが出場することになり……。", "2009-04-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("里山っ子たち", "里山っ子たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「子どもは危なくないと育たない」という理念のもと、里山での保育を実践している千葉県木更津市・社会館保育園の園児たちを、2006年11月から1年半にわたって追いつづけたドキュメンタリー。自然の中で泥まみれになって植物や生物と触れあい、時には怪我やケンカも経験しながらたくましく成長していく子どもたちの姿を見つめていく。監督は、「いのち耕す人々」などのドキュメンタリー作家・原村政樹。", "2009-01-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLESS　THE　CHILD", "ブレス・ザ・チャイルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ニューヨークの病院で看護婦として働くマギーは、消息不明の妹の子供コーディを一人で育てるシングル・マザー。少年少女の連続誘拐事件が多発する中、事件の背後に潜むカルト教団の魔の手がコーディに忍び寄る……。「L.A.コンフィデンシャル」のK・ベイシンガーが、悪の力に立ち向かう女性を熱演するオカルト・サスペンス。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA RACE DES “SEIGNEURS”", "個人生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "大臣就任を狙う若手政治家が、偶然知りあったファッション・モデル、クリージーと不倫の恋に落ち、政治的駆け引きと家庭と愛人の間、公人と私人の間でジレンマに立たされるメロドラマ。時間を交錯させたやや幻想的な作りになっている。", "1974-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KOZIAT ROG", "炎のマリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "トルコの圧政下にあった17世紀のブルガリア。４人のトルコ人に、妻を犯されたうえ殺されたイワンは、娘マリアを連れて山にこもり復讐を誓う。男として育てられたマリアの苦しみと激しい恋を描く。ヨーロッパでの公開時に、人間の憎しみと悲しみを衝撃的に描き出したと評価の高かった作品。", "1972-01-01", "65", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博命單刀奪命槍／ODD COUPLE", "燃えよデブゴン 豚だカップル拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "製作、脚本、監督、武術指導など、活躍中のサモ・ハン・キンポーが、その巨体に似合わぬスピーディーなアクションを披露するカンフー映画。槍の達人に弟子入りしたデブゴンと、刀の名人の門下に入ったガッツは、師匠を殺されてしまい復讐に立ち上がる。", "1978-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Annie Leibovitz: Life Through A Lens", "アニー・リーボヴィッツ　レンズの向こうの人生", "", "", "http://image.eiga.k-img.com/images/movie/53329/315x210.jpg?4002", 83, "ジョン・レノンが凶弾に倒れる数時間前に撮影されたポートレートや妊娠中のデミ・ムーアのヌード写真をはじめ、世界中のセレブリティをカメラに収め続けてきた女性写真家アニー・リーボビッツ。彼女の妹であるバーバラ・リーボビッツがメガホンを取り、その活動と素顔に迫ったドキュメンタリー。撮影現場の様子や被写体となった人々へのインタビュー、さらにこれまでの人生を振り返り、ひとりの女性としてのアニーの生き方を浮き彫りにする。", "2008-02-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOODBYE GIRL", "グッバイガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "いつも男にグッバイされてばかりいる子連れの女性が、ついに逃げない恋人を獲得するまでの、ハッピーエンド・コメディ。気のいい中年女の悲哀と売れない役者の軽妙なやりとりは、メイスンとドレイファスの出演を念頭に置いて書かれ、彼らの名演を引き出すことに成功した。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("台湾人生", "台湾人生", "", "", "http://image.eiga.k-img.com/images/movie/54519/315x210.jpg?3940", 81, "日本の統治下にあった1895年から1945年の間の台湾では、同化政策として日本語教育を強要され、日本人として生きる世代が生まれた。“日本語世代”と呼ばれる青春時代を日本の統治下で過ごした5人の台湾人へ当時の様子や、戦後の国民党独裁時代のインタビューを行い、台湾と日本の密接な歴史を振り返る。元新聞記者の酒井充子が初監督を務めるドキュメンタリー。", "2009-06-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bariera", "バリエラ", "", "", "http://image.eiga.k-img.com/images/movie/48119/315x210.jpg?2029", 82, "ポーランドの異才J・スコリモフスキーによる実験精神あふれる力作。レールの敷かれた人生に疑問を感じたある学生が、新しい生き方を求めて寄宿舎を出る。彼は夜行列車の中で出会った少女と一日を過ごす。まるで目に見えない障壁（バリエラ）の中で、出口を探そうとするかのような二人の放浪が始まる……。", "2009-10-20", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Under the Skin", "アンダー・ザ・スキン（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NAKED GUN−FROM THE FILES OF POLICE SQUAD！", "裸の銃〈ガン〉を持つ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "コメディ・クリエーターの黄金トリオだった、D・ザッカー、J・エイブラハムズ、J・ザッカー（略称ZAZ）が手掛けた、究極のハイ・パワー・コメディ決定版。低音が魅力のロマンス・グレイ。鍛えた肉体と豊富な経験で、次々と事件を解決する敏腕警部ドレビン。と思いきや、彼はとんでもないトラブルメーカーだった。エリザベス女王の来訪を控え、当日の警備準備に大わらわのロス市警。同じころ、ヘロイン密輸事件を探っていた警官ノードバーグが、何者かに銃撃され重傷を負う。その犯人を追うドレビンは、やがて女王暗殺の情報をつかむが……。ゴルバチョフ、アミン、カダフィ、ホメイニ、そしてエリザベス女王と、世界の要人ソックリさんの登場も見どころの一つ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I am 日本人", "I am 日本人", "", "", "http://image.eiga.k-img.com/images/movie/33589/315x210.jpg?2789", 111, "森田健作が製作総指揮、企画、原案、脚本、出演の5役を務めた人間ドラマ。日系アメリカ人の視点から、現代日本の姿を描く。カリフォルニアで育った日系3世のエミー・ワタナベは、亡き祖父から聞いた日本に憧れを抱き、日本の大学への留学を決意する。ところが、彼女が目にした現代の日本は、義理人情やわび・さびを重んじたかつての日本とはかけ離れていた……。主演は本作で映画デビューを果たしたモデルの森本クリスティーナ。", "2006-08-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("酔いどれ八萬騎", "酔いどれ八萬騎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1928～29年の名作「浪人街」をマキノ監督自ら再映画化（1957年に松竹で再度リメイク）。破産に瀕した実弟・満男が経営する東横映画のための急造企画で、撮影期間わずか１週間、出演者もスタッフもノーギャラで製作した。長いスランプを脱しつつあったマキノ監督の職人芸が冴える１編。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージーボーイズ", "クレージーボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "東映の仲村トオルに対抗して松竹が売り出した加藤昌也の映画デビュー作。少年院とシャバを行ったり来たりする若者たちのエネルギッシュな青春像をノンフィクション『塀の中の懲りない少年達』をもとに描き出している。監督は「彼のオートバイ、彼女の島」など、大林宣彦作品のシナリオも書いている異能派・関本郁夫。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE POSTMAN", "ポストマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 176, "世界戦争によって無政府状態に陥った近未来のアメリカ。暴力的な独裁者の手から逃れた流れ者の男は、ふと立ち寄った町で自分は政府の郵便配達人だと嘘をつく。未来に絶望した庶民の心に触れて一念発起した彼は、ポストマンとして人と人の心を繋ぐ手紙を運ぼうとする……。通信手段が失われた2013年の世界を背景としたドラマ大作。「ダンス・ウィズ・ウルブズ」以来７年ぶりにメガホンを執ったK・コスナーが、冒険アクションの要素をふんだんに盛り込んで、数奇な運命をたどるポストマンを熱演している。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪魔が来りて笛を吹く", "悪魔が来りて笛を吹く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "不気味なフルートの音とともに起きる殺人事件を、西田敏行扮する名探偵・金田一耕助が鮮やかに解決する。監督は『太陽にほえろ！』『俺たちの旅』などTVシリーズで傑作を撮っている斎藤光正。光と影をうまく対比させた映像作りが面白い。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンラッキー・モンキー", "アンラッキー・モンキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「ポストマン・ブルース」に続くサブ監督の第３作。災難に巻き込まれた男の一夜の奔走を、彼ならではのエネルギッシュなタッチで描く。銀行強盗を画策した詐欺師の山崎は“先客”の強盗事件に遭遇したことから、事を起こす前に警察に追われるハメに。運の悪いことに、逃亡中に出くわした女性を図らずも刺殺してしまい、そこに殺人容疑も加わった。追い詰められた山崎は、同様にツキのないヤクザの３人組を巻き込み必死の思いで逃げ回る……。不条理な設定や極限状態の張りつめた緊迫感に、サブ監督の才気がうかがえる。主人公に扮した堤真一の、ひた走る熱演も見どころ。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　VIE　DE　JE´SUS", "ジーザスの日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "定職も持たず、仲間とバイクを乗り回したり、恋人マリーとのセックスで退屈な毎日を埋めている青年フレディ。そんな折、マリーにつきまとうアラブ人の少年が現れる。激しい怒りと憎しみに駆られたフレディがとった行動とは……。常に苛立ちを抱え、無軌道な行動に走る若者の姿を通して、暴力という人間の心に潜む闇と狂気、死と性＝生を浮かび上がらせる異色の青春ドラマ。監督のB・デュモンは哲学教師やジャーナリストを経て、テレビ局で教育映画などを40本近く撮った後、39歳で監督デビュー。長編初監督作となる本作で、1997年ジャン・ヴィゴ賞、シカゴ映画祭国際批評家連盟賞などを受賞し、国内外から注目を浴びた。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("孤城の櫻", "孤城の櫻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "物語自体は、南北朝時代、北畠親房の『神皇正統記』執筆にまつわる秘話。しかし、見どころは松浦妙子扮する美剣士・松壽丸の男装姿や、政略結婚させられる姉妹の悲劇、大軍に包囲された孤城からの脱出など、娯楽色の強い部分にある。松浦妙子は、純情可憐な娘役で知られる、新興の看板女優の一人。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Filles du Botaniste", "中国の植物学者の娘たち", "", "", "http://image.eiga.k-img.com/images/movie/53139/315x210.jpg?1959", 98, "「小さな中国のお針子」のダイ・シージエ監督が、中国ではタブーとされる“同性愛”というテーマに挑んだ意欲作。孤児院で育てられた少女ミンは、植物学者チェンの下で実習するため湖の植物園で過ごすことに。彼女はそこでチェンの娘アンと出会い、次第に心を通わせていく。やがて2人は本気で愛し合うようになるが……。主演はフランス映画界でも活躍するミレーヌ・ジャンパノワと、「かちこみ！ドラゴン・タイガー・ゲート」のリー・シャオラン。", "2007-12-15", "10,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WATER AND POWER", "ウォーター・アンド・パワー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "映画作家で、SFXやアニメのスペシャリスト、P・オニールの初の長編。ロサンゼルスを人工的に作られた“光と水の都市”として捉え、数々のパフォーマンス、SFX、アニメなどを駆使し、この都市の寓話を作り出したイメージ叙事詩。バックに流れるジャズ・サウンドも効果満点。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUN CRAZY／復讐の荒野", "GUN CRAZY／復讐の荒野", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "美女の復讐をモチーフにしたバイオレンス・アクション・シリーズの第１作。無法地帯と化したギャング支配の街に、ハーレーに乗ってやってきた女アウトロー、沙紀。周囲の反対をよそに、彼女は冷酷なギャングのボス、東郷への接近を試みる。その背後には、父を殺した東郷への復讐という計画が秘められていた……。無表情で銃を乱射するヒロイン、米倉涼子のクールな魅力が光る。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AMI DE MON AMIE", "友だちの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "フランス・ヌーヴェルヴァーグの第一人者、ロメール監督の“喜劇とことわざ”シリーズ第６作。初夏の陽光まぶしいパリ郊外の新都市セルジー＝ポントワーズを舞台に、24歳のOLブランシュと22歳の女子大生レア、それに二人のボーイフレンドという４人の男女の揺れる恋心をセンシティブにユーモラスに描いた、フランス風ラブ・コメディ。タイトル通り、友だちの恋人に心惹かれ、いつの間にやら相手が入れ替わってハッピーエンド。その恋の推移をあざやかに映し出す、青と緑を基調とした巧みな色彩演出が粋で楽しい。ロメール監督特有の長回しカメラも効果的な、フランス映画のエスプリに満ちた作品である。1987年モントリオール国際映画祭同シリーズ特別賞受賞。DVDは「エリック・ロメール コレクション(5)」に収録。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八月のラヴソング", "八月のラヴソング", "", "", "http://image.eiga.k-img.com/images/movie/57469/315x210.jpg?2789", 0, "KDDI、auの携帯向け動画配信サービス「LISMOドラマ！」で配信された、K-POPスターと平凡な主婦の危険な恋を描いたラブストーリーを映画館で上映。結婚5年目でレコード会社で経理として働く34歳の明日花の前に、ある日突然、人気絶頂のK-POPアーティスト、パク・ジョンミンが現れる。スランプに陥っていたジョンミンと平凡な日常に物足りなさを感じていた明日花は、ひょんなことから一緒に逃げ出すことになり、逃避行の中で距離を縮めていくが……。主演はK-POPグループ「SS501」のパク・ジョンミンと酒井美紀。監督は「NANA」の大谷健太郎。", "2011-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'educazione sentimentale di Eugenie", "マルキ・ド・サドの調教哲学", "", "", "http://ecx.images-amazon.com/images/I/51rrOq5Z%2BOL._SL210_.jpg", 87, "「肉屋」で知られるイタリアン・エロスの鬼才アウレディオ・グリマルディが、エロス文学の最高峰として名高いマルキ・ド・サドの「閨房哲学」の映画化に挑んだエロティックドラマ。淫蕩（いんとう）な日々を送る侯爵夫人サンタンジュは、厳格な母親に育てられた18歳の美処女ユージニーを官能調教するため、彼女を2日間預かることに。サンタンジュは無神論者で快楽主義者の哲学者ドルマンセと共に、ユージニーに性の喜びを教えこんでいく。", "2006-06-10", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地球交響曲　ガイアシンフォニー 第三番", "地球交響曲　ガイアシンフォニー 第三番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "地球と人間のかかわりを見つめるドキュメンタリー・シリーズの第３作。熊に襲われ死んだ写真家、星野道夫が暮らしていたアラスカへの旅を縦糸に、映画は展開。星野のスピリットとともに、宇宙的なスケールで人類の未来を語る宇宙物理学者フリーマン・ダイソンや、ハワイ先住民であるナイノア・トンプソンの証言を通して、現代を生きる人間の心の在り方を問いかける。", "1997-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PENN ＆ TELLER GET KILLED", "ペン＆テラーの死ぬのはボクらだ!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "A・ペンが監督したミステリー・コメディ。主演に、人気コメディアン魔術師コンビ、ペン＆テラーを実名で起用し、トリッキーなストーリーが展開する。ある深夜番組で“命を狙われてみたい”と口走ったペン。その直後から、本気か冗談かわからないまま、ペンは狂信的なファンから命をつけ狙われることに……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京無印女子物語", "東京無印女子物語", "", "", "http://image.eiga.k-img.com/images/movie/57699/315x210.jpg?1435", 97, "脚本家なるせゆうせい原案のアンソロジーコミック「東京無印女子物語」から、ねむようこの「のこのこ」と山崎童々の「チェンジ」を実写映画化。故郷を離れて東京で暮らす女子の日常を描く。のんびり屋で「ノロ」というあだ名までついている就活中の女子大のぞみは、それでも大都会・東京のスピードの中でなんとか日々を送っている。同居中の恋人・亀山はのぞみ以上にマイペースで、毎日好きな絵を描いて過ごしていた。一方、広告業界で働く冴子は、同居人のカオリが恋や遊びを満喫しているのとは対照的に、仕事に忙しい。しばらく会っていなかった恋人のトモヤに電話をした冴子だったが、電話口から女の声が聞こえて……。主演は実力派若手の谷村美月、今回が本格女優デビューとなるシンガーソングライターの柳めぐみ、水谷豊と伊藤蘭の娘・趣里。監督は「恋するマドリ」の大九明子。", "2012-06-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUNSU / JEJUNG / YUCHUN THANKSGIVING LIVE IN DOME 劇場版", "JUNSU / JEJUNG / YUCHUN THANKSGIVING LIVE IN DOME 劇場版", "", "", "http://image.eiga.k-img.com/images/movie/55669/315x210.jpg?2202", 0, "韓国の3人組ボーカル・グループ、JUNSU／JEJUNG／YUCHUNが2010年6月に東京・大阪のドームで行なった初ライブ「THANKSGIVING LIVE IN DOME」。計4公演で約20万人を動員し、ダイジェスト映像のネット配信でも視聴回数が130万回に達した同ライブの模様を特別編集した劇場版。ネット配信やDVDでは見ることのできないオープニング、エンディング映像も収録されている。", "2010-08-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名", "悪名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "かつて勝新太郎、田宮二郎のコンビでシリーズ化された、今東光原作の「悪名」を再び映画化。暴れん坊のお人好し、朝吉を的場浩司、クールな男、モートルの貞を東幹久が演じている。昭和の初期、金貸しの女房お千代と駆け落ちした朝吉は有馬温泉に逃げ込み、そこの賭場で大阪松島・吉岡組の小頭、モートルの貞と出会う。その後、松島の遊郭で薄幸の娼妓、琴糸と知り合った朝吉は、やがて貞と協力して彼女の足抜けを手伝うことになる。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・サラリーマン清水港", "続・サラリーマン清水港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ライバル・黒駒醸造に都市対抗野球で負けた清水屋。ヤケ酒の長五郎社長は飲み屋でもケンカ。そのうえ、倒産寸前の神戸屋への融資まで引き受けてしまう。神戸屋乗っ取りをたくらむのがあの黒駒なのだ。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新二等兵物語・敵中横断の巻", "新二等兵物語・敵中横断の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "日支事変さなかの中支最前線。第1030部隊にすばしっこいが慌て者の横田秀作と、正直いちずで要領の悪い花岡茶吉の両二等兵が転属される。前線に送られた慰安婦が捕虜になったとの報を受けて、横田らは救出に向かうが……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アフタースクール", "アフタースクール", "", "", "http://image.eiga.k-img.com/images/movie/53399/315x210.jpg?2341", 102, "母校の中学校に勤める教師・神野の元に、かつての同級生だと名乗る見覚えのない男が現れる。現在は探偵だという彼は、同じく神野の同級生で親友の木村を探しているという。神野は成り行きから木村の捜索に協力することになってしまい……。劇場長編デビュー作「運命じゃない人」がカンヌ国際映画祭にて4冠に輝くという快挙を達成した内田けんじ監督が、練り込まれた脚本と卓越した構成力で描いたサスペンス・コメディ。", "2008-05-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホーホケキョ・となりの山田くん", "ホーホケキョ・となりの山田くん", "", "", "http://ecx.images-amazon.com/images/I/51IilMCiRiL._SL210_.jpg", 104, "朝日新聞で連載中のいしいひさいち原作の４コママンガをもとにした、スタジオジブリの劇場用アニメ第11作。「平成狸合戦ぽんぽこ」以来、５年ぶりとなる高畑勲が脚本も手掛け、心温まるユニークなホームドラマを作り上げた。中年夫婦と兄妹、祖母の５人で暮らすごく平凡な山田家の日常が、川柳を絡めたミニ・エピソードの数々で綴られる。些細なことで怒ったり笑ったり、のほほんとした家族が一喜一憂する姿がおかしくて、ときにホロリとさせる。「もののけ姫」を超える15万枚の作画を行い、セルを一切使わないフルデジタル処理で水彩画風の淡く、繊細な雰囲気を演出。矢野顕子の透き通る歌声と軽やかな音楽も心を和ませる。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阮玲玉／ACTRESS", "ロアン・リンユィ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 154, "1930年代中国で活躍した伝説の女優ロアン・リンユィ（阮玲玉）を、残されたフィルムの断片、当時を知る人たちへのインタビュー、そしてM・チャンがロアンを演じるフィクションで浮き彫りにする。1929年の上海。監督のスン・ユイ（孫瑜）は新作「故都春夢」でロアンを主役に抜擢。そして翌年の「野草閑花」でも起用し、ロアンはスターとしての地位を確立する。だが時代は移り変わり、私生活でもスキャンダルを起こしたロアンに、スターの座を譲り渡す時がやって来る……。残された映画の断片を主体に、M・チャンの演じる物語とインタビューがその間隙を埋めていく。フィルムの断片に記憶されたロアンの清楚かつ妖艶な存在感が最も印象に残るのだが、損な役まわりを演じたM・チャンの奮闘ぶりも見逃せない。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOCTOR MORDRID", "サイキック・ウォリアーズ　超時空大戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "100年の時空を越えて善と悪が戦う、SFアドベンチャー。異次元への入口を守る光の戦士モードリッド。彼は100年前、究極の生命体カベルと戦い、苦闘の末に超時空ワールドに封じ込めた。ところが、そのカベルが眠りから覚め、デーモン軍団を引き連れてこの世界に侵入しようとしていた。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("站台", "プラットホーム", "", "", "http://image.eiga.k-img.com/images/movie/1599/315x210.jpg?1609", 151, "80年代半ばの中国山西省、ミンリャンら幼なじみの4人は小さな町の劇団員。彼らは地方巡業の旅に出るが、やがて政府の方針の変化による自由化の波が押し寄せ、4人の関係は不安定になっていく。地方劇団の10年間を通して、激動の80年代中国を描く話題作。監督は中国の新世代、ジャ・ジャンクー。デビュー作「一瞬の夢」(98)でベルリン映画祭新人監督賞を受賞、本作は00年ベネチア映画祭最優秀アジア映画賞に。", "2001-12-01", "6,76,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Singularidades de Uma rapariga Loura", "ブロンド少女は過激に美しく", "", "", "http://image.eiga.k-img.com/images/movie/55579/315x210.jpg?2284", 64, "今年で102歳となる現役最高齢の映画作家マノエル・デ・オリベイラが、ポルトガルの文豪エサ・デ・ケイロスの短編小説を映画化。リスボンから南に向かう列車の中で、とある夫人と隣り合わせた青年マカリオは、「話して楽になりたい」といい、衝撃的な体験談を語り始める。それは、マカリオと、彼がひと目で恋に落ちたブロンド少女・ルイザの奇妙な物語だった……。主人公マカリオにオリベイラ監督の孫リカルド・トレバ。", "2010-10-09", "25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GROS COUP", "恐喝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "サッカー選手のフランクは交通事故で相手を死なせてしまうが、彼の元に現れた保険会社のドルーアンはそれが保険金目当ての事故だとほのめかし、被害者の妻を揺すろうと持ちかける。しかし数日後、ドルーアンは殺されフランクは一人で恐喝を実行しようと決心するが、やがて人妻に心を惹かれて……。", "1964-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FEMME PUBLIQUE", "私生活のない女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ポーランド出身の、ズラウスキー監督の異色作。エセルは、写真のヌード・モデルをやりながら、女優になる機会を狙っていた。ついに人気監督の目に留まり……。エセルの貪欲な野望が引き起こす、愛僧劇と殺人事件をスキャンダラスに描く。全裸で踊るカプリスキーは、まさに“煽情的”。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIGERLAND", "タイガーランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「依頼人」のJ・シュマッカーが、“ドグマ95”に賛同して製作した意欲作。戦地へ向けて最終歩兵訓練を行う新兵たちの中に、上官にたてつく男、ボズがいた。能力がありながらも常に責任から逃げ出してきたボズ。彼は、ひょんなことから潰されかけていく仲間を軍規の抜け道を使って次々と除隊させる。しかし、あらゆる抵抗にもかかわらず、最後の試練の地へと送られてしまう……。戦争映画ながら、敵国との戦闘シーンなどが一切ないのは、“アメリカのベトナム”と呼ばれるタイガーランドで、戦地に赴くための過酷な訓練に耐える若者たちの恐怖や不安、友情などの心の葛藤を描いたため。16ミリカメラを使用した手持ちによる撮影、照明＆音楽を極力排したドグマ流の演出が見事に作風にマッチし、ドキュメンタリーを思わせる臨場感を醸している。劇中、存在感あふれる演技を披露したC・ファレルは、この作品で高い評価を得て、一躍スターダムに。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH WISH V／THE FACE OF DEATH", "デスウィッシュ キング・オブ・リベンジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1974年製作の「狼よさらば」から始まるC・ブロンソン主演の“デスウィッシュ”シリーズ第５作。かつて、ニューヨークやロサンゼルスで悪を退治していた伝説の男ポールは、新しい町で大学の建築学教授として生きていた。ポールは、デザイナーのオリヴィアとその娘のチェルシアと幸せな生活を築こうとしていた。だが、そのことに怒ったオリヴィアの元の夫でマフィアのボスであるトミーによって、オリヴィアとチェルシアは殺されてしまう。怒りに燃えるポールは、再び悪へと立ち向かうのだった。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Vie de boheme", "ラヴィ・ド・ボエーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "プッチーニのオペラ「ラ・ボエーム」で有名な、アンリ・ミュルジェールによる小説を、フィンランドのアキ・カウリスマキ監督が”原作を台無しにしたプッチーニへの復讐をこめて”映画化。舞台はパリ。アルバニア出身の画家ロドルフォは、家賃滞納でアパートを追い出された作家マルセルと出会い意気投合。2人はマルセルの部屋へ向かうが、そこにはすでに新しい住人の音楽家ショナールが入居していた。3人は共同生活を始めるが……。", "1992-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wings of Defeat", "TOKKO 特攻", "", "", "http://image.eiga.k-img.com/images/movie/34079/315x210.jpg?4073", 90, "太平洋戦争末期に日本軍が編成した特攻隊の真実に迫るドキュメンタリー。亡き叔父が特攻隊員として訓練を受けていたことを知った日系アメリカ人のリサ・モリモト監督が、日本で生まれ育ったアメリカ人プロデューサーのリンダ・ホーグランドと共に、日米両国の視点から描き出す。元特攻隊員の生存者や親族、さらに特攻隊によって撃沈されたアメリカ軍艦の乗組員らの証言を通し、狂信的でも軍神でもなかった特攻隊員たちの姿を浮き彫りにしていく。", "2007-07-21", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLASS ACT", "クラス・アクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "当時、アメリカで人気のラップ・チーム“キッドン・プレイ”の二人が出演した学園コメディ。秀才で超マジメだが、ひ弱でモテないダンカンと、スラム育ちでケンカの強いブレード。まるで正反対の二人だが、新学期の始まった日、自分たちの成績表が入れ替わっていることに気付いた。なんとダンカンは落ちこぼれのクラスに、ブレードは秀才のクラスに入れられてしまった。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIES", "LIES／嘘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "猥褻文書製造罪として作者に有罪判決が下された小説を映画化し、本国では公開直前に上映禁止になるなど、芸術かポルノかという過激な内容が話題になった問題作。18歳の女子高生Yと、かつて売れっ子の彫刻家だが今は無職の38歳の中年男が出会い、アブノーマルなセックスにのめりこんでいく。性に対する偏見や検閲に真っ向から挑んだような描写は、賛否両論分かれるところ。", "1999-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おしまいの日。", "おしまいの日。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "新井素子の原作をもとに、対照的な二組の夫婦の愛の形を描く。夫を深く愛している妻・美津子と仕事人間の夫・忠春。自分たちとは対照的な彼らが羨ましくもあり、不安でもある久美と俊彦の夫婦は、倦怠期に差し掛かっている。だが、献身的な美津子の態度が負担になってきた忠春は、次第に彼女につらく当たるようになる。そんなある日、美津子の妊娠が発覚。彼女は“おしまいの日が来た”と書き残して姿を消す……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRUG GARDEN", "DRUG GARDEN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ドラッグとたわむれる若い世代をスタイリッシュに描いた問題作。個性派女優、広田レオナが初監督に挑戦。彼女が自分自身を演じ、現実と虚構が入り交じった奇妙な世界が展開される。レオナはパートナーのフッキー、息子のマーク、そして、５人の居候達と奇妙な共同生活を送っていた。そんなある日、レオナが突然倒れた。彼女はパニック・ディスオーダーの発作にかかっていたのだ……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Truands", "裏切りの闇で眠れ", "", "", "http://image.eiga.k-img.com/images/movie/53349/315x210.jpg?2260", 107, "パリの暗黒街で繰り広げられる男たちの抗争をブノワ・マジメル主演で描いたノワール・サスペンス。相棒のジャン＝ギィと組み闇社会で危険な仕事を請け負っているフランク。殺しでさえも冷静かつ非情に実行する彼は、暗黒街を牛耳る大物クロードから絶大な信頼をおかれていた。しかし、何者かの陰謀によりクロードが投獄されてしまったことから、街に不穏な空気が流れ始める。監督は「スパイ・バウンド」のフレデリック・シェンデルフェール。", "2008-02-23", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖怪百物語", "妖怪百物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "怪談を一つずつ語りあって、終わるごとにロウソクをふき消していく百物語。最後の１本が消えたあとにおまじないをしなければ、妖怪が現れるという。そのおまじないを怠ったために……。おどろおどろしいというよりはキッチュなポップ感覚の作品で、シャレてるホラーになった逸品。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOCTOR ZHIVAGO", "ドクトル・ジバゴ", "", "", "http://ecx.images-amazon.com/images/I/51-f4sVSooL._SL210_.jpg", 194, "本国でその出版が解禁され話題をよんだロシアの文豪パステルナークの同名小説をイタリアのプロデューサー、カルロ・ポンティが映画化した70ミリ大作。ロシア革命前後の動乱期を背景に純粋な魂を持ち、詩人でもある医師ジバゴの波乱に満ちた人生が、二人の女性、ラーラとトーニャへの愛を通して描かれる。リーンは、ボルトの名脚本を得て、多くの個性的な大物を登場させながら見事な叙事詩にまとめ上げた。ジバゴとラーラの出会いと別離を切々と奏でた主題曲『ラーラのテーマ』は、戦後映画音楽史上に残る名曲である。", "1965-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARRIED TO IT", "マンハッタン・ラブ—女と男のいい関係—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "様々な人々が集う街、ニューヨークを舞台に、世代も階層も違う３組の夫婦の友情と、夫婦愛を、ユーモアを交えて描いた都会派ドラマ。アイオワから越してきた新婚カップル、二人の息子を持つ中年夫婦、年頃の娘を連れて再婚した夫婦。それぞれの夫婦を、B・ブリッジスをはじめとする個性派俳優たちが演じている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Meet the Fockers", "ミート・ザ・ペアレンツ2", "", "", "http://image.eiga.k-img.com/images/movie/1529/315x210.jpg?2203", 115, "ベン・スティラー演じる主人公グレッグが、恋人パムと結婚するため彼女の父親と会うが、これがロバート・デ・ニーロ扮する元CIAの頑固オヤジで、大騒ぎになった「ミート・ザ・ペアレンツ」の続編。今回は、両家の両親たちが初めてのご対面。グレッグは恋人の両親を自分の両親の家に招くのだが、ここでも大騒ぎが。グレッグの専業主夫の父親をダスティン・ホフマン、セックス・カウンセラーの母親をバーブラ・ストライザンドが演じる。", "2005-11-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES TROIS FRE｀RES", "おかしな三兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "巨額の遺産相続話から、腹違いの３兄弟に様々な騒動が降りかかるコメディ。母親が残した３億フラン相続のために、異母兄弟であることを知らされた広告代理店幹部のパスカル、スーパーの警備員ディディエ、露天商のベルナール。管財人に呼ばれた３人だが、生活も身分も違う彼らを捜し出すのに時間がかかったため相続手続きは時間切れとなり、遺産は施設に寄付されてしまう。無一文となったディディエとベルナールはパスカルの部屋に転がり込み、そこにディディエの息子ミカエルも現れて、路頭に迷うハメになった４人は、物乞いや万引きをしながら旅をしていく。フランスの人気コメディ・グループ、レザンコニュの３人が主演し、うち二人が監督・脚本を務めている。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("よみがえる琉球芸能　江戸上り", "よみがえる琉球芸能　江戸上り", "", "", "http://image.eiga.k-img.com/images/movie/56649/315x210.jpg?4215", 69, "1609年（慶長14年）の薩摩侵攻により江戸幕府の支配下に置かれた琉球王国が、徳川将軍の代替わりのたびに使者を派遣し、伝統の「路次楽」など琉球芸能を披露した「江戸上り」の復元に奔走する人々を追ったドキュメンタリー。当時を描いた絵巻物や古くから残っている沖縄の音楽や舞踊、神事の舞、当時交流のあった中国に伝わる音楽などを総合し、「江戸上り」を現代によみがえらせる。", "2011-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Coriolanus", "英雄の証明", "", "", "http://image.eiga.k-img.com/images/movie/57569/315x210.jpg?3898", 123, "ローマの将軍コリオレイナスの悲劇を描いたウィリアム・シェイクスピアによる戯曲を、舞台を現代に置き換えて映画化したサスペンスアクション。レイフ・ファインズが初監督と主演を務めた。数々の武勲をたて力をつけていったローマの独裁者コリオレイナスは、その独裁性を危惧した護民官の策略で国を追放されてしまう。絶望したコリオレイナスは、自身の命を狙っている宿敵オーフィディアスのもとを訪れるが……。共演にジェラルド・バトラー、ブライアン・コックス、ジェシカ・チャステインら実力派が顔をそろえる。", "2012-02-25", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　怪傑ナガネギマンとドレミ姫", "それいけ！アンパンマン　怪傑ナガネギマンとドレミ姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "やなせたかし原作の人気アニメーション劇場版第15作「それいけ！アンパンマン　ルビーの願い」と同時上映の短編。", "2003-07-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GENTLEMAN D’EPSOM（LE GRANDS SEIGNEURS）", "エプソムの紳士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "フランス映画界の名優J・ギャバンと名コメディアン、L・ド・フュネスが、最初にして最後の見事なコンビ・プレイを発揮した快作。競馬界の裏側をコミカルに描いた本作の成功をきっかけに、フュネスは喜劇王へと大躍進をする。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　EX", "異常心理", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「ルームメイト」のJ・ラッツの原作をもとにしたサイコ・サスペンス。有能な建築家デヴィッドは、再婚して平穏な日々を送っていた。しかし、別れた妻ディアドラに再会した日から、彼は忌まわしい過去に悩まされていく。ディアドラの表の顔とは裏腹の異常な性癖、そして彼女が隠していた離婚の驚くべき真相。執拗に付きまとう彼女によって、デヴィッドの家庭は恐怖に陥っていく。「ドロップ・ゾーン」のY・バトラーが狂気の女を怪演。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕たちのプレイボール", "僕たちのプレイボール", "", "", "http://image.eiga.k-img.com/images/movie/54949/315x210.jpg?3599", 0, "元メジャーリーガーの新庄剛志がエグゼクティブ・プロデューサーを務める少年野球映画。主演は新人の小原裕貴、共演に吉田栄作、羽田美智子ほか。アメリカに暮らす球児は、怪我でマイナー落ちした元メジャーリーガーの父親との関係が悪化し、母親と一緒に日本へ帰国することになる。親友のランディと交わしたリトルリーグ世界大会での再会を果たすため、球児は地元のリトルリーグ「東陽リトル」に入団するが、日本の文化や友達とうまく馴染むことができず、孤立してしまう。", "2010-05-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Something Wild", "サムシング・ワイルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "　勤務先の副社長に昇進したチャーリーはカフェで魅力的な女性ルルと知り合う。彼女に誘われて会社をサボったチャーリーは週末も一緒に過ごすことに。そんな中、ルルの秘密も少しずつ明らかになっていく。本名がオードリーであること。そして離婚したかつての夫レイが凶暴な犯罪者であること。チャーリーはレイに捕まったオードリーを追うが……。謎めいたヒロインをめぐって繰り広げられる冒険を、コミカルかつ妖しくスリリングに描く。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・兄弟仁義", "続・兄弟仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "大正初期。土木請負業者の利三郎の助っ人に、渡世人清次がやってくる。利三郎の利根川護岸工事は、博徒の親分時蔵によって妨害されていた。利三郎の心情を察した清次は単身時蔵一家に乗り込むが、取り押さえられてしまう。居合わせた武州の大親分初太郎のはからいで、その場は丸くおさまったが、時蔵はひそかに初太郎の命を狙うのだった。やがて清次の怒りは頂点に達する。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紙風船", "紙風船", "", "", "http://image.eiga.k-img.com/images/movie/55819/315x210.jpg?2096", 117, "大正から昭和にかけて活躍した劇作家・演出家の岸田國士の短編戯曲を、東京藝術大学の学生が映画化。同大学院映像学科の学生が企画開発、脚本執筆、キャスティング、撮影、配給まですべての工程を担当した。退屈な日曜日を持て余した夫婦が鎌倉へ小旅行に出かける表題作ほか、幼なじみの淡い恋愛を描く「あの星はいつ現はれるか」、不運な男ふたりのロードムービー「命を弄ぶ男ふたり」、セレブ家族が巻き起こすコメディ「秘密の代償」の4話構成。", "2011-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FELLINI：SONO　UN　GRAN　BUGIARDO", "フェリーニ・大いなる嘘つき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1993年に他界した巨匠F・フェリーニの奔放なまでの創造性に迫ったドキュメンタリー。監督のD・ペティグリュー自身が最晩年のフェリーニに対して行ったインタビューをベースに、フェリーニ作品のアウトテイクやメイキング映像などを紹介。また、フェリーニゆかりの有名俳優やスタッフが興味深い証言を提供している。", "2002-01-01", "2,4,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("中山七里", "中山七里", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "長谷川伸の同名小説を池広一夫監督、市川雷蔵主演で映画化した股旅映画の佳編。木場の人足が恋女房を手込めにし、自殺に追いやった元締めを殺して旅に出るが、途中女房そっくりの女に出会う……。「沓掛時次郎」でも好演した市川雷蔵が長谷川伸の世界を見事に体現。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONTOYAS Y TARANTOS", "アンダルシアの恋物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "シェイクスピア劇『ロミオとジュリエット』をモチーフに、スペイン・南アンダルシア地方に敵対するジブシー、タラント家とモントーヤ家に生まれた、無垢で純粋な若い男女の悲恋を描いた作品。美しいアンダルシア各地の風景はもちろん、全編を彩るフラメンコ・ギターの調べも魅力的。", "1989-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEVEN TIMES SEVEN", "新・黄金の七人 ７×７", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "７人組の金塊強盗グループをコミカルに描いた、イタリア映画のヒット作「黄金の七人」のシリーズ第３作。ニセ札ならぬ本物の札を造幣局で印刷する奇想天外なアイデアは果たして成功するか？　好調なテンポで笑いとスリルを見事に混ぜ合わせた娯楽作だ。", "1968-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Forbidden Kingdom", "ドラゴン・キングダム", "", "", "http://image.eiga.k-img.com/images/movie/53679/315x210.jpg?4364", 105, "アジアを代表する2大スター、ジャッキー・チェン＆ジェット・リーが初共演を果たしたアクションアドベンチャー。古代中国に迷い込んでしまったカンフー映画オタクのアメリカ人青年が、2人の武術の達人に導かれて邪悪な敵に挑む姿を壮大なスケールで描く。監督は「スチュアート・リトル」シリーズのロブ・ミンコフ。アクション監督は「マトリックス」「グリーン・デスティニー」などで知られるカンフー映画の巨匠ユエン・ウーピン。", "2008-07-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Action Jackson", "アクション・ジャクソン 大都会最前線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「ロッキー」シリーズのアポロ役でおなじみC・ウェザースが型破りなバイオレンス刑事に扮し、巨大な悪に挑むアクション。相手は一流自動車会社社長。彼は邪魔者を次々と殺害し、その罪をジャクソン刑事にかぶせる。逃避行を続けながらも刑事は敵を追い詰めて……。ラストの激闘は迫力満点。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASPHALT", "アスファルト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ベルリンの宝石店でエルゼという女性が万引きで捕まった。彼女を護送する警官アルバートはエルゼの誘惑にかかり、彼女の虜になってしまう。そしてアルバートは彼女のパトロンと口論になり、思い余って殴り殺してしまった。エルゼ役のB・アマンの魅力を全編に散りばめたドイツ無声映画末期の傑作。", "1929-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("U２ RATTLE AND HUM", "U2／魂の叫び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "アイルランド出身のロック・バンド、U2。アメリカを横断した1987年“ヨシュア・トゥリー”のツアーで、彼らのナマの姿を捉えたドキュメンタリー。メンフィスでのエルヴィスの墓参、ハーレムでのゴスペル・シンガーとの共演など、彼らの音楽ルーツをたどる旅が続く。モノクロとカラーを使い分けた撮影も出色だ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Piano", "ピアノ・レッスン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "19世紀半ばのニュージーランドの浜辺に、遠くスコットランドからの“写真花嫁”エイダが降り立った。彼女は幼い娘フロラを連れており、しかも口がきけなかったが、代わりにピアノで自らの情熱を語る能力に恵まれていた。ところが当時のニュージーランドは辺境の島。新しい夫スチュアートは、彼女の大切なピアノを“運べない”という理由で浜辺に放置したままにする。悲しみにくれる母娘。そんなある日、エイダに秘かな思いを寄せる浜辺の地主ベインズは、彼女にある交換条件を出す。演奏の仕方を教えてくれれば、浜のピアノを彼女に返すというのだ。理由をつけては体に触れてくるベインズを嫌いながらも、エイダの官能的な“ピアノ・レッスン”が始まった……。「エンジェル・アット・マイ・テーブル」などで知られるオーストラリアの女流監督J・カンピオンが描く愛の名編。", "1993-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TUNNEL", "傷つけるほど愛して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "作品が理解されず悩んでいた画家ホアンは、ある日自分の絵に見入る美女を発見し、心惹かれていく。しかし彼女は富豪の夫を持つ人妻だった。結ばれぬ二人の悲劇的な恋をロマンティックに描くラブ・ストーリー。同年、「ロボコップ」でブレイクしたP・ウェラーと、J・シーモアが主役。", "1987-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逆境ナイン", "逆境ナイン", "", "", "http://ecx.images-amazon.com/images/I/31FZGC3NJYL._SL210_.jpg", 115, "'88年より『少年キャプテン』で連載後、コミックスは絶版となるもコアなファンに支えられてきた、天才漫画家・島本和彦の伝説の名作を映画化。あり得ない逆境の数々に立ち向かう主人公には「天国の本屋～恋火」の玉山鉄二。今までのイメージとはまったく違う、おバカで熱い男を好演。勝ち知らずの全力学園野球部。廃部勧告を受けたキャプテンの不屈闘志は、甲子園行きを約束。しかし、さまざまな逆境に見舞われ……。", "2005-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大根と人参", "大根と人参", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "小津安二郎が「秋刀魚の味」の次回作として予定していた企画を「自由学校」の渋谷実が映画化。弟が会社の金を使い込んだことから姿をくらました中年男。やがて、彼の愛人の娘が、家族の前に現れる……。“小津安二郎記念映画”と銘打たれ製作され、小津風の構図も見られるが、物語は渋谷と白坂依志夫のオリジナルといっていい内容。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AGUIRRE，DER ZORN GOTTES", "アギーレ・神の怒り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1560年末、インディオたちが語る伝説の国エル・ドラド(黄金郷)を発見するために、スペイン人たちがアマゾンの奥地を目指してアンデス山脈最後の峠を越えていた。アギーレは分遣隊の副官に任命され、様々な困難に遭いながらも、この分遣隊はアマゾンの奥地に入っていく。しかし、兵士たちはインディオに殺されたり、熱病に罹ったりして死んでいった。アギーレは最後までエル・ドラドが存在することを信じ続け、たった一人になっても、まだ自分が黄金郷に王国を建設することを夢みるのだった。ニュー・ジャーマン・シネマの代表的な監督であるヘルツォークは、岩肌を剥き出しにした険しい山、果てしなく流れ続ける河、森の中に隠れるインディオといった、アギーレと彼が率いる兵士たちを取り囲む荒々しく不気味なアマゾンを、見事に描いている。未開の自然の中で、何か一つのことを実現しようと夢みる男の野望は、ヘルツォークの常に求めている主題といえる。", "1972-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アウトレイジ2", "アウトレイジ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狂った果実", "狂った果実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1981年は根岸吉太郎にとって飛躍の年だった。初の一般作品「遠雷」で名実ともに若手監督のトップに躍り出ただけでなく、その直前に撮った本作で過去の７作品を集大成する一つの頂点に登りつめたのである。多くの優れたロマン・ポルノがそうであるように、この作品もポルノグラフィーとは一線を画す青春映画の佳編に仕上がった。社会の底辺に位置しつつも必死に生きる若者が金持ちの令嬢と知り合い、愛し合いながらも強烈な憎悪を抱く。踏みにじられた弱者が最後に見せる暴力のパッション！　セックスシーンも含め、歯切れのいいテンポ、時代認識の確かさ、明快な心理描写など、根岸の非凡な演出が光る一編。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武侠", "捜査官X", "", "", "http://image.eiga.k-img.com/images/movie/57759/315x210.jpg?1753", 115, "「ウィンター・ソング」「ウォーロード　男たちの誓い」に続きピーター・チャン監督と金城武がタッグを組み、ドニー・イェンとのダブル主演で送るミステリーアクション。山奥ののどかな村で、製紙工場に勤める職人リウが指名手配中の凶悪犯と遭遇。正当防衛の末に犯人を死なせてしまうが、悪人を退治したことで村では英雄扱いされる。事件を担当するため村にやってきた捜査官シュウは、なぜ平凡な男のリウがたったひとりで凶悪犯を倒すことができたのか疑問に抱き、調査を始める。するとリウの隠された過去にたどり着き……。", "2012-04-21", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("「無頼」より 大幹部", "「無頼」より 大幹部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "藤田五郎の自伝的小説『無頼』を舛田利雄監督・渡哲也主演で映画化。３年間のムショ暮らしから戻った一匹狼のヤクザ・藤田五郎は、上野組の悪らつなやり口に怒り、単身殴り込みをかける。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緑の街", "緑の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "初監督作品「いつかどこかで」での体験をもとに、小田和正が描いた半自伝的な第２作。映画の製作現場と、その劇中劇。そして、かつて恋人関係にあった監督と主演女優の恋模様が交錯していく。突然、映画作りを宣言した人気ミュージシャンの夏目。周りのスタッフはそんな彼を思い止まらせようとするのだが、夏目は着々と準備を進めていく。悪戦苦闘の末、いよいよクランクインとなるのだがそこは素人監督、夏目と映画スタッフとの間の溝は深まっていくばかりで……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・あらかわ　水の共同体をもとめて", "続・あらかわ　水の共同体をもとめて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "前作に続き荒川の諸問題と、流域に暮らす人々の生活に迫ったドキュメンタリー。川の汚染問題から生活の見直しをしている主婦や水質と微生物の問題を考えながら有機農業を続ける農民など、様々な人たちへの取材を通して、川と人間との関係を改めて見つめていく。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Miral", "ミラル", "", "", "http://image.eiga.k-img.com/images/movie/56359/315x210.jpg?2447", 112, "1948年、イスラエルがパレスチナに侵攻し、エルサレムには戦災孤児が溢れる。後にそうした孤児たちのために生涯を捧げることになるヒンドゥ・ホセイニは、孤児たちのための学校を設立。ヒンドゥから愛と教育を受けて育った少女ミラルは、幾度となく繰り返される紛争の中、自らの生い立ちと激動する世界を見つめながら成長していく。1973年イスラエル生まれで、本作の脚本を手がけたルーラ・ジブリールの自伝的物語を、「潜水服は蝶の夢を見る」のジュリアン・シュナーベル監督が映画化。主演は「スラムドッグ＄ミリオネア」のフリーダ・ピント。", "2011-08-06", "2,46,4,21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Frozen River", "フローズン・リバー", "", "", "http://image.eiga.k-img.com/images/movie/54879/315x210.jpg?3322", 97, "ニューヨーク州最北部の町で暮らすレイは、新居購入のための資金を夫に持ち逃げされ、息子2人と路頭に迷ってしまう。支払いに追われるレイは、モホーク族の女性ライラと組んで不法入国斡旋のビジネスを開始。それぞれ複雑な事情を抱える2人は、反発し合いながらもこのビジネスを続けていくが……。監督は本作が長編デビューとなったコートニー・ハント。08年のサンダンス映画祭でグランプリを受賞、第81回アカデミー賞では主演女優賞とオリジナル脚本賞にノミネートされた。", "2010-01-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLOSING TIME", "CLOSING TIME", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ヌーヴェル・ヴァーグに造詣の深い脚本家、小林政広の監督デビュー作。強盗事件で妻子を亡くしアルコール漬けとなった中年シナリオライターが、夜の街角で様々な人々との出会いを重ねていく姿を描く。４章構成になっており、特に主人公が夏木マリ扮する赤毛のホームレスと、刹那的な映画談義で盛り上がるエピソードが印象的。1997年ゆうばり国際ファンタスティック映画祭のヤング・ファンタスティック部門グランプリに輝いた。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRIECHISCHE　FEIGEN", "パトリシアの夏　ヘア・ノーカット版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "エーゲ海の美しい風景をバックに、少女のアバンチュールを綴るエロティックな青春映画。故郷ドイツに行くと両親に嘘をつき、アテネにやってきたパトリシア。さらに気ままなヒッチハイクと船旅を続けた彼女は、デロス島でトムという青年と出会い、彼を意識するようになる。しかしトムはパトリシアの体を求めず、彼女の元から去ってしまう……。", "1977-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ATANARJUAT／THE　FAST　RUNNER", "氷海の伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 172, "北アメリカの最北端である北極圏に住むイヌイットたちの間で、数百年に渡って語り継がれる神話を映画化。イヌイット人スタッフにより、イヌイット語で仕上げられた。これまであまり知られることのなかった彼らの考え方や生活が、壮大なドラマを通して描写され、背景に広がる美しい自然も深い感動を呼ぶ。", "2001-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lady Killers", "レディ・キラーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "55年のイギリス映画「マダムと泥棒」をコーエン兄弟がリメイク。トム・ハンクス扮する天才的犯罪者が、上品な紳士を装ってミシシッピ河畔の下宿家の下宿人になるが、彼の真の目的は近所のカジノ船の金庫。教会音楽の研究家だと称して仲間達を集め、借家の地下室から金庫までトンネルを掘る作業に取りかかるが、家主の老婦人が思わぬ障害となって立ちはだかる。ゴスペル中心の南部音楽はT・ボーン・バーネットが担当。", "2004-05-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEOWULF", "ベオウルフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "テクノロジーが崩壊し、一見中世を思わせるような荒涼たる近未来。辺境の地に城砦内の人々を餌食にする魔物が住んでいた。そこに現れたのが勇者ベオウルフ。彼は城砦のなかに入ると、死闘の末に魔物を退治するが……。「ハイランダー」シリーズのC・ランバート主演のSFバイオレンス。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録・新宿の顔〈ジュクのつら〉／新宿愚連隊物語 第１部", "実録・新宿の顔〈ジュクのつら〉／新宿愚連隊物語 第１部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "混乱する戦後の新宿を舞台に、伝説的な愚連隊として数々の武勇伝を残した実在の男、加納貢の青春時代を描いたドラマ。前編・後編にあたる第１部と第２部の２本立てで同時公開された。混乱期の新宿。どの組織にも属さず自分たちの流儀を通し、愚連隊連中から慕われる加納と安藤。そんな二人の前に、新宿進出をたくらむ関東曽根一家が現れたことから、不穏な空気が流れはじめ……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Escape from New York", "ニューヨーク1997", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "　1997年、ニューヨーク・マンハッタン島は犯罪者たちを収監する巨大な監獄となった。そこに不時着した大統領が街を支配するギャングによって拉致されるという事件が起こる。当局は大統領救出を画策し、かつて優秀な兵士だった犯罪者スネークを現地に送り込む。体内に爆薬を仕掛けられたスネークは逃げることもできない。やがて彼は協力者とともに大統領を助け出すが……。アウトロー・ヒーローが繰り広げる決死の救出行を描く。95年には続編も製作された。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スリ", "スリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "名匠、黒木和雄が「浪人街」から10年ぶりに手掛けた人間ドラマ。挫折からはいあがろうとする男と、彼を取り巻く人々の孤独や迷いをリアルに描き出す。かつてスリでならした海藤は、今では酒に溺れ落ちぶれていた。そんな彼のもとへ、同居する愛弟子の若い女性レイに憧れる青年、一樹が弟子入りにやって来る。彼を育てることで自身も再生を図る海藤は、トレーニングを重ね、忘れかけていた鋭い勘とテクニックを取り戻していくが……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PORTRAITS OF INNOCENCE", "サイコ・アイ　売春婦連続惨殺事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "有名写真家のジョージは、売春婦のありのままの姿を撮影した写真集“無垢な肖像”を出版する。ところが、その写真集のモデルになった売春婦たちが、次々に惨殺されるという事件が起こる。しかも犯人は殺害後、すべての被害者の血みどろの遺体を撮影しているのだ。事件を担当しているハンセン刑事は、ジョージに嫌疑をかけ彼を尋問する。ふとしたことでジョージと知り合った駆け出し弁護士のエイレンは、そんなジョージの疑いを晴らすために奔走する。そして、彼女は驚くべき真相を突き止める。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("御存じ右門 護る影", "御存じ右門 護る影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "ご存じ“むっつり右門”こと近藤右門の推理が冴える戦時中の時代劇。松平伊豆守の隠密が次々と謎の死を遂げて、右門は事件の究明にあたる。時同じくして浅草では南蛮渡来の魔法の箱を呼びものにした曲芸一座が大評判をとっていたが……。戦後に「右門捕物帖護る影」と改題されて再公開もされている。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A FEW DOLLARS MORE FOR DJANGO", "無宿のプロガンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「地獄から来たプロガンマン」「嵐を呼ぶプロ・ファイター」などのマカロニ俳優A・ステファンが、獲物の眉間を打ち抜くクールなバウンティ・ハンターに扮した西部劇。鮮やかな早撃ちで強盗一味を倒した賞金稼ぎのリーガンが、次にモンタナを荒らす強盗団に狙いを定める。そんなとき彼は、とある町の新任の保安官と間違えられ……。", "1966-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Barbary Coast", "バーバリー・コースト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ゴールドラッシュに沸く1849年のカリフォルニア。メアリーは金鉱を当てたダンと結婚するためニューヨークから遠路到着するが、ダンは暗黒街バーバリー・コーストの賭博場ベラドンナで全財産を失った上、場主のシャマリスに殺されていた。失意のメアリーは町に留まり、ベラドンナのルーレット台を受け持つこととなるが……。ホークスらしさは希薄だが、「暁の偵察」以来最大のヒット作となった。", "1936-03-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Total Balalaika Show", "トータル・バラライカ・ショー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "1993年12月23日、ヘルシンキ。トンガリリーゼントのロックンロールバンド、レニングラード・カウボーイズと、旧ソ連の退役軍人らによる大合唱団、アレクサンドル・レッド・アーミー・コーラス・アンド・アンサンブルがジョイント・ライブを敢行した。ロックンロールあり、民族音楽ありのその歴史的ライブを収録したドキュメンタリーフィルム。製作・監督は「レニングラード・カウボーイズ」シリーズのアキ・カウリスマキ。", "1994-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Little Drummer Girl", "リトル・ドラマー・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "ジョン・ル・カレの同名スパイ小説を、名匠ジョージ・ロイ・ヒル監督がダイアン・キートンを主演に迎えて映画化。ヨーロッパ各地で頻発する、ユダヤ人を狙ったパレスチナ・ゲリラによる爆弾テロ。その報復のためにイスラエル諜報機関がスパイとして選んだのは、イギリス人女優のチャーリーだった。彼女は諜報機関のジョセフという男性への恋心から仕事を引き受け、ゲリラのリーダーの弟の恋人として組織に潜入するのだが……。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瀬降り物語", "瀬降り物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "定住せず、日本中の山河を生きる場所として漂泊の中で暮らす山の民の、大地に根ざした生き方を通して、人間のあり様を見つめるサバイバル・ロマン。山の民の習俗・差別・戦争・自然と人間・男と女などのテーマが興味深く描かれている。萩原健一が好演。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒部の太陽", "黒部の太陽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 190, "黒部川上流に第４発電所を建設するべく、関西電力は社運をかけてダム建設に取り組むことになった。現場責任者の北川の熱意に負けて、父親に代わってトンネル掘りの指揮をとることになった設計技師の岩岡だったが、増える一方の犠牲者と度重なる事故に頭を抱える毎日。それでも北川共々、難所を突破すべく、技術と努力の限りを尽くすのだった……。石原裕次郎が設計技師に扮した骨太な人間ドラマ。黒四ダムの完成に尽力した人々の姿を、社会派の熊井啓が見事に描ききった。現場責任者を演じた三船敏郎の存在感も素晴らしい秀作だが、石原の遺志により現在までビデオ化に至っていない。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　仮面ライダー電王　俺、誕生！", "劇場版　仮面ライダー電王　俺、誕生！", "", "", "http://image.eiga.k-img.com/images/movie/34099/315x210.jpg?3714", 0, "“平成仮面ライダー”シリーズの第8弾「仮面ライダー電王」の劇場版。時空を旅する列車“デンライナー”に乗って未来からの侵略者イマジンに立ち向かう仮面ライダー電王の活躍を描く。時の列車ばかりを狙う強盗団によりデンライナーが強奪されてしまう。電王たちは、すべての時間を支配できるという“神の列車”を狙う強盗団のリーダー・牙王を追い、恐竜時代、西部開拓時代、古代エジプト、戦国時代など様々な時代を駆け巡る。", "2007-08-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("へんげ", "へんげ", "", "", "http://image.eiga.k-img.com/images/movie/57649/315x210.jpg?3263", 54, "「大拳銃」（2008）でゆうばり国際ファンタスティック映画祭、ぴあフィルムフェスティバルの双方で審査員特別賞を受賞した新人・大畑創監督が描く不条理ドラマ。静かな住宅街に暮らす若い夫婦は、夫を襲う奇妙な発作に悩まされていた。体を弓なりにしならせ、野獣のような咆哮（ほうこう）をあげる夫を不安に思いながらも介護する妻だったが、謎の症状は次第に悪化していき、ついに夫の体が不気味に変態を始める。", "2012-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Butch Cassidy and the Sundance Kid", "明日に向って撃て！", "", "", "http://ecx.images-amazon.com/images/I/61IVW7eMNCL._SL210_.jpg", 110, "実在した2人のアウトローをモデルに、彼らの自由奔放な生きざまをユーモラスかつシニカルに描いた青春西部劇の傑作。19世紀末のアメリカ西部。列車強盗で大金を手にしたブッチ・キャシディとサンダンス・キッドは、新たな夢を追い求めて南米ボリビアへと向かう。彼らはここでも銀行強盗を繰り返し、逃避行を続けるが・・・・・・。主人公のブッチをポール・ニューマン、サンダンスをロバート・レッドフォードが演じる。", "1970-02-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラッコ物語", "ラッコ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "パンダ、コアラと並び、アイドル動物のなかでも高い人気を誇るラッコの生態を追ったドキュメンタリー。アラスカの自然をバックに、TV『わくわく動物ランド』の永田貴士が演出にあたり、斉藤由貴らが吹き替えを担当。ラッコを擬人化してほのぼのと描く。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こどものそら", "こどものそら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1985年からスタートした、札幌の学童保育“つばさクラブ”。ここで、小学校１年生から６年生まで障害児も健常児も一緒に遊ぶ様子を追いながら、子供たちの生き生きとした表情を捉えたドキュメンタリー。遊びに夢中の子供たちを映した短編「放課後」と、６年生が挑戦したサイクリング旅行の中編「自転車」、そして子供たちをとりまく社会背景にも迫った58分の力作「雪合戦」の３本で構成されている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROM THE TERRACE", "孤独な関係", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "1946年のフィラデルフィア。父の会社を継がず、自分で航空会社を始めたアルフレッドは仕事一筋で家庭を顧みない。妻との仲も急速に冷めていくが、ふとしたきっかけから知り合ったナタリーという清純な娘との出会いにより、彼は真実の愛に目覚める。J・オハラの小説『テラスから』の映画化。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INSPECTOR CLOUSEAU", "クルーゾー警部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「ピンク・パンサー」シリーズでおなじみのクルーゾー警部の活躍を描いたコメディ映画。珍妙な秘密兵器を使い、強盗一味“マザー”に、一人立ち向かう彼であったが……。ピーター・セラーズに代わって、警部に扮するA・アーキンの好演が印象的。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃える大陸", "燃える大陸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "オーストラリアを舞台に、渡哲也と松原智恵子のコンビで描く、ムード・アクション調の青春映画。イラストレーターの敬一は、デパートのポスター製作のため、オーストラリアに赴く。そこで彼は、競走馬を持っている美女、冴子と会った。敬一は彼女に惚れるが、冴子は、製鉄所の社長に求婚されていて……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("身も心も", "身も心も", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "「赫い髪の女」や「Wの悲劇」などで知られる脚本家・荒井晴彦の監督デビュー作。実験的な手法で綴られた鈴木貞美の同名小説をもとに、中年男女４人の愛憎劇を丹念な描写で紡いでいる。仕事のこないシナリオライター・善彦は辞書校正のアルバイトを、友人の良介の留守宅で行うことになった。そこに、良介の妻・綾に呼ばれた麗子もやって来る。二人は初対面だが、ある共通点があった。かつて善彦は綾と、麗子は良介と、関係があったのだ……。ふられた者同士が不在の夫婦を演じるという屈折した愛情表現。その舞台劇のように緊迫した心理ゲームを捉えた前半から、映画は４人それぞれが１対１で向き合うシークエンスを挟み込みつつ、やがて物語の核心に迫っていく。リアリティーみなぎるセリフの数々、主演４人の静かで激しい演技も素晴らしいが、なんといっても演出が見事。奇をてらうことなく、過去にとらわれた者たちのどうしようもない感情をすくい上げている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REVENGE OF THE RADIOACTIVE REPORTER", "ドロドロ・モンスター!!～放射線レポーターの復讐劇～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "原子力発電所の放射能漏れの事実を突き止めた記者マイクは、電力会社へ乗り込み、社長を叫弾しようとするが、逆に罠にはまり、核廃棄物処理場のプールに落ちてしまう。しかし、マイクは生き延び、怪物となって社長一派への復讐を始める。怪物となってよみがえった男の復讐劇を描いたホラー・コメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本誕生", "日本誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 180, "東宝が1000本製作記念映画として、稲垣浩監督、円谷英二特技監督、三船敏郎以下オールスター・キャストによって、日本の古来の神話を映画化した超大作。イザナギ・イザナミの国造りにはじまる神々の物語の中から、オトタチバナヒメとの悲恋に彩られた、ヤマトタケルノミコトの波乱の一生を中心に、高天が原の神々の饗宴、ヤマタノオロチ退治などが描かれ、クライマックスに天変地異の大スペクタクルが展開する。有名なエピソードが次々に登場するが、構成が単調でエピソード間の相乗効果を生まず、散漫な印象が残るのは否めない。しかし、円谷英二の集大成ともいうべき特撮の数々は見ごたえ十分。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A YEAR OF THE QUIET SUN", "太陽の年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ポーランドの代表的監督の一人ザヌーシの秀作。第二次大戦直後、荒廃したポーランドの小さな街で出会った中年のポーランド女性とアメリカ兵の、清らかで悲しい恋の物語。言葉の通じぬ男女が、互いに見つめ合う時の瞳の中に揺らめく情熱の炎が印象的。", "1984-01-01", "7,42,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DADDY LONG LEGS", "足ながおじさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "アメリカの女流作家G・ウェブスターの名作『足ながおじさん』を原作にしたミュージカル大作。甘いメロディーと華麗な踊りがロマンティックな気分にさせてくれる恋物語だ。見も知らぬ“足ながおじさん”に寄せる孤児の淡い恋慕を優雅な踊りと歌でムーディーに表現した名編。孤児のジュリーに「巴里のアメリカ人」のL・キャロン、“足ながおじさん”にはミュージカルの名優F・アステアがそれぞれ扮し、G・ネグレスコが監督にあたっている。アカデミー賞数部門にノミネートされたが、惜しくも受賞は逸した。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Konyec", "人生に乾杯！", "", "", "http://image.eiga.k-img.com/images/movie/54459/315x210.jpg?3854", 107, "運命的な出会いで結婚したエミルとヘディも今では81歳と70歳になり、恋をしていた頃のことなど忘れ、細々と平和に暮らしていた。しかし、そんな2人の生活も年金だけでは足りず、借金取りに出会いのきっかけとなったダイヤの指輪を担保にされてしまう。高齢者に厳しい社会に怒りを覚えたエミルは、紳士的に郵便局を強盗することに。高齢者の生活をユーモアたっぷりに描いたハンガリー映画。", "2009-06-20", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼくんち", "ぼくんち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「顔」の阪本順治監督が、西原理恵子の同名マンガを映画化。貧乏な少年兄弟を養うことになった風俗嬢を、観月ありさが威勢よく好演し、しみじみと元気が出てくる人情ドラマとなった。さびれた漁村で貧しいながらも、それなりに平和に生きてきた一太と二太の兄弟。ところがある日、買い物に出て行ったきり半年も行方知れずだった母・今日子が、離れて暮らしていた風俗嬢の姉・かの子を連れて突然帰ってくる。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガリバーの宇宙旅行", "ガリバーの宇宙旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "みなし子のテッドは、ある晩人形の軍人、野良犬のマックとともに、遊園地の宇宙館に潜り込む。ところが夜警に追われ、ロケット花火で宇宙へ飛び出してしまう。そこで『ガリバー旅行記』のガリバー老人と出会った一行は、ロケットで“青い希望の星”を目指して宇宙の旅へ。しかし青い星は、その国民たちが作ったロボットに支配されていて、ガリバー一行はさらわれた星の姫の救出のため、青い星に乗り込んでいく。ロボットの弱点がただの“水”だったりと、ユニークなアイデアが盛り込まれたオリジナル・ファンタジー。フランスの名作アニメ「やぶにらみの暴君」に影響された部分もあるが、根強い人気に支えられている佳作。テッド少年とお姫様を、今は亡き坂本九と、当時のアイドル・本間千代子が吹き替えている。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彌次喜多道中記", "彌次喜多道中記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "江戸中を荒らしまわっている怪盗・鼠小僧次郎吉は追手を避け、旅に出る。一方、遠山金四郎も跡目相続を嫌って、旅に出ていた。二人はひょんなことから、弥次さん喜多さんと間違われてしまう。次々と巻き起こる珍騒動を、ミュージカル風に味付けした喜劇。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スーパー・ジャイアンツ／ 宇宙艇と人工衛星の激突", "スーパー・ジャイアンツ／ 宇宙艇と人工衛星の激突", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 39, "地球征服をたくらむ“黒い衛星”は、原爆ロケットを地球に発射するが、スーパー・ジャイアンツに阻止される。黒い衛星の本拠地・大人工衛星は宇宙艇の体当たりにより壊滅。スーパー・ジャイアンツは博士たちを救い出す。新東宝特撮陣苦心の特撮が見もの。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君の好きなうた", "君の好きなうた", "", "", "http://image.eiga.k-img.com/images/movie/57049/315x210.jpg?4682", 100, "「花とアリス」の郭智博と本作が映画初出演のモデル・山口尚美の主演で男女の友情をテーマに描く青春ストーリー。結婚を決めていた恋人から別れを告げられた家具職人の聡介は、突然上京してきた大学時代の友人・知加子と同居生活を始めることになる。2人の距離は徐々に友だち以上のものに縮まっていくが、知加子には聡介に言えないある秘密があった。監督は2008年SKIPシティ国際Dシネマ映画祭で短編部門奨励賞を受賞し、今回が長編デビューの柴山健次。篠原哲雄が監修を務める。", "2011-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stranded: I Have Come from a Plane That Crashed on the Mountains", "アライブ　生還者", "", "", "http://image.eiga.k-img.com/images/movie/54179/315x210.jpg?3456", 113, "1972年、遠征に向かうラグビーチームの若者45名を乗せたウルグアイの軍用機がアンデス山脈に墜落。惨事から10日後、その絶望的な状況から捜査は打ち切られたが、生存者たちは生還を諦めずに仲間の人肉を食して生き延び、遂には救出された。氷点下の雪山で72日間を生き延びた16人の生還者、彼らと事故被害者の遺族の交流を描いたドキュメンタリー。", "2009-04-11", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃色のジャンヌ・ダルク", "桃色のジャンヌ・ダルク", "", "", "http://image.eiga.k-img.com/images/movie/55219/315x210.jpg?3071", 105, "反戦アート集団「桃色ゲリラ」のリーダー・増山麗奈のドキュメンタリー。自らの母乳を使った絵画や、女性の挑発的な裸体を描く「ネオ春画」など、新進気鋭の画家としても活動する増山は、2003年のイラク戦争開戦時にゲリラ集団「桃色ゲリラ」を結成し、アートを交えたデモ活動などを積極的に行っている。過激な活動家として知られる増山の、2児の母としての穏やかな素顔にもカメラが迫る。", "2010-03-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("She Hate Me", "セレブの種", "", "", "http://image.eiga.k-img.com/images/movie/33659/315x210.jpg?4652", 140, "「25時」「インサイド・マン」のスパイク・リー監督が、レズビアンに精子提供をすることになった青年の数奇な運命を、社会風刺をちりばめて描いた問題作。勤め先の大企業を内部告発したことで解雇されてしまったエリート黒人ジャックは、レズビアンの元彼女から“種付け”の依頼を受ける。やがて彼のもとには、優秀な“種”を求める女たちが集まり始め……。主人公ジャックを「ミリオンダラー・ベイビー」のアンソニー・マッキーが演じる。", "2006-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Candy", "CANDY", "", "", "http://image.eiga.k-img.com/images/movie/1029/315x210.jpg?4730", 124, "テリー・サザーンの同名ブラック・コメディ小説を、スウェーデン出身のミス・ティーンインターナショナル、エバ・オーリン主演で映画化。純真な女子高生キャンディは、彼女とエッチしたがる男性たち全員とエッチしてあげるのだが。監督・脚本は俳優でもあるクリスチャン・マルカンとバック・ヘンリー。マーロン・ブランド、リチャード・バートン、ジェイムズ・コバーンなど大スター大挙出演。キュートな60年代ファッションも満載。", "1969-01-01", "4,2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DISHONORED", "間諜X27", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "トーキー初期に数々の名作を生み出した巨匠スタンバーグ、M・ディートリヒのコンビによるサスペンスフルなメロドラマ。第一次大戦中のウィーンでスパイ活動を命じられた女が、“X−27号”というコードネームを与えられ反逆者の身辺調査を開始する。しかし彼女は仮面舞踏会で出会ったロシア軍大佐と恋に落ちてしまい、自らが反逆罪に問われていく……。宿命のヒロインに扮したディートリヒのこの世ならぬ妖艶な美しさを、とことん引き出そうと試みた映像はため息もの。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAY OF THE PANTHER", "ブレードチェイサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "兵器には頼らず鍛え抜かれた鋼鉄の肉体だけを武器にする香港の特別捜査官ジェイソン・ブレード。香港からオーストラリアにまたがる巨大麻薬組織を壊滅させるべく殴り込みをかけた彼の活躍を描く。本格的な格闘シーンが満載のノンストップ・アクション。", "1987-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DOMINO KILLINGS", "ドミノ・ターゲット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "名狙撃兵ロイは、20年の刑で服役中に組織の男マーヴィンの力で、同房のスピベンタとともに脱獄。愛人エリーとの生活を保証する代償に、政府要人の暗殺を依頼される。だがこの条件を拒否したロイは、エリーを殺され、命を狙われる。重厚な政治サスペンス。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRINCES　ET　PRINCESSES", "プリンス＆プリンセス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "初長編アニメ「キリクと魔女」で高い評価を得たM・オスロが、影絵の手法を採り入れたアニメーション。プリンスとプリンセスの６つの物語を、中世の版画や北斎の浮世絵、エジプトのパプルスなどを取り込んだユニークな手法で繰り広げていく。光と影が織り成すファンタスティックな映像、ユーモアと愛に満ちた独創的な世界観に魅了される。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVNLY BODIES", "ヘブンリー・ボディーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "エクササイズ・スタジオ“ヘブンリー・ボディーズ”を経営するサマンサが、財閥をバックに持つライバル校と決着をつけるために“24時間マラソン・エクササイズ”に挑戦。そんな彼女が見事に勝つまでを、派手な踊りと音楽を織り交ぜながら描いた青春映画。", "1984-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秋瑾／QIU　JIN　A　REVOLUTIONARY", "炎の女・秋瑾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "清朝末期に生まれた女性革命家・秋瑾の生涯を映画化。清の将来に絶望した秋瑾は夫と子を残し単身日本へ留学。帰国後、革命を呼びかけ、蜂起を計画するが……。原作は1990年に死去した中国映画界の重鎮、シア・エン。日本ロケ・シーンも見もの。", "1983-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("漂流", "漂流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "草木のない無人の火山島に漂着して、母の“一人で生きよ”という遺言を胸に生き抜いた男が、13年振りに故国の土を踏むまでの孤独との戦いを、漁師の実話をもとにして描いた作品。期限なしの製作進行で、膨大な時間と製作費を費やした。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金髪スリーデイズ35℃", "金髪スリーデイズ35℃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "初監督作「サマーヌード」で注目を浴びた飯塚健監督によるロード・ムービー。駅員、エリートサラリーマン、ホステス、タクシードライバー、女子高生のそれぞれ違う道を歩む５人が、みんなで髪を金髪に染めて、夏の３日間共同生活を送ることになり……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妻はフィリピーナ MY WIFE IS FILIPINA", "妻はフィリピーナ MY WIFE IS FILIPINA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "フィリピン女性と結婚した貧乏な映画学校生が、２年間にわたる自身の悲喜こもごもの生活ぶりをまとめたドキュメンタリー。27歳の寺田監督が、フィリピン・パブで働く同い年のテレサとの結婚を決めたのが1991年。典型的なサラリーマン家庭である寺田の父・母・妹は、水商売で外国人の嫁にそれぞれ困惑するが……。男と女、日本人とフィリピン人などの違いを越えて、二人が次第に“夫婦”になっていく過程が面白い。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Apartment", "アパートの鍵貸します", "", "", "http://ecx.images-amazon.com/images/I/51K8q-TH18L._SL210_.jpg", 125, "　ビリー・ワイルダーの代表作ともいえるシチュエーションコメディ。10年温めたアイデアで、時代の流れにあわせてようやく映像化にこぎ着けた。出世と上司へのゴマスリのため、自分のアパートを愛人との密会場所として重役に提供するバクスター。お調子者の彼は出世街道に乗り意気揚々とするが、思いを寄せていたエレベーターガールまでもがアパートを出入りするひとりと知り、愕然とする。ワイルダーはこの作品で念願のアカデミー監督賞を手にした。", "1960-10-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("馬鹿まるだし", "馬鹿まるだし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "山田洋次が自分のスタイルを確立するきっかけになった作品。藤原審爾の原作は、以前にも滝沢英輔監督・森繁久彌主演で「安五郎出世」という題で映画化されたが、山田洋次版「馬鹿まるだし」に先だって、同じ1964年、山田の脚本で同題名のTVドラマが作られている。シベリア帰りの風来坊・松本安五郎は、瀬戸内海の平和な町にある浄念寺に転がり込む。寺には若くて美しい未亡人・夏子がいて、安五郎はひそかに思いを寄せる。ある日、ダイナマイトを持った脱獄囚が、人質を取って山へ逃げ込んだ。安五郎は夏子にいいところを見せようと、単身助けに向かったが、ダイナマイトが爆発し失明する。盲目になった安五郎が涙を流しながら、夏子に思いを語る長回しシーンでは、ハナ肇の熱演が光る。このキャラクターは、のちに“車寅次郎”へと受け継がれる。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("庄助武勇伝・会津磐梯山", "庄助武勇伝・会津磐梯山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "唄のごとく朝寝、朝酒、朝湯が大好きな小原庄助はその人柄を見込まれて、藩主に出仕を命じられる。主水、忠兵衛という居候を抱えた彼は宮仕えを決心。信姫に武術指南したり、幽霊退治をしたりと大忙し。そんな彼は今度は城内に潜入している幕府方、勤皇方の密使の目付役を命じられるが……。軽快なノリで綴られる時代劇。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O Mistério do Samba", "ミステリー・オブ・サンバ　眠れる音源を求めて", "", "", "http://image.eiga.k-img.com/images/movie/54770/315x210.jpg?1249", 88, "ブラジルの名門サンバチーム「ヴェーリャ・グアルダ・ダ・ポルテーラ」の人々にスポットを当てた音楽ドキュメンタリー。ヴェーリャ・グアルダの知られざる名曲を集めたアルバム「Tudo Azul」をプロデュースして話題を呼んだ人気女性歌手マリーザ・モンチが案内役を務め、ポルテーラの長老たちが長年に渡って築きあげてきたサンバの歴史を解き明かしていく。", "2009-10-03", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　空の境界　第三章　痛覚残留", "劇場版　空の境界　第三章　痛覚残留", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "奈須きのこの同名小説を原作に、万物の死を見ることのできる能力「直視の魔眼」を持つ少女・両儀式の苦悩と戦いを描いた人気アニメ劇場版「空の境界」7部作の第3章。類まれな捜索の才能をもつ黒桐幹也はある雨の夜、闇にうずくまっていた1人の少女と出会う。少女は幹也がいずれ探すことになる運命にあったが、幹也はまだそのことを知るよしもなかった。", "2008-02-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花嫁吸血魔", "花嫁吸血魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ある女性が、彼女に恨みを持つ友人たちのせいで死亡した。その美貌のヒロインが恐ろしい姿の吸血魔に変わり果て、次々と友人たちに復讐していくというサスペンス・ホラー。吸血魔に扮した当時の清純派女優、池内淳子の大熱演には目を見張るものがある。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京・オブ・ザ・デッド 3日", "東京・オブ・ザ・デッド 3日", "", "", "http://ecx.images-amazon.com/images/I/21XWH9YJTVL._SL210_.jpg", 45, "「JUNK FOOD」「リムジンドライブ」の山本政志監督が5年ぶりにメガホンを取った和製ゾンビホラー。両親が旅行に出かけたのを見計らい、恋人のユキを家に招いたノブ。2人は食材を調達しに近所の八百屋を訪れるが、さっきまで優しかったはずの八百屋のおばさんが突然、豹変して襲いかかってくる。なんとか家にたどり着いた2人を、今度は新聞の集金人が襲う。気づくと町は、凶暴化した人々がうごめく地獄と化していた。", "2006-04-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若い人", "若い人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "石坂洋次郎の有名な原作の４度目の映画化作品。ここでは舞台を長崎に設定することによって今までとは異なった味わいを出すことに成功している。監督は「挽歌」「あいつと私」などの河崎義祐。私生児で奔放な女生徒・江波恵子には、当時アイドル歌手として人気を集めた桜田淳子が扮して好演。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAL JOEY", "夜の豹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "J・オハラ原作でリチャード・ロジャース、ロレンツ・ハートのコンビがミュージカル化した作品。クラブの芸人ジョーイ、コーラスガール、そして未亡人の３人の恋の駆け引きを美しい歌曲にのせて描く。F・シナトラの粋な姿、シブい年増の魅力をふりまくR・ヘイワース、初々しいK・ノヴァク。誠に楽しい一編だ。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RAINBOW", "レインボウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ヴィクトリア朝時代のイギリス。田舎町の平凡な家庭に生まれながら、自由奔放な性格を持った少女アーシュラは性のモラルの厳しい時代の中で人間的成長を遂げていく。『チャタレイ夫人の恋人』などで知られるイギリスの文豪D・H・ロレンスの小説を、鬼才K・ラッセルが映画化。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・知床慕情", "男はつらいよ・知床慕情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "舞台は北海道・知床。知床の武骨な獣医はやもめ暮らしだったが、東京から結婚生活に破れた娘が帰って来た。父娘と知り合った寅は、この町のスナックのママと獣医の橋渡しを画策する一方で、娘の方にホの字となる。野外バーベキュー・パーティの席で、ママが店をたたんでこの町を去る決心を告げ、それに反対する獣医がその理由を問われて“俺が惚れてるからだ”と言い放つシーンは、さすが三船と思わせる。久々の出色の一本。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海と夕陽と彼女の涙　ストロベリーフィールズ", "海と夕陽と彼女の涙　ストロベリーフィールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "美しい自然と昔ながらの街並みが広がる和歌山県田辺市を舞台に、4人の女子高生が織りなすドラマを郷愁たっぷりに描いた青春ファンタジー。17歳の女子高生・夏美のクラスメイトの3人が、交通事故で命を落としてしまう。3人は幽霊となり48時間だけこの世に戻ってくるが、彼女たちの姿を見ることができるのは夏美だけだった。クラスでは決して仲が良かったわけではない4人は、反発しあいながらも友情を深めていく。", "2006-09-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まむしの兄弟・懲役十三回", "まむしの兄弟・懲役十三回", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "今回は昭和10年頃の浅草六区興行街が舞台。13回目の懲役を終えたゴロ政と勝は、ムショで兄弟分になった浅草南村一家の弥之助を頼って東京へ向かう。ところが、弥之助は吉原東竜会の岩淵に殺され、怒った二人は東竜会へ殴り込む。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神龍小虎闖工湖／CALL ME DRAGON", "帰って来たドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "暴利をむさぼるボスが支配する町に、ドラゴンがやって来た。狙いはボスの持つ魔法の真珠。ボスに雇われたブラック・ジャガーと謎の女ミャオとの三つ巴の戦いが展開される。ジャガーを演じた倉田保昭は、すでに香港ではスターだったが、日本でもブレイクした。", "1973-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西遊記", "西遊記", "", "", "http://image.eiga.k-img.com/images/movie/1810/315x210.jpg?2335", 120, "高視聴率を記録したフジテレビ系ドラマ「西遊記」の劇場版。天竺を目指し旅を続ける三蔵法師一行は、恐ろしい妖怪・金閣と銀閣によって緑と財宝を奪われてしまった砂漠の国に立ち寄る。王宮に招かれ、妖術によって醜い亀にされた王と王妃の姿を目にした三蔵たちは、王女の頼みで金閣・銀閣を倒す旅に出るが……。香取慎吾、深津絵里らテレビ版でおなじみのキャストが出演するほか、金角に鹿賀丈史、銀角に岸谷五朗が扮する。", "2007-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("中山安兵衛", "中山安兵衛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "おなじみ中山安兵衛に嵐寛寿郎が扮した一編。とはいえ単なる活劇ではなく、人間、安兵衛の苦悩を描いている。菅野六郎左衛門と伯父甥の契りを結んだ安兵衛は、彼が高田馬場で決闘するのを知って駆けつけ相手を倒すが伯父は絶命する。英雄扱いされる安兵衛だったが、激しい無情感が彼を襲う。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEVEN CHANCES", "セブン・チャンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "チャップリンと並ぶ偉大なる喜劇王キートンの絶頂期の大傑作。その日の７時までに結婚すれば100万ドルの遺産が手に入ることになったジミー。彼は片っ端から女性にプロポーズするが、なかなか相手は見つからない。疲れて教会で居眠りしてしまうジミーだが、その頃、彼の友人が新聞に花嫁募集の広告を出していた。そして目が覚めたジミーの前には何百人という花嫁衣裳を着た女性が……。あわてて逃げ出すジミー。追いかける花嫁軍団。果たして結末は？ 見どころは何といってもラストのジミーと花嫁たちの追いかけっこから始まる、大アクション・シーンだ。逃げ出したジミーは町を駆け抜け、丘の斜面を走り下りるわけだが、そこに、大小なんと1500個もの落石が降りそそぐ。スタントなしで体を張ったアクションを何度も見せてきたキートンだが、その中でも最高のアクション・シーンといえるだろう。以前のタイトルは「キートンの栃面棒」。ビデオタイトルは「キートンのセブン・チャンス」。", "1925-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふぞろいな秘密", "ふぞろいな秘密", "", "", "http://image.eiga.k-img.com/images/movie/34020/315x210.jpg?1358", 93, "女優の石原真理子が自身の半生を赤裸々に綴り話題となった自叙伝「ふぞろいな秘密」を、石原自らがメガホンを取り映画化。スカウトをきっかけに女優になった石原マリコは、同じ事務所に所属するバンド“セーフティゾーン”の山置洋二と出会い、恋に落ちる。しかし山置は実は既婚者だった。やがて2人は不倫カップルとしてマスコミに追われるようになり……。20代の石原を演じるのは「死びとの恋わずらい」の後藤理沙。", "2007-06-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUT OF TIME", "タイムリミット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "演技派D・ワシントン主演のサスペンス。デンゼル扮する警察署長マットが、不倫相手の殺害事件に遭遇。彼女の保険金の受取人は彼になっていたうえに、事件の捜査に当たるのは殺人課の刑事である彼の妻。がんじがらめの状況下で、主人公が容疑を晴らそうと奔走する。スリルはもちろん、オフビートなユーモアも魅力的。フロリダの小島、バニアン・キーの警察署長のマットは、夫の暴力に苦しむ人妻アンと深い関係にあった。ある時、アンががんに冒されていることを知ったマットは、その治療費にあてるため、署に保管してあった大金を持ち出す。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀のエンゼル", "銀のエンゼル", "", "", "http://ecx.images-amazon.com/images/I/414krNUzNBL._SL210_.jpg", 110, "北海道で絶大な人気を誇り、バラエティー番組『水曜どうでしょう』のDVDも好評を博した鈴井貴之。「river」に続く監督３作目は、様々な人生が交錯する田舎町のコンビニを舞台にした真冬の物語だ。北海道で暮らす監督だからこその、ポストカードにはない灰色の冬景色のなかで描かれるのは、ささやかな幸せを探してジタバタする人間の愛しさ。情けなくも憎めない父親を映画初主演となる小日向文世がチャーミングに演じている。北海道でコンビニを営む昇一は、店を妻に任せて気ままな毎日を送っていた。ところが妻が交通事故で入院。慣れない深夜の勤務に就き、18歳の娘とも向き合うことになり……。", "2004-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dirigible", "大飛行船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "元アメリカ海軍飛行士のフランク・W・ウィードによる原作を、フランク・キャプラ監督が映画化。海軍飛行船の船長ブラドンと飛行士フリスキーは大親友。しかし、ブラドンはフリスキーの妻ヘレンに密かに想いを寄せていた。南極探検の計画を企てたブラドンはフリスキーも連れて行こうとするが、夫を心配するヘレンに頼まれ、彼をメンバーからはずしてしまう。フリスキーはこれに腹を立て、ブラドン一行とは別に南極へ向かうが……。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("U-571", "U-571", "", "", "http://image.eiga.k-img.com/images/movie/1720/315x210.jpg?3910", 116, "1942年。第二次大戦下のヨーロッパ戦線で、連合軍はドイツの高性能潜水艦Uボートと暗号器「エニグマ」によって翻弄され、甚大な被害を受けていた。事態の打開のため、米海軍の巡洋潜水艦S-33にある極秘指令が下される。それは大西洋に停泊しているUボート、U-571を奇襲し「エニグマ」を奪取するというもの。だが作戦が成功したかに思えた瞬間、S-33は敵の援軍から撃沈され、辛うじて生き残ったタイラーら数人のS-33乗組員はU-571に閉じ込められてしまう。", "2000-09-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヴァンパイア・ストーリーズ CHASERS", "ヴァンパイア・ストーリーズ CHASERS", "", "", "http://image.eiga.k-img.com/images/movie/56280/315x210.jpg?1570", 75, "サークル仲間たちと河原でバーベキューを楽しんでいた大学生のシュウだったが、そこへバンパイアのアイが現れ、現場は惨劇の舞台となる。アイに襲われたことでバンパイアになってしまったシュウは、アイを追撃するが……。人気若手俳優を起用し「BROTHERS」「CHASERS」の2本同時に製作されたオリジナルのバンパイア映画。「CHASERS」主演は「カフェ代官山」シリーズの馬場徹。ヒロインに「モーニング娘。」の田中れいな。", "2011-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Vintner's Luck", "約束の葡萄畑　あるワイン醸造家の物語", "", "", "http://image.eiga.k-img.com/images/movie/55580/315x210.jpg?3407", 126, "「クジラの島の少女」で国際的に高い評価を得た女流監督ニキ・カーロが、ニュージーランドの作家エリザベス・ノックスのベストセラー小説を映画化。19世紀フランスのブルゴーニュ地方を舞台に、ワイン造りに全てをかける男と取り巻きの女たち、そしてワインづくりを導く天使が織り成すドラマを描き出す。「ある子供」のジェレミー・レニエ、「マイレージ、マイライフ」のベラ・ファーミガら実力派俳優による競演も見どころ。", "2010-10-23", "31,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OTHELLO", "オセロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "あらゆる障害を乗り越えて激情的な愛に燃える若き将軍オセロと美しいデズデモーナ。シェイクスピア原作の愛のストーリーを、言葉を現代的表現に変え、若者にも感情移入のしやすいキャラクター設定を試みた意欲作。従来、黒塗りの白人俳優によって演じられてきた主人公オセロ役は、セクシーで野性的な黒人俳優でなければ表現できないと、L・フィッシュバーンが起用され、圧倒的な存在感を見せている。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Godfather: Part II", "ゴッドファーザーPARTII", "", "", "http://ecx.images-amazon.com/images/I/51WqmR6HRBL._SL210_.jpg", 200, "前作の二年後に作られた続編。亡き父のあとを継ぎ、コルレオーネ・ファミリーのドンになったマイケルの苦悩と復讐を、亡き父が少年時代からゴッドファーザーの地位を築きあげたエピソードをはさみながら描いた作品。人間描写に力点をおいたコッポラは、淡々とそしてドラマティックに描き、前作を上回る見事な映像詩を作り上げた。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPELLBOUND", "チャレンジ・キッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アメリカで80年続く単語のつづりを競う大会“全米スペル暗記大会”に挑戦する８人の子供たちと、その家族の悪戦苦闘を追ったドキュメンタリー。大会へ向けて高まる緊張感に興奮しながら、さまざまな葛藤を乗り越え、必死で勉強する子供とそれを支える親との家族愛をしっかり見せた秀作。参加総数900万人の全米スペル暗記大会。予選を勝ち抜き、決勝戦に進んだ249人。誰もが優勝を目指す中、８人の子供を追う。", "2005-05-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE VERLOBTE", "フィアンセ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "第二次世界大戦のさなか、ともに反ナチス組織に属し、婚約者でもあるヘラとライマース。そのヘラが、反逆予備罪でナチスに捕まり、10年近くの独房生活を強いられる。面会時間だけが唯一の心の支えになっていた二人だったが、ライマースも逮捕され……。戦争という状況のなかで、離れ離れになりながらも、いちずな愛を貫き通す男女の姿を描いた感動のメロドラマ。監督は当時の東ドイツ映画界きっての実力派G・リッカーとベテランのG・ライシュ。", "1980-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Somewhere", "SOMEWHERE", "", "", "http://image.eiga.k-img.com/images/movie/55730/315x210.jpg?4235", 98, "「ロスト・イン・トランスレーション」のソフィア・コッポラ監督が、父フランシス・フォード・コッポラとの思い出や、2児の母となった自らの経験を投影して製作。第67回ベネチア国際映画祭で金獅子賞を受賞した。米ロサンゼルスにあるスター御用達の有名ホテルを舞台に、派手なセレブライフをおくる映画スターのジョニー・マルコが、離婚した妻のもとで育った11歳の娘と再会し、人生を見つめ直す姿を描く。", "2011-04-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おぼろ駕籠", "おぼろ駕籠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "お正月興行の作品として、当時の大スターを起用して製作された作品。深川の信濃屋伝右衛門の寮で、女中が殺された。現場の状況から女中の幼なじみで、旗本の次男・小柳進之介が嫌疑をかけられる。進之介は無実を主張して逃げるが、家名を気にする身内のものたちから詰め腹を切らされようとしていた。一方、岡っ引きの亀蔵は現場で女物の紙入れを拾う。この紙入れは殺された女中と大奥に上がることを争ったライバル三沢のもの。これを知った夢覚和尚と本多内蔵介は真相の究明に乗り出す。伊藤監督の語り口は導入部の移動撮影から観客をひきつけ、緩急自在。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POSTMORTEM", "ポストモーテム　死への彩り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "死体をアートのように加工する猟奇連続殺人事件の行方を描いたサイコ・スリラー。刑事に嫌気がさし、ミステリー作家に転身したマクレガーは残虐事件の記憶をぬぐい去れず、グラスゴーに新天地を求めた。だが、そこで彼を待っていたのは、防腐処理を施した女性の他殺死体。新聞広告に事件解決のヒントを得たマクレガーは、神経をすり減らしながらも犯人を追う……。グラスゴーの曇った風景が、猟奇的なムードを盛り立てる。ビデオ発売時タイトルは「ポストモーテム　検死台」。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOW GREEN WAS MY VALLEY", "わが谷は緑なりき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "19世紀末、ウェールズの炭坑地帯のある村に住んでいた一家の運命を回想形式で綴った物語。「怒りの葡萄」に続いてD・F・ザナックが個人的に製作したフォードの文芸映画。当初サウス・ウェールズのオール・ロケが決まっていたが、第二次大戦のため、計画は放棄され、代わってサン・フェルナンド渓谷に80エーカーの村がそっくり建てられた。家族が次々に死に、姉は不本意に嫁ぎ、自分はいじめられるというように、語られる出来事は陰惨で不幸なことばかりだが、それを回想する主人公の記憶の光景はあくまで美しく、J・フォードの語りはリリカル。トーンを落とし、一部分だけ強い光をあてる画面作りで視覚的にも磨き抜かれている。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渇き", "渇き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "スケート場で知り合った大学生・俊男と激しい情事に溺れていく人妻・明子。ところが、ひそかに俊男に恋していた同級生の密告で、すべては夫の知るところとなり……。山本富士子が“自立する妻”というきわめて現代的な役を演じた文芸映画で、「処女宝」など女性心理の扱いに定評ある島耕二の監督作。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIVATE POPSICLE（LEMON POPSICLE IV）", "グローイング・アップ4　渚でデート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "イスラエル版“アメ・グラ”としてスタートした同シリーズの４作目。今回は、おなじみの青春３人組が無事高校を卒業。そろって軍隊に入隊し、女兵士に恋したり、ゲイ軍医に追い回されたりと、相変わらずのドタバタ青春編。1950～60年代のヒット曲が小気味良い。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AVALANCHE EXPRESS", "アバランチ・エクスプレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "重要機密を手に西側へ亡命しようとするソ連の要人をめぐって、東西情報部員が熾烈な戦いを繰り広げる。何といってもタイトルに示されている大がかりな雪崩が見もの。公開直前に急死したM・ロブスン監督及びR・ショウにとって最後の作品となった。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("中南海保鑢／THE BODYGUARD FROM BEIJING", "ターゲット・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「ワンス・アポン・ア・タイム・イン・チャイナ」シリーズで香港アクション映画の一方の雄となったリー・リンチェイが初めて現代活劇に挑んだ香港版「ボディガード」。北京のSP部隊のフイが香港の大豊グループ社長の恋人ミッシェルを護衛することになる。彼女は大豊グループと敵対するチョン一派の殺人事件を目撃し、チョンらに命を狙われていたのだった……。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("国東物語", "国東物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "戦国時代に大分地方に覇を唱えた武将・大友義鎮の生き方を通して、日本精神の深遠さを追求した格調高い名作。「鬼の詩」「月山」と佳作を連発した村野監督が前作「遠野物語」に続いて高山由紀子の脚本と組み、日本人とキリスト教の運命的な出会いを描く。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRADING PLACES", "大逆転", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "大金持ちの老紳士兄弟が、人格形成に重要なものは環境か素養か、の賭けをする。その犠牲となったのがハーバード大出身のエリートと貧しい黒人。フィラデルフィアの商品仲買会社を舞台に、二人の立場が入れ代わる。ラストのドンデン返しも痛快なコメディ。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El Regalo De La Pachamama", "パチャママの贈りもの", "", "", "http://image.eiga.k-img.com/images/movie/54940/315x210.jpg?1581", 102, "南米ボリビア・アンデス高地にあるウユニ塩湖周辺で暮らす先住民の家族を描くヒューマンドラマ。巨大な塩湖に堆積した塩を採掘して生計を立てる少年コンドリは、貧しいながら家族と幸せな日々を送っていた。そしてある日、コンドリは初めて父と3カ月にも及ぶ塩採掘のキャラバンに出ることになる。ニューヨーク在住の松下俊文監督が、ボリビアで6年の歳月をかけて製作した長編デビュー作。", "2009-12-19", "76,1,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAEA　GIRLS", "ガイア・ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "英国人女性のフェミニズム的な視点から、日本の女子プロレス団体、GAEA JAPANのレスラーたちの人間模様を捉えたドキュメンタリー。団体のリーダーである長与千種の力強い生き方、中堅選手、里村明衣子の苦悩、練習生、竹内彩夏のあまりに苛酷なデビューまでの道のりをカメラは描出。“根性”というものが確かに存在する世界をストレートに活写し、観る者の感情を揺さぶる。", "2000-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chocolate", "チョコレート・ファイター", "", "", "http://image.eiga.k-img.com/images/movie/54320/315x210.jpg?3655", 99, "「マッハ！」「トム・ヤム・クン！」で世界中の注目を集めたプラッチャヤー・ピンゲーオ監督が3年ぶりに放つアクション映画。驚異的な身体能力を持つ美少女ゼンは、母が末期の白血病を患っていることを知る。愛する母を救うため、ゼンはマフィアとの闘いに身を投じるが……。本作のために4年間の修行を積んだ新人女優ジージャーがスタントなしで繰り広げる超絶アクションは必見。日本人ヤクザ役で阿部寛が出演。", "2009-05-23", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beautiful Losers", "ビューティフル・ルーザーズ", "", "", "http://image.eiga.k-img.com/images/movie/53650/315x210.jpg?2698", 91, "90年代のアメリカ。社会からはみ出し、ストリートで退屈を持て余していた若者たちが、ニューヨークの一角に手作りの小さなギャラリーをオープンした。彼らが生み出す型にはまらないアートはやがて世界から注目されるようになり、ギャラリーはストリート・カルチャーの聖地となる。ハーモニー・コリン、マイク・ミルズ、トーマス・キャンベルら多くの才能を生んだムーブメントの軌跡をたどり、その魅力に迫ったドキュメンタリー。", "2008-08-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISSED", "キスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "屍体愛を題材にした、カナダの女流監督L・ストップケウィッチによる異色の恋愛ドラマ。幼い頃から“死”に強い興味を抱いていた若い女性サンドラは、その欲望を満たすべく葬儀屋に就職。彼女の興味は、少しずつ死体との性交に向けられていく。その一方で彼女は若い大学生と恋に落ちる。誠実な彼は、サンドラの奇癖を理解しようと努めるが……。センセーショナルなストーリー展開ながら決して下品にならず、タッチはきわめて繊細。官能美あふれるラブ・シーンが印象に残る。", "1996-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Bronzés Font du Ski", "レ・ブロンゼ／スキーに行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「髪結いの亭主 」「イヴォンヌの香り」で知られるパトリス・ルコント初期のコメディ作品で、フランスの演劇集団「スプランディド」と取り組んだ「レ・ブロンゼ／日焼けした連中」の続編にあたる。アルプスのスキーリゾートを舞台に、集まった人々をコミカルに描く。日本初公開時のタイトルは「人生模様」。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES MARMOTTES", "メランコリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "暮れも押し迫りクリスマスも近づいてきたある日、冬のバカンスを家族や友人たちと過ごそうといつものメンバーがシャモニーへと集まって来た。おしどり夫婦のシモンとフレデリック、シモンの弟で映画監督のマックス、彼の友人で売れない俳優のステファンとその恋人リュシー、そしてシモンとマックスの父であるレオと長年の愛人フランソワ……。一面銀世界のリゾート地を舞台に、６組のフランス人たちの恋愛模様が繰り広げられる。J・H・アングラード、J・ビセット、M・トランティニャン、A・エーメら豪華キャストが競演する。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アサシン", "アサシン", "", "", "http://image.eiga.k-img.com/images/movie/56610/315x210.jpg?1395", 100, "人気作家・新堂冬樹の同名小説が原作で、非情な暗殺者と少女の逃避行を描くノワールアクション。プロの暗殺者として生きる涼は、唯一の親友・俊一とともに組織の命令に従って冷徹に任務を遂行してきた。しかし、ある暗殺現場に居合わせた女子高生リオを、俊一の反対をおしきり救ったことから、組織の裏切り者として追われる身となる。監督は「芸者VS忍者」「ゴスロリ処刑人」の小原剛。主演はミュージカル「テニスの王子様」で人気の馬場良馬と久保田悠来。", "2011-10-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAGIC IN THE WATER", "マジック・ダイナソー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "父親と一緒に夏休みを過ごすために、カナダの湖にやって来たジョシュアとアシュレーの兄弟。その湖には恐竜オーキーが住むという伝説があり、最近も遭遇者が続出していた。父も兄も恐竜の存在を信じようとしない中、熱心にオーキー探しを続けるアシュレー。そしてある日、父のジャックがオーキーに出会い、突然童心を取り戻した。しかし伝説の恐竜は、湖に不法投棄された廃棄物のために瀕死の状態にあったのだ。しかも元凶の工場経営者マックは、ニセのオーキーを使って恐竜の噂を作り話に見せかけようとしているという。それを知った兄弟たちは……。“ネス湖伝説”と環境保護を巧みに融合した、現代的な恐竜映画。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市 THE LAST", "座頭市 THE LAST", "", "", "http://image.eiga.k-img.com/images/movie/54510/315x210.jpg?3278", 132, "勝新太郎、ビートたけしが演じてきた盲目の居合いの達人・座頭市にSMAPの香取慎吾が扮し、市の最期を描く「最後の座頭市」。旅の途中で妻を殺され傷心の市は余生を静かに暮らそうと帰郷し、かつて一緒に博打の旅に出た旧友の農民・柳司の家に居候する。だが、市の故郷は博徒の天道一家に支配されており、市は再び戦いの渦中に巻き込まれていく。共演に仲代達矢、反町隆史、倍賞千恵子。監督は「亡国のイージス」「闇の子供たち」の阪本順治。", "2010-05-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いこかもどろか", "いこかもどろか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "人気TV番組『男女７人夏物語』の明石家さんま、大竹しのぶ、それに脚本の鎌田敏夫のトリオがスクリーンに登場。暴力団の裏資金に手をつけた男と、ギャンブルで会社の金を使いこんだ女の珍道中が軽快なテンションで描かれる。福島、青森、北海道とロケを敢行、その景観も見もののジェット・コースター的ラブ・コメディ。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女であること", "女であること", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "川端康成文学の映画化。佐山弁護士と妻・市子は教養深い夫婦だが子供がない。家出してきた市子の旧友の娘の面倒をみることになったところから、波紋が広がっていく。ブルジョワ家庭とその周辺の人間模様を手堅くまとめている。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DDDにおける　池田爆発郎とwattの本名", "DDDにおける　池田爆発郎とwattの本名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "浅野忠信監督作品「トーリ」など、奇抜な傑作を輩出しているDDDレーベルから、またしても気鋭のクリエイター二人による斬新な映像作品が作られた。実写、CG、手書きアニメと、ジャンルを問わずシュールワールドを炸裂する池田爆発郎。人間の個性、教育、洗脳など重いテーマをスタイリッシュなアニメーションで綴るwatt。多彩なイマジネーションに圧巻だ。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paradise Now", "パラダイス・ナウ", "", "", "http://image.eiga.k-img.com/images/movie/33880/315x210.jpg?1245", 90, "イスラエル占領地ナブルスで暮らす2人のパレスチナ人青年が自爆テロへ向かうまでの48時間の葛藤や友情を描いた衝撃作。2006年度ゴールデングローブ最優秀外国語作品賞など数々の賞を獲得したが、第78回アカデミー賞外国語映画部門にノミネートされた際には、自爆テロ被害者の遺族たちからノミネート取り下げの署名運動が起きるなど大きな波紋を呼んだ。メガホンを取るのは、パレスチナ人のハニ・アブ・アサド監督。", "2007-03-10", "2,7,70,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAXIMUM RISK", "マキシマム・リスク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ジョン・ウーと組んで「ハード・ターゲット」に出演したJ・C・ヴァン・ダムが、再び香港の映画監督R・ラムを迎えて作り上げたアクション・サスペンス。フランスの刑事アランは自分と瓜二つの男が謎の死を遂げたことを知る。実は被害者は幼い頃に生き別れた双子の弟ミカエルで、何者かに追われていたらしい。アランは真相を探るためニューヨークに渡るが、ロシア・マフィアとFBIをめぐる陰謀に巻き込まれていく。南仏、ニューヨーク、ニースと舞台を次々と移し、カーチェイス、銃撃戦などの目を見張るアクションが繰り広げられる。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BRIDGES OF MADISON COUNTY", "マディソン郡の橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "世界的なベストセラーとなった同名小説を、C・イーストウッドが製作・監督・主演を務めて映画化。アイオワ州の片田舎で出会った、平凡な主婦と中年カメラマンの４日間の大人の恋を描く。アイオワ州マディソン郡に点在する屋根付きの橋を撮りにやって来た52歳のカメラマン、ロバート・キンケイドは、小さな農場の主婦フランチェスカ・ジョンソンと出会い恋に落ちる。運命の出会いを感じた二人だが、フランチェスカには夫と二人の子供がいる。彼についていくことをあきらめた彼女は、４日間の思い出を糧に生きていくのだった……。小説に描かれた実在の場所で撮影され、美しい田園風景を背景に切ない二人の恋が情緒豊かな映像で綴られている。製作・監督を兼ねたC・イーストウッドが自らは一歩下がり、M・ストリープを立てた演出をし、M・ストリープもそれに応えた熱演を見せている。原作以上の仕上がりとなり、アメリカでも日本でも大ヒットした。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きみの友だち", "きみの友だち", "", "", "http://image.eiga.k-img.com/images/movie/53490/315x210.jpg?2467", 125, "交通事故の後遺症が原因で自分の殻に閉じこもってきた恵美と、幼い頃から病弱だった由香。10歳の時から友情を育んできた2人は、中学に入っても強い絆で結ばれていた。そんな2人を取り巻く人々も、それぞれ不安や迷いを抱えながら生きており……。直木賞作家・重松清の同名作を、「やわらかい生活」の廣木隆一監督＆「黄泉がえり」の斉藤ひろし脚本で映画化した青春ドラマ。主演は映画初出演の石橋杏奈と「誰も知らない」の北浦愛。", "2008-07-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おにいちゃんのハナビ", "おにいちゃんのハナビ", "", "", "http://image.eiga.k-img.com/images/movie/55200/315x210.jpg?3009", 0, "雪国の小さな町を舞台に、引きこもりの兄と余命わずかな妹の実話を映画化。高良健吾と谷村美月が兄妹を演じる。5年前、須藤一家は病弱な長女・華の療養のために新潟県小千谷市片貝町に引っ越してきた。毎年世界一の花火が打ち上げられる「片貝まつり」の日に半年間の入院生活を終えた華は、兄の太郎が部屋に引きこもっていることを聞かされる。華は花火大会を主催する成人会に兄を参加させようと、無理やり太郎を外に連れ出すことにする。", "2010-09-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄飛鴻鐡雎門蜈蚣／CLAWS OF STEEL", "ラスト・ヒーロー・イン・チャイナ 烈火風雲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "清朝中国に実在したヒーロー、ウォン・フェイフォンの活躍を描いた“ワンス・アポン・ア・タイム・イン・チャイナ”シリーズの番外編。シリーズ３作まで主役を務めたL・リンチェイが、自身の会社で自分が主役で製作した。政府高官の執拗な策にはまったウォンは支部道場を閉鎖され、広東州を去るハメに。一方、支部道場があった村では少女行方不明事件が多発。犯人を知った父娘を助けたことから、ウォンと犯人の戦いが始まるのだった。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Get Smart", "ゲットスマート", "", "", "http://image.eiga.k-img.com/images/movie/53480/315x210.jpg?2452", 110, "1965～70年にかけてアメリカで放送され人気を博したTVドラマ「それ行けスマート」をリメイクしたスパイコメディ。主演は「40歳の童貞男」のスティーブ・カレル、相棒役に「プラダを着た悪魔」のアン・ハサウェイ。極秘諜報機関「コントロール」で分析官として働いていたスマート。しかし、国際犯罪組織「カオス」の襲撃によって全エージェントの身元が割れてしまったことから、代わりにスマートが未知のエージェント86として極秘任務につくことになるが……。", "2008-10-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TORTURE GARDEN", "残酷の沼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「サイコ」のR・ブロックの原作を、オムニバス・タッチで映像化。見せ物小屋へやって来た５人の客は、ディアボロ博士によって各人の不気味な運命を次々と明らかにされる。B・メレディスやJ・パランスらの性格俳優が好演。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A FAREWELL TO ARMS", "武器よさらば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 152, "第一次大戦下の1917年。オーストリア国境に近いイタリア戦線を舞台に、イタリア軍に身を投じたアメリカ青年が、戦争の残酷さに耐えかね軍を脱出、看護婦とともにスイスへ向かう。しかし、彼女はそこで帝王切開の手術の末死ぬ……。E・ヘミングウェイの名作の二度目の映画化（１回は1932年の「戦場よさらば」)。1950年代末に流行のように作られたヘミングウェイ映画（「陽はまた昇る」 「老人と海」）の中で、名プロデューサー、セルズニックが得意のスペクタクル映画として製作した２時間半を超える大作。最初ジョン・ヒューストンがメガホンを執ったが、セルズニックは気に入らず、ヴィダーが代わって完成させた。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KEKEXILI：MOUNTAIN PATROL", "ココシリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "チベットのココシリ高原を舞台に、密猟者とパトロール隊の死闘を描いた硬派ドラマ。両者の戦いより壮絶なのが、標高4800メートルという環境が生み出す超苛酷な大自然。人vs人vs自然という三つ巴のバトルに圧倒される力作だ。チベットカモシカの密猟を取り締まろうと、リータイ率いるパトロール隊は日夜危険な任務に身をさらしていた。彼らの取材にやってきた記者は、パトロール隊と密猟者の過酷な現状を知る。", "2006-06-03", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANATOMY OF A MURDER", "或る殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 153, "弁護士ビューグラーは、妻をレイプした男を射殺した陸軍中尉から事件の弁護を依頼される。本当にレイプか不倫かをめぐって、彼は検事ダンサーと激しく対立、ついに依頼人の無罪が立証された。ところが依頼人夫妻は、弁護料を払わずに、姿をくらましてしまう……。ユーモラスな弁護士役のJ・スチュアートと、シャープな検事役のG・C・スコットの対照的なキャラクターが生かされた法廷劇の傑作。公開当時、作品賞、主演男優賞ほか６部門ものアカデミー賞にノミネートされた。また、殺人死体をかたどったソウル・バスのタイトル・デザインも有名だ。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ロックマンエグゼ", "劇場版ロックマンエグゼ", "", "", "http://ecx.images-amazon.com/images/I/61DA71RD5QL._SL210_.jpg", 96, "TVアニメも好評を博した人気ゲームの劇場版アニメ。邪悪な電脳生命体のネビュラグレイが暗躍、地球は消滅の危機に！　世界の平和を守るネットセイバー・光熱斗（ひかりねっと）は、パートナーのネットナビ・ロックマンとともに立ち上がる。だが、そんな彼らの前に、最強のネットナビ・フォルテが現れ……。", "2005-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愈快楽愈墜落／HOLD　YOU　TIGHT", "ホールド・ユー・タイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「ルージュ」「赤い薔薇　白い薔薇」などの女性映画で知られるスタンリー・クワン監督がゲイ問題に取り組んだ意欲作。妻を愛していながらも、つい自分の世界にこもりがちなワイに思いを寄せる少年、ジェ。同じく、ワイに静かな思いを寄せる男、トン。満たされることのない心の葛藤を内に秘めている彼らが、運命の流れの中で、出会いと別れを繰り返し、やがて、不思議な人間関係を形成していく。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("「エロ事師たち」より 人類学入門", "「エロ事師たち」より 人類学入門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "当時の性風俗にスポットライトを当て、日本人の性をブラックな笑いを交えて描いた人間喜劇。主演の小沢昭一の演技が素晴らしく、主演男優賞を総なめにした。エロ事師・スブやんは、金持ちや斜陽族相手にエロ８ミリを製作・販売しているうちに自分の方はインポになる。エロ８ミリが危なくなった彼は、死んだ愛人に似せたダッチワイフ作りに異常なまでにのめり込んでいく……。ラスト・シーンは仏教思想の影響が見られ暗示的に終わっている。俗世間のエロ事にわが身を捧げ、奥義をきわめて脱俗していく境地。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刑事物語", "刑事物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "博多署の刑事・片山元は、強制捜査の時に出会った聾唖（ろうあ）者のソープランド嬢・ひさ子の身柄を引き取ることに。沼津署に転勤した片山は、ひさ子とアパート暮らしを始める。やがて、片山は連続殺人事件の捜査に参加。そんな中、事件の背後で暗躍する売春組織によって、ひさ子が連れ去られてしまう……。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST SAFARI", "サハリ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "かつて親友を殺した狂暴な象＝ビッグ・レッドを撃ち殺すため、ライセンスを返上してアフリカ奥地に赴いたベテラン・ハンターのギルクライストと、彼とは肌があわないが同行する若手のケイシー。数日後、彼らはビッグ・レッドと出会う……。アフリカを舞台に復讐に燃えた男たちの生きざまを描くアクション・アドベンチャー。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES DEMOISELLES DE ROCHEFORT", "ロシュフォールの恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "年に一度のお祭りにわき立つフランスの海辺の町ロシュフォール。この町に住む美しい姉妹ソランジュとデルフィーヌは素晴らしい恋人の出現を待ちこがれている。またカフェを営む姉妹の母も昔の恋人の面影を抱き続けている。姉妹は町にやって来た曲乗り芸のコンビや絵の好きな水兵、ハンサムなアメリカ人らと出会っていくが、母の思い出の男性もロシュフォールへ戻って来ていた……。J・ドゥミ監督がG・ケリー、G・チャキリスを得て撮った、楽しい夢にあふれた仏製ミュージカルの決定版。", "1965-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Beguiled", "白い肌の異常な夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　南北戦争の末期。南部の林の中にある女子学園の生徒が重傷を負った北軍の兵士マクバーニーを見つけ、園内に運び込む。教師のエドウィナや女子生徒たち、そして院長マーサによってかくまわれたマクビーは次第に回復していく。しかし生徒が彼を誘惑。エドウィナがそれに嫉妬し……。監督ドン・シーゲル＆主演クリント・イーストウッドのコンビによる異色のエロティック・サスペンス。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Premier Cri", "プルミエール　私たちの出産", "", "", "http://image.eiga.k-img.com/images/movie/53430/315x210.jpg?3099", 98, "人種も文化も異なる5大陸10カ国の女性たちの出産現場に密着した感動のドキュメンタリー。医療機関に頼らない自然分娩を選んだアメリカの女性、イルカと一緒の水中出産を選んだメキシコの女性、かつて自分が生まれた産院での介助出産を選んだ日本の女性など、様々な出産のかたちを選択した女性たちの姿を通し、生命誕生の奇跡をドラマティックに綴る。監督・撮影はフランスのドキュメンタリー作家ジル・ド・メストル。", "2008-04-19", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一杯のかけそば", "一杯のかけそば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "大みそかの夜遅く、そば屋にやってきた母親と幼い兄弟の３人連れ。母子はおずおずと３人で一杯のかけそばを注文した……。日本中に感動の嵐を巻き起こして大ベストセラーになった栗良平の名作を基に、原作にはない登場人物やエピソードを加えて映画化。正攻法のしっとりとした語り口であの感動が蘇る。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("“OH，GOD！”", "オー！ゴッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "カントリー・ウエスタンのスーパースター、J・デンヴァーが、神に選ばれた男を演じる痛快コメディ。神様が、“我々は人間を見捨てていないし、神はまだ死んでいない”ことを示すため、一人の平凡な男を助手にして、神の存在を証明させるという物語。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スケこまし群団", "スケこまし群団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "自堕落な大学生二人の生活を追うピンク作品。学校にも行かず、フラフラとバイトをしてはクビになる日々を過ごしているジュンペイとゴロー。ある日、自分たちの母親に東京見物でもさせてやろうと思いつくが、計画がことごとく狂ってしまう。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　FACE　OF　FU　MANCHU", "怪人フー・マンチュー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "S・ローマーの小説から生まれた悪の怪人フー・マンチューを主人公にした怪奇活劇。あらゆる生物の血を凝固させる“黒い丘のケシ”の猛毒を利用して、世界制覇を企てるフー・マンチュー。彼を絞首刑に導いたはずのスミス警部は、宿敵の謎の復活に驚きつつも、その陰謀を阻止すべく奔走する。ホラー映画の名優C・リーがフー・マンチューを怪演。", "1964-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　SIEGE", "マーシャル・ロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "FBIとニューヨーク警察が共同で組織したテロリズム対策本部長ハバードは、テロリストによるバスジャック事件の解決に乗り出したものの、多くの犠牲者を出してしまう。捜査を始めた彼はアラブの若者サミールを犯人と断定、彼のアパートに向かうが、そこにはCIA女性捜査官のエリースがいた。ハバートは彼女がアラブ系アメリカン社会に重要な情報源をもち、諜報活動していることを察知、二人はテロ組織の根源に迫る共同戦線を組むことにする。その間にも、連続して爆破テロが勃発。陸軍のウィリアム将軍は大統領の命令によってニューヨークに戒厳令(マーシャル・ロー)を発令、軍隊を出動させる……。３大スター共演によるサスペンス大作。テロ行為に直面したFBI、CIA、軍の人間たちの姿を捉えながら、テロリストとの対決を鮮烈なサスペンスとアクションで描く。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("惚れたらあかん・代紋の掟", "惚れたらあかん・代紋の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "家田荘子のベストセラー『惚れたらあかん』を「ドンを撃った男」の和泉聖治監督、的場浩司主演のコンビで映画化した任侠ドラマ。大阪の北西会で若頭補佐を張る役座一路は会長の指令によって東京に進出。関東連合会の飯倉組との抗争はしだいに激化し、ついに双方に死者が出てしまう。この事態に、北西会と関東連合会は手打ちを行うが、自分の部下を殺されている役座は気持ちを抑えることができず、無念を晴らす機会をうかがっていた……。関東VS関西のヤクザの抗争、そのなかで“極道の道理”を貫こうとする役座の生き方、そして彼と女子大生、真夏の禁じられた愛を描く。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泣いてたまるか", "泣いてたまるか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "山田洋次と稲垣俊の原作によってTV放映されていた作品の映画化。山田洋次の助監督を務めていた宮崎晃の監督デビュー作でもある。長距離トラックの運転手でお人好しの源二郎は39歳にして未だ独身である。彼は上京してきたばかりの弘子を好きになるが、彼の腹違いの弟・五郎も彼女に好意を抱き……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUTOMATIC", "オートマティック2033", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "人間とサイボーグが世界に混在する2033年を舞台とした、SFハード・アクション。未知の力を秘めた最新バトル・サイボーグ“オートマティックJ269”は、一人の女性を守るために誤って人を殺してしまう。サイボーグ製造会社の社長は事件を隠そうともくろみ、J269とその女性を抹殺するため、最強特殊工作隊を出動させた。両者の壮絶なバトルが火の粉を散らす。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MADAME　EDOUARD", "マダムと奇人と殺人と", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "殺人事件の謎解きを軸に、“突然死”という名のビストロに集まる変わり者たちの人間模様をブラック・ユーモアたっぷりに描く。20歳の娘がいるおかまのイルマをはじめ、登場人物は全員クセ者ばかり。そんな彼らが右往左往する姿に笑わせられ、ホロリとさせられる不思議な味わいの人間ドラマだ。ベルギーの首都ブリュッセルで美大生連続殺人事件が発生。レオン警視は事件の謎を追ううちに、ビストロ“突然死”に行き着くが……。", "2005-11-19", "2,37,58", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版NARUTO ナルト　疾風伝　ザ・ロストタワー", "劇場版NARUTO ナルト　疾風伝　ザ・ロストタワー", "", "", "http://image.eiga.k-img.com/images/movie/55170/315x210.jpg?1394", 0, "累計発行部数が1億部を突破（10年現在）した大人気コミックス「NARUTO　ナルト」の劇場版第7弾。網手から任務を受けたナルトは、抜け忍・ムカデを廃墟に追いつめるが、ムカデに開放された龍脈の力に飲み込まれ、過去へとタイムスリップしてしまう。目覚めるとそこは栄華を極めた「楼蘭（ろうらん）」の街で、ナルトは伝説の忍・ミナトと出会う。未来からきたムカデがこの時代を支配しようとしていることを知り、2人は力を合わせて陰謀を阻止しようとするが……。少年時代のナルトやサスケ、サクラの魔法の壺をめぐるドタバタ争奪戦が描く短編「劇場版 NARUTO　そよ風伝　魔神と3つのお願いだってばよ!!」も併映される。", "2010-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢売るふたり", "夢売るふたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「ゆれる」「ディア・ドクター」の西川美和監督がオリジナル脚本で描く長編第4作。東京の片隅で小料理屋を営んでいた夫婦が火災ですべてを失い、店をやり直すための資金が必要な2人は結婚詐欺を計画。妻が孤独を抱える女を探して計画を練り、夫がその対象に近づき詐欺を実行していく。2人は詐欺を繰り返していくが、次第に夫婦間や騙した女たちとの歯車が狂い始め……。主演は「告白」の松たか子と「なくもんか」の阿部サダヲ。", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まひるのほし", "まひるのほし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「阿賀に生きる」が国内外で絶賛された佐藤真監督が、知的障害者の人々が３つの施設で行っているアートワークを取材したドキュメンタリー。７人のアーティストが制作した絵画、オブジェなどの作品を通して、彼らのユーモアと感動に満ちた人間性を描写。障害者問題に関するメッセージ映画ではなく、独創的なアートの素晴らしさ、その作者の人となりと生活風景をイキイキと捉えた作品。挿入歌として井上陽水のナンバーが使用されている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOODBYE EMMANUELLE", "さよならエマニエル夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「エマニエル夫人」シリーズ第３作。S・クリステルがメインとなるエマニエルは、これが最終作となった。夫のジャンと、インド洋のセイシェル島で気ままにフリーセックスを楽しんでいたエマニエル。だが、映画監督のグレゴリーと知り合い、彼女は真実の愛を知る。セックスの申し子ともいえるエマニエルが、愛に目覚める展開が新味。", "1977-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FOUR HORSEMEN OF THE APOCALYPSE", "黙示録の四騎士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "サイレント時代の最も豪奢な一編と言っても過言ではない大河ロマン。イバニェスの原作をもとに、第一次世界大戦で敵味方に分かれて戦うハメになる一族の姿を、壮大なスケールの物語の中に描いている。主演は世紀の二枚目、R・ヴァレンチノ。ちなみに黙示録の四騎士とは、征服、戦争、疫病、死を指す。", "1921-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KNIGHT WITHOUT ARMOUR", "鎧なき騎士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "第一次大戦直前の首都ペテルブルグ。イギリスの海外特派員としてロシアに滞在中の諜報員が、共産党の動向を探るうち、党員と間違えられてシべリア流刑に。そこでやはり捕虜となった伯爵夫人と知り合うが……。革命下のロシアの情況を巧みに再現した格調高いメロドラマ。", "1937-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談 累が淵", "怪談 累が淵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "安田公義監督による大映十八番の怪談もの。貧乏旗本・深見新左衛門は、借金の催促に来た針医宗悦を刃にかけ、下男・三右衛門に死体を捨てにやらせる。しかし、妻のおくまの顔が宗悦に見えてきて彼女を斬り殺し、自らも狂い死ぬ。おくまの情夫・吉松は、死体をつづらに入れて捨てに出た三右衛門を殺し、金を奪う……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("綫人", "密告・者", "", "", "http://image.eiga.k-img.com/images/movie/56800/315x210.jpg?3169", 115, "「ツインズ・エフェクト」「スナイパー：」のダンテ・ラム監督が手がけたクライムサスペンス。香港警察の捜査官ドンは、犯罪組織に送り込んだ密告者の安全を守れなかった過去に罪の意識を抱えていた。そんな中、台湾からやってきた凶悪犯罪者バーバイの動向を探ることとなり、新たな密告者として出所間近の青年サイグァイを選ぶ。家族のために金が必要なサイグァイは密告者となり組織に潜入し、バーバイの恋人ディーに近づくが……。主演は「かちこみ！ドラゴン・タイガー・ゲート」のニコラス・ツェー。", "2011-10-29", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　ハピーの大冒険", "それいけ！アンパンマン　ハピーの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "今回のテーマは“本当の強さとはなにか、勇気とはなにか”。正義のヒーローに憧れる子供たちのように、アンパンマンに弟子入りを希望したハピーの成長物語。小さな体に負けん気だけは人一倍のハピーの声を担当するのは、ともさかりえ。元気いっぱいのハピーをキュートに演じている。葉っぱのハピーは、憧れのアンパンマンに弟子入り。でも毎日の仕事は地味な人助けばかり。不満のハピーにばいきんまんが近づいた。", "2005-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sinful Davey", "華麗なる悪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "　19世紀のスコットランド。青年デイビー・ハガートは絞首刑になった父よりも高名な盗賊になることを誓う。軍隊を脱走した彼はスリのマクナブを仲間にし、自らの理想を実現すべく盗みをはたらく。やがて公爵が催す舞踏会での大仕事を計画するが……。実在した怪盗ハガートの回想録をコミカルに映画化。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BELIEVED VIOLENT", "レプスキー絶体絶命 その男凶暴につき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "元刑事で今は国際保険会社の調査員をしている男トム・レプスキーを主人公にしたハードボイルド・バイオレンス“レプスキー”シリーズの第２作。J・H・チェイスの小説をもとに、殺人鬼の汚名を着せられた老科学者の調査を進めるうち、巨大組織と戦うことになったレプスキーの活躍を描く。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crossroads", "クロスロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "　ブルース・ギタリストを目指す青年ユージンは伝説のギタリスト、ロバート・ジョンソンの幻の曲を見つけ出そうとしていた。その友人だったウィリーに出会ったユージンはミシシッピーのクロスロードへ連れていってほしいと頼まれる。そこは自らの魂と引きかえに富と名声を得ることができる悪魔との契約の場所だった……。ブルースのルーツを求める青年の姿を描いたロードムービー。クライマックスには壮絶なギターバトルが繰り広げられる。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER SURVEILLANCE", "ミッドナイト・ブレイカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "かつてはタフなアウトロー警官だった保険調査員マカリスターは、パートナーで親友のパルーソの事故死に殺人事件の匂いをかぎつける。しかし、上司のハーシュは、マカリスターの疑いを真っ向から否定し、それ以上の詮索をやめるよう命令するのだった。その後、マカリスターはハイテクの天才、ピアーソンとコンビを組むことになるが、二人のコンビネーションは最悪。何かにつけて衝突を繰り返す。そんな二人を待っていたのは、皮肉にもパルーソ殺しの事件だった……。「ダイ・ハード」のFBI捜査官役で知られるR・ダヴィがタフガイを演じたサスペンス活劇。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バナナシュート裁判", "バナナシュート裁判", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "俳優の佐藤允を父に持つ若冠24歳の佐藤闘介監督のみずみずしいデビュー作。人生の目的を決められない３人の高校生が、奇妙な風来坊の中年に出会い、それぞれの道を歩み始める。けれど、風来坊の正体を知って……。スタッフ全員が20代前半という若々しさながら、あえて生一本で古風な青春像に臨んだのが美点か、欠点かで、評価を分けた。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Death of a Dynasty", "デス・オブ・ア・ダイナスティ HIP HOPは死なないぜ！", "", "", "http://image.eiga.k-img.com/images/movie/34080/315x210.jpg?4105", 91, "ヒップホップ史上最強のレーベル“ロッカフェラ・レコード”の内幕を、虚実入り混ぜながらドキュメンタリー・タッチで描いたブラック・コメディ。ロッカフェラ・レコードの創設者であるデイモン・ダッシュのメガホンのもと、キャストにはジェイ・Z、DMC、2002年に殺害されたジャム・マスター・ジェイら超豪華アーティストが集結。さらに、物語のカギを握る魔性の美女を「シン・シティ」のデボン青木が演じる。", "2007-07-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裸の太陽", "裸の太陽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "機関車のカマ焚きの青年・木村にはゆき子という恋人がいた。二人は結婚資金をためているさなかで、いつか海に行こうと約束している。とうとう海に行くことになった日、貯金をおろした木村は“金を貸してくれ”という同僚・前田に出会う……。1960年に実生活で結婚する中原ひとみと江原真二郎が兄妹の役を演じている。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZERO PATIENCE", "ゼロ・ペイシェンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "カナダの博物館で働く性科学者バートンは、疫病展示室の催し物として、北米にエイズを持ち込んだとされているペイシェント・ゼロ（患者０号）をメインにしたエイズの展示を思い付く。一方、ひょんなことからこの世に幽霊としてよみがえったゼロは、メディアによって歪められた自分の姿を見る。その汚名を晴らそうと、自分の姿が見える唯一の人物バートンに近づくが……。あふれるユーモアと辛らつなメッセージを盛り込んだ異色のミュージカル映画。", "1993-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("８1/2　WOMEN", "ピーター・グリーナウェイ　８1/2の女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "カンヌ映画祭で賛否両論を呼んだ異才P・グリーナウェイの問題作。ジュネーブの銀行の頭取である大富豪が最愛の妻に先立たれ、悲しみの淵に。息子のストリイは父を救うため、財力に物をいわせて自分たち専用の娼館を作る。かくして世界中から美女８人と小人一人が集められるが、彼女たちはやがて親子に反旗をひるがえし……。フェリーニの「81/2」を引用しつつ、性のタブーを大らかに笑い飛ばす。", "1999-01-01", "3,2,7,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りキチ三平", "釣りキチ三平", "", "", "http://image.eiga.k-img.com/images/movie/53870/315x210.jpg?4308", 118, "1973年から10年間に渡り連載された矢口高雄の同名人気コミックを初の実写映画化。13歳ながら天才的な釣りの技術を持つ三平は東北で行なわれた鮎釣り大会で優勝する。そこでアメリカでプロとして活躍する鮎川魚紳から“夜泣谷の巨大魚”の伝説を聞かされた三平は、その巨大魚を釣るべく魚紳らと夜泣谷へ向かうが……。主人公・三平に「ALWAYS　三丁目の夕日」の須賀健太。監督は「おくりびと」でアカデミー賞外国語映画賞を受賞した滝田洋二郎。", "2009-03-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLOWERS フラワーズ", "FLOWERS フラワーズ", "", "", "http://image.eiga.k-img.com/images/movie/55250/315x210.jpg?1320", 0, "蒼井優、鈴木京香、竹内結子、田中麗奈、仲間由紀恵、広末涼子の6女優が、昭和から平成にいたるまでの3代の女性たちを演じる群像劇。監督は「タイヨウのうた」の小泉徳宏。昭和11年、封建的な家長制度で生まれ育った凛（蒼井）は、親同士が決めた結婚を受け入れられず、花嫁姿のまま家を飛び出す。時は過ぎ昭和30年、凛の長女・薫（竹内）、次女・翠（田中）、三女・彗（仲間）は高度経済成長のなか、たくましく成長していた。そして平成21年、そして平成21年、彗の長女・奏（鈴木）は、平凡ながらも幸せな生活をおくる次女・佳（広末）を優しく見つめながら、人生の岐路に立たされていた。", "2010-06-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST DINOSAUR", "最後の恐竜　極底探検船ポーラボーラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ランキン＝バス・プロと円谷プロの日米合作によるロスト・ワールドもの。北極海で発見された火口湖に棲息する恐竜ティラノザウルスをめぐって繰り広げられる男たちの冒険を綴っていく。円谷プロならではの特撮技術を駆使して、生き残った恐竜の姿を迫力満点に描いたSF作品。", "1976-01-01", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ノルウェイの森", "ノルウェイの森", "", "", "http://image.eiga.k-img.com/images/movie/53950/315x210.jpg?3935", 133, "「青いパパイヤの香り」「アイ・カム・ウィズ・ザ・レイン」のトラン・アン・ユン監督が、村上春樹の世界的ベストセラー小説を映画化。37歳のワタナベトオルは、ドイツ行きの機内でビートルズの「ノルウェイの森」を聴き、18年前の青春を思い出す。当時ワタナベは、親友キズキの恋人・直子に恋をしていたが、ある日突然、キズキは自殺してしまった。キズキを失った喪失感から逃れるように東京の大学に進学したワタナベは、ある日東京で直子に再会するが……。出演は松山ケンイチ、菊地凛子、水原希子。", "2010-12-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("散弾銃の男", "散弾銃の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "日活お得意の西部劇風無国籍アクション。主演はダンプガイ・二谷英明。監督は日活の鬼っ子・鈴木清順。散弾銃を持った流れ者が天竜川の大森林地帯で３悪人と対決し、恋人の仇を討つ。編中、二谷が小林旭ばりにギター片手に唄う、いささか珍妙なシーンがある。アクションの快感と西部劇パロディ調のおかしさが奇妙に混合した一編。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANFAN LA TULIPE", "花咲ける騎士道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "18世紀のフランス。美しい娘の甘言にのせられて兵隊になった男ファンファンが、持ち前の陽気さと軽快な剣さばきで大活躍。フィリップの個性が役柄にマッチして、明るい冒険活劇に仕上がった。この役名がその後彼の愛称となった。", "1952-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間の屑", "人間の屑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "元パンクロッカーの芥川賞作家、町田康の自伝的な小説に基づくシュールなドラマ。音楽活動に行き詰まって親戚の経営する旅館に身を寄せるも、口うるさい祖母にウンザリして飛び出した清十郎。恋人と同棲を始めたり、事業に着手したりと彼は新しい挑戦を続けるが、ことごとく失敗し……。何をしても中途半端な主人公はまさしくダメ人間だが、描写がリアルゆえに奇妙な共感を呼び起こす。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("洪福齋天／THE GAMBLING GHOST", "ギャンブリング・ゴースト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "サモ・ハン・キンポー主演のドタバタ・コメディ。ハンとホイはホテルの駐車場で働きながら、いつの日かギャンブルで大穴を当てようと考えている。ある日、駐車場に美女リリーが車でやってくる。彼女は盗難車を売り、金に換えている詐欺師だった。ハンたちは、彼女の車を横取りして、ひともうけしようとたくらむが、逆にギャングに狙われてしまう。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蜘蛛の瞳", "蜘蛛の瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "殺された娘の復讐を果たし、人生の目標をなくした新島。偶然再会した元同級生の岩松に誘われ、何気なく殺しのビジネスに加わった彼は、組織の幹部から岩松の監視を依頼されるが……。黒沢清監督が「蛇の道」に続く連作として撮った異色ハードボイルド。心にポッカリ穴が開いた男の運命が、何とも奇妙な味わいの乾いたユーモアを交えて描かれる。哀川翔、ダンカンの脱力しきった存在感も絶妙だ。ビデオタイトルは「修羅の狼　蜘蛛の瞳」。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY　BROTHER", "マイ・ブラザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "成績優秀だが気の弱い兄と無鉄砲な弟。母親に女手ひとつで育てられた彼らの愛憎半ばする関係を、熱いタッチで描き上げた韓国映画だ。ウォンビンが「ブラザーフッド」に続き、再び兄弟愛というテーマを体現している。母親に愛されている優等生の兄と、そんな彼に反発する弟。高校生から大人へと成長するうちに、二人は別々の人生を歩み出していく。", "2005-05-28", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vengo", "ベンゴ", "", "", "http://image.eiga.k-img.com/images/movie/1730/315x210.jpg?4322", 89, "スペイン、アンダルシア地方。最愛の娘を亡くして間もないカコは、悲しみから立ち直れずにいた。そんなカコに唯一の安らぎを与えるのは、失踪した兄マリオの息子ディエゴ。ディエゴは体が不自由だが、フラメンコを心から愛しカコを慕っていた。しかしそんなディエゴの命はカラバカ家に狙われていた。マリオがかつてカラバカ家の長男を殺したため、その怒りがディエゴに向けられていたのだ。カコはディエゴを守るため、ある決意をする。", "2001-05-26", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Irma la Douce", "あなただけ今晩は", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "　「アパートの鍵貸します」の2大スターを再び主演に迎え、パリを舞台に警官と娼婦のラブロマンスが展開する。パリの娼婦街をパトロールする新人警官のネスター。しかし、ガサ入れを行った場所でお楽しみ中の上官と鉢合わせし、即クビになってしまう。そんなとき、売れっ子娼婦イルマに出会い、彼女を独り占めしたいがために偽装殺人まで企てる。後にワイルダーは失敗作と語っているが、アメリカ国内では大ヒットを記録した。", "1963-11-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アカルイミライ", "アカルイミライ", "", "", "http://image.eiga.k-img.com/images/movie/1310/315x210.jpg?4000", 115, "「CURE」「カリスマ」など話題作が続く黒沢清監督の「回路」（00）以来2年振りの新作。TV「サトラレ」などで人気のオダギリジョー、浅野忠信、藤竜也の3人が初共演。工場で働く雄二が、唯一心を許せる存在は同僚の守だけ。しかし、守はある日、大切に育てていたクラゲを雄二に残して、突然、姿を消す。呆然とする雄二の前に、守の父親が現れ、雄二は彼とともに暮らすようになる。", "2003-01-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOWLING IV：THE ORIGINAL NIGHTMARE", "ハウリングIV", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "奇妙な幻覚に襲われるサスペンス作家のマリーは、夫の勧めで休暇を取りにのどかな村へとやって来る。しかし村には恐ろしい秘密があった。戦慄の変身シーンで知られるシリーズ第４作。今回はセクシーな狼女も登場。ドロドロに溶けた肉体が狼男に再生する新しい変身SFXが最大の見もの。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FILLE DE QUINZE ANS", "15才の少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "15歳の少女ジュリエットは、ボーイフレンドのトマとその父親ウィリーとともに、夏のバカンスをスペインのイビザ島で過ごす。ウィリーは次第にジュリエットに愛を感じるようになっていた。それに気付いたトマは……。一人の少女をめぐる父と息子の三角関係を、ドワイヨンならではの微細なタッチで描き上げた作品。ビデオ発売時タイトルは「15歳、無秩序な妖精」。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EYE OF THE CAT", "猫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "猫が人間を襲うという、ヒッチコックの「鳥」にも似たサイコ・サスペンス。富豪の未亡人ダニーは100匹もの猫を飼っている大の猫好き。彼女は莫大な遺産を、猫たちに与えるという遺言を書いていた。そんなある日、家を出ていた義理の息子ワイリーが遺産を狙って現れる。ダニーは、猫嫌いの彼のために猫を捨て、遺言を書き改めるが、捨てられたはずの猫はいつの間にか家に戻ってきていて……。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超新星フラッシュマン", "超新星フラッシュマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "改造実験帝国メスから地球を守るべく立ちあがった5人のヒーロー、フラッシュマンの活躍を描くスーパー戦隊シリーズ第10作「超新星フラッシュマン」の劇場版。フラッシュマン打倒に燃える改造実験帝国メスの大帝ラー・デウスは、地球と宇宙の生命体の中から数百種類の遺伝子を選び、最強の獣戦士ザ・ガラゴスを作りだす。ザ・ガラゴスの驚異的な能力にフラッシュマンたちはピンチに陥るが、そこへ1匹の子犬が現われ……。", "1986-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雁", "雁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "森鴎外の小説を豊田四郎が映画化した。舞台は明治時代の東京。貧しく育ったお玉は高利貸しの妾になり、無縁坂に家をもらって住んでいる。毎日坂を散歩しにやって来る大学生に憧れるようになったお玉は、ある日心を打ち明けようとするが、折しも大学生は海外留学に出発するところだった。彼は去り、また単調な生活が始まる……。人生の不遇に耐えながらもシンの強い女を高峰秀子が好演。戦中から戦後にかけて長いスランプにあった豊田監督は、この１作で久々に本領を発揮した。特に明治の情緒たっぷりの美術とカメラワークが素晴らしい。1966年、大映の池広一夫監督によるリメイク作品がある。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BESAME MUCHO", "ベサメムーチョ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "愛の迷路に入り込んでしまった二組のカップル、ディナとテュカ、シコとオルガ。家庭生活崩壊の危機に瀕している夫婦たちのバラ色に彩られた20年間を、1970年代、1960年代とさかのぼり、その風俗とともに描く。H・バベンコが製作した極彩色の人間模様。", "1987-01-01", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN COUNTRY", "ブルース・ウィリス イン・カントリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "N・ジュイソン監督とB・ウィリスが組んだベトナム戦争ドラマ。静から動へのドラマ展開を、しっかりとした演出で見せるジュイソン監督。一方、ウィリスも「プラトーン」「７月４日に生まれて」などに続くベトナムものの問題作に意欲的に取り組んでいる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四十不惑／A　MAN　AT　FORTY", "四十不惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "40歳になったカメラマンのツァオは幸福な家庭を築き、仕事も順調。そんな彼の前に、11歳の少年が現われる。少年は文化大革命時代、中国東北部に移住したツァオと前の妻の間にできた子供。その存在も知らされていなかったツァオは、現在の妻には内緒で彼を引き取るが……。文革の重い影を引きずる中国を舞台に、ある中年男の姿をリアルに捉えた人間ドラマ。", "1992-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROSSED SWORDS", "王子と乞食", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "児童文学の傑作の映画化。瓜二つの貧しい少年と王子が、服を交換してお互いの生活を体験。王子は気がふれたと思われながらも、世間の実情にふれる……。物語のモデルとなったヘンリー８世ゆかりの邸宅でロケし、16世紀の華麗な宮廷生活を再現。", "1977-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEA AND SYMPATHY", "お茶と同情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "ブロードウェイで大ヒットしたミュージカルを、V・ミネリが映画化。母のいない大学生トムは舎監の妻ローラを慕い、学友から“シスターボーイ”とバカにされる。その汚名を返上するため奮闘するトムだが、かえって失敗ばかり。落ちこんで森に隠れたトムを見つけたローラは、本気で彼を想う自分に気付く。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アメリカ　戦争する国の人びと", "アメリカ　戦争する国の人びと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 494, "「アメリカばんざい crazy as usual」の藤本幸久監督が、ベトナム戦争からイラク戦争にいたるまでのアメリカ人の戦争体験を描いた、8時間14分におよぶ渾身の長編ドキュメンタリー。アメリカ軍兵士の勧誘の実情、イラクやベトナムの帰還兵、戦死者の遺族、ホームレスとなった元兵士など、戦争を経験した若者たちの“その後”を中心に様々な角度から取材を行ない、戦争大国・アメリカの真実をあぶりだしていく。", "2010-06-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODY LOUNGUAGE", "ボディ・ランゲージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "場末のストリッパーとの情事から転落していく有能な弁護士の姿を描いたエロティック・サスペンス。相棒の女性弁護士T・Jとともに、マフィアの大ボスの裁判を進めていた弁護士ギャヴァン。そんな折、車の事故がきっかけでセクシーなストリッパー、ドラと知り合った彼は、次第に情事を重ねるようになるが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Venus & Mars", "喧嘩　ヴィーナスvs僕", "", "", "http://image.eiga.k-img.com/images/movie/54130/315x210.jpg?4259", 102, "倦怠期夫婦が繰りひろげる過激なケンカの行方を、「力道山」のソル・ギョング＆ドラマ「天国の階段」のキム・テヒ共演で描いたロマンティックコメディ。性格の不一致を乗りこえて結婚したジナとサンミン。ところが、ジナはサンミンの無神経すぎる言動に我慢できなくなり、次第に神経をとがらせていく。結婚2年目にしてついに離婚を決める2人だったが、ケンカは激しさを増していくばかりで……。監督は「エンジェル・スノー」のハン・ジスン。「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。", "2008-10-04", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SNIPER", "山猫は眠らない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "敵の将軍を暗殺する指令を受けて、パナマの密林で隠密作戦を展開するベテラン・スナイパーのベケットと新米のミラー。経験の浅いミラーは恐怖から錯乱状態に陥るが、そんな折、ベケットが敵の手に落ちてしまう……。T・ベレンジャーが敏腕スナイパーに扮して渋い魅力を発揮する、硬質のアクション・ドラマ。DVDタイトルは「トム・ベレンジャー　山猫は眠らない」。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NITRATE KISSES", "ナイトレイト・キス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "同性愛者の歴史を綴ったドキュメンタリーフィルム。現代のゲイ、レズビアンカップルのラブ・シーンやインタビューに、アメリカ最初のゲイ映画「ソドムの運命」（1933）、1930年代ドイツの演劇や記録映画などの歴史的な映像を織り込みながら、抑圧されてきた同性愛者への価値観の見直しを迫る。また、ナチス収容所に捕らわれていたレズビアンの証言、エイズで友人を失ったゲイの証言など様々な映像と音をコラージュし、同性愛者の過去と現在を描いていく。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOLL　FLANDERS", "モル・フランダース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "罪人の子供として生まれたモルが、美しい娘に成長。修道院を脱走した彼女は、上流家庭のメイドに雇われた幸せもつかの間、娼婦となり流転の人生を歩むはめに……。D・デフォーの有名な古典小説を映画化した大河ロマン。貧困などの苦難に負けず激動の18世紀を逞しく生き抜く女性を、R・ライトが凛々しく熱演。名優M・フリーマンが、ヒロインと友情で結ばれる元泥棒に扮している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅笠道中", "旅笠道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "誤って兄弟分の源次郎を斬ってしまった草間の半次郎。源次郎の妹おちかの頼みもあり、盲目の母の面倒をみることになる。源次郎の家は信州高遠の旧家だったが、高遠は聖天の虎五郎という悪貸元の縄張りで、国家老と結んで人々を苦しめていた。虎五郎はおちかに目をつけ、自分の別宅へ連れ去ろうとするのだが……。人情ものをベースにした恋愛時代活劇。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海の野郎ども", "海の野郎ども", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "アラブ人や中国人など多くの外国人下級船員が乗る老朽船モンロビア号。荷役会社の小頭・千鳥松は、船長の非行を罵って結束を固める彼らに殴りかかるが、食糧を水夫たちに支給することさえ拒む船長に千鳥松自身も怒りを覚えて……。デビュー１年で日活のドル箱スターとなった石原裕次郎の海洋アクション映画。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の中の子供", "風の中の子供", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "当時、世界最高の充実した子役陣を擁していた松竹蒲田（大船）の子役たちをフルに使った監督・清水宏が坪井譲治の“善太と三平”ものを手掛けた。小学五年生の善太と一年生の三平は仲の良い兄弟である。夏休みのある日、三平は二年生の金太郎から、三平の父は会社をクビになり警察に連れていかれると聞かされ、その真偽を善太や母に確かめようとするが判然としない。父は私文書偽造の嫌疑を受けたのだった。この事件で三平はおじさんに引き取られ、田舎に連れていかれる。波乱万丈の夏休みが幼い兄弟の人生を駆け抜けていく。心優しくユーモアあふれる作品だ。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地平線", "地平線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "新藤兼人が自分の姉の半生をモチーフにして描いた、日本人移民一家の大河ドラマ。1920年、写真による見合いだけで広島からアメリカ西海岸の貧しい農夫のもとに嫁いだ女・秀代が、第二次世界大戦など、激動の時代を４人の子供を抱えながら生き抜いていく。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海燕ジョーの奇跡", "海燕ジョーの奇跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "弟分を殺された沖繩の若い混血ヤクザが、復讐のために連合会の大ボスを射殺、実父がいるかもしれないフィリピンで父親探しと逃亡の日々を送る。時任＝藤谷の息の合った主演コンビと、田中邦衛、原田芳雄ら男優助演陣の活躍が光る青春アクション。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どぶろくの辰", "どぶろくの辰", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "第二次大戦末期、広島で被爆した田坂具隆監督の、戦後再帰第１作。監督助手がついているのも彼の健康回復が思わしくなかったためと言われている。道路工事現場で働くどぶろくの辰の女性関係を描いた作品で、原作は新宿ムーラン・ルージュ出身の劇作家・中江良夫による同名戯曲。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スノーフレーク", "スノーフレーク", "", "", "http://image.eiga.k-img.com/images/movie/56000/315x210.jpg?1311", 74, "モデルで女優の桐谷美玲が主演し、「時をかける少女」（2010）の谷口正晃監督がメガホンをとった青春ミステリー。映像（Cinema）と音楽（Music）のコラボレーションを目指す「CineMusica（シネムジカ）」シリーズ9作目で、同じく桐谷主演、谷口監督による「乱反射」との2本立て上映。函館に住む短大生の真乃は、就職のため上京することになる。しかしその直前、かつて「溶けない雪の欠片を見にいこう」という約束を果たせぬまま死んでしまった幼なじみの速人にそっくりな青年が現れ……。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グリーン・レクイエム", "グリーン・レクイエム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "完成から３年、陽の目を見なかった今関あきよしの劇場用映画第２作。光合成によって栄養を補給するという少女・明日香と国立植物学研究所に勤める若者・信彦との愛を描いたSFファンタジー。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の代紋", "男の代紋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "山下耕作監督による任侠映画で、高橋英樹の東映初主演作品。深川・駒井一家若松組の若い衆・栄井政治は喧嘩政と呼ばれ喧嘩っ早いのが玉にキズ。政治は手くせの悪い身内にドスを向けたために、２年間の東京所払いとなり渡世修行の旅へ出る……。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("酔拳／DRUNKEN MASTER　IN THE TIGER’S EYE", "ドランク・モンキー　酔拳", "", "", "http://ecx.images-amazon.com/images/I/51M-Tz-U1DL._SL210_.jpg", 111, "酔えば酔うほど強くなる、という秘伝の拳法の“酔八拳”をおかしな師匠から受け継ごうとする若き弟子が、その教えを見事に会得し、かつて侮辱を受けた悪漢たちに復讐するまでを描く。おなじみジャッキー・チェンの初期主演作で、日本でも大ヒットしたカンフー・アクション・コメディ。ビデオ発売時タイトルは「ジャッキー・チェンの酔拳」。", "1978-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARBOR TALE", "HARBOR TALE", "", "", "http://image.eiga.k-img.com/images/movie/57600/315x210.jpg?3173", 18, "NHK教育（Eテレ）で放送され、第1回文化庁メディア芸術祭アニメーション部門優秀賞を受賞したクレイアニメ「ニャッキ！」などを手がけた伊藤有壱監督とアニメーションスタジオ「I.TOON（アイトゥーン）」が、5年の歳月をかけて完成させたオリジナルの短編ストップモーションアニメ。港街の片隅に建つ古い洋館の壁から抜け出した一片の赤レンガが街に繰り出し、さまざまな出会いを経験する姿を描く。", "2011-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("涙でいいの", "涙でいいの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "高原の大自然の中でのびのびと育った美人姉妹の愛を描くラブ・ストーリー。姉の千春は、妹の千秋のかつての恋人であった曽根に出来心から求愛する。しかし、千秋と曽根の関係は想像以上に深いものがあり、千秋の夫はそれゆえに死を選んだという悲しき過去があったのだ。やがて千春は二人を祝福する。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BELLY OF AN ARCHITECT", "建築家の腹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "アメリカの建築家クラックライトは、若き妻ルイザをつれてローマへ行く。そこでは18世紀に生きた幻想的な建築家ブーレの展覧会が開催されようとしている。クラックライトは、その展覧会の監修を任されているのだ。彼はローマで歓迎を受け、自信にあふれていた。しかし、彼の自信は少しずつかげりをおび、突然の腹痛におそわれる……。ブーレに憧れ、彼に自分の心情を託し続ける主人公を演じるB・デネヒーは、「シルバラード」や「夜霧のマンハッタン」で、クセのある脇役をつとめ、本作品では1987年シカゴ映画祭最優秀主演男優賞を受賞。グリーナウェイ監督の独特の映像は、バランスに重点をおいたシンメトリーな構図が大きな特徴となっていて、エキセントリックな雰囲気も漂わせている。本作は、グリーナウェイの作品の中で最も成熟した完成度の高いもので、「ZOO」とは一味違った良さがある。", "1987-01-01", "3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHARIOTS OF FIRE", "炎のランナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "それまでCMやドキュメンタリーを手掛け、数々の賞に輝いたH・ハドソン監督が、1980年代の新しいイギリス映画の存在を世界に知らしめた作品。1924年パリ・オリンピックの陸上競技出場を目指すユダヤ人青年と聖職者の息子の物語。片や人種偏見への抵抗のために、一方は神のために走る彼らの姿を通して、人生そのものを謳い上げた感動のスポーツ映画。", "1981-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Land of the Pharaohs", "ピラミッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "エジプト王朝。富と権力を極めたファラオ大王は来世の栄光を得るために巨大ピラミッドの建築に乗り出す。盗掘を防ぐピラミッドの建築を命ぜられたのはクシャイト族の捕虜、バシュタルだった。ピラミッドの建設は長期化し、過酷な労働に数千もの民の信仰が奪われる中、キプロスの王女ネリファが現れ……。エジプト政府の協力を得て、１万人以上の陸軍兵士が働く奴隷を演じるなど大規模な撮影が行われた大スペクタクル史劇。", "1955-12-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パーフェクト9", "パーフェクト9", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "東京に二つしかない障害者野球チームの一つ“東京ロッキーズ”の姿を追ったドキュメンタリー。19歳から54歳まで、様々なハンデをもつ19人のメンバーたち。仕事と野球の両立に悩んだり、重い障害から野球を断念しようと考えたりする彼らが、神戸で行われる年１回の全国大会に向け、米国のデンバーに初めての海外遠征に出かけ、成長していく。野球を愛する彼らの悪戦苦闘がさわやかな感動を呼ぶ。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千年旅人", "千年旅人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "芥川賞作家、辻仁成が「天使のわけまえ」に続いて手掛けた監督第２作。豊川悦司と大沢たかおという色気ある男二人の顔合わせを実現させ、静謐な抒情詩を綴っている。能登半島の海岸に建つ民宿。余命１ヵ月と診断され、思い出のこの地を訪れた男ツルギは、亡き恋人の娘ユマと出会う。やがて親交を深めていく二人の前に、自殺願望の青年トガシが現れる……。全体的に散文的でゆるやかな構成。海を横切る船に手旗信号を送り続けるユマの姿が、画竜点睛の趣を醸し出している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サザンウィンズ", "サザンウィンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "アセアン25周年の記念イベント“東南アジア祭1992”のために、アセアン諸国と日本が共同製作。インドシナ(第１話)、フィリピン(第２話)、タイ(第３話)、日本(第４話)の“今”が、様々な世代の主人公を通して浮かび上がる。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRAZY PEOPLE", "クレイジー・ピープル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "スランプに陥ったコピーライターのエモリーは本音のコピーを会社に提出したところ、あまりに突飛すぎて精神病院に送られる。だが間違いから入院中のエモリーのコピーが世に出て大ヒット。あわてた会社側はエモリーを呼び戻すと、彼は入院中の患者を使ってヒットコピーを連発する。テンポのいい人情コメディ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴走パニック・大激突", "暴走パニック・大激突", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "銀行強盗がジャックしたバスを、それぞれに因縁のある連中が追跡するという往年のスラップスティック・コメディを思わせる一編。深作欣二は実録路線との融合を試みたが、成功には至らなかった。主演の渡瀬恒彦を尻目に室田日出男、川谷拓三らの“ピラニア軍団”が画面を席巻。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サイレン FORBIDDEN SIREN", "サイレン FORBIDDEN SIREN", "", "", "http://ecx.images-amazon.com/images/I/41GQ1QFRQHL._SL210_.jpg", 86, "国内外で大ヒットを記録したゲームソフトをもとに、「トリック」シリーズの堤幸彦監督が映画化したサイコホラー。1976年、サイレンの音とともに島民全員がナゾの失踪を遂げた夜美島。それから29年後、病弱な弟の療養のため家族でこの島に引越してきた由貴は、隣人から「サイレンが鳴ったら外に出てはならない」という忠告を受ける。島に流れる異様な空気に不安を感じながらも、少しずつ島での生活に慣れていく由貴らだったが……。", "2006-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HE´CATE", "ヘカテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "スイスを代表する監督シュミットの官能的な異色恋愛映画。北アフリカに赴任した青年外交官は、あるパーティー会場で出会った人妻に一目惚れ。だが彼女の謎めいた魅力にほんろうされた彼は、錯乱のなか、とんでもない事件を起こす。耽美な映像とハットンの妖艶ぶりが見もの。", "1982-01-01", "26,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OASIS", "オアシス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "「ペパーミント・キャンディー」のイ・チャンドン監督が手掛けた鮮烈な純愛映画。家族にも疎んじられる前科３犯の青年ジョンドゥと、重い脳性マヒを患って日常生活もままならない女性コンジュ。周囲の人々に気付かれぬまま惹かれ合い、愛を育んでいく二人の姿を力強く描き出す。主演のS・ギョング、M・ソリの渾身の演技も観る者を圧倒する一作だ。服役を終えて出所したばかりの青年ジョンドゥは、彼が起こした事故の被害者宅に残された重度の脳性マヒを持つコンジュと出会う。同情からコンジュに近づいたジョンドゥだったが、やがて二人の間には本当の恋愛感情が芽生え始め……。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO KILL FOR", "殺人容疑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "サンフランシスコ郊外の平和なリゾートタウンで殺人事件が起き、刑事歴20年のベテラン、バーデンは、相棒のアランとともに捜査を担当。最重要容疑者として、ミステリアスな美女、キャサリーンに的を絞る。ところが調べが進むうちにバーデンは、キャサリーンの魅力の虜になってしまう。“最重要容疑者には決して恋はするな……”警察の不文律を破ったバーデンが選んだ道は、仕事か恋か!? 「氷の微笑」「シー・オブ・ラブ」を思わせる、サスペンスタッチの刑事ドラマ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名寄岩物語 涙の敢斗賞", "名寄岩物語 涙の敢斗賞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "大関・名寄岩の半生を映画化した相撲映画で名寄岩自身が主演したスポーツ実話ドラマ。一時は大関まで昇った名寄岩も病魔のため転落の一途をたどるが、友人たちの激励で再起した彼は夏場所で敢闘賞を受賞。しかし、妻の初枝が帰らぬ人となって……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL BELL’ANTONIO", "汚れなき抱擁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "シチリアを舞台に一人の男の愛の形を描いたブランカティの小説の映画化。ナイーブな感性の主人公が、愛する人を冒涜するようで指一本触れることができないまま、真実の愛とは裏腹に他の女性を妊娠させ、愛の在り方にさいなまれる姿を描いた恋愛ドラマ。", "1960-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月光仮面・悪魔の最後", "月光仮面・悪魔の最後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "“白髪鬼”と名乗る怪人による連続殺人が発生。祝探偵は調査に乗り出し、戦死したはずの毒物学者・白上博士が白髪鬼だと判明する。戦時中に砂金の出る無人島に博士を置き去りにした同僚への復讐のため舞い戻って来たのだ……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEANSTALK", "ジャックと豆の木伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ケイト博士が発掘現場で見つけた豆が、ひょんなことから少年ジャックの手に。豆はみるみる成長し、雲の上まで伸びて観光客まで来るようになった。“雲の上には巨人がいる”というケイト博士の警告をよそに、ジャックは金の卵を手に入れようと、親友のダニーとともに豆の木を登っていく。と、そこには警告通り、巨人一家が住んでいた。おなじみの物語をもとに描くファミリー・ピクチャー。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRIVE", "DRIVE　破壊王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "壮絶な肉弾戦を織り込んだSF風のバトル・アクション。心臓にバイオエンジンを組みこまれ、凄腕の暗殺者に仕立て上げられた、香港の秘密警察官トビー。組織を抜けた彼はエンジン売却のために逃走する。しかし、秘密警察はトビーの逃亡を阻止するため、最強の刺客“アドバンスモデル”を送り込み……。「プレデター」のSFXを手掛けたS・ワンが演出を担当。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ケンとメリー　雨あがりの夜空に", "ケンとメリー　雨あがりの夜空に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ノルウェイの森　エクステンデッド版", "ノルウェイの森　エクステンデッド版", "", "", "http://image.eiga.k-img.com/images/movie/56270/315x210.jpg?3979", 150, "村上春樹の世界的ベストセラー小説を、「青いパパイヤの香り」のトラン・アン・ユン監督が映画化。2010年12月の劇場オリジナル版（上映時間133分）に先立ち完成していた、編集の異なるエクステンデッド版（150分）。高校時代に親友キズキが自殺してしまったワタナベは、喪失感から逃れるために東京の大学へ進学する。そこで空虚な日々を送っていたワタナベは、ある日、キズキの恋人だった直子と再会し……。出演は松山ケンイチ、菊地凛子、水原希子。", "2011-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Boccaccio'70", "ボッカチオ'70", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 204, "　14世紀の作家ボッカチオの短編小説「デカメロン」を、60年代を舞台にユーモアとエロティシズムを交え描いたオムニバス作品。名優たちを主役に迎え、イタリアの巨匠4人がメガホンをとった。第1話「くじ引き」の舞台は、グラマーな美女を商品にした秘密のくじを売る射的場。第2話は、フェデリコ・フェリーニ監督の「誘惑」。社内結婚をひた隠す「レンツォとルチャーチ」（日本公開時は、この3話目はカットされた）。ルキノ・ビスコンティの「前金」は、賭けのためにセックスのノウハウを修得する伯爵夫人を描いた作品。", "1962-06-29", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BELLS ARE RINGING", "ベルズ・アー・リンギング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "今や知られざる早逝の名女優J・ホリディの数少ない貴重な主演作品として有名なミュージカル・コメディの名作。喜劇「底抜け」シリーズが有名なD・マーティンとの共演。監督は傑作ミュージカルを数多く残した名匠V・ミネリ。電話代行業のオペレーターとして働くエラは、顧客の人生をより良い方向へ導くことに生き甲斐を感じていた。やがて劇作家と、声だけで恋に落ちてしまう。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOM DOWD ＆ THE LANGUAGE OF MUSIC", "トム・ダウド／ いとしのレイラをミックスした男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "50年にわたってソウルやジャズ、ロックの名盤を生み出してきたプロデューサー、トム・ダウドの歩んだ道を検証するドキュメンタリー。ダウドの職人気質に焦点を当てた作りはもちろん、「いとしのレイラ」などの名曲の誕生秘話にも注目したい。1950年代にアトランティック・レコードに入社し、レイ・チャールズやクリームなどの名曲創造を支えたダウド。彼やアーティストの証言を通して、裏方としての音楽人生を見つめていく。", "2006-04-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEFORE THE BAT’S FLIGHT IS DONE", "こうもりが飛ぶ前に…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "母子家庭の母と息子、そして一人の男をめぐる、決して結ばれることのない三角関係を描いた異色作。1989年のベルリン映画祭に出品され、そのスキャンダラスな内容とドラマティックな展開が注目されて銀熊賞を受賞。オープンなホモセクシャルの表現など、東欧において激しい議論を巻き起こした。", "1988-01-01", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Frankie and Johnny", "恋のためらい／フランキーとジョニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "刑務所から出たばかりのジョニーはN.Y.のカフェで働き始め、ウェイトレスのフランキーに惹かれる。だがフランキーは過去の経験から恋に臆病になっていて、ジョニーに心を開かない。ジョニーはそんな彼女に刑務所にいたことを打ち明け……。人気の舞台劇を映画化した大人のラブストーリー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("20,000 LEAGUES UNDER THE SEA", "海底２万マイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "J・ヴェルヌの同名小説を、ウォルト・ディズニーが映画化。続発する軍艦沈没事件の犯人は、戦争を憎む潜水艦ノーチラス号のネモ艦長だった。人喰い人種との戦い、巨大イカの襲撃など、アドベンチャーの醍醐味がたっぷり。SF映画初のシネマスコープ大作。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Italian Job", "ミニミニ大作戦（2003）", "", "", "http://image.eiga.k-img.com/images/movie/1540/315x210.jpg?2443", 111, "50年代に作られた小型車、ミニクーパーが活躍する強盗アクション。金庫破りのプロ、爆薬のプロ、運転のプロなど、特殊技術を持つ面々が、かつての仲間が自分たちから盗んだ金塊を奪回するため作戦を練る。監督は「交渉人」「friday」のF・ゲイリー・グレイ。音楽はUK出身、「シュレック」「ボーン・アイデンティティー」のジョン・パウエルが担当、Amon Tobin、PHOTEKなどUKクラブ系ミュージシャンも参加。", "2003-06-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東テキヤ一家", "関東テキヤ一家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "昭和30年。テキヤ一家の若者、国分は二度とドスは抜かないと誓っていた。ところが、縄張り争いが激化。親友や仲間を次々に殺され、ついにはこらえにこらえた怒りを爆発させてしまう。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ろくでなしBLUES", "ろくでなしBLUES", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "『週刊少年ジャンプ』連載の同名漫画を映画化した青春アクション。吉祥寺にある帝拳高校２年の前田太尊は、ひそかにプロボクサーを目指しつつもケンカに明け暮れる毎日を送っている。そんな彼の前に、原田成吉というプロボクサーの転校生が現れる。成吉は暴走族に襲われた少女を救おうとして目を潰され、ボクサー復帰は絶望視されていた。しかし、夢を捨てきれない彼は、復帰のテストを目指していた。一方、太尊をはじめとする東京の高校生ケンカ四天王のうち、誰が一番強いか決着をつける日がやってきた。折しも、その日は成吉のプロテストの日でもあった……。撮影時、現役キックボクシング世界チャンピオンだった前田憲作が主人公・太尊を熱演するほか、若手の現役ボクサーたちも多数出演している。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RED BERET", "赤いベレー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「007」シリーズを手掛けたことで知られるテレンス・ヤングが初期に監督した戦争映画。英国の落下傘部隊は、孤独な男カナダの功績を認められ赤いベレーの制帽を与えられる。隊長のスノウ少佐は、カナダが志願して入隊した米国人であることを知り、彼の任官を申告したが、カナダはこれを固辞した。彼は米空軍の操縦士だったが、任務中に事故のため同乗者を死なせ、その責任を感じてこの落下傘部隊に志願したのだった。", "1953-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FACE/OFF", "フェイス／オフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "かつて冷酷無比のテロリスト、トロイによって最愛の息子を失ったFBI捜査官アーチャー。壮絶な追撃戦の末、ついにトロイを捕らえたが、トロイは時限式の細菌爆弾を街に仕掛けていた。その場所を探るため、アーチャーは昏睡状態のトロイの顔を移植して、刑務所にいるトロイの弟に接近する。だが、目覚めたトロイは逆にアーチャーの顔を自分に移植しアーチャーの前に現れる……。この世で最も憎悪する互いの顔を取り替えた二人の男の果てしなき死闘を描いた、ジョン・ウー渾身のバイオレンス・アクション巨編。顔の移植によって悪役と正義漢をがらりと演じ分ける、トラボルタとケイジの好演も見もの。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER THE LIGHTHOUSE DANCING", "ラスト・ウェディング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "オーストラリアの美しい孤島を舞台に描く、実話をもとにしたロマンティックなラブ・ストーリー。恋人同士のハリーとエマが、親友の二組のカップルとともに西オーストラリアのロットネス島にやって来る。透き通った青い海、白い砂浜が広がる島で楽しいひと時を過ごしていたある日、二人は結婚式を挙げるつもりだと告白。さらに、エマが難病に冒され余命いくばくもないことを告げる。ショックを受けた友人たちは早速、最高の結婚式にするため奔走するが……。", "1996-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHINA GIRL", "チャイナ・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ニューヨーク、リトル・イタリーの一角に中国レストランができた。ストリート・キッズのアルビーは、自分たちの縄張りが中国人たちに侵され始めていることに我慢ならない。だが、アルビーの弟トニーは、一人の中国人少女と親しくなる。現代版「ウエスト・サイド物語」。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　けいおん！", "映画　けいおん！", "", "", "http://image.eiga.k-img.com/images/movie/55770/315x210.jpg?3757", 110, "かきふらいによる4コマ漫画を原作に、2009年から10年に計2シーズンがTV放映され大ヒットを記録した人気アニメーション「けいおん！」の劇場版。TVシリーズでは描かれなかった桜が丘高校軽音部の部活ライフが描かれる。卒業を控えた軽音部3年生の唯、澪、律、紬はいつも通りのゆるやかな日々を送っていたが、同級生たちが卒業旅行を企画していることを知り、自分たちも卒業旅行へ行くことに。2年生の梓も加わり、各々が候補地の希望を出す中、くじ引きでロンドン行きが決まるのだが……。", "2011-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ番外地 抹殺", "やくざ番外地 抹殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "丹波哲郎主演の“やくざ番外地”ものの第２作。関東に勢力を伸ばそうとする関西ヤクザ組織・銀誠会に組をつぶされた早川。彼は、銀誠会々長の首を狙うが、果たせず高見組にかくまわれる。銀誠会のやり方は横暴をきわめ、ついに高見も早川とともに立ち上がる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("囚人プロレス", "囚人プロレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "「溺れる魚」などで知られる作家・戸梶圭太の「燃えよ！刑務所」を、戸梶自らが監督を務めて映画化した脱力系バトル・アクション。21世紀初頭、刑務所の囚人収容率が180％を超えた。各地で発生する暴動を阻止するため、2010年に民間経営刑務所が設立される。その運営資金を確保するべく、囚人たちが参加する「囚人プロレス」が開催されることになり……。戸梶圭太が自身の小説を映画化した3作品を上映する企画「トカジゴク映画祭」の1作。", "2005-12-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GLIMMER MAN", "グリマーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "常に不死身の男を演じ続けるS・セガールが自身のプロダクションで製作したクライム・アクション。ジャック・コール刑事は、かつてグリマーマンと呼ばれていた過去を捨てていた。グリマーマンとは、どんな敵でも相手にわからず、一撃のもとに殺すことができたジャックに対して付けられたあだ名だ。過去を捨てたジャックは、ニューヨークからロサンゼルスにある捜査のためにやって来る。それは壁に被害者を磔（はりつけ）にするという猟奇殺人で、犯罪心理に詳しいジャックが呼ばれたのだ。相棒には、しゃべりまくるジムがつく。捜査は難航し、新しい被害者はジャックの前妻であり、現場にはジャックの指紋が残されていた。何者かが、ジャックを陥れようとしているのだった。いつもは単独で行動する役の多いセガールが、相棒と組むのも話題の一つ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LONG RUN ロングラン", "LONG RUN ロングラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "オレゴン州アストリアからニューヨークまで、アメリカ横断7000キロの旅に、ローラースケートで挑戦した青年と、それを自転車で追うフリー・ジャーナリスト、さらに車で追う女流写真家の３人旅を描く。フォト・ジャーナリスト・吉田ルイ子の初監督作。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAMES DEAN ： RACE WITH DESTINY", "傷心／ジェームズ・ディーン 愛の伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "永遠のスター、ジェームズ・ディーンの恋を実話に基づき映像化。「エデンの東」を撮影中のディーンが新人女優ピアと恋仲に。だが、ピアの母親は育ちのよくないディーンと娘との交際に反対。やがてピアが他の男と結婚し、ディーンは失意を紛らわそうとカーレースにのめり込むが……。「スターシップ・トゥルーパーズ」のC・V・ディーンが、繊細な演技でディーンになりきっている。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Gamin au velo", "少年と自転車", "", "", "http://image.eiga.k-img.com/images/movie/56530/315x210.jpg?1955", 87, "「ロゼッタ」「ある子供」のジャン＝ピーエル＆リュック・ダルデンヌ兄弟が、2011年・第64回カンヌ国際映画祭でグランプリ（審査員特別賞）を受賞したヒューマンドラマ。父親から育児放棄された孤独な少年が、ひとりの女性との出会いから自立していき、女性もまた少年を守ることで母性を獲得していく姿を描く。自分を児童相談所に預けた父親を見つけ出し、一緒に暮らすことを夢見る少年シリルは、ある日、美容師の女性サマンサと知り合う。週末をサマンサの家で過ごすようになったシリルは、自転車で街を駆けまわり、ようやく父親を見つけ出すのだが……。シリル役は新星トマ・ドレ、サマンサ役は「ヒア　アフター」のセシル・ドゥ・フランス。", "2012-03-31", "37,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おおかみこどもの雨と雪", "おおかみこどもの雨と雪", "", "", "http://image.eiga.k-img.com/images/movie/57620/315x210.jpg?4765", 0, "「時をかける少女」「サマーウォーズ」の細田守監督が、「母と子」をテーマに描くオリジナルの劇場長編アニメーション。人間と狼の2つの顔をもつ「おおかみこども」の姉弟を、女手ひとつで育て上げていく人間の女性・花の13年間の物語を描く。「おおかみおとこ」と恋に落ちた19歳の女子大生・花は、やがて2人の子どもを授かる。雪と雨と名づけられたその子どもたちは、人間と狼の顔をあわせもった「おおかみこども」で、その秘密を守るため家族4人は都会の片隅でつつましく暮らしていた。しかし、おおかみおとこが突然この世を去り、取り残されてしまった花は、雪と雨をつれて都会を離れ、豊かな自然に囲まれた田舎町に移り住む。「時をかける少女」「サマーウォーズ」に続いて脚本を奥寺佐渡子、キャラクターデザインを貞本義行が手がける。", "2012-07-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪霊島", "悪霊島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "1960年代の高度経済成長期を背景に、“島には悪霊がいる。鵺（ぬえ）の鳴く夜は気をつけろ”という因習から、瀬戸内海の刑部島で起こる連続殺人事件と、それに挑む金田一耕助の活躍を描く。ビートルズ・ナンバーを使用した主題曲が斬新。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CERVEAU", "大頭脳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "列車強盗のプロと脱獄犯のチンピラ二人が列車輸送される軍事資金の強奪を思いつき、さっそく情報収集を開始した。まずは輸送責任者である大佐を尾行するが、どうも彼の挙動に不審な点が多い。実は、この大佐こそ有名な英国の列車強盗の首謀者だったのだ。", "1968-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワッゲン　オッゲン　お前ん家　おれん家", "ワッゲン　オッゲン　お前ん家　おれん家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVEDEATH ラブデス", "LOVEDEATH ラブデス", "", "", "http://image.eiga.k-img.com/images/movie/33970/315x210.jpg?3851", 158, "「あずみ」の北村龍平監督が「スカイハイ」の原作者・高橋ツトムの短編コミック「69」を武田真治主演で映像化したバイオレンス・ラブストーリー。出会ってすぐに恋に落ちたサイとシーラ。しかしその3日後、シーラは突然姿を消した。彼女は1年後に再びサイの前に姿を現すが、実は彼女は黒金会の組長・クロガネの愛人で、しかも組の金を持ち逃げしたために、ヤクザ、刑事、殺し屋たちから追われる身となっていた……。", "2007-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PHILADELPHIA STORY", "フィラデルフィア物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ブロードウェイで大ヒットした舞台の映画化。フィラデルフィアの富豪の娘トレイシーは世間知らずのわがまま娘。トレイシーと離婚したばかりの記者デクスターは、彼女の再婚を妨害しようと計略を練るが……。次々と繰り出されるセリフ、右住左住する人々と目まぐるしいテンポで展開する軽快なコメディ作品。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOX 袴田事件　命とは", "BOX 袴田事件　命とは", "", "", "http://image.eiga.k-img.com/images/movie/55450/315x210.jpg?1831", 117, "「光の雨」の高橋伴明監督が、実在の「袴田事件」を映画化。昭和41年、放火された工場から一家4人の刺殺された焼死体が発見される。物証も少ないまま、元プロボクサーで工場の従業員・袴田巌が容疑者として逮捕されるが、かたくなに犯行を否認していた。だが拘留期限3日前に一転自白し、巌は起訴される。熊本典道が主任判事として裁判を担当することになるが、巌は第1回公判で起訴事実を全面否認し、以後一貫して無実を主張する。自白の信憑性を疑った典道は、供述調書を調べ始める。", "2010-05-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BALLAD 名もなき恋のうた", "BALLAD 名もなき恋のうた", "", "", "http://image.eiga.k-img.com/images/movie/54140/315x210.jpg?2798", 132, "2002年の「映画クレヨンしんちゃん／嵐を呼ぶ　アッパレ！戦国大合戦」を原案に、戦国時代の武将・井尻又兵衛と廉姫の恋を描く時代劇。主演は草なぎ剛と新垣結衣で、監督は「ALWAYS　三丁目の夕日」シリーズの山崎貴。天正2年の戦国時代、小国・春日誇る無敵の侍・又兵衛は、命をかけて春日の姫君・廉姫を守り続けていた。幼馴染の2人は互いに密かな恋心を抱いていたが、身分の違いから結ばれない運命を背負っていた。", "2009-09-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LO QUE LE PASO´ A SANTIAGO", "いつでも夢を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "定年退職したばかりの65歳の男性が主人公。昼間の公園でベンチに腰かけてボンヤリと考えごとをしていた彼の目の前に、40歳過ぎの美しい女性が現れる。二人は相手が何者なのかも知らないままに打ち解け合い、年齢も忘れたバラ色の人生が始まるが……。初老の男と中年の女のゆっくり育まれていく愛を、ほんのりと優しく包む、プエルトリコ産の大人のためのラブ・ロマンス。", "1989-01-01", "61", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dreamcatcher", "ドリームキャッチャー", "", "", "http://image.eiga.k-img.com/images/movie/1370/315x210.jpg?2071", 135, "スティーブン・キングの同名小説を「わが街」「白いドレスの女」のローレンス・カスダン監督が映画化。脚本は同じキング原作の「ミザリー」も担当、「明日に向かって撃て！」と「大統領の陰謀」で2度オスカーを受賞したウィリアム・ゴールドマン。メイン州で育った少年時代の友人4人、今は職業も住まいもばらばらな彼らが、冬の山小屋に集まるが、森に住む動物たちがみないなくなり、奇妙な出来事が起こり始める。", "2003-04-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AY，CARMELA", "歌姫カルメーラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "戦火が飛び交う内戦期のスペイン。カルメーラと夫のパウリーノ、そして戦争孤児で口の利けないグスタベーテは、共和派の兵士たちの慰問で各地を回る旅芸人の一座だ。ところが誤って対立するフランコ派の陣地に入ってしまい、そこで彼らは低俗な芝居を強要されるが、一座の花カルメーラだけは舞台を拒否した。愛に生き、舞台に散った旅芸人カルメーラの生き様を、情熱的な歌と踊りで綴る。", "1990-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジェットF104脱出せよ", "ジェットF104脱出せよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "航空自衛隊の全面協力を得て、パイロットを目指す若者たちの姿を描く青春ドラマ。ソロ飛行の日を待ち望む自衛隊の航空学生たち。厳しさに耐え切れなくなった仲間の除隊、練習飛行中の仲間の事故死など、さまざまな試練に立ち向かいながら、彼らはたくましく成長していく。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WORTH WINNING", "３人の婚約者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "３ヵ月以内に３人の女性との婚約に成功したら高価な本物のピカソの絵を贈る、という約束を親友と取り交わしたプレイボーイのTVキャスター。さっそく美女たちへのプロポーズ作戦に出たが、そのなかの一人を本気で愛するようになってしまい……。“セクシー男優”M・ハーモン主演のロマンティック・コメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOLOCAUST 2000", "悪魔が最後にやって来る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "サハラ砂漠に原子力発電所を建設しようという計画に、宗教的な予言によって反対を唱えるグループが次々と事件を起こして妨害してくる。見どころは、オカルト的なテーマとショッキングなシーン。「夢魔」に続くイタリアのA・D・マルチーノ監督作品。", "1977-01-01", "4,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("薄墨の桜", "薄墨の桜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 42, "岩波映画出身のベテラン記録映画作家・羽田澄子の幻想と詩情に満ちた映像ファンタジー。岐阜県北部の山麓に樹齢1300年といわれる“薄墨の桜”がそびえている。この桜に魅せられた羽田監督は４年の歳月をかけて、四季折々の風景や村人の姿を交えて桜の美を記録した。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バオバブの記憶", "バオバブの記憶", "", "", "http://image.eiga.k-img.com/images/movie/54340/315x210.jpg?3685", 102, "「アレクセイと泉」「ナミイと唄えば」の本橋成一監督が、巨樹バオバブをテーマに撮りあげたドキュメンタリー。セネガル内陸部に位置するトゥーバ・トゥール村。大昔からバオバブの樹に見守られてきたこの地では、村人たちにとってバオバブは生活に欠かせないものであり、信仰の対象でもあった。近代化の波が押し寄せる中で昔ながらの生活を続ける彼らの姿を通し、自然と人間との共存について見つめなおす。俳優・橋爪功がナレーションを担当。", "2009-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緑はるかに", "緑はるかに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "国産コニカカラーによる日活最初の色彩映画。オーディションで選ばれた浅丘ルリ子の映画デビュー作でもある。少年少女向けのアクション映画で、科学者である父が悪漢に誘拐され、少女ルリ子が浮浪児たちの協力を得て父を救出するというお話。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PULP FICTION", "パルプ・フィクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "「レザボア・ドッグス」で注目を集めたQ・タランティーノの監督第２作で、1994年のカンヌ映画祭グランプリを獲得、彼の名を一躍世界に知らしめた話題作。あるギャングの一味とその周辺の人々のエピソードを、それぞれが交錯するように絡めながら展開していく独特な構成になっていて、ボスの若い妻と一晩だけデートを命じられたギャングの悲喜劇が描かれている。ボクシングの八百長試合を請け負いながら裏切った男、車の中でピストルの暴発から人を殺し、血の海となった車を処分するため右往左往する男たち……。どのエピソードもユーモアたっぷりのセリフに彩られながら、次に何が起きるかまったく予想がつかないという、まさに観る者をつかんではなさないエンターテインメントの極致。キャスティングも豪華かつ異色で、特にトラヴォルタは、長い間低迷していたが、この作品の成功により、以後、主演作が続き再びトップスターに。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЖАВОРОНОК／SKYLARK", "鬼戦車T−34", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ナチスの収容所で新型砲弾の射撃訓練を強制されたソ連軍捕虜たちが、標的であるソ連製T−34戦車を奪って脱走する様を描く。クリーヒン監督の体験に基づく戦争映画で、国家の全面的支援により、スケールの大きな一編に仕上がっている。", "1965-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN ESERICITO DI CINQUE UOMINI", "五人の軍隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1914年、独裁政治に対する自由主義者や農民の革命運動が盛んだったメキシコを舞台に、脱走米軍人、剣の達者な旅芸人の日本人、銀行ギャングや家畜泥棒など５人のグループが、莫大な軍用金の輸送列車を襲い、革命運動に一役買う。", "1969-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOKYO EYES", "TOKYO　EYES", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1980年代に「夜の天使」「天使の接吻」という２本の青春映画で一部に熱狂的ファンを得たJ・P・リモザン監督が、10年ぶりに発表した長編第４作。東京ロケに参加したクルーのうちカメラマン以外すべて日本人という異色のプロジェクトだ。分厚いレンズのメガネをかけた青年による連続発砲事件が世間を騒がす。刑事の兄を持つ少女Hinanoは、電車の中で偶然出会った犯人Kに接近し、愛にも似た感情で結ばれていくが……。すべての事象がヴァーチャルな日常を生きる今どきの青年、天真爛漫な元気少女という異質な二人のラブ・ストーリーを通して描かれるのは、リモザン監督が強く興味を引かれた現代都市TOKYOの特異な曖昧さ。言葉の通じない監督との共同作業の下、主演の武田真治、吉川ひなのがユニークなボディランゲージでそれぞれの個性を発揮し、終盤にはビートたけしが情けないヤクザ役で登場する。", "1996-01-01", "2,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本鬼子〈リーベンクイズ〉／　日中15年戦争・　　元皇軍兵士の告白", "日本鬼子〈リーベンクイズ〉／　日中15年戦争・　　元皇軍兵士の告白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 160, "1931年から始まった日中15年戦争の軌跡を、当時の実行者である元皇軍兵士14人が語るドキュメンタリー。延々と繰り返された殺りくや拷問、大量処刑の実体をひもといていく。当時の日本軍が犯した加虐行為の愚かさを訴えるだけでなく、事実を語る現在の登場人物たちの苦悩も描かれ、資料性の高い貴重な記録となった。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rio Bravo", "リオ・ブラボー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "テキサス州の南端リオ・ブラボーのある酒場で揉め事が起き、無頼漢ジョーが丸腰の男を撃ち殺した。保安官チャンスはジョーを投獄するが、ジョーの兄は殺し屋を雇い、町を封鎖してしまう。孤立したチャンスは僅かな仲間と共に壮絶な戦いに挑むことに……。「赤い河」のジョン・ウェインを主役に選び、多くの古いスタッフたちを集めて作られた、ホークス監督言うところの『本物の西部劇』は大成功を収めた。", "1959-04-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THREE MUSKETEERS", "三銃士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "ルイ13世治下のフランスで、腹黒い宰相の陰謀に立ち向かう三銃士と彼らに見こまれたダルタニアンの活躍を描く、デュマの痛快小説の映画化。MGMらしい豪華なキャストで、ダルタニアン役のG・ケリー以下、ヴァン・ヘフリンらの三銃士や、色をそえる女優陣もL・ターナーを筆頭に申し分ない。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIDE BEYOND VENGEANCE", "烙印の狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ジョナスは11年間働いて貯めた１万7000ドルを持って妻の待つ故郷に帰る途中、３人の男に牛泥棒呼ばわりされ、金を奪われたうえ、胸に泥棒の烙印“T”を押される。彼は復讐を誓い、怒りに燃えて町へ急いだ……。テキサスを舞台に、正義のために命を懸ける強い西部男の姿を描いたアクション・ドラマ。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DET，YANI DOKHTAR", "７本のキャンドル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "テヘランの工場で働く少年シュアンの妹バルトが、全身麻痺の病に襲われる。たまらずシュアンは病院で精密検査をしてもらったり、祈祷師や病気治しの笛吹きの達人の世話になるが、誰もバルトを回復させることはできない。それでも奇跡を信じて、シュアンは街を奔走するのだが……。世界が注目するイラン人映画作家A・ジャリリのセリフに頼らない静謐な映像の力が、観る者の心を揺さぶる神話的なドラマ。過酷な状況の中に生まれる美しくも力強い人間の感情が、神を信仰し続ける庶民の姿を通して浮かび上がっていく。ヴェネチア映画祭で金のオゼッラ賞に輝き、ジャリリの名を一躍世界に広めた作品でもある。", "1994-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cars", "カーズ", "", "", "http://ecx.images-amazon.com/images/I/51poBoA%2BoML._SL210_.jpg", 116, "「トイ・ストーリー2」以来、7年ぶりにジョン・ラセター自ら監督に挑戦したディズニー／ピクサーの最新作。レストランがガソリンスタンド、靴屋がタイヤ販売店という「クルマの世界」を舞台に、傲慢な新人レースカー、ライトニング・マックイーンの成長を描く。声の出演は、オーウェン・ウィルソン、ボニー・ハント、そしてハリウッド一のカーレース狂として知られる名優ポール・ニューマン。", "2006-07-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gosford Park", "ゴスフォード・パーク", "", "", "http://image.eiga.k-img.com/images/movie/1410/315x210.jpg?1546", 137, "32年の英国、郊外にある貴族のカントリー・ハウス、ゴスフォード・パークに貴族、召使い、ハリウッドから来た映画プロディーサーらが集う。やがて、館の主人が変死する事件が起きたことから、秘められていた人間関係が顕わになっていく。アカデミー賞6部門ノミネート（作品賞、監督賞、助演女優賞、美術賞、衣装賞、脚本賞）のうち、唯一受賞した脚本は、ルイ・マル監督「ダメージ」などのジュリアン・フェローズ。", "2002-10-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナースのお仕事 ザ・ムービー", "ナースのお仕事 ザ・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ナースたちの恋と友情、仕事にかける情熱を描き高視聴率を得たTVドラマの劇場版。ドジだけど明るさが魅力のナース、朝倉いずみは、新婚なのに不満気味。夫で新米外科医の高杉が多忙のため新婚旅行に行けないのだ。そんな中、退院したばかりの猿渡が再入院させろと大騒ぎ。断られた彼は、マシンガンを片手にナースステーションを占拠する。観月ありさや松下由紀ら、TV版と同じメンバーが集結。底抜けに明るいドタバタ騒ぎをハイテンポで描く。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WIRED ： THE LAUGHS AND TIMES OF JOHN BELUSHI", "ベルーシ ブルースの消えた夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "『ベルーシ殺人事件』の題で訳されているB・ウッドワードの原作を映画化し、ジョン・ベルーシの半生と死にまつわる物語を描いていく作品。死体安置所で目を覚ましたベルーシは、天使とともに自分の過去を見る旅に出る……。ベルーシのそっくりさんM・チキリスを見つけ出すまで３年かかったという。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朱雀門", "朱雀門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "公武合体を策す幕府により、貴公子、有栖川宮との結婚を控えながら将軍家茂への降嫁を余儀なくされた皇女、和ノ宮。和ノ宮と18歳の春まで起居をともにし、その気持ちがよく分かる侍女の夕秀だが、しかし有栖川宮に人知れず想いを寄せてしまう。有栖川宮もまた、夕秀の情熱と魅力の虜になる。悲恋メロドラマ。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JURASSIC PARK", "ジュラシック・パーク", "", "", "http://image.eiga.k-img.com/images/movie/45490/315x210.jpg?1666", 127, "はるか昔に絶滅した恐竜たちが現代によみがえったらどうなるだろうか？　破天荒なアイデアを遺伝子工学に基づいて描き、ベストセラーとなったM・クライトンの原作を、S・スピルバーグが特殊技術の粋を尽くして映像化した驚嘆の恐竜ワールド。古代の琥珀の中の蚊から恐竜のDNAを抽出し、バイオテクノロジーによる恐竜の再生が成功した。コスタリカ近くの小島に建設中の“ジュラシック・パーク”は、よみがえった恐竜が歩き回る史上最大のテーマパークになるはずだった。モニターとして３人の学者と二人の子供が園内をめぐるが、コンピュータのトラブルから制御されていたはずの恐竜たちが暴れ出し、肉食恐竜は人間に襲いかかった……。画面の中を縦横に駆けめぐる恐竜の躍動感、現実感には映画がここまでできるのかという感動を与える。", "1993-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あの夏、いちばん静かな海。", "あの夏、いちばん静かな海。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「その男、凶暴につき」「3-4X10月」ですさまじいばかりのバイオレンス描写を見せつけた北野武が、今度は一転してラブ・ストーリーに挑んだ意欲作。聴覚障害者でゴミ収集車助手のアルバイトをする茂は、ひょんなことからサーフィンを始め、夢中になって練習に励む。それを優しく見守る恋人の貴子。そして茂の腕前は次第に上達していき、サーフィン大会に出場するまでになるが、夏が終わると同時に茂の姿は海辺になかった……。サーフィンを通して描かれる聴覚障害者の恋人たちの姿に焦点を当て、セリフ（もちろん主人公にセリフはない）や背景に流れる音楽も控えめにした映像はもの悲しさを漂わせる。北野武の並々ならぬ才人ぶりを証明した一編といっていいだろう。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE REFLECTING SKIN", "柔らかい殻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "アイダホの田舎町のガソリンスタンドの息子セスは、近所の未亡人が吸血鬼だと思い込んでしまう。セスの妄想が広がるなか、町では少年殺人事件が繰り返されていた……。少年の抱く不安と恐怖を現実と幻想の間で描くのは小説家や画家としても活躍するP・リドリー。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Initial D", "頭文字D THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/1090/315x210.jpg?3601", 109, "しげの秀一原作の人気コミック「頭文字D」（講談社）を、「インファナル・アフェア」のスタッフ＆キャストで映画化。主人公・藤原拓海には、台湾のカリスマアーティストのジェイ・チョウ、ヒロイン役に日本の鈴木杏。“藤原とうふ店”を営む父と2人暮しの拓海は、父の愛車ハチロク＜AE86＞で毎日、峠道を走っているが、彼の走りに目をつけた走り屋たちが、次々に拓海にレースバトルを挑む。", "2005-09-17", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("復活の日", "復活の日", "", "", "http://ecx.images-amazon.com/images/I/41RARf1UyBL._SL210_.jpg", 156, "事故でばらまかれたアメリカの細菌兵器により人類はほぼ全滅、わずかながら南極に逃れて生き残った人々の運命を、国際スター競演の大スケールで描いた、小松左京原作の近未来サスペンス。20数億円の製作費をかけ、大々的な南極ロケを敢行した。日本人が全滅する前半の衝撃から後半の人類再生までを描く。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎紙風船", "男はつらいよ・寅次郎紙風船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "九州路の日田で知り合った若い娘・愛子に商売のサクラをつとめてもらった寅さん。病気で寝ている昔の仲間から自分が死んだら女房をもらってくれと言われてあたふた。帰京した寅のところへ彼女たちが現れて……。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOSSIP", "ゴシップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "グレタ・ガルボの代表作「クリスチナ女王」がリメイクされることに。既にスクリーンテストを終えた９人のベテラン女優が、今まさに主役の座を射止めようとしていた。不倫や妊娠、スキャンダルに失恋など、彼女たちの抱える悩みの数々、そして水面下ではびこる駆け引きをユーモアにくるんで暴き出しながら、主役決定までの24時間を描く内幕群像劇。", "2000-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tacones lejanos", "ハイヒール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「オール・アバウト・マイ・マザー」等の作品で知られるスペインの鬼才、ペドロ・アルモドバル監督が手掛けた、母と娘の愛憎劇を描くコメディドラマ。メキシコからスペインに凱旋帰国した人気歌手ベッキーは、15年ぶりに自分の娘レベーカと再会を果たす。レベーカは、母の元恋人マヌエルと結婚していた。しかし夫婦仲はうまくいっておらず、マヌエルはそのことをベッキーに打ち明ける。その1カ月後、マヌエルが何者かに殺され……。", "1991-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カラスの親指", "カラスの親指", "", "", "http://image.eiga.k-img.com/images/movie/57330/315x210.jpg?2569", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鞍馬天狗 江戸日記", "鞍馬天狗 江戸日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "幕末の江戸では幕府の権威をかさにきて、根岸丹後守の一党が勤皇の志士の暗殺をはじめ、強盗やゆすりなどの目に余る暴挙を行っていた。一方、佐竹恵久助は同志とともに一挙に事を決するため、京都の同志との連絡を鞍馬天狗に依頼する……。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Breathless", "ブレスレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ジャン＝リュック・ゴダール監督の傑作「勝手にしやがれ」を、1980年代のアメリカに置き換え、「愛と青春の旅立ち」で大ブレイクを果たしたばかりのリチャード・ギア主演でリメイクした作品。自由奔放で破天荒なチンピラ青年ジェシーは、ラスベガスで一夜を共にしたフランス娘モニカのことが忘れられず、彼女を追ってロサンゼルスへと向かう。ところがその道中、誤って警察官を撃ってしまい、警察から追われることに……。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時代屋の女房２", "時代屋の女房２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "安物骨董店・時代屋にふらふらと居ついた真弓と主人の安さんの、奇妙な夫婦生活を描く第２作。安さんの旧友で、画家くずれの男が時代屋に転がり込むことで巻き起こる悶着を、人情味豊かに綴る。１作目の夏目＝渡瀬に代わって、名取＝古谷のとり合わせ。「アイドルを探せ」の長尾啓司の監督デビュー作。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Recruit", "リクルート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "CIAの訓練所を舞台に偽装と現実が錯綜するサスペンス・ミステリー。マサチューセッツ工科大生ジェームズは、CIAの採用担当者ウォルターにスカウトされて、特別訓練基地ファームへ。しかし、訓練生が互いを欺き合う心理戦を強いる訓練の中で、ジェームズはどこまでがテストで、どこまでが現実なのか困惑していく。そんなとき、彼に極秘指令が下るのだが。監督は「ダンテズ・ピーク」「13デイズ」のロジャー・ドナルドソン。", "2004-01-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("More American Graffiti", "アメリカン・グラフィティ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "クルマと女の子とロック……。カリフォルニアの小都市で、夏の夜に青春を謳歌する若者たちをノスタルジックに、しかもいきいきと描いた「アメリカン・グラフィティ」。あの平和な時代から３年、ベトナムへ、反戦運動へ、ヒッピー生活へ——それぞれ別の道を歩み始めた前作の登場人物を、４年にわたる大晦日の出来事を交錯させながら追っていく。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大いなる幻影", "大いなる幻影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "音楽関係の仕事をしている青年ハルと郵便局に勤める恋人ミチは、それなりに幸福な日々を過ごしていたが、形のない漠然とした不安を抱いていた。ある日、海に出かけた二人は浜辺で異国の兵士の骸骨を発見し、それから別れ別れになっていく。黒沢清監督が映画美学校の生徒たちをスタッフにして撮った恋愛劇で、彼の初期作品を思わせる１本。新世紀を迎え、熱狂が過ぎ去った2005年の近未来が舞台になっており、気怠い脱力感と不思議な清々しさが同居する作品になっている。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ladies of Leisure", "希望の星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "後に「或る夜の出来事」「素晴らしき哉、人生！」などの傑作を生みだしたフランク・キャプラ監督による恋愛ドラマ。富豪の跡取り息子ジェリーは、画家になることを夢見ていた。彼には親が決めた婚約者がいたが、ある日、絵のモデルとして起用した女性ケイと恋に落ちる。当然ジェリーの両親は2人の交際に猛反対するが……。ヒロインのケイを演じるのは、この作品を機にキャプラ作品の常連となるバーバラ・スタンウィック。", "1930-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ライフ・オン・ザ・ロングボード", "ライフ・オン・ザ・ロングボード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "定年退職後のライフ・プランは人それぞれだが、こんな老後も面白い……と思わずにいられない、ユーモラスでハートフルな人間ドラマ。本作で主人公が選んだ第２の人生は、なんとサーフィン。亡き妻の言葉を思い出し、無謀とも思える挑戦に四苦八苦しつつ、人生という波に乗り続けようとする、そんな彼の生き方を温かいまなざしで描き出す。名バイプレーヤー、大杉漣が今回は主人公に扮し、人間味を遺憾なく発揮。すべて代役なしでこなしたという、サーフ・シーンでの体当たりの熱演も見ものだ。55歳で定年退職したものの、膨大な時間を持て余す一雄。サーフボードを持っていたことを思い出した彼は、サーフィンへの挑戦を決意。かくして一雄は次女と共に種子島に向かい……。", "2005-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A DAY’S PLEASURE", "一日の行楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "C・チャップリンが、「犬の生活」と「キッド」の間に撮った短編の一つ。物語はピクニックに出かけたある一家がさんざんな目に遭う、というもの。単なる戯画ではなくペーソスを加味してあり、後の作風をも予告している。「キッド」の名子役のJ・クーガンの映画初出演。DVDは「ラヴ・チャップリン！短編集」に収録。", "1919-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BAG OF RICE", "神様への贈り物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "家族から相手にされていないおばあさんが、米の配給をもらいに行く途中、近所の６歳の少女と会い、一緒に行くことになる。しかし配給の米は二人には重過ぎた。それでも二人は人々の親切により、米を運んで行くのだが……。テヘラン市内の名所が紹介される一種のロード・ムービー。", "1996-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ME AND THE MOB", "サンドラ・ブロック　恋人はマフィア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "恋人からも愛想をつかされそうな売れない作家ジミー。思い余って彼は、“犯罪実録小説”執筆のために、叔父のトニーに取り入ってマフィアの中に潜入する。しかし。ジミーにまわってくる仕事は、借金の取立などケチな仕事ばかり。このままでは、マフィアの全貌を暴いたヒット作など到底書くことができない。焦るジミーに、トニーから思いがけない大仕事の依頼がはいる。ブレイク以前のS・ブロックがジミーの恋人役で、悩殺ランジェリーに身を包み、きわどいテクニックで恋人を昇天させる熱演を披露している。以前ビデオ発売された未公開作品。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　TUSKEGEE　AIRMEN", "ブラインド・ヒル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「マトリックス」のL・フィッシュバーン主演の異色戦争映画。1943年、アメリカ軍に実験的に組織された黒人部隊。戦線への憧れを抱いていた彼らに課せられた任務は、危険な特殊任務ばかり。人種差別の偏見のなか、プレッシャーでミスを犯し、命を落とす者が続出する。精神的にも肉体的にも追い詰められた彼らは、ついに最悪の激戦地帯に送り込まれる。果たして彼らに勝利はあるのか？　壮絶な空中戦のシーンでは、グラマン、ムスタング、メッサーシュミットなど、当時の戦闘機が続々登場する。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Breakfast on Pluto", "プルートで朝食を", "", "", "http://image.eiga.k-img.com/images/movie/1600/315x210.jpg?4451", 127, "「クライング・ゲーム」「インタビュー・ウィズ・ヴァンパイア」のニール・ジョーダン監督が、「ブッチャー・ボーイ」のパトリック・マッケイブと再びコンビを組んだコメディドラマ。舞台は60～70年代のアイルランドとイギリス。女装が趣味の青年パトリックは、自分が孤児であることを知り、母親を探す旅に出るが……。主演に「28日後…」のキリアン・マーフィ。リーアム・ニーソン、スティーブン・レイらジョーダン組の常連が共演。", "2006-06-10", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("「されどわれらが日々」より 別れの詩", "「されどわれらが日々」より 別れの詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "昭和39年に芥川賞を受賞、青春バイブルといわれた柴田翔の『されどわれらが日々—』の映画化。互いに愛し合っていると信じている二人に疑問が芽ばえて……。男と女の真の愛情とは何かをテーマに若者の苦悩を描いた一編。小川知子が優れた演技勘をみせて好演。作品中、手紙がとても効果的に使用されている。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEND OF THE RIVER", "怒りの河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "開拓団の案内役グリンはオレゴン州への途上で、リンチされかかった無法者コールを助ける。二人の間に奇妙な友情が芽ばえるが、目的地に着くとコールは開拓団の食糧を奪って売りさばこうと計略。グリンはコールを倒すため立ち上がる……。A・マン監督、J・スチュアート主演のコンビによる西部劇。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shane", "シェーン", "", "", "http://ecx.images-amazon.com/images/I/51o1NDn1HVL._SL210_.jpg", 118, "見事な俯瞰撮影で幕を開けるこの作品は、巨匠スティーヴンスがシェーファーの原作を得て作り上げた本格的西部劇。西部の新天地では、土地をめぐって開拓農民と牧畜業者との対立が絶えなかった。北へ行く途中ジョー一家に立ち寄ったシェーンは、世話を受け味方につく。ある日、酒場で敵対するライカー一味にからまれたシェーンは、黙って引き下がるが、二度目は徹底的にぶちのめす。復讐のためライカーは、名うての殺し屋ウィルソンを呼び寄せるが……。典型的な西部劇のパターンを踏襲しながら、この作品がすぐれて西部劇史上に残る名編となったのは、すべての対象を自然主義ともいえる徹底したリアリズム手法で詩情豊かに謳い上げた点と、少年の目に映るヒーロー＝シェーンを描いた点にある。ラッド一世一代の名演。殺し屋J・パランスのすご味も忘れ難い。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀座のお姐ちゃん", "銀座のお姐ちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "銀座にある週刊誌『ラッキー』編集部に勤める涼子。製薬会社の一人娘、園江。ファッションモデルの季里子の３人は大親友。お互いに助け合いながら、恋に仕事に楽しい毎日を送っている。ある日、涼子はロマンスグレーの独身社長と知り合い、結婚まで決意する。だが彼は園江の父だった……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クリスマス・クリスマス", "クリスマス・クリスマス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "劇団“ナイロン100℃”の看板役者、大倉孝二を筆頭に、演劇界出身の個性派が大挙出演したコメディ。世の中のファンタスティックな出来事は、すべて“ファンタジー保存協会”なる秘密組織の仕業という奇想天外なストーリー展開に加え、舞台的なノリで繰り広げられる独特のテンポも楽しい１本だ。恋人、美和といつものように口ゲンカをして家を飛び出した健太は、その直後、突然止まった１台のワゴン車に拉致されてしまう。", "2004-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Daehan Mingook 1%", "大韓民国1％", "", "", "http://image.eiga.k-img.com/images/movie/55940/315x210.jpg?2742", 105, "韓国軍のなかで、入隊率わずか1％の精鋭部隊・海兵隊特殊捜索隊に初めて配属された女性士官の奮闘をユーモラスに描いたアクション・ドラマ。海兵隊の訓練課程をトップ通過した女性副士官イ・ユミは、念願の特殊捜索隊に配属されるが、任務は軍事訓練で毎回最下位の落ちこぼれチームを最高のチームに育て上げることだった。しかし、イ・ユミを上官として認めようとしないチーム員やライバルによる妨害工作など、数々の問題が待ち受けていた。", "2011-03-05", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャッチボール屋", "キャッチボール屋", "", "", "http://image.eiga.k-img.com/images/movie/33700/315x210.jpg?1999", 105, "公園で“キャッチボール屋”をすることになった青年と、彼の元に集まった風変わりな大人たちとの交流を描くハートウォーミング・ドラマ。失業中の青年タカシは、公園で出会った見知らぬ男性から“キャッチボール屋”を任される。キャッチボールを通して人々の想いを受け止めていくうちに、タカシ自身も忘れかけていた大事なものを思い出していく。北野武作品などで助監督を務めてきた大崎章の監督デビュー作。主演は若手個性派俳優の大森南朋。", "2006-10-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雑婚時代", "雑婚時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "芸能界の内幕を背景に描く風俗ドラマ。芸域を広くするためには体当たりで何でもやるのだと、大女優になりたい一心で家を飛び出した友野マリ子は、プロデューサーやスポンサーとことごとく関係を結んでは大役を勝ち取って名声を得ていく。そんな彼女を一人の男がじっと見つめていた。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THEY CAME FROM WITHIN", "デヴィッド・　クローネンバーグの　シーバース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "カナダの鬼才、クローネンバーグ監督の35ミリ劇場用映画デビュー作。カナダのとある避暑地の島。人々の詰めかけるその島で、実は一人の医師による恐るべき実験が行われていた。大柄な寄生虫を人間の内臓に同化させ、野性的な活力を得ようとしたのだ。その結果、人体実験に使われた少女は狂暴化し、医師は自らの手で彼女を殺し、自殺した。ところが、発展家の彼女と関係した男たちもみな寄生虫に取り憑かれ、肉体を乗っ取られていた。狂気の集団は増え続け、やがてカナダ全土に広がっていく。血・内臓・セックスというクローネンバーグのグロテスク３要素が開花した記念すべきスプラッタ・ホラー。DVDタイトルは「シーバース」。", "1975-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEART AND SOULS", "愛が微笑む時", "", "", "http://ecx.images-amazon.com/images/I/51yO2tnDdaL._SL210_.jpg", 104, "バスの事故で不遇の死を遂げた４人の男女。幽霊になった彼らは、その死と同時に生まれたトーマスに取り憑く。やがて成長したトーマスは幽霊たちの頼みを聞き入れ、彼らの未練を晴らすことに……。R・アンダーウッド監督がゴーストと人間の交流をハートフルに描いたファンタジー。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER AT 1600", "ホワイトハウスの陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ホワイトハウスで起きた殺人事件を追う殺人課刑事リージスと女性シークレット・サービス、ニーナの奮闘を描くサスペンス・アクション。捜査を進める二人が国家機密という厚い壁に阻まれながら、事件の裏に隠された国家的陰謀を探り出すまでを、二転三転するストーリー展開で見せていく。ニーナ役のD・レインが久しぶりに存在感ある演技を披露している。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若者たち", "若者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "もとは同名のフジテレビの連続ドラマだったが、その社会批判的な内容が同局の許容範囲を越えたため打ち切りとなった、いわくつきの番組の映画化。学歴差別、被爆者、出稼ぎ農家、学園紛争といった様々な問題を５人の兄妹の生き方に絡めて描く。監督はTV版と同じ森川時久でこれが第１回監督作品となった。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アタシはジュース", "アタシはジュース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "人材派遣会社とは表向きで、実体は男女のもめごとの解決を仕事にする一瀬薫。元高校教師の彼女は、かつての教え子で会社のスタッフでもある中川静香とレズビアンの関係にあった。そんな薫に思いを寄せる極東興行のボス・一条は、彼女を静香から引き離すことを画策。薫には秘密で静香に危険な仕事を依頼する。思惑通りトラブルに巻き込まれ、ナイフで刺されてしまう静香。その時、彼女を救ったバングラディシュ人・ムスタファは静香に新しい愛に目覚めさせるのだが……。ピンク界で活躍していたサトウトシキによる軽妙な語り口の一編。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Don", "DON（ドン）　過去を消された男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 168, "インド映画界を代表するスター俳優、シャー・ルク・カーンが1人2役を務める本格サスペンス・アクション。マレーシアの国際的犯罪組織を牛耳ってきたドンが、ついに逮捕された。この機会に犯罪組織の壊滅を狙うデシルバ警視は、ドンにそっくりな青年ビジャイを替え玉として組織に潜入させることに。ところが、警視が死亡したことによって計画を知る者がいなくなり、ビジャイはドンとして警察から追われる身となってしまう。", "2008-08-30", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("All about Love", "愛と死の間（はざま）で", "", "", "http://image.eiga.k-img.com/images/movie/33600/315x210.jpg?2332", 102, "05年秋に香港、韓国、シンガポールなどアジア各国で大ヒットを記録したアンディ・ラウ主演のラブ・ストーリー。交通事故で妻を亡くし、外科医から救急隊員に転職したコウ。ある晩、彼は1人の女性を救命するが、その女性には亡き妻の心臓が移植されていた……。監督は、アンディ・ラウのビジネスパートナーで、「メイド・イン・ホンコン」のフルーツ・チャンなどを発掘した製作者としても知られるダニエル・ユー。", "2006-08-12", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スリーピー・ヘッズ", "スリーピー・ヘッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ニューヨークを拠点に活動し、トニー・チャン監督の「コンビネーション・プラッター」の撮影監督などを務めた細谷佳史の初監督作。マンハッタンを舞台に、夢を抱く３人の日本人男性の姿を追う青春グラフィティーだ。バンドを解散しN.Y.にやって来たシンガーソングライターの浩は、かつてのバンド仲間、晃とその友人、健太が住むアパートに転がり込む。貧乏暮らしの中で、日系企業のOL、秋子との恋をはぐくむ浩。そんなある日、晃がドラッグの過剰摂取で死んでしまう。しかし、浩と健太は不法滞在のため、警察にも届けられず……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若さま侍捕物帖", "若さま侍捕物帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "御用商酒問屋伊勢屋の清酒で毒味役が死に、伊勢屋一門は遠島、店は取りつぶしとなった。すべては老中の堀田に取り入り、唐金屋と組んで私腹を肥やそうとする小納戸役・鈴木の陰謀だった。若さまは伊勢屋の娘・おちかに仇討ちの助太刀を頼まれ、ついには鈴木らの正体を暴いていくのだった。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Up for Grabs", "100万ドルのホームランボール　捕った！盗られた！訴えた！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "莫大な価値のホームランボールを巡る熾烈な争いを追った爆笑ドキュメンタリー。2001年10月7日、サンフランシスコ・ジャイアンツのスター選手バリー・ボンズが、年間最多記録を更新する73号ホームランを放った。破格の値がつくそのボールを1人の幸運な男がキャッチするが、彼にボールを奪われたという人物が現われ訴訟事件にまで発展。当事者や目撃者らの証言を通し、訴訟社会・アメリカの現実を浮き彫りにしていく。", "2007-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嫉妬", "嫉妬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "夫の浮気と自分への虐待に苦しみ続けてきた妻が、一人の新聞記者との再会をきっかけに夫のもとを離れていく。夫婦生活の機微をその破局に至るまで冷静に見つめた作品。吉村公三郎の名パートナー、新藤兼人のオリジナル・シナリオの映画化。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Y Tu Mama Tambien", "天国の口、終りの楽園。", "", "", "http://image.eiga.k-img.com/images/movie/1690/315x210.jpg?3444", 106, "監督は「ハリー・ポッター」第3作「アズカバンの囚人」の監督に決まったアルフォンソ・キュアロン。「リトル・プリンセス」（95）、「大いなる遺産」（97）とハリウッドで活躍した後、10年振りに故国メキシコで撮影、メキシコで大ヒット。幼なじみの17歳、フリオとテノッチは、夏休みに美しい人妻ルイサを誘い、3人で“天国の口”という名の海岸をめざして旅に出る。その道中で、それぞれの想いが現れていく。", "2002-08-10", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESTINATION MOON", "月世界征服", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "月旅行をドキュメンタリー・タッチで描いた、初の本格的カラーSF。宇宙服や月世界、無重力状態など、当時はまだ空想の域を出ていなかったものを、実にリアルに見せてくれる。宇宙旅行の知識をウッドペッカーのアニメで伝える、ユニークな手法も見逃せない。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("You Don't Mess with the Zohan", "エージェント・ゾーハン", "", "", "http://ecx.images-amazon.com/images/I/51uHQnAW7yL._SL210_.jpg", 113, "「再会の街で」のアダム・サンドラー主演、「トランスフォーマー」のジョン・タトゥーロ共演によるアクションコメディ。イスラエルの凄腕諜報員ゾーハンは、美容師になることを密かに夢みていた。ライバルとの戦いで殉職したと見せかけてアメリカへ渡ったゾーハンは、ニューヨークで念願の美容師として働きはじめる。驚異の身体能力を駆使した裏ワザで熟女たちから絶大な人気を獲得するゾーハンだったが、かつてのライバルに見つかってしまい……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("笑の大学", "笑の大学", "", "", "http://ecx.images-amazon.com/images/I/41NAC98M86L._SL210_.jpg", 121, "三谷幸喜の舞台劇を、数々の人気TVドラマを輩出する星護監督が映画化。戦争の影が忍び寄る東京で、“笑い”を封印しようとする検閲官と、“笑い”を庶民に提供しようとする劇作家が台本をめぐり、取調室で繰り広げる攻防の物語。やがて立場の違いを越えて深い友情で結ばれていく二人の姿が温かい感動を呼ぶ。両主人公に扮する、役所広司と稲垣吾郎の息の合った巧妙なセリフ回しも見ものだ。戦争の色が濃くなる昭和15年の東京。検閲官の向坂は、劇団“笑の大学”の上演を禁止に持ち込むため、座付作家の椿の台本に無理難題を課す。ところが椿はその指摘をうまくすり抜ける台本を作り上げ……。", "2004-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KING OF THE WHITE ELEPHANT", "白象王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "第二次世界大戦の抗日“自由タイ運動”などで知られる、タイ史に残る政治家P・パノムヨンが製作・脚本を担当し、実現した珍しい英語映画。16世紀のタイ。アユタヤ朝の偉大なる王、チャクラーは戦の準備を進める隣国に対する防備のため、365頭の象を捕らえさせた。そのなかに王国の吉祥とされる一頭の白い象がいることを知ったチャクラー王は、象章の国旗を作らせる。やがて隣国との戦争が勃発。チャクラー王は単身、隣国の王と象上での一騎打ちに挑む……。タイ・アユタヤ朝史において実際にあったエピソードをもとにしており、戦前のタイ映画として唯一完全な形で残っている貴重な作品（アメリカの議会図書館で発見され、日本のフィルムセンターが修復作業を担当）でもある。", "1940-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GO　NOW", "ゴー・ナウ GO NOW", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "難病に冒された青年の恋を、何気ない日常描写を交えながら軽妙かつさわやかに綴った恋愛劇。サッカー好きの青年ニックが、クラブで出会ったカレンと恋仲になる。そんなある日、ニックが多発性硬化症という難病に冒されていることが判明。自暴自棄になる彼を、カレンは懸命に支え続けるが……。どこにでもいそうな等身大の青年ニックを、イギリスの演技派R・カーライルが好演。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍びの者 伊賀屋敷", "忍びの者 伊賀屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "徳川幕府転覆を企てる由比正雪、丸橋忠弥らを阻止するため、松平伊豆守が放った甲賀忍者たちが何者かによって殺されていく。それは島原の乱で死んだ霧隠才蔵の息子の活躍であった。シリーズ６作目は慶安の乱が舞台。八千草薫の“くノ一”がみもの。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SON OF KONG", "コングの復讐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "キング・コングの生地ドクロ島を舞台に、コングの息子の活躍を描いた続編。恐怖の怪物のイメージはなくなり、白毛のコングが、海底火山の爆発から命懸けで人間たちを守るというもの。特撮シーンは前作同様見事な出来栄え。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("綱引いちゃった！", "綱引いちゃった！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「舞妓Haaaan!!!」「なくもんか」の水田伸生監督が井上真央を主演に迎え、綱引きを題材に描くオリジナルコメディ。美人だが真面目でお堅い大分市役所広報課の西川千晶は、あまりに知名度の低い大分市をPRするため、市長命令で女子綱引きチームを結成して全国大会出場を目指すことに。勤務先の給食センターが廃止の危機にある母の容子とその同僚たちを誘い、なんとかチームを結成した千晶だったが、メンバー不足から自身もチーム入りせざるをえず、さらにはキャプテンまで押し付けられてしまう。", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIE　DOWN　WITH　DOGS", "ライダウン・ウィズ・ドッグス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ゲイたちが集まるマサチューセッツ州のビーチ、プロビンス・タウンを舞台とした青春映画。主人公はニューヨークでの生活に嫌気がさし、このビーチにやって来たゲイの青年トミー。性に奔放なこの街で彼が繰り広げる恋人探しと、理想とのギャップをコミカルに描き出す。実際に存在するロケ地のユニークさを生かしつつ、監督自ら演じる主人公がいきなりカメラに語りかけるなど、遊び心あふれる演出がなされたインディーズ・ムービー。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SOLID GOLD CADILLAC", "純金のキャデラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "国際企業株式会社の株を10株だけ持っている女優志願のローラ。株主総会に出てみたが、素人の彼女には素朴な疑問がいっぱい。彼女の口を封じたい会社はローラを雇ってしまうが……。ブロードウェイで上演された戯曲をもとに、CM女優と悪徳経営者との攻防を描いたロマンティック・コメディの傑作。ラストシーンのみカラーで劇的効果を高めている。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wednesday アナザーワールド TWILIGHT FILE VI", "Wednesday アナザーワールド TWILIGHT FILE VI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "オムニバスシリーズ「TWILIGHT FILE」第6弾の1作。人気ダンスユニット「FLAME」の北村悠、「soeur スール」の山本博子らの出演によるSFドラマ。客入りの悪さから解散寸前に追いこまれた劇団「東の国より日は出る」は、次回作を成功させなければ後がないが、自己主張の強い劇団員たちはケンカばかり。言い争いを続ける彼らの前に、不思議な少女・小夜が現われる。彼女が「や・め・てー」と悲鳴をあげると、劇団員たちは突然、動かなくなってしまい……。", "2009-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("坊っちゃん", "坊っちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "戦前から何度も映画化されている夏目漱石の『坊っちゃん』。今回は坂本九が坊っちゃんを演じ、マドンナ役に加賀まりこを配している。脇役陣の中では赤シャツ役の牟田悌三が好演。また、音楽を担当した古賀政男も校長役で出演している。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBOCOP", "ロボコップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "近未来のアメリカ。犯罪発生率の最も高いデトロイトは、巨大コングロマリット・オムニ社に支配され、警察まで企業化し、その傘下にあった。警官のマーフィは一連の警官殺しの犯人と目されるクラーレンスを追っていたが、その一味に惨殺される。警官のロボット化を目指していたオムニ社は、マーフィの体をロボコップとしてよみがえらせた。機械的に正確な射撃の腕、並外れたパワー。ロボコップはたちまち市民のスターとなった。だが、ロボコップは、次第に過去の人間時代の記憶をよみがえらせ、悪夢にさいなまれる。そして自分がマーフィ警官であったこと、クラーレンスに殺されたことを知る。クラーレンスを追い詰めたロボコップは、彼が裏でオムニ社の重役とつながっていることも突き止めるのだが……。アヴォリアッツ国際ファンタスティック映画祭最優秀SFX賞、高等技術委員会賞受賞。後に「トータル・リコール」「氷の微笑」を発表するP・ヴァーホーヴェンが、見事なハリウッド上陸を果たしたSFアクション。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼龍院花子の生涯", "鬼龍院花子の生涯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "土佐・高知の侠客、鬼政こと鬼龍院政五郎の奔放な半生と、彼が溺愛した妾の娘・花子の生涯を、義父への侮蔑から敬慕へと揺れる養女・松恵の目を通して描いた作品。昭和９年、土佐電鉄の労働争議で労組支援にまわった高校教師・田辺の気っぷに惚れ込んだ鬼政は、彼を娘・花子の婿にして一家を継がせようとする。しかし、養女の松恵が田辺を愛し始めており、二人は土佐を出て大阪で暮らすことにしたが……。夏目雅子のタンカ“なめたらいかんぜよっ”のセリフが有名になった五社リアリズム映画の代表作。鬼龍院家の権勢を物語る、広壮な邸宅などの美術セットが質感あふれる。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Rugruts Movie", "ラグラッツ・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/1790/315x210.jpg?2358", 80, "1歳の冒険幼児トミー・ピクルスは、2歳のチャッキー、生後15ヶ月の双子の兄妹フィルとリルとともに、洞窟の中を探検中だ。自分はインディ・ジョーンズと並ぶ冒険家だと思っているトミーだが、洞窟の中で危機一髪…。そんな夢から覚めたトミーには、新しい現実が待っていた。発明家の父親と母親の間に、ベイビーが誕生したのだ。ベイビーはディルと名付けられ、すくすくと育つ。", "1999-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MATERIAL URBANO", "禁断の青い肉体", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "肉感女優アンヘラ・モリーナの妹、モニカ・モリーナの鮮烈なスクリーン・デビュー作。当時、弱冠18歳だった彼女がヒロインの女子大生に扮し、自由に生きる一人の美少女と二人の若者との無軌道な性の戯れを、大胆かつ強烈なエロチシズムに満ちた映像で描く。", "1989-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("残照　フランス・芸術家の家", "残照　フランス・芸術家の家", "", "", "http://image.eiga.k-img.com/images/movie/55230/315x210.jpg?3535", 109, "フランスのアーティストのための老人ホーム「国立芸術家の家」では、画家・彫刻家・音楽家らが創作活動を行っている。18世紀の貴族の別荘を再利用した施設で、彼らは共同生活を送りながら、人生最後の優雅な時を過ごす。NHKで放送されたドキュメンタリー番組を劇場で公開するプロジェクト「This is REAL／NHK-BSドキュメンタリー・コレクション」にて上映。", "2010-02-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蟻の兵隊", "蟻の兵隊", "", "", "http://image.eiga.k-img.com/images/movie/33570/315x210.jpg?2106", 101, "第2次世界大戦終結後、中国に残留して内戦に巻き込まれた日本兵を巡る“日本軍山西省残留問題”にスポットを当てたドキュメンタリー。軍の命令に従って残留した約2600人の兵士たち。ところが政府はその事実を否定し、彼らを脱走兵として扱った。国を相手に裁判を続ける残留兵のひとりである奥村和一が、真相を求めて再び中国を訪れる。メガホンを取るのは、デビュー作「延安の娘」が世界中の映画祭で絶賛された池谷薫。", "2006-07-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("汚れた女〈マリア〉～背徳の日々～", "汚れた女〈マリア〉～背徳の日々～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "瀬々敬久監督が「雷魚」に続いて、実際に起きた事件にインスパイアされて撮り上げた奇妙な犯罪劇。行方不明になった妻を探すタクシー運転手と、その妻を殺した職場の同僚。会ってはいけなかった男女の道行きを、ほのかなユーモアを漂わせながら見つめている。「雷魚」とは対照的な感触を持った一編。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOHN JOHN IN THE SKY", "ジョン・ジョン・イン・ザ・スカイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "少年ジョン・ジョンの夢は自分の手で作った飛行機で、この小さな町から飛び立つこと。太っちょで知的障害者のゼオラとともに、ケンカをしながら飛行機を作り上げていく。映画情報番組『シネマ通信』のレポーターのJ・デイヴィスが同番組のスタッフとともに、故郷ミシシッピーを舞台に自分の少年時代を振り返り、そこで暮らす人々の自然な姿を描いた感動作。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("J’EMBRASSE PAS", "深夜カフェのピエール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "生活のために夜の公園で男娼となった青年ピエールは、奴隷のように虐げられている売春婦イングリッドに出会い、ほのかな思いを寄せるが……。愛に飢えた田舎出の青年が、大都会パリの冬の夜を孤独にさすらう様を描くドラマ。悲痛な現実を題材にした衝撃作ながら、巧みに醸し出されたフランス映画らしいポエジーが出色。ちなみに原題は“キスはしない”という意味で、娼婦が使うお決まりの文句。", "1991-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Green Zone", "グリーン・ゾーン", "", "", "http://image.eiga.k-img.com/images/movie/55300/315x210.jpg?3785", 114, "「ボーン・スプレマシー」「ボーン・アルティメイタム」のポール・グリーングラス監督＆マット・デイモンによるサスペンス・アクション。砂漠に隠された大量破壊兵器の所在を突き止めるという極秘任務につく米軍兵士のミラー（デイモン）は、調査を重ねるも兵器の痕跡すら発見できず、情報の信憑性に疑問を抱き始める。国防省の動きを不審に思ったミラーは部隊を離れ、CIAのブラウンと独自に調査を開始するが、そこには全世界を揺るがす衝撃の真実が待ち受けていた。", "2010-05-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHARAOH", "太陽の王子ファラオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "たび重なる遠征により財政は苦しくなり、政治に介入してくる司祭たちに実権を奪われてしまった王子。紀元前11世紀のエジプトを舞台に、王権と神権の対立をテーマに描くポーランド映画。", "1970-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADIEU BLAIREAU", "暗殺の報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ギャンブルによる多額の借金を抱えたしがない舞台俳優。組織はこれをきっかけに彼を犯罪の世界に引きずり込もうとする。やがて組織に裏切られ、親友を殺された彼は……。仏映画の醍醐味、フィルム・ノワールの魅力を詰め込んだハードボイルド・アクション。レオタールの男の哀愁を漂わせた渋い演技が見事。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エレナー電気工業", "エレナー電気工業", "", "", "http://image.eiga.k-img.com/images/movie/57480/315x210.jpg?4795", 84, "青春と性をテーマに監督たちが独自の個性で自由に製作する「青春H2（セカンド）」シリーズの8作目。実家を飛び出し東京で遊び歩いていた啓介は、父親が急逝したことから、兄とともに実家の小さな工場を継ぐことになる。何事も持ち前の前向き思考と人並みはずれた性欲でしのいできた啓介だったが、リーマンショックに端を発する世界不況の荒波が工場にも押し寄せて……。監督は「ほんとうにあった怖い話」「BOYS LOVE」「少林老女」など、ホラーからコメディ、BLまで幅広いジャンルの映画を手がけてきた寺内康太郎。", "2011-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELLES", "elles／エル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "恋や友情、家族間の問題で揺れ動く５人の女性の姿を描く女性映画。恋人と一定の距離を保つテレビキャスターが４人の友人と再会する。学生と浮気をしている大学教授、自由な恋愛を楽しむバツイチ女性、夫と別居中の主婦、ヘアメイクアーティストとして成功したキャリアウーマン。それぞれ環境も性格も異なる彼女らが、傷付き、悩みながら人生の喜びを見いだしていくまでが、美しい風景が広がるリスボンの港町を舞台に描かれる。", "1997-01-01", "25,2,58,37,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEW CRIME CITY：LOS ANGELES 2020", "ニュー・クライム・シティ ～新犯罪都市～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "2020年、ロサンゼルス。すでに戦争はなくなっているものの、犯罪が氾濫していた。刑務所は犯罪者であふれ、定員オーバー状態。当局は、町の半分を高圧電流フェンスで囲み、巨大な刑務所としてしまう。それに対して、囚人たちは細菌兵器を開発、フェンスの撤去を要求するのだった……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SNAKE EATERIII…HIS LAW", "コブラ・キラー３ 刺青の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "海軍特殊部隊出身で“ソルジャー”と呼ばれるジャックは、上司と衝突し停職処分中に老夫婦から復讐を依頼される。老夫婦の娘が誘拐され、身も心もボロボロにされたのだ。調査を開始したジャックは、街を牛耳る暴走集団“ヘルズ・ヒューリー”が怪しいと狙いをつける……。「コブラ・キラー」シリーズ第３作で、悪役として日本でもおなじみのプロレスラー、C・バンバン・ビガロも登場している。", "1992-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋愛戯曲　私と恋におちてください。", "恋愛戯曲　私と恋におちてください。", "", "", "http://image.eiga.k-img.com/images/movie/55570/315x210.jpg?2329", 0, "人気劇作家の鴻上尚史が自ら手がけた大ヒット舞台「恋愛戯曲」を、深田恭子主演で映画化したラブコメディ。深田が人気脚本家、主婦、セレブ作家という3つの異なるキャラクターを演じ分ける。共演に椎名桔平、塚本高史ら。落ち目の女流脚本家・真由美と、落ちこぼれプロデューサー・正也は、テレビ局の命運をかけたスペシャルドラマを作ることになる。真由美は自分と恋に落ちることを条件に、正也のオファーを受けるが……。", "2010-09-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("友だちのいる孤独", "友だちのいる孤独", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "今村昌平監督が主宰する日本映画学校の1989年度の卒業制作作品。近年多発する青少年による殺人事件をテーマに、学校内でいじめられている二人の高校生が、そのウサを晴らすかのように軽い気持ちで始めた小学生いじめ。それが次第に殺人へとつながっていくまでの心理描写を鋭く描いた意欲作である。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monte Carlo", "恋するモンテカルロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUCK サック", "SUCK サック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "イギー・ポップ、アリス・クーパーら大物ロックスターが出演するバンパイア映画。売れないインディーズバンド「ウィナーズ」のベーシストで紅一点のジェニファーは、ついにバンドに愛想を尽かし、薄気味悪い男と一緒に消えてしまう。しかしその翌日、ジェニファーはバンパイアとなって戻ってくる。人間離れした演奏力とカリスマ性を手に入れたおかげでバンドの人気は急上昇。さらなる人気を手にしようと、ほかのメンバーも次々とバンパイアになっていくが……。", "2011-02-05", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIVING FREE", "永遠のエルザ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "アダムソンのベストセラーで、映画化もされた「野生のエルザ」の続編。牝ライオン・エルザが野生に帰るまでの第１作のあと、エルザが産んだ３匹の子供ライオンが、伝染病によるエルザの死によって、厳しい自然といかにして戦ったのかを描いている。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KAUF DIR EINEN BUNTEN LUFTBALLON", "白銀に躍る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1960年代の女子フィギュア・スケート選手、イナ・バウアーが主演した青春映画。スポーツ映画やアイドル映画のテイストに溢れた楽しい一作だ。舞台装置家兼アイスホッケー選手ハンスが、スケートの上手な美貌の女性インゲと出会い惚れてしまう。彼女の夢がミュージカル・スターになることだと知ると劇場に紹介するが、それは大きな間違いだった!?", "1961-01-01", "7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARAVAGGIO", "カラヴァッジオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "イタリア・ルネッサンス期に最も異端に生きた画家、ミケランジェロ・メリツ・カラヴァッジオのスキャンダラスな生涯を描いた作品。アートフィルム界の異才として知られていたD・ジャーマンが手掛けた劇映画。その映像美は、まさに“絵画”のようだ。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRE OVER ENGLAND", "無敵艦隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "16世紀、当時無敵を誇っていたスペイン艦隊を打破したエリザベス I世の時代に、英国とスペインの対立をめぐって繰り広げられていたスパイ合戦をサスペンスフルに描いた歴史劇。実生活で後に夫婦となったL・オリヴィエとV・リーはこの作品で初共演した。", "1937-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チャイニーズ・ディナー", "チャイニーズ・ディナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "殺し屋と彼の標的となった男の息詰まる攻防を描く密室劇。高級料理店の一室。政界絡みの裏取引きで荒稼ぎしているオーナーの星野が、50億円もの大金が動く取引相手からの連絡を待っている。そこへ、銃を手にした殺し屋が現れる。雇い主は誰なのか、落ち着き払ったこの男はなぜすぐに殺さないのか？　それぞれに思惑を秘めた男たちの駆け引きを、閉ざされた空間の張り詰めた空気の中に描出。“食べる”という行為のグロテスクさが、異様な空気を醸し出している。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金星", "金星", "", "", "http://image.eiga.k-img.com/images/movie/57040/315x210.jpg?3326", 60, "東京・下北沢の短編映画館トリウッドと専門学校東京ビジュアルアーツの共同映画製作プロジェクトで、現役学生が企画から制作、宣伝までを担当する「トリウッドスタジオプロジェクト」の第6弾作品。先天性の視覚障害をもつ少年・俊は、同じく視覚障害をもつ少女ほのかを誘い、介助者の聡子とその兄・勇治と4人でハイキングに出かける。しかし山中で出会った大学生に差別行為を受け、障害者は助けてもらって当然と考えていた俊は激怒し……。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Johnny Guitar", "大砂塵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1890年代のアリゾナを舞台に、そこの賭博場にやって来た流れ者の男ジョニーと店の女主人ビエンナとの、行き場のない愛の世界を綴った作品。主役のヘイドンが渋い好演を見せる。ペギー・リーの歌う主題歌『ジョニー・ギター』が甘く切なく響きわたる。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLUBBER", "フラバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1961年のディズニーの名作「うっかり博士の大発明　フラバー」を、「101」に続いてJ・ヒューズとディズニーがリメイク。ぷよぷよとしたゼリーのような不思議な物体“フラバー”が奇想天外な騒動劇を繰り広げる。発明家のフィリップ博士は研究熱心なあまり自分の結婚式を二度も忘れるほどのうっかり者。そんな彼が３度目の結婚式の日、偶然にもスーパー・エネルギーの研究に成功、緑色の不思議な物体フラバーを開発する。婚約者にフラれて落ち込む一方で、フィリップは驚異的なエネルギーを発揮するフラバーの効果に意気揚々。そこへフラバーの横取りをたくらむ悪徳資産家が現れ……。変幻自在に姿を変え、飛んだり跳ねたりダンスをしたりとオチャメなフラバーが、最新SFXによって生き生きと動き回る。フラバーに振り回される博士に扮するR・ウィリアムズの奮闘ぶりも楽しい。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おとうと", "おとうと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "幸田文の原作を水木洋子の脚色で映画化、高い評価を得た市川崑の代表作。片足を病み、後妻というコンプレックスを強く抱いている母。作家である父は、その母を扱いあぐねている。この父母の間で、愛を与えられず不良化していく弟と、その弟をかばい、愛情を注ぐ勝ち気な姉を軸に物語は進む。遠慮なく悪口を言い合い喧嘩する姉と弟は、しかしそれゆえ深く信頼し愛し合っている。ある日弟は、暴れるだけ暴れ家族を困らせたあげく、突然結核で倒れる。父母の愛が初めて弟に注がれ、家族が一つになったのもつかの間、弟は死んだ。姉はショックで倒れたが、ふと目を覚まし、あと片付けを始めるのだった。恋人同志もかくやの美しい姉弟愛を、市川監督はワンショットごとに目配りの利いた色彩処理と画面構成でカラリと描き上げ、彼の文芸路線の白眉となった。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女囚処刑人マリア", "女囚処刑人マリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "恋人を殺したヤクザ組織を単身壊滅させ、終身刑の判決を受けた女・マリア。刑期短縮と息子との再会を条件に政府機関の暗殺者となった彼女に、猟奇殺人犯を抹殺する新たな指令が下る。だが、事件の陰には台湾マフィアと組んだ巨大な犯罪組織の陰謀が……。劇画『マリア～ブーメランのように～』を映画化したエロティック・アクションで、２本のオリジナル・ビデオに続く初の劇場版。川島なお美、杉本彩に続く３代目マリアには元C.C.ガールズの青田典子が扮して映画初主演。グラマラスな肢体とクールなマスクを生かし、切れのいいアクションを見せる。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("接吻", "接吻", "", "", "http://image.eiga.k-img.com/images/movie/53390/315x210.jpg?2327", 108, "テレビに映った殺人犯にひと目惚れした女性の破滅的な愛を描く異色ラブストーリー。まったく面識のない一家を無差別に殺害したとして警察とマスコミに名乗り出た坂口。逮捕の瞬間をテレビで目の当たりにした孤独なOL京子は、謎めいた微笑みを浮かべる坂口に親近感を抱き、事件についての情報を集め始める……。殺人犯に一途に思いを寄せるヒロインを小池栄子が演じ新境地を開拓。監督は「ありがとう」の万田邦敏。", "2008-03-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロボコン", "ロボコン", "", "", "http://ecx.images-amazon.com/images/I/41R5F23M8JL._SL210_.jpg", 118, "「まぶだち」の古厩智之監督が、注目の若手俳優４人を起用して描き出した、スカッとさわやかな青春映画。落ちこぼれ高専生たちがロボットコンテストの激戦を勝ち抜く様を自然体で紡ぐ。担当教師から補習免除のため、第２ロボット部入部を命じられた高専生・里美。そこは大会出場権すらままならない幽霊クラブだった。ところが、ひょんなことから全国大会への出場が決定し、合宿生活を始めるが……。", "2003-09-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和極道史", "昭和極道史", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "この年の３月に藤純子が引退。翌年１月に「仁義なき戦い」が封切と、時期的に任侠から実録路線への転換期に作られた、東映の現代ヤクザもの。物語は、新興暴力団・大関組に組を潰された、元・吉井組の幹部、沢木が、舎弟の神谷や、同じく大関組に怨みを持つ女胴師・夜桜の美佐とともに、大関たちを倒すというもの。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("琉神マブヤー THE MOVIE 七つのマブイ", "琉神マブヤー THE MOVIE 七つのマブイ", "", "", "http://image.eiga.k-img.com/images/movie/56360/315x210.jpg?1463", 96, "ヒーローにあこがれる青年ウルマは、出演するヒーローショーのリハーサル中、ふとしたことから琉球を守るヒーロー「琉神マブヤー」の魂が宿り、現代の琉神マブヤーになってしまう。そんな時、悪の軍団マジムンが沖縄の大切な7つの魂（マブイ）を次々と奪っていき、人々に異変が発生。沖縄中が混乱に巻き込まれてしまう。ウルマは仲間のサイオンらとともに戦い、無事にマブイを取り戻すのだが……。2008年に琉球放送で放映された沖縄発の特撮ヒーローの劇場版。主演は山田優の弟で、モデル・俳優の山田親太朗。", "2012-01-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あしたのジョー（2011）", "あしたのジョー（2011）", "", "", "http://image.eiga.k-img.com/images/movie/55380/315x210.jpg?4453", 131, "社会現象を巻き起こした歴史的コミック「あしたのジョー」を、「ピンポン」（02）の曽利文彦監督が山下智久主演で実写映画化。主人公・矢吹丈の宿命のライバル・力石徹役に伊勢谷友介、力石を支える白木葉子役に香里奈、ジョーの才能を見出す元ボクサー・丹下段平役に香川照之が扮する。舞台となるのは、昭和40年代の東京の下町。殺ばつとした日々を過ごしていた矢吹は、ボクサーとしての才能を見出されるも、問題を起こして少年院へ送られる。そこで世界チャンピオンに通用する実力をもったプロボクサー・力石と運命的な出会いを果たし、永遠のライバルとなる。", "2011-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MILES FROM HOME", "マイルズ・フロム・ホーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "かつての栄光が見る影もないほどに落ちぶれた農場を舞台に、すべてを捨て、新しい何かを求めて旅立つ二人の兄弟の姿をアメリカの広大な土地を舞台に描き出す佳作。アメリカが抱える深刻な農家の経済問題を背景に、R・ギアの哀切きわまる演技が共感を呼ぶシリアスな映画。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAGE", "恐怖の48時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "採石工場で働く男が突然発狂し、調査の結果、医師ルーベンの飼犬から感染した狂犬病だと判明する。その瞬間、ルーベンは全身の血が逆流するようなショックを受けた。実は彼も飼犬に噛まれていたのだ。48時間以内に治療しなければ命が……。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("They Live", "ゼイリブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　仕事を求めて町に流れ着いたネイダは、ホームレスのためのキャンプで寝泊りするようになったのち、教会でサングラスを発見。そのサングラスを通して見えたものは、人間になりすまし、町を支配していた奇怪な侵略者の姿と、至るところに隠され、人間を操っていた洗脳標識だった。恐るべき真実を知ったネイダは侵略者と戦うことに……。人間社会に忍び込んでいた未知の恐怖を描くSFサスペンス。人気プロレスラーのロディ・パイパーが主演し、激闘を繰り広げる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コマンダンテ", "コマンダンテ", "", "", "http://image.eiga.k-img.com/images/movie/1880/315x210.jpg?4119", 100, "「プラトーン」「7月4日に生まれて」で2度のオスカーを受賞し、最近では「ワールド・トレード・センター」などを世に送り出してきたハリウッド屈指の社会派オリバー・ストーン監督が、アメリカと国交を持たぬ隣国、キューバの最高指導者フィデル・カストロに1対1のインタビューを挑んだドキュメンタリー映画。同志ゲバラとの別離から、62年のキューバ危機、そして謎に包まれた私生活まで、カストロ本人の口から語られる。", "2007-05-26", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PARTVII　　先物取引の蟻地獄", "難波金融伝 ミナミの帝王　劇場版PARTVII　　先物取引の蟻地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "多額の借金を抱え、そのうえ先物取引会社にだまされたAV制作会社の社長。彼を救うために銀次郎は、アノ手コノ手で悪徳業者に戦いを挑む。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hidden 3D", "HIDDEN ヒドゥン", "", "", "http://image.eiga.k-img.com/images/movie/57590/315x210.jpg?3189", 81, "迷宮のように入り組んだ建物の中で、血に飢えたモンスターに襲われる若者たちの姿を描いたサバイバルホラー。薬物中毒の治療を研究していた母親が亡くなった青年ブライアンは、友人とともに母が働いていた中毒治療センターを訪問。母が発明した革命的な治療装置が、副作用で患者を血肉に飢えたおぞましいミュータントの子どもに変えてしまっていたことを知る。迷路のように入り組んだ建物の地下で昆虫の群れと共生するミュータントたちは、逃げようとするブライアンらを追いつめていく。", "2012-03-24", "4,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハワイの若大将", "ハワイの若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "京南大学ヨット部のキャプテン・田沼雄一は、ハワイに行った青大将を連れ戻すため一路ハワイへ。そこで化粧品会社のOL・中里澄子と再会し、仲良く青大将を捜索。ようやく青大将を見つけたところを、澄子は赤まむしの異名をとる色魔に誘惑されそうになる。そこを雄一に助けられ……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月光ノ仮面", "月光ノ仮面", "", "", "http://image.eiga.k-img.com/images/movie/56010/315x210.jpg?3190", 102, "板尾創路が「板尾創路の脱獄王」に続き監督した第2作。敗戦の色濃い昭和22年、戦死したと伝えられた落語家の森乃家うさぎが顔を包帯で包み、一切の記憶をなくして戻ってくる。戦前は人気、実力ともに認められ、将来を期待されていた森乃家だったが、いまはただ空ろな口調で得意だった落語「粗忽長屋」をつぶやくことしかできず……。共演は浅野忠信、石原さとみら。", "2012-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ed Wood", "エド・ウッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "アメリカ史上最低といわれた映画監督エドワード・D・ウッドJr、通称エド・ウッドの半生を、彼のファンであるティム・バートンが映画化。映画監督を夢見る青年エドは、往年の俳優ベラ・ルゴシと出会ったことをきっかけに監督デビューを果たすが……。主演のエドを演じるのは「シザーハンズ」のジョニー・デップ。また、ベラ・ルゴシを演じたマーティン・ランドーは、本作でアカデミー賞助演男優賞を受賞した。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("懲役十八年・仮出獄", "懲役十八年・仮出獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "安藤昇主演による“懲役十八年”ものの第２作。仮出獄した郡司は、ろうあの金庫師・木島と溶接工で余命いくばくもない大宮とともに、死の商人が密輸入した金塊を奪取する計画を企てる。襲撃は成功するが……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪之丞変化（総集編）", "雪之丞変化（総集編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "人気大衆作家・三上於菟吉の小説を伊藤大輔が脚色し、衣笠貞之助が監督した娯楽作品。歌舞伎の女形・雪之丞、盗賊・闇太郎、雪之丞の母親という三役を林長二郎（のちの長谷川一夫）がこなす仇討ちもの。女性以上に美しい長二郎の女形が何といっても最大の見どころ。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROOM SERVICE", "ルーム・サービス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ブロードウェイで大ヒットした舞台劇の映画化。ニューヨークのあるホテルを舞台に、無銭宿泊を決めこむ劇団主宰者のグルーチョと団員のチコやハーポが、ホテルマンや資金援助をしてくれそうな大金持ち相手に繰り広げるコメディ。シナトラ主演「芸人ホテル」(1944)はこの映画をもとにしたミュージカル。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Poor Cow", "夜空に星のあるように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "イギリスの巨匠ケン・ローチ監督の劇場映画デビュー作。泥棒家業を続ける青年トムと成り行きで結婚してしまったジョイ。産まれたばかりの赤ん坊にも無関心で、暴力までふるう夫に対し、彼女は嫌気がさしていた。そんなある日、トムはついに逮捕され、ジョイは叔母の家に居候させてもらうことになる。そこに訪ねてきた夫の仲間デイブ。彼女は優しいデイブに惹かれ、一緒に暮らし始めるが、彼もまた強盗事件で逮捕されてしまい……。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オイディプスの刃", "オイディプスの刃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "刀の収集に血道をあげる家長と、香水作りの好きな妻のいる下関のとある旧家のもとに、ラベンダーと伝説の妖刀“次吉”が持ち込まれたことから、次々と奇怪な殺人事件が起きる。第１回角川小説賞を受賞した赤江瀑の原作を映画化したミステリー・ロマン。監督は、名カメラマン・成島東一郎。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪の教典", "悪の教典", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-11-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("21エモン／宇宙へいらっしゃい！", "21エモン／宇宙へいらっしゃい！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "21世紀の東京を舞台に、宇宙飛行士志望の少年・21エモンが、愉快なロボットやエイリアンたちとともに太陽系一周の冒険旅行に旅立つ。“ドラえもん”に先駆けて『少年サンデー』連載された“21エモン”の痛快で歯切れの良い、劇場版第２作。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTER SECTION", "インターセクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "TV局の女性敏腕プロデューサーが、高視聴率獲得のため、死刑執行公開番組を企画した。殺人犯のインタビューを放映し、社会の関心を引くべく働きかけるうちに、無実を訴える殺人犯に社会全体が操られるようになる……。番組一回目の死刑囚として接触した、ルックスもカリスマ性も抜群のレイプ殺人犯に身も心も魅せられ、ついには彼の無実を確信、執行阻止に奔走する敏腕プロデューサーをS・ヤングが演じている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHADOW MAGIC", "西洋鏡・映画の夜明け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "活動写真に情熱を注ぐイギリス人と中国人の友情を通して、中国の映画草創期を見つめた好編。中国出身のアメリカ人監督アン・フーが、物語に当時の息吹を見事封入し、温かい感動を呼んだ。1902年の北京。一攫千金を狙う英国人のレイモンドは“西洋鏡”と呼ぶ活動写真小屋を開く。写真館でカメラマンをするリウはこの新映像に魅了されて助手に。やがて二人の活動写真は評判を得るが、観客を奪われた京劇の人々から反感を買ってしまう。そんな中、リウとレイモンドは活動写真を西太后に披露することになる。", "2000-01-01", "1,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("立入禁止区域・双葉　されど、我が故郷", "立入禁止区域・双葉　されど、我が故郷", "", "", "http://image.eiga.k-img.com/images/movie/57810/315x210.jpg?2697", 98, "2011年3月11日の東日本大震災による福島第一原子力発電所の事故で、立入禁止区域となった福島県双葉郡の実情を追ったドキュメンタリー。地元出身の佐藤武光監督が事故発生から1カ月後に自ら現場に入り、地震や津波が残した傷跡、避難のため誰もいなくなった町の様子をカメラに収めた。避難所で暮らす住民らのインタビューも交え、立入禁止区域の実態を明らかにしていく。ナレーションは女優の市原悦子。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vitus", "僕のピアノコンチェルト", "", "", "http://image.eiga.k-img.com/images/movie/34180/315x210.jpg?1532", 121, "「山の焚火」「最後通告」などで知られるスイスの巨匠フレディ・M・ムーラー監督がモーツァルトのようにピアノを弾き、アインシュタインのように数学の才能を持って生まれてきた孤独な天才少年、ビトスの苦悩と葛藤を描き出した人間ドラマ。ビトスの祖父役で「アメリカの友人」のブルーノ・ガンツが共演。06年度のアカデミー賞外国語映画賞スイス代表にも選ばれた。", "2007-11-03", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROSALIE　FAIT　SES　COURSES／ROSALIE GOES SHOPPING", "ロザリー・ゴーズ・ショッピング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「バクダッド・カフェ」のP・アドロン監督、女優M・ゼーゲブレヒトのコンビによる、現代の消費社会を風刺した作品。アメリカ、アーカンソー州に住むロザリーは、夫のレイと７人の子供とともに、見た目は幸福な生活を営んでいる。だが、彼女には変わった癖があり、それは37枚のクレジット・カードを使って、物を買いまくること。当然、借金地獄がロザリーと家を襲い……。行動的には悪妻なのだが、そうは見えないところが、ロザリーに扮したゼーゲブレヒトならではの魅力。", "1989-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last Detail", "さらば冬のかもめ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　海軍士官バダスキーとマルホールは、窃盗で８年の刑を宣告された若い水兵メドウズの護送任務に着く。旅をするうち、3人の間には奇妙な友情が芽生えていく。バダスキーとマルホールはまだ女性との経験がないというメドウズのために売春婦をあてがってやる。一方、メドウズは途中で出会った娘から脱走を勧められるが……。3人の男たちの交流を描いたアメリカン・ニューシネマの佳作。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不可思議物語　FANTASTIC COLLECTION", "不可思議物語　FANTASTIC COLLECTION", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "当時、新鋭監督だった４人が、それぞれ２本ずつ受け持ち、計８本で構成されたオムニバス映画。一貫してファンタスティックな題材を扱っているが、SF、オカルト、コメディ、サスペンスと多彩な内容。奇抜なカメラ・アングルや長回し撮影など、実験的センスが光る。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATAL PULSE", "戦慄殺人 ローズマリー2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "女子寮で少女が次々と変質的な手口で殺害されるという連続殺人事件が発生する。次第に、殺しがエスカレートしていき……。“姿なき殺人者”の恐怖を描いたエロティック・ミステリー。ジョセフ・ジトー監督の「ローズマリー」とは無関係。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WHITE BALLOON", "白い風船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "カンヌでカメラドールを受賞した、A・キアロスタミの助監督のJ・パナヒの監督デビュー作。パナヒのアイデアを元にキアロスタミが脚本を書いている。７歳の少女ラジェは新年のお祝いのためにどうしても金魚が欲しくなる。彼女の熱意に負けた母親からなけなしの金をもらったラジェは、町へと駆け出していく。しかし、彼女は人混みの中でお金をなくしてしまう。ラジェは金を探す過程で出会いや冒険を体験していくのだった。", "1995-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春琴抄（2008）", "春琴抄（2008）", "", "", "http://image.eiga.k-img.com/images/movie/53960/315x210.jpg?3124", 0, "谷崎潤一郎の同名原作を、「青いうた　のど自慢青春編」の金田敬監督が32年ぶりに映画化。主演は「いつかの君へ」「赤んぼ少女」の斎藤工と「大決戦！超ウルトラ8兄弟」の長澤奈央。明治初期、佐助は三味線奏者の春琴の姿を観音様とあがめ、弟子兼世話係として献身的に仕える。春琴は盲目で音曲の才能があったが、わがままで贅沢な暮らしのため生活は苦しかった。そんな折、名家の利太郎が春琴の美貌目立てに弟子入りし……。", "2008-09-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Johnny Handsome", "ジョニー・ハンサム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "　その醜い顔からジョニー・ハンサムと呼ばれていた男が強盗の共犯者ラフェとサニーの裏切りに遭い、さらに獄中で囚人に刺され、重傷を負う。ジョニーは整形手術によって人生をやり直せると主張する医師レッシャーの言葉を聞き入れることに。その後、生まれ変わったジョニーが出所。社会復帰し、ドナという恋人もできるが……。不幸な犯罪者の生き様、そして復讐の果てを描く衝撃作。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WATERSHIP DOWN", "ウォーターシップ・ダウンのうさぎたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "都市郊外の野原の地下に穴を掘り、平和に暮らす野うさぎたち。だが人間たちの乱開発が迫っていることを予感し、安住の地を求めて冒険の旅へと向かううさぎたちもいた……。うさぎの視点から見た世界観を一貫させ、自然の大切さを教えてくれる良質の動物アニメ。", "1979-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤坂の姉妹 夜の肌", "赤坂の姉妹 夜の肌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "赤坂のバー“しいの実”を舞台に、店のマダム・夏生、その妹の秋江、冬子という３姉妹のそれぞれ対照的な生き方を情感豊かに描いた作品。撮影は、山中貞雄の「丹下左膳餘話・百萬両の壷」や成瀬巳喜男の「乱れる」などを手掛けた安本淳。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Losers", "ルーザーズ", "", "", "http://ecx.images-amazon.com/images/I/51nccmsskXL._SL210_.jpg", 98, "アンディ・ディグルによるグラフィック・ノベルを実写映画化したジョエル・シルバー製作のアクションドラマ。CIA内部の大物によって裏切られたエリート特殊部隊の復讐を描く。脚本は「ハンコック」のピーター・バーグと「ゾディアック」のジェームズ・バンダービルト。監督は「ストンプ・ザ・ヤード」のシルバン・ホワイト。出演はゾーイ・サルダナ、クリス・エバンス、ジェフリー・ディーン・モーガン、ジェイソン・パトリックほか。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Talented Mr. Ripley", "リプリー", "", "", "http://image.eiga.k-img.com/images/movie/1820/315x210.jpg?4739", 140, "アラン・ドロン主演「太陽がいっぱい」（1960）の原作として知られるパトリシア・ハイスミスの原作を、「イングリッシュ・ペイシェント」のアンソニー・ミンゲラ監督が映画化。富豪の放蕩息子ディッキーを連れ戻す役目を負って、イタリアに渡ったトム・リプリー。贅沢だが刺激のない毎日を送っていたディッキーは、下層階級のトムを物珍しがり、ジャズクラブやセーリングに連れ回す。自由奔放なディッキーの人柄と優雅な生活に強く魅せられたトムは、イタリアでの毎日で彼との間に強い絆が結ばれたと思い込むが、ディッキーにとってトムは父親と結ばれた金づるにすぎなかった。", "2000-08-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小倩／A CHINESE GHOST STORY", "チャイニーズ・ゴースト・ストーリー スーシン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "T・ハークが自らの大ヒット作「チャイニーズ・ゴースト・ストーリー」をアニメ化。集金人として旅をしている少年アニンが、ゴースト・タウンで女幽霊スーシンに一目惚れ。邪悪な妖怪たちと闘いながら恋を育んでいく二人は、輪廻転生によって再び出会うことを誓い、霊魂再来の門へと向かう……。「となりのトトロ」の演出助手・遠藤徹哉や「銀河鉄道999」などの作画監督・小松原一男、「AKIRA」の作画監督・なかむらたかしなど、日本のスタッフも多数参加し、躍動感あふれる映像世界を展開。声優陣に香港を代表するスターが勢ぞろいしている点も魅力だ。", "1997-01-01", "6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLAMBAKE", "ブルーマイアミ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ダスター・オイル社の御曹子スコットは、父親から副社長にと命ぜられたため、家出を決行した。何でも金で買えるが、自分自身では何もできない億万長者の生活が耐えられなかったのだった。途中、マイアミで水上スキーの先生となる男と知りあった彼は、身代りの生活を送るベく、車や服を取りかえた。そしてスコットはマイアミで水上スキーの最初の生徒ダイアンと出会い彼女の虜になるが……。人気スター、E・プレスリーの魅力が、スクリーンにいっぱいに広がる青春サマー・ストーリー。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桜の温度", "桜の温度", "", "", "http://image.eiga.k-img.com/images/movie/57410/315x210.jpg?3460", 21, "アニメーションを主体に実写、CGなども手がける映像制作会社ユーフォーテーブルが企画・製作・配給する劇場用オリジナル短編アニメーション。何もない地元の田舎町にいらだちを感じながら日々を過ごしていた少年・柊吾だったが、ある日、何事も順風満帆にみえた兄・海斗と幼なじみの蘭澄に異変がおきて……。監督は「劇場版　空の境界」シリーズも手がけた平尾隆之。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忘れられぬ人々", "忘れられぬ人々", "", "", "http://image.eiga.k-img.com/images/movie/1740/315x210.jpg?3737", 120, "監督、篠崎誠は63年東京生まれ。都内の映画館で映画技師をしながら映画ライターとしても活動し、96年「おかえり」で劇場映画監督デビュー。北野武監督「菊次郎の夏」のドキュメンタリーを経て本作が劇場映画第2作。老人3人を演じるのは、黒沢明や成瀬巳喜男ら巨匠の作品に出演してきた三橋達也、松竹の二枚目スターを経て東映やくざ映画でも活躍した大木実、小津映画の名子役として知られる青木富夫。日本映画史を生きた面々だ。", "2001-09-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Land of Plenty", "ランド・オブ・プレンティ", "", "", "http://image.eiga.k-img.com/images/movie/1490/315x210.jpg?4403", 124, "「パリ、テキサス」「ベルリン・天使の詩」のビム・ベンダース監督が、姪と叔父のアメリカを横断を描くロードムービー。アメリカで生まれてアフリカで育った少女ラナは、亡き母の手紙を叔父に渡すために10年振りにアメリカに帰国する。ラナと叔父は、ホームレスのイスラム系青年が殺される事件に出会い、ラナはその青年の遺体を彼の兄に届けるため、叔父はその事件の真相を探るためにアメリカを横断する旅に出る。", "2005-10-22", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Burn After Reading", "バーン・アフター・リーディング", "", "", "http://image.eiga.k-img.com/images/movie/54020/315x210.jpg?3599", 96, "「ノーカントリー」でアカデミー賞を受賞したジョエル＆イーサン・コーエン兄弟による犯罪コメディ。ワシントンのフィットネスジムで働くチャドは、CIAの機密情報と思われるものが入ったCD-ROMを拾う。そのデータを利用して一攫千金を狙うチャドは、情報の持ち主である元CIAのオズボーンを脅迫するが……。G・クルーニー、B・ピット、J・マルコビッチ、F・マクドーマンドら豪華俳優陣が集結。", "2009-04-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OTLEY", "地獄のかけひき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "グータラ男のオットリーは、寝場所を探して訪れた親戚のエリックの家で泥酔中に、当のエリックの殺害事件に巻き込まれる。なぜか空港で目覚めて警察へ向かう途中、彼は何者かにさらわれ、諜報部員に尋問されたあげく、今度は殺し屋の手に渡されてしまう。まったくわけがわからぬまま、複雑なスパイ事件に巻き込まれ、次々と危険が迫ってくるというコメディ・タッチのスリラー・アクション。主演のグータラ男には、演技派T・コートネイ。謎の女スパイ役でR・シュナイダーが花を添えている。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASSIGNMENT K", "情報局K", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "任務でドイツに渡った秘密諜報部員フィリップは、そこでトニという美女と知り合う。二人の仲は急速に接近するが、諜報員に油断は禁物。さっそく彼は彼女の身元調査を始めた。しかし、トニの潔白が証明されたのもつかの間、彼女は何者かに誘拐されてしまう。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cirque Du Freak: The Vampire's Assistant", "ダレン・シャン", "", "", "http://image.eiga.k-img.com/images/movie/53850/315x210.jpg?2980", 109, "「アバウト・ア・ボーイ」のポール・ワイツ監督が、全世界ベストセラー児童文学を映画化したダーク・ファンタジー。主演は新星クリス・マッソグリア、共演にジョン・C・ライリー、サルマ・ハエック、ウィレム・デフォー、渡辺謙ほか。成績優秀で女子にもモテモテの少年・ダレン・シャンは、巨大な毒グモに噛まれた親友を救うためにバンパイアと取引し、半バンパイア人間となってしまう。家を出たダレンは奇妙なサーカス団に身を寄せ、雑用をこなしながらバンパイアの修行に励む。", "2010-03-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLUE LAGOON", "青い珊瑚礁", "", "", "http://ecx.images-amazon.com/images/I/51IkBwDCjlL._SL210_.jpg", 105, "20世紀初頭の南太平洋。船火事で無人島に流れ着いた、いとこ同士のリチャードとエミリー。やがて思春期になり、愛し合うようになる二人だったが……。ストーリーは単調ながら、南海の陽光あふれる風景を捉えた美しい映像は出色。シールズの肢体もまぶしい。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕らはみんな生きている", "僕らはみんな生きている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「木村家の人びと」「病院へ行こう」などの名コンビ、滝田洋二郎監督と脚本家の一色伸幸が放つ破天荒なコメディ。サラリーマンの高橋は１週間の出張で東南アジアのタルキスタン王国へ。ところが、帰国寸前に現地でクーデターが勃発。彼は、上司の中井戸やライバル会社の富田、升本とともに、激しい市街戦をくぐり抜け、ジャングルの中へ逃げ込む。しかし、苛酷な逃避行の中で、中井戸がゲリラに捕らえられてしまい……。日本の常識が通用しない世界であわてふためくサラリーマンの姿が、おかしくもせつない。そんな彼らの姿に浮き彫りにされた、世界の中の日本を見つめる視点は、シニカルで毒気たっぷり。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DLABOLIQUE", "悪魔のような女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "完全犯罪を仕組んだ女たちに起こる、ミステリアスな事件を描く。全寮制の聖アンセルム男子校の理事長ガイは、教師のニコルと妻ミア公認の愛人関係にあり、ニコルとミアも奇妙な絆で結ばれていた。ある日、暴力的なガイにたまりかねた二人は共謀してガイを殺害。死体を学校のプールに捨てるが、死体はこつ然と消えてしまい、二人の前に探偵を名乗るシャーリーが現れる。1955年の同名のフランス映画をリメイクしたサスペンス。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE VOYEUR", "背徳小説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「カリギュラ」のヒットから15年を経て、T・ブラスが新たな境地を示したエロス大作。フランス文学の青年教授ドドは、ある事情で妻と別居し、父のアルベルトと同居していた。ある日彼は、ケガで療養中の父を介護する女ファウスタが、父の求めで猥褻な行為をし、金をもらっていることを知る。父と同じことを彼女に要求するドド。さらに彼は、教え子とも関係を持つ。アブノーマルな性的願望を満たし、退廃の日々を送るドドだったが、妻のシルヴィアが深夜に父の寝室に入って行ったことを聞かされた……。", "1994-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八月はエロスの匂い", "八月はエロスの匂い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "「八月の濡れた砂」(1971)で日活映画に終止符を打った藤田敏八監督のロマンポルノ路線への参加第１作。デパートの貴金属売り場の店員・中原圭子は、レジの金を奪って逃げた斜視の少年がなぜか忘れられなくなる。彼女は同棲中の男や従妹らとともに田舎へ行く途中で“シラミ”と呼ばれるグループの中でのけ者にされている少年を見つける。圭子は少年をつかまえて唇を重ねる……。初のロマンポルノということで戸惑いは隠せず、藤田敏八監督としては成功作とは言えないが、主演の中原圭子に扮する川村真樹は、この作品のあと「エロスの誘惑」(1972)、「エロスは甘き香り」(1973)という藤田敏八のエロス３部作に出演した。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("没有航標識河流／THE RIVER WITHOUT BUOYS", "標識のない河の流れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "文化大革命期を舞台に、いかだ流しとして生きる老人パン・ラウオー、中年のチャオ・リアン、青年シー・クーの３人の精神史を、それぞれの恋愛を絡めながら描く。中国ニューウェーブ映画の育ての親ティエンミンの監督デビュー作。河やいかだの上など限定された舞台空間の中で強烈なヒューマニズム感覚を描き上げている。", "1983-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊能忠敬・子午線の夢", "伊能忠敬・子午線の夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "劇団俳優座創立55周年を記念して製作された伝記ドラマ。今から200年前の江戸時代、自らの足一つで初の完全なる日本地図を作り上げた伝説の人、伊能忠敬の半生を描く。18世紀末、50歳で名主家業を辞した伊能は翌年、江戸に出て幕府天文方の高橋至時に師事、56歳にして地図作りのための第１次測量に旅立つ。“子午線一度の実測値をつかみたい”という個人的野望に支えられながら始まった測量は、以後18年、74歳まで続き、およそ地球一周分の距離に達した。まるで伊能の歩行測量のような快活なリズムで進行するドラマは、堅苦しい偉人伝のイメージから遠く、スッキリした後味のさわやかな人間ドラマに仕上がっている。向学心と探求心にあふれる主人公を加藤剛が快演。高橋至時役・榎木孝明を始め、賀来千賀子、西田ひかるら、脇を固める出演陣の好演も光るが、なかでも島津重豪役の丹波哲郎が出色の怪演でクライマックスを締める。監督はTV『剣客商売』などで加藤と懇意の「鬼平犯科帳」の小野田嘉幹。オーケストラによる池辺晋一郎の音楽も壮快だ。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の蝶", "夜の蝶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "川口松太郎の原作を京マチ子と山本富士子という大映二大女優の顔合わせで映画化した風俗映画。東京・銀座の夜の世界を舞台に、二人のバーのマダムが、色と欲のために女の闘いを繰り広げる。吉村公三郎らしいハッタリをきかせた演出が見もの。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陰詩〈かげうた〉", "陰詩〈かげうた〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ヒーローもので人気を博した高野八誠や、「blue」に出演したモデルの今宿麻美、人気グラビアアイドルの熊田曜子が主演した、夢と現実が交差する不思議な物語。「ラビリンス」「クロスラブ」「シンドローム」の３エピソードで構成されているミステリー・ドラマ。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INSTINCT", "ハーモニーベイの夜明け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "ハートウォーミングな作風で知られるJ・タートルトーブによるミステリー・タッチの人間ドラマ。研究のために東アフリカを訪れたまま行方不明になっていた高名な霊長類学者イーサンが、密林で男二人を殺害した容疑で逮捕された。精神異常とみなされた彼は家族にも心を閉ざし、何も語ろうとはしない。一体彼に何が起こったのか？　真意を確かめようと真正面から向き合う若き精神科医テオに、イーサンは事件の真相を語り始める。事件をめぐるスリルと殺人容疑者と精神科医の友情を軸に、真の自由とは何かを描く。なぞめいた学者を名優A・ホプキンスが貫禄たっぷりに演じ、ドラマに厚みを持たせている。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bumazhnyy Soldat", "宇宙飛行士の医者", "", "", "http://image.eiga.k-img.com/images/movie/56810/315x210.jpg?1348", 118, "カンヌ、ベルリン、ベネチアの世界3大映画祭の受賞作を中心に、各地の映画祭で話題になりながらも日本未公開だった作品を一挙上映する「三大映画祭週間2011」にて上映。1961年、カザフスタン。医師ダニエルは、ソ連初の宇宙飛行計画に宇宙飛行士たちの健康管理責任者として従事する。しかし、国家の発展のために若者たちの命が犠牲になることが納得できずにいたある日、ついに士官の1人が死に、ダニエルは心身を消耗して神経衰弱に陥ってしまう。2008年・第65回ベネチア国際映画祭で銀獅子賞（監督賞）を受賞。監督は「フルスタリョフ、車を!!」（98）で知られるアレクセイ・ゲルマン監督の息子、アレクセイ・ゲルマン・Jr.。", "2011-08-13", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Everyone Stares: the Police Inside Out", "ポリス　インサイド・アウト", "", "", "http://image.eiga.k-img.com/images/movie/33910/315x210.jpg?4191", 74, "1970年代後半から1980年代半ばにかけて世界中のロックファンを熱狂させた3ピースバンド、ポリスを内側から捉えたドキュメンタリー。大ブレイクする前の1978年に8ミリカメラを購入したドラマーのスチュアート・コープランドは、それ以来ステージの裏側をカメラに収め続けた。瞬く間に音楽シーンを駆け上がり人気絶頂の1984年に活動を停止するまでの軌跡を、60曲を超えるオリジナル・ソングをバックに振り返る。", "2007-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOWN　＆　COUNTRY", "フォルテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "夫婦生活の危機を迎え、自分を見失いかけている大人たちのとまどいを、ユーモラスに描いた人間ドラマ。結婚して25年、建築家ポッターは妻との生活も愛人との関係も、うまくこなしている。そんなある日、夫の浮気に落ち込む親友の妻と関係を持ってしまい、不穏な空気が。さらに悪いことに、ポッターの妻が愛人の存在を知ってしまい……。ウォーレン・ベイティ、ダイアン・キートンなどベテラン俳優陣が、人生の転機に慌てる大人たちを笑いと涙を交えて妙演。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちゃんと伝える", "ちゃんと伝える", "", "", "http://image.eiga.k-img.com/images/movie/54600/315x210.jpg?4052", 108, "「紀子の食卓」「愛のむきだし」の鬼才・園子温が、亡き父への思いを込めて描く家族の物語。奥田瑛二と「EXILE」のAKIRAが親子役を熱演。父が癌で倒れたのをきっかけに初めて父と向かい合おうとする史郎。そんなある日、史郎は父よりも短い余命宣告を受けてしまう。史郎は限れた時間の中で父との大切な約束を果たそうと奔走するが……。", "2009-08-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高校デビュー", "高校デビュー", "", "", "http://image.eiga.k-img.com/images/movie/55740/315x210.jpg?3184", 93, "中学時代は部活一筋の熱血少女だった長嶋晴菜は、高校生活で恋愛を満喫するため、男にモテる方法を知り尽くす小宮山ヨウに“モテコーチ”を依頼。ヨウが出した条件は「俺を好きになるな」ということだったが、晴菜は次第にヨウにひかれていく。「別冊マーガレット」（集英社）で連載された河原和音の人気コミックを実写映画化。晴菜役は本作が女優デビューで主演となるモデルの大野いと、ヨウ役に溝端淳平。", "2011-04-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TATIE DANIELLE", "ダニエルばあちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「人生は長く静かな河」で成功を収めた、フランス人監督E・シャティリエの第２作。優しくされたくない、ガンコな老婦人ダニエルと彼女に優しくしたい甥のジャン・ピエール一家が巻き起こす辛口のコメディ。ダニエルに扮した、T・シェルトンのガッツのあるガンコぶりは、見どころ。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我が家の問題", "我が家の問題", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "朝鮮映画でタブーとされてきた官僚主義の不正告発映画。主人公の郵便局長は町でも評判の働き者だが、妻は人一倍虚栄心が強い。次第に妻のペースにはまりこんだ局長は、妻の言うままに賄賂を受け取ったり、不正をはたらいてしまう。一度味をしめた賄賂と怠け癖の習慣はなかなか抜け切れず、やがて局長は辞職するハメに陥るが……。", "1973-01-01", "48", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母", "母", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "東北のある農村を舞台に、人一倍子煩悩な母親が夫の突然の事故から母であることを棄て、看病と野良仕事に追われていく様を末娘の目を通して描いた感動的な人間ドラマ。「どですかでん」以来17年振りにスクリーンにカムバックした吉村実子が母親役を熱演。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハンドメイドエンジェル", "ハンドメイドエンジェル", "", "", "http://ecx.images-amazon.com/images/I/51VtolAkhnL._SL210_.jpg", 80, "女性専門のアダルトショップを経営することになった女の子たちの奮闘を描いたガールズ・ムービー。自分のやりたい事を探して求職中の美夏は、ひょんなことから女の子のためのアダルトショップ起業を決意。親友の看護士ほのから気の合う女友達らと共に、オープンに向けて動きだすが……。キャストには、ミスFLASH2009の準グランプリを受賞した村上友梨、「仮面ライダー電王」シリーズの秋山莉奈らフレッシュな顔ぶれがそろった。", "2010-07-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOCK, STOCK AND TWO SMOKING BARRELS", "ロック、ストック＆トゥー・スモーキング・バレルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "イギリスで大ヒットを記録し、日本でもロングラン・ヒットとなった犯罪ドラマ。マフィア相手の大金を賭けたポーカー勝負に敗れ、借金を抱えこんだ若者４人組。返済期限日が迫る中、偶然チンピラ一派の強盗計画を知った４人組は、強盗後の彼らを襲撃し麻薬と現金の強奪に成功する。しかし、怒った麻薬組織が追跡を始め、さらに奪われた物を取り戻そうとするチンピラ一派や、マフィアが放った借金取りも４人組を追いまわす……。登場人物の多い、凝った設定のドラマながら、スタイリッシュでスピード感のある映像とユーモアで、一気に見せきってしまう。新鋭G・リッチーの、デビュー作とは思えない手慣れた演出に唸らされる。新人俳優中心ながら絶妙のキャスティング。ミュージシャンのスティングやサッカー選手、V・ジョーンズらの妙演も見どころ。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOSFERATU，PHANTOM DER NACHT", "ノスフェラトゥ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "伝説上有名なドラキュラ伯爵の物語を、西ドイツの鬼才ヘルツォーク監督が映画化。ドイツ映画界には戦前、サイレント映画でF・W・ムルナウ監督によるドイツ・ロマン派の古典的名作といわれる「吸血鬼ノスフェラトゥ」があるが、本作はそのリメイクであり、ストーカーの創り出したドラキュラの世界を耽美的に描き出している。K・キンスキーのドラキュラはその怪奇性でまさに適役。", "1979-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Solaris", "ソラリス", "", "", "http://image.eiga.k-img.com/images/movie/1660/315x210.jpg?3652", 99, "惑星ソラリスの探索宇宙船に乗る友人から助けを求める連絡を受け、船を訪れた精神科医ケルビン。彼はそこでかつて自殺した妻そっくりの存在に出会う。ケルビンはその存在と新たな関係を築こうとするのだが。原作はタルコフスキー監督の「惑星ソラリス」と同じ、ロシアのSF作家スタニスラフ・レムの同名小説。もとはジェームズ・キャメロンが映画化権を持っていたが「ソダーバーグなら」と監督を譲り、自ら製作に回った。", "2003-06-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水滸傳／THE WATER MARGIN", "水滸伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "『三国志』と並ぶ中国の古典史劇を、当時のスターの共演で映像化。宋王朝の末期、108人の豪傑が梁山泊に結集。首領を殺され復讐を誓った彼らは、壮絶な戦いに身を投じる。ウエスタン風の演出が好評を呼び、台湾金馬奨優秀作品賞を受賞。日本から黒沢年男が出演し、クライマックスでは迫力満点の死闘を演じている。", "1972-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALEXANDRE LE BIENHEUREUX", "ぐうたらバンザイ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "フランスののどかな農村を舞台に、不世出のぐうたら男が巻き起こす抱腹絶倒の笑いを通して、現代をチクリと皮肉る風刺コメディ。生来の怠け者なのに、働き者の女房と結婚してしまったため、10年間も働きずくめにされていた農夫のアレキサンドル。ところが、ある日その女房がポックリ死亡、彼はそれ以来ずっとベッドの中でゆったり過ごすのだが……。", "1969-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GEORGE OF THE JUNGLE", "ジャングル・ジョージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1960年のアメリカの人気アニメを実写化した冒険コメディ。赤ん坊の頃に飛行機事故でジャングルに取り残されたジョージは、ゴリラのゴリに助けられたくましく成長。ある日、ライオンに襲われそうになっていた富豪令嬢アースラを救った彼は、彼女に恋をしてしまう。そんな中、ゴリが重傷を負い、ジョージたちは治療のためサンフランシスコに渡り、行く先々で大騒動を繰り広げる。言葉が話せるゴリラや犬のように人なつこいゾウなど、ユニークな動物たちが続々登場。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青空のルーレット", "青空のルーレット", "", "", "http://image.eiga.k-img.com/images/movie/34300/315x210.jpg?1673", 103, "辻内智貴の同名小説をもとに、夢を追いながら高層ビルの窓拭きで生計を立てる若者たちの姿を爽やかに描いた青春ドラマ。主演は「パッチギ！」の塩谷瞬と「包帯クラブ」の貫地谷しほり。ビルの清掃会社に勤めるミュージシャン志望の青年タツオは、それぞれの夢を持つ同僚たちと共に仕事に励みながら音楽活動を続けていた。そんなある日、バンド仲間と公園で練習をしていたタツオは、耳の聞こえない少女・加奈子と出会う。", "2007-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("駅弁", "駅弁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "アダルトビデオ監督、梶俊吾がAV業界の舞台裏を、虚実入り混じった構成で描いたモノクロ作品。梶自身がワンマン監督として登場し、映画製作への熱い思いを吐露したり、AV男優チョコボール向井が“男優哲学”を語るなど、一見ドキュメンタリーを思わせる展開。だが、映画全体の流れはフィクショナルであり、コンプレックスとジレンマにあえぐ監督の自画像ドラマとしての色彩が濃い。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Love Guru", "愛の伝道師　ラブ・グル", "", "", "http://ecx.images-amazon.com/images/I/51H9KuirhkL._SL210_.jpg", 0, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE JOUR SE LE｀VE", "陽は昇る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "フランス映画界に、数々の名作を送り出してきた名匠カルネとプレベールのコンビが製作。殺人を犯した男が追い詰められた状況下で、自分のこれまでの人生を回想し、やがて自殺してしまう。モノクロの陰影深い画面に詩情漂う逸品。", "1939-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Giant", "ジャイアンツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 201, "テキサスの大牧場に東部から嫁いできた美しい若妻レズリー。慣れない土地の厳しい生活の中でも彼女は自分の意志を大切に生きていこうとする。やがて、そんな彼女に淡い思いを抱く牧童ジェットは石油を掘りあて、富と名声を得るのだが……。西部での生活や、牧場主である夫とジェットとの確執の狭間に立つレズリーの姿を通じて、“時代”の流れに揺れ動く20世紀初頭のアメリカ西部をダイナミックに描いた一大叙事詩。天涯孤独の牧童を好演したディーンの遺作。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shrek 2", "シュレック2", "", "", "http://image.eiga.k-img.com/images/movie/1170/315x210.jpg?3751", 93, "おとぎ話と映画のパロディ満載で描く全米大ヒットアニメの続編。緑色の怪物シュレックが、昼は美女だが夜はシュレックと同じ緑色の怪物になるフィオナ姫を救って恋に落ちたのが前作。本作はその直後から続き、結婚した2人がフィオナ姫の両親の希望で彼らに会いに行くが、フィオナ姫の父親は娘の婿は王子が相応しいと考えて、長靴をはいたネコを雇ってシュレックを抹殺しようと画策する。クッキーマンなど前作のキャラが再び結集。", "2004-07-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Appointment", "約束", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　ローマに暮らす弁護士フェデリコは友人レンゾの婚約者カルラに心を奪われる。その後、カルラはレンゾから一方的に婚約破棄を言い渡される。レンゾはカルラがコールガールである証拠を掴んでいた。その後、誤解が解け、カルラはフェデリコと結婚することに。しかしフェデリコも彼女を疑い始めて……。誤解が生んだ悲劇を描くドラマ。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("六月の蛇", "六月の蛇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "俳優としてもが活躍が目立っていた塚本晋也の久々の監督作品。ストーカーと被害者の女性、その夫のねじれた三角関係を、生と性と死が織りなす特異なパッションのざわめきの中で描き出す。電話相談室で働いているりん子の下に、彼女を盗撮した淫らな写真が送られてくる。送り主は、かつて彼女が電話で“救った”ことのあるガン患者だった。男はやがてりん子に変態的な行為を求めてくるようになり……。ヴェネチア映画祭コントロ・コレンテ部門審査員特別賞を受賞。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SECRET OF THE INCAS", "インカ王国の秘宝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "インカ王国に伝わる秘宝を探し出すため、ペルーに現れた冒険家のハリーは、そこで同じ目的を持つ男エドと出会い、互いに争うが、やがてハリーは秘宝よりももっと大切なものがあることに気付いていく。エキゾチックな冒険アクション劇にヒューマニズムの要素を加えた作品。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Amazing Grace", "アメイジング・グレイス", "", "", "http://image.eiga.k-img.com/images/movie/55920/315x210.jpg?2102", 118, "時代を超えて愛され続ける名曲「アメイジング・グレイス」の誕生秘話と、この曲に支えられて戦った政治家ウィリアム・ウィルバーフォースの生涯を描く伝記映画。18世紀イギリスで、裕福な家庭に生まれ育った青年ウィリアムは、貴族階級の大きな収入源となっていた奴隷貿易制度に疑問を抱いていた。21歳の若さで議員となったウィリアムは、数少ない同志や愛する妻、そして師であるジョン・ニュートンが作詞した「アメイジング・グレイス」を支えとして、数々の困難を乗り越えながら貿易制度撤廃を目指す。", "2011-03-05", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Millenium 2: The Girl Who Played with Fire", "ミレニアム2 火と戯れる女", "", "", "http://image.eiga.k-img.com/images/movie/55360/315x210.jpg?3003", 130, "全世界40カ国で2600万部を売り上げた、スウェーデンの作家スティーグ・ラーソンによるミステリー小説を3部作で映画化した第2作。敏腕ジャーナリストのミカエルと天才ハッカーのリスベットが協力し、大富豪バンゲル家で起きた連続殺人事件を解決してから1年。リスベットはこつ然と姿を消したままだったが、少女売春組織を追っていたジャーナリストの殺害現場でリスベットの指紋がついた銃が発見される。無実を確信するミカエルは、仲間とともに事件の真相を追うが……。", "2010-09-11", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忠臣蔵", "忠臣蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "赤穂義士四十七人による復讐のドラマで、戦後数多く作られた作品のなかでも、娯楽性が豊かで、製作日数の短いこととともに話題を呼んだ作品。長谷川一夫の大石内蔵助をはじめ、市川雷蔵、勝新太郎ら大映オールスター・キャストで描く。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Uzak Ihtimal", "二つのロザリオ", "", "", "http://image.eiga.k-img.com/images/movie/55000/315x210.jpg?4643", 90, "トルコの大都市イスタンブールを舞台に、カトリック教徒の女性に想いを寄せるイスラム聖職者の淡い恋を綴り、ロッテルダム国際映画祭で最高賞に輝いたヒューマン・コメディ。イスラム教のモスクで礼拝の呼びかけを行う仕事に就いた素朴な青年ムサ。彼は隣に住むカトリック教徒の少女に恋心を抱いているが、なかなか想いを告げることができずにいた。そんなある日、ムサはイスタンブールに長く暮らす古書収集家の老人と出会う。", "2009-10-18", "32", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Group", "グループ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "　不況のどん底にあった1930年代。8人の女性たちが名門女子大を卒業し、それぞれの道を歩んでいく。ヨーロッパへ渡るエリノアとヘレナ、社会人となるリビー、プリス、ポリー、そして結婚するケイ……。ヨーロッパを不穏な戦雲が覆っていく一方、過酷な現実が彼女たちの人生に大きな影響を及ぼしていく。そんな仲良しグループ8人の大学卒業後から7年間を描いた群像劇。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The UnUsual Suspects: Once Upon a Time in House Music", "アンユージュアル・サスペクツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "シカゴ・ハウスのパイオニアであるチップ・Eが、ハウス誕生の歴史的瞬間と伝説の数々に迫ったドキュメンタリー映画。1977年、シカゴに“ウェアハウス”というクラブがオープンした。人々はDJのフランキー・ナックルズがかける新しいエレクトロニック・ミュージックに熱狂し、やがてその音楽を“ハウス”と呼ぶようになる。ゴッドファザー、ナックルズやラリー・ハードら、シカゴ・ハウスの大御所たちが当時のシーンを語る。", "2007-04-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVE AND THE HANDYMAN", "イヴ＆ハンディマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "効果音と音楽のみの無声映画スタイルで撮られた、異才R・メイヤーの初期の艶笑劇。気弱な便利屋の男が行く先々で、グラマーな美女たちにほんろうされ、失敗を重ねる。その後をつけては、彼の行動をメモしている謎の女イヴ。やがて彼女と便利屋は、ついにご対面を果たす……。メイヤーの二人目の妻イヴ・メイヤーが、一人で何人もの美女たちを演じ分けている。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デルタ", "デルタ", "", "", "http://image.eiga.k-img.com/images/movie/55510/315x210.jpg?2190", 75, "作家・小川国夫による数編の小説を、3人の新進監督が3話のオムニバスで映画化。長編小説「マグレブ、誘惑として」と短編「駅の明り」を与那覇政之監督が独自解釈して描く「誘惑として、」、ひとりの老人の失踪を描く高野貴子監督の「他界」、第50回読売文学賞を受賞した短編小説集を原作に日常に潜む哀しみや幻想を描く小沢和史監督の「ハシッシ・ギャング」の3本で構成される。", "2010-07-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JASON GOES TO HELL: THE FINAL FRIDAY", "13日の金曜日 ジェイソンの命日", "", "", "http://ecx.images-amazon.com/images/I/51nHJCK8OPL._SL210_.jpg", 91, "１作目の監督S・S・カニンガムの製作による、人気ホラー・シリーズの９作目。バラバラ死体となったジェイソンが他人の体に乗り移って悪事を繰り返し、完璧な体を再生するために自分の血縁者の体に乗り移ろうとする。「エルム街の悪夢」の人気キャラクター、フレディがチラリと顔を見せるのも話題になった。", "1993-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラVSメカゴジラ", "ゴジラVSメカゴジラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1975年の「メカゴジラの逆襲」から18年ぶりに復活したメカゴジラが、人類のゴジラに対する最新兵器として登場。さらに「怪獣総進撃」（1968）以来のラドン、ベビーゴジラと、キャラクターを盛り込んだ大活劇編。かつて未来人が作り上げたメカキングギドラのテクノロジーを参考に、メカゴジラが完成しつつあった。その頃、アドノア島で核の影響でラドンが誕生。島で調査をしていた大前博士と五条梓は、ラドンの卵の隣りにあった謎の卵を持ち帰る。卵から生まれたのは、ゴジラと同種族のベビーゴジラ。ゴジラはベビーゴジラに誘われて日本上陸。ついに完成したメカゴジラと戦うが……。ドラマ性よりも怪獣同士の戦いに力点を置いて描かれ、その部分では見ごたえがある。ただ、ゴジラは回を追うごとに強くなりすぎている感がある。", "1993-12-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラ・ミニラ・ガバラ オール怪獣大進撃", "ゴジラ・ミニラ・ガバラ オール怪獣大進撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "怪獣たちの戦いを弱虫少年の夢の中の世界として設定し、従来使用された様々な決闘シーンのフィルムを大幅に再使用。東宝が初めて子供を主人公にした怪獣映画で、この作品から怪獣映画は“東宝チャンピオン祭り”という名前で公開されることになる。", "1969-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版NARUTO−ナルト−／大激突！幻の地底遺跡だってばよ", "劇場版NARUTO−ナルト−／大激突！幻の地底遺跡だってばよ", "", "", "http://ecx.images-amazon.com/images/I/51Q0ZG6N39L._SL210_.jpg", 97, "一人前になるため大奮闘する落ちこぼれ忍者・うずまきナルト。'99年に『週刊少年ジャンプ』で連載開始後、コミックス累計発行部数5000万部を突破、TVアニメ版も好評を博した人気シリーズ映画化第2弾。今回は、謎の地底遺跡などが登場し、原作にも描かれていない史上最強の敵が現れる。“火の国”と“風の国”の国境の森で、謎の少年騎士・テムジンの襲撃を受けたナルト。激突の末、テムジンと崖から転落してしまう。", "2005-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姉妹", "姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "山奥の発電所勤務の父母のもとを離れ、学校に通うため伯母の家に世話になる姉と妹。そんな彼女たちが、年月を経るごとに少女から大人へと精神的に成長していく姿を、静かに温かく描いた小市民ドラマ。畔柳二美の毎日出版文化賞受賞作を映画化した。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風と樹と空と", "風と樹と空と", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "石坂洋次郎の同名小説を吉永小百合と浜田光夫の日活純愛コンビ主演で映画化した青春もの。沢田多喜子は集団就職の一員として上京し、ある家のお手伝いさんとなり、幾多の苦しみや悲しみも持ち前の無邪気な明るさで次々に克服していく。今回は浜田の出番は少なく、吉永小百合の単独主演的な印象が深い。日活はマンネリ気味のダイヤモンド・ラインに対抗してグリーン・ラインなる青春スター路線を新設し、吉永小百合と浜田光夫を中心に、田代みどり、和泉雅子、杉山俊夫、そしてデビュー当時の高橋英樹がメンバーとなり青春路線を支えた。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAGED WOMEN", "アマゾネス・プリズン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "世界中から誘拐されたグラマーな美女たちが監禁されている、南アフリカ奥地の謎の要塞。全裸で鉄格子の中に入れられ、金持ちのなぐさみ物となっていた彼女たちの怒りが、ある日ついに爆発。徒党を組んで脱獄し、悪人どもに立ち向かっていく……。イタリア産のエロティック・バイオレンス。", "1991-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JACOB’S LADDER", "ジェイコブス・ラダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "夢と現実の区別がつかなくなったベトナム帰還兵、ジェイコブ・シンガーを主人公に、人間の心の奥に潜んでいる無意識の恐怖を暴き出した心理サスペンス。「ナインハーフ」「危険な情事」で心理描写に定評のあるライン監督が、現実か、妄想か、観客をも巻き込んで展開する息詰まるドラマを鮮やかに描ききる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The French Connection", "フレンチ・コネクション", "", "", "http://ecx.images-amazon.com/images/I/61r7uIOHijL._SL210_.jpg", 104, "　ニューヨーク、麻薬密売ルートを探るポパイことドイルと相棒のラソー両刑事は、マルセイユからやってきたシャルニエの尾行を開始。強引な捜査を行なうドイルは逆に命を狙われることになり……。ニューヨーク市警の刑事ふたりが繰り広げる決死の捜査を描いたサスペンス・アクション。高架線下のカーチェイスは映画史に残る名場面となった。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE OF CARDS", "心の扉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "TV、演劇界で活躍してきたM・レサックの劇場用映画デビュー作。考古学者の父を事故で失った、６歳のサリー。彼女は南米マヤのインディオの“人は死ぬと月へ行き、選ばれた特別な子と話をする”という伝説を信じる。そして自閉症のように口を閉ざし、月からのメッセージを待つが……。サリーを愛の力で神秘の世界から呼び戻そうとする母を、K・ターナーが演じる。ビデオ発売時タイトルは「ハウス・オブ・カード～心の扉」。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明るい場所 square the circle", "明るい場所 square the circle", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "1994ぴあフィルムフェスティバルで「悲しいだけ」がグランプリに輝き、その後も「エル・ドラド」が特別招待作品に選ばれた豊島圭介。アメリカ・フィルム・インスティテュートに学ぶ彼がその留学中に撮り上げた作品。一人の女性が姉の元恋人にいたずらを仕掛けたことで、周囲の人間を戸惑わせていく様、人々の揺らめく心を繊細なタッチで描き出していく。撮影は「ウンタマギルー」の田村正毅。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("これが青春だ！", "これが青春だ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "前年の秋から始まった日本テレビの『青春とは何だ』で、夏木陽介扮する型破りの教師が人気を博したのにあやかって作られたのが本作品。地元の高校に英語教師として赴任して来た由木真介は、ラグビー部の面倒をみている。しかし部員の成績が悪いので校長や教頭は部の解散を要求。そこで先生と生徒は奮起する、という本格的学園青春映画。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THOMAS CROWN AFFAIR", "華麗なる賭け", "", "", "http://ecx.images-amazon.com/images/I/51rgBFVfjbL._SL210_.jpg", 102, "ボストンの大銀行から260万ドルが強奪された。現場には証拠のかけらも残されず、綿密な計画と緻密な計算、完璧なチームワークの犯罪だった。事件を担当するマローン刑事の前に保険会社の送り込んだ美人探偵ビッキーが現れ、次のような仮説をたてる。これは金欲しさの犯罪ではなく、おのれの頭脳と可能性を試すためのものだ、と。そして彼女が目星をつけた容疑者は、なんと世間で知らぬ者のいない大実業家トーマス・クラウンだった……。それまでとはガラリとイメージを変え、スーツでキメたマックィーンが、ダナウェイ扮する探偵とギリギリの知恵比べとロマンスを展開。粋でお洒落な犯罪映画の傑作。1999年にはピアース・ブロスナン主演で、「トーマス・クラウン・アフェアー」のタイトルでリメイクされた。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美國心／JUST　LIKE　WEATHER", "ジャスト・ライク・ウエザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "親を頼って香港からアメリカへ旅立ったある夫婦の物語。監督自身によるインタビューも交えた、ドキュメンタリー・タッチのユニークな作風で、将来への不安と苛立ちで揺れ動く男女の心模様をあざやかに映し出している。1987年の香港アカデミー賞で監督、編集、新人の３部門を受賞した作品。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD NIGHTS", "WILD NIGHTS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "闇社会に台頭する殺人スペシャリスト集団“タルタロス”の極秘ファイルをめぐって、弟を倒された元武道家が組織に戦いを挑んでいく。格闘技界から本物の武道家を多数集め、リアル・ファイトを具現化したアクション映画。アメリカで映画製作を学んだ鈴木洋一監督の第２作。主演はインディーズ・アクション映画の製作に気を吐く西冬彦。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エッチを狙え！　「イヌネコ。」", "エッチを狙え！　「イヌネコ。」", "", "", "http://image.eiga.k-img.com/images/movie/54710/315x210.jpg?3146", 0, "東萌女子学園の教師・星川幸は生まれつきの男性恐怖症で、28歳になっても処女を貫いていた。そんな幸に一目ぼれをした神田寅一は、幸に会うため女装して東萌女子学園に通っていたが、寅一に好意を寄せる青井マキノの登場で女装がばれてしまう。そんな中、雷に打たれた幸とマキノの人格が入れ替わってしまい……。監督は「春琴抄（2008）」の金田敬、原作は人気漫画家・葉月京の「イヌネコ。」。", "2009-08-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PERFECT ALIBI", "不在証明—ア・リ・バ・イ—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "裕福なバウアー医師と妻メラニーはフランス人女性のジャニーンをメイドとして雇う。あか抜けない田舎娘だった彼女は、やがてセクシーで洗練された美しい女へと変貌し、バウアーは彼女と不倫関係を持つ。しかし、一方で二人の関係を探ろうとした者たちが次々と謎の死をとげていく。女が仕掛けた罠をめぐるサイコ・サスペンス。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIAMONDS", "MR.ダイヤモンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "金庫破りとその恋人、宝石商の３人が、イスラエルのテルアビブにある世界のダイヤ取引所襲撃を計画。国際警察の尾行を承知で、無数の警備網や赤外線装置を突破、ついにダイヤを手に入れるが……。ダイヤ到達までのスリルが見もののサスペンス。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE ICE", "ブルー・アイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ジャズクラブのオーナーで、元英国情報部員のハリーは、アメリカ大使の妻ステイシーと危険な恋に落ち、彼女から依頼を受けた行方不明者の捜索を始める。だが、その背後には国際的な武器密輸に関する陰謀が潜んでいた……。ジャズのリズムにのせてアダルトな雰囲気で描かれる、ムーディーなサスペンス。", "1992-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Percy Jackson & the Olympians: The Sea of Monsters", "パーシー・ジャクソンとオリンポスの神々2 魔海の冒険（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ENFORCER", "ダーティハリー3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "シリーズ第３作。警察内部過激派グループが陸軍の兵器庫から武器を盗み出した。さらに市長を誘拐し、身代金を要求してくる。ハリーのビッグマグナムが、この過激派グループへ向けて放たれる。ハリーとコンビを組む女刑事の活躍も魅力的な痛快編。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("能楽師", "能楽師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "600年あまりの伝統を誇り、2001年にはユネスコの世界無形文化遺産にも指定された日本の古典芸術“能”の世界に迫ったドキュメンタリー。映画評論家として知られる田中千世子が、観世流シテ方、関根祥六と関根祥人の親子の舞台、稽古、対談を通して能の魅力を余すことなく伝える。通常は非公開の稽古風景など貴重なショットも満載で、能楽ファンには見逃せない作品だ。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRIME COUNTDOWN", "クライム・カウントダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "テキサス州カルウエストン、この平和な街に若い女性を襲った連続レイプ事件が起きる。ベテラン刑事マックスは、容疑者カル・ハーパーを連行するが、カルは裁判中スキを見て脱走。マックスは取り調べ中の暴行を理由に捜査を外されていたが、独自に捜査を続行。ところがカールの逃亡中に事件は再発し、事件は意外な進展を見せるが……。タイトルの“カウントダウン”よろしく、時を追ってその緊張感が増すポリス・アクション。元ポリスのドラマー、S・コープランドが音楽を担当している。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TERRORGRAM", "地獄の配達人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“テラーグラム”といわれる地獄からの小包。それはかつて自分が犯した悪行の数々が封じ込められたものだった。その小包が届いた日から、人々の恐怖が始まる。３話オムニバスで、それぞれの恐怖を描くSFホラー。ナレーションは、「スター・ウォーズ」のダース・ベーダーの声で知られるJ・E・ジョーンズ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・男は愛嬌", "喜劇・男は愛嬌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "「男はつらいよ・フーテンの寅」に続くバイタリティーあふれる森崎東の喜劇。処女作「女は度胸」の続編にあたり、自分の恋人が実はコールガールだと憶測してやきもきする男の純情が、笑いとペーソスを呼ぶ。鑑別所帰りの不良少女を倍賞美津子が、人のいい寅さんを思わせる船乗りを渥美清が演じている。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHERE IS PARSIFAL？", "トニー・カーチスの発明狂時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "世界改革に燃えているハチャメチャな妻と一獲千金を夢見ている発明狂パーシファルの夫婦が繰り広げるドタバタ・コメディ。“007”シリーズで知られるT・ヤング監督がO・ウェルズ、T・カーティスなど豪華キャストを起用し製作総指揮した爆笑もの。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kansas City", "カンザス・シティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "　1934年のアメリカ・カンザス、権力者ペンダーガストが支配する街。ブロンディはルーズベルト大統領の顧問であるヘンリーの妻キャロリンを誘拐する。ブロンディの夫ジョニーは街の顔役でもある黒人ギャング、セルダムの縄張りで強盗をはたらき、囚われの身に。そんな夫を救い出すため、ブロンディはキャロリンを利用しようとしていた。やがてブロンディとキャロリンは心を通わせていくが……。ギャングと選挙が絡む誘拐事件の関係者たちをジャズのリズムにのせて描く。", "1996-01-01", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兵隊やくざ・脱獄", "兵隊やくざ・脱獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "憲兵隊に捕まった大宮と有田は、奉天にある陸軍刑務所に放り込まれる。敵前逃亡という銃殺刑にあたる罪を犯した二人に出所の見込みはなく、有田は脱獄を決意する……。シリーズ４作目、森一生演出は他の作品と比べ軽妙なスピード感にやや欠けていた。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THUNDERPANTS", "サンダーパンツ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "“オナラ”少年が主人公の奇想天外なナンセンス・コメディ。オナラが止まらないイジメられっ子の少年パトリックは、臭覚のないオタクの天才少年アランだけが親友だ。ある日、アランがオナラパワーを利用した画期的な発明をし、パトリックに転機が訪れる。「ハリー・ポッター」シリーズのロン役ルパート・グリントが天才少年に扮し、ユニークな個性を発揮。", "2002-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Va, Vis et Deviens", "約束の旅路", "", "", "http://image.eiga.k-img.com/images/movie/33890/315x210.jpg?1253", 149, "エチオピアのユダヤ人をイスラエルに移住させた“モーセ作戦”を背景に、ひとりイスラエルへと渡ったエチオピア人少年の成長を描いた感動作。スーダンの難民キャンプにいたひとりの少年。彼の将来を案じた母は、ユダヤ人のふりをしてキャンプを脱出するよう命じる。無事イスラエルに辿り着いた少年は、母や故郷への強い思いを胸に秘めながら、優しい養父母の元で新たな人生をスタートさせる。しかしエチオピア人である彼への風当たりは厳しく……。", "2007-03-10", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE RETOUR DE CASANOVA", "カサノヴァ最後の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "希代の色事師カサノヴァの晩年を描く文芸ロマン。清楚な娘マルコリーナに恋をしたカサノヴァ。どんな女性でもモノにしてきた彼だが、今回ばかりは大苦戦。それどころか、彼女は年老いたカサノヴァに侮辱の言葉を投げかける……。A・ドロンがカサノヴァに扮して、私生活を彷彿させる演技を披露。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鳳城の花嫁", "鳳城の花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "日本初の色彩シネマスコープ作品で、ワイドスクリーンの迫力が話題を呼んだ。鳳城の若殿・松平源太郎は30歳でいまだ独身。周りは嫁探しに積極的だが本人は無関心だ。嫁を見つけるという条件で旅に出た源太郎は、道中、豪商井筒屋のおきぬとおみつという姉妹を危機から救う。そしておきぬに胸トキめかすのだが、無器用なために愛を打ちあけられない……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮澤賢治—その愛—", "宮澤賢治—その愛—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "宮澤賢治の人生は人々の幸福を願い、自然を愛する精神に貫かれていた一方、愛に苦悩し続けたものでもあった。質・古着商の長男として生まれた賢治は家業になじめず、自らの理想実現の道を歩み始めた時、父・政次郎との間に葛藤が生まれる。敬愛する父との、親子ゆえの相克。唯一の理解者だった最愛の妹トシとの死別。貧しい農民への愛も報われることは少なかった。それでもなお純粋な心を悩ませながら、人々の幸せを願い孤独な戦いを続けた賢治の愛を描く。宮澤賢治生誕100年を記念して作られた作品。同時期、東映の「わが心の銀河鉄道　宮沢賢治物語」との競作が話題を呼んだ。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HENRY FOOL", "ヘンリー・フール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "無口で冴えない日々を過ごすゴミ収集人サイモンと、彼の家の地下室に転がり込んだ文学かぶれのホームレス、ヘンリー。やがてヘンリーに影響を受けたサイモンは、突如として詩の才能に目覚めて有名人となるが、自作の小説をコキおろされたヘンリーは哀れな運命をたどっていく。何の変哲もない郊外に暮らす人々の人間模様を通して、人生の皮肉や可能性を感動的に浮き彫りにしたH・ハートリー流の“大河”ドラマ。鋭い人間観察、端正な映像美、随所に苦笑を誘うユーモアのセンスが忘れ難い１本。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄拳　ブラッド・ベンジェンス", "鉄拳　ブラッド・ベンジェンス", "", "", "http://image.eiga.k-img.com/images/movie/56450/315x210.jpg?3143", 93, "全世界でシリーズ累計4000万本以上のセールスを上げている人気格闘ゲーム「鉄拳」を、フルCG長編アニメーションとして3D映画化。中国拳法の達人でもある女子高生リン・シャオユウは、京都のインターナショナルスクールに通う神谷真という男を調査するよう依頼され、学園に潜入する。しかし真には煙に巻かれ、ナゾの少女アリサにも翻弄されて、なかなか捜査が進まずにいたある日、真が何者かに誘拐されてしまう。真を追跡してさらなる捜査に乗り出すシャオユウは、やがて巨大な陰謀に巻き込まれていく。", "2011-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Scandal Maker", "過速スキャンダル", "", "", "http://image.eiga.k-img.com/images/movie/55220/315x210.jpg?3063", 108, "「猟奇的な彼女」でブレイクしたチャ・テヒョンが主演するコメディ。元トップアイドルのヒョンスは、30代半ばになった今でも人気ラジオ番組のDJとして活躍していた。そんな彼の前に熱心なリスナーのジョンナムが現れ、自分はヒョンスの実の娘だと主張し始める。当惑するヒョンスをよそに、小さな子供を連れてストーカーのようにヒョンスに付きまとうジョンナム。そんな3人に芸能記者の魔の手が忍び寄っていた。", "2010-02-13", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Anonymous", "作者不詳（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/56750/315x210.jpg?1249", 129, "ウィリアム・シェイクスピアの作品は別人が執筆していたという説をもとにした歴史ドラマ。シェイクスピアの正体の有力候補とされるオックスフォード伯を主人公に描く。", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛と死の谷間", "愛と死の谷間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "美しい女医が、彼女を尾行する私立探偵とその正体を知らずに恋に落ちるという恋愛映画。名匠・五所平之助が監督、脚本を作家の椎名麟三が担当しているが、人間の生と死、そして愛情を悲劇とも喜劇ともつかない曖昧な視点で描いてしまったのは惜しい。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("“BLOW THE NIGHT！” 夜をぶっとばせ", "“BLOW THE NIGHT！” 夜をぶっとばせ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "高田奈美江が実際に送ってきた生活をもとに、暴力、シンナー、セックスなど荒廃した少女の日々を、「博多っ子純情」「赤い暴力」など異色の青春映画を手掛けた曾根中生が描く。ある中学校に、赤くちぢれた髪にまゆ毛をそった少女が転校してきたが……。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CHIAVE", "鍵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "日本の文豪・谷崎潤一郎の同名小説を映画化。1940年のベニスを舞台に、妻の貞淑さに不満を感じ彼女の性を目覚めさせようとする老紳士と、彼の計略にはまり、娘の恋人との情事に溺れていく妻との異常ともいえる性行動を、ドライにそしてコミカルに描いた作品。", "1984-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三人の女 夜の蝶", "三人の女 夜の蝶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "青江三奈の歌『昭和おんなブルース』をモチーフとした、流転する女の悲恋ドラマ。北海道の田舎町で、恋人とデート中のところを、ヤクザ者の英次に襲われた綾子。噂が広まり、町にいられなくなった彼女は、東京のバーのホステスになる。宝石商・石塚の慰めで綾子は立ち直るが、ある日、英次と再会する。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN　INSTANT　D’INNOCENCE", "パンと植木鉢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "イラン映画界の巨匠M・マフマルバフの日本初公開作品。映画監督マフマルバフの家に一人の男が訪ねてくる。その男は、マフマルバフが過激派少年だったころに、彼に刺された警官だった。あの日のことを映画にして再現しようと、企画が閃いたマフマルバフは、さっそくオーディションを開始し撮影を始めるのだが……。10代でゲリラ活動に投じ、17歳で警官を刺して逮捕され４年半の牢獄生活を送ったという“マフマルバフ伝説”を自ら映画化。", "1996-01-01", "15,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wonder Boys", "ワンダー・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/1750/315x210.jpg?1435", 111, "大学教授兼作家のグラディは、かつては“ワンダー・ボーイ＝神童”ともてはやされた天才作家だったが、50歳となった今、7年前に書き始めた新作の結末が書けず、妻にも家出され、愛人には妊娠を告げられていた。頭の痛いことに、大学で開催される毎年恒例の作家の祭「ワードフェスト」に合わせ、かつての編集者も新作を求めてやってきている。そんな折も折、教え子の“新世代ワンダー・ボーイ”ジェームズと出くわしたグラディは、何故か拳銃片手に泣き崩れるジェームズをなだめる羽目に。", "2000-09-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い画集・寒流", "黒い画集・寒流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "松本清張の原作を鈴木英夫監督、池部良主演で映画化。某銀行池袋支店長・沖野は料亭の女主人・奈美を見染め関係を持つが、上役で同窓生の桑山がそれを知る。桑山は沖野を左遷し奈美をものにしようとするが……。池部良が嫉妬に狂う中年男を熱演。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドーバーばばぁ　織姫たちの挑戦", "ドーバーばばぁ　織姫たちの挑戦", "", "", "http://image.eiga.k-img.com/images/movie/56680/315x210.jpg?2855", 116, "54歳から67歳までの女性6人が、親の介護や家族の世話という日常を抱えながらも2日間でドーバー海峡を泳いで横断するリレーに挑んだ姿を追ったドキュメンタリー。チームメンバー6人の2年間を通して、それぞれがチャレンジにいたるまでの葛藤（かっとう）も描かれる。", "2011-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さようならの季節", "さようならの季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "東北の寒村で台風のため家と田畑を失った幸子の一家。南米移民を志して横浜にやって来た彼女はそこで幼なじみの高志と出会う。家族ぐるみの移民には労働者が３人以上必要なため、幸子が父によって偽装結婚させられそうになっていることを知った高志は、行方不明になっている彼女の姉を探す。浜田＝小百合によるラブ・ストーリー。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEDER FU¨R SICH UND GOTT GEGEN ALLE", "カスパー・ハウザーの謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "19世紀のドイツに実在した謎の野生児を描いて、人間社会に鋭い批判を投げかけた異色作。1828年、バイエルン王国に奇妙な若者が現れた。年齢は17歳前後。ずんぐりとしていて、ろくに歩けない。言葉もほとんどしゃべれず、身元は不明。後に、カスパー・ハウザーと名付けられた若者は、生まれてこのかたずっと地下牢に閉じ込められ、自分以外に人間がいることさえ知らなかったのだ。ある意味で、アイデンティティーを欠いたカスパーは、終始人々の人生観を映す鏡となる。そして、教育を受けても社会に同化できず、秩序を脅かす“異物”として暗殺されるのだ。“人間は狼です”——カスパーの言葉が、強く心に突き刺さる。", "1975-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あすなろ物語", "あすなろ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "井上靖が少年の成長を描いた原作を黒澤明が脚色した作品。監督の堀川弘通は黒澤の助監督だったが、これは監督第１回作品。おばあちゃん子の気弱な少年が成長していく年代記ものである。題名は、桧になろうとする“あすなろ”の木に由来している。黒澤ヒューマニズムを理解する上でも貴重な作品。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("King of California", "カリフォルニア・トレジャー", "", "", "http://ecx.images-amazon.com/images/I/51oGECYVWxL._SL210_.jpg", 93, "名優マイケル・ダグラスと、「アクロス・ザ・ユニバース」のエバン・レイチェル・ウッド共演によるハートフルコメディ。母親に捨てられた16歳の少女ミランダは、マクドナルドでアルバイトしながらひとり暮らしを送っていた。そんなミランダの平穏な日常が、精神病院から退院してきた父チャーリーの登場によって激変。町のどこかに300年前のスペインの秘宝が眠っていると言いだした父の宝さがしにつきあわされるハメになり……。", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUNGEE　JUMPING　OF　THEIR　OWN", "バンジージャンプする", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "人気韓流スター、イ・ビョンホン主演の本作は、17年の時を隔てて描かれる愛の物語。ひとりの女性を想い続ける主人公の運命的な体験を、輪廻転生というテーマを絡めて紡ぎ出す。純情な大学生インウが彫刻科の女の子テヒに魅了される。ふたりは心を通わせるが、インウが兵役に出るときテヒが姿を消してしまい……。", "2005-03-19", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LONG SHOT", "恋とゲームと４人組", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ポールはスポーツ万能な青年。成績優秀ながらも、ゲームセンターでサッカーゲームに夢中な陽気な高校生だ。そんな彼の夢は、ヨーロッパに行くこと。その資金稼ぎのために、彼は全米サッカーゲーム大会に出場しようと考えた。優勝すれば賞金が手に入る。決心した彼は、両親の反対を押し切り、一流大学への推薦もけって、親友リロイと旅立った……。高度なテクニックが要求されるサッカーゲームに賭けた若者たちの奮闘ぶりを描く青春ドラマ。", "1982-01-01", "67", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Running Scared", "シカゴ・コネクション／夢みて走れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "　レイとダニーはシカゴ警察のタフな刑事コンビ。激務に明け暮れるダニーに朗報が。亡くなった親戚が4万ドルの遺産を彼にのこしたというのだ。ふたり組はマフィアのフリオを追う一方、危険ばかりがつきまとう仕事に見切りをつけ、退職を決意。遺産を元手にフロリダでバーを開くことにした彼らは最後の仕事でもあるフリオ逮捕に乗り出す。しかし敵はダニーの元・妻を人質に取った！　刑事コンビが繰り広げる痛快アクションコメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪猫呪いの壁", "怪猫呪いの壁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "お家乗っ取りをたくらむ老中の五月と御用人の田所典膳は、藩主のお気に入りの腰元・志乃を殺害し黒猫とともに壁に塗りこめてしまう。以後城内では次々と不気味な事件が起きる。白ぬり二枚目時代の勝新太郎主演による一編。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЧАЙКА", "チェホフのかもめ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "19世紀末、帝政ロシアが崩壊する前夜の、倦怠と焦燥にとらわれた知識人の生活を綴ったチェホフの同名戯曲の映画化。退屈な時代に我慢できず、新しい劇形態に可能性を見つけようとする若い作家の挫折と、女優志願の美しい娘の希望が対照的に描き出される。", "1971-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大岡政談 幽霊八十八夜", "大岡政談 幽霊八十八夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "大友柳太朗が一人三役を演じる痛快時代劇。怪盗団・蝙蝠組を親の仇と狙う、手踊り一座の美人短剣打ち、小糸。だが彼女が殺す前に、蝙蝠組一味は、次々に怪死を遂げる。その裏には、彼らが隠した金をめぐる内紛があるらしい。小糸は、この争いに謎の浪人、的場陣十郎とともに巻き込まれる。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("臨場　劇場版", "臨場　劇場版", "", "", "http://image.eiga.k-img.com/images/movie/57650/315x210.jpg?3596", 0, "横山秀夫による同名警察小説が原作で、検視官・倉石義男の活躍を描いた内野聖陽主演のTVドラマ「臨場」（2009）、「臨場　続章」（10）に続く劇場版。都内で無差別通り魔事件が起こり、遺族の訴えに反して、犯人は心神喪失を認められ無罪になる。2年後、犯人を担当した弁護士と精神鑑定を行った医師が相次いで殺害され、捜査本部は通り魔事件の遺族による犯行とにらむ。しかし、検視官・倉石は被害者の死亡推定時刻に疑問を抱き、犯人は別にいると推測するが……。主演の内野をはじめ、松下由樹、渡辺大、平山浩行、高嶋政伸らドラマ版のレギュラー陣も続投。監督は「探偵はBARにいる」の橋本一。", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alvin and the Chipmunks: The Squeakquel", "アルビン2 シマリス3兄弟vs.3姉妹", "", "", "http://ecx.images-amazon.com/images/I/51997vZqS2L._SL210_.jpg", 0, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A KING IN NEW YORK", "ニューヨークの王様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "喜劇王チャップリンが、「ライムライト」から５年ぶりに製作した作品。ヨーロッパの架空の国の王様が、ニューヨークに亡命して巻き起こす行状記。コマーシャル、整形手術、流行の音楽などなど、アメリカの文明を皮肉を込めて描写している。チャップリンが出演する劇映画としては最後の主演作品。", "1957-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL GATTOPARDO", "風雲の支那", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "DVDタイトルが「風雲のチャイナ」に変更された作品だが、当時の雰囲気を伝える点では「支那」の方が打ってつけだろう。キャプラ監督的には、例えば「ローマの休日」に通じるラブコメが「或る夜の出来事」だとすれば、本作はアメリカ人と異国人の恋愛ものという点で、「慕情」へつながる。アメリカの女性宣教師が中国に来て中国人の将軍と恋に落ちるラブ・ストーリー。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stomp the Yard", "ストンプ・ザ・ヤード", "", "", "http://image.eiga.k-img.com/images/movie/34190/315x210.jpg?1584", 114, "手足で音を鳴らしてリズムを刻むダンス“ストンプ”に熱中する青年の成長を描いた青春ダンス・ムービー。ストリート・ダンサーのDJは、ダンス・バトルで打ち負かした相手に襲撃され、兄を亡くしてしまう。兄の夢だった大学に進学した彼は、学生たちによる“ストンプ”に遭遇。チームに加わり、大会を目指して特訓を開始するが……。プロのダンサーとして活躍してきた若手スターたちによる迫力のダンス・シーンは必見。", "2007-09-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("父ちゃんのポーが聞こえる", "父ちゃんのポーが聞こえる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "筋肉が萎縮してしまうハンチントン舞踊病という、治療の方法が発見されていない難病と戦い、死に直面しながらもけなげに生きようとする少女。その心理を、両親や周囲の人々の姿を通して描いた感動作。実在した少女の詩集の映画化。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アラスカ物語", "アラスカ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "明治中期、単身アラスカに渡って、飢餓に苦しむエスキモーのため、新天地ビーバー村を築いた日本人・フランク安田の波乱に富んだ半生を描いたもの。大規模なアラスカ・ロケが敢行され、原作のイメージを裏切らないスケールの大きな作品に仕上がっている。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Die Wolke", "みえない雲", "", "", "http://image.eiga.k-img.com/images/movie/33800/315x210.jpg?3141", 103, "チェルノブイリ原発事故の翌年に発表され大反響を呼んだベストセラー小説を「レボリューション6」のグレゴール・シュニッツラー監督が映画化。高校3年生のハンナは、ほのかに想いを寄せていた転校生のエルマーにキスされ、彼も自分に好意を持っていたことを知る。しかし幸せは長くは続かなかった。近郊の原子力発電所で大事故が発生したのだ。町がパニック状態に陥る中、ハンナは必ず迎えに来ると言うエルマーの言葉を信じて待つが……。", "2006-12-30", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲット★イット★オン？", "ゲット★イット★オン？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "グラビアアイドルの周防玲子が女優として初主演を飾ったアクション・コメディ。女子高校生フタバのアルバイトをしているレストランに、バンドの軍資金稼ぎのために銀行強盗をした売れないロッカー、ヘヴン伊藤が飛び込んで来る。フタバは、自分の夢への軍資金の提供と引き換えに、協力を提案し、自分の携帯メールからメチャクチャな要求を送信。フタバからの送られてくる意味不明のメールを国際的カルト・テロ集団からのものと勘違いした警察は……。物語の狂言回しとも言える売れないロッカーのヘヴン伊藤を、本作で音楽も担当した個性派ミュージシャンのROLLYが怪演している。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BARABBAS", "バラバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "バラバは盗賊であり殺人鬼でもあるが、キリストの処刑と引き換えに、神に最も近い男として命を救われ、自由を手にする……。幾多の波乱を経て、次第にキリストの存在を信じ始めるバラバの半生を描いたスペクタクル史劇。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ある機関助士", "ある機関助士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 37, "常磐線経由、青森行の急行列車“みちのく”を舞台に、機関士とその助士との協同作業を軸に、彼らの仕事ぶりを描く、ドキュメンタリー作家・土本典昭の実質的処女作。国鉄のPR映画として企画されたが、土本監督は、機関士たちの苛酷な労働の実情や、彼らの熟練した技術の細部を映し出し、額に汗して仕事に打ち込む者への慈しみをたたえた。狭い機関室内をあらゆる角度から捉えるとともに、望遠レンズを駆使してダイナミックに疾走する汽車の美しくも力強い姿を入れるなど、力動感あふれるカメラワークも見どころ。映画史上、SL映画のベストワンとの呼び声が高い名作だ。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阪急電車　片道15分の奇跡", "阪急電車　片道15分の奇跡", "", "", "http://image.eiga.k-img.com/images/movie/55680/315x210.jpg?4728", 119, "「フリーター、家を買う。」「図書館戦争」などで知られる人気作家・有川浩の原作小説を映画化。兵庫・宝塚市の宝塚駅から西宮市の今津駅までを結ぶ阪急今津線を舞台に、婚約中の恋人を後輩社員に奪われたアラサーOL、恋人のDVに悩む女子大生、息子夫婦との関係がぎくしゃくしている老婦人らの人生が交錯する。片道15分のローカル線で起きる小さな奇跡の数々を描くヒューマンドラマ。主演は中谷美紀、戸田恵梨香、宮本信子。共演に人気子役の芦田愛菜ら。", "2011-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOOM！", "夕なぎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ナポリ湾の孤島で隠居生活を送る中年女性は、かつては絶世の美女と呼ばれながら今は謎の病に冒され、回想録を書きながら恐怖と焦燥の日々を過ごしている。その島に一人の男が流れ着いた。“その男は天使だ”と魔術師が告げるが……。T・ウィリアムズの舞台劇を映画化した異色作。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASHANTI", "アシャンティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "奴隷商人によって奪われた妻を救出すべく、男は灼熱の砂漠に追跡を開始する。彼女が売られる前に、何としても取り戻さねばならない……。国際色豊かなオールスター・キャストで描くアドベンチャー・ロマン。", "1978-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PROTECTOR", "ブレイク・スルー　クレイゴ島からの脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "新開発の有毒ウイルスを追って孤島に潜入し、敵の罠に落ちた仕事人ケン。ウイルスを投与された彼は死のタイムリミットが迫る中で、解毒ワクチンを奪取すべく狂気のクレイゴ博士に戦いを挑む……。「レッド・スコルピオン２」のM・マッコウムがマーシャルアーツの妙技を披露するバトル・アクション。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遊太郎巷談", "遊太郎巷談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "市川雷蔵が、初めて柴田錬三郎の原作ものに挑んだ、怪奇ムード漂う伝奇時代劇。上野の輪王寺の宮と将軍家の娘との間に生まれたという高貴な身分ながら、今は浪人として暮らす遊太郎。彼は幕府によって藩を取り潰された大名の娘・つう姫が、幕府転覆の陰謀をめぐらしていることを知り……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Submarino", "光のほうへ", "", "", "http://image.eiga.k-img.com/images/movie/56040/315x210.jpg?2738", 114, "幼い弟の死を受け入れられず、心に深い傷を残したまま育った兄弟は、それぞれに苦しみもがきながらも毎日を生きていた。お互いにかかわることなく別々の人生を歩んでいた2人だったが、アルコール依存症だった母親の死をきっかけに再会。再び気持ちを通わせようとするが……。「セレブレーション」「DEAR WENDY ディア・ウェンディ」で知られるデンマークのトマス・ビンターベア監督作。第60回ベルリン国際映画祭コンペティション部門に出品された。", "2011-06-04", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pianist", "戦場のピアニスト", "", "", "http://image.eiga.k-img.com/images/movie/1700/315x210.jpg?1779", 148, "ナチスドイツ侵攻下のポーランドで生きた実在のユダヤ人ピアニストの自伝を映画化。監督ポランスキーは、パリでポーランド人の両親のもとに生まれ、両親は収容所に送られ母親は死亡、自身は各地を放浪、身を隠して生き延びた、映画の主人公に似た経験をもつ。本作はカンヌ映画祭でパルムドールを受賞。監督と主演のエイドリアン・ブロディは、ボストン映画批評家賞を受賞。シカゴ映画批評家賞など数々の映画賞にノミネートされた。", "2003-02-15", "2,7,42,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("処刑の島", "処刑の島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "篠田正浩監督が松竹を退社後、初めて撮り上げた文芸映画。戦時中、軍によって太平洋の孤島に流され、地獄の責め苦を受けた少年が20年後、ある目的のために島に舞い戻ってきた。しかし、男はかつて自分を虐待した雇い主の娘と恋に落ちる。しかも、その雇い主の正体は、男の父母と兄を殺した犯人だった……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABYLON５", "スペース・ガーディアン バビロン５", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "銀河系の平和を守る宇宙基地“バビロン５”で起こる人間ドラマやスペース・バトルを描くSFアドベンチャー。ある日、惑星“イプシロン３”で奇妙な振動が観測された。調査に向かったシンクレア中佐は、衰弱したエイリアンの閉じ込められたテクノロジー・マシンを発見。エイリアンによると、マシンは未来への遺産であり、守護者の彼がいなくなると、何者かが惑星に侵入するやいなや自動的に爆発、“バビロン５”も巻き添えになるという。阻止するには、一刻も早く新しい守護者を見つけなければならない。そんな時、マシンの所有権を主張する地球軍と、“イプシロン３”の帰属権を主張するエイリアンの間で戦闘が開始される。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金環蝕", "金環蝕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "九頭竜川ダム建設をめぐる政財界の黒い霧と、総裁の座を争って血みどろの戦いを演じる政治家たちを描いた、石川達三の同名小説が原作の社会派タッチの大型ドラマ。国会の議事録から綿密に構成したという原作のエネルギーを山本薩夫が見事に爆発させる。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京家族", "東京家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「男はつらいよ」「学校」シリーズの山田洋次81本目の監督作。映画監督生活50周年を機に、名匠・小津安二郎の「東京家族」（1953）にオマージュを捧げ、舞台を現代に移して描く家族の物語。成長した子どもたちに会うため、瀬戸内海の小島から上京した老夫婦と子どもたちの姿を通じ、家族のきずなと喪失、夫婦や親子、老いや死についての問いかけを描く。", "2013-01-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THINGS　I　NEVER　TOLD　YOU", "あなたに言えなかったこと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "スペインの女性監督I・コヘットが、アメリカン・インディーズの個性派俳優と組んで撮った長編デビュー作。アメリカの小さなカメラ店で働くアンが、プラハに転勤した恋人から突然の別れを告げられる。そのショックで自殺未遂を図った彼女は、電話で人生相談をしている青年ドンと急接近していく。恋愛という主題にとどまらず、人生に迷える人々のうつろな心情を、ユーモアを交えて見据えたラブ・ストーリー。ラストのささやかな“奇跡”が観る者の心を温めてくれる。", "1995-01-01", "1,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONFESSIONS D’UN BARJO", "バルジョーでいこう！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "バルジョー（＝馬鹿者）と呼ばれる若者の日常をコミカルに描く。住み家を燃やしたことから姉夫婦の屋敷に転がり込んだ、好奇心旺盛のバルジョー君。世界の終わりを信じる彼は姉の浮気や霊能者の言葉から、それを確信し始める……。原作はP・K・ディックの『戦争が終り、世界の終りが始まった』。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("家／THE　FAMILY", "家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "中国の著名な作家、パー・チンの多分に自伝的色彩の強い同名原作を映画化したもの。1910年代の四川省成都。官僚地主家庭の高家では、かつて成朝の官僚を務めていた祖父が絶対的権力を握り、家庭内に封建秩序を維持していた。だが彼の孫たちは祖父や父たちの腐敗ぶりを目にし、反封建の志を燃やし始める。それぞれに抵抗を試みる彼らだが、長男の覚新だけは、高家の跡取りという重荷から祖父に逆らえないでいた。相思相愛の女との結婚をあきらめ、親の言うままに、別の女と結婚する覚新だが……。日本以上にかつてのハリウッドの影響を受けていた上海映画界の充実ぶりがよくわかる傑作。", "1956-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIGADOON", "ブリガドーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "100年に一度姿を現わすというスコットランドのある村に紛れ込んだ二人の男が、その村の美しい娘に恋をするファンタスティックな物語。ヒースの丘でのダンスナンバー、村祭りの群舞シーンなど、MGMの大セットの中に展開される歌と踊りがしばし現実を忘れさせてくれる。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thunderball", "007／サンダーボール作戦", "", "", "http://ecx.images-amazon.com/images/I/51UCseOLxAL._SL210_.jpg", 131, "シリーズ第4作。第1作と同じくジャマイカが舞台。悪の組織スペクターに盗まれた核弾頭をボンドが奪回するクライマックスの海中での大戦闘シーンは、迫力満点だ。ボンドの仇役には、アドルフ・チェリが扮し好演。トム・ジョーンズの主題歌もヒットした。", "1965-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　高野聖", "シネマ歌舞伎　高野聖", "", "", "http://image.eiga.k-img.com/images/movie/57390/315x210.jpg?4420", 0, "歌舞伎の舞台公演を映画館のスクリーンでデジタル上映する「シネマ歌舞伎」シリーズ第17弾。泉鏡花の同名小説を原作に、石川耕士と坂東玉三郎の脚本・演出で上演された歌舞伎を、「シネマ歌舞伎」用に新たに舞台上で撮影。ロケーション素材なども加えられ、これまでの「シネマ歌舞伎」シリーズとは異なる映画的技法で完成されている。飛騨から信濃へ抜ける山道で道に迷った修行僧の宗朝は、山中に一軒屋を見つける。そこには妖艶な女が暮らしており、女は宗朝を誘惑するが……。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カムイと生きる", "カムイと生きる", "", "", "http://image.eiga.k-img.com/images/movie/57860/315x210.jpg?4195", 116, "1938年、北海道浦河町に生まれ、数少ないアイヌ民族としてその伝統や文化を伝えている浦川治造の人物像に迫り、その生き方を通して自然と人間の共存について考えるドキュメンタリー。アイヌ民族の集落で生まれ、アイヌ民族の伝統的な知恵や技術、自然との共存を重んじる「アイヌプリ（アイヌらしい生き方）」を学んで生きてきた浦川は、生活のため40代で上京した後も、日常生活のなかで「アイヌプリ」を貫いていく。そんな浦川の周囲には、自然と多くの仲間たちが集うようになる。ナレーションは浦川の甥で俳優の宇梶剛士が担当。", "2012-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間魚雷出撃す", "人間魚雷出撃す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "敗戦の色濃い昭和20年７月、瀬戸内海基地から、特攻兵器・回天を積んで出航した伊号五八潜水艦が、グアム＝レイテ戦域で敵戦艦と遭遇。裕次郎と長門裕之の二人が回天に乗り、人間魚雷として特攻する。裕次郎のイメージとはミスマッチ。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Raid", "ザ・レイド（原題）", "", "", "http://image.eiga.k-img.com/images/movie/57840/315x210.jpg?2041", 0, "ジャカルタのスラム街を舞台に、警察の特殊部隊とギャングが繰り広げる戦いを描いたインドネシア発のバイオレンスアクション。", "2012-12-31", "73,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男対男", "男対男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "横浜の増江海運で働く梶は、作業中の事故の多発を不審に思い、かつての戦友、菊森のキャバレーに探りを入れるが手掛かりはつかめない。実は菊森は神戸のボスの命令によって渋々、増江海運の乗っ取りを画策していたのだが……。谷口監督が得意にした、男の友情と対決を交えて描くアクション・ドラマ。加山雄三のデビュー作。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エグザイル・プライド EXILE PRIDE", "エグザイル・プライド EXILE PRIDE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "2010年にグループ結成10年目を迎えた人気ダンス＆ボーカルグループ「EXILE」のリーダー、HIROに密着したドキュメンタリー。日本、香港、台湾、ハワイなどさまざまな場所で、仲間や友人、恩人、ビジネスパートナーたちと交流するHIROの姿をとらえ、「EXLILE」というグループの本質に迫っていく。監督は「CATCH THE RAINBOW」「SMILE 人が人を愛する旅」の牧野耕一。", "2011-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAPARAZZI", "パパラッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "メル・ギブソンの製作によるユニークなサスペンス活劇。道徳も法律も無視するパパラッチ集団に目をつけられた、映画スターの逆襲を描く。セレブを追いかけ、時には事件を捏造するパパラッチの狂騒を見つめたシニカルな視点に注目。アクション映画の主演で一躍スターの座に登りつめたララミー。以来、彼はパパラッチに執拗につけまわされる。やがて家族の身に危険がおよぶにいたって、ララミーは逆襲に転じ……。", "2006-04-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rejected", "リジェクテッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 9, "", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かざあな", "かざあな", "", "", "http://image.eiga.k-img.com/images/movie/56760/315x210.jpg?3662", 94, "長編劇映画2作目「ふゆの獣」（2010）が第11回東京フィルメックスで最優秀作品賞を受賞した内田伸輝監督が、07年に手がけた初の長編劇映画。ナベとミカ、ヨウの3人は学生時代からの親友だったが、ナベは次第にミカへ恋心を募らせていく。ある日、ミカに告白してキスをしたナベは、それ以上の関係を求めるがミカに拒まれてしまう。一方、ナベの気持ちに気づいたヨウはミカに嫉妬し、ナベを誘惑する。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マルサの女２", "マルサの女２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "伊丹十三監督による“マルサの女”第２作。地上げ屋の脱税を追及していた国税局捜査官・板倉亮子は、その裏に巨大なヤクザと宗教法人の力が動いていることを知る。いくらもうけても税金がかからないのをいいことに、ヤクザが宗教法人を隠れミノにしていたのだ。亮子はまず地上げ屋の代表である鬼沢に目をつけ調査を始めるが……。鬼沢に扮する三國連太郎の腹芸が見もので、取調室で最後のタンカをきるシーンもすごみが効いている。対する亮子の頭脳回転と行動力が、物語の展開にドライブをかける。次のカットへとなだれこんでいく躍動感こそ、知性派兼アクション派のエンターテイナー・伊丹監督の持ち味だ。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昴　スバル", "昴　スバル", "", "", "http://image.eiga.k-img.com/images/movie/53090/315x210.jpg?1882", 110, "天才バレリーナの成長を描いた曽田正人の人気コミックを「不夜城」のリー・チーガイ監督が映画化。主演は「クローズZERO」の黒木メイサ、共演に「蒼き狼」のAra、「武士の一分」の桃井かおりほか。ともにバレエダンサーを夢見ていた双子の弟・和馬が病気で命を落とした宮本すばるは、弟のためにも踊り続けることを誓い、バレリーナへの道を歩む。やがてプロになることを決意したすばるは、ライバルたちとともに上海で開かれる国際コンクールに挑戦する。", "2009-03-20", "76,8,54,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Marines Go Home 辺野古・梅香里・矢臼別", "Marines Go Home 辺野古・梅香里・矢臼別", "", "", "http://image.eiga.k-img.com/images/movie/53860/315x210.jpg?4429", 131, "米海兵隊基地闘争のドキュメンタリー。沖縄の半基地運動の最前線・辺野古、米軍射爆場とされた朝鮮半島の梅香里（メヒャンニ）、陸上自衛隊の実弾演習場のある矢臼別を舞台に、大国アメリカの海兵隊と住民との闘争を描く。監督はアメリカ社会の闇を鋭く描く「アメリカばんざい／crazy as usual」の藤本幸久。", "2008-07-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEET　HOME　ALABAMA", "メラニーは行く！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「キューティ・ブロンド」の大ヒットで、新たなラブコメの女王として注目されるリース・ウィザースプーン主演作品。田舎育ちのヒロインを伸び伸びと演じたリースの快活な魅力に注目。仕事で成功を収め、市長の息子との結婚も決まったN.Y.のデザイナー、メラニー。だが、彼女には前夫との離婚が成立していないという問題が残されていた。離婚届に判を押さない夫を説得するため、メラニーは懐かしの故郷アラバマへと戻るが……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もう頬づえはつかない", "もう頬づえはつかない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ベストセラーとなった見延典子の小説（早大文学部文芸科の卒業小説）『もう頬づえはつかない』を、ATGが東陽一監督で映画化した作品。早大文学部に通うまり子は、同じく早大生の橋本と同棲している。それもごく軽い気持ちで……。これにまり子のかつての同棲相手である売れないルポライターや画家くずれの大家らも登場して、物語は大きな起伏もなく淡々と進んでいく。ポスト学園紛争の“シラケ世代”や“フィーリング世代”の感覚をそのまま映画化したかのような作品。古い形の“恋愛”とはかなり遠いところで男たちの間をフラフラ漂っている女子大生を演じた桃井かおりが光る。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誘う女 レイプ", "誘う女 レイプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "鎌田敏夫の『レイプ』を映画化。ファンの偏執的な妄想心理を描いたサスペンス・スリラー。もともと童話作家志望だった弾正雅之は、穴埋めに書かされたバイオレンス小説が大ヒットし、人気作家となっていた。作品の内容は、妻の康子でさえ、雅之の実像と違い過ぎるというほど過激なもの。ある日、雅之の留守中に康子は安倍智子と名乗る女性の電話をとる。女は雅之にレイプされたと言い、康子は智子と会うことに。根拠も証拠もない話に康子は安心するが、智子の嫌がらせはエスカレートしていき、ついに自作自演のシナリオを実行に移す。何も知らない雅之は、誘惑に落ち彼女の計画通りにその体験を小説にしてしまう……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Restless", "永遠の僕たち", "", "", "http://image.eiga.k-img.com/images/movie/55280/315x210.jpg?4758", 90, "「ミルク」「グッド・ウィル・ハンティング　旅立ち」のガス・バン・サント監督が、死にとらわれた若者たちの愛と再生を描いた青春映画。交通事故で両親を亡くし臨死体験をした少年イーノックは、それ以来、自分だけに見える死の世界から来た青年ヒロシを話し相手に生きてきた。そんなある日、イーノックは難病で余命3カ月を宣告された少女アナベルと出会い、ヒロシが見守る中、残された時間を過ごす2人は輝きを取り戻していくが……。主演は故デニス・ホッパーの息子ヘンリー・ホッパー。相手役のアナベルは「アリス・イン・ワンダーランド」のミア・ワシコウスカが務め、主人公のただ1人の友人ヒロシ役で加瀬亮が出演する。", "2011-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOUNG SOUL REBELS", "ヤング・ソウル・レベルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "海賊放送でお気に入りのソウル・ナンバーを流し続けるDJコンビ。彼らの友人だったゲイの黒人青年が殺され、二人はその事件の調査に乗り出すが……。イギリスのブラック＆ゲイ・カルチャーを盛り込んだ、エネルギッシュなサスペンス。パーラメント、ファンカデリックらの音楽が、全編を盛り立てる。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILVER CITY", "愛をゆずった女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "故国ポーランドを離れ、たった一人で異国の地オーストラリアにやって来た若い女性ニーナ。彼女は“シルバーシティ”と呼ばれる移民収容施設で同郷のジュリアンと知り合い恋に落ちる。だが、彼には妻子がおり、ニーナは知らない土地でまた取り残される。監督のターキヴィッチ、主演のドブロボルスカともにポーランドからの移住者。共演のカンツもまたラトビアからの移民組。そんなスタッフ、キャストたちの情熱が圧倒的なリアリティーを生んでいる。", "1984-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHANCES ARE", "ワン・モア・タイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "20年以上も亡夫を愛し続けている未亡人コリンヌ。娘の恋人アレックスが実は亡夫の生まれ変わりだったことにコリンヌが気付いたことから奇妙にもつれた三角関係が始まっていく……。C・シェパードが６年ぶりに映画に復帰したラブ・コメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HARDER THEY COME WITH JIMMY CLIFF", "ハーダー・ゼイ・カム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "農村から歌手を夢見て首都キングストンに出てきた青年アイバンが、親切を装った男に身ぐるみはがされてしまい牧師に救われる。やがて、そこの家の娘と恋仲に。ある日偶然、レコードの録音現場に行くチャンスを得た彼は、この機にぜひ自分のオーディションをしてくれと頼みこみ“ハーダー・ゼイ・カム”をレコーディングすることに成功した。ところが喧嘩が原因で追われる身となり、歌がヒットしたにもかかわらず、自分は犯罪者として警官の銃弾に倒れていく。レゲエのスター、クリフの主演と、全編に流れるパワフルなヒット・ナンバーが話題を呼んだ青春もの。", "1973-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GROSSE POINTE BLANK", "ポイント・ブランク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「コン・エアー」のJ・キューザックが製作、脚本、主演をこなしたアクション・コメディ。一匹狼の殺し屋マーティンが、10年ぶりに高校の同窓会に出席。故郷に置き去りにした恋人デビーと再会し、ケンカの末に和解することに成功する。ところが、追いかけてきたライバルの殺し屋がマーティンを襲撃、デビーも巻き込んで死闘を展開することに……。J・キューザックの姉ジョーンが、主人公の秘書役で笑いを振りまく。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHIP OF FOOLS", "愚か者の船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "限られた場所でさまざまな人々のエピソードを交錯させる“グランド・ホテル形式”の人間ドラマ。当時アカデミー賞８部門にノミネートされた名作だ。1933年、メキシコの海岸からドイツへ向けて客船が出航した。ある男の乗客は、自分も含め愚か者ばかりが乗っていると考えて船名を“愚か者の船”と名付ける。そんな人々の愛と欲望、生と死などを交錯させながら描き出す悲喜劇。「風と共に去りぬ」の大女優V・リーの遺作でもある。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACK CAT", "黒猫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "ユニバーサル・ホラーの二大スター、B・カーロフとB・ルゴシが初共演した怪奇映画。かつてポールジッグという男に欺かれ、妻と娘を奪われたワーデガスト。彼は復讐すべくポールジッグの屋敷に乗り込むが、逆に悪魔を崇拝するポールジッグの黒猫に苦しめられる。ポーの有名な同名小説を大幅に脚色した作品で、B級映画の雄E・G・ウルマーにとって初の単独監督作品となった。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPACE BALLS", "スペースボール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "遥か宇宙の彼方。スペースボール星の陰謀と戦う一匹狼ローン・スターの活躍を描いた「スター・ウォーズ」の大パロディー。“フォース”ならぬ“シュワルツ”、“ダース・ベイダー”ならぬ“ダーク・ヘルメット”と、笑いの連続で楽しめる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Spider-Man 3", "スパイダーマン3", "", "", "http://image.eiga.k-img.com/images/movie/1180/315x210.jpg?2140", 140, "最愛の人MJ（キルステン・ダンスト）と将来を誓い合い、全てが順調のピーター・パーカー（トビー・マグアワイア）は、謎の液状生命体に取り憑かれて黒くなったスパイダーマンのコスチュームに身を包み、今までにないパワーを手にするが……。そして彼の前には、ニュー・ゴブリン、サンドマン、ヴェノムという3人の敵が現れる。人気シリーズ第3弾で、これまでのキャストに加え、トーマス・ヘイデンチャーチ、トファー・グレイス、ブライス・ダラス・ハワードらが参加。", "2007-05-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("面影三四郎", "面影三四郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "姿三四郎の得意技“山嵐”を受け継ぎ、面影三四郎と呼ばれる宇津木。戦後、一時彼はヤクザのボスの手先となるが、柔道は暴力ではないと悟り、更生。最後は、横暴なヤクザに対し、正義の山嵐をふるう。「姿三四郎」の原作者・富田常雄の原案。かつて姿三四郎に扮した藤田進主演と、すべて姿三四郎の現代版。藤田の職業が車引きならぬ、タクシー運転手というのが面白い。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE KINGS", "スリー・キングス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1999年度ボストン映画批評家協会賞で最優秀作品賞、最優秀監督賞をダブル受賞した、常識破りの新感覚戦争アクション・ムービー。湾岸戦争停戦直後に手に入れた一枚の地図には、イラク軍がクウェートから奪った金塊の隠し場所が記されていた。アーチ、トロイ、チーフは、人生をバラ色に変えてくれるに違いない金塊を求めてイラク領地に侵入する。目的の金塊は手に入れたものの、反体制派のイラクの人々を見殺しに出来ない彼らが選んだ道は……。荒野を疾走するロールスロイス、砂漠の地下基地からダイレクトにつながる携帯電話、そして金塊を詰めても破れないヴィトンのバッグと、アイテム使いも秀逸。本作で「マルコヴィッチの穴」の映像監督、S・ジョーンズが役者デビューを果たしている。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHEAPER BY THE DOZEN", "一ダースなら安くなる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "二人の作家による自伝的小説を原作にしたヒューマン・ドラマ。子供たちへの教育について考えさせる一作だ。フランク一家は夫妻と11人の子供たちの大所帯家族。夫の口癖は“一ダースなら安くなる”という能率主義的な考えで、一家を支えていた。ある意味で頑固で保守的だったが、家族は次第にそれを肝にする。やがて夫が急逝し……。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO SKIN OFF MY ASS", "ノー・スキン・オフ・マイ・アス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "カナダの鬼才B・ラ・ブルースが、ロバート・アルトマンの「雨にぬれた舗道」を、スキンヘッドの少年とゲイのヘア・デザイナーのハードコア・ラブ・ストーリーとしてリメイクした異色作。粒子の荒れた映像、不安定な手持ちのカメラ、文脈を無視したストーリー展開と、アンダーグラウンド感覚にあふれたハードコア・ゲイ・パンク・ムービーだ。", "1990-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Estomago", "イブクロ　美食物語", "", "", "http://image.eiga.k-img.com/images/movie/54790/315x210.jpg?2855", 113, "天才的な料理の才能を持つ男の数奇な人生を描き、2007年リオ・デ・ジャネイロ映画祭で作品賞など4部門に輝いたブラジル発のブラック・コメディ。トランクひとつで大都会にたどり着いた冴えない中年男のノナト。空腹に堪えきれなくなった彼はとある食堂で無銭飲食を試みるがあえなく失敗。そのままその食堂で働くことになり、料理人としての才能を開花させていく。主演を務めるのは「スエリーの青空」のジョアン・ミゲル。", "2009-09-18", "17,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("残菊物語", "残菊物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "歌舞伎界の御曹子・菊之助が、芸の未熟さを子守り女にいさめられ、旅回りの一座で修行を重ねて一人前になるまでを描いた芸道もの。当時、一連の“銭形平次”もので人気絶頂の長谷川一夫が菊之助を演じる。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dirty Dozen", "特攻大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "　1944年、ヨーロッパ大陸侵攻直前のこと。米軍のライスマン少佐は、死刑、または無期懲役を宣告された凶悪な囚人たちによる部隊を編成し、敵軍のかく乱を行なうという特別作戦の指揮を命ぜられる。選ばれた12人はいずれも一筋縄ではいかない荒くれ者ばかり。そんな囚人部隊も、ライスマンの厳しい特訓のもと、次第に結束していく。そしてついに出撃の時が……。ロバート・アルドリッチ監督の代表作として知られる豪快な戦争アクション。リー・マービン以下、個性的なキャストが集結。", "1967-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIGIL", "ビジル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "森に囲まれた谷間、両親と祖父のもとで暮らす少女トス。しかし、狩りに出た父親の突然の死で彼女の世界は一変してしまう。父の遺体をかついで現れた男イーサンは、やがて一家の仕事を手伝うようになるが、そんな彼に母親とトスは次第に惹かれていく……。ニュージーランドの気鋭V・ウォードが、繊細な感性を発揮した初監督作。", "1984-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃える南十字星", "燃える南十字星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "羽田空港で中国人女性リンヤンを助けた謎の男・三木丈太郎は、彼女の父パイミンの用心棒となる。パイミンは日本で武器を密造して海外へ売りつける死の商人なのだ。彼は丈太郎に契約解除を申し入れた工場主の暗殺を命じたが……。“エースのジョー”こと宍戸の早撃ちが見事に決まった日活アクションの快作。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fair Love", "Fair Love", "", "", "http://image.eiga.k-img.com/images/movie/56940/315x210.jpg?1549", 117, "それぞれ孤独を抱えた年の離れた男女の純愛を描くラブストーリー。かつて友人に金を持ち逃げされて以来、仕事部屋でひとり静かに暮らしていた50歳過ぎのカメラの修理屋ヒョンマン。ある日、金を持ち逃げした友人が「ひとり娘を頼む」と言い残して他界してしまう。ヒョンマンは友人の残された娘ナムンに会うが、天涯孤独となったナムンもまた殻に閉じこもり、他人とのかかわりを持たずに生きていた。それまで恋などしたことのなかったヒョンマンだったが、次第にナムンにひかれていき……。韓国映画の名作や最新作を一挙上映する「K-MOVIEセレクション2011」にて上映。", "2011-10-08", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I LIVE IN GROVENOR SQUARE", "大空に散る恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ロンドンの公爵邸を宿舎に指定された米軍の軍曹パターソンは、やがて公爵の孫娘パトリシアと恋に落ちる。だが彼女には幼なじみの婚約者がいた。生活環境の違いも痛感した彼は、思いを断ち切るべく前線に志願して大空に散っていく……。第二次世界大戦を舞台にしたラブ・ロマンス。", "1945-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIRLFRIENDS", "ガールフレンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ドキュメンタリー界出身の女性監督ウェイルが、資金難に悩みながら３年の歳月をかけて完成させた作品。カンヌ映画祭や各国の映画祭で注目を集めた後、ようやく一般公開された。女性にとっての夢、友情、結婚生活などを、みずみずしいタッチで描いた、さわやかな青春映画の佳作。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUTHERN COMFORT", "ロバート・イーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "トランスセクシャル(性同一性障害を持ち、生まれた体の性別に強い違和感を持つ人)であった人間の姿を追い、サンダンス映画祭審査員大賞を受賞するなど、各映画祭で高い評価を得たドキュメンタリー。女性として二人の子供をもうけながらも男として生きる道を選んだロバート・イーズ。監督のK・デイビスは彼の温かい人柄や歩んできた人生に魅了され、カメラを回し始めるが、唯一、彼の体に残された“女性”である子宮が末期ガンに冒されていた……。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jinxed!", "ジンクス！　あいつのツキをぶっとばせ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　カジノ・ディーラーのウィリーはギャンブラーのハロルドにいつもカモられていた。そんなウィリーにハロルドの恋人ボニータが接近。ふたりは共謀してハロルドを殺し、高額の保険金を手に入れようとするが……。名匠ドン・シーゲル監督の遺作となった劇場未公開のコメディ。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Michael Jackson Commemorated", "マイケル・ジャクソン　キング・オブ・ポップの素顔", "", "", "http://image.eiga.k-img.com/images/movie/55480/315x210.jpg?3816", 0, "故マイケル・ジャクソンさんの命日である2009年6月25日からちょうど1年、世界に先駆け日本で公開されるドキュメンタリー。元マネージャーのマーク・シャフェルが、およそ10年間にわたって撮りためたジャクソンさんのプライベート・フィルムを編集。ジャクソンさんの生まれ育った故郷への旅やネバーランドの深層部など、限られた人間しか見ることのできなかった映像を通し、ジャクソンさんの素顔に迫る。", "2010-06-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子どもたちへ いのちと愛のメッセージ", "子どもたちへ いのちと愛のメッセージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "小学校５年生の比呂子は、学校からの帰り道に同級生の男の子たちがエロ雑誌に夢中になっている姿を偶然見てしまった。初潮も始まり、ちょうど母も妊娠中の彼女は、“性”に対し嫌悪感を抱いていく。“大人は不潔だ”“クラスの男の子たちはいやらしい”そんな気持ちが彼女の中で成長していく……。正しい性知識を子供たちに伝えるべく企画された性教育映画。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Secret", "シークレット", "", "", "http://image.eiga.k-img.com/images/movie/55470/315x210.jpg?3004", 110, "「セブンデイズ」の脚本家ユン・ジェグが初監督に挑んだサスペンス・アクション。悪名高い暴力団のナンバー2が刃物で惨殺される事件が発生する。駆けつけた刑事ソンヨルは、現場で妻ジヨンの口紅やイヤリングなどを発見し、とっさに証拠を隠ぺいしてしまう。帰宅したソンヨルはジヨンに犯行を問い詰めるが、ジヨンはあいまいな返答を繰り返すばかり。一方、被害者の兄で組織のボス、ジャッカルは、自ら犯人探しを始める。", "2010-08-21", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL NORTE", "エル・ノルテ 約束の地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "エル・ノルテとは北のこと。中米から見るとそれは、憧れの地アメリカを意味する。グアテマラの貧しい山村で暮らすエンリオとローサの兄弟は両親が殺されたことをきっかけに、エル・ノルテへの脱出を計画する。密入国の案内人を探し困難な旅を続ける二人の姿がドラマティックに描かれる。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EVENING STAR", "夕べの星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "アカデミー賞５部門に輝いた名作「愛と追憶の日々」から13年ぶりに製作された続編。テキサス州ヒューストンに暮らすオーロラは、死んだ娘エマが遺した３人の孫を育てることだけに全力を注いできた。しかし孫たちは問題児ばかりで、自分の人生に疑問を感じ始めたオーロラはハンサムな精神カウンセラーの元に通うことにする……。S・マクレーン扮するヒロイン、オーロラの晩年の生き様を、周囲の人々との葛藤や友情を通して描出。前作に引き続き、名優J・ニコルソンが元宇宙飛行士役で助演している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAR MY SONG", "ヒア・マイ・ソング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "若きプロモーター、ミッキーは幻のテナー歌手ジョゼフ・ロックのリサイタルを企画。ところが舞台に登場したのはニセ者で、信用ばかりか恋人までも失ってしまう。本物のロックを捜し求め、彼はアイルランドへとあてどない旅に出る……。失われた愛と夢を取り戻そうとする男たちの心温まるファンタジー。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL RATTO DELLE SABINE", "サビーヌの掠奪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "都市国家時代のローマを舞台に、若き王の恋と活躍を描いたスペクタクル史劇大作。軍神の子ロムルスは、女に飢えた兵士たちを治めるため、小国サビーヌを襲うが……。女たちのたくましさが楽しい。また「美女と野獣」のパロディーのようなJ・マレーの役にも注目。", "1960-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH SPIRITS", "プランケット城への招待状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "N・ジョーダンが、故国アイルランドを舞台に撮った異色ファンタジー。主演のP・オトゥールが、コミカルな味つけを作品に与えている。多額の借金のカタに売り飛ばされようとしている古城プランケット城。なんとか維持しようと“ゴースト体験ツアー”を企画するが……。「スーパーマン」のデレク・メディングズが特殊視覚効果を担当。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　鉄火のマキちゃんと金のかまめしどん", "それいけ！アンパンマン　鉄火のマキちゃんと金のかまめしどん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "やなせたかし原作の人気アニメーション劇場版第14作「それいけ！アンパンマン　ロールとローラ うきぐも城のひみつ」と同時上映の短編。", "2002-07-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIEN A｀ FAIRE", "ありふれた愛のおはなし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "大人の男女の許されぬ恋を描いたラブ・ストーリー。失業中のピエールと、同じく職を探している主婦のマリー。スーパーマーケットと度々顔を合わせる二人は、境遇が似ていたため親しくなる。彼らは互いにパートナーがいながら惹かれて合っていき……。演技派ヴァレリア・ブルーニ・テデスキが揺れ動く女心を繊細に表現している。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Until Death", "ディテクティヴ", "", "", "http://image.eiga.k-img.com/images/movie/34210/315x210.jpg?3653", 106, "アクション俳優ジャン＝クロード・バン・ダムが、ダーティな刑事というアンチ・ヒーロー役に挑むアクション・ドラマ。ドラッグに溺れる潜入麻薬捜査官のストウは、大物マフィアになったかつての同僚、キャラハンを追っていた。しかし捜査は失敗、キャラハンは逃走し、銃撃戦で女性捜査官が命を落としてしまう。刑事としての自信を失いかけ、家庭にも問題を抱えるストウは、キャラハンの追跡に執念を燃やすが……。", "2007-09-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fan", "ザ・ファン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "大リーグの強打者としてスターに君臨するボビー・レイバーンが移籍した時から、一人の男、ギルの狂気が始まる。彼は、ボビーに近づくためボビーの息子を誘拐するのだった。熱狂的なファンの憧れが憎しみに変わり、スターにとって最も恐ろしい存在となる男の狂気をデ・ニーロが熱演している。メジャーリーグで活躍した選手が登場し、本場の迫力を見せてくれる大型サスペンス・アクション。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMEO IS BLEEDING", "蜘蛛女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "マフィアに情報を売り、甘い汁を吸ってきた中年の刑事ジャックは、妻を愛しながらも若いウェイトレスとの情事に溺れる自堕落な男。ところが彼は、組織の乗っ取りをたくらむ大胆不敵な女殺し屋モナに色と金で誘惑され、自滅への道をひた走る……。きわめてタフな現代の“宿命の女”が描かれた犯罪ドラマ。モナが自分の腕を切り落として罪を逃れようとするプロットなど、異色の女流脚本家ヘンキンの才気が全編にみなぎる快作。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KUFFS", "カフス！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "お気楽で、軽くて、脳天気、そのくせ憎めない主人公のカフス。人生において一度も責任感というものを持ったことがないようなこの男が、兄の死を境に民間警察の署長になる決意をし、犯人逮捕に全力を注ぐ。リズミカルなストーリー展開と、ヒネリの効いたユーモア・センスが光る痛快青春ドラマ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TERROR TRAIN", "テラートレイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "大学生活最後の大晦日をSLの中で楽しむ医学生たち。しかし一方では血まみれの惨劇が進行していた。列車という密室内での惨劇をサスペンスフルに描いた恐怖映画の名作。“悲鳴の女王”と呼ばれたJ・リー・カーティス主演。職人スポティスウッド監督の記念すべき第１回の作品。", "1980-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水戸黄門 天下の大騒動", "水戸黄門 天下の大騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "白鷹三十万石の新領主となった将軍家の若君・菊太郎の名君ぶりをひと目見んとやってきた黄門様一行は旅籠につき、そこにニセの黄門様一行が泊まっていると知りびっくり。だが彼らは翌日死体で発見される。藩の実権を握ろうとする家老・瀬戸山大典によって刺客がさしむけられたのだった。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("竜二Forever", "竜二Forever", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "たった一本の主演映画「竜二」を残してこの世を去った、金子正次の映画に賭けた晩年を描く人間ドラマ。アングラ劇団の活動に限界を感じた正次は、仲間とともに映画製作を決意。資金難や友情の破綻、結婚生活の危機など、多くの問題に直面しながらも、彼は主演映画の製作に全力を傾ける。しかし一方で、彼の体は着実に病魔に冒され……。命を削って映画を完成させた主人公の熱意の並々ならぬ熱さが印象に残る。自主映画製作の現場のリアルな描写も興味深い。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真一文字　拳", "真一文字　拳", "", "", "http://image.eiga.k-img.com/images/movie/54550/315x210.jpg?3980", 53, "中国河南省のとある山で、暗黒雷鬼拳の使い手・バイから逃げていたイーロンは、師匠から「お前の本名は真一文字拳。故郷の日本に帰り、風牙神拳の極意を手に入れろ」と遺言を受け、単身日本へと向かう。拳は男子校へ入学し、風牙神拳の真髄を取得するためあるクラブに入部するのだが……。Neo Actionシリーズの第2弾で、「バベル」「母べえ」の鈴木信二が映画初主演を果たす。", "2009-05-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KILLING FIELDS", "キリング・フィールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "1970年代にカンボジアで巻き起こった“赤いクメール”内乱を背景に、アメリカ人ジャーナリストとカンボジア人ガイドとの友情を感動的に描いた作品。N.Y.タイムズ記者として当時の混乱をルポし、ピュリッツァー賞を受けたシドニー・ジャンバーグの実体験を、R・ジョフィ監督が映像化。生死を分かつ切迫した状況を背景に、人間としての誇りを失わず、固い友情で結ばれた二人の絆を、高らかに謳い上げている。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Charlotte Gray", "シャーロット・グレイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "第二次世界大戦中、占領下のフランスを舞台に、激動の時代に身を投じた一人の女性の姿を描くサスペンスフルな人間ドラマ。スコットランド人の看護婦、シャーロットは、恋人で英国空軍パイロットの恋人ピーターから、フランスに渡りレジスタンスの仕事をしないかと誘われる。先にフランスへと向かったピーターを追って、シャーロットも旅立ち、そこでレジスタンスの任務に没頭していく。やがて、シャーロットが身を隠していた村にもナチスの影が忍びよっていた。恋人との別れ、同胞の死、ナチスによって連行される人々の後ろ姿……。様々な人との出会いによって変わっていくヒロインの人生が、戦争の愚かさと人間の尊さを浮かび上がらせる。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARSENE　LUPIN", "ルパン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "モーリス・ルブランが生み出した怪盗紳士アルセーヌ・ルパンの若き日の冒険を、本国フランス映画界が巨費を投じて映画化。少年期の暗い記憶を引きずったまま大人になったルパンが、従妹である美女クラリスとの恋、謎めいた悪女カリオストロ伯爵夫人との駆け引き、莫大な財宝をめぐる抗争などを経験していく姿を、豪華絢爛なセットや衣装を配して活写する。ルブランの『カリオストロ伯爵夫人』を下敷きに、『奇厳城』などのエピソードを散りばめた映像世界はまさにスペクタクルの連続だ。19世紀後半、盗人の子として生まれたアルセーヌ。やがて盗みの才を発揮し始めた彼は、幼なじみのクラリスと恋仲に。そんなアルセーヌの前に美しいカリオストロ伯爵夫人が現れ……。", "2005-09-17", "2,3,9,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("六本木バナナ・ボーイズ", "六本木バナナ・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "トオル＆宏次朗の“ビー・バップ・ハイスクール”コンビ主演の青春ストーリー。六本木生まれの幼なじみ、涼太郎と哲男の二人が、あるフィリピン女性を救おうとしたことからヤクザのイザコザに巻き込まれるという内容。「バカヤロー！２ 幸せになりたい」で１エピソードを担当した成田裕介が、本格的監督デビューをした作品だ。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AMANT DE POCHE", "ポケットの愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "15歳のジュリアンは、魅力的な年上の女性エレナに恋をする。しかしエレナは高級娼婦。彼女は、自分を心から愛してくれるジュリアンに救いを見出そうとするのだが……。少年のひたむきな愛を描いた、フランスならではの叙情派ラブ・ストーリー。", "1978-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽気な渡り鳥", "陽気な渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "子供時代の美空ひばりを主演とした軽快な人情劇。唄のうまい孤児のみどりは、養父母に邪魔にされていたため、町へ来た奇術師に優しくされたのが忘れられず一座について巡業にまわることになる……。美空ひばりの歌をふんだんにとり入れた大船調の温かい映画。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラフ ROUGH", "ラフ ROUGH", "", "", "http://image.eiga.k-img.com/images/movie/33470/315x210.jpg?3803", 106, "人気漫画家あだち充が、高飛び込みと競泳に打ち込む高校生たちの姿を描き、コミックの売り上げ部数が累計1500万部を突破した青春ラブストーリー「ラフ」を、「NANA」の大谷健太郎監督が映画化。脚本を担当するのは映画「電車男」も手がけた金子ありさ。「タッチ」に引き続き、2度目のあだち作品のヒロインを演じる長澤まさみと、TVドラマ「ごくせん」で大ブレイクした速水もこみちがダブル主演を務める。", "2006-08-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魁!!クロマティ高校 THE★MOVIE", "魁!!クロマティ高校 THE★MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "『週刊少年マガジン』で連載中の人気ギャグ漫画を、初監督作「地獄甲子園」で話題を集めた山口雄大監督が実写映画化。悪の巣窟と化した高校の立て直しを図る主人公の奮闘を、ナンセンス&シュールに描く。独特のヤンキー風スタイルで人気のバンド、氣志團が歌う主題歌も要チェックだ。都立クロマティ高校は、悪の巣窟と化していた。この春、1年3組に入学したばかりの神山高志は、学校の立て直しを誓うが……。", "2005-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SUBSTITUTE", "野獣教師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "プロの傭兵として一流の腕を持つシェイルは、教師をしている恋人のジェインが襲われて負傷したことをきっかけに、代理教師としてマイアミのハイスクールの教壇に立つ。だが、そこは悪徳校長と不良グループが手を組んで麻薬取引で金をもうけている悪の巣だった。シェイルは彼らの陰謀を打ち砕く闘いの中で、生徒たちと心を通わせ、傭兵としての仕事からは得られなかった何かを学んでいく。「メジャーリーグ」「山猫は眠らない」などで知られる、T・ベレンジャー主演のアクション・ドラマ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAMN YANKEES", "くたばれ！ヤンキース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "名作「パジャマ・ゲーム」を生み出したアボット＆ドーネンのコンビが再び組んだミュージカル。プロ野球狂の老人は快進撃を続けるヤンキースが気にくわない。TVを見ながら“くたばれ！ヤンキース”と叫んだとたん悪魔が現れる。老人はひいきの球団セネタースを優勝させるために魂を売り渡すが……。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Daan", "ぼくは歩いてゆく", "", "", "http://image.eiga.k-img.com/images/movie/1840/315x210.jpg?3291", 90, "9歳の少年ファルハードは、麻薬中毒である両親が出生届を出さなかったために戸籍も身分証も持っていない。そのため学校に行くこともできず、正規に働くこともできない。それでも家計を助けるため、板金工場で働いている。ある日ファルハードは警察に連れて行かれ、呼び出された母親は「なぜ子供の戸籍を取らないのか、なぜ働かせるのか」と係官に叱りつけられる。両親は息子のため、なけなしの金を用意して戸籍を取ろうとする。", "2000-04-29", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONDO SHOCK", "SM大陸 マンダラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "修行僧が自らの肉体に針を突き刺すシーンをはじめ、鳥葬でハゲタカが死体をついばむ風景、性転換手術の場面など、アジア各地に伝わる風俗習慣を捉えたドキュメンタリー映画。製作は西ドイツのスタッフが手掛けており、ドイツ人のアジア観を知るうえでもユニークな一編。", "1981-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("目を閉じて抱いて", "目を閉じて抱いて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "人気漫画家・内田春菊の同名ベストセラー・コミックを映画化。一組のカップルと、二人を魅惑する両性具有者の奇妙な三角関係を描く。平凡なサラリーマン・周は、恋人で同僚の樹里から結婚を迫られ少々憂うつ気味。そんなある日、周は自分の起こした事故でニューハーフの花房と出会う。彼女が両性具有者と知りながらも、樹里の存在を忘れて、花房の持つ妖しい魅力に惹かれていく周。花房から周を取り戻そうとする樹里だが、彼女もまた自分の傷を癒してくれる花房の独特な優しさに心を奪われ、関係を持ってしまうのだった。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彩愛 saiai", "彩愛 saiai", "", "", "http://image.eiga.k-img.com/images/movie/57670/315x210.jpg?4505", 109, "グラビアアイドルとして活躍する井上うららが体当たりの濡れ場にも挑戦した純愛ストーリー。予備校講師として働く彩花はミュージシャン志望の拓真と同棲しているが、遅くまで働いて得た稼ぎも拓真のもとに消えていってしまう。挙句の果てに拓真の浮気相手・あずさから拓真を自由にしろと迫られ、思い悩む日々を送っていた。そんなある日、予備校の合宿先で生徒の立花さくらの具合が悪くなり、迎えにきたさくらの父親・浩介と出会った彩花は、浩介にひかれていくが……。", "2012-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("監督失格", "監督失格", "", "", "http://image.eiga.k-img.com/images/movie/56260/315x210.jpg?3913", 111, "200本以上の作品に出演しながらも、2005年に34歳で急逝した女優・林由美香を題材としたドキュメンタリー。林の元恋人でドキュメンタリー「由美香」（1997）も手がけた平野勝之監督が、林とかかわった約15年間にわたる記録と、林の死後に新たに撮影した映像で構成した。「ヱヴァンゲリヲン新劇場版」の庵野秀明が実写映画初プロデュース。", "2011-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROM　DUSK　TILL　DAWN　２：TEXAS　BLOOD　MONEY", "フロム・ダスク・ティル・ドーン2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "吸血鬼と人間の戦いが繰り広げられるヒット作の続編。銀行強盗一味がモーテルに結集。だが首謀者の脱獄囚ルーサーは、吸血鬼の巣窟である酒場に立ち寄ったばかりにヴァンパイアと化していた。彼の不審な行動に疑問を覚えた仲間の一人バックは、やがてルーサー率いる凶暴な吸血鬼軍団と戦うハメに……。「死霊のはらわた２」の脚本家S・スピーゲルがコミック風のアクションを演出。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セイリング 海にはばたく", "セイリング 海にはばたく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "北海道第一水産高校に赴任した一人の教師、門田。海の男を目指していた熱血漢である彼が奮闘する姿を描いていく。監督は「やがて…春」「ブリキの勲章」など独自の視点から教育問題をとり上げる中山節夫。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("楳図かずお恐怖劇場　蟲たちの家", "楳図かずお恐怖劇場　蟲たちの家", "", "", "http://ecx.images-amazon.com/images/I/51195J83ZVL._SL210_.jpg", 60, "楳図かずお原作による短編コミック6作品を映像化したオムニバス映画「楳図かずお恐怖劇場」の1編として、「回路」の黒沢清監督がメガホンを取ったホラー作品。自分に好意を寄せている後輩・羽奈子に、「妻が人間でなくなった」と相談を持ちかけた蓮司。それが自分の妄想かどうか確かめてもらうため、羽奈子を自宅へと案内する蓮司だったが……。出演は「帰郷」の西島秀俊、「SF　サムライ・フィクション」の緒川たまきほか。", "2005-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BANDERA", "地の果てを行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "外人部隊を舞台に、落ち延びていった犯罪者と、彼を執拗に追い詰める賞金稼ぎの密偵の姿を描いたJ・デュヴィヴィエ監督のミステリー。パリで殺人を犯し、スペインへと逃げたピエールは、追い詰められ、外人部隊に入隊するが、５万フランの賞金が目当ての密偵が彼のあとを追っていた……。ラストは、密偵自身がピエールの戦死を報告するというデュヴィヴィエ監督らしいペシミズムが全編を覆っている。主演のJ・ギャバン自身が原作の映画化権を買い取り、デュヴィヴィエに映画化を勧めたといわれており、ギャバンはこの映画で役どころを確立。この二年後には「望郷」で大評判を取ることになる。", "1935-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK GUNN", "スーパー・ガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "黒人過激派グループと犯罪シンジケートの激しい抗争。それに巻き込まれてしまった男が、殺された兄の復讐のために敢然と立ち上がる。血で血を洗う凄惨な闘いとカーチェイス、J・ブラウンがロスを駆けめぐるバイオレンス・アクション。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MODE IN FRANCE", "モード・イン・フランス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ファッション写真家として高名なW・クラインが監督した最初の長編映画。フランスのファッション界をテーマに、13の独立したシークエンスによって構成され、ファッション・ショーの歴史を概観したり、関係者のインタビューやショーの様子などが収められている。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE−LOVELY", "五線譜のラブレター／DE-LOVELY", "", "", "http://ecx.images-amazon.com/images/I/51sdWs8tlGL._SL210_.jpg", 126, "ロック・ミュージシャンにも信奉者の多いアメリカの大作曲家コール・ポーターの生涯を映画化。数々の名作ミュージカルを手掛け、黄金期のハリウッドでも活躍した偉人だが、その人生はあまりにも波乱万丈だった……。主演のK・クラインの吹き替えなしの歌声にも注目だが、エルヴィス・コステロやロビー・ウィリアムス、アラニス・モリセットら現役ミュージシャンも多数登場し、ミュージカルシーンを華やかに盛り上げる。ポーターを生涯支え続けた妻リンダとのラブ・ストーリーとしても秀逸。同性愛の趣味を持つ作曲家コール・ポーターは、社交界の人気者リンダと結婚。お互いを束縛しないと誓い合った二人だが、ポーターがハリウッドで大成功を収めたことから亀裂が生じる。", "2004-12-11", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("精装追女仔／THE ROMANCING STAR", "コメディー　男たちのバッカ野郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "名作「男たちの挽歌」を見事にパロッたコメディ。自動車修理工場で働くおかしな４人組はペナンへ旅行に行き、金持ちらしき美女二人を見つける。４人はさっそくフレッドを金持ちのボッチャンに仕立て、二人の美女マギーとアグネスに接近することに成功。彼女らは実は貧しいエアロビクスの教師。フレッドとマギーは金持ちを装いながらフォール・イン・ラブ。香港に戻ってからも二人はデートを重ねるが……。チョウ・ユンファとM・チャンのホットなコンビの香港大ヒット作。DVDタイトルは「チョウ・ユンファ　男たちのバッカ野郎」。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが心の銀河鉄道 宮沢賢治物語", "わが心の銀河鉄道 宮沢賢治物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "詩人で童話作家の宮沢賢治の生涯を生誕100年を記念し映画化。宗教や農業、自然や芸術と幅広い活動に打ち込みながら37年という短さで幕を閉じてしまった生涯を、彼の童話から喚起される映像を織り込んで、ファンタジックに描く。明治29年、岩手県・花巻で質屋と古着商を営む裕福な家庭の跡取り息子として生まれた宮沢賢治は、盛岡高等農学校を卒業する頃から、父・政次郎と宗教観をめぐり対立するようになる。また同じ頃、保阪嘉内という文学仲間ができ同人誌『アザリア』を発行して、創作を始めていた。数年後、父との信仰の対立から家出をした賢治は、上京して宗教団体・国柱会に入信、布教活動のかたわら童話創作に力を注いでいた。しかしその夏、最愛の妹・トシが結核で倒れてしまうのだった。東映が配給した本作に対して、同時期、松竹が「宮澤賢治　その愛」を配給して話題となった。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE AND DEATH", "ウディ・アレンの愛と死", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "W・アレンの監督・主演による野心的なコメディ。19世紀のロシアで死刑を宣告された男が、これまでのばかばかしい生き方を回想する……という物語。『戦争と平和』をはじめ、古今のロシア文学をパロディ化して随所に盛り込んであり、アレン一流の奇妙でペシミスティックな世界が展開する。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BER IN WAR！", "ミート・マーケット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "酒場で“オンナ”を捕まえるべく、４人の男が涙ぐましい努力を続ける様を描くコメディ。ナンパすることしか頭にないヤサ男チャールズ、仕事づけの毎日を送るパトリック、女好きのジェリー、女性に縁のない気弱なジョージ、これら主役の４人をカナダの怪優、M・コテが特殊メイクを駆使して一人で演じ分ける。ギャグとお色気満載の映画。", "1989-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("元気の神様 Is that you？", "元気の神様 Is that you？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "夫を３年前に亡くしてから息子カイや姑、小姑と暮らし、美容院を切り盛りしている真弓は最近、カイが独り言をつぶやいたりして落ち着かないのが気がかり。実は彼だけには幽霊となった父・雄二の姿が見えるのだ。死に神の使者の追跡をかわしながら雄二はカイを励まし、真弓を温かく見守っているが、彼にはこの世にやり残したことがあった……。故・松田優作の夫人、松田美由紀が明るく生きるシングルマザーを演じた心温まるファンタジー。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AMOUR BRAQUE", "狂気の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "異能派ズラウスキーが描くラブ・サスペンス。ギャングの情婦と刑務所帰りの二人の男。この３人を主人公に、復讐、抗争、そして愛のもつれといった複雑な人間模様が展開していく。ヒロイン、S・マルソーの美しいヌードシーンも見もの。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER", "殺人！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ヒッチコックのイギリス時代の作品。ロンドンの劇団の花形女優が殺され、現場には同じ劇団の若い女優が火かき棒を手にぼう然として立っていた。彼女は逮捕され、起訴された。有罪は確定的だったが、陪審員の一人でアマチュア探偵を気取るサー・ジョンだけは疑問に思い、独自に調べてみると……。", "1930-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("X-Men: First Class", "X-MEN：ファースト・ジェネレーション", "", "", "http://image.eiga.k-img.com/images/movie/56050/315x210.jpg?2220", 131, "人気アクションシリーズ「X-MEN」のプリクエル（前章）。テレパシー能力をもつチャールズと、あらゆる金属を操ることができるエリックは、同じミュータントとして親友になる。しかし2人は、CIAから依頼された、邪悪なミュータント軍団が仕組んだ人類滅亡計画を阻止する戦いに参加したことから、次第に友情が崩壊していく。後のプロフェッサーXことチャールズ役に「つぐない」のジェームズ・マカボイ、マグニートーことエリック役に「イングロリアス・バスターズ」のマイケル・ファスベンダー。監督は「キック・アス」のマシュー・ボーン。", "2011-06-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SANTA SANGRE", "サンタ・サングレ 聖なる血", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "A・ホドロフスキー監督が、９年ぶりに発表した鮮血と衝撃の物語。サーカス団で育った少年が、両親の惨劇を目のあたりにして精神を病み、やがて狂気の母親が命ずるまま、殺人を犯していく。美しいまでの地獄絵が流麗に展開する神秘的作品。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The African Queen", "アフリカの女王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　ドイツ領東アフリカ。イギリス人宣教師ローズの兄がドイツ兵による略奪行為の際に命を落とす。妹ローズは飲んだくれの男チャーリーが操縦するオンボロ蒸気船“アフリカの女王”号に乗り込み、現地を脱出。川を下るチャーリーとローズは沿岸のドイツ砲艦を手製の魚雷で撃沈しようとたくらむ。しかしその途中、銃撃や急流といった数多の難関が待ち受けていた！　身分違いの男女が繰り広げる冒険とロマンスを描いた痛快娯楽作。ボギーとキャサリン・ヘップバーンの掛け合いが見もの。", "1951-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャロル", "キャロル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ロック・グループ、キャロルを捉えたセミ・ドキュメンタリー。のちに龍村仁と小野耕世の“NHK馘首事件”を誘発したTVドキュメンタリーへと発展させた作品。ハングリーなバンドが富と名声を手にした時、一人のメンバーが失踪する。彼の行方はヨウとして知れず、パリ公演の日時が迫ってくる……。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAINNED", "私のダイナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "J・クロフォード、C・ゲーブルの美女美男のコンビで描いた恋の物語。船会社の社長リチャードは放蕩三昧の妻に嫌気が差し、有能な秘書ダイアンとの結婚を望む。しかし、妻が離婚に応じないため彼はしばらくダイアンを南米旅行に出してやった。しかし、ダイアンはそこでアルゼンチンの牧場主マイクと出会い、彼の熱烈な恋に心動かされる。帰国してリチャードにすべてを告げようとするダイアンだったが、彼が妻との離婚を整え子供までも犠牲にしたことを知り、リチャードと結婚することに……。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAUN　DI　ATAS　BANTAL", "枕の上の葉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "露天商をしている中年女性アシーを母親のように慕いながら、ジョグジャカルタの街の片隅で路上生活をしている３人の孤児。生きるためには残飯あさりや盗みも辞さない彼らだったが、やるせない悲劇的運命をたどっていく。インドネシアの俊英G・ヌグロホが、貧困や親の不在といった社会問題を背景に撮り上げたヒューマン・ドラマ。同国を代表する国際派女優C・ハキムが、事件の目撃者となるアシーを演じている。第11回東京国際映画祭で審査員特別賞を受賞。", "1998-01-01", "73", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮本武蔵・巖流島の決斗", "宮本武蔵・巖流島の決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "宮本武蔵と佐々木小次郎の有名な対決をクライマックスに据えた“宮本武蔵”シリーズ完結編。武蔵は、九州の細川家の指南役に推挙され、同じく推挙されている佐々木小次郎と、巖流島において対決することになる。５部作を突っ走った武蔵役の中村錦之助が見せ場をつくる。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ICE", "ICE 氷の女豹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ポルノスターから一般作に転向したT・ローズ主演の女性アクション。ハイテクを駆使した宝石強盗エレンとチャーリー夫婦。マフィアのボスからダイヤモンドを盗み出して逃げる途中に、チャーリーが殺される。さらに弟までが誘拐されたエレンは復讐のための戦いを開始する。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Manderlay", "マンダレイ", "", "", "http://image.eiga.k-img.com/images/movie/1520/315x210.jpg?4366", 139, "ラース・フォン・トリアー監督の「ドッグヴィル」に続く“アメリカ3部作”の第2作。1933年、ドッグヴィルを後にしたグレースは父親とともにアメリカ深南部の大農園マンダレイを訪れるが、そこには廃止されたはずの奴隷制度が残っていた。グレースは奴隷たちを解放しなくてはならないという使命感に駆られて行動に出るだが……。他にウド・キア、ジャン＝マルク・バール、クロエ・セビニー、ジェレミー・デイビスらが共演。", "2006-03-11", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КАМЕВ", "ストーン —クリミアの亡霊—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "A・ソクーロフ監督の長編６作目。前作「セカンド・サークル」と、次作「静かなる一頁」の間に位置し、“生と死”をめぐる壮大なテーマの３部作を成している。生い茂る木立に囲まれたチェーホフの館。夜、誰もいないはずの館に人影が……。番人の青年が中を覗いてみると、死んだはずのかつての主チェーホフの姿が！ 死から帰還した文豪チェーホフが時空を超えて若い男と出会い、次第に感覚と欲望をよみがえらせていく過程をモノクロームの映像と神秘的な音響がシュールに描き出す。", "1992-01-01", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("警察故事III／超級警察／THE POLICE　STORYIII　SUPER　COP", "ポリス・ストーリー３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ジャッキー自身が一番好きだと言う人気シリーズの第３作。敵はアジア全土を牛耳る闇の帝王チャイバ。潜入捜査を命じられた、ジャッキー扮するチェン刑事が文字通り決死の闘いを挑んでいく。見どころは、美しき相棒ヤンに扮した新顔M・キングの体を張ったアクション。激走するトラックからの落下などなど、ジャッキーもまっ青の熱演だ。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レンタネコ", "レンタネコ", "", "", "http://image.eiga.k-img.com/images/movie/57710/315x210.jpg?1435", 110, "「かもめ食堂」「めがね」の荻上直子監督が、寂しい人たちにネコを貸し出して回る「レンタネコ」業を営む不思議な女性サヨコと、「レンタネコ」を通じて出会う人々が繰り広げる物語を描く。幼い頃からネコに好かれているサヨコは、「ネコを貸すことにふさわしい条件が揃っているか」を独自に審査した上で、心寂しい人たちとネコを引き合わせてる。夫と愛猫に先立たれた老婦人や単身赴任中の中年男、自分の存在意義に悩むレンタカー屋の受付嬢、とある組織から追われる男など、サヨコはさまざまな人にネコを貸し出していく。主演は「めがね」の市川実日子。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANGEROUS", "青春の抗議", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "B・デイヴィスが妖しい魅力を放つ女を演じたメロドラマ。かつてはブロードウェイのスターであったが、今は落ちぶれて安酒場で酔いつぶれる女優ジョイス。新進建築家ドンはそんな彼女に同情するが、いつしか彼女の虜になり、良家の令嬢ゲイルとの婚約も解消してしまう。そしてドンはジョイスのために大金を出して興行を後援することになり、ジョイスも再起をかけて稽古に取り掛かる。ドンに惹かれ、彼との結婚を望むようになったジョイスは別居している夫に正式に離婚を申し込むが、夫はそれを聞き入れず、彼女はある決心をすることに……。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀色のシーズン", "銀色のシーズン", "", "", "http://image.eiga.k-img.com/images/movie/53250/315x210.jpg?3603", 108, "「海猿」シリーズの羽住英一郎監督と「ただ、君を愛してる」の脚本家・坂東賢治がタッグを組み、雪山を舞台に3人の青年の成長を描いた青春ドラマ。寂れたスキー場で何でも屋を営む銀、祐治、次郎の3人組。彼らはスキーの個人レッスンや駅への送迎などをする一方で、賭けスキーに参加したり当たり屋になったりとやりたい放題の毎日を送っていた。そんな彼らの元に、雪山での結婚式を3日後に控えながらスキーができないという花嫁・七海が現われる。", "2008-01-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAPPY BIRTHDAY TO ME", "誕生日はもう来ない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "名門私立高校のエリート・グループの一人、ヴァージニアの周囲で失踪事件が相次いで起こった。４年前から時々幻覚をおこす彼女は、18歳の誕生日に、恐ろしい光景を目撃する。TVドラマ『大草原の小さな家』のM・S・アンダーソンの劇場用映画初主演作。", "1981-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INDIRA", "インディラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "インドのトップ女優スハーシニが監督デビューを果たした社会派の人間ドラマ。偏見と差別に立ち向かう女性の姿を力強く描き出す。貧しい村に生まれ育った聡明な女性インディラ。結婚も間近に迫ったある日、彼女はカースト制度による村の対立に巻き込まれ、最愛の父親を奪われる。悲しみと怒りを胸に、インディラは平和を取り戻すために立ち上がった。", "1995-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("手紙", "手紙", "", "", "http://image.eiga.k-img.com/images/movie/33720/315x210.jpg?4708", 121, "直木賞作家・東野圭吾によるロングセラー小説を映画化。工場で働く20歳の青年・直貴には、刑務所に服役中の兄がいる。弟の学費を手に入れるため強盗に入った家で、誤って人を殺してしまったのだ。そんな兄のせいで人生が狂わされ、夢さえも諦めてしまう直貴。そして愛する女性との幸せまでもが脅かされた時、直貴はある決断を下す……。キャストには山田孝之、玉山鉄二、沢尻エリカら人気若手俳優が顔を揃える。", "2006-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CIBLE E´MOUVANTE", "めぐり逢ったが運のつき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "初老のクールな殺し屋ビクトルと、彼に見込まれて殺しの美学を教え込まれる若者アントワーヌ。新たな標的である女詐欺師ルネに接近した彼らは暗殺の失敗を繰り返すうちに、彼女に魅了されてしまう。フランスを代表するスターの二世二人と、J・ロシュフォールの共演で描いたエスプリの効いた軽妙なコメディ。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎無頼控 魔剣地獄", "眠狂四郎無頼控 魔剣地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1956～58年に東宝で鶴田浩二の眠狂四郎シリーズが３本作られたが、これはその３作目。将軍の娘・高姫を操る薬種問屋“相模屋”は、老中が隠匿した五万両を狙うとともに、ニセ狂四郎に辻斬りをさせ江戸を恐怖に陥れていた。短命に終わったシリーズだが配役は豪華である。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hellboy", "ヘルボーイ", "", "", "http://image.eiga.k-img.com/images/movie/1080/315x210.jpg?2341", 132, "「ミミック」「ブレイド2」のギレルモ・デル・トロ監督が惚れ込んだコミック「ヘルボーイ」を映画化。ナチスによって地獄から召喚された異形ヘルボーイが、水棲人間や人体自然発火能力を持つ美女と協力し、ロシアの怪僧ラスプーチンの野望を阻止するために活躍。コミック原作者マイク・ミニョーラを共同製作総指揮とビジュアル・コンサルタントに巻き込んだ映画化に原作ファンも狂喜して全米で大ヒット、続編の製作も発表された。", "2004-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Winter Soldier 冬の兵士　良心の告発", "Winter Soldier 冬の兵士　良心の告発", "", "", "http://image.eiga.k-img.com/images/movie/54500/315x210.jpg?2487", 81, "2008年3月に米・ワシントンD.C.郊外で行われた“Winter Soldier”（冬の兵士）と呼ばれる集会に密着したドキュメンタリー。米大手マスコミが報道しなかったこの集会では、イラク戦争に従軍したおよそ50人の帰還兵が、イラクで行われた暴行や殺人を証言し、それらが組織的に強要されていたと告発した。退役軍人としての恩恵を受けられなくなることを覚悟し、勇気を持って真実を証言する若者兵士たちの姿に迫る。", "2009-03-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　CALLING", "ディアブロ・悪魔生誕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "悪魔に魅入られた我が子を守ろうとする母親の闘いを描いた終末的なホラー。米国人女性クリスティはTVキャスターのマークと結婚して男児を出産。それから９年後の2000年、その子ディランのなかに宿っていた悪魔が目を覚まそうとしていた。この世に黙示録をもたらそうとする悪魔からディランを守るため、クリスティは孤独な戦いに挑むが……。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無常", "無常", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "吉田喜重の「水で書かれた物語」で近親相姦を扱った石堂淑朗の脚本をもとに、実相寺昭雄が日本的無常観とエロス的無常観も描いた作品。実相寺にとっては、これが長編第１回作品となる。琵琶湖近くの旧家、日野家の長男・正夫は父親の意に反して、家を継ぐ気はなく、仏像の魅力にとりつかれていた。ある日、正夫は姉の百合と関係を持つ。しばらくして百合は子を宿すが、正夫は姉を書生と結婚させる。正夫は京都の仏師に弟子入りし、そこでも仏師の妻と関係を持つ。実家に帰った正夫は、再び百合と抱き合うが、それを知った書生は自殺する。京都の街並みを捉えた移動撮影が素晴らしい。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rio", "ブルー　初めての空へ", "", "", "http://ecx.images-amazon.com/images/I/51TyMJWAHRL._SL210_.jpg", 0, "米ミネソタで甘やかされて育ったインコのブルーがブラジルのリオ・デ・ジャネイロに帰郷し大冒険を繰り広げる姿を描いたアニメーション。監督は「アイス・エイジ」「ロボッツ」のカルロス・サルダーニャ。声の出演は、主人公ブルー役に「ソーシャル・ネットワーク」のジェシー・アイゼンバーグほか、アン・ハサウェイ、ジェイミー・フォックス、ウィル・アイ・アム、トレイシー・モーガンら豪華スターが集結。", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　LENGUA　DE　LAS　MARIPOSAS", "蝶の舌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "８歳の少年と老教師の心の交流を描いたハートフルな感動ドラマ。1936年、スペイン・ガルシア地方にある小さな村。８歳の少年モンチョは、喘息持ちのためにみんなよりも遅れて学校に入学する。だが、一見強面に見えるが心優しいグレゴリオ先生のおかげで、楽しい毎日を送るようになる。なかでもモンチョが心を奪われたのは、森で行う自然観察の授業だった。一方、ファシズムが台頭していたスペインは、いよいよ内戦に突入しようとしており……。グレゴリオ先生に扮するのは、スペインの名優F・F・ゴメス。子供たちへの愛情にあふれた眼差しや、現実の苛酷さに苦悩する表情が深い感動を誘う。内戦の哀しみが迫る中、蝶が舞い、花々が咲き乱れる鮮やかな自然の美しさも印象的。", "1999-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rebecca", "レベッカ", "", "", "http://ecx.images-amazon.com/images/I/51Md49-An7L._SL210_.jpg", 130, "　ヒッチコックが「風と共に去りぬ」で知られる名プロデューサー、デビッド・O・セルズニックに招かれ製作した、アメリカ進出初作品。滞在先でマキシムと運命的な出会いをしたヒロインは、彼の大邸宅があるイギリス・マンダレーへやってくる。しかしそこには、マキシムの亡き前妻レベッカの影が色濃く残っていた。これまでの監督作とは異なりユーモアが少なく、反面イギリス映画のスタイルが引き継がれた女性向きの映画である。アカデミー作品賞受賞。", "1951-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROTOCOL", "ゴールディ・ホーンの　アメリカ万才", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "場末のクラブで働いていた女の子が、偶然中東某国のプリンスを救ったのが発端で、あれよあれよという間に国務省の特別外交官に。すったもんだのあげく、最後には上院議員にまでのし上がる、というスラップスティック・コメディ。G・ホーンが大熱演。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛流通センター", "愛流通センター", "", "", "http://image.eiga.k-img.com/images/movie/53760/315x210.jpg?2256", 83, "女子高生チカコの携帯電話に奇妙なメールが入る。送信元は\"愛流通センター\"。間違えて登録してしまったチカコは、流通センターの力を借りて元彼ケンタとの復縁に成功するが、親友ハルカとの三角関係に発展してしまい……。携帯小説投稿サイトから生まれた小説を映画化したSF青春ラブコメディ。恋と友情の間で揺れ動くヒロイン役に07年ホリプロスカウトキャラバンでグランプリに輝いた足立梨花、その恋人役に「ひぐらしのなく頃に」の前田公輝。", "2008-07-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLASS OF 1999", "クラス・オブ・1999 処刑教室2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1999年、学園は戦場と化している。暴力が支配するキャンパスを舞台に、パンク・キッズ連合軍とアンドロイド教師軍団の死闘が始まる！ 1984年の学園バイオレンス映画「処刑教室」の近未来版で、同じM・L・レスターが監督。過激なアクションに加えSFXもふんだんに楽しめるバトルムービー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A PERFECT WORLD", "パーフェクト・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "俳優にして、アカデミー賞受賞監督同士でもあるC・イーストウッドとK・コスナーが、初めて顔を合わせた作品。両親の愛を知らない囚人ブッチ・ヘインズは、ある日、刑務所を脱走。少年を人質にして、逃避行を続けていく。そのうちに彼は、少年が彼と同じ環境に育ったことを知り……。コスナーが暗い過去を背負う孤独な男ブッチを、イーストウッドが、そのブッチを追い詰める警察署長ガーネットを演じる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jam Films ２", "Jam Films ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "７人の実力派映画監督によるショートフィルムを一挙に公開し、話題を呼んだ“Jam Films”のシリーズ第２作。今回は、CMやミュージックビデオなどで活躍する新進気鋭の映像クリエイター４人が監督を務めた。音楽と映像のコラボレーションを軸に、それぞれの作品の世界観とテーマソングが密接に繋がりつつ、ストーリー性も持つ新感覚のエンターテインメントだ。", "2004-03-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君にラヴソングを", "君にラヴソングを", "", "", "http://ecx.images-amazon.com/images/I/41rWve%2B7cnL._SL210_.jpg", 0, "韓国の6人組男性ダンスボーカルグループ「超新星」が本人役で出演し、メンバーのソンジェが映画初主演を果たした純愛ストーリー。レコーディングのために日本へやって来た超新星。メンバーの1人であるソンジェは、視力を失った少女サオリと出会う。超新星の大ファンだというサオリに、自分がメンバーであることを言いだせないソンジェ。やがて2人は互いに惹かれあっていくが……。ヒロイン役に「赤い糸」の南沢奈央。", "2010-05-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD　MOVIE", "バッドムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "ストリートキッズの日常を生々しく描き、韓国で論議を呼んだ異色の青春映画。少年たちはつるんでケンカや盗み、シンナーに明け暮れ、少女たちは刹那の喜びを求めて夜の街をさまよう……。劇中のティーンは、いずれも本物の路上生活者。彼らの本音を見据えつつ、フィクションともドキュメンタリーともつかぬストーリーを展開させる。", "1997-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラ×メカゴジラ", "ゴジラ×メカゴジラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1999年、対特殊生物自衛隊＝特生自衛隊は上陸したゴジラにメーサー砲で応戦するが、完敗。そんな中、家城茜三尉は自分のミスで上官・葉山を死なせ、弁明もせず資料課への異動を甘受する。一方、政府は対ゴジラ用兵器の開発に着手。各界の頭脳を集め、1954年に死亡したゴジラの骨をベースに生体ロボットの製造に取り掛かった。そして2003年、生体ロボット、３式機龍が完成。特生自衛隊の中に機龍隊が結成される。その中には３年間黙々と身体を鍛え上げてきた家城茜の姿もあった……。３式機龍＝メカゴジラとゴジラの戦いを、特撮を駆使して迫力たっぷりに描く。メカゴジラのオペレーターである寡黙なヒロインを演じた釈由美子のりりしい姿も好印象。", "2002-12-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Saw V", "ソウ5", "", "", "http://image.eiga.k-img.com/images/movie/54010/315x210.jpg?3195", 93, "ソリッド・シチュエーション・スリラー「ソウ」シリーズの第5弾。殺人ゲームの仕掛け人ジグソウが死んだ今も残虐なサバイバルゲームが続き、FBI捜査官のストラムは、死のゲームから生還したホフマン刑事をジグソウの後継者ではないかと疑う。そして再び、コンクリートの密室で5人の男女が目覚め……。出演は歴代ジグソウ役のトビン・ベルほか。監督はシリーズ第2弾からプロダクション・デザインなどを務めてきたデビッド・ハックル。", "2008-11-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIREPOWER", "リベンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ニューヨークとカリブ海の島を舞台に、ダーティ・ヒーローの賞金稼ぎが麻薬組織に挑むアクション。コバーンが２役を演じて、スマートに活躍。車とヘリの激突、ダイナマイトを使った爆発シーンなど、派手な見せ場がふんだんに盛り込まれている。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cleaner", "ザ・クリーナー　消された殺人", "", "", "http://image.eiga.k-img.com/images/movie/54290/315x210.jpg?4565", 90, "「スター・ウォーズ」シリーズなどの実力派俳優サミュエル・L・ジャクソン主演によるサスペンス・ドラマ。殺人現場の清掃請負業を営む元刑事トムのもとに、ある邸宅の清掃依頼が舞い込む。トムは邸宅に残された殺人の痕跡を完璧に除去するが、後日その依頼主が架空の人物であることが判明。やがてトムは、事件の裏に警察汚職事件を巡る組織的な陰謀が潜んでいることを知り……。共演にはエド・ハリス、エバ・メンデスら豪華キャストが揃う。", "2009-02-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白痴", "白痴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 166, "黒澤明自身が敬愛するドストエフスキーの名作「白痴」を映画化した美しくも激しい愛憎劇。純真無垢な美しい亀田青年、彼を愛するふたりの女・綾子と妙子、そして妙子を野獣のように愛する無骨な男赤間。４人の間には、神々しいまでの愛と激しい憎悪が燃えあがる。原作に忠実であろうとするあまり、当初前後編4時間25分の長編として完成したが、難色を示した松竹側と黒澤監督が対立。切るならフィルムを縦に切ってしまえと、激怒した逸話は有名。結局現在観ることのできるのは大幅にカットされた166分バージョンのみである。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CRIME ET SES PLAISIRS", "危険がいっぱい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「太陽がいっぱい」の監督・主演コンビによる、ライト感覚のサスペンス。ギャングのボスの妻と一夜をともにしたことから、命を狙われるハメになった、いかさまカード師、マーク。ニューヨークからニースに逃れた彼は、ある未亡人の元に身をひそめるが、モテモテぶりが災いして、またまた雲行きが怪しくなっていく。", "1964-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢野久作の少女地獄", "夢野久作の少女地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "夢野久作の幻想小説を原作に、小沼勝監督が官能的に描いたロマンポルノ。大人の醜さを知るアイ子と、無知な歌枝。対照的な二人の女子学生はやがて同性愛の関係になるが、校長が歌枝を犯して妊娠させたことから二人の運命は狂い、大人たちへの復讐が始まる。飛鳥裕子のデビュー作。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MACHINE−GUN　KELLY", "機関銃〈マシンガン〉ケリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "後にアクション俳優として名を馳せるC・ブロンソンの初主演作。アメリカ犯罪史上に名高い強盗犯“機関銃ケリー”の半生を描く。仲間とともに銀行を襲撃しては、大金を手にするケリー。しかし、死に対して異常なほどの恐怖心を抱く彼は、ある日、棺を目撃したことで怖気づき、計画を失敗させてしまう。銀行襲撃の警戒が強まったことで、やがて彼は誘拐に鞍替えするが……。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAKING NED", "ウェイクアップ！ ネッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "人口52人というアイルランドの小さな田舎町を舞台に展開するコメディ。高額の宝クジを当てた老人ネッドが、喜びのあまり急死。当選金を国にとられまいと、故人の友人コンビはネコババを画策。当選者の調査に訪れた役人をだまそうと、彼らは村人たちを巻きこんで大芝居を打つが、ことは思うように運ばず……。金に目がくらんだ人々の姿を描きつつも、シニカルさは控え目。奮闘する登場人物の姿を茶目っ気たっぷりに捉えた絶妙のキャラクター描写に、のどかな空気が相まって、嫌味なく楽しめる娯楽作となっている。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIFFERENT FOR GIRLS", "ディファレント・フォー・ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "性転換した男性とその同級生が恋に落ち、様々な問題に揺れ動く異色のラブ・ストーリー。男であることに違和感を感じていたカールは高校卒業後、キムと名前を変え、女性として生きていた。そんな彼が高校時代の親友ポールと再会。二人の友情はいつしか愛情へと変わっていくが、ポールは複雑な状況に葛藤を抱えていた……。第20回モントリオール国際映画祭でグランプリを受賞。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I LOVE YOU", "I LOVE YOU", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "口笛を吹くと“I LOVE YOU”と返事をする女の顔の形のキーホルダーに恋をしてしまい、次第に外界と断絶し始める男。ある日、交通事故を起こした彼は口笛が吹けなくなってしまう……。イタリアの鬼才M・フェレーリが描く悲しく歪んだ愛の物語。", "1986-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Narrow Margin", "カナディアン・エクスプレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "　殺人事件の現場を目撃した女性キャロルはロッキーの山荘に身を隠すことに。事件の背後で暗躍する組織を追うコールフィールド検事は法廷での証言を要請するためにキャロルを訪ねるが、その時、武装ヘリが山荘を襲撃。そこから脱出したコールフィールドとキャロル、そして追っ手はカナダ鉄道の特急に乗り込む。疾走する列車内で命を狙われるふたりの運命は？　リチャード・フライシャー監督の日本未公開作「その女を殺せ」をリメイクしたサスペンス・スリラー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED SHOE DIARY", "続・蘭の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "前作では一組の男女が真の愛の歓びを見いだすまでのプロセスを描いたキングだが、今回は一転して、二人の男の愛のはざまで苦しみ追い詰められていく女と、残された男たちの哀しみを叙情的に綴る。オーバー・ラップを多用した美しいセックス・シーンをはじめ、ムーディーな映像で描き出されたエロティック・ラブ・ストーリー。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL THE RIGHT MOVES", "トム・クルーズ 栄光の彼方に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "T・クルーズのファンには、見逃せない未公開作品。不景気が蔓延するペンシルバニアの鉄鋼の町。そこに育ったステフは、なんとかここを脱出し、新しい未来を築くことを夢見ていた。幸運にもフットボールの才に恵まれた彼は、それを足がかりに念願の大学進学を果たそうとするが……。挫折し絶望するステフを力づける恋人リサ役で、L・トンプソンが共演。それまでの世渡り上手な新人類とは一味違う硬派の若者像を、T・クルーズがエネルギッシュに好演した青春映画。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Master", "ザ・マスター（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("そろばんずく", "そろばんずく", "", "", "http://ecx.images-amazon.com/images/I/21H2QJA184L._SL210_.jpg", 109, "広告代理店を舞台に、クライアント獲得に狂騒する広告マンの悲哀をエネルギッシュに描いた森田監督の異色コメディ。主役の広告マン春日野八千男、時津風わたるに扮するのはとんねるずの2人。彼らのライバル会社のエリート社員・桜宮天神に小林薫。この３人の間で揺れる女として安田成美が絡む。独特の言い回しや間の持たせ方などに森田監督らしい実験的な手法が見られる。なかでも小林薫が変人ぶりを見せて怪演。", "1986-08-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SEA HAWK", "シー・ホーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "M・カーティス監督による海洋アクション。イギリスの利益を守るため、女王陛下公認の海賊が大暴れする。フリンは快活なヒーローに扮して、1930～50年代までハリウッドで人気を集めた剣戟スター。海賊はフリン最高のはまり役だった。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARRYING THE MAFIA II", "家門の危機", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "ヤクザ一家の長男の嫁探しがドタバタ騒動へと発展していくコメディ。地方都市に拠点を構えるヤクザ白虎組の女ボス、ドクチャは、長男インジェの結婚問題が悩みの種。彼女はヤクザ一家の家門に恥じないエリートの嫁を迎えることに必死だ。そんな中、インジェが亡き恋人にそっくりな女性ジンジョンに恋をする。ところが、ジンジョンはヤクザと敵対する立場の女検事であることが分かり……。", "2005-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Movie Is a Movie", "映画は映画だ", "", "", "http://image.eiga.k-img.com/images/movie/54280/315x210.jpg?3890", 113, "人気韓流スター、ソ・ジソブとカン・ジファンがダブル主演を務めるアクション・ドラマ。高慢で暴力的な映画俳優スタは、新作アクション映画の撮影現場でファイトシーンの相手役を殴り大怪我させてしまう。新たな相手役が見つからず困ったスタは、かつて映画俳優を目指していたというヤクザ、ガンペに出演を依頼するが……。鬼才キム・ギドク製作・脚本の下、これまでギドク作品の助監督を務めてきたチャン・フンが初メガホンを取る。", "2009-03-14", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犯罪作戦No.1", "犯罪作戦No.1", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "大映の“忍びの者”シリーズで知られる高岩肇のオリジナル脚本を映画化。誰にも尻尾をつかませない暗黒街の黒幕に、私怨を抱く４人の男のサスペンスフルな暗殺計画を描く。罠に堕ちて警察をクビになった元刑事、父を殺された射撃の名手、女を奪われたプロボクサーなど、復讐者たちの設定が面白い。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オン・ザ・ロード", "オン・ザ・ロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1972年以来、ほぼ80本ものピンク映画を撮り続けてきた和泉監督の、一般映画第１作。交通係に勤務する白バイ警官と、酔っぱらい運転の車を追跡中の彼に誤ってはねられてしまった女性とのラブ・ストーリー。スピーディーな展開が魅力の一編。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOPKAPI", "トプカピ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "イスタンブールのトプカピ宮殿の宝物を狙う女盗賊は、発明家、軽業師ら、その道の専門家を集めて計画を実行するのだが……。ストーリー展開の軽妙さと、本作でオスカーを獲得したP・ユスチノフのユーモラスな演技が見ものの娯楽大作。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MAN ALONE", "白昼の対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "二枚目俳優として活躍したミランドが初めて監督した西部劇。無法者ウエスは、町に向かう途中で駅馬車強盗の現場に出くわす。町人から嫌疑をかけられた彼は、身の潔白を証明するため、真犯人を捜し出す……。軽いノリで楽しめるB級ウエスタン。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Re:Play-Girls リプレイガールズ", "Re:Play-Girls リプレイガールズ", "", "", "http://image.eiga.k-img.com/images/movie/55690/315x210.jpg?1866", 94, "女子高生のミチは、親友からいじめを受けたショックで自殺サイトに書き込みを始める。そのサイトで人生の“リプレイ”を望んだミチを含む12名の女子高生は、脱出不能な孤島に連れてこられ、強制的な自殺ゲームに参加させられてしまう……。主演の外岡えりかをはじめ、佐武宇綺、佐藤さくら、小泉麻耶、小林香菜ら人気アイドルが顔を揃える。Yuki Saito監督は本作で長編デビューを飾る。", "2010-09-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANDUHAI TSETSEN KHATAN", "マンドハイ　第１部・蒼き狼の群れ　第２部・大草原の覇者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 174, "15世紀中頃、群雄割拠に乱れるモンゴルを統一に導いた女傑マンドハイの激動の生涯を描く歴史スペクタクル超大作。3000頭の騎士が広大な草原の中を入り乱れる合戦シーンが実に圧巻だ。マンドハイ役のN・ソブドは、一度も代役を使うことなしに、これらのシーンをこなしたという。", "1988-01-01", "57", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tout-Rien", "トゥ・リアン", "", "", "http://image.eiga.k-img.com/images/movie/56520/315x210.jpg?3853", 11, "人間と自然をテーマとした作品を手がけ、アカデミー短編アニメーション賞を2度受賞したカナダ在住のアニメーション作家フレデリック・バックの傑作短編4本をまとめ、ニュープリント、新字幕で公開する「フレデリック・バックの世界」にて上映。生きものたちを次々と作り出した創造主が最後に人間を作るが、しかし、人間は欲望を膨れ上がらせ、殺りくを繰り返していく……。第53回米アカデミー短編アニメーション賞にノミネートされた。", "2011-07-02", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風流活殺剣", "風流活殺剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "浪人者の扇谷十兵衛と菅新吾の二人は、選抜試合の結果、仕官の口にありついたが、新吾は自慢のプロペラ髭を切るように命令されたため、結局二人とも仕官をとりやめることにした。しかし、江戸の町を荒らしまわる強盗一味を壊滅させたことで、十兵衛と新吾のもとに各藩から召しかかえの使者が殺到する。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バラ色の二人", "バラ色の二人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "橋幸夫の同名曲を主題歌にした青春歌謡ドラマ。自動車会社の若き設計技師・次郎は下宿暮らしだ。下宿先の娘・弓子は次郎を慕っているが、彼の会社の宣伝部専属カメラマン・大村に見込まれて新社のイメージ・モデルに。弓子はモデル修行の末、見違える様に洗練されていき……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一番美しく", "一番美しく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「姿三四郎」に続く黒澤明監督第2作目。第二次大戦の最中の1944年に製作された、時代背景色の濃い作品。学徒動員により勤労奉仕に従事する女子挺身隊の姿を、ひとりひとりに焦点を当ててドキュメンタリー・タッチで描いた人間ドラマ。主演の矢口陽子は本作の翌年黒澤監督と結婚した。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("綴方教室", "綴方教室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "正子の一家は東京の町外れのバラック街に住んでいる。六年生の正子は綴方がうまく、先生も熱心に指導していたが、ある時正子の綴方が『赤い鳥』という雑誌に掲載された。そこに書かれていたのは父が仕事を貰っている頭梁の家のこと。ところがあまり好意的な内容ではなかったので、父は失業しかけてしまう。おまけに師走が近づいた頃、自転車が盗まれてしまい、とうとう父は仕事に行かれなくなる。苦しい生活の中で正子は、綴方を続けていく……。原作は当時無名の少女・豊田正子の作文集である。山本嘉次郎の演出はイタリアン・ネオ・レアリスモの先取りを思わせるほど、貧しい庶民の日常生活をきめ細かく活写している。当時、“天才子役”と讚えられた高峰秀子の演技が素晴らしい。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海女のリャンさん", "海女のリャンさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "戦前、済州島から日本に渡り、現在は大阪で一人で暮らす88歳の海女、リャンさんの生活を3年間に渡り追いかけたドキュメント。帰国船で北朝鮮に子供たちを送り出す39年前の姿を記録した映像を交え、日本各地の海で働く様子や離散した子供たちとの再会を捉えた、歴史的にも貴重な一作。戦前に済州島から日本に渡った一人暮らしのリャンさん。53年ぶりに故郷や北朝鮮を訪問し、離散した子供たちとの再会を果たす。", "2005-05-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAID TO ORDER", "星に願いを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ビバリーヒルズに住む富豪の一人娘は、世間知らずなわがままな女の子。それを天から見ていた魔法使いが、彼女を一文無しにしてしまう。今まで何不自由なく育ってきた彼女は、ある屋敷のメイドとして働きだすが……。わがままな女の子が大人の女に成長していく様がメルヘン・タッチで明るく描かれている。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PEUT−E＾TRE", "パリの確率", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「猫が行方不明」「家族の気分」など、日常風景を軽妙なタッチで描くC・クラピッシュによる異色SFコメディ。新世紀前夜の大晦日。恋人から子供が欲しいと言われ動揺した24歳のアルチュールは、一人トイレに逃げ込んで頭を抱えていた。ところが、天井に穴を発見し、彼は2070年のパリに迷い込んでしまう。そこで彼は年老いた自分の息子アコと出会い、未来の家族のために子供を作ってくれと懇願されるのだが……。文明が退化した未来とキッチュな色合いの現在が織り成す不思議なムードの中、自分の未来を知ってしまった青年の戸惑いを軽やかに描く。名優J・P・ベルモンドが主人公の息子役でコミカルな演技を披露。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("父と暮せば", "父と暮せば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「TOMORROW／明日」「美しい夏キリシマ」に続く、“戦争レクイエム３部作”の完結編としてうたわれた黒木和雄監督の感動作。井上ひさしの同名戯曲の映画化で、原爆投下から３年後の広島を舞台に、亡くなった父親と生き残った娘の心情を綴る。父娘を演じる原田芳雄、宮沢りえの熱演に加え、戦争の現実を伝えるセリフの一つ一つが胸に突き刺さる。原爆投下から３年後の広島。父を目の前で失った美津江は、自分一人だけが生き残ったことに負い目を感じている。そんな彼女の前に父の幽霊が現れる。恋をすることも止めてしまった美津江に、彼は素直になるよう説得する。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESERTO ROSSO", "赤い砂漠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "高度成長時代のイタリアの殺伐とした工業都市。ジュリアーナは工場技師の夫と息子の３人暮らし。自動車事故で入院して以来、夫との関係は疎遠になり、精神が不安定でノイローゼ状態にある。夫の出張に端を発して、彼女は不倫に走るのだが……。「情事」「太陽はひとりぼっち」など現代人の心の空洞とコミュニケーションの不在を描き続けて“愛の不毛”の作家といわれるアントニオーニ監督、初のカラー作品。無機的な工場地帯の灰色の空間を薄緑色のオーバーコートを着たヴィッティが一人歩く。工場の煙突からは黄味を帯びた煙がなびいている。神経を逆なでするような木造の赤い室内。ここでは、色が主人公の不安感、疎外感、異常な緊張感を雄弁に語る。フスコの音楽が高なるなか、海にとり残されたジュリアーナが途方に暮れて泳ぎ回る幻想シーンが印象的。", "1964-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Awesome: I Fuckin' Shot That!", "ビースティ・ボーイズ　撮られっぱなし天国", "", "", "http://image.eiga.k-img.com/images/movie/33440/315x210.jpg?1404", 89, "04年10月9日ニューヨークで行われたビースティ・ボーイズのライブを、カメラを渡された50人のファンがステージ正面からアリーナ最後列までそれぞれの場所から撮影したコンサート・フィルム。メンバーの1人であるナサニエル・ホーンブロウワーことMCAが監督を務め、編集に1年を費やした。ステージ上のパフォーマンス以外に、観客として来場した俳優のベン・スティラーの激写映像なども収録。", "2006-07-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lion King", "ライオン・キング　ディズニーデジタル3D", "", "", "http://image.eiga.k-img.com/images/movie/56670/315x210.jpg?4656", 89, "ディズニー・アニメーション史上で最大のヒット作となり、第67回米アカデミー賞で主題歌賞、作曲賞も受賞した名作アニメ「ライオン・キング」（1994）を3D化。アフリカのサバンナに広がる動物の王国プライドランドの王ムファサの子として生まれたジンバは、王位を狙うスカーの反乱により父を失い、王国を追放される。月日は流れ、たくましく成長したジンバは、暴君スカーにより変わり果てた王国を救うため立ち上がる。", "2011-10-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEA OF LOVE", "シー・オブ・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "全裸男性連続殺人事件が発生したニューヨーク。事件を担当するのは、同僚刑事のもとに走った妻と離婚して、孤独な毎日を送る中年刑事フランク。おとり捜査で容疑者は女性に絞られる。そのなかの一人、ヘレンに惹かれるフランク。彼女はシロかクロか？　容疑者と恋に落ちた刑事を描くサスペンス・ロマン。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("照和 My Little Town KAI BAND", "照和 My Little Town KAI BAND", "", "", "http://image.eiga.k-img.com/images/movie/55790/315x210.jpg?1910", 0, "日本の音楽シーンに影響を及ぼした数々のアーティストを輩出した博多のライブハウス「照和」。その象徴ともいえるアーティスト「KAI BAND」が、自らの原点である「照和」のステージで3日間にわたり敢行したライブの模様と舞台裏に追ったドキュメンタリー。「海援隊」の千葉和臣や「ザ・ロッカーズ」の陣内孝則ら、「照和」出身アーティストへのインタビューも収録。俳優の大森南朋がナレーションを務める。", "2010-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MALADIE D’AMOUR", "恋の病い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ボルドー行きのコンパートメントの中で出会ったジュリエットとクレマン。その時は視線を交わすだけの出会いだったが、二人は意外な場所で再会する。ジュリエットの同棲相手の高名な医師ラウルのもとで働いていたのがクレマンだったのである。そして、３人の抜きさしならぬ恋の病いが始まった。アニエス・bやシャネルを身にまとったナスターシャの魅力が全編にあふれる。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hard Candy", "ハードキャンディ", "", "", "http://image.eiga.k-img.com/images/movie/1420/315x210.jpg?2507", 103, "05年のサンダンス映画祭で斬新なアイデアと過激な描写が話題を呼んだサスペンス・スリラー。出会い系サイトで出逢った14歳の少女、ヘイリーと32歳のカメラマン、ジェフ。3週間のチャットを経てジェフはヘイリーを自宅へ誘い込むが、いつの間にかヘイリーが作り上げた罠にかかっていた……。監督はCFやPVで活躍し、本作が長編デビューとなるイギリス出身のデビッド・スレイド。主演ヘイリーには「X-MEN／ファイナル・ディシジョン」の新星エレン・ペイジ。", "2006-08-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RICH GIRL", "リッチガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "裕福な父親に育てられ、束縛されて生きてきたお嬢様のコートニーは、意を決して家を出て自活を開始する。ウェイトレスをしていたロック・クラブでロック歌手のリックと恋に落ちた彼女は、バンドの歌手として特訓を受け、新しい人生を踏み出すのだった。「ダーティ・ダンシング」のロック・バージョンともいえる青春ラブ・ストーリー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TOXIC AVENGER PART ２", "悪魔の毒々モンスター 東京へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "L・カウフマン監督の“悪魔の毒々モンスター”シリーズ第２作。“毒々…”の活躍で、トロマヴィルはキレイさっぱり。でも、“失業”した彼はノイローゼ寸前。通いつけの精神科医から、東京の父親に会いに行けば、とアドバイスされて、一路東京へ。浅草寺ほかの東京ロケと、日米の異色俳優の顔合わせが楽しいグチャグチャ・ホラー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("のぞき屋 NO ZO KI YA", "のぞき屋 NO ZO KI YA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "『ヤングサンデー』連載の人気コミックを映画化。探偵社よろしく、のぞきを商売とする“のぞき屋”に舞い込む二つの不思議な依頼。一つは高校生の娘レイカをのぞけという父親からのものであり、もう一つは、自分自身をのぞいて欲しいという一人暮らしの女性・加奈子からの頼み。さっそくメンバーの聴、見、スマイルはハイテクを駆使したのぞきにとりかかるが、仕事を続けるうちに、見がターゲットであるレイカに惹かれてしまう。一方、聴も加奈子への愛を気づき始めていた……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le quattro volte", "四つのいのち", "", "", "http://image.eiga.k-img.com/images/movie/55810/315x210.jpg?1781", 88, "イタリアの新鋭ミケランジェロ・フランマルティーノ監督が描く、生まれては死んでいく4つの命の物語。南イタリア・カラブリア地方の山村で暮らす年老いた牧夫が静かに息を引き取った後、彼の仔山羊はもみの大木に身を寄せて息絶える。その大木は村の祭りのために切り倒され、やがて木炭となる。一切のセリフを廃し、長回しの映像で自然のありのままの姿を描だす。第23回東京国際映画祭natural TIFF部門に出品された。", "2011-04-30", "4,7,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANCHORS AWEIGH", "錨を上げて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "休暇でハリウッドにやって来た二人の水兵を中心に、恋を絡め、ちょっぴり内幕ものぞかせた佳作。圧巻はケリーのダンスにアニメーションを合成したシーン。ともに踊るのはトム＆ジェリー。ホセ・イトウールビもゲスト出演。20台のピアノで『ハンガリー狂詩曲』を聴かせるシーンが見もの。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("覇王花／THE INSPECTORS WEAR SKIRTS", "レディ・スクワッド 淑女は拳銃がお好き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ジャッキー・チェンが自ら製作と武術指導を担当した、女たちのアクション・コメディ。香港警察では、女性特殊部隊“レディ・スクワッド”を新設する。すご腕の女性警部の指揮下で若い婦人警官たちが特別訓練に合宿。ニンジャとの戦い、米海兵隊なみの猛訓練など厳しい訓練が続く。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふるさとをください", "ふるさとをください", "", "", "http://image.eiga.k-img.com/images/movie/53620/315x210.jpg?2654", 0, "和歌山県のとある町に、障害を持つ人々の共同作業所ができた。地元住民は集団で引っ越してきた障害者に警戒心を抱き反対運動を開始するが、作業所を訪れた県庁職員の千草は彼らの働く姿を見て共感を覚える。反対運動の先頭に立つ父との溝が深まる中、千草は作業所の職員・明彦に少しずつ惹かれていき……。「独眼竜政宗」「八代将軍吉宗」など数々の名作テレビドラマを生み出したジェームス三木脚本による心温まる人間ドラマ。", "2008-04-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("隠密剣士", "隠密剣士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "人気TV時代劇の映画化作品。十一代将軍・幼少の徳川家斉就任を不満とする尾張家の策謀の証拠たる血判状をめぐって、家斉の妾腹の兄・秋草新太郎と霧の逓兵衛は甲賀竜四郎率いる甲賀忍者と激しい争奪戦を繰り広げる。藤純子が女忍者・霞に扮して初々しい姿を見せる。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Dernier Combat", "最後の戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「レオン」「フィフス・エレメント」のリュック・ベッソン長編デビュー作。文明が破壊された近未来の地球。大気汚染のため声帯を破壊され、声を出すことができなくなった男たちは、1人の女をめぐり戦うことになる。のちに数多くのベッソン作品に登場し注目を浴びる俳優ジャン・レノにとっても、本作が本格的な映画デビューとなった。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長遊侠伝・天城鴉", "次郎長遊侠伝・天城鴉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "マキノ雅広監督による次郎長もの。甲州のドモ安が囚人５名を連れて島抜けをする。次郎長一家は、ヤクザの仁義を外れたドモ安をたたき斬ろうとワラジをはく。北原三枝が流転の女に扮し、前作の女馬子役に続いて好演した。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEEDFUL THINGS", "ニードフル・シングス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "メイン州のある町に古道具屋が開店した。この店では客が欲しがる物を、ある“悪戯”と引き換えに売るという。“悪戯”が蔓延した町では人々が憎み合い、やがて殺し合うようにまでなった……。S・キングのベストセラー小説を映画化し、人の心の中の暗黒をえぐり出すかのように残酷なユーモアに富んだ一作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("King of the Mountain", "マルホランド・ラン 王者の道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ハリウッド・ヒルズのドライブウェイで警察の目を盗み、スピードを競い合うカーレース＝マルホランド・ラン。このレースをクライマックスに、若者たちの恋、友情、挫折を描く青春映画。監督のノセックはドキュメンタリー出身。ポリス、スティクスなどのヒット曲がBGMとして使われている。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("非常完美", "ソフィーの復讐", "", "", "http://image.eiga.k-img.com/images/movie/54740/315x210.jpg?4396", 107, "チャン・ツィイーが主演と製作を務めるラブコメディ。共演に「映画は映画だ」のソ・ジソブ、「新宿インシデント」のファン・ビンビンほか。漫画家のソフィーは、付き合って2年になるハンサムな外科医ジェフからプロポーズされ、有頂天な日々を送っていた。しかし、人気女優ジョアンナの手術をジェフが担当したことから2人は恋に落ち、ソフィーはジェフに振られてしまう。ソフィーは予定していた2カ月後の結婚式までに、何とかしてジェフを取り戻そうとするが……。", "2010-01-09", "8,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOY FRIEND", "ボーイフレンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「恋人たちの曲／悲愴」や「Tommy トミー」などエネルギーあふれる演出でカルト作品の多い異才K・ラッセルが、ミュージカルへ正統的にアプローチして、やはりカルト化した異色の１本。旅回り一座の主演女優がケガをして出演できなくなり、座長が演出助手の女性を代役に起用したことから、騒動劇が始まる。ツィッギーのタップ、バスビー・バークレー風の群舞、盛りだくさんのパロディー・シークエンスなどで、ファンをニヤリとさせた。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DREAM A LITTLE DREAM", "ドリーム・ドリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "私生活でも大の仲良しであるフェルドマンとハイムという二人の“コリー”の共演によるアイドル青春映画。夢と現実との接点を見つけ出す研究に熱を上げている老人は、ある日突然、近所のハイスクールの生徒と心が入れ替わってしまい……。老人の心を持った高校生、という複雑な役どころをフェルドマンが好演。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHERE THE HEART IS", "女神たちの季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ニューヨークで解体会社を経営するマクベインにとって、開発予定地にあるダッチ・ハウスの取り壊し反対の住民運動も頭痛の種だが、実はもう一つの大きな悩みがあった。それは自立できない３人の子供たちのこと。そこでマクベインは自由気ままな３人を戒めようと、彼らが守ろうとしているダッチ・ハウスに追放し、彼らにサバイバルを強要する。３人は生活のため、下宿人を迎えなんとか乗り切ろうとするが、ダッチ・ハウス取り壊しの遅れがこの一帯の再開発に大きな影響を及ぼし、マクベインの会社は窮地に立たされる。J・ブアマンが人生の喜びと悲哀を優しい語り口で描いた佳作。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rollerball", "ローラーボール", "", "", "http://image.eiga.k-img.com/images/movie/1620/315x210.jpg?4706", 97, "近未来のサンフランシスコ。この世界の人気スポーツ、ローラーボールは、インラインスケートとオートバイの混合チームが、鉄球を巡って戦う命懸けのゲーム。そのスター、ジョナサンは、ゲームの裏にチームオーナーの巨大な陰謀があることを知る。監督は「ダイ・ハード」で有名、近年では「トーマス・クラウン・アフェアー」を手がけたジョン・マクティアナン。音楽はリュック・ベッソン映画の常連エリック・セラ。", "2002-05-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にっぽん昆虫記", "にっぽん昆虫記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "東北生まれの女性の生命力に満ちた半生記を、エネルギッシュに描いた今村昌平の代表作。とめは大正７年、東北の農家で父・忠次と母・えんの娘として生まれた。23歳になったとめは、製紙工場で働くようになり、地主の本田家に無理矢理“足入れ婚”をさせられる。とめはそこで信子を生み、家を出た。上京したとめは、米軍基地ハウスのメイドや売春宿の女中となり、次第に客を取るように。やがて、コールガール組織のマダムとなって、田舎から娘の信子を呼び寄せる。売春罪で捕らわれたとめが出所すると、信子は母親のパトロンだった男の情婦になっていた……。戦中戦後を生き抜く女たちの生態を、昆虫観察の残酷さで見つめ抜く無類のリアリズム。とめの心情として時折、挿入される素っ頓狂な和歌が実に効果的で、日本的土着に根ざした庶民観を讃嘆と嫌悪こもごもにつかみ出す、懐の深い力作となった。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Abduction: The Megumi Yokota Story", "めぐみ　引き裂かれた家族の30年", "", "", "http://image.eiga.k-img.com/images/movie/1530/315x210.jpg?3648", 90, "77年11月15日、いつものように学校へ出かけたまま姿を消した横田めぐみさんの両親・横田滋さん、早紀江さん夫妻の30年にも及ぶ長い闘いをTVドキュメンタリー番組、報道映像などを組み合わせて再構築。TV番組では見たことのない素顔の横田夫妻の姿も垣間見える。監督はアメリカ人ジャーナリストであるクリス・シェリダンとカナダ人キャスターのパティ・キム夫妻。製作は「ピアノ・レッスン」「ある貴婦人の肖像」のジェーン・カンピオン。", "2006-11-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PYGMALION", "ピグマリオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "G・B・ショウの有名戯曲の映画化で、大ヒット・ミュージカル『マイ・フェア・レディ』の原形となった作品。ある言語学者が下町なまりの花売り娘を引き取り、立派なレディに仕立て上げるが……。舞台出身のW・ヒラーの演技力と美しさが見もの。", "1938-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hi, Mom!", "ロバート・デ・ニーロの ブルーマンハッタン／BLUE　MANHATTAN　Ⅰ・哀愁の摩天楼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "　おんぼろアパートに越してきた青年ジョン。彼は向かいに建つマンションの部屋を盗撮し、“覗き芸術”映画を製作していた。そんな彼は覗き見た一室の住人ジュディに恋し、自らの素性を隠して彼女をデートに誘うが……。黒人差別やベトナム戦争問題といったテーマにも触れながら、青年の日々をコミカルに、そして時に辛らつに綴る。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("噴出祈願 15代の売春婦", "噴出祈願 15代の売春婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "デリケートな思春期の少年、少女の性衝動を描いたピンク映画。高校生の保子は大人のマネをしてセックスをし、誰の子かわからない子供を身ごもってしまう。友だちも皆、性には無知で……。“若松孝二プロ”の主要メンバーだった足立正生の監督作。彼はその後、パレスチナへと飛び日本赤軍のメンバーに。ビデオ発売時タイトルは「十五代の売春婦」。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風速七十五米", "風速七十五米", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "当時の大映スターが勢揃いした大作にしてアクション、サスペンス、パニックなどの要素が渾然一体となった作品。東京のネオン建設を遠藤組と丸高組が入札競争し、丸高組が請け負った。遠藤組は田宮扮する小谷が指揮して建設を妨害する。さらに丸高組の社長まで抹殺したが、叶演じる社長の娘が建設を引き継ぐ。クライマックスは嵐の中での宇津井扮する田村と小谷の対決だ。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("You're My Pet", "きみはペット", "", "", "http://image.eiga.k-img.com/images/movie/57120/315x210.jpg?3955", 110, "2003年にTBSでTVドラマ化もされた小川彌生の恋愛コミックを韓国で実写映画化。主演は人気ドラマ「美男（イケメン）ですね」のチャン・グンソクと、「リメンバー・ミー」のキム・ハヌル。エリート街道を歩んできた出版社勤務のキャリアーウーマン、チ・ウニは、恋人にフラれたうえに職場では左遷され、初めて味わう挫折に失意のどん底にいた。そんなある日、一文なしだがイケメンのダンサー、カン・イノと出会い、イノは友達でも恋人でもない「ペット」としてウニの家に暮らすことになる。自由奔放だが繊細なイノとの生活に、次第に心癒されていくウニだったが……。", "2012-01-21", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("非洲超人／THE GODS MUST BE FUNNY IN CHINA", "ミラクル・ワールド　ブッシュマン３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「ブッシュマン２ コイサンマン」に続くシリーズ第３作。今回は中国を舞台に、ニカウさんがマラソンレースに臨む。弱小チームのコーチ、エイミーに俊足を買われたニカウさんは、世界各国から集まったランナーが参加する「世界耐久スーパーレース」に参加することになった。だが、実はこのレースの裏には“パンダ狩り”をたくらむ主催者の陰謀があったのだ……。前作同様、ジャイアントパンダをはじめ多数の動物たちが出演し、名演を披露している。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lemmy", "極悪レミー", "", "", "http://image.eiga.k-img.com/images/movie/55590/315x210.jpg?1371", 110, "1975年にイギリスで結成された伝説的ロックバンド「モーターヘッド」のフロントマン、レミー・キルミスターの半生を追ったドキュメンタリー。ドキュメンタリー作家のグレッグ・オリバー、ウェス・オーショスキーが共同でメガホンをとる。レコーディング、ライブツアー、ロサンゼルスでの家族との日常生活などを長期にわたって取材し、還暦を過ぎてもなおロックンロールを体現し続けるレミーの素顔を映しだす。", "2010-12-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOCTEUR PETIOT", "怪人プチオの密かな愉しみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ナチス占領下のパリ。医師プチオは、ユダヤ人に脱出の手助けをするとだましては殺害し、金品を奪っていた。毒薬に苦しむ犠牲者を見ながらダンスして喜ぶプチオは狂人なのか、それとも……。実際にあった猟奇事件を題材に、ドイツ表現主義を思わせる陰影を強調した映像で悪夢の世界を描く異色ホラー。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新生の歌", "新生の歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "時は1941年、翌年には映画企業統制によって映画会社は３社に統合されることになる。映画も国策ものが増え、本編でも農村回帰・防諜・大陸開拓という、国策に沿った道具立てがそろえられた。しかし、本作は活劇調メロドラマという雰囲気で、同時期の松竹作品「戸田家の兄妹」とともに、多くの観客を集めた。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TEN COMMANDMENTS", "十誡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "ハリウッド最古参の巨匠C・B・デミル監督による無声期の大作。全体は２部に分かれている。まず“古代編”は、旧約聖書の『出エジプト記』に描かれた、おなじみ聖者モーゼの物語。後半の“現代編”は本作のオリジナルで、正直者の兄とよこしまな弟が登場し、兄を見下してきた弟が結局は悪い女にだませれて死ぬ……という教訓話となっている。見どころはやはり古代編。海が割れる場面と、神の怒りの稲妻によって破戒の民が滅ぼされるクライマックスにある。特に後者は無声ながら衝撃音が響いてくるような本作最大の見せ場。すべてに1956年版のような圧倒感はないものの、映画としての出来はこちらが数段上だ。", "1923-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELP！", "HELP！ 四人はアイドル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "前作「ビートルズがやって来るヤァ！ヤァ！ヤァ！」に比べ今回はギャグを多用し、当時流行していた“007”などのパロディを随所に挿入。才人レスターにとってビートルズはうってつけの素材だった。収録曲は『恋のアドバイス』『涙の乗車券』など。20年以上たった今でも新鮮さを失っていない。ビートルズとともに育った人にとっては涙ものの逸品。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獅子の座", "獅子の座", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "松本たかしの原作『初神鳴』を伊藤大輔が映画化した力作。江戸時代に時の将軍・家慶の寵愛を受けた、能楽家・宝生弥五郎が行った弘化の勧進能と、その息子・石之助との親子の対立を描く。石之助には、加藤雅彦（現・津川）が扮し、熱演。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLAY IT TO THE BONE", "マイ・スウィート・ガイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "悪友同士であるボクサー・コンビの珍道中記。落ち目のボクサー、シーザーとビンスがベガスで行われる世界選手権の前座試合に抜擢された。一緒に車でL.A.を出発した彼らは早くも車中で火花を散らす。しかも途中で美人のヒッチハイカーを拾ったことから、いがみ合いは過熱し……。男同士のライバル意識を、ユーモラスかつホットに描出。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KEEPING　THE　FAITH", "僕たちのアナ・バナナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "演技派俳優E・ノートンが監督に初挑戦。N.Y.を舞台に、幼なじみの三角関係を軽やかに描いたロマンティック・コメディだ。カソリックの神父ブライアンとユダヤ教のラビであるジェイク。大親友の二人は16年ぶりに小学校の同級生、アナ・バナナに再会する。仕事はバリバリ、スタイルばっちり、だけど気取らないアナにフォーリン・ラブ！　しかしブライアンは恋愛禁止の神父の身、ジェイクはユダヤ教徒以外と結婚できない立場。一方、アナの気持ちにも微妙な変化が訪れていた……。コミカルな演技に定評のあるベン・スティラー、キャリアウーマンを嫌味なく演じたジェナ・エルフマン、そしてカラオケまで披露するノートン、３人のアンサンブルとテンポのよい会話が心地よい。N.Y.のオシャレなデートスポットの数々も楽しめる。ちなみに脚本のS・ブルムバーグはノートンの大学時代の親友で、アナのモデルになったのも二人の女友達なんだとか。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO´Z W WODZIE", "水の中のナイフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "鬼才ポランスキーの名を世界に知らしめた長編処女作。裕福な生活を楽しむ中年夫婦が、湖へバカンスに行く途中で貧しげな青年を車に乗せた。ところが彼はかなり屈折していて、道中もすねてばかり。現代における世代間、階層間のギャップを、ユーモアと皮肉を込めて鋭く描いた一編。ヴェネチア映画祭批評家連盟賞を受賞。「ポランスキー・スペシャルDVDコレクション」に収録。", "1962-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DR.JEKYLL AND MR.HYDE", "ジキル博士とハイド氏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「喝采」で注目を浴びたR・マムーリアンの監督第３作で、恐怖小説の古典の二度目の映画化。向性新薬の研究を続けてきたロンドンの青年医師ジキルは、自ら新薬の実験台となる。ところが、予期せぬ副作用が現れ、彼は凶悪な殺人鬼ハイドとなって夜の街をさまようようになる……。オスカーを獲得したF・マーチの熱演も見ものだが、セシル・B・デミル、ジョゼフ・フォン・スタンバーグ、エルンスト・ルビッチらの作品を多く手掛けたH・ドライアーの素晴らしい美術も見逃せない。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VIE RE＾VE´E DES ANGES", "天使が見た夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "短編３本を撮り、41歳でこの長編処女作に取り組んだE・ゾンカ監督が、自らの人生体験を女性キャラクターに託した青春映画。真冬のリールの街を舞台に、人生をさまよう21歳の娘二人の出会いと別れを綴っている。バックパック一つで旅するイザは自由な心の持ち主。彼女が意気投合し、衣食住をともにすることになった金髪のマリーは、愛を知らない女の子だった。やがてマリーが地元のブルジョワ青年クリスと交際し始めた頃から、イザとの友情に修復不可能なヒビが入っていく。A・ゴダール撮影によるシャープな映像が印象的。やや唐突なラスト・シーンに違和感を覚える人もいるだろうが、若い女性特有の繊細な感性がリアルに描き込まれたドラマは一見の価値がある。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZARDOZ", "未来惑星ザルドス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "人類の生と死という、宇宙真理のテーマに挑んだ、J・ブアマン入魂の一編。それゆえ結構難解でもある。S・コネリー扮する主人公が、不老不死の空飛ぶ神像ザルドスの秘密を暴く。カルト的ファンを有するSF作品。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETOUR A｀ L’AUBE", "暁に帰る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "戦前に“フランスの名花”と呼ばれた、妖精のごとき21歳のD・ダリューの魅力が堪能できる作品。ハンガリーの田舎駅長の若妻アニタは、都会での生活を夢に描いて暮らしてきた。が、ついにニ度と戻らぬ決心を奮い起こした彼女は家出して都会へ向かう……。後にダリューの夫となるH・ドコワン監督作。", "1938-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Space Raiders", "スペース・レイダース", "", "", "http://image.eiga.k-img.com/images/movie/58130/315x210.jpg?4286", 83, "B級映画の帝王ロジャー・コーマンが、「スター・ウォーズ」シリーズ（1977～83）のヒットの流れにあやかって製作したSFアドベンチャー。独裁者が支配する、とある銀河系。宇宙船の格納庫で遊んでいた少年ピーターは、宇宙海賊団スペース・レイダースと独裁者軍の戦いに巻き込まれてしまう。激闘を生き延びたスペース・レイダースのホークとピーターの2人は、宇宙を舞台に冒険に旅立つ。2012年、ロジャー・コーマンの軌跡をたどったドキュメンタリー「コーマン帝国」（11）の公開にあわせて日本初公開。", "2012-04-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELLRAISER", "ヘル・レイザー", "", "", "http://ecx.images-amazon.com/images/I/51SC5CY11XL._SL210_.jpg", 95, "イギリスの怪奇幻想作家C・バーカーが自作を映画化。パズルボックスを組みかえたために味わう、究極の快楽。それはSMの極限のはての地獄行き。だが人間の生を得て再びこの世に戻ると、地獄の魔道士が追ってきた。アヴォリアッツ国際ファンタスティック映画祭恐怖映画部門でグランプリを受賞。", "1988-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌麿をめぐる五人の女", "歌麿をめぐる五人の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "名作「西鶴一代女」にもつながる江戸風俗を背景として女性を描いた作品。幕府お抱えの絵師と競合して勝ってしまった町衆の画家歌麿を狂言回しとして、水茶屋に勤める気性の激しい娘おきた、美しい御殿女中お蘭などの女たちの生き方を、繊細なタッチで描き出していく佳作。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOUNG COMMANDOS", "デルタフォース3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "チャック・ノリスが主演した“デルタ・フォース”シリーズのヤング版。ここではそのC・ノリスの息子マイクと、ジョン・カサヴェテスの遺児ニックが共演。アラブの過激派テロリスト、カダルの陰謀を阻止するために結成されたヤング・コマンド一部隊が、ソ連の精鋭部隊と組んで命懸けの任務を遂行する。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帝都物語", "帝都物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "荒俣宏の長編伝奇小説を、『ウルトラマン』や『ウルトラセブン』のTVシリーズを手掛けた実相寺昭雄監督で映画化。明治45年から昭和初期までを背景に、東京壊滅をたくらむ魔人・加藤保憲と、それを阻止しようとする人々との戦いを描く。陰陽道を操る加藤の投げた紙がカラスに変化するといった最新のSFX、関東大震災を再現した、円谷プロの名残を残すミニチュア・セットなど、近年の邦画のなかでも全編の特撮処理のうまさは光る。その他、美術を手掛けた木村威夫のこだわりが現れた、昭和初期の銀座を完璧に復元したセットが見もの。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dialogue avec mon jardinier", "画家と庭師とカンパーニュ", "", "", "http://image.eiga.k-img.com/images/movie/53750/315x210.jpg?2844", 105, "都会での暮らしに疲れ、故郷のカンパーニュに戻った初老の画家。彼が庭師として雇った男は、偶然にもかつての幼なじみだった。すぐに昔のように打ち解けた2人は、豊かな自然の中で様々なことを語り合いながら楽しい時間を過ごすが……。「クリクリのいた夏」のジャン・ベッケル監督によるヒューマン・ドラマ。画家役に「ぼくの大切なともだち」のダニエル・オートゥイユ、庭師役に「サン・ジャックへの道」のジャン＝ピエール・ダルッサン。", "2008-08-02", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWORD OF SHERWOOD FOREST", "シャーウッドの剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "悪徳長官の策略で城から追い出されようとしている王を救うため、ロビン・フッドは長官とその一味に戦いを挑む。そして、激しい戦闘の末、ロビンは見事に王の命と城を守り通すのだった。英国の伝説上の英雄が大活躍するアクション作品。", "1961-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Prick Up Your Ears", "プリック・アップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "同性愛が法律で禁止されていた1960年代のロンドンを舞台に、実在した劇作家ジョー・オートンと彼の同性の恋人ケネス・ハリウェルの、悲劇に終わった純愛を描く。前作「マイ・ビューティフル・ランドレット」で国際的な評価を得たスティーブン・フリアーズ監督が、ジョン・ラーの筆によるオートンの伝記小説を基に映画化。主人公オートンを個性派俳優ゲイリー・オールドマンが、その恋人ケネスをアルフレッド・モリーナが演じる。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KNOCK　ON　ANY　DOOR", "暗黒への転落", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "H・ボガートが長編デビューから間もないN・レイ監督と組んだ社会派サスペンス。シカゴの貧民街出身の青年が警官殺しの罪で告発される。青年の無罪を信じる弁護士は、傲慢な検事に対して彼の生い立ちを語って聞かせるのだが……。意外な展開が待ち受けるラストでの弁護士役ボガートの演技が印象的。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rescue Dawn", "戦場からの脱出", "", "", "http://image.eiga.k-img.com/images/movie/54630/315x210.jpg?4098", 125, "実在の米軍パイロット、ディーター・テングラーの脱出劇をモデルにしたサバイバル・アクション。主演は「バットマン」シリーズのクリスチャン・ベール。1965年、極秘任務を遂行していた米兵パイロットのディーターは、ラオスでベトナム兵により撃墜され、ジャングルに囲まれた捕虜収容所に連行されてしまう。そこには何年も前に捕虜となった仲間たちがおり、ディーターは脱出を諦める仲間を奮起させ、決死の脱出を試みる。", "2006-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SWAN PRINCESS", "スワン・プリンセス 白鳥の湖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "チャイコフスキーのバレエ組曲でも知られるドイツのお伽話を原作にした長編アニメーション。平和な王国の美しい女王オデットと、近隣の国の王子デレクは、愛し合い、結婚も決まる。ところが魔法使いロスバートが現れ、オデットに結婚を迫る。オデットは拒絶し、怒ったロスバートは呪いをかけ、彼女を白鳥にその姿を変えてしまう。月の光が羽を照らした時だけ、元の姿に戻れるのだった。ドリームズ・カム・トゥルーが主題曲『ETERNITY』を担当。往年の性格俳優J・パランスが声の出演をしている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("301・302", "301・302", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "高層マンションの302号室に住む女性雑誌のライター、ユニは、セックスへの嫌悪感から拒食症に陥っている。そんなユニの向かいの301号室に、料理が得意なソンヒという過食症の女性が引っ越してきた。ある日、毎日おすそ分けしていた自慢の料理を、ユニがゴミ箱に捨てていると知ったソンヒは……。拒食症と過食症という対照的な食癖を持つ女性二人の奇妙な友情と葛藤のドラマ。図書館のように殺風景な302号室、レストラン顔負けのシステムキッチンが備えられた301号室というように、趣向を凝らしたセットやブラック・ユーモアあふれる人物造形が、現代の都会人の孤独を鮮烈に浮き上がらせている。ユニ役のファン・シネは韓国の正統派の国民的女優であり、演じるキャラクターの意外性が本国で大きな話題を呼んだ。", "1995-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lads Et Jockeys", "ジョッキーを夢見る子供たち", "", "", "http://image.eiga.k-img.com/images/movie/54110/315x210.jpg?3343", 99, "凱旋門賞に出場することを夢見て、フランス唯一の騎手・厩務委員養成寄宿学校に入学した少年たちを追ったドキュメンタリー。親元を離れ、厳格な規律のもとで馬について学ぶスティーブ、フラビアン、フロリアンに10カ月密着し、彼らのジョッキーになる夢や将来への不安を見つめる。世界的ジョッキーの武豊、オリビエ・ペリエも出演。", "2009-01-24", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PEPPERMINT FRAPPE´", "ペパーミント・フラッペ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "中年の医師フリアンはパブロ夫妻からディナーに招待される。ところが彼の妻はパブロ夫人に瓜二つ。パブロ夫人に心惹かれたフリアンは、妻をパブロ夫人のように仕立てあげるが……。ルイス・ブニュエルに捧げられたシュールなブラック・コメディ。顔はそっくりでも性格はまるっきり違う女を二股かける男の、奇妙な愛を描いている。一人二役を演じたG・チャップリンとC・サウラは、この映画が縁で結婚。", "1967-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("毎天愛弥８小時／YOUR　PLACE　OR　MINE", "君を見つけた25時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ウディ・アレン映画などを彷彿させる、小粋なセックス・コメディ。恋とはスレ違いばかりの売れっ子CFディレクターが、マネージャーを務めることになった新人アイドルと、やり手の美人上司との間で優柔不断な日々を送る。男の情けなさを憎めないキャラクターとして体現したT・レオンの妙演、自身のサクセス・ストーリーを再現したようなV・スーの自然体の好演。さらには、主人公の親友のセックス・トラブルなどのサブ・エピソードもキラキラと輝いている。", "1998-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鑓の権三", "鑓の権三", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "近松門左衛門の世話浄瑠璃「鑓の権三重帷子」の映画化作品。松江藩の表小姓・笹野権三は、器量よく槍さばきも見事で茶道にも通じていた。茶の湯の極意の伝授を茶道の師の妻・おさゐに懇願するが、それがもとで不義密通の濡れ衣を着せられ、二人は道行きの旅に出る。心中ものとなる恋愛時代劇の傑作。第36回ベルリン映画祭で銀熊賞（調和作曲）を受賞した。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIODAGAR", "ムービー・デイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "1960年代のレイキャビク。10歳の少年トーマスは、おめかしして家族とともに映画館に通うのが最大の楽しみだった。田舎の親戚の農場で夏休みを過ごすことになった彼は、そこで様々な神秘的体験をすることに……。F・T・フリドリクソン監督が、ウディ・アレンの「ラジオ・デイズ」にヒントを得て撮り上げた自伝的作品。ニコラス・レイやロイ・ロジャースの映画が挿入されている。", "1994-01-01", "77,7,18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("October Sky", "遠い空の向こうに", "", "", "http://image.eiga.k-img.com/images/movie/1860/315x210.jpg?2017", 108, "米ソ冷戦時代のアメリカ、コールウッド。ここに生まれた男は誰もが将来炭坑夫になると約束されたこの町で、高校生ホーマーは自分の将来に不安を感じていた。そんな1957年10月4日。ホーマーは星空を美しい軌跡を描いて飛んでいくソ連の人工衛星スプートニクを見る。宇宙の夢に魅せられたホーマーは悪友たちと「ロケット・ボーイズ」を結成、ロケット製作に夢中になるが、父はそんなホーマーを理解できず、二人は衝突する。", "2000-02-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拳銃無頼帖 流れ者の群れ", "拳銃無頼帖 流れ者の群れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "組長を殺されたヤクザの幹部・麻島は、組員に足を洗わせ、自分一人で復讐を誓う。だが、敵方のボス・貝塚は、復讐を恐れて、麻島をはじめ、３人の元幹部に殺し屋を差し向けた……。善玉の小林旭を含む３人が、逆襲に立ち上がるあたりから、ハードボイルド・タッチの味わいが出る、アクション映画の佳作。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEY BABU RIBA", "ヘイ・バブリバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1953年ユーゴスラビア・ベオグラード。ボートのクルーチームの４人組は、美しく利発な少女エスターに恋していた。なんとか自分こそ彼女の心を射止めようとする彼らに、エスターは優しくささやく。“４人とも好きよ”。ところがある日エスターが妊娠し……。10代の若者たちの恋と友情を、叙情豊かに描いた、温かくさわやかなユーゴ版青春グラフィティー。", "1986-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トップドッグ２ さらば友よ", "トップドッグ２ さらば友よ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "２ストローク500ccのマシンによってワールド・チャンピオンの座が競われるオートバイ・レースの最高峰、世界GP・A500ccレース。前作「トップドッグ」に続いて、その1988年シーンの全貌を追ったドキュメンタリー。今回はヤマハのエース・ライダー、E・ローソンを中心に、各国を転戦する選手やチームの熾烈な争いをダイナミックに捉えている。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チョットだけョ全員集合!!", "チョットだけョ全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "東京の下町で必殺病院と噂されている薮井病院の院長・長介は、酒屋の娘・あやめに惚れる。ところが彼女は会津若松に住む大富豪に見そめられて養女になることに。かくて色と欲に目がくらんだ長介ら５人とヤクザ・黒木組が会津若松へ乗り込む……。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RISKY BUSINESS", "卒業白書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "T・クルーズの初々しさが光る佳編。高校卒業を間近に控えた青年ジョエルは、両親の留守をいいことに美人娼婦を呼んで大人への旅立ちとしゃれこむのだが……。当世学生気質をユーモラスに活写する青春コメディ。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A ILHA DOS AMORES", "恋の浮島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 170, "日本と日本女性を愛し、東洋について数々の優れた著作を残して徳島で没した、ポルトガルの文豪モラエスの生涯を軸に、大航海時代から今日に至る東洋と西洋の魂のふれあいを描く。映画構成は中国古代の詩人、屈原の『楚辞』中の歌に即している。", "1982-01-01", "76,25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CE＾ RE＾ MONIE", "沈黙の女　ロウフィールド館の惨劇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "イギリスの人気ミステリー作家R・レンデルの原作を、C・シャブロルが映画化したスリラー。家政婦が罪のない雇い主一家を惨殺するまでを描く。ブルジョア家庭ルリエーブル家に住み込み家政婦としてやって来たソフィー。彼女の仕事ぶりは完璧だったが、文盲であることを気付かれないために人と打ち解けない女だった。そんな彼女だが、村の郵便局員で好奇心旺盛なジャンヌにだけは心を開いていき、頻繁に行き来し合うようになった二人は、過去に共通する秘密を持つことを知る。友情がやがて同志にかわり、結束を固めた彼女たちの敵意は恵まれたルリエーブル一家へと向けられる。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅色恋人／A　TIME　TO　REMEMBER", "追憶の上海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1930年代、激動の上海を背景に、理想を追い求めた男女の悲しい恋を描くラブ・ストーリー。頭に致命的な怪我を負いながらも、革命の実現に燃える闘士ジン。そして秘かに思いを寄せる彼を、全力で支えようとする女性チウチウ。やがて秘密警察に追い詰められていく二人の姿を、アメリカ人医師による現代からの回想の形でドラマティックに映し出す。", "1998-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Micmacs A Tire-larigot", "ミックマック", "", "", "http://image.eiga.k-img.com/images/movie/55310/315x210.jpg?3968", 104, "「アメリ」「ロング・エンゲージメント」のジャン＝ピエール・ジュネによるブラック・コメディ。発砲事件に巻き込まれたバジルは、頭の中に銃弾が残ったまま、家も仕事も失ってしまう。ある日、バジルは彼の頭の中の銃弾を作った会社を偶然にも見つける。するとその向かいには、父親の命を奪った地雷を作った会社があった。宿命を感じたバジルは、それらの兵器製造会社に制裁を下すことにする。", "2010-09-04", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABEL", "アベル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "アベルは31歳になるのに外に一歩も出たことがないという変わり種。父親があれこれ手段を講じてみても効果なし。ついに怒った父親は彼を外に放り出す。やがて彼は街角でストリッパーと出会い恋に落ちる。だが彼女は、何と父親の愛人だった……。大人になりきれない青年の姿を描いたブラック・コメディ。", "1986-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Relic", "レリック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　シカゴの博物館内で職員が惨殺される事件が起こる。ダガスタ警部補の捜査が進められる一方、進化生物学者のマーゴは事件と同じ日に南米から届いた荷物の中身に気を留める。それは葉に付着した胞子だったが、やがてその恐るべき力が明らかに。胞子はとり込んだ生物を変貌・凶暴化させる寄生生命体だったのだ。博物館に潜む怪物は次々と人間を餌食にしていく……。未知の生命体が生んだ怪物の恐怖を描くサスペンス・ホラー。", "1997-01-01", "1,3,7,76,31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STATE OF GRACE", "ステート・オブ・グレース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "ニューヨーク最悪の犯罪地帯にシマを張るアイリッシュ・ギャング。元ギャングで久しぶりに故郷に帰ってきたテリーとその幼なじみを主人公に、アイリッシュ・ギャングの知られざる実態に光を当てたスタイリッシュなギャング・ロマン。ロック・ドキュメンタリー「U2／魂の叫び」で手腕を注目されたジョアノーの監督作品。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEN", "メン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "妻の浮気相手を観察することによって妻の魅力を探っていく中年男を、大人の笑いで綴った異色コメディ。妻の愛人との奇妙な共同生活を軸に、三角関係を女性ではなく、男性の目を通して見つめている点が目新しく、屈折した男の心情を女性ならではの視点で巧みに演出しているD・ドゥリー監督の手腕が光る。", "1985-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Miss Potter", "ミス・ポター", "", "", "http://image.eiga.k-img.com/images/movie/34070/315x210.jpg?1403", 93, "100年以上もの間世界中で愛され続けているキャラクター“ピーターラビット”の生みの親、ビアトリクス・ポターの人生を、レニー・ゼルウィガー主演で映画化。上流階級の家に生まれたビアトリクスは、幼い頃から動物の絵を描いたり物語を創ることが大好きだった。ある日、自作の絵本が出版されることになり、新米編集者のノーマンがその担当になる。2人は恋に落ち結婚を約束するが、身分の違いからビアトリクスの両親に猛反対されてしまい……。", "2007-09-15", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUNDOWN", "砂丘の敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "大いなるアフリカの大地を舞台に、反乱を企てるシェンジ族と、その地を統治するイギリス軍との戦いを描く。戦争下にあったイギリスとドイツとの関係をはるかに匂わせた物語、エキゾチックな魅力をふりまくG・ティアニー、そしてH・ハサウェイによる、男たちの熾烈な争いを魅せる骨太の演出が見もの。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUN　CRAZY", "拳銃魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "実話をベースにした短編小説を原作に、オリジナリティーあふれる設定で魅せる拳銃アクションを満載したギャング映画の隠れた名作。1953年に日本公開されてヒットした。人殺しが嫌であるにも関わらず銃器が大好きな男が、ショーで射撃の腕前を披露したことをきっかけに、芸人一座の美人スターと恋に落ちる。そんな彼が彼女にせがまれて強盗を続けていくのだが……。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("P.J.", "野良犬の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ニューヨークの大富豪を護衛することになった貧乏私立探偵のP.J.。大富豪は、たびたび何者かに命を狙われるが、その裏には産業スパイの影がちらついて……。貧乏だが正義感は人一倍、G・ペパード扮する探偵P.J.を主人公にした、ハードボイルド・タッチのアクション・ミステリー。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　VENT　D’EST", "東風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1968年の反国家的大衆蜂起の試みの後、“政治的西欧劇”（ウエスタン）として企画されたセミ・ドキュメンタリー。当時のヨーロッパで吹き荒れた無政府主義とマオ主義の闘争を綴る。騎兵隊将校、ネイティブ・アメリカンの青年、共産党員らの姿が、ストーリー性を放棄した映像表現と唐突に挿入されるナレーションなどで描かれる。", "1969-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天若有情／RUNAWAY", "アンディ・ラウの逃避行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "香港で大ヒットした、A・ラウ主演の胸に迫る純愛映画。アンディ演じるチンピラ、ワァディは、強盗を犯した逃走中に警察の手から逃れるため、ジョジョという娘を人質にする。ワァディとジョジョは惹かれ合うが、ジョジョは財閥の娘。当然、この恋に邪魔が入って……。身分違いの恋という定番パターンだが、アンディの熱演が光る。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("МАЛВ УИКИ", "少年たち 「カラマーゾフの兄弟」より", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "世界文学屈指のドストエフスキー作品『カラマーゾフの兄弟』を、アリョーシャと少年たちとのふれあいに焦点を当てて綴る。少年イリューシャが大勢の生徒に石をぶつけられているのを、神学生アリョーシャは止めさせる。しかし、イリューシャは彼に石を投げ、手を噛み、泣きながら立ち去る。", "1990-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本任侠伝 花の渡世人", "日本任侠伝 花の渡世人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "徳川時代からの由緒ある“め組”の一員・纒の清二は、自分たちが請け負っている建築工事を松木組に邪魔されたことに怒り、単身殴り込みをかけるが、そのために組を破門されてしまう。３年後、清二がめ組に帰ってきたとき、組はすっかり落ちぶれていたが……。高橋英樹のドスさばきが光る任侠ヤクザ映画。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どこに行くの？", "どこに行くの？", "", "", "http://image.eiga.k-img.com/images/movie/53350/315x210.jpg?2262", 100, "伝説のカルトムービー「追悼のざわめき」の松井良彦監督が22年の時を経て再びメガホンを取り、同性愛に目覚めた青年とニューハーフの恋を描いた衝撃のラブストーリー。幼い頃に両親を亡くした青年アキラは、父親代わりであった木下から受けた性的虐待がトラウマとなり、人を愛することができずにいた。ある日、彼はバイクではねてしまった女を自分の部屋へ連れて帰り介抱するが……。主演は「カミュなんて知らない」の柏原収史。", "2008-03-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刀馬旦", "北京オペラブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "辛亥革命直後の激動の中国。私兵を操る軍閥たちが私腹を肥やし、意のままにふるまっていた時代。そんな軍閥のひとり娘として生まれたツォワンは、民衆解放のための地下組織に加担。父親をも敵に回し、京劇（北京オペラ）一座の娘たちを巻き込んで過激な戦いを繰り広げていく。男装の麗人・ブリジット・リンの美しさが光る。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スマイル　聖夜の奇跡", "スマイル　聖夜の奇跡", "", "", "http://image.eiga.k-img.com/images/movie/53140/315x210.jpg?1626", 124, "「ロッカーズ」で監督デビューを果たした陣内孝則が企画・監督・原作・脚本を手掛け、弱小アイスホッケーチームの少年たちと素人監督の成長を描いたハートウォーミングな青春映画。プロのタップダンサーになる夢を諦めた修平は、恋人の静華がいる北海道へとやって来る。結婚を望む修平に静華の父が出した条件は、これまで1度も勝ったことのない少年アイスホッケーチーム“スマイラーズ”の監督に就任し、勝利を収めることだった……。", "2007-12-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("これでいいのだ!!　映画★赤塚不二夫", "これでいいのだ!!　映画★赤塚不二夫", "", "", "http://image.eiga.k-img.com/images/movie/55650/315x210.jpg?1703", 110, "「天才バカボン」「おそ松くん」などを生み出し、2008年にこの世を去った“ギャグ漫画の王様”赤塚不二雄の人生を映画化。原作は、小学館に36年間勤務し、200人以上の漫画家を世に送り出してきた編集者・武居俊樹の著書「赤塚不二夫のことを書いたのだ!!」。まじめ一徹の新人編集者のまなざしを通して、ギャグで駆け抜けた自由奔放な赤塚の姿を描く。主人公の赤塚役には浅野忠信、彼を見守る新人編集者役に堀北真希。そのほか阿部力、木村多江、いしだあゆみ、佐藤浩市らが共演。", "2011-04-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sicko", "シッコ", "", "", "http://image.eiga.k-img.com/images/movie/1930/315x210.jpg?4143", 123, "「華氏911」のマイケル・ムーア監督が、アメリカにおける医療保険問題に焦点を当てたドキュメンタリー。国民健康保険がないアメリカでは、国民は民間の保険会社で保険に加入するしかない。多くの人々が無保険で命を落としていく一方、高額な保険料を払っているにもかかわらず、いざという時に保険金をもらえず満足な治療を受けられない例も多数存在する。ムーア監督はこういった数々の事例を紹介し、アメリカが抱える医療保険制度の闇を暴いていく。", "2007-08-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Solitary Man", "ソリタリー・マン", "", "", "http://image.eiga.k-img.com/images/movie/56170/315x210.jpg?4301", 90, "無類の女好きでカーディーラーとして成功を収めた50代後半のベンは、妻と離婚後、大手自動車メーカーの役員を父にもつジョーダンとつきあっていた。しかし、ジョーダンの娘アリソンに手を出してしまったことから、ビジネスも私生活も棒にふってしまう。自分自身に正直に生きる還暦寸前の男の生きざまを描いたドラマ。「オーシャンズ13」や「ラウンダーズ」の脚本家コンビとして知られるブライアン・コッペルマンとデビッド・レビーンが共同監督。スティーブン・ソダーバーグがプロデュースを務める。", "2011-06-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUI？", "どしゃ降り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ある冬の日。一組の男女の乗った車が海中に転落した。女は助かり、男の死体はあがらない。駆けつけた男の兄は女に疑惑を抱く。しかしやがて愛し合うようになった二人の前に、死んだはずの男が現れる。クールなタッチの本格サスペンス。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Take the Lead", "レッスン！", "", "", "http://image.eiga.k-img.com/images/movie/34060/315x210.jpg?1393", 117, "ニューヨークのスラム街にある学校でダンスを教えていた実在の社交ダンサー、ピエール・デュレインをモデルに、ダンスを通して問題児たちの更正を図る教師の姿を描いた青春ドラマ。スラム街の高校に通う不良たちに社交ダンスを教えることになったピエール。それぞれの悩みを抱え夢も希望も持てずにいた生徒たちが、ピエールの授業を通して多くのことを学んでいく。主演は「レジェンド・オブ・メキシコ」のアントニオ・バンデラス。", "2007-07-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK DOG", "ブラック・ドッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「ゴースト　ニューヨークの幻」のP・スウェイジが、家族のためにハンドルを握るトラック野郎を演じるアクション。生活に窮し、アトランタからニュージャージーまで謎の積み荷を運ぶ仕事を請け負ったジャック。ドライバーを襲う死の幻影“ブラック・ドッグ”と闘いながら大型トレーラーを走らせる彼の前に、不気味な武装バイカー集団が現れる。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("標錯参／TO ERR IS HUMANE", "サモ＆ケニー 人質に気をつけろ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "サモ・ハン・キンポーが何をやっても駄目な愛すべき男を演じた、アクション・コメディ。宝石店で働くチンは、強盗を手引きしたという、いわれのない疑いを店主にかけられた。腹を立てた彼はなんとか店主にひと泡ふかせようと、店主の夫人を誘拐して身代金を取ることを計画するが、何もかもうまくいかず……。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の暦", "女の暦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "壷井栄の処女作で、『二十四の瞳』と同じく瀬戸内海の小豆島を舞台とした原作を、久松静児が新東宝で初監督している。日向家のクニ子と実枝は二人暮らし。父母はすでになく、10人の兄妹も女ばかりの５人に減ってしまっていた。そんなある日、実枝の発案で、10数年会っていなかった姉たちを呼んで、死んだ家族の法事をすることになる。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BULL’S EYE！", "ダブルチェイス 俺たちは007じゃない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ダンディな悪党二人組が、自分たちと瓜二つのペテン科学者になりすまし、アメリカのCIAやイギリスの情報局相手に秘密原子エネルギー開発の機密争奪戦を展開する。ムーアとケインというスパイ・アクションの二大スター共演。ムーアの娘、D・バリモアがこの作品で本格的にスクリーン・デビュー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAR NORTH", "ファーノース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "現代アメリカを代表する劇作家兼俳優としても知られるS・シェパードが、夫人のJ・ラング主演で初監督した、ユーモアとペーソスあふれる人間ドラマ。大ケガをした父親を見舞うために故郷のアメリカ北部を訪れたケイト。父親は彼女に自分を負傷させた愛馬を殺してくれるよう頼むのだが……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MITAD DEL CIELO", "天国の半分", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "手を伸ばせば天国に届きそうなほど、のどかで自然に恵まれた山村に生まれ育ったロサ。やがて故郷を離れたロサは、幼い娘とともにマドリードで生活を始めるが、数々の困難が待ち受ける。しかし、田舎から訪ねてきたおばあさんのにおいに触れた時、彼女は不思議な安らぎと、生きていく力を確信するのだった……。スペイン映画界の巨匠アラゴンの、温かさに満ちた日本初公開作。", "1986-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カフェ代官山III それぞれの明日", "カフェ代官山III それぞれの明日", "", "", "http://image.eiga.k-img.com/images/movie/54370/315x210.jpg?3619", 76, "カフェ代官山III／それぞれの明日1軒のカフェを舞台に繰り広げられる人間模様を、相葉弘樹、馬場徹ら若手俳優共演で描いたシリーズの最終章。カフェ・レーブ・コンティニュエからガクが去って1年。ヒビキ、サーディン、コトッチらおなじみのメンバーが集うカフェに、ガクの従兄弟・キョウカが現われる。ガクの手紙に誘われて店を訪れたと言うキョウカは、顔はガクと瓜ふたつなのに性格は正反対。自分勝手で周囲を振り回してばかりだが、パティシエとしての腕は一流で……。", "2009-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビジターQ", "ビジターQ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "日本を代表する６人の監督がデジタル・ビデオで自由自在に撮りあげ話題となった“ラブシネマ”。鬼才・三池崇史が描くのは、壊れた家族を取り巻くエロティックで荒唐無稽な物語。家庭内暴力の息子、家出して援助交際で食い繋ぐ娘、愛に飢えた妻は薬物に溺れる日々。キャスターを降板させられた夫は、息子のドキュメンタリーを撮り現場復帰をたくらむ。そんな崩壊寸前の家庭に、謎の男がやってきて同居することになったことから家族４人に異変が起こり始める……。遠藤憲一のキレた演技や女優・内田春菊の生活感あふれる生々しい怪演が印象深い。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MURA DI MALAPAGA", "鉄格子の彼方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "名も知れぬ港へ降り立った初老の男、ピエールは身も心も疲れ果てていた。そんな彼を港の女マルタは手厚く介抱する。マルタの娘チェッキーナは深まる二人の仲を嫉妬し、彼を船へ戻そうとする。そんな折、警察はピエールの捜索を始めていた。J・ギャバンの渋味が光る名作。", "1949-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("問題のない私たち", "問題のない私たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "執筆当時15歳の中学生だった牛田麻希による、現実そのもののリアルな描写が話題となった同名マンガを映画化。いつどこで立場が逆転するか分からない恐怖の中で、自分がターゲットにならないために、必死であがいている少女たち。いじめを苦に自殺した中学生の事件を冷笑していた主人公が、その本当の苦しみを味わうことになる。クラスのリーダー的存在でいつもいじめの首謀者である澪は、転校生の麻綺が、すぐにみんなに溶け込んだことが気に入らない。麻綺を次のいじめのターゲットにするが、翌日、クラス全員から無視され始めたのは、澪の方だった。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カインの末裔", "カインの末裔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「日本の裸族」「赤線」といった問題作を発表し続ける奥秀太郎監督が、医療少年院を出たばかりの青年を通して人間の醜さや現代社会の不条理を浮き彫りにした社会派ドラマ。15歳で母親を殺害し、医療少年院で10年間を過ごした棟方は、川崎の小さな工場に住み込みで働きはじめる。新生活に期待を抱く棟方を待っていたのは、薄汚れた工場と狭い部屋を往復するだけの退屈な毎日。そんなある日、棟方はテレビリモコン型改造銃の密造を命じられ……。", "2007-02-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大学の山賊たち", "大学の山賊たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "当時のスキー・ブームを反映して、岡本喜八が佐藤允以下、東宝青春スターを総動員して監督した青春アクション映画。吹雪のために山小屋に閉じ込められた若者たちの前に、警官に追われた二人組の強盗が逃げ込んでくる。鶴田浩二が山岳部の先輩役で特別出演。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("未来世紀ニシナリ", "未来世紀ニシナリ", "", "", "http://image.eiga.k-img.com/images/movie/54460/315x210.jpg?3855", 68, "日本最大のドヤ街やあいりん地区など負のイメージで語られることの多かった大阪市の西成区。ある夏、ニシナリの町づくりメンバーは、かつては犯罪多発のスラム街であった英国ロンドンのブロムリ・バイ・ボウ地区で実践されている町づくりの取り組みを学ぶ。西成という町とそこに暮らす人々の営みを見つめるドキュメンタリー。", "2009-03-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三萬両五十三次", "三萬両五十三次", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "黒船来航で、開港か否かで議論を呼んでいた幕末。堀田備中守は開港へと動き、京の公家を懐柔するため、江戸から京へ３万両を送ることにする。その運搬の責任者となったのが、浪人・馬場蔵人で……。“狸御殿”もので知られる木村恵吾監督だけに、コミカルな味わいがある。轟夕起子の美しさにも注目。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESIDERIA，LA VITA INTERIORE", "デシデーリア＝欲望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "イタリア文学界の巨匠モラヴィアの発禁小説『深層生活』の映画化。ブルジョア家庭の少女デシデーリアは醜く太っていたが、自分の出世の秘密を知ったことから美しい女へと変身する。そんな彼女にレズビアンを迫る義母と、しつこく言いよるその愛人。ブルジョアジーの退廃を描いたエロティックな一編。", "1980-01-01", "4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タッチ2 さよならの贈り物", "タッチ2 さよならの贈り物", "", "", "http://ecx.images-amazon.com/images/I/5183GGYYJQL._SL210_.jpg", 79, "あだち充の同名コミックを原作とする人気テレビアニメの劇場版シリーズ第2作。和也が交通事故で突然この世を去ってから1年。「南を甲子園に連れて行く」という和也の遺志を継いで野球部に入部した達也は、黙々と練習に打ちこむ毎日を送っていた。一方、新体操部に入った南は、大会で個人優勝を果たして一躍注目を集めるようになる。それぞれが複雑な思いを抱える中、ついに高校野球の地区大会が始まり……。", "1986-12-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("North Country", "スタンドアップ", "", "", "http://image.eiga.k-img.com/images/movie/1670/315x210.jpg?3379", 124, "「モンスター」のオスカー女優シャーリーズ・セロン主演で、全米初のセクシャル・ハラスメント訴訟勝訴の実話を映画化。シングルマザーのジョージーは故郷に戻り炭鉱で働き始めるが、男性達の露骨な嫌がらせに直面する。監督は「クジラの島の少女」のニキ・カーロ。撮影は「マイケル・コリンズ」「プレッジ」のクリス・メンジス。脚本はリーリー・ソビエスキーとジョシュ・ハートネットの「愛ここにありて」のマイケル・サイツマン。", "2006-01-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("men's egg Drummers メンズエッグ・ドラマーズ", "men's egg Drummers メンズエッグ・ドラマーズ", "", "", "http://image.eiga.k-img.com/images/movie/55910/315x210.jpg?2682", 99, "若者向け人気雑誌「men's egg」をモチーフにした青春映画。神社の跡取り息子・啓太が、年に一度の大祭「観月祭」の目玉である「奉納太鼓」のメンバーを募集すると、場違いな「men's egg」カリスマ読者モデルの塁が現れ、2人は友情を育んでいく。監督は「地獄甲子園」「激情版　エリートヤンキー三郎」の山口雄大。「高校デビュー」の古川雄輝が主演。「men's egg」読者モデルの佐藤歩や田中大地らが、同誌にあこがれる青年役で多数出演する。", "2011-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Academy", "アカデミー", "", "", "http://image.eiga.k-img.com/images/movie/34000/315x210.jpg?1343", 111, "オーストラリアの芸術学校を舞台に、夢を追って成長していく若者たちの姿をポップな映像で描いた青春ドラマ。モデルとなったメルボルンの名門芸術学校ビクトリア・カレッジ・オブ・アーツによる全面協力のもと、同校出身のギャビン・ヤングス監督が学生たちの等身大の日常を切り取った。日本からの留学生役で、テレビシリーズ「ウルトラマンコスモス」で大ブレイクした杉浦太陽と「凶気の桜」の高橋マリ子が出演。", "2007-06-02", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IF", "if もしも…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "1960年代後半。世界的に学生運動の気運が高まり、アメリカではアメリカン・ニュー・シネマが台頭するなか、イギリスの名門私立校を舞台に、炸裂するスチューデント・パワーを現実と幻想を織り交ぜて描いた１本。見事カンヌ映画祭グランプリを獲得し、L・アンダーソン監督の名を世界的なものにした学園ドラマの怪作。500年の伝統を誇る、とあるイギリスの全寮制の私立中・高等学校。ここはまた、校則の厳しいことでも有名だ。そんな中で、反逆分子ミックは、“革命”を起こそうと決意。３人組の仲間ジョニー、ウォーレスに、下級生の美少年やコーヒーショップの娘を引き入れ準備を進める。決行日は父兄や名士の集まる開校500年の記念祭当日。機は熟した……。アイロニカルでブラックな笑いを存分に盛り込んだ、アンダーソン監督の切れ味鋭い演出は圧倒的。根底に力強く流れる、ラジカルなアナーキズムも衝撃的だ。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHECKERS in TAN TAN たぬき", "CHECKERS in TAN TAN たぬき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "チェッカーズの歌あり恋あり冒険ありといった、リチャード・レスター監督のビートルズ映画を意識した作品。チェッカーズの面々が、実は超能力を持ったたぬきであるという設定で、彼らの超能力を狙って国際的陰謀団が暗躍する。サスペンスフルな味つけがなされていて、川島透監督が職人的手腕で手堅くまとめ上げている。SFXを駆使した、たぬきの変身場面が見もの。さりげなく“アイドル批評”になっているあたりにも、ご注目を。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　CORRUPTOR", "NYPD15分署", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "N.Y.市警15分署の刑事チェンはアジア人の犯罪を取り締まる一方で、中国系マフィアに通じていた。そんなある日、東洋人のみの15分署に、新米の白人刑事ウォレスが赴任してくる。初めは彼を疎んじていたチェンだが、命を救われたことによって友情を深める。一方、マフィアからの買収の誘いに、ウォレスの心は揺れ動き……。多民族社会というアメリカの断面を描きながら、警察機構の腐敗を浮かび上がらせた社会派アクション。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("淀川長治物語・神戸篇／サイナラ", "淀川長治物語・神戸篇／サイナラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "20世紀を代表する映画の語り部として、世界的にも広く親しまれた映画評論家・淀川長治。そんな彼の幼年期から青年期にかけての、映画にとりつかれるきっかけとなった神戸時代のあらましを、彼を敬愛し親交も深かった大林宣彦監督がドラマ化。はじめにTV放映された後、映像を追加・再構成した完全版として劇場公開された。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Slow Burn", "ダウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ある犯殺害事件の裏に隠された驚きの真相を描くクライム・ミステリー。元警官の地方検事フォードは、恋人のノラが強姦（ごうかん）事件に遭遇し、犯人の男を射殺してしまったとの報せを受ける。駆けつけたフォードに対し、正当防衛を主張するノラ。そんな中、殺された男の友人だというルーサーが警察署を訪れる。ルーサーは、この事件は計画された殺人だと訴えはじめ……。主演は「ハンニバル」のレイ・リオッタ。共演に「S.W.A.T.」のLL・クール・J。", "2007-03-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　ゆうれい船をやっつけろ!!", "それいけ！アンパンマン　ゆうれい船をやっつけろ!!", "", "", "http://ecx.images-amazon.com/images/I/315CY467QJL._SL210_.jpg", 56, "子どもたちから絶大な人気を集める名作アニメ「それいけ！アンパンマン」の劇場版第7作。海へやって来たメロンパンナたちは、そこで出会った小さな白馬にマリンと名づけて友達になる。ところが、ばいきんまんが現われてマリンをガイコツ島へと連れ去ってしまう。マリンはガイコツ島にいたメロンパンナの姉ロールパンナと仲良くなるが、ばいきんまんがドクター・ヒヤリと組んでロールパンナから良い心をなくしてしまい……。", "1995-07-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不良少女 魔子", "不良少女 魔子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "日活アクション映画最後のプログラムの１本である。ヤクザ組織の庇護の下で、夏純子扮する魔子の率いるズベ公グループの無軌道ぶりを描く。蔵原惟繕監督の弟・蔵原惟二の監督デビュー作で、宍戸錠がスケ番グループを束ねるヤクザの組長を貫禄十分に演じた。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOUVELLE VAGUE", "ヌーヴェルヴァーグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「勝手にしやがれ」での鮮烈な長編デビュー以来、常に映画と戦い続けてきたJ・L・ゴダール。円熟などという言葉からはほど遠いエネルギッシュな彼が60歳になった1990年に撮り上げたのがこの作品だ。人を喰ったようなタイトルに、ミスマッチとも思えるA・ドロンの起用。だが、作品自体には後の「ゴダールの決別」にも繋がる、深遠なテーマが隠されている。財界の有力者エレーナは、春のある日、ロジェという男と出会い、謎めいた彼に惹かれていく。だが二人がボートで湖に出た時、彼は湖中に落ちてしまう。やがて冬が過ぎ再び春が訪れるとエレーナの前にロジェそっくりの紳士リシャールが現れた……。ビデオ発売時タイトルは「ヌーベルヴァーグ」。", "1990-01-01", "2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASYLUM", "アサイラム 狂人病棟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "精神科医に明かされる患者たちの恐怖談をオムニバス形式で綴る。バラバラ死体の復讐劇、死者をよみがえらせる服の仕立て屋、分裂症患者の殺人への恐怖、そして呪いの殺人人形の怪奇……。最後には、さらに恐ろしい大ドンデン返しが用意されている。", "1972-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST STARFIGHTER", "スター・ファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "カリフォルニアの片田舎の若者がゲーム・マシンで最高新記録を達成し、宇宙から異星人戦士としてスカウトされる。奇想天外なストーリー展開はもちろん、「スター・ウォーズ」などのロン・コッブによる異星の都市のデザインも十分に楽しめる娯楽作。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNION PACIFIC", "大平原", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "西部開拓史を描く大作ドラマ。南北戦争ぼっ発直後、リンカーンの遺命によって大陸を東西で結ぶ鉄道が建設されることに。しかし、建設をめぐって私欲を肥やそうとする財界の大物が建設を妨害する。秩序回復のために現場に派遣された連邦官吏と先住民族の争いに、ロマンスを絡めながらダイナミックに描き出す。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("前科者", "前科者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "若山富三郎が、大阪から東京に乗り込んできた人の良い暴れん坊に扮したアクション。“尻斬れ常”こと杉田常次郎は、その破天荒な暴れっぷりで東京でも一躍名を広めた。だが、人の良い常はズル賢い黒崎兄弟にうまいように利用されてばかり。怒った常は、ダンプもろとも黒崎組に殴り込んでいく。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベオグラード1999", "ベオグラード1999", "", "", "http://image.eiga.k-img.com/images/movie/55760/315x210.jpg?3155", 80, "1970年の三島由紀夫・森田必勝自決事件に衝撃を受けた活動家たちによって創設された新右翼団体「一水会」の活動を追ったドキュメンタリー。1999年、同事務局で働く元恋人を介して木村三浩代表に近づいた金子遊監督は、国際的な反米ネットワークの構築を目指す木村代表の活動を追ってイラクや旧ユーゴスラビアの紛争地域を訪問する。ユーゴスラビア最後の大統領や民族浄化の首謀者、セルビア民兵の指導者らの貴重な姿をカメラに収めていく。", "2010-11-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　HIRED　HAND", "さすらいのカウボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "西部を旅するハリーが放浪生活に疲れ、長年顧みなかった妻子の元に帰ることを決意する。しかし妻は頑なな態度をとり、身勝手なハリーを許そうとしなかった。ハリーは親友アーチとともに納屋で寝起きし、ひたむきに働くことで妻の愛を取り戻そうとする。アメリカン・ニューシネマのスター、P・フォンダが初監督した西部劇。19世紀末の生活様式を再現した丹念な人物描写、V・ジグモンドの幻想的ともいえる詩情豊かな映像美が、さすらいの主人公の悲哀を浮き彫りにする優れた人間ドラマだ。ビデオ、DVDはディレクターズカット版。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼくのおばあちゃん", "ぼくのおばあちゃん", "", "", "http://image.eiga.k-img.com/images/movie/54000/315x210.jpg?2893", 123, "住宅展示場に勤務するトップ・セールスマンの智宏は、幼馴染と結婚し子供にも恵まれるが、忙しい日々に追われ、家庭を省みる余裕を失いかけていた。ある日、智弘は家の建替えを考える家族を担当し、幼い頃に自分の面倒を見てくれた祖母の記憶が蘇ってくる。監督は「VERSUS／ヴァーサス」「あずみ」などで知られる俳優の榊英雄。出演は岡本健一、菅井きん、柳葉敏郎ほか。", "2008-12-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　IRREFUTABLE　TRUTH　ABOUT　DEMONS", "デモンズ2001", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "考古学者ハリーの元に悪魔の集会の模様を収めた怪しげなビデオが届き、彼の周囲で不審な出来事が起こり始める。何者かに妻を殺されたハリーは、敵か味方かわからない美女ペニーに導かれ、命を懸けた闘いに身を投じていく。おぞましいクリーチャーや邪教集団が登場するニュージーランド製のアクション・ホラー。", "2000-01-01", "31", 0, NOW(), NOW());
