INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小巷名流／BACK ALLEY CELEBRITIES", "胡同〈フートン〉模様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "文革の波押し寄せる1970年代四川省の田舎町。工芸美術屋を営むインテリの司馬、古着屋で暴利を得たとされる春娟、酔ってデモ行進を妨害した肉屋の牛三の３人は、ともに文革の犠牲となり学習班に入れられる。日ごろ反目し合っていた３人だが、この逆境のなか次第に協力し合うようになり……。「芙蓉鎮」にも似た文革批判ものだが、本作の方が先に発表された。", "1985-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間標的", "人間標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "原作は藤原審爾の『新宿警察・復讐の論理より』で、井上梅次が脚本と監督を担当している。刑務所を脱獄したある殺し屋が、自分を捕まえた刑事が愛人を横取りしたことを恨み、その復讐に刑事と愛人を殺そうとたくらむ。殺し屋に山崎努、刑事に若林豪、愛人に香山美子がそれぞれ配されている。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Scenes From A Mall", "結婚記念日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "　ロサンゼルスの高級住宅街に暮らす夫婦ニックとデボラ。結婚16年目を迎えたふたりは弁護士と心理学者というインテリであり、理想的なカップルだった。彼らは結婚記念パーティの準備のためにショッピングセンターへ買い物に出かけるが、そこでニックは自らの浮気をデボラに告白。これがきっかけでふたりは大喧嘩に……。中年夫婦の危機を描いたコメディ。ウディ・アレンとベット・ミドラーがショッピングセンターで掛けあいを展開。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("流転の王妃", "流転の王妃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "原作はもと満州国皇帝・溥儀の弟の妻、愛親覚羅浩の自叙伝である。彼女は、1957年冬に愛娘を天城山心中事件で失い、その悲しみを込めて自らの数奇な半生を綴ったのだった。関東軍の策略で、溥傑と無理矢理結婚させられた主人公は、ソ連の参戦や日本の敗戦によって流転の人生を余儀なくされる。監督は女優の田中絹代。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCORE２／ザ・ビッグファイト", "SCORE２／ザ・ビッグファイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "「SCORE」で主演した小沢仁志の監督第３作。遊園地の地下に眠る現金５億円をめぐって、ならず者たちが殺し合いを繰り広げていく。チームを組んだ６人の男女が互いへの不信感から疑心暗鬼になり、さらにはクレイジーな刑事コンビがそこに殴りこみをかけるという展開。架空の町の深夜、という舞台設定で低予算臭さをかわし、スリリングで硬派な密室劇にしている。「SCORE」との関連性は特にない。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RKO 281", "ザ・ディレクター／［市民ケーン］の真実", "", "", "http://ecx.images-amazon.com/images/I/51J4B322T4L._SL210_.jpg", 83, "弱冠24歳にしてハリウッドに乗り込んだ世紀の天才オーソン・ウェルズが、映画史上に輝く傑作「市民ケーン」を完成させた舞台裏を描く実録ドラマ。もともとテレフィーチャーとして作られた作品だが安っぽさはまったくなく、日本では劇場公開された。ハリウッドにも睨みをきかす新聞王ウィリアム・ランドルフ・ハーストの人生に関心を抱き、映画の製作に着手したウェルズが、絶対的な権力者の妨害工作に立ち向かう姿を、簡潔な語り口で描き出す。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("２　MANNER，２　FRAUEN−４　PROBLEME", "逢いたくてヴェニス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "売れない画家の夫ルイスと二人の子供の面倒を見るため、ウエイトレスをしているエバ。彼女はある日、夫が浮気旅行でシャルロットというキャリアウーマンとヴェニスに旅立つのを目撃。怒りに燃えるエバはシャルロットの恋人ニックや二人の子供とともに車でヴェニスに向かうのだった……。ドイツ産のタフでキュートなロード・ムービー。ヴェニスに向かったエバとニックが反発しながらも、次第に惹かれ合っていく様子を生き生きと描き出す。", "1998-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ランニング・オン・エンプティ", "ランニング・オン・エンプティ", "", "", "http://image.eiga.k-img.com/images/movie/55070/315x210.jpg?4749", 80, "異色ロードムービー「まだ楽園」、小林薫＆西島秀俊主演の人間ドラマ「休暇」（脚本）で脚光を浴びた佐向大の商業映画監督デビュー作。定職にも就かずにダラダラと生きているヒデジと同棲しているアザミは、ヒデジへの不満を募らせ、ちょっとした喧嘩をきっかけに狂言誘拐を企てる。周囲の人間に協力を仰ぎ、自分が誘拐されたように見せかけるアザミだったが、思うように事は進まず、予想外の方向へ展開してゆく……。出演は、小林且弥、みひろ、「赤目四十八瀧心中未遂」の大西信満。", "2010-02-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dressed to Kill", "殺しのドレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　夫婦生活に不満を抱えるケイトは精神分析医エリオットのもとへ通っていた。そんな彼女が美術館で出会った男性との情事のあと、剃刀を手にした女性に惨殺される。現場に偶然居合わせたコールガールのリズは、警察から疑いの目を向けられ、街中で危険な目に遭いながらも、ケイトの息子ピーターと組んで真犯人を追うことに……。ニューヨークを舞台に繰り広げられる官能的なサイコ・サスペンス。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅立ち　足寄より", "旅立ち　足寄より", "", "", "http://image.eiga.k-img.com/images/movie/54090/315x210.jpg?3189", 112, "フォークシンガー松山千春が23歳の時に書き下ろした自伝小説「足寄より」を映画化。「リアル鬼ごっこ」「泣きたいときのクスリ」の大東俊介が若き日の松山を演じている。共演に萩原聖人、石黒賢、泉谷しげる、尾野真千子ほか。北海道の足寄から出てきた破天荒なシンガー松山と、松山のデビューに人生を賭けたラジオディレクターの竹田の世代を超えた男同士の深い絆を描く。", "2009-01-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市二段斬り", "座頭市二段斬り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ヤクザ稼業に嫌気がさした座頭市だが、あんまの師匠・彦の市は、悪玉ヤクザ・辰五郎に殺されその娘のお小夜は女郎にされていた……。三木のり平がコミカルな用心棒役で好演するが、加藤武の用心棒役はミス・キャストであった。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女ごころ", "女ごころ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "原節子と森雅之が、結婚して10年目の夫婦の倦怠と愛情の機微を演じる風俗ドラマ。伊曽子は夫・朝吉に若い愛人ができ、自分に友情しか感じないと言われ、“試験別居”をすることを決意するのだが……。引退近くの原節子の生彩のなさとは対照的に、昼は出版社、夜はバーに勤めるドライな女を演じる団令子が魅力的。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オリオンの殺意より・情事の方程式", "オリオンの殺意より・情事の方程式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "横暴な父に耐えきれず母が家出したため、父を憎む少年は、彼を殺す手口を克明に日記に書き込む。しかし、それを読んだ父の後妻とその恋人は、日記に書き込まれている通りに殺人を犯し、罪を少年になすりつけようとする……。根岸吉太郎が監督デビューを飾った、若い才気がきらめく日活ロマンポルノの佳品。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTIMO", "ランジェリー・モデル 危険な戯れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ランジェリー・ファッション・ショーのモデルをしている女性が、ある夜正体不明の男に犯される。屈辱的な行為を受け、涙にくれる彼女だったが、肉体はいつしかその男を求め始めていた……。激しいセックスで“性”に目覚め、堕ちていく女をエロティックに綴ったアダルト・ミステリー。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天然華汁さやか", "天然華汁さやか", "", "", "http://image.eiga.k-img.com/images/movie/54670/315x210.jpg?4161", 70, "清楚な優等生のさやかは、内心ではエッチな妄想を膨らませる巨乳女子高生。スポーツ万能で人気のある伊藤に憧れていたさやかは、ある日、伊藤から試験勉強に誘われたことから果てしない妄想を抱き始め、同じくエッチに憧れる同級生らとサークル「処女組」を結成するが……。「すんドめ」シリーズの宇田川大吾監督が、佐能邦和による同名原作コミックを映画化。", "2009-07-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BARQUERO", "鷲と鷹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "西部の町ロンリー・デルに悪名高き盗賊団レミー一味が現れた。町を荒らしまわるレミーたちに対し、町の開拓団の先頭に立つトラビスが立ち上がる。怒り狂ったレミーはさらに狂暴な仕打ちを町の人々に行い……。冷静沈着なL・V・クリーフと荒くれ者のW・オーツのキャラクターの対照が印象的な西部劇。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Disturbia", "ディスタービア", "", "", "http://image.eiga.k-img.com/images/movie/1770/315x210.jpg?4069", 104, "「トランスフォーマー」のシャイア・ラブーフ主演によるサスペンス・スリラー。父親を自動車事故で亡くした青年ケールは、自暴自棄になって学校で教師を暴行し、3カ月の自宅軟禁処分を受ける。暇を持て余した彼は、友人たちと共にゲーム感覚で近所の覗き見をするように。そんなある日、ケールは血まみれのゴミ袋を引きずる人影を目撃する。時を同じくして、世間では赤毛の女性ばかりが行方不明になる事件が続発しており……。", "2007-11-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SAINT", "セイント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "バットマン、ジム・モリソン、エルヴィス・プレスリーなどを演じてきたV・キルマーが、12人の聖人の名を使い分ける怪盗に扮した冒険アクション。暗い少年時代を経て、世界を股にかける大泥棒になったセイントことサイモン・テンプラー。純粋な心を持つ科学者エマと恋に落ちた彼が、ロシア帝国復活の野望に燃える大富豪トレティアックの陰謀を打ち砕く。元ネタはロジャー・ムーアが1960年代に主演したTVシリーズ『天国野郎／セイント』。野暮ったいオヤジから謎めいた芸術家青年まで、クルクルと変装してみせるV・キルマーの演技が楽しい。その軽いテイストの一方で、いかにも寒そうなロシア・ロケによる映像が重厚なサスペンスを醸し出している。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MASTER OF SHIATSU", "THE MASTER OF SHIATSU", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 13, "指圧の不思議に迫る石井聰亙監督の短編映画。前半は指圧師・浪越徳治郎が白人女性のつぼを攻略していく様子を描いていき、後半はその白人女性が飛翔する様を描いている。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARIE，BAIE　DES　ANGES", "アデュー、ぼくたちの入江", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "紺碧の海に眩い太陽光線が降り注ぐコート・ダジュール。観光客の別荘などに潜り込んで盗みをはたらくジプシーの少年オルソ、米兵たちを挑発して気ままな日々を過ごす美少女マリーは、森や海辺で戯れ合い、つかの間の幸福なときを過ごすが……。住所不定のティーンエイジャーたちが直面する痛ましい現実を描き、きらびやかなリゾート地の暗い一面を露わにした青春映画。ストーリーラインこそ曖昧な作品だが、光に満ちた浜辺の風景を躍動感たっぷりのカメラワークで捉えた映像が、全編に渡って詩的な輝きを放っている。ビデオ発売時タイトルは「渚のロリータ」。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE TRAIN", "離愁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "第二次大戦下、北部フランスの村に住むジュリアンは、戦火を避けるため、故郷を去らねばならなかった。村人たちとともに列車に乗ったジュリアンの家族。女子供たちは客車へ、男たちは貨車へ……。列車の行く先々で避難民が乗り、ドイツ生まれのユダヤ人アンナも乗り込んできた。身動きできない列車の中で男と女は道ならぬ恋と知りつつ愛情を深めていく。やがて引き離される運命の時がくる……。ジョルジュ・シムノンの原作で、戦時下の宿命的な悲しい恋を綴ったドラマ。ドフェール監督は、カメラのほとんどを列車内に置きながら、飽きさせることなく描写。緊張感みなぎる映像も見事である。", "1973-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ПУТЕВКА В ЖИЗНЪ", "人生案内", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ソビエト革命の国内戦争が一段落しても、街には浮浪児があふれ軽犯罪が絶えなかった頃、無学な少年たちが勤労工場で働く喜びに目覚めていく、というお話。ソ連初のトーキー劇映画で、静寂を生かした音の感情表出にすぐれた成果を上げる。素朴で気高い力作。", "1931-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NATIONALE７", "ナショナル７", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "トゥーロン近郊の身体障害者施設で働く美しい女性看護人ジュリは、筋ジストロフィーを患うひねくれ者の障害者ルネの嫌がらせにほとほと手を焼いていた。そんなある日、ルネは性欲を満たしたいという悩みを打ち明け、ジュリは彼を娼婦の元に連れて行くことに。J・P・シナピ監督が車椅子生活を送る身体障害者の切実な問題に目を向けつつ、デジタル・カメラで軽やかに描き上げた笑いと涙のドラマ。お下劣な言動を連発するトラブルメーカー、ルネに扮したO・グルメの演技が圧巻だ。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG MAN ON CAMPUS", "屋根うらの怪人 キャンパスの春は大珍事！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "時計台に住みついている奇人ボブを主人公に、唯一の理解者である教授やボブを邪魔者扱いする人々、そしてあこがれのマドンナが入り乱れるスラップスティック・コメディ。ボブを演じるのはTVシリーズ“M★A★S★H”ほか、コメディ・ショーを多数手掛けるハリウッドでも有名なコメディ作家A・カッツで、本作でも脚本を書いている。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浪曲映画・紺屋高尾", "浪曲映画・紺屋高尾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 59, "花魁道中で初めて見た高尾太夫が忘れられない紺屋久造。いよいよ来年、高尾がやって来ると知って……。不朽の名作浪曲を篠田實の語りにのせて、クレーン撮影を多用し映画化。五月信子ひさびさの銀幕カムバック作。自らの異才が発揮できる場を求めて多くの撮影所を渡り歩いた志波西果、晩年の作品。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガクの冒険", "ガクの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "椎名誠の初監督作で、通常の劇場公開はされず、ホールのみでの全国縦断上映という形で公開された。ふとしたことから、主人と離ればなれになってしまったカヌー犬“ガク”が大自然を背景に、主人を捜し求めながら様々な冒険を繰り広げていく模様をほのぼのとしたタッチで描く。DVDは「ホネ・フィルム式活動寫眞全記録　椎名誠　DVD-BOX」に収録。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A｀ COEUR JOIE", "セシルの歓び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "愛する人がありながら、不思議な魅力を持つ青年と激しい恋に落ちるファッション・モデルの愛の行方を、スコットランドとイギリスの美しい風景の中にあざやかに描く。B・バルドーの魅力をひときわ輝かせているファッションはピエール・カルダン。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONDO CANE NO.２", "続・世界残酷物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「世界残酷物語」で残酷ドキュメンタリーとして名を馳せたG・ヤコペッティが、引き続いて製作した続編。サイゴンの人間バーベキュー、アメリカの女装警官、生きている虫のアクセサリー、アフリカの女奴隷など、世界各地の残酷シーン、奇習風俗などを収めている。", "1963-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アニムスアニマ", "アニムスアニマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "美しくあり続けることにこだわる姉と弟の、恋人同士のような関係を綴った異色のラブ・ストーリー。外の現実を受け入れず、自分たちだけの世界を保持し、ついには一線を越える二人。そんなドラマが、繊細な感性で語られていく。弟役に扮した忍成修吾の、研ぎ澄まされた存在感が印象深い。見かけの美にこだわり続け、優雅に奔放に生きるスイとトキオの姉弟。しかしスイが一人暮らしを始めるとともに、トキオの生活は荒れていく。タイトルの“アニムスアニマ”とは、女性が抱く理想の男性像“アニムス”と男性が抱く理想の女性像“アニマ”を組み合わせた言葉。", "2005-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LORENZO’S OIL", "ロレンツォのオイル 命の詩〈うた〉", "", "", "http://ecx.images-amazon.com/images/I/51ZLSdMliUL._SL210_.jpg", 136, "難病といわれる副腎白質ジストロフィーに冒された息子を救うため、医学の知識もない夫婦がすさまじい努力の末に生み出した薬。“ロレンツォのオイル”と名付けられたこの薬の誕生秘話を、親子の固い絆を中心に演出する。G・ミラーが実話を映画化した、力強い人間ドラマ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("胸より胸に", "胸より胸に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "戦争で肉親を失い、知人の世話で浅草のストリッパーとなった宮島志津子。大学助教授の恋人になるが幻滅を感じて別れ、世話をしてくれた知人と一緒になるも、退廃した生活にいや気がさす。すべての生活に別れを告げ、新たに旅立とうとする志津子だが……。高見順の小説をもとにした、にんじんくらぶ第１回自主作品。フラフラする女を描いた女性映画。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pirates of the Caribbean: At World's End", "パイレーツ・オブ・カリビアン　ワールド・エンド", "", "", "http://ecx.images-amazon.com/images/I/61R-uLvyj7L._SL210_.jpg", 170, "世界的大ヒット作「呪われた海賊たち」（03）、「デッドマンズ・チェスト」（06）に続くジョニー・デップ主演の海洋アドベンチャー3部作完結編。世界の海から海賊を駆逐しようと企む東インド会社のベケット卿が、ジャックの宿敵デイヴィ・ジョーンズと結託。彼らに対抗するには9人の“伝説の海賊”の結集が不可欠だが、その最後の1人はデイヴィ・ジョーンズが放った怪物クラーケンの餌食になったジャックだった……。", "2007-05-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MISSION", "ミッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "18世紀中ごろの南米奥地。ガブリエル神父は、大瀑布イグアスの上流に住むインディオの村へ入り込み、彼らの信頼を得る。一方、村へ人狩りに来ていた奴隷商人メンドーサは、弟を殺した罪滅ぼしのため、ガブリエルとともに伝道に参加、自らに苦行を課し、インディオたちと和解する。だが、当地を征服しようとするスペイン、ポルトガル政府が伝道師たちと対立、大量の軍隊を送り込む……。壮大なイグアスの滝を背景に、利害による戦いを続ける人間たちと、自らの信念に生きる男たちとの葛藤をスケール豊かに描く。「キリング・フィールド」の製作・監督コンビによる感動巨編。デ・ニーロの決死の大滝登りのシーンが圧巻だ。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A STAR IS BORN", "スター誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "北ダコタの寒村に生まれた娘が、映画の都ハリウッドに憧れ、様々な苦労を体験しつつ、ついに大スターになるまでのサクセス・ストーリー。特に結婚後、名声の陰で、酒乱の夫を助けていく“妻”としての生き方が感動を呼ぶ。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zoo", "動物園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("豆富小僧", "豆富小僧", "", "", "http://image.eiga.k-img.com/images/movie/55860/315x210.jpg?4268", 86, "人気作家・京極夏彦の「豆腐小僧双六道中ふりだし」をアニメ映画化。人間と妖怪が一緒に暮らしていた江戸時代。豆腐を持って立っているだけの気弱な妖怪・豆富小僧は、人間を驚かすのが大の苦手だった。父親である妖怪総大将・見越し入道からいつも怒られてばかりのため、指南役のダルマとともに母親探しの旅に出る。豆富小僧の声を深田恭子、ダルマの声を武田鉄矢が担当。「あらしのよるに」の杉井ギサブローが総監督を務める。", "2011-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HOUSE ON CARROLL STREET", "事件を追え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1951年、“赤狩り”の嵐の中で国家の巨大な陰謀に巻き込まれた女性ジャーナリストと、彼女を助けるFBIエージェント。扉の向こうに隠された謎を追う二人のスリリングなロマンティック・サスペンス。名匠P・イエーツ監督が手腕を発揮、K・マクギリスがヒロインを好演。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEMORY RUN", "ショックウェーブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "西暦2015年。ロサンゼルスにその本拠地を置く巨大企業“ライフ・コーポレーション”では、生命の永続をはかるために、老いていく肉体から頭脳を切り離し、若い肉体に移植する脳移植の実験が行われていた。そこへ、殺人事件に巻き込まれ、脳死状態になっている若く美しい女が運び込まれてきた。彼女が再び目覚めた時、彼女は特権階級の人間のために、自らの過去も記憶も存在すら消されてしまい、まったく別の人間に作り変えられていた。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まだ、人間", "まだ、人間", "", "", "http://image.eiga.k-img.com/images/movie/57790/315x210.jpg?4640", 0, "撮影時26歳の新人・松本准平監督が、都会に生きる3人の若者の迷いや苦悩をリアルに描いた人間ドラマ。ある時、ひとりの男が殺され、男が手にしていた金が消えてなくなる。男に不正に金を流していた大手企業のエリートサラリーマン達也は、男の恋人だった女性ルカに出会い、2人は一緒に犯人と金を探す。その過程で達也は大学の後輩リョウと再会。同性愛者のリョウは達也に密かに思いを寄せるが、リョウの知られざる過去が明らかになり……。主演は俳優のほか監督としても活躍する辻岡正人。", "2012-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("植村直己物語", "植村直己物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "モンブラン、エベレスト、北極圏など、冒険家・植村直己の足跡を忠実に追ってロケを敢行。エベレスト登頂から北極点到達まで、自分との戦いとして冒険に懸けた彼の半生を劇的に再現したヒューマン・ドキュメンタリー・タッチのドラマ。西田敏行の執念ともいえる演技が光る。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUST CAUSE", "理由", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "死刑反対論者として知られる法学部教授のポールは、フロリダからやって来た黒人の老婦人に一通の手紙を渡される。それは、８年前少女誘拐の犯人として死刑判決を受けた彼女の孫ボビーが、死刑監房で記した無実の訴えだった。妻の説得もあって事件の再調査に乗り出すことにしたポールは、面会したボビーから意外な事実を聞くのだった……。製作総指揮も手掛けるS・コネリーが、人道主義を貫き、明晰な判断力を発揮するポール役をヒロイックに演じている。次々と予測を裏切り展開する、謎解きの醍醐味あふれるサイコ・スリラー。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ対Gメン・囮", "やくざ対Gメン・囮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "飯干晃一の同名小説を工藤栄一監督、梅宮辰夫、松方弘樹主演で映画化したヤクザ映画。保釈を条件に警察のスパイになったヤクザ組織の幹部と、すご腕の麻薬Gメンが、ヤクザ組織に潜入する。梅宮辰夫と松方弘樹が、暗黒街に生きる男の心意気を熱っぽく演じる。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門捕物帖 伊豆の旅日記", "右門捕物帖 伊豆の旅日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "右門シリーズ、新東宝初登場作品。前作の「右門捕物帖・謎の八十八夜」（1949）で、京都まで出向いた右門。江戸への帰り道に立ち寄った伊豆の温泉で殺人事件が起こり、手がかりは現場に残された矢場の賞品である折鶴。容疑者として、金蔵守護人・堂本軍之助の名前が浮かび上がるが……。撮影中の題名は、「伊豆の緋牡丹」だったという。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OUTSIDERS", "アウトサイダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "オクラホマ州タルサの少年グループ、“グリース”と“ソッシュ”の対立を縦糸に、貧富の差、恋愛、殺人などを散りばめた、「ウエスト・サイド物語」を思わせる構成の青春映画の佳作。当時、“ヤング・アダルト”と名づけられた若手スターたちの豪華な共演も見もの。主題歌はスティーヴィー・ワンダー。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("噛む女", "噛む女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "にっかつロマンポルノが路線変更して“シネ・ロッポニカ”に変わっての第１作。妻子ある男が、小学校の同級生と関係を結んだことから、その女性によって家庭の平和を壊されていくというもの。マイケル・ダグラス主演のアメリカ映画「危険な情事」を日本的にアレンジしたような映画である。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEN WE WERE KINGS", "モハメド・アリ　かけがえのない日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1974年10月30日に行われ、“キンシャサの奇跡”として語り継がれることになるボクシング史上最高級の名勝負を、1990年代の視点から探求したドキュメンタリー。当時のヘビー級チャンピオン、ジョージ・フォアマンはまだ25歳。既にボクサーとしての旬を超えていた32歳のモハメド・アリが、この無敵の若者に挑戦するのは無謀だと誰もが思っていた……。映画は、対照的な両者の姿を丹念に再構築しながら、悪名高きプロモーター、ドン・キングが仕掛け、ザイールの首都で繰り広げられたこのタイトルマッチの“政治的”な意味にも言及していく。また、黒人の地位向上のための広告塔とするべく開催された音楽イベント“ブラック・ウッドストック”のライヴ演奏の模様もふんだんに盛り込まれている。まさに奇跡と呼ぶべき大逆転劇の白熱した舞台裏もさることながら、あくまでも“芸人”としてカメラにサービスし続けるアリの突出したキャラクターが見どころ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The King's Speech", "英国王のスピーチ", "", "", "http://image.eiga.k-img.com/images/movie/55750/315x210.jpg?3642", 118, "現イギリス女王エリザベス2世の父ジョージ6世の伝記をコリン・ファース主演で映画化した歴史ドラマ。きつ音障害を抱えた内気なジョージ6世（ファース）が、言語療法士の助けを借りて障害を克服し、第2次世界大戦開戦にあたって国民を勇気づける見事なスピーチを披露して人心を得るまでを描く。共演にジェフリー・ラッシュ、ヘレナ・ボナム・カーター。監督は「くたばれ！ユナイテッド」のトム・フーパー。第83回米アカデミー賞で作品、監督、主演男優、脚本賞を受賞した。", "2011-02-26", "3,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Profondo rosso", "サスペリア PART2 紅い深淵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「サスペリア」のヒット後公開されたため、“PART２”の邦題がつけられた。猟奇殺人を目撃したマークが、犯人捜査に乗り出す。謎を解く鍵は、子供の歌声と怪しげな絵。熱湯に顔を押しつけるなど残虐な連続殺人事件が、絶妙のカメラワークで鮮烈に描かれる。シトヘス国際恐怖映画祭グランプリ受賞。", "1975-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GROUNDHOG DAY", "恋はデジャ・ブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "２月２日の聖燭祭（グランドホッグ・デイ）の取材でペンシルヴァニアを訪れた、高慢な天気予報官のフィル。ところが、仕事を終えた彼が目覚めるたびに、２月２日が何度もやって来る。フィルはこの時間の迷宮を利用して、憧れの女性を口説こうとするが……。B・マーレーの軽妙な演技が光る、ファンタスティックな恋愛劇。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男性対女性", "男性対女性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "2000年６月に閉鎖された大船撮影所の建設記念映画として製作されたオール・キャスト大作。この種の作品をしばしば任された島津保次郎が、今回も監督を担当している。商社の社長渥美の次男・哲也はフランス帰りで今はレビュー劇場の演出家。踊り子の美代子は彼に恋心を抱いている。一方、劇場経営者の娘・時子は哲也の兄を恋慕っていた。ところが渥美も時子の父も事業に失敗、二つの恋は思わぬ急流に巻き込まれる。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KAENA　LA　PROPHE´TIE", "ケイナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "欧州初のフルCGアニメとなるSFファンタジー。エネルギーである樹液の枯渇ゆえに、滅亡の危機に瀕した故郷を救うために立ち上がった少女の冒険のドラマが繰り広げられる。監督のC・デラポートは和製アニメからの影響を公言する新鋭。「風の谷のナウシカ」を思わせる設定や、ディテールの濃密な描写に注目。突然変異で生まれた樹木“アクシス”はあまりにも巨大化し、惑星アストリアの生命もろとも崩壊の危機に瀕していた。再生の鍵を握る少女ケイナは不思議な夢に導かれ、人々を救う方法を探るため旅立つ……。", "2003-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COOKIE", "私のパパはマフィアの首領〈ドン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "かつては、マフィアのボスだったが、今は仮出所の身の上のしがない父親と、ティーンエイジャーのじゃじゃ馬パンク娘。二人の仲はぎくしゃくしがちだったが、マフィアの権力抗争に巻き込まれたのをきっかけに、二人はがっちりと手を組んで……。女性スタッフの手による映画史上初の“女の子の女の子による女の子のための”マフィア映画。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("J’ENTENDS PLUS LA GUITARE", "ギターはもう聞こえない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1960～70年代を通じて数々の前衛的な作品を発表、非商業的・非妥協的な姿勢から熱烈な信奉者を持つP・ガレルが、1988年に急逝した最初の妻でヴェルヴェット・アンダーグラウンドの歌姫だったニコに捧げた作品。「秘密の子供」「愛の誕生」とともに自伝３部作とされる。最初の妻と別離と再会を繰り返し、新しい相手との情事を重ねながらも、心の中では常に彼女の姿を追い求め続けた主人公の愛の過程が、ゴダールの撮影監督も務めるC・シャンプティエによる光あふれる映像と美しい台詞で綴られる。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Codename: JCVD", "その男ヴァン・ダム", "", "", "http://image.eiga.k-img.com/images/movie/54050/315x210.jpg?3439", 96, "「ユニバーサル・ソルジャー」「タイムコップ」「サドン・デス」で90年代のハリウッドを席巻したアクション・スター、ジャン＝クロード・バン・ダムが、落ち目の自分自身を演じるセルフ・パロディの意欲作。私生活では金銭トラブルや娘の親権争いに悩まされ、主演作をスティーブン・セガールに奪われてギャラも人気も低迷するバン・ダムは、故郷のブリュッセルへと帰るのだが……。", "2008-12-27", "37,77,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANCY PANTS", "腰抜け千両役者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「腰抜け顔役」に続いてボブ・ホープとルシル・ボールが凸凹コンビを組んだコメディ。途中の「腰抜け二挺拳銃」「腰抜け大捕物」ではコンビを組んでいない。レディとしての教養を身につけるためにイギリスに渡った成金の娘と、彼女に気に入られてアメリカに向かう途中、伯爵と間違われてしまった役者との恋を、面白おかしく描いていく。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心", "心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "時代を現代に移し、二人の若い男と一人の娘をめぐる愛の葛藤に焦点を絞り込んだ、新藤兼人監督・脚本による夏目漱石の同名小説の映画化。同じ大学に学ぶKとS。Kは自分が見つけた未亡人と娘の二人暮らしの下宿に、Sも同居させることにするが……。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SELF AND OTHERS", "SELF AND OTHERS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "1983年に36歳の若さで他界した写真家、牛腸（ごちょう）茂雄をめぐるドキュメンタリー。佐藤真監督が何気ない人間の日常のスナップを撮り続けた牛腸の写真や映画フィルムをコラージュしつつ、生前の彼が抱いた心の葛藤、真っ直ぐな情熱を浮き彫りにしていく。いわゆる人物伝や作家論といった説明的な内容からは遠く、どこか夢のような趣のある感覚的な映像世界になっている。また、牛腸がテープレコーダーに吹き込んだ肉声も、異様な迫力をもって観る者の心に迫ってくる。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Elephant", "エレファント", "", "", "http://image.eiga.k-img.com/images/movie/1380/315x210.jpg?3864", 81, "99年のコロンバイン高校での銃乱射事件を、初期には「ドラッグストア・カウボーイ」や「マイ・プライベート・アイダホ」を撮っていた「グッド・ウィル・ハンティング／旅立ち」のガス・バン・サント監督が描く。撮影は「裏切り者」「ゲーム」のハリス・サヴィデス。生徒役の出演者はみな俳優ではなく実際の高校生で、セリフは彼らが即興で話すという演出。03年のカンヌ映画祭でパルムドールと監督賞を史上初のダブル受賞。", "2004-03-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISSION OF JUSTICE", "アンダーカバー 炎の復讐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「アンダーカバー」に続き、J・ウィンコットが、その鍛えあげられた肉体と技を披露する格闘アクション。カートとリンは、ロサンゼルス警察でも検挙率No.1の警官だった。一方、警察に対抗して“正義の使節団”なる集団が現れ、犯罪者たちを次々と消していった。上司と衝突してバッジを返したカートは、新しい人生を始めようと旧友セドリックを訪ねる。ところがセドリックは“正義の使節団”によって殺されていた。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AVENGING　ANGELO", "シルベスター・スタローン／ザ・ボディガード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "S・スタローン主演によるサスペンス。彼扮するマフィアの護衛が、ボスの遺言に従い、その娘を守ることに。世間知らずの彼女に振り回されつつ奔走する、彼の活躍がスリリングかつユーモラスに描かれる。タフで人間味もある主人公を、スタローンが軽妙に演じる。共演のM・ストウとの、息もピッタリの掛け合いも見もの。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春来る鬼", "春来る鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "遥か昔、相思相愛だったが身分の違いのために結ばれず、駆け落ちの果て流れ着いた島で、岬の長老たちに様々な試練を強いられた青年漁師とその恋人。原作を一読してその世界に魅了され、何度も企画を出し続けた“マイトガイ”小林旭の念願の一作。空想の世界を借りて日本古来の慣習や生命の尊厳を訴える。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春の夢いまいづこ", "青春の夢いまいづこ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "学生時代に親友同士だった哲夫と一郎は、社会に出ると皮肉なことに雇用主とそこで働く使用人という立場におかれる。やがて二人は一人の娘をめぐって三角関係に落ちるが……。小津得意の“カレッジ・コメディ”の味を残しながら、哲夫が一郎を殴り続ける激烈なシーンもあり、小津の転換期にあたる作品である。", "1932-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILHOUETTE", "シルエット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "女性弁護士のサマンサは、ある夜、友人の娼婦が殺し屋に追われているところを助ける。殺し屋二人は、娼婦といた男を射殺して逃走した。殺された男は、組織を裏切り、米ソ間の冷戦を再発させる鍵となるブローチを持ち出していた。サマンサは、自ら娼婦になりすまし裏の世界へ入り、単独捜査を始める。TVドラマ『ダイナスティ』に出演していたT・スコギンスが初ヌードを披露し、大胆な演技をみせる。", "1991-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎物語", "次郎物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "母親の体が弱かったために田舎の家に預けられ、自然のなかでのびのびと育てられた主人公・次郎。６歳の時、躾が厳しく士族の格式を守る本家へ連れ戻されるが、すでに母と子の間に愛情らしきものはなかった……。親子のふれあいを描いた古典的名作の映画化。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん　のび太のドラビアンナイト", "ドラえもん　のび太のドラビアンナイト", "", "", "http://ecx.images-amazon.com/images/I/31GZRPVJ5ZL._SL210_.jpg", 100, "絵本の中に入って遊んでいたのび太たちだったが、しずかだけが『アラビアンナイト』の中に閉じ込められてしまう。彼女を救うため時空を越えて西暦794年のアラビアにたどり着いたドラえもんたち。しかし四次元ポケットを盗賊に盗まれてしまう。やがて４人はシンドバッドと知り合うが、シンドバッドが盗賊にさらわれて……。", "1991-03-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("流れる", "流れる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "幸田文の同名小説を映画化した成瀬巳喜男監督の名作。「めし」「晩菊」の名コンビ、田中澄江と井手俊郎が脚色を担当、東京の下町にある芸者置屋を舞台に、住み込みの女中の目を通して花柳界に生きる女たちの姿を描いている。出演は、18年振りに復帰した日本映画草創期の大スター・栗島すみ子をはじめ、女中役の田中絹代、女将の山田五十鈴とその娘・高峰秀子、そして芸者を演じる杉村春子、岡田茉莉子、中北千枝子とまさにそうそうたる顔ぶれ。こうした大女優の競演に際し、成瀬の演出は、派手で大仰な芝居よりはむしろ抑えた演技を要求しており、それにより各人の持ち味が十二分に引き出されて緊張感を高めている。衣装や小道具にも細かい配慮がなされ、女優たちの魅力と相まって実に印象深い。女優の芸と個性がぶつかり合い絡み合った、白粉の匂いにむせかえるような、まさしく女の世界である。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRINCESS CARABOO", "プリンセス・カラブー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "19世紀初頭、宿無しは絞首刑にされるという、非人道的な法律があった頃のイングランドの片田舎に宿無しの美女が迷い込んでくる。裁判で危うく刑に処せられそうなところ、彼女は人道家のウォラル夫人に助けられる。英語もわからず“カラブー”と名づけられた彼女だったが、ウォラル夫人に引き取られ、エキセントリックなふるまいと美しさでたちまち人気者に。アジアから来たプリンセスとまで噂され、社交界デビューまでも果たす。しかしカラブーは、本当はメアリーという名のイギリス人の孤児だった。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Other Boleyn Girl", "ブーリン家の姉妹", "", "", "http://image.eiga.k-img.com/images/movie/53510/315x210.jpg?2496", 115, "16世紀イングランド。新興貴族のブーリン家は、世継ぎに恵まれない国王ヘンリー8世に長女アンを差し出す。彼女が世継ぎの男児を産めば、一族は富と名声が得られる。しかし、王が目をかけたのはアンの妹メアリーで……。後に英国黄金時代を築くエリザベス1世の母となるアン・ブーリンと、その妹メアリーの王の寵愛を巡る確執を描く歴史劇。姉にナタリー・ポートマン、妹にスカーレット・ヨハンソンと若手トップ女優が競演。脚本は「クィーン」のピーター・モーガン。", "2008-10-25", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ALAMO", "アラモ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "J・ウェイン製作・監督・主演によるアラモ砦の攻防戦を描いたスペクタクル西部劇。1836年、メキシコの独裁者サンタ・アナの圧政に反乱を起こしたテキサス独立の義勇軍は、アラモ砦に立てこもり、13日間の壮絶な戦いを繰り広げる。初公開時の上映時間は199分。ジョン・フォードが監修にあたっている。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAUNTED", "月下の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "イギリスでベストセラーとなったJ・ハーバートの同名小説を、F・F・コッポラが製作総指揮をし、L・ギルバートが映画化。ゴシック・ロマンス特有の怖さとせつなくも美しいラブ・ストーリーが融合されている。超心理学教授のデビッドは、霊の存在を信じず、超自然現象に苦しむ人々を助ける活動をしている。デビッドはウェッブという老婆から、“屋敷に亡霊がいる。助けてほしい”と手紙をもらう。早速、出かけたデビッドを迎えたのは若く美しいクリスティーナだった。彼女の話によると、ウェッブはクリスティーナとその二人の兄の乳母テスであり、３人の母はすでに死んでいた。デビッドとクリスティーナはすぐに惹かれ合い、やがて結ばれる。その直後から屋敷では不可解なことが次々と起こり、デビッドの身にも危険が迫る。ビデオタイトルは「ホーンテッド」。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAVE", "デーヴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "大統領に瓜ふたつのデーヴは1晩だけの約束で影武者に雇われる。ところがその夜、大統領が倒れてしまい、契約は延長されることに。側近たちの言うまま大統領を演じていたデーブだが、ファーストレディ、エレンの福祉への思いを知り、政治改革に乗り出していくが……。ラスト、代役が元の自分にどうやってすりかわるかが見せ所。痛快なポリティカル・コメディの傑作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOPLESS　WOMEN　TALK　ABOUT　THEIR　LIVES", "トップレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "１話４分間というユニークな形態で放送され、ニュージーランドの若者が熱狂したTVシリーズに再編集を施した劇場版。20代の男女が味わう悲喜こもごもを、ライブ感あふれる演出で連鎖的に描いていく。主演女優D・コーマックが実際に妊娠したままヒロインを演じたのも話題となった。自分が妊娠していると気付いたリズが、恋人ジェフに別の女がいると知り別れる。仕事もクビになり、とことんついてない彼女の前に、元ボーイフレンドのニールが父親候補として名乗り出る。そんなとき彼女の親友プルー、ノイローゼ気味の脚本家アントらにもトラブルが起こり……。", "1997-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("転校生", "転校生", "", "", "http://ecx.images-amazon.com/images/I/5176T1YN89L._SL210_.jpg", 113, "監督・大林宣彦の故郷を舞台とした、いわゆる“尾道もの”の長編劇映画第１作。山中恒の原作『おれがあいつであいつがおれで』を「ゴキブリ刑事」「時をかける少女」などの剣持亘が脚色している。監督の大林宣彦は自主製作映画からコマーシャル映画の世界を経由して長編劇映画を手掛けるに至るという、監督コースを歩んできた。いわばのちの自主映画出身の若手監督たちの先駆者といえる。勉強よりもいたずらが大好きなガキ大将中学生・斎藤一夫のクラスに斎藤一美という幼なじみの女の子が転校してくるところから始まる。ある日、一夫と一美は神社の石段を転げ落ち、これがもとで二人の身体が入れ替わってしまう。おかげでガキ大将の一夫は女っぽくなるし、一美は荒っぽくなる。本人たちはもちろん、両親や仲間たちまでも巻き込んで大騒動という傑作ユーモア思春期編。TV『３年B組金八先生』出身の小林聡美の本格的映画デビュー作でもある。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("縄文うるしの世界", "縄文うるしの世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "記録映画作家、飯塚俊男が青森県三内丸山遺跡を取材した、「木と土の王国」「一万年王国」に続く“縄文映画”シリーズの第３作。遺跡から縄文時代の漆器が出土したことから、カメラは福井の鳥浜貝塚をはじめ、北陸、東北の遺跡を訪ねる。その過程で、縄文人たちの樹木に関する知識の深さや彼らの生活様式が明らかになっていく。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("覇王別姫／FAREWELL TO MY CONCUBINE", "さらば、わが愛／覇王別姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 172, "京劇の十八番『覇王別姫』を得意演目とする二人の京劇俳優の生きざまに中国の近・現代史を重ね合わせた壮大な物語を描き、記録的な大ヒットとなった一編。1925年、９歳の少年、小豆子が娼婦の母親に連れられ、京劇養成所“喜福成”へとやって来た。いじめられっ子の彼は、唯一かばってくれた少年、小石頭と仲良くなる。やがて二人は成長し、女性的な小豆子は女形に、男性的な小石頭は男役に決められ、それぞれ程蝶衣、段小樓と名乗り有名スターとなっていった。1937年、日本との戦いが深まるなかで段小樓は娼婦の菊仙と出会い、結婚。段小樓に人知れぬ思いを抱いていた程蝶衣は、二度と共演しないと捨てぜりふを吐き、彼の元を去った。1949年、程蝶衣は街でスイカ売りをしていた段小樓と再会し、和解。再び舞台に立つが、京劇は変革を求められ……。同性愛的なニュアンスも含め、中国近・現代史のタブーに挑んだC・カイコー監督作。程蝶衣を演じたL・チャンの妖艶さも話題になった。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("荒野のダッチワイフ", "荒野のダッチワイフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "「裏切りの季節」に続く大和屋竺の監督第２作。風采のあがらぬ殺し屋が町の有力者に雇われる。狙う相手は殺し屋自身の女を殺した仇でもあった。大和屋はこの作品に先立ち日活で具流八郎の名で「殺しの烙印」（鈴木清順監督）のシナリオを書いており、この作品における殺し屋のシュールな妄想の連続は明らかに「殺しの烙印」の延長線上にある。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("When the Road Bends: Tales of a Gypsy Caravan", "ジプシー・キャラバン", "", "", "http://image.eiga.k-img.com/images/movie/53130/315x210.jpg?1945", 115, "インドに起源を持つ移動型民族ロマ（ジプシー）。11世紀頃から世界各地に散らばった彼らは、数世紀に及ぶ迫害の中で、圧倒的なパワーを持つ音楽を生み出してきた。そんなジプシー音楽をルーツとするスペイン、ルーマニア、マケドニア、インドの4カ国5バンドが、6週間かけて北米を廻った“ジプシー・キャラバン”に密着。さらにアーティストたちそれぞれの故郷を訪れ、ロマとその音楽の魅力を探るドキュメンタリー。", "2008-01-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEATHER JACKTES", "レザー・ジャケット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "レザー・ジャケットを着て街を闊歩するストリート・ギャングたち。かつてはその一人だったミッキーも、今はマジメになり、クローディアとの結婚を控えていた。ところがミッキーの昔の仲間ダブスが、対立するベトナム人グループを襲撃し、ミッキーとクローディアもその争いに巻き込まれる。そんな中、ミッキーは、親友であるダブスが実はクローディアと昔関係があったことを知る……。ストリート・ギャングに命を狙われ、逃亡する男女の姿をハード・タッチで描いたサスペンス。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひき逃げファミリー", "ひき逃げファミリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ひき逃げ事故を起こした中堅サラリーマンとその家族が、ふだんの不響和音もなんのその証拠いん滅のために一致団結し、自宅の居間で自家用車を解体するハメになる……という奇想天外なドラマ。「視姦白昼夢」などで知られるピンクの鬼才・水谷俊之が、初めて一般向け劇場用映画を監督したもの。中尾ミエが、いざという時にふんばる“日本の母”を体現するほか、シンボリックな家族たちの設定が心憎い。シチュエーション・コメディにこだわるあまり中盤もたつくのは残念だが、全体として手堅くまとまっているのはやはり監督の力量であろう。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ENTITY", "エンティティー 霊体", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "B・ハーシーが色情霊にレイプされるというオカルト佳作で、実話がベースになっている。対策にあたった超心理学者は、現れた霊体を、液体ヘリウムで凍結しようと試みる。見えぬ霊体にもまれる乳房の揺れ、ベッドでのたうつさまなどがエロティック。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("壬生義士伝", "壬生義士伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "浅田次郎の同名小説を、中井貴一と佐藤浩市の顔合わせで映画化。新選組に参加した名もない武士のひたむきで壮絶な生き様を、エモーショナルなドラマの中に感動的に描き出す。新選組に盛岡南部藩出身の吉村貫一郎が入隊した。何かと金に意地汚い吉村に、新選組のはぐれ者、斎藤一は最初は嫌悪感を抱いたが、次第に惹かれていく。ところが、新選組には終わりのときが刻一刻と近づいていて……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雨の轍", "雨の轍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "昔話『狐の嫁入り』をモチーフにした異色の幻想話。サラリーマンの船井は、東京近郊のマンションに妻と息子とともに引っ越してきた。隣室の営業マン、西村から『狐の嫁入り』を聞かされた３人は、ある日滝に遊びに行く。息子の昌平は、そこで死んでしまった愛犬ピーターを目撃。滝の向こう側には“雨の向こうの世界”がある。そう気づいた西村は、事故死した兄と出会おうとするのだが……。劇団“新宿梁山泊”の俳優たちが出演。演劇的なタッチが次第に変貌していく様が、なんとも奇怪な印象を持たせる１本だ。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOCKING DARK", "エイリアンネーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "有毒ガスに覆われ、死の町と化した近未来のベニス。環境調査をしていた研究所からSOSが発信され、救出に向かった海兵隊特捜班の隊員たちは、そこで遺伝子操作で人間をもとに生み出された恐ろしい怪物を目の当たりにする……。「エイリアン２」「ターミネーター」のイタリア製焼き直しバージョンといった趣きの１本。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LET IT BE", "ビートルズ／レット・イット・ビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ビートルズ最後の主演映画で、アルバム『レット・イット・ビー』の製作風景を追ったドキュメンタリー。“僕がビートルズを去ったんじゃない。ビートルズがビートルズを去ったんだ”というポールの名言通り、４人の心がすでに離ればなれになっている姿を奇しくも浮かび上がらせる結果になった。ジョンがヨーコと踊る『アイ・ミー・マイン』、ポールの『ザ・ロング・アンド・ワインディングロード』なども印象深いが、圧巻はラストのアップル社屋上での演奏シーン。真っ赤なレインコートのリンゴ、髪を風になびかせながら『ゲット・バック』を歌うポール、集まってくる人々。その偉大さをとどめておくにふさわしい結末である。ジョン、ポール、ジョージ、リンゴ、そしてヨーコの５人の人間関係の微妙な揺れが見えるのもおもしろい。", "1970-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Repo! The Genetic Opera", "REPO！レポ", "", "", "http://image.eiga.k-img.com/images/movie/54220/315x210.jpg?3271", 98, "「ソウ」シリーズの新鋭ダーレン・リン・バウズマン監督によるブラック・ファンタジー。主演は「スパイキッズ」のアレクサ・ベガ、共演に歌手のサラ・ブライトマン、パリス・ヒルトンら。総合音楽プロデュースをX JAPANのYOSHIKIが担当する。2056年、奇病の流行により人類は滅亡の危機にあった。17歳のシャイロは先天性の難病のため外界から隔離されて暮らしていたが、様々な思いを胸に禁断の外の世界へと足を踏み出す。", "2009-03-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あばよダチ公", "あばよダチ公", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "日活が、1970年代初頭のニュー・アクション復活をもくろんで作った青春アクション。松田優作をはじめとするチンピラ４人組と加藤小夜子演じる女の５人が、珍道中の果てに暴力団や警察相手に抗争するという内容。澤田幸弘監督に往年のキレはないが、役者陣に味がある。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ME´LODIE EN SOUS−SOL", "地下室のメロディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "名匠ヴェルヌイユ監督が、ギャバンとドロンの二大スターを共演させて発表した犯罪アクション映画。５年の刑期を終えて出所した老ギャングのシャルルは、再びカジノの現金を襲う計画を立て、新しい仲間の青年フランシスと組み、見事、強奪に成功する。だが犯行は意外なところから露呈し……。ヴェルヌイユ監督は二大スターの対照的な性格描写の面白さで物語を進行させ、モノクロのシャープな映像とファンキーなモダンジャズの効果的な使用によって、この映画にさらに磨きをかけ、一級の娯楽作品に仕上げている。以前、コンピュータ処理によりカラー化された、アメリカ公開バージョンのビデオが発売された。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("貞子3D", "貞子3D", "", "", "http://image.eiga.k-img.com/images/movie/56030/315x210.jpg?1666", 0, "Jホラーブームの火付け役となりハリウッドリメイクもされた「リング」（1998）の名物キャラクター・貞子がもたらす新たな恐怖を3Dで描く。教師の茜が勤務する女子高で、インターネットで配信された自殺の生中継映像が噂になる。その映像を見た各地の人々が同時期に死を遂げ、茜の教え子の典子もまた不可解な死を遂げてしまい……。「リング」原作者の鈴木光司がオリジナルストーリーを考案。石原さとみがホラー映画初主演を務め、茜の恋人役で瀬戸康史が共演。監督は「ハンサム★スーツ」「高校デビュー」の英勉。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("buy a suit スーツを買う", "buy a suit スーツを買う", "", "", "http://image.eiga.k-img.com/images/movie/54200/315x210.jpg?3253", 47, "失踪した兄・ヒサシを捜しに大阪から上京したユキは、ヒサシからの葉書の住所をもとに不慣れな土地・東京の街を歩く。吾妻橋のたもとにたどり着いたユキは、隅田川に添って点々並ぶ段ボールの家から出てくる兄ヒサシを見つける。「東京夜曲」「トニー滝谷」などで知られる名匠・市川準が仕事仲間とともに作り上げた自主制作映画で、2008年9月に急逝した市川監督の遺作となった。", "2009-04-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLORIDA STARAITS", "フロリダ・ストレイツ ～脱出海峡～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "20年間に及ぶキューバでの刑務所暮らしのあと、アメリカへ追放された男が命を懸けて再び厳戒のキューバに潜入。一つは残してきた恋人に会うため、もう一つはひそかに隠した200万ドルの金貨を手に入れるために……。軍隊と山賊の待ち受けるなか、男たちの熱いロマンと友情が展開。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春婦伝", "春婦伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "同じ原作を映画化した谷口千吉の「暁の脱走」と比較され、公開当時は失敗作の烙印を押された不運な作品だが、時を経て清順再評価の気運とともに、この映画も見直されてきた。撮影当時は中国と国交がなく、御殿場の広野に中国の山脈を合成したという清順映画の中で珍しく特撮を多用した作品。激戦下で傷ついた男と塹壕の中で横たわり、空を見上げる女の目に砲火が花火のように美しく映えるシーンに、清順独特の映像美が見られる。第二次大戦中の天津、気性の激しい売春婦の春美は、副官の当番兵をしている三上に惹かれる。副官に言いなりの三上を反抗させようと春美は三上をけしかけ、二人は肉体を交わし合う。だが、それを知った副官は三上を最前線へと送った。一時は八路軍に捕えられた三上は無事に帰隊するが、軍法会議にかけられ……。宣伝では膨大な製作費を使った大野心作とうたわれたが、その実「ビルマの堅琴」（1956）の小道具の残り物を使うなど苦労があった。“日活流れ者”の清順らしいエピソードだ。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NATIONAL LAMPOON’S ANIMAL HOUSE", "アニマル・ハウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1962年のカレッジを舞台に、劣等生グループの無茶苦茶な行動ぶりを描いたコメディ。監督のJ・ランディスは、前作「ケンタッキー・フライド・ムービー」で注目を浴び、本品はわずか36日間で撮り上げ、しかも、当時の全米歴代のヒットランクの上位になるほどのヒットを記録。名作「アメリカン・グラフィティ」のパロディーともいわれる。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朝霧街道", "朝霧街道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ちょっとしたいざこざから御用に追われて江戸を逃れた三日月安は、故郷の九十九里浜に帰って来た。が、浜をとりしきっていた利兵衛親分は死に、腹黒い仁左衛門が権力を握っていた。酒びたりの弟分・与助を立ち直らせると、安は浜のために立ちあがる……。加藤泰がいきいきと描く得意の任侠もの時代劇。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仁寺洞スキャンダル　神の手を持つ男", "仁寺洞スキャンダル　神の手を持つ男", "", "", "http://image.eiga.k-img.com/images/movie/55420/315x210.jpg?3484", 109, "室町時代、日本の水墨画にも多大な影響を与えた幻の名画「碧眼図（ビョガンド）」をめぐるサスペンス。「Mr.ソクラテス」「花影」のキム・レウォンが主演を務める。仁寺洞（インサドン）の骨董街を牛耳るギャラリーのオーナー、テジンは、朝鮮王朝期を代表する水墨画家・安堅（アン・ギョン）の幻の名画「碧眼図」を手に入れる。神の手を持つと呼ばれる修復士ガンジュンが雇われるが、そこにはさまざまな人々の思惑が交錯していた。", "2010-06-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FILLE SEULE", "シングル・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "妊娠と就職に直面した少女が、思春期から大人へと成長していく姿をリアルタイムの進行で捉えた青春映画。サン・ラザール駅前の大きなホテルにルーム・サービス係として就職が決まった17歳のヴァレリー。初出勤の早朝、彼女は恋人のレミにカフェで、妊娠していることを告げる。突然の告白に戸惑うレミ。二人はお互いの意思を固め、１時間後に落ち合う約束をするが、ヴァレリーはレミの答えにかかわらず、子供を生む決心をしていた……。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛情夢幻号／FASCINATION　AMOUR", "愛は波の彼方に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "豪華客船を舞台にした恋愛ドラマ。アンディ・ラウと石田ひかりが惹かれ合う男女に扮して、すれ違いの恋を演じている。プレイボーイである富豪の御曹司アルバートは、意に沿わない相手と船上結婚を強いられ大弱り。そんな彼の前に勝ち気な女性サンディが出現。彼女に成金趣味を非難されながらも、そんな率直な態度にアルバートは心惹かれていく。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ugly Truth", "男と女の不都合な真実", "", "", "http://image.eiga.k-img.com/images/movie/54430/315x210.jpg?4373", 95, "「オペラ座の怪人」のジェラルド・バトラー＆「幸せになるための27のドレス」のキャサリン・ハイグル主演で描く大人のラブコメディ。アビーは美人の敏腕TVプロデューサーだが、恋愛はご無沙汰。理想通りの男性コリンに出会うも、なかなか関係を進展できずにいた。そんな彼女は、下品なトークで高視聴率を稼ぐ恋愛パーソナリティ、マイクと仕事をすることになり……。監督は「キューティ・ブロンド」のロバート・ルケティック。", "2009-09-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Long Goodbye", "ロング・グッドバイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "　私立探偵フィリップ・マーロウはメキシコに行くという友人ハリーを車で送った翌日、警察に連行される。ハリーの妻が殺害されたのだ。しかしメキシコでハリーが自殺したことが伝えられ、マーロウは釈放される。その後、マーロウはアイリーンという女性から作家である夫を捜してほしいと依頼されるが……。レイモンド・チャンドラーの推理小説を、鬼才ロバート・アルトマンが映画化。監督アルトマンとエリオット・グールドのコンビが独特のマーロウ像を生み出している。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOHNNY STECCHINO", "ジョニーの事情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "パーティーでは仲間はずれにされ、女にはもちろんもてない。そんなどこといって取り柄のない男が、組織から命を狙われているマフィアの男に瓜二つだったがため、替え玉にされ、命を狙われるはめに陥る。ジム・ジャームッシュ監督の「ダウン・バイ・ロー」でひょうひょうとした演技を見せたR・ベニーニの監督・主演作。ビシッとキメたマフィアの男と、さえない中年男の二役を絶妙の演技で演じ分けている。ジャームッシュ作品にも通底する、オフビート感覚にあふれたカルト・コメディ。", "1992-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("20世紀少年　最終章　ぼくらの旗", "20世紀少年　最終章　ぼくらの旗", "", "", "http://image.eiga.k-img.com/images/movie/54310/315x210.jpg?2761", 155, "浦沢直樹の同名人気コミックを全3部作で映画化した超大作の最終章。「世界大統領」として君臨する“ともだち”に支配された西暦2019年。殺人ウイルスが蔓延した東京は巨大な壁によって分断され、市民の生活は制限されていた。そんな中、“ともだち”が「世界の終わり」を予言。地下に潜りレジスタンス活動を続けていたカンナは市民に武装蜂起を呼びかけるが、そこへ矢吹丈と名乗る謎の男が現れ……。監督は堤幸彦。出演は唐沢寿明、豊川悦司、平愛梨、常盤貴子、香川照之ほか。", "2009-08-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("走れジュリー", "走れジュリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "資金難で経営不振に陥り、動物たちに食物も与えられない動物園。その窮状を知った小学生の児童たちと獣医が、動物を救おうと情熱を傾けて活動する姿を描いた感動的な物語。ジュリーを演じる（？）チンパンジーのほか、犬やシカ、カラスなど約200匹にも及ぶ動物たちが登場する。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("罵詈雑言", "罵詈雑言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「島国根性」「ザザンボ」などの異色作を放ってきた福島在住の異才、渡邊文樹の問題作。実際に起きた事件を元に、原発事故に絡んだ選挙問題や警察と役場の癒着の真相に迫る。チェルノブイリ原子力発電所で故障が発生、運転停止を聞き入れてもらえなかった責任者が抗議の投身自殺をした。現場を一人の青年が目撃していたが、彼はある女教員宅のくみ取り式の狭い便槽の中で腐乱死体となって発見される。のぞき目的の過誤死と片付けられるが、被害者の家族は司法解剖へ持ち込む。果たして本当の死因は何なのか？　ナゾがうず巻く中、一人の男が真相究明に乗り出す。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROKEN BLOSSOMS", "散り行く花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "ロンドンの波止場町で酒浸りの生活を送る元ボクサーのバロウズは、一人娘のルーシーをうさ晴らしのために殴り続けている愚劣な男。それでも献心的に父に仕える少女はある日、チェンという中国人青年と知り合いになる。不幸な境遇に同情した彼は、虐待に耐えかねて家を飛び出した彼女をかくまう。娘が初めて知った人の優しさ。だが幸せはつかの間、チェンの留守に父が現れ、強引にルーシーを家に連れ帰った。有色人種の情を受けたと娘を責めながら激昂した彼は、ついに彼女をぼろ布のように打ち殺してしまう。一方、すべてを知ったチェンはバロウズを銃で撃ち殺し、自らも命を絶つ……。“映画の父”グリフィスが監督した胸の震えるような無声映画で、L・ギッシュのはかなさ、美しさが際立つ傑作。ゴダールの「勝手にしやがれ」に引用されるなど、後進に与えた影響は計り知れない。", "1919-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リアルメン・スタンド！", "リアルメン・スタンド！", "", "", "http://image.eiga.k-img.com/images/movie/33510/315x210.jpg?4533", 80, "「溺れる魚」「アウトリミット」などで知られる小説家の戸梶圭太が監督、脚本、撮影、音楽の他プロデュースまで担当した1人5役のコメディ。ひょんな事から、場末の個室ビデオ屋“リアルメン”の店長になった田中だが、そこは浮浪者、キリスト、神父、全裸のAV評論家、自主製作AV売り込み女、ルワンダ料理屋の店長など個性溢れる珍客ばかりが集まる奇妙奇天烈な個室ビデオ屋だった……。", "2006-07-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BREAK", "ザ・ブレイク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「クライング・ゲーム」のS・レイが、再びIRAテロリストに扮したサスペンス。彼のストイックなキャラクターが、ドラマにハードボイルドな味わいを添えている。脱獄したIRA兵士ダウドは過去を捨てて、N.Y.へと渡る。そんな彼の前に、南米の革命運動家モニカが出現。彼女と恋に落ちたダウドは、眠っていた闘争心を燃え上がらせ、モニカが加担している暗殺計画の指揮を執るが……。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("婉という女", "婉という女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "社会派の巨匠・今井正には珍しく、異常な状況下におかれた女の“性”にメスを入れた時代劇。家老であった父親の失脚と死により、狭い獄舎に幽閉された一家の物語。年頃を迎えるとともに苦悩となって現れる性的欲求や、外界への募る思いが力強く表現されている。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うつしみ", "うつしみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "街頭パフォーマンス『東京ガガガ』で知られる園子温監督の話題作。“うつしみとは現身、現世（うつしよ）の身の現身なり”との解釈のもと、写真家・荒木経惟の撮影現場やデザイナー・荒川眞一郎のパリ・コレ、舞踏家・麿赤児の活動ドキュメンタリーと主人公の女子高生が男を求めてただひたすら疾走する、青春ドラマが同時進行で繰り広げられる。主人公の女子高生を劇団“ナイロン100℃”の澤田由起子が演じる。衣装はシンイチロウアラカワが担当。園子温のパワフルな演出にはただただ脱帽だ。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("盗まれた欲情", "盗まれた欲情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "今東光原作の『テント劇場』を、今村昌平が第１回監督作品として映画化した風俗ドラマ。大阪は河内地方のドサまわり一座。大学出の文芸部員・岡田信吉は小難しい理論をたてにこの世界に飛び込んできたが、理想と現実のギャップはあまりにも大きかった……。今村昌平の演出には、新人にみられがちな緊張がみじんもみられず、ひとクセもふたクセもありそうな連中がのさばる、この人間のごった煮のような風俗ドラマを完璧に仕切り、すでに大家の風格さえ漂わせている。特に大騒ぎで村を去っていく旅役者の一座が画面狭しとひしめくラストシーンには、今村昌平の持ち味である土着のバイタリティーが見事に表われている。DVDは「今村昌平 日活作品全集(1)」に収録。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Astro Boy", "ATOM", "", "", "http://image.eiga.k-img.com/images/movie/53220/315x210.jpg?4112", 0, "手塚治虫の「鉄腕アトム」を、香港やロサンゼルスを拠点とするイマジ・スタジオがフルCGアニメーション化。事故死した息子に似せて、科学者テンマ博士により生み出されたロボットのアトムは、本物の息子との違いに戸惑ったテンマ博士に拒絶されるが、アトムの動力源ブルー・コアを狙う悪の大統領と戦うため、再び父のもとに舞い戻る。フレディ・ハイモア、ニコラス・ケイジらが声優を務める。日本語吹替え版は上戸彩、役所広司ら。", "2009-10-10", "1,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鴛鴦〈おしどり〉歌合戦〈デジタルリマスター版〉", "鴛鴦〈おしどり〉歌合戦〈デジタルリマスター版〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "サイレント映画からトーキーへの移行期である30年代に製作された、伝説のオペレッタ映画。セリフの代わりに歌がフィーチャーされ、江戸時代の恋騒動が陽気なムードの中に繰り広げられる。名匠、マキノ雅広によるアップテンポな演出、志村喬やディック・ミネの見事な歌いっぷりが見どころだ。貧乏長屋に住む浪人、礼三郎に思いを寄せる３人の娘たち。その娘の一人に若殿様が恋したことから、恋模様はややこしいことになり……。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("反則王", "反則王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「JSA」に次ぎ、韓国で2000年の観客動員数、年間第二位の大ヒットを記録した痛快プロレス・コメディ。昼はうだつのあがらない銀行員、夜は反則レスラーという二つの顔を持つ男の奮闘をパワフルに活写。ダメダメサラリーマンが厳しいトレーニングの末、悪役プロレスラーとして大成功、というスポ根ものの王道に乗せてサラリーマンの悲哀も滑稽に描写、ギャグと会話の応酬で異様なテンションで突っ走る。珍妙なリングネームなど、爆笑ネタの数々も、プロレスに興味がなくとも十分に楽しめる。監督は、前作「クワイエット・ファミリー」が三池崇史監督作「カタクリ家の幸福」としてリメイクされた韓国の気鋭、キム・ジウン。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NATIONAL LAMPOON’S CHRISTMAS VACATION", "ナショナル・ランプーンクリスマス・バケーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「ナショナル・ランプーン／ホリデー・ロード4000キロ」に続く、コメディ・シリーズの第３作。C・チェイス扮するクラークが、まさに絵に描いたようなクリスマスの実現を夢見て奔走する。しかしその行く手には、数々の邪魔が入ってしまい……。すでに理想的クリスマスもギャグの一つか。中流家庭の生活のくすぐり方が面白い。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RESUCUE ME", "わたしを救けて レスキュー・ミー！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ネブラスカの田舎町。高校生のフレーザーは、憧れのマドンナ、ジニーを追いかけるのが日課。だが内気でオクテなフレーザーのこと、なかなかうまくはいかない。ある時、いつも通りカメラ片手にジニーをつけていると、なんと彼女が誘拐されてしまった。警察も本気で取り合ってくれない。仕方なくベトナム帰りのマックの協力を得て、ジニー救出の旅に出発する。M・ダディコフが、ベトナム帰還兵になりきってコメディに初挑戦。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUSHER", "プッシャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "コペンハーゲンに暮らすフランクはヤリ手の麻薬密売人。ボスから預かった麻薬を紛失してしまった彼は、必死に穴埋めの資金をかき集めようとするが……。デンマークのN・W・レフン監督が25歳で撮り上げたデビュー作。非情なギャングの世界でドン詰まりの状況に陥った男の苦闘を、ユーモアを交えてスピーディーに活写する。", "1997-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UFO食堂", "UFO食堂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "UFOで町おこしをする田舎町を舞台に、食堂を営む家族が織りなす人間模様を描いたドラマ。UFOの目撃情報が後を絶たない田舎町。この町で暮らす女子高生・上戸ユウは、数年前に母が行方不明になったことから、家業の“UFO食堂”を手伝っていた。やがて、UFO食堂では東京への進学を目指すユウの代わりにアルバイトを募集するが、やって来たアルバイト希望の女性は、なんと失踪していた母だった。", "2010-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAY DOWN EAST", "東への道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "“映画の父”D・W・グリフィスの代表作の１本。男に裏切られ、不道徳な女という噂をたてられてしまった少女は、住み慣れた町に居られなくなり田舎町へさすらっていく。やがて金持ちの屋敷の女中となった彼女は、その家の息子に心惹かれていくが、そこに彼女を裏切った男が現れ、彼女の過去を暴露。彼女は屋敷を出ると荒野をさまよいはじめる。見どころは、ラスト近く主人公の少女が滝の手前の流氷の上で気絶し、氷河を流されていくシーン。主演のL・ギッシュは本当に厳冬の凍りつく川を流れる氷の上で演技させられ、凍死寸前になったという。まさに命懸けのこのシーンの迫力は必見である。", "1920-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("痴人の愛", "痴人の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "大映のドル箱スター、京マチ子を売り出す切り札として製作された作品。谷崎潤一郎の同名小説を原作に、木村恵吾監督が“狸御殿”シリーズとはうって変わった粘着的演出で、一人の男が育て上げた自由奔放な女ナオミの官能の世界が展開されていく。原作と異なり良識的な結末は当時の世相ゆえか？", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひとりね", "ひとりね", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "大人の女性の孤独と欲望をポジティブな視線で描いた人間ドラマ。孤独な主婦、織江の前に若い男が現れる。名前も知らないその男は、たびたび織江の前に現れるようになり……。クラシカルな雰囲気を醸し出すためにモノクロ映像にこだわった監督、すずきじゅんいちの熟練した演出が冴える。引退を決意して撮影に挑んだという榊原るみの大胆なヌードや自慰シーンも話題に。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍在江湖", "ファイアー・ドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "父・ブルース・リー同様、若くして非業の死を遂げたブランドン・リーのスクリーン・デビュー作。親友の策略と知らずに殺人を犯し、投獄されたブランドン。8年後、出所した彼の前に組織のボスとなったかつての親友が現れる。平穏な生活を送ろうとするブランドンだったが、親友の魔手が愛する家族に及んだ時、怒りは限界を超えた！クンフーのみならず、壮絶なガン・アクションで魅せる香港娯楽活劇。", "1987-06-30", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("結婚のすべて", "結婚のすべて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "“独立愚連隊”シリーズや“暗黒街”もので日本映画に新風を吹き込んだ名匠・岡本喜八監督のデビュー作。当時アイドル・スターであった雪村いづみを主演に据えた恋愛結婚に憧れる若い女性を描く風俗コメディで、そのテンポの速さが評判になった。衣装や装置も実にモダン。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY BOYFRIEND IS TYPE−B", "B型の彼氏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "韓国で大ブームを巻き起こした血液型占いで、最も彼氏にしたくないとされたB型。そんな自己中で身勝手なB型男と従順なA型女との恋愛の行方を、恋の切なさと甘さをたっぷり織り交ぜてキュートに綴る。韓国TVドラマ「パリの恋人」でブレイクしたイ・ドンゴンが奔放だが憎めない男を好演。恋愛に慎重な女子大生、ハミはある日偶然出会ったヨンビンを運命の人と思いこむが、典型的B型の彼の勝手な行動に振り回される。", "2006-01-28", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エデンの海", "エデンの海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "海沿いの女子高を舞台に、赴任してきた若い青年教師と、勝気で反抗的な女生徒との交流を描く。山口百恵主演の文芸シリーズ４作目で、相手役が三浦友和から南条豊に変わった。1950年に鶴田浩二と藤田泰子で、1963年に高橋英樹と和泉雅子で映画化されている。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("g@me.", "g@me.", "", "", "http://ecx.images-amazon.com/images/I/41ACJHH0BML._SL210_.jpg", 112, "ベストセラー作家・東野圭吾の『ゲームの名は誘拐』を、藤木直人、仲間由紀恵主演で映画化。狂言誘拐を画策した若い男女の大胆な誘拐ゲームの顛末を、恋愛のテイストを絡めながら描く。携帯電話とインターネットを巧みに使いながら、スピーディーな演出と透明感のある映像美で、デジタル感覚あふれる都会的なクライム・ムービーに仕上げている。広告代理店のクリエイター・佐久間は、進行中の30億円プロジェクトを大手ビールメーカー副社長・葛城に突然白紙にされる。呆然とする佐久間は、葛城の娘・樹里と偶然出会う。葛城に恨みを持つ二人は、狂言誘拐を計画するが……。", "2003-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("復讐の牙", "復讐の牙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "かつて名人肌のスリだったが、今は足を洗って場末でバーを経営する吾郎。ある日、自分の戸籍がいつのまにか消されていることに気づいた彼は、さっそく調査に乗り出す。戸籍は、弟の高志によって消されており、その裏には、麻薬取引が絡んでいて……。田宮二郎主演のサスペンス・ドラマ。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUR MAN FLINT", "電撃フリントGO！GO作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "理想世界を作ろうとたくらむ陰謀団“ギャラクシー”を倒すため、国際連帯秘密諜報機関からスパイとして送り込まれたフリント。しかし、このことをギャラクシー団はすでに知っており、彼らはフリント殺害を計画する。殺し屋の手を逃れて、ギャラクシー団の秘密に何とか迫ろうとするフリントだが……。コバーン主演スパイ・アクション・シリーズの第１作。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOO MANY THIEVES", "泥棒がいっぱい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "マケドニア共和国の秘宝が、女も含む４人組の強盗一味によって盗まれた。その３週間後、秘宝と引き換えに50万ドルを支払えという要求が、同国のニューヨーク総領事館に届く。強盗一味の仲間割れから混沌としてくるストーリー、事件の仲介を頼まれたP・フォークのひょうひょうとした演技、そしてB・エクランドのお色気シーンと、娯楽要素もぎっしり詰まったアクション・コメディ。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOR THE HUNTER FROM THE FUTURE", "未来から来たハンター ヨオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "俳優出身のA・M・ドーソンによるSFアドベンチャー。紀元前21世紀へ、自らの祖先を求めてタイム・トラベルした未来人ヨオが、そこで出会った美女とともに大冒険を繰り広げる。ヒロイン役で色を添えるのはC・クレリー。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふるさとの歌", "ふるさとの歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 42, "文部省が懸賞募集した脚本の当選作を日活が委託され製作した教育映画。向学心に燃えながらも、家庭の事情で都会の学校に進学できず村で働いている青年が、休暇で帰省したかつての同級生と再会する。が、その友人はすっかり派手になっており、村にひと騒動が起きる。現存する最も古い溝口健二のサイレンと作品である。", "1925-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A｀ CAUSE，A CAUSE D’UNE FEMME", "女は夜の匂い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "殺人事件の容疑者にされた一人のプレイボーイをめぐる女たちの様々な反応を、ユーモアとウイットを交えて巧みに描いたミステリー。ドモンジョ、ラフォレ、ベルソワ、メニエルら、当時のフランス映画を代表する人気女優が総出演、事件の謎解きと恋のアバンチュールがスクリーンで交錯する。", "1963-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天城越え", "天城越え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "何十年も昔に起こった殺人事件に疑問を持った刑事が、当時少年だった関係者に会いに出かける。男の口から語られた意外な真相とは、遠い日、天城の峠ですれちがった裸足の女、彼女によせたほのかな憧れ、そして……。当時の松竹期待の大型新人監督・三村晴彦が松本清張の原作を、師とあおぐ加藤泰と共同脚色して撮り上げた会心の処女作。少年の心の揺れ動きを華麗な画面に見事に捉えて各方面から絶賛を浴びた。伊藤洋一が、ナイーブな少年を見事に演じきって強い印象を残すとともに、田中裕子が少年の前に現れる妖しく美しい女に扮する。特にそぼ降る雨の中の彼女のクローズ・アップは忘れられない。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極私的エロス・恋歌1974", "極私的エロス・恋歌1974", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「ゆきゆきて、神軍」のドキュメンタリスト・原一男が1970年代に手掛けた渾身の“私映画”。赤ん坊を抱いて家を捨てた同棲者を追って、カメラ持参の原は沖縄へ。旧コザ市で米軍相手に生計を立てる彼女のプライバシーに、カメラは執拗に迫る。とりわけ、黒人との混血児を自力で出産する元愛人の姿を、真正面から息をつめて捉えたシーンは、撮る者と撮られる者のデス・マッチといえる生々しい緊迫感。撮影行為が撮り手の存在を根こそぎにするようなすご味がある。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビルマの竪琴（総集編）", "ビルマの竪琴（総集編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "ドイツ文学者・竹山道雄の同名小説を市川崑が監督した作品。１部、２部と分けて公開された。太平洋戦争末期、ビルマからタイへ逃がれようとする井上部隊は、井上隊長以下、みな音楽好きで、特に水島上等兵の弾く竪琴は、疲れ切った隊員たちの心に滲みた。やがて戦争が終わり、井上部隊はムドンの収容所へ送られることに。水島は一人、戦争終結を信じず立てこもる日本軍の説得に派遣される。だが、彼の説得は聞き入れられず隊は全滅する……。収容所に落ち着いた井上部隊は、水島そっくりの僧とすれ違う。それは収容所に向かう途中、放置された日本兵の白骨を見て、霊を慰めることに決めた水島の姿だった。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いじめて、ください。アリエッタ", "いじめて、ください。アリエッタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ヤクザの夫が残した借金のため、良子は様々な仕事につく。しかし、無口で暗い彼女は、結局、SMホテトル嬢となり、死んだ気になって、客のどんな要求にも答えるのだったが……。実相寺昭雄がSMの世界を不気味なリアルさで描いている。DVDは「実相寺昭雄コレクション｛エロス｝」に、「アリエッタ」のタイトルで52分のR指定バージョンが収録されている。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREAK ORLANDO", "フリーク・オルランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "V・ウルフの『オーランド』をもとに、ドイツの女性監督、U・オッティンガーが自由に改編し映画化した異色作。巡礼者の格好をし、荒野を放浪するオルランドがたどり着いたフリーク・シティ。そこで彼、彼女が、５つの異なる時代で５つの経験を経て、また元の荒野へと帰って行くまでを描く。聖と俗、男と女の混在化した世界で、５つの異形の物語が繰り広げられていく。ヴェルナー・シュレーター監督作品の常連として知られるM・モンテツマがオルランドを演じているのも見どころの一つ。", "1981-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRENCH KISS", "フレンチ・キス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "M・ライアンがフランスを舞台に、婚約者にふられ新たな恋を見つけるヒロインを演じるロマンティック・ラブ・ストーリー。パリでフランス美人と恋仲になってしまった婚約者から突然の別れを告げられたケイトは、何とか彼を取り戻そうと飛行機恐怖症も顧みずパリへ飛ぶ。旅の途中でケイトは怪しげなフランス男性のリュックに出会い、男の心を取り戻すための“フランス式恋愛作法”を教え込まれるが……。リュックを絶妙のコメディ感覚を持つK・クラインが好演。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONKEYBONE", "モンキーボーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「ナイトメアー・ビフォア・クリスマス」のH・セリック監督が実写とCGアニメを組み合わせて描くファンタジー。コミック作家のスチュは、交通事故に遭い意識不明の重体に。彼の意識は時空を超えて、ダークタウンと呼ばれる町にたどり着いた。何とか帰還を試みるスチュの前に、やがて彼が作り出した人騒がせなキャラクター、モンキーボーンが出現、彼の生還を妨害し……。ブラックユーモアと異様なクリーチャーの造形に注目。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Grey", "ザ・グレイ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/57260/315x210.jpg?3243", 0, "「特攻野郎Aチーム THE MOVIE」のジョー・カーナハン監督とリーアム・ニーソンが再タッグを組んだアクション。石油掘削に向かう男たちを乗せた飛行機がアラスカに墜落し、過酷な自然の中でサバイバルを強いられる姿を描く。", "2012-08-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関の弥太っぺ", "関の弥太っぺ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "恩を果たすために徳造という男を斬った弥太郎は、徳造の義理の弟・森介と知り合う。森介は弥太郎の腕に惚れ、子分にしてくれと頼むが、弥太郎はそれを断り、二人は別れ別れに。それから数年後、腕を上げた森介と弥太郎は、一人の女をめぐって争うことになり……。おなじみ長谷川伸原作による股旅もの。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Nanny Diaries", "私がクマにキレた理由（わけ）", "", "", "http://image.eiga.k-img.com/images/movie/34170/315x210.jpg?2336", 106, "「アメリカン・スプレンダー」のシャリ・スプリンガー・バーマンとロバート・プルチーニのコンビがスカーレット・ヨハンソンを主演に描くロマンチック・コメディ。大学を卒業したものの自分の将来が見えないアニーは、ひょんなことからニューヨークの高級住宅街に住むセレブ一家のナニー（ベビーシッター）をすることに。家庭に無関心な両親にこき使われ、愛情に飢えた息子もなかなかなつかない。そんなある日、クマのぬいぐるみに監視カメラが仕込まれていることを知ったアニーは……。", "2008-10-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PROCE｀S DE JEANNE D’ARC", "ジャンヌ・ダルク裁判", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "15世紀のフランス救国の英雄、ジャンヌ・ダルクの物語は、「裁かるゝジャンヌ」をはじめ何度となく映画化されてきた。イングリッド・バーグマンには２本も主演作品がある。その中で、このブレッソン作品のユニークな点は、裁判そのものに焦点をあて、現在も保存されている当時の裁判記録に基づいて、忠実にジャンヌ・ダルクの孤独を再現しているところにある。映画は、法廷で凛として屈することのないジャンヌと、独房で悲しみにくれるジャンヌを繰り返し描く。死の恐怖におびえ、独房で泣き悲しみながらも信念をまげない人物はブレッソン映画に典型的なもの。出演者はほとんど素人が使われ、撮影はイル・ド・フランスのムードン城の庭と地下室で行われた。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIGHTY JOE YOUNG", "マイティ・ジョー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1949年の「猿人ジョー・ヤング」をリメイクしたハートフル・アドベンチャー。巨大ゴリラと人間の交流をSFXを駆使して描く。アフリカのジャングルで、密猟者に互いの母親を殺された女性ジルとゴリラのジョーは、姉弟のように育った大の仲良し。体長５メートルにまで成長したジョーを密猟者たちから救うため、ジルは動物学者グレッグとともにロスの動物保護センターに向かう。だが、ジョーはそこでもマスコミやかつての密猟者に目をつけられ……。５回のアカデミー賞に輝く名手R・ベイカーによるアニマトロニクスに、ドリーム・クエスト・イメージズとILMによるCG技術を組み合わせて、巨大ゴリラの微妙な表情やダイナミックなアクションを生み出している。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEAT LOVE", "肉片の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "世界的に名高いアニメーション作家、J・シュヴァンクマイエルの短編。愛し合っている二つの肉片がラジオの音楽に合わせてロマンティックに踊っているが、突然、フォークに刺されて熱いフライパンに放り込まれてしまう、という内容。DVDは「ヤン・シュヴァンクマイエル　短編集」に収録。", "1989-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FAN", "殺しのファンレター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "バコールがブロードウェイの大女優役を熱演するミステリー作品。バコールのもとへ、毎日執拗なファンレターが届くが、彼女はとりあわない。その結果、怒った送り主は彼女に殺意を抱きはじめる。被写体をなめるようなカメラワークが、犯罪心理を巧みに表現。また、異常心理のファンをメジャーになる以前のM・ビーンが演じている。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プレイ", "プレイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "新進監督と若手俳優による新感覚のホラー映画製作を目指す“絶対恐怖”シリーズの第１弾。「逆境ナイン」の玉山鉄二が主演を務める本作では、少女を誘拐した男女が想像を絶する惨劇に巻き込まれる恐怖を描く。奈落の底に落ちる主人公役の玉山が、心に闇を抱えた男の心理を体現している。恋人同士のミツルとマキは、うだつのあがらない現在の生活から抜け出すために誘拐を計画。そして彼らは愛という名の少女を誘拐するのだが……。", "2005-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAR AND PEACE", "戦争と平和", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 208, "文豪トルストイの名作の映画化。19世紀の帝政ロシアを舞台に、ナポレオンの侵攻による母国での大戦闘、動乱期の貴族や農民などの生きざまをダイナミックに綴る。貴族の娘ナターシャを、若き日のヘプバーンが熱演した格調高い文芸大作。", "1956-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("脇役物語", "脇役物語", "", "", "http://image.eiga.k-img.com/images/movie/55640/315x210.jpg?3191", 97, "いつも脇役ばかりの冴えない中年俳優の奮闘を描いたヒューマン・コメディ。職場でも私生活でもうだつの上がらない俳優ヒロシに、新進女優アヤとの恋、そして映画主演の話という思いがけないチャンスが訪れるが……。主人公ヒロシをベテラン俳優の益岡徹が、アヤを永作博美がそれぞれ好演。メガホンをとるのは、短編映画「不老長寿」が世界各国で高く評価され、今作が長編デビューとなる緒方篤監督。", "2010-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AMERICANO", "辺境の掠奪者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "大牧場の経営を夢見る男は、手持ちの牛を売って資金を稼ごうとする。ブラジル奥地で買い手が見つかり、彼はそこへ赴くが、当の買い手は殺されていた。地元の探偵から別の買い手を紹介された彼は、すべてその別の買い手が仕組んだ罠であったことに気付く。西部風の設定でありながら、南米を舞台とした異色ドラマ。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の歴史", "女の歴史", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "一人の女の半生を描いた成瀬巳喜男晩年の作品。めまぐるしい時間の交錯の中、高峰が戦時中の娘時代から老け役までを好演。夫の死後、真に想いを寄せる男性の出現に揺れ動きながらも、その愛をあきらめるというヒロイン像に、成瀬メロドラマの真髄が見いだせる。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Anne of Green Gables:The Sequel", "赤毛のアン　アンの青春　完全版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 220, "　世界中で大ヒットした「赤毛のアン」の続編。マリラとマシューのもとに引き取られてから5年、相変わらずおしゃべりと空想が大好きなアンは教師として働いていた。かつてはライバルであったギルバートから愛を打ち明けられるが……。少女から大人へと変化していくアンの心情が丁寧に綴られている。1988年公開の「続・赤毛のアン／アンの青春」に、アンを取り巻く人たちのエピソードを約50分追加。", "1988-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Teenage Mutant Ninja Turtles", "ミュータント・タートルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "全米で大人気のコミックを、『セサミストリート』などのマペットで知られるジム・ヘンソンのキャラクター・デザインによって実写版で映画化。アメリカでは大ヒット、キャラクター・グッズが飛ぶように売れ、TVのアニメ番組も制作されるなど一大ブームとなった。ニューヨークで暗躍する強盗一味を追う美人ニュースキャスターのエイブルを危機から救ったのは人間ほどの背丈のカメだった。放射性物質で異常成長した正義感の強い４匹のカメたちが、エイプリルに味方して強盗退治に立ち上がる！　 表情豊かな“タートルズ”が何より最大の魅力。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鍵 THE KEY", "鍵 THE KEY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "何度か映画化された谷崎潤一郎の同名小説を、ロマンポルノ出身で「人魚伝説」「ちぎれた愛の殺人」の池田敏春がリメイク。妻への欲望を日記にしたため、熟睡している彼女の裸体をカメラに収める夫。彼の内に潜む異常性愛が、柄本明の怪演によって浮かび上がる。夫の気持ちを察し、官能的な性のゲームに身をゆだねる妻役の川島なお美も、惜しげなく裸体を披露し迫真の演技を見せる。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水の女", "水の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "田舎町の銭湯の一人娘・涼が、父親の死によって天涯孤独の身となる。ある日、旅から戻った彼女は、火を眺めていると心が安らぐという不思議な青年・優作と出会い、彼に銭湯の仕事を手伝ってもらうが……。これが映画初監督作となるCMディレクター、杉森秀則が、水、火、風、大地という自然の四元素をモチーフに紡ぎ上げた変化球のラブ・ストーリー。人気シンガー、UAが生まれついての雨女であるヒロイン役で映画デビューを果たし、“火の男”優作に扮した浅野忠信と共演している。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIMPATHY　FOR　MR．　VENGEANCE", "復讐者に憐れみを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "「JSA」「オールド・ボーイ」のパク・チャヌク監督が'02年に手がけたハードボイルドの傑作。大事な娘を殺された男の復讐と、その陰にある悲痛なドラマを描き出す。緊迫した静けさの中に、悲劇の連鎖の怖さと哀しみが浮き彫りにされる。ソン・ガンホ、ペ・ドゥナら、韓国の人気俳優陣が熱演を披露。病気の姉の手術費用を得るため、聴覚障害者の男が社長の娘を誘拐するが、偶発的な事故で娘が死亡。社長は男への復讐を誓う。", "2005-02-05", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Repentance", "懺悔", "", "", "http://image.eiga.k-img.com/images/movie/53990/315x210.jpg?3168", 153, "旧ソビエトの検閲下、1984年に製作されたグルジアの名匠テンギズ・アブラゼによる社会派ドラマ。スターリンの粛清を喚起させ、ソ連崩壊につながったペレストロイカを象徴する作品で、1987年カンヌ国際映画祭審査員特別大賞を受賞。とある地方都市で高名な市長が死去し、市長の墓を掘り起こした罪で、生前の市長の独裁により家族を失った女性が逮捕される。彼女は法廷で市長の粛清の過去を告発し、埋もれつつあった悲劇の歴史を訴える。", "2008-12-20", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADY CHATTERLEY", "チャタレイ夫人の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 175, "戦争で下半身不随となった夫を持つコニーは、満たされない日々を送っていた。そんな折、彼女は森番の男と関係を持ってしまうのだった。D・H・ロレンスの名作をK・ラッセルが監督、もとはTVシリーズだったが再編集して、劇場で公開された。DVDはノーカットヘア解禁全長版で発売されている。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROSSOVER／A　WORLDWIDE　BASKETBALL　ODYSSEY", "クロス・オーヴァー／ワールドワイド・バスケットボール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "世界的なスポーツ・ブランド、NIKEの製作によるバスケットボール・ドキュメンタリー。カルチャーやファッションと結びつきながら、若者たちを熱狂させるこのスポーツの魅力に多角度から迫る。チャック・D、プロフェッサー・グリフという、ヒップホップ界のカリスマによる音楽も魅力的だ。楽しむため、また生きるためにバスケットに臨む、世界中の若者のライフスタイルを追跡。さらに有名選手たちが、競技の魅力を熱く語る。", "2005-09-17", "1,27,44,4,9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("High School Musical 3: Senior Year", "ハイスクール・ミュージカル　ザ・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/53880/315x210.jpg?2036", 112, "ディズニー・チャンネルのオリジナル・ムービーとして放送され大ヒットを記録したミュージカル・シリーズの3作目。主演は「ヘアスプレー」のザック・エフロン、幼い頃からミュージカルの舞台に立ってきたバネッサ・ハジェンズ。高校卒業を間近に控え、恋仲であるバスケット青年のトロイと優等生のガブリエラは悩んでいた。名門バスケットチームのある大学に内定をもらっているトロイだったが、ガブリエラの進学する大学と1600キロも離れていたのだ。", "2009-02-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In un altro mondo", "もうひとつの世界で", "", "", "http://image.eiga.k-img.com/images/movie/55010/315x210.jpg?4659", 75, "1922年にイタリアで最初の国立公園に指定されたイタリア・アルプスの山岳地帯に広がるグラン・パラディーゾ国立公園では、現在も雄大な自然の中に貴重な野生動物たちが数多く生息している。ここで管理人として野生のヤギやカモシカの保護観察を行いながら暮らすダリオ・ファーブレの日常を通し、自然や動物たちの姿をありのままに捉えたドキュメンタリー。監督・撮影・編集はイタリアのジョセフ・ペカン。", "2009-10-21", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Leningrad Cowboys Go America", "レニングラード・カウボーイズ・ゴー・アメリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "メンバー全員が長く鋭いリーゼントにサングラスというスタイルのロックンロールバンド、レニングラード・カウボーイズ。彼らを主演に迎え、フィンランドの鬼才アキ・カウリスマキ監督が放つ、シュールな笑い満載の傑作ロードムービー。最果ての地ツンドラからアメリカツアーにやって来た民族音楽バンド。しかし彼らの音楽は全く受ず、ライブ会場はいつもガラガラ。マネージャーの提案で、彼らはロックンロールバンドへと大変身する。", "1989-01-01", "20,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イツカ波ノ彼方ニ", "イツカ波ノ彼方ニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「殺し屋１」の主人公・イチの高校時代を描いたOV「１−イチ−」でスクリーンデビューした丹野雅仁監督作品。夢を追い続ける青年と都会で夢敗れた青年という対照的な二人が、沖縄を舞台に成長していく姿を描いた青春映画。現代の若者たちのリアルな心情をスタイリッシュな映像で見せる。幼い頃、竜宮城を夢見た勝男とアキ。二人はいつしか別々の人生を歩むようになったが、アキは勝男を頼って沖縄に逃げ帰ってくる。", "2005-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小早川家の秋", "小早川家の秋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "主として松竹で映画を作ってきた小津が、珍しく東宝で、しかも東宝主演級の俳優を多数出演させ撮り上げた作品である。造り酒屋の小早川万兵衛を中心に、小早川家にかかわる人々の悲喜こもごもを独特の情感で描写している。万兵衛の死んだ長男の嫁・秋子を再婚させようと、親戚連中は手を尽くすが、秋子はなかなか承諾しない。次女・紀子は転勤した同僚への恋を断ち切れずにいる。一方、妻に先立たれた万兵衛は、昔なじみの妾とよりを戻し、人目を盗んでは通い詰めていた。妻の法事の日の夜、急に倒れる万兵衛。一時は回復するが数日後、妾の家でぽっくり逝ってしまう。親戚一同が会し、静かな葬式が営まれる。火葬場の煙突のけむりを人々がそれぞれの思いを抱いて見上げる名シーンの記憶が、伊丹十三監督の処女作「お葬式」に反映した。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青い山脈・続青い山脈", "青い山脈・続青い山脈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 183, "ある地方の町。女学生の寺沢新子は現金の代わりに米を持たされて町の雑貨屋へ学用品を買いに行く。留守番をしていた学生・金谷六助はちゃっかり新子に御飯を炊いてもらう。そんなことがきっかけで新子と六助は交際を始めた。ある日、新子のもとにラブレターが届き、新子はそれを信頼する島崎先生に見せる。島崎は校医の沼田にこのことを相談する。学校では理事会が開かれラブレター問題の大論争。しかし実はラブレターは新子の同級生の仕業だった……。というあまりにも有名な石坂洋次郎の原作を今井正が演出したユーモア青春編。藤原釜足がラブレターを読み上げる有名な場面は何度観ても笑わせられる。DVDタイトルは「青い山脈 前・後篇」。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレープ", "クレープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "恋人との生活に不満はないが、どこか空しい日々を送っている中年男と、離婚した彼の妻に引き取られ、14年も会っていない一人娘。そんな二人が久しぶりに再会を果たし、親子の絆を確かめ合う姿を、透明感あふれる映像で描いていく。伊集院静の短編小説を、市川準が繊細な演出で映像化。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWINGERS", "スウィンガーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "主演のJ・ファブローが自身の体験を脚本化。ロサンゼルスを舞台に、恋や仕事の悩みを抱える若者たちの姿をコミカルに綴る青春映画だ。コメディアン志望のマイクは、別れた恋人のことが気になって仕方がない。彼女からの電話を待ち続ける彼を見かねた友人トレントは、ラスベガスやバーに誘って気分転換をさせる。ところが、プレイボーイのトレントと違って、マイクはことごとくナンパに失敗。そんな彼を面白がる友人たちを尻目に、ある晩、マイクは一人の女性と意気投合するが……。テンポのよい展開とユーモラスな何気ない会話が絶妙。「レザボア・ドッグス」などのパロディーも随所に盛り込んだ、映画への愛があふれるさわやかな好編だ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rush Hour 2", "ラッシュアワー2", "", "", "http://image.eiga.k-img.com/images/movie/1160/315x210.jpg?4730", 90, "香港警察のリー捜査官とロサンゼルス警察のカーター捜査官が前作のラストで向かった香港で、米国大使館が爆破されるという事件が発生。香港マフィア絡みのこの事件を追って、2人はやがてラスベガスへと飛ぶ。監督と脚本家は前作と同じ、ドラマも前作のすぐ後から始まる正統派の続編。アクション・コーディネイターは「ロミオ・マスト・ダイ」「X-メン」のコンラッド・パルミザーノ。特撮コーディネイターは「ザ・ロック」「60セカンズ」のマイク・メイナーダス。アラン・キングら有名俳優のカメオ出演も話題。", "2001-09-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海角七號", "海角七号　君想う、国境の南", "", "", "http://image.eiga.k-img.com/images/movie/54820/315x210.jpg?2698", 130, "2008年に台湾で公開され、台湾映画興行収入の記録を塗り替えた大ヒット作。監督は本作が劇場デビューとなるウェイ・ダーション。主演は映画初主演となったミュージシャンのファン・イーチェンと日本人女優の田中千絵。台湾最南端の町・恒春、ミュージシャンとしての成功を夢見ながらもうだつのあがらない生活を送る青年・アガは、父親の計らいで郵便配達の仕事に就くことに。日本統治下の住所「海角7号」宛ての小包を手にしたアガだったが、現在、それは存在しない住所だった。", "2009-12-26", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カラオケ", "カラオケ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "個性派俳優、佐野史郎の初監督作品。とある地方都市ののどかな風景をバックに、中年の男女７人の友情を綴っている。中学校時代の同級生、林がアイドル歌手と電撃結婚したことがワイドショーで報じられ、びっくりしたカメラ屋の泉。やがて彼は林を含むかつての級友たちとカラオケに行き、初恋などの思い出話にふけっていく。ユーモアと哀感が入り交じったドラマを彩るのは、カルメン・マキの『戦争は知らない』を始めとする1970年代のヒットナンバー。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'Homme qui Plantait des Arbres", "木を植えた男", "", "", "http://image.eiga.k-img.com/images/movie/43870/315x210.jpg?3901", 30, "人間と自然をテーマとした作品を手がけ、アカデミー短編アニメーション賞を2度受賞したカナダ在住のアニメーション作家フレデリック・バックの傑作短編4本をまとめ、ニュープリント、新字幕で公開する「フレデリック・バックの世界」にて上映。羊飼いのブッフィエは、たった1人で荒野に木を植え続けていた。やがてブッフィエは、不毛の大地を生命の輝きに満ちた場所によみがえらせる。バックが5年の歳月をかけ、2万枚におよぶ作画をほぼ1人で担当。第60回米アカデミー賞で短編アニメーション賞を受賞した。声優は英語版がクリストファー・プラマー、仏語版をフィリップ・ノワレが担当。", "2011-07-02", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("第2写真部", "第2写真部", "", "", "http://image.eiga.k-img.com/images/movie/54730/315x210.jpg?4246", 110, "少子化の影響を受け、霧鳴女子大学は創立50周年を節目に男女共学へと転換。しかし入学した男子はたったの5人で、女子に囲まれ肩身の狭い思いをしていた。5人はキャンパスで自由に写真が撮れる環境を作るため、女子の写真部とは別の第2写真部を設立するが、理事長から写真部を1つに統合するよう指示され、女子写真部と競い合うことになってしまう。", "2009-07-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hostsonaten", "秋のソナタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "スウェーデンが誇る巨匠イングマール・ベルイマンが、奔放に生きる母と彼女に不満を抱える娘の心の葛藤を描いた人間ドラマ。ノルウェー北部の片田舎。牧師館で暮らす女性エバは、歳の離れた夫とともに平穏な毎日を送っていた。そんなある日、エバは著名なピアニストである母シャルロッテを自宅に招待し、母娘は7年ぶりの再会を果たすが……。母親役のイングリッド・バーグマンはこれが最後の映画出演作となった。2012年、デジタルリマスター版でリバイバル公開。", "1978-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Taken", "96時間", "", "", "http://image.eiga.k-img.com/images/movie/54420/315x210.jpg?3798", 93, "リュック・ベッソン製作、リーアム・ニーソン主演で描くサスペンス・アクション。17歳のアメリカ人少女キムが、友人と訪れていたパリで何者かに誘拐された。事件発生時にキムと携帯電話で話していた父親のブライアンは、元秘密工作員の知識と行動力で犯人グループの身元を割り出し、娘を救出するために単身パリへ向かう。共演は「X-メン」シリーズのファムケ・ヤンセン、「ジェイン・オースティンの読書会」のマギー・グレイス。", "2009-08-22", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿部一族", "阿部一族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "当時の前進座俳優が総出演の本格的歴史映画。熊本藩藩主の逝去に際し、殉死することを許されなかった阿部一族の反抗と悲劇が描かれる。因習と面目に生きる武士の愚かさを描いた森鴎外の原作に対し、超国粋主義者の熊谷久虎監督は、忠君の美学を前面に押し出すことで、その思想を明確にしている。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREEWAY", "復讐のハイウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ロサンゼルスのハイウェイで、無差別にドライバーを射殺し続ける謎の“ハイウェイ・キラー”。数年前に夫を殺された看護婦のハニーは、自らの手で犯人を見つけ出そうと行動を開始した。そして不思議な男と出会い、やがて犯人を突き止めるが……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFTER DARK，MY SWEET", "アフター・ダーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "忌まわしい過去から逃れるために放浪の旅に出た元ボクサーのコリー。場末の酒場で、彼はフェイという一人の女と出会う。彼女の妖しい魅力に徐々に惹かれていったコリーは、いつしか彼女とその叔父が仕組んだ誘拐計画に引きずり込まれていくのだった。M・ジャールの音楽を効果的に使った、ハードボイルド・タッチの男と女のドラマ。見事に役になりきったJ・パトリックの好演も印象深い。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージー大作戦", "クレージー大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アメリカ旅行から帰国したクレージー・キャッツが、その成果を披露。“世界中の悪銭をかっさらう”と豪語する石川五郎をはじめとする７人の義賊団が、日本の暗黒街に君臨する“頭取”から10億円を強奪する。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last Station", "終着駅　トルストイ最後の旅", "", "", "http://image.eiga.k-img.com/images/movie/55490/315x210.jpg?2075", 112, "ロシアの文豪トルストイと、世界3大悪妻として知られる妻ソフィアの知られざる生活を描く伝記映画。主演のヘレン・ミレンほか、クリストファー・プラマー、ポール・ジアマッティ、ジェームズ・マカボイら豪華キャストが顔をそろえる。晩年を迎えたトルストイは突然、爵位や財産を捨て、菜食主義の独り身になると宣言する。献身的な妻として50年近くトルストイに寄り添ってきたソフィア伯爵夫人はこれに驚がくし、憤慨（ふんがい）。ソフィアは夫を崇拝するワレンチンを利用し、トルストイを引き止めようとする。そして、自分を取り巻く環境や陰謀にうんざりしたトルストイは、82歳にして家出をしてしまう。", "2010-09-11", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STICKY FINGERS", "スティッキー・フィンガーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "成功を夢みる貧乏音楽女学生二人組のもとに、90万ドルもの大金が預けられた。危ない金だと思いつつ、ついつい手を出してしまった彼女たちは、次から次へと衝動買いを始めてしまうが……。アップビートなBGMにのせて描いた、おしゃれなライト・コメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("卒業旅行 ニホンから来ました", "卒業旅行 ニホンから来ました", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "金子修介が一色伸幸の脚本を映画化したコメディ。卒業旅行で東南アジアの小国を訪れた大学生が怪しげなブローカーに騙されて、“一発太郎”の芸名で国民的アイドルになってしまう……。『ヤングマン』『ペッパー警部』を熱唱する織田裕二のノーテンキぶりも楽しい。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NAKED GUN２1/2：THE SMELL OF FEAR", "裸の銃〈ガン〉を持つ男 PART２1/2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "無責任、悪ノリ、大マヌケのフランク・ドレビン警部が前作より、さらにパワーアップして再登場。エネルギー問題の権威マインハイマー博士誘拐事件に挑むのだが……。相変わらず周囲を迷惑の渦に巻き込み、混乱は悪化するばかり。「ターミネーター２」をはじめヒット映画のパロディーも散りばめられた理屈抜きギャグ映画の娯楽作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("再生の朝に　ある裁判官の選択", "再生の朝に　ある裁判官の選択", "", "", "http://image.eiga.k-img.com/images/movie/55880/315x210.jpg?4736", 98, "中国で起きた実在の事件をモチーフにした社会派ドラマ。裁判官のティエンは、盗難車による事故で娘を亡くして以来、淡々と法に忠実な仕事をこなしていた。ある日、車2台の窃盗で捕まった青年チウ・ウーの裁判を担当することになった彼は、法に従って彼に極刑を宣告する。一方、腎臓病を患う地元の有力者リーは、チウ・ウーの腎臓が自分に適合することを知り……。", "2011-03-05", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暖流", "暖流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "戦前の同名作品を、名匠・増村保造監督が再映画化。前作では高峰三枝子扮する啓子が自ら身を引いたのに比べ、本作では啓子がぎんに敗北するという形に改変され、女のバイタリティーが強調されている。“二号でも妾でもいい”と東京駅で叫ぶ左幸子の姿は圧巻。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二人で歩いた幾春秋", "二人で歩いた幾春秋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "河野道工の歌集「道路工夫の歌」をもとに木下自身が脚本化した作品。妻、両親、息子らと質素な生活を送る道路工夫の姿を描いたもので、1950年代には二枚目スター的イメージの目立った佐田啓二が起用されている。妻役は「喜びも悲しみも幾歳月」でも彼と共演した高峰秀子。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUILTY AS CHARGED", "タイム・トゥ・ダイ—闇の処刑人—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "保護監察官を務める女性キンバリーは、仮釈放された犯罪者たちの連続失踪事件を調査していた。ある時仮釈中の男を訪問、その男に逆にレイプされそうになる。だがその時、ベン・カリーンという男が現れた。ベンは表向きは工場を経営する平凡な市民だった。しかし、彼にはもう一つの顔があり……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大仏さまと子供たち", "大仏さまと子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "戦争孤児たちが奈良を訪れる観光客を相手に、ガイドをやりながら生活を立てていく。その様子を清水は淡々と描くことに成功している。当時清水は、蜂の巣プロの子供たちや素人俳優を好んで使ったが、これもそうした１本で、その結果、作品に透明感を横溢させることになった。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAZIONE TERMINI／INDISCRETION OF AN AMERICAN WIFE", "終着駅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "ローマにやって来たアメリカ女性とイタリア青年の恋、そして別れ……。男と女の悲恋物語を語る一方で、駅に集う様々な人間たちの人生の営みを点描。ネオ・レアリスモ派監督らしい飾り気のない淡々とした映像が、かえって情感深く胸を打つ。", "1953-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お姉チャンバラ THE MOVIE", "お姉チャンバラ THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/53500/315x210.jpg?2069", 80, "水着姿のセクシー美女が日本刀でゾンビを斬りまくる奇抜な設定でカルト的人気を誇るゲームソフト「お姉チャンバラ」を実写映画化したバイオレンス・アクション。西暦20××年の戦乱の世。謎の科学者・杉田が作り出したゾンビが街に溢れ返る中、\"忌血\"の継承者にして最強のお姉チャン・彩は杉田を倒すためにゾンビたちと壮絶な闘いを繰り広げていた。そんな彼女の前に、腹違いの妹でありながら父を殺した仇・咲が現われる。", "2008-04-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("木枯し紋次郎・関わりござんせん", "木枯し紋次郎・関わりござんせん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "菅原文太による“木枯し紋次郎”シリーズ第２作。今回、紋次郎は姉と再会する。赤子の紋次郎を間引きから救ってくれた姉だったが、今は女郎に身をおとし、苦境から逃れるために紋次郎を裏切るような女になっていた。紋次郎の人間形成の秘密に触れ、過去との訣別を描く。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇役者たち・九八とゲーブル", "喜劇役者たち・九八とゲーブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "製作当時、売り出し中のタモリと愛川欽也を共演させたコメディ。この二人が浅草の舞台で喜劇役者としてコンビを組み、人気者になるというお話。タモリの中国語放送など、彼一流の話芸の片鱗をのぞかせているが、内容がいささかお粗末で、笑いが不発に終わっている。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAS　FLIEGENDE　KLASSENZIMMER", "飛ぶ教室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "『点子ちゃんとアントン』など、ドイツで次々と映画化されているE・ケストナーの児童文学。彼の同名小説に基づき、寄宿学校に通う少年５人の姿を描く友情ドラマだ。秘密の隠れ家で演劇の台本を発見した５人組が、ミュージカルの創作に挑みつつ、その台本をめぐる大人たちの確執も鮮やかに解決していく。子役たちの伸びやかな演技にも注目！", "2003-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不良番長・一網打尽", "不良番長・一網打尽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "“不良番長”シリーズ第15作。一匹狼・神坂弘は、生まれ育った新宿を我がもの顔で支配する新興暴力団・共友会に我慢できず、仲間たちとカポネ団を結成した。そんな弘を消そうと共友会が差し向けたのは、彼のムショ仲間・力石。彼は逆に弘をかばったために殺され、弘はカポネ団を引き連れて復讐に向かう……。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("梁祝", "バタフライ・ラヴァーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "身分の差が激しかった東晋の時代。名家の娘、祝は人目をごまかし男装して大学へ通っていた。苦学生の梁と知り合い、友情を育むが、やがて女であることを気づかれ、互いに愛し合うようになる。しかし、祝は政略結婚を企てる父親に家に呼び戻され、二人は駆け落ちを決行するが、あえなく失敗、彼女は父親に連れ戻され……。京劇などで知られる古典「梁山伯與祝英台」を題材に香港版「ロミオとジュリエット」としてまったく新しくリメイク。主演は当時の人気アイドル、ニッキー・ウーとチャーリー・ヤン。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("How to make an American quilt", "キルトに綴る愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "　13歳でデビューし、その後「シザーハンズ」「ドラキュラ」など華々しいキャリアを歩むウィノナ・ライダーが結婚に戸惑う大学院生を演じる。卒論を仕上げるため、カリフォルニアの片田舎にある祖母の家を訪れたフィン。そこには、様々な愛の形を紡いできた女性たちがいた。アカデミー賞女優、アン・バンクロフト（「奇跡の人」）、エレン・バースティン（「アリスの恋」）をはじめ、そうそうたる女優陣が脇を固める。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たそがれ酒場", "たそがれ酒場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "“たそがれ酒場”と呼ばれる大衆酒場は、薄幸の老ピアニスト、その愛弟子のバリトン歌手、戦時中に描いた戦争画で名をなしたことに責任を感じている老画家、旧軍人、サルトルを論ずる大学講師や学生たちで今日もにぎわっていた。この酒場随一の出し物はエミー・ローザのストリップ・ショー。ショーが始まって間もなく、エミーの元パトロンがエミーを切りつける事件が起こる……。おのおの異なる職業を持つ人物の出入りする大衆酒場を舞台にして、ある日の夕刻から深夜までの７時間ほどの間に起こる、様々な事件や人の出会いを“グランド・ホテル形式”で描く内田吐夢の佳編。脚本の灘は、新聞記者で初めてシナリオを書いた無名の新人であった。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROM　MOON　TILL　THREE", "正午から3時まで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "硬派俳優C・ブロンソンとJ・アイアランドの、時のおしどり夫婦共演による西部劇。逃亡中の強盗一味の一人、グレアムは未亡人アマンダの家に逃げ込んだ。仲間と落ち合うまでの正午からの３時間、彼らは愛の芽生えを予感させる甘美な時を過ごす。その後、グレアムの死によって、この３時間は悲劇の伝説として語られるようになるが……。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サクゴエ", "サクゴエ", "", "", "http://image.eiga.k-img.com/images/movie/34260/315x210.jpg?1628", 98, "俳優・弓削智久が初めて脚本を手掛け、「プッシーキャット大作戦」の本田隆一監督がメガホンを取った人間ドラマ。騙されてヤクザに追われるハメになった男・森田は、自殺しようと訪れたビルの屋上で、トミーと名乗る不思議な青年に出会う。彼に誘われるまま富士山へと行ってしまう森田だったが、その帰り道、トミーが親友の裏切りによって逃亡生活を送っていることを知り……。主演は弓削と「運命じゃない人」の中村靖日。", "2007-10-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・いそぎんちゃく", "続・いそぎんちゃく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "渥美マリの主演によるエロティックなサクセス・ストーリーのシリーズ第２作。罪作りな美貌と色気ゆえに退学させられた東北の高校生、浜子は上京してガソリンスタンドの住み込み店員に。田舎の土地を買い戻すため、男たちの誘いにも乗らず必死で働く彼女は、モデル業にも乗り出すが、やがて仕事一徹のカメラマンに恋してしまい……。渥美は美しい肢体に加え、劇中で歌も披露。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Klimt", "クリムト", "", "", "http://image.eiga.k-img.com/images/movie/1480/315x210.jpg?3917", 97, "19世紀末から20世紀初頭にかけて活躍したオーストリアの天才画家グスタフ・クリムトの破天荒な人生を映像化した伝記ドラマ。何に対しても自由奔放なスタイルで通したクリムトが死の床で見る夢と記憶を通して、彼の生きた時代、世界、そして彼自身の愛を描く。監督は「見出された時『失われた時を求めて』より」のラウル・ルイス。主演のクリムトには名優ジョン・マルコビッチ。", "2006-10-28", "3,77,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("樹氷悲歌", "樹氷悲歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "競馬騎手を夢見て東京に行きながら故郷に舞い戻ってきた信一。志なかばの帰郷で親の不信まで買い、意気消沈した彼を同級生のみやと邦夫は懸命に助力する。温かい友情に応えるため、信一は二人と一緒に幼い頃の思い出の地・蔵王へ、スキーに出かける。しかし、雪崩が彼らを襲い……。友情も描く青春ラブ・ストーリー。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日常　恋の声", "日常　恋の声", "", "", "http://ecx.images-amazon.com/images/I/31PjEYaR4JL._SL210_.jpg", 105, "ケンドーコバヤシ、友近、ブラックマヨネーズら吉本興業の人気若手芸人が勢ぞろいした群像コメディ「日常」の続編。大阪の街を舞台に、会話の噛みあわないストリートミュージシャンとその彼女、動物園でケンカするカップル、勤務中に恋人と携帯電話で話す警察官など、様々な男女が繰りひろげる愛すべき日常風景を、笑いを織りまぜながら切り取っていく。音楽を担当するのは元「サニーデイ・サービス」の曽我部恵一。", "2007-02-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ORLANDO", "オルランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "V・ウルフのベストセラー小説を女流監督S・ポッターが繊細な演出で映像化。16世紀から400年もの間に渡り、男から女へと生まれ変わりながら生き続ける青年貴族オルランドの、何事にもとらわれない自由奔放な生を描く。日本公開時に芸術性を認められ、無修正で登場した主人公の全裸シーンも話題となった。", "1992-01-01", "3,24,4,2,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠山の金さん はやぶさ奉行", "遠山の金さん はやぶさ奉行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "目下江戸で評判のお道、およう両太夫がギヤマン水槽の水中曲芸を披露。そのさなかに殺人事件が起こる。大工・藤兵衛の娘・お景の家に弟子となって住み込んでいた御存知遠山の金さんは、事件の真相解明に乗り出すのだが……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("毒婦高橋お伝", "毒婦高橋お伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "とかくスキャンダラスな面が強調されがちな高橋お伝を、中川信夫が正攻法で描いた意欲作。低予算で黒沢治安が苦心した美術も見事だが、特筆すべきはお伝に扮する若杉嘉津子の好演で、彼女としては名作「東海道四谷怪談」と並ぶ代表作である。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の景色", "風の景色", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "“舞踏神”の異名をとった暗黒舞踏の大家、土方巽が舞台空間から飛び出し、東京の風景の中で踊った姿を収めたアバンギャルドな映像詩。土方が1986年に他界するまで彼とともに舞踏と映像の一体化を試みてきた映画作家、大内田圭彌が、1976年に未完成版を上映して以来、長らく封印されてきたフィルムを再編集。撮影から27年の時を経て、ディレクターズ・カット版として再上映された。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINAL EXAM", "ファイナル・イグザム 惨殺の５日間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ジョン・カーペンター監督の「ハロウィン」の爆発的なヒットに刺激されて作られた無差別殺人ムービー。期末試験中の大学のキャンパスに、殺人鬼が忍び込んだ。やがて女子大生の悲鳴が辺りにこだまし、構内は次第に恐怖と血糊に染まっていく。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄色い風土", "黄色い風土", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "モダンなアクション映画を得意とする石井輝男監督が松本清張の原作を得て、推理ミステリーに挑んだ意欲作。『週刊東都』の記者、若宮は仕事で立ち寄った熱海で、新婚の夫が自殺し新婦が失踪するという事件に遭遇する。若宮と同僚の田原はこの事件を追うが、謎は深まるばかりであった。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("МОСКВА СЛЕЗАМ НЕ ВЕРИТ／MOSCOW　DOES　NOT　BELIEVE　IN　TEARS", "モスクワは涙を信じない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "1958年、モスクワ。資格取得を目指す努力家テリーナ。何ごとにも積極的なリュドミーラ。結婚を目前に迎えた控え目なアントニーフ。夢と希望を持って都会にやって来た彼女たちの以後20年にわたる、愛と苦悩そして友情などそれぞれの人生模様を温かい視線で描き上げた大河ドラマ。", "1980-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄火大名", "鉄火大名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "市川右太衛門主演“大名”ものの第３作。豊臣の恩義を忘れぬ信義の厚い福岡五十二万石・黒田長政の客分・後藤又兵衛は、太閣秀吉亡きあと家康につく外侯諸藩を腰抜け呼ばわりし、家康のド肝を抜いた。これを機会に又兵衛の失脚を謀る長政の家老三左衛門は、罠を仕掛け、まんまとはまった又兵衛は大隅城を落ちていく。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明治侠客伝・三代目襲名", "明治侠客伝・三代目襲名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1960年代に量産された東映ヤクザ映画のなかでも、最高傑作の一つに数えられる作品。ヤクザ一家の三代目を継いで、渡世の掟と近代化の波の間で苦悩する主人公を鶴田浩二が演じた。大阪・木屋辰一家の二代目が祭りの日に刺された。親分不在の木屋辰は、信頼の厚い代貸・菊地浅次郎のさい配で仕事を続けるが、商売仇の唐沢組の悪質ないやがらせもまた激化していく……。一緒に逃げてくれとせがむ女をふりきって鶴田の言う“あほな男や、せやけどわいにはこういう生き方しかでけへんのや”のセリフ、川辺で藤が鶴田に桃を手渡す場面など、忘れがたい美しさに満ちた作品。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一枚のハガキ", "一枚のハガキ", "", "", "http://image.eiga.k-img.com/images/movie/55610/315x210.jpg?1648", 114, "撮影当時98歳という日本最高齢監督の新藤兼人が、自らの実体験をもとに引退作として製作した戦争ドラマ。出演に豊川悦司、柄本明、大竹しのぶ、大杉漣らベテラン俳優が顔をそろえる。戦争末期に召集された中年兵士の啓太は、1人の兵士から「自分は戦死するだろうから生き残ったらハガキは読んだと妻を訪ねてくれ」と一枚のハガキを託される。終戦後、啓太はわずかな生き残り兵士となり故郷に戻るが、妻は父と出奔し、村で彼を待つ者は誰もいなかった。ハガキを書いた友子を訪ねると、彼女は家族を亡くし、貧しい農家でひとり懸命に生きていた。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("警察故事４簡單任務／FIRST STRIKE", "ファイナル・プロジェクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「ポリス・ストーリー　香港国際警察」を筆頭に製作された、J・チェン主演の“超級警察”シリーズ第４弾。香港の国際警察特捜隊員であるジャッキーにCIAから緊急指令が届けられた。任務は国際的密輸グループメンバーのナターシャというロシア女性をウクライナまで追跡するという簡単なもの。彼の頭の中はその後の休暇のことでいっぱいだった。だが、その裏には全世界を崩壊させるほどの威力を持つ謎の最終兵器の取引がからんでいた……。香港、ウクライナ、オーストラリアと世界を駆けめぐる、ジャッキー版“007”というべきサスペンス・アクション。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In Her Shoes", "イン・ハー・シューズ", "", "", "http://image.eiga.k-img.com/images/movie/1450/315x210.jpg?4590", 131, "キャメロン・ディアス演じるモテモテだけど無職のマギーは、優秀な弁護士だけど恋はうまくいかない姉ローズと大喧嘩して家を追い出され、祖母と共に暮らしながら本当の自分に気づく。姉ローズ役は「アバウト・ア・ボーイ」のトニ・コレット。祖母役は「愛と追憶の日々」の大ベテラン女優シャーリー・マクレーン。エミネム主演「8 Mile」「L.A.コンフィデンシャル」の監督カーティス・ハンソンが同名ベストセラー小説を映画化。", "2005-11-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("102 DALMATIANS", "102", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "大ヒットした動物喜劇「101」の続編。今回は101匹のダルメシアン犬にブチのない真っ白な子犬が加わり、さらにキュートな物語が展開する。毛皮マニアのクルエラとの対決から３年。ロンドンで平穏に暮らしていた愛犬家と犬たちに事件が舞い込む。刑務所にいたクルエラが出所したのだ。動物愛護のカリスマとして注目を集めるクルエラだったが、やがて過去の欲望に目覚め、世界一の毛皮のコートを作ろうとダルメシアンたちを狙い始める……。１匹だけブチのない子犬、オッドちゃんがコンプレックスからいじけたり、失敗する姿が実にかわいい。この役のために用意された子犬は10匹に及び、子犬のブチをデジタル・ペイントで消して撮影された。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STICKMEN", "スティックメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ウエリントンの場末のプールバーにたむろする３人の男たち。それぞれ性格は異なるが、賭けビリヤードを通じて結ばれている彼らは、破産寸前のプールバーのオーナーのため、多額の懸賞金が出るトーナメントに出場することに。しかし、主催者のマフィアは彼らに次々と強敵を送り込んでいく……。テンポよく繰り広げられていくビリヤード・ムービー。マフィアの部下がゲームのルールまできちんと説明してくれるという、ビリヤード未経験者のための気配りも嬉しい。", "2000-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トラブルシューター TROUBLE WITH NANGO", "トラブルシューター TROUBLE WITH NANGO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "元はヤリ手の刑事で、今はドサ回りの演歌歌手に身をやつす南郷大輔。南郷は、叔父の秋田匠三とたまたま入った喫茶店でママの悩みを聞き出す。二人はこのもめ事を手際もあざやかに解決。手にした金で、もめ事請負人“トラブルシューター”の事務所を開く。やがて舞い込んだ大きな仕事が、外国人女性ばかりを狙う強姦事件の犯人探し。しかしその裏には、暴力団・東山組の陰謀が隠れていたから大変。暴力団新法成立後の闇の街の事情を織り込みながら、トラブルシューターたちの日常を時に厳しく、時にとぼけた笑いの中に描き出す。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドキュメンタリーごっこ", "ドキュメンタリーごっこ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "仲間４人と、近所にある小学校の養護学級の様子を２年近くにわたり記録した中学生時代。自主制作映画は完成せず、その時撮影した５時間にもおよぶ８mmフィルムは長い間、押入れにしまわれていた。カメラマン内藤雅行のプライベートフィルムを、35年の時を経て、伊勢真一が構成・編集して世に送り出したドキュメンタリー映画。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男たちの挽歌 A BETTER TOMORROW", "男たちの挽歌 A BETTER TOMORROW", "", "", "http://image.eiga.k-img.com/images/movie/55890/315x210.jpg?4218", 123, "ジョン・ウー監督の出世作として知られるバイオレンス・アクション「男たちの挽歌」を、「私たちの幸せな時間」の韓国人監督ソン・ヘソンがリメイク。脱北を目指しながらも生き別れとなってしまったヒョクと弟のチョル。数年後、脱北に成功したヒョクは、親友ヨンチュンとともに釜山を拠点とする武器密輸組織の大物となり、行方不明になっていたチョルと再会を果たす。「ゴースト　もういちど抱きしめたい」のソン・スンホンら韓国の人気俳優陣が出演する。", "2011-02-19", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎恋歌", "男はつらいよ・寅次郎恋歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "さくらの夫・博の父親である老哲学者から“一家の団らんこそ真の幸福である”と諭された寅次郎だが、柴又帝釈天前に開店した喫茶店の未亡人ママにまいってしまう。彼女の息子の自閉症を直してやるが……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("酔いどれ波止場", "酔いどれ波止場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "脚本・新藤兼人、監督・井上昭のコンビによる“酔いどれ”ものの１編。酔いどれの名医・ギョロ松は、ある港町にふらりとやって来る。ギョロ松は町医者のオイボレ先生のもとで評判の医者となり、二組の暴力団をうまく操って町を一掃する。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Astronaut Farmer", "庭から昇ったロケット雲", "", "", "http://image.eiga.k-img.com/images/movie/53580/315x210.jpg?2599", 104, "実家の農場を継ぐことになった元宇宙飛行士ファーマー。宇宙への夢を諦めきれない彼は、自分でロケットを作ることを決意。家族の協力を得て農場で準備を進めるが、FBIやCIAから危険人物として目を付けられてしまい……。デビュー作「ツイン・フォールズ・アイダホ」で注目を集めたマイケル＆マーク・ポーリッシュ兄弟によるヒューマン・ドラマ。主演は「バッドサンタ」などの個性派俳優ビリー・ボブ・ソーントン。", "2008-07-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Constant Gardener", "ナイロビの蜂", "", "", "http://image.eiga.k-img.com/images/movie/1130/315x210.jpg?2347", 128, "「シティ・オブ・ゴッド」のフェルナンド・メイレレス監督がスパイ小説の名手ジョン・ル・カレの同名小説を映画化。アフリカで暮らす英国外務省一等書記官ジャスティンと妻テッサ。だがある日、テッサが謎の死を遂げ、ジャスティンはその原因を追求していく中で、彼女がその信念のために巨大な陰謀に巻き込まれていたこと、そして彼女の真実の愛を知る。妻テッサ役のレイチェル・ワイズがアカデミー助演女優賞を受賞。", "2006-05-13", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AGE OF INNOCENCE", "エイジ・オブ・イノセンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "1870年代のニューヨーク。若き弁護士のニューランドは幼なじみのエレンと再会し、その美しさに心を揺さぶられる。ニューランドには婚約者がおり、エレンにも離婚を承知しない夫がいた。世間体ばかりを気にする上流社会のなかで、二人の恋は不可能なはずだった。だが、ニューランドとエレンの思いはその壁をのり越えるほど強かった……。ニューヨークを撮り続けてきた鬼才M・スコセッシが、19世紀末に舞台を移して描く“完璧なるラブ・ストーリー”。悲恋に終わるはずの関係がいかにして完璧に成就されるのか？ D・デイ・ルイス、M・ファイファーをはじめ個性的なキャストをそろえた、熱い感動を呼ぶ秀作である。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の上海", "夜の上海", "", "", "http://image.eiga.k-img.com/images/movie/34110/315x210.jpg?1447", 110, "仕事や恋に悩むヘアメイクアーティストの水島は、仕事で訪れていた上海の街をさまよい歩くうちに迷子になってしまう。そんな時、女性タクシー運転手のリンシーと偶然に知り合い、言葉も通じないまま行動を共にすることになるが……。「スパイ・ゾルゲ」の本木雅弘と「少林サッカー」のビッキー・チャオによる日中合作ラブロマンス。西田尚美、塚本高史、ディラン・クォ、サム・リー、竹中直人ほか日本、台湾、香港から共演者が集う。", "2007-09-22", "76,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beautiful Sunday", "Beautiful Sunday", "", "", "http://ecx.images-amazon.com/images/I/31E9SQEHX3L._SL210_.jpg", 93, "CMディレクターの中島哲也が「夏時間の大人たち」に続いて手掛けた劇場用映画の監督第２作。静かな住宅地のマンションを舞台に、会話のない若夫婦や、嫌いな女の子と張り合いガリ勉に励む少女、決まって正午に奇声を発する老婆、ストーカーに追われるナルシストの女性など、住人たちの休日を捉えていく。けだるいムードとシニカルなユーモアが全編に充満。“楽しくない日曜日”を、ズレた笑いとともに伝える異色作だ。数々のアニメ作品などで知られる菅野よう子が音楽を担当している。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STELLA", "ステラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "かつてハリウッドの大プロデューサーとして知られたサミュエル・ゴールドウィンが生前、1925年と1937年に二度映画化した名作を、息子がリメイク。未婚の母として娘を育ててきたヒロインは、娘の幸福のためにやがて自分の夢を犠牲にしてしまう。強くも哀しき母親をB・ミドラーが熱演。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARK CITY", "ダークシティ", "", "", "http://ecx.images-amazon.com/images/I/51d0jArsaVL._SL210_.jpg", 100, "「クロウ−飛翔伝説−」のA・プロヤスが描くSFスリラー。太陽が昇らない漆黒の街。何者かによって記憶を奪われたジョンは、自分が連続殺人犯として追われていることを知りがく然とする。妻のことも思い出せず、自分が何者かも分からないまま追い詰められていく彼の前に、やがて正体不明の男たちが現れる。謎が謎を生むミステリアスなドラマが、混沌とした雰囲気の中に展開。緻密なデザインによるセット、SFXを駆使した壮大なスケールの都市空間など、洗練された映像が秀逸。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PREFONTAINE", "プリフォンテーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "全米を熱狂させた伝説のランナー、スティーブ・プリフォンテーン。彼が人気絶頂期、24歳の若さで他界するまでを、彼の家族や友人、スポーツ選手のインタビューを交えてドキュメンタリー・タッチで描く。プリフォンテーンはオレゴン大学で名コーチ、ビル・バウワーマンと出会い、才能を開花。ハンサムで自信家で、カリスマ性を持つ彼はファンやマスコミを魅了するが、1972年のミュンヘン・オリンピックで敗北してしまう。その後スランプを抜け出し、再度オリンピックに挑戦するためプロ契約を蹴って、アマチュアとして活動。1975年に、自ら企画した国際競技大会で活躍するが、その夜、交通事故に遭う。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALIFORNIA DREAMING", "カリフォルニア・ドリーミング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "シカゴのミーハー少年“TT”が夢のカリフォルニアに憧れてやって来た。サーフィン、ビキニ・ギャルズ、ビーチ・バレー、そしてホロ苦い恋。軽くて陽性なアメリカン・ライフの一面を描く。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レッド・ティアーズ", "レッド・ティアーズ", "", "", "http://image.eiga.k-img.com/images/movie/57350/315x210.jpg?3358", 87, "アクション俳優の倉田保昭がプロデュースしたバイオレンススリラー。都内各地で頭部を切断された遺体が発見され、失踪者も続出する。事件を追う刑事の野島は、捜査の過程で美しい女性・紗代子に出会い、2人はひかれあっていく。一方、野島の先輩刑事・三島は特殊部隊を率いて都会に潜む吸血鬼一族を抹殺するという極秘任務に就いていたが……。事件の鍵を握る紗代子役に加藤夏希が扮する。2011年・第24回東京国際映画祭「日本映画・ある視点」部門にて「レッド・ティアーズ　紅涙」のタイトルで上映。12年、劇場公開。", "2012-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WRECKING CREW", "サイレンサー第4弾／破壊部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "D・マーティン演じるプレイボーイ・スパイ、マット・ヘルムが活躍する“サイレンサー”の第４作。デンマークで列車が襲われ、50億ドルの金塊が奪われた。マットはICEのチーフ、マクドナルドの依頼で、強盗団の首領と思われるコンチーニ伯爵を探るため、コペンハーゲンへと向かうが……。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN TO OZ", "オズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ジュディ・ガーランド主演のミュージカルとして有名な「オズの魔法使」が、ディズニー・プロダクションの手により最新SFXを駆使した超大作としてよみがえった。オズの国へ誘いこまれたドロシーが、悪の化身を倒して、再び平和をもたらす。おなじみのキャラクターが続々と登場。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUARTET", "カルテット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "女流作家ジーン・リースが発表した同名の半自伝的小説の映画化。退廃的な1920年代のパリ。美しいコーラス・ガールのマリアは、夫のステファンの突然の投獄によって行き場を失い、芸術家のパトロンとして知られるハイドラー夫妻のもとに身を寄せる。やがてマリアを愛人にしてしまうH・J・ハイドラー。倦怠した結婚生活を維持しようと、それを黙認する彼の妻、ロイス。そして刑務所の夫、ステファン。この４人が奏でる華麗な四重奏（カルテット)。1920年代のモンマルトルの華美で退廃的な風俗と人間模様が、見事に捉えられている。", "1981-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Karate Kid", "ベスト・キッド（2010）", "", "", "http://image.eiga.k-img.com/images/movie/55270/315x210.jpg?4514", 140, "1984年の同名映画をウィル・スミス＆ジェイダ・ピンケット・スミス製作、ジェイデン・スミス＆ジャッキー・チェン主演でリメイクしたアクションドラマ。父親を亡くし、母と2人でアメリカから北京に引っ越してきた少年ドレ（スミス）。言葉にも文化にも馴染めず、地元の子供たちにいじめられる毎日を過ごすドレだったが、ある日、マンションの管理人ハン（チェン）に助けられたことをきっかけにハンからカンフーを習うことになる。監督は「ピンクパンサー2」のハラルド・ズワルト。", "2010-08-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Door in the Floor", "ドア・イン・ザ・フロア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "不幸な事故で息子を失った一家の哀しみを、部外者である少年の目を通して描いた人間ドラマ。心に潜む業や愚かさを描きながらも、悲壮感に偏らないユーモラスなムードなど、アメリカ現代文学の巨匠J・アーヴィングの世界をみごとに映像化している。児童作家テッドの助手に雇われた高校生のエディは、テッドと別居中の妻マリアンに一目惚れ。ひょんなことから彼女と愛人関係になる。", "2005-10-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES RENDEZ−VOUS DE PARIS", "パリのランデブー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "E・ロメールが前作「木と市長と文化会館、または七つの偶然」のスタッフとともに作り上げた、３話オムニバス構成で描く相変わらず若々しい恋愛コメディ。第１話「７時の約束」は、法学部の女子大生の物語。恋人の浮気を疑う彼女が、見知らぬ男から求愛を受け、彼の言う待ち合わせ場所に赴くと、事の真相がハッキリする。第２話「パリのベンチ」の主人公は、郊外に住む文学教師。秋に逢瀬を重ねた女性と、パリを訪れた観光客の気分でホテルへ。ところが、彼女は留守中の同棲相手が他の女を連れてそこに入って行くのを目撃してしまう。そして第３話「母と子1970年」も、ある待ち合わせと男の浮気心を描いたもの。ピカソ美術館に知り合いのスウェーデン娘を案内することになった画家が、すれ違った人妻に興味を覚え彼女をアトリエに招く。DVDは「エリック・ロメール コレクション(6)」に収録。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TRIDENT FORCE", "砂漠の戦士 テロ・ファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "国際的テロ事件を描く荒唐無稽な戦争アクション。舞台は“世界の火薬庫”中東。残虐なテロ行為を続けるパレスチナ革命軍に頭を痛めていた各国は、国際連合組織として反テロリスト部隊“トライデント・フォース”を結成。悪の張本人ハッサドの組織壊滅に乗り出す。砂漠地帯のハデな戦闘シーンが見もの。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JENNIFER EIGHT", "ジェニファー8", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "都会を離れ田舎町の警察に赴任したジョン。愛妻に逃げられ、生活に疲れ果てた彼が最初に出会った事件は、盲目の女性ばかりを狙う連続殺人だった。捜査の中でジョンは、ヘレナという盲目の美しい女性と知り合う。そして姿なき犯人の次のターゲットは、ヘレナだった……。執拗に事件を追う刑事を、A・ガルシアが好演、U・サーマンと初共演している。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sing for Darfur", "シング・フォー・ダルフール", "", "", "http://image.eiga.k-img.com/images/movie/54520/315x210.jpg?3941", 78, "世界規模のチャリティイベント「Sing for Darfur」が行われるスペイン・バルセロナのとある1日を描いた意欲作。人が人に触れることで物語の主人公が入れ替わり、さまざまな視点で1日が展開していく。スーダンのダルフールと無縁に暮らす人々を描き、国際的な社会問題となっているダルフール危機を想起させる。監督はオランダの新鋭ヨハン・クレイマー。", "2009-09-19", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANTZ", "アンツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "アリたちの世界を、３D−CGテクノロジーを駆使して描くアニメーション。10億匹のアリが住むコロニーの働きアリ、Z(ジー)は今の自分の立場にちょっぴり不満。そんなある日、王女バーラに一目惚れしたZは、彼女に会うため兵隊アリになりすまして歓兵式に出ることにする。ところが、白アリ軍団との戦争が始まり、思いがけず大活躍したZだが、正体がバレてしまい大騒動に発展。その騒ぎに乗じて、Zはバーラをさらって昆虫の楽園インセクトピアを目指して旅立つが……。表情豊かでリアルに動くアリたちの世界がいきいき描かれる。なかでも、６万匹以上のアリの群衆による戦闘シーンや、アリの視点から捉えた人間の世界など、ダイナミックな映像が圧巻。悩み多き主人公にW・アレン、タフな王女にS・ストーンと、豪華俳優陣が声優として結集した。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GABY−A TRUE STORY", "ギャビー 愛はすべてを越えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "生まれながらに脳性小児マヒというハンディを負った少女ギャビーの、成長を追った愛と感動の実話ドラマ。ギャビーが自らの半生を綴った本はメキシコでベストセラーとなり、彼女の全面協力のもと、７年の歳月をかけてメキシコの新進監督マンドキが現実的で力強い作品に仕上げた。スウェーデンの演技派L・ウルマンほか、豪華な顔ぶれが楽しませる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUSH HOUR", "ラッシュアワー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「レッド・ブロンクス」全米ヒットの勢いに乗って、再びハリウッドに進出したジャッキー・チェンが刑事役を熱演するコミカルなアクション。L.A.在駐の中国領事の娘が誘拐されるという事件が発生。FBIに邪険にされた市警のはみだし刑事カーターは、香港から来た捜査官リーと、厄介者同士で捜査に乗り出す。FBIにニラまれつつも、彼らは真相に近づき、やがて意外な黒幕の正体を突き止める……。ジャッキーのスピーディーな立ち回りに、カーターに扮するC・タッカーのマシンガン・トークが噛み合い、歯切れのよい物語が楽しめる。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火の鳥", "火の鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "手塚治虫の長編SFマンガ『火の鳥・黎明編』の映画化。永遠の時間にわたって人類の運命を温かく見守る“火の鳥”の視点から、人類の歴史を描いた作品で、日本の古代史のヤマタイ国を舞台にしている。部分的にアニメーションを使用。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛情の系譜", "愛情の系譜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "原作は円地文子の新聞小説。この時期、五所平之助は松竹で、井上靖ら人気作家の小説をもとにメロドラマを連続して作っていた。この作品もその一つ。社長令嬢と結婚するために、恋人が二人の関係を清算したがっていることを知ったOLの心理を追う。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朝鮮の王（原題）", "朝鮮の王（原題）", "", "", "http://image.eiga.k-img.com/images/movie/57970/315x210.jpg?1610", 0, "", "2012-12-31", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワニと鸚鵡とおっとせい", "ワニと鸚鵡とおっとせい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "サーカスをクビになった仲良しの男女が、退職金がわりにもらったオウムの鳥かごの中から1,200万円也の借用書を発見、そこから起こる珍騒動ぶりを描いたコメディ。若者の初恋と下町の人情をも絡ませ、生け花師匠役を池波志乃が好演している。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("King Rat", "キング・ラット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "太平洋戦争終結間際の捕虜収容所を舞台にした戦時下の人間ドラマ。1945年、シンガポールの日本軍捕虜収容所では、捕虜の自主性を尊重する管理体制の下で、米軍伍長キングが牢名主のように権力を振るっていた。英国の捕虜グレイは彼の不正を告発しようとするが、なかなかうまくいかない。やがて終戦が訪れ……。極限下の人間模様を、シニカルな視点で描く。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("福星高照／MY　LUCKY　STARS", "香港発活劇エクスプレス　大福星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "世界的大スター、ジャッキー・チェン主演の現代アクションもの。香港警察のドジな刑事チェンと相棒のピョウは、汚職ポリスとその悪漢たちを追跡して日本までやって来る。追いつ追われつの迫真のカーチェイス、見どころいっぱいの娯楽編。「五福星」に続く、“福星”シリーズ第２作。ビデオ発売時タイトルは「大福星」。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAKING CONTACT", "デビル・ドール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "死んだ父親と霊界交流できるジョーイ少年が、その霊的能力の強さで、邪悪な腹話術人形をも呼び寄せてしまった……。荒れ狂うポルターガイスト現象を低予算で映像化したのは、後に「インデペンデンス・デイ」を撮ることになる西ドイツ出身の監督R・エメリッヒ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAVINCI’S WAR", "ダヴィンチ・ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "今やニューヨークをしのぐかという犯罪都市となったロサンゼルスを舞台に、ベトナム帰還兵、フランク・ダヴィンチが、妹を殺した謎のテロリスト集団に、敢然と戦いを挑むアクション。フランクは、協力者として、プロの殺し屋チャイナ・スミスを得、テロリスト集団を探すが、その陰にはCIAの不気味な動きがあった。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAGECOACH", "駅馬車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "９人の乗客を乗せた駅馬車がアリゾナからニューメキシコまでひた走る。乗客は、アル中の医者、守備隊の夫を訪ねる若妻、復讐の鬼と化した脱獄囚、賭博師、脱獄囚を追う役人、臆病な酒商人、町を追放された商売女、公金横領の銀行家、そして気の小さい御者、と顔ぶれは実に多彩だ。映画は、乗客の様々なエピソードを盛り込みながら、インディアンの襲撃、そしてクライマックスの脱獄囚キッドと３人の無法者との対決までを息をもつかせず描破する。ヘイゲマンのテーマ音楽、グレノンのカメラ、ニコルズの脚色、ウェインの颯爽としたキッド、加えるにダイナミックなアクションと圧倒的なスピード感と、すべてにわたって素晴らしい。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("That Kind of Woman", "私はそんな女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "　1944年、第2次大戦末期。ニューヨークに向かう列車に乗り込んだアメリカ兵のレッドとその友人ケリーはケイとジェーンに出会う。レッドは黒髪の美しい娘ケイに一目惚れ。しかしケイは自分が実業界の大物に囲われている身だと言い、レッドをかわす。諦めきれないレッドは、その後、再びケイを訪ねる。哀れに思ったケイは思い悩むが……。揺れ動くヒロインをソフィア・ローレンが演ずるラブ・ロマンス。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クローズ・ユア・マインド　馬熊横町 TWILIGHT FILE III", "クローズ・ユア・マインド　馬熊横町 TWILIGHT FILE III", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "オムニバスシリーズ「TWILIGHT FILE」第3弾の1作。女優やモデルなど幅広いジャンルで活躍する加藤夏希が主演を務めるラブストーリー。精神状態が不安定な女子大生・小夜は、家族や親友に心配されながら毎日を過ごしていた。そんなある日、小夜は河原でテント生活を送る放浪者の集団と出会う。彼らの自由奔放な生き方にあこがれを抱いた小夜は、その中の1人の無口な青年・亮と恋に落ちる。しかし小夜との恋は、亮を絶望と希望の混ざりあう未来へと導いていく。", "2007-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("傷だらけの人生", "傷だらけの人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "鶴田浩二の大ヒット曲『傷だらけの人生』の人気に便乗して映画化された作品だが、小沢茂弘監督の折目正しい演出で見ごたえある任侠映画に仕上がった。鶴田浩二と若山富三郎が義兄弟に扮し、大人の芝居をたっぷりと見せてくれる。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガメラ 大怪獣空中決戦", "ガメラ 大怪獣空中決戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ガメラ生誕30年を記念して、14年ぶりに復活した新生ガメラ。敵は、第３作で登場したギャオスだが、旧作のイメージを一新、超古代文明の遺伝工学が生み出した、異常な繁殖能力と三段進化で人類を破滅の恐怖に陥れる怪鳥として現れる。はるかな時を超え、現代日本に甦ったギャオス、そしてギャオスを追って長い眠りから覚めたガメラ。福岡ドームを舞台に繰り広げられた２匹の壮絶な戦いは、東京へ場所を移し、首都上空で大空中バトルが始まる。ガメラとただ一人交信できる美少女を演じる藤谷文子は、三井不動産の６代目リハウスガールであり、スティーヴン・セガールの娘でもある。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHORT CIRCUIT", "ショート・サーキット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "最終戦争用に開発されたロボット、“ナンバー5”は、落雷でショートしてから人間感情に目覚める。彼を破壊しようとする研究所の手から救い出してくれた若い女性に恋する彼……。パロディ満載のサスペンスとユーモアあふれるロボット版“E.T.”ともいえる一編。主役のロボット・ジョニー５のデザインを「ブレードランナー」のシド・ミードが担当している。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Road to Guantanamo", "グアンタナモ、僕達が見た真実", "", "", "http://image.eiga.k-img.com/images/movie/33830/315x210.jpg?4804", 96, "無実の青年たちがテロリストとしてアメリカ政府に拘束され、2年以上もの間キューバのグアンタナモ米軍基地にある収容所に収監されたという実際の事件を映画化した衝撃作。地獄のような収容所生活を送りながらも希望を捨てずに闘う青年たちの姿を通し、グアンタナモの恐るべき真実を暴き出す。監督は「イン・ディス・ワールド」のマイケル・ウィンターボトムと、これまでウィンターボトム作品の編集などを務めてきたマット・ホワイトクロス。", "2007-01-27", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HAPPIEST MILLIONAIRE", "最高にしあわせ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "夢多き青年ジョン・ローレンスは、大富豪ビットル氏の執事になろうと面接を受けにきた。ところが、ビットル家はそろいもそろって変わり者ばかり。ジョンはあきれて帰ろうとするのだが、ひょんなことで富豪から大いに気に入られてしまう。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャップ・ザ・ロック・リボルバー", "ジャップ・ザ・ロック・リボルバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "2008年に結成20周年を迎えた聴覚障害者4人と健聴者1人による手話ロックバンド「BRIGHT EYES」の活動にスポットを当てたドキュメンタリー。ライブ映像やメンバーへのインタビューを織りまぜながら、生きる喜びや圧倒的な孤独のすべてを“ロックンロール”というパワーに変えていくメンバーの生きざまを捉える。監督は、「電撃BOPのセクシーマザーファッカーズに!!」の島田角栄。", "2009-05-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Martin", "マーティン／呪われた吸血少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "　ピッツバーグの親戚の家で暮らすことになったマーティンは列車での旅の途中、女性客を襲い、彼女の血をむさぼった。マーティンを出迎えた老人クーダーは彼を悪魔呼ばわりし、その行動に目を光らせる。一方、クーダーと同居する孫娘クリスティーナは狂信的な祖父の言葉を信じようとしない。そんな中、マーティンは血を求めて真夜中の街を徘徊し、さらに自分の身の上をラジオの深夜放送で告白するが……。ジョージ・A・ロメロが放った新種の吸血鬼ホラー。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ごくつま刑事（デカ）", "ごくつま刑事（デカ）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "作・香川まさひと、画・和気一作によるコミック「ごくつま刑事」を、松本莉緒主演で実写映画化。高級クラブ「京桜」のママ、黒不二組若頭の妻、警視庁総務部統計課特別室室長付きの隠密捜査官という3つの顔をもつ京極さくらは、服役中の夫の刑期短縮のため、新生ドラッグ「スキップ」の取引現場にスパイとして潜入することになる。しかしその先には、巨悪な組織に仕組まれたワナが待ち受けていた。", "2010-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vampires", "ヴァンパイア／最期の聖戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "　バチカンの命を受けて活動する吸血鬼ハンター集団がニューメキシコで敵を始末する。しかし復讐に燃える吸血鬼のボス、バレックが逆襲を仕掛け、彼らを虐殺。さらにバレックは陽光の下でも活動できる力を手に入れるため、その儀式を行なおうとする。生き残ったハンターのジャックとモントーヤが阻止に動き出すが……。バンパイアVS凄腕ハンターの死闘をスタイリッシュに描いたアクションホラー。続編「ヴァンパイア／黒の十字架」も製作された。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("漂流街 THE HAZARD CITY", "漂流街 THE HAZARD CITY", "", "", "http://image.eiga.k-img.com/images/movie/39100/315x210.jpg?2318", 103, "日系ブラジル人のマーリオは、不法滞在で強制送還される恋人、中国人のケイを取り戻すため入国管理局のバスを襲撃する。無事ケイを取り戻し、二人は日本脱出を決意するが、ケイに異常な執着を見せる中国マフィアのボス、コウがあらゆる手で二人の行方を阻むのだった。脱出に大金が必要と知ったマーリオは、コウが仕切る闘鶏場の上がりを奪う計画を実行するが、なんと奪ったのはコウが岡島組と取引していたコカインだった……。", "2000-11-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I DRINK YOUR BLOOD", "処刑軍団ザップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ヒッピー・カルチャーを反映したカルト・ホラー。ニュージャージーの小さな町にやってきた凶暴なヒッピー集団。“サタンの息子”を名乗る彼らは、町の娘をレイプするなど、野蛮の限りを尽くす。怒った少年が、狂犬病の犬から抜き取った血液を彼らの食物に忍ばせた。やがてヒッピーたちは殺し合いをはじめ、それは町の人々にも飛び火する。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Celebrity", "セレブリティ", "", "", "http://ecx.images-amazon.com/images/I/516qyvN0p1L._SL210_.jpg", 114, "W・アレンが監督に専念し、セレブリティ（有名人）の仲間入りを目指す中年男の悲喜こもごもを、軽快なモノクロ映像で活写したコメディ。高校の同窓会に出席したことをきっかけに、人生を変えようと決意した作家志望の芸能記者リー。ところが彼を待ち受けていたのは、全身性感帯のスーパーモデルや若手人気俳優とのトラブルだった。一方、リーと離婚したロビンは積極的な生き方に目覚め、TVレポーターとして頭角を現していく。主演のK・ブラナーがアレンそっくりのオドオド演技を披露。L・ディカプリオがホテルで大暴れするアイドル・スターに扮するなど、豪華キャストがスキャンダラスな人間模様をイキイキと演じている。", "1999-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SARATOGA", "サラトガ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "競馬で有名な土地サラトガで牧場を営むクレイトンが、馬券屋のデュークに多額の借金を残したままこの世を去る。娘のキャロルは、父が残した競馬馬を競売にかけて借金を返済しようと奔走するが、次第にデュークに惹かれていく。C・ゲーブル、J・ハーローのコンビが「紅塵」「妻と女秘書」に続いて共演したラブ・ストーリー。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANGEROUS SEDUCTRESS", "グール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "恋人との悲惨な別れに傷ついたスーザンは、姉の住むジャカルタの屋敷で快適な生活を送っていた。そんなある日、彼女が知らずに手にした一冊の本は、姉がインドネシアの魔術研究をしている友人に貰ったスマトラの古い書物だった。そして、彼女が何気なく口にしたフレーズが悪霊を呼び出してしまうことに……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PIRATE MOVIE", "パイレーツ・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "孤独な少女メイベルは、波止場で出会った青年フレデリックに一目惚れしてしまう。そして、夢の中に海賊王子に扮した彼が現れて……。ギルバート＆サリバンのオペレッタ、『ペンザンスの海賊達』をミュージカル仕立てで映画化。", "1982-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PADAYAPPA", "パダヤッパ・いつでも俺はマジだぜ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 180, "大ヒット作「ムトゥ 踊るマハラジャ」で一躍日本を席巻したインドのスーパースター＝ラジニカーントが、同じくK.S.ラヴィクマール監督と組んだ一大エンターテインメント。もちろん歌あり踊りありの豪華絢爛マサラ・ムービーだ。信心深い娘と富豪の令嬢が、偶然通りかかったパダヤッパに恋をする。娘を選んだパダヤッパにプライドを傷つけられた令嬢が仕組んだ復讐大作戦とは？　爆笑かつ驚愕必至の怒濤のカーチェイス・シーンにもご注目。", "1999-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DET SJUNDE INSEGLET", "第七の封印", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "巨匠ベルイマンが、ペストの流行する終末的世相を呈していた中世に、現代に通じる死生感の問題を探った作品。遠征に失敗した十字軍の騎士が、人生に懐疑を抱き諸国を遍歴する。宗教に解答を求める騎士の悲壮な姿を、古典様式の画面にドラマティックに描いた秀作。", "1956-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎紅の花", "男はつらいよ・寅次郎紅の花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "神戸から瓦せんべいをよこしたきり、音沙汰のない寅。くるまやの面々が噂をしていると、村山首相と一緒に寅の姿がテレビに映し出される。どうやら震災後の神戸でボランティアとして働いていたらしい。一方、甥の満男のもとに、かねて思いを寄せる泉が訪れ、見合い相手との結婚を考えていると伝える。思わず祝福の言葉をかける満男だが、式当日、花嫁行列を通せんぼして大騒動に。傷心の満男は、その足で奄美大島にたどり着く。一文無しの満男は美しい女性に助けられ、案内された家には呑気にくつろぐ寅の姿が。女性は、寅のかつての恋人・リリーだった。そして、結婚をやめた泉が満男を追いかけてくる。満男と泉、そして寅とリリーの恋の行方は……。フレームを強く意識した画面作りも印象的な堂々たる第48作。幕切れも絶妙で、はからずも有終の美を飾ることになった。浅丘ルリ子が11作、15作、25作に続いて15年ぶり４度目のリリーを演じている。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gulliver's Travels", "ガリバー旅行記", "", "", "http://image.eiga.k-img.com/images/movie/55190/315x210.jpg?4748", 85, "ジョナサン・スウィフトによる同名古典小説を現代に置き換えて3D映画化したジャック・ブラック製作・主演の冒険コメディ。ニューヨークの新聞に寄稿する旅行ライターのレミュエル・ガリバー（ブラック）は取材でカリブ海のバミューダ諸島に行くことに。だが、その途上で巨大な竜巻に巻き込まれてしまう。気がつくとガリバーは浜辺に打ち上げられ、体長15cmほどの人間たちに縛り上げられていた。監督は「モンスターVSエイリアン」のロブ・レターマン。", "2011-04-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版デュエル・マスターズ", "劇場版デュエル・マスターズ", "", "", "http://ecx.images-amazon.com/images/I/61PG4GRTWXL._SL210_.jpg", 46, "人気カードゲームの劇場版アニメ。謎のデュエリスト、オルフェと姉のエレクトラがデュエル大会を開催する。日本一のデュエリスト、切札勝舞（きりふだしょうぶ）をはじめ、最強のデュエリストたちが集結。だが、大会の陰には恐ろしい陰謀が隠されていた……。", "2005-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志 荒神山・前編", "次郎長三国志 荒神山・前編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "吉良の仁吉は祭礼の賭場をめぐる神戸の長吉と安濃徳の縄張り争いの仲裁に立つ。しかし、仁吉は安濃徳の黒幕である黒駒勝蔵を敵に回してしまい、清水一家とともに荒神山で一戦を交えた際に、銃弾に倒れる。結局「荒神山」は前編だけで後編は作られなかった。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハードライフ　紫の青春・恋と喧嘩と特攻服", "ハードライフ　紫の青春・恋と喧嘩と特攻服", "", "", "http://image.eiga.k-img.com/images/movie/56190/315x210.jpg?1735", 82, "ヤンキー雑誌「ティーンズロード」で人気を博した女性暴走族の元総長・中村すえこの自伝「紫の青春　恋と喧嘩と特攻服」を映画化。1990年代、埼玉県東松山。高橋すえこは中学生になると髪を染め、万引きやタバコを覚えて非行に走っていく。やがてレディース暴走族「紫優嬢」に入ったすえこは、特攻服を身にまといケンカに明け暮れ、次第に頭角を現していく。主演は「受験のシンデレラ」の寺島咲。", "2011-05-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Anthony Zimmer", "アントニー・ジマー", "", "", "http://ecx.images-amazon.com/images/I/51GsHNPJhNL._SL210_.jpg", 0, "フランスの人気女優ソフィー・マルソーと「フレンチなしあわせのみつけ方」のイバン・アタル共演によるラブサスペンス。警察からもマフィアからも追われる凄腕の金融犯罪者アントニー・ジマー。整形手術を受けたため、現在の彼の姿を知る者は誰もいない。そんな中、警察はジマーが恋人キアラに会いに来るという情報を得るが……。2010年にジョニー・デップ＆アンジェリーナ・ジョリー共演により「ツーリスト」としてリメイクされた。", "2005-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PELLE", "狂える戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "第二次大戦末期、ドイツ軍が撤退したあとのローマに、連合軍が入城してくる。華々しい戦闘シーンではなく、敗戦国イタリアの様々なエピソードを積み上げて戦争の狂気の実態を描く。L・ウェルトミューラーと並んで現代イタリアを代表する監督のカヴァーニが「善悪の彼岸」のあとに作った戦争ドラマ。原題は“皮膚”の意味。", "1981-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NET", "ザ・インターネット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ロサンゼルスに住むアンジェラ・ベネットは、自宅を仕事場にしているフリーのコンピュータ・アナリスト。彼女は人とのつき合いを持たないばかりか、唯一の肉親である年老いた母も数年前からアルツハイマー病を患い、アンジェラが娘であることも識別できなくなっていた。ますます自分の世界に閉じこもってしまう彼女。そんなある日、アンジェラは偶然にも政府機密にアクセスしてしまう。事の重大さに気付かない彼女はバカンスへと旅立ち、謎の男ジャックに命を狙われる。何とか逃げきってロスに戻ってきたアンジェラだったが、自分のアイデンティティーが奪われ、代わりに犯罪歴のある別人のそれにすり替えられてしまったことを知る。現代人にとって便利さの象徴である“情報化社会”が、逆に人を窮地に陥れてしまうという恐怖を描いたサスペンス。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戀戦沖縄", "恋戦。OKINAWA Rendez-vous", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "沖縄を舞台に、失われた手帳をめぐって３人の男女が危険な犯罪に巻き込まれていくクライム・サスペンス。日本人のサトウから警視庁にある手帳を盗むように依頼されたジミーは、手帳と200万ドルの報酬受け渡しのために沖縄を訪れる。しかし、受け渡しの直前、報酬がサトウの愛人ジェニーによって持ち逃げされてしまい……。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シークレット・ワルツ", "シークレット・ワルツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "父親から性的虐待を受けた心の傷から、愛し合った相手を傷つけずにはいられないマキに、切ない思いを寄せる血のつながらない弟の慎二。あるきっかけから姉弟は犯罪の世界に巻き込まれていく。そこで出会う人々は皆、孤独でピュアで、不器用ゆえに凶暴で破滅的、そして愛を求めていた。姉と弟も、いつの間にか二人の絆を見失ってしまい……。「ダイヤモンドの月」で第３回ゆうばり国際冒険・ファンタスティック映画祭オフシアターコンペ部門グランプリを受賞した野火明が、その時の審査委員長であるツイ・ハークに認められ、ツイとの共同原案によって監督した劇場用映画デビュー作。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THOSE MAGNIFICENT MENIN THEIR FLYING MACHINES", "素晴らしきヒコーキ野郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "人類が空を飛びたいと願い、その夢が実現した1900年初頭。世界中から愛機を駆ってやって来たヒコーキ野郎たちが、ロンドン～パリ間レースに参加する。各国の個性的な飛行機の登場が観る者を楽しませてくれる。フォックスほか、ホイットマン、日本から石原裕次郎が出演した娯楽作。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A VERY BRADY SEQUEL", "ゆかいなブレディー家　トラブルinハワイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "往年の人気TVドラマに基づくファミリー・コメディの劇場用シリーズ第２作。ブレディー家のママ、キャロルが陶器の壷を、１億円の値打ちがあるとも知らずにバザーに出してしまったから、さあ大変。騒動は、やがてキャロルの誘拐事件にまで発展してしまう。家の人々はキャロルを救うため、犯人である彼女の前夫を追ってハワイへ飛び……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUPS", "パップス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "将来に希望を見いだせない13歳の少年スティビーは、母親が無造作にしまっていた拳銃を発見し、一つ年下のロッキーと銀行に強盗に入る。地元警察とFBIに包囲され、銀行に立てこもる二人。状況は緊迫し、事件は思わぬ方向へと進んでいく……。「BANG」で一躍注目を浴びたアッシュと、チーム・オクヤマが手を組んで作り上げた衝撃作。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA SPOSA AMERICANA", "アメリカン・ブライド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "アメリカ国籍ほしさにアメリカ女性エディスと結婚したイタリア男エドアルド。しかし、彼は結婚式の時に会ったエディスの兄の妻アンナに心惹かれる。エドアルドとアンナは、やがて愛し合うようになり……。アメリカとイタリア、二つの国を舞台に、大人のアバンチュールを描いた作品。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／TUBE", "TUBE　チューブ", "", "", "http://ecx.images-amazon.com/images/I/31ZNWCKV44L._SL210_.jpg", 116, "韓国版「スピード」ともいえるアクション大作。政府への恨みを抱いて地下鉄をジャックしたテロリストと、その凶行を食い止めようとする刑事の攻防が描かれていく。本物と同じ地下鉄車両のセットを作り、金浦国際空港を封鎖してまで銃撃シーンを撮ったスタッフの意気込みが全編を貫く熱血作だ。", "2004-11-06", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("保密局的槍声／GUNSHOTS　IN　THE　CIB", "保密局の銃声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "国民党軍と人民解放軍の戦闘が続く新中国成立直前の上海を舞台に、共産党地下党員として任務を遂行する劉嘯塵という男の活躍ぶりを描いたスパイ・サスペンス映画。保密局とは、国民党の特務機関で対共産党秘密工作を担当する部局のこと。監督はカメラマン出身のチャン・イェン。", "1979-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道三国志２ 総長への道", "極道三国志２ 総長への道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "大阪を舞台に、現代ヤクザの抗争を描いたシリーズ第２作。前作で固い絆で結ばれた義兄弟３人組が、巨大組織に立ち向かう青年に手助けをする。かつて竜神会に所属していた若者・剛が、組織を潰した鳴海組への復讐に乗り出した。その頃、鳴海組と敵対組織の深大寺一家の抗争が激化。深大寺の若き武闘派３人組は剛と組み、鳴海組に戦いを挑む。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRUE BLOOD", "男は死んで血を流せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "警官殺しの濡れ衣を着せられ、弟をニューヨークに残して逃亡した兄。10年後、そこに戻ってきた兄は、不良グループに入った弟の姿を発見する。置き去りにした償いを果たそうと苦悩する兄に対して弟は冷たく……。フィルム・ノワールを思わせるクールな雰囲気とアメリカン・バイオレンスの要素を加えた人間ドラマ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ra-X Boaders", "ラ・エックス・ボーダーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "元サーフィン世界チャンピオンのバートン・リンチがプロデューサーを務めるオーストラリアのスポーツ・ドキュメンタリー番組「Ra」の映画版第3弾。サーフィンを中心に、スケートボード、ボディボード、スノーボード、カイトなど、あらゆるボード系スポーツに注目。大迫力のライディング映像はもちろん、ボーダーたちのファッションやライフスタイルにもスポットを当て、さまざまな角度からその魅力を紹介していく。", "2008-06-25", "76,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sweet and Lowdown", "ギター弾きの恋", "", "", "http://image.eiga.k-img.com/images/movie/1510/315x210.jpg?2841", 95, "“世界で2番目”を自称するギタリスト、エメット・レイは、音楽の才能に恵まれながらも派手で自堕落な生活を送っていた。ある日彼は口のきけない純情な女性ハッティと出会い、愛しあうようになる。時にエメットに横暴な態度をとられながらも、献身的に愛を捧げ続けるハッティ。やがてエメットはギタリストとして名声を手に入れるが、気紛れな生活がやめられず、ハッティを捨て上流階級の女性ブランチと衝動的に結婚する。", "2001-03-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIAGEM AO PRINCI´PIO DO MUNDO", "世界の始まりへの旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "老匠オリヴェイラが78歳で完成させた、自伝的な肌触りの一編。ポルトガル北端を目指し、ドライブを続ける４人。目的はその中の一人、フランス人俳優アフォンソの伯母探しだ。途中、映画監督マノエルは、幼いころに親しんだ自然と再会して思い出を反芻させていく。立ち現れては消え去る車窓の情景の数々。かけがえのない一瞬一瞬が流れるような時間感覚で捉えられたロード・ムービーだが、終盤にはポルトガルの歴史から派生した過酷な現実も待ち受けている。監督の分身マノエルを豊かな微笑みで演じ切ったM・マストロヤンニは、これが遺作となった。", "1997-01-01", "25,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAST DAYS", "ラストデイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ガス・ヴァン・サントが、'94年に自殺した\"ニルヴァーナ\"のカート・コバーンにインスパイアされた作品。コバーンの最後の2日間をモデルに、人気絶頂で自殺を遂げるカリスマロッカーの葛藤を、淡々としたタッチで綴る。音楽は\"ニルヴァーナ\"とも交流のあったインディーズ界の大御所バンド、\"ソニック・ユース\"。「ヘドウィグ・アンド・アングリーインチ」のマイケル・ピットが主演し、自作の曲を披露している。ミュージシャンのブレイクはリハビリ施設から抜け出して森の中をさまよい歩く。自宅にたどり着くが、居候中の取り巻き連中の声は耳に入らない。そしてクローゼットから猟銃を取り出し……。", "2006-03-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Final Destination", "ファイナル・デスティネーション", "", "", "http://image.eiga.k-img.com/images/movie/1390/315x210.jpg?3921", 98, "高校生アレックスは修学旅行に旅立つ離陸前の飛行機の中で居眠りし、飛行機が離陸直後に大爆発するという悪夢を見る。飛び起きて「この飛行機は爆発する！」と叫ぶアレックスに周囲は騒然。この騒ぎに巻き込まれた数人の同級生と先生、アレックスの7人は飛行機から降りるが、彼の言葉通り飛行機は大爆発。それから数日後、生き残りの1人である親友トッドにまたも奇妙な予兆を感じたアレックスはトッドの家に急ぐが、彼はすでに帰らぬ人となっていた。", "2001-01-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE SANG D’UN POE｀TE", "詩人の血", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "詩人コクトーが、映像という新境地にチャレンジしたアヴァギャルド映画。ギリシャ神話、鏡、生と死などコクトーの溺愛する様々なモチーフが、物語性にとらわれない自由な構成によって断片的に表現される。処女作にしてコクトー芸術の神髄を示した傑作。", "1930-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陸海軍流血史", "陸海軍流血史", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "満州事変から太平洋戦争に至る激動の昭和初期にスポットをあて、軍部が次第に国政の主導権を握っていくなかで、若手将校たちが救国の情にかられ、決起する様子をドラマティックに描く。使命感に燃える将校に扮した宇津井健の熱演が光る。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’OEUVRE AU NOIR", "黒の過程", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "『ハドリアヌス帝の回想』などで知られるフランスの女性作家M・ユルスナールの同名小説をもとにした、思索的かつ幻想的な一編。16世紀のフランドル地方。医者であり錬金術師でもあるゼノンは、その著書が異端とされ、教会から追われる身となる。名を変え、放浪の旅に出た彼の遍歴、そして当時のヨーロッパの風景が、静かに描かれていく。監督は“魔術的リアリズム”をその作風とするA・デルボー。ユルスナールの原作の客観性を、ゼノンの主観性に置き換えた物語構成、幻想と現実がないまぜになった映像など、ヨーロッパで高い評価を受けるデルボー監督の才能が遺憾なく発揮されている。", "1988-01-01", "2,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新射雕英雄傳之東成西就／THE EAGLE SHOOTING HEROES", "大英雄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "香港と台湾映画界の俳優、女優陣オールキャストのアクション＆コメディ大作。とある王国の女王といとこの陰謀、それを阻止せんと立ち上がる一族。その争いの中、殺害された者の仇討ち、そしてもちろん恋物語も絡んでくる。スターそれぞれに見せ場があるところに注目。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サマータイムマシン・ブルース", "サマータイムマシン・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「踊る大捜査線」シリーズの本広克行監督が、京都の人気劇団『ヨーロッパ企画』の傑作戯曲を映画化。タイムマシンに乗り込んだSF研究会メンバーの1日をめぐるタイムトラベルが笑い満載で描かれる。進境著しい若手俳優の瑛太や上野樹里らフレッシュな顔ぶれが揃ったキャスト陣に注目だ。SF研究会部員の甲本らメンバー5人は部室で本物のタイムマシンを見つける。とりあえず彼らは昨日にタイムスリップしてみるが……。", "2005-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGEL", "エンジェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "昼は優秀なハイスクールの生徒、夜はメイクをしてハリウッドに出没するストリート・ガール。華麗な映画の都ハリウッドを舞台に、現代アメリカに内在する世相の暗部を色濃く反映した作品。少女娼婦役ウィルクスの、がむしゃらな演技が小気味良い。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("300", "300 スリーハンドレッド", "", "", "http://image.eiga.k-img.com/images/movie/1910/315x210.jpg?4381", 117, "「シン・シティ」のクリエイター、フランク・ミラーによるグラフィック・ノベルを「ドーン・オブ・ザ・デッド」（04）のザック・スナイダー監督が映画化。紀元前480年。スパルタ王レオニダスのもとに、侵略を目論むペルシア帝国の王クセルクセスから使者が来訪し、水と土地を差しだし、服従の証を示せと要求。レオニダスはこれを拒否し、たった300人の重装歩兵で100万のペルシア軍を迎え撃つことになる。", "2007-06-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pee-wee's Big Adventure", "ピーウィーの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「バットマン」「シザーハンズ」のティム・バートン監督が長編デビュー作。主演は赤い蝶ネクタイとピッタリしたスーツで人気のコメディアン、ピーウィー・ハーマン。大切な赤い自転車を盗まれてしまったピーウィー。テキサスのアラモ砦にあるという占い師の言葉を信じ、ピーウィーの大冒険が始まった。音楽は「スパイダーマン」「ミッション：インポッシブル」のダニー・エルフマン。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO PROTECT AND SERVE", "バイオレントヒート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "C・T・ハウエルが型破りな刑事に扮したポリス・アクション。ロサンゼルス市警の殺人課刑事、イーガンは、警官による集団暴行事件を目撃し、独自の捜査を開始する。そこへ、連続警官殺人事件が発生。捜査の進むうちイーガンは恐るべき真相へと踏み込んでいく……。暴力、麻薬、そして警官の不正と、病むアメリカ社会の背景がリアル。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学校III", "学校III", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "日本経済を襲った大不況を反映し、中高年向けの職業訓練学校に舞台を移したシリーズ第３作。主人公は自閉症の息子を女手一つで育てる紗和子と、エリート意識が抜け切らない元証券会社部長の周吉。ともに会社をリストラ退職した彼らが、ボイラー技師の資格を得るためのクラスで出会い、心を通わせていく姿がホロ苦く綴られる。感情を伝えることが苦手な不器用男を、小林稔侍がハマリ役で好演。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE PIECE ワンピース　エピソード　オブ　アラバスタ　砂漠の王女と海賊たち", "ONE PIECE ワンピース　エピソード　オブ　アラバスタ　砂漠の王女と海賊たち", "", "", "http://image.eiga.k-img.com/images/movie/33870/315x210.jpg?3617", 0, "子どもから大人まで圧倒的な支持を誇る大人気コミック「ONE PIECE」。劇場版第8弾となる本作では、ファンの間で特に人気の高いエピソード“アラバスタ編”を映画化。秘密犯罪会社バロック・ワークスの陰謀により、崩壊の危機に瀕したアラバスタ王国。ゴム人間モンキー・D・ルフィ率いる麦わら海賊団は、バロック・ワークスの正体を暴くため奔走していた王女ビビを仲間に迎え、アラバスタ王国を救うべく立ち上がる。", "2007-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kibong at Barefoot", "裸足のギボン", "", "", "http://image.eiga.k-img.com/images/movie/53370/315x210.jpg?4050", 99, "幼少期に高熱病を患い、年齢は40歳なのに知能は8歳で止まったままのギボン。走ることが得意な彼は、ある日偶然紛れ込んだマラソン大会で入賞してしまう。次は全国大会を目指すことになり、その賞金で大好きなお母さんに入れ歯を贈ろうと練習に励むギボンだったが……。実話を基にした韓国発のハートウォーミング・ストーリー。少年のようにピュアな心を持つ主人公ギボンを、「家門の危機」のシン・ヒョンジュンが好演。", "2008-03-08", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浪人八景", "浪人八景", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "浪人・比良雪太郎は、箱根で武家の娘・松江に頼まれ、江戸まで夫婦を装って旅することになる。スリの松吉や浪人の三井も一行に加わるが、やがて松江は郡山藩の追手から逃れているさなかだと判明する……。連載小説の映画化で、登場人物のキャラクターが実に個性豊かに描き分けられている時代劇小品。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TAKE", "わいろ／ザ・テイク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "賄賂を贈り、警察の口封じをしている大ボス、マンソーの牛耳る街に赴任してきた黒人刑事クニードはワイロの摘発どころか、マンソーの悪事を暴き、賄賂をゆすり取るというワルだった。逆転、また逆転のポリス・アクション。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHAT ABOUT ME", "WHAT ABOUT ME", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "早くして母が亡くなり、父も行方不明のリサは叔母と一緒に暮らしていたが、叔母も死んでしまったため、アパートを追い出されてしまう。すぐに無一文となったリサはベトナム帰りのホームレス、ニックと知り合い、広場で一緒に過ごすようになる。しかし、ニックが酔った勢いでリサを殴ったことでケンカをし、リサに同情したトムの部屋に転がり込む。数日後、テレビで広場のホームレス取り締まりのニュースを見たリサはニックを探しに、広場へ向かうが、その途中バイクにはねられてしまう。命の別状のなかったリサは、彼女をはねたレズのカップルと一緒にパーティーに行き、そこでポールと知り合う。リサはポールに自由の女神を見たいと頼み、二人はパーティーを抜け出すが……。J・サンダースやラモーンズのD・D・ラモーンなどニューヨーク・パンクのミュージシャンたちが出演しているのが話題のインディーズ映画。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仁義の墓場", "仁義の墓場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ヤクザ社会の常軌すらも逸し、誤った殺人を繰り返す、戦後暴力団史上最も凶悪といわれた男の30年の人生を実録タッチで描いた力作。主人公が女房の遺骨をかじりながら歩くシーンなど、奈落の底へ落ちきった人間の姿が衝撃的に捉えられている。渡哲也が凄まじい演技を見せる。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビー・バップ・ハイスクール 高校与太郎行進曲", "ビー・バップ・ハイスクール 高校与太郎行進曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ゲームセンターをたまり場にする無期停学グループは、立花商業の菊永、北高の前川ら名うてのワルを病院送りにし、次はトオルとヒロシを血祭りにあげようと狙っていた。翔子と舎弟の信雄が連れ去られ、工場を舞台に無期停学グループと二人の死闘が始まった。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いんこう", "いんこう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "小沼勝監督が撮り上げた“にっかつロマンポルノ”の中でも、ラブ・ストーリー度の高い作品。普段はOL然とした主演の麻生かおりが、けだるいセックスの色香をまき散らす。26歳のプログラマー、真理子は恋人との情事にマンネリ感を抱いていた。一方、彼女の仕事上のパートナー、高校生の講平は恋人が最後の一線を越えさせてくれない。そんな二人が突然、セックスに溺れて……。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後の攘夷党", "最後の攘夷党", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "稲垣浩監督による戦後第１作の時代劇。攘夷派の大葉慎吾は米軍宣教師宅を襲って傷つくが、逆に彼らに助けられ、はじめて自分の思想の過ちに気づく。戦後日本を支配したGHQの指導による“民主主義的な映画”である。当時はチャンバラも禁止されており、この作品も“チャンバラ抜きの時代劇”の１本だ。", "1945-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Indian Runner", "インディアン・ランナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "俳優ショーン・ペンの監督・脚本デビュー作。真面目に生きる警官の兄と暴力的な性格の弟。ある日ベトナム戦争から帰還した弟は、以前よりも暴力性を増し、問題行動を繰り返すようになる。兄は弟を立ち直らせようと奮闘するが、ついに弟は取り返しのつかない事件を引き起こし……。兄を「グリーン・マイル」のデビッド・モースが、弟をビゴ・モーテンセンがそれぞれ熱演。名優チャールズ・ブロンソンやデニス・ホッパーが脇を固める。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BULLETS OVER BROADWAY", "ブロードウェイと銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "イルミネーションが輝き、ギャングたちが濶歩する1920年代のブロードウェイをW・アレンが再現した作品。主人公のデビッドは若い劇作家。新作の上演が決まったものの、ギャングの顔役に演技力ゼロのショーガールを押し付けられ、三角関係の愛に悩み、やがて殺人事件にも巻き込まれていく。ブロードウェイの魅力を見事に映像化した本作はアレンの前作までとは異なり、J・キューザックなど当時の若手を中心に据えることによって、初々しく新鮮なタッチの映画に仕上がっている。アカデミー賞では６部門にノミネートされ、D・ウィーストが助演女優賞を受賞した。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Swan Lake", "World Classics＠CINEMA 白鳥の湖", "", "", "http://image.eiga.k-img.com/images/movie/55110/315x210.jpg?2739", 129, "世界中から選りすぐったオペラやバレエをスクリーン上映する「World Classics＠CINEMA」の1作で、ロシアのマリインスキー・バレエの2006年上演作「白鳥の湖」を映像化。悪魔によって白鳥の姿に変えられた美女オデットと、彼女に愛を誓いながらも悪魔の娘オディールに誘惑されてしまう王子ジークフリートの悲劇的な愛を描く。オデットとオディールを演じるのは“ロシアの至宝”と称えられるダンサー、ウリヤーナ・ロパートキナ。", "2010-03-27", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("問題はこれからです", "問題はこれからです", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "1999年度の文化映画の各賞を受賞したドキュメンタリー映画。福祉を公約に掲げて当選した若い町長と彼が住民に呼びかけて作った、ワーキンググループの活動を追った1997年の作品「住民が選択した町の福祉」のその後を追っている。その後の町長の考え、議員や議会の対応、住民の活動、そして、前作で議会を一票差で通過した福祉施設ケアタウンが機能していく様子を追う。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HONEY，I SHRUNK THE KIDS", "ミクロキッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "大ぼけサイエンティスト、サリンスキー博士が開発した電磁物体縮小機のために、６ミリ・サイズに縮んでしまった４人の子供たちの冒険を描くコミカルなSFXアドベンチャー。昆虫は怪物、煙草の火やスプリンクラーの水滴が爆弾と化す芝生のジャングルといった、巨大セットや小道具なども見ものだ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD SPORT", "ブラッド・スポーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "日本人に育てられた武道家フランクが、格闘技世界一の座を目指し、試練に耐えながら友情や恋を体験していく、実話をもとにした青春アクション。本物の武術チャンピオン、イエンとの決勝戦は圧巻。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忘れえぬ慕情", "忘れえぬ慕情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "フランスから長崎に赴任してきた造船技師は、両親を失い弟妹の面倒を見ながら呉服店を切り回している娘に愛情を抱くようになるが、そこに技師の昔の恋人が現れる……。愛の葛藤を描くイヴ・シャンピの日・仏合作映画。この作品が縁となってイヴ・シャンピと岸恵子は結ばれた（しかしその後離婚）。「アンナ・カレニナ」「ローマの休日」などで知られる名カメラマン、H・アルカンが撮影を務めている。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍争虎闘／ENTER THE DRAGON", "燃えよドラゴン", "", "", "http://ecx.images-amazon.com/images/I/51ywwOLvAAL._SL210_.jpg", 100, "日本のみならず、世界中を席巻したドラゴン・カンフー映画ブームの火付け役となった作品。また、主演のリーがこの１作で彗星のごとく現れ、映画の完成後急逝したことも、いっそう興味をかきたてた。香港の沖に浮かぶ要塞島に秘密諜報員として乗り込んだリーは麻薬密売組織の一味を追い詰めていく……。スパイもどきのストーリー展開はともかく、目を見張る肉体の特異な動きと、“アチョ～”という奇声が画面を圧倒。また、活劇職人演出も冴えており、鏡の部屋での対決に使われた、ミラー・ショットはオーソン・ウェルズの「上海から来た女」にも匹敵。", "1973-01-01", "1,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銭形平次捕物控・幽霊大名", "銭形平次捕物控・幽霊大名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "市川雷蔵のデビュー第２作で、彼は善悪二役を演じている。辻斬りや娼婦誘拐事件が発生。平次が調べてみると、三万八千石の大名・金森頼錦の邸内が怪しいとわかる。金森家当主・万之助は病床に臥していて、その双子の弟・千之助が、悪業を重ねていた……。中村玉緒の大映映画デビュー作でもある。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花心中", "花心中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "“和製ルルーシュ”斎藤耕一が、作詞家・阿久悠と『同棲時代』の上村一夫のコンビによる劇画を映像化した恋愛風俗映画。若者の街、原宿を舞台に、心中で一人生き残った男と死んだ女の妹の愛憎模様が繰り広げられる。やがて来る、二人の事故とも心中ともしれぬ死……。中野良子の繊細な美しさが心に残る。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MA　FEMME　EST　UNE　ACTRICE", "ぼくの妻はシャルロット・ゲンズブール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "フランス映画界の若きおしどり夫婦イヴァン・アタルとシャルロット・ゲンズブールの共演作。アタルが監督を兼任し、女優を妻にした普通の男の受難を、実体験に基づいて軽妙に描く。パリ在住のスポーツ記者イヴァンはサッカー好きのごく普通の男だが、実は妻は人気女優のシャルロット。妻に比べて影の薄い自分に、彼は不満を抱いている。そんなある日、妻がロケでロンドンに飛ぶが、共演者はプレイボーイとして知られる俳優で……。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lust och fagring stor", "あこがれ美しく燃え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "第2次世界大戦中のスウェーデンを舞台に、中学生と女性教師の愛と葛藤を描いた青春映画。15歳の少年スティグは、新しく赴任してきた女性教師ビオーラに恋心を抱く。やがて愛し合うようになった2人。しかしスティグはビオーラの夫フランクとも親しくなってしまい……。「みじかくも美しく燃え」等の作品で名高いスウェーデンのボー・ウィデルベルイ監督の遺作となった本作は、ベルリン国際映画祭審査員特別賞を受賞している。", "1995-01-01", "14,18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Spit on Your Grave", "発情アニマル", "", "", "http://image.eiga.k-img.com/images/movie/58040/315x210.jpg?2050", 101, "実在の事件をもとに、男の欲望のはけ口として陵辱を受けた女性が、加害者の男たちを血祭りに上げていく様を描いた壮絶な復讐劇。湖畔の別荘でバカンスを過ごしていた若い女性小説家のジェニファーは、4人組の不良少年に襲われ際限ない暴行を受ける。ボロボロになり恐怖と屈辱に打ち震えるジェニファーは、男たちを1人残らず殺害することを心に誓い、行動に出る。日本では1979年に「発情アニマル」のタイトルでポルノ映画としてひっそりと劇場公開された後、ビデオ発売時は「悪魔のえじき」、TV放送時は「女の日」と邦題を変化させたカルト的作品。リメイク版「アイ・スピット・オン・ユア・グレイヴ」（10）が11年に劇場公開されて話題を呼び、オリジナル版の本作が「発情アニマル　アイ・スピット・オン・ユア・グレイヴ1978」のタイトルで33年ぶりに劇場公開されることになった。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE VALACHI PAPERS", "バラキ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "マフィアの準幹部だったバラキが、組織に欺かれ、復讐のためにマフィアの組織、コーザ・ノストラの内情をFBIの係官にぶちまけるが……。「ゴッドファーザー」と同様に、残虐場面が話題を呼ぶ。一度足を踏み込んだら抜けられない、ファミリーの怖さを訴えた実録もの。", "1972-01-01", "4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猩猩王／THE MIGHTY PEKING MAN", "北京原人の逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「キングコング」に対抗すべく、日本から「ゴジラ」の特撮スタッフを招いて製作された香港製怪獣映画。ヒマラヤの奥地へと分け入った探検家が、半裸の金髪美女の助けを借りて北京原人を捕獲。だが香港に連行された原人は突如暴れ出して……。", "1977-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SENSATION", "異常心理教室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "心理学の大学教授バートンは、感受性の強い女子学生ライラにある実験を試みる。バートンからストッキングを渡された日、ライラはそれを身につけた女の夢を見、イヤリングを渡された日にはめくるめく官能の世界を夢見る。そしてコンパクトを渡された日、夢でコンパクトを開いたライラは、そこに自分ではない女が映っているのを見る。その女とは、数ヵ月前に殺されたバートンの教え子だった……。官能と異常心理の世界が交差するサイコ・サスペンス。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刺青", "刺青", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "女の情念と、その妖しい美しさを生涯通して描き続けた大映出身の鬼才・増村保造の美学のまさに頂点ともいうべき作品。谷崎潤一郎の有名な処女短編小説『刺青』を原作として新藤兼人がシナリオ化した。主演の若尾文子は、増村保造とは何本もコンビを組み、増村描く女性像になくてはならない存在となった。物語は手代と駆け落ちした質屋の娘が、悪人のたくらみにかかり芸者として売られる。刺青師に白い肌一面に巨大な女郎グモを彫られた娘は、男と女の欲望のうごめく世界に本能のうごめくまま生きていく……。冒頭、山本学が演じる刺青師が、薬で眠らせた若尾文子の肌に、丹念に女郎グモを彫り込んでいくシーンの妖気ただよう美しさは見事。そしてラスト、若尾文子の肌に彫られたクモが、娘の体に絡み付き、まるで呼吸でもするかのように血の中でうごめくシーンのあざやかな地獄絵は日本映画史上忘れられない名場面であろう。また、随所に増村保造独特の色彩感覚があふれ、オレンジ・赤・青などの原色を使った画面は“大映調”の真骨頂といえる。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN SHAOLIN：KING OF KICKBOXERSII", "キング・オブ・キックボクサー　—ファイナル—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ごく普通のアメリカのティーンエイジャー、トゥルーは、最高の師を求め中国へ旅立つことを決意。目指すは伝説の少林寺であった。彼は頭髪を剃り、格闘着を身につけ、決死の覚悟で修行を始める。「キング・オブ・キックボクサー」シリーズの４作目で、少林寺の発祥地、中国でロケーションを行った。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOMO EROTICUS", "黄金の７人 １＋６ エロチカ大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "精力絶倫のスーパー・プレイボーイ、ミケーレがシシリー島からナポリへやって来た。実業家の奥方のご寵愛を受けたのを皮切りに、やり手の女社長の“ペット”になったまではよかったが……。大らかであけっぴろげなお色気が清々しい。コシナの脱ぎっぷりも楽しみの一つ。「黄金の七人」シリーズとは無関係の艶笑コメディ。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花様年華", "花様年華", "", "", "http://image.eiga.k-img.com/images/movie/1470/315x210.jpg?3912", 98, "62年、香港。新聞記者のミスター・チャウと商社で秘書として働くミセス・チャンは、同じ日に同じアパートに引越してきて、隣人となる。家庭を持つ貞淑な男と女。しかしやがて2人は互いの伴侶が不倫関係にあることに気づき、次第に時間を共有するようになる。そして、この運命の皮肉ともいえる出会いにより、2人はいつしか戸惑いつつも強く惹かれ合っていくのだった……。", "2001-03-31", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("孤高のメス", "孤高のメス", "", "", "http://image.eiga.k-img.com/images/movie/54760/315x210.jpg?1602", 126, "医師の大鐘稔彦のベストセラー小説を、「クライマーズ・ハイ」脚本、「フライ,ダディ,フライ」の成島出が映画化した医療ドラマ。主演に成島と3度目のタッグとなる堤真一、 共演に夏川結衣、吉沢悠、中越典子ほか。地方都市にあるさざなみ市民病院に赴任してきた外科医の当麻鉄彦は、保身のための無責任な手術や患者のたらいまわしを繰り返すその病院で、患者を第一に考えた処置で淡々と手術をこなしていく。そんな当麻の姿勢が、大学病院や医療事故への恐れから停滞していた病院を少しずつ変えていくが……。", "2010-06-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VORTEX", "ボルテックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "異色の女性ロッカー、ランチ主演によるサスペンス・ムービー。N.Y.を舞台に、一人の上院議員の死を追ううち、巨大企業間の覇権争いに巻き込まれていくクールな女探偵の活躍を描く。全編に流れるパンク・ロックのナンバーも魅力。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("横浜ばっくれ　～夏の湘南純愛篇", "横浜ばっくれ　～夏の湘南純愛篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "湘南を舞台に恋とケンカに明け暮れるツッパリ高校生の姿を描いた青春コメディ。大好きな美奈ちゃんとの夏休みを心待ちにしていた常松。ところが美奈は、アメリカにいた時の友だちと海に行ってしまった。ショックにうちひしがれる常松だが、ひょんなことからヤクザの抗争に巻き込まれてしまう。『月刊少年チャンピオン』で人気を呼んだ同名漫画の映画化第２作。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THIN MAN", "影なき男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "『マルタの鷹』などで知られるダシール・ハメットの原作『影なき男』をヴァン・ダイクが映画化したもの。サンフランシスコの探偵社に勤めるニックは、大金持ちの娘ノラとの結婚を機に、会社を退職。だが探偵好きなノラのため、ある発明家の失踪事件に結局首を突っ込むハメになってしまう……。この作品のニック＆ノラ夫妻のユーモラスなキャラクターと、名犬アスタの個性的な活躍ぶりは超人気となり、「第三の影」「影なき男の息子」など計６本のシリーズが作られ、探偵映画史上に残る名作となった。監督のヴァン・ダイクは1920年代に連続活劇を撮っていた人物で、男っぽいアクションを多数手掛けている。1930年代以降はメロドラマやスペクタクルを中心にメガホンを執り、MGMの秘蔵っ子とうたわれ、D・W・グリフィスの超大作「イントレランス」では助監督を務めていた。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("斑女", "斑女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ナイトクラブのホステスを主人公にした、中村登監督お得意の大船調女性映画。夫を捨てて義弟と駆け落ちした英子は、東京でナイトクラブに勤めることになる。やがて義弟の心は英子から離れていくが……。原作は、村松梢風の短編『塔』『斑女』『波の塔』の３編を一つにまとめたものである。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE CRIMES", "ラブ・クライム 官能の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "S・ヤングが、セクシーな魅力を発揮するサスペンス・ドラマ。カメラマンを装ったレイプ強盗犯に監禁され、過去の性的抑圧を解放する女性検事補の、恐怖と官能の体験がミステリアスに描かれる。狂気の犯人に扮したP・バーギンもさることながらヌードも辞さないS・ヤングの熱演に圧倒される。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奇談〈きだん〉", "奇談〈きだん〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "熱狂的なファンを持つカリスマ漫画家・諸星大二郎の原作を、小松隆志が脚色・監督したホラー作品。東北の閉鎖的な村を舞台に、失われた記憶を求めて同地を訪れたヒロインの旅を、村に秘められた衝撃の真実とともに語り明かす。想像を絶するラストに圧倒される、伝奇ミステリーだ。幼いころ神隠しに遭った大学院生の里美は、その時失った記憶を確認するため、“隠れキリシタンの村”と呼ばれる村を訪れるが……。", "2005-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("波光きらめく果て", "波光きらめく果て", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "壱岐の島を舞台に、自分の欲望や感性に正直なばかりに男との愛に走ってしまい、身内や故郷からはじき出される女の姿を描いた作品。松坂慶子が好演、彼女に夫をとられてしまういとこ役の大竹しのぶが、松坂とは正反対の女を演じる。芥川賞受賞小説の映画化で、新しいタイプの女性像を描いた女性ドラマ。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　FEMME　DE´FENDUE", "視線のエロス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "主人公の視点である主観カメラで全編を捉えたユニークなラブ・ストーリー。妻子のいるフランソワは若い女性ミュリエルと出会い、不倫の恋に落ちる。しかしミュリエルは罪悪感にさいなまれ、フランソワと距離を置いては、また接近し……。主観的な映像から、愛の言葉や痴話ゲンカが臨場感豊かに伝わってくる。見つめられるヒロイン、I・カレーが揺れ動く女心を好演。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDERCOVER BROTHER", "アンダーカバー・ブラザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "インターネットの人気アニメから生まれたおバカ・コメディ。1970年代からよみがえったようなアフロヘアーのアクション・ヒーローの活躍劇が、ファンキーなギャグと音楽たっぷりに描かれる。白人至上主義団体が黒人の大統領候補を洗脳。それに気付いた黒人秘密結社は、義賊アンダーカバー・ブラザーをスカウトして、この陰謀に立ち向かうことに。だが、彼は白人美女、ペネロープの誘惑にメロメロになってしまう。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fast Food Nation", "ファーストフード・ネイション", "", "", "http://image.eiga.k-img.com/images/movie/53330/315x210.jpg?2230", 108, "ジャーナリストのエリック・シュローサーがファーストフード業界の闇を暴いたルポタージュ「ファストフードが世界を食いつくす」を原作に、大手ハンバーガーチェーンでの“牛肉パテへの大腸菌混入”を巡る人間模様を通して現代社会が抱える問題をあぶり出した衝撃作。「ビフォア・サンセット」のリチャード・リンクレイターが監督を務め、グレッグ・キニア、イーサン・ホーク、アブリル・ラビーンら豪華キャストが顔を揃える。", "2008-02-16", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WARLORDS OF ATLANTIS", "アトランティス ７つの海底都市", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "19世紀末のカリブ海で、巨大なタコに襲われた潜水艦が漂着したのは、海底都市アトランティス。７つの都市を次々と案内されながら、洗脳の危機を乗り越え、見事に一行は脱出する……。伝説のアトランティスを、地球へ飛来した火星人の衛星がその起源だとする発想がユニーク。", "1978-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　一谷嫩軍記　熊谷陣屋", "シネマ歌舞伎　一谷嫩軍記　熊谷陣屋", "", "", "http://image.eiga.k-img.com/images/movie/56780/315x210.jpg?3829", 101, "歌舞伎の舞台公演をスクリーンでデジタル上映する「シネマ歌舞伎」シリーズ第14弾。「平家物語」を題材にした全5段の物語「一谷嫩軍記（いちのたにふたばぐんき）」の3段目にあたる「熊谷陣屋」を上演。源平合戦のさなか、源氏の武将・熊谷直実は、平家の若武者・平敦盛を討ち取る。しかし、実は敦盛は後白河法皇の子であり、母・藤の方はかつて熊谷を救った恩人でもあった……。「歌舞伎座さよなら公演」最後の公演として2010年4月に上演された。", "2011-10-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CURE", "マイ・フレンド・フォーエバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "輸血が原因でエイズに感染した11歳の少年デクスターと、その隣に住む12歳の少年エリックのひと夏の友情と冒険を描いた感動作。エリックの隣にエイズ感染者のデクスターが引っ越してきた。初めは偏見から距離を置き、塀越しに会話をしていたが、やがて塀を乗り越え、二人は一緒に遊ぶようになり固い友情が芽生えていった。そんなある日、タブロイド新聞で医師がエイズの特効薬を発見したという記事を見つけ、二人はルイジアナを目指して旅に出る……。輸血感染によるエイズを扱った初のハリウッド映画。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IMPRESSIONEN　UNTER　WASSER", "ワンダー・アンダー・ウォーター／原色の海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "2003年、惜しまれつつ亡くなったドイツの女性映像作家レニ・リーフェンシュタールが、48年ぶりに発表したドキュメンタリー。世界中の海の生物をカメラに収め、その神秘の世界に迫っている。", "2002-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前天神", "喜劇・駅前天神", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "予備校職員の徳之助は、浮浪児だった次郎を今日まで我が子同様に育てあげてきた。お好み焼き屋のママ・景子はかつて徳之助との間に由美を生んでいた。徳之助は由美を次郎と一緒にさせようとするが、次郎は同じお好み焼き屋の店員・染子に首ったけ。これに町内のまとめ役・魚屋の孫作が絡んで、さてどうなることやら……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海底大戦争", "海底大戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "海中で人間に似た生物を見たという報を聞いた日本人記者は、アメリカ人の恋人とともに捜査に出かける。海底にはサイボーグを作って世界征服をたくらむムーア博士の基地があり、科学者とムーア博士の戦いがはじまる。東映が手掛けた日米合作のSF映画。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マネキン少女", "マネキン少女", "", "", "http://image.eiga.k-img.com/images/movie/57370/315x210.jpg?4267", 9, "芸能事務所イトーカンパニー所属の俳優たちが出演するショートムービーをネット配信している「イトーチューブ」で公開された作品を映画館で上映する「イトーチューブ Shrot Movie Colleciton」の1本。人気アイドルグループ「AKB48」の藤江れいなの主演で、キュートなマネキンが繰り広げる一晩の冒険を描いたファンタジー。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents うそ　「きみをよんでるよ」", "桃まつり presents うそ　「きみをよんでるよ」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 26, "", "2010-03-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONDO", "モンド　～海を見たことがなかった少年～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "フランスを代表する作家ル・クレジオの原作をもとに、無垢な少年と人々の交流をみずみずしく綴る。舞台は南仏のニース、どこからともなくやって来たモンドは、純粋でどこかミステリアスな少年。野良犬や浮浪者を捕らえる大人たちが嫌いで、彼らを恐れているモンドだが、気に入った大人たちには“僕を養子にしてくれる？”と、人なつこく尋ねる。しかし、彼らはモンドに好意を持っていても、ただ戸惑うばかり。ホームレスの老人や大道芸人、ベトナム人の老女と心を通わせていく彼は、ある日突然姿を消すのだった。社会から疎外された人々に対する優しいまなざしを、詩情あふれる映像の中に描出した愛すべき小品。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海峡", "海峡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "およそ30年の間、日本のビッグ・プロジェクトといわれた青函トンネル工事に携わった人々の人生を、青函トンネル技術調査団員の阿久津を中心に描いた作品。東宝創立50周年記念作品で、自然と技術との対立のドラマを丁寧な演出でみせてくれる。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Good Doctor", "グッド・ドクター　禁断のカルテ", "", "", "http://image.eiga.k-img.com/images/movie/57470/315x210.jpg?1887", 93, "オーランド・ブルームが製作・主演し、若き研修医が自らの欲望におぼれて罪に手を染めていく姿を描いたサスペンス。真面目な研修医のマーティンは、18歳の美しい患者ダイアンに尊敬され、信頼されることで医師として自信を深めていく。やがて病気が回復したダイアンは退院することになるが、すでにダイアンに対して特別な感情を抱くようになっていたマーティンは、ひそかに薬を入れ替えて病気を再発させ、病院に引き留める……。ダイアン役はエルビス・プレスリーの孫娘ライリー・キーオ。そのほかの共演にマイケル・ペーニャ、タラジ・P・ヘンソンら。", "2012-01-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("13号待避線より その護送車を狙え", "13号待避線より その護送車を狙え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "護送車が襲われ、囚人が即死、犯人は逃亡した。護送責任を問われた看守長の多門は、６ヵ月の停職処分を受け、単身犯人追及に動き出した。そしてすでに保釈処分になっている五郎を追い、熱海へ。しかし、そこでまた囚人の姉が殺されてしまう……。島田一男の原作を鈴木清順が映画化したアクション・ドラマ。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("禁室培慾", "紅蜘蛛女", "", "", "http://image.eiga.k-img.com/images/movie/33660/315x210.jpg?4711", 89, "「紅薔薇夫人」の坂上香織が男を監禁する悪女役に挑んだ、香港発のエロティック・サスペンス。夫との思い出の地・香港にやって来た由美は、そこで出会った中国人カメラマンのケニーと一夜を共にする。しかし、ケニーに本命の恋人がいることを知った由美は、嫉妬心からケニーを監禁してしまう。これまで「インファナル・アフェア」シリーズなどでカメラマンとして活躍してきたン・マンチンが、本作で監督デビューを果たした。", "2006-09-23", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("世紀は笑ふ", "世紀は笑ふ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "浪花節の名調子で一世を風靡した広沢虎造の、実人生を重ね合わせたような立身出世物語。売れない浪花節語りの虎造が、杉狂児扮する友人の影の尽力によって名声を得る。しかし、オゴリの心を持った虎造は友情のありがたさを忘れていた……。マキノ雅広の人情噺の呼吸を心得た演出も見事。現存フィルムは、後半部分のみだが、作品の味わいは伝わる。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONEHEADS", "コーンヘッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "人気TVコメディ番組『サタデーナイト・ライブ』の有名キャラクターが主役のSFホーム・ドラマ。地球征服をたくらむコーンヘッド一家は、ニューヨークに降り立ち、地球人を装って生活を始める。移民局とのトラブルや反抗期の娘などを抱えて、ドタバタと騒ぎの止まないこの一家の行く末はいかに？", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緋牡丹博徒・お命戴きます", "緋牡丹博徒・お命戴きます", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "上州の熊谷在に軍部御用の兵器工場ができ、周辺の百姓はその公害のために苦しんでいた。結城は工場に交渉して保障金を出させようとするが、富岡組・組長に殺される。結城に淡い恋心を抱いていたお竜は、富岡を斬って仇を討つ。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD DOG TIME", "マッド・ドッグス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "暗黒街の大物ビックが、精神病院から退院するというニュースが駆けめぐる。折しも街ではビックのいない間にヨソ者たちが彼の縄張りを狙って暗躍していた。ビックの腹心で彼の愛人グレースを寝取っていたミッキーは、抗争の真っ直中で“落とし前”を迫られることに……。キラ星のごとき個性派＆実力派スターが暗黒街の男と女に扮し、クエンティン・タランティーノ作品を思わせるユーモラスな掛け合いを見せるギャング映画。なかでもポール・アンカ（本人出演）とともにステージに立ち、『マイ・ウェイ』の替え歌を披露するG・バーンの悪ノリぶりが傑作。暗黒街のグラマラスなムードを彩る人工的なセットや小粋な音楽も魅力の一つ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版 ICE", "劇場版 ICE", "", "", "http://image.eiga.k-img.com/images/movie/54240/315x210.jpg?2107", 102, "秋元康が原作・企画を担当し、「機動戦士Zガンダム」のメカデザインなどを手がけた小林誠がメガホンを取ったOVAの劇場版。多数の国際映画祭に招待されるなど、海外でも高い評判を得た。2012年、環境の劇的変化と未知の汚染物質により全ての男性が死滅し、人類は女性だけとなってしまう。人類滅亡を受け入れる人々と科学の力で運命を切り開こうとする人々の対立が生まれる中、必死に生き方を模索する少女達がいた。", "2008-11-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デメキング", "デメキング", "", "", "http://image.eiga.k-img.com/images/movie/54330/315x210.jpg?3669", 99, "鬼才漫画家いましろたかしの同名長編デビュー作を、お笑い芸人のなたぎ武（ザ・プラン9）を主演に映画化。監督は「少林老女」「口裂け女2」の寺内康太郎。1970年の瀬戸内の港町、中学生のひろしは小学生らと“田ノ浦少年探検団”を結成し、無作為な日々を過ごしていた。夏のある日、幽霊船と噂される船を探検していたひろしらは、遊園地で働く寡黙な青年の蜂屋に出会う。", "2009-03-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("安藤組外伝・掟", "安藤組外伝・掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1980年代後半、「ちょうちん」「疵」などの青春ヤクザ映画を撮り続けた梶間俊一が、一人のヤクザの生きざまを熱く描いた任侠映画。植松一家若衆の竜崎は敵対する佐久間組組長を殺し、16年の刑を受ける。抗争はいったん終焉を迎えたと思われたが、佐久間組の二代目組長、長坂が植松一家総長を暗殺。植松一家は弱体化していった。刑を終えて出てきた竜崎は妻子に会うのも拒絶し、報復の闘いに臨んでいく。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハブと拳骨", "ハブと拳骨", "", "", "http://image.eiga.k-img.com/images/movie/53450/315x210.jpg?4263", 124, "ベトナム戦争真っ只中の60年代末、米軍占領下の沖縄最大の歓楽街コザを舞台に、運命に翻弄されながらもたくましく生き抜こうとする血の繋がらない家族の姿を鮮やかに描き出した人間ドラマ。ミュージシャンの田中雄一郎が原案と音楽を手掛け、長編映画初挑戦の中井庸友がメガホンを取る。出演は自身も沖縄出身である「アコークロー」の尚玄、「SHINOBI」の虎牙光揮、「サッドヴァケイション」の宮崎あおいほか。", "2008-06-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("転がれ！たま子", "転がれ！たま子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "デビュー作「LOVE／JUICE」が国内外で高い評価を受けた新鋭、新藤風監督の長編第２作。新たな一歩を踏み出せないでいる若い女性が、さまざまな体験を通して成長していく姿をコメディ・タッチで描き出す。自分の半径500メートル以内の世界から一歩も外に出ることのない24歳のたま子。そんな殻に閉じこもった彼女に、大きな転機が訪れる……。昔ながらの下町を舞台にしながら、どこかファンタスティックな物話が進展。奇妙な設定に目が向くものの、そこから人の優しさや人生の素晴らしさが紡ぎ出され、物語は心温まるヒューマン・ドラマに仕上がった。", "2006-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Män som hatar kvinnor", "ミレニアム　ドラゴン・タトゥーの女", "", "", "http://image.eiga.k-img.com/images/movie/54920/315x210.jpg?3011", 153, "スウェーデンの作家スティーグ・ラーソンの処女作にして遺作となった大ベストセラー小説の映画化。原作は、著者の死後、世界中で2100万部を売り上げたミステリー巨編。主演は新星ノオミ・ラパス。40年前、スウェーデンの資産家の邸宅から忽然と姿を消した少女がいた。少女の親族から捜索依頼を受けたジャーナリストのミカエルは、背中にドラゴンのタトゥーを入れた天才ハッカー・リスベットの協力のもと、事件解明に挑む。", "2010-01-16", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE INDIAN IN THE CUPBOARD", "リトル・ヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「リトル・ショップ・オブ・ホラーズ」「ハウスシッター　結婚願望」のF・オズ監督が手掛けたジュブナイル・アドベンチャー。９歳になったオムリは小さなカップボード(戸棚)を手に入れる。そこに大好きなインディアンの人形を入れると、手のひらサイズの本物のインディアン、リトル・ベアに変わってしまった。オムリは自分だけの秘密にしておくが、友達のパトリックには話してしまう。面白がった彼がカウボーイ人形をカップボードに入れると、ブーンというカウボーイが現れ……。オムリとリトル・ベアの間に芽生えていく友情、オムリが生命の尊さを学んでいく過程が、ファンタジックに描かれていく。製作のK・ケネディと脚本のM・マシソンは「E.T.」のコンビ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A SUMMER STORY", "サマーストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "20世紀初めのイギリス、大学を卒業し弁護士の資格を取ったばかりの都会の青年フランクは、旅先の田舎町で美しい少女ミーガンと出会い、恋に落ちる。そして、身分の違いから二人は駆け落ちすることを約束するものの、やがて二人は別れてしまう。20年後、フランクは再びその町を訪れたが、心痛む真実に出会ったのだった……。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊豆の踊子", "伊豆の踊子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "山口百恵の映画初主演作品で、のちに結婚した三浦友和との記念すべき初共演作品でもある。大正末期を舞台に、一高生と踊り子の淡い初恋とその別れを描く川端文学の人気作の６度目の映画化。アイドル映画として、おなじみの安定企画なので監督・脚本とも特別な冒険はせず、真正面から原作を料理している。それだけに原作の踊り子に近いイメージを持つ山口百恵は、実に魅力的。名場面である船と岬の別れのシーンも印象的だが、酔客に絡まれながら芸をする踊り子の寂し気な笑顔でストップ・モーションというラストも切なく、哀しい。ヒロインを美しく撮っているという点で良質のプログラム・ピクチャーといえる。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Blueberry Nights", "マイ・ブルーベリー・ナイツ", "", "", "http://image.eiga.k-img.com/images/movie/53360/315x210.jpg?2278", 95, "「2046」「恋する惑星」のウォン・カーウァイ監督が初めて英語劇に挑んだラブストーリー。失恋したばかりのエリザベスは、カフェオーナーのジェレミーが焼くブルーベリーパイによって少しずつ癒されていく。それでも元恋人への想いを捨てきれない彼女はひとり旅に出ることを決意し……。グラミー賞受賞歌手ノラ・ジョーンズが主演デビューを飾ったほか、ジュード・ロウ、ナタリー・ポートマン、レイチェル・ワイズら豪華キャストが勢ぞろい。", "2008-03-22", "6,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かべちょろ家守", "かべちょろ家守", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "", "2006-12-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボールZ　銀河ギリギリ！！ ぶっちぎりの凄い奴", "ドラゴンボールZ　銀河ギリギリ！！ ぶっちぎりの凄い奴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "悟空の死後、孫悟飯がニュー・ヒーローとなって活躍するシリーズ第12弾。今回は武道大会をフィーチャー、格闘ゲームソフト感覚を増した作りで展開するアドベンチャー・アクション・アニメの会心作だ。武道大会が始まり、悟飯、トランクス、クリリン、ピッコロらお馴染みのキャラが参加。しかし途中から急転し、地球征服を狙う悪者ボージャックとの戦いへ。DVDは『DRAGON BOX THE MOVIES』に収録。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初恋宣言", "初恋宣言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "徳島でスターを夢見る娘・真由美は、人気スターのさくらを頼って上京。踊り子志願の笑子と一緒にさくらの付人になることに成功する。やがて、笑子はチャンスをものにしてスターになるが、真由美は傷心の日々。おまけにインチキ芸能プロにストリッパーにされかかるが……。西野バレエ団で金井克子に次いでトップ・スターとなった由美かおるがはつらつと主演を務めた。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PRINCESSE DE CLE｀VES", "クレーヴの奥方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "16世紀の華やかな宮廷社会を背景に繊細な恋愛心理を綴った、ラファイエット夫人の名作文学の映画化で、名匠ドラノワが演出している。20歳も年上の夫と結婚したクレーヴ公夫人が、若き貴公子ヌムールに抱くプラトニックな恋の行方を描く。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIRED TO KILL", "地獄の女囚コマンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "中東の独裁国家サイブラに捕らわれている革命派リーダーの救出を依頼された元コマンドのライアン。さっそく彼は各地の屈強の囚人や元殺し屋といった女ばかり６人をスカウト。サイブラに侵入した彼らは、無事独裁者バルトスに近づくが、実は仕事の依頼者が裏切り者だと知って……。B級ながら大がかりなアクションと豪華なキャストが魅力。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PASSION FISH", "パッション・フィッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "1992年アカデミー賞で主演女優賞、オリジナル脚本賞にノミネートされたヒューマン・ドラマ。中年のTV女優メイ・アリスが交通事故で下半身不随になり、故郷ルイジアナの古めかしい屋敷に帰ってくる。身寄りのない彼女は人生に絶望してアルコール漬けになるが、心に傷を持つ年下の看護婦シャンテルに支えられ、二人の間に固い友情が芽生えていく。舞台となるアメリカ南部、ケージャン・カントリー独特の自然美や音楽を採り入れた映像世界のみずみずしさは、インディペンデント作家J・セイルズ監督の面目躍如といったところ。単なるメロドラマにとどまらない豊穣な味わいを醸し出す秀作だ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初春狸御殿", "初春狸御殿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "大映は新興以来の“狸もの”の元祖。木村恵吾監督だけでも「狸御殿」（1939）「歌う狸御殿」（1942）「春爛漫狸祭」（1948）「花くらべ狸御殿」（1949）を製作している。日本で数少ない傑作マゲものミュージカルの一本。御殿で踊って歌って恋の三角関係が展開。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Caged Heat", "女刑務所・白昼の暴動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "　ジャッキーは車椅子のマックイーンが所長を務める刑務所に収監される。そこでジャッキーを待ち受けていたのは、暴力の日々、そしてサディスティックな医師による電気ショックの矯正治療だった。やがてジャッキーは仲間と結託し、脱獄を図るが……。70年代にアクションとお色気で人気を博した女囚刑務所ものの一本。ジョナサン・デミがロジャー・コーマン率いるニューワールド・ピクチャーのもとで手がけた初監督作品。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・ギャンブル必勝法", "喜劇・ギャンブル必勝法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "“日本一のギャンブラー”を気取るイカサマ師の鉄火人生を描くコメディ。競馬、競輪、麻雀、パチンコと何をやってもボロ勝ちする五郎が地回りのヤクザ・大矢根組の悪どい手口に憤激。ぼろアパートの住民たちを守るため、彼は一世一代の大勝負に出る……。山城が「白馬童子」以来、初めて主演した作品。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドリフターズですよ！　冒険冒険また冒険", "ドリフターズですよ！　冒険冒険また冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "シリーズ第３作は、学生運動の華やかなりし1968年ならではのキッチュでポップな怪作。そして若き日の志村けんが衝撃デビュー!?　を飾ったことでも知られるカルト編。全学連マルカク派が校舎を占拠し、機動隊が出動。逮捕者の中にドリフの面々がいるのだが……。学生運動、街頭パフォーマンス、アングラ演劇といった当時の風俗にも注目。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Grand Bleu Version Longue", "グラン・ブルー　グレート・ブルー完全版", "", "", "http://image.eiga.k-img.com/images/movie/10780/315x210.jpg?3436", 169, "「レオン」「フィフス・エレメント」のリュック・ベッソン監督の出世作「グレート・ブルー」に49分の未公開場面が加えられた完全版。幼い頃からイタリア少年エンゾにライバル扱いされていたフランス少年ジャック。大人になったエンゾは、ジャックを探し出し再び勝負を挑むのだった。素潜りで深度を競うフリーダイビングにかける男たちの物語。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAR　TREK：NEMESIS", "ネメシス／S.T.X", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "新・シリーズのレギュラーメンバーでの最終章となる劇場版第４作。自身の“クローン”という難敵と闘うピカード艦長と部下たちの姿を、ダイナミックな見せ場満載で描く。エンタープライズ号が緊急の外交ミッションにより、宇宙連邦と対立中のロミュランに向かう。だがそこでピカードが対面したロミュラン代表とは、青年時代のピカードそっくりのシンゾンと名乗る男だった。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("「あの子を探して」ができるまで", "「あの子を探して」ができるまで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "1999年のヴェネチア映画祭でグランプリに輝いた中国の名匠、チャン・イーモウ監督の「あの子を探して」。世界に感動を運んだこの名作の完成に至る軌跡を追ったドキュメンタリーだ。実際の撮影現場にカメラが密着し、山あり谷ありの制作過程をたどるとともに、監督と生徒役の子供たちとの間で育まれた心の交流をつぶさに見つめる。「あの子…」の感動を新しい形で蘇らせた１本だ。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Conversations with Other Woman", "カンバセーションズ Conversation(s)", "", "", "http://image.eiga.k-img.com/images/movie/33840/315x210.jpg?2904", 84, "ウェディング・パーティーで10年ぶりに再会した男と女の揺れ動く心情を、ふたりが交わすリアルな会話を通して描き出す大人のラブ・ストーリー。主演は「チャーリーとチョコレート工場」のヘレナ・ボナム＝カーターと、「サンキュー・スモーキング」のアーロン・エッカート。画面を2分割する“デュアル・フレーム”という手法を採用し、ふたりの表情を同時に映し出すことで、それぞれの心の動きを繊細に綴っていく。", "2007-02-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A｀　MA　SOEUR！", "処女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「ロマンスX」のC・ブレイヤ監督が手掛けた問題作。“ロスト・ヴァージン”を主題に置き、性に揺れる少女たちの焦燥感をなまめかしく描き出す。15歳のエレナと13歳のアナイスの姉妹はもっか処女喪失に興味津々。そんな二人は休暇先で大学生のフェルナンドと出会う。ここで容姿端麗なエレナは、太めの妹を尻目に彼をものにする。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASPER ： A　SPIRITTED　BEGINNING", "キャスパー 誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "映画やコミック、TVなどで長い間子供たちに愛されている人気キャラクター、キャスパー。これは彼の誕生秘話が明かされる心温まるファンタジー。オバケの学校に入りそびれたキャスパーは10歳の孤独な少年クリスと出会う。怪現象に興味がある彼の友達で、イタズラ好きな３人のオバケに紹介されたキャスパーは、早速オバケとしての特訓を受けることになるが……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林足球", "少林サッカー", "", "", "http://image.eiga.k-img.com/images/movie/1650/315x210.jpg?4163", 109, "かつて人気サッカー選手だったが、今は雑用係になっているファンは、少林拳を愛する青年シンと出会い、ブロック塀を蹴り崩す彼の脚力を見込んで、サッカーを教え込む。シンは少林拳仲間を集めてチームを結成。一方、敵チームはハイテク・トレーニングと筋肉増強剤で試合に備えていた。「食神」「喜劇王」のチャウ・シンチーが香港アクションの技術の粋をサッカーに導入し、香港で空前の大ヒット、アメリカ公開も決定。", "2002-06-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("孝子洞理髪師／THE　PRESIDENT'S　BARBER", "大統領の理髪師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "軍事政権下の1960年代初頭から1970年代末までの韓国。理髪店を営む男が、激動の時代に翻弄されながらも、家族と共に明るく、たくましく生き抜いていく姿がコミカルに描かれる。これが映画初監督作となったイム・チャンサンが、さりげなく権力批判を込めつつ、愛すべき庶民の姿を温かいまなざしで見つめた秀作だ。韓国大統領官邸、青瓦台のお膝元である町、孝子洞。そこで理髪店を営む平凡な男が、ひょんなことから大統領の理髪師に選ばれ……。", "2004-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャプテン翼　明日に向って走れ！", "キャプテン翼　明日に向って走れ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 35, "劇場版アニメ・シリーズ第3弾。中学サッカー大会が終わった後、欧州遠征に向けて再び全日本選抜チームが召集、そして合宿が始まった。翼は過去を振り返りながらも、ゴールキーパーの若林や、戻ってきた岬らと猛特訓に励む。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("開國大典／FOUNDING CEREMONY", "開国大典", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 174, "1989年、中華人民共和国建国40周年を記念して製作された一種の国策映画。1948年末、毛沢東率いる人民解放軍が次第に蒋介石の国民党軍を追い詰め、1949年10月１日、ついに新中国成立が宣言されるまでを描いている。毛沢東役のクー・ユエ、蒋介石役のソン・フェイフーはともに、それぞれの役の専門役者として有名。", "1989-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLICE ACADEMY", "ポリスアカデミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "世間から恐れられる（？）警察学校を舞台にしたドタバタ・コメディ。ある都市の女性市長が、警察官採用に関する制限、差別を撤廃。おかげで、とんでもない志願者が殺到する。拳銃発砲狂、ブタ箱入り寸前の奴、興味本位の金持ち娘……。おちこぼれキャラクターたちのハッスルぶりが痛快で大ヒット。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN HELL", "ヘル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "愛妻を無残に殺した犯人への復讐を遂げた服役中の男が、そこで蔓延する暴力と賄賂にまみれた地獄絵図のような実態を暴露すべく、脱獄を決意するサバイバル・アクション。鍛え抜かれた肉体によるアクションだけでなく、人間的な弱さも垣間見せるヴァン・ダムが、極限状態の刑務所で耐え抜き、地獄の苦しみを味わう主人公を体当りで熱演。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜叉", "夜叉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "今は漁師となって、家族とともに幸福に暮らす元ヤクザの男が、飲み屋の女の妖しい魅力に惹かれ、そのヒモのヤクザの存在によって再び暴力の世界に引き戻されていく様子を描いた人情アクション。わがままなヒモのヤクザにビートたけし、飲み屋の女に田中裕子が扮して、それぞれ好演。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gravity", "グラビティ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オードリー", "オードリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "", "2012-06-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Body Double", "ボディ・ダブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "　俳優のジェイクはしばらく旅に出ることになったサムの部屋の留守番を請け負う。そこの望遠鏡で向かいに暮らす美女グロリアの部屋を覗き見してしまうジェイク。彼はグロリアを尾行し、その後、ふたりは結ばれることに。しかしグロリアが電気ドリルで惨殺されるという事件が。やがてジェイクは自分が罠にハメられたことに気づく……。ヒッチコック調の設定に加え、官能的なシーンを盛り込んだデ・パルマ監督お得意のサスペンス・スリラー。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Plein soleil", "太陽がいっぱい", "", "", "http://ecx.images-amazon.com/images/I/51aWIrZPz4L._SL210_.jpg", 117, "パトリシア・ハイスミスの原作小説を、巨匠ルネ・クレマン監督が映画化したサスペンスドラマ。主演アラン・ドロンはこの1作で一気にスターダムを駆け上がった。貧しいアメリカ人青年トム（ドロン）は、金持ちの道楽息子フィリップの父親に頼まれ、彼を連れ戻すためナポリにやってきた。金にものを言わせ女遊びに明け暮れるフィリップに怒りと嫉妬を覚えたトムは、フィリップを殺して彼に成りすまそうと計画するが……。音楽はニーノ・ロータ。", "1960-12-31", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BABY MAKER", "受胎の契約 ベビー・メーカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "子供ができない夫婦に頼まれ、金のために“代理母”を決意したティッシュ。だが赤ちゃんが体内で育っていくに従い、徐々に母親としての本能が芽生えてきて……。夫と代理母との肉体的結びつきから生じる愛など、微妙な問題も絡めて描く、社会派のシリアス・ドラマ。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リストラ代紋", "リストラ代紋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "『リイドコミック』誌連載のハードボイルド漫画の映画化。あまりの狂暴さゆえ、所属する急坂組をリストラされてしまったヤクザの鳴海俊は、愛する妻・映子の勧めで区役所の臨時職員に転職。だが、ヤクザ時代の彼への恨みから暴力団員たちが鳴海潰しへと動き始め、彼の周辺で暴力団絡みの不審な事件が多発。真相解明に乗り出す鳴海だったが、最愛の妻が人質に取られてしまう。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DOOM GENERATION", "ドゥーム・ジェネレーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ドラッグ、セックス、クラブ通いで退屈な日々を紛らわしていたエイミー・ブルーとボーイフレンドのジョーダン・ホワイト。ある時二人は、クラブの駐車場で数人の男に襲われていたXことゼイビア・レッドを助け、ともに旅することに。だが、Xは自分に危害を加える人物はあっさりと殺す残虐な人物だった。エイミーは恐怖を感じながらもXと関係を持ち、不思議な三角関係が出来上がる。そしてXが行動を起こす度に事件が起こり、３人の逃避行は次第に破滅へと向かっていく。米インディペンデント界の鬼才G・アラキのメジャー・デビュー作。ゲイである監督が初めて男女の愛を描いた。", "1994-01-01", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Choirboys", "クワイヤボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "　クワイヤボーイズ（少年聖歌隊）と称するロサンゼルス警察の10人の警官たちは、勤務19年のベテラン、ウェーレンを筆頭に強烈な個性の持ち主ばかり。巡回先でくすねた酒を持ち寄り、パーティを開くなど好き放題のならず者たちだった。しかしインテリでハンサムなバクスターの自殺がきっかけとなり、やがて凄惨な事件が……。警官出身の原作者が著したベストセラー小説を映画化。痛快な笑いとともに、ベトナム戦争後の鬱屈感も漂う。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さくら隊散る", "さくら隊散る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「原爆の子」「第５福竜丸」と、原爆問題と取り組んできた新藤兼人監督の原爆ものの１本。第二次大戦下、丸山定夫・園井恵子・高山象三、などの新劇人を中心とした移動演劇隊の一つ・櫻隊が広島で巡演中に原爆の洗礼を受け、９人が非業の死を遂げるまでを忠実に再現した。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン／瞳の中の暗殺者", "名探偵コナン／瞳の中の暗殺者", "", "", "http://ecx.images-amazon.com/images/I/51I-xsYRg%2BL._SL210_.jpg", 100, "少年探偵・江戸川コナンが活躍するジュヴナイル・ミステリー・アニメの劇場版第４作。現職警官が次々と射殺される事件に遭遇したことがきっかけとなって、その調査に乗り出したコナンたち一行。やがて彼らは、１年前に起こった外科医師の自殺事件を担当した者たちが、今回の犠牲者でもあることを知らされるが……。", "2000-04-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD AND SAND", "血と砂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "スペインの社会派作家ブラスコ・イバニェスの小説の初映画化。伝説の美男俳優ヴァレンチノと“毒婦”女優N・ナルディの共演により、世界中のファンを魅了した無声映画である。貧しさを乗り越え花形闘牛士となった男が、妻を捨てて妖艶な女に走り、悲劇的な最期を遂げるまでを描いたメロドラマ。", "1922-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女唇のわななき", "女唇のわななき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "「借王〈シャッキング〉」ほか、やくざ映画などで活躍する和泉聖治監督が1975年に手掛けた、ミステリー仕立てのピンク映画の異色作。大物彫刻家の父亡き後、家出していた長女と次女が戻った山村家。遺産をめぐって三姉妹は対立し、殺人事件まで起こってしまう。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("酒井家のしあわせ", "酒井家のしあわせ", "", "", "http://image.eiga.k-img.com/images/movie/33780/315x210.jpg?4761", 102, "複雑な関係の一家が、ある事件を通して壊れかけた絆を取り戻していく姿を綴るホームドラマ。新鋭・呉美保監督が愛情とユーモアをこめて丁寧に描き出し、サンダンス・NHK国際映像作家賞2005日本部門を受賞した。父親役に「UDON」などヒット作への出演が続くユースケ・サンタマリアが、母親役に人気お笑い芸人の友近が扮する。また、様々な問題と向き合いながら成長していく多感な年頃の息子を「血と骨」の森田直幸が好演。", "2006-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SKIPPER BLOODY ATLANTIC", "デスウインズ 沈黙の航海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「U・ボート」で国際スターとしての名を高めたJ・プロホノフ主演の海洋アドベンチャー。刺激を求め、田舎から逃げてきたスーとロウ。二人は自分たちが働くナイトクラブで、スキッパーという男と知り合い、４週間の航海に出る。どこまでも続く青い海の中、航海を楽しむ３人の男女。しかし次第にスキッパーをめぐり、険悪なムードが漂い始める……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BACKDRAFT", "バックドラフト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "兄弟愛に、放火事件のサスペンスを絡めたR・ハワード監督の大作。幼い頃に、父を失ったスティーブンとブライアンの兄弟。スティーブンは、今や亡き父と同様消防士になっていた。そこに、弟のブライアンが新米として入ってくる。徹底的に反目する二人。やがてブライアンは、消防士として兄に勝てないことを悟り、放火犯罪調査官の助手に転身。そこで、ブライアンが手掛けた連続殺人事件に、意外な犯人像が浮かび上がり……。K・ラッセルが、勇気ある消防士を熱演。まるで生き物のような、火事の炎のSFXが秀逸だ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボール　摩訶不思議大冒険", "ドラゴンボール　摩訶不思議大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 46, "80年代から90年代にかけ大ブレイクした、ゲームソフト感覚の絶妙キャラたちが活躍するアドベンチャー&アクション・アニメの劇場版第3弾。2006年にDVD化されてリバイバル・ヒットとなったのは記憶に新しい。毎回登場する映画版オリジナルの強敵キャラクターも魅力だ。本作では、原作やTVシリーズとは異なる設定で描かれたパラレル・ワールドが展開する。DVDは『DRAGON BOX THE MOVIES』に収録。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RADIO FLYER", "ラジオ・フライヤー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "母の再婚のため、カリフォルニアに越して来た幼い兄弟マイクとボビー。新しい父は彼らに冷たく、外に出ればいじめられてしまう。そんな彼らはここから逃げ出すため、夢の中のお告げに従い、空飛ぶマシン“ラジオ・フライヤー”を作り始める……。虐待に遭いながらも明るく生きようとする兄弟の姿が感動的な、ちょっと切ないドラマ。トム・ハンクスがノンクレジットで出演。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK SHEEP", "プロブレムでぶ　何でそうなるの!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "『サタデー・ナイト・ライブ』出身のC・ファーレイ、D・スペードのデブチビ・コンビが主演した爆笑コメディ。ドジなマイクは州知事選挙に立候補した兄のためにひと肌脱ぐが、ヘマばかり。そのうえ、ドラッグをやっているという噂まで立てられてしまう。マイクは選挙が終わるまで兄の選挙参謀スティーブと田舎町に隠れ住むことにするが……。製作と監督は「ウェインズ・ワールド」を生んだR・マイケルズとP・スフィーリス。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L.A. Without a Map", "GO！GO！L.A.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "フィンランドの異才ミカ・カウリスマキが、リチャード・レイナーの自伝的小説をもとに映画化したオフビートなラブ・コメディ。イギリスの片田舎で稼業の葬儀屋を継いだ22歳の青年リチャードが、旅行に来ていたアメリカ人の女優の卵バーバラに一目惚れ。彼は仕事も婚約者も捨てて彼女を追ってロスへ向かうが、田舎者の彼はなかなか都会になじめない。やっと見つけたバーバラにも冷たくあしらわれ、落ち込む彼は、クールなギタリスト、モスに気に入られ何かと面倒を見てもらうことになる。リチャードとバーバラ、彼女の友人とモスの２組のカップルが巻き起こす奇妙な恋模様を、乾いた笑いのなかに温かさをにじませて描く。J・デップやA・エーメなどスターの特別出演にも注目。", "1998-01-01", "3,2,20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワーキング・ホリデー", "ワーキング・ホリデー", "", "", "http://image.eiga.k-img.com/images/movie/57950/315x210.jpg?2918", 70, "2012年・第4回沖縄国際映画祭の長編プログラム「Peace」部門にて上映。", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("醜聞", "醜聞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「野良犬」を完成させて脂ののりきった時期の黒澤監督作品。いたずらに醜聞を追うジャーナリズムを糾弾する一方で、病気の娘を持つ一人の弁護士の人間像を力強く描き出す。志村喬が弁護士役を熱演、醜聞に巻き込まれる歌手を演じる山口淑子は息をのむ美しさ。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mortal Thoughts", "愛を殺さないで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "　シンシアとジョイスは幼なじみの親友同士。ジョイスと暴力的な夫ジェームズの間にはいさかいが絶えなかった。ある夜、シンシアとジョイスはカーニバルに出かけるが、そこでジェームズが殺されるという事件が。一体何が起こったのか？　刑事ウッズがシンシアを事情聴取するが……。殺人事件の真相、そしてそれに絡むふたりの人妻の心理を描いたサスペンス。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古都", "古都", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "スーパースター・山口百恵の引退記念作品として撮られた一編。片や裕福な呉服問屋に引き取られ、片や村の捨て子として育った双子の姉妹の、出会いと別れをしみじみと描く。京の老舗の生活のたたずまいに、市川崑監督らしい艶やかな美意識がにじむ。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("＄（DOLLARS）", "バンクジャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ハンブルグにある銀行の貸金庫に眠る不正な金。その巨額の札束を、情婦とともに知的にスマートに盗み出した警備部長。悪党どもの追跡をかわして開いた鞄には、入っているはずの150万ドルの代わりに紙クズが……。G・ホーンのキュートな魅力がいっぱいのサスペンス・ドラマ。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRIPLE CROSS", "レディ・ファイター 怒りのエンジェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "元全米女性空手チャンピオンのS・ラスロック主演によるアクション映画。アメリカより“バブル・コンピュータ”の輸送を任されたナンシーは、途中シンジケートの罠にはまり、コンピュータを奪われてしまう。だが、それが偽物と知ったシンジケート側は、ナンシーを追跡、襲撃する。ナンシーは途中、偶然再会した元恋人とともに逃げようとするが……。", "1991-01-01", "73", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋と涙の太陽", "恋と涙の太陽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "橋幸夫がパイロットに扮した青春映画。日の丸観光のパイロット・健一は母に結婚をせっつかれ、乗り気のしないまま女医の和美と見合いをする。和美も家族から結婚を強要されたクチ。二人は見合い粉砕の杯を上げる。が、ひょんな事件から再会、二人の仲は急速に進展する。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Big Fish", "ビッグ・フィッシュ", "", "", "http://image.eiga.k-img.com/images/movie/1290/315x210.jpg?3541", 125, "死期の近づく老いた父エドワードの看病に里帰りしたウィル。父の得意なホラ話ではなく本当の話を聞きたいというウィルに、父は若き日に巨人と旅に出た話を語るのだが。ティム・バートン監督がダニエル・ウォレスの同名小説を映画化。アルバート・フィニー扮するエドワードの若き日をユアン・マクレガーが、ジェシカ・ラング扮するその妻の若き日を「マッチスティック・メン」のアリソン・ローマンが演じる。", "2004-05-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PIRATE", "ラ・ピラート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "アルマは誰からも愛される女である。夫のアンドリューはもちろん、彼と知り合う前からレズ関係にあったキャロルも、そしてキャロルにぴったり寄り添う奇妙な少女も、アルマなしでは考えられない。その日もキャロルと少女は、アンドリューのところから強引にアルマを連れ去った。アンドリューはナンバー５と呼ばれている男にアルマを連れて帰るよう依頼する。こうしてアルマをめぐる４人の情念の駆け引きが、異常な精神世界の中でエスカレートしていく。B・ニュイッテンの官能的なカメラワークと、P・サルドの激しくも美しい旋律を得て初めて勝ち得たJ・ドワイヨン芸術の最高峰。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("J.A.C.E.", "J.A.C.E. ジェイス", "", "", "http://image.eiga.k-img.com/images/movie/57360/315x210.jpg?4120", 142, "", "2011-01-01", "27,25,77,32,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大太監李蓮英／LI　LIANYING，THE　LMPEMAL　EUNUCH", "李蓮英〈リー・リエンイン〉　清朝最後の宦官", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「ロック青年」「青い凧」のチィエン・チュアンチュアン監督が手掛けた重厚な歴史大作。19世紀後半、宦官の最高位に登り詰めて権力をふるった李蓮英が、西太后の死によって自らも宮殿を去っていくまでの後半生を描き上げる。圧政の女帝、悪の宦官として名高い西太后と李蓮英を、人間らしい人物と見なした歴史的解釈が話題を呼んだ。", "1990-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Session 9", "セッション9", "", "", "http://image.eiga.k-img.com/images/movie/1640/315x210.jpg?3077", 100, "19世紀に建設、15年前に閉鎖されたゴシック様式の巨大な精神病院。その改修作業に赴いた清掃業者5人のうち、ひとりが行方不明に。一方、院内に残る当時の診療記録テープには、陰惨な出来事が記録されていた。「ワンダーランド駅で」（98）のブラッド・アンダーソン監督の第4作。舞台となった精神病院は、マサチューセッツ州の実在の建物。アイスピック療法、前頭葉白質切除など劇中の療法も当時、実際に行われていたもの。", "2002-06-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・極道の妻たち", "新・極道の妻たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "藤波組を切り盛りする２代目の未亡人、加奈江。彼女には、同系列の組を仕切る息子・直也がいた……。今回は母として、そして姐御として生きるヒロインと、その息子との壮絶なまでの葛藤が描かれる。主演はこれがシリーズ３度目となる岩下志麻。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVEN-６-BOX", "HEAVEN-６-BOX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "ホモセクシャル映画でも注目される自主映画作家、大木裕之が、1994年９～11月の３ヵ月間に渡って、高知県での様々な出来事や人々の表情などを撮影した作品。高知県立美術館が製作し、橋本大二郎県知事も出演している。ベルリン国際映画祭でネット・パック賞を受賞した。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Open Season 2", "オープン・シーズン2 ペットVS野生のどうぶつたち ", "", "", "http://image.eiga.k-img.com/images/movie/54590/315x210.jpg?3629", 76, "人間に育てられた熊の冒険を描いたCGアニメーション「オープン・シーズン」の続編。元ペットの熊ブーグと野生育ちの鹿のエリオットは仲良く森で暮らしていた。ところがエリオットの結婚式が近づいたある日、元ペットのダックスフンドのウィニーが飼い主に見つかってしまい……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使のわけまえ", "天使のわけまえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "南果歩の設立したアポロフィルムの第１回製作作品であり、辻仁成の初監督作。クリスマスの１日、ヒロトとミンコが性感マッサージの仕事で出会う人々のドラマをオムニバス形式で描く。世間への不満からオモチャのマシンガンを撃ち鳴らす孤独な画学生、記憶喪失の詩人、男と別れたばかりの女流作家、死んだ妻の遺体と暮らす狂気の男。様々な人々との出会いから多くの不幸と幸せを知り、二人はまた次の世界へと旅立っていく。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO LOVE／二つの愛の物語・キャッチボール", "TWO LOVE／二つの愛の物語・キャッチボール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "浜田省吾の楽曲を基に製作した短編。彼のビデオクリップなどを担当してきたスタッフが、曲とドラマの融合という新しいステージを見せてくれる。単身赴任中の父に会いに、新潟へと向かった康汰と綾佑の兄弟。だが、初めての二人旅はうまくいかないことの連続。一方の父も、まさか子供たちが自分に会いにきているとは知らず、仕事上のトラブルでなかなか家に帰れずにいた……。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRUE LIES", "トゥルーライズ", "", "", "http://ecx.images-amazon.com/images/I/519k37qrSML._SL210_.jpg", 141, "「ターミネーター」シリーズを大ヒットさせた名コンビ、J・キャメロンとA・シュワルツェネッガーによるアクション巨編。家族にはコンピュータのセールスマンと偽りながら、実は対テロリストのスペシャル・エージェントのハリーは、とり逃したテロ犯につけ狙われ、妻もろとも誘拐されてしまう。テロ集団のアジトには４基の核兵器が、アメリカの主要都市を標的に準備されていた……。フロリダの観光名所セブンマイル・ブリッジを実際に爆破するなど、ド派手なアクションがめじろ押し。だがそれ以上に印象的なのは随所に散りばめられたコメディ・シーン。シュワちゃんのドタバタ喜劇役者ぶりも見どころだ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КАМЕННЫЙ　ЦВЕТОК", "石の花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ソ連映画初のカラー作品となった癒し系のファンタジー作品。鮮やかに輝く石の花の映像や幻想的な女王の姿、神秘に満ちた森の様子など、ウラルの風土性を取り込んだ色彩感覚が新鮮。後発のファンタジー映画に多大なインパクトを与えたと言われている。石細工師の青年はフィアンセに石の花の鉢を贈ったが、仕上がりが不満だった。そんな時、森の女王の甘い囁きを耳にして森の中へ……。", "1946-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Yumurta", "卵", "", "", "http://image.eiga.k-img.com/images/movie/56180/315x210.jpg?2681", 97, "イスタンブールで暮らす詩人のユスフは、母親の訃報を受けて何年も帰っていなかった故郷に戻る。古びた家ではアイラという美しい少女が待っており、ユスフはアイラが5年にわたり母の面倒を見てくれていたことを知る。アイラから聞いた母の遺言を実行するためユスフは旅に出るが、それは自身のルーツをたどる旅でもあった。第60回ベルリン国際映画祭で金熊賞を受賞した「蜂蜜」（2010）へと続く、セミフ・カプランオール監督の“ユスフ3部作”の第1部。", "2011-07-30", "32,27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スノープリンス　禁じられた恋のメロディ ", "スノープリンス　禁じられた恋のメロディ ", "", "", "http://image.eiga.k-img.com/images/movie/54300/315x210.jpg?4464", 0, "昭和11年、北国で暮らす少年・草太は貧しいながらも祖父と2人で慎ましく暮らしていた。学校に通えない草太のそばにはいつも秋田犬のチビと村一番の令嬢・早代がいて、草太は絵描きになることを夢見ていつも絵を描いていた。草太と早代は密かに心を通わすが、クリスマスの夜、2人に悲しい運命が訪れる……。主演は映画初出演となるジャニーズJr.の森本慎太郎。監督を「東京タワー／オカンとボクと、時々、オトン」の松岡錠司、脚本を「おくりびと」の小山薫堂が手がける。", "2009-12-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHAG", "ラスト・サマー・グラフィティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "P・ケイツ主演のトキメキの青春コメディ。まだビートルズもロックンロールもなかった時代、若者たちは“シャグ”というダンスに夢中だった。ハイスクールを卒業したばかりの仲良し４人組の女のコたちは、学生最後の夏の思い出をつくろうと、ビーチサイドの別荘へ繰り出していくのだが……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPY HARD", "スパイ・ハード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ある事件がきっかけで引退した伝説のスパイ、ディックに再び極秘指令が下された。15年前に爆風で飛ばされたと思っていたランカー将軍が、目の前に現れた。死んでも死なないダイ・ハードな男と、世界一運のいいスパイの因縁対決を、抱腹絶倒のパロディーのオンパレードで描いたコメディ。有名スターたちの出演も要チェック。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バカブロッサム！馬鹿風呂", "バカブロッサム！馬鹿風呂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "インディーズ監督、泉常夫によるハイテンションなおバカ・ムービー。100人以上の出演者たちが時空を越えて繰り広げる、何でもありの奇想天外な物語にぶっ飛ぶこと必至だ。ある日、地球侵略を目論む３人の美少女型宇宙人が襲来。そんな彼女たちの前に地球守護天使が現れ、人類に7つの大罪を知らしめようと、人類のバカさ加減満載の壮絶な光景を見せつける。次の日彼女たちの体に異変が生じていて……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("炎の肖像", "炎の肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "マスコミを通じて作られた虚像と、暴力や性衝動への志向性を秘めた実像を持つ、一人のスーパースターの姿を通して、その日常性や、押し寄せてくる疎外感を描き出した異色の青春映画。当時の音楽界のスーパースター、ジュリーこと沢田研二本人のステージ・シーンは圧巻。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版デュエル・マスターズ　炎のキズナXX（ダブルクロス）!!", "劇場版デュエル・マスターズ　炎のキズナXX（ダブルクロス）!!", "", "", "http://image.eiga.k-img.com/images/movie/55600/315x210.jpg?2122", 0, "トレーディングカードゲームをモチーフにした大人気コミック「デュエル・マスターズ」の劇場版。闇文明により、全文明が支配されようとしている世界。最強デュエリストの勝舞は、火文明のピンチを救うためクリーチャーの世界“デュエマ”へと向かう。だがそこにいたのは、すっかり自信をなくした火文明の切り札・爆竜ストームXX（ダブルクロス）だった。闇クリーチャーに捕まった勝舞の幼なじみのくれ太とストームXXの親友レッピ・アイニーを助けるため、2人は黒雲に覆われた巨大火山にいる“殲滅の覚醒者ディアボロスZ”に挑む。", "2010-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はいすくーる仁義", "はいすくーる仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "『週刊ヤングジャンプ』で連載された、奇想天外な発想で人気の同名コミックの映画化。筋金入りのヤクザが教師になってしまい、ツッパリ相手に大格闘を繰り広げる痛快学園アクション。「いそげブライアン」「バス」などの自主映画で注目された小松隆志の商業映画デビュー作。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EXORCIST II：THE HERETIC", "エクソシスト２", "", "", "http://ecx.images-amazon.com/images/I/518XWA0SA8L._SL210_.jpg", 118, "あの恐るべき事件から４年。メリン神父の悪魔祓いにより救われたリーガンはニューヨークで平凡な学生生活を送っていた。そのリーガンに再び異変が起こりはじめる。ちょうどその頃、教会中央当局ではメリン神父に疑惑が集中した。つまり彼は悪魔と手を結んだ異端の徒だというのだ。さっそく枢機卿の依頼を受け、ラモント神父が調査に乗り出す。ラモント神父は、リーガンの記憶を追い、ワシントン、ローマ、そしてメリン神父が昔いたというアフリカへと飛ぶ……。前作のウィリアム・フリードキン監督に代わってブアマンが演出したが、前作には及ばなかった。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Easy Rider", "イージー☆ライダー", "", "", "http://ecx.images-amazon.com/images/I/51qlRRlUudL._SL210_.jpg", 95, "メキシコからマリファナを密輸し、ひともうけしたキャプテン・アメリカとビリーは、特製オートバイを駆って一路、南へ放浪の旅に出る。自由を求めて旅を続ける彼らだが、社会はそんな彼らを受け入れようとはしなかった……。マリファナ、人種問題、ベトナム、反体制運動の激化など病めるアメリカの悲劇をヤング・ジェネレーションの鋭敏な感覚で徹底的に暴き、世界に衝撃を与えたアメリカン・ニューシネマの代表作。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("口裂け女", "口裂け女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1970年代末に流布した都市伝説「口裂け女」を題材にしたホラー映画。かつて日本中の子どもたちを恐怖に陥れた口裂け女の噂が生まれた町で、再びその噂が流れはじめる。やがて噂は現実となり、町の子どもたちが次々と姿を消していく。自身が担任を務めるクラスの生徒・美佳を目の前で連れ去られてしまった教師・京子は、同僚の松崎と共に調査を開始するが……。主演は「キューティーハニー」の佐藤江梨子。", "2007-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人形嫌い", "人形嫌い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "カメラマンの兄を慕って会津から上京した高校生の妹は、兄の恋人の存在に嫉妬し、同級生に彼女の下着姿の写真を撮らせていやがらせをするが……。TVドラマなどを手掛けたベテラン、日高武治の劇場映画デビュー作。三原順子が初々しい魅力を発揮。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN TO RUN", "アクセル 爆走マッハ428", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "生死を懸けた違法レース“ストリート・ロッド”を題材にしたカー・アクション。“ストリート・ロッド”で連勝を続ける、若き天才レーサー、ニッキー。そんな彼を心良く思わない街の顔フィルは、ニッキーの刺客として、賞金稼ぎを差し向ける。ニッキーはレース中に危機に陥り、少年を車ではねてしまう……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JURY DUTY", "陪審員だよ、全員集合！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "いい年なのにもかかわらず、いまだに両親とトレーラーハウス暮らしをしているトミー。ところが、両親が結婚記念でトレーラーハウスごとラスベガスへ旅行に行ってしまったから大変。住む場所もなく途方に暮れていたトミーだが、陪審員に選ばれ事態は好転し始めた。裁判の間、他人に会うことを禁じられる陪審員には、政府がホテルを用意してくれるのだ。せっかくの待遇を手放すまいと、裁判を長引かせるためにトミーは、一人で凶悪犯の無罪を主張するという無謀な作戦に打って出る。陪審員制度にスポットを当てた、お気楽コメディ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hustler", "ハスラー", "", "", "http://ecx.images-amazon.com/images/I/51HQQNazwlL._SL210_.jpg", 135, "W・テヴィスの小説の映画化。ビリヤードで金を稼ぐプロの“ハスラー”の世界を描く。全編の３分の２がゲーム・シーンというビリヤード・ファンにはこたえられない一編。球の動きと回転に人生のすべてを賭ける男たちを見事に描き切った異色作である。若きハスラー、エディが“ミネソタの名人”ファッツに勝負を挑み、前半はリードするが、逆転されて文無しとなる。酒びたりのなかで献身的に尽くす女の愛に報いることもできずにいるが、何とか再挑戦を申し込み、今度は勝つのだが……。ニューマンの熱気と老練グリースンの好演が光る。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ADVENTURES　OF　ELMO　IN　GROUCHLAND", "エルモと毛布の大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "水色の毛布が何よりも大好きなエルモ。ところが、ひょんなことから大切な毛布が、がめついハックスリーの手に渡ってしまい、エルモはそれを取り返すために大冒険を繰り広げるはめに。人気番組『セサミストリート』のキャラクターたちが共演したファミリー映画。ジム・ヘンソン・カンパニーが本作のためにデザインした新キャラもお目見えする。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I EVEN MET THE HAPPY GIPSIES", "ジプシーの唄をきいた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ユーゴ映画界において「時代」「スリー」（ともに未公開）といった意欲的作品で、同国の“新しい波”と称されるペドロヴィッチ監督の第４作。ヨーロッパ各地で生活を営むジプシーの部落にオール・ロケを敢行し、そこに生きる人たちのロマンに満ちた生活を、セミドキュメンタリー・タッチで描写。主人公の男ボラは、羽根買いをして毎日の生計を立てているが、金が入れば泥酔して踊り、歌う、典型的なジプシーだ。監督は、民族性豊かで自由気ままに生きる主人公の生き方を温かい目で見つめ、カメラに収める。主演のフェーミュはこの作品の成功で一躍国際スターになり、「冒険者」（1970）などで活躍。", "1967-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天と地と", "天と地と", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "20億円以上の予算を投入し、カナダの大平原に壮大なロケを敢行した戦国絵巻。上杉謙信と武田信玄の戦国史上名高い“川中島の決戦”をクライマックスに様々な人間模様が描かれていく。その膨大なスケールのため映画化は不可能といわれていた海音寺潮五郎の原作を、角川春樹が思い入れたっぷりに描いた。DVDは37分長い「天と地と　天の盤」と海外編集版特別バージョン「天と地と　HEAVEN&EARTH　地の盤」がある。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("37°2 le matin, l'integrale", "ベティ・ブルー　インテグラル　完全版", "", "", "http://ecx.images-amazon.com/images/I/41YVTX9WHYL._SL210_.jpg", 185, "「ベティ・ブルー　愛と激情の日々」に1時間近いシーンが追加された完全版。ベストセラーとなったフィリップ・ディジャンの小説をディジャン自ら脚色。「ディーバ」のジャン＝ジャック・ベネックスが監督、「愛人　ラマン」「コールド・マウンテン」のガブリエル・ヤーレが音楽を務めた。", "1992-12-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀玉マサやん", "銀玉マサやん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "東京・高円寺の“サンコーホール”は、パチプロのマサやんとガン鉄のホームグラウンドだ。マサやんは、最近そこで見かける人妻風の静香に一目惚れ。そしてデートに誘うことに成功。一方のガン鉄もキャバレー勤めの敏江に惚れデートにこぎつけるが……。「３−４×10月」でも、ビートたけしを食う好演を見せたダンカンの主演作品。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("港町紳士録", "港町紳士録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "港町・横須賀を舞台に、小料理屋の看板娘の恋人騒動をめぐる庶民たちの喜怒哀楽ぶりをほのぼのと描いた人情コメディ。大柄のマグロ漁船員は彼女にベタ惚れだが、気が小さくてなかなか想いを告白できない。それどころか、彼女に近づく男はことごとく暴力で追い払う始末で……。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hollywood Homicide", "ハリウッド的殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "ハリウッド署殺人課のベテラン刑事ジョーは副業の不動産斡旋業に熱中。彼とコンビを組むことになった新人刑事K.C.の副業はヨガのインストラクターだが、本当の夢は俳優になること。この刑事コンビが人気ラップ・ユニット射殺事件を捜査する中で、事件の背後に音楽業界の大物プロデューサーの影が浮かび上がる。監督は「さよならゲーム」「ティン・カップ」のロン・シェルトン。元ロサンゼルス市警刑事ロバート・ソウザが監督と共同脚本を担当。", "2004-01-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("樹の上の草魚", "樹の上の草魚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "第15回吉川英治文学賞新人賞を得た薄井ゆうじの同名小説を映画化した青春映画。きわめて珍しい両性具有（アンドロギュノス）体質を持った孤独な少年、比呂司が、８年のブランクを経て唯一の友人、亘と再会。やがて生命の危険から比呂司は手術を受けて女性に生まれ変わり、亘との友情は愛に変わっていく。映画初出演の吉本多香美が、複雑な境遇に置かれたアンドロギュノスのヒロインをみずみずしく好演。特異な設定にとらわれず、登場人物の揺れる感情をしっかり見据えた作品に仕上がっている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE　CITY", "リトルシティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "サンフランシスコを舞台に、男女の揺れ動く恋模様を軽妙に描くラブ・ストーリー。30歳を迎えたニーナは、画家志望のタクシー運転手アダムと同棲中。ところが、彼女は同性愛に走った元恋人が忘れられないアダムに不安を抱き、セクシーなバーテンダー、ケビンと浮気してしまう。二人の男性の間で揺れ動くニーナは、やがて妊娠していることに気付くが……。ビデオタイトルは「恋人たちの選択／リトルシティ」。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY FATHER’S HAPPY YEARS", "家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "第二次世界大戦が終った1945年、ブダペストにも新しい時代がやって来た。薬理学者のトロク・ヤーノシュは製薬会社を作り、必死の思いで事業を拡張していく。長年の彼の夢がようやくかなったかに見えた時、突如として国政に大転換が……。激動のハンガリー史を背景に描いた大河ドラマ。", "1977-01-01", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陰陽師", "陰陽師", "", "", "http://image.eiga.k-img.com/images/movie/1580/315x210.jpg?2321", 119, "夢枕獏の同名連作に出てくる逸話を使って、オリジナルストーリーを展開。晴明のライバルである悪の陰陽師、導尊役は真田広之。晴明の親友、源博雅役は「ブリスター」の伊藤英明。式神役で元SPEEDの今井絵里子、正体不明の謎の女役で小泉今日子が共演。監督は「秘密」の滝田洋二郎。同作のスタッフ、撮影の栢野直樹、照明の長田達也、編集の冨田功が再集合。美術は「サトラレ」の部谷京子、音楽は「不夜城」の梅林茂。", "2001-10-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("めいとこねこバス", "めいとこねこバス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 14, "宮崎駿が館主を務める三鷹の森ジブリ美術館にある「土星座」にて公開された、スタジオジブリのオリジナル短編アニメーション。こねこバスと知り合い、友だちになった少女メイ。その夜、メイを迎えに来たこねこバスに乗り、メイは森の奥へと冒険に出かけるのだった。同監督の長編アニメーション「となりのトトロ」の番外編的な作品。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES FEMMES S’EN BALANCENT", "そこを動くな", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "アメリカの探偵作家P・チェイニーによる“レミー・コーション”シリーズの一編。レミー・コーションはアメリカFBIのGメンだが、イタリアでのドル紙幣偽造事件でローマへやって来ていろいろな事件にぶつかる。歌手E・コンスタンティーヌ主演の探偵活劇。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("双雄／HEROIC　DUO", "ヒロイック・デュオ　英雄捜査線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「WHO AM I？」のB・チャン監督が手掛けたサスペンス・アクション。警察署の保管庫で起こった、放火自殺事件の捜査を担当することになった特捜部の敏腕刑事と、彼とともに事件の真相を追う心理学者の活躍を、謎が謎を呼ぶストーリーの中で描いていく。また、迫力たっぷりのアクションも見もので、なかでも香港の人気スター、E・チェンによるスタントなしで行われたベイブリッジから海へのダイブ・シーンは、手に汗握る一番の見どころ。東洋と西洋が入り混じる大都市・香港。国際ダイヤモンド展覧会の開幕前日、一人の刑事が署の事件資料保管庫から資料を盗み、保管庫に放火した。その後、彼は取調べの最中に自殺してしまい、事件は謎に包まれる……。", "2003-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CINE BRAVO！", "シネ・ブラボー！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "現在も多くの観客に愛されているサイレント映画の歴史と、草創期に誕生した喜劇と連続大活劇のアンソロジーを２部構成で紹介する。1903年、初の劇映画「大列車強盗」にはじまり、セネット、チャップリンらの名場面が続々と登場する、ファンにはうれしい１本。日本語版は映画評論家の山田宏一が構成を担当している。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD TIMING", "ジェラシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "時空間をバラした幻惑的なカッティング、毒気を含んだ色彩感覚、足もとが崩れていくような落下感覚……。その特異な映像表現で知られるローグ監督が、性の陶酔と不安に激しくアプローチした異色作。世紀末都市ウィーンで暮らす謎の女ミレーナと精神科医アレックス。二人の出会いと破局の物語に、ミレーナの睡眠薬自殺の真相を刑事がアレックスに追及する現時点のシーンが交錯する。官能の画家、クリムトやシーレの絵画が効果的に引用され、またキース・ジャレットやビリー・ホリデイの音楽が、“死”を呼び込む愛のミステリーの密度を見事に高めている。", "1979-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love Streams", "ラヴ・ストリームス", "", "", "http://image.eiga.k-img.com/images/movie/31550/315x210.jpg?1833", 139, "　離婚暦のある流行作家のロバートはハリウッドで若い女性たちと暮らしている。その姉サラは夫ジャックと別れ、娘の養育権をめぐって争っている。ロバートは先妻の子供と過ごすことになるが、どう接していいのかわからず困惑する。一方、サラは心を病みながらも、ロバートを心配する。ロバートもサラをかばうが……。傷つきながらも、愛を求める姉弟の姿を描いたドラマ。ジョン・カサベテス監督の集大成的な作品。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もっこす元気な愛", "もっこす元気な愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "脳性マヒのために両腕と言語に障害を持ちながらも自分を受け入れ、明るく意欲的に生きる倉田哲也氏を追ったドキュメンタリー。車を改造して足で運転免許を取得するための苦労や、交際中の女性との結婚を何とか彼女の母親に許してもらおうと奮闘する姿は、勇気と感動を与えてくれる。脳性マヒのために両腕に障害を持つが前向きに生きる哲也。彼は運命の女性・美穂と出会って結婚を決意するが、彼女の母親は猛反対するのだった。", "2005-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALIBABA AND THE FORTY THIEVES", "アリババと四十人の盗賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "あまりにも有名な『アラビアン・ナイト』から想を得て作られた作品。アリ・ババは蒙古の大軍による攻撃を受けて死んだ、バグダッド王の息子。運よく海賊団の手によって救われた彼はやがて若き首領となる。ある日、不覚にも蒙古兵に捕らえられるが……。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新吾十番勝負・完結篇", "新吾十番勝負・完結篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "武田一真を追って四国へ向かった新吾は、お鯉の方の使者・お縫と半十郎に会い、海路江戸へ。大台ヶ原で修行を積んだ新吾は、江戸城内で催される上覧試合に自源流を代表して出場。柳生流を代表する宿敵・武田一真と一戦を交える……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIGBY −THE BIGGEST DOG IN THE WORLD", "むく犬ディグビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ミルクと間違えて急速成長薬を飲まされたむく犬が人間の数倍もの大きさになり、悪い連中にサーカスに売り飛ばされたり、爆撃機や戦車隊に包囲されたり……。飼い主の少年との交情が心温まるファンタジー・コメディ。", "1972-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ghosts of Girlfriends Past", "ゴースト・オブ・ガールフレンズ・パスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Phantom the Submarine", "ユリョン", "", "", "http://image.eiga.k-img.com/images/movie/1760/315x210.jpg?3085", 103, "韓国の原子力潜水艦“幽霊（ユリョン）”が、極秘指令を受けて大平洋へ出航した。ここにいる男たちは記録では死んだことにされ、全員が番号で呼ばれていた。記録上は軍事裁判で銃殺刑となったイ・ソンチャクも“431”と呼ばれ、ユリョンに搭乗する。しかし、恐るべき指令の全容を探り当てた副艦長202は、クーデターを発令して艦長を殺害、核ミサイルの照準を日本に定める。世界戦争への危機を阻止すべく、431はたった1人で闘いを挑む。", "2001-03-03", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("なにはなくとも全員集合!!", "なにはなくとも全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "初夏の草津を舞台に、旅客の獲得をめぐって犬猿の仲にあるローカル線の駅長とバス会社の営業所長が、あの手この手の旅客獲得作戦を展開するが、若い二人の力添えで和解する。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SOUND BARRIER", "超音ジェット機", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "巨匠D・リーンが製作・監督した航空映画の記念碑的作品。音速の壁を破るという野望に燃える一人の男の激しい生きざまを描いている。当時、巷の話題をさらっていたジェット機の登場をタイムリーに捉え、“コメット”“バンパイヤ113”“アタッカー”“スウィフト”など、懐かしい往年の名機が「トップガン」顔負けの迫力で飛び交う様が見られる。どちらかというとD・リーンらしくない印象を受けがちだが、メイン・テーマは科学という魔物に挑む人間の苦悩である。", "1952-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ICHI", "ICHI", "", "", "http://image.eiga.k-img.com/images/movie/53420/315x210.jpg?3263", 120, "勝新太郎の代表作で、北野武も自ら監督・主演した時代劇「座頭市」。その主人公を女性に置き換え、綾瀬はるか主演、「ピンポン」の曽利文彦監督で映画化。共演に大沢たかお、中村獅童、窪塚洋介。ある宿場町に流れ着いた盲目の三味線弾きの女・市は、一帯を荒らす野党集団・万鬼党に絡まれる。そこに旅の侍・十馬が通りかかり、彼は市を助けようとするも、なぜか刀を抜こうとしない。市はやむなく、自らの仕込み刀で野党たちを一刀のもとに切り伏せるが……。", "2008-10-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("肉体の盛装", "肉体の盛装", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "新藤兼人の原作『偽われる盛装』を本人自ら脚色した女性ドラマ。元芸者のきくと、その娘でドライな芸者の君蝶は、まるで正反対の性格で、君蝶は金のことしか頭になかった。妹の妙子は市役所に勤めながら恋人との愛をはぐくむが、先方の親に結婚を反対されてしまう。３人の女、三様の生き方を描く。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REVERSAL　OF　FORTUNE", "人生の逆転", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "人気TVドラマ『ホテリアー』で多くの女性ファンを獲得したキム・スンウが主演を務める、ハートウォームなファンタジー・コメディ。共演は「ボイス」の人気女優ハ・ジウォン。かつてプロゴルファーを目指していたが、今は破産目前のサラリーマン、スンワン。彼はある日、自暴自棄に車を飛ばして事故を起こしてしまう……。目覚めたスンワンは、自分が人気プロゴルファーに変身していることに気付く。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花のズッコケ児童会長", "花のズッコケ児童会長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "1972年の発売以来、小学生の間で人気の児童文学のベストセラー“ズッコケ三人組”シリーズ。その第11作の映画化で、瀬戸内海に面したミドリ市の花山第二小学校を舞台に、生徒会長選に立候補することになったズッコケ三人組の奮闘を描く。「タスマニア物語」の助監督を務めた中島俊彦の監督第１作。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼくたちは見た　ガザ・サムニ家の子どもたち", "ぼくたちは見た　ガザ・サムニ家の子どもたち", "", "", "http://image.eiga.k-img.com/images/movie/56480/315x210.jpg?1811", 86, "2008年から09年のイスラエル軍によるパレスチナ・ガザ地区への大規模空爆と地上侵攻により、300人以上の子どもたちが犠牲となった。その事実に衝撃を受けたジャーナリストの古居みずえは、攻撃直後に現地入りし取材を始まる。封鎖されたガザ地区で、物資もなく食事がままならない日々を送りながらも、がれきの中で遊びながらたくましく生きる子どもたちの姿を通して、人間の「生きる力」に迫ったドキュメンタリー。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD COLD", "デッド・コールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "閉塞状況での犯人推理が緊迫を呼ぶサスペンス・スリラー。人里離れた山荘へ愛妻アリシアとのセカンド・ハネムーンに出かけた人気脚本家のエリック。二人きりで甘い時を楽しむのもつかの間、道に迷った若者、ケールが助けを求めてくる。寒さのため車は動かず電話も不通になり、仕方なく見知らぬ若者としばらく過ごすことになった夫妻だが、ケールは次第に大胆な行動をとり始めた。さらに酒に酔った彼は突然エリックに殴りかかり、自分は脱走犯だと告白するのだった……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("邪魔者は消せ", "邪魔者は消せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "熊井啓のオリジナル脚本による日活アクション映画。和製ジェームス・ディーンこと赤木圭一郎が主演している。麻薬Gメンの秋津は麻薬王クレイグ逮捕のために、売人の元締めである岩瀬組に潜入。元警官でありながらその身を暗黒街におとしめている長塚は、秋津の素性に疑問を持つのだが……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Close Encounters of the Third Kind", "未知との遭遇", "", "", "http://image.eiga.k-img.com/images/movie/49880/315x210.jpg?3507", 135, "人類と異星人との接触を感動的に描いたS・スピルバーグのSF大作。砂漠で第二次世界大戦時の戦闘機が発見され、ラコームをリーダーとして調査が行われている頃、インディアナ州で奇妙なことが起きる。町一帯の停電を調べていた電気技師のロイは、恐ろしい光を発見、追いかけて行くと、そこには同じように光を追いかけてきた人たちが集まっていた。それ以来、ロイは光に憑かれ、会社もクビになり、妻子にも逃げられる。同じ頃、ワイオミング州のデビルズ・タワーという山で毒ガスが発生し、住民が避難していた。そして、光を追い続けていたロイたちもこの山にたどり着く。そこでロイたちが見たのは、巨大なUFOのマザー・シップが降り立って来る姿だった……。地球人と異星人との間で音楽を使って交流が始まるラスト20分が圧巻。特に、ダグラス・トランブルのSFX、V・ジグモンドによる映像が素晴らしい。調査団のリーダー、ラコームをF・トリュフォー監督が演じているのも話題に。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現実の続き 夢の終わり", "現実の続き 夢の終わり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "婚約者の訃報を聞いて、台湾へと飛んだ加奈子。婚約者の正体が麻薬密売人で、台湾マフィアと交渉中、乱入者に殺されてしまったことを知った彼女は、犯人への復讐を誓うが……。日本と台湾が合作したバイオレンス映画。もともとアクション・スターを夢みて芸能界入りしたという水野美紀ならではの、ハードかつしなやかな動きに注目！", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE RIDEAU CRAMOISI", "恋ざんげ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 44, "1813年頃、ナポレオン軍の青年士官が勤務地でブルジョワ家庭に下宿し、そこの娘と恋仲になる。娘は夜ごとに青年士官の部屋に忍んでくるが、ある夜……。台詞は一切なく、すべてナレーションが語る。“カメラ＝万年筆”論で有名なアストリュックのデビュー作。", "1952-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪魔の手毬唄", "悪魔の手毬唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "横溝正史の原作を高倉健の金田一耕助で映画化したミステリー。流行歌手の和泉須磨子が、帰郷の途上、何者かによって惨殺される。死体のかたわらのトランジスタ・ラジオからは彼女の新曲“鬼首村手毬唄”が流れていた。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パンツの穴", "パンツの穴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "九州から東京の中学に転校してきた、いたずら好きの少年が巻き起こすドタバタ劇を描いた青春アクション・コメディ。中学生対高校生の対決シーンで、主人公が肥桶をかついで仲裁に入るくだりが話題に。ヤング向け月刊誌の読者投稿欄を原案とした。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Across the Universe", "アクロス・ザ・ユニバース", "", "", "http://image.eiga.k-img.com/images/movie/34240/315x210.jpg?3198", 131, "「ライオン・キング」の演出家としても知られる「フリーダ」のジュリー・テイモア監督が、全編ビートルズのナンバーで構成したオリジナルミュージカル。1960年代、父を探しにリバプールからアメリカに渡ったジュードは、カウンターカルチャーやミュージシャンとの刺激的な生活に出会い、親友マックスの妹ルーシーと恋に落ちるが、ベトナム戦争の激化で仲間たちはバラバラになっていく……。U2のボノ、サルマ・ハエックらのカメオ出演も話題。", "2008-08-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴れ犬", "暴れ犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "“悪名”シリーズの名コンビ、森一生監督と田宮二郎による４作目。拳銃マニアの大介は、ドヤ街で精巧な小型拳銃を見せられる。大介はその銃を売りに出かけるが、その間に銃を渡した男は何者かに殺される。裏には何か暴力団が関係しているらしい。命を狙われる大介は……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("援助交際撲滅運動", "援助交際撲滅運動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "少女たちの援助交際に憤った男たちが、独自の方法で彼女らに罰を与えることを決意する。それは“ヤリ逃げ”であった……。個性派・遠藤憲一が、少女をこらしめたいのか食いものにしたいだけなのか分からない変態オヤジを怪演する超過激作品。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUGLES IN THE AFTERNOON", "午後の喇叭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "西部開拓史上名高い、カスター将軍の最後の戦いを題材に「駅馬車」の原作者E・ヘイコックスが書いた小説の映画化。もっとも主人公は騎兵隊員ながら“最後の戦い”には加わらず、恋敵が戦死したために最後はヒロインと結ばれるという設定。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大丈夫であるように Cocco 終らない旅", "大丈夫であるように Cocco 終らない旅", "", "", "http://image.eiga.k-img.com/images/movie/54040/315x210.jpg?3811", 107, "「誰も知らない」「歩いても　歩いても」の是枝裕和がミュージシャンのCoccoに密着したドキュメンタリー。故郷・沖縄の環境問題に取り組むCoccoは、「ゴミゼロ大作戦」を開催したり、絶滅の危機に瀕するジュゴンについての歌「ジュゴンの見える丘」を発表するなど、独自のアプローチで音楽活動を続けている。そんな折、青森のファンから届いた手紙で核再生処理施設の存在を知ったCoccoは北へと向かう。", "2008-12-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents kiss!　「収穫」", "桃まつり presents kiss!　「収穫」", "", "", "http://image.eiga.k-img.com/images/movie/57910/315x210.jpg?2692", 26, "", "2009-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Chase", "逃亡地帯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "石油成金が権力を握るアメリカの一地方都市。ある日、ここの土地出身の殺人囚が脱獄、街に舞い戻ったというニュースが入り、人々は騒然となる。脱獄囚の“人間狩り”に狂気する人々の描写が衝撃的。現代社会に渦巻く不安と退廃、そして人間の内なる暴力性を鋭くえぐり出した、社会派ドラマだ。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARNIVAL STORY", "カーニバルの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "西ドイツで撮影された愛情ドラマ。A・バクスターのセクシーな魅力が楽しめる。アメリカのサーカスがミュンヘンを巡業中、スリを働いたドイツ娘がサーカスの呼び込み人に助けられるが、その代償として肉体を奪われてしまう。しかし彼女にとってこれは次々と起こる悲劇の始まりにすぎなかった。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽の季節", "太陽の季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "石原慎太郎が前年に芥川賞を受賞した同名小説を長門裕之と南田洋子のコンビで映画化した話題作。主人公の虚無的な高校生は、銀座で拾った娘と肉体関係を結ぶ。色恋ざたになりそうになり兄へ売り渡すが、娘は妊娠中絶に失敗して死んでしまう。石原慎太郎の原作は大胆なセックスと暴力描写で話題を呼び、小説の舞台となった湘南海岸に群がる青少年たちは“太陽族”と呼ばれた。映画化された作品は平凡な風俗映画であったが、興行的に大ヒットを記録し、再開したばかりの日活の明るい話題となった。原作者の実弟・石原裕次郎が主人公の友人であるボクシング部員の役というチョイ役でデビューし、そのずば抜けたスター性が注目された。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎純情詩集", "男はつらいよ・寅次郎純情詩集", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "母と娘のダブル・マドンナ登場の一編。さくらの息子・満男の担任の先生にのぼせた寅さんだったが、彼女の母親を見てそっちの方にクラクラ。この母親は寅さんの幼なじみだったが、今では未亡人で病気がちの身。寅の同情は募るばかり……。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年ケニヤ", "少年ケニヤ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "大林宣彦を監督に据えて、エンピツ画の使用や画面の反転など、多彩な手法を取り入れて製作された冒険アニメーション。角川映画らしく、声の出演でも高柳良一と原田知世が、主人公ワタルとケートを演じている。舞台は1941年のイギリス領ケニア。サイに襲われ、織物商の父とはぐれた日本人少年、村上ワタルは、マサイ族の大酋長、ゼガと知り合う。３年後、成長したワタルはゼガとともに、凶悪な部族から白人の少女、ケートを救うが、３人はやがてナチスの陰謀へと巻き込まれていく。なお原作者の絵物語作家、山川惣治が、冒頭に自ら出演している。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAZZIA SUR LA CHNOUF", "筋金〈ヤキ〉を入れろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "フランスの麻薬組織を壊滅させるため、身分を偽ってアメリカの暗黒組織に入りこみ、大親分の信頼をつかんだ上で再びパリへ帰ってきた警視庁刑事の活躍を描く。フランス映画にギャングものが流行した1950年代の代表作の一つで、冷たく乾いた詩情がいい。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARIS BY NIGHT", "パリス by ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "英国政界、保守党の花形党員。しかも欧州議会のメンバーでTVやラジオでも活躍するなど人々の注目を集めているクララ・ペイジ。だが彼女の“過去”を知る男から脅迫され、ついには男を殺してしまう……。C・ランプリングが久しぶりにイギリスに戻って主演した政界サスペンス。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("X2000", "X2000", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 8, "", "2004-05-25", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京ド真ン中", "東京ド真ン中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "宍戸錠が風来坊に扮し、片想い、失恋、傷心の旅立ちという“男はつらいよ”を完全に意識した人情喜劇。独立した作品としてよりも、パロディとして捉えると面白い見方ができる。もちろん渥美清も寅さん的キャラクターで登場、大いに笑わせてくれる。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("混在北京／THE　STRANGER　IN　BEIJING", "新北京物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "北京の出版社の社宅アパートには、編集者、詩人、小説家などが暮らしていた。編集者のサーシンは硬派で軽佻浮薄な風潮に怒りを覚えていた。装丁担当のチーツは、妻子のある副編集長から言い寄られて困っていた。詩人のイーリーは妻と二人暮らしだが、新人歌手と不倫をしている。ある日、副編集長が会社の方針変更を告げる。それはもうけ優先で台湾や香港の低俗小説を出すというもので、サーシンには我慢できなかった。チーツも副編集長の妻からあらぬ疑いを持たれ、会社にいることが嫌になっていた。そして二人は、それぞれ北京を後にするのだった。", "1995-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　GREEN　HONEST：FURY OF THE DRAGON", "ブルース・リー 電光石火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "人気TVシリーズ『グリーン・ホーネット』の劇場版第２作。B・リーは、ホーネットの助手にして空手の達人カトーに扮し、肉体のみによる派手なアクションを見せてくれる。ビデオ、DVDタイトルは「ブルース・リーINグリーン・ホーネット２ 電光石火」。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAISSE ALLER…C’EST UNE VALSE", "狼どもの報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "美女と宝石をめぐって、男たちの戦いがコミカルに展開するフィルム・ノワールの快作。仮出所した主人公セルジュの目的は、宝石の奪還と女への復讐のはずだったが……。ロートネルのポップなタッチと、コケティッシュなダルクが魅力。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MONSTER SQUAD", "ドラキュリアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "少年怪物退治隊“モンスター・スクアット”の活躍を描いたホラー・コメディ。物語は100年に一度集い合った“往年のモンスター”たちが、不思議な力を発揮する“光る石”をめぐって人間と壮絶（？）な対決をするというもの。SFXの第一人者、R・エドランドがSFXと製作を担当している。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Patriot", "パトリオット", "", "", "http://image.eiga.k-img.com/images/movie/1590/315x210.jpg?1591", 164, "1763年、フレンチ・インディアン戦争が終結。有能かつ残虐な戦場の勇士だったベンジャミンは、7人の子供の父親となった今、亡き妻の願い通り暴力とは無縁の平穏な生活を送っていた。やがて独立戦争が始まり、イギリス軍がアメリカに侵入。ベンジャミンはかつて同志だったイギリス軍との戦いに乗り気ではなく、愛国心に燃える長男ガブリエルの参戦に苦い顔を見せる。しかし、家族にも戦火が迫り、イギリス将校タビントンに次男を殺された時、彼は再び銃を取ることを決意する。", "2000-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ションベン・ライダー", "ションベン・ライダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "退屈な学校生活を送る３人の中学生、ジョジョ・辞書・ブルースの目の前で、ガキ大将・デブナガが誘拐されてしまった。デブナガの行方を追う３人はいつしか覚醒剤をめぐるヤクザの争いに巻き込まれてしまう。河合美智子、坂上忍らを使って相米慎二が撮った豪快な“ガキ映画”。冒頭の８分間にも及ぶワンシーン・ワンショットの大移動撮影は、３台のクレーンを用意し、カットを割らずにクレーンからクレーンへカメラを回しながら移し替えてのすさまじい現場だったという。またスタントマンを使わぬ役者酷使のアクションはケガ人続出の大迫力。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crazy Horse", "クレイジーホース・パリ　夜の宝石たち", "", "", "http://image.eiga.k-img.com/images/movie/57280/315x210.jpg?4820", 134, "デビュー作「チチカット・フォーリーズ」（1967）以降、現代社会のさまざまな場所や問題を切り取ってきた米ドキュメンタリー界の巨匠フレデリック・ワイズマンが、世界的にも有名な仏パリの老舗ナイトクラブ「クレイジーホース」にカメラを向けたドキュメンタリー。幻想的できらびやかなショーの模様から、そこで働く女性ダンサーたちの姿、スタッフや舞台裏、オーディションの風景なども収められている。本編の最後には、著名振付師フィリップ・ドゥワレによる最新ショー「DESIR」も披露。", "2012-08-31", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　天元突破グレンラガン　紅蓮篇", "劇場版　天元突破グレンラガン　紅蓮篇", "", "", "http://image.eiga.k-img.com/images/movie/53590/315x210.jpg?3470", 0, "「新世紀エヴァンゲリオン」のガイナックスによる人気ロボットアニメの劇場版第1弾。「阿修羅城の瞳」など劇団☆新感線の作家として活躍する中島かずきが、テレビ版に引き続き原作・脚本に参加。地震・落盤に怯えながら人類が地中深くで暮らしている未来。ある日、ジーハ村の天井が崩れ去り、ヨーコと名乗る少女が落ちてくる。地上が存在することを知った2人の少年シモンとカミナは、シモンが掘りあてたメカ“ラガン”に乗り、地上へと飛び出すが……。", "2008-09-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太王四神記", "新編集版　太王四神記　第2章：すれちがいの運命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "人気韓流スター、ペ・ヨンジュンが主演を務めるテレビドラマ「太王四神記」を4部作の新編集版として劇場映画化したシリーズの第2章。タムドクの王位継承を阻止しようとする陰謀の数々により、互いを思いながらもすれ違ってしまうタムドクとキハ。そんなタムドクに惹かれるスジニは、複雑な思いでタムドクを戦士として支えようとしていた。一方、ホゲはキハへの愛や亡き母への想いを、タムドクへの激しい闘志へと昇華させていく。", "2010-05-08", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("監獄風雲／PRISON ON FIRE", "プリズン・オン・ファイアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "はりうっどにも活躍の場を広げた、チョウ・ユンファ主演の監獄アクションもの。悪徳渦巻く刑務所で、たくましく生き抜く男サバイバー・ティムの闘いを描く。S・スタローン主演の「ロック・アップ」を思わせるストーリーに、おなじみ香港テイストのアクションが展開する。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京夜曲", "東京夜曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "数年前に、妻の久子や家族を置いて家を出ていった浜中が東京の片隅にある上宿商店街にふらりと舞い戻った。何事もなかったように父が経営する電気屋で働き始める浜中。久子にひそかに思いを寄せていた青年、朝倉はそんな彼が許せず、過去を調べ始めた。そして、商店街の喫茶店のオーナー・たみと浜中が昔、付き合っていたこと、二人は当然結婚すると思われていたが、突然、たみが大沢という男と結婚したこと、そして、大沢が病死した頃に浜中が町を出ていったらしいことを知る。浜中の出現に心揺さぶられるたみ、何も言わず彼を迎え入れる久子、そして、無口で多くを語ろうとはしない浜中。40代の男女の移ろい、さまよう心を市川準が優しく綴っていく切ない大人の愛の物語。熟達した味を見せる３人の主演俳優の演技も心地よい。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("籠民", "籠民", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "実話をもとに３年がかりで製作し、香港電影金像奨で最優秀作品賞など主要４部門を制したJ・チョン監督の意欲作。“籠民”とは、金網でできた粗末なカプセルホテルのような空間のこと。この貧民向けの住居に引っ越してきた青年が、様々なワケアリの人々と出会い、心が打ち解けたのもつかの間、土地開発業者の立ち退き要求に直面する姿をコミカルに描く。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORITURI", "南太平洋爆破作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "第二次大戦中、日本から占領下のフランスへ、物資を満載した船が出港しようとしていた。これを知ったイギリス情報部のスタッター大佐は、それを連合国側のものにしようと、ある男をその船に潜り込ませるが……。B・ヴィッキ監督が、極限状態の中で虚々実々の駆け引きにほんろうされる男たちの姿を描いた戦争ドラマの佳作。ビデオ発売時タイトルは「モリツリ　南太平洋爆破作戦」。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀河の魚", "銀河の魚", "", "", "http://ecx.images-amazon.com/images/I/21ZHE0HXSFL._SL210_.jpg", 23, "絵本作家のたむらしげるが描く短編ファンタジーアニメ。湖に浮かぶ島の研究所で星を観測しながら暮らす天文学者の老人と孫の少年ユーリーは、ある日、星を食らう怪魚が天体に出没しているのを見つける。2人は怪魚を退治するため銀河の海原に旅立つ。1993年度毎日映画コンクール大藤賞受賞。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すっかり…その気で！", "すっかり…その気で！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "映画製作を夢見る弱小プロの社員と、整形外科医院に勤める女、それに失職したスタントマンの３人組が、政財界の黒幕の２号から見事に大金を強奪することに成功、セスナ機で高飛びをはかるのだが……。“トットちゃん”“伊藤素子”など当時の風俗をパロディー化した一面も。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バカバカンス", "バカバカンス", "", "", "http://image.eiga.k-img.com/images/movie/53710/315x210.jpg?2783", 78, "美智男が働く小さな洋食店のオーナーが、ある日突然失踪した。失業の危機に陥った彼の前に、半年前に新しい恋人をつくって出て行った彼女・亜衣が現われる。行き場がないという彼女を泊めてあげる美智男だったが、翌朝、彼が先輩コックの隆司と共にオーナー探しの旅に出ようとすると、何故か亜衣もついて来て……。TAMA映画祭でTAMA NEW WAVE特別賞を受賞した期待の新人・宮田宗吉監督によるロードムービー。", "2008-07-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRST　KISS", "ファースト・キス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "韓流スター、アン・ジェウクとジウ姫ことチェ・ジウが共演を果たしたロマンティックなラブ・コメディ。「火山高」「オオカミの誘惑」などで知られるヒットメーカー、キム・テギュン監督がメガホンを取っている。奥手で気が強い27歳の女性記者ヨナが、プレイボーイで知られるカメラマンと取材に出かけることに。彼の軽い態度に我慢できず、彼女は遂に辞表を出すのだが……。", "1998-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INDECENT PROPOSAL", "幸福の条件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "“百万ドルで奥さんと一夜をともにしたい”と、若夫婦に富豪が申し出る。強く愛し合っているがゆえにお互いを信じた夫婦は、どうしてもお金が必要だったためにこの申し出を受け入れる。だが二人の関係はその夜を境に少しずつ狂っていく……。“愛はお金で買えるか”という残酷な問いを現代に突きつける話題作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REVOLUTION", "レボリューション めぐり逢い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "アメリカ合衆国独立戦争を民衆の側から描いた壮大な戦争叙事詩。1776年、独立戦争のさなか、最愛の息子ネッドが独立軍に入隊したことから、トムは息子とともに戦うことを決意する。H・ハドソン監督が膨大な製作費を投じて描いた力作。A・パチーノとN・キンスキーの共演も魅力。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("End Game", "エンドゲーム　大統領最期の日", "", "", "http://image.eiga.k-img.com/images/movie/53160/315x210.jpg?3892", 96, "大ヒットドラマ「プリズン・ブレイク」のブレット・ラトナー製作総指揮の下、「ザ・エージェント」のオスカー俳優キューバ・グッディング・Jrが主演を務めたポリティカル・アクション。現職のアメリカ大統領が大観衆の前で銃撃された。犯人はその場で射殺されるが、大統領は死亡。自責の念に駆られるシークレット・サービスのアレックスは、事件の裏に黒幕がいることを突き止めた新聞記者ケイトと共に調査を開始するが……。", "2007-12-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙戦艦ヤマト 新たなる旅立ち", "宇宙戦艦ヤマト 新たなる旅立ち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1979年に放映されたTVフィーチャーを、そのまま劇場版として公開したもの。したがって公開作としては４作目だが、劇場版「ヤマト」の中には加えられないことも多い。物語も第３作「ヤマトよ永遠に」の“前編”に当たる内容。西暦2201年、デスラー艦隊と暗黒星団帝国との戦いでガミラス星が爆発。その二連星のイスカンダル星も暴走を始め、ヤマトがこれを追跡する。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLINDFOLD: ACTS OF OBSESSION", "シャナハン・ドハーティ・　イン・ボンデージ 殺人療法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "結婚二年目にして倦怠期を迎えた若妻が、精神科医のセラピーを受けたことから連続レイプ殺人事件に巻き込まれるというサイコ・エロティック・スリラー。人気TVドラマ『ビバリーヒルズ高校白書』のヒロイン、ブレンダを演じていたS・ドハーティがヌードに挑戦、体当たりの演技を見せている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TINTIN ET LE MYSTE｀RE DE LA TOISON D’OR", "タンタンとトワゾンドール号の神秘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "海の盟友パラメリックの遺言により海洋船トワゾンドール号を譲り受けたハドック船長は、タンタンとミローを連れて久しぶりに航海に出た。何のとりえもないオンボロ船のトワゾンドール号だが、法外な値段で買い取ると申し出る謎の人物が現れ、それを断るとやがてはタンタンたちの身にも危険な影が……。パラメリックが残したトワゾンドール号の秘密とは？　ヌーヴェル・ヴァーグ華やかなフランス映画の黄金期に製作された、永遠のベストセラー絵本『タンタン』の実写映画化。日本では長い間未公開だったが、1996年に初めて公開された。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE　TAKE　ONLY", "ONE TAKE ONLY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "タイの若い世代から圧倒的な支持を得ている「the EYE【アイ】」のO・パン監督が手掛けた異色の青春映画。バンコクの裏社会を舞台に、ギャングに憧れているチンピラの男と、親に仕送りをするために売春をしている女の刹那的な日常が、生々しい映像とスピード感あふれる演出で綴られる。「レイン」で耳の聞こえない殺し屋を演じたP・モングコンビシットが、本作ではドラッグの売人を等身大で好演している。", "2001-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD END", "−less〈レス〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "クリスマス・イブの夜に車で遠出をした一家の悪夢のような体験を描くスリラー。デヴィッド・リンチの影響を色濃く感じさせる新人監督コンビが、全編が夜という不条理感たっぷりの映像世界を創造。「ツイン・ピークス／ローラ・パーマー最期の７日間」の怪優レイ・ワイズがパニック状態に陥っていく一家の主を演じる。恒例のクリスマス・パーティが行われる親戚宅を目指すハリントン一家。しかし、近道を選んだ彼らは次々と怪現象に見舞われる。", "2005-06-04", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("墨東綺譚", "墨東綺譚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "永井荷風の没後一周年を記念して製作された、同名小説の映画化。監督は文芸作品の映画化で名高い豊田四郎で、ナレーションによって原作の文体を尊重しながら、玉の井に生きる娼婦の生態や街の風俗を緻密に描写している。娼婦役の山本富士子が夢幻的な美しさをたたえて絶品。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緑子 MIDORI-KO", "緑子 MIDORI-KO", "", "", "http://image.eiga.k-img.com/images/movie/56350/315x210.jpg?1699", 55, "5人の科学者が来るべき食糧危機に備え、野菜と肉の特性を兼ね備えた夢の食物「MIDORI-KO」を開発するが、食べられることを拒否したMIDORI-KOは研究所を脱出してしまう。5人の科学者は、同じ町内で同種の研究に励んでいた農学部研究生のミドリがMIDORI-KOを飼育していることを突き止め奪還を試みる。それはやがて、人々の欲望がむき出しになる争奪戦へと発展していく。国内外でカルト的人気を誇るアニメーション作家・黒坂圭太が10年以上の歳月をかけ、色鉛筆のタッチと色彩でほぼ全てを1人で手描きした異色の長編。", "2011-09-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IKHO　CHACHVI　MGALOBELI", "歌うつぐみがおりました", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "フランスのヌーヴェル・ヴァーグの影響を受けたO・イオセリアーニ監督の瑞々しさが光る好編。自由奔放に生きる、憎みきれないろくでなしの主人公の姿が眩しい。監督が得意とする群像劇タッチも健在だ。オーケストラ奏者の主人公は、本番中に勝手にドロップ・アウトしてしまう。その後、女の子をナンパしたり約束をすっぽかしたりと、やりたい放題の毎日を過ごしていたが、意外な結末へ……。", "1970-01-01", "68", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OCTOBER 32ND", "ペンドラゴン 伝説の魔剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "中世と現代を舞台に、伝説の魔剣をめぐる黒魔術師と勇者との戦いを描く。中世、世界を支配する力を持つ“力の剣”を奪おうとする黒魔術師のペンドラゴン、それを阻止しようとする勇者ジョン・ポープ。どうにか剣を守ることはできたが、ペンドラゴンには逃げられてしまう。時が流れ、現代。10月31日、ある星が異常な動きをしていた。それはペンドラゴンが“力の剣”を奪うために復活する予兆だったのだ……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Captain Corelli's Mandolin", "コレリ大尉のマンドリン", "", "", "http://ecx.images-amazon.com/images/I/51j3LF0IPzL._SL210_.jpg", 123, "イタリア軍占領下にあるギリシャ、ケファロニア島。占領軍としてやってきたイタリア兵の中に、音楽をこよなく愛するコレリ大尉がいた。島の娘ペラギアと出会い、互いに愛し合うようになる2人。それは敵味方を超えた真実の恋だった……。", "2001-09-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE　HOUR　PHOTO", "ストーカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "人気俳優R・ウィリアムズが「インソムニア」に続き、異色の役柄に挑んだサイコ・スリラー。幸福な家族生活への幻想を抱く中年男が、ストーカーに変貌していく姿をリアルに描き出す。写真のスピード現像店で働く孤独な中年男サイ。彼は常連客で人妻ニーナの写真を現像し、自分が彼女の家族の一員になったような空想にふけっていた。そんなある日、サイはニーナ一家の残酷な現実を知り、ストーカーに変貌していく。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents 真夜中の宴　「感じぬ渇きと」", "桃まつり presents 真夜中の宴　「感じぬ渇きと」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 11, "", "2008-04-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人・鬼・情／WOMAN DEMON HUMAN", "舞台女優", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "劇団の男役、鐘馗役者として今やトップスターの座にある秋芸。彼女は観客の拍手を浴びながら、その人生を見つめ直していた。幼いころ目撃した母の逢引、役者になることに反対した父のこと、劇団の先生との愛とその苦しみ。そして彼女は一生を舞台に捧げる決意を固めるのだった。父親役で「菊豆」のリー・パオティエンが出演し、金鶏賞最優秀助演男優賞を受賞したほか、同最優秀脚本賞、リオデジャネイロ映画祭グランプリを受賞。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIME　WITHOUT　PITY", "非情の時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "“赤狩り”でハリウッドを追われた名匠J・ロージーが、ヨーロッパ亡命後に初めて本名で監督したサスペンス・ドラマ。息子のアレックが殺人罪で死刑を宣告され、ロンドンに駆けつけた中年男デビッド。しかしアレックは、アルコール漬けの彼を冷たく罵倒するのだった。奮起したデビッドは父親の面目を保つべく、アルコールの誘惑と闘いながらアレックの無実を裏付ける証拠を探し回るが、死刑執行の期限は容赦なく迫ってくる……。タイム・リミットに追われる人間の孤独と焦燥に、粘っこい心理描写で肉薄。思わぬ真実を突き止めた主人公が、捨て身の行動に出るラスト・シーンにも強烈なインパクトがある。", "1957-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARA　RECIBIR　EL　CANTO　DE　LOS　PAJAROS", "鳥の歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ボリビアの映画製作集団“ウカマウ”の代表的存在であるホルヘ・サンヒネス監督による問題作。ボリビアのある映画製作者集団が先住民たちが住む村で、16世紀にアンデス社会にやってきたスペイン人遠征部隊に関する映画の撮影を開始する。スペインによる征服という歴史的事実を批判するのが目的だったはずが、彼らもまた、無意識のうちに先住民を偏見によって撮影していき、先住民たちを怒らせてしまう。", "1995-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT GAMES", "ナイト・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "一見、何不自由なく暮らす美しい人妻ヴァレリーだが、レイプされた暗い過去があり、そのために夫との愛の行為を受け入れることができずにいた。そんな彼女の前に不思議な影が現れ……。若い女の性への恐れを描くエロティック・ファンタジー。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒砲事件／THE BLACK COMNON INCIDENT", "黒砲事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "中国映画の新しいパワーを感じさせた異色作。中国将棋の駒の一つ、黒砲。その黒砲が紛失したことがいつしか、国家経済に大きな損失をもたらす事件になっていく。いつ終わるともなく回を重ねる会議など、中国社会の問題に迫る。", "1985-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ☆ドラえもんズ　ドキドキ機関車大爆走！", "ザ☆ドラえもんズ　ドキドキ機関車大爆走！", "", "", "http://ecx.images-amazon.com/images/I/311YJ5AQ79L._SL210_.jpg", 17, "「ザ・ドラえもんズ」の活躍を描く短編アニメシリーズの第5弾。パワーセンターのエネルギーカプセルが悪の科学者アチモフによって奪われ、街中が停電になってしまった。街を救うべく集結したドラえもんズは、残されたたったひとつのエネルギーカプセルを2時間以内にパワーセンターに運ぶため、博物館にあったオンボロ蒸気機関車に乗りこんで出発するが……。「のび太の太陽王伝説」「おばあちゃんの思い出」と同時上映。", "2000-03-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAPOLE´ON", "ナポレオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 240, "オリジナル版が約12時間にも及ぶという、A・ガンス監督の「ナポレオン」のフル・オーケストラ版（４時間）。若きナポレオンが、革命期を経てイタリア遠征に挑む様がダイナミックな演出で描かれていく。クロード・ルルーシュやF・F・コッポラたちの手を経て約半世紀ぶりに復活したこの映画の特色は、“トリプル・エクラン”といわれる３台の映写機から投映される迫力ある映像、そして上映にあたって各地の一流交響楽団から集められた50～60名のフル・オーケストラの演奏効果。復活版はアメリカ、ヨーロッパをはじめカナダ、オーストラリア、日本などで上映された。", "1927-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不知火検校", "不知火検校", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "勝扮する徳の市は、意地汚なく貯め込んだ金を旗本の若奥様に貸し、その弱みにつけこんで犯す。彼は師の検校を殺し、自らが成り代わり悪業の限りをつくすが、ついには役人に捕えられる。ピカレスク時代劇の佳作。勝の盲目の演技は、のちの“座頭市”への布石ともいえる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROLLING VENGEANCE", "ローリング・ベンジェンス　復讐のモンスタートラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ささいなことから街のならず者一家に目をつけられ、恋人をレイプされ家族全員を死に追いやられた青年が、奇怪な姿の巨大トラックを作り復讐に立ち上がる。アクションはたっぷりだが流血シーンは避け、アメリカ社会の病巣に目を向けた製作態度が意欲的。", "1987-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE INCREDIBLE MR.LIMPET", "秘密兵器リンペット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "魚を愛し、空想の世界に逃避するのを唯一の楽しみにしているブルックリンの帳簿係リンペット。その思いが天に届いたのか、ある日彼はイルカに変身。海底の中を自由に泳ぎ回る彼は、美しいレディ・フィッシュに出会い愛し合うようになる。随所にアニメを取り入れて綴る愉快なファンタスティック・コメディ。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バカヤロー！ 私、怒ってます", "バカヤロー！ 私、怒ってます", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "森田芳光が新人監督４人を起用して、総指揮・脚本を務めたオムニバス・コメディ。普段はおとなしい人間が、とことん追いつめられて「バカヤロー！」と叫び出すまでの４つの物語で構成されている。各エピソードの主演者が好演し、なかでも第３話「運転する身になれ！」で、タチの悪い乗客に悩まされるタクシー運転手を演じた大地康雄が秀逸。", "1988-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不敵なあいつ", "不敵なあいつ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "その悪どさにいや気がさし、組を飛び出して流れ者となった都築浩介。彼は流れ歩くうちに同業の流し・熊五郎と知り合い、食堂兼ホテルの黒潮亭に身を落ちつけることにする。しかし、そこにも横暴なヤクザ・港東会が。都築は何度か彼らを叩きのめす……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("想色～オモイ・ノ・イロ～", "想色～オモイ・ノ・イロ～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "杏奈、亜紀、祐香の仲良し３人組は高校を卒業して、別々の道を歩み始めていた。ある日、祐香が病院に運ばれた事で、久々に顔を合わせるのだったが……。若槻千夏、岩佐真悠子、太田千晶という人気グラビア・アイドル３人が共演。悩みを抱えながらも、少女から大人へ成長する微妙な時期を、繊細に綴っている。監督は「輪舞曲～ロンド～」の喜屋武靖。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRAILTY", "フレイルティー／妄執", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「サンダーバード」のB・パクストンが監督に初挑戦した問題作。FBI捜査官ウェズリー・ドイルのもとに、フェントン・ミークスと名乗る謎の男が訪ねてくる。“神の手”（ゴッド・ハンド）と呼ばれる連続殺人鬼の正体が、自殺してしまった弟のアダムだというのだ。アダムが埋葬されているという場所に車で向かいながら、ドイルはミークスから子供時代の恐ろしい物語を聞く……。ミークスが語る回想シーンで、パクストン演じる父親が、神の使命だと信じて殺人を重ねて行く過程は想像を絶するほど恐ろしい。意外な真相が明かされるラストも衝撃的だ。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pain／ペイン", "Pain／ペイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "行き場所もわからないまま都会をさまよう二人の男女。真理はストリートキッズの群れに引き込まれ、敦はAVスカウトマンの世界へ足を踏み入れていく。東京の裏舞台を漂う若者たちの姿を、痛み“＝ペイン”を伴うドキュメンタリー・タッチで描いた石岡正人監督のデビュー作。オポルト映画祭で審査員特別大賞を受賞している。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bob", "ボブ＆キャロル＆テッド＆アリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　ボブとその妻キャロルは人里離れた山中にある施設で愛の研修を受ける。その後、ボブはキャロルに自らの浮気を告白するが、彼女に対する愛情は変わらないと主張。これによって、ふたりの信頼はより強くなる。しかしこの話を聞いた親友のテッドとその妻アリスは大ショック。キャロルはセックスを拒むようになり……。ポール・マザースキーが初監督を務めたコメディ。セックスの自由化というテーマを、やがて夫婦交換へと至る四人を通して軽妙に描く。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fly Penguin", "飛べ、ペンギン", "", "", "http://image.eiga.k-img.com/images/movie/55240/315x210.jpg?3615", 110, "韓国の女性監督イム・スルレが、市役所に勤める人々とその家族の日常を繊細なタッチで描いた群像劇。9歳の息子に英語教育を強制する教育熱心な母親、早期教育のため妻子を海外留学させた寂しさに苦しむ中年男性、同僚たちにベジタリアンであることをからかわれる新入社員など、さまざまな人間模様を通し、韓国社会の抱える問題を浮き彫りにする。2010年の「真！韓国映画祭」にて上映。", "2010-02-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Morning Light", "モーニング・ライト", "", "", "http://image.eiga.k-img.com/images/movie/54610/315x210.jpg?4067", 97, "ロサンゼルスから4000キロ沖のハワイ・オアフ島をめざすヨット・レースに挑んだ若者15人の過酷な訓練とレースを記録したドキュメンタリー。スピードと忍耐が要求されるトランスパシフィック・ヨット・レースにおいて2度のスピード記録を樹立したロイ・ディズニーは、次世代の若者たちにチャンスを与えるため、彼のヨット“モーニング・ライト”号のクルーを募る。厳しい選考を通過した15人の若者は、友情を築きながら過酷な訓練へ挑む。", "2009-06-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADOR アドア", "ADOR アドア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "日本人女性プロボディボーダーの大河原未都、小野塚葉子、橘田妙子にスポットを当てたサーフドキュメンタリー。世界を舞台に活躍する3人の美しいライディングシーンをはじめ、3人のオシャレでロハスなライフスタイルを、8ミリフィルムによるノスタルジックな映像と、アメリカのジャズグループ「TorCH」のサウンドをバックに映しだしていく。撮影当時、妊娠6カ月だった大河原と小野塚のマタニティサーフは必見。", "2007-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハナ肇の一発大冒険", "ハナ肇の一発大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「喜劇・一発勝負」と「喜劇・一発大必勝」の間に作られた山田洋次監督・ハナ肇主演の“一発”もの第２作。肉屋の店員・間寛一が見知らぬ女からダイヤを預かってくれと頼まれ、スリリングな冒険旅行が始まる……。倍賞姉妹の使い方がミソ。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝ予科練", "あゝ予科練", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "東映戦記映画三部作の完結篇となった青春群像劇スタイルの戦争映画。特攻隊員として突撃するやるせない余韻を残しながらも、彼らの友情、恋、家族愛を謳い上げる。昭和18年の春。航空隊に5人の若者が入隊し、さっそく厳しい訓練の中へ。家族らとの束の間の交流を経て激戦地へ飛ぶ。初陣の戦いは成功するが……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワールドクラシック＠シネマ2011 オペラ 「ばらの騎士」　バーデン・バーデン祝祭劇場", "ワールドクラシック＠シネマ2011 オペラ 「ばらの騎士」　バーデン・バーデン祝祭劇場", "", "", "http://image.eiga.k-img.com/images/movie/56460/315x210.jpg?1625", 211, "世界各地の有名オペラハウス、バレエ団の上演を映画館のスクリーンで上映する「ワールドクラシック＠シネマ2011」（全8作品）の1作。リヒャルト・シュトラウスによる名作オペラを、ドイツを代表する指揮者ティーレマンがミュンヘン・フィルを率いて演奏。南西ドイツの温泉地として有名なバーテン・バーテンで開かれる冬の音楽祭で2009年に上演された。", "2011-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOSPEL", "ゴスペル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1981年６月、カリフォルニアのパラマウント劇場でのコンサートを収録した作品。マイティ・クラウズ・オブ・ジョイやシャーリー・シーザー、J・クリーブランドなどの西海岸の人気スターが勢ぞろい。ゴスペルの圧倒的な迫力が生々しく伝わる。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今日のいのち", "今日のいのち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 156, "病院の再建に奔走する理子と、彼女をいちずに慕う年下の男・岳二との恋愛を描いたメロドラマ。前年の「太陽の季節」でデビューした石原裕次郎が岳二の恋のライバルとして出演している。原作は「女中っ子」に続いて由起しげ子の作品が選ばれた。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("平凡ポンチ", "平凡ポンチ", "", "", "http://image.eiga.k-img.com/images/movie/53940/315x210.jpg?3688", 102, "ジョージ朝倉の同名人気コミックを「東京ゾンビ」の佐藤佐吉が監督・主演で映画化。ヒロイン役「仮面ライダー電王」シリーズのグラビアアイドル、秋山莉奈。真島は30過ぎのデブで冴えない男だが、映画への強い志だけはあった。そして遂に商業映画デビューのチャンスを掴むが、土壇場でライバルにそのチャンスを奪われてしまう。落ち込む真島の前に、謎の美少女ミカが突然現れ、真島に自分の映画を撮って欲しいと頼むが……。", "2008-11-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CAGE AUX FOLLES II", "Mr.レディ Mr.マダム2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "舞台で、映画で大ヒットを記録したシリーズの第２作。主要キャスト、スタッフは前作と同じで、舞台は南フランスのニース。レナートとアルバンのゲイカップルが経営するナイト・スポットが、ひょんなことから共産圏スパイ団の標的にされてしまうが……。", "1980-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒線地帯", "黒線地帯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "石井輝男監督の犯罪劇“地帯（ライン）”シリーズの一編。記者が目撃する売春地帯の実態をドキュメンタリー・タッチで描く。新聞記者の町田は秘密売春組織を追ううちに何者かの罠に堕ち、犯人に仕立て上げられる。無実を証明すべく売春地帯に足を踏み入れた彼は、そこにはびこる麻薬汚染の現実と、その売買に関係した陰謀を知る。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空飛ぶゆうれい船", "空飛ぶゆうれい船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "輸送船を次々と襲う幽霊船の出現に続いて、東京にその使いと称する巨大ロボット、ゴーレムが現れた。育ての親をゴーレムに殺された少年、嵐山隼人は、ゴーレムを操っているのが実は大企業の会長、黒潮と知るが、幽霊船の船長から、さらに意外な事実を教えられる。黒潮は輸送船で武器を輸出する死の商人で、その背後には海底魔王、ボアがいるというのだ。船長が自分の本当の父と知った隼人は、幽霊船の形をした超メカに同乗して、ボア打倒に出発する……。石ノ森章太郎の少年マンガに脚色を加え、世界征服の道具として使われる毒入りジュースに、資本主義への風刺を絡ませた異色作。曖昧な幕引きに不満の声もあるが、独特のドラマ風味への人気は今も高い。宮崎駿の描いたゴーレム都市破滅シーンも、アニメ・ファンの注目を集めた。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君と別れて", "君と別れて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "菊江と照菊は場末の芸者。姉芸者の菊江は中学に通う息子・義雄のことを思ってつらい勤めに出、妹芸者の照菊と義雄は互いに想い合っていた。だが両者の家庭は貧しく、次々と困難が襲いかかる……。自ら企画・脚本を手掛けた成瀬巳喜男監督のサイレント時代の代表作。底辺に生きる人々の姿を澄んだまなざしで見つめ、初めて高い評価を与えられた。", "1933-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヨーロッパ特急", "ヨーロッパ特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "西ドイツ、オランダ、ベルギー、フランス、スイス、イタリアのヨーロッパ６ヵ国が舞台。各国の列車の写真を撮りにきた日本人カメラマンと、無一文で正体不明の謎の美少女との珍道中を描く。当然のようにヨーロッパの風景が美しく、観光映画としても見られる。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON GIOVANNI", "ドン・ジョヴァンニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 168, "モーツァルト作曲の名作オペラ『ドン・ジョヴァンニ』を、社会派監督ロージーが、本格的オペラ映画として再現。「トスカ」「カルメン」「フィガロの結婚」同様、パリ・オペラ座の一流オペラ歌手が勢ぞろいする、ファン必見の１本。", "1979-01-01", "2,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO BE OR NOT TO BE", "生きるべきか死ぬべきか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "舞台は、第二次世界大戦直前のワルシャワ。『ハムレット』を上演中の劇団員たちが、ナチスのスパイからレジスタンスのメンバーの名簿を取り戻そうとして親衛隊に化けたことから始まる一大騒動を描く。サイレント映画時代から、コメディの名手として艶笑、爆笑、哄笑、何でもござれのE・ルビッチが故郷ドイツの体制を徹底的に皮肉り、スピーディーで圧倒的な傑作に仕上げている。この映画ばかりはどんな言い方をしても面白さは表現しつくせない。クラーク・ゲーブル夫人でスクリューボール・コメディの華、C・ロンバートが事故死する直前の主演作。リメイクに「メル・ブルックスの大脱走」があり、見比べてみるのも面白い。題名は『ハムレット』の中のあまりにも有名な台詞。無関係なこの話がナチに絡んでくる絶妙な演出テクニックこそ、世に人々が“ルビッチ・タッチ”と呼んだ追随を許さぬ至芸だ。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("気球クラブ、その後", "気球クラブ、その後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「紀子の食卓」の鬼才・園子温が、荒井由美の名曲「翳りゆく部屋」をモチーフに描いた青春ドラマ。かつて二郎が所属していたサークル「気球クラブ・うわの空」には、様々な目的を持った若者たちが参加していた。5年後、ガールフレンドのみどりと微妙な関係を続けていた二郎のもとに、気球クラブのリーダーだった村上の訃報が入る。村上を偲ぶため、バラバラになっていた仲間たちが再び集まることになり……。主演は「HAZARD」の深水元基。", "2006-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO HARD FEELING", "追いつめられた女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "暴行事件で最愛の妻を亡くしたドン。マーシャル・アーツの達人の彼は、友人に紹介され母校の大学で護身術を教えはじめる。キャンパスで多発する悪質なレイプ事件に心を痛める彼。そんなある日、恋人イブが暴行犯に襲われる。カー・チェイス・シーンなども盛り込み、アクティブに仕上げた、サスペンス風味の娯楽作。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUDHONEY", "マッド・ハニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "異才R・メイヤーのバイオレンス・ドラマ。刑期を終えて出所した青年が、人里離れた農場に住み込みで雇われた。青年は農場の経営者である人妻と惹かれ合う。しかしその後、人妻の夫が牧師の妻をレイプするという事件が起こり、農場は暴力の気配に覆われていく。乱闘やリンチなどの暴力描写が田舎町の狂気を伝える。劇場初公開時のタイトルは「欲情」。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母の曲", "母の曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1937年に山本薩夫監督で映画化された小説のリメイク。元ネタはアメリカ映画の「ステラ・ダラス」（1937）である。大学教授の淳吉は、20年前に家が倒産したため、ピアニストの香苗との恋をあきらめ、田舎の温泉で働く春子と結婚した。現在は桂子という娘がいるが、春子との教養の差は隠せない。春子も娘の前途を思い、今も独身でいる香苗と純吉を一緒にさせようとして……。主演は34本もの“母もの”に主演した三益愛子。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("破れかぶれ", "破れかぶれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "アルゼンチンに行くことを夢見ながらもヤクザ稼業に精を出す光夫は、ある日、兄貴分である辰の金を使いこんでしまったことがもとで、手ひどいリンチを受ける。光夫の情婦の加代は身体を代償に金を作り、すべてを解決しようとするが……。一人の若者の夢と復讐を追う日活アクション映画の１本。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WITHOUT A TRACE", "天使の失踪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "大学教授スーザンは、夫と別れて一人息子アレックスと平穏な毎日を過ごしていたが、ある朝息子が忽然と姿を消した。狼狽する彼女の祈りもむなしく捜査活動は縮小されていく。そんなある日、スーザンのもとに見知らぬ女から電話が……。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PORTE DE LILAS", "リラの門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "パリの下町。“ろくでなし”だが人の善さでリラの住民から愛されているジュジュと、一人暮らしの歌手のもとへ殺人犯がころがりこんできた……。シャンソン界の大物ブラッサンスが主演し、話題を呼んだ人間ドラマ。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛撫", "愛撫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "温泉町医者が息子を東京の大学に送るが、息子は勉強もせず遊び暮らす毎日。ある日、誤診がもとで信用ががた落ちした医者のもとに息子の実情を知らせる便りが届き、医者はショックにうちひしがれるが……。題名とはあまりつながりのない親子の確執を描いた作品で、幻の女優・及川道子も出演している。", "1933-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("牙狼 GARO MAKAISENKI", "牙狼 GARO MAKAISENKI", "", "", "http://image.eiga.k-img.com/images/movie/57090/315x210.jpg?3925", 72, "雨宮慶太の原作・監督による人気特撮「牙狼」のTVシリーズ第2期を、TV放映に先がけて3話ごとに映画館で先行上映（全24話を3話ごとに上映）。人間の邪心に巣くう魔獣ホラーを討伐する魔戒騎士で、その最高位・ガロの称号を持つ冴島鋼牙は、元老院から新たなホラー討伐の指令を受ける。ホラー・シガレインを討ち取った鋼牙だったが、そこへ赤い仮面の謎の男が現れ、鋼牙の胸に不気味な破滅の刻印を残して去っていく。鋼牙は周囲に胸の刻印を隠しながらホラー討伐を続けるのだが……。", "2011-09-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空がこんなに青いわけがない", "空がこんなに青いわけがない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "俳優、柄本明の監督デビュー作。商事会社の課長、小林健太郎は、平凡なサラリーマン。妻は、古い家を壊してマンションにすることに熱中し、母はボケ始めている。健太郎は、OLのかおると不倫中……。そんな設定はあるものの、とり立ててドラマが起こるわけではない。ただ、平凡な健太郎の日常にちょっとしたズレが生じる様を、淡々とした映像の中に綴っていく。存在自体が無気力に見える三浦友和の好演、全編発作のような演技で場面をさらうかおる役の夏川結衣など、役者陣はいずれも印象的。映画的に成立しにくい、波風の立たない物語を、映像によって繋ぎとめた柄本監督の非凡さが光る。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Etre et Avoir", "ぼくの好きな先生", "", "", "http://image.eiga.k-img.com/images/movie/1270/315x210.jpg?2086", 104, "フランス中部、オーベルニュ地方にある小学校は、児童の数が少ないため、ひとつの教室で3歳から11歳までの13人のクラスメイトがいっしょに授業を受けている。この学校の教師ロペスは定年のため、あと半年で35年間の教師生活を終えなければならないが、生徒たちはその事実を受け入れられない。「音のない世界で」でも絶賛をあびたニコラ・フィリベール監督の最新作。本国フランスで200万人の観客を動員したヒット・ドキュメンタリー。", "2003-09-20", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRAINSPOTTING", "トレインスポッティング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "本国イギリスで巻き起こった一大ブームが、日本にも飛び火して、異例のロングランとなった青春映画。ジャンキーの気ままな生活をシビアに綴りながら、グルーヴ感たっぷりの映像センスでエンターテインメントとして処理していくスーパークールな手法が大いにウケた。数度にわたる“ドラッグ断ち”から、麻薬取引でもうけた金を仲間を裏切り独り占めしてしまうラストまで、主人公レントンの姿に一貫して流れているのはカタルシスの不在。リスキーでありながら刹那的では決してなく、単純な楽観主義の持ち主でもない。あたかも、“永遠に続く日常”を悟り、悩むことなどなく身を委ねているかのようなそのたたずまいは、すこぶる魅力的。“1990年代の「時計じかけのオレンジ」”との評も納得できる、現代性あふれる作品である。監督は「シャロウ・グレイブ」のD・ボイル。主演のE・マクレガーとともに、一躍時代の寵児となったが、レントンの凶暴な友人ベグビーを快演したR・カーライルの存在感も見逃せない。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青年の海　—四人の通信教育生たち—", "青年の海　—四人の通信教育生たち—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "大学で通信教育を受けている学生たちのグループが、通信教育の制度を厳しい条件に改革しようとする文部省を相手に反対運動を起こした事件のドキュメンタリー。悪条件の下、通信教育で学ぶことの苦しさを訴えるだけでなく、そうまでして学ぶことへの学生たちの懐疑にまで踏み込んでいることが、作品に血を通わせている。来たるべき大学闘争への予感を伝えて、“三里塚”や“牧野村”の連作で知られる小川プロの出発点となった。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸っ子繁盛記", "江戸っ子繁盛記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "酒好きの魚屋勝五郎は、浜から帰る途中、百両の大金を拾うが、長屋一同に大振る舞いしてしまう。酔って寝た翌朝、それが夢であったことを知る。恋女房・おはまに意見されて真面目に働く決心をする勝太郎だったが、今度は夜な夜な夢枕に妹・お菊が現れ……。落語の『芝浜』から材をとったマキノ雅広監督作品。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GRAVE", "共犯者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "劇場未公開のサスペンス。ホーク司祭の墓に莫大な財宝が隠されていることを聞きつけた囚人のキングとティン。脱獄を図った二人は、逃走中に撃たれたティンを助けた街の葬儀屋キャスの元に身を隠す。財宝の噂を聞いたキャスの弟トラビスは、ティンを拷問にかけてありかを聞き出し、二人の仲間とともに財宝探しに墓地へと向かう。だが、そこにキングが現れ……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A DOG OF FLANDERS", "フランダースの犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "世界中の人々に愛されているウィーダの同名童話の映画化。両親を亡くし、祖父と貧しい暮らしを送る少年ネロ。絵の才能に恵まれ、画家になる夢を抱く彼と犬のパトラッシュの友情を綴る。映画化に当たって、ハッピーエンド版とアンハッピーエンド版の二種類のエンディングが作られたが、日本では原作に忠実な悲しいエンディングが上映された。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Yeux Clairs", "明るい瞳", "", "", "http://image.eiga.k-img.com/images/movie/34160/315x210.jpg?1508", 87, "風変わりで周囲に馴染めずにいた女の子が自分の居場所を見つけ出すまでを描いた大人のおとぎ話。フランスの小さな村で暮らすファニーは、同居する兄嫁との関係がうまくいかず居心地の悪さを感じていた。そんなある日、兄嫁の秘密を知ってしまった彼女はついに家を飛び出し、ドイツにある父の墓を目指し旅を始める。国境を越え森を抜けた彼女は、きこりの青年オスカーと出会い……。主演は「不完全なふたり」のナタリー・ブトゥフ。", "2007-09-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG RED ONE", "最前線物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1918年、終戦を知らず一人のドイツ兵を殺した男がいた。そして第二次大戦中の1942年、激戦の北アフリカで、その男“軍曹”は４人の新兵を含む歩兵第16中隊第１狙撃兵分隊を指揮していた。“僕には殺人はできない”という新兵に“殺人じゃない、ただ殺すだけだ”と答える彼。やがてシシリー島からノルマンディー上陸作戦、そしてチェコのユダヤ人強制収容所の解放へと歴戦、“戦場で生き残ること”をモットーとする軍曹の教えのもと、若者たちは一人前の男に成長する……。全体を貫く明るさとヒューマンな感情、戦車の中の出産などのギャグ、従来の戦争映画にない感動を与えた異才フラーの半自伝的な快作。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SKULLS", "ザ・スカルズ／髑髏〈ドクロ〉の誓い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "アメリカに実在し、陰で国家や政治を操作していると語り継がれている秘密結社を題材にしたサスペンス・スリラー。上流階級と無縁の平凡な大学生ルークはボート大会で優勝した後、謎の秘密結社“スカル”から呼び出される。この組織の一員になれば何でも手に入り、希望通りの将来も約束されるが、組織の存在は他言してはならない。最初はとまどったルークだったが、いつしかメンバーとしての楽しさを謳歌し始める。ところが“スカル”の正体を暴こうと、ルークのルームメイトがあれこれ調べ回り……。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かもめ食堂", "かもめ食堂", "", "", "http://ecx.images-amazon.com/images/I/41oSMJvPOPL._SL210_.jpg", 102, "女流監督、荻上直子監督によるヘルシンキを舞台にしたユニークな物語。フィンランドに日本のお袋の味がメインの食堂を開いた日本人女性を主人公に、ささやかだけど幸福な日常が温かく描かれる。不思議な魅力で独特の存在感を放つ、小林聡美に片桐はいり、もたいまさこという個性派で鳴らす３人が主演し、確かな演技力による絶妙なコラボレーションを見せる。一見、ミスマッチな設定を、現実離れしないストーリーにしっかりと仕立てた荻上監督の演出も素晴らしい。日本人のサチエはおにぎりが看板料理の食堂をフィンランドに開く。しかし、現実は厳しく客足はさっぱり。そんな折、ひょんなことから出会ったミドリとマサコが、店の手伝いを始める。", "2006-03-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRNKENHOOKER", "フランケンフッカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "N.Y.42番街にたむろする理想的な娼婦のボディ・パーツをつなぎ合わせ、事故で死んだ恋人を復活させるという許されざる行為に走る、若きマッド・サイエンティスト、ジェフリーと彼が生み出した怪物の物語。鬼才ヘネンロッターが、伝説的な怪物フランケンシュタインの物語を下敷きに作り上げた異色ホラー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Celda 211", "プリズン211", "", "", "http://image.eiga.k-img.com/images/movie/55130/315x210.jpg?4721", 110, "刑務所の職員として働くことになったファンは、職場で思わぬアクシデントに見舞われ、気を失ってしまう。その直後、凶悪犯が収容されている監獄で暴動が発生し、気を失っていたファンは第211号監房に置き去りにされてしまう。ファンは、身を守るためにしばらく囚人として振る舞うことにするが……。「スペイン映画祭2009」のオープニング作品。", "2009-12-08", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ソウル・オブ・ロック", "ソウル・オブ・ロック", "", "", "http://image.eiga.k-img.com/images/movie/57530/315x210.jpg?4785", 70, "居酒屋と音楽スタジオを舞台に描く脱力系コメディで、「フィールヤング」（祥伝社）で連載されたノセクニコの同名コミックを実写映画化。売れないロックバンド「バーミヤンズ」でボーカルを担当するレイカは仕切り屋で強引だが、持ち前の明るさでバンドを引っ張っていた。ドラムのザキはマイペースな変わり者で、常に冷静な傍観者。ギターのタクは情熱的だがいつも空回りしてばかり。全く性格の異なる3人が居酒屋で夢を語り、時にスタジオでケンカもしながら、ロックへの愛と未来への夢を追いかけていく。「SEVENTEEN」専属モデルの有末麻祐子が映画初主演を飾る。", "2012-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デスノート", "デスノート", "", "", "http://image.eiga.k-img.com/images/movie/33420/315x210.jpg?4306", 126, "「このノートに名前を書かれた人間は死ぬ」。死神が落とした“死のノート＝デスノート”を拾ったのは、誰にも負けない頭脳を誇るエリート大学生・夜神月。彼は、理想の世界を築くため、自らの手で世界中の犯罪者を裁き始める。「週刊少年ジャンプ」での連載開始と同時に、圧倒的な人気を巻き起こしたカリスマ・コミックの実写版。主人公に藤原竜也、彼と対峙する名探偵Lに松山ケンイチと、若手演技派らが登場し壮絶な頭脳戦を繰り広げる。", "2006-06-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COOLANGATTA GOLD", "ルーカス・ルーカス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "過酷なオーストラリアン鉄人レース、クーランガッタ杯の優勝に執念を燃やす家族を描いた物語。鉄人レースにすべてを懸ける父ジョーと兄アダムのために、兄のペースメーカー役を務めてきたスティーブが自ら出場を決意。一人で訓練を開始する。", "1985-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Cider House Rules", "サイダーハウス・ルール", "", "", "http://image.eiga.k-img.com/images/movie/1830/315x210.jpg?3879", 126, "セント・クラウズの孤児院で生まれたホーマーは、父のように自分を育ててくれたラーチ院長の後を継ぐべく医術を学んでいた。しかし将来に疑問を抱き始めていた彼は、ある日若いカップル、キャンディとウォリーと共に孤児院を飛び出す。初めて見る外の世界、初めての外の仕事──ホーマーはリンゴ農園で働き、収穫人の宿舎“サイダーハウス”で暮らし始める。ほどなく軍人のウォリーは戦地へ召集され、残されたホーマーとキャンディは次第にお互いに惹かれていく。", "2000-07-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("演劇2", "演劇2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「選挙」「精神」の想田和弘監督が、劇作家の平田オリザと平田が主宰する劇団青年座を描くドキュメンタリー。2部作の後編。", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　WILD　ANGELS", "ワイルド・エンジェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "1960年代後半に大量生産されたバイカー・ムービーの代表的な作品。無軌道な暴走族エンジェルを率いるリーダー、ブルースは、警官に撃たれて重傷を負った相棒を連れ戻すため、病院へと潜入するが……。随所にサーフ・ミュージックなどのロックナンバーを挿入。P・フォンダが主人公ブルースに扮してニヒルな魅力を発揮した。ピーター・ボグダノヴィッチが助監督を務めている。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画監督になる方法", "映画監督になる方法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "“イチゴちゃん”の芸名でインディーズ映画界の人気者となった斉藤と、AV監督になったキタガワ。監督志望の旧友であった二人のもとに、斉藤主演によるAV作品の依頼が舞い込むが……。インディーズ・シーンで活躍する松梨智子が自身の体験を基に、監督志望の若者の夢と現実をユーモアたっぷりに描き出す。劇中にミュージカル風の歌やダンスを挿入するなど、松梨監督らしい意表を突いた演出がユニークだ。", "2006-04-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Sauvage", "うず潮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "　「シェルブールの雨傘」「８人の女たち」のカトリーヌ・ドヌーブと国際的スター、イブ・モンタンの初共演作。結婚に嫌気がさしたネリーは、式直前に逃げ出してしまう。程なく婚約者に見つかってしまうが、通りがかったマルタンに助けられる。文明生活を嫌い孤島に住む彼のもとにネリーが押し掛けたことから、2人の奇妙な共同生活が始まる。音楽は「華麗なる賭け」でアカデミー歌曲賞を獲得した、ミシェル・ルグラン。", "1975-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("John Carter", "ジョン・カーター", "", "", "http://image.eiga.k-img.com/images/movie/53180/315x210.jpg?4464", 133, "「ターザン」でも知られる小説家エドガー・ライス・バローズが1912年に発表したSFヒロイックファンタジーの古典的名作「火星のプリンセス」（創元SF文庫刊）を、「ファインディング・ニモ」「ウォーリー」のアンドリュー・スタントン監督が自身初の実写作品として映画化したアクションスペクタクル。1868年、アメリカ南北戦争で活躍したジョン・カーターは、ある出来事がきっかけで地球から遠く離れた滅びゆく神秘の惑星バルスームに瞬間移動してしまう。そこで出会ったサーク族の戦士タルス・タルカスやヘリウム国の王女デジャー・ソリスらと心を通わせたカーターは、バルスームの危機を救う戦いに身を投じていく。タイトルロールを演じるのは、「ウルヴァリン：X-MEN ZERO」の新鋭テイラー・キッチュ。美貌のヒロイン、デジャー・ソリスをリン・コリンズが演じる。", "2012-04-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GUIGNOLO", "ジャン・ポール・ベルモンドの　道化師 ドロボー・ピエロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "刑務所から出所してきたばかりの泥棒アレキサンドルは、さっそくいつもの悪い癖で新しい獲物を探した。アタッシュ・ケースを奪ったのはいいが、これが原因で追いつ追われつの大混戦が始まって……。アクション以外にコメディ作品でも味を見せるベルモンド面目躍如の娯楽作品。", "1980-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NON C’E PACE TRA GLI ULIVI", "オリーヴの下に平和はない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "第二次世界大戦で捕虜となり、ようやく故郷へと帰り着くことができたフランチェスコ。ところが帰ってみると、飼っていた家畜どころか婚約者までも、村の実力者に横取りされてしまっていた。怒るフランチェスコは復讐を誓う……。一人の男のすさまじいまでの復讐劇を描いた壮絶なアクション編。R・バローネの熱演も見ものだ。", "1950-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Unborn", "アンボーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "", "2009-01-01", "1", 0, NOW(), NOW());
