INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rabia", "激情", "", "", "http://image.eiga.k-img.com/images/movie/54960/315x210.jpg?4581", 95, "「タブロイド」のメキシコ人監督セバスチャン・コルデロによるサスペンス・ラブストーリー。建設作業員として働く移民の青年ホセは、現場監督を口論の末に殺害してしまう。恋人のロサが住み込みで家政婦をしている老夫婦の屋敷に逃げ込んだホセは、荒れ果てた屋根裏部屋に隠れ、ロサの様子を観察しながらひっそりと暮らし始める。「パンズ・ラビリンス」の鬼才ギレルモ・デル・トロがプロデューサーとして参加。", "2009-10-18", "9,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イサム・カタヤマ＝アルチザナル・ライフ", "イサム・カタヤマ＝アルチザナル・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/54570/315x210.jpg?4011", 96, "世界的に有名なレザー・ブランド「BLACKLASH」のオーナー兼デザイナーの片山勇のドキュメンタリー。音楽ドキュメンタリーを数多く手がける監督の牧野耕一は、一切の妥協を許さない職人・片山に1年以上密着し、片山がレザー・ファッション界に進むきっかけとなった父親との関係、ブランドに対する熱い想いなどに迫る。主題歌をビジュアル系ロックバンドのGLAYが手がける。", "2009-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドライ夫人と亭主関白", "ドライ夫人と亭主関白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "歌手だった近江俊郎が監督、脚本出演など、５役を務めた歌謡ショー乗りのキャバレー・コメディ。女房に頭のあがらない亭主たちは、グチをいいながらの毎日を過ごしていた。そんなある日、洋装店店主、金助に莫大な遺産が入ると知らせがあった。前祝いに亭主らはキャバレーへと向かうが、そこでヤクザに絡まれてしまう。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワイルド・フラワーズ", "ワイルド・フラワーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "「木更津キャッツアイ・日本シリーズ」などで人気の岡田義徳が主演した異色の青春ムービー。潰れかけた女子プロレス団体の生き残り合戦を軸に、岡田扮する若き社長と所属レスラーたちの奮闘が、コミカルに描かれる。これまでのプロレス映画とは比較にならない迫力あるリアルな格闘シーンが見もので、キューティー鈴木などの豪華ゲストも登場する。落ちぶれた女子プロレス団体“ガリンペイロ”の女社長がこの世を去る。残された女子レスラーたちは、遺書に記された社長の息子・伸一を後継者にしようとするが、医者を目指して医大で研修中の伸一は、聞く耳を持たず……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Mirror Has Two Faces", "マンハッタン・ラプソディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "「サウス・キャロライナ　愛と追憶の彼方」に続くB・ストライサンドのロマンティックな監督第３作。彼女のお相手を務める大学教授役のJ・ブリッジスのコミカルな演技もさることながら、母親役のL・バコールが貫禄の存在感を発揮し、ゴールデン・グローブ賞の助演女優賞に輝いた。コロンビア大学の数学教授グレゴリーの悩みは美人を見ると、めまいがして気分が悪くなること。かつての不幸な女性経験がたたって真っ当な恋愛ができなくなっている彼は、セックス抜きのプラトニックな愛を求め、新聞広告に恋人募集の告知を出す。ある日、同じ大学の文学教授ローズから告知の返事が返ってきた。実はそれは、ローズの妹が行き遅れそうな姉を心配して発送したものだった。そうとは知らず、早速、彼女の授業を見学にいったグレゴリーは、彼女とならうまく付き合えると確信し、デートに誘うことに成功。最初は戸惑ったものの、グレゴリーの優しさに惹かれていったローズは、ついに彼のプロポーズを受けるのだが……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCARAMOUCHE", "血闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "サバティーニの著名な原作を映画化した伝記時代劇。フランス革命の頃、匿名人の仕送りで日々を過ごすモロウは、その仕送り主が父と判明するに及んで、親友の革命家フィリップとともに父の家を訪ねる。しかし、父はすでに亡くなっていた。加えて決闘狂の貴族にフィリップの正体が見破られ、その邪剣の前に親友が倒れる。復讐を誓った彼は、身を道化芝居の一座に投じ、道化役者スカラムーシュに扮して舞台に立つかたわら、ひそかに剣技を磨くのだった。ビデオ、DVDタイトルは「スカラムーシュ」。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRUBAKER", "ブルベイカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "賄賂と暴力の横行する刑務所に、囚人ブルベイカーが送り込まれた。実は彼は、所内の実態を知るために潜入した新任の所長だった……。「暴力脱獄」で権力の不気味さを余韻に残した監督が、実話をもとに、腐敗しきった刑務所の権力の不正を暴き出した佳作。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東宮西宮／EAST　PALACE　WEST　PALACE", "東宮西宮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「北京バスターズ」「広場」などの問題作を発表し、中国当局の物議を醸しているチャン・ユアン監督のハードな人間ドラマ。若いゲイの作家アランが、警官にキスしたため公園内の派出所に連行される。そこで警官の激しい尋問を受けるアランは、少年時代からの生い立ちを語り明かしていく……。主要登場人物はわずか二人という一夜の物語。尋問シーンと回想場面で構成される映画は、いかなる暴力や侮辱にも屈しない主人公の“自由への闘い”を、異様なまでの緊迫感で映し出していく。タイトルは、宮殿の両脇に建つ二つのトイレを意味し、同時にゲイのコミュニティを指す中国の隠語だという。ビデオ発売時タイトルは「インペリアル・パレス」。", "1997-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Strings", "ストリングス　愛と絆の旅路", "", "", "http://image.eiga.k-img.com/images/movie/33960/315x210.jpg?4261", 93, "デンマーク生まれのドール・ムービー「STRINGS」を、「新世紀エヴァンゲリオン」の庵野秀明監督と、映画や舞台で俳優としても活躍する長塚圭史の脚色で新たに演出し直した日本版。すべての生き物から天井に糸が伸びているマリオネットの世界を舞台に、父の仇を討つために旅立った王子が、ある女性と恋に落ちたことから平和への願いに目覚めていく姿を描く。声優陣には草なぎ剛、中谷美紀ほか豪華キャストが集結。", "2007-04-28", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hotel for Dogs", "ホテル・バディーズ　ワンちゃん救出大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOMBRE", "太陽の中の対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "E・レナードの小説『オンブレ』をもとにした異色西部劇。インディアンに育てられた白人青年が、公金を横領した悪玉と同じ駅馬車に乗り合わせたために強盗に襲われる……。「暴行」など、ウエスタンに心理ドラマを持ち込んだM・リット監督、P・ニューマン主演のコンビ第３作。撮影をハリウッド古参のカメラマン、J・W・ハウが担当している。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅蝙蝠", "紅蝙蝠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "松竹が当時、“七人若衆”シリーズで花ノ元寿らとともに売り出そうとした７人のリーダー格・松本錦四郎主演作。幼なじみの姫・おいちが、家老の息子・帯刀と婚約したのを苦に脱藩した戸並長八郎。彼は家老と帯刀の陰謀を知り、藩と姫を救うため大活躍をする。引退近くの丸根演出には、残念ながら往年の才気が感じられない。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くっつきぼし", "くっつきぼし", "", "", "http://image.eiga.k-img.com/images/movie/58110/315x210.jpg?3464", 0, "アニメーション作家の石川直哉が監督、脚本、アニメ制作の全工程を1人で手がけ、2年8カ月をかけて完成させた個人制作アニメ。女の子同士の少し過激で危うい関係を描き、ニコニコ動画では再生回数88万回を記録した。女子高に通うキーコは、離れたところにあるものを少しだけ動かせる不思議な力をもち、妄想癖のある17歳。高校2年生の春、同じクラスに成績優秀で少し強引な性格のアーヤが転入してくる。2人は一緒に夏休みを過ごすが……。2010年に前編がDVD発売され、12年に後編のDVDが発売。それにあわせて前後編をまとめてイベント上映。", "2012-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("てなもんや幽霊道中", "てなもんや幽霊道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "百万石の大藩・加賀美藩城下にやって来たあんかけの時次郎と学僧・珍念の二人は、腰元・雪枝から加賀美藩のお家騒動を聞き、行方不明のまゆみ姫を探しに雪枝とともに旅立つ。やがて家老・大杉源蔵一味に捕えられていたまゆみ姫を助け、二人はまた旅立つのだった……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Poseidon", "ポセイドン", "", "", "http://image.eiga.k-img.com/images/movie/1150/315x210.jpg?3213", 98, "登場人物はオリジナル作とは別物で、消防隊員経験のある元NY市長をカート・ラッセル、その娘を「オペラ座の怪人」のエミー・ロッサム、元海兵隊員のギャンブラーを「ステルス」のジョシュ・ルーカス、ゲイの建築家をリチャード・ドレイファスが演じる。ただしオリジナル同様に少年は登場し、「ファイヤー・ウォール」でハリソン・フォードの息子を演じたジミー・ベネットが扮する。", "2007-06-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT THING YOU DO！", "すべてをあなたに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "人気、実力ともにハリウッドNo.1スター、T・ハンクスの初監督作品。1964年、ペンシルバニア州の小さな町、エリー。陸軍を除隊したガイは、ジャズが好きでドラムを叩いている時が幸せだった。ある時、仲の良いバンドのメンバーからケガをしたドラマーの代役を頼まれ、州立大学のコンテストに出場し、優勝する。バンドは町のライヴハウスでも演奏し、人気者に。自信をつけたバンドはレコードを自主製作するが、それも好評で、レコード会社のミスター・ホワイトに見込まれる。プロ・デビューしたバンドは、レコードもヒットチャートをにぎわすようになる。全国的な人気者になったバンドだったが……。使われる曲はすべてオリジナルで、T・ハンクスも数曲作詞作曲をしている。また、ハンクス夫人であるR・ウィルソンが、ジャズ・クラブのウエイトレス役で出演している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちいさな恋のものがたり", "ちいさな恋のものがたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "東京・渋谷を舞台に、それぞれの悩みを抱えて生きる若者たちの姿を、小野真弓、田中幸太朗、宮地真緒、波岡一喜ら若手キャスト共演で描いた青春群像ドラマ。母を亡くした有希は記憶にない父を探すため上京するが、東京駅で財布と父の写真をなくしてしまう。たったひとり東京で生きていくことになった有希は、バンドデビューを目指す良太や、良太のバンド仲間の美沙、ケンカしてバンドを脱退した修らと出会い、心を通わせていく。", "2008-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOTORIOUS", "汚名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "南米に逃れたナチの大物を追うFBI捜査官と、売国奴の汚名を着せられた父を持つ娘アリシアの虚々実々の駆け引きが展開される。サスペンスよりも、二人のメロドラマ風なシークエンスが強調された、ヒッチコック作品のなかでも異色なドラマ。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NARCO DOLLAR", "麻薬捜査官 バニシング・サンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "麻薬捜査官、マーク・スティーブンスは、コロンビアの麻薬王モルテラを追い詰めるため、自ら逮捕した密売人エステバン（実はキューバの反政府ゲリラ）をおとりに使う。捜査が進むうちに二人に芽ばえる連帯感……。やがて二人は、モルテラの本拠に乗り込み、100人を超す敵に戦いを挑む。ハードな銃撃戦が見もののアクション。", "1990-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マイフェニックス", "マイフェニックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "アメフトに青春をかける男たちと、芸術学部写真学科に籍を置き、卒業制作のテーマにアメフトを選んだ女子大生との交流を軸に、就職や恋に悩む若者たちの青春群像を描き出した作品。大下英治の『小説・日本大学』をベースに、日本大学創立100周年記念として製作された。タイトルは日大アメフトチーム名からとっている。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Store", "ストア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Good Luck Chuck", "噂のアゲメンに恋をした！", "", "", "http://image.eiga.k-img.com/images/movie/34220/315x210.jpg?1580", 99, "30代になっても理想の女性とめぐり逢えずにいる歯科医のチャーリーは、元恋人の結婚パーティで出会ったキャムにひと目惚れする。それと同時に、自分が女性たちから「彼と寝ると、次に運命の男性が現われる」と噂されていることを知ってしまう。キャムとの関係を進展させたいチャーリーだったが、1度限りの関係を求める美女に次々と迫られ……。ジェシカ・アルバが等身大のヒロインを魅力たっぷりに演じ、新境地を開いた。", "2008-05-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トラック野郎・御意見無用", "トラック野郎・御意見無用", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "長距離トラック運転手のコンビが繰り広げる笑いと涙の珍騒動。東北のドライブ・インのウェイトレス・洋子に一目惚れの桃次郎。彼に思いを寄せる同業の未亡人運転手・モナリザのお京の方には見向きもしない。捨て子を抱えるハメになった桃次郎は洋子も入れて３人で“ねぶた祭り”に出かけ、すっかり夫婦気分。しかし洋子の婚約者の存在を知った桃次郎は彼女をトラックに乗せ、漁船で出航する男のもとへ間一髪送り届ける。ヤクザ映画のスター・菅原が新境地を切り開いた。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サザエさん", "サザエさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "サザエさん一家は弟のカツオ、妹のワカメとお父さん、お母さんの５人暮らし。長女のサザエさんは高校を卒業してもおてんばぶりを発揮している。やがて彼女が雑誌社“女性クラブ”に入社して……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Yellow Handkerchief", "イエロー・ハンカチーフ", "", "", "http://image.eiga.k-img.com/images/movie/53540/315x210.jpg?3308", 0, "山田洋次監督の「幸福の黄色いハンカチ」（77）をハリウッド映画としてリメイク。主演はウィリアム・ハート、クリステン・スチュワート、エディ・レッドメイン。アメリカ南部、憂うつな日々を送る少女マーティーンは、風変わりな青年ゴーディにドライブへ誘われ、流されるまま旅に出る。やがて2人はミシシッピ川のほとりでひとりたたずむ中年男性ブレットに出会い、旅路をともにすることになる。6年の刑期を終えたばかりのブレットは、「ニューオーリンズで確かめたいことがある」と、過去を語りだす。", "2010-06-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コトバのない冬", "コトバのない冬", "", "", "http://image.eiga.k-img.com/images/movie/55140/315x210.jpg?1255", 94, "北海道の小さな町で、父親と単調だが平穏な日々を暮らす冬沙子は、ひょんなことから言葉の話せない男と出会う。男との出会いで、これまでの人生になかった感情が芽生えたことに驚く冬沙子。しかし、仕事先の牧場で落馬事故を起こし、目覚めた冬沙子は記憶を少し失ってしまっていた。俳優・渡部篤朗が、高岡早紀を主演に初の長編作品の監督を務める。", "2010-02-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAMPIONS", "チャンピオンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "花形騎手ボブ・チャンピオンの波乱の半生を映画化した伝記映画。人気の絶頂期にガンの宣告を受けたボブと、同じく骨折事故で廃馬寸前となった名馬が、再びコンビを組み奇跡的なカムバックを遂げる。演技派ハートのデリケートな名演が光る感動作。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Acoustic", "アコースティック", "", "", "http://image.eiga.k-img.com/images/movie/57660/315x210.jpg?3386", 88, "「CNBLUE」のイ・ジョンヒョン、カン・ミンヒョク、「2AM」のイム・スロンら人気K-POPアーティストも出演し、音楽を通じて夢や希望を見出していく若者たちの姿を描いた青春ドラマ。不治の病に侵されたシンガーソングライターのセギョンは、自分の歌を世に伝えようと病院を抜け出す。一方、とあるライブハウスで演奏するバンド「タージマハル」は、客に全く受けずに店から追い出されてしまう。それから30年後、音楽が消えてしまった世界で、音の研究をする大学生ジフは風変わりな少女ジニと出会い……。", "2012-02-04", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おと・な・り", "おと・な・り", "", "", "http://image.eiga.k-img.com/images/movie/54190/315x210.jpg?3460", 0, "岡田准一と麻生久美子の主演による、30歳の男女を描いた等身大のラブストーリー。監督は「ニライカナイからの手紙」の熊澤尚人。進むべき道に迷うカメラマンの聡と、フラワーデザイナーを目指しフランス留学を控える七緒は、都会の古アパートに暮らす隣人同士。お互いに顔を合わせたこともなかった2人だが、壁越しに聞こえる生活音で次第に心を通わすようになる。", "2009-05-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・極道渡世の素敵な面々女になった覚えはねぇ", "新・極道渡世の素敵な面々女になった覚えはねぇ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "安部譲二の書き下ろしによるオリジナル・ストーリーを、「Morocco 横浜愚連隊物語」や「修羅がゆく」など、アウトローたちの生き様を描いて定評のある和泉聖治が映画化した、哀感あふれる任侠アクション。前科者の弘は南という女と出会い、結婚を決意。彼女の兄であるヤクザの代貸・石川にあいさつに行くが、その腕っぷしの強さに惚れ込んで舎弟にしてもらうことに。だが、二人の所属する組は借金返済に窮しており、そのうえ中国マフィアの魔の手が伸びてくる……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹下左膳", "丹下左膳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "好漢・大友柳太朗が、明るく豪快な丹下左膳を演じる。百万両の秘密を封じ込んだこけ猿の壷をめぐって、柳生源三郎と丹下左膳そして大岡越前守が争奪戦を展開する。左膳スターの大河内伝次郎が客演。ビデオタイトルは「丹下左膳　決定版」。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lady Vanishes", "バルカン超特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "　列車という密室を舞台に、主人公の男女が孤立無援に陥る心理サスペンス。ロンドン行きの列車に乗り込んだアイリスは、ミス・フロイという老女と知り合う。しかし一眠りしたあと気がつくと彼女の姿がこつ然と消え、乗客のみんなが存在を否定する。魔術師や尼層、脳外科医など疑わしい連中ばかりの四面楚歌の状況下、1人の青年と共に彼女の捜索を始めるが……。イギリス時代を代表するヒッチコックの傑作で、公開当時批評家たちから絶賛を浴びた。", "1976-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使突抜六丁目", "天使突抜六丁目", "", "", "http://image.eiga.k-img.com/images/movie/56950/315x210.jpg?4694", 96, "「つぶろの殻」で2004年ぴあフィルムフェスティバル観客賞を受賞し、商業作品では「ひとりかくれんぼ　劇場版」などを手がけてきた山田雅史監督の劇場公開長編3作目。会社が倒産し追われる身となった昇は、見知らぬ街「天使突抜六丁目」に迷い込む。街の不思議な住人たちと過ごしていたある日、いつか背中に羽が生えて街から飛び立つことを夢みている女みゆきと出会った昇は、みゆきの奔放な魅力にひかれていく。しかし、みゆきが自身の夫を撲殺する事件に直面したことをきっかけに、平穏な日常がゆがみはじめ……。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Spirit of St.Louis", "翼よ！あれが巴里の灯だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "　1927年大西洋無着陸横断飛行に成功したリンドバーグの偉業を、彼の友人でもあったビリー・ワイルダーが映画化。エピソードは自伝に忠実に描かれたため制約が多く、なかでも単独飛行であるがゆえに、当然飛行中のシーンはリンドバーグ独り。それを回想シーンとハエという同乗者をさりげなく盛り込むことで、ワイルダーらしい伝記物に仕上がった。空の英雄を演じたのは、後にアカデミー名誉賞を受賞したジェームズ・スチュアート。", "1957-11-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ARBALETE", "アスファルト・ウォリアーズ 鮮血ストリート殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "パリの街では麻薬をめぐって、３組のストリート・ギャングが生死を懸けた抗争を展開していた。この抗争を鎮圧し平和を取り戻すため雇われた二人の探偵が、ギャングたちと死闘を繰り広げるバイオレンス・アクション。探偵サスペンスの面白さを加味したネオ・ハードボイルドで、派手なファイティング・シーンが興奮度満点。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONDEMNED", "曳かれ行く男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "トーキー初期の作品。熱帯のリゾート地と見まごうような悪魔島にある流刑地で繰り広げられる、囚人と刑務所長の美しい妻との禁じられた愛の物語。妻の不貞を知った所長は浮気相手を独房に閉じ込めるが、脱獄されてしまう。やがて彼は再び島に戻り、刑期を果たすが、所長の妻との愛の行方は……。", "1929-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zazie Dans Le Metro", "地下鉄のザジ　完全修復ニュープリント版", "", "", "http://image.eiga.k-img.com/images/movie/54640/315x210.jpg?3467", 93, "「死刑台のエレベーター」など多くの名作を遺したフランスの巨匠ルイ・マルが1960年に手掛けたスラップスティック・コメディを、完全修復ニュープリント版でリバイバル上映。パリで暮らすガブリエル叔父さんの元に預けられた少女ザジ。地下鉄に乗ることを楽しみにしていた彼女は、叔父さんの家を抜け出してパリの街へと繰り出すが、地下鉄はストで運休しており……。60年代パリの街角の風景やファッションにも注目。", "2009-09-26", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GROUND ZERO", "グランド・ゼロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "CFカメラマン、ハービーの身辺にある日、奇妙なことが起き始める。その原因は、30年前に死んだ父の死に関係があるらしいことを突き止めた彼は、父の本当の死因を探る。実はカメラマンだった彼の父は、30年前の核実験の模様を撮影していて……。1952年、イギリスがオーストラリアで行った核実験を題材にしたサスペンス。", "1988-01-01", "1,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHASE", "ザ・チェイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "銀行強盗の濡れ衣を着せられたジャックは、脱走し、富豪の娘を人質に盗難車でハイウェイを疾走する。追いかけるパトカー軍団。そのカーチェイスはTV各局により全国放送される。全米中が見守るその追跡劇の結末は？　大仕掛けなカースタントがたたみかけるように全編に展開する豪快カー・アクション。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白髪魔女傅", "白髪魔女伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "戦乱の中国・明朝末期、互いに闘う宿命を背負った、武術家と魔族の女刺客との愛と葛藤を描く伝奇ロマンアクション。中原八大門で武道を学んだ青年・一航は、邪教集団と闘っていた。ある日彼は、森で謎めいた美女・狼女と出会い恋に落ちる。しかし戦いの中、一航の前に刺客として立ちはだかったのは、あの狼女であった……。レスリー・チャンをはじめ、香港映画界を代表する人気スターと一流スタッフが結集したヒット作。", "1994-11-30", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TigerShark", "虎鮫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "鮪漁の一大基地サンディエゴ港は人食い鮫の巣窟でもあった。サンタ・マリア号の船長マイクも遭難した際、左手を鮫に食われてしまう。カギ爪をつけて漁を続けるマイクは鮪の捕獲記録は更新し続けているが、女には縁がなくなった。ある日、鮫の餌食となった乗組員の家を訪ねたマイクはその娘を世話することになる。そしてマイクは娘に結婚を申し込み……。映画界における「何でもこなす商業監督」としてのホークスの評価を高めた一作。", "1933-01-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("婚約三羽烏", "婚約三羽烏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "昭和10年代の就職難の中、三羽烏はそれぞれ巧みな知恵を使って銀座の織物会社に入社した。社長令嬢は飛び切りの美人。３人の婿の座を賭けたレースが始まるが……。1956年に杉江敏男監督（東宝）でリメイクもされたサラリーマン喜劇。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATAL BOND", "硝子のパートナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "町から町へ車をとばし旅を続けるジョーは、ある田舎町でリオーニという女性と出会う。意気投合した二人は、ジョーの兄が住む海辺の町へ向かった。だが途中、ジョーと関係のあった少女が殺される事件が起こり、少女の父親がジョーを狙う。リオーニは、ジョーを疑い始めるが……。恋人に不審を抱きながらも一途に愛を深めていくヒロインをL・ブレアが演じる。またメル・ギブソンの実弟D・ギブソンが、ハードな悪役で出演している。", "1991-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PITCH　BLACK", "ピッチブラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「アライバル／侵略者」のD・トゥーヒーがアイザック・アシモフのSF小説『夜来たる』にインスピレーションを受けて描いたSFアクション。闇に覆われた惑星を舞台に、未知の生命体と人間の死闘が展開する。宇宙船が砂漠の惑星に不時着、凶悪犯や女性副操縦士ら数名の乗客が生き残る。そこは夜が訪れない灼熱の星。だが、ある日22年に一度の皆既日食によって夜が訪れ、暗闇を好む謎の生物が動き出す！　闇の中に突如現れる宇宙人の襲撃と、互いの素性を知らない人々の間に生じる猜疑心が引き起こす二重のスリルが重なり、全編に異様な緊張感がみなぎる。ウヨウヨと現れるエイリアンの不気味な造形も強烈な印象を与える。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魂を握り潰した男", "魂を握り潰した男", "", "", "http://image.eiga.k-img.com/images/movie/57080/315x210.jpg?2805", 95, "脚本家・監督の前田万吉と俳優・幸将司が立ち上げた「劇団クラゲ荘」の同名舞台劇を、前田が自身初の長編監督作として映画化した青春ファンタジー。ビル警備の仕事に就いていた虎太郎は、屋上から飛び降りようとしていた青年・拓を引きとめようとして一緒に転落し、あの世にいってしまう。死者の集う「三途の国」で虎太郎は天国行きが決まるが、なぜか生前の自分を思い出せない。一方の拓は地獄行きが決まり、地獄行きの特訓を課せられる。そして現世では、残された虎太郎の妻・さくらや拓の恋人・由紀らもそれぞれ悲嘆に暮れていたが……。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪人二十面相・三部作", "怪人二十面相・三部作", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "昭和30年代、江戸川乱歩の“少年探偵団”ものは、東映によって次々に映画になったが、これはその前身ともいえる松竹版。「第１部・人か魔か？」「第２部・巨人対怪人」「第３部・怪盗爆砕」と３部からなる連続もので、この年の12月、３週に分けて公開された。物語は、少年探偵団団員の一人、荘二の父・羽柴博士が完成した、原子炉の秘密設計図をめぐって、怪人二十面相と名探偵・明智小五郎が攻防を繰り広げるというもの。翌年、同じ若杉英二の明智小五郎で「青銅の魔人」４部作も作られた。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロストクライム　閃光", "ロストクライム　閃光", "", "", "http://image.eiga.k-img.com/images/movie/55160/315x210.jpg?3022", 0, "1968年に起こった未解決窃盗事件「三億円事件」の真相に迫るサスペンス。「女囚さそり」シリーズの生みの親・伊藤俊也監督がメガホンをとり、「ラストゲーム　最後の早慶戦」の渡辺大とベテラン俳優の奥田瑛ニが熱血刑事コンビを演じる。出世を夢見る若手刑事の片桐は、ある殺人事件をきっかけに定年間際のベテラン刑事・滝口とコンビを組まされる。滝口の破天荒な捜査に反発する片桐だったが、被害者が「三億円事件」の犯行グループの1人だと知らされ衝撃を受ける。", "2010-07-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FLANAGAN BOY", "ブロンドの虜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ジョニー・フラナガンはアメリカの富豪ベッチにボクサーとしての素質を見込まれスポンサーについてもらう。ジョニーは、ベッチの屋敷でトレーニングを始めるが、ベッチの妻ローナの誘惑に負け、深みへはまってしまう。ローナはベッチに嘘の妊娠を告げ、そのため彼は試合にも負けてしまった。さらに夫の財産を狙うローナは、ジョニーに夫殺害を実行させた。良心の呵責に耐えかねたジョニーは自首しようとするが……。「アパッチ騎兵隊」のR・ルボルグ監督は、典型的な職人気質の監督であり、その作品のテンポは、観る者に安心感を与える。", "1953-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Images", "ロバート・アルトマンのイメージズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "　キャスリンは夫ヒューとともにのどかな田園地帯に移り住む。しかしそこに死んだはずのかつての愛人レネが出現。さらに、やはりキャサリンと関係のあったマルセルが娘スザンナをつれて訪ねてくる。現実とも幻覚ともつかない事態に混乱するキャスリン。ついにはもうひとりの自分まで現れて……。次第に正気を失っていく女性の心理をミステリアスに綴る。ツトム・ヤマシタによる音響や主演のスザンヌ・ヨークが書いた童話が効果的に使われている。", "1972-01-01", "3,33,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・テンプターズ／涙のあとに微笑みを", "ザ・テンプターズ／涙のあとに微笑みを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ザ・スパイダース、ザ・タイガースと並ぶ人気GS、ザ・テンプターズの主演作品。高校生役のショーケンこと萩原健一とそのバンド仲間や美少女・美香たちとの交流に加え、母親と友人の父の結婚話がからんでいく。いかにもザ・テンプターズらしく哀愁のある作品に仕上がっている。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CINICO　TV", "皮肉テレビ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "イタリア、シチリア島のローカルTVで人気を博している、お笑い番組『皮肉テレビ』の劇場用編集版。人間がコンドームになってインタビューを受ける「ジョルダーノ、コンドームになる」をはじめ、過激でシュールな笑いに満ちたエピソード満載。出演者のほとんどがシチリアの浮浪者だったという、フリークショー的な作りにもスゴみがある。", "1991-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("なにわ忠臣蔵", "なにわ忠臣蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "おなじみの「忠臣蔵」を現代のヤクザ世界に置き換えて描く任侠アクション。大阪天王寺を仕切る浅野組の若き二代目組長が射殺される。残された組員たちは敵対する吉良組に復讐を開始しようとするが、元若頭の大石は彼らを抑えるのだった。大石をはじめ、組員たちは馴れない堅気の生活を送り、ひそかに復讐の機会を待つことに。そして１年後、組長の仇を討つため大石は組員たちに招集をかける。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠くの空に消えた", "遠くの空に消えた", "", "", "http://image.eiga.k-img.com/images/movie/34050/315x210.jpg?4009", 144, "「世界の中心で、愛をさけぶ」「北の零年」の行定勲監督が、自らの手によるオリジナル・ストーリーを映画化。空港建設計画に揺れるのどかな田舎町に引越して来た空港公団団長の息子・亮介は、地元の悪ガキ・公平や、父親がUFOに連れ去られたと信じる少女・ヒハルと仲良くなる。やがて空港を巡る争いが子供たちをも巻き込み始め……。主演には「妖怪大戦争」の神木隆之介や「SAYURI」の大後寿々花ら注目の子役が集結。", "2007-08-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("流星〈りゅうせい〉", "流星〈りゅうせい〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "年金生活のかたわら、競馬に没頭する孤独な老人・菊次郎が地元の名馬リュウセイ号を“誘拐”してしまう。チンピラの川崎、美少女ひとみを加えた彼ら３人は、リュウセイの馬主に身代金を要求するが、ことごとく失態を連発してしまい……。若手監督４人がデビューした“リトル・モア”シリーズを締め括った第４作。冴えない人生を送る老人を、緒形拳が頭を金髪に染めて味わい深く演じ、山仲浩充監督がオフビートなユーモア演出を披露。一世一代の“大穴”を狙う男女の姿が、情けなくも胸のすくロマンを感じさせる。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MINUTE DE VE´RITE´", "愛情の瞬間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "医師ピエールはガス自殺で死亡した青年の部屋で１枚の写真を見つける。そこには青年とピエールの妻が一緒に写っていた。ピエールが妻にそのことを問い詰めると、妻は青年との不倫の日々を告白し始める……。J・ドラノワお得意の恋愛心理もの。", "1952-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("栄光への挑戦", "栄光への挑戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "裕次郎が、元ボクサーを演じたアクション。ボクシングで稼いだ資本をもとに遊興娯楽の事業に乗り出した宗吾郎。彼は、事業発展を邪魔する暴力団・野沢会に片腕・長田を殺され、営業停止に追い込まれる。すっかり意気消沈する吾郎だったが、恋人・紀子に励まされ、彼は野沢会に復讐を遂げる。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NADINE ： A BULLET PROOF LOVE", "消えたセクシーショット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "離婚寸前にあるバーノンとナディーン。若気のいたりで撮ったヌード写真のネガを取り返そうと写真屋へ行ったナディーンは、町を巻き込む陰謀に陥れられ、離婚どころの騒ぎではなくなる。夫婦間の危機をサスペンスフルに描いたベントン監督作。ビデオ発売時タイトルは「ナディーン／消えたセクシーショット」。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sisters", "悪魔のシスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "　美しいモデルのダニエルと一夜を過ごした男性が殺害される。その現場を真向いのマンションから目撃した記者グレースは探偵ジョセフとともに事件解明に乗り出す。やがてグレースたちはダニエルをめぐる秘密を知ることに。ダニエルは体がつながったまま生まれたシャム双生児だった。彼女には、切り離し手術後に死んだ双子の片割れの意識が乗り移っていたのだ……。ヒッチコックへのオマージュも色濃いブライアン・デ・パルマ監督のサスペンス・スリラー。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地球防衛軍", "地球防衛軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "本多猪四郎監督と円谷英二特技監督の名コンビによる東宝初の本格的SF映画。地球征服をもくろむ宇宙人ミステリアンは、前線基地として富士山麓に巨大な半球形の白色ドームを建設するが、地球側も地球防衛軍を組織して対抗する。ミステリアンのドームの円盤、地球防衛軍の数々の科学兵器の大攻防戦が繰り広げられる。東宝はすでに「ゴジラ」の成功で特撮映画の王道を走っていたが、「禁断の惑星」や「宇宙水爆戦」といったアメリカ製の本格的SF映画に刺激され、総天然色シネマスコープによる空想科学映画として製作された。この映画が公開された年、国産ロケット第１号の打ち上げが成功し、ソ連のスプートニク１号が地球の周回軌道に乗り、夢物語であった宇宙時代が幕を開けた。小松崎茂デザインによる超科学兵器が次々と惜しげもなく登場し、円谷英二の乗りに乗った特撮シーンのみだれ打ちは圧倒的な迫力で、平凡なドラマ部分の不備を補って余りあった。「ゴジラ」とともに日本SF映画史上のターニング・ポイントとなった秀作である。伊福部昭が音楽を担当している。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人質　メディアは何を伝えたのか", "人質　メディアは何を伝えたのか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "2004年4月、イラクの反米組織が3人の日本人を拘束し、日本政府に自衛隊撤退を要求した。人質となった3人は9日間の拘束の末に無事解放されたが、日本メディアは被害者たちの自己責任論や被害者家族をも巻きこんだバッシングで報道合戦を繰りひろげた。そんなメディアのあり方に疑問を感じた吉岡逸夫監督が、人質となった郡山総一郎氏と安田純平氏にインタビューを行ない、事件の真相を明らかにするドキュメンタリー。", "2005-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あたえられるか否か　徳川埋蔵金120年目の挑戦", "あたえられるか否か　徳川埋蔵金120年目の挑戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "江戸時代末期、徳川幕府再興のため、大老・井伊直弼の命で群馬県赤城山中に埋められたといわれる幕府御用金360万両。時価数十兆円とされるこの埋蔵金を、明治16年から120年間、祖父子3代にわたって探し続けている水野家の活動にスポットを当てたドキュメンタリー。現在の当主・水野智之の埋蔵金発掘事業に3年間密着し、埋蔵金を掘り続けなければならない理由とその意味を解き明かしていく。", "2006-09-09", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラミラクルラブストーリー", "ウルトラミラクルラブストーリー", "", "", "http://image.eiga.k-img.com/images/movie/54070/315x210.jpg?1842", 120, "青森で農業を営みながら1人で暮らす無邪気な青年・陽人は、野菜を売りに行った先の幼稚園で新任の町子先生に出会い、一目で恋に落ちる。陽人は町子に強引なアプローチを続けるが、町子が事故で亡くなった元カレの首を見つけるために、東京からカミサマと呼ばれる占い師に会いに青森までやって来たという噂を耳にする。「ジャーマン＋雨」で注目を浴びた青森出身の新鋭・横浜聡子が、同郷の松山ケンイチを主演に迎えて描くラブストーリー。", "2009-06-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("柳生武芸帳", "柳生武芸帳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "五味康祐の同名小説を、近衛十四郎の柳生十兵衛というはまり役で映画化した“柳生武芸帳”シリーズの第１作。柳生新陰流の秘伝と称され、天下を左右する恐怖の謎を秘めた水月・浮月の２巻の柳生武芸帳をめぐって、柳生十兵衛が次々と襲いかかる敵を切り捨てる。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　CALVI　AFFAIR／THE　BANKERS　OF　GOD", "法王の銀行家／ロベルト・カルヴィ暗殺事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "1982年に謎の死を遂げたイタリアの銀行家ロベルト・カルヴィをめぐる社会派サスペンス。様々な政治や宗教的な思惑が絡み合った、一大金融スキャンダルの真相をスリリングに描き出す。アンブロジアーノ銀行の頭取カルヴィは、ヴァチカン銀行総裁のマルチンクス大司教の庇護を受けていた。やがて違法の取引が発覚し、警察に追い詰められたカルヴィは何とか銀行を守ろうとするが、次第に孤立していく。", "2002-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("B-ON 美女木兄弟戦争篇", "B-ON 美女木兄弟戦争篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "不良男子高校生たちの戦いを描いた「ビーオン」シリーズ第2作。蘭城高校でトップを張る桜場らは、不動が去ったあともいつもどおりつるんでいた。そんなある日、鑑別所上がりの男・砂生未来が近隣の美女木高校に転入してきたことから、周囲の高校を巻き込んだ不良たちの抗争が勃発する。", "2010-04-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLARA　ET　MOI", "メトロで恋して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "俳優としてのキャリアも豊富なアルノー・ヴィアールの監督デビュー作。パリを舞台に、どこか愛を信じられない30代独身男性の恋愛観が、リアルに描かれる。難病に冒されたヒロインに扮するフランスの実力派女優、ジュリー・ガイエの好演が印象深い。地下鉄での運命的な出会いで恋に落ちたアントワーヌとクララ。しかし、クララが医者にHIVと診断されたのを機に、両者の関係に亀裂が入っていき……。", "2005-08-27", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タナカヒロシのすべて", "タナカヒロシのすべて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "その独特の風貌と過激な発言で人気を呼ぶお笑いパフォーマー、鳥肌実の映画初主演作。30過ぎの平凡な男を主人公に、現代の世相を反映させた鋭いブラックなユーモアの盛り込まれた物語が進展していく。今回が初監督となる新鋭、田中誠の独特のアイデアと視点を感じさせる脚本が秀逸。32歳の田中宏は、無趣味で恋人もいないものの、現状に満足していた。しかし両親の死を機に、彼の平凡な生活に暗雲がたちこめる……。", "2005-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Devil's Rejects", "デビルズ・リジェクト　マーダー・ライド・ショー2", "", "", "http://image.eiga.k-img.com/images/movie/33670/315x210.jpg?4663", 108, "ヘビー・ロック界の重鎮ロブ・ゾンビが初メガホンをとり話題となったホラー映画「マーダー・ライド・ショー」の続編。恐ろしい殺人鬼一家の逃避行を、全編ヘビー・ロックに乗せて描き出す。残虐な殺人を繰り返す一家を逮捕するべく、100人を超える警官隊が彼らを急襲。激しい銃撃戦の末、母親が逮捕されるが、兄と妹は逃亡する。自分の兄を一家に殺された保安官は、殺人を楽しみながら逃げ続ける一家を執拗に追跡するが……。", "2006-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CEZANNE", "セザンヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "ストローブとユイレのコンビ監督による、ユニーク極まりないドキュメンタリー・フィルム。原作者J・ギャスケが自分の記憶に基づいて構築したセザンヌの名言集にインスパイアーされたという異色作だ。実際にセザンヌの絵画を撮影しているが、その言葉の意味を多彩な象徴表現で探ろうとする点ではフィクション性もある。ジャン・ルノワール監督の「ボヴァリー夫人」からの引用も。", "1989-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD END RUN", "DEAD END RUN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 59, "鬼才・石井聰亙監督の「ELECTRIC DRAGON 80000V」以来２年ぶりの本作は、永瀬正敏、浅野忠信、伊勢谷友介を主演に据えた３本のオムニバス・ドラマ。「LAST SONG」「SHADOWS」「FLY」 と題された物語は、それぞれに何者かに追われる男が登場。緊迫の逃亡が繰り広げられる。逃亡者の内面までもあぶり出す、エッジの利いた映像と臨場感あふれる音響が鮮烈。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Taurus", "牡牛座　レーニンの肖像", "", "", "http://image.eiga.k-img.com/images/movie/53280/315x210.jpg?2157", 94, "「太陽」で昭和天皇ヒロヒトの苦悩を描いたアレクサンドル・ソクーロフ監督が、祖国ロシアにおいて史上初の社会主義政権を築いたレーニンの晩年に焦点を当て、権力者の人間的な側面を浮かび上がらせた問題作。モスクワ郊外の屋敷で外界から切り離された療養生活を送る52歳のレーニン。右半身が麻痺しているため体の自由はきかず、痴呆の症状も現われ始めている。スターリンが見舞いに訪れても、その名前さえ思い出すことができず……。", "2008-02-02", "24,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLASS", "恋のスクランブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "こともあろうに、親友の母親に恋してしまった高校生の甘くホロ苦い初体験レポート。A・マッカーシーとR・ロウが初共演した、さわやかな学園もの。舞台となる全寮生スクール独特のライフ・スタイルとファッションも楽しみの一つ。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日輪の遺産", "日輪の遺産", "", "", "http://image.eiga.k-img.com/images/movie/55500/315x210.jpg?3539", 134, "ベストセラー作家・浅田次郎の同名小説を、「半落ち」の佐々部清監督が堺雅人主演で映画化。昭和20年8月10日、帝国陸軍の真柴少佐は、陸軍が奪取した900億円ものマッカーサーの財宝を陸軍工場へ移送し隠匿（いんとく）せよと密命を受ける。その財宝は、敗戦を悟った軍上層部が祖国復興を託した軍資金だった。真柴は小泉中尉や望月曹長、そして勤労動員として呼集された20人の少女たちとともに任務を遂行するが……。", "2011-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("口笛を吹く無宿者", "口笛を吹く無宿者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "和人とアイヌが品物を交換する“交易会”の交渉のため、北海道に渡った月輪主膳・源太郎親子。だが約束を無視した上役の軍太夫によって“交易会”はアイヌたちに不利な条件で終始、アイヌたちは月輪たちが裏切ったと信じ込む。一方、軍太夫はアイヌに銃を売り、さらに彼らと一合戦をもくろみ、反乱の名目でアイヌを討伐しようとしていた。特異な設定で描く歴史ドラマ。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("行け！男子高校演劇部", "行け！男子高校演劇部", "", "", "http://image.eiga.k-img.com/images/movie/56100/315x210.jpg?4705", 85, "池松男子高校に入学した小笠原は、ふとした勘違いから廃部寸前の演劇部に入部。頼りない部員とアイドルオタクの顧問しかいないなか、なんとか残り2人の部員を確保し、廃部を免れる。そして演劇の見学に訪れた女子高で、ある美少女に一目ぼれした小笠原は、開催の迫る演劇祭へ向けてモチベーションを高めるのだが……。「TRICK」シリーズで知られる俳優・池田鉄洋の初脚本作を、「ハンサム★スーツ」の英勉監督が映画化した青春ストーリー。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PET SEMATARY", "ペット・セメタリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "モダンホラー小説の雄S・キングが、同名小説を自ら脚本化した作品。アメリカ・メイン州の小さな街ルドロー。トラックが行き交う道路沿いに引っ越して来たルイス一家は、新しい家に大喜び。だが数日後、ペットの猫が轢死。近くにあるペット・セメタリー（＝動物墓地）の奥の“禁断の場所”に埋めると、なんと翌日猫はよみがえった。日を置かずして、幼い息子ケイジがトラックにはねられ死亡。嘆き悲しむルイスは、遺体をこっそり例の場所に埋葬する……。愛する者を取り戻したいがために“タブー”を侵してしまったルイス。愛と哀しみに彩られた、戦慄のホラーである。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wanted", "ウォンテッド", "", "", "http://image.eiga.k-img.com/images/movie/53560/315x210.jpg?2569", 110, "マーク・ミラーのグラフィックノベルを、「ナイト・ウォッチ」のロシア人監督ティムール・ベクマンベトフが映画化したアクション大作。冴えない日常を送っていたウェスリーの前に突然現れた、謎の暗殺組織“フラタニティ”の一員という美女フォックス。彼女によれば、組織の優秀な暗殺者だったウェスリーの父が裏切り者に殺され、その魔の手がウェスリーにも迫っているという……。主演は「つぐない」のジェームズ・マカボイ。共演にアンジェリーナ・ジョリー、モーガン・フリーマンら。", "2008-09-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勝手にしやがれ!! 黄金計画", "勝手にしやがれ!! 黄金計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ある老人を探して欲しいとの依頼を受けた雄次と耕作。ところが、せっかく老人ホームで発見したその老人・波多野は心臓発作を起こして死んでしまう。やがて、彼の孫と名乗る女、律子が登場。どうやら彼女の目的は10年前、波多野が銀行から強奪した5000万円なのだが……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死づえ　呪縛病棟", "死づえ　呪縛病棟", "", "", "http://ecx.images-amazon.com/images/I/2176KWDA08L._SL210_.jpg", 60, "恐ろしい噂話が現実となって巻き起こる惨劇を描いたホラー映画の第2弾。日陰産婦人科にひと晩だけ出向することになったナースの沙織。夜明け前、患者の急死に驚いていた沙織に、ベテランナースの妙子が病院にまつわる噂話を打ち明ける。それは、娘を亡くしたショックで自殺した母親の霊の話だった。その翌日、沙織の母親が突然倒れて日陰病院に入院することになり……。前作の脚本を担当した櫻井雅章がメガホンをとる。", "2006-05-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飛越陰陽界／THE DECEASED LADY", "ジョイ・ウォンの霊界伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "J・ウォンが二役を演じるサイコ・サスペンス。ある会社のイメージキャラクターである美女ジニーが殺害された。会社はこの事実を隠そうとたくらむ。社員の一人ユーシャオの夢にジニーが現れ、アファンという女性に自分の遺骨を食べさせれば自分はよみがえると訴える。アファンはジニーの双子の妹だった。", "1989-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YASHI", "ウイグルからきた少年", "", "", "http://image.eiga.k-img.com/images/movie/54880/315x210.jpg?4470", 65, "現役自衛官の佐野伸寿がカザフスタンやイラクでの滞在経験をもとに、中央アジアの現状とそこで生活する人々を描いた意欲作。中国の新疆ウイグル自治区からカザフスタンへと逃げてきた少年アユブは、カザフの少年カエサル、ロシアの少女マーシャと兄弟のように廃屋で暮らしていた。そんなある日、アユブは「良い話がある」と持ちかけてきた廃屋の管理人ブラートに騙され、自爆攻撃者に仕立て上げられてしまう。", "2009-10-03", "76,24,51", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG PICTURE", "ケビン・ベーコンの　ハリウッドに挑戦!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "シリアスからコメディまで様々な役をこなすK・ベーコンが、ハリウッドの新人映画監督を演じる青春ストーリー。駆け引き、商業主義、泥々した人間関係が渦巻くハリウッド内幕モノでもある。学生映画コンテストで優勝したニックは、大手映画会社と契約し、デビューすることになる。だが、彼のオリジナル・ストーリーは、金もうけ主義の重役たちに無残に改変される。一方ニックは、美女に囲まれ、スポーツカーを乗り回し、すっかり自分を見失っていた。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROYAL WEDDING", "恋愛準決勝戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "製作A・フリード、監督ドーネン、作曲バートン・レーン、作詞・脚本ラーナーと、ミュージカルの黄金スタッフが作った作品。イギリス王女成婚の祝典でロンドンに招かれたN.Y.の売れっ子ダンスチームがそれぞれ恋をし……。元英国首相チャーチルの令嬢セイラが踊り子役で出演したのも話題となった。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PARENT TRAP", "罠にかかったパパとママ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "児童文学の傑作、エーリッヒ・ケストナーの『ふたりのロッテ』をもとに、舞台をアメリカ西海岸に移して、ユーモラスなタッチで描いた家庭映画。別々に暮らしていた双子の姉妹が、別れた両親を再び結婚させようとして、あれこれおませな作戦を考えるが……。一人二役を演じたH・ミルズの可愛らしさが評判となった。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Disaster Movie", "ディザスター・ムービー！　おバカは地球を救う", "", "", "http://image.eiga.k-img.com/images/movie/53980/315x210.jpg?2561", 88, "「最終絶叫計画」のジェイソン・フリードバーグとアーロン・セルツァー共同監督によるパニック映画をテーマにしたパロディ映画。「インディ・ジョーンズ／クリスタルスカルの王国」「ナイトミュージアム」「デイ・アフター・トゥモロー」を物語のベースに、2008年を代表するヒット作品のギャグネタが続々と登場する。", "2009-01-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARTHUR 2 ON THE ROCKS", "ミスター・アーサー2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "大金持ちアーサーをめぐる、ハッピーな笑いと感動が詰まったロマンティック・コメディの続編。アーサーとリンダが結婚して３年。元フィアンセのスーザンはまだ彼をあきらめきれず、スーザンの父は婚約破棄の仕返しを決意、アーサーの財産を奪ってしまう。大富豪アーサーは気付くと一文無しに……。そんなある日、死んだ執事ボブソンが幽霊となって現れる。前作で絶妙の呼吸を見せたD・ムーアとL・ミネリが再び熱演。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Goal!", "GOAL!", "", "", "http://image.eiga.k-img.com/images/movie/1070/315x210.jpg?2984", 118, "プロのサッカー選手を夢見る少年の成長を描く3部作の第1作。メキシコの貧しい家庭に生まれた少年サンティアゴは、米ロサンゼルスに移住しサッカーの才能を発揮していく。20歳になった彼は、英国の名門チーム、ニューカッスル・ユナイテッドのスカウトの目にとまり、父親の反対を押して英国へ渡るが、さまざまな試練が待ち受けていた。FIFA（国際サッカー連盟）が製作協力、デビッド・ベッカム、ジネディーヌ・ジタンら人気選手が多数出演。", "2006-05-27", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA POLIZIA RINGRAZIA", "黒い警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "証拠不十分で続々と釈放される犯罪者たち。しかし、表では手出しできない警察にも、裏にまわって釈放された犯罪者たちを次々に処刑する闇の殺人集団“黒い警察”が存在していた。その実態を暴こうとした警部にも魔手が迫って……。セミ・ドキュメンタリーの手法を用いた異色の社会派サスペンス。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WARLOCK", "ワーロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "J・サンズが、世界を破滅に導こうとする魔道士を怪演するファンタジー。17世紀に悪魔と契約したワーロックは、現代にタイム・ワープして３つに分断された悪魔経典“グラン・グリモアール”を手中に収めようと謀る。J・ゴールドスミスの不気味な音楽が緊迫感を盛り上げる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOREIGN FIELDS", "フォーリン・フィールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "国連軍の特別任務でボスニアに駐留した農夫出身の兵士ヤコブ。セルビア人による大量虐殺の中、危ういところをホルト軍曹に救われたヤコブは恩義に引きずられ、いつしか軍曹の恐るべきサイド・ビジネスに参加させられてしまう。大義名分でカモフラージュされた戦争の虚構を描くヒューマンドラマ。その衝撃的な内容で1999年のカンヌ映画祭では上映が中止された。", "1998-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逢魔が辻の決闘", "逢魔が辻の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "東照宮より拝領した短刀を紛失したため、取り潰しとなった金森藩の浪人たちは、将軍の代参で上洛する竹姫に再興を直訴。竹姫は、京滞在中の間に、初姫と短刀を持ってくれば願いを叶えるというが……。大河内の役は浪人たちを助ける青江隼人。岡譲二演じる槍の名手、大泉夕雲斎が隼人に斬られて“こんなはずではなかったわい”と言う場面では、公開当時笑いが起こったとか。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dans La Ville De Sylvia", "シルビアのいる街で", "", "", "http://image.eiga.k-img.com/images/movie/55520/315x210.jpg?3607", 85, "「ミツバチのささやき」「エル・スール」の名匠ビクトル・エリセが、“現代スペインで最も優れた映画作家”と絶賛するホセ・ルイス・ゲリン監督のミステリードラマ。ヨーロッパの古都にあるホテルの一室で目覚めた画家志望の青年は、街のカフェに行くと、かつてバーで会ったことのある美しい女性シルビアの姿を見つける。青年はそのシルビアと思しき女性を追いかけて街中を駆けずり回るが……。", "2010-08-07", "9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EINSTEIN ON THE BEACH：THE CHANGING IMAGE OF OPERA", "海辺のアインシュタイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "1976年のアビニョン演劇祭で初演されたロバート・ウィルソンとフィリップ・グラスによるエピデミック・オペラ『アインシュタイン・オン・ザ・ビーチ』。当時、このオペラは演劇におけるアヴァンギャルドとして大いに話題を呼んだ。そのオペラが1985年にブルックリン音楽アカデミーで再演され、その模様を収めたのがこのドキュメンタリー映画。舞台とともに、ウィルソンやグラス、また俳優たちへのインタビューを交えながら綴られていく。初演当時にこの作品がいかに難解なものと受けとられたか、また、グラスの音楽の秘密はどこにあるのか、などが語られていて興味深い。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死国", "死国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "直木賞作家、坂東眞砂子の怪奇小説を映画化したホラー。15年ぶりに四国、高知の村に帰省した比奈子は、友人の莎代里が10年前に事故死したことを知らされる。莎代里の家は代々、死者の言葉を伝える“依童（よりわらわ)”の家系。彼女の母は、88か所のお遍路を逆順で回る禁じ手“逆打ち”を行うことで、娘を甦らせようとしていた。やがて、莎代里が死んだ時の年齢の数である16回目の逆打ちが終わろうとした時、比奈子の周囲で次々と怪事件が起こる……。因習や儀式の不気味さが、日本人の土俗的な感性を喚起。じわじわと深層心理に迫る恐怖がある。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Devotion・　小川紳介と生きた人々", "Devotion・　小川紳介と生きた人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "“三里塚”シリーズなどで知られる記録映画界の重鎮、小川紳介は1992年に18本の作品と１億円の借金を残してこの世を去った。彼の没後10年を迎えて作られたドキュメンタリー。彼と共同生活を送っていた小川プロの元スタッフやその家族、大島渚、羽田澄子など同時代を生きた映画人たちの証言を元に、ストイックな制作活動の実態を赤裸々に暴き出す。監督は、小川作品に感銘を受けたという、レズビアン・フェミニスト女流映像作家のアメリカ人バーバラ・ハマー。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャプテン", "キャプテン", "", "", "http://image.eiga.k-img.com/images/movie/34130/315x210.jpg?1470", 98, "アニメ化もされ人気を博したちばあきおの名作コミックを原作に、野球に打ち込む少年たちの姿を爽やかに描いた青春ストーリー。野球の名門校・青葉学院から墨谷第二中学に転校してきた谷口タカオ。野球部に入部した彼は、名門野球部にいたことからキャプテンに任命されてしまう。ところが彼には球拾い程度の経験しかなく、初めての試合で大失敗。名誉挽回するべく秘密の特訓を開始し、地区予選で奇跡の逆転ホームランを放つ。", "2007-08-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビッグ・マグナム黒岩先生", "ビッグ・マグナム黒岩先生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "暴力・強姦・破壊行為が横行する悪の園の学園に、暴力学生対策に頭を悩ませる文部省が、超法規的措置で拳銃所持を認めた日本でただ一人の仕事人教師・黒岩を送り込む。かくして黒岩対右翼系暴力学生集団との壮絶な戦いが展開されるのだが……。過激な学園アクション。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVE′S　BAYOU", "プレイヤー　死の祈り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "全米で評価を得たS・L・ジャクソンが製作と主演を務めた人間ドラマ。ルイジアナの田舎町。少女イヴはパーティーの夜、医者の父ルイスの不倫現場を目撃してしまう。そのことを心の奥深くに仕舞い込んでいた彼女だがある日、姉から父に乱暴されたと告白され、父に対する憎悪を募らせる。町で知り合った霊媒士の力を借りて、彼女はある復讐を決意するが……。少女の目を通して崩壊していく家族の姿を描出。悲劇的だが、穏やかな景色とおとぎ話風の不思議な雰囲気が余韻を残す。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH HUNT", "デス・ハント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "北極圏に近い酷寒の雪原の奥深く、殺人容疑者が一匹狼の自由を懸けて逃げ込んだ。騎馬警察隊の隊長は、名誉の回復を懸けて彼を追う。壮絶な闘いのうちに生まれる奇妙な友情。C・ブロンソンとL・マーヴィンによる、実話をもとにした壮大なスケールの男臭いアクション。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO WEEKS NOTICE", "トゥー・ウィークス・ノーティス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "バリバリのキャリアウーマンと、情けないタイプのお坊ちゃん。そんなユニークなカップルの恋の成就を描く恋愛ドラマ。「ラブ・アクチュアリー」のヒュー・グラントが芸達者なところを見せる。一流不動産会社の御曹司ジョージの下で働くことになった弁護士ルーシー。だが、いざ仕事が始まってみるとジョージの身の周りの世話ばかり。疲れ果てた彼女は辞めることを宣言するが、後任の女性がなぜか気になって……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミラーを拭く男", "ミラーを拭く男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "2002年のサンダンス・NHK国際映像作家賞を受賞した梶田征則監督の長編デビュー作。交通事故をきっかけに定年直前で会社を辞めた中年男が、自転車で全国のカーブミラーを拭く旅に出るロード・ムービー。理由も話さず、ひたすらミラー拭きの旅を続ける主人公を緒形拳が好演。人生を見つめ直すことができた男の心境が、優しい視線で綴られる感動作だ。定年間近の中年男・皆川勤は自分が起こした交通事故をきっかけに、仕事を辞めてしまう。ある日、事故現場にやって来た勤は、汚れたカーブミラーを見つめる。そして清掃用具を手に取り、そのミラーを一心不乱に拭き始めた。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEMI—TOUGH", "タッチダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "野球映画の佳作「がんばれ！ベアーズ」のリッチー監督が、フットボールの世界にスポットをあてたスポーツ・コメディ。プロチームの主将と良き相棒の名レシーバー、それにオーナーのおてんば娘３人の友情を、試合シーンをはさんでさわやかに描いている。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MARGE", "夜明けのマルジュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "マンディアルグ原作『余白の街』をボロズウィック監督が映画化。一人のセールスマンが、出張先のパリで娼婦と一夜をともにする。ところが明くる朝、彼は妻と息子の急死の知らせを受けた……。“世界一美しい娼婦”クリステルが見もの。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KLIC＾", "抵抗のプラハ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ドイツ占領下のチェコで地下潜行を余儀なくされたチェコ共産党と国民たちの抵抗を描いた名作。ナチス・ドイツ軍の徹底的弾圧下、共産党中央委員のヤンはついに捕まり、ポケットから鍵を発見される。ゲシュタポ長官のフリードリヒはその鍵の合うドアを捜し、さらなる“赤狩り”を進めようとするが……。", "1971-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上海之夜", "上海ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1937年の上海。戦火が激しくなる上海の空襲のなか、橋のたもとで出会ったお互い名前もわからない男と女は再会を約束した。運命を信じてその時を待ち続けて、そして10年後……。運命に翻弄されながら切ない恋を募らせる3人の男女の友情と愛を、コメディタッチで描いた珠玉のラブ・ストーリー。優柔不断な男、ケニー・ビーを巡る2人の女性を、大女優シルビア・チェンと人気歌手のサリー・イップがチャーミングに演じる。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOMMY TRICKER AND THE STAMP TRAVELLER", "悪ガキ・トミーのスタンプ・アドベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "子供たちが切手の世界に入って旅行をする、実写とアニメを合体させたファンタジー・アドベンチャー。12歳のトミーは熱心な切手コレクターで、それは５人兄弟と母親の生活費にもなっている。仲間に高値で切手を売るトミーを唯一信じているのは内気なラルフ。ある日、ラルフは別世界に隠された貴重な切手アルバムを呼び戻す呪文を知り……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dawn Patrol", "暁の偵察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "第一次大戦中の西部戦線で、独軍と戦う英国航空部隊の厳しい戦況と、若い士官を死地に送り出さなければならない司令官の苦悩を描いた物語。ホークスのトーキー第１作。大富豪ハワード・ヒューズが自らの超大作「地獄の天使」との酷似を指摘し、あらゆる手段で製作の妨害や法廷闘争を仕掛けてきたが、製作中止にまで追い込むことは出来ず、完成した映画は記録的大ヒットとなった。", "1930-11-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サルサとチャンプルー", "サルサとチャンプルー", "", "", "http://image.eiga.k-img.com/images/movie/53440/315x210.jpg?1304", 100, "1920年代、政府の移民政策により、沖縄をはじめ日本各地から多くの人々がキューバへと移住した。しかし彼らを待ち受けていたのは、差別や大戦中の強制収容といった過酷な現実だった……。映画評論家・波多野哲朗が初メガホンを取り、わずか2人となった移民1世や現地で生まれ育った日系人を取材。日系キューバ人の知られざる受難の歴史を明らかにすると共に、その中で生まれた新しい文化にもスポットを当てる。", "2008-05-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("斬り込み", "斬り込み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "任侠のヤクザとして鳴らした父と幼い頃に死に別れ、鳶頭の仙吉に育てられた石神新三。若い頃は背中に不動明王の刺青を彫る暴れ者だった彼も、今は皆に慕われる堅気の鳶職人だ。喧嘩はしないと誓った彼だが、ずっと思いを寄せてきた幼なじみの藤川夕子の家を暴力団が乗っ取ろうとするのを見て、怒りと正義が爆発。彼らの情け容赦ない非道なやり口に、任侠の血をたぎらせる。町の人々が心ならずも追い詰められ募らせていく行き場のない憤り、新三の我慢の限界を超えた怒りと意地を、現代的感性でリアルに描く任侠アクション。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・三等重役", "新・三等重役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "源氏鶏太原作の『三等重役』を、タイトルに冠しているが、その内容、キャスティングなどからみて、本編から始まる“新・三等重役”４本は、紛れもなく“社長”シリーズである。大阪にあるKK世界電機の社長は外遊中で、独身の沢村専務が留守を預っていた。そんな折、前の社長夫人鶴子会長から東京の取引先、鬼塚商会の一人娘・舞子を入社させるよう厳命が下される。独身社員らは大いに奮起するが……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD MAN WALKING", "デッドマン・ウォーキング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "死刑囚と彼を取り巻く人々の心情をテーマに、死刑囚の精神アドバイザーとなったシスターの目を通して命の尊厳を描いた感動ドラマ。ルイジアナ州立刑務所の死刑囚マシューから手紙を受け取ったシスター・ヘレン。マシューと対面した彼女は、彼が若いカップルを惨殺した囚人であることを知る。傲慢な態度のマシューに憤慨しながらも、心のどこかで彼の無実を信じるヘレンは、不公平な裁判によって死刑となったマシューを救うために弁護士を探し特赦審問会を申請する。だが嘆願は却下され、同時に被害者の家族の悲しみにも直面する。そして死刑執行が迫るなか、ヘレンの努力によって頑なに閉ざしていた心を開き、人間としての感情に目覚めていった彼は、真実を告白し自らの犯行を認めるのであった……。社会問題である“死刑”を正面から捉えた本作は、各国で絶賛を浴び多くの映画賞を受賞、ノミネートされた。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本侠客伝 花と龍", "日本侠客伝 花と龍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "玉井金五郎は生来の腕と度胸で男をあげ、マンと夫婦になった。その頃、戸畑港では、永田・大庭組と伊崎組一派とが荷役をめぐって争っていた。玉井組を興した金五郎は、伊崎の挑戦を受けて立つ。金五郎の肩には、女賭博師のお京が彫った花と龍の刺青があった……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OMEN IV ： THE AWAKENING", "オーメン4", "", "", "http://ecx.images-amazon.com/images/I/21BVQ4WAE7L._SL210_.jpg", 98, "官界入りも噂される弁護士として成功し、幸福な結婚生活を送るジーンとカレンのヨーク夫妻。唯一の悩みは子供ができないことで、ある日、夫婦は養女をもらい受けることにする。だが、その女児の成長とともに恐ろしいオーメン（不吉な前兆）が再び始まった。第１作からのストーリー・クリエーター、バーンハードの新たな構想に基づいたシリーズ第４作。", "1991-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌う彌次喜多 黄金道中", "歌う彌次喜多 黄金道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "旅先で悪役人のため黒船に連れさられた母親を探し求める娘に出会い同情した弥次さんと喜多さんは、娘を連れて母親探しの旅に出ることになった……。演劇・歌謡・スポーツ・漫才界の人気者たちを一堂に会したミュージカル時代喜劇。連続ラジオ放送劇の映画化。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BURROUGHS", "バロウズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "自らの麻薬体験をもとにした小説『ジャンキー』で、衝撃的なデビューを飾った作家バロウズの生涯を綴るドキュメンタリー。生い立ちから、ビート・ジェネレーションを経て、現代に至るまでのライフ・プロセスに加え、彼の妻の射殺事件の真相も語られる。彼と彼が生み出した文化を知るうえで貴重な１本。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES (".hack//G.U. TRILOGY", ".hack//G.U. TRILOGY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "大ヒットを記録したプレイステーション2用ゲームソフト「.hack//G.U.」を長編アニメ化。西暦2017年。世界最大のオンラインゲーム「The World R:2」の中で、AIDAと呼ばれる不思議な現象が起こりはじめ、現実世界のプレイヤーにまで深刻な被害を及ぼすようになる。ハセヲはAIDAに巻き込まれた志乃を救うため、その原因を作ったとされる謎のプレイヤーキラー・三爪痕を追い続けていたが……。", "2007-12-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風が強く吹いている", "風が強く吹いている", "", "", "http://image.eiga.k-img.com/images/movie/54250/315x210.jpg?3550", 133, "箱根駅伝に挑む大学生たちの青春を描いた、直木賞作家・三浦しをんによる同名小説の映画化。主演に小出恵介、林遣都。箱根駅伝へ出場することを夢見る寛政大学4年生のハイジ（小出恵介）は、事件を起こして陸上から遠ざかっていた天才ランナーの新入生カケル（林遣都）と出会う。致命的な怪我からランナーになることを諦めかけていたハイジだったが、自らが寮長を務める寮にカケルを強引に入居させ、密かに駅伝出場への計画を立て始める。", "2009-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("10 Items or Less", "素敵な人生のはじめ方", "", "", "http://ecx.images-amazon.com/images/I/51cl64LoDVL._SL210_.jpg", 82, "", "2006-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BICENTENNIAL MAN", "アンドリューNDR114", "", "", "http://ecx.images-amazon.com/images/I/41xMwQWJ0fL._SL210_.jpg", 131, "I・アシモフの『バイセンテニアル・マン』を映画化したヒューマンSF作品。近未来、サンフランシスコ郊外に住むマーティン一家に届いた家事専用ロボットNDR114。アンドリューと名付けられた彼はほかのロボットと違い、芸術的才能と感情をもっていた。一家の末娘アマンダとの心の交流は深まっていくが、彼女が老いても自分は変わることなくロボットであり続けることに対してアンドリューは孤独を感じ始め、人間でありたいと願うようになる。やがて彼は自分と同じ様に感情をもったロボットを探す旅を始めるが、見つけることはできず、数十年後、サンフランシスコに戻ってくる。そしてアマンダの孫娘ポーシャに出会った彼は恋をするのだった……。創造力と個性をもったロボットが人間に近づこうとする姿を 200年の時の流れの中に映し出し、“人間であること”の意味を問いかけてくる。R・ウィリアムズの顔が登場するのは物語半ばからだが、前半のロボットの中に入って演技しているのも彼自身である。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おれは男だ！完結編", "おれは男だ！完結編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1970年代に人気を博したTVドラマ「おれは男だ！」の続編的作品。TVドラマで主演を務めた森田健作が、製作も兼任している。受験勉強一色に染まり、無気力な生徒ばかりが目立つ母校・青葉学園に喝を入れるべく、サイパンで日本語を教えていた剣道部OB・小林が帰ってくる。森田流青春学をストレートに表出。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐の中を突っ走れ", "嵐の中を突っ走れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "当初、アウトサイダーとしてデビューした裕次郎は、年輪を重ねるにつれて、体制内のヒーローに変貌していった。これはその先駆けの一本。女子高に赴任した熱血教師は、女子高生や芸者に大モテ。そんな男が水産研究所の悪事を暴き、自分のせいで職を失った恋人の父を復職させて街を去るまでを描く。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若き日の啄木 雲は天才である", "若き日の啄木 雲は天才である", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "怪奇映画監督としてよく知られる中川信夫であるが、彼はまた若かりし時、啄木・朔太郎・新感覚派に心酔した文学青年でもあった。本作はそんな中川の長年の念願の企画で、新東宝も芸術大作としてこれを遇した。進歩的な教育を行い、故郷の学校を追われた啄木の北海道における記者生活、文学と現実の相克、芸者小奴とのかかわりが描かれるが、あまりに啄木に傾倒しすぎたためか、真面目すぎるきらいも。後年、中川自らも“苦しい。苦しい酒みたいだね”としか言及していない。なお、彼はほかにも「虞美人草」と「三四郎」という、２本の明治文学ものを撮っている。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("V・マドンナ大戦争", "V・マドンナ大戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "生徒会長が雇った７人のおんな用心棒・マドンナ軍団。彼女らと、生徒会費300万円を略奪に来る番長連合・柳生軍団の死闘を描く。第９回城戸賞入選作品の映画化。７人対1000人で繰り広げられる、バイクありセメント爆弾ありの壮絶なアクション・シーンが見もの。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NANG−NAK", "ナンナーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "タイに古くから語り継がれている伝説を映像化した幻想的なラブ・ストーリー。タイのバンコク郊外にある水辺の美しい村。マークとナークの若い夫婦は幸せに暮らしていたが、夫は兵士として内戦に参加することに。戦場で瀕死の重傷を追いながらも、マークは妻の元へ戻ってくるが……。", "1999-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和残侠伝・破れ傘", "昭和残侠伝・破れ傘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "シリーズ最終作。東北制圧をもくろむ鬼首一家は、寺津一家を利用して天神浜一家をつぶし、郡山を手中に収めようとしていた。非道な鬼首は邪魔になると寺津さえも惨殺する。寺津の兄弟分・花田秀次郎と天神浜の元代貸し・風間重吉は、降りしきる雪のなか、鬼首のもとに斬り込む。新人・檀ふみの可憐な姿が見られる。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE−ARMED　SWORDSMAN", "片腕必殺剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "香港の活劇映画を確立した“ショウ・ブラザース社”製作による武侠アクション・シリーズ第１作。片腕でカンフー＆剣劇を繰り出すスタイルは、のちに同じくジミー・ウォング主演の「片腕ドラゴン」へとつながっていく。父を殺された過去を持つ主人公は金刀派の後継者と目されていたが、彼を愛する師匠の娘が嫉妬に狂い彼の右腕を切り落としてしまう。その後ある娘に救われ、“片腕必殺剣”を編み出す。", "1967-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lonely Hearts", "ロンリーハート", "", "", "http://image.eiga.k-img.com/images/movie/34310/315x210.jpg?1686", 107, "1940年代のアメリカで実際に起こった連続殺人事件を、ジョン・トラボルタ、サルマ・ハエックら豪華キャストで映画化したクライム・サスペンス。新聞の恋人募集欄で見つけた女性をターゲットに結婚詐欺を繰り返すレイと恋人のマーサ。やがてマーサは、レイが誘惑した女性に対する異常なほどの嫉妬心から殺人を犯しはじめる。一方、女性が自殺したと思われる現場に駆けつけたロビンソン刑事は、そこに犯罪の匂いを感じ捜査を開始する。", "2007-11-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE McCONELL STORY", "マッコーネル物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "朝鮮戦争の空の英雄として活躍した、アメリカ空軍のパイロット、マッコーネル。空の英雄といわれながら、その後、新型ジェット機のテスト飛行中に事故死した彼の半生を描く。妻との交流を中心に据えながら、一人の男の生き様を綴ったストーリーがさわやかな感動を残す。妻役のJ・アリスンの好演も光っている。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンダルシア　女神の報復", "アンダルシア　女神の報復", "", "", "http://image.eiga.k-img.com/images/movie/55950/315x210.jpg?3103", 125, "織田裕二が外交官・黒田康作を演じる映画「アマルフィ　女神の報酬」（2009）、ケータイドラマ「アマルフィ　ビギンズ」（09）、TVドラマ「外交官・黒田康作」（11）に続くシリーズ新作。黒田が新たに赴任したスペインで、日本人投資家の殺人事件が発生。やがてその事件は全世界を揺るがす国際犯罪へと発展していく。黒田は事件解決のため、バルセロナからアンドラ、そしてアンダルシアを奔走する。「容疑者Xの献身」で知られる西谷弘監督が、前作「アマルフィ」から引き続きメガホンをとる。", "2011-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シアトリカル　唐十郎と劇団唐組の記録", "シアトリカル　唐十郎と劇団唐組の記録", "", "", "http://image.eiga.k-img.com/images/movie/53100/315x210.jpg?1898", 102, "天才劇作家・唐十郎率いる“劇団唐組”の活動を記録したドキュメンタリー。演劇界に衝撃を与えた1967年の新宿・花園神社での紅テント公演から40年。67歳となった唐は、現在でも紅テントにこだわり続け、ほぼ年1本のペースで新作戯曲を世に送り出している。本作では2006年秋の戯曲執筆から2007年春の公演初日までを追い、唐と14人の劇団員たちの芝居に対する凄まじいまでの情熱を描き出していく。監督は大島渚の次男、大島新。", "2007-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lost and Found", "甘いウソ", "", "", "http://image.eiga.k-img.com/images/movie/54840/315x210.jpg?4410", 113, "「宮女」のパク・チニが主演するロマンティック・コメディ。売れない放送作家のジホは、10年間彼氏のいない酒癖の悪いシングル。ある日、突然のクビを宣告されたジホは、ショックで呆然と家路に着く途中で車にはねられてしまう。ジホは車を運転していたのが初恋の相手ミヌだと分かると、記憶喪失のフリをしてミヌの家に転がり込み、女らしさをアピールしてミヌのハートを射止めようとする。", "2009-10-10", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タケオ　ダウン症ドラマーの物語", "タケオ　ダウン症ドラマーの物語", "", "", "http://image.eiga.k-img.com/images/movie/56870/315x210.jpg?4454", 76, "アフリカの民族楽器サバールやジャンベ、バラフォンをはじめ、ピアノやマリンバも演奏し、年間20回近いコンサートも行っているダウン症の青年・新倉壮朗の姿を追ったドキュメンタリー。11歳の頃から太鼓を叩き続けている壮朗は、アフリカン・ドラムの本場へあこがれを抱いていた。免疫力の弱いダウン症のためアフリカへの渡航は難しいといわれていたが、2008年、念願のセネガルへの渡り、同国の人間国宝でサバール奏者のドゥドゥ・ンジャエ・ローズとのセッションも果たす。", "2011-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("U.S.MARSHALS", "追跡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "ハリソン・フォード主演の「逃亡者」で強烈な印象を残したT・L・ジョーンズ演じるジェラード連邦保安官を主役にすえたサスペンス・アクション。監督はデビュー作「エグゼクティブ・デシジョン」で卓越したサスペンス演出を見せたS・ベアード。ジェラードが同乗していた囚人護送機がシカゴからニューヨークへ向かう途中、墜落。殺人犯のシェリダンが逃亡したことに気付いたジェラードは部下を集め、彼の追跡を開始した。しかし、シェリダンはただの男ではなかった。元海軍特殊部隊隊員で、最近は政府の密命で動く特殊工作員だったのだ。追跡を続けながらもシェリダンが犯したとされる殺人事件を調査するジェラード。無実を訴えるシェリダン。二人はついに対決の時を迎える……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の門 ?二代目流血の盃?", "極道の門 ?二代目流血の盃?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "極道の世界をリアルに描いた任侠ドラマの第２作。敵対する義道会の刺客５人を切り倒した村田は組長・鹿島と若頭・赤垣に見込まれ、他の幹部の反対をよそに若頭補佐と二代目・尾形組組長に昇格した。幹部たちが村田の足を引っ張ろうとするなか、幹部の一人、倉嶋が彼の舎弟となり、ねたまれる村田の立場はますます悪化。一方、義道会組長の大田黒も村田の命を狙っているのだった……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殿さま弥次喜多 捕物道中", "殿さま弥次喜多 捕物道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "中村錦之助と中村賀津雄の兄弟コンビが尾張と紀州の若殿に扮し、身分を隠して珍道中を繰り広げるシリーズ全３作中、２作目にして最高傑作。今回、二人は義賊観音小僧に罪を着せようとする悪徳商人の悪事を暴き、海賊を退治する。職人・沢島忠の演出はミュージカル風、スラップスティック風と軽妙洒脱。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤壁", "レッドクリフ Part I", "", "", "http://image.eiga.k-img.com/images/movie/53290/315x210.jpg?3066", 145, "西暦208年、曹操率いる80万の魏軍に対抗するため、蜀の軍師・孔明は同じく魏に降伏を迫られている呉と同盟を結ぶ策に出る。魏軍と呉・蜀連合軍とが長江の赤壁で繰り広げ、「三国志演義」でも描かれた“赤壁の戦い”を、ジョン・ウー監督が壮大なスケールで映画化する全2部作の前編。蜀の劉備を支えた名軍師・諸葛孔明を金城武、呉の孫権に仕えた水軍の将・周瑜をトニー・レオンが演じるほか、チャン・チェン、ビッキー・チャオ、リン・チーリン、中村獅童などアジアのスターが集結。", "2008-11-01", "1,8,76,53,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Music and Lyrics", "ラブソングができるまで", "", "", "http://image.eiga.k-img.com/images/movie/33930/315x210.jpg?1287", 104, "ヒュー・グラントとドリュー・バリモアが初共演を果たしたロマンティック・コメディ。トップ歌手への曲提供で返り咲きを狙う元ポップスターと、成り行きで彼の曲作りを手伝うことになった植木世話係の女性との恋の行方を描く。監督・脚本は「トゥー・ウィークス・ノーティス」「デンジャラス・ビューティー」のマーク・ローレンス。随所に散りばめられた1980年代ネタが笑いを誘う。グラントが当時の装いで歌やダンスを初披露。", "2007-04-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セブンスフロア", "セブンスフロア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "オーストラリア・シドニーを舞台に繰り広げられるサイコ・サスペンス。突然の事故で夫に先立たれたケイトは、失意のなか、夫の会社の経営まで引き継ぐことになった。そんな心の不安定な時期に出会ったのが、日本人のコンピュータ・プログラマー佐藤満。どこか陰のある佐藤にケイトは惹かれ、二人は愛し合うようになる。ケイトの前に明るい道が開けたのもつかの間、彼女を会社から追い出したビビアンが不審な死を遂げる。ビビアンの死の裏に感じられる、佐藤や彼の姉・ナオコの動き。独自に調査を始めたケイトが佐藤の忌まわしい過去を知った時、佐藤の狂気が彼女に恐怖をもたらす……。加藤雅也とブルック・シールズが共演した日米合作作品。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARTICLE99", "ドク・ソルジャー 白い戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ベトナム戦争によって、心身に深い傷を負った退役軍人を収容する病院での、若き医師たちの姿を描いた感動作。「プリティ・イン・ピンク」などの青春映画で定評のある、H・ドイッチが演出。“本作品はベトナム戦争そのものを扱っているわけではなく、いまだにシェル（砲弾）・ショックやエイジョント・オレンジ（枯れ葉剤）に苦しむ退役軍人たちの実態という現実の問題を描いた作品である”と監督自身は語っている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gib Gas - Ich will Spass!", "恋のロックバルーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "世界的な大ヒットを記録した「ロックバルーンは99」で知られる西ドイツの女性ボーカリスト、ネーナ主演で製作された青春ロード・ムービー。2人の青年の間で揺れ動く少女の旅をコメディ・タッチで描く。ハイスクールの人気者ティナは、恋人ティーノを追うために、彼女に思いを寄せる青年ロビーを言いくるめて旅に出た。ベニスを目指し、ロビーの愛車ベスパで出発した2人。ところが旅の途中、暴走族にベスパを壊されてしまい……。", "", "45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユダ", "ユダ", "", "", "http://ecx.images-amazon.com/images/I/51Mu5tFBz-L._SL210_.jpg", 113, "「MOON CHILD」の瀬々敬久監督がメガホンを取り、性同一障害の少年ユダと、心と身体に傷を持つ女・美智の数奇な運命を描いたロード・ムービー。東京でドキュメンタリービデオを制作している“私”は、自分が“ユダ”と名づけた少年の日常をカメラで追いはじめるが、ユダは私のカメラと共に姿を消してしまう。それから数カ月後、私の前に現われた美智という女が、ユダが死んだことを告げ……。名監督と新人監督たちが作品を競う「エロス番長」の1作。", "2004-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PALOOKAVILLE", "パルーカヴィル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "オマヌケな(＝パルーカ)３人組の強盗がたくらんだ強盗作戦の行方を、ドライなユーモアとともに綴ったオフビート・コメディ。宝石店と間違えてパン屋に侵入し、現金輸送車を襲っても心臓発作を起こした運転手をついつい助けてしまう３人組。コーヒーショップで細々と反省会を開く彼らの、情けないほどのお人好しぶりが微笑ましい。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WHOLE TOWN'S TALKING", "俺は善人だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "心優しいサラリーマンのアーサーは、やっとの思いで誘った同僚のクラークと食事中、警官に逮捕されてしまう。脱走したギャングのボスと瓜二つだったために間違って逮捕されたのだが、それがきっかけとなり、アーサーの身辺で様々なトラブルが巻き起こる。「幌馬車」などの巨匠J・フォードが手掛けたギャング・コメディ。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PULSE", "パルス・ショック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "電気が悪意を持つ生命体として、人々に襲い掛かるパニック・ホラーの会心作。悪魔と少年が登場する伝統的なアメリカン・ホラーを踏襲した作品。主人公の少年が、母と離婚し新妻と暮らすロスの父を訪ねてきた。そんな時、隣人の感電死をきっかけに、電気が人間を殺そうとしている話を聞いた彼は恐怖に駆られる。やがて父の元を去り、タクシーに乗るが、その車も電気の攻撃を受ける。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Otac na sluzbenom putu", "パパは、出張中！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "スターリンの影響から未だ脱け出せない1950年代初頭のユーゴスラビアを舞台に、時代の波に翻弄される一家の姿を体制批判をこめて描いたヒューマンドラマ。家族に囲まれて幸せに暮らしていた少年マリック。ところが、父親が愛人にふと洩らした国政批判のせいで逮捕され、強制収容所に入れられてしまう。不思議がるマリックに対し、父親は出張中だとごまかす母親だったが……。1985年度、カンヌ国際映画祭グランプリを受賞。", "1985-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とんねるらんでぶー", "とんねるらんでぶー", "", "", "http://image.eiga.k-img.com/images/movie/57500/315x210.jpg?4779", 32, "吉本興業が主催する2011年・第3回沖縄国際映画祭の企画「地域発信型プロジェクト」で製作された6本の短編作品を、「東」と「西」の3本ずつに分けて劇場公開する「沖縄国際映画祭　地域発信型映画」の「東」の1本。静岡県・三ヶ日町に暮らす歌が大好きな女子高生の咲子は、退屈な田舎町を早く抜け出したいと願っていた。現実にいらだち、感情の赴くままにトンネルを抜けたその先へと向かって走り出した咲子が出会ったものとは……。", "2011-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I'm Not There", "アイム・ノット・ゼア", "", "", "http://image.eiga.k-img.com/images/movie/53320/315x210.jpg?3376", 136, "ケイト・ブランシェット、ヒース・レジャー、リチャード・ギア、クリスチャン・ベール、ベン・ウィショーら6人の豪華キャストが、伝説的アーティスト、ボブ・ディランをそれぞれ異なった角度から演じるという話題作。「ベルベット・ゴールドマイン」などの鬼才トッド・ヘインズがメガホンを取り、フォークシンガー、ロックスター、詩人、映画スターなど様々な顔を持つボブ・ディランの謎に包まれた人生に迫る。", "2008-04-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十七歳", "十七歳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "当時現役女子高生だった井上路望が、自らの体験を綴った同名エッセイの映画化。高校２年の路望は、パート勤めをしている母と二人暮し。家では母と上手く行かず、学校ではイジメにあい、クラスメイトや教師とも馴染むことができない。唯一の救いはバイクレーサーになるという夢を持つ、同級生の中島要の存在だけだった。だが、周囲の状況は次第に耐え難いものになっていく……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sentinel", "ザ・センチネル　陰謀の星条旗", "", "", "http://image.eiga.k-img.com/images/movie/33490/315x210.jpg?2614", 108, "シークレット・サービスが大統領警護の任務に就いてから141年。内部の何者かが大統領暗殺計画に関与しているという情報によって、揺るぎない忠誠心を誇ってきた組織に激震が走る。果たして真相は!?　裏切り者として仲間から追われる伝説のエージェント、ギャリソンにマイケル・ダグラス。彼を追い詰めるかつての部下、ブレキンリッジにキーファー・サザーランドが扮している。監督は「S.W.A.T.」のクラーク・ジョンソン。", "2006-10-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嫁ぐ日まで", "嫁ぐ日まで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "島津保次郎の東宝移籍第２作。妻を若くして亡くした生方光三と、その代役として家事にいそしむ姉娘・好子。光三はやがて再婚するが、好子の方は妹の浅子と後妻との不和を心配して、好意を寄せてくれる青年からの求婚を断り、若き外交官と結婚する……。好子役を原節子が演じているのをはじめ、設定にも小津的なものが漂う一作。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富江　アンリミテッド", "富江　アンリミテッド", "", "", "http://image.eiga.k-img.com/images/movie/55960/315x210.jpg?2967", 83, "長い黒髪と左目の泣きぼくろが特徴の美少女・富江の恐怖を描く、伊藤潤二のホラー漫画を実写映画化したシリーズ第8作。美しい姉・富江に負い目を感じていた女子高生の月子は、凄惨な事故で姉を亡くす。しかし1年後、死んだはずの富江が家に帰ってきたことから新たな恐怖の幕が上がる。富江役に元グラビアアイドルの仲村みう、月子役に人気ドラマ「ゲゲゲの女房」にも出演した荒井萌。そのほか「AKB48」の多田愛佳らが出演。", "2011-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄河大侠／YELLOW　RIVER　FIGHTER", "黄河大侠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "唐朝末期、戦乱に明け暮れる中国を舞台に、黄河を隔てて対立する３人の王の争いに巻き込まれる無頼の剣士の活躍を描く。主演のユー・チェンウェイをはじめ、出演者はいずれも中国武術界のリーダー的存在。新味はないが一級の武侠映画。", "1987-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN OF THE LIVING DEADIII", "バタリアン リターンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "死者をよみがえらせる軍の秘密実験の現場を目撃した青年カートは、事故死した恋人、シュリーを生き返らせようと、軍の実験室に忍び込むのだが……。おなじみ、スプラッタホラーのシリーズ第３作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REVIVAL OF EVANGELION 新世紀エヴァンゲリオン劇場版 DEATH(TRUE)2／Air／まごころを、君に", "REVIVAL OF EVANGELION 新世紀エヴァンゲリオン劇場版 DEATH(TRUE)2／Air／まごころを、君に", "", "", "http://ecx.images-amazon.com/images/I/51M3KJWEHYL._SL210_.jpg", 160, "1997年の春と夏にそれぞれ公開された「新世紀エヴァンゲリヲン劇場版　シト新生」と「新世紀エヴァンゲリオン劇場版 Air／まごころを、君に」から、TV版1～24話の総集編になる「DEATH」編と、完全新作の最終2話「Air」「まごころを、君に」をまとめて1本にしたエヴァ劇場版の最終バージョン。「DEATH」編は97年公開版にさらなる編集や修正が加えられた「DEATH(TURE)2」版になっている。", "1998-03-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋文", "恋文", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ある日、夫が昔の恋人が余命いくばくもないと知り、妻と子供を捨てて、かつての恋人のためにいちずに尽くす。一見わがままに見える夫の行動は、男の甘えを持つ萩原健一が演じているため映画的虚構として成り立っている。捨てられる妻を演じる倍賞美津子は、バイタリティーと大いなる母性愛を持つ女として登場。出色の演技を見せ、この年の女優賞を総ナメにした。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おぎゃあ。", "おぎゃあ。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "若手女優、岡本綾が妊娠中の少女に扮した映画初主演作。“神々の住む島”といわれる沖縄県浜比嘉（はまひが）島を舞台にしたコミカルでファンタスティックなドラマだ。幼い頃に死んだと聞かされていた母親、幸子が生きていると知り、浜比嘉島を訪れた19歳の花。彼女のお腹には失踪した恋人ハヤトとの子供が宿っていた。花はこの不思議な島で豪快な性格の幸子を始め、個性的な人々との出会いを経験し、新たな価値観に目覚めていく。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARDON MY SARONG", "凸凹宝島騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "米・ユニバーサル映画のドル箱コメディチーム、アボット＆コステロが主演した“凸凹”シリーズ第７作。バスの運転手に扮した二人が、ひょんなことからエキゾチックな南の島に流される。こりゃ極楽だと思いきや、そこで寺院の宝石を狙う悪漢たちと対決するはめに……。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Queen", "クィーン", "", "", "http://image.eiga.k-img.com/images/movie/33920/315x210.jpg?1593", 104, "ダイアナ元皇太子妃が交通事故で急逝した直後のイギリス王室の内幕を描いた作品。かねてからダイアナとの不仲説が噂され、事故後も沈黙を貫き通そうとするエリザベス女王に、国民は次第に不満を募らせはじめる。そんな中、就任したばかりの首相トニー・ブレアが王室と国民の和解に奔走する。ひとりの人間として苦悩するエリザベス女王を見事に演じたヘレン・ミレンは、アカデミー賞をはじめ多くの映画賞で主演女優賞に輝いた。", "2007-04-14", "3,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チャンドマニ　モンゴル・ホーミーの源流へ", "チャンドマニ　モンゴル・ホーミーの源流へ", "", "", "http://image.eiga.k-img.com/images/movie/55260/315x210.jpg?3405", 96, "モンゴルの伝統声楽ホーミーを通じ、自分自身のアイデンティティーと向き合う2人の青年の姿をドキュメンタリータッチで描いたドラマ。ホーミー発祥の地として知られるチャンドマニ村で、ホーミーの名人ダワージャブは昔ながらの遊牧生活を営んでいるが、息子のザヤーはお金にならない遊牧民の生活を捨て、首都ウランバートルへと移住していた。一方、民族芸能劇場の人気歌手スター・ダワースレンは、チャンドマニ村の存在を知り、長距離バスで村を訪れることにする。", "2010-03-20", "76,57", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NARA：奈良美智との旅の記録", "NARA：奈良美智との旅の記録", "", "", "http://image.eiga.k-img.com/images/movie/33860/315x210.jpg?2446", 93, "国際的に高い評価を受けるアーティスト奈良美智が、クリエイティブユニットgrafと共に行なった展覧会“A to Z”。ソウル、横浜、ニューヨーク、大阪、ロンドン、バンコクなど世界各地を巡り、2006年に青森県の古いレンガ倉庫に架空の街“A to Z”を作りあげた。展覧会を成功させるまでの420日間をカメラに収め、これまで密着取材を断り続けてきたアーティスト、奈良美智の素顔に迫るドキュメンタリー。", "2007-02-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'heure Zero", "ゼロ時間の謎", "", "", "http://image.eiga.k-img.com/images/movie/53150/315x210.jpg?1978", 108, "アガサ・クリスティーの傑作ミステリー「ゼロ時間へ」を、「アガサ・クリスティーの奥様は名探偵」のパスカル・トマ監督が映画化。新妻のキャロリーヌを連れ、海辺の別荘で暮らす大金持ちの叔母カミーラの元を訪れたギヨーム。しかし別荘には彼の前妻オードも来ることになっていた。さらにキャロリーヌとオードに恋心を抱く2人の友人たちも加わり、嫉妬と欲望が渦巻く別荘は緊迫の様相を呈していく。そして遂にある人物が死体となって発見され……。", "2007-12-15", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Billy Elliot", "リトル・ダンサー", "", "", "http://image.eiga.k-img.com/images/movie/1500/315x210.jpg?3043", 111, "イギリスの炭坑町に住む少年ビリーは、偶然目にしたバレエ教室に惹かれ、女の子たちに混じって練習するうち夢中になっていく。めきめき上達する彼に自分の夢を重ね、熱心に指導するウィルキンソン先生。しかし大事なお金をバレエに使うことを知った父は激怒し、教室通いを禁じる。先生はビリーにロイヤル・バレエ学校のオーディションを受けさせたい一心で無料の個人レッスンを行うが、オーディションの朝、炭鉱夫の兄トニーがスト中に逮捕される。", "2001-01-27", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GANG IN BLUE", "ギャング・イン・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "現代ブラック・シネマの活劇派M・V・ピーブルズが、父親のメルヴィンと組んで手掛けた社会派アクション。白人警官による異人種への暴力を見かねた黒人警官ローズは独自に内部の捜査を開始。背後には、警察内部の人種差別組織“ファントム”の存在があった。やがてFBIに通報したローズは、ファントムに命を狙われることになり……。ビデオ発売時タイトルは「ザ・ファントム」。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最も危険な遊戯", "最も危険な遊戯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "財界の大物たちが誘拐されるという事件を連日のように報道する新聞やテレビ。そんなある日、業界でも最大手の東日電気社長・南条が誘拐された。会長の小白河は南条救出を５千万円の謝礼で殺し屋・鳴海昌平に依頼する。敵のアジトに乗り込み、見事救出に成功するかに思えた時、何者かによって南条は射殺される……。防衛庁をはじめとする国家権力に孤独な挑戦を試みる殺し屋の姿を、ラブ・シーンやカー・アクションを織りまぜながらハードボイルド・タッチで描いている。日活アクション映画の流れをくみ、一時代を築いた村川＝松田コンビの出世作。仙元誠三による、殺りくシーンの長回し撮影と、松田優作のアクションが見どころ。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Last Chance Harvey", "新しい人生のはじめかた", "", "", "http://image.eiga.k-img.com/images/movie/55100/315x210.jpg?4788", 93, "アカデミー賞俳優のダスティン・ホフマン＆エマ・トンプソン共演による恋愛ドラマ。バツイチのCM作曲家ハーベイ（ホフマン）は娘の結婚式のため、ニューヨークから遠路はるばるロンドンに来たが、娘からはバージンロードを歩くことを断られ、トラブルで仕事はクビになるなど散々な目に遭う。そんな中、ハーベイは空港のバーで一人で飲んでいた中年女性ケイト（トンプソン）と出会う。", "2010-02-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Romantic Comedy", "ロマンチック・コメディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "舞台劇の共作者である男女が、愛し合いながらも幾度か精神的にすれ違い、９年の時を経てゴールインするまでを描いたヒット舞台劇の映画化。この間に、他の女性と結婚、離婚、浮気など、様々な人生の波にもまれていく男を、D・ムーアが小気味よく演じる。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hancock", "ハンコック", "", "", "http://image.eiga.k-img.com/images/movie/53200/315x210.jpg?2047", 92, "ウィル・スミスが嫌われ者のヒーローに扮したアクションコメディ。圧倒的なパワーで人々を救うスーパーヒーローのハンコックは、暴走しすぎて市民から大ブーイングを受けてしまう。そんなある日、ハンコックは偶然にも命を救ったPRマンのレイの提案で“皆から愛されるヒーロー”を目指すことになるが……。監督は「キングダム／見えざる敵」のピーター・バーグ。共演にシャーリーズ・セロン、ジェイソン・ベイトマンほか。", "2008-08-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛のえじき　女教師ハルカの告白", "愛のえじき　女教師ハルカの告白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "学校内で起こった強姦（ごうかん）事件をきっかけに、男子生徒3人がこつ然と姿を消してしまう。被害者でもある女性教師のハルカは、自身や生徒たちのためにも事件を隠し、独自に調査を開始。その過程で自身の過去と向き合うことになるが……。セクシー女優の範田紗々が主演するエロティック・サスペンス。監督は、インディペンデント映画「無垢なモノ my simple things」（2006）がゆうばり国際映画祭で高い評価を受けた筒井勝彦。", "2010-12-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひとつぶの涙", "ひとつぶの涙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "画家になることを夢見る孤児院出身の若者と、マッサージの学校に通う盲目の少女の恋愛を描くさわやかな青春映画。この二人を森田健作と吉沢京子の青春コンビが演じ、監督には娯楽作品に定評のある市村泰一があたっている。出演者としてほかに若き日の水谷豊や西城秀樹が起用されている。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("尊王攘夷", "尊王攘夷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "サイレント期から戦後まで活躍した大監督・池田富保が、日活の新旧スターを一堂に会して製作。自身の代表作とした歴史映画の大作。時代の先覚者であり憂国の士でもあった井伊直弼を主人公に、井伊の大老就任、尊王攘夷派と開国派の血闘、井伊の開国へと至る苦衷、それゆえに水戸浪士に暗殺されるまでを描く。そのタッチは当時全盛の乱闘大チャンバラから喜劇的な場面まで、放縦にして重厚。特にラスト、撮影当時の横浜の実景場面は池田の歴史的視点を感じさせ、当時「忠臣蔵」や「荒木又右衛門」「水戸黄門」などが代表作で、封建的な時代劇作家の感が強かった彼の株を大いにあげた。無声映画を代表する作品。", "1927-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE TOMBEAU D’ALEXANDREーLE DERNIER BOLCHEVIK", "アレクサンドルの墓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「サン・ソレイユ」などで知られるフランス人監督C・マルケルがロシアの監督アレクサンドル・メドベトキンに捧げたビデオ作品。メドベトキン作品からの映像、関係者へのインタビュー、写真、当時のニュースなどをコラージュのようにつなげ、エイゼンシュテインと同時期に活躍しながら、忘れられた監督の生涯に迫っていく。第１部「影の王国」(58分)、第２部「王国の影」(59分)の２部構成になっている。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SAINT OF FORT WASHINGTON", "聖者の眠る街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "大都会ニューヨークにひっそりと生きるホームレスたちの、人種・世代を超えた友情と小さな奇跡をしみじみと描くヒューマン・ドラマ。ホームレスの黒人ジュリーは落ちぶれた若者マシューと出会い、この新米ホームレスの面倒を見るようになるが……。映画は、短命のマシューに老い疲れた者の身体の痛みを治す霊能力を与え、都市の片隅に光をもたらす新たな“聖者”伝説を生み落とそうと試みている。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふり袖捕物帖 若衆変化", "ふり袖捕物帖 若衆変化", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "お江戸八百八町で奇怪な女さらい事件が発生した。踊りの代稽古を務めるお七は、へなちょこ剣士の源太郎が役立たずと見るや、自ら男装して真相究明に乗り出していく。美空ひばりの八変化が見ものの明朗時代劇。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花と蛇", "花と蛇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "過去に５度も映画化された、SM小説の巨人、団鬼六の代表作を鬼才・石井隆が映画化した官能ドラマ。夫との関係が冷め切った美しきタンゴ・ダンサーが、男たちの陰謀によって性のアンダーグランドに突き落とされていく様をスリリングに描く。かつて谷ナオミらが演じてきたヒロイン役に、“女王様”的なイメージの強い杉本彩が挑戦。その大胆な新境地が話題となった。実業家の妻でタンゴ・ダンサーの静子は美貌と知性を備えた貞淑な女性。ある日突然、男に拉致され、セレブたちがSM殺人ショーを楽しむ極秘の仮面パーティへ連れて行かれる。最初はどんな責めにも屈しない静子だったが……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WINGS OF DANGER", "国際贋札団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "恐怖映画の職人監督T・フィッシャーが演出した犯罪サスペンス映画。バンとニックはスペンサー所有の航空会社のパイロット。ある時ニックが仕事中、墜落死する。その後バンは、ニックが贋札密輸の嫌疑をかけられていたことを知る。ところが死んだはずのニックが恋人とともに潜伏していたのだ。彼はスペンサーの手先として密輸の片棒を担がされ、偽装事故を起こしたのだった。", "1952-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NORTH TO ALASKA", "アラスカ魂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1900年、ゴールドラッシュの頃のアラスカ。サムと相棒のジョージは金鉱を堀りあてることに成功。サムはジョージの婚約者をここに連れてこようと故郷のシアトルに戻るが、彼女は別の男と結婚していた。サムは代わりにミシェールという美女をアラスカまで連れていくが……。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIBERTE´，LA NUIT", "自由、夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1950年代末のパリ。アルジェリア解放戦線に身を投じたジャンは妻ムーシュと別れるが、ムーシュは極右組織に暗殺される。失意のジャンはアルジェ生まれの女性と知り合い、心の安らぎを得るが、それもつかのま、二人のもとに暗殺者の影が……。仏映画界の最前衛、P・ガレルの独自の映像世界。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泣くな小鳩よ", "泣くな小鳩よ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "将来を誓った恋人がいたが、下宿先の娘との結婚を勧められとまどう青年。戦争で盲目となったその恋人は、彼の幸福のために身を引く決心をするが……。当時人気絶頂だった岡晴夫主演の歌謡映画で、彼の歌声もじっくりと堪能できる。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("轟沈 印度洋潜水艦作戦記録", "轟沈 印度洋潜水艦作戦記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "一隻の潜水艦にインド洋出撃命令が下った。厳しい訓練を繰り返し敵地へ赴く兵士。その合間の和やかな食事の風景なども追ったドキュメンタリー。時に敵の爆雷を受けながら、輸送船団を轟沈するまでの３ヵ月に及ぶ艦内生活を緊迫感ある映像で綴った作品。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("船、山にのぼる", "船、山にのぼる", "", "", "http://image.eiga.k-img.com/images/movie/53460/315x210.jpg?2428", 88, "長い反対運動を経てダム建設が決定した広島県灰塚地域。美術家・写真家・建築家からなるユニット“PHスタジオ”は、建設に伴い20万本以上もの木が伐採されることを知り、“森の引越し”をテーマとするアートプロジェクトを開始した。伐採された木で巨大な船をつくり、ダムの水を利用して山頂まで移動させようというのだ。12年に渡るプロジェクトの全貌を、「ニュータウン物語」の本田孝義監督が穏やかな目線で捉えたドキュメンタリー。", "2008-04-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Great Santini", "パパ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "原作者コンロイの自伝的小説の映画化。頑固一徹で、海軍内でもその反骨精神がけむたがられているブル・ミッチャム中佐と、その家族、とりわけ長男ベンとの対立や確執、愛情などを描く。頑固な中年男を、まさにはまり役のデュヴァルが好演。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鳥を見て！", "鳥を見て！", "", "", "http://image.eiga.k-img.com/images/movie/58070/315x210.jpg?3600", 85, "青春と性をテーマに監督たちが独自の個性でオリジナル作品を手がける「青春H」シリーズの22作目。「ちょちょぎれ」の佐々木友紀監督が、次々と男をものにする肉食系女子の恋愛模様を描く。清楚な見た目とは裏腹に合コンを繰り返しては男を物色し、狙った相手は必ず落とす肉食系の女子大生かおり。ある日、そんなかおりの前に全く自分になびかない男・弦太が現れる。かおりは意地でも弦太を落そうと、あの手この手で気を引こうとするが逆にストーカー扱いされてしまう始末。しかし、弦太の趣味がバードウォッチングであることを知り、それを利用してなんとかデートに誘い出すことに成功するが……。", "2012-04-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bad Influence", "バッド・インフルエンス／悪影響", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "　マーケティング・アナリストのマイケルはアレックスと名乗る謎めいた男と知り合う。エリートでありながら悩みを抱えていたマイケルは、やがてアレックスの影響を受け始める。お陰で魅力的な女性クレアをものにすることもできたマイケル。しかしアレックスの行動は次第に過激になり、そしてマイケルはいつしか窮地に追い込まれていく……。セックス・ビデオのスキャンダルで騒がれた美形俳優ロブ・ロウがビデオを使って主人公を脅迫する悪役を演じ、話題をさらった。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORDER LINE", "BORDER LINE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "PFF2000グランプリに輝いた「青・chong」の李相日(リ・サンイル)監督が、５人の男女の姿を俯瞰的な視点と繊細な叙情が織りなす堂々たる筆致で紡ぎ出した鮮烈な劇場デビュー作。高校生、周史が乗っていた自転車がタクシーに激突。ある事件で逃走中だった周史は自宅は北海道と言い、運転手の黒崎はやむなく旅をともにする。その頃、主婦の美佐は息子のいじめや夫のリストラに対処できず、呆然としていた。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAKOB　THE　LIAR", "聖なる嘘つき・その名はジェイコブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ドイツの作家J・ベッカーの小説をR・ウィリアムズ主演で映画化。1944年、ナチス占領下にあるポーランドのユダヤ人居住区ゲットー。そこで暮らすジェイコブはある夜、ドイツ軍司令部で、ソ連軍がポーランドまで進攻してドイツ軍と交戦しているというラジオのニュースを聞く。その帰り道、彼は収容所行きの列車から脱走した少女リーナと出会い、自宅でかくまうことにする。翌日、ジェイコブは昨日聞いた朗報を二人の友人に話すが、それは瞬く間に広がり、彼がラジオを持っているという噂まで流れてしまう。ラジオを持っているだけで射殺される危険な状況の下、ジェイコブは人々に希望を与えるために、ラジオから聞いたニュースとして嘘の話をでっちあげ、伝え続けるのだが……。嘘をつき通すことで心ならずも聖なる予言者になってしまう男の行動、そして彼と少女の心温まる交流を、ヒューマニズムと辛らつなユーモアを交えながら綴った感動作。ビデオ、DVDタイトルは「聖なる嘘つき」。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE WOMEN", "若草物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "オルコットの小説の４度目の映画化作品。南北戦争下、アメリカ東北部の町コンコードでつつましく暮らす中流家庭、マーチ家。４姉妹が様々な体験を重ねながら成長していく姿を、ルロイが女性的なやわらかいタッチで描いている。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　HORSE　WHISPERER", "モンタナの風に抱かれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 167, "R・レッドフォードが初めて自分の監督作品に俳優として主演した感動の人間ドラマ。乗馬中の事故によって片足を失い、愛馬ピルグリムに大ケガを負わせてしまった13歳の少女グレース。母親アニーは心を閉ざした彼女と、事故以来人間に敵意を向けるピルグリムの運命は分かち難いものと感じ取る。ある日、アニーは雑誌で馬にささやきかけるだけで馬の心の傷を癒すことができるホース・ウィスパラー、トム・ブッカーの記事を読み、娘とピルグリムを連れて彼の牧場があるモンタナへと旅立つ。トムによってピルグリムが立ち直っていくと同時に、グレースの精神はしだいに解放され、それと並行するようにアニーはトムに惹かれていくが……。３人が出会うことによってお互いの心が癒されていく姿を、R・レッドフォードが繊細なタッチで描写していく。雄大なモンタナの自然を捉えた美しい映像が観る者の心も癒してくれる。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXPOSED", "愛と死の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "N・キンスキーの主演による異色の恋愛サスペンス。世界的なバイオリニストと恋に落ちた娘エリザベス。ところが何の因果か、テロリスト組織をめぐる陰謀に巻き込まれてしまい……。パリの街を背景に繰り広げられるサスペンスにからむ世界的バイオリニストにはR・ヌレエフ、そしてテロの組織の男にH・カイテルと、ひとクセある俳優たちの競演が見もの。H・ドカエの撮影、さらにG・ドルリューの音楽と、フランス映画ファンなら要チェックの一編。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大野一雄　ひとりごとのように", "大野一雄　ひとりごとのように", "", "", "http://ecx.images-amazon.com/images/I/51Kkwf2MfsL._SL210_.jpg", 100, "舞踏の第一人者として世界的に注目を集めてきた舞踏家・大野一雄の活動を追ったドキュメンタリー。腰を痛めて歩行不能に陥りながらも、踊りへの強烈な欲求と強靭な精神力に支えられて障害を乗りこえ、95歳の誕生日記念公演に挑む姿、さらに織部賞受賞の際に披露した圧倒的な踊りを映しだす。メガホンを取るのは、土本典昭監督の「水俣」シリーズなど数々の名作ドキュメンタリーを手がけてきた撮影監督・大津幸四郎。", "2007-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陸に上がった軍艦", "陸に上がった軍艦", "", "", "http://image.eiga.k-img.com/images/movie/34090/315x210.jpg?1425", 95, "日本映画界最高齢となる95歳の現役監督・新藤兼人が、自身の戦争体験を語ったドキュメンタリー・ドラマ。1944年、召集令状を受けて広島県の呉海兵団に入隊し、翌年の敗戦まで過酷な軍隊生活を送った新藤監督。本人による証言と、リアリズムに徹底した再現ドラマを交えながら、軍隊の不条理を描き出す。メガホンを取るのは、これまで「生きたい」「ふくろう」など新藤監督の下で助監督を務めてきた山本保博。", "2007-07-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEAVING NORMAL", "フォーエバー・ロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "夫の暴力から逃れるため家出した主婦と、バーで働く女が偶然に出会い、新たな夢を求めてアラスカに旅立つ。「テルマ＆ルイーズ」を彷彿させる、女の自立をテーマにしたロード・ムービー。こちらは「テルマ＆ルイーズ」とは違い犯罪には走らないが、地味ながら、二人の女性の心情とその成長を時にはコミカルに、そしてほろ苦く描いている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MONTH IN THE COUNTRY", "ひと月の夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "どしゃぶりの雨の降る田舎町のホームに、一人の男が降りた。戦争後遺症の吃音症に悩む青年バーキンだった。教会の壁画修復のためやって来た彼は仕事に没頭するが、そこで発掘作業を行っているムーンや牧師の妻アリスと出会い、心の交流を深めていく。C・ファースが好演。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Starship Troopers: Marauder", "スターシップ・トゥルーパーズ3", "", "", "http://image.eiga.k-img.com/images/movie/53610/315x210.jpg?3026", 93, "昆虫型宇宙生命体“バグズ”と人類との戦いを描くSFアクションの第3弾。戦況が一進一退を繰り返す中、地球連邦軍の拠点となる惑星ロク・サンの防衛基地がバグズの攻撃によって壊滅的なダメージを受ける。さらに総司令官アノーキが乗る戦艦も撃沈されてしまったため、ジョニー・リコ大佐率いる特殊部隊が救出に向かうが……。鬼才ポール・バーホーベン製作総指揮の下、シリーズ全作の脚本を手掛けたエド・ニューマイヤーが初メガホンを取る。", "2008-07-19", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("濡れた欲情 特出し21人", "濡れた欲情 特出し21人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "「四畳半襖の裏張り」などの密室劇を得意としていた神代監督の転機となった作品。時間と空間の構成がバラバラに解体され、ストリッパーたちは京都・浅草・信州・北上までを踊りまわる。スケコマシ芳介の“気の強い女は誠実やでえ”のセリフが印象的。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狂った野獣", "狂った野獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "会社をクビになった男が腹いせに宝石店を襲い、まんまと宝石強奪に成功、バスに乗り込んだまではよかったが、そのバスを銀行強盗に失敗して警察に追われている男たちが占拠してしまった。乗り合わせた乗客たちは、徐々にエゴむき出しになってきて……。追い詰められた人間たちのパニックを描くサスペンス映画。パニックものとしては多彩な要素を思いっきり詰めた、今までにない作品。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Young@Heart", "ヤング＠ハート", "", "", "http://image.eiga.k-img.com/images/movie/53700/315x210.jpg?2766", 108, "平均年齢80歳のロックンロール・コーラス隊「Young@Heart」の音楽ドキュメンタリー。1982年、アメリカ・マサチューセッツ州の小さな町ノーサンプトンに誕生したコーラス・グルー「Young@Heart」は世界中を飛び回る。そんな彼らが1年に1回のコンサートに向けて、ソニック・ユース、ラモーンズ、ボブ・ディラン、トーキング・ヘッズの曲の練習を重ねていく。", "2008-11-08", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ARBRE， LE MAIRE ET LA ME´DIATHE｀QUEOU LES SEPT HASARDS", "木と市長と文化会館　または七つの偶然", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ヌーヴェル・ヴァーグの精神を軽やかに保ち続ける巨匠E・ロメールが、“四季の物語”シリーズの合間に撮り上げたコメディ。大都市郊外の空間を好んで舞台にしてきた彼は、都市対田舎という対立軸に着目、文化会館建設の代償に樹齢百年の樹が伐採されることの是否をめぐっての政治的立場やエコロジー的観点から、大人も子供も自由に議論するさまをアクチュアルに捉える。といって、主張の正当性を急ぐ映画ではまったくなく、トータルには他愛ない偶然に左右される人間喜劇に尽きぬ味をみせる。ドキュメンタリーからミュージカル的手法までを射程に入れつつ、全カットが簡潔にしてみずみずしいという演出の離れ技。本国フランスでは総選挙２ヵ月前にゲリラ的に封切られ、口コミ効果でクリーン・ヒットとなった。DVDは「エリック・ロメール コレクション(6)」に収録。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("特捜戦隊デカレンジャー THE MOVIE フルブラスト・アクション", "特捜戦隊デカレンジャー THE MOVIE フルブラスト・アクション", "", "", "http://ecx.images-amazon.com/images/I/51ty4iThgML._SL210_.jpg", 39, "宇宙の犯罪者から人々を守る5人の若き刑事の戦いを描いたテレビシリーズ「特捜戦隊デカレンジャー」の劇場版。地球に侵入したアルゴル星の犯罪集団「ガスドリンカーズ」を追っていたデカレンジャーたち。そんな中、捜査でエイリアン・バーを訪れたバンは、美しい歌姫マリーと出会う。実は彼女は、ガスドリンカーズがばらまいたウィルスにより人々が機械奴隷化されてしまったレスリー星からやって来たスペシャルポリスだった。", "2004-09-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PALE RIDER", "ペイルライダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "ゴールドラッシュにわくカリフォルニアを舞台に、鉱山の利権をめぐる地方ボスとヒーローの闘い。弱い者の味方として、馬にまたがって登場するのが、イーストウッド。やや暗い画面ながらロケ効果が生かされたイーストウッド版“シェーン”だ。地方ボスの“むき出しの権力”に対し、あまりにも無力な開拓者たち。業を煮やしたイーストウッドが、俺が相手だとばかりにスゴ腕を発揮し、敵のツワモノを倒していく。悪者の一人として大男R・キールも登場。イーストウッドとしては「アウトロー」（1976）以来久しぶりの西部劇主演で、かつ監督も兼ねた入魂の一編。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAKED SPACE", "レスリー・ニールセンの裸の宇宙銃〈スペースガン〉を持つ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "L・ニールセンが“裸の銃〈ガン〉を持つ男”シリーズ以前に主演したSFコメディ。彼扮するジャームズ船長が率いる宇宙船のクルーたちとゼリー状の謎の異星人との攻防が、馬鹿馬鹿しく展開されていく。「エイリアン」をはじめ、「ダーティハリー」などの映画のパロディーが次々と登場。チープな特撮が笑いを誘う。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時計 Adieu I'Hiver", "時計 Adieu I'Hiver", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "５年という長い歳月をかけて、スケート選手を目指す一人の少女の成長と、それを見守る周囲の人々の人間模様を描いた倉本聰の話題作。主演・中嶋朋子の実際の成長に合わせて作品を撮影、金子由香利のシャンソンをはじめ、倉本自身が選んだ音楽も聴きもの。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Roi et L'Ouiseau", "王と鳥", "", "", "http://image.eiga.k-img.com/images/movie/33580/315x210.jpg?4585", 87, "宮崎駿監督や高畑勲監督も多大な影響を受けたというフランス初の長編アニメーション映画「やぶにらみの暴君」を、ポール・グリモー監督自身が20年以上の歳月をかけて改作。孤独な暴君シャルル16世が治めるタキカルディ王国。王が住む宮殿の最上階には、3枚の絵が飾られていた。美しい羊飼いの娘と煙突掃除の青年、そして王の肖像画。愛し合う娘と青年は、仲を引き裂こうとする王から逃げるため、ある晩絵の中から脱け出すが……。", "2006-07-29", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Guilty by Suspicion", "真実の瞬間〈とき〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ハリウッドに悪名高い“赤狩り”（レッドパージ）について、映画人の立場から描いた一編。1951年のハリウッド。売れっ子監督のデビッド・メリルは、20世紀フォックス社の社長ダリル・F・ザナックに呼ばれフランスから帰国した。しかしその時すでに彼は、友人の脚本家がHUAC（下院非米活動委員会）に召喚された際、メリルの名を挙げていたため、ブラックリストに載せられてしまっていた。ザナックの忠告に従い弁護士に会うメリル。だが、弁護士の言葉に納得できない彼は、HUACの喚問において、法廷侮辱罪で投獄されると知りつつも、自己に忠実であろうとする……。R・デ・ニーロは役作りにあたって、“赤狩り”で自殺にまで追い込まれた悲劇の名優ジョン・ガーフィールドをモデルにしているようだ。また、メリルの友人の映画監督（ジョゼフ・ロージーとチャップリンを一部モデルにしている）役として、マーティン・スコセッシが顔を見せている。しかし、ラストで主人公を悲劇のヒーローに祭りあげてしまうところなど、詰めの甘さは否めない。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alexander", "アレキサンダー", "", "", "http://image.eiga.k-img.com/images/movie/1250/315x210.jpg?3381", 173, "紀元前4世紀、古代マケドニアの英雄アレキサンダー大王の生涯を、製作費200億円を投じてオリバー・ストーン監督が映画化。マケドニアの王子アレキサンダーは、20歳で即位して以来、ギリシャ、ペルシャをはじめ遠くアジアまでも遠征の旅を続けて壮大な帝国を築いていくが、その心境はどのようなものだったのか。語り手役でアンソニー・ホプキンス、主人公の友人役でジャレッド・レト、ジョナサン・リース・マイヤーズらが共演。", "2005-02-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MUPPETS TAKE MANHATTAN", "マペット めざせブロードウェイ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "人気TV番組『セサミストリート』のキャラクターたちが大活躍するミュージカル・ファンタジー。田舎町では評判の、青ガエル“カーミット”率いる劇団が成功を夢見てニューヨークにやって来る。ところが、どこからもお呼びがかからず、一同は意気消沈……。B・シールズなど豪華スターを据えて、楽しい夢を繰り広げる。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PURE　DANGER", "ピュア・デンジャー　追撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "壮絶なカーチェイスや銃撃戦が展開するハード・アクション。マフィア抗争のさなかに、500カラットのダイヤモンドを持ち逃げしたギャングが、場末の食堂で息絶えた。食堂で働くジョニーは、このダイヤを奪って逃走。そんな彼にマフィアの追っ手が迫る……。監督を兼任したC・T・ハウエルのタフな熱演も見どころ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Film Socialisme", "ゴダール・ソシアリスム", "", "", "http://image.eiga.k-img.com/images/movie/55820/315x210.jpg?2492", 102, "2010年12月で80歳になった鬼才ジャン＝リュック・ゴダールによる3部構成の映像コラージュ。地中海を航行する豪華客船内の人間模様（第1章「こんな事ども」）、選挙に立候補しようとするフランスの子供たちと彼らの日常を追うTVクルーの取材（第2章「どこへ行く、ヨーロッパ」）、そしてエジプト、オデッサ、パレスチナ、バルセロナなどを訪問しながら人類史を紐解く物語（第3章「われら人類」）が、鮮烈な映像と凄まじい音響によって綴られる。ロック歌手のパティ・スミス、ギタリストのレニー・ケイら個性的な面々が出演。", "2010-12-18", "26,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wind That Shakes The Barley", "麦の穂をゆらす風", "", "", "http://image.eiga.k-img.com/images/movie/1550/315x210.jpg?3955", 126, "「大地と自由」「ケス」などで知られる社会派ケン・ローチ監督が1920年代のアイルランド紛争を描いた悲劇。英国による長年の支配から独立を求める声が出始めた1920年アイルランド。医者を志すデミアンと兄テディは武器を手に取り独立戦争に出征する。やがて勝利を収め、講和条約を締結させたアイルランド軍だったが、その条約の内容を巡って、今度は国内で争いが起こる……。06年度カンヌ映画祭パルムドール受賞作品。", "2006-11-18", "33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAS ZWEIT GESICHT", "セカンドフェイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "前世の記憶に苦しむヒロインの数奇な運命をサスペンス・タッチで描く。19世紀末、冬のドイツのあるアパートで若い娘が自殺を遂げる。それから90年後の現在、書店で働くアンナは、自分の中に別の誰かがいて、その記憶のイメージに苦しめられていた……。", "1982-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワンルーム・ストーリー", "ワンルーム・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "都会で暮らす女性の恋愛模様を描いた同名漫画の映画化。猫をきっかけとする同じマンションに住む二組のカップルの恋愛模様を描く「あ・い・た・い」、アナウンサーを目指すヒロインと、彼女の面倒を見る“ハウスくん”との恋の行方を描く「ディスタンス」、事故で記憶喪失になった女の子と加害者の青年との奇妙な同居生活を描く「いかしたベイビー」。個性的な活躍を続けるそれぞれの役者が監督・主演に挑んだ異色オムニバス。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・いじわる大障害", "喜劇・いじわる大障害", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "祖父の代から、三遊亭円朝の芸を伝える三遊派宗家の後継者、藤浦敦の監督第１作。藤浦監督は、一方で立川談志の新作落語なども執筆していたことから、落語界を中心に、喜劇人が大挙出演した。物語は、東京でひと旗あげることを夢見て、田舎から出てきた青年、猪狩次郎が、失敗と成功を繰り返しながら、愛する春子とゴールインするまでを描いたもの。立川談志が監修にあたっている。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユッコの贈りもの　コスモスのように", "ユッコの贈りもの　コスモスのように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1980年８月７日に急性白血病のため、わずか13歳４ヵ月の短い生涯を終えた鹿村由起子、通称ユッコの入院中の交換日記をもとに映画化された感動作。ユッコ役の新人・大島明美の純朴なさわやかさと健気さが印象的だ。母親役には、宮本信子が扮している。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("気違い部落", "気違い部落", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "きだみのるの小説『気違い部落周遊紀行』を菊島隆三が脚色、渋谷実が演出した。東京近郊の奥多摩山村に、株屋と高利貸の二人のボスに牛耳られている“気違い部落”と呼ばれる村がある。農民はやせた狭い土地にひしめいていた。そんななかで、土地問題から村八分にされた鉄次の一家が村に敢然と反抗する……。日本の農村社会の前近代的な構造を取り上げ、前半では戯画化し後半でやや深刻に描いている。伊藤雄之助がガンコ者の親爺を好演。渋谷実は、大船調の風俗喜劇に社会派の色あいを持たせた上で、農村のファルス劇を作り上げた。いつもの名調子で解説する森繁久彌の語りも聞きもの。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zack and Miri Make a Porno", "恋するポルノ・グラフィティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「40歳の童貞男」「グリーン・ホーネット」のセス・ローゲンが主演のコメディ。幼なじみのザックとミリーは、ルームメイトとして仲良く暮らしていたが、ザックが家賃を滞納したせいでアパートの電気と水道が止められてしまう。そこで2人は、生活費を捻出するためにポルノ映画を撮ってひと儲けしようと計画する。さっそく「スター・ウォーズ」のパロディ・ポルノの撮影を始めた2人だが、セックスシーンを演じることでこれまでの友情関係が微妙に変化していく。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Touchez pas au grisbi", "現金に手を出すな", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "フランスの名匠ジャック・ベッケル監督によるギャング映画の傑作。初老のギャング、マックスと相棒のリトンは、パリの飛行場から5千万フランの金塊を強奪することに成功する。2人はこの仕事を最後に暗黒街からの引退を考えていたが、リトンがナイトクラブの女にうっかり口をすべらせたことから、麻薬密売のボス、アンジェロに目をつけられる。ジャン・ギャバンが初老のギャングを見事に演じ、ベネチア映画祭主演男優賞を受賞。", "1954-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODY SNATCHERS", "ボディ・スナッチャーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "SFホラーの古典といわれるJ・フィニーの小説の３度目の映画化。フォート・デイリー米軍基地に、科学者として働くことになった父とともに引っ越してきたマーティーと弟のティム、義母のキャロル。新しく生活を送る基地は、何やら不穏な空気に包まれていた。そしてバスルームでうたた寝するマーティーは、不気味な植物に襲われる。慌ててかけ込んだ父の寝室で見たのは、植物に覆われ瀕死の父と、その横のもう一人の父の姿だった。眠っている人間の精気を吸い取り体を乗っ取る植物に、基地のほとんどの住民が侵されていたのだ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("侠骨一代", "侠骨一代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "富沢有為男の小説を映画化したマキノ雅広監督の任侠映画。芝浦を舞台に繰り広げられる組同士の血で血を洗う抗争を高倉健主役で描く。マキノならではのメリハリの効いた演出が見事。また、亡き母と彼を救う芸者の二役を演じる藤純子は花のように美しい。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真夜中からとびうつれ", "真夜中からとびうつれ", "", "", "http://image.eiga.k-img.com/images/movie/57110/315x210.jpg?2656", 13, "「ウルトラミラクルラブストーリー」の横浜聡子監督が、季刊雑誌「真夜中」の映画特集の連動企画としてWEBで期間限定公開した短編映画。何もないところから「映画」が生まれる瞬間を、独自の感性で描き出していく。主人公の少女を演じる多部未華子、共演の宇野祥平のほかにも、鈴木晋介、戌井昭人、渡辺謙作、鈴木卓爾ら、作家、映画監督、脚本家など個性的な面々が出演している。", "2011-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DESPERADOES", "無頼漢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "男たちの熱いハートがぶつかり合う快作ウエスタン。西部のならず者である主人公が、叔父を頼ってある町にやって来たが、叔父は銀行家とつるんで住人から金を巻き上げる悪党だった。その悪事を知った時、彼は命を狙われる。その上、仲間が銀行強盗をはたらき……。名作『武器よさらば』のC・ヴィダー監督がメガホンを取っている。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Black Water", "ブラック・ウォーター", "", "", "http://image.eiga.k-img.com/images/movie/54660/315x210.jpg?4146", 89, "オーストラリアで実際に起きた事件をモチーフに、巨大クロコダイルに襲われた若者たちの恐怖を描いたサバイバルホラー。休暇で北オーストラリアを訪れたグレース＆リー姉妹とグレースの恋人アダム。川釣り体験ツアーに参加した彼女たちは広大なマングローブの沼地に入り込むが、突然ボートが転覆して3人とも川の中に投げ出されてしまう。何とかマングローブの木に這いあがったグレースとアダムは、川の中に凶暴なクロコダイルが潜んでいるのを発見する。", "2009-08-22", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RETURN OF FRANK JAMES", "地獄への逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ヘンリー・キング監督の「地獄への道」の続編。前作が西部のならず者ジェシー・ジェームスの半生を描いていたのに対し、本作は殺された弟ジェシーの仇を討つため、兄のフランク・ジェームスが裏切者フォード兄弟を追い詰めるという物語。アクションよりも人間関係に重点を置いたF・ラングの異色西部劇。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高校生番長・ズベ公正統派", "高校生番長・ズベ公正統派", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "松坂慶子が準主役ながら映画デビューを果たした作品。模擬試験の集団ボイコットに失敗した不良グループを尻目に“非処女同盟”をつくり意気上がるスケ番グループ。気の弱い優等生と清純派高校生の恋を成就させようと大活躍……。高校生の不良ぶりがとても可愛いい。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMOS＆ANDREW", "アモス＆アンドリュー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "黒人であるというだけで泥棒と間違えられた著名作家アンドリュー。気付いた時にはすでに遅く、警官隊が彼の家を包囲。不祥事を揉み消すべく、警察署長は獄中のコソ泥アモスを犯人に仕立てようとするが……。ジョナサン・デミがノンクレジットで製作総指揮を務めたブラック・コメディ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本の悲劇", "日本の悲劇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "戦争未亡人の春子は、熱海の旅館で働いている。敗戦後の混乱のなかで彼女は歌子と清一という二人の子供を抱え、かつぎ屋や怪しげな女としての商売までやらねば生きていけない。おまけに唯一の財産だった土地も義兄夫婦に騙し取られる。時がたち、歌子は英語塾に通い、清一は医科大学に進学。春子には二人の将来がなによりの生きがいだった。しかし、春子の思いとは裏腹に子供たちは母に冷たくあたり、ついに春子は失望のあまり自殺を図る。木下惠介には珍しいリアリズム色濃い作品。春子をとことん追いつめていく木下惠介の厳しい演出は見事である。望月優子の力演と桂木洋子の可憐さが印象的。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吸血蛾", "吸血蛾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "横溝正史の同名怪奇推理小説を中川信夫監督が映画化し、池部良が名探偵・金田一耕助に扮した。一流ファッション・デザイナー浅芽文代のもとに、狼男から歯型のついたリンゴの入った箱が届けられ、モデルたちが次々と殺されていく……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("がんばっぺ　フラガール！　フクシマに生きる。彼女たちのいま", "がんばっぺ　フラガール！　フクシマに生きる。彼女たちのいま", "", "", "http://image.eiga.k-img.com/images/movie/57220/315x210.jpg?4449", 102, "映画「フラガール」（2006）で誕生秘話が描かれたことでも知られる福島県いわき市のレジャー施設「スパリゾートハワイアンズ」が、2011年3月11日の東日本大震災を受け、46年ぶりにフラガールたちの全国キャラバンを敢行。その行方を追ったドキュメンタリー。地震、津波、原発事故と風評被害により苦境に立たされるスパリゾートハワイアンズ所属のフラガールたちが、復興に向けて全国に笑顔を届けるため「フラガール　全国きずなキャラバン」に出発する。「フラガール」の蒼井優がナレーションを、ウクレレの第一人者ジェイク・シマブクロが音楽を担当。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カレンダー if just now", "カレンダー if just now", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "70歳をすぎ、50年ぶりに再会した恋人同士が、ひょんなことから20代の肉体に逆戻り。突然、青春の真っただ中に放り出された老カップルの戸惑いをユーモラスに描いたロマンティック・ラブ・ストーリー。若返った老女と、その孫娘の２役を演じる川越美和がコメディエンヌぶりを発揮する好編。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WRITTEN ON THE WIND", "風と共に散る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "石油会社の社員ミッチは、道楽者であるが親友の若社長カイルに傍系会社の秘書ルシイを紹介する。カイルは彼女に一目惚れし、二人はやがて結婚する。彼女をひそかに愛していたミッチはショックを受けるが、カイルの道楽癖が収まるのを見て、これでよかったと納得するのだった。一見平和に思えた新婚生活だったが、ルシイの不妊がもとでカイルは酒びたりの日々に。やがて家庭と友情の双方に亀裂が入り始める……。ハリウッド1950年代のメロドラマでは右に出る者のなかったD・サークが、その力量のすべてを尽くして撮り上げた彼の代表作。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Waitress", "ウェイトレス　おいしい人生のつくりかた", "", "", "http://image.eiga.k-img.com/images/movie/1900/315x210.jpg?3564", 108, "田舎町のダイナーで働くウェイトレスの人生をユーモラスに綴り、小規模作品ながら全米で大ヒットを記録したハートウォーミング・ストーリー。女優として活躍してきたエイドリアン・シェリーが監督・脚本を手掛け、彼女の遺作となった。パイ作りにかけては天才的な腕前を持つウェイトレスのジェンナは、私生活では嫉妬深い夫アールに悩まされ、密かに家出を計画していた。そんなある日、彼女はアールの子を身ごもっていることに気付く。主演は「M:i:III」のケリー・ラッセル。", "2007-11-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENTRE CIEL ET TERRE", "天と地の間に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "山の詩人とも哲学者とも呼ばれ、世界的名声を持つ登山家G・レビュファが手掛けた長編山岳ドキュメンタリー映画。雄大なアルプス山脈の景観が、様々なカメラ・アングルから見事に捉えられている。山脈の積雪、氷河、そして登山の模様などが、著名な山岳カメラマン、テーラによって十分に収められた作品。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Missing", "ミッシング", "", "", "http://ecx.images-amazon.com/images/I/51oOFqDCliL._SL210_.jpg", 137, "「ビューティフル・マインド」のR・ハワード監督が描く家族の再生物語。愛娘を米国原住民に誘拐された主人公が、断絶していた父親と協力して取り戻そうとする。主役にC・ブランシェット、彼女と20年間別居していた父親にT・L・ジョーンズが扮し、親子の苦悩と、再生への過程をスリリングかつ繊細に描写する。1885年、米国。荒野に建つ一軒家で、女医マギーは女手一つで二人の娘を育てていた。ある日、アパッチ族と暮らす道を選んだ父ジョーンズが、20年ぶりにマギーの元に帰って来るが、母も失い、苦労を重ねた彼女は父を追い返す……。", "2004-05-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Super", "スーパー！", "", "", "http://image.eiga.k-img.com/images/movie/56300/315x210.jpg?1369", 96, "冴えない夫のフランクは、セクシーなドラッグディーラーの後を追って家を出てしまった妻を取り戻すため、自前のコスチュームを身にまとったお手製ヒーロー「クリムゾンボルト」に変身。クレイジーな相棒ボルティーとともに危険地帯の犯罪に立ち向かう。レイン・ウィルソン主演、エレン・ペイジ、リブ・タイラー、ケビン・ベーコンが共演するアクションコメディ。監督は「ドーン・オブ・ザ・デッド」の脚本で注目され、SFホラー「スリザー」でデビューしたジェームズ・ガン。", "2011-07-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GATTACA", "ガタカ", "", "", "http://ecx.images-amazon.com/images/I/51cXjCzXL6L._SL210_.jpg", 106, "「トゥルーマン・ショー」の脚本家としても注目されたニュージーランドの新鋭A・ニコルの監督デビュー作。優秀な遺伝子を組み合わせて生まれた“適正者”たちが支配する近未来。自然出産による“不適正者”の青年ヴィンセントは宇宙飛行士になる夢を叶えるため、下半身不随になって夢を絶たれた“適性者”ユージーンと取引をし、彼になりすまして宇宙局“ガタカ”に入社する。ひんぱんに行われる化学検査をくぐり抜け、彼はついに探査船の航海士の座を射止めるが、ある日ガタカで起こった殺人事件で嫌疑をかけられ……。個人よりもDNAを優先する未来社会で夢を追い続ける主人公の苦悩と恋の行方は、青春映画のみずみずしさが鮮烈にあふれて感動的だ。「ピアノ・レッスン」のM・ナイマンによる哀切な音楽も物語をドラマティックに盛り上げている。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聖戦／MONK’S FIGHT", "ドラゴン忍者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "中国のある寺から高僧の遺灰を納めた舎利が盗まれた。寺で修行中のチュンキは、仏門と縁を切り、ドラゴン忍者となって、舎利を盗んだフー一味を追う。香港映画ならではのサービス満点の扮装やアクション描写が見もの。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こんばんは", "こんばんは", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "2003年度のキネマ旬報ベストテンで見事、文化映画第１位に輝いた感動のドキュメンタリー。舞台となるのは、山田洋次監督の「学校」のモデルにもなった教員が教壇に立つ、墨田区立文花中学の夜間学級。そこで交わされる、年齢、職業を越えた生徒たちの温かな心の触れ合いや、教師との信頼関係を捉えるとともに、鋭い視点で“学ぶ”ことの本質に迫る。墨田区にある文花中学の夜間学級では、16歳から92歳までの幅広い世代の生徒たちが学んでいる。生徒たちの熱心な取り組みに、ほとんどの教員が授業の終わりのチャイムを聞くと、“もう時間か”と嘆くほどだった。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Visages", "ヴィザージュ", "", "", "http://image.eiga.k-img.com/images/movie/55040/315x210.jpg?4703", 141, "国際的にも高い人気を誇る台湾のツァイ・ミンリャン監督が、自身が敬愛するヌーベルバーグの名匠フランソワ・トリュフォーにオマージュを捧げた意欲作。「サロメ」をモチーフにした映画を製作するためルーブル美術館にやって来た台湾人監督を主人公に、撮影中に起こる様々な出来事を幻想的なタッチで描き出す。キャストにはジャン＝ピエール・レオ、ファニー・アルダン、ジャンヌ・モローらトリュフォーが愛したスターたちが集結。", "2009-11-22", "2,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("E´GLANTINE", "小さな約束", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "J・C・ブリアリの監督デビュー作。フランスの寄宿学校に通う少年レオポルは、夏休みで実家に帰る。優しい祖母や従妹のポーリンとの楽しい日々が始まるが……。美しい田園風景の中でたわむれる祖母と少年の姿を映したカメラの冴え、ひと夏の少年の記憶をみずみずしく切りとった演出が光る。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Serious Man", "シリアスマン", "", "", "http://image.eiga.k-img.com/images/movie/55320/315x210.jpg?1645", 106, "「ノーカントリー」のジョエル＆イーサン・コーエン監督が、不幸にばかり見舞われる中年男の姿をブラックユーモアたっぷりに描いたコメディ・ドラマ。舞台は1967年のアメリカ中西部。平凡で真面目な人生を歩んできた大学の物理学講師ラリーは、ある日突然、長年連れ添った妻から別れ話を切り出されてしまう。それ以来、周囲では想像を絶するやっかいな出来事が次から次へと起こりはじめる。", "2011-02-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・極道三国志２　伊豆代理戦争勃発", "新・極道三国志２　伊豆代理戦争勃発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "極道に生きる男の姿を描いた任侠ドラマのシリーズ第２作。深大寺一家の跡目を、松岡が正式に襲名する。しかし襲名披露の席上で、伊豆連合会会長で稲鳥組の稲鳥が何者かに襲われる。ちょうどその頃、伊豆では大型事業が進められていた。関西・黒田組の陰謀渦巻く中、龍介、健、銀次の三人は黒田組に立ち向かっていくが…。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("尋槍！", "ミッシング・ガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「鬼が来た！」の監督・主演を務め、国際的に高い評価を得たチアン・ウェンの主演によるサスペンス。彼の人間味あふれる妙演に加え、新鋭ルー・チューアンの若々しい演出も見もの。妻子持ちの警官マーは、ある朝、拳銃の紛失に気付き大慌て。最後に拳銃を見たのは、前夜、妹の結婚式に出席したときだった。泥酔していた昨夜の記憶を取り戻せず、マーは出席者を訪ね歩いて情報を求め、やがて意外な人物が列席していたことを知る。", "2001-01-01", "8,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jalainur", "ジャライノール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ロシアと国境を接する内モンゴル自治区、ジャライノール。その炭鉱で古びた蒸気機関車を動かす機関士のジュー・ヨウシアンと後輩リー・ジーチョンは、仕事以外の時間もいつも一緒に過ごしていた。ある日、30年間働き続けたジューは娘夫婦と暮らすために炭鉱を去るが、リーもまたその後を追って旅に出る。長編第2作となる中国の新鋭チャオ・イエ監督が、“蒸気機関車最後の聖地”と言われるジャライノールの美しい風景とともに描きだす人間ドラマ。", "2011-01-15", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vanilla Sky", "バニラ・スカイ", "", "", "http://image.eiga.k-img.com/images/movie/1210/315x210.jpg?3724", 120, "主演・製作のトム・クルーズがアレハンドロ・アメナバール監督の「オープン・ユア・アイズ」に惚れ込み、「ザ・エージェント」で組んだキャメロン・クロウ監督でリメイク。クルーズが、オリジナル作にも出演したペネロペ・クルスと本作で出会い、交際中なのも話題。ニューヨークの大手出版社の株主デビッドは、富と美貌を享受するプレイボーイ。しかし、交通事故で醜い顔になり、生活が激変。しかも彼の周囲で、奇妙な出来事が起こり始める。", "2001-12-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lovely Bones", "ラブリーボーン", "", "", "http://image.eiga.k-img.com/images/movie/53190/315x210.jpg?3388", 135, "アリス・シーボルトのベストセラー小説「ラブリー・ボーン」を、「ロード・オブ・ザ・リング」のピーター・ジャクソン監督が映画化したファンタジードラマ。家族と幸せに暮らしていた14歳の少女スーザンは、ある日何者かに殺される。この事件をきっかけに家族は崩壊していくが、その様子をスーザンは天国から見守り、決して届かない声を家族にかけ続けていた……。出演はマーク・ウォールバーグ、レイチェル・ワイズ、「つぐない」のシアーシャ・ローナン。", "2010-01-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使のウィンク 日光猿軍団", "天使のウィンク 日光猿軍団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "日光猿軍団の猿の良太は、ある夜、神様から“本当は人間に生まれるハズだったが、天使のミスで魂が入れ替わった。今は10歳になっている少年を探し出せ”と告げられる。いったい、誰が僕なんだ？　猿の良太の人間世界への冒険、見ること聞くこと初体験の旅が始まる。ユーモアと感動あふれる冒険ファンタジー。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("12人の優しい日本人", "12人の優しい日本人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "“もしも日本に陪審員制度が導入されたら？”という架空の設定で、12人の陪審員が殺人容疑者の判決をめぐって議論を繰り広げていくうちに混乱に陥っていく姿をユーモラスに描いたコメディ。「櫻の園」で高い評価を得た中原俊が再びアルゴで撮り上げた新作で、「櫻の園」でも見せた一つの場所で演じられるリアルタイムの群像劇という手法をさらに推し進めている。平凡な人々による陪審が次第に非凡な領域に流れていく過程が面白く描かれている点にも注目。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Boat", "ノーボーイズ，ノークライ", "", "", "http://image.eiga.k-img.com/images/movie/53900/315x210.jpg?3055", 114, "日韓の人気若手俳優、妻夫木聡とハ・ジョンウが共演する青春ドラマ。「ジョゼと虎と魚たち」の渡辺あやが脚本を担当。オンボロのボートで韓国から日本へ密輸品を運ぶヒョングは、日本で成功したボギョンおじさんに荷物と手作りキムチの壺を定期的に届けている。ある日、荷物の受取人である、ボギョンおじさんの親戚の青年・亨が遅れ、船酔いしたヒョングは謎の女に海に引きずり落とされてしまう。目が覚めたヒョングは、キムチの壺に麻薬を隠す亨を見て、自分が麻薬の運び屋をやらされていたことに気付く。", "2009-08-22", "76,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O Caminho das Nuvens", "Oi ビシクレッタ", "", "", "http://image.eiga.k-img.com/images/movie/33690/315x210.jpg?2411", 85, "ブラジル北部からリオ・デ・ジャネイロまで自転車で3,200kmを旅した実在の家族を映画化したロード・ムービー。失業中のトラック運転手ロマンは、月1,000レアル稼ぐことの出来る仕事に就くため、リオ・デ・ジャネイロへ行くことを決意する。彼は妻ローゼと5人の子どもたちを連れて自転車で出発するのだが……。一家の父親ロマンを演じるのは、ウォルター・サレス監督作「ビハインド・ザ・サン」のバグネル・モーラ。", "2006-10-14", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lo straniero", "異邦人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "　1942年に発表されたカミュの「異邦人」を、ネオレアリズモの先駆者として知られるルキノ・ビスコンティが映画化。会社員のムルソーのもとに、母の死の知らせが届く。葬儀で涙も流さない彼は、翌日、元同僚の女性と喜劇を観に行き夜を共にする。彼にとって現実は、ただただ通り過ぎていく時間でしかなかった。ビスコンティ監督作としては評価が分かれる作品だが、生きることに無関心な中年男をマルチェロ・マストロヤンニが好演している。", "1968-09-21", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISS　OR　KILL", "キス・オア・キル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "オーストラリア・アカデミー賞で５部門を受賞したサスペンス。バーで出会った男たちから金をだまし取って生計を立てているニッキとアル。ある日、いつものようにニッキが男をホテルに誘い込むが、薬の過剰摂取で死なせてしまう。さらに、二人は男の所持品から、元フットボール選手と少年のいかがわしい行為を録画したテープを発見。危険を感じた二人は街を抜け出し、ナラーボー平原へと逃亡するのだが……。", "1997-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUTCH AND SUNDANCE: THE EARLY DAYS", "新・明日に向って撃て！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "アメリカン・ニュー・シネマの代表作となった異色ウエスタン「明日に向って撃て！」の中で鮮烈な最後を遂げた名コンビ、ブッチとサンダンスの“若き日々”にスポットを当てた一編。若手俳優の溌刺とした好演がレスター監督の手堅い演出を支える。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MASKED GIRL 女子高生は改造人間", "THE MASKED GIRL 女子高生は改造人間", "", "", "http://image.eiga.k-img.com/images/movie/53800/315x210.jpg?2485", 0, "ごく普通の女子高生だった星野愛は、悪の化身ブラックマリア率いる悪の秘密組織ジョーカーに手術され、改造人間にされてしまう。ジョーカーの追っ手に狙われる愛は、親友の片桐由美に悩みを相談するが、由美は改造でジョーカーへの忠誠を誓うように洗脳されていた……。押井守の「アヴァロン」などで助監督を務めてきた金子功が長編監督でビュー。主演はアイドルグループ「美少女クラブ31」の清水由紀と中村静香。", "2008-08-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今、愛する人と暮らしていますか？", "今、愛する人と暮らしていますか？", "", "", "http://image.eiga.k-img.com/images/movie/53640/315x210.jpg?2682", 116, "建設会社取締役のヨンジュンとインテリアショップを経営するソヨンは誰もが羨む成功者カップルだが、その関係は冷め切っていた。一方、ファッションアドバイザーのユナとホテリアーのミンジェは結婚3年で倦怠期を迎えている。2組のカップルは偶然にもお互いの相手と出会い、それぞれ恋に落ちてしまい……。日本でも大人気の韓流スター、イ・ドンゴンと「僕の、世界の中心は、君だ。」のチョン・ユンス監督がタッグを組んだ大人のラブストーリー。", "2008-05-25", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROSEMARY'S BABY", "ローズマリーの赤ちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "オカルト映画ブームのはしりとなった異色ホラーの傑作。悪魔主義者の儀式によって、知らないうちに悪魔の子を産ませられるというショッキングな内容が話題をさらった。舞台は現代のニューヨーク。売れない俳優のガイとその妻ローズマリーが、マンハッタンの古いアパートに越してくる。となりは親切だがおせっかいな老夫婦。ある日彼らの養女が窓から飛び降りて自殺した。やがて妊娠したローズマリーは悪夢にうなされ、身体もやつれていくのだが……。現代ニューヨークの一角に、魔族がひそかに暮らしているという設定が、なんとも不気味。悪魔の子供を妊娠させられたのではと、恐怖におののく主人公役のM・ファローが鬼気迫る演技をみせた。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LAST　STOP", "スノー・ステーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "コロラド州を大寒波が襲い、ロッキー山中のモーテルで10人の男女が足止めを食らう。やがて一組の男女の死体と大金入りのバッグが見つかり、現場に居合わせた新米警官ジェイソンが捜査を開始。彼は客の中に、逃亡中の銀行強盗犯が紛れていることを知る。雪に閉ざされたモーテルを密室に見立て、疑惑と欲望が渦巻く人間模様を描いたサスペンス・ミステリー。", "2000-01-01", "10,1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WRONG IS RIGHT", "シークレット・レンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "世界を股にかけてホット・ニュースを追う全米No.1のキャスター。彼は、中東を取材中に死の武器商人によって原爆が売買されている事実を突き止める。「冷血」などで常に問題作を発表しているブルックス監督が、世界各国にロケを敢行し、タイムリーな主題を追った力作。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES AVENTURIERS", "冒険者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "アンリコ監督の名を永遠のものにした青春レクイエムの名作。ドロン、ヴァンチュラ、シムカスの３人組は、夢と冒険を目指して、アフリカの海底に眠る財宝探しに旅立つ。ところが、宝を狙う一味に襲われて……。真っ青な海面に白波をたてながら沈んでいく、シムカスの埋葬シーンが忘れられない。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犯人に願いを", "犯人に願いを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "カメラマンの瀬島が突然、意識不明となり、その場にいたスタイリストのあゆみが参考人として室町署に呼ばれる。担当するのは激しい思い込みと正義感から罪もない一般人を次々と罪に陥れる“誤認”の田中と、“予断”の加藤の二人の刑事。彼らは任意の名目で、時間無制限の事情聴取を始める。しかし、それは心理的拷問と言うべきものだった……。「シャブ極道」の細野辰興監督が、狂気が生み出す極限状況に迫る。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sweetie", "スウィーティー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "度を超してわがままで狂暴な姉スウィーティーと、彼女を“悪魔”と呼んで忌み嫌う自閉症気味の妹ケイ。まったく正反対なこの姉妹の衝突の日々をドライかつシニカルなタッチで描き出す。「エンジェル・アット・マイ・テーブル」「ピアノ・レッスン」で世界的に評価された女流監督J・カンピオンが、無名時代に実験的なスタイルで撮り上げた処女長編デビュー作。", "1989-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COME BACK，LITTLE SHEBA", "愛しのシバよ帰れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "かわいがっていた子犬のシバが行方不明になって以来、はかない昔の思い出を追うばかりで、現在の生活を築いていこうとしない妻のローラとアルコール中毒から立ち直るため、禁酒同盟に入って更生を誓っている夫のドン。そんな夫婦のもとに、ある日、女学生のマリーが下宿人としてやってくる。若々しく明るいマリーの姿は、ローラに再び青春の夢を追わせるのだった。舞台でもローラを演じた、S・ブースの名演が光る。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hobo with a Shotgun", "ホーボー・ウィズ・ショットガン", "", "", "http://image.eiga.k-img.com/images/movie/57240/315x210.jpg?1282", 85, "ロバート・ロドリゲスとクエンティン・タランティーノの「グラインドハウス」のために作られたフェイク予告編を長編映画化したバイオレンスアクション。初老の流れ者ボーボーは、巨大な犯罪組織が牛耳る街で暴力に屈し、無気力にみじめな日々を送っていた。そんなある日、訪れた質屋で強盗に出くわしたボーボーは、反射的に店内にあったショットガンで強盗を射殺。これをきっかけに再び悪に立ち向かう決意をし、次々と犯罪者を血祭りにあげていく。監督はカナダ出身のジェイソン・アイズナー。主演に「ブレードランナー」のルトガー・ハウアー。", "2011-11-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KARAKURI", "KARAKURI", "", "", "http://image.eiga.k-img.com/images/movie/57540/315x210.jpg?2510", 20, "自主制作映画「キヲクドロボウ」（2007／山岸謙太郎共同監督）が第11回上海国際映画祭パノラマ部門で入選するなど高い評価を受けた石田肇監督が、水野美紀を主演に迎えて描いた短編SFアクション。過去に事故で恋人と片腕を失い「カラクリ」と呼ばれる義手をつけた男が、夜の京都で謎の女と戦う姿を、着物や刀など和のテイストをふんだんに盛り込んで描く。「キヲクドロボウ」で石田と共同監督を務めた山岸謙太郎監督の新作「イヤータグ」と同時上映。", "2012-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社葬", "社葬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "大手新聞社のトップが急死し、次期社長の座をめぐる社内抗争がスキャンダラスに展開。クライマックスの社葬でその争いの終結を迎える、大企業版の「仁義なき戦い」。緒形拳扮する叩き上げの販売局長が、あくまでも派閥にくみせずに、バイタリティーあふれる行動力で、大物たちに立ち向かっていく。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Dream Comes True", "石ころの夢", "", "", "http://image.eiga.k-img.com/images/movie/55410/315x210.jpg?2224", 103, "日本人脚本家と韓国のスター俳優がタッグを組む、日韓共同ドラマプロジェクト「テレシネマ7」シリーズ。ギャンブル好きの三流芸人サンヒョンは、借金取りに追われながらも、ナイトクラブの司会をこなしながら何とか生計を立てていた。そんなある日、交通事故に遭い息を引き取る間際の見知らぬ男に「息子を母親のもとへ送り届けてほしい」と頼まれ、幼い少年ジェヨンを託されてしまう……。主演は韓流ドラマ「All My Love for You」でブレイクしたチャ・インピョ。", "2010-05-29", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOULEVARD NIGHTS", "ブールバード・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "舞台は、メキシコ系の人々が多い西ロサンゼルス。そこで縄張りを持つ二つの不良グループの対立を描いた作品。脚本のD・ナカノは日系アメリカ人で、UCLAの在学中に本編のシナリオで、同校のサミュエル・ゴールドウィン賞に輝いている。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("tokyo.sora", "tokyo.sora", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "夢を追って都会で暮らす女性たちの姿をリアルに切り取った群像劇。日本語の勉強を積む台湾の留学生、普段はティッシュくばりのバイトをしている売れないモデル、小さな胸に劣等感を抱く美大生、とりとめのない会話を重ねるウェイトレス、小説家を目指すランパブ嬢と、その同僚の美容師見習い。それぞれ壁にぶつかり、苦悩する彼女たちの人生はやがて一瞬、交錯し……。女優陣の肩肘を張らない好演や、自然体の描写に好感が持てる。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Irina Palm", "やわらかい手", "", "", "http://image.eiga.k-img.com/images/movie/53110/315x210.jpg?1560", 103, "60年代のポップ・アイコンとして世界中を魅了したマリアンヌ・フェイスフルが38年ぶりに主演を務めた人間ドラマ。ロンドン郊外で暮らす主婦マギーは、孫の手術費を稼ぐために奔走していた。ある日“接客係募集”の貼り紙を目にした彼女は面接を受けるが、なんとそこは壁越しに手でサービスする風俗店。戸惑いながらも仕事を始めたマギーは、自分が意外な才能を持っていることに気付き……。共演に「アンダーグラウンド」のミキ・マノイロビッチ。", "2007-12-08", "3,2,37,7,58", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OLTRE LA PORTA", "愛の謝肉祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "エジプトを舞台に妻殺しの罪で服役中のエンリコと、その義理の娘ニーナをめぐる異色のミステリー。若いアメリカ人の技師マチューはニーナとミラノで幸福に暮らしている。そこへ、ある日、ニーナの義父エンリコが訪ねて来て物語は過去へと遡る。「愛の嵐」にも通ずる不条理な愛のドラマ。", "1982-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOY STORY", "トイ・ストーリー", "", "", "http://ecx.images-amazon.com/images/I/51rEgFeeTIL._SL210_.jpg", 81, "６歳になるアンディの誕生日に、最新式のアクション人形バズ・ライトイヤーがやって来る。アンディのお気に入りのオモチャ、木製の操り人形のウッディは大好きなアンディをバズに取られてご機嫌ななめだ。そんなある日、ちょっとしたハズミから二人は家の外に飛び出してしまう。オモチャにしてみれば人間の世界は危険がいっぱい。アンディの元へ帰るため力を合わせる二人の間に、やがて友情が芽生える……。ピクサー・アニメーション・スタジオにより、世界で初めて3DCGで製作された。映像的な驚きもさることながら、基本をしっかりふまえた物語展開も実に見事。声優としてT・ハンクスがウッディを、T・アレンがバズを担当している。", "1996-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Now and Then", "Dearフレンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "12歳の時に大親友だった４人の女性が、20年ぶりに再会。秘密の出来事、ファーストキス、彼女たちの前に訪れた謎の老人……。４人は、思い出話に花を咲かせる。ただ懐しむだけでなく、自分たちがそれぞれ素敵な大人になったことを確かめ合うのだった。D・ムーアが自らのプロダクションで製作した友情と思い出の物語。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLESH GORDON MEETS THE COSMIC CHEERLEADERS", "フレッシュ・ゴードン２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "全宇宙が謎のインポビームに冒され、人類は危機に瀕する。正義の味方フレッシュ・ゴードンは、マスカキ博士らとともにその悪魔的計画に立ち向かう途中、セクシーなチア・リーダーに出会う。彼女たちは世界勃起リハビリ団体を結成しており、ゴードンは彼女たちと手を組んでインポビーム発信地へと向かうのだった。いかにも“ペントハウス・ムービー”といった感じのエロティックSF。", "1990-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マタンゴ", "マタンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "プチブルの若い坊ちゃんたちがヨットで遭難。漂着した孤島で体験する恐怖を描くホラー映画。登場人物を７人に限定して、さらに鳥も動物もいない無人の孤島という一種の“密室”に舞台を設定した中で、極限状況に追い込まれたエゴむき出しの人間ドラマが展開する。やがて７人を襲ってくる飢餓と、幻惑的な味覚を持ちながら、食べると自らもキノコとなってしまう怪キノコ・マタンゴの誘惑との相剋。メンバーが次々とマタンゴとなっていくにしたがい、サスペンスがふくらんでいく。また、イーストマンカラーを生かした、毒々しいマタンゴの極彩色も不気味な感じを盛り上げている。クライマックスのゾンビのように迫ってくるマタンゴの群れ、そしてアッと驚くラストは必見。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHANGHAI EXPRESS", "上海特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "革命の嵐吹きすさぶ中国。北京から一路上海へ向かう特急列車の中では、様々な人間たちのドラマが交錯している。上海リリーと名乗る謎の美女と、米軍将校ハーベイの運命的な恋。食い詰めたイカサマ賭博師、高級売春婦らしき中国娘、そして商人に化けたスパイ。やがて反乱軍のゲリラの手で列車は荒野に急停車させられる。反抗する者は即座に殺す、と宣言した無情なゲリラにいま、乗客のすべての未来は握られてしまった……。M・ディートリヒとJ・V・スタンバーグ監督よる４本目のコンビ作。「バグダッドの盗賊」などで知られる中国系女優A・M・ウォンの存在感あふれる演技も素晴らしい。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダースーパー1", "仮面ライダースーパー1", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "仮面ライダーシリーズ第9作「仮面ライダースーパー1」の劇場版。マタギの里・山彦村に祀られた古代中国の戦車・空飛ぶ火の車に目をつけた闇の帝王テラーマクロは、メガール将軍と地獄谷五人衆に火の車を強奪させ、日本各地の攻撃を開始する。火の車を破壊するには、マタギの頭領が5人の子どもたちに託したという5個の光る石が必要だと知ったスーパー1は、東京に逃れた子どもたちの行方を追うが……。", "1981-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ОДИНОКИЙ ГОЛОС ЧЕЛОВЕКА", "孤独な声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "国内戦が終結した直後、すっかり荒廃した故郷の村に帰って来た復員兵ニキータ。傷心から立ち上がった彼は、貧困にあえぎながらも幼なじみのリューバとの愛をはぐくんでいく。ペレストロイカ後、世界から注目される存在となったA・ソクーロフ監督の1978年の大学卒業作品で、死と隣り合わせの生活のなかでも気高さを失わなかった人々の精神を描く。原作はナスターシャ・キンスキー主演作「マリアの恋人」と同じプラトーノフの小説。", "1978-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LOVE MACHINE", "ラブ・マシーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "性的魅力を利用してわずか６週間でニュース・キャスターから局長にのし上がったロビン。裏ではやりたい放題だが、最後はスキャンダルが暴かれ、カメラの放列の前でTV界から追放される。TV界の腐敗をついたJ・スーザンの小説の映画化。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逢えてよかった。", "逢えてよかった。", "", "", "http://ecx.images-amazon.com/images/I/51U-iTdJ11L._SL210_.jpg", 105, "夜回りをして若者たちの更正に取り組む元暴力団組長・石原伸司が自身の体験をつづった同名ノンフィクション小説を、ベテラン俳優の黒沢年雄主演で映画化。刑務所から出所し、不良たちを更正させるために夜回り活動を行なっていた石原は、ある日、両親から虐待を受けてきた少女・チエコと出会う。恐喝や援助交際など様々な悪事に手を染めるチエコにかつての自分の姿を重ねた石原は、チエコを更正させようと奮闘するが……。", "2010-04-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARDCORE", "ハードコアの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "脚本家としても名を知られるシュレーダーの監督第２作。アメリカの小さな町で家具工場を経営するジェイクの一人娘が、旅先で失踪。私立探偵を雇って調査するうちに、彼女がポルノ映画に出演していることが判明する……。娘を思う父親の苦しみを描いた異色作。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIANCA", "僕のビアンカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "監督・脚本・主演を一人でこなし、その独特のコミカルな味わいで人気を博すN・モレッティの日本初登場作品（第１回東京国際映画祭ヤングシネマ上映)。新任数学教師のミケーレは潔癖症で、なかなか新しい生活にもなじめない。しかし美人教師のビアンカにだけは心の安らぎを感じていた。やがて隣のアパートの女が殺され、ミケーレは刑事に付きまとわれ始める……。", "1984-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本一のゴリガン男", "日本一のゴリガン男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "不況の波が押しよせ、とかくせちがらい世の中を、合理的に計算づくで渡っていこうと決心した日本等。どんどん成績を上げる日本に対し、社内では反感が盛り上がっていく。ゴリガンとは“合理化案”のこと、相変わらずマイペースで突っ走る植木等がおかしい。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("洗礼", "洗礼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "恐怖漫画の第一人者として名高い巨匠、楳図かずお原作の同名コミックを映像化したホラー映画。スター女優の若草いずみは得体の知れない皮膚病に冒され、醜い顔になり世間から身を隠す。それから10数年後、いずみは13歳になる娘さくらとひっそりと郊外の洋館で暮らしていた。娘を溺愛しているかのように見えたが、いずみはひそかに脳移植手術により娘の体を奪おうと企んでいた。逃げ惑うさくらを押さえ付け、手術により再生したいずみは、さくらのピアノ教師を誘惑するが……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE VINDICATOR", "悪魔の改造人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "天才科学者のカールは、野心家の航空宇宙研究所長官の策略で改造人間にされてしまう。殺人的破壊力を持つカールは厳重な監視をくぐり抜け逃げ出すが、長官は女ハンターを雇い彼の行方を追う……。「エイリアン２」のクリエイター、スタン・ウィンストンの特殊メイクに要注目。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Slender Thread", "いのちの紐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "自殺者救援施設に睡眠薬自殺を図った女性から電話がかかってきた。宿直のアルバイト学生はなんとか命を救おうと対応するが……。ポラック監督デビュー作の異色のヒューマンドラマ。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LION IN WINTER", "冬のライオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "J・ゴールドマンの舞台劇の映画化で、彼自身が脚色し、主演のヘプバーン、音楽のジョン・バリーとともにオスカーを獲得した。王位継承問題で苦悩するイギリス王ヘンリー二世を中心に、複雑きわまる人間葛藤を重厚な演出で描く一大叙事詩。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("童貞。をプロデュース", "童貞。をプロデュース", "", "", "http://image.eiga.k-img.com/images/movie/34150/315x210.jpg?1495", 85, "「あんにょんキムチ」「セキ☆ララ」の松江哲明監督が、2人の青年を“童貞”から脱出させるべくプロデュースするドキュメンタリー。片思い中だがキスの経験すらない半ひきこもり青年に、ある荒療治を施す第1部、80年代のB級アイドルに思いを寄せ、自主制作映画まで作ってしまったサブカルオタク青年が主人公の第2部から構成される。さらに人気バンド・銀杏BOYZの峯田和伸が第1部と第2部の間に特別出演。", "2007-08-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOG　STAR　MAN", "DOG STAR MAN〈完全版〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "1967年に“序章”部分が日本公開されたものの、その後は伝説の一作として噂だけが流通していたアンダーグラウンド映画の名作。日本では2000年に初めてその全貌が明かされた。木こりが山を登るというイメージを一つの象徴に、セックスから出産、赤ん坊の成長といった生の営みの記録が、多彩な映像テクニックを駆使しながら綴られていく。明確な物語を持たないサイレント・フィルムながら、多重露光やコマ撮り、スクラッチ、はてはフィルムの腐食までを用いて視覚的なテンションが持続するヴィジュアル・インパクトは圧巻。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホームシック", "ホームシック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "恋人とのささいなケンカで道端に放り出された明美が、中村という老人の車に乗せてもらう。中村は故郷の町をめざして孤独な旅をしていた。世代を超えて心を通わせた彼らは、大金を抱えた怪しい中年女などに出会っていく……。晩秋の北海道ののどかな風景をバックに、それぞれの切ない思いを抱えた人々の姿を心優しい視線で見つめるロード・ムービー。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOTHING PERSONAL", "ナッシング・パーソナル　報復の無差別爆破", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "北アイルランドのテロ紛争を、カトリック系のナショナリスト側ではなく、英国領であることを望むプロテスタント側から描いた珍しい１本。「フィオナの海」の美少女J・コートニーが、悲劇のヒロインを熱演している。1975年、ナショナリストとロイヤリストの休戦協定が進む中、それが気に入らない過激派IRAは殺りく行為を開始する。その影響は、誠意あるカトリックのリアム一家にまで及んでいく。「秘密と嘘」のマイク・リー監督と長年コンビを組むディック・ホープが撮影を担当している。", "1995-01-01", "33,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Errance", "この胸のときめきを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「ナイト・オブ・ザ・スカイ」のブノワ・マジメル＆「歓楽通り」のレティシア・カスタ共演によるラブストーリー。1960～70年代のフランスを舞台に、深く愛するがゆえに傷つけあってしまう夫婦の10年間にわたる愛の軌跡を描く。1968年、ジャックの妻ルーは男の子を出産する。しかし、ジャックは胡散臭い取引きのため、妻と息子を置いて出て行ってしまう。ルーに責められたジャックは、これからは真面目に生きていくことを誓うが……。", "2006-07-15", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("未完の対局", "未完の対局", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "1924～56年までの日本と中国の歴史的な出来事を背景に、中国の囲碁の名人の息子で日本へ連れ帰られた阿明と、日本の名人の娘・巴の悲恋を描く。軍部による中国侵略の卑劣さ、中国人を蔑視した日本人の愚かさなど、日本の浅ましさを全面的に展開して見せる。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("首領になった男", "首領になった男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "松方弘樹が主演を兼ねて初プロデュースに挑んだ意欲作。バブル経済下の現代社会を背景に、駆け出しのヤクザが株、不動産などに手を出してのし上がっていく様を壮絶に描いていく。少年時代の主人公役に松方の息子である目黒大樹が演じ、スクリーン・デビューを飾っている。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い殺意", "赤い殺意", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "封建的なところがあった頃の東北を舞台に、古い因習の重圧に苦しむ小心な女が、ある事件を契機に強い女へと変わっていくさまをどっしりと描いた今村映画の真骨頂。「にあんちゃん」が文部省推薦になり、あまりの恥ずかしさにもっと“まじめな”ものを作ろうと企画したというのがいかにも今村らしい。古い家族制度の中でいちばん下におかれ、いつもビクビクしながら生活している貞子。小心な大学職員の夫が留守の夜、眠っていた彼女は強盗に犯された。一度は死のうと思った貞子だが、なかなか死ねずにあきらめる。その後も強盗はやってきて関係を持つうちに、本気で貞子を愛するようになる。心臓を病む強盗は、最初は暴力で犯したのに、やがて哀願に変わる。貞子は強盗に殺意を抱くが、自分が手を下す前に男は病死。そして強くなった彼女は家族の中で主婦の座を確立するのだった……。地味な題材を暗くせずに、女性の心奥に隠れているバイタリティーさ、生命力の噴出を描いて、今村節が冴えわたる重喜劇。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mysterious Object at Noon", "真昼の不思議な物体", "", "", "http://image.eiga.k-img.com/images/movie/57730/315x210.jpg?2174", 83, "アーティスト、映像作家として活動するタイのアピチャッポン・ウィーラセタクンの長編映画初監督作。タイ北部の田舎の村で行商人の女性がある物語を語り始める。その続きが象使いの少年、伝統演劇の劇団員などに引き継がれ、リレー形式で語られていくうちに物語は思わぬ方向へと進んでいく。2012年、ウィーラセタクン監督作を一挙上映する「アピチャッポン・イン・ザ・ウッズ」にて上映。", "2000-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dogville", "ドッグヴィル", "", "", "http://image.eiga.k-img.com/images/movie/1360/315x210.jpg?3221", 177, "「奇跡の海」で審査員グランプリ、「ダンサー・イン・ザ・ダーク」でパルムドールと、2作連続カンヌ映画祭を制したデンマークの異才、ラース・フォン・トリアー監督。彼がブレヒトの「三文オペラ」の挿入歌「海賊ジェニー」にインスパイアされ、ダシール・ハメットの「血の収穫」やカフカの「アメリカ」を参考に“想像上のアメリカ”を描いたと語る実験的作品。監督自身がDVカメラで撮影にも参加している。", "2004-02-21", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lawrence of Arabia", "アラビアのロレンス　完全版　ニュー・プリントバージョン", "", "", "http://image.eiga.k-img.com/images/movie/54150/315x210.jpg?4431", 227, "1962年に初公開され、第35回アカデミー賞7部門を制覇した名作が、デビッド・リーン生誕100周年、コロンビア映画創立85周年を記念してニュープリント完全版で復活。監督は「大いなる遺産」「ドクトル・ジバコ」のデビッド・リーン、主演は「おしゃれ泥棒」「ラ・マンチャの男」のピーター・オトゥール。アラブ国民からも英雄と称えられるイギリス人考古学者であり軍人のT・E・ロレンスの半生を描いた壮大なスペクタクルの歴史映画。", "2008-12-20", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lou Reed's Berlin", "ルー・リード　ベルリン", "", "", "http://image.eiga.k-img.com/images/movie/53790/315x210.jpg?2505", 85, "「潜水服は蝶の夢を見る」のジュリアン・シュナーベルがステージセットを担当し、監督したルー・リードのコンサート・フィルム。2006年の真冬のニューヨーク、33年の月日を経て、5日間に渡り元ヴェルヴェット・アンダーグラウンドのルー・リードは、アルバム「ベルリン」の全曲ライブ・パフォーマンスを敢行した。バイセクシャル・ドラッグ・暴力をテーマにした「ベルリン」は商業的には失敗したが、現在では70年代ロックを代表する作品。コンサート映像の撮影は、ミシェル・ゴンドリー作品でも知られる女性撮影監督エレン・クラス。", "2008-09-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2011-2012 マスネ「マノン」", "METライブビューイング2011-2012 マスネ「マノン」", "", "", "http://image.eiga.k-img.com/images/movie/57170/315x210.jpg?4271", 248, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を映画館で上映する「METライブビューイング」2011～12年シーズンの10作目。16世紀フランスの作家アベ・プレボーの恋愛小説「マノン・レスコー」に基く、19世紀の作曲家ジュール・マスネによるオペラ。享楽的な性格ゆえに修道院に入れられることになった美少女マノンは、道中に出会った騎士デ・グリューと恋に落ち、駆け落ちする。しかし、デ・グリューとの貧乏な生活に耐えられなくなったマノンは逃げ出し、貴族プレティニの庇護を受けるが……。", "2012-05-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sexy Beast", "セクシー・ビースト", "", "", "http://ecx.images-amazon.com/images/I/51VMKSXY69L._SL210_.jpg", 91, "", "2000-01-01", "3,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WARU〈ワル〉", "WARU〈ワル〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "鬼才、三池崇史監督が、作家生活45周年を迎えた真樹日佐夫の代表作に挙げられる『ワル』シリーズから『ワル 最終章』を映画化。無頼派の男がマフィアから世界的テロリスト集団までを敵に回す、壮絶な戦いに巻き込まれていく。悪しき世を正す会“地平同”に属す氷室は、マフィアや地下組織を叩き潰す活動を続ける。だが、その結果、極東マフィアに追われ……。男気溢れる主人公役で、哀川翔がその持ち味を存分に発揮している。", "2006-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEFORE THE RAIN", "ビフォア・ザ・レイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "ヴェネチア映画祭で10部門を独占した、M・マンチェフスキーの監督デビュー作。３つのラブ・ストーリーで構成され、舞台をマケドニアからロンドン、そしてまたマケドニアへと移し、巧みに登場人物を交錯させ循環した一つの物語を描く。第１話「言葉」では、マケドニア人と敵対するアルバニア人の娘が修道院に逃れ、若い僧と恋に落ちるが、宗教や言葉の違いなどが二人の恋の行方を阻み……。第２話「顔」では、写真エージェントで働く女性編集者アンが、マケドニア人の愛人アレックスと退屈な夫ニックの間で気持ちが揺れ動いている時、思いがけない事件が起こる。第３話「写真」では、２話に登場したアレックスが主人公。故郷に帰るが民族紛争で荒れ果て、昔好きだった彼女は敵対するアルバニア人。そんな中、彼の叔父がアルバニア人に殺される。マケドニアの美しい景観を映し出し、人々が争うことの愚かさをも浮き彫りにしている。", "1994-01-01", "3,2,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンダンテ　稲の旋律", "アンダンテ　稲の旋律", "", "", "http://image.eiga.k-img.com/images/movie/55120/315x210.jpg?3974", 108, "母親の強い希望で幼い頃から音楽の道を歩んでいた千華は、激しい競争の中で次第に自信を喪失していく。やがて登校拒否が原因で大学を中退した千華は、フリーター生活を続けるうちに家に引きこもるようになってしまう。思いつめた千華は、「誰か私を助けてください」と書いた手紙を入れたペットボトルを千葉の水田へと流すと、それを拾った農家の晋平から手紙が届くようになる。主演はミュージカル界の新妻聖子、筧利夫。", "2010-01-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANGMAN", "殺人ドットコム／satujin.com", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "殺人予告のゲームを警察に送りつけ、ゲームをクリアしなければ殺人が起こるという事件が発生する。やがて、精神科医グレースの自宅からも被害者が発見された。事件を追う刑事ニックは、被害者の接点を探すうち、グレースがかつて勤務していた犯罪者専門の精神病院にたどり着き……。ルー・D・フィリップス主演のサイコ・サスペンス。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いつか誰かが殺される", "いつか誰かが殺される", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「晴れ、ときどき殺人」に続く、赤川次郎＝渡辺典子のコンビによるミステリー第２作。赤川作品らしい、女子高生が活躍する作品。平凡でキュートな18歳の少女の高校生活最後の夏休みに父親が失踪し、彼女の周りには何やらミステリアスな影が忍び寄ってくる。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無言館", "無言館", "", "", "http://image.eiga.k-img.com/images/movie/56150/315x210.jpg?3688", 86, "第2次大戦で志半ばに散った画学生たちの作品を収集する戦没画学生慰霊美術館「無言館」にまつわるドキュメンタリー。同館の館主・窪島誠一郎さんは、画学生たちの残した作品を収集するため全国各地を訪ねる。1997年、長野県上田市に無言館を開いてからは、毎年8月に画学生たちの無念を弔う慰霊祭「千本の絵筆」の供養も行っている。", "2011-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("玻璃、少女。", "金魚のしずく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "孤独な少女と、孫娘を探す初老の男の性別も年齢も超えた心の交流を通して、信じることの大切さを描く感動作。家出した孫娘を探し始めた元警官の初老の男ウーは、孫娘の友人Pと出会う。愛情のない両親に傷つき、大人を毛嫌いするPは、なかなかウーに心を開かない。だが、行動をともにするうち、Pはウーの優しさに心癒されていく……。ふてぶてしい態度の中に、少女らしい純粋さをのぞかせるヒロイン、ゼニー・クォックが鮮烈な印象を残す。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bread and Roses", "ブレッド＆ローズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "イギリスの名匠K・ローチが、不当な扱いを受ける移民労働者たちの苦悩を描き出す人間ドラマ。姉を頼ってアメリカに不法入国したマヤは、ロスの清掃会社で働き始める。最悪の労働条件にも必死に耐えていたマヤだが、労働組合活動家の青年サムと出会い、自分たちの権利を主張する組合活動に傾倒していく。解雇されるリスクを背負いながらキャンペーンを繰り広げる移民労働者たちの姿をリアルに描き、社会問題を提起する。平穏な暮らしを望む姉と、正義のために闘おうとする妹との確執のドラマも盛り込こまれ、単なる社会派作品には終わらない深みを与えている。", "2000-01-01", "3,7,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いのち耕す人々", "いのち耕す人々", "", "", "http://image.eiga.k-img.com/images/movie/53670/315x210.jpg?2723", 100, "30年以上に渡り有機農業に取り組んでいる山形県高畠町の人々の軌跡をたどったドキュメンタリー。20年前、農薬の空中散布拡大によって有機農業が崩壊の危機に陥った際に撮影された記録映像を中心にその歴史を振り返るとともに、毎年都会からやって来る多くの農作業体験者を受け入れる夫婦など、現在の高畠町の農民たちの姿を映し出していく。信念を貫く彼らの姿を通して、現代人が見失いつつある“食べ物の大切さ”が見えてくる。", "2008-06-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIGHT NIGHT PART２", "フライトナイト2 バンパイアの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1985年のヒット作「フライトナイト」の続編。前作で壮絶な死を遂げた兄バンパイアの復讐のため、美貌の妹バンパイアが手下を引き連れて襲来する。前作同様R・マクドウォールとW・ラグズデールがバンパイア・ハンターとなり活躍。「ミラグロ」の実力派J・カーメンがセクシーな女吸血鬼を演じている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Prêt-à-Porter (Ready to Wear)", "プレタポルテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "　1994年、パリ・コレクション開催にともない、街には関係者と取材陣が詰めかけていた。ファッション業界の大物オリビエの急死で、愛人と本妻は鉢合わせ。不倫、同性愛と様々なスキャンダルも明らかになり、やがてショーは佳境へ……。パリ・コレに集まったデザイナー、マスコミ、バイヤーといった人々が巻き起こす騒動を描き、華やかなファッション業界を皮肉ったロバート・アルトマン監督の群像劇。国際的豪華キャストに加え、スーパーモデルやトップデザイナーが続々登場。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SQUARE DANCE", "スクエアダンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "家庭崩壊が深刻な社会問題となっている現代、“家族の絆”とは何かを真摯に見つめた問題作。気難しい祖父と二人暮らしの少女ジェマは、かつて自分を捨てた実の母の出現に心乱されるが……。R・ロウが難役を好演している。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("成吉思汗", "成吉思汗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "戦時体制の真っ只中、中国大陸を征服したジンギスカンを描くことで、戦意の昂揚を図るべく製作された国策映画。モンゴル政府の協力で現地ロケし、当時としては類のない人馬スペクタクルが展開されていく。オリジナル上映時間は約99分だが、現存するプリントは16分カットされた16ミリのものだけである。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポルノ時代劇 忘八武士道", "ポルノ時代劇 忘八武士道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "劇画界の雄・小池一雄の原作を基にしたカルト時代劇の名作。鬼才・石井輝男による過激なバイオレンスと迫力のエロティシズム満載で描き出す。丹波哲郎扮する非情の剣客・明日死能は、大名に気に入られ用心棒兼刺客に登用される。大名の目的は個人営業の娼婦グループを潰すことにあった。そこで大名は由緒ある大刀を死能に与え、娼婦の巣窟を襲うように仕向け……。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほんとにあった！呪いのビデオTHE MOVIE", "ほんとにあった！呪いのビデオTHE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "OVシリーズで20タイトル以上が製作された人気ホラーの劇場版。物語は、シリーズのスタッフに届いた一件の投稿から始まる。そこには、同シリーズの“パート２”を見た友人が突然死したと書かれていた。投稿者は友人とビデオを見た後、そのまま一緒の部屋で寝たところ、朝になって友人が死んでいたというのだ。死因は不自然な窒息死というが……。リアルな演出で恐怖をあおる一作。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京マリーゴールド", "東京マリーゴールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "林真理子の短編小説をもとにした、切ない恋愛ドラマ。恋人と別れたばかりで空しい毎日を送る21歳のエリコは、合コンで出会ったタムラに惹かれる。だがタムラには留学中の彼女がいた。エリコはそれを知りながら、タムラに１年だけの期間限定恋愛を提案してしまうのだった。田中麗奈が初めて本格的な恋愛映画に挑戦。つらい恋を通して心の成長をみせる、等身大のヒロインを好演している。叙情あふれる演出に定評のある市川準監督が、都内30ヵ所以上でロケを行い、東京という変化し続ける街の表情とヒロインの揺れる心情を巧みに重ねている。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DEMON DANS L’ILE", "サイキックゾーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "様々な生活道具が突然暴走し、人間に重傷を負わせはじめた。美しい女医は、この奇怪な事態を解明しようとするうち、驚くべき事実を知る……。フランスでは珍しいサイコ・ホラーで、アヴォリアッツ・サスペンス大賞を受賞。J・C・ブリアリが怪演。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生きてはみたけれど・　小津安二郎伝", "生きてはみたけれど・　小津安二郎伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "日本映画史上の巨匠・小津安二郎監督の人となりと、その作品を多くの人々が語っていく、インタビュー中心のドキュメンタリー。登場するのは女優（岸恵子・司葉子・岡田茉莉子・杉村春子ら）、男優（笠智衆・中村伸郎ら）、監督（木下惠介・今村昌平ら）など多岐にわたる。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブレイクブレイド　第一章「覚醒ノ刻」", "ブレイクブレイド　第一章「覚醒ノ刻」", "", "", "http://image.eiga.k-img.com/images/movie/55060/315x210.jpg?3146", 50, "吉永裕ノ介の人気ロボット・コミックを6部作で映画化した第1作。「昆虫物語みつばちハッチ　勇気のメロディ」のアミノテツロ監督がシリーズ総監督を務める。化石燃料を採掘できないクルゾン大陸で、文明は魔力で動く工作材料“石英”を動力源として発展してきた。ある日、100万人に1人といわれる魔力を持たない青年ライガットは、士官学校時代の友人でクリシュナ国王ホズルと王妃シギュンに召還され、隣国アテネス連邦による侵攻を知る。さらにその強襲部隊を率いるのは、天才魔動戦士で3人の親友ゼスだった。", "2010-05-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STREET CRIMES", "ストリート・クライム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "凶悪化するストリート・ギャングと、タフな刑事コンビの抗争を描くバイオレンス・アクション。ドラッグと暴力に蝕まれた街で、使命に燃え犯罪に目を光らせる警官コンビ、ブライアンとトニー。繰り返されるストリート・ギャングとの凶行に業を煮やしたトニーは、実力を駆使して壮絶な肉弾戦を挑む。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Black Hawk Down", "ブラックホーク・ダウン", "", "", "http://image.eiga.k-img.com/images/movie/1020/315x210.jpg?4612", 145, "1993年に米軍が失敗したソマリアの将軍の捕獲作戦を、ノンフィクション小説を原作にリドリー・スコット監督が映画化。撮影は、クシシュトフ・キエシロフスキー監督とのコンビで知られるスワボミール・イジャック。編集は「G.I.ジェーン」以降のコンビ、ピエトロ・スカリア。撮影にはロケ撮影と野外セットを駆使。モロッコの町中に、ドラマの標的となるビルを含む巨大な野外セットを4カ月かけて建築、あらゆる角度から撮影された市街戦が展開。", "2002-03-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sportkill", "スポーツキル　地獄の殺戮ショー", "", "", "http://image.eiga.k-img.com/images/movie/34250/315x210.jpg?4239", 83, "ラスベガスの秘密クラブで繰り広げられる殺人賭博ショーを描いた残虐ホラー。拉致してきた男女に殺し合いを強制し、観客たちが賭けを行う地下クラブ“スポーツキル”。不運にもターゲットになった者は、目の前に引き出された相手を与えられた命令通りに殺さなければならない。そんな恐ろしい殺人ショーの参加者として選ばれてしまった美しい女性レイチェルは、生き延びるために次々と命令を実行していき……。", "2007-10-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kotch", "コッチおじさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "70歳のコッチは妻に死なれ、独り身だがまだまだ元気。老人ホームなんかに入るものかと、息子夫婦の家を飛び出した……。老いの苦難に勇敢に立ち向かう、賢明で頑固な老人を、マッソーが好演。俳優レモンの監督第１作で、誠実で手堅い演出が光る良質のドラマ。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANTO＾MAS SE DE´CHAINE", "ファントマ 電光石火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "フランスの有名な活劇小説をもとにした“ファントマ”シリーズの第２作。今回はフュネス扮するさえない警部ジューブが、新聞記者の協力を得てファントマを追いかける。ところが、この記者がファントマにあまりにもよく似ていて……。次々と登場する新兵器にも注目。", "1965-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の紋章・流転の掟", "男の紋章・流転の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "旅修行を続ける竜次は、手にかけた刺客３兄弟の妹が、悪玉ヤクザの借金のカタに無理やり遊廓に売られそうになるのを救う。悪玉は母と兄弟分だが、村田組の縄張りを狙い殴り込みをかけようとする。しかし竜次は先制攻撃をかけて決着をつける。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WANDERERS", "ワンダラーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "舞台は1963年、ニューヨークのブロンクス。イタリア系白人の不良グループ“ワンダラーズ”と、そこに集まる黒人や中国人など、様々な人種の少年たちの青春群像が描かれる。R・プライスの小説の映画化で、1960年代前後のヒット曲をたっぷり聴かせる。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旗本退屈男", "旗本退屈男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "市川右太衛門の映画出演300本を記念してオールスター・キャストで製作されたシリーズ第23作。伊達藩の当主・忠宗の狂態振りを伝え聞いた早乙女主水之介こと旗本退屈男が乗り出してくる。顔見せという言葉がピッタリの超豪華なキャスティングに注目。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAST OF THE DOGMEN", "ネイティブ・ハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "130年前、白人部隊の奇襲によって絶滅したシャイアン族。その悲劇的な逸話をもとに、「愛は霧のかなたに」で原案を、「ノートルダムの鐘」で脚本を担当したT・マーフィーが、伝説の部族が生き残っていたら、という夢を壮大な自然を背景にファンタジーへと昇華させた。脱走した囚人を追う賞金稼ぎのルイスが、未開の地であるモンタナ州の山奥へと足を踏み入れる。そこで彼が見たのは血の海と一本の矢、そして不気味な人影だった。先住民族研究家のリリアンは、この矢が絶滅したシャイアン族の戦士ドッグ・メンのものに酷似しているという。真実を探るため、二人は鬱蒼と木々が生い茂る山の中へと入っていく……。シャイアン族が、眼にも止まらぬ速さで霧の中を駆け抜けるシーンに異様な緊張感と迫力が充満。伝説を追い求める主人公の男女には渋い魅力を放つT・ベレンジャーと、「ある貴婦人の肖像」でアカデミー助演女優賞候補となったB・ハーシーが扮している。ビデオ発売時タイトルは「ラスト・オブ・ドッグメン」。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOSFERATU，EINE SYMPHONIE DES GRAUENS", "吸血鬼ノスフェラトゥ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "ドラキュラ映画の古典で、ドイツ・ロマン派的雰囲気を持つサイレント映画。ノスフェラトゥとは不死者のことである。ブレーメンの町に、吸血鬼ノスフェラトゥ伯爵がやって来た。以後町にはペストが蔓延し、葬列の絶える日がない。伯爵の連れてきたネズミが、ペスト菌をまき散らしているのだ。この事実を悟った美女ニーナは、我が身を犠牲にして吸血鬼に挑む。血を与え続けて朝までひきとめ、朝日で吸血鬼を倒そうというのだ。馬車が吸血鬼の城に向かうシーンをモノクロ反転したり、ニーナに迫る吸血鬼を影で描いたり、映像効果が素晴らしい。", "1922-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HARMONIUM IN MY MEMORY", "我が心のオルガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "60年代の韓国の山奥を舞台に、古き良き時代の純粋な初恋を美しい映像で叙情的に描き、数々の国際映画祭で受賞。素朴で懐かしい風景と17歳にして小学5年生の少女の初々しい心情がシンクロし、心にしみる感動作に仕上がっている。17歳で遅れて小学校に進級したホンヨンは、新任教師スハに道を聞かれ、自分を初めて“お嬢さん”と呼んでくれた彼に初恋をした。しかしスハは同僚の女教師に想いを寄せていた。", "1999-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATTY AT CONEY ISLAND", "ファッティとキートンのコニー・アイランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "感謝祭に沸くニューヨーク近郊の海水浴場を舞台に、デブ君、キートン、セメント君ら５人の男女が繰り広げるドタバタ騒動を描く無声短編。後の喜劇王キートンはまだデブ君の助演だが、それだけに彼が大笑いする場面があり、ファンには見逃せない。", "1917-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小説吉田学校", "小説吉田学校", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "1940年代末期から1950年代半ばに至る日本の保守政界の主要人物をほぼ全員登場させて、スケール豊かに描く政界群像劇。保守政治の大きな流れを作り上げた吉田茂を中心に、鳩山一郎・池田勇人・佐藤栄作・田中角栄・宮沢喜一らの姿が描かれる。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　仮面ライダーフォーゼ（仮題）", "劇場版　仮面ライダーフォーゼ（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("元祖パチンコ物語 駅前戦争", "元祖パチンコ物語 駅前戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "女好きのオーナー・国松が仕切るパチンコ店、マツヤ駅前店の近隣にライバル店“ジャンボ”が進出。敵情視察に赴いたマツヤの店長・丸山は、ジャンボで働く昔の恋人と再会。ワケありの彼女に同情し苦悩する丸山のために、国松はひと肌脱ぐことに……。中年男の純情を軸にした、人情喜劇“パチンコ物語”シリーズ第９作。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEALERS", "ディーラーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "イギリス金融市場のディーラーたちの実体を赤裸々に描いた問題作。１億ドルの欠損を出してピストル自殺したディーラーの事件をきっかけに、銀行首脳人の企業内権力闘争、有能ディーラーのスカウト、新人と古参との対立など、ビジネス最前線に働く男たちの熱いドラマが真っ正面から綴られていく。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dark Shadows", "ダーク・シャドウ", "", "", "http://image.eiga.k-img.com/images/movie/54540/315x210.jpg?1481", 0, "1966年から71年まで米ABCテレビで放送され、「血の唇」（70）として映画化もされたゴシック・ソープオペラを、ティム・バートンとジョニー・デップが8度目のタッグで新たに映画化。1752年、裕福なコリンズ家に生まれ育ったプレイボーイのバーバナスは、魔女アンジェリークの手により不死のバンパイアにされ、生き埋めにされてしまう。2世紀を経た1972年、ふとしたきっかけでバーバナスは自身の墓から解放されるが、200年が過ぎたコリンズ家はすっかり落ちぶれ、末裔たちは誰もが暗く不可解な秘密を抱えて生きていた……。バーバナス役でデップが主演し、エバ・グリーン、ミシェル・ファイファー、ヘレナ・ボナム・カーター、クロエ・モレッツら豪華キャストが共演。", "2012-05-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents kiss!　「マコの敵」", "桃まつり presents kiss!　「マコの敵」", "", "", "http://image.eiga.k-img.com/images/movie/54480/315x210.jpg?1405", 30, "", "2009-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌４", "釣りバカ日誌４", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "“釣りバカ”コンビ、浜崎伝助ことハマちゃんと彼の会社の社長・鈴木一之助ことスーさんの珍騒動を描いたシリーズ第４作。今回はスーさんの甥・和彦が登場、ハマちゃんのいる営業３課へ配属されるところから始まる。すっかり意気投合した二人を心配するスーさん。そんなある日、和彦がハマちゃんの親友の妹・町子に一目惚れしてしまい……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔性の夏 四谷怪談より", "魔性の夏 四谷怪談より", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "浪人の伊右衛門が、金持ちの婿になろうとして、邪魔になった妻のお岩を殺し、その怨霊にたたられる。このあまりにも有名な古典的ストーリーを、岡場所にたむろする不良たちの生き方や、売春宿などのグロテスクな雰囲気描写に力を入れた怪談映画。演劇界の雄・蜷川幸雄が監督し話題になった。DVDは「夏目雅子 出演名作集 DVD-BOX」に収録。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAMLET", "ハムレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 153, "イギリスの名優オリヴィエが、自ら監督となって、シェイクスピアの代表的名作を映画化。キャスティングには、舞台畑からベテラン俳優陣を招へいし、重厚で見ごたえのある作品に仕上げた。オフェリア役シモンズの好演が印象深い。ハムレット映画の決定版といえる一編。", "1948-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OWL", "ザ・オウル 激闘伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "愛する妻と子供を殺された男アレクサンダーは、毎夜、街をさまよい歩き、復讐のチャンスを待っていた。ある時、一人の少女から誘拐された父親を助けてほしいと頼まれたアレクサンダーは、その行方を追ううち、憎むべき相手を発見する。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("華鬼　三部作　麗二×もえぎ編", "華鬼　三部作　麗二×もえぎ編", "", "", "http://image.eiga.k-img.com/images/movie/55050/315x210.jpg?4312", 80, "「華鬼　三部作／華鬼×神無編」の続編。朝霧神無が木籐華鬼に出逢う20年前、　鬼の花嫁として高槻麗二（細貝圭）のもとへ強引に嫁がされたもえぎ（川村ゆきえ）は、なかなか麗二を好きになれずにいた。そんなある日、500年前に封印されたはずの怪物「凝鬼」が復活し、彼らの故郷・鬼ヶ里を恐怖の渦へと陥れる。書籍化され累計25万部を突破したオンライン小説の映画化。", "2009-11-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANNA’S WAR", "ハンナ・セネシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "ナチス政権下のハンガリーにおいて、レジスタンスとして戦い、短い生涯を終えたユダヤ人女性ハンナ・セネシュの戦いの日々を描くキャノン映画の超大作。主演のM・デートメルスの高貴な美しさが光るが、戦闘シーンで好戦アクションものと化してしまうM・ゴーラン監督の演出が散漫だ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EUROPEANS", "ヨーロピアンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "19世紀アメリカ、ニューイングランドを舞台に、フランスからやって来た二人の従姉妹を迎えたアメリカ人一家の姿を描いた作品。少し屈折した人々の愛の姿をヨーロッパの香りで味付けして描く事を得意とするアイヴォリーが、L・レミックを得て撮り上げている。", "1979-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひとりっ子", "ひとりっ子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "松竹大船時代から1976年に没するまで、時代に即した青春群像を誠実に追及し続けた家城巳代治が、政治の時代に贈るメッセージ。防衛大学の１次試験に合格した高校生の迷いを描いている。彼がふと参加した反戦デモ。その波紋は学校中に広がっていく……。現在見ると生真面目すぎる一面も。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ONE AND ONLY", "ワン・アンド・オンリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "コメディアン志望の大学生が、女の子とショービジネスへの憧れの間で板挟みになるのだが……。C・ライナー監督が、得意のバックステージもので手腕を発揮。テレビ俳優だったH・ウィンクラーのみずみずしい演技にも注目したい、芸人コメディの快作。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("8 Million Ways to Die", "800万の死にざま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　麻薬捜査官マシューは丸腰の者を撃ったことがきっかけで転落の道を歩むことに。アル中になった彼は禁酒会参加を経たのち、ようやく社会復帰する。やがて私立探偵となったマシューは、売春婦殺害事件の犯人を追ううちに、サラという女性と知り合い、さらにドラッグの売人エンジェルに接近するが……。ローレンス・ブロックのハードボイルド小説を映画化。敵役を演じたアンディ・ガルシアが注目を浴びた。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("６３３　SQUADRON", "633爆撃隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "第二次世界大戦のモスキート爆撃隊の活躍を、本物のモスキートを飛ばして撮影し、ダイナミックに描いた戦争巨編。1944年、イギリス空軍はノルマンディ上陸作戦に先駆けて制空権を握るため、633爆撃隊に特命を課しドイツ軍に攻撃を仕掛けるが……。", "1963-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TARGETS", "殺人者はライフルを持っている！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "P・ボグダノヴィッチ監督のデビュー作。怪奇映画の人気スター、バイロン・オークラがドライブイン・シアターでの最終出演作上映を機に映画界引退を発表。まさしくその場へ、平凡な家庭に育ちながらも歪んだ心をもった青年が、ライフルで次々と人を射殺しながらやって来る……。1960年代のケネディ暗殺事件やテキサス大学ライフル乱射事件などに想を得て、ボグダノヴィッチが製作した問題作。オークラにB・カーロフを起用し、怪奇映画へのオマージュを捧げるなど、シネフィル・ボグダノヴィッチの面目躍如たるところ。名手L・コヴァックスによる撮影にも注目。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不能者", "不能者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "進は短小のため満足な性行為ができない。相手にする女にはことごとく笑われるが、幼なじみのマリ子が彼と結婚する。進は昼は幸福感でいっぱいの生活をするが、夜を迎えるのが怖くてたまらなかった……。短小男の絶望と怒りをリアルな展開で描いた、ピンク映画界のパイオニア、小林悟監督作品。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANNA KARENINA", "アンナ・カレニナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ロシアの文豪トルストイの代表作を完全映画化。謎に包まれた美の女神、ハリウッドが誇る伝説のスター、G・ガルボが、匂い立つような美しさで主演した文芸作品の名作だ。近衛連隊の青年将校ウロンスキーは、友人の妹アンナ・カレニナを紹介され、あまりの美しさに呆然。彼女が人妻であることも忘れて、熱い恋心を打ち明ける。初めはたじろぎ彼から逃げようとしたアンナもいつしか心動かされ、醜聞も恐れず彼の胸に飛び込んだ。“妻”よりも“母”よりも“女”であることを選んだアンナの恋の道行きが、美しくも哀しい。ラスト、走り来る汽車の前に身を投げたアンナを悼むように鳴り続ける踏切り音が、胸に残響する。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nacho Libre", "ナチョ・リブレ　覆面の神様", "", "", "http://image.eiga.k-img.com/images/movie/33730/315x210.jpg?4126", 100, "「スクール・オブ・ロック」のジャック・ブラック＆マイク・ホワイトが、修道院を立て直すためにプロレスラーとなったメキシコに実在する男の実話を映画化。孤児たちに満足な食事を与えることが出来ていないと感じている修道院育ちの孤児ナチョは、ある日たまたま目にしたプロレスラーの豪華な生活に惹かれ、孤児たちのためにプロレスラーとしてお金を稼ぐ決意をする。監督は「バス男」で全米の注目を集めたジャレッド・ヘス。", "2006-11-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンの頑張り戦術", "エノケンの頑張り戦術", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "エノケン＝中川信夫コンビの第２作。エノケンの相手役に如月寛多を配し、隣同士に住み、家の造りもまったく同じ、勤めも同じという二人の男がなにかにつけ競い合うさまを、スラップスティック・タッチで描いている。エノケンと寛多の壮絶な頑張りが見もの。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タナトス", "タナトス", "", "", "http://image.eiga.k-img.com/images/movie/56390/315x210.jpg?4254", 101, "元WBA世界ミドル級チャンピオンの竹原慎二が原案の人気ボクシングコミック「タナトス　むしけらの拳」を実写映画化。孤独な不良少年のリクは、ある日、将来を有望視されながらも脳に障害があることから日本ではプロボクサーになれない棚夫木と出会う。棚夫木にパンチ一発で倒されてしまったリクは、棚夫木が所属する「西田ボクシングジム」の門を叩き、ボクサーとしての才能を開花させていく。一方、棚夫木は再起をかけてメキシコに渡りプロデビューを目指すが……。", "2011-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBIN AND MARIAN", "ロビンとマリアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "十字軍遠征から18年ぶりに故国に戻ったロビン・フッドと、今や修道院の院長となっているマリアン姫が再会する“ロビン・フッド物語”後日譚。ロビンに扮するコネリーが、心優しい中年好男子ぶりを見せる。マリアン姫役のヘプバーンもなかなかの好演。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場リミックス版　アップルシード XIII 遺言", "劇場リミックス版　アップルシード XIII 遺言", "", "", "http://image.eiga.k-img.com/images/movie/56110/315x210.jpg?2769", 0, "「APPLESEED」（2004）、「エクスマキナ」（07）と2度にわたって映画化もされた士郎正宗の人気コミックを、新たに全13話でアニメーション化。そのシリーズ前半の映像をリミックスして製作した劇場版。巨大人工都市オリュンポスの治安を守る特殊部隊「ES.W.A.T.」のデュナン・ナッツと相棒のブリアレオスは、オリュンポスに牙をむく「人類解放戦線」と戦っていた。しかし、その動乱の裏には、20年前に死んだはずのテロリスト集団「アルゴノーツ」の英雄アル・ケイデスの影があった。", "2011-06-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PETIT BAIGNEUR", "大沈没", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "俳優としても経験豊かなデリー監督が、フュネスの原案のもと、自ら主演したコメディ。フランスの造船会社を舞台に、才能ある技師と、欲の深い社長が巻き起こすドタバタ騒動。両者の息の合った競演ぶりが見事な娯楽作品。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RANSOM！", "誘拐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "当時深刻な社会問題となり始めていた誘拐の事件にメスを入れたヒットTVドラマの映画化。８歳の子供が誘拐され、犯人から身代金を要求する電話がかかってくる。父親は金を払う決心をするが、警察署長と居合わせた新聞記者はそれに反対した。やがて父親がテレビを通じて犯人に、身代金は払わないと回答、世論は彼を非難し、彼もまた苦悶する。1996年にメル・ギブソン主演で「身代金」としてリメイクされた。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREEN MANSIONS", "緑の館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "革命運動のためベネズエラを追われアマゾンの奥地に逃れた若者は、そこで森の妖精のように美しい女に出会う。二人は愛し合うようになり一緒に暮らし始めるが……。オードリーのまばゆいばかりの美しさが圧巻。当時、彼女と結婚したばかりの監督の心境がうかがえるラブ・ファンタジー。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山を守る兄弟", "山を守る兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "大佛次郎の時代小説を基に描いた娯楽時代劇。クライマックスとなる山中での攻防戦など、見どころ満載。甲州の山岳地帯では、玉置家とその敵である豪族の二つのグループが縄張り争いをしていた。そこへ、江戸修行から帰る玉置家の兄弟が、途中で敵方の娘を助けたにも関わらず襲われ、さらに家まで焼かれてしまい悪代官から拷問を受ける。兄弟は越中守へ直訴するが……。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN HOMME ET UNE FEMME：20 ANS DE´JA｀", "男と女 II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "かつて激しい恋に落ちた男と女。アンとジャン・ルイは20年後に再会する。映画の女プロデューサーとして活躍するアンは、二人の愛の思い出を映画にしようと決意する。20年前と同じスタッフ・キャストが組んでの続編。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VERTICAL　FEATURES　REMAKE", "VERTICAL FEATURES REMAKE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "「英国式庭園殺人事件」「数に溺れて」などの傑作を発表しているP・グリーナウェイ監督の、デビュー初期の作品。サイレント映画タッチの中編で、前作「A WALK THROUGH H」の架空の続編という形式を取っている。前作にも登場していたタルス・ルーパーという名の主人公が再登場。1930年代のアヴァンギャルドな映像作家で、彼の企画が失われてしまうストーリーを軸に展開。", "1978-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WOUNDED", "ラスト・ハンター　非情の標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「ツイン・ピークス」「水曜日に抱かれる女」のクール・ビューティ、M・アミックが、ショートヘアのアクティブな美女に大変身したサスペンス・アクション。彼女の役どころはロッキー山脈の森林監視員ジュリー。凶悪な密猟者によって恋人を銃殺されたヒロインが、中年刑事の支えを得て絶望の淵から這い上がり、復讐の女狩人として犯人に挑む姿を描き出す。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TREMORS　THE　WORMS　HAVE　TURNED．", "トレマーズ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "宇宙生まれのエイリアンとは違い、純粋な地球製巨大モンスター“グラボイズ”が大暴れ。「ジュラシック・パーク」にもヒントを与えた第１作目の壮絶な死闘から７年が経ち、再び強靭になって地下怪獣がその姿を現す。ハンターたちが奴らを退治しようと対策を練るものの、今度は新たなタイプが大挙して登場する。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カラテ大戦争", "カラテ大戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "大山倍達の門弟で原作者・梶原一騎の実弟・真樹日佐夫が主人公を演じるカラテ巨編。さすがに本物の達人がやるだけあってアクション・シーンは迫力満点。大神達矢はライバルのタイ式ボクシングの英雄キング・コブラと命賭けの戦いを繰り広げる。なかなか決着のつかない男同士のラスト・ファイトは感動的だ。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あしたのジョー２", "あしたのジョー２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ライバル・力石徹の死から１年。丹下ジムに戻ってきた矢吹丈は、ボクサーとして再デビュー。しかし連勝も束の間、力石との戦いの記憶が後遺症となって、頭部へのパンチが出せなくなっていることを知られてしまう。それでも無冠の帝王、カーロス・リベラとの白熱戦を経て、後遺症から解放されたジョーは、バンタム級世界チャンピオン、ホセ・メンドーサとの試合で、その生命を燃やしつくす。前半はTVシリーズの再編集、後半は映画用の新作で構成されているが、出崎統独特の演出術が冴えわたり、ダイナミックな好編となった。特にジョーとメンドーサの、壮絶極まりないファイトは、アニメならではの迫力に満ちている。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZAPPED AGAIN", "超能力学園Z PART２ パンチラ・ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "高校生が超能力を身につけ騒動を巻き起こした青春映画「超能力学園Z」の続編。数年前のプロム・パーティーで大騒動があったハイスクールへ、一人の少年が転校してくる。彼は、前作の主人公が発明した超能力薬を飲んでスーパーボーイに変身！　またまた、エッチで楽しい大騒動を巻き起こす。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チキン・ハート", "チキン・ハート", "", "", "http://image.eiga.k-img.com/images/movie/1330/315x210.jpg?3648", 105, "同じアパートの住民、元ボクサーの岩野、自由人の浅田、元教師の丸は、昼はバイト、夜は殴られ屋をしながら、だらだらと日々を過ごしている。が、アパートが取り壊されることになり、それぞれが逃げていた問題と対面しなくてはならなくなる。監督・脚本は64年生まれの清水浩。北野武監督の「みんな～、やってるか！」（95）から「BROTHER」（00）までの助監督を務め、「生きない」（98）で監督デビュー、本作が第2作。", "2002-08-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Objective", "オブジェクティブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "", "2008-01-01", "38", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEN NIGHT IS FALLING", "月の瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ミッション系の大学で神話学を教えるカミールは、婚約者のマーティンにも自分自身の生活にも息苦しさを感じていた。そんなある日、愛犬を失ってしまったことで彼女は感情のコントロールが効かなくなってしまう。その日彼女は、サーカスの女性ダンサー、ペトラに出会い、不思議な雰囲気と何にも縛られずに自由に生きるその姿に魅力を感じる。正反対に見える二人だったが、徐々にお互い強く惹かれ合っていくようになる。保守的で常識的な生活を送ってきたカミールは、初めて自分の欲望があふれ出るのを感じるのだった……。「ソナチネ」の名子役P・ビュシエールが成長した姿を見せる。", "1995-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HUNT FOR RED OCTOBER", "レッド・オクトーバーを追え！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "ソビエトの最新型原子力潜水艦レッド・オクトーバーが突然姿を消した。アメリカへの攻撃か、それとも亡命か。対応に苦悩するCIAは、一人の男をレッド・オクトーバーの艦長の真意を探らせるために送り込む。しかしその頃、ソビエト側は艦長が発狂したと断定し、“レッド・オクトーバーを撃沈せよ”との指令を下した。果たしてレッド・オクトーバーを捕らえるのはソビエト側か？ アメリカ側か？ そして艦長は何を考えているのか？「ダイ・ハード」の大成功に続き全米で大ヒットとなったJ・マクティアナン監督による娯楽超大作。個性あふれる登場人物たちの壮絶な駆け引きを描き出した演出は見事。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女競輪王", "女競輪王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "自転車なら人には負けない自信を持つ美樹は恋人の健一に競輪選手になりたいと打ち明け、彼の父、源造から金を借りて日本競輪学校に入学、晴れてB級選手となる。しかし、勝負の世界は厳しく、レースに負けてクビになる選手の姿を見て、美樹は悲しみに襲われたりもした。そんな美樹も27連勝を記録、源造から借りた金も貸すことができたが、その源造からミス競輪レースで八百長をしてくれと頼まれる……。競輪選手として奮闘するヒロインの姿と、競輪界の内幕を描く。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AND GOD CREATED WOMAN", "可愛い悪女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ブリジット・バルドーを世界的なセックスシンボルにした「素敵な悪女」をヴァディム自身がリメイク。刑務所から出所したさに、男を利用する悪女ロビン。結婚することによって出所した彼女は、年齢も社会的地位も違う二人の男をほんろうしながら、彼女自身が求める自由奔放な生活へ戻っていく。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真田幸村の謀略", "真田幸村の謀略", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 148, "関ヶ原の戦いで敗れ、高野山麓に身を潜めながらもひそかに宿敵・家康打倒の機会をうかがっていた真田幸村は、屈強な10人の勇士を集め、家康首取り計画を実行するが……。真田十勇士と家康軍団とのラストの攻防戦がアクションたっぷりで見もの。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIRDS", "バード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 160, "役者のみならず、監督としても高く評価されているC・イーストウッド。これまでも一部を除いて主演も兼ね、アクションものを中心に数多くの作品を手掛けてきた彼がこの作品では演出に専念し、一人の偉大なアーティスト、チャーリー・“バード”・パーカーに焦点を当てている。サックス奏者として1930～50年代にかけて活躍し、ジャズに革命をもたらしながら、34歳の若さで逝った彼の悲劇的な生きざまを、回想を交えた絶妙な演出と深味のある映像で映し出していく。パーカーのオリジナル演奏を生かしたライヴ・シーンも十分に楽しめる。18歳でニューヨークに進出したパーカーは、1940年代後半になるとディジー・ガレスピーらとともに、“ビ・バップ”と呼ばれる新しいジャズの中心的存在として活躍し、音楽的絶頂期を迎えていた。しかし、華やかな活躍とは裏腹に、彼は酒と麻薬に溺れていく。この作品で、イーストウッドはゴールデン・グローブ最優秀監督賞を受賞した。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらば愛しのやくざ", "さらば愛しのやくざ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1980年、ヤクザの悟郎と学生の達也は新宿の夜の街で出会う。お互いに興味を持った二人の間に奇妙な友情が芽ばえていく。悟郎は自分の組と敵対する栄組の工藤と決着をつけようとするが罠にはまる。それを助けたのは達也だった。それから10年の月日が流れ、達也は出所した悟郎と再会を果たすが……。柳葉、陣内のコンビで男の友情を描いた、ニュータッチのヤクザ映画。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("びんばりハイスクール", "びんばりハイスクール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "頭脳明晰で運動神経も抜群、暴走族も一目置く“正義の不良”織田陽湖と、親友で予知能力を持つ珠子の凸凹コンビが、学園の様々な事件に挑んでいく痛快アクション・ムービー。オーディションで選ばれた新人や、“ミス・マガジン”の女子プロレスラーのキューティー鈴木など美少女が大挙登場。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GONE IN 60 SECONDS", "バニシング IN 60”", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "保険会社の盗難車調査員ペイスは、実は高級車専門の泥棒のボス。どんな車でもあっという間に盗み出すテクニックは抜群。製作・監督・脚本・主演・スタントの一人５役を、スタントマン出身のH・B・ハリッキーが務めている。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN WITH THREE COFFINS", "旅人〈ナグネ〉は休まない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "韓国現代文学において、難解といわれる作家イ・ジェハの同名小説の映画化。タイトルの“旅人(＝ナグネ)”とは、故郷を出てさすらい続ける人とその人生を意味する。交通事故で亡くなった妻の遺骨を故郷に埋葬しようとするスンソク。だが、彼女の故郷は南北分断の境界上にある。スンソクは遺骨を持って北を望む地帯を放浪する間、北の故郷に帰ることを願う実業家と彼の面倒を見る看護婦に出会い、心を通わせるが……。南北分断という厳しい祖国の問題を現代韓国人に再び問いかけるイ・チャンホの意欲作。黄色を基調とした色彩処理や、民俗信仰の巫堂（シャーマニズム）の登場など、実験精神にあふれている。ベルリン映画祭カリガリ賞、東京国際映画祭国際批評家連盟賞を受賞した。", "1987-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みすゞ", "みすゞ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1930年、26歳という若さで自らの命を絶った童謡詩人・金子みすゞの半生を映画化。「地雷を踏んだらサヨウナラ」の五十嵐匠監督が、前作に続き、夭折の人物を丁寧に撮り上げた。金子みすゞ、本名テル。15歳で母の再婚とともに移住した下関で、本屋の店番をしながらペンネームで詩を投稿。天才詩人として羨望を集めつつも23歳で結婚、娘・ふさをもうけるが遊郭通いの絶えない夫から淋病をうつされてしまう。離婚後も病に臥せりがちなみすゞに、元夫は娘の親権を主張。そしてある日、彼女は一つの悲しい決断をする……。ピンと張り詰めたような静謐な映像が印象的。生きとし生けるものへの深い炯眼が身に染みる柔らかなみすゞの詩を、セリフに頼らず映像まるごとで体現したような手触りを残す作品だ。許されぬ愛、不幸な結婚など様々な思いを抱えながら何かを悟っていたようにも見える、みすゞの不思議な“たたずまい”を表現した田中美里の存在感も賞賛に値する。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばりの悲しき瞳", "ひばりの悲しき瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「悲しき口笛」「悲しき小鳩」に続く、美空ひばり主演の歌謡映画“悲しき”ものの第３作。ひばりは下町の中学生に扮し、定年間近の中学教師である父が、人の借金を肩代わりしたことから、不幸のどん底に落ちる一家を、明るく健気に支えるという役どころ。小津映画でおなじみの日守新一が、父親役で出演している。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャンク・フード・ジェネレーション", "ジャンク・フード・ジェネレーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "アリゾナでスリをしながら生きるツトムが、恋人に騙された腹いせにマフィアのドラッグを盗んでしまった日本人観光客アミのトラブルに巻き込まれ、二人で逃走を余儀なくされてしまう……。俳優の坂上忍が監督・脚本・編集、そして主演の４役を担当し、オール・アメリカ・ロケを敢行したクライム・ロード・ムービー。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL SOL DEL MEMBRILLO", "マルメロの陽光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "スペインの画家、アントニオ・ロペスは毎年秋になると庭のマルメロの実に向かって絵筆を取る。愛妻と愛娘に見守られながら、時に訪ねてくる友人の画家エンリケと歌を口ずさみながら。マルメロの実はやがて熟してゆく……。10年に1本の寡作の名匠、ビクトル・エリセがドキュメンタリー風に輝く陽光を切り取った、美しい第3作。", "1993-04-10", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EM・エンバーミング", "EM・エンバーミング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "題名のエンバーミングとは、傷ついた人間の遺体を修復＆保存する技術のこと。この仕事に従事する美しきヒロインを、高島礼子がクールに演じている。冷静沈着なエンバーマー、美弥子の元に、ビルの屋上から転落した少年の死体が運ばれてくる。ところが死体の頭部が何者かに強奪され、その行方を追う美弥子は思わぬ真実にたどり着くのだった。サイコ・サスペンスとしてはやや唐突なストーリー展開に違和感を覚えてしまうが、死体に対する執拗なまでに粘っこい描写が強烈な印象を残す一編。青山真治監督としては、これが初の原作ものとなった。ビデオタイトルは「EM・エンバーミング　遺体処置」。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホ・ギ・ラ・ラ", "ホ・ギ・ラ・ラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "核汚染が広がる近未来を舞台に、美しい島で暮らす子供たちの楽園を描くファンタジー。2034年、放射能汚染された子供たちは廃棄物として捨てられている。ドラム缶に詰められ海に流された少年は、謎めいた島ホギララに流れ着く。そこは子供たちだけが生活する楽園だった。彼らは、美しい自然に囲まれながら夢のような生活を満喫していたが……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あにいもうと", "あにいもうと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "室生犀星原作の『兄いもうと』の３度目の映画化。川べりで暮らす一家の娘もんは、ある日、妊娠したことを家族に打ち明ける。父と兄になじられたもんは家を出てしまう。もんを探しに出た兄はストリップ小屋で働くもんを見つけ出す……。草刈正雄や秋吉久美子といった当時の若手をうまく使って大成功を収めた。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIL BY MOUTH", "ニル・バイ・マウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「レオン」の性格俳優G・オールドマンが、親友L・ベッソンの協力を得て撮り上げた監督デビュー作。オールドマンの故郷サウスロンドンを舞台に、自伝的な要素を含んだ人間ドラマを演出している。夜になると、酒と麻薬を求めて街に繰り出す男たちと、パブでウサを晴らしながら家庭を守る女たち。家庭内暴力や麻薬に囲まれた彼らの日常を追いながら、市井の人間模様を切り取っていく。ハンディカメラで捉えられた感情のぶつかり合いがドキュメンタリー風のリアルさを醸し出し、緊迫感が全編に充満。暴力や麻薬にまつわるハードな描写も多いが、登場人物へ向けられた温かいまなざしが何よりも忘れがたい。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOUR ROOMS", "フォー・ルームス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "Q・タランティーノをはじめとするアメリカ・インディペンデント界を代表する４人の監督が、大晦日のホテルを舞台に奇想天外な出来事に遭遇するベル・ボーイの姿を描いたオムニバス映画。マドンナやB・ウィリスなど超大物が意外な役で登場するのも見逃せない。Q・タランティーノが全体の製作総指揮のほか第４話では、監督・脚本・主演を務めている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛を歌うより俺に溺れろ！", "愛を歌うより俺に溺れろ！", "", "", "http://image.eiga.k-img.com/images/movie/56930/315x210.jpg?3230", 0, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブラック・ジャック　ふたりの黒い医者", "ブラック・ジャック　ふたりの黒い医者", "", "", "http://ecx.images-amazon.com/images/I/31GGBXCQNSL._SL210_.jpg", 92, "手塚治虫の代表作のひとつである「ブラック・ジャック」を、実の息子である手塚眞監督がアニメ映画化。世界的製薬会社ダイダロスのビルで爆破テロが発生した。鉄骨に挟まれてしまった瀕死の少年を救うため、無免許での医療行為を無罪にする条件でブラック・ジャックが手術に挑む。事件からしばらく経ったある日、爆破に巻き込まれて重傷を負った母親の子どもたちが、手術を依頼するためにブラック・ジャックのもとを訪れるが……。", "2005-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MADAME　DE…／THE EARRINGS OF MADAME DE…", "たそがれの女心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「忘れじの面影」などで、女性を撮らせたら右に出る者なしのフランスの名匠M・オフュルスが、当時絶頂期のD・ダリューを主演に据えて描いた作品。パリのある貴婦人は借金に困って、夫に内緒で耳飾りを売る。その耳飾りはめぐりめぐってドナティ男爵の手に渡り、パリでその貴婦人と恋に落ちた男爵は彼女に耳飾りを贈るのだった。しかし妻の浮気を知った貴婦人の夫は男爵に決闘を申し込む。一つの耳飾りが引き起こした悲劇の結末は？　演出、美術、撮影どれを取ってもぜいたくな香りにあふれた、オフュルス芸術のまさに真骨頂といえる作品だ。", "1953-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Phileine Zegt Sorry", "フィレーネのキライなこと", "", "", "http://image.eiga.k-img.com/images/movie/33820/315x210.jpg?4796", 94, "オランダ発のピュアでパワフルなラブストーリー。人一倍気が強く謝ることが大嫌いな女の子フィレーネは、ハンサムで優しい理想の男性マックスと恋に落ちる。しかし幸せな気分もつかの間、俳優を目指すマックスはニューヨークへと旅立ってしまう。寂しさに耐えられなくなったフィレーネは彼の後を追ってニューヨークを訪れるが……。「リトル・シスター」のキム・ファン・コーテンが主人公フィレーネをキュートに演じる。", "2007-01-27", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京交差点", "東京交差点", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "新藤兼人の助監督を務めてきた３人の新人監督によるオムニバス。女子高生と中年男の奇妙なふれあいを描く松井稔監督「漂流」、老人福祉の問題を通して、老夫婦の姿を描く須藤公三監督「幸福」、母親に捨てられながらも、施設への入所を拒否する３人の子供たちを描く山本伊知郎監督「出発」。いずれもオールハイビジョンで撮影された意欲作である。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Faute a Fidel!", "ぜんぶ、フィデルのせい", "", "", "http://image.eiga.k-img.com/images/movie/53260/315x210.jpg?2127", 99, "反体制運動や共産主義思想に揺れる激動の70年代フランスを、9歳の少女の目線で描いたハートフル・ドラマ。監督・脚本は、名匠コスタ＝ガブラスの娘で本作が長編監督デビューとなるジュリー・ガブラス。お嬢様学校に通う9歳のアンナは、弁護士の父と雑誌記者の母、やんちゃな弟に囲まれ、何ひとつ不自由のない幸せな毎日を送っていた。ところが、両親が共産主義を支持しはじめたことから生活が一変し……。", "2008-01-19", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WARMING　BY　THE　DEVIL’S　FIRE", "デビルズ・ファイヤー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "音楽ドキュメンタリー・シリーズ“THE BLUES Movie Project”からの一編だが、シリーズ中、唯一のドラマ仕立て。C・バーネット監督自身の実体験が元になっており、南部の町にやって来た黒人少年が、ヤクザ者の叔父さんに連れられてディープなブルースめぐりの旅に出る。ブルースが“悪魔の音楽”と呼ばれていた1950年代を背景に、大人の世界を垣間見る少年の眼を通して、聖と俗とが同居する黒人文化の奥深さを伝える好編だ。サン・ハウスら伝説的なブルースマンの貴重な演奏シーンも満載され、特に女性ブルースマンたちの珍しい映像が、音楽ファンの心理をくすぐってくれる。北部の都会に暮らす少年チャールズは、両親の故郷である南部の町にたった一人で帰省。教会で罪を清めるためだったのだが、迎えに来たのはヤクザ者で有名な叔父。やがて叔父は、チャールズを無理やり放蕩生活に引きずり込む。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITY OF INDUSTRY", "バッドデイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "引退した強盗のプロ、ロイは弟の誘いで宝石店の襲撃に加担。計画は成功したが、仲間の一人である若きキレ者スキッパーが獲物の独占をたくらみ、二人の仲間を射殺した。弟を殺されたロイは命の危険に見舞われながらも、裏切り者スキッパーに立ち向かう……。仁義なき犯罪者の死闘を描いたハードボイルド・サスペンス。復讐に燃えるロイに扮した、演技派H・カイテルの強烈な存在感が光る。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE LOVE ワン・ラブ", "ONE LOVE ワン・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/33630/315x210.jpg?2383", 100, "ボブ・マーリーの息子でシンガーのキマーニ・マーリー主演で贈るラブ・ストーリー。ジャマイカのキングストン郊外を舞台に、音楽コンテストでの優勝を目指すラスタファリアンの青年と、厳格な牧師の父に育てられたクリスチャンの女性の恋愛を描く。監督は、70年代UKパンクとレゲエの橋渡し的役割を果たしたDJのドン・レッツと、「ウォー・レクイエム」等のデレク・ジャーマン監督作品で編集を手掛けてきたリック・エルグッド。", "2006-09-02", "77,36,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バカは2回海を渡る", "バカは2回海を渡る", "", "", "http://image.eiga.k-img.com/images/movie/54900/315x210.jpg?4498", 75, "俳優の弓削智久と須賀貴匡が手掛けた短編映画「FREE」の撮影に密着し、広大なアメリカを舞台に繰り広げられる2人の旅を追ったドキュメンタリー。脚本に沿って撮影を進めながらも、自分たちの作品にどこか物足りなさを感じていた2人。そんな中、旅の途中でなくしたはずのハンディカムが無事発見される。2人はそこに収められていた映像にヒントを得て旅先で出会った人々へのインタビューを開始するが、思わぬアクシデントが発生する。", "2009-11-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代っ子", "現代っ子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "倉本聰と引田功治が脚本を担当した連続TVドラマの映画化作品で、監督には中平康があたっている。父の突然の死によって別々に生活しなければならなくなった長男、次男、長女のそれぞれ３人の異なった生き方を描くシリアスな青春ドラマ。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小森生活向上クラブ", "小森生活向上クラブ", "", "", "http://image.eiga.k-img.com/images/movie/53840/315x210.jpg?2963", 108, "家庭では妻や子供にバカにされ、会社では部下になめられっぱなしの冴えない中年サラリーマンの小森は、ある日、通勤電車の中で痴漢の濡れ衣をきせる常習犯の女に出会い、怒り爆発。女をホームから突き落として退治したことから身勝手な正義感に目覚め、社会の小さな悪を退治すると意気込んで犯行を重ねていく。やがて彼の周囲には、熱狂的な支持者が集まってきて……。室積光の小説「小森課長の優雅な日々」の映画化で、小森課長役の古田新太が映画初主演。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("英雄好漢／TRAGIC HERO", "愛と復讐の挽歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「男たちの挽歌」と並ぶ香港ノワールの力作。香港暗黒街の実力者サイとユンはかつてはともに戦った仲間だった。しかし、サイがトップの座に着いた時、野心あふれるユンは裏切られ、追放される。その後ユンは次第に力をつけ、ついにサイと肩を並べるボスにのしあがった。サイへの復讐に狂うユンは卑劣な手段によって彼のファミリーを壊滅させる。マラッカに逃げのびたサイはユンの弟クオのもとに身をよせるが、ユンはクオの家族もろともサイの妻子を爆殺してしまう。怒りに燃える二人は狂ったユンへの復讐に立ち上がる。ラスト15分にわたる決戦は戦争映画並みの凄絶さ。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD　GALS　OF　THE　NAKED　WEST", "ワイルドギャルズ・オブ・ザ・ネイキッドウエスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "西部劇とヌード映画を合体させた奇才R・メイヤーの怪作。殴り合うガンマン、男を投げ縄で捕まえる娼婦たち、女性を追い掛け回す男たちなどで、風紀が乱れきった西部の町。そこに流れ着いたガンマンが悪を一掃しようとするが……。メイヤーの代表作「ファスター・プシィキャット！キル！キル！」の脚本家J・モランがシナリオに参加。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Big Knife", "悪徳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "　人気俳優チャールズ・キャッスルがスタジオとの契約更新を拒んだ時、事態は最悪な方向へ。重役はキャッスルを服従させようとするが……。巨大映画産業のシステムを暴くシニカルなドラマ。ベネチア映画祭で銀獅子賞に輝いた。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAPA NUI", "モアイの謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "K・コスナーが「ファンダンゴ」からの友人であるK・レイノルズとのコンビで作り上げた一作。舞台は、まだオランダ人によって発見される前の孤島ラパ・ニュイ。島民は貴族のロングイヤー族と労働階級のショートイヤー族の二つに分かれていた。先祖ホツ・マツアに捧げるモアイ造りのため、ショートイヤー族は日々過酷な労働を強いられていた。そんな中、ロングイヤー族の青年ノロは階級の差に疑問を持ち、ショートイヤー族である恋人ラマナとの結婚を望むのだが……。壮大な南太平洋の自然に恵まれたイースター島でのオール・ロケで展開される歴史ドラマ。主役のJ・S・リーが若さあふれる生き生きとした演技を見せる。ビデオタイトルは「ラパ・ニュイ　モアイの謎」。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍鳳鬥／YESTERDAY　ONCE　MORE", "イエスタデイ、ワンスモア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "恋と宝石をめぐって、男女が頭脳合戦を繰り広げる大人の恋愛ドラマ。二人の駆け引きは、高級レストラン、ワインショップ、レース場のVIP席などセレブの通うハイソな場所を舞台に、ミステリアスに描かれていく。アジアのスーパースター、アンディ・ラウが、洗練された大人の男を好演。突然、夫の申し立てで一方的に離婚したトウ夫妻。やがて元妻は資産家の息子から求婚されるが、再婚直前に彼の宝石が元夫に盗まれてしまう。", "2005-10-22", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南京・引き裂かれた記憶", "南京・引き裂かれた記憶", "", "", "http://image.eiga.k-img.com/images/movie/54930/315x210.jpg?4540", 85, "１937年に旧日本軍によって行われた南京大虐殺の被害者7人と元日本兵6人の証言で構成されるドキュメンタリー。元日本兵250人以上、中国人被害者300人以上に取材を敢行、生々しい証言を繋ぎ合わせて南京大虐殺の真相を見つめる。監督はドキュメンタリー作家の武田倫和。", "2009-11-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHERE SLEEPING DOGS LIE", "犬の眠る場所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "女であることを武器にして、男たちを罠にはめていく、おなじみ“魔性の女”ストーンが主演したサスペンス・ドラマ。タイトルは“Let Sleeping dogs Lie”——(眠れる犬は起こさないで放っておく、そうしないと噛まれてしまうだろう)というアメリカの古いことわざから来ている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダメおやじ", "ダメおやじ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "かつては不動産会社のホープだったが、結婚して10年、万年ヒラ社員の何をやってもダメな通称ダメおやじと、その家庭の悲喜こもごもを描き出した、笑いとペーソスあふれるナンセンス喜劇。不動産業の昇進試験を受けるための連夜のしごき特訓が展開される。", "1973-01-01", "76", 0, NOW(), NOW());
