INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pleasure Garden", "快楽の園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "", "1925-01-01", "3,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八月のかりゆし", "八月のかりゆし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "松田龍平が沖縄ロケに挑んだ幻想的な一編。父親は行方不明、母親は他界。身寄りを失った高校生・テルは親戚の謝花家を頼って、母の故郷である沖縄に初めて足を踏み入れる。そこで14歳の従姉妹・マレニと行動をともにするうち、テルは母の秘密を知る。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blood Simple the Thriller", "ブラッドシンプル　ザ・スリラー", "", "", "http://image.eiga.k-img.com/images/movie/1300/315x210.jpg?2883", 95, "コーエン兄弟が長編デビュー作「ブラッド・シンプル」（1984）を自ら再編集し、新撮カットも加えて完成させた完全版。テキサスの片田舎。酒場を経営する短気でケチな男と、その美しい妻。ある日、妻と酒場の従業員との浮気を知った男は、私立探偵にふたりの殺害を依頼する。だが、探偵はトリックを案じ、殺人を偽装して依頼人に報告、逆に依頼人に対して銃を向ける……。", "2000-11-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Phantom of the Opera", "オペラ座の怪人", "", "", "http://image.eiga.k-img.com/images/movie/1140/315x210.jpg?3873", 143, "「キャッツ」「エビータ」などの大ヒットミュージカルを生みだした作曲家アンドリュー・ロイド＝ウェバーの舞台「オペラ座の怪人」を彼の製作で映画化。監督は「フォーン・ブース」のジョエル・シュマッカー。19世紀のパリ、オペラ座。その地下に住む謎の怪人ファントムは、新人歌手クリスティーヌに惹かれ、姿を隠して歌を指導し彼女に主演させるためベテラン女優を負傷させるが、クリスティーヌは青年貴族ラウルと恋に落ちる。", "2005-01-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GATE", "ザ・ゲート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "S・ドーフが映画デビューを果たしたファンタスティック・ホラー。少年グレンは庭で不思議な石を発見。彼が友だちとその石を割ると、中には悪魔を呼び出す呪文が記されていた。庭に開いた巨大な穴から次々現れる怪物たちと少年たちの戦いが始まる。SFXを駆使した驚異的な映像が満載。", "1987-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("All Good Things", "幸せの行方...", "", "", "http://image.eiga.k-img.com/images/movie/57580/315x210.jpg?2043", 101, "ライアン・ゴズリング、キスステン・ダンスト主演で描くラブミステリー。大富豪の御曹司デビッドとごく普通の家庭で愛情に恵まれて育ったケイティは、出会ってすぐにひかれあう。周囲の反対を押し切って結婚した2人は田舎でつつましい生活を始め、やがてケイティが妊娠する。しかし一族の血を自分の代で終わらせたいと考えるデビッドは、ケイティに中絶を強要し……。実在の不動産王セイモア・ダーストの息子ロバート・ダーストの周辺で起きた夫人失踪事件と殺人事件をもとに映画化。", "2012-01-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A View to a Kill", "007／美しき獲物たち", "", "", "http://ecx.images-amazon.com/images/I/51eSKpCXucL._SL210_.jpg", 131, "高性能マイクロチップを追ってパリへ飛ぶボンドの前に現れた青い瞳の男。地球征服をたくらむ大富豪を相手に、頭脳と技の凄絶な闘いが始まる……。クリストファー・ウォーケン＆グレース・ジョーンズの怪演、主題歌をデュラン・デュランが担当、と話題の多いシリーズ第14作。", "1985-07-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PINK FLAMINGOS", "ピンク・フラミンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "いわゆるインディーズ・ブーム、カルト・ブームの大いなる源流が、この「ピンク・フラミンゴ」である。世界でいちばん卑しい人間を競いあうディヴァイン一家とマーブル兄姉。ベビー・ベッドで卵ばかり食う母親、ニワトリをはさまなければセックスできない息子、公園で若い女にハレンチ行為をする男や、生まれた赤ん坊を売りさばく女、といったケタはずれな登場人物たちが、犬のウンチをなめたり、家中の家具をなめまわしたり、野外パーティーでアヌスぱっくん踊りをしたり、警官を食っちまったりと、とにもかくにも、あらん限りの俗悪を展開してみせる。だが同時に、これほど無邪気な好奇心あふれる笑いをたたえた映画も珍しい。画面が俗悪であればあるほど、奇妙なおかしみが増すのだ。ただ残念なことに、いくつかの俗悪シーンにボカシがかかるのが惜しい。主演のディヴァインは、この１作でアンダーグラウンド界のスーパースターとなった。1998年には25周年記念バージョンとして、15分間のシーンが付け加えられた〈特別編〉が公開された。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE QUIET ROOM", "クワイエット・ルーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "両親の不仲に心を痛め、“話さない”ことで訴える幼い少女の心象を独特の映像センスで描ききった異色のホーム・ドラマ。昔はとても仲のよかったパパとママが、今は口を開けばケンカばかり。“もう一度、あのころの幸せな生活に戻りたい。ケンカはもうやめて！” そう、切に願う少女は、口を閉ざすことで二人に気付いてもらおうと試みるのだが……。静かな抵抗を続ける幼い少女の切ない胸の内、そんなわが子の気持ちを察しながらも夫婦仲を修復できない両親の心情を映し取った感動作。", "1996-01-01", "11,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN WILD", "ボーン・ワイルド～サバンナに生きて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "映画製作会社に勤めるクリスティーンは、南アフリカの自然の中で暮らす自然保護主義者ジョンのもとへ、ロマンス映画の企画を持って訪ねていく。しかし彼女は、そんなものより真実のアフリカの姿を伝えてほしいというジョンの熱意や、彼が12年間追い続けている雌の豹の記録フィルムを見て心動かされる。本社に戻って上司のダンに、改めて雌豹の記録映画の企画を提出するクリスティーン。同僚のジャックは反対するが、ダンはゴーサインを出す……。アフリカの動物たちの自然な動きを膨大なフィルムに収め、長時間かけて編集している。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRAIT JACKET ストレイト・ジャケット International ver.", "STRAIT JACKET ストレイト・ジャケット International ver.", "", "", "http://image.eiga.k-img.com/images/movie/53730/315x210.jpg?4191", 75, "科学と魔法が共存する世界。魔法を乱用した人間の成れの果て“魔族”の脅威から人々を守るため、街では魔族退治の専門家・戦術魔法士が雇われていた。そんなある日、魔法士を魔族化させてしまう連続テロが発生する。絶望的な状況に追い込まれた魔法監督官シモンズは、一匹狼の凄腕魔法士レイオットを紹介され……。人気ライトノベル作家・榊一郎の小説を日米共同製作でアニメ化したガン・アクション。榊自らが脚本を担当する。", "2008-06-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST DAYS OF POMPEII", "ポンペイ最後の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ローマ帝国隆盛のころ、一介の鍛冶屋職人から、運命にほんろうされるまま、闘技場の戦士となり、やがて富豪の奴隷商人となった男の波乱万丈の半生を、ポンペイ大地震をクライマックスに描いたスペクタクル・ロマン大作。製作・監督は、モンスター映画の傑作「キング・コング」のコンビが担当。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("D−DAY MINUS ONE 1944", "ザ・ロンゲスト・デイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "ロッシュ大尉をリーダーとするフランス軍兵士たちは、母国を占領したドイツに反撃するためイギリスで特殊部隊の訓練に参加していた。そして４年の歳月が流れ、1944年６月６日、連合国軍によるヨーロッパ上陸作戦の日＝Dデイがやって来る。450名のフランス軍は前日に上陸し、ドイツ軍をたたくように命じられるが……。「史上最大の作戦」などでは描かれなかった、より悽惨な攻防を繰り広げた特殊部隊の局地戦にスポットを当てた戦記スペクタクル。記録映像を随所に挿入したほか、実際に使われたドイツ軍戦車を使用するなど、リアル感が出されている。", "1994-01-01", "2,7,3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MRS.DALLOWAY", "ダロウェイ夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "オランダの女性監督M・ゴリスの「アントニア」に続く第５作。作家V・ウルフが、1925年に発表した同名代表作の映画化だ。舞台は第一次大戦後のロンドン。国会議員ダロウェイを夫に持つ初老のクラリッサが、自宅のパーティーの準備をしながら、30年前のロマンティックな青春の日々を回顧する。当時の恋人ピーターではなく、ダロウェイと結婚した自分の選択は正しかったのだろうか？　そんな時、インドから帰国したピーターが、久々にクラリッサを訪ねて来る……。", "1997-01-01", "3,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GRASSHOPPER", "クリスチーヌの性愛記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "19歳でカナダの田舎からアメリカの都会へ出たクリスチーヌが、自分の気のむくままの生活を続け、わずかの間にコールガールへ転落、貯めたお金も持ち去られる悲劇を描く。M・マクシェーンの小説『通り魔』の映画化。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUNA PAPA", "ルナ・パパ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "タジキスタンの新鋭B・フドイナザーロフの長編第３作。厳格な父と戦争の後遺症で精神を病む兄とともに暮らす17歳の少女マムラカットが、ある晩、俳優と名乗る男に声をかけられ、その日に妊娠してしまう。父は烈火のごとく怒るが、男の顔も名前もわからない。とにかく男を見つけるため、父と兄、マムラカットの３人は旅に出発。やがてマムラカットは、本物の愛と出会うのだが……。出会いと別れ、過酷な現実の中を生き抜いていくヒロインの姿をユーモアを交えながら描く感動のファンタジー。おとぎ話のように幻想的な風景も印象的。", "1999-01-01", "7,77,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プロジェクトY　ゆふいん after X", "プロジェクトY　ゆふいん after X", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "人気の温泉街として知られる大分県湯布院町の町議会選挙を追った記録映画。前町長の逮捕をきっかけに町全体が揺れ動く中、この状態の打破を目指し立ち上がった新人候補者たちの熱き選挙戦の模様を伝える。自治体レベルを越え、日本社会の抱える問題までが見えてくる奥深いドキュメントだ。賄賂による町長の逮捕をはじめ、行政の不祥事が続いた大分県湯布院町。2004年１月、そんなごたごた続きの中、町会議員選挙が始まる。", "2005-02-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("御存じ快傑黒頭巾 神出鬼没", "御存じ快傑黒頭巾 神出鬼没", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "万延元年12月、幕府はプロシヤと修好通商条約を結び、新鋭兵器・火炎砲を手に入れる。しかし、黒頭巾はこの砲を破壊。幕府は、もう一門、火炎砲を得るため、発明者の遺児ミス・ゲルダに頼み込んだ。彼女は交換条件として亡父の遺品“竜之壺”を要求。“竜之壺”をめぐる争奪戦が始まった……。黒頭巾は、蘭学者・山鹿土行の息子・弦一郎という正体を見せ、父子の対面を果たす。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("代打教師 秋葉、真剣です！", "代打教師 秋葉、真剣です！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "『週刊少年チャンピオン』に連載された同名コミックの映画化。学力、スポーツともにトップの有名私立高校に“代打教師”として就任した秋葉真剣が、落ちこぼれ生徒を独特の熱意と体力によって立ち直らせていく過程を痛快に描いていく。吉田栄作が破天荒な教師像を演じるハードなタッチの青春ドラマ。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gambit", "ギャンビット（原題）", "", "", "http://image.eiga.k-img.com/images/movie/58010/315x210.jpg?3331", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAHAR", "スプリング・春へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "イラン・イラク戦争のさなかに製作されたヒューマン・ドラマ。ハメド少年が戦火を逃れてある森へとやってくる。ハメドの住んでいたボスタンが軍隊により占拠されてしまったからだ。美しい泉の近くに住む森番の老人に面倒を見てもらうことになったハメド少年だったが、まだ故郷に残っている両親のことを思い沈みがちだった。しかし、穏やかな森の中で過ごすうち、ハメド少年は老人とうち解けていく……。イランの俊英A・ジャリリが、戦争によって引き裂かれた少年の心情を繊細なタッチで描き出す。製作当時、最も厳しい戦況にあったにもかかわらず、自然の美しさを余すところなく映し出した数々のシーンが印象的。", "1985-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EMISSAY", "KGB／CIA 大統領の密使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "大統領の密命を受け、南アフリカにてKGBへの機密漏洩を防ごうとする米国務次官補。しかしKGBは彼の妻を誘拐、暴行して脅迫手段に訴え出る。国際緊張の犠牲となる夫婦の凄絶な反撃を描くサスペンス・アクション。南アフリカのスター総出演。アメリカ大使役でR・ヴォーンが特別出演している。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Harry and Tonto", "ハリーとトント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "　72歳になるハリーは愛猫のトントと一緒にニューヨークの古いアパートに暮らしていた。しかし建物の取り壊しが決まり、ハリーは退去せざるをえなくなる。近くに住む長男の家は居心地が悪かったため、ハリーはトントをつれてシカゴに暮らす娘を訪ねることに。車で旅するハリーは様々な人々と出会う……。孤独な老人のアメリカ横断行を明るくドライに綴る。アート・カーニーがバイタリティあふれる主人公を好演。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狂気の海", "狂気の海", "", "", "http://image.eiga.k-img.com/images/movie/53780/315x210.jpg?2892", 34, "国会での圧倒的な議席数を背景に日本国首相・真壁晋太郎（田口トモロヲ）は憲法改正に着手するが、首相夫人（中原翔子）は“憲法9条”を偏愛していた。そんな中、合衆国大統領が何者かによって呪い殺されるという事件が起き……。「女優霊」「リング」の脚本で知られるJホラーの立役者・高橋洋が、低予算で“日本沈没”を描くスペクタクル映画。憲法9条改正、合衆国大統領暗殺、日本核武装など過激なテーマも満載。", "2008-06-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLESH AND THE DEVIL", "肉体と悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "無声期のハリウッド映画で“最高の美男美女カップル”として名をはせた、J・ギルバートとG・ガルボが初共演した記念すべきMGM作品。美しき令嬢フェリチタスをめぐって幼なじみの二人の男が争うが、女の不実を知って彼らが友情を取り戻すまでを描いている。ガルボの信頼を得て、後に彼女の専属扱いとなったW・H・ダニエルズの陰影に富んだ撮影も印象的。渡米直後、まだ22歳のガルボはまばゆいばかりだ。原作は、ドイツの作家H・ズーデルマンの小説『消えぬ過去』。", "1927-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こちら葛飾区亀有公園前派出所 THE MOVIE 勝どき橋を封鎖せよ！", "こちら葛飾区亀有公園前派出所 THE MOVIE 勝どき橋を封鎖せよ！", "", "", "http://image.eiga.k-img.com/images/movie/56070/315x210.jpg?4615", 112, "1976年から続く秋本治の国民的長寿漫画を、「SMAP」の香取慎吾主演で実写化したドラマの劇場版。亀有公園前派出所勤務の巡査長・両津勘吉が小学校時代のあこがれの同級生・桃子と偶然再会し、心をときめかせる。そんなとき、警察庁長官の孫娘が誘拐される事件が発生。桃子が事件に巻き込まれていることを知った両津は、真相解明に乗り出すことになる。香里奈、速水もこみちらドラマ版のメインキャストのほか、劇場版のヒロイン・桃子役で深田恭子が出演。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BITKA NA NERETVI", "ネレトバの戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "第二次世界大戦中、ドイツ軍とイタリア軍に対抗して祖国を守ったユーゴ・パルチザンの凄絶な死闘をユーゴ映画界が総力を挙げて製作した戦争巨編。国際的スターも多く参加し、画面に精彩を放っている。同国の民族の運命を重厚なドラマで再現した注目すべき作品。", "1969-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Being Julia", "華麗なる恋の舞台で", "", "", "http://image.eiga.k-img.com/images/movie/33850/315x210.jpg?4819", 104, "「メフィスト」「太陽の雫」で知られるハンガリーの巨匠イシュトバン・サボー監督が、サマセット・モームの「劇場」を原作にロンドン演劇界のスター女優の恋と失恋、そして再生をユーモアたっぷりに描くコメディドラマ。主役のジュリアには本作でゴールデングローブの主演女優賞を受賞し、アカデミー賞にもノミネートされたアネット・ベニング。共演にイギリス演劇界の重鎮ジェレミー・アイアンズ、マイケル・ガンボンら。", "2007-02-10", "10,1,3,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MATKA JOANNA OD ANIOLOW", "尼僧ヨアンナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "歴史的記述をもとにした短編小説の映画化。17世紀のポーランドの寒村にある尼僧院で、院長ヨアンナが悪魔に取り憑かれる。その調査と悪魔ばらいに派遣された一人の神父は、彼女に同情し、なんとか救おうとするが……。閉鎖的環境の中で抑圧される人間たちの姿に、現代ポーランドの苦悩を投影した秀作。", "1961-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不当取引", "生き残るための3つの取引", "", "", "http://image.eiga.k-img.com/images/movie/56020/315x210.jpg?3115", 119, "警察が失態を重ね、世間から批判を浴びていたある日、連続殺人事件が発生。大統領から今回こそ事件を解決するよう厳命が下るが、警察は有力容疑者を誤って射殺してしまう。上層部からこの事実のもみ消しを命じられた刑事チョルギは、ヤクザに証拠づくりを頼み、偽の容疑者を仕立てあげるが……。「相棒　シティ・オブ・バイオレンス」のリュ・スンワン監督が描くサスペンス。「ユア・マイ・サンシャイン」のファン・ジョンミンらが出演。", "2011-04-29", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANIAC COP2", "マニアック・コップ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "劇場では未公開ながらもビデオ販売やTV放映で好評となりホラーファンの注目を集めた「マニアック・コップ」の続編。無実の罪に問われ刑務所で惨死したN.Y.の元敏腕警官が、街の悪を一掃するために墓場からよみがえる。前作同様L・コーエン、W・ルスティーグのコンビによる快作。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おさな妻", "おさな妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "31歳の子持ちの建築家と結ばれた17歳の女子高校生が、妻として母として高校生として、３役をはたしながら懸命に生きる姿を、甘ずっぱく綴った異色青春もの。原作は『ジュニア文芸』に連載された富島健夫の人気小説。この映画の後ヌードを披露する関根恵子が、可愛い清純派を好演。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("貴様と俺", "貴様と俺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "１ヵ月後にタイトル・マッチが控えるボクサー・佃。ある事情から、互いに愛していた恋人・梨香との間に亀裂を生じてしまった彼は、トレーニングもせず荒れまくっていた。トレーナーを買って出た先輩ボクサーの忠告も聞かない彼はある日……。佃のトレーナーに扮した成田三樹夫が、男の友情を絡ませた脇役として好演。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙戦艦ヤマト2199 第二章「太陽圏の死闘」", "宇宙戦艦ヤマト2199 第二章「太陽圏の死闘」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOM−YUM−GOONG", "トム・ヤム・クン！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「マッハ！」の監督＆主演コンビが新たに放つライブ・アクション。大事な象を救うために戦うムエタイ戦士の活躍が繰り広げられる。プロレスや剣の達人とムエタイでガチンコ勝負する、主演のトニー・ジャーの熱演は今回もアツい！ 家族同然に暮らしていた象の親子を密猟者に連れ去られた、ムエタイ戦士の末裔カーム。怒りに燃える彼は、その行方を追ってタイからオーストラリアへ渡り、黒幕の犯罪組織に殴りこむ。", "2006-04-22", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地球防衛ガールズP9", "地球防衛ガールズP9", "", "", "http://image.eiga.k-img.com/images/movie/57100/315x210.jpg?3515", 76, "「いかレスラー」「日本以外全部沈没」の河崎実監督が、自身の初監督作のビデオ作品「地球防衛少女イコちゃん」（1987）を発展させたオリジナルSFコメディ。本格的な怪獣や宇宙人の襲来もなく、組織縮小の憂き目にあう地球防衛軍。女性だけで構成される特殊精鋭部隊「P9（パラノーマルナイン）」は、起死回生を狙う長官の“愛される防衛軍”構想のためアイドル歌手としてデビューすることに。しかしP9の土浦隊員は、一連の流れの中に何者かの陰謀をかぎとり……。P9隊員役に「AKB48」の片山陽加、「SDN48」の野呂佳代、日テレジェニック2010の浅倉結希ら話題のアイドル陣がそろう。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEN A MAN LOVES A WOMAN", "男が女を愛する時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "パイロットのマイケルとキャリアウーマンのアリスは、お互いに一目惚れで恋に落ち、結婚する。しかしマイケルは仕事柄、不在がち。寂しさをまぎらわすためアリスは酒に頼り、やがてアル中に……。破滅寸前の夫婦が直面する残酷な現実を、タイトルと同名のヒット曲にのせて描く辛口のラブ・ストーリー。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE TOUGH COP", "N.Y.殺人捜査線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "実在の刑事ボウ・ディートルの実体験をもとに描くサスペンス。ニューヨーク・クィーンズ地区のイタリア移民の街で生まれたボウ。マフィアに囲まれて育った彼は、暴力を嫌い刑事になった。親友でマフィアのボス、リッチーから情報を得て、裏社会に精通している彼はFBIに目を付けられている。そんなある日、酒と賭博に溺れる相棒のデュークがクラブでトラブルを起こし、現場に向かったボウは、そこでリッチーの愛人オハラと恋に落ち……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH TRAIN", "テロリスト・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「ナバロンの要塞」の原作者A・マクリーンの原案に基づくスリリングなサスペンス・アクション。ソ連を軍事力の増強によって再生しようとする極右軍人が、テロリストを派遣してプルトニウムを搭載した貨物列車を占拠。元傭兵のマイクは国連の要請で特殊部隊を組織し、列車への潜入に成功するが……。", "1993-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRIDE OF THE YANKEES", "打撃王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "アメリカ野球界で連続出場2130回を記録した不世出のホームラン・バッター、ルー・ゲーリックの半生を描いた伝記映画。P・ギャリコの原作をもとに、巨匠ウッドが演出にあたった。ゲーリックの青年時代から、彼が難病よって引退していくまでの姿を、素直に、好感をもって描いていく。主役を演じたクーパーは、ゲーリックのキャラクターを通して、「ヨーク軍曹」「誰が為に鐘は鳴る」といった諸作品に通じる、普遍的なアメリカ人像をここに体現。ハリウッドで後に監督に転向した、名カメラマン、ルドルフ・マテの秀逸な映像が映画の風格を盛り上げる。当時の名選手もゲスト出演する野球映画の佳作。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MILES DAVIS IN DINGO", "ディンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ジャズ・トランペットの帝王、M・デイヴィスが本格的に主演した唯一の作品。オーストラリアの片田舎にある日突然、707機が不時着。そこには、伝説のトランペッター、ビリー・クロスとそのバンドが乗っていた。彼らはそこで演奏を始める。そしてビリーは、見学していた少年ディンゴに“君は音楽をやるべきだ。パリに来たら俺を訪ねろ”と言い残して去っていくのだった。20年後、平凡な生活に満足できないディンゴが、パリのビリーを訪ねてみることにする……。", "1991-01-01", "11,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARADISE， HAWAIIAN STYLE", "ハワイアン・パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "プレスリー主演の21作目。女性問題で傷ついたプレスリーが生まれ故郷のハワイに帰り、パイロットとして商売を始めるというお話。歌あり恋ありのいつも通りのパターンで、新しさはないが、プレスリーの持ち味は満喫できる。彼の挿入歌は全部で10曲。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Aachi and Ssipak", "アーチ＆シパック　世界ウンコ大戦争", "", "", "http://image.eiga.k-img.com/images/movie/57560/315x210.jpg?4753", 88, "おバカでお下劣なテーマに完成まで7年、日本円にして3億5000万円の製作費が費やされた韓国製アニメーション映画。エネルギーが枯渇し、人の排せつした便だけが唯一のエネルギー源になった未来都市。市民の排便量を厳しく管理する政府と、排便能力を失った突然変異種「フロシキ」が結成した反政府組織「フロシキ・ギャング」の抗争で都市が荒廃する中、アーチとシパックのチンピラ2人組はイプニという女優の卵と出会う。シパックがイプニに一目ぼれしてしまうが、イプニは自称映画監督の詐欺師ジミーに特殊なチップを埋め込まれたことで驚異的な排便能力を有し、そのために政府やフロシキ・ギャングから狙われるはめになってしまう。「生き残るための3つの取引」のリュ・スンボムらが声優を務める。第40回シッチェス・カタロニア国際映画祭で最優秀長編アニメーション賞を受賞。", "2012-02-11", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AFFAIRE MAURIZIUS", "埋れた青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "妻殺しの嫌疑によって終身刑の判決を受けたレオナール・モーリツィウス。しかし事件の裏側には、複雑に絡み合った人間模様が存在していた……。J・デュヴィヴィエ監督が描いた人間ドラマの秀作。彼の作品らしくペシミスティックな色合いはぬぐいきれないものの、サスペンス・タッチのストーリー展開が見事だ。", "1954-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千年女優", "千年女優", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "かつて一世を風靡しながらも、30年前に突如銀幕から姿を消した大女優の藤原千代子。彼女の元に小さな鍵が届けられ、それを機に彼女は時空を超えた自分の人生を語り始めていく……。「パーフェクト・ブルー」でデビューした今敏が、現実と幻想の世界を交錯させながら描きあげた、一人の女の壮大かつ繊細な恋と情熱の人生模様。まるで“騙し絵”のようなめくるめく展開に、観る者も幻想世界に取り込まれていくかのようである。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PECCATO VENIALE", "続・青い体験", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "兄が仕事で留守の間、美しい妻ラウラの護衛を頼まれてしまった弟サンドロ。魅力的な彼女に憧れていた思春期のサンドロは、性への好奇心も手伝ってますます彼女の虜に。大ヒットの前作より内容も演出もスマートになり、少年の性を健康的でユーモラスに描くさわやかな青春映画に仕上がった。", "1975-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Search and Destroy", "サーチ＆デストロイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "芸能プロの社長マーチンは、失敗続きで破産寸前。そのうえ、税務署に滞納していた15万ドルの支払いを要求され、妻にも逃げられる始末。やけになったマーチンは、若者のカリスマ的な思想家、ルーサー博士の著書を映画化するという途方もない夢を実現しようとする。しかし、博士は映画化権に法外な金額を要求してきた。そこでマーチンは、ニューヨークでひともうけする計画を立てるが……。マーティン・スコセッシのプロデュースのもと、現代美術アーティスト、D・サーレが監督に挑戦。R・アークエット、D・ホッパー、C・ウォーケンをはじめとする豪華な顔ぶれがスクリーンを彩るハイパー・コメディ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIVINE　SECRETS　OF　THE　YA−YA　SISTERHOOD", "ヤァヤァ・シスターズの聖なる秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "全米ベストセラーの同名小説を映画化したヒューマン・ドラマ。人気女優＆名女優が多数共演し、母娘の絆、女性同士の友情といったテーマを掘り下げたユーモアと感動たっぷりの逸品だ。ニューヨークの若き女性劇作家シッダが、母親ヴィヴィと大ゲンカ。二人を仲直りさせようと集まった母親の親友たち“ヤァヤァ・シスターズ”からあるスクラップブックを見せられたシッダは、母親の意外な一面を知る……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コント55号・水前寺清子の大勝負", "コント55号・水前寺清子の大勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "当時のお笑い界の人気者、コント55号と艶歌の女王、水前寺清子が贈る爆笑コメディ。水前寺のヒット曲『大勝負』のモチーフが加わり、この上ないドタバタ喜劇となった。清子の姉の澄子を巡り、幼い頃からケンカが絶えない金一と次郎。大人になって上京した二人は、ヤクザを手始めに様々な職を転々とする度に、対決を繰り返すが……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神の子どもたちはみな踊る", "神の子どもたちはみな踊る", "", "", "http://image.eiga.k-img.com/images/movie/55710/315x210.jpg?3074", 85, "ベストセラー作家・村上春樹の短編小説をアメリカ人監督ロバート・ログバルがで映画化。ロサンゼルスで母と2人で暮らす青年ケンゴは、宗教活動に熱心な母から“神の子”として育てられ、職場の上司や恋人とも距離を置いた関係しか築けずにいた。そんなある日、耳の欠けた男を見かけたケンゴは、男を本当の父親だと確信して後を追うが……。主人公の恋人役で、名女優ナスターシャ・キンスキーの娘ソニア・キンスキーが銀幕デビューを果たす。共演にジョアン・チェンほか。", "2010-10-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MILK MONEY", "ミルク・マネー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "亡き母の代役を務め、家事を引き受ける12才のフランクは、高校教師の父親トムと二人暮らし。性に目覚めたばかりのフランクは、“ミルク・マネー”を貯めて仲間たちと街へ繰り出すが、ひょんなことから出会った娼婦のVを家に連れて帰ることに……。身体は売っても心は売らなかったV。しかし優しく実直なトムとの出会いによって、彼女は本当の自分を見出していくのだった。M・グリフィスがチャーミングな娼婦に扮する大人のラブ・ストーリー。「花嫁はエイリアン」「恋する人魚たち」などハートウォーミングなヒット作を飛ばしているR・ベンジャミンが、優しさにあふれた作品に仕上げている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悲しくなるほど不実な夜空に", "悲しくなるほど不実な夜空に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "スカトロビデオに出演し家族を養う姉、無職の父、そして怠慢な日々を過ごす弟。互いに干渉し合う訳でもないあいまいな“家族”の姿を淡々と描く異色のホームドラマ。「空の穴」の熊切和嘉監督と大学同期の宇治田隆史が、現代ニッポンの姿を乾いた視点で綴る。主演は数々のピンク作品に野心的に出演している葉月螢。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WEEKEND AT BERNIE’SII", "バーニーズ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "A・マッカーシーとJ・シルヴァーマンのボケとツッコミでヒットした「バーニーズ　あぶない!? ウィークエンド」の続編。保険会社に勤めるラリーとリッチーは、社長バーニーの殺人事件に巻き込まれて以来、不運続き。ところが、バーニーが生前に横領した200万ドルが、カリブの銀行にあると知る。さっそくバーニーの死体を盗み出して、バーニーを生きているように見せかけ、銀行から金を引き出す計画を思いつく。一方では、同じ大金を狙うギャング団が動き出していた。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crossing the Bridge: The Sound of Istanbul", "クロッシング・ザ・ブリッジ　サウンド・オブ・イスタンブール", "", "", "http://image.eiga.k-img.com/images/movie/33900/315x210.jpg?1262", 92, "トルコ最大の都市イスタンブールで独自に発展した音楽シーンにスポットを当てたドキュメンタリー。1980年代のインダストリアル・ミュージック界を牽引したアインシュテュルツェンデ・ノイバウテンのメンバー、アレキサンダー・ハッケ。東洋と西洋、伝統と現代が融合したイスタンブールの音楽に興味を持った彼は、その秘密を探るべくトルコへと旅立つ。監督は「愛より強く」でベルリン国際映画祭金熊賞を獲得したファティ・アキン。", "2007-03-24", "32,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("王様とボク", "王様とボク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "やまだないとの同名コミックを「ブタがいた教室」の前田哲監督が実写映画化した青春ドラマ。恋人キエと初めて結ばれた18歳の誕生日の夜、ミキヒコは子どもの頃に事故にあって以来眠り続けているモリオのことを思い出す。そんなある日、モリオが長い眠りから目を覚ますが、心は事故にあった6歳のときのままだった。着実に大人になっていく自分に戸惑いを感じていたミキヒコは、モリオの姿を通して自由気ままだった子ども時代に思いをはせる。映画初主演となる菅田将暉ほか、松坂桃李、相葉裕樹、二階堂ふみら注目の若手俳優がそろう。", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GELOSIA", "嫉妬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "結婚式を挙げ、新婚旅行へと出かけたロッコとアグリッピーナの若いカップル。しかしその途上、ロッコが何者かに射殺されるという痛ましい事件が起こる。警察は近くに住むネリを逮捕するが、犯人はほかにいて……。P・ジェルミ監督が、三角関係が悲劇をもたらすまでを、ミステリアスな雰囲気の中に描き出している。", "1953-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二代目はクリスチャン", "二代目はクリスチャン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "聖なる教会に仕える純真なシスターに、ヤクザの二代目が惚れ込み、組員全員に洗礼を受けさせるなど大騒動。やはりシスターを愛する刑事と張り合って二転三転の末、二代目とシスターは結婚にこぎつける。ところが、式場で二代目は情婦の手にかかり殺されてしまう。二代目を継いだシスターは、対抗する組の仕打ちに手を出さず、組員にもひたすら耐えることを命じるが、いやがらせはエスカレートし、組員は一人二人と命を落としていく。言いつけを守り、黙って殺される組員の姿にシスターはついに白刃を手に立ち上がるが……。コミカルな前半から一転、緊迫の後半へと転換するテンポも絶妙なスピード感あふれる作品。つか色が強く前面に出ている。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LANCELOT AND GUINEVERE", "剣豪ランスロット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "イギリスのアーサー王の時代を採り上げた、スペクタクル・ヒーロー史劇の大作。騎馬戦アクションはもちろん、剣豪と王妃の悲恋ドラマとしても泣ける仕上がり。王の命を受け円卓の騎士ランスロットは豪族から王妃を救い出したが、王妃との抱擁シーンを王に見られてしまう。二人とも火刑に処せられることになったが、ランスロットは逃亡し、さらに王妃を救うのだが……。", "1963-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("売身契／THE CONTRACT", "Mr.Boo！ インベーダー作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "毎度おさわがせ香港コメディアン、ホイ兄弟共演の第４作にあたる作品だが、日本公開は「ギャンブル大将」よりも先。長兄のマイケルがTV局の専属タレントになって大暴れするというもの。アブない罰ゲームのクイズ番組やら契約書盗み出しのズッコケやら、ホイ兄弟があの手この手の新番組を作り出す。", "1978-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サル", "サル", "", "", "http://ecx.images-amazon.com/images/I/2117PFJX35L._SL210_.jpg", 107, "多額報酬の魅力に惹かれ、新薬投与実験に参加した若者たちに次々と襲いかかる恐しい出来事を、ドキュメンタリー・タッチで描いたサスペンス・ホラー。長編初作品となる葉山陽一郎監督が、実際にクスリの人体実験アルバイトに参加した体験を元に、実験がもたらす狂気の世界をリアルに描写する。自主製作映画の資金に困っていた映画監督の福家は、新薬投与実験のバイトを知り、撮影仲間と実験に参加するため、ある病院へ向かう。簡単に高報酬を得られ、気をよくする彼らだったが、やがて異常事態が起こり始めてしまう。隠しカメラを使用した映像が、生々しい恐怖を醸し出す。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("９ TO ５", "９時から５時まで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ロサンゼルスの大企業に勤める３人のOL。９時から５時まで、横暴なボスにこきつかわれる彼女たちは、団結してボスに復讐する。ウーマン・リブ活動の盛んなアメリカのOLの実態をちょっぴり風刺しながら、女権獲得を訴えるJ・フォンダ主演のコメディ。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPTIONS", "オプションズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "大都会から野生の荒野へ、ある王女の伝記の映画化権（＝オプションズ）を取るためやって来た男のカルチャーギャップ・コメディ。ドナルドは映画会社に勤務する仕事一筋のサラリーマン。ある時彼は、地位も名誉も捨てアフリカで象と暮らすベルギー王女ニコルの半生を映画化するため、アフリカへ旅立つ。だが次々と災難が……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剣と花", "剣と花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "大学時代に傷害事件を犯し、学園を追われた文三郎は、今は酒と喧嘩の日々を送っている。そんな彼は清純な義妹に想いを寄せていたが、自分のような生き方はさせられないと、逆に言い寄る彼女を突き放すのだった……。孤独で喧嘩っ早い男を渡哲也が演じ、ストイックに生きる彼の姿を描いていく。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若い川の流れ", "若い川の流れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "興行的にも批評的にも大成功した前作「陽のあたる坂道」に続いて、田坂が再び石坂洋次郎原作に取り組んだ作品。脚色も前作に引き続き池田一朗が担当している。石原裕次郎演じる豪快な青年社員をめぐって展開される二人の女性の恋のさやあて合戦が明るいタッチで描かれていく。田坂にとっては日活最後の作品となった。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UKオペラ＠シネマ　ビゼー：カルメン", "UKオペラ＠シネマ　ビゼー：カルメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "世界5大歌劇場のひとつとして名高いイギリスのロイヤル・オペラ・ハウスで2007年に上演された名作オペラ「カルメン」をデジタルシネマ化。タバコ工場で働く自由奔放な美女カルメン。ある日、ケンカして逮捕されたカルメンを護送することになった工場の衛兵ドン・ホセは、その途中でカルメンの誘惑に負けて彼女を逃がしてしまう。その後、酒場で再会を果たしたカルメンとホセは、密輸団の一員となって暮らしはじめるが……。", "2008-12-20", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HISTOIRES EXTRAORDINAIRES", "世にも怪奇な物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "特異な作風で知られるE・A・ポーの怪奇と幻想の世界をヴァディム、マル、フェリーニの３監督が、それぞれの個性とスタイルを生かして映画化したオムニバス３部作。「黒馬の哭く館」は殺された男の魂が、黒馬に乗り移り、美しい令嬢を死の世界へ連れ去る。ヴァディムが監督、当時彼の妻だったフォンダが主演。「影を殺した男」は背徳的な生活に溺れる男が、自分と瓜二つで同姓同名の男にことごとく悪事を邪魔されるために相手を殺すが、自らも死ぬ。監督はL・マル。「悪魔の首飾り」は過度の飲酒で絶望的な生活を続ける天才的な舞台俳優が、少女の幻影に取り憑かれ自殺を遂げる物語で、監督はフェリーニ。強烈な風刺を鋭ぎすまされた映像の中に描いたこの第３部が白眉。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("One Shot", "ワンショット（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紀雄の部屋", "紀雄の部屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "「青い春」の高岡蒼佑と「赤線」のつぐみが共演のラブ・ストーリー。「自転車とハイヒール」の新鋭・深川栄洋監督が、等身大の恋をユーモラスかつ切なく描き出す。現役プロレスラーや小劇場役者、アイドルなど脇の出演陣も多彩。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三つ首塔", "三つ首塔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "９年にわたるシリーズの最終作。『小説倶楽部』に連載された同名小説を映画化している。兵庫の山里にある三つ首塔が秘める怪奇な伝説と、10億円の遺産相続をめぐる連続殺人事件とが交錯する中で、金田一耕助と助手の白木静子は、危機をかいくぐりながら事件の真相へと近づいていく……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rear Window", "裏窓", "", "", "http://ecx.images-amazon.com/images/I/51RZudkbRwL._SL210_.jpg", 113, "　ヒッチコックの最高傑作と評される一級サスペンス。自由気ままな生活を送っていたカメラマンがケガをし、車いすの生活を強いられる。部屋から一歩も出られず暇を持て余した彼は、隣人の生活をのぞき見る楽しさを覚える。ある日、ケンカの絶えない夫婦の妻がこつ然と姿を消す。夫の行動に不審を抱いた彼は警察に相談するが……。物語は1セットで展開されるが、構成とカメラワークが秀逸で視聴者はスクリーンとの一体感さえ感じられる。", "1955-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八星報喜／EIGHTH HAPPINESS", "僕たちは天使じゃない！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "TV番組司会者の長男、プレイボーイの次男、漫画家の三男。性格も女性の好みも違う三人兄弟それぞれの恋の行方を描くコメディ。ユンファは次男に扮し、北京オペラ舞台で女装を披露。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女巌窟王", "女巌窟王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ルミとエミの姉妹が踊る港町のキャバレーは、凶悪な麻薬団の根城だった。姉妹は危険を感じ脱出を計画、すんでのところでつらくも逃げきる。数年後、二人は当時キャバレーのボスであった若原に復讐すべく、次々とキャバレー事業で対決する。アドベンチャー感覚を盛り込んだ踊子姉妹のリベンジ物語。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITY SLICKERS", "シティ・スリッカーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ニューヨークのエリート・ビジネスマン３人が、ちょっとした骨休めのつもりで二週間の“カウボーイ体験ツアー”に。万事においてシニカルな生き方が染付いてしまっていた彼らだが、次第に少年の頃のような無邪気さを取り戻して……。現代人の“ミドル・エイジ・クライシス”を面白おかしく描いた、大人のための“スタンド・バイ・ミー”。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA HORSE", "ジャン・ギャバン ドン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "たった一人の孫息子が麻薬組織に絡んでいると知った老地主が“家”を守るため犯罪グループに体当たり。法律に対しても精神力のかたまりをぶつけていく。ギャバンが暗黒街映画で大いに鳴らした貫禄をここでも巧みに生かし、老境の男の闘志をじっくり見せる。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING SIZE", "キングサイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "地下室に住むブラウニー（小さな妖精たち）の世界が舞台。普通の人間と同じサイズになれる薬＝キングサイズの調合法を発見したオロとアダムが王国のエリートたちと激しい戦いを繰り広げる。実際の20倍の大きさでセットを組んで撮影しているため、特撮合成技術にはないスケール感が楽しめるファンタジー・コメディ。アヴォリアッツ国際ファンタスティック映画祭で審査員特別賞を受賞した。", "1987-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("洪興仔之江湖大風暴／WAR　OF　THE　UNDERWORLD", "欲望の街 外伝ロンリーウルフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "大ヒットした「欲望の街」シリーズの仕掛け人、アンドリュー・ラウ、マンフレッド・ウォン、バリー・ウォンの３人が再結集。トニー・レオンを主役に据えて製作したアクション・ドラマ。監督は話題を呼んだ猟奇作「八仙飯店之人肉饅頭」の奇才ハーマン・ヤオ。洪興社と東星社が対立する香港の銅鑼湾。洪興社のチンピラ、チュンは東星社のボスの息子バウに捕まりかけていた。そんな彼をフェイが救う。彼は洪興社のボスの息子でありながら組織を去った伝説の男だった。そのころ、東星社の挑発がエスカレートし、ついに全面戦争が勃発。ヤクザの世界を拒んだはずのフェイやチュンもいや応なくその渦中に巻き込まれていくのだった。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　VIE　PROMISE", "いつか、きっと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「ヴィオレット・ノジェール」「ピアニスト」でカンヌ映画祭の女優賞を２度も獲得しているフランスの名花I・ユペール。とりわけ抑圧された役を演じる機会の多い彼女だが、本作では奔放に生きながら、孤独でもある娼婦を演じていて新鮮。夫と子供を捨て、娼婦になった主人公が娘と再会し、自分の少女時代の記憶をたどることで癒されていく愛情物語だ。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Little Challengers 小さな挑戦者たち", "Little Challengers 小さな挑戦者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "里山の自然の中で保育を行なっている千葉県木更津市・社会館保育園の子どもたちの成長を追った長編ドキュメンタリー「里山っ子たち」の続編。秋の運動会に向けて、逆上がりや縄跳び、竹馬など様々な課題にチャレンジすることになった里山っ子たちに、長期にわたって密着。挫折と達成を繰りかえすことで強い意志力をはぐくんでいく子どもたちの奮闘を、彼らを支える大人たちの様子とともに映しだしていく。", "2009-01-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　BRIDE　SUR　LE　COU", "何がなんでも首ったけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "B・バルドー主演、彼女の前夫R・バディムが監督したロマンティック・コメディ。売れっ子カバー・ガールのソフィは、カメラマンの恋人フィリップとの結婚を望んでいる。ところが、彼がアメリカの富豪の娘バルバラと浮気していることを知り、怒りが爆発。ソフィは彼女に思いを寄せる青年医師アランとともに、冬山へ出かけた二人の後を追うが……。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kill Bill: Vol. 2", "キル・ビル Vol.2", "", "", "http://image.eiga.k-img.com/images/movie/1100/315x210.jpg?3612", 136, "「Vol.1」の直後から続く続編。結婚式をかつての暗殺団仲間に襲撃されたヒロイン、ザ・ブライドの復讐のターゲットは全部で5人。前作では2人を倒したが、本作では残る3人、ビルの弟バド、ビルの現在の恋人エル、そしてかつての恋人ビルへの復讐を遂行していく。前作ではタランティーノの日本製ヤクザ映画への愛が爆発したが、今回はマカロニ・ウエスタンと70年代のショウ・ブラザース製香港カンフー映画への愛が充溢。", "2004-04-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Departed", "ディパーテッド", "", "", "http://image.eiga.k-img.com/images/movie/1050/315x210.jpg?3464", 152, "「タクシードライバー」「グッドフェローズ」の巨匠マーティン・スコセッシ監督が、香港映画の傑作「インファナル・アフェア」（02）を米・ボストンに舞台を置き換えてリメイク。第79回アカデミー賞で作品賞ほか、スコセッシ自身初となる監督賞も受賞した。主演は「ギャング・オブ・ニューヨーク」「アビエイター」に続いてのスコセッシ作品となったレオナルド・ディカプリオとマット・デイモン。共演にジャック・ニコルソン、マーク・ウォールバーグら。", "2007-01-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lezioni private", "課外授業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "ピアノ教師に一目惚れし、思いは日々募るばかりの高校生アレッサンドロ。そんな彼を見て、親友を奪われた嫉妬から同級生ガブリエルは教師を尾行し、秘められた私生活の写真を盗み撮る……。性に目覚めていく若者の欲望を捉えた青春映画。", "1975-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼくの孫悟空", "ぼくの孫悟空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "手塚治虫がこよなく愛した『西遊記』の世界を、手塚プロが劇場向けにアニメ化。お調子者だが憎めない人気キャラ・孫悟空の声を優香が担当し、悟空と仲間たちの大冒険が展開する。天上界で大暴れしたため、釈迦によって五行山に閉じ込められた孫悟空。500年後、三蔵法師に封印を解かれた悟空は、三蔵法師らと共に天竺を目指して旅することになる。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Basquiat", "バスキア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1980年代のニューヨーク・アート・シーンで活躍し、27歳の若さで世を去った画家ジャン・ミシェル・バスキアの生涯を、実話に基づいて描く。成功への道と恋、アンディ・ウォーホルとの親交などのエピソードを絡めながら、癒されぬアーティストの孤独を浮き彫りに。ウォーホルに扮したD・ボウイのなりきりぶりや、D・ホッパー、G・オールドマンら演技派ぞろいのキャストなど、見どころは多い。バスキアの友人だった芸術家、J・シュナーベルが演出を手掛けている。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNSTRUNG HEROES", "想い出の微笑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "12歳のスティーヴンは、科学者の父親シドと、優しい母親セルマ、妹のサンディと暮らしていた。幸せな一家は、ある日、突然の不幸に襲われる。セルマががんに冒されていたのだ。母親の余命が少ないことを隠すシド。しかし、ふとしたことから母親が不治の病だと気付いたスティーヴンは、二人の伯父さんの所へ家出する……。最愛の母親の死に直面して動揺する少年が、変わり者の伯父たちとの生活を通して個性と価値観を学び、大人へと成長していく、ほろ苦くも心温まる上質のハートフル・コメディ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Incendies", "灼熱の魂", "", "", "http://image.eiga.k-img.com/images/movie/57070/315x210.jpg?4341", 131, "レバノン出身のカナダ人劇作家ワジディ・ムアワッドの戯曲を映画化し、第83回米アカデミー外国語映画賞にノミネートされたヒューマンミステリー。心を閉ざして生きてきた中東系カナダ人女性ナワルは、ある日、実の子で双子のジャンヌとシモンに謎めいた遺言と2通の手紙を残してこの世を去る。手紙はジャンヌとシモンが知らされていなかった兄と父に宛てたもので、まだ見ぬ家族を探すためナワルの母国を訪れたジャンヌとシモンは、母の痛切な過去と向き合うことになる。", "2011-12-17", "10,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦国　伊賀の乱", "戦国　伊賀の乱", "", "", "http://image.eiga.k-img.com/images/movie/54270/315x210.jpg?3627", 65, "天正9年、忍びの集団・伊賀の国は織田信長と甲賀の大軍に攻め入られ、苦戦を強いられていた。敵は伊賀に密偵を放つが、伊賀の上忍が内部に潜む裏切り者たちの密会を突き止め、凄腕の下忍・突破を敵の元へ送り込む。自爆攻撃を命じられた突破は腹に爆薬を巻き、護衛の下忍・三者らと敵が潜む洞窟へと向かうのだが……。", "2009-02-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PASSAGER DE LA PLUIE", "雨の訪問者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "地中海に面した初秋の避暑地を舞台に、若い人妻が殺人事件に巻き込まれていく様をスリリングに描いたサスペンス映画。脚本は、S・ジャプリゾがブロンソンをモデルに書き下ろしたもので、この作品でブロンソンは人気をさらに高めた。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺っちのウエディング", "俺っちのウエディング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "幸福な結婚式の日、花嫁が謎の女にいきなりナイフで切りつけられる。命に別状はなかったが、犯人は爆弾で自殺、身元がわからない。当然、世間や警察は花婿・勉の過去の女ではないかと疑う。勉は疑いを晴らすために関係のあった女たちの生存を確かめに出かけるが……。サスペンス・タッチのストーリー展開だが、内容は丸山昇一のオリジナル脚本らしいハートウォーミングなコメディ。ハッピー・エンドのラストまで、安心して見ていられる。一連の根岸作品とは少し異色となるが、ワンカット、ワンカットのキッチリした構図の取り方が喜劇のテンポとうまくマッチして良質の出来となっている。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RE＾VE DE SINGE", "バイバイ・モンキー　コーネリアスの夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "荒涼たるニューヨークを舞台に、孤立した男たちの姿をシンボリックな映像で描いた異色作。ニューヨークでロケを行った、英語の台詞による作品である。主人公ラファイエットは他人とのコミュニケーションが下手な寡黙な青年。彼の数少ない友人には、古代ローマ帝国のロウ人形館を経営しているブラックマン、イタリア人の中年紳士ルイジがいる。ある時、ラファイエットはハドソン川の河原で、生まれたばかりのチンパンジーを拾い、育てることになるが、それは巨大なキングコングの死体から生まれたチンパンジーで……。哀感漂う、フェレーリ流の文明論的作品。ビデオ発売時タイトルは「バイバイ・モンキー／摩天楼ラプソディ」。", "1978-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真夜中のマーチ", "真夜中のマーチ", "", "", "http://image.eiga.k-img.com/images/movie/34280/315x210.jpg?1653", 114, "直木賞作家・奥田英朗による同名小説を「手紙」の玉山鉄二主演で映画化したクライム・サスペンス。イベント会社を経営する横山健司と一流商社の落ちこぼれ社員・三田総一郎は、賭場で数千万単位の金を動かしている美術商・黒川から大金を横取りしようと思いつく。黒川の娘・千恵と知り合った彼らは、父親を嫌っている彼女と手を組んで行動を開始するが……。「SHINOBI」の下山天監督がスタイリッシュかつスピーディに描き出す。", "2007-10-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN WHO KNEW TOO MUCH", "知りすぎていた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "パリの医学会議のあと、妻子を連れてモロッコを訪れたベン。ところが現地で知り合ったルイが、“アンブローズ・チャペル”という言葉を残して何者かに殺され、彼らの楽しいはずの休暇に暗雲が。犯人は某国首相の暗殺をたくらむ一団で、ベンの息子を誘拐し、彼の口をふさごうとする。息子を気づかいながら、ベンは犯人の行方を追うが……。日常生活のなかで、誰もが気が付かないようなところに、巧みな“罠”を仕掛けるヒッチコック監督。ロケ地となったモロッコの片田舎マラケシュに漂う異国情緒も効果的に使い、怒りと恐怖感を銀幕いっぱいに繰り広げた作品。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パルコフィクション", "パルコフィクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "“渋谷のパルコをモチーフにした映画を”というスポンサーからの命題の下、矢口史靖と鈴木卓爾の両監督が手掛けた、コミカルなオムニバス・ムービー。パルコの入社試験を受けた女性の受難や、パルコの店員がバーゲン品ほしさに四苦八苦するなど、抱腹絶倒のエピソードが軽やかに綴られていく。", "2002-07-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A−Kuei ＆ Gatchiｎpo THE MOVIE", "A−Kuei ＆ Gatchiｎpo THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "テレビ東京系で放映され人気を博した、台湾生まれのアニメーション。本国で公開された映画「アークエと魔法のハンマー」と、オリジナル・エピソード「チェルシーの逆襲」の２本立てで公開された。グラビア・アイドルのインリン・オブ・ジョイトイが、世界征服を企む天才少女チェルシー役で声優に初挑戦。世界征服を企む天才少女チェルシーは最終兵器“メカプリオ”を完成。UNCOエージェントの面々は彼女の妨害でお目当ての店に入れず……。（「チェルシーの逆襲」）", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DENIAL", "愛に渇いて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "映画製作に情熱を傾ける恋人と暮らすサラは、映画出演のために町を訪れた女性と出会いがく然とする。その女性、旧友ジュリーとの再会は、サラに昔の悲恋をよみがえらせた。突然の友人の出現で、苦い過去を思い出し、自分の殻に閉じこもっていく女を、R・ライトが好演。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRANGERS KISS", "ストレンジャーズ・キス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1955年のハリウッド。男女の三角関係を描くロマンス映画の撮影で、共演した男女優は現実でも本当に三角関係に陥っていく。だがこれは演技力のない彼らをノセるために、監督がわざと仕向けたものだった。しかしその関係は次第に盛り上がり……。映画の愛をモノクロで、現実の愛をカラーで表現した異色のラブ・ロマンス。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRINCE OF PENSYLVANIA", "旅立ちの季節　プリンス・オブ・ペンシルバニア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ペンシルバニアの炭鉱町の高校生ルパートは、人に左右されぬ自由な生き方を望むが、父親には理解されない。彼を唯一理解するのは年上の女性カーラーだったが、彼女には恋人がいるのだった……。家族とのかかわりや恋を経験して成長していく高校生をK・リーヴスが演じた青春映画。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ご存知！ふんどし頭巾", "ご存知！ふんどし頭巾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "会社では落ちこぼれ営業マン、家では妻や娘から見放されているダメ亭主の帆立沢小鉄は、強くなりたいと願い、正義をなすためにふんどしを頭にかぶり、“ふんどし頭巾”になることを決意する。しかし、なかなか性格を変えることは難しく、不倫をする娘には話を切り出せず、万引き女子高生からは逆襲に遭うことに。一時は落ち込む彼だったが、上司と政府役人の汚職、そして役人が同僚OLの体を要求していると知った時、ついに怒りを爆発させる。中年サラリーマンの突飛な行動を、ユーモアとペーソスを交えて描くシニカル・コメディ。主人公に扮した内藤剛志が哀愁漂う表情で好演を見せている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次男坊", "次男坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1952年、中編映画「鳩」で監督に昇進した野村芳太郎は、その翌年には早くも５本の作品を連発するが、これはその一編。堀尾家の長男・玉男は温和だったが、次男の吉晴は曲がったことが大嫌いで、不正があると黙っちゃいられないガチガチの硬派だった。大学生になった吉晴が恋と就職に青春を燃やす。1956年には続編が作られた。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ソウルパワー", "ソウルパワー", "", "", "http://image.eiga.k-img.com/images/movie/55440/315x210.jpg?3519", 93, "1974 年、ザイール（現コンゴ民主共和国）で行われたモハメド・アリ対ジョージ・フォアマンの世界ヘビー級王者決定戦を盛り上げるため、世界最大級のブラックミュージックの祭典が開催された。“キンシャサの奇跡”と呼ばれた歴史的な試合の前夜、ジェームス・ブラウン、B・B・キング、ミリアム・マケバ、「ザ・クルセイダーズ」ら、伝説のアフリカン・ミュージシャンがステージに結集した舞台裏に迫る音楽ドキュメンタリー。", "2010-06-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("驟雨", "驟雨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "岸田国士の一幕ものの戯曲数編を、水木洋子がまとめて脚色し、成瀬巳喜男が演出した小市民映画の名作。倦怠期にさしかかった夫婦が、様々な波紋に揺られながらも現在の生活の中に幸福を見出していく姿を、きめ細かな描写で綴っている。映画は新婚旅行から戻ったばかりの親戚の娘の来訪で始まり、夫への不満をぶつける娘に、二人がそれぞれの立場から反論することで、夫婦間の微妙なズレが示される。続いて二人の家の隣に若い新婚夫婦が越してきて、物語は両家をお互い映し合う形で展開していく。空間の造型にもこうした劇的構成が反映され、敷居をはさんで向かう妻と夫、生け垣を隔てて隣り合う２軒の家などの計算された配置が見事である。また、玉井正夫による神業のようなカメラが捉えた、夕立の場面での闇の中にかすかに浮かぶ原節子のクローズアップは、ラストで紙風船を打ち合う夫婦の上に広がる晴れわたった空とあざやかな対照をなし、比類ない美しさをたたえている。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SENSE AND SENSIBILITY", "いつか晴れた日に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "過酷な運命に翻弄されながらも、三姉妹が多難な恋を通して成長し真実の愛を得るまでを、様々な人物模様を交えて情緒豊かに描く恋愛ドラマ。19世紀初頭のイギリス、ダッシュウッド家当主ヘンリーは死に際、先妻との息子ジョンに現在の妻と3人の娘を世話させる代わりに、全財産を与えるとの約束をする。だがその遺言はジョンの妻ファニーの反対により反故にされ、夫人と3人の娘たちは新しい家を探すことに……。イギリスの女流作家ジェーン・オースティンの原作を主演のエマ・トンプソン自身が脚本化し、アカデミー脚色賞を受賞した作品。", "1995-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIMIC2", "ミミック2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "他の生物に疑態する凶暴な昆虫の暴走を描いたパニックスリラーの続編。新種の昆虫“ユダの血統”が猛威を振るってから数年後、当時の悪夢に悩まされる女性教師レミの周囲で奇妙な猟奇殺人が続発。不安に駆られる彼女のまえに、やがて人間に疑態したユダの血統が出現。夜の校舎に侵入した教え子を守りながら、レミは必死のサバイバルを繰り広げる。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CURE´E", "獲物の分け前", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "莫大な財産を相続した娘ルネは20歳年上の実業家アレクサンドルと結婚するが、連れ子の息子に恋をする。夫は息子と結託して恐ろしい復讐を企てる。ゾラの小説の現代版。耽美派ヴァディムが、当時の夫人J・フォンダのみずみずしい裸体美を礼賛するために作ったような映画。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O'horten", "ホルテンさんのはじめての冒険", "", "", "http://image.eiga.k-img.com/images/movie/54170/315x210.jpg?3431", 90, "「キッチン・ストーリー」「酔いどれ詩人になるまえに」の名匠ベント・ハーメルが、故郷ノルウェーを舞台に定年間近の老人をコミカルに描いた人間賛歌。2008年度アカデミー賞外国語映画賞ノルウェー代表作品。主演は「ヨーロッパ」「デュカネ・小さな潜水夫」のボード・オーベ。ホルテンは規則正しい生活を送る真面目なノルウェー鉄道の運転士だが、定年退職前夜の送別会で予期せぬ事態に見舞われ、最後の出勤日に大遅刻をしてしまう。", "2009-02-21", "36", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("扉の向こう", "扉の向こう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "結成から22年を迎えた、宮本浩次が率いるロック・バンド“エレファントカシマシ”。彼らのアルバム『扉』を、「誰も知らない」の是枝裕和監督が約３ヵ月に渡って密着取材したドキュメンタリー・フィルム。長年音楽を続けることで陥った、音楽への動機や自身に対する疑問、それらともう一度向き合うための肉体的、精神的取捨選択。過去と現在を見つめ受け入れ、未来への扉を開こうとする彼らの姿が、演出をいっさい排したドキュメンタリーならではのリアルさで観る者に迫る。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スキトモ", "スキトモ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "ミュージカル「テニスの王子様」で注目を集めた若手俳優・斎藤工＆相葉弘樹共演による青春ラブストーリー。大学のボクシング部に所属する蒼井智和。同じ大学の後輩で幼なじみでもある斉藤ヨシキと、智和の血の繋がらない妹・みさおは、どちらも智和に密かな恋心を抱いていた。やがて智和は2人の気持ちを知り、ヨシキとみさおは互いに意識しあいながらも打ち解けていく。「あしたはきっと…」の三原光尋監督が、同性や兄への恋を爽やかに描く。", "2007-01-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画 サラリーマン金太郎", "映画 サラリーマン金太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "同名人気TVドラマシリーズを、辣腕・三池崇史が映画化した痛快作。ヤマト建設に勤務する元暴走族の豪快サラリーマン・矢島金太郎が東北営業所に赴任。やはり豪放な所長、伊郷洋造の下で悪戦苦闘しながらも、市民ホール増改築工事の入札にこぎつける。ところがヤマトが本命に決まりかけた談合の席で、暴力団をバックにつけるライバル会社、芝幡工務店がヨコヤリを入れてきた……。前半ではドラマのテイストを踏襲しながらも、金太郎がかつての仲間を率いて殴りこみをかけるクライマックスでは三池らしいハード・バイオレンスがたっぷり。山崎努演じる伊郷所長のキャラクターも出色で、物語を鮮やかなものにしている。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("守護天使", "守護天使", "", "", "http://image.eiga.k-img.com/images/movie/53720/315x210.jpg?2800", 0, "「キサラギ」（07）の佐藤祐市監督が、第2回「日本ラブストーリー大賞」受賞作を映画化。主演をお笑い芸人のカンニング竹山が務め、佐々木蔵之介や寺島しのぶが脇を固める。冴えない薄給サラリーマンの須賀は、一目ぼれした美少女高校生を闇サイトの魔の手から救うべく、幼馴染のチンピラやイケメンの引きこもり青年ら社会のはみ出し者と必死に奔走するのだが……。", "2009-06-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("元禄忠臣蔵・前編", "元禄忠臣蔵・前編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "史実に忠実な真山戯曲の映画化。監督の溝口健二も時代考証には厳格を極め、松の廊下を原寸大で再現したことはあまりにも有名。映画界のスターに加え、舞台で『元禄忠臣蔵』を系統的に上演していた前進座が総出演し、長回しを多用する溝口演出を支えている。セット建築を新藤兼人が担当。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("義務と演技", "義務と演技", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "映画に先行してTVドラマにもなり、評判を呼んだ人気脚本家、内館牧子の小説を映画化。東洋電器の広報課長、大倉謙次は社会的地位にも恵まれ、結婚７年目を迎えた妻、みさきとの間に娘もあって一見幸福のようだが、セックスレスに陥っており、妻との年に１回ほどの行為が義務感になっていた。一方、照明プランナーとして働く武田祥子は夫、芳彦との演技で応えるだけのセックスに疲れを感じていた。そんな二人が仕事をきっかけに出会い、いつしか結ばれてしまうが、二人の関係はみさきや芳彦の知るところとなり……。二つの家庭が崩壊の危機をむかえていく様を、男女のセックスの駆け引きを交えながら綴り、現代の夫婦像に迫る。カメラマン、仙元誠三による濃密かつお洒落な画面構成が赤裸々な物語にロマンティックなムードを与えている点も見逃せない。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東海道お化け道中", "東海道お化け道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "「妖怪大戦争」の黒田義之と安田公義が共同監督した妖怪ものの第３作。凶行現場を目撃したために、悪い親分に命を狙われる少女を助け、八つ墓村の妖怪たちが悪人どもを退治する。結局、この作品が大映妖怪ものの最終作となった。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("i & i", "i & i", "", "", "http://image.eiga.k-img.com/images/movie/54620/315x210.jpg?4084", 96, "ジャマイカのルーツレゲエ全盛期に深く関わり、レゲエ映画の金字塔「Rockers」にもスタッフとして参加したIZABAことダラス・ロジャースによるレゲエ・ドキュメンタリー。親交のあったボブ・マーリーと「ラスタによる正しいラスタの映画」の製作を約束したというIZABAが、ボブの没後から現在までの25年以上に渡り世界中を周って撮り続けた貴重な映像の数々を通し、レゲエとラスタの真実に迫る。", "2009-07-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHATTER", "シャッター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "殺し屋シャッターは、ある組織から大統領暗殺の依頼を受ける。仕事を成し遂げたあと、報酬を受け取りに行くが、逆に組織から命を狙われるハメに陥る。ひとまず、バーで知り合った女メイリンの部屋へと隠れたが、追っ手は次々とシャッターを襲う。香港を舞台に、カンフー・アクションをとり入れたサスペンス。", "1972-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乙女のゐる基地", "乙女のゐる基地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "航空機の整備という難しい仕事を愛し、飛行機を愛し続ける女子挺身隊員の青春をさわやかに描いた戦意昂揚映画。戦場に行けない女性ら銃後はかくあるべきとでもいった、軍部のメッセージが濃厚な一編。まるで男を愛するよりも飛行機を、ひいては国をこそ愛せといわんばかりであるが、ただし現代とはあまりにも隔絶した、当時の慎ましやかな乙女たちの青春像をかいまみる気持ちよさもある。", "1945-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SONG REMAINS THE SAME", "レッド・ツェッペリン／狂熱のライブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "イギリスのロック・グループ、レッド・ツェッペリンを３年以上も追いかけて作り上げた記録映画。強烈なロック・ビートの中に、陶酔と恍惚、叙情を盛り込んで若者を魅了した彼らのナマの姿が、余すところなく伝えられる。特にラストを飾る、1973年マジソン・スクエア・ガーデンのコンサート模様が圧巻。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本海大海戦 海ゆかば", "日本海大海戦 海ゆかば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "明治38年、日本海軍がロシアのバルチック艦隊を破った海戦を題材にした戦争映画。本編はこれを戦争活劇ではなく、海軍の旗艦・三笠に乗艦した水兵と下士官とが繰り広げる人情ものに仕上げている。機関兵曹に扮したガッツ石松が、軍人らしい風貌で好演。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("8 Mile", "8 Mile", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "デトロイトの工場で働くラビットこと青年ジミーには夢があった。ラッパーとして成功し、どん底の日々から抜け出すこと。プロデビューを目指す彼は街で行なわれているラップ・バトルに出場しようとするが、繊細な性格ゆえにプレッシャーを感じ、棄権してしまう。モデル志望の娘アレックスとの恋、トレーラーハウスに暮らす家族との生活、そして仲間たちと思い描く成功の夢。ジミーは迷い、傷つきながらも、やがてラップのバトルステージに立つ……。カリスマ的人気を誇るラップ・ミュージシャン、エミネムの半自伝的な青春物語。", "2003-05-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／ASAKO　IN　RUBY　SHOES", "純愛譜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "日本、アラスカ、韓国で撮影された日韓合作の恋愛ドラマ。生きる目的を見失ったソウルの青年ウインと、“死”に憧れを抱く孤独な東京の少女・彩。全く関係のない二人を、運命の赤い糸が結び付けていく……。個性派女優の橘実里が、透明感のある演技でヒロインを好演している。", "2000-01-01", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和ひとけた社長対ふたけた社員", "昭和ひとけた社長対ふたけた社員", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "小林桂樹が社長を務める新パターンの“社長”シリーズで、最終シリーズ（正編・続編）となった。大日本製鋼の子会社である洋々産業の社長に就任した若い奈良田真介は、さらに若い昭和ふたけた世代の社員たちを掌握しようとはりきるが……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CIEN NINOS ESPERNDO UN TREN", "100人の子供たちが列車を待っている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "チリ、サンチャゴの南東にある貧しい地域ロ・エルミーダの古い教会で、半年間行われた映画教室の様子を撮影したドキュメンタリー映画。この村の子供たちは小さなころから親とともに重労働し、映画を見に行ったことがある者はほとんどいない。その子供たちに映画の歴史や構造、撮影方法などを遊びながら楽しんで学ばせるのは、チリ各地で映画教室を開いてきたアリシア・ベガという女性。彼女のもとで子供たちはチャップリンに笑いころげ、ミッキーマウスに語りかける。題名にあるリュミエール兄弟の「列車の到着」を見るシーンやバスに乗って映画館に行くシーンの大げさにならない演出が静かな感動を呼ぶ。", "1988-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冬の幽霊たち／ウインターゴースト", "冬の幽霊たち／ウインターゴースト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "人気劇団、WAHAHA本舗の映画製作第１弾。主宰兼演出家の喰始がこよなく愛する北海道・夕張を舞台に、幽霊と人間の奇妙な交流劇を描いたハートウォーミング・コメディだ。監督は喰始の一番弟子、稲葉耕作。ゆうばり国際ファンタスティック映画祭2004では、見事ファンタランド大賞を受賞した。冬の夕張。人々の目に幽霊が見えるようになり、街は騒然。だが、その怪現象にも慣れ、人間たちと幽霊たちの奇妙な交流が始まり……。", "2005-01-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IRON EAGLE ４", "アイアン・イーグル4", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "L・ゴセットJr.とシリーズ生みの親S・J・フューリー監督のコンビが再び組んだ、スカイ・アクションシリーズ第４作。チャッピーこと元空軍司令官チャールズ・シンクレアは、様々な過去を持つ不良少年少女の更正の一環として、パイロットになる訓練を行う飛行学校の教官をしている。相棒は、かつて父を救うためにジェットに乗り込んで大活躍したダグ・マスターズ。ある日、ダグとチャッピーは、６人の飛行生の訓練をしている際に、偶然秘密組織の細菌兵器投下計画を知ってしまう。チャッピーたちは重装備で、戦闘的なその組織に敢然と闘いを挑む！", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Snow Falling on Cedars", "ヒマラヤ杉に降る雪", "", "", "http://image.eiga.k-img.com/images/movie/1850/315x210.jpg?3125", 128, "ワシントン州北西部に浮かぶ、小さな移民の島。幼い頃、アメリカ人少年イシュマエルと日系人の少女ハツエはヒマラヤ杉の洞で愛を育んだ。だが、2人の秘密の時は、太平洋戦争の勃発と共に打ち砕かれた。強制収容所へ送られたハツエは、同じ日系人のカズオと結婚する。それはイシュマエルとの永遠の別 れを意味していた。そして戦後9年を経た1954年冬。数十年ぶりの猛吹雪の中、2人はハツエの夫カズオの裁判で再会する。", "2000-04-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お吟さま", "お吟さま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "キリシタン大名・高山右近と千利休の娘・吟との悲恋を描いた、今東光の同名小説の映画化。吟に有馬稲子、高山右近に仲代達矢を配し、田中絹代の女流監督らしい耽美的でキメの細かい演出により、時代の流れに抗い、自分の愛を通した女性の生き方を映し出している。熊井啓監督によるリメイク版もある。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRACK IN THE WORLD", "地球は壊滅する", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "地下のマグマを取り出し、それをエネルギーとして活用しようという計画が実施された。ところが、大地震が発生し、大規模な地割れが進行。これを食い止めるには水爆の使用しか方法がない……。地球壊滅の危機を描いたSFメロドラマ。当時しては大掛かりな特殊撮影が見どころとなっている。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛欲", "愛欲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "麻井食品の宣伝課長・江崎は銀座の高級バーのマダム奈津子を愛していたが、京都に出張した時に知り合った未亡人の由喜と、にわかに相愛の仲となる。仕事と愛に悩み、江崎は行きづまるが……。タイプの違う女を演じる佐久間良子と三田佳子が美しい。二人の美女の間で苦悩する男を三國連太郎が好演。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("塚本晋也10000チャンネル", "塚本晋也10000チャンネル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "塚本晋也が、高校時代に出演したテレビ『ギンザNOW』や、芝居、８ミリ作品、CMなど自らの作品を編集したもの。1989年PFFイベントで１度だけ上映されただけだったが、1995年に一般公開された。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sweet Rain 死神の精度", "Sweet Rain 死神の精度", "", "", "http://image.eiga.k-img.com/images/movie/53340/315x210.jpg?3954", 113, "音楽好きの死神・千葉は強烈な雨男で、彼が人間界に現れるときはいつも雨。彼の仕事は、7日後に死ぬ予定の人間に近づき、“実行＝死”か“見送り＝生かす”かを判定すること。ある時、1985年の東京に現れた千葉は、次のターゲットとなる薄幸の女性、藤木一恵に近づき観察を始めるが、判定を下す7日目、彼女に思いがけない運命が訪れる。伊坂幸太郎の人気小説「死神の精度」を金城武主演で映画化。共演は小西真奈美、富司純子、石田卓也ら。", "2008-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("是枝裕和監督最新作（タイトル未定）", "是枝裕和監督最新作（タイトル未定）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー対じごく大使", "仮面ライダー対じごく大使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 34, "日本中の子どもたちを熱中させた特撮ヒーロードラマ「仮面ライダー」の劇場オリジナル作品第2弾。富士山頂に設置したスーパー破壊光線砲で日本全土を焼き尽くそうと企むショッカーの大幹部・地獄大使は、東日本ロードレースに参加していた本郷猛と滝和也を襲撃。さらに本郷の仲間である立花藤兵衛らも誘拐してしまう。ショッカーの計画を阻止するべく反撃に出た本郷の前に、地獄大使率いる怪人軍団と新怪人カミキリキッドが現われ……。", "1972-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crocodile", "鰐　ワニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "韓国の鬼才キム・ギドクが1996年に手がけたデビュー作。漢江の橋の下で暮らす路上生活者のヨンペは“ワニ”と呼ばれ、橋から投身自殺した者の死体を引きあげて金品を奪い取るような粗暴な男だった。ある日、自殺を図った女性ヒョンジョンを助けたヨンペは、彼女を自らの性欲のはけ口として利用するように。同じ橋の下で暮らす老人と少年は、ヒョンジョンを守ろうとする。やがて、ヨンペもヒョンジョンに対して愛情を感じはじめ……。", "2007-04-28", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Casualties of War", "カジュアリティーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "　1966年、ベトナム。アメリカ兵エリクソンの所属する小隊は敵地の偵察任務に就く。そんな中、上官のミザーブ軍曹を筆頭とする4人の兵士が現地の娘を誘拐し、レイプしたあげく射殺するという暴挙に走る。彼女を救うことができなかったエリクソンはその件を軍上層部に報告するが、それが原因でミザーブらから命を狙われることに……。上官たちの犯罪に苦悩しながらも、やがて告発へと至る若き兵士、その孤独な戦いと葛藤を実話に基づいて描く。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EDGES OF THE LORD", "ぼくの神さま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ナチス占領下のポーランドを舞台に、天才子役H・J・オスメントがカトリック教徒に偽装してホロコーストを生き延びた11歳のユダヤ人少年を演じる感動作。主人公の少年が預けられる田舎の家の幼い弟を演じたリアム・ヘスは、オスメントに一歩も譲らない演技を披露し作品により深い感動を持たらした。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("螢", "螢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「ちょうちん」「疵〈きず〉」に続いて梶間俊一が手掛けた青春ヤクザ映画。主人公は、その世界でノシていくことを夢みるチンピラ謙次と、彼のためにピンサロからキャッチバー、ソープへと身を落としていく薄幸の女・麻子。この二人の７年にわたる愛の軌跡を描いている。柳葉敏郎の本格的初主演作。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いろは若衆 ふり袖ざくら", "いろは若衆 ふり袖ざくら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "土地の浪人と喧嘩し、江戸追放になった名物団子屋のせがれ・菊田郎。父の手紙を持ち、浜松の親分・仏の銀兵衛のもとに向かうが、同じく江戸を追われたケンカ相手の浪人・権九郎も浜松に流れ着いた。この権九郎を拾ったのが、銀兵衛と対立している天狗屋熊五郎という親分であったことから二人は再び出会うが……。ひばり得意の股旅もの。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUNNY SIDE", "サニーサイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "当時、人気を博していたチャールズ・レイの映画を風刺した作品であり、チャップリンが初期に作った短編映画の傑作。", "1919-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社長洋行記", "社長洋行記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "肩こりの膏薬で売り出した桜堂製薬だったが、香港をはじめとする東南アジア方向で、ライバルの椿パスターに押され気味。社長の本田英之助自ら部下を連れて香港へ売り込みに。けれども美人が多くて目うつり……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤いハンカチ", "赤いハンカチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "日活ムード・アクション最盛期の傑作。石原裕次郎＝浅丘ルリ子の共演作には「夜霧よ今夜も有難う」「夕陽の丘」「帰らざる波止場」など佳作が多いが、本編はそれらのなかでも代表的な１本。横浜麻薬ルートを追っていた刑事の三上は、参考人の男を殺してしまう。彼は自分の過失を悔やんで辞職し、北海道へと流れるが、土屋警部補から、誤射事件は同僚の石塚刑事の罠だと聞かされる。三上が横浜に舞い戻ると石塚は大実業家に変貌していた。石塚は、三上が殺した男の娘と結婚したが、彼女は以前知り合った三上の面影をひきずっている。麻薬を扱う裏稼業を三上に詮索され、彼に妻の心も奪われそうになった石塚は、三上を殺そうとするが……。大ヒットした主題歌、三上のストイックな愛など、ホロ苦いトーンが全体に漂う。さらに、この映画が魅力的なのは、下町の娘として登場する浅丘ルリ子の陽から陰への変貌ぶり。特に三上に抱いてくれと迫り、ベッドに身を投げ出した時の彼女のアップは、女優の美しさのきわみともいえる。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Night and Day", "アバンチュールはパリで", "", "", "http://image.eiga.k-img.com/images/movie/54680/315x210.jpg?4176", 144, "画家のソンナムは妻をソウルに残し、ひとりでパリを訪れていた。ある日、出会った画学生ユジョンに一目ぼれしてしまったソンナムは、妻を気にかけながらもユジョンに思いをぶつける。初めは気持ちのなかったユジョンも、次第にソンナムに惹かれていくが……。監督は韓国の奇才ホン・サンス。", "2009-10-17", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMARCORD", "フェリーニのアマルコルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "フェリーニ自身の故郷である北部イタリアの港町の四季を、少年チッタの眼を通してノスタルジックに描いた作品。いたずら好きのチッタは仲間と一緒に憧れの女性グラディスカのあとを追い回しているが、彼女はチッタのことを子供扱い。一方チッタの父は、ファシズムに反対して拷問を受け、伯父は精神病院に入れられるなど一家には不幸続き。おまけに優しかった母までが死んでしまう。それでも明るさを失わないチッタは結婚の決まったグラディスカを祝福するのだった。町の住人が豪華客船を見物する有名なシーンに代表される人工的イメージと、温かい人間ドラマが絶妙に溶け合った傑作だ。", "1974-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大停電の夜に", "大停電の夜に", "", "", "http://image.eiga.k-img.com/images/movie/1350/315x210.jpg?3166", 132, "12月24日、クリスマスのイブの夕方、突然の停電に見舞われた首都圏を舞台に、自殺しようとする女性と少年、死期の迫った父と息子、不倫中の上司と部下などさまざまな人々の一夜を描く群像劇。監督は「東京タワー」（05）で劇場映画デビューを果たした源孝志。撮影はフランスでジャン＝ピエ−ル・ジュネやフランソワ・デュペロン、ヤン・クーネン監督等と組んでいる永田鉄男。", "2005-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIZALOM", "コンフィデンス 信頼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "I・サボーが、極限状態における男女の愛を描いた作品。第二次世界大戦末期、ナチス統治下のブダペスト。平凡な人妻のカタリンは、夫がレジスタンスに参加していて、当局の追及を逃れるため地下に潜ったことを知らされる。そしてニセの身分証明書を渡され、ヤノシュという見ず知らずの男と夫婦を装うように指示される。極度の緊張と不安の中でカタリンとヤノシュの間に愛が生じる。ともに家族がありながら惹かれ合う男女。特殊な環境に置かれた女の心理と愛の本質を追求した佳作。", "1979-01-01", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ごろつき無宿", "ごろつき無宿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "九州の炭坑夫・武田勇は、落盤事故で死んだ父の遺書で東京へ出ることになる。途中、汽車の中で上京するバレーボール選手・ゆきと知り合い、将来の夢を語り合うが、勇を待っていたのは暴力団の抗争だった……。伊藤俊也、澤井信一郎が脚本を共同執筆した青春アクション。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ライアーゲーム　再生（リボーン）", "ライアーゲーム　再生（リボーン）", "", "", "http://image.eiga.k-img.com/images/movie/57270/315x210.jpg?4046", 131, "甲斐谷忍の人気コミックが原作のTVドラマ「ライアーゲーム」（2007、09）と、その劇場版「ライアーゲーム　ザ・ファイナルステージ」（10）から、主演の松田翔太以外のキャストをほぼ一新。原作で人気のエピソード「イス取りゲーム」を映像化し、復讐に燃えるゲーム出資者たちが仕かけるゲームに挑む天才詐欺師・秋山深一の姿を描く。秋山とともにゲームに挑む新ヒロイン・篠宮ユウ役に多部未華子が扮し、人気子役の芦田愛菜がゲームを仕切る事務局員役で自身初の悪役に挑む。", "2012-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録・新宿の顔〈ジュクのつら〉／新宿愚連隊物語 第２部", "実録・新宿の顔〈ジュクのつら〉／新宿愚連隊物語 第２部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "辺見一家に舎弟入りを誘われた加納だが、これを拒否。しかしある抗争をきっかけに、加納を疎ましく思う辺見は曽根一家と手を結び新宿に乗り込む……。実在の人物、加納貢と彼を取り巻く愚連隊連中の青春群像を、アクション・タッチで描いたドラマの後編。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふうせん", "ふうせん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ふうせんのように自由に生きようとするテキヤ稼業の男二人と、彼らの憧れのマドンナとの交流を描く青春ヤクザ映画。まるでテキヤ版「冒険者たち」を狙ったかのような雰囲気で物語は進行するが、青春ものの要素とアクションとがうまくかみ合わず、どっちつかずの一編となった。宮崎萬純が好演。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不見", "迷子", "", "", "http://image.eiga.k-img.com/images/movie/33620/315x210.jpg?4231", 88, "ツァイ・ミンリャン監督作品の常連俳優リー・カンションの監督デビュー作。謎の伝染病が蔓延する台北の街を舞台に、大切なものを失いかけた人々の姿をリアルに描く。公園で幼い孫を見失ってしまった年老いた女性。彼女は街中を探し歩くが、夜になっても孫を見つけることは出来ず、途方に暮れて公園へと戻ってくる。一方、昼間ずっとネットカフェに入り浸っていた少年もまた、帰りの遅い祖父が気になり公園へとやって来ていた……。", "2006-08-26", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STREETS OF GOLD", "ニューヨーク ベイサイド物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "オリンピック出場という栄光を奪われ、アメリカに亡命して挫折の日々を送る元ボクサーが、若く有能なボクサーのコーチとして再び人生を取り戻していく姿を描く。かつてロシアのボクシングチャンピオンだったアレックは、ユダヤ人であるということで国の代表から外され、失意のうちにアメリカへやって来た……。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("潜水艦イ-57降伏せず", "潜水艦イ-57降伏せず", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "東宝が誇る特撮技術の粋を集めた潜水艦アクションが繰り広げられる戦争映画。当時日本に一隻しかなかった海上自衛隊の潜水艦“くろしお”を使って、リアルかつスリリングなストーリーが展開。日本の敗戦が濃厚となった太平洋戦争末期。ある国の高官を乗せ密命を帯びてカナリア諸島へと出港した潜水艦だが、クルーたちはミッションに疑問を抱いていた。敵の防衛ラインを突破することができるのか？", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠り姫", "眠り姫", "", "", "http://image.eiga.k-img.com/images/movie/53070/315x210.jpg?1852", 80, "漫画家・山本直樹が内田百閒の短編小説「山高帽子」を基に描いた同名コミックを、「のんきな姉さん」の七里圭監督が映画化。中学校の非常勤講師・青地は、近頃いくら眠っても寝不足ぎみで学校へ行くのを億劫に感じていた。奇妙な夢を繰り返し見続ける彼女は、やがて現実にまで違和感を覚えるようになり……。夢と現実の境界線を見失っていくヒロインの心象風景を、画面に姿を見せない登場人物の声や気配で描き出していく。", "2007-11-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京の合唱（Le chorus de Tokio）", "東京の合唱（Le chorus de Tokio）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "勤め先の保険会社をクビになった同僚に同情し会社に抗議、自らもクビとなってしまった岡島。やがて彼は、街で偶然出会った母校の恩師・大村の世話で、彼が学校退職後に始めたカレー屋“カロリー軒”を手伝うことになるが……。小津安二郎が、それまでのハリウッド映画完全再現指向を捨て、日本の風土の中でそのテクニックを充分に生かし始めた、転換点とでもいうべき作品。退職した元教師が東京で料理屋を開き、そこでかつての教え子の世話をするというのは、小津作品にしばしば現れるパターンである。", "1931-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三十六人の乗客", "三十六人の乗客", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "強盗事件が発生し、犯人が上信越方面に逃亡したという報が入る。気の弱い渡辺刑事は偶然、草津行のスキー・バスに乗り合わせていたため警戒を依頼される。乗客は35人。彼には誰もが怪しく思えてくるのだった。バスという限られた空間を舞台にした異色の密室サスペンス・ドラマ。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEBE’S KIDS", "ベベズ・キッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "子持ちの美女ジャミカとデートにこぎつけたロビン。しかし、ジャミカは息子のほかに３人の目つきの悪い子供たちを連れて来た。ロビン一行は遊園地にたどり着いたものの、４人の悪ガキどものせいで、デートはメチャクチャ。そのうえ、ロビンの前妻が現れて……。ラップにバラード、リズム＆ブルースにのって、ヒップホップに展開するアニメーション・コメディ。当初はこの企画、「ドゥ・ザ・ライト・シング」「モ'・ベター・ブルース」などで注目されたコメディアン、ロビン・ハリスが主演する実写版で製作される予定だったが、ハリスの急逝で、急遽アニメに変更されたといういわくつきの作品。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOING UNDER", "ピンク・ノーベンバーを追え!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "タイトルからわかる通り「レッド・オクトーバーを追え！」のパロディ映画。潜水艦恐怖症の艦長が潜水艦“ピンク・ノーベンバー”に乗り込み、ソ連海域に向かうという極秘任務を受ける。そして乗組員は海軍中のカスばかり……。「インデペンデンス・デイ」のB・プルマンほか、クセモノの役者を配して、バカバカしいギャグを連発する。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tickets", "明日へのチケット", "", "", "http://image.eiga.k-img.com/images/movie/33710/315x210.jpg?4699", 110, "「木靴の樹」のエルマンノ・オルミ、「桜桃の味」のアッバス・キアロスタミ、「麦の穂をゆらす風」のケン・ローチら、いずれもカンヌ国際映画祭の最高賞パルムドールを受賞した経験のある3人の名匠が奇跡のコラボレーションを果たしたヒューマンドラマ。ローマ行きの特急列車に偶然乗り合わせた、人種も階級も異なる人々が織り成す人生模様を描く。オムニバス形式ではなく、3つのエピソードが互いに重なり合う1本の長編作品になっている。", "2006-10-28", "3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPERFLY", "スーパーフライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ニューヨークのハーレムに住むポン引き、売人、マダムを含む18人の投資家から出資を得て作られ、ヒットしたインディーズ映画。ハーレムに住むコカイン売人組織のボス、プリースト。金にも女にも不自由しない彼が夢みるのは、本物の自由。大きな取引を成功させ、恋人とこの街から去るという計画を立てたプリーストは相棒のエディに打ち明けるが……。ニューソウル運動の第一人者だったC・メイフィールドの音楽が全編を彩る。監督は「黒いジャガー」のゴードン・パークスの息子であるG・パークスJr.。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗黒街の牙", "暗黒街の牙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1961年の「暗黒街撃滅命令」に次いで福田純が手掛けた“暗黒街”もの。主人公は弟が麻薬中毒に陥り、行方不明となってしまったことから、麻薬ルート撲滅に執念を燃やす秘密捜査官・木崎。彼が弟を見つけ出し、ヤクを牛耳るニつの暴力団を叩き潰す姿が派手な銃撃戦を交えて描かれている。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Revenge", "リベンジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "海軍を退役したコクランはかつての上官であり、メキシコの権力者として君臨するティブロンのもとへ向かい、バカンスを楽しむことに。ティブロンの美しい妻ミリエアと出会ったコクランはいつしか彼女と愛し合う仲になる。それに気づいたティブロンは怒り、コクランに重傷を負わせたあげく、ミリエアを売り飛ばしてしまう。一命を取りとめたコクランはやがて復讐を開始するが……。許されぬ愛、そして壮烈な復讐を描くハードボイルド・ロマン。", "1990-05-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAI—PAN", "タイパン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "『将軍』の著者として知られるクラベルのベストセラーを映画化。1841年、阿片戦争の真っ只中の香港。アヘンと引き換えに銀を手に入れるイギリスの密輸商人たち。その頂点に立つ人物を、人々は“タイパン”と呼んだ。愛と野望が燃えさかる“香港”一大叙事詩。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("後ろから前から（2010）", "後ろから前から（2010）", "", "", "http://image.eiga.k-img.com/images/movie/55330/315x210.jpg?3307", 71, "日活ロマンポルノを現代に復活させた「ロマンポルノRETURNS」シリーズ第2弾として、テレビドラマ「出動！ミニスカポリス」の宮内知美が主演を務めたコミカルなロードムービー。営業成績を伸ばすために女の武器を使うことを思いついたタクシー運転手の桃子。ところが会社にバレてクビになり、さらに売春容疑で警察から追われる身となってしまう。メガホンを取るのは、元芸人で俳優や脚本家としても活躍する増本庄一郎。", "2010-02-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES GRANDES MANOEUVRES", "夜の騎士道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "19世紀末の良き時代。フランスの平和な村にやって来た青年士官と、村一番の美女との恋を描いた風俗コメディ。R・クレール最初のカラー作品で、B・バルドー、D・カレルが、フィリップを追いかける女たちとして脇役で出演している。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鐘", "鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "高度経済成長の黎明期を背景に、虚無と頽廃に明け暮れる当時の若者たちのやり場のないカタルシスを描いた異色作。どうしても自分の作品と呼べるものを作りたいと、当時30歳の青島幸男が、一人で監督・脚本・作詞・作曲など９役を担当。カンヌ映画祭批評家週間に入選した。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIAZ", "選択", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "父が死んだためアリは仕事に就こうと考えるが、学校を卒業してもらいたい母のことを思い、夜間学校に通いながら働くことにする。叔父の紹介で印刷所に行くと、レザという少年がいて、仕事ぶりを見てからどちらか一人を雇ってくれることになる。二人は喧嘩しながら、お互いを認め合うようになる。ある日、レザの家に行ったアリはレザの家が自分の家より貧しいことを知る。数日後、アリは印刷所を辞め、旋盤工として働き始めるのだった。イランの社会状況を反映した骨太な人間ドラマ。", "1991-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　仮面ライダーキバ　魔界城の王", "劇場版　仮面ライダーキバ　魔界城の王", "", "", "http://image.eiga.k-img.com/images/movie/53680/315x210.jpg?2553", 0, "平成仮面ライダーシリーズ9作目「仮面ライダーキバ」の劇場版。高校に通いはじめた紅渡は、クラスメイトのなつきと仲良くなる。そんな中、ファンガイアを越える強敵・レジェンドルガがよみがえった。22年前のとある事件にレジェンドルガ復活の原因があることを突き止めた渡は過去へと飛び、自分の父親である音也や、事件のカギを握るなつきの母親・とわと出会う。奮闘の末、なんとかレジェンドルガの復活を阻止して現代へと戻る渡だったが……。", "2008-08-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ディープ・イマジネーション　創造する遺伝子たち", "ディープ・イマジネーション　創造する遺伝子たち", "", "", "http://ecx.images-amazon.com/images/I/211GQXB817L._SL210_.jpg", 0, "「アニマトリックス」「マインド・ゲーム」など独創的なアニメーションで世界から注目を集めるスタジオ4℃制作によるオムニバス・アニメ。ショートフィルムマガジン「Grasshoppa！」に収録された“スウェットパンチ”シリーズより「ダンペトリー教授の憂鬱」「End of the World」「COMEDY」「彼岸」、そして「スチームボーイ」などの原画を担当した伊東伸高による新作「ガラクタの町」の全5作品。", "2006-07-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自殺マニュアル", "自殺マニュアル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "様々な自殺実践論が説明されて話題を呼んだ『完全自殺マニュアル』をモチーフに、相次ぐ集団自殺の真実に迫る異色ホラー・サスペンス。映画初主演に挑んだグラビア・アイドル、森下千里にも注目。ネットで知り合った４人の若い男女がアパートの一室で集団自殺を図った。ローカルTV局のディレクター、悠は、現場に残された『自殺マニュアル』というDVDと、自殺サイトに現れるリッキーというハンドルネームの女の調査に乗り出す。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浦添ようどれ　よみがえる古琉球", "浦添ようどれ　よみがえる古琉球", "", "", "http://image.eiga.k-img.com/images/movie/56650/315x210.jpg?3187", 73, "三山統一後の琉球王国最初の都といわれる浦添に、1261年に築かれたといわれる王陵・浦添ようどれ。太平洋戦争後の復元・発掘調査で明るみになったさまざまな歴史的事実を通して、琉球王国三山てい立のグシク時代から第二尚王統の時代、薩摩藩の琉球入りで江戸幕府の体制に組みこまれたことによる琉球王国の実質的な滅亡までを追想するドキュメンタリー。", "2011-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEPHEW", "ネフュー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "P・ブロスナンが設立した製作会社アイリッシュ・ドリームタイムの第１回作品。自然に囲まれたアイルランドのイシュダラ島。農場を営むトニーを訪ねて、彼の亡くなった妹の息子チャドがやって来る。初めて出会う甥が黒人との混血であることに驚愕するトニー。チャドの出現は厳格な田舎町に刺激を与え、トニーと島の実力者ジョーとの確執が表面化する。二人の対立の原因は何か、母はなぜ島を去ったのか？　島民の胸の奥に隠された哀しい過去が徐々に明らかになっていく。人々の葛藤や悲喜劇を、荒々しくも美しい自然の中に情感豊かに描き出す。人間くさい温かみを漂わせて、頑固者のトニーを好演したD・マッキャンの遺作となった。", "1998-01-01", "33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Caravaggio", "カラヴァッジョ　天才画家の光と影", "", "", "http://image.eiga.k-img.com/images/movie/55020/315x210.jpg?4418", 133, "16世紀イタリアを代表するバロック絵画の巨匠カラバッジョの生涯を描く伝記映画。主演に「輝ける青春」のアレッシオ・ボーニ。ミラノで絵画の修行をしていたカラバッジョは、幼い頃から恋い焦がれていたコロンナ侯爵夫人の援助で芸術の都・ローマに向かう。ローマでダルピーノの工房に入ったカラバッジョは、繰り出した夜の街で高級娼婦のフィリデに一目ぼれするが、かなわぬ恋と貧困に悩む日々が続く。そんなある日、デル・モンテ枢機卿が絵の評判を聞きつけて、カラバッジョに生活の援助を申し出る。", "2010-02-13", "4,2,9,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Basic Training", "基礎訓練", "", "", "http://image.eiga.k-img.com/images/movie/57290/315x210.jpg?4675", 89, "1967年のデビュー以降、現代アメリカ社会を独自の視点で見つめ続けてきたドキュメンタリー作家フレデリック・ワイズマン監督が、一般の入隊志願者を一人前の兵士に鍛える米陸軍の基礎訓練に密着したドキュメンタリー。行進や格闘などの教練や自動小銃・銃剣の使い方、夜間のほふく訓練、潜伏訓練、イデオロギー教育と洗脳への対抗術など、9週間にわたる非人道的なまでの過酷な訓練の模様が映し出されていく。2011年、ワイズマン監督作を一挙上映する「フレデリック・ワイズマンのすべて」にて日本初公開。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジーナ・K", "ジーナ・K", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "石井聰亙、東陽一、橋口亮輔などの助監督として活躍してきた藤江儀全監督のデビュー作。ヴォーカリスト、“ジーナ・K”として圧倒的な存在感を見せる少女かやの。彼女がイメージとのギャップに苦しみ、ストリッパーである母親に対する葛藤などを抱えながら、愛を知り、成長していく様を熱く描いた。ドキュメンタリー作家の荒木は、消えたヴォーカリスト、ジーナ・Kを追う。まずは伝説のストリッパーである彼女の母親に会うが……。", "2005-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼんち", "ぼんち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "大阪に４代続いた足袋問屋の一人息子・ぼんちの嫁は、義祖母と義母にいびり倒され、追い出されてしまう。残されたぼんちは、華やかな女遍歴を重ねていく。永遠の二枚目・市川雷蔵が、豪華な女優陣を相手に、不世出の役者としての資質を十分に見せつけた市川崑の佳作。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("指輪をはめたい", "指輪をはめたい", "", "", "http://image.eiga.k-img.com/images/movie/53810/315x210.jpg?2665", 108, "芥川賞作家・伊藤たかみの小説を山田孝之主演で映画化。29歳独身で製薬会社の営業マンとして働く片山輝彦はある日、頭を打って気を失ってしまう。目を覚ました輝彦はカバンの中に婚約指輪を見つけるが、その指輪を誰に渡そうとしていたのか思い出せない。そんな時、輝彦の前にまるでタイプの異なる3人の女性が現れて……。輝彦をめぐる3人の女性には、小西真奈美、真木よう子、池脇千鶴が扮する。監督は「檸檬のころ」の岩田ユキ。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUMMO", "ガンモ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「KIDS」の脚本で注目されたH・コリンの監督デビュー作。竜巻に襲われたオハイオ州の町を舞台に、住人たちの虚無的な日常を描く。核となるストーリーを持たず、野良猫を捕獲して小銭を稼ぐ子供たちや、暴力にはけ口を見いだす若者たちの姿をコラージュ。グランジ以降の無気力な時代の空気を、ありのままに切り取った、強烈なインパクトのある作品だ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃尻娘 ラブアタック", "桃尻娘 ラブアタック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "“桃尻娘”シリーズの第２作。高校生の裕子とレナのコンビのあっけらかんとした魅力が光る。物語は、妊娠した裕子の堕胎費用を稼ぐため、ピンクサロンでアルバイトをすることになった、二人の奮戦記。相変わらず、亜湖の舌っ足らずのしゃべりがかわいい。原作者の橋本治が、ゲスト出演している。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK NARCISSUS", "黒水仙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「赤い靴」で見事なカラー映像を披露したM・パウエルとE・プレスバーガーのコンビがルーマー・ゴッデンの同名小説を再び美しい映像で描いた作品。ヒマラヤ山麓の小村に赴任した英国人の尼僧５人が、土地の子供たちを教育しながらも、異常な環境からくる不安や人間関係に悩まされて、信仰心がぐらついていく姿を綴る。主人公の尼僧にはD・カー、僧院で育った奔放な少女にJ・シモンズが扮する。", "1946-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK ROSE", "ブラックローズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "舞台はアメリカ中西部の静かな田舎町ミルバレー。ある日、ヘビメタ・グループ“ブラックローズ”がその町にやって来る。彼らの公演に、道徳を重んじる教育団体は猛反対。しかし、彼らが最初に披露した曲は意外なほどまともで美しく、大人たちを安心させてしまう。やがて、大人たちが帰ったところを見計らって、バンドは本性を現わした。彼らは悪魔の化身だったのだ……。“悪魔のロック”にのせられて、狂暴化していく若者たちの姿を描くロック・ホラー。監督はホラー・マニアを自称する新鋭J・ファサーノ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cars 2", "カーズ2", "", "", "http://image.eiga.k-img.com/images/movie/54470/315x210.jpg?1412", 112, "ジョン・ラセター監督によるピクサーアニメ「カーズ」（2006）の続編。前作に続きラセターが監督を務める。天才レーシングカーのマックィーンとおんぼろレッカー車メーターの珍コンビが、前作の舞台ラジエーター・スプリングスを飛び出し、日本をはじめフランス、イタリア、イギリスとワールド・グランプリレースを転戦。その道中でスパイ工作に巻き込まれていく姿を描く。", "2011-07-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KIDS", "キッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ニューヨークの街角にたむろする、ストリート・キッズのセックスとドラッグに明け暮れる24時間をドキュメンタリー・タッチで描く。セックスのことしか頭にないストリート・キッズのテリーとキャスパーは、いつもようにあてもなく街をぶらついている。テリーとしかセックスしていないジェリーは、HIV検査の結果がポジティブとの宣告を受け、彼にこの事実を伝えようと街をさまよい歩く……。写真家L・クラークの初監督作品。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILVER STREAK", "大陸横断超特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "アメリカ大陸を横断する列車の中で、主人公は謎の美女と知り合う。やがて殺人事件が起き、事態は思いがけない方向へ……。ヒッチコックを思わせるラブ・サスペンス。ヒラー監督の職人芸ともいえる緩急自在な演出は、ラストの列車暴走場面で最高の迫力を生み出した。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NUIT D’E´TE´ EN VILLE", "真夜中の恋愛論", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "都会の夏の夜に出会った男と女が、アパルトマンの一室で一夜をともにし語り合う。ただそれだけのシンプルなストーリー展開の中に、恋の駆け引き、感情のさざ波立つ様子を綴るソフィスティケイテッドな対話劇。エスプリの効いた洒落た作品がお得意のM・ドヴィルらしい、粋でお洒落な恋愛映画だ。名手B・リュティックの洗練されたカメラワークが秀逸。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("必殺仕掛人・梅安蟻地獄", "必殺仕掛人・梅安蟻地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "出演者をTVのレギュラー陣に入れ替えた第２作。梅安と浪人・小形十五郎は、裏で悪事をたくらむ伊豆屋と医者の宗伯の殺しを頼まれ、衆人環視の中で伊豆屋を殺す。梅安のテクニックが見ものだが、全体的なまとまりに欠ける。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE I INSIDE", "Re：プレイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "巧妙に作り込まれたストーリーで観客を虜にした「アイデンティティー」の脚本家M・クーニーによる舞台劇を、「トンネル」のR・Z・リヒター監督が映画化。２分間の臨死体験によって失われた、２年間分もの記憶の謎をパズルのように解明していくという、斬新なアイデアで練られたサスペンス。何が真実で何が虚構なのか？　緊急で病院に運ばれた男サイモン。２分間におよぶ心拍停止に陥るが、奇跡的に命を取り留める。しかし、目覚めると彼は過去２年間の記憶を失っていた……。", "2003-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT BOAT TO DUBLIN", "間諜M１号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "核爆弾の製造法発見者であるスウェーデン人科学者が失踪。ナチに誘拐されたと判断した英国諜報部は調査要員を送るが、彼は怪しいとにらんだ資本家を尾行するうちにダブリンからの夜行船で殺されてしまう。同僚が再び資本家を追うが……。第二次大戦を背景にしたサスペンス。", "1945-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森と水のゆめ", "森と水のゆめ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "北海道・新得町在住の藤本幸久監督が、地元の有志と一緒に３年がかりで完成させたドキュメンタリー。大雪山系トムラウシ山にカメラを持ち込み、山の奥深くまで丹念なロケを敢行。樹齢300年ともいわれるエドマツ、トドマツなど針葉樹の森林、十勝川の源流、イワナが群生する沢、そこに生きる鳥や昆虫の生態などが、映像でスケッチされていく。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　PRICE　OF　MILK", "ミルクのお値段", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ニュージーランドの牧場を舞台にした、おとぎ話風ロマンティック・ラブ・ストーリー。結婚を前にした幸せなルシンダに突如、愛の試練が見舞う。二人の仲に嫉妬する女友達、二人の愛を呪縛する森の妖精。失いかけた愛を取り戻すために彼女の取った方法は……。「ロード・オブ・ザ・リング…」のエオメル役として世界中に認知されたK・アーバンがピュアな主人公を好演している。", "2000-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アヒルと鴨のコインロッカー", "アヒルと鴨のコインロッカー", "", "", "http://ecx.images-amazon.com/images/I/517yGuXEeSL._SL210_.jpg", 110, "", "2007-06-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCKIN' BROADWAY", "ROCKIN' BROADWAY", "", "", "http://image.eiga.k-img.com/images/movie/55620/315x210.jpg?3727", 95, "DJ、BAND、DANCEという一貫したスタイルで活動を続けるロックイベント“ROCKIN' BROADWAY”の10周年を記念して製作されたロードムービー。監督・脚本を手掛けるのは同イベントの主催団体RED HOT GANGのリーダーで、「呪怨」シリーズの清水崇監督の実弟でもあるDJ.TERRY。“夢見るころを過ぎて”をテーマに、若者たちが抱えるかっとうや喜びを、実際のライブシーンやダンスシーンを織り交ぜながら描いていく。", "2010-10-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ice Age 2: The Meltdown", "アイス・エイジ2", "", "", "http://image.eiga.k-img.com/images/movie/1440/315x210.jpg?1503", 91, "大ヒットアニメ「アイス・エイジ」の続編。前作に登場したマンモスのマニー、サーベルタイガーのディエゴ、ナマケモノのシドの3匹に、今度は氷河期の終焉の大洪水の危機が迫る。今回登場する新キャラは、「TAXY NY」などで活躍するクイーン・ラティファが声を演じるメスのマンモスのエリー。彼女は、フクロネズミたちに育てられたため自分をフクロネズミだと思っている変わり種。監督は前作で共同監督を務めたカルロス・サルダーニャ。", "2006-04-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIS−MOI　QUE　JE　RE＾VE", "夢だと云って", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "様々な問題を抱える家族の悲喜劇を温かいユーモアで描くハートフル・ドラマ。フランスの田舎町。19歳のジュリアンはちょっと精神的に不安定で何かと騒ぎを起こしてばかりだが、家族は彼を優しく見守っていた。そんなある日、ジュリアンが弟の恋人に一目惚れし強引にアプローチしたことで深刻な事態に発展。母がついにジュリアンの病院収容を決意したとき、祖母が21年間秘めてきたある事実を打ち明ける。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我是大丈夫", "無問題〈モウマンタイ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ナインティナインの岡村隆史が主演した香港映画。突然、別れを告げて香港に行ってしまった恋人、玲子を追って現地へ飛んだ大二郎。友人のコネで撮影所に潜り込んだ彼はいつの間にか映画のスタントマンとして活躍するようになるが、玲子の心は取り戻せないでいた。そんな大二郎のアパートに、不法入国で警察に追われる中国人少女リューが逃げ込んでくる。恋人を追って来たという彼女に同情し、一緒に恋人探しを始める大二郎。二人の心は次第に接近していくのだが……。スタント・シーンでの派手なアクションや笑い、涙を交えながら、二人の恋愛模様を追ったラブ・ストーリー。サモ・ハン・キンポーが本人自身の役で特別出演している。", "1999-01-01", "6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("將軍３／THE GENERAL’S SON PART３", "将軍の息子３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "韓国で記録的なヒットを記録、空前の巨費を投じたシリーズの完結編で、日本でも公開された。韓国独立闘争の英雄的存在、キム将軍の息子、キム・トゥハンの波乱万丈の半生を描く。1930年代、日本の植民地政策が強まるソウルを、ヤクザとして渡り歩く斗漢。警察や検兵隊をバックにつけた彼が、地元に進出した日本人ヤクザに戦いを挑んでいく。", "1992-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヱヴァンゲリヲン新劇場版：破", "ヱヴァンゲリヲン新劇場版：破", "", "", "http://image.eiga.k-img.com/images/movie/53380/315x210.jpg?3415", 108, "社会現象を巻き起こしたTVアニメ「新世紀エヴァンゲリオン」を新たに描き直す劇場版4部作の第2部。汎用ヒト型決戦兵器エヴァンゲリオンに乗り、自ら戦うことを選んだ14歳の少年、碇シンジ。そんな彼のもとに、新たにエヴァ2号機とそのパイロット、式波・アスカ・ラングレーらが加わり、謎の敵“使徒”との戦いは激化していく……。TV版に登場しなかった新キャラ、真希波・マリ・イラストリアスや新メカのエヴァンゲリオン仮設5号機なども登場。", "2009-06-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE ANGST", "不安", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "薬品会社を経営する夫や子供たちと平和な家庭生活を送る人妻が、いつしか若い作曲家と不倫の関係に陥った。夫の目を盗み、密会が続けられたが、ある日彼女の前に脅迫者が現れ金をせびり始めた。脅迫者を操る影の男とは……。不倫の罪悪感と脅迫者の影におびえる不安の日々。実生活で、主役のI・バーグマンと愛人関係にあったR・ロッセリーニ監督が、サスペンス・タッチで描いた愛の心理ドラマ。追い詰められていく女の不安感を綿密な演出で描いていく。", "1954-01-01", "4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五人の突撃隊", "五人の突撃隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "本郷巧次郎、川口浩ほか大映の青春スターがズラリ顔をそろえた戦争アクション。太平洋戦争末期のビルマを舞台に、最前線に取り残された５人の若き兵士の運命を、ヒューマニズム豊かに語り明かす。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新しい風／若き日の依田勉三", "新しい風／若き日の依田勉三", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "北海道十勝支庁の首都・帯広市の開基120周年を記念し、帯広の原野開拓に尽力した依田勉三の生涯を映画化した歴史ドラマ。時は明治初頭。30余名の同志からなる“晩成社”を率いて、未開の蝦夷地（当時の北海道）に踏み出した依田の熱意は、やがて人々を巻き込み、日本を支えるまでに発展し、現在の北海道農業の原点ともいうべき“新しい風”を吹き込んだ。常に未来の日本を展望し続け、満ち満ちる生命力でその人生を駆け抜けた知られざる偉人の生き様は、観る者の心をとらえて離さない。明治15年。“晩成社”の同志とともに蝦夷地十勝野の開拓に乗り出した依田勉三。しかしオベリベリ（帯広市）に辿り着いた彼らを待っていたのは、自然災害、飢餓の悲劇だった。", "2005-12-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hurt Locker", "ハート・ロッカー", "", "", "http://image.eiga.k-img.com/images/movie/53910/315x210.jpg?4209", 131, "戦時下のイラク・バグダッドで爆発物処理に従事する特殊部隊EODの活躍を描くサスペンス・ドラマ。04年夏、これまでに870以上の爆発物を解体処理しているジェームズ2等軍曹がEODの新リーダーとして赴任してくる。部下となったサンボーンとエルドリッジはあと39日でEODの任務から外れる予定だったが、恐れ知らずのジェームズにより、これまで以上の危険にさらされることになる。監督は「ハートブルー」「K-19」のキャスリン・ビグロー。第82回アカデミー賞では作品賞以下6部門を受賞、ビグローは女性で初めての監督賞受賞者となった。", "2010-03-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヤマトタケル", "ヤマトタケル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "『古事記』を題材に、ヤマトタケルの英雄伝説をロール・プレイング・ゲーム感覚で映画化したSFアドベンチャー。不吉な力を持つとして故郷を追われたヤマトタケルは巫女・オトタチバナと出会い、自らの神に選ばれた戦士としての運命を知り、邪神に仕える祈祷師・ツキノワとの壮絶な戦いに赴くが……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　MAN　WHO　LOVED　WOMEN", "グッバイ、デイビッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "彫刻家デイビッド・ファウラーは女性のあらわな太ももを見ただけでもイッてしまうという超純情男。そんな彼が若くて美しい女医さんに恋をしてしまったから、さぁ大変。なんとか、すぐイッてしまう妙な病気(？)を治そうと厳しい訓練を開始するのだが……。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ROYAL BALLET", "ローヤル・バレエ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "イギリス・バレエ界の至宝マーゴット・フォンテーンを擁するローヤル・バレエ団のレパートリーから『白鳥の湖』『火の鳥』『オンディーヌ』を紹介したバレエ映画。戦前ドイツで活躍したツィンナーが製作、11台のカメラがあらゆる角度から舞台を捉えている。", "1960-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("麻雀飛翔伝　哭きの竜　外伝2", "麻雀飛翔伝　哭きの竜　外伝2", "", "", "http://image.eiga.k-img.com/images/movie/56720/315x210.jpg?1530", 77, "能條純一の人気麻雀コミック「麻雀飛翔伝　哭きの竜」シリーズの「外伝」を2部作で映画化した後編。桜道会の内部抗争時代から10年。死んだと思われていた竜が新宿歌舞伎町に出現。時を同じくして、関西最大の組織・共武会が全国制覇に向けて動き始める。主演は「仮面ライダー響鬼」ほか多くの平成仮面ライダーシリーズに出演している松田賢二。", "2011-07-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPELLBOUND", "白い恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "恐怖小説で有名なF・ビーディングの原作を映画化したヒッチコック得意のラブ・サスペンスもの。精神科の女医と医学博士の恋に絡む殺人事件。男の過去には恐るべき秘密があった……。シュールレアリスムの画家サルバドール・ダリによる夢想場面が見もの。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西陣心中", "西陣心中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "妻子ある男との心中に失敗し、京都に出てきたゆみは、つづれ帯の美しさに心を奪われ、西陣の織り屋に住みつく。若い職人・博之は、過去の陰を持つゆみのイメージを帯に織り込もうとするが……。京都出身の高林陽一が、一人の女の情念を、西陣の情緒あふれる風土をバックに描いている。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RA／SURF　ACROSS　THE　BORDER", "Ra〈ラー〉／サーフ・アクロス・ザ・ボーダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "人が波乗りという行為を始めてから約1500年。波乗りはスポーツとして定着し、サーフィンとなって進化を遂げてきた。サーフィンを芸術の域にまで高めたラビット・バーソロミュー、苦悩の中で誰も成し得ない記録を創り上げたスレーター、レディース・サーフィンの可能性を広げたリサ・アンダーソン、滞空時間の限界を高めたラットボーイらの軌跡を余すところなく紹介。本物のサーフスピリットを語り継ぐべく製作されたドキュメンタリー。", "2002-01-01", "76,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　INVISIBLE　CIRCUS", "姉のいた夏、いない夏。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "自由奔放な姉フェイスが恋人のウルフとヨーロッパに旅立ち謎の死を遂げたのは、フィービーが12歳のとき。晴れて高校を卒業したフィービーは、母親の反対を押し切り姉の足跡をたどる旅に出る。パリでウルフを訪ねたフィービーが知ったのは、彼女が知る姉の姿とはかけ離れた苦悩と葛藤に満ちた物語だった。一人の少女が、死んだ姉の思い出に縛られて生きてきた自分に別れを告げ、大人へと成長を遂げるまでを描いた感動のロード・ムービー。C・ディアス扮するフェイスの妹フィービーを演じるのは、「パラサイト」のJ・ブリュースター。内気で臆病な少女から自立した大人の女性へと変貌していく妹を鮮烈な魅力で演じている。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("F〈エフ〉", "F〈エフ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "英国ロイヤルバレエ団のプリンシパルを最年少で務め、日本でも活躍する熊川哲也が映画初出演を果たした、甘く切ないラブ・ストーリー。彼が演じるのはイギリスのロイヤルバレエ・シアターで注目されながら足を骨折し、療養をかねて日本に帰国した古瀬郁矢。そして、もう一人の主人公は幼なじみの章吾と交際中のヒカル。二人は偶然から出会い、惹かれ合うものを感じながら、お互い名前を名乗らず別れてしまう。それから数日後、自らを落第生という古瀬は英語の落第点を意味する“F”の名前でラジオのディスク・ジョッキーを始める。“F”があの日の彼ではないかと感じたヒカルは番組にハガキを出す。こうして二人の心は次第に接近していくのだが……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VALENTINA／1919", "バレンチナ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "スペインの北部の小さな村に、フランスから一人の男がやって来た。彼は村に住むバレンチナという女性に、かつての戦友ホセ・ガルセスの思い出を語り始める……。スペイン内乱の英雄ホセ・ガルセスの幼き日の恋を、牧歌的ともいえる語り口で描く。幼い二人が過ごす、秘密の城の神秘と幻想に満ちた世界が美しい記録をとどめる。", "1983-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BACK IN THE USSR", "バック・イン・ザ・USSR", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "モスクワ観光に来ていたアメリカ人大学生アーチャーは、帰国直前にふとしたきっかけでロシア人の娘レナと知り合う。しかし、彼女が長い間秘宝とされていたマドンナ像を盗み出していたことから、二人は殺し屋や密輸業者から命を狙われるハメに……。全編モスクワ・ロケによるスリリングなロマンティック・アドベンチャー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BARNIE　ET　SES　PETITES　CONTRARIE´TE´S", "バルニーのちょっとした心配事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "妻に浮気がバレた中年男性が、家庭を取り戻すために珍騒動を繰り広げるコメディ。どこにでもいる普通のビジネスマンのバルニー。実は愛する家族に隠れて、ハンサムなイギリス人マークと若いフランス人マルゴの二人と浮気をしていた。だが、バルニーの45歳の誕生日が近づいたある日、マークとマルゴが結託してバルニーの家にやってきて……。F・ルキーニ、N・バイ、M・ジランらフランスの実力派が、鮮やかなアンサンブルを見せ、シニカルな笑いを誘う。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Killer's Kiss", "非情の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "　日本で劇場公開された最も古いスタンリー・キューブリック作品。映画「2001年宇宙の旅」をはじめ原作を重要視したキューブリックにしては珍しく、監督自らが原案を手掛けている。プロボクサーのデイヴィは、ノックアウト負けを帰した試合の夜、想いを寄せるグロリアが彼女の雇い主であるラパロに言い寄られている姿を目撃する。彼女を助けたことをきっかけに2人の関係が深まっていくが、男の嫉妬を買うことになる。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lo spazio bianco", "まっさらな光のもとで", "", "", "http://image.eiga.k-img.com/images/movie/55430/315x210.jpg?1625", 98, "離婚後、教師として独立した暮らしを送ってきた30代後半のマリアは、予期せず彼氏の子供を授かる。出産を望まない彼氏に、マリアはシングルマザーとして生きていくことを決意する。しかし、妊娠6カ月で子供を出産したマリアは、保育器の中で生きる赤ちゃんを毎日見守ることしかできなかった……。主演は「イタリア的、恋愛マニュアル」「題名のない子守唄」のマルゲリータ・ブイ。2009年のベネチア国際映画祭コンペティション部門に出品され、日本ではイタリア映画祭2010にて上映。", "2010-05-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ХРУСТАЛЕВ， МАШИНУ！", "フルスタリョフ、車を！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "反ユダヤ人主義の風潮が強まる1953年のロシア。優秀な脳外科医でもある赤軍の将軍クレンスキーは、KGBによるユダヤ人医師迫害計画に巻き込まれて強制収容所に監禁され、意外な人物と対面することに……。レンフィルムを代表する巨匠A・ゲルマンがフランス資本を得て、「わが友、イワン・ラプシン」以来、実に14年ぶりに完成させた入魂作。主人公の替え玉、スウェーデン人の記者、ユダヤ人の姉妹、スターリンの側近といった膨大な数の登場人物がめまぐるしく錯綜するストーリーは複雑怪奇を極め、論理的に理解することはほとんど不可能。とはいえ、驚異的なカメラワークによるモノクロの映像美は一瞬たりとも目が離せないほどの素晴らしさで、本作の凶暴なまでの理不尽さそのものが、当時のロシアの混沌を現しているのかもしれない。ちなみに謎めいたタイトルは、スターリンが死に際に残した言葉だという。", "1998-01-01", "2,24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MADCHEN　MADCHEN", "GIRLS ★ GIRLS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ドイツで大ヒットした青春コメディ。セックスに興味津々の女子高生３人組インケン、ビッキー、レーナは“イクこと”を目標に、悪戦苦闘の毎日。自転車のサドルで思いがけず絶頂体験をしたり、チャットにハマって同性愛に目覚めたり、ロスト・ヴァージンを予感してドキドキしたり……。Hなエピソードの数々が、カラっとした明るいノリでテンポよく展開。ひたすら前向きな少女たちのパワーも壮快感を残す快作だ。ビデオ、DVDタイトルは「過激GIRLS★GIRLS」。", "2001-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニュージーランドの若大将", "ニュージーランドの若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "日本自動車の海外普及部宣伝課に配属された田沼雄一は、ニュージーランドでの新型車発売のデモンストレーションを企画。見事に成功を収めたうえ、ニュージーランド振興会の森川節子の愛も獲得する。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SECRET　OF　LIFE", "生命の神秘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "ベルリン映画祭ドキュメンタリー部門で金熊賞を受賞したディズニー製作のドキュメンタリー“自然の冒険”シリーズ第３作。生物のユニークな受粉の方法、ミツバチの不思議な世界、肉眼で見ることが出来ない水の中の生物たち、深い海底で生きる魚たちの生活……。微速度撮影、顕微鏡撮影を駆使して、動植物の生態と、生命の神秘に迫る。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　VILLE　LOUVRE", "パリ・ルーヴル美術館の秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「ぼくの好きな先生」も高い評価を得た注目のドキュメンタリー作家ニコラ・フィリベールが撮りためたルーヴル美術館の舞台裏。芸術的視点ではなく、約５ヵ月の間、キュレーターや美術品の設置係、清掃員、案内係など1200人にも及ぶ職員の姿を追い、魅力的な人間模様を綴る。普段知る静寂な館内からは想像できない活気に溢れた側面を引き出している。たった１日の予定だった撮影を終えた次の日、何か起こりそうな予感がした監督はもう一度ルーヴル美術館へ足を運ぶ。すると、何年にも及ぶ大掛かりな改装が始まっていた。そこには、忙しく働く職員たちの輝く姿があった。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋の夏 L'ETE DES AMOURS", "恋の夏 L'ETE DES AMOURS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "言葉と国境の垣根を越えた愛の成就を、真夏の太陽の下に描き出すメロドラマ。友人を頼って日本を訪れたピエールと、彼の友人の恋人・優子が、ひょんなことから大島から油壷に向かうヨットに同乗する。ヨットは潮に流され、言葉は通じずとも狭いキャビンに男と女がいれば……。やがて、二人の間に愛が芽生える。「愛ふたたび」に続くR・ベルレーの日本映画出演第２作。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARILYN　MONROE ： LIFE　AFTER　DEATH", "マリリン・モンロー／ライフ・アフター・デス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "20世紀を代表する伝説的女優M・モンローが、不可解な死を遂げてもなお人々に与えた影響を検証するドキュメンタリー。モンローの葬儀のシーンで幕を開け、『プレイボーイ』誌の発行人H・ヘフナー、女優S・ストラスバーグら生前の彼女を知る人々へのインタビューを軸に構成。モンローの親しい友人だったフォトグラファー、M・H・グリーンによる貴重な未公開写真も約150点収録している。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vladimir et Rosa", "ウラジミールとローザ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "", "1970-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("でらしね", "でらしね", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ホームレスの画家がいる。画商の女はすべてを懸けて彼をデビューさせようとする。やがて画家は女をモデルに全身全霊を懸けた絵を描き始める。二人の経緯を現在と過去を交錯させながら、男と女の本質的な思いと差異を浮き彫りにする物語。一見、淫らな印象ながら、実はストイックな逡巡に描写は費やされる。それをイタイ男のわがままと受け取るか、傲慢な女の自己満足と捉えるかは自由。ただ、未完成こそが恋愛の至福とする主題には納得する人も多いはずだ。水木譲司は、ホームレスに身を落とし、自分の絵と格闘していた。ダンボールに描かれたその作品に惹かれた画商・橘今日子は彼に大作を描かせようとするが……。", "2004-11-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Beaver", "それでも、愛してる", "", "", "http://image.eiga.k-img.com/images/movie/57990/315x210.jpg?1877", 91, "ジョディ・フォスターが「ホーム・フォー・ザ・ホリデー」（1995）以来16年ぶりにメガホンをとった監督第3作。メル・ギブソンを主演に迎え、ビーバーのぬいぐるみを心の支えに苦難を乗り越えようとする男とその家族の姿を描くファミリードラマ。2人の息子にも恵まれ順風満帆な家庭を築いていた玩具会社社長のウォルターと妻のメレディス。しかし、ウォルターがうつ病を発症してしまい、働けなくなってしまう。メレディスは夫の分まで仕事や家事に奮闘するが、ある日、ウォルターが捨てられていたビーバーのぬいぐるみを拾ってきたことから事態が好転していき……。", "2012-08-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAMP", "呪われたランプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "5000年以上も前の古代メソポタミアのランプが、殺人事件をきっかけに博物館に納められた。ある日、その博物館を訪れた女子高生がランプに手を触れたとたん、その中に封じ込められた邪悪なる物が……。血なまぐさいシーンが連続するホラー映画。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON DANGEROUS GROUND", "危険な場所で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ヴィム・ヴェンダースをはじめとする後進の映画作家たちに敬愛されるN・レイ監督作品。孤独で粗雑な刑事ジム・ウィルソンは、その乱暴な捜査のやり方から上司ににらまれ、田舎に左遷される。だが、その田舎で少女殺人事件が起こり村人たちは自分たちの手で解決しようと、ジムを相手にしない。ジムは犯人を追って、ある一軒家にたどり着き、そこで美しい盲目の女性マリーと出会う……。孤独な刑事と盲目の娘との恋をメロドラマ・タッチで描いたサスペンスフルなフィルム・ノワール。前半における都会の夜の暗いシーンと、後半における田舎の雪景色の明るいシーンとのコントラストが見もの。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひいろ", "ひいろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "戦後の混乱の中で2つの国に引き裂かれてしまった家族が、60年の時を経て再び絆を取りもどすまでを描いた人間ドラマ。上海近郊の小さな陶芸の街・宜興で窯元を営む父と母、祖母に囲まれて幸せに育った彩陽。現在は上海の美術学校で陶芸を学んでいる彩陽の元に、祖母が危篤との報せが入る。実家に駆けつけた彩陽は、祖母から実は父が日本人であることを打ちあけられ、父の本当の母を探すために日本へと向かう。", "2007-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チンパオ —陳宝的故事—", "チンパオ —陳宝的故事—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "元小学校教諭、黒薮次男が大戦中の中国での体験をもとに執筆した児童文学「少年の目」をベースにした日中合作映画。76歳になる相澤健は戦争中に駐屯していた中国、桂林を訪れ、当時のことを回想していく。相澤のいた隊は食料調達と称して中国農民の家畜や穀物を略奪していた。そして両親を失った幼い兄妹が飼っていた牛も奪ってしまう。だが、その幼い兄妹は家族同然の牛を返してほしいと部隊の後を追ってくる。その懸命さに相澤や上官も心打たれるのだった……。戦争が生んだ悲劇を切なく綴ったヒューマン・ドラマ。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日蔭者", "日蔭者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "支那事変で負傷し帰国した武州松尾一家の小池鉄太郎が、病床にある親分に代わって、地元農地への工場建設をめぐって起きた争いを収めるべく奔走する。やがてそれはヤクザ同士の血で血を洗う戦いとなり……。鶴田浩二が、男の仁義に生きる日蔭者を演じた、まさに彼のイメージにぴったりの作品。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Las buenas hierbas", "グッド・ハーブ", "", "", "http://image.eiga.k-img.com/images/movie/56370/315x210.jpg?3080", 120, "ベルリン国際映画祭やサンダンス映画祭で受賞歴をもつメキシコの女性監督マリア・ノバロが、自身の体験をもとに執筆した脚本で撮り上げた人間ドラマ。シングルマザーのダリアは、母のララに内緒で、すでに母と離婚している父から息子の養育費を援助してもらっていた。一方、ハーブ研究者でもあるララはひとりで植物の研究を続けていたが、ある日、アルツハイマー型認知症と診断されてしまう。衝撃を受けたララはハーブで心を落ち着かせ、ダリアは母との人生を振り返るが……。", "2011-07-23", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A GIRL IN EVERY PORT", "マルクスの競馬騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "今なお多くのファンを持つマルクス兄弟。その三男で、墨でかいた口髭でおなじみのグルーチョが、元野球選手W・ベンディックスとコンビを組んで繰り広げる痛快コメディ。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIRTY ROTTEN SCOUNDRELS", "ペテン師とサギ師 だまされてリビエラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "フレディはその日暮らしの気楽なペテン師。一方ローレンスは豪邸を構える自称、一流のサギ師。そんな二人が南の楽園コート・ダジュールで互いのメンツを賭け、美貌の相続人の財産を手中にしようと、熾烈なサバイバル・ゲームを繰り広げる。1964年のマーロン・ブランド、デヴィッド・ニーヴン共演による「寝室ものがたり」のリメイク版。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sweet Sixteen", "SWEET SIXTEEN", "", "", "http://image.eiga.k-img.com/images/movie/1680/315x210.jpg?2005", 106, "15歳のリアムの夢は、もうすぐ刑務所から出所する母と、母を嫌って家出した姉といっしょに暮らすため、自分の家を手に入れること。親友のピンボールとともに、母のボーイフレンドが隠しているドラッグを手に入れ、それを売って家を買う資金を作ろうとするが。監督ケン・ローチは、英国北部の労働者階級出身。「ブレッド＆ローズ」に登場した少年たちの世界を描こうと本作を発想。ポール・ラバティが本年のカンヌ映画祭脚本賞を受賞。", "2002-12-28", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ROARING TWENTIES", "彼奴〈きゃつ〉は顔役だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "名匠ウォルシュと、ギャング・スター、キャグニーが組んだハードボイルド。戦線から帰還後、文通していた女性ジーンをナイトクラブへ世話してやり、かつての戦友であるロイドを法律顧問に、そして同じくハリーを相棒にして金持ちになったエディ。だがやがて彼らは喧嘩別れする。それから５年、堅気になったロイドと結婚しているジーンの前に、今は顔役となったハリーが脅迫に現れる。昔の恋人の幸福のため、エディはハリーを殺そうとするが……。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Once Upon a Time in Mexico", "レジェンド・オブ・メキシコ　デスペラード", "", "", "http://image.eiga.k-img.com/images/movie/1120/315x210.jpg?2645", 101, "ロバート・ロドリゲス監督が、デビュー作「エル・マリアッチ」、そのブロウアップ作「デスペラード」と同じ凄腕の殺し屋マリアッチを主人公に描くロドリゲス流マカロニ・ウェスタン。軍部のクーデターの噂が飛び交うメキシコを舞台に、妻子を殺した男への復讐を誓う暗殺者バンデラス、情勢に乗じて私腹を肥やそうとするCIA捜査官ジョニー・デップ、麻薬王ウィレム・デフォー、その用心棒ミッキー・ロークらの男のドラマが展開。", "2004-03-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SURPRISE PARTY", "さよなら夏のリセ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "谷あいの小さな町にやって来た母子によって、試験休みで大騒ぎの若者たちの周りに、さざ波が起こる……。悩みなき時代のホロ苦い青春映画。ヴァディム監督とカトリーヌ・ドヌーヴとの愛児C・ヴァディムのデビューが話題になった。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天地英雄／WARRIORS　OF　HEAVEN　AND　EARTH", "ヘブン・アンド・アース／天地英雄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "『西遊記』で有名な玄奘三蔵のシルクロード横断をモチーフに、敵同士だった男二人が仏教僧のキャラバンを守るためにトルコ軍との大バトルを繰り広げる。遣唐使として中国にやってきた剣士を中井貴一が、義のために反逆者となった元軍人を「紅いコーリャン」のチアン・ウェンが演じ、熱い演技の火花を散らす。遣唐使として訪中してから20年、日本人の来栖は帰国の前に、皇帝に反逆した元軍人、李の討伐を決意をする。ところが李は、僧侶のキャラバンをトルコ軍から守ろうと奮闘中。来栖はひとまず休戦して、キャラバンの護衛を手伝うが……。", "2003-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伏　鉄砲娘の捕物帳", "伏　鉄砲娘の捕物帳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lawn Dogs", "キャメロット・ガーデンの少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "空想癖のある少女と孤独な青年の交流を、エロティックな雰囲気を漂わせて描く異色ファンタジー。ケンタッキー州の閑静な住宅街に越してきた10歳の少女デヴォンは、森の中に棲む魔女の伝説を信じていた。ある日、森に探索に出かけた彼女はトレーラーハウスに住む青年トレントと出会う。住宅街の芝生を刈るバイトをしている貧しい彼を住人たちは快く思っていなかったが、デヴォンは彼に信頼を寄せ、度々森を訪れるようになる。ところが、そのことを知った大人たちが激怒し、二人の友情が引き裂かれる事件が……。", "1997-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bad Boys II", "バッドボーイズ2バッド", "", "", "http://image.eiga.k-img.com/images/movie/1280/315x210.jpg?2327", 146, "「アルマゲドン」のマイケル・ベイ監督の95年のデビュー作、8年振りの続編を彼自身が監督。今や大スターとなった主演コンビ、ウィル・スミスとマーティン・ローレンスが続投。脚本は「ティン・カップ」などの監督でもあるロン・シェルトンとTV「CSI　科学特捜班」のジェリー・スタール。マイアミ警察のマイクとマーカスは麻薬密売捜査係。マイクが交際中のマーカスの妹、連邦麻薬捜査局員のシドが麻薬事件に巻き込まれる。", "2003-11-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE PIECE ワンピース THE MOVIE カラクリ城のメカ巨兵", "ONE PIECE ワンピース THE MOVIE カラクリ城のメカ巨兵", "", "", "http://ecx.images-amazon.com/images/I/6132fedw1rL._SL210_.jpg", 95, "コミックス、テレビアニメともに大ヒットを飛ばし続ける海賊ファンタジーの劇場版第7弾。沈没寸前の海賊船で大きな宝箱を発見したルフィたち。ところが中から現われたのは、お宝ではなく1人の老婆だった。老婆を島に送り届ければ伝説の宝物・金の冠をもらえると言われたルフィたちは、期待に胸をふくらませて「メカ島」を目指すが……。ゲスト声優として「SMAP」の稲垣吾郎とお笑いコンビ「極楽とんぼ」が参加。", "2006-03-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Upside Down: The Creation Records Story", "アップサイド・ダウン：クリエイションレコーズ・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/56160/315x210.jpg?4073", 101, "1983年に英ロンドンで設立され、「オアシス」「プライマル・スクリーム」「ジーザス＆メリーチェイン」「マイ・ブラッディ・ヴァレンタイン」など人気バンドを多数輩出してきた世界的名門レーベル「クリエイションレコーズ」と、その創設者アラン・マッギーに迫るドキュメンタリー。レーベル設立前から99年の経営破たんに至るまでの歴史やマッギーの破天荒な人物像を、数々のバンド、アーティストのインタビューやライブ映像を交えて描く。", "2011-08-27", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SERENDIPITY", "セレンディピティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "クリスマス直前のニューヨークで偶然出会った男女。互いに強く惹かれ合うが“この出逢いが運命なら、いつかまた会えるはず”と、名前も告げずに別れる。時は流れ、二人にはそれぞれ恋人が出来、幸せに暮らしていた。しかし、あの時の出会いが忘れられない二人はある行動を起こすことにする……。タイトルはニューヨークに実在する老舗カフェの名前であり、“幸せな偶然”を意味する言葉。“運命の赤い糸”を信じたくなる、ロマンティックなラブ・ストーリー。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CA N’ARRIVE QU’AUX AUTRES", "哀しみの終るとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "愛娘を亡くし、失意のうちに暮らしていた一組の夫婦が、悲しみを乗り越え、そしてそれをバネとして新しい生を見いだすまでを描く人間ドラマ。M・マストロヤンニとC・ドヌーヴの蜜月時代のコンビ作。ジャン・ルイ・トランティニャンの妻でもあるナディーヌが、女性らしい細やかな演出を見せる。", "1971-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEW ADVENTURES OF PIPPI LONGSTOCKING", "長くつ下ピッピの冒険物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "国際アンデルセン賞を受賞したリンドグレーンの名作を映画化。いつも長い靴下を履き、長い赤毛を３つ編みお下げにしているソバカスだらけの女の子ピッピ。彼女は、天井を逆さに歩いたり、指一本で大人を持ち上げたりと、不思議な力を持っている。そんなピッピが繰り広げるイタズラや愉快な騒動を描いた作品。", "1988-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dead Silence", "デッド・サイレンス", "", "", "http://image.eiga.k-img.com/images/movie/53400/315x210.jpg?2342", 89, "「ソウ」のジェームズ・ワン監督＆脚本家リー・ワネルが再タッグを組んだオカルト・ホラー。差出人不明のトランクを受け取ったアーシェン夫妻。開けてみるとそこには1体の腹話術人形が。外出した夫ジェイミーが帰宅すると、なんと妻が舌を切られて死亡していた。ジェイミーは妻が人形を見た際に不気味な詩を口ずさんでいたことを思い出し……。予想外の展開で観る者を驚愕させた「ソウ」のコンビだけに、本作にも衝撃の結末が待ち構えている。", "2008-03-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGHWAYMEN", "ハイウェイマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "カルト的なスリラー映画「ヒッチャー」で知られるR・ハーモン監督のバイオレンス・アクション。女性ばかりを狙う異常な連続ひき逃げ犯と、彼に愛妻を殺害された青年の死闘を、激烈なカーチェイスを盛り込んで描き出す。５年前、目の前で愛妻をひき殺されたクレイは、その犯人を２年間追い続けてきた。そんな折り、モリーという女性とその友人の乗った車が事故に遭い、友人は無残にも命を落としてしまう。クレイは、モリーに犯人探しの協力を求めるのだが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("首", "首", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "映画「真昼の暗黒」の原作者で弁護士の正木ひろしの原作をもとに、東宝青春映画を撮り続けていた森谷司郎が、弁護士の執念を正攻法で描いた作品。昭和18年の冬、一人の鉱夫が警察で死んだ。死因は脳溢血ということだったが、遺族はそれを不満とし、正木に調査を依頼してくる。正木も死因を拷問死だと思うが、死体はすでに埋葬されていた。正木は警察の圧力にも屈せず、死因究明のため死体を掘り返し、首を切断して持ち帰る。鑑定の結果、鉱夫の死因は脳溢血ではなく殴打によるものだとわかった。白黒スタンダードを生かした森谷の演出に対し、正木役の小林桂樹が熱演で応えている。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("亜細亜の娘", "亜細亜の娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "陸軍省、海軍省、外務省、中華民国維新政府が賛助し、上海ロケを敢行したことで話題となった国策映画的な大作。「秦・始皇帝」をはじめ多作、娯楽路線派で知られる田中重雄が監督し、「滝の白糸」「残菊物語」など溝口作品のシナリオで多大な評価を得た依田義賢が脚本を担当。蘆溝橋事件をきっかけに、日本に留学中の娘と彼女の将来を約束した日本人記者がたどることになる激動の日々を描く。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Notebook", "きみに読む物語", "", "", "http://image.eiga.k-img.com/images/movie/1570/315x210.jpg?3166", 123, "「メッセージ・イン・ア・ボトル」などで知られるニコラス・スパークスのベストセラーを、「シーズ・ソー・ラヴリー」のニック・カサベテス監督が実母ジーナ・ローランズ出演で映画化、全米でロングラン・ヒットを記録。療養施設で暮らす初老の女性は記憶を失っていたが、ひとりの男性が彼女を訪れて、ある物語を読んで聞かせる。それは1940年代のアメリカ南部の町で良家の子女と地元の貧しい青年の間に生まれた純愛の物語だった。", "2005-02-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カメラになった男　写真家　中平卓馬", "カメラになった男　写真家　中平卓馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1970年前後に注目を集めた伝説の写真家・中平卓馬の現在を追ったドキュメンタリー。先鋭的な写真表現で当時の若者たちから支持されていた中平は、1977年にアルコールの大量摂取によって倒れ、記憶と言葉の大半を失った。その後も写真家として精力的に活動を続けている中平に、3年近くにわたって密着。横浜の自宅周辺を毎日撮り続けている中平の日常や、思い出の地・沖縄へと撮影旅行に向かう姿を追う。", "2006-10-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Incendiary", "ブローン・アパート", "", "", "http://image.eiga.k-img.com/images/movie/55870/315x210.jpg?2754", 100, "ミシェル・ウィリアムズとユアン・マクレガーが共演する社会派サスペンス。夫との関係がうまくいっていない“若い母親”は、パブで知り合った新聞記者ジャスパーと関係をもつ。しかし情事の最中、サッカー観戦に出かけていた夫と息子が爆弾テロに巻き込まれてしまう。深い悲しみと罪悪感にさいなまれるなか、やがてジャスパーから驚くべき情報を聞かされる。「ブリジット・ジョーンズの日記」のシャロン・マグワイア監督がメガホンをとる。", "2011-01-29", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("МУ−МУ", "ムムー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ロシアの文豪・ツルゲーネフの同名小説を映画化。耳と口が不自由なゲラーシムは、洗濯女と恋に落ちるが、女主人の陰謀により仲を引き裂かれてしまう。悲しみに暮れる毎日だったが、子犬にムムーと名付け我が子のように世話をする。犬の声がうるさいと、再び女主人の怒りをかったゲラーシムは、ムムーを連れて川の小舟に乗り込む……。", "1998-01-01", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Glitter", "グリッター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "コーラスガールをしていたビリーは、敏腕音楽プロデューサーのジュリアンに才能を見いだされ、歌手になる夢を実現させていく。たちまちスターへの階段を駆け上がるビリーだったが、恋と仕事の両立に悩み……。世界の歌姫M・キャリーが1980年代のニューヨークのクラブ・シーンを背景に、自伝的ともいえるヒロインを演じた初主演作。ビデオタイトルは「グリッター」。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAMBO III", "ランボー3　怒りのアフガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "スタローンの「ランボー」シリーズ第３作。1988年５月、実際にはソ連軍撤退が始まったアフガニスタンだが、映画ではいまだ侵攻中、俗世間を離れ、バンコクの仏寺でひっそりと暮らすランボーのもとに知らせが入る。無二の親友トラウトマンが、ソ連軍に拉致されたというのだ。彼の怒りと闘争本能がよみがえった。親友救出のため、たった一人でアフガニスタンに侵入するランボー。ついにソ連軍との壮絶な戦いの幕が切って落とされた。戦闘用ヘリや一撃必殺スティンガー・ミサイルなどが続々登場。それにも勝るランボーの超人的パワーが度肝を抜く、アクション超大作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRAIN TO HEAVEN", "夢みるように微笑んで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "雄大なアンデスの自然を背景に、純粋な少年の苛酷な旅路をファンタスティックに描いた感動のドラマ。アンデス山頂に向かう、“天国への列車”と呼ばれる山岳鉄道。孤児院を抜け出した少年ニーニョは捨て犬のペリートとともに、死んでしまった両親に会えると固く信じその汽車に乗り、旅を始めるが……。", "1990-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("БРОНЕНОСЕЦ “ПОТЕМКИН”", "戦艦ポチョムキン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "世界映画史上の先駆的傑作。1905年黒海で起きたポチョムキン号の兵士の革命的叛乱を、オールロケによる記録映画的手法と独創的なモンタージュ技法で再現。オデッサの階段上の民衆虐殺場面など、その斬新な映画表現は後世の映画人に多くの影響を与えた。", "1925-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("道頓堀川", "道頓堀川", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "道頓堀川に面した喫茶店に住み込みで働く画学生と、小料理屋のママとのラブ・ストーリーを中心に、喫茶店のマスターとその息子のエピソードや、道頓堀川の周辺に住む人々の青春群像をあざやかに描いた作品。松坂慶子と真田広之の組み合わせが新鮮。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PANDORA　PEAKS", "パンドラ・ピークス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "巨乳女優を起用してエロティックなエクスプロイテーション映画を撮り続けたラス・メイヤーの20年ぶりの監督作にして遺作。創作の秘密や大きなバストに魅了された理由などをまくしたてるように語るメイヤーのナレーションに乗って、美女軍団の胸が次々と映し出される。メイヤーがどれほど巨乳を愛したかが、よくわかる(？)私小説的な怪作。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真夜中の招待状", "真夜中の招待状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "理由不明のまま次々と蒸発していった４人の兄弟にまつわる謎を、最後に姿を消した四男の婚約者が解き明かしていくという、遠藤周作の推理小説『闇のよぶ声』を映画化したサスペンス・ミステリー。小林麻美が、初々しい演技を披露。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リング・リング・リング　涙のチャンピオンベルト", "リング・リング・リング　涙のチャンピオンベルト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "幼い頃からプロレスに憧れていた千種は、車イス生活の夫と酒好きで働かない義父、そして折り合いの悪い義母を彼女の収入で支えていた。唯一の味方である警察官・亀田の紹介で女子プロトレーナー水島と出会った彼女は、幼い頃からの夢をかなえるべく上京するのだった……。この作品がきっかけとなり、現役復帰を果たした女子プロレス界のスター長与千種の初主演作品。全日本女子プロレスの全面協力を受け、現役のレスラーの多くが出演。また、つかこうへい演劇に欠かせない役者陣が顔を出しており、つかファンにとっては楽しみの一つである。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE FRAME", "ブレードプラネット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "エイリアンとそれを追う科学者の追跡を描くSFホラー。そのエイリアンは、人間の肉体と融合し永遠の命を得る精神エネルギー生物体であった。研究所から逃亡したエイリアンを追うフレミングは政府の特別科学官であり、昔、愛娘をエイリアンに誘拐されたことがあった。復讐に燃えるフレミングの前には、レプリカントが次々と現れる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嗚呼!!花の応援団 役者やのオー", "嗚呼!!花の応援団 役者やのオー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "少々オツムの弱い河内八郎太は大学の応援団が天国だと聞かされ、勇んで入団する。実はこの八郎太、大阪一の暴力団・悪心会会長の孫なのだ。そうとは知らない青田赤道。八郎太にかみついたからさあ大変。コワイお兄さんたちがやって来た……。２代目青田は井上治之。薬痴寺センパイが初登場する。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMBITION", "アンビション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "病気の父と暮らす売れない作家ミッチェルは、殺人鬼メリックの物語を小説にしようとする。だがその権利は、すでに別の作家が持っていた。そこで出所したメリックを雇い再び殺人を犯すよう仕向ける。だが更正したメリックは殺人を犯さず、逆に焦るミッチェルは現実と妄想が交錯し、父と刑事を殺す。ミッチェルは刑務所へ入れられ、判決は死刑。皮肉にも彼の獄中手記と処女作は出版社の間で引っ張りだことなっていた……。ミッチェル役を熱演するルー・ダイアモンド・フィリップスが自ら脚本を書いた本格的サイコ・スリラー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADIES IN LAVENDER", "ラヴェンダーの咲く庭で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "単調な毎日を送る老姉妹が、浜辺に打ち上げられた異国の青年と出会い、忘れていた淡い感情を蘇らせる大人の素敵なおとぎ話。英国の大物女優たちの心を捉える青年役を、ドイツの若手実力派ダニエル・ブリュールが清々しく好演。'36年、イギリス。二人で静かに暮らす老姉妹。嵐の翌日、浜辺に打ち上げられた青年を助けたことで、彼女たちの穏やかな生活に変化が生じる。", "2005-06-04", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死んでもいい", "死んでもいい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "“天使のはらわた”シリーズで知られる石井隆が「天使のはらわた・赤い眩暈」に続き演出を手掛けたヘビーなラブ・ストーリー。激情を抑えきれない青年と、彼の愛を真っ向から受け入れる人妻。二人の禁じられた恋は、やがて彼女の夫を殺すという狂気へと変貌していく……。ドロドロした愛憎劇ながら、陰影に富んだ美しい映像が実に魅力的。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RELENTLESS", "処刑マニア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "警察官を目指してきた青年が一転して、警察をあざ笑うかのような凶悪犯罪を繰り返す殺人鬼と化していく。未公開のサスペンス・アクション。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CIMARRON", "シマロン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "当時のアカデミー賞で作品賞を受賞し、アメリカン・ヒーローの1タイプを示した西部劇タッチのヒューマン・ドラマ。1889年のオクラホマで土地獲得競争があり、若い娘に騙されて土地を奪われた主人公と妻は、新天地を求めて旅立った。そして、ある町に辿り着き、そこで新聞社を創立。主人公は町の暴力団に抗してせん滅し、銀行強盗の一団を撃退したりの大活躍を見せる。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JSA: Joint Secury Area", "JSA", "", "", "http://image.eiga.k-img.com/images/movie/1460/315x210.jpg?3756", 110, "朝鮮半島を南北に分けた境界線に位置する共同警備区域（Joint Security Area）で起こった武力衝突事件。捜査を進める監視委員会の女性将校ソフィは、生き残った両国の兵士から事情聴取を進めるうちに、南と北で証言がまったく相反していることに悩む。そして、事件の核心に迫るにつれ、両国の兵士の間に信じがたい事実があったことを知るが……。", "2001-05-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIRECTED BY ANDREJ TARKOVSKY", "タルコフスキー・ファイルIN 「サクリファイス」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アンドレイ・タルコフスキーの遺作「サクリファイス」の撮影現場を捉えたドキュメンタリー映画。撮影現場の生々しい記録に、タルコフスキーへのインタビューと彼の著書『映像のポエジア』からの引用によるナレーションとが重ねられ、タルコフスキーの演出と同時に彼の映画への姿勢が具体的に示されている。なかでも主人公の家が燃え上がるシーンの撮影におけるハプニングは、観る者に緊迫感をもたらし、またラストの病床から死を意識しつつ色彩処理の指示をするタルコフスキーの姿には心を打たれる。監督のM・レシチロフスキーは、「サクリファイス」でも助監督と編集についていて、これが監督デビュー作。この作品はそんな彼のタルコフスキーに捧げるオマージュであり、タルコフスキー芸術の秘密に迫るものとなっている。", "1988-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地底の歌", "地底の歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "今は群衆を痛めつける都会のダニでもあるが、心の奥底には悲哀や情感もある東京の伝統ある侠客・伊豆一家を中心に、ヤクザと賭博の世界を描いた作品。新聞に連載されて話題を呼んだ平林たい子原作の映画化で、石原裕次郎が初々しい演技を披露。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUNBUS", "ガンバス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "西部を荒らす強盗二人組が、やがて第一次大戦下のヨーロッパに渡り、愛機ガンバスを駆ってドイツ無敵巨大飛行船に立ち向かう痛快娯楽アクション映画の快作。西部劇から戦争アクション、空中戦、SFXと、あらゆる娯楽要素をふんだんに盛り込んだゴッタ煮の面白さを満載している。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOMEBODIES", "ホームボディーズ 老人殺人倶楽部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "老人たちが殺人を犯すというとんでもない“シルバー・ホラー”。アパートの取り壊しに反対する老人たち６人が結束を固め、工事関係者を一人ずつ血祭りにあげていく。人間セメント漬け、高層エレベーターでの逆落としなど、残酷きわまりない手口が見もの。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("晴れ、ときどき殺人", "晴れ、ときどき殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "母の偽証によって無実の人を自殺に追い込んでしまったことを聞かされた娘が、母の遺志を継いで真犯人を探し出すまでを描いた異色ミステリー。平凡な女子大生が次々と連続殺人事件に巻き込まれていく過程を、パロディー感覚に溢れた躍動感で描く。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MERES　TOU　３６", "1936年の日々", "", "", "http://ecx.images-amazon.com/images/I/413PXDV70RL._SL210_.jpg", 110, "ギリシャの巨匠T・アンゲロプロスの長編第２作。後の「旅芸人の記録」「狩人」へと続く“現代ギリシャ史３部作”の１作目にあたり、この作品で早くも彼特有の長回しなどの撮影スタイルが実践されている。1936年のギリシャ。ある政治家を暗殺した犯人として同性愛者のソフィアノスが逮捕される。ところが彼は右翼系国会議員を人質にして刑務所に立てこもり、自分を釈放するよう要求する。", "1972-01-01", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("絵島生島", "絵島生島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "徳川中期、幕府七代将軍の座をめぐって江戸城・大奥が暗躍する中、その政治の犠牲となった御殿女中・絵島と、歌舞伎役者・生島の美しくも悲しい愛を描いた悲恋物語。女性ものに定評のある舟橋聖一の原作を大庭秀雄が監督、淡島千景が絵島を、九代目市川海老蔵が生島を熱演している。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニッポン無責任野郎", "ニッポン無責任野郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "“真面目な野郎は損をする、嬉しがらせてもうけて逃げる”。これが無責任男・源等（みなもとひとし）の人生哲学。ひょんなことから明音楽器営業部長と知り合った彼は、天性のハッタリとオトボケでまんまと入社に成功し、無責任ぶりを発揮しつつも出世していく。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴力団再武装", "暴力団再武装", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "佐藤純彌監督、鶴田浩二主演による現代任侠ヤクザ映画。将来の大工業都市化が約束されている地方都市で、広域ヤクザ組織の先兵として地元ヤクザを蹴散らすが、組織の舎弟頭の陰謀で窮地に立たされる。鶴田浩二と若山富三郎のコンビがいい味を見せる。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひめゆりの塔", "ひめゆりの塔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "1953年の同名映画を、おなじ今井正監督、前作の水木洋子のシナリオをそのまま用いてリメイク。昭和20年、沖縄の女学生によって結成されたひめゆり部隊の悲劇を描く。前作に比べ、感銘度の薄い平凡作。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うる星やつら４ ラム・ザ・フォーエバー", "うる星やつら４ ラム・ザ・フォーエバー", "", "", "http://ecx.images-amazon.com/images/I/21C9YR6MVDL._SL210_.jpg", 95, "諸星あたるやメガネたちは、自主映画の製作を進めていた。映画は面堂家の庭の桜の巨木にまつわる鬼姫伝説を題材にしていたが、あたるがそれを切り倒した時から、友引町で異変が続発する。桜の木の跡に出現する湖、凍っていく人々の夢。そしてラムは超能力を失い、ツノもとれてしまう。友引町が意思を持ち、ラムという異物を調べようとしていたのだ。なす術のないあたるは、ただ走り続けることでラムへの想いを表現するが……。感覚的な描写など、冴えを見せた作画に比べて、物語的には混乱。ウエットでミステリアスな雰囲気が漂い続ける、まさに“五里霧中”の作品となった。DVDタイトルは「うる星やつら 劇場版 ラム・ザ・フォーエバー」。", "1986-02-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEDALES", "迷宮の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ギリシャ神話の“ダイダロスの迷宮”に材を取ったフレンチ・スリラー。多重人格者による連続殺人事件の行方を、ひねりの効いた構成で語り明かす。サイコロなどの小道具がミステリーの興趣を高め、終盤に明かされる意外な犯人像が観る者のド肝を抜く。その濃密な仕上がりは一見の価値あり。パリの連続殺人鬼クロードが逮捕された。捜査に協力することになった精神科医ブレナックは、７つもの人格を宿す犯人に接するが……。", "2005-07-02", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森聞き", "森聞き", "", "", "http://image.eiga.k-img.com/images/movie/55780/315x210.jpg?3998", 125, "進学、就職などそれぞれの進路に悩む高校生100人が、100人の「森の名人」を訪ねる。そのうち4人の高校生に密着し、日本各地の山村で森とともに暮らす70～80歳の老人たちの言葉に耳を傾け、新たなものの見方や未来を学んでいく姿を追ったドキュメンタリー。ひめゆり学徒隊の現在の姿を追ったドキュメンタリー「ひめゆり」（2007）が高い評価を受けた柴田昌平の監督第2作。", "2011-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幽霊暁に死す", "幽霊暁に死す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "新演技座と、マキノ雅広が主催するCAC（映画芸術協同）が製作した作品。当時、マキノは健康を害していたが、軽妙で楽しい作品に仕上がっている。新婚ホヤホヤの小平太は中学校の先生だが、非民主的な校長に抗議して逆にクビになってしまう。やむなく小平太は叔父に就職の斡旋を頼むが、まったく相手にしてもらえず、代わりに幽霊屋敷と噂のある軽井沢の別荘の管理人を引き受けてしまう。そしてある日、小平太の前に噂通り幽霊が現れたが、それは彼の父の幽霊だった。小平太役とその父親を長谷川一夫が二役で演じているほか、田端義夫も特別出演し、歌声を披露している。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL CIELO CADE", "ふたりのトスカーナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "イタリア文学の名作として有名なロレンツァ・マッツェッティの自伝的小説を映像化。美しいトスカーナで過ごす少女たちの成長談を、第二次世界大戦の厳しい現実を織り交ぜて描いた感動作だ。1943年、両親の突然の死によって田舎の伯父夫婦に引き取られたベニーとベビーの姉妹。豊かな自然に囲まれ、近所の子供たちと交流を重ねながら、彼女たちはのびのびと成長していく。しかしそこに突然の悲劇が……。", "2000-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWILIGHT", "トワイライト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "スター夫婦の隠されたスキャンダルをめぐる豪華俳優競演のサスペンス。元刑事で私立探偵の経験もあるハリーは、今ではハリウッド・スター、ジャックとキャサリンの夫婦の豪邸に居候し、彼らの雑務をこなす便利屋のような存在。そんなある日、彼はジャックの依頼で、ある女に金を届けに行く。だが、そこにいたのは重傷を負った男だった。不審に思ったハリーは男の身分証を手に入れ、彼の家に侵入、バスルームからキャサリンをめぐるスキャンダル記事のファイルを発見する。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誰よりも誰よりも君を愛す", "誰よりも誰よりも君を愛す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "結婚を誓い、幸せの絶頂にあったTV局職員明人。だがその婚約者は、金でものを言わすいまいましい進藤によって傷ものにされていた。進藤を殴った彼は、進藤の資本の入ったテレビ局を辞めざるを得ない。行方をくらました婚約者を追って明人の捜索は続くのだが……。ストレートな思いを描いた恋愛映画の佳作。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents すき　「フィガロの告白」", "桃まつり presents すき　「フィガロの告白」", "", "", "http://image.eiga.k-img.com/images/movie/57880/315x210.jpg?1810", 22, "若手女性監督による映画製作・上映集団「桃まつり」が、「好き」をテーマに女性監督たちが撮り上げた短編作品を上映する「桃まつり presents すき」の1本。夏休みにいつもの秘密基地に集まった男子中学生4人は、女の子の話で盛り上がり、それぞれ好きな女子へ告白することになるが……。監督はぴあフィルムフェスティバル入選経験もある天野千尋。「壱のすき」として「帰り道」（竹本直美監督）、「the place named」（小森はるか監督）と同時上映。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ケメ子の唄", "ケメ子の唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "不動産屋の六造と元バレエ教師の恭介は、人生に疲れともに自殺を決意するが、偶然ゴーゴーの大好きなケメ子に出会い、生きる希望を取り戻して彼女をスターにしようと奔走する。間もなく、ケメ子はスケジュールで満杯の売れっ子スターに。でも、彼女には冷たくなった恋人の愛をとり戻すほうが大切だった……。同名の大ヒット・ソングを新人監督田中康義が映画化。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("National Treasure", "ナショナル・トレジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "「パイレーツ・オブ・カリビアン」のジェリー・ブラッカイマー製作のアドベンチャー。ニコラス・ケイジ扮する歴史学者で冒険家の主人公が、テンプル騎士団の財宝の行方を追い、アメリカ独立宣言書にその鍵が隠されていることを突きとめる。が、彼のライバルもこの秘宝を狙っていた。監督はサンドラ・ブロック主演の「あなたが寝てる間に…」やアンソニー・ホプキンス主演の「ハーモニーベイの夜明け」などのジョン・タートルトーブ。", "2005-03-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("All About Steve", "ウルトラ I LOVE YOU！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "変わり者のメアリーは、真っ赤なブーツが大好きなクロスワードパズル作家。ある日、ハンサムなTVカメラマンのスティーブと出会い、一目ぼれしてしまう。メアリーは仕事で全国を駆け回るスティーブにしつこくつきまとうが、スティーブはエスカレートしていくメアリーの愛情表現と奇行にうんざりしていた。「しあわせの隠れ場所」（09）でアカデミー主演女優賞を受賞したサンドラ・ブロックが、最低な主演女優におくられるラジー賞（ゴールデンラズベリー賞）を受賞したことでも知られる話題作。", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mo'Better Blues", "モ'・ベター・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "ジャズ・トランペッターのブリークは才能こそあったが、エゴが強く、バンドのメンバーとの衝突も絶えなかった。私生活では、教師のインディゴ、そしてジャズ歌手のクラークというふたりの恋人の間を揺れ動いている。そんな中、バンド内も不和状態になり、女性たちとブリークの仲もこじれていく。その上、ギャンブルにのめり込んだマネージャーのトラブルに巻き込まれ、唇を殴られたブリークは再起不能に……。ジャズの名曲にのせて描かれるミュージシャンの人生ドラマ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("横道世之介", "横道世之介", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Envy", "隣のリッチマン", "", "", "http://ecx.images-amazon.com/images/I/516m3RRoIrL._SL210_.jpg", 0, "人気コメディアンのベン・スティラー＆ジャック・ブラック共演によるコメディドラマ。同じ職場で働き、隣どうしの家で暮らす大親友のティムとニック。ところがある日、ニックがペットのフンを消す画期的なスプレーを発明し、突如として大富豪になる。ニックへの投資を断っていたティムは激しい嫉妬と後悔によって自分を見失い、ついには妻と仕事まで失ってしまう。監督を務めるのは、「レインマン」などの名匠バリー・レビンソン。", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンの爆弾児", "エノケンの爆弾児", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "「エノケンの……」という題名でおなじみの“エノケン”ものの戦前における最後の作品。監督は「エノケンの猿飛佐助」（1938）以来３年ぶりにコンビを組む岡田敬。エノケンはこの作品でも例によって一人三役をこなしている。田舎から東京の呉服店に丁稚奉公に出て来た健吉は、決して腹を立ててはならぬという父の遺言を守り、どんなにいじめられても我慢を続ける。それに加えてこの店の御隠居は極端な迷信家ときている。その御隠居の選んだ日に一家総出のハイキングとなったが、大嵐となりあわや遭難というところを健吉が救う。これで健吉は御隠居に気に入られ番頭に昇進、娘の百合子と結ばれる。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO¨RST", "渇望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "過去に傷を持つ男女のエゴイズムむき出しのせめぎ合いを追いながら、分かり合えない恋人たちの葛藤をシンボリックに捉えていく。女の友人としてレズビアンの女性が登場し、当時は検閲スレスレだったというエピソードも残る。ベルイマンの初期作品。", "1949-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ノーパンツ・ガールズ／　Movie Box-ing２", "ノーパンツ・ガールズ／　Movie Box-ing２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "函館港イルミナシオン映画祭でシナリオ大賞を受賞した「ノーパンツ・ガールズ」。非日常を求めてパンツを脱ぎ捨てた子供たちを描いた原作を、3人の監督がそれぞれの視点で映像化。原作に忠実な作品から大胆なアレンジを加えた作品まで、三者三様の個性が光る見ごたえたっぷりのオムニバスだ。平凡な日常に退屈した小学生のこのみたちは、非日常を求めて“ひ・ぱんつ”に。しかしクラスの男子に脱ぎ捨てたパンツを拾われて……。", "2005-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SALSA（IT’S TOO HOT！）", "サルサ 灼熱のふたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "キューバで発生したラテン・ダンス音楽をベースに、N.Y.に移住してきたプエルトリコ系の人々が磨きぬいた音楽“サルサ”をモチーフに、ヒスパニック系のしがない修理工がアメリカン・ドリームを実現させようとする姿を描くダンシング・ムービー。サルサの音楽とダンスが一体となった切れ味鋭い映像が魅力。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬飼さんちの犬", "犬飼さんちの犬", "", "", "http://image.eiga.k-img.com/images/movie/56120/315x210.jpg?4084", 92, "2011年1月～3月にTV放送された同名ドラマの劇場版。名字とは裏腹にイヌが嫌いな犬飼保が単身赴任先から自宅に戻ってみると、家族が内緒で飼いはじめたサモエド犬のサモンがわがもの顔で居座っていた。イヌ嫌いのサラリーマンと一匹のイヌが、一家の主の座をかけて巻き起こす騒動を描くハートウォーミングコメディ。", "2011-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Yes Man", "イエスマン　“YES”は人生のパスワード", "", "", "http://image.eiga.k-img.com/images/movie/53530/315x210.jpg?1815", 104, "「ノー」が口癖の人生に後ろ向きな男カールは、生き方を変えるために参加したセミナーで、「どんな事に対しても『イエス』と答えることが、意味のある人生を送るための唯一のルール」と教えられる。それから「イエス」を連発するようになったカールの運気は上がっていくのだが……。「マスク」「トゥルーマン・ショー」のジム・キャリーが主演するコメディドラマ。共演に「ハプニング」のズーイー・デシャネル。監督は「チアーズ！」のペイトン・リード。", "2009-03-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマンゼアス２　超人大戦・光と影", "ウルトラマンゼアス２　超人大戦・光と影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "1990年代に復活した“ウルトラマン”シリーズの第２作。ニュー・ヒーロー、ゼアスが黒い容姿の悪玉ウルトラマン・シャドーに完敗を喫し、ゼアスに変身した科学特捜隊員の勝人は自信を喪失してしまう。その頃、シャドーを操るレディベンゼン星人が地球侵略作戦を開始。道場で特訓を積んだ勝人はシャドーとのリターン・マッチに挑む。レディベンゼン星人に扮した神田うのの悪女ぶりや、今は亡き、K-1王者、A・フグの特別出演にも注目。DVDは「ウルトラマンゼアス　１＆２」に収録。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／THE　CLASSIC", "ラブストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "日本でも大ブームを巻き起こした「猟奇的な彼女」のK・ジェヨンの監督第２作。先輩に片思い中の引っ込み思案な女子大生が、母の日記を手にしたことから不思議な恋に導かれていく。前作のようなコメディ要素はないが、意外性の連続する巧みなストーリーテリングはそのまま。ヒロインに抜擢されたソン・イェジンの好演も印象深い。友人に頼まれて、片思いの先輩へのラブレターの代筆をすることになった大学生のジヘ。複雑な心境に揺れ動く彼女はある日、古い日記帳と何十通もの手紙を見つける。そこには母の秘められた初恋の思い出が綴られていて……。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOST FOR FREEDOM", "ハード・プリズン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "トロマ社製作による、B級映画の定着メニューの女囚もの。不条理ないわれなき罪で砂漠の女刑務所に放り込まれた元婦人警官。その中での不正や性的いやがらせを受け怒り爆発、女囚を率いて女看守や判事に銃を向ける。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ST.HYACINTHE", "セント・ヒヤシンス物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "平凡な青年ラローズは、祖母から譲り受けた家の修理を思いつく。旧友ピエローとその恋人ルシーがそれに加わり奇妙な共同生活が始まった。ケベック州の美しい風景をバックに、若い男女３人の微妙に交錯する感情を淡々と描いた叙情性豊かな作品。", "1982-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Melvin and Howard", "メルビンとハワード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "　妻子持ちのメルビンは夢見がちな冴えない男。そんな彼の惨めな人生が一変する。ネバダ砂漠で大富豪ハワード・ヒューズと出会ったメルビンは、やがて莫大な遺産の相続人に指名されることに……。メルビン・デュマーの実話を映画化。ジョナサン・デミ監督の出世作として知られるコミカルな物語。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラ", "ゴジラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "田中友幸製作、本多猪四郎監督、円谷英二特技監督という東宝特撮映画の黄金トリオによる日本初の特撮怪獣映画で、内外の特撮映画に多大な影響を与えた記念碑的な名作である。相次ぐ水爆実験により太古の眠りから覚めた体長50メートルに及ぶ大怪獣ゴジラは、口から放射能線を吐き東京を焼土と化す。しかし芹沢教授が発明した水中酸素破壊剤（オキシジェン・デストロイヤー）の前にさしものゴジラも敗れる。当時、普通作品の直接製作費が１本・２千万円の頃に約６千万円をかけ、撮影日数も４ヵ月に及ぶ超大作で、空前の大ヒットを記録した。またこの作品は日本映画では初めて1956年ニューヨークの一流劇場でロードショー公開され、これまた大ヒットを記録するという快挙を演じた。以降東宝の特撮映画はほとんどアメリカで公開されている。なお、「ゴジラ」のアメリカ版はレイモンド・バー扮する新聞記者の部分が撮り足され細部を改編、「怪獣王ゴジラ」のタイトルで公開された。", "1954-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("You Can't Take It with You", "我が家の楽園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "ブロードウェイで大ヒットを記録した戯曲を、「或る夜の出来事」のフランク・キャプラ監督、ロバート・リスキン脚本で映画化し、アカデミー作品賞・監督賞を受賞したコメディ・ドラマ。大富豪のカービーは、軍需工場拡大のため土地の買収に乗り出すが、ある風変わりな一家の反対にあう。跡取り息子トニーの恋人アニーがその一家の娘であることを知った彼は、2人の交際に大反対。トニーはなんとか交際を認めてもらおうと奮闘する。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらば愛しの大統領", "さらば愛しの大統領", "", "", "http://image.eiga.k-img.com/images/movie/55340/315x210.jpg?1933", 87, "お笑い芸人・世界のナベアツが初メガホンをとり、CMディレクター・柴田大輔とのタッグで撮りあげたお笑いエンタテインメント。大阪府知事に当選した世界のナベアツが、独立国家宣言をして“大阪合衆国”初代大統領に就任。ところが暗殺予告が届き、大阪府警随一のアホコンビ・早川刑事と番場刑事が捜査に乗り出す。刑事役の宮川大輔＆ケンドーコバヤシほか、キャストには吉本の人気お笑い芸人が勢ぞろい。", "2010-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Help", "ヘルプ　心がつなぐストーリー", "", "", "http://image.eiga.k-img.com/images/movie/56860/315x210.jpg?3719", 146, "1960年代の米ミシシッピを舞台に、白人女性と黒人家政婦たちの友情が旧態依然とした街を変革していく様子を描いたベストセラー小説の映画化。南部の上流階級に生まれた作家志望のスキーターは、当たり前のように黒人のメイドたちに囲まれて育ったが、大人になり白人社会に置かれたメイドたちの立場に疑問を抱きはじめる。真実を明らかにしようとメイドたちにインタビューを試みるスキーターだったが、誰もが口を閉ざすばかり。そんな中、ひとりのメイドがインタビューに応じたことから、社会全体を巻き込んだ大きな事態へと進展していく。主演はエマ・ストーンとビオラ・デイビス。監督は「ウィンターズ・ボーン」などにも出演している俳優のテイト・テイラー。第84回アカデミー賞でオクタビア・スペンサーが助演女優賞を受賞した。", "2012-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEVIL TO PAY", "放蕩息子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "往年の美男俳優の代表格ロナルド・コールマン主演のロマンティック・コメディ。二人の女を同時に愛してしまった男の悩みをコミカルに描く。三角関係のドロドロした面は見せず、むしろ、いそいそと楽しそうに二人の女をくどくプレイボーイの明るさとC調の軽さが魅力的な作品。", "1930-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キューバの恋人", "キューバの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "キューバ独立10周年記念合作映画として撮られた「美しい夏キリシマ」「父と暮らせば」などの名匠、黒木和雄の劇映画第２作。日本劇映画初のキューバロケを敢行し、鈴木達夫のカメラが日本人青年の女性革命兵士への想いを追う。ドライかつドキュメント・タッチのラブ・ストーリー。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOI　CE´SAR　１０ANS　１／２　１M３９", "ぼくセザール10歳半１n39cn", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "2004年のフランス映画祭横浜で大好評を博し、観客賞に輝いた感動作。ちょっぴり恥ずかしがり屋の10歳の少年セザールが、学校や家庭での日常の中で体験する小さな冒険を生き生きと綴る。これが監督２作目となる名優R・ベリが、主人公セザールのナレーションや彼の身長に合わせたカメラアングルを導入し、観る者を童心に帰らせる珠玉作を完成させた。ちょっと太めの小学生セザールは、憧れの女の子と話もできない内気な少年。ある日家に警察が現れ、数日後に父親がいなくなった。セザールの父親は犯罪者だったのか!?　たちまちウワサが広がって、セザールは学校の人気者に……。", "2003-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("楳図かずお恐怖劇場　ねがい", "楳図かずお恐怖劇場　ねがい", "", "", "http://ecx.images-amazon.com/images/I/41M4TXQX5VL._SL210_.jpg", 60, "ホラー漫画家・楳図かずおのデビュー50周年を記念して製作されたオムニバスムービー「楳図かずお恐怖劇場」の1作として、同名短編コミックを「チェーン」の清水厚監督が映像化。友達ができず孤独な毎日を送っていた小学生の等。そんなある日、ゴミ捨て場で拾った木片で人形をつくった等は、その人形にモクメと名づけて友達のように接しはじめる。やがて、同じ塾に通う女の子と親しくなったことから、等はモクメを捨てることにするが……。", "2005-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST WINTER", "最後の冬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "中東戦争真っ只中のイスラエル。行方不明になった夫を捜すため、二人の女性、ジョイスとマヤが軍本部へやって来た。ある日、スクリーンに映し出された兵士の姿を見た二人は、互いに自分の夫であると主張するのだが……。戦争にほんろうされる女性という悲劇的テーマが、正攻法の演出で描かれているドラマ。", "1984-01-01", "46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お受験", "お受験", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "45歳の峠を過ぎた実業団のマラソン・ランナー、富樫は突如リストラに遭ってしまう。社長に掛け合った彼は湘南マラソンまでクビをつなげてもらう約束をする。しかし、娘は名門小学校の入学に向けて“お受験”の真っ只中。彼に代わって妻が仕事に出ることになるが、大会の日と娘の受験の日が重なってしまい……。矢沢永吉が映画初主演を飾ったハートフル・コメディ。リストラに遭いながら意地でマラソンを続ける中年男の悲哀を、矢沢が見事に演じ切っている。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピンクカット　太く愛して深く愛して", "ピンクカット　太く愛して深く愛して", "", "", "http://ecx.images-amazon.com/images/I/21YDPFKQAML._SL210_.jpg", 68, "超ミニスカートのギャルたちが相手をする美人理容室が、やがて風俗店なみのサービスをする店になるまでを描くポルノ・コメディ。", "1983-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲキ×シネ「五右衛門ロック」", "ゲキ×シネ「五右衛門ロック」", "", "", "http://image.eiga.k-img.com/images/movie/54490/315x210.jpg?2956", 189, "演劇の舞台をスクリーンで上映する“ゲキ×シネ”の第6弾。2008年に上演された劇団☆新感線の舞台「五右衛門ロック」を、最新鋭のデジタル技術で映像化。謎の美女・真砂のお竜の計らいで刑死をまぬがれた大泥棒・石川五右衛門は、南の果てのタタラ島にあるという神秘の石“月生石”を求めて出航するが、タタラ島の国王クガイに捕らえられ……。劇団の看板役者である古田新太をはじめ、松雪泰子、江口洋介、北大路欣也ら豪華俳優陣が大暴れ。", "2009-05-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("熱血ゴルフ倶楽部", "熱血ゴルフ倶楽部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "野々山辰平は、高山商事に入社３年目のサラリーマン。落ちこぼれの集まりと噂される営業部開発課に所属するダメ社員だが、明るさとゴルフの腕前だけは誰にも負けない。ある日、高山商事が毎年参加する社会人ゴルフトーナメントの代表選考会に辰平の出場が決まる。選考会当日、会場には、かねてから辰平をバカにする営業部一のエリート社員、芝原の姿もあった。ゴルフの試合を舞台に、落ちこぼれサラリーマンが活躍するライト・コメディ。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("卍", "卍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "谷崎潤一郎の同名小説の２度目の映画化。レズビアンの女二人と、一方の女の夫という奇妙な三角関係から映し出されるエロチシズムを、セックスを戯画的に捉えて描く。小悪魔的な魅力を放つ樋口可南子と、これにこたえる高瀬春奈のレズシーンが話題になった。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAMA—月桃の花—", "GAMA—月桃の花—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "日本で唯一、地上戦が行れた沖縄を舞台にした終戦50周年記念映画。終戦も間近い頃、主婦の房は二人の子供を連れてガマと呼ばれる鍾乳洞に避難。しかし、激しい空爆が始まり、息子を亡くしてしまう。数日後、日本が戦争に負け、ガマにいた人たちは米軍の呼びかけに従い、外に出ようとしたが……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Viva Cuba", "ビバ・キューバ", "", "", "http://image.eiga.k-img.com/images/movie/54060/315x210.jpg?3273", 97, "第5回スペイン・ラテンアメリカ映画祭上映作品。ハバナの小学校に通うマルとホルヒートは大の仲良しだが、社会階級の異なる親同士は犬猿の仲。一緒に遊ぶことを禁止されながらも仲良くつるむ2人だったが、マルの母親が国外に引っ越すことを決め、離れ離れの危機に。離婚して遠くに住むマルの父親に助けを求めるため、2人はキューバの最東端を目指し危なっかしい旅に出る。", "2008-09-13", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPLITTING HEIRS", "相続王座決定戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "“モンティ・パイソン”シリーズでおなじみのE・アイドルが、R・モラニスと共演。“世界一不運な男”と“幸運な男”に扮してスラップスティック・コメディが展開される。とある侯爵家は14代目まで、事故や突然死と不運続き。ところが15代目のヘンリーは唯一幸運な男。かたや友人のトミーは不運続き。しかもトミーは、インド人の母親から重大な告白を受けた。自分は本当の子ではないというのだ。そしてトミーが赤ん坊の時の洋服には、なぜか侯爵家の紋章が付いており……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NICKELODEON", "ニッケルオデオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "映画草創期のハリウッドを舞台に、映画に命を懸ける人々の生きざまを、スラップスティック・タッチで描く。「ペーパー・ムーン」のオニール父娘が出演し、ボグダノヴィッチ監督が手掛けたノスタルジー・ムードあふれる佳作。テータムの愛らしさが印象的だ。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN COMPLICATO INTRIGO DI DONNE，VICOLI E DELITTI", "女と路地と陰謀と", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "犯罪の匂いが漂うナポリ。元売春婦の女が経営する小さなホテルで男が殺された。男はナポリの犯罪組織カモッラのファミリーの一人だと判明する。警察は捜査に乗り出すが、同じ手口でまたも殺人事件が起こる……。麻薬問題を絡めながら、苦悩する母親の姿を浮き彫りにしたミステリー仕立てのドラマ。L・ウェルトミューラーにしては珍しい題材だが、A・モリーナの熱演と相まって印象深い一編となった。", "1985-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Twilight Gangsters", "ミス・ギャングスター", "", "", "http://image.eiga.k-img.com/images/movie/55990/315x210.jpg?4196", 109, "ジョンジャ、ヨンヒ、シンジャの平均年齢65歳の3人は、銀行強盗の被害にあい、夢にみていたハワイ旅行を実現させるための貯金を奪われてしまう。3人はお金を取り戻すため、今度は自分たちが銀行強盗になることを決める。3人の老婦人が銀行を襲おうと悪戦苦闘する姿を、笑いや涙を交えて描く。「ノッキン・オン・ヘブンズ・ドア」のティル・シュバイガーが製作したドイツ映画「ヤンババ！　ばばぁ強盗団がやって来る！」（2000／日本未公開）の韓国リメイク。監督は「花嫁はギャングスター」の脚本家カン・ヒョジン。", "2011-04-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幼獣マメシバ", "幼獣マメシバ", "", "", "http://image.eiga.k-img.com/images/movie/54160/315x210.jpg?2137", 106, "「イヌゴエ」「ネコナデ」に続く動物シリーズ第3弾。主演は個性派俳優の佐藤二朗、共演に安達祐美、笹野高史ら。二郎は無職で実家暮らしの中年ニート。しかし父親が突然亡くなり、母親は家出をしてしまったことで途方に暮れていた。そんなある日、二郎は買い物に出かけた道中でマメシバの子犬に出会う。追い払ってもついてくるその子犬は、母親が自分を探させるために仕掛けた犬だった……。", "2009-06-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SON　DE　MAR", "マルティナは海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "地中海沿岸の港町のカフェで働く若い娘マルティナが、ウリセスという若い教師と恋に落ちる。マルティナを詩的な言葉で賛美するウリセス。しかしある日、ウリセスは船に乗ったまま失踪。マルティナは地元の有力者と結婚するのだが……。官能的な作風で知られるスペインのB・ルナ監督が手掛けた情熱的なラブ・ストーリー。大胆なラブ・シーンを披露する主演の新進女優L・ワトリングも魅力的。", "2001-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("An Annapolis Story", "USタイガー攻撃隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "　ジムとトニーの兄弟はアナポリス海軍兵学校の士官候補生。優等生の兄ジムには美しい恋人ペギーがいた。彼女は訓練中の怪我で入院した弟のトニーを見舞うが、そんなペギーに、トニーは愛を告白してしまう。それが原因で兄弟の間にヒビが。やがて兵学校を卒業したふたりは空母に配属されるが……。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lucky Number Slevin", "ラッキーナンバー7", "", "", "http://image.eiga.k-img.com/images/movie/33810/315x210.jpg?4787", 111, "ジョシュ・ハートネット、ブルース・ウィリス、モーガン・フリーマン、ルーシー・リュー、ベン・キングズレーらが豪華競演を果たしたクライム・サスペンス。仕事をクビになり、恋人の浮気現場を目撃し、住むところも失った不運続きの青年スレブン。友人を頼ってニューヨークへやって来た彼を、さらなる不運が襲う。人違いされ、敵対する2人のギャングから同時に借金の返済を迫られたのだ……。2転3転するスリリングな展開から目が離せない。", "2007-01-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市と用心棒", "座頭市と用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "座頭市＝勝新太郎と用心棒＝三船敏郎が世紀の対決をする岡本喜八監督による一編。ストーリーそっちのけで二人の対決が注目されたが、結果は予想通り（？）引き分けに終わり観客をガッカリさせることに。ともあれこの記念すべき20作目は、シリーズ最大のヒットとなった。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　怪談レストラン", "劇場版　怪談レストラン", "", "", "http://image.eiga.k-img.com/images/movie/55370/315x210.jpg?1415", 100, "累計800万部を突破した人気児童文学シリーズを、実写とCGアニメを融合させて映画化。「催眠」「感染」などのホラー作品を数多く手がける落合正幸監督がメガホンをとり、第12回全日本国民的美少女コンテストでグランプリを受賞した工藤綾乃が映画初主演を果たす。山桜市にある不気味な廃墟「怪談レストラン」で人々が突然姿を消す事件が続発し、それは死神からのメールの仕業だと噂されるようになる。小学6 年生の大空アコは、事件解明のため仲間とともに「怪談レストラン」へと向かう。一方、自称・怪奇探偵の天野ハルは、消えた妹を探して山桜中学校を訪れていた。", "2010-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOLD DIGGERS OF 1935", "ゴールド・ディガース36年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "医学生のディックは夏休みにアルバイトをしていたホテルで、富豪のプレンティス夫人とその娘アンと知り合いになる。アンは、ふたまわりも歳の離れた大金持ちとの結婚を押しつけられていたが、最終的にはディックと結ばれることに。挿入曲『ララバイ・オブ・ブロードウェイ』の大ヒットで知られるミュージカルの傑作。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　MIRACLE　OF　BERN", "ベルンの奇蹟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "元サッカー選手であるゼーンケ・ヴォルトマン監督が、今なお奇跡と語り継がれるスイス・ワールドカップでの西ドイツ代表の初優勝を背景に描いた家族劇。元戦争捕虜の父親と憧れのサッカー選手との間で揺れる少年の姿を、ハンガリー代表との決勝戦の模様を再現しながら感動的に綴る。ルバンスキー家に戦争捕虜だった父が帰ってきた。家族間の葛藤が生じる中、末っ子マチアスはサッカーW杯決勝戦を見たいと願うが……。", "2005-04-16", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フランケンシュタインの怪獣 サンダ対ガイラ", "フランケンシュタインの怪獣 サンダ対ガイラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「フランケンシュタイン対地底怪獣」の姉妹編。クローン生物のフランケンシュタインが、より怪獣的になり、分裂によって山の怪獣サンダと海の怪獣ガイラの２体に分かれる。心優しい兄のサンダと人間を食う凶暴な弟のガイラが、骨肉相食む死闘を演じる。２匹の死闘は都市を中心に展開され、従来脇役に徹してきた自衛隊が、今回は生命の細胞までも焼きつくす殺人光線兵器・メーサー光線砲戦車を登場させ、互角の闘いを演じる。この日・米合作によるフランケンシュタイン２部作は、当時の怪獣ブームから、人間不在の怪獣トーナメント化した怪獣映画に一石を投じたが、本流にはならなかった。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RICH AND STRANGE", "おかしな成金夫婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "夫婦仲の冷めたヤング・カップルに突然大金が転がり込み、二人は退屈しのぎに世界一周の旅に出るのだが……。旅の道中で起こる思わぬハプニングをユーモアを交えて撮った、ヒッチコックのトーキー初期の珍品。各国ロケは当時にしては珍しい。ビデオは、別題「リッチ・アンド・ストレンジ」でも発売。", "1932-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　THOMAS　CROWN　AFFAIR", "トーマス・クラウン・アフェアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "趣味で美術品の泥棒を繰り返す富豪と、賞金目当てで調査に乗り出した保険会社の美人調査員を主人公に、華麗な泥棒ゲームと恋の駆け引きを描いたおしゃれなロマンティック・サスペンス。スティーヴ・マックィーンの「華麗なる賭け」を、P・ブロスナンが製作・主演でリメイク。ニューヨークの美術館に４人組の泥棒が入り、モネの絵が盗まれる。犯人たちは捕まったものの、絵の行方は分からないまま。保険会社から派遣された調査員キャサリンは４人が名も知らぬ男に雇われたザコであることを突き止め、モネの愛好家である実業家トーマス・クラウンに目をつける。そして彼女は大胆にも彼に接近するが、いつしか彼に惹かれ始め……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うさぎドロップ", "うさぎドロップ", "", "", "http://image.eiga.k-img.com/images/movie/55660/315x210.jpg?1987", 113, "累計発行部数60万部を突破した宇仁田ゆみの人気漫画を、SABU監督が松山ケンイチ主演で実写映画化。亡き祖父の隠し子である6歳の少女りんを引き取り、不器用ながらも必死に育てようと奔走する姿を描く。りんを演じるのは、「告白」「ゴースト　もういちど抱きしめたい」の天才子役・芦田愛菜。また、りんと同じ幼稚園に息子を通わせるシングルマザーのモデルを演じる香里奈のほか、池脇千鶴、木村了、キタキマユ、風吹ジュン、中村梅雀らが脇を固める。", "2011-08-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Goods: Live Hard, Sell Hard", "迷ディーラー!?ピンチの後にチャンスなし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今宵かぎりは…", "今宵かぎりは…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "同じ団地に住む２組の夫婦と、その夫たちと不倫関係にある若い女二人組。彼らの錯綜したストーリーをクールに紡いだ人間ドラマ。コミカルな軽妙さと、シリアスな冷徹さが一体化した演出が光る。主演の葉月蛍を中心に、久保田あづみ、佐々木ユメカら女優陣の自然体の演技も、映画を風通しのよいものにしている。「新・団地妻／不倫は蜜の味」のタイトルで、ピンク映画としても公開された。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2010-2011 プッチーニ「西部の娘」", "METライブビューイング2010-2011 プッチーニ「西部の娘」", "", "", "http://image.eiga.k-img.com/images/movie/56470/315x210.jpg?1779", 205, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を劇場のスクリーンで上映する「METライブビューイング」2010～11年シーズンの5作目。開拓時代のアメリカ西部カリフォルニア。保安官のランスは、街のマドンナ的存在で酒場の女主人のミニーに求婚していたが、ミニーは盗賊のジョンソンと恋に落ちる。嫉妬したランスはジョンソンを捕らえ、ミニーは愛する男のため身をていして救出に向かう。「蝶々夫人」で日本を描いたプッチーニが、同作の次にアメリカ西部を舞台に描いたラブストーリー。", "2011-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春のお通り", "青春のお通り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "当時、日活が青春スター３人娘として売ろうとしていた吉永・浜川・松原の共演による明朗青春編。“チャッカリスカヤ”とあだ名される桜子は、楽しい短大生活を経て文化人宅のお手伝いさんに雇われた。ところがこの高名な放送作家と女優のカップルは、好き勝手に浮気したりして彼女を幻滅させる……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅がゆく７ 四国烈死篇", "修羅がゆく７ 四国烈死篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "同名人気コミックを映画化した人気シリーズの第７作。大恩ある先代の岸田組組長の三回忌のために高知を訪れた本郷流一。そのころ四国連合という不可侵の一枚岩の下、各組が共存していたこの地に不穏な空気が流れ始めていた。新道建設に伴う莫大な利権に目をつけた岸田組の現組長・伊能が四国に火種を巻き始めたのだ。そして火の粉は本郷にも降りかかってくる……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飛鷹計劃／PROJECT EAGLE", "プロジェクト・イーグル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "世界をまたにかけて秘宝を探す冒険家“アジアの鷹”の活躍を描く、アクション満載のミステリー・アドベンチャー。ジャッキー・チェン自身がいちばん気に入っているという「サンダーアーム／龍兄虎弟」のキャラクターが再び登場、砂漠の中で展開する限界ぎりぎりのアクションが必見だ。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVEN IS A PLAYGROUND", "ダンク・ストリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ストリート・バスケットのメッカ、シカゴを舞台に、プロや大学生リーグにスカウトされて“ゲットー”を抜け出そうとする青年たちの青春を描くスポーツ・アクション。当時、NBAの人気選手だった、H・オラジュワン、B・キンブルらが出演するなど、NBA及びバスケットファンなら見逃せない一作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星に願いを。", "星に願いを。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「いま、会いにゆきます」の竹内結子主演によるロマンティック・ストーリー。香港映画「星願」を日本風にリメイクした。別人として甦った青年と、彼に気づかない女性の“すれ違い”を、「ごめん」の冨樫森監督が正攻法の演出で描く。函館。事故で失明し、声も失ってしまった笙吾は自暴自棄になっていたが、担当看護士、奏の尽力で生きる希望を取り戻す。ところが彼は車にはねられ死亡。“星”の計らいで別人としてよみがえることを許されるのだが……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Saw 3", "ソウ3", "", "", "http://image.eiga.k-img.com/images/movie/1630/315x210.jpg?3996", 107, "04年、05年に引き続き製作されたゲーム感覚の監禁スリラー第3弾。小学校で発生した殺人事件の現場に呼び出された女刑事ケリーは、現場にある死体が同僚のエリックのものではないと安堵するが、その夜、何者かに拉致され、どこかの地下室に監禁されてしまう……。原案は本シリーズの産みの親ジェームズ・ワン＆リー・ワネル。監督は前作「ソウ2」でもメガホンをとったダーレン・リン・バウズマン。", "2006-11-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画 Yes！プリキュア5 GoGo！　お菓子の国のハッピーバースデイ♪", "映画 Yes！プリキュア5 GoGo！　お菓子の国のハッピーバースデイ♪", "", "", "http://image.eiga.k-img.com/images/movie/53690/315x210.jpg?2453", 70, "女の子に大人気のTVアニメ「Yes！プリキ ュア5 GoGo!」の劇場版。夢原のぞみはサンクルミエール学園に通う元気な中学生。チョコラという少女を助けたことで、お菓子の国「デザート王国」に招待される。お菓子を堪能するプリキュアたちだったが、そこに悪者ビター、ドライ、ムシバーンが現れ、プリキュアたちをお菓子にしてしまおうと企んでいた……。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おれさま", "おれさま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "20年前にタイムスリップして過去の自分と出会い、新たな一面を見出していく青春音楽ものの感動作。ヴィジュアル系ロック・スター、雅-miyavi-が初主演し、“dir en grey”“Psycho le Cemu”などのミュージック・クリップを手掛ける近藤廣行が監督。音楽も雅-miyavi-が手掛けている。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOTEL OKLAHOMA", "新・チェーンヒート 戦慄の女刑務所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "宝石店強盗の共犯者として女性刑務所に送られた若く美しい娘クリッシー。だが冷酷な女性副所長に支配されているそこは、サイコ、殺人者、同性愛者の巣で、彼女は受刑者たちの注目の的となる。前半は看守や囚人同士の暴力など、女性刑務所の実態を淡々と、後半はボーイフレンドによる囚人奪回作戦をスリリングに見せるセクシャル・バイオレンス。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍虎風雲／CITY ON FIRE", "友は風の彼方に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "優秀な銃を調達することで宝石強盗団とコンタクトするチャンスを得た秘密捜査官のチャウ。まんまと組織への潜入に成功するが、組織の幹部フーと次第に心を通わせるようになり……。「男たちの挽歌」以前に、地元香港でノワール人気に火をつけた、ハードボイルド・アクション。監督のラムと主演のユンファはこの後共同で映画製作会社“シルヴァーメダル”を設立、「いつの日かこの愛を」を世に送り出している。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ellie Parker", "ナオミ・ワッツ　プレイズ　エリー・パーカー", "", "", "http://image.eiga.k-img.com/images/movie/33740/315x210.jpg?4727", 95, "ナオミ・ワッツ扮する女優の卵エリー・パーカーが、失敗や挫折を繰り返しながらもスターを夢見て奮闘する姿を描いたコメディ・ドラマ。彼女が「マルホランド・ドライブ」で共演した俳優のスコット・コフィと共に製作した16分の短編を、4年の歳月をかけて長編化。製作中に「ザ・リング」「21グラム」などの成功で実際にハリウッド・スターとして大ブレイクした彼女自身の体験も織り交ぜながら、ハリウッドの内幕がコミカルに描かれる。", "2006-11-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN TO ME", "この胸のときめき", "", "", "http://ecx.images-amazon.com/images/I/51Lf9AFYKbL._SL210_.jpg", 116, "「グリーンマイル」のトム・ハンクスの妻役などで知られる女優ボニー・ハントが初監督に挑戦したラブ・ストーリー。妻を亡くした痛手から立ち直れない建築技師ボブと長い入院生活から解放されたばかりで恋に臆病になっているウエイトレスのグレース。ボブが偶然グレースの店に立ち寄ったことから彼らには次第に愛が芽生え始めるのだが、実は二人の愛を阻む衝撃の事実が待ち受けていた。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大幹部・無頼", "大幹部・無頼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "第１作によってシリーズの性格が見事に設定されたが、このシリーズ第２作によってさらに深く描き込まれていく。雪に覆われた小都市に現れた五郎は、金のために昔のヤクザ仲間の組織の用心棒となる。小沢啓一監督が、処女作とは思えないほど情感の入った演出をみせ、シリーズの最高作に仕上がった。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽炎２", "陽炎２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "女胴師とその姉貴分の確執を軸に、ヤクザの抗争を絡めて描いている。パート１を手掛けた故・五社英雄監督の遺志を受け継ぎ、奥山和由プロデューサーが映画化。昭和４年、九州の花街・門司に、なまめかしい菩薩像の刺青を背負った女胴師・不知火おりんが、姉貴分の由良を訪ねてやってくる。由良は昔、15歳のおりんを拾って一人前の女胴師に育てたが、借金のかたにおりんをヤクザに売った。この事実を知らないおりんは、由良に誘われ“大世界”で開かれた夜叉組の主催する花会で胴師を務める。しかし、自分の利益の為に手段を選ばないやり方を目の当たりにしたおりんは、由良に戦いを挑む……。総工費３億円をかけたオープンセットをすべて燃やし尽くす、ラストの爆発炎上シーンは見どころ。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あしたはきっと…", "あしたはきっと…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "TVやCMでも活躍中の吹石一恵が、等身大の少女をさわやかに演じるファンタスティックな青春映画。明るく毎日を過ごす高校２年生の水沢夏音。唯一の悩みは、片思いの相手にどうやって自分の気持ちを伝えるかということだ。そんなある日、葡萄畑で謎の少女に出会ったことから、夏音の不思議な体験が始まる。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEET KILLING", "依頼殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "銀行の取締役のアダムは、仕事と趣味の漫画に没頭して静かな生活を送ることを望んでいたが、口うるさい妻ルイーズとの関係はぎくしゃくしていて、会社でも家でも心が休まることがない。そんな彼はある日、妻の殺害を企てる。緻密な計画は、完璧なアリバイを作ったかに見えた。しかしアダムの前に、彼のアリバイを疑う刑事ガルシアと、彼がでっちあげた架空の顧客であるはずの男ザーゴが現れるのだった。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大菩薩峠・第２部", "大菩薩峠・第２部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "龍之助に心を寄せるお豊の手引きで、伊勢古市に身を隠した失明の龍之助。運命は彼を神尾主膳の愛妾、お絹に会わせる。浜松から江戸へと二人の跡を追って、兄の仇を討たんと宿敵、宇津木兵馬は馬を走らせる。龍之助は血に狂う悪癖の果てに谷底へ落ち、ここで薬売りのお徳に救われる。物語は錯綜のきわみをみせたが、内田監督の描写力はさすが。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATHE AND SON", "ファザー、サン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ロシアが世界に誇る映画作家、アレクサンドル・ソクーロフが人間関係を題材にした3部作の第2作に当たる作品。第1作の母子に続き、今回は父子の関係を描く。レンブラントの絵画を想起させるような映像の美しさはため息が出るほど。古びた建物で息子と暮らす父親。亡き妻を今も愛している彼にとって息子は彼女とのつながりで、離れて暮らすことは考えられない。だが彼は考えを改め、息子との別れを決意するが……。", "2006-04-29", "7,24,2,4,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社長行状記", "社長行状記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "既製服の大手メーカー・栗原サンライズ。社長の栗原弥一郎は不況を克服すべく意欲満々である。ライバルの大阪衣料が名古屋に進出するらしいとあって、社長自ら部下を連れて名古屋に繰り込んだまではよかったが、地元の芸者に“ホの字”でメロメロ。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にっぽん美女物語 女の中の女", "にっぽん美女物語 女の中の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "神楽坂の料亭“駒蝶”の４人姉妹の一人、ズッコケ娘のひらめが巻き起こす珍騒動を描いた第２作。今回は、厄払いのために出かけた金比羅参りの旅に絡んで、ドタバタが繰り広げられる。脇役陣が好演するアットホームな人情コメディ。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Star Wars: Episode I - The Phantom Menace", "スター・ウォーズ　エピソード1 ファントム・メナス", "", "", "http://ecx.images-amazon.com/images/I/51902JZJS3L._SL210_.jpg", 133, "SF映画の金字塔「スター・ウォーズ」3部作から16年を経て新たに描かれたシリーズ前章。秘められたフォースの力を宿す少年アナキン・スカイウォーカーが、やがて銀河を震かんさせるダースベイダーになるまでの姿を描く3部作の第1部。平和な惑星ナブーが銀河通商連合に襲撃され、その事件の調査に向かったジェダイ騎士のクワイ＝ガン・ジンとオビ＝ワン・ケノービは、メカの不調で砂の惑星タトゥイーンに不時着する。そこで出会った9歳の少年アナキンに眠るフォースの力を感じ取ったクワイ＝ガンは、アナキンをジェダイ騎士団に入団させようとするが……。", "1999-07-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀座旋風児 黒幕は誰だ", "銀座旋風児 黒幕は誰だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "日本開拓公団汚職の核心であるニセ札事件を探知した銀座旋風児・二階堂卓也。殺し屋の手から印刷工を救った彼は、ニセ札の首謀者が公団理事長であることを突き止めるが、その裏にはまだ、新日本共産促進連盟の黒川という黒幕が潜んでいて……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Reign Over Me", "再会の街で", "", "", "http://image.eiga.k-img.com/images/movie/53060/315x210.jpg?1835", 124, "家族と恵まれた生活を送っているが、どこか息苦しさを感じている歯科医師のアランは、ニューヨークの街角で学生時代の友人チャーリーと偶然再会する。しかし、チャーリーは9・11テロで愛する家族を一遍に失って以来、ひとり心を固く閉ざしていた……。心に深い傷を負った男チャーリーを、コメディアンのアダム・サンドラーがシリアスに演じる人間ドラマ。監督はコメディアンのマイク・バインダー。共演はドン・チードル、ジェイダ・ピンケット＝スミス、リブ・タイラーら。", "2007-12-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CIMARRON", "シマロン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "西部開拓時代の1889年。新婚のクラベット夫婦は多くの開拓者とともにオクラホマに新天地を求めて旅立つ。だが行く手には様々な苦難が待ち構えていた……。ファーバーのベストセラーの２度目の映画化。アメリカ映画初出演のM・シェルが熱演。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小心間喋／TO SPY WITH LOVE", "噂のテディ・ロビン　美女・美女スパイにご用心！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "宝の地図をめぐって巻き起こるコミカル・アクション・ムービー。ロックバンドのマネージャー、テディは謎の女と出くわした。女はテディのポスターに、あるネガフィルムを張って死んでしまう。その日からテディの周りに怪しげな奴らが集まってくる。脚線美の見事なリリー、台湾文化保護局長と名乗るリー。しかし彼女たちは、すべて女スパイだった。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE COINS IN THE FOUNTAIN", "愛の泉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "肩ごしに銀貨を投げると願いが叶うという伝説の“トレビの泉”をはじめとした様々なローマの名所を背景に、３組の男女のそれぞれの愛を優しい眼差しで見つめた、まさにクラシックと呼ぶにふさわしいロマンティックなラブ・ストーリー。フランク・シナトラが主題歌『愛の泉』を歌っている。原作はJ・H・セコンダリーのベストセラー小説。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("家族会議", "家族会議", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "横光利一の新聞連載小説を島津保次郎が映画化した作品。東京の株屋のインテリ若主人・高之と彼に恋する同業者の娘二人との恋愛の行方を追いながら、株屋同士の熾烈な争いを描いていく。高之役の佐分利信をはじめ、及川道子、桑野通子、高田浩吉ら配役の豪華さも見もの。脚本は「浮草物語」「戸田家の兄妹」など戦前の小津安二郎とのコンビで名高い池田忠雄。美術も「東京の合唱」「出来ごころ」など初期小津作品で知られる脇田世根一である。キネマ旬報ベストテン第６位に輝いたこの作品は、1954年、中村登監督によって再映画化された。及川道子の最後の出演作品としても記憶すべき映画。吉村公三郎が助監督についている。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い家", "黒い家", "", "", "http://image.eiga.k-img.com/images/movie/1800/315x210.jpg?3205", 118, "古都・金沢。昭和生命北陸支社に勤務する若槻慎二は、保険金の請求書類に埋もれ苦闘する日々を送っていた。ある日、若槻は中年女性の声で「自殺でも保険金は下りるのか」という問い合わせの電話を受ける。思いつめたその声の様子に、思わず自殺を思いとどまるようなだめたが、相手は彼の名前を確認すると電話を切った。そして翌日、菰田と名乗る契約者から若槻名指しのクレームを持ち込まれる。", "1999-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUCKY NUMBERS", "ラッキー・ナンバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "アメリカで実際にあったナンバーくじの不正工作事件をもとにしたクライム・コメディ。ラスは地元のTVで人気のお天気キャスター。街を歩けば顔入りの大きな看板がそこかしこに立っていて、行きつけのファミレスには専用駐車場と専用テーブルまで用意されている。しかし本業の商売はあがったりで、実情は火の車。困り果てたラスは宝くじ番組のロット・ガール、クリスタルと組んで一獲千金のイカサマを思いつく。人気シットコム『フレンズ』のL・クードローが好漢トラヴォルタを向こうにまわし、したたかなのにちょっと抜けているTVタレント役をあっけらかんと演じている。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brassed Off", "ブラス！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "炭鉱夫たちによるブラスバンド、グライムソープ・コリアリー・バンドの実話を基に映画化した感動作。鉱山閉鎖の危機に揺れるイギリス北部の小さな炭鉱町。伝統的なブラスバンドのメンバーたちも、いつ失業するかわからない自分たちのことで精一杯。そんな中、指揮者のダニーだけが全英選手権への出場を目指して張り切っていたが……。「トレインスポッティング」で世界的に注目を浴びたユアン・マクレガーが出演したことでも話題に。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AIR BUD", "エア・バディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "友達もできず、大好きなバスケット・チームにも入れずにいるシャイな転校生ジョッシュは、ある日、バディというゴールデン・レトリバーの捨て犬を拾う。ところが、そのバディはバスケの天才で、学校のチームに入って大活躍を繰り広げ、ジョッシュもたちまち人気者となる。しかし、そこにバディの元飼い主が現れ……。少年と犬の友情を描いた心温まる一作。アメリカでは続編も公開されている。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METROPOLITAN", "メトロポリタン／ニューヨークの恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "アイビー・リーグへ入学するための進学校に通っている、アメリカ上流階級の子弟たち＝通称プレッピー。そんな彼らが作るグループに、偶然のことからアウトサイダーであるトムが仲間入り、そしてグループは徐々にバランスを崩していく……。滅びつつあるアメリカ上流階級をモチーフに、その子弟たちの姿を描く青春映画。アメリカで単館ロードショーながらロングランを続けた。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Undertow", "アンダートウ　決死の逃亡", "", "", "http://ecx.images-amazon.com/images/I/21SRVFW7Q3L._SL210_.jpg", 108, "", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富士に立つ影", "富士に立つ影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "市川右太衛門と北大路欣也の親子共演作。将軍家斉の治世、幕府は富士の裾野に軍略研究のための城を築くことを計画。佐藤菊太郎と熊木伯典の二人によって、築城軍師の地位を懸けた大問答が行われることになった。軍配は菊太郎に上がったが、伯典は陰謀をめぐらす……。北大路は父演じる菊太郎を慕う若侍役。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Aliens in the Attic", "屋根裏のエイリアン", "", "", "http://ecx.images-amazon.com/images/I/51b1Y4ASplL._SL210_.jpg", 86, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("砂繪呪縛", "砂繪呪縛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "この年、阪妻プロ、日活大将軍、マキノ御室と３社競作になった土師清二の原作映画化の、これはマキノ版。主演に月形龍之介、脚本を山上伊太郎と、当時の若き才能を結集した時代劇である。元禄時代における、柳影組と天目党の次期将軍の跡目争いを描いたもの。現在は、完結編にあたる第３編のフィルムは失われている。", "1927-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SON OF THE SHEIK", "熱砂の舞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "ハリウッドの伝説的二枚目俳優、R・ヴァレンチノの遺作で、彼の代表作「シーク」の続編。サハラ砂漠の王子アーメッドは酒場の舞姫ヤスミンと激しい恋に落ちるが、悪党のガーバ一味が身代金を狙ってヤスミンを誘拐する。アーメッドは彼女を取り返すため、敵の巣窟であるガーバの砦に乗り込む……。", "1926-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH CRIMES", "ハイ・クライムズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "女性弁護士クレアの夫トムが突然FBIに逮捕される。トムの本名はロナルド・チャップマンであり、かつて海兵隊の特殊工作員だった彼は1988年、エル・サルバドルで一般市民９人を殺害し、その後逃亡を続けてきたというのだ。軍の留置所に入れられたトムと面会したクレアは彼の無実を信じ、軍事法廷に立つことにする。そして、一般的な法律が通用しない軍事裁判に対応するため、元軍の弁護士だったチャーリーと手を組むことに……。夫の汚名を晴らすため閉鎖的な軍の機構に挑むヒロインの活躍に、二転三転する謎解きの面白さを加えて描くサスペンス。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAPTAIN FROM CASTILE", "征服への道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "H・キング監督が描いた、愛憎渦巻く歴史ドラマ。1518年、スペイン。有力者の反感を買った貴族の息子ペドロは、両親とともにイタリアへ逃れようとしていた。ところが両親とはぐれてしまい、仕方なくメキシコへとその目的地を変える。そして彼は、後に南アメリカにその名をとどろかすことになる征服者コルテスに認められる……。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DIABOLICHE", "デンジャラス・ウーマン もう誰も殺せない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "車イスで、愛のない孤独な生活を送ってきた富豪の女性エリーゼ。彼女はある日、心の支えにしてきた運転手とメイドに自分の全財産を残そうと決意する。だがそれを知った二人は、突如、豹変し、看護婦までも巻き込んでエリーゼを殺そうとする……。金に目がくらんだ人間たちの醜い争いをスリリングに描いたサスペンス。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("顔", "顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "男くさいドラマを描いてきた阪本順治が、初めて女性を主役に手掛けた人間ドラマ。内向的な性格の正子は、家に閉じこもって洋裁の仕事をしている。仲の悪い妹からいつも罵倒されていた彼女は、母の葬式の日、ついに怒りを爆発させ妹を殺害してしまう。行くあてもなく逃亡し、バーでの住み込みの仕事を始める正子。従業員や客、様々な人々との出会いと別れ、ささやかな恋によって彼女は生まれて初めて生きる実感を味わい始める。逃亡生活の中で輝き、たくましくなっていくタフなヒロインに、喜劇役者として舞台で活躍する藤山直美が扮している。次第に別人のようにイキイキとした表情になる彼女の二重人格的演技力に注目。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SMOKE SIGNALS", "スモーク・シグナルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "サンダンス映画祭で観客賞を受賞したロード・ムービー。10年前に失踪したきり音信不通だった父親の死を知り、ネイティブアメリカンの青年ビクトールは友人のトーマスとともに、遺骨を引き取るための旅に出た。赤ん坊の頃、火事から救ってもらったことがあるトーマスは、恩人であるビクトールの父を慕っているが、ビクトールは父に対して複雑な思いを抱えていた。やがて二人は父の最期を看取った女性から、意外な事実を聞かされる……。インディアンの生活をリアルに捉えてはいるが、それを強調することなく、あくまで一人の青年の成長を温かく見つめていく。自身もネイティブアメリカンである新鋭C・エアの、腰の座った演出が光る。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNLAWFUL ENTRY", "不法侵入", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ロスの高級住宅街に住むマイケルとカレン夫婦。彼らはある強盗事件を契機に警官ピートと親しくなった。やがてマイケルの周辺でおかしなことが起き始め、彼は窮地に立たされる。すべてはカレンに魅せられたピートの狂気の罠だった……。人間不信が渦巻く大都会の恐怖を描いたサスペンス。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Morning Glory", "恋とニュースのつくり方", "", "", "http://image.eiga.k-img.com/images/movie/55150/315x210.jpg?2289", 107, "　レイチェル・マクアダムス、ハリソン・フォード、ダイアン・キートン共演のラブコメディ。失業中のTVプロデューサー、べッキー（マクアダムス）がやっと見つけた仕事は低視聴率のモーニングショー。ベッキーは伝説的なニュースキャスターのマイク（フォード）を起用するが、マイクは共同ホストのコリーン（キートン）と衝突してしまう……。製作にJ・J・エイブラムス、監督は「ノッティングヒルの恋人」ロジャー・ミッシェル。", "2011-02-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POV 呪われたフィルム", "POV 呪われたフィルム", "", "", "http://image.eiga.k-img.com/images/movie/57320/315x210.jpg?3628", 92, "人気若手女優の志田未来と川口春奈がダブル主演し、「リング0 バースデー」「おろち」などで知られる鶴田法男監督のメガホンで描くホラー。女優の志田未来と所属事務所の後輩・川口春奈は、視聴者から投稿された心霊動画を紹介する携帯配信用の番組を収録していた。しかし、収録中に投稿動画に異変が起こり、春奈が通う中学校で起こった怪奇現象がモニターに映し出される。霊能者から「実際にその学校へ行って浄霊しなければならない」と助言を受けた志田と川口は、番組ディレクターらとともに学校に赴くが……。", "2012-02-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東方老虎／THE CYPRUS TIGERS", "刑事ディック キプロスの虎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "キプロスを舞台に、チャイナマフィアとの攻防を描いた香港アクション。ディック、ジャッキー、そしてボムの３人は“キプロスの虎”と呼ばれる警官だった。キプロス島のチャイナ・マフィアのボス、キンが仕組んだ大掛かりな偽造米ドル紙幣の取引を内偵中である。だがキンの巧妙な罠にかかり、ボムが殺人の濡れ衣で逮捕されてしまい……。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆裂刑警／BULLETS　OVER　SUMMER", "OVER SUMMER", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "性格は正反対だが、ともに刑事として生死を懸けて戦ってきたマイクとブライアンは強い友情で結ばれていた。ある日、二人は強盗逮捕のため張り込みを始める。捜査の途中、マイクはシングルマザーになることを決めた臨月の女性と出会うが……。香港電影金像奬最優秀主演女優賞ほか、香港の各映画賞を多数受賞したヒューマン・ドラマ。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間失格", "人間失格", "", "", "http://image.eiga.k-img.com/images/movie/54410/315x210.jpg?2910", 117, "太宰治生誕100周年を記念して製作された文芸大作。津軽の資産家の息子・葉蔵は、幼い頃から周囲となじむために偽りの自分を演じていた。上京して高等学校に入学した葉蔵は、同じ画塾に通う遊び人の青年・堀木と出会う。堀木とともに酒におぼれ、放蕩生活を始めた葉蔵は、女にも不自由することがなかったが、そんな生活に徐々に虚しさを募らせていく。主演に生田斗真。監督は「赤目四十八瀧心中未遂」の荒戸源次郎。", "2010-02-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖獣都市／THE WICKED CITY", "妖獣都市 香港魔界篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "菊地秀行の同名小説を、香港のヒットメーカー、T・ハーク製作のもとで映像化したSFバイオレンス。人間界の侵略をもくろむ凶悪な妖獣たちと、“闇ガード”と呼ばれる特別部隊との壮絶な戦いを穏健な妖獣と人間との恋や友情を交えて、アッと驚くSFX映像とダイナミックなアクションを盛り込んで描く。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAAI　CHEK　LIU", "マッスルモンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "A・ラウが特殊メイクでマッチョなキャラに扮した異色アクション。主人公は前世の業（カルマ）が招く死を、他人の姿に見ることができる元僧。好意を寄せる女性刑事を悲劇の死から救おうとする彼の奔走が描かれる。ラウの熱演はもちろん、パワフルな演出も好評を呼び、香港のアカデミー賞といわれる電影金像奨で作品賞、主演男優賞を受賞した。大切な人の死から、死にゆく人の前世の業が見えるようになったビッグガイは、因果の非情さを知り、僧を辞めてストリッパーになった。ある夜、ストリップバーでおとり捜査官の女性フンイーに出会うが、彼女の背後にある醜悪な業が見えてしまい……。", "2003-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("走り屋ZERO II", "走り屋ZERO II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "走り屋の姿を描いたカー・アクション「走り屋ZERO I」の続編。レースバトルで全戦全敗の走り屋ハチのもとに、廃刊間際の走り屋雑誌編集マン・岡田がスカウトにやって来る。ハチは岡田の知識と経験を活かし、順長にトレーニングを重ねていくが、そこにハイテク機材を駆使する新星、一豊が現れる。ハチは最速の称号「ZERO」を賭け、一豊とのバトルに挑む。お笑い芸人のほっしゃん、川島明（麒麟）らが出演。", "2009-06-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Today We Live", "今日限りの命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "第1次大戦を背景に、従軍看護婦となった英国女性をめぐり、水雷艇乗組員の彼女の夫、彼女と相思相愛の米国人空軍青年の愛とヒロイズムを綴る戦争映画。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN CHAPEAU DE PAILLE D’ITALIE", "イタリア麦の帽子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "トーキー初期に「ル・ミリオン」「自由を我等に」などの傑作を連打、フランス映画の充実ぶりを世界に知らしめたR・クレールのサイレント喜劇。結婚式当日、花嫁や参列者そっちのけで風に飛ばされた帽子を追う、花婿の奇想天外な騒動を描いている。メカニカルに動きの連鎖を捉えた微笑ましい小品。", "1927-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ghost", "ゴースト　ニューヨークの幻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "1990年、アメリカのサマー・シーズンで、数ある超大作（「トータル・リコール」「バック・トゥ・ザ・フューチャーPART３」など）を向こうに回し、興行収入No.1に輝いた大ヒット作。ラブ・ロマンスにファンタジーを絡めた１本である。銀行員のサムは恋人のモリーと共同生活を始めるが、その矢先に暴漢に襲われて幽霊になってしまう。かつての同僚カールに操られ、自分を殺した男を発見したサムは、エセ霊媒師オダ・メイの力を借りて、モリーを狙うカールの悪事に立ち向かう……。心で通じ合う恋人たちの姿が感動的。名曲『アンチェインド・メロディ』が切ないドラマを盛り上げる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("装甲騎兵ボトムズ　孤影再び", "装甲騎兵ボトムズ　孤影再び", "", "", "http://image.eiga.k-img.com/images/movie/55840/315x210.jpg?3020", 0, "人気ロボットアニメ「装甲騎兵ボトムズ」の新作3本を連続上映する「ボトムズフェスティバル」第3弾。シリーズの生みの親である高橋良輔が監督を務め、OVA「赫奕たる異端」と「幻影篇」との空白期間のエピソードを描く。“アレギウムの赫い霍乱”でマーティアルを追われたテイタニアは、キリコの目的地が砂漠の交易都市グルフェーであることを知る。キリコは途中で出会ったバニラの娘ステビアとともにグルフェーを目指すが、街はメルキア軍のAT部隊に包囲されていた。", "2011-01-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THIRTY TWO SHORT FILMS ABOUT GLENN GOULD", "グレン・グールドをめぐる32章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1982年10月、50歳で亡くなったカナダの天才ピアニスト、グレン・グールド。この作品は、その彼の人生を、二年間に及ぶ調査によって映画化。32のエピソードを、彼の音楽パフォーマンスにのせて描いた、モザイク伝記映画である。奇癖があり、処方薬物嗜好者でもあったグールドの魅惑的なキャラクターを、断片的に切り取った異色作。", "1993-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋愛白書", "恋愛白書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "恋愛漫画で人気の高い里中満智子の同名コミックを映像化。見た目は派手だがオクテな奈緒と、清純なお嬢様風だが男性経験が豊富な香の二人が、理想の恋を追求する姿を描いた恋愛基礎知識満載のラブ・コメディ。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("軍艦武蔵", "軍艦武蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "“不沈戦艦”とうたわれた軍艦武蔵の建造から終焉までの航跡を追った長編ドキュメンタリー。凄惨をきわめた昭和19年10月24日のシフヤン海の戦闘、つらくも生き残った兵士たちの生への戦いなど、想像を絶する事実が次々と暴かれる。27人の元乗組員、関係者の証言を中心とする構成が、戦争下の人間の姿を浮き彫りにしていく。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Z", "Z", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "実際に起こったギリシャの左翼政治家暗殺事件を取り上げ、国際的な注目を喚起した問題作。社会派監督のコスタ・ガヴラスは、この映画１本で政治的映画の作家として名声を獲得した政治サスペンスの力作。ギリシャでは上映禁止となった。", "1969-01-01", "2,71", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOOLISH　WIVES", "愚なる妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "サイレント期の異才E・フォン・シュトロハイムが監督、出演など５役を務めた監督第３作。当時としては破格の110万ドルの費用と一年以上の撮影期間が費やされたといわれ、そのセットの豪華さと上流階級に対する辛らつな風刺などが議論を呼んだ。舞台は地中海の歓楽地モンテ・カルロ。そこを訪れたアメリカの富豪ヒューズ夫妻がカラムジン伯爵と名乗る男に出会う。金持ちに取り入ろうと画策していたカラムジンはヒューズ婦人に紳士らしい態度で接近し、婦人の信頼を勝ち取る。やがてカラムジンは卑劣な手段で婦人からカネを巻き上げていくが……。当初のオリジナル版は５時間以上の大作だったが、公開時にはユニバーサル社の意向で３時間半程度に再編集され、さらに現在はその原版も散逸し、断片から復元された110分のバージョンのみ現存している。", "1922-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE MAI＾TRE DE MUSIQUE", "仮面の中のアリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ベルギーが世界に誇るオペラ歌手J・V・ダムが、音楽家の内面を見事に演じてみせた、1989年アカデミー外国語映画賞ノミネート作。彼が扮するのは有名なオペラ歌手ジョアキム。彼はある日突然引退を宣言し、以来、弟子のソフィと街で見つけた美声の青年ジャンに、自分のすべてを注ぎ込もうとする。そんなジョアキムに、ソフィはほのかな思いを寄せている。やがて実力を身につけたソフィとジャンは、ジョアキムの昔のライバル、スコッティ公爵が主催するコンテストに出場、見事に栄冠を手にするが、その時二人のもとにある死の知らせが届く……。全編に流れるオペラが場面を盛り上げる。", "1988-01-01", "37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("牝犬", "牝犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "保険会社に勤務するまじめ一筋の経理部長・堀江は、部下が会社の金を使いこんだことをきっかけに、浅草のレビュー劇場へと足を運ぶ。そこで知り合ったエミーと深い関係になり、とうとう港町に二人でキャバレーを開くのだが……。男を骨抜きにしてズルズルと身を落とさせていく女を、京マチ子が妖艶に演じる危険な女のドラマ。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRESTAR", "ファイヤースター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "太陽暦2022年。極秘任務を帯びて宇宙を航行中の巡視艇“ファイヤースター”は小型宇宙戦と出会う。中から現れた小生物は、見る間に巨大な怪物に成長をし、クルーたちを襲い始めた。「エイリアン」を思わせるSFホラー映画。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beyond the Peloton Season 1", "ビヨンド・ザ・プロトン 2009", "", "", "http://image.eiga.k-img.com/images/movie/56500/315x210.jpg?2204", 167, "世界最高峰の自転車ロードレース「ツール・ド・フランス」に出場するプロチームに密着したドキュメンタリーを一挙公開する特集上映「サイクルロードレースの世界」にて上映。カナダのバイクフレームメーカー、サーヴェロが2009年に立ち上げた「サーヴェロ・テストチーム」が、春のクラシックレースや、「ジロ・デ・イタリア」「ツール・ド・フランス」といったグランツールに挑戦する姿を克明に記録。", "2011-06-04", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABDUCTED2: THE REUNION", "禁猟区 誘拐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "山中で人間狩りの標的となった女たちの逃亡劇を描いたサスペンス。行方不明者が続出していることも知らずに、ヘムロック山にキャンプに出かけたマリア、シャロン、イングリットの３人組。同じ頃、密猟者のブラッドとジョーも高価な鹿の角をとるために山へと入っていく。そして、夜を迎えた彼女たちは突然伝説の殺人鬼バーンに襲われ、マリアが捕らえられてしまう。ブラットたちも助けに加わり、マリアを救出しようと壮絶なバトルが繰り広げられる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PURELY BELTER", "シーズンチケット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "イギリス北部の街ゲーツヘッドに住む少年ジェリーと失業中のスーエルは、名門ニューカッスル・ユナイテッドのシーズンチケットを手に入れることを夢見ていた……。サッカーへの深い愛情をモチーフに「ブラス！」のM・ハーマン監督が描く友情と希望、そして成長の感動作。二人の相棒である名犬ラスティの名演も見ものである。", "2000-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吸血少女対少女フランケン", "吸血少女対少女フランケン", "", "", "http://image.eiga.k-img.com/images/movie/54650/315x210.jpg?4130", 85, "グラビアアイドルの川村ゆきえと「お姉チャンバラ　THE MOVIE」の乙黒えりが、壮絶な恋愛バトルを繰り広げるバイオレンス・アクション。原作は内田春菊のコミックで、監督を「東京残酷警察」の西村喜廣が務める。謎の転校生もなみからバレンタインのチョコレートをもらった水島は、それを口にすると吸血体質になってしまう。一方、水島に恋するけい子は2人を引き離そうとして屋上から転落してしまうが、科学者の父によりフランケンとして蘇り……。", "2009-08-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAMAGE", "ダメージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "人望も厚く、温かい家族にも囲まれた政治家スティーブンは、ある日息子の恋人アンナと知り会う。二人はお互いに運命的なものを感じとり、関係を持つようになる。禁じられた情事に溺れるスティーブンは、やがてアンナと息子が婚約したことを知る……。豪華キャストで描くスキャンダラスなラブ・サスペンス。", "1992-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST WAVE", "ザ・ラスト・ウェーブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "オーストラリアが生んだ鬼才ウィアー監督が描く、神秘に包まれた異色ミステリー。シドニーに住む一人の弁護士が、下町で起こったアボリジニ（先住民族）殺人事件の弁護につく。ところが彼の周辺で奇怪な出来事が続出、オーストラリアは異常気象による災害でパニックに陥る。そんな中、彼は怪しげな幻覚に悩まされていく……。巨大な波の襲来を予感させるラストが、このうえなく恐ろしい。", "1977-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SNIPER３", "山猫は眠らない３／決別の照準", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "トム・ベレンジャーが孤高の凄腕スナイパーに扮する、戦場アクション・シリーズの第3弾。今回の標的は、かつての命の恩人。しかもその妻に秘めた想いを寄せているなど、知られざる主人公の内面が明かされる。凄腕スナイパーのベケットに与えられた新任務は、死んだはずの恩人、ポールを狙撃すること。ベケットは任地のベトナムに赴くが……。", "2005-07-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みんなわが子", "みんなわが子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "昭和20年夏、本土への空襲が激しさを増すなか、山奥へ疎開を重ねる子供たちと、彼らのために食料を確保しようと努力する女教師の姿を描く。“学童疎開の記録”をもとに、植草圭之助が疎開児童の生活をリアルにシナリオ化。「荷車の歌」（1959）に続く、全農映協の第２回作品。戦後、子供たちがミコシをかついで平和を喜ぶシーンは感動的。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WEREWOLF OF LONDON", "倫敦の人狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "満月の夜に変身する狼男“ウルフマン”が登場するタイプの、怪奇映画のルーツ作品。製作年と同じ1935年に日本公開されて、観客を震え上がらせた作品。名作「狼男」(1941年)などに大きなヒントを与えた。イギリスの博士がチベット高原にだけ咲くという奇妙な花“狼草”を求めて現地へ。助手と共に谷で草を採取している時、博士は狼に噛み付かれてしまい……。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEPUIS　QU’OTAR　EST　PARTI．．．", "やさしい嘘", "", "", "http://ecx.images-amazon.com/images/I/316YTZW74EL._SL210_.jpg", 102, "フランスの新鋭女性監督J・ベルトゥチェリが、素朴な国グルジアを舞台に、質素ながらも幸せに暮らす３世代の女性たちの複雑な思いを、温かい目線で描いた珠玉の一作。85歳で女優デビューを果たした90歳のE・ゴランタンが演じるエカおばあちゃんの振る舞いに、穏やかな感動を覚える。グルジアに住むエカおばあちゃんは、パリに住む息子・オタールの手紙が何よりの楽しみだった。しかしある日、オタールが事故死したと娘と孫が知らせを受け……。", "2004-10-30", "2,68", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("窓からローマが見える", "窓からローマが見える", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "報道カメラマンのカルロは、不思議な日本人の娘Oに出会い惹かれるが、一方で彼の留守中、黒人に強姦された妻の幻影に悩まされる……。世界的な版画家にして芥川賞作家、池田満寿夫の「エーゲ海に捧ぐ」に続く監督第２作。夫人の佐藤陽子がジプシー女で登場。イタリアと日本の合作映画。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わたしの名は情婦", "わたしの名は情婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "麻薬強盗犯とは知らずに高田を愛した妙子は、彼の逮捕とともにスキャンダルの渦中に放りこまれてしまう。新聞記者の中野は自分のスクープから妙子が身を落としていくのに責任を感じ、彼女に力を貸すのだった。戦前は時代劇を得意としていた森一生が、戦後の占領軍の方針で作った一連の現代劇の中の代表作。鋭い切れ味の演出が光る。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DISTINGUISHED GENTLEMAN", "エディ・マーフィの　ホワイトハウス狂騒曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "巧みなペテンの手腕を駆使して、下院議員に当選した詐欺師のトマスは、意気揚々とワシントンへ。もちろん、目当ては金もうけだったが、女性活動家との恋をきっかけに、果敢にも環境問題に取り組み始める……。E・マーフィが口八丁手八丁のサギ師役に扮して、腐敗政治家と対決する痛快コメディ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO BIG DEAL", "ノー・ビッグ・ディール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "マイケルの高校にやって来た転校生のアーノルドは、少年院出で母親はアル中だった。しかし先生の愛情やマイケルの友情で、ぎくしゃくしながらも彼は次第に心を開く。マット・ディロンの弟ケヴィンが、人より先に人生を知ってしまった陰のある転校生役でデビューした学園ドラマ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふうけもん", "ふうけもん", "", "", "http://image.eiga.k-img.com/images/movie/54100/315x210.jpg?2667", 0, "", "2008-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　ドキンちゃんのドキドキカレンダー", "それいけ！アンパンマン　ドキンちゃんのドキドキカレンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "やなせたかし原作の人気アニメーション劇場版第3作「それいけ！アンパンマン　とべ！とべ！ちびごん」との同時上映作品。", "1991-07-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kicking & Screaming", "ペナルティ・パパ", "", "", "http://ecx.images-amazon.com/images/I/516MS1A1B6L._SL210_.jpg", 95, "人気コメディアンのウィル・フェレルが主演を務めるスポーツコメディ。闘争心あふれる父バックに育てられた冴えない中年男フィルは、息子が所属する弱小サッカーチーム「タイガース」の監督を引きうけることに。バックが監督を務める強豪チーム「グラディエーターズ」にボロ負けしたことから、フィルは鬼コーチと化してチームメイトたちを鍛えはじめるが……。主人公の父バック役に、「ゴッドファーザー」などの名優ロバート・デュバル。", "2005-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("化粧", "化粧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "恋人に裏切られて自殺した双子の姉を想う妹は、男に対して復讐を決意。やがて、男が再起をかけたデパートとの取引に彼女が待ったをかけ、その結果、男を自殺に追い込むことに成功する……。多彩な女優陣のなかで京マチ子の美しさが光る。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WANTED：DEAD OR ALIVE", "WANTED ウォンテッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "改造ショットガンを片手に凶悪犯を追う現代の賞金稼ぎ、ニック。25万ドルの賞金がかかった国際的爆弾殺人鬼を相手に、彼はL.A.で壮絶な戦闘を開始した。スリリングに展開するアクション映画。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マッドドッグ", "マッドドッグ", "", "", "http://image.eiga.k-img.com/images/movie/57250/315x210.jpg?1282", 0, "小沢仁志、哀川翔が主演する任侠映画。地元ヤクザ・任龍会組長の息子ジン、漁師の息子テツ、火葬場職員の息子マサの3人は、それぞれ異なる生い立ちながらも友情を育んでいた。しかし3人の行動が原因で仁龍会との抗争が起こり、ジンの父親が命を落としてしまう。ジンは父親の仇討ちのためヤクザとしての道を歩み始め、3人はバラバラになるが、運命の皮肉により3人は時を経て再び結びつくことになる。", "2011-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジュリエット・ゲーム", "ジュリエット・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "劇団“第三舞台”の主宰者、鴻上尚史が自らのシナリオで初監督に挑んだ意欲作。小学校教師の正彦は、駅のホームで出会った真智子に一目惚れ。その日から正彦は真智子を追いかけて大冒険を繰り広げる。村上弘明が昔演じた仮面ライダーのギャグに取り入れて、全編走りっぱなしの大活躍を見せている。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEWIS＆CLARK＆GEORGE", "ルイス＆クラーク＆ジョージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "刑務所から脱走した凶悪犯ルイスとコンピュータ詐欺師クラークは、隠し金鉱の場所を暗号で記した地図を手に入れ、奪った車で旅を始める。途中、口の聞けない女ジョージと出会い、惚れてしまったクラークは彼女も連れていくことにするのだが……。簡単に殺人を犯すルイス、知能犯クラーク、そして謎の女ジョージ。危険をはらんだ３人の旅を、乾いたタッチで綴ったバイオレンス作品。セクシーでミステリアスな女を演じたR・マッゴーワンの個性が光る。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hit", "殺し屋たちの挽歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「ハイ・フィデリティ」「グリフターズ／詐欺師たち」等の作品で知られるスティーブン・フリアーズ監督初期のサスペンス作品。強盗仲間を裏切り、復讐を恐れてスペインに潜伏したウィリー。だが10年後のある日、彼の元に2人の殺し屋が現れる。彼らの任務はウィリーを誘拐してパリへ連れ戻すこと。さらに途中で巻きこまれた女性も加わり、4人の長い旅が始まった……。殺し屋2人組を個性派俳優ティム・ロスとジョン・ハートが熱演。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PASAZERKA", "パサジェルカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "戦争中に女将校だったリザが、戦後、豪華客船で新婚旅行に出た。その船上でかつての強制収容所の囚人マルタに出会い、回想が始まる……。ムンク監督の未完の作品を、彼の死後、友人たちが完成させた異色作。", "1963-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Player", "ザ・プレイヤー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "　映画会社の重役グリフィンは脅迫状まがいの葉書を送りつけてきた脚本家デビッドを勢いあまって殺してしまう。その後、グリフィンは殺人の事実を伏せたまま、デビッドの恋人ジューンと親しくなっていく。一方、警察の捜査も進んでいたが……。ハリウッドから離れて映画製作を行なっていたロバート・アルトマン監督が見事復活を果たしたブラック・コメディ。ハリウッド業界を皮肉った作品ながら、ハリウッドの人気映画人たちが大挙してカメオ出演している。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVERYBODY LOVES SUNSHINE", "エブリバディ・ラブズ・サンシャイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "イギリス・マンチェスターのギャング抗争を描いた犯罪ドラマ。同じギャング組織で幹部を務めている従兄弟同士テリーとレイが、服役生活を終えて出所。折しも中国系ギャングとの対立が激しくなり、血の気の多いテリーは躍起になる。しかし、殺伐とした日々に嫌気の差したレイは出所を機に足を洗おうとしていた。彼を実の兄弟のように思っているテリーは、レイを犯罪の世界に引き戻そうと、常軌を逸した行動に走る……。監督のA・ゴスがレイ役を兼任し、鍛えぬかれた体で強烈な存在感を発揮。組織の参謀に扮して研ぎ澄まされた個性を見せつけるD・ボウイ、暴走するテリーを怪演したゴールディなど、人気ミュージシャンの出演も見どころ。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("直撃地獄拳・大逆転", "直撃地獄拳・大逆転", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "六億円の宝石がマフィアに強奪され、甲賀忍法の甲賀、金庫破りの桜、元刑事の隼の３人がマフィア組織に乗り込む。表向きは空手映画だが、中身は徹底したスラップスティック・コメディという石井輝男監督の快作。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シグナル　月曜日のルカ", "シグナル　月曜日のルカ", "", "", "http://image.eiga.k-img.com/images/movie/57770/315x210.jpg?4282", 111, "「時をかける少女」（2010）の谷口正晃監督が、映画館から一歩も出られなくなった映写技師と、臨時バイトにやってきた大学生とのひと夏の恋を描く青春ミステリー。夏休みに地元の古めかしい映画館「銀映館」でバイトを始めた大学生の恵介は、過去のある事件が理由で3年間、映画館から外に出ていない美人映写技師のルカと出会う。バイト採用の条件として、支配人から「月曜日はルカをそっとしておく」「ルカとの恋愛は禁止」「ルカの過去について質問してはいけない」という約束をさせられた恵介だったが、次第にルカにひかれていき……。原作は関口尚の小説「シグナル」（幻冬舎刊）。ミステリアスなルカ役は今回が演技初挑戦の新人・三根梓。恵介に扮する西島隆弘（AAA）ほか、ルカの過去を知る男レイジ役で高良健吾が共演。", "2012-06-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　FOUR　FEATHERS", "サハラに舞う羽根", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "「エリザベス」のシェカール・カプールが、19世紀の英国社会を背景に元イギリス軍人がたどる数奇な運命を描く感動作。「10日間で男を上手にフル方法」のケイト・ハドソンがヒロインを熱演。1884年、イギリス軍士官のハリーは戦争に意義を見いだせず、出兵直前に除隊する。彼の行動に落胆した同僚たち、そして婚約者のエスネは、決別を示す白い羽根をハリーに送り去っていった。失意のハリーだったが、ある時、元同僚の危機を知り、民間人として友の出兵したスーダンへと向かう。", "2002-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREAKS", "怪物団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "旅回りの見世物興行の一行。様々なアクロバット芸を見せる軽業師や力自慢の大男に交じって、胴体だけの芋虫男、手がないため足芸を見せる女、シャム双生児の娘、ヒゲ女、ガイコツ男、そして小人といった見るからに奇形を売り物とするフリークスたちがいた。彼らはお互いに自分たちの置かれた境遇を思って連帯感が強い。そうした仲間の一人の小人は、気が優しくて、同じ小人の可愛い娘に惚れられていた。だが、クレオパトラという美人軽業師が、大男のヘラクレスと共謀して、この小人から金を巻き上げることをたくらむ。彼女は小人を愛しているふりをして結婚し、そのうえで毒殺を図ったのだ。それを知ったフリークスの仲間は、クレオパトラとヘラクレスに復讐する。ある嵐の夜、彼らが住む大型馬車をフリークスたちが包囲する。そして、彼らがやった復讐とは……。フリークスたちをスクリーンに登場させたことであまりにも有名な作品。その残酷なユーモアのなかで、心優しいフリークスたちの愛が輝く。ビデオ、DVDタイトルは「フリークス」。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MILKY WAY", "ロイドの牛乳屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "気の弱い牛乳配達の青年が、いんちき興行師のおかげでボクシングで連戦連勝する……というコメディ編。L・マッケリー監督が描いた、ロイドのトーキー映画の最高傑作。1947年にリメイク版「ダニー・ケイの牛乳屋」が作られたが、L・スタンダーとC・レインは同じ役で再登場している。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DROP DEAD GORGEOUS", "わたしが美しくなった100の秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ミスコンの“地方予選大会”を舞台に、ミスコンに象徴されるアメリカ文化の裏側をシニカルな視点で徹底的に暴き出した青春サクセス・ストーリー。ミネソタのド田舎で行われた“美少女コンテスト”の地区予選大会。コンテストを目前に控えた出場者の少女たちを奇妙な事件が襲う。その事件の陰に見え隠れする大人たちの陰謀と男たちの影が。やがてコンテストは思わぬ悲喜劇を引き起こし、収拾のつかない地獄絵図を繰り広げていく……。松田聖子がカメオ出演しているのも話題に。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Takhte Siah", "ブラックボード　背負う人", "", "", "http://image.eiga.k-img.com/images/movie/1010/315x210.jpg?3296", 85, "イラン・イラク戦争末期。空襲で学校を失ったイランの教師たちは、黒板を背負い村を回って人々に読み書きを教えていた。教師サイードは、危険な国境地帯で年老いたクルド人の一行に出会う。難民としてイランに逃れていた彼らは、戦局が収束に向かったためイラクの故郷の村に戻ろうとしていた。サイードは彼らを見過ごせずに道案内を買って出る。", "2001-03-17", "15,4,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緯度０大作戦", "緯度０大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "本多監督と円谷英二特技監督の名コンビによる日米合作のSFアクション映画。“緯度０”という海図にない海底のユートピアの平和を守る主人公たちが、世界征服をたくらむ悪の天才科学者と壮絶な死闘を展開する。盛りだくさんの内容ながら、円谷英二の特撮が不調で失敗作に終わった。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明日", "明日", "", "", "http://image.eiga.k-img.com/images/movie/57980/315x210.jpg?4297", 130, "2001年から宮城県仙台市で開催されている「ショートピース！仙台短篇映画祭」が、11年3月11日の東日本大震災後、被災地から映画を発信するため立ち上げた「3.11 明日」プロジェクトで生まれた短編集。冨永昌敬、入江悠、真利子哲也、河瀬直美ら、同映画祭とかかわり合いが深く、プロジェクトの主旨に賛同した41人が、それぞれ「明日」をキーワードに3分11秒のオリジナル短編を手がけた。", "2012-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Manuale d'am3re", "昼下がり、ローマの恋", "", "", "http://image.eiga.k-img.com/images/movie/57550/315x210.jpg?3761", 126, "ロバート・デ・ニーロがイタリア映画初出演を果たし、モニカ・ベルッチと共演。ローマのアパートに暮らす3組の男女の恋模様を描くラブストーリー。心臓の移植手術をしたばかりの元歴史教授エイドリアンは、パリから帰国したアパートの管理人オーグストの娘ビオラと出会い、ある秘密を抱えたビオラと語らうことで新たな人生を歩む決意をする。監督は「イタリア的、恋愛マニュアル」のジョバンニ・ベロネージ。", "2012-02-18", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LASSIE，THE NEW BEGINNING", "名犬ラッシー　ロッキー山脈を越えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "両親を亡くした幼い姉弟サムとチップ、そしてコリー犬のラッシーがカリフォルニアまで旅をする。ロッキーの雄大かつ厳しい自然を舞台に、ラッシーと子供たちとの愛の絆を描く。ラッシーの名演技もさることながら、子役の二人が愛らしい。子供だけでなく、大人も十分に楽しめる作品。DVDは「名犬ラッシー DVD−BOX」に収録。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富江 BEGINING", "富江 BEGINING", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "「富江・最終章／禁断の果実」以来、3年ぶりに製作された“富江”シリーズ第５作。第１作「富江」の及川中監督が、それ以前のできごとを描く種明かし的作品で、モンスター・富江の誕生の秘密が明かされるファン必見の内容だ。活躍目覚ましい若手女優、松本莉緒が富江役を妖艶な魅力で演じる。高校の同窓会案内が届き、とある廃校に足を運んだ礼子と健一。しかしそこには、誰の姿もない。二人は同級生、富江の悲惨な記憶を思い出す。", "2005-04-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Final Destination 3", "ファイナル・デッドコースター", "", "", "http://image.eiga.k-img.com/images/movie/33640/315x210.jpg?1900", 93, "「ファイナル・デスティネーション」「デッドコースター」に続き、“死の運命”から逃れようとする若者たちを描くサバイバル・ホラーの第3弾。ハイスクールの卒業記念に訪れた遊園地で、予知夢によってジェットコースター事故から逃れたウェンディたち。しかし、死を免れたはずの10人は、次々と恐ろしい死を遂げていく……。メガホンを取るのは、シリーズ第1作「ファイナル・デスティネーション」を手掛けたジェームズ・ウォン監督。", "2006-09-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Some Like It Hot", "お熱いのがお好き", "", "", "http://ecx.images-amazon.com/images/I/51oG4EmU1oL._SL210_.jpg", 121, "　偶然殺人事件を目撃してしまった2人の男は、追われる身に。カツラと化粧を施し女性バンドの中に潜り込み、ギャングの目を欺こうとする。名優ジャック・レモンとトニー・カーティスの女装をよりナチュラルに見せるため、モノクロで撮影された。「七年目の浮気」製作終了後、「もう2度とマリリンとは仕事をしない」と公言していたビリー・ワイルダーだったが、彼女の起用により「I wanna be loved by you」のミュージックシーンなど記憶に残る名場面が生まれた。", "1959-04-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RENT−A−COP", "レンタ・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "麻薬取引の現場を急襲した謎の男を目撃してしまったため、命を狙われるハメになった娼婦デラ。彼女は護衛のため刑事を雇うことにするが……。刑事ものアクションだが、B・レイノルズとL・ミネリの台詞のやり取りからコミカルでスピーディーな、テンポの良い作品となっている。監督はTV映画『将軍』の演出も担当したJ・ロンドン。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛇鶴八歩／SNAKE ＆ CRANE ART OF SHAOLIN", "蛇鶴八拳", "", "", "http://ecx.images-amazon.com/images/I/51aYOVdJH%2BL._SL210_.jpg", 100, "少林寺の秘技“蛇鶴八拳”の師範たちが何者かの手によって毒殺され、秘伝書が持ち去られてしまう。師範殺しの犯人を捜してチェンが大活躍。共演のカンフー女優ミャオの華麗な棒術と最後の大決戦シーンが見ものの痛快娯楽作。DVDタイトルは「ジャッキー・チェン／蛇鶴八拳」。", "1978-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNA　BARA　PER　LO　SCERIFFO UNA BARA PER LO SCERIFFO", "荒野の棺桶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "最愛の妻を殺され、流れ者シェナンドーを名乗るようになった元保安官ローガン。山賊のロホ一味に加わった彼は、リンチで殺される寸前に、妻をあやめた犯人を知って猛反撃。憎き敵とのゴーストタウンでの対決になだれ込んでいく。F・デ・マージによるマカロニ・サウンドがマニアの心をくすぐる、A・ステファン主演の復讐ドラマ。", "1966-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAPERTRAIL", "レッド・ラム　狂気の挑戦状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "FBI捜査官と連続殺人犯の攻防を描いたサスペンス。12人の女性を殺害した残忍な殺人鬼が、４年ぶりに犯行を開始した。FBI捜査官エノーラは、次なる標的らしき女性精神科医に接触し、彼女を守ろうとするが……。「レザボア・ドッグス」で強烈な個性を発揮したC・ペンとM・マドセンが再共演している。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニュータイプ　ただ、愛のために", "ニュータイプ　ただ、愛のために", "", "", "http://image.eiga.k-img.com/images/movie/54080/315x210.jpg?4012", 90, "フェリー乗り場の切符売り場で働くユリは、いつも眼帯をつけて生活していた。ある日、神無島への船を探す青年クォンが現れるが、船が3日出ないことを知るとユリを脅し、船を出航させようとする。自分の部屋に監禁されたユリは、クォンに自分と同じ特殊能力があることに気付き……。TVドラマ「太陽と海の教室」の大政絢が映画初主演、監督は「ヴァイブレータ」「きみの友だち」の廣木隆一。", "2008-11-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kaleidoscope", "カレードマン 大胆不敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「動く標的」に続いて鬼才J・スマイト監督が撮り上げた、痛快無比のサスペンス。トランプの製造工場に潜入し、カードに細工してカジノで大もうけするハンサム青年バーニーの物語。彼をめぐって、警部の娘エンジェルやギャングの大ボスらが大騒動を展開する。若きW・ベイティのカッコ良さが堪能できる作品。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARENA", "アリーナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "41世紀の未来、宇宙ステーションに特設されたコロシアムで闘う様々なエイリアン・ファイターたちの闘志を描く、前代未聞のSFX格闘技映画。各エイリアン・ファイターのなかで最も異様なスロスのデザインを、スクリーミング・マッド・ジョージが担当している。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Silence de Lorna", "ロルナの祈り", "", "", "http://image.eiga.k-img.com/images/movie/53970/315x210.jpg?3137", 105, "「ロゼッタ」「ある子供」で2度のパルムドール、「息子のまなざし」で主演男優賞とカンヌ国際映画祭で高い評価を受けるベルギーの名匠ダルデンヌ兄弟が描くラブストーリー。アルバニアからベルギーにやって来たロルナ（アルタ・ドブロシ）は、国籍を取得するため、社会のつまはじき者のクローディと偽りの結婚をする。ロルナはクローディにも言えない秘密を抱えながらも情緒不安定な彼を支え、いつしか2人の間に愛情が芽生えてるが……。08年カンヌ映画祭では脚本賞を受賞。", "2009-01-31", "37,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("哀憑歌 CHI-MANAKO", "哀憑歌 CHI-MANAKO", "", "", "http://image.eiga.k-img.com/images/movie/53570/315x210.jpg?3614", 85, "アニマルパニックホラー「哀憑歌」シリーズ3部作の第1作。男たちから金やモノをもらい、物欲にまみれた生活を送る売れっ子ホステスの美咲。そんな彼女にとって、同棲しているストリートミュージシャンの恋人・健哉だけが本音を打ち明けられる存在だった。ある晩、帰宅途中の夜道で1羽のウサギを見つけた美咲は、そのウサギを自宅に連れ帰って飼いはじめるが……。主演は、「血と骨」「隠し剣　鬼の爪」の田畑智子。", "2008-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Luck by Chance", "チャンスをつかめ！", "", "", "http://image.eiga.k-img.com/images/movie/54980/315x210.jpg?4612", 154, "ボリウッド・スターを夢見る若者たちの恋と奮闘を描いた青春ストーリー。映画スターを目指してムンバイへとやって来たソーナーは、同じく俳優志望の青年ビクラムと恋に落ちる。しかし、ビクラムが新作映画の主演に大抜擢されたことをきっかけに、2人はすれ違うようになり……。夢を追う若者たちの姿を通し、華やかなボリウッドの舞台裏を皮肉たっぷりに映し出す。シャールク・カーンら、ボリウッドスターたちが多数カメオ出演している。", "2009-10-20", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花芯の刺青 熟れた壺", "花芯の刺青 熟れた壺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "少女時代、娘道成寺の蛇身の衣装を着た役者に犯された経験を持つ江戸千代紙人形師・みち子。身持ちの堅いことで通っていた彼女が、その役者の息子との情事をきっかけにして欲情を爆発させていく様を、小沼勝が豊かなイメージを積み重ねて描いたロマンポルノの秀作。みち子を演じるのは、谷ナオミ。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青葉学園物語", "青葉学園物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "敗戦後の戦災孤児収容施設・青葉学園を舞台に、身寄りをなくした子どもたちが、貧しい境遇や厳しい条件にめげず、人間としてたくましく生きていく姿を描いた吉本直志郎のベストセラーの映画化。大澤監督がすぐれた演出力で子供たちをいきいきと描く。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("京都太秦物語", "京都太秦物語", "", "", "http://image.eiga.k-img.com/images/movie/55670/315x210.jpg?4694", 90, "山田洋次監督が、立命館大学の学生22人をスタッフに迎えて製作したラブストーリー。「築城せよ！」の海老瀬はなと人気グループ「EXILE」のUSAが主演を務める。京都・太秦の大映通り商店街にあるクリーニング店の娘・京子は、立命大の図書館に勤める平凡な女性。幼なじみの豆腐屋の息子・康太は、アルバイトをしながらお笑い芸人を目指していたが、なかなか夢がかなわずに焦りを感じていた。そんなある日、京子は白川静文字学を研究する大地と出会い、一目ぼれされてしまう。", "2010-09-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎 女地獄", "眠狂四郎 女地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "佐伯藩14万石をめぐる二人の家老の権力争い。その渦中で密書を手に入れた狂四郎は、それがきっかけで角兵衛獅子の姉弟を死なせてしまう。二人の死を胸に狂四郎は佐伯藩に乗り込むが……。伊藤雄之助、田村高廣など仇役の男優陣が充実している。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LADYKILLERS", "マダムと泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "老婦人の下宿に、音楽好きの教授がやって来る。やがて同好の士も集まり、弦楽四重奏に興じている……。だが、実はそうと見せかけて彼ら５人は現金輸送車を狙う計画を練っているのだ。気のいい老レディーの目をかすめる、キャラクター豊かな５人の名演で見せる傑作コメディ。", "1955-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL VANGELO SECONDO MATTEO", "奇跡の丘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "無神論者として知られるパゾリーニが、新約聖書のマタイ伝を忠実に映画化した話題作。それまで映画に描かれてきたハリウッド的なキリストの描写を排して、素人俳優を使って古代世界をリアリズム豊かに描いている。白黒画面の美しさは格別。", "1964-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林木人巷／SHAOLIN WOODEN MAN", "ジャッキー・チェン　少林寺木人拳", "", "", "http://ecx.images-amazon.com/images/I/51PNc-HixTL._SL210_.jpg", 106, "父の仇をとるためにろうあを装う若者、一龍。兄弟子にからかわれながら、やがて少林寺の奥義をきわめていく彼に決戦の時が訪れて……。後に大スターとなるジャッキーが、コミック・カンフーに踏み出す前に主演した本格少林寺もの。", "1977-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BILL T.JONES／ARNIE ZANE ＆ COMPANY", "ビル・T・ジョーンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "アメリカン・モダンダンスの鬼才であり、日本でも1995年に公演を行ったビル・T・ジョーンズの代表作である『アンクル・トムの小屋の最後の晩餐／約束の土地』のリハーサルから公演のクライマックスまでを追ったドキュメンタリー。ビルと人種も職業も様々な舞踏団のメンバーによる創作の背景に迫り、そのエネルギッシュなダンスの魅力を紹介していく。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("電影版　獣拳戦隊ゲキレンジャー　ネイネイ！ホウホウ！香港大決戦", "電影版　獣拳戦隊ゲキレンジャー　ネイネイ！ホウホウ！香港大決戦", "", "", "http://image.eiga.k-img.com/images/movie/34100/315x210.jpg?1594", 0, "獣の心を感じ取ることによってその力を手にする拳法“獣拳”の使い手・ゲキレンジャーの戦いを描いたスーパー戦隊シリーズ「獣拳戦隊ゲキレンジャー」の劇場版。不思議な光と共に忽然と姿を消したゲキレンジャーたちと、悪の組織・臨獣殿のリーダー理央。彼らは香港のメディア王・ヤンが主催する武術会で戦うため、ヤンが所有する島に転送されたのだった。ところがその裏には、ヤンによる恐るべき陰謀が隠されており……。", "2007-08-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Valkyrie", "ワルキューレ", "", "", "http://image.eiga.k-img.com/images/movie/53210/315x210.jpg?1637", 120, "国家に忠誠を誓った軍人シュタウフェンベルクだったが、ヒトラーの独裁政権に絶望し、祖国のために総統暗殺を企てる。第2次大戦中の1944年7月20日、ナチスのクラウス・フォン・シュタウフェンベルク大佐を首謀者として決行されたヒトラー暗殺計画を、ブライアン・シンガー監督、トム・クルーズ主演で描く歴史サスペンス。共演にケネス・ブラナー、テレンス・スタンプ、ビル・ナイ、カリス・ファン・ハウテンら豪華キャストが集結。", "2009-03-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALLAS　FOREVER", "永遠のマリア・カラス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "今は亡きオペラ界の天才歌手マリア・カラスの晩年を、彼女の友人だった名匠フランコ・ゼフィレッリがドラマ化。世界中を魅了する美声の裏側に秘められた、カラスの苦難の闘いに迫る。引退し、パリで隠遁生活を送るマリア・カラスの元に、オペラ映画の企画が舞い込んだ。それは、全盛期の録音を使って彼女の主演作を撮るというもの。最初は難色を示すカラスだったが、芸術への意欲に駆られて出演を決心する。", "2002-01-01", "4,2,3,56,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIFE WITH MIKEY", "ライフwithマイキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "元子役スターで、今は子役専門のタレント・エージェントに勤めるマイキーは、11歳になるスリの女の子アンジーをスカウト。育ちの悪い彼女をスターに仕立て上げようと、マイキーは悪戦苦闘するが……。人気スター、M・J・フォックスがセルフ・パロディーともいうべき役柄に挑んだ、ほのぼのコメディ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mortal Transfer", "青い夢の女", "", "", "http://image.eiga.k-img.com/images/movie/1260/315x210.jpg?2159", 122, "「ディーバ」「ベティ・ブルー／愛と激情の日々」のベネックス監督、8年振りの新作。原作はジャン・ピエール・ガノニョーの同名小説（扶桑社刊）。ベネックスがカメラを回し、撮影監督である「シクロ」のデノムは照明を担当。「ベティ・ブルー」のアングラード、「アサシンズ」のフジュロールが共演。精神分析医デュランは、患者であるマゾヒスティックな人妻オルガに惹かれてた。ある日、彼は彼女の診察中に眠ってしまい、目覚めると、彼の前には彼女の死体があった。", "2001-12-22", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月の夜・星の朝", "月の夜・星の朝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "バスケット部で活躍する女の子の、幼なじみを相手にした乙女チックな恋の心理を描いたほほえましい青春映画。相手との小さな駆け引きや、ちょっとした好意に有頂天になる様子など、女の子ならだれでも覚えのありそうな恋心を見事に描写している。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タイム・リープ　～あしたはきのう～", "タイム・リープ　～あしたはきのう～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "高校生の翔香は突然、日曜日から火曜日へと時間を飛んでしまう“タイム・リープ”という不思議な体験をする。クラスメイト、彰男の助けを得ながら、“タイム・リープ”の謎と学園の近辺で起きた通り魔事件の解決に乗り出す彼女の冒険を描く。「りぼん RE-BORN」「すももももも」など、少女映画の異才、今関あきよしが、映画初主演の佐藤藍子の魅力を存分に引き出し、彼女の豊かな表情を画面に映し出すことに成功。監修にやはり同じくワープ系の「時をかける少女」を撮った大林宣彦が当たった。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八つ墓村", "八つ墓村", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "毛利に敗れ、この地に逃れて住みついたが、村人のだまし討ちにあい、呪いの言葉を吐きながら死んだ落武者たち。その怨念が眠る備中・神代の八つ墓村を舞台に、次々と起こる奇怪な殺人事件と、私立探偵・金田一耕助の活躍を描いた、松竹版の横溝ミステリー大作。渥美清が金田一に扮した。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POSSESSION", "抱擁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「ベティ・サイズモア」を手掛けた気鋭のニール・ラビュート監督と「ザ・ロイヤル・テネンバウムズ」のグウィネス・パルトロウが顔を合わせた恋愛映画。研究対象である詩人の情熱的な恋に感化される学者二人の行方を追う。イギリスの大学教師、モードはヴィクトリア朝の詩人ラモットの生涯を研究中。一方、アメリカの学者ローランドは、ヴィクトリア女王の桂冠詩人アッシュを追っていた。二人は導かれるように出逢い、恋に落ちる。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The United States of Leland", "16歳の合衆国", "", "", "http://ecx.images-amazon.com/images/I/31RC4QKFPNL._SL210_.jpg", 104, "16歳の少年が恋人の知的障害を持つ弟を刺し殺した。一体、彼の中で何があったのか……？　少年の複雑な心情を解き明かすとともに周囲の人々の心の裏側も掘り下げていく。少年院で教師をしていたという新鋭M・R・ホーグが、悲劇的事件の陰にある哀しみや残酷さをリアルに描出。製作を務めたケヴィン・スペイシーが少年の父親役で出演している。ごく普通の16歳の少年リーランド。彼は恋人ベッキーを心から愛し、彼女の知的障害者の弟ライアンもかわいがっていた。そんなある日、リーランドがライアンを刺殺する。彼は少年院の教官に、固く閉ざしていた口を開き始める。", "2004-08-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AGANTUK", "見知らぬ人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "秋祭りの近いある日、ボシュ夫妻のもとに一通の手紙が届いた。妻オニラの叔父だと差出人は書いていたが、彼女にはまったく覚えがない。やがて数日後“叔父”ミットロがボシュ夫妻の前に姿を現わした。財産狙いかもしれぬと疑念を抱く夫妻や親戚たちは、ミットロに質問を浴びせかける……。インド映画界の巨匠S・レイの遺作となった、深遠なる人間ドラマ。見知らぬ叔父の出現に戸惑う姪夫妻と、豊かな教養を身につけた叔父を対比させながら、民族的、伝統的アイデンティティーを見失った現代文明に警鐘を鳴らしていく。教養人として知られたレイの遺作にふさわしい、風格を備えた力作。", "1991-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤胴鈴之助 月夜の怪人", "赤胴鈴之助 月夜の怪人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "物太夫一味に追いつめられて危機一発の鈴之助としのぶ。急を知って駈けつけた千葉道場の面々によって一味は退散。鈴之助は祖父の仇・物太夫一味を討つため旅に出る。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCREAM OF STONE", "彼方へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ニュー・ジャーマン・シネマの旗手、W・ヘルツォークが登山の世界に生きる男たちにスポットを当てた人間ドラマ。現代登山の父とうたわれるラインホルト・メスナーをモデルに、自然と人間の壮大な物語を生み出している。垂直の岩壁に覆われたセロトーレ山は、地上で最も登頂が難しいといわれる山。ロッチャはこれまでに二度、この山に挑み失敗している。それだけに彼は慎重になっていたが、ライバルのマーチンはそんな彼の態度に苛立ち、ロッチャの盟友ハンスとともに登頂を決行。だが雪崩でハンスが帰らぬ人となり、マーチンだけが下山。彼は登頂の成功を主張し、一躍名声を手に入れ、ロッチャの恋人の心をも奪ってしまう。友人と恋人を失ったロッチャは傷つき、現地にとどまる。やがて登頂に疑惑を持たれたマーチンが再び、セロトーレ山に登ることを知り、ロッチャは一人ザイルを手に巨大な岩山へと向かう……。物語さながらの過酷な状況下で撮影された、壮大な大自然の風景が圧倒的に素晴らしい。", "1991-01-01", "7,2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIMPLY IRRESISTIBLE", "バニラ・フォグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "母が残した経営不振のレストランを受け継ぎ、シェフとして立て直しを図るアマンダと、デパートの腕利きビジネスマンで店内に最高級レストランを開設する仕事を任されたトム。アマンダの作るエクレア“バニラ・フォグ”はトムを虜にし、奇跡を呼び起こす……。美味な料理が作り出す恋のマジックを描いたロマンティックなラブ・ストーリー。監督は「ジャック・サマースビー」「コピーキャット」でプロデューサーを務めたM・ターロフで、これが初演出となった。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISS THE GIRLS", "コレクター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "連続猟奇殺人を追う刑事の執念と事件の行方を、二転三転するスリリングな展開で描いたサイコ・サスペンス。ノースカロライナで起こる連続女子大生誘拐事件。ワシントンD.C.署の科学捜査専門の刑事クロスは自分の姪ナオミがその被害者になったことを知り、現地に飛ぶ。これまでに８人が誘拐され、そのうち３人が死体となって発見されたことから、地元警察は猟奇殺人事件と断定するが、クロスは長年の勘から犯人は単なる殺人者ではなく、女性を監禁して飼育する“コレクター”であるとにらむ。そんな矢先、９番目の被害者となった外科医ケイトが自力で脱出し、生還。犯人の声を聞いた彼女はクロスの捜査に参加することになる……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE NOUVEAU MONDE", "アメリカの贈りもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "フランス、オルレアン地方の小さな町で育ったパトリック・カリオン。ティーンエイジャーの彼は、経済的には何の苦労もないものの、不和な両親のもとで心を閉ざしがちな毎日を送っていた。時は1950年代前半。Tシャツやブルージーンズなど、町はずれの米軍基地から漂うアメリカ文化の匂いだけが、彼の心を高揚させる唯一のものであった。しかしふとしたきっかけで、アメリカ人少女トルーディと知り合い、彼の世界は大きく変わっていく。まだ見ぬ文化とのふれ合いを通して成長していく少年の感性を、バディ・ホリーやポール・アンカなどのオールディーズサウンドにのせて描き出した作品。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼのぼの／クモモの木のこと", "ぼのぼの／クモモの木のこと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "ロングセラーの人気コミックを、原作者のいがらしみきお自身が脚本、絵コンテ、監修を担当してフルCG化。かわいい動物たちの家族愛をほのぼのと描いている。飼っていたコゲトリ虫がいなくなり寂しくなったラッコのぼのぼのが、いやなことを忘れさせてくれる力を持つクモモの木の下に行くと、そこにはフェレットのポポがいた。誰かを待っている様子のポポと仲良くなるぼのぼのと森の仲間たち。そんなある時、クモモの木の枝が何者かに盗まれる事件が起きて……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("義兄弟 SECRET REUNION", "義兄弟 SECRET REUNION", "", "", "http://image.eiga.k-img.com/images/movie/55630/315x210.jpg?1383", 116, "南北それぞれの国家に翻弄された2人の男の運命を、ソン・ガンホとカン・ドンウォン主演で描いた社会派ドラマ。ソウル市内で発生した国家情報員と北朝鮮工作員との銃撃事件。工作員を取り逃がした責任を負わされ情報員をクビになったハンギュは6年後、国から見捨てられ潜伏生活を続けていた工作員ジウォンと偶然にも再会を果たす。それぞれの目的を隠しながら行動をともにするようになった2人は、次第に心を通わせていく。", "2010-10-30", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mickey Blue Eyes", "恋するための3つのルール", "", "", "http://image.eiga.k-img.com/images/movie/1230/315x210.jpg?4134", 102, "アート・ディーラーのマイケルは、教師である恋人のジーナにプロポーズするが、なぜか断られてしまう。やっとのことで彼女に会って理由を聞くと、ジーナは家族の秘密を打ち明けた。彼女の父フランクは、ニューヨークの残忍な犯罪組織グラシオシ一家の主要メンバーだったのだ。愛する人を危険な目に遭わせたくないと言う彼女にマイケルは「マフィアとは関わりを持たない」と約束するが、次第にマフィアの世界のトラブルに巻き込まれて行く。", "2000-09-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Top of the World", "カジノ・ヒート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "P・ウェラーとD・ホッパーという個性派二人が演技の火花を散らすアクション。服役していた元警官レイがシャバに戻ると、妻レベッカはカジノ王アトラスの愛人となっていた。そんな折り、強盗団がカジノを銃撃。だが、これは経営に行き詰まったアトラスが保険金目当てに企てた芝居。それを知ったレイはレベッカを救い、アトラスと対決する。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOPHIE’S　WORLD", "ソフィーの世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "世界的ベストセラー小説の映画化。2400年ものヨーロッパの歴史をめぐる時空の冒険に出かけた14歳の少女ソフィーが、本当の自分を発見していくファンタスティック・ムービー。ノルウェーのオスカーにあたるアマンダ賞を三度受賞しているE・グスタヴソンが、深遠で哲学的なテーマを北欧映画特有の素朴で愛らしいテイストで、メルヘンタッチに仕上げている。", "1999-01-01", "36", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FLY", "蝿男の恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "物質転送装置の実験中に紛れ込んだ一匹の蝿が、科学者に悲劇をもたらした。両方の遺伝子が融合して、彼の頭は蝿と化したのだ。自分の頭と蝿の体を持つ分身蝿を捜し求めるうちに彼の人間としての意識も薄らいでいき、絶望の淵に追いやられた彼は……。蝿の複眼から見た映像が印象的。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHARLIE ＆ LOUISE／DAS DOPPELTE LOTTCHEN", "ふたりのロッテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "E・ケストナーの大ベストセラー小説の映画化。スコットランドの英語学校夏期コースに参加したことがきっかけで知り合ったシャーリーとルイーゼは瓜二つのうえに、誕生日も出身地もまったく同じ。しかし、それは偶然ではなかった、二人は本当の双子だったのだ。二人の両親は、10年前に離婚、子供はそれぞれが引き取ったのだ。家族の絆を取り戻すため、シャーリーとルイーゼは入れ替わって、相手の家庭へと行くのだった……。主役を演じるアイヒホーン姉妹は、540組の双子の中から選ばれ、みずみずしい演技を見せている。", "1994-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　IMMORAL　MR．　TEAS", "インモラル・ミスター・ティーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "ストリップ映画を手掛けていた異才R・メイヤー初の劇映画。歯科用器具のセールスマン、ティーズ氏は一見堅物だが、営業に行けば美人の歯科助手に、バーではマダムに、あらぬ妄想を抱いてしまう。さらに浜辺でグラマー美女の水着を脱がし、ウエイトレスを素っ裸にするなど、彼の妄想はエスカレートする。セリフを用いず、すべてメイヤーのナレーションで進行。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POETIC JUSTICE", "ポエティック・ジャスティス　愛するということ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "スーパースター、J・ジャクソンの初主演が話題となった、辛口の青春ドラマ。暴力沙汰で恋人を亡くした、詩を愛する美容師ジャスティス。旅に出た傷心の彼女は道中をともにするお調子者の青年と反目し合いながらも、次第に心を通わせていく。だが、旅の終わりには思わぬ悲劇が彼らを待ち受けていた……。ビデオタイトルは「ポエティック・ジャスティス」。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOOKER", "ルッカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "コンピュータによって、TV視聴者をコントロールする邪悪なシステム“ルッカー”。これを使って、世界制覇をたくらむ大企業と、ただ一人陰謀を暴こうとする男の息詰まる戦いを描く。人気作家クライトンの奇抜なアイデアと、先進的なCGシーンが魅力。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗殺の街 極道捜査線", "暗殺の街 極道捜査線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "警察内部の不祥事を調査する監察官、佐伯刑事はかつての上司、北上が殺された事件を捜査するため身分を隠して地元警察に乗り込む。北上の部下、寺岡は佐伯のことを煙たく思いながらも犯人逮捕のため彼に協力する。佐伯は北上が親しくしていた青東組に潜入し、汚職警官である小野田のことを突き止めるが、同時に北上の汚職の証拠も手に入れたのだった……。「女優霊」の中田秀夫によるハードボイルド映画。任務と友情のはざまで揺れ動く主人公の苦悩と葛藤を描き出す。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO WAY BACK／逃走遊戯", "NO WAY BACK／逃走遊戯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "豊川悦司がニューヨークのヤクザに扮する日米合作映画。FBI捜査官ザックは、マフィアの首領サラーノの息子を殺した重要容疑者としてニューヨークのヤクザ組織のボス、ユウジを逮捕する。しかし、サラーノは復讐のため、ザックの息子を誘拐し、代わりにユウジの引き渡しを要求してくる。ザックはFBIと連絡を絶ち、ユウジを連れてロサンゼルスへと向かうが、途中、ユウジがスチュワーデスを人質に取り、砂漠に飛行機を不時着させる。逃げるユウジをサラーノ、ザック、そしてFBIも追うのだった。ビデオ、DVDタイトルは「逃走遊戯／NO WAY BACK」。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIDEWALKS OF NEW YORK", "サイドウォーク・オブ・ニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「マクマレン兄弟」などの秀作を発表する一方、俳優としての活躍も目覚ましいエドワード・バーンズの監督作品。ニューヨークで生きる男女の恋愛模様を鮮やかに描く。結婚の約束までした女性にあっけなく捨てられたトミー。新しい恋を模索する彼は、街で出会ったマリアをデートに誘う。だがマリアは離婚歴があり、しばらくぶりの恋愛に怖気づいていた。しかも元夫は彼女に未練があって……。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gomorra", "ゴモラ", "", "", "http://image.eiga.k-img.com/images/movie/56130/315x210.jpg?1235", 135, "2008年・第61回カンヌ国際映画祭でグランプリを受賞し、同年のヨーロッパ映画賞では作品賞、監督賞ほか5冠を達成。イタリア南部ナポリを本拠に、かつてのマフィアをはるかに凌ぐ権力と経済力を誇る犯罪組織「カモッラ」の暗部を描き出した社会派ドラマ。組織同士の抗争が絶えない地域で育った少年トトはある日、度胸試しの儀式を経験して組織の一員となるが、友人のシモーネは対立するグループに入り、2人の友情は終わりを告げる。トトはあらゆる悪事に手を染めていくが、そんな中、組織から下されたある命令がトトの人生を大きく変えていく。", "2011-10-29", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラッフルズホテル", "ラッフルズホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "シンガポールに建ち、１世紀以上の歴史を誇るラッフルズホテル。そこに愛する男を捜すため、仕事を投げうってやって来た天才肌の女優・萌子。彼女とその男・狩谷の再会から二人の愛の行方を幻想的なシーンも織りまぜて描いたラブ・ストーリー。村上龍が６年ぶりにメガホンを取った監督第３作。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MAISON ASSASSINE´E", "ラ・メゾン 惨劇の館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ロートネルが久々に手掛けた、壮絶な復讐劇。天涯孤独の身の上である24歳の男が、ふとしたきっかけで自らの恐るべき出生の秘密を知る。24年前、何者かによって一家５人が惨殺されるという猟奇的殺人事件が起こった。その時、たった一人生き残った赤ん坊が彼だったのだ。血族たちの断ち難い面影を胸に、彼は真犯人への復讐を誓う。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シルクハットの大親分　ちょび髭の熊", "シルクハットの大親分　ちょび髭の熊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "四国一の大親分・熊虎の活躍を描く任侠シリーズ第２作。一家を引き連れて熱海の温泉にやって来た熊虎は、ひょんなことから天皇に献上する第１号自動車の運搬と警備を任せられるが、地元のヤクザたちの妨害により、自動車は爆破、雇い主は殺されてしまう。怒りの熊虎はお竜とともに敵陣へ殴り込む。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嘩！英雄／WHAT A HERO", "英雄烈伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "警察を舞台に、決して理想を忘れない熱血隊員の夢と希望、挫折と愛を描く青春ハードアクション。意気揚々と七分署第三隊に配属された華。しかし、そこはやる気ゼロの雨隊長率いる落ちこぼれ隊だった。一方の楊隊長率いる第七隊は全署No.1のエリートぞろい。華は、第三隊を七隊に負けないチームにしようと意気込む。華の過失で雨隊長が負傷したり、故郷の恋人からの結婚の知らせに帰郷してみれば、母親と同僚が特別な関係に陥っていたりと、悩み尽きない。そんななか、華は手柄を立てるが、それもずる賢い楊隊長の画策で彼らのものに。華の怒りは爆発し、楊隊長を倒すべく警視憲術大会に臨む。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thirteen Days", "13デイズ", "", "", "http://image.eiga.k-img.com/images/movie/1000/315x210.jpg?1943", 145, "1962年10月16日、米軍の偵察機がキューバに建設中のミサイル基地を発見した。しかも、この基地には核弾頭が持ち込まれているという。もしそれが事実であれば、アメリカ本土が核の危機にさらされることになる。ケネディ大統領は、アメリカ合衆国建国以来の、ひいては人類全体の未曾有の危機に立ち向かうべく、緊急危機管理チームを召集した……。", "2000-12-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("好男好女", "好男好女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「悲情城市」、「戯夢人生」に続く、ホウ・シャオシエン監督が描く“台湾現代史”3部作の完結編。舞台は現代の台北。女優であるリャンジンは、まもなくクランクインする映画「好男好女」で1940年代の抗日戦に参加した実在の女性を演じるのだが、やがて彼女は自分がヒロインと同一化していくのを感じ始めていた……。奥山和由プロデューサー率いるチーム奥山とホウ・シャオシエン電影社の共同製作による日・台合作映画。", "1995-01-01", "53,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOW GIRLS", "ショーガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "「氷の微笑」でエロティック・サスペンスというジャンルを超A級に昇格させたヴァーホーヴェン監督と、脚本家エスターハスのコンビで描いた衝撃の問題作。欲望や金、権力がうごめくラスベガスのショービジネス界を舞台に、一流のショーガールを夢見る女性の生き方を描く。ヒッチハイクでベガスに着いたとたんスーツケースを盗まれたノエミは、一流のショー“女神”で衣装係を務めるモリーに助けられる。ある日モリーと“女神”のショーを見たノエミは迫力のステージに圧倒され、トップスターのクリスタルを紹介してもらう。が、彼女はノエミが三流クラブ“チーター”のダンサーと知ると彼女をバカにし去っていく。数日後、チーターにクリスタルと恋人であり手腕ディレクターでもあるザックがやって来るが……。ヴァーホーヴェン監督が直感で選んだヒロイン役のE・バークレーが見事な肢体を惜し気もなくさらし熱演。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tron: Legacy", "トロン：レガシー", "", "", "http://image.eiga.k-img.com/images/movie/55210/315x210.jpg?4255", 0, "1982年に世界初のデジタル・コンピュータ・グラフィックスを導入したSFアドベンチャー「トロン」の3D版続編。「トロイ」「エラゴン」のギャレット・ヘドランドが主演を務め、その父を「クレイジー・ハート」のジェフ・ブリッジスが演じる。7歳の息子サムをひとり残し、デジタル界のカリスマ、ケビン・フリンがこつ然と姿を消す。20年後、サムは父から届いたメッセージに従い、あらゆる不完全性を排除した理想世界「トロン」へ向かう。そこで未知の敵と激戦を繰り広げながら、「トロン」に隠された秘密に迫る。", "2010-12-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・ガードマン 東京用心棒", "ザ・ガードマン 東京用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "人気TVドラマ『ザ・ガードマン』の映画化第１作。密輸ダイヤをめぐってザ・ガードマンと密輸組織の間に、陸・海・空を股にかけた大争奪戦が展開される。ヘリコプターまで駆使した派手なアクション・シーンを用意したが、シナリオの練りが足りず凡打に終わった。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Attack the Block", "アタック・ザ・ブロック", "", "", "http://image.eiga.k-img.com/images/movie/56960/315x210.jpg?3210", 88, "「ショーン・オブ・ザ・デッド」「ホット・ファズ　俺たちスーパーポリスメン！」のエドガー・ライトが製作総指揮を務め、ニック・フロストが出演。新人ジョー・コーニッシュ監督が手がけたSFパニックアクション。ロンドンの貧しい公共団地で荒んだ生活を送る不良少年たちは、毎晩騒いでは住民を困らせていた。そんなある日、街に凶暴なエイリアンが襲来し、不良少年たちは家族や友人を守り、街を救うために立ち上がる。", "2012-06-23", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("或る夜ふたたび", "或る夜ふたたび", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "椎名鱗三の短編『妻の失踪』を基に長谷部慶次が脚本を書き、五所平之助が演出した作品。失業した夫の代わりに働く妻が生活苦のために堕胎し、怒った夫も最後には理解するという物語で、同監督の「煙突の見える場所」を彷彿とさせる。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHRISTMAS　IN　CONNECTICUT", "アーノルド・シュワルツェネッガーのキッチン・ウォーズ彼女の恋は五ツ星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "A・シュワルツェネッガーが、念願の監督業に初めてチャレンジしたラブ・コメディ。彼自身は出演していない。TVの料理番組で人気を博す女性パーソナリティーのエリザベスは、孤独な生活を送っている。そんなある日、人命救助で英雄に祭り上げられたレスキュー隊員と共演した彼女は、彼に少しずつ惹かれていき……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREAT EXPECTATIONS", "大いなる遺産", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "ディケンズの小説を映画化した文芸作品。17世紀初頭のロンドン近郊。伯母夫婦に養われている孤児のヒップ少年は、近くの大きな屋敷に住む美しい少女エステラが唯一の心の友だった。そんな彼が20歳になった時、ある人物から莫大な遺産が与えられる……。", "1946-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIFUNES　SIDSTE　SANG", "ミフネ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "“ドグマ95”のルールに基づいてS・クラウ・ヤコブセン監督が製作した心温まるヒューマン・ドラマ。貧しい田舎者という過去を捨てた野心家のクレステンは、都会人を装い自分が勤める会社の社長令嬢との結婚で成功をつかもうとしていた。しかし、父が亡くなったことから田舎にいる知的障害者の兄の面倒をみることを余儀なくされる。足手まといの兄、ハウスキーパーの女性とその弟との奇妙な共同生活が始まり、クレステンが素直に人を愛する喜びに目覚めていく姿を描く。タイトルは、主人公が虚しい野心から解き放たれる糸口として描かれる、幼い頃の兄との思い出「七人の侍」の三船敏郎を真似た“ミフネごっこ”からきている。", "1999-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二十四の瞳", "二十四の瞳", "", "", "http://ecx.images-amazon.com/images/I/31g%2B9A8f9NL._SL210_.jpg", 155, "本作品はおそらく木下の作品歴のなかでも、その叙情的性格を最も色濃くうまく出しきった傑作。主人公の若き女教師・大石久子が小豆島の分教場に赴任した昭和３年から、終戦の翌年までの激動する18年間を年代記的に描いている。分教場の12人の生徒と大石先生の師弟愛が戦争や家族制度、貧困といったものに踏みにじられていく様子を、幾歳月を経ても変わらぬ、小豆島の美しい自然の中に対象的に映し出し、時代の悲劇を見事に見せている。女学校出のハイカラな大石先生は、赴任当初は村の人々から白い目で見られたが、次第に受け入れられるようになる。やがて時代の流れも12人の優しい教え子たちを離散させ、戦地で命を失う者も出る。終戦後、生き残った者が集まって、再び大石先生を囲む。“二十四の瞳”を演じる現地で募集した素人の兄弟姉妹と、20代から40代を演じる高峰秀子の演技が素晴らしい。DVDは「木下惠介　DVD-BOX 第1集」に収録。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヴァージン", "ヴァージン", "", "", "http://image.eiga.k-img.com/images/movie/57700/315x210.jpg?3439", 127, "ロストバージン（処女喪失）をテーマに、気鋭の監督3人がそれぞれ10代、20代、30代の女性のロストバージンを描いたオムニバス。姉の二葉にふられた同級生・市川とつきあう女子高生の翠が、二葉の部屋で市川とセックスしようと試みる姿を描く「くちばっか」（今泉力哉監督）、OLとして働きながら、相方のモテ女のリエとお笑い芸人を目指すブスで卑屈なアズサが、同僚から告白されたことで起こる変化を描いた「ゴージャス・プリンセス！」（福島拓哉監督）、何かに突き動かされることもなく淡々と35年間を生きてきた女性・和代が、突然現れた高校生の少年に恋をする姿を描いた「ふかくこの性を愛すべし」（吉田光希監督）の3本で構成される。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL GRANDE BLEK", "青春の形見", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1970年代、イタリアのある地方都市。主人公の青年は少年時代に別れを告げ、生まれ育った故郷を旅立とうとしていた。頭の中をめぐるのは優しい母と母を捨てた父、カーニバルの夜、初めて結ばれた恋人……。そんな彼の回想を通して描かれる青春物語。M・マストロヤンニの姪フェデリカが出演。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THREE CRAZY JERKSII", "マイケル・ウィンスローのタイム・トラベル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“ポリス・アカデミー”シリーズでおなじみ、人間効果音の異名をとるM・ウィンスロー主演のSFタッチのコメディ。2036年の特許庁に勤めるフランクとザビエルは頑固なボスと衝突ばかり。そこで二人は発明家が申請したタイムマシンを使って、ボスの誕生を阻止しようとする。", "1989-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT CALLER", "レイジング・シティ 野獣捜査線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "マイアミが舞台のハードアクション・ムービーで、P・フォンダが久々にアクション映画に挑戦している。犯罪組織を追うマックとジェイクの周辺で次々と殺人事件が発生。そして彼らもまた、謎の凶弾に襲われる。二つの点を結ぶものは何か？ そして別れたマックの妻ジェニファーが忽然と姿を消したことから、事態は急展開を見せる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花籠の歌", "花籠の歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "銀座の裏街に軒を構える１軒のトンカツ屋。店のご自慢はトンカツよりも看板娘。そんな彼女がある学生との間に恋の炎を燃やし始めて……。岩崎文隆の『豚と看板娘』を、小津作品の脚本でとりわけ著名な野田高梧と五所監督が脚色した作品。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KIPPUR", "キプールの記憶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "イスラエルの鬼才A・ギタイが自身の戦争体験をベースに綴った戦時下のドラマ。1973年、ヨム・キプール戦争（第四次中東戦争）が幕を開け、若いワインローブとルソは空軍の救急部隊に入る。最初は興奮していた彼らだが、傷ついた兵士を救えないことに対する無力感と疲労が募り……。ドキュメンタリー風の作りで戦場の内面的な苛酷さをリアルに伝える。ビデオ、DVDタイトルは「キプール　勝者なき戦場」。", "2000-01-01", "46,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名太鼓", "悪名太鼓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "清次は狼王会の菊沢にだまされて、朝吉の大事にしていた太鼓の胴に５億円の密輸品をつめて関西ルートに流す手伝いをするハメに。しかし真相を知った二人の活躍で、悪党一味は残らず逮捕される。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2STEPS！", "2STEPS！", "", "", "http://image.eiga.k-img.com/images/movie/54030/315x210.jpg?4265", 104, "ミュージカル「テニスの王子様」で人気を博した若手男優を中心に描く青春映画シリーズ“キラキラMOVIES”の第1弾。主演は「キズモモ」の古川雄大、舞台を中心に活躍する中河内雅貴。同じダンススタジオに通う隼人と巧は育ちも性格も正反対。ストリートダンススタイルの隼人と、バレエをアレンジした独自のスタイルで踊る巧はときに衝突するが、徐々にお互いの個性を認めていく。", "2009-01-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVING COUPLES", "ラヴィング・カップル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "結婚生活に疑問を抱き始めている中年の医師夫婦と、同棲中の若いカップル。この二組がふとしたことから知り合い、アバンチュールのつもりのひそやかな浮気がいつの間にか本気となり、パートナーが入れ替わってしまう。けれども、最後には……。なかなか豪華な顔ぶれが楽しめるラブ・コメディ。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("英雄無涙", "ソルジャー・ドッグス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "それまでのカンフーやコメディー映画路線からガラリと趣を変えた戦争映画。呉宇森作品初の銃撃戦が展開され、得意の二挺拳銃も早々と披露される。あまりに過激な描写ゆえに製作会社に長期間封印され、しばらくウーが香港での活動を許されなかったといういわくつきの作品。黄金の三角地帯を舞台に、麻薬組織撲滅のため戦いに命を懸ける男たちの激しくも哀しいドラマに圧倒される。", "1998-03-31", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELL’S HEROES", "戦場の野獣たち ヘルズ・ヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ベトナム戦争を題材にした娯楽戦争アクション。前線監獄から脱走した５人のアメリカ兵が、ジャングルの厳しい自然の中に仕掛けられた様々な“罠”をかいくぐりながら、ひたすら戦い続けるサバイバルを描いている。なかでも連続する爆破シーンは迫力いっぱいで見もの。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWORD OF THE VALIANT−THE LEGEND OF GAWAIN AND THE GREEN KNIGHT", "勇者の剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "会う人間に謎を持ちかけ、答えられないと命を奪ってしまうという亡霊グリーンナイト。若き騎士ガワインは生命を懸け真っ向から戦いを挑んでいく……。古代王国を舞台にS・コネリー扮する主人公が壮絶な死闘を繰り広げる冒険アクション。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN COLD BLOOD", "冷血", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "６年間に及ぶ調査を経て発表された、T・カポーティの同名実録小説の映画化。1959年の秋にカンザス州西部で起こった一家４人惨殺事件の背景を、刑事たちが丹念に追っていくという構成。余分なスリルやサスペンス描写がない代わりに、事実の重みだけが淡々と積み重ねられ、犯人の逮捕から死刑に至る部分では戦慄と哀れみが交錯する。S・ウィルソンが一世一代の名演。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マイブリッジの糸", "マイブリッジの糸", "", "", "http://image.eiga.k-img.com/images/movie/56910/315x210.jpg?4392", 12, "「頭山」（2002）で第75回アカデミー短編アニメーション賞にノミネートされた山村浩二の監督作。ギャロップする馬の連続動作を撮影するシステムを設計し、映画の発明にも大きな影響を与えた19世紀の写真家エドワード・マイブリッジの人生と、娘の成長を見守る現代の母親の物語を対比させることで、「時間」について思いをめぐらせる短編アニメーション。", "2011-09-17", "10,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四月の魚 Poisson d'avril", "四月の魚 Poisson d'avril", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "７回目の結婚記念日を迎えた売れない映画監督と女優夫婦の家に、かつて世話になった南の島の酋長がやって来た。南の島での最高の接待は、ワイフを一晩提供することだというのだが……。そこから始まるてんやわんやの家族騒動を描いたラブ・コメディ。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デコトラの鷲〈しゅう〉／会津・喜多方・人情街道！", "デコトラの鷲〈しゅう〉／会津・喜多方・人情街道！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "主演99作目となる哀川翔による平成版「トラック野郎」。デコトラ運転手の鷲一郎は、ライバル・トラッカー、天馬とのトラックレースの途中、孝作という青年と出会う。彼は喜多方の障害者施設に勤める姉を訪ねるため、ヒッチハイクを続けていた。鷲一郎はレースそっちのけで、孝作を姉のもとへ送り届けようとするのだが……。“デコトラ”とはトラックをド派手に飾ったデコレーション・トラックのこと。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゼロ年代全景", "ゼロ年代全景", "", "", "http://image.eiga.k-img.com/images/movie/54910/315x210.jpg?2164", 0, "2000年～2009年のゼロ年代と呼ばれる世代に登場した3人の新鋭若手監督によるオムニバス・ストーリー。同棲生活にも仕事にもうんざりした29歳派遣OLが繰り広げる子作り作戦を描いた星崎久美子監督の「茜さす部屋」、世界の終末が近づく中で生きる人々を綴った長久允監督の「FROG」、3年前に発生した監禁事件に関する雑誌記事を巡って出会った3人の男女の運命を描いた波多野純平監督の「ブーケガルニ」の3本で構成される。", "2009-12-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("るろうに剣心", "るろうに剣心", "", "", "http://image.eiga.k-img.com/images/movie/56790/315x210.jpg?1771", 0, "1994～99年に「週刊少年ジャンプ」（集英社）で連載され、テレビアニメ化もされて人気を博した和月伸宏の剣客漫画「るろうに剣心　明治剣客浪漫譚」を実写映画化。伝説の「人斬り抜刀斎」と名高い緋村剣心が明治維新以後、殺さずの誓いをたて、決して人を斬ることのできない「逆刃刀」を携えて町から町へ流浪の旅を続ける姿を描く。主人公・剣心役に佐藤健、ヒロイン・神谷薫役に武井咲。NHK大河ドラマ「龍馬伝」や「ハゲタカ」で知られる大友啓史監督がメガホンをとる。", "2012-08-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VIE ET RIEN D’AUTRE", "素顔の貴婦人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "B・タヴェルニエが第一次大戦後の北フランスを舞台に撮りあげた運命のラブ・ストーリー。1920年の北フランス。初老のデラプラーヌ陸軍少佐は、戦死者身元確認・行方不明者捜索局局長をしている。この地へ、パリの社交界でその名を知られた貴婦人イレーヌが夫の行方を捜しにやって来た。デラプラーヌはイレーヌに対し淡い恋心を抱くのだが、うまく表現することができない。そうしているうちに、イレーヌの捜す男に似た男を、田舎娘のアリスも捜しているということが判明する……。「恋するシャンソン」のS・アゼマがイレーヌ役を好演している。ビデオ発売時タイトルは「愛をもとめて　素顔の貴婦人」。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Between Strangers", "微笑みに出逢う街角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "イタリアの大女優ソフィア・ローレンが、息子エドアルド・ポンティの監督デビュー作で、記念すべき映画出演100作目を飾った。自分の居場所を求めてさまよい、葛藤を続ける世代の違う女性3人。過去と決別し、幸せへの第一歩を踏み出そうとする彼女らの人生が鮮やかに交錯していく。あきらめきれない夢、仕事と自分の価値観との相違、家族との接し方。3人の女性たちが、それぞれ悩みに答えを出そうとしていた。", "2005-03-12", "10,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Winchell", "ザ・ジャーナリスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "　1930～50年代に新聞、ラジオ、テレビで活躍したジャーナリスト、ウォルター・ウィンチェルの生涯を描く。1929年、ニューヨーク。ウォルターは新聞社でゴシップを織り込んだコラムを書き、人気を得る。やがてその才能に目をつけたラジオ局が彼の出演番組をスタートさせることに。個人でスタッフを雇い、スクープをモノにしていくウィンチェルはジャーナリストとして頂点に登りつめるが……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROME　HEARTS", "メタルアマゾネス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "女性だけの暴走族サイクル・ラッツは、安住の地を求め、ザリアーという町に流れ着く。そしてさっそく乗っ取りにかかるが、その町には世界征服を狙うマッド・サイエンティスト、ウィリアムがいた……。女暴走族とゾンビの戦いを描くアクション・ホラー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METALLICA：SOME　KIND　OF　MONSTER", "メタリカ：真実の瞬間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "へヴィメタ界の帝王、メタリカの3年間の活動を追ったドキュメンタリー。5年ぶりとなる新作アルバムのレコーディング過程を中心に、分裂寸前の状態から新たな道を見いだしていく男たちの葛藤と再生のドラマを、余すところなく映像化。偉大なバンドの裏側に隠されていた驚愕の真実に迫る。新作アルバムのレコーディングを開始したメタリカ。彼らは20年間の活動を経て、プロとして、一人の人間として転換期を迎えていた。", "2005-07-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HONEYSUCKLE ROSE", "忍冬〈すいかずら〉の花のように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "カントリー・ミュージックの大物シンガー、W・ネルソンの人柄と味のある歌唱を全面開花させた音楽映画。西部の町から町へ巡業するカントリー・シンガー、バックの愛と心意気をJ・シャッツバーグがソフトな温もりで描き出す。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STUART SAVES HIS FAMILY", "アル・フランケンは　Mr.ヘルプマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アメリカの人気番組『サタデー・ナイト・ライブ』の脚本家兼出演者のA・フランケンが、原作、脚本、主演の３役をこなすコメディ。テレビ番組でおバカな自分を素直に表わし、自信のない人を救済するスチュアート。ところが、陰険なオーナーのせいでクビになり、おまけに叔母の遺産トラブルに巻き込まれ、もうさんざん。さすがのスチュアートも落ち込んだが、持ち前の明るさで困難に立ち向かう。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEPHEN KING'S GRAVEYARD SHIFT", "スティーヴン・キング 地下室の悪夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "アメリカの田舎町に建つ古い繊維工場。その地下には巨大な迷路と基地が広がり、醜悪で狂暴なクリーチャーが、迷い込んだ人間を餌にしていた……。モダン・ホラー小説の旗手、S・キングが、作家活動初期に発表した同名短編小説の映画化。キング作品ならではの、超自然的恐怖ワールドが展開する。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プライド in ブルー", "プライド in ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "2006年8月にドイツで開催された知的障害者による“もうひとつのワールドカップ”、INAS-FID（国際知的障害者スポーツ連盟）サッカー選手権を題材にしたドキュメンタリー。それぞれの思いを胸にドイツへと渡った総勢20人の日本代表選手に密着。出場までの道のりと現地で繰り広げられた熱い戦い、そして彼らのその後を追う。監督は、「棒　Bastoni」の中村和彦。俳優の寺田農がナレーションを務める。", "2007-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK EAGLE", "ブラック・イーグル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "全米で忍者ブームを巻き起こしたS・コスギ主演のスパイ・アクション。敵役には肉体派J・C・ヴァン・ダム。地中海に墜落したアメリカの戦闘機から、レーザー誘導装置を盗み出したKGBのアンドレイ。CIAからその奪還命令を受けたケンが、彼との死闘を繰り広げる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香港之夜", "大混乱・ホンコンの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "田口浩正が情けないサラリーマンに扮するドタバタ・コメディ。香港支社で働く平凡なサラリーマンの三木拓郎は、未だに広東語を話せず冴えない日々を送っている。30歳の誕生日の夜、三木は自分へのプレゼントのためにアダルトビデオの女優と一夜を過ごすという計画を立てる。だが部屋にコンドームがないことから、とんでもない騒動を巻き起こしてしまう。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE TOUGH BASTARD", "ラストリベンジ　怒りの標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "陸軍軍曹のジョン・ノースは、ある日、警察隊が包囲する事件現場に遭遇する。そこで、自分の妻子がマフィアの武器横流しに巻き込まれたことを知り、単身救出に乗り込む。しかし、時すでに遅く、妻子は殺され自らも重傷を負ってしまう。２ヵ月後、復讐を誓ったジョンは、犯人の首筋に彫られたヴァンパイアの入れ墨だけを手掛かりに捜査を開始。やがてジョンは、マフィアのボスとFBIの悪徳麻薬捜査官の関係を探り出す。ラップミュージシャン、M.C.ハマーがマフィアのボスを演じている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dr.MABUSE DER SPIELER", "ドクトル・マブゼ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 245, "後世に様々な影響を与えることになった、F・ラング監督の犯罪映画。映画は第１部「大賭博師」と第２部「犯罪地獄」の２部からなっており、変装の名人にして催眠術を自由自在に操る怪人マブゼ博士が、ドイツ−スイス間の経済協定書を奪い、株式市場を大混乱に陥れ、自ら偽札を発行して莫大な利益を得ようと画策する様が描かれる。オリジナルはおよそ４時間半にもおよぶ壮大な作品で、主要なストーリー以外の細部への描き込みが恐ろしく緻密で、ラング得意の表現主義的な映像とあいまって、単なる犯罪映画とは言い切れない壮麗な作品となった。なお本作の続編的な作品として、「怪人マブゼ博士」（1932）そして「怪人マブゼ博士」（1960）がある。", "1922-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博徒百人", "博徒百人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "昭和の初期、九州一の大親分・村井磯吉は、若松の縄張りを江頭と郷田の二人に分け与えた。しかし、郷田は江頭を暗殺し、領地を我がものとする。江頭の息子・悟は、父の仇を討つことを誓い、郷田を追いつめていく。高橋英樹が復讐に燃える博徒を熱演する任侠映画。大親分役には嵐寛寿郎が扮している。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狐のくれた赤ん坊", "狐のくれた赤ん坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1945年に阪東妻三郎主演で作られた「狐の呉れた赤ん坊」のリメイク。純情な大井川の川越え人足がひょんなことから大名の捨て子を育てるハメになるという人情喜劇の快作。勝新太郎が粗野だがお人好しの人足を好演している。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alien", "エイリアン3", "", "", "http://ecx.images-amazon.com/images/I/414ao81EGaL._SL210_.jpg", 115, "凶暴な宇宙生命体と女性宇宙航海士リプリーの戦いを描く名作SF「エイリアン」シリーズ第3作。2270年、エイリアンの脅威から逃れたリプリーの乗る救命艇が囚人惑星フィオリーナに不時着。唯一の生存者だったリプリーは男ばかりの惑星に波紋を起こす。そんな中、エイリアンが惑星に紛れ込んでおり、囚人たちを次々と襲撃。武器のない不利な状況に加え、リプリーは自分の体がエイリアンに寄生されていたことを知る……。監督はミュージックビデオやCMを手がけてきた新人デビッド・フィンチャー。03年に30分長い「完全版」が発表。劇場公開版ではエイリアンが寄生した宿主は犬だったが、完全版では牛になるなど本来描かれる予定だったシーンなどが加えられている。", "1992-08-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本女侠伝・真赤な度胸花", "日本女侠伝・真赤な度胸花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "開拓期の北海道・札幌。博労総代の松尾兼之助は、馬市の利権をめぐって博徒の大野に射殺される。娘の雪は父の跡を継ぎ、五郎の助けを得て父の仇を討つ。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEVIL NIGHT〈デビルナイト〉", "DEVIL NIGHT〈デビルナイト〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "浅野忠信監督作品「トーリ」など新感覚の映像世界を提供する“DDDレーベル”の作品。今回は、注目のデザインチーム“デビルロボッツ”が放つアニメーション集。歌う長編アニメ「海賊ちゃん」や、親子愛を描いた「トーフ親子」など、可愛いけれど毒のある不思議なキャラクターがたまらない。宝島で蛮行を繰り広げる海賊ちゃんと弟ちゃんの兄弟舟。そんな中、パパ賊ちゃんとの親子愛が初めて明らかに……。(「海賊ちゃん」)", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大決戦！超ウルトラ8兄弟", "大決戦！超ウルトラ8兄弟", "", "", "http://image.eiga.k-img.com/images/movie/53550/315x210.jpg?4510", 0, "昭和の4大ウルトラマンと平成の4大ウルトラマンが時代と次元を超えて共演するウルトラマン映画の決定版。監督はこれまでに多数の「ウルトラマン」シリーズを手掛けてきた八木毅。ウルトラマンに憧れていたダイゴ、アスカ、我夢は、気付けばごく普通の日常を送る大人になっていた。そんなある日、横浜港に砂漠の蜃気楼が現れ、ダイゴだけがその中で横浜を守るため戦うウルトラマンの姿を見る。", "2008-09-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒木太郎の愛と冒険", "黒木太郎の愛と冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "気弱なくせに冒険好きでおせっかいな42歳の映画スタントマン、黒木太郎を主人公に、東京の片隅に生きる庶民群像の姿を力強く描いた秀作。都会の裏のゴミゴミした風景、風物のシーンにリアリティーがあり、極貧の夫婦の岡本喜八、杉本美樹が印象的。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("河内山宗俊", "河内山宗俊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "情婦の居酒屋でむだ飯を食って暮らすヒモ・河内山宗俊。守銭奴の顔役・森田屋一家の用心棒の金子市之丞。二人は日々をのんびりと気ままに過ごしていた。二人のマドンナは、若いながらけなげに駄菓子や甘酒を売って弟を養っているお浪という娘。そのお浪が、弟の不始末の三百両の金のために森田屋や女衒(ぜげん)の毒牙にかかり、女郎に売られようとする。宗俊と市之丞はお浪を救うために命を懸けて戦うのだった……。若くして死んだ天才監督・山中貞雄の、現在残されたたった３本の作品のうちの一つ。市民の情緒を確かな演出で描ききった秀作である。また若き日の原節子がお浪役で初々しい演技をみせるのも見どころ。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゾンビアス", "ゾンビアス", "", "", "http://image.eiga.k-img.com/images/movie/57180/315x210.jpg?2505", 85, "「片腕マシンガール」「富江アンリミテッド」「電人ザボーガー」などで知られる井口昇監督が手がけた異色ゾンビ映画で、恐ろしい寄生虫により誕生した排泄物まみれのゾンビ＝ウンデッドが、キャンプを楽しみにやってきた若い男女を襲う様子を描いたショッキングホラー。子役時代に出演したNHK教育「天才てれびくん」で知られ、現在は女優・モデルとして活動する中村有沙が、汚物まみれのゾンビと戦う女子高生役を熱演。", "2012-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("成吉思汗／GENGHIS KHAN", "ジンギス・カン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "中国から中央アジアまでのユーラシア大陸に広がる、モンゴル帝国の初代皇帝ジンギス・カンの、出生からモンゴル帝国建国までの半生を綴り、その愛と野望と戦いを空前のスケールで描くスペクタクル・ロマン大作。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帝国海軍勝利の記録", "帝国海軍勝利の記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "昭和16年12月、宣戦布告から真珠湾攻撃、香港攻略などの奇襲と、昭和17年５月までの総合戦果を知らしめるために製作された。現在残されている映像は、当時映画館などで上映されていた「日本ニュース」より再録編集されたもので、欠落部分などがあるものの、日本海軍の力、国民へのプロパガンダの姿勢を観ることができる。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LETHAL WEAPON2", "リーサル・ウェポン2 炎の約束", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "人気抜群のM・ギブソンが、過激なタフガイ刑事に扮し大活躍する、痛快刑事アクション第２作。息をもつかせぬスピーディな画面展開と、ユーモラスで切れ味の良い語り口が絶品。アクション映画の醍醐味を十二分に満喫させてくれる１本だ。ロス市警殺人課刑事リッグスと、相棒の黒人刑事マートフは、麻薬シンジケートの黒幕が、南アフリカの駐米大使と知る。外交特権をカサに、逆にリッグスらに脅しをかける大使一派。刑事仲間が一人また一人と殺されていく。そして、マートフもまた命を狙われ……。大使秘書役P・ケンジットが、男臭い画面に、程よく甘いムードを漂わせている。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUTHOR！ AUTHOR！", "喝采の陰で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "前妻の子と、二度目の妻の連れ子４人と生活する劇作家が悪戦苦闘しながらも親子の情愛を深めていく姿を、ペーソスたっぷりに描いた一編。当時、重い役の続いたA・パチーノが、優しく温かい父親像を演じているのに注目。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Mill and the Cross", "ブリューゲルの動く絵", "", "", "http://image.eiga.k-img.com/images/movie/57140/315x210.jpg?2501", 96, "「バベルの塔」「雪中の狩人」などで知られる16世紀フランドル絵画の巨匠ピーテル・ブリューゲルの作品の中に入り込み、絵画の世界を旅するかのような感覚を味わえる体感型アートムービー。ルトガー・ハウアー扮するブリューゲルを案内役に、ブリューゲルの絵画「十字架を担うキリスト」に描かれている人々の日常生活をなぞりながら、絵画に秘められた意味を解き明かしていく。監督は「バスキア」の原案・脚本を手がけ、アート界でも活躍するレフ・マイェフスキ。実写映像と絵画を融合させてブリューゲルの絵画世界を再現する表現手法が話題となり、ルーブル美術館でも特別上映された。", "2011-12-17", "42,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gloria", "グロリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "　3年の刑期を終えて出所したマフィア組織の女グロリアはニューヨークへ帰るが、かつての仲間に裏切られてしまう。彼女は組織に監禁されていた少年ニッキーを連れて逃走を図る。ニッキーの父親は会計士だったが、組織の秘密を収めたフロッピーを持ち出したために、家族とともに殺されていた。標的となったグロリアとニッキーに迫る追っ手。やがてニッキーは彼らの手に落ちてしまうが……。ジョン・カサベテス監督の同名作品をリメイク。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あしたが消える　どうして原発？", "あしたが消える　どうして原発？", "", "", "http://image.eiga.k-img.com/images/movie/56920/315x210.jpg?4406", 55, "チェルノブイリ原発事故から3年後の1989年、世界が原発への不安を抱くなか日本の福島第一原子力発電所を追って製作されたドキュメンタリー。福島第一原発の定期検査などに携わっていた52歳の父を骨ガンで亡くした仙台市の主婦による新聞投書をきっかけに、日本の原発で働く労働者、被ばくの危険性を明らかにしようと奮闘する医師、福島第一原発4号機の設計に携わった現サイエンスライターの田中光彦氏らの証言を集めた。福島第一原発が東日本大震災によりトラブルに見舞われた2011年、デジタルリマスター版で初の劇場公開。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人嚇鬼／HOCUS POCUS", "霊幻百鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "サモ・ハン・キンポー製作による“霊幻”ものの第３作で、ヒット作「霊幻道士」の前に作られたもの。ドサ回りの京劇の劇団に愉快なお化けが住みついた。成仏させてあげようと墓を掘ったものの、手違いで悪霊を呼び起こしてしまう……。DVD発売時タイトルは「霊幻百鬼 人嚇鬼」。", "1983-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅は青空", "旅は青空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 11, "片岡千恵蔵自らが主宰する千恵蔵プロの第１回トーキー作品。P.C.Lシステムによって録音されている。山六三郎、略して山六をはじめ、板万、ホロ権の三浪人と女旅芸人おろく一行との愛あり、涙ありの道中記。千恵蔵は、ほかのチャンバラ・スターとは違い、この手の明朗時代劇にも、イヤ味のない個性を見せた。現存部分は、第１巻のみ。", "1932-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラスト・キャバレー", "ラスト・キャバレー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "数日後に閉店してしまうキャバレーを舞台に、父娘の愛情、そしてそれぞれの自立を描く。ロマンポルノへのオマージュとして設定されたラストのパーティー・シーンは、祭りの猥雑さとその後の寂しさを漂わせ、にっかつファンにとっては感涙もの。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIAR", "ライアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "双子の新鋭監督ジョナス＆ジョシュのペイト兄弟が手掛けたサスペンス・ミステリー。娼婦惨殺事件の重要参考人としてウェイランドという富豪の御曹司が取調室に呼ばれる。担当の刑事は、ベテランのケネソウと経験の浅いブラクストン。二人はポリグラフ（嘘発見器）を用いて真相を探ろうとするが、IQ151の知能を誇るウェイランドは、逆に彼らを大混乱に陥れていく。登場人物３人が抱える嘘と秘密を、名カメラマン、B・バトラーの撮影による幻惑的な映像で描出。インディペンデント系作品を中心に活躍する実力派の曲者キャストによる迫真のアンサンブルも見ごたえありだ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("下妻物語", "下妻物語", "", "", "http://ecx.images-amazon.com/images/I/51hcARbyslL._SL210_.jpg", 102, "ロリータのカリスマと呼ばれる小説家、嶽本野ばらの同名小説を映画化。監督を務めたのは、サッポロ黒ラベルやNTT東日本などのCMで話題を集めたCM界の奇才、中島哲也。ぶっ飛んだ個性のロリータ少女とヤンキー少女の間に生まれた意外な熱い友情を、これまた意外にも感動的に描いた青春コメディだ。配役の上手さが濃いキャラを引き立てている。茨城県下妻に住むロリータ・ファッション命の桃子は、洋服を買う資金集めにバッタ物ブランド品販売を始めた。早速買いにきたのはヤンキー少女のイチゴ。理解し得ない彼女を見た桃子は、イチゴを遠ざけようとするのだが……。", "2004-05-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("柔道龍虎榜／THROW DOWN", "柔道龍虎房〈じゅうどうりゅうこぼう〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "香港の人気監督ジョニー・トーが、黒澤明監督のデビュー作「姿三四郎」にオマージュを捧げた異色の青春アクション。香港の裏社会を必死に生きる3人の若者の恋や夢を、激しくスタイリッシュな柔道の魅力を融合させて描いた痛快作だ。その昔、柔道界では最強と言われたシト・ポウだが、今ではしがない場末の酒場で雇われマスター兼バンドマンをする日々を過ごしていた。そんなある日、トニーという若者が現われ……。", "2006-05-13", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オオ！市民諸君", "オオ！市民諸君", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "横井福次郎のマンガを原作にして、当時軽演劇の喜劇王として人気のあった清水金一が主演、川島雄三が監督した喜劇。成金が無人島のつもりで買った島に、難破船から流れついた男女がいたことから起こる騒動。成金がやたら文化を口にするなど、文明批判も痛烈。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE MEN AND A LITTLE LADY", "スリーメン＆リトルレディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "男３人の子育て奮闘記「スリーメン＆ベビー」の続編。あれから５年、赤ちゃんのメアリーはリトル・レディに成長。ママは女優として成功し、３人の中のピーターは気がある様子。メアリーのためにも本当のパパが欲しいと願うママだが……。今回はリトル・レディのメアリーを間に置いた大人のラブ・ストーリーになっている。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPER　８　STORIES", "SUPER８", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "プロのミュージシャンとしても活躍するE・クストリッツァが、自身が参加するバルカン・バンド“ノー・スモーキング・オーケストラ”のヨーロッパ・ツアーを収めた音楽ロード・ムービー。移動の合間に行ったメンバーへのインタビューでは、幼い頃の音楽体験やバンドへの思いなどが浮き彫りになる。クストリッツァがギターを弾き、実の息子ストリボールがドラムを担当している。", "2001-01-01", "4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LETTRE A FREDDY BUACHE", "フレディ・ビアシュへの手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 12, "スイスのローザンヌ市創設500年記念映画として、市の発注でつくられた短篇映画。冒頭でゴダールがレコード針を落とすボレロに乗って、ローザンヌ市のスケッチが綴られていく。タイトルのフレディ・ビュアシュはローザンヌのシネマテークの館長として、ランググロワなき後、世界のシネマテークで最も信頼され親しまれた人。「カルメンという名の女」と併映で日本初公開された。", "1984-06-23", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒いダイスが俺を呼ぶ", "黒いダイスが俺を呼ぶ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "兄と恋人を賭博がもとで失った氷室浩次は、もう２度とダイスは握るまいと決心するが、ぶらりと立ち寄った街で、暴力団にイカサマバクチで金をむしりとられる市民を目にする。自らの誓いに悩みながら、見かねた氷室はついに立ち上がるのだった。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRESTARTER", "炎の少女チャーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "キングのベストセラーを映画化したSFスリラー。念力放火能力を持つ少女チャーリーを軍事利用しようと、政府機関“ショップ”が暗躍。両親を殺害し、少女を捕らえて実験室に送り込むが、彼女の怒りは頂点に達していた……。ラストの超能力スペクタクル、炎の中に立つバリモアの形相が圧巻。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWING MAN", "SWING MAN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "第32回文芸賞優秀賞を受賞した池内広明の原作をもとに描く異色サスペンス。売れない俳優のほづみは、金属バットで何者かに襲われ大怪我を負う。殴られた当時の記憶が欠落してしまった彼は犯人を探し出そうと目撃者に会うことにするが、有力な情報は得られなかった。そんなある日、ほづみは６人目の目撃者、双美に出会う。彼女は特殊な能力を持っていて……。日本映画界のバイプレーヤーとして貴重な存在の木下ほうかが初主演を果たした。役所広司が本人役で出演している他、つぼを得たキャスティングに注目。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Edge of Eternity", "グランド・キャニオンの対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "　アリゾナ州の保安官レスはグランド・キャニオンの一帯で起こった三件の殺人事件の捜査を行なう。鉱業会社・前社長の娘ジャニースの協力で、やがて被害者の身元が判明。一方、ジャニースの弟ボブやバーの店主にも不審なところがあった。はたして犯人は？　クライマックスには、渓谷で宙吊りのまま激闘が繰り広げられる。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博奕打ち 不死身の勝負", "博奕打ち 不死身の勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "基本ラインは賭博を通じての戦いだが、毎回趣向を変える任侠シリーズ。東映のプログラム・ピクチャーで鶴田浩二が見事な任侠男っぷりを見せ付けてくれる。今回は昭和初期の九州の筑豊炭田を舞台に、鶴田はバクチ名人の常太郎に扮し男の意地を懸けて対決。“爆弾常”の異名を取る常太郎の登場場面から、グッとドラマの中へと引き込まれていく。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Santa Clause3 : The Escape Clause", "ウォルト・ディズニーのサンタクローズ3 クリスマス大決戦！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "サンタクロースの活躍を描くファンタジック・コメディのシリーズ第3弾。ひょんなことからサンタクロースとなった元サラリーマンのスコットは、ミセス・サンタと共に世界中の子どもたちにプレゼントを贈り続けていた。しかし、人々から愛されるサンタクロースに嫉妬したジャック・フロストが、タイムマシンを使ってスコットからその座を奪ってしまう。前2作に引き続き、サンタクロース役に人気コメディ俳優のティム・アレンが扮する。", "2006-12-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギターを持った渡り鳥", "ギターを持った渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "函館にギターを背中に流れてきた“渡り鳥”滝伸次は、腕を見込まれて土地の親分に雇われるが、密輸取引きの船上で殺されそうになり、伸次の怒りの拳銃が火を吹く。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APRE｀S　LA　RE´CONCILIATION", "そして愛に至る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "ミエヴィルが女友達と言葉や欲望、人生について議論しているところにミエヴィルの夫のロベールが加わる。ミエヴィルがワインを買いに出た隙に女友達はロベールを誘惑するが失敗。そこに、ミエヴィルがアチュールという航海士を連れてきて……。ウイットに富んだ大人のための会話劇。当初、予定していた女優が直前で出演できなくなったため監督のミエヴィルがこの役を請け負うことに。奇しくも、公私ともにかけがえのないパートナーであるジャン・リュック・ゴダールと劇映画初共演作となった。", "2000-01-01", "26,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nancy Drew", "美少女探偵ナンシー・ドリュー", "", "", "http://ecx.images-amazon.com/images/I/51VmRcmlD3L._SL210_.jpg", 0, "", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("任逍遥", "青の稲妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "前作「プラットホーム」で80年代の中国の若者たちをビビッドに描いて高い評価を得たジャ・ジャンクー監督が、再び中国の地方都市を舞台に、現代の若者たちの不安や焦燥を等身大で見せる青春ドラマ。2001年の中国山西省・大同（ダートン）。経済的、国際的にも急成長を遂げた現代中国の地方都市で、期待と恐れ、社会への矛盾とを抱え苦悩する2組の若者が、未来に立ちはだかる大きな壁に立ち向かう姿を描く。「好きなように生きる」という歌詞が受け、大ヒットした台湾の曲「任逍遥」が劇中印象的に使われる。第55回カンヌ国際映画祭正式招待作品。", "2003-02-01", "8,76,5,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUGARFOOT", "グレートロックの決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "西部劇俳優R・スコットの主演作。騎兵隊を除隊後、アリゾナで商売をすることになったシュガーフット。酒場の女リバーを助けたことから恨みを買い、彼は悪党どもからつけ狙われるハメになる。最後の最後で勝利を収めるヒーロー、迫力満点の早撃ちシーンと、正当派西部劇の王道を行く一編。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新諸国物語 黄金孔雀城 第１部", "新諸国物語 黄金孔雀城 第１部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "北村寿夫のNHK連続放送劇を映画化したシリーズ。今から450年前の昔・琉球の島に黄金の孔雀城と呼ばれる王城があったが、平村杢内の裏切りで一夜にして滅んだ。20年の歳月が流れ、山城の国の代官の息子・左近は役人になり、不幸な人々を救おうと都へ上る。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("るろうに剣心・維新志士への鎮魂歌", "るろうに剣心・維新志士への鎮魂歌", "", "", "http://ecx.images-amazon.com/images/I/51EgKMOK4yL._SL210_.jpg", 90, "『週間少年ジャンプ』に連載されたコミックを原作にした、人気TVアニメの劇場版。新時代・明治の世で、腰の逆刃刀に不殺を誓った流浪人（るろうに)・剣心は、凄腕の剣士・時雨に出会う。やがて二人の過去にまつわる因縁が甦ったとき、最強の刃の壮絶なぶつかり合いが繰り広げられる。", "1997-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SONGCATCHER", "歌追い人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "英国の実力派女優ジャネット・マクティア主演による感動作。アパラチア山脈で土地に根づいた“歌”の掘り起こしに邁進する女性音楽学者の喜びと哀しみを、雄大なタッチで紡ぎ出す。全編を彩る伝統音楽の数々は新鮮かつ余韻たっぷり。イーストウッドの「ミスティック・リバー」にも起用された新鋭エミー・ロッサムの美しい歌声にも注目したい。1907年、音楽学者リリーはノースカロライナ州の奥深くにあるアパラチアに向かった。そこには、200年以上前のアイリッシュ移民の伝統歌がそのまま伝承されていた。リリーは“収集”を始め、地元の人々とも交流を深めていくが……。ビデオ、DVDタイトルは「Songcatcher 歌追い人」。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cronicas", "タブロイド", "", "", "http://image.eiga.k-img.com/images/movie/1190/315x210.jpg?1812", 98, "子供ばかりを狙う連続殺人鬼“モンスター”を追うTVの人気レポーター、マロノ。彼は「誰も知らないモンスターの情報を話す」という囚人ビニシオを取材し、その代償に彼が無罪であると主張する番組を作るという取引きをする。が、マロノはビニシオを取材する中で、ビニシオ自身がモンスターなのではないかと疑うようになる。製作は「天国の口、終りの楽園。」「ハリー・ポッターとアズカバンの囚人」のアルフォンソ・キュアロン。", "2006-01-21", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shoot the Moon", "シュート・ザ・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "「ミッドナイト・エクスプレス」「フェーム」のアラン・パーカー監督が、アルバート・フィニーとダイアン・キートンを主演に迎えて贈るシリアスな家族ドラマ。結婚15年目のダンロップ夫妻は近頃うまくいかず、お互いの愛は完全に冷め切っていた。夫ジョージにはすでに愛人もおり、ついに彼は家を出ることに。両親の破局を目の当たりにした4人の娘たちはそれぞれ思い悩む。やがて妻フェイスもある男性と親密な関係になり……。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORDER RADIO", "ボーダー・レディオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "失踪したミュージシャンを探して仲間を訪ね歩く、その妻ルアンナ。彼女が旅で出会う奇妙な人々の人間模様を、淡々としたタッチで描く。L.A.パンク・シーンのロッカーたちが出演したオフビート・コメディ。カルト的なバンド“グリーン・オン・レッド”もクラブ・バンドとして出演し、演奏を聴かせる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SON　OF　THE　MASK", "マスク２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ジム・キャリーをスターに押し上げた大ヒット・コメディの10年ぶりの続編。傍若無人の怪人マスクに、なんと子供が誕生。スーパー・パワーを持つマスク・ベイビーが繰り広げる騒動を、アニメやSFXを駆使したパワフルな映像で描き出す。愛犬が拾ってきた奇妙なマスクを着け、マジカル・パワーを持つ怪人に変身した漫画家ティム。だが、彼の子供にもパワーが乗り移り……。", "2005-04-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD THING", "吐きだめのヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "両親を殺された子供が、大都会の片隅で浮浪者の女に育てられ“野生児”となる現代版ターザン。麻薬と暴力に冒された都会のジャングルで、彼は女性ソーシャル・ワーカーを助け、親の仇を討つ。J・セイルズ脚本による一味違うアクション・ファンタジー。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING OF GRIZZLIES", "灰色ぐまの一生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "『シートン動物記』からの同名作品の映画化。先祖からの言い伝え通りに、自分が熊と血縁関係にあると信じているインディアンの青年、モキ。彼は狩りの途中で出会った４本指の灰色熊を逃がしてやった。数年後、モキが他の熊に襲われた時、灰色熊が現れ……。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("G.I.Jane", "G.I.ジェーン", "", "", "http://ecx.images-amazon.com/images/I/513JzcbEcYL._SL210_.jpg", 126, "　男女雇用差別撤廃法案の成立を目論む女性議員は海軍情報部に所属する女性大尉オニールを精鋭部隊SEALの訓練テストに送り込む。鬼教官のもと、オニールは過酷なテストをクリアするが、それが特別扱いだったことを知り、抗議。彼女は自らの髪を切り落とし、男性と同等の試練に耐え、やがて同僚たちから仲間として迎えられる。そんな訓練生たちが実戦に参加することになるが……。厳しい訓練、そして差別やスキャンダルに立ち向かうヒロインの姿を描いたミリタリー・ドラマ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thelma, Louise et Chantal", "テルマ、ルイーズとシャンタル（原題）", "", "", "http://image.eiga.k-img.com/images/movie/55350/315x210.jpg?2543", 90, "「ナック」「欲望」の名女優ジェーン・バーキンが主演するロードムービー。50代の仲良し女性3人組、ガブリエルとネリーとシャンタルは、昔の恋人の結婚式に出席するため、フランス西部の都市ラ・ロシェルへ行くことになる。その道すがら、一緒に小旅行をすることにした3人は、恋や人生についておかしく語りながら、波乱に満ちた旅路を行く。", "2009-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOWTIME", "ショウタイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ロサンゼルス市警の堅物刑事ミッチはTVの捜査実況番組『ショウタイム』の主役としてカメラに追われるハメに。しかも、相棒は俳優志望の軽薄な警官トレイ。渋々テレビに出演するミッチだったが、視聴率は急上昇、お茶の間の人気者になってしまう。そんな中、ミッチとトレイは麻薬ディーラーを追うが、相手は家一軒を一瞬に消し去る大型特殊マシンガンで仕返しをしてくる……。デ・ニーロ、E・マーフィの異色の顔合わせによるアクション・エンターテイメント。「ビバリーヒルズ・コップ」を思わせるE・マーフィのノリノリの演技と、終始しかめっ面のデ・ニーロの表情の対比が笑いを誘う。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLUCHT IN DEN NORDEN", "愛の逃避行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "反ファシズム運動に揺れる1930年代初頭のドイツ。若く美しいレジスタンスの女闘士ヨハンナはドイツを逃れ、恋人ブルノーの待つフランスへ行くことを決意。しかし、旅先でフィンランドの貴公子ラグナーと知り合ったことから……。", "1985-01-01", "7,20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ここに、幸あり", "ここに、幸あり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「いつものように」で日本映画プロデューサー協会最優秀新人監督賞を受賞した、けんもち聡監督による作品。九州の小さな離島を訪れた売れない役者・幸と、水着の撮影でやってきたモデルの成美。そんな彼らを待っていたのは、民宿を切り盛りする浪人生・邦との出会いだった。男二人、女一人の離島での奇妙な共同生活が始まり、島民の邦を通して大切なものを取り戻す。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("荒川の佐吉・遊侠夫婦笠", "荒川の佐吉・遊侠夫婦笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "真山青果の名作戯曲を映画化した股旅もの。ヤクザの世界に飛びこんだ“大工の佐吉”が、持ち前の意地と度胸で、見事に親分の仇を討つまでを描いたもの。この年、低迷していた高田浩吉は、主役として人気を挽回、“奇蹟のカムバック”と言われた。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hunting Party", "ハンティング・パーティ", "", "", "http://image.eiga.k-img.com/images/movie/53410/315x210.jpg?2357", 103, "紛争終結から5年後のサラエボで再会した元戦場リポーターのサイモンとカメラマンのダック。紛争取材中に大失態を演じてから行方をくらましていたサイモンは、ダックに重要戦争犯罪人フォックスについての情報を持ちかける。新米プロデューサーのベンも加わり、3人でフォックスの行方を追うことにした彼らだったが……。米Esquire誌に掲載された衝撃の実話をベースに、リチャード・ギア、テレンス・ハワード共演で放つ社会派サスペンス。", "2008-05-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("It's Complicated", "恋するベーカリー", "", "", "http://image.eiga.k-img.com/images/movie/55090/315x210.jpg?4741", 120, "「ホリデイ」「恋愛適齢期」「ハート・オブ・ウーマン」のナンシー・マイヤーズ監督による恋愛コメディ。3人の子供を立派に育て上げたシングルマザーのジェーンは、サンタ・バーバラの有名ベーカリーの経営者。仕事もプライベートも充実しているが、なにか心が満たされない日々を送っている。そんなある時、ジェーンは10年前に別れた元夫の弁護士ジェイクと再会し……。主人公ジェーンにメリル・ストリープ、共演にアレック・ボールドウィン、スティーブ・マーティン。", "2010-02-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD ＆ BURIED", "ゾンゲリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "監督に「ザ・モンスター」のG・A・シャーマン、脚本に「エイリアン」のD・オバノン、そして特殊メイクに「ターミネーター」のS・ウィンストンという、通好みのスタッフが手掛けたゾンビものの怪作。テキサスの田舎町の保安官が、ある殺人事件を追及。検死官にして葬儀屋の男が、死体の美化蘇生に見入られ、殺しては墓をあばきゾンビに仕立てていることが判明する。だが時すでに遅く……。眼球を刺す注射針、鼻から注入した硫酸で溶ける顔など、残酷ボルテージも高いが、とりわけ演出力、構成力は、同類作品とは一線を画している。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Y’AURA　T’IL　DE　LA　NEIGE　A｀　NOE¨L？", "クリスマスに雪はふるの？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ポンヌフの恋人」で美術アシスタントを務め、レオス・カラックスの勧めで脚本執筆を始めたというS・ヴェッセが、才能を開花させた監督デビュー作。南仏プロヴァンスで、農業を営んでいる母と７人の子供たち。父親は別に家庭＝“本宅”を持っている。映画は、夏、秋、冬へとこの一家に訪れる変化を、静かに深く追っていく。夏、子供たちがパパに怒鳴り散らされながら働いている。それでも、なんとかパパとうまくやっているママ。秋、夏休みが終わり、子供たちは学校へ。しかし、次第に雲行きが怪しくなっていく。冬、身も心も吹き荒ぶ日、運命のクリスマスを迎える……。劇的な描写を抑えた展開ながら、味わいは格別。悲痛な物語なのに、終始柔らかな感触を放っているのは、“リアリズム”と“おとぎ話”が融合したような独特の演出タッチのたまもの。音と映像の力だけで仕上げている点も驚嘆に値する。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEN GODA VILJAN", "愛の風景", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 180, "名匠I・ベルイマンが自身の両親をモデルに執筆した脚本を、B・アウグストの演出によって映画化。神学校に通う貧しい青年ヘンリクは、良家の令嬢アンナと恋に落ちる。アンナの親の反対や、ヘンリクの昔の恋人とのしがらみもあって、彼らの恋はなかなか成就しない。皮肉な運命にほんろうされながらも、ようやく結婚したヘンリクとアンナ。育った環境の違いから、二人はしばしば言い争うが、少しずつ理解を深めていく。流麗なタッチでよどみなく、３時間に及ぶ恋のドラマを綴ったアウグストの手腕は見事。「ペレ」に続いて、２度目のカンヌの最高賞に輝く作品となった。", "1992-01-01", "14,18,2,3,7,4,36,20,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUDITH", "栄光の丘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1947年のパレスチナ分割による動乱のイスラエルを舞台にした恋物語。出世のために幼い息子をナチに密告、自分を将校慰安所に送り込んだ夫グスタフに復讐するため、不法入国の移民としてパレスチナにやってきた女、ジュディス。そこにはこれまたグスタフを探す、イスラエル建国に情熱を傾ける男アーロンがおり、ジュディスは彼に魅力を感じる。", "1965-01-01", "1,3,46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぜったい多数", "ぜったい多数", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "うたごえ喫茶“仲間”の常連・暁子は、ボーイとして働く早大生の秋山と知り合う。若者たちは“仲間”を舞台に青春を謳歌していたが、ある日秋山は胃ガンの診断を受け、長くて１年の命と宣告される……。曽野綾子の同名小説を映画化した青春映画。当時の風俗がふんだんに取り入れられており、興味深い。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANCE OF DEATH", "死のダンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "フランケンシュタインの怪物役で有名なB・カーロフ主演のホラー・サスペンス。老紳士マチアスは財産相続のため一族を呼び集めた。彼の一族は昔、王様や王子を喜ばせるため、人間そっくりの機械人形を作ることで巨富を築いてきたのだった。だが同時に、それらは、人間を殺す人形でもあった。遺産相続人たちは次々と何者かに狙われ、姿を消していく……。", "1968-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROADRACERS", "ロードレーサーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1950年代アメリカの田舎町が舞台のR＆R青春ドラマ。デュードは車とケンカに明け暮れ、保安官に追いかけられる毎日を送っていた。そんな彼に恋人のドナは、バンドのオーディションに出演することを勧める。デュードはオーディションへの出場を決意するが、彼を目の敵にしている保安官の息子に決闘を申し込まれる。自分の将来をとるか、男としてのプライドをとるか。デュードが選んだ道は……。TVフィーチャー作品。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAVING　PRIVATE　RYAN", "プライベート・ライアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 170, "「シンドラーのリスト」で戦時下における人間の尊厳をアーティスティックに描いたS・スピルバーグが、観る者に戦争の恐ろしさを疑似体験させる衝撃的作品。1944年、ノルマンディ上陸作戦をからくも生き延びたアメリカ軍のミラー大尉に、“行方不明のライアン二等兵を探し出せ”との緊急指令が下る。７人の精鋭を引き連れて出発したミラーだったが、途中ドイツ軍の攻撃で死者が出たため、兵士たちの間にこの過酷な任務への不満がくすぶり始める……。ノルマンディ上陸作戦における最激戦地オマハビーチの惨劇を再現した冒頭30分間のシークエンスは圧倒的。スピルバーグ作品としては珍しくハンディカメラを多用し、ニュースリールのように色の彩度を抑え、無惨な肉塊と化す兵士の姿や血に染まる浜辺を映し出す。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぬくぬくの木", "ぬくぬくの木", "", "", "http://image.eiga.k-img.com/images/movie/57440/315x210.jpg?4249", 15, "無名の若手監督たちが作り上げた15分の短編10作品を特集上映する「NO NAME FILMS」の1本。小さな神社のひとり娘で、巫女として神社の仕事を手伝っているはりこ。その神社は、思い出のつまったぬいぐるみを木に結び供養することでも知られているが、ある日、ボストンバッグに入れられた赤ちゃんが置き去りにされており……。監督は、第32回ぴあフィルムフェスティバル準グランプリ受賞作「くらげくん」の片岡翔。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蟹工船（2009）", "蟹工船（2009）", "", "", "http://image.eiga.k-img.com/images/movie/54230/315x210.jpg?3518", 0, "2007年から08年にかけて人気が再燃し、話題となった小林多喜二による1929年のプロレタリア文学の名作を、「弾丸ランナー」「疾走」のSABUが映画化。出稼ぎ労働者達が蟹缶の加工を行う船・蟹工船で、劣悪な環境と低賃金に疑問を抱いた新庄は、労働者たちを蜂起させ、非道な監督の浅川に立ち向かう。松田龍平と西島秀俊が激しく対立する労働者と権力者に扮する。", "2009-07-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pippi on the Run", "続・長くつ下のピッピ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「ロッタちゃん」シリーズでおなじみのアストリッド・リンドグレーンによる原作を基に、赤いおさげとそばかすがトレードマークの“世界一強い女の子”ピッピが仲間たちと繰り広げる大冒険を描く。お母さんに叱られて、ついに家出を決意したトミーとアニカ。彼らの隣家“ごたごた荘”で暮らすピッピも一緒に行くことになる。途中で会ったコンラッドさんから、世界一強力なノリをもらった3人は、楽しい家出旅行を続けるが……。", "1970-01-01", "14,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PORTRAITS　CHINOIS", "恋人たちのポートレート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "監督デビュー作「ミナ」で二人の女の子の数年間に渡る軌跡を痛切に描いたM・デュゴウソンが、一転して複数の男女の人間関係をユーモアたっぷりに綴ったラブ・ストーリー。有名デザイナーのメゾンで働くアダ、彼女の恋人で映画脚本家のポール、アダに憧れる娘リーズ、ポールの脚本を映画化する監督のイヴなど、微妙に絡み合う９人ものキャラクターが愛と友情のドラマを織り成していく。英・伊・仏・ユーゴスラビアから集まったキャストの軽妙な掛け合いや、映画への愛がこもった幸福感あふれるラスト・シーンも見どころ。", "1996-01-01", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fighting Mad", "怒りの山河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "　トムは5歳になる息子を連れて、父が暮らすアーカンソーの農場に帰る。しかしその地は悪徳政治家と結託する企業家クラブトリーの土地買い占めによって、すっかり変わり果てていた。そんな中、クラブトリーを相手に賠償訴訟を起こしていたトムの弟とその妻が死亡するという事件が。土地の明け渡しを迫るクラブトリーの嫌がらせは続き、その後、トムの父親も命を落とす。そしてついにトムの怒りが爆発する！　ピーター・フォンダが復讐に燃える男を熱演するサスペンス。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("掌の小説", "掌の小説", "", "", "http://image.eiga.k-img.com/images/movie/54990/315x210.jpg?4627", 80, "文豪・川端康成が長年に渡って書き溜めた122編からなる掌編小説集「掌の小説」を原作とする4編のオムニバス映画。「アコークロー」で注目を集めた岸本司、「Lost＆Found」でオースティン映画祭グランプリに輝いた三宅伸行、「美式天然」でトリノ映画祭グランプリを獲得した坪川拓史、ドキュメンタリーやCMなど多方面で活躍する高橋雄弥ら4人の気鋭監督が集結。出演は吹越満、香椎由宇、福士誠治ほか。2009年の第22回東京国際映画祭「日本映画・ある視点」部門にて上映後、2010年劇場公開。", "2010-03-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Straight to Hell", "ストレート・トゥ・ヘル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "パンク・スピリット溢れる映画監督アレックス・コックスが放つ、マカロニウエスタン風パンクムービー。銀行強盗で大金を手に入れた3人の殺し屋と1人の女。ところがその逃走中、砂漠のど真ん中で車が立ち往生してしまう。やがてエル・ブランコという町にたどり着いた彼ら。しかしそこは、血の気の多いマクマホン一家が牛耳る地獄の町だった……。ジョー・ストラマー、エルビス・コステロ、ザ・ポーグス等豪華キャストが集結。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大殺陣", "大殺陣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "一連の東映集団時代劇の一本。時は四代将軍・家綱の頃。将軍危篤に乗じて、大老・酒井は五代将軍に自分の息のかかった甲府宰相・綱重を立てて、天下を我がものにしようとする。荒んだ政道に憤りを感じた軍学者・山鹿素行は一党を組んで、綱重暗殺計画を企てる。1960年代初期の東映は、安保闘争の影響を受けて、それまでの白塗りの時代劇から一変し、テロを扱ったリアルな集団時代劇を量産した。この作品もそうした作品の１本で、監督の工藤栄一と脚本の池上金男は、前作「十三人の刺客」でもコンビを組んだ仲だ。ギラギラした太陽の下で、泥と血にまみれた生々しい殺陣が繰り広げられる。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　よみがえれ　バナナ島", "それいけ！アンパンマン　よみがえれ　バナナ島", "", "", "http://image.eiga.k-img.com/images/movie/57900/315x210.jpg?4263", 0, "やなせたかし原作の国民的アニメーション「それいけ！アンパンマン」の劇場版第24作。美味しいバナナにあふれた南の国のバナナ島で開かれるバナナ祭りに招待されたアンパンマン。しかし、島を訪れてみると寒さでバナナはすべて枯れてしまっていた。変わり果てたバナナ島を救うためアンパンマンと仲間たちはバナナ山に向かうが……。元気いっぱいだがちょっとワガママな島の女王バンナの声優に、女優の木村佳乃。島の住民スッテンとコロリン役にお笑いコンビ「バナナマン」の設楽統と日村勇紀。", "2012-07-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH SOCIETY", "上流社会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "デクスターは、隣に住む前夫人、トレイシーにまだ未練をたっぷりもっていた……。「フィラデルフィア物語」のリメイクだが、有名ミュージシャンの競演と、とりわけこれがハリウッド最後の主演作品となったケリーの優雅な美貌が見逃せない。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("荒ぶる魂たち", "荒ぶる魂たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "三池崇史が、全編照明なしの撮影で正統派のヤクザ群像劇に挑んだ、見ごたえ満点の傑作。総長暗殺に始まる二大組織の軋れき、それを利用した謀略の進行、それに伴って殺された兄貴分の復讐に燃える主人公といった展開は、これまでのヤクザ映画が何度も繰り返してきたルーティンそのもの。ドキュメンタリー的撮影手法により硬質なダイナミズムが密閉されているものの、映像が自己主張する瞬間は皆無に等しく、ひたすら出演者たちの俳優としての華が際立っている。潔い激情を画面に刻みつける加藤雅也、抑えた風情に凄味をたたえた竹中直人、凄腕の殺し屋に扮して軽やかな魅力を発散する白竜、すべての悪玉たるフィクサーを演じて孤高の存在感をアピールする松方弘樹ら、究極の適材適所ともいうべきキャスティングが素晴らしい。タンゴ調の音楽も、男たちの艶と陰影を引き立てて秀逸。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wild", "ライアンを探せ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "動物園からさらわれてしまった子ライオンを救うため、父ライオンと仲間の動物たちが奮闘する姿を描いたCGアニメーション。ニューヨーク動物園で人気を集めるライオンのサムソンとその息子ライアン。ある日、何者かによってライアンが動物園の外へ連れ去られてしまう。サムソンと仲間たちはニューヨークの街へ飛び出し、ライアンの行方を追うが……。サムソンの声を演じるのは、人気ドラマ「24」のキーファー・サザーランド。", "2006-12-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビートキッズ", "ビートキッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "大阪を舞台に、ドラムのビートに魅せられてゆく高校生の音楽への情熱を描いた青春ドラマ。幼いころから、だんじり祭りの太鼓を体感し続けていた主人公が、ブラスバンド、さらにはロックバンドにのめりこんでいく。そんな青春の風景を、風変わりな少女への淡い思い、仲間との友情をからめて展開させる。大阪出身で現役の高校生からなる新進バンド、HUNGRY DAYSが主演を務め、音楽に懸けた青春をリアルに、切なく好演。彼らの音楽はもちろん、だんじり祭りの再現もパワフルで熱気満点だ。転校先でブラスバンド部に入部させられた高校生エージ。バンドを率いる学ラン姿の不思議な女生徒ナナオとの出会いによって音楽に目覚めた彼は、やがてロックバンドに加入するが……。", "2005-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WIFE　VS　SECRETARY", "妻と女秘書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「アンナ・クリスティ」「緑園の天使」などの名監督C・ブラウンが、洒落たタッチで仕上げたロマンティック・コメディ。出版社社長の妻が、夫の美人秘書に嫉妬したために思わぬ騒動が持ち上がるという物語。デビュー間もないJ・スチュアートが、J・ハーローとのキス・シーンで何度もNGを出したというこぼれ話もある。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LITTLE FOXES", "偽りの花園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "「嵐が丘」「西部の男」など数多くの秀作を生んできたゴールドウィンとワイラーの名コンビが、女流劇作家ヘルマンのヒット舞台劇を映画化。ヘルマン自らが脚色にあたっている。女主人公の居間だけに舞台を限った原作を巧みに解体し、戸外へ屋内へと自在に映画的にドラマ空間をひろげた手腕はさすが。物語の舞台は20世紀はじめの南部のある町。心臓の悪い資産家の夫からなんとかして綿工場の資金を引き出そうと画策するその妻リジーナと、その二人の兄のエゴイスティックな姿が、一流の冷徹なリアリズム手法で描かれる。ラスト近く、突然心臓発作に襲われた夫を、リジーナが冷然と見殺しにするシーンはことにショッキング。このシーンをはじめ、例によって階段の劇的な使い方に、独自の演出を発揮し、出演陣もそれぞれの力量を出しきっている。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Streetcar Named Desire", "欲望という名の電車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "1947～48年にかけて、ブロードウェイで大ヒットとなったT・ウィリアムズの舞台劇の映画化。アルコールで身を持ちくずした姉ブランチが、ニューオリンズに住む妹ステラの家を訪ねてくる。ブランチは上品にふるまい、ステラの夫の友人と恋仲になるが……。数多くの賞に輝くカザンの代表作。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUR", "スール その先は……愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「ラテン・アメリカ／光と影の詩」のソラナス監督が、８年間の亡命生活を終え、祖国に戻って製作した記念的作品。５年間の刑務所暮らしからやっと解放されたフロレアルは、妻子のもとに帰ろうとしていた。夜半、久しぶりに見る故郷の街並み。人気のない街角のカフェの前で、今は亡き友人たちの亡霊が談笑している。フロレアルの目前で、５年前の様々な思い出が、走馬灯のごとく流れていく。弾圧、逃亡、そして愛……。７年に及ぶ軍事政権のもとで、苦汁をなめたアルゼンチンの人々。解放された今も、心に受けた傷は深く重い。そんなアルゼンチン人の苦しみや哀しみを、幻想と現実を織り交ぜて描き出す。情感たっぷりに流れるタンゴと、ブルー調の映像美が素晴らしい。", "1988-01-01", "2,22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAIN", "雨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "サマセット・モームの同名舞台劇の２度目の映画化。MGMの大スター、J・クロフォード扮する美しい娼婦サディがある日、南海の小島に流れ着く。敬虔な伝導師はなんとか彼女を更生させようとするのだが、ついにその魅力に負けて……。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カナカナ", "カナカナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "30歳いう微妙な年齢の独身女性の多面性を描いた作品。３年近くの同棲生活を解消して実家に戻った塚本徳子。家では、婚期を気にする母とのいさかいが絶えず、かつての安息を得ることはできなかった。生彩のない日々を過ごす徳子はある日、登校拒否の少年・隆人に出会い興味を抱く。やがて、母と子のような関係での共同生活が始まるが、隆人が徳子に女を感じだした時、二人の間はギクシャクしたものに……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fahrenheit 9/11", "華氏911", "", "", "http://image.eiga.k-img.com/images/movie/1060/315x210.jpg?4825", 122, "「ボウリング・フォー・コロンバイン」のマイケル・ムーア監督の新作ドキュメンタリーは、第57回カンヌ国際映画祭最高賞パルムドールと国際批評家連盟賞をダブル受賞した話題作。今回のテーマは、戦争反対とブッシュ政権批判。その内容から、全米配給予定だったミラマックスが親会社のディズニーからの政治的圧力で配給を拒否、ライオンズ・ゲートから全米公開されて興収ナンバーワンのヒットを記録したという経緯も注目を集めた。", "2004-08-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アクアリウム", "アクアリウム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "須藤真澄の同名漫画を映像化したファンタジー。水族館好きの中学生、杢子は飼っていた金魚が死んだのを悲しんで、金魚の死体を持って水族館へ行く。そこで彼女は、幼い頃に夢で見た金魚の精である老人と再会し……。デジタル・ビデオで撮影され、コンピュータ処理を経て映画フィルムに変換された“水の中の世界”のイメージ・シーンが幻想的。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("清水港に来た男", "清水港に来た男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "とぼけたヤクザに扮した勤皇の志士・跡部政之進が、桂小五郎の密命を受けて清水の町にやって来る。次郎長の真意を探るため、三下として一家にもぐり込んだ彼は、やがて親分に正体を明かし、ともに新選組くずれの悪らつな用心棒を一掃する……。名匠・マキノ雅広監督お得意の“次郎長伝”余話。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Machete", "マチェーテ", "", "", "http://image.eiga.k-img.com/images/movie/55720/315x210.jpg?4741", 105, "「グラインドハウス」（07）のために製作したフェイク予告編をもとに、ロバート・ロドリゲス自身が1本の映画として作り上げたB級アクション。メキシコの麻薬王トーレスを追跡していた麻薬捜査官マチェーテは、トーレスの罠にはまり家族を皆殺しにされてしまう。数年後、米テキサスへ逃亡し、肉体労働者として糊口をしのいでいたマチェーテにテキサス州の上院議員暗殺を持ちかける人物が現れる……。タイトルロールにダニー・トレホ。共演にスティーブン・セガール、ロバート・デ・ニーロ、ジェシカ・アルバ。監督はロドリゲスとイーサン・マニキス。", "2010-11-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT AND THE CITY", "街の野獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ロンドンで観光客相手の客引き稼業を請け負っているチンピラのハリーは、一獲千金を夢見てレスリングのプロモーターを志す。しかし、彼の汚いやり口は周囲の反感を買い、ついにプロレス興行界のボスの手によって闇に葬られていく……。R・ウィドマークのキャラクターにあった小悪党演技が印象的な、渋味のある人間ドラマ。1992年の「ナイト・アンド・ザ・シティ」は、本作のリメイク。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fishing with John", "FISHING WITH JOHN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 154, "ジム・ジャームッシュ監督作品などで俳優としても活躍するミュージシャン、J・ルーリーが監督した、とぼけた味わいの作品。ルーリーが気心の知れた映画人たちと世界各地で“釣り”に興じるというユニークな６話構成の連作短編だ。一見ドキュメンタリー風だが随所にコミカルな演出が施され、各エピソードに登場する５人のゲストとルーリーのトークが、ひょうひょうとした笑いをふりまいている。", "1997-01-01", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("てんやわんや次郎長道中", "てんやわんや次郎長道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "黄金騒ぎに沸く宿場町にひょっこりと現れた若き旅鴉は、やがて町のゴタゴタに巻き込まれていく。果して、この二枚目旅鴉の正体は？　市川雷蔵のほかは関西喜劇陣がズラリ勢ぞろいの時代劇コメディ。森一生監督によるひょうひょうとした演出と、雷蔵のおとぼけ演技も見ものの一つだ。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLER IMAGE", "キラーイメージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "上院議員である兄と娼婦との密会写真を取り戻すため、弟ルーサーはプロカメラマンを殺した。だがフィルムは見つからない。殺しの味を覚えたルーサーは、目的を果たすため次々と殺人を繰り返す。無差別殺人と汚い政界の陰謀が絡み合うサスペンス・アクション。M・アイアンサイドが、存在感ある悪役演技でルーサーを演じる。", "1991-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・泥棒大家族／天下を盗る", "喜劇・泥棒大家族／天下を盗る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "原作は東京新聞の加藤延之記者の潜入ルポルタージュ『こちら特捜部・泥棒村潜入記』。九州の筑豊炭田にある全住民200余人が、血縁で結ばれた一大万引き団を形成し、前科はしめて309犯、年間稼ぎ高２億円という泥棒大家族の人間模様を描く大ケッサク喜劇。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("13 Beloved", "レベル・サーティーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "闇のネットゲームに参加した男が、過酷な試練によって人間性を失っていく姿を描いたタイ発のサスペンス・スリラー。セールスマンのプチットは、ある日突然会社を解雇されてしまう。破産寸前に追い込まれた彼は、携帯電話にかかってきた1本の電話によって、賞金3億円を獲得できるゲームに参加することになるが……。「キル・ビル」「シン・シティ」のワインスタイン兄弟が全米配給権とリメイク権を獲得した注目作。", "2007-06-09", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ODEUR DE LA PAPAYE VERTE", "青いパパイヤの香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "フランス在住のベトナム系フランス人、トラン・アン・ユン監督の処女作。1951年、サイゴン。10歳の娘ムイが田舎から奉公へ出され、とある家にやって来た。何もせずただ楽器を楽しむだけの父と布地屋を営み家を支える母、社会人となった長男と二人の幼い弟たち、それに嫁に口うるさい祖母。召使いとして食事の世話や家の細々としたことを任されるムイだったが、家へ遊びにやって来ていた長男の友人クェンに、ほのかな恋心を抱くようになる。やがて時は流れて10年後、長男は結婚し、ムイは暇を出され、今度はクェンの家へと奉公することになる……。まだ幼いムイが興味を示す、昆虫や小さなものに対する愛情豊かな視線、そしてそれを捉えた光のなまめかしく、つややかな輝き。フランスで、セットを組み撮られたとは思えぬ光の戯れに驚かされる。と同時に、全編にまき散らされたエロティックな記号がこの映画を、ほのかに彩る。", "1993-01-01", "2,60", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FADE TO BLACK", "フェイド TO ブラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ロサンゼルス・ベニスビーチの一角。足の不自由な叔母と二人住まいのエリックは、無類の映画狂。彼の部屋は、映画のポスター、スチール、16ミリ映写機などで埋めつくされている。ある日、映画のシーンそのままに叔母を殺したことから、エリックは現実とフィクションの一線を踏み外していく……。ユニークなテーマで青春の悲劇を描いたサイコ・スリラー。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLER MANIA", "キラー・マニア 殺人遊戯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "次々と美女を誘拐していけにえにし、狂気の儀式を行う戦慄の集団キラー・マニア。ある夜、魅力的な女性ローズリーが誘拐され、町はずれに住む謎の科学者オズボーン教授に疑いがかかる。さらにまた一人が誘拐され、人々の怒りは爆発する。教授は自らの疑惑を晴らすためにこの不気味な集団に戦いを挑むが……。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PERFORMANCE", "パフォーマンス／青春の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "M・ジャガー主演の映画として知られながら公開されず、ビデオのみ広範に出回っていたカルト作品。エキセントリックな性格のヤクザな青年・チャスは、チンピラを殺したことから暗黒街の組織に追われるハメになった。逃亡を画策するうち、チャスはある邸に入りこむ。所有者のターナーは引退したロック・スターで、二人のガールフレンド、ファーバーとルシーとともに快楽にひたりきる生活を送っていた。３人は次第にチャスに惹かれ、ひきずり回されるようになる。時間と空間をバラバラに分析するスタイルは、これがデビューのN・ローグが後年華麗に展開していくスタイル。加えて1960年代末のサイケデリック調の残り香を濃厚にとどめている。バイセクシュアルやサディズム、マゾヒズムの低く響く調子。故ブライアン・ジョーンズの恋人、A・パレンバーグとM・ジャガーの共演など話題は尽きない。", "1970-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほっこまい　高松純情シネマ", "ほっこまい　高松純情シネマ", "", "", "http://image.eiga.k-img.com/images/movie/54450/315x210.jpg?3839", 60, "大阪万博が開幕した1970年、香川県の名門高校に入学した隆志は勉強もそっちのけで、映画館に通う日々を送っていた。そんなある日、隆志は近所の公園で見かけた春奈という少女に一目ぼれし、映画デートに誘うもあっけなく断られてしまう。落ち込む隆志は友達に春奈がヒロインの映画撮影を提案され……。", "2009-03-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MUTATIONS", "悪魔の植物人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「ザ・フライ」のハエ人間も恐ろしいが、本作では人間と多種の植物をミキシングして、醜悪なフランケンシュタイン博士の怪物と同様の悲劇を描いたショッキング・ホラーだ。教授は新しい人間を作りたがっていた。そこで、自分の助手を使い実験台になる人間を探させた。最初は女子大生が餌食になり、醜くなった彼女は見世物小屋で見世物にされる。DVDタイトルは「The Freakmaker」。", "1973-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モスラ", "モスラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「空の大怪獣ラドン」でリアルな怪獣映画の一つの到達点をきわめた東宝特撮陣は、以降怪獣もの以外のSFジャンルに挑戦するが、興行的に苦戦し、安定路線として怪獣映画の本格的な復活が図られた。かくて怪獣映画初のカラー・ワイド画面による「モスラ」が東宝特撮映画の黄金トリオの手により製作された。南海の幻想的なインファント島にやって来た悪徳ブローカーは、身長30センチの小美人を誘拐し東京でショーを開催して巨利を得る。しかし、捕われた小美人のSOSテレパシーに呼応して守護神・巨大蛾モスラが東京に上陸する……。東京タワーに寄りかかってサナギから成虫へと変態する場面は、あまりにも有名。またザ・ピーナッツの歌う『モスラの歌』は、東宝怪獣シリーズの挿入歌として不滅の曲だ。なお、この作品は「ゴジラ」のようなリアルな怪獣ものとは違いファンタジックな要素にあふれた怪獣ものとして企画された。３人の純文学者によって原作が書かれた点も記したい。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL BRIVIDO BLU", "裏窓の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "自分の部屋の窓ごしに、向かいのアパートの一室でハードなSEXに興じる男女の姿を目撃してしまった青年。だが彼は、アパートのバルコニーから何者かが侵入し、男の方が殺されるというショッキングなシーンをも目撃してしまう……。イタリア映画お得意のエロティック・サスペンス。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ye Haeng Ja", "冬の小鳥", "", "", "http://image.eiga.k-img.com/images/movie/54970/315x210.jpg?1927", 92, "フランス在住の韓国人女性ウニー・ルコントが、韓国から養子としてフランスへ渡った自らの少女時代を映画化した自伝的デビュー作。父親に捨てられ孤児院に入れられてしまった9歳の少女ジニ。はじめのうちは状況を受け入れられず反発してばかりの彼女だったが、やがて自らの運命を受け入れて養子縁組に希望を抱くようになる。「シークレット・サンシャイン」のイ・チャンドン監督がプロデューサーとして参加。", "2010-10-09", "5,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AMITYVILLE CURSE", "続・悪魔の棲む家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ロング・アイランドの町、アミティヴィルの司祭館で実際に起こった超常現象を映画化した「悪魔の棲む家」。それから12年後、奇怪な事件が続発した館を購入した数人の男女に襲いかかる恐怖を描く。スタッフもキャストも一新、監督は俳優、脚本家、プロデューサーとしても活躍するT・ベリー。アミティヴィルを舞台にしているが1979年製作の「悪魔の棲む家」とは別作品である。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SILENT FLUTE", "サイレント・フルート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ブルース・リーが遺した企画を映像化した異色カンフーアクション。世界最強といわれる伝説の武術家を探す役目を賭けた武術トーナメントで、勝利したにもかかわらず、対戦相手に任務を奪われた武道家コード。悔しさをかみしめつつ、対戦相手の旅の後を追う彼は、目の不自由な武術の達人と出会い、神がかり的なその妙技に魅了され、弟子入りを志願するが……。リーの武術哲学を繁栄した、思索的なセリフが印象に残る。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rambo", "ランボー　最後の戦場", "", "", "http://image.eiga.k-img.com/images/movie/53120/315x210.jpg?1550", 90, "シルベスター・スタローン主演によるメガヒットシリーズの20年ぶりとなる第4弾。同シリーズで初めてスタローン自らメガホンを取り、ミャンマーの社会情勢を盛り込みつつランボーの壮絶な戦いを描き出す。タイとミャンマーの国境付近でミャンマー軍事政権によるカレン族の迫害が激化。タイ辺境のジャングル地帯で暮らすランボーは状況を知りつつも静観の構えを見せていたが、ある事件をきっかけに再び戦いの中に身を投じていく。", "2008-05-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エロス＋廃墟", "エロス＋廃墟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "崩壊し荒れ果てた建造物とエロティックな肢体との絡み合いによる、アンニュイにしてある種の癒しへと導くイマジネーション・アート作品。田中昭ニ監督が「廃墟ロマネスク」に続いて撮った映画。写真集、インターネットなどでの静かな廃墟ブームに新しい視点をプラスした。女は廃墟にたどり着き、そこで彷徨ううちに安らぎと自由を感じ、建物に身体を絡ませ官能の世界に浸る。", "2005-01-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO　GUYS", "あぶない奴ら／TWO GUYS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「猟奇的な彼女」で日本でも多くのファンを獲得した若手俳優チャ・テヒョンが主演したアクション・コメディ。気弱でお人好しのキャラクターが似合うテヒョンが、本作ではお調子者の詐欺師に扮してコミカルな魅力を披露。そんな主人公と借金取り立て屋の男が、ひょんなことから国際スパイ組織とのイザコザに巻き込まれ、波瀾万丈の運命をたどっていく痛快バディ・ムービーだ。ダブル主役のもう一人、テヒョンと巧みな掛け合いを見せるのは「NOWHERE 情け容赦なし」のパク・チュンフン。借金を踏み倒す常習犯の青年フンが、取り立て屋のジュンテに捕まった。ところが二人は、ある鞄の中身をめぐって国際スパイ組織が絡む思わぬ大事件に巻き込まれてしまう。", "2005-12-23", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES E´GOUTS DU PARADIS", "掘った奪った逃げた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "地下にトンネルを掘り、南フランス、ニースのソシエテ・ジェネラル銀行の地下金庫を破って50億フランを奪い、そして逃げた……。怪盗スパジァリの自伝・実話の映画化で、これも犯罪歴のあるジョヴァンニ監督が、リアルな映像で再現している。一味と金塊のその後は未だ不明である。", "1979-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEUX HOMMES DANS LA VILLE", "暗黒街のふたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「地下室のメロディー」「シシリアン」に続くドロン、ギャバン３度目の顔合わせ。銀行強盗で12年の刑に服していたジーノが老保護司ジェルマンの力添えで仮出所する。待っていた妻とジェルマンの励ましでまじめな生活を始めたジーノだが……。", "1973-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("果しなき欲望", "果しなき欲望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "敗戦時、一人の軍医がひそかに埋めた時価６千万円のモルヒネ発掘をめぐって、軍医の旧部下と称する４人の男たちと、今は亡き軍医の妻と称する女が繰り広げる色欲と物欲のドラマ。そこに長門裕之が演じる失業青年が絡み、恋人の浮気を警戒しながらも、自分は妖艶な渡辺美佐子扮する志麻の誘惑に簡単に落ちてしまうところがおかしい。西村晃、小沢昭一など今村作品ではおなじみの芝居巧者が、作品を盛り立てている。今村監督はこの欲にとりつかれた亡者たちが自滅していく様を、粘り気のあるエネルギッシュな演出で描く。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犯され志願", "犯され志願", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "都会に生きるキャリアウーマンたちの性的日常をほろ苦く描くロマンポルノ。関本郁夫監督の「天使の欲望」で強烈な印象を残した有明祥子が、自意識過剰で根の明るいヒロインを好演。彼女は好きな男がいながらも他の男に抱かれていく。中原俊監督のデビュー作。官能シーンも柔らかに、リアルに綴られる。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SKY’S THE LIMIT", "青空に踊る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "第二次大戦下を背景とした歌と踊りの喜劇。中国で偉功を立てた空の勇士が休暇でニューヨークへ帰ってくる。そこで知り合った雑誌の写真リポーター、ジョーンと恋に落ちるが、明日をも知れぬ軍人、と身を引こうとする。踊りの振り付けもアステア自身。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCOTT OF THE ANTARCTIC", "南極のスコット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "厚い氷に閉ざされた大陸・南極。20世紀初頭、列強各国はこの大陸の利権をめぐって多くの探検家を送り込んでいた。英国のスコット大佐も一行とともに二度の南極探検に挑んだが、1912年１月18日に極点に達した後に遭難、スコット隊全員が無念の最期を遂げる。この実話をドキュメンタリー・タッチでつぶさに映し出した作品。", "1949-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("命美わし", "命美わし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "新聞記者・伊村は、自宅の近くの堀で身投げしようとした娘・房江を救う。訳を聞けば、彼女は市議会議長、梅沢の息子に身重のまま捨てられたという。伊村は事件を新聞に発表して世論に訴えるが、梅沢の流言によって逆に苦境に追い込まれる……。小津作品でおなじみの笠智衆が主演した社会派風のメロドラマ。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("木曾の天狗", "木曾の天狗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "阪東妻三郎が、母と故郷を捨てイカサマ博奕の道具を売り歩いて旅を続ける渡世人・名なしの権兵衛に扮した娯楽時代劇。自分を窮地から救ってくれた百姓・茂左衛門のために、彼は一念発起。２年前家を出て行った茂左衛門の息子・甚兵衛と息子の恋人・おとよを町の悪漢の手から救い出す。DVDは「阪東妻三郎傑作選　DVD-BOX」に収録。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("道場破り", "道場破り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "剣の達人、三沢伊兵衛は、藩主から側室になるよう強要されていた家老の娘、妙を連れて脱藩出奔し、道場破りで金を稼ぎながら旅を続けていた。そんなある日、彼は藩がさしむけた剣客、大庭軍十郎と、互いの素性を知らぬまま賭け試合で対決することになるが……。山本周五郎の『雨あがる』を原作に、内川清一郎監督がダイナミックに描いた時代劇の秀作。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京マーブルチョコレート", "東京マーブルチョコレート", "", "", "http://image.eiga.k-img.com/images/movie/53830/315x210.jpg?3649", 46, "「攻殻機動隊」シリーズをはじめ数々の世界的ヒット作を生み出してきたプロダクションI.Gと、音楽レーベルBMG JAPANの創立20周年を記念して制作されたアニメーション。クリスマスイブの東京を舞台に、明るくてドジな少女チヅルと優しくて臆病な青年・悠大が織りなす純愛物語をそれぞれの視点からとらえ、SEAMOの曲「マタアイマショウ」とスキマスイッチの曲「全力少年」に乗せて描き出す。", "2008-08-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("One,Two,Three", "ワン、ツー、スリー／ラブハント作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "　ドイツで撮影されたドタバタ爆笑コメディ。コカコーラ社のベルリン支社長を務めるマクナマラは、本社の重役から娘のおもりを頼まれる。だが、この娘は一筋縄ではいかない厄介者だった。出世のためなら……という主人公心理は、ビリー・ワイルダーの前作「アパートの鍵貸します」にも共通する。息つく間もなくギャグが応酬される台詞まわしは、ワイルダーならでは。監督業もならしたジェームズ・ギャグニーの喜劇役者としての魅力も全開である。", "1962-03-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("珈琲時光", "珈琲時光", "", "", "http://image.eiga.k-img.com/images/movie/1340/315x210.jpg?4499", 103, "小津安二郎監督の生誕100年を記念して、「悲情城市」「フラワーズ・オブ・シャンハイ」のホウ・シャオシェン監督が、東京を舞台にひとりの女性の日常を繊細に描く。フリーライターの陽子は、産みの母が台湾人で日本と台湾を行き来しているが、ある日、台湾の男性の子供を妊娠していることに気づく。ヒロイン陽子を演じるのは、デビュー曲「もらい泣き」で注目を集めた一青窃。彼女に想いを寄せる古本屋店主役で浅野忠信が共演。", "2004-09-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("茶の味", "茶の味", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "「PARTY７」の石井克人が描き出す、ファンタスティックな味わいのホームドラマ。それぞれモヤモヤした思いを抱えている一家の姿を軸に、ヘンテコな人々が形成する田舎町の“小宇宙”を、ときに顕微鏡で覗き込むように、ときに風景を眺めるように味わいつくした“通”な感覚の一篇。脇役ながら浅野忠信が、ひと声だけで観客を吸引する特異な才能を発揮している。田舎町に住む春野家の長男・一は、片思いの女子が転校してしまい、自分のふがいなさを嘆いていた。一方妹の幸子は、自分の巨大化した姿が見える幻覚に悩んでいる。そんな二人に叔父のアヤノは、ウンコと幽霊について語り始め……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOOKING FOR RICHARD", "アル・パチーノの　リチャードを探して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "A・パチーノが長年の夢だったW・シェイクスピアの『リチャード三世』の映画化を、自ら製作・監督・主演で実現させた作品。『リチャード三世』を上演する役者たちの初日までの様子を追いながら、同時に『リチャード三世』のストーリーも描き、さらにK・ブラナー、J・ギールグッド、V・レッドグレーヴらイギリスの名優にシェイクスピア劇についてのインタビューを行うという構成になっている。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長血笑記 富士見峠の対決", "次郎長血笑記 富士見峠の対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "兄弟分の仇を長楽寺に斬り捨てた次郎長一家だったが、代官所の手が伸びたのを知ってふた手に分かれた。次郎長、お蝶、石松らは巾下の長兵衛宅に宿を取り、温かいもてなしを受ける。だが長兵衛の仇敵、久六の密告で出立を余儀なくされた彼らは道中、長兵衛が久六に殺されたのを知り、とって返すのだった。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOFFA", "ホッファ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "D・デヴィートが、「カッコーの巣の上で」で共演して以来の親友J・ニコルソンを主演に据えて撮り上げた人間ドラマ。長距離トラックのドライバーから運転手組合の会長にまで成り上がり、ケネディ大統領とも法廷で闘ったジェームズ・R・ホッファの、謎の失踪を遂げた晩年に至るまでの姿を描く。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("想い出の渚", "想い出の渚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "常夏の島サイパンを舞台に、日本から訪れた3人の中年男性が織りなす恋模様を描いたラブコメディ。福引きで当てたサイパン旅行へやって来た信吾、銀次、ひろしの中年オヤジ3人組。待ちうけていた美女たちにはしゃぐ銀次とひろしだったが、信吾だけはなぜか塞ぎこんでおり……。キャストには石橋保、中野英雄、遠藤憲一らベテラン個性派俳優がそろう。メガホンを取るのは、「まいっちんぐマチコ！ビギンズ」の鈴木浩介監督。", "2007-04-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("市川馬五郎一座顛末記 浮草日記", "市川馬五郎一座顛末記 浮草日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "悪興行師にあって貧乏のどん底に落ちた旅回りの市川馬五郎一座。解散の一歩手前、炭鉱町に乗り込んで座員の必死の呼び込みが功を奏し、小屋は久々に満員となる。しかし炭鉱町はストに入り、興行師はドサクサに紛れて売上金を持ち逃げ、一座は組合と対立することになるが……。底辺に生きる人々を描くのがお得意の山本監督の野心作。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝ青春の胸の血は", "あゝ青春の胸の血は", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "東都大学のボート部員である清水邦夫は、練習の帰り道、何者かに追われている少女を助けた。少女の名は友子。邦夫は友子を愛するようになるが、彼女は自分の過去を語ろうとはしない。彼女は感化院上がりだった……。同名の歌謡曲をモチーフにした青春歌謡ドラマ。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("開運旅行", "開運旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "東京～熊本間を走る特急“みずほ”に勤務する夫婦がいる。ケチで有名な車掌の修平と浪費家の食堂車ウェイトレス・京子だ。その二人が巻き起こす珍道中。左とん平・左卜全らが脇を固めている。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHERE THE RIVERS FLOW NORTH", "リバー・ノース～遥かなる河の流れに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "1927年、秋。人生も終盤にさしかかり、ノエルはバーモントの大自然の中でネイティブ・アメリカンの女性バンガーと静かな日々を送っていた。しかしその生活も、森にダムを造る計画が持ち上がったことからにわかに騒がしくなる。開発会社のボス、ファーンズ・ワースが、二人を立ち退かせるために様々な陰謀を画策し始めたのだ……。M・J・フォックスが、愛するバーモントの自然保護を訴えるために初めての敵役に挑戦。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METAL：A HEADBANGER’S JOURNEY", "メタル／ヘッドバンガーズ・ジャーニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ヘヴィメタルの人気を探りながら、相反する社会的な反発の理由を検証した音楽ドキュメンタリー。新旧人気アーティストのインタビューやライブ映像などファンの見逃せない映像が続々登場。何よりメタルへの愛情あふれる語り口が嬉しい。'86年、ヘヴィメタルの人気沸騰と共に、世間から沸き起こった激しいバッシング。若き人類学者サム・ダンがその理由を探り、人気アーティストを訪ねて米英、北欧と旅をする。", "2006-06-24", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L' AMOUR PAR TERRE", "地に堕ちた愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "ジェラルディン・チャップリンとジェーン・バーキンが演じる苦悩を見事に体現、サスペンス風味も効いたリベットお得意の舞台ものの傑作。未完の劇台本を渡された3人の役者が、稽古のために演出家の屋敷に住むことになった。演出家の実体験らしいその劇には女はひとりしか登場しないのに、女優はふたり。幻覚や幻視に襲われる不思議な数日を過ごした屋敷に、いよいよ本番の日がやってきた。劇が進行し、現実の世界と交錯していく……。", "1993-08-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE VAMPIRE HAPPENNING", "バンパイア・ハプニング 噛みついちゃってごめんなさい!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ハリウッドのセックス・シンボルとして人気を集める美人女優が、ある日祖母の故郷ペンシルバニアにやって来る。しかしかつて名門の男爵夫人だった祖母は実は吸血鬼だった。町のヒーローを抜擢し、なんとか吸血鬼を退治しようとする住人たちだったが、なにしろ吸血鬼と彼女は瓜二つ。大騒動が巻き起こって……。", "1971-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE POTION", "ドラッグ・トリック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "麻薬中毒患者のリハビリ病院で繰り返される、秘密の実験と不可解な死。その事実を知ってしまった娘は、病院を脱出しようとする。だが、彼女はその後、恐怖と緊張のパラノイア体験にさいなまれていく。人間の心理的な恐怖をうまく利用した、サイコ・サスペンス・ドラマ。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Almost love", "青春漫画　僕らの恋愛シナリオ", "", "", "http://image.eiga.k-img.com/images/movie/33610/315x210.jpg?4653", 111, "「同い年の家庭教師」のクォン・サンウとキム・ハヌルが再共演を果たした青春ラブ・ストーリー。アクション俳優を夢見る大学生ジファンと女優志望のダレルは、小学生からの幼なじみ。毎日のようにケンカしながらも、お互いの夢を尊重して支えあう兄妹のような関係だった。やがて彼らはそれぞれの夢を実現するが、ある日ジファンが突然の事故により大怪我を負い、アクション俳優としての道を閉ざされてしまう。監督は「永遠の片想い」のイ・ハン。", "2006-08-19", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL CAMMINO DELLA SPERANZA", "越境者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "イタリアン・ネオ・リアリスモの巨匠、P・ジェルミが不法移民をテーマに描いた社会派の感動作。貧困にあえぐシシリーの坑夫たちは、フランスへ行けば楽に暮らせると聞き、不法移民となることを決意する。一行はイタリア本土を北上するが、ローマに着いたところで仲間の一人が旅費を持ち逃げ、仕方なく彼らは農園のアルバイトに雇われる。絶望した何人かはさらにグループから脱落していくが……。", "1950-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スーパー・ジャイアンツ／　宇宙怪人出現", "スーパー・ジャイアンツ／　宇宙怪人出現", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "シリーズ第７作は三輪彰の監督デビュー作。日本征服をたくらむ世界暗黒党の党主・黒川博士は、宇宙怪人を使って東京を恐怖のどん底に。スーパー・ジャイアンツは宇宙怪人を激闘の末に倒し、世界暗黒党の本部に乗り込み、これを破壊する。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2001: A Space Odyssey", "2001年宇宙の旅", "", "", "http://ecx.images-amazon.com/images/I/51IL6W-Uf6L._SL210_.jpg", 140, "スタンリー・キューブリック監督と原作者アーサ・C・クラークによる、映画史を代表する不朽の名作SF。月に人が住むようになった時代。月のクレーターの地中からの謎の石碑が発掘され、宇宙評議会のフロイド博士が調査に向かう。それから18カ月後、最新型人工知能「HAL（ハル）9000型コンピュータ」を搭載した宇宙船ディスカバリー号は、デビッド・ボウマン船長、フランク・プールら5人のクルーを乗せて木星探査に向けて航行していた。しかし、その途上でHALが探査計画に対して疑問を抱いていることを打ち明ける。ボウマンとプールはHALの不調を疑い、いざというときはHALの回路を切断することを決めるが、それを知ったHALは反乱を起こす。極端に少ない台詞や固定した長回しのカメラワーク、「ツァラトゥストラはかく語りき」「美しく青きドナウ」をはじめとするクラシック音楽の使用などが斬新で印象的。撮影時に開発された新技術と、科学的裏付けの追求により人工知能HALの暴走がリアルに描かれている。第42回アカデミー特殊視覚効果賞受賞。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スーパーカブ", "スーパーカブ", "", "", "http://image.eiga.k-img.com/images/movie/53270/315x210.jpg?3613", 80, "「タッチ」の斉藤慶太扮する伝説の走り屋が、出前ライダーとなって街を駆け抜ける青春バイク・アクション。峠のレースで最速を誇る走り屋の武史は、ライバルである真治とのレース中に白バイに追われ、運悪く捕まってしまう。海外で暮らす親の金をバイクにつぎ込んていたことが発覚し、借金返済のために父の友人が営む蕎麦屋で働くことになった武史。愛車は没収されたものの、出前用のスーパーカブにだけは乗ることを許され……。", "2008-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUN CRAZY／裏切りの挽歌", "GUN CRAZY／裏切りの挽歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "女性を主人公にしたアクション・シリーズの第２作。告発したレイプ犯が無罪となり、無力感に苛まれる女性弁護士、由紀。ある男との出会いから殺し屋となった彼女は、法で裁けない悪を次々と葬りさる。だが、その世界にも正義の限界を感じたとき、由紀は裏切りへの報復に乗り出し……。ヒロイン、菊川怜のしなやかな熱演が見どころ。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BON　VOYAGE", "ボン・ヴォヤージュ", "", "", "http://ecx.images-amazon.com/images/I/51x3JZfDpXL._SL210_.jpg", 115, "「プロヴァンスの恋」以来８年ぶりとなる、J・P・ラプノーの監督作。フランスが最も激動したとされるパリ陥落から36時間の様相を、一人の女優をめぐる恋模様と絡めて描き出す。I・アジャーニ、G・ドパルデューら豪華キャストの競演も見ものだ。ナチス占領下のフランス。女優のヴィヴィアンヌは戦火を逃れボルドーへ向かう……。", "2004-12-11", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bagdad Cafe: New Director's Cut", "バグダッド・カフェ　ニュー・ディレクターズ・カット版", "", "", "http://image.eiga.k-img.com/images/movie/54950/315x210.jpg?1779", 108, "「シュガーベイビー／ミュンヘン　男と女の夜」のパーシー・アドロンが1987年に製作した「バグダッド・カフェ」のディレクターズ・カット版。アドロン自らがオリジナル版を再編集し、色・構図を修正して完成させた。アメリカ西部の砂漠にあるモーテル「バグダッド・カフェ」に、ドイツから旅行に来たジャスミンがやってくる。最初はジャスミンを敬遠していた女主人のブレンダも徐々にジャスミンと親交を深め、2人で店を盛り上げていくが……。", "2009-12-05", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAMMY GOING SOUTH", "サミー南へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "イギリスとエジプトの間で戦争が勃発しようという頃のエジプト・ポートサイド。10歳の英国人少年サミーは、両親のはからいで南アフリカのダーバンでホテルを経営する叔母のもとに預けられることになる。しかし出発直前、家が爆撃され両親が死去。サミーは一人、方位磁石を頼りに南へと旅立つ。セミ・ドキュメンタリー・タッチの冒険ドラマ。", "1963-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEVER BEEN KISSED", "25年目のキス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "キスの経験もないオクテの女性新聞記者ジョジーが、現代の高校生の生態を取材するため、生徒に扮してハイスクールに潜入。サエなかった高校時代を回想しつつ、彼女は人気者グループの仲間に入れてもらおうと悪戦苦闘。一方で、優しい文学の教師に惹かれていくが……。ハイスクール時代のトラウマに終止符を打つ、若い女性の奮闘を描いたロマンティック・コメディ。どんくさいヒロインの変身を、D・バリモアがはつらつと好演する。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENEMIES，A LOVE STORY", "敵、ある愛の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ユダヤ人のハーマンは大戦後ポーランドの娘と結婚、渡米して幸福に暮らしていた。平凡な妻に飽き足らぬ彼には情熱的な愛人がいたが、そこにユダヤ人収容所で死んだはずの最初の妻が現れる。３人の女性と重婚することになった彼の選択は？ 主演３女優の個性が存分に生かされたおかしくも苦い愛の物語。P・マザースキーが、３人の女の中で揺れる男の心のさすらいを、手際の良い演出でさばいている。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四十八人目", "四十八人目", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "赤穂浪士の盟約に加わり、同志として積極的に行動しながら、討入り直前となって脱落した実在の人物・毛利小平太を描いた伊藤大輔監督作品。同志の掟と美しい娘・お篠への思いとの間で苦悩する毛利を、歌舞伎出身の坂東が好演。ヒロインの山田五十鈴は、この翌年に溝口健二作品「浪華悲歌」「祇園の姉妹」に出演、日本を代表する大女優への道を歩む。戦後、「小平太何処へ行く」と改題された。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新宿アウトロー・ぶっ飛ばせ", "新宿アウトロー・ぶっ飛ばせ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "渡哲也と原田芳雄の顔合わせによる藤田敏八監督のニュー・アクション。渡哲也扮する死神と恐れられる若いヤクザが、金持ちのフーテン野郎やズベ公と手を組んで、ヤクザ組織からマリファナを強奪する。サソリと呼ばれる組織の殺し屋に扮した成田三樹夫がすごみを利かせる。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALENDAR GIRL MURDERS", "魔性の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "雑誌のグラビア・モデルを次々と狙った連続殺人事件を追う、刑事ダン・ストナー。彼は捜査を進めるうち、妖しく美しい謎の女、キャシーの魅力にほんろうされ……。人気女優S・ストーンがまさにハマリ役のセクシーな元モデル役を演じた、テレフィーチャーによるエロティック・サスペンス。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONNIE＆CARLA", "コニー＆カーラ", "", "", "http://ecx.images-amazon.com/images/I/519PXQ5SY0L._SL210_.jpg", 98, "「マイ・ビッグ・ファット・ウェディング」のN・ヴァルダロスが、今度はドラッグ・クイーンに大変身。「アバウト・ア・ボーイ」のT・コレットを相棒に迎え、ユーモラスで豪快、ちょっぴりチャーミングな“男性”エンターテイナーを熱演している。皮肉にも男装で魅力を開花させる彼女らが、どんな苦境にもめげずに夢を勝ち取る姿を、友情ドラマ、ロマンス、サスペンスを絡めてテンポよく描出。見事な歌唱力とセクシーダンス、毒舌トークが披露されるステージ・シーンには大興奮だ。売れないダンサーのコニーとカーラが、ある日、店の経営者が殺されるのを目撃してしまい、LAへ逃亡。そこでドラッグ・クイーンを装って仕事を得た二人は人気者になってしまう……。", "2004-11-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUSANA", "スサーナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "嵐の夜、刑務所を脱獄した悪女スサーナはある農場に入り込み、そこの主人、息子、使用人たちを次々と誘惑し、平和だった家庭に不隠な空気を巻き起こす。ブニュエルがメキシコ時代に撮った作品の一つ。ブラック・ユーモアの中に女の恐ろしさと、彼女にほんろうされる家族を描いた人間ドラマ。", "1950-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピカ☆ンチ・LIFE IS HARD だけど HAPPY", "ピカ☆ンチ・LIFE IS HARD だけど HAPPY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "人気グループ“嵐”の５人が映画初主演。ジャニーズ事務所の先輩 “V6”の井ノ原快彦の実体験である、マンモス団地での愉快でちょっと変わったエピソードをベースにした明るく楽しい青春ドラマ。東京・品川にある世帯数6000のマンモス団地、八潮団地に暮らす18歳の少年５人の、ノホホンとお気楽な日々。時には無茶もするけれど、恋、家庭、高校卒業後の進路など、悩みも尽きない彼らの表情をイキイキと活写。全体のテンポも良く、単なるアイドル映画にとどまらないさわやかな好編になっている。", "2002-10-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRES WITHIN", "熱い夜に抱かれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "革命のキューバと、マイアミを舞台にしたラブ・ロマンス。「推定無罪」のグレタ・スカッキが二人の男の間で揺れ動く人妻を熱演。キューバ革命の犠牲となり、終身刑の身となった夫を残し幼い娘とともにマイアミへ逃げ延びたイザベル。夫への思いと、見知らぬ土地での戸惑いのなか、生きる勇気を失いかけたイザベルの前に、一人の男が現れる。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無頼平野", "無頼平野", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "漫画家つげ忠男の未完の大作を、石井輝男がオリジナリティー豊かにシナリオ化。ナミは街の皆が憧れる“カジノ座”の踊り子。無頼漢として知られるサブも、ナミに思いを寄せる一人だった。同じくナミを狙うヤクザ組織のボス・梶山は、彼女に陰湿な嫌がらせを重ねる。窮地を幾度も救うサブだが、ナミは無頼漢からの好意は受けようとはしない。嫌がらせはエスカレートし、梶山はついにナミを無理に連れ去ってしまう。梶山のアジトに単身乗り込むサブ。手強い殺し屋のため窮地に追い込まれた彼の前に現れたのは、ナミの父親でありサブの幼い頃の記憶にもある伝説の無頼漢、リュウだった……。ビデオタイトルは「昭和侠客外伝　BURAI-HEIYA」。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE ON HAUNTED HILL", "TATARI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "J・シルヴァーとR・ゼメキスが設立したダークキャスル・エンターテインメントの第１作目となるホラー映画。人を恐怖に陥れることに快感を覚える大富豪プライスは、かつて人体実験が行われていた精神病院を買い取り、妻の誕生パーティーを開くことにする。だが、不思議なことに、そこには招待されていない５人の男女が集まった。それでもプライスは、この館で一晩過ごすことができた者には賞金を出すと宣言。あらかじめ用意した装置で客たちを驚かそうとするが、呪われた館に巣くう“何か”によって客たちが惨殺されていく。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミステイクン", "ミステイクン", "", "", "http://image.eiga.k-img.com/images/movie/57160/315x210.jpg?1349", 60, "お笑いコンビ「野性爆弾」の川島邦裕が主演を務め、第3回沖縄国際映画祭で上映されたホラーコメディ。自身の部屋に言い知れない違和感を抱いた川島はカメラを回すが、その時、玄関のベルが鳴る。それをきっかけに次から次へと奇怪な現象が起こりはじめ、川島は極限まで追いつめられていくが……。「野性爆弾」相方のロッシーほか、「次長課長」の河本準一、「はんにゃ」の金田哲らお笑い芸人が多数出演。", "2011-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOR ALL MANKIND", "宇宙へのフロンティア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "1968～72年にかけて、９回にわたって敢行された月への有人宇宙飛行アポロ計画。この計画で月面に到達した24人の男たちの貴重な体験を、何万フィートにものぼる記録フィルムから再構成し、ロケット発射から月面着陸までの過程を鮮明な画像でまとめたドキュメンタリー映画。U.S.フィルムフェスティバルのドキュメンタリー部門で、グランプリを受賞。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Men Who Stare at Goats", "ヤギと男と男と壁と", "", "", "http://image.eiga.k-img.com/images/movie/55080/315x210.jpg?3061", 94, "ジョン・ロンスンのベストセラー・ノンフィクション「実録・アメリカ超能力部隊」をベースにしたコメディ。ジョージ・クルーニー、ユアン・マクレガー、ケビン・スペイシー、ジェフ・ブリッジスら豪華俳優が結集する。ミシガン州の地方紙記者ボブ（マクレガー）は、離婚の痛手を断ち切ろうと戦下のイラクへ向かう。そこでかつて存在した極秘部隊「新地球軍」の特殊工作員リン（クルーニー）に出会い、同行することにになる。リンは悪の力に支配された超能力戦士ラリー・フーパー（スペイシー）から超能力マスターのジャンゴ（ブリッジス）を救出しようと奔走するが……。", "2010-08-14", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Plages D'agnes", "アニエスの浜辺", "", "", "http://image.eiga.k-img.com/images/movie/54350/315x210.jpg?3701", 108, "ヌーベルバーグを代表するフランスの女性監督アニエス・バルダが、転機となった浜辺を通して自身の人生を振り返るドキュメンタリー。ジャン＝リュック・ゴダール、ジム・モリスン、ハリソン・フォードなど各界の著名人が多数登場するほか、アニエスが亡き夫のジャック・ドゥミ監督への想いなどを赤裸々に語る。", "2009-10-10", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Even Cowgirls Get the Blues", "カウガール・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "カルト的人気を誇る作家トム・ロビンスによる小説を、「マイ・プライベート・アイダホ」のガス・バン・サント監督がユマ・サーマン主演で映像化した、奇想天外なロードムービー。生まれつきの巨大な親指を武器に、史上最強のヒッチハイカーとなったシシー。生理用品会社のモデルでもある彼女は、新製品のCM撮影のため、社長が所有するラバーローズ牧場を訪れる。そしてそこで働く自由奔放なカウガールたちに刺激を受けるが……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH　WATCH", "デス・フロント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「リトル・ダンサー」でのハツラツとした演技で人気を博したジェイミー・ベル主演の異色ホラー。雨でぬかるみ、死体が散乱する塹壕で異常現象に見舞われた兵士たちの恐怖を描き出す。第一次大戦中のヨーロッパ西部戦線。16歳の少年兵チャーリーが所属するイギリス軍の小隊が、雨でぬかるんだドイツ軍の塹壕を占拠した。しかし兵士の一人が何者かに殺され、この世ならぬ怪現象が次々とチャーリーらを襲う。", "2002-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月のあかり", "月のあかり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "沖縄を舞台に繰り広げられる不思議感覚のドラマ。沖縄の山羊料理屋“月子”を営む主人“おっさん”の元に東京から一人の男が現れた。沖縄に楽園を求め、自分を慕う女から逃げてきた男を疎ましく思うおっさん。そこに男を追ってきた女、謎のヤクザ、店の常連客も加わり、おっさんの周囲は突如騒がしくなっていく。歌手の椎名へきるが映画初出演。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG BAD MAMA II", "ビッグ・バッド・ママ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "A・ディキンソンがパワフルでセクシーな女ギャングを演じ、B級アクションの快作としてカルト的な人気を博した「ビッグ・バッド・ママ」の続編。1930年代のテキサス。悪徳地主に殺された夫の復讐のために、二人の娘を相棒に“ママ”がマシンガン片手に再び立ち上がる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ポケットモンスター　アドバンスジェネレーション　ポケモンレンジャーと蒼海（うみ）の王子マナフィ", "劇場版ポケットモンスター　アドバンスジェネレーション　ポケモンレンジャーと蒼海（うみ）の王子マナフィ", "", "", "http://image.eiga.k-img.com/images/movie/33550/315x210.jpg?4308", 108, "劇場版ポケットモンスター第9作。海を舞台にサトシやピカチュウたちが繰り広げる大冒険を描く。サトシたち一行は、みずポケモンと心を通わせることが出来る“水の民”の末裔ヒロミと出会う。彼女から、広大な海のどこかにあるという“海の神殿アクーシャ”の伝説を聞いた一行は、ポケモンレンジャーのジャックと共に神殿を探す旅に出るが……。新ポケモンのマナフィやゲームでも大人気のポケモンレンジャーなど、新キャラクターも登場。", "2006-07-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRISON SANS BARREAUX", "格子なき牢獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "少女感化院を舞台にした、古き良き時代の女性ドラマ。“真の愛情によって不良少女は更生する”という信念を持つ院長のイヴォンヌと、世をすねた美しい娘ネリーの心のふれあいを描く。奔放な少女ネリーを演じたC・リュシエールは本作で多くの人の心を捉え、以後も「美しき争ひ」（1938）など数編に顔を出したが、1950年に29歳の若さで没した。ロシア出身のL・モギー監督の出世作でもある。", "1938-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD WIND", "軍用列車大爆破", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "第二次世界大戦末期におけるユーゴスラビアでは、ナチス・ドイツの支配下にある民兵と共産ゲリラが対立していた。こうした状況のなかで、バルカン半島へのドイツ軍の進攻を阻止しようとする連合軍は、鉄橋爆破作戦をひそかに計画するのだが……。G・モンゴメリーをはじめ、ユーゴスラビア、ソ連を含む世界のオールスター・キャスト共演による戦争ドラマ。", "1986-01-01", "1,12,34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Punk in London", "パンク・イン・ロンドン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "パンク・ムーブメント真っ只中のロンドンのミュージックシーンを、ドイツのボルフガング・ビュルト監督が撮ったドキュメンタリー。ザ・クラッシュ、ザ・ジャムといった大御所から、アドバーツ、ラーカーズ（両親も出演！）のような中堅バンド、さらに80年代にブレイクしたデキシーズ・ミッドナイト・ランナーズのフロントマン、ケビン・ローランドが在籍していたB級パンクバンド、ザ・キルジョイズの映像まで収録された貴重な1本。", "1977-01-01", "45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦争と青春", "戦争と青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ヒューマニズムに裏打ちされた数々の名作で知られる今井正が、昭和20年３月10日未明の東京大空襲を背景に、戦争によって運命をほんろうされた一人の女性の姿を描いた早乙女勝元原作を映画化。工藤夕貴が現代の女子高生と、戦争の傷跡を引きずる叔母の青春時代の二役を熱演している。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博奕打ち", "博奕打ち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "鶴田浩二扮する賭博師が、イカサマ博奕で素人衆を騙しては、店や財産を巻きあげている悪玉ヤクザと賭博で対決する。悪玉側の賭博師に扮した小池朝雄がもうけ役。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CALL OF THE WILD", "野性の叫び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1896年のアラスカ。犬ゾリで郵便を運ぶ仕事をするジョン。９匹の犬のうちバックというシェパードは、特に屈強で優れた犬だった。ある日ジョンが仲間とともに金鉱を求めて山奥に入ったとき、バックは狼の声に野性の本能を呼びさまされ、その群れに交じっていく。シェパード犬を主人公にしたJ・ロンドンの有名小説の映画化。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊賀の影丸", "伊賀の影丸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "横山光輝の同名マンガを映画化。百地三太夫の遺言によって、徳川家康を守る影丸に松方弘樹。明智光秀の配下で、山城新伍扮する、阿魔野邪鬼を筆頭とする、甲賀七人衆。その戦いを描いている。のちのTV『仮面の忍者・赤影』の原型とも言える、少年向け活劇。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Seul contre tous", "カノン", "", "", "http://image.eiga.k-img.com/images/movie/1320/315x210.jpg?2435", 93, "パリ郊外で馬の肉を売っていた男。彼は自分の口のきけない娘が強姦されたと勘違いして労働者を刺し殺し、投獄される。出所後、彼はバーのウェイターの仕事につき店のマダムの愛人となる。男の子供を妊娠したマダムは人生をやり直そうともちかけ、それを受け入れた男は施設に入れられた娘に別れを告げて北の町リールへ旅立つ。月日は流れ、今、再び男は娘の待つパリへ向かっている。目指すは娘の母親が彼女を身籠った“未来ホテル”だ……。", "2000-09-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妻二人", "妻二人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "作家を志していた柴田健三は、愛人の順子と別れ、雑誌社の娘・道子と結婚した。ある夜、何年かぶりに順子と会った彼は、彼女が昔の自分と同じように作家志望の男・小林を養っていることを知る。一方、順子と健三の関係を知った小林は、それをネタに道子を脅迫するのだが……。P・クェンティンの『酔いどれ波止場』を新藤兼人がシナリオ化、ミステリー趣向で迫る増村監督の異色作。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門捕物帖 まぼろし燈篭の女", "右門捕物帖 まぼろし燈篭の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "むっつり右門の活躍を描く“右門捕物帖”もの。事の発端は、浪人の惨殺事件。さっそく、捜査を開始した右門は、３ヵ月前の藩金盗難事件が関係ありとにらみ、当事者・甲斐達也を調べ始める。そして、達也の隠れ家を密告してきた易者・南雲堂に不審を抱き、その行動に目を光らせるのだった……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAIT UNTIL SPRING，BANDINI", "ウィンター・テイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "自由を求めてアメリカに渡ったイタリア移民一家の生活を描いた作品。レンガ工として働くスベボは、妻と３人の息子を抱え、クリスマスにパン一つ買うお金もない。やがて彼は金のため富豪の未亡人と付き合うようになり、その誘惑に負けて安楽な生活に溺れていく。", "1989-01-01", "37,2,4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野良猫ロック セックス・ハンター", "野良猫ロック セックス・ハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "米軍基地の町には、マコをリーダーとするズベ公グループ、バロン率いるイーグルス、混血児たちのグループらがたむろしていた。混血児・数馬は10年前に別れた妹を捜して、この町に立ち寄る。一方、バロンは姉を黒人兵に犯されたことから、混血児を憎んでいて、混血児狩りを実行に移す。バロンは数馬の妹を犯し、数馬を挑発する。翌日、復讐の鬼と化した数馬とバロンは、ついに対決の時を迎える。長谷部はシャープな構図やカッティングを駆使して、アメリカの半植民地的な日本の基地の町での憎悪と差別を摘出していく。二人の対決シーンではためく日の丸が空しい。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜明けのうた", "夜明けのうた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "作曲家の愛人を持つ役者・緑川典子のもとに、新作映画「夜明けのうた」の脚本を持って、プロデューサーと脚本家が出演交渉にやって来る。しかし、その内容は彼女と作曲家との情事に酷似していた。彼女は怒って出演を断わるが、ふとしたことから純情なカップルに出会って……。蔵原＝浅丘コンビによるメロドラマ。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POURQUOI PAS！", "彼女と彼たち —なぜ、いけないの—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "パリ郊外の一軒家に住む、男二人に女一人の３人組。女が唯一の稼ぎ手で、たくましき中年男が家事いっさいを担当。そして彼らは一つベッドに眠る……。３人のアブノーマルな共同生活を、ごく自然にリアルに描写。異色な設定ながら、女流監督らしい細やかな演出に好感の持てる作品。", "1978-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Great Gatsby", "グレート・ギャツビー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE IRON CURTAIN", "鉄のカーテン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "“鉄のカーテン”という言葉が流行した時代を背景に制作された作品で、原子爆弾の秘密をめぐって繰り広げられる各国スパイの暗躍を、実話に基づいて描いたサスペンス・ストーリー。暗号係としてカナダのソ連大使館に勤務する主人公グーゼンコ。彼は、この自由と平和の国で妻と幼い息子とともに幸せな生活を送るうち、祖国のスパイ活動に疑問を持ち始める。そんなある日、モスクワへの帰還命令を受けたグーゼンコは、意を決して諜報関係の重要書類をたずさえ、家族を伴いカナダ政府に出頭しようとするのだが……。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("X —エックス—", "X —エックス—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "人気漫画家集団CLAMPのコミックを映画化。地球滅亡がささやかれる1999年。人類を滅ぼし自然再生を目指す地の龍と、結界を張ることで滅亡を防ごうとする天の龍が、地球の運命を握る少年“神威”を巡って激しい攻防を繰り広げていく。主題歌をX JAPANが手掛けたことも話題になった。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES METAMORPHOSES DU CHOEUR", "合唱ができるまで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "パリのアマチュア合唱団がコンサートに出演するまでの練習風景を追った音楽ドキュメンタリー。アマチュア合唱団を指揮するクレール・マルシャンは、教会のコンサートに向けてレッスンを開始。ユニークな発声法やオーケストラを交えたリハーサルなど、その真摯な姿から音楽に対する愛情が伝わってくる。そんな彼女の指導の下、大人から子どもまで、実にさまざまな人々の歌に対する純粋な気持ちが、やがてひとつのハーモニーへと昇華していく様が深い感動よ呼ぶ。", "2004-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TENUE DE SOIRE´E", "タキシード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「美しすぎて」「メルシー・ラ・ヴィ」のB・ブリエ監督が、男と女の愛について、猥雑に、そして切なく語った異色のラブ・ストーリー。娼婦のモニクとそのヒモ、アントワーヌの間に割り込んできた自由奔放な男、ボブ。男と女の関係だけにとどまらず、男と男の愛情関係も含む、奇妙なトライアングルがこの３人の間に形成されていく。本国フランスでは、公開されるやセンセーショナルな評判を呼び大ヒットを記録した。このきわどいストーリーを支えているのは、ブリエ監督の出世作、「バルスーズ」で共演したG・ドパルデューとミュウ・ミュウ。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高原児", "高原児", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ヒーローを慕う浅丘ルリ子のヒロイン、ライバルの殺し屋など、ほとんど同じ小林旭の“渡り鳥”シリーズを思わせるアクション。看護婦の伸子は、射撃の名手・健次に惚れていたが、彼の態度が煮えきらないので、ほかの男と婚約する。一方で、伸次の姉夫婦が経営する牧場を悪玉が狙っていた。その危機を知った健次は、牧場に駆けつけて……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Death Proof", "デス・プルーフ in グラインドハウス", "", "", "http://image.eiga.k-img.com/images/movie/34140/315x210.jpg?1483", 113, "鬼才Q・タランティーノ監督が、殺人鬼とセクシー美女軍団の激闘をカーアクション満載で描いたスラッシャー・ムービー。テキサスの人気女性DJジャングル・ジュリアは、女友達と一緒にお気に入りのバーを訪れる。しかし彼女たちの背後には、車を凶器に美女を狙う恐ろしい殺人鬼スタントマン・マイクが忍び寄っていた。それから14カ月後、今度はスタントウーマンのゾーイたちがマイクの標的となるが……。", "2007-09-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flight of the Intruder", "イントルーダー／怒りの翼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　1972年、ベトナム戦争の只中、アメリカ海軍のA6爆撃機イントルーダーのパイロット、グラフトン大尉は爆撃を禁止されている重要拠点ハノイへの攻撃を訴える。やがて彼はコール少佐とともにハノイのミサイル基地を爆撃。しかしその独断行為によって、ふたりは軍法会議にかけられることに……。高性能を誇るイントルーダーのパイロットの戦闘を、戦友や上官たちとの友情、そして戦争で夫を亡くした女性との恋を絡めながら描く。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sweeney Todd: The Demon Barber of Fleet Street", "スウィーニー・トッド　フリート街の悪魔の理髪師", "", "", "http://image.eiga.k-img.com/images/movie/34270/315x210.jpg?2105", 117, "トニー賞8部門に輝くスティーブン・ソンドハイム作の同名ミュージカルをティム・バートン監督＆ジョニー・デップ主演で映画化。19世紀末のロンドン。色好みなターピン判事に妻子を奪われた上に無実の罪を着せられ、流刑にされてしまった理髪師ベンジャミン・バーカーが、15年ぶりにロンドンへと舞い戻ってきた。スウィーニー・トッドと名を変えた彼はターピンへの復讐を開始する。共演はヘレナ・ボナム・カーター、アラン・リックマン、サシャ・バロン・コーエン、ティモシー・スポールら。", "2008-01-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("告訴せず", "告訴せず", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "衆院選に立候補した木谷は、派閥を裏切り、反対派のボスから３千万円を調達する。ところが金の運搬役が、その金を横領して姿をくらましてしまう。告訴したくても告訴できない、社会の仕組みを鋭く突いたブラック・コメディ。主役の金に操られていく男を青島幸男がコミカルに演じている。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEN THE BOUGH BREAKS", "犯罪心理捜査官", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "女性犯罪心理分析官と犯人との対決が描かれたサイコ・サスペンス。７月４日、雨が降りしきる独立記念日の夜、切断された７つの手首が発見される。その手のひらには１から14までの謎の数字が記されていた。女性犯罪心理分析官マクレアーは、捜査の過程の中で、精神病院の患者である自閉症の少年と面会する。手首に異常な執着を見せる少年は、病院から外に出たことがないにもかかわらず、事件の手掛かりを知っていた。その後、手首の身元も判明し、犯人も逮捕され、事件は解決したかに見えたが……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("麗霆”子〈レディース〉MAX", "麗霆”子〈レディース〉MAX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "女暴走族の姿を描いたシリーズ第３作。組織の崩壊と再生を軸に４人の少女が巻き込まれる一夜の騒動を描く。４代目総長・桐生有里が率いる房狂会・麗霆”子〈レディース〉は、カツアゲ、シャブ中毒が横行し、腐り切っていた。黒崎魔子、北沢鮎美、朝岡久美子、夏樹由香里の４人組は、総長の妹・朱里が魔子の名で恐喝したことに端を発し、上層部と対立。４人は麗霆”子を追放され、それぞれ夜の町に繰り出すが……。主演は、人気アイドル・グループMAXの４人が務めている。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BARFOOT CONTESSA", "裸足の伯爵夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "舞台はマドリード。偶然のことから映画に出演し、大スターになった裸足のダンサー、マリアは、イタリアの伯爵に見初められ結婚する。が、伯爵は性的不能者だった。やがて、彼女は不義の子を身ごもる……。自由奔放な女がたどる悲劇的な生涯を巧みな語り口の回想形式で描いた力作。主題歌『裸足のボレロ』は大ヒット。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gerry", "ジェリー", "", "", "http://image.eiga.k-img.com/images/movie/1400/315x210.jpg?3304", 103, "2人の男が砂漠へ迷い込み、ひとりだけが帰ってきたという実際の事件を元に、「エレファント」のガス・バン・サント監督がマット・デイモン、ケイシー・アフレックとともに脚本を考案、2人が砂漠で彷徨うさまを映していく。03年ニューヨーク批評家協会賞撮影賞を受賞した撮影は、「エレファント」「小説家を見つけたら」でもバン・サントと組み、デビッド・フィンチャー監督の「ゲーム」を手掛けたハリス・サビデスが担当。", "2004-09-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SONG　OF　THE　STORK", "コウノトリの歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "史上初となるベトナムとシンガポールの合作で作り上げた、ベトナム戦争映画。一人の従軍カメラマンが回顧する形式で、若きベトナム兵たちそれぞれの人生が語り明かされる。「夏至」「シクロ」のQ・ハイらベトナムの役者たちの熱演が印象的。ベトナム戦争をアメリカ側から捉えるのではなく、ベトナム側からきちんと見つめ、描き切った意義深い１本。2000年、サイゴン解放から25年目の前日、映像作家トランは従軍カメラマン時代を回顧し、当時の若きベトナム軍兵士たちを思っていた。恋人が気になって訓練に身が入らない詩人のバンや、サイゴンで大佐の娘に近づいた工作員ラム……。ホーチミンルートに沿って南下し、ジャングルを徒歩で移動する中、飢えや病気が部隊を襲い、兵士は次々と倒れていく。", "2001-01-01", "60,54", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cartola-Música para os olhos", "カルトーラ　サンビスタの物語", "", "", "http://image.eiga.k-img.com/images/movie/54780/315x210.jpg?2419", 88, "サンバ界の巨匠として世界中から愛され続けているブラジルの作曲家・歌手カルトーラを題材にした音楽ドキュメンタリー。長い不遇の時代を経て、65歳にしてようやく初のオリジナルレコードを発表したカルトーラ。名門サンバチーム「マンゲイラ」の創設者としても知られる彼の生涯を、生前の本人へのインタビューや彼を知る大御所アーティストたちによる証言、さらに貴重な演奏シーンなどを交えながら描き出す。", "2009-10-03", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("網走番外地・悪への挑戦", "網走番外地・悪への挑戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "九州が舞台。鬼寅が働く不良少年保護施設を手伝うことになった橘真一は、暴力団・門馬組が少年たちをそそのかしているのを知り立ち上がる。ギャング映画の名作「汚れた顔の天使」を見事にパクったラスト・シーンが見もの。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BERETTA’S　ISLAND", "ゴリラ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "元ミスター・ユニバースであり、ボクシング・チャンピオンでもあったF・コロンブの製作・脚本・主演によるバイオレンス・アクション。元インターポールの秘密工作員フランコ、通称ベレッタが故郷のサルディニアにはびこる麻薬組織を壊滅するため任務に復帰し、激しい闘いを繰り広げていく。A・シュワルツェネッガーが彼の友人役で出演している。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUSIME　SI　POMAHAT", "この素晴らしき世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "チェコがナチス支配から解放されるまでの厳しい歳月、人々が生き抜いていく姿を笑いと涙を交えて描いた感動作。子宝に恵まれないヨゼフとマリエの家に収容所を脱走したユダヤ人青年ダヴィトが転がり込んでくる。マリエに横恋慕するナチス信奉者のホルストは、隠し事をしているような夫婦の態度に疑いを抱く。", "2000-01-01", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄の祭典", "地獄の祭典", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "宍戸錠がライフルの名手に扮して、愛用の銃を奪ったヤクザ一家に挑戦する日活アクション映画。やがて彼は、東南アジア一帯に君臨する麻薬王スペードをめぐる三つ巴のヤクザ同士の争いに巻き込まれていく。目にも止まらぬ鮮やかな宍戸のライフルさばきが見ものだ。原作は遠山照彦の同名小説。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAZE〈マゼ〉", "MAZE〈マゼ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "高知を舞台にした、感動のヒューマン・ドラマ。両親を失った上、病に倒れた少年と、その祖父が深い絆で結ばれていく姿を描く。無骨な漁師の祖父を味わい深く演じた実力派俳優・蟹江敬三だが、意外にも本作が映画初主演作となった。交通事故で両親を失った裕太は、高知で漁師をする祖父・弦次郎のもとで暮らし始める。周りの励ましもあり、次第に立ち直る彼だったが、その矢先に完治したはずの病気が再発してしまう。", "2006-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏目漱石のこころ", "夏目漱石のこころ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "日置にとって野淵先生は最も尊敬する人物であったが、奥さんとの間に漂う何かしら暗い影が気がかりだった。ある日、先生は突然の自殺を遂げ、その遺書により日置は先生の孤独の真相を知る……。文豪・夏目漱石の名作を市川崑が忠実に映画化。鋭い心理描写が冴え、俳優たちの緻密な演技が強い印象を残す。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOHOSHINKI THE LIVE a-nation 2006-2009", "TOHOSHINKI THE LIVE a-nation 2006-2009", "", "", "http://image.eiga.k-img.com/images/movie/55550/315x210.jpg?2835", 0, "韓国の人気アイドルグループ「東方神起」のライブ映像を映画館で上映するプロジェクトで、「TOHOSHINKI THE LIVE 2009」に続く第2弾。所属事務所エイベックスが主催する夏の野外音楽フェスティバル「a-nation」でのパフォーマンスを中心に、2006～ 09年にかけて行ったライブパフォーマンスを一挙公開。", "2010-06-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("打工皇帝／WORKING　CLASS", "ツイ・ハークのチャイニーズ・ファースト・ラブ・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "厳しい管理で悪名高いラーメン工場で働くヤン、サニー、アーミンの３人組。ある日ヤンは、工場経営者の娘とは知らずに美しいアミーに恋をした。彼女をめぐって３人が繰り広げる珍騒動を描くラブ・コメディ。アミー役にJ・ウォンが扮している。1986年香港電影博人気投票第一位に輝いた話題作。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INNERSPACE", "インナースペース", "", "", "http://ecx.images-amazon.com/images/I/51P24MED24L._SL210_.jpg", 120, "S・スピルバーグが「トワイライトゾーン／超次元の体験」「グレムリン」に続いて三たび、J・ダンテ監督と組んだSFコメディ。“ヒューマン・スモール計画”の成功を担ってウサギの体内に入るはずだったパイロット、タックが、スパイの急襲によって、さえない若者ジャックの体内に紛れ込んでしまったことから始まる冒険を描いた一編。体内を映像化したSFXの面白さに加え、はからずもめぐり会う(？)二人の珍妙なやりとりも大いに笑わせる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("孔雀／KUJAKU", "孔雀／KUJAKU", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ウォン・カーウァイ作品のカメラマンとして有名なC・ドイルの監督デビュー作。沖縄の孤島で生まれた青年アサノが、香港のゲイ・バーに流れ着く。そのバーの孔雀の羽を思わせるソファーが気に入った彼は、英国人ゲイのオーナー、ケビンや中国人娘スージーとの奇妙な共同生活を過ごすことになる。さすらいの人生を送ってきたドイル自身を思わせる主人公を演じるのは浅野忠信。記憶に囚われた主人公の心象風景をビジュアル化した映像が、夢幻的な美しさを放っている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPITE MARRIAGE", "キートンの結婚狂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "ある洋服店でアイロン係をしているエルマー青年は、美しい舞台女優トリルビーに夢中。恋の病が高じて、最近では彼女の出ている劇場でエキストラをしているほど。そんなある日、男と別れて自暴自棄のトリルビーが気まぐれから彼と結婚したいと言い出したから、さぁ大変。有頂天の彼は、夜ごと酒に溺れる新妻との新婚生活を始めるが……。B・キートン最後のサイレント映画。", "1929-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("執行先鋒／RIGHTING WRONG", "検事Mr.ハー 俺が法律だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "人気アクション・スター、ユン・ピョウが格闘もできる正義の検事に扮して、麻薬密売にかかわる悪を退治する痛快アクション映画。170メートル上空から海に飛び込むなど、危険度200パーセントの超過激アクションが見ものだ。コリー・ユエン監督が出演も兼ねている。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Harry Potter and the Deathly Hallows: Part I", "ハリー・ポッターと死の秘宝 PART1", "", "", "http://image.eiga.k-img.com/images/movie/53520/315x210.jpg?2592", 146, "J・K・ローリングの児童小説を映画化した大ヒットシリーズ「ハリー・ポッター」の最終章前編。ヴォルデモートの復活により魔法界に暗雲がたちこめるなか、打倒のカギとなる“分霊箱”を探す旅に出たハリー、ロン、ハーマイオニー。だが、困難を極める旅の途中で3人はついに仲間割れをしてしまう。一方ヴォルデモートと死喰い人たちは、魔法省やホグワーツにも着々と魔の手を伸ばしていく。監督は第5作、第6作に引き続きデビッド・イェーツが担当。", "2010-11-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新二等兵物語・めでたく凱旋の巻", "新二等兵物語・めでたく凱旋の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "シリーズの最終作。中部部隊に入隊した横田番作は３ヵ月後、初年兵教育係を命じられる。横田は初年兵のなかに親友の花岡茶吉を見いだす。やがて横田らは中支戦線に送られるが、方向を誤った中隊は敵に囲まれ進退極まってしまう……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("99 44/100% Dead!", "殺し屋ハリー／華麗なる挑戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　敵対する二大組織のボス、フランクとエディはそれぞれ凄腕の殺し屋を雇う。フランクは10万ドルでハリーを、そしてエディは、特殊な義手をつけた“爪”ことザッカーマンを味方に引き入れる。彼らの戦いが繰り広げられる中、ハリーは恋人バフィを誘拐されてしまう。助っ人を引き連れて救出に向かうハリーだったが……。ポップな演出が冴えるコミック調の異色アクション。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAMES GALANTES", "宮廷円舞曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "戦争中だというのに女のことしか考えない田舎貴族のブラントームは、40歳になって、人生のすべてを女のために捧げることを決意する。彼は宮廷の友人から主人に至るまですべてを愛し、さらに彼女たちとの情事を永遠に残すために、作家になることを決意、かかわった女たちのことについて書き始める。16世紀後半の実話を、タケラ監督が女性への敬意を込めて綴るオマージュ。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("玄海遊侠伝・破れかぶれ", "玄海遊侠伝・破れかぶれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "北九州の若松を舞台に、朝鮮から帰って来た無軌道な男・吉田磯吉が、地元の悪人たちを相手に大暴れする任侠もので巨匠マキノ雅広が手掛けた痛快な一編。特に安田道代がピストル片手にタンカをきるシーンが印象的。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鴬", "鴬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "原作は『警察日記』で知られる伊藤永之介の同名小説で、ここでもやはり田舎の警察署が舞台となっている。演出には豊田四郎があたっており、警察署内で起こる村人たちと署員のユーモラスなやりとりを、限定された空間の中だけで描くという室内劇的ともいえる手法で、見ごたえのある佳作に仕立て上げている。東北のある田舎町の警察署に、老女が昔、曲馬団に売ってしまった養女の捜索願いを出しにくる。署には狂言泥棒の娘や鶏泥棒をした男、もぐりの産婆などが調べを受けている。老女は業を煮やして祈祷師の所へ。翌日、署に陣痛に苦しむ女が運ばれてくるが、この女こそが老女の捜していた養女だった。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE ハウス", "HOUSE ハウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「時をかける少女」「漂流教室」などファンタジー映画の第一人者・大林宣彦監督の劇場映画デビュー作。７人の少女が生き物のような“家”に、次々と食べられてしまうというホラー・ファンタジーを、様々な特撮を使って見せてくれる。ソフト・フォーカスを用いたCF的映像、実写とアニメの合成など、以後の大林映画につながるほとんどすべての技術が、この作品に集約されている。テーマ主義に走り、映像への配慮があまりなされていなかった当時の日本映画の中で、遊び心のあるエンターテインメント映画として異彩を放っていた。池上季実子、松原愛のヌード・シーンもあり、サービス精神も満点。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トロピカルミステリー 青春共和国", "トロピカルミステリー 青春共和国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "若者たちが自主生活を満喫できるパラダイスということで、若者の間で話題となっている島“青春共和国”では、実は独裁者の手で厳しい洗脳が行われていた……。赤川次郎原作の軽いタッチの青春ミステリー。安田成美が好演し、主題歌も歌っている。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猿飛佐助 闇の軍団", "猿飛佐助 闇の軍団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "「新・影の軍団」の宮坂武志監督が、松方弘樹を主演に据えて描く本格忍者アクション。時は慶長19年。大阪城が“大阪冬の陣”により壊滅状態の豊臣勢は、猿飛佐助率いる“闇の軍団”に徳川家康暗殺を命じるが、計画を阻む巨大な敵が現れて……。縦横無尽に、斬って斬って斬りまくる、佐助の大暴れは爽快！　ビデオ・DVDタイトルは「猿飛佐助　闇の軍団　天の巻」。", "2004-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の詩女　ゴティックメード", "花の詩女　ゴティックメード", "", "", "http://image.eiga.k-img.com/images/movie/56090/315x210.jpg?1989", 0, "", "2012-11-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The 8 Sentiments", "キュレーター、ジョンフンさんの恋愛日記", "", "", "http://image.eiga.k-img.com/images/movie/56770/315x210.jpg?2112", 90, "ソウルの有名ギャラリーにキュレーターとして勤めているジョンフンは、有名画家チョンの展示を準備するために釜山を訪問。そこで以前から知り合いだったウンジュに会い、ふとしたことで心を奪われてしまう。ソウルに戻ったジョンフンは恋人に別れを告げ、ウンジュと付き合うようになるのだが……。男女の気まぐれな恋愛感情をつづったドラマ。2011年「真！韓国映画祭」にて上映。", "2011-05-28", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太の海底鬼岩城", "ドラえもん のび太の海底鬼岩城", "", "", "http://ecx.images-amazon.com/images/I/21ZFV8525PL._SL210_.jpg", 95, "ドラえもんの提案で、太平洋の海底でキャンプをすることになったのび太たち。ピンチを海底人のエルに救われ仲良くなるが、そんな折、バミューダの海底にある鬼岩城が動き始めた。幻の国、アトランティスの防御システムである鬼岩城が地球を滅ぼそうとしているのだった。ドラえもんたちは危機を救うことができるのか？　巨大イカとの攻防など、見どころも多い１本だ。", "1993-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こつまなんきん", "こつまなんきん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "大阪・河内地方に生まれ育った女の奔放な愛欲生活を描いた今東光原作の映画化。教祖商売から易者、寄席の舞台に立つ芸人と次々と職を変えて成長していく河内の女・お市。その先々で、男からの誘いを受けては裏切られていく。骨太の女性映画。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alice in Wonderland", "アリス・イン・ワンダーランド", "", "", "http://image.eiga.k-img.com/images/movie/53230/315x210.jpg?3669", 109, "ティム・バートン監督がルイス・キャロルの「不思議の国のアリス」「鏡の国のアリス」を元にアリスの新たな冒険を描くファンタジー大作。19歳に成長したアリスは、幼い日に地下世界を冒険したことを忘れていたが、ある日、洋服を着た白ウサギを目撃し、その後を追って再び地下世界へ。するとそこは独善的な赤の女王に支配されていて……。タイトルロールに新人ミア・ワシコウスカ。共演にジョニー・デップ、アン・ハサウェイ、ヘレナ・ボナム・カーターほか。", "2010-04-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mother", "母なる証明", "", "", "http://image.eiga.k-img.com/images/movie/54360/315x210.jpg?1976", 129, "「殺人の追憶」「グエムル／漢江の怪物」のポン・ジュノ監督が手がけた3年ぶりの長編。国民的人気女優のキム・ヘジャ、5年ぶりの映画出演となるウォンビンが親子を熱演する。貧しいながらも幸せに暮らしていた親子であったが、ある日1人息子が警察に拘束されてしまう。殺人事件の容疑者にされてしまった息子の無実を信じ、孤立無援の母は悲しむ間もなく、たった1人で真相に迫ろうとするのだが……。", "2009-10-31", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛の讃歌", "愛の讃歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "フランスの作家、マルセル・パニョルの小説『ファニー』を翻案した人情喜劇。瀬戸内海の日向島を舞台に、島を出た青年の子供を宿した娘・春子と、島民の温かい交流をほのぼのタッチで描く。撮影・音楽など、ほとんどがのちの“男はつらいよ”シリーズのスタッフ。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WINNING RUN", "ウイニングラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "F-1グランプリとロードレース、モトクロスの模様を収めたドキュメンタリー。レースにつきものの凄絶をきわめるクラッシュ・シーンを盛り込んだスリリングな一編。ラウダをはじめ名ドライバーが総出演。ニューマンもチラリと顔を出す“オマケ”がうれしい。", "1983-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("坂の上のマリア", "坂の上のマリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "高齢者の生活と地域コミュニティのあり方を問う社会派ドラマ。車道から数百メートルもの狭い路地と階段を登ったところにある一軒家で、一人暮らす老女マリア。ある日、遊びに来た孫のために買ったスイカを階段で落とし、自らも転倒してしまう。マリアは、この事故をきっかけに平坦な新興住宅地に引越して息子夫婦と同居をする決心をするが、引越しが近づくにつれ、この土地の何気ない風景が新鮮に映り始め……。老女・マリアの心の変遷をリアルに描くために撮影の何日も前から現地に住み込んだことが功を奏し、地元の人々も多数出演。フィクションでありながら、ドキュメンタリーのリアルさを兼ね備えたドラマに仕上がった。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIN　REMITENTE", "差出人のない手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ナント３大大陸映画祭でグランプリを奪取したメキシコ産の人間ドラマ。孤独な日々を送る老人アンドレスのもとに、差出人名のないラブレターが届く。それは同じアパートの上階で暮らす女性カメラマン、マリアナが悪戯で投函したものだったが、本気になったアンドレスは探偵事務所に依頼して、相手を捜し始める。ところが悪質な探偵は売春婦テレサを差出人に仕立て上げ、アンドレスは彼女と真剣に恋に落ちてしまう……。平凡な人生がひょんなことからズレて亀裂が生じていく。その悲喜こもごもを、新鋭C・カレラが淡々と描く。", "1995-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD OR ALIVE２・逃亡者", "DEAD OR ALIVE２・逃亡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "異才、三池崇史と竹内力＆哀川翔のトリオが再結集した異色アクション。１作目で男の対決を演じた竹内と哀川が、ここでは友情で結ばれた殺し屋を熱演する。暴力団組長の暗殺現場で偶然、再会した幼なじみの殺し屋ミズキとシュウ。幼少期を過ごした孤島の孤児院に逃亡した彼らは、久しぶりに平和な時を過ごす。やがてミズキの提案で海外の病気の子供たちにワクチンを送ろうと、二人は手を組んで殺しの仕事に精を出すが……。バイオレンスはもちろん、天使の羽をつけた主人公たちの幻想的な描写や、唐突なギャグなど、観る者の意表を突く三池演出が冴える。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿佐ヶ谷ベルボーイズ", "阿佐ヶ谷ベルボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 41, "PFFアワード2004でグランプリに輝き、各国の映画祭で大絶賛された「ある朝スウプは」の広末哲万と高橋泉が作り上げた一作。交通事故で一人奇跡的に生き残ってしまった男の複雑な心境が綴られる。人間の深層心理に深く分け入る好編だ。会社員4名を乗せた車が事故に遭遇し、3名が死亡。同日、葬儀が執り行われる。そこへ生き残った川上が姿を現し……。", "2005-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Killer Elite", "キラー・エリート", "", "", "http://image.eiga.k-img.com/images/movie/57230/315x210.jpg?2761", 117, "ジェイソン・ステイサム、クライブ・オーウェン、ロバート・デ・ニーロが共演し、元SAS（Special Air Service＝英国陸軍特殊部隊）隊員のラヌルフ・ファインズによるベストセラー小説を映画化したアクション。殺し屋稼業から足を洗ったダニーのもとに一通の手紙が届き、師であり相棒でもあったハンターが、オマーン首長の息子3人を殺した男たちへの報復という依頼に失敗し、捕えられたことを知る。ハンターを助けるため依頼を引き継いだダニーだったが、ターゲットとなる男たちはみな、国家レベルの秘密結社「フェザー・メン」に固く守られた元SASの兵士たちだった。", "2012-05-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハワイの夜", "ハワイの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "日華事変の頃、日本からスポーツ親善使節としてハワイに派遣された若者が、現地の日系２世の娘と恋に落ちる。日米開戦が二人の仲を引き裂くかにみえたが、やがて運命の力によって……。鶴田浩二主宰の新生プロにより、戦後初の本格的ハワイ・ロケを敢行した意欲作。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南国土佐を後にして", "南国土佐を後にして", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ペギー葉山が歌い大ヒットを記録した同名曲にあやかり、斎藤武市監督、小林旭主演で映画化された歌謡アクション。刑務所を出所したヤクザの原田譲司は、故郷の高知へ帰り恋人と堅気に生きようとするが、恋人に横恋慕する地元ヤクザのボスにことあるごとに痛めつけられ、またヤクザの世界に舞い戻る。しかし再び恋人のために出直そうと決意し、警察に自首する。この作品は封切りで予期せぬほどの興行成績を収め、マイトガイ・小林旭は、石原裕次郎と並ぶスターの地位を獲得した。また、小林旭の全盛期を代表する“渡り鳥”と“流れ者”の両シリーズの原型となった点でも重要な作品。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪奇！！幽霊スナック殴り込み！", "怪奇！！幽霊スナック殴り込み！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "独特な作風で人気を誇る漫画家・杉作J太郎の初監督となる１本。幽霊３人が居つくスナックを営むユキは、服役中の夫を暴力組織から守るため、女の武器を使って司法当局の内偵調査に協力するが……。夫を救うために体を張る人妻の奮闘を、ホラー要素を絡めた奇抜な展開で描く。豪華キャストやクレイジーケンバンドの主題歌など、杉作の豊富な人脈により実現した異業種コラボも楽しい。", "2006-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN OF THE SEVEN", "続 荒野の七人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "人気作の続編。前作で村娘と結ばれ、平和な農村生活を送っていたチコが暮らす村に、再びその辺り一帯を勢力下に納めんとする野盗が現れる。知らせを受けたチコの昔の仲間、クリスとヴィンは、さっそく村へ急行すべく仲間集めを開始する。たちまち、それぞれひと癖もふた癖もある男たちが集まって来るが……。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El Nino Pez", "フィッシュチャイルド　ある湖の伝説", "", "", "http://image.eiga.k-img.com/images/movie/54860/315x210.jpg?2343", 96, "監督のルシア・プエンソが23歳のときに書き下ろした、近親相姦、同性愛、格差社会、尊属殺人、児童買春などの過激なテーマを扱った同名小説の映画化。アルゼンチン・ブエノスアイレスの高級住宅地に住むララと、その邸宅で働くパラグアイ人メイドのラグア。生まれも育ちも異なる2人の少女は秘かに愛し合い、いつかラグアイの故郷にあるイポア湖畔に住むことを夢見ていた。ある日、イポア湖を訪れたララは、そこでラグアイの怪しい過去を知る。", "2009-01-01", "22,2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOMBE´S DU CIEL", "パリ空港の人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "訳あって空港に住み着いてしまった人たちの、おかしくてちょっぴりせつない物語。図象学者のアルチュロは、モントリオール空港で居眠りをしている間に、パスポートや財布など一切合切盗まれてしまう。唯一残された搭乗券と、シャルル・ド・ゴール空港に迎えに来ているはずの妻スサーナを頼りに飛行機に乗り込んだのだが、着いてみたところでパスポートなしでは入れてもらえるはずがない。結局、アルチュロは出発地モントリオールから身分を証明する書類が届くまで、トランジット・ゾーン（外国人用処理区域）に留まることに。そしてそこには、様々な理由で入国できない外国人たちが何人もいたのだった。アルチュロ役のJ・ロシュフォールが哀れな住人たちの心の拠り所である中年男を魅力的に演じている。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山のあなた　徳市の恋", "山のあなた　徳市の恋", "", "", "http://image.eiga.k-img.com/images/movie/53300/315x210.jpg?2458", 94, "山の温泉地で働く目の不自由な按摩・徳市は、東京から来た美しい女性・美千穂の療治をすることに。美千穂に淡い恋心を抱く徳市だったが、彼女は宿屋で起こった盗難事件の犯人として疑われてしまい……。名匠・清水宏監督が38年に手掛けた「按摩と女」を、「茶の味」の石井克人監督が草なぎ剛主演でリメイク。清水監督によるオリジナル脚本をほぼそのまま使用し、台詞から構図まで忠実に再現している。共演に加瀬亮、堤真一ほか。", "2008-05-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE", "ガバリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "息子が行方不明になり、その後、妻と離婚した作家のロジャーは、叔母が自殺した館に越して来て小説を書き始める。しかし、その館は様々なモンスターが出没するお化け屋敷だった。息子が彼らに捕らえられていることを知ったロジャーは、敢然とモンスターたちに挑戦する……。ジェットコースター的展開のノンストップ・ホラー。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釜ヶ崎極道", "釜ヶ崎極道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "釜ヶ崎の極道・島村清吉は、広域暴力団・八ツ藤組とのいざこざが絶えず、警察は両組の解散を迫る。島村は組を解散し芸能社を旗揚げするが、八ツ藤組がことあるごとに横ヤリを入れるのに業を煮やし、兄弟分の死をきっかけに猛然と殴り込みをかけるのだった。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES SEPT PE´CHE´S CAPITAUX", "新・七つの大罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "P・ド・ブロカの「大食い」J・L・ゴダールの「怠け」R・ヴァディムの「見栄っぱり」C・シャブロルの「貪欲」など、個性豊かなフランス映画界の異才たちが一人ワンテーマずつ、聖書が説く７つの大罪をテーマに生み出したオムニバス編。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デス・トランス", "デス・トランス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「VERSUS〈ヴァーサス〉」「SHINOBI」などでアクション監督を担当してきた下村勇二が初監督に挑んだ新感覚アクション時代劇。戦乱の世を生きる男たちが、願いが叶う棺を巡って壮絶な死闘を展開する。リアルをとことん追求した生身のアクションは必見だ。開ければどんな願いごとも叶うと伝わる“棺”の存在を知ったグレイヴは、ある日、その棺の強奪に成功する。彼は棺を禁断の森で開けるために、行く手を阻む豪傑たちをなぎ倒していく。", "2006-05-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lean on Me", "ワイルド・チェンジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "　荒廃の一途をたどるニュージャージーの高校に、クレージー・ジョーとあだ名される校長クラークが赴任。その型破りな指導方法に父兄らから反発も起こるが、しかし生徒たちは次第にクラークを尊敬し始める……。熱血教師の実話を映画化。モーガン・フリーマンが熱演する感動の人間ドラマ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版MAJOR メジャー　友情の一球（ウィニングショット）", "劇場版MAJOR メジャー　友情の一球（ウィニングショット）", "", "", "http://image.eiga.k-img.com/images/movie/53310/315x210.jpg?1746", 0, "NHK教育テレビでもアニメ化された満田拓也の人気野球コミックを映画化。強豪“横浜リトル”との熱戦を制した“三船ドルフィンズ”だったが、エースで4番の吾郎はプロ野球選手の父親の移籍に伴い福岡へ引っ越すことになる。横浜リトル戦で負傷した吾郎の肩を気にかける母親の心配をよそに、福岡でも野球を始めようとする吾郎。父親は投球をしないことを条件に名門チーム入団を許すのだが……。吾郎がサウスポーになった理由が明かされる。", "2008-12-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤ちゃんと僕", "赤ちゃんと僕", "", "", "http://image.eiga.k-img.com/images/movie/54850/315x210.jpg?1975", 96, "ドラマ「プラハの恋人」「ファンジニ」などで注目を集める若手韓流スター、チャン・グンソクが映画初主演を果たしたハートウォーミング・コメディ。毎日トラブルばかり起こしている19歳の問題児ジュンス。息子のあまりの素行の悪さに困り果てた両親は、彼を懲らしめようとわずかな生活費だけを残して姿を消してしまう。そんな最悪な状況の中、ジュンスは突然現われた赤ん坊ウラムの世話までするハメになり……。", "2009-11-21", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャッカー電撃隊 8スーパーカー!!　時速300キロ", "ジャッカー電撃隊 8スーパーカー!!　時速300キロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "犯罪組織クライムと戦うサイボーグ部隊の活躍を描いた、石ノ森章太郎原作による特撮テレビシリーズの劇場版。国立保管金庫に収められた莫大な量の財宝を狙うクライムは、厚さ1メートルの超合金でできた扉を爆破するため、国際科学特捜隊が開発した新型爆弾・ジャッカーXを盗みだした。ジャッカーXの実験に立ちあっていた東は、執念の追跡を開始するが……。テレビ版の第7話「8スーパーカー!!時速300キロ」を再編集したもの。", "1977-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL SOLE NELLE PELLE", "ふたりだけの恋の島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "15歳の美少女リザは、19歳の神秘的な青年ロベルトと出会い恋に落ちた。しかし大会社を経営するリザの父親がヒッピー青年を認めるはずもなく、二人は自由と自然を求めて地中海へと旅立つ。無人島に漂着し愛し合う二人。だが幸せもつかの間、リザの父親が送った警察と軍隊が乗り込んできた……。悲劇の愛を描いたラブ・ストーリー。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘祭", "秘祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "石原慎太郎の小説を映画化。建設会社の若き社員、高峯はリゾート開発を進めるために沖縄の離島へ飛んだ。島民は一見、地上げに好意的だったが、不審な行動も目につく。やがて１年に１度の祭の日、高峯は恐ろしい事件に巻き込まれる……。南国ののどかな風景と儀式の神秘性、スリラー風の展開が混在した、不思議な味わいの異色ドラマ。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ATTACK　OF　THE　GIANT　MOUSSAKA", "アタック・オブ・ザ・ジャイアント・ケーキ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ギリシャの伝統料理“ムサカ”が人間を襲うという、ナンセンスでチープなホラー・コメディ。UFOからの光線を受けてムサカが巨大化。このモンスターは高熱の油で人を襲撃し、アテネをパニックに陥れる。この危機を回避すべく、ゲイの科学者が奔走。一方、UFO内のセクシーな宇宙人たちは、ムサカのなかに取り込まれた仲間を救おうとするが……。", "1999-01-01", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野獣の復活", "野獣の復活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ヤクザ稼業から足を洗い、堅気の生活を送っていた伊吹のもとに、かつての弟分が恋人とともにヤクザ組織から逃げてくる。しかし、弟分は殺され、伊吹は再び修羅場の中に身を投じていく。岡本喜八門下の、山本迪夫の監督デビュー作。任侠アクションを踏襲した丹念な作り。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("てぃだかんかん　海とサンゴと小さな奇跡", "てぃだかんかん　海とサンゴと小さな奇跡", "", "", "http://image.eiga.k-img.com/images/movie/54700/315x210.jpg?2936", 0, "世界で初めて養殖サンゴの移植・産卵を成功させた男の実話を、お笑いタレント・ナインティナインの岡村隆史主演で映画化。共演に松雪泰子、原田美枝子、國村隼ほか。「デトロイト・メタル・シティ」の李闘士男がメガホンをとる。故郷・沖縄で仲良く暮らす健司と由莉の夫婦は、気楽にはじめた飲食業で大成功を収める。しかし、沖縄のサンゴ礁が死滅しつつあることを知った健司は、店を閉め、サンゴの養殖に人生の全てを賭けることにする。", "2010-04-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA KERMESSE HE´ROI¨QUE", "女だけの都", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "17世紀初めのフランドルの小都市。ある日、スペイン軍から一夜の宿を借りたいという申し出があり、町中が大騒ぎとなる。乱暴なスペイン軍を知る町の人々が考えついた策とは……。大国を前にした小国の運命を握る妻君たちの活躍を描いた風刺コメディ。", "1935-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIESEL", "ディーゼル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "舞台は核戦争後の荒廃した近未来。狂気の建築家フィンシュが支配する地下都市には、放射能汚染によって地上から移り住んだ人々がいた。その中の一人、管理社会を逃れて自由を求める一匹狼ディーゼルと、フィンシュの殺し屋たちとの凄絶な戦いを描いたSFアクション。インディペンデント系の映像作家クレイマーが珍しく手掛けたSF。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乱れ雲", "乱れ雲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "女性映画の巨匠として名高い成瀬巳喜男監督の遺作。夫を交通事故で亡くした若い美貌の人妻と、その加害者である青年との純愛を描いたメロドラマの傑作である。主演は前年の「ひき逃げ」（成瀬巳喜男監督）や「紀ノ川」（中村登監督）で演技面での成長を高く評価された司葉子と、やはり成瀬の「乱れる」（1964）で新境地を開いた加山雄三。司の上品で繊細な美しさと、加山の明朗な個性の組み合わせは絶品で、そのたたずまいだけで切ない思いをかきたてる。ヒロインを矢継ぎ早に不幸が襲うという状況設定には多少の無理があるものの、青年への憎しみが激しい愛へと転じる感情の動きの展開は、まさしく成瀬の独壇場だ。とりわけ印象深いのは、転勤で明日は西パキスタンへ発つという加山と司が湖畔で１日を過ごし、タクシーで宿舎へと向かうシーン。シネスコ画面が捉えたゆるやかに流れ去る車窓の外の風景と彼らの心が重なり合い、そこに見た交通事故の光景が忌まわしい過去をよみがえらせて二人を分かつまでが、無言のうちに語られる。武満徹の叙情的な音楽も忘れがたい。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏草の女たち", "夏草の女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "昭和27年の東京・東中野。秋には立ち退きを迫られているオンボロアパート、通称“東中野ハウス”を舞台に、そこに住む戦争の傷あとを負った女たちのそれぞれの人間模様を情感豊かに描いた作品。落合恵子の『東中野ハウスの夏』の映画化。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("9/10 ジュウブンノキュウ", "9/10 ジュウブンノキュウ", "", "", "http://image.eiga.k-img.com/images/movie/33680/315x210.jpg?2688", 86, "同窓会に集まった9人の男たちに生じた記憶の空白の謎が、9人の会話を軸に展開されていく新感覚のシチュエーション・ミステリー。とある洋館で7年ぶりの再会を果たした高校時代の野球部仲間9人は、思い出話をするうちに、それぞれの記憶が少しずつ食い違っている事に気づく。彼らは記憶を掘り起こすためにタイムカプセルを開けることにするが、9人が1つずつ持つ9個の鍵で開くはずの箱には、なぜか10個の鍵穴があった……。", "2006-10-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAN −CAN", "カンカン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "W・ラング監督による、パリを舞台としたミュージカル・コメディ。1896年、カンカンがワイセツだとされ禁止されるが、シモーヌの店では弁護士フランソワの知恵で警官たちを丸め込み、これをやめようとしない。そこに新任の判事ポールが取締りに乗り出し……。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あぶない刑事", "あぶない刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "横浜港警察署の型破りな刑事たちの活躍ぶりを、しゃれた会話とスピーディーなアクションで綴った痛快娯楽作。製薬会社の研究所を傭兵に襲撃させ、ライバル薬品会社の株価を上昇させた画廊オーナーに、タカとユージが立ち向かう。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Captain America: The First Avenger", "キャプテン・アメリカ　ザ・ファースト・アベンジャー", "", "", "http://image.eiga.k-img.com/images/movie/54260/315x210.jpg?2410", 124, "「スパイダーマン」「X-MEN」などで知られるマーベル・コミック原作のヒーローアクション大作。第2次大戦下、病弱のため兵士として不適格とされた青年スティーブは、軍の極秘実験「スーパーソルジャー計画」の被験者第1号になる。強じんな肉体と破壊不可能なシールドを武器に戦うヒーロー、キャプテン・アメリカとして生まれ変ったスティーブは、レッド・スカル率いるヒドラ党との戦いに挑む。主演は「ファンタスティック・フォー」のクリス・エバンス。監督は「ジュラシック・パークIII」「ウルフマン」のジョー・ジョンストン。", "2011-10-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pick-up Artist", "ピックアップ・アーチスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "小学校教師でナンパの天才ジャックは、ディスコで見かけた娘ランディに一目惚れ。中年のアル中パパと暮らし、ヤクザに追われる彼女に本気で夢中になってしまう。なかなかなびかぬ彼女の気を引こうと、ジャックはあの手この手をつくすが……。男女の駆け引きがユーモラスな恋愛映画。飲んだくれの父親役はD・ホッパー。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("…ING", "アメノナカノ青空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "話題となった韓国ホラー「箪笥〈たんす〉」で注目を浴びた若手演技派女優イム・スジョン主演最新作。不治の病を抱えながら初恋を知った少女、娘の幸せを願う母親、そして苦しいほどの深い愛に目覚めた青年。それぞれの想いが丁寧に描かれ、ただのラブ・ストーリーでは終わらない感動ドラマ。19歳のミナが暮らすマンションに写真家の卵ヨンジェが越してきた。彼は何かとミナに絡み、彼氏に立候補。だがミナの余命は数ヵ月だった。", "2005-12-10", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒッチハイク／溺れる箱舟", "ヒッチハイク／溺れる箱舟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "恐怖のヒッチハイカーに“監禁”され、“動く密室”と化した自動車内で、ズタズタに壊れていく夫婦の絆。緊迫のロード・ムービーであると同時に、ストーカー心理を絶妙に切り取りつつ、人間誰しもが持つ根源的な“弱さ”に踏み込んでいった力作だ。夫婦と犯人、三者三様の思いと関係性の推移をスリリングに見つめる映像世界の中で、寺島進が持ち味を遺憾なく発揮。北海道に単身中の姿俊夫は、半年ぶりに訪ねてくる妻・麗子を空港で出迎える。その帰り道、二人は黒田と名乗る男を拾った。最初は礼儀正しかった黒田だが、突然ショットガンを取り出し、“どこまでも走り続けろ！”と強要する……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アドレナリンドライブ", "アドレナリンドライブ", "", "", "http://ecx.images-amazon.com/images/I/21416RN69KL._SL210_.jpg", 112, "「ひみつの花園」に続く矢口史靖監督の長編第３作。ヤクザの車に傷をつけ、因縁をつけられた、気弱なレンタカー店の店員・悟と、同僚からバカにされるほどお人好しの看護婦、静子。ヤクザの事務所の爆破事故現場に偶然、居合わせた彼らは組から大金を強奪。チンピラたちに追いかけられ、必死で逃げるうちに、二人は心を通わせていく……。矢口監督ならではのユーモラスな演出で、物語は転がっていく。マジメな堅物から恋する女に変わっていく静子に扮した、石田ひかりのコケティッシュな好演が印象的。", "1999-06-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BELT", "ベルト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ローマで生活をともにする作家志望の青年とブルジョワ家庭に育った美しい娘。ある日、青年は娘に一本のベルトをプレゼントするがそれが危険な愛の始まりだった。ちょっとしたいさかいをきっかけに、青年は娘の衣服をはぎとり、彼女の白い裸身にそのベルトを叩きつける……。性の深淵に迫ったイタリア製のインモラル・ファンタジー。ビデオ発売時タイトルは「ベルト　愛と哀しみのムチ」。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fifth Element", "フィフス・エレメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "「グラン・ブルー」「レオン」のリュック・ベッソン監督による、制作費100億円のSFアクション大作。2214年、地球に5千年に1度の危機が迫っていた。タクシードライバーコーベンと、地球を救うというモンドシャワン星人の細胞から生まれた美女リールーは宇宙へと旅立つ。主演はブルース・ウィリス 、共演はスーパーモデルとして活躍していたミラ・ジョボビッチほかゲイリー・オールドマン、イアン・ホルムなど。", "", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Temps Qui Reste", "ぼくを葬（おく）る", "", "", "http://image.eiga.k-img.com/images/movie/1710/315x210.jpg?4142", 81, "31歳のカメラマン、ロマンは、ある日突然、あと3カ月の命だと宣告される。彼はこの悲劇にどう対処し、何に生の意味を見出していくのか。「まぼろし」で最愛の人の死を描いたフランソワ・オゾン監督の＜死についての3部作＞の第2章。主演は「ル・ディヴォース／パリに恋して」のメルヴィル・プポー。「ミュンヘン」のバレリア・ブルーニ・テデスキ、「死刑台のエレベーター」「突然炎のごとく」の大女優ジャンヌ・モローが共演。", "2006-04-22", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スパンキング・ラヴ", "スパンキング・ラヴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アダルトビデオの監督・益田亮は、出演予定の女優に逃げられ街で代役を捜していた。そこでスカウトしたのは、コピーライターを目指す坂田幸江という不思議な女。幸江のビデオは会員制秘密SMクラブのオーナーの目にとまり、益田を通して彼女はクラブへと連れ出される。オーナーの尾此木は広告業界のフィクサーでもあり、幸江は作家として一躍、時代の寵児となるのだが……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT OF THE CREEPS", "クリープス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "コーマン大学の研究室に保管されているのは、27年前謎の死を遂げた死体。ある日、その死体が突然ゾンビの如くよみがえり、黒い粘着物質を放ちながら次々と人間に襲いかかる……。マイケル・ジャクソンの「スリラー」などで知られるデヴィッド・B・ミラーが特殊メイクを担当したSFホラー。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("織田信長", "織田信長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "山岡荘八の人気時代小説の映画化。若き織田信長は隣国・美濃の城主、斎藤道三の娘・濃姫を妻とするが、道三の尾張を狙うたくらみに油断はできない……。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ARGENT DES AUTRES", "銀行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "突然、発覚した財務的なスキャンダルのために、安定した銀行重役の座をスケープゴートとして無理やり降ろされた男の、名誉回復をめぐる苦闘を描いたスリリングな社会派ドラマ。銀行という情け容赦ないビジネスの現場で孤立し、もがきながらも根源にある腐敗の構造を暴いていく孤独な銀行マンを、J・L・トランティニャンが熱演する。1970年代的な不透明感が全編を覆い、やり場のない怒りが何も解決されないままに放置されるラストシーンに凝縮される。1978年のセザール賞作品賞とルイ・デリュック賞をダブル受賞した。", "1978-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Diner", "ダイナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1959年12月のボルチモア。50年代の終わりと共に、あるダイナーに集まる5人の若者たちの青春も終わりを告げようとしていた。大人になりきれない彼らの悩みと希望を、当時のヒットナンバーを随所にちりばめながら生き生きと描いた青春群像劇。「レインマン」「スリーパーズ」等のヒットメイカー、バリー・レビンソンの監督デビュー作。まだブレイク前のケビン・ベーコンやミッキー・ロークも好演を見せ、注目を浴びた。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CARPETBAGGERS", "大いなる野望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "大富豪ハワード・ヒューズをモデルにした大ベストセラー小説をもとにハリウッドの内幕を暴いた野心作。若くしてコード化学の社長となったジョナスは亡父の後妻リナを追い出し、事業を拡大して映画界へ進出する。その頃、かつてのコード家の使用人ネバダ・スミスはカウボーイ・スターとして成功していた。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ’BURBS", "メイフィールドの怪人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "T・ハンクス主演、エンターテインメントの名手J・ダンテ監督による郊外で巻き起こるドタバタコメディ。閑静な住宅地メイフィールド。そこへ引っ越してきたクロペック家から、夜な夜な聞こえる不気味な物音。隣に住むレイは、近所の住人と協力して、真相究明に乗り出すが……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sunny", "サニー　永遠の仲間たち", "", "", "http://image.eiga.k-img.com/images/movie/57570/315x210.jpg?2304", 124, "1970～80年代の洋楽ヒットナンバーの数々で彩られ、2011年韓国で740万人を動員したドラマ。夫や娘にも恵まれ、何不自由ない生活を送っていた42歳のナミは、ある日、母の入院先で高校時代の友人チュナと再会する。25年前の高校生時代、ナミやチュナら7人の仲良しグループはずっと一緒にいると約束しあったが、ある事件がきっかけで離れ離れになってしまっていた。病気に苦しみ、最後にみんなに会いたいというチュナのため、ナミは当時の仲間を集めようと決意。各地に散った仲間を訪ねる旅の過程で、再び人生に輝きを取り戻していく。監督は「過速スキャンダル」のカン・ヒョンチョル。", "2012-05-20", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("電影少女 VIDEO GIRL AI", "電影少女 VIDEO GIRL AI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "『少年ジャンプ』に連載された桂正和原作の同名人気コミックの映画化で、ビデオの世界から飛び出した不思議な少女と、失恋したばかりの内気な少年との恋を描いた青春ファンタジー。大沢健と、キュートな魅力の坂上香織のフレッシュ・コンビが見もの。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("つる—鶴—", "つる—鶴—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "鶴の化身が自分を救った男の嫁となり、自分の羽で布を織るという民話『鶴の恩返し』を題材にした美しくも悲しい愛のドラマ。実写としては初の映画化で、この作品が映画出演100本目となる吉永小百合が数多くの企画の中から自ら選んだものである。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mission: Impossible III", "M:i:III", "", "", "http://image.eiga.k-img.com/images/movie/33500/315x210.jpg?4525", 126, "トム・クルーズ製作・主演の大ヒットシリーズ第3弾。イーサン・ハント（クルーズ）は第一線から退き、後進の指導をしていたが、自分の教え子の女性エージェントが、IMFの長年の宿敵である武器商人デビアン（フィリップ・シーモア・ホフマン）捕獲作戦に失敗し、拉致される。彼女を救助するためにベルリンへ飛んだハントたちだが……。監督はTVドラマ「エイリアス」で注目され、本作が長編劇場映画デビューとなるJ・J・エイブラムス。", "2006-07-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("第三病棟の鬼", "第三病棟の鬼", "", "", "http://image.eiga.k-img.com/images/movie/57210/315x210.jpg?1502", 92, "「路地裏のコッペリウス」のかわさきひろゆき監督が手がけるホラー。盲目の少女とホームレスの老人が、死んだ人間が集い地獄の使いが現れるという公園のベンチで語り合っていた。少女はそこで、小鬼という純粋な青年との切ない思い出を回想する。一方、2人の会話を公園の片隅で聞いていた真知子もまた、自分の過去を思い出し、暗く悲しい世界が広がっていく。2つのストーリーが交錯する中、“鬼”と呼ばれる青年たちの姿が次第に浮き彫りになっていき……。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ペエスケ／ガタピシ物語", "ペエスケ／ガタピシ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "朝日新聞夕刊に連載された人気４コマ漫画の映画化。どこといって取り柄のない雑種犬ガタピシと、落ちこぼれサラリーマンのペエスケ一家の庶民的な生活を軸に、自然を愛し、生き物への思いやりを忘れない家族をユーモラスに描く。つかこうへいが初の単独脚本で参加、洗練されたユーモアセンスを見せる。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love of 6 years", "6年目も恋愛中", "", "", "http://image.eiga.k-img.com/images/movie/54120/315x210.jpg?4376", 112, "交際6年目を迎えた倦怠期カップルのリアルな日常を、ラブコメの女王キム・ハヌル＆「僕らのバレエ教室」のユン・ゲサン共演で描いたロマンティックコメディ。大学のサークルで知り合ったジェヨンとタジンは、つきあって6年目の恋人同士。編集者となったタジンは自らの経験をもとにした恋愛マニュアル本で大成功をおさめ、ジェヨンも友人たちの恋愛相談に乗っている。周囲からは完璧なカップルに見える2人だったが、実は互いへの不満を募らせており……。「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。", "2008-09-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Adaptation.", "アダプテーション", "", "", "http://image.eiga.k-img.com/images/movie/1240/315x210.jpg?4362", 115, "実在の本「蘭に魅せられた男　驚くべき蘭コレクターの世界」（ハヤカワ書房）の映画脚本化＝アダプテーションをめぐるあれこれを映画化。脚本家チャーリー・カウフマンも、この本の筆者スーザンも、本の題材となった男ジョンも実名で登場（ただし俳優が演じる）、本作の監督＆脚本家の映画デビュー作「マルコヴィッチの穴」の撮影現場もドラマの舞台となる。映画クレジットにも記載されているカウフマンの双子の弟ドナルドは、架空の人物（念のため）。", "2003-08-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女と味噌汁", "女と味噌汁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "同じ池内淳子の主演で『東芝日曜劇場』の人気シリーズとなったTVドラマの映画化。美人でしっかり者の芸者・千佳子は、貯金をはたいて買ったライトバンで移動味噌汁屋を始める。庶民性と色気を兼ね備えた池内淳子のはまり役。五所平之助の最後の劇映画。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大日本コソ泥伝", "大日本コソ泥伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "『どてらい男』など上方根性もので知られる花登筐原作・脚本による、関西お笑いスター総出演の喜劇。名門の怪盗石川二十五右衛門の跡目（二十六右衛門）を狙って、日本全国からはせ参じたコソ泥たちが豊臣秀吉の末裔の持つダイヤを盗もうと繰り広げる珍騒動の数々。果たして最後に笑うのは誰か。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tango", "タンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "バンサン、ポール、エレガンは、立場は違えど、妻に関する三様の悩みを抱えていた。妻を殺したバンサンは、バンサンを無罪にしてくれた裁判官エレガンの頼みで、妻に逃げられて悩むポールの妻を殺すことを引き受けるのだが……。「仕立て屋の恋 」「橋の上の娘」のパトリス・ルコントが3人の中年男の珍道中を描いたコメディ作品。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺手壕／BATTLE CREEK BRAWL", "バトルクリーク・ブロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "レイモンド・チョウ、ロバート・クローズという「燃えよドラゴン」のコンビが手掛けた、ジャッキー・チェンのアメリカ進出第１作。元全米プロレス・チャンピオンのH・B・ハガティらを相手に、ジャッキーがコミカルなアクションを披露。", "1980-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WEDDING　SINGER", "ウェディング・シンガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "歌とトークで結婚パーティーを陽気に演出する仕事“ウェディング・シンガー”にスポットを当てたロマンティック・コメディ。この職業に就いているミュージシャン志望のロビーは、自分の結婚式で花嫁に逃げられ、失意のドン底に。そんな彼を慰めたのが、同じパーティー会場で働くウエイトレスのジュリア。交流を重ねるうちに、彼らは強く惹かれ合うが、ジュリアには意地の悪い婚約者がおり……。お人好しの主人公ロビーを人気コメディアン、A・サンドラーが好演。ドラマが進むほどキュートさを増す、相手役のD・バリモアも好感度大。舞台は1980年代のアメリカで、全編に流れる当時のヒット・ナンバーがノスタルジックに響く。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日日の背信", "日日の背信", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "地位も財産も何一つ不自由しない経済雑誌の社長が、山の温泉宿で出会った女・幾子に惹かれていく。彼女は銀座の宝石商の妾だったが、社長と出会ったことによって人生を立て直す決心をし、二人は激しく結ばれる。しかし、社長には病身の妻がいて……。愛とモラルの狭間に揺れる社長役を佐田啓二が熱演する不倫メロドラマ。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thirteen", "サーティーン　あの頃欲しかった愛のこと", "", "", "http://ecx.images-amazon.com/images/I/51PteWmx-RL._SL210_.jpg", 101, "素直で真面目な13歳の少女トレイシーは、ある時から学校の人気者イービィに憧れるようになり、彼女のファッションやメイクを真似、非行の道へと入り込んでいく……。多感で傷つきやすいティーンの少女の苦悩や母親との関係に焦点をあてた人間ドラマ。イービィ役のニッキー・リードが、自身の実体験を基にキャサリン・ハードウィック監督とともに脚本を執筆シタ。主演のエバン・レイチェル・ウッドがゴールデングローブ賞、ホリー・ハンターがアカデミー賞の候補に。", "2005-04-16", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍飛岬", "龍飛岬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "生後間もなく、母親の自殺によって龍飛岬の漁師夫婦に引き取られ、15歳まで成長した少女が、初めて自分の生い立ちを知って上京。母を死に追い込んだ男に復讐しようとする様を描いた作品。本作の製作会社である木村プロダクションの代表、木村元保が原作も執筆した。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長崎の鐘", "長崎の鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "原爆被災者、永井隆博士の手記をもとにして大庭秀雄監督、若原雅夫主演で映画化されたメロドラマ。当時GHQの占領下にあった日本映画は原子爆弾の被害に関することはタブーの一つであったが、メロドラマというベールをかぶせることによって映画化が可能となった。新藤兼人、橋田寿賀子が脚本に参加している。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NIGHT OF THE SHARKS", "死海からの脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "人喰いザメが生息するメキシコのある海辺を舞台に、一枚のCDをめぐって一大アクションが繰り広げられるアドベンチャー映画。T・ウィリアムズ扮する主人公は、弟が送ってきたCDをサメの腹の中に隠し、敵の襲撃に備える。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ИЛЬЯ　МУРОМЕЦ", "豪勇イリヤ　巨竜と魔王征服", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "合成、トリック撮影など当時のソ連の特撮技術を駆使して作られたヒーロー・アクション映画の大作。「ロード・オブ・ザ・リング」の原作小説「指輪物語」にインスパイアされた感がある一作だ。さらに、ロシアの美しい自然の数々に目を奪われる仕上がりとなった。太古のロシアの大地にイリヤ・ムーロメツという豪傑がいた。やがて彼は、3つ首頭の怪獣と決闘することに。", "1956-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("En Kille och en tjej", "恋する男と彼の彼女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「サイダーハウス・ルール」等の作品で知られる、スウェーデンのラッセ・ハルストレム監督の劇場映画デビュー作。ジャーナリスト志望の青年ラッセは、なかなか希望の職に就けず、新聞配達のアルバイトをしながら自堕落な生活を送っていた。そんなある日、彼は教会で聖歌隊をしているレナという女性と恋に落ちる。始めの内はうまくいっていた2人だったが、いつまでも真面目に働かないラッセに対し、レナは不満を募らせていき……。", "1975-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A CERTAIN SACRIFICE", "マドンナ　堕天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "常にセンセーショナルな話題を提供してくれるアメリカのスーパー・ロック・シンガー、マドンナ。本作は彼女がまだ無名だったころに、たった100ドルで主演したという幻の映画。集団レイプ・シーンをはじめとする、かなりきわどいセックス描写がアメリカでも話題を呼んだ。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("よみがえれカレーズ", "よみがえれカレーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "日本ドキュメンタリー界の名匠・土本典昭らとアフガニスタン側スタッフとの共作で撮影された、内戦に揺れる激動期アフガニスタンの記録。数々の撮影禁止場所を撮影することに成功したのみでなく、内戦のさなか、日常生活を生きる人々の姿を印象的に描き出している。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Switch", "スウィッチ", "", "", "http://image.eiga.k-img.com/images/movie/57340/315x210.jpg?3957", 100, "ごく平凡な女性が「ホーム・エクスチェンジ（自宅交換）」により殺人の疑惑をかけられ、身元を乗っ取られてしまう恐怖を描いたサスペンス。カナダ・モントリオール在住のソフィは、期間限定のアパート交換サイト「switch.com」でフランス・パリに暮らすベネディクトという女性とアパートを交換。あこがれのパリに旅立つ。しかし到着した翌日、アパートの寝室で男性の惨殺死体が発見され、ソフィは殺人容疑者ベネディクトとして逮捕されてしまう。監督は「スパイ・バウンド」のフレデリック・シェンデルフェール。脚本に「クリムゾン・リバー」原作者のジャン＝クリストフ・グランジェ。カナダの若手実力派カリーヌ・バッナッスが主演し、ソフィを追う警部役で元マンチェスター・ユナイテッドのエリック・カントナが共演。", "2011-12-10", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WELCOME HOME，SOLDIER BOYS", "ソルジャー・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ベトナム戦争から帰還した４人の兵士が、牧場を経営する夢を抱いて、キャデラックに乗り、一路西部を目指す。その道中で娼婦を輪姦したり、与太者とケンカしたりして彼らはホープという村にたどり着くが……。戦争によって精神を傷つけられた青年たちの、破滅に向かう青春を描いた作品。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林老女", "少林老女", "", "", "http://image.eiga.k-img.com/images/movie/53660/315x210.jpg?4775", 0, "山奥にある“少林寺”で門下生と共に修行に励んでいた美代子は、“一本足”と名乗る女武術使いとの戦いに敗れ、少林寺を奪われてしまう。都会でショー芸人に身を落とした彼女は人並みの幸せを手に入れて闘争心を失いかけるが、やがて果たすべき闘いを思い出し……。バラエティ番組「ダウンタウンのガキの使いやあらへんで!!」で強烈な存在感を放っている浅見千代子が最強の老女に扮する異色カンフー・コメディ。共演にダチョウ倶楽部の上島竜兵。", "2008-05-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Big Lebowski", "ビッグ・リボウスキ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「ファーゴ」に続いてコーエン兄弟が手掛けた、ユニークな人間ドラマ。デュードは1970年代から抜け出したような、無職でだらしない中年男。そんな彼が、同姓同名の大富豪リボウスキ氏と間違えられ、ギャングに襲われたことから事件は起こる。賠償を求めるためにリボウスキを訪ねたデュードは、誘拐されたリボウスキの妻の身代金受け渡しを引き受けるハメになる。ところが、受け渡しに同行した悪友が勝手な行動に出たことから、デュードはさらなる騒動に巻き込まれる……。ボーリングを愛する主人公をはじめ、ベトナム戦争を懐かしむ悪友、エキセントリックな女芸術家など、風変わりな人物が次々と登場。サスペンス性は薄いが、幻想や現実を股にかける大胆な展開に魅せられ、コーエン兄弟のユーモラスな話術にはまってしまう。人の世の愚かしさとおもしろさを、笑いとともに伝える快作。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arctic Tale", "北極のナヌー", "", "", "http://image.eiga.k-img.com/images/movie/34230/315x210.jpg?1592", 84, "地球温暖化により急激なスピードで氷が溶けはじめた北極。過酷な状況の中で生まれた白くまのナヌーが、ブリザードや飢えなど数々の困難に直面しながらも必死に生き抜こうとする姿を捉えた感動のドキュメンタリー・ドラマ。これまで自然をテーマにしたドキュメンタリー番組を数多く制作してきたナショナル・ジオグラフィック社と「皇帝ペンギン」のアメリカ版スタッフがタッグを組み、構想から10年の歳月を費やして完成させた。", "2007-10-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トカレフ", "トカレフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "幼稚園バスの運転手・西海道夫はある朝、覆面の男に銃で脅され、自分が運転するバスからわが子・タカシを誘拐された。狡猾な犯人グループは、まんまと警察の裏をかき、身代金を奪ったあげくに子供を殺して捨てる。やり場のない怒りに駆られる道夫と、放心状態の妻・あや子。二人の気持ちは離れていった。ところが、ひょんなことから同じ住宅に住む印刷工の松村が、“主犯”であることを確信した道夫は……。おなじみ荒戸＝阪本コンビの第４作で、初めて大阪ではなく東京近郊が舞台となった。作風にも大きな変化が見られ、極端なまでに抑制が利いたぶっきらぼうな画面の連鎖が強い印象を残す。「鉄拳」に続いて２度目の阪本作品の主演となる元プロボクサー大和武士の熱演と、対照的な佐藤浩市の“狂気の無表情”は忘れがたい。ただし終盤になって、いつもの阪本映画と同じく物語がファンタジーへと滑っていくあたりには賛否両論があるだろう。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒロシマ・ピョンヤン　棄てられた被爆者", "ヒロシマ・ピョンヤン　棄てられた被爆者", "", "", "http://image.eiga.k-img.com/images/movie/55460/315x210.jpg?4765", 90, "ジャーナリストの伊藤孝司が、朝鮮民主主義人民共和国で暮らす広島・長崎の在朝被爆者を追ったドキュメンタリー。1945年の終戦直後、日本で暮らしていた幼い李桂先（リ・ゲソン）さんは、朝鮮への帰国手当てが出ると聞いて向かった広島で、残留放射能を浴びてしまう。その後、大学進学のために単身平壌（ピョンヤン）へと向かうが、日に日に健康状態が悪化していき、自らが被爆していることを知る。", "2010-07-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIAGARA", "ナイアガラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "1952年に“明日のスター”でトップにあげられ一躍スターになったモンローの本格的主演作。ナイアガラ瀑布の景観をバックに、人妻の姦通と殺人事件をスリルとサスペンスを交えて描いていく。なんといっても話題の中心はモンローで、この映画の中で有名になった“モンロー・ウォーク”も見ることができる。全世界をわかせた官能的なシーンは男性ファンを完全にノックアウトした。共演のJ・ピータースも彼女の陰に隠れがちだが、なかなか魅力的な演技を見せる。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルルの森の物語", "ウルルの森の物語", "", "", "http://image.eiga.k-img.com/images/movie/54750/315x210.jpg?4830", 119, "新潟県中越地震を生き抜いた実在の柴犬を描いてヒットを記録した「マリと子犬の物語」のスタッフ・キャストが再結集して製作した、オオカミの子供と人間の交流を描く物語。東京で暮らす昴としずくの兄妹は、母親の入院をきっかけに、5年前に離婚した父親・大磁の住む北海道へ引っ越すことになる。慣れない父親との生活に戸惑う2人だったが、大自然や野生動物との触れ合いにより徐々に心を開いていく。ある日、2人は母親とはぐれたオオカミのような子犬に出会い、「ウルル」と名付けて飼うことにする。", "2009-12-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("樺太 1945年夏　氷雪の門", "樺太 1945年夏　氷雪の門", "", "", "http://image.eiga.k-img.com/images/movie/55540/315x210.jpg?1955", 119, "1945年夏、太平洋戦争が終末を迎えようとしていた樺太。ソ連が「日ソ不可侵条約」を破り、突如として日本への進撃を開始した。樺太全土に疎開命令が出るが、真岡郵便局の交換嬢20名は決死隊として職務を遂行すべく、その地にとどまった。そして終戦後の8月20日、真岡沿岸にソ連艦隊が現われ町は戦火に包まれるが、交換嬢たちは自決用の青酸カリを胸に、緊急を告げる電話の回線をつなぎ続ける。「東京犯罪地図」（56）の村山三男監督がメガホンをとった同作は、74年にソ連からの圧力で公開中止となったが、36年越しとなる10年に劇場公開が決定。", "2010-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦国野郎", "戦国野郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "時は戦国、当時の最新兵器“種ヶ島”をめぐって武田の忍者と村上水軍、そして木下藤吉郎たちの間で、壮絶な争奪戦が展開される。岡本喜八の西部劇タッチの演出と藤吉郎に扮した佐藤允の快演で楽しめる一編になった。時代劇アクション映画とでもいうべき作品。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カンバック", "カンバック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ボクシングの元世界チャンピオン、ガッツ石松が、監督・主演を兼ねたボクシング映画。今は焼き鳥屋を営む元ボクサー、鈴木丈。彼は、兄が背負った莫大な借金を返すため、再びリングに上がる。対戦相手は、かつての宿敵、フェルナンド・ロペス……。ガッツ石松が自分の実像をダブらせた丈役を熱演。安部譲二の原作を倉本聰が脚色したシナリオをもとにガッツが脚本化している。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブレイクブレイド　第二章「訣別ノ路」", "ブレイクブレイド　第二章「訣別ノ路」", "", "", "http://image.eiga.k-img.com/images/movie/55530/315x210.jpg?3154", 50, "吉永裕ノ介の人気ロボット漫画を6部作で映画化した第2作。「昆虫物語みつばちハッチ　勇気のメロディ」のアミノテツロ監督がシリーズ総監督を務める。舞台は魔力で動く工作材料“石英”を動力源として発展してきたクルゾン大陸。クリシュナ国に暮らす魔力を持たない青年ライガットは、士官学校時代の親友で天才魔道戦士ゼスが率いる隣国アテネスとの和平交渉に向かう。2人は4年ぶりの再会を果たすが、互いに主張を譲らず、戦渦はどんどん拡大していく。", "2010-06-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mein bester Feind", "ミケランジェロの暗号", "", "", "http://image.eiga.k-img.com/images/movie/56490/315x210.jpg?3792", 106, "第2次大戦下のオーストリア。ユダヤ人の画商一族カウフマン家は、所有していたミケランジェロの絵をナチスに奪われ、収容所に送られてしまう。ナチスは奪った絵をイタリアとの交渉材料にしようとするが、がん作であることが判明。本物を隠した一家の父はすでに収容所で死亡していた。父からナゾのメッセージを受け取っていた息子のビクトルは、母の命を救うためナチス相手に危険な取引に出る。アカデミー外国語映画賞を受賞した「ヒトラーの贋札」（2007）のプロデューサーが製作したサスペンスミステリー。", "2011-09-10", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狼の紋章", "狼の紋章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "平井和正の代表作“ウルフガイ”シリーズ第１作を映画化。狼男・犬神明が、想いを寄せる英語の教師をはさみ、学園の番長・羽黒と対立するというホラー・アクション。全体に漂う暗めのトーン、犬神と教師とのふれあいなどがうまく処理された佳作となっている。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INSIDE DAISY CLOVER", "サンセット物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "R・マリガン監督が、一人の少女がハリウッドの大スターになるまでを綴った青春ストーリー。15歳の少女デイジーは、母親と二人暮らし。ある日、新人タレントのオーディションに応募するために送ったレコードが映画会社の目にとまり、女優としてデビューすることになる……。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLASH FIRE", "フラッシュ・ファイヤー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "放火されたアパートから少年を救出したものの、犯人を取り逃がしてしまう刑事のジャック。その晩、相棒のアートが娼婦リサとの情事の最中に殺された。悲しみの中、ジャックは昇進とともに署内のデスクワークに転属される。そんな折、事件の一部始終を目撃し姿を隠していたリサから助けを求められ、ジャックは彼女をかくまうことになる。そして独自に真相究明に乗り出したところ、事件のカギは警察内部にあることがわかる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暖", "故郷〈ふるさと〉の香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "中高年を感涙させた大ヒット作「山の郵便配達」で知られるフォ・ジェンチイ監督が、再び郷愁を呼び起こす美しい山村を舞台にした感動作を完成させた。テーマは“初恋の想い出”。10年ぶりに故郷の村に帰った男が初恋の人と再会。すでに別々に結婚してしまった二人の運命は、再び交わる……。日本の個性派俳優・香川照之が、口の利けないヒロインの夫役を熱演。原作は「紅いコーリャン」や「至福のとき」が名匠チャン・イーモウによって映画化されている、中国の人気作家のモォ・イエン。10年ぶりに帰った故郷の村で、ジンハーは初恋の相手ヌアンと再会。彼女の家を訪ねると、耳が不自由な夫ヤーバと6歳の娘を抱えていた。ジンハーの訪問に、ヤーバは思わぬ決断をする……。", "2005-01-29", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Roselyne et les Lions", "ロザリンとライオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "調教学校で助手を務める美少女ロザリンの姿を見て、調教師に憧れた17歳の青年ティエリーは、自らもロザリンの師匠に弟子入りする。やがて2人は恋に落ちるが、一人前の調教師を目指す2人に、さまざまな困難が待ち受けていた。「ベティ・ブルー」のジャン＝ジャック・ベネックス監督が、実在の調教師をモデルに描いたラブ・ロマンス。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALIGULA’S SLAVES", "カリギュラ III", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "おなじみの暴君カリギュラの狂乱の日々を描く。紀元一世紀。古代ローマの若き皇帝カリギュラは、母を殺され恋人を兄に奪われて以来、人を愛することができなくなり、人民に暴虐の限りをつくす。反逆者たちを火あぶりにし、奴隷女をムチ打ち、ライオンに人間を襲わせ……。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あの橋の畔で　第二部", "あの橋の畔で　第二部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "松竹大船お得意の、泣けるメロドラマ大作となった傑作シリーズの第２部。東京、北海道、九州と巡る舞台スケールの大きさが見どころだ。今回は九州が主舞台。既婚者となってしまったかつての恋人、葉子による看病で次第に記憶を取り戻していく光晴。しかし葉子は夫と姑、さらに兄にまで光晴との仲を疑われ、悩んだ末実家の九州へ帰ってしまう。それを知った光晴は……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE ＆ A.45", "ガンズ・オン・ザ・ラン　明日なき堕天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ワティとスターレーンは束縛を嫌い自由に生きる恋人同士。ワティは、弾を抜いた銃で誰も傷つけずに済ますコンビニ強盗を稼ぎにしていた。そんな彼女の元を刑務所時代の相棒ビリーが訪ね、強盗の話を持ちかける。渋々承知したワティだったが、麻薬漬けでキレかかったビリーは犯行現場で相手を射殺。さらに動転してワティまでも脅そうとする。その場を何とか逃れスターレーンの元へ向かうワティ。しかしそこには警察の手が……。現代版“ボニー＆クライド”ともいうべき、二人の逃避行を描く。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING GAME キングゲーム", "KING GAME キングゲーム", "", "", "http://image.eiga.k-img.com/images/movie/55560/315x210.jpg?1644", 93, "「東京大学物語」などで知られる漫画家・江川達也が、社会現象になったパーティーゲーム“王様ゲーム”をモチーフに原案と監督を務めるシチュエーション・ミステリー。石田卓也、芦名星、木村佳乃らが出演する。ある秘密の契約に基づき、10人の見知らぬ男女が密室に集められる。彼らに与えられた課題は、 “王様ゲーム”を勝ち抜き続けること。先の見えない状況に不安がつのるなか、王様の命令は次第にエスカレートしていく。", "2010-08-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Coo／遠い海から来たクー", "Coo／遠い海から来たクー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "南太平洋に浮かぶ島・フィジーを舞台に、海竜プレシオザウルスと少年の交流、そして人間たちの争いを描いた長編アニメーション。島で海洋生物学者の父と暮らす少年、洋助はある日、海辺で見たこともない生物を発見し、Coo（クー）と名付けて育てることに。洋助を母親と思い込んでいるCooは日増しに大きくなっていくが、同じころフランス軍の特殊部隊がCooの存在を突き止めて島に上陸しようとしていた……。原作は直木賞を受賞した故・景山民夫の同名小説。岡本喜八による脚本も見逃せないが、愛くるしいCooの表情が印象的。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRUMB", "クラム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ジャニス・ジョプリンのアルバム『チープ・スリル』のジャケット・アートや、『フリッツ・ザ・キャット』で知られるコミック界のカルト作家ロバート・クラム。彼の20年来の友人T・ズウィコフがクラムの知られざる半生を描いたドキュメンタリー。本人はもちろん、暴力的な父や薬物中毒の母親や精神を病んだ兄と弟、同業者らが、彼の家庭環境や性体験、LSD体験などのエピソードを赤裸々に語る。エロ・グロ趣向の強いクラムの作品群の背景が浮かび上がる、興味深い一編。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忘却の花びら", "忘却の花びら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "建築技師・藤崎のもとに、終戦直後生き別れになった恋人・葵の妹、鮎子が訪ねて来る。お互いに惹かれるものを感じる二人。実は葵は生きているが、敵兵に身を汚されて皆の前に姿を現わせずにいたのだ。そうとは知らぬ藤崎と鮎子は愛し合うが……。菊田一夫によるラジオ連続ドラマの映画化。三角関係タイプのメロドラマ。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・与太郎戦記", "新・与太郎戦記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "与太郎がついに一等兵に昇格！　だが相変わらずドジの連続で、しまいには食べものにあたって入院。ところがそこの看護婦が昔、想いを寄せていた女で……。今回の演出は、大映プログラム・ピクチャー路線の中でコンスタントに作品を発表し続けた田中重雄。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AS YOU LIKE IT", "お気に召すまま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ハンガリー生まれの国際派監督P・ツィンナーによるシェイクスピアの同名喜劇の映画化。弟に領地を奪われた公爵。その娘が忠臣と恋に落ちて……。ヘンリー・アインリー、レオン・カーターメイン、マッケンジー・ワードら当時のイギリス演劇界の大物たちが総出演という豪華キャストによる超大作。", "1936-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SONS", "父の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "腹違いの３人の息子たちが、死期が迫った老父を連れて、かつて父が愛した女性を探す旅に出るロード・ムービー。老父役にはS・フラー監督。一行が到着するノルマンディーはフラー自身の思い出の地でもある。J・ビールスが男役、しかもオカマ役で登場するのも一興。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅雪姫", "修羅雪姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "小池一夫の同名劇画の２度目の映画化。原作をそのまま映像化せず、その精神をくみとり近未来活劇に仕上げている。暗殺集団、建御雷（たけみかづち）家の娘・雪は、母を殺した犯人が首領の白雷であると知り、反旗を翻すが返り討ちに遭う。重傷の彼女を救ったのは反政府組織の活動家、隆。彼の優しさにふれ、雪は初めて安らぎを覚えるが、そんな彼らに白雷の刺客が迫る……。これが初主演となった釈由美子がヒロインをパワフルに熱演。「マトリックス」シリーズなどハリウッドでも活躍するユエン・ウーピンの愛弟子、ドニー・イェンの武術指導の下で、スピーディーなアクションを披露する。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀色の雨", "銀色の雨", "", "", "http://image.eiga.k-img.com/images/movie/54720/315x210.jpg?1614", 0, "直木賞作家・浅田次郎の短編を、「銀のエンゼル」の鈴井貴之監督が映画化したヒューマン・ドラマ。父を知らずに育った男子高校生・和也、スナック勤めの孤独な女・菊枝、引退を勧告されたボクサー・章次。偶然出会った3人の男女が、奇妙な共同生活を通してそれぞれの傷ついた心を再生させていく姿を描く。「ごくせん　THE MOVIE」の賀来賢人、「最終兵器彼女」の前田亜季、「レッドクリフ」の中村獅童らが出演。", "2009-11-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Thief Who Came to Dinner", "おかしなおかしな大泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "コンピュータの指示で大企業をクビになり、妻とも離婚してしまった青年が、独学で泥棒になるための技術を学び、次々と完全犯罪に挑戦していく痛快泥棒コメディ。ついには、彼は恋人をも引き込み、共同で犯行をはじめるようになる。脚本を監督デビュー前のW・ヒルが担当している。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明日に架ける愛", "明日に架ける愛", "", "", "http://image.eiga.k-img.com/images/movie/56510/315x210.jpg?3152", 105, "日中友好（国交正常化）40周年を記念して製作された日中合作ドラマ。両国のファッション業界を背景に、中国残留日本人孤児の祖母を持つシングルマザーが、中国人男性との国境、時代を超えた恋を通して成長する姿を描く。主演は元「モーニング娘。」の市井紗耶香と中国の若手俳優アレックス・ルー。「君が踊る、夏」を手がけた香月秀之監督がメガホンをとり、「東京ガールズコレクション」など実際のファッションショーの舞台裏で撮影された映像が盛り込まれている。", "2012-03-31", "76,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON THE TOWN", "踊る大紐育", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "24時間の休暇をもらった水兵ゲイビー、チップ、オジーの３人が、ニューヨーク埠頭に降り立ち『ニューヨーク、ニューヨーク』を歌い出すファースト・シーンからラストの３人の水兵と女、警官隊の巻き起こす大騒動まで、実に楽しいミュージカル。G・ケリーの振付けは見事。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALENDAR　GIRLS", "カレンダー・ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "中年女性たちが自分たちのヌード写真をカレンダーにして売り出し、大反響を巻き起こしたというイギリスの実話をもとに描く人間ドラマ。白血病研究基金の資金集めのために始めたカレンダー作り。その成功が主婦たちの生活を変えていく様を、イギリスを代表する女優H・ミレンとJ・ウォルターズの二人が体当たりで熱演する。平凡な主婦として暮らす親友同士のクリスとアニーは、白血病で亡くなったアニーの夫を偲ぶため婦人会で自分たちのヌード・カレンダーを作ることを思いつく。皆、戸惑いながらも完成して売り出してみると、大反響を巻き起こし……。", "2003-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前探検", "喜劇・駅前探検", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "質屋の孫作と顧客の鉱山師・徳之助、考古学教授の次郎。古い春画の中から１枚の古文書が出た。豊臣家の軍資金が隠されているらしい。３人は調査を始めるが、この話を耳にしたペテン師のグループも加わって争奪戦となる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UMUT", "希望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "カンヌ映画祭で上映され、トルコの名匠ギュネイの名を世界に知らしめた力作。馬車の御者をしているジャバルは、自動車の衝突によって大事な馬車と馬を失ってしまう。貧しい彼の家庭は、ますます生活に困窮。借金を断られ、強盗をしても失敗とツキもない。やがてジャバルは宝探しに最後の望みを託すが……。ネオリアリスモの視点と詩情豊かな映像で、ある男がたどる悲劇を描写。辛らつなドラマから、トルコ社会の構造的な矛盾が浮き彫りになる。", "1970-01-01", "32", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼に訊け　宮大工　西岡常一の遺言", "鬼に訊け　宮大工　西岡常一の遺言", "", "", "http://image.eiga.k-img.com/images/movie/57520/315x210.jpg?3458", 88, "法隆寺の昭和の大修理、薬師寺白鳳伽藍復興工事などに携わり、飛鳥時代から続く寺院建築の技術を後世に伝えることで「法輪寺の鬼」「最後の宮大工」と呼ばれた西岡常一（1908～95）の生涯に迫ったドキュメンタリー。宮大工の技術は言葉ではなく体で覚え、長い時間をかけて技術を盗むものとされていたが、西岡は自身の経験と培ってきた技術、感覚を若い世代に言葉で伝えようとしていた。記録映像や西岡にゆかりのある人々へのインタビューを交え、西岡が言葉に託した思いを探る。", "2012-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("石の降る丘", "石の降る丘", "", "", "http://image.eiga.k-img.com/images/movie/55980/315x210.jpg?2921", 79, "殺された高名な雨乞い師のたたりで雨が降ると必ず石も降ってくる安吾村。東京から故郷の安吾村に戻ってきた外史は、村人が建てた祠（ほこら）が荒らされご神体がなくなっていることに気がつく。そのせいか村は雨と石が降り止まない。男色だったという雨乞い師のたたりを鎮めるためには、童貞の男を生けにえとしてささげなければいけないというが……。", "2011-01-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金枝玉葉／HE’S　A　WOMAN，　SHE’S　A　MAN", "君さえいれば 金枝玉葉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ボーイッシュなウィンは、人気・実力とも香港No.1の女性歌手ローズの熱狂的なファン。ローズのプロデューサーで、誰もが知る恋人でもあるサム・クーが、男性歌手発掘のためにオーディションを開くことを知ったウィンは、ローズに近づくチャンスとこれに応募する。男装したウィンが女性であることには誰も気付かず、彼女は見事オーディションに合格。サムとローズの住むマンションに、男として住み込むことになった。しかし、ゲイではないのになぜかウィンに惹かれる自分にサムが混乱、ひと時の楽しい共同生活は微妙に揺らぎ始める。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("踊る大捜査線 THE MOVIE", "踊る大捜査線 THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "TVで人気を博した刑事ドラマの劇場版。同時期に発生した４つの事件が複雑に絡み合っていく様を軽妙なストーリー展開で描き出し大ヒットとなった。署管轄の川で男の水死体が発見され、対応に追われる湾岸署内で何者かによる窃盗事件が続出。同じ頃、警視庁副総監の誘拐事件、殺人を示唆するネット犯罪と、重大事件が重なり署内には騒然とした空気が流れる。本庁の指示で思うように捜査ができない青島刑事は独自の捜査を開始、やがて最悪の危機を迎える……。事件をめぐるスリルと、本庁と湾岸署刑事との確執の緊張感が次第にエスカレート。そんなシリアスな部分と随所に散りばめられたユーモアが巧みに織り込まれた力作だ。猟奇殺人犯を不気味なムードで怪演する小泉今日子や、トボけた味で笑いを誘うお気楽な管理職３人組など、ワキ役陣の好演も光る。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兄弟仁義・関東三兄弟", "兄弟仁義・関東三兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "大正末期、老松一家は二代目・梅乃井が代貸・政吉と細々と縄張りをしきっていた。ところが、阿古島一家が何かといいがかりをつけ、ついに梅乃井は殺される。豪雨の中、政吉は二人の義弟とともに死地に向かう。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("林家舗子／THE　LYN　FAMILY　SHOP", "林商店", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "戦後の日本で、最初に公開された中国映画「白毛女」(1950)の監督として知られるシュイ・ホァの代表作。日本商品ボイコット運動の渦巻く1931年のとある街。林商店の主人は国民党役人にワイロを贈って、こっそりと日本製品を売っていたのだが……。日本製品不売運動に揺れる林商店と周囲の人々の混乱ぶりを描いた作品。", "1959-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Finders Keepers", "マネーハンティングUSA 500万ドルを追いかけろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "美しい富豪の娘ジョルジアナは、使用人のジョセフとともに500万ドルを奪い、大金を棺桶の中に隠して大陸横断列車に乗り込んだ。そこへ、いつもドジばかり踏んでいる男マイケルが現れ、なぜか付き添うはめに……。ドタバタで展開する痛快コメディ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPLASH", "スプラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ギリシャ時代から西欧文化に深く根をおろしている人魚伝説を、SF風にアレンジしたロマンティック・ファンタジー。一人の内気な少年が、ある夜、泥酔して溺れかかっているところを美しい人魚に救われる。少年は人魚に一目惚れ。やがて人魚が都会へ現れ、大騒動が起こる……。コメディとシリアス・ドラマの二つを、巧みにミックスさせた絶妙な作品。水をかけられると人魚になるというアイデアが卓越している。スキューバ・ダイビングのインストラクターに「ジョーズ」などの名手マイク・ノーマッドがあたっている。神話のヒロインを現代に見事によみがえらせた一編。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレヨンしんちゃん　嵐を呼ぶジャングル", "クレヨンしんちゃん　嵐を呼ぶジャングル", "", "", "http://ecx.images-amazon.com/images/I/61pzjpvFYQL._SL210_.jpg", 80, "嵐を呼ぶ幼稚園児・野原しんのすけことしんちゃんの活躍を描いた劇場版シリーズ第８作。南海の船上試写会に参加したしんちゃんご一行様。しかし試写のさなか、突然大人たちが謎のお猿さんたちにつれ去られてしまう。その救出のため、近くに浮かぶ怪しげな島に上陸したしんちゃんたちは、そこで世界征服をたくらむパラダイスキングと戦うことに!?", "2000-04-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白日夢", "白日夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "谷崎潤一郎の同名小説を武智鉄二が監督した問題作。青年画家と流行歌手が歯科医で治療を受けている間に、画家は女が医師に犯されることを妄想する。芸術かワイセツかで問題になったが、興行的には空前の大ヒットを記録した。なお、武智鉄二は1981年に愛染恭子主演で再映画化している。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN BEHIND THE GUN", "銃の後に立つ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "19世紀の中ごろ、特命を受けてロサンゼルスへ乗り込んだ元騎兵隊司令官少佐の活躍を描くウエスタン。カリカット少佐の目的は軍隊内反乱をたくらむ者たちを突き止めることであり、身分を偽ってならず者たちの溜り場に飛び込んでいく。反乱の指導者の正体は、実に意外な人物であった……。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAHLER", "マーラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "ラッセルならではの奔放なイメージあふれる音楽家マーラーの伝記映画。1911年、マーラーと妻のアルマは、ウィーンに向かう列車のコンパートメントに座っている。途中、マーラーの脳裏をよぎる過去の情景や苦悩、そして悪夢……。美しい自然描写と奇想天外かつ卑猥なイメージとが、見事な調和をみせる。DVDタイトルは「ケン・ラッセル／マーラー」。", "1974-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女忍 KUNOICHI", "女忍 KUNOICHI", "", "", "http://image.eiga.k-img.com/images/movie/56080/315x210.jpg?2162", 65, "「ハイキック・ガール！」「KG カラテガール」の武田梨奈が主演する忍者アクション。甲賀の集落から女たちをさらう伊賀の忍者たちの目的を暴くため、わざと敵の術中にはまった如月だったが、連行される途中でナゾの男・神無が女たちの縄を解き放ったため、事態は混迷する。如月は追っ手を倒し、神無の真の目的を探るが……。監督は「忍邪」「AVN」など多くの忍者映画を手がける千葉誠治。共演に「ハブと拳骨」の虎牙光揮ら。", "2011-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LARGER THAN LIFE", "小さな贈りもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "死んだと思っていた父親の訃報が、講演で各地を回る主人公ジャックのもとに突然届いた。しかも遺産を相続することに。ところが、その遺産とは３万５千ドルもの借金と、一頭の象だった……。亡き父から譲り受けた“遺産”をめぐって繰り広げられる大騒動を描いたハートウォーミング・コメディ。巨象を相棒に大陸横断の旅に出るハメになった中年男を、B・マーレーがユーモアたっぷりに演じる。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本妖怪伝 サトリ", "日本妖怪伝 サトリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "東陽一の初カラーの作品。人間の考えていることをすべて言い当てて、考えることがなくなるとその人間を食べてしまうという、妖怪サトリ民間伝承に材を取り、曖昧な人間関係の上に立脚した自閉的状況を描き出す。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三本木農業高校、馬術部", "三本木農業高校、馬術部", "", "", "http://image.eiga.k-img.com/images/movie/34200/315x210.jpg?1557", 117, "青森の農業高校を舞台に盲目の馬と馬術部の女子高生の交流を描いた実話のドラマ。監督は「半落ち」「夕凪の街　桜の国」の佐々部清。歌手・長渕剛の実娘・長渕文音が、女優デビュー作で主演を務める。左目を患い三本木農業高校に引き取られたコスモと、馬の優美な姿に憧れて馬術部に入部した香苗。プライドが高く、盲目の不安から言うことを聞かないコスモに手を焼く香苗だったが、ひたむきに世話をする彼女にコスモは少しずつ信頼を寄せ、香苗もまた生命の尊さを学んでいく。", "2008-10-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌20 ファイナル", "釣りバカ日誌20 ファイナル", "", "", "http://image.eiga.k-img.com/images/movie/54390/315x210.jpg?3350", 0, "88年にスタートした「釣りバカ日誌」シリーズの通算22作目（特別版2作を含む）にして完結編。監督は14作目からメガホンを取り続けた朝原雄三。ゲスト・キャストに松坂慶子、吹石一恵、塚本高史。鈴木建設の業績悪化に悩むスーさんこと鈴木一之助（三國連太郎）を助けようと、釣りと家庭を愛する万年平社員・ハマちゃんこと浜崎伝助（西田敏行）は、釣りの人脈から思いがけない大型受注を取ってくる。そのご褒美に釣り休暇をもらったハマちゃんは、スーさんとともに北海道へ渓流釣りに向かう。", "2009-12-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TERMINAL VELOCITY", "ターミナル・ベロシティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "地上１万５千フィートから落下するキャデラックの中での格闘をはじめ、空中シーンが見どころのスカイ・アクション。腕利きのスカイダイビング・インストラクターのディッチは、金髪の美女クリスにパラシュートの手ほどきを頼まれる。ところが落下ポイントに向かう途中、彼女は空中へと身を投げた。ディッチは殺人の容疑をかけられたうえ、謎の男たちに命を狙われる羽目に。危うく逃げ出した彼が見たものは、生きていたクリスだった。敵の執拗な攻撃を受ける二人。必死の逃亡の中で、彼女は自分の正体を明かす……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pat Garrett & Billy the Kid", "ビリー・ザ・キッド／21才の生涯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "友人の保安官ギャレットの警告を無視して町にとどまったビリーは、撃ち合いの末に逮捕される。だが、ビリーはギャレットのスキをついて脱獄しメキシコに逃亡する。跡を追うギャレット。二人には伝説の対決が待っていた。21歳の生涯で21人を殺した西部史上に名高いビリー・ザ・キッドのエピソードの断面を、ペキンパーが独自のスタイルで映画化したウエスタン。おなじみペキンパー一家の出演者に加え、B・ディランがビリーの手下エイリアス役で出演し、異彩を放つ。クリストファーソン、コバーンとも持ち味を発揮して好演している。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("動乱", "動乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "二・二六事件を背景に、青年大尉の高倉健と、彼の部下の姉・吉永小百合の悲恋を古典的なタッチで描いた２部構成の作品。フィクションとノンフィクションを巧みに織り交ぜながら、銃声とともに散った男女の愛を淡々と綴った森谷の演出が光る大作。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PARADINE CASE", "パラダイン夫人の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ロンドンの若手敏腕弁護士アンソニー・キーンは、名門パラダイン家の当主が毒殺された事件の弁護を持ちかけられた。若く美貌の夫人が逮捕されたが、彼女は死んだ夫に献身的に尽くしていたではなかったか？　キーンは夫人に会いにいくが、秘密と溝を感じて不安になる。事実以上に夫人の心に入ることの大切さを感じたキーンは、使用人のラトゥールと夫人の関係に迫るが二人にほんろうされてしまう。次々と出てくる不利な証言。やがて意外な結末が……。謎解きよりも心理に重さを置いたヒッチコックの異色作。マルチ・カメラによる法廷のシーンに注目。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIPLOMATIC IMMUNITY", "殺しの外交特権", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "カリフォルニアで殺人を犯しながら、海外法権による外交特権で南米に逃れた殺人犯。だが殺された娘の父親は、執念で犯人を追い詰めていく。個性あふれる登場人物のキャラクターが、比較的シンプルな構成に奥行きを与えるアクション快作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASK", "マスク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1980年、わずか16歳で亡くなったロッキー・デニス少年の実話をもとに描いた感動作。顔が異様に大きく、奇妙な“マスク”をかぶったようにみえる病気に冒されたロッキーが、持ち前の明るさで周囲にとけ込み、短くも美しい人生を送る。重たいテーマを温かく、味わい深い作品に仕上げている。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("永遠の1/2", "永遠の1/2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "すばる文学賞受賞作品の映画化。婚約者と別れ、市役所を辞めた宏は、競輪場で出会った良子と親密になる。だが、その頃から宏の周辺で変なことが起こり始める。見も知らないヤクザに追いかけられて殴られたり、可愛い女子高生に泣かれたり、宏には身に覚えのないことばかりだ。どうやら、宏ソックリの別人がいろいろと悪事をはたらいているらしい。ついに、宏はもう一人の自分を探し出すことにした……。都会派・根岸吉太郎が、生き方の定まらない中途半端な男の姿をクールに描いた現代的恋愛物語。リアリズムとフィクションが交錯し、何が起きてもおかしくない世界は、まさに根岸にピッタリの題材だ。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NANOOK OF THE NORTH", "極北の怪異〈ナヌーク〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "無声期の記録映画を代表する秀作。カナダ北東部に暮らすエスキモーたちの生活を丹念に追いながら、詩情漂う独特の映像世界を展開する。アザラシの捕獲、氷の家を造る様子など興味深い場面を満載し、世界的に大ヒットを記録。この映画によって、“ドキュメンタリー・フィルム”なる新語も生まれた。", "1922-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD THINGS", "ワイルドシングス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「恋に落ちたら…」の鬼才J・マクノートンが、ニ転三転する先の読めないサスペンスに挑戦。熾烈にだまし合うクセ者たちの末路を、ありきたりの映像テンポやキャラクター描写に頼ることなく、一貫して流れる奇妙な“軽さ”とともにあぶり出す。舞台はフロリダ州ブルー・ベイ。ハンサムな高校教師サムは、女子高生ケリーをレイプしたと訴えられる。彼に恨みを持つ教え子ケリーと友だちのスージーによる偽証では？　スキャンダルがヒートアップするなか、突然スージーがケリーの仕組んだ計画だったと発言。無罪のサムは示談で大金をせしめるが、刑事デュケは事件の真相を独自に究明しようとする……。淫行、乱行、同性愛が入り乱れる愛と欲望の犯罪劇は、登場人物の誰もが信じられないほどの混戦模様。まるで物語を転落させるために生きているような人間たちを見つめる監督の視線が、不気味なほどクールだ。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Helpless", "Helpless", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ダニエル・シュミットや黒沢清の助監督を務めていた青山真治の監督デビュー作。愛する者を失った若者３人の哀しみや虚無感をバイオレンスとともに描く。高校卒業を目前に控えた健次は、これといった目的もないまま日々を過ごしていた。そんなある日、彼の慕っていたヤクザの安男が４年振りに仮出所した。組の解散と組長の死が信じられない安男は仲間を殺し、組長捜しに出かけるため、健次に妹ユリとショルダーバッグを預ける。組長を捜しながら衝動的殺人を繰り返す安男の足取りを、健次とユリは追うが……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CYBORGII", "サイボーグ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "巨大企業が世界を支配する2074年。女サイボーグ戦士キャッシュが脱走する。人間のコルトや彼らに指示を与えるマーシーが、悪の巨大企業に闘いを仕掛ける。しかし、キャッシュの体内には時限爆弾がセットされていた。女サイボーグに扮するのは「トゥーム・レイダー」シリーズのA・ジョリー。脇を固めるシブい俳優陣にも注目。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TURNING POINT", "黒い街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "故郷の町へ乗り込んで犯罪調査委員会を設立したジョン・コンロイは、秘書のアマンダや幼なじみの記者ジェリーの協力によって、町の顔役マイケルバーガー一味の粛清を果たそうとする。実際に起きたキーフォバー上院議員の粛清活動に案を得て記された、H・マッコイの原作の映画化。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幕末", "幕末", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "時代劇の大スター・中村錦之助が自らプロダクションを設立し、伊藤大輔と組んだ大作。司馬遼太郎の人気小説『竜馬が行く』をベースにして史実に忠実に作り上げた。明治維新を前にして揺れ動く世相を背景に、やがて暗殺される竜馬を、錦之助が重厚に演じている。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCKERS", "ロッカーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ジャマイカNo.1のドラマー、ロリーは、借金して買ったオートバイを盗まれ、大憤慨。さっそく仲間を集め、盗っ人たちに仕返ししようとするが……。トッシュのヒット曲をはじめ、全編を通して流れる本場レゲエ・ミュージックがファンを酔わせる軽妙なアクション・コメディ。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・渡世人", "続・渡世人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "東映任侠スタイルの一側面を打ち出した会心作。下総屋の貸元・喜納が殺される。犯人は現場に残された手ぬぐいから法王寺一家の早川と目された。それから２年、下総屋を継いだ音次郎は早川と再会、一度は彼を殺そうとするが、すべてが法王寺一家の貸元・石黒と万増一家の貸元の陰謀だと聞かされ、早川とともに怒りの刃を彼らに向ける。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タオの月", "タオの月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "“ゼイラム”シリーズで名を上げた雨宮慶太監督が、戦国時代を舞台に撮った伝奇ドラマ。不思議な宝剣を手に入れ、その謎を探る旅に出た若侍と修験僧。天下を狙う悪の首領と対決することになった彼らの運命が、宇宙から飛来した恐るべき怪物を絡めて描かれる。雨宮監督ならではのSFXを駆使したスピーディーなアクションも見もの。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("倚天屠龍記之魔教教主／THE　EVIL　CULT", "カンフー・カルト・マスター　魔教教主", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "香港のベストセラー作家キン・ヨウの小説を、リー・リンチェイの製作・主演（製作のリー・リンチュイはリーの変名）で映画化した伝奇アクション・シリーズの１作。監督は「ゴッド・ギャンブラー」を生んだヒット・メーカー、バリー・ウォン。舞台は元の時代。最強の魔力を秘めた宝刀、倚天刀と屠龍刀をめぐって、全中国の武道家たちは争いを続けていた。幼いころ、その渦中に巻き込まれ、父母を亡くしたモウケイは呪いのために武術を使えない体のまま成長。だが、その呪いを解かれた彼は超人的な強さを発揮し、宝刀を求める人々の争いの中に身を投じていく。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Deeper Shade of Blue", "ディーパー・シェイド・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISTY", "MISTY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "黒澤明の「羅生門」の原作として名高い芥川龍之介の『薮の中』を映像化。武士とその妻が、森の中で粗野な野盗と遭遇。のちに武士が死体となって発見された。その妻は尼寺に保護され、野盗は犯人として逮捕されるが、目撃者や当事者の証言は微妙に食い違い……。３パターンの証言が織り重ねて、ミステリアスなムードを高めつつ、ラストでは真相とおぼしきエピソードが語られる。自然の荘厳さや、官能を捉えた映像美が秀逸。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DIABLE AU COEUR", "スキャンダル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "内向的な少年エリックは、同居しているリンダの美しさに惹かれ愛し合う仲に。しかし彼女を抱いた父親を射殺してしまい、エリックとリンダの危険な逃避行が始まる。今や二人は大きなスキャンダルの当事者となってしまったのだ。J・バーキンの小悪魔的な魅力がまぶしい１作。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODAS　DE　SANGRE", "血の婚礼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "「カルメン」「恋の魔術師」に先立つ、カルロス・サウラ監督のフラメンコ・ミュージカル三部作の第１作。アントニオ・ガデスの舞踏団が劇場の楽屋に入り、メイクや着替えなど準備を済ました後、舞台劇『血の婚礼』の通しリハーサルを始めていく。その芸術的かつ情熱的な踊りと音楽が、観る者を圧倒する。", "1981-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DOORS", "ドアーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "1960年代の伝説的ロック・グループ、ドアーズのヴォーカリスト、ジム・モリソンの半生をO・ストーンが映画化した野心作。ステージ上でのワイセツな言動や、謎の死などで今なお神格化されたカリスマであるモリソンとはいかなる人物だったのだろうか？ モリソンそっくりのV・キルマーが好演。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花旗少林／TREASURE HUNT", "フル・ブラッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「黒薔薇VS黒薔薇」でロングラン・ヒットを飛ばしたJ・ラウ監督の“トラップ（罠）・ガン・アクション・ムービー”。任務を捨て、一人の男として愛する女性のためにすべてを懸けて戦う諜報員をチョウ・ユンファが熱演。チャン・チンは、中国の秘宝奪取の任務を受けたCIA諜報員。中国情報局のトン・リンから、謎の秘宝が少林寺にあることを聞き潜入するが、そこで彼は寺内に監禁されていた不思議な力を持つ少女シウチンと出会う。彼女との交流が続く中、彼は中国情報局の不穏な動きを察知する……。主題歌をテレサ・テンが歌っているのも話題の一つ。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Happy-Go-Lucky", "ハッピー・ゴー・ラッキー", "", "", "http://image.eiga.k-img.com/images/movie/56808/315x210.jpg?3716", 118, "カンヌ、ベルリン、ベネチアの世界3大映画祭の受賞作を中心に、各地の映画祭で話題になりながらも日本未公開だった作品を一挙上映する「三大映画祭週間2011」にて上映。小学校低学年の教師をしているポピーは楽天的な30歳。開けっぴろげな行動が時に周囲に誤解されもするが、それでもポピーは前向きに生きていく。「秘密と嘘」「ヴェラ・ドレイク」のマイク・リー監督が手がけ、主演のサリー・ホーキンスが2008年・第58回ベルリン国際映画祭で銀熊賞（女優賞）を受賞。ゴールデングローブ賞や全米批評家協会賞など、全米の賞レースでも話題になった。", "2011-08-13", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天若有情III烽火佳人／A　MOMENT　OF　ROMANCE", "アンディ・ラウ 戦火の絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「アンディ・ラウの逃避行」に続いて、A・ラウとW・チェンリェンが共演したラブ・ロマンス。日中戦争を背景に、空軍兵と村娘の純粋な愛を描く。1938年、空軍兵ワイは、日本軍との銃撃戦の末、小さな村に不時着、助けてくれた村の娘ウーと恋に落ちる。ところが、彼女は婚約者がいる身。一時はあきらめたものの、二人は惹かれ合う気持ちを抑えることはできなかった。そんななか、ワイは危険な任務のために飛び立っていく……。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女を忘れろ", "女を忘れろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「完全な遊戯」と並ぶ小林旭の初期の代表作。ボクシングの試合で相手を失明させてドラマーに転向した主人公は、浅丘ルリ子扮する美しい娘に心惹かれ彼女のために悪徳土建屋と戦う。恋人と別れて異国へ旅立つ小林を乗せた車が、冬の夜道を静かに去っていくラストシーンの余韻は忘れがたい。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TARZAN，THE APE MAN", "類猿人ターザン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "1910年代。生後１年で別れた探検家の父を訪ねて西アフリカへやって来たジェーンは、ある日ターザンと対面。たくましく心優しい彼に惹かれた彼女は、ついにこの地に留まる決心をする。16代目ターザン映画だが、主眼はデレク。彼女の豊満な肢体が目にまぶしい。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お琴と佐助", "お琴と佐助", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "谷崎潤一郎の『春琴抄』の３度目の映画化で、山本富士子と本郷功次郎が主役の二人を抑えた演技で演じている。大阪商家の娘として生まれたお琴が、盲目ながら遊芸の道をきわめていく陰で、忠実な下男の佐助が彼女に献身していく。主従の関係を越えた秘められた愛をしっとりと描く。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄門社長漫遊記", "黄門社長漫遊記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "業績が悪化しているだけでなく、社長が脳溢血で倒れてしまった某企業。頭を悩ませた従業員一同は急拠隠居中の前社長を呼び戻す。前社長は品質低下の原因を探るため工場の視察に出かけるが、それを快く思わない現社長が秘書の格さんに監視を命じる。一方、地方工場の腐敗に腹を立てた助三郎は陳情のため上京、前社長のあとを追う。“水戸黄門”をモチーフにした、サラリーマン喜劇。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Baby Princess 3Dぱらだいす0（ラブ）", "Baby Princess 3Dぱらだいす0（ラブ）", "", "", "http://image.eiga.k-img.com/images/movie/56748/315x210.jpg?4271", 30, "「電撃G's magazine」（アスキー・メディアワークス）で連載されている読者参加企画で、公野櫻子によるライトノベルも発売されている「Baby Princess」をアニメーション化。とある理由で新しい家族と暮らすことになった陽太郎を迎えたのは、0歳児から大学生までの華やかな19人姉妹だった。陽太郎を迎えることに喜ぶ者も迷惑がる者もそれぞれいるなか、突然、姉妹の母親から旅行の話が飛び出し、陽太郎と19人姉妹は夏休みの旅行にでかけることに……。2Dで制作されたアニメを最新技術で3D化して上映する。", "2011-07-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UK/DK", "UK/DK", "", "", "http://ecx.images-amazon.com/images/I/61ftv9X7puL._SL210_.jpg", 55, "", "2008-02-09", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SOUTHERN STAR", "サファリ大追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "西アフリカのとある国の王様の娘エリカと、アメリカ人地理学者ダンの婚約パーティー中に、王のダイヤ“サザンスター”が盗まれた。その行方を追うハメになったダンとエリカの奮闘ぶりを、ジャングルを舞台に描く。オーソン・ウェルズが脇役で出演。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間の証明", "人間の証明", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "「犬神家の一族」に続く角川春樹事務所製作第２作。日本のオールスター・キャストに加え、アメリカロケ、一流のアメリカ俳優の起用、そしてなによりTVスポットの大量放映という当時としては破格の宣伝方法によって大ヒットを飛ばした。角川映画はこうして映画界に確固たる地位を築き、以後話題作、ヒット作を作り続けていくことになる。また脚本を一般公募で募集したことでも話題となったが、選ばれた脚本がプロの手になるものだったのは皮肉。事件の手掛かりになる西条八十の詩も流行した。アメリカからやって来た黒人青年の不審な死をきっかけに、高度成長する戦後日本の、母子の悲劇が浮かび上がる。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUSPICIOUS　RIVER", "沈みゆく女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「キスト」のL・ストップケウィッチ監督と主演女優M・パーカーが再び手を組んだエロティックなサスペンス・ミステリー。幼い頃に母親を亡くしたヒロインの心の闇を、冷ややかなムードを宿した端正な映像美で語り明かす。カナダの田舎町の川べりに建つモーテル。ここで働く主婦レイラには、娼婦として客の相手をするという秘密の一面があった。やがてレイラは、自分に暴力をふるったゲイリーという若い客とともに町を出る決意を固めるが、その先にはおぞましい出来事が待ち受けていた。", "2000-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO MAN IS AN ISLAND", "最後の一人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1941年12月７日。グアム島に駐留していた海軍通信兵トウィードの前に日本軍の真珠湾奇襲の報が入った。無防備に近かったグアム島は日本軍に攻撃され、仲間をほとんど失ったが、神父によって彼は島の東側に逃げることに成功し……。太平洋戦争中にあった実話をもとに映画化した作品。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("義膽羣英", "ワイルド・ヒーローズ／暗黒街の狼たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "土地の名士・ソウが事故死したことで、一族の後継者をめぐって3人の男たちが火花を散らしていた。ソウの遺言を託された弁護士は、シュウを後継者とする。しかし、その継承を不満とするリュウは、シュウを再三襲撃しシュウの怒りは爆発、ついに血で血を洗う抗争の火ぶたが切って落とされる……。香港映画界の大物、ウー・マとの共同監督作品という珍しい顔合わせ。また、ダニー・リーに見出され喜劇王としてブレイクする直前のチャウ・シンチーが、熱血漢のヤクザをシリアスに熱演している。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユンボギの日記", "ユンボギの日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "大島渚監督が韓国旅行中に撮り溜めた、主に韓国の少年少女の生活を捉えたスチール写真に、少年の手記「ユンボギの日記」の朗読をかぶせて構成された作品。“イ・ユンボギ、君は10歳の少年……”と、何度も繰り返される作者の呼びかけに、韓国への万感がこもる。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHALL WE DANCE", "踊らん哉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "原題の同名タイトル曲『シャル・ウィ・ダンス』など、ガーシュイン兄弟がアステア＆ロジャースのコンビのために書いた曲を中心にしたミュージカル。バレエ界のスターとミュージカル・コメディの女王のロマンスを描いている。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Single Man", "シングルマン", "", "", "http://image.eiga.k-img.com/images/movie/54998/315x210.jpg?1254", 100, "世界的ファッション・デザイナーとして活躍するトム・フォードが長編映画初監督に挑んだ話題作。1964年に発表されたクリストファー・イシャーウッドの同名小説を原作に、長年のパートナーを亡くした50代のゲイのイギリス人大学教授の愛と葛藤を描き出す。「ブリジット・ジョーンズの日記」のコリン・ファースが主人公を繊細に演じ、ベネチア国際映画祭で主演男優賞を獲得。共演にジュリアン・ムーア、マシュー・グードほか。", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　FAST　AND　THE　FURIOUS", "ワイルド・スピード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "改造車を乗り回す一味によるトラック襲撃事件が頻発するロサンゼルス。警察がストリート・レースに熱狂する若者たちをマークするなか、カリスマ的なレーサー、ドミニクはその世界の頂点に君臨していた。そんなとき若きおとり捜査官ブライアンは、素性を隠してドミニクに近づいていくが……。スピードの限界に挑む男たちの友情と葛藤を、圧倒的な迫力のカー・アクション満載で描いた熱血ドラマ。ドミニクをワイルドに演じたヴィン・ディーゼルが一躍スターに。", "2001-10-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐を呼ぶ十八人", "嵐を呼ぶ十八人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "日本の高度成長下の底辺で働く社外工の青年たちの姿を、吉田喜重が従来の労働者映画への批判をこめて描いた作品。瀬戸内海に面したK市の造船所の下請けで働く社外工の島崎は、タコ師（労働者の手配師）が集めてきた新入りの社外工18人の僚監になる。彼らはいずれも札つきの連中ばかりで、花札バクチをやる奴、ケンカをする奴と、島崎はことごとく手を焼く。夏季闘争のシーズンがやってくると、組合員でない社外工には稼ぎ時だ。懐の暖かくなった島崎と18人は夏祭りに出かけるが、仲間の一人が島崎の恋人を暴行する。そして……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐の季節", "嵐の季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "高橋三千綱の恋愛小説『掠奪の初夏』の映画化。元空手選手の片桐は、骨折と父の死を同時に体験し自立を決意、年上の恋人・泰子からも離れストイックな生活を送っていた。そんな中、片桐に命を救われた過去をもつ元不良少女のアイが現れ、二人の奇妙な関係が始まる。ある日、ふとしたことから片桐はアクション俳優のチャンスを手にするが、過去の因縁からアイは新興ヤクザに襲われてしまう。映画監督の崔洋一が俳優として出演しているのも見もの。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マスター・オブ・サンダー　決戦!!封魔龍虎伝", "マスター・オブ・サンダー　決戦!!封魔龍虎伝", "", "", "http://image.eiga.k-img.com/images/movie/33458/315x210.jpg?4578", 92, "日本アクション映画界の2大スター、千葉真一と倉田保昭が初共演を果たした格闘アクション。永い封印を打ち破り現世に甦った怨霊・小野篁。かつてその怨霊を封印した三徳和尚や源流和尚には、年齢的に再び怨霊を封印することは不可能だった。そこで、三徳和尚の弟子アユミは、仲間を集めて怨霊退治の修行を始める。主人公アユミを「特捜戦隊デカレンジャー」の木下あゆ美が演じるほか、人気ヒーローシリーズの若手俳優たちが集結。", "2006-08-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The King", "キング　罪の王", "", "", "http://image.eiga.k-img.com/images/movie/1698/315x210.jpg?4032", 105, "古より語り継がれてきた父と子の対立と愛憎を、ガエル・ガルシア・ベルナル主演で描くサスペンスドラマ。海軍を除隊したばかりのエルビスは、亡き母親から聞かされていた父親デビッドに会うためにテキサス南部の小さな町を訪れる。だが、現在は牧師となり、妻、息子、娘に囲まれて幸せに暮らしているデビッドは、エルビスを拒絶する……。監督は「ハワーズ・エンド」などの編集を経て、TVドキュメンタリーなどを手がけてきたジェームズ・マーシュ。", "2006-11-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("そよかぜ リンゴの唄", "そよかぜ リンゴの唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "戦後初めて企画された作品で、ヒット曲『りんごの唄』で有名な音楽映画。歌好きのレビューの照明係・みち子はチャンスをつかみスターになるが、やがて、その座を捨てて恋する男と結ばれる。最初の設定は街頭慰問隊で内容も米軍の機嫌をうかがったものだが、逆に軽く明るくウキウキした感じが時代の心を捉えて、大ヒットした。", "1945-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPER FORCE ２", "スーパーフォース２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ストーリーはまったく別物ながら、前作のスーパースーツやスーパーサイクルといったメカ設定を使って作られた、近未来SFバイオレンス。遺伝子操作によって殺人マシンとなった男、U・ジーンが指令に背き逃走。追う者と追われる者——超人同士の死闘の幕が上がる……。キャラクター・デザインを担当したのは「ビートルジュース」でアカデミー賞を受賞したロバート・ショート。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUATRE AVENTURES DE REINETTE ET MIRABELLE", "レネットとミラベル 四つの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「緑の光線」「友だちの恋人」などで、日本にも熱狂的ファンを持つ、E・ロメールの粋でオシャレなオムニバス風の一編。夏のある日、田舎道で偶然知り合った二人の少女レネットとミラベル。性格も人生観も環境もまったく異なる彼女たちが出会う、日常のささいな事件を、４つのエピソード（「青の時間」「カフェのボーイ」「物乞い、万引、ペテン師の女」「絵の販売」）にまとめて綴っていく。一つの出来事に対する、レネットとミラベルの相反する反応が面白い。若い女性の微妙な心理描写を得意とするロメールの、まさに面目躍如の演出。光に満ちた澄んだ色彩感とリズミカルな会話が、一段と小気味よい。DVDは「エリック・ロメール コレクション(6)」に収録。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GHOST OF FRANKENSTEIN", "フランケンシュタインの幽霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "ロン・チェイニーJr.演じるモンスターが初登場する怪奇映画シリーズ。新たな恐怖を呼び込む仕掛けが満載だ。怪人イゴールは依然として生きている怪物を連れて、フランケンシュタイン男爵の次男を訪ねた。次男は怪物に正常な脳を移植して残虐性を抑えようとしたが、共同研究者の博士の裏切りにあいイゴールの脳を入れてしまう。より恐るべき怪物に変身して暴れ出す。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TASTE　OF　FEAR", "恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "「フランケンシュタインの逆襲」「吸血鬼ドラキュラ」などの脚本家J・サングスターが、製作と脚本を担当したミステリー・ホラー。母親の死を契機に、フランスのニースに住む父親の屋敷を訪ねた若い女性ペニー。しかし父親は急用で外出しており、車椅子の身のペニーの周辺では次々と不可解な出来事が起こる。怪奇俳優C・リーが怪しげな医師役で助演している。", "1961-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("網走番外地・大雪原の対決", "網走番外地・大雪原の対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "正月作品らしく派手な構成になった。仲間の恨みを晴らすためにノサップへ現れた橘真一は、発見された大油田の利権争いに巻き込まれ、暴力団と死闘を繰り広げる。吉田輝雄が例によってキザな殺し屋を軽快に演じる。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DU　SKAL　ARE　DIN　HUSTRU", "あるじ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "後に「裁かるゝジャンヌ」「吸血鬼」を監督した名匠C・T・ドライヤーの、サイレント時代のコメディ。妻を召使のように扱い、専制君主のように振る舞う夫。妻はそんな彼に愛想を尽かして家を出て行ってしまう。夫はかつての乳母の再教育の大変さと妻がいない不自由さを味わい、初めて彼女のありがたさを知るのだった……。", "1925-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOW TO STEAL A MILLION", "おしゃれ泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "シャルルは美術館に絵の寄贈もする名画のコレクター。だが実は贋作の名人で寄贈はすべてニセモノ。この秘密を知って胸を痛めるのは娘ニコル。パリの美術商は、シャルルに不信を抱き、私立探偵シモンを雇い調査を依頼する。シャルル家に忍び込んだシモンはニコルに発見されるが、その美しさに一目惚れ。ニコルはシモンを泥棒と思い込み、父が政府の依頼で美術館に出品している名画を盗み出してくれと頼む。ニコルとシモンは美術館へ忍び込み……。「ローマの休日」「噂の二人」に続いて、ワイラーとオードリーがコンビを組んだ、ロマンティック・コメディ。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARKNESS", "ダークネス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「ネイムレス・無名恐怖」のジャウマ・バラゲロ監督の第2作。皆既日蝕の到来とともに、40年前に起こった子供の集団失踪事件に関連する怪現象に巻き込まれた一家の恐怖を描くホラー。父親の病気療養のため、アメリカからスペイン郊外の屋敷に引っ越してきたレジーナ。しかし家の電気がたびたび消え、父親や弟の身に異変が起こるなど怪現象が続発する。やがてレジーナは、この家にまつわる恐ろしい秘密を知る。", "2002-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE HEAVENLY NIGHT", "天国の一夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ブダペストの町の花売り娘がキャバレーの歌手にせがまれて彼女の身代わりをつとめ、伯爵に見そめられるが、歌手もまた伯爵に恋したことから大混乱となるオペレッタ喜劇。ゴールドウィンがイギリスの舞台で発見したレイの映画デビュー作で、ボールズやタシュマンとともに美声を聞かせる。", "1930-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TREASURE ISLAND", "宝島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ディズニー・プロ初の長編劇映画。原作は有名なスティーヴンソンの同名小説。海賊が隠した財宝を探しに宝島へ向かったジム少年と片眼の悪漢ロング・ジョン・シルバー船長たちを待ち受けていたものは……。夢とスリルにあふれる傑作冒険ファンタジー。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学生〈せいがく〉やくざ", "学生〈せいがく〉やくざ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "侠客だった祖父の元にいた板前・源吉の計らいで、一人前の男になるべく大阪・浅田一家に旅立った北陽大生・島崎角太郎。落ち着いた先の釜ヶ崎は、かつての浅田一家の若衆・秋津が興した新興暴力団・北政会が牛耳っていた。労務者に混じり働く角太郎は、そこで暴力団追放・ピンハネ一掃運動をしている堀田と出会った。そんな時、北政会の車が労務者の中に突っ込む事件が起こる……。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("育子からの手紙", "育子からの手紙", "", "", "http://image.eiga.k-img.com/images/movie/55218/315x210.jpg?2444", 106, "主婦の副島喜美子が、同室で闘病した少女の実話を綴った「育子からの手紙　十五歳、ガンと闘った日々」の映画化。喜美子の入院する病室に13歳の育子が緊急入院してくる。喜美子は、骨折の激しい痛みに苦しむ育子と、彼女を献身的に看病する母の姿を温かく見守っていた。次第に喜美子と育子の間には、病気と闘う同志のような絆が芽生え、歳を越えた友情を築いていくが……。主演は原日出子。", "2010-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TYSTNADEN", "沈黙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ベルイマンが“神の存在”をテーマにした「鏡の中にある如く」「冬の光」に続く３部作の１本。言葉の通じない異国を旅する姉妹の確執を通し、愛情の絆を失った現代人の苦悩を描く。“人は宗教によって救済されるか”の問題を内包しつつ、人間のコミュニケーションの在り方を厳格なドラマによって問う重厚な作品。", "1963-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DIARY OF ANNE FRANK", "アンネの日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "世界的なベストセラーになった『アンネの日記』の映画化。第二次大戦中のオランダ。ナチスの凶暴なユダヤ人狩りを逃れて、アンネ・フランクは家族とともに屋根裏部屋に隠れて暮らすが……。人間の善意を信じて疑わなかったアンネの短い青春を映像美の中に描いた感動作。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dream House", "ドリーム・ハウス（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死亡塔／TOWER OF DEATH", "ブルース・リー 死亡の塔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "B・リー扮するカンフーの達人ビリー・ローの仇を討つために、日本へやって来た弟ボビーの活躍を描いたアクション映画。兄の死因究明のために、地下にある死亡の塔に潜入する彼であったが……。タイトルに“ブルース・リー”と付いているが、本人が登場する場面はわずかで、これまで未使用に終わっていた映像を使っているにすぎない。", "1981-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIGHTNING IN A BOTTLE", "ライトニング・イン・ア・ボトル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ブルーズ生誕100年を記念した一大プロジェクトの一つとして、'03年2月7日に開催されたニューヨークのラジオシティ・ミュージックホールでのライヴを収めた音楽ドキュメント。B.B.キングら伝説のブルースマンをはじめ、50名を超える豪華ミュージシャンの競演を心ゆくまで味わいたい。'03年2月7日、ブルース生誕100周年を記念したスペシャル・コンサートが開催の運びに。一夜限りの奇跡のライヴが始まる。", "2005-03-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIENS DU TOUT", "百貨店大百科", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "日本では1997年に公開されたC・クラピッシュ監督の長編デビュー作。経営不振にあえぐ老舗百貨店を舞台に、その再建の物語と雑多な人間模様がみずみずしく描かれる。冒頭、小刻みに語られる挿話が示すように、大きな起伏のないドラマをささやかな出来事の積み重ねで盛り上げるクラピッシュの手法は見事で、第１作にして独自の語り口とテーマを獲得している。物語の主人公は倒産寸前の百貨店“ル・グラン・ギャラリー”の新社長ルプチ。彼は経営立て直しのため従業員へ意識改革を訴えていく……。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャズ大名", "ジャズ大名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "“日本にジャズがやってきたのは幕末だった!?”という筒井康隆の奇想天外な短編を、鬼才・岡本喜八が一種のやぶれかぶれスピリットで映画化した快作。もちろん、戦争なんかやってるより、ジャズをやってた方がいいという喜八式“反戦論”もキッチリと描かれている。船が嵐に遭い、幕末の日本へ黒人４人が漂流した。駿河の藩主・海郷亮勝は黒人たちを助けて地下牢に保護するが、彼らの演奏するジャズの虜になり、クラリネットを習い始める。やがて城中がジャムセッションする中、時代は明治へと移っていく……。ラストの10数分にも及ぶ地下牢ジャムセッションはいうに及ばず、全編が、疾走する快感を観る者に与えてくれる。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大阪物語", "大阪物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「赤線地帯」を撮り上げた溝口健二が、次回作として依田義賢とともに構想していた作品。1956年、溝口が58歳で急死したことにより、吉村公三郎がメガホンを執ることになった。貧しい農家の出身で極貧の身から一代にして財をなした商売人・仁兵衛の人生を、喜劇的な描写で映画化している。西鶴の『日本永代蔵』『当世胸算用』『萬の文反古』のエピソードをもとにしたストーリー。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREE MONEY", "フリーマネー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ノースダコタのツキのない悪友コンビ、バドとラリーが、ソーンソン姉妹と結婚するはめに。姉妹の父親スウィードは鬼看守のごとき男で、まさしく監獄暮らしのような結婚生活に疲れ果てたバドたちは、無謀にも現金輸送列車の襲撃計画を実行に移す。名優M・ブランドが凶暴なスウィードを演じ、奇怪な言動を連発するコミカルなクライム・アクション。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いつかの君へ", "いつかの君へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "", "2007-07-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恐るべき火遊び", "恐るべき火遊び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "東宝プログラム・ピクチャーの一翼を荷った福田監督の出発点となるハイティーン青春エレジー。体制にどっぷりつかった親たちにジェームス・ディーン主演の「理由なき反抗」のような行為を繰り返し、疾走する自動車の直前を横切って車を止める“ウェイ・クロス・マンボ”と称する危険な遊びに、19歳の梶本たちは興じる。ある日、急ブレーキをかけた車に社長令嬢・麻理子が乗っていた。梶本と麻理子は遊び仲間として若さを浪費し合うのだが……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANDERLAY", "コルシカン・ファイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "地中海に浮かぶコルシカ島の美しい景観をバックに展開するサスペンス。人探しでこの島を訪れたパリの私立探偵が、謎の覆面集団やセクシー美女らが絡む怪事件に巻き込まれ、危機一髪の冒険を繰り広げていく。ジャン・レノのハードボイルドな存在感とユーモアを満喫できる一作だ。簡単な仕事を片づけるつもりでコルシカ島にやってきた探偵ジャック。ところが、島民は奇妙な人たちばかりで、彼は次々とトラブルに見舞われていく……。", "2006-03-11", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE HEAT", "ブルーヒート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "個性派俳優B・デネヒー主演によるポリス・アクション。麻薬の特別捜査班を従えた警官デイリーは、ある日コカイン精製所になっている精肉工場へ乗り込む。その途端に工場から出火、すべては灰と消えた。そのうえデイリーたちに放火の疑いがかけられて……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君に届け", "君に届け", "", "", "http://image.eiga.k-img.com/images/movie/55448/315x210.jpg?1670", 0, "累計発行部数1000万部を突破する椎名軽穂の人気漫画を、多部未華子と三浦春馬主演で映画化。誰よりも健気で純粋ながら、見た目が暗いことで周囲から「貞子」と呼ばれる女子高生の黒沼爽子が、クラスの人気者・風走翔太の気さくな優しさに触れながら、徐々に打ち解けて変わっていく姿を描く。多部、三浦のほか蓮佛美沙子、桐谷美玲、夏菜、青山ハルらが出演し、「おと・な・り」「ダイブ!!」など青春映画に定評のある熊澤尚人監督がメガホンをとる。", "2010-09-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NANNY MCPHEE", "ナニー・マクフィーの魔法のステッキ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "イギリスの演技派女優エマ・トンプソンの脚本・主演によるファンタジー。英国の児童書を基に、魔法使いと子供たちの交流をユーモラスに描く。大きなイボや付け鼻で、魔法使いになりきったトンプソンの妙演は絶品。母を亡くして以来17人の子守をいたずらで追い出したブラウン家の７人の子供。そんな彼らのもとにマクフィーがやってくる。彼女の魔法のステッキにより、子どもたちは行儀を学び始め……。", "2006-04-15", "1,3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("皇家師姐III 雌雄大盗／IN THE LINE OF DUTY PART３", "香港・東京特捜刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "香港D&B社のアクション・シリーズ“皇家師姐”の第３作。香港の新入り警官と日本人刑事が、ともに宝石ギャングを追い詰めていく。主役のC・カーンは「三頭魔王」のヒロインなどを経て、香港映画界にデビューした期待のアクション女優。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RIVER", "河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "インドを流れる悠久のガンジス川の流れに寄せた、名匠J・ルノワールの恋愛ドラマ。ベンガル地方で製麻業を営む英国人一家を中心に、その長女ハリエット、工場主の娘ヴァレリー、そしてアメリカ人とインド人とのハーフの娘メラニーの３人娘と、第二次大戦で片足を失った男との淡い関係を、ゆったりとしたリズムのなかに描き出す。奔放なヴァレリー、インド的な神秘を秘めたメラニー、そして男を慕い川に身を投げるハリエット。それぞれの娘たちの姿がガンジスに反映される。ルノワールにとって初のカラー作品であるが、中間色を省いた色彩の圧倒的な躍動感が注目される。ヴェネチア映画祭国際賞を受賞。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CANDLESHOE", "キャンドルシュー　セント・エドモンドの秘宝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "若き日のJ・フォスター主演による、ほのぼのタッチの冒険物語。14歳の孤児ケイシーはある男の策動で、行方不明中の娘になりすまし、キャンドルシュー邸に入り込む。彼女はそこで隠された財宝を捜すはずだったが、心温かい夫人や執事に囲まれ、いつの間にか当初の目的を忘れて打ち解けていくのだった。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NECRONOMICON", "ネクロノミカン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "恐怖小説の祖ラヴクラフトの原作を映画化、日・米・仏３ヵ国の監督が演出したオムニバス・ホラー。第１話は60年前の惨劇を再体験する男の物語。第２話は不老不死を研究した博士と若い娘の愛の悲劇。第３話は魔物の巣に迷い込んだ男の悪夢。予算のせいか特殊メイクの完成度が今一つだが、ロマンと恐怖を描き出す語り口はどれも一級品。SFXをトム・サヴィーニ、スクリーミング・マッド・ジョージ、トッド・マスターズが担当している。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STOLEN SUMMER", "夏休みのレモネード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1970年代のシカゴを舞台に、純真な少年のホロ苦い体験を描く好編。宗教が題材のシビアなテーマを扱いながらもヒューマンな内容に仕上げた脚本が素晴らしい。1976年のシカゴ。アイルランド系カトリックの少年ピートは、異教徒を改宗させると天国に行けると知り、ユダヤ教の教会堂へ。そこで彼は不治の病に冒されているダニーと出会う。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ねこのひげ", "ねこのひげ", "", "", "http://image.eiga.k-img.com/images/movie/53438/315x210.jpg?2346", 100, "それぞれの家族を捨てて一緒に暮らし始めてから3年が過ぎた38歳のえりと39歳の賢治。賢治は別れた妻と幼い娘に未だに罪悪感を抱いており、えりも苦しむ賢治に対して責任を感じている。そんな2人と、彼らを取り巻く人々が繰り広げる穏やかな日常を繊細なタッチで綴った人間ドラマ。主演を務めるのは、企画・製作・脚本も手掛ける大城英司と「殯(もがり)の森」の渡辺真起子。共演には馬渕晴子、モロ師岡らベテラン俳優が揃う。", "2008-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NICK’S　MOVIE　LIGHTNING　OVER　WATER", "ニックス・ムービー／水上の稲妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "名匠W・ヴェンダースが、ジェームズ・ディーン主演の青春映画「理由なき反抗」などで知られるN・レイ監督との共同作業を詳細に綴った異色作。撮影当時、レイ監督は末期の肺がんを患っており、ある種の緊張感に満ちたドキュメンタリー・タッチの人間ドラマとしても興味深い一作だ。ヴェンダースがある映画化の話をするためにレイ監督を訪問するが、彼は不治の病の床にあった。その瞬間をカメラは克明に映し出す。", "1980-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARCTIC　MISSION：THE　GREAT　ADVENTURE", "アークティックミッション白熊はみていた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "カナダ国立映画制作庁が、ハイビジョン撮影で制作した大自然ドキュメンタリー。生物学者ジャン・ルミール率いる科学者たちが、海洋調査船に乗り込み北極海北西航路の旅へ出発。餓死の危機に瀕した白熊をはじめ、生態系バランスを崩しつつある動物たちの実態を通して地球温暖化の深刻さを訴える。", "2004-12-11", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIX WEEKS", "オータム・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "不治の病に冒され、残された６週間を懸命に生きる少女が、憧れのブロードウェイで踊る日がきた……。わずか12歳の若さでこの世を去った、天才的バレエダンサーの愛と青春の物語を通して、人間の生の気高さを描く感動の一編。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バージンブルース", "バージンブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "脱サラでラーメン屋を開業したものの、やる気のない40代の中年男と、空しさに耐えかねて時々集団万引をする予備校の女子学生とのあてのない旅を描いた作品。女子学生が襲われそうになった時の長門のセリフ“オレはバージンを守る義務があるんだ”が印象的。さらに秋吉のアンニュイな魅力にも注目。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("20,000 Leagues Under the Sea: Captain Nemo", "海底2万マイル（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2014-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DARK CRYSTAL", "ダーククリスタル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "『セサミ・ストリート』などのマペット人形の名手J・ヘンソンとF・オズが作り上げたファンタジー・アドベンチャー。黒水晶と貪欲で残忍なスケクシー族に支配された暗黒の国に平和を取り戻そうと、ゲルフリン族の生き残りジェン少年が立ち上がった……。想像上の生物、設定が見もの。アヴォリアッツ国際ファンタスティック映画祭グランプリ受賞。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くたばれ愚連隊", "くたばれ愚連隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "鈴木清順初のカラー作品であり、ダイヤモンド・ラインの和田浩治と初めてコンビを組んだ記念すべき作品。二人のコンビは７作続いた。淡路島の旧家の当主が死に、妾腹の定夫が跡取りとして島に呼ばれる。そこへ用地買収を進める悪徳業者と定夫の実の母が現れ……。痛快青春アクション。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アブラクサスの祭", "アブラクサスの祭", "", "", "http://image.eiga.k-img.com/images/movie/54808/315x210.jpg?1945", 0, "現役住職の芥川賞作家・玄侑宗久の小説を、ミュージシャンのスネオヘアー主演で映画化。ともさかりえ、本上まなみ、小林薫らが脇を固める。かつてミュージシャンだった僧侶・浄念は、仏の道を志しながら音楽への夢があきらめきれずにいた。浄念は妻や周囲の反対を押し切り、何とかライブを実現させようと奔走するが……。", "2010-12-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANTON", "ダントン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "フランス革命の中心人物として歴史にその名をとどめるダントンとロベスピエール。これは革命の初期、固い絆に結ばれてフランス人民を動かす原動力となった彼らの、数年後の確執に焦点をあてた作品。A・ワイダ監督は、一つの目的のために結ばれていた者同士が、宿命的に対立し、悩み、悲しむ過程の中に人間の本質を描き出そうと試みている。", "1982-01-01", "2,42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALADDIN", "アラジン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ディズニー・スタジオが最新のアニメーション技術を駆使して作り上げた冒険ファンタジー。貧しくも希望にあふれる青年アラジンは、魔法の洞窟で不思議なランプを手に入れ、その中から現れた魔法使いのジーニーに“３つの願い”を叶えてもらえることになる。王宮の美しいお姫様、ジャスミンに恋をしていたアラジンは、さっそく王子に変身させてもらい宮殿に向かうが、王の座を狙う悪大臣ジャファーはアラジンと姫の結婚を阻止すべく動き出した……。アメリカで前作の「美女と野獣」をもしのぐ、当時のアニメーション映画史上最大のヒットとなった、ディズニー長編第31作。『千夜一夜物語』をベースに現代的なセンスで脚色された、ハイテンポの痛快ロマンとなっている。CGを導入し、従来のアニメーション映画とは一味違う、立体感あふれるシーンの数々も出色の出来。またジーニーの声を担当したR・ウィリアムズによる、たたみかけるようなテンポの話芸も楽しめる。彼はこの作品でゴールデン・グローブ賞特別賞を受賞した。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRY−BABY", "クライ・ベイビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「ピンク・フラミンゴ」で知られる、異才J・ウォーターズが監督した青春ラブ・コメディ。1954年のアメリカ・ボルチモアを舞台に、対立する不良グループのリーダーとお嬢様が恋に落ち、グループを巻き込んで大騒動を繰り広げるというもの。主演のJ・デップにとって「シザーハンズ」の主役を手にするための、大きなステップボードになった。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAID ON ROMMEL", "ロンメル軍団を叩け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "第二次大戦下の1942年、北アフリカを舞台に勇名を馳せたロンメル軍団に挑んだ、イギリス・コマンド部隊の活躍を描く戦争アクション。アーサー・ヒラー監督による「トブルク戦線」（1967）のヒットで、その姉妹編として作られた作品。同作品のフィルムを流用している。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cashback", "フローズン・タイム", "", "", "http://image.eiga.k-img.com/images/movie/53128/315x210.jpg?1942", 102, "ファッション誌のフォトグラファーとしても活躍するイギリスの俊英ショーン・エリスが、アカデミー賞にノミネートされた自身の短編作品を長編映画化。失恋によるショックから不眠症になった画家志望の青年ベンは、仕方なく24時間営業のスーパーでアルバイトをはじめる。ところが、そこで働いているのは変わり者ばかり。相変わらず眠ることができず精神的に限界に達したベンは、ある日自分以外の世界の時間が止まっていることに気付き……。", "2008-01-26", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LOST WORLD", "失われた世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "アマゾン川上流に今もなお太古からの恐竜が生き残っているという学説を発表したチャレンジャー博士は、探険隊を組織し、アマゾンの奥地へと向かう。そして彼らが目にしたものは、まさに紀元前の“失われた世界”だった！　コナン・ドイルの原作を映画化したSF冒険映画。特撮は安っぽいが、それなりに楽しめる一作。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA HAINE", "憎しみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "当時27歳のM・カソヴィッツがカンヌ映画祭で最優秀監督賞を受賞した作品。ヴィンス、サイード、ユベールの３人は、パリ郊外の“バンリュー”と呼ばれる地域に住んでいた。移民労働者や低所得者層が多く暮らすこの町で、ある日暴動が発生する。きっかけは、刑事から暴行を受けた少年が瀕死の重傷を負い、病院に担ぎ込まれたことだった。暴動には参加しなかったものの、社会のシステムや警察権力に憎しみを抱いている３人は、少年の病院へ見舞いに行くことに。しかし、ヴィンスが暴動の際に警官が落とした拳銃を手に入れたことから、彼らの“憎しみ”は、暴発の危険性を帯びていく。閉鎖された社会での若者のやり場のない怒りや憎しみを、モノクロームの鮮烈な映像で表現している。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN TO RIDE", "ボンバー・ライダーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "第二次大戦直前の頃。ハーレー・ダヴィッドソンを乗り回していたならず者グレーディーは、とうとう刑務所送りに。だが、彼のバイクの腕前に目を付けた陸軍大尉が、彼をスカウトする。ナチスに囚われている重要人物の救出作戦のチーフにしようというのだ。アウトローなグレーディーが、軍隊に入ることになる。映画の中に登場するハーレーは、ほとんどが39年型の年代物。バイク・ファンならずとも、楽しめる名車がそろう。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おばちゃんチップス", "おばちゃんチップス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "“2時間ドラマの帝王”こと船越英一郎主演の人情コメディ。東京から大阪にやって来た中年男性と地元のパワフルなおばちゃんたちとの交流を、笑いと涙を織り交ぜながら描く。方言を研究するために大阪へ引っ越してきた家弓修平は、昔ながらの雑貨屋“樋元商店”で下宿生活を始める。店主の千春や近所のおばちゃんたちのパワーに圧倒されながらも、修平は彼女たちと次第に打ち解けていく。しかし、桶本商店が地上げのターゲットにされてしまい……。", "2007-01-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SUGERLAND EXPRESS", "続・激突！ カージャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "アメリカのTV作品『激突！』（日本では劇場公開）で、演出手腕に冴えを見せたスピルバーグの劇場映画デビュー作。パトカーをカー・ジャックしたおかしな夫婦をコミカルに描きながらも、大量のパトカーを登場させてのアクション場面の演出はシャープそのもの。B・ジョンソンが好演。“続”となっているが、「激突！」の続編ではない。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛の渇き", "愛の渇き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "三島由紀夫の同名小説を蔵原惟繕監督、浅丘ルリ子主演で映画化した文芸メロドラマ。大阪の豊中付近の大邸宅。未亡人・杉本悦子は夫の死後も杉本家に住み続け、いつしか義父と深い関係に陥っていた。ある日悦子は下男・三郎の若く粗野な肉体のたくましさに心惹かれるが、三郎は女中に子を孕ませていた……。「執炎」と並ぶ蔵原惟繕監督の代表作で、果てることのない愛のさすらいが白黒画面の中に鮮烈に焼きつけられた。「執炎」で演技開眼したといわれる浅丘ルリ子が、ここでも期待に応えて難役を見事にこなし、日活唯一の演技派スターの地位を不動のものにした。特にルリ子扮する未亡人が、下男の三郎に自分の愛の渇きを訴えて三郎に抱かれるものの、彼の強い抱擁が単なる男の暴力にすぎないと訴え、三郎を突き放すシーンは圧巻。浅丘ルリ子の女心のもろさと毅然とした外面の二律背反ぶりが示す演技は、実に見事であった。なお、蔵原惟繕監督は、この作品を最後に日活を退社しフリーになった。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("看上去很美", "小さな赤い花", "", "", "http://image.eiga.k-img.com/images/movie/53738/315x210.jpg?2826", 92, "“良い子”には紙製の赤い花がもらえ、“悪い子”は花が没収される——そんなシステムの全寮制幼稚園に預けられた4歳の少年チアンは、赤い花をもらうために努力するが、やがて厳しい統制に子供ながらに疑問を持ち始め、周りを巻き込み反抗的な行動を取り始める。作家ワン・シュオの半自伝的小説を原作に、「ウォ・アイ・ニー」「緑茶」のチャン・ユアン監督が過剰な教育政策に潜む矛盾を描き出す。2006年ベルリン国際映画祭・国際芸術映画評論連盟賞受賞。", "2008-08-23", "8,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新生 トイレの花子さん", "新生 トイレの花子さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "全国の小・中学生の間で流布する怪談話をまとめたベストセラー『トイレの花子さん』を、当時のチャイドル・ブームを支えた前田愛らの出演で映画化したホラー。里美が入学した緑台中学で起きる様々な怪奇現象。11年前、彼女の姉・かおりが行方不明になった時と同じように、生徒たちの間に誰かが死ぬという噂が流れる。すべては校舎裏のほこらに納められている人形のたたりなのか。かおりの担任でもあった教師の浅岡は事態を探るため、当時の彼女の友人で不思議な霊能力を持つ鹿島玲子を学校に招くが……。脚本は「THE JUON／呪怨」でハリウッド・デビューを果した高橋洋。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若き日の次郎長 東海の顔役", "若き日の次郎長 東海の顔役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "マキノ雅広監督と中村錦之助のコンビによる“若き日の次郎長”もの。清水港は米飢饉で、米問屋坂田屋の養子・長五郎は昼間からバクチに明けくれていた。しかし、ふとしたことから親子の情愛に心をうたれた彼は、難民たちに禁制の米を売る決心をする。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sunshine Cleaning", "サンシャイン・クリーニング", "", "", "http://image.eiga.k-img.com/images/movie/54328/315x210.jpg?3666", 92, "「リトル・ミス・サンシャイン」の製作スタッフが再び集結し、不器用な家族の再生を描いたドラマ。「魔法にかけられて」のエイミー・アダムス、「プラダを着た悪魔」のエミリー・ブラントが姉妹役を演じる。ローズは清掃の仕事をしながら昔の恋人と不倫しているシングルマザー。妹ノラは未だに父親と同居するフリーター。ある日、ローズは事件現場の清掃で大金が稼げると聞き、ノラを誘って事業をスタートさせるが……。", "2009-07-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE ANGST DES TORMANN’S BEIM ELFMETER", "ゴールキーパーの不安", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「都市の夏」に続きヴェンダースが撮った初の本格的劇場用長編映画。プロサッカーのゴールキーパー、ヨーゼフはウィーンでの試合で審判を罵倒して退場処分に。ウィーンの街をさまよい歩く彼は映画館のレジ係の女性と仲良くなるが、彼女の部屋で一晩過ごしたあと衝動的に彼女を絞め殺してしまう……。", "1972-01-01", "7,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMPIRE OF THE SUN", "太陽の帝国", "", "", "http://ecx.images-amazon.com/images/I/41D6XPIYq0L._SL210_.jpg", 152, "スピルバーグが撮った、シリアスな戦争歴史ドラマ。少年ジムの目を通して、戦争を描き、戦争が成長過程にある少年の感受性にどんな影響を与えるかを、映像詩として見事に捉えている。上海で暮らすジムとその両親。ジムは日本軍のゼロ戦のパイロットになり、大空を飛ぶ姿を思い描いていた。戦争が迫り、ジムの一家も上海脱出の準備をしていたが、時すでに遅く、日本軍が上海に侵攻してくる。否応なく戦争という波に巻き込まれ、ジムは市街で両親とはぐれてしまう。それからの飢えや孤独にさらされ、苦しみながらもたくましく生き抜いていくジムの姿を淡々と描き出す。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・ゴキブリ", "ザ・ゴキブリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "新岡勲の劇画を映画化した、ダーティな手口で暴力団（ゴキブリ）をせん滅する刑事・鳴海涼が活躍するアクション編。コンビナート臨海工場地帯で、公害企業と結託するヤクザや政治家と鳴神が戦う。「ゴキブリ刑事」の続編だが、結局この２作で打ち止めとなった。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MILITIA", "ブレイク・スルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "カリスマ的なテロリスト、モンゴメリ率いる武闘組織“自由の兄弟”が勢力を拡大し、世界平和を脅かす。アメリカの政府機関ATFは、国際会議場の爆破を企てる一味を壊滅させるため、エージェントのカーターと“自由の兄弟”の元リーダー、ウィリアムを派遣する。テロ集団根絶に挑む男たちの闘いを、スペクタクル満載で描く軍事アクション。DVDタイトルは「ブレイクスルー　極秘機動部隊」。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画クレヨンしんちゃん　オタケベ！カスカベ野生王国", "映画クレヨンしんちゃん　オタケベ！カスカベ野生王国", "", "", "http://image.eiga.k-img.com/images/movie/54308/315x210.jpg?1691", 97, "子供から大人まで根強い人気を誇るアニメ「クレヨンしんちゃん」の劇場版第17弾。エコ活動の一環としてゴミ拾いをしていたしんのすけは、河原でおいしそうなドリンクを発見。しかしそのドリンクは、カスカベの新町長・四膳守が推進する究極のエコ活動“人類動物化計画”の第一歩だった。しんのすけが冷蔵庫で冷やしておいたドリンクを勝手に飲んでしまった両親は、ニワトリとヒョウに変身してしまい……。", "2009-04-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千人斬／THE BEHEADED1000", "ジョイ・ウォンの妖女伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "“黄金刀”の使い手ユアンとその弟子アルは、８人の盗賊“鬼八仙”を女一人だけ残して処刑する。残った女ユイは、ユアンに復讐するべく、ユアンとその仲間に近づくものの、あえなく返り討ちにあう。ところが、死んだユイたち“鬼八仙”は、妖魔となって復活。再びユアンたちに迫る……。J・ウォンが持ち前の色香で男を惑わし、男根を切り取るなど、一味違う悪女ユイを演じる。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウタヒメ　彼女たちのスモーク・オン・ザ・ウォーター", "ウタヒメ　彼女たちのスモーク・オン・ザ・ウォーター", "", "", "http://image.eiga.k-img.com/images/movie/57578/315x210.jpg?3919", 113, "五十嵐貴久の人気青春小説「1995年のスモーク・オン・ザ・ウォーター」を映画化。悩み多きパート主婦の美恵子、美恵子の後輩でバツイチのかおり、万引きをやめられない雪見、謎めいた元ロッカー・新子の4人の中年女性がひょんなことから出会い、ロックバンドを結成して一夜限りのコンサートにのぞむことになる。新子以外はまともに楽器に触ったこともないなか、「ディープ・パープル」の名曲「スモーク・オン・ザ・ウォーター」の演奏を目標に練習を繰り返す4人は、ぶつかり合いながらも次第に輝きを取り戻していく。主人公の4人の女性に扮するのは、黒木瞳、木村多江、山崎静代（南海キャンディーズ）、真矢みき。", "2012-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朝やけの詩", "朝やけの詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "舞台は、観光開発計画が進む信州の美しい高原地帯。自然の破壊に反対する地域の人々と、それを着々と切り崩していく会社側やもうけを狙って暗躍する政治家たちの姿を描く。脚本の桂明子は熊井監督夫人。現代の環境破壊を先取りした人間ドラマ。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE WISHES", "ウィッシュ 夢がかなう時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "カリフォルニアの片田舎に住むホルマン家は父親を戦争で亡くし、母親のジーンとトムとガニー兄弟の３人暮らし。ある日、謎の男ジャックを車ではねてしまったジーンは、傷を負ったジャックを家に迎え入れる。他人との生活に戸惑いを見せていたホルマン家だったが、次第にジャックは打ち解けていく。そんな矢先、ガニーがガンに冒されていることが判明。打ちのめされるジーンにジャックは、３つの願いを叶える約束をする……。女性監督らしい繊細なタッチで綴る愛と奇跡のヒューマン・ファンタジー。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ACID HOUSE", "アシッド ハウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「トレインスポッティング」の原作者I・ウェルシュの同名短編集のなかの３話を映像化したオムニバス劇。第１話「ザ・グラントン・スターの悲劇」は、サッカー・チームからも家からも追い出され、失業、失恋したドン詰まりの青年が神と出会い、ハエに姿を変えられ、その姿のまま周囲の人間に復讐しようとする話。第２話「カモ」は性悪女をはらませ、責任をとって結婚した気弱な男の日常を描いたもの。第３話「アシッドハウス」はLSDのトリップのなかで、生まれたばかりの新生児と意識が入れ替わってしまったフーリガンの物語。いずれも“トレスポ”テイストのブラック・ユーモアを満載した、はきだめのドラマが繰り広げられる。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Killers", "殺人者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "　殺し屋のチャーリーとリーのコンビは標的のジョニーを殺害する。しかしあまりにも簡単に事が運んだこと、そして元レーサーのジョニーが郵便車襲撃事件に関わっていたことを知り、殺し屋たちは調査を開始。やがてシーラという女性と悪党一味の存在が明らかに。チャーリーとリーは郵便車から奪われた現金を横取りしようとするが……。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実写映画　テニスの王子様", "実写映画　テニスの王子様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「テニプリ」の愛称で幅ひろい層から親しまれている人気コミック「テニスの王子様」を、本郷奏多、城田優ら注目の若手俳優共演で実写映画化。テニスの名門校・青春学園中等部に編入したテニスの天才少年・越前リョーマ。同校のテニス部には、全国レベルの実力を持つ部長の手塚ら個性豊かなメンバーがそろっていた。リョーマはクールすぎる態度で彼らの反感を買いながらも、圧倒的な実力でレギュラーの座を獲得する。", "2006-05-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("P.S.", "ルイーズに訪れた恋は…", "", "", "http://image.eiga.k-img.com/images/movie/1508/315x210.jpg?2145", 97, "39歳の独身女性と15歳年下の青年の恋愛を描く大人のラブ・ストーリー。大学の入学選考部に勤めるルイーズの前に、かつて交通事故で亡くなった初恋の人に生き写しの青年スコットが現われた。2人はすぐに恋に落ちるが……。年下の青年との恋に悩みながらも人生の輝きを取り戻していく主人公ルイーズを、「ラブ・アクチュアリー」のローラ・リニーが好演。相手役には、「スパイダーマン3」で新悪役に抜擢されたトファー・グレイス。", "2006-09-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A WORLD APART", "ワールド・アパート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "1936年の南アフリカ共和国。13歳の少女、モリーの両親は反アパルトヘイトの熱心な活動家だった。一家を取り巻く状況は次第に悪化、弁護士の父は国外へ逃亡、ジャーナリストの母もついに逮捕されてしまう。一人になったモリーの不安は募るばかり。ある日メイドの黒人エルシーの実家を訪れたモリーは、そこでアパルトヘイトの過酷な現実を目の当たりにする……。白人運動家の一家にふりかかる様々な弾圧を、多感な少女のまなざしを通して描く人間ドラマ。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホテルチェルシー", "ホテルチェルシー", "", "", "http://image.eiga.k-img.com/images/movie/55388/315x210.jpg?1574", 0, "映画「レオン」の舞台にもなったニューヨークの「チェルシーホテル」で起こるサスペンス。主演は「春琴抄」「芸者VS忍者」の長澤奈央。新婚旅行でニューヨークを訪れた日本人夫婦、健二と恵美。2人は街の様子をビデオカメラに収めながら、旅行を楽しんでいた。ある夜恵美が、これまでに2人が撮影した映像を見ていると、そこには健二が何者かに惨殺される映像が記録されていた。そして恐る恐るシャワールームを覗くと、そこには健二の惨殺死体があった……。", "2010-05-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("亡命", "亡命", "", "", "http://image.eiga.k-img.com/images/movie/56218/315x210.jpg?3768", 118, "1960年代の文化大革命から1989年6月4日の天安門事件に至るまでの中国民主化運動と、それに対する弾圧のなかで海外へ亡命を余儀なくされた人々を追ったドキュメンタリー。欧米へ亡命した芸術家、作家、詩人、政治活動家ら14人にカメラを向け、事件後の逃亡、逮捕、獄中生活、弾圧から亡命に至る様子や、情報が自由に得られる海外で生活する亡命者の目から見た現在の中国の姿を浮き彫りにする。", "2011-05-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SEA CHASE", "男の魂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "J・ウェインが第二次大戦下のドイツ貨物船船長を演じる異色の戦争サスペンス映画。反ナチス主義者のカール船長は、オーストラリアから南米バルパライソ中立港に向けて出航した。あとを追うのはイギリス巡洋艦ロックハンプトン号。やがて一行は、ヒトラーの命によりおとり航海を命じられるが……。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あひるのうたがきこえてくるよ。", "あひるのうたがきこえてくるよ。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "野田知佑の原案による椎名誠の短編小説『アヒルの飼育』を、山村の美しい自然の景観をバックに映画化。都市生活に疲れた男が、旅の途中に出会った３匹のアヒルの子を従え、湖畔にテントを張ってキャンプ生活を始める。村人との交流も始まり、小事件が起こる。やがてアヒルの子が湖に巣立つ時、男もまた思い出を残して旅立つ。“コンバットツアー”と称する地方巡業風の映画興行が、前作「うみ・そら・さんごのいいつたえ」に続いて成功した。DVDは「ホネ・フィルム式活動寫眞全記録 椎名誠 DVD-BOX」に収録。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BANDIT OF SHERWOOD FOREST", "戦うロビン・フッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "エロール・フリン主演のものをはじめとして何度も映画化された、シャーウッドの森の英雄“ロビン・フッド”。この映画は1940年代に作られたなかの１本。ロビンが暴君ジョンを倒してから20年後、今度はロビンの息子ロバートが父とともに、悪と闘うヒーローとなって登場する。ロバートに扮するC・ワイルドはオリンピックのフェンシング選手からハリウッドに入り、「楽聖ショパン」「地上最大のショウ」などに出演、1955年からは製作、監督にも進出した。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ides of March", "スーパー・チューズデー　正義を売った日", "", "", "http://image.eiga.k-img.com/images/movie/53188/315x210.jpg?1262", 101, "2004年の民主党大統領予備選に立候補したハワード・ディーンの選挙スタッフだったボー・ウィリモンが、同選挙に着想を得て執筆した戯曲「ファラガット・ノース」を、ジョージ・クルーニーが映画化。理想に燃える若い政治活動家が選挙戦において情報操作の責任者を務めていくなかで、政界の汚れた側面に直面していく姿を描くポリティカルサスペンス。マイク・モリス知事の大統領選挙キャンペーンチームで働くスティーブンは、オハイオ州予備選討論会の後、対立候補陣営から密会を持ちかけられ……。主人公スティーブンを演じるのは、「ブルーバレンタイン」「ドライヴ」のライアン・ゴズリング。クルーニーはモリス知事役で出演。", "2012-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Worlds Fastest Indian", "世界最速のインディアン", "", "", "http://image.eiga.k-img.com/images/movie/1088/315x210.jpg?3110", 127, "ニュージーランド南端の街に住むスピード狂の男が、地上最速マシンのスピードを競う大会“スピードウィーク”が行われるアメリカユタ州のボンヌビル・ソルトフラッツを目指し、ニュージーランドからアメリカ西部を旅する姿を描くロードムービー。主演はオスカー俳優のアンソニー・ホプキンス。監督はホプキンス主演の「バウンティ／愛と反乱の航海」や「追いつめられて」「13デイズ」のロジャー・ドナルドソン。", "2007-02-03", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学校", "学校", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "名匠、山田洋次が15年間温めていた教育の喜びというテーマを通して、人間の幸福を問いかける感動作。下町の夜間中学校を舞台に、登校拒否児や不良少女、労働者、在日外国人など様々な境遇、年齢の生徒たちと、転任要請を拒み続けている心優しき教師、黒井の心の触れ合いを、叙情豊かにしみじみと描いていく。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pope of Greenwich Village", "悪の華 パッショネイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "M・ロークの魅力を十二分に満喫できる一編。「暴走機関車」で好演したE・ロバーツとコンビを組んでいる。ヤクザな世界に足を踏み入れて珍騒動を巻き起こす二人組の姿を、時にはシリアスに、時にはコミカルに描いている。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FALLEN ANGEL", "フォーリン・エンジェル 最後の24時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "死期が間近に迫ったマフィアの殺し屋が、残されたわずかな人生をマフィアへの反逆と、殺された父の復讐のために使おうと決意。組織の金400万ドルを持ち逃げ、ラスベガスへ乗り込む。途中知り合った女コリナへの愛、そして、逃走車を運転するチャーリーへの友情を絡めながら、小気味よいアクションが連続するハードボイルド作品。主演に、「48時間」でエキセントリックな凶悪犯を印象的に演じたJ・レマーを起用。ワイルドな殺し屋を好演している。", "1992-01-01", "1", 0, NOW(), NOW());
