INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("憂國", "憂國", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 28, "わずか28分の短編ながら、文豪・三島由紀夫の初監督映画として日本のみならず、欧米でも高い評価を受けた“幻の作品”。能舞台に見立てた、様式美的なセットの中で繰り広げられる、愛と死の美学ともいうべき物語。昭和11年、2.26事件が勃発した。武山中尉は、新婚であるが故に、仲間から反乱に誘われなかったが、逆に反乱軍の鎮圧を命じられ、その苦悩から愛する妻・麗子との心中を決意する。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("王将一代", "王将一代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "北条秀司が1947年に発表した戯曲『王将』は新国劇で上演され、好評を博した。これに目をつけた伊藤大輔は翌年、阪東妻三郎主演により映画化。坂田三吉の名は瞬く間に全国に知られるようになった。この作品は、その後北条が発表した「続・王将」をも含め、辰巳柳太郎の三吉、島田正吾の入江名人という新国劇のオリジナル・キャストでの再映画化である。前作は入江名人（前作では関根名人）との対局中に妻・小春の訃報を知るところで終わっているが、今回は関西棋院の初代名人となり、日本棋院に絶縁されていた三吉が、20年ぶりに入江名人と対戦するまでの三吉の将棋人生すべてをカバーしている。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Unser taglich Brot", "いのちの食べかた", "", "", "http://image.eiga.k-img.com/images/movie/34308/315x210.jpg?1683", 92, "誰もが毎日のように口にしている肉や魚、果物や野菜といった食べ物が、どのようにして食卓へと辿り着くのかを追ったドキュメンタリー。近年、食糧生産の現場では、大量生産のために家畜や魚までも機械で管理せざるを得ない状況となっている。オーストリアのドキュメンタリー作家、ニコラウス・ゲイハルターが2年の歳月をかけてその現場を取材し、作業の様子を淡々と綴ることで、“いのちを食べる”ことの真実を浮き彫りにしていく。", "2007-11-10", "7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES MAUDITS", "海の牙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "第二次大戦末期、ノルウェーのオスロ港から出航した一隻のドイツ潜水艦。艦内には敗戦を前に南米に脱出しようとするナチスの高官たちが乗っていた……。追い詰められた人間たちのエゴを、潜水艦という極限状況のなかで描いた、ドキュメンタリー・タッチの異色作。", "1946-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BALLOONATIC", "キートンの空中結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "遊園地にやって来たキートン。女の子にフラれ、気球に乗ればロープが切れて空高く飛んでいく不運にも彼は全然めげない。腹が減ったら鳥を撃ち、気球に穴が開いたら川に落ちて今度はカヌーの達人だ。さらに森の中で、先ほどフラれた女の子をクマから守って……。自主プロ短編時代の掉尾を飾るサイレント作品。", "1923-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ときめきメモリアル", "ときめきメモリアル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "大ヒットしたゲーム・ソフト『ときめきメモリアル』をモチーフに、海の家で働く高校生たちの恋と友情を描いた青春ドラマ。瀬戸内のある街に住む明彦は、夏の間学校の４大美少女が、幼なじみの浩介とともに海の家でバイトすることを知り、自分もそこで働くことにする。５人は毎年同じメンバーでバイトをしてきた仲なので、明彦はなかなか溶け込めずにいるのだったが……。「That's　カンニング！　史上最大の作戦？」の菅原浩志監督が、キャストに人気アイドルを起用したさわやかな作品。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUS STOP", "バス停留所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "男のことは表も裏も知りつくしたはずなのに、なぜかモンタナ出の純情青年の熱意にほだされ恋に落ちる歌手の物語を、舞台出身のJ・ローガンがユーモアたっぷりに描く。モンローのトボけた演技は、“彼女はついに女優になった”(N.Y.タイムス)と評された。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUKA’S CHILD", "ヘルダミアン３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "若手女優のオーディションが行われている会場。選ばれて有頂天になっている女たちを、係の男が次々と暗い独房に監禁していた。ロス市警の刑事は、この失踪事件が謎の魔女集会とかかわっていることに気付く。コウモリの形をした恐るべきクリーチャー“チャイルド”を崇拝するこの集会では、若い女性をいけにえとして捧げるのだった……。ダミアンとはいっても“オーメン”シリーズとは無関係のホラー・ムービー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生れてはみたけれど", "生れてはみたけれど", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "小津安二郎のサイレント期の代表作であるばかりでなく、日本のサイレント映画の白眉として海外にまで広く知られた傑作。東京の郊外の新興住宅にサラリーマン一家が引っ越してきた。小学生の息子たちは早くも近所のガキ大将となり、金持ちの息子も子分として従えている。が、父親はその金持ちにペコペコと卑屈な態度ばかりとっている。その情けない父親の姿に耐えきれなくなった息子たちは、憤りを父親にぶつけるのだった……。前半の子供たちの実にユーモラスな描写から後半、父親をなじり大人の世界を告発する子供のシリアスなシーンへと一転。その躍動する画面はサイレントであることを忘れるほど。", "1932-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOIS BELLE ET TAIS TOI", "黙って抱いて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "孤児院を３度も脱走したという前歴のある少年ビルジニーはある日、４度目の脱走をし、町のベビー・ギャングたちと知り合う。一方警部のジャンはヴァンドーム広場の宝石商からエメラルドを盗んだギャング集団の行方を追うが……。当時人気上昇中のドモンジョが活躍するロマンティックな青春コメディ。", "1958-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La tragedia di un uomo ridicolo", "ある愚か者の悲劇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "　予期せぬ事件により、息子の命と全財産を天秤にかけさせられた両親の苦悩と心の弱さを映し出した作品。小さな工場を営む夫婦の目前で、息子が誘拐される。大金の身代金要求に、工場を手放さざるを得ないところまで追いつめられる2人。しかし、息子の恋人や従業員が何かしら関わっていることが判明し、夫婦の心情に微妙な変化が生まれる。父親役を演じたウーゴ・トニャッツィがカンヌ映画祭主演男優賞を受賞。", "1981-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("抜き射ち風来坊", "抜き射ち風来坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "韓国の抑留漁夫収容所から脱出を図った３人組。だが、入り江に待たせてあった船に乗り込んだのは一人だけ、残りの二人は彼に裏切られたのだった。やがて一人は監視兵に狙撃されて死亡、たった一人残されたジョーは激しい怒りを胸に、復讐の日を待つ。男たちの熱気がギラギラほとばしるリベンジ・アクション。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エルマーの冒険", "エルマーの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1960年代から世界中の子供たちに愛読されている、ロングセラーの名作童話をアニメ化。TRFのYUKIが声優に初挑戦し、主題歌も担当した。がれき街に住む少年エルマーは、外国船でやって来た老ネコの冒険譚に興味津々。意地悪な動物たちに捕らえられたかわいそうな竜の子を救出してほしいと持ち掛けられるが、エルマーはがれき街から出たことがなかった。戸惑うエルマーだったが、猫に“望めば何でもできる！”と励まされ、空飛ぶ竜のいる動物島に出発。ところが、乱暴者のゴリラや島の親衛隊のイノシシなど、恐ろしい動物たちに行く手を阻まれることに……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAPONICA VIRUS ジャポニカ・ウイルス", "JAPONICA VIRUS ジャポニカ・ウイルス", "", "", "http://image.eiga.k-img.com/images/movie/56618/315x210.jpg?3569", 94, "学生時代に制作した短編映画「OBSESSION」「SEVEN DRIVES」で注目を集めた新鋭・入江悠監督の長編デビュー作となるSFロードムービー。中国で発生した新種の“カニエ・ウィルス”が上陸し、混乱が広まりつつある近未来の日本。東京で暮らす青年・陽一郎は、ウィルスに感染して命を落とした弟・浩次の遺骨を故郷に届けるため、恋人の倫子を連れて北陸へ向かう。しかし、ようやくたどり着いた故郷では、さらに不穏な空気が流れていた。", "2006-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙皇子", "宇宙皇子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "藤川桂介の同名ベストセラー小説をもとに描くアニメ化作品。舞台は壬申の乱後の奈良の都。その頃、朝廷の悪政によって世は乱れ、民は苦しんでいたが、金剛山に住む“鬼”と呼ばれる者たちは民を救済し世の乱れを正そうとしていた。宇宙皇子もその一人。だが宇宙皇子は若い正義感から、鬼の総帥・小角の理想に反対して行動を起こし、挫折を重ねる。やがて彼は、妖魔や支配階級との戦いの中で、真の平和の意味に目覚めていくのだった。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOKYO POP", "TOKYO POP", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "監督のF・L・クズイの体験を映像化した作品。ロックシンガーを夢見てニューヨークから東京へやって来たアメリカ娘ウェンディ。アマチュア・バンドのリーダー、ヒロとの出会いから、あの手この手でラッキーなレコード・デビューを果たすのだが……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女人、四十／SUMMER SNOW", "女人、四十。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "会社では、キャリアウーマンとして重要な仕事をこなしていた中年女性が、アルツハイマーでボケてしまった義父を心から介護する姿を温かく描いた作品。主演のJ・シャオがベルリン映画祭主演女優賞を受賞している。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カクレンボ", "カクレンボ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "まだ20代ながら、すでにその才能が日本のみならず海外からも注目を集めている次世代クリエイター、森田修平と桟敷大祐が作り上げたアニメーション。風習や民話の中にある恐怖をベースにした、異色のホラー・ストーリーが展開する。そのオリジナリティ溢れる作品世界と映像美が印象的。少年のヒコラとヤイマオらは、秘密の遊戯“カクレンボ”に参加。彼らは“鬼”と呼ばれる異形の者たちが集う世界へ足を踏み入れてしまう。", "2005-04-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("安藤組外伝・人斬り舎弟", "安藤組外伝・人斬り舎弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "組長の半生を描いた2004年の「渋谷物語」でも有名な安藤組。その結成当初の８人の幹部に焦点を当て、13年間にわたる安藤組の勢力抗争を描く実録ものの一編。本作では、愚連隊から安藤の舎弟として暴れた日向謙の悲劇をクローズ・アップする。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("隠し砦の三悪人 THE LAST PRINCESS", "隠し砦の三悪人 THE LAST PRINCESS", "", "", "http://image.eiga.k-img.com/images/movie/53058/315x210.jpg?2502", 118, "時は戦国時代。隣の小国・秋月を攻め落とした山名軍は、秋月の軍資金\"黄金百貫\"を血眼になって探していた。そんな中、山名軍の労役から逃げ出した武蔵と新八は、偶然にもその黄金百貫を発見。驚喜する2人の前に、真壁六郎太と名乗る野ぶせりが現われ……。黒澤明監督による不屈の名作を、オリジナルに大胆な脚色を加えてリメイク。「日本沈没」の樋口真嗣がメガホンを取り、キャストには松本潤、長澤まさみ、阿部寛ら豪華メンバーが集結した。", "2008-05-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("華麗なる追跡", "華麗なる追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "アメリカで優勝経験ある女性ドライバー、忍。日本でのデビュー戦も見事、優勝を飾った。そんな彼女の目的は、東南アジア航路を担当した船長である父のえん罪を晴らすことであった。ある日、父が死んだ刑務所の元看守との接触を追っていると……。鈴木則文の本領が発揮されるアクション映画。志穂美悦子の七変化が見ものだ。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOKUMENT FANNY OCH ALEXANDER", "ベルイマンの世界 ドキュメント　「ファニーとアレクサンデル」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "スウェーデンの巨匠ベルイマンが、ライフワークとして撮り上げた超大作「ファニーとアレクサンデル」。その撮影の模様をベルイマン自身が映し撮ったドキュメンタリーがこの作品である。彼の知られざる素顔を知る上で、格好のフィルムといえるだろう。", "1985-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FITZCARRALDO", "フィツカラルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 157, "19世紀末の南米ペルー。未開の土地になんとか文明の光を当てようと試みては失敗を繰り返す冒険家フィツカラルド。カルーソーのオペラを聴き感激した彼は、今度はオペラハウスを建てようと決意する。資金ぐりのため、アマゾン河の奥地にゴム園を造る準備にとりかかるが、目的地まで達するには、数トンもの巨船を山越えさせなければならない。かくして、インディオたちの手を借り、７ヵ月にもおよぶ決死の山越えが敢行された。主役が３度も代わったのも当然と思えるほど、難行した撮影現場がスクリーンからうかがえるが、それだけに、ラストの船上オペラは圧巻。見はてぬ夢を追い求める男を、いつも微笑みながら包み込む女性をC・カルディナーレが好演。", "1982-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El Orfanato", "永遠のこどもたち", "", "", "http://image.eiga.k-img.com/images/movie/54068/315x210.jpg?3287", 108, "「パンズ・ラビリンス」のギレルモ・デル・トロが製作を担当した、スペインのスピリチュアル・ドラマ。2008年アカデミー外国映画賞のスペイン代表作品で、本国のゴヤ賞でも脚本賞ほか多数の賞を受賞している。監督は新鋭J・A・バヨナ。自らが育った孤児院を、障害を持つ子供達のための施設に再建することを決めたラウラは、夫と息子のシモンと古い屋敷へ引っ越してくる。しかしその屋敷で、シモンは空想上の友達と遊ぶようになり……。", "2008-12-20", "9,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOOD　BYE，LENIN！", "グッバイ、レーニン！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "舞台は東西ドイツ統一前後の東ベルリン。愛国心の強い母親が昏睡状態中に、ベルリンの壁が崩壊してしまう。東西ドイツ統一を知らない母親にショックを再び与えまいと、息子はその事実を必死に隠し通そうと奮闘する。これが長編２作目となるヴォルフガング・ベッカー監督が、激動の時代に生きる一家の心温まる家族愛を、ユーモアを交えつつ見事に捉え、ドイツの歴代興収記録を塗り替えた好編。", "2003-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("親指さがし", "親指さがし", "", "", "http://image.eiga.k-img.com/images/movie/33618/315x210.jpg?3955", 96, "子どもたちの間で噂される都市伝説を題材にした、山田悠介原作による同名小説を映画化。12歳の夏に“親指さがし”をした6人の子どもたち。ところがその途中、言いだしっぺの由美子が忽然と姿をくらまし、そのまま行方不明になってしまう。8年後、同窓会で再会した5人は、リーダー的存在の武の提案で、由美子を探すためにもう1度“親指さがし”を決行するが……。主人公・武を、単独での映画主演は初となるV6の三宅健が演じる。", "2006-08-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北村透谷 わが冬の歌", "北村透谷 わが冬の歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "日清戦争へとひた走る世の中に異和を唱えながら、自殺せざるをえなかった天才詩人、北村透谷の苦悩を鮮烈に描き出した作品。時代を明治26年末から27年初夏に限定、その半年間に透谷家に訪れる人々との激しいディスカッションを通じて、“明治の子”透谷の人間像が浮かび上がる。みなみらんぼうがそんな透谷を演じる。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝ！一軒家プロレス", "あゝ！一軒家プロレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "AV業界のカリスマ社長、高橋がなりが、巨費を投じて製作した野心作。突然一軒家が大爆発する衝撃の冒頭から、からくり仕掛けの平城を舞台にしたラストのデスマッチまで奇想天外なストーリーが炸裂！　既存の常識にとらわれない徹底した娯楽精神がまぶしい。夢のマイホームを手に入れた人気プロレス団体の主宰者、獅子王が、皆と自宅の庭でパーティーをしていると、宿敵の一条が現れる。", "2004-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL ABOUT THE BENJAMINS", "グッドボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "I・キューブが製作と主演を兼ねたクライム・コメディ。コメディアン、M・エップスとの絶妙のかけ合いで笑いを誘う痛快作だ。賞金稼ぎのバッカムに執拗に追われて逃げ回る詐欺師レジーが、偶然ダイヤモンド強奪現場に遭遇。何とかバッカムをまいたものの、そこに100万ドルの当たりクジを落としてきてしまう。レジーは当たりクジを、バッカムはダイヤモンドを奪還するべく手を組むことになるが……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANGEROUS MISSION", "雪原の追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "アメリカの最も美しい自然美に輝いている国立公園グレイシャーを舞台にした活劇。大雪崩、山火事、大雪渓と、ナイトクラブの殺人事件に巻き込まれた目撃者の女性が逃げ込む先には危険が待ち受けている。3−D映画として作られた作品。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("It Happened One Night", "或る夜の出来事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "父親に結婚を反対されて家を飛び出した大富豪の娘エリーは、ニューヨーク行きのバスで失業中の新聞記者ピーターと出会う。最初は反発しあっていた2人だったが、旅を続けるうちにいつしか惹かれ合うようになり……。1934年度アカデミー賞の主要5部門（作品賞・監督賞・主演男優賞・主演女優賞・脚本賞）を独占したロマンティック・コメディの大傑作。監督のフランク・キャプラ、主演のクラーク・ゲイブルの出世作でもある。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Before Sunset", "ビフォア・サンセット", "", "", "http://image.eiga.k-img.com/images/movie/1018/315x210.jpg?1426", 81, "95年のヒット作「恋人までの距離（ディスタンス）」＝原題「ビフォア・サンライズ」の続編を、同じリチャード・リンクレイター監督、イーサン・ホーク＆ジュリー・デルピー主演で9年振りに映画化。前作の主人公2人が9年後にパリで再会する。ジェシーは結婚して子供も生まれ、作家となってパリの書店に講演にやってくる。セリーヌは環境保護団体で働き、今は旅行中の写真家の恋人と同居しているが、ジェシーの講演会に現れる。", "2005-02-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Open Range", "ワイルド・レンジ／最後の銃撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "K・コスナーが監督、主演を務め、こよなく愛する西部劇に再挑戦。悪に牛耳られた街を舞台に、嫌がらせを受け、ついには戦いに挑むカウボーイたちの姿を描いた正統派のウエスタンだ。早撃ちの主人公に扮するコスナーの颯爽とした活躍は大きな見どころ。友愛や正義など、随所に顔を覗かせる古き良きアメリカの美徳に加えて、雄大な映像美も印象深い。チャーリーは、過去を捨ててカウボーイの世界に身を投じて10年。頼りになるボスのもとで、牛を追いながら各地を放浪していた。ところが大地主のバクスターの子分たちが牛たちを奪おうと襲いかかり、仲間が殺されてしまう。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("From Russia With Love", "007／ロシアより愛をこめて", "", "", "http://ecx.images-amazon.com/images/I/51I8gLRZ32L._SL210_.jpg", 115, "シリーズ第2作。「007／ゴールドフィンガー」と並んで最も人気の高い初期代表作。犯罪組織スペクターは仕事の邪魔になるボンド暗殺計画を立て、暗号解読機と美女タチアナを呼び水に彼をイスタンブールに誘い出す。敵側の殺し屋との列車内での息詰まる対決、ボートでの追撃戦、ヘリコプターからの銃撃戦など見せ場満載。ヤングはメリハリの効いた演出で、アクション映画の原点を心ゆくまで追求している。ショウ、レーニアなどの名優たちの悪役ぶりも見事にキマり、クライマックスを盛り上げる。1964年初公開時のタイトルは「007／危機一発」で、マット・モンローの歌う主題歌も大ヒットした。", "1964-04-30", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLTERGEIST III", "ポルターガイスト３ 少女の霊に捧ぐ…", "", "", "http://ecx.images-amazon.com/images/I/516Xu%2B7cCRL._SL210_.jpg", 98, "主演のH・オルークが謎の急死を遂げたことが話題を呼んだ、シリーズ第３作。今回の舞台はシカゴ。叔母夫婦のもとへ預けられた少女に、鏡の中から悪霊どもが容赦なく襲いかかってくるが、それを阻止せんとする一家と激しい闘いを展開。鏡や光を効果的に使用し、前２作とは一味違う正統派ホラー映画に仕上がっている。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bedtime Stories", "ベッドタイム・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/54288/315x210.jpg?2816", 0, "「再会の街で」のアダム・サンドラーが主演するファンタジー・アドベンチャー。共演にケリー・ラッセル、ガイ・ピアース、コートニー・コックスほか。監督は「ヘアスプレー」のアダム・シャンクマン。甥っ子たちの子守りを頼まれた冴えない独身男のスキーターは、子供たちを寝かしつけるため毎晩ハチャメチャなお話を語って聞かせていた。ある日、子供たちの奇想天外なアイデアがスキーターの物語に加わり、その物語は現実のものとなってしまう。", "2009-03-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サチコの幸", "サチコの幸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "終戦直後の新宿２丁目、通称“赤線”を舞台に不幸にめげず明るく生きる娼婦サチコの物語。成人映画的な題材をさわやかでメルヘンチックな甘い青春映画に仕上げている。サチコに求婚する誠実な若者をはじめ、彼女の周りは善人ぞろい。娼婦たちはみな女学生のように明るい。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風たちの午後", "風たちの午後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "都会のアパートで一緒に暮らしている二人の女性、美津と夏子。ひそかに美津を愛し始めた夏子は、美津を自分だけのものにしようと、彼女の恋人に近づき、子供を宿してしまう。美津は夏子のもとを去っていくが、夏子は彼女のあとを追い続ける……。微妙な心理ドラマを力強い演出で見せる佳作。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CREW", "マイアミ・ガイズ／俺たちはギャングだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "マイアミで暮らす４人の元ギャングたちが大活躍を繰り広げる犯罪コメディ。かつては名の知れたギャングだった４人も、今はしがない隠遁生活を送っている。だが、ひょんなことから彼らはマフィアのおたずね者になってしまう。R・ドレイファス、B・レイノルズら往年のスターによる、ユーモアたっぷりのコミカルな演技が楽しい。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君よ憤怒の河を渉れ", "君よ憤怒の河を渉れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "巨大な権力の陰謀によって無実の罪を着せられた現職の検事が、見えない敵を追って東京から北海道へ500キロの復讐の旅を行く、というスケールの大きいアクション作品。彼を追う警部に原田芳雄、恋人役に中野良子ら個性派が脇を固めている。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さすらいのトラブルバスター", "さすらいのトラブルバスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "TV局の裏側を舞台にしたコメディで、直木賞作家・故景山民夫の同名小説を映画化。TVディレクターの宇賀神邦彦は、担当していた生番組での失態が問題視され、総務部制作庶務係に異動となる。だが、庶務係とは名ばかりで、実際には制作局長から持ち込まれる様々なモメ事を処理するのが仕事だった。局長には、大物俳優から預かった拳銃の処理、超売れっ子アイドルの過去の隠匿、ワイドショーに出演する記憶喪失青年の見張りという、３つのトラブルをバスターしたらディレクターに戻してやると言われ……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("警察故事／POLICE STORY", "ポリス・ストーリー 香港国際警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "香港映画50周年記念作品。麻薬シンジケート壊滅に挑む、国際警察特捜隊員の活躍をスリルたっぷりに描く。凄絶なカーチェイス、10メートルものビルからの決死のジャンプなど見せ場満載。監督、主演を兼ねたチェンのファン・サービスに徹した映画作りが快い。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD CALM", "デッド・カーム 戦慄の航海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "N・キッドマン主演による海洋サスペンス。ジョンとその妻レイは、息子を交通事故で失った心の痛手を癒すため、ヨットで海の旅に出た。そこで、小型ボートで漂流してきた男に出会う。男は“乗っていた船が沈没し、仲間も死んだ”と話すが……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版 Fate/stay night UNLIMITED BLADE WORKS", "劇場版 Fate/stay night UNLIMITED BLADE WORKS", "", "", "http://image.eiga.k-img.com/images/movie/54818/315x210.jpg?2719", 0, "人気アニメシリーズ「空の境界」の原作者・奈須きのこ率いる「TYPE-MOON」が原作を務め、ゲームやTVアニメなどさまざまメディアで展開する「Fate/stay night」の劇場版。少年・士郎は街を焼き尽くすほどの大災害で全てを失い、魔術師を名乗る男に引き取られる。それから10年、士郎はふとしたことから「聖杯戦争」と呼ばれる魔術師同士の争いに巻き込まれ、幼い頃に起きた惨劇を繰り返さないために、壮絶な戦いに身を投じることになる。", "2010-01-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tideland", "ローズ・イン・タイドランド", "", "", "http://image.eiga.k-img.com/images/movie/1708/315x210.jpg?4038", 117, "「ブラザーズ・グリム」のテリー・ギリアム監督が、少女の幻想的な想像力を独自の世界観で映像化。母親が急死して、元ロック・スターでジャンキーの父親と草原の中のボロ家にやってきた女の子、ジェライザ＝ローズ。孤独な彼女の前に、次々と不思議なことが起こり始める……。主演のジョデル・フェルランドは人気ゲームの実写版「サイレントヒル」にも出演。父親役には「シービスケット」のジェフ・ブリッジス。", "2006-07-08", "3,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オーズ・電王・オールライダー　レッツゴー仮面ライダー", "オーズ・電王・オールライダー　レッツゴー仮面ライダー", "", "", "http://image.eiga.k-img.com/images/movie/55958/315x210.jpg?2872", 90, "1971年の初代「仮面ライダー」から40周年を記念し、歴代の仮面ライダーが総出演する劇場版。仮面ライダーの生みの親・石ノ森章太郎による「人造人間キカイダー」「怪傑ズバット」など70年代に人気を博した特撮ヒーローも出演し、仮面ライダーとの初共演を果たす。", "2011-04-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雨の中に消えて", "雨の中に消えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "お寺の部屋を借りて自由奔放な同居生活を送る高校の同窓生３人組。快活な女子大生のあや子、花嫁修行に励む感傷的なきみえ、一番大人の出版社編集部員たか子。３人はそれぞれに思いを馳せる男たちがいたが、やがて彼女らは不可解な大人の世界の激しい空気に巻き込まれていき……。西河克己監督らしい快活な青春映画。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奴隷船", "奴隷船", "", "", "http://image.eiga.k-img.com/images/movie/55328/315x210.jpg?1662", 71, "SM作家の団鬼六の短編小説を、「青いうた／のど自慢青春編」の金田敬が映画化。アダルト女優・愛染恭子の引退記念作品となる。SM小説家の鬼又は、東京湾に浮かべた屋形船を「奴隷船」と称し、船内でSM趣味の男たちが他人の“愛奴”を競り落とす「鬼面の会」というSMマニアの会を主催していた。そんなある日、鬼又は秘書兼愛人の美幸の不貞の現場を目撃し、ショックから以前愛人関係にあった旅館の女将・菊江のもとを訪れるが……。", "2010-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHISTLE DOWN THE WIND", "汚れなき瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "イングランド北部の寒村に住むキャシーとその弟妹の３人は、納屋に隠れていた殺人犯をひょんなことからイエス・キリストと思い込む。感傷に流されず、純真な子供の心を魅力的に描いたウェルメイドな佳作。", "1961-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD COMPANY", "ネイチャー・オブ・ビースト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "セールスマンのジャックは、ハイウェイを走りながらヒッチハイカーを通り過ごす。しかし、バラバラ殺人事件があったためドライブインに足止めされていたジャックの車に、そのヒッチハイカー、エイドリアンが強引に乗り込んでくる。彼が時折見せる狂気はジャックを精神的に追い詰めていくが……。車の中という閉ざされた空間で、正気と狂気が錯綜するサイコ・サスペンス・アクション。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2010-2011 ドニゼッティ「ドン・パスクワーレ」", "METライブビューイング2010-2011 ドニゼッティ「ドン・パスクワーレ」", "", "", "http://image.eiga.k-img.com/images/movie/56468/315x210.jpg?1660", 182, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を劇場のスクリーンで上映する「METライブビューイング」2010～11年シーズンの3作目。花嫁募集中のローマの金持ち老人ドン・パスクワーレは、友人の医師マラテスタが美人の妹を紹介してくれるという話に上機嫌だった。一方、甥のエルネストは財産を譲り受けて愛するノリーナと結婚しようとしていたが反対されてしまう。ノリーナこそがマラテスタの妹だったのだが、パスクワーレはそのことを知らず……。ドニゼッティ晩年の喜劇オペラ。", "2010-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLADIATOR", "ファイティング・キッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "転校生のトミーは父親の借金を返済するため、賭けボクシングのボクサーになった。連戦連勝を続ける彼だったが、やがて大の親友で、脳に出血を起こしているらしいリンカーンと戦うハメになり……。「ツイン・ピークス」のバイク少年役で名をはせたJ・マーシャルがハードな魅力を披露する青春アクション。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KNACK… AND HOW TO GET IT", "ナック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ビートルズの映画「HELP！ 四人はアイドル」などで有名なR・レスター監督による遊び心たっぷりなコメディ。ナックとは“うまいやり方、コツ”の意味で、この場合は女の子を引っかけるコツのこと。教師のコリンは自分のアパートにミュージシャンのトーレンを住まわせているが、トーレンは女の子を取っ替え引っ替えしている大モテの男。コリンもトーレンのように女の子をモノにする“コツ”を得たいものだ、と思っている。そんな彼らの前にロンドンには不似合いな田舎娘ナンシーが現れる……。J・バリーの軽快な音楽とD・ワトキンのポップな撮影を得て、カンヌ映画祭のグランプリにも輝いた作品だ。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MILAGRO EN ROMA", "ローマの奇跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "G・G・マルケスの短編小説『マルガリート・ドゥアルテの長く幸福な生涯』を映画化。コロンビアの小さな町に住む判事マルガリートの７歳になる娘が急死。彼が、12年後に娘の墓を掘り返してみると、娘は昔と変わらぬ姿で眠っていた。人々は、これを奇跡と呼ぶが……。“奇跡”を認めるか認めないかをめぐる、人間たちの愛と欲望が交錯する人間ドラマ。", "1989-01-01", "77,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Waltz with Bashir", "戦場でワルツを", "", "", "http://image.eiga.k-img.com/images/movie/54408/315x210.jpg?3782", 90, "フランスのセザール賞ほか数々の賞に輝き、第81回アカデミー外国語映画賞にもノミネートされたイスラエル映画。2006年のある夜、旧友に呼び出された映画監督のアリは、26匹のどう猛な犬に追われる悪夢に悩む話を聞き、それは自分たちが従軍した82年のレバノン侵攻の後遺症ではないかと疑う。しかし自分に当時の記憶が全くないことに気付いたアリは、その謎を解こうとかつての戦友たちを訪ねるが……。監督のアリ・フォルマンの実体験に基づいて製作されたアニメーションの意欲作。", "2009-11-28", "46,7,2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　RAID", "ザ・レース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "大自然の中で繰り広げられるアドベンチャーレースを題材にしたアクション・コメディ。スカイダイビングやカヌー、雪上チェイスなど、雄大な自然の中でのアクションは迫力満点だ。アドベンチャーレースのチームを率いる富豪令嬢の暗殺を命じられた、マヌケな強盗４人組。チームに加入して標的に接近した彼らだったが、苛酷なレースに振りまわされ計画は難航する。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Maumy 2", "ボクとマウミの物語", "", "", "http://image.eiga.k-img.com/images/movie/57408/315x210.jpg?2464", 93, "TVシリーズ「トキメキ☆成均館スキャンダル」で人気の韓国の若手俳優ソン・ジュンギが主演し、犬の母子と青年との絆を描いたハートウォーミングストーリー。父親が残した愛犬マウミとその子犬たちを献身的に育てるドンウクは、マウミの世話で学校をサボりがちに。怒ったドンウクの母親が犬たちを叔父の家に預けてしまう。そんな時、2人組の宝石泥棒が末っ子の犬、ジャングンを宝石の運搬に使うために誘拐してしまい、ドンウクはマウミとともに泥棒を追跡する。2011年、新世代の韓流スター出演作を上映する「韓流新世代特集　チャン・グンソク☆ソン・ジュンギ」で日本初上映。", "2011-11-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moulin Rouge!", "ムーラン・ルージュ", "", "", "http://image.eiga.k-img.com/images/movie/1548/315x210.jpg?3543", 128, "舞台は1899年、パリの魅惑的なナイトクラブ、ムーラン・ルージュ。作家を目指してパリに出てきたばかりのクリスチャンは、このクラブの花形スターであり高級娼婦のサティーンと恋に落ちる。が、彼女を我がものにしようとする公爵が登場し、悲劇が起こる。「ロミオ＆ジュリエット」（96）のバズ・ラーマン監督がミュージカル映画に挑戦。音楽、振り付け、撮影、編集、美術、衣装ともに同作のスタッフが集合、一大映像絵巻を展開。", "2001-11-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Katyn", "カティンの森", "", "", "http://image.eiga.k-img.com/images/movie/54348/315x210.jpg?3698", 122, "1943年にソ連国内の森で数千人のポーランド将校の遺体が発見された「カティンの森事件」を、巨匠アンジェイ・ワイダ監督が、実際に遺された手記や手紙を基に描き出す歴史ドラマ。1939年、西からドイツ軍に追われる人々とソ連軍に東から追われた人々が、ポーランド東部にあるブク川の橋の上で偶然出くわす。西側からはソ連の捕虜となった夫・アンジェイ大尉を探すアンナと娘のニカ、東側からは大将夫人ルジャがおり、彼らはそれぞれの目的地へ向かうため川を渡る。", "2009-12-05", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴力戦士", "暴力戦士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「ウォリアーズ」（1979）と「ウエスト・サイド物語」（1961）を足したような、石井輝男流の青春アクション。六甲山で行われたロック大会で、東京のストリート・ファイターズと、神戸のドーベルマン・キッドという、ニつの不良グループで争いが起きる。その騒ぎの中、ファイターズのリーダー、ケンと、ドーベルマンのリーダーの妹、マリアは、手錠で繋がれた。ケンは、マリアを連れて東京へ逃げる。それを追うドーベルマンたち……。オール・ロケによる、石井輝男ならではのスリルとアクションの連続が心地良い。だが、この作品から「ゲンセンカン主人」（1993）まで、石井監督はスクリーンから遠ざかることになる。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ絶唱", "やくざ絶唱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「兵隊やくざ」以来５年ぶりにコンビを組んだ増村保造監督と勝新太郎の、兄妹相姦願望をテーマにした作品。妹に異常ともいえる愛をそそぐ勝新扮するヤクザは、彼女に近づく男は皆ぶっ飛ばす。妹はそんな兄を嫌い去っていく。絶望した勝新は新興ヤクザのボスを殺し、自らも銃弾に倒れる。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔女の宅急便", "魔女の宅急便", "", "", "http://ecx.images-amazon.com/images/I/51QNFcDwoGL._SL210_.jpg", 102, "角野栄子の同名タイトルシリーズを、宮崎駿監督が映画化したスタジオジブリの長編アニメーション作品。古くからのしきたりに従い、修行の旅に出ることになった13才の魔女、キキ。黒猫ジジとともに港町にたどりついたキキは、パン屋のおソノに気に入られ、店の手伝いをしながら配達屋を始めることにするが……。ヨーロッパを舞台に、少女が成長していく姿を描き大ヒットを記録した。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Speed Racer", "スピード・レーサー", "", "", "http://image.eiga.k-img.com/images/movie/53238/315x210.jpg?4287", 135, "「マトリックス」3部作のウォシャウスキー兄弟が、タツノコプロ制作のTVアニメ「マッハGoGoGo」を実写映画化。生まれながらの天才レーサーのスピード（エミール・ハーシュ）は、今は亡き伝説的レーサーの兄レックスを超えようとひたすら走り続ける日々。しかし、ひょんなことからレース界を牛耳る大企業ローヤルトンの怒りを買ったスピードは、たびたび妨害工作を受ける羽目に。そんな彼の前に、兄レックスを髣髴とさせる謎の覆面レーサー（マシュー・フォックス）が現れて……。", "2008-07-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金融破滅ニッポン・桃源郷の人々", "金融破滅ニッポン・桃源郷の人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "三池崇史が、それまでの荒々しいイメージを裏切り、実直な職人としての腕前を披露した端正にして真っ当な人情劇。大手スーパーの倒産によって、連鎖倒産の危機に直面した小さな印刷会社の社長が、金融の裏事情に通じている謎のホームレスと出会い、大胆不敵な復讐を遂げていく。“夜逃げ”や“株操作”など、金融もの定番のエピソードを破綻なく織り込み、ハートウォーミングな痛快物語に仕上げているのもさることながら、徳井優や佐野史郎、室井滋といった意外なキャスト陣を据えて、俳優たちの個性を巧みにレイアウトしている点がお見事。極力ケレンは封印されているが、三池作品常連の哀川翔の頭だけ、アフロヘアで大爆発。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PHILADELPHIA EXPERIMENT", "フィラデルフィア・エクスペリメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "第二次大戦中、米海軍は味方の船を敵のレーダーからくらますために電子カモフラージュの開発を行った。その際、実験船から二人の水兵が忽然と姿を消してしまう。異次元へのタイム・ワープをしてしまった若者の行方は……。1984年の世界にさまよい込んだ主人公たちの決死の逃亡劇を、特殊効果をたっぷり盛り込んで描くSFサスペンス。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHIROH〈映像版〉", "SHIROH〈映像版〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 200, "ライブ感あふれる演劇の興奮と感動を、ハイクオリティーなデジタル撮影＆編集によって映画館の大スクリーンに蘇らせる好企画《ゲキ×シネ》の第３弾は、壮大なエンタメ演劇で知られる“劇団☆新感線”による同名ロック・ミュージカル。有名なキリシタンの反乱“島原の乱”をモチーフに、同じ名前を持った二人のSHIROHが、歴史の大きなうねりに巻き込まれていく姿を映し出す。神の力を授かりながらも、その能力を失った島原の益田四郎時貞と、人の心を操る歌声を持った天草のシロー。彼らが出会ったことで、天下を揺るがす大乱が巻き起こる……。“神の歌声”を体現するシロー役の中川晃教の驚異的なパフォーマンスと、“四郎”を演じた実力派・上川隆也の熱演は必見だ。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROTEUS", "プロテウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "麻薬捜査官のアレックスとレイチェルは、おとり捜査のために麻薬輸送船に乗り込んだ。しかし、船は遭難し、二人は麻薬密輸人の４人とともに、救命ボートで海洋の油田基地にたどり着く。そこにはほとんど人影もなく、突然彼らの前に現れた老科学者が、この島を立ち去るよう警告するのだった。なんとこの島にはDNA合成によって生まれた、まったく新しい生命体“プロテウス”が潜んでいたのだ。不死身のプロテウスは、生物の身体を乗っ取り、習性や知識を吸収して成長する恐ろしい生き物だった。レイチェルの身体を乗っ取ったプロテウスとアレックスの死闘が始まる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おしどり道中", "おしどり道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "いかさま賭博をあばき、悪ボスの次郎太郎に苦汁をナメさせた旅鴉・草間の半次郎が、草鞋を脱いだ先の貸元・金平の家でその娘・おもんと知り合う。おもんは金平の兄弟分・勘平にしつこく言い寄られていたが……。三度笠も目深い大川橋蔵主演の股旅もの。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゼノ・かぎりなき愛に", "ゼノ・かぎりなき愛に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "“ゼノさん”と呼ばれ親しまれた実在の修道士、ゼノ・ゼブロフスキーの半生を描いたアニメーション作品。1930年にポーランドから長崎に渡ったゼノは、1945年８月９日に投下された原爆に被爆する。過酷な毎日にめげることなく、食べ物や衣類を子供たちに配り歩いた彼は、やがて修道院を戦争孤児たちを収容する場所として開放していく。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("善人の条件", "善人の条件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "人気脚本家・ジェームス三木が、日本の政治の裏側で蠢く人間たちの姿を痛烈な皮肉を込めて描き出した監督デビュー作。クリーンな選挙戦を心がけ、市長選に出馬した大学助教授が、選挙のプロたちによって毒されていく様がユーモラスに綴られている。名うての選挙屋を演じた丹波哲郎、橋爪功らの怪演が光る。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELLO　HEMINGWAY", "ハロー・ヘミングウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1956年、貧しい女学生ラリータがアーネスト・ヘミングウェイの『老人と海』の世界に魅了される。彼女は文学をより深く学ぶためアメリカに留学しようとするが、ことごとく周囲に反対される。困り果てたラリータは、自宅の近所に豪邸を構えるヘミングウェイに保証人になってもらおうとするが……。革命前夜のハバナを舞台に、17歳の少女の夢と挫折をみずみずしく描き上げた青春ロマン。", "1990-01-01", "49", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鞍馬天狗 角兵衛獅子", "鞍馬天狗 角兵衛獅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "幕末の京都、鞍馬天狗は角兵衛獅子の少年・杉作と知り合うが、杉作は悪玉一味に捕われる。鞍馬天狗は、罠を承知で単身大阪城へ乗り込んでいく……。嵐寛寿郎の日活入社第１回作品。当時の日活はアラカンを含め四巨頭時代（阪東妻三郎、片岡千恵蔵、月形龍之介）と言われた。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Twilight", "トワイライト　初恋", "", "", "http://image.eiga.k-img.com/images/movie/54208/315x210.jpg?2287", 122, "バンパイアと少女の禁断の恋を描き、全世界でシリーズ累計4200万部を売り上げたベストセラー小説をクリステン・スチュワート（「イントゥ・ザ・ワイルド」）＆ロバート・パティンソン（「ハリー・ポッターと炎のゴブレット」）主演で映画化。アリゾナからワシントン州の高校に転校してきたベラは、謎めいた美青年エドワードに心を奪われる。だが、彼は陽の光を浴びても死ぬことのない特別な吸血鬼だった……。監督は「ロード・オブ・ドッグタウン」のキャサリン・ハードウィック。", "2009-04-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鎧　サムライゾンビ", "鎧　サムライゾンビ", "", "", "http://image.eiga.k-img.com/images/movie/54168/315x210.jpg?1512", 91, "「あずみ」「ゴジラFINAL WARS」の監督・北村龍平がプロデュースするゾンビ・アクション。「VERSUS（ヴァーサス）」「荒神」と北村作品に出演してきた坂口拓がメガホンを握り、お笑い芸人の桜塚やっくんが俳優・植田浩望としてシリアスな役を演じている。共演は夏目ナナ、いしだ壱世ほか。祟られた禁断の土地に踏み入れ、全身腐乱した落武者の封印を解いてしまった主人公たちが、増殖するゾンビ軍団と戦いを繰り広げる。", "2009-02-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUCAS", "ルーカスの初恋メモリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "14歳のルーカス少年は、チビだが頭脳明晰。そんな彼が、二つ年上の転校生マギーに一目惚れ。しかし彼女に交際を断られた彼は意地で、マギーのBFが主将のフットボール部に入部してしまう。C・ハイムをはじめとする人気アイドル競演で、甘く切ない初恋と友情を描く青春グラフィティ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IMPRINT", "インプリント／ぼっけえ、きょうてえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "トビー・フーパー、ダリオ・アルジェント、ジョン・カーペンターら、ホラー界を代表する監督13人が競作した米国のTVシリーズ「マスターズ・オブ・ホラー」。このプロジェクトに参加した唯一の日本人監督、三池崇史が手がけ、あまりの怖さからアメリカでは放映が見送られたといういわくつきの作品だ。忌まわしい過去を持つ女郎が流浪の外国人に\"とり憑く\"様を、派手な見世物感覚で大胆に描く。明治時代の日本。アメリカ人のクリスは\"運命の女\"小桃との再会を夢みてとある遊郭にやって来た。そこで出逢った女郎は、小桃を知っていると言い、彼女の行方を話し始めるのだが……。「マスターズ・オブ・ホラー　DVD-BOX　Vol.1」に収録。", "2006-05-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暁の非常線", "暁の非常線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "天知茂が目的のためには手段を選ばぬ非情なギャングを演じて、ピカレスクな魅力を発揮したアクション映画。組の跡目を狙う大幹部・馬島は次々と銀行を襲い株を上げる。ところが、一人の警官を殺したことから、証拠隠滅のため情婦を殺し、さらに足手まといと部下を殺し、組長の娘を人質に逃げるハメになるが、結局逮捕されてしまう。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OF　MICE　AND　MEN", "廿日鼠と人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "文豪スタインベックの小説を、「西部戦線異状なし」で知られるロシア出身の名匠L・マイルストン監督が映画化。人並みはずれて体が大きく力持ち、幼児並の知能だが純粋な男レニーと、レニーの幼なじみの小柄な男ジョージは農場を渡り歩く日雇い農夫。いずれは二人で農場を持つ夢を持っていたが、ある牧場で、レニーが巨漢ゆえに悪意なく農場主の娘を殺してしまう。友情の先の悲劇を徹底したリアリズムで描き、当時のアメリカの冷たい現実をえぐり出した秀作。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORBIDDEN", "ベルリンは夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1939年９月、第二次世界大戦さなかのベルリンで、一組の男女が出会った。女はドイツ人の伯爵夫人ニナ、男はユダヤ人の詩人フリッツ。二人はヒトラーのユダヤ人迫害が激しくなるなか、情熱的な恋の炎を燃え上がらせていく……。典型的な禁断の恋のドラマといえる本編は、実話に基づいて書かれたL・グロスの小説『ベルリン最後のユダヤ人』の映画化作品。主人公ニナにはJ・ビセット、フリッツには「U・ボート」の艦長役が印象深いプロホノフが扮している。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BALLATA　PER UN　PISTOLERO", "拳銃のバラード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "当時新人のA・カルタビアーノ監督が、ユーゴスラヴィア出身のA・ギドラを主演に撮り上げたマカロニ・ウエスタン。悪漢エル・ベドジャに牛耳られた西部の町マリンタンにやってきたクッド。ベドジャに父親を殺され、自らも濡れ衣を着せられた過去を持つ彼は、その恨みを晴らさんとしていた。やがてクッドは同じくベドジャの命を狙うガンマン、ニグロスと知り合い、協力関係を結ぶのだった。", "1967-01-01", "4,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Children of the Corn: Genesis", "ザ・チャイルド　悪魔の起源", "", "", "http://image.eiga.k-img.com/images/movie/57588/315x210.jpg?1971", 81, "これまでにも何度か映像化されてきたスティーブン・キングの「とうもろこし畑の子供たち」を、新たなアレンジを加えて映画化したホラー。車の事故で立ち往生した若い夫婦が助けを求めた一軒の民家には、見るからに怪しげな夫婦が住んでいた。真夜中に不審な物音を聞いた若い夫婦は逃げ出そうとするが、何者かに阻まれ……。", "2012-03-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海と自転車と天橋立", "海と自転車と天橋立", "", "", "http://image.eiga.k-img.com/images/movie/57118/315x210.jpg?2442", 81, "ロードレーサーを中心に扱う自転車ショップ「なるしまフレンド」会長で、自転車界のカリスマアイドル的存在として知られる鳴嶋英雄が、75歳の誕生日に向けて自転車での日本海側日本縦断を敢行した様子を追ったドキュメンタリー。同時に越、丹後、出雲、筑前など日本海側の古代文化、渡来文化の豊かさにも焦点を当てる。監督は、同じく鳴嶋を追ったドキュメンタリー「なるしまフレンド　俺っち自転車道」（2009）も手がけた田中千世子。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE", "SSU", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「シュリ」「ブラザーフッド」のカン・ジェギュ監督が設立したプロダクション製作による、ドラマティックな人間ドラマ。実在する韓国の海難救助隊をモデルに、ダイバーの人間模様をエモーショナルに描く。幼いころからの親友同士のジュンとテヒョンは、韓国海軍のエリート潜水部隊SSUの一員。ジュンは女性士官スジンと恋に落ちるが、テヒョンも彼女に思いを寄せていることを知り、黙って身を引いた。やがて３年後、彼らに危険な任務が下り……。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆発！750CC族", "爆発！750CC族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "警察の暴走族取締まり強化のため身をひそめていた一人のライダーが、金持ちのドラ息子たちが乗る乗用車グループと対立、たった一人で戦いを挑んでいく。屈折した暴走族青年の心情の爆発を、ナナハンに託して描く。岩城滉一がはまり役のシリーズ第４作。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACE HIGH", "荒野の三悪党", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "金塊をめぐって繰り広げられるアクション・ウエスタン。19世紀後半の西部。30万ドルに及ぶ金塊を強奪した二人のならず者、キャットとハッチは、銀行に雇われた悪党カコプーロスに金塊を奪われる。彼らはカコプーロスと金塊を求め、必死の追跡を開始するが……。切れのいいアクション・シーンと、E・ウォーラックはじめ個性派俳優の熱演が見もの。", "1969-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛しのハーフ・ムーン", "愛しのハーフ・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "結婚を前にしたカップルの幸せでちょっぴり不安な日々を、才人・滝田洋二郎がライトな感覚で演出。ギャグに少々エッチも加えた好編となった。アイドル出身の伊藤麻衣子が、清純さを感じさせる上に大胆な演技もみせていて注目される。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Digital Short Films by Three Filmmakers 2004", "三人三色（2004）", "", "", "http://ecx.images-amazon.com/images/I/31WJ9KZN25L._SL210_.jpg", 98, "韓国のチョンジュ国際映画祭が毎年3人の注目監督を集めて制作するオムニバス企画「三人三色」の2004年版。「殺人の追憶」のポン・ジュノが、ソウル市内に設置された監視カメラの映像を通してソウルの“狂気”を浮き彫りにした「インフルエンザ」、「天上の恋歌」のユー・リクウァイが近未来を舞台に描いたラブストーリー「夜迷宮」、「狂い咲きサンダーロード」の石井聰亙監督が、バリの美しい自然を背景に1人の女優の苦悩をつづった「鏡心」の3作品。", "2004-11-20", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剣", "剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "大学の剣道部の部長・国分は純粋に剣の世界に打ち込み、自らはもとより部員に対しても厳しかった。国分とは考え方が対照的な賀川は、国分が学内ナンバーワンの恵理を誘惑したと部員に説き、皆で厳禁にされている水泳をしてしまう。自らのいたらなさを恥じた国分は自殺する。雷蔵がストイックに剣の道に打ち込む主人公を好演。色濃く漂う虚無感は観る者を圧倒する。賀川を演じる川津もデカダンスな臭いを発散させて、雷蔵とは好対照な持ち味を発揮。三隅監督の演出も三島文学の香りをうまく生かして、硬質なストイシズムで貫かれた映像を作り出している。三隅監督には珍しく現代劇だが、三隅独特の正統時代劇の香りを得て、「斬る」「剣鬼」とともに代表作となった。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O THIASSOS", "旅芸人の記録", "", "", "http://ecx.images-amazon.com/images/I/413PXDV70RL._SL210_.jpg", 232, "『再現』、『1936年の日々』に続く第3作にしてアンゲロプロス監督が独自の手法を確立、ついに日本で初公開された伝説の傑作。他国の干渉や国内の政権交代に揺れる激動のギリシャで、たったひとつの劇「羊飼いの少女ゴルフォ」を演じて廻る旅芸人一座の39年から52年までの愛と裏切りの日々がギリシャ悲劇になぞらえて描かれる。1カット内で時代が進み、また遡るという3時間54分が、難解という解釈を超えて息を飲ませる、映画ファン必見の1本。", "1979-08-11", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SYLVIA", "シルヴィア", "", "", "http://ecx.images-amazon.com/images/I/31QN442RQNL._SL210_.jpg", 110, "今なおその作品が世界中のファンに読み継がれている夭逝の女流詩人、シルヴィア・プラスの半生を描いた伝記ドラマ。詩人テッド・ヒューズとの運命の恋、幸せな結婚生活と破綻、創作活動の苦悩が描かれる。誰よりも優しく繊細だったために、傷つき、バランスを崩していったヒロインの心のさまよいを、G・パルトロウが好演。シルヴィアは詩の才能を持つイギリス人大学院生テッドと恋に落ち、結婚。彼らはともに執筆に励みながら、二人の子にも恵まれ、幸せな家庭を築くのだが……。", "2004-12-25", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウミヒコヤマヒコマイヒコ", "ウミヒコヤマヒコマイヒコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「たそがれ清兵衛」「メゾン・ド・ヒミコ」などの存在感ある演技で注目を集めた舞踊家・田中泯が、踊りの始原を求めてインドネシアの島々を旅する姿を記録したダンス・ロードムービー。“職業は農民、ダンスは命そのもの”という彼が、現地の人々や動物たちとの交流を通し、道や海上などあらゆる場所で独特の踊りを生み出していくさまを映し出す。監督は、アートディレクターや写真作家として活躍してきた油谷勝海。", "2007-06-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お友達になりたい", "お友達になりたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ピンク映画界期待の新星、荒木太郎監督が、脚本家の登竜門“城戸賞”の入選作品を映画化。常識やモラル、社会的地位に縛られない、主人公たちの大胆な生き様を通して、真の人間らしさを描き出す。夫の三回忌を済ませ、一人住まいのマンションに帰り着いた佳恵は、玄関に不動産屋と若い女性を見つける。すれ違いざま、その女性が発した言葉。それは夫の生前、佳恵に電話をかけてきた愛人のセリフと同じだった。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMMANUELLE", "エマニエル夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1960年代のポルノの傑作を原作に、ファッション写真家として知られたジャカンが華麗な映像美でみせた話題作。外交官の夫に呼ばれバンコクへやって来たエマニエルが、急激にセックスに目覚める。それは果たして解放か、飼育か……。", "1974-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jumper", "ジャンパー", "", "", "http://image.eiga.k-img.com/images/movie/53178/315x210.jpg?2018", 88, "母が家を出て、父親と2人暮らしの少年デビッドは、ある日、自分が空間をジャンプするテレポート能力を持つ“ジャンパー”であることを知る。その能力を活かし、世界中を飛び回って気ままな生活を送っていたデビッドだったが、やがてジャンパー抹殺を使命とする組織バラディンにマークされるようになり、また、彼と同じ能力を持つ青年と出会う。「Mr.＆Mrs.スミス」のダグ・リーマン監督が、スティーブン・グールドのSF小説を映画化したアクション大作。", "2008-03-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Am Number Four", "アイ・アム・ナンバー4", "", "", "http://image.eiga.k-img.com/images/movie/55908/315x210.jpg?3287", 111, "マイケル・ベイ製作によるSFアクションスリラー。超人的な能力がありながら、その力を隠して、街から街へと移り住む生活を送っていた孤独な青年ジョンはオハイオ州の高校で少女サラと恋に落ちる。その一方で、ジョンを超能力者として認識する謎の組織がジョンへの攻撃を開始する……。出演は主人公ジョンに「アレックス・ライダー」のアレックス・ペティファー。共演にティモシー・オリファント、ダイアナ・アグロン。監督は「イーグル・アイ」のD・J・カルーソ。", "2011-07-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MACARONI", "マカロニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ビリー・ワイルダー作品などでおなじみの名優J・レモンと、イタリアを代表する国際派俳優M・マストロヤンニ。この二大スターが、初めて共演した作品。レモン扮するアメリカの会社副社長ロバートが、40年ぶりにイタリアを訪れる。ナポリに住むかつての友人アントニオは、感激の再会を期待して彼のホテルを訪ねるのだが、ロバートはとんとアントニオのことが思い出せない。やがて、かつての友情を復活させ、互いの人生を顧みる二人の姿を、映画はユーモアとペーソスたっぷりに綴っている。", "1985-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀の男・六本木ホスト伝説", "銀の男・六本木ホスト伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "本宮ひろ志の漫画『銀の男』をモチーフにして描く青春劇。大学の民族研究室で助手をする倫子は教授に命じられ、ホストクラブに集まる人々の研究をすることに。その店は俳優志望の若者たちが働くプレイヤーズクラブで、彼らはプレイヤーと呼ばれていた。そこで彼女は、ある風俗嬢を幸福に導いたという伝説のホスト“銀の男”の話を耳にする。そんな中、“銀の男”伝説に入れ込んでいたプレーヤー、康平が殺される事件が起きる……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’E´TE´ MEURTRIER", "殺意の夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "南フランスの田舎町。素朴な青年フロリモンは、美しいエルに心を奪われる。セクシーだが、あどけなく、つかみどころのないエル。だが、フロリモンを見つめるエルの瞳には暗い影が宿っていた。哀しい愛と復讐を描く異色ミステリー。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARKED MAN", "ブレイク・アウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "服役中の刑務所で、偶然、二人の看守が囚人を殺害するのを目撃してしまったギブソン。見られたことに気付いた看守たちは、ギブソンに殺人の罪を着せ殺そうとする。彼らと格闘するうちに、看守の一人を殺してしまったギブソンは、無我夢中で脱獄し、もう一人の看守の家に忍び込む。しかし、真相を探ろうとした彼は、そこで新たな死体を発見してしまう。一方、警察とFBIは、異常な緊急体制でギブソンを追い詰める。次々と襲ってくる追っ手をかわしながら、ギブソンは真相に迫っていき、警察をも自由に操る黒幕の正体を知るのだった。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上海陸戦隊", "上海陸戦隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "激戦のあとも生々しい上海にロケーションして製作されたドキュメンタリー・タッチの戦争映画。海軍陸戦隊の上海での活躍から陸軍大部隊の敵前上陸までを描く。熊谷監督は映画人のなかでも超国粋主義者として知られた人物。中国人避難民に扮した原節子の義兄でもある。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銃爪・HIKIGANE", "銃爪・HIKIGANE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "「昭和鉄風伝・日本海」の佐々木正人監督による任侠バイオレンス。10年ぶりに博多に帰郷した高志。兄貴分に九州の大物、富岡を殺すよう命じられていた彼は襲撃を決行するが、ボディガードの竜崎に阻止されてしまう。しかし、高志の去った後、竜崎は富岡を射殺。すべての罪を高志に背負わせた竜崎は……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パーマネント野ばら", "パーマネント野ばら", "", "", "http://image.eiga.k-img.com/images/movie/54838/315x210.jpg?2674", 100, "「腑抜けども、悲しみの愛を見せろ」「クヒオ大佐」の吉田大八監督が、西原理恵子のコミックを映画化した恋愛ドラマ。離婚の末に一人娘を連れて故郷に出戻ったなおこと、なおこの母・まさ子の2人で営む海辺の町の美容室「パーマネント野ばら」に集まる女性たちの悲喜こもごもの恋愛模様が描かれる。主演は北野武監督作「Dolls（ドールズ）」以来、8年ぶりとなる菅野美穂。共演に江口洋介、夏木マリ、小池栄子、池脇千鶴ほか。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ICE　AGE", "アイス・エイジ", "", "", "http://ecx.images-amazon.com/images/I/51PKmubb3sL._SL210_.jpg", 82, "氷河期を舞台に動物たちが繰り広げる冒険をスリルと感動の中に描くCGアニメーション。今から２万年前、寒さを逃れて南へ向かう仲間の群れから離れ、北へと向かうマンモスのマニーとナマケモノのシドは、河の下流に流れ着いた人間の赤ちゃんを拾う。彼らは氷河を越えて人間のいる場所まで送り届けることにするが……。コミカルでハートウォーミングな心地良さと、人間と動物の相容れない関係性も逃さない厳しさとが見事に共存した秀作。", "2002-08-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Afro Samurai: Resurrection", "アフロサムライ：レザレクション", "", "", "http://image.eiga.k-img.com/images/movie/54878/315x210.jpg?2314", 100, "サミュエル・L・ジャクソンが製作総指揮と声優を務める日米合作アニメーション「アフロサムライ」の続編。原作は岡崎能士の人気コミック、アニメーション制作を「ブレイブストーリー」のGONZOが担当。前作で父の仇を取ったアフロサムライは、これまで斬ってきた者たちの魂を供養するため、仏像を彫り続けるという贖罪の日々を送っていた。しかし一方で、死んだはずの兄弟子・仁之助と謎の女・シヲがアフロへの復讐を遂げようとしていた。", "2009-12-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN THE HEAT OF PASSION", "ボディ・パッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "売れない俳優のチャーリーは、ドキュメントTV番組で地元で起きたレイプ犯の役を演じたばかり。普段は生活のために町のガソリンスタンドで働く彼は、ある日車の修理にやって来た美しい人妻リーに魅了される。ケーブルTVの修理人を装い彼女の屋敷に行った彼はそれ以来、リーの夫サンフォードの目を盗み愛し合うようになる。しかし、リーの息子こそがレイプの犯人だったのだ……。貞淑な仮面の人妻に誘惑され殺人鬼にされてしまう男のさまを描いたエロティック・サスペンス。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にっぽん・ぱらだいす", "にっぽん・ぱらだいす", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "喜劇の第一人者・前田陽一の監督デビュー作。赤線で働く娼婦たちの生き方を、ペーソスを交えて描いた風刺劇に仕上がっている。昭和20年、日本は国民外交を円滑にするという名目で、“特殊慰安施設協会”を設置した。蔵本はこれを機会に、今まで疎開させておいた女たちを連れ戻して店を開く。時は流れ、日本の発展に伴って店も大繁盛。すったもんだの末、やがて売春防止法が施行される。『蛍の光』の合唱とともに赤線の灯が消えていく、もの悲しいラストが印象的。ホキ徳田が自慢の脚線美を披露。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNCOMMON VALOR", "地獄の７人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ベトナム戦争が終結して10年。いまだ戻らぬ息子の生存を信じるロウズ大佐は、かつての息子の戦友を呼び集め、救出作戦を開始、現地へ侵入する……。戦争ハード・アクションものだが、男たちの友情や親子の愛情をうたい上げ、独特の味を出した小品に仕上がっている。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RICHIE RICH", "リッチー・リッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "リッチー・リッチは8000エイカーの敷地を有する大金持ちリッチ家の一人息子。キャビアやフォアグラは食べ放題、ジェット・コースターを庭にしつらえ、スーパーモデルの専属トレーナーを雇うという、想像を絶するリッチな生活。そんなある日、リッチ家が傘下に収めるリッチ・インダストリーズ社の重役バンドーが会社乗っ取りを企てる。両親の乗った自家用飛行機が時限爆弾により墜落し、なんとか無事に脱出したものの、二人は海に浮かぶゴム・ボートの上。リッチーは両親が戻るまで、巨大な屋敷と会社を守るべくバンドーと戦うことを決意する。リッチー役のM・カルキンが彼独特の存在感を十分に発揮している。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOMETHING ELSE AGAIN", "愛が聞こえる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "アメリカの盲目の人気シンガー・ソングライター、トム・サリヴァンの生き様と、彼を支える周囲の人間たちとの愛を描いた心温まる青春物語。トム・サリヴァンは生まれながらの盲目というハンディキャップを背負いながら、とてつもないエネルギーと才能を持つシンガー・ソングライター。ユーモアのセンスに富み、ハンサムで運動神経も抜群の彼は、「エアポート'77」や「M★A★S★H」に役者として出演しているが、彼の生活を描いたこの作品では、やはり音楽家でスポーツマンでもあるM・シンガーが彼に扮している。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN FROM THE ALAMO", "平原の待伏せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "1836年、アラモ砦の戦いにおいてアメリカ守備隊がメキシコ軍の攻撃で全滅する直前、本部に急を知らせる命を受けて砦を脱出したがために卑怯者の汚名を着せられてしまったジョン。やがて彼は、メキシコ人を装うならず者の白人集団によって殺された妻の仇を討つべく、復讐の旅に出る。西部史上に名高いアラモの戦いの外伝とでもいうべき異色西部劇。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COACH コーチ 40歳のフィギュアスケーター", "COACH コーチ 40歳のフィギュアスケーター", "", "", "http://image.eiga.k-img.com/images/movie/54368/315x210.jpg?3651", 108, "40歳独身の美和は、プロフィギュアスケーターとしてアイスショーなどに出演しながら、スケートスクールで子供たちを指導するコーチとしても働いている。しかし、元恋人の娘をコーチするうちに、オリンピック選手になるというかつての夢に向かい、新たな挑戦を始める。主演は、現役最年長のプロフィギュアスケーターとして活躍する西田美和。安藤美姫、荒川静香、伊藤みどりらオリンピックのメダリストがゲスト出演している。", "2010-02-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN AWFULLY BIG ADVENTURE", "恋する予感", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "H・グラント、M・ニューウェルの「フォー・ウェディング」コンビが描いたロマンティックなラブ・ストーリー。16歳のステラは劇団のステージ・マネージャー。独特の人生観と哲学を持ちつつも、チャーミングで無垢な彼女はみんなの人気者。そんな彼女の恋の本命は、劇団の主宰者メレディス。しかし彼はゲイであり、彼女の思いは受け入れられない。やがて、ステラは劇団の主演男優オハラとつきあうようになる。しかし、劇団内で対立や暴力が噴出し始め、ステラの秘められた過去が明らかになる。そして、それを知ったオハラも大きな悩みを抱えることになり……。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("舞姫", "舞姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "川端康成の同名小説を映画化した成瀬巳喜男監督作品。バレエ教室を営む中年女性が愛情の冷めた夫と別れて新たな人生を歩もうとするが、結局もとの生活に戻るまでを香気豊かに描く。これがデビューの岡田茉莉子が美しく、早くも大女優の風格を備えている。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JANE B.PAR AGNE｀S V.", "アニエス・vによるジェーン・b", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "その名の通りA・ヴァルダが、女優J・バーキンの姿を、半分フィクショナルに、かつドキュメント・タッチで捉えた作品。とはいえ単にバーキンの姿を捉えるのでなく、彼女とヴァルダとの関係を描くことが主題となっている。バーキンのかつてのパートナー、セルジュ・ゲンズブールとの間に生まれたC・ゲンズブールも出演した、ヴァルダとバーキン共作のプライベート・フィルムとでも呼ぶべき作品。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢幻彷徨", "夢幻彷徨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 35, "日本映画界屈指の美術監督、木村威夫が、85歳にして初めてメガホンを執った、自由かつ大胆な中編。自身の戦争体験をもとに、戦火の中で出会った男女の魂の彷徨を描いた本作は、“物語ありきでなく、自由な発想を優先させた”との監督の言葉通り、一切のセリフを排除し、映像と音（音楽）のみで語られていく。また、主人公の男女がさまよう迷宮では、「肉体の門」や「ピストルオペラ」など、かつて木村が手掛けた作品のセットが再現されるなど、ファン心理をくすぐる憎い演出も。35分という上映時間ながら、全く物足りなさを感じさせない贅沢な仕上がりになっている。戦争の空襲の最中に出会った、目隠しをした二人の若い男女。戦争が終わると男は、魂の自由を求めてさすらい、女は体を売って生計を立てていた。しかし男への愛に目覚めた女は、彼を捜し求め、旅立つのだった。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEMON LOVER", "DEMON LOVER", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "企業社会の駆け引きを描きながら、フランスらしいアート・センスを散りばめたスタイリッシュ・サスペンス。お洒落なパリジェンヌとクールなスパイの顔を持つ主人公を「ハンテッド」のコニー・ニールセンが好演。日本のシーンには、大森南朋ら演技派俳優が出演している。フランスの世界的な大企業V.G.は、日本企業“東京アニメ社”の買収を進めていた。しかし、買収担当者の中にライバル社のスパイが潜んでいて……。", "2005-03-12", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母と子", "母と子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "成瀬巳喜男、五所平之助らの助監督を経て1937年に監督デビューした渋谷実の第４作。彼が一流監督として認められるきっかけともなった作品である。主役は会社重役の妾とその娘。歳をとるにつれ、重役から見離されていく母親の姿を見ていた娘が、自分はそういう打算的人生を送らないとの覚悟を決めるまでを冷徹なタッチで描いていく。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君が青春のとき", "君が青春のとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "女性ディレクターとして初めて自分の企画が通り、ドキュメント番組『君が青春のとき』を手掛けることになった木所香。原宿族の生態を捉えようと制作を開始するが、そこで彼女はチンピラのピン公と知り合う。仕事と良心のはざまに立つヒロインを吉永小百合が熱演する日活青春路線の一編。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lady Chatterley", "レディ・チャタレー", "", "", "http://image.eiga.k-img.com/images/movie/34298/315x210.jpg?1671", 135, "過激な性表現のために各地で論争を引き起こしたD・H・ロレンスの小説「チャタレイ夫人の恋人」を、フランスの女流監督パスカル・フェランが映画化。主演は「みなさん、さようなら」のマリナ・ハンズ。イギリス中部の炭鉱地帯に建つ大きな屋敷。ここに暮らすコンスタンス・チャタレーは、戦争で下半身不随となった夫の世話に追われていた。そんな毎日に息苦しさを感じた彼女は、敷地内の森で猟番をする青年パーキンと恋に落ち……。", "2007-11-03", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEPARATE TABLES", "旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "夏のにぎわいが嘘のような、冬のイギリスの小さな海岸町を舞台に、逃避的生活を送る中年男女の様々な人生模様を描いたシリアス・ドラマ。「マーティ」でアカデミー賞を受けたマンの文芸作品。製作者の一人でもあるランカスターが好演。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sneakers", "スニーカーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "警備システムをチェックするため、ハイテクを駆使してビルに潜入するエキスパート集団“スニーカーズ”。リーダー、ビショップの指揮下、抜群のチームワークを誇る彼らは政府の依頼で、高性能の暗号解読機“ブラック・ボックス”を盗み出す。だが、何者かに命を狙われ、ブラック・ボックスを奪われてしまった。この事件を影で操っていたのが、ビショップの昔の友人コズモ。世界中の情報を掌握しようとする彼からブラック・ボックスを奪い返すため、スニーカーズは反撃に出る……。「ウォー・ゲーム」の脚本家コンビ、W・F・パークスとL・ラスカーがアメリカに実在するハイテク集団にヒントを得て、10年間温めていた企画。じっくりと練られた脚本だけあって、入り組んだ物語の中に社会性と娯楽性が巧みに盛り込まれている。スニーカーズの個性的な面々に扮した、R・レッドフォード、S・ポワチエらスターの競演も見もの。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劔岳　点の記", "劔岳　点の記", "", "", "http://image.eiga.k-img.com/images/movie/1958/315x210.jpg?1809", 139, "日本映画界を代表するキャメラマン・木村大作が初監督に挑戦し、日本地図完成のため未開の地・劔岳を命懸けで目指した測量師たちの姿を描いたドラマ。原作は新田次郎の同名小説。明治時代末、陸軍参謀本部陸地測量部の柴崎は、地図上で空白となっている劔岳の登頂と測量の命令を受け、仲間と共に劔岳に挑む。しかし、そこには雪崩や暴風雨など想像を絶する困難が待ち構えていた……。浅野忠信、香川照之、松田龍平、宮崎あおいら、豪華俳優陣が出演。", "2009-06-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COACH OF THE YEAR", "勝利へのタッチダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "元プロ・フットボールの花形選手ジムはベトナム戦争での傷で下半身不随の身。そんな彼が少年院の少年たちにフットボールのコーチをすることで人生に意味を見いだし、一方少年たちも次第に心を開いていくという感動のストーリー。名門高校チームと試合をするラストがさわやかな興奮を呼ぶ。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERBIE GOES TO MONTE CARLO", "ラブバッグ・モンテカルロ大爆走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "フランスを縦断するレース、モンテカルロラリーに参加するためパリにやって来たジムとウィリーのコンビは美人ドライバーのダイアンに一目惚れする。恋のライバル、ハービーも加わってレースは始まるが、パリで盗まれた世界一のダイヤがハービーの車に投げ込まれていた……。ユーモア満点のシリーズ第３作。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brenda Starr", "ブレンダ・スター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "『ブレンダ・スター』は、全米100紙におよぶ新聞に連載されていたという人気コミック。美人新聞記者ブレンダ・スターが事件を追い大活躍するが、本作では謎のエネルギー“ミラクル燃料”をめぐり、N.Y.、南米、アフリカなど世界中を飛び回る。T・ダルトンが“007”さながらに主人公の危機を救う。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地上", "地上", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "遊廓裏のある置家の２階で、母とともに貧しい暮らしを送る中学５年生の一郎と、置屋に売られることを嫌がり一郎の家に飛び込んできた娘・冬子との悲恋を描く。大正初期の作家・島田清次郎の原作をもとにした文芸編。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひとり狼", "ひとり狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "村上元三の原作を池広一夫監督、市川雷蔵主演で映画化した正統派股旅映画の佳作。“人斬り伊三”の異名をとる一匹狼のヤクザ追分の伊三蔵は、博奕も剣の腕も確かでヤクザ仲間からも一目置かれていた。しかし彼には奉公先の主人の娘との仲を裂かれ、許婚の武士を傷つけて逃走したという暗い過去があった……。中年ヤクザ・孫八の回想という形式を取り“人斬り伊三”の過去が次第に浮かび上がってくるという直居欽哉のシナリオ構成はなかなか巧みである。“人斬り伊三”に扮する市川雷蔵は折り目正しい演技で時代劇スターとしての貫禄を示し、晩年の代表作とした。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brave", "メリダとおそろしの森", "", "", "http://image.eiga.k-img.com/images/movie/56798/315x210.jpg?2312", 0, "ピクサー・アニメーション・スタジオ13本目の長編作。スコットランドを舞台に、自由を愛する王女メリダが、精霊に守られた神秘の森の奥で待ち受ける運命と対峙する姿を描くファンタジーアドベンチャー。王家の伝統に嫌気がさしていた王女メリダは、ある日、不思議な鬼火に導かれて森の奥深くに住む魔女に出会う。メリダは魔女に「魔法で自分の運命を変えてほしい」と頼むが、古来より人間が森の魔法を使うことはタブーとされていた。魔女はメリダの願いを聞き入れ呪文を唱えるが、それと引き換えに平和だった王国に恐ろしい呪いがかけられてしまう。ピクサー史上初となる人間の女性を主人公にした作品。", "2012-07-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DOBERMANN", "ドーベルマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "フランスで絶大な人気を誇るパルプ・フィクションを、CF出身の新鋭J・クーネンが映画化。ドーベルマンと名乗るダーティヒーローとその仲間が、続けざまにパリの銀行を急襲する。ことごとく一味に手玉に取られた警察は、目的達成のためには手段を選ばない極悪警視クリスチーニにすべてを託すが……。コミック感覚の映像とデジタル・ロックが融合され、フランス映画にかつて見られないほど過激なバイオレンスが炸裂するバトル・アクション。まずオープニングのアニメで意表を突き、後半のドーベルマン一味VSクリスチーニ率いる警官隊の全面戦争になだれ込んでいく。ドーベルマン役のV・カッセル、クリスチーニ役のT・カリョ、そしてドーベルマンの恋人で口の利けない爆薬のプロに扮したM・ベルッチという３者のアクの強いキャラクターも際立っている。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIRAKATA", "HIRAKATA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "人気ロックバンド“Janne Da Arc”が、2004年11月に母校で行った最初で最後のシークレット・ライブ。閉校の決定した母校の後輩の呼びかけに卒業生のメンバーが応えるかたちで実現。その貴重なライブ映像やインタビューに、様々な悩みの渦中にいる人々の架空のドラマを加え、異色の構成の青春劇が展開していく人間ドラマとなった。大阪府枚方西高校に通うユウジ、隆二、健太、郁巳はバンドを結成するが、ライブハウスで喧嘩を起こして、出入り禁止になる。そんな中、４人は卒業生であるバンド“Janne Da Arc”が体育館でライブを行うことを知り……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GALLIPOLI", "誓い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "オーストラリア映画では初の米国資本で製作された話題作。1915年、ヨーロッパのガリポリ戦線でのオーストラリア軍の活躍に胸おどらせ、やがて出征していく同国の二人の青年の運命を感動的に描く。ウィアー監督の名を一躍世界に知らしめた佳作。", "1981-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京に来たばかり", "東京に来たばかり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "", "2012-08-31", "76,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボール　神龍の伝説", "ドラゴンボール　神龍の伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "『孫悟空』をモチーフにゲームソフト感覚で作られた、絶妙のキャラクターたちが活躍する冒険アクション・アニメ。鳥山明の同名コミックを基にTVアニメ化され、さらにその人気を受けて劇場映画化されたシリーズ全17作中の序章を飾る第1弾。後に続く「ポケットモンスター」ほかに大きな影響を与えた会心作だ。映画版オリジナル・キャラが毎回登場し、7つのドラゴンボールを巡って孫悟空たちが悪と激闘する。DVDは『DRAGON BOX THE MOVIES』に収録。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEST PILOT", "テスト・パイロット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "航空会社のテスト・パイロット、ジムは新鋭機の試験飛行中にカンサス州の田舎に不時着。そこで農家の娘アンと出会い、恋に落ちた二人は翌日に結婚する。しかし、その後もジムは危険な飛行競技会に参加したり、危ない試験飛行を続け、アンは彼が仕事に向かうたびに不安に駆られる日々を送った。そんなある日、ジムと相棒のガナーは空軍の重爆撃機のテストを命じられる……。C・ゲーブル演じる空に生きる男と、M・ロイ演じる妻の愛情を描く。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山田ババアに花束を", "山田ババアに花束を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "舞台化もされた花井愛子の人気小説を映画化。清花女学院高等部の生活指導教員で35歳になるオールドミス・通称“山田ババア”と校内一の遊び人・３年C組のルナとが、ふとしたことから中身が入れ替わる。互いに天敵の体に入り込んだ二人は、初めは混乱するが、相手のことがわかってきて……。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バイオハザード　ディジェネレーション", "バイオハザード　ディジェネレーション", "", "", "http://image.eiga.k-img.com/images/movie/53938/315x210.jpg?2874", 98, "大ヒットゲーム「バイオハザード」のCG長編映画。主人公はゲーム「バイオハザード2」のレオンとクレア。「日本沈没」「L change the WorLd」の特撮監督の神谷誠が監督、脚本は「CASSHERN」「攻殻機動隊 STAND ALONE COMPLEX」の管正太郎。ラクーンシティ消滅事件から7年、レオンとクレアはアンブレラ社が開発した人間をゾンビ化させるt-ウイルスを手にしたテロリストのバイオテロ鎮圧に向かうが、黒幕の存在に気付き始め……。", "2008-10-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREAT HUNTING 1984", "グレートハンティング'84", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“ライオンが人間を食べた!!”というショッキングな内容で話題を呼んだ「グレートハンティング」シリーズの第３作。今回は２万頭に及ぶ大殺りくを行った象狩りや、もはや動物狩りに飽きた人間たちの狂気の“人間狩り”など、文明社会の発展と相反する一面をのぞかせた残酷記録映画。", "1983-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THEY GOT ME COVERED", "腰抜けスパイ騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "“腰抜け”シリーズの未公開作。ホープ扮する新聞記者がワシントンに赴くが、そこで各国のスパイが暗躍していることを知る。それを追ううちに彼はスパイ事件に巻き込まれ、てんやわんやの大騒動を繰り広げる。“珍道中”シリーズの常連の美女D・ラムーアも登場する。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハードロマンチッカー", "ハードロマンチッカー", "", "", "http://image.eiga.k-img.com/images/movie/56498/315x210.jpg?2957", 108, "「偶然にも最悪な少年」のグ・スーヨン監督の半自伝的な小説を、グ監督が自らのメガホンで映画化。山口県・下関に暮らす高校中退のフリーターで在日韓国人2世の青年グーは、外国人、警察、ヤクザとさまざまなところに顔がきくが、誰とつるむことなく生きていた。しかし後輩の辰が起こした事件をきっかけに、逃れることのできない暴力の連鎖に巻き込まれていく。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　PARENT　TRAP", "ファミリー・ゲーム／双子の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "カリフォルニア州で大規模なワイナリーのオーナーである父と暮らす11歳の少女ハリー。そしてロンドンでデザイナーである母と暮らす少女アニー。二人はサマー・キャンプで出会ったことから、自分たちが双子であり、産まれてすぐに両親が離婚したために離れ離れになったことを知る。そこで二人は見たこともない父、母に会うために入れ替わって帰宅する……。両親への愛情にあふれた双子の少女がロマンティックな計画を繰り広げるファミリー映画。オリジナルは、ドイツの人気児童文学『ふたりのロッテ』を原作にした「罠にかかったパパとママ」。ビデオ、DVDタイトルは「ファミリー・ゲーム」。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIDAY THE １３TH PART VII：THE NEW BLOOD", "13日の金曜日PART7 新しい恐怖", "", "", "http://ecx.images-amazon.com/images/I/51JTZHAV1EL._SL210_.jpg", 89, "本国アメリカではTVシリーズにもなった人気ホラーの第７作。今回は、不死身の名をほしいままにする殺人鬼ジェイソンに、一人の超能力少女が果敢にも戦いを挑んでいくというもの。前作でクリスタル・レイクの底に葬り去られたはずのジェイソンが再び蘇生し、容赦ない殺人を繰り返していくさまと、少女ティナが恐れおののきながらも巨大なテレキネシスを身につけていく過程とを緊迫感豊かに描いている。監督は、「ZOMBIO　死霊のしたたり」「ドールズ」などでSFXを担当してきたJ・ブックラー。", "1988-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月の出の決闘", "月の出の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "原作は川口松太郎の『利根川鴉』。片腕のない旅鴉・枡形の朝吉は、ある湯治場で死んだ女房そっくりの声を持つ、お安と出会う。お安は宮大工・徳次郎の妻だったが、今の徳次郎は右腕が利かず、しかもバクチ狂。その徳次郎が、事件で死んだお安を、朝吉が殺したと勘違いして……。役者として転期を迎えた、「不知火検校」直後の勝新太郎が、片腕の渡世人、朝吉を好演。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KHARTOUM", "カーツーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "19世紀半ば、清朝中国を舞台に内乱鎮圧に活躍した英国将軍“支那のゴードン”の晩年を描く歴史大作。スペクタクルものにしては説明が多すぎるが、ゴードン将軍役のヘストンと反乱軍側の回教徒リーダー役のオリヴィエの堂に入った名演が光る作品。1883年、スーダンで回教徒のリーダー、マーディによってイギリス人の将校と１万人ものエジプト人が殺される。事件の解決のためイギリス政府は、かつて清朝中国でも内乱を治め“支那のゴードン”と呼ばれるゴードン将軍をスーダンに派遣する。ゴードンはマーディと話し合うが、マーディの殺りくは止まらず、首都カーツームは死体であふれ出す。話し合いを続ける二人の間には、敵意ではなく、お互いを尊敬する気持ちが芽生え始めるが、マーディは戦いを止めるわけにはいかなかった。やがて、マーディの軍隊とイギリス軍は正面衝突を始めるのだった。", "1966-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヴィヨンの妻　桜桃とタンポポ", "ヴィヨンの妻　桜桃とタンポポ", "", "", "http://image.eiga.k-img.com/images/movie/53868/315x210.jpg?4177", 114, "太宰治の小説「ヴィヨンの妻」を、「雪に願うこと」の根岸吉太郎監督が映画化し、第33回モントリオール世界映画祭で監督賞を受賞した人間ドラマ。人気作家として世間から注目されるも、生きることに苦しみ酒や女に溺れる大谷と、そんな彼が酒代を踏み倒した小料理屋で働き、放蕩を続ける夫を愛し続ける妻・佐知の姿を描く。主演は松たか子、浅野忠信。共演に広末涼子、妻夫木聡、堤真一ら豪華キャストが揃う。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOTRE HISTOIRE", "真夜中のミラージュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "パリ郊外を走る列車のコンパートメントの中で、ロバートは奇妙な女と出会う。その女と行動をともにするうち、彼は次第に変な人間関係に巻き込まれていき……。A・ドロンがひさびさの熱演で、1984年セザール賞主演男優賞の栄光に輝いた本格的ミステリー・サスペンス。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Walk Softly, Stranger", "追いつめられた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "「第三の男」のジョセフ・コットンとアリダ・バリが再共演したサスペンス・ドラマ。裏社会から足を洗おうと故郷へ戻った賭博師クリスは、かつて思いを寄せていた女性エレインと再会する。彼女は事故により車椅子での生活を強いられており、性格までも暗く悲観的になってしまっていたが、クリスのひたむきで献身的な愛に少しずつ心を開いていく。しかし、完全に清算しきれていなかったクリスの過去が2人の元に忍び寄り……。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN TO RACE", "ボーン・トゥ・レース 青春の賭け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "スピードに命を懸けるストックカー・レースと、それに絡んで起きた女性誘拐事件を描いた、スリリングなアクション映画。監督は「ダーティハリー３」でデビューしたJ・ファーゴ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("有楽町で逢いましょう", "有楽町で逢いましょう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "フランス帰りの新進デザイナー・亜矢は知り合いの女子大生、加奈の兄・練太郎にプライドを傷つけられて怒り心頭。そのうえ弟・武志と加奈の交際に関しても彼と対立することに。だがファッション・ショーが不評に終わって落ち込む彼女を慰めてくれたのは、その練太郎だった。ヒット曲からタイトルを得た風俗ドラマ。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JACK THE BEAR", "みんな愛してる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "シングル・ファーザーと子供の愛情を描いた、心温まるドラマ。妻を事故で失い、12歳と３歳の二人の男の子を男手で育てることになった、TVのホラー番組で大人気のジョン。だが、母のいない寂しさと型破りな父の存在が、子供たちの重荷になってしまい……。D・デヴィートが父親役を熱演。ビデオ発売時タイトルは「ジャック・ザ・ベア／みんな愛してる」。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒルコ／妖怪ハンター", "ヒルコ／妖怪ハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "突如失踪したクラスメイトの行方をめぐって、奇怪な事件に巻き込まれていく考古学者と高校生たちが“ヒルコ”なる謎の妖怪と対決することに……。「鉄男　TETSUO」「東京フィスト」の塚本晋也が、異色漫画家・諸星大二郎の原作に挑んだSFホラー。考古学者役の沢田研二が三枚目ぶりを発揮した好演を見せる。ビデオ発売時、DVDのタイトルは「妖怪ハンター　ヒルコ」。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ROCK", "ザ・ロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "今では観光地となっているアルカトラズ島を突如、14人のテロリストたちが観光客を人質に立てこもる。テロリストたちは、海兵隊から奪った神経性毒ガスを積んだロケットをサンフランシスコに向け、国防総省（ペンタゴン）に１億ドルを要求してくる。テロリストのリーダーは、ベトナム戦争から数々の武勲を立てた英雄、ハメル准将だった。ロケット発射まで40時間。FBI長官は科学兵器のスペシャリスト、スタンリーに毒ガスを処理するように命じる。そして、もう一人、島の内部に詳しい男パトリックが呼ばれる。彼は33年前、アルカトラズ島から脱獄した経験を持ち、ある理由から30年間裁判も受けずに拘留されていたのだった。科学兵器のスペシャリストだが実戦経験のないスタンリーと元英国特殊部隊員のパトリックは、テロリストたちの待つアルカトラズ島に潜入する。平凡な男スタンリーをN・ケイジが、元英国特殊部隊員パトリックをS・コネリーがそれぞれ好演。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cheerfu11y チアフリー", "Cheerfu11y チアフリー", "", "", "http://image.eiga.k-img.com/images/movie/57208/315x210.jpg?4248", 80, "ハロプロエッグ出身の吉川友、元「ももいろクローバー」の早見あかりをはじめ、「ぱすぽ☆」「THE ポッシボー」「アップアップガールズ（仮）」のメンバーら総勢11人のアイドルが集結した青春映画。かつてチアダンスの名門だった都内の女子高に通う石川カンナは、ふとしたことがきっかけでチアダンス大会に出場することになる。早速メンバーを集め練習に励むカンナだったが、チアダンスに夢中になるあまり、家族や周囲と衝突することも増えてしまう。それでもチームは次第に一致団結していき、いよいよ大会本番当日を迎えるが……。", "2011-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREY FOX", "グレイフォックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1901年、ビルという名の老人が33年の服役を終えて、刑務所を出獄してきた。彼こそが“手を上げろ”というセリフを初めて使った西部史上名高き紳士強盗“グレイフォックス”だ。彼は駅馬車専門の強盗だったが、時代は変わり、今では蒸気機関車が主役。堅気に戻ろうと努力するビルだが、やはり昔の血が騒ぎ、列車強盗をたくらむ。一度目は大失敗。しかし、なんとかカナダへ逃げのび、そこで良き相棒を得る。今度はカナディアン・パシフィック鉄道を襲い、見事成功。カナダ史上初の列車強盗として記録されることになった。二人はある町に身を潜め、ビルはそこで女写真家ケートに出会い急速に接近。しかし、そんな幸せもつかの間、追手がすぐそこまで近づいていた。カナダの美しい自然と老紳士強盗の孤高の精神を見事に描いて、1983年のカナダ・アカデミー賞の主要７部門を独占した傑作。ファーンズワースは、本作以前に300本余りの映画に出演したという名バイプレーヤー。", "1983-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("孕み〈HARAMI〉白い恐怖", "孕み〈HARAMI〉白い恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "ピンク界の気鋭、田尻裕司監督が、「リンダ リンダ リンダ」などで活躍中の前田亜季を主演に撮り上げた“殺戮系”ホラー映画。主人公は、雪山のペンションで出産の時を迎えようとしている17歳の少女。彼女が山小屋に住む異様な風貌の男と接触したことから勃発する、凄まじい悲劇を描き出す。雪山での出産を控え、妊娠したことに実感を持てない少女、ゆい。彼女は大人たちが気味悪がる山小屋の大男、坂田に興味を抱くが……。", "2005-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CARRIE｀RE DE SUZANNE", "シュザンヌの生き方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "E・ロメールの初期中編の１本で、“６つの教訓的物語”シリーズの第２作でもある。薬科大学一年生のベルトランはホテル暮らしをしていた。ある日、カフェで休んでいると、隣のテーブルに女の子が座る。ベルトランの友人のギョームが早速、声をかけ、彼女が通訳学校に通うシュザンヌであることがわかる。ギョームは女の子には声をかけずにはいられない性格で、ベルトランはうらやましかった。長い間、公開されなかったが、「モンソーのパン屋の女の子」とともに1996年に初めて公開された。DVDは「エリック・ロメール コレクション(1)」に収録。", "1963-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Elephant Man", "エレファント・マン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "19世紀末のロンドンを舞台に、実在した奇形の青年ジョン・メリックの悲劇の人生を、「イレーザーヘッド」の鬼才デビッド・リンチ監督が映画化。見世物小屋で“エレファント・マン”として暮らしていた青年ジョンの元に、ある日外科医のトリーブスという男が現れる。ジョンの特異な容姿に興味を持ったトリーブスは、彼を研究材料にするため、自分が勤める病院に連れ帰ることに。こうしてジョンとトリーブスの交流が始まるが……。", "1980-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い画集・あるサラリーマンの証言", "黒い画集・あるサラリーマンの証言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "松本清張の原作を橋本忍脚色、堀川弘通監督で映画化した社会派推理映画の秀作。某社の課長・石野は、部下の女性との情事の帰り道に知人・杉山に会う。数日後杉山は、殺人事件の容疑者になり、アリバイ立証のために石野に証言を求めるが、石野は自分の地位や家庭を守るために証言を拒否する。杉山は逮捕され石野は愛人との仲を清算しようとするが……。日本の社会生活に密着し、なおかつミステリーとしての面白さも兼ね備えた松本清張の原作は、映画化にはもってこい。「張り込み」を筆頭に数多くの傑作が生まれているが、この作品は一連の松本清張のもののなかでは、特にエンターテインメント的な面白さを持った作品。この作品の成功により“黒い画集”シリーズが連作される。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Innocence", "エコール", "", "", "http://image.eiga.k-img.com/images/movie/1378/315x210.jpg?3863", 121, "森の奥深くにある学校“エコール”に、6歳の少女イリス（ゾエ・オークレール）がやってくる。高い塀で外部と遮断されたその学校では、6歳から12歳までの少女たちが年齢を区別するリボンと白い制服を身につけ、ダンスと自然の生態を学んでいた。男性のいない女性だけの閉ざされた世界にイリスは順応していくが、ある少女は耐えられず、壁を乗りこえて脱走を図る。中編「ミミ」のルシーア・アザリロビック監督による長編デビュー作。", "2006-11-04", "37,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふるさと", "ふるさと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "岐阜県の徳山村を舞台に、ダム工事のためにまもなく故郷を捨てなければならず、ボケの初期症状にも悩まされている老人が、自然の中で子供と対峙することで心をなごませ、ふるさとの山野を眺めながら息を引きとっていくまでを描いた叙情味豊かなドラマ。プレハブ小屋に住まわされている老人が、アマゴ釣りの名人だと知った少年は、老人に釣りを教えてくれと頼む。この少年との触れ合いによって老人の心もなごみ始め、やがて夏の終わり、二人は秘境・長者ヶ淵にやって来る。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMONG WOLVES", "狼たちの報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "テロリストに誘拐されたNATOの将軍救出を目的に集められた、様々な分野のエキスパートたちが大活躍するアクション映画。見事、将軍救出に成功したものの、事件が明るみに出るのを恐れたNATOによって、彼らは命を狙われることになる。監督は、渋い男の友情や義理と人情の世界を得意とするフランスの名匠J・ジョヴァンニ。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バッシュメント", "バッシュメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "元シブがき隊の布川敏和が初監督に挑戦。主演に「下妻物語」で圧倒的な存在感を示した土屋アンナを迎え、数奇な人生をたくましく生きる若者たちの姿をポジティブに描いた青春映画。辛い過去を胸に、激しい感情を吐き出す主人公の姿は、現代の若者の見えない本音をリアルに表出している。両親を殺害して失踪した兄へ復讐を誓うサーファー娘、樹里。ひょんなきっかけで襲った密輸ダイヤの取引現場で、兄と再会するが……。", "2005-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奇跡", "奇跡", "", "", "http://image.eiga.k-img.com/images/movie/55738/315x210.jpg?3329", 127, "「誰も知らない」「空気人形」の是枝裕和監督が、2010年3月の九州新幹線全線開通に沸く九州を舞台に描く家族ドラマ。主演は、兄弟お笑いコンビ「まえだまえだ」の前田航基と前田旺志郎。両親の離婚で鹿児島と福岡に離れて暮らす兄弟が、新幹線の全線開通をきっかけに、バラバラになった家族を取り戻そうと奮闘する姿を描く。共演に夏川結衣、阿部寛、原田芳雄、樹木希林ら是枝作品の常連が顔を揃える。", "2011-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Witch Hunt", "デニス・ホッパー　魔界世紀ハリウッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "怪優D・ホッパーが超現実都市の探偵役に挑んだSFハードボイルド。誰もが黒魔術を使える、架空の都市ハリウッドで映画プロデューサーが不審な死を遂げた。私立探偵のラブクラフトは、その謎を追ううちに、魔術をめぐる政治的な陰謀に巻きこまれ……。P・シュレーダー監督が異様なムードにあふれたドラマをスリリングに演出。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千の風になって", "千の風になって", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「君は裸足の神を見たか」の金秀吉監督が、オムニバス形式で生きる者と死せる者の関係を描く物語。新潟放送のラジオ番組『ミュージックポスト』内の名物コーナー“天国への手紙”に寄せられた実話をもとに、残された人間が先に逝った家族の記憶をどう受け止め生きていくか、その多様な姿を真摯に見つめた群像劇だ。新潟の人気ラジオ番組“天国への手紙”を記事にすることになった雑誌記者の紀子は、手紙を書いた人々に取材を始めた。取材を通じて生きる意味を考える紀子だったが、彼女もまた新たな生命の誕生の瞬間を迎えようとしていた。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Closer", "クローサー", "", "", "http://image.eiga.k-img.com/images/movie/1338/315x210.jpg?2764", 103, "ロンドンを舞台に、女性写真家、小説家志望のジャーナリスト、医師、アメリカから来た女性ストリッパー4人のそれぞれの出会いと、いくつかの別れが描かれていく。ローレンス・オリビエ賞新作劇賞などを受賞したパトリック・マーバーの戯曲を作者自身が脚本を執筆、「卒業」「ワーキング・ガール」などのベテラン、マイク・ニコルズが監督して映画化。ゴールデン・グローブ賞でクライブ・オーウェンが助演男優賞、ポートマンが助演女優賞を受賞。", "2005-05-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エドワード・サイード／OUT OF PLACE", "エドワード・サイード／OUT OF PLACE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "日本のドキュメンタリー界を代表する名手、佐藤真監督が、パレスチナ出身の世界的知識人として知られるエドワード・サイードの足跡を追う。パレスチナ問題の代表的な論客として注目を浴び、アメリカ言論界から迫害を受けながら勇気ある発言を続けた知識人、エドワード・サイード。2003年に亡くなった彼の生涯を、紛争の絶えないイスラエル・パレスチナの地を旅し、さまざまな人々の証言を基に解き明かしていく。", "2006-05-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIRAGE", "交差死体", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "不慮の事故で少女を殺してしまった元秘密警官のマット。苦い過去にさいなまれ堕落した生活を送る彼はある日、大金持ちの環境保護論者の妻ジェニファーのボディガードを依頼される。昼は貞淑な妻で夜は淫らなショーガールになる多重人格の彼女に気付きながらも、惹かれていくマット。だが、無残にもまた彼の目の前でジェニファーは惨殺されてしまう。そして一年後、ジェニファーにそっくりのレイシーと出会うのだが……。死んだはずの美女と出会った元警官の愛と憎しみを描いたミステリー・サスペンス。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽のあたる坂道", "陽のあたる坂道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "石坂洋次郎の同名小説の３度目の映画化。前２作で主人公・信次を石原裕次郎、渡哲也が演じたのに対して、今回は、三浦友和が演じている。原作の魅力を生かした吉松の演出は要所を押さえ、三浦友和、檀ふみのコンビもフレッシュな演技で好感が持てる。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市血笑旅", "座頭市血笑旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "５人組の殺し屋に狙われる座頭市は、自分の身代わりに殺された若い母親の赤ん坊を抱え、子守旅を続ける。５人組は好機とばかりに市に襲いかかり……。市の居合いも三隅研次の演出も冴え、大ヒットを記録。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEUX", "ふたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "パリの街で出会った音楽プロデューサーの男と不動産会社オーナーの女。恋に落ちた二人は結婚を決意するが、自由な大人であるがゆえに結婚に意味を見出せず、好きでいながら別れることになる。現代の恋愛観、結婚観を反映、男と女の愛の本質を捉えたラブ・ストーリー。次々に登場する美しいパリの風景も見もの。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASCARA", "マスカラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "C・ランプリングとM・サラザンらが織り成す美しくも異常な愛の世界。ゲビーは一人娘を抱えた未亡人、弟のバートは刑事で、二人はお互いを信頼し助け合って生活していた。しかしゲビーに恋人ができたことからバートは嫉妬し、少しずつ二重人格者になっていく……。", "1987-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("安娜與武林", "風雲！格闘王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "香港の人気スター、E・チェンとM・ヨンが共演した、カンフーたっぷりのロマンティック・コメディ。ひとクセもふたクセもある猛者たちが集う“天下武道大会”の裏で、男一人女二人の三角関係が進行するという組み合わせが愉快。日本が誇るアクションスター・倉田保昭も出演。広告代理店で働くケンは、栄養ドリンクのPRのために“天下武道大会”を発案。日本から参加したカンフーの達人、アンナと恋に落ちる。", "2004-12-04", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("唄祭り八百八町", "唄祭り八百八町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "年間平均10本の映画に出演していた美空ひばりの初期の１本。監督は名コンビの斎藤寅次郎。目明かしの三女で投げ縄の名人、実は大名の落としだねのおみよが、大名側近の陰謀をくじき、親子対面を果たすまでを描く。仇探しとろうあの娘の治療のため、盗みを続ける女賊・お鶴に清川虹子。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("行きずりの街", "行きずりの街", "", "", "http://image.eiga.k-img.com/images/movie/55018/315x210.jpg?1568", 123, "1992年度「このミステリーがすごい！」で第1位に輝いた志水辰夫の小説を、「闇の子供たち」の阪本順治監督が仲村トオル主演で映画化。かつて教え子との結婚が原因で都内の名門女子高を追われた元教師の波多野は、妻と離婚し、現在は故郷で塾の講師として働いている。そんな波多野は姿を消した元教え子のゆかりを探すため、12年ぶりに東京へと向かう。自分を学園から追放した男たちが事件にかかわっていることを知り、捜索の途中で元妻・雅子との再会を果たす。", "2010-11-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初恋死守決起大会／CRAZY FIRST LOVE", "君に捧げる初恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "“初恋の人と結婚する”という目標一筋な青年の奮闘を描くラブ・コメディ。実生活でも初恋の女性と結婚した「猟奇的な彼女」のチャ・テヒョンの直球芝居に加え、本作で清楚キャラを捨て初のコメディエンヌに挑戦したソン・イェジンが新鮮だ。テイルの夢は初恋の女性・イルメと結婚すること。しかし、それに反対する彼女の父親から、次々と難題を突きつけられてしまう。愛する彼女のため困難に立ち向うテイルだったが……。", "2006-06-10", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREQUENCY", "オーロラの彼方へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "N.Y.でオーロラが見えた日、刑事ジョンは、倉庫でたまたま発見した父の形見の無線機で30年前の父と交信することに成功する。その日は、父が事故死する前日だった。何とか父を助けたジョンだったが、彼のとった行動によって全く予期しない事態へと発展していく……。親子の絆を描くヒューマン・ファンタジーだけに留まらず、斬新なタイムトラベルのアイデアや火災シーンのスリリングなアクション、殺人がらみのサスペンスに転じていくストーリー展開など、常に観客をグイグイ引き付けるG・ホブリットの演出が冴える感動作。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDERED INNOCENCE", "偽証／殺人者は２度裁かれる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "殺人罪で服役中ながら、説法家として囚人たちからの人望が厚いティーチ。彼は入所以来、えん罪を主張し仮釈放を求めていたが、却下され続けていた。今度こそと思われていた申請が却下された時、ティーチはついに脱走を決行する。彼の目的は、自分に罪を着せたスコットに会い無実を証明すること。同じ頃、別の刑務所から仮釈放で出たスコットはティーチの脱走を知り、潔く対峙するために故郷へと急ぐが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UP PERISCOPE", "潜望鏡を上げろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "秘密の命令を受けて、日本占領下の島クサイエに向かったアメリカの潜水艦バラクーダ号。秘密の任務とは、島に上陸して日本軍基地内に潜入、暗号文書を盗み出すことだった。だがバラクーダ号はゼロ戦の攻撃で損傷を受ける。海戦ドラマに主人公のロマンスをちょっぴり絡ませた戦争もの。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SWORD IN THE STONE", "王様の剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "アーサー王伝説をもとにしたホワイトの小説『石の中の剣』をアニメーション化したディズニー作品。銃床に突き立てられた剣を引き抜く者が、次のイングランドの王になるという伝説が語り継がれていた中世。魔法使いのマーリンが予言した次の王は地方領主の下男ワートだった。心優しいワートにマーリンは帝王教育を施していくが……。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDERS IN THE RUE MORGUE", "モルグ街の殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "AIPの“ポー”シリーズ最後の作品。映画は原作と大幅に異なり、舞台となるのは恐怖劇上演中の芝居小屋。そこで起こった「オペラ座の怪人」ばりの連続殺人を描く。シリーズ初期の格調高さより、むしろ殺人の残虐描写に主眼を置いた演出が現代的だ。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DROP ZONE", "ドロップ・ゾーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "連邦捜査官のビートとテリーの兄弟は、コンピュータ・ハッカーのリーディを民間機で連邦刑務所に連行する途中、武装したテロリストたちの攻撃を受け、リーディを奪われてしまう。彼らは、後部扉を爆破し、時速900キロの機体から飛び降りた。犯人を奪われ、おまけにこの時の爆破でテリーを亡くしたビートは、復讐心に燃える。そして、犯人たちを一流のスカイダイバーとにらむのだった。「ブルーサンダー」でヘリコプターの空中戦を描いたJ・バダムがここではスカイダイビングを題材にしたエンターテインメント作品。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FASHION STORY", "FASHION STORY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOU LIHGT UP MY LIFE", "マイ・ソング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "CM音楽作曲家であるブルックスが低予算、ノー・スターをポリシーに製作したラブ・ストーリー。恋人がいるにもかかわらず、他の男を愛してしまった女の物語。主題歌も大ヒット、この曲を歌ったデビー・ブーンは一躍人気歌手になった。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VISION QUEST", "ビジョン・クエスト 青春の賭け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "アメリカの高校生の生活をさわやかに綴った青春映画。学校のレスリング部に所属する18歳のラフティンは、減量して１ランク下の州チャンピオンに挑戦したり、年上の女性に恋をしたりとチャレンジ精神が旺盛だ。そんな彼の日常がアップテンポで描かれた佳作。主題歌をマドンナが担当。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の警察・国際線待合室", "女の警察・国際線待合室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "“女の警察”と呼ばれる正秋が一流のホステスに育てた佐和子が失踪する。正秋は彼女の勤めていた店を訪ね、客の田淵が怪しいとにらむ。やがて佐和子は、シンガポールの私娼街で麻薬漬けになっているところを発見されるが……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Riff-Raff", "リフ・ラフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "タイトルのリフラフとは、最下層の人々を指す蔑称。サッチャー保守党政権下のロンドンを舞台に、彼らリフラフ達の厳しい現実を、イギリスの巨匠ケン・ローチ監督が皮肉をこめて描いた作品。刑務所を出たばかりのスティーブは、建設現場に職を見つける。しかしそこは、劣悪な労働条件の下で働かされる掃き溜めのような場所だった。ある日彼は、歌手志望の娘スーザンと出会う。お互いに惹かれあった2人は一緒に暮らし始めるが……。", "", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ridicule", "リディキュール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "18世紀のフランスを舞台にした貴族社会の人間模様を「仕立て屋の恋 」のパトリス・ルコント監督が描く。故郷を救う計画を国王ルイ16世に陳情するため、ベルサイユへやってきた地方貴族のポンスリュドン。ベルガルド侯爵という後ろ盾を得た彼は、宮廷社会で次第に人脈を広げていく。ポンスリュドンをシャルル・ベルリングが、ポンスリュドンと恋に落ちるベルガルドの娘マチルドをジュディット・ゴードレシュが演じている。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南国太平記", "南国太平記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "維新の英傑、益満休之助を主人公にした大河ドラマ。２部構成で、前編が島津藩の後継をめぐるお家騒動を描く、暗殺と復讐のドラマ。後編が一藩の中の抗争に嫌けのさした益満がより大きな理想である討幕・維新に向かい、元部下の一弾に倒れるまでを描く。何度も映画化された有名な原作の初のトーキー化作品。今井正が助監督として名を連ねている。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが町", "わが町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "フィリピンの困難な道路建設に従事した男が、人力車をひいて娘と孫娘を育てあげるド根性物語を、明治・大正・昭和にわたって描く。川島雄三は敬愛する織田作之助の原作をもとに、男の意地と執念を短い時間の流れのなかで、テンポよく綴った。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SANS ESPOIR DE RETOUR", "ストリート・オブ・ノー・リターン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "黒人と白人の人種対立が暴動となって荒れ狂う港町を舞台に、数年前の事件が原因で声を失い、浮浪者にまで身を落とした元歌手の男が繰り広げる、復讐と愛の物語。ハリウッドB級映画の異端児フラーが描くアクション・メロドラマで、効果的に使われるフラッシュ・バックや回想ショットが印象的。K・キャラダインが久々に歌手役で、得意のノドを聴かせている。", "1989-01-01", "2,25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アウトフォーカス", "アウトフォーカス", "", "", "http://image.eiga.k-img.com/images/movie/57858/315x210.jpg?1265", 80, "日本ジャーナリスト専門学校の卒業生が中心となって手がけた青春恋愛映画。写真をきっかけに気持ちを通わせ、戸惑いながらも成長していく高校生の姿を描く。同級生の恵に恋心を抱く高校3年生の晴樹は、隠れて恵を撮影し、その写真を眺めるだけで満足していた。ある日、ひょんなことから撮った写真を恵に見られてしまうが、恵はバスケ部のエースの圭介が女の子が親しくしている写真を撮ってくれないかと晴樹に依頼。2人は写真を撮るためにともに行動することで次第に距離を縮めていく。", "2012-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸城大乱", "江戸城大乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "江戸城を舞台に徳川五代将軍の座を狙う争いを描いた娯楽色の強い時代劇。1680年。大老・酒井雅楽守は、四代将軍・家綱が病弱で子供がないことから五代目継承の人選を進める。家綱の弟・綱重か、綱吉か？ それとも尾州、紀州、水戸御三家から出すか？ ところが、そんな時に綱重が襲撃され暗殺されたことから事態は二転三転する。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In the Shadow of the Pagodas: The Other Burma", "ビルマ、パゴダの影で", "", "", "http://image.eiga.k-img.com/images/movie/53488/315x210.jpg?2464", 74, "ビルマ（ミャンマー）の少数民族に対する迫害の実態を捉えたドキュメンタリー。スイス人監督アイリーヌ・マーティーと撮影クルーが、\"観光用PR番組の撮影\"と偽り軍事政権による独裁が続くビルマに潜入。弾圧から逃れるためジャングルの奥地を移動しながら暮らす少数民族や、20年以上も故郷に戻れず難民生活を送る人々、さらに民主化を求めて戦う反政府組織の兵士たちの証言を通し、彼らの過酷な現実を明らかにしていく。", "2008-03-15", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bird’s Eye〈バーズ・アイ〉", "Bird’s Eye〈バーズ・アイ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "「仮面ライダー龍騎／EPISODE FINAL」の涼平と「凶気の桜」のRIKIYA共演による新感覚アクション。アクション・スターとそのスタントマンのコンビが、ある事件に巻き込まれ大暴れする。アクション俳優・直人とそのスタントマン・剛志は高校時代からの悪友同士。あるドラマの撮影中、一人の女子高校生が紛れ込み現場は騒然となる。どうやら、彼女は謎のグループに追われているようで……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TECHNICA DI UN OMICIDIO", "殺しのテクニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "寡黙な殺し屋ハリスは引退を決意していたが、犯罪組織から最後の仕事として秘密情報を漏らした裏切者の射殺を依頼される。さっそく助手のトニーとともにハリスはパリに飛んで裏切者の消息をたどり追い詰めるが、その正体は意外な人物だった。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’HOMME BLESSE´", "傷ついた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1991年にエイズで亡くなった、作家E・ギベールが脚本に参加した、J・H・アングラードの出世作。孤独な18歳の少年、アンリ。彼はある日、野性的で危険な匂いを漂わせる男ジャンと出会う。アンリは徐々に、ジャンに魅了されていき……。ホモセクシュアルを描いているが、柱となるのはアンリとジャンのパッションのぶつかり合い。フランス演劇界を代表するP・シェロー監督の演出も見事だ。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING OF THE KICKBOXERS", "キング・オブ・キックボクサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "タイ・バンコクとニューヨークを舞台にした格闘アクション。10年前、キックボクシングの新チャンピオンとなった兄を、目の前で殺された弟ジェイクは、今はニューヨーク市警の捜査官となっている。ある犯罪集団の捜査で入手したビデオに、忘れもしない兄を殺した男カーンが映っていた。ジェイクは単身タイヘ飛び、キックボクシングの過酷なトレーニングを積みながら、兄の復讐を誓う。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDERTOW", "アクシデント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「ヒッチャー」の脚本を手掛けたE・レッドによる密室サスペンス。流れ者のジャックは、ある嵐の夜、車で谷底へ転落してしまう。山間の一軒家に住む夫婦に助けられ意識を取り戻したものの、ジャックはいきなり夫のライルに銃を突きつけられる。部屋の中にはライルの狂気を象徴するかのように、あらゆる武器がそろえられており、ジャックは成す術もなく密室に閉じ込められる。ライルはおとなしい妻ウィリーを虐待し、ジャックには度胸試しを強要するなど、次第に狂暴性をエスカレートさせていく。一方虐げられた者同士、ジャックとウィリーは互いに惹かれ合い、ライルのさらなる怒りをかってしまう……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHECKING OUT", "チェッキング・アウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「あなたがいたら／少女リンダ」に続くD・リーランド監督の第２作。友人の死を境に、死の恐怖に取り憑かれた男を描いたコメディ。航空会社に勤めるレイの友人、パットが心臓発作で急死。翌日からレイは、胸を押さえて病院に駆け込むようになり……。まるでウディ・アレンが演じるような神経症の男を、J・ダニエルズが好演。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BULLES", "奴らを2回黙らせろ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "自分たちにたてつく者がいれば即座に血の制裁を加える暴力一家に支配されたホーンタウン。誰もが恐れ口を閉ざすなか、愛しい女のため、傷ついた母のため、男の誇りを懸けて一家に戦いを挑む男の姿を描くバイオレンス・アクション映画。", "1986-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAMELOT", "キャメロット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 176, "アーサー王と円卓の騎士ランスロットの有名な逸話をもとにしたブロードウェイ・ヒットミュージカルの映画化。英国王アーサーは王妃グエナビアとともに平和と幸福の地“キャメロット”を築き円卓の騎士を集めるが、ランスロットと王妃の許されざる恋で楽園は血にまみれる。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナチュラル・ウーマン2010", "ナチュラル・ウーマン2010", "", "", "http://image.eiga.k-img.com/images/movie/55368/315x210.jpg?3785", 104, "「親指Pの修業時代」「犬身」などで知られる松浦理英子が、女性同士の恋愛を描いた小説を、写真家・野村誠一が映画化。主演は亜矢乃、汐見ゆかり。フォトグラファーの容子は、学生時代の恋人・花世との別れ以来、なかなか人と深く付き合うことができずにいた。ある日、容子は少し前から互いに惹かれ合っている由梨子と取材旅行に行くことになるが、その矢先に近所のペットショップで働く花世と偶然再会する。封印していた花世との思い出が蘇り、容子は2人の女性の間で揺れる。", "2010-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PIRANHA", "ピラニア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ジョーズ」のあとを狙って作られた、動物パニックものの一編。ある科学者が作ったスーパー・ピラニアと人間との戦い。監督を「グレムリン」のJ・ダンテ、脚本を「ブラザー・フロム・アナザー・プラネット」「希望の街」のJ・セイルズが担当しているのも興味深い。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Vie Est Un Miracle!", "ライフ・イズ・ミラクル", "", "", "http://image.eiga.k-img.com/images/movie/1498/315x210.jpg?3926", 154, "「黒猫・白猫」「アンダーグラウンド」でヨーロッパの映画賞を席巻、本年度カンヌ映画祭の審査委員長を務めたエミール・クストリッツア監督が、祖国旧ユーゴスラビアの歴史を背景に、実話を基に描いたラブ・ストーリー。ボスニアの小さな村の鉄道技師ルカは、妻とサッカー選手を夢見る息子と平穏な日々を送っていたが、紛争勃発で激変。妻は去り、息子は徴兵されて一人暮らしになったルカは、ムスリム人の女性サバーハに出会う。", "2005-07-16", "44,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超・悪人", "超・悪人", "", "", "http://image.eiga.k-img.com/images/movie/56258/315x210.jpg?4666", 88, "青春と性をテーマに監督たちが独自の個性で自由に製作する「青春H2（セカンド）」シリーズの1作目。とある実録犯罪系雑誌の編集部にある日、1本のビデオテープが届く。そこには、過去10年間に107人もの女性を強姦（ごうかん）したと豪語する自称「悪人」の男からの告白と、ある強姦殺人事件の一部始終が収められていた。編集者の白石とルポライターのヤエコは、その男を取材するため指定された場所へ向かうが……。監督は「ノロイ」「テケテケ」「グロテスク」などホラー映画で活躍する白石晃士。", "2011-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ライブイン・茅ヶ崎", "ライブイン・茅ヶ崎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "PFFアワードに入選した八ミリ作品。湘南に住む若者たちの日常を洒脱な映像とセリフでつづる。記念撮影風にカメラに向かう三人を露出を変えて何度も撮ったり、東京に遊びに行くときのときめきが伝わってくるような場面など、さりげない日常をありのままに捉えた伝説的な作品。", "1978-05-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新上海灘／SHANGHAI GRAND", "上海グランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1930年代の上海を舞台に、暗黒街に生きる二人の男と、一人の女の三角関係を描いたアクション・ロマン。暗黒街でのし上がるホンとリク。彼らの固い友情は、同じ女性を愛したことから揺らぎ始める。さらに、身内の裏切りによって彼らは窮地に立たされ……。L・チャンとA・ラウという香港の二大スターが、男の友情を哀切に演じている。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピッグス・インフェルノ", "ピッグス・インフェルノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "リオ・デ・ジャネイロ映画祭など海外の映画祭で評価を受けた悲しい愛の物語。ミュージカル映画の要素を取り入れながら、ドキュメンタリータッチで描かれた前半部分から、クライマックスの黙示的風景まで、美しく斬新な映像世界が物語に深みを与えている。幼いわが子を監禁し、兄妹の愛を綴った作家が謎の失踪。物語の真意を探るドキュメンタリー作家は監禁された兄らしき青年と出会う。", "2005-11-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オムライス", "オムライス", "", "", "http://image.eiga.k-img.com/images/movie/56048/315x210.jpg?4796", 109, "「ニセ札」「ワラライフ!!」に続く、お笑いタレント・木村祐一の監督第3作。木村自身が街で見つけたモノを写真に収め、ツッコミや妄想によって笑いに転換していく「写術」をベースに、街中にあふれる日常的な風景、モノから連想、妄想をふくらませて生まれたいくつもの物語が連なる。木村自身のほか、明石家さんま、岡村隆史、板尾創路、東野幸治ら吉本芸人・タレントが多数出演。", "2011-09-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今年の恋", "今年の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "正月映画として製作された軽いタッチの風俗映画。親友同士の高校生・光と一郎、それぞれの兄と姉ということで偶然出会った正と美加子。正は美加子に一目惚れしてしまう。美加子はいつまでもつっけんどんな態度をくずさないのだが……。「東京湾の突風野郎」など新東宝作品で活躍していた吉田輝雄の松竹移籍第１作。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パレットナイフの殺人", "パレットナイフの殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "江戸川乱歩の名作ミステリーをベースにした推理ドラマ。画家の松村は、未亡人の悠紀子の肖像画を描いていたが、悠紀子に恋する特高の岩崎によって投獄される。やがて、釈放された松村は再び悠紀子の絵を描き始め、思いを断ちきれない岩崎は悠紀子を殺害。だが、殺人の容疑は松村にかけられてしまった……。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONA LISA", "モナリザ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "刑務所帰りのしがない中年チンピラのジョージが、ボスの“身代わり服役”のお礼としてもらった仕事は、黒人の高級娼婦シモーヌのドライバーだった。初めはソリが合わない二人も心打ち解け、いつしかジョージはシモーヌにかなわぬ恋心を抱いてしまう。主演のホスキンスは、カンヌ以下ゴールデン・グローブ賞など、世界中の主演賞を独占するほどの見事な演技を見せた。ナット・キング・コールのヒット曲『モナリザ』に想を得て作られたこの映画は、「狼の血族」に続くジョーダン監督第３作である。G・ハリスンが製作総指揮を担当した。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　PACIFIER", "キャプテン・ウルフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "アクション俳優のヴィン・ディーゼルが、本格コメディに挑戦。国家機密レベルの重要任務に就いたはずの無敵のソルジャーが、意に反して悪ガキどもの世話をするハメに。炊事、洗濯、カモの飼育に奔走する、これまで見たことがないヴィン・ディーゼルの愛すべき姿に注目。米海軍特殊部隊のエリート軍人シェーン・ウルフ。ある日、彼に新たな任務が下るが、それはワガママな子供たちの世話係だった。", "2005-10-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AS DES AS", "ジャン・ポール・ベルモンドの　エースの中のエース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "第一次大戦のさなか、仏・独それぞれの撃墜王として銃火を交じえた二人のパイロット。戦後、親友となった彼らはヒトラー率いるナチスに迫害される人々を救うため、ともに命を懸けた行動に走る。ベルリン・オリンピックを背景に、男の友情、冒険、コメディ、スリルを織り交ぜた傑作アクション。", "1979-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シャーリーの好色人生と転落人生", "シャーリーの好色人生と転落人生", "", "", "http://image.eiga.k-img.com/images/movie/54508/315x210.jpg?3927", 110, "周りを気にせず、本能と欲望のまま生きるシャーリーと、シャーリーとくされ縁の中内、そして彼らが訪れる場所で巻き起こる美女たちとの愛憎と陰謀のドラマ。新鋭・佐藤央が監督を務める「シャーリーの好色人生」と、「パンドラの匣」「パビリオン山椒魚」など話題作を手がける冨永昌敬の「シャーリーの転落人生」がお互いを補完し合う、２つのストーリーで構成された意欲作。", "2009-04-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まぶだち", "まぶだち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "少年少女の初恋をみずみずしく描いた「この窓は君のもの」で鮮烈な監督デビューを飾った古厩智之が７年ぶりに手掛けた作品。幼い頃の実体験をもとに、ドライに見えてとても繊細な中学生の複雑な心境をのどかな風景の中に描き出し、ロッテルダム国際映画祭でグランプリと国際批評家連盟賞を受賞したホロ苦い青春ドラマだ。長野県の田舎町。中学生のサダトモは何でもソツなくこなし、友人からは一目置かれた存在。イタズラばかりしているサダトモたちを、担任教師はクズ呼ばわりしている。そんなある日、サダトモたちは遊び半分で万引きをし、これがきっかけである事件が起きる。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLIND JUSTICE", "ブラインド・ジャスティス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "時は南北戦争時代。アメリカの荒野を赤ん坊を連れて旅をしているケイナンという男がいた。ロス・ポタレスという名の土地を探して、西部にたどり着いた彼は、金目当てのならず者たちに襲撃されるが、相手の気配をいち早く感じたケイナンは、次々と相手を撃ち殺していく。驚くべきことに、彼は盲目のガンマンだったのだ！　「ジャッジ・ドレッド」で、強烈な悪役を演じた個性派俳優A・アサンテが、盲目の子連れガンマンというユニークなキャラクターに扮している。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAGUAR LIVES！", "ジャガー No.1", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "世界８大都市を舞台に繰り広げられるスーパー・アクション。元プロ空手世界ヘビー級チャンピオンのルイスが、各国の指導者暗殺をたくらむ悪の組織“第四世界”を、得意の空手で壊滅させる。スリリングでリアルな死闘シーンが迫力ものの本格派。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャッチボール×3 TWILIGHT FILE VII", "キャッチボール×3 TWILIGHT FILE VII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "オムニバスシリーズ「TWILIGHT FILE」第7弾の1作。塾講師の風間亮は、父や教育熱心な母に反発する娘・楓について思い悩んでいた。そんなある日、偶然「市民三角ベース大会」のポスターをはっている佐伯小夜と出会った亮は、三角ベース大会を通じて家族のきずなを取り戻そうと、妻の反対を押し切り楓を連れて練習に参加するが……。主演は狂言師の和泉元彌。", "2010-11-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEART OF DIXIE", "思い出のハートブレイク・ホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "アン・リバース・シドンズの小説をもとに、３人の女性が恋愛、友情を通して成長していく姿を描く。1957年のアメリカ南部。大学４年生のマギー、ディリア、エイケンは親友同士。マギーはある日、黒人差別に批判的なホイトと知り合い、自らも怒りを感じて、大学新聞に記事を書くが、大学から圧力をかけられる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キリコの風景", "キリコの風景", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "人気のないマンションが建ち並ぶ函館の町にやって来た男。彼はタクシー運転手や不動産会社の社員らを振り回し、不思議な超能力で人々の心の奥底を暴き出していく。そんな男がこの地を訪れた真の目的とは……。森田芳光が脚本を執筆したシュールなドラマ。函館で再会するかつての夫婦を、杉本哲太と小林聡美が演じている。", "1998-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE McKENZIE BREAK", "マッケンジー脱出作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "第二次世界大戦中、実際に起こったドイツ軍捕虜脱走事件をもとにした娯楽派のアクション戦争映画。脱出不可能といわれたスコットランドのマッケンジー捕虜収容所を舞台に、英国諜報部と脱出を企てたドイツ人捕虜たちとの駆け引きを、スリリングかつ見せ場もたっぷりに描く。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明日なき暴行", "明日なき暴行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "強盗をはたらき、相棒を殺していた男が恋人と逃亡、女は男と別れようとするが、男は彼女を離さない。二人の争いから女は土壇場で警察に通報する。追いつめられた二人の人間の醜さを浮き彫りにした、ドキュメンタリー・タッチの力作。“ピンク映画第２世代”渡辺護監督の最も脂の乗った時期の作品。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RAWHIDE YEARS", "顔役時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "開拓史時代のアメリカ南部。ミシシッピ川の蒸気船でいかさま賭博のサクラをしているベンは、殺人の汚名を着せられ、流浪の牧童となって西部へ逃れた。３年後、将来を誓った恋人の元を訪れた彼は……。犯人探し、恋人奪還など、様々な要素のある娯楽西部劇。A・ケネディのケチな相棒ぶりが絶品。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASK　DE　４１〈マスク・ド・フォーワン〉", "MASK　DE　４１〈マスク・ド・フォーワン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "CMディレクターとして活躍する村本天志監督が、プロレスへの愛情を込めて撮り上げた笑いと涙のドラマ。家族との絆は崩壊寸前、会社ではリストラに脅えるばかりという中年サラリーマンが、身に染みついた負け犬根性を返上し、プロレスの新団体設立に情熱を燃やしていく様を描く。田口トモロヲが肉体改造を行い、リングに上がる主人公を熱演している。自動車会社で勤務する平凡なサラリーマン倉持忠男は、会社から突然の肩叩きに遭う。不景気な世の中で再就職に不安を抱く忠男の前に、学生時代の友人である蛯脇が現れて、新興プロレス団体設立の話を持ちかけられるが……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUFFY THE VAMPIRE SLAYER", "バッフィ ザ・バンパイア・キラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "高校のチア・ガール、バッフィは素質を見込まれてバンパイア・キラーの後継ぎに選ばれる。いやいやながら吸血鬼殺しの特訓を受けるバッフィだが、その頃、町には凶悪な吸血鬼がよみがえり、人々を襲い始めていた。果たしてバッフィは町を救えるのだろうか？　ホラーと学園コメディをミックスさせた痛快娯楽映画。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sunshine", "太陽の雫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 180, "19世紀末、ブダペストに暮らすユダヤ系のゾネンシャイン（ドイツ語で“太陽”“喜び”の意）一族は、秘伝の薬草酒“サンシャインの味”で財を成す。だが、その栄光もつかの間、彼らはナチのユダヤ人迫害、そしてハンガリー動乱など激動する歴史の波に飲み込まれていく。ゾネンシャイン家の一人、イヴァンが曾祖父、祖父、父、そしてイヴァン自身の半生という一族の100年の歴史を追憶する形で語られる壮大な叙事詩。世代の違う男性たちを演じ分けたR・ファインズの好演が光る。監督は「メフィスト」でアカデミー賞外国語映画賞を受賞したハンガリーの巨匠I・サボー。ヨーロッパ映画祭主演男優賞、撮影賞、脚本賞受賞、ゴールデン・グローブ賞作品賞、監督賞、オリジナル作曲賞ノミネート。", "1999-01-01", "10,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獄（ひとや）に咲く花", "獄（ひとや）に咲く花", "", "", "http://image.eiga.k-img.com/images/movie/55238/315x210.jpg?2044", 94, "1854年、吉田松陰は海外密航を企てたという大罪で、長州藩の士分を収容する牢獄“野山獄”に送られる。ひとたび入れば生きては出られない地獄の牢獄として知られる野山獄は、長い幽閉生活で希望を失った者ばかりで溢れていた。しかし、常に前向きな松陰の姿に、彼らも少しずつ心を動かされていく。唯一の女囚・高須久も、短歌の集いなどを通じ、松陰との仲を深めていくが……。主演に近衛はな、前田倫良。原作は直木賞作家・古川薫の「野山獄相聞抄」（改題：吉田松陰の恋）。", "2010-04-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAKPOSHTHA　HAM　PARVAZ　MIKONAND", "亀も空を飛ぶ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "イランのクルド人監督、バフマン・ゴバティの「わが故郷の歌」に続く長編第３作。アメリカの侵攻が近づき、にわかに揺れ動き出すイラクの小さな村を舞台に、過酷な状況下に置かれながらも逞しく生き抜く子供たちの姿が描かれる。ニュースでは伝えられないイラクの悲痛な現実が盛り込まれた物語には、胸が締め付けられる。'03年、アメリカによるイラク侵攻の暗い影が忍び寄る、イラクのクルディスタンの村。戦争孤児のソランは、村に避難してきた難民の少女、アグリンに淡い恋心を抱く。", "2005-09-17", "77,15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　MATRIARCA", "女性上位時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "若くして未亡人となったミミは、葬式のあとで、夫がひそかにマンションを隠し持っており、またそのマションは夫の“特殊な欲望”を満足させるすべてのものがそろっていることを知る。ミミはそのすべてを知りたい探求心に燃えて、歯医者やテニス教師、親友の夫と、次々と相手を変えては秘密の生活を楽しむが、医師デ・マルキが彼女に求愛して……。1960年代に明るいエロティシズムを撒き散らして人気を得ていたC・スパーク主演のセクシー・コメディ。", "1968-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LOVER", "令嬢チャタレイ３ 愛人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "前２作の監督ローレンス・ウェーバーに代わり、フランク・デ・ニーロが撮ったシリーズ第３作。チャタレイ卿の妻ジョエルは、昔の愛人である庭師のチャールズが殺人罪の服役から戻ると再び関係を持ち始めた。その乱れた関係を知ったチャタレイ卿はチャールズを永久に屋敷から追放する。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LOSS　OF　SEXUAL　INNOCENCE", "セクシュアル・イノセンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「リービング・ラスベガス」のM・フィギス監督が、自身の体験に基づいて描いた人間ドラマ。主人公は、５歳でセックスを意識し出した早熟な映画監督ニック。アフリカに向かった彼は機内で美しい女性と出会い、好意を抱く。だが彼女には、ある秘密があり、アフリカに着いたニックは、それが原因で悲劇を目の当たりにする……。幻想と現実が交錯するユニークなドラマを通して、性を知ることで失われる純粋さを浮き彫りに。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("都会の空の非常線", "都会の空の非常線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "「都会の空の用心棒」に続く小林旭のパイロットもの。旭扮するヘリコプターのパイロットが、土地の利権を狙う悪玉一味と戦う。「紅の翼」などで見せた日活特撮陣の航空シーンは今回も快調で、ヘリコプターによる航空活劇シーンが次々と登場する。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不良番長・やらずぶったくり", "不良番長・やらずぶったくり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "暴力団・花岡組に新宿を追放された神坂以下カポネ団の面々は、神坂の“練鑑”時代のダチ公・伊吹を訪ねて、ある漁村で働く。この漁村では、水産会社の肥料工場から排出される廃液のための公害問題が持ち上がっていた……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moscow on the Hudson", "ハドソン河のモスコー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　ソ連のサーカス団の一員でサックス奏者のウラジミルは、生活物資が満足に供給されず、監視の目も厳しい母国で祖父や両親とともに暮らしていた。ある時、サーカス団がアメリカ・ニューヨークで公演することになり、ウラジミルも渡米。その公演後、彼は勇気を奮い、土産を買いに寄ったデパートで亡命を決行する。やがて始まった新生活の中で友人や恋人もできたが、国に残してきた家族のことも気がかりで……。そんな亡命者の姿をユーモラスかつ感動的に描いた人間ドラマ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("USB", "USB", "", "", "http://image.eiga.k-img.com/images/movie/54488/315x210.jpg?3897", 95, "原子力発電所の臨界事故が原因で放射能汚染が進む、茨城県筑波のとある町。この町に暮らす26歳の祐一郎は、医者だった父の跡を継ぐべく医学部を目指しているが、5年目の浪人生活を送るうち、絶望と焦燥からギャンブルやドラッグの世界にはまっていく。そんなある日、極秘で行なわれている放射線科の臨床試験アルバイトを知った祐一郎は……。監督は、ベルリン国際映画祭フォーラム部門に出品された「カインの末裔」などで知られる奥秀太郎。", "2009-06-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("案山子／KAKASHI", "案山子／KAKASHI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "行方不明の兄、剛のアパートを調べていたかおるは、彼女の高校時代の同級生だった、泉が剛に宛てたラブレターを見つけ、泉の実家がある山間の村に向かう。しかし、両親は泉は診療所に入院中だと言い、面会もさせてくれず、結局その晩は泊めてもらうことになる。夜中、眠れないかおるは人影を追って納屋に向かい、そこで赤い服を着せられた案山子を見つける。そして背後からの声に振り返ると闇の中に泉がいた……。伊藤潤二原作の短編を、数々のオリジナル・ビデオや「リング０・バースデイ」などでホラー・ファンをうならせる鶴田法男が映画化したサスペンス・ホラー。かおる、泉、剛の奇妙な愛の構図、その先に生まれた恐怖をじっくりと映し出していく。哀しき幽霊を演じた柴咲コウの冷淡な目の表情が印象的。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAMELEON", "ロマンシング・トレジャー 遺産相続・40億の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "南米の老ボス、セルジオは40億円の遺産を譲る後継者として二人の若者を競わせることにする。二人はジャングルでのサバイバル・ゲームで勇気を、娼家で性的実力を競い、最後に知力の勝負となったが……。膨大な遺産を持つ老ボスを、1989年に他界したL・V・クリーフが演じる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERCULE＆SHERLOCK", "２dogs　エルキュールとシャーロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "仏米合作のファミリー・コメディ。ドジな人間よりも賢い犬たちのキュートな活躍が見どころ。チンピラのヴィンセントとブルーノが警察の麻薬捜査のとばっちりを受け、苦労して手にしたニセ札を押収されてしまう。困り果てた彼らは、ニセ札のありかをかぎ分けることができる二匹の警察犬を誘拐するが……。", "1996-01-01", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プライド・ワン", "プライド・ワン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "オートバイのワールド・グランプリ・レースの模様を追ったドキュメンタリー。世界の超一流レーサーたちの凄まじい勝利のゴールへの執念が、緊迫するレース展開の中で映し出されていく。日本からは片山敬済、平忠彦が出場、激しいデッドヒートを繰り広げる。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ITバブルと寝た女たち", "ITバブルと寝た女たち", "", "", "http://ecx.images-amazon.com/images/I/41AXQZEjhBL._SL210_.jpg", 95, "家田荘子のノンフィクション小説「バブルと寝た女たち」をもとに、ITバブルに翻弄されたカップルの愛と転落を描いたエロティック・ラブストーリー。女優を夢見て田舎から上京した碧は、友人に誘われて参加した合コンで、ITベンチャーの若きオーナー社長・小田島と出会う。やがて小田島と恋に落ちた碧は、夢のようなセレブ生活を手に入れるが……。「東京大学物語」の三津谷葉子がヒロインを体当たりで熱演。", "2007-06-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLAWLESS", "フローレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "保守的でマッチョ・タイプの元警官ウォルトが、ある日突然、脳卒中で半身麻痺になってしまう。惨めな自分を他人の目にさらしたくない彼だったが、犬猿の仲であるドラッグ・クイーンのラスティからリハビリのための歌のレッスンを受けることに……。ニューヨークの片隅に暮らす人々の心の交流を、生活感たっぷりに綴った感動作。豪華なスタッフ、キャストの顔ぶれからは想像できないインディペンデント映画風の小品で、名優R・デ・ニーロとP・S・ホフマンが水と油の男たちの友情をしみじみと伝えてくれる。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボトムズ；ケースアービン", "ボトムズ；ケースアービン", "", "", "http://image.eiga.k-img.com/images/movie/55838/315x210.jpg?3039", 0, "1983～84年にテレビ放映され、現在も根強いファンをもつロボットアニメ「装甲騎兵ボトムズ」の新作3本を劇場で連続上映する「ボトムズフェスティバル」の第1弾。新天地への脱出を目指し、妹のドナとともに人型兵器ATの整備工場を営んでいる青年アービン。彼にはAT同士の決闘による裏賭博“バトリング”のファイターという裏の顔があった。ある日、彼はペイガンという男との勝負に敗れてしまい……。", "2010-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("好きだ、", "好きだ、", "", "", "http://ecx.images-amazon.com/images/I/41RGY94Z15L._SL210_.jpg", 104, "17年の空白を経て再会した男女の姿を端正な映像で紡ぎ出す。17歳のパートを演じる宮崎あおい＆瑛太は沈黙の中のドキドキ感を、34歳のパートを体現する永作博美＆西島秀俊は対話の中の覚悟を、それぞれ精緻に表出。息づかいまでがリアルに感じられる恋愛映画だ。女子高生ユウは、同級生ヨースケへ淡い恋心を抱いていた。ところが、互いに惹かれあいながらも気持ちを素直に表現できないまま、ある出来事をきっかけに二人は会わなくなってしまう。それから17年後、偶然二人は再会するが……。", "2006-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("呀 KIBA 暗黒騎士鎧伝", "呀 KIBA 暗黒騎士鎧伝", "", "", "http://image.eiga.k-img.com/images/movie/56908/315x210.jpg?4746", 45, "雨宮慶太が手がける人気特撮シリーズ「牙狼 GARO」のドラマ版に登場し、冴島鋼牙＝黄金騎士ガロと死闘を繰り広げた暗黒騎士キバを主人公にしたスピンオフ作品。鋼牙の父を殺害し、鋼牙の運命の人・御月カオルを己の欲望のために利用する闇の魔戒騎士バラゴ（暗黒騎士キバ）の過去が明らかにされる。キバ役の京本政樹、カオル役の肘井美佳らドラマ版のキャストが集う。", "2011-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("憐れみムマシカ", "憐れみムマシカ", "", "", "http://image.eiga.k-img.com/images/movie/55518/315x210.jpg?1282", 74, "山間の小さな町・怒猿町で町議会議員を務める柏木は、町長から町おこしのために未確認生物のねつ造を依頼される。はじめは乗り気でなかった柏木も、自ら扮する“未確認生物ヌッチー”が世間の話題になるにつれ、次第に計画にのめり込んでいく。家族に見放され計画をあきらめかける柏木だったが、騒動は次第に激しさを増していき……。新鋭・松田健太郎監督がメガホンをとり、名脇役の浅見小四郎が初主演を飾る。", "2010-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("N45°", "N45°", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "21世紀を担う映画監督に挑戦と競作の場を提供する〈J・MOVIE・WARS〉第２期の１本で、“ルーキーバトル”と銘打った、４人の新人監督によるオムニバス。北緯45度に位置する架空の町を舞台に思い思いの世界が描かれる。第１話は祭主恭嗣の1970年代の香りあふれるロード・ムービー「ワンダー・ラビッシュ」、第２話は細部への徹底したこだわりで日本映画の常識を覆した小峯隆生のガン・アクション「パオさんとの復讐」、第３話が佐々木浩久による郷愁を誘う近未来ウエスタン「情熱の荒野」、そして第４話は中嶋竹彦の世紀末的感性に彩られたサイキック・サスペンス「風は、どっちに吹いている」。祭主、佐々木、中嶋は助監督出身、小峯は雑誌編集者出身。小峯が取材したことのあるA・シュワルツェネッガーが声の出演をしている。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実験映画", "実験映画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "「白痴」にカメオ出演していた永瀬正敏が手塚眞監督と顔を合わせたコンセプチュアルな短編作品。映画撮影の依頼を受けた青年が、廃墟で一人の少女と撮影のための一週間を過ごすことになる。設定だけで脚本は用意せず、実際に７日間で撮影。劇中には永瀬が撮影したフィルムも使用されているが、映画自体には即興性はあまり感じられず、きわめてフィクショナルな肌触りとなっている。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OVERCOMING", "OVERCOMING／ ツール・ド・フランス激闘の真実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "世界最大の自転車レース、ツール・ド・フランスの内幕にカメラを向けたドキュメンタリー。デンマークの名門チームCSCによる'04年のレースを中心に、貴重なトレーニング風景から監督や選手の関係性までを臨場感たっぷりに映し出す。往年の名選手として知られるビャルネ・リース監督率いる、デンマークの名門チーム“CSC”は、'04年のツール・ド・フランスを前に、有能な選手の間でさまざまなトラブルが起きてしまう。", "2006-06-17", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("装甲騎兵ボトムズ　ペールゼン・ファイルズ　劇場版", "装甲騎兵ボトムズ　ペールゼン・ファイルズ　劇場版", "", "", "http://image.eiga.k-img.com/images/movie/54198/315x210.jpg?4057", 118, "1983年に放送を開始したTVアニメ「装甲騎兵ボトムズ」の前日譚で、メカや戦闘シーンを3DCGを用いて描いたOVA「装甲騎兵ボトムズ／ペールゼン・ファイルズ」の劇場版。監督の高橋良輔やキャラクターデザインの塩山紀生ら、初期オリジナルスタッフが再集結。100年にも及ぶ戦争が続くアストラギウス銀河で、“異能生存体”と呼ばれる不死の得意遺伝子について記されている極秘文章「ペールゼン・ファイル」を巡り、戦局は大きく変わろうとしていた。", "2009-01-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Catch a Fire", "輝く夜明けに向かって", "", "", "http://image.eiga.k-img.com/images/movie/33818/315x210.jpg?4794", 101, "アパルトヘイト政策下の南アフリカで民衆から“自由の戦士”と英雄視された実在のテロリスト、パトリック・チャムーソの半生を、「今そこにある危機」の名匠フィリップ・ノイス監督が映画化した社会派サスペンス。普通の男から反逆の英雄へと変貌していくチャムーソを「きみの帰る場所／アントワン・フィッシャー」で注目を集めたデレク・ルークが熱演。彼を追う捜査官に「ミスティック・リバー」のティム・ロビンスが扮する。", "2007-01-27", "2,3,67,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秋日和", "秋日和", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "小津安二郎作品の家族劇では娘役として欠かせない存在であった女優・原節子が母親役に回った、小津晩年の傑作。夫を失ったばかりの秋子は、亡夫の友人たちに再婚を勧められる。彼女にはその気はないが、まだ美しい未亡人である母親が再婚するのではないかと、娘のアヤ子は気が気でない。母親の気持ちを誤解した娘は反抗し始める。やがて二人は和解し、いつの日か嫁いでいく娘をつれて秋子はささやかな二人きりの旅行に出かける……。亡夫の友人を演じた佐分利信、北竜二、中村伸郎のとぼけたやりとりがおかしく、岡田茉莉子の初々しさも印象深いが、何より母娘旅行のシーンの優しさが心にしみる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROM NIGHT IV：DELIVER US FROM EVIL", "プロムナイト４", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1957年、地獄の神父“ジョナス”は、高僧の法力によって聖堂に幽閉された。生きながら悪霊と化したジョナスの怨霊は、33年後のプロムナイトの夜によみがえる。絶叫する半裸の美女、切り裂かれる肉体など、相変わらずのスプラッタ・シーンが次々と登場する。", "1991-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOU ARE NOT I", "ユー・アー・ノット・アイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "ジャームッシュ作品のプロデュースで知られる才媛、ドライヴァーの監督作。精神病院を脱走した女が、姉の家に戻り、精神力で姉と入れ替わろうとする。精神病患者の妄想世界を、淡々としたモノクロームの映像で描き上げる。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PSYCHIC", "サイキック・ウォー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "生まれつき予知能力があり、その能力で恋人が変質者に暴行殺害されるシーンを見てしまう……。クローネンバーグ監督作「デッドゾーン」にも通じる、絶望的な未来予知を描いたサイキック・サスペンス。主人公が、突然襲ってくる予知の中で見る女性たちの殺害シーンが見もの。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("２４ HEURES DE LA VIE D’UNE FEMME", "ブラウン夫人のひめごと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "南フランスへ避暑に訪れた元外交官のルイ。彼は少年時代にブラウン夫人から聞かされたポーランド人将校と彼女の悲恋物語を、偶然知り合った若い女性オリビアに話して聞かせるが……。'06年に「ジュテーム・シネマ・フェスティバル」と冠した映画祭で上映された。DVDタイトルは「秘密は誰かに話すもの」。", "2006-07-15", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("She's Gotta Have It", "シーズ・ガッタ・ハヴ・イット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "　束縛を嫌い、自由を望んでいる黒人女性ノーラには3人の恋人がいた。エゴイストだが、彼女のことを理解してくれているジェイミー、失業中ながら陽気なマーズ、常にエクササイズに精を出しているモデルのグリアー。さらに、ノーラをレズビアンの道に誘おうとしているオパルも絡み、彼らの間にはトラブルが生じていた。ノーラは感謝祭のディナーに3人の男たちを招待するが……。ヒロインと男たちの関係を、独特のユーモアを織り交ぜて描いたラブ・コメディ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONA　LISA　SMILE", "モナリザ・スマイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "“キャリアよりも結婚が大事”。そんな保守的な考え方に縛られた女生徒たちに、自立心を教える大学教師をJ・ロバーツがさわやかに演じる感動作。K・ダンスト、M・ギレンホールらが、それぞれに個性的な女生徒を熱演。1950年代のクラシカルな雰囲気の中、自分の生き方を模索する女性たちの姿を活写していく。1953年、ウェルズリー大学の美術史の助教授に就任したキャサリンは、保守的な校風にがく然とする。キャサリンは、エリートと結婚することが何よりも価値があると考える女生徒たちに、自由な精神を教えようとするが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・警察日記", "続・警察日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "田舎の警察署に勤務する好人物たちが、右往左往するさまをスケッチ風に描いたもの。ヒット作の続編だが、ストーリーは独立している。森繁、東野、杉村などの人気者が抜けた穴を、前作では脇だった伊藤の格上げをはじめ、大坂、安部、林、北林ら通好みのキャスティングで埋めているあたりが憎い。風速50メートルの台風が近づき、磐梯山のふもとの牛方町は大騒ぎ。結婚を明日に控えた娘の鉄道自殺未遂に始まって、ストリッパー上がりの女の無責任な予言騒動、バス会社の女車掌身体検査をめぐる悶着、公金横領犯の出没、もぐり産婆の逆襲など、気の良い警察官たちを悩ます数々の難事件（？）が展開する。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ねむの木の詩がきこえる", "ねむの木の詩がきこえる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "肢体不自由児の施設「ねむの木学園」の運営にあたる、宮城まり子自身の闘いと喜びの日々を記録したドキュメンタリー風の作品。全編を通して彼女の歌とナレーションで語りかけ、製作・監督・脚本・音楽・主演と一人五役をこなし切っている。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Old School", "アダルト♂スクール", "", "", "http://ecx.images-amazon.com/images/I/51xSv9Q0qrL._SL210_.jpg", 92, "ルーク・ウィルソン、ウィル・フェレル、ビンス・ボーン共演のコメディ。恋人に浮気されて別れた青年ミッチは、大学の近くに建つ一軒家に引越すことに。そんなミッチの新居で、親友のフランクやビーニーが学生たちも集めて盛大なパーティを開くが、過去の因縁を持つ学部長に目をつけられ、家を大学の施設として使用するよう言い渡されてしまう。そこで彼らは、社交クラブ「アダルト・スクール」を開校して立ちむかうが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUNMEN", "ガンメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "南米を舞台にしたアクション活劇。麻薬組織から４億ドルを奪った男が殺された。その兄である脱獄囚ダニーは賞金稼ぎのコールと組んで、いがみ合いながらもともに組織と戦い、大金のありかを探し出していく。凸凹コンビに扮したC・ランバートとM・V・ピーブルズのタフガイ共演が見もの。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Anne of Green Gables:The Sequel", "赤毛のアン／完全版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 195, "　もとはテレビシリーズだったが日本では劇場公開され、その際カットしたシーン約50分を追加し完全版として再公開された。カナダのテレビ界で活躍していたミーガン・フォローズが孤児のアンを生き生きと演じている。親友のダイアナ役のシュイラー・グラントは、名優キャサリン・ヘップバーンの姪にあたる。カナダ版エミー賞にあたるジェミニ賞、ヒューストン国際映画祭グランプリなど世界中で多くの賞を受賞した。", "1986-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Time", "絶対の愛", "", "", "http://image.eiga.k-img.com/images/movie/33878/315x210.jpg?1243", 98, "韓国の鬼才、キム・ギドク監督が放つ衝撃のラブ・ストーリー。セヒとジウは付き合い始めて2年になる恋人同士。2人は強く愛し合っていたが、セヒはジウが自分に飽きてしまうのではないかとの不安にさいなまれていた。ある日、整形を決意したセヒは、ジウの前から突然姿を消す。それから半年後、セヒの失踪に傷ついたジウは、スェヒという美女と出会い恋に落ちるが……。愛のために整形するヒロインを「スカーレットレター」のソン・ヒョナが熱演。", "2007-03-10", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽の王子 ホルスの大冒険", "太陽の王子 ホルスの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "「風の谷のナウシカ」の宮崎駿、「火垂るの墓」の高畑勲をはじめとして、現在のアニメ界を支える最良のスタッフが、“人間の団結”をテーマに作り上げた名作。深沢一夫の戯曲「チキサニの太陽」をもとに、北欧的なイメージで舞台が設定され、人間の持つ善悪両面にまで踏み込んだ描き方がなされている。それを象徴したヒロインのヒルダに、ファンの人気が集まった。悪魔グルンワルドの脅威が広がる北の世界。父を失った少年ホルスはグルンワルドの誘いを断わり、川のほとりの村にたどり着く。しかしグルンワルドの妹ヒルダは、言葉巧みに村人を操って彼を追放させ、崖から突き落とす。それでもヒルダの葛藤を見抜いたホルスは、太陽の剣を鍛え直し、村人を率いて、とうとうグルンワルドを打ち倒すのだった。製作状況の様々な問題から、多少息つぎの余裕がない箇所があるが、それでも才人たちの能力は十分に発揮され、見ごたえのある仕上がりになった。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新碧血劍／THE　SWORD　STAINED　WITH　ROYAL　BLOOD", "ユン・ピョウIN妖刀秘伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "明朝の頃の中国を舞台にした戦国アクション。殺されたヤン大臣の息子チェンは、ウーマー将軍からルビーの剣を授けられる。彼は、知らずに高価な白蛇を食べたため、五毒教団から生き血を狙われるハメになってしまったが……。Y・ピョウがキレのいいアクションを全編で披露。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOYS", "君がいない夜に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ピーターは、自分と別れて職場の上司と新しい生活を始めたタニヤのことが忘れられず、空虚な日々を送っていた。友人トムが、気晴らしにディスコや海辺のナンパに誘い出すが、どうにも心の切替えができない。一方のタニヤも、新しい恋人とうまくいかず、本当に愛していたのはピーターだったと気付く。しかし、二人を元のさやに収めようとするトムの取り計らいが仇となって、すれ違いばかり。ままならぬ恋が甘くせつない青春グラフィティ。", "1991-01-01", "70,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星空のむこうの国", "星空のむこうの国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "NHK“少年ドラマ”シリーズを映画に、というコンセプトで作られた愛らしい小品佳作。難病の少女との約束を果たすため、パラレル・ワールドに迷い込んだ少年の冒険物語で、監督の小中和哉は当時22歳、若者の心をつかみ、小劇場ながらヒットを記録。特撮も上々。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Twelfth Night", "十二夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "シェイクスピアの恋愛喜劇を、“ロイヤル・シェイクスピア・カンパニー”の舞台監督が映画化。男女の恋のさや当てが、豪華キャストで軽やかに、ユーモラスに繰り広げられる。1890年イギリス。船の転覆で双子の兄と生き別れたヴァイオラが、男装して侯爵家に少姓として潜り込む。彼女は侯爵に心惹かれていくが、侯爵は令嬢オリヴィアに恋していた。ところが、オリヴィアがヴァイオラに一目惚れ、３人は奇妙な三角関係に発展していく……。監督夫人でもあるI・スタッブスが男装の麗人に、美貌の令嬢に久々のコスチューム劇となるH・B・カーターが扮して複雑な女心を巧みに表現。N・ホーソーン以下、ベテラン俳優らのコミカルな演技も見もの。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOU CAN’T RUN AWAY FROM IT", "夜の乗合自動車 ナイト・バス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "S・H・アダムスの短編小説『ナイト・バス』をもとにしたミュージカル・ラブ・ロマンス。結婚取り消しといきり立つ父親に、強引に結婚生活を引き裂かれた娘は、父のもとから姿を消し、大陸横断のバスに乗り込む。その隣に座ったのが新聞記者のピーター。かくして二人の逃避行生活が始まる……。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MACBETH", "マクベス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "『マクベス』３度目の映画化。ポランスキーが妻シャロン・テートを惨殺されたあとの初作品。『マクベス』を従来のイメージより若く設定し、現代アメリカの若者像とだぶるイメージで描いている。『マクベス』初のカラー作品だが、その重厚な色彩がオカルトチックな効果を上げている。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GAMBLER", "熱い賭け ザ・ギャンブラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ギャンブルに生き甲斐を求め、孤独な戦いに身をおくことにすべてを賭ける大学教授が主人公。彼は組織の思惑通り負けが込み始じめ、ますますギャンブルの深みにはまっていき、ついに最後の賭けに挑む……。J・カーンの好演が光るギャンブル映画の意欲作。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD　ALLEY", "中共脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "“ミスター・ウエスタン”ことJ・ウェインが西部劇とは違う、手に汗握るアクションを繰り広げる冒険活劇。クールな美人スター女優、L・バコールとの共演で贈る。中国から香港へ脱出を図る人々のために、主人公の船長は一大決心をして船に乗り込んだ。凄まじい嵐の中での航海が始まるがさらに、敵の駆逐艦に追われるはめに。スリリングな追跡・迎撃戦で魅せる。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHISPERING SMITH HITS LONDON", "ささやきスミス倫敦〈ロンドン〉へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "アメリカ人の売れっ子探偵スミスは、バカンスを楽しむためにイギリスへとやって来る。彼はホテルでアンという女性から手紙を受けとった。ある娘の死に関する調査依頼だった。表向きは自殺というその死には、殺人の匂いがし、しかも犯人は父親らしい。スミスは、関係者に次々と会うが、謎は深まるばかりであった……。", "1952-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("異度空間／INNER　SENSES", "カルマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「ダブルタップ」の監督、ロー・チーリョンとレスリー・チャンが再び組んだ香港製サイコ・ホラー。心を病む主人公を熱演するレスリーや意外な物語の展開から目が離せない。超常現象否定派の精神科医、ジムは霊に悩まされる小説家志望の若い女性、ヤンを診察。カウンセリングを重ねた結果、彼は彼女を快方へ導いていた。ところが、それと同時に今度はジム自身が奇怪な現象に遭遇し始める。2003年に急逝したレスリーの遺作。", "2002-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BOUM ２", "ラ・ブーム２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "前作から２年、より魅力的に女っぽく成長した15歳のビックが、旅先で出会った青年フィリップと偶然にパリで再会する。そして、大人の恋の第１歩を踏み出すことに……。彼女の良き相談相手の祖母プぺットをはじめ、前作と同じキャストで描いた、さわやかな青春映画。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANIMALS", "夢の旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "N.Y.のタクシー・ドライバー、ヘンリーの車に謎の老人３人が無賃乗車してきて、そのままあてのない旅が始まってしまった。しかし、その旅の過程で、彼は見失っていた人生の大切なものを探し当てていく……。若きフィルムメーカーが、長編映画監督デビューながらベテラン名優たちを用いて描くヒューマン・ファンタジー。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREENWICH　MEAN　TIME", "g：mt", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ロンドンのグリニッジを舞台にした辛口の青春映画。高校を卒業した友人同士の若者たちが音楽業界での成功を目指して奔走。明るい未来しか見えていなかった彼らに、ある日、仲間の交通事故という悲劇が襲いかかる。それを機に歯車が狂い始め、ケンカしてグループを抜けた者はドラッグに溺れるなど、彼らはそれぞれの壁に直面する……。“g：mt”とはグリニッジ・ミーン・タイム（世界標準時）の略で、“グリニッジの厳しい現実”と掛けた言葉。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渡世一代", "渡世一代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "舞台は革新政治結社が暗躍した大正中期の浅草。親分に絶対の敬意を表す代貸しの伊蔵と、以前カタギの娘に手をつけて組を破門になったものの、殴り込みに備えて機関銃持参で舞い戻ってきた銀次との確執を描いたアクション作品。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MERCENARIES（DARK OF THE SUN）", "戦争プロフェッショナル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1960年、中央アフリカのベルギー領コンゴが舞台。政府軍と叛乱軍の戦いを背景に、政府軍に雇われた戦争プロ５人の住民救出作戦を描く戦争アクション。W・A・スミスの小説『カタンガから来た列車』の映画化。R・テイラーの代表作。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　COLLECTIONNEUSE", "コレクションする女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「モンソーのパン屋の女の子」「シュザンヌの生き方」に続くE・ロメール監督の“六つの教訓話”シリーズ第３作。世間知らずの女の子に振り回される男の理性とホンネを皮肉たっぷりに綴っている。自信家のアドリアンがひと夏のバカンスを過ごすため、プロヴァンス地方の別荘にやってくる。そこで彼は美術家の友人と合流し、無為で平穏な日々を過ごす腹づもりだったが、アイデという娘に心をかき乱されて……。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LONG HOT SUMMER", "長く熱い夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ミシシッピー州の小さな村に、放火の容疑者として他の村を追い出された若者ベンが流れて来た。農場主のバーナーはベンを雇い、やがて彼を気に入って自分の跡取りにしようかと考える。それを知った知能の低い長男は父に怒り、納屋に閉じ込めて火をつけるが……。フォークナーの小説『村』の映画化。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A L'interieur", "屋敷女", "", "", "http://image.eiga.k-img.com/images/movie/53578/315x210.jpg?2597", 83, "クリスマス・イブの夜、出産目前の妊婦サラの家を不審な女が訪れる。女が窓から押し入ろうとしたためサラが警察を呼ぶと、女は姿を消す。ひと安心して床に就くサラだったが、なんと女はすでに家の中に侵入しており……。「ベティ・ブルー／愛と激情の日々」の個性派女優ベアトリス・ダルが女殺人鬼を怪演。初監督作となる本作で高く評価されたアレクサンドル・バスティロ＆ジュリアン・モーリーはハリウッド・デビューも決定している。", "2008-06-21", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TECHNO FEAR", "テクノ・フィアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "GCI社は原子力によって意志を持つ新型サイボーグ・タイラーの開発に成功。一方、ウクライナ研究所では旧型サイボーグ・ミケーレが突然暴走、所員を殺りくし始めていた。開発主任のナタリーは、タイラーらとともに鎮圧に向かうが、ミケーレは何者かの陰謀で軍用サイボーグとして強化され、破壊の限りを尽くしていた。それを知ったナタリーたちは、タイラーをレベル・アップして再び戦いを挑む。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京タワー", "東京タワー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "田舎の高校を卒業し、それぞれの夢を抱いて東京で共同生活を始めた信也、春奈、杏の３人。３人は楽しい毎日を過ごしながらも、理想と現実のギャップに戸惑いを感じ始める……。ロックと映画の融合をテーマに撮り続けるナリオ監督がロックの突き抜ける爽快感をできるだけ排除して３人が模索する姿を綴り、ロックと映画の可能性を引き出そうとしている。", "2002-06-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海潮音", "海潮音", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "日本海に面する旧家に暮らす父親と娘。母親は娘の幼時に他界し、今では祖母が母代わりになっている。娘には友人もなく、話相手といえば叔父だけ。ある日父親が波打ち際に倒れた記憶喪失の女を連れて来る。女には忘れてしまいたい痛切な過去があるらしい。閉鎖的なこの空間に突如出現したこの謎の女が登場人物たちの心に微妙な影響を与え、そしてどことも知れず去っていく。橋浦監督は日本海の荒涼とした風景の中に生きる人間たちの空虚な姿を、繊細なタッチで描いて見せた。当時15歳だった荻野目慶子が孤独や不安におののく娘を好演。特に女を強姦しようとする父親に猟銃を向けるシーンは衝撃的。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スキージャンプ・ペア Road to TORINO 2006", "スキージャンプ・ペア Road to TORINO 2006", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "1組のスキー板を2人同時に装着してジャンプする奇想天外な架空の競技「スキージャンプ・ペア」を描き、DVDのシリーズ売上が40万枚を突破するなど異例のヒットとなったCG作品を映画化。競技の誕生から“トリノ・オリンピッグ”決勝までの長く険しい道のりを追い、スキー・ジャンプに人生のすべてを捧げた男たちの挑戦と苦悩の日々を、実写とCGを織りまぜたフェイク・ドキュメンタリー形式で描いていく。", "2006-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎かもめ歌", "男はつらいよ・寅次郎かもめ歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "北海道・江差で仲間の墓参りをした寅さんはその娘・すみれと出会い、東京での勉学の世話をすることになった。定時制高校に通うすみれのいきいきとした姿を見た寅は自分も入学願書を出したが……。元キャンディーズのランちゃんこと伊藤蘭がマドンナ役で登場。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beat ビート", "Beat ビート", "", "", "http://image.eiga.k-img.com/images/movie/56608/315x210.jpg?3907", 119, "警察小説の名手・今野敏の原作小説を、奥田瑛二の監督・主演で映像化。警視庁捜査二課の警部補・島崎は日和銀行の粉飾決算疑惑の調査にあたっていたが、長男の丈太郎が日和銀行員で柔道部のOBでもある富岡に内部情報を漏らしてしまう。続けて富岡が殺害され、その容疑者として次男の真次が捜査線上に浮上。真次は反抗的だが殺人まで犯すとは思えない島崎は、真次の動向を調査し始めるが……。2011年2月にWOWOWのオリジナルドラマ「ドラマW」枠で放送され、同年6月に劇場公開。", "2011-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("笛吹若武者", "笛吹若武者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "平安朝の末期。平家一門が源氏の軍勢に追われるなか、薄幸の姫・玉織は平敦盛とのはかない恋に落ちていく……。源平の大合戦を背景に描き上げた抒情的な悲恋ドラマ。大川橋蔵が笛の名手として知られた平敦盛に扮し、美空ひばりを相手に華麗な映画デビューを飾った一編。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Once", "ONCE ダブリンの街角で", "", "", "http://image.eiga.k-img.com/images/movie/34218/315x210.jpg?3530", 87, "ダブリンのストリートで自作の歌を奏でる男の前に、ひとりの女が現れる。彼女のピアノにほれ込んだ男は、彼女のために曲を書き、2人のセッションは美しいハーモニーを奏でる。07年サンダンス映画祭で注目を集め、上映館が全米2館から口コミで140館まで拡大したドラマ。主演はアイルランドのロックバンド、ザ・フレイムスのグレン・ハンサードと、彼のアルバムで共作しているチェコ出身のミュージシャン、マルケタ・イルグロバ。", "2007-11-03", "33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HE SAID，SHE SAID", "ヒー・セッド，シー・セッド 彼の言い分、彼女の言い分", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "人気TV番組のキャスター、ダンとローリー。番組途中いきなりローリーがダンの顔めがけてカップを投げつけた。控え室に戻ったダンは、二人の出会いから恋人となった今日までを回想する……。映画の前半はダンの言い分、後半はローリーの言い分を、双方の視点から、まったく同じシーンで展開させていくユニークな構成。二人の思いがいかに食い違っているか。例えば初デートの記憶は、ダンから見ればときめくデートでも、ローリーにはとってはダンが強引な男だったとしか残っていないのだ。主演二人の魅力も加わって、心温まるラストまで、さわやかにまとまったロマンティック・コメディの秀作。男性と女性が共同で監督を務めた効果もしっかり表われている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Red Riding Hood", "赤ずきん", "", "", "http://image.eiga.k-img.com/images/movie/56038/315x210.jpg?3854", 100, "「トワイライト　初恋」のキャサリン・ハードウィック監督が、グリム童話の「赤ずきん」の“その後”を描く。美しい大人の女性に成長した赤ずきん、バレリーは、幼なじみピーターと、親が決めた婚約者ヘンリーという2人の男の間で揺れていた。親の反対を押し切りピーターと駆け落ちすることを決めた矢先、バレリーの姉が何者かに殺される事件が発生。魔物ハンターのソロモン神父は、満月の夜にオオカミに変身する人狼の仕業だというが……。主演は「マンマ・ミーア！」のアマンダ・セイフライド。", "2011-06-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画版　ふたりエッチ　セカンド・キッス", "映画版　ふたりエッチ　セカンド・キッス", "", "", "http://image.eiga.k-img.com/images/movie/57468/315x210.jpg?2558", 71, "克・亜樹の長寿ラブコメ漫画を、人気グラビアアイドルの森下悠里主演で実写映画化した「映画版　ふたりエッチ」の続編。互いに異性経験のないまま結婚した優良と真が、「本当の夫婦」を目指してステップアップしていく姿を描いた前作から半年後が舞台。真は毎日のように優良を求めていたが、マンネリ化してしまい優良は感じることができなくなってしまう。優良の気持ちを考えた真は交渉をもたない3週間を設けるが……。新たに明日花キララ、谷桃子らが参加。", "2011-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENEMY", "デッドロック ベトナム死の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "CIAの捜査官が、北ベトナムの将軍を暗殺する命を受けて、ニュース映画カメラマンを装い、戦場に派遣される。同じく、中国人の工作員も、将軍の命を狙い派遣されてくる。だが、CIAは、暗殺の事実のもみ消しを企て、捜査官を抹殺しようとする。二人は、狂気が渦巻く戦場で逃避行を開始する。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遥かなる山の呼び声", "遥かなる山の呼び声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "北海道の酪農の町・中標津の美しい四季を背景に、誤って人を殺して逃亡中の男と、牧場を経営する未亡人、そして小学生になるその子供との交流を描いた人情ドラマ。慎重に進む二人の恋の行方を、農作業と日常生活をたっぷり盛り込みながら描いている。西部劇の名作「シェーン」へのオマージュシーンも。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES HORIZONS GAGNES", "新・天と地の間に　星にのばされたザイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "「天と地の間に」の続編となる山岳ドキュメンタリーの会心作。絶壁の崖に挑む命知らずのクライマーたちの姿が、近接撮影によってビビッドに映し出されていく。その緊張感、臨場感は特筆ものの仕上がりとなっている。登山家のレビュファが様々な国の人たちと共に、世界の7大山脈に登頂目指してアタックする。DVDタイトルは「星にのばされたザイル」。", "1975-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Highlander: The Source", "ハイランダー　ネクスト", "", "", "http://image.eiga.k-img.com/images/movie/53878/315x210.jpg?3022", 86, "首を斬られない限り死ぬことはない不老不死の民“ハイランダー”のマクラウドは、かつて愛した女性アンナが、ハイランダーの起源といわれる“奇跡の源”と呼ばれる謎の存在の鍵を握っていることを知る。マクラウドはアンナや仲間たちと共に奇跡の源を求める旅に出るが、彼らの前に奇跡の源を守る最強のガーディアンが立ち塞がる。ラッセル・マルケイ監督の「ハイランダー／悪魔の騎士」（86）に始まるSFアクションのシリーズ第5作。", "2007-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Double Zero", "ダブルオー・ゼロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「TAXi」のジェラール・ピレス監督が手がけたスパイアクション・コメディ。フランスで核ミサイルが盗まれるという大事件が発生。内部に二重スパイがいると考えた当局は、どう見てもシークレットエージェントに見えないという理由から、民間人のベンとウィルをおとりとして派遣することに。2人は厳しい訓練の末、事件の鍵を握る美女を追ってモナコへと飛ぶが……。主演はフランスのテレビ界で人気のコンビ、エリック・ジュドールとラムジー・ベディア。", "2007-03-24", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GORILLAS IN THE MIST", "愛は霧のかなたに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "S・ウィーヴァーの主演で、マウンテン・ゴリラの保護救済に力を注いだ女性学者ダイアン・フォッシーの半生を描いていく。ダイアンは絶滅の危機に瀕しているマウンテン・ゴリラ保護のため、棲息地・中央アフリカへ単身で赴く。彼女は様々な困難を乗り越え、ゴリラとのふれあいに成功、研究所を開設するまでに至った。しかし、密猟者との対立により、予期せぬ事態に巻き込まれてしまう……。監督は「Oh！ベルーシ絶体絶命」でも自然破壊の危機を取り扱ったM・アプテッド。特殊メイクアップのリック・ベイカーがアソシエイト・プロデューサーを務めている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女賭博師丁半旅", "女賭博師丁半旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "大滝銀子はツボ振りの名人である父が殺されたことを知る。父の無念を晴らすために、父の一番弟子・鉄五郎の指導で、日本一のツボ振りになるべく血のにじむ修行を積み、ついに名人戦に出場する。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Coming Out Story", "Coming Out Story", "", "", "http://image.eiga.k-img.com/images/movie/57378/315x210.jpg?4833", 60, "トランスジェンダーであることに葛藤しながらも、自分の居場所を探し続ける人々の姿を追ったドキュメンタリー。京都の公立高校で数学教師をする土肥いつきさんは「女性の身体を獲得すること」を願いつづけ、性別適合手術を受ける。笑顔をたやさない土肥さんの周りにはいつも生徒たちが集まり、なかには同じようにトランスジェンダーに悩む高校生も相談にくる。土肥さんと接した人々はそれぞれ「自分とは何か？」という問いを深めていく。監督の梅沢圭が日本映画学校の卒業製作作品として手がけ、同校の今村昌平賞を受賞。卒業後にさらに再編集を加えて完成させ、第20回東京国際レズビアン＆ゲイ映画祭でも上映された。", "2012-01-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新幹線大爆破", "新幹線大爆破", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 152, "新幹線に爆弾を仕掛けた犯人と警察、国鉄の息づまる駆け引きをテンポ良く見せる一流のサスペンス映画。フランスでも劇場公開され、大ヒットした。走行中のひかり109号に爆弾がセットされており、その爆弾は時速80キロ以下になると自動的に爆発する。そんな脅迫電話が国鉄に入った。犯人は500万ドルを要求。警察は取引に応じるが失敗、犯人の一人を死亡させてしまう。止まることのできないひかり109号は、何も知らずに終点・博多に向かって疾走していた……。カット・バックにより犯人、警察、国鉄、車内のそれぞれを見せる演出とトリッキーな魅力にあふれるシナリオがうまくマッチした最高の娯楽作。英語・仏語音声を収録したDVD「海外版」もリリースされた。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PURPLE RAIN", "プリンス　パープル・レイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "黒人はもとより、白人の中にも熱狂的なファンを多く持つロック・シンガーのプリンス。その彼の自伝的作品といえるもので、一人の黒人青年の生きざまを描いた青春映画に仕上がっている。同名のサントラ盤とともに、アメリカでは大ヒットを記録。本作品の続編にあたる「グラフィティ・ブリッジ」（日本未公開）も製作された。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("先生騙鬼", "Missゴースト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "生まれ変わることを望み、呪われた寺、関若寺へ自殺するために来た書生チャイ。彼はそこで幽霊退治をもくろむ剣士チャンと出会い、寺を出るように忠告される。だが、まったく耳を貸さずにいたチャイは、やがて美しい女幽霊の虜となり……。矢継ぎ早な展開で見せる、冒険ファンタジー。", "1987-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏服のイヴ", "夏服のイヴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ニュージーランドへ移住することになった３人の子持ちでやもめの貿易商に同行を求められ、困惑する住み込み家庭教師の牧子。彼女にはルポライターの恋人がいたが……。松田聖子が二人の男に愛される役をあっけらかんと演じたラブ・ロマンス。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Never Let Me Go", "わたしを離さないで", "", "", "http://image.eiga.k-img.com/images/movie/55678/315x210.jpg?3960", 105, "キャリー・マリガン、キーラ・ナイトレイ、アンドリュー・ガーフィールドが共演するラブストーリー。原作は「日の名残り」で英文学界最高峰のブッカー賞を受賞した、日本生まれのイギリス人作家カズオ・イシグロの小説。「ストーカー」（2002）の新鋭マーク・ロマネク監督がメガホンをとる。外界から隔絶された田園地帯に佇む寄宿学校ヘイルシャムで学ぶキャシー、ルース、トミーは、絵や詩の創作活動に励みながら、“特別な子ども”として育てられた。18歳になってヘールシャムを出た3人は、農場のコテージで共同生活を始めるが、ルースとトミーが恋人同士になったことで友情が崩壊していく。", "2011-03-26", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("12", "12人の怒れる男", "", "", "http://image.eiga.k-img.com/images/movie/53748/315x210.jpg?2841", 160, "シドニー・ルメット監督の名作「十二人の怒れる男」（57）を、「機械じかけのピアノのための未完成の戯曲」「太陽に灼かれて」「シベリアの理髪師」などで知られるロシア映画界の巨匠ニキータ・ミハルコフがリメイク。ロシア軍将校だった養父を殺害したチェチェン人少年の裁判を通して、現代ロシア社会が抱える問題や多民族国家ならではの偏見を浮き彫りにしていく。", "2008-08-23", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美しい夏キリシマ", "美しい夏キリシマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "戦時中に思春期を過ごした黒木和雄監督が、自身の記憶をたどり、故郷の宮崎県・霧島を舞台に美しい自然に囲まれた片田舎の人々の機微を描き出した渾身の一作。あからさまな戦争風景を描かず、爆撃で親友を失ったことにより心の病を患った少年の苦悩や、戦争の背景で苦しむ人々の姿を映し出すことによって、その悲惨さを浮き彫りにしている。戦争３部作の第２弾。終戦間近の1945年、夏。満州で暮らす家族と別れ、厳格な祖父母と暮らしている中３の庚夫は、目の前で爆死した親友を助けられなかった罪悪感と空襲のショックで、肺浸潤という病にかかり、自分の居場所も見失っていた……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLI STERMINATORI DELL’ANNO 3000", "マッドライダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "核戦争によるオゾン層の破壊で、極度の水不足に陥った地球を舞台にしたSFアクション。貴重な水源の独占をたくらむ凶悪メカ軍団に、孤児のトミーと一匹狼の流れ者エイリアンが力を合わせて立ち向かう。地球に緑がよみがえる日は来るのだろうか……。", "1983-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("台所太平記", "台所太平記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "谷崎潤一郎の同名の小説を原作に「雁」「雪国」「夫婦善哉」などの文芸ものを得意とする名匠・豊田四郎が演出した佳編。老小説家夫婦の家に雇われた戦前から1960年代初頭にかけての９人の女中のそれぞれの個性をユーモアたっぷりに描く。女中に扮する女優たちの色合いの変化が見もの。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たぬき坊主", "たぬき坊主", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "通称“たぬき部落”と呼ばれる田舎の小さな村があった。村の有力者や農協組合長らに請願されて、寺の住職になるために北海道からたぬき部落にやって来た甲丸和尚の奔放な活躍ぶりを描いた人情喜劇。得意の話術と心臓の強さで、ことごとく問題を解決してしまう和尚をフランキー堺が力演。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Magick Lantern Cycle", "マジック・ランタン・サイクル", "", "", "http://image.eiga.k-img.com/images/movie/57448/315x210.jpg?3888", 0, "1940年代から映像製作を続け、デレク・ジャーマンやジャン・コクトー、デビッド・リンチらにも影響を与えたアンダーグラウンド映画の伝説的映像作家ケネス・アンガーの短編作品をまとめたプログラム。数十年にわたり改訂が続けられ、1980年に完成した。サイケデリックな映像で神話や歴史の人物たちを描く「快楽殿の創造」（53）をはじめ、「ルシファー・ライジング」（80）、「我が悪魔の兄弟の呪文」（69）、「人造の水」（53）、「スコピオ・ライジング」（63）、「K.K.K. Kustom Kar Kommandos」（65）、「プース・モーメント」（49）、「ラビッツ・ムーン」（50）、「花火」（47）の9つの作品で構成される。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hills Have Eyes 2", "ヒルズ・ハブ・アイズ2", "", "", "http://image.eiga.k-img.com/images/movie/34248/315x210.jpg?1614", 90, "ウェス・クレイブン監督によるホラー「サランドラ」をリメイクした衝撃作「ヒルズ・ハブ・アイズ」の続編。ウェス・クレイブンが息子ジョナサンと共に脚本を手掛け、「サランドラ2」とは異なるオリジナル・ストーリーが展開される。かつて惨劇が繰り広げられた砂漠の核実験場で、今度は米軍兵士が食人集団の餌食になる。しかし、米軍には“死亡した兵士の遺体は持ち帰らなければならない”という規則があり……。", "2007-10-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEET SMELL OF SUCCESS", "成功の甘き香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "芸能界に絶大な影響力を持つ新聞コラムニスト、ハッセンカーは、やり手のプレス・エージェント、ファルコを使って、妹とその恋人の歌手との仲を裂こうとする。芸能ジャーナリズムの冷酷な裏面をきわめてドライに描いた異色作。ジャジーな音楽の効果が際立つ。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朱と緑（朱の巻・緑の巻）", "朱と緑（朱の巻・緑の巻）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "会社重役の娘・千晶は深夜強盗に襲われ、恋人・戸山のいる大阪へと逃がれる。ところが彼の周りでは様々な女たちの欲望が渦巻いていた。千晶もまたその渦に巻き込まれていくが……。「朱の巻」「緑の巻」の２部からなる大作。1956年、中村登監督によってリメイクされた。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大事件", "ブレイキング・ニュース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "切れ者の銀行強盗一味と、その逮捕を生中継して威信の回復を図る香港警察の攻防を描いたサスペンス。追跡に加え、人質交渉の駆け引き、小型カメラやネットを駆使した報道合戦など、さまざまなエピソードが凝縮されスリルを引き立てる。「PTU」の鬼才ジョニー・トーの骨太な演出は絶品だ。銀行強盗一味を追う警官の失態が報道され、香港警察は非難の的に。警察上層部は逮捕の瞬間を生中継することで名誉挽回を図るが……。", "2005-12-03", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("華の乱", "華の乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "大正時代、愛に芸術に命を燃やした男たち、女たちを豪華キャストで描く。晶子は何もかも捨てて、歌の師・与謝野鉄寛と結婚した。しかし年が過ぎ彼女が11人の子持ちになった頃、鉄寛はすっかり無気力になっていた。そんなある日、晶子は作家・有島武郎に出会い、ひかれていく。他にもアナキスト・大杉栄、女性社会運動家・伊藤野枝、近代演劇の巨星・島村抱月、スター女優・松井須磨子など歴史を彩った人物が次々登場。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MELO", "メロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "音楽院時代の親友ピエールとマルセルが再会する。いつしかピエールの妻はマルセルと愛し合うようになり、やがて自殺する。3年後、彼女を愛する2人の男は深い愛の憂いの中で対峙する……。常に文学者のオリジナル脚本で映画を作っていたレネ監督が長編第11作にして選んだ原作がメロドラマだったことが世間を驚かせた美しい作品。", "1988-01-23", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真救世主伝説　北斗の拳　ラオウ伝　殉愛の章", "真救世主伝説　北斗の拳　ラオウ伝　殉愛の章", "", "", "http://ecx.images-amazon.com/images/I/3138M5PR1BL._SL210_.jpg", 95, "1980年代に一大ブームを巻きおこしたアクション漫画「北斗の拳」を、「真救世主伝説」シリーズとして新たにアニメ映画化した5部作の第1作。核戦争により荒廃した近未来の世界。究極の暗殺拳・北斗神拳の伝承者リュウケンから後継者に選ばれたケンシロウは、民衆の救世主としての道を歩みだす。一方、リュウケンと決別したラオウは、自らを「拳王」と称して覇道を突き進んでいく。声優陣には阿部寛、柴咲コウら豪華キャストを起用。", "2006-03-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("横浜暗黒街・マシンガンの竜", "横浜暗黒街・マシンガンの竜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ラオール・ウォルシュ監督の傑作「白熱」を下敷きにした本格的ギャング映画。かつての日本の母・三益愛子が、菅原文太の母に扮してマシンガン片手に怪演。また、往年の大スター・中野英治がギャングのボス役で出演している。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Legally Blonde", "キューティ・ブロンド", "", "", "http://image.eiga.k-img.com/images/movie/1348/315x210.jpg?1709", 96, "おしゃれで成績優秀のブロンド美人エルは、政治家を目指す恋人に「ブロンドは官僚夫人に不向き」と振られ、彼を追って難関ハーバード・ロー・スクールに進学。周囲のブロンドへの偏見の中、弁護士をめざして奮闘する。監督ロバート・ルケティックはオーストラリア出身、本作でハリウッドデビューし、ミラマックスと3本の契約を結んだ注目株。撮影はニコラス・ローグ監督とのコンビで知られるアンソニー・B・リッチモンド。", "2002-04-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RUNNING MAN", "バトルランナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "TVが唯一の娯楽として許されている近未来社会を舞台に、罠にはまった元警官が、武器を持った敵から逃げ切ればリゾート地で一生暮らせるというサバイバル番組“ランニングマン”に出場させられるというSFアクション映画。チェーンソーや放電式の戦闘服、さらにはナパーム砲などを持った刺客たちにその強靭な肉体だけで立ち向かっていくA・シュワルツェネッガーの激闘ぶりが見どころ。原作のR・バックマンは、スティーヴン・キングの別名である。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DAY THE EARTH STOOD STILL", "地球の静止する日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "SFもののクラシックとして評価の高い作品。ワシントン上空に突如現れた銀色の円盤が地上に着陸した。戦争と混乱の地球へ、別の天体から平和勧告に訪れた彼らと地球人とのいさかいや友情をオーソドックスな演出で描いている。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Whip It", "ローラーガールズ・ダイアリー", "", "", "http://image.eiga.k-img.com/images/movie/54938/315x210.jpg?4759", 112, "ドリュー・バリモアが初監督を務めた青春ドラマ。17歳の女子高生ブリスは、美人コンテストで優勝することが人生の成功と信じる母親や、田舎町の退屈な生活にうんざりしていた。そんなある日、隣町の大都市オースティンに出かけた彼女は、女たちが繰り広げるパワフルなローラーゲームに魅了され、その世界に生きがいを見出す。主演に「JUNO／ジュノ」のエレン・ペイジ。共演にマーシャ・ゲイ・ハーデン、ジュリエット・ルイスら。", "2010-05-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOOD MORNING，VIETNAM", "グッドモーニング、ベトナム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1965年のサイゴン。将兵の士気高揚のために米軍放送の人気DJクロンナウアー一等兵が本国から呼び寄せられる。が、やることなすこと型破りな彼の放送に軍上層部は動転、いったんは放送を中止する。だが彼を支持する将兵たちは大いに不満で……。泥沼化するベトナム戦争を舞台にマイクを通じて戦った一人の破天荒なDJの姿を、ユーモアを交えてペーソスたっぷりに描く。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Indian Summer", "インディアン・サマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「手紙」のパク・シニャンと「純愛中毒」のイ・ミヨン共演によるラブストーリー。エリート弁護士のソ・ジュナは、夫を殺害した容疑で死刑を求刑された女性イ・シニョンの弁護を引き受ける。自らの死を強く望むシニョンの態度が気になり事件について調べはじめたジュナは、不可解な点があることに気づく。真相を明らかにしようと奔走するジュナの姿を見て、固く心を閉ざしていたシニョンも彼にだけは笑顔を見せるようになり……。", "2007-03-10", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD MAN BLUES", "ワイルド・マン・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ジャズ・ファンであり、クラリネット奏者としても有名な鬼才W・アレンの、ミュージシャンとしての側面にスポットを当てたドキュメンタリー。妻のスー・インを伴いヨーロッパへの演奏ツアーに出かけた彼の、ステージ・パフォーマンスや舞台裏の素顔をユーモラスに捉えていく。ファンの狂騒に神経をマイらせ、スー・インに甘えるアレンの姿は、彼が映画で演じるナーバスなキャラクターを彷彿させる。ラストに登場するアレンそっくりの両親など、ファン必見の映像が満載。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALLEGRO　NON　TROPPO", "ネオ・ファンタジア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "イタリアの巨匠ブルーノ・ボツェットによる、1976年製作の傑作ミュージカル・アニメーション。ラヴェルの『ボレロ』、ストラヴィンスキーの『火の鳥』など、全６曲のクラシックの名曲に幻想的な映像をのせて、ドタバタあり、シュールな笑いありのイマジネーションあふれる創造世界が広がる。６編のアニメ＆実写が名曲クラシックを幻想的に演出。風変わりな指揮者、老婆だらけのオーケストラなど、シュールでシニカルな笑いが満載だ。", "1976-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Air Circus", "空中サーカス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "飛行家を目指す青年、バディとスピードは親友同士。念願の飛行場へ練習生として入った二人は猛特訓を受けるが、才能があり、すぐに上達するスピードに比べ、バディは一度墜落してから飛行機が怖くなってしまう。バディが悩み、焦る一方でスピードは飛行術を教えているスーとの恋を育んでいき……。ハワード・ホークスとルイス・セイラーの共同監督作品。", "1928-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YELLOW　SUBMARINE", "ザ・ビートルズ／イエロー・サブマリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ビートルズの深海を行く冒険を描いた傑作アニメーション。海底の楽園ペパーランドが、悪党ブルー・ミニーの攻撃を受けた。その危機を知ったビートルズの面々は潜水艦イエロー・サブマリンに乗りこみ、ユーモアと音楽を武器にブルー・ミニーに反撃する。ビートルズのヒット曲にサイケデリックな色彩感覚が融合し、イマジネーション豊かな映像世界が展開。1999年に日本公開され、以前ビデオ化されていたバージョンはUK公開版で、イギリス以外の国ではカットされた『ヘイ・ブルドッグ』が収録されていた。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅がゆく５ 広島代理戦争", "修羅がゆく５ 広島代理戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "哀川翔の熱演が映えるシリーズ第５作。親殺しの濡れ衣を着せられ大阪を追われた本郷組の本郷流一は、新宿に組を構える。ところがその矢先、彼の恩人である広島平世連合副会長の神石が何者かに殺されるという事件が発生。葬儀のため広島に向かった本郷にまたも危機が襲いかかる。彼は産業廃棄物処理施設の建設をめぐって対立する、神石組と熊野組の抗争に巻き込まれていく……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tears of the Sun", "ティアーズ・オブ・ザ・サン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "内戦下のアルジェリア、米軍特殊部隊ウォーターズ大尉は、難民治療にあたっている女医リーナ救出の任務を命じられる。しかし彼女は難民を見捨てて自分だけ助かるわけにはいかないと救出を拒否、ウォーターズと7人の部下は、リーナと共に28人の難民を救出することを決意する。が、非武装地帯までの60キロには、彼らの行手を阻む300人もの反乱軍兵士たちがいた。監督は「トレーニング・デイ」のアントワン・フークワ。", "2003-10-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXPLOSIVE CITY", "爆裂都市", "", "", "http://ecx.images-amazon.com/images/I/51hm7hXKyPL._SL210_.jpg", 98, "香港とマカオを股にかけ、テロリスト一味を追う刑事の奔走を描いた豪快アクション。愛する妻子を殺された怒りを胸に、一味の一人である記憶をなくした女性を連れ、憎き敵に迫る主人公の情念は鮮烈そのもの。本気度満点のカーチェイスや肉弾戦がエモーショナルなドラマを盛り立てる。とりわけ、市街地や空港で繰り広げられる銃撃戦では、凄まじい爆音と火薬量に圧倒される。記憶喪失のテロリストに扮する日本人女優しらたひさこの熱演に加え、テロの親玉役の千葉真一による怪演も見もの。空港で行政庁長官の狙撃未遂事件が起こり、現場にいた容疑者の女テロリスト、ジェイドが拘禁された。彼女の引き渡しを拒否して一味に妻子を殺された刑事、ミンは彼女の記憶を頼りに陰謀の真相に迫る。", "2004-11-13", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若さま侍捕物帖 鮮血の人魚", "若さま侍捕物帖 鮮血の人魚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "遊び上手の若さまは名うての釣り狂い。そんな若さまが殺人事件の現場で踊っていたという謎の人魚を追い求めて、人魚島へ乗り込む。その背後には、新造火薬をめぐる商人の陰謀と、尾張家滅亡をたくらむ蘭学者・九鬼家遺臣らによる謀叛計画があった……。猟奇的な題名を持つ本作はシリーズ中でも、きわめつけの１作。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新 仁義なき戦い・組長最後の日", "新 仁義なき戦い・組長最後の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "“新 仁義なき戦い”シリーズ第３作で、ストーリーは前作同様、独立したものとなっている。大阪の大暴力団と九州の暴力団の抗争のなかで、組織を外れた男が単独で大暴力団の組長の首を狙う。組織の上層部の駆け引きと、血気にはやり死んでいく若者たちが描かれる。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLANES，TRAINS AND AUTOMOBILES", "大災難P.T.A.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「フェリスはある朝突然に」など、1980年代アメリカ青春映画の第一人者であったJ・ヒューズ監督による、抱腹絶倒、究極のロード・ムービー。感謝祭の二日前、広告代理店のヤリ手セールスマン、ニールは、出張先のニューヨークから家族の待つシカゴへ帰ろうと空港へ。ところが大雪のため飛行機はシカゴならぬカンザス州ウィチタに着陸。かくて偶然隣り合わせたデブ男デルとニールの300キロにわたる珍道中が始まった。P.（飛行機）、T.（列車）、A.（自動車）と、ご難続出。ギャグとブラックな笑い満載の傑作コメディ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH OF A GUNFIGHTER", "ガンファイターの最後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アメリカ、西部の小さな町。20年にわたってこの町の保安官を務めているフランクは、犯罪と見るとすべて情け容赦なく裁く、鬼の保安官として名が通っている。だがこのような彼のやり方は、次第に時代遅れなものとなってきていた……。R・ウィドマークが、古き西部の保安官を熱演する異色ウエスタン。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラストラブ", "ラストラブ", "", "", "http://image.eiga.k-img.com/images/movie/34018/315x210.jpg?1356", 110, "田村正和が14年ぶりのスクリーン復帰を果たした大人のラブ・ストーリー。88年に放映され人気を誇ったテレビドラマ「ニューヨーク恋物語」のスタッフが再結集し、再びニューヨークを舞台に撮り上げた。かつてニューヨークでサックス奏者として活躍した阿川明。妻の死をきっかけに帰国した彼は、清掃局に勤める女性・上原結と出会い、その後ニューヨークで偶然再会する。前向きな結と過ごすうちに、明は音楽への情熱を取り戻していくが……。", "2007-06-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("0（ゼロ）からの風", "0（ゼロ）からの風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "一般市民による初の法改正となる「危険運転致死傷罪」の新設に貢献した女性の実話を基にした社会派ドラマ。最愛のひとり息子を悪質な飲酒運転事故で亡くした茂木圭子。何の罪もない若者の命を奪ったにもかかわらず、わずか数年の刑にしか問えないという現状に納得できない圭子は、刑法の厳罰化に向けて行動を開始する。2011年4月に死去した元「キャンディーズ」田中好子の最後の主演作。「ビートキッズ」の塩屋俊監督がメガホンをとる。", "2007-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("輪廻〈りんね〉", "輪廻〈りんね〉", "", "", "http://ecx.images-amazon.com/images/I/41RWBG7PN7L._SL210_.jpg", 96, "清水崇監督が、“生まれ変わり”という新たなテーマに挑んだサスペンス・ホラー。35年前にホテルで起こった大量殺人事件の映画化企画に参加した新人女優が、忌まわしい前世の秘密によって想像を絶する運命をたどっていく様を語り明かす。これまで以上にストーリー性を重視した清水監督が、ミステリー・タッチの巧みな語り口を披露。なかでもホテルの巨大セットを建造し、現実と過去を交錯させた幻惑的な演出にぐいぐい引き込まれる。35年前、群馬のとあるホテルで起きた無差別殺人事件の映画化に挑む松村監督が、新人の渚を主演女優に抜擢する。しかしそれ以来、渚の周囲に謎の少女が出没するなど、異様な出来事が続発するようになり……。", "2006-01-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CIRCLE OF FRIENDS", "サークル・オブ・フレンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "幼なじみの３人の女子大生の恋と友情を描く青春コメディ。一人娘のベニー、尼僧院で育てられた孤児のイヴ、願いごとは何でも叶うと考えているナンの幼なじみ３人は、そろってダブリンの大学へ進む。ナンは二人にラグビー部のスター、ジャックを紹介し、ベニーは会った瞬間に心をときめかす。ジャックのほうもベニーと一緒だと素直になれるのだった。ナンは大学生には目も向けず、大人の男を感じさせるサイモンにアタックし、イヴはジャックの友だちのエイダンといい感じ。ナンはサイモンの子供を妊娠するが、サイモンは中絶を命じる。しかし、カトリックのため中絶ができないナンは、サイモンと別れ、衝動的な行動に出ることに。ナンはジャックと酒を飲み、誘惑し一夜だけの関係を持つ。そして、妊娠したと嘘をつくのだった……。", "1995-01-01", "1,33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("交響詩篇エウレカセブン　ポケットが虹でいっぱい", "交響詩篇エウレカセブン　ポケットが虹でいっぱい", "", "", "http://image.eiga.k-img.com/images/movie/54318/315x210.jpg?3677", 115, "2005年4月から1年間に渡って放送されたロボットアニメ「交響詩篇エウレカセブン」の劇場版。新作カットにTVシリーズの映像を加え、主人公レントンとエウレカの“もうひとつの物語”として生まれ変わる。謎の生命体イマージュと人類が半世紀に及ぶ戦いを繰り広げてきた西暦2054年。独立愚連隊の戦闘母艦・月光号に乗る少年兵レントンは、8年前に連れ去られた幼なじみの少女エウレカを助け出して故郷へ帰ることを胸に誓っていた……。", "2009-04-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Way Back", "ザ・ウェイ・バック（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "「刑事ジョン・ブック　目撃者」「マスター・アンド・コマンダー」の名匠ピーター・ウィアーが、スラボミール・ラウイッツによる手記「脱出記　シベリアからインドまで歩いた男たち」を映画化した実録冒険ドラマ。1940年、スパイ容疑でソ連当局に逮捕されたポーランド軍人のヤヌスは、極寒のシベリア強制収容所に収監される。ヤヌスは収容所内で出会った仲間6人とともに収容所からの脱出を図るが……。出演はエド・ハリス、ジム・スタージェス、コリン・ファレルに、「つぐない」のシアーシャ・ローナン。", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はじめての家出", "はじめての家出", "", "", "http://image.eiga.k-img.com/images/movie/54248/315x210.jpg?3819", 104, "「ニュースター・シネマ・コレクション」として、エイベックスが同社の新人を起用し製作した青春映画の第4弾。両親が離婚し、父と母のそれぞれの家を行き来する生活を送る中学生の翠は、転校先の中学で朝子という少女と仲良くなる。朝子は家族への抗議として家出を計画中で、翠は朝子と一緒に家出することを決意する。主演は「容疑者Xの献身」の金澤美穂、「おっぱいバレー」の斉藤リナ。", "2009-02-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Great Debaters", "グレート・ディベーター　栄光の教室", "", "", "http://ecx.images-amazon.com/images/I/51stuKwHU6L._SL210_.jpg", 128, "", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MTV Unplugged ayaka cinema edition", "MTV Unplugged ayaka cinema edition", "", "", "http://image.eiga.k-img.com/images/movie/55178/315x210.jpg?3351", 60, "2009年11月18日、大阪城ホールにて開催された絢香の無期限活動休止前のラストワンマンライブを収録した映像作品。代表曲「三日月」「I Believe」「みんな空の下」などを熱唱するほか、故マイケル・ジャクソンの「Human Nature」をカバーするなど、MTVの伝統的なアコースティックライブ“MTV Unplugged”のステージらしい選曲となっている。", "2010-01-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("櫂", "櫂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "五社英雄監督、宮尾登美子原作のコンビによる第３作。大正３年から昭和11年に至る、女衒を生業とする一家の骨肉相はむ争いを、五社監督独特の骨太なタッチで描いている。女衒の岩伍を演じる緒形はもちろんだが、妻の喜美に扮した十朱幸代の熱演が見どころ。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crossing", "クロッシング", "", "", "http://image.eiga.k-img.com/images/movie/54098/315x210.jpg?4473", 107, "「火山高」「彼岸島」のキム・テギュン監督が、脱北者の過酷な現実を描く社会派ドラマ。北朝鮮の炭鉱町に住む元サッカー選手のヨンスは、妻ヨンハと11歳の息子ジュニとともに、貧しいながらも幸せな日々を送っていた。しかし、ヨンハが肺結核に倒れ、ヨンスは治療薬を手に入れるため中国へ向かう。決死の覚悟で国境を越えたヨンスは、必死に働いて薬を手に入れようとするが、北朝鮮では夫の帰りを待ちながらヨンスが静かに息を引き取る。孤児となってしまったジュニは、父との再会を信じて国境を目指すが……。", "2010-04-17", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　天守物語", "シネマ歌舞伎　天守物語", "", "", "http://image.eiga.k-img.com/images/movie/57388/315x210.jpg?1953", 108, "歌舞伎の舞台公演を映画館のスクリーンでデジタル上映する「シネマ歌舞伎」シリーズ第15弾。美しい異界の人と現世の人間の恋を描いた泉鏡花の戯曲が原作。藩主の鷹を逃がしたために切腹を命じられた鷹匠の姫川図書之助は、異形の者が住まうといわれる異界のある白鷺城（姫路城）最上階へいけば命を救うと言われ、白鷺城天守閣へ向かう。そこでは異界の主こと天守夫人の富姫が宴を開いており、一度は追い返された図書之助だったが、いつしか富姫が図書之助に心を奪われ……。2009年7月に歌舞伎座で上演された舞台をデジタルシネマ化。", "2012-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("On The Way", "On The Way", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "ソウル生まれの国際的女性アーティスト、崔在銀が手掛けたドキュメンタリー・ドラマ。板門店の38度線、アウシュビッツの収容所跡、そして緑豊かな自然、幼女と老婆などの映像を織り交ぜながら、人間と人間、人間界と自然界との間にある“国境線”に秘められた様々な想いを浮かび上がらせる。ドラマというよりインスタレーション作品といった趣で、光あふれる美しい映像が印象的。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飼育の部屋・連鎖する種", "飼育の部屋・連鎖する種", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "女性拉致監禁事件を描く社会派官能シリーズの最終作となる第３作。クリーニング店を営む宏一は、毎日黙々と仕事をしている無口な男。幼少期に虐待されていた彼は、そのことが原因で度々湧き上がる暴力衝動に苦しんでいた。そんなある日、若い女性客がクレームをつけにやってくる。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GETTING EVEN", "復讐の弾丸 リベンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ショッキングなレイプ・シーンと非情な殺しの手口が連続するハード・バイオレンス。コリンは札つきのゴロツキ４人組に、従妹の少女をレイプされたうえに殺されてしまう。ところが裁判の結果、無罪放免となった４人の悪業にコリンの怒りが……。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Meet the Robinsons", "ルイスと未来泥棒", "", "", "http://image.eiga.k-img.com/images/movie/53158/315x210.jpg?1990", 102, "ウィリアム・ジョイスの絵本「ロビンソン一家のゆかいな一日」をアニメーター出身のスティーブン・J・アンダーソン監督がCGアニメとして映画化。孤児院で育てられ、発明に明け暮れる孤独な少年ルイスは、幼い頃に生き別れた母の面影を思い出そうと、記憶を取り戻す装置“メモリー・スキャナ”を開発する。だが、山高帽をかぶった謎の男にその装置を奪われてしまう……。", "2007-12-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続々番頭はんと丁稚どん", "続々番頭はんと丁稚どん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "娘の見合い相手の貿易会社若社長から、時価100万円のエンゲージ・リングを預った“七ふく堂”。だがその指輪を店内で紛失してしまい、弁償しなければノレンを下ろさねばならぬハメに。短波心霊者によって餅の中にあると判断され、崑松、雁七らは懸命に餅を食うが……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レディ・ドラゴン　怒りの鉄拳", "レディ・ドラゴン　怒りの鉄拳", "", "", "http://image.eiga.k-img.com/images/movie/57828/315x210.jpg?4106", 76, "元グラビアアイドルで女優として活動している阿部真里が主演のカンフーアクション。恋人同士の美麗と剛は、かつてはともに武闘を極めるため精進していたが、剛はいつしかカンフーを捨ててしまい、自堕落な生活を送っていた。そんなある日、2人の前にかつての剛のライバルで恋敵だった純が現れる。純は決着をつけようと剛を誘い出すが、剛にはカンフーを捨てたある理由があった。そして美麗は2人の間で揺れ動き……。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("激突！格闘技 四角いジャングル", "激突！格闘技 四角いジャングル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1970年代を席巻させた猛者たちの熱い戦いが収められているドキュメンタリー映画の第２作。1978年のプレ日本選手権で行われたアントニオ猪木対ヒロ・マツダ戦や、熊殺しの最強空手家W・ウィリアムスをはじめ名格闘家が総出演！　DVDタイトルは「四角いジャングル　激突！格闘技」。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("越南仔／THE MAN FROM VIETNAM", "男たちの鎮魂歌〈レクイエム〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "香港経済界を牛耳る黒幕リン兄弟を殺した、ベトナム密航者唯一の生き残りウォンは今や追われる身。彼は逃走中に美しい女性に出会い、彼女と恋に落ちる。だが、彼女の兄は、彼を追いかける熱血刑事チャンだった。国際都市香港を舞台に繰り広げられるハードボイルド・サスペンス。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もう、ひとりじゃない", "もう、ひとりじゃない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「櫻の園」の脚本家・じんのひろあきが「月より帰る」に続いて演出を手掛けた監督第２作。多重人格者同士の迷走する恋を、ユーモラスに描く。５重人格者の男が、８人の人格を持つ女に恋をした。だが、惹かれ合う双方の人格が同時に顔を出すことはまれで、二人は恋を進展させることができず……。それぞれの人格を複数の俳優に演じさせた点がユニーク。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monsters University", "モンスターズ・ユニバーシティ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「モンスターズ・インク」（2001）のシリーズ第2作。", "2013-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("流血の抗争", "流血の抗争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "長谷部安春監督と宍戸錠の名コンビによるニュー・アクションの佳作。宍戸錠扮する主人公が刑務所から出所。中央から進出してきた大組織の圧力から、地元の組同士の代理戦争をさせられるが、大組織は主人公の組を叩きつぶす。深手を負った主人公は弟分とともに大組織の事務所に斬り込む。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひかりのおと", "ひかりのおと", "", "", "http://image.eiga.k-img.com/images/movie/57348/315x210.jpg?1739", 89, "", "2011-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RIFLESSI DI LUCE", "煉獄アムール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "イタリアの美しい田園風景の中で展開する、倒錯した性の世界を鋭く描いた問題作。昔の女の死を今もひきずり、固く心を閉ざすフェデリコ。愛に飢えた一家が、新しい女の出現によって異常な愛欲の渦に溺れていく。現代イタリア映画界の４大セクシー女優が競演した、リアルなセックス・シーンが見もの。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("テレビに挑戦した男・牛山純一", "テレビに挑戦した男・牛山純一", "", "", "http://image.eiga.k-img.com/images/movie/57758/315x210.jpg?2026", 82, "テレビドキュメンタリー界の草分け的な人物で、2400本近い作品を制作したプロデューサー・牛山純一（1930～97）の業績をたどるドキュメンタリー。テレビが放送を開始した58年から、97年に他界するまでテレビ制作の一線で活躍した牛山は、民放初のドキュメンタリー番組「ノンフィクション劇場」を制作するなど、話題作を次々と手がけていった。牛山とともに働いた番組スタッフやテレビ草創期の放送人たちのインタビューを交え、牛山がテレビにかけた生涯を紐解く。ドキュメンタリー監督・佐藤真の企画により制作がスタートしたが、07年に佐藤が他界。映画美学校で佐藤の教えを受けていたゼミ生たちが引き継ぎ完成させた。", "2012-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STREET TRASH", "吐きだめの悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "オープニングの長回しが全編のムードを予感させる21歳の学生監督による作品。マンハッタンの下町。ゴミ捨て場に居を構える兄弟が盗んだワインは人間の身体を溶かしてしまう代物だった……。目を覆うばかりの汚なさに圧倒されるが、おかしさと悲しみに彩られた好編。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Going to Pieces: The Rise and Fall of the Slasher Film", "封印殺人映画", "", "", "http://image.eiga.k-img.com/images/movie/34048/315x210.jpg?1379", 88, "恐ろしい殺人鬼が若者たちを次々と惨殺していく姿を描き、1980年代のアメリカをはじめ世界中で一大ブームを巻き起こした“スラッシャー・ムービー”を題材にしたドキュメンタリー。「ハロウィン」のジョン・カーペンター監督、「エルム街の悪夢」のウェス・クレイブン監督、「13日の金曜日」のショーン・S・カニンガム監督ら関係者へのインタビューを通し、スラッシャー・ムービーの歴史を文化的側面から検証していく。", "2007-07-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　SCORE", "スコア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "世界を股にかける超一流の泥棒ニックは、長年のパートナー、マックスから新たな仕事を依頼される。それは地元モントリオールの税関に保管されたフランス王家の秘宝の杓を盗み出すというもので、障害者と偽って清掃職員として税関に入り込んでいる若い男ジャックが相棒になることに。この仕事で引退することを決めたニックはジャックとともに準備を進めるが、慎重派のニックは向こう見ずなジャックに不安と不信感を覚える……。M・ブランド、R・デ・ニーロ、E・ノートンという３世代にわたる名優の共演によって、アウトローたちの意地のぶつかり合いを描いたクライム・サスペンス。ラストに展開する裏切りとだましあいの連続も小気味よい。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCARY MOVIE", "最終絶叫計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "全米で大ヒットを記録したコメディ映画。ハロウィン・マスクの殺人鬼が次々と学生たちを血祭りに上げていく。すべてはシンディたちが一年前に起こした自動車事故のせいなのか……。という「スクリーム」と「ラストサマー」を交ぜ合わせたようなストーリーの中に、「マトリックス」や「ブレア・ウィッチ・プロジェクト」などのヒット作のパロディーが連発されていく。監督は「ダーティ・シェイム」のK・I・ウェイアンズで、その弟であるショーン、マーロンが共同脚本を担当、出演もしている。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GODS MUST BE CRAZY", "ミラクル・ワールド　ブッシュマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "カラハリ砂漠に住む友好的な部族、ブッシュマンを扱ったコメディ。コーラの空きビンを悪魔の道具として、世界の果てまで捨てに行くブッシュマンの代表者。彼のトンチンカンな道中を、砂漠地帯の僻地に赴任してきた女教師と気の弱い微生物学者の恋を絡めて描く。ニカウの天衣無縫さが大ウケして、世界的な大ヒットを記録。DVDタイトルは「コイサンマン」。", "1981-01-01", "67", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIRL’S　NIGHT　OUT", "ディナーの後に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "現代韓国の若い女性たちの愛や性、結婚に対するホンネを切り取った女性映画。同棲中の恋人がいるヨニと、気ままな恋愛を楽しむ女社長ホジョン、オクテの大学院生スニは親友同士で、食事をともにしては恋愛観を語り合っている。彼女たちは、やがてそれぞれの試練を迎え……。ハンディカメラで捉えた映像が、ドラマにリアルな感触をあたえている。", "1998-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("源氏九郎颯爽記 白狐二刀流", "源氏九郎颯爽記 白狐二刀流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "美剣士・源氏九郎は先祖の源義経が残したという財宝をたずねて無人島にわたる。しかし、その財宝は元海賊の播州屋十兵衛も狙っていたものだった。奉行連中も味方につけた十兵衛一味は九郎に襲いかかる……。白づくめの剣士・源氏九郎を主人公にした人気シリーズの第２作。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("受取人不明／ADDRESS　UNKNOWN", "受取人不明", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "世界に名を轟かす韓国の鬼才キム・ギドクが、その持ち味を遺憾なく発揮した監督第６作。説明を排除した静かなバイオレンスや葛藤描写は本作でも健在。70年代の韓国。米軍基地のある町に暮らす３人の若者たちの愛と青春を切々と映し出す。気が弱く大人しい青年ジフムは、同じ町に住む事故で失明した女子高生ウノクに想いを寄せていた……。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裸の大将", "裸の大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "放浪の画家・山下清の半生記『放浪日記』を原作として水木洋子が脚色、堀川弘通が監督した作品。生まれつき頭の弱い清が、戦争怖さに兵隊検査を逃れようと入れられていた八幡学園を飛び出し、放浪の旅を続ける。その行く先々で繰り広げる実話喜劇。天才・山下清の飄々とした持ち味を小林桂樹が見事に表現。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BATTAGLIA DEL DESERTO", "熱砂の戦車軍団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "砂漠の戦場を舞台に、ドラマティックな人間模様を描く戦争アクション。恋人をイギリス人に殺されたドイツ軍将校と、作戦本部を潰され、部下４人と砂漠に取り残されたイギリス軍大尉が出会い、休戦協定を結ぶ。そして互いに協力しあい砂漠からの脱出を図る。", "1969-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUKUSHIMA DAY", "FUKUSHIMA DAY", "", "", "http://image.eiga.k-img.com/images/movie/58038/315x210.jpg?2634", 48, "「イノセントワールド」などで知られる作家で、映画監督としても活動している桜井亜美が、2011年3月11日の東日本大震災と福島第一原子力発電所の事故を受けて製作した短編映画。放射能にさらされ、目に見えない恐怖が迫る中でも、家族や友人、仕事などそれぞれが抱える日常があり、その土地から離れられない人々の姿を描く。「虹の女神 Rainbow Song」でも桜井とコラボレーションした岩井俊二がプロデューサーを務め、俳優の柏原収史が音楽を手がける。", "2012-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガメラ対深海怪獣ジグラ", "ガメラ対深海怪獣ジグラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ジグラ星の海が公害で汚染されたために地球の海を征服しにやって来た深海怪獣ジグラは、正義の味方ガメラと激突を繰り広げる。“ガメラ”シリーズの第７作だが、この年の大映の倒産により、実質的には最終作となってしまった。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GENTLEMAN TRAMP", "放浪紳士チャーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "チャップリンの88年の生涯を、彼の映画とプライベート・フィルムを交えて綴るドキュメンタリー作品。最大のポイントは、めったに見ることのできないチャップリンの私生活の一面を垣間見ることができるという点である。ファン必見の作品。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’APPA＾T", "ひとりぼっちの狩人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "パリで起きた事件をもとにしたベストセラー小説を映画化。18歳のナタリーは、恋人エリックの父親が経営するブティックのマヌカン。アメリカで高級ブティックを開いて一旗あげるのが夢だが、金持ちの大人たちに若さの魅力を振りまいていくら売り上げを伸ばしても、自分の手に金は残らない。そして大金を簡単に手にする方法として、大金持ちを襲い強奪することを思いつく。彼女とエリックは同居人でエリックの友人のブルーノを仲間に誘い、計画を実行。しかし目的の金は手に入らず、殺人だけが行われた。次の計画へと向かう彼らだが、警察の手は着実に伸びていた。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Schindler's List", "シンドラーのリスト", "", "", "http://ecx.images-amazon.com/images/I/41w7XUVFzWL._SL210_.jpg", 195, "第二次大戦中の実話をもとに、S・スピルバーグが、商業的採算をあえて無視して取り組んだ入魂の反戦ヒューマン・ドラマ。ナチスドイツの嵐が吹き荒れるポーランドのある街の実業家シンドラーは、迫害されるユダヤ人をこき使って財をなす。やがて強制収容所が建設され、ユダヤ人たちが虐殺され始めた。ナチスのやり方についていけぬシンドラーは、私設収容所を設け、1200人ものユダヤ人を救おうとするが……。やや演出にもたつきがあるものの、白黒画面の美しさと主役のL・ニーソンの好演も手伝い、スピルバーグは初のオスカーに輝いた。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Revolver", "リボルバー", "", "", "http://image.eiga.k-img.com/images/movie/53558/315x210.jpg?4373", 115, "リュック・ベッソン製作の下、「ロック、ストック＆トゥー・スモーキング・バレルズ」のガイ・リッチー監督とジェイソン・ステイサムが再びタッグを組んだクライム・アクション。カジノ王マカにハメられて刑務所に送られたギャンブラーのジェイク。7年間の刑期中にギャンブルの腕を磨いた彼は出所後すぐにマカのカジノに乗り込む。大金を巻き上げられ激怒したマカはジェイクに殺し屋を差し向けるが、突然現われた謎の2人組がジェイクを救い……。", "2008-06-07", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOIN’ SOUTH", "ゴーイング・サウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "J・ニコルソンが監督・主演をこなした異色のウエスタン。悪事を働き、それがもとで処刑される寸前の男を見かねた女が、彼と結婚することで身柄を預かることを保安官に約束。いいかげんな男と頑固な女の間には、いつもケンカが絶えなかったが、そのうち本物の愛が芽生え始める……。ニコルソンに加え、J・ベルーシにD・デヴィートといった充実した脇役陣も見もの。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若人よ、いのちと愛のメッセージ", "若人よ、いのちと愛のメッセージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "16歳の女子高生が妊娠という思いもかけない出来事に見舞われ、高校を辞めてでも子供を産んで育てると決心。彼女とその恋人は、周囲の反対を押しきって家を飛び出すが……。現代に生きる若者たちの“性”という問題に焦点を当てた社会派ドラマ。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("福耳", "福耳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "“木更津キャッツアイ”シリーズなどの脚本を手掛けた宮藤官九郎の初主演映画。老人の霊に取り憑かれた青年が、その老人の望みを叶えるため“老いらくの恋”に奔走する姿を描く。29歳のフリーター、高志は好きになった看護士を追いかけて浅草の老人向けマンションのレストランで働くことになった。そこで老人・富士郎に出会った高志は彼の“悔い”を聞く。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SURRENDER", "恋はお手あげ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "もう結婚はこりごりという売れっ子作家が、ひょんなことからつきあい始めた女流芸術家との騒動の末ゴールイン、というアメリカお得意のスクリューボール・コメディ。S・フィールド、M・ケインのオスカー受賞俳優、S・グッテンバーグ、初期のウディ・アレン作品常連のL・レッサーらの演技も見もの。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PIE IN THE SKY", "あなたに逢いたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "交通渋滞のタイミングを計る驚異的な能力を持つチャーリーは、幼なじみのエイミーと運命の再会を果たし、愛し合ったまではよかったが、遠距離恋愛のためうまくいかない。さらに仕事までもがつまずき始めている。ところが、そんな彼に再び出会いの機会が訪れる。チャーリーは、起死回生のチャンスを生かせるか？　現代の若者共通の悩み“恋と就職”をテーマに、アップテンポなノリと軽いギャグで、恋する二人を描くロマンティック・コメディ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In the Soup", "イン・ザ・スープ 夢の降る街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ニューヨークの青年アルドルフォは映画製作への情熱は人一倍だが、いつも金策に困っていた。そんなある日、ジョーと名乗る怪しげな初老の男が現れ、彼への資金援助を約束。やがてアルドルフォは、ひそかに思いを寄せていた美女アンジェリカとも親しくなるが、思わぬ事件が起こり……。アメリカン・インディーズの雄A・ロックウェルが、自らの体験をベースにしたというユニークなコメディ。夢みる映画青年を取り巻くトラブルの数々を、微笑ましいペーソスを込めて描き上げている。J・ジャームッシュがウサン臭いプロデューサー役でゲスト出演。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last American Hero", "ラスト・アメリカン・ヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "密造酒の運び屋をしていた青年が、ストックカー・レースに青春を懸ける。アメリカのオートレース史上空前の飛ばし屋といわれた実在の名レーサー、ジュニア・ジョンソンの青春と豪快なレースぶりを再現。ジム・クローチの『アイ・ガッタ・ネーム』が主題歌として使われている。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グスコーブドリの伝記（2012）", "グスコーブドリの伝記（2012）", "", "", "http://image.eiga.k-img.com/images/movie/57598/315x210.jpg?2933", 0, "宮沢賢治が1932年に発表した童話を、「銀河鉄道の夜」（85）も手がけた杉井ギザブロー監督がアニメーション映画化。1920年代の冷害にみまわれた東北の森を舞台に、厳しい自然と向き合う青年の姿を描き出す。イーハトーブの森で両親と妹と幸せに暮らしていたグスコーブドリは、森を襲った冷害のため家族を失ってしまう。やがて青年に成長したグスコーブドリは火山局に勤めるようになるが、再び大きな冷害が発生。被害を防ぐためには誰かが犠牲になって火山局に残り、人工的に火山を噴火させなければならず、グスコーブドリがその役割を担う決意をする。", "2012-07-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雁の寺", "雁の寺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "原作は前年に直木賞を受賞した水上勉の同名の小説。洛北衣笠山にあって、厳しい戒律の守られている禅寺、雁の襖絵で名高く俗に“雁の寺”とも呼ばれるその寺の中で、サディスティックな住職と愛人とのただれた愛欲と、それをのぞき見る不遇な少年僧の虐げられた生活が繰り広げられる。鋭角的でキビキビした川島雄三の演出、独特の角度と構図を見せる村井博の撮影、愛の犯罪ドラマともいうべきサスペンスに満ちあふれたストーリーを展開させた川島と舟橋和郎の脚色、そのどれもが驚嘆に値する仕上がり。そして若尾文子の妖艶さは観る者を圧倒する。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あなたは遠いところに", "あなたは遠いところに", "", "", "http://image.eiga.k-img.com/images/movie/54848/315x210.jpg?1645", 126, "「王の男」のイ・ジュニク監督が「夏物語」などの演技派女優スエを主演に迎え、ベトナム戦争を背景に描いたヒューマンドラマ。農家のひとり息子サンギルに嫁いだスニは、厳格な姑に言われるがまま、軍に入隊した夫の元に通っている。しかしサンギルはある出来事をきっかけに、スニに何も告げぬまま志願兵としてベトナムへ旅立ってしまう。行方のわからなくなった夫を探すため、スニは慰問公演団の歌手としてベトナムへ向かうが……。", "2009-11-21", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tin Men", "ティンメン／事の起こりはキャデラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "バリー・レビンソン監督が、リチャード・ドレイファスとダニー・デビートを主演に迎えて贈るコメディ・ドラマ。1960年代のボルチモア。アルミ建材セールスマン（通称ティンメン）のBBは、購入したばかりのキャデラックを、同業者であるティリーの車にぶつけられてしまう。どちらが悪いかで大喧嘩となり、それ以来ことあるごとに衝突しあうようになった2人。復讐が復讐を呼び、事態はどんどんエスカレートしていく……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOMEONE　LIKE　YOU", "恋する遺伝子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "真実の愛を求めて奮闘する女性の姿を描くロマンティック・コメディ。TV局で働くジェーンが、新任プロデューサー、レイと急接近。幸せな日々に酔いしれる彼女だが、突然同棲を断られ失意のどん底に突き落とされる。やむを得ず同僚のエディの家に転がり込んだ彼女は、これまでの経験をもとに、“雄牛は同じ雌牛と交尾しない”というテーマでエッセイを書き始め、一躍時の人となってしまう。表情豊かに感情を表現するヒロイン、A・ジャッドが、チャーミングな魅力を放っている。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悲しみは女だけに", "悲しみは女だけに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "尾道で飲食店を開いている政夫のもとに、姉の秀代がアメリカから帰ってきた。実は彼女は30年前に結納金目当てで、とある家に身売りをされていた。やがて、秀代の財産を狙った一族のいさかいが始まり……。劇団民芸が上演した新藤兼人の戯曲『女の声』を、新藤自身が舞台的手法も取り入れて脚色・監督している。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EN EFFEUILLANT LA MARGUERITE", "裸で御免なさい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「素直な悪女」で、センセーショナルなデビューを飾った、フランス映画界きってのグラマー女優B・バルドー主演のお色気コメディ。セックス小説を書いて話題になった娘が、ひょんなことからヌード・コンテストに出るハメに……。当時のB・バルドーの夫ヴァディムが脚本参加。", "1956-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tinker Bell", "ティンカー・ベル", "", "", "http://image.eiga.k-img.com/images/movie/53818/315x210.jpg?2092", 0, "「ピーター・パン」に登場する妖精ティンカー・ベルを主人公にしたCGアニメーション。ネバーランドにある妖精の谷“ピクシー・ホロウ”で生まれたティンカー・ベルは、もの作りの才能に恵まれ、仲間の妖精たちと楽しい日々を送っていた。しかし春を届けにメインランド（人間界）へ向かう妖精たちに、もの作りの妖精はメインランドには行けないと教えられ……。全4部作の第1部で、ティンカー・ベル誕生の秘密が明かされるファンタジー。", "2008-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おてんとうさまがほしい", "おてんとうさまがほしい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 47, "照明技師として半世紀以上を映画界で生きてきた渡辺生は、妻のトミ子さんがアルツハイマーであると知って、カメラを回し始めた。梅ケ丘病院への入院、病院での日常とバスハイクや夏祭りといったささやかな行事、１年ぶりの帰宅と瞬く間の再入院。妻の姿を撮り続けるうちに、看護婦さんや他の患者さん、痴呆の身内を抱える家族たちへも視線は注がれていく。病と真正面から向き合い、ともに生きていこうとする渡辺夫婦の絆、生へのメッセージが伝わるシネ・エッセイ。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN BETWEEN", "キャロル・リードの　二つの世界の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "休暇で東ベルリンを訪れたスザンヌ。彼女はそこでイーボという男と出会う。しかしその男は警察やギャングから狙われる身だった。スザンヌはいつしか渦中に巻き込まれ、悲劇的な結末へ……。東西の冷戦を背景としたサスペンス・ドラマ。", "1953-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　EXTRA　LEGAL", "デッド・アウェイ／バンコク大捜査線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "タイ製のパワフルなアクション。麻薬王の釈放を求めるテロ一味が列車を乗っ取った。特殊部隊の活躍により事件は解決したかに見えたが、一小隊が裏切って仲間や乗客を殺してしまう。すべてを目撃した隊員のヌイは事件を告発しようとするが、逆に罠にハメられ……。チェイスや爆破などバンコクの街で撮影されたダイナミックなアクションに圧倒される。", "1999-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふゆの獣", "ふゆの獣", "", "", "http://image.eiga.k-img.com/images/movie/56168/315x210.jpg?2383", 92, "ユカコは同じ職場のシゲヒサと付き合っているが、最近はシゲヒサの浮気を疑っていた。精神的に不安定になり、ある日、駅の地下道で倒れたユカコは、同僚のノボルに介抱される。ノボルはアルバイトのサエコに恋心を抱き、思いを伝えるが、シゲヒサと密会を繰り返しているサエコは、ノボルを同僚以上に見ることができない。やがて4人はひとつの狭い部屋で顔を合わせることになるが……。男女4人のむき出しの感情を描き、第11回東京フィルメックスで最優秀作品賞を受賞した。監督は本作が長編2作目の新鋭・内田伸輝。", "2011-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD AND WONDERFUL", "ムッシュ・コニャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "パリで有名な映画スターとして莫大な収入を上げ、豪華な大邸宅に住むプードル犬のコニャック。しかしこの犬には、むやみに酒を好むという弱点がある。ある日、コニャックはナイトクラブでフルート吹きのテリーと知り合い意気投合、グデングデンに酔っ払って彼のアパートに行き、朝まで寝込む。姿を消されたコニャックの飼い主は大騒ぎで……。“スター犬”の迷走ぶりを描く異色コメディ。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("激突！若大将", "激突！若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "加山雄三主演のヒット・シリーズに続く、“新・若大将”第２作。草刈正雄扮するアイスホッケー部主将の若大将が、合宿費調達のために行なった歌手稼業が巻き起こすキャンパスの騒動を描く。若大将を慕う女子大生役で、坂口良子が映画初出演。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAKE THEM DIE SLOWLY", "人喰族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "アマゾンにカニバリズムの調査にやって来た女子学生グロリアらは、原住民の集落を発見するが、そこは死体の山だった。だが、その惨殺を行ったのは……。人肉食いのゲテもの題材とはいえ、監督は「戦争と平和」のベテラン監督U・レンツィである。", "1984-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聖戦３年／聖戦４年", "聖戦３年／聖戦４年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 34, "昭和12年から15年にかけて、“南京総攻撃”“広東攻略戦”“五原作戦”など、中国戦線における日本軍の作戦と戦闘を余すところなく伝える戦記映画。映像の保存状態がきわめて良く、軍用機・戦車・野砲・機銃などの戦闘シーンも非常に細かな箇所まで描写されている。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЧАСТНАЯЖИЗНЬ", "解任", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "長年勤めてきた会社から突如として解任を迫られ、路頭に迷う男とその家族の姿を描いたソ連製の社会派ドラマ。合併により公社総裁の座を失ったアブリーコフ。突然年金生活に入るはめになって初めて自分の人生を振り返るが、気付いてみると家族の心は自分から遠く離れたところにあるのだった。", "1982-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CURE IN ORANGE", "ザ・キュアー／イン・オランジュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "エキセントリックなライブ活動で人気のあるザ・キュアーが、南フランスの古代円形劇場テアトル・オランジュで行ったコンサートの模様を収めた音楽ドキュメンタリー。『シェイク・ドッグ・シェイク』をはじめヒット・ナンバーの数々が青白いライトの下で繰り広げられる。ポープ監督は200本以上ものビデオ・クリップを手掛けた才人。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あしたのジョー", "あしたのジョー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "今も根強いファンを持つボクシング漫画の傑作『あしたのジョー』の実写版。主役の矢吹丈には石橋正次が扮している。原作に忠実なエピソードを盛りこみ、力石徹の死までを描いて、ファイティング・シーンのストップ・モーションを多用した緊張感がたまらない。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("種まく旅人　みのりの茶", "種まく旅人　みのりの茶", "", "", "http://image.eiga.k-img.com/images/movie/56868/315x210.jpg?1278", 121, "大分県臼杵市を舞台に、お茶の有機栽培を営む人々が織り成すドラマを描く。農林水産省の役人・大宮金次郎は、全国各地の農家をまわっては作業を手伝い、酒を酌み交わすという変わり者だが、農家の人々の間では「風来坊の金ちゃん」と呼ばれ親しまれていた。ある時、金次郎は大分県臼杵市で緑茶の有機栽培を行っている修造のもとを訪れるが、修造が心臓発作で倒れてしまい、リストラされて東京から戻ってきたばかりの孫娘のみのりが茶畑の世話をすることになるが……。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBERTO　SUCCO", "ロベルト・スッコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "1980年代、欧州を震撼させた実在の連続殺人鬼ロベルト・スッコの実像に迫る衝撃作。「倦怠」のセドリック・カーン監督が主人公に抜擢した新鋭ステファノ・カセッティの怪演が見もの。両親を殺害したロベルトはイタリアから逃走する。フランスに渡った彼はカートと名乗り、レアという女性と恋に落ちる。その後、彼は彼女にこそ手はかけないものの、殺人と強盗を繰り返していく。", "2001-01-01", "2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死ね！死ね！シネマ", "死ね！死ね！シネマ", "", "", "http://image.eiga.k-img.com/images/movie/56848/315x210.jpg?4568", 72, "映画美学校の実習として作られた短編をもとに、「東京島」の篠崎誠監督が追加撮影を行い1本の長編として完成させた。東京・渋谷に移転したばかりの映画美学校で、学生の映画を酷評した講師シマザキが野次られたことで怒り狂い、42人を虐殺するという惨劇が起きる。それから4年後、事件当時、女子高生だったなつきが、いまは廃きょとなった映画美学校で究極のホラー映画を撮ろうとしたことから、周囲を恐ろしい事態に巻き込んでいく。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蒸発旅日記", "蒸発旅日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "人気漫画家、つげ義春のエッセイ『貧乏旅行記』の一編をもとにしたシナリオを、「アンモナイトのささやきを聞いた」の山田勇男監督が映画化。イマジネーションあふれる映像世界が鮮烈だ。今の生活に不安を抱く漫画家の津部は、面識のない愛読者の静子が暮らす町へ。無事、静子と会った彼は結婚を漠然と思い浮かべる。彼女と１週間後の再会を約束した彼はその間、旅に出た先々で様々な人々と遭遇する。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Junior", "ジュニア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ラリーとアレックスは新薬の研究をする博士コンビ。ひょんなことから自分の体で人体実験をしたアレックスは妊娠してしまう。途中で止めるはずだったが、実験に使った卵子が、アレックスと恋に落ちたダイアナのものだったことが判明し、子供に愛着を持ち始める2人。アレックスは子供を産もうと決意するが……。母性にめざめるシュワちゃんの妊婦姿が見もの。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("АЭЛИТА", "アエリータ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1902年に作られたフランスの「月世界旅行」などに触発されて作られた、ソ連製SFサイレント映画の古典。原作者の文豪トルストイが脚本で参加。三角関係の恋愛映画としての側面も持ち合わせた作品だ。火星行きの宇宙船を発明した主人公は、妻を狙う男に嫉妬して男を射殺し、刑事に追われて火星へ逃げる。そして、そこで火星のアエリータ女王と恋に落ちて……。", "1924-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("象を喰った連中", "象を喰った連中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "戦時下で「敵機空襲」「決戦」といった作品を撮った後、タイに機関銃隊小隊長として派遣されていた吉村公三郎が、捕虜生活を経て帰国第１作として発表したドタバタ喜劇。彼としては３年ぶりの作品となった。知らずに死んだ象の肉を食べてしまった５人の人間が、象の死肉は有毒だと知らされて大騒ぎになる、というもの。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タムくんアニメ／ウィスット・ポンニミット作品集", "タムくんアニメ／ウィスット・ポンニミット作品集", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "タイ出身の漫画家、ウィスット・ポンニミットの作品13本。ラクガキのような単純なタッチでありながら、不思議と心に残る作風は、奈良美智ら日本のアーティストにもファンが多い。本人が奏でる音楽も、味わい深い余韻を残す。イノセントな少女マムアンちゃんが愛犬マナオと繰り広げる心暖まる物語(「葉っぱ」)や、彗星の女の子との、切なくて哀しい恋の物語(「彗星」)など。", "2001-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUNK・死霊狩り", "JUNK・死霊狩り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "沖縄で宝石強盗をしでかした一味が逃げこんだ廃工場。そこには人喰いゾンビがうごめいていた。やがて彼らは、群れをなして一味に襲いかかっていく。すべては、在日米軍がひそかに研究していた人体細胞活性化薬が原因だった……。室賀厚監督が「ゾンビ」にオマージュを捧げながら演出したバイオレンス・ホラー・アクション映画の快作。DVDは「地雷撤去隊～THE GROUND／JUNK～死霊狩り」に収録。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHECKPOINT", "アルプス殺人レース —スピードを盗んだ男—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "フィレンツェからスイスのロカルノまでを走る国際オートレースに、国境越えをもくろむ逃亡者が紛れ込む。ピストルを突きつけられたまま疾走するトップ・レーサー。アルプスの美しい風景をバックに、生命ぎりぎりのサスペンス＆スリルが加速する。R・トーマス監督の息詰まる演出が見もの。", "1956-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALIFORNIA　HOLIDAY", "カリフォルニア万歳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "E・プレスリーの映画出演10周年を記念して製作されたミュージカル。プレスリー扮する巡業歌手マイクは独身主義で、仲間とバンドを組み気ままな旅暮らしをしている。ある街で、実業家の令嬢に惚れられた彼は、彼女の父親の金持ち根性が気に入らず最初は突き放すが、その愛らしさに次第に心惹かれていく。ところが、モテモテのマイクはさらに二人の女性に言い寄られ……。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄飛鴻 男兒嘗自強", "ワンス・アポン・ア・タイム・イン・チャイナ　天地大乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "中国最大の英雄・黄飛鴻（ウォン・フェイフォン）の活躍を描いた“ワン・チャイ”シリーズの第2弾。医学学会に出席するために広州を訪れた黄飛鴻一行。そこで彼らに秘密結社・白蓮教徒が襲いかかる。黄は孫文と出合いともに打倒白蓮教を目指す。乱世の清朝末期を舞台に、動乱期の中国が持つ異文化との相克というドラマが展開する。最大の見どころであるワイアーを使っての格闘シーンは、功夫アクションの魅力をあますところなく見せる。シリーズ中唯一、全国ロードショー公開された作品。", "1993-09-30", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("からたち日記", "からたち日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "地方都市で芸者をしていた増田小夜の波乱に富んだ半生をふり返った自伝『芸者』を映画化。題名には原作とはまったく無関係の島倉千代子の当時の大ヒット曲の曲名が使われている。地主の家の子守をしていた主人公が芸者の家に売られ、やがて敗戦後の生活苦の中で百姓になることを決意するまでを描く。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Law and Order", "法と秩序", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE OF THE CLOCKS", "ルチオ・フルチのクロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "人里離れた山奥にある古ぼけた屋敷で次々と起こる惨劇。そこには恐ろしい秘密が隠されていた……。「サンゲリア」などイタリアン・ホラーの旗手ルチオ・フルチが自ら原作も担当した作品。タイトル通り“時計”を巧みに使った時間逆転のトリック、ブラック・ユーモアの効いた皮肉な結末など、フルチ監督独特の演出と残酷シーンたっぷりのシュール・ホラー。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN KICKBOXER１", "アメリカン・キックボクサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "マーシャルアーツの元世界チャンピオンと空手の世界チャンピオンが競演する格闘アクション映画。キックボクシング界のスター、BJは、ある祝勝パーティーでケンカに巻き込まれ、誤って人を殴り殺してしまう。一年後出所したBJはパーティーでケンカ相手デナーが、反則スレスレの汚い戦法で、新チャンピオンになっていることを知る。BJはデナーを倒し、再びチャンピオンになることを決意する。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE HASARD ET LA VIOLENCE", "潮騒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "国際的ジャーナリストでもあるラブロの原作の映画化。犯罪心理学者ローランは、ある日、魅力的な28歳の女医と恋仲になる。それをきっかけに、彼は暴力事件に巻き込まれていく。暴力の視点から人間の存在を探求した問題作。", "1974-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN THIS WORLD", "イン・ディス・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「24アワー・パーティ・ピープル」のマイケル・ウィンターボトムが、難民問題に真正面から取り組んだロード・ムービー。難民キャンプを脱出し、イギリスを目指すアフガン人少年の長く過酷な旅の模様をドキュメンタリー風の映像で追っていく。次々と試練に襲われながらもただ進み続ける少年の姿が胸を打つ。パキスタンのペルシャワール。ここの難民キャンプで暮らすアフガン少年のジャマールと従兄弟のエナヤットは、密入国業者の手はずにより、希望を求めてロンドンへと旅立つ。二人は様々な危険を切り抜けながら夢の街、ロンドンを目指すが……。", "2002-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モンゴリアンB・B・Q", "モンゴリアンB・B・Q", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "刑務所から６年ぶりに出所した精二と彼を迎える健。再会を祝う二人だったが、彼らには会わねばならない女性がいた……。かつて一人の女を愛した男たちが織り成す不思議な愛情関係を、独特の映像の中に描き出す異色ファンタジー。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Believer〈ビリーバー〉", "Believer〈ビリーバー〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「星に願いを。」など好青年役のイメージが強い吉沢悠が、初の悪役に挑戦。全編を渋谷で撮影し、若者の街のリアルな姿を盛り込んで、スピード感あふれる青春映画に仕上げた。松重豊、田中要次、諏訪太朗といった名バイプレーヤーたちが脇を固めている。かつて超能力少年と呼ばれたが一度の失敗で大嘘つきと言われ、今は詐欺師として生計を立てる英治。仕事は順調だが、過去の傷は癒えない。ある日、ビルの屋上から女が落ちてきた。英治が介抱すると、女はしつこくつきまとい……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HOUSE ON 92TH STREET", "Gメン対間諜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "セミ・ドキュメンタリー映画の先駆けとして知られるサスペンス・ドラマ。第二次大戦中、ドイツ系米国人のビルが、ナチスからスパイになるように誘われたことをFBIに通報する。ビルは大物スパイ摘発をもくろむFBIの指示に従ってハンブルグのスパイ養成所に入所し、ニューヨークへと戻ってくるのだが……。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("懲役太郎・まむしの兄弟", "懲役太郎・まむしの兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "菅原文太と川地民夫の名コンビによる“まむしの兄弟”シリーズの記念すべき第１作。前科12犯の通称ゴロ政こと政次郎と、兄弟分の勝次が神戸新開地のヤクザ組織を相手に一歩も引かぬ大暴れを演じる。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A WOMAN OF AFFAIRS", "恋多き女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "結婚を誓い合ったダイアナとネヴィル。だが、ネヴィルの父の猛反対にあい、仲を引き裂かれてしまう。やがてダイアナはほかの男と結婚するのだが……。C・ブラウン監督が、ハイソサエティーを舞台に描く悲恋のメロドラマ。G・ガルボとJ・ギルバートという、サイレント期のハリウッドを代表する美男美女が共演。", "1928-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ВЕЛИКАЯ　ОТЕЧЕСТВЕННАЯ", "大祖国戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "第二次世界大戦中のソ連対ナチス・ドイツのショッキングな場面を捉えた戦争ドキュメンタリー。ソ連国防省が長い間隠し続けていたというフィルムを中心に構成されている。撮影では、ソ連の従軍カメラマン236名が戦場へ赴き40名が死亡したという。そんな彼らが撮った膨大な数のフィルムをメインに編集された。ドイツ軍が民間人を暴行する姿、至近距離での大爆発など衝撃の映像を収める。", "1965-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jリーグを100倍楽しく見る方法!!", "Jリーグを100倍楽しく見る方法!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "日本初のプロサッカー“Jリーグ”熱に沸いた1993年の様々な名試合を収めた実写部分と、人気選手のキャラクターによる短編アニメが合体した企画もので、初めてのJリーグ公認映画である。製作母体となったTBSテレビがらみで集められたユニークな声優陣も話題となった。同趣向の「プロ野球を10倍楽しく見る方法」と同じく、漫画家のいしいひさいちがアニメ部分のキャラクター原案を担当している。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女たちは二度遊ぶ", "女たちは二度遊ぶ", "", "", "http://image.eiga.k-img.com/images/movie/55558/315x210.jpg?3332", 0, "芥川賞作家・吉田修一のオムニバス小説を、「世界の中心で、愛を叫ぶ」の行定勲監督が映像化。携帯用動画配信サービス「Bee TV」専用に制作されたが、異例の累計900万ダウンロードを記録し、10年に劇場公開が決定した。「どしゃぶりの女」「自己破産の女」「夢の女」「平日公休の女」「つまらない女」の5つのエピソードで構成され、相武紗季、水川あさみ、小雪、優香、長谷川京子がそれぞれ主人公を演じる。", "2010-07-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秩父水滸伝 必殺剣", "秩父水滸伝 必殺剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "村松梢風の梢風名勝負物語『秩父水滸伝』の映画化。明治17年の秩父地方で剣術の腕を競い合う小野派一刀流と甲源一刀流の対決を描いていく。小野派の“秩父の小天狗”の異名をとる早乙女玄吾は、毎年秩父神社で開催される奉納試合に情熱を燃やすが、甲源派はヤクザと手を結び、非道を重ね始めていた。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE HAS MANY FACES", "アカプルコの出来事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "愛が冷めかけた一組のカップルを中心に、そこに割り込んできた一人の女をめぐる三角関係を大胆な筆致で描く。セックス・シンボル、L・ターナーがその妖艶な魅力を披露するメロドラマ。実話をもとにしたもので、ステファニー・パワーズが割り込み女を演じている。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヘブンズ・ドア", "ヘブンズ・ドア", "", "", "http://image.eiga.k-img.com/images/movie/53408/315x210.jpg?2354", 106, "余命わずかと宣告された28歳の勝人は、病院で幼い頃から入院生活を続けている14歳の少女、春海と出会う。「死ぬ前に海を見たい」という春海を連れ、勝人は病院を抜け出し海を目指すが、盗んだ車には謎の大金が積まれていて、怪しい組織や警察から追われる身に……。1997年のドイツ映画「ノッキン・オン・ヘブンズ・ドア」を原案に、「鉄コン筋クリート」のマイケル・アリアス監督が初の実写映画を手がけた青春ロードムービー。主演は長瀬智也、福田麻由子。", "2009-02-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兄いもうと", "兄いもうと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "室生犀星の小説を映画化。家族愛、兄弟愛を描いた佳作。多摩川の寒村に働く赤座という男に、３人の子供がいた。長男の伊之と、妹のもんとさんである。ある日、東京で奉公していたもんが、妊娠して帰ってくる。相手は小畑という学生だが、行方知れず。家族の反応も冷たく、肩身の狭い思いをするもんはやがて子供を死産し、家を出ていく。そんなある日、相手の学生がもんを訪ねて姿を現すが……。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Day of the Dead", "死霊のえじき", "", "", "http://ecx.images-amazon.com/images/I/2172WTP49NL._SL210_.jpg", 102, "　ゾンビは増殖を続け、地球を埋め尽くそうとしていた。軍の地下基地ではサラを始めとする科学者たちがゾンビに関する研究を行なっていた。ローガン博士はバブと名づけたゾンビを飼いならしていたが、肝心の研究は遅々として進まず、人間同士の対立も絶えない。そんな争いの果てに、基地内にゾンビたちが侵入。サラたちの運命は？　ジョージ・A・ロメロ監督による「ゾンビ」シリーズの第3弾。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLAME OF BARBARY COAST", "炎の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "20世紀初頭のサンフランシスコ。モンタナの牧童デュークは、バーバリー・コーストを支配するライト・モレルの借金を取り立てに、当地に乗り込む。やがて彼はモレルと対立する勢力家にのし上がり……。J・ウェインが製作・主演を兼ねたアクション大作。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La commare secca", "殺し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "　詩人として活躍していたベルナルド・ベルトルッチの処女作。原案のピエロ・パオロ・パゾリーニのもとで助監督を務め、21歳で監督デビューした。ローマ、テーベレ川の土手で中年娼婦の死体が発見される。こそ泥、娼婦相手に高利貸しを持ちかける男、同性愛者の男、そのレインコートを盗んだ少年2人組など次々と浮かび上がる容疑者。彼らの証言からたどり着いた事件の真相とは……。", "1962-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROSE MARIE", "ローズ・マリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "北西騎馬警官マイクは、死んだ猟師の娘ローズ・マリーを引き取って育て、美しく成長した彼女を愛するようになる。しかし彼女が思いを寄せるのは荒くれ猟師ジェームズ。その彼に殺人の容疑がかかって……。主題歌『ローズ・マリー、アイ・ラブ・ユー』が甘く切なく胸に染みわたる。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OH，BABYLON", "バビロン 愛欲の予言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "エウリピデスの戯曲『バッカス祭の女たち』をモチーフに映画化。古代ギリシャの薄着をまとった女たちが繰り広げる祝祭、海辺での幻想的な饗宴シーンなど、奇怪なムードが漂う。愛にも恋にも無関心な合理主義のインテリ男をめぐる摩訶不思議な世界が、けれんみたっぷりに綴られる異色作。", "1988-01-01", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナイン　トゥ　イレブン", "ナイン　トゥ　イレブン", "", "", "http://ecx.images-amazon.com/images/I/512v8%2B8IuZL._SL210_.jpg", 90, "堀田ゆい夏、谷桃子、百瀬実咲ら人気グラビアアイドル共演によるファンタジックドラマ。食品メーカーのお客さま相談センターで働く香、自分の価値を高く見せるためにウソばかりついている咲、理想と現実の差に悩む貧乏女優ゆい。夜9時から11時までしか営業しない不思議なコンビニを訪れた3人は、そこで扱っている「夢がかなう」という商品により、思い描いてきた願望を現実のものとする。しかし、そんな3人を意外な運命が待ちうけていた。", "2010-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大菩薩峠・甲源一刀流の巻", "大菩薩峠・甲源一刀流の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "中里介山の未完の同名小説を渡辺邦男監督・片岡千恵蔵主演で映画化した“大菩薩峠”シリーズの第１部。徳川末期・大菩薩峠で机龍之助は巡礼を試し斬りにし、奉納試合で因縁の宇津木文之丞を倒す。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("George Harrison: Living in the Material World", "ジョージ・ハリスン　リヴィング・イン・ザ・マテリアル・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/57248/315x210.jpg?2126", 210, "「ザ・ビートルズ」のギタリストで2001年に58歳の若さで他界したジョージ・ハリスンの人生の軌跡をたどる音楽ドキュメンタリー。監督は「ラスト・ワルツ」「ザ・ローリング・ストーンズ　シャイン・ア・ライト」など音楽映画の名作を生み出してきたマーティン・スコセッシ。貴重な未公開映像やハリスンを取り巻く人々のインタビューで構成され、ビートルズ時代の名曲やエリック・クラプトン、ボブ・ディラン、ロイ・オービソンらとコラボレーションをはじめ、50曲以上にのぼるハリソンの楽曲が物語を彩る。", "2011-11-19", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Return of the Jedi: Special Edition", "スター・ウォーズ　ジェダイの復讐　特別篇", "", "", "http://ecx.images-amazon.com/images/I/51ZWX2VQGFL._SL210_.jpg", 137, "「スター・ウォーズ」シリーズ20周年を記念して製作された、第3作「ジェダイの復讐」（1983）の特別篇。ついに訪れた帝国軍と反乱軍の最終決戦、そしてルーク・スカイウォーカーと父ダース・ベイダーの宿命の対決を描く。特別篇での主な変更点は、ジャバ宮殿の演奏シーンにおけるミュージシャンやダンサーの追加、砂漠に棲む怪物サーラックの修正、エンディング直前の各地で勝利を祝うシーンの追加など。", "1998-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEVER SAY NEVER AGAIN", "ネバーセイ・ネバーアゲイン", "", "", "http://ecx.images-amazon.com/images/I/51TCiPTxMGL._SL210_.jpg", 134, "“ボンド役はやっぱりコネリー”という圧倒的な声にこたえて製作されたシリーズ番外編。「サンダーボール作戦」のリメイクで、再び宿敵スペクターと対決する。鮫と闘う水中シーンをハイライトに、新兵器ありロマンスありの楽しい一編。12年ぶりにボンド役を演じたコネリーが生き生きと好演。", "1983-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WINGS", "つばさ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "“航空映画”の元祖とされる無声期の大作。幼なじみの若い男女、メアリーとデビッドの恋のさやあてを軸に、第一次世界大戦下のフランス戦線で激闘する米空軍の勇姿を描いている。C・ボウの魅力あふれる演技や、後に大スターとなるG・クーパーの端役出演、「ロイドの人気者」のJ・ラルストン（この映画の相手役、R・アーレンとのちに結婚)、第１回のアカデミー作品賞受賞と話題にはこと欠かない作品。また、本作は効果音と音楽入りの“サウンド版”無声映画であり、本国での上映の際にはスペクタクル場面になると画面の両端が左右に開いてワイドスクリーン化する“マグナスコープ”も用いられた。", "1927-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BLANCA PALOMA", "アントニオ・バンデラス 暴走遊戯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "スペイン・バスク地方では、学生たちの暴動は日々過激さを増していた。その中心的役割を担っている多感な青年マリオは、暴力的な毎日に鬱屈している。一方、落ちぶれた安酒場“白いハト”の娘ローズもまた、この町に嫌気がさしていた。そんなマリオとローズは一目で激しい恋に落ち、何もかも捨てる決心をする。自由を求めて酒場に火を放つマリオ。だが、事態は予期せぬ方向へと向かうのだった……。A・バンデラスが複雑すぎる心の痛みゆえに暴走するしかなかった青年マリオ役を好演している。", "1990-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDERWORLD", "アンダーワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "犯罪者が巣く体う闇の世界を舞台にしたバイオレンス活劇。仲間に裏切られ、服役していたジョニーが７年ぶりに出所。父に重傷を負わせ、自分を牢屋に送った裏切り者を探すべく、彼は親友の協力を得て行動を開始。しかし、残酷極まりないやり口で報復行為に出たジョニーを、罠が待ち受けていた……。「恋する泥棒」のD・レアリーが、激情的だが、どこか憎めないジョニーを妙演。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Afterglow", "アフターグロウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "　若きエリート・ビジネスマンのジェフリーとその妻マリアン、そして便利屋のラッキーと元女優だった妻のフィリス、その二組の夫婦の関係を描いた物語。夫とのすれ違いに不満を抱えていたマリアンは自宅の出張工事を頼んだことからラッキーと知り合い、不倫関係に。そしてジェフリーとフィリスもいつしか心を通わせていく。モントリオールを舞台にアラン・ルドルフ監督が可笑しくも切ない男女の関係、悲痛な過去の秘密、といったお得意の題材をロマンティックに描く。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("栄光への5,000キロ", "栄光への5,000キロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 177, "パリ～ダカール・ラリーと並ぶ過酷な自動車レース、サファリ・ラリーに挑む日本人ドライバーの勇気と栄光を描く石原プロ製作の超大作。世界に通用する国際映画を作ろうとする製作者・石原裕次郎の熱い思いがこの作品となって結実した。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴行切り裂きジャック", "暴行切り裂きジャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "ひょんなことから人を殺してしまったユリとケン。彼らは、その時の異常な興奮が忘れられず、殺人を伴わなければセックスの快楽を味わえなくなってしまう。そして次々と殺人を繰り返していくことに……。長谷部安春が、前作「犯す！」同様、アクション映画タッチで描いたポルノ。主演の桂たまきが好演。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デジモンセイバーズ THE MOVIE 究極パワー！バーストモード発動!!", "デジモンセイバーズ THE MOVIE 究極パワー！バーストモード発動!!", "", "", "http://image.eiga.k-img.com/images/movie/33768/315x210.jpg?4751", 20, "大人気アニメ「デジモンセイバーズ」の劇場版。ケンカ好きな少年・大門大と、はぐれデジモンのアグモンが、デジモン事件の捜査組織“DATS”のメンバーとなり悪のデジモンと闘う姿を描く。オリジナル・ストーリーの劇場版では、世界の滅亡を目論むアルゴモンによって永遠の眠りについてしまった大たち人間を救うため、アグモンたちが凶悪なデジモンたちと激しい闘いを繰り広げる。お笑い芸人のはなわがアルゴモンの声を担当。", "2006-12-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Take This Waltz", "テイク・ディス・ワルツ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "女優サラ・ポーリーの長編監督第2作。「アウェイ・フロム・ハー君を想う」で認知症に直面した老年夫婦の愛と葛藤を描いたポーリーが、今度は結婚5年目の若い夫婦に訪れる変化を描いた。結婚して5年がたつマーゴとルーは、まだ子どもはいないが仲睦まじく穏やかな日々を過ごしていた。そんなある日、マーゴは仕事で訪れた島で出会った情熱的な青年ダニエルにひかれるものを感じてしまう。さらに、ダニエルが偶然にも自分たちの家のすぐ向かいに住んでいることを知り、マーゴの心は揺れ動いていく。主演は「マリリン 7日間の恋」のミシェル・ウィリアムズと、「50/50 フィフティ・フィフティ」のセス・ローゲン。", "2012-08-31", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANKENSTEIN AND THE MONSTER FROM HELL", "フランケンシュタインと地獄の怪物〈モンスター〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "若き外科医サイモンは、墓場から死体を盗んでは、人造人間作りの研究をしていた。だが警察に知られ、精神病院へ送り込まれる。ところが、そこには死んだと思われていたフランケンシュタイン博士が、主治医として働いていたのだ。サイモンは博士の助手となり、病人を診察するかたわら故意に死に追いやり、人造人間作りに必要なパーツを集めた。やがて呪われた怪物がついに完成した……。「ドラキュラ」「狼男」などハマー・フィルムで多数の作品を監督したT・フィッシャーの遺作。作品の設定が何とも異様で怖さをいっそう盛り上げる。", "1973-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽の墓場", "太陽の墓場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "血を売る商売の男と愚連隊の話をモチーフに、助監督石堂淑朗との共同脚本を映画化。舞台は大阪のドヤ街。愚連隊信栄会の会長信は、大きな縄張りをもつ大浜組に対抗すべく、厳しい規律で子分たちを使い、会を強大なものにしようともくろんでいた。しかし、小遣い銭欲しさから、ヤスたちは信の目を盗んで、日雇いたちから血を採る商売を手伝う……。非人間的な状況の中で非人間的に生きる人間たちを凝視する大島渚監督の第3作。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪の華", "悪の華", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "芸能界のドス黒い人間模様を描く愛憎ドラマ。花山エージェンシーの看板アイドルが頬をナイフで切られる事件に巻き込まれ、仕事を次々とキャンセルされる。その代役として人気を得たのは赤井マリという新人タレントだった。フリーの芸能ライター、陽子は、マリが所属するハイライトプロの若き社長、新城研一郎の素性を探るため接近するが……。複雑な過去を引きずりながら大手プロダクションへの復讐に挑む一匹狼を、大沢樹生がニヒルに演じている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("婦系図・湯島の白梅", "婦系図・湯島の白梅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "泉鏡花の小説『婦系図』を原作として脚色した作品。早瀬主悦は、芸者あがりの愛人・お蔦と同棲していたが、師匠に別れるように勧められる。恩師の言葉にそむけず主悦は、お蔦に別れ話をもちかけるが、お蔦は病床に伏す身となってしまう……。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dolphin Tale", "イルカと少年", "", "", "http://ecx.images-amazon.com/images/I/51UCm4ynjCL._SL210_.jpg", 113, "", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("苺の破片〈イチゴノカケラ〉", "苺の破片〈イチゴノカケラ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「でらしね」の中原俊監督と、「スカイハイ」などで絶大な人気を誇る漫画家、高橋ツトムが共同監督で作りあげた感動作。愛する人の死を引きずる女性漫画家の再起と女同士の友情が描かれる。深い失望にある女性の心情をしっかりと見つめた主演女優、宮澤美保と梶原阿貴による脚本が秀逸。漫画家のイチゴは、自作の主人公にした憧れの人が亡くなってからスランプに。マネージャーの知子は、彼女を立ち直らせようとするが……。", "2005-02-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Town", "ザ・タウン", "", "", "http://image.eiga.k-img.com/images/movie/55398/315x210.jpg?4303", 123, "チャック・ホーガン原作の「強盗こそ、われらが宿命（さだめ）」を、ベン・アフレック監督・主演で映画化した犯罪ドラマ。全米一、銀行強盗発生率の高い街ボストン・チャールズタウン。そこで育ったダグ（アフレック）は当然のように強盗を生業として暮らしていた。だがある日、強盗に入った銀行の行員クレアに一目惚れをしてしまう。そんなダグにFBIの捜査の手が迫る……。共演にレベッカ・ホール、クリス・クーパー、ジェレミー・レナー。", "2011-02-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOUNG LADY CHATTERLEY", "ヤング・チャタレイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "イギリスの文豪ロレンスの問題作の再映画化だが、今作はチャタレイ夫人の姪をヒロインにしてアレンジされたリメイク作品。官能映画の名作「エマニエル夫人」の世界的ヒットに刺激を受けて作られた一本だといえる。古い祖母の日記を読んで妄想をたくましくした姪は、祖母と同じような行為にふけり始める。", "1977-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AGE OF LULU", "ルルの時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "男と女とゲイの男、３人が交わす狂気と快楽の世界を描く、スペインの同名ベストセラー小説の映画化。15歳のルルは、兄の友人パブロに憧れていた。ある時、誘われるままにパブロの家へ行き、二人は結ばれる。ルルは、彼にどんな行為を求められても応じた。やがて二人は結婚するが、次第に普通の愛では満足できなくなっていく。", "1991-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN EXILE", "EX エグザイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "M・ハミル主演のSFアクション。2021年、地球はエイリアンに征服され、無差別な殺りくが繰り返されていた。家族を惨殺され、危機一髪のところで宇宙船に乗り込んだレイナーは、エイリアンの執拗な追撃を避けるべくブラックホールへと飛び込む。未知の空間から抜け出し、タイムスリップの果てにたどり着いたのは1991年の地球だった。ここでもエイリアンが潜入し、ひそかに地球侵略の準備を進めていた。凶悪なエイリアンから人類を守り地球の未来を救うため、レイナーはたった一人で戦うことを決意する。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("季節風", "季節風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ふと知り合った売れっ子モデルへの憧れから東京に出てきた浪人中の青年が、都会における様々な苛酷な体験を積み、最初からやり直す決心で故郷へ戻っていくまでを描く。野口五郎の同名ヒット曲を素材にした歌謡映画。大竹しのぶと田中邦衛が好演。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOW DRY", "シャンプー台のむこうに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「フル・モンティ」のS・ボーフォイの脚本を映画化したハートフル・ドラマ。ヨークシャー地方の田舎町で全英ヘアドレッサー選手権が開催される。“伝説のハサミ師”と呼ばれたフィルに、別居中の美容師の妻シェリーが一緒に選手権に出場しようと持ちかける。妻がヘア・モデルの女性と家出して以来すっかり落ちぶれていたフィルは、新米美容師の息子ブライアンの後押しもあって、参加を決意。バラバラになった家族は選手権優勝を目指して絆を回復していく。美容師たちが鮮やかなテクニックを競い合う熱気あふれる選手権で、彼らが作り上げる奇抜でゴージャスなヘアスタイルが見事。溝ができた両親の間で板ばさみになる心優しい息子をJ・ハートネットが誠実感たっぷりに好演する。", "2000-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FILLE DE D’ARTAGNAN", "ソフィー・マルソーの三銃士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "修道院で育った、ダルタニャンの血を受け継ぐエイローズは、凛とした気高さと、貧しい者たちへの愛情にあふれる美しさを放っていた。ある日、修道院に脱走奴隷が逃げ込み、これをかばった院長が悪漢たちに殺されてしまう。敵を討つことを胸に誓ったエイローズは、父のダルタニャンの住むパリへと向かう。アレクサンドル・デュマの『三銃士』に大胆な構想を加え、新たな登場人物、エイローズが女騎士へと変身していく姿を描いた冒険ロマン。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUX YEUX DU MONDE", "愛を止めないで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「愛さずにいられない」で一躍注目を浴びたE・ロシャンの第２作。遠く離れた恋人に会うためにスクール・バスをジャックした青年ブリュノ。運転手にピストルを押しつけ、泣き叫ぶ子供たちに手を焼きながらひた走る彼の無軌道な愛を鮮烈に描く。運転手に扮したM・ベルマンの人間味あふれる演技も味わい深い。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ВРЕМЕ РАЗДЕЛНО", "略奪の大地 Time of parting", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 164, "オスマン・トルコ帝国の支配下にあった17世紀のブルガリア。イスラム教への改宗を迫るオスマン帝国に対し、徹底して反逆したキリスト教徒たちの悲劇を描いた歴史的叙事詩。改宗しない者を串刺しにし、虐殺を行うトルコ軍の残虐行為が、まるで地獄絵図のように映し出されている。", "1988-01-01", "65", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLOOD：RIVER’S　RAMPAGE", "フラッド・クラッシャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1993年にミシシッピー州一帯を襲った大洪水の実話をベースとしたパニック・ドラマ。災害の恐怖そのものをことさらスリリングに描くのではなく、何とか洪水を食い止めようと堤防作りに励む地元の人々の奮闘ぶり、浸水した家屋に取り残された市民のサバイバル劇などを丁寧に描き込んでいる。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("氷壁", "氷壁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "増村保造の映画には時々主題の展開の仕方、キャラクター、設定などに強い類似をもつものがあり、この映画も「妻は告白する」の姉妹編といえる。登山中に死んだ友人の愛した人妻に心惹かれた男に待ち受ける悲劇を、社会批判を交え鮮烈に描く。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メールで届いた物語", "メールで届いた物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "個性豊かな４人の監督がメールにまつわる４篇のストーリーを競作、タイプの異なるショートフィルムを集めたオムニバス。とはいえ、１本１本が完全に独立しているのではなく、どれもが微妙にリンクする構成。切ないラブ・ストーリーからコメディまで、それぞれの作品が“メール”というアイテムを通して、一つの世界観を創り上げていく。相武紗季をはじめ、北村一輝、加瀬亮、大倉孝ニらバラエティーあふれるキャストが織り成すアンサンブルも見もの。難病の少女と郵便配達人の交流を描く「mail」をはじめ、チェーンメールや架空詐欺請求などの迷惑メールから人の温もりが伝わる手紙まで、“メール”にまつわる４つのエピソードが展開していく。", "2005-05-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER　IN　LAW", "ミリー　招かれざる母", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "家族とともに平穏な生活を送っていたビリーの元へ、10年間消息不明だった母ミリーが現れ、ビリーと妻のアーリーンはミリーを家族の中に迎える。しかし、ミリーは異常な行動や暴力を見せ始め、アーリーンとの確執が深まっていく。そんな時、ミリーの荷物の中から、精神病院の患者用衣服が見つかる。彼女は病院を脱走してきているのだった。人間の精神、それが歪んだときに見せる異形の姿が、恐ろしく胸に迫る作品。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新網走番外地・吹雪の大脱走", "新網走番外地・吹雪の大脱走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "久々に網走刑務所を舞台にした一編。刑務所の物資横流しなど監獄内を我がもの顔でのさばる熊沢グループの悪どいやり口に、末広勝治の怒りが爆発する。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版 TRIGUN Badlands Rumble", "劇場版 TRIGUN Badlands Rumble", "", "", "http://image.eiga.k-img.com/images/movie/55058/315x210.jpg?1357", 0, "内藤泰弘の人気SFコミックを映画化。舞台は近未来、流砂に囲まれた町・マッカ。伝説の大強盗ガスバックに町を狙われた市長のケプラーは、ガスパックを捕らえるため、利き腕の賞金稼ぎを集めていた。賞金を目当てに次々と集まってくる賞金稼ぎたちの中には、真紅のコートに身を包んだ伝説的ガンマン、ヴァッシュ・ザ・スタンピードの姿があった。", "2010-04-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fly Away Home", "グース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "14歳の少女エイミー・アルデンは、事故で母親を失い、10年間会ったことのない父親のトーマスに引き取られる。トーマスはカナダの大自然のなかの農場で、彫刻を彫ったり、グライダーで空を飛んだりする変わり者だった。そんな父にもなじめず、鬱々としていたエイミーは、ある日、森の中でグースの卵を発見する。卵がかえり、16羽のグースの母親になってしまったエイミーは、彼らに飛び方を教え、南の越冬地まで連れて行かなければならなくなる。グースのために協力するエイミーとトーマスはグースたちの訓練を通じて、理解と信頼の絆を取り戻していく。カナダで実際にあった話を映画化、そのモデルのB・リッシュマンが協力している。本物のグースが軽飛行機とともに空を飛ぶシーンが、ファンタスティックな感動を呼ぶ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("H story", "H story", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「M／OTHER」の諏訪敦彦監督が「ガーゴイル」のベアトリス・ダルを主演に起用、名作「二十四時間の情事」のリメイクに挑戦。その企画が消滅していく過程をスリリングに画面に収めていく。諏訪敦彦監督による「二十四時間の情事」のリメイクの撮影が始まった。が、主演のベアトリス・ダルはシナリオの言葉に違和感を感じ、演技ができなくなってしまう。そこに作家・町田康が現れる。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLUE", "殺人ゲームへの招待", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "参加者すべてが容疑者であり、探偵であるというゲーム“クルー”。これは、そのゲームの面白さをそっくり映画に移し替え、とある大邸宅で起きる殺人劇として描いたミステリー。1954年、大邸宅で豪華なパーティが開かれる。招待客は地位も名誉もある者ばかり。やがて、ひとりまたひとりと不審な死を遂げる。果たして犯人は誰なのか……？ E・ブレナン、M・カーンをはじめクセのある役者たちの演技も見もの。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("失楽園", "失楽園", "", "", "http://ecx.images-amazon.com/images/I/211CGC06JBL._SL210_.jpg", 119, "渡辺淳一のベストセラー小説の映画化。出版社に勤務する久木は突然、閑職である調査室行きを命じられる。これまでの人生に虚無感を覚え始めた彼の前に凜子という美しい人妻が現れ、情事を重ねるように。やがて彼らの仲は、久木の妻、凜子の夫、そして久木の会社にも知れることになる。世間から孤立し、愛を深めていく久木と凜子が辿り着いた場所とは……。失楽園という言葉が流行語大賞に選ばれるほどのブームを呼んだ。", "1997-05-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("祇園の姉妹", "祇園の姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "溝口健二が戦前に撮り上げた女性ドラマの名作のリメイクは、長回しを多用しないテンポある演出で、より大衆性を増した作品に仕上げられた。人気者の祇園の3芸者は、実の姉妹と姉の娘という組み合わせだ。娘役には中村玉緒が扮し、かわいく健気に演じている。彼女たちの元に、姉と離婚したはずのお坊ちゃん育ちの夫が戻ってきたことから、姉妹の間に亀裂が生じる。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("踊る大捜査線 THE MOVIE 2 レインボーブリッジを封鎖せよ！", "踊る大捜査線 THE MOVIE 2 レインボーブリッジを封鎖せよ！", "", "", "http://image.eiga.k-img.com/images/movie/1138/315x210.jpg?3474", 138, "大ヒットした前作の5年後を舞台に、前作の主要キャスト＆スタッフが再結集。観光名所となったお台場では、凶悪事件が続発。やがて殺人事件が発生し、湾岸所轄内の殺人事件特設本部長に初の女性管理官、沖田警視正（真矢みき）が任命される。第2の殺人が発生するが、犯行動機は不明のまま。さらに警察機構内にも不穏な動きが起こる。この5年間のキャラたちの部署異動、昇格や降格が描かれるなどファンサービス満載。", "2003-07-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN UNA NOTTE DI CHIARO DI LUNA", "ムーンリットナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "自分をエイズ・キャリアだと偽ってヨーロッパ各地での“差別意識”について取材するため、次々と人に接するジャーナリストのジョン。だがある日、彼が一度だけ関係を持った女性がエイズで死亡、そして自分もエイズに冒されていることを知って……。「流されて…」など、ギャップのある愛を描き続けてきた女流監督ウェルトミューラーが、困難なテーマに意欲的に取り組んだ渾身の力作。", "1989-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY FIRST 40YEARS", "マリーナの甘い生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "戦後の1950年代、経済成長期の1960年代、衰退の1970年代、そして現代に至るまでの40年間のイタリアを、一人の女性の半生を通して描く、ロマンティック・コメディ。恋多き美女の自由奔放な半生とともに、その時代での有名なエピソードを交じえながら綴る、ちょっぴりほろ苦くて心温まる青春ドラマ。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Central Park", "セントラルパーク", "", "", "http://image.eiga.k-img.com/images/movie/57308/315x210.jpg?4565", 176, "1967年のデビュー以降、現代アメリカ社会を独自の視点で見つめ続けてきたドキュメンタリー作家フレデリック・ワイズマン監督が、ニューヨーク市のランドマークとして知られるセントラルパークを題材に製作したドキュメンタリー。日常的な運動の場としてはもちろん、音楽や演劇の発表の場としても人々に親しまれているセントラルパーク。一方で公園を維持し一般に開放するため、市の公園課が様々な問題に対処すべく日々奮闘を続けていた。2011年、ワイズマン監督作を一挙上映する「フレデリック・ワイズマンのすべて」にて日本初公開。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CIOCIARA", "ふたりの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 185, "第二次大戦下のイタリア。戦火を逃れて故郷の山に戻ってきた母娘は、そこでシャイなインテリ青年ミケーレと出会う。ミケーレは母の方に恋するが、ミケーレに恋するのは娘の方だった。一人の女と一人の女の戦いが始まったのだ……。たくましく、そして美しく生きるイタリアの女、イタリアの母をイタリア映画の“ディーヴァ（女神）”ローレンが演じる愛の物語。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらば愛しき大地", "さらば愛しき大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "「十九歳の地図」など、社会によって疎外され、反抗心をたぎらせた若者像を描き続ける柳町光男監督が、茨城県・鹿島地方の工業団地化を背景に、郷里の荒廃、人間の堕落していく姿を力強いタッチで描いた力作。映画の中で度々象徴的に現れる、風に立ち騒ぐ稲穂を捉えた田村正毅のカメラが美しい。当時問題になり始めた覚醒剤やジャパゆきさんの問題をしっかりと作品の中に取り込み、病める農村の風土的リアリティーを出すことに成功している。農業からダンプカーの運送屋に転業した幸雄は、仲間に勧められて覚醒剤に溺れていく。最愛の二人の息子が沼で水死したことから、幸雄はますます覚醒剤にのめり込み、幻覚の末、ついに愛人を殺す。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使の牙〈B.T.A.〉", "天使の牙〈B.T.A.〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "大沢在昌の同名小説を映画化。別人となって蘇った女刑事が、因縁の宿敵打倒のため、奮闘する姿をスタイリッシュな映像で追いかけていく。監督は本作がデビューとなる新人・西村了。麻薬王の君国を負う刑事・明日香とその相棒・古芳。君国の愛人・はつみから保護を求められた明日香は、彼女と接触。ところが、何者かの凶弾によって、二人とも倒れてしまう。その後、明日美の脳ははつみに移しかえられるが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セロ弾きのゴーシュ", "セロ弾きのゴーシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "宮沢賢治の名作をアニメ化するにあたって、本来は作画専門のオープロダクションが、６年をかけて自力で製作、さらに公開するに至った渾身の珠玉作。原作に漂う素朴な叙情性を表現した作画と、椋尾篁の描く牧歌的な田園風景とが溶けあって、画面から詩情を伝えてくる得がたい作品となった。田舎町の楽団のセロ（＝チェロ）奏者の若者・ゴーシュを主人公に、彼が毎夜訪ねてくる動物たちの導きで、見事な演奏をものにするまでが綴られている。演奏会の曲目となる曲にはベートーベンの『田園交響曲』があてられ、それを同時に作品のテーマ曲として、音楽と画面の調和に細心の注意が払われている。鬼才・高畑勲の知られざる秀作。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GET REAL", "同級生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ちょっと気弱な高校生スティーヴンが好きになったのは陸上部のヒーロー、ジョンだった……。ゲイの若者の悩みと苦しみ、そして成長をさわやかに綴った青春映画。スティーヴンをいつも力づける朗らかなお姉さんを「恋はハッケヨイ！」のC・ブリテンが好演している。1998年エジンバラ映画祭観客賞、ディナール英国映画祭審査員賞・観客賞受賞。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE IPCRESS FILE", "国際諜報局", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "原子力の権威ラドクリフ博士がスパイ団に誘拐されたため、諜報局員パーマーが派遣される。捜査の途上パーマーは誤ってCIA局員を射殺してしまい、スパイ団とCIAの両方を敵にまわすことになる。果たしてパーマーの運命はいかに……。M・ケインのシニカルなスパイぶりが光る異色のスパイ・サスペンス。", "1964-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STUNT MAN", "スタントマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "殺人未遂の容疑で警察に追われているキャメロンは、戦争映画撮影中の映画監督イーライにスタントマンとして雇われる。イーライは、キャメロンが逃走中に見せた機敏な動きを買ったのだ。撮影が始まると、キャメロンは主演女優のニナに惚れてしまい、関係を持つようになる。同時に、キャメロンは映画一筋のイーライが自分の命などなくなってもいいと考えていると思うようになり、ニナに一緒に逃げようと誘うが、彼女は取り合わない。いよいよ本番が行われ、キャメロンは見事にスタントに成功する。イーライはキャメロンにヨーロッパでの撮影にも誘うが、彼はそれを断るのだった。P・オトゥールが映画監督に扮し、映画製作の一端を見せてくれるアクション・ドラマ。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地球交響曲　ガイアシンフォニー 第二番", "地球交響曲　ガイアシンフォニー 第二番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "地球の未来にとって示唆に富むメッセージを持つ人々を描いたオムニバス映画。本編では、映画「グラン・ブルー」のモデルとなった海洋冒険家J・マイヨール、全人類の慈悲心の目覚めを唱え続け、1989年ノーベル平和賞を受賞した14世ダライ・ラマ法王、1960年から地球外知的生命体の探査を続けているF・ドレイク、岩木山麓に心を病んだ人たちのための憩いの場を作り、人の生と死に立ち会い、生きる喜びを分かち合っている佐藤初女の活動を描く。", "1995-04-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHO’S HARRY CRUMB？", "ジョン・キャンディの　迷探偵ハリーにまかせろ!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "海運王ダウニングの娘ジェニファーが誘拐された。捜査を依頼された探偵社社長・エリオットは、史上最大の大ドジで田舎に左遷されたハリーを呼び寄せ、この事件を担当させる。実はこの事件は、エリオットがダウニングの後妻の気をひこうと仕組んだものだった。そうとは知らないハリーは事件の真相を追う。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虎の尾を踏む男達", "虎の尾を踏む男達", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "能の「安宅」とその歌舞伎化の「勧進帳」を原案に、源頼朝から終われる身となった義経と弁慶一行の“安宅の関越え”を描く。黒澤は義経一行７人に付き添う軽妙な強力（ごうりき）を新たに配し、よく知られたこの話を、実にに軽快なコメディとして映像化した。大河内伝次郎演じる堂々の弁慶とエノケンのおしゃべりな強力、この対比の妙がこの映画を非凡なものにしている。息詰まる関所での問答とその後の晴れやかでいて物哀しいエピローグ。緩急自在の演出が堪能できる実に中身の濃い中編。", "1945-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL　THE　LITTLE　ANIMALS", "コーンウォールの森へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「ラストエンペラー」「戦場のメリークリスマス」の敏腕プロデューサー、J・トーマスの初監督作品。W・ハミルトンの長編処女小説を映像化したさわやかなヒューマン・ストーリー。施設に入れられるのを恐れた青年ボビーは、ヒッチハイクの旅にでる。その途中で、車に轢かれた動物の埋葬をライフワークにするサマーズと出会い自分自身の命の価値に目覚めていく。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長州ファイブ", "長州ファイブ", "", "", "http://image.eiga.k-img.com/images/movie/33848/315x210.jpg?4818", 119, "黒船来航から10年後の1863年。外国を排除しようとする攘夷の声が高まる中、命をかけてイギリスに密航し、最先端の技術や知識を学んだ5人の若者たちがいた。いずれも後の日本で大きな功績を遺すことになる彼らの青春の日々を、「地雷を踏んだらサヨウナラ」の五十嵐匠監督が鮮烈に描き出す。後に“日本工学の父”と呼ばれる山尾庸三を松田龍平が、後の初代内閣総理大臣・伊藤博文を「ウォーターボーイズ」の三浦アキフミが演じる。", "2007-02-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOD'S LITTLE ACRE", "真昼の欲情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "『タバコロード』等で著名な、アメリカの南部小説の名手コールドウェルの原作をベースに、同時期に西部劇の新しいキャラクターを作り出した名作「西部の人」のアンソニー・マンが監督した一作。一人の女を巡りさまざまな男が争い、家族が崩壊していくという悲劇性を含んだドラマだ。父の遺言を信じ、金を探して土地を掘り続ける家族の姿などユーモラスな場面も。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生きてゐる小平次", "生きてゐる小平次", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "天才の晩年は不遇と相場が決まっているが、中川信夫もその例にもれなかった。ただ、遺作となったこの映画を撮れたのがせめてもの救いか。登場人物が３人だけという実験的な手法に加え、お堂の扉を開けると外がいきなり沼に変わっているなどの恐怖演出に相変わらず冴えを感じさせる。旅役者の小平次は、幼なじみの太九郎の女房・おちかに横恋慕し、怒った太九郎に沼に突き落とされる。太九郎とおちかは、小平次の幻影から逃れるため江戸を離れるが、小平次の影はどこまでも二人に付きまとう。３人はさいの河原で再会し、男二人は相果てる。人間の業を静かに見つめてゾクっとさせる名ラストシーンだ。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("茅山彊屍拳／THE　SPIRITUAL　BOXER　PART２", "霊幻少林拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「少林寺三十六房」の監督・主演コンビによるコミカルなホラー活劇。呪文で死人をよみがえらせては生まれ故郷に届ける霊幻道士と、その弟子。彼らの旅に、指名手配中の拳士がキョンシーのふりをしてまぎれこんだことから、大騒動が持ち上がる。キョンシーと笑い、カンフーを組み合わせた点では、後のヒット作「霊幻道士」の先駆といえる。", "1979-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("早池峰の賦", "早池峰の賦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 186, "岩手県の北上山地の主峰、早池峰山の山麓にある“岳”と“大償”という二つの村落に伝承されている山伏神楽と、その神楽を演じる村人たちの生活を余すところなく伝えた羽田澄子監督・脚本によるドキュメンタリー。８月の早池峰神社の例祭、神楽は宵宮に神社の神楽殿で奉納される。境内にはたくさんの人々が集まり、篝火がたかれ祭りは盛り上がる。翌日は祭りの当日。近所の各家々に祀ってある権現さまを持ち出して、神社への道を列をなして踊る人々の姿が映し出される。神楽がこの村落の住人にとってどれほど心の支えになっているか、また自然と一体となって四季を生きていく山村の人たちと神との結びつきなど、風俗を記録するにとどまらず、自然の中で生きる人々の姿を讃えた秀作。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Dangerous Woman", "欲望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "純粋さゆえに社会に適応できず、伯母に面倒を見てもらっている女性マーサ。ある日、彼女は流れ者の男コリンに惹かれて一夜をともにするが、それが思わぬ事態を招き……。傷つきやすいヒロインに扮した、D・ウィンガーが熱演する文芸ロマン。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新宿欲望探偵", "新宿欲望探偵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "寺尾聰が枯れたキャラクターを渋く演じるハードボイルド・ロマン。かつては敏腕刑事として鳴らしたものの、今ではオカマ・バーで飲んだくれの日々を送る草薙。ある日、彼の面倒を何かとみていた元刑事が何者かに殺された。草薙は恩人の死の謎を探るうちに、巨大な麻薬シンジケートの存在に気づく。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FRUSTA E IL CORPO", "白い肌に狂う鞭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "小間使いを辱しめ、彼女を自殺に追いやったため、父親に勘当されていた男クルトが突然帰ってくる。だが、ある晩、彼は何者かに殺され……。イタリア耽美派の巨匠マリオ・バーヴァによる傑作ホラー。なお、スタッフは別名を使っており、監督のバーヴァはジョン・M・オールド、撮影のウバルド・テルツァーノはデヴィッド・ハミルトンとクレジットされている。", "1963-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古惑仔３之隻手遮天／YOUNG　AND　DANGEROUS３", "新・欲望の街I　古惑仔疾風、再び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "香港暗黒街を当世風の流儀で生き抜くチンピラ“古惑仔（こわくちゃい）”の過酷な青春群像劇。前作同様のスタッフ＆キャストに「天使の涙」のK・モクを新メンバーに加えて、よりスタイリッシュに描き出す大ヒット・シリーズ第３作。ナンたちの属する洪興社と同じく、暗黒結社である東星社がメキメキと頭角を表し始めた。単なる拝金主義的な経済マフィアと思われていた東星社だったが、その内部で血気盛んなタイガーとクロウの二人が、ボスを尻目に洪興社の壊滅を画策。そんな折、洪興社の古惑仔たちは、過去の激しい抗争で記憶を失ったナンの恋人を治療すべく努力を重ねていた。その隙を狙うかのように、東星社の二人は洪興社のボスを殺害。彼らの罠により、その罪を被せられたナンは仲間に追われることになる。しかも、狂暴化するクロウに恋人を殺されナンの怒りは頂点に。再び古惑仔たちと結束した彼は、東星社に対して壮絶な復讐戦を繰り広げる。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Land of Oblivion", "ランド・オブ・オブリビオン（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "2,69,42,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIRACLES STILL HAPPEN", "奇跡の詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1971年に起きた実話をもとに映画化した感動のドラマ。アマゾンのジャングルに旅客機が墜落し、乗客は一人を除いて全員死亡。奇跡的に助かった17歳の少女は、独力でジャングルを踏破し、生還を試みる……。ワニやヘビ、昆虫などが潜むジャングルの様子をグロテスクに描くシーンの迫力はまさに圧倒的だ。", "1974-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OLD GRINGO", "私が愛したグリンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "祖国アメリカを去り、革命の嵐が吹き荒れる異郷の地メキシコへと旅立って行った女、ハリエット。彼女はそこで、世界を変えようとする若き情熱家と、老ジャーナリストという、国籍も人生観も違う二人の男と出会う。彼女はその二人ともに激しくも切ない愛の炎を燃やすのだが……。J・フォンダが主宰するフォンダ・フィルムズの第６回製作作品。全編にあふれる“メキシコの香り”がいい。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAD　ON", "ヘッド・オン！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "同性愛者の青年の苦悩をパワフルな語り口で描き、カンヌ映画祭で話題を呼んだゲイ・ムービー。メルボルン在住のギリシャ移民アーリはゲイの若者。夜の街をさまよい、行きずりのセックスで虚無感を埋める彼だったが、やがて警察に連行され辱めを受け……。生々しい性描写を交えつつ、24時間のできごとを息詰まるような閉塞感とともに捉える。", "1998-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Splendor in the Grass", "草原の輝き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "ハイスクールの同級生、バッドとディーンの二人は、互いに愛し合っているが、セックスについては意識して触れないようにしていた。だが、バッドが他の女の子と関係したことがわかり、ディーンは河に身を投げる。ディーンは入院し、二人は気まずいまま卒業を迎える。そして、バッドはエール大学に入学し、ディーンは医師と婚約。だが、時は1929年。経済恐慌はそれぞれの環境にふりかかってくる。そして数年後、二人は再会する。若者の愛とセックスを通して青春の輝きを描いた人生ドラマ。タイトルはイギリスの詩人ワーズワースの詩句からとったもの。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("憧れのハワイ航路", "憧れのハワイ航路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "夜間中学の教師・岡田はハワイ生まれで、母の死後、父をハワイに残して日本の学校に進学したが、戦争の勃発により父の消息は不明となった。岡田はその父の面影を懐かしみハワイ時代の思い出に日夜を過ごす。本題に母娘の因果話が絡んだりもする、喜劇監督・斎藤寅次郎のマジメな物語。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Michel Bringt die Welt in Ordnung", "いたずら天使ミッシェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「長くつ下のピッピ」でおなじみの人気児童文学作家アストリッド・リンドグレーンによる小説「いたずらっ子エーミール」を基に、作者自らが脚本を手掛け、実写版「長くつ下のピッピ」シリーズのオル・ヘルボム監督がメガホンをとった作品。スウェーデンの小さな村で暮らす5歳の男の子ミッシェルは、いたずらが大好き。いつもいたずらをしてはお父さんに怒鳴られている。そんなミッシェルが巻き起こす大騒動を、ほのぼのと描く。", "1971-01-01", "14,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOOL’S GOLD", "フールズ・ゴールド　史上最大の金塊強奪事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "空港近くの警備会社の保安倉庫に武装した６人の男が侵入した。大量の現金を期待していた彼らが金庫の中で見たものは、時価１億ドル、約３トンに及ぶ金塊の山だった。誰が、何のために、これほどの金塊を預けていたのか。多くのナゾに包まれたまま、彼らは陰謀と裏切りへと導かれていく……。実際に起こった金塊強奪事件のナゾに斬新な解釈で迫るクライム・サスペンス。", "1993-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABE", "ベイブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "純粋無垢な一匹の子ブタが自分の夢に向かって、健気にがんばる姿を描いた感動作。養豚場で生まれたベイブは、遊園地の体重当てコンテストの商品として、農場主のハゴットにもらわれていく。農場には家畜の馬や羊、牧羊犬一家、鶏など様々な動物が暮らしていた。独りぼっちのベイブは、牧羊犬のフライと仲良くなり、家畜社会のルールを学ぶ。そしてベイブは、いつかは人間に食べられるブタの運命を知らずに、牧羊ブタになる決意をする……。全編の80パーセントを動物たちの会話が占めるが、違和感はなく、動物社会は人間社会の縮図を見ているよう。撮影には48匹のブタを使い、また実際の動物で撮影ができない場面では、アニマトロニクスという複製を使い、本物と区別がつかないほど精巧な仕上がりとなっている。ゴールデン・グローブと全米批評家協会の最優秀作品賞を受賞したほか、アカデミー作品賞にもノミネートされた。", "1995-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージーの無責任清水港", "クレージーの無責任清水港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "おなじみ“次郎長”ものがネタに。東海道清水港、売り出し中の清水次郎長も経済成長のひずみで一家は火の車、おまけに鷹岡の勘助が喧嘩の機会を狙っていた。そこへC調の風来坊・追分三五郎がやってきて大騒動が始まる。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彼岸島", "彼岸島", "", "", "http://image.eiga.k-img.com/images/movie/54688/315x210.jpg?2854", 122, "松本光司の人気コミックを実写映画化したサバイバル・アクション。監督は「火山高」「オオカミの誘惑」のキム・テギュン、脚本を「デスノート」シリーズの大石哲也が担当する。行方不明の兄・篤が彼岸島と呼ばれる未知の孤島で生存していることを知った高校生の明は、謎の美女・冷に導かれるまま、仲間とともに彼岸島へ上陸する。しかし、島は吸血鬼と化した人々に占拠されており、明たちは次々と吸血鬼たちに襲われる。主演は「ごくせん　THE MOVIE」「携帯彼氏」の石黒英雄。", "2010-01-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KOKKURI・こっくりさん", "KOKKURI・こっくりさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「ザ・制服レズ／禁男の園」や「終わらないセックス」など、目をみはる充実のピンク映画を連発してきた瀬々敬久監督が、初めて手掛けた一般作品。“こっくりさん”ゲームを始めた３人の女子高校生が、それをきっかけに謎の殺人事件に巻き込まれていく。傑作「本番レズ・恥ずかしい体位」でオカルトを媒介に少女の深淵を浮き彫りにした瀬々は、ここでもホラー・サスペンスの様式を借りながら、過去と現在をめぐる因果や主人公の二面性というテーマに肉迫。視線劇がスリリングなゲーム・シーンや“水”を象徴的に用いたイメージなどに、独自の映像美を見ることができる。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("老井／OLD WELL", "古井戸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "原題の“老井”は小西省太行山中にある村の名。石ころだらけで山に囲まれたその村を舞台に物語が展開する。村人たちは、自分たちの土地から水が得られるようになることを願って、井戸を掘り続けたが、どこからも水は出なかった。ある日都会から戻った青年ソン・ワンチュワンは、自分の学んだ知識を生かして、なんとか井戸を掘りあてようと決意する。彼には将来を約束した村の娘チャオ・チィアオインがいた。しかし、彼の家は二人の結婚を許さなかった。井戸掘りに情熱を傾けるソン青年と、実らない愛とを絡めながら、静かに人物と風景を描いた作品だ。1987年の東京国際映画祭でグランプリを受賞している。チャン・イーモウ監督が主役のソンを熱演。ウー監督は、「標識のない河の流れ」や「人生」、そしてこの作品と、黄河や黄土高原などを舞台とした小説の映画化を一つの方向にしている。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HORS LA VIE", "無防備都市 ベイルートからの証言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "長く激しい内戦が続くレバノンの首都ベイルート。この地を取材していたフランス人のフォト・ジャーナリスト、パトリックは帰国を目前にしたある日、イスラム系武装グループに拉致される。窓を鉄板で遮断し、トイレも洗面所もない一室で、彼の監禁生活が始まった……。死と隣り合わせの生活の中で、活路を見い出そうとする記者の恐怖の体験の数々を描いた社会派サスペンス。", "1991-01-01", "2,4,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ISHTAR", "イシュタール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "W・ベイティとD・ホフマンがコンビを組んだ冒険コメディ。ショービジネス界で見はてぬ夢を追う売れないシンガー・ソングライター二人が、謎の王国イシュタールで米・ソのスパイ戦争にかかわる事件に巻き込まれていく。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIVATE PARTS", "プライベート・パーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "全米で絶大な人気を誇るラジオDJ、ハワード・スターンの自伝的なドラマ。スターン自身が主人公に扮して、痛快なサクセス・ストーリーをリアルに演じている。ハイスクール時代からDJを目指していたスターンは、地方のラジオ局に就職。政治やセックスをネタにした彼の過激なトークは、リスナーに支持される。やがてニューヨークに進出したスターンは、さらにアブナい放送を繰り広げるが、そんな彼を潰そうとおカタい重役連中が動き出し……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY STEPMOTHER IS AN ALIEN", "花嫁はエイリアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "５年前に妻を亡くして以来、研究一筋の科学者スティーブ。ある日彼は実験に失敗し異常ビームを放ったことから、ある惑星が絶滅の危機に。そのピンチを救うために美しい女性エイリアンが地球にやって来た。そうとは知らず彼女に出会ったスティーブは一目惚れ、妻として迎えたのだ。しかし13歳になる娘に継母はエイリアンであることを見抜かれてしまう……。K・ベイシンガーとD・エイクロイドが好演する、宇宙スケールのSFXロマンティック・コメディ。往年の名画のシーンがあちこちに登場するのも見逃せない。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いつでも夢を", "いつでも夢を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "日本レコード大賞を受賞した同名主題歌のデュエット・ナンバーがおなじみの青春映画。歌っていた二人、橋と小百合が主演した。下町の工場地帯、看護婦のひかるは工員たちの人気者。工員で夜間高校に通う勝利と新入り運転手の留次は、ひかるをめぐる恋のライバルである。若者たちが幸せを求め、恋や友情に支えられながら生きていく姿をさわやかに描く。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FALLEN IDOL", "落ちた偶像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ロンドンの某国大使館。大使の息子フェリッペが崇拝していた執事のベインズは、タイピストのジュリーと愛し合っていたが、ベインズ夫人が二人の仲を疑い始めた。そのベインズ夫人が墜落死し……。大人の世界の裏表を子供の目で眺めたサスペンス・ドラマ。", "1948-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋愛季節／THE SEASON FOR LOVE", "恋愛季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "蒙古族の女性監督ウアルシャナのデビュー作。中国でも増えてきた30過ぎのシングル女性の孤独を描いている。大学院で哲学を学ぶ左麗は自信に満ちたインテリ。すでに30歳を過ぎ、周囲からいろいろな男性を紹介されるが、なかなか本気になれる人はいない。というのも彼女が文革で下放していた時、蒙古族の青年と愛し合い、社会情勢ゆえにその恋をあきらめていたのだった……。", "1986-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("およう", "およう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "大正時代、縛り絵の巨匠・伊藤晴雨はモデルのカネヨにほれ込み同棲を始めるが、やがてカネヨに魅かれた竹久夢二は伊藤から彼女を奪い、おようという名を与えて彼女の絵を描き続けようとする……。一人の女に狂う画家たちの葛藤をロマンティシズムに満ちた映像で描いた作品。女を描けばピカ一の関本郁夫監督が、長年宿願としていた映画化でもある。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大魔神怒る", "大魔神怒る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "八雲の湖のほとり、千草と名越の平和な領内に隣国の御子柴弾正が攻め入った。弾正は一族の守護神である武神像を爆破。千草の若き城主・十郎とその許嫁・早百合が捕らえられ、火あぶりにされようとする時、湖が泡立ち大魔神が出現する……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あばしり一家", "あばしり一家", "", "", "http://image.eiga.k-img.com/images/movie/55048/315x210.jpg?4716", 0, "永井豪の異色ギャグ漫画の映画化。凶悪犯罪者を治外法権の「日本国番外地」に収容するシステムが施行されている近未来。不良たちの暴力といじめが横行する番外地立パラダイス学園に通う亜馬尻（あばしり）菊の助は、なぜか過去の記憶を失っていた。そんなある日、いじめのターゲットにされている転校生ユキをかばおうとした菊の助は、暴力をふるおうとして激しい頭痛に襲われる。そこには「日本国番外地」設立の秘密が隠されていた。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Scarface", "スカーフェイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 169, "　1980年、キューバからアメリカ・マイアミへ渡ったトニーはコカインの取り引きに携わる。その働きが認められたトニーはマフィア組織の配下に収まった後、ボスを殺害。無一文の身からマイアミ暗黒街の頂点へと上りつめ、さらにはボスの愛人エルビラも手に入れることに。しかしその栄光は長く続かなかった……。ハワード・ホークス監督作品「暗黒街の顔役」の現代版リメイク。主演アル・パチーノの鬼気迫る演技、そしてバイオレンス描写も凄まじいアクション・ドラマ。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUET　FOR　ONE", "デュエット・フォー・ワン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "天才バイオリニストの女性が、多発性硬化症という難病により、音楽家としての生命を絶たれる。夫にも弟子にも去られた後、彼女は一人の精神科医と出会う。主役のバイオリニストをJ・アンドリュースが熱演。1980年にロンドンで上演され、1981年にはウィリアム・フリードキン監督によりブロードウェイでも上演された舞台劇の映画化。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エコエコアザラクIII　MISA THE DARK ANGEL", "エコエコアザラクIII　MISA THE DARK ANGEL", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "黒魔術を駆使して数々の怪事件を解決する女子高生、黒井ミサ。ある日、街中で一人の少女がミサの名を叫びながら焼死した。遺体に残された刻印から魔術がらみの争いを読み取ったミサは、事件とかかわりがあるらしい聖セイレム女学院の演劇部に潜り込むのだが……。本作は1997年に深夜枠で放映されたTVシリーズの延長上にある作品で、主演もTV同様、佐伯日菜子が務めている。監督はこれが劇場映画デビューとなる上野勝仁。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Raiders of the Lost Ark", "レイダース／失われたアーク《聖櫃》", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「スター・ウォーズ」のジョージ・ルーカスと「ジョーズ」「未知との遭遇」のスティーブン・スピルバーグが初タッグを組んだ冒険活劇。第2次世界大戦前夜の1936年を舞台に、旧約聖書に記されている十戒が刻まれた石板が収められ、神秘の力を宿しているという契約の箱（＝聖櫃）を巡って、ナチスドイツとアメリカの考古学者インディ・ジョーンズ（ハリソン・フォード）が争奪戦を展開する。原案はルーカスとフィリップ・カウフマン。脚本は「スター・ウォーズ　帝国の逆襲」のローレンス・カスダン。", "1981-12-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mrs Henderson Presents", "ヘンダーソン夫人の贈り物", "", "", "http://image.eiga.k-img.com/images/movie/33798/315x210.jpg?4779", 103, "1930年代のロンドンで、女だてらに劇場経営に乗り出した婦人がいた。英国初の女性ヌードレビューを行い、第2次大戦中も唯一オープンし続けた、歴史に残るウィンドミル劇場の実話に基づく物語。劇場主ヘンダーソン夫人をジュディ・デンチ、やり手の支配人をボブ・ホスキンスが演じる。監督は「グリフターズ／詐欺師たち」「ハイ・フィデリティ」のスティーブン・フリアーズ。05年のアカデミー賞に2部門でノミネートされた。", "2006-12-23", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボールZ　燃えつきろ！！　熱戦・烈戦・超激戦", "ドラゴンボールZ　燃えつきろ！！　熱戦・烈戦・超激戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "劇場版シリーズ第11弾。今回も孫悟空をはじめ、ブルマ、ピッコロ、孫悟飯、クリリンらのお馴染みのキャラが勢揃い。かつて破壊された惑星べジータが新しく再生するが、それを狙う謎の超サイヤ人がいるらしい。正体不明の強敵を相手に、孫悟空たちは戦いの渦中に入るが……。DVDは『DRAGON BOX THE MOVIES』に収録。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUR　TWISTED　HERO", "われらの歪んだ英雄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "家族の事情で大都会ソウルから田舎の小学校に転校したビョンテ。しかし彼が入ったクラスには、ソクテという絶対的権力を誇る級長が君臨しており、担任教師もそれを黙認していた。あまりにも理不尽に優遇されたソクテへの反発を試みるビョンテだったが、孤独な自分の無力を痛感し、ついにはソクテに屈服してしまう……。韓国を代表する俊英P・ジョンウォンが、小学校というコミュニティーの非情な上下関係をリアルに描いた異色作。イジメを扱ったドラマというより、ワイロなどがはびこる大人社会の実態を、子供たちに託して鋭く風刺した作品と見なすことができる。新任教師の“改革”によって哀れな運命をたどった“英雄”ソクテを回想する、30年後の主人公の複雑な心情もホロ苦く伝わる。", "1992-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("28 Days Later...", "28日後...", "", "", "http://image.eiga.k-img.com/images/movie/1228/315x210.jpg?4618", 114, "「トレインスポッティング」のダニー・ボイル監督が、久々に英国を舞台に英国俳優出演で英国で撮影。脚本は「ザ・ビーチ」の原作小説を書いた英国作家アレックス・ガーランド。撮影はラース・フォン・トリアー監督作の常連で、ハーモニー・コリン監督の「ジュリアン」も撮ったアンソニー・ドッド・マントル。主演はアイルランド出身、アンソニー・ミンゲラ監督の新作「コールド・マウンテン」にも出演しているキリアン・マーフィ。", "2003-08-23", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIFE　OR　SOMETHING　LIKE　IT", "ブロンド・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "アンジェリーナ・ジョリーが主演したラブ・ストーリー。TV業界で大奮闘するブロンド美女の公私に渡るトラブルと、思わぬ形で芽生えた恋の行方を綴る。シアトルのTVリポーター、レイニーの夢は全米ネットに進出すること。預言者から一週間後に死ぬと告げられ、恋人との関係もおかしくなった彼女は、犬猿の仲のカメラマンと接近することに……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("動天", "動天", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "作詞家・なかにし礼がプロデュース。幕末の動乱の中で生きた商人・名居屋重兵衛の半生を描いた人間ドラマ。日米通商条約が締結した安政５年。医学、武術、語学とあらゆる分野に秀でた才能を持つ重兵衛は、世界に目を向けて横浜に外国人用の豪華な館を造り、外国貿易を切り開いていくが……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONIBUS１７４", "バス174", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "'00年にリオ・デ・ジャネイロで実際に起きた、乗客11人を人質にしたバスジャック事件を追う衝撃のクライム・ドキュメンタリー。犯人の過去を取材した監督のジョゼ・パジーリャは、事件が起きた社会的背景にまでさかのぼって真実を検証。現在のブラジルが抱える深刻な社会問題を鋭く描き出した骨太な作品だ。'00年6月のある日、リオ・デ・ジャネイロを走る大勢の乗客を乗せたバスが、サンドロという拳銃を持った男に突然ジャックされてしまい……。", "2005-06-04", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOR LOVE OR MONEY", "バラ色の選択", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "仕事の成功と愛の間で板ばさみになる、ホテルの接客係の奮闘を描くラブ・コメディ。一流ホテルの接客係をしているダグは、自分のホテルを持つのが夢。すでに物件を見つけ、あとは出資者を待つばかりだ。そんな彼に富豪のクリスチャンが出資を申し出る。ところが、クリスチャンの愛人が、ひそかに思いを寄せていた香水売り場の店員アンディだと知って大ショック。泣く泣く二人のために奔走することになるが、ミュージカル・スターを夢見て努力を惜しまないアンディにますます恋心を募らせていき……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE REINCARNATION OF PETER PROUD", "リーインカーネーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "毎夜、同じ夢に悩まされる大学教授が、その夢の中の女と、瓜二つの女性に出会い、自分が彼女の死んだ夫の生まれ変わりであることを知る……。死人の霊が生きている人間に取り憑くというオカルト・タッチのホラー・ムービー。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雨の翼", "雨の翼", "", "", "http://image.eiga.k-img.com/images/movie/53328/315x210.jpg?2226", 35, "ユナイテッド・シネマによる\"みんなの映画プロジェクト\"の一環として企画されたシネマプロットコンペティションの入選作品を、「ニライカナイからの手紙」の熊澤尚人監督のメガホンで映画化した青春ドラマ。高校生の陽介は、学校の屋上で見かけた少女・透花のことが気になりはじめる。2人はそれぞれ雨に対して特別な想いを抱えており……。主演は「シムソンズ」の藤井美菜と「グミ・チョコレート・パイン」の石田卓也。", "2008-02-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eyes Wide Shut", "アイズ ワイド シャット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 159, "S・キューブリック監督が「フルメタル・ジャケット」以来、実に12年ぶりに発表した作品で、惜しくも1999年３月７日に死去した彼の遺作となったミステリアスな心理劇。A・シュニッツラーが1926年に書いた小説を現代のニューヨークに翻案。古今東西、永遠のテーマ“セックス”を取り上げている。若き医師ビルと妻のアリスは心から愛し合い、裕福で満ち足りた日々を過ごしているはずだった。ところがある日、アリスから思わぬ性的欲望を告白されたビルは衝撃を受け、彼女が男に抱かれる妄想に囚われながら夜の街をさすらっていく。劇場公開前にしきりに噂された過激なセックス描写はさほどでもなく、ストーリー自体もかなり古風なのだが、嫉妬と不安の強迫観念に囚われた主人公のエロティックな迷宮体験は、リアリティーある愛の寓話として観る者の感性を刺激する。耳にこびりつくピアノの旋律など音楽の使い方も絶妙。尊敬する巨匠との仕事に全力投球したT・クルーズ、N・キッドマンのスリリングな夫婦共演(当時)も見逃せない。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANYTHING ELSE", "僕のニューヨークライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "N.Y.派の名匠ウディ・アレンが、全盛期に帰ったようなスタイルで撮り上げた都会派コメディ。気弱な新進コメディ作家がヘンテコな先輩や奔放な恋人に翻弄される姿を、N.Y.の美しい風景を盛り込んで綴る。アレンの若き日を思わせる主人公を、「アメリカン・パイ」で注目されたジェイソン・ビッグスが好演。コメディ作家のジェリーは、同棲中の女優の卵アマンダのきまぐれな性格に振り回されてばかり。さらに、アマンダの母が家に転がり込んできたり、マネージャーが頼りなかったりと、悩みは尽きない。そんな中、彼は年上の先輩作家ドーベルと親しくなる。ドーベルは、仕事から性生活まで、ジェリーにさまざまな助言を与えるが……。", "2006-01-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BUTCHER’S WIFE", "夢の降る街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "D・ムーアが、予知能力を持つ純粋無垢なヒロインを演じるファンタスティックなラブ・コメディ。星のお告げを信じてニューヨークの精肉店の主人と結婚したマリーナが、持ち前の不思議な力で、都会の人々をケムに巻いていく。彼女の本物の恋のお相手である理論家の精神科医には、後に「ジム・キャリーはMr.ダマー」でおマヌケな３枚目ぶりを見せたJ・ダニエルズ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妹と油揚", "妹と油揚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 37, "PFFアワード1990で審査員特別賞、並びに最優秀男優賞を受賞した天願大介の自主製作作品。近親相姦に溺れる兄妹のもとに、ある日突然、妖怪“イヅナ”が現れ妹に取り憑いた。そしてイヅナと奇妙な同居生活を送るハメになった兄に、多様な人間関係が絡んでくるのだが……。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("害虫", "害虫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "母の自殺未遂や小学校時代の担任との恋愛を経て、独特の雰囲気を持つようになった女子中学生、サチ子。学校をドロップアウトして街をさまよう彼女は、様々な体験の後、級友の勧めで学校へ戻っていくが……。塩田明彦監督が、大人へと成長していく少女の葛藤や不安、残酷さなどを描いた青春映画。思春期の内面、その混乱を見事に醸し出すヒロイン宮崎あおいの等身大の演技が素晴らしい。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・蛇姫道中", "続・蛇姫道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "「蛇姫道中」の続編。琴姫の身代わりとなった瀬川菊之助は、実は姫と男女双生児であった。事の真相を知った菊之助は、妹の代わりとして大役を果たそうとするが、彼に危機が忍び寄る。一方、姫もまた兄を助けるために岡崎へと向かい……。正月映画らしいにぎやかな顔触れで、これぞ娯楽時代劇といえる。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gandhi", "ガンジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 188, "インド独立の偉大な指導者ガンジーの波乱の生涯を、製作費4000万ポンド、エキストラ延べ30万人以上の壮大なスケールで描くスペクタクルな伝記映画。1890年の南アフリカの人種差別抵抗運動を皮切りに、インドで起こった反英不服従運動から、1948年、78歳で暗殺されるまでを追う。", "1982-01-01", "3,21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Theremin : An Electronic Odyssey", "テルミン", "", "", "http://image.eiga.k-img.com/images/movie/1198/315x210.jpg?2244", 83, "1920年、ロシアの科学者レフ・セルゲイビッチ・テルミンによって発明された世界初の電子楽器“テルミン”。触れずに奏でるこの不思議な楽器に魅せられた人々と、テルミン博士自身の数奇な運命をドキュメンタリー形式で綴る。", "2001-08-11", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THINGS CHANGE", "週末はマフィアと！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "殺人を犯したマフィアのボスの身代わりを頼まれた実直そのものの靴磨きの老人と、彼の見張りを任された組織の下っぱとの、おかしな友情を描くギャング・コメディ。主演のD・アメチーとJ・マンテーニャは、ヴェネチア映画祭男優賞を分け合った。「RONIN」「ハンニバル」の脚本家D・マメットの監督作品だ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOURNEY　TO　THE　SUN", "遥かなるクルディスタン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "トルコの女性監督Y・ウスタオウルが、同国のクルド人差別問題を採り上げた社会派ヒューマン・ドラマ。イスタンブールに住むトルコ人青年メフメットが、警察にクルド人と間違えられてマークされ、理不尽な迫害を受ける。仕事も住居も失った彼に救いの手を差しのべてくれたのは、ベルザンというクルド人だった。二人は友情で結ばれるが、ベルザンの身に思わぬ悲劇が降りかかる。", "1999-01-01", "32,7,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の妻たち 三代目姐", "極道の妻たち 三代目姐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "関西を中心とする大暴力団・坂西組三代目組長の妻・葉月。三代目が死に、空席となった組長の座をめぐって幹部たちが争い始めた。全国に１万５千人の構成員を擁する大世帯の“姐”である葉月は内部抗争を収めんと懸命に努力するが……。家田荘子の原作をベースに、今回も様々な妻たちの苦悩の日々を描く。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フリック", "フリック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 154, "日本人監督として初めて３年連続カンヌ映画祭出品を果たした小林政広監督の異色犯罪劇。若い女性を狙った連続猟奇人事件を追う刑事が、数奇な運命に巻き込まれていく過程をスリリングに描いたサスペンスだ。捜査にのめり込んでいく主人公を、香川照之が圧倒的な迫力で熱演している。愛妻を殺され、無気力となった刑事の村田は、殺された女性の身元確認をするため、被害者の兄が暮らす北海道へと捜査に行くが……。", "2005-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アフリカ物語", "アフリカ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "アフリカの大自然を背景に、奥地の森林地帯に不時着した小型機の青年パイロットと、森の番をする老人の孫娘との恋を描く。ストーリーよりも、生き生きと捉えられた動物たちの生態の方が見もので、動物映画の専門家S・トレバーが、監督と撮影に参加している。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Halloween II", "ハロウィンII", "", "", "http://image.eiga.k-img.com/images/movie/55468/315x210.jpg?2754", 106, "デスメタル界のカリスマでありながら、映画監督としても活躍するロブ・ゾンビが、奇才ジョン・カーペンター監督によるオリジナル版をリメイクした「ハロウィン」（07）の続編。あるハロウィンの夜、孤独な少年マイケルは家族やその恋人を惨殺し殺人鬼と化すが、実の妹ローリーの手によりこの世から葬られたはずだった。しかし、精神バランスを崩したローリーのもとに、再びマイケルが現れ……。前作に引き続き、「X-メン」のタイラー・メインがマイケルを、「時計じかけのオレンジ」のマルコム・マクダウェルがマイケルのやみを暴こうとする医師を演じる。", "2010-06-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paul Blart: Mall Cop", "モール☆コップ", "", "", "http://image.eiga.k-img.com/images/movie/54398/315x210.jpg?3769", 91, "ポール・ブラートは警察官になることを夢みるショッピング・モールの警備担当者。彼の仕事ぶりは真面目なのだが、的外れなことばかり。しかし、ある日ショッピング・モールに強盗が！果たしてポールはショッピング・モールを守れるのか！", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blade Runner:The Director's Cut", "ディレクターズカット／ブレードランナー　最終版", "", "", "http://ecx.images-amazon.com/images/I/51NKA7RJ13L._SL210_.jpg", 117, "　酸性雨が降り続く2019年ロサンゼルスの未来都市を舞台に、レプリカントと呼ばれる人造人間と捜査官“ブレードランナー”の戦いを描いたSFハードボイルド・アクション。82年の公開から確実にファンを増やし、伝説の映画と化したこの作品を、監督リドリー・スコットが再編集。主人公デッカードのナレーション、バイオレンス描写、そしてハッピー・エンディングが削除され、一角獣の神秘的な映像が追加。音楽もリミックスされた。", "1992-10-24", "1,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GAY DIVORCEE", "コンチネンタル／離婚協奏曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "アステア、ロジャースが本格的に共演した最初の作品。コール・ポーターの名曲『ナイト・アンド・デイ』、ハーブ・マジソンの『ザ・コンチネンタル』など現在なおスタンダード・ナンバーとして耳にする曲がドラマに巧みに盛り込まれている。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INSIDE MOVES", "サンフランシスコ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "自殺未遂で歩行困難になったローリーは、ふらりと立ち寄った“マックス”という名の店で同じ境遇のバーテンダーや客から励まされ、失いかけた生への意欲を取り戻す。障害者の日常を扱いながら決して暗く湿っぽくならず、あざやかな青春映画に仕上がっている。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魚河岸の女石松", "魚河岸の女石松", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "魚河岸の仲買人の娘、加納佳子はきっぷの良さで人気者だ。ある日彼女はトップ屋、喜多川によって自分が会社社長、立花の実の娘であることを知らされる。その立花が陰謀によって社長の座を追われそうだと知った佳子は、実父のためにひと肌脱ぐことに……。ひばりと健さんの明朗娯楽作品。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blackbeard's Ghost", "黒ひげ大旋風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "海賊の幽霊が繰り広げる大騒動をコミカルに描いた実写版ディズニー映画。弱小チームとして知られるゴドルフィン大学の陸上チームに新コーチとして赴任して来たスティーブ。彼の宿泊先は、悪名高き海賊“黒ひげ”の子孫が経営しているという古びた旅館。彼が冗談半分で呪文を唱えると、なんとそこに本物の黒ひげの幽霊が現われた……。ロバート・スティーブンソン監督をはじめ、「メリー・ポピンズ」のスタッフ陣が名を連ねる。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GHOST IN THE INVISIBLE BIKINI", "ヘブンリービキニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "莫大な財産を残して死んでしまった大実業家ヒラム。しかし、ヒラムは現在、天国と人間社会の中間にある不思議な空間を浮遊中。そんな彼の前にビキニ姿の女の子が現れたから、お話は妙な方向へ……。お色気満点の楽しい作品。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("凍える鏡", "凍える鏡", "", "", "http://image.eiga.k-img.com/images/movie/53268/315x210.jpg?2140", 100, "親から愛を受けられないまま成長した若者たちの苦悩と再生を、一組の母娘と青年の交流を通して描いたヒューマン・ドラマ。自分が描いた絵を路上で売る青年・瞬は、信州の山荘で暮らす初老の童話作家・香澄と知り合う。瞬が幼児期に母から受けた虐待のせいで心に傷を抱えていることを知った香澄は、臨床心理士の娘・由里子に彼の治療を依頼する。しかし、由里子もまた母に複雑な感情を抱いており……。監督・脚本は「カナカナ」の大嶋拓。", "2008-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NN-891102", "NN-891102", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "原爆にとり憑かれてしまった少年の生涯を綴るシュールな物語。５歳の頃、零一は長崎で原爆を体験し父親を失う。以後、彼は原爆の爆音を再生することに全精力を注ぎ込み、母親でさえその奇妙な行動を理解することができなくなっていく。監督は、本作で製作を手掛けている芸術団体DMTを率いる柴田剛。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラッキーロードストーン", "ラッキーロードストーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「ロード」で映画界に乗り出した、THE虎舞竜のボーカル、高橋ジョージが監督・主演を手掛けたロード・ムービー。かつて恋人の通報で拘置所送りとなった40歳の男と、ロスから日本にやってきた16歳の少女。男は恋人への復讐のため、少女は父の非業の死を母に伝えるため、同じ金沢をめざしていた。二人はひょんなことから出会い、ともに旅を続けるうち、心を通い合わせていく。「友情・friendship」の三船美佳が共演。高橋と三船は、これがきっかけで結婚している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHOCOLATE WAR", "チョコレート・ウォー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「殺しのドレス」「クリスティーン」などで知られる若手スター、K・ゴードンの第１回監督作品。トリニティ・カソリック学院の副学院長は、財政難を乗りきるために、生徒たちの秘密組織“ビジルス”にバザーのチョコレート２万箱を売るよう呼びかける。しかし、一人だけ命令を拒否する者がいた……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRU¨MPI", "トルンピ／アントン・ブリューヒンの口琴新世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "前衛口琴（トルンピ）演奏家アントン・ブリューヒンが、口琴をめぐる友人たちに会いに出かけるロード・ムービー。ラジオの時報が口琴というほど口琴が盛んな極東シベリア・サハ共和国と世界初の口琴音楽劇が開催された東京を訪れ、全く異なるルーツを抱く街と国に見えない橋をかける。監督のイワン・シューマッハは、極力映画から言葉を除き字幕すらも排除。それは、観る者もその国の言葉が分からない旅人の気分を彷彿させる手段として効果をもたらした。", "1999-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEEL AND LOVE", "地獄の女アンドロイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "レイプされ、裁判に持ち込むが負けてしまい、裁判所の屋上から身を投げて死んだゲイリー。５年後、彼女をレイプした男たちが次々と惨殺される。彼女はアンドロイドになって男たちに復讐を開始したのだ。残虐なまでの殺人描写に女の執念を感じさせる、ストレートなSFサスペンス。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIAMI GOLEM", "怒りの標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "シュベイカー教授は、最新のバイオ・テクノロジーを駆使して地球外生物の謎を解き明かそうと、日夜研究を続ける純粋な生物学者。その教授の研究所が何者かに襲撃される。SF的要素を加味して、教授の研究を狙う者たちと教授、そして彼を救おうとする人々の死闘を描く。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMMUNION，A TRUE STORY", "コミュニオン 遭遇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "真偽をめぐって全米で論争を巻き起こした、W・ストライバーの原作を映画化したSFドキュメント。1947年、当時二歳のストライバーの一家にUFOが訪れるが、本人はこの出来事の記憶を失ってしまう。そして40年余経過した今、流行作家となった彼の前に再び異星人が現れ、コンタクトを行おうとする。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AKIBA", "AKIBA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "オタクの聖地・秋葉原を舞台に、2人の少女が織りなす美しくも切ない青春ストーリー。秋葉原で生まれ育ったミナノは、歌手になる夢を抱きながらメイドカフェで働いている。ある日、ミナノは小学校時代の同級生・カナコと8年ぶりに再会する。子どもの頃からミナノのことが大好きだったカナコは、それ以来、ミナノにまとわりつくように。そんなカナコを疎ましく思うミナノだったが、一緒に過ごすうちに気持ちに変化が生まれはじめる。", "2006-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("April Snow", "四月の雪　ディレクターズ・カット完全版", "", "", "http://image.eiga.k-img.com/images/movie/33638/315x210.jpg?2314", 140, "2005年9月に劇場公開され大ヒットを記録したぺ・ヨンジュン主演の恋愛映画「四月の雪」のディレクターズ・カット版。妻が交通事故に合ったとの知らせを受け、病院へ駆けつけたインス。そこには、事故の際に妻と同乗していた男性の妻ソヨンがいた。お互いの妻と夫が不倫関係にあると知った2人は、苦悩しながらも次第に惹かれあっていく……。20箇所以上の未公開シーンが追加され、エンディングにも新たな演出が施されている。", "2006-09-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crusoe", "クルーソー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1810年のアメリカ・バージニア州。若き奴隷商人クルーソーは、アフリカへ奴隷探しの旅へ出るが、その途中嵐に見舞われて船が転覆。九死に一生を得たクルーソーは絶海の孤島で、唯一生き残った犬とともに過酷な生活を送るのだが……。デシャネルが「マジック・ボーイ」以来７年ぶりに手掛けた長編第２作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・オーディション", "ザ・オーディション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "陰謀にかかって失脚し、今はマネージャー業に没頭する元ロック・グループのリーダーが、若さと情熱で４人の少女を売り出すまでを描いた作品。板谷・浜田・鈴木・岩間は“セイント・フォー”のメンバーで、若さあふれる演技を見せてくれる。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("77BOADRUM", "77BOADRUM", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "国内外で活躍するバンド「ボアダムス」が2007年7月7日にニューヨークで敢行したライブイベント「77BOADRUM」を捉えたドキュメンタリー。ボアダムスのコンセプトを基に77台のドラムで一斉に演奏された圧巻のパフォーマンスを中心に、2日間にわたって行なわれたリハーサルの模様や関係者へのインタビューなどを交える。監督はこれまで多くのミュージシャンのPVを手がけてきた川口潤。", "2008-07-07", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("復讐するは我にあり", "復讐するは我にあり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "直木賞を受賞した佐木隆三のノンフィクション小説を原作として、「神々の深き欲望」以来10年ぶりに今村昌平が手掛けたドラマ。原作が話題の作品だけに何度も映画化の話がもち上がり、黒木和雄はじめ何人もの監督が立候補。映画化権を射止めた今村昌平が満を持して演出し、高い評価を得た。５人を殺害したあと全国を逃走した男とその父の相剋を通して、人間の原罪と救済を問う物語で、犯罪心理がカミュの「異邦人」のような不条理なものとして描かれている。検閲によってカットされたシーンが多く、難解な印象を与えるが、ダイナミックな画面と緒形、三國のパワフルな共演は圧倒的。父子対面シーンは鬼気迫る演技。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自分の穴の中で", "自分の穴の中で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "主人公・多美子の一家の家庭崩壊劇。将来有望な青年医師に多美子を嫁がせようとする戦争未亡人の義母。医師は義母と関係を持とうとたくらむ。妻に逃げられた兄は株に失敗、肺病が悪化して死ぬ。多美子は義母を追い出し、一人ぼっちになってしまう。北原三枝の美しさといい、木村威夫の美術の出来といい、見ごたえ十分の作品。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CANNONBALL RUNIII：SPEED ZONE", "キャノンボール3　新しき挑戦者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "すっかりおなじみ、ルール無用の“早い者勝ち”カーレースを描いた「キャノンボール」シリーズ第３作。今回はB・シールズにA・ミラノ、そしてあのC・ルイスらが出演。J・ドレイク監督のもと、前２作をしのぐド派手なカー・アクションを見せる。", "1989-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デトロイト・メタル・シティ", "デトロイト・メタル・シティ", "", "", "http://image.eiga.k-img.com/images/movie/53108/315x210.jpg?3349", 104, "若杉公徳の同名人気コミックを映画化。オシャレな渋谷系ポップミュージシャンを夢見て上京してきた根岸崇一だったが、ふとしたことから奇抜なメイクと演奏で人気を博す悪魔系デスメタルバンド「デトロイト・メタル・シティ」のギターボーカル「ヨハネ・クラウザーII世」として活躍する羽目に……。崇一からクラウザーII世に豹変する松山ケンイチのなりきりぶりが話題に。クラウザーのライバル役でKISSのジーン・シモンズも出演。", "2008-08-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大怪獣バラン", "大怪獣バラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ムササビからヒントを得たような大怪獣が活躍する一編。湖底から現れるスローモーションのカットや、山中で主人公たちを襲う場面など、出色の出来。脚本を担当した関沢新一は石井輝男とともに清水宏に師事した門下生。大詰め、羽田沖から都内へ上陸しようとするバランと、それを阻止しようとする自衛隊の攻防が見ものだ。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三池／終わらない炭鉱の物語", "三池／終わらない炭鉱の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "日本最大の炭鉱として、かつて栄えた三池炭鉱の歴史と現在を追うドキュメンタリー。朝鮮人や中国人の強制労働や炭じん爆発事故といった風化しつつある炭鉱で起きたさまざまな事柄を、今の時代にしっかりと伝えてくれる意義ある一本だ。日本最大の炭鉱として高度成長を支えたが、時代の流れと共に閉山を余儀なくされた福岡県の三池炭鉱。一部の人たちから“負の遺産”とも呼ばれるその炭鉱の歴史と現在を見つめる。", "2006-04-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ごろつき", "ごろつき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ベテランのマキノ監督、高倉健主演によるアクション映画。北九州の炭鉱が廃鉱になり上京して来た主人公が、ボクサーと流しの歌手を目指して猛練習に励むが、暴力団に恩人を殺され殴り込む。菅原文太が高倉健の素朴な友人役を神妙に演じて印象的。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京タワー　オカンとボクと、時々、オトン", "東京タワー　オカンとボクと、時々、オトン", "", "", "http://image.eiga.k-img.com/images/movie/33918/315x210.jpg?4323", 142, "ベストセラーとなったリリー・フランキーの自伝小説を、オキダリジョーと樹木希林の共演で映画化。幼いボクを連れてオトンの家を出たオカンは、女手ひとつでボクを育て上げた。やがて美大に通うため上京したボクは、オカンへの罪悪感を感じながらも自堕落な日々を送ってしまう。数年後ようやくイラストやコラムの仕事が軌道に乗り始めた頃、オカンのガンが発覚し……。監督を「さよなら、クロ」の松岡錠司、脚本を松尾スズキが手掛ける。", "2007-04-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市果し状", "座頭市果し状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "座頭市はある宿場町で、医者の順庵と娘お志津を助けて悪玉ヤクザ・松五郎と対決する。市は深手を負いながらも松五郎一家と用心棒・弦八郎を倒すが、弦八郎は順庵の息子だった……。野川由美子がイキのいい姐御ぶりをみせる。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京の女", "東京の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 47, "大学予科生の良一は、一緒に暮らす姉のちか子に月謝と小遣いをもらって学校に通っている。ちか子は弟のために昼はタイピスト、夜は翻訳の手伝いをして働いているのだ。だがある日、姉がいかがわしい酒場に勤めているという噂が良一の耳に……。登場人物を徹底的に切り詰めた室内心理劇で細かい描写が印象的。", "1933-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ghost Rider", "ゴーストライダー", "", "", "http://image.eiga.k-img.com/images/movie/1068/315x210.jpg?4830", 110, "マーベル・コミックの人気キャラクターをニコラス・ケイジ主演で映画化。17歳のときに病の父親を救うために悪魔メフィストに魂を売り渡したジョニー。その10数年後、30歳になったジョニーの前に再びメフィストが現れ、魂を売った代償として魔界の反逆者を捕らえるよう命じる……。監督は「デアデビル」のマーク・スティーブン・ジョンソン。共演はエバ・メンデス、ピーター・フォンダ。", "2007-03-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DU SOLEIL PLEIN LES YAUX", "さらば夏の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "地中海の青く澄んだ海を背景に、「個人教授」のスタッフが再び取り組んだ青春映画。ベルレー扮する青年医師バンサンとその恋人との間に起こった小さな波紋を通して、愛にとまどう繊細な心の動きをみずみずしく描出。当時人気No.1だったベルレーの代表作の１本。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUE CASES−NE｀GRES", "マルチニックの少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "カリブ海の小島、マルチニック出身の作家ゾベルがまとめた1930年代の少年期の物語を、当時27歳の女性監督パルシーが優しいタッチで描く。島の少年を通じ、貧困と矛盾に満ちた生活がそのまま伝わってくる。少年の祖母が何かと才覚のある人で、面白い役まわりを演じている。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISS OF DEATH", "死の接吻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ギャング組織とつながる従弟を助けるため、密輸の片棒をかついだジミー。銃弾を受け、逮捕されたジミーは地方検事に共犯者の密告を強要される。しゃべれば当然報復される……。検事と弁護士にほんろうされ、一方で暗黒街のボスに追い詰められていくジミーの闘いがスリリングにハードボイルドに展開される。N・ケイジが、凶悪な犯罪組織のボスを凄味のある演技で演じているのにも注目。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("We Were Soldiers", "ワンス・アンド・フォーエバー", "", "", "http://image.eiga.k-img.com/images/movie/1578/315x210.jpg?3697", 138, "64年、ベトナム戦争初期の激戦を、実際に従軍した米軍大佐とUPIの戦地特派員が書いた同名ノンフィクションを原作に映画化。戦地と並行して本国に残る家族の状況も描く。監督はメル・ギブソン監督・主演の「ブレイブハート」で脚本がオスカーにノミネートされたランダル・ウォレス。これが「仮面の男」に続く監督第2作。撮影は「ダンス・ウィズ・ウルブズ」でオスカーを受賞した、オーストラリア出身のディーン・セムラー。", "2002-06-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新しき土", "新しき土", "", "", "http://image.eiga.k-img.com/images/movie/57848/315x210.jpg?2632", 106, "「青い山脈」「東京家族」などで知られ、日本映画の黄金時代に活躍した伝説の女優・原節子が、デビュー間もない16歳のときに主演した映画。1937年に製作された日本初の国際合作映画で、山岳映画で知られるドイツの巨匠アーノルド・ファンクと伊丹万作の共同監督による日独合作ドラマ。欧州留学を終えドイツ人女性ジャーナリストとともに帰国した輝雄（小杉勇）は、一途に待ちわびていた許婚の光子（原）と父・巌（早川雪洲）に温かく迎えられる。しかし、西洋文化になじんだ輝雄は、許婚という日本的な慣習に反発を覚え悩む。輝雄の変化に気づき絶望した光子は、花嫁衣裳を胸に抱き、噴煙をあげる険しい山にひとりで登り始める……。日独版（ファンクが責任監督・編集）と日英版（伊丹が責任監督・編集）の2本の異なるフィルムが存在するが、公開後は日独版がスタンダードとみなされるようになった。2012年、75年ぶりに劇場公開。", "1937-01-01", "76,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WIND", "ウインズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "初参加にして、栄えあるアメリカズ・カップ出場艇のクルーとして迎えられたウィル。が、この年、初めてアメリカズ・カップはオーストラリア艇にもっていかれてしまう。そして彼の恋人ケイトも去っていった。それから半年後、ウィルは再挑戦すべく仲間と資金を集め、新しい艇ジェロニモの作成に乗り出す。やがてケイトもこのプロジェクトに参加し、いよいよ大会本戦へ……。ヨット・レースの最高峰、アメリカズ・カップを背景にして、さわやかなラブ・ロマンスを描いてみせたバラードの演出。そしてスリリングなレース・シーン、波を切る疾走感が感動を呼ぶ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Subway", "サブウェイ", "", "", "http://ecx.images-amazon.com/images/I/51k1iWzdhuL._SL210_.jpg", 102, "パリの地下鉄の下に広がる地下道を拠点にするフレッド。彼は、同じく地下道に住む若者たちとロックコンサートを開くことを決意する。「レオン」「フィフス・エレメント」のリュック・ベッソンの長編第2作。主人公のフレッドをクリストファー・ランバート、彼が恋に落ちる美しい人妻エレナをイザベル・アジャーニが演じている。", "2010-09-25", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ministers", "ハーヴェイ・カイテル　銃撃のレクイエム", "", "", "http://ecx.images-amazon.com/images/I/51OOaiI-KqL._SL210_.jpg", 89, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DAY AFTER", "ザ・デイ・アフター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "アメリカのABC放送で放映され、46パーセントの視聴率を獲得したテレフィーチャー。東西間の緊張が高まり、核戦争が勃発。カンザスシティーを舞台に、核爆発の前後数日間を描く。ICBMの発射シーンなど、兵器の特撮は見事な出来栄え。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEMPO DI MASSACRO", "真昼の用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "“イタリアン・スプラッタの父”と異名をとる、「サンゲリア」のフルチ監督だけあって、執拗なムチ打ち場面などマカロニ・ウエスタンのなかでもひときわ凄絶な残虐性に目を見張る。主人公が久々に帰った故郷は悪玉の支配下。しかも、その悪玉は実の父だった。主題歌が切なく胸を打つ。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Road Trip", "ロード・トリップ", "", "", "http://ecx.images-amazon.com/images/I/51XiO9wi7sL._SL210_.jpg", 94, "「ゴースト・バスターズ」などのヒットメイカー、アイバン・ライトマン製作総指揮による青春コメディ。大学生のジョッシュは幼なじみの恋人ティファニーと遠距離恋愛中。寂しさから同級生のベスと浮気したジョッシュは、情事を撮影したビデオを誤ってティファニーに送ってしまう。慌てたジョッシュはビデオがティファニーに届く前に取り戻すべく、3人の悪友と一緒にニューヨークからテキサスを目指すが……。監督のトッド・フィリップスは後に「ハングオーバー」で大ブレイク。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARCO 母をたずねて三千里", "MARCO 母をたずねて三千里", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1976年、高畑勲監督が手掛け、感動の涙をふりしぼったTVアニメ・シリーズ『母をたずねて三千里』を劇場版としてリメイク。出稼ぎに出た母親を追って、１万２千キロにおよぶ旅を続けた少年マルコの心の軌跡が綴られる。19世紀、不況のイタリア。南米に働きに出た母を追って、９歳のマルコはペットの猿アメディオと一緒に船出する。ところが、旅は困難続きだった……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鶴彬　こころの軌跡", "鶴彬　こころの軌跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "昭和初期に反戦を貫き通した石川県出身の川柳作家・鶴彬の波乱万丈な人生を、「ハチ公物語」「ひめゆりの塔」の名匠・神山征二郎監督が映画化。日本が軍国主義に傾倒していく中、戦争の悲惨さを訴えた反戦川柳を詠んだために治安維持法違反で逮捕され、29歳の若さで獄死した鶴彬の生き様を描きだす。主演を務めるのは、これまでにも「北辰斜にさすところ」などの神山監督作に出演してきた池上リョヲマ。", "2009-07-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Colors", "カラーズ 天使の消えた街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "ロサンゼルス市警のギャング犯罪取り締まりチーム“CRASH”に所属する刑事コンビとストリート・ギャングたちとの抗争を徹底して乾いたタッチで描く。制服の威信を信じ捜査に突っ走る新米警官マクガバンと、定年を一年後に控えたベテラン警官ホッジスが続発するギャング犯罪に向けてコンビを組まされる。事あるごとに先輩のやり方に反発を覚えるマクガバン。そんなある日、彼はコーヒー・ショップに勤めるルイーザに心を惹かれる。が、彼女はマクガバンの向こう見ずな捜査に批判の言葉を向ける……。怪優D・ホッパーが久々に監督を手掛けたハード・アクション。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが恋の旅路", "わが恋の旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "曽野綾子の原作を寺山修司と篠田正浩のコンビで映画化したメロドラマ。有閑マダムとの関係を清算した石原潔は、喫茶店のウェイトレス・千江に恋をし結婚しようとするが、千江は自動車事故に遭い記憶を失ってしまう。技巧派の篠田正浩がうまくまとめた小品。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今日に生きる", "今日に生きる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "裕次郎が演じる城俊次が、北関東の鉱山町にふらりとやって来て、山一運輸に嫌がらせをする三国運輸の連中をやっつけて去っていく。これはのちの小林旭“渡り鳥”シリーズの原型を思わせる作品。悪玉・金子信雄の子分として宍戸錠が登場し、すご味を見せる。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAPID FIRE", "ラピッド・ファイアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ブルース・リーの遺児で、1993年４月に父親同様、謎めいた死を遂げたB・リーの主演作。マフィアの殺人を目撃したジェイクは、保護してくれるはずのFBIからも命を狙われる。どうやら、双方には裏取引があったらしい。格闘技を得意とするジェイクは、親しくなった刑事とともに真相の究明に乗り出すが……。リーの流麗なアクション演技に注目。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A thousand acres", "シークレット／嵐の夜に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　「リア王」をモチーフに、ピューリッツァー文学賞を受賞したジェーン・スマイリーの小説を映像化。広大な農場を舞台に、姉妹と父親の葛藤を描く。大農場を経営する父ラリーを娘のジニーとローズは献身的に支えてきた。しかし、父の意志で娘に農場を明け渡すと、思いもよらぬ亀裂が生じ始める。脚本は「ある貴婦人の肖像」「女と女と井戸の中」のローラ・ジョーンズ。「恋のゆくえ／ファビュラス・ベイカー・ボーイズ」のミシェル・ファイファー、オスカー女優ジェシカ・ラングが姉妹を演じる。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIAL M FOR MURDER", "ダイヤルMを廻せ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ブロードウェイでヒットを記録したノット原作の映画化。中年の紳士トニーは、美貌の若妻マーゴの心変わりを知り、妻を殺害する完全犯罪を企む。だが少しの狂いで犯罪は未遂に終わる。事件直後、必死でアリバイを固持する夫と、事件に巻き込まれ、あげくに無実の罪を着せられ苦悩する妻との息詰まるミステリー・ドラマが展開していく。犯罪現場の邸内の部屋、そして廊下や階段などを、巧みな映画的空間として再現、観客の推理意欲を喚起させる。「裏窓」と同年、ヒッチコック最盛期に作られた知的スリラーの代表的作品。ケリーの美しい容姿と見事な演技は絶品。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fast and The Furious: Tokyo Drift", "ワイルド・スピードX3 TOKYO DRIFT", "", "", "http://image.eiga.k-img.com/images/movie/1748/315x210.jpg?1715", 104, "カリフォルニアに住む高校生ショーンは、違法運転の常習犯。事故を起こし少年院行きを免れなくなった彼は、軍人の父を頼って日本に渡る。転校先で疎外感を覚える彼を熱くさせたのはやはり車。彼はドリフトレースの世界に足を踏み入れ、天才ドライバーD.K.との対決に挑む。シリーズ3代目のスピード・キングをルーカス・ブラックが熱演、北川景子、土屋圭市など多くの日本人キャストが出演する。", "2006-09-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("π", "π〈パイ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "アメリカでカルト的な人気を呼んだ異色スリラー。世界のすべての事象を数式で表現できると信じる学者マックスは、株式市場の分析に精を出している。そんなある日、故障したコンピューターが216ケタもの数字の羅列を弾き出した。ユダヤ学者の話によると、これはユダヤ教の神を表わす数字であるらしい。研究を止めるよう説く先輩教授の声を無視してマックスはその分析にのめりこみ、やがて妄想の世界に入りこんでいく。数学と宗教、哲学が結びついたドラマには、何度も見直したくなるような知的な興奮がある。黒みと白みを明確に浮かび上がらせたモノクロ映像と、テンポの速いテクノ・ミュージックの融合も強烈な印象を残す。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い鳥逃げた？", "赤い鳥逃げた？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "藤田敏八が日活を離れ、初めて他社で撮った作品。28歳のチンピラ・宏と、その弟分・卓郎は、謎の少女マコと出会う。マコが失踪した大富豪の娘だと知った宏は、彼女を使って一獲千金を狙うが……。1970年代前半の、目標を見失った若者の姿が、見事に描かれている。桃井がオール・ヌードを披露。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Buddha Collapsed Out of Shame", "子供の情景", "", "", "http://image.eiga.k-img.com/images/movie/54108/315x210.jpg?3340", 81, "破壊された仏像が瓦礫となって残るアフガニスタンのバーミヤン。そこで暮らす6歳の少女バクタイは学校に通うことを夢見ていたが、実際は鉛筆とノートを買うお金も持っていなかった。バクタイは文房具を買うお金を稼ごうと待ちに卵を売りに出かけるのだが……。イランを代表する映画監督のモフセン・マフマルバフの娘、ハナ・マフマルバフが撮影当時18歳で監督を務めた作品。", "2009-04-18", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAMPEN 短篇", "TAMPEN 短篇", "", "", "http://ecx.images-amazon.com/images/I/51RlnyI9GlL._SL210_.jpg", 65, "「監督のいない現場で映画をつくる」というアイデアのもと、カメラマンや照明として日本映画界を支えてきた人々と俳優たちがつくりあげたオムニバス映画。恋人を亡くしたばかりの女が、やり場のない思いを抱えながらいつも通りの1日を過ごそうとする「空 koo-ghe 華」、部屋をシェアして暮らす正反対の2人の女を描いた「share」など、若い女性の日常をつづった4作品がそろう。出演は「M／OTHER」の渡辺真起子、永瀬正敏ほか。", "2001-12-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Before Sunrise", "恋人までの距離（ディスタンス）", "", "", "http://ecx.images-amazon.com/images/I/51Tfeu8MmcL._SL210_.jpg", 101, "列車の中で出会ったアメリカ人青年ジェシーとフランス人女性セリーヌ。意気投合した彼らはウィーンで途中下車し、14時間だけという約束で一緒に過ごすことにするが……。リチャード・リンクレイター監督が贈る、極上のラブ・ストーリー。ウィーンの街を歩きながら2人が交わす、時に他愛なく、時に哲学的な会話の数々が光る。2004年には、本作より9年後の彼らの姿を描いた続編「ビフォア・サンライズ」も製作されている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Boarding Gate", "レディ　アサシン", "", "", "http://ecx.images-amazon.com/images/I/51773E5Nq7L._SL210_.jpg", 106, "「デーモン・ラヴァー」などで知られるフランスの鬼才オリビエ・アサイヤスが監督・脚本を手がけたサスペンス・アクション。かつて、経済界を仕切る実業家マイルズとパトロン関係を結んでいた元娼婦のサンドラ。彼女は夢を実現させるのに必要な資金を手に入れるため、中国人貿易商の恋人レスターに言われるがままにマイルズを殺害するが……。イタリアの人気女優アーシア・アルジェントがヒロインを存在感たっぷりに熱演。", "2008-08-03", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やさしい旋律", "やさしい旋律", "", "", "http://image.eiga.k-img.com/images/movie/54188/315x210.jpg?3457", 0, "携帯書籍サイトで人気を博し、書籍化された井上香織の同名原作を映画化。「むずかしい恋」「イエスタデイズ」の柳沢ななが主演。銀行員の茉莉亜は仕事の帰り道に、ごみ置き場に捨てられたピアノのようなエレクトーンのような楽器を見つけ、惹かれるように自宅に持ち帰る。その日をきっかけに、弟と同じ夢を見るなどの不思議な現象が茉莉亜の周りで起こり始める。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄人28号　白昼の残月", "鉄人28号　白昼の残月", "", "", "http://image.eiga.k-img.com/images/movie/33908/315x210.jpg?4149", 95, "原作連載当時の昭和30年代という時代設定と骨太な人間ドラマが特徴の今川泰宏監督版テレビアニメをオリジナルストーリーで映画化。敗戦から10年が過ぎ復興を続ける東京で、少年探偵・金田正太郎と鉄人28号は数々の難事件を解決していた。ある日、不発弾を狙うロボットと戦っていた正太郎の前に1人の青年が現われ、見事に鉄人を操縦して敵を撃退する。彼は行方不明だった同姓同名の義兄“ショウタロウ”だった……。", "2007-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五人の賞金稼ぎ", "五人の賞金稼ぎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "若山富三郎が主演するアクション時代劇。代官の悪政に苦しめられてきた村の名主に依頼され、腕利きの賞金稼ぎたちが集まった。代官一味との大攻防戦では、工藤栄一監督ならではの集団活劇の醍醐味を味わうことができる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("巨乳をビジネスにした男", "巨乳をビジネスにした男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "多くの巨乳タレントを世に送りだした元イエローキャブ社長・野田義治の半生を遠藤憲一主演で映画化。芸能プロダクションの代表を務める野村義宏は、「巨乳アイドルはお茶の間でうけない」という業界の常識を覆しつつあった。そんなある日、野村が育てあげたタレント・堀川しおりが不治の病に倒れてしまう。しおりと重ねあわせるように期待の新人・若林恵子を育てはじめる野村だったが、一方で所属タレントの移籍などのトラブルが発生し……。", "2007-05-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE RAYON VERT", "緑の光線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "E・ロメール監督の“喜劇とことわざ”シリーズ第５作。夏のバカンスを一人で過ごすことになってしまった若い娘のさすらう姿を、親密な日記体スタイルでスケッチ。柔和な光と優しい風が彼女を慰撫する戸外を舞台に、娘の寂りょう感が切々と迫ってくる。ヌーヴェル・ヴァーグの牙城を守り続ける重鎮ロメールは、無名の女性スタッフ３人と小編成で身軽な撮影部隊を組んで、ハンディの16ミリカメラでロケ撮りを敢行。登場人物から思いもよらぬ身振りや台詞を引き出して、画面に初々しさを生起させるのに成功している。光と声の、ハッとするほどの明るい官能性に射し貫かれるラストが最高。DVDは「エリック・ロメール コレクション(5)」に収録。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さくら", "さくら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "太平洋と日本海を桜の木で結んだ男の、実話をもとにした感動作。樹齢400年という桜の木に魅せられた国鉄バスの車掌・佐藤良二。ガンと闘いながら、彼は名古屋から金沢までのバス路線に千本の桜を植樹する。そんな彼の、周囲の反感に屈しない生き方を、神山征二郎がヒューマンな視点で描く。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLYESTER", "ポリエステル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "監督のJ・ウォーターズいわく、“この作品はアルコール中毒と離婚のコメディである”。アメリカの中流家庭を猥雑にコケにするという、ウォーターズ流ホームドラマ。匂いの出る“オドラマ・カード”で、映画を観ながらクサイ体験ができることが、公開当時話題になった。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やさしいにっぽん人", "やさしいにっぽん人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "東陽一監督のデビュー初期の代表作。沖縄・慶良間列島の集団自決を生きのびたものの、今はそのことを何も覚えていない青年が、真の“ことば”を求めて旅を続ける姿を描く。いずれも個性的な俳優たちの奇妙な存在感が印象に残る。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Avatar: Special Edition", "アバター　特別編", "", "", "http://image.eiga.k-img.com/images/movie/55658/315x210.jpg?1300", 170, "巨匠ジェームズ・キャメロンが「タイタニック」（97）以来12年ぶりに放つ、デジタル3DによるSF超大作。22世紀、地球から遠く離れた惑星パンドラへとやってきた元海兵隊員ジェイクは、自らの分身となる“アバター”を操り、先住民ナヴィと交流するが、やがて鉱物資源を巡って勃発する人類とナヴィとの戦争に巻き込まれていく。主人公ジェイクに「ターミネーター4」のサム・ワーシントン。共演にゾーイ・サルダナ、シガニー・ウィーバーほか。未公開映像8分が追加された特別編。", "2010-10-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("≒〈ニア・イコール〉舟越桂", "≒〈ニア・イコール〉舟越桂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "様々な分野のカリスマ的人気を誇るアーティストを取り上げるドキュメント「≒〈ニアイコール〉」シリーズの第３作。第１作で森山大道を追った藤井謙二郎監督が、今度は国際的な彫刻家、舟越桂に密着。舟越桂のプライベートからアーティストとしてのメンタル部分まで見て取れる見ごたえある一本だ。楠を素材にした木彫半身像などで独特の世界を切り開き、海外で高い評価を集める舟越圭。新作の製作に取りかかった彼の姿をカメラが追う。", "2004-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANON70", "恋のマノン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "世界を股にかけて自由奔放な愛を求める女性マノン。原作『マノン・レスコー』の舞台をそのまま現代に置きかえて、女性心理の幸福と悲哀の姿を描く。ドヌーヴの際立った美しさが印象的で、女性心理の奥深い動きをさらりと演じている。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猫目小僧", "猫目小僧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "恐怖漫画家、楳図かずおの数ある作品中でも異彩を放つ異色ダークヒーローの実写映画化。妖怪からも人間からも忌み嫌われる孤独な妖怪“猫目小僧”の活躍を、異才・井口昇がキッチュに映像化している。かつて妖怪ギョロリが出没した東北の片田舎に、まゆかと浩の姉弟が引っ越してきた。彼らは猫のような顔を持つ奇妙な少年“猫目”と出会い、心を通わせるようになる。そんな時、村で怪事件が続発し……。", "2006-06-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROUBLE WITH SPIES", "スパイ大混戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「普通の人々」のシリアスな父親役、「アニマル・ハウス」のケツ丸出し教授、「バックドラフト」の精神異常の放火魔など、様々な役を見事に演じている性格俳優D・サザーランドが落ちこぼれのスパイを演じる。イギリス情報部とKGBのスパイ戦の真っ只中、自分の任務を勘違いして送り込まれた主人公が繰り広げるドタバタ・コメディ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEMPHIS BELLE", "メンフィス・ベル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "第二次大戦のさなか、ナチスの軍事基地を爆撃する任務を負った連合軍の編隊が飛び立った。その中には幾多の戦火を無キズで生還してきた強運の爆撃機メンフィス・ベルの姿もあった。しかし今回の任務はあまりに危険だった。命懸けの戦場で友情と勇気を確かめ合う若者たちを描いて大ヒットした戦争ドラマ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUNSTON　CHECKS　IN", "Let's チェックイン！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "いたずら盛りの兄弟ブライアンとカイルは、五つ星ホテルに住み込みで働いている父のおかげで、ホテル中を遊び場にしていた。そんなある日、いたずらの罰としてホテルの仕事を言いつけられたカイルは、屋上で変なオラウータンに出会う。ダンストンという名のそのオラウータン、実はコソ泥の名人で、客を装ったラトリッジ卿の泥棒家業の片棒を担がされていたのだ。ブライアンとカイルは、スキを見て逃げ出したダンストンをかくまうことにする。でもそのために、ラトリッジ卿や動物捕獲人に狙われるハメになる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕の初恋をキミに捧ぐ", "僕の初恋をキミに捧ぐ", "", "", "http://image.eiga.k-img.com/images/movie/54278/315x210.jpg?4246", 122, "青木琴美の同名ベストセラーコミックを映画化した、井上真央と岡田将生主演によるラブストーリー。監督は「Life／天国で君に逢えたら」の新城毅彦。医師の娘・繭と、重い心臓病を患い闘病生活を送る少年・逞（たくま）は互いに恋心を抱き、「大人になったら、結婚しよう」と初恋の約束を交わす。しかし、医師から逞の余命は20歳頃までと宣告されてしまい……。", "2009-10-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千年火", "千年火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "日本で初めて公設ホールが製作した作品。両親を失った少年が、人を信じること、生きることの素晴らしさに目覚めていくまでを描くヒューマン・ドラマだ。舞台は福岡県新宮町。海と山に囲まれた美しい自然を捉えた詩情あふれる映像の中、素朴な人々の人間模様がユーモラスに繰り広げられる。丹波哲郎、吉行和子ら、ベテラン俳優陣が味わい深い演技を披露。幼い頃に母を失った11歳の少年・聡は、最愛の父をも交通事故で亡くし、ショックで口がきけなくなってしまう。福岡の祖父母の家に引き取られた彼は、美しい自然や心優しい人々との触れ合いを通して、次第に心癒されていく。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("J’AI PAS SOMMEIL", "パリ、18区、夜。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "C・ドゥニの長編第４作。女優としてのチャンスをつかむため、リトアニアからパリ18区にやって来た若い娘ダイガ。彼女が遠縁の親戚を頼りに身を寄せた安ホテルには、老女ばかりを狙う連続強盗殺人犯カミーユが暮らしていた……。1987年にパリを震撼させた凶悪犯罪“ティエリー・ポーラン事件”がベースになっているが、いわゆるサイコ・スリラーの類とは大きくかけ離れている。リトアニアからの異邦人の視点を通して浮き彫りにされるのは、様々な階級や人種の人々が共存するパリ18区の都市観そのもの。意図的な感情描写を排したA・ゴダール撮影によるブルートーンの映像が、夢幻的なまでに美しい叙情性を醸し出す。ゲイの殺人鬼カミーユの日常描写からは、ドゥニの次作「ネネットとボニ」で強烈に発揮されるなまめかしい官能性も感じ取れる。オンボロ車でブラリとパリに現れて平然と立ち去っていくヒロイン、ダイガに扮したK・ゴルベワのタフな存在感も忘れられない。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PARTY", "パーティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "B・エドワーズが製作・脚本を務め、P・セラーズが主演したドタバタ・コメディ。インドの舞台俳優だと名乗るフルンディがハリウッドにやって来て、スターたちの集まるパーティーに出席。大騒ぎとなるのだが……。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I criminali della galassia", "惑星からの侵略", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "太陽系の惑星デハフォスの狂信的科学者とその一味は、生化学の実験用に地球人を特殊液で縮小人間に変え、ひそかに地球から誘拐していた。だが、その陰謀は宇宙飛行士ラッセルによって見破られ、宇宙空間で凄絶な戦いが始まる。イタリア製SF犯罪アクション。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　COMPANY", "バレエ・カンパニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「ゴスフォード・パーク」「ザ・プレイヤー」の巨匠R・アルトマン監督が、実在の名門バレエ団とのコラボレーションで作り上げた群像劇。バレエにすべての情熱を注ぐダンサーやスタッフの日常が、ドキュメンタリー・タッチで描かれる。かつてバレリーナを目指していたという人気女優N・キャンベルが企画、原案、主演を兼ね、代役を立てずに難しいバレエ・シーンにも挑戦。その華麗なダンスは圧巻だ。バレエ団“ジョフリー・バレエ・オブ・シカゴ”の一員として、順調に歩んでいるライ。今シーズンから新しく加わる演目では、主役マイラの代役としてレッスンしていた。ある日、マイラがレッスン中に怪我をして、ライにチャンスが訪れるが……。", "2003-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO WONG FOO，THANKS FOR EVERYTHING！JULIE NEWMAR", "３人のエンジェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ドラッグ・クイーン３人が、全国大会へ向かう途中立ち寄った田舎町を優しさと心意気で活気づけていくロード・ムービー。女装したゲイが美を競うドラッグ・クイーン・コンテストのニューヨーク地区大会で優勝したノグジーマとビーダ。全国大会への切符を手にした二人は、優勝を逃し失意のドン底にいたチチを誘い、ポンコツのキャデラックでハリウッドへと向かう。しかし途中、車の故障で３人はスナイダースビルという田舎町に立ち止まるが、町はまるで時が止まってしまったかのような雰囲気で住む人々にも精彩がない。ホテルの女主人キャロル・アンは夫の暴力に耐えかね、老婆のクララは失語症になっていた。そんな町の人々に３人は持ち前のパワーを発揮し勇気や活力を吹き込んでいく。W・スナイプス、P・スウェイジ、J・レグイザモという、男くさい俳優陣がドラッグ・クイーンを熱演。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COUP DE FOUDRE", "女ともだち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "フランスの女流監督D・キュリスが、女性同士の愛を描く。戦争中、収容所入りを逃れるため愛のない結婚をしたレナと、新婚まもなく夫を射殺され、今は別の男と結婚したマドルーヌ。互いに暗い過去を持つ二人の人妻は、やがて精神的に惹かれていく……。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪物はここにいる", "怪物はここにいる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "", "2008-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEGAFORCE", "メガフォース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "“緊急機動軍団メガフォース”—— それは世界各国の超エリート戦士で構成される、極秘の科学戦隊だ。彼ら精鋭600名は、サハラ砂漠を荒らす戦車部隊を撃破するため、アフリカへと向かった……。SF的な未来兵器、米軍全面協力の戦闘シーンなど、見せ場十分。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Harry Potter and the Half-Blood Prince", "ハリー・ポッターと謎のプリンス", "", "", "http://image.eiga.k-img.com/images/movie/53228/315x210.jpg?2081", 154, "人気シリーズ第6作。ハリーの宿敵ヴォルデモートの過去が描かれる。幼少期のヴォルデモート＝トム・リドルを演じるのは、レイフ・ファインズの甥ヒーロー・ファインズ・ティフィン。ヴォルデモートとの最終決戦が迫っていることを予感するダンブルドア校長は、ハリーとともにヴォルデモートの守りをとく手がかりを見つけようと、かつて学生時代のトム・リドルを教えたこともあるホラス・スラグホーンを魔法薬学教授として学校に迎える。", "2009-07-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Excess Baggage", "エクセス・バゲッジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "A・シルヴァーストーンが設立した『ファースト・キス・プロダクション』の第１回作品。オーディション、キャスティングなどにもかかわった彼女が、勝ち気な富豪の令嬢を演じるラブ・コメディだ。自分を顧みない父親をギャフンといわせるため、偽装誘拐作戦を実行したエミリー。ところが、彼女がトランクに身を隠した愛車を、自動車泥棒のチンピラ青年ヴィンセントが盗んでしまい、二人は奇妙な逃避行を繰り広げるはめに。ビデオタイトルは「エクセス・バゲッジ」。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖怪天国 ゴースト・ヒーロー", "妖怪天国 ゴースト・ヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ハイテク技術が駆使された高層ビルの工事現場で、地中から不思議な石碑と一振りの刀が発見される。それから１年後、完成した高層ビルでとてつもない怪異現象が次々と巻き起こって……。様々なメディアを往来して表現活動を続けるビジュアリスト手塚眞が、新境地に挑むファンタスティック・アクション。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Little Pond", "小さな池 1950年・ノグンリ虐殺事件", "", "", "http://image.eiga.k-img.com/images/movie/56768/315x210.jpg?2145", 86, "朝鮮戦争下の1950年7月、韓国・ノグンリの鉄橋下のトンネルに逃げ込んだ村の住民数百人が米軍の無差別射撃で殺害された「ノグンリ事件」をもとに映画化。朝鮮半島の中程に位置する小さな谷間の村デムン・バウィゴルに、北朝鮮軍に負けた米軍が敗走してくる。何も知らない村人たちはとるものもとりあえず避難を始めるが……。2011年「真！韓国映画祭」にて上映。", "2011-05-28", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男児本色", "インビジブル・ターゲット", "", "", "http://image.eiga.k-img.com/images/movie/53798/315x210.jpg?2914", 129, "「プロジェクトBB」のベニー・チャンが製作・監督・脚本を手掛け、ニコラス・ツェー、ショーン・ユー、ジェイシー・チャンら香港の若手スターが競演を果たしたポリス・アクション。現金輸送車襲撃事件で婚約者を失ったチャン刑事は、犯人グループへの復讐を胸に誓う。一方、同じグループに襲撃されたフォン警部補も名誉挽回のために彼らを追っていた。やがて2人は、兄を襲撃事件の容疑者に仕立て上げられてしまったワイ巡査と出会い……。", "2008-08-30", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にごりえ", "にごりえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "明治の女流作家・樋口一葉の３つの短編小説『十三夜』『大つごもり』『にごりえ』をオムニバス形式で映画化した作品。文学座の団員が総出演、それぞれが息の合った演技をみせる。監督の今井正は一葉の描く明治の市井の人々の生活を原作に忠実に再現、その緻密な演出手腕は見事の一語につきる。キネマ旬報ベスト・ワンに選出された傑作。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HORS SAISON", "季節のはざまで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "D・シュミットが、少年時代の記憶をもとに撮り上げたノスタルジックなムードの一編。祖父母が経営するスイス山中のホテルで幼少期を過ごしたバランタン。取り壊されることになったホテルを久しぶりに訪れた彼が、優しい祖父母や従業員たち、風変わりな客と過ごした日々を回想していく。", "1992-01-01", "26,7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Center Stage: Turn It Up", "センター・ステージ2 ダンス・インスピレーション！ ", "", "", "http://image.eiga.k-img.com/images/movie/54588/315x210.jpg?1969", 95, "バレエに情熱を注ぐ若者たちの栄光と挫折を描いた青春ドラマ「センターステージ」の第2弾。全米一のアメリカン・ダンス・カンパニーに入団することを夢見て、デトロイトの片田舎からニューヨークへやってきたケイト。しかし、厳しいバレエの世界と恋人との関係で、ケイトの苦悩の日々が始まる。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月光仮面・怪獣コング", "月光仮面・怪獣コング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 59, "国際暗殺団は８名の死刑囚を脱獄させ、彼らを使って山脇博士をはじめとする日本の10人の重要人物を暗殺せんと企てる。さらに彼らは怪獣コングを使って政府の高官を襲撃。ついに警察総監が怪獣コングに襲われた。そこに月光仮面が現れ、たちまちコングを撃退する。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浮雲", "浮雲", "", "", "http://ecx.images-amazon.com/images/I/21VSA2QET4L._SL210_.jpg", 124, "成瀬巳喜男監督の代表作であり、世界映画史に燦然と輝く名作中の名作。原作は成瀬がその出世作「めし」（1951）以来、「稲妻」（1952）、「妻」（1953）、「晩菊」（1954）と立て続けに映画化して成功した林芙美子の同名小説。戦時中、赴任先のインドシナで愛し合った妻ある男を追って引き揚げてきたゆき子は、次から次へと女を変える相手の自堕落さに、一時は外人相手の娼婦にまで身を落とすが、別れることができない。二人で新しい生活を始めるべく旅立った小島でゆき子は男に見とられながら病死する……。起伏の激しい物語展開でありながら、成瀬の冷徹な対象凝視の姿勢は一貫しており、主人公を演じる高峰、森の緊張感みなぎる絡み合いを捉えて、その演出スタイルは一つの頂点に達したといえる。地の果てまでも男を追うヒロインは、映画が描き得た最も鮮烈な女性像の一つであり、女優・高峰秀子の名は永遠に映画史に刻まれた。小津安二郎の言葉“俺にできないシャシンは溝口の「祇園の姉妹」と成瀬の「浮雲」だ”はあまりにも有名。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUNGA DIN", "ガンガ・ディン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "19世紀インドに駐留するイギリス軍の軍曹３人の冒険ロマンを描いた娯楽大作。カッター、マクチェスニー、バレンタインの３人の軍曹は、陽気で、勇敢で、友だち思い。ある日、血の女神カーリーを信奉する殺人者集団の攻撃を警戒して、３人は隣町に偵察に出かける。途中、カッターは、インド人の従者ガンガ・ディンから埋もれた財宝の話を聞き、夜になって黄金の寺院を目指す。だが、そこは殺人者集団の本拠地。カッターとガンガ・ディンの二人は発見され、たちまち捕らえられてしまう。RKO社の製作費の記録を塗り替えたといわれる作品。「インディ・ジョーンズ」シリーズとのつながりが面白い。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SARA", "サラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "入院中の夫の治療費に困ったサラは、夫の部下であるゴシタスブに工面してもらう。しかし、夫には父から相続した遺産の一部を使ったと嘘をつく。病気の治った夫は順調に出世をしていたが、ゴシタスブは仕事につまづき、サラを脅し始める。夫が援助しなければ事実をばらすというのだが、曲がったことの嫌いな夫は援助を断ってしまう。怒ったゴシタスブは事実をばらし、それを知った夫はサラに問いただしてくる。サラは、自分の苦労をわかろうとしない夫を見て、家を出て自立することを決意するのだった。イプセンの『人形の家』を下敷きにした心理劇。サラを演じたN・キャリミが、サン・セバスチャン映画祭主演女優賞を受賞している。", "1993-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名月佐太郎笠", "名月佐太郎笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "高田浩吉が義に厚く、腕っぷしの強い旅烏に扮した娯楽時代劇。旅の空で浅間の佐太郎は角兵衛獅子の一行と知り合う。実はそれは、陣次郎兵衛という武士と、松平家５万石の御落胤・亀千代君が、身を隠して江戸の上屋敷へ向かう姿だった。だが、次郎兵衛は殺され、代わって佐太郎が亀千代を江戸へ届けることになり……。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEN　MINUTES　OLDER：THE　CELLO", "10ミニッツ・オールダー／イデアの森", "", "", "http://ecx.images-amazon.com/images/I/41AK4ou5a2L._SL210_.jpg", 106, "“時間”をテーマに世界の名監督が短編映画を製作するオムニバス。有名監督が集まった姉妹編「…人生のメビウス」に対して、こちらはヨーロッパの大御所や実験精神あふれる前衛監督らが多く参加、アーティスティックな視点が貫かれた深遠な作品が目立つ。ラストを飾るフランスの巨匠J・L・ゴダールが、得意の映像とサウンドのコラージュを駆使して、強烈な印象を残す。ビデオタイトルは「10ミニッツ・オールダー　GREEN」、DVDは「10ミニッツ・オールダー　コレクターズ・スペシャル」に収録。", "2003-12-20", "7,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談 蚊喰鳥", "怪談 蚊喰鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "宇野信夫の原作を、森一生監督が正攻法で映画化した文芸怪談映画。常盤津の師匠とその情婦、そして按摩が、50両の金をめぐってみにくい争いを繰り広げ次々と自滅していく。船越英二が、得体の知れない双子の按摩を不気味に演じた。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Memoirs of an Invisible Man", "透明人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "　ある研究所で事故が起こり、そこに居合わせたニックの身に異変が。なんと彼の体は透明になってしまった！　透明人間ニックの存在に気づいたCIAは彼の能力を諜報活動に利用しようと考え、追跡を開始。一方、ニックは友人の紹介で知り合った女性アリスに助けを求めるが……。透明人間となってしまった男の逃避行を描くSFコメディ。透明化した人体のリアルでユーモラスなVFXも見どころ。", "1992-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女番長 野良猫ロック", "女番長 野良猫ロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "新宿にたむろするスケ番グループが、暴力団の絡んだボクシングの八百長試合に巻き込まれ、ついに暴力団と対決し自滅するまでを描く。スタイリッシュな長谷部の演出が見もの。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　VITA　E｀　BELLA", "ライフ・イズ・ビューティフル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "アカデミー賞で主演男優賞ほか３部門を受賞するなど、各映画祭で数々の賞を受賞した感動作。戦争という過酷な状況の中で、家族を愛し、守り続けた男の姿を笑いと涙で描く。1939年イタリアのトスカーナ地方。本屋になる夢を抱いてやって来たユダヤ系イタリア人グイドは、一目惚れした小学校教師ドーラと結ばれ、一人息子をもうける。だが、幸せな家庭を築いた彼らを強制収容所行きという試練が待ち受けていた。彼は息子を悲しませないために、これはゲームだと偽り、生きる希望を与えようとするが……。イタリアの名優R・ベニーニが監督、脚本、主演の３役をこなし、ユーモアと悲哀が入り交じった軽妙な演技で人生の素晴らしさをうたい上げる。『ボイス・オブ・ムーン』などの作曲家N・ピオヴァーニの優しく軽やかな音楽も印象的。", "1998-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVERYONE SAYS I LOVE　YOU", "世界中がアイ・ラヴ・ユー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "往年のミュージカルを意識した、名手W・アレンのラブ・ロマンス。複雑な家庭環境に育った少女DJを中心に、その一家の様々な恋模様が、陽気な歌やダンスとともに描かれる。アレン自身が扮するのは、離婚してパリで暮らす、DJの実の父親。失恋する度にニューヨークに戻って来る彼は、DJの協力を得て、セラピストに通う美貌の人妻にアタックするが……。アレンをはじめE・ノートンやJ・ロバーツら演技派俳優たちの、せつない恋心を語る歌声は聴きモノ。ヘタウマな踊りにも、妙に味がある。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("淑女は何を忘れたか", "淑女は何を忘れたか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "いつも女房の尻に敷かれている大学教授の小宮が、突然大阪から上京してきた姪の節子に刺激され、発奮。口げんかをしても引き下がらず、初めて女房に平手打ちをくらわす。それ以来、夫婦の仲はかえって良くなるのだった……。小津安二郎のトーキー第２作。晩年の小津作品に頻出する、中年もしくは老年夫婦と、若い世代の娘の間の対立を含んだ信頼関係というテーマが、初めて大きく取り扱われた作品である。しかし、晩年のそれとは違い、全編に小津の西洋指向、モダニストぶりがあふれ、見事なソフィスティケイテッド・コメディとなっている。めったに雨の降らない小津作品中、これは珍しくも雨の降る一編。助監督で吉村公三郎が参加。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バックドロップ・クルディスタン", "バックドロップ・クルディスタン", "", "", "http://image.eiga.k-img.com/images/movie/53628/315x210.jpg?2665", 102, "日本にも数多く存在するクルド人難民に焦点を当てたドキュメンタリー。映像系専門学校に通っていた野本大は、ひょんなことから出会ったクルド人のカザンキラン一家が強制送還の危機に直面していることを知り、クルド人難民についての取材を開始。UNHCR（国連難民高等弁務官事務所）に対して難民認定と第三国出国を必死でアピールするカザンキラン一家の運命を追うとともに、自らトルコへを訪れてその背景を探り出す。", "2008-07-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECRET ADVENTURES OF TOM THUMB", "親指トムの奇妙な冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "ある夫婦の間に突然、親指ほどの大きさの息子トムが誕生。だが、その存在を知った役人が彼をバイオ実験室へと連れ去る。そこは遺伝子を操作し、奇形の動物を生み出す恐怖の場所だった。奇形のトカゲとともに脱出をしたトムは、自分と同じ大きさの人々が住む廃棄処理場にたどり着き……。古典的童話『親指トム』に、異様なイマジネーションを交えて描いた人形アニメ。ゆうばり国際冒険・ファンタスティック映画祭審査員特別賞受賞。テーマ曲を元レッド・ツェッペリンのジョン・ポール・ジョーンズが作曲している。", "1993-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワールドクラシック＠シネマ2011 オペラ「カルメン」　ミラノ・スカラ座", "ワールドクラシック＠シネマ2011 オペラ「カルメン」　ミラノ・スカラ座", "", "", "http://image.eiga.k-img.com/images/movie/56458/315x210.jpg?1667", 175, "世界各地の有名オペラハウス、バレエ団の上演を映画館のスクリーンで上映する「ワールドクラシック＠シネマ2011」（全8作品）の1作。作曲家ビゼーの最後の作品としても知られる傑作オペラで、2009年ミラノ・オペラ座のシーズン初日の模様を上映。世界が注目するテノールのヨナス・カウフマンがドン・ホセを歌い、巨匠ダニエル・バレンボイムがタクトを振る。", "2011-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽気なギャングが地球を回す", "陽気なギャングが地球を回す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "人気作家・伊坂幸太郎の同名小説を映画化したクライムコメディ。他人のウソを見破ってしまう男・成瀬、正確な体内時計を持つ女・雪子、演説の達人・響野、天才的なスリの腕前を持つ青年・久遠という、それぞれ奇妙な特殊能力を持つ4人組銀行強盗が、何者かに横取りされた大金を奪いかえすべく奮闘する姿をスタイリッシュに描く。主演は大沢たかお、鈴木京香、佐藤浩一、本作がスクリーンデビューとなる故・松田優作の次男・松田翔太。", "2006-05-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOLDIER OF FORTUNE", "アフガン・フォース　戦場の黙示録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "記憶を失った元グリーンベレー隊員が、ソ連撤退前のアフガニスタンに潜入して極秘調査を続けるうち、異境世界に迷い込んでいく冒険ファンタジー映画。戦争アクションものの要素とSFミステリーを巧みに絡ませている。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I LUNGHI GIORNI DELLA VENDETTA", "星空の用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "G・ジェンマ主演のマカロニ・ウエスタン映画の１本。無実の罪で30年の強制労働を宣告され、テキサスの監獄に収監された男が復讐のため脱獄し、自分に罪を着せた一味に孤独の戦いを挑むというもの。", "1967-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gaspard et Lisa", "リサとガスパール　とびきりキュートなパリの住人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "アン・グッドマンとゲオルグ・ハレンスレーベンによるフランスの人気絵本シリーズを、初のCGアニメーションで映画化。「スペースロケット」「ちいさなともだち」「凧あげ」「マジックショー」の4話とスペシャルエピソード「リサのいもうと」で構成される。ウサギでもイヌでもない愛らしいキャラクター、おしゃまで元気な女の子・リサと心優しい男の子・ガスパールが、パリの街を舞台にお茶目ないたずらを巻き起こす。", "2010-09-11", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("D♯１ ～プロローグ・倒錯～", "D♯１ ～プロローグ・倒錯～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "幼児性愛者による幼女連続殺人事件の意外な成り行きを、CGによる感覚的な映像を交えつつドキュメンタリー・タッチで描く異色作。性倒錯者の主人公にとどまらず、芸能リポーター、刑事ら関係者の心理やワイドショーの過熱ぶりを描写。現代社会に渦巻く病理をえぐり出していく。ビデオ、DVDタイトルは「幼狩　ディー・シャープ・ワン」。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("必殺女拳士", "必殺女拳士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "1974年の「女必殺拳」に始まる志穂美悦子の空手ものの一編。ストーリーは、紋切り型の泥臭い復讐ものだが、志穂美のまぶしいまでの健康美とアクションが、すべての短所を吹き飛ばす。ラストで彼女と対決する天津敏の、ヘビー級の悪役ぶりが見もの。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRETTY BABY", "プリティ・ベビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "L・マルがアメリカで初めて撮った作品。ニューオリンズの娼婦の子に生まれた少女ヴァイオレットがたどる数奇な運命を描く。少女役B・シールズが、幼さの中にも妖しい色気を漂わせて好演。けだるい南部の風景を捉えたカメラワークも美しい。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狼と豚と人間", "狼と豚と人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "スラム街で育った三兄弟は生き方や考え方がそれぞれ違い、互いに敵視し合っている。長男“豚”は老母の金を奪ってヤクザの幹部になっている。次男“狼”は金を信じて人間を信じない一匹狼でヒモ稼業。三男“人間”は母親の死を見看ったあと、チンピラ・グループを結成。この３人が麻薬資金をめぐり、血で血を洗う戦いを繰り広げる。深作監督の演出は下層のアウトローの暴力の噴出をダイナミックに活写している。万力に手の甲を挟み込み拷問を加える描写はバイオレンスに満ちて、あとの「仁義なき戦い」シリーズなどの実録ヤクザ映画を先取りしたような迫力がある。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若き日の次郎長 東海一の若親分", "若き日の次郎長 東海一の若親分", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "マキノ雅広監督・中村錦之助主演による“若き日の次郎長”ものの第２作。お蝶と祝言を済ませ清水港をあとにした次郎長一家は、秋葉の火祭りに乗り込み、人身売買で悪どく稼ぐドモ安らをやっつける。マキノ雅広十八番の題材だけにソツのない仕上がり。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’E´CUME DES JOURS", "うたかたの日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "“永遠の青春小説”として、今もなお読者を増やし続けるB・ヴィアンの同名小説を映画化した奇作。おしゃれでキュートな二組のカップルが繰り広げるカラフルなラブ・ストーリー。ジャズを奏でると美味なカクテルができ上がるピアノや、肺に睡蓮の花が咲いてしまった女の子など夢のようなストーリーが次々と展開される。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後の顔役", "最後の顔役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "朝比奈建設社長の菊屋によりシンジケート加入を拒否されたブラウンは、手先の山岡とともにその報復として拳銃乱射事件を起こす。子分の白滝らとともに挑戦状を叩きつけた菊屋は、山岡の麻薬組織を次々と襲いつぶした。そんな時、名古屋の弟分こと芝木が菊屋を訪ねて来る……。片岡千恵蔵が任侠道を貫こうとする古いタイプの大親分を好演。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Manchurian Candidate", "影なき狙撃者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "　朝鮮戦争の最中、中共軍の捕虜となったマーコ大尉の小隊はのちに釈放され、本国へ帰還。その後、マーコは不可解な夢を見るようになっていた。やがてマーコの部下だったショウの妻とその父親が何者かに殺されるという事件が起こる。洗脳による恐るべき暗殺計画がついに決行される！　名匠ジョン・フランケンハイマー監督の代表作として知られるサスペンス・スリラー。リバイバル公開時のタイトルは、「失われた時を求めて」。04年にリメイク「クライシス・オブ・アメリカ」も製作された。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FLUFFER", "ハードコア・デイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ゲイ・ポルノ業界の内幕を描く愛憎劇。ドラッグに依存するポルノ・スターのジョニー、彼に心引かれてゲイ・ポルノ映画のカメラマン助手となったナイーブな青年ショーン。そんなふたりの微妙な関係に、ジョニーの恋人である女性ストリッパーが絡み、3人の破滅的な愛と葛藤のドラマが展開していく。カリスマ的な主人公ジョニーを演じるのは、TVシリーズ『ベイウォッチ』のレギュラーを務めたスコット・ガーニィ。元ブロンディのデボラ・ハリーがストリップ劇場の女主人役で助演している。映画界への就職を夢みるショーンは、間違って借りてきたゲイ・ポルノの主演俳優ジョニーに心奪われる。ショーンはカメラマン助手としてジョニーに近づくが、彼には恋人がいて……。", "2005-09-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Raggare!", "怒れる若者たちの遊び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「長くつ下のピッピ」シリーズのオル・ヘルボム監督初期の作品。スウェーデンを舞台に、怒れる若者たちの破滅的な青春を描く。ロフェは自分の恋人ビバンが他の男と浮気したことに腹を立て、仲間たちとともに彼女に暴行を加える。ボロボロになったビバンを救ったのはラッセという青年で、2人はそのまま一夜を共にしてしまう。怒ったロフェはラッセとの闘いの末に再びビバンを取り戻し、彼女を連れて自動車で走り去るのだが……。", "1959-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tanztraume - Jugendliche tanzen Kontakthof von Pina Bausch", "ピナ・バウシュ　夢の教室", "", "", "http://image.eiga.k-img.com/images/movie/57608/315x210.jpg?1811", 89, "ダンス経験のない40人の少年少女が世界的舞踏家ピナ・バウシュの下に集まり、バウシュの代表作として知られる「コンタクトホーフ」の舞台に立つまでの10カ月を追ったドキュメンタリー。演劇好きの少年や不慮の事故で父を亡くした少女など、バウシュの下に集まったティーンエイジャーは、性格も家庭環境も全く異なるが、ただひとつ共通するのはダンス経験がないこと。バウシュ自身によるこの企画に参加した少年少女は、厳しい特訓に時に弱音を吐きながらも、ひとつの目標に向かって成長していく。バウシュの貴重な指導風景や創作の裏側を映し出している。", "2012-03-03", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stage Struck", "女優志願", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "　エバは舞台女優になることを夢見てニューヨークへやってきた。やがて劇作家のシェリダンと知り合った彼女はオーディションを受けるが、それに失敗。その後、プロデューサーであるイーストンのパーティで新鮮な才能を披露し、彼を惹きつけることに。ところがエバは酔ったあげく、イーストンと一夜をともにしてしまう……。33年の「勝利の朝」をリメイク。少女がブロードウェイで成功を収めるまでを描いた物語。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍たま乱太郎", "忍たま乱太郎", "", "", "http://image.eiga.k-img.com/images/movie/55708/315x210.jpg?1876", 100, "NHK教育でアニメ化され人気を集めた尼子騒兵衛によるギャグコミック「落第忍者乱太郎」を、NHK大河ドラマ「天地人」や「桜田門外ノ変」の人気子役・加藤清史郎主演で実写映画化。「ヤッターマン」「十三人の刺客」の三池崇史監督がメガホンをとる。エリート忍者を目指して忍術学園に入った乱太郎は、友達のしんべヱやきり丸と一緒にドジを繰り返す日々を過ごしていた。そんなある日、忍術学園の上級生である髪結いの息子・タカ丸の家に暗殺者が現われる。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAYDIRT", "ペイダート　この家、一獲千金！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "刑務所勤めのさえない心理学者ウィリスは、ある日老囚人から、昔、隠したマフィアの裏金10億円の話を聞く。さっそく金が埋まっている家を探し出すが、そこには離婚寸前のヒステリー女ジェシカが住んでいた。ジェシカ役は「ホーム・アローン」でケビン少年のことを忘れてしまう母親に扮したK・オハラ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wisdom Teeth", "オヤシラズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 3, "", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Man in the Iron Mask", "仮面の男", "", "", "http://ecx.images-amazon.com/images/I/51EfhVZD1bL._SL210_.jpg", 132, "文豪アレクサンドル・デュマの小説を、「ワンス・アンド・フォーエバー」のR・ウォレスが映画化した歴史ロマン。1662年パリ。民衆は飢餓に苦しめられ暴動が続出。それでも、若き国王ルイ14世は民衆の声に耳を貸さず傍若無人に振る舞っていた。しかし、彼にはある秘密があった。地下牢に双子の弟フィリップを幽閉していたのだ。このことを知った元四銃士の勇者たちが、腐敗した政治を立て直すためフィリップの救出に挑む。L・ディカプリオが、富と権力に取り憑かれた暴君ルイ14世と悲劇の王子という初の二役に挑戦、善と悪を演じ分けた。また、ベテラン俳優たちが演じる四銃士が脇を固め、政治的策略やロマンス、友情と裏切りが絡むドラマティックな物語に深みを与えている。", "1998-08-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帰らざる日々", "帰らざる日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "第３回城戸賞入選作『夏の栄光』を、藤田敏八が映画化。カット・バックを大幅に導入して、青春の日の恋や友情を感動的に描いている。６年ぶりに帰郷する主人公が、列車の中で高校生の頃の自分を回想するという構成で、親友との三角関係、初体験、親子の確執などが語られていく。ドラマティックなラストの良さはもちろん、誰もが体験するような小さなエピソードも魅力的だ。特に、主人公と初体験の相手・竹田かほりとの別れのキスシーンは日本映画史上においても屈指の出来。ルーティーンを破る最高のシチュエーションが妙にリアルだった。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Boiling Point", "ボイリング・ポイント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "偽札造りの犯人を追う最中に、相棒を犯人に殺されてしまったベテラン捜査官マーカー。復讐に燃える彼は捜査を続け、やがて容疑者として老練の詐欺師レッド・ダイヤモンドの存在に行き当たる……。W・スナイプスと敵役D・ホッパーの強烈な個性派共演で描いたサスペンス・アクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パンダ物語 熊猫的故事", "パンダ物語 熊猫的故事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "パンダの飼育に夢を託した日本の少女が、中国に渡り、傷ついた子パンダを助け出し、大事に育てていく。パンダの成長とともに少女自身も一人前になっていく姿を、中国の少年との友情を絡めて描いている。撮影途中で、監督交代のトラブルなどがあったが、のべ187日間の中国ロケーションを主演の八木さおりが頑張り抜いた。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍びの者 霧隠才蔵", "忍びの者 霧隠才蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "前作から一転、豊臣方の霧隠才蔵が主人公となった。大阪冬の陣が終わり豊臣家滅亡の危機が近づくなか、家康暗殺のため命を賭ける真田幸村配下の霧隠才蔵の活躍を描く。迫力のある合戦シーンに加えて戦士としての忍者を描き、娯楽性十分。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カポネ大いに泣く", "カポネ大いに泣く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "「悲愁物語」の大失敗から８年後、鈴木清順が再び松竹でメガホンを執った作品。しかし、「ツィゴイネルワイゼン」「陽炎座」と続いた前作、前々作が大正ロマンを見事に再現した美しいセットの中に絢爛たる美学を散りばめてみせたのに対し、なんとも安っぽいセットを使って撮られたこの作品は、再びあの玄妙な清順世界を期待した観客たちに肩すかしをくらわせた。昭和初期の浪花節語りがアメリカに出かける話だが、“海外ロケができたって別に変わりはしない”という清順はハリボテ、カキワリのアメリカを涼しい顔ででっち上げる。賛否両論分かれる作品だが、清順自身はあいかわらずマイペースであった。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I'M FLASH!", "I'M FLASH!", "", "", "http://image.eiga.k-img.com/images/movie/57698/315x210.jpg?1427", 0, "藤原竜也と「青い春」「ナインソウルズ」の豊田利晃監督が初タッグを組み、宗教団体の若きカリスマ教祖が直面する衝撃的な運命を描いた人間ドラマ。新興宗教団体「ライフイズビューティフル」を率いる教祖・吉野ルイは、ふとしたことから謎の美女・流美と出会う。しかし、ルイが引き起こした交通事故によって流美は植物状態になってしまい、教団の幹部であるルイの母や姉は事故のもみ消しを計る。3人のボディガードとともに南海の孤島に避難したルイは、教団の陰謀を暴く重大な決断を下すことになる。ボディガードの1人で、ルイの存在にひかれながらも対峙することになる新野風を、藤原とは初共演となる松田龍平が演じる。", "2012-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MATEWAN", "メイトワン1920", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "J・セイルズの監督第５作。1920年にウエストヴァージニアの小さな炭鉱町“メイトワン”で実際に起きた大虐殺の事件をもとに、低賃金でも働く移民や黒人たちを雇い入れようとする経営側と、組合を作ってそれに対抗しようとする労働者たちとの激しい対立を丹念に描く。カメラのレンズにソフトフォーカスを使用した美しい映像に注目したい。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moby Dick", "白鯨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "　風来坊の青年が捕鯨船の乗組員となる。その船長エイハブは顔に深い傷を負い、鯨の骨で作った義足をつけていた。モビー・ディックと呼ばれる白鯨に片足を食いちぎられたエイハブは復讐に燃える。捕鯨船は大荒れの海を乗り越え、やがて白鯨と遭遇。ついに対決の時が来た！　鯨との壮絶な対決は大迫力。グレゴリー・ペックの鬼気迫る熱演も見もの。", "1956-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　カンナさん大成功です！", "劇場版　カンナさん大成功です！", "", "", "http://image.eiga.k-img.com/images/movie/53658/315x210.jpg?4241", 0, "2006年に韓国で映画化されて大ヒットを記録した鈴木由美子の同名コミックを、日本で再映画化。主演は人気モデル山田優と、お笑いコンビ“南海キャンディーズ”のしずちゃんこと山崎静代。幼い頃からいじめられてきたブスで卑屈な神無月カンナは、貯金をはたいて全身整形を行い、スーパー美人へと生まれ変わる。カンナは片思いの相手、浩介に近づくため彼の会社の受付嬢になるが……。", "2009-01-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAU GESTE", "ボージェスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "外人部隊の様子を描いたレンの小説『ボージェスト』の３度目の映画化。愛人の夫の罪をかぶって外人部隊に加わったイギリス紳士ボー。かつて部下の失敗で階級を下げられたことのある特務曹長ダジノーは、そんな彼に冷たくあたった。やがて彼ら外人部隊は孤立するジンダーヌーフ砦に派遣され、凶悪なツアレダ族の激しい攻撃を受ける。そしてボーとダジノーだけが生き残るのだった。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PORTRAIT OF JENNIE", "ジェニーの肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "R・ネイサンの幻想小説の映画化。貧しい画家が、偶然出会った少女ジェニーの肖像画を描き、一躍有名になる。だが彼女の生い立ちには意外な事実が隠されていた……。きめ細かな演出で綴った、情感豊かなラブ・ロマンス。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN AIR DE FAMILLE", "家族の気分", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「百貨店大百科」「猫が行方不明」の人気監督C・クラピッシュの長編第４作。フランスで 230万人を動員する大ヒットを記録し、セザール賞の最優秀助演男優賞、最優秀助演女優賞、最優秀脚本賞の３賞を獲得したユニークな人間ドラマ。1994年にパリで初演された戯曲が原作。フランスの田舎町のとあるカフェに舞台を限定し、その店を切り盛りする店主の長男アンリとバーテンのドニ、そしてそこを訪れたアンリの弟フィリップと妻のヨヨ、それに妹のベティとアンリの母の計６人による会話劇となっている。アンリは妻のアルレットが家出してイライラし通し。ドニは恋人のベティがつれないのが気がかりで仕方がない。フィリップはTV出演の自分の映り具合が気になり、ヨヨは誕生日だというのに気持ちが落ち着かない夫に不機嫌。母も恒例の晩餐会がなかなか始まらないため、フィリップに文句ばかり。そんなメナール家のささやかな騒動が、時にコミカルに時にアイロニカルに綴られていく。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キクとイサム", "キクとイサム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "水木洋子のオリジナル脚本を名匠・今井正が映画化、占領時代の落とし子である混血児姉弟が明るく生きる様を乾いたタッチで描く。主人公の二人に起用された映画初出演の混血児たちの存在感が素晴らしい。実年齢の倍近い老婆に扮する北林谷栄の熱演も見もの。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪魔が棲む家2001", "悪魔が棲む家2001", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "15歳から21歳までの美少女アイドル14人が出演する３話オムニバスのホラー。水泳部に所属する６人が合宿所で遭遇する恐ろしい殺人事件、クリスマスの夜のパーティ前にヒロインたちを襲う血まみれのサンタクロース、友人の田舎を訪れた主人公が気味の悪い幻覚にさいなまれる、といったエピソードをフレッシュな面々で描く。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴルフ夜明け前", "ゴルフ夜明け前", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "創作落語として芸術大賞を受賞、小説に書き下してベストセラーとなった桂三枝の原作を映画化。吉本興業のコメディアンたちが勢ぞろいして、京都の郊外のゴルフ場でゴルフ外交実現のために奔走する坂本竜馬と近藤勇の珍妙なマッチ・プレーを描く。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AROUOND THE WORLD UNDER THE SEA", "海底世界一周", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "海底に地震警報装置を設置するため、海底世界一周をする科学者グループの姿を描いたSF映画。超深海潜水艦ハイドロノート号を舞台に、女性科学者をめぐる男女間のトラブルなどを織り交ぜて、スリリングな海底冒険譚が展開。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の紋章・喧嘩状", "男の紋章・喧嘩状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ヤクザの賭博を見逃すかわりにワイロを要求して私腹をこやす悪徳刑事が、悪玉ヤクザと組んで正月の飾りものを仕切る権利を奪い、竜次の子分たちをささいな罪で逮捕し次々に拷問にかける。しかし激怒した竜次は、伝家の宝刀を抜き悪玉を倒す。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妻は告白する", "妻は告白する", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "日本的束縛の世界に生きる女性が、恋愛を契機になりふり構わず一直線に情念に走る姿を描き続けてきた、増村保造＝若尾文子コンビの最高作。登山パーティーの一人が転落死する。メンバーは、大学の助教授とその妻、助教授の教え子で妻の愛人である男の３人。死んだのは助教授で、ザイルを切った妻は、その行為が事故を最低限度にするための緊急避難であったか、それとも殺意によるものだったのか裁判にかけられる。ラスト近く、雨でびしょ濡れになった和服姿で、男の会社を訪れる若尾の姿は、増村の演出と若尾の熱演により、恋に狂う幽鬼のような、すさまじい迫力を出している。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヘレンケラーを知っていますか", "ヘレンケラーを知っていますか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "生きる意味を見失った少年が、目と耳が不自由でも自立して生きる老女との出会いによって再生していく姿を描いた感動作。山口県で暮らす北嶋絹子は、目が見えず、耳が聞こえないという障害を抱えながらも、1人で自立した生活を営んでいた。ある日、絹子の家に、リストカットを繰りかえす少年・祐介がやって来る。祐介は前向きに生きる絹子との交流を通し、自分を見つめなおしていく。NHKドラマ「おしん」の名子役として知られる小林綾子が、15歳から78歳までのヒロインを熱演。", "2007-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("京極真珠", "京極真珠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "京都のインディーズ・シーンで活躍する佐藤訪米が手掛けた劇場用16ミリ作品。同棲相手の女性を探しまわるヒロインのさ迷いを、幻想的に描き出す。珠子と鏡子は祇園の廃屋ビルで共同生活を送っている。ある日、鍋の食材を求めて外出した珠子が戻ってみると、鏡子が消えていた。鏡子を探して街に出た珠子は、謎めいた坊主に導かれ迷宮の世界へと迷い込んでいく。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千里眼", "千里眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "“ミドリの猿”なるテロ集団による爆破事件が日本各地で発生。米軍基地に乗り込んでミサイル攻撃の拡大を阻止したのは、“千里眼”の異名を持つカウンセラー、友里佐知子だった。その一部始終を目撃した若き航空自衛官、岬美由紀は、独自の調査を行ううちにさらなる怪事件に巻き込まれていく。『催眠』で知られる松岡圭祐の同名小説を映画化したサイコ・サスペンスで、クライマックスでは水野美紀が香港アクションばりの必殺カンフーを披露する。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ICE", "グランド・クロス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "世紀末に地球を襲う大異変の恐怖を描くパニック映画。異常な暑さが続くアメリカ。天文学者キスラー博士は、太陽黒点の異常膨張を観測、地球に氷河期が訪れると警告する。やがて異常寒波が全土を襲撃。都市機能が麻痺し、人々はパニックに陥る。生き残る道は、赤道直下に残された安全地帯に避難することだけ。タイムリミットは48時間。ロス市警のドレイク刑事は凍死寸前のキスラー博士を連れて、猛吹雪に立ち向かっていく。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美しき母", "美しき母", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "鉱山に手を出して失敗した父・富太郎。妻の光代と子供の日出夫は、以前女中だった喜代の家に厄介になることに。光代は製紙工場の女工として懸命に働き、日出夫も中学合格を目指して懸命に勉強する。しかし、入学試験の朝、父の危篤を知らせる電報が届いて……。夫と別れたくましく生きる母の姿を原節子が好演。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("廃市", "廃市", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "古い歴史を持った小さな運河の町が火事で焼けたというニュースに、江口は10数年前卒業論文執筆のため、この町を訪れた時のことを思い出す。泊まり込んでいた一軒の旧家、そこの美しい娘・安子、そしてその姉・郁代のことを……。オプチカル合成など、これまで技巧に走りがちだった大林宣彦が、シンプルに撮り上げた作品で、全編16ミリで撮影された。私的映画風味わいの深い低予算作品だが、画面や音への繊細な配慮は群を抜く。安子役・小林聡美は、その後の彼女の役柄とは反対に、正当派美少女を好演。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆音", "爆音", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「真実一路」「五人の斥候兵」といったまじめな大作の多い田坂具隆監督には珍しい、のどかな喜劇。ある農村が献納した飛行機・愛国号に村長の息子が乗って飛来するとの知らせで村中大騒ぎ。空を仰ぐ村人の前を愛国号は悠然とアクロバット飛行をして故郷に錦を飾った。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK ORCHID", "黒い果実 ～愛と欲望のはて～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1950年代のハリウッド。欲望と情熱と権力がうごめくこの世界は、まさにスキャンダルの都。そんな中、女優のナチカ・ジャクソンは、一人皆から距離をおき、孤独を好んでいた。ある日、車の事故で出会った男性と一度きりのつもりで関係を持つ。だが、偶然にも彼と再会したために、大スターへの道を歩き始めていたナチカの人生が狂い出した……。セクシーでスキャンダラスなハリウッド女優を、M・ファイファーが熱演するTVフィーチャー作品。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蟲師", "蟲師", "", "", "http://image.eiga.k-img.com/images/movie/1128/315x210.jpg?3731", 131, "約100年前の日本。目には見えない謎の生命体“蟲（むし）”が引き起こす不可解な現象を紐解き、蟲を鎮めて旅をしている蟲師のギンコ（オダギリジョー）は、旅の途上で自身の過去と向き合うことになるが……。「AKIRA」「スチームボーイ」の大友克洋が、91年の「ワールドアパートメントホラー」以来の実写で、漆原友紀の同名人気コミックを映画化。第63回ベネチア国際映画祭のコンペティション部門に出品された。", "2007-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROVA D’ORCHESTRA", "オーケストラ・リハーサル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "13世紀に建てられた由緒ある寺院の礼拝堂。今日の演奏会はTVの取材もあり、演奏者たちははりきっている。だが、リハーサルはやがて彼らの不満の声で大混乱となり、さらに異様な事態に……。N・ロータの急死によって、フェリーニとのコンビの最後となった作品。", "1978-01-01", "4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIRACLE ON INTERSTATE 880", "カリフォルニア大激震　−ハイウェイI−880の奇跡−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1989年10月17日、大リーグ・ワールドシリーズに沸くカリフォルニア州オークランドを大地震が襲う。崩壊したハイウェイでは多くの犠牲者が出たが、その中には幼い姉弟キャシーとフリオもいた。1989年に実際に起きた、サンフランシスコ大地震の惨劇をもとに作られたドラマ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BROKEN CHAIN", "ブロークン・チェーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "18世紀半ば、アメリカ独立戦争中の実話をもとに製作された作品。独立戦争の真っただ中、目覚ましい活躍を続けるウィリアム・ジョンソン大佐は、この戦争の中での自分の立場を守るために、一つの決断をする。それは地元のネイティブ・アメリカンの青年をイギリスの学校へ通わせることであった。しかし、この策略は後に、二つの種族にとって大きな影響を及ぼすことになる。正義と信念を武器に、時代を駆け抜けた男たちの戦いを描く戦争アクション・ストーリー。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("好色元禄(秘)物語", "好色元禄(秘)物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "関本郁夫が川谷拓三ら“ピラニア軍団”を起用して監督したポルノ映画。元禄時代の京都を舞台に、色と欲に狂った好色姉妹が、あらゆるテクニックを駆使して次々に男どもを轟沈させていく。潮吹き女優・窪園千枝子が特別出演。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("絵の中のぼくの村", "絵の中のぼくの村", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "絵本作家・田島征三の自伝的エッセイを映画化。ある夏、征三は双子の兄・征彦のアトリエを訪ねる。二人の夢のような少年時代を合作で絵本にする打ち合わせのためだ。そして物語は昭和23年、高知県の田舎村へ。優しい母と、めったに帰ってこない父、思春期の姉。そんな一家にあって、絵が好きでいたずらも大好きな兄弟は鳥やナマズと遊び、よその畑を荒らしたりもするやんちゃな毎日を過ごしていた。不思議な老婆や妖怪なども登場して、少年ならではの、夢と現実に境のないファンタジックな世界を描く。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紙屋悦子の青春", "紙屋悦子の青春", "", "", "http://image.eiga.k-img.com/images/movie/1468/315x210.jpg?2354", 111, "今年4月に亡くなった「竜馬暗殺」「父と暮らせば」などで知られる黒木和雄監督が現在、最も注目される劇作家といわれる松田正隆の戯曲を映画化。昭和20年、特攻基地のあった鹿児島県出水市を舞台に、ひとりの女性と間もなく特攻隊として出撃する1人の兵士とその親友の三角関係を静かな映像で綴った戦争ドラマ。主人公紙屋悦子には原田知世。原作者の母親が主人公のモデルになっている。", "2006-08-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PART VI 偽装結婚", "難波金融伝 ミナミの帝王　劇場版PART VI 偽装結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "人気コミックを映画化したシリーズ第６作。平成不況をものともしない、天下無敵の高利貸し・萬田銀次郎が悪徳業者を相手に今回も大暴れする。借金を焦げ付かせている男が台湾人ホステスのリンダと結婚したと聞き、銀次郎は妻のところへ取り立てに向かった。だが、リンダは夫の名前すら知らない。実は結婚は偽装で、その裏にはアジアの女性を相手に偽装結婚を斡旋し、日本永住権を高く売り付ける組織があったのだった……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Galaxy Quest", "ギャラクシー・クエスト", "", "", "http://image.eiga.k-img.com/images/movie/1398/315x210.jpg?4163", 102, "79年から放映された人気TVシリーズ「ギャラクシー・クエスト」は、宇宙船プロテクター号の乗組員たちの活躍を描いたSFもの。放送打ち切りから20年たった今でも、コンベンション会場は熱狂的なファンで超満員だった。しかしそこへ紛れ込んでいたのは“サーミアン”と名乗るネビュラ星エイリアン。TV放映を宇宙で傍受しドキュメンタリーと勘違いした彼らは、宿敵エイリアンと戦うため力を貸して欲しいと出演者たちの元へやってきたのだ。", "2001-01-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モスリン橋の、袂に潜む", "モスリン橋の、袂に潜む", "", "", "http://image.eiga.k-img.com/images/movie/56128/315x210.jpg?2385", 106, "季節が雨季と乾季に別れた架空の日本を舞台に繰り広げられるミステリードラマ。乾季が終盤を迎えるころ、モスリン橋のたもとで水死体が頻繁に発見される。三流雑誌の記者・蓮見は、先輩カメラマン・ツゲの失そうを追って訪れた遊郭の島カンテラで、ナゾの娼婦・白亜と出会う。いつしか白亜のとりこになっていく蓮見だったが、白亜の客が過去に何人も変死しているという噂を聞き……。", "2011-02-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春との旅", "春との旅", "", "", "http://image.eiga.k-img.com/images/movie/55118/315x210.jpg?4818", 134, "「バッシング」「愛の予感」の名匠・小林政広が、仲代達矢を主演に描く家族のドラマ。共演に徳永えり、大滝秀治、菅井きん、田中裕子、小林薫、柄本明、香川照之ら豪華キャストが揃う。4月の北海道。孫の春に面倒を見られながら静かに余生を過ごす元漁師の忠男は、人生最後の住まいを求め、親戚縁者を訪ねる旅に出る。家族との確執や過去との対面により、忠男と春は人生そのものをじっくりと見つめ直すことになる。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AMORE", "アモーレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "アンナ・マニャーニを主演にした二つのエピソードから成るオムニバス。第１話は電話の向こうにいる相手にマニャーニが独演で愛を語り続ける室内劇「人間の声」。第２話は妊娠した女性が独力で子供を産もうとする「奇蹟」。どちらもマニャーニの圧倒的な存在感が画面いっぱいにみなぎった彼女の代表作といえよう。", "1948-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トラック野郎・熱風5000キロ", "トラック野郎・熱風5000キロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "化粧品のキャンペーン・ガールで売り出した小野みゆきがマドンナ。木曽の運送会社の男勝りの娘・夏と出会った桃次郎はそこで働き、彼女への接近を図る。だが、意外にも従業員・ノサップの勝が彼女の幼い頃の命の恩人だったと分かり……。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風雲児　長者番付に挑んだ男", "風雲児　長者番付に挑んだ男", "", "", "http://ecx.images-amazon.com/images/I/517TPEADQ5L._SL210_.jpg", 90, "どん底から這いあがり長者番付トップとなった実在の人物、浅岡裕二をモデルに描いた実録ドラマ。ソープ嬢のヒモとして堕落した生活を送っていた青年・浅岡は自分の生き方を見つめなおし、猛烈に働きはじめる。さまざまな困難を乗りこえながら独自のビジネスを展開し、和服の販売を中心に着々と業績を伸ばしていく浅岡だったが、ある事件をきっかけに突然逮捕されてしまい……。主演は「LIMIT OF LOVE 海猿」の美木良介。", "2006-06-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RISING SUN", "ライジング・サン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "日本企業がロサンゼルスに建てたビルの落成パーティーで娼婦が殺された。捜査を担当する黒人刑事ウェッブは、日本通の刑事コナーと組むことになった。日本人の習慣や考え方に分け入りながら、ウェッブは事件の真相に迫っていく……。ベストセラー作家M・クライトン原作によるサスペンス・アクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Butterfly Kiss", "バタフライ・キス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「イン・ディス・ワールド」等の作品で世界から注目を集め続けるマイケル・ウィンターボトム監督の長編デビュー作。全身ピアスと刺青だらけの娘ユーニスは、ジュディスという女を探し求めて街道沿いのガソリンスタンドを訪ね歩いている。そして店員がジュディスではないことを知ると殺してしまうのだ。やがて彼女は、あるガソリンスタンドで働くミリアムという娘に出会う。ミリアムはユーニスに恋心を抱き、彼女の後を追うのだが……。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Express", "エクスプレス　負けざる男たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANG　THE　DJ", "ハング・ザ・DJ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "クラブシーンの頂点に君臨する世界のトップDJの姿を収めたドキュメンタリー。N.Y.の大物ジュニア・ヴァスケスやそのライバル、ロジャー・サンチェスらがDJのアーティスト性について率直に意見を述べ、ターンテーブルの妙技を披露する。ダンス・ミュージックの多用化、ドラッグ問題などのレイブ・カルチャーの現在も視野に入れており、クラブ未体験者にも楽しめる作品になっている。", "1998-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEDAZZLED", "悪いことしましョ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "スタンリー・ドーネン監督の同名作品を、現代の特殊効果技術をフルに使ってリメイクしたファンタスティック・コメディ。コンピュータ関連会社に勤めるエリオットは少しピントのずれた気弱な男。職場のみんなからは仲間外れ。同じ職場のアリソンに惚れているが話をしたこともない。そんな彼の前に現れたのが美女の悪魔。魂と引き換えに７つの望みを叶えるという彼女の申し出に、エリオットはつい契約を交わしてしまう。その後、彼はアリソンの気を引くために大金持ちの権力者、繊細で感情豊かな青年、プロ・バスケの選手など次々と夢を叶えてもらうが、どれも欠点ありの失敗作。彼は真の幸福を手に入れられるのか？ B・フレイザーが夢が変わるごとに見せる変身ぶりや、ちょっとエッチな悪魔を演じるE・ハーレーのセクシーさも見もの。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぷかぷかジュジュ", "ぷかぷかジュジュ", "", "", "http://image.eiga.k-img.com/images/movie/57978/315x210.jpg?3309", 25, "アニメーションの制作環境の問題解決や振興を図ることを目的とした文化庁の若手アニメーター育成プロジェクト「アニメミライ」2011年度制作の4作品のうちの1本。仕事ばかりでなかなか海に連れて行ってくれない父親に不満を抱く5歳の少女ミカは、家のビニールプールでひとり寂しく遊んでいた。そんな時、フロートボードのジュゴンのジュジュが言葉を話しはじめ、ミカを不思議な海の世界へと連れ出すが……。美しい海中で繰り広げられる少女の冒険を描いたファンタジーで、ウォルト・ディズニー・アニメーション・スタジオ・ジャパンを前身とするアンサー・スタジオが制作。監督は、NHKの「大人女子のアニメタイム『川面を渡る風』」などに携わってきた川又浩。", "2012-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("前科 ドス嵐", "前科 ドス嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "渡哲也主演“前科”もの第２作。出所した主人公は博徒の長老のもとへ身を寄せるが、長老は悪玉の組長によって殺される。主人公は、単身殴り込み組長を刺し殺す。小沢啓一の演出には“無頼”シリーズほどの情念が感じられなかった。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAHARA", "サハラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "サッチャー首相の息子が国際ラリー参加中、サハラ砂漠で行方不明になった事件をヒントにした映画。舞台設定は1927年の北アフリカで、自動車王だった父の遺志を継ぎ女子禁制のレースに出場する娘の物語。B・シールズの男装が話題の一編。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("16 jyu-roku", "16 jyu-roku", "", "", "http://image.eiga.k-img.com/images/movie/33988/315x210.jpg?3535", 76, "「赤い文化住宅の初子」で主演に抜擢された東亜優をモデルに、女優を目指しひとり上京した少女が、淋しさを抱えながらも少しずつ歩き始めていく姿を繊細なタッチで綴った青春ストーリー。モデルとなった東亜優が主人公サキを自ら演じるほか、彼女の幼なじみに「あしたの私のつくり方」の柄本時生、憧れの俳優に「紙屋悦子の青春」の松岡俊介が扮する。監督・脚本は「青い車」など青春ドラマには定評のある奥原浩志。", "2007-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年探偵団 妖怪博士", "少年探偵団 妖怪博士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "江戸川乱歩の原作を映画化した“少年探偵団”シリーズの第１作。原子炉の設計図をめぐって、怪人二十面相と明智小五郎、そして明智を助ける少年探偵団が争奪戦を展開する。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Presents 合い鍵", "Presents 合い鍵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "角田光代の短編小説集「Presents」に収録された1編「合い鍵」を、広末涼子＆玉山鉄二主演で映画化したラブストーリー。編集プロダクションで働く由加里には、20歳の時から8年間交際を続けている恋人・博明がいる。フリーカメラマンとして活躍する博明とは、お互いの忙しさからなかなか会えずにいた。そんなある日、久々のデートに心を浮かせて出かけた彼女は、博明から突然の別れを告げられてしまう。", "2006-12-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE BOY BLUE", "ザ・タブー 暴かれた衝撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "父レイの暴力を恐れながらも、義母ケイトと情事を重ねる青年ジミー。ある日、両親が経営する酒場で中年の探偵が謎の死を遂げる。警察はレイに容疑を向けるが、結局事故死として片付けられた。そんな中、ジミーは自分がレイの子供でないと知って失踪してしまう。そして、探偵を雇っていた女が現れたことから19年前に起きた事件の全貌が明らかになっていく……。「白い嵐」のR・フィリップとN・キンスキーが禁断の愛に落ちる義理の母子を演じたサスペンス。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEARTS　OF　THE　WORLD", "世界の心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "フランスの村で生活する二つのアメリカ人家族、それぞれの娘と息子が婚約を交わす。しかし結婚式の準備中に戦争勃発の知らせが届き、娘マリーはドイツ軍に支配された村をさまようはめに……。第一次大戦中にD・W・グリフィスがイギリス政府の招きで撮りあげた戦意昂揚的な色合いの濃い戦争メロドラマ。悲壮感漂うリリアン、コケティッシュなドロシーというギッシュ姉妹の対照的な魅力も大きな見どころになっている。DVDは「D・W・グリフィス傑作選」に収録。", "1918-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・酔いどれ博士", "続・酔いどれ博士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "医術にも強いが、酒にはもっと強く、さらに腕っぷしは誰にも負けぬ勝新扮する“ギョロ松”こと大松伝次郎の活躍を描くシリーズ第２作。スラム街にやって来たギョロ松が街に巣くう悪党をブルドーザーまで動員してぶっ倒し、またふらりと去って行く。新藤兼人原作・脚本の暴力的ヒューマニズム映画。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EXPERT", "エキスパート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "元特殊任務のエキスパートのジョンは、最愛の妹を猟奇連続殺人犯に殺されるが、犯人は精神異常を装って極刑を免れる。ジョンは法に代わって犯人を闇に葬るべく、難攻不落の監獄に単身で乗り込むが……。おなじみ「狼よさらば」型の復讐アクション。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAT AND SUNLIGHT", "ヒート・アンド・サンライト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "破局に近づいた恋人との仲に執着し、落ち着きなく夜の街をさまよう写真家のメル。そんな彼の破滅的な一日をエネルギッシュに描き出していく。ジョン・カサヴェテスを敬愛する監督のR・ニルソンは、即興性を重視した演出で、主人公の心の痛みをリアルに表現。セリフはすべてアドリブで生み出されたとか。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAMES DEAN，THE FIRST AMERICAN TEENAGER", "ジェームズ・ディーンのすべて 青春よ永遠に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "３本の主演作を残して他界した永遠の青春スター、J・ディーンの伝記映画。未発表の貴重な記録フィルムを盛り込み、間に彼の友人や共演者、監督などのコメントをはさんで彼の素顔に迫る。ラストは「ジャイアンツ」での彼のセリフ“see you again”というのも、心憎い演出。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("左ききの狙撃者 東京湾", "左ききの狙撃者 東京湾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "麻薬取締官が射殺され、銃弾の角度から犯人は左利きと判定された。捜査一課の澄川刑事は麻薬関係者の立ち回り先を洗い始めるが、容疑者を尾行中、戦場での命の恩人である左利きの狙撃兵、井上と再会する……。俳優の佐田啓二の第１回企画作品。松山善三と多賀祥介のオリジナル脚本を、刑事ものに定評のある野村芳太郎が監督。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIXING THE SHADOW", "キング・オブ・ハーレー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "C・シーンの硬派な魅力が光るバイク・アクション。麻薬と武器の密売ルートを潰滅するおとり捜査官に抜擢されたダンは暴走族“ジャッカルズ”に潜入。持ち前の腕っぷしと度胸のよさで、仲間の信頼を得て密売の証拠を握る彼だったが、一方では奔放な暴力の世界に惹かれていく。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版 エースをねらえ！", "劇場版 エースをねらえ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "TVでは実写版連続ドラマも放映された、女の子たちが活躍するテニス・スポ根アニメ。ヒロインの岡ひろみ、華麗なプレーで魅せる“お蝶夫人”の愛称がある竜崎麗香、鬼コーチの宗方ら、魅力的なキャラクターが大活躍する。テニスの名門高校に入ったひろみは、麗香に憧れて得意でもないのにテニス部に入部する。だが、コーチは彼女の天才的なセンスを既に見いだしていた。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛・旅立ち", "愛・旅立ち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "近藤真彦と中森明菜の初共演で話題になった青春メロドラマ。不治の病におかされ、死の恐怖におびえる薄幸の美少女に、けなげな青年が生きる希望と勇気を与え、一度は愛の力で死をも乗り越えてしまうのだが……。砂浜で歌う二人の姿が印象的。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天保六花撰 地獄の花道", "天保六花撰 地獄の花道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "練堀小路の河内山宗俊は、武士たちに追われる元松江藩士・金子市之丞を救う。市之丞は公金横領の罪を着せられて殺された父の仇・家老北村大膳を狙っていた。河内山は、市之丞の父の無罪を晴らし、大膳の悪行を暴くため、松江藩邸に乗り込んで大芝居を打つ……。名匠マキノ雅広監督の娯楽時代劇。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cecil B. DeMented", "セシル・B ザ・シネマ・ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/1328/315x210.jpg?2401", 88, "ハリウッド女優のハニー・ホイットロックは、主演作のプレミア上映会でセシル・B・ディメンテッドと名乗る男とその仲間に誘拐される。セシルたちは、世に蔓延する“腐った映画”を打倒すべく、“予算ゼロ”“究極のリアリティ”をスローガンに真の映画を撮ることを企ており、その主演女優として選ばれたのがハニーだったのだ。最初は脅されて撮影に参加していたハニーだが、やがて彼らの真剣で向こう見ずな映画作りに強い共感を抱き始める。", "2001-04-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOSTONIANS", "ボストニアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "舞台は1875年のボストン。女性解放運動の指導者オリーヴ・チャンセラー夫人は、ある会合で美しく雄弁な娘ベレナと出会い、彼女を自分の運動に協力させることにした。期待通りの活躍をするベレナだったが、一人の男性が現れて……。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渋谷怪談 THEリアル都市伝説", "渋谷怪談 THEリアル都市伝説", "", "", "http://ecx.images-amazon.com/images/I/219RT4FW0WL._SL210_.jpg", 112, "インターネットで噂される様々な都市伝説を題材にしたオムニバスホラーの第4弾。渋谷の廃墟ビルに忍びこんだ高校生カップルを襲う恐怖を描いた「リアルなお化け屋敷」、実体化した都市伝説に襲われる女性教師の運命を描く「リアル都市伝説」など、背筋の凍るエピソード8編を収録。それぞれのエピソードが複雑に絡みあうことで、恐怖が倍増していく。キャストには、石坂ちなみ、福士誠治、虎南有香ら次世代を担うフレッシュな若手俳優陣がそろう。", "2006-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("茶館／TEA HOUSE", "茶館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "国際的に有名な中国の作家ラオ・シャの舞台劇の映画化。清朝末期から新中国成立直前までの北京を舞台に、“裕泰”という名の茶館の主人とそこに集まる人々の悲哀を描く。茶館の主人、王利発は時代の移り変わりに応じて、なんとか商売をうまくやろうと様々な手をつくすのだが……。監督は「林商店」などで俳優としての評価も高いシエ・ティエン。", "1982-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Let's Spend the Night Together", "ザ・ローリングストーンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "　ザ・ローリングストーンズが81年に行なった全米ツアーの模様を収めたライブ・ドキュメンタリー。20台のカメラを駆使して撮影されたステージは大迫力。「サティスファクション」「タイム・イズ・オン・マイ・サイド」など25曲の演奏が収録されている。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋する女たち", "恋する女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "金沢を舞台に３人の女子高生が恋に悩み、成長していく姿を描いた青春映画。多佳子は高校２年生。大学４年の姉・比呂子と下宿生活をしている。今日は、クラスメイトの緑子の３度目の葬儀の日。緑子は失恋のたびに葬儀をあげるのだった。ただ、今回は、今までとちょっと違うらしい。その日、同じ友人の汀子からも好きな人がいると聞かされショックを受ける多佳子。彼女も気になる相手・勝がいるのだが、どうしても口に出せないでいた。そんな時、偶然出会った勝に下級生の可奈との恋の悩みを打ち明けられる。様々な視点の交錯や女の子たちの脈打つ身体をソフト・タッチの画面に定着させた、大森一樹の温かい眼差しと確かなテクニックが心地よい。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TALL MEN", "たくましき男たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "南北戦争直後、ならず者の兄弟ベンとクリントは、テキサスからモンタナまで牛の大群を輸送する仕事を引き受ける。しかしその途上、偵察に出たクリントはインディアンに殺されてしまう。果たしてベンはインディアンの襲撃から逃れることができるのか……。4000頭の牛を使ったアクション・シーンはまさに圧巻。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏日福星／TWINKLE，TWINKLE，LUCKY STAR", "七福星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "サモ・ハン・キンポーの監督、出演による「五福星」「大福星」に続くシリーズ第３作。香港映画界の大スター、ジャッキー・チェン、ユン・ピョウらの豪華キャストによる大活劇。物語は、香港を中心に暗躍する暗殺者集団と刑事との戦いを描いていく。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flight", "フライト（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER'S ROW", "サイレンサー第2弾／殺人部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "D・ハミルトンのスパイ小説を原作とするシリーズ第２作。女好きのスパイ、マット・ヘルムは、科学発明者を誘拐し世界支配を狙う国際陰謀組織のボスと対決する。A・マーグレットが魅惑的。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Я ХОТЕЛА УВИДЕТЬ АНГЕЛОВ／I　WANTED　TO　SEE　ANGELS", "モスクワ・天使のいない夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「自由はパラダイス」で知られるS・ボドロフがモスクワの若者像を捉えたシャープな青春映画。盗まれた金を取り戻すため、マフィアの命令でモスクワへ出向いた20歳のボブ。仕事のかたわら、町で出会った16歳の少女に本気で恋をしてしまう彼の刹那の青春を、アメリカン・ニュー・シネマ風のシビアな語り口で描く。", "1992-01-01", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PINGU", "PINGU～世界で一番元気なペンギン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 35, "日本でもキャラクター・グッズが売れている、ペンギンを主人公にしたクレイ・アニメーション。南極に住む、オスのペンギン、ピングーの活躍を、１話５分で計５話収録したもの。各話のタイトルは「ピングーの魚でテニス」「ピングーの夜ふかし」「ピングーのお留守番」「ピングーのサーカス」「ピングーの手回しオルガン」。", "1993-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bal", "蜂蜜", "", "", "http://image.eiga.k-img.com/images/movie/56078/315x210.jpg?1253", 103, "養蜂家の父と母とともに静かに暮らしていた6歳のユスフは、父親が消えたハチを探しに森へでかけてから、言葉を失ってしまう。それでもユスフは、大嫌いなミルクを飲んで心配する母親を励まし、帰ってこない父親の後を追って1人で森の奥へと入っていく。現代トルコ映画界を代表するセミフ・カプランオール監督の「卵」（2007）、「ミルク」（08）に続く“ユスフ3部作”の第3作。美しい森を舞台に少年の成長と家族のきずなを描き、第60回ベルリン国際映画祭で金熊賞を受賞した。", "2011-07-02", "32,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPPORTUNITY KNOCKS", "微笑みがえし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "陽気な詐欺師エディはギャングの車を盗んだことから追われる身に。偶然逃げ込んだ高級住宅は長期間、留守であることが判明。家主の主人になりすましたエディは上流階級の仲間入りをはたすが……。「ウェインズ・ワールド」のD・カーヴィが素顔で笑わせるハートフル・コメディ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンコ椿は恋の花", "アンコ椿は恋の花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "都はるみの1964年度のレコード大賞新人賞に輝いた大ヒット曲に乗せて、伊豆大島の若者たちの奮闘する姿を描く青春映画。大島に住む美人の姉妹。姉はバスガイド、妹は歌手を目指している。彼女たちに好意を寄せる青年たちが登場して……。市川昭介節をうなる都はるみのコブシのキレは見事。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乾いた湖", "乾いた湖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "篠田正浩が無名時代の寺山修司と武満徹を起用した監督第２作。安保闘争の時代、学生運動の闘士・下条卓也は、大衆運動を軽蔑し英雄気取りでテロリストを夢み、デモのさなか“デモなんかくだらない、革命のチャンスだ！”と叫び外へ飛び出すが、刑事につかまってしまう。後年「乾いた花」「涙を、獅子のたて髪に」で身を結ぶ篠田正浩のニヒリズムの美学が明確に示され、大島渚が火つけ役となった松竹ヌーヴェル・ヴァーグの一翼をになった。三上真一郎が主人公のテロリストに扮して好演。後年篠田正浩夫人となる岩下志麻が、主人公が思慕を寄せる可憐な女子大生役で篠田作品に初出演。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANON", "情婦マノン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「恐怖の報酬」などの傑作サスペンスで知られるフランスの名匠H・G・クルーゾーが一躍、世界的名声を博した作品。1944年、終戦時の混乱のなか出会った若い男女の宿命的な愛の行方をセンセーショナルに描く一方、解放直後のパリの生活風景やユダヤ人の悲劇など、戦争という“犯罪”を冷徹に見つめる。", "1949-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOLLYWOOD SHUFFLE", "ハリウッド夢工場 オスカーを狙え!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ホットドッグ屋のロバートは、エディ・マーフィのようなスターになることが夢である。ある日、オーディションを受けたロバートは、女性プロデューサーからエディの物真似を強要されて面白くない。ままならない現実から逃避して、彼は様々な映画で自分が主役を演じているのを夢想しはじめる。「ランボー」「アマデウス」「ダーティハリー」「ゾンビ」と、彼の夢は尽きることを知らず……。R・タウンゼントが持ち前のパロディー精神を軽快なリズムに乗せて披露する傑作コメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love at Large", "トム・ベレンジャーの探偵より愛をこめて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "　ニヒルな探偵ハリーは美貌の女性からある男の素行調査を依頼される。早速、仕事に取りかかるが、なんとハリーは全く別人を尾行してしまう。しかしその別人にも重婚という秘密があった。一方、ハリーはもうひとりの女探偵の存在に気づく……。誤解と偶然によって発覚する事件、そして愛の行方をユーモアを織り交ぜながらフィルム・ノワール調で描く。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Elegy", "エレジー", "", "", "http://image.eiga.k-img.com/images/movie/53808/315x210.jpg?2927", 112, "「死ぬまでにしたい10のこと」のイザベル・コイシェ監督が、アメリカ文学の巨匠フィリップ・ロスの短編「ダイング・アニマル」を原作に描く大人のラブストーリー。主演は「バニラ・スカイ」「ボルベール／帰郷」のペネロペ・クルス、「ガンジー」「シンドラーのリスト」のベン・キングズレー。共演にデニス・ホッパー、ピーター・サースガードら。大学で教授と学生という立場で出逢ったデビッドとコンスエラは、30歳の年齢差がありながらも自由な恋愛を楽しんでいたが……。", "2009-01-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀座の若大将", "銀座の若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "京南大学の田沼雄一は、腕を見込まれ拳闘部へスカウトされる。レストランに住み込み、昼は練習、夜はウェイターのきつい生活を送っている。雄一の心の支えになったのは、向かいの洋品店に住む澄子の存在だった。しかし澄子は雄一に彼女がいると誤解していて……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バトルフィーバーJ ロボット大空中戦", "バトルフィーバーJ ロボット大空中戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "秘密結社エゴスに立ちむかうバトルフィーバー隊の活躍を描いた特撮テレビシリーズ「バトルフィーバーJ」の劇場版。テレビシリーズの第5話「ロボット大空中戦」を再編集したもので、「東映まんがまつり」の1編として上映された。国防省の要人・坂口の子どもたちが、エゴスによって誘拐された。坂口は子どもたちを解放する条件として、現在建造中の巨大ロボット、バトルフィーバーロボについての情報を流すよう迫られ……。", "1979-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITY　OF　GHOSTS", "シティ・オブ・ゴースト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "俳優M・ディロンの初監督作品。自分を裏切ったボスを追って、ニューヨークからカンボジアにやって来たジミー。そこで危険なマネーゲームへと巻き込まれていく彼の運命を描き出す。ディロン自ら主演を兼任し、「ロスト・ハイウェイ」のB・ギフォードとともに脚本も執筆。クセ者俳優を脇役に配しつつ、巧妙なドンデン返しを用意した犯罪サスペンスだ。保険金詐欺で得た大金を持ち逃げしたボスのマーヴィンを追って、カンボジアに到着したジミー。ジミーは、仲間の助けを借りてマーヴィンと会うことができたが、かつてのボスは、今では同地の有力者にまで上り詰めていた。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あの手この手", "あの手この手", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "連続ラジオドラマを市川崑監督が映画化した軽妙なコメディ。万年助教授の鳥羽は、才女の近子夫人にまったく頭があがらない日々を送っていた。そんな折、夫人の姪であるじゃじゃ馬娘のアコが家出して鳥羽の家に転がり込んできた。アコは自分の居住権を夫人に認めさせるとともに、鳥羽の夫としての復権を画策していくのだが……。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("股旅 三人やくざ", "股旅 三人やくざ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "オムニバス構成の股旅映画。少年との出会いから、実力をはるかに上回る悪代官に挑んだヤクザを描く第１話「春の章」（脚本・野上）、斬った相手の女房とめぐり会ったヤクザの物語を綴る第２話「秋の章」（脚本・笠原）、若いヤクザが、老年のヤクザと実の娘の対面に出会って心を痛める第３話「冬の章」（脚本・中島）の３話からなる。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOBOS〔ホーボーズ〕", "HOBOS〔ホーボーズ〕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "メジャーデビューする前のミュージシャン、TSUNTAを主役に配して描く群像劇。歌うことが生きがいのストリート・ミュージシャン、TSUNTAが出会う様々な人々。うだつの上がらない元ヤクザや彼に尽くす美少女、露店で密輸の音楽テープを売る少女……。それぞれに悩みを抱えた彼らが絡み合って展開するドラマに、東京の一面を浮き彫りにする。転落の一途をたどる元ヤクザを怪演する、あがた森魚が強烈な印象を残す。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIE PRIVE´E", "私生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "女優となったジルは、次第に人気を高めて行き、マスコミにも連日取り上げられる。しかし、彼女はマスコミの取材に疲れ果てて、故郷に帰ってしまう。つかの間、安らぎの日々を送るジルだったが、ここにもマスコミが訪れてくる。耐えきれなくなったジルはとうとう屋根から身を投げてしまう……。ジルが落ちていくところをストップモーションで描くラストが哀しい。前年に主役のB・バルドー自身が自殺未遂を図っていたため、世界的に話題になった作品。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばり・チエミのおしどり千両傘", "ひばり・チエミのおしどり千両傘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "美空ひばりが主演を張った歌謡時代劇の快作。恋や歌ありで笑いも誘う、楽しいコメディ＆ミュージカル。美人で知られる姫に縁談があり、江戸へと旅立つことに。だが、姫は縁談に気乗りがせず、道中ユーウツだった。そんな姫を食欲不振が原因とにらんだお守り役が調べたところ、腰元が姫のために出された料理を食べていたことが判明。その隙に乗じ姫は逃亡してしまう。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COURT−MARTIAL OF BILLY MITCHELL", "軍法会議", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "誰も予想できなかった日本軍の真珠湾奇襲を約20年も前に予言していたことで知られるアメリカ空軍大佐ビリー・ミッチェル。その彼が、確固たる信念のおかげでしばしば軍の上層部と対立、そしてついに軍部批判を新聞に大々的に発表する。軍当局は彼を軍法会議にかけ、窮地に追い込む。法廷での攻防シーンが見どころで、卑劣な手段でミッチェルを苦境に追い込む検事に扮したスタイガーの名演もさることながら、それを受けて立つミッチェル役のクーパーの堂々たる貫禄も見事。裁判ドラマとしても人間ドラマとしても興趣つきない出来栄えになっている一編だ。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOTEBOOK ON CITIES AND CLOTHES", "都市とモードのビデオノート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "パリのポンピドゥー文化センターによる企画をもとに、映画作家ベンダースが世界的ファッション・デザイナー山本耀司を追う。パリ・コレクションの準備に追われる山本が語るモードへの思いに、フィルムとビデオで迫るベンダースの映画への思いが重なる……。東京とパリ、モードと映画の“いま”を共感し合うふたりのアーティストによるシネ・エッセー。", "1992-03-28", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PASSION　OF　MIND", "薔薇の眠り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "現実と夢の二つの世界で生きる女性のとまどいを描いたファンタジー。南フランスの村で静かに暮らす未亡人マリーは、朝目覚めてみるとN.Y.のビジネスウーマン、マーティとなり、もう一つの世界に。翌朝には再びマリーに戻っていた。目覚めるたびに彼女は二つの世界を往来する。それぞれの世界で恋を体験する彼女は、やがてどちらが本当の自分かわからなくなり……。D・ムーアが迷えるヒロインを好演。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dream Concert ドリームコンサート History'07～'09", "Dream Concert ドリームコンサート History'07～'09", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "1995年の開催以来、毎年約7万人の観客動員数を誇る韓国最大級の音楽フェスティバル“Dream Concert”。日本から参加するファンも多い同イベントの2007年～2009年の模様を厳選してスクリーンで上映。「東方神起」「SUPER JUNIOR」「少女時代」「KARA」など、日本でも人気の高い豪華アーティストたちによるパフォーマンスを、臨場感あふれるサウンドで堪能できる。", "2010-11-13", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRE IN THE SKY", "ファイヤー・イン・ザ・スカイ　未知からの生還", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "UFOに誘拐されたというT・ウォルトンのノンフィクション小説をもとにしたSF。1975年11月15日。アリゾナの田舎町で森林伐採の仕事を終えたマイケルとトラヴィスたちは、帰り道、不思議な赤い光と出会う。そして近づいていったトラヴィスを、連れ去ってしまう。警察に通報するマイケルたちだったが、誰にも信じてもらえず５日間が過ぎた。そしてトラヴィスから電話が……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NON　OU　A　VA＾　GLO｀RIA　DE　MANDAR", "ノン、あるいは支配の空しい栄光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "現在、現役最長老のオリヴェイラ監督が発表当時、再評価されたスケールの大きいスペクタクル戦争映画。何世紀にもわたる戦争の悲惨さを象徴的に描き抜いた圧巻の仕上がり。1200人に及ぶエキストラと350頭の馬を使いこなした、物量共に壮大な作品だ。紀元前から現代までの戦争の形式と敗北の歴史が、アフリカのジャングルをパトロールする軍人によって物語られる。", "1990-01-01", "25,2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Phenomenon", "フェノミナン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "　突然、天才に変貌した中年男の姿をハートフルタッチで描く。カリフォルニアの田舎町に住むジョージは、最近越してきたレースのことが気になっている。37歳の誕生日、夜空に不思議な閃光を目撃してから彼の人生が一変する。脇を固めるのは「７月４日に生まれて」のキーラ・セジウィック、オスカー俳優ロバート・デュバル。エリック・クラプトンが歌うテーマ曲「チェンジ・ザ・ワールド」のアコースティックサウンドが秀逸。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春デンデケデケデケ", "青春デンデケデケデケ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "直木賞に選ばれた芦原すなおの小説を、大林宣彦が映像化。1960年代を背景に、ベンチャーズに憧れてロック・バンドを組んだ少年たちの“純情”を繊細なタッチで描き出していく。青春期の甘ずっぱさを表現するのが得意な大林だけに、その切なさは心に残る。マドンナ的存在を置かないというのが、彼にしては珍しいところ。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ETAT DE SIE｀GE", "戒厳令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "政情不安定なウルグアイで一人のアメリカ人が誘拐された。事件の調査を始めたジャーナリストは、彼がアメリカから左翼勢力弾圧のために派遣されていた事実を発見。一方、政府とゲリラの関係は悪化し戒厳令が発令された……。実話をモデルに描かれ、監督の鋭い政治批判がうかがえる。", "1973-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POINT OF NO RETURN", "銃弾に追われた街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "暴力団に雇われている殺し屋イーグルは、裏切り者のマイケルを消すよう指令を受け、タイへと向かう。任務を果たしたイーグルはマイケルの兄弟分に命を狙われる。そんな中でイーグルは美少女ジョイに魅かれ、自分を刑事と偽って恋人同士となるが……。J・ウォンとJ・チュン共演の香港ノワール。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Tigre e la Neve", "人生は、奇跡の詩", "", "", "http://image.eiga.k-img.com/images/movie/33788/315x210.jpg?4768", 114, "　「ライフ・イズ・ビューティフル」のロベルト・ベニーニが、監督・脚本・主演を務めるファンタジー。イラク戦争に巻き込まれ、意識不明になってしまった女性伝記作家（ニコレッタ・ブラスキ）と、彼女を愛し、助けようとバグダッドへ向かう大学教授で詩人（ベニーニ）の物語。ジャン・レノがイラク人の詩人役で出演している他、トム・ウェイツが本人役として出演し、ピアノ演奏を披露している。音楽はニコラ・ピオバーニ。", "2006-12-09", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SALVATORE GIULIANO", "シシリーの黒い霧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "1950年７月５日、シシリー島でサルバトーレ・ジュリアーノという若者の射殺死体が発見される。果たして彼は誰によって、なぜ殺されたのか。ジュリアーノはマフィアのメンバーであり、かつ警察とも関係していたことがわかってくるのだが……。最後まで真相が解き明かされぬことが、人々に鮮烈なショックを与えた、F・ロージの代表作。ベルリン映画祭では、監督賞のほかに黒白撮影賞、音楽賞を受賞している。", "1962-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOT BLOOD", "ホット・ブラッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "スペインの小さな町に、アメリカから二人の青年が流れ着く。この二人サムとリチャードは、町の名士ドン・ルイスの銀行を襲うのだが、警官隊に包囲されてしまう。人質の中には、アリシアという娘がいた。アリシアの婚約者はドン・ルイスの息子フリオ。そしてなんとリチャードは、ドンが捨てた先妻の子だった。フリオはアリシアを助けようと銀行内に潜入し、リチャードと対面する。複雑な経路を経て再会した兄弟の運命を、情熱の国スペインを舞台に描いたドラマ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北海遊侠伝", "北海遊侠伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ニシン漁に命を賭ける男たちの姿を任侠映画タッチで描いた抗争劇。元ヤクザだった立花伊之助は、北海道ニシン漁の網元・間徳兵衛に拾われて以来、堅気として生活していた。森ヶ崎一家はそんな彼らとことごとく張り合い、ついに堪忍袋の緒が切れた伊之助は、一家に殴り込みをかけていく。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一命", "一命", "", "", "http://image.eiga.k-img.com/images/movie/56278/315x210.jpg?4800", 126, "滝口康彦が1958年に発表し、62年には仲代達矢主演の「切腹」として映画化もされた時代小説「異聞浪人記」を三池崇史監督が3Dで再映画化。江戸時代初頭、大名の御家取り潰しが相次ぎ、困窮した浪人たちの間では、裕福な大名屋敷に押しかけて切腹を申し出ると面倒を避けたい屋敷側から金銭を与えられることを利用した「狂言切腹」が流行していた。そんなある日、名門・井伊家の門前に切腹を願い出る1人の侍・津雲半四郎が現れ、井伊家の家老・斉藤勘解由を前に驚くべき真実を語り始める。主演は歌舞伎俳優・市川海老蔵と瑛太。共演に満島ひかり、役所広司ら。", "2011-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GUN IN BETTY LOU’S HANDBAG", "ベティ・ルーは犯罪者〈クリミナル〉？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "小さな田舎町で平凡な毎日を送る主婦が、ある殺人事件をきっかけに変身していく、アクションタッチのコメディ。図書館に勤めるベティ・ルーは、内気な性格と退屈な毎日にあきあきしていた。警察官である夫にも、自分の言いたいことが言えない始末。ある日、散歩中に銃を発見したベティは、うっぷん晴らしに図書館のトイレで発砲してしまう。だが、その銃は殺人事件に使われた銃だったのだ。反抗的な気分になっていたベティは、なんと警察で自分の犯行と嘘をついてしまう。一躍、町の有名人になってしまったベティであったが……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪魔が来りて笛を吹く", "悪魔が来りて笛を吹く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "戦慄のフルートの調べとともに次々と起こる元子爵家の血の惨劇。死体に残る火焔太鼓のアザに絡む恐怖の秘密と、悪魔の笛の正体を追う金田一耕助の活躍を描く。1952年の「女王蜂」で、二代目金田一耕助役者となった岡譲二が出演しているのが面白い。この作品までの４作を松田定次が監督している。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pandorum", "パンドラム", "", "", "http://image.eiga.k-img.com/images/movie/53908/315x210.jpg?4177", 108, "地球滅亡寸前の西暦2174年。人類は地球と同じ環境の惑星タニスへの移住計画を実行し、選ばれた者だけが搭乗した宇宙船エリジウムが宇宙へと飛び立つ。やがて2人の宇宙飛行士が冷凍睡眠から目を覚ますが、彼らはそれまでの記憶を失っており、船内を捜索するうちに正体不明の何かが潜んでいることに気付く。ドイツの新鋭クリスチャン・アルバートがメガホンをとり、「バイオハザード」シリーズのポール・W・S・アンダーソン監督が製作を務める。デニス・クエイドが閉所恐怖症の宇宙飛行士に扮する。", "2010-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BONJOUR TRISTESSE", "悲しみよこんにちは", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "17歳のブルジョワ娘の恋と、父の愛人への嫉妬を描くF・サガンの処女作の映画化。現在が白黒、回想のリビエラの夏がテクニカラーで、グレコの歌を伴うその移行シーンが美しい。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FULL ECLIPSE", "シュリンジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "瀕死の重傷を負ったはずの刑事が数日後、何事もなかったように出勤。不審に思った相棒のマックスは、やがて警察内部の謎めいた特殊部隊の存在を知る。そのメンバーは、実は全員が狼男だった……。注射器（シュリンジ）で血清を注入することで、狼男に変身するという設定が斬新なホラー・アクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Headshot", "ヘッドショット", "", "", "http://image.eiga.k-img.com/images/movie/57358/315x210.jpg?3720", 106, "", "2011-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空海", "空海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 169, "日本で最初に密教を学び、宗教を体系化した偉大な宗教人、弘法大師・空海の人生を描いた超大作。難解と思われる宗教映画を、佐藤＝早坂のコンビが、実物大の遣唐船を作るなど映画的スペクタクルをとり入れてわかりやすい映画に仕上げた。壮大な中国ロケも圧巻。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CASSE", "華麗なる大泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "地中海沿岸のギリシャの町を舞台に、３億フランのエメラルドを盗んだ４人組の泥棒と、O・シャリフ扮する悪徳警官が宝石の争奪戦を繰り広げる。H・ヴェルヌイユ監督の娯楽アクション。", "1971-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEYOND FORGIVENESS", "処刑ドクター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「ルドガー・ハウアー　処刑脱獄」に続くR・ハウアー主演の“処刑”もの第２作。刑事である自分の目の前で、弟を殺されてしまうフランク。フランクはその場で犯人たちを撃ち殺すが、覆面の男だけを取り逃がしてしまう。しかし残された証拠から、男がポーランドから来たことが判明。フランクは休暇を取り単身捜査に乗り込むが、弟を襲った犯人たちの裏には臓器密売組織の陰のボス、ドクター・レムの存在があった。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRAPPED IN PARADISE", "パラダイスの逃亡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ドジな泥棒３兄弟が繰り広げる爆笑ヒューマン・コメディ。ビル、デイブ、アルヴィンの３人は刑務所時代の知り合いからサラという娘に渡す手紙を頼まれ、ペンシルバニアのパラダイスという町を訪れる。彼らは銀行に勤めているサラを訪ねた際、町民が貯金していた27万5000ドルがクリスマスの祭りのため解約されるという話を耳にし、銀行強盗を企てるが……。ビデオ発売時タイトルは「パラダイスの天使たち」。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京戦争戦後秘話", "東京戦争戦後秘話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1969年11月の羽田闘争（東京＊争）の敗北で一つの時代が終わったとする大島渚が、映画製作に没頭する一人の“戦後”派の若者を通じて、来たるべき時代の可能性を模索した作品。若い世代による新しい映画作りを目指して、原・後藤の10代の映画作家を起用。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAP OF THE HUMAN HEART", "心の地図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "カナダの北極圏で育ったイヌイットの少年が、インディアンと白人の混血である少女とモントリオールで出会う。文明社会にほんろうされながらも、アイデンティティーを探り出そうとする彼らの恋模様を、叙情あふれる語り口で描く。「ビジル」で注目を集めたニュージーランド出身のV・ウォードによる大河ロマン。", "1993-01-01", "3,2,10,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MINKY MOMO IN 夢にかける橋", "MINKY MOMO IN 夢にかける橋", "", "", "http://image.eiga.k-img.com/images/movie/56818/315x210.jpg?3348", 40, "「この橋で出会った者は別れてもまた会える」という言い伝えのある橋でモモが出会った少年は、待ち人が現れず夢をあきらめかけていた。モモは言い伝えが本当だと証明するため少年との再会を約束し、橋に通い続ける。季節が移ろう中、モモは橋の上でさまざまな出会いと別れを目撃していく。1993年に製作された「魔法のプリンセス　ミンキーモモ」のOVA作品。82年のTVシリーズ初放送から30周年目前を記念して、過去のOVA作品を映画館で上映する「魔法のプリンセス　ミンキーモモ PARTY」にて劇場初上映（「MINKY MOMO IN 旅だちの駅」との2本立て）。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("第五福竜丸", "第五福竜丸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1954年３月１日午前３時42分、第五福竜丸の乗組員たちは暗闇の中で火柱が天空に立ち昇るのを目撃。数分後大爆音が響き渡り、やがて雪のような死の灰が降り注ぐ。アメリカのビキニ環礁での水爆実験時に立入禁止区域外にいて何も知らされず被爆した人々の逸話。そのパニックへ向かう運命を描いた社会派の人間ドラマ。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワタリ", "ワタリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "戦国時代、伊賀の里では百地三太夫を頭とする百地党と、藤林長門を頭とする藤林組とが勢力を争っていた。その戦乱の時代、一人の少年忍者ワタリが天才的な忍術で活躍、伊賀の里に平和を取り戻す。白土三平の同名マンガを子供向けに脚色。ビデオ、DVDタイトルは「大忍術映画　ワタリ」。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("半落ち", "半落ち", "", "", "http://image.eiga.k-img.com/images/movie/1418/315x210.jpg?4343", 121, "02年に刊行されたベストセラー・ミステリーを「陽はまた昇る」の佐々部清監督が映画化。元捜査一課警部、梶が3日前に妻を殺害したと警察に自首してくる。だが、犯行後2日間の行動については沈黙を守る「半落ち」の状態。しかも梶は半年前に若くしてアルツハイマー症になった妻の看病のため辞職し、警察学校で教師をする人望の厚い人物だった。その犯行を訝しむ刑事、検事、弁護士、新聞記者らは調査を進めていく。", "2004-01-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COUNTRY BEARS", "カントリー・ベアーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ディズニーランドの人気アトラクションから生まれたファンタジー・コメディ。子グマの冒険、クマのロックバンドの活躍が結びついた、ユーモラスでハートウォーミングなドラマが展開する。人間とクマが共存する社会。人間の親に育てられた子グマのベアリーは、自分が養子だったことにショックを受けて家出をし、寂れたミュージックホールにたどり着く。やがてそこが取り壊しの危機に瀕していることを知ったベアリーは、かつての人気バンド“カントリー・ベアーズ”の再結成興行でホールを立て直そうとする。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED，WHITE＆BLUES", "レッド、ホワイト＆ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ブルース生誕100年を記念したシリーズ“THE BLUES Movie Project”の一編となるドキュメンタリー。1960年代、英国のミュージシャンの間で火がついたブルース熱。ローリング・ストーンズ、エリック・クラプトンらの人気者を輩出した、この音楽ムーブメントを検証する。インタビューはもちろん、ジェフ・ベックら大物アーティストのライブ映像も見もの。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リオの若大将", "リオの若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "京南大学４年生でフェンシング部のキャプテン・田沼雄一は、リオの造船所に招待され、観光会社の営業部員・澄子と知り合う。実家の反対を押し切って造船会社就職を決めた彼はフェンシング大会で優勝し、澄子と愛を確かめる。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARD TICKET TO HAWAII", "グラマー・エンジェル 危機一発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "グラマーな美女ばかりを集めたハワイの麻薬捜査班、通称“グラマー・エンジェル”。そんな彼女たちが巨大麻薬シンジケート撲滅のために大暴れするというアクションもの。プレイメイト・ミス・バストのD・スペアがグラマラスなボディを披露。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOKER・厄病神", "JOKER・厄病神", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "“厄病神”として恐れられる村越が、７年の刑を終えて池袋に戻ってきた。村越の帰還で敵対組織との衝突を恐れる金町恒産の常務は、若い組員ヒロシを彼につけることにする。ところが過度の運動をすると血管が破裂する病気を持つヒロシもまた、母親から“厄病神”と罵られて育った男だった……。激しい抗争の中で復讐に身を投じていく男たちの運命を、文芸座などの池袋ロケで描くハードボイルド・アクション。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はつ恋", "はつ恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "長編デビュー作「月とキャベツ」で高く評価された俊英、篠原哲雄による切なく心に染みる秀作ドラマ。17歳の聡夏が、母がある人に宛てた古いラブレターを見つける。24年前に叶えられなかった約束を実現させようと、彼女は母が胸に閉じ込めた思い出の人を探す旅に出る。母の初恋の思い出をたどるうちに、自らも成長していくヒロインを、田中麗奈が自然体の魅力で好演。久石譲による音楽も情感豊かにドラマを包み込む。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("100 RIFLES", "100挺のライフル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "アリゾナで銀行強盗を働いたインディアンのジョーを逮捕するため、メキシコはノガレスの町にやって来たアメリカ人警官ライデッカー。しかし町はインディアンの反乱を鎮圧するためにやって来たベルデューゴ軍総督によって大混乱に陥っており、彼はやっと見つけたジョーともどもベルデューゴに捕らえられてしまう。R・マクラウドの小説『カリフォルニア人』を素材にした西部劇。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACCION MUTANTE", "ハイル・ミュタンテ！ 電撃XX作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "美しいものがすべてを支配する西暦2012年の地球。容貌が醜いゆえに人々から虐待を受けるミュータントたちが、人類に対し敢然と反旗を翻した……。大富豪の娘の結婚式に侵入して汚濁をぶちまけ、娘を誘拐。しかもその娘を宇宙船に乗せて宇宙に飛び出しては、女のいない惑星に不時着するなど、破天荒なストーリーが展開する。どうにも止まらない悪ノリぶりもかえって新鮮だ。", "1993-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トニー谷の忍術選手権試合", "トニー谷の忍術選手権試合", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 44, "「トニー谷の忍術武者修業」の続編。インチキ忍術使い・福太郎の手で悪家老・大仁良兵衛の衝撃から救われた小夜姫は、無事に波乱万城へ帰ることができた。しかし、彼女の婿となり、藩を牛耳ろうとする良兵衛は八百長の剣術試合を計画。一方、小夜姫も自分の愛する千代之進を勝たせようと福太郎に協力を求めるのだった……。エンタツの妻に扮した、クサリ鎌を使う猛女の浪花千栄子が怪演。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BENNY ＆ JOON", "妹の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "神経を病み、周囲とうまくコミュニケートできない妹と、そんな彼女から目が離せない兄。彼らの前にバスター・キートンに憧れる無口な青年が現れ、兄妹の間に波乱をもたらす。オフビート感覚のユーモアを盛り込んだ、ハートウォーミングな人間ドラマ。J・デップが見せる妙演が光る。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Die Herbstzeitlosen", "マルタのやさしい刺繍", "", "", "http://image.eiga.k-img.com/images/movie/53728/315x210.jpg?2811", 86, "夫に先立たれ、元気もなく毎日をただ何となく過ごしている80歳のマルタ。そんなマルタを心配する友人たちは、 “自分の店を持つ”という彼女の長年の夢を応援するが、マルタの開きたい店とは、なんと“ランジェリー・ショップ”だった。伝統的で保守的な小さな村の人々はマルタの計画に驚き、中には軽蔑する人も出てくるが……。本国スイスで大ヒットを記録し、2007年度アカデミー賞外国語映画賞のスイス代表作品にもなったヒューマンドラマ。", "2008-10-18", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STIGMATA", "スティグマータ・聖痕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "十字架に磔にされたキリストと同じ５つの傷痕が人間の体に現れる聖痕現象を題材にしたオカルト・ホラー。ピッツバーグに住む平凡な女性フランキーはある夜、手首に釘を打ち付けたような原因不明の傷を負う。そしてこの忌まわしい出来事は連日彼女を苦しめるようになる。この奇怪な現象の報告を受けたバチカンは、神父であり、科学者でもある調査員アンドリューを派遣する。彼はフランキーが無神論者であることから、この件は聖痕現象ではないと判断するが、彼女の身には更なる異変が起き始めた……。黒白を際立たせたショッキングな映像と、G・バーンの厚みのある演技が恐怖感をより盛り上げている。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GETTING EVEN WITH DAD", "ゲッティング・イーブン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "M・カルキンの、やんちゃな魅力を捉えたホーム・コメディ。コソ泥である父親と久しぶりに再会し、まっとうな道を歩かせようとする11歳の少年の、あの手この手の奮闘を描く。H・ドイッチが笑わせながらもホロリとさせる物語を巧みに演出。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REBEL", "レベル 反逆者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "第二次大戦下のシドニー。アメリカ太平洋艦隊が集結する歌とダンスと狂騒の街。１組の男女が出会い、恋に落ちる。男は海兵隊の脱走兵、女は夫を前線に送り出したクラブ歌手……。舞台劇のヒット作をMTV感覚で映画化した。M・ディロンがはまり役の愛と反逆の物語。", "1985-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Horde", "ザ・ホード　死霊の大群", "", "", "http://image.eiga.k-img.com/images/movie/55488/315x210.jpg?2056", 97, "フランスの新鋭ヤニック・ダアンとベンジャミン・ロシェによるアクション・ホラー。パリ北部を舞台に、ギャングに同僚を殺された警官たちが、復しゅうのためギャングの潜伏する古いビルに突入する。しかし、そこにゾンビの大群が押し寄せ、ビル内は大混乱に陥ってしまう。警官たちはビルから脱出するため、やむをえずギャングと手を組むことになるが……。", "2010-07-17", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TILAI¨（THE LAW）", "掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "「ヤーバ」で第３回東京国際映画祭ヤングシネマ部門“さくらゴールド”賞を受賞した、I・ウエドラオゴ監督の長編第３作。サバンナの谷間にある小さな村。放浪の旅を終えて一人の男が故郷に帰って来た。男の名はノグマ。彼は村に残してきた恋人のサガと結婚するため長い道のりを越えて帰郷したのだが、サガはノグマの父と結婚してしまっていた。しかし、若い二人の愛情は募るばかり。やがてノグマとサガは肉体的にも結ばれるのだが、村の掟はノグマに死を宣告し、弟のクグリがそれをやり遂げねばならないことになる……。淡々と綴られるストーリーが悲劇性をいやがうえにも高め、イブラヒムの乾いた音楽が全編を彩る。「ヤーバ」同様、美しい映像と神話を思わせるような物語の構成が、人間の本質に迫るプリミティブな力強さへと導いていく。アフリカの小国、ブルキナファソという映画界にあっての辺境性を超えた、映画の普遍性がこの作品にはある。", "1990-01-01", "63,26,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUN60", "RUN60", "", "", "http://image.eiga.k-img.com/images/movie/56448/315x210.jpg?2079", 0, "60分以内に“ある荷物”を届けられるか、死ぬかをかける闇のギャンブルゲーム「RUN」のターゲットに選ばれてしまった青年・高杉裕は、数々の罠をかいくぐっていく中でナゾの女性・水木レナと出会う。「仮面ライダーW」の桐山漣が主演。1～5話がインターネットで先行配信され、劇場公開される本作でストーリーが完結する。", "2011-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AE　FOND　KISS．．．", "やさしくキスをして", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "カトリックの女性とパキスタン移民の青年。社会派ケン・ローチが、さまざまな問題に直面しながら愛を貫こうとする恋人たちの姿を描く人間ドラマ。家族の在り方や結婚観など、宗教の違いを真摯なまなざしで浮き彫りにする。計り知れない葛藤の中で人間的に成長する彼らの姿が清々しい。カトリックの高校教師ロシーンは、パキスタン移民のカシムと恋に落ちる。だが、彼には両親が決めた婚約者がいて……。", "2005-05-07", "3,4,7,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("郷音／COUNTRY　COUPLE", "郷音", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "近代化の波が押し寄せるある農村で、渡し守としてまじめに生きる男と、従順なその妻。ある日妻はがんに冒されていることが判明し……。妻役はチャン・ウェイシン。監督は本作が独立デビュー作となるフー・ピンソンで、本作によって金鶏奨最優秀作品賞を受賞した。", "1983-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWITCHBLADE　SISTERS", "スウィッチブレイド・シスターズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "クエンティン・タランティーノの主宰するレーベル“ローリング・サンダー”の第１作目として全米でリバイバルされたカルト・ムービー。飛び出しナイフを持ち歩き乱闘騒ぎを繰り返す、不良女子高生たちの姿を活写。クライマックスには装甲車が登場して大銃撃戦が展開!? 1970年代ファッションとB級感覚が刺激的なバイオレンス・アクション。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("119", "119", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "18年間も火事が起こったことのない、とある海沿いの田舎町。そこへ蟹の研究にやって来た都会の美女をめぐる騒動を縦糸に、ただただ出動を待ち続ける６人の消防士とその家族たちのほのぼのした人間模様を描く。「無能の人」に続く竹中直人の監督第２作。スタッフは前作とほぼ同じだが、竹中の演劇仲間たちが大挙出演して怪演を見せている。あえて“何も起こらない”ことを仕組むあたりが、好悪の分かれるところ。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SANGUE VIVO", "血の記憶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "中年男ピーノはマフィアの仕事を手伝って家族を養い、自分が率いるバンドのレコード・デビューを夢みていた。一方、彼の弟である元タンバリン奏者ドナートはヘロインに溺れ、破滅的な行動に走ってしまう……。イタリア南部、プーリア州の町サレントを舞台にした人間ドラマ。“ピッツィカ”と呼ばれる激しいリズムの民族音楽が挿入され、登場人物の喜びや悲しみを代弁する役目を果たす。主役の兄弟を演じたP・ズィンバ、L・プローボは、音楽を手掛けたグルッポ・ゾエのメンバーだ。", "2000-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE UNGUARDED MOMENT", "狙われた女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "男子学生からラブレターをもらい、会って説得しようと文面通りの場所へ出向いた美しい音楽教師のロイスは、暗闇の中で暴行を受ける。学生の仕業だと信じた彼女は警察の介入を断り、自らの手で事件を解決しようとするが……。女優R・ラッセルのオリジナル・ストーリーを映画化した異色作。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUNE FROIDE", "つめたく冷えた月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "L・ベッソン製作の下で、異端の作家C・ブコウスキーの短編『人魚との交尾』『充電のあいまに』をミックスして映像化した風変わりな人間ドラマ。ミュージシャンに憧れる女好きの青年と若い女性の死体に恋心を抱き続ける中年男。酔いどれた彼らの破天荒な日常を、幻想的な映像で綴る。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARUNACHALAM", "アルナーチャラム・　踊るスーパースター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 166, "「ムトゥ・踊るマハラジャ」でおなじみのインドのスーパースター、ラジニカーントが製作、主演した娯楽作。村の実力者の家に育ち、人々の信望を得てきたものの、捨て子だったことを知らされショックを受けたアルナーチャラム。傷心を抱えて都会に出た彼が、遺産相続の大騒動に巻きこまれる。ダンサーで埋めつくされた歌と踊りのスペクタクルは「ムトゥ…」以上に迫力がある。", "1997-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌２", "釣りバカ日誌２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "同名人気マンガの映画化第２作。鈴木建設の社長・鈴木一之助は、役員たちの態度に腹を立て姿をくらましてしまう。スーさんとは社員、社長の仲を越え、釣りで結ばれたハマちゃんは、スーさんを捜して釣りの名所・伊良湖岬へ。そこで彼は、弥生という美女に出会う……。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の妻たち・リベンジ", "極道の妻たち・リベンジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "高島礼子が極道の姐に扮して、義理と任侠を欠く者どもを成敗する新シリーズの第３作。沙知子の夫が若頭を務める、東大阪の内藤組が襲撃された。しかし、その犯人は何と、かつて沙知子の元から去っていった初恋の相手だったのだ。思いもかけない劇的な再会に、彼女の心は揺れ動くのだが……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TENTACOLI", "テンタクルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "晴天のサロナビーチで、赤ん坊や作業中の船員などが何者かに襲われた。海底トンネル作業の影響で海に何らかの変化が起こり、巨大なタコが発生したのだ……。「ジョーズ」の二番煎じだが、キャストは豪華。劇場では“トレンブル・サウンド”なる方式で上映されたパニック映画。", "1977-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARIS PALACE HOTEL", "幸福への招待", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「ヘッドライト」など、どちらかといえばペシミスティックな作品が多いH・ヴェルヌイユ監督が撮った恋愛喜劇。ホテルのマニキュア係として働くフランソワーズは、クリスマス・イヴに金持ちのハンサムな青年と出会い、ついホテルに泊まっている大金持ちの娘だと嘘をついてしまう。ところが青年も実は車の修理工で……。最後にはめでたく結ばれハッピーエンド、というエスプリの利いた一編。", "1956-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SURROGATE", "子宮の蠢き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "アメリカでは、一つの社会問題となっている“代理妊娠”をテーマにしたサスペンス。美術大学生のエミリーは、奨学金で生計を立てていたが、格安の貸し部屋があるということで、クイン夫妻の屋敷に住むことになる。身寄りのないエミリーに親切にしてくれる夫人のジョアンは、ある日、エミリーに思いがけない依頼をする。子供のできない自分の代わりに、人工受精をして子供を産んで欲しいというのだ。さんざん悩んだあげく、“代理妊娠”を承諾したエミリー。しかし、彼女が妊娠した時から、彼女の周りで不可解な出来事が起こり始めた。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バカ政ホラ政トッパ政", "バカ政ホラ政トッパ政", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "銀座を舞台に暴れまわる３人の無鉄砲野郎を描いたアクション映画。無鉄砲３人組と言えば深作欣二監督の傑作「人斬り与太 狂犬三兄弟」(1972)を思い出すが、中島貞夫監督のこの作品では３人はひたすら脳天気に暴れまくる。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WARHEAD", "ジャッジメント・デイ　合衆国が震憾した日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "武装テロ集団が、核誘導装置の開発者エバンス博士を誘拐し、カリブ海の秘密ミサイル基地を乗っ取った。そして、12時間以内に要求が受け入れられなければ、核ミサイルをワシントンに向けて発射すると合衆国政府を脅迫。大統領からの緊急指令を受けた国防総省ペンタゴンは、ミサイル発射を阻止するため、タネン少佐率いる特殊部隊を投入する。しかし彼らの動きを事前に察知したテロ組織の奇襲を受け、部隊は壊滅。ただ一人生き残ったタネンは、部隊を再編成し、エバンスの娘とともにミサイル基地への潜入を試みる。B級アクション・スター、F・ザガリーノが大活躍。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グッドラックLOVE", "グッドラックLOVE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ショー・ビジネスの世界で生きることを夢見て本場ニューヨークへやって来た青年が、蒸発した父との再会や愛する人の死を乗り越えて人間的に成長していくさまを描く。田原俊彦の初主演作。“たのきん”ものの３作目で、トシちゃんが一途な青年を好演。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Cramps: Live at Napa State Metal Hospital", "ザ・クランプス　精神病院ライブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "1976年の結成以来、30年以上にわたって現役で活動を続け、世界中に熱狂的なファンを持つアメリカのガレージパンクバンド「ザ・クランプス」。1978年6月、彼らがカリフォルニアのナパ州立精神病院で、患者たちを前に行なった伝説的ライブの模様を記録した衝撃のドキュメンタリー。最初は呆然としていた患者たちがロックンロールによって次第に解放され、自由奔放に踊り狂う姿が映しだされていく。", "2008-01-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤西蠣太", "赤西蠣太", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "志賀直哉の短編『赤西蠣太の恋』を名脚本家・伊丹万作が監督し、映画化した日本映画初期の傑作。伊丹万作は、伊丹十三の父としても知られているが、独特のユーモアとウィットの持ち主である。江戸の伊達屋敷の長屋に無口で無愛想で醜男の田舎武士が着任する。名を赤西蠣太といい、実はお家騒動をたくらむ陰謀派の秘密をさぐりに国元から派遣された密偵だった。調べを終えた蠣太は国元へ密書を届けに帰りたいが、黙って逃げたら怪しまれるので、美女の小波に付け文を送る。フラれて邸中の笑い者になれば、逃げ出しても不思議に思う者もいない。だが蠣太の思惑は外れ、小波は蠣太に好意を寄せていると告白する……。片岡千恵蔵が醜男の蠣太と、白ぬりの歌舞伎もどきの二枚目・原田甲斐の二役を演じた。音楽もショパンのピアノ曲を使ったり、演出に歌舞伎の様式美を取り入れたりと、数々の冒険がいずれも見事に決まっており、伊丹万作の才能の大きさが存分に味わえる。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スーパー・ジャイアンツ／ 人工衛星と人類の破滅", "スーパー・ジャイアンツ／ 人工衛星と人類の破滅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 39, "秘密国家“黒い衛星”は人工衛星に原爆ロケットを運ぶ大型宇宙艇を製造すべく、山中博士と娘のかおるをさらい、宇宙艇で人工衛星に。スーパー・ジャイアンツは、大火星をくぐりぬけ宇宙艇を追跡する……。スペース・オペラ的な要素が多分に盛り込まれている。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INVADERS FROM MARS", "スペースインベーダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "火星から謎の宇宙人が襲来した。それを目撃した少年は大人たちに告げるが、誰も信じない。少年と彼を助ける校医以外は宇宙人にコントロールされているのだ。事態を察知したNASAとインベーダーの攻防戦が見もの。宇宙人のユニークな姿かたちも楽しめる。SFXをション・ダイクストラが担当している。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("漢方道", "王様の漢方", "", "", "http://image.eiga.k-img.com/images/movie/1098/315x210.jpg?3458", 104, "小さな貿易会社の社長、市川は漢方治療ツアーをビジネスにしようと決意。万里の長城にあるリ・レンの家に4人の患者＝インポを治したいヤクザ、末期ガンの老人、ダイエットしたいモデル、性同一性障害の青年を連れて行く。彼らはリとアメリカ人の弟子の治療を受けるが、その願望は果たして叶うのか？　監督・原作・脚本・美術は、60年、北京生まれ、文学や美術で活躍してきたニュウ・ポが映画初挑戦。共同脚本・製作は江戸木純。", "2002-10-12", "8,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まだまだあぶない刑事", "まだまだあぶない刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "TV放映開始から20年近くを経ても、ファンの根強い支持を受ける刑事ドラマの劇場版シリーズ第６作。タカ＆ユージのはみ出し刑事コンビが、今回は横浜を震撼させる核爆弾騒動に対峙する。舘ひろしと柴田恭兵のノリノリのかけあいはますます快調。スケールの大きなアクションも見どころだ。韓国から横浜に持ち込まれた高性能の小型核爆弾。これを追うタカとユージは、自分たちがかつて逮捕した脱獄犯との攻防を繰り広げる。", "2005-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ROSSITER CASE", "ロシターの事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "交通事故で不自由な体となったリズ・ロシターは、医者に治る見込みはないことを宣告される。さらには夫のピーターが、妹オナーと情事を重ね、リズは苦悩する。ある時、リズはオナーのコテージを訪ね、言い争いとなってしまう。ヒステリーを起こしたオナーに、思わず銃を取り出したリズは、誤ってオナーを殺してしまう。人間の深層心理に鋭くメスを入れた異色のサスペンス。", "1951-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("七つまでは神のうち", "七つまでは神のうち", "", "", "http://image.eiga.k-img.com/images/movie/56478/315x210.jpg?4053", 82, "10年前のある事件をきっかけにトラウマを抱え、心を閉ざしたまま教会に通い続ける女子高生の繭が、ある日、深い森の奥で行方をくらます。一方、夫と娘と平穏な生活を送っていた真奈は、7歳の娘が森に遊びに行ったまま帰らなくなり、不安で次第に精神が崩壊していく……。連続する失踪事件を軸に、人間の奥底にうごめく悲しみを描き出すサスペンススリラー。女性ファッション誌「non-no」の専属モデル、日南響子が映画初出演にして初主演を飾る。監督は「怪談新耳袋　怪奇」「呪怨　白い老女」の三宅隆太。", "2011-08-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLPO DEI SETTE VOMINI D’ORO II", "続・黄金の七人 レインボー作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "奇想天外な方法で大銀行の金庫から金塊を盗み出す７人組のハチャメチャ大騒動を描いた前作「黄金の七人」の続編。今回は、彼らが南米の独裁国家に潜入し、独裁者を誘拐するが……。監督、出演者ともに前作と同じ顔ぶれ。次々に飛び出す珍兵器が楽しい。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おニャン子・ザ・ムービー 危機イッパツ！", "おニャン子・ザ・ムービー 危機イッパツ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ダンス・レッスンやテレビのリハーサル場面など、おニャン子たちの楽屋裏を軽くドキュメントしながら、彼女たちの熱烈なファンとのかかわりをサンペンス仕立てでコミカルに描いたアイドル映画。爆発的なブームを巻き起こしたおニャン子の映画初出演作。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忘不了／LOST IN TIME", "忘れえぬ想い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "セシリア・チャンが香港アカデミー賞の主演女優賞に輝いたラブ・ストーリー。婚約者を亡くした女性シウワイが、彼の連れ子を育てるため、ミニバスの運転手として働き始める。さまざまな困難に苦しむ彼女は、手を差し伸べてくれた同僚に安らぎを感じ始めるのだが……。過去にとらわれて前に進めない男女の愛を繊細に描き出し、じわりと感動を盛り上げる。", "2006-02-04", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TONI", "トニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "J・ルノワールが、パリを離れマルセイユの劇作家マルセル・パニョルのもとで撮った作品。1920年代初頭、新聞紙上をにぎわせた犯罪事件をもとにしたもので、むしろ男女の愛憎に焦点を当て描こうとした。南フランスのおおらかな光（撮影はほとんど屋外でのロケ撮影であった)、気ままに生きる南仏人たちの情感を主軸にした物語に、ルノワールらしさを感じることもできるはず。マルセイユにほど近い地中海沿岸の町にある石切り場へと出稼ぎにやって来たトニ。彼に恋する娘ジョゼファーとマリー、ジョゼファーに横恋慕し、ことあるごとにトニを邪険にする現場監督のアルベールを中心とした愛憎劇が展開。演出はリアリズムを基調としたものには違いないが、「ゲームの規則」などにも一脈通じる男女の痴情沙汰が物語の重きをなす。イタリアン・ネオ・レアリスモにも多大な影響を与えた作品ともいわれており、演出助手としてルキノ・ヴィスコンティがついていた。", "1934-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ☆ドラえもんズ　怪盗ドラパン謎の挑戦状！", "ザ☆ドラえもんズ　怪盗ドラパン謎の挑戦状！", "", "", "http://ecx.images-amazon.com/images/I/21S10EQ41YL._SL210_.jpg", 31, "ドラえもんと６人の仲間“ザ・ドラえもんズ”が活躍するシリーズの第２作。フランスの怪盗ドラパンは、ある研究所にドラえもんズを集めて彼らから友情パワーを奪い、ブロンズ像に変えてしまう。何とか難を逃れたドラメッドIII世とドラリーリョは、そこに捕らわれている少女ミミミから、ドラパンが世界征服をたくらむアチモフ博士に利用されていることを知り……。DVDは「ドラえもん　ぼく桃太郎のなんなのさ」を併録。", "1997-03-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КРУГ ВТОРОЙ", "セカンド・サークル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "猛吹雪の中、久しぶりに故郷の家に帰ってきた青年を待っていたのは、ベッドに横たわった父親の死体だった。機械的に現場検証をする当局の男たち、死因をガンと宣告する検死の女医。段取りと葬儀代の交渉をする葬儀屋。何の意味も尊厳もない不条理な“死の儀式”に、青年はほんろうされる。その夜、父親の身体を丹念に洗い、添い寝した青年は悪夢を見る。目覚めた青年は、死体のまぶたをそっと押し開く。青年の現実と幻想の世界を往来しながら、ドラマは“死”を通じて人間であることの意義を問う。", "1975-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　BUG’S　LIFE", "バグズ・ライフ", "", "", "http://ecx.images-amazon.com/images/I/51DD4P850PL._SL210_.jpg", 94, "「トイ・ストーリー」のスタッフが３年の歳月を費やし、3D・CGアニメでは世界初のワイドスクリーンに挑戦。昆虫たちのミクロな世界をスケール豊かに描く冒険ファンタジーだ。秋になるとアリたちの国は、乱暴なバッタたちの食糧集めに大忙し。そんな中、発明好きのドジなフリックが大切な貢ぎ物をなくしてしまう。責任を感じ一人旅に出たフリックが、やがて“用心棒”たちを連れて帰って来るが、彼らがただのサーカス団だとわかり……。ユニークなキャラクターの昆虫たちが生き生きと動き回り、夢あふれる楽しい世界が展開。大粒の雨が降り注ぐ嵐のシーンや奥行きのある風景など、リアルな質感を感じさせる迫力ある映像が広がる。エンディングに用意された彼らのNG集も必見。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY　LITTLE　GARDEN", "マイ・リトル・ガーデン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「エマ」「MIFUNE」のS・クラウ・ヤコブセンが、U・オルレブの実体験をもとにした名作『壁のむこうの街』を映画化した感動作。父と伯父とともにナチスに連行された11歳のユダヤ人少年アレックスが、すきを見て脱走する。廃墟の穴蔵を隠れ家にした彼は、ねずみのスノーと愛読書の『ロビンソン・クルーソー』を心の支えに数々の受難に立ち向かう。縄ばしごなど必要な物を器用に作り、ナチスから身を隠してたった一人で戦争を生き抜こうとする少年の奮闘を、抑制の効いたタッチで描き出す。壁一つ隔てた向こう側の自由の世界で出会った優しい人々との交流などを交え、愛に満ちた感動作に仕上げた。", "1997-01-01", "18,7,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASEY’S SHADOW", "すばらしき仲間たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "アメリカ南部に住む一家を、ヒューマンなタッチで描くホーム・ドラマ。妻に去られ、息子３人と暮らす馬の調教師は全米レースを目指し血統書つきの牝馬を育てるが……。肩の力を抜いた素朴な演出が好感の持てる作品。南部の牧歌的な競馬風景も見もの。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Because I Said So", "恋とスフレと娘とわたし", "", "", "http://image.eiga.k-img.com/images/movie/34158/315x210.jpg?1505", 102, "「恋愛適齢期」のダイアン・キートン主演によるロマンティック・コメディ。ロサンゼルスでスウィーツショップを経営するパティシエのダフネは、若くして夫と死別し、女手ひとつで3人の娘を育て上げてきた。まともな彼氏に恵まれない末娘ミリーを心配した彼女は、ミリーに内緒で花婿募集の広告を出して応募者を面接することに。ところが、自らが花婿候補の父と恋に落ちてしまい……。共演はミュージシャンとしても活躍するマンディ・ムーア。", "2007-09-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Place Beyond the Pines", "ザ・プレイス・ビヨンド・ザ・パインズ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARLENE　DIETRICH　HER　OWN　SONG", "真実のマレーネ・ディートリッヒ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "マレーネ・ディートリッヒの膨大な秘蔵フィルムや彼女に関する数々の貴重な証言をもとに、20世紀を代表する大女優の知られざる逸話を再構築し、その魅力に迫るドキュメンタリー。ビリー・ワイルダー監督や、作曲家バート・バカラックらの証言は、引退まで“夢の女”を演じきった大女優の素顔を浮き彫りにする。幼い子を抱える既婚の身でありながら、ショービズの世界に身を投じ大スターとなった彼女だが、ナチスの広告塔になることを拒否してしまう。そのため彼女は波乱の人生を歩むことになり……。監督はディートリヒの孫、J・D・ライヴァ。", "2001-01-01", "2,7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Magicians", "マジシャンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「スパイダー・フォレスト」のソン・イルゴン監督が、95分全編をワンカットで撮りあげた青春ドラマ。大みそかの夜、森の中のカフェ・マジシャンズで、ジェソンとミョンスという2人の男が酒を酌み交わしていた。かつて2人が組んでいたバンド「マジシャンズ」は、3年前にメンバーの女性ジャウンが自殺したために解散しており、この日はジャウンの命日だった。2人は当時の話で盛りあがりながら、もう1人のメンバーであるハヨンの到着を待っており……。", "2007-01-20", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("G.I. BLUES", "G.I.ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "兵役を終え、芸能界に帰って来たE・プレスリーの、軍隊生活をヒントにして作られた娯楽映画。監督はこの作品以降、プレスリーとのコンビ作を多く手掛けたN・タウログ。西ドイツに駐屯するアメリカ軍。人気者のタルサは、仲間たちと300ドルを賭けて、女を口説き落とせるかどうか挑戦することになる。ところが彼は、賭けの対象であった女を真剣に愛するようになってしまい……。『G.I.ブルース』や『ブルー・スエード・シューズ』など、随所に挿入されたプレスリーの歌唱シーン、そしてスピーディーな場面展開など、プログラム・ピクチャーとして、完成された仕上がりを見せている。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Good Year", "プロヴァンスの贈りもの", "", "", "http://image.eiga.k-img.com/images/movie/34108/315x210.jpg?3323", 118, "アカデミー賞5部門に輝いた「グラディエーター」のリドリー・スコット監督＆ラッセル・クロウが再びタッグを組んだヒューマンドラマ。監督の友人でもあるベストセラー作家ピーター・メイルによる小説を基に、ロンドンで多忙な日々を送る敏腕ビジネスマンが、幼少期を過ごした南フランスで人生を見つめ直す姿を綴る。主人公の少年時代を「チャーリーとチョコレート工場」の名子役フレディ・ハイモア、彼の伯父を名優アルバート・フィニーが演じる。", "2007-08-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAZARIN", "ナサリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「黄金時代」や「砂漠のシモン」など、ブニュエルと宗教の問題には密接な関係があるが、この「ナサリン」も宗教というテーマが大きくかかわっている。メキシコのスラム街。“窓”から出入りすることができるようなボロアパートに住む神父ナサリンは、殺人を犯して逃げてきた娼婦のアンダラをかくまう。だがこれが人々の噂となり、教会からも神父の資格を剥奪された彼は、巡礼の旅に出た。その途上、不治の病にかかった少年を治し、ナサリンは聖人として貧しい人々から崇められるようになる。そしてアンダラと再会し、彼女とともに暮らしていたベアトリスも引き連れて彼は旅を続けるのだが、アンダラが警察に捕まり、彼らの旅も終わりを迎えるのだった……。「哀しみのトリスターナ」で知られるベニト・ペレス・ガルドスの小説をもとに、サドの『司祭と臨終の男の対話』の中のエピソードを加え完成されたストーリーは、様々な解釈が可能であり、公開当時は物議を醸した。", "1958-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUFFALO SOLDIERS", "戦争のはじめかた", "", "", "http://ecx.images-amazon.com/images/I/31J0AE7VM0L._SL210_.jpg", 98, "あまりにブラックな内容のため、何度も上映延期となった問題作。ベルリンの壁が崩壊する間近の西ドイツを舞台に、悪事に手を染める駐留アメリカ兵の姿を描く。物資の横流しや、麻薬の密売などの悪行をこともなげに行う米軍兵が次々と登場。その姿はおよそ規律を重んじる軍人の行動とは思えないほど。また、J・フェニックス、E・ハリス、S・グレンら実力派俳優が競演。彼らの怪演が強烈なインパクトを残す。DVD「バッファローソルジャーズ」", "2004-12-11", "3,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お伊勢詣り", "お伊勢詣り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "吉本興行（戦後は吉本興業）の演芸が、急激に大衆に受け入れられたことに伴い、松竹の子会社である新興キネマは演芸部を設立。吉本の芸人の引き抜きを画策した。当時人気絶頂のワカナ・一郎、ラッキーセブンなどの漫才コンビが引き抜かれ、本編に出演することになった。同じ頃、東宝の前身P・C・Lでは、吉本の花形、エンタツ・アチャコの映画がヒットを飛ばしており、この作品はワカナ・一郎で、それに対抗しようとしたもの。こちらも一応のヒットを収め、第２作「金比羅道中」（1940）などの新作が生まれた。物語は、とある旅篭に住み込んだワカナ扮する底抜けに明るい仲居の奮戦記。のちに「旅籠屋騒動」と改題されている。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tyrannosaur", "ティラノサウルス（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "", "2012-10-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いつかギラギラする日", "いつかギラギラする日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "“仁義なき戦い”シリーズをはじめとする一連の実録もので1970年代の日本映画界の流れを大きく変えた深作欣二監督が久々に手掛けたアクション大作。北海道を舞台に、ギャングたちの血で血を洗う抗争が展開する。若い愛人と北海道で暮らす元ギャングの柴は、若い角町と知り合い、現金輸送車から２億円を強奪する計画を持ちかけられる。柴は昔の仲間の神崎、井村の二人を東京から呼び寄せる。計画は成功したかに見えたが、中には5000万円しかなく、仲間割れに。角町が井村を射殺、柴も重体となる。かろうじて逃げ延びた神崎が角町を追い、そこへ角町に金を貸しているヤクザも加わって、三つ巴の戦いが始まる……。息を飲む銃撃戦やカーチェイス、バイオレンスに拍車をかけるBGMのハード・ロックなど注目すべきシーンが多い。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社長学ABC", "社長学ABC", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "大日食品社長の網野参太郎は親会社の大社長に就任することになり、彼の後任に専務の丹波を選んだ。ところが株主総会で前・大社長の留任が決定、参太郎は大日食品会長となった。会長と社長、ボスが二人になって社員たちは落ちつかない……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("終わってる", "終わってる", "", "", "http://ecx.images-amazon.com/images/I/51O6L03LpnL._SL210_.jpg", 100, "青春と性をテーマにした「青春Hシリーズ」第8作。妻子持ちの晋助は、妻のまきと共通の友人である町子が気になりはじめ、2人はお互いのパートナーと別れて付き合うことを決意する。晋助はまきに別れを告げ、家を出るが、町子は元の恋人と寄りを戻してしまう。妻のまきも親友の馬場と暮らし始め、晋助は行き場を失ってしまう。ドキュメンタリー「たまの映画」でデビューした今泉力哉監督の初長編作。", "2011-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道懺悔録", "極道懺悔録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "直木賞作家・浅田次郎が原作を手掛けた同名劇画の映画化。1980年代、バブル真っ盛りの東京で、法律の盲点をつき中小企業から金をだまし取る青年、浅川次郎。相棒や組織の親分を相手に丁々発止の駆け引きを繰り広げながら、まんまと金ヅルを仕留めようとする彼の危険な生き様を描く。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("炎上", "炎上", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "実話をベースにした三島由紀夫の『金閣寺』の映画化。この世で最も美しいものは驟閣寺（金閣寺）の建築だと亡父から教えこまれた青年は、深い憧憬から驟閣寺の徒弟となるが、戦後、訪れる雑多な観光客で寺は俗気にまみれる。彼は、師と仰ぐ住職が戒律を犯して女色に溺れることを知り、不信と絶望感に追いつめられる。自殺しそこなった青年は、ついに寺に放火。そして囚われの身となり、護送される途中列車から身を投じて死ぬ。市川監督はこの観念的な題材を、青年の内面にひそむ心理をリアルに表現することに重点をおいて演出。宮川一夫の美しい白黒撮影も効果を上げていて、原作とは違う魅力をひき出すのに成功している。市川雷蔵初の現代劇出演で、時代劇スターである雷蔵の本作出演には会社側の反対も多かったが、雷蔵の強い要望で実現。以降演技者としても着実に認められるようになった。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆走トラッカー軍団", "爆走トラッカー軍団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "同名のビデオ・オリジナル・シリーズを映画化したもので、アートトラック竜神丸で日本中を駆けめぐるトラッカー・竜一の生き様を描く。任侠を売りものにする誠心会が、実はトラッカーたちをクスリの恐怖に陥れていた事実が判明した。そのことを知った竜一は、怒りが爆発。自分を慕う気のいいチンピラ・次郎とともに、誠心会を相手に闘いを繰り広げるのだった……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CROSSING", "ザ・クロッシング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1960年代のとある田舎町の一日を追いながら、３人の若者の青春を綴るノスタルジックな内容のオーストラリア版「アメリカン・グラフィティ」。サムは、かつての恋人メグを迎えに町に戻ってきたが、メグは前日にジョニーと結婚の約束をしたばかりだった。折しも町は戦勝記念パレードの真っ最中。祭りのにぎわいとは裏腹に、３人の心は沈んでいた。そして夜、ダンス・パーティーが始まって……。", "1990-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cookie's Fortune", "クッキー・フォーチュン", "", "", "http://image.eiga.k-img.com/images/movie/1788/315x210.jpg?2031", 118, "復活祭目前の週末。夫に先立たれ一人寂しく暮らす老女クッキーは、家族同様に長年一緒に過ごしたウィリスに最期の手紙を残し、お気に入りの宝石で身を飾って拳銃自殺を図る。横たわるクッキーを最初に発見したのは、彼女と反りが合わなかった姪のカミールだった。カミールはウィリス宛ての手紙を咄嗟に飲み込んで「これは強盗殺人よ！」と周囲に嘘の証言をし、ほどなくウィリスが逮捕される。", "2000-02-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　CIRCLE", "チャドルと生きる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "少女のささやかな冒険を綴った「白い風船」で注目されたイランのJ・パナヒ監督が、新たなテーマに取り組んだ意欲作。産婦人科で女の子が出産されるシーンから始まり、刑務所を仮釈放された３人の女性、妊娠してしまった未婚の女性、生活苦で娘を捨てようとしている母親らのエピソードが、緻密に練られた循環的なストーリー構造の中に展開していく。女性が様々な形で抑圧されているイラン社会の現実を毅然と見据え、国内では上映禁止処分を受けたものの、海外では高い評価を得たヒューマン・ドラマだ。", "2000-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アフロ田中", "アフロ田中", "", "", "http://image.eiga.k-img.com/images/movie/56378/315x210.jpg?4013", 114, "「ビッグコミックスピリッツ」連載ののりつけ雅春による人気コミックシリーズを実写映画化。彼女いない歴24年（＝年齢）のアフロヘアの青年・田中広が彼女を作ろうと奮闘する姿を、松田翔太の主演で描くラブコメディ。強烈な天然パーマでアフロヘアの田中広は、学生時代からのダメ仲間の1人、井上が結婚するという知らせを受ける。井上を含めた仲間たちと「誰かが結婚するときはそれぞれ彼女を連れていこう」と約束していたことを思い出した広は、早急に彼女を作らなければと慌てる。そんな折、アパートの隣室に美少女が引越してきて……。", "2012-02-18", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TURBO CRASH", "ターボクラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "各地のレースを総ナメにしているポールとニックのコンビは、八百長を断ったために顔役のロンスキーに狙われることになる。カリフォルニア最大のレース、デモリッショ・ダービーに勝つため、ロンスキー一味はポールとニックに様々な襲撃を加えるが……。カー・アクション満載のスピード狂映画。", "1981-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DURAS／CET　AMOUR−LA｀", "デュラス／愛の最終章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "20世紀を代表するフランスの女流作家であり、映画監督としても活躍したマルグリット・デュラス。彼女が晩年の16年間をともに過ごした38歳年下の愛人ヤン・アンドレアとの日々を、数々のTV映画を手掛けたきたJ・ダヤン監督が映画化した人間ドラマ。時にエゴイスティックに振る舞い、時に少女のような表情を見せるデュラスを、生前の彼女と親交があったJ・モローが演じている。ビデオ、DVDタイトルは「愛人／ラマン 最終章」。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャーマン＋雨", "ジャーマン＋雨", "", "", "http://image.eiga.k-img.com/images/movie/53148/315x210.jpg?1833", 71, "ゴリラ顔でわがままで天涯孤独な少女のパワフルな生きざまを描いた異色作。監督は若干29歳の新鋭・横浜聡子。たった1人でボロ家に住み、植木職人の見習いとして働いている16歳のよし子は、“ゴリラーマン”と呼ばれても棟梁に殴られても気にすることなく生きている。縦笛でオリジナル曲を作り歌手を目指している彼女は、近所の子供たちを相手に縦笛教室を始め……。山下淳弘監督作「道」などの個性派女優・野嵜好美が主人公よし子を怪演。", "2007-12-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pink Panther 2", "ピンクパンサー2", "", "", "http://image.eiga.k-img.com/images/movie/54008/315x210.jpg?3542", 91, "ブレイク・エドワーズ監督＆ピーター・セラーズ主演の同名人気シリーズを、スティーブ・マーティン主演でリメイクした「ピンクパンサー」（06）の続編。世界各国で貴重な文化遺産が盗まれる事件が相次ぎ、現場には共通して「トルネード参上」と記されたカードが残されていた。そこでトルネード逮捕のために各国の精鋭でドリームチームを結成することになり、フランス代表としてクルーゾー警部が選抜されるのだが……。", "2009-04-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAJOR　LEAGUE ： BACK　TO　THE　MINORS", "メジャーリーグ３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "メジャーリーグの痛快さをコミカルかつ感動的にドラマ化したシリーズの第３作。今回の主役となるのは前２作のインディアンズに代わって、3Aのサウスカロライナ・バズ。3Aのピッチャーとして引退したガスはバズの監督を任される。だが、チームは最下位を独走するオンボロ軍団。ガスは元インディアンズのペドロ、タカを現役復帰させ、バズの面々に野球の楽しさを教え込む。これによってチームは活気づき、連戦連勝。ところが、オーナーのアイデアで、エキシビション・ゲームでバズとメジャー球団ツインズが戦うことになってしまう！", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ИВАН　ГРОЗНЫЙ", "イワン雷帝（上・下）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 209, "“モンタージュ理論”とともに映画史を語る上で欠かすことのできないエイゼンシュテインの遺作。16世紀半ば、ロシア史上初めて“ツァー”を称し、祖国を統一して専制君主となったイワン４世の半生を描く２部作で、歌舞伎を思わせる様式美を持った入魂の大作。１部では皇帝となったイワンが封建貴族と対立し孤立しながらも民衆の支持を得るまでを、２部では雷帝と恐れられるまでに力をつけたイワンが専制君主として君臨するまでを描いている。民俗学や歴史学の成果を総動員して細部まで驚くほど論理的に設計されている。２部の最後は、エイゼンシュテインの映画で唯一のカラー・シーンになっており、美学の一端がよくわかる。", "1944-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FEMME AUX BOTTES ROUGES", "赤いブーツの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "美貌の女性作家フランソワーズと彼女を愛する二人の男。この３人に、芸術家である彼らを破滅させようとするペルーなる男が寓話的に絡む。ドヌーヴの不思議な魅力が印象的なメルヘン的映画。ルイス・ブニュエルの息子の第１作。", "1974-01-01", "2,9,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("“CHE！”", "ゲバラ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "キューバ革命の悲劇の英雄ゲバラの姿を描いた伝記もの。カストロとともにキューバ革命を成功させたゲバラ。だが彼はさらに南米13ヵ国の革命を企図する。1966年ボリビアに潜入したゲバラはゲリラ部隊を組織し、政府軍と戦うが、飢えに苦しみ、次第に部下を失っていく……。ビデオ発売時タイトルは「革命戦士 ゲバラ！」。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUFFLOSEN", "はじまりはオペラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "オペラの仕事に携わる二人の男女の愛を、オペラの名曲の数々に乗せて描く大人のラブ・ストーリー。プロンプターとしてオペラの舞台を支えているシヴは、結婚を間近に控えて幸せいっぱいの日々を送っている。そんなある日、オーケストラに新しく入ったチューバ奏者のマルカスと出会う。", "1999-01-01", "36", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("川島芳子／YOSHIKO KAWASHIMA", "女スパイ・川島芳子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "清朝第10代粛親王・善耆の第14王女として生まれながら、その後、日本人の満州浪人・川島浪雄の養女となり、日中戦争中に日本軍のスパイ活動を行って“男装の麗人”“東洋のマタハリ”と呼ばれた川島芳子の生涯。ゆうばり国際ファンタスティック映画祭でグランプリを獲得した「双旗鎮刀客」の監督ハー・ピンの処女作。", "1989-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OF HUMAN BONDAGE", "痴人の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "上流階級の若い医学生が奔放なウエイトレスに溺れていくさまを描いた、S・モームの自伝的小説『人間の絆』の映画化。悪女役では右に出る者のないB・デイヴィスの原点ともいえる作品で、“堕ちる女”を大熱演し、“呪縛の愛”を見事に表現する。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Anderson Tapes", "ショーン・コネリー／盗聴作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　凄腕の金庫破りアンダーソンは10年の刑期を終え、出所したのち早速、次の獲物として、警備の厳重な高級マンションにある全財産を狙う。彼は鑑定や電気系統といった各分野のプロを集め下準備に入る。しかし予期せぬ誤算が。各人がFBIなどの捜査機関から目を付けられ、盗聴監視下に置かれていたのだ。はたして強奪計画の行方は？　ショーン・コネリーが「丘」に続いてルメット監督と組んだクライム・サスペンス。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("行動隊長 血盟", "行動隊長 血盟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "“Vシネの帝王”竹内力が主演する任侠アクション。御木本、本多、石津が結成した鬼道会が、花山一家の傘下になることが決定した。花山一家の行動隊長となった御木本は、数々の抗争を片づけ、組織の中で一目置かれる存在となっていった。そんな折、一家の若頭と敵対する黒田組の理事が兄弟盃を交わす計画が進んでいることが発覚。黒田組に吸収されることは明白だった。それを知った御木本は、理事の抹殺に乗りだしていく。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I BOUGHT A VAMPIRE MOTORCYCLE", "暴走スプラッターマシーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "イギリスの若手スタッフが撮り上げた血しぶき満載のスプラッタ・ホラー。人間の生き血を燃料として暴走する“呪われたバイク”が、町を恐怖に落とし入れる。全編にあふれるブラック・ジョークやグロテスクな殺りくシーンに、原案を出したS・ライミが“すさまじい……”と洩らしたとか。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGHWAY RACER", "フェラーリの鷹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ローマ警察のパルマは覆面パトカーを運転する大の車好き。スピードを出し過ぎて車を壊したり、いつも上司ににらまれている。そのころ、白昼堂々と銀行を襲うレーサーくずれの銀行強盗が現れ世間を騒がす。絶好の機会到来と喜ぶパルマ。カーチェイスが小気味良くキマったアクション映画。", "1977-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOP HAT", "トップ・ハット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1930年代のアメリカ・ミュージカル映画の最高傑作の一つ。エキゾチックで華麗なセットを背景に、アステアとロジャースのゴールデン・コンビがスクリーン狭しと踊りまくる。劇中披露される名曲は『トップ・ハット』『ピッコリーノ』他多数。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Love Bug", "ラブ・バッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "感情を持つ車ハービーが活躍する姿を描いたファンタジック・コメディ。スランプに陥ったレーサーのジム・ダグラスは、自動車販売店でひどい扱いを受けていたフォルクス・ワーゲンを助ける。彼が店を出ると、信じられないことにその車が後をついて来て……。公開当時アメリカで大ヒットを記録し、続編も製作されている。また2005年には「ハービー／機械じかけのキューピッド」として、リンジー・ローハン主演でリメイクされている。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("林世榮／THE MAGNIFICENT BUTCHER", "燃えよデブゴン７", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ゴールデン・ハーベスト10周年を記念して製作されたカンフー・アクション。香港映画界の人気スター、サモ・ハン・キンポーとユン・ピョウが共演しており、血気盛んな青年が極悪非道な五龍組を相手に繰り広げる壮絶な死闘を描いている。", "1979-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Camino", "カミーノ", "", "", "http://image.eiga.k-img.com/images/movie/54788/315x210.jpg?4335", 143, "「ミラクル・ペティント」「モルタデロとフィレモン」で知られるスペインの奇才ハビエル・フェッセル監督が、実話をもとに撮りあげた長編第3作。神の業を意味するカトリックの組織オプス・デイの家庭に生まれ育ち、突然の病によって14歳の若さでこの世を去った少女カミーノの生涯を圧倒的な映像美で綴る。スペインのアカデミー賞といわれるゴヤ賞で最優秀作品賞・監督賞など6部門を総なめにした話題作。", "2009-09-18", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Men in Black III", "メン・イン・ブラック3", "", "", "http://image.eiga.k-img.com/images/movie/55278/315x210.jpg?4006", 0, "ウィル・スミス、トミー・リー・ジョーンズ主演で、地球に生息する異星人を監視する極秘組織「MIB」のエージョントの活躍を描く人気SFアクションコメディのシリーズ第3作。MIBのエージェントJは、コンビを組むKが単独で事件を追っていることを不審に思い問いただすが、Kは全く取り合わない。さらに上司からは「Kは40年前以上に亡くなっている」と聞かされ、過去が書き換えられていることに気がつく。謎を解明するためJは40年前にタイムスリップするが……。前2作から引き続きスティーブン・スピルバーグ製作総指揮のもと、バリー・ソネンフェルド監督がメガホンをとった。", "2012-05-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUST A DAMNED SOLDIER", "バイオレンス・ハンター 黄金の謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ベトナム戦争とアフガニスタン侵攻という二つの戦争を背景に、男たちの運命的な出会いと戦いをハードに描くバイオレンス・アクション。ベトナム時代の戦友タイドルマンとエルンスト。今ではタイドルマンは武器密輸組織のボスに、エルンストは傭兵になっていた。敵同士として再会した二人だが……。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Absence of Malice", "スクープ・悪意の不在", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "ある労働組合のリーダーが失踪した。FBIは事件解決を焦るあまり、1人の男を犯人に仕立て上げようとする。その情報操作にかかり、スクープ記事を書いた女性記者は真相究明に乗り出すが、弁護のために書いた記事が男の最愛の女性を自殺に追い込み……。真実のための報道さえも人を傷つけてしまう恐さを描いた社会派ドラマ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LITTLE GIRL WHO LIVES DOWN THE LANE", "白い家の少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "美しいブロンドの少女リンは、人里離れた丘の上に立つ白い家に、たった一人で住んでいた。不審に思った家主とその息子は、彼女に近づくが、いつの間にか家主は行方不明に……。やがて少女の恐るべき素顔が明らかになる。J・フォスターの冷ややかなまなざしと、美しいショパンの調べが織り成すサスペンス。", "1976-01-01", "10,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Freakonomics", "ヤバい経済学", "", "", "http://image.eiga.k-img.com/images/movie/56328/315x210.jpg?2476", 93, "経済学者スティーブン・レビットとジャーナリストのスティーブン・タブナーの共著で、全米400万部のベストセラーとなった同名経済書をもとに映画化。「大相撲の八百長はデータで証明できた」「子どもは名前で人生が決まってしまう」など、インセンティブの考え方から人間の行動パターンまで、さまざまな常識をユニークな視点で解説。「エンロン 巨大企業はいかにして崩壊したのか？」「闇へ」のアレックス・ギブニーや「スーパーサイズ・ミー」のモーガン・スパーロックら、気鋭のドキュメンタリー作家が共同でメガホンをとった。", "2011-05-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸福の黄色いハンカチ", "幸福の黄色いハンカチ", "", "", "http://ecx.images-amazon.com/images/I/51-Y5CapxNL._SL210_.jpg", 108, "ヒット・ポップス『幸せの黄色いリボン』から発想を得て作られた感動作。北海道を舞台に、刑務所帰りの中年男と若いカップルがそれぞれの愛を獲得するまでのロード・ムービー。新車を買って、北海道に旅行に出た欽也は早速、朱実という女の子をナンパ。二人は旅を続けるが、途中で刑務所から出たばかりの中年男・勇作に会う。勇作は妻に“もし俺を待っていてくれるなら、家の前に黄色いハンカチを下げておいてくれ”という手紙を書いていた……。図式的で予定調和のドラマだが、ラストに向かってのサスペンス仕立ての盛り上げ方がうまい。風景と人物のカットバックという古典的手法が実に効果的に使われている。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("100万回生きたねこ", "100万回生きたねこ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "名作絵本「100万回生きたねこ」の著者・佐野洋子の生前のインタビュー映像などでつづるドキュメンタリー。", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪に願うこと", "雪に願うこと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "鳴海章のベストセラー小説『輓馬』を、名匠・根岸吉太郎監督が映画化。第18回東京国際映画祭でグランプリをはじめ史上初の４冠に輝いた話題作だ。元農耕馬がソリを引きながら障害レースに挑む“ばんえい競馬”と、一人の青年の再起を重ね合わせた感動作。東京で始めたネットビジネスに失敗した学は、北海道の家族のもとに13年ぶりに戻ってきた。地元名物“ばんえい競馬”の騎手だった兄は、学に厩舎の見習いとして馬の世話をさせる。", "2006-05-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESTATE VIOLENTA", "激しい季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "第二次世界大戦も末期に近い夏、高級避暑地リッチョーネで上流社会の若いグループが、祖国の苦境をよそに青春を享楽している。パーティーの主人公ロッサーナが心を寄せていたのは、途中からやって来たファシスト党高官の息子カルロだった。こんな町にも突然、敵機が来襲。海岸を逃げまどううちにカルロは、小さな女の子を連れた未亡人ロベルタと言葉を交わした。激しく彼女に惹かれたカルロは別荘のパーティーに誘い、二人はいつしか抱擁しあう。イタリアン・ネオ・レアリスモ以後の監督、ズルリーニの苦い青春を描いた傑作。パーティーでかかる『テンプテーション』のメロディーが強く心に残る。", "1960-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DINOSAURUS！", "最後の海底巨獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "アメリカの海底で工事中、冷凍状態の二頭の恐竜が発見された。海岸に引き揚げておいたところ、夜中に雷が落ちて二頭が生き返り、島はパニックに陥る……。現在では珍しい人形アニメを盛り込んだ作品。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新吾十番勝負・第一部 第二部総集篇", "新吾十番勝負・第一部 第二部総集篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "川口松太郎の原作を大川橋蔵主演で映画化した“新吾十番勝負”第一・ニ部。真崎庄三郎と恋仲であった町娘・お長は松平頼方に召し上げられ、お鯉の方として新吾を生む。庄三郎はその子を盗んで剣道指南・多門と育てるが、新吾は黒田藩から狙われる。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KICKBOXER ５", "キックボクサー５", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "若くして現役を退いた天才格闘家のマットがコーチを務めた親友のジョニーは、チャンピオンシップに挑み、激しい格闘の末、勝利を手にする。と同時に、地下格闘技界の帝王・ネガールに組織に入るよう強要されるが、それを拒んだジョニーはネガールに殺されてしまう。マットは親友の仇を討つためにネガールに立ち向かっていく。本格的な格闘アクション・シリーズの第５作。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT VONQUERED THE WORLD", "金星人地球を征服", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "低予算、早撮りで多くの快作を残したR・コーマン本領発揮のB級SF映画。絶滅の危機に瀕した金星人たちは地球への移住を計画。科学者アンダーソン博士と無線で交信した金星人は、人類救済を偽ってアンダーソンを操り、地球を征服する準備を始める。アンダーソンの親友、ネルソン博士は異星人の陰謀に気付き、侵略を防ぐべく行動を起こすが……。SFファンの間で通称“金星ガニ”と呼ばれて人気の高かった金星人のデザインが1950年代B級SFの味わいを伝える。また、後にマカロニ・ウエスタンでスターになるL・V・クリーフが、金星人に操られる科学者を鬼気迫る表情で演じるのも印象的。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L' ENFANT", "ある子供", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ベルギーの兄弟監督ジャン・ピエール＆リュック・ダルデンヌが、「ロゼッタ」以来2度目のカンヌ映画祭パルムドールに輝いた青春映画。主人公は定職に就かず、盗みをはたらく20歳の若者ブリュノ。何事にも無責任な彼が、恋人との間にもうけた赤ん坊を闇組織に売ったことから、初めて自分の人生を見つめ直していく。ダルデンヌ兄弟の演出は、これまでにも増してシンプルながら、シャープで的確。彼らの作品では最もストレートな感動を呼び起こすであろう情感溢れるラスト・シーンに注目。チンピラ風情だが気のいい若者ブリュノは、盗品売買でその日暮らしの生活を送っていた。彼は、年下の恋人ソニアが産んだばかりの赤ん坊を闇の組織に売り、彼女を激怒させてしまう……。", "2005-12-10", "37,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君のままで", "君のままで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "人気のポップ・ロック・バンド“DEEN”の音楽をフィーチャーし、メンバー３人も演技に初挑戦した話題作。妻子のある男との別れを機に信州に帰郷した30歳の独身OL・加奈美。かつての友人たちと触れ合う中で、彼女が自分を見つめ直していく姿をノスタルジックに描く。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLUFFY", "Mr.ライオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "動物の能力を進化させ、人間の言葉を理解するライオンの“フラッフィ”を誕生させることに成功した科学研究所のポッター博士。フラッフィはおとなしく無害なライオンだったが、近所の住民が危険だと騒ぎ出したため、彼らは山奥のホテルに身を隠すことになる。ライオンの演技が愛らしいさわやかな上質コメディ。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DearGirl Stories THE MOVIE", "DearGirl Stories THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/55758/315x210.jpg?2781", 0, "“視覚的ラジオ”の異名をとる人気ラジオ番組「神谷浩史・小野大輔の Dear Girl Stories」の劇場版。人気声優の神谷と小野が、茨城県・牛久市で行われた「小野大輔生誕祭in牛久」に参加し、牛久大仏や牛久沼などの観光スポットをめぐり歩く姿を収録する。", "2010-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬と歩けば　チロリとタムラ", "犬と歩けば　チロリとタムラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「おかえり」「忘れられぬ人々」で海外でも高い評価を得た篠崎誠監督が、ココリコの田中直樹とりょうの共演で描くハートウォーミング・ドラマ。何の取り柄もない男が、捨て犬を介護犬に育てようと悪戦苦闘。犬を通して心を通い合わせていく人々の姿を温かい眼差しの中に見つめる。純粋無垢な瞳を輝かせて人々の心を癒すかわいらしい犬の名演にも注目を。29歳の靖幸は、同棲していた恋人の美和が末期ガンの母親と引きこもりの妹の世話をするため帰郷することになり、アパートを追い出される。行く当てもなく途方に暮れていた彼は、一匹の捨て犬と出会い、その犬を介護犬に育て始める。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幻の光", "幻の光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「誰も知らない」の是枝裕和監督による劇場映画デビュー作で、原作は宮本輝の同名小説。祖母の失踪という幼い頃の思い出を引きずるゆみ子は、子どもを授かり、夫の郁夫とともに幸せに暮らしていた。しかしある日、動機がわからないまま郁夫は突然自殺をしてしまう。再び愛する者を引き止められなかったゆみ子は、悔恨の思いを秘めながら奥能登へと嫁いでいく……。ベネチア国際映画祭金のオゼッラ賞受賞作品。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN FROM CORLEONE", "ドン・コルレオーネの娘／禁断", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "父親の埋葬のためにシシリー島を訪れた男が、島で出会ったドンの娘。無邪気だが、男を挑発するその色気に、次第に彼女の魔性に溺れていく。シシリーを舞台に、禁断の愛に溺れていく男と女を、サスペンス・タッチで描いたエロティック・バイオレンスもの。「夕陽の用心棒」のジェンマの新境地開拓編。", "1983-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シベリア超特急２", "シベリア超特急２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "水野晴郎が“MIKE MIZNO”と名前を変えて再び監督・製作・原作・脚色・主演で臨んだサスペンス・ミステリー第２作。第二次世界大戦直前の満州。ヨーロッパ情勢視察を終えた山下将軍はシベリア鉄道に乗車していたが、謎の鉄道爆破の影響でホテルに宿泊することに。同宿するシベリア超特急の一等乗客たちは、いわくありげな人間ばかりだ。その夜、客の一人である戦争成金の社長が刺殺される事件が起きる。しかも部屋は密室、全員にアリバイがあった……。またしても山下将軍が人智を超えた推理を働かせ、事件を解決に導く。前作のダブルどんでん返しほどの強烈さはないものの、“晴郎節”と言うほかない映画文法を超えた手法は健在。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひみつの花園", "ひみつの花園", "", "", "http://ecx.images-amazon.com/images/I/21FD2CGRRSL._SL210_.jpg", 83, "鈴木咲子は“三度のメシより金が好き”というほどのお金好き。それがこうじて銀行員となり、幸せな日々を送るはずだったが、突然の銀行強盗事件をきっかけに彼女の人生は思いもよらない方向へ急展開。咲子を人質に取った犯人の車は富士の樹海で横転爆発。咲子は川に落ち、スーツケースもろとも池に流れつく。奇跡の生還を遂げた咲子は“盗まれた５億円は焼失”というニュースを聞く。その日から、樹海の池に沈むスーツケースを発見するための咲子の挑戦が始まった……。東宝と、ぴあが提携して若手作品を後押しする『YES』の第２弾であり、「裸足のピクニック」に続く矢口史靖の第２作。奇想天外な物語を軽やかに転がしていく矢口演出はここでも快調だ。ハワイ国際映画祭審査員特別賞（主演女優賞）に輝いている。", "1997-02-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Predators", "プレデターズ", "", "", "http://image.eiga.k-img.com/images/movie/55188/315x210.jpg?3656", 107, "アーノルド・シュワルツェネッガー主演の大ヒットSFアクション「プレデター」（87）シリーズを、新たな視点・キャストで描く。ロバート・ロドリゲスが製作と脚本を担当し、新鋭ニムロッド・アーントルがメガホンをとる。主演はエイドリアン・ブロディ。傭兵のロイスはある未知なる惑星で、囚人、日本人の殺し屋、メキシコの暗殺団一味などからなる戦闘集団を、いやいやながら統率することになる。やがて、自分たちが新種プレデターの餌食として惑星に連れてこられたことが判明。生き残りをかけたプレデターとの壮絶な闘いが始まる。", "2010-07-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Deuxième souffle, Le", "マルセイユの決着（おとしまえ）", "", "", "http://image.eiga.k-img.com/images/movie/54078/315x210.jpg?3302", 156, "ヌーベルバークの作家たちに多大な影響を与えたとされる名匠ジャン＝ピエール・メルビルが暗黒街を描いた「ギャング」（66）を、「めぐり逢う朝」のアラン・コルノー監督がリメイク。「あるいは裏切りという名の犬」「ぼくの大切なともだち」のダニエル・オートゥイユ、「マレーナ」「アレックス」のモニカ・ベルッチが主演。1960年代のフランス、脱獄に成功した大物ギャングのギュは亡き相棒の未亡人マヌーシュや仲間達に会うためパリへと向かう。", "2008-12-20", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZULU DAWN", "ズールー戦争 野望の大陸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1879年、英国の植民地・南アフリカ連邦ナタールで、英国側は隣接するズールー族に対し一方的な統治の勧告をする。この自己本位な政策はズールー王を戦いに駆り立てた。ズールー族と土地に精通したタンフォード大佐は、将軍の軍略ミスを指摘するが受け入れられず、結局不本位な態勢で敵に向かい、ズールー族軍の圧倒的勢力によって無残な死を遂げる……。屈強なズールー族の前に、大英帝国の権威が屈服した劇的史実を迫力ある戦闘シーン、豪華キャストで映画化したスペクタクル映画。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Looking For", "Looking For", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "声優、歌手として活躍する國府田マリ子の主演によるロード・ムービー。デビュー間近のバンドでボーカルを担当している奈津子は、病に倒れた母のために消息不明の父親を探し出すことに。異母弟の一馬とともに、彼女は父親の消息をたどる旅に出る。笑いと涙の要素に加えて國府田のライブ・シーンも盛り込まれ、後味のさわやかな作品に仕上がっている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GHOULIES", "グーリーズ 肉体の小悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "美術品倉庫に忍び込んだアレクサンドラは、妖艶な光を放つルビーを盗み出すと、暗黒界から一人の男を呼び出した。それは、悪の権化であるファウスト。ファウストの虜になったアレクサンドラは、彼を復活させるためにルビーを手に入れたのだ。完全に復活させるためには、さらに魂を乗り移らせる肉体が必要になる。彼女はその肉体にロサンゼルス市警のジョナサンを選び、黒魔術で彼をおびき寄せる。ところが、魔術によって謎の怪物グーリーズまでもが現れてしまい……。ホラー・コメディシリーズの第４作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("License to Wed", "ライセンス・トゥ・ウェディング", "", "", "http://ecx.images-amazon.com/images/I/51JBx4jMU1L._SL210_.jpg", 91, "憧れの教会で結婚準備講座を受けることになったカップルが、ロビン・ウィリアムズ扮する変わり者の牧師に翻弄される姿を描いたコメディ映画。婚約したばかりのベンとサディーは、サディーのファミリーチャーチである聖オーガスティン教会で結婚式を挙げることに。ところが予約は2年先まで埋まっており、空いているのは3週間後の1日だけだという。しかも結婚式を挙げるには、フランク牧師による結婚準備講座を受けなければならず……。", "2007-11-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ソラからジェシカ", "ソラからジェシカ", "", "", "http://image.eiga.k-img.com/images/movie/57498/315x210.jpg?4796", 32, "吉本興業が主催する2011年・第3回沖縄国際映画祭の企画「地域発信型プロジェクト」で製作された6本の短編作品を、「東」と「西」の3本ずつに分けて劇場公開する「沖縄国際映画祭　地域発信型映画」の「東」の1本。千葉県・成田市で父親の経営する落花生工場を切り盛りする毅は、人手不足のため外国人労働者を雇うことに。しかし、力仕事のできる男性を迎えるはずが、手違いで看護師志望のペルー人女性ジェシカが来てしまう。仕方がなく、当面の間ジェシカを工場で雇うことになるが、無口な毅はなかなかジェシカと打ち解けることができず……。", "2011-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アキハバラ＠DEEP", "アキハバラ＠DEEP", "", "", "http://image.eiga.k-img.com/images/movie/1248/315x210.jpg?2363", 118, "「池袋ウエストゲートパーク」「4TEEN」などで知られる流行作家、石田衣良の原作を「東京タワー」「大停電の夜に」の源孝志監督が映画化。世界最大の電気街、秋葉原を舞台に、ベンチャー企業を立ち上げた、それぞれに弱みを持つ5人のオタクの若者と、金で全てを牛耳る巨大IT企業との戦いを描く。主演は「あずみ」の成宮寛貴、ファッションモデルの山田優。共演に寺島しのぶ、佐々木蔵之介ら。", "2006-09-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ノストラダムス戦慄の啓示", "ノストラダムス戦慄の啓示", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1999年７月の地球壊滅を予言したと言われる、中世フランスの医学者ノストラダムス。本作は愛なき現代の地球がその予言通りに壊滅していく様と、宗教団体“幸福の科学”の教義に基づく“救済”の過程を神秘的に描いたSFスペクタクル巨編だ。ハリウッドにて“シネオン”技術を使ってデジタル合成されたリアルなCGをはじめ、全体の９割に及ぶ特撮シーンが見もの。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鍵", "鍵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "壮年の大学教授は、若い妻との情交を鍵のついた日記に綴り続けていく。だがある日、その鍵を拾った教授の娘が日記の中味を見てしまい、娘は嫌悪から親への残酷な仕打ちを始める……。谷崎潤一郎の小説をもとに、ある家族の愛憎せめぎ合う性のゲームを神代辰巳が独特の淫靡な映像世界に展開する。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT SUMMER OF WHITE ROSES", "ホワイト・ローズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "第二次大戦末期のユーゴスラビアの田舎街。まったく戦争の影さえ差してこないこの村で、ひそかにパルチザンの妻をかくまっていた河の水泳監視員のアンドレア。ある日、彼は河で溺れていた男を助ける。だがその男は、ドイツ軍の将校で……。東京国際映画祭グランプリを受賞した寓話風の人間ドラマ。", "1989-01-01", "34,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOCTOR FAUSTUS", "ファウスト 悪のたしなみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "C・マーロウが1558年頃に発表した戯曲『ファウスタス博士の悲劇』を、R・バートンが製作・主演を兼ねて映画化、オックスフォード大学演劇科の学生たちが共演している。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZORRO", "アラン・ドロンのゾロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "どこからともなくさっそうと現れ、剣でZの文字を刻んで去っていく黒覆面の騎士ゾロ。無声映画の昔から何回となく映画化されたヒーローにドロンが挑戦。ラスト10数分にわたる決闘シーンは、絶妙なカメラワークとともに迫力満点。", "1974-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロッテ・ライニガーの世界", "ロッテ・ライニガーの世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "'26年に発表され、世界初の長編アニメーション映画となったドイツの影絵アニメーション作家、ロッテ・ライニガーの「アクメッド王子の冒険」が、修復を施されたニュープリント版。伝説的に語り継がれてきた繊細かつ流麗なライニガーの影絵アニメは、80年を経た今見ても心を奪われる。アニメの歴史に興味ある人のみならず、新たな映像体験を求めている人なら必ず見て欲しい傑作だ。2005年、おなじみの童話を影絵スタイルで映像化した「眠れる森の美女」など、貴重な5作品も同時上映された。アクメッド王子は、妹を妻に望む魔法使いの計略で空飛ぶ馬に乗せられ追放されてしまう。やっと降り立った島で、彼は美しい妖精パリバヌーと出会う。(「アクメッド王子の冒険」)", "2005-01-01", "7,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PINK PANTHER＆INSPECTOR CLOUSEAU", "ピンク・パンサー＆クルーゾー警部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "“ピンク・パンサー”シリーズの冒頭でおなじみのアニメ・キャラクターを主人公に、そのまま長編映画にしてしまったコミカルなアニメーション。茶目っ気たっぷりのピンク・パンサーが、クルーゾー警部や月星人、悪徳ハンターらを相手に大活躍を繰り広げる、13話の愉快なエピソードから構成されている。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHERCHEZ L’IDOLE", "アイドルを探せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ギターに隠されたダイヤモンドの行方を追って繰り広げられるドタバタ騒ぎを描いた、人気歌手総出演のフランス版歌謡映画。当時、ヒットパレードをにぎわせたバルタンの『アイドルを探せ』、アズナブールの『思い出の瞳』などのヒット曲が続々登場。", "1963-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shanghai", "シャンハイ", "", "", "http://image.eiga.k-img.com/images/movie/53978/315x210.jpg?2241", 104, "1941年の太平洋戦争前夜の上海を舞台に、ある米諜報部員の死の裏に隠された中国、アメリカ、日本の巨大な陰謀と、決して愛し合ってはならない運命を背負う男女の悲恋を描く。主人公の諜報部員ポールをジョン・キューザック、ポールを狙う冷酷な日本人将校タナカを渡辺謙、物語の鍵となる謎の女スミコを菊地凛子が演じるほか、コン・リー、チョウ・ユンファらアメリカ、アジアのスターが集結。監督は「1408号室」のミカエル・ハフストローム。", "2011-08-20", "1,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アベックパンチ", "アベックパンチ", "", "", "http://image.eiga.k-img.com/images/movie/56208/315x210.jpg?3573", 85, "「月刊コミックビーム」（エンターブレイン刊）で2007年～10年に連載されたタイム涼介の同名漫画を、「オトシモノ」の古澤健監督が実写映画化。男女がペアになり、手をつないだ状態で戦う架空の格闘技「アベック」に打ち込む高校生たちの姿を描く青春アクション。お金も彼女もない負け犬不良高校生のイサキとヒラマサはある日、男女2組が戦う格闘技「アベック」に出合い、わけあり少女のメバル、エツとペアを組んで出場を決意する。若手俳優集団「D-BOYS」の牧田哲也が単独初主演を果たす。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うる星やつら２　ビューティフル・ドリーマー", "うる星やつら２　ビューティフル・ドリーマー", "", "", "http://ecx.images-amazon.com/images/I/519XK5GV6EL._SL210_.jpg", 98, "『うる星やつら』TVシリーズの演出で、原作とは一味違った感覚の表現を獲得しつつあった押井守が監督・脚本を担当し、独特の不条理感覚の表現を押し進めて話題となった注目作。友引高校の学園祭の前夜、あたるたちは泊まり込みでその準備に大忙しだった。だが、何日経っても学園祭当日はやってこない……!? すべては妖怪・夢邪鬼が作り出した夢だったのだ。夢邪鬼はラムの純粋な夢を使って、悪夢に変わることのない永遠の夢の世界を作ろうとしていた。一人、また一人と仲間が姿を消していく中で、やがて真相を知ったあたるは、夢邪鬼が作った夢を食べるというバクを手に入れ、夢の世界から脱出を図る。それを食い止めようとする夢邪鬼。そして、脱走と追跡の果てしないドタバタ劇が続くことになるが……。公開時にこれを原作の発展的な表現とする声と、原作の否定とする声が上がり、賛否両論が交錯した。だがいずれにせよ、日本の劇場用アニメが、高度な映像表現を成し得ることを実証した作品であることは間違いない。", "1984-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BATTLE BEYOND THE STARS", "宇宙の7人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "黒澤明の「七人の侍」というよりは「荒野の七人」の舞台を宇宙に置き換えたSF活劇。宇宙征服をたくらむセイドーの野望を阻むため、様々な星から戦士が結集する。R・ヴォーンが、「荒野の七人」と同じようなニヒルなガンマンを演じるなど、お遊び感覚が楽しい一編。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUBA", "ルーバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "アルゼンチンに生まれ、オランダで映画作家活動を続けるA・アグレスティ監督の第４作。これまでに発表した作品はみな各国の映画祭などで高い評価を受けるなど、今後の活躍が期待されている監督である。物語は第二次大戦下のロッテルダム。秘密警察から必死に逃げ回っている作家のロベルトは、豪華な娼館にやって来る。そこで彼は、ルーバという名の娼婦に出会う……。異端のイメージと鮮烈な映像美学、ラブ・ストーリーと華麗な映像が織物のように絡み合ったアグレスティ・ワールドが炸裂する。主演はアグレスティ作品に欠かせぬE・マルチと、ポーランド生まれの美人女優B・ラソータ。", "1990-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京マダムと大阪夫人", "東京マダムと大阪夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "東京の紡績会社の社宅で西川隆吉と伊東光雄はお隣り同士。西川の妻・房江は大坂の船場育ち、伊東の妻・美枝子は東京の下町育ち、二人は夫が同じ課の同輩ということで、何かにつけて張り合っていた。彼女たちの思いをよそに、房江の弟と恋に落ちる美枝子の妹・康子を演じているのはSKD出身で、本作品が映画初出演の芦川いづみ。当時としてはテンポのいいシティー・コメディである。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名もなく貧しく美しく", "名もなく貧しく美しく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "松山善三が自らのオリジナル脚本を演出した第１回監督作品。戦争末期から終戦後までの困難な時代を生き抜いた、ろうあ者夫婦の姿を感動的に描く。成瀬作品で知られる玉井正夫の撮影が素晴らしく、高峰秀子も迫真の名演技。二人の手話の内容は字幕によって表される。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARIZONA COLT", "南から来た用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "強盗団は手下を補充するため国境の牢獄を襲い、囚人を解放して連れ出す。彼らの脱走を防ぐため、左腕に鉄ゴテで烙印を押すが、うまくそこを逃げ出した男が、娘を強盗団に殺された酒場の主人の頼みを聞き入れ、復讐を買って出る。ジェンマ主演作の定石といえるマカロニ・ウエスタン。DVDは「マカロニウエスタン・コレクション ジュリアーノ・ジェンマ ボックス」に収録。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　FINGER　POINTS", "無冠の帝王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "正義感あふれる若い新聞記者リーは最初こそギャングの首領ブランコの買収に応じなかったが、恋人マーシャルとの結婚を考えたとき、貧しい生活を恨み、彼と手を組むことにする。賄賂を受け取り、ブランコの流す情報を新聞に載せる彼は腕利きの記者となっていくが、マーシャルだけは彼の変化に気付いていた。悪に染まりきったリーは新しい賭博場の情報をネタにブランコをゆすって大金を得て、マーシャルと街を出ようとするが……。人間のもろさを描いた社会派の人間ドラマ。C・ゲーブルがブランコを演じている。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRISM", "PRISM", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "自主製作・自主上映で活躍してきた鬼才・福島拓哉監督によるサイコ・ファンタジー。昼は映写技師として働き、夜になるとドラッグにふけり、性別を問わない肉体関係を持つ日々を送っている青年・真。映画「PRISM」を観た日から、彼の日常は現実と妄想が入り乱れたものになっていく。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("For Horowitz", "私のちいさなピアニスト", "", "", "http://image.eiga.k-img.com/images/movie/34148/315x210.jpg?1493", 108, "ピアノの才能を持つ孤独な少年と落ちこぼれピアノ教師の心の交流を描いたハートフル・ドラマ。ピアニストになる夢をあきらめ、ソウル郊外で小さなピアノ教室を営んでいる女性ジス。近所に暮らす少年キョンミンが絶対音感の持ち主であることを知った彼女は、キョンミンをコンクールで優勝させてピアノ教師としての名声を得ようと思いつく。レッスンを続けるうちに、2人は本当の母子のような絆で結ばれていくが……。", "2007-08-25", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シムソンズ", "シムソンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "2002年のソルトレイク五輪に出場したカーリング女子日本代表チーム「シムソンズ」をモデルに、4人の少女たちの奮闘を描いた青春ストーリー。北海道常呂町の高校に通う伊藤和子は、自分の将来に焦りを抱きながらも無為な日々を過ごしていた。そんな和子が、カーリング日本代表として長野五輪に出場した地元の英雄・加藤正人との出会いをきっかけに、友人たちとカーリングチームを結成する。監督はテレビ版「ウォーターボーイズ」の佐藤祐市。", "2006-02-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FALCON AND THE SNOWMAN", "コードネームはファルコン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "1977年６月、アメリカ連邦地方裁判所は二人の若者に、おのおの40年と終身刑という判決を下した。中流家庭で何不自由なく育った若者が、全米を震撼させたスパイ行為に走ったのだ……。実話に基づくサスペンスで現代社会にゆがめられた若者の苦悩を描く。固く結ばれた友情が陰謀へと発展するさまをリアルに再現する。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("榴連飄飄", "ドリアンドリアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「メイド・イン・ホンコン」の鮮烈な監督デビュー以来、香港インディペント映画界の雄として意欲作を送り出してきたフルーツ・チャン監督による繊細な女性映画。返還後の香港で風俗嬢として出稼ぎに来ていた女性イェン。就労ビザ３週間の期間を過ぎ、中国東北部の故郷へ帰るや、彼女は一躍成功者として親戚や友人にもてはやされる。だがその心中は複雑だった。劇中たびたび登場する、強烈な匂いを放つ南国のフルーツ、ドリアンと北国の美しく白い雪、そして喧騒の香港と素朴で閉鎖的な田舎、それぞれを鮮やかに対比させながら、一人の女性の心の移ろいを丹念に映し出す。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画監督って何だ！", "映画監督って何だ！", "", "", "http://image.eiga.k-img.com/images/movie/33728/315x210.jpg?4716", 88, "日本映画監督協会創立70周年を記念し、150名以上もの監督たちがキャスト・スタッフとして集結した“監督劇”。1970年に改正された現行の著作権法では、映画の著作権者は監督ではなく製作者にあるとされている。時代劇、再現ドラマ、インタビューなど様々な手法を交え、法によって奪われた監督の著作権を取り戻そうと訴える。出演は大島渚、山田洋次ら名だたる巨匠たちの他に、小泉今日子、原田芳雄、佐野史郎ら。", "2006-11-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CETTE SACRE´E GAMINE", "この神聖なお転婆娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "若きB・バルドー主演のミュージカル・コメディ。ニセ札犯罪組織に巻き込まれた父がジャジャ馬娘をナイトクラブ歌手の家にかくまうが、彼女が大騒動を引き起こし……。“B・B（ベベ）”の愛称で親しまれた、バルドーの小悪魔的な魅力が全編に漂う。", "1956-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Postman to Heaven", "天国への郵便配達人", "", "", "http://image.eiga.k-img.com/images/movie/55408/315x210.jpg?1777", 108, "日本人脚本家と韓国のスター俳優がタッグを組む、日韓共同ドラマプロジェクト「テレシネマ7」シリーズの1作。主演は、人気歌手グループ「東方神起」のジェジュンとハン・ヒョジュ。野原の真ん中にたたずむ赤いポストに手紙を投函しにきたハナは、不思議な雰囲気を持つ若い男性ジェジュンに出会う。彼は、自分は天国にいる人たちに手紙を届ける“天国への郵便配達人”だと名乗るが、ハナはまともに相手にしなかった。数日後、再びジェジュンに出会ったハナは、説得されて仕事を手伝うことになる。", "2010-05-29", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT FALLS ON MANHATTAN", "NY〈ニューヨーク〉検事局", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "社会派S・ルメットによる辛口のサスペンス。気鋭の検事補ショーンは全米注視の麻薬王裁判に抜擢された。容疑者はニューヨーク市警の汚職を暴露。それでもショーンは、法廷で見事に勝利を収める。出世街道を歩むものの、その喜びの一方で、刑事の父を持つ彼の心には容疑者の告白がシミのように広がっていく。果たして、ショーンの父は賄賂を受け取っていたのか……。職務の理想と現実の狭間に立つ主人公の苦悩をスリリングに描写。正義を固持することの難しさを見つめた、ルメットならではの視点の鋭さが光る。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャプテン劇場版", "キャプテン劇場版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "主人公・谷口タカシが頑張る野球スポ根アニメの名作。原作は『月刊少年ジャンプ』に連載されて大人気になったマンガ。連続TVアニメ放送よりも前に作られた劇場版のアニメというローテーションも珍しい一作。野球の名門中学から敗北続きの中学へ転校した谷口はみんなの期待を受け、キャプテンになり、父との猛特訓などを経て地区大会に出場。連戦連勝して決勝まで進む。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREE WILLY3: THE RESCUE", "フリー・ウィリー3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "J・J・リクター演じる少年ジェシーとオルカのウィリーの友情、冒険を描くシリーズ第３作。17歳になったジェシーは海洋調査船でのアルバイトを見つけ、クルーとともに北大西洋沿岸の沖合に生息するオルカの個体数が減少している原因の調査に向かう。ウィリーとの再会に喜ぶジェシー。だが、ウィリーとその家族に密漁者の魔の手が迫っていた。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー×スーパー戦隊　スーパーヒーロー大戦", "仮面ライダー×スーパー戦隊　スーパーヒーロー大戦", "", "", "http://image.eiga.k-img.com/images/movie/57568/315x210.jpg?3828", 0, "「仮面ライダー」と「スーパー戦隊」シリーズの歴代ヒーローが合計240人集結する劇場版。長らく地球の平和を守ってきた仮面ライダーとスーパー戦隊だったが、仮面ライダーディケイド＝門矢士がすべてのスーパー戦隊を、ゴーカイレッド＝キャプテン・マーベラスがすべての仮面ライダーを襲い始める。ディケイドはライダーと敵対してきた悪の組織をまとめあげた「大ショッカー」の首領となり、ゴーカイレッドも「大ザンギャック」の帝王として君臨。戦いを止めるため、各ライダーやスーパー戦隊が動き出す。", "2012-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おんなの細道 濡れた海峡", "おんなの細道 濡れた海峡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "弱気な男の“ボク”が行きずりの女たちと関係を繰り返していきながら、性と生の悲しみを切々とうたい上げるロマンポルノ。原作者を彷彿とさせる三上寛が“ボク”を演じ、人妻のストリッパー・山口美也子と東北の雪の中をさまよい歩いていく。武田一成監督の代表作の中の１本。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PSYCHO", "サイコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "サイコ・スリラーの原点といわれるヒッチコックの名作を、G・V・サントがリメイク。モノクロのオリジナルに対して、こちらはカラーだが、ほとんどのシーンはオリジナルのショットをほぼ忠実に再現している。狂気のキャラクター、ノーマン・ベイツに扮したV・ヴォーンの、アンソニー・パーキンスとは異なる脂ぎった存在感が鮮烈。DVDタイトルは「サイコ（1998）」。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Going Vertical: The Shortboard Revolution", "ゴーイング・バーティカル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "", "2012-06-16", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くノ一忍法帖 忍者月影抄", "くノ一忍法帖 忍者月影抄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "時は享保年間。名君として名高い徳川吉宗だが、実は妾を持ち、多くの女たちを泣かしていた好色者。そんな彼の秘密を暴こうとする、御三家・尾張徳川宗春に雇われた伊賀くノ一忍群と、吉宗の女を晒し者にする使命の甲賀忍者卍谷衆が忍法合戦を繰り広げる。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APPLAUSE", "喝采", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "トーキー映画に移動撮影を初めて持ち込んだ記念すべき作品。ブロードウェイを夢見ながらバーレスク劇に出演する女優キティ。修道院に入れた娘のエイプリルが大きくなり、彼女は娘を引き取る。やがて母のあとを継ぐように女優になったエイプリルだったが……。バックステージもののお決まりのストーリー展開ながら、大胆なカメラアングルや音の処理など、当時としては画期的な実験的手法が使われており、R・マムーリアンの演出が冴えわたった一編となった。", "1929-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shadows", "アメリカの影", "", "", "http://image.eiga.k-img.com/images/movie/3798/315x210.jpg?1914", 87, "　ベニーはトランペット奏者になることを夢見ながらも空しい毎日を送っていた。その兄ヒューは歌手だが、最近の仕事には不満だらけ。ふたりの妹レリアはトニーという青年と結ばれるが……。ニューヨークに暮らす若者たちの姿を活き活きと描いたジョン・カサベテスの初監督作品。台詞も演技も自由に任せるという即興演出によって作られたこの映画はアメリカよりもヨーロッパで熱烈に支持された。58年に60分版が完成、その後、59年に追加撮影・編集を経て、87分版となる。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLI SPECIALISTI", "スペシャリスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "マカロニ・ウエスタンの鬼才S・コルブッチが、人気歌手J・アリディを主演に据えた異色作。個性的なフランス人俳優たちが脇を固めている点も異彩を放つ。ブラックストーンの町に現れた拳銃使いの名手ハッドの目的は、大金強奪の濡れ衣を着せられて縛り首になった兄の仇を討つことだった。そんな主人公を中心に、消えた10万ドルをめぐるミステリアスな人間模様が展開していく。", "1969-01-01", "4,2,77,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WAR BETWEEN MEN AND WOMEN", "おかしな関係／絶体絶命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "女と子供と犬への憎しみを、日夜マンガに託していた中年男が、皮肉なことに３人の子持ちで、犬も一匹飼っている離婚女性に恋をした……。男尊女卑のジョークやユーモア、ウーマンリブ運動への反発、ベトナム戦争の悲哀、男女の愛、家庭の絆など、もろもろの要素を詰め込んだ、J・レモン主演によるラブ・コメディ。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dance Flick", "最強絶叫ダンス計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アシンメトリー", "アシンメトリー", "", "", "http://image.eiga.k-img.com/images/movie/53758/315x210.jpg?1295", 82, "フリーライターの北斗は、入院中の恋人の担当医が高校時代の同級生・慎一郎であることを知る。当時は親友同士で何でも語り合える仲だった北斗と慎一郎は、一緒に応募した写真コンテストに北斗だけが入賞したことから気まずくなってしまう。そしてある事件をきっかけに慎一郎が海外に留学してからは、それぞれ別の道を歩んでいたのだが……。若手俳優集団D-BOYSの和田正人と荒木宏文がW主演を務める青春ドラマ。", "2008-08-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("同窓會", "同窓會", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "キャリア40年を超える向井寛監督が、高齢化社会をテーマにした3部作の最終章として作り上げた渾身作。戦後移住した先から60年ぶりに帰国した男の心情を通し、戦争の悲劇や現代の日本が抱える老人問題を浮き彫りにする。加藤剛、池内淳子らベテラン俳優の熟練された演技も印象深い。戦後すぐに日本からペルーへ移住した俊作が、約60年ぶりに帰国することに。故郷の鹿児島に戻った彼は、旧友たちと再会を果たす。", "2005-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Taxidermia", "タクシデルミア　ある剥製師の遺言", "", "", "http://image.eiga.k-img.com/images/movie/53428/315x210.jpg?3136", 91, "若干28歳で手掛けたデビュー作「ハックル」が世界各地の映画祭で高評価を受けたハンガリーの鬼才パールフィ・ジョルジ監督の長編第2作。妄想にふけることを唯一の楽しみとしていた軍人の祖父、国家のために勝利を目指した大食い選手の父、太りすぎて動けなくなった父の世話に追われながら究極の芸術作品を創ろうとする剥製師の息子。父子3代の物語をグロテスクかつユーモラスに描くことで、人間の欲望や狂気を浮かび上がらせた異色作。", "2008-03-29", "30,77,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Son of Flubber", "フラバァ・デラックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「うっかり博士の大発明／フラバァ」の続編で、前作に引き続き実写版ディズニー映画の常連監督ロバート・スティーブンソンがメガホンを取ったファンタジック・コメディ。不思議な反重力物質フラバーを発明した、うっかり者のブレナード博士は、母校メドフィールド大学のピンチを救おうと大奮闘。今度はフラバーを改良した“フラバー・ガン”を作り出すが……。日本では1971年に「新フラバー」というタイトルで再上映されている。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大いなる学生", "大いなる学生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "８ミリ作品「花」で、1989年PFFアワードのグランプリを受賞。その時の審査員だった市川準の協力を得て完成した、小池隆の劇場用映画デビュー作。100年後の、地球パトロール軍や“宇宙空港建設予定地”という看板が、すでに日常の中に埋没している未来。幼稚園しか出ていない文盲の小森は、知人の告別式に行く途中、幼稚園の同級生だった植田と再会する。気は小さいが、野望は大きい植田のペースに小森は巻き込まれて……。SF的設定を小津映画のような日常に取り込み、独特の詩的イメージを醸し出している。東京乾電池の演出家でもあった岩松了が主演。他にも劇団・東京乾電池の面々が多数出演している。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TOAST OF NEW YORK", "富豪一代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ニューヨーク、ウォール街を舞台に、一代で大富豪にのし上がった男の成功と悲劇を描いたサクセス・ストーリー。ウォール街をも脅かす金融界の天才にE・アーノルドが扮し怪演するほか、二枚目スターのC・グラントが相棒役で好演している。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Khane-ye doust kodjast?", "友だちのうちはどこ？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "A・キアロスタミの代表作。一人の子供を主人公に、友だちのノートをもって帰って来てしまった彼が、そのノートを返すために迷子になりながらも友だちの家を探し回る姿を、ドキュメンタリー・タッチで描いていく。イラン北部の小さな村を切り取った映像、ジグザグの坂道や幾重にも折り重なった階段、少年が迷い込んだ空間で目にする家々の扉（冒頭がまさに扉の映像である）など、どれもが豊かな表情を浮かべる。そしてそれ以上に新鮮な驚きをもたらせてくれるのが、子供たちの演技だ。宿題を忘れて泣き出す少年や、友だちのノートを持って帰って来てしまった主人公の心配そうな顔など、ハッとするような瞬間を、カメラはすくいとってしまう。まさに子供たちだけの世界を、魔法のような演出で差し出すのだ。ジグザグ道を走る少年の姿が心に焼き付いて離れない、さわやかな印象を残す一編だ。", "1987-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Himpapawid", "マニラ・スカイ", "", "", "http://image.eiga.k-img.com/images/movie/54958/315x210.jpg?4578", 110, "フィリピンのインディペンデント映画界を代表する存在として80年代から国際的に注目されてきたレイモンド・レッド監督が実話をもとに描く社会派ドラマ。田舎町から希望を胸に大都会マニラへとやって来たものの、厳しすぎる現実に翻弄されて強盗団のメンバーに身を落とした青年。追い詰められて衝撃の決断を下してしまう彼の姿を通し、現代フィリピン社会が抱える闇をあぶりだしていく。", "2009-10-19", "35", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JACK'S BACK", "ジャック・ザ・リッパー／　殺しのナイフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "19世紀末のロンドン市民を震撼させた謎の連続殺人鬼“切り裂きジャック”、その手口をまねた殺人事件が現代のロサンゼルスに続発する。この犯人に双子の弟を殺されたリックは美しい娘クリスとともに犯人を追うが……。古典的なキャラクターに、双子というミステリアスな要素を加えたサスペンス。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ツレがうつになりまして。", "ツレがうつになりまして。", "", "", "http://image.eiga.k-img.com/images/movie/55978/315x210.jpg?3921", 121, "実写ドラマ化もされた細川貂々の同名コミックエッセイを、「半落ち」「夕凪の街　桜の国」の佐々部清監督が映画化。実直なサラリーマンの夫に頼りきりだった漫画家の妻が、夫のうつ病発症をきっかけに、夫婦の関係を見つめ直していく姿を描く。2008年のNHK大河ドラマ「篤姫」でも共演し好評を博した宮崎あおいと堺雅人が、同ドラマ以来約2年半ぶりに強いきずなで結ばれた夫婦を再び演じる。", "2011-10-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IRONWEED", "黄昏に燃えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "1984年度ピュリッツァー文学賞を受賞した同小説を、著者自身が脚本化している。ニューヨーク州オールバニーを舞台に、自分の過去にさいなまれて放浪生活をしている元野球選手フランシスと、才能がありながらアルコールに溺れて落ちぶれている元歌手ヘレンが、大恐慌の中を生き、自分自身を発見する姿を描いた文芸ドラマ。M・ストリープとJ・ニコルソンというアカデミー賞受賞者の顔合わせで、前者は再びオスカーにノミネートされるほどの好演。後者は抑制された演技で、うらぶれた男の哀愁を見事に出し、この作品を味わい深くしている。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紳士同盟", "紳士同盟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "詐欺にかかって借金を抱え込んだお人好しの女子大生が、逆に詐欺をたくらむという、“コン・ゲーム（信用詐欺）”を題材とした明るく楽しい犯罪映画。薬師丸ひろ子の主演10作目の作品で、那須博之監督が、コメディ・タッチで描く。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Matrix", "マトリックス", "", "", "http://ecx.images-amazon.com/images/I/51PITFttT7L._SL210_.jpg", 136, "ヴァーチャル世代のアクションとして熱狂的な支持を受け、大ヒットしたSFX活劇。主人公はコンピュータ・プログラマーで、ハッカーでもある青年ネオ。謎の男モーフィアスと知り合った彼は、衝撃的な事実を聞かされる。実は人間はコンピュータに支配されており、現実と思われている世界は“マトリックス”と呼ばれる仮想現実に過ぎないということ。そして、ネオこそ人類の救世主であるということ。半信半疑のまま、ネオはモーフィアスに誘われ、壮絶な戦いに身を投じていく。大胆かつ意味深な設定の魅力に加え、アクション描写のインパクトも強烈。重力に逆らい、超高速で弾丸をも避ける登場人物の縦横無尽の素速い動きを、カンフー＆ワイヤーアクションという香港映画のエッセンスとCGで作り出し、観客を大いに驚かせた。マンガ世代のウォシャウスキー兄弟による歯切れよい演出、カンフーの特訓を積んだK・リーヴスの精悍なアクション演技も光る。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狗神〈イヌガミ〉", "狗神〈イヌガミ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "“狗神”とは“取り憑かれると食い殺される”と、人々から忌み嫌われる呪われた血族。この狗神筋の家系に生まれついた女性・美希は恋も人生もあきらめ、高知の山里で独り静かに和紙を漉く日々を送っていた。しかし、村に小学校教師としてやって来た青年・晃と出会い、次第に激しく惹かれ合うようになる。同じ頃、村人が次々に変死する事件が起き、村にはこの怪奇現象が狗神筋の仕業だという噂が流れ始める。坂東眞砂子の同名小説を「金融腐食列島 呪縛」の原田眞人が映像化したエロティック・ホラー。白髪の中年女性だった天海祐希が愛する人と逢瀬を重ねるたび、急速に若返っていく様が官能的。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ERIC", "エリックの青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "実際に白血病で息子を失った母親D・ランドが綴った実話をもとに、突然襲った不治の病と闘う青年と、その恋人、そして家族の交流を描く。大学に入学したばかりのサッカー少年エリックは自分の重い病気を知る。だが彼は生きる望みを捨てず、闘病生活を続けながら最後のサッカーの試合に臨むのだった。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Charlotte's Web", "シャーロットのおくりもの", "", "", "http://image.eiga.k-img.com/images/movie/33778/315x210.jpg?4759", 0, "「スチュアート・リトル」の原作者E・B・ホワイトによるベストセラー小説を映画化したハートウォーミングなファンタジー。農場の娘ファーンに育てられた子ブタのウィルバーは、自分がいずれクリスマス用のハムとして食卓に乗る運命にあることを知る。脅えるウィルバーを、納屋に住むクモのシャーロットが励ますが……。主人公ファーンを演じるのは天才子役ダコタ・ファニング。ジュリア・ロバーツ、ロバート・レッドフォードら豪華声優陣も魅力。", "2006-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・日本の首領", "新・日本の首領", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1977年に第１作が公開された中島貞夫監督、佐分利信主演の任侠シリーズ“日本の首領”をリメイク。日本列島に吹き荒れるヤクザ社会のし烈な抗争を、台頭するチャイニーズ・マフィアや、暗躍する政財界との関係にも目を向けながら壮大なスケールで描き出す。松方弘樹を始め、ベテラン俳優たちが大集結、裏社会で生きる男たちの血生ぐさい戦いを熱く演じる。昭和55年。辰野会は、対立していた安西組の解散により大阪で勢力を拡大。やがて、組員が京都の組織・沖名組に殺害されたことで、両組織の対立が激化。辰野会直参上村組組長、上村は、広島の峠会会長に仲介役を依頼するが……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Who Killed Nancy", "フー・キルド・ナンシー", "", "", "http://image.eiga.k-img.com/images/movie/54468/315x210.jpg?3868", 96, "1978年、ニューヨークのチェルシーホテルにてパンクロック・バンド“セックス・ピストルズ”のベーシスト、シド・ビシャスの恋人ナンシー・スパンゲンの遺体が発見され、容疑者としてシドが逮捕された。しかし保釈期間中の1979年、シドはヘロインの過剰摂取により死亡、捜査は打ち切られた。この事件の真相究明に23年の月日を費やし、182人へのインタビューを敢行した作家のアラン・パーカーが監督を務めたドキュメンタリー。", "2009-04-11", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WINDS OF GOD　ウィンズ・オブ・ゴッド", "WINDS OF GOD　ウィンズ・オブ・ゴッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "売れないコメディアン・田代と金太は、ある日、交通事故に遭い、気がつくと、太平洋戦争末期の特攻隊員になっていた。驚きと死への恐れはやがて、特攻隊員たちが生きる時代への、そして自分たちが生きてきた時代への疑問へと姿を変えていく。ロサンゼルス、ニューヨークの上演で絶賛を浴び、国連主催の芸術賞を受賞、日本では文化庁演劇部門の芸術祭賞を獲得した舞台作品の映画化。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼくらの七日間戦争", "ぼくらの七日間戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「汚れた英雄」「愛情物語」で監督補やプロデューサーを務めた、菅原比呂志の監督デビュー作。厳しい学校側のしめつけや、家庭での小言に嫌気がさした中学１年生11人が廃工場に立てこもり、大人たちに宣戦布告するという、憤りのみで主義のない少年たちの闘争を描く。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CORRINA CORRINA", "コリーナ,コリーナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "本作が監督デビューとなった、舞台女優J・ネルソン自身の少女時代をベースにした半自伝的ストーリー。CM音楽家のマニーは、母親が死んだショックで口をきかなくなってしまった７歳の娘モリーの面倒をみてもらうために、家政婦のコリーナを雇うことにする。コリーナは、洗練された物腰と教養あふれる話術を持った魅力的な女性で、モリーも、“ママは天国からモリーを見守っていてくれる”という彼女の言葉に、笑顔と言葉を取り戻す。一方、マニーは作曲の仕事に行き詰まっていたが、音楽評論家志望のコリーナが適格な助言を示し、マニーは信頼と感謝を寄せるようになる。お互いの絆は次第に深まり、モリーは３人が家族になることを願うようになるが……。モリー役のT・マジョリーノが、芸達者のW・ゴールドバーグら大人たちを食うほどの名演を披露。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ねらわれた学園", "ねらわれた学園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "薬師丸ひろ子主演の同名作品の再映画化。「エコエコアザラク」の佐藤嗣麻子の脚本を、実相寺昭雄に師事していた清水厚が大胆に映像化し初監督を果たした。飛鳥学園の新生徒会長に美少女、高見沢みちるが就任、風紀を乱す生徒を徹底的に取り締まる。彼女率いる巡回パトロールのメンバーによって、異様な緊張感に包まれていく学園では、不可解な事件が続発。和美をはじめ、特殊な能力を持つ一部の生徒が真相究明に乗り出す。やがて追い詰められた和美は、みちるの恐るべき陰謀を知り……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三月のライオン", "三月のライオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "病院で目覚めた記憶喪失の兄に、恋人だとウソをついて同居を始める妹。兄はやがて少しずつ記憶を取り戻し、二人の生活は揺れ動き始める……。自主製作の雄、矢崎仁司が満を持して放つ、世紀末の香りに彩られた禁じられた愛の物語。ベルリン映画祭をはじめ海外でも話題となった注目作。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Poetry", "ポエトリー　アグネスの詩", "", "", "http://image.eiga.k-img.com/images/movie/55848/315x210.jpg?1273", 139, "「オアシス」「シークレット・サンシャイン」のイ・チャンドン監督が、アルツハイマー症に冒され徐々に言葉を失っていく初老の女性が、一編の詩を編み出すまでを描いた人間ドラマ。釜山で働く娘に代わり中学生の孫息子ジョンウクを育てる66歳のミジャは、ふとしたきっかけで詩作教室に通い始めるが、その矢先に自分がアルツハイマー型認知症であることが発覚する。さらに、少し前に起こった女子中学生アグネスの自殺事件にジョンウクがかかわっていたことを知り、ショックを受けたミジャは、アグネスの足跡をたどっていくが……。2010年・第63回カンヌ国際映画祭で脚本賞を受賞。", "2012-02-11", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY TUTOR", "個人授業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "高校３年生のボビーの悩みはフランス語の成績がかんばしくないこと、そしていまだに女性経験がないことだ。父親は息子を優秀な大学に入れようと一計を案じ、ブロンド美女の家庭教師をつけるが……。初体験、年上の女性とのラブ・ロマンスを描く一編。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼子來了", "鬼が来た！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "日中戦争の末期、中国・華北の寒村に一人の日本兵が置き去りにされた。村人たちは、話し合いの結果、男を生かしておくことに決め、やがて彼らは日本軍から謝礼を受け取ることになるのだが……。「太陽の少年」のチアン・ウェン監督が、日本侵略下における中国人と日本人との不可思議かつ凄惨な関係をブラック・ユーモアたっぷりに描いた意欲作。日本から参加の香川照之が、当時の日本人の滑稽かつ残忍な姿を見事に体現している。", "2000-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すけばん刑事 ダーティー・マリー", "すけばん刑事 ダーティー・マリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "他人の情事を望遠レンズで盗み撮りしてはそれをネタにゆすりを働く男、坂井。ある日彼はデザイナーの女をユスったが、騒がれたため彼女を殺してしまう。ジーパン姿の変わりダネ刑事“ダーティー・マリー”がこの事件の担当になるが、彼女は坂井に逆に犯されてしまうのだった。女刑事が主人公のハードボイルド・タッチのニュー・アクション。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Happy Life", "楽しき人生", "", "", "http://image.eiga.k-img.com/images/movie/54128/315x210.jpg?1970", 112, "「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。監督は「王の男」のイ・ジュンイク、出演に「王の男」のチョン・ジニョン、「ヨコヅナ・マドンナ」のキム・ユンスクほか。20年前に解散したロックバンド“活火山”のメンバーたちはすっかり中年オヤジとなり、無為な毎日を過ごしていた。そして、バンドのリーダーだったサンウの葬儀を機に、バンドの再結成を決意したメンバーだったが……。", "2008-11-08", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ライフ・イズ・ジャーニー", "ライフ・イズ・ジャーニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "俳優、田辺誠一の監督第２作。一人の女性の生涯をワンシーン・ワンカットで撮った「LIFE」、恋人に去られた男が町をさまよう姿を綴った「ん」、失恋した女の子と友人の会話を描いた「ヤ」、これが映画初出演となるシンガーのhitomiをヒロインに迎え、ポルトガル・ロケを実施した「No where」の４話で構成。実験的な手法を導入し、“人生”というテーマと向き合った意欲作だ。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mikey and Nicky", "マイキー＆ニッキー", "", "", "http://image.eiga.k-img.com/images/movie/49548/315x210.jpg?2254", 106, "「こわれゆく女」（1974）、「オープニングナイト」（78）などで知られるピーター・フォークとジョン・カサベテスのコンビが主演した男の友情を描くドラマ。日本では「マイキー＆ニッキー　裏切りのメロディ」のタイトルでレンタルビデオのみがリリースされていたが、2011年ニュープリントで劇場公開が実現した。組織に切り捨てられ命を狙われるニッキーと、同じ組織に関係する幼なじみのマイキーは、2人で町を出ることになる。しかし、その前に立ち寄ったニッキーの愛人宅での出来事が、2人の逃避行に思いもよらぬ結末をもたらす。", "2011-03-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吶喊", "吶喊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1968年の「肉弾」で自らの戦争体験の意味を問い直した岡本喜八監督が、100年前に時代をさかのぼって戊辰戦争を材にとり、同じ境遇に生きる若者の青春を描いた作品。師・マキノ雅広を思わせる軽妙なタッチながら、死と背中合わせの若者の心情を刻む。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAREER OPPORTUNITIES", "恋の時給は４ドル44セント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "親元から自立できないお調子者のジムは、スーパーの夜間清掃員の職を得た。出勤当日、誰もいないはずのスーパーで、彼の前に現れたのが憧れの美少女ジョシー。厳格な父に反抗しようと、彼女は万引きをするつもりだったのだ。お互いの本心を語り合った二人は打ち解け、なんだかいいムードに。ところがスーパーの中には、二人組の強盗殺人犯も忍び込んでいたことから、ジムとジョシーは愛を確かめ合う間もなく、大騒動に巻き込まれていく……。J・ヒューズが製作、脚本を担当した青春コメディ。人気女優J・コネリーが見せる、ちょっぴり大胆でセクシーなポーズも楽しみの一つ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TALES OF TERROR（THE BLACK CAT）", "黒猫の怨霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ポーの原作に基づく３話構成のオムニバス映画。第１話は、妻の霊が娘に取り憑く『怪異ミイラの恐怖』。第２話は、壁に死体とともに塗り込められた黒猫が殺人事件を暴く『黒猫の怨霊』。第３話は、催眠術によって成仏できない亡夫の霊がよみがえる『人妻を眠らす妖術』。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("としごろ", "としごろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "当時“花の中三トリオ”と呼ばれた、山口百恵、桜田淳子、森昌子の3人。本作は森を主演に据えたアイドル青春映画。百恵をはじめ歌謡界から和田アキ子、石川さゆりらがサポート出演を務めている。バレーボール部の友達同士だった昌子と優子が中学校を卒業。昌子は母子家庭のために進学を諦めて工場に就職するが、優子は高校でバレーを続けることに。そんなある日、元同級生が心中事件を起こす。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花も実もある為五郎", "花も実もある為五郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "義理と人情に生きる男・阪東為五郎の活躍を描いたシリーズ第４作。荒神山のふもと加佐登町を舞台に、温泉で一旗揚げようとたくらむ町長一味と、それが嘘の温泉であることを知った為五郎との対決を描く。バスジャック犯の小松政夫など、脇役陣が笑わせる。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雲ながるる果てに", "雲ながるる果てに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ベストセラーとなった同名の学徒航空兵の手記集をもとに、家城巳代治監督が松竹退社後初のメガホンを取り、鶴田浩二・木村功主演で映画化した反戦映画の代表作。太平洋戦争末期の昭和20年春、本土南端の特攻隊基地を舞台に、様々な思惑を胸にした学徒航空兵の短い青春が綴られる。家城巳代治監督は、特攻隊の学徒航空兵という後戻りできない悲運の青春群像を通して、決して力むことなくむしろ淡々としたタッチで、戦争に対する怒りをぶつけた。戦争の臭いがまだ生々しく残っている時代だけに、鶴田浩二・木村功らの出演者が実にリアリティーあふれる学徒兵ぶりである。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Princess Diaries 2", "プリティ・プリンセス2 ロイヤル・ウェディング", "", "", "http://ecx.images-amazon.com/images/I/511D0YV661L._SL210_.jpg", 113, "普通の女子高生だったミアが実はヨーロッパのある王国のプリンセスであることが判明してから5年が過ぎた。今やミアはプリンセスとして国で優雅な日々を送っていた。ところが、王位継承のためには30日以内に結婚しなければならないという事実が判明し……。「プリティ・プリンセス」の続編。", "2005-02-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Next Stop Wonderland", "ワンダーランド駅で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "主人公の男女が、なかなか出会えないユニークなラブ・ストーリー。失恋して新たに恋を探す看護婦と、配管工をしながら大学に通う青年。ボストンのワンダーランド駅行きの地下鉄を通勤に利用している彼らは、ニアミスを繰り返しながら、それぞれの恋に悪戦苦闘し……。ユーモアにあふれた軽妙な会話や、全編に流れるアンニュイなボサノバが魅力的。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PATHER PANCHALI", "大地のうた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "ベンガルの貧しい下級官吏の息子の成長過程を追った大河ドラマ３部作の第１作。オプー少年の父が賃労働で遠出する間、貧窮をきわめる生活を母は必死で切り盛りする。しかし、姉が肺炎で倒れたのに意を決して、新しい生活を求め一家は住みなれた故郷を離れていく。インドの片田舎の日常生活を、少年のくもりのない眼を通して、静かに力強く描ききったレイの処女演出は、厳しいリアリズムと節度のある叙情性をもっていて見事。特に、オプーが優しい姉を連れ、森を抜け、すすき野を横切って、平原を走る汽車を見に行くシーンは、大自然を映し出すモノクロ映像が少年の遠い憧れにきらめいて、実にみずみずしい。", "1955-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONEY　ISLAND", "コニーアイランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "サイレントを代表するコメディアン、バスター・キートン主演による約20分の短編。街が謝肉祭でにぎわう日、キートンは恋人を遊園地に誘い、デートとしゃれこんだ。しかし遊園地では、恐妻から逃れようとしていたデブ男が、ゴーカートに乗って気分が悪くなったキートンの彼女にちょっかいを出し始めていく……。", "1917-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("M／OTHER", "M／OTHER", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 147, "「２／デュオ」で注目された諏訪敦彦監督の第２作。デザイン会社勤務のアキは、レストラン・オーナー、哲郎と同棲中。彼らの一軒家に、哲郎の別れた妻との子供、俊介が転がり込んできたことをきっかけに、目に見えなかった“他者”との亀裂が表面化していく。完成台本がないという独自の手法を再び実践しながらも、ストーリーすら決まっていなかった前作とは異なり、撮影前に監督とキャストが登場人物の背景に関するディスカッションを実施。序盤にはおよそ10分間に及ぶ長回しのショットが盛り込まれるなど、現場の臨場感を重視した映像には異様な緊迫感が張りつめている。また“疑似家族”の営みの中で突如ストレスを暴発させるヒロインの心の揺れを、渡辺真起子がリアルに体現している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名波止場", "悪名波止場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "朝吉と清次は四国からの帰りの船中、だまされて鬼瓦のところで働く羽目になる。そこで、鬼瓦一味が麻薬の密売で悪どくもうけていることを知り、怒った二人は鬼瓦一味を叩きのめすのだが……。水原弘がヒモ役をニヒルに演じる。", "1963-01-01", "76", 0, NOW(), NOW());
