INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mannen pa taket", "刑事マルティン・ベック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "スウェーデンの人気小説「マルティン・ベック」シリーズの第7作「唾棄すべき男」を「みじかくも美しく燃え」等の作品で知られるボー・ウィデルベルイ監督が映像化。ストックホルムの病院で、入院中のニーマン警部が殺害された。捜査に乗り出したマルティン・ベック刑事らは、ニーマンが悪名高い人物だったことを知る。やがて捜査線上に、愛する妻をニーマンに見殺しにされた過去を持つ元警察官エリクソンの姿が浮かび上がる……。", "1976-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RE´GIME SANS PAIN", "ロック公国・ジャゾン III世", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "チリ出身の異才R・ルイスがミュージシャンのアンジェル＆メモーヌと組んで作ったキッチュなSF作品。ロック公国の国王であり、国民のスターであるジャゾン III世は、仕組まれた死の儀式が迫ってくるのに気付く。彼の創造主であるピー博士は、ジャゾン III世の人格を抹消するが、彼には計画があった……。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギャンブラー", "ギャンブラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "　カナダ国境近くの町に、三流のトランプ賭博師マッケイブが流れ着く。彼はギャンブル好きの鉱夫たちから金を巻き上げ、賭博場を建てる。その後、娼婦のコンスタンスと組み、売春宿の経営で大儲け。しかし、やがてマッケイブは町を乗っ取ろうとする者に命を狙われることに……。鬼才ロバート・アルトマン監督の異色ウェスタン。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Conspirator", "ザ・コンスピレイター（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHIENS PERDUS SANS COLLIER", "首輪のない犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "タイトルの“首輪のない犬”とは、親元から離れた寄るべない子供たちのこと。競技場の更衣室からサイフを盗んだ少年、両親に会うために新聞に載ろうとして農家に放火した少年など、様々な事情を持つ少年たちを判事のラミイが温かく見守り、更生させていくさまを描く。判事役は、名優J・ギャバン。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Parallax View", "パララックス・ビュー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "次期大統領候補が暗殺され、単独犯とされた犯人が墜落死した。それから三年後、女友達の死をきっかけに新聞記者が調査をすると、進歩派を憎むプア・ホワイトをテロに仕立て上げる暗殺者養成所の存在が浮かび上がる。社会派として名高いパクラ監督の政治サスペンス。", "1975-06-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Glory Stompers", "続・地獄の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "バイクに乗り、セックスとスピードに溺れる一団“グローリー・ストンバーズ団”と“ブラック・ソウル団”。この抗争劇にラブ・ロマンスを絡ませた1960年代版「ウエスト・サイド物語」。一方のバイク団の首領をD・ホッパーが好演。「イージー・ライダー」へとつながる彼の役者遍歴がわかる１本。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAS CABINET DES Dr.CALIGARI", "カリガリ博士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "不滅の古典とされるドイツ表現主義の傑作サイレント映画。製作者により、すべては精神病患者の妄想というエピソードが付け加えられたが、もともとは権力者による精神操作を強烈に批判した作品である。とある町にやって来たカリガリ博士と名乗る怪人。彼は、ツェザーレという男を催眠術にかけて、お客の運命を占う商売を始めた。予言通りに次々と死んでいく人々。実は、博士こそが、ツェザーレを使って殺人を重ねていたのだ。主人公フランシスはカリガリ博士を追跡する。博士は精神病院に逃げ込み逮捕されるが……。コントラストの強い画調と奇妙に歪んだセットの中で、加害者と被害者が入り組んだ奇怪なドラマが展開。", "1919-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("油断大敵", "油断大敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "脚本家の成島出が監督に初挑戦。実在の人物をモデルに、新米刑事と大泥棒の奇妙な交流を描く。子育てに悪戦苦闘しながら、泥棒専門の刑事として成長していく刑事に役所広司、警察をほんろうする伝説の大泥棒に柄本明。敵対する立場でありながら不思議な絆で結ばれていく男たちを、演技派二人が人間味豊かに好演。取調室での長ゼリフのシーンは圧巻だ。妻を亡くし、男手一つで娘を育てる刑事・仁は、多忙の毎日。そんなある日、彼はひょんなことからプロの大泥棒・猫田、通称ネコを捕まえる。決して自供しないことで有名なネコだが、仁の実直さに惹かれて犯行を語り始める。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FFOLKES FFOLKES", "北海ハイジャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "“007”のR・ムーアが、女性より猫と刺しゅうが好きだ、という変わりダネの私設フロッグメン・チームのリーダーに扮し、北海油田の爆破をネタに身代金を要求するハイジャッカーと対決。間一髪で爆破を阻止するというアクション映画。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAREM　SUARE’", "ラスト・ハーレム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "20世紀初頭、オスマントルコ帝国最後のハーレムに生きた絶世の美女、サフィエがたどる数奇な運命を豪華絢爛に描いた壮大なラブ・ロマンス。ハーレムの女となったサフィエは、その美貌と利発さで、女たちに仕える黒人の宦官ナディールの目に留まる。二人はスルタンの寵姫の座を得て、権力を手中にすることを誓い合ったのだが……。映画をよりリアルにするために、撮影は実際の史跡で行われた。", "1999-01-01", "4,2,32", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN AMOUR DE SWANN", "スワンの恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "フランスの文豪プルーストの大長編『失われた時を求めて』の一編を映画化。19世紀末のパリ社交界。高級娼婦オデットに心惹かれる青年スワンは、ついに彼女と一夜をともにするが……。狂おしい恋に悩むスワンの心の葛藤を秀逸な映像美で描く。", "1983-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES CARABINIERS", "カラビニエ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「軽蔑」準備中のゴダールが酷寒のパリ郊外で3週間で撮りあげた快作。いつともどことも知れぬ戦争の最中、ふたりのカラビニエ（機銃兵）が、国王からの徴兵令状を荒野のユリシーズとミケランジェロ兄弟に届けにくる。兄弟はカラビニエとなり戦場へ赴く。戦争でさえなければ楽しい旅の日々からふたりが持ち帰った戦場の土産とは……。", "1989-01-14", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Baby Face Nelson", "殺し屋ネルスン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "　刑務所に服役していたレスターは情婦スー・ネルスンの助けで脱獄し、彼を罠にはめたギャングのロカに復讐を果たす。その後、レスターはネルスンを名乗って、スーと内縁の夫婦になる。凶悪な犯罪者デリンジャーと知り合ったネルスンはその一味に加わることに。悪事を繰り返し、やがてベビー・フェイスのネルスンと呼ばれるお尋ね者となった彼にFBIが迫る……。実在した犯罪者を描くドン・シーゲル監督の傑作。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cosmonauta", "コズモナウタ　宇宙飛行士", "", "", "http://image.eiga.k-img.com/images/movie/55428/315x210.jpg?1622", 85, "1950～60年代のイタリア。風変わりなアルトゥ−ロと快活なルチャーナは、共産主義を心から信奉し、コズモナウタ（ソ連の宇宙飛行士）に憧れる仲の良い兄妹だった。しかし、異性の視線が気になる年頃になったルチャーナは、アトゥーロと距離を置くようになる。監督は本作が長編デビュー作となるスザンナ・ニッキャレッリ。イタリア映画祭2010にて上映。", "2010-04-28", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("手をつなぐ子等", "手をつなぐ子等", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "個性が強すぎて先生を手こずらせ、３度も学校を変えられた中山寛太は、両親も持て余してしまうほどの少年だった。そこに児童教育に熱心な松村という先生が現れ、寛太を受け入れようという。寛太はこの松村先生がすっかり気に入り、学校へ行くのが面白くてしようがなく、毎朝門が開く前から学校へ行くほどであった。そこに金三という札つきの悪童が編入して来る。金三は善良な寛太をおもちゃにし、ことあるごとにいじめるが、ついに寛太の善良さの前に意地悪な性格を改めるのだった。寛太という愛すべきキャラクターを作り上げたのは、稲垣監督と名コンビで知られる伊丹万作。稲垣演出は優しさにあふれ、宮川一夫の撮影も素晴らしい。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　恐竜ノッシーの大冒険", "それいけ！アンパンマン　恐竜ノッシーの大冒険", "", "", "http://ecx.images-amazon.com/images/I/21Y8ZADYM9L._SL210_.jpg", 60, "アンパンマンと楽しい仲間たちの活躍を描く人気アニメの劇場版第5作。SLマンの列車に乗ってピクニックに出かけたアンパンマンたち。ところが、途中でばいきんまんたちの妨害を受け、全員が異次元空間へと迷い込んでしまう。恐竜の国にたどり着いたアンパンマンたちは、そこで出会った恐竜の子ノッシーと仲良くなる。恐竜の国を支える「命の木」が枯れかかっていると知ったアンパンマンたちは、命の木を救うことができる「光の玉」を探しに行くが……。", "1993-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドリフターズですよ！　前進前進また前進", "ドリフターズですよ！　前進前進また前進", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "組の解散で、なすすべもなくなった下っ端のヤクザ者たち。新たに“なんでもコンサルタント”なる会社を始めるのだが……。監督は、処女作「自動車泥棒」の斬新さが注目された和田嘉訓で、これが３年ぶりの監督第２作。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HALLOWEEN６ THE CURSE OF MICHAEL MYERS", "ハロウィン６　最後の戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ブギーマンことマイケルが死んで５年。ハドンフィールドの町は、すっかり平和を取り戻していた。しかし、肉親殺害に駆り立てる“イバラの呪い”によってマイケルは復活してしまう。そこへ、執念深くマイケルを追い続けるルーミス医師や、マイケルの最初の殺人の目撃者トミーもやって来て、惨劇を阻止するために立ち上がる。本作出演後、第１作からルーミス医師役で出演していたD・プレゼンスが急逝。これが、シリーズ完結編ともいうべき作品となった。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LOST BOYS", "ロストボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ヴァンパイアのターゲットになった兄や母を救うために、10歳の少年が仲間とともに吸血鬼退治に乗り出すという異色ホラー。スリラー、サスペンス、スペクタクルの要素がぎっしりと詰まった娯楽大作だ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("甦るヒーロー 片山敬済", "甦るヒーロー 片山敬済", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "1983年のロードレース選手権で転倒して両足を骨折。再起不能といわれながらも不屈の闘志で、再びレースの世界に戻った天才ライダー・片山敬済の、戦いぶりやレースに賭ける情熱などに焦点を当てたドキュメンタリー。泉監督は、片山の友人でもある。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHANEL SOLITAIRE", "ココ・シャネル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "スーツや香水などでおなじみの人気ブランド“シャネル”の創始者ココ・シャネルの恋と波乱に満ちた半生を描いたメロドラマ。母を亡くし父に去られ孤児院生活を送った少女期から初恋、初体験、そしてファッション界へ。ピジェの熱演に加えて、男優ダルトン、ハウアーとの共演も見もの。", "1981-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Apocalypse Now Redux", "地獄の黙示録　特別完全版", "", "", "http://image.eiga.k-img.com/images/movie/1008/315x210.jpg?2120", 203, "オリジナルの未編集分を再編集。新たに付け加えられた53分の映像は、オリジナルになかったフランス人入植者の農園でのエピソードや、海軍巡視艇が川を上る旅を始めようとしているシーンなどを含む。コッポラによれば「アメリカ大衆がいかにウソをつかれているかを見事に言及してしまっているそのシーンは、公開バージョンに入れることが出来なかった」とのこと。", "2002-02-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("闇の狩人", "闇の狩人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "金権汚職の田沼意次の時代を背景に、勢力の拡大を図る殺しの組織を率いる五名の清右衛門と、それにかかわる人々との愛憎劇、人間模様を現代的なドラマの感覚で描いた時代劇映画の佳作。殺陣に次ぐ殺陣の激烈なチャンバラ・シーンと、舞台美術が見もの。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL BIDONE", "崖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "僧侶の衣装を着て、純真な人々から金を巻き上げるという商売をする男たちの話。フェリーニ監督が、人間の良心や宗教の問題をペーソスを込めて描いた秀作。最後に崖の下に転落して、そこからはい出ようとするが、果たせず死んでしまうラストが印象的。", "1955-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISS TOMORROW GOODBYE", "明日に別れの接吻を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「午後の喇叭」のW・キャグニーが製作した切なくも哀しいギャング映画。刑務所を脱獄した凶悪犯が、姿をくらますどころか、仲間と組んで自動車修理工を殺し、マーケットを荒らし、ついには警察に乗り込んで自分の前科記録を奪い去る。しかし、上流社会の娘に接近したのが運のつき、情婦の銃弾に命を落とすことに……。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BANGER SISTERS", "バンガー・シスターズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ジム・モリソンの元追っかけで、今は別々の道を歩く二人の女性が再び心を通わせるまでを描いたドラマ。ゴールディ・ホーンとスーザン・サランドンの二人が初共演、軽妙な掛け合いで楽しませてくれる。元グルーピーで伝説のロッカーたちと一夜をともにした経験を持つスゼット。今はライブハウスで働く彼女だが、店をクビになる。お金に困った彼女は、かつての相棒ヴィニーの元に向かうが、ヴィニーは過去をきっぱりと捨てていて……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLING　TIME", "キリング・タイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "パニック映画と恋愛劇の要素を豪快にミックスさせた「ダウンタイム」でデビューしたB・ナルルーリ監督が、再び斬新な映像感覚を発揮した犯罪アクション。黒服に身を包んだ謎の女殺し屋が、冷酷非情なギャングのボス、ライリーを暗殺するためにイギリスの空港に降り立つ。彼女を雇ったのは誰なのか。その行動をマークする刑事やチンピラらの思惑が絡み、やがて死者続出の異常事態が引き起こされる。イタリア人の女殺し屋がさっぱり英語がわからないという設定がユーモアをふりまくと同時に、ドラマの伏線になっている仕組み。群がる敵を返り討ちにし、ほとんどセリフをしゃべらない主演女優K・トーガンのクールな魅力も出色だ。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("≒天明屋尚", "≒天明屋尚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "アーティストたちの魅力を独自の視点でとらえたアート・ドキュメンタリー「≒（ニアイコール）」シリーズの第4弾。2006年ドイツFIFAワールドカップのアートポスターに日本人で唯一参加するなど、大胆さと繊細さをあわせ持った「ネオ日本画」で国内外から注目を集めるアーティスト、天明屋尚に密着。作品制作の様子や関係者らへのインタビューを交えながら、自らを絵で闘う「武闘派」と称して現代社会に物申してきた天明屋の素顔を映しだしていく。", "2006-06-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILENT MOVIE", "メル・ブルックスのサイレント・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "売れない映画監督メルは、トーキー映画全盛の現代に無声映画を作り、世間をアッと言わせようと画策するのだが……。M・ブルックスの妻である名女優A・バンクロフトまで担ぎ出し、無声映画に対して、パロディーで敬意を捧げたM・ブルックスらしい傑作コメディ。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARNIVAL OF SOULS", "恐怖の足跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "衝撃のクライマックスが「シックス・センス」などに密かに影響を与えたとみられるカルト・ホラー作品。公開当時ゴーストものが多かった中でも、異彩を放つ怪作だ。3人の娘を乗せた車が川に転落し、メリーだけが何とか助かった。しかし、その後時々幽霊を見るようになる。やがて、自分が夢遊病にかかっていることに気付き、さらに幽霊の大祭を目撃する。幽霊の正体とは？", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Magnum Photos", "マグナム・フォト　世界を変える写真家たち", "", "", "http://image.eiga.k-img.com/images/movie/53098/315x210.jpg?1895", 89, "1947年にロバート・キャパが発案し、50年以上に渡って世界の決定的瞬間をカメラに収め続けてきた写真家集団“マグナム・フォト”。その輝かしい歴史を振り返りつつ、メンバーたちの活動を追うドキュメンタリー。創設メンバーの1人であるアンリ・カルティエ＝ブレッソンや次世代を担うマーティン・パーら16名の中心メンバーがそれぞれの思いを語るほか、彼らの撮影現場やマグナムの年次総会の模様も映し出し、その内幕を明らかにしていく。", "2007-12-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　鋼の錬金術師　シャンバラを征く者", "劇場版　鋼の錬金術師　シャンバラを征く者", "", "", "http://ecx.images-amazon.com/images/I/51BRXE3PM2L._SL210_.jpg", 105, "2003年～04年に放送された荒川弘の人気コミックが原作のアニメーション「鋼の錬金術師」の劇場版。ふたつの世界に引き離されてしまったエルリック兄弟は、それぞれ再会を願って元に戻る手段を探し求めていた。1923年ドイツ・ミュンヘンに飛ばされてしまった兄エドワードは、弟と似た面影をもつ若者アルフォンス・ハイデリヒの力を借り、科学技術の粋を集めたロケット工学の力で故郷への帰還を目指していが、そんな時、理想郷「シャンバラ」を追い求め暗躍する者たちが現れ……。監督はTVシリーズに続き水島精二。ゲスト声優で俳優の小栗旬が参加。", "2005-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新女囚さそり・701号", "新女囚さそり・701号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "さそり役には梶芽衣子に代わって多岐川裕美が扮し、監督は小平裕があたった。ストーリーは政治家の陰謀で姉を殺されたナミが復讐するというもの。小平の演出は、伊藤＝梶コンビのさそりを意識し、白塗りを使用するなど様式美で描いている。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生贄夫人", "生贄夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "主演・谷ナオミ、田中陽造脚本、小沼勝監督のトリオによる「花と蛇」に続くSMシリーズ第２作。アブノーマルな性向を持つ夫によって山小屋に監禁され、調教のあとマゾヒストと化した秋子が今度は心中未遂の男女を緊縛・浣腸となぶりまくる。強烈な異常性愛シーンのみならず、作品としても上出来。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Terra em Transe", "狂乱の大地", "", "", "http://image.eiga.k-img.com/images/movie/56298/315x210.jpg?4188", 107, "ブラジルの新しい映画＝シネマ・ヌーボの旗手として賞賛されたグラウベル・ローシャ監督の没後30年を記念し、日本初公開作を含む代表作を連続上映する「グラウベル・ローシャ・ベスト・セレクション」にて上映。架空の共和国エルドラドを舞台に、抑圧と貧困からの解放をめぐって揺れ動く人々の心象と現実を、さまざまなエピソードを交えて壮大に描く。2011年日本初公開。", "2011-06-18", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("懲役三兄弟", "懲役三兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "腕も度胸も十分の３人の義兄弟が、素人衆を泣かせる別府の暴力団の東洋会に、命賭けの勝負を挑む。菅原文太の東映時代初期の主演作で、高倉健が特別出演。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎 無頼剣", "眠狂四郎 無頼剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "義憤にかられ、江戸の町を火の海にしようとたくらむ浪人・愛染と、それを阻む狂四郎の対決。伊藤大輔の脚本を得て一段とスケール・アップした第８作。大火をバックに大屋根で展開する天知茂扮する愛染との一騎打ちなど三隅の演出が冴えわたり、シリーズ屈指の出来に仕上がった。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／H", "H〈エイチ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "妊娠中の女性ばかりが狙われる連続殺人事件が続発。この異常な事件と獄中のシリアルキラーとの関連性を探ろうとする男女の刑事コンビの苦闘を、おぞましい猟奇シーンを散りばめて描いたサイコ・スリラー。「ラブストーリー」で純朴な若者を好演した若手俳優チョ・スンウが、「羊たちの沈黙」のレクター博士を彷彿とさせる獄中の死刑囚を怪演している。ゴミ捨て場で妊娠中の女子高生と、引きずり出された胎児の死体が発見される。それから１ヵ月後、今度はバスの中で腹部を十文字に切り裂かれた臨月の女性の死体が……。その手口は１年前に起きた６つの連続殺人と酷似していた。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAR GAMES", "ウォー・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ハイテク時代の危機を告発する衝撃のSFサスペンス。パソコン狂の少年が、偶然アクセスしたのは北米大陸防空司令部のコンピュータであった。そうとは知らず、少年は全面核戦争ゲームを始める。やがてコンピュータが反応し、ゲームは現実化していくが……。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銭形平次", "銭形平次", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "業平橋の下に千両箱を背負った男の死骸が浮かび、袂の手紙の切れ端から絵師の三浦楪渓と判明する。平次は奉行所の書類を調査していくうちに、事件の裏に公金を奪って逃走中の６人組の強盗団が関係しているとにらむ。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Majestic", "マジェスティック", "", "", "http://image.eiga.k-img.com/images/movie/1118/315x210.jpg?2534", 153, "51年、赤狩りの気配が忍び寄るハリウッド。新進脚本家ピーターは交通事故で記憶を失い、小さな町にたどり着く。彼を見て戦争から帰った息子だと思いこんだ老人は、彼とともに、休館中の老朽化した映画館マジェスティックを再建しようとする。監督は「ショーシャンクの空に」「グリーンマイル」のフランク・ダラボン。主演はシリアス作の続くジム・キャリー。老映画館主役でマーティン・ランドーが共演。", "2007-06-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FEMALE　PERVERSIONS", "イヴの秘かな憂鬱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "デレク・ジャーマン監督作品のミューズとして知られる英国の美人女優T・スウィントンが主演した心理ドラマ。敏腕検事として活躍するかたわら、愛人とのセックスに身を焦がすキャリアウーマン、イヴ。田舎暮らしの姉が万引きで捕まったことをきっかけに、過去のトラウマと向かい合う彼女の葛藤を通して、現代女性が抱えるストレスの問題が浮かび上がっていく。ビデオ発売時タイトルは「イヴ」。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APFEL BA¨UME", "林檎の木", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ドイツ分断の直後に生まれ、旧東ドイツの林檎園で働く女性の半生を綴った感動ドラマ。レーナはベルリンの壁が築かれて１年後、ポツダムで生まれた。成人になった彼女は、社会主義国家の理想が薄れていくのを感じ林檎園に就職。そこで働くハイツと結婚するが、ブルジョワ階級出身の労働組合長ジェンケの強引な求愛を受け入れてしまい、三角関係に陥ってしまうのだった……。", "1992-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Seven Years in Tibet", "セブン・イヤーズ・イン・チベット", "", "", "http://ecx.images-amazon.com/images/I/41TGfEQbSLL._SL210_.jpg", 136, "実在のオーストリアの登山家H・ハラーが、第二次大戦前後にたどった波乱の運命を描くヒューマン・ドラマ。1939年、ヒマラヤ登頂の野心に燃えるハラーは、身ごもった妻のことも顧みずヒマラヤ山脈に向かう。しかし戦争が勃発し、ハラーと登山隊のペーターらは連合軍の捕虜となる。ペーターとともに収容所を脱走したハラーは、二年間の山中でのサバイバル生活を経てチベットへ到達。そこでハラーは若きダライ・ラマの家庭教師となり、西洋文化に興味を抱く彼との交流の日々を過ごすのだった。偉大な宗教指導者ダライ・ラマとの出会いによって、独善的だった主人公の人間性の目覚めをピットが熱演。アンデス山麓周辺に大がかりなセットを建造し、チベットの生活様式を再現している。序盤の登山シーンをはじめとする壮大な自然美も見もの。", "1997-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARTY GIRL", "暗黒街の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1930年代初頭のシカゴ。ギャングの法律顧問として闇社会で生きてきた辣腕弁護士のトムは、暗黒街のクラブで踊り子をしているビッキーと出会い、暗黒街で働きながら純粋さを失わない彼女に強く惹かれていく。やがて二人の純粋で激しい愛は暗黒街で抗争を巻き起こす引き金となるのだった。過酷な現実に立ち向かいながら、純粋な愛をはぐくむ男女の姿を描く、N・レイ最後のハリウッド作品。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本春歌考", "日本春歌考", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "添田知道が性に関する俗歌を収集した「日本春歌考」に題名を借りて、大島渚が監督した異色の風俗ドラマ。セクシャルな妄想と現実との間で出口を見失い、自己を喪失していく大学受験生たちの姿を通じて、国家や家族といった共同体と一個人との係わり、性の問題を強く打ち出している。こののち「儀式」「愛のコリーダ」という、それぞれの問題を究極にまでつきつめた作品を発表した大島渚を語る上で、もっとも注目すべき一編。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仕掛人梅安", "仕掛人梅安", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "仕掛人の元締め、音羽屋の抹殺をたくらむ安部長門守と近江屋の異母兄弟。音羽屋一派から仕事を請け負っていた仕掛人・梅安は逆襲に出るのだが……。松竹で作られていた“必殺仕掛人”シリーズが、製作会社を東映に変えて再登場した。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOLSHE VITA", "カフェ・ブダペスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ソ連軍が撤退した1990年夏、ハンガリーの首都ブダペストに集まった男女６人の姿を綴る青春映画。時代が変わりゆく狂騒の東から西へ、または熱狂を求めて西から東へ向かう若者たちの交錯が、混沌とした熱気の中に語られる。祖国ソ連をニセ楽団の一員として脱出し、ブダペストにたどり着いたユーラとワジム。そこで彼らは二人のアメリカ人女性と恋に落ち、カフェでの演奏のバイトを始めるが……。", "1995-01-01", "30,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("川の流れのように", "川の流れのように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "美空ひばりの名曲をモチーフに、その作詞家でもある秋元康が自らメガホンを執ったヒューマン・ドラマ。時代に取り残されたような故郷へ、60年ぶりに帰ってきた女流作家と、無為な日々を過ごしているかつての友人たちとの心の交流がノスタルジックに描かれていく。主演の森光子は、これが13年ぶりの劇映画出演となった。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の子どものように", "風の子どものように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ドイツを代表する児童文学作家、ペーター・ヘルトリングの『ヨーンじいちゃん』を映画化。瀬戸内海の小豆島に住む娘夫婦と孫たちに会うため、ギリシアからおじいちゃんがやって来る。何ごとにもとらわれない型破りなおじいちゃんは、島で騒動を起こしたあと、ある婦人と恋に落ちて、ギリシァへ再び旅立つが……。祖父、娘、孫。三代の交流を温かい視線で見つめたファミリー映画。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("СВОЙ　СРЕДИ　ЧУЖИХ, ЧУЖОЙ СРЕДИ　СВОИХ", "光と影のバラード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "N・ミハルコフ監督の長編デビュー作。舞台は1920年代初頭、食糧難にさらされたロシア。外国の穀物を輸入するための金塊を護送する任務に就いた赤軍兵士シーロフを主人公に、白軍やアナーキスト一味との攻防がスリリングに描かれていく。ハリウッドの西部劇を思わせるダイナミックな列車強盗シーンなどの見せ場を織り込み、それまでの“革命映画”とは一線を画す仕上がりが国内外で評判になった。", "1974-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後の言葉・dear be loved", "最後の言葉・dear be loved", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "路上から飛び出した人気女性シンガーソングライター、川嶋あいに迫るライブ・ドキュメント。1000回を目標に続けられた彼女の路上ライブの模様をカメラが追う。災害地への慰問やボランティア活動など、さまざまな形でライブを継続する川嶋。このライブに懸けた彼女の想いが明かされる。シンガーソングライターの川島あいは、路上ライブを始める。目標を1000回とした彼女は、全国津々浦々を巡り、ライブを継続する。", "2005-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("偽大学生", "偽大学生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "当時、文壇の若きリーダーだった大江健三郎の『偽証の時』を、脚本・白坂依志夫と監督・増村保造という大映の気鋭コンビが映画化した作品。学生運動で揺れるT大学歴史研究会に一人の新入生・大津彦一が加わる。リーダー・空谷の保釈運動にも彦一は積極的に参加し、メンバーの信頼を得るようになる。だが警官との乱闘で留置場に入った彼がニセ大学生であることをすでに警察側は見破っていた。やがて研究会の中にスパイ疑惑が発生し、一人だけ早く釈放された彦一がまず疑われる。猿ぐつわで椅子に縛りつけられた彼は発狂してしまう。同年の「日本の夜と霧」（大島渚監督）にも共通する、学生運動を扱った問題作。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もず", "もず", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "もとは水木洋子原作のドラマで、映画化にあたっては渋谷実が演出した。新橋の三流小料理屋の住み込み女中として働く母と、松山から上京して来た娘とが、20年ぶりに再会するが、娘は母を嫌悪する。そんな母娘の情愛を厳しく哀しく描く渋谷実の力作。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The sheltering sky", "シェルタリング・スカイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "　アカデミー賞9部門に輝いた「ラストエンペラー」のスタッフが再び集結し、サハラ砂漠を舞台に描いたヒューマンドラマ。原作は、アメリカ人作家ポール・ボウルズのベストセラー小説。1947年、ポートとキットは冷えた夫婦関係を見つめ直すため、ニューヨークから北アフリカに降り立つ。しかし、現地についた早々夫は女を買い、妻は同行するジャックとベッドを共にする。壮絶な砂漠の旅を経験するキット役は、「愛と青春の旅立ち」のデブラ・ウィンガー。", "1991-03-30", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("薔薇ホテル", "薔薇ホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "東京の商社に勤める美知代の夢は、一流のダンサーになって最高のステージで踊ること。夢実現のために男をたぶらかし、資金調達に励んでいた彼女の新たな標的は上司・伊達。美知代の度肝を抜くアプローチにたじたじだった伊達も、次第に彼女に夢中になる。だが、美知代には夫だった二人の男が謎の死を遂げた過去があり刑事が動きを追っていた。そんなある日、二人はドライブに出かけるが、途中、美知代が海に身を投げてしまう。彼女を助けた伊達は、身体を温めるために薔薇の咲き乱れる“薔薇ホテル”を訪れるが……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DARK LIGHT", "ダーク・ライト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "イングリッシュ・コーストから20マイル沖にある灯台に、一隻のボートが漂着する。乗っていた３人の男女は、銀行強盗を犯して逃亡中であった。灯台守の老人は、彼らを逃すまいとして殺され、残った二人の灯台守は３人組に加わり、再び沖へと乗り出す。が、船上で争いが起こって……。極限状態の中での人間の欲望を描く海上サスペンス。", "1951-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARTAS DEL PARQUE", "公園からの手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "キューバ映画界を代表する巨匠T・G・アレアが描く、現代版『シラノ・ド・ベルジュラック』。1913年、ハバナの東にある小さな町。空を飛ぶことに憧れるフアンと、美しい娘マリアは互いに、一目惚れし、偶然に同じ代書屋のペドロにラブレターの代筆を頼む。ペドロは、二人の愛のやりとりを書いているうちに、マリアに惚れてしまい……。", "1988-01-01", "49,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ともしび", "ともしび", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "沼に囲まれた小さな村の中学校で教鞭を執っている松熊先生。生徒たちは先生が大好きだったが、村の人々は彼の新しい教育が気にくわず、村長の銅像除幕式の席で生徒たちが笑ったのを機に先生は転任させられてしまう。そして先生は……。農村オール・ロケによる佳作で、より新しい先生像を作り上げることに成功した逸品。。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAST BREAK", "ザ・ドロッパーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ニューヨークのダウンタウンにあるレストランで働いているデビッドは、バスケットボール・クレージー。チームのコーチを夢見る彼のところにチャンスがめぐってくるが、選手はアウトサイダーの集団。彼らは全米No.1を狙って奇襲作戦を展開する……。同性愛者をはじめとする、個性豊かなチームメイトたちの友情に心温まるスポーツ・コメディ。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Quiet Earth", "クワイエット・アース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "画期的な地球規模のエネルギー電送実験フラッシュ・ライト計画。それによって人類及びすべての生物は滅び、唯一の生き残りの科学者ザックは孤独と絶望に苦しんでいた。そこへ一組の男女が現れ、奇妙な三角関係が始まるが……。珍しいニュージーランド製の近未来SF映画。", "1985-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三年身籠る", "三年身籠る", "", "", "http://ecx.images-amazon.com/images/I/51lDr%2BZGRHL._SL210_.jpg", 99, "妊娠18ヵ月！ どんどんでかくなる妻の腹を前に、だらしなかった夫も覚悟を決めた。病院で見世物になるよりはと、深夜に彼女を連れ出すが……。3年もの間、胎児を身籠り続けた母親という、奇想天外な設定に基づいたコメディ。胎児をめぐる狂騒をユーモラスにとらえる一方で、大人になりきれない大人たちの成長をまっすぐに見つめ、さわやかな感動をあたえる。これが初監督作となる実力派女優、唯野未歩子の繊細かつ大胆な語り口と、ヒロインを演じたオセロの中島知子の好演も見逃せない。", "2006-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Taxi 2", "TAXi（2）", "", "", "http://image.eiga.k-img.com/images/movie/1818/315x210.jpg?2648", 88, "スピード狂のタクシー運転手ダニエルは、恋人リリーの父親ベルティーノ将軍に気に入られ、VIP警護用特殊カー“コブラ”で日本から視察に来た防衛庁長官をパリまで送り届けるように、と依頼される。マルセイユ警察は“コブラ”の性能を長官にアピールしようと偽の襲撃隊を用意し、エミリアン刑事とペトラ刑事にその指揮を任せていたが、突如現れた謎の日本人グループに長官とペトラが連れ去られてしまう。", "2000-08-12", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("War Dance", "ウォー・ダンス　響け僕らの鼓動", "", "", "http://image.eiga.k-img.com/images/movie/53948/315x210.jpg?1289", 107, "夫婦で監督のショーン＆アンドレア・ニックス・ファインによる、ウガンダ共和国の難民キャンプに住む子どもたちを追ったドキュメンタリー。　1970年代、アミン大統領の悪政に苦しむウガンダ。反政府武装組織は子どもを拉致し、彼らを強制的に兵士や慰安婦にしていた。運よく脱走した子らは、劣悪な衛生状態にある避難民キャンプで先祖伝来の音楽を学び、混迷する時代の中で明るい未来を模索している。", "2008-11-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VESNIC｀KO MA´ STR｀EDISKOVA´", "スイート・スイート・ビレッジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "毎朝、口笛を合図に仕事に出かけるデブの運転手パベクと、のっぽの助手オチク。両親を亡くしたオチクにとって、パベクは父親のような存在で、心の支えとなっていた。だがパベクにとっては、ドジばかりするオチクは、心配と頭痛のタネだった……。チェコの小さな村を舞台に、この二人の関係を温かく、コミカルに描いた秀作。本作品は、1986年モントリオール映画祭で審査員特別賞・エキュメニック賞を受賞するなど、世界各国の映画祭で高い評価を得た。", "1985-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("結婚します", "結婚します", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "山口瞳の小説の映画化。『週刊表情』の腕きき編集者・北川浩太郎はハンサムなくせに女嫌いで結婚は見合いと決めている。７回目の見合いの後、彼は編集長から自分の見合い体験談を連載記事にするよう命ぜられ……。竹脇無我がカタブツ男を演じて笑わせる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love", "愛　サラン", "", "", "http://image.eiga.k-img.com/images/movie/54118/315x210.jpg?4290", 104, "「友へ　チング」のクァク・キョンテク監督による純愛ドラマ。7年にわたり思い続けてきた初恋相手ミジュを守るため、暴力団員を刺殺した17歳の青年イノ。この事件をきっかけにイノは行方をくらませ、ミジュは組織から追われる身となってしまう。7年後、釜山の有力者ユ会長の下で働くことになったイノは、偶然にもミジュと再会するが……。主演は「カンナさん大成功です！」のチュ・ジンモ。ヒロイン役にドラマ「マイガール」のパク・シヨン。「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。", "2008-10-04", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夕凪の街　桜の国", "夕凪の街　桜の国", "", "", "http://image.eiga.k-img.com/images/movie/34088/315x210.jpg?1422", 118, "原爆をテーマに、過去と現代に生きる2人の女性の物語を描いたこうの史代の傑作コミックを、「出口のない海」の佐々部清監督が映画化。原爆投下から13年後の広島に暮らす平野皆実は、被爆体験による深い心の傷を抱えて生きていた。それから半世紀後、皆実の弟・旭は家族に黙って広島へと向かう。娘の七波は父を心配して後を追ううちに、自分のルーツを見つめ直していく……。2人のヒロインを田中麗奈と麻生久美子が演じる。", "2007-07-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("柳生武芸帳", "柳生武芸帳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "正保４年、徳川幕府を左右する謎を秘めた柳生武芸帳は、竜造寺家の遺児・夕姫の手にあった。肥前の術者・山田浮月斎は、多三郎、千四郎兄弟を使ってこれを奪いとろうと企てる。名匠・稲垣浩がベストセラーをオーソドックスに映画化した時代劇。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEIZE THE DAY", "ミッドナイト・ニューヨーカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "トミー・ウィルヘルムは、40歳のセールスマン。仕事に見放され、父の愛は得られず、妻とも別居。すべての事実と現実が彼には絶望的だった。果たして彼はこの底知れぬ地獄から抜け出すことができるのか。S・ベローの原作をもとに1950年代のニューヨークの狂気を描く異色作。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VE´RITE´", "真実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "クルーゾー監督がB・バルドーの奔放で小悪魔的なキャラクターを余すとこなく使って、愛の真実を探った作品。パリで気ままに暮らすドミニクが、姉の恋人ジルベールと熱烈な恋に落ちる。だが、真面目で嫉妬深いジルベールは彼女に耐えられなくなり、ドミニクの姉と結婚する。本当に愛しているのはジルベールと悟ったドミニクは、彼の前で自殺しようとするが、思わずそのピストルで彼を死なせてしまう。法廷はこの愛の事件をふしだらなドミニクのせいだと裁こうとするのだが……。共演したS・フレイと実際の恋のもつれから自殺未遂事件を起したB・バルドーの演技は、鬼気迫るものがある。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風に逆らう流れ者", "風に逆らう流れ者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "野村浩次は、親友の瀬沼に会うため豊橋市にやって来る。ところが瀬沼は、すでに火薬工場の爆破事故で死亡。その裏に事件の匂いを嗅ぎつけた浩次は、塩沢火薬の社長に近づく。塩沢が、瀬沼の父が持つ造船所と島を乗っ取り、火薬の密造所にしようとしていることを知った浩次は……。ライバルの拳銃使いが神山繁では、いささか役不足の感じがするシリーズ最終作。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SNOW　DOGS", "スノー・ドッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "C・グッディングJr.のコミカルな演技が光るハートフル・コメディ。ひょんなことから、極寒のアラスカの地で犬ぞりレースに参加することになったマイアミの歯科医テッド。だが、気難しい７匹のハスキー犬はテッドを馬鹿にして全く言うことを聞かない。その日から、テッドの悪戦苦闘の日々が始まった。「ドクター・ドリトル」のアニマル・トレーナー、ステーシー・バージルの指導による犬たちの妙演が笑いを誘う。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANIAC", "マニアック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "マザーコンプレックスから、ゆがんだ性格になってしまい、次から次へと残虐な殺人を重ねていくフランク。彼の趣味はなんと殺した女性の頭皮を収集するという異常なもので……。天才サヴィーニの特殊メイクも見もののホラー映画。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE ME´PRIS", "軽蔑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "官能的なカミーユを妻に持つ劇作家ポールは、ローマ・チネチッタ撮影所で製作中のF・ラング監督（72歳の本人自演）の「オデッセイア」のシナリオを手直しするためプロデューサー、プロコシュに呼ばれる。横暴な彼に卑屈に従う夫を見てカミーユは軽蔑し、ベッドもともにしない。カプリ島ロケの帰り、カミーユはプロコシュの車に同乗し、衝突事故で二人は即死する。モラヴィアの原作をもとにした、製作費100万ドル、ゴダール初のオールスター商業大作。映画作りの現場を諧謔的に描いており、後の「パッション」と比べてみるのも一興。", "1963-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学生ロマンス 若き日", "学生ロマンス 若き日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "現存する小津安二郎の作品の中では最も古い作品。一般的に小津作品は、後期の印象からローアングル、固定画面のスタイルで広く知られているが、この「若き日」をはじめとする初期の作品群にはまったく違った小津のスタイルが散りばめられている。冒頭、主人公たちの住む早稲田の学生下宿を映しだすまで、カメラがパンにつぐパンを繰り返し、目まぐるしいまでのスピード感を生むさまを見れば、後期の小津作品しか知らないファンは目を丸くすることだろう。S大学に通う学生・渡辺の住む下宿には“２階貸間”の札が貼ってある。渡辺は、なんとか２階にきれいな女性が入って知り合いになれないものかと、やきもきする毎日。そこへ千恵子という美人がやって来た。渡辺は強引に千恵子と知り合いになるや、彼女が赤倉にスキーに行くことを聞き出し、自分も友人の山本とともにスキーに出かけることにするが……。キートン、ロイドなどのアメリカン・コメディ映画を下敷きにして、たたみかけるようなテンポで展開する爆笑ものの一編だ。", "1929-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMMON　LAW　CABIN", "カモンロウー・キャビン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "異才R・メイヤーがグラマー女優を配して描く愛憎劇。宝石を盗んだ私立探偵の一行が、アリゾナの民宿にたどり着いた。探偵は近隣の農場を買収したいと民宿の経営者に申し出るが、うまくいかない。そこで探偵は、農場に住む女性たちを次々と誘惑し……。浮気や情事によってもつれていく人間関係を、官能的な映像で捉えていく。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("藍月／BLUE　MOON", "藍月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "エドワード・ヤンらとともに取り組んだオムニバス映画「光陰的故事」でデビューし、台湾ニューウェーブの旗手と謳われたクー・イーチェン監督作品。都会で愛を求めて生きる男女３人の日常を、ユーモアを交えて切り取ったラブ・ロマンスだが、５巻のフィルムをどう繋いでも映画が成立すると監督が豪語するように、ストーリー上の時間的流れから解放された実験的な１本になっている。またブルー・トーンの透明感あふれる映像は一度見たら忘れられない美しさで、“始まりは終わり、終わりは始まりかもしれない”という監督の人生観を夢幻的に彩っている。", "1997-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若い人", "若い人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "石坂洋次郎の同名小説の２度目の映画化。北海道のミッション・スクールの若くてハンサムで女生徒からも人気のある国語教師・間崎慎太郎は、教え子で私生児の江波恵子に興味を持つが、恵子は間崎と英語教師・橋本の関係に嫉妬し、自分は間崎の子供を妊娠したと言いふらす。３人の心が揺れ動くさまをドラマティックに描く。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戀愛行星", "ティラミス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "香港の若手俳優N・ツェーが主演した「ゴースト／ニューヨークの幻」を彷彿とさせるラブ・ファンタジー。難聴の郵便配達員が恋心を抱いた女の子が事故死してしまう。やがて彼女はゴーストとして再び彼の前に現れて……。体に障害がある青年とゴーストの少女が織り成す恋愛模様が、タイトル通りにホロ苦く切ない１本だ。", "2002-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mon Meilleur Ennemi", "敵こそ、我が友　戦犯クラウス・バルビーの3つの人生", "", "", "http://image.eiga.k-img.com/images/movie/53648/315x210.jpg?4349", 90, "ナチス・ドイツ親衛隊に所属して\"リヨンの虐殺者\"の異名で恐れられたクラウス・バルビー。しかし彼はその後も戦犯として裁かれることなく、アメリカ陸軍情報部のために対ソ連のスパイ活動を行ったエージェント・バルビーとして、続いて南米ボリビアで軍事独裁政権の誕生に関わったクラウス・アルトマンとして、歴史の影で暗躍を続けた。3つの人生を生きた男の数奇な運命を検証することで戦後史の裏側を照らし出したドキュメンタリー。", "2008-07-26", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUNCHLINE", "パンチライン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "T・ハンクスがベテラン女優S・フィールドと共演した、少し辛口のコメディ作品。平凡な主婦ライラは小さい頃からのコメディアンになる夢を叶えようと、コメディ・クラブの仕事を続ける。そこの人気No.1は落ちこぼれ医大生のスティーブンで、二人はやがて愛し合うが……。T・ハンクスは、ロサンゼルス映画批評家協会主演男優賞を受賞。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マナに抱かれて", "マナに抱かれて", "", "", "http://ecx.images-amazon.com/images/I/21HQBZEPQKL._SL210_.jpg", 105, "ハワイの言葉で“超自然の力”“魂”などを意味する“マナ”。傷ついた一人の女性が、ハワイで島の人々との交流や雄大な自然に触れるうちに“マナ”の大切さに気づくまでを優しく見つめる。仕事にも男にも行き詰まった30歳の渚は、パレオの輸入を自分の力で成功させようとハワイへ飛ぶ。ひょんなことから中年男性のカイが経営するレストラン兼ホテルに滞在することになった彼女は、そこで様々な人と出会う。", "2003-06-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハッピーフライト", "ハッピーフライト", "", "", "http://image.eiga.k-img.com/images/movie/53308/315x210.jpg?3884", 103, "「ウォーターボーイズ」「スウィングガールズ」の矢口史靖監督が、旅客機にまつわる人々の悲喜こもごもをコメディタッチで描く。羽田空港から飛び立とうとしているホノルル行きチャーター便・NH1980に、機長昇格が間近の副操縦士・鈴木や教官役の機長・原田、新米CAの斉藤らが搭乗。グランドスタッフは座席のオーバーブッキングの対応に追われ、整備士たちは時間のない中で整備にあたるが、なんとか飛行機は定刻通りに離陸。あとは快適な空の旅のはずだったが……。", "2008-11-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バッドガイ・ビーチ", "バッドガイ・ビーチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "俳優の哀川翔が主演も兼ねた、監督デビュー作。ブルーな日々を送るしがない海辺の私立探偵“キッスのシロー”はある日、弟分の達也が持ってきたレイプビデオを見て驚く。そこに映っている少女は、少年の頃にビーチで危ない所を助けてやったシローの“天使”ユカなのだ。その真実を確かめるために調査に乗り出したシローは、トラブルに巻き込まれ……。私立探偵の活躍と少女との触れ合いを軽快に描いたハードボイルド・ドラマ。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OL忠臣蔵", "OL忠臣蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "アメリカで悪質な企業乗っ取り解体屋として名を馳せる朝吹季里子が次のターゲットに選んだのは、日本の中堅通販会社オレンジハウス。人のよい社長は彼女の手腕によってあっけなく退陣に追い込まれ、あきらめきった男性社員たちも次々と退社していく。そんな中、佐倉ふぶき以下数名のOLが、ある者は愛社精神によって、ある者は愛する人を守るため結集、反撃ののろしを上げる！“夜逃げ屋本舗”シリーズの原隆仁が、OLたちの活躍を軽妙なタッチで描く現代女性版忠臣蔵。それぞれがOLならではの特技を生かして巨悪に挑むという、エンターテインメントの本質を突いた演出が痛快。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALLDAYS 二丁目の朝日", "ALLDAYS 二丁目の朝日", "", "", "http://image.eiga.k-img.com/images/movie/53368/315x210.jpg?3707", 0, "赤線地帯からゲイの街へと変わりつつある昭和30年代初頭の新宿2丁目を舞台に、そこで生きる個性豊かな人々の姿を描いた人情コメディ。監督はテレビドラマ「怪奇大家族」などの異才・村上賢司。軽演劇場の役者である青年・真雄は、戦時中に出会ったアイパッチの男を忘れることができず、戦後も彼のことを探し続けていた。そんな彼に女を知ることを勧めていた座長は、おかまの八百屋・精さんと一緒にいる真雄を目撃して彼が男好きだと確信する。", "2008-02-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents 真夜中の宴　「座って！座って！」", "桃まつり presents 真夜中の宴　「座って！座って！」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 21, "", "2008-03-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OVER　THE　RAINBOW", "オーバー・ザ・レインボー", "", "", "http://ecx.images-amazon.com/images/I/51771PXP1TL._SL210_.jpg", 109, "交通事故で部分的な記憶喪失状態に陥ったお天気キャスターの青年が、恋人らしき“長い髪の女性”を捜し求めていくラブ・ストーリー。「ラスト・プレゼント」のイ・ジョンジェと「菊花の香り」のチャン・ジニョンが共演。新人監督アン・ジヌが、雨などを効果的に用いてロマンティックなムードを演出している。", "2004-11-06", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SET−UP", "罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "ボクサーのストーカーは35歳。盛りをとうに過ぎたことを知っているマネージャーのティニーは、今回の対戦相手のボスから持ちかけられた八百長の申し出を受け、本人に無断で金を受け取った。試合が始まって初めて八百長の取り決めを知ったストーカーは怒って、逆に相手をKOしてしまった。ティニーは逃げ、ストーカーは帰宅途中、相手のボス一味に襲われ、二度とリングに立てないように手を叩きつぶされた。傷だらけのストーカーは妻とともに新しい生活への決意を新たにする。アクションに加えて豊かな社会性を持った異色作。物語の進行時間と映写時間を一致させる試みも斬新。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャバレー日記", "キャバレー日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "「情事の方程式」でのデビュー以来、にっかつで何本かの注目作を演出してきた根岸吉太郎監督による、ロマンポルノとしては最後の作品。新宿・歌舞伎町のピンクキャバレーを舞台に、そこで働く男と女の物語だ。このキャバレーでは店長以下全員が、軍隊式人間管理のもとにチェーン店のトップを目指し奮闘している。そこへやって来た若者が見習いからスタートしてやっとのことで係長に昇進するものの、好きな女は課長のもとへ……。ドライなタッチを貫いた異色のキャバレー・エレジー。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABUS DE CONFIANCE", "背信", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "身寄りのない貧しい女学生リディアは、ある裕福な紳士をだまし、かつて生き別れた娘だと名乗り出る。娘になりすましたリディアは、やがて弁護士となり、自分と同じように身分を偽った罪で告訴された女性の弁護をすることに……。名女優D・ダリュー主演による人情劇の秀作。", "1937-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROAD HOUSE", "ロードハウス 孤独の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "P・スウェイジが、クールでハードでしかも本当の優しさを持った“男の中の男”を好演。アクティブでサスペンスあふれる、現代版西部劇ともいえる“用心棒物語”である。ダントンは、すべての面でNo.１と評判高いロードハウスの用心棒。彼の新しい職場“ダブル・デュース”は、酒ビンが転がり暴力沙汰が絶えない、ゴロツキどもの溜まり場だった。顔色一つ変えず、与太者たちをつまみ出すダントン。街を牛耳るブラッドは、そんな彼の存在を煙たがり……。夜空に燃えさかる炎、空手を駆使した１対１の死闘、そしてラストの壮絶な銃撃戦と、全編ド迫力の映像で、興奮度は満点といえるだろう。ビデオ発売時タイトルは「ロードハウス 誓いのカクテル」。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("END CALL", "END CALL", "", "", "http://image.eiga.k-img.com/images/movie/55788/315x210.jpg?4820", 89, "アメリカ資本で製作され、海外で人気を博したジャパニーズ・ホラー。「水霊　ミズチ」の山本清史監督がメガホンをとる。主演は「仮面ライダー」シリーズのヒロインで知られる芳賀優里亜、共演に「色即ぜねれいしょん」の臼田あさ美、「愛のむきだし」の永岡佑ら。女子高生の舞は、一目ぼれした堂島との恋を成就させるため、願いをかなえてくれる代わりに通話時間の分だけ命が短くなるという呪われた番号に電話をかける。その後、舞はめでたく堂島と付き合い始めるが、そのころから周囲で奇怪な出来事が起こり始める。", "2010-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cruising", "クルージング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "　連続殺人事件に震撼するニューヨーク。被害者はいずれも男性、そして犯行現場はゲイたちが集まる場所だった。捜査本部は若手の警官スティーブをゲイがたむろするクリストファー・ストリートに潜入させる。スティーブは恋人のナンシーにも伏せたまま、おとり捜査を遂行することに。やがて捜査線上に怪しい男が浮上するが、その一方、スティーブの心に異変が……。ハード・ゲイを題材にし、賛否両論を巻き起こした衝撃のサスペンス・スリラー。", "1980-01-01", "1,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・柴又より愛をこめて", "男はつらいよ・柴又より愛をこめて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "タコ社長の娘・あけみが嫁ぎ先から家出し、連れ戻し役に寅さんが伊豆下田へ派遣される。帰りたくないと駄々をこねるあけみの機嫌をとるため、二人は船で式根島へ渡る。ところが島の小学校に美人の先生がいて寅は一目惚れ。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Angels & Demons", "天使と悪魔", "", "", "http://image.eiga.k-img.com/images/movie/53208/315x210.jpg?2055", 138, "ダン・ブラウンの世界的ベストセラーを映画化した「ダ・ヴィンチ・コード」に続く、ハーバード大学教授ロバート・ラングドン（トム・ハンクス）を主人公としたサスペンスミステリー第2弾。爆発すれば核にも勝る驚異的な破壊力を持つ“反物質”をめぐり、秘密結社イルミナティの陰謀を阻止するため、ラングドンがローマ、バチカンを奔走する。監督は前作からの続投となるロン・ハワード。", "2009-05-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RISE UP", "RISE UP", "", "", "http://image.eiga.k-img.com/images/movie/54658/315x210.jpg?3560", 0, "パラグライダーに夢中な少年・航は、ある日、ひき逃げ事故により失明してしまった少女・ルイに出会う。初めは心を閉ざしていたルイも、パラグライダーに情熱を注ぐ航に次第に惹かれていく。しかし、2人の間には残酷な過去が隠されていた。主演は「バッテリー」「ダイブ!!」の林遣都、「MW　ムウ」の山下リオ。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Manoeuvre", "軍事演習", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FEAR", "フィアー 超能力殺人ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "少女時代から超能力を持つキャシーは、その能力と体験をもとに売れっ子作家となる。ある日、殺人事件を予知した彼女は警察に連絡、事件は防がれたかに思えた。ところが犯人もまた彼女と同じ超能力者であったことから、二人の強力なサイキック・パワーによる死闘が始まる。A・シーディが、超能力を使って正義を守る主人公キャシーを演じる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("濡れた週末", "濡れた週末", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "町工場の社長と不倫の関係にある事務員・志麻子は、若い男女のカップルと共謀し、社長の娘を誘拐して身代金をせしめようとするが……。もう若くなく婚期を逸した女の心情が、真夏の気だるい雰囲気のなかでせつせつと胸にしみる。宮下の好演が光る。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROAR", "ロアーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "動物愛護運動家でもある映画製作者マーシャルと、女優ヘドレン夫妻が、ロサンゼルス郊外の広大な自宅内で飼育する猛獣156頭と“共演”するファミリー・ムービー。悲鳴と笑いが満載の型破り猛獣チェイスが圧巻。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Spider-Man", "スパイダーマン", "", "", "http://image.eiga.k-img.com/images/movie/1178/315x210.jpg?1362", 121, "原作同様、ニューヨークの街が舞台のため、2001年9月11日の事件により、予告編とポスターの回収を余儀なくされたが、もちろん舞台は替えずに登場。スパイダーマンのコスチュームは原作とは少し色を替えている。VFXは「ハリー・ポッター」シリーズを担当するソニー・ピクチャーズ・イメージワークス。視覚効果スーパーバイザーは「スター・ウォーズ」のベテラン、近年では「スチュアート・リトル」を手がけたジョン・ダイクストラ。音楽は「バットマン」のダニー・エルフマンが担当。", "2002-05-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Adjustment Bureau", "アジャストメント", "", "", "http://image.eiga.k-img.com/images/movie/56028/315x210.jpg?3310", 106, "バレリーナのエリースに一目ぼれした政治家のデビッドは、決められた運命を逸脱しないよう世の中を監視している「アジャストメント・ビューロー（運命捜査局）」に拉致されてしまう。同局は、本来なら出会う運命にはないデビッドとエリースを引き離そうとするが、2人はその運命にあらがう。「ブレードランナー」「マイノリティ・リポート」のフィリック・K・ディックによる原作小説を、マット・デイモン主演で映画化したSFサスペンスアクション。「ボーン・アルティメイタム」の脚本家ジョージ・ノルフィが初メガホンをとる。", "2011-05-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OTHELLO", "オセロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "シェイクスピア４大悲劇の一つ。ヴェネチアの将軍でムーア人のオセロはデズデモーナの愛を得て結婚するが、敵意をもつ部下イアーゴの言葉に惑わされて妻の貞操を疑い、嫉妬に狂ってデズデモーナを殺す。長い構想のもとに完成したユトケーヴィッチの代表作。", "1955-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("傷だらけの勲章", "傷だらけの勲章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "砂漠、スフィンクス、ピラミッドそしてカイロと、灼熱と砂塵のエジプトを舞台に、一人の刑事が人間的に成長していく様を描いた本格的なポリス・アクション。エジプトで一流企業の社長が射殺された。行方不明の遺言書を巡って、遺族や関係者の間で不可解な事件が続発。刑事、都築はエジプトへ向かい、一連の事件の解明に乗り出す。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTELE FAUSS AND BIG HALSY", "お前と俺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "アリゾナ周辺を舞台に、チビとノッポのオート・レーサー二人が組んでレース場を転戦する友情物語。R・レッドフォードが個性派M・J・ポラードと共演した意欲作。砂漠を爆走するオートレース・シーンのダイナミックな迫力が見もの。レッドフォードの無責任ぶりも珍しい。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMATEUR", "愛・アマチュア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「トラスト・ミー」を観てハートリーの才能に惚れ込んだI・ユペールが、彼に出演志願の手紙を出したことから実現した作品。ポルノ冒険小説を書く元女僧と過去を失った男、二人の愛を発見する過程が、推理ドラマの展開をたどりながら描かれる。イザベルはつい最近まで尼僧だったが、今は修道院を出て、ポルノ小説を書く毎日。ある日、仕事場代わりのカフェで記憶喪失の男と出会い、いつしか愛するようになる。男の過去を捜し求めるうちに、次第に事件に巻き込まれ……。“アマチュア”という題名には、何かを発見して、それに慣れ過ぎることなく、大切に愛するという意味が込められている。", "1994-01-01", "1,3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Badge 373", "バッジ373", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "一匹狼の刑事の活躍を描いたハードボイルド・アクション。麻薬の売人を追い詰めて死なせてしまった主人公、ライアン。彼が越権行為で停職処分を受けている間に、相棒が何者かに殺される。彼は単独で捜査を始めるが今度は目前で妻を射殺され、復讐を決意する。元刑事の実体験をもとに映画化。本人がスキャロン警部の役で出演している。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASHANI SANKET", "遠い雷鳴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1942年、インドの田舎町ベンガル。カースト（身分差別）制度の厳しいこの町は戦争の影響によるひどい食料不足に悩んでいた。バラモンという最も高い身分を持つ男ガンガも食料調達に困りはて、自分の身分上は許されない肉体労働をしてまで米を手に入れようとするが……。", "1973-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一億の猫", "一億の猫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "ペットをめぐる人間模様をテーマにした「PETBOX ペットボックス」シリーズの1編で、お笑いコンビ「キャイ～ン」の天野ひろゆきがメガホンをとったコメディドラマ。著名な作家だった父の遺産をめぐり、3人の娘たちが集まった。相続の条件は、父が飼っていた黒ネコのクロを大事に世話すること。3人姉妹に弟子まで加わり、それぞれの思惑を胸に壮絶な相続争いを繰り広げる中、肝心のクロが行方不明となってしまい……。", "2006-11-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEX AND THE SINGLE GIRL", "求婚専科", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "由緒ある出版社に編集長として雇われたボブは、規定ギリギリのワイセツ雑誌にする方針にし、ベストセラー“セックスと独身女性”の著者ヘレンをケチョンケチョンにけなすことで売り上げを伸ばす。ある日彼は、ヘレンの愛を勝ち取ろうと、アパートの隣の住民になりすますのだが……。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喧嘩屋一代・どでかい奴", "喧嘩屋一代・どでかい奴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "勝新太郎の豪快なキャラクターを生かしたアクション・ドラマ。地方都市のドヤ街で、“ゴキブリ”の異名をとる青木。彼の住む街が、ヤクザ組織が絡んだ土地開発計画によってつぶされ、青木を筆頭とした住人たちは、ヤクザと対立していく……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLI AMANTI／A PLACE FOR LOVERS", "恋人たちの場所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "イタリアを舞台に、不治の病に冒されたアメリカのファッション・デザイナーとイタリアのエンジニアの絶望的な恋の激しさを描いたメロドラマ。ブルネロ・ロンディの戯曲を映画化。音楽はデ・シーカの息子マヌエル・デ・シーカ。", "1968-01-01", "4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAPPAQUA", "チャパクア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "1972年に「シッダールダ」を残し、映画界から姿を消してしまった異色の映画作家C・ルークスの伝説的な監督デビュー作。1960年代のビートニク文化の洗礼を受けたルークスの実体験をベースにした作品で、バロウズ、ギンズバーグといった代表的なビート作家が出演している。ドラッグとアルコール中毒の男ラッセル（ルークス自身が演じる）が、フランスのサナトリウムで治療を受けるというストーリーはあるものの、ドラキュラやギャング映画、ビートニク集会などを夢みる主人公の幻覚を様々な技法を凝らしてヴィジュアル化した奔放な映像世界は、まさにカルト的。映画では使用されなかったが、本作のためにオーネット・コールマンが作り上げた『チャパクア組曲』は、ジャズ史に残る名盤となった。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Little Nikita", "リトル★ニキータ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「スタンド・バイ・ミー」で人気を集めたR・フェニックスが主演するサスペンス・ドラマ。典型的なアメリカの高校生が、偶然にも両親がKGBの“スリーパー・スパイ”だったことを知り、両親への愛と祖国アメリカへの忠誠心の間で揺れ動き、次第に国際的スパイ事件に巻き込まれていくさまを描いた作品。「スティング」「普通の人々」などで知られるM・ハムリッシュによる音楽も秀逸。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フリージア", "フリージア", "", "", "http://image.eiga.k-img.com/images/movie/33838/315x210.jpg?4343", 103, "松本次郎による同名人気コミックを、「鬼畜大宴会」「アンテナ」の熊切和嘉監督が玉山鉄二主演で映画化。犯罪被害者が加害者を処刑できる“敵討ち法”が成立した近未来の日本。被害者が処刑を依頼した“執行代理人”と、加害者が雇った“警護人”との壮絶な戦いが、白昼堂々繰り広げられていた。執行代理人の青年ヒロシは、表情ひとつ変えずに黙々と処刑を実行していく。彼は過去のトラウマから、すべての感情や感覚を失ってしまっていた……。", "2007-02-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森ウルフ　零零から愛を植える", "森ウルフ　零零から愛を植える", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "", "2012-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こっくりさん　劇場版", "こっくりさん　劇場版", "", "", "http://image.eiga.k-img.com/images/movie/57338/315x210.jpg?3635", 85, "人気アイドルグループ「AKB48」の鈴木まりやが映画初出演で初主演を飾るホラー。とある山間部で、死後30年以上がたった少年の白骨遺体が「こっくりさん」の紙と一緒に発見される。一方、女子高生・絵梨の学校では昔と変わらず「こっくりさん」が流行しており、白骨遺体発見のニュースを見ていた絵梨の母・友子が翌朝、顔がミイラ化した状態で死んでしまう。霊視能力があるという担任の北村から「すでに呪いがかかっている」と言われた絵梨は、呪いを解くために北村とともに友子の故郷へ向かうが……。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いのちの作法　沢内「生命行政」を継ぐ者たち", "いのちの作法　沢内「生命行政」を継ぐ者たち", "", "", "http://image.eiga.k-img.com/images/movie/54048/315x210.jpg?3714", 107, "2005年に岩手県旧湯田町と旧沢内村の町村合併により誕生した西和賀町は、第18代村長の深沢晟雄が提唱した「生命尊重」を今でも村是としている人口約8000人の自然豊かな町。独自の保健医療政策を守り続け、地域に生きる人々にスポットを当てたドキュメンタリー。監督は「免田栄　獄中の生」の小池征人。", "2008-11-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOL", "路〈みち〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "仮出所を許された囚人５人の５日間を描く。妻のもとに帰る途中で許可書をなくし拘禁される者。不貞を犯した妻を罰するため、雪に閉ざされた山中の村へ向かう者。ゲリラ活動に身を投じる者……。政治犯として、人生の大半を刑務所で送った今は亡きギュネイが、現代トルコ社会の矛盾を鋭くえぐり出した力作。", "1982-01-01", "32,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rabid", "ラビッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "　オートバイ事故で重症を負った女性ローズは病院で皮膚の移植手術を受ける。やがて彼女の身に異変が。夜な夜な町をさ迷い歩き、わきの下から生えた突起物で抱きついた相手の血を吸っていたのだ。吸われた側にも同じ症状が現れ、被害は拡大。町は感染者であふれていくが……。「グリーンドア」で知られるハードコア女優マリリン・チェンバースを主演にすえたホラー。クローネンバーグ監督が性的なイメージを散りばめながら描く震撼の惨劇。", "1977-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浪人街", "浪人街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "日本映画の父、牧野省三の追悼60周年記念作品。マキノ雅広（この作品より雅裕から改名）によって、彼の代表作「浪人街」（最初は1928年）の４度目のリメイクがついに実現。マキノは総監修を務めた。近来稀な大チャンバラ・シーン、原田芳雄をはじめとする役者陣の好演など、見るべきところは多い。マキノの少年時代からの朋友、名カメラマン宮川一夫が、クライマックスのカメラを担当しているのも話題である。江戸の下町、食いつめ浪人が集う所。ここで夜鷹の連続殺人が起こる。犯人は、暇をもて余す旗本たち。権力の笠に隠れた旗本たちの横暴に、自分の武士としての存在を賭けた浪人４人が立ち上がる。対する旗本は120人。４対120の闘いの行方は？", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瘋狂的石頭", "クレイジー・ストーン　翡翠狂騒曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「モンゴリアン・ピンポン」で知られる中国の鬼才ニン・ハオ監督が、高価な宝石をめぐって繰り広げられる男たちの戦いを描いた痛快アクション・コメディ。四川省重慶市にある倒産寸前の工場で、高価な翡翠（ひすい）のペンダントが発見された。ペンダントを高く売るために展示会が開かれることになり、元警察官のバオが会場の警備を任される。ところが工場の周囲では、欲深い複数の男たちが翡翠の奪取を企んでいた……。", "2007-07-21", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THESE　THREE", "この三人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "L・ヘルマンの戯曲を映画化した人間ドラマ。1961年にW・ワイラーが再び同原作をもとに、「噂の二人」という題名でリメイクした。大学を卒業したカレンとマーサは、青年医師ジョーの協力を得て田舎に学校を建てる。カレンとジョーが恋に落ち、マーサも二人を見守る。だが、カレンから叱責を受けたことを逆恨みした不良娘が、マーサとジョーの不倫関係をでっち上げ、騒動を巻き起こす。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ORCA", "オルカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "船長ノランが撃ち殺したシャチの一種、オルカが死ぬ寸前に胎児を産み落とす。やがて、以前身重の妻を事故で死なせていたノランと、同じく伴侶を失ったオスのオルカとの壮絶な闘いが始まる……。オルカの悲痛な鳴き声が耳に残る、海洋パニック・ロマン。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROKEN LANCE", "折れた槍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "E・ドミトリク監督、S・トレーシー主演の、異色の社会派ウエスタン。1880年代のアメリカ西部。牧場の川上にある銅精錬所から出る鉱毒のため、家畜がバタバタと死んでいった。怒ったマットは、精錬所まで抗議に出かけるが相手にされず、ついには激しい銃撃戦となってしまう。裁判にも敗れ、投獄されてしまうマット。やがて彼の子供たちも気持ちがバラバラになってしまい、マットも死んでしまう……。古き西部が敗れ去り、新しく生まれ変わろうとする時代。そうした変動の時代に生きざるを得なかった人々の闘いをドミトリク監督は、マットと息子のジョーのなかに凝縮させている。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CURE キュア", "CURE キュア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "被害者の胸に×の字が刻まれる異様な猟奇殺人事件が続発する。いずれのケースでも犯人はあっさり捕まったが、担当刑事、高部の悩みは加害者たちに動機が見あたらないことだった。そんな折、間宮という記憶喪失の青年が捜査線上に浮かび上がるが……。恐怖映画にも造詣が深い鬼才、黒沢清が放った異色の連鎖型サイコ・サスペンス。奇怪な話術をまき散らす青年との出会いをきっかけに、平凡な市民が衝動殺人を犯していく様を、不気味なまでに淡々としたタッチで描き出す。萩原聖人扮する容疑者の、およそサイコキラーらしからぬ謎めいたキャラクターも異彩を放っているが、情緒不安定な妻を抱えた刑事の心の治療（CURE）のドラマとして観るのも興味深い。なお本作は1997年の東京国際映画祭のインターナショナル・コンペ部門に出品され、役所広司が最優秀主演男優賞を射止めた。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABY BOOM", "赤ちゃんはトップレディがお好き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "バリバリのキャリアウーマンが相続した遺産は、なんと“赤ちゃん”だった！　インテリ女性と赤ちゃんが巻き起こす騒動を描いたコメディで、D・キートンのコミカルな演技が光る。また彼女が着こなすDCブランド・ファッションが注目された。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOCTURNE INDIEN", "インド夜想曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "友人の行方を捜しにインドヘとやって来たロシニョル。彼は友人が病気にかかっていたこと、神智学協会とかかわりがあったことを突きとめ、協会に赴く。協会には“私は夜鳴く鳥”という、友人からの謎の手紙が残されていて……。ボンベイ、マドラス、ゴアとインド各地をさすらう男の、魂の紀行を描いた作品。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HENRY V", "ヘンリィ五世", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "15世紀。いわゆる百年戦争といわれたイギリスとフランスの長い戦争の末期を舞台に、ヘンリー五世の活躍と、フランスの王女キャサリンとの結婚で和義が成立するまでをスペクタクルに、そしてユーモラスに描く。名優オリヴィエのシェイクスピアものの第１作。", "1945-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHTMARE CONCERT", "ナイトメア・コンサート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "スプラッタ映画の代表選手、イタリアのL・フルチ監督。その彼が主演し、しかもフルチ自身を演じた異色作。映画製作に没頭するあまり、映画と現実の見分けがつかなくなってしまったフルチが、ついには自作さながらの残虐行為に走るというユニークな展開がみられる。", "1990-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Misma Luna", "おなじ月の下で", "", "", "http://image.eiga.k-img.com/images/movie/54058/315x210.jpg?3269", 106, "第5回スペイン・ラテンアメリカ映画祭上映作品。メキシコからアメリカへと渡り、不法労働で家計を支える女性ロサリオは、毎週日曜にロサンゼルスの公衆電話から息子カリートに電話をかける。そんな生活が4年続き、9歳になったカリートは母に会うため、国境を越える決意をする。警察の取り締まりや不法入国者を狙う犯罪組織などの危機を、アメリカ社会に生きるメキシコ人たちの助けや偶然の旅仲間エンリケと乗り越え、カリートは母がいるはずの公衆電話を目指す。", "2008-09-12", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRY THE BELOVED COUNTRY", "輝きの大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「サラフィナ！」のD・J・ルートが手掛けた社会派ドラマ。皮肉な運命に導かれて出会った黒人と白人の葛藤を描く。黒人の牧師のクマロは音信不通の息子を捜して都会へ。そんな折、白人の社会福祉家が殺害された。なんと、犯人の一人はクマロの息子。しかも、黒人に偏見を抱く被害者の父ジャービスはクマロと同郷だった……。", "1995-01-01", "1,67", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNA PISTOLA PER RINGO", "夕陽の用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "G・ジェンマを一躍スターダムに押し上げた、記念すべきマカロニ・ウエスタンの傑作。極悪非道の野盗一味を撲滅するため、早射ちリンゴはサンチェス一味にもぐり込み、壮絶な戦いの末に首領サンチェスをも倒したのだった……。", "1965-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fine Windy Ddays", "風吹く良き日", "", "", "http://image.eiga.k-img.com/images/movie/43338/315x210.jpg?4625", 113, "地方からソウルに出てきたトッペ、チュンシク、キルナムは、互いを励ましあい、反発しあいながら生活していた。3人は人生に生きがいも希望ももてずにいたが、それぞれに思いを寄せる相手がいるため街を離れられない。そんなある日、チュンシクが傷害事件を起こしてしまい……。民主化運動が高まる1980年代、韓国ニューウェーブの第一人者イ・ジャンホ監督が、当時の若者たちのリアルな現実を描いたドラマ。主演は韓国の国民的俳優アン・ソンギ。日本では2011年「韓国ニュー・ウェーブ、再発見」にて劇場初公開。", "2011-06-18", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("烈火戦車２　極速伝説／THE　LEGEND　OF　SPEED", "超速伝説／ミッドナイト・チェイサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "公道レースに生きる飛ばし屋の青春を描いたアクション。町いちばんの公道レーサー、スカイは因縁のライバルのソンカイとの勝負に敗れ、恋人を死なせてしまった。失意の中、死んだと思っていた父が異国タイにいることが判明。スゴ腕のレーサーだった父を訪ねて、スカイはタイに渡り走ることへの情熱を取り戻していく。CGを駆使したクライマックスの猛スピード・バトルは圧巻。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うた魂♪", "うた魂♪", "", "", "http://image.eiga.k-img.com/images/movie/53378/315x210.jpg?2308", 120, "北海道のとある町。高校の合唱部に所属するかすみは、歌っている自分が大好きで、ルックスにも才能にも自信満々。しかし、憧れの生徒会長に、歌っている時の顔を“産卵中の鮭みたい”と言われて自信喪失。そんな彼女の前に、合唱をこよなく愛するヤンキーの権藤が現れて……。函館イルミナシオン映画祭第8回シナリオ大賞を受賞した脚本を夏帆主演で映画化。ヤンキー高校生・権藤をお笑い芸人のゴリ（ガレッジセール）が演じる。", "2008-04-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("When in Rome", "みんな私に恋をする", "", "", "http://ecx.images-amazon.com/images/I/519KXwh60xL._SL210_.jpg", 91, "クリステン・ベル＆ジョシュ・デュアメル共演によるラブコメディ。野心的なニューヨーカーのベス（ベル）は、ある日突然、年上のボーイフレンドにふられてしまう。傷心のベスだったが、妹の結婚式に出席するため、イタリア・ローマへ行くと、TVレポーターのニック（デュアメル）と出会い、恋に落ちる。監督は「デアデビル」「ゴーストライダー」のマーク・スティーブン・ジョンソン。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUBLE SUSPICION", "サスペクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "猟奇殺人事件の真相に迫るサイコ・スリラー。連続殺人犯をとり逃がし、刑事の職をあきらめていたドワイトは、大学の部室で女性コーチの死体が発見されたのをきっかけに復職。今度の事件が、以前追っていた連続殺人犯の手口と酷似していることを知り、再調査にかかる。被害者は全員未婚女性。暴行の形跡はなく、首をひと突きにされている。犠牲者ははっきりした意識を保ちつつ、呼吸困難で苦しみながら死んでいく。その姿を見て快楽をむさぼる、きわめて異常性の高い犯人像が浮かび上がってくる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGEL IS SEDUCING THE DEVIL", "誘惑の天使　—娼婦の館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1930年代の東欧のクラブを舞台にした愛と裏切りのミュージカル・ドラマ。エンジェルの名で親しまれるレナータはクラブ“リビエラ”の歌姫。彼女は銀行家と結婚しステージを去ってしまう。しかし、クラブのマダムは彼女をステージに戻すために罠を仕組みはじめる……。", "1987-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("体育館ベイビー", "体育館ベイビー", "", "", "http://image.eiga.k-img.com/images/movie/53598/315x210.jpg?1370", 0, "高校の競泳部でエースを務める潤は、インターハイ予選でライバルの村井に敗れてしまう。村井からコーチを頼まれ引き受ける潤だったが、やがて受験勉強に専念することを決意。それを聞いた村井は自分も競泳部をやめると言い出す。村井が待つプールへと駆けつけた潤は、突然彼にキスされ……。“JUNON恋愛小説大賞”受賞作が原作の青春ドラマ「同級生」の公開にあわせて制作された、同じ主人公によるボーイズラブ・ストーリー。", "2008-05-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECRET PLACE", "少年が知っている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "英国の俊才、ドナー監督の処女作。若い男女が生活資金獲得のため、宝石泥棒を計画。だが、仲間に幼い少年を加えたことで、犯行後、彼らは意外な運命をたどる……。英国映画の伝統であるドキュメンタリー・タッチを巧みに生かしたサスペンス映画の秀作。", "1956-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DES FEMMES DISPARAISSENT", "殺られる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ピエールはマルセイユの労働者。彼がある日家へ帰ると、フィアンセのベアトリスがどこかへ出ていったあとだった。今日は縫い子仲間のコラリーヌの結婚を祝って、みんなは裁縫屋へ集まっているはずだった。ベアトリスが出てくるのを裁縫屋の入口で待っているピエールを数人の男たちが襲った。彼らの魂胆は、パーティーだと言って若い娘を集め、彼女らの処女を奪い、海外へと売り飛ばそうというものだった。ピエールは、ベアトリスを連れ戻すために、彼ら暗黒街の男たちに敢然と立ち向かっていく。ジャズ・メッセンジャーズの醸し出すけだるい音楽と、「居酒屋」の名カメラマン、ジュイヤールのシャープな映像が見事に結実した、フィルム・ノワールの傑作。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Star Wars: Episode V - The Empire Strikes Back", "スター・ウォーズ　帝国の逆襲", "", "", "http://ecx.images-amazon.com/images/I/51M-yE3N9JL._SL210_.jpg", 124, "大ヒットSFシリーズ「スター・ウォーズ」の第2作。前作で帝国軍が反乱軍に敗れてから3年。今度は帝国軍が猛反撃を開始し、反乱軍は窮地に立たされる。そんな中、ルークはフォースの修行を積むため、惑星ダゴバにいるというジェダイ・マスター、ヨーダの元を訪れる。一方、ハン・ソロとレイア姫は雲の惑星ベスピンへと逃れるが、ソロのかつての仲間ランドの裏切りによってダース・ベイダーに捕らえられてしまう。後に製作された新3部作とあわせたシリーズ6部作の第5部（エピソード5）にあたる。", "1980-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HURLYBURLY", "キャスティング・ディレクター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "1984年以来、何度も上演されてきた舞台『Hurlyburly』の映画化。キャスティング・ディレクターというハリウッドの花形職業に就きながら不器用に生きるアウトサイダーたちの群像劇で、混乱したうんざりするほどの苦悩、そして救済とは一体何なのかを派手なアクションなしに言葉を駆使して描く。同役で舞台出演経験者でもあるS・ペン、K・スペイシーの演技対決もさることながら、M・ライアンが初の汚れ役を演じているのも話題となった。ちなみに原題の『Hurlyburly』は、暴動、混乱、大きな騒音や不快音、群集シーンの意味。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("New York, New York", "ニューヨーク・ニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 163, "アクの強いサックス奏者ジミーと、やがて売れっ子となる歌手フランソワーズ。二人は激しく惹かれ合いながらも別々の道を歩み出す。ラストのL・ミネリの一大ショーが圧巻だ。スコセッシ監督が1940年代のニューヨークのムードを見事に活写。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Field of dreams", "フィールド・オブ・ドリームス", "", "", "http://ecx.images-amazon.com/images/I/51NOW-XXVJL._SL210_.jpg", 107, "　監督第2作目のF・A・ロビンソンが、6年の歳月をかけて製作にこぎつけた秀作。レイ役のケビン・コスナーにとっては「さよならゲーム」に続く野球作品への出演。広大なトウモロコシ畑を営むレイは、ささやかながら幸せな日々を送っていた。ある春の日の夕暮れ、彼の人生を一変させる“声”を聞く。「畑をつぶして野球場を作れば、彼が帰ってくる……」オスカー俳優バート・ランカスターが、実在の大リーガー役を演じる。", "1990-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITY OF JOY", "シティ・オブ・ジョイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "東洋のパリと呼ばれながら裏を返せば数十万人のもの人が路上での生活を余儀なくされているカルカッタ。その中でも“歓喜の街（シティ・オブ・ジョイ）”と呼ばれるスラムに、人生に絶望したアメリカ人医師がやって来た。極限状況の中で、彼は人生の意義を再発見していく……。長期ロケを敢行した、R・ジョフィ入魂の感動ドラマ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD GIRLS", "バッド・ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1890年代の西部。軍人を殺したかどで縛り首を宣告されたサロンのマダムが３人の娼婦に助けられる。それぞれ不幸な生い立ちの４人は、男顔負けのガンさばきで、西部の荒野に自らの人生を切り開いていく……。M・ストウ、D・バリモアら人気女優４人のガンマンぶりが楽しめる異色痛快ウエスタン。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレヨンしんちゃん　電撃！ブタのヒヅメ大作戦", "クレヨンしんちゃん　電撃！ブタのヒヅメ大作戦", "", "", "http://ecx.images-amazon.com/images/I/61Ca3ZBgq4L._SL210_.jpg", 99, "スーパー幼稚園児・野原しんのすけが迷活躍を繰り広げる劇場版アニメのシリーズ第６作。電子工学博士をだまし、恐ろしいコンピュータ・ウイルスを作り出した悪の組織“ブタのヒヅメ”。それを知った正義の秘密組織SMLの一員“お色気”は機械のパスワードを盗み出し、お台場の屋形船に逃げ込むが、そこにはしんのすけとふたば幼稚園の先生・子供たちがいた。そして、しんのすけやネネちゃんら仲間たちは屋形船ごと飛行船に連れ去られてしまう……。", "1998-04-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("21", "ラスベガスをぶっつぶせ", "", "", "http://image.eiga.k-img.com/images/movie/53198/315x210.jpg?4245", 122, "マサチューセッツ工科大学に通うベンは、天才的な数学の能力を認められてローザ教授の研究チームに招かれる。彼らの目的は、卓越した頭脳を駆使してブラックジャックの必勝法を習得すること。教授の下で特訓を積んだメンバーたちは、ついにラスベガスに挑み……。主演を務めるのは全米期待の若手俳優ジム・スタージェス。ケビン・スペイシー製作の下、「キューティ・ブロンド」のロバート・ルケティック監督がメガホンを取る。", "2008-05-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOMETIMES A GREAT NOTION", "わが緑の大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "オレゴンで森林伐採業を続けてきたタンパー一家は組合員との交渉が折り合わず、ストに入った同業者と対立。やむなく一家の力だけで材木運搬を決行するが、事故で家長ヘンリーが命を落とす。組合員のいやがらせのなか、タンパーの一家は仕事を遂げることができるか……。P・ニューマンの監督第２作。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Trouble in Mind", "トラブル・イン・マインド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "　刑務所帰りの元・刑事ホークが街に戻ってくる。同じ頃、若い夫婦クープとジョージアが赤ん坊を連れて街にやってきた。ジョージアがホークの元・恋人ワンダのカフェで働き始める一方、クープは犯罪に手を染め、次第に変貌していく。悲しみに暮れるジョージアはホークと心を通わせていくが……。インディペンデント作家アラン・ルドルフ監督が大都会で繰り広げられる人間模様をとおして、男女の愛と孤独をロマンティックに描く。フィルム・ノワール調ながら、独特のユーモアも魅力的。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武闘派仁義 全面抗争篇", "武闘派仁義 全面抗争篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "経済ヤクザに堕ちた組長を殺害し“親殺し”となった武士は、ドヤ街に身を隠し、肉体労働者として生きていた。ところが、それを知った地元の鎌田組がドヤ街の住民立ち退き作業に武士を誘う。ヤクザ世界復帰という甘いエサをちらつかせながら……。任侠シリーズ第２弾。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KVINNORS VA¨NTAN", "シークレット・オブ・ウーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "I・ベルイマンの最初のコメディ。ストックホルム郊外の小島にある別荘を舞台に、夫の帰りを待っている４人の義理の姉妹がそれぞれの結婚や情事を語り合う。ラーケル、マッタ、カーリンの３人の回想シーンを軸にした映像は、リアリズムから幻想的な表現まで幅広く豊かだ。記念パーティーのあと、カーリンとフレードリックがエレベーターに閉じ込められるシーンは、公開時からすでに“映画史に残る名場面”と評判を呼び有名。このシーンはベルイマンの希望で、エレベーターのドアのアップから二人が眠ってしまうまでの15分26秒をほとんどカットなしで撮影している。", "1952-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ME´FIEZ−VOUS FILLETTES", "目撃者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "チェイスの犯罪小説『キャラハン嬢とんだ御災難』を原作に、暗黒街に渦巻く売春組織の内幕を鋭く描いた作品。５年の刑期を終えたラバンは、自分を密告した売春組織の顔役を射殺し、キャバレーに乗り込んで新しいボスになる。だがラバンを快く思わないキャバレーの主人は、彼を押さえつける切り札として事件の唯一の目撃者をホテルに監禁したが……。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREMLINS ２ ： THE NEW BATCH", "グレムリン2 —新・種・誕・生—", "", "", "http://ecx.images-amazon.com/images/I/51vl8o1N8qL._SL210_.jpg", 108, "製作総指揮のスピルバーグや監督のダンテなど、第１作を手掛けたオリジナル・メンバーが集結、ギャグとパロディを満載し、前作よりよりコメディ色を強くしたヒット作の続編。今回は最先端のコンピュータが管理する巨大ハイテクビルが、グレムリン・パニックに巻き込まれていく。陽気で不気味、喜劇的で気まぐれ、手のつけられなくなったグレムリンが大量発生。バイオテクノロジーと融合して、よりパワーアップした新種グレムリンの暴れ方がすさまじい。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニライの丘 A Song of Gondola", "ニライの丘 A Song of Gondola", "", "", "http://image.eiga.k-img.com/images/movie/55588/315x210.jpg?4502", 99, "沖縄芝居「丘の一本松」をモチーフに、沖縄・北谷町と吉本興業が協賛して製作した家族ドラマ。幼いころに母親を亡くした良は、寡黙な父・良造と病弱な妹・風子と慎ましく暮らしていた。ある日、父親に内緒で空手道場に通い始めた良は、アメリカ人の少年ケビンと出会い、国境を越えた友情を深めていく。しかし、ケビンが起こしたある事件をきっかけに、良は父親がずっと隠してきた秘密の過去を知ってしまう。", "2010-07-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二人が喋ってる。", "二人が喋ってる。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "女性漫才コンビ、トゥナイトを主演に、漫才師の道を選んだ二人の女性を、過去と現在を交錯させて描く。高校卒業直前、一緒に漫才をやろうと言い出すシズカと、進路に迷うナルミ。「やろう」「やらない」で対立を繰り返すハイテンションでスピーディーな二人の会話は、まさに漫才そのもの。大阪の街で展開する絶妙の掛け合いの中に、友情や恋愛、将来への不安で揺れ動く女性の生の声もすくい取る。ドキュメンタリーのようなリアリティーを感じさせる構成もユニークだ。1996年、サンダンス・フィルム・フェスティバルの東京グランプリと日本映画監督協会新人賞を獲得。DVDは「犬童一心作品集　金魚の一生」と併録。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE CAESER", "犯罪王リコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "田舎で強盗を働いていたイタリア系のならず者リコは、相棒のジョーとともに大都会へ出て“暗黒街の帝王”を目指す。射撃の腕と度胸を見込まれ、着々とその夢を実現させていくリコだが、同時に警察も彼の逮捕に全力を上げていた……。名優E・G・ロビンソンの出世作ともなった、ギャング映画の快作。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tears of April", "4月の涙", "", "", "http://image.eiga.k-img.com/images/movie/55808/315x210.jpg?2358", 114, "フィンランド国内で記録的なヒット作を連発するアク・ロウヒミエス監督が、20世紀初頭のフィンランドを舞台に、過酷な運命に翻ろうされる男女の姿を描く。1918年の内線末期、約2000人の女性兵士が敵軍に暴行を受け、逃亡を図った女性兵ミーナは敵兵アーロに拘束されてしまう。アーロは適正な裁きの場に彼女を連れて行こうとするが、ミーナの抵抗により海で遭難してしまう。そしてたどり着いた島で暮らし始めた2人は、やがて愛しあうようになる。", "2011-05-07", "20,7,27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK　CAT　RUN", "ブラック・ラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「グリーンマイル」の監督F・ダラボンが製作・脚本を手掛けた脱獄アクション。真夏のオクラホマ刑務所、冷酷非道な囚人ウィーラーは警官を次々と殺して車を奪い、脱獄に成功。逃走の途中に若い女性を人質に取って彼は車を走らせるが、彼女を助けるために一人の男が決死の追跡を始める……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Juha", "白い花びら", "", "", "http://image.eiga.k-img.com/images/movie/1828/315x210.jpg?4755", 78, "フィンランドの小さな村でキャベツを作る幸せな夫婦、ユハとマルヤ。ある日、村にシェメイッカと名乗る男が車で通 りかかる。車が故障したという男は畑にいたユハに助けを求め、ユハは快く車の修理を引き受ける。しかしシェメイッカは隙を見てはマルヤを誘惑、マルヤも彼を強く意識するようになる。再びシェメイッカが2人を訪れ、マルヤはシェメイッカと駆け落ちするが、2人が結ばれたあとシェメイッカの態度は豹変する。", "2000-06-24", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレヨンしんちゃん 嵐を呼ぶ　　モーレツ！オトナ帝国の逆襲", "クレヨンしんちゃん 嵐を呼ぶ　　モーレツ！オトナ帝国の逆襲", "", "", "http://ecx.images-amazon.com/images/I/61bHSjynFeL._SL210_.jpg", 89, "テレビでもおなじみの人気アニメの劇場版シリーズの第９作。しんのすけの住む春日部で突然開かれた20世紀博。大人たちは昔を懐かしんで入り浸り、しんのすけの両親も会社を休み、家事をサボって夢中に遊んでいた。しかし、それは“ケンちゃんチャコちゃん”をリーダーとする組織の罠であり、大人だけの楽しい世界を作って時間を止めてしまおうという“オトナ帝国”化計画だった。やがて、大人たちは20世紀博から帰ってこなくなってしまう。しんのすけは幼稚園の仲間たちとともに、20世紀博に乗り込んでいくが……。", "2001-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Too Young to Die?", "トゥルー・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "実生活でも話題を呼んだB・ピットとJ・ルイスが共演したバイオレンス・ラブ・ストーリー。養父にレイプされた15歳の少女アマンダは、逃げるように18歳のボーイフレンドと結婚するがすぐに破局。ストリップ、売春、ドラッグが渦巻く廃退した街に転落した彼女は、狂暴だがピュアなハートを持つ青年ビリーと出会い、再び恋に落ちるのだった……。TVムービー作品。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROCODILE DUNDEE II", "クロコダイル・ダンディー２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ニューヨークにオーストラリアからやって来たミック・ダンディーは、美人記者スーと同棲中。その彼女の前の亭主が、南米コロンビアで、麻薬組織のルイス・リコの決定的現場をカメラに収めたため殺された。写真をスーに送ったことから、ニューヨークに来たリコは、彼女を誘拐。ダンディーは得意の戦術で彼女を助け出すが、マンションに戻ったところを襲われたため、二人はダンディーの故郷オーストラリアに姿を隠した。だがリコもオーストラリアまで追ってきた。かくしてダンディーとリコの一大対決。大ヒット・シリーズの２作目で、相変わらずダンディーの一挙一動は笑いを誘う。サスペンスもミックスした快調編。", "1988-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　CONVENT", "ザ・コンヴェント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "40年前の聖フランシス修道院、一人の女子高校生クリスティーンが尼僧たちを皆殺しにした。それ以来、閉鎖された修道院は廃墟となっていた。ある夜、男女７人の大学生が肝試しゲームのために足を踏み入れる。だが、その中の一人の女が悪魔崇拝者たちに捕らえられて儀式が行われたとき、かつてクリスティーンが封印したゾンビたちが息を吹き返す。次々と惨殺される大学生たち。唯一生き残ったクラリッサにも魔の手が……。残虐シーンの数々を盛り込んで作られたB級スプラッタ・アクション・ホラー。ラスト、修道院でショットガン片手にゾンビ軍団と戦う熟女クリスティーンを演じたのは、元ジョン・カーペンター夫人のA・バーボー。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("光の雨", "光の雨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "連合赤軍の集団リンチ致死事件を描いた小説が映画化されることになり、若い役者たちが集結していく。しかし、彼らは当時事件を起こした青年たちの心理が理解できず、戸惑いを隠しきれない。そんな中、赤軍と同じ時代に青春期を過ごしていた監督が“消えます。映画のために”と書き置きを残して失踪してしまい、それまでメイキングを回していた新進監督が後を引き継ぐことになるのだが……。学生運動のキャリアを持つ高橋伴明監督が、己の内に秘めた想いを映像に叩きつけた青春群像劇。立松和平の同名小説が劇中劇として描かれ、現代の若者たちの視点から連合赤軍が捉えられることにより、理想を追い求めていった果てが殺りくであったという当時の青春、その痛恨の惨劇が時代を超越して観る者の胸を突く傑作に仕上がっている。現代と当時の若者を二役で演じる若手俳優たちが、それぞれ忘れられない鮮烈な印象を残す。ビデオタイトルは「光の雨・連合赤軍事件」。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("進撃の巨人", "進撃の巨人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「このマンガがすごい！」2011年版のオトコ編で1位となった諫山創の人気コミックを、「告白」の中島哲也監督が実写映画化。最大50メートルの謎のヒト型怪物「巨人」たちが支配する世界を舞台に、巨大な防護壁の内側で生活する人類と、壁を越え侵略してきた大巨人たちとの壮絶な戦いを描く。", "2013-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Three Musketeers", "三銃士　王妃の首飾りとダ・ヴィンチの飛行船", "", "", "http://image.eiga.k-img.com/images/movie/55968/315x210.jpg?2522", 110, "アレクサンドル・デュマの冒険活劇「三銃士」を、「バイオハザード」のポール・W・S・アンダーソン監督が映画化した3Dアクション。17世紀フランス。パリにやってきた気が強く無鉄砲な青年ダルタニアンは成り行きで三銃士の仲間となり、国王の側近の裏切りによって狙われた王妃の首飾りを取り戻すためイギリスへ。しかし、そんな彼らの前に事件のカギを握るバッキンガム公爵と正体不明のナゾの美女ミレディが立ちはだかる。主人公ダルタニアン役に「パーシー・ジャクソンとオリンポスの神々」のローガン・ラーマン。バッキンガム公爵を演じるオーランド・ブルームが自身初の悪役に挑む。", "2011-10-28", "2,1,3,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIDEAWAY", "ハイダウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "交通事故に遭い、死の淵から奇跡的な生還を遂げたハッチは、自分が見知らぬ少女を刺し殺すという奇妙な白昼夢に悩まされ続けていた。一方、ハッチの娘レジーナは友人と出かけたクラブでバサーゴという少年に出会う。どこか異様な雰囲気を漂わせた彼は、レジーナに再会を約束したあと、別の少女を誘い殺害する。そしてその光景はハッチの夢の中に現れていた。ハッチは自らのビジョンの意味を知るため、透視能力を持つ女性ローズに助言を求め、レジーナが危ないと教えられるのだが……。モダンホラーの奇才D・R・クーンツの原作を映画化。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("意外", "アクシデント", "", "", "http://image.eiga.k-img.com/images/movie/55038/315x210.jpg?3062", 87, "「エレクション」のジョニー・トー製作の下、「ドッグ・バイト・ドッグ」のソイ・チェンがメガホンを取った香港のクライム・サスペンス。3人の仲間たちと共に綿密な計画を立て、完璧な事故にみせかけて標的を殺害してきたプロの殺し屋・大脳。ところがある日、仲間の1人が事故死を遂げたことから、大脳は今度は自分たちが殺害の標的にされているのではないかと疑いはじめる。主演は「プロジェクトBB」のルイス・クー。2009年東京フィルメックスコンペティション部門にて上映。11年劇場初公開。", "2011-10-08", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ОКРАИНА", "国境の町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "戦後、ゴダールやジャック・リヴェットらによって再評価され、映画史にその名を刻んだ名匠B・バルネットの傑作。ロシアの片田舎の平和な町に“ドイツが戦争を仕掛けてきた”との報が届く。靴工場のオヤジや商人は軍靴を供給しようと愛国心をあおるが、男手を兵隊に取られる女たちはそれどころではない。そんな混乱のさなか、捕虜になった若いドイツ兵が町に送られてきて……。国境の人々を襲う戦争の悲劇とプロレタリア革命の勝利を描く群像ドラマ。庶民感情をすくい取ったバルネットの躍動感あふれる喜劇センスが、時代がかったプロパガンダ的な内容を吹き飛ばして痛快だ。", "1933-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サッド　ヴァケイション", "サッド　ヴァケイション", "", "", "http://image.eiga.k-img.com/images/movie/34098/315x210.jpg?1434", 136, "中国からの密航者を手引きする健次は、父親を亡くした少年アチュンを引き取ることに。職業を変え、アチュンや幼馴染の男の妹ユリと家族のような共同生活を送っていたある日、健次はかつて自分を捨てた母親・千代子に再会する。捨てられた恨みを果たすため、母と共に暮らし始める健次だったが……。青山真治が自身の代表作「Helpless」「EUREKA　ユリイカ」に続く“北九州サーガ”の集大成として作り上げた1作。前2作と共通する人物も多数登場。", "2007-09-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("臍帯", "臍帯", "", "", "http://image.eiga.k-img.com/images/movie/56758/315x210.jpg?4091", 108, "", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三等重役", "三等重役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "地方都市の有力会社・南海産業の桑原社長は、社長復帰が予定されていた前社長・奈良剛造が脳溢血のため、当分復帰が困難となり、ホッと胸をなで下ろす。一方、桑原社長夫人は社長夫人でいる間に新調の裾模様を着たいばっかりに社内結婚の奨励を命じる……。1956年の「へそくり社長」に始まる東宝の“社長”シリーズの原型となった作品で、敗戦後占領軍が行なった公職追放によって臨時社長に昇格した、いわゆる“三等重役”を主人公にした、時代を感じさせる題材である。脇役として長いキャリアを誇る河村黎吉が、小心翼々としていながら憎めない“三等重役”桑原社長を絶妙に演じ、1952年度毎日映画コンクールの演技特別賞を受賞した。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高校生番長・棒立てあそび", "高校生番長・棒立てあそび", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "10代の性に対する葛藤を、明るいタッチで描いた“高校生番長”ものの一編。都南高校応援団の大作は、夏合宿で行なった全裸の特訓がキッカケで、光代を意識し始める。合宿も終わり、やがて彼女をめぐって男たちの争いが始まる……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPECIES　III", "スピーシーズ３／禁断の種", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "異星の遺伝子配合によって誕生し、人間との交配で種を残そうとするエイリアンの暴走を描いた人気SFシリーズの第３作。シリーズの持ち味であるエロスとバイオレンスを交錯させつつ、スリリングなアクションを展開させる。危険なヒロインを演じる新星サニー・メイブリーの妖艶な魅力に注目。異星人純血種の血を引き、人間の女性の姿で成長した未知生命体サラ。彼女を混合種の魔の手から守るため、若き科学者が奔走していく。", "2005-06-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緋牡丹博徒・鉄火場列伝", "緋牡丹博徒・鉄火場列伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "明治中頃の四国・徳島。百姓たちは地主である旦那衆にしいたげられて、小作争議を起こすが、旦那衆の命をうけた鳴門川一家は、悪どい仕打ちに出る。ついにお竜の怒りは爆発し、阿波踊りに興ずる鳴門川を倒す。のちに「仁義なき戦い」の脚本を務める笠原和夫が参加している。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("老人与狗／AN OLD MAN AND　HIS DOG", "犬と女と刑〈シン〉老人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「芙蓉鎮」「乳泉村の子」のヒットにより日本でも高名なシェ・チン監督が、チャン・シエンリアンの小説『刑老人と犬の物語』を映画化した渾身の一作。中国の文化大革命時代、貧しい農村に愛犬とともに生きる刑老人と、富農階級の出身であるため物乞いすら許されない女、そして政府の方針により、人間の食料を脅かすとして虐待されていった犬たちのたどる数奇な運命を描く。彼らの一挙一動を淡々と撮ることによって文革時代の政治的緊張と、体制の矛盾をリアルに浮かび上がらせている。", "1993-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A｀ CHACUN SON ENFER", "愛の地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "９歳の少女が何者かに誘拐される。母親の必死の訴えも空しく、少女は遺体となって戻ってきた。事件は一応解決したかのように見えたが、実は意外な真相が誘拐事件には隠されていた。社会派A・カイヤットの力作で、複雑な人間関係を鋭く描いている。ジラルドの熱演も見もの。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後の恋，初めての恋", "最後の恋，初めての恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "オール上海ロケで描く日中合作のラブ・ストーリー。人生に絶望した日本人男性と美しい中国人姉妹。３人が織り成す切ない愛の物語を、「うつつ」の当摩寿史が魅惑的な映像で紡ぎ上げる。恋によって人生を再出発させる男に渡部篤郎、彼の心を動かす対照的な美人姉妹を「スパイシー・ラブスープ」のシュー・ジンレイと「至福のとき」のドン・ジェが好演している。深い傷を負った早瀬が、赴任先の上海でミンとリンの中国人姉妹と知り合う。早瀬は明るく積極的な女子大生のリンに好意を寄せられるが、彼の心を動かしたのは物静かな姉のミンだった。だが、ミンはある秘密を抱えていた……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴンドラ", "ゴンドラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "無機質で騒々しい都会の片隅で出会った高層ビルの窓ふきをしている青年と、母と二人きりで暮らす自閉症気味の少女とが静かに、温かな心の交流を繰り広げていく様子をメルヘン・タッチで綴る。学生時代から映画監督を志していたという伊藤智生のデビュー作。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HOUSE OF WAX", "肉の蝋人形", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "1933年の同名映画を、A・D・トス監督のもと3Dでリメイク。火災や追跡シーンなど迫力に満ちた立体映像を散りばめながら、狂気に陥った彫刻家がもたらす恐怖の世界を描いている。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北斎漫画", "北斎漫画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "絵師・葛飾北斎と友人の戯作者・滝沢馬琴の二人の交遊を長い年月にわたって追い、北斎の娘・お栄、馬琴の女房・お百、魔性の美女・お直の存在を絡めて描いた作品。15歳から70歳までを演じた田中裕子と、35歳から90歳までを演じる緒形拳が、ともに熱演。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・刑事〈でか〉まつり／一発大逆転", "新・刑事〈でか〉まつり／一発大逆転", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "今回はお馴染みのルールに、映画監督をキャスティングするという新規定を追加した大好評のオムニバス“刑事まつり”シリーズ第４作。登板するのは「ヴァイブレータ」の大森南朋、「あずみ」の榊英雄、「模倣犯」の津田寛治、「き・れ・い？」の中村麻美など、それぞれが様々なメディアで活躍中の個性派俳優。彼らが見せる演出に注目。", "2003-05-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOMMY", "Tommy　トミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "イギリスのロックバンド“ザ・フー”のP・タウンゼントが創作したロック・オペラの映画化。視覚、聴覚、言語機能を失ったトミーの数奇な人生と、彼を取り巻く人間たちのアブノーマルな行動を、エキセントリックな映像感覚で描く。音楽映画の歴史を変えた異端児ラッセルの華麗な世界が展開される。", "1975-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNA MAGNUM SPECIALE PER TONYSAITTA／BLAZING MAGNUM", "ビッグ・マグナム77", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "たった一人の妹ルイーズを残酷な手口で殺されてしまった刑事トニーの復讐を描いたアクション映画。S&W44スペシャル・マグナムM29を片手に、悪の集団に単身乗り込む彼を待つものとは……。壮絶なカーチェイスや銃撃戦が印象的な１作。", "1976-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きかんしゃトーマス　伝説の英雄（ヒロ）", "きかんしゃトーマス　伝説の英雄（ヒロ）", "", "", "http://image.eiga.k-img.com/images/movie/55338/315x210.jpg?2875", 65, "人気児童絵本「きかんしゃトーマス」の劇場版第2弾。トーマスは、夏の間だけソドー島にやってきたいじわるなスペンサーと、どちらが強い機関車かを決める勝負をすることになる。しかし、必死に走っていたトーマスは、突然ブレーキが故障し、丘を急降下して森の茂みに突進してしまう。そこには遠い昔に日本からやってきた蒸気機関車のHIRO（ヒロ）がいた。ヒロはかつて“鉄道の神様”と呼ばれていたが、今ではボロボロの年老いた機関車になっていた。", "2010-04-24", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PICNIC", "ピクニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "アメリカ中西部の小さな町。町をあげてのピクニックの朝、無一文の流れ者ハルが友人を頼ってやって来る。半裸で庭仕事を手伝うハルに目を見張る女ばかりの隣家。ピクニックに興じるうち、次女ミリーがハルに夢中になる。しかし、夜のダンス・パーティーをきっかけにハルの心を射抜いたのは長女マッジだった……。おのおのの生活を抱えた人々が年に一度のピクニックにうきうきと繰り出し、また元の生活に帰っていく光景が、風物詩的な味をもち、恋心の甘さと苦さをにじませた名編だ。名曲『ムーングロウ』のリズムにのって踊り出すノヴァクが女盛りの色香を漂わせて好演。舞台出身のローガンの最高作。DVDは「キム・ノヴァク DVDトリプルパック」に収録。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハネムーンは無人島", "ハネムーンは無人島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "“結婚”をテーマに現代的な恋愛模様をユーモラスに描いたラブ・コメディ。パソコン通信が趣味の売れない構成作家・卓也は、“戸惑うペリカン”と名乗る女性と交信していた。ある日、とあるパーティで知り合ったバイク好きのかほりと付き合う卓也。実は彼女こそ“戸惑うペリカン”だった……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("純情部隊", "純情部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "力道山が主演し、貴重なリング姿も登場するプロレス好きにはたまらない作品。軍隊仲間の５人組が、終戦から５年後のクリスマスに再会。ただ一人落ちぶれている元力士の水田を、戦友たちが一致協力してプロレスラーとして成功させる……。助演陣も豪華だが、マキノ監督としては凡作の部類。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンのとび助冒険旅行 （エノケンのとび助）", "エノケンのとび助冒険旅行 （エノケンのとび助）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "エノケン＝中川信夫コンビによる第７作。エノケン扮する人形使いのとび助と少女が出会い、少女は母を求めて、とび助は悪い頭が良くなるという黄金の実を食べるため、ともに旅をする姿を描く。背景はすべて書き割り、特撮も用いられ、夢幻的な雰囲気が醸し出されている。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お引越し", "お引越し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "独創的な映像世界を開拓し続ける相米慎二の長編10作目。レンコは気の強い11歳の少女。彼女の両親はお互いの生活がかみ合わずに、離婚を前提とした別居を始める。はじめのうちは家が二つできたと喜んでいたレンコだが、自分勝手なことばかり言って、いがみ合う両親の姿を見ているうちに納得できないものを感じるようになる。家族の絆をとり戻そうと、以前行ったことのある琵琶湖畔への旅行に両親を無理矢理連れ出したレンコは、父母の間の溝の深さを思い知るが……。撮影に「クッキー・フォーチュン」「アフターグロウ」などアメリカ映画界で活躍するカメラマン、栗田豊通を迎えたことで画面もより厚みを増し、これまで以上の完成度を持った相米ワールドが展開する。特にラスト近くで祭りの中を少女がさまよう幻想的ともいえるシーンの美しさは圧倒的。またレンコ役の田畑智子の、新人離れした自然な演技と存在感にも注目したい。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAT PEOPLE", "キャット・ピープル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "猫族の美女アイリーナの悲しい運命。それは、同族以外の人間と愛し合えば、豹に変身し、相手を喰い殺さねば人の姿に戻れないというものだった……。ホラーというより、哀れな宿命を背負った女の悲劇のドラマ。キンスキーの野生的仕草が美しい。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パッテンライ!!　南の島の水ものがたり", "パッテンライ!!　南の島の水ものがたり", "", "", "http://image.eiga.k-img.com/images/movie/54498/315x210.jpg?2499", 0, "日本統治下の台湾で大規模な灌漑事業を成功させ、現在までその名を語り継がれている日本人技師・八田與一の実話をアニメ映画化。不毛の地と呼ばれる嘉南平原に派遣された八田は、同地に広大な灌漑施設を建設する計画を立てる。しかし、工事中のトンネルで50人以上が犠牲となる爆発事故が起きてしまい……。監督を務めるのは「宇宙戦艦ヤマト」「ルパン3世」といった人気作に参加し、日本アニメの黄金期を支えてきた石黒昇。", "2009-05-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いろはにほへと", "いろはにほへと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "“庶民のための唯一の投資銀行”としての急成長を遂げた投資経済会。この会の裏側に何かうさん臭いものを感じた警視庁捜査二課の松本刑事は、さっそく内偵に乗り出すが……。1959年度芸術祭文部大臣賞受賞のTVドラマを映画化したもので、撮影は小津作品の撮影で知られる名手・厚田雄春。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Daytrippers", "デイトリッパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タクミくんシリーズ　虹色の硝子", "タクミくんシリーズ　虹色の硝子", "", "", "http://image.eiga.k-img.com/images/movie/54458/315x210.jpg?4268", 74, "ボーイズラブ小説「タクミくんシリーズ」の映画化第2弾。全寮制男子高校・祠堂学院の2年生になったタクミは、恋人のギイや仲間たちに囲まれて幸せな日々を送っていた。ある日、彼らはギイの提案でハロウィン・パーティを開催することになるが、ギイが1年生の美少年・森田も誘うと言い出し……。キャストにはミュージカル「テニスの王子様」で注目された浜尾京介、渡辺大輔らイケメン俳優が勢揃い。「イヌゴエ」の横井健司がメガホンを取る。", "2009-04-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("СОЛДАТЫ СВОБОДЫ", "続 ヨーロッパの解放／第１部 ワルシャワ大攻防戦第２部 カルパチア大突破作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "旧ソ連が国をあげて製作した戦争スペクタクル「ヨーロッパの解放」の続編。ナチス・ドイツに蹂躙されたヨーロッパを、ソ連がいかに解放していったかを描いた自己礼賛的な作品だが、そのスケールと迫力は見もの。最近、評価が地に落ちた感のスターリンをはじめ、ヒトラー、チャーチル、チトーなどを演じる俳優陣の“そっくりさん”ぶりにも注目。", "1977-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドモ又の死", "ドモ又の死", "", "", "http://image.eiga.k-img.com/images/movie/53638/315x210.jpg?2679", 80, "麻薬中毒更生施設に収容された10人の女性が、課題として“大正時代の売れない画家たちと、彼らにモデルとして雇われた女性”を描いた舞台を上演することに。依存症に苦しみながらも練習に励む彼女たちだったが、次第に現実と物語の世界が交錯するようになり……。有島武郎の戯曲をモチーフに、現代女性の心の闇を描き出した異色の女性ドラマ。「カインの末裔」の奥秀太郎がメガホンを取り、劇団「毛皮族」主宰の江本純子が映画初主演を果たした。", "2008-06-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Against the Dark", "斬撃 ZANGEKI", "", "", "http://image.eiga.k-img.com/images/movie/54738/315x210.jpg?2519", 0, "スティーブン・セガール主演のゾンビ・アクション。街中に謎のウイルスが蔓延し、住民の大半が人肉をむさぼるゾンビと化してしまう。感染の拡大を防ぐため、ゾンビの集まる地帯に空爆を行うことを決定した政府だったが、そこにはウイルスに感染していない数名の生存者が残されていた。空爆のタイムリミットが迫る中、ゾンビ・ハンターのタオらは生存者の救出に向かう。", "2009-10-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ARBRE DE NOE¨L", "クリスマス・ツリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "米軍機の放射能を浴び、白血病で半年の命のパスカル少年とW・ホールデン扮する父親との心温まる交流をセンチメンタルにうたい上げた感動作。原作はM・バタイユのベストセラー、撮影はフランスで行われた。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sin Nombre", "闇の列車、光の旅", "", "", "http://image.eiga.k-img.com/images/movie/55138/315x210.jpg?3081", 96, "日系米国人のキャリー・ジョージ・フクナガ監督が移民たちの現実を描いた人間ドラマ。ホンジュラス出身の少女サイラは、父と叔父とともに移民たちでひしめく列車でアメリカを目指し旅立つ。道中、メキシコの強盗集団に襲われたサイラは、彼らの一味である青年カスペルに命を救われる。その後、サイラは裏切り者として組織から追われるカスペルと行動を共にし、国境を目指すが……。製作総指揮にガエル・ガルシア・ベルナルとディエゴ・ルナ。", "2010-06-19", "1,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルート２２５", "ルート２２５", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "芥川賞受賞作家、藤野千夜の同名小説を映画化した一風変わったファンタジー。町の風景は一緒だが、両親がいない別世界に迷い込んでしまった中学２年生のエリ子と、一つ年下の弟・ダイゴ。不安に駆られる二人は、何とか元の世界に戻ろうとするのだが……。 予想もしない状態にほうり出された少女の微妙な心の変化をリアルに描出。それにより、現代を生きる子供の心情に深く分け入る作品に仕上がった。", "2006-03-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Equus", "エクウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "　愛馬の目をピックで突き刺した17歳の少年。その謎を探る精神分析医は馬に対する少年の異常な愛情を知ることに。馬と少年、そして年上の女性との関係から、やがて歪んだ心理が浮き彫りになっていく……。大絶賛され、ブロードウェイでロングラン・ヒットしたピーター・シェーファーの戯曲を自らが脚本化し、映画化。主演のピーター・ファースは舞台でも少年を演じている。", "1977-01-01", "3,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAIT UNTIL DARK", "暗くなるまで待って", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "オードリーが盲目の人妻に扮した傑作スリラー。夫が謎の女から預かった人形には麻薬が縫い込まれてあった。やがて夫の留守中に見知らぬ男たちがアパートを訪れ、妻は恐怖のドン底に突き落とされる……。犯罪者３人が代わる代わる素性を偽って、盲目の主人公に接近してくる巧みな設定と、やがて訪れる暗闇でのクライマックスが観る者に緊張感を与える。映画の大半の舞台をヒロインの自宅に限定したテレンス・ヤングの演出も見事。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Week with Marilyn", "マリリン 7日間の恋", "", "", "http://image.eiga.k-img.com/images/movie/56958/315x210.jpg?2201", 100, "2012年で没後50年となる世界のセックス・シンボル、マリリン・モンローの秘めた恋をミシェル・ウィリアムズ主演で描いたドラマ。1956年、ローレンス・オリビエが監督・主演を務める映画「王子と踊子」の撮影のためロンドンを訪れたモンローは、初めて体験する海外での撮影のプレッシャーと、夫との確執により仕事に集中することができずにいた。さらに演技方法でオリビエとも対立し孤立してしまったモンローは、ただひとり的確な助言をくれた第3助監督のコリン・クラークと親密になっていく。クラークの回想録をもとに映画化。ウィリアムズがモンロー役を演じ、共演にはエディ・レッドメイン、ケネス・ブラナー、ジュディ・デンチ、エマ・ワトソン、ドミニク・クーパーら豪華英国俳優が集う。", "2012-03-24", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOOK OF LIFE", "ブック・オブ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "N.Y.インディーズの鬼才H・ハートリーがデジタルビデオで撮り上げた異色ファンタジー。1999年12月31日、キリストがマグダラのマリアを連れてN.Y.の空港に降り立った。ミレニアムの黙示録を予言したことに悩む彼は、悪魔から人類を救うべきか自問自答を続ける。トボケたユーモアに加え、ミュージシャン、P・J・ハーヴェイの出演も見逃せない。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スープ・オペラ", "スープ・オペラ", "", "", "http://image.eiga.k-img.com/images/movie/54948/315x210.jpg?2694", 119, "人気エッセイスト・阿川佐和子の小説を、坂井真紀主演で映画化。共演に藤竜也、加賀まりこ、西島隆弘ら。三十路なかばを過ぎた独身女性ルイは、30年以上ともに暮らしていた叔母のトバちゃんが嫁いだことで、ひとり寂しく暮らすことになる。そんなある日、風変わりな初老男性のトニーさんと、いつも笑顔の青年・康介が家に転がりこんできて、ひょんなことから同居することに。ルイは2人と一緒に食事をともにする毎日をおくることで、再び人生の活力を取り戻していく。", "2010-10-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hole", "穴", "", "", "http://image.eiga.k-img.com/images/movie/1258/315x210.jpg?4467", 102, "英国の名門パブリックスクールで、4人の男女が行方不明に。ひとり生還したリズは、精神科医に事の顛末を語る。が、それは彼女の企てに手を貸した友人の証言とは異なっていた。はたして事件の真相は？　原作はオックスフォード在学中に出版した本作で注目を集めたガイ・バートの同名小説（アーティストハウス刊）。監督は「マーサ・ミーツ・ボーイズ」でデビューしたニック・ハム。音楽は「π」のクリント・マンセル。", "2002-05-18", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Southern Comfort", "サザン・コンフォート／ブラボー小隊　恐怖の脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　ルイジアナの湿地帯で訓練を行なう州兵“ブラボー小隊”の男たちはケイジャンと呼ばれる現地民が暮らす地域に足を踏み入れる。だが、そこでしでかした悪ふざけがきっかけでケイジャンが発砲。リーダーのプール軍曹が殺害され、さらに残りのメンバーにも危険が迫る。地獄と化した一帯から脱出できるのか？　兵士たちのサバイバルを描いたアクション・スリラー。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Asphalt Jungle", "アスファルト・ジャングル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "　刑務所を出たばかりのドクは宝石泥棒を計画。 いかがわしい弁護士らと共謀して、犯行に及ぶ。首尾よく宝石を盗んだものの、仲間のひとりが負傷。やがて仲間割れも起こり……。悩みや弱みを持つリアルなギャングたちの末路がハードボイルド・タッチで描かれる。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mother & Child", "愛する人", "", "", "http://image.eiga.k-img.com/images/movie/55208/315x210.jpg?3347", 126, "14歳のときに身ごもった過去をもつカレンは、母の介護に追われながらも手放した娘のことを忘れられずにいた。その娘エリザベスは、母の顔も知らずに孤独な日々を過ごしていた。しかし、エリザベスは突然の妊娠をきっかけに、まだ見ぬ母への思いを募らせていく。「21グラム」のナオミ・ワッツが主演を務め、ノーベル賞作家ガルシア・マルケスの息子ロドリゴ・ガルシア監督がメガホンをとる。製作総指揮に「バベル」のアレハンドロ・ゴンサレス・イニャリトゥ。", "2011-01-15", "1,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COOL GIRLS クールガールズ", "COOL GIRLS クールガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "矢吹春奈、上原歩、相澤仁美ら人気グラビアアイドル共演で描いたスタイリッシュアクション。建国60周年を目前にした北朝鮮から1人の工作員が日本に密入国し、歌舞伎町に潜伏する。捜査に行き詰まった警視庁公安課の女捜査官・裕美は、刑務所に収監されていたマコ、ジュン、ミチ、マユミの4人の女たちを、自由の身と引きかえに潜入捜査させることに。かつて歌舞伎町でたくましく生きていた4人は、工作員を追って夜の街へと飛びだしていくが……。", "2009-07-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MOUNTAIN", "山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "E・ドミトリク監督が、山に取り憑かれた兄弟の愛情の相克を描いた人間ドラマ。スイス・アルプスの山麓に住むザハリーとクリストファーの兄弟。山のガイドとして身を立てている兄のザハリーに対し、弟のクリストファーはのんべんだらりと毎日を暮らしている。ザハリーは、なんとか弟をまともな道に連れ戻そうとするが……。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泥にまみれて", "泥にまみれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "志乃は学生時代、鶴岡知而と知り合い結婚した。やがて長女が生まれ、知而は劇作家として成功するが、当局の弾圧で公演は中止、劇団員は留置される。嫁いだばかりで夫の浮気から実家へ帰ってきた長女に、志乃が自分たちの夫婦の足跡を語って聞かせる形で、ストーリーが進行。苦労ばかりで泥にまみれた20年の女の人生模様をじっくりと描き抜いた女性映画。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUNT JULIA AND THE SCRIPTWRITER", "ラジオタウンで恋をして", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "人々がラジオの前に集まり、ソープオペラ（昼メロ）に釘づけになっていた1951年のニューオリンズ。低迷にあえいでいたソープオペラを局の看板番組にまで押し上げた台本作家と、その作家にインスピレーションを与え、叔母と甥の関係にありながら愛し合ってしまう二人の計３人が織り成すコメディ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Million Dollar Baby", "ミリオンダラー・ベイビー", "", "", "http://image.eiga.k-img.com/images/movie/1538/315x210.jpg?1812", 133, "ロサンゼルスの寂れたボクシングジムの門を叩いた田舎育ちのマギー。ジムのオーナー兼トレーナーのフランキーは彼女を拒んでいたが、彼女の真剣さに打たれ、彼女のトレーナーとなる。お互いに父娘の関係をなくしている2人は、激しいトレーニングの中で人間的に歩み寄っていく。アカデミー賞作品賞、監督賞、主演女優賞、助演男優賞の主要4部門を受賞した話題作。", "2005-05-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長血笑記 秋葉の対決", "次郎長血笑記 秋葉の対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "メキメキと売り出してきた新参の次郎長一家を煙たく思った武居の安五郎は、貸元・矢部の虎次郎と図って、次郎長一家の石松と熊次郎を痛めつける。しかし、秋葉の喧嘩で次郎長に叩き斬られる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富嶽秘帖", "富嶽秘帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "陣出達朗の原作を伏見扇太郎主演で映画化した冒険時代劇で、工藤栄一の監督デビュー作。榊原家の息女・亜矢姫と忠臣・来間又四郎は、榊原家の財政難を救うため、角行上人が残した財宝を手に入れようとする。しかし、悪漢・天堂勘兵衛一味の襲撃にあい、亜矢姫は谷底に転落していく。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRASH DIVE", "潜航決戦隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "三角関係のメロドラマを取り入れた潜水艦アクションの会心作。製作者が第二次大戦中、海軍に従軍していたというだけあり、リアル・フィクションの趣きも。中尉と中佐は共に潜水艦内で勤務をしている。ある日、中尉は中佐の彼女とは知らずに女教師と知り合いになったが、深い関係になる前に潜水艦出撃を命じられる。戦果を挙げて帰還した二人だが、女教師を巡りひと悶着が起こる……。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三百六十五夜", "三百六十五夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "市川崑監督作に続く、小島政二郎の小説の２度目の映画化。それぞれの親の借金のために、引き裂かれそうになる相思相愛の若い男女の姿を描いている。高倉健が（何となくつまらなそうに）メロドラマの主人公を演じた作品で、逆に喜々として“色悪”を演じる平幹二朗と好対照を成している。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホームカミング", "ホームカミング", "", "", "http://image.eiga.k-img.com/images/movie/55818/315x210.jpg?1474", 105, "名作ドラマ「金曜日の妻たちへ」などの飯島敏宏監督がメガホンをとり、高田純次が映画初主演を果たした人情コメディ。定年退職を迎え、妻と2人きりの暮らしをスタートさせた61歳の鴇田。彼らは、都心から40キロ離れたニュータウン、虹の丘タウンに暮らしていた。しかし、かつて賑わっていた町も少子高齢化が進み、すっかり老人の町へと様変わりしていた。鴇田は町に活気を取り戻すべく、仲間たちとともに町興しのお祭復活運動を開始する。", "2011-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents 真夜中の宴　「功夫哀歌　カンフーエレジー」", "桃まつり presents 真夜中の宴　「功夫哀歌　カンフーエレジー」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 33, "", "2008-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("009 RE:CYBORG", "009 RE:CYBORG", "", "", "http://image.eiga.k-img.com/images/movie/57258/315x210.jpg?2621", 0, "石ノ森章太郎の代表作で、特殊能力をもつ9人のサイボーグ戦士の戦いを描く「サイボーグ009」の舞台を現代に置き換えて新たに3Dアニメーション化。「攻殻機動隊 STAND ALONE COMPLEX」「東のエデン」の神山健治監督が手がける。", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社長太平記", "社長太平記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "婦人下着メーカー・錨商事はライバルのさくら商会の東京進出を前に、決戦体勢。ヤマ場は大福デパートにどちらの製品を納入させるかにかかっている。錨商事側は牧田社長の命を受けて大森専務が出向きデパートと交渉するも難航し……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("独立少年合唱団", "独立少年合唱団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "1970年初頭の緑豊かな北の町にある全寮制中学を舞台に、合唱に情熱を燃やす二人の少年の友情を描いた青春映画。吃音症の道夫は、同級生たちにいじめられる毎日を送っていた。そんな彼を救ったのは、美しい顔立ちをした康夫だった。天使のようなソプラノの声を持つ康夫に導かれ、道夫は合唱部に入部する。歌っているときは、どもらないと知った道夫は次第に明るさを取り戻す。そんな時、康夫を待ち受けていたのは、“声変わり”という思春期の宿命だった……。映画全篇を通して流れる弦楽合唱の切なく美しい調べが秀逸。2000年第50回ベルリン国際映画祭で高い評価を受けた。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パンツの穴 THE MOVIE 童貞喪失ラプソディ", "パンツの穴 THE MOVIE 童貞喪失ラプソディ", "", "", "http://image.eiga.k-img.com/images/movie/57178/315x210.jpg?4017", 0, "1984年の菊池桃子の銀幕デビュー作を皮切りに、何度も映画化されてきた青春コメディの21年ぶり再映画化作。とある田舎町に暮らす高校1年生の博志、亮太、拓郎の3人は、夏休みの間に童貞を卒業することを宣言。そんなある日、3人の前に絶世の美女・一ノ瀬なるみが現れて……。エロティックな体験の悲喜こもごもが語られる「月刊BOMB！」の人気読者投稿コーナー「パンツの穴」をもとに、映画オリジナルのストーリーを構成。主演は人気グラビアアイドルの篠崎愛。「子猫の涙」「女の子ものがたり」の森岡利行がメガホンをとる。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("だれも知らない夏の空", "だれも知らない夏の空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "関西自主映画界で活躍してきた中治人が劇場デビューを飾った、愛すべきロード・ムービーの小品。自由気ままに歌いながら全国を旅する姉トモに憧れて、家を飛び出したシンガーソングライター志望の妹ナツ。弾き語り放浪の末、トモと再会したナツの高校最後の“夏休み”を、大らかなムードの中に綴っていく。インディーズ・シーンで活動中の渡辺智江がトモに扮し、生命感あふれる伸びやかな個性で、映画に活力とダイナミズムを与えている。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUTINY ON THE BOUNTY", "戦艦バウンティ号の叛乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "タヒチに向けて航海中の英国戦艦バウンティ号。やがて非情な艦長による苛酷な労働に憤った船員たちは、艦長らを小舟に置き去りにしてしまう……。実際に起きた叛乱事件を描いた最初の映画化作品で、劇場公開時の邦題は「南海征服」。C・ゲーブルの人気を確立した作品だが、当時は検閲のため大幅にカットされた。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NIGHT OF THE PENCILS", "ナイト・オブ・ペンシルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "軍事政権下のアルゼンチンで、実際に起こった事件をセミドキュメンタリー・タッチで再現した衝撃的な作品。地獄のような収容所から、たった一人生還した学生の証言をもとに描かれている。1976年、ラプタの町から学生運動のリーダーたちが次々と消えていき、行方不明となる。その実態は……。ビデオ発売時タイトルは「ミッドナイトミッシング」。", "1986-01-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AIR UP THERE", "アフリカン・ダンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "大学バスケット・チームのコーチ、ジミーは監督昇進を狙い、未来のスターを発掘すべくアフリカの奥地までスカウトにやって来る。そこで彼は、想像を絶する才能の持ち主サレと出会うが、サレは一族の王子で、アメリカに行くことはできない。あらゆる手をつくしてサレを勧誘するジミーだが……。大らかでアップテンポなスポーツ・コメディ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES ORGUEILLEUX", "狂熱の孤独", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "妻をお産で失ってから酒びたりの日々を送っている医者のジョルジュが住む、メキシコの小さな漁村。そこに夫婦で旅行に訪れたフランス人カップル。だが夫が伝染病で死んでしまい、一人残された妻のネリー。ある日ジョルジュは美しいネリーに出会い、忘れかけていた人を愛する感情を、徐々にとり戻してくる。そしてネリーの方も次第にジョルジュに惹かれていった。だがネリーを狙うホテルの主人ドン・ロドリゴに襲われそうになり、彼女は必死に逃れてジョルジュの胸へ飛び込む。サルトルの小説を、フランス映画界きっての美男美女によって叙情的に映画化された秀作。ヴェネチア映画祭では銅獅子賞を受賞した。", "1953-01-01", "2,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Game 6", "ライフ・イズ・ベースボール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "現代アメリカ文学を代表する小説家ドン・デリーロが初めて脚本を手掛け、「卒業の朝」のマイケル・ホフマン監督がメガホンを取った人間ドラマ。1986年のニューヨーク・ブロードウェイを舞台に、人生の正念場を迎えたレッドソックスファンの劇作家の男性と、68年ぶりの優勝にあと一歩のところまで迫ったレッドソックスの運命を交差させながら描いていく。主演は「バットマン」シリーズのマイケル・キートン。", "2007-04-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("徳川女刑罰史", "徳川女刑罰史", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "近親相姦、強姦、拷問などタブーの連続で魅せる“異常性愛路線”のヒット作品。江戸時代を背景に、３編にわたる壮絶なセックス・バイオレンスが繰り広げられる。多彩な娯楽を提供し続ける石井輝男監督が、いわゆるエログロな側面を鮮烈に打ち出した。罪を告白し刑に処せられたヒロイン。そんな彼女が兄を殺すに至った経緯が物語られる。第２話は尼寺での拷問・自殺事件。さらに、刺青モデル殺人事件が発生。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピンクのカーテン", "ピンクのカーテン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "ジョージ秋山の同名コミックを、これが３作目の上垣保朗監督が軽妙かつ哀愁たっぷりに描いたロマンポルノ。都会で肩を寄せあって生きる兄と妹の、近親相姦になりそうでならない危険な関係。自由に生きようとふるまいながら、兄にいちばん信頼と愛情を寄せる美保純の無邪気な笑顔が印象的だ。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Red Baron", "レッド・バロン", "", "", "http://image.eiga.k-img.com/images/movie/54918/315x210.jpg?4207", 120, "80機の敵機を落とした第1次大戦時のドイツ軍撃墜王マンフレート・フォン・リヒトホーフェンの実話を映画化。赤い戦闘機を操り、正確無比で華麗な腕前を誇るリヒトホーフェンは、「レッド・バロン（赤い男爵）」の異名で敵味方問わず恐れられ、また賞賛されていた。しかし、自身が戦争のプロパガンダに利用されていることに気がつくと、戦うことへの嫌悪とパイロットとしての責務のあいだで板ばさみになっていく。", "2011-05-21", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Omen", "オーメン（1976）", "", "", "http://ecx.images-amazon.com/images/I/51V566d%2BzjL._SL210_.jpg", 0, "“悪魔の子”ダミアンに翻弄される人々の恐怖を描き、世界的ヒットを記録した名作オカルトホラー。アメリカ人外交官ロバートは、6月6日午前6時にローマの産院で生まれてすぐに死んだ我が子の代わりとして、同時刻に誕生した男の子を引き取りダミアンと名づける。ところが、ダミアンが5歳の誕生日を迎えた頃から周囲で不可解な事件が次々と起こりはじめ……。ジェリー・ゴールドスミスによる音楽がアカデミー作曲賞に輝いた。", "1976-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("危険な女たち", "危険な女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "A・クリスティの『ホロー荘の殺人』を野村芳太郎が翻訳して映画化。とある別荘に集まった人々の、複雑に絡んだ人間関係が殺人を呼ぶという内容。この作品以前に金田一耕助を演じた石坂浩二がここでも探偵まがいの小説家を演じている。室内劇スタイルで贈る極上の犯人当てミステリー。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Meres et Filles", "隠された日記　母たち、娘たち", "", "", "http://image.eiga.k-img.com/images/movie/55458/315x210.jpg?2218", 104, "「海のほとり」でカンヌ映画祭カメラドール(新人監督賞)を受賞したフランスの若手女性監督ジュリー・ロペス＝クルバルが、カトリーヌ・ドヌーブら豪華女優陣を迎えて描く家族のドラマ。恋人ではない男性の子供を妊娠したキャリアウーマンのオドレイは、母親になることに不安を抱き、産むべきか否か迷っていた。そんなある日、久々に帰省したオドレイは、亡き祖父が暮らしていた海辺の家で1冊の日記を見つける。それは、50年前に家族を残して家出した祖母のものだった。", "2010-10-23", "2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPERAZIONE SAN GENNARO", "ナポリと女と泥棒たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ナポリの寺院にある時価300億リラの宝石をめぐって展開するアクション・コメディ。シカゴのギャング、ジャックは情婦や子分とともにナポリに乗り込み、当地の泥棒ドドと組んで宝石盗みの珍作戦を開始する。ドドは手下を集めるが、これがどうにも頼りない連中ばかりで……。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CUTTHROAT ISLAND", "カットスロート・アイランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "「ドリヴン」のR・ハーリンが、17世紀の絶海の孤島を舞台に描いたアドベンチャー。世界を変えるほどの莫大な財宝が眠る島“カットスロート・アイランド”。そこを目指した者には死が訪れるという、その島の地図の一部を手に入れたモーガン・アダムス。それは何者かに襲われ、息を引き取る間際に父から渡されたものだった。父の遺志を継ぎ、彼女は命懸けの冒険に出る。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Triumph of Love", "愛の勝利", "", "", "http://ecx.images-amazon.com/images/I/51KHSZ6E17L._SL210_.jpg", 112, "", "2001-01-01", "3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本の首領 野望篇", "日本の首領 野望篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "三船敏郎が暴力団会長に扮して登場する。昭和46年、勢力を回復した中島組は、関東進出の第一歩として桜商事を作る。東京の暴力団松風会の会長・大石は右翼の大物・大山を顧問に迎えて、暴力団連合“関東同盟”を結成し、これに対抗する。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOMER AND EDDIE", "天使が降りたホームタウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "人は善いが少し頭の足りないホーマーは、父が死にかかっていることを知り、故郷に帰ることになった。旅先で出会う人々は決して善意を持つ人とは限らなかったが、黒人のルンペン女性エディだけは違った……。A・コンチャロフスキーが愛と友情を優しく描く心温まるロード・ムービー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AIR HEADS", "ハードロック・ハイジャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "自分たちの曲をラジオでかけたいがために、ラジオ局そのものをジャックしてしまった、おマヌケなヘビメタ小僧３人組を描いたロック・コメディ。ほんの冗談のつもりが警察、SWATまでも出動することになり、慌てる彼ら……。ホワイト・ゾンビ、モーターヘッドなど、本物のハードロック・バンドも特別出演するおまけつき。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベルセルク　黄金時代篇II ドルドレイ攻略", "ベルセルク　黄金時代篇II ドルドレイ攻略", "", "", "http://image.eiga.k-img.com/images/movie/57528/315x210.jpg?1783", 0, "三浦建太郎のベストセラーコミック「ベルセルク」の「黄金時代」編をアニメーション映画化した3部作の第2部。主人公の剣士ガッツと傭兵団「鷹の団」が挑む難攻不落の要塞ドルドレイの攻略戦や、ガッツと戦友グリフィスの決闘と別れまでが描かれる。鷹の団に入団して3年、団長グリフィスの夢をともに追ってきたガッツだったが、グリフィスのふとした言葉がきっかけで退団を決意する。一方、ミッドランド王国とチューダー帝国の戦争は最終局面を迎え、鷹の団は5000人の兵力で3万人の敵兵がいる難攻不落のドルドレイ要塞に挑むことになる。", "2012-06-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("甘い夜の果て", "甘い夜の果て", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "吉田喜重の第３作で松竹ヌーヴェル・ヴァーグ末期の作品。新興工業都市を舞台に、デパートに勤める貧しい青年が、女を食いものにして次第にのし上がるが、結局は挫折する姿を、冷たく突き離したタッチで描く。青年役の津川雅彦、彼と関係を結ぶ社長令嬢に扮する瑳峨三智子の存在感が光る。DVDは「吉田喜重 DVD-BOX［60-64］新しい波」に収録。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("台風騒動記", "台風騒動記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "東海地方を襲った台風のために町は被害を受けた。町の有力者は壊れてもいない学校の校舎をぶち壊し、政府の補助金をガッポリと懐へ入れようと企んでいた。それに気づいた町の青年たちが立ち上がる。けっこう笑える本格的風刺喜劇。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／SCENT　OF　LOVE", "菊花の香り／　世界でいちばん愛されたひと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "韓国ほかアジア各国でベストセラーとなったキム・ハインの同名小説を映画化した純愛ドラマ。大学時代にめぐり合った男女が、７年以上の時をかけてかけがえのない愛情で結ばれていく様を切々と描き出す。「殺人の追憶」の殺人容疑者役で鮮烈な印象を残したパク・ヘイルと、「オーバー・ザ・レインボー」の美人女優チャン・ジニョンの顔合わせも魅力の感動作だ。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MERRY CHRISTMAS Mr. LAWRENCE", "戦場のメリークリスマス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "第2次大戦中のジャワ山中。日本軍捕虜収容所という、極限状況に置かれた人間たちの相克を描いた異色のヒューマンドラマ。日本軍のエリート士官ヨノイと連合軍捕虜セリアズ少佐の愛情めいた関係を中心に、日本軍人と西洋人捕虜との関係が興味深く描かれる。坂本龍一、ビートたけし、デビッド・ボウイといった国内外の異色スターたちが共演し、坂本の音楽も高い評価を得た。", "1983-01-01", "76,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Perfect Stranger", "パーフェクト・ストレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ハル・ベリーとブルース・ウィリスが共演を果たしたサスペンス・ドラマ。女性新聞記者ロウィーナは、幼なじみのグレースが変死した事件の調査を開始する。容疑者として浮かび上がったのは、広告会社を経営する大富豪ハリソン・ヒル。事件の前、グレースはハリソンを脅迫しようとしていたのだ。ところが、調査を続けるうちに新たな容疑者が次々と現われ……。監督は「コンフィデンス」のジェームズ・フォーリー。", "2007-09-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Spirit", "ザ・スピリット", "", "", "http://image.eiga.k-img.com/images/movie/54338/315x210.jpg?3682", 103, "「シン・シティ」のフランク・ミラーが初の単独監督に挑み、革新的なビジュアルで描いたアクション。スカーレット・ヨハンソン、エバ・メンデスら豪華な女優陣が脇を固める。殉死した元警官デニー・コルトは死の淵から蘇り、スピリットとなって深夜の街で犯罪と戦っていた。やがて宿敵のオクトパスと“ヘラクレスの血”をめぐる争いの中で、彼の壮絶な過去が明かされていく。原作はアメコミ界の大御所ウィル・アイズナーのコミック。", "2009-06-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASINO", "カジノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 179, "欲望渦巻くラスベガスを舞台に、カジノのボスの栄光と凋落を描いた大作。1973年、ラスベガス。中西部のギャングからのし上がったノミ屋のサム“エース”ロススティーンは、ボスたちに可愛がられ４つのカジノを任されていた。マフィアの取り分を二倍に増やし、経営のルールを塗り替えるまでの大物になったエースは、妖艶なチップ詐欺師のジンジャーに夢中になり結婚をする。だが、独占欲の強いエースは彼女を篭の鳥にしてしまい、欲求不満から彼女は次第に酒と麻薬に溺れていった。ついにはエースの親友で、彼の右腕として長年ヒットマンを務めてきたニッキー・サントロと深い関係を結んでしまい、やがて３人の生活に陰謀と妄執が渦巻き始める……。スコセッシ、デ・ニーロのコンビ第８作。めまぐるしい映像と編集が織り成すスピードの間に、悲哀と表裏一体の卓越したユーモアセンスがのぞく逸品である。またジンジャーを熱演したS・ストーンは、本作でゴールデン・グローブ最優秀主演女優賞を獲得した。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Leviathan", "リバイアサン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "　海洋開発企業によって運営されているフロリダ沖の巨大基地では、地質学者のベックをはじめとする計8人のスタッフが職務に従事していた。ある日、海底探査に出た者たちがソ連の艦艇リバイアサンから遺留品を持ち帰る。しかしその中のビンには新種の水中生物を生み出す試薬が入っていた。それを飲んでしまった者の肉体に変化が。人間の知能を持つ生物がベックたちに襲いかかる！　海底版「エイリアン」とも呼べるSFスリラー。", "1989-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IEDEREEN　BEROEMD！", "エブリバディ・フェイマス！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "2001年のアカデミー外国語映画賞候補になったベルギー産ハートフル・コメディ。歌手になりたいという反抗期の娘マルヴァの夢をかなえるため、人気歌手デビーを誘拐することにしたジャン。人質の交換条件は自分が作曲した“ラッキー・マヌエロ”で娘をデビューさせることだった。娘のために頑張るジャンの親バカぶりが微笑ましい。", "2000-01-01", "37,2,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAMA CUMPLE 100 ANOS", "ママは百歳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "家庭教師をしていた家の、100歳になるという老婆の誕生パーティーに招かれたアナ。３人いた教え子のうち、一人は死に、一人は失踪、家にいるのはただ一人だけだった。そんな中、老婆は嵐が家を吹き飛ばした夢を見たと告げる。それと同時に失踪していた一人が突然帰宅。老婆は発作を起こす……。財産目当ての息子たちと、100歳を迎えた老婆が巻き起こすファンタスティックなコメディ。C・サウラが1972年に撮った「Ana y lobos（アナと狼たち）」の続編ではあるが、これ１作で完成した作品となっている。サン・セバスチャン映画祭審査員特別賞を受賞している。", "1979-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("King of the Hill", "わが街セントルイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "不況時代の1933年、セントルイス。母は療養所、父はセールスの仕事で他州を渡り歩き、弟は親戚に預けられ、1人残されてホテルに住んでいる12歳の少年は、家賃が払えず追い出される隣人たちを見つつ、必死に生きていく。厳しい現実に立ち向かう知恵に溢れた少年の日常に、多彩な登場人物が絡み合う精緻な語り口が見事。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les revenants", "奇跡の朝", "", "", "http://image.eiga.k-img.com/images/movie/33658/315x210.jpg?4549", 103, "“死者の蘇生”をテーマに、突然蘇った死者と、彼らを再び迎え入れることになった家族の苦悩や葛藤を丁寧に描き、ベネチア国際映画祭をはじめ世界中で賞賛されたフランス映画。フランスのとある街で、多数の死者が生前のままの姿で帰ってくるという不思議な現象が起こった。彼らの家族はかつての生活を取り戻そうとするが、蘇生後の様子が以前とは違うことに戸惑いはじめる。出演は「ふたりの5つの分かれ路」のジェラルディン・ペラスほか。", "2006-09-23", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN ALMOST PERFECT AFFAIR", "カンヌの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "自分の映画を売り込もうとカンヌにやって来た映画青年のハル。フィルムを税関に差し押さえられるというアクシデントに見舞われるが、居合わせたとある大プロデューサーの夫人に助けられて……。「がんばれ！ベアーズ」のM・リッチーが、カンヌ映画祭を舞台に描いたロマンティック・コメディ。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER ORDAINED", "断罪 堕ちた聖者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 186, "アメリカ中西部のルーテル派牧師の妻と、彼と関係をもった人妻の夫が時を同じくして事故死するという事態から、牧師の仮面の下に隠された悪魔の顔が暴かれていく。1982年にアメリカで実際に起きた事件をもとに作られたドラマ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦國時代", "戦國時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "一人の薄幸な女性を守る蓮如上人の姿を通して、彼の教えの崇高さを描く宗教映画。時は戦国。漁民の次郎は愛するお菊を残して戦場へ獲り出され、戦地で恋仇の策謀により行方不明に。一方、村で孤立し絶望したお菊は蓮如の説法に活路を見い出す。やがて次郎が戻り、二人は結ばれる。月形は蓮如と次郎の二役を演じる。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("咬みつきたい", "咬みつきたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "幼いころからドラキュラに憧れていた女医・ゆず子は、ドラキュラの血液の入手に成功するが、社内抗争に巻き込まれて殺された石川に間違ってその血が輸血されてしまう。１年後、石川は吸血鬼としてこの世にみがえることに。吸血鬼という異色な役柄に挑んだ緒形拳の怪演が見ものの異色ホラー・コメディ。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛妻記", "愛妻記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "新進作家の多木太一は妻とも別れ、のんきに一人暮らし。学生である後輩が妻と営む麻雀クラブの手伝いなどをしていた。その店には彼女の友人、芳江が働いている。太一は明るい彼女にひかれていき、ついには結婚をするのだった。貧しいながらも幸福に生きる夫婦を温かいタッチで綴る。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不死鳥", "不死鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "女学生の頃に出会い、激しい恋に燃え、父の頑強な反対にもかかわらず結婚した主人公が、その後戦争で失った夫を回想する。当初、田中絹代と上原謙のコンビ映画として企画されたが、上原の調整がつかず急きょ代わりに佐田啓二が抜擢されデビューを飾った。楠田浩之のカメラワークがいい。DVDは「木下惠介　DVD-BOX 第１集」に収録。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("F／X２ ： THE DEADLY ART OF ILLUSION", "F／X２ イリュージョンの逆転", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "20年前に迷宮入りした“バチカン秘宝盗難事件”に絡む巨大組織の陰謀を、偽装、変装何でもござれの天才的SFX（特殊視覚効果）マン、ロリー・タイラーが持ち前の技術を駆使して暴き出す。サスペンス色の強かった前作に、アクションの醍醐味をプラス、二転三転するプロットが痛快だ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WALLACE＆GROMIT", "ウォレスとグルミット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "お坊ちゃんで世間知らずの発明家ウォレスと、彼より利口な犬のグルミットが活躍するクレイ・アニメーション。「快適な生活」(1990年、５分)、「チーズ・ホリデー」(1989年、23分)、「ペンギンに気をつけろ！」(1993年、29分)の３本の短編により「ウォレスとグルミット」というタイトルで公開された。「快適な生活」は、動物園の動物たちにインタビューをし、快適な暮らしとは何かを探るドキュメンタリータッチの作品で、1990年アカデミー賞アニメーション部門最優秀賞を受賞。「チーズ・ホリデー」では、ウォレスとグルミットが、チーズを求めてロケットを作って、月へと旅立つ物語。「ペンギンに気をつけろ！」は、グルミットの誕生日、生活費に困ったウォレスが、ペンギン紳士を下宿させることにするが、その日からグルミットとペンギン紳士は衝突してばかり−−という内容。この作品は、1994年アカデミー賞短編アニメーション部門最優秀賞をはじめ、数々の映画祭で受賞している。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・右むけェ左！", "喜劇・右むけェ左！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "女性下着メーカーに新設された外国課の面々は、体力を鍛えるために自衛隊に体験入隊するハメに。慣れない猛訓練で右往左往するが、それまで冴えなかった戦中派の課長は俄然、張りきりだす。喜劇の名手・前田陽一が描く痛快コメディ。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おまえを逮捕する", "おまえを逮捕する", "", "", "http://image.eiga.k-img.com/images/movie/33928/315x210.jpg?4813", 120, "テレビドラマ「チェオクの剣」で人気のキム・ミンジュンが映画主演デビューを果たした刑事アクション。直感で犯人を見破る才能を持つ新米刑事ホンジュや、健忘症に悩むベテラン刑事ムンら個性的なメンバーが集まる捜査3課は、情熱だけは人一倍だが失敗ばかり。署内ではお荷物扱いされ、ついに捜査費まで削減されてしまった。そんなある日、偶然麻薬密売の手がかりをつかんだ彼らは、名誉挽回のチャンスと張り切って捜査に乗り出すが……。", "2007-04-14", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A CUT ABOVE", "メディカル・レッスン 青春解剖学", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "スタンリー・キューブリック監督の「フルメタル・ジャケット」で本格的に注目されたM・モディンが、「メンフィス・ベル」「パシフィック・ハイツ」の前年に出演した青春映画。記憶力抜群で苦労知らずのジョーは、医学院に入学する。必死で勉学に励むほかの学生たちをよそ目に優雅な毎日を送るジョーだったが、同級生や教授たちとの交遊の中で、やがて恋と友情、生命の尊さを体感していく。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渡り鳥故郷へ帰る", "渡り鳥故郷へ帰る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "“渡り鳥”シリーズの便宜上の最終作。瀬戸内海の地方都市に、筧組幹部の滝浩が３年ぶりに戻って来て、新興ヤクザと闘い、組を立て直し去っていく。タイトルに“渡り鳥”とあるのは興行的な配慮によるもので、主人公の名も監督もヒロイン役も異なるシリーズ外の作品。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOZART", "モーツァルト／青春ヘの旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "現存する手紙をもとにした登場人物の語りで進行するこの映画は、「アマデウス」のような外観的な描き方とは対照的にモーツァルトの心の内を浮き彫りにしている。少年のころから、母の死を迎えることになる青春時代までを中心に描いたドラマ。", "1976-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOING STEADY", "グローイング・アップ2　ゴーイング・ステディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "世界中で大ヒットを記録した青春映画の続編。前作の大失恋にも懲りず、仲間のボビーやヒューイとともにナンパに精を出すベンジー。美少女タミーと恋に落ちた彼だったが、まじめな彼女はなかなか体を許してくれない。そんな二人が紆余曲折を経て強く結ばれるまでを、軽快なテンポで描いていく。", "1979-01-01", "46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADOLESCENTS", "新・青い体験", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "スペインからロンドンに留学した女子高生が大都会の刺激にふれ、ディスコのDJに恋し、初体験を……。主演はチャールズ皇太子とのスキャンダルで話題をふりまいたスタークとTVシリーズ『華麗なる貴族』で評判となったA・アンドリュース。", "1976-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A TIME OF DESTINY", "デスティニー 愛は果てしなく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "戦火の吹き荒れる1943年にスペインからアメリカへ移住して来たララネッタ家。頑固な父に結婚を反対された娘は若い兵士ジャックと駆け落ちするが、すぐに連れ戻される。が、その帰途に父は運転を誤って死ぬ。長男マーティンは父はジャックに殺されたのだと思い込み、彼を恨み綴ける。G・ナヴァとA・トーマス夫妻の実体験をもとに、父子、兄妹、夫婦のそれぞれの立場からそれぞれの愛を描いた大河ドラマ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲゲゲの鬼太郎　千年呪い歌", "ゲゲゲの鬼太郎　千年呪い歌", "", "", "http://image.eiga.k-img.com/images/movie/53288/315x210.jpg?2793", 119, "“かごめ歌”を聞いた若い女性が失踪する事件が続発。現場にはいずれも“銀の鱗”が残されていた。事件が千年の時を経て蘇った悪霊の仕業だと突き止めた鬼太郎たちは、悪霊の呪いを封印するために必要な“古の楽器”を集めることに。しかし事件の裏には妖怪と人間の切ない恋の物語が隠されていた……。水木しげるによる名作コミックの実写映画化第2弾。鬼太郎役のウエンツ瑛士をはじめ、田中麗奈、大泉洋ら豪華キャストが再結集。", "2008-07-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DERANGED ……THE CONFESSIONS OF ED GEIN", "ディレンジド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「サイコ」「悪魔のいけにえ」「羊たちの沈黙」など数々の名作映画のもとになった実在の殺人鬼エド・ゲイン（映画ではエズラ・コブ）をモデルにした作品。母親と二人暮らしの中年男のエズは、母親が死んでからも死体と一緒に生活していた。死体が腐乱し始めると墓地から死体を盗み、母親の死体の修理をしたり、母親が淋しくないようにと頭蓋骨を並べたりする。やがて、彼は二人の女を殺してしまうのだった……。1974年に公開されるや高い評価を得たもののフィルムが行方不明になるなど、伝説のカルト・ムービーとなった作品。1995年に日本でも初めて公開された。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拳銃無頼帖 不敵に笑う男", "拳銃無頼帖 不敵に笑う男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "早射ちの竜は、出獄して恋人のいる金沢へ帰ってくるが、彼女はすでに事故死していた。町では浜田組と船田組という二つのヤクザ組織が対立。竜は恋人の妹の安全と引きかえに船場組の用心棒になる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEETING VENUS", "ミーティング・ヴィーナス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ヨーロッパ各国から、パリで行われるオペラ公演のために集められた、指揮者サントーをはじめとする音楽家たち。だが、指揮者の意思は伝わらない、交響楽団は残業を一切しないと、演奏以前の問題が山積み。苦悩するサントーの前に、オペラ歌手カーリンという救世主が出現する。彼女との恋がサントーを変えていき……。I・サボー監督が、EC統合に向かって混迷する今のヨーロッパを凝縮したような、オペラ楽団の人間模様をコメディ・タッチで描く。オペラの声は、すべて吹き替えで、ヒロインのカーリンを演じるG・クローズの吹き替えは、名歌手キリ・テ・カナワが担当した。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fat Chance", "THE ダイエット！", "", "", "http://image.eiga.k-img.com/images/movie/54418/315x210.jpg?1594", 52, "壮絶なダイエットに挑む女性監督自らが被写体となった、コメディタッチのセルフ・ドキュメンタリー。関口は子供のころから大柄であったが、23歳の時に渡ったオーストラリアで自分の体格を標準体型であると認識し、暴飲暴食を繰り返す生活を送っていた。やがて体重は90キロを超えてしまい、医者からは「このままだと長生きできない」と宣告され、ダイエットの一大決心をするが……。", "2009-07-25", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("弾痕", "弾痕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "CIAの工作員である滝村は、来日中の中共貿易促進使節員を助けた夜、中共側の工作員によって狙撃された。滝村は難を逃れたが、通りすがりの女性・沙織を負傷させてしまった。加山雄三が非情なスナイパーに扮して、殺人マシンの孤独を描いたクール・アクション。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUSSOLINI：ULTIMO ATTO", "ブラック・シャツ 独裁者ムッソリーニを狙え！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ファシストである“黒シャツ党”の党首として第二次世界大戦中欧州に君臨した、独裁者ムッソリーニの生きざまを描いた戦争大作。ムッソリーニ自身と、彼が愛した女クララ、そして彼を戦犯として処刑しようとするパルチザンたちの、希望と恐怖、心の葛藤をドラマティックに描く。ムッソリーニを演じたスタイガーのそっくりぶりに注目。", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUCI DEL VARIETA｀", "寄席の脚光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "共同監督作品であるとはいえ、フェリーニの記念すべき第１作。小さなレビューの一座が旅から旅への生活を送っている。そこへ入ってきた若くて美人のリリアーナに役者のケッコは心を奪われる。何とか彼女との新劇団結成にこぎつけるも、野心家のリリアーナはケッコを見捨てて大物プロデューサーと契約してしまう。傷心のままもとの劇団に戻ったケッコを、彼に惚れていた座長の娘メリーナは慰めるのだった……。男に踏みつけにされても男をうらむどころか優しく慰めてやるメリーナは、後年フェリーニ作品でマシーナが演じる女を予告しているかのようで、すでにフェリーニ色は強く出ているといえよう。", "1951-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少女革命ウテナ／アドゥレセンス黙示録", "少女革命ウテナ／アドゥレセンス黙示録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「美少女戦士セーラームーンR」などで才気あふれる演出を見せてきたアニメ作家、幾原邦彦が自身の手掛けたTVシリーズを斬新な発想で映画化した野心作。全寮制の学園に転校してきた男装の美少女ウテナを主人公に、彼女のかつての恋人や“薔薇の花嫁”と呼ばれる謎の少女たちとの関係が描かれていく。シアトリカルな舞台設定の中に展開する禁断の物語。耽美的な雰囲気が加速し、やがて迎える超現実的なクライマックスには、アニメーションにしかなしえない映像の魅惑が充満している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風を見た少年", "風を見た少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "C.W.ニコルのエコロジカルな同名小説を、大森一樹を総監督に据えてアニメ化した冒険ファンタジー。ストレートなメッセージよりも活劇としての面白さが躍動する一編になっている。“風をよむ”ことのできる少年アモンは、その能力を狙われ、物理学者である両親を殺されてしまう。アモンを捕えた独裁者ブラニックは、アモンの力で世界を征服しようとするが……。全編に乗り物好きな大森らしさが満載。声優陣も豪華で、アモン役の安達祐実とブラニックに扮した内藤剛志の声の対比も鮮やかに効いている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("脱走遊戯", "脱走遊戯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "囚人を刑務所から脱走させる“脱走仕掛人”の活躍を描くアクション映画。多額の報酬で脱獄の手助けをする神木隆は、ある脱獄グループの一員として働くことになった。しかし彼の本当の目的は、グループのボス・田所が狙う30億円のダイヤを横取りすることだったのだ。キレのいいアクションで魅せる千葉の悪党ぶりが見もの。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポルノスター", "ポルノスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "阪本順治作品の脚本などを手掛けてきた豊田利晃の監督デビュー作。ストリートの若者たちの救いようのない日常が、冷徹なバイオレンス描写を交えて浮き彫りにされる。渋谷の街でデートクラブを経営し、ヤクザに上納金を納めるチンピラ・上条。そんな彼の前に、無表情で殺人を犯すミステリアスな少年・荒野が現れる。反発し合いながらも荒野と行動をともにする上条だったが、やがて予期せぬ事件が……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Life Before Her Eyes", "ダイアナの選択", "", "", "http://image.eiga.k-img.com/images/movie/53958/315x210.jpg?3121", 90, "郊外の美しい住宅で優しい夫と愛する娘と暮らすダイアナ。しかし、彼女は17歳の時に高校で起こった銃乱射事件の記憶に今でもさいなまれてた。事件当時、親友のモーリーンといたダイアナは、犯人に銃を突きつけられ「どちらかを殺す」と迫られる。その時、彼女が選んだのは……。監督は、デビュー作「砂と霧の家」がオスカー3部門にノミネートされたバディム・パールマン。ユマ・サーマンとエバン・レイチェル・ウッドが30代と10代のダイアナを演じる。", "2009-03-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPEN HEARTS", "しあわせな孤独", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "交通事故で全身麻痺になった男性と、彼を懸命に支える若い女性。罪の意識に苛なまれる加害者の中年女性と、医師でもある夫。それぞれに苦しみを抱える４人が、許されない恋によってさらに追い詰められていく姿を描く痛切な愛のドラマ。ラース・フォン・トリアーをはじめとするデンマークの監督集団が掲げた“ドグマ95”の規則にのっとって撮影が行われた。結婚を控えたセシリとヨアヒムが突然不幸に見舞われる。ヨアヒムが車にはねられ全身麻痺になってしまったのだ。動揺するセシリは、加害者の女性マリーの夫で医師のニルスに精神的に支えられ、それはやがて愛へと変わっていく。", "2002-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOMMARNATTENS LEENDE", "夏の夜は三たび微笑む", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "弁護士フレデリックは16歳のアンを妻にして幸福だったが、ある日、昔の情婦デジレと再会。彼女とよりが戻り、一方アンもフレデリックの前妻の子ヘンリックと結ばれ駆け落ちしてしまう……。ベルイマンの喜劇では最高傑作の呼び声も高い作品である。", "1955-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャンプで逢いましょう", "キャンプで逢いましょう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "タレント田中律子の同名エッセイをもとに、若者たちの恋とアウトドアライフの魅力を描いた青春映画。広告代理店に勤める水穂は、憧れの先輩・瑛子とキャンプ場での結婚式を企画するが、アクシデントから瑛子本人が挙式を挙げることになる。ところが、企画はアウトドア・カメラマン豊彦のせいで大失敗に。先輩の結婚と企画の失敗に落ち込む水穂。そんな水穂を見かねた瑛子は、彼女をキャンプに連れ出すがそこで豊彦と再会し、次第に彼の素朴さに惹かれていくのだった……。カヌー、パラグライダーなどアウトドアスポーツも満載。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("РОБИНЗОНАДА，ИЛИМО ААНГШИЙСККЯ ДЕДУШКА", "ロビンソナーダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1920年頃。グルジアの山村に赴任してきたイギリスの電信技師クリスファーは、土地の娘アンナに恋をするが……。電柱の周囲３メートル以内に立てこもった彼の、奇妙なロビンソン・クルーソーの生活とその死の謎を追った、スリリングでロマンティックな珍作“ロビンソン物語”。", "1986-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鴛鴦楼／YOUNG　COUPLES", "北京物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "原題「鴛鴦楼（おしどりアパート）」の名の通り、あるアパートに住む６組のカップルのある日曜日の生活ぶりを描いた、６話構成オムニバス。言い争いの絶えない労働者夫婦、互いに無関心で行動も別々な夫婦、海外留学を目指し勉強に励む大学院生カップルなどをドキュメンタリー的視線で捉えていく。大学院生カップルの女役は「最後の貴族」のシャオ・シュン。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Strawberry Statement", "いちご白書", "", "", "http://image.eiga.k-img.com/images/movie/42428/315x210.jpg?3265", 109, "1968年に起きたコロンビア大学の学園紛争をモチーフとし、70年・第23回カンヌ国際映画祭で審査員特別賞を受賞した青春映画の名作。ごく普通の大学生サイモンは、学生運動に参加する女性リンダと知り合い、リンダと親しくなりたいがために闘争にも参加するようになる。しかし、サイモンの闘争に対する姿勢が気に入らないリンダはサイモンの下を去ってしまい、失望したサイモンは急速に学生運動にのめりこんでいく。バフィー・セント＝マリーが歌うテーマ曲「サークル・ゲーム」やジョン・レノンの「平和を我等に」など、時代を彩る名曲の数々も話題に。2011年、初公開から40年ぶりにニュープリントでリバイバル公開。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El laberinto del fauno", "パンズ・ラビリンス", "", "", "http://image.eiga.k-img.com/images/movie/34228/315x210.jpg?1372", 119, "メキシコ人の鬼才ギレルモ・デル・トロ監督によるダーク・ファンタジー。1944年、フランコ独裁政権下のスペイン。冷酷で残忍な義父から逃れたいと願う少女オフェリアは、昆虫に姿を変えた妖精に導かれ、謎めいた迷宮へと足を踏み入れる。すると迷宮の守護神パンが現われ、オフェリアこそが魔法の王国のプリンセスに違いないと告げる。彼女は王国に帰るための3つの試練を受けることになり……。", "2007-10-06", "9,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOPE AND GLORY", "戦場の小さな天使たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1939年、第二次世界大戦の勃発で、ロンドン郊外に住む７歳の少年ビルは、他の児童とともに疎開させられることになったが、片時も子供を手離したくない母の願いから、妹とともに町に残ることになる。しかし、夜ごと繰り返されるドイツ軍の空爆の恐怖の合間に、ビルが垣間見る光景は、いずれもショッキングな魅惑に満ちていた。若いカナダ兵と恋に落ち、焼け跡の物陰で彼と抱き合う15歳の姉の姿。父親の出征と久しぶりの帰休の喜び。不時着した敵機から落下傘で舞い降りたドイツ兵の瞳の意外なやさしさ。母親がかつて思いを寄せていたマックおじさんに連れられて４人で出かけたピクニックのつかの間の楽しさ。その留守中に家が火事で焼け、一家はのどかな田舎の祖父の家へ引っ越すことになるが……。悲惨であるべき戦争。しかし少年の幼い目を通して見た戦時下の人々の生と性の営みは豊かな人間味と詩情にあふれ、それが逆にこの映画を秀逸な戦争批判ドラマにしている。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OBLONG BOX", "呪われた棺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ポーの『長方形の箱』をもとにしたハマー・プロのホラー。アフリカで原住民の呪いのために醜くなった兄を連れ、ジュリアン卿が帰国する。だが、世間体をはばかった彼は兄を幽閉。さらに兄は脱出に失敗して生き埋めにされる。助かった彼は復讐を誓う。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DELTA FORCE", "デルタ・フォース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "アメリカの旅客機がアラブ系のゲリラによってハイジャックされた。すぐさま特殊コマンド部隊デルタ・フォースが出撃。機内に残された人々とゲリラ本部へと連れ去られた人質の救出のため、激しい戦いが続く。今はなき量産プロダクション、キヤノンフィルムの総帥自らの手によるB級アクション映画。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Petit Nicolas", "プチ・ニコラ", "", "", "http://image.eiga.k-img.com/images/movie/55598/315x210.jpg?4137", 91, "ルネ・ゴシニとジャン＝ジャック・サンペによるフランスの国民的絵本「プチ・ニコラ」を実写映画化。「モリエール　恋こそ喜劇」のローラン・ティラールがメガホンをとる。1960年代のパリ。想像力豊かな少年ニコラは、優しい両親と友達に囲まれ、幸せいっぱいの毎日を過ごしていた。そんなある日、ニコラは両親の会話を盗み聞きし、弟が生まれると信じこむ。弟が生まれたら森の中に捨てられるかもしれないと不安になったニコラは、両親の気を引き、母親の出産を阻止しようと奔走するが……。", "2010-10-09", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅", "紅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "涙をなくした女・サキと、去勢された男・政弘とのプラトニックなメロドラマ。対立組織の幹部襲撃に失敗した報復として去勢されてしまった元ヤクザの政弘は、途方に暮れた生活を送り、同じ命令を受けながら逃げてしまった進次もまた行き場のない生活を送っていた。そんな二人の前に涙の代わりに自分を傷つけ血を流す女サキが現れ……。スタッフ、キャストが投資という形を取り、上映後一定の利益が出た所であらかじめ決めた配分率にしたがって配当を得るシステムで製作された作品。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blitz", "ブリッツ", "", "", "http://image.eiga.k-img.com/images/movie/56388/315x210.jpg?2406", 97, "「トランスポーター」シリーズのジェイソン・ステイサムが主演するクライムサスペンスアクション。ロンドン市警の荒くれ刑事ブラントは、常に犯罪者を徹底的に打ちのめしてきた。そんなある時、ロンドン市内で警官ばかりを狙う連続殺人事件が発生。タブロイド紙の記者ダンロップが「ブリッツ」と名乗る犯人から連絡を受ける。ブリッツはダンロップに記事を書かせることで予告殺人を行い、警察を挑発。ブラントは次々と仲間を失っていき、やがてブリッツの最後の標的が明らかになる。", "2011-10-15", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The War Zone", "素肌の涙", "", "", "http://image.eiga.k-img.com/images/movie/1738/315x210.jpg?4099", 100, "15歳のトムと18歳の姉ジェシーは、両親と共にロンドンからデボン郊外へ引っ越したばかり。二人は今でもソファでじゃれあうほど仲のよい姉弟だ。父は事業の巻き返しに忙しく、母は3人目の子供を妊娠しており、家族は赤ん坊の誕生を心待ちにしていた。赤ん坊が無事に生まれ家族が幸せに包まれたある日、母との買い物から戻ったトムは、父とジェシーの間にある恐ろしい秘密を偶然窓の外から垣間見てしまう。", "2000-09-02", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUNCHIES", "まんちいず", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ペルーの山奥で発見された珍獣“まんちいず”がアメリカの片田舎で大暴れ。「グレムリン」の亜流だが、これが初監督のB・ハーシュは「グレムリン」の編集者だった女性。1987年東京国際ファンタスティック映画祭で上映された。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Power of the Press", "渦巻く都会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "市長選をめぐる陰謀に巻きこまれる新聞記者を描いたサスペンスドラマで、監督は後の名匠フランク・キャプラ。駆け出しの新聞記者クレム・ロジャースは、検事殺害事件についての取材をまかされる。事件現場となった検事宅を訪れた彼は、市長候補アットウィルの娘が窓から出てくるところを目撃し、早速それを記事にしようとするのだが、実はこの事件はもう1人の市長候補ブレークがアットウィルを陥れるために企んだものだと知る。", "1928-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本一のホラ吹き男", "日本一のホラ吹き男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "シリーズ中最高傑作との声もある痛快爆笑の一編。東京オリンピックの三段跳びのホープとして注目を集める初等は、アキレス腱を切って入院。入院中に祖先の伝記を手に入れ“大ボラを吹けど必ず成功し……”という一節に勇気づけられ、日本一の大会社に入社を決意する。入社試験には失敗するが、社長にゴマをすり正社員の座につき、超ハードワークであっという間に課長に出世してしまうのだった……。クレージー・キャッツ最盛期にあたる作品で、超過密スケジュールのあい間をぬって撮影され、植木等など本当に入院しているのに強引に引っぱり出されたという。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STANDING　IN　THE　SHADOWS　OF　MOTOWN", "永遠のモータウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1960～70年代の全盛期に、ビートルズをも凌ぐ数のヒット曲を連発していた伝説のレコードレーベル“モータウン”。その独特のサウンドを生み出したバックミュージシャンたちに焦点を当てた音楽ドキュメンタリー。ひたすらに音楽を愛し、素晴らしい演奏を残しながらも、クレジットすら得られなかった彼らの再結成コンサートは、涙ナシには見られない。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("静かなるドン THE MOVIE", "静かなるドン THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1991年にVシネマとして製作され評判となった新田たつおの同名漫画の映画版。Vシネマと同様に香川照之、喜多嶋舞の主演コンビが10年ぶりに復活。下着デザイナーという昼の顔を持つ新鮮組組長・近藤は組を捨て、明美とまっとうな人生を送るかどうか迷っていた。そんな折、彼の職場が解散の瀬戸際に立たされ、一方では鬼州組との抗争も勃発した。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORT APACHE", "アパッチ砦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "公開の順は逆になったが「黄色いリボン」「リオ・グランデの砦」と続くフォード監督の騎兵隊３部作の第１作。砦の新任隊長H・フォンダと先任大尉J・ウェインの対立から、最後の騎兵隊の全滅まで、フォードがヒューマニティーあふれた騎兵隊映画に仕上げている。ペドロ・アルメンダリスらの助演陣も充実。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Know What You Did Last Summer", "ラストサマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "大ヒットホラー、「スクリーム」で注目を集めたケヴィン・ウィリアムソン脚本によるショッキング・ホラー。高校生活最後の夏を楽しんでいた二組のカップルが、パーティの帰り道で交通事故を起こしてしまう。はねた男を海へ捨て、４人だけの秘密にすることを誓い合った彼らに１年後に脅迫状が届く。“去年の夏、何をしたか知っているぞ……”。不気味なメッセージに脅える彼らをあざ笑うかのように、犯人の魔の手は刻一刻と迫る……。次々と起こる猟奇殺人と、至る所に仕掛けられたワナ。恐怖をあおり立てる凝ったショック演出と犯人探しのミステリーに一瞬たりとも目が離せない。TVドラマや歌手としても活躍するジェニファー・ラヴ・ヒューイットら、犯人に果敢に挑むアイドル俳優たちの熱演にも注目。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("We Were Strangers", "ストレンジャーズ6", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "　1930年代のキューバ。弟を秘密警察によって殺された女性が復讐を誓い、反政府組織に加わる。彼らは要人暗殺を目論むが……。キューバを独裁から解放しようとする革命家を描いた人間ドラマ。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A REVENGRS TRAGEDY", "リベンジャーズ・トラジディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「レポマン」や「シド・アンド・ナンシー」など、常に野心的な作品を発表してきた英国の鬼才アレックス・コックス。2011年のリバプールを舞台に、地位を奪われ妻を殺された男の復讐を、エリザベス朝時代の戯曲をベースにクールな演出で描き出した作品だ。2011年のリバプールは、すい星の衝突により大きな被害を受けていた。そんな荒廃した街に、10年前に愛する女を殺され、地位も奪われたヴィンディチが帰ってきた。彼はすべてを奪った男デュークへの復讐の炎をたぎらせていた……。", "2002-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・人間革命", "続・人間革命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 159, "創価学会の誕生を描いた「人間革命」の続編。創価学会を大きくしようと努力を続けていた戸田は、勉強会で熱心に戸田の話を聞いていた山本伸一を経営する正学館に採用、『少年日本』の編集長に抜擢する。しかし、事業はうまくいかず、営業停止になってしまう。会員でヤクザの組長である島谷が資金援助を申し込むが、戸田は断る。数日後、島谷は抗争により死亡してしまい、そのことがもとで、戸田は宗教の道に専心することを決意するのだった。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大利根の対決", "大利根の対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "３年前に誤って恋女房のお千代を殺し、今は賭博荒らしで追われる身となり果てた左腕のない渡世人・朝吉を主人公にした股旅もの。戦後製作を再開した日活が新国劇と提携、島田正吾をはじめ、新国劇役者総出演でタップリと殺陣を披露する。脚本は名匠・伊藤大輔。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("贅沢な骨", "贅沢な骨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "行定勲監督がなじみあるスタッフ、キャストとともに自主製作で撮り上げた青春映画。奔放なホテトル嬢ミヤコと、内気な女の子サキコはルームメイト同士。そんな二人の穏やかな共同生活は、ミヤコの客である不思議な男、新谷の出現によって微妙に変化していく。二人のヒロインの愛と孤独を繊細なタッチで描出。主役３人が抜群の存在感を発揮しているほか、原因不明のノドの激痛に襲われたミヤコが金魚のように呼吸する仕草など、暗示的なシーンが随所に散りばめられている。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLY！　平凡なキセキ", "FLY！　平凡なキセキ", "", "", "http://image.eiga.k-img.com/images/movie/57748/315x210.jpg?2784", 106, "吉本新喜劇の座長・小籔千豊の主演で、冴えない地球人と宇宙人の友情とそれぞれの恋を描いたコメディドラマ。大阪の町工場の冴えない工員・満男は、小学1年生の息子を女手ひとつで育てる事務員のななみに思いを寄せていた。そんなある日、満男は遠い星から偶然地球に落ちてきた宇宙人シタカと出会い、自分と同じく冴えなくてモテないシタカと友情を育むが……。ヒロインのななみを演じるのは、自身初の母親役となった相武紗季。宇宙人シタカ役に温水洋一。そのほか大杉漣、笹野高史、本仮屋ユイカ、西田敏行らが出演。", "2012-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泣蟲小僧", "泣蟲小僧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "啓吉は気が弱く、すぐに泣き出してしまうので泣虫小僧と呼ばれていた。父はなく母と二人だけで暮らしてきたが、母の再婚により３人の叔母の家に順番に預けられる。やっと母のもとへ戻ったと思ったら、両親は遠国へ行くことに……。再び啓吉は３人の叔母の家をめぐることになる。母と子の絆を模索する家族人情ドラマ。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Divine Intervention-Yadon Illaheya", "D.I.", "", "", "http://image.eiga.k-img.com/images/movie/1358/315x210.jpg?2924", 94, "イスラエルに住むエリアが愛する女性マナルは、パレスチナ在住。会えるのは2国間のチェック・ポイントだけの2人は、愛を貫けるのか？　全編を通して主人公もヒロインも台詞ナシで、サイレント映画のコメディを思わせる作劇術。監督スレイマンは60年、イスラエルのナザレ生まれ。82年からニューヨークで活動するが、93年に帰国。96年の長編第1作「消滅の年代記」でベネチア映画祭新人監督賞を受賞、本作でカンヌ映画祭審査員賞を受賞。", "2003-04-26", "2,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五重塔", "五重塔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "原作はあまりにも有名な幸田露伴の同名の中編小説である。監督は松竹蒲田調でならした大御所・五所平之助、主演には「残菊物語」（溝口健二監督）の花柳章太郎と森赫子のコンビが配された。また脚色には川口松太郎が、美術考証には木村荘八画伯が参加するというぜいたくな作品でもある。感応寺という寺に五重塔が建立されることになった。仲間からは馬鹿扱いされている十兵衛という大工がその建立に名乗り出たため、自分が指名されると思い込んでいた名うての棟梁・源太は驚く。寺の上人は思うところあって十兵衛に建立を命ずる。怒った源太の弟子たちは十兵衛の仕事を妨害するが、十兵衛は不屈の精神でついに五重塔を完成させるのだった。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎の縁談", "男はつらいよ・寅次郎の縁談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "12年ぶりに、松坂慶子が２度目のマドンナ出演。大学卒業を控えながら、就職が決まらない満男は、思い悩んで家出。満男捜しを請け合った寅次郎は、瀬戸内海の小島・琴島で漁師をしている満男を発見。満男は、島の看護婦・亜矢に惚れていて、寅次郎も満男が世話になっている旧家の娘・葉子に恋をしてしまう……。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン　探偵たちの鎮魂歌（レクイエム）", "名探偵コナン　探偵たちの鎮魂歌（レクイエム）", "", "", "http://ecx.images-amazon.com/images/I/61KpT5WO6IL._SL210_.jpg", 111, "青山剛昌の大ヒット漫画を原作とするテレビアニメ「名探偵コナン」の劇場版シリーズ第10作。ナゾの依頼人から招待を受け、横浜の高級ホテル「レッドキャッスル」へやって来た小五郎とコナンたち。ところが依頼人が仕かけたワナにかかり、少年探偵団の仲間たちを人質に取られてしまう。彼らを救う条件は、ある事件のナゾを12時間以内に解きあかすこと。コナンと小五郎は、依頼人からのヒントを頼りに捜査を開始するが……。", "2006-04-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hitcher", "ヒッチャー（2007）", "", "", "http://image.eiga.k-img.com/images/movie/53078/315x210.jpg?1865", 84, "86年に公開されたサスペンス・アクション「ヒッチャー」を、ハリウッドのヒットメイカー、マイケル・ベイ製作でリメイク。大学生カップルのグレースとジムは、ドライブの途中で“ジョン・ライダー”と名乗る男性ヒッチハイカーを乗せる。だがライダーの正体は、近頃巷を震撼とさせている連続殺人犯だった……。オリジナルでルトガー・ハウアーが演じた殺人鬼に扮するのは、「ロード・オブ・ザ・リング」のショーン・ビーン。", "2007-11-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SWORD AND THE SORCERER", "マジック・クエスト ～魔界の剣～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "中世の暗黒時代。伝説の武器を持つ若き王子と邪悪な王、そして醜悪な妖術使いの三つ巴の死闘が繰り広げられるマジカル・アドベンチャー。「ウィロー」「プリンセス・ブライド・ストーリー」などと並ぶ“剣と魔法”ものの一編で、全米で大ヒットを記録した。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON’T GIVE UP THE SHIP", "底抜け船を見棄てるナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ディーン・マーティンとのコンビで一世を風靡したルイスが単独出演した“底抜け”シリーズの一編。行方不明の駆逐艦捜しに、当時の乗組員であるジョン大尉が呼び出されるが、彼は記憶が定かでない……。監督は、“底抜け”シリーズを数多く手掛けているN・タウログ。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK SNOW", "ドラッグ・シティL.A.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "“麻薬都市”ロサンゼルスで、コカイン密売組織に立ち向かう男が主人公のハード・アクション映画。この手の犯罪アクションに多い、ヒーローのトラブル巻き込まれ型映画だが、後半でのギャング同士の裏切りや計略で先の読めないストーリー展開が楽しめる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SQUANDRERS", "横領", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "逮捕直前のマフィアが、車に大きなバッグを隠すのを監視カメラで目撃したケビン。警察にはそのシーンをカットしたビデオを渡し、まんまとそのバッグをせしめる。そのバッグには500万ドルもの金が入っており、ケビンは仲間とその金を山分けする。一方、金を横領されたことを知ったマフィアと、ビデオの編集に気付いた警察は、ケビンらの追跡を始める。スティーヴ・マックィーンの息子チャド、マーティン・シーンの実弟J・エステベス、P・スウェイジの実弟D・スウェイジなど、有名スターのファミリー俳優勢ぞろいといった感じのキャスティングもユニークな作品。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHAT PRICE GLORY", "栄光何するものぞ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "第一次大戦中のフランスの田舎町。米軍のフラッグ大尉とクワート曹長は昔からのライバルで、この町でも一人の娘シャルメインをめぐって争っていた。二人は相手が戦地に出ている間に娘を自分のものにしようと躍起になるが……。ジョン・フォード監督お得意の荒くれ男たちの友情を豪快に描く佳作。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEET THE APPLEGATES", "アップルゲイツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "どこから見ても絵から抜け出したような典型的なアメリカン・ファミリーが、とある片田舎の町に引っ越してくる。しかし彼らは、乱開発される熱帯雨林を守ろうと、原子力発電所を破壊するためにアメリカの町にやって来たアマゾンの謎の使者だった。随所に風刺をきかせた奇想天外なコメディ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("September Tapes", "セプテンバー・テープ", "", "", "http://image.eiga.k-img.com/images/movie/33668/315x210.jpg?4661", 95, "9．11テロの1年後に紛争真っ只中のアフガニスタンで撮影を行なったドキュメンタリー風ドラマ。アフガニスタンの国境近くで発見された8本のビデオテープ。それは、オサマ・ビンラディンの行方を追う途中で消息を絶ったアメリカ人監督、ドン・ラーソンのものだった……。本物の報奨金ハンターや武器ディーラーとの緊張感溢れるやり取りや実際の銃撃戦など、情勢の不安定なアフガニスタンの現状をリアリティたっぷりに映し出す。", "2006-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FIVE PENNIES", "5つの銅貨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1920年代アメリカのジャズ界で活躍した名コルネット奏者レッド・ニコルズの半生を描いた伝記映画。歌手ボビーとの結婚、バンド“五つの銅貨”の巡業、そして娘の小児マヒという不幸……。D・ケイの絶妙な演技による笑いと涙にあふれた楽しい音楽映画だ。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAPER MOON", "ペーパー・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "白黒スタンダードの素朴な画面に、1930年代、大不況のアメリカ中南部で、聖書を売り歩くサギ師のモーゼと、親のない少女アディの珍道中と心の交流を綴った珠玉作。“紙でできた月”同様、ホンモノ以上に温かい、ニセの親子関係が胸を揺さぶる。サンセバスチャン映画祭で３つの賞を受賞している。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TJOET NJA，DHIEN", "チュ・ニャ・ディン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "19世紀末、インドネシア・スマトラ島北部で起きた対オランダ戦争を勇敢に戦い抜き、今日までその名を伝えられている女性チュッ・ニャ・ディンの激動の半生を映画化したもの。夫を敵弾で失った彼女は、そのあとを継ぎ軍を率いているが、激しい戦いの連続に次第に体を弱らせていく。インドネシア映画祭で主要８部門を独占した作品。", "1988-01-01", "73", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("We Will Rock You: Queen Live in Concert", "QUEEN ROCK MONTREAL cine sound ver.", "", "", "http://image.eiga.k-img.com/images/movie/53788/315x210.jpg?1906", 95, "現在も世界中に熱狂的なファンを持つ伝説のロックバンド「QUEEN」が、81年にカナダ・モントリオールで行ったライブをフルデジタルで再現。幸運にも保存されていた原盤フィルムを、ギタリストのブライアン・メイとデジタル技術者の手でリマスターし、映画館の最新音響システムで上映する。ボーカルのフレディ・マーキュリーをはじめ、当時全盛期にあった彼らのパワフルなパフォーマンスが圧倒的な臨場感でスクリーンに甦る。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の袋", "花の袋", "", "", "http://image.eiga.k-img.com/images/movie/54268/315x210.jpg?4234", 0, "自主制作映画団体「チーズfilm」代表の戸田彬宏が監督を務める奈良を舞台にした青春映画。引っ込み思案な女子高生の亜矢香は、同じ陸上部の洋介に恋をしている。明るくてお節介な恵、大人びたアネゴ肌の宏美ら、亜矢香を取りまく友人のおかげで2人の恋愛は順調だったが、それぞれが他人に言えない悩みを抱えていた……。", "2008-12-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空へ　救いの翼 RESCUE WINGS", "空へ　救いの翼 RESCUE WINGS", "", "", "http://image.eiga.k-img.com/images/movie/53888/315x210.jpg?2864", 108, "航空救難隊の活躍を描いたTVアニメ「よみがえる空／RESUE WINGS」を、「戦国自衛隊1549」の手塚昌明監督が実写化したレスキュー・アクション。主演は映画初出演の高山侑子、共演に「ラストゲーム／最後の早慶戦」の渡辺大、「砂時計」の井坂俊哉ら。かつて母の命を救ってくれた救難隊へ憧れた遥風は、救難ヘリ初の女性パイロットとなり、基地中の批判や期待を背負いながら厳しい訓練をこなしていた。そして初めての実任務の日がやってくるが……。", "2008-12-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フリテンくん", "フリテンくん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ギャンブル雑誌を中心に発表された面白四コマ漫画を原作にした、笑って笑って笑い涙が止まらない快作コメディだ。OVAでの先行作品『フリテンの麻雀放浪記』とか『フリテンの白い巨塔』とかを観れば分かるように、映画のパロディも入った劇場版で、とにかくとても楽しい仕上がり。７編のショートフィルム集ではあるが、実写版の喜劇短編集以上に笑いのクオリティーは高い。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ある大阪の女", "ある大阪の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "貧苦の中を、だらしない男どもをダシに身体を張って生きる悪女をじっくり描いた人間ドラマ。アヤコは貧民街育ち。婚約者の進は借金に苦しみ、弟はヤクザで父はヤケ酒をあおっている。彼女は勤務会社の社長から預かったヘソクリを使い込もうとしてバレるが、逆に社長にとり入って彼をだまし、進の借金返済をもくろむ。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAGE FRIGHT", "舞台恐怖症", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「ロープ」「山羊座のもとに」などで長回し技巧を試行錯誤してきたヒッチコックが、久しぶりに編集の妙味を存分に利用した作品。演劇学校の学生イヴは同級生のジョナサンを殺人事件に巻き込まれた窮地から救ってやる。スターのシャーロットの夫が何者かに殺され、その疑いがかかっているという。イヴは父親や刑事の手助けにとメイドになってシャーロットのもとへと乗り込む。謎解きを中心にすえ、演劇界の舞台裏、複雑な人間関係、大道具・小道具などをサスペンスの材料に取り入れた作品。フラッシュバックそのものが手がかりになることに注目。", "1950-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Reign of Assassins", "レイン・オブ・アサシン", "", "", "http://image.eiga.k-img.com/images/movie/56588/315x210.jpg?4581", 120, "「レッドクリフ」のジョン・ウー監督が、香港のミシェル・ヨー、韓国のチョン・ウソンらアジアのスターを迎えて描く歴史アクション大作。明王朝時代、ナゾの暗殺組織に属する女刺客の細雨（シーユー）は、忌まわしい過去と決別するため、手に入れた者に絶大な力を与えるといわれる古代武術の達人・達磨大師のミイラを組織から奪い、脱走する。やがて都会で心優しい配達人の阿生（アシャン）と出会い結ばれた細雨だったが、組織の殺し屋たちが迫っていた……。台湾の気鋭監督スー・シャオピンが共同でメガホンをとる。", "2011-08-27", "8,6,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水戸黄門", "水戸黄門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "常陸の国の西山荘で、悠々自適の生活を送る水戸黄門。そこに加賀百万石、前田家城代家老・奥村作左衛門の息女・由美が現れ、加賀百万石が次席家老・村井主水らに乗っ取られそうとの知らせを聞く。敵の目をくらますため途中で出会った“ニセ黄門一家”と入れかわって加賀へ赴く黄門一行だが……。TVシリーズとは違い、コメディ・スパイスをふりかけた。ビデオタイトルは「東野英治郎の水戸黄門」。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プレイガール", "プレイガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "昭和40年代に、５年半にわたり放映されたTVドラマ『プレイガール』を、佐藤江梨子ら今が旬のグラビア・アイドル５人を主演に迎え映画化。可憐なコスチュームと、ド派手なアクションが見ものだ。召集がかかると特殊犯罪捜査チーム“PG＝プライベート・ガバメント”として極秘任務に就く５人。今回の任務は大企業の家族を狙った連続誘拐事件。しかし犯人のボスはメンバーの元同僚であり、リーダーの遙がかつて愛した人だった……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANATOMY", "アナトミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "名門医科大学のアナトミー(解剖学教室)を舞台にしたホラー。ハイデルベルグの名門医科大学で、解剖研究コースに参加することになった医学生パウラ。大学へ向かう途中、心臓に持病を持つデビッドと知り合う。だが、デビッドと再会したのは授業の教材用死体としてだった。心臓を摘出するよう教授から指示されたパウラは、彼の血液に異常を感じてひそかに調査を始める。精巧な人体模型や実験シーンなど、グロテスクだが洗練されたアーティスティックな映像が目を引き付ける。", "2000-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逃亡くそたわけ 21歳の夏", "逃亡くそたわけ 21歳の夏", "", "", "http://image.eiga.k-img.com/images/movie/34258/315x210.jpg?2295", 99, "芥川賞作家・絲山秋子の小説を原作に、精神病院から逃亡した男女の逃避行を描いたロードムービー。自殺未遂を図り、福岡の精神病院に入院させられた21歳の大学生・花ちゃん。脱走を企てた彼女は、名古屋人なのに標準語しか話さない鬱病の青年・なごやんを強引に誘って病院を抜け出す。2人はなごやんのおんぼろ車ルーチェで九州縦断の旅をはじめるが……。主演は「さくらん」の美波と「夕凪の街　桜の国」の吉沢悠。", "2007-10-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHAT A FAMILY", "喜劇 ピンぼけ家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "文革が終結し、再び喜劇が作られるようになった頃の家族コメディの佳作。工場の現場主任の胡さん一家は４人家族。彼の娘で同じ工場に働く模範労働者の嘉英。お調子ものの息子、嘉奇。子供を溺愛する母。胡さんは娘を早く嫁にやろうと、その恋人に迫るのだが……。", "1979-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Good Day to Die Hard", "ダイ・ハード5（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("１１’０９”０１", "11'09\"01／セプテンバー11", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "2002年の９月11日に一日だけ劇場公開され、同日TV放映もされたオムニバス映画。ニョーヨークの世界貿易センタービルが倒壊したテロリスト事件をモチーフに、世界11ヵ国の監督が11分９秒１フレームの短編を制作。意外なハッピーエンドに持っていくショーン・ペンや蛇になった男の物語で聖戦を批判する今村昌平など、それぞれの視点が際立つ見ごたえのある佳作だ。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOOD", "フード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "世界的に名高いアニメーション作家J・シュヴァンクマイエルの短編。朝食のテーブル。男１の首に指示書が下がっている。そこへ男２がやって来て、指示書通りに食事を終えると、男２が硬直する。男１が男２の首に指示書を下げて部屋を出て行くと、男３が入って来て、指示書通りに食事を始める。昼食のレストラン。ウエイターに無視された男１と男２は、花を食べ、最後には自分の衣服やナイフ・フォークまで食べ尽くす。そして最後に男１の目は男２に向けられるのだった。夕食、義手の男が大盛の人間の手を食べている。隣ではスポーツ選手が足を食べ、赤いマニキュアの女がレモンを食べ、その隣では男がぺニスを食べているのだった。DVDは「ヤン・シュヴァンクマイエル　短編集」に収録。", "1992-01-01", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("満願旅行", "満願旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "寝台特急“はやぶさ”の独身専務車掌・村上洋介と彼にぞっこん惚れ込んでいる食堂車ガールの八重子、洋介が一目惚れした見合いの相手・すみ江との、恋の三角関係を描くコメディ。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("土", "土", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "自然主義文学の名作として名高い長塚節の同名小説を、巨匠・内田吐夢が映画化。厳しい農村の生活を、四季の推移の中に描く。地味な内容ながら大ヒット、高い評価を受けて日本の“農民映画”の伝統を築くに至る。オリジナル版は消失、現行版は50分短い。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO HOLDS BARRED", "ゴールデンボンバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "世界チャンピオンの意地と、愛する家族への忠義心から否応なく恐ろしく狂暴な男たちとの戦いに挑むプロレスラー“リップ”の姿を描いたアクション。現役のプロレス界のスーパースター、ホーガンが初主演。ハーレーにまたがって街を暴走したり、恋に落ちて目を潤ませたりとアクションにドラマに八面六臂の大活躍。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フランキー・ブーチャンの殴り込み落下傘部隊", "フランキー・ブーチャンの殴り込み落下傘部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "フランキー堺と“ブーチャン”こと市村俊幸のコンビによる“軍艦旗”もの第３作。ドジでマヌケな門馬三太郎と間々田伍作は、落下傘部隊へ転属となり、相変わらず失敗を繰り返すが、ひょんなことから大手柄を立てる。フランキー堺はこの作品を最後に日活を退社し東宝へ移った。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE YOUNG POISONER’S HANDBOOK", "グレアム・ヤング毒殺日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1960～70年代に多くの人を毒殺し、1990年に刑務所で謎の死を遂げたイギリスの毒殺魔グレアム・ヤングの実話をもとに映画化した作品。幼い頃から薬品に興味を持っていたグレアムは、14歳の時、硫化アンチモンという薬品と出会い、その怪しい魅力にひきつけられる。ある時、グレアムは女の子とデートした友人に嫉妬し、彼にアンチモンを塗ったサンドイッチを食べさせる。次に彼は、隠していたポルノ雑誌を燃やした母親にアンチモン入りのチョコレートを食べさせ、さらに医者がくれた薬にも混ぜて母親を毒殺する。すっかり毒殺の魅力につかれた彼は、次に父や姉に狙いを定めるが、警察に疑われ逮捕される。異常性格と判断されたグレアムは、精神病棟で治療され、やがて釈放される。社会復帰もし、職場の仲間ともうまくいっていたが、偶然、薬品棚にある緑色の瓶を見たことで、彼の心は大きく動揺するのだった……。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRST LOVE", "ファースト・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ニューイングランドの大学を舞台に、古風なモラリストで変わり者エルジンの初恋と、プレイボーイのデビッドとの友情を描く、さわやかな一編。ハロルド・ブロドキーの短編小説を映画化した、女流監督J・ダーリングの劇場用映画デビュー作。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CERCLE PARFAIT", "パーフェクト・サークル　戦場の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "第10回東京国際映画祭でグランプリと監督賞をダブル受賞するなど、世界各国の映画祭で高い評価を得たヒューマン・ドラマ。サラエボ出身のA・ケノヴィッチ監督が、ユーゴ紛争に苦しめられながらも現地で生き抜く市民の生活を、平和への祈りを込めて描き出す。1992年の冬、妻子を国外に送り出し、ボスニアの首都サラエボに一人とどまった詩人ハムザ。そんな彼の家に戦争で肉親を失った幼い兄弟ケリムとアーディスがさまよい込み、３人は家族のような生活を始めるのだが……。", "1997-01-01", "39,40,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BADBOYS", "BADBOYS", "", "", "http://image.eiga.k-img.com/images/movie/55548/315x210.jpg?3390", 93, "1988～96年に「ヤングキング」（少年画報社）で連載され、単行本の累計発行部数4000万部を記録した田中宏の人気不良漫画を映画化。暴走族のナンバーワンチームを率いる段野秀典が君臨する広島に、かつて好敵手として恐れられた野村豊が戻ってくる。激しい勢力争いを繰り返しながら成長する男たちの姿を描く。三浦友和と山口百恵の次男で俳優の三浦貴大が主演する。", "2011-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fog", "ザ・フォッグ", "", "", "http://image.eiga.k-img.com/images/movie/33578/315x210.jpg?1308", 99, "ジョン・カーペンター監督による同名ホラーのリメイク作品。海辺の小さな町、アントニオ・ベイ。生誕100周年を迎えるこの町に、不気味な霧と共に怨霊たちが姿を現した。町の人間に次々と襲いかかる怨霊たち。彼らは町の歴史から葬られた忌まわしい沈没事故の犠牲者だった……。メガホンを取るのは「スティグマータ／聖痕」のルパート・ウェインライト監督。主演はテレビシリーズ「ヤング・スーパーマン」のトム・ウェリング。", "2006-07-29", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUSTIN POWERS", "オースティン・パワーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "M・マイヤーズが製作、脚本を兼ね、なおかつ主人公のスパイ、オースティン・パワーズと敵の組織のボス、ドクター・イーブルの二役を演じた脳天気コメディ。1967年、オースティンは宿敵ドクター・イーブルを追い詰めるが、彼は冷凍冬眠装置付きロケットで逃亡。オースティンも冷凍冬眠することに。そして1997年、イーブルもオースティンも冬眠から目覚めるが、30年間のギャップは非情なもの。モテモテだったはずのオースティンは今や死語となった1960年代の流行語を連発、新しいパートナーのバネッサも呆れ果てる始末。イーブルもトンチンカンな作戦を考えては部下にバカにされてしまう。そんな二人がついに宿命の対決を迎えるが……。", "1998-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春☆金属バット", "青春☆金属バット", "", "", "http://image.eiga.k-img.com/images/movie/33408/315x210.jpg?4319", 96, "古泉智浩の同名コミックを、「アンテナ」の熊切和嘉監督が映画化。ひょんなことから2人組バット強盗として警察から追われるハメになった27歳のコンビニ店員・難波と酒乱の巨乳娘エイコ。彼らを見つけた不良警官・石岡は、難波の高校時代の野球部仲間だった……。3人の冴えない主人公たちが、野球を通して青春時代の輝きを取り戻すまでを描いた青春映画。フォーク・ロック・バンド野狐弾の竹原ピストルが難波役で映画デビュー。", "2006-08-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Banlieue 13: Ultimatum", "アルティメット2 マッスル・ネバー・ダイ", "", "", "http://image.eiga.k-img.com/images/movie/54478/315x210.jpg?2636", 101, "リュック・ベッソンが製作・脚本を手掛けたCGに頼らない肉体派アクションの続編。主演は前作に引き続き「ダイハード4.0」のシリル・ラファエル、「バビロンA.D.」のダビッド・ベル。近未来のパリ、さまざまな人種が混在するパリ郊外のバンリュー13地区はギャングの巣窟と化していた。ある日、13地区で警官が殺された事件をきっかけに地区の一掃計画が持ち上がる。その裏に陰謀を嗅ぎつけたレイトと潜入捜査官のダミアンは、再びタッグを組んで闇の組織に挑む。", "2009-09-19", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TILL　HUMAN　VOICES　WAKE　US", "記憶のはばたき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「イノセント・ボーイズ」などで脚本家として活躍するM・ペトローニが、大学時代に執筆した脚本を自ら映画化した神秘的な物語。15歳の時に愛する少女シルヴィに起こった事故のせいで、彼女の記憶を心の奥底に閉じ込め、感情までも押さえ込んでしまったサム。20年後、精神分析医となった彼はあの事故以来、遠ざかっていた故郷に戻る。そこで、サムはルビーと名乗る謎めいた女性と出会う。行方不明になったシルヴィを思わせる言動を繰り返すルビー。果たして彼女は……。淡くも切ない鎮魂と再生の物語が、風景画を思わせる美しい映像で綴られる。", "2001-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("背", "背", "", "", "http://image.eiga.k-img.com/images/movie/53678/315x210.jpg?1883", 48, "ぴあフィルムフェスティバル2007で2冠を獲得したサスペンススリラー。高校を卒業したばかりのサトルは、路上で女性が酔いつぶれているのを発見し、アパートまで送り届けることに。そのまま女性の部屋にあがりこんだサトルは、彼女に誘惑されるままに唇を重ねてベッドに押し倒すが、ちょうどその時、アパートに黄色い靴下の男が訪ねてくる。なんとかその場を逃げだしたサトルだったが、それ以来、行く先々で黄色い影がつきまとうようになり……。", "2008-08-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クラヤミノレクイエム", "クラヤミノレクイエム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「新・悲しきヒットマン」「鬼火」などの脚本家、森岡利行の監督デビュー作。彼自ら主催する劇団“STRAYDOG”で1994年の初演以来、再演を重ねる人気作を映画化。現在廃館になった中野光座でロケを敢行、劇場という空間を巧みに利用した印象的な映像が展開される。音楽は、シーナ＆ザ・ロケッツの野島健太郎が担当している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pirates of the Caribbean: On Stranger Tides", "パイレーツ・オブ・カリビアン　生命（いのち）の泉", "", "", "http://image.eiga.k-img.com/images/movie/54518/315x210.jpg?3810", 137, "ジョニー・デップ主演の世界的大ヒットシリーズ初の3D版で、「呪われた海賊たち」（2003）、「デッドマンズ・チェスト」（06）、「ワールド・エンド」（07）に続く第4作。イアン・マクシェーン演じる史上最強の敵“黒ひげ”やペネロペ・クルス演じるナゾの女海賊“アンジェリカ”といった、新キャラクターが多数登場する。前作のラストで暗示された永遠の命をもたらすという「生命（いのち）の泉」をめぐり数々の野望が渦巻くなか、海賊ジャック・スパロウが新たな冒険へと旅立つ。", "2011-05-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE LIEU DU CRIME", "夜を殺した女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「海辺のホテルにて」「夜の子供たち」などでもコンビを組み、深い信頼関係で結ばれた名匠A・テシネと大女優C・ドヌーヴが1986年に取り組んだ官能サスペンス。夫と離れて暮らし、ダンス・クラブを経営するリリの前に、マルタンという逃亡中の犯罪者が現れる。つかの間の激しい情事に溺れる二人。しかしマルタンと相棒の仲間割れ、迫りくる警察の捜査によって、彼らは破滅的な運命をたどり始める。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Black Rain", "ブラック・レイン", "", "", "http://ecx.images-amazon.com/images/I/51-REmq4TmL._SL210_.jpg", 125, "　ニューヨーク市警の刑事ニックとチャーリーはヤクザの佐藤を逮捕し、日本に連行する。しかし目的地の大阪に到着するなり、佐藤が仲間の手によって逃亡。言葉も通じない国で困惑しながらも、ニックとチャーリーは佐藤の追跡に乗り出す。そんなふたりを監視するベテランの松本警部補。やがてチャーリーが佐藤に惨殺されるという事態に。復讐に燃えるニックは松本とともに佐藤を追う！　日米の刑事の友情、そしてその激闘を描くサスペンス・アクション。これが遺作となった松田優作も強烈な印象を残す。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピエタ", "ピエタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1968年に起きた３億円強奪事件。その犯人が同じ年格好で現代に現れたら？　そんな大胆な発想で描かれる大人のメルヘン。年をとらない、名前もない、写真にも写らないという謎めいた犯人を演じるのは「日本製少年」で好評を得た大沢樹生。彼に振り回されながら惹かれていく女、そして彼の昔の恋人、彼女の元不良仲間が犯人のもとに集まり、あの時と同じように現金輸送車襲撃を実行するが……。ビデオ発売時のタイトルは「大強奪～PIETA～」。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flashdance", "フラッシュダンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "昼は田舎町の製鉄所の溶接工として働き夜は酒場でダンサーとして踊りながら、プロのバレリーナを目指す少女の物語。新星J・ビールスの美しい肢体と容姿が魅力的で、大ヒットしたテーマソングとともに強烈な印象を残した。これを機にジャズダンスもブームに。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIMENTICARE PALERMO", "パレルモ　血と掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "F・ロージが手掛けた、シリアスな人間ドラマ。ニューヨーク市長選に立候補し、麻薬撲滅を掲げるカーマインは、父の故郷シチリアを新婚旅行で訪れた。そこは美しい自然とは裏腹に、麻薬によって繁栄した腐敗の地。麻薬組織の罠にはまった彼は、公約の撤回を余儀なくされる。", "1990-01-01", "1,4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星砂の島、私の島／アイランド・ドリーミン", "星砂の島、私の島／アイランド・ドリーミン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "沖縄本島から離れること南へおよそ400キロ、人口約300人の小さな島、竹富島を舞台にした、ヒューマン・ドラマ。夢に破れた女性が臨時教師として竹富島へ。島民との触れ合いの中で自身を見つめ直していく。ヒロインを若手女優、大多月乃が演じるほかに、“モーニング娘。”の田中れいなや亀井絵里、人気バンド“BEGIN”などユニークなキャストが実現。体操競技の選手だった女子大生・早苗は、オリンピック出場への夢破れ、臨時体育教師として東京から竹富島にやって来る。同僚の聡と反発し合いながらも、彼女は島の人々と触れ合い、美しい自然に囲まれた生活の中で成長していく。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美少年之恋／BISHONEN……", "美少年の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ホモセクシャルを題材にした恋愛劇。香港の美男俳優４人が愛憎模様を耽美的に織り成していく。同性愛者のジェットとアチンは共同生活をおくる友人同士。アチンは人気スター、K.S.との出会いに胸をときめかせ、ジェットは美少年サムと恋に落ちる。だがサムはアチンの昔の恋人だった。やがてサムがアチンと鉢合わせし、思わぬ悲劇が起こる。", "1998-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE SECRET", "暗殺の詩 知りすぎた男どもは、抹殺せよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "R・アンリコ監督の未公開サスペンス。ある国家機密を知った疑いで、デビッドはニ度と出られない刑務所に入れられる。だが彼は看守を殺し、山中に逃亡。そんな彼を芸術家夫婦が助け、デビッドの無実を晴らそうとするが……。", "1973-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー×仮面ライダー×仮面ライダー THE MOVIE 超・電王トリロジー EPISODE BLUE 派遣イマジンはNEWトラル ", "仮面ライダー×仮面ライダー×仮面ライダー THE MOVIE 超・電王トリロジー EPISODE BLUE 派遣イマジンはNEWトラル ", "", "", "http://image.eiga.k-img.com/images/movie/55378/315x210.jpg?4107", 0, "「劇場版　超・仮面ライダー電王＆ディケイド　NEOジェネレーションズ　鬼ヶ島の戦艦」の続編第2弾。「EPISODE RED　ゼロのスタートウィンクル」「EPISODE BLUE派遣イマジンはNEWトラル」「EPISODE YELLOW お宝 DE エンド・パイレーツ」の3連作を、2週間ごとに連続上映する。味方であるはずの怪人・イマジンが、乗車チケットの盗難事件を起こす。良太郎とモモタロスはチケットを奪い返そうとするが、逆に深手を負ってしまい……。", "2010-06-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版BLEACH ブリーチ The DiamondDust Rebellion もう一つの氷輪丸", "劇場版BLEACH ブリーチ The DiamondDust Rebellion もう一つの氷輪丸", "", "", "http://image.eiga.k-img.com/images/movie/53168/315x210.jpg?1824", 0, "週刊少年ジャンプで大人気のアクション漫画「BLEACH」を原作に、死神の力を手にした高校生・黒崎一護の戦いを描いたアニメの劇場版第2弾。尸魂界の秘宝“王印”が、運搬中に謎の集団によって強奪されてしまう。同時に、警護を担当していた十番隊隊長・日番谷も行方をくらませる。数日後、現世でその知らせを聞いた一護は、父が営む病院で日番谷と対面。しかし日番谷は事件について何も語ろうとせず……。", "2007-12-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUSTICE EST FAITE", "裁きは終りぬ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "愛人がガンで苦しんでいるのを見て安楽死させたエルザ。この安楽死裁判が始まった。エルザを裁くのは７人の陪審員。彼らは仕事も環境も異なった人たちだった……。元弁護士のカイヤットが陪審員制度に対して疑問を投げかけた裁判ドラマ。", "1950-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("１７才・旅立ちのふたり", "１７才・旅立ちのふたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "モーニング娘。の石川梨華と藤本美貴がダブル主演した青春ストーリー。「仔犬ダンの物語」に続き、アイドル映画で定評のあるベテラン・澤井信一郎がメガホンを執った。絵本作家を目指す高校生の真衣子は、ひょんなことでどこか影のある理沙と知り合う。それぞれ家庭に問題を抱える二人は、お互いの悩みを知る中で友情や家族の大切さに気が付いていく。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAR TREK：FIRST CONTACT", "ファースト・コンタクト　STAR TREK", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「スター・トレック」のニュー・キャラクターによる新シリーズ。「ジェネレーションズ」に続く第２作。ライカー役のJ・フレイクスが長編映画の初監督に挑んでいる。今回、エンタープライズEのクルーの前に立ちはだかる敵は、宇宙に存在するすべての生命体を破壊、吸収しようとする機械生命体ボーグ。彼らは地球歴2063年にタイムトラベルし、地球を支配を企んでいた。ワープ航法に成功する前に地球へ総攻撃をかけようとしていたのだ。すべてを知ったエンタープライズEも、2063年に向かう。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TKO HIP HOP", "TKO HIP HOP", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "東京、渋谷発のリアルなHIPHOPストリートカルチャーを描いた音楽映画。シーンを代表するアーティスト、ダンサー、DJ、グラフィティが集結している。RINO LATINA II、DABO、AIなど日本のヒップホップ界を引っ張るアーティストが集結した超豪華サウンドトラックも凄い。ある事件がもとで剛と雅志は大物ラッパーAPIとMCバトルをやることに。ド素人ながら挑んだバトルが彼らの運命を大きく変える。", "2005-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWINGER", "スインガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "どぎつい記事とヌードを売りものにしている雑誌社に、小説家志望の女が作品を売り込みに来る。軽く一蹴されるが、しゃくにさわった彼女はエロ小説を数冊買い込むと、ショッキングな転落の女の物語をでっちあげ、自伝だと言って再び売り込みに来る。今度は社長もOKを出すが……。A・マーグレット主演のセクシーなミュージカル・ロマンス。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("West Side Story", "ウエスト・サイド物語", "", "", "http://ecx.images-amazon.com/images/I/51AW1UXLhrL._SL210_.jpg", 151, "シェイクスピアの「ロミオとジュリエット」を下敷きにした大ヒットブロードウェイ・ミュージカルを、巨匠ロバート・ワイズとミュージカル版を手掛けた演出家のジェローム・ロビンスが共同で映画化。ニューヨーク・マンハッタンのスラム街を舞台にイタリア系のジェット団とプエルトリコ系のシャーク団の抗争と悲劇を描く。主演はナタリー・ウッド。61年度のアカデミー賞では作品賞、監督賞を含む10部門で受賞した。", "1961-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RETURN OF THE TEXAS CHAINSAW MASSACRE", "悪魔のいけにえ　レジェンド・オブ・レザーフェイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "シリーズ誕生20周年を記念する第４作は、伝説の第１作でトビー・フーパーとともに脚本を書いたK・ヘンケルが監督・脚本を担当、オリジナルに忠実に作り上げた。卒業パーティーを抜け出したヒロイン、ジェニーと３人の高校生たちは、テキサス郊外の真っ暗な田舎道で、あの狂気の殺人鬼ファミリーに遭遇する。顔の皮で作った仮面をかぶり、愛用のチェーンソーで人肉を切り刻む猟奇殺人鬼レザーフェイスが、次々と友人たちを惨殺。ブンブン唸りを上げて襲いかかるチェーンソーから、ジェニーは逃れられるのか？", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("精武風雲・陳真", "レジェンド・オブ・フィスト　怒りの鉄拳", "", "", "http://image.eiga.k-img.com/images/movie/56678/315x210.jpg?1700", 105, "ブルース・リーが「ドラゴン怒りの鉄拳」（1971）で演じたヒーロー、チェン・ジェンを主人公にしたカンフーアクション。1925年の上海。極秘裏に日本軍へのレジスタンス活動を行っているチェン・ジェンは、各国の要人が出入りするナイトクラブ「カサブランカ」へ素性を隠して潜入。そこで出会った歌手キキとひかれあっていくが、キキもまた打ち明けられない秘密を抱えていた。チェン・ジェンに扮するのは「イップ・マン　葉問」のドニー・イェン。監督は「インファナル・アフェア」のアンドリュー・ラウ。", "2011-09-17", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渋谷ブランニューデイズ", "渋谷ブランニューデイズ", "", "", "http://image.eiga.k-img.com/images/movie/57988/315x210.jpg?3388", 78, "東京・渋谷で居場所を守るために奮闘する路上生活者たちの姿を追ったドキュメンタリー。2009年、仕事と住居を失った52歳の派遣労働者・宮沢徹雄さんは、やむなく路上生活を送ることになる。あてもなく街をさまよった末、ようやく渋谷区役所の駐車場（通称「しぶちゅう」）に安らげる場所を見つける。多いときで50人ほどが寝泊りする「しぶちゅう」だったが、渋谷区が夜間・休日の駐車場の閉鎖を決定。80人が野宿していた宮下公園も工事で封鎖され、宮沢さんは仲間とともに居場所を守るため立ち上がるが……。", "2012-04-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ落語　落語研究会　昭和の名人　弐", "シネマ落語　落語研究会　昭和の名人　弐", "", "", "http://image.eiga.k-img.com/images/movie/56508/315x210.jpg?1686", 121, "東京・国立劇場小劇場で毎月開催されている「落語研究会」の映像を映画館のスクリーンで上映する「シネマ落語」第2作。3代目古今亭志ん朝の「船徳」、10代目金原亭馬生の「臆病源兵衛」、6代目三遊亭圓生の「引越の夢」、8代目林家正蔵の「中村仲蔵」を上映し、昭和の噺家たちの貴重な名演を堪能できる。", "2011-05-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兄弟仁義", "兄弟仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "生き別れの母を探して、北海道から上州までイカサマ旅を続ける通称“壺振りの勝”。草間温泉の賭場でそのイカサマがバレるが、彼の母を慕う気持ちを察した鳴子組の代貸勇吉は、彼を責めるのをやめる。おかげで母と会うことができた勝だったが、夢にまで見た母は、今の生活を守るため冷たく彼を追い返す。そんな折、鳴子組と対立する鬼頭組が、卑劣な手段に出始めた……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROBLEM CHILD", "プロブレム チャイルド うわさの問題児", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "当時、米映画界を席巻した“チャイルド・パワー・ムービー”の一編。孤児院でいたずらの限りをつくし追い出される寸前に、どうしても子宝に恵まれない夫婦に養子にもらわれたジュニア。養父は良き父親になろうと懸命に努力するが、ジュニアのいたずらはエスカレートするばかりで……。M・オリヴァーの自由奔放な演技が愉快。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("娘の冒険", "娘の冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "早くに妻を亡くして、その後ずっと独身でいる父・宏二は小唄の師匠・田村ますにゾッコン。娘の京子はその師匠の弟子になりすまし、なんとか父の恋を成就させようと画策するが、ますには親の一存によって決められた結婚相手の株屋の若旦那・浅井がいた。大映オールスター・キャストによる青春明朗編。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVEN’S　SEVEN", "バトル７", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「マッハ！」のプラッチャヤー・ピンゲーオ監督が製作に当たった破天荒なアクション。財宝をめぐる争いに巻き込まれたアウトロー7人組の死闘を、ダイナミックなアクションとトボケた笑いを盛り込んで描く。情けなくもガッツだけはある7人の、それぞれに個性的なキャラクターが魅力的だ。米軍の財宝強奪を町の顔役から依頼された、はみ出し者7人組。裏切りに遭った彼らは、米軍やギャングを敵に回して戦いを繰り広げる。", "2005-12-17", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thomas & Friends: Day of the Diesels", "きかんしゃトーマス　ディーゼル10の逆襲", "", "", "http://image.eiga.k-img.com/images/movie/57648/315x210.jpg?3124", 61, "イギリスにある架空の島・ソドー島を舞台に、蒸気機関車、飛行機、自動車たちが活躍する姿を描く人気児童絵本「汽車のえほん」を映像化した「きかんしゃトーマス」の劇場版。ディーゼル機関車の「ディーゼル10」の悪だくみによってソドー鉄道が大混乱に陥り、窮地に立たされた蒸気機関車のトーマスを救うため、親友のパーシーや仲間たちが立ちあがる。お笑いコンビ「南海キャンディーズ」の山里亮太が意地悪なディーゼル10の吹替え声優を務める。", "2012-04-28", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富貴列車／MILLIONAIR’S　EXPRESS", "冒険活劇 上海エクスプレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1930年代の中国を舞台に、大陸横断の豪華列車“上海エクスプレス”をめぐっての大混乱。強盗団、脱獄囚、諜報部員らが入り乱れ、現金争奪戦が繰り広げられる。香港映画界のドン、サモ・ハン・キンポー監督、主演のギャグ満載痛快アクション。ユン・ピョウら出演陣も見逃せない香港エンターテインメントの一編。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビー・バップ・ハイスクール", "ビー・バップ・ハイスクール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "私立愛徳高校のツッパリコンビ・トオルとヒロシは二人そろって２年に留年。憧れの泉今日子と同じクラスに。ヒロシたちが戸塚水産高校の連中と争ったことがもとで、今日子は戸塚の中村兄弟に殴られ、髪を切られる。二人は戸塚に果たし状を叩きつけた……。1980年代日本映画に突如出現した久々の学園アクション映画。仲村トオルはこの作品がきっかけで人気上昇。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIVE MY REGARDS TO BROAD STREET", "ヤァ！ ブロード・ストリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「レット・イット・ビー」以来14年ぶりにP・マッカートニーが、脚本・主演・音楽の３役を担当したミュージカル。オリジナル８曲の他、ビートルズ・ナンバー６曲を再演し話題となった。お話は、ポップスの人気スターの新作が盗まれるという騒動劇。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIRLS", "ガールズ／恋の初体験", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "青春真っ盛りの４人の少女たち。その友情と恋愛模様を、現代のパリを舞台にシャレたタッチで描く。これまでの「エマニエル夫人」や「O嬢の物語」とはうって変わって、透明感あふれる少女の世界をテーマに取り組んだJ・ジャカンの、ちょっぴりホロ苦い青春映画。", "1979-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VERGINE DI NORIMBERGA", "顔のない殺人鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "後のスプラッタ・ホラーなどに影響を与えた、イタリアン・ホラーの隠れた名作。黒沢清監督が自分のホラー映画ベスト50に挙げている。イタリアの名門家の息子と結婚したアメリカ人のメアリー。しかし、ある夜、屋敷の納屋で目を潰されて死んでいる女を発見する。やがて、夫と管理人が女の死体を運ぶ姿や、謎の覆面男が女を拷問する姿を目撃する。その男の正体とは？", "1963-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／ETERNAL　EMPIRE", "永遠なる帝国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "韓国の気鋭P・ジョンウォンが、西暦1800年の、ある一日に焦点を当てて描ききった歴史ミステリー。韓国大鐘賞の作品賞、監督賞など8部門を独占した。改革を進める国王、正祖と保守派の官僚たちが対立する李氏朝鮮。ある朝、宮廷の王立図書館で検書官の変死体が発見される。第一発見者の若きエリート書記官、李人夢は、現場から消えた一冊の書物を探すよう正祖に命じられるが……。権力闘争に明け暮れる宮廷内の人間模様を、風格に満ちた語り口で描写。韓国を代表する名優アン・ソンギが、正祖を悠然と演じている。", "1995-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOO BEAUTIFUL TO KILL", "TOO BEAUTIFUL TO KILL", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "突然の交通事故で最愛の妻を亡くした精神科医のラザレイ。事故に不審を抱く彼は毎夜悪夢にうなされ、ついにノイローゼに陥ってしまう。そんな彼を見かねた友人のジョーと二度目の妻テレサは、アルコール・麻薬患者専用のブラック・ハーツ病院にラザレイを送り込む。だがそこは、看護婦のチェンバースが患者を誘惑して思い通りに操る狂気の世界だった。屈辱的な日々を送るうち、ラザレイの精神状態はますます悪化し、幻想と現実の中をさまよい始める……。サディスティックな看護婦が患者たちを誘惑して君臨する官能サスペンス。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("細雪", "細雪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "谷崎潤一郎のベストセラーの映画化。1950年には阿部豊が監督しており、轟夕起子はこの前作に引き続いての出演となった。大阪の船場の旧家・藤岡家の四人姉妹のそれぞれの生き方と愛を描き、女の幸せを問う。島の演出はやや一本調子だが、風格のあるドラマに仕上がっている。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　虫皇帝", "劇場版　虫皇帝", "", "", "http://image.eiga.k-img.com/images/movie/54718/315x210.jpg?2676", 90, "推理小説作家の新堂冬樹が初監督を務める、昆虫と毒虫の死闘を描いたドキュメンタリー。ヘラクレスオオカブト、スマトラオオヒラタクワガタ、ダイオウサソリ、オオムカデなど、大型で人気の高い昆虫と未知なる毒虫との壮絶なバトルを記録し、その勝利の行方を追う。", "2009-08-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROPE OF SAND", "刑事ジョン・ケイン 影なき殺人者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「ライトスタッフ」「バックドラフト」「レッド・オクトーバーを追え！」などの娯楽大作での脇役——といっても重要な役を演じることの多いS・グレンが殺人課の刑事役で主演した、テレフィーチャーによるサスペンス・アクション。刑事ケインは、アリゾナから殺人犯を護送中、その犯人に逃げられてしまう。地元の警官たちとともに追跡を始めるが、超自然的な能力を持つ犯人は逆にケインたちを狙い、恐しい罠へと追い詰めていくのだった。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELENI", "哀愁のエレーニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "第二次大戦直後、内乱に揺れるギリシャで子供たちを守るために死んでいった母エレーニ。30年後、ジャーナリストになった長男ニックは、母の死の真相究明のため故郷を訪れるが……。サスペンス・タッチの感動的な作品。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本脱出", "日本脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "自己の映画哲学と通俗メロドラマを見事に融合させた傑作「秋津温泉」を経た吉田喜重が、初めて手掛けたアクション映画。東京オリンピックで日本中がにぎわっていた頃、孤独と焦燥の中で日本脱出を渇望する下働きのバンドボーイ・竜夫がいた。彼は麻薬中毒の兄貴分・浅川の計画するソープランドの金庫破りに加わることを強制される。犯行は成功するが、警察に追われる立場に。竜夫は米軍の朝鮮輸送船に潜伏し、日本脱出の夢に懸ける。当時の日本を風刺する視線が鋭い。一般公開の際、ラストの主人公の発狂シーンが削られ、吉田は大島渚に続いて松竹を去ることになる。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長屋紳士録", "長屋紳士録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "太平洋戦争後の映画界では軍部による統制が解かれ、軍政批判の題材を取り上げる映画作家が続出したが、小津はそんな風潮には迎合せず、サイレント期の「出来ごころ」「浮草物語」「東京の宿」などの坂本武を主人公とした“喜八”ものを取り上げた。だがここでは喜八本人はむしろ脇役で、相手役の“かあやん”が主人公となっている。荒廃した焼け野原の東京を舞台に、親にはぐれてしまった子供が長屋に連れて来られる。人々はグチをこぼしながらも次第にその子供と情が通じるようになり、やがてその子は長屋になくてはならない人気者になる。しかしある日子供の父親が姿を現した……。ほのぼのとした人情とユーモアにあふれる作品であり、小津安二郎監督の作品のなかでも、最も温かい感情に満ちた一編と言える。笠智衆ら長屋の連中が酔っぱらって“のぞきからくり”の口上を歌いはじめるシーンのにぎやかな盛り上がりは抱腹絶倒もの。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COBB", "タイ・カップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "ベーブ・ルース、ルー・ゲーリックと並び、アメリカ・メジャーリーグの基礎を築くとともに、数々の前人未到の記録を打ちたてた男、タイ・カップ。彼の終身通算打率３割６分７厘は現在でも破られていない。しかし偉大な野球人カップは黒人や女性を敵視する差別主義者であり、試合に勝つためなら相手チームの選手にケガをさせることもいとわないダーティな面も多々あった。尊敬と軽蔑、二つの評価を併せ持つ伝説の男の晩年をT・L・ジョーンズが好演。カップの存在感を十分に表現している。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE FALSE MOVE", "運命の引き金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "逃走中のコカイン強奪グループとそれを追う保安官。一見ありきたりの逃走劇と思いきや、犯人グループの黒人女性と保安官が、かつて恋人同士だったことから、事件は意外な方向に転がり始める。逃走の末の再会、燃え上がる男女の思い、そして裏切りと復讐……。息もつかせぬ展開と人間の激情が入り乱れるアクション。ビデオ、DVDタイトルは「運命の銃爪〈ひきがね〉」。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HOURS AND TIMES", "僕たちの時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "ビートルズのマネージャー、ブライアン・エプスタインとジョン・レノンの関係に着目した異色作。ホモセクシャルとして知られるエプスタインがレノンとともに過ごした、1963年４月のバルセロナでのバカンスを、大胆な解釈で綴っていく。レノン役に扮したI・ハートは、後に「バック・ビート」でもレノンを演じている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャプテン翼　ヨーロッパ大決戦", "キャプテン翼　ヨーロッパ大決戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 41, "『週刊少年ジャンプ』に連載されたコミックを原作に、TVアニメ化に続き劇場映画化。多くのサッカー少年を生み出した青春スポーツ・アニメだ。最高視聴率21.2％を記録したTV版に合わせて、本作も大ヒットした。主人公、大空翼の必殺シュートや空中キックなど、見どころがいっぱい。全日本ジュニア選抜チームがヨーロッパ遠征に出陣して、強豪チームと白熱の試合を展開することに……。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("路上", "路上", "", "", "http://image.eiga.k-img.com/images/movie/57438/315x210.jpg?2209", 15, "無名の若手監督たちが作り上げた15分の短編10作品を特集上映する「NO NAME FILMS」の1本。日本に観光にやってきたトミーは、母親とはぐれて迷子になってしまう。なんとか母親に電話がつながったトミーだったが、目の前でホームレスが手売りしている雑誌を目印として伝えてしまい……。外国人の視点を取り入れ、子どもとホームレスの交流を通して現代の社会問題をあぶりだしていく。監督は、「あんたの家」（2008）で第32回ぴあフィルムフェスティバルのグランプリを受賞した山川公平。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狐と狸", "狐と狸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "化繊を純毛とごまかし、人絹を本絹とごまかして洋服を売り歩く甲州の行商人と、それを買う農家の人々の強欲ぶりを狐と狸のばかし合いにたとえて描く千葉泰樹監督の本格喜劇。菊島隆三の脚本がテンポを出し、軽快な作品となっている。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MODIGLIANI", "モディリアーニ／真実の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "ピカソらと同時代に活躍した天才画家、アメデオ・モディリアーニの芸術に魅入られた怒濤の生涯を映画化。ライバルであるピカソとの激しい対立、若く美しい妻ジャンヌとの情熱的な夫婦愛など、どこを切ってもドラマチックな人生模様を'19年のパリを再現した美しいヴィジュアルと共に綴る。パリに渡った若き芸術家モディリアーニは、ライバルのピカソに挑発され、妻ジャンヌをモデルに美術コンテストに挑むことを決意する。", "2005-07-09", "2,3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("30 Days of Night", "30デイズ・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/34288/315x210.jpg?1661", 113, "ジョシュ・ハートネット主演のバンパイア・アクション。製作を「スパイダーマン」シリーズのサム・ライミ、監督を「ハードキャンディ」のデビッド・スレイドが務める。アラスカ州に位置するアメリカ最北端の街バロウの冬には、太陽が24時間昇らない日々が30日間続く白夜の反対現象の極夜が訪れる。その期間に狙いを定めて襲来した吸血鬼と、保安官エバンとステラ率いる住人との壮絶なるサバイバルが展開する。", "2009-08-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いま、会いにゆきます", "いま、会いにゆきます", "", "", "http://ecx.images-amazon.com/images/I/31NX43FN7AL._SL210_.jpg", 119, "梅雨の６週間だけ亡くなったはずの妻が戻ってくるという、奇跡の感動物語でベストセラーとなった市川拓司の同名小説を映画化。『ビューティフルライフ』『オレンジデイズ』など人気TVドラマを手掛け、演出力に定評のある土井裕泰が映画監督に初挑戦し、不思議なストーリーの中で、誰もが共感できる心温まる純愛を美しく綴った。妻が他界して１年、巧は息子と二人で何とか幸せに生きていた。そんな梅雨のある日、亡くなったはずの妻、澪が突然目の前に現れる。しかし彼女には記憶がなかった……。", "2004-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼馬雙星／GAMES GAMBLERS PLAY", "Mr.Boo！ ギャンブル大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "おなじみホイ兄弟コメディの第３作。一流ペテン師＆ギャンブラーのブーは、刑務所で同じくギャンブル狂ロンと出会って意気投合。出所後、ロンはブーの家に転がり込み、二人で金もうけに精を出すが、なぜかツキがまわってこない……。ギャグ連発のドタバタ喜劇。", "1976-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仁鶴・可朝・三枝の男三匹やったるでぇ！", "仁鶴・可朝・三枝の男三匹やったるでぇ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "万国博覧会でにぎわう大阪の街を舞台に、仁鶴扮するヤクザ気取りの男と、可朝扮する寿司屋の店員、三枝扮する学生食堂の給仕係の、異父３人兄弟が巻き起こす珍騒動を描いた上方喜劇。ぐうたら息子たちにカツを入れる“おっかさん”を演じるミヤコ蝶々の演技が見もの。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤城の血祭", "赤城の血祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "10年ぶりの池ざらえで活気づく、上州赤城山の麓・国定村。ひでりに備えての工事とは表向きだけ。実は百姓を苦しめる悪代官と名主、さらにそれとつるんだ大親分・国定忠治の悪だくみだった。ある日この村に二人の旅鴉がやってきて、百姓たちを救おうとするが……。横倉辰次原作のアクション時代劇。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FALLINGWATER", "フランク・ロイド・ライトの落水荘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "アメリカを代表する世界的建築家であり、日本でも帝国ホテルなどを残したフランク・ロイド・ライトが1934年に作った別荘、落水荘をテーマにしたドキュメンタリー。その持ち主であるデパート王の息子であるエドガー・カウフマンJr.の回想により、設計時のエピソード、ライトの姿、カウフマン家の映像などが映し出されていく。一つの建築物をめぐる人々の姿を描くことによって古き良きアメリカを知ることができる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("12 Rounds", "12ラウンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Age of Stupid", "THE AGE OF STUPID", "", "", "http://image.eiga.k-img.com/images/movie/54908/315x210.jpg?2858", 92, "2055年の荒れ果てた地球にたったひとり残された男が2008年の映像アーカイブを見て嘆く姿を通し、地球温暖化に警鐘を鳴らすドキュメンタリー。インドで格安航空会社を経営する男性、イギリスで風力発電の開発に取り組む男性、アメリカのハリケーンで家を失った石油会社従業員らを捉えた6本の映像を紹介。「ユージュアル・サスペクツ」などで知られるイギリスの名優ピート・ポスルスウェイトが案内役を務める。", "2010-04-03", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST RIDE", "ラストライド 恐怖のヒッチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "暴走する大型トラック、殺人を繰り返す不気味な男。「激突！」に「ヒッチャー」の恐怖を混ぜ合わせたようなサイコ・スリラー。無実の罪から４年ぶりに解放された男が、ヒッチハイクで乗ったトラックの運転手が異常者だったことで恐ろしい事件に巻き込まれていく。その運転手は、男の手にナイフを突き刺し、対向車を木に激突させ、相手を殺害、ドライブインの店員を誘拐、次々と殺人ゲームを続けるのだった。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大丈夫日記／THE DIARY OF A BIG MAN", "大丈夫日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「男たちの挽歌」をヒットさせたT・ハーク、チョウ・ユンファのコンビによる痛快恋愛コメディ。雰囲気のある二枚目ユンファが口八丁手八丁のひょうきんなエリート社員に扮し、二人の女性と結婚して二重生活をおくる男のドタバタぶりを軽快に演じている。相手役の妻たちに扮するのは「上海ブルース」のサリー・イップと「チャイニーズ・ゴースト・ストーリー」の妖艶な幽霊役で人気を呼んだジョイ・ウォン。笑いのテンポが速く、広東語のテンポがこれにピッタリ。二人を会わせまいとして、ユンファが妻たちに向けてとっさに発する言い訳がご都合主義の面白さにあふれ、積み重なってさらに悪い事態を招くタイミングがキマっている。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("藪の中の黒猫", "藪の中の黒猫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "平安中期の京都。４人の落武者に暴行され、家ごと焼かれた母娘が、妖怪となって次々と復讐を遂げる……。民間伝承話に着想を得た新藤兼人作品。師である溝口健二の「雨月物語」を思わせる幻想的な物語のなかに、生命の根源である“性”の主題を追及している。太地喜和子の妖艶な化け猫ぶりが圧巻。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHALLOW GRAVE", "シャロウ・グレイブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "大金を手にした３人の若者の友情が裏切りへと変わっていく様をミステリアスなタッチで描いたスリラー。固い友情で結ばれたジャーナリストのアレックス、医師のジュリエット、会計士のデビッド。グラスゴーのアパートで一緒に暮らす３人はある日、ルームメイトを募集する。面接の結果ヒューゴという男に決まるが、越してきたその日に彼は、大金の入ったスーツケースを残したまま麻薬の過剰摂取で死んでしまう。３人は大金を山分けし、死体を森の中に埋める。だが、そこから思いがけないアクシデントが彼らを襲い始める……。「トレインスポッティング」で知られるD・ボイルの初監督作で、ロンドンでは記録的な大ヒットを飛ばした。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超能力者 未知への旅人", "超能力者 未知への旅人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "平凡なサラリーマン、タカツカヒカルは、危篤の母の回復を祈るうち、自分の手から強力なエネルギーが放射されているのに気づく。母の回復に驚いたタカツカは、自分が不思議な“癒”の力を身につけたことを悟り、次々と難病を治していくようになるが……。実在の超能力者をモデルに未知の世界を探る異色作。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEN A STRANGER CALLS", "夕暮れにベルが鳴る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "子供を惨殺した精神異常者が、７年後精神病院を脱走。今度は事件当時のベビー・シッターの子供を狙うが……。電話を小道具として恐怖を盛り上げる前半の20分が、とりわけ見ごたえあるサスペンス・ホラー。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L.A. Confidential", "L.A.コンフィデンシャル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "　1953年、ロサンゼルス。元刑事を含めた6人の男女が惨殺された事件の捜査にあたっていたロス市警の刑事バドは、やがて売春組織の娼婦リンにたどり着く。一方、殉職した刑事を父に持つ野心家のエド、そしてテレビの刑事ドラマでアドバイザーをしているジャックも動き出す。刑事たちはぶつかり合いながらも、やがて手を組み、警察内部の汚職に立ち向かうことに……。作家ジェームズ・エルロイが著した「L.A.4部作」のひとつを映画化したクライム・サスペンス。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("命しらずのろくでなし", "命しらずのろくでなし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "日本を代表する三大ギャンブルの関係者が何者かによって次々と殺される。たまりかねた協会は犯人に1000万円の懸賞金をかけた。ちょうど博奕の借金がたまっていた、ろくでなしのジョーこと竜巻竜次はそれを聞いて大喜びで事件に挑むが……。宍戸錠主演の陽性アクション。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蓮如物語", "蓮如物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "真宗再興の祖として知られる、蓮如上人の没後500年を記念して製作された感動のアニメーション。五木寛之の原作をもとに、乱世に苦しむ民衆に愛の手を差し延べた蓮如の激動の半生を描く。本願寺の長子として生まれた布袋丸は、６歳のとき正妻ではなかった母とのつらい別れを体験する。成長した布袋丸は僧侶となり、名を蓮如と改名。飢饉と疫病に苦しむ民衆を救うため、宗祖・親鸞の教えを得ようと厳しい修行を重ねるのだった……。米良美一が歌う美しい主題歌「やしょめの唄」も感動的。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虹の岬", "虹の岬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "世間を騒がせた歌人、川田順と大学教授の妻、森祥子の禁じられた愛を描く実話の映画化。昭和19年。大学教授の夫と３人の子供に囲まれ、何不自由なく幸せな日々を送る祥子。ある日彼女は、64歳の著名な歌人、川田順と運命的な出会いを果たす。それまで知らなかった短歌の世界に心の安らぎを感じ、川田との激しい愛に身を焦がす彼女は、やがて家庭を捨てる決意をする……。一人の女性を愛し続ける男の純粋さをはつらつと表現する三國連太郎と、すべてを捨てて新しい愛に生きる女性の情熱を気品を漂わせて演じる原田美枝子が、しっとりと大人の愛の物語を紡ぎ出す。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふたり若獅子", "ふたり若獅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "大老・田沼意次の指図で印旛沼の埋立測量を強行した土地代官に反抗して斬られた庄屋喜左衛門の子、春風安太郎。彼は黒装束の怪盗となって田沼屋敷に出没するが、忍びそこねて追われたところを浪人、墨染新九郎に救われる。田沼の悪事を暴こうとする二人だが、卑劣な罠にはまってしまい……。江戸時代劇アクションの快作。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("52 Pick-Up", "デス・ポイント／非情の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　実業家ハリーは何者かに浮気現場をビデオテープに撮られ、それを材料に恐喝される。ハリーは犯人一味の要求を拒むが、その後、彼らは浮気相手がハリーの銃で射殺される現場を収めた次のテープを送りつけてきた。窮地に追い込まれながらもハリーは決死の抵抗を試みる……。恐喝犯に立ち向かう主人公の戦いを描いたハードボイルド・サスペンス。エルモア・レナードの小説「5万2千ドルの罠」を映画化。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女中ッ子", "女中ッ子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "東北地方の寒村から上京してきて住み込みの女中として働く娘と、ヒネクレっ子の次男坊の心の交流を描いた佳編。1976年に、西河克己監督・森昌子主演で、「どんぐりっ子」という題名でリメイクされている。左幸子の初々しく発刺とした演技が印象に残る", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続兵隊やくざ", "続兵隊やくざ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "第１作で、軍用機関車を奪って脱走した大宮・有田のコンビは、ゲリラの地雷に吹き飛ばされ、気がついたら陸軍病院。完治後、再び北支の独立守備隊へ配属されてしまう。今回は看護師への淡い恋などのエピソードもあり、大宮の純粋さ、優しさが強調される。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IF　ONLY", "イフ・オンリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "終わった恋を断ち切れないヴィクターは、別れた恋人シルヴィアの結婚の前日、したたかに泥酔。気付けば、別れのきっかけとなった８ヵ月前に時間がさかのぼっていた。過ちを反省し、彼はシルヴィアとの関係をやり直すが、そこに彼女と結婚するはずの男が現われ……。コミカルでファンタスティックな恋愛劇。「フェイス」のリナ・ヒーディが都会的なヒロインを、さわやかに演じている。", "1998-01-01", "3,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMY", "AMY／エイミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ロック・スターだった父親の死を悲しみ、言葉を失ってしまった幼い少女エイミーが、母親に連れられてメルボルンの空き家に引っ越す。そこはおかしな住民だらけのさびれた下町だったが、エイミーはなぜか隣の家に住む売れないミュージシャン、ロバートが作った音楽に反応。何と彼女が、歌だけを通してコミュニケートできることが判明する。この現実離れしたストーリーを、オーストラリアの女性監督N・タスがペーソス豊かに描き上げた感動作。エイミーに扮した子役A・ディ・ローマが見事な演技力と歌声を披露し、小さなヒロインの周囲に歌があふれていく物語の展開にワクワクさせられる。", "1997-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("独眼竜政宗", "独眼竜政宗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "“独眼竜”の異名をとった武将・伊達政宗が、片目失明という肉体的コンプレックスを乗り越えて、武士として成長していく様を描いた政宗の青春記。この年、中村錦之助は「忠臣蔵」の浅野内匠頭役などで演技派へ脱皮しつつあった。本編でも片目を失った政宗の苦悩をうまく表現している。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八月の光", "八月の光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "PFF招待作品の16ミリ短編「SHORT SCAN」に続き、女性監督二人が共同で撮った自主映画作品。事故死したはずの幼なじみからの連絡を受けたヒロインが、10数年前の８月の記憶をたどって思い出の場所へと向かう姿が、ノスタルジックな詩情漂う美しいイメージで語られていく。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AVENIR D’EMILY", "エミリーの未来", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "３世代の女性が主人公。祖母ポーラは自分から自由を奪った戦争と夫を憎む、家庭の中で生きる女。その娘イザベルは仕事と家庭を両立させんと張りきる現代ママ。祖母と母の愛と憎しみにはさまれたイザベルの娘エミリーの未来は？　女にとっての幸福とは何かを問う、ブラームス監督の自伝的要素の濃い作品。", "1984-01-01", "7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Desperate Hours", "逃亡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　凶悪犯マイケルが彼と恋仲になった女性弁護士の手引きで脱獄。やがてマイケルは仲間とともに閑静な住宅街にある家に押し入る。主人のティム、その妻ノラ、そしてふたりの子供たちも捕らわれ、人質に。その後、FBIの捜査が進み、犯人一味は包囲されるが……。ウィリアム・ワイラー監督の55年作「必死の逃亡者」をリメイク。犯人と人質になった家族の姿を息詰るタッチで描く。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武士の家計簿", "武士の家計簿", "", "", "http://image.eiga.k-img.com/images/movie/55098/315x210.jpg?1841", 129, "古文書から幕末の武士の暮らしを読み解いた磯田道史による教養書「武士の家計簿　『加賀藩御算用者』の幕末維新」を、森田芳光監督＆堺雅人主演で映画化。御算用者（経理係）として加賀藩に代々仕えてきた猪山家の八代目・直之。しかし当時の武家社会には身分が高くなるにつれて出費が増えるという慣習があり、猪山家の家計もいつしか窮地に追い込まれてしまう。そこで彼らは、直之の提案で武家とは思えないほどの倹約生活を実行することになる。", "2010-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Il Camorrista", "“教授”と呼ばれた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 172, "　今作の3年後に「ニュー・シネマ・パラダイス」で世界中から絶賛を浴びた、ジュゼッペ・トルナトーレの監督第1作。ナポリの犯罪組織の抗争を描いた、実話小説をもとにしている。妹をからかった男を殺害した兄は、20年の実刑を受ける。暴力にまみれた刑務所内、冷酷な行動の彼は一目置かれる存在になり、いつしか仲間から“教授”と呼ばれる。“教授”は刑務所内にいながらあらゆる策略を練り、イタリア全土を牛耳るまでにのし上がる。日本では、イタリア映画祭で上映された。", "1985-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAKS THE MOVIE", "バード・パニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "アカプルコではハンティング中のカップルが、メキシコではキャンプ中の家族が——。スペイン、イタリアでも、野性化、狂暴化した鳥の群れが人間を襲うという事件が相次いで発生した。R・カルドーナJr.監督が各地でロケを敢行。自然対人間の戦いを描くパニック・アドベンチャー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("牛若丸", "牛若丸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "美空ひばりが牛若丸と、可憐な娘・桔梗の二役を演じた時代劇。鞍馬山で新田ノ太郎吉光の監視下に置かれていた牛若丸。彼の遊び友だちは、吉光の娘・桔梗だけだった。牛若丸は、源氏再興のため、武芸の稽古にはげみ、のちに五条大橋で、怪力無双の弁慶を打ち負かすまでになる。やがて、平清盛は、牛若丸の行く末に恐怖を覚え始めて……。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あつもの", "あつもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "『イエスの方船』などのTVドラマの脚本家として知られる池端俊策がメガホンを執った人間ドラマ。定年を迎えて消防士を辞めた杢平は大好きな菊作りに情熱を傾けようと、息子を頼って北陸から上京。すでに菊の“あつもの”作りの名人として知られていた彼だが、品評会の常連優勝者、黒瀬にはいつも敗れていた。秘訣を尋ねようと黒瀬を尋ねた杢平だったが、偏屈者として評判の黒瀬は取り合わない。それでも杢平は、あきらめず、黒瀬に接触し……。菊作りに情熱を傾ける主人公を中心に、援助交際をしている女子大生との交流、精神を病む妻との関係を描きながら、現代人の孤独や精神的な飢餓感を浮き彫りに。第４回釜山国際映画祭で外国映画特別賞を受賞。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん　のび太の宇宙小戦争　〈リトル・スターウォーズ〉", "ドラえもん　のび太の宇宙小戦争　〈リトル・スターウォーズ〉", "", "", "http://ecx.images-amazon.com/images/I/3127QFHDPAL._SL210_.jpg", 98, "森でビデオ撮影していたのび太、しずか、ドラえもんは、小さな宇宙船に乗ったパピという宇宙人と出会う。彼はピリカ星という星の大統領で、反乱軍から逃れて地球にやってきたのだ。ところが、反乱軍のギルモア将軍がパピを探して大軍を送りつけてきた。パピは軍に捕まってしまい、ピリカ星へと連れ去られてしまった。のび太たちはパピを救うため、宇宙の旅に出る。シリーズで初めてゲストキャラに俳優を起用、八名信夫がギルモア将軍役でアテレコに挑戦した。", "1985-03-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAIS−TOI !", "ルビー＆カンタン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「奇人たちの晩餐会」や「メルシィ！　人生」で絶妙なユーモアのセンスを発揮した笑いの天才F・ヴェベール監督作。G・ドパルデューとJ・レノというフランス映画界の２大スターを主演に据えた脱獄コメディ。主人公は復讐に燃える寡黙な男と、口から生まれたようなおしゃべり野郎。タイプの違う名優二人が妙演する凸凹コンビぶりが見もの。殺された恋人の復讐に燃える寡黙なルビーと、迷惑者でおしゃべりなカンタンが、同じ刑務所で知り合う。ルビーのことを勝手に運命の親友と思い込むカンタンは、一方的に彼につきまとい続けるがやがて友情が芽生え……。", "2003-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SYLVIA SCARLETT", "男装", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "K・ヘプバーンとK・グラントのコンビ第１作となった、ちょっとおシャレなラブ・コメディ。ひょんなことから窃盗事件に巻き込まれて、美少年に変装し、サギ師の父と逃避行を続けるヘプバーンがある日、旅回りの役者に出会い……。C・マッケンジーの小説『シルビア・スカーレットの若き日々と冒険の数々』を映画化。何と言ってもヘプバーンの男装が見どころ。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渋谷怪談", "渋谷怪談", "", "", "http://ecx.images-amazon.com/images/I/41rymb4Ob9L._SL210_.jpg", 71, "「呪怨２」などで俳優としても活躍する堀江慶が監督したホラー映画。登場人物は河原での合コン・キャンプで一夜を過ごした大学生６人。やがて東京に戻った彼らが怪現象に見舞われ、次々と怪死＆失踪を遂げていく様を描く。「リング」などに通じる心霊ホラーに都市伝説という要素を絡め、新たな恐怖キャラ“サッちゃん”がもたらす惨劇を映像化した一編だ。友人の愛がメールで知り合った圭太郎たちとの合コンキャンプに参加したリエカ。夜、怪談話で盛り上がる中、奇妙な泣き声を耳にした。東京に戻ると、愛と連絡が取れなくなり、参加者の一人が変死体で発見される。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ずらり俺たちゃ用心棒", "ずらり俺たちゃ用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "その腕を買われて大須賀組の用心棒になった竜崎四郎と、彼に助けられた玉突屋の鬼子、正一、そして大須賀の身代わりで刑務所に入ったが、出てきたら幹部にするという約束を反古にされ、激怒する新吉の３人が、大須賀組の陰謀に挑んでいく。二谷英明のクールなガンさばきも見もののアクション編。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HUMAN　TRAFFIC", "ヒューマン・トラフィック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "週末のパーティー、クラブ、ドラッグに48時間だけ生きがいを求める若者たち。そんな彼らの現実からの逃避行を描いたUK発の青春映画。本作が監督第１作となるジャスティン・ケリガンは、現代のクラブカルチャーの語り部として絶賛され、トロント映画祭ディスカバリー賞をはじめとする多くの賞を受賞した。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("国際秘密警察・指令第８号", "国際秘密警察・指令第８号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "三橋達也主演によるスパイ・アクション。南ヴェトナムのサイゴンで、政府の建設調査官・クエンが暗殺され、彼を通じて入札の協力を受けている豊光商事は打撃を受ける。一方、国際秘密警察官・北見次郎は、国際的な死の商人、ルドルフ・ケントを追って日本へ……。その頃、ケントの手掛かりを持つ豊光商事の社員が失踪した。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING PIN", "キングピン ストライクへの道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「ジム・キャリーはMr.ダマー」で監督デビューしたファレリー兄弟の第２作。幼い頃から父親からボウリングの特訓を受けてきたロイは、ライバル、アーニーの策略で利き腕の右手を失ってしまう。それから17年、すっかり落ちぶれた彼は、アーミッシュの素朴な男イシュマルと出会い、彼にボウリングの才能を見いだす。再びやる気を取り戻したロイはイシュマルのコーチとなり、トーナメントに出場。ところが、そこで宿敵アーニーの傲慢な態度に腹を立てたイシュマルが、彼を殴ろうとして大ケガをしてしまう。かつての闘志に目覚めたロイは、義手でアーニーとの闘いに挑むが……。主人公のライバルに扮するB・マーレーが、悪趣味な衣装と派手なパフォーマンスで怪演。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Das Haus der Schlafenden Schonen", "眠れる美女", "", "", "http://image.eiga.k-img.com/images/movie/53138/315x210.jpg?1634", 103, "川端康成の傑作短編小説を、俳優としても活躍するドイツ人監督バディム・グロウナが、自ら主演を務めて映画化した官能ドラマ。15年前に事故で妻子を亡くした事業家エドモンドは、友人の勧めでベルリンの街角に建つ1軒の館を訪れた。信用のおける老人だけが顧客として入ることを許されるその館では、死んだように眠っている美女たちと一夜を共にすることができる。孤独なエドモンドはすっかりその館の虜になり……。", "2007-12-15", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸せな時間", "幸せな時間", "", "", "http://image.eiga.k-img.com/images/movie/57668/315x210.jpg?3545", 72, "長年連れ添った老夫婦の日常を、孫である武井彩乃カメラマンの視点から5年にわたり映し出したドキュメンタリー。結婚して50年になる武井カメラマンの祖父母は静かな毎日を過ごしていたが、ある日、祖母が認知症であることがわかり、祖父にもガンが見つかる。2人の娘で武井カメラマンの母は介護に奔走し、孫自身もカメラを通して現実と向き合い、失われていく時間に思いをはせる。祖母はやがて孫のことは忘れてしまうが、それでも夫のことだけを心配し続けていた。", "2012-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sukiyaki Western Django", "スキヤキ・ウエスタン　ジャンゴ", "", "", "http://image.eiga.k-img.com/images/movie/1948/315x210.jpg?4152", 121, "鬼才・三池崇史監督が西部劇と時代劇を融合させ、全編英語で製作したアクション活劇。壇ノ浦の合戦から100年後。源氏、平家の末裔が、埋蔵金を巡って抗争を繰り広げる寒村を舞台に、流れ者のガンマン、源平両家の末裔、そして村の住民による血みどろの戦いが描かれる。撮影は「インプリント／ぼっけえ、きょうてえ」に続いての三池組となる名手・栗田豊通。", "2007-09-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paintball", "サバイバル・フィールド", "", "", "http://image.eiga.k-img.com/images/movie/54888/315x210.jpg?2177", 90, "都会で暮らす8人の若者たちは極限のスリルを味わうため、ペイント弾を敵チームに当ててフラッグを奪い合うサバイバルゲーム「ペイントボール」に参加する。「グリーン」と「オレンジ」チームに分かれた8人は森でゲームを楽しんでいたが、突然「グリーン」チームの1人が実弾で撃たれ、死んでしまう。パニックに陥った彼らは仲間すら信用できず、次々と犠牲者が増えていく。本作が初監督作となるスペインの新鋭ダニエル・ベンメイヤーがメガホンをとる。", "2009-11-28", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WOMEN’S CLUB", "華麗なる不倫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "売れない脚本家がその若い肉体を見込まれて、女性専門の高級サロンで金持ち女たちのベッドの相手をする仕事にありつき、その体験を脚本に書こうと思いつく。たちまち脚本には買い手がついたが、それを知ったサロンの女性オーナーがカンカンになって……。美女たちの狂態痴態が見もの。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wonderful World ワンダフルワールド", "Wonderful World ワンダフルワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "レオナルド・ディカプリオの吹き替えなどで知られる声優・浪川大輔が、キャストに人気声優陣を起用して挑んだ映画初監督作。人間が睡眠中に訪れているという第2の世界「ヘム」。生きのこることが最終目的のようなその世界では、人々は自分の内面に潜むもう1人の自分を解放して生きていた。動物愛護団体の顧問弁護士・片山誠志は、幼い頃に自分の過失で植物状態となってしまった兄・晃一とヘムで再会するが……。", "2010-06-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La flor de mi secreto", "私の秘密の花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "スペインのペドロ・アルモドバル監督が手掛けた恋愛ドラマ。ロマンス小説の覆面作家として活躍するレオ。しかし現実は小説のようにうまくいかず、愛する夫に冷たくされ、寂しい毎日を送っていた。ある日とうとう我慢できなくなった彼女は、心理カウンセラーである親友ベティに助けを求める。取り乱すレオを心配したベティが気分転換にと彼女に新聞記者の青年アンヘルを紹介したところ、彼はレオにひと目惚れしてしまい……。", "1995-01-01", "9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狭山事件／石川一雄・獄中27年", "狭山事件／石川一雄・獄中27年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1963年５月に埼玉県狭山市で起きた女子高校生殺害事件。その犯人として逮捕され、27年たった当時も千葉刑務所の中から無実を訴え続けていた一人の男を中心に、狭山事件に真っ向から取り組んだドキュメンタリー。当時の事件関係者による生々しい証言をも交えて、その真相に迫った問題作である。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニッポン警視庁の恥といわれた二人組 刑事珍道中", "ニッポン警視庁の恥といわれた二人組 刑事珍道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "やることなすことドジばかりのズッコケ刑事二人組を主人公にしたコメディ。銀行強盗の模擬訓練中に、本物の１億2000万円を盗まれたズッコケ・コンビ。さっそく捜査に乗り出すが、手掛かりとなる人物は次々と殺されて……。中村・勝野のコンビネーションが絶妙。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE TRYING：BLOOD FIST VI", "ドラゴンチェイサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "WKAワールド・キックボクシング・チャンピオンのウィルソンをはじめ、数々の格闘技チャンピオンたちが共演。ローン未払いの車を持ち主から引き上げるという危険な仕事につくウィルソンが、巨大犯罪組織の事件に巻き込まれ、自慢の肉体を武器に戦っていく。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Opening Night", "オープニング・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/6228/315x210.jpg?2182", 144, "　舞台女優マートルは劇場の外で彼女を待っていたファンの少女の事故死を目撃してしまう。これにショックを受けるマートル。しかもその後、少女の幻覚を見るようになり……。盛りを過ぎ、老いを自覚し始めた女優の不安と再生を描く。監督ジョン・カサベテスが自己資金で製作。一時金が底をつき、製作が中断したことも。78年に再編集された110分版も作られている。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EDDIE", "エディー 勝利の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "エディーは地元のプロバスケット・チーム、ニューヨーク・ニックスの熱狂的なファン。ところがニックスは、力はありながらもチームの経営問題やコーチとのトラブルが重なって連敗記録を更新中。そんな中、ひょんなことからエディーは愛するチームのコーチに就任、リーグ最下位のずっこけチームを率いて大奮闘が始まる。彼女の心遣いで生まれ変わった選手たちは一大奮起。ニックスはついにプレーオフ出場をかけた試合に臨む。その前夜、エディーは商売本位のオーナーがたくらむ陰謀を知ってしまい……。デニス・ロッドマン、ミッチ・リッチモンドなどNBAスタープレーヤーも大挙出演。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOE'S APARTMENT", "ジョーズ・アパートメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ニューヨークにやって来た田舎者の青年ジョーはボロアパートに住むことになるが、そこには５万匹のゴキブリが先住していた……。ジョーを気に入ったゴキブリたちが地上げにやって来た悪党を退治したり、ジョーと憧れの女性との仲を取り持とうとしたり、大活躍を繰り広げるファンタジー・コメディ。最新CG映像で作られたゴキブリが可愛らしい。特に彼らが歌い、踊るミュージカル・シーンは秀逸の出来栄え。ゆうばり国際ファンタスティック映画祭ビデオ部門グランプリと市民賞を受賞している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちゃんばらグラフィティー　斬る！", "ちゃんばらグラフィティー　斬る！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "東映が創立30周年を記念して、自社の時代劇の思い出の名場面、スター極めつけのシーンで綴ったチャンバラ映画の“ザッツ・エンタテインメント”。“斬る”“斬られる”“走る”“踊る”“お姫様”などの場面をピック・アップしてつなげた構成が楽しい。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("36 HOURS", "36時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "米軍将校が意識をとり戻した時、ベッドのかたわらに軍医が立っていた。そして、戦争は終わったこと、ここはアメリカの病院だということを知らされ、ノルマンディ上陸作戦の機密を問いただされるが……。第二次大戦末期、連合軍上陸までの36時間を描いたサスペンス。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kwamjum Sun, Tae Rak Chan Yao", "ベスト・オブ・タイムズ", "", "", "http://image.eiga.k-img.com/images/movie/54978/315x210.jpg?4609", 0, "日本でも大ヒットを記録したタイ映画「アタック・ナンバーハーフ」のヨンユット・トンコントーン監督によるラブストーリー。初恋相手である友人の元妻に想いを寄せ続ける獣医師の青年と、彼の気持ちに気づかないまま元夫への未練を残す女性。高齢者向けのパソコン教室で知り合った農場主と未亡人の熟年カップル。世代の違う2組の男女が織りなす淡く切ない恋の行方を優しいまなざしで描く。", "2009-10-19", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Network", "ネットワーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "　ハワードがキャスターを務めるニュース番組は視聴率が低下。テレビ局UBSはコングロマリット企業によって買収され、局内の異動も行なわれる。そんな折、ハワードは解任されることに。疲れきった彼は番組の中で自殺を予告。しかしこの一件によって視聴率は急上昇。その後も野心家の編成主任ダイアナによってハワードの番組は大人気を博すが……。テレビ業界の裏側でうごめく人間たちの愛憎と欲望を描く衝撃作。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("特命係長　只野仁　最後の劇場版", "特命係長　只野仁　最後の劇場版", "", "", "http://image.eiga.k-img.com/images/movie/53338/315x210.jpg?3942", 0, "柳沢きみおのコミックを原作に、深夜で人気を博したTVシリーズ「特命係長　只野仁」の劇場版。監督・脚本はTVシリーズと同じく植田尚と尾崎将也。大手広告代理店・電王堂の総務二課係長の只野仁は、普段は地味なダメ社員だが、裏では会長の指令で社内外のトラブルを解決する“特命係長”として活躍していた。ある日、電王堂がCMに起用したグラビアアイドルが謎の人物による脅迫を受け、彼女の護衛と身辺調査を頼まれた只野は、早速調査に乗り出すが……。", "2008-12-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("…AND JUSTICE FOR ALL", "ジャスティス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "主人公は正義感の強い若き弁護士。ある時、宿敵の悪徳判事の弁護を引き受けるハメに陥るが……。司法に挑む熱血漢を、A・パチーノが持ち前のキャラクターを発揮して熱演。法曹界の内幕を軽妙な演出で暴き出した、ジュイソン監督お得意の社会派娯楽作だ。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FIRST GREAT TRAIN ROBBERY", "大列車強盗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1885年のヴィクトリア朝大英帝国を背景に、ある金塊強奪事件を中心に、その首謀者でロンドン暗黒街の希代の悪漢といわれるエドワード・ピアースとその一味の犯罪作戦の全貌を描く。４つの鍵をそろえなければ絶対開かないという、英国中の犯罪者が注目する軍の金塊輸送。怪盗ピアースは鍵を手に入れるため、まず銀行頭取の娘に接近するが……。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("World Trade Center", "ワールド・トレード・センター", "", "", "http://image.eiga.k-img.com/images/movie/1218/315x210.jpg?2643", 129, "01年9月11日に起きた全米同時多発テロで、象徴的な被害を被った世界貿易センタービル（＝ワールド・トレード・センター）から奇跡的に生還した2人の警察官の実話を、「プラトーン」「7月4日に生まれて」のオスカー監督、オリバー・ストーンが映画化。主演はニコラス・ケイジと「クラッシュ」で注目されたマイケル・ペーニャ。共演にそれぞれの妻を演じたマリア・ベロとマギー・ギレンホール。", "2006-10-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STARDUST MEMORIES", "スターダスト・メモリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "映画祭会場をめぐる悲喜こもごものエピソードを、星くずのように散りばめて見せたアレンの私映画的野心作。アレン本人を連想させる俳優兼監督のサンディ・ベイツを主人公に、彼にふりかかる称賛と非難、彼の私生活に立ち現れる３人の女、子供時代への郷愁——それらが、ある時は現実として、ある時は幻影として交錯する。アレンの心酔するフェリーニの「８1/2」を彼流の露悪的ユーモアとロマンチシズムで、巧みに換骨奪胎。とりわけ、サッチモのレコード『スターダスト』をバックに、午後の小部屋でくつろぐ愛する女の表情を、サンディが飽かず眺める至福のシーンが圧巻だ。モノクロ撮影も見事。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr.レディー 夜明けのシンデレラ", "Mr.レディー 夜明けのシンデレラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "心優しい家族思いの男が、ひょんなことから高校の先輩でゲイバーのママをしているアンナに再会。店に連れていかれた彼はMr.レディたちの明るくスマートな振る舞いに魅せられ、いつしか彼自身も仲間になっていく。個性派役者・片岡鶴太郎が一人の男（女）の揺れ動く心模様を熱演した人情ロマン。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("細川血達磨", "細川血達磨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "歌舞伎や講談で有名な「細川の血達磨」を題材に、外様大名取潰しに絡んだ忠君・忠死を描いた作品。主演は、日活のニューフェイスとしてデビューし、宝塚キネマから新興へと流れたスポーツマンタイプの剣戟スター、大谷日出夫で、大衆受けするマスクで一世を風靡した魅力の片鱗が本編でもわかる。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Sconosciuta", "題名のない子守唄", "", "", "http://image.eiga.k-img.com/images/movie/34188/315x210.jpg?1543", 121, "イタリアの巨匠ジュゼッペ・トルナトーレ監督が、「マレーナ」以来6年ぶりにメガホンを取ったミステリー・ドラマ。北イタリアの港町トリエステにやって来た謎めいた美女イレーナ。彼女はある目的のために貴金属商のアダケル夫婦に近づいていく。アダケル家にメイドとして雇われることになったイレーナは、彼らの4歳になる娘テアと親しくなるが……。トルナトーレ監督作には欠かせないエンニオ・モリコーネの音楽がミステリアスなムードを盛り上げる。", "2007-09-15", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("電人ザボーガー", "電人ザボーガー", "", "", "http://image.eiga.k-img.com/images/movie/55568/315x210.jpg?1761", 114, "「マグマ大使」の制作会社ピープロが手がけ、1974～75年にTV放送された特撮ヒーロードラマを、「片腕マシンガール」の井口昇監督が映画化。秘密殺人強盗機関「Σ（シグマ）」に父親を殺され復しゅうを誓う大門豊は、父が残した変形型バイクロボット「ザボーガー」を駆使して戦う。「青年期の章」「熟年期の章」の2部構成で、豊の熟年期を板尾創路、青年期を「炎神戦隊ゴーオンジャー」の古原靖久が演じる。", "2011-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Liebte der Osten anders? - Sex im geteilten Deutschland", "コミュニストはSEXがお上手？", "", "", "http://image.eiga.k-img.com/images/movie/53588/315x210.jpg?2610", 52, "東西ドイツの歴史を\"セックス\"をテーマに紐解いたドキュメンタリー。91年の東西ドイツ統一後、それまで謎に包まれていた東ドイツの生活実態を調査しはじめた社会学者たちは、人々の性生活が西ドイツとは大きく異なることに気付く。セックス研究家や歴史学者ら専門家の証言、さらに当時のニュース映像や性教育映画“女体の神秘”といった貴重な映像資料を通し、東西ドイツにおけるセックスの違いを科学的に解き明かしていく。", "2008-06-21", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE VIERDE MAN", "４番目の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「トータル・リコール」「氷の微笑」のP・ヴァーホーヴェンがオランダ時代に撮った異色サスペンス。流行作家のジェラルド・リープは、ある作家グループの講演会に出席。その彼が、ブロンド美人のクリスティンと知り合ったことから不気味な出来事が次々と起こり……。1983年アヴォリアッツ国際ファンタスティック映画祭批評家賞受賞作。", "1982-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生徒諸君！", "生徒諸君！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "庄司陽子の同名連載マンガの映画化。陽気でおてんばな転校生・ナッキーが大あばれするコメディ・タッチの学園ドラマ。映画初主演の小泉今日子が双子の姉妹を演じて好評。「ザ・スパイダースのバリ島珍道中」などアイドルものを得意とする西河克己の演出も冴え、痛快な一編。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あいのこころ", "あいのこころ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "豊かな自然の広がる福島県二本松市を舞台に、合気道を通して成長していく高校生たちの姿を描いた青春ドラマ。自由奔放な母に連れられて転校を繰りかえしてきた女子高生あい。祖母が暮らす田舎町へと引越して来た彼女は、そこで合気道と出合い、その理念に心ひかれていく。合気道を愛する3人の仲間たちと友情を育んでいくあいだったが、ある日、仲間内で1番の使い手であるひとみが合気道をやめると言いはじめる。", "2008-01-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FIGHTING KENTUCKIAN", "ケンタッキー魂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "19世紀初めのアラバマを舞台に、ワーテルローの戦いに敗れてアメリカに亡命、定住してきたナポレオン軍の一団と、ケンタッキー銃隊とのいざこざを描く西部劇。ジョン・フォード映画の名脇役J・ペニックを迎えて、J・ウェインが自ら製作。先込め銃時代という珍しい時代背景の作品である。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEGIONNAIRE", "レジョネア・戦場の狼たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "J・C・ヴァン・ダムが製作、脚本にかかわった意欲作。灼熱のサハラ砂漠を舞台にしたハード・アクションだ。1925年、裏社会のボクサー、アランが、八百長試合を拒否したためマフィアのボスから追われる身に。やがて彼がモロッコの外人部隊に加わり、盗賊の襲撃、大自然の脅威、マフィアの追跡という苦難の中で、仲間の兵士たちと友情で結ばれていく姿を描き出す。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE REMAINS OF THE DAY", "日の名残り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "英国の日系人作家K・イシグロが、ブッカー賞を得た同名小説を映画化。英オックスフォードにある名門貴族の館を舞台に、職務を何より重んじる“有能な”執事長スティーブンスの数奇な生き方を描く。1936年、次第にヨーロッパに戦雲が近づく頃。彼は、主人が親ナチスであることも無関心で、若く勝ち気なメイド、ミス・ケントンへの恋心も抑えながら、ひたすら自らを律するばかりであった。やがて時はめぐり、1957年。今や主人も代わった。幸せな結婚をして、もうじき孫も生まれるというミス・ケントンを再びメイドとして屋敷に招くため、彼は慣れぬ車を走らせている。ハンドルを握りながら静かにこれまでの来し方を思う時、その胸に去来するものは……。前作「ハワーズ・エンド」とほぼ同じ布陣による時代錯誤すれすれのウェルメイドなドラマだが、“物語の強さ”が傑出していて、ひき込まれる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Head−On", "愛より強く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "2004年のベルリン映画祭で金熊賞を受賞した、俊英ファティ・アキンによるラブストーリー。人生に絶望して自殺未遂を起こした40歳のジャイトは、親から自立したいという理由でジェイドとの偽装結婚を望むトルコ系女性シベルの申し出を受け、彼女と偽装結婚することになる。複雑な偽装生活の中で、ジャイトは彼女に次第に惹かれていくことに気づくのだが……。2009年3月にファティ・アキン監督特集としてリバイバル上映。", "2006-04-29", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・男の子守唄", "喜劇・男の子守唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "元戦災孤児の独身男が10歳の少年を育てながら、精一杯生きていく姿を描く。チンドン屋とホストクラブ兼業の福田清造は、焼け跡時代に自分を育ててくれたパンパンのお竜の忘れ形見・太郎を育てている。ある日そんな太郎を引き取って教育を受けさせたいと金融業者が申し出てきた。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blood Simple.", "ブラッド・シンプル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "アメリカ南部の田舎町で、酒屋の主人が妻の浮気調査を探偵に依頼したことをきっかけに起こった殺人事件を描いたサスペンス。「ファーゴ」「オー！ブラザー」のジョエル＆イーサン・コーエン兄弟の製作・監督・脚本デビュー作。撮影を担当するのは後に「メン・イン・ブラック」等で監督として大成するバリー・ソネンフェルド。コーエン兄弟は99年にこの作品を再編集して「ブラッド・シンプル／ザ・スリラー」として公開している。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("櫻の園　さくらのその", "櫻の園　さくらのその", "", "", "http://image.eiga.k-img.com/images/movie/53538/315x210.jpg?3666", 102, "吉田秋生の人気コミックを映画化し、数々の映画賞を受賞した1990年の同名映画を、ストーリーを一新して再映画化。監督は前作と同じ中原俊。名門女子高・櫻花学園に転校してきた結城桃は、学校の伝統行事として毎年上演されていたチェーホフの「桜の園」の台本を見つけるが、過去のある事件がきっかけで、いまは上演が禁じられていた。桃はなんとか自分たちの手で「桜の園」を上演しようと奔走するが……。「ヤッターマン」の福田沙紀が映画初主演を飾る。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("START THE REVOLUTION WITHOUT ME", "004／アタック作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "二組の双子誕生に、あわてた医者が取り違えたことから起こる風刺コメディ。革命前夜のフランス。二組の双子が生まれるが、医者が農家の赤ん坊と貴族の赤ん坊を取り違えてしまう。それから30年後、革命の戦火の下、彼らは再び運命を同じくすることになる……。O・ウェルズが最初と最後にナレーターとして特別出演している。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RUNNER", "ブレード・サバイバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "TVニュースの取材班であるジャックたちは、世界を飛び回り、常に危険と隣り合わせにいる。彼らは危険な取材での事故を防ぐトレーニングとして、サバイバル・アイランドと呼ばれる孤島を訪れる。そこでは海軍の退役兵であるマクベインらが指揮をとっていたが、厳しいトレーニングも次第にエスカレートし、ついには仲間が爆死する。なんとマクベインは、核爆弾が世界を救うという危険思想を持つ異常者だった。ジャックたちは、孤島で戦争のプロと戦うことになった。主役のT・フォードはハリソン・フォードの弟。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARD COVER", "ハードカバー 黒衣の使者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "T・タカクスがアメリカのパルプ・マガジン文化にオマージュを捧げたサスペンス。「フライトナイト」のSFXスタッフ、ランダム・W・クックが特撮を担当、さらに殺人鬼マルコム役もこなしている。恐怖小説の殺人鬼が現実の世界に登場するB級感覚が楽しい。アヴォリアッツ国際ファンタスティック映画祭グランプリ受賞。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ポケットモンスター　ダイヤモンド＆パール　幻影の覇者　ゾロアーク", "劇場版ポケットモンスター　ダイヤモンド＆パール　幻影の覇者　ゾロアーク", "", "", "http://image.eiga.k-img.com/images/movie/55168/315x210.jpg?4751", 0, "人気アニメ「ポケットモンスター」の新シリーズ「ダイヤモンド＆パール」の劇場版第4弾。サトシとピカチュウ一行は、「ポケモンバッカー・ワールドカップ」を観戦するため、古い街並みと近代的な建物が美しく調和するクラウンシティへと向かっていた。その道中、サトシらはポケモンのゾロアに出会い、仲間となって旅をともにする。一方、幻影を自在に操るポケモン・ゾロアーがクラウンシティを破壊し、20年ぶりに街に戻ってきた幻のポケモン・セレビィに危機が迫っていた。", "2010-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女優T", "女優T", "", "", "http://image.eiga.k-img.com/images/movie/57368/315x210.jpg?4294", 14, "芸能事務所イトーカンパニー所属の俳優たちが出演するショートムービーをネット配信している「イトーチューブ」で公開された作品を映画館で上映する「イトーチューブ Shrot Movie Colleciton」の1本。ともさかりえ主演で、とある女優の秘密が暴かれるフェイクドキュメンタリー。某人気女優「T」のプライベートが隠し撮りされ、華やかなで明るいTの裏に怪しげな女占い師の存在が浮かび上がり……。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UP IN ARMS", "ダニー・ケイの新兵さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "D・ケイのデビュー作となったミュージカル・コメディ。第二次大戦下の1943年、体中に病気を持っていると思い込んでいるダニーは、ついに徴兵される。親しくしていた看護婦メアリーとの別れを惜しむダニーだったが……。D・ケイの芸が全編に散りばめられた、おっちょこちょい新兵の手柄もの。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばり捕物帖 ふり袖小判", "ひばり捕物帖 ふり袖小判", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "吉例舞踊祭の楽屋で、かつての兄の部下、兵馬と会ったお七、酒で失敗した兵馬のために帰参を兄に願うが逆に意見をされてしまう。翌日、女スリ殺しの現場で一枚の小判をお七は見つけた。同じ夜、兵馬もその小判に絡む怪しい侍を見つけていた……。千代之介、ひばりコンビによるミステリー・タッチの時代劇。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mala Noche", "マラノーチェ", "", "", "http://image.eiga.k-img.com/images/movie/34078/315x210.jpg?1411", 78, "ガス・バン・サント監督が1985年に発表した幻の長編デビュー作。ポートランド出身の詩人ウォルト・カーティスによる自伝小説の映像化で、後に「ドラッグストア・カウボーイ」「マイ・プライベート・アイダホ」と続く“ポートランド3部作”の第1作。小さな食料品店で働く青年ウォルトは、仲間と共にメキシコからやって来た不法移民のジョニーに恋をする。ウォルトとの距離を縮めたいがために彼らの世話を焼くジョニーだったが……。", "2007-07-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BOULANGE´RE DE MONCEAU", "モンソーのパン屋の女の子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 26, "E・ロメールの初期短編の１本で、“６つの教訓的物語”シリーズの第１作でもある。法律を学ぶ“僕”は、５月のある日、前から気になっていたが声もかけられなかったシルヴィーとすれ違う。この日も友人シュミットにそそのかされるが、結局、声をかけられなかった。しかし、ある時チャンスがやって来るのだった……。長い間、公開されなかったが、「シュザンヌの生き方」とともに1996年に初めて公開された。DVDは「エリック・ロメール コレクション(1)」に収録。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DE´CLIN DE L’EMPIRE AME´RICAIN", "アメリカ帝国の滅亡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "リゾート地に集まった４人の歴史学者と、やはりヘルスクラブにやって来た彼らの妻や愛人たち。それぞれにパートナーのことを思い存分しゃべりまくるのだが……。現代の恋愛観を、セックスに関するシャレた会話を織り交ぜながら描いたカナダ映画の佳作。", "1986-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("思えば遠くへ来たもんだ", "思えば遠くへ来たもんだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "九州からみちのくの小さな城下町、秋田県角館市の高校へ臨時教師として赴任してきた熱血漢の主人公が巻き起こす笑いとペーソスを描く。女子高の教師に熊谷真実、落ちこぼれ生徒の姉にあべ静江がそれぞれ扮し、教師役が似合う武田鉄矢が恋する陽・陰二様のマドンナを演じる。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オオカミの護符", "オオカミの護符", "", "", "http://image.eiga.k-img.com/images/movie/53858/315x210.jpg?3724", 114, "絶滅したといわれるニホンオオカミの護り札を通し、自然の山々や農民の暮らしを見つめるドキュメンタリー。日本の人口が集中する関東平野を取り囲む山々、そこには自然を尊ぶ山岳信仰の素朴な生活がある。「お犬さま」と呼ばれるオオカミの護符を土蔵や畑に貼ってきた農民たちの暮らしぶりや自然を尊ぶ姿、“山びと”とオオカミの関わりに迫る。", "2008-10-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仇討膝栗毛", "仇討膝栗毛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "放蕩息子の新之助に手を焼く父は、自分が何者かに討たれたことにして、新之助を仇討ちの旅に立たせる。だが、新之助が仇役と仲良くなったり、兄の仇を探す美しい娘・おあきに助太刀の約束をするなど、旅はいささか脱線気味。そして、現れた偽の仇敵というのは、実は娘の兄の仇であった。溝口作品で名声を得た依田義賢のオリジナル脚本を映画化した、名人・森一生の処女作品。現存フィルムは音声に欠陥部分がある。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BATMAN　＆　ROBIN", "バットマン＆ロビン　Mr.フリーズの逆襲!!", "", "", "http://ecx.images-amazon.com/images/I/51fhE%2B95BQL._SL210_.jpg", 124, "闇のヒーロー、バットマンが活躍するシリーズ第４作。天才分子生物学者が研究中の事故によって、冷却スーツに身を包む悪党“ミスター・フリーズ”に変身。元女性植物学者である悪女ポイズン・アイビーと手を組んだ彼は、ゴッサムシティの氷漬けを画策する。これに挑むのが、バットマンと相棒ロビン。正義の少女バットガールを味方に加え、悪の打倒に乗り出していく。フリーズ役にシュワルツェネッガーを起用したことによって、アクション濃度は上昇。バットマン役もG・クルーニーにバトンタッチし、シリーズに若々しさが加わった。特撮を担当したのは「スター・ウォーズ」などで知られるジョン・ダイクストラ。毎回、話題となる主題歌はスマッシング・パンプキンズが担当。", "1995-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃える雲", "燃える雲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "福本和也の原作『航空検察官』を映像化した日活アクション映画。かつて優秀なジェット・パイロットだった朝日名隆は、航空検察官の一員として勤務することになった。そこで彼は、香港に働きに行っていた踊り子が帰国直後に殺された事件を担当することになる。航空網犯罪の摘発に命を懸ける男の姿を描く。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("木更津キャッツアイ　日本シリーズ", "木更津キャッツアイ　日本シリーズ", "", "", "http://image.eiga.k-img.com/images/movie/1478/315x210.jpg?1260", 123, "「GO」「ピンポン」の宮藤官九郎が脚本を担当した02年の同名人気TVドラマを映画化。余命半年と宣告されたぶっさんと、野球とバンドの仲間“木更津キャッツアイ”が、美礼先生の同級生でニセ札作りの名人ジョージ（内村光良）の原版を狙って怪盗団に。ぶっさんと韓国パブで働くユッケ（ユンソナ）との恋も展開。TV版監督の金子文紀が本作で映画初挑戦。製作はTV版や宮藤の「池袋ウエストゲートパーク」も手掛けた磯山晶。", "2003-11-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乳房", "乳房", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "伊集院静の小説を根岸吉太郎が哀歓を込めて映像化した、やるせないラブ・ストーリー。ギャンブルと酒、女遊びにのめり込む中年男・立原は、仕事で一緒になった里子という女性と恋に落ちる。しかし、彼女は白血病に冒されていた。それでも一途な愛を捧げる彼女の姿に、立原は我が身を顧みる。故・夏目雅子の半生を描いたドラマだ。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WITCHES", "ジム・ヘンソンのウィッチズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "肉親を事故で亡くし、祖母にひきとられた少年ルークは、祖母とともに、イギリスの海岸のホテルを訪れる。ペットのネズミと遊んでいるうち、ルークは凶悪な大魔女、魔女たちの集会を目撃する。イギリス中の子供を消そうとたくらむ魔女たちは、まずルークをつかまえ、ネズミに変えてしまう……。「セサミストリート」や「ミュータント・タートルズ」のマペットを手掛けた故J・ヘンソンが製作総指揮するファンタジー・アドベンチャー。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("炎と女", "炎と女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "人工受精をテーマに、吉田喜重監督が撮り上げた心理ドラマ。造船技師の伊吹と妻の立子の間には１年と７ヵ月の一人息子がいたが、実はその子は人工授精によって生まれた子だった。精子の提供者である坂口の妻は、その子が坂口と立子の姦通で生まれた子と思い込み、自分は伊吹を誘惑しようとする……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鈴木先生", "鈴木先生", "", "", "http://image.eiga.k-img.com/images/movie/57878/315x210.jpg?3332", 0, "独自の教育理論で教育現場のさまざまな難問に立ち向かう国語教師・鈴木先生の奮闘を描いた、長谷川博己主演の人気ドラマを映画化。緋桜山中学校の2年A組を受け持つ鈴木先生は、理想のクラスを作る上で必要なスペシャルファクターとして、ひとりの女子生徒・小川蘇美を重視していた。しかし、そうしているうちに蘇美の魅力にとりつかれ、良からぬ妄想をしてしまう。妊娠中の妻・麻美に心配をかけまいと、なんとか自分を律して日々を過ごしていたが、2学期になったある日、OBが学校に立てこもり蘇美が人質にとられるという事件が発生する。妻の麻美役に臼田あさ美、同僚の教師役で田畑智子、でんでんらが共演。", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REACH　THE　ROCK", "リーチ・ザ・ロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ある夜、問題児ロビンがショーウィンドーを破壊し、警察に拘留される。その見張り番を務めるのは、ロビンを敵視する巡査部長クインと新米警官。まんまと留置場のカギを手に入れたロビンは警察を抜け出し、次々と事件を引き起こしていく。J・ヒューズの秀逸な脚本が光るサスペンス・コメディ。理不尽な暴走を繰り返す若者の屈折した感情をスリリングに浮き彫りにし、ホロ苦い後味を残す好編に仕上がっている。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blade 2", "ブレイド2", "", "", "http://image.eiga.k-img.com/images/movie/1298/315x210.jpg?4379", 118, "脚本は前作と同じ「ダークシティ」「ザ・クロウ」でおなじみのデビッド・S・ゴイヤー。撮影はデレク・ジャーマンの「カラヴァッジオ」「アリア」など欧州の光と影が得意なメキシコ人、ガブリエル・ベリスタイン。美術は「デッド・ゾーン」以降すべてのクローネンバーグ監督作を担当するキャロル・スピアー。VFXは「マトリックス」のニコラス・ブルックス、SFXは「フィフス・エレメント」のニック・アルダーが監修している。", "2002-06-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SMOKEY　AND　THE　BANDIT　III", "トランザム7000　PART３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "B・レイノルズがトランザムに乗り大暴走を繰り返す、超ド級のウルトラ・カーアクションが人気のシリーズ第３弾。本作は主人公の宿敵である保安官に焦点を当てたストーリー展開。大富豪から時間内にある場所に着けば大金を払うと告げられた保安官は、その賭けに乗り息子を連れて出発する。途中でトランザムと出くわした彼は、ルートを外して追跡するが……。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NE NOUS FA＾CHONS PAS", "女王陛下のダイナマイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "南フランスのコートダジュールを舞台に、フィルム・ノワールの大御所、L・ヴァンチュラが大活躍するアクション・コメディ。M・ダルクが花を添えている。今はカタギになっているヴァンチュラのところに、昔のやくざ仲間が訪れて、金塊の話をする。事件に巻き込まれていくヴァンチュラだが……。イギリスの犯罪者一味とフランス人による派手なダイナマイト爆破作戦が始まる。ダイナマイトにはダイナマイトを、ヴァンチュラの怒りも爆発した。ノワールな役者、M・コンスタンタンやJ・ルフェーブルらも顔をそろえ、渋い魅力で脇をかためている。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デコトラの鷲〈しゅう〉／祭りばやし", "デコトラの鷲〈しゅう〉／祭りばやし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "浅草を舞台に、気のいいトラック野郎の奮闘を笑いと涙で綴った人情ドラマ。主人公に扮した哀川翔の人間味豊かな熱演に加えて、祭りの熱気などを捉えた下町の風情も見どころだ。一年に一度、三社祭に合わせて浅草に帰郷するトラック運転手、鷲一郎。実家に居候していた女性ルーシーに一目惚れした彼は、彼女の父親探しに協力するが、そのためトラブルに巻き込まれる。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THIRTY−NINE STEPS", "39階段", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ヒッチコック「三十九夜」と同じ原作の、３度目にして最も忠実な映画化。第一次大戦後のイギリス。ギリシャ首相暗殺計画の鍵を握るスパイを殺したと誤解された主人公は、警察とスパイ団の両方から追われる身となる。ヒッチコックを意識した演出が随所に楽しめる。", "1978-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふりむいた花嫁", "ふりむいた花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "古いのれんを誇る“どじょう隅田”の主人・三田亀太郎は妻に先立たれ、子供二人とやもめ暮らし。娘の春江は、下町っ娘らしい働き者で帳場を切りまわしているが、息子の新一はTVタレントを夢見ている……。伴淳三郎の人柄のよくでた下町喜劇。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Breath", "ブレス", "", "", "http://image.eiga.k-img.com/images/movie/53418/315x210.jpg?2368", 84, "刑の執行が迫った死刑囚チャンは、自殺を図ろうと自らの喉を突き声を失ってしまう。一方、夫の浮気を知り心に傷を負った主婦ヨンは、チャンの自殺未遂のニュースを見て興味を抱き、刑務所へと向かう。チャンとの面会に成功した彼女は、余命わずかな彼のために“四季”をプレゼントしようと思いつき……。韓国の鬼才キム・ギドク監督が、2人の愛の行方を色鮮やかに描き出す。主人公チャンを演じるのは「呉清源／極みの棋譜」のチャン・チェン。", "2008-05-03", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こどもの時間", "こどもの時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "埼玉県桶川市にある、いなほ保育園の四季を追いかけたドキュメンタリー。０歳から６歳までの100人が過ごす、この保育園では約400坪の広大な土地で子供が自由に駆け回っている。動物と戯れ、また世話をしたり、顔も手も食べ物で汚しながら食事をとったり、ケンカしては仲直りしたりなど、そんな子供の伸び伸びとした世界を、丹念にとらえていく。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Curious George", "おさるのジョージ Curious George", "", "", "http://image.eiga.k-img.com/images/movie/33358/315x210.jpg?2003", 87, "1941年の誕生以来、世界中で愛され続けてきた名作絵本「おさるのジョージ」を、「ダ・ビンチ・コード」で監督を務めたロン・ハワードのプロデュースによりアニメ映画化。ニューヨークの博物館で働くテッドは、新しい展示物を探しにアフリカへ旅立つ。彼はジャングルで人懐こいイタズラ好きのこざると仲良くなるが……。メガホンを取るのは、クリエイターとして「シュレック」等の人気アニメを手掛けたマシュー・オキャラハン。", "2006-07-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベイビークリシュナ", "ベイビークリシュナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "京都を舞台に、日本人青年とアジア系青年の交流を温かく描き出したヒューマン・ドラマ。オタク風情の考古学者、佐々木が同窓会の帰りに、ネパール人の青年クリシュナをはねてしまった。これが縁となり、彼はクリシュナと共同生活をすることに。クリシュナの天真爛漫な姿に接するうちに、佐々木は次第に外向的な自己を取り戻していく。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY　LIFE　WITHOUT　ME", "死ぬまでにしたい10のこと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「トーク・トゥ・ハー」のP・アルモドヴァルが製作を務めた感動作。23歳で余命２ヵ月と宣告された女性が、残された時間で何ができるかをリストアップする。夫と娘たちのために、自分のために、精一杯生きようとするヒロインを、「めぐり逢う大地」のサラ・ポーリーが気負いのない自然体の演技で好演する。死ぬ準備を始めてから、次第に魅力を増していくヒロインの姿が印象的だ。失業中の夫と幼い娘二人とともにトレーラーハウスで暮らす23歳のアンは、貧しいながらも幸せだった。そんなある日、彼女はガンの告知をされる。アンは誰にも打ち明けないと決め、死ぬまでにできることを考え始める。", "2002-01-01", "9,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL NINO DE LA LUNA", "月の子ども", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "超能力を持つ孤児のダビーは、ある時、自分が神によって選ばれた人間であることを悟る。自分に与えられた神意を人々に伝えなければいけないという使命感にみまわれた彼は使命を果たそうとする。しかし、ダビーの超能力を利用しようとする秘密結社がそれを阻止する。これまでのスペイン映画のカラーを大きく変えた、都会的で洗練された話題作。", "1989-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("多甚古村", "多甚古村", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "井伏鱒二の同名小説の映画化。田舎町の平凡な巡査の駐在生活がユーモラスに、叙情的に描かれていく。今井正監督の28歳の時の野心作であり、彼の第３作である。当時時代劇スターとして活躍していた清川荘司の珍しい現代劇でもあり、以後彼は軍人役や悪役が増えていくようになる。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("楳図かずお恐怖劇場　絶食", "楳図かずお恐怖劇場　絶食", "", "", "http://ecx.images-amazon.com/images/I/51195J83ZVL._SL210_.jpg", 60, "恐怖マンガの巨匠・楳図かずおの短編コミック6作品を、注目の映画監督＆キャストで実写映画化したオムニバス「楳図かずお恐怖劇場」の1作。「リング」シリーズの脚本家として知られる高橋洋が脚色を手がけ、過激なダイエットにのめりこんでいく少女の運命を描く。思いを寄せるあきらにフラれてしまった女子高生の知子。なんとかあきらに振り向いてもらおうとダイエットを決意した知子は、絶食によってついに理想的な体型を手に入れるが……。", "2005-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛と死", "愛と死", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "武者小路実篤の『友情』『愛と死』をミックスさせ、山田太一が脚色した。高校時代の親友との友情と、親友の恋人への愛との狭間で苦悩する男を新克利が演じ、一人が破滅することになる三角関係の情景を描く。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シュート！", "シュート！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "大島司の同名人気マンガを、SMAPを主演に据えて映画化。大森監督はこれを手堅くまとめて、正統派のさわやかな青春サッカー映画に仕上げた。先輩・久保の天才的サッカー・プレーに憧れて掛川高に進学したサッカー少年、トシが逆境を乗り越え、久保から学んだ左足のシュートに勝利の道を切り開いていく。地面スレスレの移動撮影を駆使したサッカー・シーンにも精彩がある。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雄呂血", "雄呂血", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "それまでの大見得をきるような旧劇から、時代劇に激しい立ち回りを持ち込んで、新しい波を作り出した大スター、阪東妻三郎。その無声映画時代の代表作が本作品だ。主人公を最後のギリギリの淵まで追いつめ、爆発的な大乱闘を展開するという手法を日本映画に定着させた大作である。善意の行動が誤解を受けて藩を追われた若侍が、放浪の旅の途中で知り合った女に裏切られ、ある親分の用心棒となる。が、親分が若侍の初恋の女を犯そうとするのを知り、ついに怒りが爆発。多勢を相手に大立ち回りを演じる。熱い反逆のニヒリズム、立ち回りの間（ま）に見せる血のたぎりを時代劇に持ち込んだ記念碑的作品。", "1925-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO FOR THE ROAD", "いつも2人で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「シャレード」に続くヘプバーンと名匠ドーネンのコンビ作品で、現在と過去を巧みに交錯させたハイセンスな画面構成で、12年間にわたる一組の夫婦の愛の軌跡を描いた佳作。H・マンシーニの音楽も華麗で、フィニーの味のある芸の広さやオードリーの色とりどりに変わるファッションなど、見どころがたっぷり。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Jacket", "ジャケット", "", "", "http://image.eiga.k-img.com/images/movie/1458/315x210.jpg?2229", 103, "「愛の悪魔／フランシス・ベイコンの歪んだ肖像」のジョン・メイバリー監督による異色サスペンス。1992年、湾岸戦争で負傷して記憶を失ったジャックは、精神病院で拘束衣＝ジャケットを着せられて特殊な治療を受けることになる。だが、治療中に意識を取り戻したジャックは、2007年の別の場所にいた。彼はそこで出会った女性ジャッキーに心惹かれるが、彼女の協力で自分の過去を調べていくと、彼は1993年に死亡したことになっていた。", "2006-05-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("清水の次郎長 任侠中仙道", "清水の次郎長 任侠中仙道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "時代劇のヒーロー、清水次郎長と国定忠治が、互いに子分を従え、農民を苦しめる悪代官らを懲らしめるという時代劇アクション。清水次郎長を片岡千恵蔵、国定忠治を市川右太衛門が演じているほか、月形龍之介、大河内伝次郎、大友柳太朗など、オールスター・キャストが揃い踏み。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("えきすとら", "えきすとら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "いつかスターになることを夢見て九州から出てきた青年と、エキストラ仲間のガールフレンドとの恋を描く、朝間＝武田コンビによる４作目の青春映画。松竹大船撮影所をそのまま使った劇中の撮影所シーンなど、楽屋裏が垣間見られるのも見どころの一つ。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Clownhouse", "ピエロの館 マニアック1990", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "子供たちにとって何よりも楽しいはずのサーカスで、その人気者であるピエロが殺人鬼となって突然彼らを襲い始める。最も愛すべきアイドルが、一瞬にして恐怖と惨劇をもたらす悪魔の存在に変わる、という設定のホラー映画。伏線や人物の性格づけがしっかりしており、ホラー映画にありがちな矛盾を感じさせない。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED MOB", "レッド・モブ 地獄からの奪還", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "“レッド・モブ”とは、旧ソビエトの兵士たちが結成したマフィア。政府の有力者にも顔が利く彼らは、アフガンからの武器・麻薬の密輸をたくらむ。“レッド・モブ”のボス、ヤファールは、この作戦にアフガンでの勇者オレグを引き込もうとするが、オレグはそれを拒否。残忍なヤファールはオレグの息子を誘拐する。激動するロシアで、ペレストロイカを題材としたアクションものとして注目を集めた映画。", "1992-01-01", "1,24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Magic Flute", "魔笛", "", "", "http://image.eiga.k-img.com/images/movie/28698/315x210.jpg?2454", 139, "1791年、作曲家ボルフガング・アマデウス・モーツァルトが生涯最後に完成させた同名オペラを「フランケンシュタイン」「ハムレット」のケネス・ブラナー監督が、舞台を古代エジプトから第1次世界大戦下のヨーロッパに置き換えて映画化。キャストには、世界屈指のバスとして知られるドイツ出身のルネ・パーペ、実力派テノールのジョセフ・カイザー、ロシアのソプラノ歌手リューボフ・ペトロバをはじめとする一流の歌手が名を連ねている。", "2007-07-14", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前満貫", "喜劇・駅前満貫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "駅前通りのマージャン屋の主人・徳之助は大繁盛にゴキゲン。近所のミカン売り・孫作とアイデア・センターの次郎は悪友連。次郎は人口胸毛など新製品開発に燃えているが失敗ばかり。彼らとキレイな女性たちが巻き起こす珍騒動。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Graduate", "卒業", "", "", "http://ecx.images-amazon.com/images/I/51x4bqGO1VL._SL210_.jpg", 107, "成績優秀で大学を卒業したベンジャミンは、両親が開いたパーティーで父親の知り合いの中年女性ロビンソン夫人に誘惑され、関係を持つようになる。二人はデートを重ねるが、夫人の娘のエレーヌが学校の休みで帰郷したことで、関係が微妙になる。心優しく純真なエレーヌに惹かれ愛し始めるようになるベンジャミンに対し、嫉妬した夫人は交際を続けるなら、二人の関係を娘にばらすと脅すが、ベンジャミンの気持ちは変わらなかった。怒った夫人は、エレーヌにベンジャミンとの不倫をばらし、ショックを受けたエレーヌは学校に帰ってしまう……。D・ホフマンの映画デビュー作であり、アカデミー監督賞を受賞した青春映画の名作。特に結婚式から花嫁を奪い出すラストシーンが話題になった。また、『サウンド・オブ・サイレンス』『ミセス・ロビンソン』などサイモン＆ガーファンクルの挿入歌もヒットした。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サンクチュアリ", "サンクチュアリ", "", "", "http://image.eiga.k-img.com/images/movie/13338/315x210.jpg?1760", 95, "「DOG STAR」「肌の隙間」の瀬々敬久監督が、のどかな田舎町を舞台に繰り広げられる2人の女の愛憎劇を描いた衝撃作。深夜、走行する車の中で目覚めた山本アキは、自分が手錠をかけられていることに気づく。車を運転しているのは黒木裕子。2年前に出会って以来、2人は激しい愛と憎しみで結ばれていた……。2人のヒロインを演じるのは「嫌われ松子の一生」の黒沢あすかと、「油断大敵」「青い車」の山下葉子。", "2006-10-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺たちの血が許さない", "俺たちの血が許さない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "日活時代の鈴木清順が最もノッていた頃の作品。ヤクザの兄と会社員の弟が、暴力団の抗争に巻き込まれる。恋人を殺され復讐を誓う兄が、それでも弟にだけはまじめな一生を送らせようとするが……。車に降る不思議な雨の効果が有名。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THEY DIED WITH THEIR BOOTS ON", "壮烈第七騎兵隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "南北戦争の英雄カスター将軍を描いた伝記映画。士官学校の落ちこぼれカスターは北軍として出兵、勇敢な戦いぶりで凱旋し副将軍に昇進。退役軍人の生活に飽き足らない彼は、インディアンとのゴタゴタの続くリンカーン砦の第七騎兵隊の司令官となるが……。戦争シーンよりも人間ドラマに重点を置いた秀作。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長靴をはいた猫", "長靴をはいた猫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "脚本の井上ひさし、山元護久から美術の浦田又治、土田勇に至るまで、スタッフがそれぞれの個性をフルに発揮し、それらがバランスよくまとめ上げられた会心作。原作となったペローの童話に、猫の殺し屋トリオやネズミの泥棒たちなどコメディ・リリーフが加えられ、東映長編動画中でも最上質の“笑い”に満ちている。美しいローザ姫が暮らす城に現れた魔王ルシファが、姫と３日後に結婚すると王様たちに宣言した。それを知った猫のペロは、二人の兄に家から追い出された若者、ピエールを公爵と偽らせ、姫の婿にと推薦する。だがピエールに好意を持った矢先、姫はルシファにさらわれてしまう。魔王の城に乗り込んだピエールとペロは、ルシファの弱点が首にかけたペンダントであることを知る……。クライマックスの舞台となる魔王の城には、様々な仕掛けがほどこされ、巧みにスリルを盛り上げている。宮崎駿が原画マンとして参加している。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHINJUKU BOYS", "新宿ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "二人のイギリス人女性監督が、新宿のいわゆる“おなべ”（男装している女ホスト）の実態を浮き彫りにしたドキュメンタリー。外国人の目から、女であり、男でもあるおなべたちの生活を見つめている。監督の一人、J・ウィリアムズは13年間日本で生活した経験がある。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOY ON A DOLPHIN", "島の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "エーゲ海に眠る古代ギリシャの秘宝“イルカに乗った少年”を発見したヒドラ島の女フェードラは、アテネに行き、考古学者や収集家にそのことを話す。純金の像を独り占めしようとする収集家の陰謀。しかし像は守られフェードラも考古学者と結ばれる。野性味あふれるS・ローレンの若い肢体が魅力的。主題歌『イルカに乗った少年』はあまりにも有名。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("競輪上人行状記", "競輪上人行状記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "西村昭五郎監督の会心のデビュー作。住職の突然の死で、オンボロ寺を任された男が未亡人への思慕も手伝って、真面目に務めを果たそうとする。ところが未亡人にふられて、ヤケになって競輪に狂い、すべてを失う。最後には競輪の予想屋になって生き抜いていくバイタリティーが、実に爽快だ。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACKMAIL", "ヒッチコックの恐喝〈ゆすり〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "劇場未公開のヒッチコック初期の貴重な作品。雑貸商の娘アリスは恋仲の刑事フランクと街へ遊びに行ったが、いさかいを起こし、男に誘われてついていった。男は画家で、自室に行くと、アリスは襲われそうになり、逆に男を殺してしまう。事件を担当したフランクは遺留品から犯人がアリスと知った。同じ頃、事件の真相を知った浮浪者がアリスたちをゆすりに来るが、この男は警察の要注意人物で、フランクはこの男に犯罪をなすりつけようとする。駆けつけた警官たちを見て、男は逃走し、最後に大英博物館に逃げ込むが、ドームの上に追い詰められて墜落死した。イギリス映画の記念すべきトーキー第１作。サイレント映画で制作中だったものの一部を作り変えてトーキーにした。ナイフという言葉にアリスが動揺していくシーンのような、トーキー演出とイントロのサイレント・タッチの両方が楽しめる貴重品。", "1929-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大陸流れ者", "大陸流れ者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "香港の水の欠乏を解消すべく、浄水場建設工事を進める関東若松会・国分銀次郎。だが水を高く売って利益を得ている香港の不良外人グループ・和合会に工事を妨害され、銀次郎の弟・勝治も殺される。身の危険を顧みず殴り込みをかける銀次郎だが……。ヤクザ映画の名手、山下耕作の佳編。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星影のワルツ", "星影のワルツ", "", "", "http://image.eiga.k-img.com/images/movie/33938/315x210.jpg?1294", 97, "写真家として第一線で活躍してきた若木信吾の監督デビュー作。写真集「Takuji」など被写体として20年間カメラに収め続けた、今は亡き祖父との思い出を詩情豊かに綴る。浜松の実家に帰省した信人は、祖父・啄次の姿をコンパクトカメラで撮影したり、幼なじみの友人たちと遊んだりと平穏な日々を過ごしていた。そんなある日、啄次の兄が突然自殺してしまい……。上方漫才の大御所・喜味こいしが祖父役を好演。", "2007-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Knowing", "ノウイング", "", "", "http://image.eiga.k-img.com/images/movie/53848/315x210.jpg?2977", 122, "ニコラス・ケイジ主演、「アイ，ロボット」のアレックス・プロヤス監督によるディザスター・サスペンス。マサチューセッツ工科大学の宇宙物理学者ジョンは、息子ケイレブが通う小学校の記念式典で50年前のタイムカプセルから取り出された1枚の紙に綴られた数字の羅列が、過去50年間の災害、事故、事件の発生と犠牲者数を的確に予言していることを突き止める。その紙にはさらに、これから起こる未曾有の大災害も予言されていた……。", "2009-07-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUNNING OUT OF LUCK", "ランニング・アウト・オブ・ラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ビデオクリップから映画へ進出したJ・テンプル監督による、M・ジャガー主演作品。ブラジルを舞台に、田舎へ迷い込んだロック・スター、M・ジャガーが繰り広げるユニークな冒険を描く。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NAKED FACE", "露骨な顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "精神科医ジャドの周囲で次々に殺人事件が起き、ジャドは容疑者にされてしまう。無実の罪を訴えるが、彼に恨みを抱く警部補は取り合わない。だが、今度はジャドが殺し屋に命を狙われ、本当の標的が自分だと知ったジャドは、自ら真犯人を追うが……。ベストセラー作家、S・シェルダンのデビュー作を映画化したサスペンス。通好みの渋いキャスティングが見もの。ビデオ発売時タイトルは「顔のない男」。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Butterfly Effect 3: Revelations", "バタフライ・エフェクト3 最後の選択", "", "", "http://image.eiga.k-img.com/images/movie/54778/315x210.jpg?2468", 90, "自由自在にタイムスリップできる男を描いた「バタフライ・エフェクト」シリーズの第3弾。米TVシリーズ「デスパレートな妻たち」のクリス・カーマックが主演。過去に戻って殺人現場を目撃し、警察の捜査に協力することを生業としていたサムのもとに、ある日、かつての恋人レベッカの姉・エリザベスが訪ねてくる。「10年前に妹を殺した犯人を捕まえて欲しい」と懇願されたサムは、周りの反対を押しのけて事件の真相を究明しようとする。", "2009-10-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEN", "ベン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ねずみが人間を襲うパニック「ウイラード」の続編。生き残ったねずみのリーダー、ベンと心臓病で体の弱い少年の奇妙な友情がテーマ。前作のぞくぞくする恐怖感には今一歩という仕上がりだが、マイケル・ジャクソンの主題歌が大ヒットした。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("視界ゼロの脱出", "視界ゼロの脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "女アウトローをボスとする犯罪集団と、パイロットが対決するという図式の爽快なアクション映画。看護士を乗せてワクチンを輸送中の飛行機が、台風観測所の指示を受けある島に着陸した。すると、観測所を占拠して飛行機を乗っ取ろうと狙っている悪の一団がいた。だが、彼らの目的地へ行くまでの燃料が不足していた……。銃撃、爆発炎上などの活劇シーンが見せ場に。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("S.W.A.T.", "S.W.A.T.", "", "", "http://image.eiga.k-img.com/images/movie/1678/315x210.jpg?4792", 111, "麻薬王アレックスが「逃がしてくれたヤツには1億ドル払う」と宣言し、街中のギャングがその賞金のため彼の奪回を計画。そんな状況の中、相棒のせいでS.W.A.T.から追放されていたストリート、女性ながらすご腕のサンチェスらがアレックス護送の任務に当たる。70年代の同名TV番組の映画化。監督は俳優として「ニック・オブ・タイム」などで活躍、TV監督を経て映画に進出したクラーク・ジョンソン。", "2003-09-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAND OF ANGELS", "南部の反逆者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "南北戦争時代の奴隷問題をテーマとした、R・ウォルシュの名作。父を亡くし、恋人が奴隷解放運動に参加して北へ行ってしまい、自らも奴隷として売られてしまったマンシイ。そんな彼女を買い取ったのはハミッシュという理解ある男だった。やがて戦火が迫り、ハミッシュの家も攻め入られる。いったんは北軍の手に渡されるハミッシュだが……。ハミッシュ役はC・ゲーブル。R・バラードによる撮影、M・スタイナーによる音楽など、一流スタッフが顔をそろえている。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Harry Potter and the Order of the Phenix", "ハリー・ポッターと不死鳥の騎士団", "", "", "http://image.eiga.k-img.com/images/movie/1888/315x210.jpg?3348", 138, "前作で闇の帝王ヴォルデモート卿（レイフ・ファインズ）の復活を目の当たりにしたハリー（ダニエル・ラドクリフ）だが、魔法省はその事実を認めようとしない。ハリーは、来るべき戦いに備え、有志の生徒を集めて魔法の特訓に励むが……。イギリスTV界出身のデビッド・イェーツが、長編映画初監督でメガホンを取った人気シリーズ第5弾。おなじみのキャスト陣に、イメルダ・スタウントン、ヘレナ・ボナム・カーターらが新たに参加。", "2007-07-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("賭侠３上海灘賭聖／GOD OF GAMBLERSIII BACK TO SHANGHAI", "ゴッド・ギャンブラー３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "1991年の香港で、中国の超能力ギャンブラー、大軍、超能力大師らと戦う賭聖（No.1ギャンブラー)、シン。しかし、彼らのあまりに強力な超能力は地球の磁場に影響し、全員が1937年の上海にタイムスリップしてしまう。シンたちは、そこでディン・ロクと日本人、川島女史のカジノ対決に巻き込まれ……。シン役を気鋭のC・シンチーが熱演。シンが一目惚れする美女をG・リーが華麗に演じる。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅の長脇差 花笠椿", "旅の長脇差 花笠椿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "それまで松竹で活躍してきた高田浩吉が東映に入社しての第１回出演作品。暴漢に襲われた伊丹屋万兵衛の息子を救った浪人の椿平九郎。その伊丹屋は長五郎の強要によって娘・美津江を旗本、日下部家の進之丞と見合いさせることに。そして付き添いを請われた平九郎は見合いの場で進之丞の父を斬ってしまう。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CANTERBURY TALES", "カンタベリー物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "中世イギリスの詩人チョーサーの同名物語集を、鬼才パゾリーニが映画化したオムニバス形式の艶笑コメディ。とある宿に泊まったチョーサーが、カンタベリー寺院への巡礼者たちから話を聞くという設定でドラマが展開。若い娘と結婚した老人が失明してしまう話や、死神の宝物をめぐる話など、８つのエピソードが大らかなセックス描写を交えて語られていく。パゾリーニ自身がチョーサーに扮して出演。DVDタイトルは「パゾリーニ・コレクション　カンタベリー物語【オリジナル全長版】」。", "1972-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Exorcist: The Version You've Never Seen", "エクソシスト　ディレクターズ・カット版", "", "", "http://image.eiga.k-img.com/images/movie/1058/315x210.jpg?4190", 131, "1970年代のオカルト映画ブームを牽引したホラー映画の金字塔「エクソシスト」（73）に15分間の未公開シーンを追加したディレクターズ・カット版。女優のクリスは、一人娘リーガンの12歳の誕生日を祝おうと友人達を招待した。夜更けまでパーティーが盛り上がっていると、突然、寝室から降りてきたリーガンが、客に汚い言葉を吐き、しかも、立ったまま放尿した。そしてその夜、リーガンの部屋から、突然大きな悲鳴が聞こえてきた。駆けつけたクリスが見たものは、激しく揺れるベッドと、その上で泣き叫ぶ娘の姿だった……。", "2000-11-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUST YOU AND ME，KID", "裸足の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "かつて舞台の喜劇俳優として活躍した82歳のビルは、健康で快適な余生を送っている。そこに舞い込んだのが、車のトランクに潜り込んでいた素っ裸の少女ケイト。孤児院育ちで同棲相手から逃げ出したという彼女と、ビルの奇妙な共同生活が始まった……。B・シールズが16歳の時に主演したほのぼのコメディ。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恐怖", "恐怖", "", "", "http://image.eiga.k-img.com/images/movie/55438/315x210.jpg?2818", 94, "「リング」「仄暗い水の底から」「呪怨」などで知られるプロデューサー・一瀬隆重が手がけるホラー。「リング」の脚本を務めた高橋洋がメガホンをとり、藤井美菜、中村ゆりが主演を務める。森の一軒家で優しい両親に育てられた無垢な姉妹みゆきとかおり。2人はある真夜中、悪夢にうなされてベッドを抜け出す。そのとき、両親が上映していた16ミリフィルムが発する不気味な白い光を浴びてしまった2人は、大人になっても死への誘惑に取り憑かれていた……。", "2010-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ПИСЪМА МЕРТВОГО ЧЕЛОВЕКА", "死者からの手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "某国のコントロール・センターで、技師のささいなミスのために、核兵器の発射ボタンが押されてしまった。太陽の光は届かず、防毒マスクがなくては生きていけぬ死の世界で、行方不明の息子を探し続ける学者ラルセンを中心に、核シェルターを求める人々の確執を描く近未来SF。ロプシャンスキー監督のデビュー作。", "1986-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE WITH THE PROPER STRANGER", "マンハッタン物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "出来心の一夜がもとで女を妊娠させてしまい、堕胎医を探してほしいと頼まれてしまったジャズ楽団の男の悲喜劇を描いたコメディ。主演のS・マックィーンが、アクションものとはまた違った絶妙のコメディアンぶりを披露している。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dear John", "親愛なるきみへ", "", "", "http://image.eiga.k-img.com/images/movie/55318/315x210.jpg?3947", 108, "「メッセージ・イン・ア・ボトル」「きみに読む物語」で知られるニコラス・スパークス原作のベストセラー（邦題「きみを想う夜空に」）を、名匠ラッセ・ハルストレムが映画化したラブストーリー。ドイツ駐在の特殊部隊兵士ジョンは、アメリカ帰郷中に女子学生サバナと出会う。2人はすぐに恋に落ちるが、ジョンの仕事のために離ればなれに。文通しながら遠距離恋愛を続け、ジョンの除隊後に結婚を約束する2人だったが、9・11の同時多発テロ事件が発生し……。主演に「G.I.ジョー」のチャニング・テイタム、「マンマ・ミーア！」のアマンダ・セイフライド。", "2011-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("群青の夜の羽毛布", "群青の夜の羽毛布", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "直木賞受賞作家、山本文緒の同名小説を「がんばっていきまっしょい」の磯村一路監督が映画化。支配的な母親の呪縛から逃れられないアダルトチルドレンの女性を主人公に、彼女が大学生の青年と出会い交際を始めたことから、巣立とうとする姿を追っていく。「tokyo.sora」でも好演を見せた本上まなみが映画初主演。もろそうに見えて頑固なヒロインの内面を、無理のない演技で表現している。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLONDE VENUS", "ブロンド・ヴィナス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "アメリカの科学者ファラディは、ドイツでクラブ歌手のヘレンを見初め、故郷に戻って結婚する。息子にも恵まれ、彼らは幸せな家庭生活を続けた。だが、やがて夫は病気になり、彼女は再び舞台に立つことになる。ほどなく“金髪の女神”としてスターになったヘレンの前に、若き政治家ニックが現れたことによって、彼女の心は夫とニックの間で激しく揺れるのだった。映画史に残るコンビ、スタンバーク監督とディートリヒの５本目となるメロドラマ。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古墳ギャルのコフィー　コフンデレラ", "古墳ギャルのコフィー　コフンデレラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「秘密結社　鷹の爪」のFROGMANが、さまざまな古墳たちの繰り広げる騒動を描いたシュールな短編FLASHアニメ「古墳ギャルのコフィー」の劇場版第3作。心優しい古墳の娘コフィーは、義姉のダニエルらにこきつかわれる日々を送っていた。ある日、誰もがあこがれるチャールズ王子が花嫁をさがす舞踏会を開くが、留守番を言いつけられたコフィーは舞踏会に参加することができず……。「秘密結社 鷹の爪 THE MOVIE3 http://鷹の爪.jpは永遠に」と同時上映。", "2010-01-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白椿", "白椿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "夢野久作の同名小説を、畑野ひろ子＆加藤夏希主演で映像化。文学館に勤めている知美は、娘の世話を父に任せて仕事に追われる毎日を送っていた。夫はそんな彼女の生き方に理解を示さず、彼女に心ない言葉を投げかける。一方、受験生の鈴は、母親からのプレッシャーに押しつぶされそうになっていた。そんな2人が、偶然通りかかった公園で季節はずれの白椿を見つける。白椿の前で大きなクシャミをした2人は、白椿と入れ替わってしまい……。", "2007-10-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POUSSIE｀RES　D’AMOUR", "愛の破片", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "オペラの演出家としても活躍するドイツの映画監督W・シュレーターが手掛けた、ドキュメンタリー風の映像エッセイ。シュレーターの敬愛する名歌手アニタ・チェルケッティとマルタ・メードル、女優キャロル・ブーケがパリ近郊の修道院に結集。彼女たちの歌声はもちろん、恋愛観や死生観を語るインタビューを交えながら、愛と死の本質に迫る。彼の親友であるI・ユペールが、インタビュアーとして出演。ユペールは歌も披露している。", "1996-01-01", "7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOLDILOCKS AND THE THREE BEARS", "スリー・ベアーズ＆リトル・レディ魔法の森のおしゃべりクマさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "両親を事故で亡くした10歳の少女、ゴールディーは大自然に囲まれたハル叔父さんの家に預けられる。ある日、迷い込んだ森の中で、ゴールディーは人間の言葉を話せる３匹のクマに出会う。彼らはこの魔法の森で暮らす“森林熊”の最後の生き残りだった。ところが、この森をリゾート開発業者が不当に破壊しようとしていることが分かり……。「フォレスト・ガンプ／一期一会」で、ガンプの彼女ジェニーの少女時代を演じたH・ホール主演作。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東おんな悪名", "関東おんな悪名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "江島組二代目を襲名した美樹は大阪に来て、悪どい東西会に潰されかけている島田組にやっかいになる。島田組復興のために力を貸すことを誓う美樹だが、東西会は美樹暗殺にのり出す……。登場人物のキャラクターが実にいきいきと描かれた「関東おんな極道」の続編にあたる快作。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("本日またまた休診なり", "本日またまた休診なり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "戦後の京都で開業医を営む我が父は、町の人々に慕われる一方、今日も“本日またまた休診なり”の札を出して、どこかへいなくなる。しかし、それでも待合室は社交場のように賑やかなのであった……。山城新伍が自伝をベースに、貧しくも古き良き時代をコミカルに描いたヒューマン・ドラマ。一癖も二癖もある個性派俳優たちの共演も楽しい。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLOSE MY EYES", "クローズ・マイ・アイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "両親の離婚で離れ離れに育てられた姉弟、ナタリーとリチャード。二人は久しぶりに再会するが、金持ちと結婚した姉は見違えるように美しくなっていた。やがて弟は彼女に身も心も奪われ、許されぬ愛に溺れていく。エイズや環境問題、失業問題などイギリスが抱える悩みを随所に織り込みながら展開するスリリングで官能的な作品。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHERE THE HEART IS", "あなたのために", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "N・ポートマンが健気な未婚の母を演じる感動の人間ドラマ。17歳のノヴァリーは、身重の体のまま恋人に知らない町に置き去りにされる。スーパーマーケットに忍びこんで寝起きしていた彼女は、やがてそこで出産、一躍有名人になる。温かい町の人々に囲まれて新しい人生を歩み始めたノヴァリーは、心優しい青年と恋に落ちるが……。血縁関係を超えて強い絆をはぐくむ人間模様を温かいまなざしで描き出す。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAIDERS OF THE MAGIC IVORY", "地獄のレイダース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ホラー、アクション、スペクタクルなど様々な要素をミックスしたエンターテインメント作品。おどろおどろしいジャングル地帯を舞台に、ゲリラや黒魔術を操る不気味なゾンビや、ゲリラ、美女などが登場。“マジック・アイボリー”の秘密をめぐる男たちの戦いが描かれる。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HORNET’S NEST", "要塞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "第二次大戦下のイタリア戦線。ナチス・ドイツの虐殺を受けた村で、生き残った17人の子供が、米軍将校に率いられ、パルチザン部隊となって復讐を果たすアクション映画。ラスト、彼らは戦争のむなしさに目覚めていく。ダム破壊のクライマックス・シーンが迫力。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("御存じ快傑黒頭巾 新選組追撃", "御存じ快傑黒頭巾 新選組追撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "大阪を中心とする薩長の倒幕軍用金調達運動を妨害するため、幕府は新選組を大阪に送り込む。新選組は、何度も軍用金奪取に失敗。その裏には、曲芸美津次一座に潜り込み、新選組の動向を探っていた黒頭巾の活躍があった……。ニセ黒頭巾の乱行による仇討ち騒動が、サブ・ストーリーとして絡む。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPIRIT OF THE AIR: GREMLINS OF THE COLUDS", "スピリッツ・オブ・ジ・エア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ミュージック・ビデオの製作によって、数々の賞に輝いたA・プロヤス監督による、初の劇場用映画。荒野の中ただ１軒の家。ここに、空を飛ぶ夢に取り憑かれた、足の不自由な兄と、この地に異常な執着を持つ妹が住んでいた。そこに一人の流れ者が現れ、兄と意気投合。二人で人力飛行機を作り始めるが……。", "1988-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("群鴬乱舞／PROFILES OF PLEASURE", "エロティック・ヘヴン 鴬華楼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1930年代の香港の遊郭を舞台にした、エロティック・ストーリー。男たちからは最も評判が高く、婦人たちからは悪名高い、ある遊郭。未亡人のインホンと、知性を売り物にしているチンチンは、ここの二枚看板だった。だが繁盛していた店も、婦人活動家のつきあげもあり、警察の手がのびてきた。その警察の捜査陣の中には、インホンの昔の恋人がいた。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道兇状旅", "極道兇状旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "釜ヶ崎の極道・島村清吉は、ヤクザの出入りで警察に逮捕されるが越が谷竜蔵の計らいで釈放される。越が谷は、実は島村の縄張りを独占しようという魂胆であった。島村はそんなこととは知らず、旅に出てしまう……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四谷怪談", "四谷怪談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "原作は歌舞伎の当たり狂言として名高い鶴屋南北の代表作。何度となく映画化され、日本映画の夏の興行には欠かせない素材として名高い。豊田演出は原作を古典として忠実に解釈、グロテスクな要素は排した。歌舞伎界から参加した第十七代目・中村勘三郎の怪演が圧巻。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十五少女漂流記", "十五少女漂流記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "南海の無人島に取り残された15人の少女たち。空腹、いがみ合い、襲い来る嵐、仲間の死……。そして一人が妊娠していると分かった時、少女たちは命の尊さを再認識し、困難に向かって生き続けようと団結する。喜多郎の音楽と大自然の美しい映像にのせて、３万人以上の中から選ばれた少女たちが活躍。ビデオタイトルは「喜多郎の十五少女漂流記」。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOCTOR PHIBES RISES AGAIN", "怪人ドクター・ファイブスの復活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「怪人ドクター・ファイブス」で妻を生き返らせようとしたファイブス博士が、今回はエジプトの死者をよみがえらせる生命の河を探し求め、科学者バイダペックとその先陣争いを始める。しかし、その身辺で謎の殺人事件が勃発して……。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tom and Huck", "トム・ソーヤーの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "マーク・トゥエインの名作「トム・ソーヤの冒険」を、ハリウッドの人気子役ジョナサン・テイラー・トーマスとブラッド・レンフロー主演で映画化。「ビルとテッドの地獄旅行」のピーター・ヒューイット監督がメガホンを取る。ミシシッピー川沿いの小さな町。いたずら好きのトム・ソーヤーと宿無しハックは大親友。ある夜、彼らは墓場で恐ろしい殺人事件を目撃してしまう。恐怖のあまり慌ててその場を立ち去った彼らだったが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Farce of the Penguins", "童貞ペンギン", "", "", "http://image.eiga.k-img.com/images/movie/34058/315x210.jpg?1390", 80, "日本でも大ヒットを記録したドキュメンタリー「皇帝ペンギン」のパロディ。愛するメスペンギンとのセックスを夢みる童貞ペンギン・カールとタフガイ気取りの親友・ジミーが、メスペンギンたちを求めて70マイルにおよぶ南極大陸横断の旅に出る。製作・監督・脚本・声の出演を務めるのは、人気ドラマ「フルハウス」などコメディアンとしても活躍するボブ・サゲット。ナレーションをサミュエル・L・ジャクソンが担当する。", "2007-07-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門捕物帖 片目の狼", "右門捕物帖 片目の狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "嵐寛寿郎主演によるシリーズ終了後、東映に場を移して再開された大友柳太朗主演の一編。“むっつり右門”の異名をとる、江戸の同心・近藤右門が“江戸名物・首尾の松”で発見された５人の首吊り死体をめぐる難事件を、名推理で解決する。大友柳太朗のどこか飄々とした持ち味に加え、堺駿二、進藤英太郎など、名バイプレーヤーの演技も見もの。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋の大冒険", "恋の大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "和田誠・山田宏一・渡辺武信といった映画の楽しさを語らせたら右に出る者はいない３人のシネマディクトが集結し、ありったけの映画的知識を注ぎ込んだミュージカル・コメディ。当時、野心的な映画製作を続け、成功を収めていた羽仁進だが、人気絶頂のピンキーとキラーズのスケジュールに振り回され、不本意な出来となった。企画当初の「大失恋」という題名通りの内容をアニメや多くのゲスト陣が彩る。特にピンキーとアニメのカバのデュエット（実はカバの声もピンキー）が見もの。なお、山田と和田はこの作品が初対面だったという。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・平家物語 義仲をめぐる三人の女", "新・平家物語 義仲をめぐる三人の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "吉川英治の歴史小説『新・平家物語』を映画化した３部作の第２作。海外では高い評価を得た「地獄門」と同じ時代設定。ほぼ同じスタッフ・出演者で製作した歴史大作で、豪華絢爛たる色彩画面が見もの。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画ジュエルペット　スウィーツダンスプリンセス", "映画ジュエルペット　スウィーツダンスプリンセス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OCI CIORNIE", "黒い瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "チェーホフの『小犬を連れた貴婦人』ほか、数編の短編小説をもとにしたロシア人の人妻に恋をした中年イタリア人の物語。20世紀初頭、イタリアに向かう船のレストランで初老のロシア商人は、イタリア人のロマーノから彼の恋の話を聞く……。大銀行家の一人娘エリザと結婚して25年になるロマーノは、仕事もせずに怠惰な生活を送っていた。ある日、湯治場でロシア女性アンナと出会い、一夜をともに過ごすと、彼女はロシアに去って行った。ロマーノは、アンナを追ってロシアに行き、アンナに新しい生活を約束して身辺整理のためにローマにいったん帰る。しかし、家に帰ると、妻エリザの事業は破産し、ロマーノに慰めを求める彼女に、本当のことが言えず、アンナとはそれきりだった。こうしてロマーノは今は落ちぶれて船のウェイターをしているのだった……。意志薄弱な中年男の悲しさ、愚かさをマストロヤンニが見事に演じる。F・レイの音楽が美しく、撮影、美術ともに素晴らしい。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺は田舎のプレスリー", "俺は田舎のプレスリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "青森県五所川原ののどかな風景を舞台に、リンゴ園で働く純情素朴青年の恋と、その兄の性転換をめぐる珍騒動を描いた喜劇。映画のヒントとなったのは吉幾三のヒット曲『俺はぜったい！プレスリー』。彼自身も純情少年の友人として、チョイ役で出演している。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CLASSE OPERAIA VA IN PARADISO", "労働者階級は天国に入る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "工場のモデル労働者として信望の高かった男が、作業中の事故で重症を負った。それを理由に解雇を言い渡された男のため、労働者たちは決起し、ストライキに突入する。しかし、その時すでに職場復帰を果たしていた男は、一人ストライキ中の工場へ向かう……。イタリアの社会派E・ペトリ監督が、ストライキを主題に描いた社会派人間ドラマ。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("C.I.A.II TARGET：ALEXA", "アレクサ・リターンズ C.I.A.2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "元CIA部員アレクサは二人の娘とともに、母として女として平穏な日々を送るはずだった。だが非情な指令により、再び任務に戻ることになる。そして、盗まれた核兵器のコントロール・システムを奪回するため、国際的テロリストの軍事キャンプへ向かう。「ターミネーター２」「アビス」の美術デザイナー、ディブ・ハルバーが制作した本物そっくりのCIA本部や兵器収容基地が見どころ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INCOMPRESSO", "天使の詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "父親と幼い息子の断絶を描いたモンゴメリーの名作の映画化。フィレンツェ駐在の英国領事ダンカンは、妻に先立たれ、幼い息子アンドレアとミロが残される。だが、悲しみに沈むアンドレアの心を父親は理解せず、父と子の間の溝は深まっていく。そんなある日、アンドレアは木から落ちて死んでしまう……。", "1967-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋人をさがそう", "恋人をさがそう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ラグビー部のキャプテン・哲夫は大学受験を控えたある日、彼をたった一人で育ててくれた母の交通事故の報を聞く。哲夫は進学をあきらめ、手術代を稼ぐためマネージャー・田鶴子の父に紹介してもらった新聞の配送係に。やがて母が回復し、再び受験に取り組む哲夫の脇には田鶴子が……。月刊誌『平凡』に連載された大林清の同名小説を映画化した青春歌謡もの。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TREE OF HANDS", "身代りの樹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "R・レンデルの同名原作を完全映画化したミステリー。最愛の息子を亡くして悲しみに沈むベネットの身を案じ、情緒不安定に陥った母マーシャは、何の罪の意識もなしに一人の幼児を誘拐してきた。しかし、その幼児の体には無数の傷跡があり、いつしかベネットは、この子に愛情を抱き始めるが……。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Coraline", "コララインとボタンの魔女 3D", "", "", "http://image.eiga.k-img.com/images/movie/54438/315x210.jpg?3625", 100, "「ナイトメアー・ビフォア・クリスマス」のヘンリー・セリック監督が、ニール・ゲイマンによるファンタジー小説を、ストップモーションアニメで映画化。好奇心旺盛な少女コララインは、引っ越してきたばかりの古いアパートで、封印された小さなドアを見つける。ドアの向こうには不思議なサーカスやミュージカルで賑わう異世界が広がっており、そこにはコララインのいうことを何でも聞いてくれる優しい両親がいたが、彼らの目はなぜかボタンでできていた。", "2010-02-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOME FOR THE HOLIDAYS", "ホーム・フォー・ザ・ホリデイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "都会人の視点で“家庭”の憂うつをリアルでコミカルに描いたJ・フォスター監督第２作。クローディアは大都会シカゴで、美術館の仕事をしながらシングルマザーとして自活してきたキャリアウーマン。だが、ある日突然、仕事をクビになってしまう。呆然としながらも感謝祭に参加するため、故郷に戻った彼女をまたもや災難が襲う。落ち着きのない両親、同性愛の弟、いじけた妹、ボケはじめた叔母……。家族それぞれの恥部が明かされ、せっかくの集いは混乱模様に。クローディアの唯一の救いは弟の友人レオ。彼に惹かれながらも新しい愛に踏み出す勇気がないまま、ホリデイの終わりが近づいてくるが……。ビデオ発売時タイトルは「ホーム・フォー・ザ・ホリデイ　家に帰ろう」。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("突破者太陽傅", "突破者太陽傅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "広告代理店に勤務している章吾は、組長を務める父の遺志を受け継いで、組を解散し、その後の組員たちのシノギの面倒を見ることになった。そんな折り、黒金組の侵攻が始まった。あくまでカタギとして対処する章吾が、最後にとった手段とは……。ヤクザでもなければカタギでもない“突破者”の意地と心意気を描いた、任侠映画の異色作。原作は宮崎学のベストセラー小説『突破者』。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Joe Hill", "愛とさすらいの青春　ジョー・ヒル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "実在した労働運動家ジョー・ヒルの半生を、「みじかくも美しく燃え」のボー・ウィデルベルイ監督とトミー・ベルグレン主演のコンビで映画化。20世紀初頭、移民としてアメリカへやってきたスウェーデン人ジョー・ヒル。放浪の旅に出た彼は、その道中で労働者組合IWWの一団と出会う。やがてIWWに参加するようになったジョーは歌を武器に労働運動を展開していくのだが……。ジョーン・バエズによる主題歌が話題を呼んだ。", "1971-01-01", "14,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチンコ　疾走上等", "ガチンコ　疾走上等", "", "", "http://image.eiga.k-img.com/images/movie/55248/315x210.jpg?3460", 0, "ヤンキーが集う霧風高等学校では、龍一派、望月派、トシ派の3つのグループが激しい派閥抗争を繰り広げていた。しかし、たった4人しかメンバーのいないトシ派は、闘争を避けてひっそりと生き残っていたに過ぎなかった。そんな気弱なグループのリーダー・トシが、決意を新たに、2大派閥に決闘を挑むことになる。監督は「走り屋ZERO」シリーズの広瀬陽。", "2010-03-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春の門", "青春の門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "主人公・伊吹信介の波乱に富んだ幼年期～青年期を中心に描いた、五木寛之の自伝的大河小説の再映画化。深作・蔵原の共同監督により、炭坑の町に生まれた主人公が、そこに生きる男たち女たちに支えられながら生きていく姿が語られる。原作の『筑豊篇』にあたる部分を描いている。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Scaphandre et le Papillon", "潜水服は蝶の夢を見る", "", "", "http://image.eiga.k-img.com/images/movie/53278/315x210.jpg?3130", 112, "身体的自由を失った男性が瞬きだけで自伝を綴ったという実話を、「バスキア」「夜になるまえに」のジュリアン・シュナーベル監督が映画化した人間ドラマ。ELLE誌の編集長として順調な人生を送っていたジャン＝ドミニク・ボビー。ところがある日脳梗塞で倒れ、全身麻痺で左目のまぶたしか動かせない状態になってしまう。言葉すら発することができない彼は、言語療法士の力添えで瞬きを使ってコミュニケーションを取るようになり……。", "2008-02-09", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("書劍恩仇録／THE ROMANCE OF BOOK ＆ SWORD香香公主／PRINCESS FRAGRANCE", "風と興亡（清朝皇帝）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 181, "清朝が最盛期を誇った18世紀前半の中国を舞台に、兄弟でありながら互いに血を流さなければならなかった清の第四代皇帝・乾隆帝と、反清組織・紅花党の首領・陳家洛との宿命の対決を描いた歴史スペクタクル。虚実を織り交ぜた歴史ロマンだが、女性監督アン・ホイが、達者なアクション演出で見せる作品に仕上げている。全２部作。", "1987-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男組・少年刑務所", "男組・少年刑務所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "同名人気劇画の映画化第２作。五家宝連の５人を率いる流全次郎と、四天王を擁する神竜剛次との戦いを描いた青春バイオレンス。見方によっては舞台を学園に変えたヤクザ映画ともいえる。妖術や棒術が飛びかう様は、“スケバン刑事”を思わせる。コミックの実写版。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("S.F.W.", "S.F.W. メディアへの銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "クリフは親友のジョーとともに、ビールを買おうと出かけたコンビニエンス・ストアでテロリストグループに監禁されてしまう。二人はジョーの反攻をきっかけに、銃撃戦の末、テロリストたちを全滅に追い込んだ。病院のベッドで意識を取り戻したクリフは、いつの間にか自分がスーパースターになっていたことを知るが……。1990年代の『ライ麦畑でつかまえて』と称され、プレイボーイ新人賞を受賞したA・M・ウェルマンの同名小説の映画化。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ПАСТОРАЛЬ／PASTORAL", "田園詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "グルジアの首都トビリシから遠く離れた農村で合宿生活を送る音楽家たちの日々を、農家の少女の目を通して描いた叙情詩。音楽家と村人との交流が美しい田園風景の中に綴られている。物語は村人たちが彼らに興味を抱き、酒場でからんだり、子供たちは演奏の見物に集まったりといった、日常の些細な出来事の中で展開していく。すべて素人の出演者の演技と、ユーモアやペーソスを交えた叙情豊かな映像で、現代に生きる人間の本質に迫った作品。", "1976-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渋谷物語", "渋谷物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "裏社会の顔で、昭和の映画スターでもあった安藤昇の若き日を映像化。戦後の混乱の中で巨大な権力にひるむことなく立ち向かっていった男の生きざまを、「疵」などで知られる硬派、梶間俊一監督が骨太に描き出す。闇市を再現したセット、ノスタルジーをくすぐる歌謡曲の採用にも注目。昭和20年に復員した特攻隊の安藤。渋谷の街でのし上がる彼は組織を大きくしていく。だが、非道な権力者が彼の前に立ちはだかり……。", "2005-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Undisputed", "デッドロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　凶悪犯罪者を収容するスウィートウォーター刑務所。そこでは鉄格子の中で囚人同士のボクシング試合が行なわれていた。現王者は妻の不倫相手を殺害し、終身刑を宣告されたモンロー。67戦無敗の彼は誰からも恐れられていたが、そこに元ヘビー級王座のアイスマンが現れ、モンローを挑発。囚人たちや刑務所長までもがふたりの男の動向に目を光らせることに……。プライドをかけた囚人同士の激突を描く。", "2002-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLOW・僕らはここに…。", "GLOW・僕らはここに…。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "ファッション誌で人気のティーン・モデルたちを起用し、リアルな若者像を描いた青春劇。高校を中退して喫茶店で働くアユミのもとに、昔の恋人、シゲトが帰ってきたとの噂が。だが、かつて不良を仕切っていたシゲトは人が変わったように思慮深くなっていた。再会したアユミとシゲトは改めて心を通わせるが、シゲトの昔の仲間はそれを許そうとしなかった。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIFEFORCE", "スペースバンパイア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ホラー映画の第一人者、フーパー監督の意欲作。宇宙から吸血鬼ならぬ、人間の精気を吸いとる吸精鬼が、美女の姿となって地球に侵入してくる。やがてロンドン中がパニック状態に……。精気を吸いとられた人間たちが、みるみるうちにミイラ化されていくSFXシーンが見もの。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緑の大地", "緑の大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "日本で初めてのお産を終えて、中国大陸で運河建設を進める夫・洋一のもとに帰る初枝。だが初枝の乗船した船に同乗した女学校教師・園子は、かつて洋一が愛した女だった……。島津が初めて中国大陸を舞台とした作品で、青島ロケが行われている。この時代ならではの帝国主義的な内容だが、脚本はのちに日本共産党系の映画人として知られることになる山形雄策が担当している。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Spring Breakdown", "スプリング・ブレイクダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STORIE　DI　ORDINARIA　FOLIA", "ありきたりな狂気の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "人気作家C・ブコウスキーの原作をベースに、イタリアの巨匠M・フェレーリ監督が丹念に撮り上げた悲恋ものラブ・ロマンス。米国を舞台にしながらも、イタリア的風土性を感じさせる作りは同監督ならではの個性が発揮されている。アルコール中毒の老詩人と中年の娼婦が恋に落ち、破滅的な結末へと向かっていく。", "1981-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOWLINGII", "ハウリングII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "暗黒の土地、トランシルバニアを舞台に、人間の生き血を吸って永遠の生を営む人狼族と、彼らを追うハンターたちとの壮絶な闘いを描いたホラー映画。前作同様、SFXを駆使した人間から狼人間へと変身していくショッキングなシーンは圧巻。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER SCHO¨NSTE TAG MEINES LEBENS", "野ばら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ハンガリー動乱の折に、オーストリアに逃れてきた孤児トーニを主人公とする娯楽作。トーニ少年はウィーン少年合唱団に迎えられ、ある山荘で起こった盗難事件で犯人と間違えられた看護婦を救う。ミュージカル形式はとっていないが、合唱団のメンバーが歌う、ウェルナーの『野ばら』をはじめとする名曲の数々は必聴。", "1957-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三尺左吾平", "三尺左吾平", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "「河内山宗俊」「人情紙風船」の脚本家・三村伸太郎の原作・脚色により、最後期の石田民三が監督したエノケン主演のコメディ。石田監督は本作の次の作品「縁は異なもの」が遺作となった。刀も身長も三尺三寸で足が速いだけが自慢の左吾平が、殿様たちに気に入られ、用事を頼まれていくうちに、伊達騒動に巻き込まれる。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VITA DURA／THE HARD WAY", "ソルジャー・ハンティング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "アメリカ麻薬取締局の命を受けた３人のコマンダーと、アマゾンの奥地に存在する巨大シンジケートとの死闘を描くバトル・アクション映画。「類猿人ターザン」で知られるM・オキーフが、スタントなしでコマンダーの一人を演じる。悪役を演じたら天下一品のH・シルヴァが敵の鬼将校役に。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪人", "悪人", "", "", "http://image.eiga.k-img.com/images/movie/55078/315x210.jpg?1885", 139, "芥川賞作家・吉田修一の同名ベストセラーを妻夫木聡＆深津絵里主演で映画化した人間ドラマ。長崎の外れの小さな漁村に住む祐一（妻夫木）は出会い系サイトを通じて佐賀在住の光代（深津）と出会う。逢瀬を重ねる2人だったが、祐一は世間を騒がせている福岡の女性殺人事件の犯人だった……。監督は「69」「フラガール」の李相日。共演に岡田将生、満島ひかり、柄本明、樹木希林。", "2010-09-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　フレッシュプリキュア！　おもちゃの国は秘密がいっぱい!?", "映画　フレッシュプリキュア！　おもちゃの国は秘密がいっぱい!?", "", "", "http://image.eiga.k-img.com/images/movie/54728/315x210.jpg?2194", 0, "女の子に大人気のテレビアニメ「プリキュア」シリーズの第6弾「フレッシュプリキュア！」の劇場版。プリキュアたちがパジャマパーティで盛り上がっていたある晩、子供たちのおもちゃが町中から消えてしまう怪現象が起こる。突然しゃべりだしたぬいぐるみのウサピョンから、おもちゃを消した犯人は世界征服を目論むトイマジンだと知らされた彼女たちはおもちゃの国へと向かうが、そこにはトイマジンが仕掛けた数々の罠が待ち受けていた。", "2009-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRE，ICE AND DYNAMITE", "サマーシュプール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「007」シリーズで、スキー・スタント・シーンの特別カメラマンやディレクターをこなした元プロ・スキーヤーのボグナーが、縁の深いムーアを主役に起用して製作したスポーツ・ドラマ。ダイナミックに展開するスキー大会の映像の中に、親子のふれあいを描く。アクロバティックで華麗な滑走シーンも見もの。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガレキとラジオ", "ガレキとラジオ", "", "", "http://image.eiga.k-img.com/images/movie/58018/315x210.jpg?2576", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブレター", "ラブレター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "“にっかつロマン・ポルノ”の10周年記念作品。金子光晴が愛人にあてた100余通の手紙と、その聞き書きをもとに詩人の愛欲を描いたもの。東陽一の演出は「もう頬づえはつかない」と同様、けだるさの中で生きる女を映し出していく。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝ海軍", "あゝ海軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "太平洋戦争を背景に、若き兵士の青春を描く戦争ドラマ。海軍兵学校を卒業した若者、平田は航空本部に配属され、ラバウル航空隊の一員に。ある日、山本五十六長官の機の護衛にあたった彼は敵機の襲撃に遭い、傷を負う。責任を感じた平田は自決しようとするが……。昭和“ガメラ”シリーズの湯浅憲明が特撮を手掛け、迫力ある空中戦を作り出している。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Step Up 3D", "ステップ・アップ3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "大ヒット青春ドラマの第3弾。ニューヨーク大学に入学したばかりのムースは、街で出会ったストリート・ダンサー、ルークとナタリーのダンスチームに入ることになる。そこで、ムースはまったく新しいタイプのヒップホップ・ダンスを目の当たりにする。監督は「ステップ・アップ2／ザ・ストリート」のジョン・チュウ。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠くの空", "遠くの空", "", "", "http://image.eiga.k-img.com/images/movie/55498/315x210.jpg?2485", 0, "映像（Cinema）と音楽（Music）のコラボレーションを目指す「CineMusica（シネムジカ）」シリーズ第8弾。内山理名と韓国のベテラン俳優キム・ウンスが共演するラブストーリー。「音符と昆布」の井上春生監督がメガホンをとる。今は亡き日本人の父親と在日韓国人の母親に日本で育てられた美江は、職場に上司としてやってきた韓国人の柳正培(ユウ・ジョンベ)と出会う。2人は次第に愛し合うようになるが、ジョンベの過去を知った美江は、残酷な運命に翻弄（ほんろう）される。", "2010-09-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NINIFUNI", "NINIFUNI", "", "", "http://image.eiga.k-img.com/images/movie/56288/315x210.jpg?2934", 42, "国内外で活躍するクリエイターら10人が次世代のクリエイターを発掘・育成するプロジェクト「コ・フェスタPAO」で選出された、期待の若手監督3人による中編映画を一挙上映する「movie PAO」の1本。ある地方都市で起こった事件に関与した青年が、奪った車であてどなく国道をさまよい、人気のない海岸にたどり着く。数日後、そこへアイドルグループのPV撮影隊が訪れ、青年の乗っていた車が発見されるが……。監督は東京藝術大学修了作品「イエローキッド」が高い評価を受けた真利子哲也。人気アイドルグループ「ももいろクローバー」が、劇中のアイドルグループ役で登場。2011年6月「movie PAO」特集上映で公開。2012年2月、「FULL VOLUME Ver.」として単独で劇場公開。", "2011-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LITTLE KIDNAPPERS", "リトル・キッドナッパー 赤ちゃんの贈り物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "両親を失った幼い兄弟二人と、彼らを引き取った頑固じいさんの心の交流を描く。頑固じいさん役には、70ミリ・スペクタクル史劇、そしてパニック映画の名優C・ヘストンが久々に出演、熟練した重厚な演技をみせる。TVフィーチャー作品。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博徒外人部隊", "博徒外人部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "沖縄が舞台。10年の刑期を終えてシャバに戻って来た横浜の浜村組の元代貸・郡司は、様変わりした横浜に見切りをつけ、新しい縄張りを築こうと昔の子分を連れて沖縄の那覇に乗り込むが……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紫雨風暴／PURPLE　STORM", "パープルストーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "香港電影金像奨で５部門を受賞したサスペンス・アクション。香港海上で貨物船襲撃事件が発生し、化学兵器“リシンX”が強奪される。激しい銃撃戦の末、テロリストの一人、トッドが頭部に重傷を負い、香港警察に身柄を拘束される。対テロ部隊のマー・リーは、テロ組織の活動阻止に利用するため、記憶を失ったトッドに潜入捜査官としての偽の記憶をすり込む。やがて組織が“リシンX”を使って無差別殺人を企てていることが判明するが、トッドは組織に奪還されてしまった。捜査官としての自分に疑問を持ちながらも任務を遂行しようとするトッドだが……。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私の愛、私のそばに", "私の愛、私のそばに", "", "", "http://image.eiga.k-img.com/images/movie/55828/315x210.jpg?2334", 121, "「ユア・マイ・サンシャイン」のパク・チンピョ監督が脚本を兼ね、難病に冒された男性とその妻の愛を描くラブストーリー。体が徐々に麻痺していく難病を患うジョンウは、唯一の肉親である母を亡くした日、幼なじみのジスと運命の再会を果たす。2人は1年後に結婚し、病院で新婚生活をスタートさせるが、ジョンウの病状は悪化していくばかりだった。ドラマ「白い巨塔」のキム・ミョンミンが、20キロ以上の減量を行い主人公を熱演する。", "2011-02-05", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMITYVILLE DOLLHOUSE", "悪魔の棲む家・最終章　—ザ・ポルターガイスト—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "再婚して郊外の新居に引っ越してきたビルとクレアの夫婦だが、お互いの連れ子同士がうまくいかずに、けんかの絶えない日々が続いていた。ある日、ビルは家のそばに建つ古い納屋で精巧にできた“人形の家”を見つけ、それを娘の誕生日プレゼントにする。だが、そこには多くの家族を犠牲にしてきた悪魔が宿っており、一家は想像を絶する恐怖に巻き込まれるのだった。「悪魔の棲む家」シリーズすべてにスタッフとしてかかわってきたS・ホワイトが監督したホラー・ムービー。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROP BELLE POUR TOI", "美しすぎて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "美しい妻と二人の子供に恵まれた自動車ディーラーマン、ベルナールは、派遣秘書としてやって来たコレットという女に安らぎを覚える。決して美女ではない彼女に、ベルナールはだんだんと恋心を燃やし……。実験的作風で知られていたB・ブリエ監督のヒット作。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BRAVE", "ブレイブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "J・デップの初監督作品。自身のルーツであるネイティブ・アメリカンの現状を見据えて、インディアンの青年の心のさまよいを詩的な映像で綴っている。貧困にあえぐ妻子持ちの青年ラファエルは生活のために、本物の人間の死を撮影するスナッフ・ビデオへの出演を引き受けた。撮影までの猶予は７日間。ラファエルは精神的な葛藤を乗り越えて、死までの残された時間を、愛する家族のために費やそうと決意する……。数奇なドラマを通して、生死を超越した魂の存在が浮き彫りになる。ネイティブ・アメリカンの思想を捉えつつ、真の“勇気”を問いかける力作。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("事件", "事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "犯罪事件の中に、骨太の人間ドラマをカット・バックを多用して構築するという、野村芳太郎監督の手腕がさえる１本。単純な刺殺事件と思われた犯行の裏に、ひと組の姉妹の葛藤が激しく渦を巻いていた。回想を的確に取り入れて事件の真相を観客とともに解明していく。各映画賞で助演賞を総ナメした大竹しのぶの体当たりの演技も秀逸だ。19歳の工員がスナックのママを刺殺する事件が起きた。動機は？ 殺意はあったのか？ 公判が進むうちに、工員の青年をめぐって、ママとその妹が激しい争いをしていたことが明るみに出る……。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crimes of the Future", "クライム・オブ・ザ・フューチャー／未来犯罪の確立", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "　近未来、女性たちの間で奇妙なルージュ病が蔓延。ある科学者がこれを発見した科学者ルージュの学説をたどるが……。不可解な病気、肉体の変化といったテーマを描くクローネンバーグ監督の中編。", "1970-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拳精／SPIRITUAL KUNG−HU", "ジャッキー・チェン 拳精", "", "", "http://ecx.images-amazon.com/images/I/51YDS-H4ZvL._SL210_.jpg", 98, "少林寺のさえない寺男に扮したジャッキー・チェンが、５人の拳法の妖精たちに極意を学び、悪人と対決する。特撮あり、笑いあり、アクションありのバラエティーに富んだカンフー映画。チェンの新しい魅力の世界が広がり、あらためてジャッキー・カンフーのすごさが感じられる傑作。", "1978-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VOZVRASHCHENIE", "父、帰る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ロシアの新鋭アンドレイ・ズビャギンツェフ監督が、ヴェネチア映画祭グランプリを獲得したロード・ムービー。12年ぶりに家に帰って来た父親が、二人の息子を連れ出してキャンプ旅行に出発。戸惑う子供の複雑な心理を、サスペンスの要素を織り交ぜて綴る。正体不明ながらも筋の通ったスパルタぶりで、子供たちをシゴく父親の存在が圧倒的。繊細かつ重厚な映像の迫力と相まって、グイグイと引き込まれてしまう１本だ。", "2003-01-01", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リング", "リング", "", "", "http://ecx.images-amazon.com/images/I/21CQ614633L._SL210_.jpg", 95, "鈴木光司の同名ベストセラーを映画化した作品。「女優霊」の中田秀夫がメガホンを取り、グロテスクな描写やショック演出に頼らない正統派の恐怖映画に仕上げている。“そのビデオを見たら１週間後に死ぬ”。そんな噂の実態を探り始めた浅川玲子は、ついに問題のビデオを見てしまう。別れた夫、高山竜司とともに、その映像に秘められた謎を追及し、ある女超能力者の悲劇に突き当たるのだが……。刻一刻と迫り来る“１週間後”。短い中に豊穣なイメージがぎっしり詰まったビデオ映像が静かな凄みを発揮して、思いもかけぬラストまで連れていってくれる。公開時は後編に当たる「らせん」を伴映。２本続けて見ずにはいられないという上映形態も、物語の構成に見事にリンクしていた。", "1998-01-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜半歌聲", "夜半歌聲／逢いたくて、逢えなくて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「オペラ座の怪人」を下敷きにした1937年の上海映画「夜半歌聲」のリメイク。1930年代の北京。火事で荒れ果てた劇場にやって来た劇団員ウェイチンは、そこで不思議な歌声を耳にする。その歌声に隠されていたのは、激しく愛し合いながらも引き裂かれた、人気俳優と財閥令嬢の身に降りかかった10年前の悲劇だった……。90年以来芸能活動を停止していたレスリー・チャンが主演・主題歌を担当した芸能界復帰作。", "1997-02-28", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冬物語", "冬物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "原秀則原作の人気マンガを、TVの敏腕プロデューサーとして活躍する倉内均が演出、彼の第１回監督作品である。大学受験を前に勉強よりも友情や恋に悩みながら、人間として成長していく予備校生たちを描く。宮崎萬純が年下の主人公に思いを寄せる純粋で明るい女の子を好演。“ビー・バップ・ハイスクール”シリーズとは違う一面を見せている。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CONQUEROR", "征服者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "12世紀の覇王ジンギス・カン“テムジン”の活躍を中心としたスペクタクル史劇。蒙古の熱血児テムジンは、鷹狩りの途中、タタール王女ボルタイの嫁入り行列を見つけた。タタール王に父親を毒殺された恨みに燃えるテムジンは、一族を率いて行列を襲い、彼女を虜にする。やがてテムジンはボルタイを妻にすると宣言するが、彼女と結婚するはずだった首長がテムジンの部落を夜襲する。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無問題２／NO　PROBLEM　２", "無問題〈モウマンタイ〉２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "ナインティナインの岡村隆史が香港で大暴れするコメディ第２作。香港映画ファンの青年ケンが、念願の香港に旅行にやって来る。ところが、殺し屋に間違われて、巨大企業の陰謀に巻き込まれるハメに。ケンは彼らが狙う資産家令嬢を救うために騒動の渦中に飛び込んでいく。ギャグを連発しながら、二丁拳銃やワイヤーアクション、カンフーを駆使して本格的なアクションを披露する岡村隆史のハチャメチャな活躍に注目。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("この窓は君のもの", "この窓は君のもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "第８回PFFスカラシップ作品。高３の夏休み、タローのグループの一人で、北海道へ引っ越して行くはずだった陽子が、嫌がるおじいちゃんを説得するため、町に残った。寝泊まりするのは、タローの隣の家。彼女は、屋根に板を渡し、窓からタローの部屋に入ってくる。ひそかに想い合う二人、そしてタローを好きなフミエ、陽子を好きな前田……。急に与えられた短い猶予期間に、恋の矢印がいくつも浮かび上がっていく。別れを前にあえてキスもセックスもせず、しかしそれによって、高３の恋をピークのまま、永久保存する彼ら。18歳にしてすでに身につけている諦観、世紀末に生きるティーンエイジャーの成熟した恋の処し方をみずみずしく描く。「まぶだち」「ロボコン」「さよならみどりちゃん」など青春映画の佳作を生み出す古厩智之の記念すべき長編デビュー作。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Devil's Brigade", "コマンド戦略", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "第2次大戦下の1942年、連合軍は、アメリカ軍のならず者とカナダ軍のエリートを掛け合わせた第一コマンド部隊を編成し、ノルウェーに潜入させる作戦を立てる。はじめはお互いに反目し合っていた両軍の兵士だったが、訓練を重ねるうちに友情が芽生えていく。だが、いよいよ出陣というときになって第一コマンド部隊解散の命令が下る……。出演はウィリアム・ホールデン、クリフ・ロバートソン、ビンセント・エドワーズ。監督は西部劇、戦争映画の名手アンドリュー・V・マクラグレン。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHATTERED", "プラスティック・ナイトメア 仮面の情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "交通事故で記憶を失ったダン。妻ジュディスの献身的な努力によって彼は徐々に社会へ復帰するが、記憶は一向に戻らなかった。そして不安に責められる彼に追いうちをかけるように、ジュディスの過去の浮気が発覚したが……。過去を失い、妻への疑惑に苦しむ男の姿を描く心理サスペンス。意表を突く結末が見もの。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Requiem for a Dream", "レクイエム・フォー・ドリーム", "", "", "http://image.eiga.k-img.com/images/movie/1608/315x210.jpg?2147", 102, "テレビを見るのが何より楽しみな中年未亡人サラは、ある日テレビ番組への出演依頼の電話を受け、お気に入りの赤いドレスでテレビへ出るためダイエットを始める。一方、息子のハリーは恋人マリオンとのささやかな夢を叶えたいと麻薬売買に手を染める。季節が変わり、赤いドレスが着られるようになったサラはダイエット薬の中毒に、麻薬の商売がうまくいかなくなったハリーとマリオンは自らがドラッグの常用者となっていた……。", "2001-07-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Assassination of a High School President", "処刑教室", "", "", "http://image.eiga.k-img.com/images/movie/56098/315x210.jpg?4044", 93, "高校2年生で新聞部のファンクは、学校の腐敗をかぎとり独自に調査を開始。学校一のアイドル、フランチェスカの全国共通テストの答案が盗まれた事件を解決し、たちまち校内の人気者になる。元軍人の校長にも一目置かれるようになったファンクだったが、調査を進めるうちに次々と校内の複雑な実態が明らかになっていく……。CM界出身のブレット・サイモン監督がメガホンをとった学園コメディ。ブルース・ウィリスが軍人あがりの校長に扮する。", "2011-04-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あねといもうと", "あねといもうと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "岩下志麻、倍賞千恵子、中村晃子が若き三姉妹を演じる女性文芸ドラマ。明るい個性が評判の高岡家の娘たちは、やがてそれぞれの恋の問題に直面していく。結婚を間近に控えながらほかの男から求婚される長女、恋人の死を看とる次女、亡き兄の妻を気づかい独立を勧める三女。すべては変わろうとしていた。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Postia Pappi Jaakobille", "ヤコブへの手紙", "", "", "http://image.eiga.k-img.com/images/movie/55748/315x210.jpg?4410", 76, "1970年代のフィンランド。恩赦を受けて12年ぶりに刑務所を出所したレイラは、片田舎で暮らす盲目のヤコブ牧師のもとに身を寄せることになる。彼女に与えられた仕事は、ヤコブのもとに毎日届くさまざまな人々からの悩みの手紙を朗読し、返事を代筆すること。心を固く閉ざしているレイラは、嫌々ながらもその仕事をこなしていたが、ある日ヤコブへの手紙がぴたりと届かなくなってしまう。第82回アカデミー賞外国語映画賞フィンランド代表で、「フィンランド映画祭2010」で上映された。", "2011-01-15", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG SLEEP", "大いなる眠り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "R・チャンドラーの同名小説の映画化。おなじみの私立探偵、フィリップ・マーロウに、「さらば愛しき女よ」に続いてR・ミッチャムが挑戦。もの憂げな表情、少々疲れた身のこなしで独特のマーロウ像を作り上げている。依頼人スターンウッド将軍の娘がかかわっている脅迫事件の調査に乗り出すマーロウ。娘の夫の失踪を追ううち、マーロウはロンドンの犯罪世界に踏み込んでいく。", "1978-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワカラナイ", "ワカラナイ", "", "", "http://image.eiga.k-img.com/images/movie/54758/315x210.jpg?4288", 104, "母子家庭で育った16歳の少年・亮は、入院中の母親を支えるためコンビニのアルバイトで生計を立てていたが、レジをごまかして小銭を稼いでいたことがばれてクビになってしまう。やがて母親を病気で亡くすが、亮は医療費も葬儀代も払えない。行きづまった亮は、自らの手で母親の亡骸を葬ることにする。監督は「バッシング」「愛の予感」など海外で高い評価を得る小林政広。", "2009-11-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POWER PLAY", "パワープレイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "軍事政権下のヨーロッパ某国でひそかにクーデターをたくらむナリマン大佐とゼラー大佐。二人はブレアー秘密警察部長の妨害にもめげず、壮絶な市街戦の結果、見事に権力の座を手中に収めるが、そこには陰謀、策略など数々の試練が待ち構えていた。P・オトゥール主演のシブい抗争劇。", "1978-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本以外全部沈没", "日本以外全部沈没", "", "", "http://image.eiga.k-img.com/images/movie/33628/315x210.jpg?2398", 98, "「日本沈没」を筒井康隆がパロディ化した同名小説を、「いかレスラー」「コアラ課長」の鬼才・河崎実監督が映画化した異色作。2011年、原因不明の天変地異により日本以外のすべての陸地が沈没。日本には世界中から難民たちが押し寄せ、大パニックとなる。「あずみ」の小橋賢児や「カミュなんて知らない」の柏原収史ら人気若手俳優の出演に加え、元祖映画版とテレビ版で主演を務めた藤岡弘、と村野武範も豪華共演を果たしている。", "2006-09-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("祭りの準備", "祭りの準備", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "原作は、脚本も担当している中島丈博のきわめて自伝的色彩の濃い同名の小説。監督は前年「竜馬暗殺」で切れ味鋭い演出を見せた黒木和雄、撮影は黒木作品を数多く手掛けている鈴木達夫が担当している。高知県・中村市の信用金庫に勤める楯男はまだ20歳で、いつの日にか東京へ出てシナリオ・ライターになるのが夢である。楯男には片想いの幼なじみ・涼子がいるが、涼子にサークル仲間の恋人がいると知り、隣家の精神異常の娘と寝てしまう。時がたち恋人に捨てられた涼子は、楯男を安宿へ誘惑。涼子への夢に破れた楯男は東京へ行くことを決心する。俳優としてはデビュー作の江藤潤と、映画デビュー２作目の竹下景子の初々しさ、原田芳雄の人のいいチンピラぶりなどが素晴らしい黒木和雄の秀作。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新二等兵物語・吹けよ神風の巻", "新二等兵物語・吹けよ神風の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "シリーズ第８作以降は梁取三義の原作を離れたオリジナル・ストーリーで、出演者に三木のり平が加わる。昭和20年７月の広島。車輌部隊・菊村隊に転属した古巻源三一等兵は、そこでろうあの竹田二等兵と親しくなり、また親友・柳川一等兵とも再会する。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GLASS MENAGERIE", "ガラスの動物園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "T・ウィリアムズの出世作を、P・ニューマンが映画化した作品。人間の様々な屈折した思いや焦燥感、そして愛憎を、静謐で透明感あふれる映像美で、繊細かつ鮮烈に映し出した珠玉の人間ドラマ。1930年頃のセントルイスの小さなアパート。古き良き思い出のなかで生きる母、ガラス細工の動物に囲まれ孤独な世界に閉じこもる娘ローラ、二人の間で行き詰まり絶望する弟トム。ある夜、トムの同僚の青年が、夕食に招かれやって来る。彼はローラが高校時代にあこがれていた青年で、彼女は思いがけない再会に胸をときめかせるのだが……。完璧な室内劇構成のなかで、４人の登場人物を演じる役者陣の、それぞれに個性的で深みのある演技が素晴らしく、感銘を呼ぶ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE AFFAIR", "邂逅〈めぐりあい〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ヨーロッパからアメリカへ向かう豪華客船の中で出会ったミシェルとテリー。彼らは６ヵ月後、ニューヨークのエンパイアステートビルで再会を約束する。だがテリーが事故に遭い、二人は逢うことができなかった。モノクロ映像でしっとりと綴られる、大人のロマンス。“すれ違いラブ・ストーリー”の原点ともいえる作品で、後年何度もリメイクされた。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Peacock", "孔雀　我が家の風景", "", "", "http://image.eiga.k-img.com/images/movie/33858/315x210.jpg?4826", 136, "文化大革命後の中国の田舎町を舞台に、ある家族が繰り広げる人生模様を描いた人間ドラマ。落下傘部隊の将校に恋をした奔放な姉、知的障害を持つ兄、家族という鎖に嫌気がさして町を飛び出した弟。彼ら3人の青春の日々と、その後の皮肉な運命を綴る。監督は、これまで撮影監督としてチャン・イーモウやロバート・アルトマンらの作品に参加してきたクー・チャンウェイ。長編監督デビュー作となる本作で、見事ベルリン国際映画祭銀熊賞に輝いた。", "2007-02-17", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESMERALDERO", "エメラルド・カウボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "南米・コロンビアでエメラルド・ビジネスを成功させた実在の人物、早田英志の実話を基にした人生劇。エメラルド業界で生きる彼の壮絶な半生を描く。早田自身が監督と主演を務め、敢えてゲリラが潜伏するエメラルド採掘地域でロケを強行。同国の人々もそのリアルな描写に舌を巻いた1本だ。一攫千金を夢みる早田は、エメラルドの眠るコロンビアへ。無法地帯のその地で生き抜いた彼は、エメラルド・ビジネスで大成功を収める。", "2005-02-05", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新人（ルーキー）刑事（でか）まつり", "新人（ルーキー）刑事（でか）まつり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "オムニバス映画「刑事（でか）まつり」シリーズの第7弾。「主人公は刑事であること」「完成尺は10分を超えてはならない」「本編中に最低でも5回はギャグを入れること」という3つのルールのもと、後に「イエローキッド」で注目を集める真利子哲也監督をはじめ、これまで長編劇場公開作品を手がけたことのない7人の新人監督たちがメガホンを取った。出演は、「集団殺人クラブ　最期の殺戮」の中村愛美ほか。", "2005-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAIZO", "TAIZO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「地雷を踏んだらサヨウナラ」で、26歳の若さでこの世を去った戦場カメラマン、一ノ瀬泰造の生涯に迫った奥山和由率いるチーム・オクヤマが、没後30周年を迎えてあらためて泰造の真の姿に迫ったドキュメンタリー。カンボジアのアンコールワットに魅せられた泰造の情熱を、新人女性監督、中島多圭子が数々の写真やインタビューで浮き彫りにしていく。1970年代、カンボジアのアンコールワットに魅せられた戦場カメラマンの一ノ瀬泰造。彼は、戦火のカンボジアへ単独潜入し、その姿を撮影することに情熱を燃やした。しかし、友人へ宛てた手紙を最後に消息を絶ってしまう。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不倫純愛", "不倫純愛", "", "", "http://image.eiga.k-img.com/images/movie/55938/315x210.jpg?4571", 95, "新堂冬樹の小説を、「スイートリトルライズ」の矢崎仁司監督が映画化した官能ラブストーリー。出版社で編集長を務める辰波京介は、結婚15年を迎えた妻とのセックスを重荷に感じるようになっていた。そんなある日、新進作家・岡セイジの家を訪れた彼は、セイジの恋人・川島澪香の妖えんな美しさに心を奪われる。やがて突然の失踪を遂げたセイジを捜索することになった澪香と京介は、ある出来事をきっかけに肉体関係を持つが……。", "2011-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("社長忍法帖", "社長忍法帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "岩戸久太郎社長を中心に、岩戸建設は躍進を続けていた。そんな時、北海道から気になる情報が入って来た。ライバルの東西組が札幌に進出するという。こうなっては岩戸建設もあとへは退けぬ。お色気芸者衆も交えての作戦が始まった。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("萬歳樂", "萬歳樂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "狂言師、野村万之丞が、自身の活動と思想を解き明かしていくドキュメンタリー。なぜ袴を左足からはき、舞台では左足から踏み出すのかなど、狂言に関する素朴な疑問から、なぜ人はパレードするのかといった、芸能や文化全般に及ぶ命題まで縦横無尽に発展していく映像エッセイともいうべき親しみやすさで、狂言に興味のない観客にとっても、知的想像力を喚起する刺激的な作品に仕上がっている。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新仙鶴神針／THE MAGIC CRANE", "ツイ・ハークTHE マジック・クレーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "太古戦乱の世の中国。多くの武術人たちが、全国制覇をたくらんでしのぎを削る中、超人的パワーを持った二人の女、白雲飛と藍小蝶がいた。二人は血を分けた姉妹であったが、同時に宿命のライバルでもあった。そして、彼女たちのパワーを利用しようとする者、邪悪な力を操る者が二人の運命を変えていく……。香港のスピルバーク、T・ハークが手掛けた迫力あるSFXアクション大作。香港を代表する大女優の共演も見もの。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　遙かなる時空（とき）の中で　舞一夜（まいひとよ）", "劇場版　遙かなる時空（とき）の中で　舞一夜（まいひとよ）", "", "", "http://image.eiga.k-img.com/images/movie/33608/315x210.jpg?3163", 104, "人気ゲームを原作に、異世界に召還された少女の戦いと恋を描いたテレビアニメ「遥かなる時空の中で」の劇場版。異世界・京にやって来た女子高生の元宮あかねは、神の力を宿した“龍神の神子”として、神子を助ける8人の男性“八葉”とともに京を守りたいと願いながらも、何をすれば良いかわからずにいた。そんなある日、雨の中を歩いていたあかねは、1人の青年と衝撃的な出会いを果たす。監督は、「犬夜叉」の劇場版シリーズを手がけた篠原俊哉。", "2006-08-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西幹道", "1978年、冬。", "", "", "http://image.eiga.k-img.com/images/movie/53518/315x210.jpg?2508", 101, "文化大革命が終わりを告げ、自由の光が見え始めた1978年冬の中国。地方都市・西幹道で暮らす11歳の内気な弟ファントウと18歳の兄スーピンの前に、叔父を頼って北京からやって来た孤独な少女シュエンが現われる。彼女に憧れを抱いた兄弟は、初めての感情に戸惑いながらも彼女と交流を深めていく。「思い出の夏」のリー・チーシアン監督がメガホンを取り、変わりゆく時代の中で生きる少年たちの青春を繊細なタッチで綴る。", "2008-06-14", "8,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トラ・コネ Triangle Connection", "トラ・コネ Triangle Connection", "", "", "http://image.eiga.k-img.com/images/movie/53898/315x210.jpg?1783", 0, "大阪・天王寺でいつも遊んでいる女子高生3人組のマリア、ユイカ、エルナは、スキンヘッドのゾンビや忍者のヒーロー、大阪を狙うテロリストがいるという都市伝説に夢中。そんなある日、ブログアイドルであるマリアのコンサート会場の楽屋に、何者かに追われる天才数学者の町田タケヒコが逃げ込んできて……。主演は杉浦太陽、共演に「芸者VS忍者」の佃井皆美、「クライマーズ・ハイ」の遠藤憲一ほか。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIGHTY JOE YOUNG", "猿人ジョー・ヤング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アフリカのコンゴで少女に育てられたゴリラは巨大な成長を遂げ、アメリカのナイトクラブに出演するハメに。だがある日、酔客のイタズラで大混乱が起きる。「キング・コング」のスタッフが手掛けた“巨猿シリーズ”の第３作。さわやかな後味の残るラストが良い。レイ・ハリーハウゼン、ウィリス・H・オブライエンらが特撮を担当している。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KAMIKAZE", "神風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "L・ベッソンが製作、彼の助監督を務めていたD・グルッセが初監督したファンタスティック・スリラー。TVの画面内の人間をモニター伝いに殺してしまうメカを開発した、マッド・サイエンティストの“神風”的な行動をサスペンス色豊かに描いていく。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　こわれかけのオルゴール", "劇場版　こわれかけのオルゴール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「ハヤテのごとく！」「もえたん」の川口敬一郎監督が、2009年に発売された自身の同人アニメーションにオリジナルショートストーリーを追加した劇場版。アンドロイドの“ペアレンツ”が家事全般をこなす近未来の日本。家族を亡くした青年・ケイイチロウは、ゴミ捨て場に捨てられた旧型アンドロイドを拾い、“ふらわー”と名づけて一緒に暮らし始める。", "2010-09-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("元祖 大四畳半大物語", "元祖 大四畳半大物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "青雲の志を抱いて東京のボロアパートにやって来た九州男児・足立太を中心に、アパートの連中が巻き起こすドタバタ騒動と、若者の夢と性の形をホロ苦い笑いで描いた青春人情劇。原作は松本零士の半自伝的マンガで、出演者たちの熱演ぶりにも注目。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　PERE　NOEL　A　LES　YEUX　BLEUX", "サンタクロースの眼は青い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 47, "J・ユスターシュのデビュー作「わるい仲間」でその才能を認めたジャン・リュック・ゴダールがプロデュースし、「男性・女性」のフィルムを提供したといわれる短編。フランスの地方都市ヌルボンヌに住むダニエルは、悪友とカフェで時間をつぶし、本屋で万引きをしたりしている青年。ダッフルコートを着て、女の子とデートをしたいと思っている彼は、クリスマスの格好で通行人と写真を撮るバイトを始めるが……。ビデオ、DVDは「ジャン・ユスターシュ　コレクション２」に収録。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Attack on The Pin-up Boys", "花美男（イケメン）連続ボム事件", "", "", "http://image.eiga.k-img.com/images/movie/53828/315x210.jpg?2422", 82, "韓国の男性アイドルグループ「SUPER JUNIOR（スーパージュニア）」が主演する青春コメディ。イケメン男子高校生ばかりが、“とある爆弾”で襲われる珍事件が発生。事件は瞬く間に高校生たちに広がり、いつしか「襲われるのはイケメンの証」という妙な都市伝説まで生まれ……。", "2008-07-12", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("らしゃめん", "らしゃめん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "主演の鰐淵晴子の同名レコードから企画された作品。明治維新、激動期を舞台に、外国人の現地妻となった洋妾（らしゃめん）の悲劇を描くメロドラマ。没落士族の娘・雪は借金のために恋人と別れたあと、アメリカ公使の洋妾になり、数奇な運命にもてあそばれるのだった……。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVILSPEAK", "デビルスピーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "カリフォルニアの陸軍士官学校を舞台にしたオカルト映画。日ごろ、行動が愚鈍だという理由で、クラス全員から馬鹿にされている少年が、コンピュータを駆使して悪魔パワーを身につけ、世間に報復する。男性版「キャリー」といった作品。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Little Brother", "奇跡の夏", "", "", "http://image.eiga.k-img.com/images/movie/33548/315x210.jpg?3496", 97, "実話を基に、兄の病気に直面した少年が家族とともに成長していく姿を描き、本国韓国で大ヒットを記録した感動作。9歳のわんぱく坊主ハニにとって、3歳上の優しい兄ハンビョルは最高の遊び相手。ところがある日、ハンビョルが脳腫瘍と診断されて入院することに……。主人公ハニを演じたパク・チビンは、ニュー・モントリオール国際映画祭主演男優賞を最年少の10歳で受賞。音楽を「冬のソナタ」のイ・ジスが手掛ける。", "2006-07-15", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冬の旅人", "冬の旅人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "脚本のチェ・イノと主演のアン・ソンギは韓国映画の逸材。これに新人監督とスター女優が加わって作られた“純情”大メロドラマだ。二組の美男美女の友情と愛、別れと出会い、そして一人の男の死。波乱に富んだストーリーに、ストレートな感傷を貫いたところが大衆映画の真骨頂。", "1985-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SURF　ADVENTURES−THE　MOVIE−", "サーフ・アドベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ブラジルのトップ・サーファーたちが最高の波を探し、ハワイ、南アフリカ、インドネシアなどを巡ったドキュメンタリー。サーフボードが高級品であるブラジル。若者たちが夢みるサーフィン・ライフから現代のブラジルが垣間見られる。多角的にサーフィンの魅力に迫っていて、見応えあり。ブラジルの片田舎のサーファーたちが完璧な波を探す旅へ。しかし、インドネシアのスマトラ島近くのメンタワイ島で大地震に遭い……。", "2005-05-28", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長富士", "次郎長富士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "長谷川一夫以下大映オールスター・キャストによる“次郎長富士”ものの一編。秋葉の火祭りで売り出した次郎長一家が、富士川川原で黒駒一家と大喧嘩するまでを講談調で描いた一大娯楽作品。勝新太郎の森の石松がはまり役。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファンシイダンス", "ファンシイダンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1984年のピンク映画「変態家族・兄貴の嫁さん」で注目された周防正行の第２作。岡野玲子の同名マンガをもとに、修行僧たちの青春を“お坊さんライフ”のHOW TOを絡めて描いた異色作だ。東京の楽しい生活と恋人に別れを告げ、実家の寺を継ぐため、頭を丸めて修行寺へと向かった塩野陽平。彼と同日入門の仲間たちには、お寺の作法と規則に縛られた生活が待っていた。初めは苦しいだけの生活も、馴れとともにそれぞれが楽しさを見出していく……。お寺の生活をわかりやすく解析しながら、傾倒する小津安二郎タッチをすり込ませた周防監督の手腕が異彩を放つ。陽平に扮した本木雅弘の好演と大槻ケンヂ、竹中直人など異色共演陣も見どころ。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本殉情伝おかしなふたり　ものくるおしきひとびとの群", "日本殉情伝おかしなふたり　ものくるおしきひとびとの群", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "尾道を舞台にして私小説映画を撮り続けてきた大林宣彦の、これも尾道を描いた作品の一つ。1986年に作られながらオクラ入りになっていたところを1988年に公開され、救われた。永島敏行や水島道太郎ほか大林作品としては異色のキャスティング。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CUT", "CUT", "", "", "http://image.eiga.k-img.com/images/movie/55718/315x210.jpg?2314", 132, "イランの名匠アミール・ナデリ監督が西島秀俊を主演に迎え、殴られ屋をして金を稼ぐ売れない映画監督の映画への愛情を描き出す。いつも兄からお金を借りて映画を撮っていた秀二だったが、どの作品も映画館にかけることができない。そんなある日、秀二は兄が借金のトラブルで死んだという報せを受け、兄が自分のために借金をしていたことを知る。罪悪感にさいなまれる秀二は、兄の痛みを分かち合い、借金を返済するため、兄が死んだヤクザの事務所で殴られ屋を始めるが……。青山真治が共同脚本で参加。共演は常盤貴子、笹野高史ら。", "2011-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("壮烈新撰組・幕末の動乱", "壮烈新撰組・幕末の動乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "勤皇派を名乗る強盗が相次ぐ中、新選組は密告を受け勤皇派の但馬を襲った。しかし真犯人が新選組隊員だと分かり……。近藤勇を中心に幕末の動乱と新選組をめぐる多彩な人間模様を描く。片岡千恵蔵や若山富三郎などオールスターキャストが揃った群像時代劇。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恐竜・怪鳥の伝説", "恐竜・怪鳥の伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "とうの昔に死滅したはずの恐竜“プレシオザウルス”と怪鳥“ランホリンクス”が、富士五湖の一つ西湖に甦って死闘を展開。富士山の大噴火によって割れた大地の中に落ち、再び死滅するまでを描いた特撮ファンタジー。海外配給を大々的に宣伝した。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幻魔大戦", "幻魔大戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "すでに実写作品によって邦画界に新風を送り込んだ角川映画が、劇場用アニメに進出した第１作。原作の『幻魔大戦』はコミックと小説の両ジャンルで様々な展開がなされているが、この映画版は石ノ森章太郎と平井和正により最初に発表されたマンガ版をベースにしている。トランシルバニア王国の王女ルナは、宇宙エネルギー生命体で善の象徴であるフロイから、破滅と悪のエネルギー生命体“幻魔”の地球来襲を知らされた。ルナはこれを迎え討つため、フロイが地球に派遣したサイボーグ戦士ベガとともに、日本の高校生・東丈をはじめとする世界各地の超能力者たちに参加を呼びかける。そして幻魔一族と超能力戦士たちとの、ハルマゲドンが開始された……。キャラクターデザインに、当時気鋭のマンガ家として注目を浴びていた大友克洋を起用し、それまでのアニメキャラとは異なる、新鮮なデザインが話題を呼んだ。正義の超能力戦士たちと幻魔一族とを光と影の見事なコントラストで描き分ける。特に富士山を舞台にした、ラストの決戦シーンは圧巻。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALLA", "ホワイト クリスマス　恋しくて、逢いたくて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "TVドラマ『夏の香り』などで人気上昇中のソン・スンホンの映画デビュー作は、時空を超えてタイム・トラベルするファンタジック・モードの純愛ストーリー。ヒロインにアタックしデートにこぎつけた主人公だが、その場に凶悪犯が押し入り彼女は殺されてしまう。だが、３年後、奇跡が訪れる。", "1999-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESCAPE FROM PARADISE", "ファイナルゾーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "核戦争後の近未来。一部の人類は、核汚染から逃れ、地下で厳しい法律のもと細々と生き残っていた。ある日、恋人同士のテオとベアトリーチェは、その法律を破り、地下から荒廃した地上に脱出する。二人は地上でミュータント人間と知り合うが、地下社会は、追っ手としてパニッシュ戦士を送り込んでくる。そして地上は、二人と組んだミュータント人間対パニッシュ戦士の生き残りをかけた戦いの場に変わろうとしていた……。核戦争後の変わり果てた人類と地球環境が衝撃的な、SFバイオレンス・アクション。", "1989-01-01", "4,2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子猫の涙", "子猫の涙", "", "", "http://image.eiga.k-img.com/images/movie/53248/315x210.jpg?2109", 97, "1968年、メキシコオリンピックで銅メダルを獲得したボクサー、森岡栄治は、華々しい戦績でプロボクシングの世界に入るも、右目網膜剥離のため25歳で早々に引退を余儀なくされる。ボクシング界から去った栄治は……。日本ボクシング史上3人目の五輪メダリスト・森岡栄治の父、夫としての姿を娘の視点から描いた家族ドラマ。監督は栄治の甥で脚本家・舞台演出家の森岡利行。第20回東京国際映画祭「日本映画・ある視点」部門で特別賞を受賞。", "2008-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　WILDCATS", "ワイルドキャッツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "アメリカン・フットボールの女性コーチ、ホーンが、落ちこぼれハイスクール・チームを鍛え直そうと一大奮起。軍隊から政治まで、「プライベート・ベンジャミン」を始め、男のものとされてきた世界に挑戦し続けるホーンが、今度はアメフトにチャレンジし、たくましきコメディエンヌぶりを発揮する。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRIVING MISS DAISY", "ドライビング・ミス・デイジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "アカデミー賞の最優秀作品・主演女優・脚色・メイキャップの計４部門を受賞したアットホームなヒューマン・ドラマ。頑固な老未亡人デイジーと、彼女のお抱え運転手となる老黒人ホーク、この二人のギクシャクしながらも次第に心を通わせていく25年間の友情の軌跡を淡々と描いていく。その間に事件らしきものは特に起きないのだが、さりげなく黒人差別の実態を要所にはさんでいくあたり、並のホームドラマには終わっていない。主演のJ・タンディは、80歳にして生涯で最高の名誉に輝くことになった。文盲の老運転手を演じるM・フリーマンも「グローリー」に続く好演である。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Billy Madison", "アダム・サンドラーはビリー・マジソン　一日一善", "", "", "http://ecx.images-amazon.com/images/I/516CSs8E12L._SL210_.jpg", 108, "父親が経営するホテルを継ぐため、小学校から勉強をやり直すことになった青年の奮闘を描いたコメディドラマ。ホテル王マジソンの御曹司ビリーは、27歳になっても定職につかず自堕落な毎日を送っていた。そんな息子に愛想を尽かした父は、副社長のエリックに社長の座を譲ろうとする。そこでビリーは会社を継ぐための条件として、小学校から高校までの全学年をそれぞれ2週間ずつやり直すことになるが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("終の信託", "終の信託", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SINGLE　ACTION", "シングル・アクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ロバート・ロドリゲスの出世作「エル・マリアッチ」で主演を務めたC・ガラルドが、監督・製作・脚本・主演の４役をこなした意欲作。1994年にメキシコで起きた大統領暗殺事件に材を得た異色西部劇だ。メキシコの片田舎で次期大統領が暗殺された。現地に派遣されたメキシコ諜報部捜査官は、町がカマロとマッコの二人のボスに支配されていることを知る。捜査官はマッコと手を組み、容疑者であるカマロ打倒を試みるのだが……。", "1996-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIEBE　IST　KALTER　ALS　DER　TOD", "愛は死より冷酷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ドイツ映画界の歴史的ジャンルである“ニュー・ジャーマン・シネマ”を牽引していたファスビンダー監督が、自ら出演まで務めて撮り上げたギャング映画三部作の第一弾。作家性を発揮した冷徹でクールな視線が光る。一匹狼でならしていたギャングの一員が、組織から協力を求められた。やがて、組織から派遣された男と友情を深め二人で銀行強盗へと突っ走る。", "1969-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージーの花嫁と七人の仲間", "クレージーの花嫁と七人の仲間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "クレージー・キャッツの面々が初めてグループで主演した事実上の第１作。赤坂にあるすし屋“にしき”を舞台に、植木等をはじめとするクレージー・キャッツの面々が活躍する。松竹という会社のカラーのためか、ハチャメチャなエネルギーは控えめに。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jour De Fete", "のんき大将脱線の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "フランス中部ののどかな田園地帯。年に1度の夏祭りの手伝いで大忙しの郵便配達員フランソワは、祭り当日にテント映画館で観た「アメリカ式郵便配達」に触発されて一念発起。アメリカ式のスピード郵便配達で村中をかけまわるのだが……。ジャック・タチの長編監督デビュー作。1949年度、ヴェネチア映画祭最優秀脚本賞。後に本作のカラー版が発見され、復元して「新のんき大将」として公開されている。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECRET LIFE", "ジェフリー・ダーマー　ミルウォーキー連続虐殺食人鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1991年７月。ウィスコンシン州ミルウォーキーを巡回していたパトカーに、手錠をされた裸の男が助けを求めてくる。アパートの一室で暴行され、殺されそうになったというのだ。彼の言葉を信じて、部屋に踏み込んだ警官はおびただしい数の人肉、内臓、生首、頭蓋骨を発見する……。13年間に17人を殺した実在の猟奇殺人事件犯人ジェフリー・ダーマーの衝撃の姿に迫るサイコ・サスペンス。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀座三四郎", "銀座三四郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "「姿三四郎」の富田常雄の原案による柔道映画。主演は黒澤明の「姿三四郎」でも主役を演じた藤田進。銀座で診療所を開く熊介は柔道六段であったが、銀平との約束で腕力を封じている。学生時代、熊介が銀平の片目に傷を負わせたのが原因だった。そんなある日、マリエが暴力団に捕まっているのを知り、ついに大暴れ……。のちに「銀座の猛者」と改題された。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たみおのしあわせ", "たみおのしあわせ", "", "", "http://image.eiga.k-img.com/images/movie/53398/315x210.jpg?2339", 118, "地味でオクテな青年・民男は、息子をどうしても結婚させたい父・伸男の強引な勧めでお見合いを繰り返していた。そんな彼の前に、ついに理想の女性・瞳が現われ……。人気ドラマ「時効警察」など俳優としても活躍する劇作家・岩松了がメガホンを取り、ダメ父子が織りなす結婚協奏曲をユーモラスに紡ぎだす。人生の一大イベントに向けて右往左往する民男と瞳をオダギリジョー＆麻生久美子の「時効警察」コンビ、民男の父を原田芳雄が演じる。", "2008-07-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦国自衛隊1549", "戦国自衛隊1549", "", "", "http://image.eiga.k-img.com/images/movie/1638/315x210.jpg?2097", 119, "79年の大ヒット作「戦国自衛隊」を原案に、「亡国のイージス」「ローレライ」の福井晴敏の原作で描くSF戦国アクション。自衛隊の中隊が最新軍事設備と共に戦国時代にタイムスリップ。彼らを救出するためにその時代に向かった元自衛隊員・鹿島は、部隊を率いる的場1佐が自ら織田信長となって天下を支配しようとしていることを知る。監督は「ゴジラ×モスラ×メカゴジラ　東京SOS」の手塚昌明。綾瀬はるか、北村一輝らが共演。", "2005-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr. & Mrs. Smith", "Mr.＆Mrs.スミス", "", "", "http://image.eiga.k-img.com/images/movie/1658/315x210.jpg?1358", 118, "2大人気スター、ブラッド・ピットとアンジェリーナ・ジョリーが初共演する娯楽アクション。スゴ腕の暗殺者2人が相手の正体を知らずに結婚するが、数年たった今は倦怠期に突入。そんなある日、2人は同じ標的を狙うはめになったことから初めてお互いの正体に気づき、相手を抹殺しなければならなくなる。2人の戦闘は、巨大組織を巻き込んでエスカレートしていき……。監督は「ボーン・アイデンティティー」のダグ・リーマン。", "2005-12-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("When the Wind Blows", "風が吹くとき　デジタルリマスター版", "", "", "http://image.eiga.k-img.com/images/movie/53768/315x210.jpg?2873", 81, "「スノーマン」で知られるレイモンド・ブリッグズの絵本を原作に、“核戦争”の恐怖を描いた1987年の名作アニメのデジタルリマスター版。イギリスの片田舎で平穏に暮らすジムとヒルダの老夫婦派、近く始まるという核戦争に備えて家を核シェルターへと改造するが、ラジオが敵国の攻撃を伝えた数分後、凄まじい爆風に襲われ……。ピンク・フロイドやデビッド・ボウイが音楽を担当したことでも話題に。日本語吹替え版を大島渚が監修。", "2008-07-26", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花のこえ", "花のこえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 41, "静岡県伊豆の山間部にある旧韮山町立南小学校高原分校。全学年あわせて6人しかいないこの小さな分校が廃校になるまでの1年間を、同校の卒業生である22歳の新人監督・太田綾花がとらえたドキュメンタリー。豊かな自然の中で少しずつたくましくなっていく子どもたちの姿、そして太田監督の担任でもあった古屋先生の最後の授業など、何気なくもかけがえのない日々を、監督自身の少女時代と重ねあわせながら描いていく。", "2005-11-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生死決／DUEL TO THE DEATH", "ザ・SFX時代劇 妖刀・斬首剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "古来、10年に一度、中国と日本の両国から最高の武術の達人を出して戦わせる習わしがあった。これに勝つために日本は忍者を使って、少林寺から秘伝法を奪っていった。中国の代表は“剣聖”と呼ばれる青雲、日本の代表は宮本武蔵。武蔵は日本側の裏工作を阻むつもりで……。何とあの宮本武蔵が中国に現れ、剣豪たちと対決するという奇想天外な剣劇アクション。", "1982-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATAL TEMPTATION", "情事の報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "イタリアの閑静なリゾート地にあるホテルのオーナー、パウロ夫妻の間は夫の浮気が原因ですっかり冷え切っていた。ある日パウロは愛人とドライブ中に事故に遭い、目と片足が不自由になってしまう。一方妻のシルヴィアは、この時同乗していた美青年に一目惚れ。愛欲の日々に溺れていく。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOKYO G.P.", "TOKYO G.P.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "当初、ハード・コアバンド“RISING SUN”の『TOKYO G.P.』のプロモーション・ビデオを製作する予定が、数分では収まりきらず、急遽、劇場公開作として製作されたバイオレンス・ムービー。クラブで踊るHIROとZEEBRAらは、倒産した病院に隠されたティディ・ベアを、明け方までに東京タワーの展望台で待つ女に届けるというゲームに巻き込まれる。もしも失敗したら仲間の女が殺されるか売られてしまうと知った彼らは、ティディ・ベアを探し出し東京タワーへと向かうが……。ミュージック・ビデオの枠を飛び越えバイオレンス映画としての厚みを持たせたのは、「死んでもいい」「GONIN」の石井隆。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロマンス祭", "ロマンス祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "宝塚映画お得意のミュージカル・コメディ。出演もおなじみのチエミ、いづみと宝田明、そして豪華喜劇陣で、監督もこれまたいつも通りの杉江敏男である。ジャズ・シンガー志望のチエミがリサイタルを開くまでの紆余曲折を、いづみのロマンスを織り込んで描くにぎやかな明朗喜劇。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARMEN", "carmen.〈カルメン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「女王フアナ」のV・アランダが実在の事件を題材にした不朽の名作小説を映画化。自由奔放な美しいジプシー女と、彼女に運命を狂わされた男の悲しくも情熱的なラブ・ストーリーを、美しい映像の中に描き出す。ミステリアスな魅力で男を虜にするヒロインを「トーク・トゥ・ハー」のP・ヴェガが好演。彼女のしなやかで妖艶な肉体美に魅了される。スペインを旅するフランス人作家が出会った精悍な男ホセ。彼はジプシーの美しい女性カルメンに心奪われていた。ホセはカルメンへの愛のために山賊に身をやつすが、束縛を嫌う自由奔放な彼女にその純粋な愛は受け入れられず……。", "2002-01-01", "9,3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅の贈りもの☆0:00発", "旅の贈りもの☆0:00発", "", "", "http://image.eiga.k-img.com/images/movie/33678/315x210.jpg?4670", 109, "大阪駅から深夜0時に出発する行き先不明のミステリー列車。それぞれの悩みを抱えながらこの列車に乗り込んだ男女5人の旅を描くハートフル・ドラマ。見知らぬ田舎町に降り立った彼らは、そこで暮らす人々との出会いを通して心の傷を癒していく。出演はテレビドラマ「ショムニ」の櫻井淳子や映画初出演となる歌手の徳永英明ら。西日本各地で撮影された美しくも懐かしい風景や、「いい日旅立ち」「時代」などの名曲が物語を彩る。", "2006-10-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天狗党", "天狗党", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "武士階級の圧政から村を追われた百姓・仙太は、江戸でヤクザになり故郷に帰った。剣の腕前を水戸天狗党に買われた仙太は、世直しを信じ天狗党と行動をともにすることにした。巨匠・山本薩夫が、階級闘争の厳しさを娯楽色豊かに描く骨太の時代劇。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Royal Tenenbaums", "ザ・ロイヤル・テネンバウムズ", "", "", "http://ecx.images-amazon.com/images/I/51NdZxcB6tL._SL210_.jpg", 110, "テネンバウム家の子供3人は全員天才児。長男は相場、長女は戯曲、末の弟はテニスの天才だったが、父が家を出て別居し、その後の生活ははかばかしくない。その父が22年振りに家に戻り、それを機に家族全員が再会する。マーティン・スコセッシが「21世紀のスコセッシだ」と宣言したウェス・アンダーソン監督の長編第3作。オーソン・ウェルズなどの古典映画やポップ音楽の引用ぶり、凝った小道具使い、ナイスなギャグなど見所満載。", "2002-09-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIDDHARTHA", "シッダールタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ビートニク文学に傾倒し東洋思想に深い関心を抱いたC・ルークスが、ヘッセの同名小説を自主制作で映画化した監督第２作。厳格なバラモン僧の子として生まれた主人公シッダールタが、退屈な日常を捨てて苦行僧となり、苦悩の放浪生活の果てに安らぎを見いだすまでを描く。ニューヨークで限定公開された後、フィルムそのものが行方不明になっていたが、ようやく1997年に日本で劇場公開された。悠久たるインドの大地を切り取った名手S・ニクヴィストによる映像が美しい。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真夏の夜の夢", "真夏の夜の夢", "", "", "http://image.eiga.k-img.com/images/movie/54428/315x210.jpg?3809", 105, "「ナビィの恋」「ホテル・ハイビスカス」の中江裕司監督が、シェイクスピアの「真夏の夜の夢」を翻案し、沖縄の小さな離島を舞台に描くラブコメディ。不倫の恋に疲れて東京から故郷の沖縄の小さな島、世嘉冨（ゆがふ）島に戻ってきたゆり子。そんな彼女を追って、不倫相手やその妻もやってきて小さな島は大騒動に。島の精霊マジルーは、恋の秘薬を振りまいて、ゆり子の身を守ろうとするが……。主演はNHK大河ドラマ「風林火山」の柴本幸。", "2009-07-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fazil", "ファジル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "アラブの伝統に生きる王子ファジルと自由奔放なパリの娘。二人はベニスの街で出会って恋に落ち、ファジルの国で結婚生活を始めるが、考え方の違いから、二人の間の大きな隔たりが次第に明らかになる。だがお互いを愛する気持ちは止められず…。製作費が大幅に予算超過したというだけあって、セットや衣装の豪華さも印象的である。", "1928-11-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CATCH ME A SPY", "雨のパスポート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "新婚初夜のベッドから、夫がスパイ容疑で東側の秘密警察に連行されてしまった。かくて残された新妻はロンドンに戻って、謎の男の影に脅かされながらも事件の謎に挑んでいく。そんな彼女の奮闘ぶりをシャレたムードたっぷりに描いたサスペンス・コメディ。", "1971-01-01", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("耳をすませば", "耳をすませば", "", "", "http://ecx.images-amazon.com/images/I/51bQ7qxqDDL._SL210_.jpg", 110, "いつかは自分でも物語を書こうと思っている読書家の中学３年生・雫の前に、イタリアに渡ってバイオリン職人になることを夢見る少年・聖司が現れる。中学生でありながら、すでに自分の将来を考えている聖司に雫はコンプレックスを感じつつも惹かれいく。やがて、聖司はイタリアへと旅立つ。宮崎駿が、カントリーの名曲『カントリーロード』を聞くうちに“今の中学生にとって、カントリー（故郷）とは？”と考え始めたことから構想が広がっていったというだけあって、作中ではこの曲が重要なモチーフとなっている。宮崎駿は今回はプロデュースと脚本を担当、監督には「火垂るの墓」「魔女の宅急便」「おもひでぽろぽろ」の作画監督・近藤喜文が当たっている。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El Topo", "エル・トポ　製作40周年デジタルリマスター版", "", "", "http://image.eiga.k-img.com/images/movie/55728/315x210.jpg?2454", 123, "チリ出身のアレハンドロ・ホドロフスキー監督が手がけたカルト映画が、製作40周年を記念しデジタルリマスター版で復活。銃の名手エル・トポ＝モグラは、最強のガンマンになるため、息子を置き去りにして砂漠へと旅立つ。エル・トポは、砂漠に暮らす4人の銃のマスターたちを卑劣な手で倒すが、最強であることの無意味さを思い知る。", "2010-09-25", "1,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不可殺／PULGASARI", "プルガサリ　伝説の大怪獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「ゴジラ」の日本人スタッフが特撮面で協力した北朝鮮生まれの怪獣映画。着ぐるみでゴジラを演じた薩摩剣八郎が、プルガサリを演じている。時の権力者に横暴な政策によって捕らえられた老人が、米つぶで人形を作り息絶える。老人の死とともに魂を吹き込まれたこの人形が怪獣プルガサリとなって巨大化し、虐げられた民衆を救うため、権力者の軍隊に立ち向かう。", "1985-01-01", "48", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("For Your Eyes Only", "007／ユア・アイズ・オンリー", "", "", "http://ecx.images-amazon.com/images/I/51GsR7HmRkL._SL210_.jpg", 128, "東西軍事バランスを左右するミサイル誘導装置を、東側に売り渡そうとギリシャの富豪とボンドが奪い合う。派手なメカニック・アクションは少なく、ボンドのスーパーマン的活躍にスポットを当てたシリーズ第12作。主題歌はシーナ・イーストン。ボンドガールはキャロル・ブーケ。", "1981-07-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECRET GARDEN OF SENO＾R LOPEZ", "秘密の庭のロペス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "家庭でも会社でもさえない中年男ロペスの唯一の憩いの場所はトイレだった。その中で彼は空想の世界に逃避し、美女と戯れたりしている。そんな彼はある日、会社で使い込みの疑いをかけられてしまう。そして社長の美人秘書が彼に急接近してきて……。現実と空想が入り乱れるファンタジー・コメディ。", "1988-01-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ST.ELMO’S FIRE", "セント・エルモス・ファイアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "大学を卒業し、社会に出たばかりの７人の男女の姿を捉えた青春映画。７人がそれぞれまったく異なった人物像として綴られ、青春期特有の波乱に富んだドラマを展開。夢、希望、挫折、友情、そして愛といった人生の側面が、真摯に描き上げられている。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超電影版SDガンダム　三国伝 Brave Battle Warriors", "超電影版SDガンダム　三国伝 Brave Battle Warriors", "", "", "http://image.eiga.k-img.com/images/movie/55148/315x210.jpg?4592", 0, "「三国志」の物語をベースに、SDガンダムたちが繰り広げる戦いを描くアニメーション。伝説の大陸「三璃紗」は、悪政を行う董卓ザクによって支配され、民は絶望の淵に立たされていた。そこに幽州の義勇軍、劉備ガンダム、関羽ガンダム、張飛ガンダムの3人が現れ、董卓軍の鎮江将軍・胡軫ギャンに挑む。「超劇場版ケロロ軍曹／誕生！究極ケロロ　奇跡の時空島であります!!」と2本立て同時上映。", "2010-02-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("色情男女／VIVA EROTICA", "夢翔る人・色情男女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "L・チャンが売れない映画監督に扮するハートフル・コメディ。映画監督シンにやっと舞い込んできた仕事は何とポルノ映画。気乗りしないうえに、主演女優モニクはまるでやる気なし。さらに、話題作りにとプロデューサーから街頭撮影を命令され……。それぞれ勝手し放題の映画製作の舞台裏をユーモアたっぷりに描出。バラバラだった人々が信頼関係を築き、一致団結していく姿にほのぼのとしたムードが漂う。劇中のポルノ映画のワン・シーンをレスリー自身が監督している。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Adventureland", "アドベンチャーランドへようこそ", "", "", "http://ecx.images-amazon.com/images/I/61vNDlljkKL._SL210_.jpg", 106, "「スーパーバッド　童貞ウォーズ」のグレッグ・モットーラ監督が、1980年代カルチャーを背景に若者たちのひと夏の恋と成長をつづった青春コメディ。87年夏、大学院への進学を控えた冴えない青年ジェームズは、父親が減給されたために夏休みに予定していた海外旅行をあきらめ、地元の寂れた遊園地「アドベンチャーランド」でアルバイトすることに。ジェームズはそこで、エキセントリックなバイト仲間たちや美少女エムと出会う。「ゾンビランド」のジェシー・アイゼンバーグと「トワイライト」シリーズのクリステン・スチュワートらが出演。", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.NANNY", "ハルク・ホーガン　Mr.ベビーシッター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "プロレスラーのH・ホーガンが、ベビーシッターに挑戦したコメディ。落ち目のプロレスラー、ショーンは、試合もなく日々の生活にも困る始末。見かねたマネージャーが彼に勧めたのが、何とリッチなメイソン家でのベビーシッターの仕事だった。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誰も知らない", "誰も知らない", "", "", "http://image.eiga.k-img.com/images/movie/1568/315x210.jpg?1826", 141, "12歳の長男役を演じた柳楽優弥が第57回カンヌ国際映画祭最優秀男優賞を受賞。父親が異なる4人の兄妹と母の母子家庭。アパートを追い出されないために、父が海外赴任中で母と息子の2人暮らしだと偽って暮らす彼らは、そのため学校にも通ったことがない。だが母親に新たな恋人が出来て、兄妹に20万円を残して失踪、子供たちはなんとか自分たちで暮らしていこうとする。監督は「ワンダフル・ライフ」「ディスタンス」の是枝裕和。", "2004-08-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アカルイミライ", "アカルイミライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「回路」の黒沢清監督が丹精込めて綴った“親子”ドラマ。兄貴分に去られた青年と、息子を失った父親が出逢い、二人で生きる術を発見しようとする様を、透明感あふれる映像で見つめていく。おしぼり工場で働く仁村はキレやすい青年。彼は、何かと気にかけてくれる同僚の有田を兄のように慕っていた。ところがある事件で有田は捕まり、飼っていたクラゲを仁村に残して獄中で自殺してしまう。途方に暮れる仁村の前に、なぜか有田の父親が現れ……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍在少林／LITTLE　SHAOLIN　DRAGON", "ビビアン・スーのロマンシング・ドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "Y・ピョウとV・スーが共演したアクション・アドベンチャー。北京で開かれた美術工芸品のオークションで、仏像のモデルになった仏頭が、シャンという男に落札される。彼は文化遺産的品物を聖地に戻すため、世界各地を回っていた。早速、仏頭が出土したセイセイ省に向かった彼は、スリの美少女スエンと知り合い、次第に恋心を抱き始める。しかし、そんな二人に、仏頭を狙うシャンの兄の魔の手が迫る。", "1996-01-01", "6,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春スケッチ", "青春スケッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "どこにでもいる平凡な大学生チョルスと、気が強くキュートな女の子ミミ。そして、いつも望遠鏡を離さない不思議な男“宝島”。３人の姿を通して、韓国の若者たちのみずみずしい青春像が描かれている。大学のキャンパスやディスコの様子、女子大生のファッション感覚やデートコースなど韓国を感じさせる風俗が続々登場。なかでも、学生たちが飲み明かす屋台が面白い。その題材の身近さとフレッシュな感覚が、若者に圧倒的な支持を受け、韓国では1987年度興行記録第一位を記録した。ミミに扮するのは、「シバジ」で1987年度ヴェネチア映画祭の主演女優賞を受賞したカン・スーヨン。", "1987-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スイート・スイート・ゴースト", "スイート・スイート・ゴースト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "長崎県佐世保の沖合に浮かぶ島の高校に、東京から謎めいた少女が転校してきた。やがて彼女と二人の少年との間に、奇妙な想いが芽生えていく……。ゴーストにマジックといったファンタスティックなアイテムを絡めながら描かれる、切なさと優しさに満ちた青春映画。これがデビュー作となる芳田秀明監督の実際の故郷で撮影された。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒバクシャ／世界の終わりに", "ヒバクシャ／世界の終わりに", "", "", "http://image.eiga.k-img.com/images/movie/40968/315x210.jpg?1785", 116, "フリーの映像作家として医療、環境問題などのノンフィクション作品を手掛ける鎌仲ひとみ監督によるドキュメンタリー映画。1998年、湾岸戦争の傷跡が残るイラクを訪れた鎌仲監督が、白血病やがんと闘う多くの子供たちとの出会いに端を発して、イラク、アメリカ、日本にいる被爆者を取材。被爆者の切なる声に、耳を傾けていく映像が綴られていく。1988年１月、フリーの映像作家・鎌仲ひとみ監督が、湾岸戦争の傷跡が深く残るイラクを訪問する。現地バグダッドの病院で出会った14歳の少女・ラシャが、“私を忘れないで”と書いた小さな紙切れを残し亡くなってしまう。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やっさもっさ", "やっさもっさ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「てんやわんや」（1950）、「自由学校」（1951）に次ぐ獅子文六原作＝渋谷実監督コンビの第３作。終戦直後の横浜、駐留軍基地にある混血児収容所を舞台にしたコメディ。混乱した戦後社会への諷刺的な作品で、淡島のフレッシュな魅力と東山の存在感が印象的。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Coco Chanel", "ココ・シャネル", "", "", "http://image.eiga.k-img.com/images/movie/54358/315x210.jpg?3713", 138, "1954年のパリ、15年のブランクを経て復帰コレクションを開催したココだったが、「過去から脱却できないシャネル」と酷評されてしまう。ココは孤児だった頃や、ボーイ・カペルとの悲恋など、自らの過去を振り返り、デザイナーとしての再起を誓う。晩年のココを「アパートの鍵貸します」「愛と追憶の日々」のシャーリー・マクレーン、若き日のココをチェコの新星バルボラ・ボブローバが好演。", "2009-08-08", "1,4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY BLUE HEAVEN", "マイ・ブルー・ヘブン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "マーティン扮する魅力的で雄弁な犯罪通報者が、自分の身を守るためカリフォルニアにある小さな町に移り住む。彼は一流の犯罪通報者から今は政府のエージェントにさえなっていたが、FBI捜査官や地方検事補が複雑に絡んできて話はややこしくなり……。３度目の共演となるS・マーティンとR・モラニスのコンビネーションが抜群のコメディ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dillinger", "デリンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "　不況の嵐が引き荒れる1933年、デリンジャーと彼の手下たちは各地で銀行強盗を繰り返していた。途中、先住民の血を引く娘ビリーと出会ったデリンジャーは彼女までも引き連れて旅を続ける。その後、一度は逮捕されたものの、まんまと脱獄し、犯行を重ねるデリンジャー。そんな彼の前に、悪名高い犯罪者たちを次々と葬っていたFBI捜査官パービスが立ちはだかった！　犯罪史にその名を轟かせたジョン・デリンジャーの半生を描くギャング・アクション。30年代を再現した映像も出色。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レイン・フォール　雨の牙", "レイン・フォール　雨の牙", "", "", "http://image.eiga.k-img.com/images/movie/53668/315x210.jpg?4200", 111, "バリー・アイスラーのベストセラー小説「雨の牙」を、椎名桔平主演、 ゲイリー・オールドマン、長谷川京子共演で描いたクライム・サスペンス。日系アメリカ人の殺し屋ジョン・レインは、国土交通省の官僚の暗殺を依頼される。しかし、殺害した官僚が持っているはずのメモリーデータが消えたことから運命が狂いはじめ……。監督・脚本は黒沢清監督作「トウキョウソナタ」の脚本を手掛けたオーストラリア出身のマックス・マニックス。", "2009-04-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INNER SANCTUM", "スキャンダラスな女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "階段から落ちて、車イスでの生活を余儀なくされたジェニファー。自分の殻に閉じこもり、やがて病的なまでに精神状態が不安定となった彼女は、夫やその周囲の人間が自分を殺そうとしているように見えてくる。錯綜したストーリーと大胆なエロティック・シーンで見せるスリラーに、低予算映画の名物監督、レイが挑戦。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サロネン・コンダクツ・アダージョ", "サロネン・コンダクツ・アダージョ", "", "", "http://image.eiga.k-img.com/images/movie/56188/315x210.jpg?1615", 45, "グフタス・マーラーの伝記映画「マーラー　君に捧げるアダージョ」で劇中楽曲を演奏したエサ＝ペッカ・サロネン指揮、スウェーデン放送交響楽団の録音風景を、同作のパーシー・アドロン監督が撮りおろしたミュージック・メイキング。", "2011-04-30", "7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本一短い「母」への手紙", "日本一短い「母」への手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "母親への様々な思いを込めた手紙を１冊の本にまとめた、同名のベストセラーを映画化。秀作の中の１編で30字の短い手紙をモチーフに、一つの家族のドラマを描く。18年前に両親が離婚。母親は家族を捨て男の元へと走り、服飾デザイナーの真紀と大学生の宏の姉弟は男手一つで育てられた。そんな生活を送る中、父親が急死。真紀は自分の気持ちをワープロに綴り、それを見た宏はこの手紙を“日本一短い「母」への手紙コンテスト”に応募。そして、作品が秀作に選ばれたのを機に宏は母親探しを始める……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("光戦隊マスクマン", "光戦隊マスクマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 21, "地上の支配を企む地底帝国チューブに立ちむかう5人の戦士の奮闘を描いたスーパー戦隊シリーズの劇場版。地底の海に住む人魚レライが、地帝王子イガムに恋をした。愛するイガムのため、美しい人間の姿となって歌うレライ。ところが、レライの美しい歌声は地帝獣ホーンドグラーの鳴き声と共鳴し、地上に大地震が引き起こされてしまう。駆けつけたマスクマンたちに説得されても、レライは自分がイガムに利用されていることを信じようとせず……。", "1987-07-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wig", "鬘　かつら", "", "", "http://image.eiga.k-img.com/images/movie/33588/315x210.jpg?2906", 102, "「亡国のイージス」のチェ・ミンソ主演のサスペンスホラー。美術家として活躍するジヒョンは、がん治療により髪の毛をすべて失った妹スヒョンのために、美しい黒髪のかつらを用意する。スヒョンがかつらを付けると、不思議なことに彼女は瞬く間に元気な姿を取り戻し、性格までも一変してしまう。愛する妹のあまりの豹変ぶりに、次第に恐怖を感じ始めるジヒョン。やがて彼女は、かつらに秘められた忌まわしい呪いの全貌を知る。", "2006-08-05", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Interview", "Interview", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "映画監督と彼のインタビューに応える女性が、カメラを通して心を通わせる異色の愛の物語。ロケ撮影や手持ちカメラなど、独特の撮影方法を提唱する監督集団“ドグマ95”の初のアジア映画だ。映画監督のウンソクが、ドキュメンタリー制作のため、様々な人に恋愛に関するインタビューを始める。その中の一人の女性イ・ヨンヒの寂しげな表情が彼の心を捉えるが、美容師見習いだと語っていた彼女の話がすべてウソであることが分かり、ショックを受ける。やがて、彼女は重い口を開き始め、カメラに向かって真実を話し始める。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIVORZIO ALL’ITALIANA", "イタリア式離婚狂想曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "名匠P・ジェルミによる、イタリアお得意の艶笑喜劇。若い従妹アンジェラと一緒になるため、口うるさい妻ロザリアを殺そうと計画しているフェルディナンド。彼は妻殺しの条件として、ロザリアに浮気させようとするが……。アンジェラを演じるS・サンドレッリの小悪魔的魅力が見もの。", "1962-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARABESQUE", "アラベスク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "舞台はロンドン。中近東の政治的陰謀に巻き込まれたアメリカの考古学者が、訪英する首相暗殺計画を察知。謎の美人スパイの協力を得て、間一髪でこれを救う。ロンドン・ロケを生かし、美しい画面を次々と展開させるロマンティック・ミステリー。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOURS TRANQUILLES A｀ CLICHY", "クリシーの静かな日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "H・ミラーの自伝的短編小説をC・シャブロルが映像化。1930年代、憧れのパリにやって来た新進小説家と前衛的な写真家、そして二人に愛された小悪魔的少女の情愛の日々をロマンティックに映し出していく。ヨーロッパ的な退廃の美学と、若き日の芸術家たちの陶酔を漂わせたデカダンスな一編。", "1990-01-01", "4,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dictator", "ディクテーター　身元不明でニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/58128/315x210.jpg?3132", 0, "「ボラット　栄光ナル国家カザフスタンのためのアメリカ文化学習」「ブルーノ」のサシャ・バロン・コーエンが、今度は世界一危険な独裁者アラジーンに扮し、ニューヨークで身元不明人になってしまったアラジーンが巻き起こす騒動を描くコメディ。監督は「ボラット」「ブルーノ」に続きラリー・チャールズ。", "2012-09-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君と歩こう", "君と歩こう", "", "", "http://image.eiga.k-img.com/images/movie/54988/315x210.jpg?3110", 90, "「剥き出しにっぽん」「ばけもの模様」などで国内外から高い評価を受ける石井裕也監督の長編第5作。田舎町の高校で英語教師として働く34歳の明美は、冴えない教え子のノリオと勢いだけで駆け落ちしてしまう。彼らは東京でアパートを借りて一緒に暮らし始めるが、厳しい現実や周囲の奇妙な人々に振り回されるうちに、2人の心は少しずつすれ違っていく。主演は「こぼれる月」の目黒真希、「色即ぜねれいしょん」の森岡龍。", "2010-05-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("No Country for Old Men", "ノーカントリー", "", "", "http://image.eiga.k-img.com/images/movie/53088/315x210.jpg?3640", 122, "第80回アカデミー賞において、作品、監督、脚色、助演男優の4部門で受賞した犯罪ドラマ。1980年の米テキサスを舞台に、麻薬密売人の銃撃戦があった場所に残されていた大金を盗んだベトナム帰還兵（ブローリン）と殺し屋（バルデム）の追跡劇、そして2人を追う老保安官（ジョーンズ）の複雑な心情が描かれる。原作はピュリッツァー賞作家コーマック・マッカーシーの「血と暴力の国」（扶桑社刊）。監督・脚色は「ファーゴ」（96）、「ビッグ・リボウスキ」（98）のジョエル＆イーサン・コーエン。", "2008-03-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gray's Anatomy", "グレイズ・アナトミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "“マスター・オブ・モノローグ”（独白名人）と呼ばれる芸人、スポルディング・グレイが発表した、自分の経験に基づくワンマン・ショーを映画化した異色コメディ。", "1996-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("D.O.A.", "D.O.A. 死へのカウントダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "教え子が校舎から転落した夜、大学教授のコーネルは、女子大生シドニーの部屋に泊まってしまう。だが彼は何者かに、遅効性の毒薬を飲まされた。48時間という残された時間と、真犯人を追うコーネル。だが次第に意識は薄れ、身体は衰弱していく。1949年製作の「教会の牙」の３度目の映画化。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン／水平線上の陰謀〈ストラテジー〉", "名探偵コナン／水平線上の陰謀〈ストラテジー〉", "", "", "http://ecx.images-amazon.com/images/I/61ck9e-lrLL._SL210_.jpg", 105, "コミック累計部数１億部を突破し、絶大な人気を誇る国民的アニメの劇場版第９作。豪華客船内の連続殺人事件に巻き込まれたコナンが、逃げ場のない海上で大ピンチに。過去と現在の事件が交錯する二重サスペンスの展開に、いつもはドジばかりの小五郎の推理もコナンに負けず冴え渡る。太平洋をいく豪華客船の処女航海にコナンたちが参加。しかし、その船内で殺人事件が発生。事件の裏には過去の恐ろしい陰謀があった。", "2005-04-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOTOR PSYCHO", "モーター・サイコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "「ファスター・プシィキャット！キル！キル！」へと続く、異才R・メイヤーのバイオレンス路線の一編。我が物顔でハイウェイを行く、無軌道なバイカー３人組は、暴行に飽き足らず、ついには殺人をも犯す。彼らに妻をレイプされた獣医は復讐に燃え、３人の魔の手を逃れた人妻とともに、その行方を追跡する。エキゾチックな巨乳美女ハジが存在感を発揮。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARTY7", "PARTY7", "", "", "http://image.eiga.k-img.com/images/movie/1588/315x210.jpg?4665", 104, "とある街の郊外。訳アリの金を持って逃走するチンピラ三木がホテルニューメキシコへ辿り着いた。案内されたのは401号室。そして隣室にはノゾキ常習犯オキタが忍び込んでいた。生来のノゾキ趣味に逮捕歴を重ねていた彼は、出所後「俺の作ったホテルにはノゾキ部屋がある」という父の遺言を頼りにここへ辿り着いたのだった。しばらくして401号室には三木の元彼女カナが借金を取り返しに現れ、ノゾキ部屋ではオキタが謎の男“キャプテンバナナ”に遭遇する。", "2000-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD BROTHERS", "愛の断層", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "R・ギアの比較的初期の頃の出演作品で、R・マリガン監督と組んだ青春映画。早く一人前の大人の男になろうとする青年と、彼をいつまでも子供として扱おうとする父親との親子の断絶を描く。ウォルター・ニュートンが1978年度アカデミー賞で脚本賞にノミネートされた。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サラリーマン目白三平", "サラリーマン目白三平", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "雨もりするような家に住みながら、奥さんや子供たちとともに和やかな家庭を築いている勤続20数年のサラリーマン・目白三平を主人公に、彼の日常生活をほほえましく描いた作品。ラジオドラマとして連続放送された中村武志の同名小説を原作に映画化。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROME，2072 A.D.THE NEW GLADIATORS", "未来帝国ローマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "時は西暦2072年、主人公はバイク・レースのチャンピオン。彼は巨大な陰謀に巻き込まれ、TVの殺人ショーに出るはめになる。スプラッタ・キング、L・フルチが作った近未来バイオレンス・アクション。", "1985-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMBRYO", "エンブリヨ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "成長ホルモンを使い生物の成長を加速させる実験をしていたポールは、ある日、犬を使ってその効果に自信を持ち、人間の胎児に投与する決心をする。自殺未遂の女性の胎児を実験台にしたポールだが、結果はポールの想像を絶するものだった！　科学実験により怪物化していく女性の悲哀を描いた異色ホラー。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sucker Punch", "エンジェル　ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/54538/315x210.jpg?4318", 110, "「300 スリーハンドレッド」「ウォッチメン」のザック・スナイダー監督が描くアクションファンタジー。精神病棟に閉じ込められた少女が、現実から逃避するためにつくり出した鮮烈なバーチャル世界で、仲間たちと自由を勝ち取るための戦いに身を投じていく姿を描く。エミリー・ブラウニング、ジェナ・マローン、バネッサ・ハジェンズ、アビー・コーニッシュ、ジェイミー・チャンら若手女優が豪華出演。", "2011-04-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PANEL STORY", "パネルストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "手抜きだらけの住宅政策のおかげで、欠陥団地に住むハメになった多くの人々。歴史も伝統もない、そんな団地に住む普通の人々、老若男女の暮らしを見つめた悲喜こもごものエピソードを綴っていく。チェコの女流監督V・ヒティロバが、日常に潜む暗鬱とした思いを強烈なユーモアで笑い飛ばす、アイロニーに満ちた人間群像劇。", "1979-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DR.M", "ドクトル・エム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "C・シャブロルが、“壁崩壊”で揺れ動くベルリンを舞台に描くスリラー作品。シャブロル自身が最も尊敬する映画作家フリッツ・ラングへのオマージュ作品として作られている。ベルリンで芸術家や有名タレントたちが、次々と自殺する謎の事件が続く。刑事のハルトマンは、あるクラブのイメージ広告の潜在作用と関係しているとにらんだ。やがて巨大企業を統率するマースフェルド博士という人物が浮かび上がってくる。この謎の人物“M博士”こそ、メディアと高度なエレクトロニクスを駆使して、民衆洗脳独裁の野望をもつ恐るべき人物であった。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Navarasa", "ナヴァラサ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "実在する南インドの女装フェスティバルをモチーフに、ひとりの少女の視点から性同一性障害の問題を描いた作品。13歳の少女シュエータは、叔父が女装しているのを目撃し、ショックを受ける。叔父が間もなく開催されるクーバガムの祭で女として生きることを誓おうとしていると知ったシュエータは、叔父の後を追うことに。初めのうちは祭の参加者たちに偏見を抱いていたシュエータだったが、さまざまな出会いを通して次第に理解を深めていく。", "2007-03-17", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("敦煌", "敦煌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "11世紀の戦乱の中国大陸を舞台に、新興国・西夏に魅せられてシルクロードに旅立つ若者・趙行徳と、侵略が繰り返される砂漠に生きる人々の姿を描いたスペクタクル大作。そもそもは小林正樹が1963年に立てた企画といわれ、その後、スタッフやキャストの交代劇、中国ロケの了承を取りつける難しさなどがあり、完成には25年が費された。それだけにでき上がった作品は、のべ10万人のエキストラ、４万頭の馬によるロケーションを敢行するといった熱の入れよう。西田敏行の好演も光る。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAAR　DE　KIOTE！", "アムステルダム・ウェイステッド！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "麻薬が事実上解禁されているアムステルダムを舞台に、現代のドラッグ・カルチャーを捉えた青春ドラマ。アムステルダムに上京した若いカップルを主人公に据えて、彼らの周囲に群がるドラッグの売人やドラッグに溺れる者たちの享楽的な日常を追っていく。デジタルカメラで撮影した鮮烈な色使いの映像と、テクノ・サウンドが融合し、トランス感覚を醸し出す。", "1996-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沖縄やくざ戦争", "沖縄やくざ戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "本土復帰を目前にした沖縄を舞台に、地元暴力団同士で繰り広げられる血で血を洗う抗争を熱っぽく描いた、実録風ヤクザ映画。沖縄の本土復帰問題をバイオレンスの陰に潜ませた、中島貞夫監督のシニカルな視線が印象的である。千葉真一、松方弘樹が熱演。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鴎よ、きらめく海を見たか／めぐり逢い", "鴎よ、きらめく海を見たか／めぐり逢い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ソ連モスフィルムとの合作「モスクワわが愛」を手掛けて話題を呼んだ吉田憲二監督の青春映画。幻のユートピアを夢見ながら、都会の片隅で不器用に生きる若者の真摯な愛とその破局を描く。高橋洋子が華やかな都会の生活に憧れて上京した地方出身のOLを好演。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DE´JEUNER SUR L’HERBE", "草の上の昼食", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "人工受精論者の有名な生物学者アレクシス教授が、かつぎ出されて欧州連合の大統領に立候補。しかも、強引に政略結婚までさせられそうになる。しかし、挙式の前に田舎娘ネネットの水浴姿を見た教授は官能の虜になってしまう。妨害もあるが、自然の法則に従う幸福を選んで、彼はネネットと結ばれる。ロバート・アルトマンの「ウエディング」、伊丹十三の「お葬式」にも影響を与えた巨匠ルノワールの会心作。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・純情篇", "男はつらいよ・純情篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "家出妻二人の面倒をみる寅次郎。旅先で助けた人妻を実家へ送り届けた寅さんが柴又へ帰ってみると、おばちゃんの遠縁にあたる美しい家出妻が“とらや”の手伝いに来ていた。のぼせる寅だが、彼女も結局は夫のもとへ帰っていく。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女がいちばん似合う職業", "女がいちばん似合う職業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「野獣死すべし」の丸山昇一が脚本を書いた久しぶりのアクション映画。「最も危険な遊戯」の仙元誠三のカメラ、そして桃井かおりの主演と、本作は1989年に亡くなった松田優作へ捧げられた作品である。桃井扮する女刑事きぬが、岡本健一演じる魅力的な凶悪犯を追いつめていくという内容で、意匠をこらした映像に見ごたえあり。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONTE　CARLO", "モンテカルロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "オットー伯爵との結婚を嫌い、侍女を伴って逃げ出した令嬢のラマは、カジノの街、モンテカルロでギャンブルに興じていた。そんな彼女を見初めたルドルフ伯爵は身分を偽り彼女に接近しようとする。やがて、金に困った彼女の前に、婚約者のオットーが現れ、ラマを連れ帰ろうとするが……。E・ルビッチお得意のオペレッタもの。", "1930-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAUNTED　CASTLE", "ホーンテッド・キャッスル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "最新のCG映像と3D技術を駆使した、臨場感あふれる“体感”ホラー・ムービー。ミュージシャンとして活躍するジョニーは、彼が3歳のときに生き別れになった母の訃報を受け、彼女が人里離れて住んでいた不気味な古城を訪れる。なんとそこには、かつてロック・スターだった母の秘密が隠されていた……。", "2000-01-01", "37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("或る夜の接吻", "或る夜の接吻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "詩人の貝原、建築技師の雁金、発明家の紙の３人は、かつて生死をともにした戦友同士。３人それぞれのしゃれた恋愛模様をさわやかに描いていく。大映が脚本募集した際に選ばれた『新生』を映画化したもの。『新生』とは、貝原が愛した歌手の綾子がラストで歌う歌のタイトルでもある。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラVSキングギドラ", "ゴジラVSキングギドラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "“ゴジラ”シリーズ第18作。監督には前作「ゴジラVSビオランテ」の大森一樹が再度登板。1992年、東京上空にUFOが出現。そんな時、作家の寺沢は1944年に恐竜がアメリカの機動部隊を撃退した話を聞き、その恐竜が核実験でゴジラに変身したという事実を知る。そしてUFOの未来人は日本の死滅を予告。寺沢らはタイムワープ機に乗り、ゴジラ消滅作戦を開始するが……。今回はシリーズ中最も凶暴なライバルとして注目されたキングギドラを再登場させ空前のバトルロイヤルを展開。さらにゴジラが太平洋戦争の攻防戦に登場していたという新説を設定し、過去、現在、未来のタイムトラベルも加えたSF大作に仕上げている。", "1991-12-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cape Fear", "恐怖の岬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "残虐な性的犯罪で長年刑務所に入っていたキャディが出所した。公判で彼を無罪にする努力を怠った弁護士のサムは、身の危険を感じ、彼を町から追放しようとする。警察署長、私立探偵、ついには暴力団のボスとあらゆる手段を使うが、それもかなわず、やがてキャディの復讐の手が娘におよぶ。マーティン・スコセッシ監督の1991年作「ケープ・フィアー」のオリジナル版。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦 IKUSA 第弐戦　二本松の虎", "戦 IKUSA 第弐戦　二本松の虎", "", "", "http://image.eiga.k-img.com/images/movie/33568/315x210.jpg?1532", 94, "2005年に公開された異色任侠映画「戦 IKUSA」の続編。東北で繰り広げられる暴力団同士の抗争を背景に、前作ではアニメを作った主人公・御堂一沙が、傾きかけた喜多方ラーメン店を再生させようと奮闘する姿を描く。前作に引き続き「仮面ライダー THE FIRST」の高野八誠が主演を務め、小沢和義、遠藤憲一ほか豪華レギュラー陣が脇を固める。さらに「小さき勇者たち ガメラ」の津田寛治が扮する新キャラクターも登場。", "2006-07-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Informant!", "インフォーマント！", "", "", "http://image.eiga.k-img.com/images/movie/54528/315x210.jpg?1771", 108, "「オーシャンズ」シリーズのマット・デイモンとスティーブン・ソダーバーグ監督が、カート・アイケンウォルドの原作を基に、ある大企業の重役が起こした内部告発事件の顛末を描いたコメディドラマ。1992年、アメリカ・イリノイ州の大手穀物加工会社で働くエリート社員のマーク・ウィテカーは、自社の価格協定の陰謀をFBIに内部告発する。彼は秘密諜報員気取りで自ら調査に乗り出すが、次第に自身の不正も明らかになっていき……。", "2009-12-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE 7TH DAWN", "第七の暁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "「フレンズ／ポールとミッシェル」や「007」シリーズなどの監督として知られる娯楽派、L・ギルバートの演出による戦争ドラマ。第二次大戦終結後のマラヤ。ゴム園を経営するフェリスと、元抗日ゲリラとして活躍し、今は共産軍のゲリラとなったヌーとの友情、そして揺れ動く政情を描く。主演はW・ホールデンと、後に「007は二度死ぬ」で再びギルバート監督と組むことになる丹波哲郎。", "1964-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モウ翔ブ夢ハ見ナイ", "モウ翔ブ夢ハ見ナイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "タクシー運転手、瑠璃子が自殺未遂を犯した友人、照美の命を救う。照美の希望で病院を抜け出した二人は海辺のホテルにたどり着くが、奇怪な殺人事件に巻き込まれていく。高島礼子がハーレーダビッドソンを乗りこなすハードボイルドなヒロインに扮したサスペンス映画。「凶銃ルガーP08」の渡辺武がクールな映像感覚を発揮、事件の鍵を握る謎の男・生駒を鶴見辰吾が演じている。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Rafle", "黄色い星の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/56158/315x210.jpg?2799", 125, "ナチス占領下のパリ。11歳の少年ジョーは、ユダヤ人であることを示す黄色い星を胸につけることに嫌悪感を抱きながらも、家族とのささやかな幸せが続くことを信じていた。しかし1942年7月16日、パリでユダヤ人の一斉検挙が始まってしまう。フランス政府がナチスに協力し、1万3000人のユダヤ人を競技場に収容した事件を、当時の経験者の証言をもとに映画化。監督は元ジャーナリストのローズ・ボッシュ。「オーケストラ！」のメラニー・ロラン、「レオン」のジャン・レノが出演。", "2011-07-23", "2,7,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVERGREEN", "イギリス物語～ミュージカル「永遠の緑」～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1930年代の代表的なイギリス・ミュージカル映画。母と娘がともにミュージカル女優になるという物語の主人公の女性二人を当時の人気スター、J・マシューズが二役で演じている。モノクロの古典的な映像が、かえって新鮮な印象を与える。", "1934-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野獣の肖像", "野獣の肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アウトローやくざの生き様をエネルギッシュに描いた任侠アクション。“人斬り鬼”の異名をとるやくざの鬼塚が、対立組織のチンピラを刺殺。その一部始終をビデオカメラでスクープされたことから、彼は服役した。５年後、出所した鬼塚は、組が対立組織に吸収されたことを知り、帰る場所を失う。そんな彼に、５年前にスクープをものにして出世した女性カメラマンが興味を抱き……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHERE THE BOYS ARE", "ボーイハント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "イースターの休みに北部からフロリダにバケーションする４人の女子大生の青春を描いた、当時のヤングスター総出演の音楽コメディ。C・フランシスの第１回出演作品で、主題歌が大ヒット。1984年にリメイクされた。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wild Wild West", "ワイルド・ワイルド・ウエスト", "", "", "http://image.eiga.k-img.com/images/movie/1798/315x210.jpg?2724", 106, "時は西部開拓時代。連邦特別捜査官ジェイムズ・ウェストは、“流血将軍”の異名を取るマグラス将軍を追いかけていた。もう少しで逮捕できるところを、やけに厚化粧でグラマラスな女のせいで取り逃がしてしまう。女の正体は、同じく連邦特別 捜査官のアーティマス・ゴードン。なんと、女装して潜入捜査をしていたのだ。2人は合衆国の命を受け、マグラス将軍の背後にいるラブレス博士を逮捕するためコンビを組むことになる。", "1999-12-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THOSE DARING YOUNG MEN IN THEIR JAUNTY JALOPIES", "モンテカルロ・ラリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "1920年代、車自慢の男たちが自分の運転能力と愛車の性能を試すために、お祭り騒ぎで行っていたモンテカルロ・ラリー。それに参加した米・英・仏・伊のツワモノたちの悪戦苦闘をコミカルに描く。出演者にも各国から芸達者を集めたドタバタ調が痛快。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AENIGMA", "怒霊界エニグマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "周囲からいつもいじめられている女子高校生が、彼女をいじめた人間たちを怨念によって次々と血祭りにあげていく。デ・パルマ監督の「キャリー」を思わせるSFサイキック・ホラー。カタツムリが鼻や耳、口から入り込むというグロテスク感覚いっぱい。イタリアのベテラン監督L・フルチの異色作。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Secret Agent", "間諜最後の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "　第1次世界大戦最中のスパイ暗躍を題材にしたサスペンス・ムービー。エドガーはドイツ諜報員の正体を突き止めるため、アシェンデンと名を変えスイスへやってくる。そこには“妻”を名乗るエルサが待ち構えていた。他の仲間と共に、敵の諜報員らしき男を追いつめ死に追いやることに成功するが……。ラスト、アシェンデンとエルサの決断は見物。主演女優はヒッチコックの前作「三十九夜」に続いてマデリーン・キャロルが務めている。", "1938-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Duel at Diablo", "砦の29人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "アパッチ・インディアンと騎兵隊との戦いを主軸に描いた西部劇。妻を殺されたレンズバーグは、クリール砦に行く途中、一人の女を助けた。やがてレンズバーグはじめ騎兵隊は、アパッチ族が襲撃を狙っている輸送隊を守るため出撃するが……。妻を失った男の苦悩と憎しみ、アパッチ族の虜になった女のエピソードなど、異色の一編となっている。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON THE BEACH", "渚にて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "第三次世界大戦の核戦争による恐るべき未来を予測したセンセーショナルなSF大作。1964年、数ヵ月前に勃発した第三次世界大戦の核爆弾によって地球上はすべて放射能で覆われ、わずかに南半球の一部が残される。オーストラリアのメルボルンはそのわずかに残った一部だが、そこにも死の灰は近づきつつあった。そのメルボルン港に一隻のアメリカ原子力潜水艦が入港。ここで死を待つか、それとも死滅した祖国に戻るか。艦長はついに決意、町で出会った科学者やアメリカ人女性を伴い、出港するが……。恐るべき核戦争の実態をドラマ化した力作。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本心中／針生一郎・日本を丸ごと抱え込んでしまった男。", "日本心中／針生一郎・日本を丸ごと抱え込んでしまった男。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "現代美術家の大浦信行が、1925年生まれの異端的な美術・文芸評論家、針生一郎を主人公に据えたドキュメンタリー。韓国・光州の路地や雑踏の中を歩き回り、日本の自宅の書斎にこもって熱弁をふるう針生一郎。そんな彼の脳裏をよぎる様々な夢想や言説を通して、さ迷える戦後日本の50余年を見つめ直していく。記録映画の枠を超え、多様な風景、記憶、幻想がイマジネーション豊かな映像世界を織りなす問題作だ。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("草迷宮", "草迷宮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "フランスのプロデューサー、ブロンベルジュが製作したオムニバス映画「プライベート・コレクション」の一話として製作された中編映画。寺山の死後、彼の実験映画などを添えてようやく日本公開となった。泉鏡花の同名の原作の映画化だが、主人公・あきらの青年時代と少年時代とが交錯し、きわめてイマジネーション豊かではあるが同時に難解な作品ともなった。物語は青年のあきらが亡き母の歌っていた手毬唄の歌詞の由来を求めて旅を続けていくうちに、魔性の美少女の館に迷い込むという話が主軸になっいてる。手毬から球体のイメージをたたみかけるように一気に連鎖させていく幻想シーンが圧巻。寺山映画の極致。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIBERA", "リベラ　ナポリな女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "厳しい現実に立ち向かう女性たちの姿を快活に捉えたオムニバス。夫に捨てられた人妻が昔の恋人に振り回される「幸福への選択」、失踪した父の意外な秘密を息子に打ち明ける母親の姿を描いた「パパと呼ばないで」、夫の浮気現場を収めたビデオを売る人妻の物語「リベラ・裏の情事」の３話を収録。いずれも傷つきながら、それでもめげない女性像を軽妙に描き出している。", "1993-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("なにがなんでも為五郎", "なにがなんでも為五郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ハナ肇のヒット曲からヒントを得て、ジェームス三木と野村芳太郎が脚本を担当。ムショ暮らしを務め上げた為五郎は故郷・上州赤城へ帰って来たが、駅前に昔世話になった黒岩一家が総出でお出迎えと思いきや、誰もおらずズッコケる。別荘暮しをしている間にすっかり変わってしまった町のヤクザと、為五郎のスレ違いをユーモラスに描く。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ももドラ momo＋dra", "ももドラ momo＋dra", "", "", "http://image.eiga.k-img.com/images/movie/57738/315x210.jpg?3804", 93, "人気アイドルグループ「ももいろクローバーZ」が主演し、テレ朝動画で2011年12月に限定無料配信されたオムニバス形式のドラマを映画館で上映。「神様だって、」（玉井詩織主演）、「姉カレ」（佐々木彩夏主演）、「コトダマ」（有安杏華主演）、「色恋 colorful Love」（高城れに主演）、「お願い！DJ」（百田夏菜子主演）の全5話を1本化し、エンドロールには初公開となるメイキング映像が流れる。", "2012-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRST　DAUGHTER", "ホワイト・プリンセス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "若手女優ケイティ・ホームズ主演で贈る、全米でスマッシュヒットを記録したハートフル・コメディ。恋にオシャレに心躍らせる普通の女の子になりたいと、キャンパス・ライフをスタートさせる大統領の娘の奮闘を描く。普通の女の子の生活を夢みる、大統領のひとり娘サマンサ。彼女は家から離れた大学に入学し、新生活をスタートをさせるが……。", "2005-04-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE BITES OF THE APPLE", "ベニスの出来事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "気まじめな英国の旅行添乗員が、イタリアの片田舎で姿をくらました旅行客を追って、奇想天外な事件に巻き込まれていく。「0011ナポレオン・ソロ」のD・マッカラム主演でもあり、一応サスペンス仕立てだが、ヨーロッパの名所を次々と紹介する観光映画といった趣きの作品。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PIT ＆ THE PENDULUM", "ペンデュラム 悪魔のふりこ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "伝奇小説家E・A・ポーの『落し穴と振り子』の二度目の映画化。15世紀、魔女狩りが横行するスペインを舞台に、残忍な宗教裁判所所長が、美しき人妻マリアを拷問にかけていく様をスリリングに描く。所長に扮したL・ヘンリクセンの怪演が見もの。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("King-kong-eul deul-da", "キングコングを持ち上げる", "", "", "http://image.eiga.k-img.com/images/movie/54968/315x210.jpg?4592", 119, "弱小重量挙げチームの少女たちが元五輪メダリストのコーチと共に五輪金メダルを目指す姿を、実話をもとに描き韓国で大ヒットを記録したスポ根コメディ。1988年のソウル五輪重量挙げで銅メダルを獲得したジボンは、ひょんなことから田舎の女子中学校の重量挙げ部監督に就任。重量挙げに嫌気がさしていた彼だったが、少女たちの懸命な姿に心を打たれて本格的な指導を開始する。主人公ジボンに扮するのは人気ドラマ「オンエアー」のイ・ボムス。", "2009-10-21", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sahman", "牛は語らない　ボーダー", "", "", "http://image.eiga.k-img.com/images/movie/55008/315x210.jpg?4584", 82, "ソ連崩壊後のアルメニアとアゼルバイジャンの紛争を背景に、セリフやナレーションを一切排して牛の目線から人間の愚かさと悲しみを描いた異色のドラマ。とある村に連れられてきた1頭の牛。長引く戦火に疲れ果てた村人たちは、何も喋らない牛に憎悪の目を向け容赦なくムチを打つ。そんなある日、村に突然の終焉が訪れ……。監督・脚本を手掛けたのはアルメニアを代表する映画作家ハルチュン・ハチャトゥリアン。", "2009-10-20", "74", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'ANNEE DERNIERE A MARIENBAD", "去年マリエンバートで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "豪華で冷たいバロック風のホテルの城館で、富裕な客たちが情熱を欠いた社交に興じている。若い女に1人の見知らぬ男が近づいてきて、あなたとは1年前に会い、愛し合ったと語りかける。彼は単なる誘惑者なのか、気違いか？　男の話は真実なのか、男とともに、夫によって表象されているこの住みなれた世界を去ることができるのか、女は苦悩する……。監督レネと脚本ロブ＝グリエでさえ見解の食い違う、意識と無意識の問題を問う傑作。", "1964-05-31", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("倩女幽魂II之人間道／A CHINESE GHOST STORYII", "チャイニーズ・ゴースト・ストーリー２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "SFX伝奇ロマンの第２作。前作で美しい幽霊スー・シンを失った青年道士ツァイ・サン。彼は、スー・シンと瓜二つの娘、チー・フォンと出会い恋に落ちる。無実の罪で投獄されているチー・フォンの父をツァイは助けようとするが、邪悪なゴーストの妨害にあって……。ジョイ・ウォンの美しさは、やはりこのシリーズでこそ光り輝く。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LORCA", "ロルカ、暗殺の丘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "20世紀スペインを代表する偉大な作家であり詩人でもあったフェデリコ・ガルシア・ロルカ。1936年8月19日に暗殺された彼の死の謎に迫るサスペンス・ドラマ。内戦勃発直後にファシストによって銃殺されたロルカ。謎の多いこの事件の真相を青年ジャーナリストが解き明かしていく。「オーシャンズ12」のA・ガルシアがロルカ役を圧倒的な存在感で演じている。", "1997-01-01", "9,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lady in the Water", "レディ・イン・ザ・ウォーター", "", "", "http://image.eiga.k-img.com/images/movie/1488/315x210.jpg?4667", 110, "「シックス・センス」「サイン」のM・ナイト・シャマラン監督によるファンタジー。掃除や壊れた設備の修理などをし、静かに淡々とした日々を過ごしていたアパートの管理人のクリーブランドは、ある日、プールの下の通路でストーリーという名前の女性を見つける。彼女は精霊で、恐ろしい怪物に追われており、アパートのプールに身を潜めていたのだった……。主演はポール・ジアマッティとブライス・ダラス・ハワード。", "2006-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山中傅奇", "山中傅奇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 192, "時代は宋。死者の魂を鎮めるという仏経典の写経の依頼を受けた貧乏書生のホウは、向かった山中の砦で出会った妖艶な女性と無理矢理結婚させられるはめに。一見平穏な結婚生活が始まるが、彼女は経典を狙う妖怪だった……。アジアの巨匠、キン・フーが描く幻影的な伝奇ロマン。韓国で8ヶ月の長期ロケを敢行、完全版は192分の長編である。最近は監督としても活躍するシルヴィア・チャンが、清楚な亡霊役として出演している。", "1979-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Seven-Per-Cent Solution", "シャーロック・ホームズの素敵な挑戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "コカイン中毒で極度のスランプに陥っていた名探偵ホームズは、著名な心理学者フロイト博士の助けを借りて中毒の原因といえる心の傷を克服し、推理の冴えを取り戻す。時代のムードを生かした撮影、名優たちの優れたアンサンブルを得たミステリー・コメディ。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("СТАРОЕ　И　НОВОЕ／GENERALINAYA LINYA", "全線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "エイゼンシュテインが、処女作以来ずっと助手を務めてきたG・アレクサンドロフとの共同監督で描くドキュメンタリー風のドラマ。革命後も未だに生活が苦しいのは、党の言うことを聞かずに機械化、集団農場化の機会を無駄にしてきた村の無策にあることに気付いた女性マルファの献身的な行動を描いている。プロパガンダ色が強いものの、得意のモンタージュ技法を駆使して見せる佳編。", "1929-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Mechanic", "メカニック（2011）", "", "", "http://image.eiga.k-img.com/images/movie/55998/315x210.jpg?2960", 93, "1972年公開のチャールズ・ブロンソン主演の同名アクション・スリラーを、ジェイソン・ステイサム主演でリメイク。いかなる殺しも事故に見せかけて遂行する凄腕の暗殺者ビショップ（ステイサム）の親友マッケンナが殺害される。ビショップは親友の死を静観し、次の仕事にとりかかるが、マッケンナの息子スティーブが暗殺者として弟子入りを志願してくる。共演にベン・フォスター、ドナルド・サザーランド、トニー・ゴールドウィン。監督は「トゥームレイダー」のサイモン・ウェスト。", "2011-08-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUELQU’UN DERRIE｀RE LA PORTE", "扉の影に誰かいる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ブロンソンとアイアランドの夫妻が共演するサスペンスに満ちた犯罪映画。精神科医のローレンスは、記憶喪失で病院に運ばれてきた男を自宅へ連れ帰る。憎らしい妻の浮気相手を、この男に殺させるのが目的である。彼の中に虚構の記憶を作り上げ、そこに自分の性格の一部を移植していくローレンスだったが……。", "1971-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cyrus", "僕の大切な人と、そのクソガキ", "", "", "http://ecx.images-amazon.com/images/I/5125p9hL2yL._SL210_.jpg", 91, "ジョン・C・ライリー＆ジョナ・ヒルが共演のラブコメディ。7年前に離婚して以来、恋人がいない男ジョンは、元妻とその夫たちが開いたパーティで魅力的な女性モリーと出会い、デートの約束をする。誰もが驚く中、ジョンとモリーはデートを重ね親密になっていくが、モリーの家には21歳になるモリーの息子サイラスがいた……。製作総指揮はリドリー・スコット＆トニー・スコット。監督は新鋭ジェイ＆マークのデュプラス兄弟。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("3:10 to Yuma", "3時10分、決断のとき", "", "", "http://image.eiga.k-img.com/images/movie/34198/315x210.jpg?1554", 122, "エルモア・レナード原作の傑作西部劇「決断の3時10分」（1957）を、ラッセル・クロウ＆クリスチャン・ベール主演、「ウォーク・ザ・ライン／君につづく道」のジェームズ・マンゴールド監督でリメイク。困窮した生活から抜け出すために、悪名高い強盗のベン（クロウ）を、3日後の3時10分発の汽車に乗せるまで護送をすることになった牧場主ダン（ベール）の決断と運命が描かれる。共演はピーター・フォンダ、グレチェン・モル。", "2009-08-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KUNG　POW！　ENTER　THE　FIST", "クン・パオ！ 燃えよ鉄拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "カンフー好きのスティーヴ・オーデカーク監督が手掛けた超おバカ映画。1976年の無名のカラテ映画の権利を買い取り、監督自身がデジタル合成で出演。特殊効果を駆使したギャグ満載の珍作だ。生まれて間もなく両親を悪のカンフーマスター、イテテ師に殺されてしまった赤ん坊。荒野のネズミに育てられ、たくましい青年に成長した彼は、タン師匠の道場で超人的な猛特訓を積み重ね、因縁のイテテ師との宿命的な闘いに挑む。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モテキ", "モテキ", "", "", "http://image.eiga.k-img.com/images/movie/56148/315x210.jpg?2492", 118, "三十路前のモテない男・藤本幸世が、ある日突然、モテはじめたことから起こる騒動を描き、2010年にはTVドラマ化もされた人気コミックを実写映画化。主演はドラマ版に続き森山未來。ドラマ版から1年後を舞台に、原作者・久保ミツロウによるオリジナルストーリーが展開される。派遣社員を卒業し、ニュースサイトのライターとして正社員になった幸世に、ある日突然「第2のモテキ」が到来。新たに目の前に現れた女性たちと過去の女性たちとの間で揺れ動く幸世は……。幸世を取り巻く新たなヒロインたちを演じるのは長澤まさみ、麻生久美子、仲里依紗、真木よう子。監督はドラマ版の演出も手がけた大根仁。", "2011-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自由戀愛", "自由戀愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "第9回島清恋愛文学賞を受賞した岩井志麻子の同名小説を映像化。激動の大正初期、“職業婦人”という言葉が生まれ、女性が新しい生き方を求めた時代の人間ドラマ。正反対の魅力を持つ二人の女性が執念深い愛憎劇を繰り広げながら、一人の女性として自立していく姿を勇ましく描いている。夫・優一郎と幸せに暮らす明子は、離婚した同級生の清子を憐れに思い、夫の会社の事務職を紹介。しかし優一郎と清子が恋仲になり……。", "2005-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЗАСТАВА ИЛЬИЧА／I AM TWENTY", "私は20歳〈はたち〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 198, "1960年代初頭のモスクワを舞台に、人生で一番輝く時を生きる若者たちの心模様を描いた青春映画。その内容が当時のソ連首相フルシチョフの怒りを買い、大幅に修正を命令され、公開も1965年まで延期されたいわくつきの作品。ペレストロイカを迎えるにあたり、監督自らがオリジナルを復元し、1990年に完全版が完成、1995年に日本で初めて公開された。兵役から帰ってきたセリョージャは、友人たちとともに自由な日々を過ごすうちに、自分が何を求めているかを悩み始める。そんなある日、彼は名士の娘アーニャと恋に落ちる。夜露に濡れる白夜の町並みやメーデーのパレード・シーンなど、繊細かつ大胆なモノクロの映像が印象深い。アンドレイ・タルコフスキーやアンドレイ・ミハルコフ・コンチャロフスキーらが特別出演している。", "1962-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GHOST CHASE", "ゴースト・チェイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ウォーレンとフレッドは資金難に苦しみながらも、ホラー映画作りに毎日いそしんでいる。そんな折、21歳の誕生日を迎えたウォーレンが祖父の遺産を相続することになった。二人が喜んだのもつかの間、怪しげな古い時計を含むガラクタの遺品でいっぱいのスーツケース一つだけ。がっかりする彼らだったがある夜、遺品の時計が夜中の12時を差したその時、フレッドの前にウォーレンの祖父の執事ルイスが幽霊となって現れた。フレッドはその夜の出来事を脚本に書くのだったが……。ハリウッドを舞台に、遺産相続が引き起こす奇想天外なファンタジー・アドベンチャー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fighting", "ファイティング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("頣和園", "天安門、恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/53698/315x210.jpg?2763", 140, "1987年、学生たちの間に自由と民主化を求める声が高まる中、北京の大学で出会ったユー・ホンとチョウ・ウェイ。しかし、89年6月の天安門事件を境に2人は離ればなれになってしまう……。中国社会が激動した80年代末から2000年代初頭を背景に、恋人たちの出会いやすれ違いを描く青春ドラマ。天安門事件を学生の視点から描き、過激な性描写もあることから中国国内では上映禁止になったが、2006年カンヌ映画祭のコンペティション部門に出品され、評価された。", "2008-07-26", "8,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Elle s'appelait Sarah", "サラの鍵", "", "", "http://image.eiga.k-img.com/images/movie/56118/315x210.jpg?3018", 111, "タチアナ・ド・ロネの同名ベストセラー小説を、クリスティン・スコット・トーマス主演で映画化。1942年のフランス、パリ。ユダヤ人の迫害が過激化するある日、幼い弟を納戸に隠したサラは、納戸の鍵を手にしたまま収容所へ送られてしまう。そして現代。アメリカ人ジャーナリストのジュリアは、ユダヤ人迫害事件を取材するうちに、あるユダヤ人家族の悲劇を知り……。第23回東京国際映画祭で監督賞と観客賞を受賞。", "2011-12-17", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Captains Courageous", "我は海の子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "R・キプリングの小説を映画化した感動作。学校で問題ばかり起こしている富豪の息子ハーヴェイは、父によってイギリスに留学させられることになる。船中でもわがまま放題の彼は誤って海に転落し、漁師マニュエルに助けられる。人望の厚いマニュエルは、優しく人としての道を説き、ハーヴェイは次第に改心していった。そんなある日、マニュエルが事故で悲惨な死を遂げる。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANDY，THE ALL AMERICAN GIRL", "スーパーカー・フェラーリ青春の暴走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "車泥棒を職業とし、スーパーカーを次々と盗んでいく少女と、彼女を裁判で弁護することになった弁護士の物語。フェラーリ・ディノ、ポルシェなどのスーパーカーが登場する。「キリング・フィールド」の新聞記者役S・ウォーターストンが弁護士を演じている。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUBLE NICKELS", "シャドー55", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "二人のはみだし刑事がひょんなことから首を突っ込んだサイド・ビジネス。しかし気付いてみると彼らはヤクザの片棒をかつがされており、追われる身になっていた。自分たちの潔白を証明するために、ヤクザの黒幕の追及をはじめるが……。バディ・ムービーの佳作。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRONACA DI UN AMORE", "愛と殺意", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "M・アントニオーニ監督の長編デビュー作。巨大財閥の総裁から調査依頼を受けた私立探偵のカルリーニ。依頼の内容は、新しい妻パオラの過去を調べてほしいというものだった。さっそくパオラの故郷であるフェラーラへ飛んだカルリーニは、そこで意外な事実を知る……。一人の女へと接近する探偵の姿をハードボイルド・タッチで描きながら、後に続く“愛の不毛”のテーマをも匂わせた演出に注目。", "1950-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRANNIGAN", "ブラニガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ウェイン晩年の刑事もの。シカゴ警察のブラニガン警部補は、起訴まで持ち込んで逃げられた悪党を追って、ロンドンに乗り込み大活躍。“強いアメリカ”の象徴ウェインとイギリス人のやりとりの妙とともに、パブでの乱闘シーンなど、西部劇さながらの見せ場もタップリ堪能できる。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fragment フラグメント", "Fragment フラグメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ひとりの若き僧侶の成長を通し、9・11後の世界の変化をとらえたドキュメンタリー。僧侶となって実家の寺を継ぐため、タレント活動を休止した井上実直。同時多発テロから半年後のニューヨークを訪れた実直は、そこでグラウンドゼロを目の当たりにし、帰国後に“修法師”となることを決意する。実直からその話を聞いた友人・佐々木誠監督は、過酷な“日蓮宗100日大荒行”に入る実直をカメラで追いはじめる。やがて修法師となった実直は、再びグラウンドゼロへと向かう。", "2006-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の診察室", "夜の診察室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "マンションの一室で医学博士である父が開業している性相談室を手伝っている、純情で可憐だが人一倍好奇心の強い女子大生。彼女がポルノ作家と自称する青年と恋に落ちるというコメディ・タッチの作品。松坂慶子がトンでる演技を披露する。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VOULEZ−VOUS DANSER AVEC MOI？", "気分を出してもう一度", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "K・ローズのミステリー・ノベル『金髪は踊り乍ら死んだ』をもとにした作品。B・バルドーがお色気たっぷりの探偵に扮して、新婚早々の男が巻き込まれた殺人事件の謎を、あざやかな推理で解いていく。洒落たムードが漂う一編。", "1959-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダーW（ダブル） FOREVER A to Z 運命のガイアメモリ", "仮面ライダーW（ダブル） FOREVER A to Z 運命のガイアメモリ", "", "", "http://image.eiga.k-img.com/images/movie/55538/315x210.jpg?1374", 0, "架空の街・風都を舞台に、“ドーパント”と呼ばれる異形の超人たちと戦いながら難事件を解決していく「仮面ライダーW」の3D版。AからZまである26個の次世代ガイアメモリが、突然町にばらまかれる。そのころ、凶悪なテロリスト集団「NEVER」の大道克己は“E”のメモリを手に入れ、町を支配しようとしていた。 “2人で1人の探偵”こと左翔太郎とフィリップが変身する仮面ライダーWは、国際特務調査機関員・マリアの協力を得て、町の危機を救うべく出動する。", "2010-08-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Music of the Heart", "ミュージック・オブ・ハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "　1980年、ロベルタはニューヨーク、イースト・ハーレムの小学校でバイオリン教室の教員を務めることに。彼女は人種差別にも負けることなく、子供たちに音楽の素晴らしさを教える。それから10年後、ロベルタの教室が財政的事情から存続の危機にさらされる。そこで彼女は救済コンサートを開く決意をするが……。音楽教師ロベルタ・ガスパーリと子供たちの13年の軌跡を綴る感動作。ロベルタの活動を記録したドキュメンタリー映画に感激したというウェス・クレイブン監督がこれを映画化。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏の妹", "夏の妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "本土復帰にわきかえる沖縄を舞台に、一人の少女の恋と青春、さらに彼女の視点を通して、本土と沖縄の関係をも浮彫りにする。沖縄の美しい自然を背景に、本土と沖縄の戦中戦後史を貫く愛と憎しみと怨念が激しく葛藤する。ヒロインを演じる当時14歳の栗田ひろみの愛らしさが評判となった。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SNOW WHITE AND THE SEVEN DWARFS", "白雪姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ディズニー・アニメの最高傑作として時代を越えた支持を世界中から受けている名作中の名作。映画史上初の長編カラー・アニメーション作品ながら、その完成度はほとんど非の打ちどころがない。物語はおなじみの白雪姫と７人の小人が活躍するグリムのおとぎ話。森の女王が自分より美しい白雪姫に嫉妬して殺そうとする。森の中へ逃げた白雪姫は７人の小人に助けられるが、女王は老婆に化けて毒りんごを白雪姫に与える……。ディズニーは小人たちと姫の交流をポイントに物語をふくらませ、７人の小人それぞれにはっきりとしたキャラクターを決めて、ディズニー映画らしいにぎやかなテンポを生み出した。また姫や王女の登場する場面の半分以上は実際に俳優を使って撮影し、その動作を分解して絵を描き込んでいくという画期的手法を採用し、人物たちの息をのむほどになめらかな動きを表現してみせた。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初めての旅", "初めての旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "貧しい家に育ち、すさんだ生活を送っていた尾根勝と、きちんとした家に育ちながらどこか空しくやるせない日々を過ごす西村純一。偶然出会った二人は、どちらからともなく停めてあったスポーツカーに乗り込むと、そのまま旅に出ていく……。“新しいフィーリング”をキーワードに、森谷司郎が青春の奥深い心理を描く和製ロード・ムービー。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEMORIES OF ME", "メモリーズ・オブ・ミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "腕はピカイチの心臓外科医が、自身も心臓発作に襲われ、人生の意味を再確認するという心温まる人間ドラマ。B・クリスタルが製作・主演の二役を務め、監督は「ラブinニューヨーク」などで知られる俳優、H・ウィンクラーが務める。G・ドルリューによる音楽も心に染みる一編。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("杉の子たちの50年", "杉の子たちの50年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "第二次世界大戦中、40万人を上回る規模で行われた学童疎開の事実を体験者や関係者への取材、現地ロケにより本格的に記録したドキュメンタリー。疎開地への訪問に始まり、外国でも行われた疎開の実態、疎開生活、そして疎開地での事件や戦後に残したものなどを綴っていく。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コント55号 俺は忍者の孫の孫", "コント55号 俺は忍者の孫の孫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "山田風太郎の奇想天外な小説をベースに作られた、ハチャメチャなドタバタ喜劇の快作。欽ちゃんと二郎さんのコント55号コンビが今回も、大いに笑わせてくれる。甲賀忍者の末裔・甲賀二郎と伊賀忍者の末裔・伊賀欽一が出会い、対決の末、二郎のイカサマ忍法に欽一は敗れる。その後、彼らは２派の政治家に利用され、二郎は選挙に立候補、欽一はそれを妨害するが……。DVDは「コント55号結成40周年記念ムービーBOX」に収録。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILENT RUNNING", "サイレント・ランニング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "SFXの第一人者、D・トランブルの監督デビュー作。20世紀末、地球上の植物は全滅し、わずかに残された木々や草花は、宇宙船のドームで育てられていた。だがある日、乗組員に帰還命令が下され、ドームの爆発が指示された。乗組員の一人ローウェルは、植物を守るため３人の同僚を殺し、小型ロボットとともに土星に向かう。後半は、一人の人間と、もの言わぬロボットたちによる淡々とした構成だが、ロボットたちの圧倒的な“生命感”が生命の意味を強烈に問いかけてくる。切り離されたドームが、宇宙の深淵に向けて“静かに潜航”していくラストは、人類に残されたかすかな希望なのである。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORT YUMA GOLD", "さいはての用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "C・J・パジェット監督＆G・ジェンマのコンビによる正調マカロニ・ウエスタン。南北戦争末期のアメリカ。北軍の捕虜となった南軍の将校ゲーリーは、土地に明るいことからユマ砦まで北軍の伝達係の道案内をすることになる。だがその途中、敵の攻撃を受けたどさくさに紛れ、ゲーリーは伝達書を持って逃亡を図る……。DVDは「マカロニウエスタン・コレクション ジュリアーノ・ジェンマ ボックス」に収録。", "1967-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剣・花・煙雨紅南／TO KILL WITH INTRIGUE", "成龍拳", "", "", "http://ecx.images-amazon.com/images/I/51R7whmSfhL._SL210_.jpg", 107, "現代アクションに転向する以前のジャッキー・チェンが主演した、カンフー・アクションものの１本。両親を盗賊団“花蜂党”に殺された小雷。彼は賊の女首領・丁を敵視するが、やがて真の敵は親友の金川であることを知る……。女首領役はキン・フー監督作品のヒロインでおなじみのシー・フン。DVDタイトルは「ジャッキー・チェン／成龍拳」。", "1977-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I LOVE YOU TO DEATH", "殺したいほどアイ・ラブ・ユー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ピザ屋を経営するジョーイ。彼は妻のロザリーの前では、働き者の模範亭主。ところが裏では浮気のし放題だった。ロザリーはそれを知ってしまい、母と店で働くディーボを巻き込んでジョーイを殺そうと案を練る。主演のK・クラインの好演が光る、L・カスダン一流のコメディ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONEY TRAIN", "マネートレイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ジョンとチャーリーはニューヨーク地下鉄警察のおとり捜査官。幼いころから一つの家で育った彼らは、抜群のコンビネーションで日々多発する地下鉄犯罪に挑んでいる。ある日、二人の間にセクシーな女性捜査官グレースが配任されることになった。そんな時、事件が起きる。地下鉄の売上金強盗が連続して発生し、ジョンとチャーリーは犯人を追い詰めるのだったが……。「ハード・プレイ」でも顔を合わせたスナイプスとハレルソンが再共演。地下鉄の売上金を夜ごと徴収して回る“マネートレイン”をめぐってハイ・スピードなアクションが繰り広げられる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("稲妻ルーシー", "稲妻ルーシー", "", "", "http://ecx.images-amazon.com/images/I/61YdonyBbkL._SL210_.jpg", 70, "イタズラ好きな女性ルーシーが巻き起こす騒動を、「リング」シリーズの佐藤仁美主演で描いたスラップスティックコメディ。ある日、ルーシーはタレントのスカウトマン、フランキーに声をかけられるが、交番に駆け込んでストーカーに狙われていると訴えたため、フランキーは逮捕されてしまう。しかしタレントにもなってみたいルーシーは、フランキーの名刺を頼りにタレント事務所を訪れ……。名監督と新人監督が作品を競いあう「ワラ（＾O＾）番長」シリーズの1本。", "2004-04-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A SIMPLE PLAN", "シンプル・プラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "S・スミスの同名ベストセラー小説に基づくサスペンスフルな人間ドラマ。舞台は雪深い田舎町。身重の妻を抱えるハンクと兄ジェイコブ、その悪友ルーが墜落機を発見。機内には440万ドルもの大金が入っていた。最初は着服に反対していたハンクだが、他の二人に言いくるめられ、結局は中心になって慎重に計画を練る。だが３人の足並はそろわず、さらにハンクの妻も絡んできたことから計画は思わぬ方向に向かい……。“簡単な計画（シンプル・プラン）”のはずが、殺人に仲間割れ、新たな敵の出現と、大事件に発展していくスリリングな展開。奇才S・ライミが人間ドラマをていねいに描き出し、留まるところを知らない人間の欲望を浮き彫りにする。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キアロスタミとの一週間", "キアロスタミとの一週間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "日本人クルーが「風が吹くまま」を撮影中のアッバス・キアロスタミ監督に密着取材したドキュメンタリー。わずか10人ほどのスタッフを率いて、台本なしの撮影を淡々と進めていくキアロスタミ。彼らとロケ地の村人たちが自然に溶け合う様子を捉えながら、世界的な巨匠の創作の秘密に迫った貴重な作品だ。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心臓抜き", "心臓抜き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "フリーの駆け出し記者名雪は、ある日、街のカフェテラスで心臓発作により死んだ男を発見する。ところが数日後、友人の婚約者として紹介された男は、名雪が見つけた死体とそっくりだった。名雪はその男の身辺調査を開始するが、何者かが彼を妨害する。日常の中に忍び込む静かな異常を描く新しいサスペンス。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chacun Son Cinema", "それぞれのシネマ", "", "", "http://image.eiga.k-img.com/images/movie/53688/315x210.jpg?2333", 118, "2007年、60回目を迎えたカンヌ映画祭の開催を記念し、世界中の巨匠がそれぞれ“映画館”を題材に3分間の短編を製作したオムニバス。", "2008-05-17", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beastly", "ビーストリー", "", "", "http://image.eiga.k-img.com/images/movie/55288/315x210.jpg?2095", 86, "「美女と野獣」をモチーフに魔法で容姿を変えられた高校生が真実の愛を見つけていく姿を描いたラブストーリー。17歳の高校生カイルは、優れた容姿と校内での自身の人気ぶりに鼻を高くし、わがまま放題に振る舞っていた。そんなカイルに教訓を与えるため、魔女と噂されていたケンドラが魔法をかけ、自慢の容姿を醜く変えてしまう。カイルが元の姿に戻るためには、外見ではなく内面を見て愛してくれる人を1年以内に見つけなければならず……。主演は「ハイスクール・ミュージカル」のバネッサ・ハジェンズと「アイ・アム・ナンバー4」のアレックス・ペティファー。魔女ケンドラ役にメアリー＝ケイト・オルセン。", "2012-02-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ORIGINAL SIN", "チャールトン・ヘストン キング・マフィア／偽りの報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "幸せな家庭生活を送っていたジョンとシャロン、それに４歳の息子。だがある日突然、息子が何者かに誘拐されてしまう。ジョンは実父でありマフィアのドンであるルイス・マンシーニを訪ねるが……。大作スターとして知られるヘストンが久しぶりに、しかも難役に挑戦しているのが話題の作品。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Winter's Bone", "ウィンターズ・ボーン", "", "", "http://image.eiga.k-img.com/images/movie/56058/315x210.jpg?1384", 100, "2010年サンダンス映画祭でグランプリを受賞し、第83回アカデミー賞で作品賞、主演女優賞ほか4部門にノミネートされたヒューマンドラマ。米ミズーリ州南部・オザーク山脈の村に住む17歳の少女リーは、心を病んだ母親に代わり幼い弟と妹の世話をして暮らしていた。しかしある日、とうの昔に家を出て逮捕された父親が自宅と土地を保釈金の担保にして失踪。このまま裁判に出廷しなければ家を没収されてしまう。やむを得ずリーは自ら父親捜しに乗り出すのだが……。", "2011-10-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blame It on the Bellboy", "ベルボーイ狂騒曲／ベニスで死にそ～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "「ブラス！」のマーク・ハーマン監督の長編デビュー作。水の都ベニスを舞台に、ホテルのベルボーイが引き起こすドタバタ騒動を描いたコメディ。オートン、ホートン、ロートンという、名前の似通った3人の男たちが、偶然同じ日、同じホテルにチェック・インする。彼らの職業は不動産屋、市長、殺し屋とバラバラ。英語が苦手なベルボーイが彼らの名前を取り違え、それぞれに別のメッセージを渡してしまったことから大騒ぎになる。", "", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シンクロニシティ", "シンクロニシティ", "", "", "http://image.eiga.k-img.com/images/movie/56398/315x210.jpg?3073", 80, "画家を目指す岡崎は就職意欲もなく、母親や複数の女性に金銭を無心する生活を送っていた。美術学校で知り合った緑子とも真剣に付き合うでもなく、ダラダラとした関係が続いていたが、それでも緑子は岡崎のことをそれなりに愛していた。そんな毎日が続くと思われていたある日、岡崎の生活に突如として異変が起こる。世界的な経済不況に見舞われた現代社会で、モラトリアムに生きる若者の姿をリアルに描いたドラマ。", "2011-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SZEGE´NYLEGE´NYEK", "密告の砦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ハンガリー映画界を代表する鬼才M・ヤンチョーが描く人間ドラマ。ハンガリー人将校たちが巧妙に仕組んだ“密告”の罠にはまり、次々に倒れていく義賊集団のゲリラたちの、抵抗と悲痛な運命を、史実に基づいて描いている。軍によって砦に数百人の農民たちが集められ、かつての義賊団のゲリラ狩りが行われる。", "1966-01-01", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UTOPIA", "ユートピア", "", "", "http://ecx.images-amazon.com/images/I/31RTZQNY0FL._SL210_.jpg", 101, "予知能力を持つ青年が、夢の中で危険にさらされた女性を救うために決死の行動に出るというサイコ・スリラー。フラッシュフォワード（＝予知のイメージ）とフラッシュバックを盛り込んだダークトーンの映像は臨場感に満ち、すべての主要登場人物がある種の“悲しみ”を宿している点も印象深い。予知能力者のグループ“ユートピア”で育った青年アドリアン。その特殊能力で他人を救うことができず苦悩する彼だったが、カルト的なゲリラに所属する女性アンヘラの危機を察知し、一味のアジトに潜入していく。", "2004-10-30", "9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBERTE，CE SOIR", "ロベルトは今夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "作家、エッセイスト、画家など、マルチグラウンドで活躍中のクロソフスキーが、自らの代表作『歓待の掟』から第１部「ロベルトは今夜」第２部「ナントの勅令破棄」を脚色。自分の妻ドゥニーズとともに夫婦で主演した、退廃と官能に彩られた、究極のプライベート・フィルム。60歳を過ぎた神学教授オクターブは、自分の若妻ロベルトを、堕落した女に仕立て上げることに異常な快楽を感じていた。妻を他の男に与え、それを覗き見て恍惚とするオクターブ。ロベルトは、初めは夫の願望を受け入れ、淫らなセックスにふけるが、次第にそんな生活に耐えられなくなり、夫を殺す決心をする。ロベルトの胸をまさぐる手、彼女を背後から抱きしめる鎧の男などなど、シュールでエロティックなシーンが鮮烈のきわみ。", "1977-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女地獄・森は濡れた", "女地獄・森は濡れた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "性による悦楽の世界を追求する作家・神代辰巳が、避けて通ることのできないサディズムの世界に挑んだ野心作。原作はマルキ・ド・サドの『ジュスティーヌ』で、舞台を大正の米騒動の騒乱期に移している。殺人の濡れ衣を着せられ、さまよい歩く幸子は洋装の女に森深くのホテルに連れて行かれる。そこは女とその夫の退廃と悪徳に満ちた性と暴力の快楽殿であった。ほとばしる血と精液の中、幸子だけは快楽を拒否するのであった。神代はこれまでにない様式的な画面構成を用い、一方では当時の流行歌を彼独自の方法で使うなど、意欲的。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子連れ狼・その小さき手に", "子連れ狼・その小さき手に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "柳生一族の画策により妻を殺され、公儀介錯人の地位も追われた主人公、拝一刀が復讐を誓い、息子の大五郎を連れて旅に出る……。このおなじみの物語を、原作者、小池一夫の強い希望により、活劇調の前シリーズとはうって変わって、親子の情愛や絆を前面に押し出して描いた本格派時代劇。かつてのトレード・マークであった“乳母車”は登場せず、アクションも豪快さは消えたものの、情感豊かな好作に仕上がった。ラスト、波打ち際で繰り広げられる主人公と宿敵、柳生烈堂との対決は、緊張感にあふれ見ごたえがある。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISTRIAL", "ミストライアル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「ロング・キス・グッドナイト」の監督レニー・ハーリン、主演ジーナ・デイヴィスが製作総指揮に当たった法廷サスペンス。警官殺しの事件を担当する捜査官ドナヒューは、逃げようとする容疑者エディを格闘の末に逮捕するが、現場に居合わせたエディの妻と弟が彼の弾に当たって死んでしまう。エディは無実を主張し、マスコミも彼に同情的だった。そして当然のように陪審員は無罪の判決を下す。その瞬間、ドナヒューは銃を手にして立ち上がり、偽善と虚飾に彩られた裁判所で真実を暴こうとするのだった……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Horror of Dracula", "吸血鬼ドラキュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "英国ハマー・プロが描いた、C・リー主演による“ドラキュラ映画”の記念すべき第１作。カルパチア山中のある屋敷に図書係として雇われた英国青年ハーカー。彼はそこで、屋敷の主人とメイドが恐るべき吸血鬼であることに気付く。が時すでに遅く、彼もまた血を吸われて生気を失ってしまった。次に伯爵は、ハーカーの新妻と姉を狙って遥かイギリスに移住して来る。悪魔学に詳しい医師ヴァン・ヘルシングは、十字架とニンニクと杭で吸血鬼に立ち向かうのだが……。それまで米ユニバーサル映画の専売だったドラキュラものを初めて本国で、しかもカラーで映画化。口の周りを血まみれにしたリーのショッキングな演技が受け、世界中で大ヒットした作品である。監督は前作「フランケンシュタインの逆襲」で名を上げた、ハマー・ホラーの中心人物T・フィッシャー。ちなみにビデオはアメリカ公開版である。", "1958-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("なつかしき笛や太鼓", "なつかしき笛や太鼓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "32年間松竹に在籍し作品を発表してきた木下惠介の、松竹退社後第１作。松竹を去ったあとは木下プロを主宰し、TVシリーズを手掛けてきた彼の３年ぶりの映画である。内容は、瀬戸内海の貧しい島で生まれ育った少年少女たちが、バレーボールの練習に打ち込み、ついには大会で優勝する、というもの。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRACULA A.D.72", "ドラキュラ72", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "現代のロンドンにドラキュラ伯爵が現れる、という奇抜な筋立てによる、英ハマー・プロの“ドラキュラ映画”最終第６作。遊び半分に行われた、若者たちの悪魔復活の儀式によってドラキュラが現代によみがえった。彼が狙うのは宿敵ヴァン・ヘルシング教授の孫とその娘ジェシカ。かくて吸血鬼対人間の最後の戦いが始まった……。サイケな若者風俗とドラキュラもののギャップが楽しい珍作。", "1972-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Deer Hunter", "ディア・ハンター", "", "", "http://ecx.images-amazon.com/images/I/51XPlYOS21L._SL210_.jpg", 183, "　60年代末、ペンシルバニアの製鋼所で働くマイケル、ニック、スティーブンたちは休日になると鹿狩りを楽しんでいた。やがてマイケルたちは徴兵され、ベトナムへ。彼らは戦場で再会するが、捕虜となり、残虐な拷問ゲームを強要される。マイケルの機転で脱出に成功するが、その後、ニックは行方不明に。マイケルは彼を捜すが……。男たちの命がけの友情、そして戦争の狂気を描いた衝撃作。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD CHASER", "マッドチェイサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "独裁者リンカーンが支配するトレードタウンに、たった一人残った警官ジョン・トラヴィスは、略奪集団スキャブスに襲われていた美女レイチェルを救う。二人は、苦しむ人々を安住の地へ送るために必要な“クリスタル”を探し、支配者たちに立ち向かう。破滅と暴力の世界を舞台にした近未来アクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REAL THE MOVIE", "レアル ザ・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“銀河系最強軍団”の異名を取るレアル・マドリード初の公認映画。ベッカムらスタープレイヤーを擁するチームの内部に肉迫したドキュメンタリー映像とドラマパートを絡ませ、ファン垂涎の異色の構成でチームの魅力を浮き彫りにする。レアル対バルセロナの一大決戦がクライマックスに待ち受ける中、東京、ニューヨーク、ベネズエラ、セネガル、マドリードを舞台にした、選手にまつわる複数のドラマが交差していく。", "2006-04-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アヴァロン", "アヴァロン", "", "", "http://image.eiga.k-img.com/images/movie/1268/315x210.jpg?3607", 106, "近未来。若者たちは仮想戦闘ゲーム“アヴァロン”に熱中していた。パーティーと呼ばれるプレイヤーの非合法集団が増える中、アッシュはパーティーを組まない最強の女戦士として注目されていたが、彼女もかつては無敵と謳われた“ウィザード”の一員だった。謎の解散を遂げた“ウィザード”の元メンバーと再会したアッシュは、リーダーのマーフィーがリセット不能の幻のフィールドに足を踏み入れ廃人になったことを知る。", "2001-01-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO−MINUTE WARNING", "パニック・イン・スタジアム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "10万人もの観客を収容したスタジアムに、ライフル銃を持った男が現れる。今まさに試合が終了しようとするラスト二分前、突然、観客に向かってライフルが発砲され、スタジアムはパニックの嵐と化す……。群衆心理の恐ろしさを描いた白熱編。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラトル指揮　ベルリンフィル3D“音楽の旅”", "ラトル指揮　ベルリンフィル3D“音楽の旅”", "", "", "http://image.eiga.k-img.com/images/movie/57038/315x210.jpg?3555", 105, "ベルリンフィルハーモニー管弦楽団の演奏を最新の3D技術で撮影。楽団の全面的な協力のもと、クレーンやレールなどの映画用の撮影機材も使用し、演奏中の演奏者にも肉薄したダイナミックな映像が収められている。サイモン・ラトルがタクトを振り、日本でも人気の高いマーラーの交響曲第1番「巨人」と、ラフマニノフの「交響的舞曲」の2曲を収録。", "2011-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンブルー", "ドラゴンブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "風水師の真佑子は上司の天堂の旧友の依頼で、龍の宮島へ行く。島では、若い女性が続けて行方不明になっていて、海魔の仕業と噂されていた。真佑子は、島で出会った探偵の竜崎と一緒に調査を始めるのだが……。人気プロレスラー武藤敬司が「光る女」(1987)以来、８年ぶりに映画出演。また、「プレデター」「ビートルジュース」などのSFXを担当したスティーヴ・ワンがクリーチャー・デザインを行っている。ビデオタイトルは「妖獣伝説　ドラゴンブルー」。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Legion", "レギオン", "", "", "http://image.eiga.k-img.com/images/movie/55258/315x210.jpg?4824", 100, "神VS人類の最終決戦を描いたSFアクションホラー。信仰心を失った人類に天罰を下すため、神は天使の軍団を地上へ派遣する。だが、大天使ミカエルだけは神の命に背き、人類の味方となり、アメリカ辺境にあるダイナーにたまたま居合わせた人間たちと共に天使の軍団と戦うことになる。出演はポール・ベタニー、デニス・クエイド、タイリース・ギブソン。監督は「ロスト・ワールド／ジュラシック・パーク」「シン・シティ」などの特殊効果を手掛け、本作が長編デビュー作となるスコット・スチュワート。", "2010-05-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴールドラッシュ", "ゴールドラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1974年、一獲千金を夢みて上京した北海道のロック大好き青年、村木純一。東京で出会った銀行マン・大沢とともに当時は誰も見向きもしなかった原宿を“若者がワンサカ押しかける街にしてやろう”と自分たちの店を持つべく奮闘を始める……。“原宿を作った男”といわれる実在の人物、山崎真行をモデルにした原作を和泉聖治が、人気バンド、元ハウンドドッグの大友康平を主演に描く青春サクセス・ストーリー。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("首領〈ドン〉への道", "首領〈ドン〉への道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "昭和45年の連載開始以来、息の長い人気を博している村上和彦の劇画を映画化。激しい抗争と略謀の中で、極道のトップを目指して突き進む男の物語が、ドラマティックに繰り広げられる。大阪、島田組の組員、桜井は賭場を密告した敵対組織の幹事長を殺害。たちまち抗争勃発の危機が持ち上がる。だが桜井は命を賭けて共倒れの危機を阻止。関西の極道社会でのし上がっていくが、やがて反対勢力が動き出し……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONTROL", "コントロール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "レイ・リオッタとウィレム・デフォーという、個性派として鳴らす二人が競演したサイコ・サスペンス。レイ扮する冷徹な殺人犯が、死刑を免れる代わりに凶暴性を抑制する効果があるとされる新薬の実験台に。実験の最終段階として彼が社会に放たれたことから、トラブルが巻き起こる。製薬会社のエゴともいうべき思惑と、本人のみぞ知る薬の効果、死刑を免れたことを知った被害者の家族の暴走が絡み合うドラマは、意外な顛末に向かってニ転三転。最後まで隙のない構成が見事だ。凶悪犯のリーは死刑免除と引き換えに医学実験の参加を持ちかけられ、その申し出を了承。実験の結果、彼は社会に復帰する。だが、リーが処刑されなかったことに被害者家族が気づき……。", "2005-03-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mongol", "モンゴル", "", "", "http://image.eiga.k-img.com/images/movie/53478/315x210.jpg?2449", 125, "たった一代でモンゴル帝国を築き上げた英雄チンギス・ハーンの波乱に満ちた半生を壮大なスケールで映画化した、ドイツ・ロシア・カザフスタン・モンゴル合作による歴史スペクタクル。国内外で注目を集める浅野忠信が主演に抜擢され、全編モンゴル語の台詞で若き日のチンギス・ハーンに挑む。メガホンを取るのは「コーカサスの虜」のロシア人監督セルゲイ・ボドロフ。第80回アカデミー賞外国語映画賞ノミネート作品。", "2008-04-05", "7,51,24,57", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("素晴しき男性", "素晴しき男性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ミュージカル・ショーの演出家志望の青年と仲間で劇場の下積みの連中が、苦難の末、自分たちの力でミュージカル・ショーの公演を実現する。日本映画では難しいミュージカルに挑戦した井上梅次の意欲作だが、スタッフに恵まれず失敗に終わった。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shark Tale", "シャーク・テイル", "", "", "http://image.eiga.k-img.com/images/movie/1168/315x210.jpg?1235", 90, "海の生物たちの都リーフシティに暮らす、大きな夢を抱いた小さな魚のオスカー。ある日、オスカーは魚たちに恐れられているサメの仲間でありながらも、ベジタリアンで優しい性格のレニーと出会う。2匹は互いの夢のために協力するが……。「シュレック」シリーズのドリームワークスが送る海洋アドベンチャーアニメ。まさにオスカー級の俳優たちが勢揃いしている声優陣にも注目。", "2005-03-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EYE OF THE NEEDLE", "針の眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "第二次大戦末期、連合軍のヨーロッパ上陸作戦の内容を探るため、ヒトラーの命を帯びてイギリスに潜入したスパイ“針”を主人公にしたスパイ・アクション。監督のR・マーカンドはこの作品で認められ、「スター・ウォーズ／ジェダイの復讐」に起用された。", "1981-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE SILENCE EST D’OR", "沈黙は金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "軽妙洒脱な作品を生み続けてきたフランスの巨匠R・クレールが描く、古き良き時代のパリを舞台にしたコメディ。サイレント映画の監督に扮したM・シュヴァリエを中心に、笑い、人情の機微、恋の楽しさなどを詩情をたっぷりに織り込んだフランス映画らしい一編。1947年ブリュッセル国際映画祭映画大賞受賞。", "1946-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GoldenEye", "007 ゴールデンアイ", "", "", "http://ecx.images-amazon.com/images/I/51Nkdt-ky%2BL._SL210_.jpg", 0, "", "1995-12-16", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女優霊", "女優霊", "", "", "http://image.eiga.k-img.com/images/movie/37158/315x210.jpg?3058", 76, "未現像だったフィルムの発見をきっかけに、撮影所で起こり始めた怪奇な出来事を描いたホラー映画。新人監督の村井俊男は、デビュー作の準備に余念がない。ある晩、主演女優のカメラテストのラッシュを見ていると、途中からまったく別の映像がダブリ、一人の女優が映っているのに気づく。それ以後、主演の黒川ひとみの声に別の声がかぶさって聞こえたり、新人女優の村上沙織がセットから転落死したりと奇妙な事件が続出。未現像フィルムを昔テレビで見た記憶のある村井は、このフィルムが気になり、過去の事件を調べていくと……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼咬鬼／ENCOUNTER OF THE SPOOKY KIND II", "鬼喰う鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "香港映画界の重鎮、サモ・ハン・キンポーが製作総指揮・主演にと大活躍する香港映画お得意のキョンシー・ホラー・アクション。原題からも想像されるように、キンポーが1981年に製作した「鬼打鬼」の続編にあたるが、ストーリーはまったく関係なし。自分が死んだことを隠して盲目の母親の世話をする薄幸な女幽霊のシュホ。そんな彼女に同情する若者ポウは、彼女の役に立とうとするうちに恋に落ちてしまう……。ふんだんに盛り込まれたSFXシーン、摩訶不思議なキョンシーたちの暗躍と、香港映画らしいエンターテインメント作品になっている。キンポーの相手役に大抜擢されたジャニス・オンの美貌も見どころの一つ。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2 FAST 2 FURIOUS", "ワイルド・スピードX2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "全米で大ヒットを記録したカー・アクションの続編。陽光まばゆいマイアミで、スピード狂の主人公が豪快な市街地チェイスを演じる。ユーモラスで熱い友情のドラマも見ごたえあり。元警官の経験を買われ、潜入捜査に協力することになった公道レースの天才ドライバー、ブライアン。マネーロンダリング組織のボスの信用を勝ち得た彼は、汚れた金を運ぶことになり……。", "2003-08-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANCHE GLI AMGELI MANGIANO FAGIOLI", "くたばれカポネ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "1926年、不況に見舞われるアメリカ。その日暮らしのチンピラ、ソニーと元プロレスラーのチャーリーの二人はマフィアのボスに取り入って手下として雇われるが、やることなすことドジ続き。借金の取り立てに行った貧しい一家に、逆に差し入れをしたりする心優しい二人だったが、ついにボスから殺人を命じられて……。", "1973-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I am: SM Town Live World Tour in Madison Square Garden", "アイ・アム SMタウン・ライブ・ワールド・ツアー・イン・マディソン・スクエア・ガーデン（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-06-30", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Krótki Film o Zabijaniu", "殺人に関する短いフィルム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ポーランドの巨匠クシシュトフ・キエシロフスキー監督のTVシリーズ「デカローグ」第5話のロングバージョン。青年ヤツェックは、ある日衝動的にタクシー運転手を殺害する。彼の裁判を担当する弁護士ピョートルは、殺人のあった日に弁護士試験に合格していた。ドキュメンタリー監督として活躍してきたキエシロフスキーが、1人の男の殺人から死刑執行までを描いたドキュメンタリー・タッチの作品。", "", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHE’S HAVING A BABY", "結婚の条件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "シカゴに住む新婚ホヤホヤのカップル。ジェイクは修士課程を諦め、広告会社に就職した。妻のクリスティはすでにOLとしての実績がある。この二人が様々な困難に直面するさまを、J・ヒューズ特有のユーモアで綴っている。サントラもブライアン・フェリーをはじめ、豪華メンバーが参加。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("XII—1118", "XII—1118", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "若いダイバーたちと老婦人の不思議な交流を通じて描く、笑いと感動の冒険ファンタジー。フィリピンの美しいマリンリゾート、セブ島に一人の老婦人が訪れる。彼女が探しているのは、51年前に撃ち落とされた戦闘機“X II—1118”。その戦闘機の中には、彼女の夫が眠っているはずだったが……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Celine: Through the Eyes of the World", "セリーヌ　スルー・ザ・アイズ・オブ・ザ・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガラスのうさぎ", "ガラスのうさぎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "世代を超えて読み継がれるロングセラー小説を映画化した長編アニメーション。過酷な戦後を生き抜いた少女の姿が描かれる。終戦から長らく経ち、戦争が風化しつつある現代に、その悲惨さや平和の大切さを伝えてくれる。太平洋戦争後期、少女の敏子は東京の下町で家族と共に懸命に生きていた。ところが大空襲により、彼女は最愛の母と妹二人を失ってしまう。", "2005-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUMANJI", "ジュマンジ", "", "", "http://ecx.images-amazon.com/images/I/51C4kYhGqNL._SL210_.jpg", 103, "12歳のアランは、ニューハンプシャー州の小さな町で製靴工場を営む名家パリッシュ家の一人息子。ある日彼は工事現場の中から太鼓の音が聞こえてくるのに気付き、土の中から古い木箱を掘り出す。ふたを開けてみると中には“ジュマンジ”と書かれた不思議なゲーム盤が入っていた。このゲームは投げたダイスの目に応じて結果が文字となって現れ、さらに結果通りの現象が実際に起こるという。アランはガールフレンドのサラと一緒にゲームを始めるが、ダイスを振ったアランは自分が出した目の結果として、ゲーム盤の中のジャングルの世界へと吸い込まれ、サラの目の前から姿を消してしまった……。R・ウィリアムズが、子供の頃のままゲームの世界で大人になってしまったアラン役を演じるSFXアドベンチャー大作。なかでも屋敷がジュマンジの世界となり、ジャングルになってしまった部屋中で様々な動物たちが暴れ回る特撮シーンは必見。", "1996-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Il generale della Rovere", "ロベレ将軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "", "1959-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURIEL OU LE TEMPS D’UN RETOUR", "ミュリエル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "未亡人エレーヌは養子ベルナールと二人暮し。ベルナールはアルジェリア戦線で虐殺された娘ミュリエルの悲痛な思い出に打ちひしがれていた。一方、エレーヌは初恋の相手と再会する……。監督の永遠のテーマ“時間”に疲れた人間たちの日常を、徹底したリアリズムで鮮烈に映し出した秀作。", "1963-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・葛飾立志篇", "男はつらいよ・葛飾立志篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "寅さんが何を思い立ったか学問を志す、という一編。かつて想いを寄せた人の娘の面倒をみるうちに学問の大切さが身にしみてきた寅次郎。ちょうど“とらや”には大学に残って考古学を研究している女性が下宿することになったから、さあ大変。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いらっしゃいませ、患者さま。", "いらっしゃいませ、患者さま。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "借金苦に喘ぐ病院に、サービス業の権化である水商売仕掛け人が再生手術を施す……。風俗チックに改革されていく病院を描き、医療界が抱えるさまざまな問題点を明るく笑い飛ばしていく破天荒なコメディ。無気力院長役の渡部篤郎と“風俗界の救世主”に扮した大友康平の硬派な友情ドラマとしても楽しめる。閉鎖寸前の近馬記念病院。そこに風俗ビジネスに長けた恩地が急患として運ばれる。病院の実態を知った彼は立て直しに奔走するが……。", "2005-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEN WANTED MEN", "十人のならず者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "正義対悪という直球勝負の図式で展開する正統派西部劇アクション。ハリウッドでは50年代にこうした西部劇が量産されていたが、本作は中でもアウトローのキャラ作りに工夫が凝らされている。1860年のアリゾナのある町に“10人のならず者”と呼ばれる一団が暗躍、日々悪行を繰り返していた。スコット扮する主人公ジョンは見て見ぬ振りをしていたが、やがて立ち上がる時がくる。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Punch", "ワンドゥギ", "", "", "http://image.eiga.k-img.com/images/movie/57958/315x210.jpg?3524", 108, "「トキメキ☆成均館スキャンダル」のユ・アインと「チェイサー」のキム・ユンソク主演で、2011年韓国で530万人を動員するヒットを記録したヒューマンドラマ。障害をもつ父親と風変わりな叔父と3人で暮らす高校生のワンドゥク。家庭は貧しく勉強もできず、夢も希望ももてない上に、隣に住む担任教師のドンジュが生活に干渉してくることにうんざりしていた。ドンジュがいなくなることを切実に願うワンドゥクだったが、ある日、そんなドンジュから会ったことのない母親の消息を知らされ……。", "2012-04-28", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Elementarteilchen", "素粒子", "", "", "http://image.eiga.k-img.com/images/movie/33898/315x210.jpg?1261", 113, "ヨーロッパで大反響を呼んだミシェル・ウエルベックのベストセラー小説を映画化。親に捨てられた異父兄弟の姿を通し、現代社会が抱える様々な問題を浮き彫りにする。主人公ブルーノを熱演したモーリッツ・ブライブトロイは、本作でベルリン映画祭主演男優賞に輝いた。さらに、ブライブトロイとは「ラン・ローラ・ラン」以来の共演となるフランカ・ポテンテや「善き人のためのソナタ」のマルティナ・ゲデックらドイツ映画界を代表する俳優陣が集結。", "2007-03-24", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEADLY TAKEOVER", "テロリスト・ウォー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "テルアビブ郊外の科学兵器研究所で開発された細菌兵器“コンパウンドX”。未だに解毒剤が開発されていないこの兵器は、各国の脅威となりつつあった。この細菌兵器に目をつけた国際テロ集団が、研究所内に侵入。“コンパウンドX”の強奪を企てた。そんな中、危機をすばやく察知したハットフィールド軍曹は、ただ一人研究所に飛び込んでいく。ハットフィールドは世界を救えるか？ アメリカ軍、イスラエル軍の協力で実現したバトル・アクションや18回の爆破シーンなど、実に全編の85パーセントを占めるアクション・シーンは圧巻。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ライアーゲーム　ザ・ファイナルステージ", "ライアーゲーム　ザ・ファイナルステージ", "", "", "http://image.eiga.k-img.com/images/movie/54608/315x210.jpg?4616", 133, "甲斐谷忍による人気コミックが原作のTVドラマ「ライアーゲーム」の劇場版。主演は戸田恵梨香、松田翔太。謎の組織が主催する騙し合いのゲーム「ライアーゲーム」に巻き込まれた女子大生の直は、天才詐欺師・秋山の助けを借り、なんとか決勝戦まで勝ち残る。優勝賞金50億円がかかった「エデンの園ゲーム」に挑んだ2人だったが、決勝進出者の中には最強の刺客「プレイヤーX」が姿を潜めていた。", "2010-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君へ。", "君へ。", "", "", "http://image.eiga.k-img.com/images/movie/56998/315x210.jpg?4668", 94, "「仮面ライダーW」で人気の若手俳優・桐山漣の主演で、心に喪失感を抱きながらも成長していく青年の姿を描いたドラマ。幼なじみの小夜子と互いに恋心を寄せていた晃治だったが、中学生のある日、小夜子が突然消えてしまう。晃治は小夜子との思い出から離れるため東京の高校へ進学し、そのまま東京で大学から大学院へと進学していく。そうして月日が流れたある日、小夜子の父が亡くなり、晃治は葬儀に出席するため中学卒業以来となる故郷に戻ってくるが……。", "2011-09-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("絶唱", "絶唱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "日活時代は吉永小百合の陰に隠れがちだった和泉雅子が、ほか２作のヒロインに劣らぬ魅力を発揮する、３度映画化された純愛メロドラマの２作目。親の不理解、身分違いの恋、戦争に引き裂かれる愛、病気によるヒロインの死と悲愛もののパターンが集約。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARROW", "告発弁護人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "個性派K・スペイシーの初主演作。アメリカに実在した名弁護士の謎に満ちた私生活を描くサスペンス。1894年、シカゴ。ノースウエスタンの顧問弁護士クラレンス・ダローは信念を持って仕事に取り組むやり手だが、仕事に没頭するあまり、家庭は崩壊寸前だった。やがて彼は、不当な労働条件に対してストライキを決行した全米鉄道員組合とともに会社を告発。自らの立場に背いて会社と闘う姿は、人々の厚い信頼を得るが、彼にはある秘密があった……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Late Autumn", "レイトオータム", "", "", "http://image.eiga.k-img.com/images/movie/57708/315x210.jpg?3269", 113, "ドラマ「シークレット・ガーデン」「私の名前はキム・サンスン」などで人気のヒョンビンと「ラスト、コーション」のタン・ウェイが共演したラブストーリー。韓国の往年の名作「晩秋」（1966）を、舞台を米シアトルに置き換えてリメイクした。DVの夫を誤って死なせてしまい収監されていたアンナは、母親の訃報を受けて3日間だけ外出が許可される。葬儀に向かうためシアトル行きのバスに乗ったアンナは、寂しい女性たちに「エスコート・サービス」をしている男、フンに出会うが……。", "2012-02-04", "5,6,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("華岡青洲の妻", "華岡青洲の妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "世界最初の全身麻酔に成功した華岡青洲。だがその麻酔薬発明の裏には、青洲の妻と母との深い確執があった。青洲の愛を確かめるために、互いに争って実験台になろうとまでする女たちの凄まじいまでの自我を、増村は鬼気迫る演出で描き出す。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベンポスタ・子ども共和国", "ベンポスタ・子ども共和国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "スペインのオレンセ市郊外にある“ベンポスタ・子ども共和国”の人々の生活模様を記録したドキュメンタリー。国民約140人の共同体であるベンポスタ、その中の100人は６歳から18歳の子供である。そこの生活は学校と労働とサーカスの練習という３つの要素で成り立っていた。サーカスは国民全体が団員であり、“生きる喜びと博愛”を世界各国の人々に伝えていく……。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OKITE・やくざの詩〈うた〉", "OKITE・やくざの詩〈うた〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ベテラン俳優、松方弘樹が60歳にして初監督に挑んだ極道アクション。恩人のために、死を覚悟して戦いに挑むアウトローの生きざまを描く。加藤雅也と吉川晃司がバトルで見せる。新宿、歌舞伎町で生きるヤクザの寺島。ある日、敵対組織との抗争によって、彼の敬愛する組長が殺された。手打ちで抗争が収まるなか、納得のいかない寺島は大組織を相手に戦いを挑む。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("友情／Friendship", "友情／Friendship", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "三船敏郎の娘、三船美佳の主演デビュー作。父親の転勤で北海道から東京の中学校に転入した14歳のあゆみ。彼女は急性リンパ性白血病に冒されており、病状は悪化の一途をたどる。そんなあゆみを気遣う担任の野本先生は、クラスの面々に夏休みを利用した２泊３日の下田旅行を提案。治療で髪の毛が抜けてしまったあゆみは、尻込みしながら旅行に参加するが……。CNNニュースで世界に報じられた感動的な実話の映画化。残り少ない命のヒロインを励ますために、女子生徒を含むクラスメート全員が丸坊主になって彼女を迎えるシーンが涙を誘う。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHAKER RUN", "シェイカーラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "人間の抵抗力を奪う恐るべきウイルスを開発したクリスティーン。彼女はその管理を米国政府にゆだねるため、スタントマンのジャッドに運搬を依頼した。命知らずの彼は報酬欲しさにこれを快諾、自慢のトランザムを駆り目的地へ向かうのだが……。ニュージーランド産のB級サスペンス。", "1985-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("14歳", "14歳", "", "", "http://image.eiga.k-img.com/images/movie/33978/315x210.jpg?1326", 114, "「ある朝スウプは」でPFFアワード2004グランプリを受賞し注目を浴びた映像ユニット“群青いろ”の廣末哲万と高橋泉が、PFFスカラシップ作品として手掛けた群像ドラマ。監督・主演を廣末、脚本を高橋が務め、14歳という多感な時期を生きる少年少女たちと、過去の記憶を引きずったまま大人になったかつての14歳たちの葛藤を描き出す。共演は「ある朝スウプは」の並木愛枝、「ゆれる」の香川照之ほか。", "2007-05-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES KIDNAPPEURS", "キッドナッパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「ドーベルマン」のプロデューサー、E・ネーヴェと「シューティング・スター」の俊英G・ギットが手掛けた、イキのいい犯罪映画。金庫破りのプロ、アルマンは、ハッカーの恋人やスピード狂の弟、短気な詐欺師という仲間とともに大金強奪に挑戦。しかし金庫に金はなく、彼らはリトアニアの王子をそうとは知らず人質にとるハメになり……。南仏の陽光を背景にしたスリリングな展開は、1970年代のフィルム・ノワールを彷彿させる。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聞こゆるや", "聞こゆるや", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "演劇出身の山田武が、故郷の熊本を舞台にして撮り上げた初監督作。20年ぶりに帰省した男の脳裏に甦る青春の日々。初恋の甘美な思い出、純粋さゆえに湧き上がった怒り……。さまざまな思いの中で葛藤し、成長していく青年の姿を描き出す。衝撃的なラストが賛否両論を巻き起こした。高校生の慎一は、市電の中で見かける女子高生に手紙を送り、交際を始める。だが、とある出来事が、慎一の未来を狂わせることになり……。", "2005-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SKY HIGH", "スカイ・ハイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "正義を守るスーパーヒーローを養成する学校に通う、劣等生のティーンエイジャーの青春を描いた学園アクション・コメディ。主人公のパパにして、超有名なスーパーヒーローという役どころで「バックドラフト」のカート・ラッセルが登場。B級っぽい愉快なノリを、一層盛り上げてくれる。スーパーヒーローを養成する学校「スカイ・ハイ」。超有名なヒーローを両親に持つウィルは、一向に特殊なパワーに目覚めず……。", "2006-02-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNTIL THE END OF THE WORLD", "夢の涯てまでも", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 158, "近未来の1999年、滅亡の恐怖におののく世界。ベニスを発ったクレアが道中で何者かに追われる男トレバーと出会う。彼を追うクレアの旅はヨーロッパからロシアを経てアジアへ、そしてオーストラリアへと進む。トレバーは盲目の母の脳に送り込むために、世界中の映像を集めて旅していたのだった……。豪華なキャストによる世界各地でのロケや、最先端のハイビジョン映像を取り入れた手法が話題に。サントラで参加したU2のボノと監督ベンダースが出会った作品。", "1992-03-31", "76,1,7,2,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRIPES", "パラダイス・アーミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "軽い気持ちで陸軍に入ったジョンとオックス。ところが鬼軍曹に連日しごかれ、早くもうんざり。そんな２人がヨーロッパ駐留中に新兵器を持ち出したことから、思いがけず英雄となり……。ダメ男が活躍する軍隊コメディ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OGGI A ME，DOMANI A TE！", "野獣暁に死す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "恋人殺しの罪を着せられた男が出獄して４人の男を雇い、真犯人の強盗団のボスに復讐するまでを描くマカロニ・ウエスタン。仲代達矢が悪役で出演し、話題となった。主演のM・フォードはブレッド・ハルゼイの改名。劇場公開は日本語吹替版だった。", "1968-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AUBERGE　ESPAGNOLE", "スパニッシュ・アパートメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "スペインに留学したフランス人大学生が、ドイツやイタリアなど国籍のバラバラな若者６人との共同生活を通して、大人へと成長していく姿を綴る青春映画。「パリの確率」のC・クラピッシュ監督が、デジタルカメラを駆使して若者たちの群像とバルセロナの街並みを生き生きと映し出す。主人公の恋人を「アメリ」のA・トトゥが演じている。", "2001-01-01", "2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅雪姫", "修羅雪姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "夫と息子を殺された女が犯人の一人を殺して投獄され、無期懲役の判決を受ける。その娘・お雪は母親のうらみを晴らそうと、浮浪者部落の人々の情報をもとに次々と犯人たちを追いつめていく……。文明開化の鹿鳴館を舞台にした、梶芽衣子人気絶頂期の復讐譚。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桜蘭高校ホスト部", "桜蘭高校ホスト部", "", "", "http://image.eiga.k-img.com/images/movie/57108/315x210.jpg?1333", 0, "良家の子女が通う名門・桜蘭高校を舞台に、校内屈指の美男子が女子生徒をおもてなしする「ホスト部」で起こる騒動を描いた学園青春ストーリー。葉鳥ビスコの人気少女コミックを実写化した同名ドラマの映画化。男子と偽りホスト部員として活動するハルヒは、部長の環に密かに思いを寄せるようになっていた。そんなある日、シンガポールから短期留学生のミシェルがやってくる。ミシェルは初対面の環に学内でのエスコートを依頼し、環もミシェルのわがままを黙って受け入れる。ハルヒはそんな2人の姿に不安を募らせるが……。ドラマ版から続投の川口春奈、山本裕典らに加え、ミシェル役で「AKB48」の篠田麻里子が出演。また、ミシェルの兄ローレンス役でK-POPグループ「2PM」のニックンが日本映画初出演を果たす。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宗方姉妹", "宗方姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "『朝日新聞』に連載された長編小説を、松竹の名匠・小津が新東宝の資本で撮り上げた文芸作品。小津が得意とする平坦な日常感覚のリズムを刻む物語とは幾分趣を異にし、原作ものらしいドラマティックな展開が特徴。日本の古い習慣に準じて生きる姉と、束縛されず自由に生きる妹を対照的に描きながら、日本の社会を浮き彫りにしている。夫・亮助が失業し、やむなくバー勤めをする妻・節子。亮助は自信をなくし、暗く心を閉ざしている。夫のある身でありながら、姉がひそかに神戸の家具屋・田代に想いを寄せているのを知る妹・満里子は気が気でない。亮助、田代、そして姉に、満里子は正直な気持ちをぶつけていく。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キトキト！", "キトキト！", "", "", "http://image.eiga.k-img.com/images/movie/33888/315x210.jpg?1251", 109, "富山県高岡市を舞台に、型破りな母と子供たちの絆をユーモラスに描いたファミリー・ドラマ。高校3年生の優介の母・智子は、女手ひとつで2人の子供を育てあげた肝っ玉母ちゃん。しかし、優介は破天荒すぎる母の行動について行けず、家を出て上京することを決意する。主人公の優介に「夜のピクニック」の石田卓也、母・智子にベテラン女優の大竹しのぶが扮する。監督・脚本は井筒和幸監督作品の助監督を務めてきた新鋭・吉田康弘。", "2007-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Coeur et le Courage, Bejart Ballet Lausanne", "ベジャール、そしてバレエはつづく", "", "", "http://image.eiga.k-img.com/images/movie/54928/315x210.jpg?1709", 84, "エネルギー溢れる作風で、バレエの歴史を塗り替えた20世紀最高の振付師との呼び声も高いモーリス・ベジャール。2007年11月にこの世を去ったベジャールの軌跡を振り返りつつ、その後継者に任命されたジル・ロマン率いるモーリス・ベジャール・バレエ団の新たな挑戦を追ったドキュメンタリー。ロマンの初振付作品「アリア」のワールド・プレミアが初日を迎えるまでの舞台裏に迫り、プレッシャーと闘いながら厳しい練習を重ねるロマンと団員たちの姿を映し出す。", "2009-12-19", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARTIFICIAL PARADISE", "アーティフィシャル・パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "戦前のドイツ映画の巨匠フリッツ・ラング。映画に目覚めた彼の青春時代をフィクションも交え描いた作品。第一次大戦中、砲兵士官としてスベロニアに赴いた若きラングが、弁護士ガトニック一家から、後の映画のインスピレーションをうけていく様を、ラングの映画を挿入しながら描いていく。", "1990-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("重力ピエロ", "重力ピエロ", "", "", "http://image.eiga.k-img.com/images/movie/53548/315x210.jpg?2551", 119, "人気作家・伊坂幸太郎の直木賞候補になった同名ベストセラーを映画化。大学院で遺伝子の研究をする兄の泉水と、自分がピカソの生まれ変わりだと思っている弟の春。2人は、仙台の街で起こる連続放火事件と、現場近くに必ず残されるグラフィティアートの関連性に気付き、事件の謎解きに乗り出すが、そのことで24年前から今へと繋がる家族の謎が明らかになっていく。監督は「Laundry」の森淳一。泉水役に加瀬亮、春役は岡田将生。", "2009-05-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　人魚姫のなみだ", "それいけ！アンパンマン　人魚姫のなみだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "アンパンマンたちが暮らす陸の世界に憧れる人魚姫サニーは、人間になれるという髪飾りを海底のうずまき城からこっそり盗みだし、陸へ上がっていった。しかし、その頃海底では、髪飾りの封印が解けた怪獣ゴロンゴラが暴れ始め……。『人魚姫』の物語をモチーフにしたシリーズ第12作。サニーの声を南果歩が担当している。", "2000-07-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の三度笠", "花の三度笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "名門役者の若手・嵐粂吉は深川芝居の満津次が恋人だったが、彼女に旗本への身請話が持ち上がり、二人は駆け落ちする。が、追手につかまり粂吉は崖から海へ。３年後、疾風の粂と名乗る旅の男がある女を尋ねて現れる。彼こそ粂吉その人であった……。人気連載小説を映画化した人情股旅もの。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHASING　SLEEP", "REM〈レム〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "この作品が長編デビューとなるM・ウォーカーが、不眠症をモチーフにある男の不条理な恐怖体験を語り明かすスリラー。ある夜、自宅で目を覚まし、妻が帰宅していないことに不安を抱いた大学教授エド。そんな彼が教え子や警官といった訪問者に対応するうちに、現実とも幻覚ともつかない奇妙な出来事に見舞われていく姿を描き出す。主人公の自宅内に舞台を限定し、観る者の時間感覚を曖昧にさせる迷宮的世界を構築した演出が秀逸だ。", "2000-01-01", "1,2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Malcolm X", "マルコムX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 201, "　黒人解放運動の指導者マルコムXの生涯を描くスパイク・リー監督渾身の力作。ボストンのスラム街に暮らす青年マルコムは犯罪に手を染め、やがて逮捕される。刑務所でイスラム教に改宗した彼はマルコムXと改名。出所後、ブラック・ムスリム運動の指導者イライジャ・モハメドのもとで、白人を敵視する扇動的な運動を展開することに。その後、人種差別に対する抵抗運動が各地で激化。メッカを巡礼したマルコムは異人種との共存という思想に目覚めるが……。", "1993-03-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PETIT SOLDAT", "小さな兵隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「勝手にしやがれ」で華々しいデビューを飾ったJ・L・ゴダールの第２作。カメラマンとして活躍するかたわら秘密軍事組織OASの一員としても働いているブルーノは、OASに攻撃的なスイス人ジャーナリストを暗殺するよう命令される。暗殺にだけは手を出したくないブルーノは、知り合ったばかりの女の子ベロニカと逃走の旅に出るのだが……。ゴダール＝A・カリーナのコンビによる第１作。アルジェリア戦争に批判的だとして1963年まで公開禁止となっていた。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フレンズ　もののけ島のナキ", "フレンズ　もののけ島のナキ", "", "", "http://image.eiga.k-img.com/images/movie/55898/315x210.jpg?2385", 88, "小学校の教科書にも採用されたことのある浜田広介の児童文学「泣いた赤おに」をベースに、「ALWAYS 三丁目の夕日」「SPACE BATTLESHIP ヤマト」の山崎貴監督と、新人の八木竜一監督が共同で手がける3DCGアニメーション。もののけ島に住む赤おに・ナキと青おに・グンジョーの友情を軸に、人間の子ども・小竹との交流を描いたオリジナルストーリー。ナキの声に「SMAP」の香取慎吾、グンジョーに山寺宏一。そのほか、YOU、阿部サダヲらが声優を務める。", "2011-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lie lie Lie", "Lie lie Lie", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "ガランとした仕事場で黙々と電算写植機をオペレートする波多野のもとに、高校時代の同級生、相川が転がり込んでくる。相川は怪しげな事業に手を染めるサギ師になっていた。やがて相川の提案で、波多野が睡眠薬でもうろうとした状態で書き上げた原稿を“幽霊が書いた”との触れ込みで出版社に売り込もうとするが、敏腕編集者の美咲にウソを見破られてしまう。軽いノリのコンゲームを媒介として、奇妙な方向に発展していく人間関係の楽しさを描いたコメディ。ユニークなキャラクターに取り組んだ主役３人の個性も本作の大きな魅力になっており、特にしたたかなキャリアウーマンに扮した鈴木保奈美が、コップ酒を豪快に飲み干すシーンが痛快。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BYE BYE BIRDIE", "バイ・バイ・バーディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ブロードウェイで好評を博したミュージカルの映画化。若者の間で圧倒的人気を持つロック歌手バーディが、兵役に就くことになった。彼と別れを惜しむファンは、“サヨナラ・バーディ・ショウ”を開催する。恋人役でジャネット・リーが出演。気楽に楽しめるライト・コメディ。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛇のひと", "蛇のひと", "", "", "http://image.eiga.k-img.com/images/movie/55638/315x210.jpg?3972", 0, "第2回WOWOWシナリオ大賞に輝いた脚本を、永作博美＆西島秀俊主演で映像化したヒューマン・サスペンス。部長の自殺と時を同じくして姿をくらませた課長・今西の行方を探すことになったOLの陽子。調査を続けるうち、彼女はよく知っていたはずの今西の人物像に疑問を抱きはじめる。「Laundry」「重力ピエロ」の森淳一監督がメガホンを取り、美しくも悲しい人間の“業”をみずみずしい映像美で描き出していく。", "2010-09-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EGG", "EGG", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "「TRICK〈トリック〉」の堤幸彦監督が、気鋭のVFXディレクター、原田大三郎とコンビを組んで作り上げた異色の怪獣映画。怪獣を体内に宿した女性の運命が描かれる。氏家恵、犬山イヌコら個性あふれる役者の怪演が絶妙。2002年の東京国際ファンタスティック映画祭で話題を呼んだ一作だ。目を閉じるとまぶたの奥に卵が見える月子。そんな折、卵が割れ、中から怪獣が現れる。それを機に彼女の体が内側から破壊されていく。", "2005-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WOMEN IN RED", "ウーマン・イン・レッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "市役所のPR課に勤める平凡な中年男、テディ。真面目一方で妻に貞節を守っている彼だったが、ある日、出勤途中で見かけた“赤いドレスの女”に一目惚れ。絶世の美女をモノにしようと悪戦苦闘を始める主人公の滑稽な有り様を描いたロマンティック・コメディ。出演は「ヤング・フランケンシュタイン」で知られ、この作品の演出も手掛けているG・ワイルダーをはじめ、C・グローディンなど達者な面々。彼らのやりとりが絶妙なアンサンブルを奏でる楽しさあふれる作品。スティーヴィー・ワンダーの歌う主題歌『心の愛』がアカデミー音楽賞に輝き、世界中で大ヒットを記録した。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHRISTOPHER COLUMBUS", "コロンブスの探検", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "時は15世紀末。“大西洋を西に向けて航海すれば必ずインドに達する”という自分の考えを証明するため、数人の仲間とともに未知の大海に旅立った探検家コロンブス。しかし、彼らの行く手には……。", "1948-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Confessions of a Dangerous Mind", "コンフェッション", "", "", "http://image.eiga.k-img.com/images/movie/1038/315x210.jpg?2458", 113, "視聴者参加型TV番組の草分け、「デート・ゲーム」（65）や「ゴング・ショー」（76）を発案・司会した米TV界の伝説的人物チャック・バリス。彼が84年に出版した自伝は、実は彼がCIAのスパイでもあったというトンでもない内容。その自伝を原作に「マルコヴィッチの穴」のチャーリー・カウフマンが脚本を執筆、ジョージ・クルーニーが初監督に挑戦した注目作。主演は「チャーリーズ・エンジェル」第1作の悪役サム・ロックウェル。", "2002-08-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RANEN", "忍者と悪女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "R・コーマンが、３大怪奇映画俳優を起用して、ポーの世界を映画化。ある晩、読書中のクレーブン博士のもとに一羽の大鴉が舞い降りる。ところがそれは、スカラバスの手によって鴉の姿にされたペドロ博士の化身なのだった。ペドロを人間に戻したクレーブンは、彼と我が子を伴いスカラバス邸に出向くが……。ビデオ発売時タイトルは「大鴉」。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHO’S GOT THE ACTION？", "浮気の計算書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "J・ローズ監督が製作・脚本を担当し、D・マンが監督したコメディ映画。夫が競馬にハマっていることを知った妻は、夫にこれ以上損をさせないためにもと自分でノミ屋を始める。ところが、あまりの評判に他のノミ屋は客をとられて大弱り。ノミ屋の総元締が現れて、大騒ぎが起こり……。夫役のD・マーティンをはじめ、芸達者がそろった。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PRISONNIE｀RE", "囚われの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "女性を完全に服従させたいと願う芸術家と、そうされることに歓びを感じる人妻が、純粋な愛を見いだしていくさまを描く。「恐怖の報酬」「娼婦マノン」などで知られる鬼才クルーゾー監督が、異常な男女の愛の姿を斬新な映像感覚で捉えたサスペンス。", "1968-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("任侠外伝・玄海灘", "任侠外伝・玄海灘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "アングラ演劇の雄、唐十郎が初監督したサスペンス人間ドラマの異色作。かつて朝鮮で女を殺し、その死体を犯した近藤と沢木。今や沢木はヤクザの代貸しとなり、近藤は韓国から密航してくる女を売りさばく運び屋だった。ある日、近藤は密航者の中に、かつて殺した女と瓜二つの女性を発見するが……。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEPPENWOLF", "ステッペンウルフ 荒野の狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ドイツのノーベル賞作家H・ヘッセが、1927年に発表した『荒野の狼』の映画化。監督のハインズは、アニメーションやビデオに転換した映像を駆使し、既成の文芸映画とはまったく異なった大胆で野心的な作品に仕上げた。1920年代のドイツ。中年の作家ハリーは、孤独で厭世的な生活を送っている。ある晩、彼はヘルミーネという美しく謎めいた女と知り合い、彼女を介して未知の享楽の世界に引き込まれていく。この作品の独創的なところは、シュールリアリスティックな魔術劇場の場面であるが、ナイトクラブの場面での、1920年代の雰囲気も魅力的である。サンダとクレマンティが好演。グルンツの音楽が作品全体に活気を与えている。", "1974-01-01", "1,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すべての女に嘘がある", "すべての女に嘘がある", "", "", "http://image.eiga.k-img.com/images/movie/57538/315x210.jpg?4202", 70, "役者、アイドル、素人、キャバ嬢、AV女優など、職業や経歴もさまざまな女性たちが「ハリウッド映画に出演する女優になる」という目標にむけて結成した「KU.RO.FU.NE PROJECT」の第1弾として製作された映画。精神科の病院で3人の女性の拉致監禁事件と、院長の妻が殺害される事件が発生。捜査を担当する刑事は事件当時監禁されていた3人の女性の事情聴取を始めるが、3人の証言はそれぞれ食い違っており……。出演は日テレジェニック2010ファイナリストの浅居円、グラビアアイドルの長谷川ミク、AV女優のかすみりさら。", "2012-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青空に一番近い場所", "青空に一番近い場所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "サラ金に追われ、同僚にも自殺されたサラリーマン俊太郎は、行き場を失って立ち入り禁止の会社の屋上に逃げ込む。そこは、許された者だけが足を踏み入れることのできる、不思議に心やすらぐ異空間だった……。劇団・第三舞台の演出家、鴻上尚史が監督した、人生に疲れた現代人に贈るファンタジー。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Good", "善き人", "", "", "http://image.eiga.k-img.com/images/movie/57418/315x210.jpg?2483", 96, "ナチス政権下のドイツに生きるひとりの大学教授の苦悩と葛藤（かっとう）を描いた、英国の劇作家C・P・テイラーの舞台劇「GOOD」を、ビゴ・モーテンセン主演で映画化。ヒトラー独裁が進む1930年代のドイツ。ベルリンの大学で文学を教えるジョン・ハルダーは、過去に書いた小説をヒトラーに気に入られたことから、ナチ党に入党せざるをえなくなる。家庭と文学を愛し、かつて戦争をともに戦ったユダヤ人モーリスとの友情を大切にしていたジョンだったが、ナチス入党により人生が一変。自分と家族の命を守るか、それとも友情と信念を貫くかで苦悩し、追い込まれていく……。", "2012-01-01", "3,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("About a Boy", "アバウト・ア・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/1238/315x210.jpg?2057", 102, "親の遺産で暮らす38歳無職の独身男ウィルの苦手なものは「責任」。が、情緒不安定の母親と2人暮らしのイジメられっ子、12歳のマーカスと出会い、人生観が変わっていく。「アメリカン・パイ」のポール＆クリス・ワイツ監督が「ハイ・フィデリティ」のニック・ホーンビィの同名小説を映画化。「ブリジット・ジョーンズの日記」の製作プロ、ワーキング・タイトルとロバート・デ・ニーロの製作プロ、トライベッカが共同製作。", "2002-09-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rivals", "ライバルズ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喧嘩犬", "喧嘩犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "喧嘩が強く、そのうえ女にもモテ過ぎて困るという男・鴨井大介。出所後、刑務所で同房だった小吉の家を訪ねた大介は、そこで女房を暴力団に連れ去られ、新聞記者にしつこく質問されている小吉の姿を発見した。暴力団のアタマは、これまた同房で、かつて大介がコテンコテンに殴り倒したことのある小森だった。相手が大介と知り、あっさりと小吉の女房を返す小森だが、ひそかに大介の命を狙って……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Twilight Saga: Breaking Dawn - Part 1", "トワイライト・サーガ　ブレイキング・ドーン Part 1", "", "", "http://image.eiga.k-img.com/images/movie/56928/315x210.jpg?3053", 118, "人間の少女ベラとバンパイアのエドワードの禁断の恋を描く、ステファニー・メイヤーによる人気小説を映画化したシリーズ最終章2部作の前編。監督は「ドリームガールズ」のビル・コンドン。ついに結ばれたベラとエドワードは結婚式を挙げ、新婚旅行に出る。旅行から戻れば念願のバンパイアへの転生がかなうベラだったが、滞在中の南の島で自身の中に新たな命が宿っていることに気づく。「不滅の子」と呼ばれ、決して作ってはならないといわれる人間とバンパイアの混血児の存在が、2人の運命を大きく揺るがしていく。", "2012-02-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEART BREAKERS", "ハートブレイカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "結婚サギ師の美人母娘が繰り広げる騒動を描くラブ・コメディ。男に捨てられたのをきっかけに、サギ師になったマックスは、娘ペイジを相棒にカモになる男を探して旅を続けている。ところが、成功を目前にして、ペイジがターゲットの男と恋に落ちてしまい……。お色気と話術で男を惑わす母娘をS・ウィーヴァーとJ・L・ヒューイットがコミカルに好演。彼女たちをてこずらせる億万長者役G・ハックマンの怪演も見もの。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIRTY DANCING", "ダーティ・ダンシング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1963年の夏、とある避暑地にやってきた17歳の少女ベイビーが、ダンスのインストラクター、ジョニーと出会って愛に目覚めていく姿を、1960年代のヒット・ナンバーと官能的なダンス・シーンで綴る躍動感あふれる作品。主人公のベイビー役でキュートな魅力をふりまいているのは、「フェリスはある朝突然に」のJ・グレイ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ROMANTIC　PRESIDENT", "ピアノを弾く大統領", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "TVドラマ『冬のソナタ』で一躍有名となった人気女優チェ・ジウが、強気なキャラクターを演じ新境地を開いたロマンチック・コメディ。韓国の国民的スター俳優、アン・ソンギ共演。女子高校で教師を務めるヒロインが生徒と揉めてしまった結果、生徒の親を呼び出すことに。その親とは何と韓国の現職大統領だった！ やがてふたりは度々会ううちに、恋へと発展していく。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Harry Potter and the Prisoner of Azkaban", "ハリー・ポッターとアズカバンの囚人", "", "", "http://image.eiga.k-img.com/images/movie/1078/315x210.jpg?1280", 142, "ホグワーツ魔法魔術学校の3年生になるハリー。人々の噂では、囚人シリウス・ブラックがアズカバン監獄を脱獄し、ハリーの命を狙っているという。ホグワーツにはブラックを捕縛するためという名目で、アズカバン監獄の看守たち＝人間の魂を吸い取る恐ろしい吸魂鬼ディメンターたちがやってくる。大ヒット・シリーズの第3弾は、監督がアルフォンソ・キュアロンに交替。ティーンエイジャーになったハリーたちが活躍する。", "2004-06-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHADOW OF DEATH", "殺人マシーン デストロイヤー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "婦女連続殺害犯モーゼの処刑中に事故が起こり、その事故後、刑務所は閉鎖された。その刑務所に女囚ものの映画を撮りにきた撮影隊の一行。やがて彼らの前にたくましい肉体を持つ不気味な男が出現し、一人、また一人と惨殺されていく。刑務所を舞台に全身凶器と化した男の恐怖を描くアクション・ホラー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真救世主伝説　北斗の拳　ラオウ伝　激闘の章", "真救世主伝説　北斗の拳　ラオウ伝　激闘の章", "", "", "http://image.eiga.k-img.com/images/movie/33948/315x210.jpg?1405", 0, "一大ブームを巻き起こした超人気コミック「北斗の拳」に新たなエピソードを交えて再構成した「真救世主伝説」5部作の第3作。物語最大のクライマックスであるラオウとケンシロウの宿命の対決をラオウの視点から描く。“拳王”ラオウの前に立ちはだかる南斗最後の将。その正体がかつて愛した女ユリアだと知ったラオウは、彼女の居城に攻め込み、ケンシロウと最後の戦いを繰り広げる。本作の劇場公開を前にラオウの告別式が実際に開催され話題に。", "2007-04-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちょんまげぷりん", "ちょんまげぷりん", "", "", "http://image.eiga.k-img.com/images/movie/55308/315x210.jpg?4603", 108, "「アヒルと鴨のコインロッカー」「フィッシュストーリー」など伊坂幸太郎原作の映画化を多く手がける中村義洋監督が、荒木源の小説「不思議の国の安兵衛」を映画化。人気アイドルグループ「NEWS」「関ジャニ∞」の錦戸亮が映画初主演を務める。忙しい日々をおくるシングルマザーのひろ子（ともさかりえ）は、江戸時代からタイムスリップしてきた侍・安兵衛と出会い、なりゆきから家に居候（いそうろう）させることに。安兵衛は家事のすべてを引き受け、やがてプリンをきっかけにお菓子作りに目覚めていく。", "2010-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APPLESEED アップルシード", "APPLESEED アップルシード", "", "", "http://ecx.images-amazon.com/images/I/41XX7ME9P7L._SL210_.jpg", 103, "「GHOST IN THE SHELL 攻殻機動隊」の士郎正宗によるSFコミックを、モーションキャプチャーと3DCGを駆使してアニメーション映画化。西暦2131年、女兵士のデュナン・ナッツはサイボーグと化した元恋人のブリアレオスと再会し、未来都市オリュンポスに招かれる。一見すると平和で理想的な都市のオリュンポスだったが、感情を抑制したバイオロイドが政治を司り、人類の存在を揺るがす計画が密かに進められていた。デュナンは人類の未来の鍵を握る「アップルシード」をめぐり戦いに身を投じていく。「ピンポン」の曽利文彦がプロデュースを務めた。", "2004-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄塔武蔵野線", "鉄塔武蔵野線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "日本ファンタジーノベル大賞を受賞した銀林みのるの小説の映画化。東京都保谷市と埼玉県日高市を結ぶ送電線、通称武蔵野線。一体、これはどこまで続いているのかという疑問を持った小学６年の少年・見春が２歳下のアキラを連れて、送電線を逆にたどっていく。二人が体験するひと夏の冒険物語を、郷愁の中に綴った心優しきキッズ・ムービー。監督は第１回ぴあフィルムフェスティバルの入賞者でもある長尾直樹。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SANDS OF IWO JIMA", "硫黄島の砂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "時代は第二次大戦下。アメリカ海兵隊のライフル部隊に所属する鬼軍曹と、軍人のインテリ息子の葛藤を軸に、部下の教育、軍曹の離婚の傷、そして硫黄島の激烈な戦闘を描いた戦争映画。主人公の鬼軍曹ジョンにJ・ウェインが扮し、一徹な男を力演している。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("如何なる星の下に", "如何なる星の下に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "文芸作品の映画化に意欲を燃やす豊田四郎が、名コンビ・八住利雄の脚色を得て、高見順原作に取り組んだもの。ただし、時代、場所設定とも変えられている。佃島のおでん屋“惣太郎”を舞台に、今は投げやりな人生を送る主人の惣太郎や妻、そして３人の娘たちそれぞれの人生模様を軽いタッチで描いていく。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お嫁にゆきます", "お嫁にゆきます", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "森昌子のデビュー７周年記念作。監督は、一連の山口百恵もので知られる西河克己だが、ここでは昌子の親しみやすいキャラクターを生かし、要所を押さえた人情ものに仕上げている。内容は、年頃の昌子が紆余曲折をへて、山本伸吾扮する幼な友だちと結ばれるというストーリー。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("土竜の祭", "土竜の祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "インディペンデントで活動を続ける井土紀州が日本映画学校俳優科、映画美学校フィクションコースとコラボレーションして作り上げた一連の作品を公開する「映画一揆　井土紀州2011」にて上映。ホームヘルパーの女性3人組が、介護する老人の身に起こったある事態を発端に大きな事件に巻き込まれていく姿を描く。", "2010-11-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BETRAYED BY LOVE", "パトリシア・アークウェット　愛の疑惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "FBI捜査官が不倫関係にあった女性を殺害した実話を映画化。銀行強盗事件を追うFBI捜査官のジェフ・アベリーは、事件のカギを握るのはケンタッキーの田舎町に住むディーン・ウィルソンであることを知る。彼はディーンの元を訪れ、互いに惹かれ合った二人は激しく愛し合うようになる。退屈した日々にうんざりしていたディーンは、ジェフとの新生活を夢みるが、野心家のジェフは次第に彼女を遠ざけていき、彼女の妊娠をきっかけに惨劇が起こる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREED", "グリード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "超大作を連発し、ユニバーサルを首になったE・フォン・シュトロハイムがゴールドウィン（MGMの前身）で撮った作品。F・ノリスの小説『マクティーグ』を下敷きに、モグリの歯科医師マクティーグと、その妻となるトリナ、そしてマクティーグの親友マーカスとの愛憎のもつれを、すさまじいばかりの筆致で描いていく。宝クジに当たったばかりに貪欲で金銭への執着が激しくなるトリナに辟易ぎみのマクティーグ。だがマクティーグを妬むマーカスは、彼がモグリであることをバラしてしまい、マクティーグは日雇い労働者に落ちぶれ……。完全主義者シュトロハイムは、無声映画であるにもかかわらずセリフをすべて俳優たちにしゃべらせ、衣裳や人物たち、あるいは背景の描写など、細かい部分にまで完全であることを求めた。ゆえに完成当初、フィルムは全部で47巻（およそ９時間半）あったといわれる。それを４人の編集者が現在の10巻にまとめ、公開された。", "1924-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かげろう", "かげろう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "尾道のバーのマダムおとよが殺された。大石刑事らの捜査で、おとよの呪われた過去が明らかにされていく。彼女の住んでいた瀬戸内海の貧しい島で、20年前の嵐の晩、島民たちが難破船を襲い彼女の夫は口封じのために殺されていたのだった。島の美しさと対照的に住民のうめきが悲痛な、ミステリー色の人間ドラマ映画。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿波DANCE", "阿波DANCE", "", "", "http://image.eiga.k-img.com/images/movie/34128/315x210.jpg?1467", 111, "阿波踊りとヒップホップを融合した“AWA DANCE”に熱中する高校生たちの友情と成長を描いた青春ムービー。東京のヒップホップダンス大会で優勝した茜は、両親の離婚により徳島県の高校に転校することに。ダンス部を発見して喜んだのもつかの間、部室内で繰り広げられていたのは、茜が愛するヒップホップとは程遠い阿波踊りだった。茜は阿波踊り好きのコージたちと対立するが、やがて2つの踊りをミックスしようと思いつく。", "2007-08-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チーズとうじ虫", "チーズとうじ虫", "", "", "http://image.eiga.k-img.com/images/movie/33518/315x210.jpg?4541", 98, "撮影当時まったくの素人であった加藤治代監督が、ガンに侵された母の姿を中心に、家族の日常を綴ったドキュメンタリー。母や高齢の祖母と共に過ごす何気ない日々の風景や、やがて訪れる母の死を穏やかに映し出す。2005年度の山形国際ドキュメンタリー映画祭で小川紳介賞と国際批評家連盟賞をダブル受賞したほか、フランスのナント三大陸映画祭でドキュメンタリー部門グランプリを受賞するなど、国内外で高い評価を得た。", "2006-07-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("笑傲江湖", "スウォーズマン／剣士列伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "香港で大ヒットを記録した伝奇アクション。明の時代、秘伝の武術書をめぐる争奪戦を描いた娯楽活劇巨編。製作はツイ・ハークが務め、アジアの巨匠キン・フーが監督として招かれたが、製作途中で降板、その後ツイ・ハーク自らがメガホンを取り完成させた。ツイ・ハーク得意のワイアーアクションが随所に盛り込まれ、後にシリーズ化された人気作品。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHASE IV", "フェイズ IV 戦慄！昆虫パニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "高度な知能を持ったアリの集団と、科学者たちの人類存亡をかけた戦いを描く異色SF。「北北西に進路を取れ」「サイコ」「ウエスト・サイド物語」などの斬新なタイトルを手掛け、「サイコ」のシャワー・シーン・デザイナーとしても知られるバスの劇場用映画デビュー作。宇宙で起こった変動の影響で、地球のアリたちが知能を持ちはじめる。それに気付いた科学者たちは、アリを全滅させようとするが、前線基地は、逆にアリの手中に落ちる。局面（＝フェイズ）は、最終段階“４”に突入しようとしていた……。「大自然の闘争」でアリの調教に成功したゲン・ミドルハムによるアリの群れの演出が、SFXを超えて不気味。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金田一少年の事件簿２／殺戮のディープブルー", "金田一少年の事件簿２／殺戮のディープブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "人気マンガの劇場版アニメ第２作。南海に浮かぶ超高層ホテルを舞台に、謎めいた怪事件に挑むはじめの活躍を、迫力のアクションを交えて描く。海底遺跡が眠るアドベンチャー・リゾートのオーナーの娘、藍沢茜に招待され、はじめと美雪が島を訪れる。だが、到着早々、テロリストが乱入。はじめは茜の家族や考古学者ら数名とともにホテルに閉じ込められ、事件に巻き込まれていく。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THIS HAPPY FEELING", "年頃ですモノ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "人気絶頂なのに舞台を引退、二度とカムバックしようとしない名優ミッチェルは、ひょんなことから若く美しい娘ジャネットと知り合う。ジャネットは彼を慕っていたが、隣家の青年が彼女に夢中であることを知ったミッチェルは、名優である自分の力を生かして、一世一代の大芝居をうつ。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUMMER LOVERS", "青い恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "エーゲ海に浮かぶサントリーニ島にやって来たアメリカ人の若いカップル。しかしミステリアスなムードの美しい娘が二人の前に現れ、男は夢中になってしまう。初めはショックを受けた恋人も次第にその娘に惹かれて……。エーゲ海ロケが美しい。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代やくざ・血桜三兄弟", "現代やくざ・血桜三兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "大組織とチンピラの抗争を描いた一編。全国制覇を狙う大阪誠心会が岐阜に乗り込んできて、地元の広道会と抗争がはじまった。広道会の邦夫、宏、信男、そして今は堅気になっている武らが、力を合わせて殴り込みをかける。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("色彩の記憶", "色彩の記憶", "", "", "http://image.eiga.k-img.com/images/movie/54378/315x210.jpg?3746", 70, "「世界はときどき美しい」の御法川修監督が色彩の匠たちを見つめたアート・ドキュメンタリー。美容界をリードするカラースペシャリスト、京都西陣織の匠、有田窯の陶工など、豊かな感受性で独自の色彩を創造する表現者たちの技と心に迫る。「クライマーズ・ハイ」の小林元ほか、日本映画界を代表するカメラマンたちが上質な色彩を見事に映し出している。", "2009-02-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOMINICK＆EUGENE", "ニッキーとジーノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "愛と共感について問う感動作。医大に通うジーノには、清掃員をしながら、ジーノの学費の面倒を見ている障害を持つ双子の兄ニッキーがいた。。そして彼は、ジーノが病院で働くジェニファーと親しくなるのを複雑な思いで見つめる。そんなある日、ニッキーは近所の子供が父親に虐待されるのを見て、自分の過去を思い出す。ニッキー役をT・ハルスが好演。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH WISH ４：THE CRACKDOWN", "バトルガンM−16", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「狼よさらば」「ロサンゼルス」「スーパー・マグナム」と続く、ブロンソン主演“デスウィッシュ”シリーズの第４作。忌まわしい過去を捨て、サラリーマンとして生活していたポール。だが知り合いの娘が麻薬中毒で死んだことをきっかけに、彼は次第に“一匹狼ポール”の姿に戻っていくのだった。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VEAR STORIA DELLA MONACA MONZA", "尼僧の背徳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "17世紀初頭に実際あった尼僧にまつわるスキャンダラスな事件を、実話をもとに綴った異色作。神に仕えるとは名ばかりで、背徳の日々に明け暮れる修道院。そこに入った領主の娘ビルジニアは、放蕩貴族オシオの罠にはまり、愛欲の虜となる。やがて子供を死産、川に捨てたことが世間に知られてしまう。", "1980-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cabeças Cortadas", "切られた首", "", "", "http://image.eiga.k-img.com/images/movie/55868/315x210.jpg?1662", 94, "1960年代にブラジルで起こった映画運動「シネマ・ノーボ」を代表する監督として知られ、「黒い神と白い悪魔」「狂乱の大地」といった作品で国際的にも高い評価を受けたグラウベル・ローシャ監督が1970年に手がけた作品。とある国の城の中が舞台。仮想国エルドラドの権力者になることを夢見るディアスは、地元の原住民や農民たちから猛反発を受けていた。そんなディアスのもとに奇跡を起こす羊飼いが現われる。", "2010-12-04", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AU HASARD BALTHAZAR", "バルタザールどこへ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ブレッソン監督がフランスの大プロデューサー、アナトール・ドーマンと初めて組んだ作品。主人公は不幸なロバのバルタザール。家畜としての過酷な日々から逃げ出した彼が向かったのは、幼い頃に可愛がってくれた少女マリーの許だった。今も優しいマリーはしかし、愛と打算の間で揺れ動く悲しい女に成長していた。バルタザールの受難の日々は続く……。物言わぬロバの視点を通して、人間の愚かさ、醜さを、突き放すような厳しいタッチで淡々と描く傑作。", "1970-05-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザブングル グラフィティ", "ザブングル グラフィティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "TVシリーズ『戦闘メカザブングル』を、名場面を中心に再構成した、タイトル通りのアニメ・グラフィティー。ウォーカー・マシンと呼ばれるロボットが車がわりに使われる惑星ゾラを舞台に、そこで繰り広げられる支配階級と被支配階級の人々との闘いが描かれている。主人公・ジロンは父親の仇の謎の男を追うが……。ウエスタン調アクション・コメディといった趣で、TV版からの熱心なファンにはうれしい映画版となった。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子象物語・地上に降りた天使", "子象物語・地上に降りた天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "太平洋戦争下、政府は動物園非常措置を発動する。動物たちが次々と殺されるなかで辛うじて生き残った１頭の子象を、命賭けで守ろうとする飼育係らの姿を描いた子象と人間の愛情物語。タイでロケーション、子象も同国の寄贈による。武田鉄矢が力演。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮 Love in Palace THE MOVIE 第2章", "宮 Love in Palace THE MOVIE 第2章", "", "", "http://image.eiga.k-img.com/images/movie/55618/315x210.jpg?2001", 0, "現代の韓国に王室が存在しているという設定で大ヒットした少女漫画が原作のTVドラマを、劇場版3部作として再編集した第2作。主演のユン・ウネ、チュ・ジフン、John-Hoonら若手人気俳優が多数出演した。皇太子妃となったチェギョンは、慣れない宮廷生活に戸惑いながらも、持ち前の明るさで夫シンとの距離を縮めていく。しかし、ある出来事から従兄弟のユルとチェギョンの関係を疑っていた皇太子シンは、元恋人ヒョリンとの2ショットをパパラッチに撮られ、王室スキャンダルを巻き起こしてしまう。", "2010-07-24", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bewitched", "奥さまは魔女", "", "", "http://image.eiga.k-img.com/images/movie/1288/315x210.jpg?4668", 103, "ニコール・キッドマン主演、「めぐり逢えたら」「ユー・ガット・メール」のノーラ・エフロン監督で、60年代の人気TVドラマ「奥さまは魔女」を元ネタに描くラブ・コメディ。舞台は現代。落ち目の俳優ジャックは「奥さまは魔女」のリメイク版の主演女優に街で見つけた素人のイザベルを抜擢するが、実はイザベルは本当の魔女だった……。魔女イザベルの父親役でマイケル・ケイン、大物女優役でシャーリー・マクレーンが共演。", "2005-08-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サディスティック・ソング", "サディスティック・ソング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "マンハント・ゲーム(人間狩り)を楽しむ４人の若者の姿を、セックスと暴力シーンを織り交ぜて描いた世紀末・青春バイオレンス映画。祐樹、美果、徹、雅広の４人は、毎年恒例のマンハントの獲物を探していた。祐樹はテレクラで知り合った年増女・涼子とその夫・真一、徹はバイト先のコンビニの万引き常習犯女二人組を捕まえる。彼らは祐樹の父親が所有する人気のない工場跡に獲物を連れ込み、改造エアガン片手に人間狩りを開始。だが、その時工場には一人の男が近付いていたのだった……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RICOCHET", "リコシェ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "自らの不注意から、新米警官ニックに逮捕された筋金入りの凶悪犯ブレイク。自分がきっかけとなって出世していくニックに対し、その復讐のみを考えて、巧みに脱獄を図る。そして地方検事補までになったニックに襲いかかるが……。復讐に燃える凶悪犯ブレイクを演じたJ・リスゴーの鬼気迫る演技が素晴らしい。ビデオ発売時タイトルは「リコシェ 炎の銃弾」。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL BRUTO", "乱暴者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "メキシコ時代のブニュエル作品の一つ。地主の用心棒ペドロは、主人の命令で小作人を殺したが、その家の娘に心惹かれ、彼女をさらって逃走する。娘の純情に触れてペドロにも人間らしい心が生まれてくる。筋立ては並でも、演出は並ではないブニュエルのメロドラマ。", "1952-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　STORY　OF　US", "ストーリー・オブ・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "夫婦の破局の危機を、ユーモアたっぷりに描いた恋愛ドラマ。作家のベンと、彼の秘書だったケイティーは結婚15年目の夫婦。二人の子供をもうけて、一見、理想的に暮らしているかのようにみえるが、じつは夫婦仲は冷え切っていた。争いに疲れた彼らは子供たちのサマーキャンプのあいだに試験的な別居に踏み切る。離れてみれば、それはそれで寂しさが募るが、会うとやはりケンカは避けられない。そんなある日、ケイティーの前に優しい男性が現われ……。「恋人たちの予感」のR・ライナー監督が、すれ違う夫婦の感情をリアルに描出。夫と男友達、妻と女友達による会話の中に結婚生活のホンネを覗かせ、共感を惹きつける。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRIME＋PUNISHMENT　IN　SUBURBIA", "クライム・アンド・　パニッシュメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ドストエフスキーの『罪と罰』を大胆にアレンジしたサスペンス。カリフォルニア郊外の街。美少女ロザンナとアメフトのスター選手ジミーは、学園中の注目を集める恋人同士。孤独な青年ヴィンセントは、いつもカメラのファインダーを通してロザンナを見つめていた。やがて、ロザンナが義父を殺害する事件をきっかけに、彼女とヴィンセントは急接近するが……。平穏な暮らしから転落していく少女と、彼女のすべてを受け入れる青年のスリリングなロマンスを描く。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sharpay's Fabulous Adventure", "シャーペイのファビュラス・アドベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/56968/315x210.jpg?4639", 89, "ディズニー・チャンネルの大ヒットテレビ映画「ハイスクール・ミュージカル」シリーズでおなじみの“わがまま女王”シャーペイを主役にしたスピンオフ・ドラマ。ハイスクールを卒業したシャーペイは、愛犬ボイと一緒にチャリティー・ショーに出演したところをブロードウェイのキャスティング担当者にスカウトされる。ミュージカルのオーディションを受けるためニューヨークへ向かうシャーペイだったが、そこでは厳しい現実が彼女を待ち受けていた。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我的父親母親", "初恋のきた道", "", "", "http://image.eiga.k-img.com/images/movie/1618/315x210.jpg?3988", 89, "都会で働くビジネスマン、ユーシェンは父の訃報を聞き、数年ぶりに故郷の村へ帰ってきた。年老いた母は、今では行われなくなった古いしきたり通りの葬儀をすると言って周りの人を困らせている。そんな母の頑固な様子を見て、ユーシェンは村の語りぐさとなっている父と母の恋物語を思い出していた。都会からやってきた若い教師と、彼に恋した文盲の少女。手作りの料理に込めた彼女の恋心はやがて彼の元へと届くのだが……。", "2000-12-02", "1,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Passig Show: The Life and Music of Ronnie Lane", "ロニー MODSとROCKが恋した男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "60年代初頭からイギリスのロック・シーンをリードし続けたロニー・レインに迫るドキュメンタリー映画。後のミュージシャンたちに多大な影響を及ぼしたモッズ・バンド、スモール・フェイセズや、ロッド・スチュアートらと組んだフェイセズ等の活動で知られる彼の人生を、彼と親交の深かったミュージシャンや友人、家族らへのインタビューを中心に振り返る。さらにスモール・フェイセズの未公開ライブ映像など貴重な資料も満載。", "2007-05-12", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("E´TOILE", "エトワール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ローマのバレエ学校に入学するためにニューヨークから単身、大西洋を渡ってきた少女クレア。オーディションの日、緊張のあまり教室へ入れず逃げ出してしまった彼女は迷路のような廊下を抜け、誰もいない舞台で無心に踊る。やがて彼女は何かに憑かれたように変わっていく。J・コネリーが初々しいバレエ少女に扮した、イタリア産のスリラー。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HAND", "キラーハンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "人気漫画家が事故で右手首から先を失う。その右手首が主人公の無意識の中でよみがえり、憎悪の化身と化して次々と人々を襲い始める。O・ストーン監督のハリウッド・デビュー作。動く右手の特殊効果は「エイリアン」のカルロ・ランバルディが担当。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BITE THE BULLET", "弾丸を噛め", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "1880年代から20世紀初頭にかけて、西部では賞金付きマラソン競馬が流行したが、この作品は1906年、当時としては画期的な2000ドルの賞金レースの模様を描いている。男女８人が1000キロを走り続ける厳しいレースを荒々しく描写した異色西部劇。DVDは「コロムビア・トライスター ザ・ウェスタン・ムービーズBOX(2)」に収録。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Demoiselle d'honneur", "石の微笑", "", "", "http://image.eiga.k-img.com/images/movie/34038/315x210.jpg?1371", 107, "フランス映画界の巨匠クロード・シャブロルが、イギリスの女流ミステリー作家ルース・レンデルの同名小説を映画化した官能サスペンス。25歳の青年フィリップは、妹の結婚式で出会った美しい女性センタと恋に落ちる。情熱的な愛に溺れていくフィリップだったが、次第にセンタの不可解な言動に振り回されはじめる。やがてセンタは、自分への愛の証として、フィリップに殺人を犯すよう迫り……。主演は「ピアニスト」のブノワ・マジメル。", "2007-06-30", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三國之見龍卸甲", "三国志", "", "", "http://image.eiga.k-img.com/images/movie/54228/315x210.jpg?3515", 102, "「三国志演義」に基づき、蜀の君主・劉備に仕えた平民出身の英雄・趙雲を描いた歴史大作。趙雲を演じるのは「インファナル・アフェア」「LOVERS」のアンディ・ラウ、監督は「ドラゴン・スクワット」のダニエル・リー。戦乱の中国、祖国統一の夢を抱き同志の平安と共に劉備に仕える趙雲は、数々の戦でその武勇を示していった。しかし曹操率いる大国・魏との戦で平安が護衛に失敗し、劉備の家族が人質に取られてしまう……。", "2009-02-14", "8,5,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サウダーヂ", "サウダーヂ", "", "", "http://image.eiga.k-img.com/images/movie/56918/315x210.jpg?2159", 167, "8ミリ映画「雲の上」（2003）や、映画美学校のスカラシップで製作した「国道20号線」（07）で注目された富田克也監督が、不況と空洞化の進む日本の地方都市を舞台に、移民問題の実態を描きだすドラマ。崩壊寸前の土木建築業の現場で、日系ブラジル人やタイ人をはじめとする移民労働者たちが、過酷な労働条件の下で懸命に生き抜く姿を描く。第64回ロカルノ国際映画祭インターナショナル・コンペティション部門に出品された。", "2011-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Infernal Affairs II", "インファナル・アフェア　無間序曲", "", "", "http://image.eiga.k-img.com/images/movie/1448/315x210.jpg?2861", 119, "大ヒット作「インファナル・アフェア」の監督・脚本家が前作の11年前を描く続編。第3作「インファナル・アフェア　終極無間」へと続く3部作の第2作。中国への返還に揺れる香港で、マフィアの大ボスが暗殺され、次男ハウが2代目の大ボスになったのを危険視した警察は、ハウの異母兄弟であるヤンを組織に潜入させる。一方、マフィアは新興マフィアのサムの子分、ラウを警察に潜入させるが、彼は大ボスの暗殺に加担していた。", "2004-09-18", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("軍妓／COMFORT WOMEN", "従軍慰安婦２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "第二次大戦中、非人道的な扱いを受けた従軍慰安婦たちの悲痛な運命を描いた作品。1938年、上海入りをした従軍記者の秋山和美はある夜、帰宅途中暴漢に襲われ中村少佐に助けられる。以後彼が和美の保護と監視役を負うが、和美に思いを寄せていることを知った上官の川崎少佐は、中村を投獄に送り込み、残虐な板垣大尉を代わりにする。だが、板垣大尉によって、非道な日本軍の姿を追う和美は、虐待の巣窟、従軍慰安所に送り込まれてしまうのだった。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUE SOLDI DI SPERANZA", "2ペンスの希望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "貧しい家の長男であるアントニオは、母や姉妹を養うため、休む間もなく仕事をしなければならない。しかし、どこも不景気で定職はなかった。そんなある日、彼はカルメラという娘と出会い、恋に落ちる。彼女は貯金もあるから結婚しようと持ちかけるが、生活に追われるアントニオはそれどころではなく、やがて彼は職を求めてナポリの町へ出る。貧しい状況のなかでも幸せを手に入れようとする人々をほのぼのと描いた喜劇。", "1951-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たまもの", "たまもの", "", "", "http://ecx.images-amazon.com/images/I/21PVS8XTAFL._SL210_.jpg", 65, "瀬々敬久、サトウトシキらピンク四天王の次世代を担う存在として注目を集める、いまおかしんじ監督作。微妙な年齢に突入した女性のひたむきな愛と、その強い想いが生み出す狂気が描かれる。恐怖ともブラック・ユーモアともとれる衝撃の結末にはびっくりさせられるはずだ。ボウリング場で働く愛子は、偶然出会った良男と付き合うことに。幸せいっぱいの彼女はかいがいしく彼に尽くす。だが良男はそれが重荷になり……。", "2004-11-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("老獄 OLD PRISON", "老獄 OLD PRISON", "", "", "http://image.eiga.k-img.com/images/movie/55858/315x210.jpg?3059", 60, "「ロスト・バイ・デッド」などで知られる辻岡正人監督が、老人介護の知られざる実態をあぶり出す社会派ラブストーリー。特別養護老人ホームを舞台に、職員による入居者への暴行を知った女性看護士が、事実を隠ぺいしようとする施設側に立ち向かいながらも、新人介護士の青年と激しい恋に落ちていく姿を描く。本作が映画初主演となる女子プロレスラーのアジャ・コングが、主人公の看護士役に扮する。", "2012-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("与太郎戦記", "与太郎戦記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "兵役検査に甲種合格した与太郎。さっそく軍隊生活に入るが、その厳しさになかなかついていけない。そんな彼を救ってくれたのが特技とする落語。皆の前で一席ぶつと、これが上官に大うけ。一躍昇進街道を突走るが、持って生まれたあわて症と女好きは相変わらずで……。弓削太郎全盛期の作品。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE PIECE 3D 麦わらチェイス", "ONE PIECE 3D 麦わらチェイス", "", "", "http://image.eiga.k-img.com/images/movie/55778/315x210.jpg?4466", 30, "コミックス累計発行部数が2億部を突破した国民的人気漫画「ワンピース」の、劇場版シリーズ初となる3D作品。ある朝、ルフィが目覚めると、ルフィ海賊団のトレードマークでもある麦わら帽子が消えていた。幼いころにあこがれの大海賊“赤髪のシャンクス”から預かったその帽子は、これまでどんな時も手放すことのなかった大切な宝物。大慌てで探しはじめたルフィたちは、麦わら帽子をくわえた大ワシを発見するが、大ワシを追ってグランドラインの危険地帯へと入り込んでしまう。「トリコ3D 開幕！グルメアドベンチャー!!」と2本立て上映。", "2011-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Becoming Jane", "ジェイン・オースティン　秘められた恋", "", "", "http://image.eiga.k-img.com/images/movie/53778/315x210.jpg?2585", 120, "現在に至るまで根強い人気を誇る19世紀イギリスの女流作家ジェイン・オースティンの知られざる恋を描いた伝記ラブストーリー。「キンキーブーツ」のジュリアン・ジャロルド監督が、伝記作家ジョン・スペンスが2003年に発表した原作をもとに映画化。主人公ジェインを「プラダを着た悪魔」のアン・ハサウェイ、彼女と激しい恋に落ちる青年トム・ルフロイを「ウォンテッド」のジェームズ・マカボイが演じる。", "2009-10-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ma Mere", "ジョルジュ・バタイユ／ママン", "", "", "http://image.eiga.k-img.com/images/movie/33508/315x210.jpg?4531", 110, "20世紀最大の思想家ジョルジュ・バタイユの遺作「聖なる神」を、本作で長編映画デビューを飾るクリストフ・オノレが完全映像化。父親と2人暮らしの青年ピエールは、自堕落な父親の下を離れ、長年崇拝に近い気持ちを抱いていた母親ママンのいるスペイン、カナリア諸島を訪れる。だが、ピエールはそこで母親ママンの本性を見て愕然とする……。主演はフランスを代表する女優イザベル・ユペールと「ドリーマーズ」のルイ・ガレル。", "2006-07-01", "2", 0, NOW(), NOW());
