INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUN SHY", "ガンシャイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ストレスをためこんだ囮捜査官の悪戦苦闘を描くサスペンス・コメディ。任務中に危険な目に遭ったことから情緒不安定になった麻薬取締局の潜入捜査官チャーリー。それでもおとり捜査を強いられる彼は、麻薬組織の凶暴な幹部に妙に気に入られてしまう。やがてストレスから腹痛となったチャーリーは、病院で看護婦ジュディと出会い、恋に落ちるが……。オフビートなユーモア感覚がドラマを軽やかに弾ませている。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Genius Within: The Inner Life of Glenn Gould", "グレン・グールド　天才ピアニストの愛と孤独", "", "", "http://image.eiga.k-img.com/images/movie/57048/315x210.jpg?3071", 108, "1982年に50歳で世を去ったカナダの天才ピアニスト、グレン・グールドのドキュメンタリー。真夏でも手袋とマフラーを手放さず、ハミングしながらピアノを演奏するなどエキセントリックな言動で注目を集めたグールド。これまで未公開だった映像や写真、プライベート・レコーディング、日記の抜粋、かつての恋人や友人たちへのインタビューなどを通し、グールドの人間としての側面に焦点を当てる。", "2011-10-29", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("麗霆”子 レディース!!", "麗霆”子 レディース!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "“女暴走族”の金看板を背負い、暴走と抗争に明け暮れる少女たちの姿を描く異色の青春映画。千葉の外房を仕切る房狂会・麗霆”子〈レディース〉初代総長の創山さとみは、敵対する悪魔女族（ゾンビィ）をついに叩き潰す。が、悪魔女族は房総一のレディース血護苦（ジゴク）の傘下にあり、ひとみらは巨大な抗争に否応なく巻き込まれていく……。元アイドルの渡辺美奈代が乳首を切って詫びを入れるなど、衝撃シーンも満載。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Twins Effect", "ツインズ・エフェクト", "", "", "http://image.eiga.k-img.com/images/movie/1718/315x210.jpg?3414", 107, "バンパイアの王族6人の王子の血を吸い、聖典「昼と夜の書」を開く者は全世界の支配者となる。バンパイア集団とスレイヤーたちが、この聖典の行方と最後の王子を巡って壮絶な闘いを繰り広げる中、2人の少女が世界の存亡をかけて立ち上がる。破格の製作費5000万香港ドルで製作され、香港の03年度ナンバーワンヒットを記録。ジャッキー・チェン、イーキン・チェン、カレン・モクら香港の人気スターたちの共演も話題。", "2004-02-28", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lawman", "追跡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "法律に忠実に生きた、一人の孤独な保安官の姿を描いた異色西部劇。徹底して非情な保安官マドックスは、町の牧場主や世慣れたほかの保安官らと思いもかけない対決に巻き込まれていく。一瞬、昔の女の情に心が揺らいだ彼だが、結局は何人かを殺し、何人かを不幸にして、町を去っていくのだった。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr. Pilipenko and His Submarine", "ピリペンコさんの手づくり潜水艦", "", "", "http://image.eiga.k-img.com/images/movie/54798/315x210.jpg?4351", 90, "ウクライナの草原地帯で自作の潜水艦を作り上げた男に密着したドキュメンタリー。ウクライナの小さな村で年金生活を送る62歳のピリペンコの夢は、自力で作った潜水艦で黒海に潜ること。貯めた年金を切り崩しては古い部品を買い集め、30年に渡って独学で潜水艦を組み立てている。妻や村人たちに温かく見守られてきたピリペンコはついに潜水艦を完成させ、念願の黒海へと向かう。", "2009-11-14", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PLUMBER", "ザ・プラマー 恐怖の訪問者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "水道管の修理と称し、夫の留守中に毎日やってくる奇妙な配管工。彼は、浴室をメチャクチャに壊しながら、いっこうに直そうとはしない。苛立ち、怯える妻のジリー。彼女の神経は、極限まで張り詰めていく。心理的恐怖がジワジワと高まるサスペンス・ホラー。", "1979-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチバンVI 野獣降臨", "ガチバンVI 野獣降臨", "", "", "http://ecx.images-amazon.com/images/I/61Mi8Zz6apL._SL210_.jpg", 70, "", "2009-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LASSIE COME HOME", "名犬ラッシー／家路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "E・ナイトの原作による“ラッシー”シリーズの第１作。家の都合で売られてしまったラッシーが、はるか遠くから慕っている少年の待つ家に、様々な苦難を乗り越えてたどり着く……。戦時中に作られたこの映画は８年後、日本でも公開され大ヒットした。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIERCE CREATURES", "危険な動物たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1988年の「ワンダとダイヤと優しい奴ら」のキャストが再集結したコメディ。“モンティ・パイソン”のメンバーだったJ・クリースが製作・脚本・主演の３役をこなしている。大企業に買収されたロンドンのマーウッド動物園。本社から派遣された新任の園長や女重役、社長のバカ息子の３人は赤字続きの動物園の利益を上げるため、危険な動物である猛獣だけを飼育しようと計画。可愛い動物たちの排除を命令するが、大反対の飼育係たちは様々な作戦で動物たちを守り抜こうとする……。社長とその息子の二役を演じるK・クラインの芸達者ぶりも見もの。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("惜春", "惜春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "平岩弓枝の原作を映画化。上野・池の端で八代続く紐の老舗の３人姉妹。店を切り盛りする長女と次女・三女とは母親が違う。店の相続をめぐって波風がたつが……。丹後や奈良などの美しい背景も登場する、のどかなメロドラマ。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私と私", "私と私", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "スタッフとキャストに連なる名前を見れば、もうこれが歌謡ミュージカル・コメディであることは一目瞭然。東伊豆バスの女車掌と伊豆のゴルフ場の売店で働く売子は双子の姉妹である。そこに目をつけた芸能プロの社長は二人を歌手にし、ザ・ピーナッツと名付けてデビューさせる。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEXED", "ボディコン・モデルは殺しがお好き!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "過去を秘めたトップ・モデルと、その秘密を知ってしまったお調子者のホテルマンが繰り広げるドタバタ・コメディ。ホテルのフロント係マシューは、ホテルのリムジンを勝手に使ったり、客のスーツを拝借したりのチャッカリ者。超売れっ子モデル、ヘキシーナへの電話を取り次いだマシューは、待ち合わせの相手になりすましてヘキシーナとデート。ところが、彼女は整形美女だった。さらにはもっと恐ろしい秘密が隠されていて……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OLD SCORES", "ノーサイド 25年目のスクラム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ラグビーを題材にしたユーモラスな人間ドラマ。ラグビーのジャッジを務めた男の死の間際に残した言葉。それは25年前に行われたウェールズ・チーム対ニュージーランド、オール・ブラックス戦で、ウェールズに有利な判定をしたということだった。この衝撃的な発言を受け、ラグビー協会は当時とまったく同じメンバーで再試合を決定。25年を経て、選手たちも政治家、司祭、浮浪者と、様々な生活を送っていたが、再戦に向けて続々と集まり、鈍った体にムチを入れ、猛特訓へと臨んでいく……。実在するラグビー・チーム“オール・ブラックス”の面々も出演し、迫力あふれるゲーム・シーンを見せる。", "1991-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOMORROW 明日", "TOMORROW 明日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "昭和20年８月８日、長崎。この原爆投下の前日を生きた人々の、悲喜こもごもの人間模様を描いた作品。初夜を迎える夫婦、出産寸前の妊婦、召集令状によって引き離される恋人たち。明日を生きることを夢みて今日に生きた人間の姿を、黒木和雄が真摯に捉えている。ラストは原爆投下シーンのみで、各人物の夢や希望の途絶を思うと涙するしかない。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・冠婚葬祭入門", "喜劇・冠婚葬祭入門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "原作は塩月弥栄子のベストセラー『冠婚葬祭入門』というエチケット集である。これを前田陽一と宮川一郎が要領よく喜劇に仕上げた脚本によって出色の出来となった。定年間近のうだつの上がらぬサラリーマンにも、一つだけ取り柄がある。彼は冠婚葬祭の術を心得ており会社関係の冠婚葬祭となるや、俄然生気を得て大張り切りするのだった……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最高でダメな男　築地編", "最高でダメな男　築地編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "3人の監督が、それぞれ築地を舞台にだらしない男たちの人間模様を描くオムニバス映画。バツイチの人妻に恋する男を描く内田栄治監督の「築地で一番ダメな男」、ニートの寿司職人の成長を描く小澤雅人監督の「TSUKIJI流」、ダメな父親とその家族を描いた藤橋誠監督の「かんからちん」で構成される。主演はお笑い芸人・バナナマンの日村勇紀、加藤和樹、中野英雄。", "2010-04-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらば宇宙戦艦ヤマト 愛の戦士たち", "さらば宇宙戦艦ヤマト 愛の戦士たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "前作のヒットを受けて製作された、シリーズ初の劇場用オリジナル大作。かつてヤマトと戦い、敵ながら人気を集めた元ガミラス帝国総統・デスラーの再登場や、ヤマトが乗員とともに超巨大戦艦に特攻していくラスト・シーンなどがファンを大いにわかせて、第１作を上回るヒット作となった。ガミラス帝国との戦いから地球が再興した2201年。宇宙の覇者・白色彗星帝国の脅威が、再び地球に及ぼうとしていた。新たに土方を艦長に迎えたヤマトは、謎のメッセージを送ってきたテレザード星に向けて出発。そこで反物質世界の少女・テレサから、敵の正体を伝えられる。再び現れたデスラーの艦を打ち破り、彗星帝国軍を追撃するヤマト。すでに地球艦隊を壊滅させた敵に肉迫したヤマトは、波動砲を巨大な彗星の中心核に放つが、やがて帝国の恐るべき本体を知ることになる。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花婿は18歳", "花婿は18歳", "", "", "http://image.eiga.k-img.com/images/movie/54238/315x210.jpg?4011", 71, "「カフェ代官山」の監督・武正晴が年の差夫婦を描くラブ・コメディ。「カフェ代官山」の大河元気と元宝塚の純名りさが夫婦役を演じる。共演に「春琴抄」の長澤奈央、雑誌「ピチレモン」の人気モデル・加地千尋ほか。運命の赤い糸で結ばれた雅也と愛子だったが、愛子は新しく雅也の通う高校の校長職に就くことに。夫婦であることを秘密にする2人だったが、校内の新聞部が2人の関係を怪しみ始め……。", "2009-01-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dog Bite Dog", "ドッグ・バイト・ドッグ", "", "", "http://image.eiga.k-img.com/images/movie/34118/315x210.jpg?1457", 108, "暗い過去を背負った殺し屋と、彼への復讐に燃える刑事の闘いを描いた香港発のバイオレンス・アクション。殺し屋パンに同僚を殺されたワイ刑事は、捜査を続けるうちに、パンが幼少時から闘犬のように育てられてきたことを知る。一方、ゴミ埋立地に逃げ込んだパンは、そこで出会った少女ユウと惹かれ合っていく。殺し屋パンを「インファナル・アフェア」シリーズのエディソン・チャン、刑事ワイを「ピンポン」のサム・リーがそれぞれ熱演。", "2007-08-11", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HET JAAR VAN DE KREEFT", "ルトガー・ハウアーのキャンサー カニ座の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "年上の美しい女トニーに一途な思いを寄せる青年ピエール。だが、子持ちでだらしない夫がいる身のトニーは、ピエールの愛を受け入れようとはしなかった。R・ハウアーの、まれなる純情ラブ・ストーリーで、ファンには興味津々。", "1979-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BELL，　BOOK　AND　CANDLE", "媚薬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "キム・ノヴァクとジェームズ・スチュアート共演によるロマンティック・コメディ。ニューヨークで美術工芸店を営む魔女のギリアンが、上階に住む男シェパードに恋をする。ギリアンは魔術で彼の心を射止めるが……。魔法の力で結ばれた男女が、いつしか本当の恋に落ちるまでを軽妙なタッチで描出。キム・ノヴァクの妖艶な魅力が際立つ。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arvacska", "だれのものでもないチェレ", "", "", "http://image.eiga.k-img.com/images/movie/46618/315x210.jpg?4665", 89, "1930年代、ホルティ独裁政権下のハンガリーの農村で、富農に引き取られた孤児チェレは、過酷な労働と虐待に耐える日々を送っていた。ある日、家を飛び出したチェレだったが、再び孤児院に収容され、また別の農家へと養子に出されてしまう。そこでチェレは馬小屋に暮らす心優しい老人に出会うが、一家の秘密を握っていた老人は、チェレの養母に毒殺されてしまう。実話を基にしたジグモンド・モーリツの小説の映画化。1979年に日本で初公開され、2010年にリバイバル上映される。", "1976-01-01", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO VLEMMA TOU ODYSSEA", "ユリシーズの瞳", "", "", "http://ecx.images-amazon.com/images/I/41J8Z8CVQGL._SL210_.jpg", 177, "アメリカの映画監督Aが、故郷ギリシャから戦火のサラエヴォへ、バルカン半島最初の映画であるマナキス兄弟の幻のフィルムを探して旅する物語。時空を超えた1カットの中で、現在のサラエヴォ、自分の幼少時代の思い出、マナキス兄弟の記憶の中の数々の戦争をくぐり抜けながら、解体されたレーニン像とともにドナウ河を下る映画監督をハーヴェイ・カイテルが体現。何も見えない真っ白な霧の中で響く銃声をかいくぐり、最後に彼がスクリーンに見た幻のフィルムとは……。アンゲロプロス監督がカンヌで審査員大賞グランプリを受賞した巨編。", "1996-03-23", "2,4,27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAMIEN：OMEN II", "オーメン2 ダミアン", "", "", "http://ecx.images-amazon.com/images/I/41KMEWXFYML._SL210_.jpg", 107, "「オーメン」の続編。養父の弟夫婦に引きとられたダミアンは13歳に成長、内なる“力”の目覚めを感じるのだが、それは悪魔の子としての力であり、恐ろしい出来事を引き起こす……。女性記者の目を突いてつぶすなど、秘密を知った者を襲う、悪魔の使いのカラスが不気味。", "1979-02-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録・私設銀座警察", "実録・私設銀座警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "戦後のドサクサのなかで、自然発生的に誕生した銀座の暴力団の抗争の日々を荒々しく描く。焼け野原と化した銀座の飲み屋で４人の荒くれ男が出会い、銀座警察という名の暴力組織を形成する。やがて銀座警察は名実ともに銀座を制圧するのだが、つまらぬ内部紛争から自滅していく。安藤昇がギラギラしたヤクザ像を熱演。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN FIRE ： JUNGLE WOLF II", "ジャングルウルフ２　復讐の一匹狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "マーシャルアーツ出身のマッチョマン、R・マルチニ主演の「ジャングルウルフ」シリーズ第２作。戦火の南米から奇跡の生還を果たしたウルフ。だがサンフランシスコの友人は何者かに殺されており、預けておいた息子の姿はない。見えざる敵を探し、新たな戦いに挑むウルフだったが……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Arbitres", "レフェリー　知られざるサッカーの舞台裏", "", "", "http://image.eiga.k-img.com/images/movie/55508/315x210.jpg?4641", 77, "サッカーの試合を陰ながら支えるレフェリーの姿に迫るUEFA（欧州サッカー連盟）公認のドキュメンタリー。ヨーロッパのベスト・レフェリーに選出されたイギリスのハワード・ウェブ。彼がポーランド代表チームに下したジャッジが大きな波紋を呼び、ポーランド首相の「誰かを殺したい」という問題発言にまで発展してしまう。", "2010-05-22", "37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERR ARNES PENGAR", "吹雪の夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "16世紀のスウェーデン、国王の反乱罪で投獄された３人の兵士が脱獄する。彼らは吹雪の野を逃げながら、食物と金のためにある僧侶の一家を惨殺する。ただ一人生き残った娘エルサリルは事件の数日後、貴族のアーチーと知り合い恋に落ちるが、このアーチーこそ彼女の家族を殺した犯人であったのだ……。荒涼たる雪原をロケとセットワークを絶妙に組み合わせて作り上げた鬼気迫る画面や、登場人物の心理をオーバーラップを駆使して幻想的に描き出す手法などは、今見ても鮮烈である。スウェーデン神秘主義の巨匠M・スティルレルの不朽の名作として永遠に語り継がれるであろう映画史上に輝く金字塔だ。", "1919-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Silence de la Mer", "海の沈黙　デジタルリマスター版", "", "", "http://image.eiga.k-img.com/images/movie/55068/315x210.jpg?4195", 86, "「ギャング」「仁義」などの傑作フィルムノワールで知られるジャン＝ピエール・メルビル監督がベルコール原作の同名小説を映画化した長編デビュー作。1941年、ドイツ占領下のフランス地方都市。一人の老人とその姪が暮らす家に独軍将校ベルナーが同居することに。彼はフランス文化に造詣が深い作曲家で、独仏の文化的融和を2人に説くが……。巨匠メルビルが被占領下での人間の誇りを描いた戦争ドラマ。", "2010-02-20", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無宿人御子神の丈吉・ 川風に過去は流れた", "無宿人御子神の丈吉・ 川風に過去は流れた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "笹沢左保の原作を原田芳雄主演で映画化したシリーズ。一度は堅気の生活に入った無宿人御子神の丈吉であったが、妻子を惨殺されて再び渡世の道へと舞い戻る。そして仇である国定忠治とその仲間を追って復讐の旅を続ける。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OLDK", "OLDK", "", "", "http://ecx.images-amazon.com/images/I/31EHJP1PEKL._SL210_.jpg", 71, "「ヴァイブレータ」の廣木隆一監督が企画した“ラブコレクション”プロジェクトの一作。「美しい夏キリシマ」の助監督を務めた原正弘が初監督したブラック・ユーモアに満ちた女性映画の会心作。シチュエーション・コメディの要素も含まれている。会社では人間関係に迷い、恋する彼は婚約。自室では右隣からあえぎ声、左隣からへヴィメタル、上からは軋み音。そんな彼女の対抗策は……？", "2004-12-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小武", "一瞬の夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "政府の犯罪追放キャンペーンが厳しさを増すにもかかわらず、いまだスリで生計を立てる青年・シャオウー。友人の結婚式にも呼ばれずショックを受けた彼は、カラオケ・バーでメイメイという娘に出会う。彼女の歌声が心の琴線に触れ、シャオウーは将来への“夢”に目覚めるが、二人が抱える“現実”は容赦なく二人を引き裂く事に……。世界の三大映画祭をわずか31歳の若さで制した、中国映画界のカリスマ、ジャー・ジャック監督の長編デビュー作品。", "1999-12-31", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOGS IN HEAVEN", "ドッグ・イン・パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「ニュー・シネマ・パラダイス」でスターになった天才子役S・カシオが出演した、心温まる人間ドラマ。９歳のトムは、母親とその恋人ボブとともに、トスカーナ州にあるカバルビオ城にやって来る。しばらくここで暮らすことになった３人は、犬の一時預かり所を開業。城は、犬の天国となるが、この城を悪徳不動産業者が狙っていて……。", "1990-01-01", "4,2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’IMMORALE", "インモラル・獲物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "20歳の看護婦キャロルは美しいが引っ込み思案で平凡な女。しかし、そんな彼女が女友達に誘われ好奇心のおもむくままに夜の街に足を踏み入れたことから、いつしか高級コールガールとなっていった。社会の裏側の、金とエロスにあふれた世界に自らの肢体を沈めていくヒロインを、S・ラモーが妖艶に熱演。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("会社物語 MEMORIES・OF・YOU", "会社物語 MEMORIES・OF・YOU", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "定年を間近に控えた地味な中年サラリーマンの悲哀を市川準監督がペーソス豊かに撮り上げたヒューマン・ドラマ。クレージー・キャッツの面々が久々に顔をそろえて、主人公の定年の日（の翌日）ジャズの演奏を社員たちに披露する。並行して描かれるOL・西山由美の失恋エピソードも印象的だ。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Seconds", "セコンド／アーサー・ハミルトンからトニー・ウィルソンへの転進", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "　平凡で単調な毎日に不満を抱えていた銀行家ハミルトンは、ある秘密組織の手で生まれ変わることに。彼は手術を経て、画家のウィルソンという人物になり、新生活をスタートさせる。しかし過去と新しい人生が対立し、次第に彼を追い込んでいく。混乱するウィルソンがとった行動とは？　第二の人生が生む悪夢を描いたSFスリラー。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イズ・エー［is A．］", "イズ・エー［is A．］", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "無差別爆破殺人を犯した少年。爆破に巻き込まれ妻子を失った刑事。息子と自分の人生をリセットしようともがく少年の父親。復讐に燃える男と、それを阻止しようとする男という“二人の父”の苦悩をあぶり出し、出口のない現代社会犯罪の深層を見つめた意欲作。大人たちからすり抜けていく冷静な少年を「ロボコン」の小栗旬が、抑制の効いた演技で体現。渋谷で無差別爆破殺人事件が起こり、大量の死者が出た。ホーリー・ナイトと名乗る犯人は、多くのメディアで話題に。しかし捕まったのは14歳の少年。少年法によりたった４年で出所した彼を、事件で家族を失った刑事が追う。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EKSTASE", "春の調べ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "性の解放をテーマにし、全裸の女性が登場することから当時は世界的なセンセーションを巻き起こし、様々な検閲を受けることになった問題作。新婚の花嫁エバは夫に不満を感じ、実家に逃げ帰る。そこである青年と関係を持つようになってしまったが、ふとしたことからそれが夫に知られてしまう……。ビデオは67分の短編版。", "1932-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOST ANGELES", "LOST ANGELES", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "林海象が全編アメリカ・ロケを敢行して撮り上げた、虚実入り交じったエロス・ロード・ムービー。主演は林監督の前作「CAT'S EYE」に出演した原田喧太率いるロックバンドSYNNY CRUISERで、彼ら自身を演じる。アメリカ・デビューという夢を求めて渡米したSYNNY CRUISERのメンバー、原田、小池ヒロミチ、酒井麿の３人。だが、プロデューサーからの連絡もなく、ラスベガスで無一文になった彼らはアリゾナの砂漠に迷い込む。そこで彼らはインディアンらしき裸の娘と出会うが……。インディアン太鼓のミュージシャンとして世界的に知られるホピ族のインディアン、A・セカククが出演、音楽も担当している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Total Recall", "トータル・リコール（2012）", "", "", "http://image.eiga.k-img.com/images/movie/56068/315x210.jpg?2614", 0, "アーノルド・シュワルツェネッガー主演、ポール・バーホーベン監督による同名SFアクション大作（1990）をリメイク。", "2012-08-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獅子王たちの夏", "獅子王たちの夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "金子正次の遺稿シナリオを高橋伴明が映画化した念願の意欲作。Vシネマ「ネオチンピラ／鉄砲玉びゅ～」でも伴明とコンビを組んだ哀川翔と的場浩司が見えない糸で結ばれながらも、組織の抗争に巻き込まれ、破滅に向かっていく末端のヤクザを好演。映画化されるたびに高い評価を得ている金子作品であるが、今回の映画化では舞台やキャラクター設定など、金子脚本を現代風にアレンジしており、どちらかと言うと共同脚本としてクレジットされている西岡琢也のオリジナルに近い内容になっている。しかし、不幸なことに正月映画不調のあおりを受け、急遽封切り日が繰り上がり、宣伝もろくにされないまま公開された。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE IRON MASK", "鉄仮面", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "D・フェアバンクス主演で大ヒットを記録した「三銃士」の続編となる娯楽歴史活劇。王位を狙う奸臣一味は、本物の王子に鉄仮面をかぶせ、ニセ王子を仕立てる。正義の剣士ダルタニアンと三銃士はその陰謀を阻止すべく立ち上がった……。脚本を手掛けたのは、E・トーマス名義のD・フェアバンクス自身。", "1929-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("АННА КАРЕНИНА", "アンナ・カレーニナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "トルストイの原作を老匠ザルヒが淡々としたタッチで描く文芸大作。政府高官と政略結婚させられたアンナは、ふとしたことから知り合った青年将校と恋に落ち、駆け落ちするが……。サモイロワが好演。名プリマ、プリセツカヤも特別出演。", "1968-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Karate Kid, Part II", "ベスト・キッド2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "　空手ボーイ、ダニエルの闘い、そして彼とミヤギの師弟愛を描く大ヒットシリーズの続編。父危篤の報を聞いたミヤギは、ダニエルとともに沖縄へ向かうことに。そこでミヤギを待っていたのは、ライバルのサトーだった。一方、ダニエルはミヤギのかつての恋人、ユキエの姪にあたるクミコに心惹かれていく。そんな中、サトーの弟子チョーゼンがミヤギの道場を襲撃。ダニエルはそれに立ち向かうが……。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Swedish Love Story", "スウェーディッシュ・ラブ・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/53568/315x210.jpg?2581", 114, "スウェーデンの巨匠ロイ・アンダーソン監督が69年に手掛けた長編デビュー作。ストックホルム郊外の療養所にいる祖父の元を訪れた15歳の少年ペールは、そこで見かけた美しい少女・アニカに一瞬で心を奪われる。2人は友人の紹介を経て急速に親しくなり……。幼い2人の恋を瑞々しいタッチで綴り、ベルリン国際映画祭で絶賛された感動作。後に「ベニスに死す」に出演するビョルン・アンドレセンが、端役ながらスクリーンデビューを飾っている。", "2008-04-26", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STRANGER", "ストレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "O・ウェルズの作品としては珍しいシンプルな構成の作品。コネチカット州に逃げ込んだナチ戦犯が、偽名を使い結婚してから、正体を暴かれ死に至るまでの最期の日々を描いたもので、ヒッチコックの「疑惑の影」の影響を受けた作品ともいわれている。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHADOW　OF　DOUBT", "疑惑の幻影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "巨大な権力に立ち向かう女性弁護士の奔走を、スリリングに描いたサスペンス。資産家の令嬢が殺害され、前夜の情交相手だった気鋭のラップ・シンガーが逮捕された。容疑者の弁護に当たったやり手の女性弁護士キットは、無罪の証拠集めに奔走。事件には次期大統領候補と目される、資産家の友人一家が絡んでいるらしい。圧力にもめげずキットは調査を続け、意外な事実を突き止めるが……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOME　SWEET　HOBOKEN", "ホーム・スウィート・ホーボーケン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ニューヨークを拠点にする日本人映像クリエーター、細谷ヨシフミが米国で撮りあげたオフビート・コメディ。マンハッタンを望む小さな町ホーボーケンを舞台に、宝石をめぐって巻き起こる争奪戦をコミカルに描く。ベスおばあちゃんが亡くなった夫からもらった赤いダイヤの指輪が、1000万ドルの価値があることが発覚。ぐうたらなおばあちゃんの孫、宝石商、伝説の大泥棒らがあの手この手で指輪を手に入れようと奔走するが、おばあちゃんのガードは鉄壁で……。細谷監督がインディペンデント映画界の雄、ジョン・カサヴェテスに捧げた作品で、カサヴェテス組の常連でもあった名優B・ギャザラが往年の大泥棒役で出演したことも話題になった。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CADDYSHACK", "ボールズ・ボールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "フロリダの名門ゴルフ場を舞台に、伝統もテクニックも無視した不動産成金やプレイガールが登場。ハチャメチャな笑いを繰り広げる。「アニマル・ハウス」のスタッフが製作、監督、脚本を担当したドタバタ喜劇。地ネズミ“ゴーファー”の大活躍が笑いを誘う。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MERCANO　EL　MARCIANO", "火星人メルカーノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "南米アルゼンチンから届いた、ブラックな視点の光る長編アニメーション。首都ブエノスアイレスを舞台に、現代のアルゼンチンが抱える貧富の格差や失業問題から、テクノロジーに支配された世界情勢までを徹底的に皮肉った抱腹絶倒の物語が展開する。「サウスパーク」を彷彿とさせる一編だ。地球で寂しく暮らす火星人のメルカーノは、インターネット上に火星のバーチャル世界を作る。ところが、これに悪徳企業が目をつけ……。", "2005-02-26", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("An American Carol", "最終突撃取材計画！", "", "", "http://ecx.images-amazon.com/images/I/518JokjmD9L._SL210_.jpg", 83, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD IS THE WIND", "野性の息吹き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "妻を亡くした中年のイタリア系移民の男ジーノと、後妻として迎えられたジオイア、そして養子のベンとの三角関係を軸にしたドラマ。亡き妻を思うジーノ、夫から顧みられないジオイア、そして年上の女の魅力の虜になっていくベン……。それぞれの人間の心の壁に迫っていくかのような、G・キューカーの演出が見事。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美徳のよろめき", "美徳のよろめき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "三島由紀夫による同名の原作をもとに、名シナリオライター・新藤兼人が脚色して映画化された作品。名門の家に育ち、親の決めた相手と結婚して子供をもうけた節子は、やがて夫に内密で禁じられた交際を始めるのだが……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大森林に向って立つ", "大森林に向って立つ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "南アルプス。津山木材が所有する路線を乗っ取ろうとたくらむ神戸産業は、津山木材の人夫を一人残らず引き抜いていまう。仕方なく集められた臨時の人夫は、風来坊、賭博好き、酒飲みといったありさま。だが津山木材に恩があるヤクザの伊吹がやって来て、山は急に活気づき……。アルプスの広大な自然を背景にしたアクションもの。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURPHY’S ROMANCE", "マーフィのロマンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "オスカー女優S・フィールド主演のロマンティック・コメディ。離婚したばかりのエマは牧場で馬を育てようと、息子とアリゾナに移り住む。そこで風変わりな薬剤師と知り合い、奇妙な友情で結ばれるが、そこへ前の夫がやって来て三角関係に……。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大奥　右衛門佐・綱吉篇", "大奥　右衛門佐・綱吉篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "よしながふみの人気コミックを映画化した「大奥」（2010）の続編。前作より過去にさかのぼり、ひとりの女将軍に3000人の美しい男たちが仕える女人禁制の大奥がどのように成立していったのかを、TVドラマ版「大奥　有巧・家光篇」（12年10月～12月放送）との2部構成で描く。徳川5代将軍綱吉の時代。公家出身の謎の男・右衛門佐（えもんのすけ）が綱吉の夫にあたる御台信平（みだいのぶひら）に呼ばれ大奥に入る。綱吉の側室候補であったが年齢を理由にそれを辞退した右衛門佐は、代わりに大奥総取締役の座を望むが……。主演はドラマ版「有巧・家光篇」の主人公・有巧（ありこと）役とあわせて1人2役となる堺雅人。菅野美穂がヒロインの綱吉に扮する。", "2012-12-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN TO SING", "青きドナウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ウィーン少年合唱団の少年たちをモデルに描いた音楽映画。声変わりのために世界公演に参加できなくなりそうになった年長団員と、それをかばう新入団員の心の交流。その事情を知った講師の心温まる配慮。合唱団の美しいハーモニーの中で繰り広げられる感動的な物語。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("McDull, Kung Fu Kindergarten", "マクダルのカンフーようちえん", "", "", "http://image.eiga.k-img.com/images/movie/57218/315x210.jpg?2131", 0, "", "2012-08-31", "76,8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOLOMON AND SHEBA", "ソロモンとシバの女王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "旧約聖書に有名なソロモン王とシバの女王の物語を、K・ヴィダーが描いた史劇スペクタクル。紀元前のイスラエル。エジプトとの戦いに勝利を収め、ソロモンは王位へと就いた。女王のマグダは、色仕掛けで彼に改宗を迫るが、神の怒りが下る。そこへエジプト軍が奇襲をかけて来た。何とかこれに勝利するソロモンだったが……。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀河鉄道999／ エターナル・ファンタジー", "銀河鉄道999／ エターナル・ファンタジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "名作アニメの17年ぶりの続編。TVシリーズ“ゲゲゲの鬼太郎”“金田一少年の事件簿”などの宇田鋼之介が監督を担当、前作から１年後を舞台に、鉄郎とメーテルの新たな旅を描き出す。キャプテン・ハーロックなどの懐かしいキャラクターの登場も見逃せない。新政府によって平和に繁栄した近未来の地球。地下世界の支配者によって危険分子として捕らえられていた星野鉄郎は、処刑寸前に銀河鉄道999号に救われる。メーテルと再会した鉄郎は、グレードアップした999号で再び宇宙への旅に出発。金属生命体メタノイドと地獄の聖母騎士ヘルマザリア、そして宇宙を支配しようとする闇の支配者に立ち向かう。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジュテーム　わたしはけもの", "ジュテーム　わたしはけもの", "", "", "http://image.eiga.k-img.com/images/movie/54148/315x210.jpg?4442", 109, "淡々とした毎日を送るフリーターの由佳は、思い描く理想と現実がかけ離れていることに悩んでいた。なんとか自分を変えたいと思う由佳は、街で出会った女性から紹介されたコールガールの仕事を始めることに。地味で内気だった由佳は、男を魅了する美しい女に変身していくのだが……。主演は「シルク」「鴨川ホルモー」「カムイ外伝」の芦名星。", "2008-11-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばり・橋の花と喧嘩", "ひばり・橋の花と喧嘩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "美空ひばりと橋幸夫という大御所同士の初共演による歌謡娯楽映画。唄あり恋あり笑いありの作品。学生時代の初恋を胸に秘め潮来を訪れた自動車セールスマンが、その恋人に再会し愛をつかむまでを描く青春編。美空ひばりが３役、橋幸夫が二役をこなすというハデな趣向が見もの。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狄仁杰之通天帝国", "王朝の陰謀　判事ディーと人体発火怪奇事件", "", "", "http://image.eiga.k-img.com/images/movie/57868/315x210.jpg?2228", 128, "オランダ人外交官で小説家のロバート・ファン・ヒューリックが、中国の「公案小説」をモデルに執筆した推理小説「ディー判事」シリーズを、香港のツイ・ハーク監督が映画化。中国・唐王朝の時代。女帝・則天武后の即位を目前にひかえたある日、人体が突然発火して焼きつくされるという事件が連続して発生する。7人の犠牲者はすべて政権を司る重要人物で、比類なき知性を持ち武術の達人である判事ディー・レンチェは、相棒のドンライとともに事件を解決するため捜査にあたる。主演のアンディ・ラウほか、リー・ビンビン、レオン・カーフェイ、カリーナ・ラウら香港・中国のスターが集う。", "2012-05-05", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富夫", "富夫", "", "", "http://image.eiga.k-img.com/images/movie/57088/315x210.jpg?4796", 60, "ホラーコミック界を代表する古賀新一、伊藤潤二、御茶漬海苔の3人が、各々の代表作を自らのメガホンで実写化した「古潤茶」プロジェクトの第2弾。「富江」シリーズの伊藤潤二が原作・脚本・監督を務め、男性主人公・富夫が体験する不条理な恐怖を描き出していく。主演は「高校デビュー」の古川雄輝。", "2011-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SQUEEZE PLAY", "スクイズ・プレイ／恋の一発大逆転", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "恋人や夫がソフトボールに熱中してまったく相手にしてもらえず、ムクれた女たちが女だけのチームを結成、男どもに挑戦するスラップスティック映画。濡れたTシャツのお色気、スピーディーなギャグ……。30万ドルの製作費で800万ドルの収益を上げたといわれる。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダーリンは外国人", "ダーリンは外国人", "", "", "http://image.eiga.k-img.com/images/movie/54868/315x210.jpg?4167", 100, "小栗左多里の人気コミックエッセイを、「花より男子ファイナル」の井上真央主演で映画化。漫画家を夢見るイラストレーターのさおりは、漢字の美しさに一目ぼれして来日したアメリカ人・トニーと出会い、交際を始める。しかし、2人は日本とアメリカの文化の違いを実感し、やがて心はすれ違い始める。", "2010-04-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死霊の罠２−ヒデキ−", "死霊の罠２−ヒデキ−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "恋人に捨てられ、堕胎を体験したことから対人恐怖症に陥っている亜紀。勤め先の映画館に棲みつく謎の少年ヒデキの死霊が、友人のTVリポーター絵美に取り憑き、亜紀に襲いかかる。TVドラマ『ふぞろいの林檎たち』で人気を呼んだ中島唱子主演によるスプラッタ・ホラー。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ござまれじ", "ござまれじ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "才人、秋元康企画の深夜番組『24ピース』に参加した24人の監督のストーリーが、最終的に一つの物語として成立するという新感覚ホラー。インターネットで日本一恐い怪談があると知った３人の女子高生。彼女たちは、ほんの好奇心からその怪談について調べ始める。しかしやがて、彼女たちの周囲で不可解な事件が続発。どうやら「ござまれじ」という言葉に秘密が隠されているようで……。秋元康を筆頭に、タレントや俳優、スポーツ選手など、バラエティーに富んだ監督陣の演出術が興味深い。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA LECTRICE", "読書する女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ある日友だちから“あなたのキレイな声を生かさない手はない”と指摘されたヒロイン、マリーは、さっそく新聞に“お宅で朗読します”という広告を出す。お客となったのは下半身不随のデリケートな青年やら、劣等感に悩む中年実業家など５人の、ひと癖もふた癖もある人物ばかり。若く魅力的な彼女には事件が絶えない。間断なく流れるヴェートーヴェンの名曲の数々に合わせて、モーパッサンなど客に応じた名作の一節を朗読する彼女の様子がロマンティックに、そしてエロティックに描かれている。風変わりな切り口と、ブルーを基調にしたミュウ・ミュウのセンスの良いファッションが受けて世界中で大ヒットした。モントリオール映画祭でグランプリを受賞。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダーX", "仮面ライダーX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 24, "秘密工作機関GODに立ちむかう仮面ライダーXの活躍を描いた特撮テレビドラマの劇場版。テレビ版第3話「暗殺　毒ぐも作戦!!」を再編集し、「東映まんがまつり」の1編として劇場公開。GODの怪力改造人間ヘラクレスが、来日中のアダブ王国石油大使を暗殺するべく動き出した。偶然この計画を知った少女がヘラクレスに命を狙われたところを、間一髪で敬介が救う。敬介は計画を阻止するため、仮面ライダーXとなってヘラクレスに戦いを挑む。", "1974-03-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版テンペスト3D", "劇場版テンペスト3D", "", "", "http://image.eiga.k-img.com/images/movie/57078/315x210.jpg?1337", 149, "激動の琉球王国を舞台に、男として生きる運命を背負った女の波乱万丈の物語を描く歴史ドラマ。舞台化、TVドラマ化もされてきた池上永一の同名ベストセラーの3D映画化で、舞台版、ドラマ版に続き仲間由紀恵が主演を務める。19世紀の琉球王国。家の再興という父の願いをかなえるため、自ら名を孫寧温とあらため、男子として王宮に仕える身分となった真鶴は、外交や財政改革で手腕を発揮し、異例の出世をとげていく。しかし、やがて王宮内の権力争いに巻き込まれ、女であることが明かされてしまう。", "2012-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG TRAIL", "ビッグ・トレイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "トーキー初期に、200万ドルの巨費を投じて製作された超大作西部劇。インディアンの襲撃、大河横断、猛吹雪などを乗り越えて、希望の地オレゴンを目指す大幌馬車隊の苦難の旅を描いている。西部劇の巨人J・ウェインの記念すべき初出演作だが、本作が興行的に失敗したため、彼は1939年のJ・フォード監督作「駅馬車」までB級男優として雌伏の時を過ごさねばならなかった。またこの作品は、“グランデュール方式”と呼ばれる70ミリ版も同時に製作されており、後に隆盛をきわめる大型映画の先駆けとしても記憶されるべきもの。1985年にニューヨーク近代美術館がグランデュール版の復元を果たしている。", "1930-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Proof", "プルーフ・オブ・マイ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/1768/315x210.jpg?2823", 103, "デビッド・オーバーンのピュリッツァー賞受賞戯曲を「恋におちたシェイクスピア」の監督＆主演コンビ、ジョン・マッデンとグウィネス・パルトロウで映画化。天才数学者だったが精神のバランスを崩した父を看病し、死を看取ったキャサリン。彼女は父が証明しようとした定理を証明したいと考える一方で、自分も父と同じ病になるのではないかという不安を抱く。アンソニー・ホプキンスとジェイク・ギレンホールが共演。", "2006-01-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パラレル", "パラレル", "", "", "http://image.eiga.k-img.com/images/movie/54028/315x210.jpg?3223", 88, "2008年北京パラリンピックで日本選手団長を務めた京谷和幸とその妻の実話を、要潤＆島谷ひとみ主演で映画化。Jリーガーの和幸は、婚約者・陽子との結婚式を目前に控えたある日、交通事故で危篤状態に陥ってしまう。命は取り留めたものの車椅子での生活を余儀なくされた和幸は絶望の淵に立たされるが、車椅子バスケットのコーチ・近藤に誘われパラリンピックを目指すことを決意。陽子の愛に支えられながら練習に励むが……。", "2009-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shrek Forever After", "シュレック　フォーエバー", "", "", "http://image.eiga.k-img.com/images/movie/54258/315x210.jpg?1802", 93, "ドリームワークス・アニメの大ヒットシリーズ最終章。「遠い遠い国」で平和に暮らす怪物シュレックだったが、長年「遠い遠い国」の王座を狙っていた魔法使いランプルスティルスキンの罠にはまり、もう一つの「遠い遠い国」に送り込まれてしまう。何が何だかわからないシュレックは、フィオナ姫ほか、仲間たちに声をかけるが、誰もがシュレックを知らないという……。監督はディズニーのファミリー映画「スカイ・ハイ」（05）を手掛けたマイク・ミッチェル。", "2010-12-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents うそ　「shoelace」", "桃まつり presents うそ　「shoelace」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 26, "", "2010-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHO’S AFRAID OF VIRGINIA WOLF", "バージニア・ウルフなんかこわくない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "「卒業」で知られるM・ニコルズが描く、壊れかけた夫婦の愛と憎しみの物語。アメリカ・ニューイングランドの小さな大学構内の住宅。そこには、すでに冷え切った関係の教授夫婦ジョージとマーサが住んでいた。ある日二人の元へ、この大学の生物学教師ニックとその妻ハニーがやって来た。若くてハンサムなニックに興味を抱いたマーサは彼をベッドに誘うが、ジョージは見て見ぬふりをする。そんな二人の唯一の絆は息子のジムであったが、なぜかジムのことは他人に語られることがなかった……。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Johnny Suede", "ジョニー・スエード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "マンハッタンのボロアパートに住み、ロカビリー歌手を目指す青年ジョニーは、ある日、空から降ってきたスエード・シューズを手に入れる。この靴を手に入れた日から、彼の周囲には様々な事件が起こり始める……。ジム・ジャームッシュ作品の撮影監督として知られるT・ディチロが自ら監督したキッチュな青春映画。1991年ロカルノ国際映画祭でグランプリ受賞。", "1993-03-31", "1,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／SILVER　STALLION", "銀馬将軍は来なかった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "日本でも活躍していたイ・ヘスクが、入魂の演技を見せる人間ドラマ。朝鮮戦争のさなか、クムサン里という山あいの部落に、米軍の部隊がやってくる。二人の子持ちの若い未亡人、オルレは、米軍兵士にレイプされ、それがもとで村八分にされてしまう。やがて彼女は、子供たちと生き抜くために、米兵相手の娼婦になり……。戦争が起きることによって、人生を一変させてしまう女性、村人たちの痛ましい姿を、痛烈なタッチで描く。モントリオール映画祭主演女優賞、脚本賞を受賞。ビデオ発売時タイトルは「シルバースタリオン　銀馬将軍は来なかった」。", "1991-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RANSOM", "身代金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "「アポロ13」に続いてR・ハワードが手掛けたサスペンス巨編。強気のビジネスで全米トップ５に食い込む躍進を遂げた航空会社の社長トム・ミューレンは、９歳の息子ショーンを誘拐され身代金200万ドルを要求される。金の受け渡しに失敗し、犯人を取り逃がしたFBIに見切りをつけたトムは、用意した200万ドルを賞金にすることを決意。TVの生放送で大胆にも犯人に宣戦布告した……。頭脳明晰な犯人像は珍しくないが、“取引”に長けた主人公は新鮮。肝心の二人の対面以降は凡庸だが、前半のスリルはかなりのもの。特に身代金受け渡しのたたみかけるようなシークエンスの連鎖は絶品だ。TVドラマをもとにした「誘拐」（1956）のリメイク作。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLER TOMATOES EAT FRANCE", "キラートマト 赤いトマトソースの伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "“アタック・オブ・ザ・キラートマト”シリーズ第４作。飽きもせず(？)トマトが人間を襲うナンセンス・ギャグとパロディーの連発。今度はキラートマト軍団がフランス征服をたくらむ。それを阻止せんと立ち上がる青年の名は、マイケル・J・フォックスである！", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Made in Jamaica", "メイド・イン・ジャマイカ", "", "", "http://image.eiga.k-img.com/images/movie/53708/315x210.jpg?2779", 109, "ジャマイカで生まれたレゲエ・ミュージックの魅力に迫ったドキュメンタリー。ボブ・マーリーらが結成したオリジナル・ウェイラーズのメンバーであるバニー・ウェイラーをはじめ、伝説的アーティストから最先端のアーティストまで豪華な面々が熱狂的なライブ・パフォーマンスを繰り広げる。さらに彼らの貴重な証言を通し、世界中に影響力を持つレゲエ・ミュージックが人口300万人ほどの小さな国からどのようにして生まれたかを解き明かしていく。", "2008-07-05", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マンハッタン・キス", "マンハッタン・キス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ニューヨークに住む姉の春子を訪ねた夏子。彼女の目的は不倫相手の中年男・大川と逢うことだったのだが、その大川が実は姉の元恋人だと判明し……。監督２作目の秋元康が、全編ニューヨークロケで描いたラブ・ストーリー。「グッバイ・ママ」同様、奇をてらわぬ正攻法の演出を見せる。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le nottei bianche", "白夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "　ドストエフスキーの同名短編小説の映画化。オールロケが多いルキノ・ビスコンティ作品には珍しく、全編スタジオで撮影された。イタリアの港町に住む青年がある晩、橋のたもとで悲しみに暮れる美しい女性と出会う。離ればなれになった恋人を探すため毎晩同じ場所で待ち続ける彼女に、青年は魅かれていく。舞台俳優をしていたマルチェロ・マストロヤンニがビスコンティに才能を見出され、主役に抜擢された。1957年ベネチア国際映画祭銀獅子賞受賞。", "1958-12-31", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METELLO", "わが青春のフロレンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "『家族日誌』などで知られるイタリアの作家、B・プラトリーニのベストセラー小説『メテッロ』を映画化した文芸映画の名作。20世紀初頭のフロレンス（＝フィレンツェ)。若い労働者のメテッロは、社会主義者の友人の影響を受け、階級意識に目覚め、次第に労働運動の闘士に成長していく。その間に彼は、年上の未亡人と恋に落ちて、別れた後、仕事仲間の娘、エルシリアと結婚する。メテッロは組合運動で何度か投獄され、ストライキ中に隣家の若い女と関係を持つが、最後には彼をひたむきに待ち続けるエルシリアへの愛を見いだす。20世紀初頭の時代色が見事に再現され、新人ラニエリとピッコロの演技が新鮮である。", "1970-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　RECORD", "REC〈レック〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "山奥の別荘でひ弱な同級生に悪戯を仕掛けようとし、誤って本物のナイフで刺し殺してしまった５人の高校生。一年後、そのことを秘密にしようと誓った彼らに降りかかる惨劇を描く。ハリウッド映画「ラストサマー」を彷彿とさせる韓国産のショッキング・ホラー。殺人鬼に狙われる若者たちの恐怖を、新人K・ギフン監督が荒削りなまでに豪快な演出で映像化している。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PICKPOCKET", "スリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "R・ブレッソンが「抵抗」に続いて発表した異色作。ソルボンヌの貧しい学生ミシェルは、手先が器用なことからスリをして生計をたてている。そうするうちに病みつきになり、腕を磨いてプロのスリになった。ジャンヌは、そんなミシェルにひそかに思いを寄せるのだが……。前作「抵抗」で脱獄の克明な描写に徹したブレッソンが、この作品ではスリの驚くべき手口の数々をドキュメンタリー・タッチで、凝視する。競馬場でのスリ、アパートの一室で上着から財布を抜き取る練習、地下鉄の車内とプラットホームでのスリなど、スリのテクニックの多彩さには驚かされる。出演者は、ほとんどのブレッソン作品がそうであるように全員が素人で、映画の中でスリの頭目を演じ、同時にスリの演技指導を行っているのは、有名な魔術師のカッサジである。撮影はパリ市内と北駅などでロケされた。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEONARD， PART６", "ビル・コスビーのそれ行けレオナルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "全米で人気のコメディアン、B・コスビーが出演した、製作費約40億円を投じた爆笑スパイ・コメディ。ベジタリアンの悪女が、長年人類の食用にされてきた動物たちの怨みを利用し、地球の征服を狙う。元秘密諜報部員は伊勢海老やダチョウとの戦いを強いられるが……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪猫お玉が池", "怪猫お玉が池", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "山道に迷った忠彦と啓子は、ふと現れた猫を追って廃屋にたどり着く。そこは100年前、厳しい年貢に苦しむ百姓たちの訴えを伝えた名主を悪代官が殺害し、娘を襲って死に至らせるという惨事が起きた屋敷の跡で……。橘外男の原作『私は呪われている』を翻案した、新東宝得意のお化け映画。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le streghe", "華やかな魔女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "　イタリア人監督による5つのオムニバス映画。「山猫」のルキノ・ビスコンティ、「デカメロン」のピエル・パオロ・パゾリーノ、「ひまわり」のビットリオ・デ・シーカなどそうそうたる顔ぶれがメガホンをとった。第1話はビスコンティの「疲れ切った魔女」。お忍びで友人の誕生日パーティにやってきた女優が、倒れてしまう。妊娠していることも公にできず、心とは裏腹に素顔を笑顔で隠してマスコミの前に現れる。シルバーナ・マンガーノが全エピソードで主演している。", "1967-12-31", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大紅燈籠高高掛", "紅夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "　1920年代、19歳のスンリェンは大富豪ののもとに第4夫人として嫁ぐ。主人が絶対的な権力を持つ屋敷内は、夫の寵愛を受けるために暗躍する女の策略と裏切りがはびこり、さながら中国版「大奥」。赤の色彩美を追求するチャン・イーモウらしく、本作でも真っ赤な提灯は愛と性の象徴として描かれている。ベネチア国際映画祭銀獅子賞受賞。", "1991-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴスロリ処刑人", "ゴスロリ処刑人", "", "", "http://image.eiga.k-img.com/images/movie/55528/315x210.jpg?4446", 88, "人気グラビアアイドルの“オシリーナ”こと秋山莉奈が、母のかたきを討つゴスロリ少女を体当たりで演じたスプラッター・アクション。優しい父母のもとで幸せな日々を送っていた少女ユキ。だが、突如現われた黒マント5人組によって母が惨殺されてしまう。復しゅうを誓ったユキは、漆黒のゴスロリファッションに身を包み、黒マントたちを1人ずつ追い詰めていく。監督は「お姉チャンバラ THE MOVIE」のアクション監督を務めた小原剛。", "2010-09-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("なみだ川", "なみだ川", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "山本周五郎の名作『おたふく物語』を三隅研次が情緒豊かに演出した佳編。江戸・日本橋を舞台に、長唄の師匠、おしずと仕立屋のおたかの姉妹が、お互いの幸せを願って見せる美しい姉妹愛を描く。藤村志保と若柳菊が、姉妹に扮し好演。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEEP　BLUE　SEA", "ディープ・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「ドリヴン」のR・ハーリンが手掛けた海洋アクション。太平洋上に浮かぶ海洋医学研究施設アクアティカ。ここでサメの脳組織を抽出して新薬を作り出す研究が続けられていた。だが、投資家を招いて実験を行っている時、DNA操作で知力も体躯も異常発達したサメたちが人間に襲いかかってくる。外は暴風雨が接近し、救助隊を呼ぶこともできない。沈んでいく施設の中で、人間たちと凶暴なサメたちの戦いが繰り広げられていく。最新のアニマトロニクスとCGを使って画面上に再現されたサメたちのリアルな動きが恐怖感を一層盛り上げる。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MADAME CLAUDE", "マダム・クロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "米・仏の政財界をスキャンダルに巻き込んだマダム・クロードは実在の人物。パリ上流社交界の裏に君臨し、各国のVIPに女を送り込んで彼らのネタを仕入れる彼女の姿をサスペンスフルに描く。J・ジャカン監督による、美しい映像が見もの。", "1977-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しなの川", "しなの川", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「同棲時代」のヌードが当時、評判になった由美かおるをヒロインに、野村芳太郎監督が女の愛の遍歴をしっとりと描く。良家の娘の奔放な生き方の描写より、ヒロインの和服姿や全裸の美しさが際立つ出来ばえ。岡崎英生・作、上村一夫・画の同名人気劇画の映画化。DVDは「由美かおる　DVD-BOX」に収録。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("≒〈ニア・イコール〉森山大道", "≒〈ニア・イコール〉森山大道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1960年代後半から写真界のカリスマ的存在として知られる写真家、森山大道の姿をデジタルビデオで追ったドキュメンタリー。マスコミへの登場を避けてきた彼の素顔を、友人でもある荒木経惟や評論家、西井一夫らが披露する。森山本人もロング・インタビューで自らの半生や作品に対する思いを語り、資料としての価値も高い。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("迷い猫", "迷い猫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "ピンク四天王の一人、サトウトシキ監督が人妻の心理に一風変わった構成で迫った作品。夫を殺した妻の独白を新聞記者が聞くという設定で、彼女がなぜ殺したのかを、退屈しのぎの売春から虚無的な不倫に自身のアイデンティティーを見出していく過程を丁寧に綴ることで暴き出している。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Band Wagon", "バンド・ワゴン", "", "", "http://ecx.images-amazon.com/images/I/51eFvGLVKHL._SL210_.jpg", 112, "人気落ち目のダンサーが、友人夫妻に書いてもらったミュージカル・コメディ“バンド・ワゴン”を、大バレリーナとの共演で上演しようとするが……。数あるアステア作品のなかでもモダンで洗練された一編で評価が高い。特に『ガールハント・バレエ』のダイナミックなナンバーが圧巻だ。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香香閙油坊", "香香〈シャンシャン〉の油屋騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "売買婚で結婚した香香(シャンシャン)は、不能の夫と姑のいびりに耐えかね自力で離婚を勝ち取った村の人気者。また彼女が売られることを案じた村長は、閉鎖されていた胡麻油工場を再開させ、香香を働かせることに。だが、近代化を進めようとする彼女に、伝統を重んじる職人たちは反発し様々な事件を企み彼女を阻んでいく。山奥の農村を舞台に、自力で離婚を勝ち取ったヒロインが騒動を巻き起こす痛快コメディ。", "1994-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Movie Relation", "Movie Relation", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "高林陽一監督の「愛なくして」などの撮影監督として知られる、としおかたかおの監督第３作。亡くなった父親への思いを込めた「言葉・ビデオ・記憶」、訳あって結婚できない男女の姿を描いた「恋愛映画あるいはカメラ」の２作品を上映。前作「−less」に出演した高城ツヨシが主演を務めている。お互い母親を抱えているため、結婚できない男女。傷つけ合いながらも関係を続けていたが……。（「恋愛映画あるいはカメラ」）", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パ★テ★オ", "パ★テ★オ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "PART１、PART２をTV放映、完結編を劇場公開するという、初の試みによるサスペンス・ミステリー。謎の秘宝“パテオの赤いバラ”を求め、菊池桃子扮する旅行添乗員の真理と、加勢大周演じる考古学者の優が、冒険を繰り広げる。香港、シドニー、ジャカルタ、インドネシア、そして日本。各地に点在する、パテオ伝説をつなぎ合わせた、その結末とは……。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聖戦風雲／UNDECLARED WAR", "聖戦 UNDECLARED WAR", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ヨーロッパと香港を結ぶ国際的テログループに対し、真っ向からぶつかり合う香港警察とCIAの活躍を壮大なスケールで描いたバイオレンス・アクション。ポーランド当局の全面協力のもと現地でロケを敢行、実弾を使った銃撃シーンが圧巻だ。スクリーンに久々に登場したハッセーが、テロリストの主犯格の役で起用されているのにも注目。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("U−TURN", "Uターン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "O・ストーンがB級映画の線を狙ったという犯罪ドラマ。マフィアに借金を返す旅の途中、車の故障によって砂漠の町に足止めを食らったボビー。強盗の被害に遭い、金を失った彼は町の不動産業者に誘われ、その妻の殺害を引き受ける。だが、殺すべき妻の妖艶さに魅せられたボビーは、逆に彼女の夫の殺害を企み……。瞬時のイメージ・カットの挿入や、斜に構えたカメラ、人物の超アップなど、シャープな映像が「ナチュラル・ボーン・キラーズ」を思わせる。クセ者俳優たちの遊び心たっぷりな共演も見どころ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アサルトガールズ", "アサルトガールズ", "", "", "http://image.eiga.k-img.com/images/movie/54768/315x210.jpg?2532", 70, "「イノセンス」「スカイ・クロラ」の押井守監督による実写SFアクション。仮想ゲーム空間に集う戦士たちが幻のモンスターを追って戦う姿を描く。主演に黒木メイサ、菊地凛子、佐伯日菜子。仮想空間「アヴァロン（f）」内の砂漠のステージにグレイ、ルシファ、カーネル、そしてイェーガーという3人の女と1人の男のプレイヤーが集う。それぞれに巨大モンスターのスナクジラを狩る4人は、幻の大物を仕留めるため一時共闘することになるが……。", "2009-12-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　PRICE　ABOVE　RUBIES", "しあわせ色のルビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "自分らしさを取り戻そうと運命を切り開いていく女性をR・ゼルウィガーが好演する人間ドラマ。ユダヤ教の厳しい戒律の中で、息苦しさを感じているソニア。宝石鑑定の仕事を始めた彼女は、生きがいを見いだし始める。そんな彼女を理解できない夫との心の溝は次第に深まっていくが、ソニアは自分を信じて歩み始める。ルビーにまつわる幻想的なエピソードを交えながら、自由を求めるヒロインの心情を丹念に追った良質のドラマ。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和枯れすすき", "昭和枯れすすき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "兄は刑事、妹は不良少女。屈折した兄妹愛を描いた好編。実に典型的、類型的な設定だが、事象を追っていく淡々とした演出によっておのおのの思いを静かに表現している。真面目に洋裁学校へ通っているはずの妹が、街を遊び回っていた。実は学校を勝手に中退していたのだ。事実を知った兄は妹を責めるが、妹は耳を貸さない。そんな時、殺人事件が起こる。被害者は妹のボーイフレンド、しかも現場には妹のネックレスが落ちていた……。サスペンス仕立てにはなっているが、ここで焦点となるのはむしろ兄妹の愛憎と確執。低予算の小品ながら、キリリと心にしみてくる。新宿の繁華街を活写する、川又昂のアクチュアルなカメラも秀逸。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みんなあげちゃう・", "みんなあげちゃう・", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "夜空の星の間から突如現れたウルトラの母の教えによって、近く出会うとされる恋人にすべてを捧げることを誓った高校生の悠乃が、双眼鏡に映ったオナニーにふける予備校生を相手に奮闘努力する様を描く。人気マンガの原作の持ち味を生かした爆笑コメディ。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOUND", "バウンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「暗殺者」で脚本家デビューを果たしたウォシャウスキー兄弟の初監督作。マフィアの金を盗み出そうとたくらむ二人の女性の姿を、ひねりの利いたストーリー展開で見せる。５年の刑期を終えたばかりの盗みのプロ、コーキーは、マフィアのボスから彼の所有するアパートの修理を頼まれる。隣の部屋に住んでいるのはマフィアの一員シーザーとその情婦ヴァイオレット。レズビアンのヴァイオレットは魅惑的なコーキーに惹かれ、やがて二人は激しく求め合う。数日後、組織の会計士が200万ドルを横領したことが発覚。ヴァイオレットはその金を横取りして一緒に逃げようとコーキーに持ちかける。二人は周到な準備の下、計画を実行に移すのだが……。主人公の二人の女性には「ブロードウェイと銃弾」でアカデミー助演女優賞候補となったJ・ティリーと「ショーガール」のG・ガーション。彼女らの関係がもたらすエロティシズムと、過激なバイオレンス描写が作品に圧倒的なパワーを与えている。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カミュなんて知らない", "カミュなんて知らない", "", "", "http://image.eiga.k-img.com/images/movie/1318/315x210.jpg?3457", 115, "暴走族を描くドキュメンタリー「ゴッド・スピード・ユー！ BLACK EMPEROR」でデビュー、「さらば愛しき大地」、中上健次脚本の「火まつり」などを撮ってきた柳町光男監督が、2000年に起きた男子高校生による老婆刺殺事件をモチーフに描く青春群像劇。都心の大学生たちが、この事件の犯人の「人を殺したらどうなるか、実験してみたかった」という証言を元にこの事件の映画化を試みていく。", "2006-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Good Night, and Good Luck.", "グッドナイト＆グッドラック", "", "", "http://image.eiga.k-img.com/images/movie/1408/315x210.jpg?3484", 93, "1950年代初頭、ソ連との冷戦が激化するアメリカ。マッカーシー上院議員らによる共産主義者を排斥する“赤狩り”が実施される中、TVの人気ニュースキャスター、エド・マローと番組スタッフたちは、自由を守るために権力に立ち向かう。全編モノクロ撮影。ジョージ・クルーニーの「コンフェッション」に続く監督第2作は、受賞は逃したもののアカデミー賞作品賞、監督賞、主演男優賞を含む6部門にノミネート。", "2006-04-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小城之春", "春の惑い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "中国の古典映画「小城之春」をリメイクした恋愛劇。「青い凧」のティエン・チュアンチュアン監督が、微妙に揺れ動く男女３人の恋愛心理を、幽玄なムード漂う映像でじっくりと描き出す。1946年の蘇州。ダイ家のリーイェンと若妻ユイウェンの元に、上海の青年医師チーチェンが現れる。かつての初恋の人との再会に戸惑う彼女の心に、忘れかけていた恋の激情がよみがえる。その夜、ユイウェンは彼の部屋を訪ねる。ヴェネチア映画祭コントロ・コレンテ部門サン・マルコ賞受賞。", "2002-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒薔薇の館", "黒薔薇の館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「黒蜥蜴」に続く女装の麗人・丸山明宏主演の異色作。黒薔薇の館に君臨する謎の美女と、組織を裏切って人を殺し、組織と警察の両方から追われる青年との死をも恐れぬ絶対の愛を描く。前作は三島由紀夫の舞台劇の映画化だったが、今回は映画用のオリジナル・ストーリー。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЧЕЛОВЕК С КИНО АППАРАТОМ", "これがロシアだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "現実を記録することよりも、記録する手段としての映画の可能性を追求した映画史上に輝く前衛ドキュメンタリー。原題は「カメラを持った男」であり、そのカメラを抱えたM・カウフマンが1920年代の高揚するソビエトの街をめぐりながら、人々の生活やモスクワの風景を記録していく。彼が撮影する姿、そこで撮影されたショット、そしてそれらが編集され映写機にかけられていくまで……、つまり映画が一つの作品として誕生するまでが描かれていくのだ。その過程で、スローモーションやコマ落とし、多重露光、逆転撮影などあらゆる映画技法が駆使される。史上初の映画言語についての映画ともいえるだろう。", "1929-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遺産相続", "遺産相続", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "下町の中小企業の社長が急死。遺産は想像以上に多く50億円を超え、それには遺書がないため法律通りに分配されることになる。この会社を大きくしたのは専務の喜久恵だったが、内縁の妻であるため彼女に遺産が入って来ないと知り……。内縁の妻と本妻の遺産をめぐる闘いを緊張感をはらんでリアルに描いていく。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OKLAHOMA KID", "オクラホマ・キッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "流れ者オクラホマ・キッドの活躍を描いた西部劇。開拓者に解放されたオクラホマの肥沃な土地に、悪名高いマコードが強引に賭博場を開いた。街の浄化を図る父を殺されたオクラホマ・キッドは、兄ネッドとともに、憎きマコードとその一派を追い詰めていく。ワーナーの当時の二大スター、J・キャグニーとH・ボガートの共演が見もの。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忘れてはイケナイ物語り・オキナワ", "忘れてはイケナイ物語り・オキナワ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 38, "「火垂るの墓」の原作者、野坂昭如による短編集『戦争童話集』。これに感銘を受け、全12話の映像化を実現した画家、黒田征太郎の原画によるスチール・アニメーションと、黒田自身のドキュメンタリーで構成された作品。黒田の膨大な原画は、すべて１コマ１コマ、フィルムに焼き付ける手作業により撮影された。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THEORY　OF　THE　LEISURE　CLASS", "ファットマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "アリゾナ州レイクマナーに暮らす主婦キャリーが何者かに襲われ、彼女の子供たちが殺される。やがて激昂したキャリーの夫は容疑者の男を銃殺。さらに観光旅行中のイタリア人夫婦が、死体となって発見され……。何もかもが退屈な田舎町で起こった奇妙な殺人事件の顛末を、回想形式で描く異色ミステリー。主人公の中年女性キャリーの“嘘”が明らかになるにつれて物語は意外な方向へ転がり、不条理な心理ドラマの様相を呈していく。ビデオ、DVDタイトルは「セオリー・オブ・マーダー」。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千姫と秀頼", "千姫と秀頼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "徳川家康を祖父に、豊臣秀頼を夫に持つ不遇の姫君・千姫。徳川家の政略の犠牲となった彼女の数奇な生き様をロマン豊かに描き上げた時代劇。千姫役の美空ひばりを、秀頼役の萬屋錦之介、時代劇初出演の高倉健らが盛り立てている。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("基因決定我愛你", "DNAがアイ・ラブ・ユー", "", "", "http://image.eiga.k-img.com/images/movie/54018/315x210.jpg?3207", 95, "台湾シネマ・コレクション2008上映作品。「ターンレフト・ターンライト」「軍鶏　Shamo」のテリー・クワン、「トゥヤーの結婚」「スピード・レーサー」のユー・ナンが出演するラブコメディ。DNAに影響を与える新薬の研究者メラニーは、肥満DNAを持っているため肥満体質で、太ることに怯えて暮らしている。潔癖症のDNAを持つ研究所員のジジは、過度な潔癖症なため恋愛がうまくいかない。彼女たちは科学の力で恋愛を成就させようとするのだが……。", "2008-08-23", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GROW 愚郎", "GROW 愚郎", "", "", "http://image.eiga.k-img.com/images/movie/34178/315x210.jpg?1983", 108, "謎の不良中年3人組との出会いを通して成長していく高校生の姿を描いた青春映画。長編初監督となる俳優・榊英雄がメガホンを取る。学校ではいじめられ、家では父親から暴力を振るわれる冴えない高校生の敦は、自殺を図ろうと訪れた廃墟で、どう見ても中年なのに学ランに身を包んだ奇妙な男たちに出会う。口は悪いが義理人情に厚い彼らと接するうち、少しずつ自信を取り戻していく敦だったが……。寺島進、菅田俊、木下ほうかが中年3人組を熱演。", "2007-09-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本やくざ伝・総長への道", "日本やくざ伝・総長への道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "高崎前田一家の代貸、不動竜太郎が関東前田一家の総長に推されたことから、総長の跡目相続をめぐって一門の中で熾烈な争いが始まる。「昭和残侠伝」に続く健さんの新シリーズ第１作として作られたが、東映ヤクザ映画の衰退期であとが続かなかった。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ODESSA FILE", "オデッサ・ファイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "1963年、ハンブルクでルポライターのミラーは自殺した老人の日記から偶然に元ナチスの殺人組織“オデッサ”の存在を知り、その実態を探り始める。元収容所長ロシュマン大尉の行方を追うミラーは、ついに彼と対決するが……。フォーサイスのベストセラーを映画化。", "1974-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN NOS OLA LEUAD", "ワン・フルムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "英国ウェールズ地方の無垢な自然を背景にした、不思議な少年映画。貧しい暮らしの中でひたすら信仰に傾倒する母親の影響を受け、信仰の象徴や儀式に魅了される少年。物語は主人公がそんな自分の少年時代を回想する形式で、過去と現在が交錯しながら展開。やがて不可解な少女殺害事件へと発展していく。繰り返し映し出される満月や、手招きする天使のイメージ場面が幻想的なムードを醸し出す。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE SERPENT", "エスピオナージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "Y・ブリンナー、H・フォンダ、D・ボガードら世界の大スターの競演によるスパイ・サスペンス。KGB高官がスパイ・リストを手みやげに西側への亡命を申し出た。CIAはこのリストをもとに捜査を開始するが、リスト上の政府要人たちが次々と殺害されはじめた。", "1973-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dr. Seuss' The Lorax", "ロラックスおじさんの秘密の種", "", "", "http://image.eiga.k-img.com/images/movie/56728/315x210.jpg?3326", 0, "「グリンチ」「ハットしてキャット」などの映画化作品で知られる児童文学作家ドクター・スース原作による3Dアニメーション。環境破壊が進んだ世界で、最後の一粒となる木の種をめぐり少年テッドと不思議な森の住人ロラックスが繰り広げる冒険を描く。クリス・ルノー監督、プロデューサーのクリス・メレダンドリら「怪盗グルーの月泥棒 3D」のスタッフ陣が集う。", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN　SPE´CIALISTE", "スペシャリスト・自覚なき殺戮者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "ナチス・ドイツのホロコーストの実行責任者として、数え切れないほどのユダヤ人を抹殺したアドルフ・アイヒマン。1961年のイスラエルで行われた彼の戦争犯罪をめぐる裁判の模様を、克明に記録したドキュメンタリー。冷酷非情な大量殺人者のイメージからは想像できないほど、姑息な弁明に終始するアイヒマンの小役人ぶりが強烈な印象を残す。", "1999-01-01", "2,7,37,77,46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCENER UR ETT A¨KTENSKAP", "ある結婚の風景", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 168, "幸福な結婚生活を続けて10年。ヨハンとマリアンヌ夫妻は、このうえなく満ち足りた日々を送っていた。ある日、地元新聞社から、模範的結婚生活についてインタビューを受ける。数日後、活字になった二人についての記事は、空虚でありきたりな、なんとも味気ないものに感じられ、その時から夫婦の間に、少しずつ見えない溝が広がり始めていく……。全編ほぼ二人だけの出演者による、すさまじいダイアローグのぶつかり合いで、人間にとって結婚とは何か、夫婦生活と個人の人間性の調和とは何か、厳しく問いかけてくる。当初５時間のTVシリーズであった本作品は、再編集された後、劇場公開となった。", "1973-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STARSHIP TROOPERS2: HERO OF THE FEDERATION", "スターシップ・トゥルーパーズ2", "", "", "http://ecx.images-amazon.com/images/I/41P9FD6ZM4L._SL210_.jpg", 92, "全人類と昆虫型宇宙生物の全面戦争を描き、熱狂的なファンを持つSFアクションの７年ぶりの続編。戦争をカリカチュアすることで反戦を匂わせた前作の“魂”を継承しつつ、密室ホラーのテイストも取り入れた１本だ。前作にも参加した“特殊効果の神様”P・ティペットの監督デビュー作としても要注目。シェパード将軍の歩兵隊は、宇宙生物バグズとの激戦で全滅寸前。逃げ込んだ無人の砦でたった一人、監禁されていたダックス大尉を発見するが……。", "2004-06-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮本武蔵・二刀流開眼", "宮本武蔵・二刀流開眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "高倉健の佐々木小次郎が初登場する。柳生石舟斎の指導を受けようと訪ねてきた宮本武蔵は、石舟斎の高弟たちと対決する。そのとき聞えてくるお通の笛の音。武蔵は心が乱れ窮地に陥るが、瞬間両刀を構える。それから京へ上り、吉岡清十郎と洛北蓮台寺野にて１対１で対決し、勝利を得る。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Herencia", "オリンダのリストランテ", "", "", "http://image.eiga.k-img.com/images/movie/54038/315x210.jpg?3239", 96, "2001年モスクワ国際映画祭の最優秀作品賞を受賞した女性監督パウラ・エルナンデスによるアルゼンチンのヒューマンドラマ。ブエノスアイレスの小さなレストランの女主人オリンダは、女手ひとつで店を切り盛りする元気な中年女性。ある日、昔の恋人を探し歩くドイツ人青年のピーターと出会う。当てもなく彼女を探し続けるピーターを放っておけないオリンダは、食事と宿をピーターに提供する。", "2008-11-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗黒街最後の日", "暗黒街最後の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "関西犯罪シンジケートのボスが設立した企業の利権をめぐり、ボスの弟分、関西犯罪シンジケートが激烈なギャング戦争を展開。これを鎮圧するため、密令を受けた敏腕警部補がシンジケートに潜入する。鶴田浩二をはじめ、オールスターをそろえたキャスティングが見もの。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("でっかい太陽", "でっかい太陽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "利根川を挟んで長年対立し、何かにつけて喧嘩を繰り返している北高と南高。南高に赴任したばかりの英語教師・由木真介は、河原で喧嘩している両校の生徒を発見、劣勢にあった北高の生徒の方に味方してしまう。怒った南高の校長は彼の採用を取り消すが……。TVの青春ドラマから飛び出したキャラクターの先生が活躍する学園もの。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("正午なり", "正午なり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "丸山健二の同名小説を映画化した後藤幸一の監督デビュー作。都会での生活に疲れて帰郷した若者が、生きる根拠を見出せないまま過ごす日々を、映画初出演の金田賢一主演で描く。俳優の長門裕之が製作者に名を連ね、漫画家の福地泡介が脚本を手掛けたのも話題に。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太王四神記", "新編集版　太王四神記　第4章：かなわぬ想い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "韓流スター、ペ・ヨンジュン主演の人気テレビドラマ「太王四神記」を劇場用に新たに編集した4部作の第4章。ついに火蓋を切ったタムドクとホゲの直接対決。キハへの愛や母の仇をタムドクへの闘志に変えたホゲの猛攻撃により、タムドク軍は絶対絶命の窮地に陥ってしまう。一方、タムドクの子どもを身ごもったキハの元に、大長老が放った刺客サリャンが迫る。同じ頃、スジニもキハの命を奪うべく動きだしていた。", "2010-06-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄の警備員", "地獄の警備員", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "“人を守るはずの警備員が逆に襲ってきたら……”。黒沢清が現代社会の片隅に潜む身近な恐怖を描いたスリラー。とある商事会社に新入社員の秋子と巨漢の警備員・富士丸がやって来る。やがて、同僚や残業中の社員を次々と殺す富士丸。秋子は閉ざされたビルの中で彼と死闘を繰り広げる。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Traviata", "World Classics＠CINEMA 椿姫", "", "", "http://image.eiga.k-img.com/images/movie/55108/315x210.jpg?2753", 134, "世界のオペラやバレエをデジタル映像化し大迫力の映像で楽しめる「World Classics＠CINEMA」シリーズの1作で、イタリア最高峰と称されるミラノ・スカラ座で2007年に上演されたベルディ作曲による名作オペラ「椿姫」をスクリーン上映。高級娼婦ビオレッタと、彼女を愛しながらも父親に仲を引き裂かれてしまうアルフレードの悲恋を描く。ビオレッタに扮する世界的オペラ歌手アンジェラ・ゲオルギューの迫真の演技に注目。", "2010-03-20", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOURNEY TO THE CENTER OF THE EARTH", "地底帝国の謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "SFの古典、ヴェルヌの『地底旅行』を「エレクトリック・ドリーム」の脚本家レモランドが脚色、現代に設定を移して映画化。ハワイで犬のお守をすることになったクリスティーナが、知り合った兄弟と洞窟に落ちた。しかし、そこにはアトランティス王国があった……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VILLA SANTO SOSPIR", "サント・ソスピール荘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 38, "南仏、サン・ジャン・カプ・フェラにあるサント・ソスピール荘に残されたJ・コクトーの壁画を、作者自身が紹介するドキュメンタリー・フィルム。後援者の別荘であるこの建物に、コクトーは1950年から翌年にかけて滞在し、部屋や廊下に多くの壁画を描いた。映画ではその一つ一つをコクトーが解説する。また、庭園やヨットで遊ぶコクトーの姿なども収められており、プライベート・フィルムとしての要素も見どころの一つ。", "1952-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Go Master", "呉清源　極みの棋譜", "", "", "http://image.eiga.k-img.com/images/movie/53068/315x210.jpg?1848", 107, "中国から来日し昭和の囲碁界で伝説的存在となった天才棋士・呉清源の半生を、「春の惑い」のティエン・チュアンチュアン監督が映画化。主演は「グリーン・デスティニー」のチャン・チェン。昭和3年、北京で囲碁の天才少年と呼ばれた14歳の呉清源が日本へやって来た。彼は日本でも実力を発揮し、瞬く間にトップ棋士の仲間入りを果たす。やがて清源は日本囲碁界を牽引する存在となっていくが、その一方で日中関係は悪化して行き……。", "2007-11-17", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOTRE−DAME DE PARIS", "ノートルダムのせむし男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ユゴーの有名小説の映画化。15世紀のパリを舞台に、鐘つき男カジモドの、美しい娘エスメラルダに寄せる恋心を、切なく叙情たっぷりに綴っていく。当時の町並みを再現した壮大なセットが素晴らしい。巨匠ドラノワが手掛けた、文芸大作。", "1956-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志・初祝い清水港", "次郎長三国志・初祝い清水港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "次郎長に預けられた喜代蔵少年にお仲は、次郎長と結婚して自分の“おっかぁ”になってくれとせがまれ、困ったお仲は旅へ出る。死んだお蝶の百カ日、次郎長一家は酒盛りでフグの毒にあたり、動けないところを久六一味に襲われるが、女たちの必死の奮戦でなんとか久六を撃退する。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サラリーマン清水港", "サラリーマン清水港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "時代劇をパロッたサラリーマン喜劇。酒造会社の大手・清水屋は社長の山本長五郎はじめ“清水28人衆”と知られた社員たちで伸びいく会社。ライバル・黒駒醸造と新製品合戦を繰り広げているが、長五郎親分は少々浮気者で……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍道 SHINOBIDO", "忍道 SHINOBIDO", "", "", "http://image.eiga.k-img.com/images/movie/57188/315x210.jpg?3093", 93, "栃木県日光市のテーマパーク「江戸ワンダーランド　日光江戸村」が開園25周年を記念して製作した時代劇アクション。泰平の世が訪れ、使命を終えた忍たちは里で静かに生きていた。しかし、忍たちの力を恐れ、その存在を抹消しようと企む者たちが隠密組織「黒羽衆」を差し向ける。女忍のお甲は、族長の命に従い黒羽衆の侍と目される東五郎を密かに調査するが、次第に東五郎に対して恋慕の情を抱いてしまう。主演は「鈍獣」「nude」の佐津川愛美。共演に人気アイドルグループ「AKB48」の菊地あやか。", "2012-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロケみつ THE MOVIE このさきのむこうに ", "ロケみつ THE MOVIE このさきのむこうに ", "", "", "http://image.eiga.k-img.com/images/movie/57948/315x210.jpg?3352", 0, "2012年・第4回沖縄国際映画祭の長編プログラム「Laugh」部門にて上映。", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("祭りだお化けだ全員集合!!", "祭りだお化けだ全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "浅草の老舗の料亭“川きん”で働く４人は、板前の長吉にイビられっ放し。ついに耐えきれず逃亡するが、結局戻ってくる。長吉は４人を海辺に連れていき、海へ突き落としてしまう。救助された４人は幽霊に化けて長吉に復讐を開始……。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが愛の記", "わが愛の記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "戦争中に重傷をおった身体の不自由な兵士との結婚を陸軍病院の看護士が綴った実話小説の映画化。看護士のさと子が、患者として病院に入院中の戦傷兵・山田と結婚しようと決意するきっかけから、両親の反対、そして最終的には幸せに結婚するに至るまでを描いた、国策色の濃厚な作品。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Narc", "NARC ナーク", "", "", "http://image.eiga.k-img.com/images/movie/1558/315x210.jpg?4400", 105, "話題のジョー・カーナハン監督は69年生まれ。地元TVの予告編編集から脚本家に進出し、監督・脚本・主演した低予算映画「ブラッド・ガッツ」（97）が話題を呼び、BMWのサイトで配信された競作短編映画に、ジョン・ウー、トニー・スコットら人気監督とともに参加した注目株。本作は94年に彼が書いた短編用脚本を長編にしたもの。麻薬取締のため囮捜査をする刑事ニックとヘンリー。2人は囮捜査官を殺した犯人の捜査を命じられるが、その過程でそれぞれの秘められた過去が浮かび上がる。", "2003-05-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香華（前編・後編）", "香華（前編・後編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 201, "「永遠の人」以降「今年の恋」という喜劇を除けば、以前のように優れた作品を続々と発表することもなく、どちらかといえば低迷した状態にあった木下惠介が久々に手掛けた力作。原作は、舞台化され評判にもなった有吉佐和子の同名ベストセラー。60年以上にも及ぶ母娘二代の波乱に富んだ人生を描く超大作である。20歳で後家になった郁代は娘・朋子を連れて再婚したが、夫の親との折り合いが悪く子供を連れて家出。貧乏生活の果てに朋子は半玉として売られる。朋子13歳の時、母・郁代が花魁(おいらん)となって現れ、17歳の時、伯爵に水揚げされる。やがて、芸者置屋を始めたり旅館を開業したり、淫蕩で虚栄心の強い母に痛めつけられながらも、黙々と生きていく女性像が、あざやかに浮かび上がっていく。木下流女性映画の真骨頂。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪獣島の決戦 ゴジラの息子", "怪獣島の決戦 ゴジラの息子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "南海のゾルゲル島を舞台にゴジラの息子ミニラが初登場し、怪獣カマキラスやクモンガと対決する。この作品から特技監督が名匠・円谷英二から弟子の有島貞昌にバトン・タッチされた。ゴジラとミニラの親子の愛情がしっかり描き込まれている。", "1967-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪夢探偵", "悪夢探偵", "", "", "http://image.eiga.k-img.com/images/movie/33808/315x210.jpg?3858", 106, "国内外から注目を集める塚本晋也監督が、松田龍平を主演に迎えて放つサイコサスペンス。自宅で眠ったまま切り刻まれるという連続殺人事件が発生。事件を解く鍵は夢にあると推理した女刑事・慶子は、他人の夢の中に入りこむ特殊能力を持つ“悪夢探偵”こと影沼京一に協力を求めるが……。ヒロインの女刑事に扮するのは、本作が映画初出演のhitomi。共演には安藤政信、大杉漣、原田芳雄ら豪華俳優陣が顔を揃える。", "2007-01-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・喜びも悲しみも幾歳月", "新・喜びも悲しみも幾歳月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "木下監督自身が1957年に作った「喜びも悲しみも幾歳月」のあとを受けて、現代の灯台守夫婦とその周辺を描いた松竹大船撮影所50周年記念映画の一つ。伊豆・石廊崎の風景が美しく、植木等が老け役に味を見せた。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブサイコ　狂惑のホラー", "ラブサイコ　狂惑のホラー", "", "", "http://ecx.images-amazon.com/images/I/51FM33NWMML._SL210_.jpg", 102, "オリジナルのホラー作品をウェブ上で配信する「角川ホラーシネマ」の第3弾となるオムニバスホラー「ラブサイコ」の「狂惑のホラー」編。「輪廻」で清水崇と共同脚本を手がけた安達正軌がメガホンを取る。人気作家・吉村達也の短編を原作に、定年退職を迎えた男が長年連れ添った妻から想像を絶する手紙とプレゼントを受け取る「11037日目の夫婦」ほか、女性たちの愛と狂気を題材に描いた全4話を収録。出演は「チルソクの夏」の水谷妃里ほか。", "2006-07-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不連続殺人事件", "不連続殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "いずれも元夫婦、元愛人などの愛憎深い関係にある十数人の男女が隔離された山奥の豪邸に集まり、そこで狂宴を繰り広げるが、やがて奇怪な連続殺人事件が起こる。終戦直後の混乱期を背景に、特権階級の俗物エリートたちの狂態を描きだした犯人当てミステリー・ドラマ。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HISTORY IS MADE AT NIGHT", "歴史は夜作られる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "アメリカの海運王ブルースの妻アイリーンは夫の嫉妬深さに耐えられなくなり、離婚訴訟を起こしてパリに旅立ってしまう。ブルースは彼女の敗訴を狙って策を弄するが、ピンチの彼女をホテルの給仕長ポールが救う。以来、アイリーンとポールは親しくなっていく。それを知ったブルースは今度は殺人事件を仕組み、ポールを犯人に仕立て上げ、自分のところに戻って来るならポールを助けてやろうとアイリーンを脅迫。仕方なく、アイリーンはパリを去るのだが……。J・アーサー、C・ボワイエという当時の人気俳優の共演によって作られたラブ・ストーリー。二転三転するストーリー展開の中に、二人の恋の行方をリリシズムあふれるタッチで描いていく。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誰も守ってくれない", "誰も守ってくれない", "", "", "http://image.eiga.k-img.com/images/movie/53118/315x210.jpg?4552", 118, "大ヒットシリーズ「踊る大捜査線」の製作チームが、殺人事件の被疑者家族として言われのない社会的制裁を受ける15歳の少女（志田未来）と、彼女の保護を命じられた中年刑事（佐藤浩市）の逃避行をドキュメンタリータッチで描いた社会派サスペンスドラマ。監督・脚本は「MAKOTO」「容疑者　室井慎次」の君塚良一。第32回モントリオール世界映画祭では最優秀脚本賞を受賞した。", "2009-01-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鳳凰王子", "新桃太郎３ 聖魔大戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "桃から生まれた桃太郎といえば日本古来のおとぎ話だが、カンフーとSFXを加え、アクション・パワーをアップさせた「新桃太郎」が台湾で映画化され大ヒットを記録。“逆輸入”という形で日本でも同シリーズの２作品が公開されている。その、さらに奇想天外なシリーズ第３作。武蔵という仙人と知り合ったことから仙界と魔界の争いに巻き込まれる桃太郎たちの冒険を、香港映画顔負けの妖術合戦やカンフー・アクションをたっぷり盛り込んで描いている。前２作同様桃太郎役を演じるのは、当時、台湾きっての人気女性といわれたリン・シャオロウ。", "1988-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("留守女士／LEDIES LEFT BEHIND", "さよなら上海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "出国熱高まる大都市・上海で、夫がアメリカ留学中の女医が、やはり妻が出国してしまったタクシードライバーと出会い、やがて愛を育み始めるのだが……。取り残された男女の微妙な心情を都市の風俗の中にえぐり出した現代青春映画。中国映画第６世代の新鋭フー・シュエヤンのデビュー作である。", "1991-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ああ爆弾", "ああ爆弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "昔かたぎのヤクザ、大名組の大名大作は、３年ぶりに刑期を終えて出所してみると、組が市会議員に立候補する新興ヤクザ、矢東に乗っとられているので驚く。怒り狂った大作は、単身矢東のところへ乗り込むが、簡単に追い返され、幼なじみのシイタケこと椎野に助けられる。シイタケは矢東の運転手で、毎週銀行に行くという。そこで大作は、子分に命じて万年筆爆弾を作らせ矢東を殺そうとするが、ひょんなことからそれが矢東には渡らず、あちこちを転々とする……。大作のバックには浪曲や御詠歌、矢東のバックにはモダンジャズやワルツを流すなど、BGMの使い方も秀逸な岡本喜八の痛快作。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤いグラス", "赤いグラス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "渡哲也扮する一等航海士が、久しぶりに航海から帰って来ると恩人の海運会社社長が殺され、渡は相棒のアイ・ジョージとともに事件の真犯人を探し始める。デビュー間もない渡哲也の主演だが、モノクロでユニークな彼の一面が観られる作品。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hitchhiker's Guide to the Galaxy", "銀河ヒッチハイク・ガイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "宇宙のバイパス工事のために地球が消滅。その寸前、フツーの英国人アーサーは、親友のベテルギウス星人デントとともに宇宙をヒッチハイクする旅に出て、2つの頭を持つ躁病気質の元銀河大統領ザフォドの宇宙船へ。彼らは「人生、宇宙、すべてについての答」を知るのだが……。原作はモンティ・パイソンの脚本も書いたダグラス・アダムスが、自作ラジオドラマを小説化したSFコメディ。監督は音楽クリップ出身のガース・ジェニングス。", "2005-09-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拳神／THE AVENGING FIST", "拳神／KENSHIN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "2030年の香港。遺伝子研究の驚異的な進歩によって、人間の脳を覚醒させるパワーグローブの開発が成功したが、被験者の一人、ジン21が副作用の影響で悪への野望を目覚めさせてしまう。それから20年後、亡き父が生み出した最強の武術“神拳道”を習得したクァンは、かつて父の同僚だった刑事からパワーグローブに関する真実と、亡くなったはずの父がジン21の部下となって生きていることを知らされる。ジン21はパワーグローブと“神拳道”を合体させて世界征服を狙っており、そのためにクァンの姉ベルを誘拐したのだ。クァンは仲間たちとともにベル救出に向かうが……。久しぶりの共演となるユン・ピョウとサモ・ハンが脇を固める。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIEWINNI CZARODZIEJE", "夜の終りに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "平和が訪れた戦後のポーランドで、目的を喪失し、空しい毎日を過ごす若者の青春群像にスポットを当てた巨匠ワイダの異色作。いつものテーマを前面に押し出す力強い作風とは異なり、行きずりの男女の心の触れ合いとすれ違いを、ソフトで繊細なタッチで描いている。", "1961-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白浪五人男 天下の大泥棒", "白浪五人男 天下の大泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "八丈に島流しになっていた主人公が、島で死んだ老人がいまわの際に残した言葉から、島を抜け出して“謎の壷”を求めて花のお江戸に現れる。森繁・フランキー・アチャコなど“五人男”の顔ぶれの面白さに加えて女優陣の多彩さも見ものの喜劇。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE METAL YEARS ： THE DECLINE OFWESTERN CIVILIZATION PART II", "ザ・メタル・イヤーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "オジー・オズボーンやアリス・クーパーらロック界の大御所たちが続々登場。彼らとメガデス、オーディンほかの若きアーティストたちを対比させることによって反体制的といわれるロック・ムーブメントの移り変わりを正面から見据えた野心作。彼らのライブとインタビュー・シーンを中心に構成されている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太と竜の騎士", "ドラえもん のび太と竜の騎士", "", "", "http://ecx.images-amazon.com/images/I/31CRHSKMK9L._SL210_.jpg", 91, "偶然、地下に巨大な空洞を見つけたのび太は、みんなを連れて探検に向かう。ところがスネ夫が行方不明になってしまった。スネ夫を探すうち、のび太たちは地球征服をもくろむ地底人と戦う事になる。そんな中、巨大すい星が落下し……。戦いの場でのび太たちが立てこもる城の名は「風雲ドラえもん城」で、当時ビートたけしらが出演し人気を博したテレビ番組が由来だ。", "1987-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WARLOADS 3000", "アトミックレイザー3000", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "地球が人類によって破壊された近未来。不毛の地での人々の唯一の逃げ場は“シード”と呼ばれる強力な麻薬だけだった。その“シード”を支配する権力者たちに、立ち向かおうとする男がいた……。主演のJ・ロバーツは、何本かの格闘技映画に出ているが現職の警官でもあった。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門捕物帖 拾万両秘聞", "右門捕物帖 拾万両秘聞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "大阪城内の“竹流し分堂金”十万両を江戸へ運ぶ一行が鈴ヶ森で煙にまかれ、十万両は煙とともに消え去る。奪回の命をうけたむっつり右門、煙の仕掛けで評判の芝居小屋が怪しいとにらみ……。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BELOW UTOPIA", "アリッサ・ミラノの堕落の園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "セラピストのスーザンは恋人ダニエルに招かれ、彼の一家が住む屋敷を訪れるが、そこに強盗団が現れ、一家は皆殺しになってしまう。生き残ったスーザンとダニエルは一味のすきを見て屋敷の中を逃げ惑う。やがて二人や強盗一味にとって意外な事実が発覚する……。A・ミラノが製作総指揮を務め、体当たり演技にも挑戦したサスペンス。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ここに泉あり", "ここに泉あり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 176, "群馬県高崎市の群馬交響楽団が、様々な困難を克服して設立されていった過程を、ポピュラーな名曲や童謡などを盛り込んで描く。今井正と名コンビといわれた水木洋子の脚本だが、エピソードを詰め込みすぎ、全体的に緩慢な仕上がりになっているのが惜しい。なお、ビデオは148分のカット版。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELEN　OF　TROY", "トロイのヘレン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ホメロスの一大叙事詩「イリアス」の中のトロイ戦争に材を得たスペクタクル・ロマン。敵対する国の男女の悲恋物語にスポットを当て、壮大なスケールで描き出す。2004年にブラッド・ピット主演で「トロイ」というタイトルで再映画化された。紀元前1100年。トロイの王子パリスは、スパルタ王メネラウスの妃ヘレンと一目で恋に落ちる。パリスがヘレンを自国に連れ去ったため、両国の間で戦争が勃発する。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sisters", "シスターズ", "", "", "http://image.eiga.k-img.com/images/movie/53348/315x210.jpg?2258", 91, "ブライアン・デ・パルマ監督が73年に手掛けたスリラー「悪魔のシスター」をリメイク。小児病棟での不審死を調査するためラカン医師に近づいた女性記者グレースは、彼の患者で元妻のアンジェリークの部屋で男が殺害される場面を目撃してしまう。アンジェリークの過去を探り始めたグレースは、彼女と双子の姉アナベル、そしてラカンにまつわる衝撃の事実を知る。出演は「デルフィーヌの場合」のルー・ドワイヨン、クロエ・セビニーほか。", "2008-02-23", "1,10,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Muertos des Risa", "どつかれてアンダルシア（仮）", "", "", "http://image.eiga.k-img.com/images/movie/1368/315x210.jpg?2965", 100, "とある酒場で知り合った売れない芸人2人が結成した漫才コンビ“ニノ&ブルーノ”は、過激などつき芸を武器に、20年の歳月に渡ってヨーロッパ芸能界の頂点に君臨している。今や別々に活動はしているものの、同じ女性を愛し、隣同士に家を建て、同じ型のベンツに乗る彼らは、どこから見ても完璧なコンビ。しかし互いの心には、嫉妬と憎悪の炎が渦巻いていた。そして、運命の瞬間は久々に行われた復活ライブの夜にやってくる……。", "2001-03-31", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NE´A", "シビルの部屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "シビルは16歳の多感な少女。ある時、知り合いの男の勧めで書いた、彼女自身の経験をもとにした“初体験小説”がベストセラーになって……。『エマニエル夫人』の著者E・アルサン原作。女流監督ならではのきめ細かさも光る、思春期ものの１本。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY　FATHER　−RUA　ALGUEM　５５５５−", "マイ・ファーザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ヒトラー政権下、収容所で人体実験を行った医師、ヨゼフ・メンゲレの息子であるヘルマンの証言を基にした人間ドラマ。ブラジルに逃亡した父と35歳にして初対面したヘルマンの嫌悪と愛情の狭間で揺れ動く情が綴られる。父親の罪を背負ってしまった息子のやり場のない悲しみに胸が痛む。ブラジルで逃亡生活を送る元ナチスの医師で父親のメンゲレと再会した息子のヘルマン。複雑な心境の彼は父親に過去の罪について問う。", "2005-07-02", "4,17,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FARINELLI − IL CASTRATO", "カストラート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "カストラートとは、18世紀のヨーロッパにおいてボーイソプラノを保持するために去勢された男たちのこと。３オクターブ半もの声を持つファリネッリと、彼の歌うオペラを作曲する兄のリカルドは、イタリア全土の群衆の賛美を得ていた。ファリネッリはロンドンで貴族オペラの有力なメセナ（庇護者）の姪アレクサンドラに出会う。兄の作曲した凡庸なオペラに不満を感じていた彼は、アレクサンドラが盗みとったヘンデルのマリアを歌うことを決意するが、それは長く続いたリカルドとの関係の終焉を物語っていた。ファリネッリはカストラートであることのジレンマの中で、栄光にも名声にも裏打ちされることのない永遠の愛を見つけていく……。「王は踊る」のG・コルビオがバロック調の壮大な世界を創り出している。", "1994-01-01", "2,4,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兒子的大玩偶", "坊やの人形", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ホウ・シャオシエン、ゾン・ジュアンシャン、ワン・レンの3人によるオムニバス映画で、当時、ホウ以外のふたりはまったく演出経験のない新人。60年代前半の台湾を舞台に、必死に生活を送る人々の姿を描く。ホウ・シャオシエン監督の「坊やの人形」は赤ん坊を育てるため映画館のサンドイッチマンをして日銭を稼ぐ青年とその妻の話。ゾン監督の「小泙の帽子」は日本製圧力鍋のセールスマンとして地方を回る青年たちの話。ワン監督の「りんごの味」は駐留米軍の車にひかれた男とその家族が、手厚すぎる補償を約束され、喜こぶべきか悲しむべきか悩む話。いずれのエピソードも当時の台湾の庶民を描いたホワン・チュンミンの原作の映画化。", "1983-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHiLDREN チルドレン", "CHiLDREN チルドレン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "人気作家・伊坂幸太郎の小説「チルドレン」を、「東京タワー」の源孝志監督が映像化したヒューマンミステリー。家庭裁判所で調査官として働く武藤は、先輩の陣内と共に銀行強盗に巻きこまれる。無事に解放されたものの、武藤は一緒に人質になった女性・美春に一目ぼれしてしまう。翌日、万引きで補導された少年・志朗を担当することになった武藤は、志朗の父親に対する様子に違和感を抱く。やがて武藤は、志朗の衝撃の秘密を知り……。", "2006-11-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOUS，LES ENFANTS DU XXE｀ME SIE｀CLE", "ぼくら、20世紀の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「動くな、死ね、甦れ！」「ひとりで生きる」で国際的な評価を得たV・カネフスキーの長編第３作。ドラッグや窃盗など犯罪に手を染めるストリート・キッズたちや、施設に収容される10歳に満たない子供たち、鑑別所の青年たちへのインタビューをドキュメンタリー・タッチで綴った作品。子供たちに質問を続けるうちに、一人の青年がカメラの前に姿を現わす。それは、なんとカネフスキー監督の前２作で主人公ワレルカを演じたP・ナザーロフ。鉄格子の向こうで青ざめた笑いを浮かべる彼だったが、かつて共演したD・ドルカーロワとの再会に心を開いていく。この映画は、残忍性をエスカレートさせる子供たちの裏側にある、傷つきやすい感受性が鮮明に映し出された作品である。", "1993-01-01", "24,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2011-2012 ワーグナー「ニーベルングの指環」　第3夜「神々の黄昏」", "METライブビューイング2011-2012 ワーグナー「ニーベルングの指環」　第3夜「神々の黄昏」", "", "", "http://image.eiga.k-img.com/images/movie/57168/315x210.jpg?2423", 384, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を映画館で上映する「METライブビューイング」2011～12年シーズンの8作目。ワーグナーの全4部からなる大作オペラ「ニーベルングの指環」の最終章。「指環」を恋人のブリュンヒルデに預けて人間界へと冒険に出たジークフリート。しかし、アルベリヒの息子ハーゲンがジークフリートに薬を飲ませてブリュンヒルデを忘れさせ、指環を奪おうとする。", "2012-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使と野郎ども", "天使と野郎ども", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "競輪場の利権をめぐってことあるごとに対立する赤木組と新興勢力の黒田組。赤木組の兄貴株・上村義雄は、度胸も腕っ節も一流だったが、ヤクザの世界を嫌い足を洗おうとしていた。その交換条件として、組長とその片腕は彼に黒田の暗殺を命じる。義雄に憧れる弟の健児はそれを聞き、代役を買って出るのだが……。日活製の任侠アクション。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMORES　PERROS", "アモーレス・ペロス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 153, "各映画祭で絶賛されたメキシコの新鋭による秀作ドラマ。貧富の差が激しく、犯罪がはびこる街メキシコシティー。義姉への愛を募らせる青年、不倫の愛を勝ち取ったばかりの美しいモデル、離れて暮らす娘を心の支えにしている落ちぶれた殺し屋。全く関係のない３人の運命が、ある交通事故をきっかけに交錯し、それぞれ転落の一途をたどることになる……。３つのドラマを時間を前後させて描く巧妙なプロットが秀逸。報われぬ愛を求めてもがく登場人物たちの悲痛な思いがヒシヒシと伝わってくる。", "1999-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIA EONIOTITA KE MIA MERA", "永遠と一日", "", "", "http://ecx.images-amazon.com/images/I/418N4AJ45PL._SL210_.jpg", 134, "不治の病に冒されたひとりの詩人が過ごす最後の一日。自分の詩は下書きに過ぎなかったと悔やむ今、生前は寂しい思いをさせてしまった妻が幸せそうに輝いていた夏の日の記憶が甦る。死の床にある母を見舞った帰りに思わず助けてしまった難民の少年から、耳慣れない言葉をひとつまたひとつと買い集める詩人。しかしもう時間は残されていなかった。少年と夜の街を走った“運命のバス”を思い出に、詩人は海から聞こえる亡き妻の声に誘われるようにあの砂浜に立つ。アンゲロプロス監督がブルーノ・ガンツを主演に迎えたパルム・ドール受賞作。", "1999-04-17", "4,2,27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PALM BEACH STORY", "パームビーチ・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ある若夫婦が巻き起こす大騒動を描くスクリューボール・コメディ。耳がほとんど聞こえない老人にも嫉妬する、夫のトムにすっかり業を煮やした妻ジェリーは、簡単に離婚のできるフロリダのパームビーチへ。急いで跡を追うトムだが、そこで二人を待つのは、型破りの石油成金ハッケンサッカー姉弟だった……。まさに狂乱という言葉がふさわしい、スタージェスの才気あふれる快作。1948年の日本初公開時の題は「結婚五年目」。1994年にこのタイトルでリバイバル公開された。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("酔いがさめたら、うちに帰ろう。", "酔いがさめたら、うちに帰ろう。", "", "", "http://image.eiga.k-img.com/images/movie/55198/315x210.jpg?4349", 118, "人気漫画家の西原理恵子の元夫で、2007年に死去した戦場カメラマン・鴨志田穣さんの自伝的小説を、浅野忠信＆永作博美主演で映画化。重度のアルコール依存症で入院することになった主人公が、個性的な患者たちと触れ合いながら、家族の深い愛情に支えられて心身ともに再生していく姿を描く。監督は「サード」「絵の中のぼくの村」の東陽一。共演に香山美子、市川実日子、利重剛ほか。", "2010-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TA　FA　LIKIT", "タイムリセット・運命からの逃走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「レイン」で世界的に高い評価を得たパン監督のデビュー作。明日の出来事を事前に知った主人公が限られた時間の中で奔走するというありがちなファンタスティック・ミステリーだが、仏教信仰の厚いタイらしいテイストが随所に盛り込まれ独特の世界観を醸し出している。ジァップの恋人ワーンが原因不明の重体に。ワーンを助けるためには翌日の死亡記事が記載されている不思議な新聞を元に、５人の命を助けることが必要だと告げられ……。", "1997-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマンG ゴーデスの逆襲", "ウルトラマンG ゴーデスの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "200X年、国際的軍事組織“UMA”の隊員ジャック・シンドーは、銀色の巨人“グレート”と一体になり地球の平和のために闘っていた。ある時、宇宙から一片の細胞になっても再生する邪悪な生命体ゴーデスが飛来するが……。オーストラリアで作られた、“ウルトラマン”久々の実写版。", "1990-12-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUCE　DEI　MIEI　OCCHI", "ぼくの瞳の光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "イタリア・ローマを舞台に、都会の孤独と、それでも心の交流を求める人間たちの姿をナイーブなカメラ・ワークで撮り上げたコンテンポラリーな寓話。ヴェネチア国際映画祭で最優秀男優・女優賞に輝いた主演二人の妙演が素晴らしい。タクシードライバーの青年がある日、少女を轢きそうになってしまう。その後、少女の母親と出会うことに。母親に惹かれていく彼は、借金を抱える彼女のために肩代わりし、やがてドロ沼生活に身をやつしていく。", "2001-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪国", "雪国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "温泉町で知り合った芸者に不思議な魅力を感じて心惹かれていく若者の姿を描いた川端康成の同名小説の映画化。岩下志麻が演じる芸者・駒子のしっとりとした美しさと内面の強さが巧みに表現されていて、主人公の木村功もナイーブな青年の心理を好演。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOWING WILD", "吹き荒ぶ風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "仲間と全財産をかけて掘った油田を盗賊に破壊されて無一文になったジェフはある町に流れ着く。そこで彼は昔の恋人と出会うが、再会もつかの間、彼の前に再び盗賊団の影が忍び寄ってきた……。B級アクションの快作を多く残したH・フレゴネーズが腕利きスタッフと黄金キャストを得て撮った代表作。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使の欲望", "天使の欲望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "故郷を捨てて上京、姉は大病院、妹は小さな産婦人科へと勤めた姉妹が、心を離反させ、ついには殺し合うまでに至る姿を描いた異色サスペンス・ドラマ。妹がことあるごとに純朴な短歌を綴るシーンなど、故郷と大都会・東京の対比の図式も随所に見られる。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOTHER，JUGS＆SPEEP", "走れ走れ！救急車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ロサンゼルスの私設救急会社で働く、お袋というあだ名の頼もしい黒人タッカー、紅一点のセクシーな美女ジェニファー、女に手の早いベトナム帰りのトニー。彼ら３人の珍妙な活躍をライバル社とのケガ人争奪競争や、会社の合併問題を絡めてコミカルに描きながら、現代社会の隠れた腐敗を覗かせていく。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN ENEMY COUNTRY", "魚雷特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ドイツ軍の魚雷製作工場破壊の命を受けたフランス政府諜報部員チャールスは、逃亡労働者に身をやつし、わざと敵軍に捕らえられて、軍需工場に潜入する。地下運動者たちの協力を得て、なんとか魚雷を一つ手に入れようとする彼だが、ゲシュタポが動き出して……。スリル満点の戦争スペクタクル。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("It's a Free World...", "この自由な世界で", "", "", "http://image.eiga.k-img.com/images/movie/53718/315x210.jpg?2796", 96, "「麦の穂をゆらす風」でカンヌ映画祭パルムドールに輝いた名匠ケン・ローチ＆脚本ポール・ラバティによる人間ドラマ。2007年ベネチア映画祭最優秀脚本賞受賞作。ロンドンに暮らすシングルマザーのアンジーは、働いていた職業紹介所を理不尽な理由でクビになり、ルームメイトと自分達の職業紹介所を立ち上げる。やがて、不法移民を働かせる方が儲けになることを知ったアンジーは、越えてはいけない一線を越えてしまい……。", "2008-08-16", "3,4,7,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DREAM MAN", "第一容疑者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "透視能力というサイキックパワーをもつ美人刑事クリスは、女性上院議員殺害に関して透視能力を駆使し、ある男を犯人として特定する。しかし彼女の意見は無視され、上院議員の夫デビッドが疑われる。さらに今度は、被害者の妹が殺され、現場の指紋からデビッドの容疑は決定的なものになる。一連の捜査に納得できないクリスは、被害者の爪に残された犯人の皮膚のDNA鑑定を依頼。その結果デビッドは釈放される。しかし、事件はこれで終わったわけではなかった……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vacancy 2: The First Cut", "モーテル2", "", "", "http://image.eiga.k-img.com/images/movie/54828/315x210.jpg?4392", 86, "とある町の郊外にある古びたモーテル。ひと時の安らぎを求め宿泊客が訪れる、自分が悲惨な末路を迎えるとも知らずに……。そこは客を殺害し、殺人ビデオを撮影している恐ろしいモーテルだった。 「モーテル」以前の最初の犠牲者を描く衝撃作。", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLEEPAWAY CAMP ２：UNHAPPY CAMPERS", "レディ・ジェイソン 地獄のキャンプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "若く気さくなキャンプのインストラクター、アンジェラ。だが彼女には、自分の意にそぐわない生徒たちを処刑するという、恐ろしい一面があったのだった。“13金”の殺人鬼ジェイソンを彷彿とさせるスプラッタ・ヒロインが斬新な、残酷血みどろホラー。ブルース・スプリングスティーンの妹パメラが狂気のマッド・キラーを演じる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("馬喰一代", "馬喰一代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "中山正男の原作を木村恵吾監督・三船敏郎主演で映画化した佳編。大正末から昭和にかけての北海道・北見の大高原を舞台に“北海の虎”の異名をとる乱暴者の馬喰が、女房に死なれ、一人息子を男手一つで育て上げる。三船敏郎が粗野な中にも息子への愛情をたぎらせた馬喰を好演。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハワイアン・ドリーム", "ハワイアン・ドリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ヒット作「チ・ン・ピ・ラ」の主人公二人を思わせる、時任三郎とジョニー大倉が、オール・ロケで撮影されたハワイを背景に活躍する。正確には続編ではないが、続「チ・ン・ピ・ラ」の気分で観るといいだろう。ラストはむこうの警察までも相手にまわして大カーチェイス。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("育ちざかり", "育ちざかり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "当時、人気絶頂だったアイドルスター・内藤洋子主演の青春映画。新人・小寺朝のオリジナル脚本を森谷司郎が演出、思春期の少女が夏休みに体験した初めての失恋を描く。乗馬やテニスに興じる内藤の、陽光を受けて輝く額が美しく、若い肉体が躍動感にみなぎっている。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の勝負", "男の勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "村田英雄が任侠道まっしぐらの博徒を演じるヤクザ映画。山田屋一家の養子・重助は、かつて刑場だった千日前を繁華街にすべく計画をたてるが、同じことをたくらむ五十路駒蔵は重助を抹殺しようとする。駒蔵の陰謀にめげず計画を実行した重助だったが、ある日彼の子分が女のことから事件を起こしてしまう。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀座旋風児 嵐が俺を呼んでいる", "銀座旋風児 嵐が俺を呼んでいる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "『銀座旋風児』シリーズの第4弾は、謎解きが入りミステリー的色彩の濃い作品になった。小林旭が演じるマイトガイの神出鬼没な活躍に加え、探偵助手に扮する若かりし頃の浅丘ルリ子の、アイドル乗りのキュートさにも要注目! 東京税関の係官殺人事件が起こり、さらにその娘が誘拐された。事件を知った探偵が真相究明に乗り出す。後半の犯人追跡シーンなどがスリリング。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЛЕТУЧИЙ ГОЛЛАНДЕЦ／THE　FLYING　DUTCHMAN", "さまよえるオランダ人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "今はレストランに姿を変え、自力で航海することができない古い帆船が、突然大海原を漂流し始め、居合せた客は大パニックに陥る。軍人、踊り子、成金、役人、アナーキスト、環境保護主義者など、様々な人間が織り成す珍騒動をおもしろおかしく描いた作品。ペレストロイカ以降、社会暗部を暴いた露骨な作品が多かったソ連映画において一抹の清涼剤の役割を果した。", "1990-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUSH！", "RUSH！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "偽装誘拐がとんでもない事態を引き起こしていくクライム・ムービー。焼肉屋のチェーン店で働く昌也が、オーナーの娘ソヨンから狂言誘拐を持ち掛けられる。彼女は身勝手な父親から大金をせしめるつもりだ。だが、悪徳刑事の乱入で殺人続発の大騒動に発展。妻の浮気に絶望している冴えないサラリーマン島崎が、ひょんなことからこの大金を手に入れてしまい命を狙われるはめに。狂言誘拐が首謀者の知らないところで様々な人を巻き込み、ロマンス、友情、父娘の葛藤などのエピソードを交えて展開。予測不可能なドラマの疾走感に、個性的な登場人物たちが醸し出すシニカルなユーモアが重なって弾ける痛快作だ。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゼイラム", "ゼイラム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「未来忍者」でデビューした雨宮慶太の35ミリ劇場映画デビュー作。大宇宙の片隅のとある街を舞台に、逃走した護送中の戦士ゼイラム、伝説の不死身のエイリアンである彼を追う女バウンティ・ハンター、イリアは、相棒であるコンピュータ、ボブとともにゼイラムが逃げ込んだ地球へと向かうが……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD BOYS CAN’T FLY", "Dead Boys Can't FLY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "実際に起こった事件をモデルにした、バイオレンス映画。仕事を売り込みにいった一人の青年が、馬鹿にされ、怒りと不満を抑え切れずにその会社を襲撃、無差別殺人を始める。アメリカのマスコミに、“1990年代の「時計じかけのオレンジ」”と評された作品。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("反逆の報酬", "反逆の報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "日活がロマン・ポルノに転向し、ほとんど出番のなかった澤田幸弘が初めて他社で監督したアクション大作。欲と復讐にかられた二人の男が、巨大な麻薬組織に挑戦し見事に壊滅させるまでをムード・アクション調で描く。石原裕次郎と渡哲也の久々の顔合わせが見ものである。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blue City", "ブルー・シティ　非情の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ハードボイルドの巨匠R・マクドナルドの原作を、W・ヒルのプロデュースにより映画化。フロリダを舞台に、父親殺しの謎を探る青年ビリーとその恋人アニーのハードなアクションが展開する。R・クーダーの哀愁漂う音楽も印象的。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Los Abrazos Rotos", "抱擁のかけら", "", "", "http://image.eiga.k-img.com/images/movie/54388/315x210.jpg?1748", 128, "「オール・アバウト・マイ・マザー」「ボルベール／帰郷」のペドロ・アルモドバル監督とペネロペ・クルスが、4度目のコンビを組んだミステリー仕立ての恋愛ドラマ。14年前に車の事故で視力を失った映画監督マテオ・ブランコは、その事故以来ハリー・ケインというペンネームで脚本家を生業としている。そこに14年前のマテオを知る1人の若者が現れる……。共演に「バッド・エデュケーション」のルイス・オマール。", "2010-02-06", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フィラメント", "フィラメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "グラビアモデル出身の井川遥の映画デビュー作としても話題を呼んだ、辻仁成監督の通算第４作。キレやすい一人の青年を主人公に、夢遊病の妹、女装する父親、10年ぶりに“帰宅”した母親が織りなす、ねじれた家族のドラマがノスタルジックな美術や衣装の中で描き出される。女優に扮した写真作品などで知られる現代美術家、森村泰昌が写真館を営む父親役で出演。特異なキャラクターで画面をさらい、物語の“落とし前”も鮮やかにつけている。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Tree", "ザ・ツリー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "", "2012-12-31", "11,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("理由", "理由", "", "", "http://ecx.images-amazon.com/images/I/61X3TQ5H58L._SL210_.jpg", 160, "ミステリー作家・宮部みゆきが直木賞を受賞した同名小説を、名匠・大林宣彦監督が豪華キャストで映画化。高層マンションで起こった不可解な殺人事件の顛末を、多数の人物の証言を通して浮かびあがらせていく。東京が大嵐に見舞われたある晩、荒川区の超高級マンションで、一家4人の惨殺事件が発生する。捜査が進むうち、被害者たちはその部屋に住んでいたはずの一家ではなく、全くの他人同士であったことが判明し……。", "2004-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Little Romance", "リトル・ロマンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「スティング」のジョージ・ロイ・ヒル監督による青春ロマンスで、ダイアン・レインの映画デビュー作。ベルサイユ宮殿で出会ったフランス人の少年ダニエルとアメリカ人の少女ローレン。2人はたちまち恋に落ちるが、ローレンはアメリカに帰国しなければならない。彼らは謎の老人から聞いた「ベネチアにあるためいきの橋の下で、日没の瞬間にキスした恋人たちは永遠に結ばれる」という伝説を実行しようと、ベネチアへと旅立つが……。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗夜行路", "暗夜行路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "志賀直哉唯一の長編小説を、豊田四郎がドラマチックに映画化。原作では、主人公・時任謙作は意志の強い青年として描かれているが、ここでは池部良のキャラクターもあって、出生の秘密に悩むインテリとして登場する。謙作の妻となるお直に扮した山本富士子の演技も見もの。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KEVIN＆PERRY　GO　LARGE", "イビサボーイズ GO DJ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "高校生のケビンとペリーはモテない＆冴えない童貞くん。人気DJになれば群がる女の子たちとヤリ放題と考えた彼らは、脱・童貞を目指してクラブの聖地、イビサ島へ行くことに。早速かわいい女の子と憧れのカリスマDJ、アイボールと顔見知りになるのだが……。イギリス製TV番組の１コーナーからスピンオフしたコメディで、ペリーを女優のキャシー・バークが演じているのが見もの。", "2000-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("港のヨーコ ヨコハマ ヨコスカ", "港のヨーコ ヨコハマ ヨコスカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ダウンタウン・ブギウギ・バンドの同名大ヒット曲の歌詞に沿ってストーリーを展開、一人の少女の青春の高揚と挫折のドラマを描いた作品。“髪の長い女・ヨーコ”を早乙女愛が演じ、松坂慶子、多岐川裕美が脇を固める。宇崎竜童が初めて映画音楽を担当。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("D.A.R.Y.L.", "ダリル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ヘリに追われ、崖下に転落する車から脱出した少年ダリルは過去の記憶を失っていた。谷間の町で保護され、天才少年ぶりを発揮した彼の前に実の両親を名乗る男女が現れ、彼を連れ去る。謎の組織の暗躍に立ち向かう不思議な少年の大活躍が最大の見もの。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CERVANTES", "セルバンテス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "『ドン・キホーテ』を著した文豪セルバンテス。本作は、恋あり冒険あり、まさに波乱万丈の彼の若き日々を描いた娯楽大作である。教皇の使徒だった彼は、派遣先でのつかの間の恋に破れて軍隊に身を投じる。戦では功を立てるが、帰途海賊に襲われ、囚われの身に。", "1968-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Good Morning, Vietnam", "グッドモーニング，ベトナム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ある1人のDJの姿を通してベトナム戦争を描いた、バリー・レビンソン監督による傑作ヒューマンドラマ。ベトナム戦争真っ只中のサイゴンに、兵士たちの士気高揚のため呼び寄せられた米軍放送の人気DJクロンナウアー。マシンガン・トークとロックンロール満載の彼の放送は、兵士たちから大人気を得る。ところが、彼の型破りな言動に驚いた軍上層部は放送を中止してしまい……。まさにハマリ役のロビン・ウィリアムズの熱演は必見。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE DOG", "ホワイト・ドッグ／魔犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "若い女優が飼う白い犬は、前の飼い主によって黒人を襲うよう調教されていた。彼女はそれを再調教しようとするが……。ギャリーが愛妻ジーン・セバーグを実名で出し、５月革命や黒人暴動を背景に書いた寓話的小話を、S・フラーが映画化した異色サスペンス。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フクシマ2011 被曝に晒された人々の記録", "フクシマ2011 被曝に晒された人々の記録", "", "", "http://image.eiga.k-img.com/images/movie/57888/315x210.jpg?4666", 85, "2011年3月11日の東日本大震災により発生した福島第一原子力発電所の事故で、放射線にさらされた人々の姿を追ったドキュメンタリー。監督は「二重被爆」シリーズの稲塚秀孝。原発事故による計画的避難区域に指定された福島県南相馬市や飯館村を中心に、放射能汚染に向き合い町に暮らすことを選んだ人々が町の再生・復興に挑み続ける姿を、延べ60日間にわたり撮影した。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Being John Malkovich", "マルコヴィッチの穴", "", "", "http://image.eiga.k-img.com/images/movie/1518/315x210.jpg?1840", 112, "定職のない人形使いのクレイグは、新聞の求人欄を見てマンハッタンにあるオフィスビルの7と1/2階にある小さな会社に就職する。文書整理の仕事を得た彼は、ある日落としたファイルを拾おうとキャビネットを動かし、偶然壁に小さなドアを発見する。ドアを開けて穴の中に入った彼は、それが俳優ジョン・マルコヴィッチの脳へと続く穴であることに気付く。", "2000-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霧丸霧がくれ", "霧丸霧がくれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "“鳴らせば必ず地獄に堕ちる”という恐ろしい言い伝えのある鐘をついてしまった二出川霧丸と恋人の浪江。鐘の呪いはまず浪江の方に現れる。彼女は瀬戸内海を荒らし回る海賊“黒い狼”に連れ去られ、さらに江戸の人買い紋吉に買われてしまったのだ。霧丸はその行方を追うが……。松方弘樹の時代劇主演第１作。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MATINEE", "マチネー 土曜の午後はキッスで始まる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1960年代初頭のマイアミ。海軍将校の息子ジーンはホラー映画の大ファンで映画館に入り浸り。だがキューバに核兵器が配備されたことから核戦争が始まるかもしれないという噂が街に広まる。ジーンは父の身を案じながらも、映画館に新作ホラー映画『マント』を観に行く。放射能による突然変異のアリ人間が登場するこの映画の評判を聞きつけて、映画館には長蛇の列ができるが、核戦争の危機は刻一刻と迫っていた……。自らもホラー映画の大ファンだったJ・ダンテが描くB級映画への熱きオマージュ。核戦争の恐怖と劇中映画の恐怖が、少年たちの淡い恋物語と絡んでテンポよく展開していく一級娯楽作品である。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHARGE OF THE LIGHT BRIGADE", "遥かなる戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "1960年代に多くの秀作、異色作を発表した英国の鬼才T・リチャードソンが、19世紀半ばのクリミア戦争を背景に撮り上げた歴史活劇。上官と対立するほど気性の激しい大尉ノーランが、旧友の新妻クラリッサと心惹かれ合うようになる。そんなときクリミア半島に出征することになったノーランを待ち受けていた壮絶な運命とは……。痛烈な戦争批判をこめた終盤の玉砕シーンが凄まじい。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUEL IN THE SUN", "白昼の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "「風と共に去りぬ」の大プロデューサー、セルズニックが、当時としては空前の製作費（600万ドル）を投じた大作。舞台は南北戦争終結後のテキサス。大牧場主のもとに引き取られたインディアンとの混血娘パールをめぐる兄弟の愛と確執を中心に、夫婦、親子の対立、鉄道利権などをめぐる資本家と牧場主の争いが、巨大なスケールのなかに浮き彫りにされる。ペック、コットン、ジョーンズをはじめとするビッグ・スターの競演、L・ガームスほかの秀逸な撮影、ティオムキンの音楽などに加え、ヴィダーの演出もすこぶるエネルギッシュで叙事詩風の格調を見せ、この雄大な西部ドラマをうたい上げた。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRESH KILL", "フレッシュ キル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ニューヨークをベースに活動する台湾系女性アーティスト、シュー・リー・チェンの劇場用映画デビュー作。レズビアンだということを父親に告白できないシャリン。ある日、体が緑に変色し誘拐されてしまったハニー。ニューヨークのスタテン島をメインの舞台に、いくつかのエピソードが重なりながら物語は展開していく。目まぐるしいストーリー展開の中に様々な社会問題に対する批判を込めた作品。リヴィングカラーのV・リードによる音楽も、斬新なスピード感あふれる画面を際立たせている。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪役パパ", "悪役パパ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "新田たつおの同名マンガの映画化で、親子の愛を絡ませた、異色バック・ステージもの。悪役専門の俳優、芥田は、妻を事故で失い、二人の娘を男手一つで育てている。父親が悪役で、何かと肩身の狭い思いをする娘たちだったが、ある日、芥田に初の主役の話が来る。タイトルは『極悪人への道』という、大型時代劇だったが……。内藤剛志の、恐さと優しさを使い分けた演技に注目。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Family Stone", "幸せのポートレート", "", "", "http://image.eiga.k-img.com/images/movie/1388/315x210.jpg?3153", 103, "「SEX and the CITY」のサラ・ジェシカ・パーカー、ダイアン・キートン、クレア・デーンズ、ダーモット・マローニー、「きみに読む物語」のレイチェル・マクアダムスらアンサンブル・キャストによるファミリードラマ。恋人エベレットと結婚間近のキャリアウーマン、メレディスはクリスマスに初めてエベレットの実家を訪れる。だが、彼女はどういうわけかエベレットの家族たちから嫌われてしまう……。", "2006-07-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いぬばか", "いぬばか", "", "", "http://image.eiga.k-img.com/images/movie/54678/315x210.jpg?4173", 92, "タレントのスザンヌが映画初主演を務めるコメディ。雑種の愛犬・るぱんを連れて熊本から上京してきたすぐりは、立派なラブラドール・レトリーバーを連れた青年・哲平と出会う。彼が店長を務めるペットショップで働くことになったすぐりは、個性的な店員や客、そして可愛い犬たちに囲まれながら多くのことを学び成長していく。共演は「愛の言霊」の徳山秀典。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("將軍", "将軍の息子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「シバジ」「ハラギャティ」などで知られる韓国の名匠イム・グォンテクが、実在した韓国独立運動の雄、キム・トゥハンの波乱万丈の青春像に迫った大河ドラマ。", "1990-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Peacock", "サイコ　リバース", "", "", "http://ecx.images-amazon.com/images/I/51hgT6FDbFL._SL210_.jpg", 0, "", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Oceanworld 3D", "オーシャンワールド 3D（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2009-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダンボールハウスガール", "ダンボールハウスガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "すべてを失ってしまい、路上生活をするハメになった女性が、人生の再出発をするまでを描く人間ドラマ。恋人と渡米するために退屈なOL生活に別れを告げ、アパートを解約した杏は、苦労して貯めた500万円を奪われたうえに、恋人の二股も発覚。絶望と空腹で心身ともに疲れきった彼女は、ホームレスの人々と共同生活を始める。何にも捕らわれずに自由に生きる人々との心の交流によって、杏は再び生きる意欲を取り戻していく。映画初主演となる米倉涼子が、たくましいヒロインをはつらつと好演。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花と竜・洞海湾の決闘", "花と竜・洞海湾の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "錦之助版「花と竜」の続編。正編のような出世話の色彩は薄まったが、その分貫禄のついた玉井金五郎親分で魅せる。若松港に一家を構えた金五郎が悪どい友田喜造の妨害をはねのけながら、沖仲士の組合結成をサポートしていく。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Giallo", "ジャーロ", "", "", "http://image.eiga.k-img.com/images/movie/55648/315x210.jpg?1524", 92, "イタリアン・ホラーの巨匠ダリオ・アルジェントが、自身の原点ともいえるジャーロ映画に回帰した猟奇スリラー。「戦場のピアニスト」のエイドリアン・ブロディが主演を務める。トリノで外国人女性ばかりを狙う残忍な誘拐殺人事件が続発。行方不明になったセリーヌの姉リンダに依頼されて捜査を開始したエンツォ警部は、被害者が遺した「彼は黄色い（ジャーロ）」というメッセージをヒントに、殺人鬼の正体に迫っていくが……。", "2010-09-11", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西の魔女が死んだ", "西の魔女が死んだ", "", "", "http://image.eiga.k-img.com/images/movie/53358/315x210.jpg?3953", 115, "学校へ行くことが苦痛になってしまった中学生のまいは、母に勧められて祖母の家に身を寄せることに。母とまいが\"西の魔女\"と呼ぶイギリス人の祖母は、大自然に囲まれた一軒家で穏やかな生活を送っている。祖母との田舎暮らしは閉ざされたまいの心を少しずつ解きほぐしていく。梨木香歩のロングセラー小説を「8月のクリスマス」の長崎俊一監督のメガホンで映画化。祖母役に往年の名女優シャーリー・マクレーンの娘サチ・パーカー。", "2008-06-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Boxing Gym", "ボクシング・ジム", "", "", "http://image.eiga.k-img.com/images/movie/57318/315x210.jpg?4231", 91, "1967年のデビュー以降、現代アメリカ社会を独自の視点で見つめ続けてきたドキュメンタリー作家フレデリック・ワイズマン監督が、テキサス州オースティンのボクシング・ジムにカメラを入れたドキュメンタリー。多様な人種が交錯するアメリカという国そのもののように、年齢、人種、職業、性別を問わずさまざまな人々がそれぞれの目的でジムに通い、出会いを繰り返す様子を映し出す。2011年、ワイズマン監督作を一挙上映する「フレデリック・ワイズマンのすべて」にて日本初公開。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SACCO E VANZETTI", "死刑台のメロディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "アメリカの裁判史上の汚点といわれたサッコ＝バンゼッティ事件の映画化。1920年、労働運動に参加していたイタリア移民のサッコとバンゼッティは、無実の罪に問われ電気椅子による死刑を宣告される。当時のアメリカの人種偏見と思想弾圧をドラマティックに描いた傑作。ジョーン・バエズの主題歌も話題となった。", "1970-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fido", "ゾンビーノ", "", "", "http://image.eiga.k-img.com/images/movie/34278/315x210.jpg?1650", 93, "少年とゾンビの友情をブラック・ユーモアたっぷりに綴ったカナダ発のゾンビ・コメディ。長期に渡るゾンビ戦争の末、ゾンビを従順にさせる首輪を開発して勝利を収めた人間たち。今ではゾンビが一般家庭のペットになる時代が訪れていた。ある日、いじめられっ子ティミーの家にもゾンビがやって来る。ティミーは“ファイド”と名付けたゾンビに次第に友情を感じ始めるが……。コメディ俳優のビリー・コノリーがゾンビのファイドを怪演。", "2007-10-27", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABRAXAS", "エイリアンチェイサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "地球人女性の遺伝子を利用して製造されるエネルギー“コーメイター”をめぐって、闘いが繰り広げられるSFバイオレンス。元パワー・リフティングの世界チャンピオン、スーベン・オリトールセンと、プロレスの元WWF世界ヘビー級チャンピオン、ジェシー・ベンチェラが共演する。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本侠客伝 斬り込み", "日本侠客伝 斬り込み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "新宿では、露天商の自由組織・街商同盟と、縄張りの拡張をもくろむ相州一家が対峙していた。子連れの渡世人・中村真三は、中村一家の名乗りをあげ街商同盟を守る。仲裁に入った若松一家の親分が倒され、中村一家の面々は、相州を叩き潰さんと立ち上がった。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MEDALLION", "メダリオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "2004年に50歳を迎えたJ・チェンが、ますます快調なアクションを披露した冒険活劇。無限のパワーを与える伝説のメダル。犯罪組織に奪われた、この秘宝を追って、ジャッキー扮する捜査官が激闘を繰り広げる。スピード感にあふれた立ち回りはもちろん、「ジョー・ブラックをよろしく」のC・フォーラニをヒロインに据えた、恋の行方にも注目。", "2003-01-01", "6,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOSTALGHIA", "ノスタルジア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "死を間近に控えた異邦人が二度と帰れぬ故郷の大地を切々と思いつつ、そのノスタルジアのくびきを超え出ていくさまが、重厚無比の詩的密度で描かれる。イタリアのとある寒村の湯治場を舞台に展開する詩人と“狂人”のエピソードが鮮烈。村人や湯治客から狂人扱いされているドメニコは、ロシアから来た詩人アンドレイに“世界の救済をかけて、ロウソクの火を消さずに湯治場の広場を横断するように”との謎めいた懇願をする。ローマの著名な騎馬像の上で、ドメニコが命懸けの演説の末、焼身自殺し火ダルマとなって落ちる時、アンドレイは吹き渡る風ですぐ消えてしまうロウソクの炎を守りながら広場を渡り切ろうとする……。タルコフスキー監督は自らもソ連からイタリアへ亡命しており、詩人アンドレイに自身の姿を投影して故郷への思いを吐露したともとれる。火と水の暗喩に富んだイメージは彼一流のもの。ラストの雪の降りしきるイメージも深い感動を呼ぶ。", "1983-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EASY　COME，　EASY　GO", "GO！GO！GO！／ゴー・ゴー・ゴー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "E・プレスリー主演の青春ドラマ。海軍の潜水夫テッドは、機雷除去中に財宝を積んだ沈没船を発見。持ち主は船長の子孫で、若い芸術家のパトロンをしているジョーという美しい女性だった。テッドはジョーの美術館設立のために財宝の引き上げを引き受けるが、お宝を狙う者が現れ、争奪戦に巻き込まれる。プレスリーの歌を全編に散りばめ、ロマンスと冒険の心踊る物語が展開する。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喪の仕事", "喪の仕事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "美大を卒業し、美術館に勤める主人公が、大学時代の友人の死を契機に、彼が生前に残した足跡を追いながら、自分自身を見つめ直していく様を静かなタッチで描いていく。監督の君塚匠は、自らの実体験をもとに“死”というテーマに真正面から取り組み、緊張感あふれる映像を作り出している。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛ふたたび", "愛ふたたび", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「個人教授」で一躍人気者になったフランスのR・ベルレーと浅丘ルリ子が共演した愛の物語。パリへ留学したみやは、レーザー技師のニコと知り合い、愛するようになる。１年半のパリ生活を終え、みやは“さよなら”も言えずにニコと別れてしまう。そして数ヵ月後、ニコはレーザー・セミナーに出席するため来日、みやと再会する。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エリック・クラプトン＆スティーヴ・ウィンウッド　ライブ・フロム・マジソン・スクエア・ガーデン cine sound ver.", "エリック・クラプトン＆スティーヴ・ウィンウッド　ライブ・フロム・マジソン・スクエア・ガーデン cine sound ver.", "", "", "http://image.eiga.k-img.com/images/movie/54748/315x210.jpg?3047", 90, "2008年2月、ニューヨークのマディソン・スクエア・ガーデンにて行われたブリティッシュ・ロック界の至宝エリック・クラプトンとスティーブ・ウィンウッドによる奇跡のジョイントライブを上映。大物ミュージシャンが一堂に会したスーパーバンド「ブラインド・フェイス」など、2人が関わったバンドの名曲やそれぞれのソロ作品を、ライブ会場さながらの臨場感と迫力でスクリーンに映し出す。", "2009-09-26", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("笑う警官", "笑う警官", "", "", "http://image.eiga.k-img.com/images/movie/54088/315x210.jpg?3315", 0, "実際に起こった北海道警察の汚職事件を基に、警察組織の腐敗を描いた佐々木譲の同名ベストセラーを、角川春樹が13年ぶりのメガホンで映画化した社会派サスペンス。札幌市内のアパートで女性警官の他殺体が発見されたことで、元交際相手の巡査部長・津久井に容疑がかけられ、即座の射殺命令までもが下される。この流れに疑問を抱いた所轄の刑事・佐伯は、信頼できる仲間を集めて独自に捜査を開始するが……。主演に大森南朋、共演に松雪泰子、宮迫博之ら。", "2009-11-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖刀物語 花の吉原百人斬り", "妖刀物語 花の吉原百人斬り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "歌舞伎の「籠釣瓶花街酔醒」をもとに内田吐夢監督・片岡千恵蔵主演で映画化した力作。顔にみにくいアザがある田舎商人は、吉原の遊女に夢中になるが手玉に取られ、太夫となった彼女の行列に名刀・村正を抜いて斬り込む。田舎商人役の片岡千恵蔵と、遊女役の水谷良重が好演。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GIRL CAN’T HELP IT", "女はそれを我慢できない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "元祖バスト・ガールとして有名なJ・マンスフィールド主演によるお色気コメディ。かつてのギャングのボスのため、グラマー娘をスターに仕立てあげねばならなくなった男の悪戦苦闘を描く。『ロック・アラウンド・ザ・ロック・パイル』なる珍妙なロックンロールが全編に流れ、それとともに揺れる100センチを超えるマンスフィールドのバストが見もの。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACKSNAKE！", "ブラック・スネイク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "人種問題を独自のモンドな視点で捉えた異才R・メイヤーのバイオレンス映画。1835年、孤島のブラックムーア農園は独裁的な売春婦に仕切られ、黒人奴隷たちは彼女の死の制裁をも辞さない暴力支配に怯え切っていた。そこにリベラルな思想を持つイギリス人貴族がやってきたことから主従関係のバランスが崩れだし、やがて暴動が起こる。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天地無用！ 真夏のイヴ", "天地無用！ 真夏のイヴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "「天地無用！ in LOVE」に続き、オリジナル・ストーリーで展開される人気ファンタスティック・アニメの劇場版第２作。不思議な力を秘めた高校生、柾木天地の元にある日突然、天地の娘を名乗る謎の美少女が現れる。そんな彼女の母親が誰なのかをめぐって、天地に気のある皇女たちは大騒動を繰り広げるが……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("木と土の王国　青森県三内丸山遺跡'94", "木と土の王国　青森県三内丸山遺跡'94", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "それまでの学説を一新する大発見といわれる、青森県三内丸山遺跡。本作品は、日々進む発掘の様子を同時進行形で捉えたドキュメンタリー。600人を超える作業員で活気づく発掘現場からは、5500年前から1500年間にわたって、縄文人たちが途切れることなく定住していたことを証明する、おびただしい量の出土品がもたらされる。朱漆塗りの木製品、原石のままのヒスイ、現代のオールと寸分違わない船の櫂など、一つ一つの発見が“縄文”のイメージを大きく覆していく。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Iron Giant", "アイアン・ジャイアント", "", "", "http://image.eiga.k-img.com/images/movie/1848/315x210.jpg?4459", 86, "1957年秋。メイン州の小さな町に住む9歳の少年ホーガースは「金属で被われた大男が空から降ってきた」という話を耳にする。時はまさにソ連が世界初の人工衛星スプートニクを打ち上げた直後。宇宙人の存在を信じるホーガースは、夜中に家を抜け出し、空から来たロボットを探しに行く。そしてホーガースが森でみつけたのは、金属を食べ子供のように好奇心旺盛な鋼鉄の巨人、“アイアン・ジャイアント”だった。", "2000-04-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Meet the Parents", "ミート・ザ・ペアレンツ", "", "", "http://image.eiga.k-img.com/images/movie/1528/315x210.jpg?3251", 108, "シカゴに住む看護士グレッグは恋人のパムに夢中。すぐにでもプロポーズしたいのだが、どうやら結婚は彼女の父親の一存にかかっているらしい。そんなときパムの妹が結婚することになり、グレッグとパムは式に出席するためニューヨークの実家へ。それは結婚を切り出す絶好のチャンスになるはずだった。しかし彼を待っていたのは手強い父親ジャック。彼にとって目の中に入れても痛くない長女パムに相応しい男などいるはずもなく、グレッグの何もかもが気に入らないのだった……。", "2001-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHE", "炎の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "初代ボンド・ガールのU・アンドレスが永遠の生命を持つエジプト女王に扮するファンタスティックな冒険映画。第一次大戦直後、夜のパレスチナで出会った妖艶な美女に魅せられた主人公。彼は、運命に導かれていくかのように砂漠を旅し続けたあと、ついに数千年来生きながらえていた彼女と再会するのだが……。", "1965-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("佐平次捕物控・紫頭巾 解決篇", "佐平次捕物控・紫頭巾 解決篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "マキノ省三も演出した『紫頭巾』を、マキノ雅広が阪妻、大河内の顔合わせでリメイクした作品。豊臣の残党が何者かに化けて徳川の重臣の命を狙うという基本ストーリーを、大幅に改変している。前・後編２部からなり、後編は前編の１週間後に封切られた。脚本はマキノの前作「ボス」に続いて八尋不二。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG STORE", "マルクス兄弟 デパート騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "３兄弟がデパートの大株主の人気歌手を助けて、デパート乗っ取りを企てる凶悪ギャング一味とわたりあう。例によってグルーチョが歌、チコがピアノ、ハーポがハープと見せ場いっぱいでマルクス・ファンにはうれしい一編。特に“３人ハーポ”のハープ３重奏が楽しい。DVDは「マルクス・ブラザーズ コレクターズ・ボックス」に収録。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バトル・ロワイアルII 鎮魂歌（レクイエム）", "バトル・ロワイアルII 鎮魂歌（レクイエム）", "", "", "http://image.eiga.k-img.com/images/movie/1308/315x210.jpg?4452", 133, "前作の監督、深作欣二の撮影中の死により、息子・深作健太が監督を引継いで完成させた続編。前作で描かれたドラマの3年後、首都はBR法に反対する者たちにより爆破されて崩壊、その1年後、前作で島を脱出した七原は、反BR法組織＜ワイルドセブン＞のリーダーとなり大人に宣戦布告。彼らを抹殺するための新世紀テロ対策特別法「BRII」が施行され、鹿之砦中学校3年B組の生徒42人はテロリストとの闘いの最前線に送り出される。", "2003-07-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'age de raison", "マーガレットと素敵な何か", "", "", "http://image.eiga.k-img.com/images/movie/56488/315x210.jpg?3443", 89, "結婚間近の恋人もいるマーガレットは、毎日仕事に没頭して忙しい日々を送っていた。しかし40歳の誕生日を迎えた日、公証人と名乗る男が手紙を届けに現れる。その手紙は7歳の自分が将来の自分にあてた手紙だった。両親の離婚や初恋など、つらい思い出を心の奥底に閉じ込めていたマーガレットだったが、次々に届く過去の自分からの手紙に次第に心を開いていく。ソフィー・マルソーが働く女性を等身大に演じたドラマ。監督は「世界でいちばん不運で幸せな私」のヤン・サミュエル。", "2011-10-29", "2,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE VOLEUR", "パリの大泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "20世紀初頭のパリを荒らし回った大泥棒、ジョルジュ・ランダルをJ・P・ベルモンドが演じた痛快な犯罪ストーリー。伯父に財産を横領され、愛する従妹のシャーロットとも引き裂かれたジョルジュは、過去を捨て、泥棒として身を立てた。やがて裏の世界で成功し、シャーロットともめでたく結ばれた彼だったが、盗みのスリルに取り憑かれ……。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BACK STAB", "ダブル・クロス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "建築家のクリフは、会社オーナーの歓迎会をすっぽかして向かったナイトクラブで、謎の美女キャロラインと会う。車の中で突然の情事。翌朝、再び彼女からの電話を受けたクリフは、誘われるまま激しく愛し合う。だが目覚めたクリフの横には会社オーナーの死体が残されていた……。大人の男女のエロティックな関係の中に展開するサスペンス。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ☆ドラえもんズ　おかしなお菓子なオカシナナ？", "ザ☆ドラえもんズ　おかしなお菓子なオカシナナ？", "", "", "http://ecx.images-amazon.com/images/I/31KRBB74M0L._SL210_.jpg", 16, "ザ・ドラえもんズの奮闘を描く短編アニメシリーズの第4弾。世界一のお菓子工場・オカシナナ王国で毎年恒例のオカシナナ祭が開催され、お菓子作りの名人たちが腕を競いあっていた。そんな中、コンテストに出場するジェドーラに頼まれて食材を運んでいた王ドラとエル・マタドーラが謎のロボットに襲われ、食材をメチャクチャにされてしまう。それは、コンテスト優勝を狙うニガニガの仕業だった。同時上映は「のび太の宇宙漂流記」「のび太の結婚前夜」。", "1999-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rikos ja rangaistus", "罪と罰", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "フィンランドの鬼才アキ・カウリスマキの監督デビュー作。原作はロシアの文豪ドストエフスキーによる傑作「罪と罰」で、舞台をペテルブルグから現代のヘルシンキに移し、見事に映像化している。食肉解体工場で働く青年ラヒカイネンは、ある日仕事を終えた後、ひとりの男性を尾行し、突然射殺する。警察の捜査線上にラヒカイネンの名が挙がるが、彼はまるでゲームを楽しむかのように巧みに捜査をすりぬけ、刑事たちを翻弄する……。", "1983-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林小英雄／SHAOLIN ： BLOOD MISSION", "真説 少林寺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "時は清朝末期。革命に立ち上がった志士たちを残虐な暴力で掃討する政府軍に、逆らったとして焼き討ちされた少林寺。人民の幸福、少林寺の復興、そして殺された師の復讐のため、３人の修行僧が革命志士の群に身を投じ、政府軍を相手に戦うアクション巨編。", "1978-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌う暴れん坊", "歌う暴れん坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "大学時代に“フットボール部の三羽烏”と呼ばれた親友同士が、対立する二つの暴力団の跡目、やり手の警視として運命的な再会を果たす。高速道路をめぐる受注争い、麻薬密売騒動の果てに、縄張りを横取りしようとする黒幕の存在に気づいた彼らは……。クライマックスが歌謡ショーという趣向の異色活劇。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("×××HOLiC　真夏ノ夜ノ夢", "×××HOLiC　真夏ノ夜ノ夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "幅広いファン層を持つ、女性4人からなる気鋭の漫画創作集団“CLAMP”の人気漫画を映画化したアニメーション。怪しげな洋館を舞台に、謎が謎を呼ぶミステリアスなストーリーが進展していく。あるオークションに招かれた侑子は、会場でコレクター７人と出会う。ところが時が経つにつれ、彼らが神隠しにあったかの如く姿を消す。", "2005-08-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TANTE ZITA", "若草の萌えるころ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "フランスの女流作家アモンが映画のために書き下ろした物語。愛する伯母の死に直面した多感な少女が、ある夜パリの裏街をさまよい、様々な人との心のふれあいによって人生の哀歓を体験する。詩情豊かなアンリコ監督の佳作。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Salmon Fishing in the Yemen", "サーモン・フィッシング・イン・ザ・イエメン（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TELL　ME　SOMETHING", "カル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "韓国で大ヒットを記録したハン・ソッキュ主演のサイコ・スリラー。ソウルで３件の猟奇殺人が続発する。バラバラにされた死体はいずれも身体の一部がなくなっていた。捜査上には被害者３人と付き合っていた女性スヨンが浮上。チョ刑事はスヨンの身辺を探るうち、彼女の隠された悲しい過去を知る。切断された生々しい死体の衝撃映像とミステリアスな緊張感の中に、事件に絡んだ人間たちの悲しみが浮き彫りにされる。", "1999-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Love You Phillip Morris", "フィリップ、きみを愛してる！", "", "", "http://image.eiga.k-img.com/images/movie/54568/315x210.jpg?4471", 97, "愛する妻子と平穏な生活を送っていた警察官のスティーブは、ある日、瀕死の交通事故に遭ったことで人生を見つめ直し、妻に自分がゲイであることを告白して家を出て行く。やがて詐欺で得た金で恋人を満足させるようになったスティーブは、逮捕・収容された刑務所で、フィリップという青年と運命的な恋に落ちる。ジム・キャリーとユアン・マクレガーがゲイのカップルを演じる、実話に基づいたラブストーリー。", "2010-03-13", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STARTING—君が輝くとき", "STARTING—君が輝くとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "F1レースに魅せられた吉井均が、1987～89年までの３年間、プロのレーシング・ドライバーを目指す若者を撮り続けた自主製作のドキュメンタリー作品。モーター・スポーツの入門クラスである“F1レース”に集うドライバーたちの日常生活と夢に賭ける強い意志が、丹念に描き出されている。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAILAN", "パイラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "日本でも映画化された浅田次郎のベストセラー小説『ラブ・レター』を韓国で映像化。「オールド・ボーイ」のいぶし銀俳優、チェ・ミンシクの主演で、哀愁あふれる愛のドラマを切々と織り成していく。40歳を過ぎた独身の遊び人カンジェの元に、妻死亡の知らせが届く。妻とは、かつて戸籍を売って偽装結婚した中国人パイランのことだった。亡骸を引き取るため、彼は地方の街に向かう。ビデオ、DVDタイトルは「ラブ・レター パイランより」。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ghost dance ゴースト・ダンス", "ghost dance ゴースト・ダンス", "", "", "http://ecx.images-amazon.com/images/I/51VA7ok%2BBEL._SL210_.jpg", 119, "ネイティブ・アメリカンが死者と再会するために行なっていた儀式「ゴースト・ダンス」をモチーフに描いた人間ドラマ。ある日突然、好きな人を亡くしてしまった女子高生の満。失意の底にいた彼女は、死者に会えるという踊り「ゴースト・ダンス」の集会が行われることを知り、参加を決意する。そこに集まった人々は、大切な人との再会を夢みて踊り続けるが……。「天国からのラブレター」の山口円監督が2005年に発表した長編デビュー作。", "2006-03-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎物語", "次郎物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "秀作「風の又三郎」を監督した島耕二が、宮澤賢治に続いて下村湖人の名作に挑んだ作品。村の旧家に生まれた次郎という少年が波乱に富んだ生活の中で大人へと成長していく過程を描く。館岡謙之助の脚本が原作をうまくまとめており、島耕二の演出も手堅く清らかな作品である。マンドリンを生かした伴奏音楽が斬新な効果を出している。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIED GREEN TOMATOES", "フライド・グリーン・トマト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "人生に目的を失った中年主婦が、老人ホームで知り合った老女の昔話を聞くようになる。老女の語る南部田舎町の様々な物語は、主婦に生きることの価値を再び教えていく……。J・タンディとK・ベイツの個性派オスカー女優競演で、静かに、しかし熱く愛と勇気と友情をうたい上げた珠玉の感動作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PIEDRAS", "靴に恋して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "失恋した女性、知的障害者の娘を抱える女性、冷え切った結婚生活の寂しさを買い物で埋める女性……。それぞれ愛を求め、仕事と家庭の間で揺れ動きながらも懸命に生きている。そんな年齢も立場も違う女性５人の運命が交錯するストーリーを、シャルル・ジョルダン、グッチ、プラダなど最高級の靴300足以上が華麗に彩る。全世代の女性たちへの応援讃歌だ。", "2002-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("岳　ガク", "岳　ガク", "", "", "http://image.eiga.k-img.com/images/movie/55418/315x210.jpg?2590", 125, "山を知り尽くした山岳救助ボランティアの島崎三歩が暮らす山に、新人救助隊員の椎名久美がやってくる。久美は三歩の指導の下、着実に成長していくが、自身の未熟さや厳しい自然の猛威により遭難者の命を救うことができない日々が続く。そんな折、猛吹雪の雪山で多重遭難事故が発生し……。“山岳愛好家のバイブル”とも言われる石塚真一の人気漫画を、小栗旬主演で映画化。小栗とは「ロボコン」（2003）以来約7年ぶりの共演となる長澤まさみが久美役を演じる。", "2011-05-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嗚呼　満蒙開拓団", "嗚呼　満蒙開拓団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "満州事変以降、日本政府の国策によって旧満州（中国東北部）に送りこまれた満蒙開拓団。敗戦までに約27万人が移住したが、そのうち約8万人が帰国できないまま命を落とし、多くの残留孤児や残留婦人を生みだした。自らも旧満州に生まれた羽田澄子監督が、中国政府が黒竜江省方正県に建立した日本人公墓の墓参りツアーに同行。当時を知る人々の貴重な証言を通し、今なお続く満蒙開拓団の悲劇を明らかにしたドキュメンタリー。", "2009-06-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シュガー＆スパイス　風味絶佳", "シュガー＆スパイス　風味絶佳", "", "", "http://image.eiga.k-img.com/images/movie/33648/315x210.jpg?3751", 125, "山田詠美の短編小説「風味絶佳」を原作に、大ヒット映画「冷静と情熱のあいだ」の大多亮プロデューサーと中江功監督が贈る、珠玉のラブストーリー。アメリカの雰囲気が漂う街・福生を舞台に、平凡な毎日を送る少年が初めて経験した“本当の恋”に悩みながらも成長していく姿を描く。主演は、カンヌ国際映画祭主演男優賞を史上最年少で受賞するという快挙を成し遂げた柳楽優弥と、「パッチギ！」で数々の賞を総なめにした沢尻エリカ。", "2006-09-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Up", "カールじいさんの空飛ぶ家", "", "", "http://image.eiga.k-img.com/images/movie/53508/315x210.jpg?2493", 103, "亡き妻エリーとの思い出が詰まった家にひとり静かに暮らしている78歳のカールじいさん。だが、周囲の再開発でその生活が失われそうになったある日、エリーの夢だった南米奥地の秘境を目指すため、人生最後の大冒険に出ることを決意。家に大量の風船をつけて大空へと飛び立つ。ピクサーの長編第10作で、監督は「モンスターズ・インク」でリー・アンクリッチと共同監督を務めたピート・ドクター。", "2009-12-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風来坊探偵 赤い谷の惨劇", "風来坊探偵 赤い谷の惨劇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "深作欣二の第１回監督作品で、中編アクション映画２部作の１本。風来坊探偵の西園寺五郎は、セスナ機で墜落死した土地開発会社社長の本当の死因を調査すべく、赤岩岳山麓の村にやって来た。その村では地元の観光会社が、付近一帯の観光事業を独占すべく、人々に立退きを迫っていた。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FOLIE DES GRANDEURS", "大乱戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "Y・モンタンとL・D・フュネスの二大名優が競演した、フランスならではの味のあるコメディ。悪税を取り立てて私腹を肥やす悪徳大臣と下男。だが甘い日々もつかの間、ある日突然王妃から国外追放を命じられ、二人はパニック状態になる。ビデオ発売時タイトルは「ビッグ・スクランブル／やっぱりパリから出られない」。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAGIC OF LASSIE", "ラッシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "“ラッシー映画”は、11歳のエリザベス・テイラーが主演したMGM作品「家路」以後、数本が製作されたが、これは久々に劇場用に作られたもの。自分を可愛がってくれたやさしい姉弟のもとへ、3000キロの旅を続けるラッシーのけなげな姿が描かれる。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベル・エポック", "ベル・エポック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "逢坂みえこの同名コミックを映画化。恋と仕事に悩みながら懸命に自分探しをする20代の女性の心情を、松岡錠司が繊細に描き出す。女性雑誌の編集者・綺麗は親友の男性が結婚すると聞いて大ショック。女性副編集長の秀子はクールな性格のため心を許せる相手がいない。元アイドルのひとみや小説家の詩音は仕事に、バツイチの良子は理想の相手を求めて奮闘中。それぞれに悩みを抱える彼女らは励まし合い、ケンカをしながら自分自身を見つめていく……。津川雅彦、佐藤浩市、長瀬智也ら友情出演の豪華俳優陣にも注目。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八月の幻", "八月の幻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "グラビアアイドル、仲根かすみのイメージビデオ『もういちど逢いたい』を長編映画へと発展させたノスタルジックなラブ・ストーリー。８年ぶりに故郷の尾道へ恋人・佐知を連れて帰省したマモルは、かつてこの地で昔の恋人、めぐみを亡くした過去があった。様々な記憶が脳裏に甦り、やりきれない思いでいっぱいになるマモル。佐知とともに思い出の地をめぐるうち、彼にしか見えないめぐみの姿が行く先々に現れ始める……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("玄牝　げんぴん", "玄牝　げんぴん", "", "", "http://image.eiga.k-img.com/images/movie/55628/315x210.jpg?2077", 92, "「殯の森」「七夜待」の河瀬直美監督が、自然分娩をテーマに撮りあげたドキュメンタリー。愛知県岡崎市の森の中にある吉村医院。これまで2万例以上もの出産に立ちあってきた自然分娩の第一人者・吉村正院長のもとには、さまざまな理由で自然分娩を希望する妊婦が全国から集まってくる。出産が近づくにつれて輝き始める女性たちの姿を四季の移り変わりとともに捉え、命と向き合う妊婦たちの美しさ、そして生命の神秘を映し出していく。", "2010-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔界転生", "魔界転生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "深作欣ニ監督の「魔界転生」のリメイクに窪塚洋介が挑む異色時代劇。魔界の亡霊となった天草四郎を、窪塚がダイナミックに演じている。1638年の島原の乱で殺された天草四郎が、十数年後徳川幕府への怨念から復活。宮本武蔵ら伝説の剣豪たちを次々に地獄から現世に送り込む。柳生十兵衛はそれを食い止めるべく、孤軍奮闘。ついに四郎との対決の時を迎えるが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASCHENKA", "マーシェンカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "『ロリータ』で知られるV・ナボコフの同名処女小説を映画化。1924年、ベルリンで孤独で虚無的な日々を送っているロシア移民の若者ガーニンの心理を、創作にいき詰まった詩人や、ゲイのカップルといった人々の姿とともに繊細な映像で綴った作品。祖国ロシアへの郷愁、そして初恋の女性マーシェンカについての甘い追憶の日々が幻想的に交錯していく。主人公にはC・エルウェス、マーシェンカには、演出家ピーター・ブルックの娘、I・ブルックがそれぞれ扮している。", "1987-01-01", "2,7,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏の家族", "夏の家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "フランスに在住しヨーロッパを中心に活動する舞踏家・岩名雅記の監督第2作で、南仏を舞台に描くファンタジードラマ。フランス・南ノルマンディに住む日本人舞踏家カミムラのもとへ、東京から妻アキコと8歳の娘マユがやってくる。カミムラは愛人ユズコと同せいしていたが、家族関係は破たんすることなく穏やかだった。ユズコはニューヨークへ旅行に出かけ、カミムラと妻子の平穏な夏の日々が続く。しかし、娘のマユは声はすれども一度も姿を現すことがなく……。", "2010-10-09", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROM MAO TO MOZART", "毛沢東からモーツァルトヘ　—中国のアイザック・スターン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "世界屈指のヴァイオリニスト、アイザック・スターンの、１ヵ月にわたる中国滞在の模様を綴ったドキュメンタリー映画。1979年６月、スターンは中国外相の招待に応じ、家族やピアニスト、デヴィッド・コルブを伴い中国を訪れた。彼の言葉を借りると“中国の人々と友だちになる”ために。公開レッスンとコンサート。その合間の京劇スクール、学校訪問などなど。圧巻は、スターンの指導を受ける子供たちの奏でる音色が、素人耳にもわかるほどに変化していくことだ。“音楽は言葉に勝り、意味深く……”というスターンの言葉通り、言語や国境を超えて人々の心を結びつける音楽の偉大さを今さらながらに痛感。ほのぼのと心温まる佳品だ。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Syriana", "シリアナ", "", "", "http://image.eiga.k-img.com/images/movie/1188/315x210.jpg?1551", 128, "「トラフィック」でアカデミー賞脚本賞を受賞したスティーブン・ギャガンが、全米ベストセラーとなったノンフィクション「CIAはなにをしていた？」（新潮社刊）を元に映画化した社会派群像劇。CIA工作員、アラブの王族、米国の石油企業、イスラム過激派テロリストら石油利権の周辺にうごめく人間たちの運命をドキュメンタリータッチで描く。ウィリアム・ハートやクリス・クーパーといったアカデミー賞俳優が脇を固めるほか、アマンダ・ピート、クリストファー・プラマーらが共演。", "2006-03-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DREAMCHILD", "ドリームチャイルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "世界中の人々に今も愛されている小説『不思議の国のアリス』。その原作者ルイス・キャロルとの淡い恋の思い出を、かつてアリスのモデルとなった老婦人が走馬燈のように回顧していく……。ジム・ヘンソンのマペットなどの特撮と格調高い人間ドラマが見事にドッキングした佳作。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANOTHER WOMAN", "私の中のもうひとりの私", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "短期間で次から次へと傑作を撮り続ける才人W・アレンが、「セプテンバー」に続いて撮った監督第17作。初老に差しかかった一人の女性を主人公に、些細なことがきっかけで“本当の自分”に気付く彼女の姿を、心理サスペンス風のタッチで綴っている。大学で哲学を教えるマリオンは、知的で理性的な女性。医師で優しい夫を持ち、自らの幸福な人生を疑ったことのなかった彼女は、仕事のために静かなアパートを借りる。そんなある日、隣室から聞こえてきた会話。精神分折医に語る患者の赤裸々な告白を耳にした時、彼女の心にかすかな変化が起こり始める。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喧嘩番長　劇場版　一年戦争", "喧嘩番長　劇場版　一年戦争", "", "", "http://image.eiga.k-img.com/images/movie/56358/315x210.jpg?4118", 87, "人気ゲームを実写化した「喧嘩番長」劇場版シリーズ第2作。ずば抜けたケンカの実力をもつ速水勇太は、地区最強の不良校といわれる江南高校に入学。権力争いに野心を抱かず、ただ純粋に強さを求める速水は、自然と周囲の不良たちをひきつけていく。そんなある日、お嬢さま学校に通う白鳥綾乃と出会った速水は、江南高校生であることを隠してデートにこぎつけるが、ケンカに巻き込まれたことから綾乃に身元がバレてしまう。主演はお笑いコンビ「エリートヤンキー」の西島永悟。ヒロイン・綾乃役に人気アイドルグループ「アイドリング!!!」の横山ルリカ。", "2011-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旗本退屈男 江戸城罷り通る", "旗本退屈男 江戸城罷り通る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "元禄の頃、老中・松平左近将監の甥の早乙女主水之介、人呼んで“旗本退屈男”は、時の将軍の御落胤と称して華々しく江戸に乗り込んできた徳川浄海坊の身辺を洗う。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kung Fu Hustle", "カンフーハッスル", "", "", "http://image.eiga.k-img.com/images/movie/1108/315x210.jpg?2106", 103, "「少林サッカー」のチャウ・シンチーが今度も製作・監督・脚本・主演を担当してカンフー映画に挑戦したアクション・コメディ。シンチー演じるチンピラのシンは、町を牛耳る極悪ギャング団に入るのが夢。だが、貧民街の住民達が、カンフーの技でギャングたちを倒していくのを目撃して、カンフーの魅力に取り憑かれていく。ユン・チウやユン・ワーら70年代香港アクションの名スタントマンたちの出演などオマージュも盛り沢山。", "2005-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血風ロック・ The Bloody Afternoon", "血風ロック・ The Bloody Afternoon", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "銀行強盗に入った男の夢と現実をシュールに描くアクション映画。俳優・演出家・プロデューサーとして小劇場界で活躍する流山児祥が初めてメガホンを執った意欲作。演劇風のデフォルメされたシーンが印象的だ。様々な劇団の名優・怪優が共演している点にも注目。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花咲く家族", "花咲く家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "夫の死後、女手一つで３人の兄妹を育ててきた篤子は、長男の嫁と折り合いが悪い。次男の嫁こそは自分の気に入った娘をと考え、親戚の娘を田舎から呼び寄せるが……。子供たちを想う一念から息子たちを束縛してきた母親が、やがて若い世代への理解に目覚めていく姿を描いたホーム・ドラマ。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I LOVE N.Y.", "I LOVE N.Y. ～ニューヨーク恋物語～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ニューヨークの駆け出しカメラマン、マリオは、ブロードウェイのスーパー・スター、イェーツの娘ニコルに一目惚れ。しかしイェーツは、マリオがリトル・イタリーの出身だからという理由で二人の交際を絶対認めてくれない。マリオの熱愛の行方は……。「ダウンタウン物語」のS・バイオが成長した姿を見せてくれる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mission: Impossible", "ミッション：インポッシブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　秘密組織IMFの諜報員に新たな指令が下る。東欧に潜入しているCIA情報員のリストを盗んだプラハの米国大使館員とその買い手を逮捕せよ。ジム・フェルプス以下、イーサンらは作戦を実行するが、あろうことか情報が敵に漏れ、イーサンを除く全員が殺されるという事態に。ひとり生き残ったイーサンは裏切り者を捜し出すべく動き出すが……。往年の大人気テレビシリーズを映画化。世界的スケールでスパイたちの戦いが繰り広げられる。", "1996-07-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REVENGE OF THE NERDS", "ナーズの復讐 集結！恐怖のオチコボレ軍団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "何をやっても、うだつの上がらない学園のオチコボレ集団が、いつも自分たちを目の敵にし、迫害する優等生グループの鼻を明かそうと一致団結。何の取り柄もない彼らが、あり余る性力(？)を武器にしようと思いついた奇想天外な作戦とはいったい……。本国アメリカではヒットした、お下品なコメディ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Twin Town", "ツイン・タウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ウェールズの田舎町スウォンジー。3歳違いの兄弟なのに“ツインズ”と呼ばれるジェレミーとジュリアンは、ドラッグ中毒でトラブルばかり起こしている。彼らの父の骨折をきっかけにして繰り広げられる復讐合戦を、ブラック・ユーモアたっぷりに描く。「トレイン・スポッティング」のダニー・ボイルが製作総指揮を手掛けたことで話題に。主演は「ノッティングヒルの恋人」の個性派俳優リス・エバンスと、彼の実弟リル・エバンス。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("3-Iron", "うつせみ", "", "", "http://image.eiga.k-img.com/images/movie/1728/315x210.jpg?4049", 89, "「魚と寝る女」「サマリア」のキム・ギドク監督が描く、寡黙な青年と人妻の奇妙な物語。チラシ配りをしながら空家を見つけては忍び込み、そこでしばらく過ごすという生活をしている青年が、ある家で夫に暴力を振るわれている人妻に出会う。2人はいっしょに逃避行に出るが、その先でも空家に忍び込み、やがてある空家で死体を見つけてしまう。04年のベネチア国際映画祭で監督賞、国際映画批評家同盟賞など4部門を受賞。", "2006-03-11", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌イレブン", "釣りバカ日誌イレブン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ハマちゃんは、沖縄へ転勤していった吾郎から“釣り三昧の日々を満喫”なる手紙をもらい、地団駄を踏んでいた。そんな折り、スーさんが沖縄に出張することになったことを幸いにハマちゃんはそのお供としてついていくことになったのだが……。監督を、これが２作目となる本木克英にバトンタッチして、気持ちも新たに製作されたシリーズ第13作。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナースコール", "ナースコール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "苦悩する看護婦たちの姿をユーモラスかつ真摯に描きながら、女性の生きがいを問う人間ドラマ。整形外科病棟での仕事がマンネリ化したことを感じ始め、煮え切らない日々を送っている看護婦歴６年の梢。そんな彼女が、腫瘍が発見されて自暴自棄になっている若者の看護に当たることになる。ナースに扮した女優陣の、生き生きとした演技が魅力的。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BONES", "BONES〈ボーンズ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ラップ・アーティスト、S・ドッグの主演によるオカルト・ブラック・ムービー。恨みを残して死んだギャングスターにふんするスヌープのカリスマ的な魅力が光る。４人の若者がクラブ経営のために買い取った古い屋敷。そこは非業の死を遂げたギャングの住み家だった。不審な事件が続発の不吉な予感を漂わせつつ、やがてクラブのオープンの日、怨霊が復讐のために目を覚まし……。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIMES SQUARE", "タイムズ・スクエア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ニューヨークのタイムズ・スクエアを舞台に、ふとしたことから知り合った純情な家出娘と、街の不良娘との交流を描いた心温まる青春映画。二人のヒッピー的生活と街の風格、住人たちの細やかな人情が描き出される。全編に流れる軽快なロックもさわやか。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Anaconda 3", "アナコンダ3", "", "", "http://image.eiga.k-img.com/images/movie/54578/315x210.jpg?4023", 91, "大蛇アナコンダの恐怖を描いたシリーズの第3弾。女性科学者のアマンダは、密林にある施設で2匹の巨大な蛇を使った不治の病の治療に関わる研究を行っていたが、投資家が実験を急がせたため、大蛇が檻を突き破って逃走してしまう。産卵の準備で凶暴化した蛇は、餌と人間を求めて街へ向かい、アマンダは蛇ハンターのハーマットらと共に蛇を追跡するが……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RASTAFARIANS AND THE MYSTIQUE OF BOB MARLEY", "ラスタファリアンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "レゲエ音楽によって世界中に広められた“ラスタファリ”という思想。その本質をボブ・マーリィらレゲエ・ミュージシャンの姿を通して探っていく。ラスタ信仰のルーツでもあるエチオピア皇帝ハイレ・セラシエの存在、マーカス・ガーヴェイの予言とは？　また彼らのトレードマークともいえるドレッドヘア、マリファナなどの周辺文化についても検証し、音楽に込めて発信されてきた思想に迫る。", "2005-01-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAN’T STOP THE MUSIC", "ミュージック・ミュージック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "明日のスターを夢見てニューヨークに集まる若者。世界でいちばんホットな町は、いつでもはじけるエネルギーに満ちている。傷つき疲れながらも、決してくじけない——そんな若者の青春像を追ったこの映画は、大画面・超ステレオ音響で、そのエネルギーを再現する。ビレッジ・ピープルの主題歌も忘れがたい。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("非行少年 陽の出の叫び", "非行少年 陽の出の叫び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "藤田繁矢（藤田敏八）の自らのオリジナル・シナリオによる監督昇進第１作。更生を誓って少年院を出た純は、指導員の家に引き取られるが素行は相変わらずだった。やがて昔の仲間の集団脱走計画に巻き込まれるものの、結局純は彼らの行動にも共鳴できない。藤田繁矢監督は従来の不良少年もののパターンを踏襲しているが、すべてをクダラないと感ずる少年像の生々しい存在感には、強烈なオリジナリティーが感じられた。このシラけきった少年像は、日活が誇る青春映画の１本「八月の濡れた砂」（1971）においてより深く描き込まれ、一つの完成をみる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リサイクル　死界", "リサイクル　死界", "", "", "http://image.eiga.k-img.com/images/movie/1908/315x210.jpg?1997", 0, "ヒット作「The EYE／アイ」のリメイク権をトム・クルーズが獲得し、サム・ライミ製作による「ゴースト・ハウス」でハリウッドデビューを飾ったタイの兄弟監督、オキサイド＆ダニー・パンによるサスペンス・ホラー。次回作の執筆が難航する人気作家ディンイン（アンジェリカ・リー）は、自身が考えては破棄していった小説の設定が、周囲で現実に起こりはじめ、やがて現実と虚構の狭間で混乱しいくが、その現象は止まることを知らず……。", "2007-06-23", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おかあさん", "おかあさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "全国の小学生が書いた作文に着想を得た水木洋子のオリジナル脚本を成瀬巳喜男が演出した名作。都会の下町に暮らす一家が世帯主である父親を病気で亡くし、母を中心に困難な状況を生きる姿を綴っている。導入部における作文の朗読のようなナレーションによって物語は長女の視点をとり、日常の小さな出来事や人々の微妙な心の揺れが、思春期の少女らしい鋭敏さで細やかに捉えられる。父親の死、里子へ行く次女との別れなどの不幸な事件をも劇的な誇張を排して淡々と描く成瀬監督の作風は、その繊細さゆえに豊かな情感を生み出し、登場人物への親愛の情をかきたてられずにはおかない。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("首領（ドン）を殺（と）れ", "首領（ドン）を殺（と）れ", "", "", "http://ecx.images-amazon.com/images/I/514ME55BL2L._SL210_.jpg", 78, "「ITバブルと寝た女たち」の金子昇主演による任侠アクション。半澤組大鶴一家の若頭補佐・鍋島は、同じく半澤組系の4人の男たちと共に、本部からある場所に呼びだされる。やがて彼らの前に現われた半澤組若頭の忍足は、男たちに「平成日本の首領」と呼ばれる神威一家総長・金丸の暗殺を命じるが……。これまで多くの作品でガンエフェクトを手がけてきた浅生マサヒロ監督による激しいアクションシーンが見どころ。", "2010-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIZARD WOMAN", "トカゲ女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "パワフルな娯楽作を量産するタイ映画界が、とんでもないバケモノ映画を製作。悪霊にとり憑かれ、“人喰いトカゲ女”に変貌した女流作家の惨劇が繰り広げられる。CGや特殊メイクを駆使し、気合い十分のショック描写に驚愕必至の怪作だ。新作ホラー小説の宣伝でチェンマイを訪れた女流作家クワン。民芸品の木箱を購入した彼女の周辺で、不可解な変死事件が続発し始める。恋人ウィトゥーンは彼女の言動を不審に思うが……。", "2006-04-08", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Escape from L.A.", "エスケープ・フロム・L.A.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "　大地震直撃の末、孤島化したロサンゼルスは犯罪者や反逆者の流刑地となった。そして2013年、大統領の娘がその政策に異議を唱え、国家機密を奪ってロサンゼルス島に逃亡。その機密を奪還するために、かつてニューヨークで大統領を救出した犯罪者スネークが送り込まれる。しかし彼の行く手には、アメリカ侵略を目論む悪党クエボが待ち受けていた！　「ニューヨーク1997」の続編となるSFサスペンス・アドベンチャー。前作よりブラックユーモアが色濃くなっている。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心中天使", "心中天使", "", "", "http://image.eiga.k-img.com/images/movie/55928/315x210.jpg?4332", 92, "「殯の森」「トロッコ」の尾野真千子が主演するファンタジー・ドラマ。「溺れる人」の一尾直樹が監督・脚本を兼ねる。ある日、両親とともに暮らすピアニストのアイ（尾野）の心の中に、空から突然“何か”が落ちてくる。同じころ、会社員のユウと高校生のケイも全く同じ感覚に陥り、気を失ってしまう。そのときから、3人は些細な出来事が実は大切なメッセージを伝えているような不思議な思いに取りつかれるようになる。やがて、ナゾの女性の出現をきっかけに、3人の心はシンクロしはじめ……。", "2011-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心動／TEMPTING　HEART", "君のいた永遠〈とき〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "20年におよぶ男女の葛藤を、女優としても有名なシルヴィア・チャン監督がドラマティックに描いたラブ・ストーリー。1977年、女子高生のシューヤウは受験生のホークァンと恋仲に。そんな彼女を、親友のチャンリーは複雑な思いで見守っていた。やがて恋は破局を迎えるが、７年後、旅行代理店で働くホークァンは日本でシューヤウと再会。二人の思いは再燃するが、ホークァンはチャンリーと結婚していた……。後戻りのできない、もつれた関係が切なさを醸し出す。ジジ・リョンがシューヤウ役をキュートに演じれば、「天使の涙」の個性派カレン・モクは女心の機微をリアルに好演。魅力的な彼女たちと金城武のアンサンブルも見どころ。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES AMAZONES", "アマゾネス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "強大な戦闘能力を誇る女だけの部族アマゾネス。その中でも最強の誉れ高い女王アンティオペーが、種族存続の種つけのため招き入れたギリシャ軍長官と恋に落ち、部族は崩壊していく……。画面を闊歩する肉体派美女の姿が生々しく艶やかで、不思議なムードを持った神話物語。", "1973-01-01", "4,2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍虎門／THE CHINESE BOXER", "吼えろ！ドラゴン 起て！ジャガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "中国産カンフーと日本産空手の対決という構図で展開する、香港製活劇。「片腕ドラゴン」を撮った監督兼主演のジミー・ウォングが、凄まじい死闘復讐アクションに仕上げた。中国の道場破りに来たヤクザだが返り討ちに遭い、街から追放された。空手を学んで日本の達人たちと共に再び道場へ。今度は道場のほぼ全員が殺されるが、生き残った主人公が復讐を誓う。", "1970-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ディア・ドクター", "ディア・ドクター", "", "", "http://image.eiga.k-img.com/images/movie/53838/315x210.jpg?2960", 127, "「蛇イチゴ」「ゆれる」の西川美和監督が、笑福亭鶴瓶を主演に迎え、僻地医療を題材に描いたヒューマンドラマ。都会の医大を出た若い研修医・相馬が赴任してきた山間の僻村には、中年医師の伊野がいるのみ。高血圧、心臓蘇生、痴呆老人の話し相手まで一手に引きうける伊野は村人から大きな信頼を寄せられていたが、ある日、かづ子という独り暮らしの未亡人から頼まれた嘘を突き通すことにしたことから、伊野自身が抱えいたある秘密が明らかになっていく……。", "2009-06-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVY　METAL", "ヘヴィメタル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アメリカのアダルト向けコミックから派生したオムニバス・アニメーション。“ロック・ナー”と呼ばれる邪悪な緑色の球体をめぐる、エピソード７話で構成されている。ロック・ナーによって生き返る第二次大戦の戦死者、その不思議な魔力を求める争奪戦、異星にもたらされた異変など、いずれもSF色の強い内容。ナザレス、チープ・トリックなどのハードロックバンドのナンバーが、幻想的な映像を彩る。", "1981-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRESURE PLANET", "トレジャー・プラネット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "あまりに有名なスティーヴンソンの冒険小説『宝島』をSFに翻案したディズニー・アニメ。宇宙に飛び立つ冒険のスリル、コミカルなキャラなど、随所にディズニーらしさがうかがえる一方、２Dと３Dを合体させた新しい試みも見受けられる。財宝が眠る伝説の星の地図を手に入れた、15歳のジム。宇宙船レガシー号の乗組員としてこの星を目指す彼はベテラン・クルー、シルバーから宇宙で生きる術を教わり、信頼を寄せるようになる。だが、シルバーはある陰謀を企てており……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("せんせい", "せんせい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "“よい映画を作りたい”と松方・梅宮・山城らが共同出資で設立したプロジェクト、トム・ソーヤ企画の第１回作品。昔ながらの風情を残す東京・佃島。ウォーターフロント計画で廃校の危機に瀕している中学校を舞台に、型破りな先生と生徒の交流、下町特有の温かい人情などが描かれる。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブリュレ", "ブリュレ", "", "", "http://image.eiga.k-img.com/images/movie/53988/315x210.jpg?3165", 71, "一卵性双生児の近親相姦を繊細なタッチで描いた意欲作。映画初出演の一卵性双生児の中村梨香と美香が主演、監督・脚本は本作が劇場デビューとなる林田賢太。東北の田舎町で、放火を繰り返す女子高生の日名子には、13年前の事故により離れて暮らしている双子の妹、水那子がいる。ある日、水那子が祖母の遺骨を持って九州から日名子の暮らす町へやって来る。叔父の経営する洋菓子屋で2人は一緒に暮らし始めるが、水那子は嘘をついていた。", "2008-10-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COP　LAND", "コップランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "マンハッタンにほど近い静かな街ギャリソン。ニューヨーク市警の多くの警官が住み“コップランド”と呼ばれるこの街は、ベテラン警官レイによって支配されていた。ある夜、警官ハビッチが暴走車を運転する黒人青年を誤って射殺する。混乱したハビッチは川に身を投げ、彼の伯父レイによって事件は警官の殉死という形で片付けられる。そんな中、耳が不自由なため警官になれなかった保安官のフレディは、死んだはずのハビッチを目撃する。不審を抱くフレディの前に、やはり事件に疑問を抱き始めていた内務調査官モーが現れ、捜査協力を依頼。義務感と権力の間で苦悩するフレディは次第に追い詰められていく……。豪華スターの競演で、B級サスペンス風のストーリーを丁寧に語った異色作。その意外性もさることながら、スタローン扮する主人公を待ち受ける空白のクライマックスと空虚なラスト・シーンの哀切さが、映画の味わいを非凡なものにしている。“正義の誘惑”を体現したデ・ニーロの存在感もユニークだ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アインシュタインガール", "アインシュタインガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "「富江」「ラヴァーズ・キス」の及川中監督が手掛けた、SFファンタジー。タイムスリップした女子高生を主人公に、時空を駆け巡る切ない家族物語が展開する。多感な10代の少女を映画初主演となる岩佐真悠子が等身大で好演。１年前に母親を交通事故で亡くした薫。時空の歪みに紛れ込み、１年と２日前にタイムスリップした彼女は、母を助けようとするが……。", "2005-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コミック雑誌なんかいらない！", "コミック雑誌なんかいらない！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "ピンク映画出身の滝田洋二郎が内田裕也と組んで撮った初めての一般映画。芸能レポーターという特異な存在に焦点を当てながら、事件やスキャンダルを好む現代の大衆を鋭く皮肉っている。そのテーマ性ゆえか、ニューヨーク映画祭で上映され、大きな反響を呼んだ。芸能レポーターのキナメリは、有名人のスキャンダルをハイエナのごとくかぎ回るので有名だ。そんな彼は、当然芸能人にも嫌われ、時に暴力を振るわれたりもするが、少しもひるむことはない。しつこく、あくどく、無神経に他人の生活に踏み込んでいく……。現実の事件の関係者を登場させたり、再現したり、キワモノすれすれの刺激的な映画である。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOOK OF SHADOWS: BLAIR WITCH2", "ブレアウィッチ2", "", "", "http://ecx.images-amazon.com/images/I/41joJgdzG6L._SL210_.jpg", 91, "口コミでうわさが広がり、大ヒットしたホラー「ブレア・ウィッチ・プロジェクト」の続編。ドキュメンタリー映画で評価の高いJ・バーリンジャーが監督を務め、前作でナゾに包まれたままだった秘密を解きほぐしていく。今回もセミ・ドキュメンタリーの手法を用いているが、前作に比べ、より劇映画に近い形となった。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCREAM AND SCREAM AGAIN", "バンパイアキラーの謎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "米国AIPと英国アミカス・プロの合作ホラー映画。アメリカで、奇妙な殺人が起こる。死体に血がないのだ。病理学者は吸血鬼の仕業だと思うが、実はサイボーグを作るマッド・サイエンティストの犯行だった。ホラー役者ビッグスリーの顔合わせが見ものだ。", "1970-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Keeper", "沈黙の逆襲", "", "", "http://image.eiga.k-img.com/images/movie/54858/315x210.jpg?3808", 94, "スティーブン・セガール主演の刑事アクション。ロサンゼルス市警の刑事・ローランドは、金に目のくらんだ同僚に命を狙われる。同僚を返り討ちにしたローランドだったが、その事件をきっかけに刑事を辞め、ニューメキシコへと移り住むことに。ローランドはボディガードとして人生を再出発しようとするが、そこで再び仲間の裏切りに合う。", "2009-10-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワンダフルライフ", "ワンダフルライフ", "", "", "http://ecx.images-amazon.com/images/I/41DoQUonYZL._SL210_.jpg", 118, "「幻の光」の是枝裕和が、人が死んでから天国へたどりつくまでの7日間を描いたファンタジードラマ。霧に包まれた施設にたどりついた22人の死者たちは、待ち受けていた職員から、天国へ行くまでの7日間で人生で一番大切な思い出を選ぶように指示される。すぐに思い出を選ぶ者もいれば、なかなか選べない者もいた。しかし試写会の開かれる最終日は刻々と迫ってくるのだった。主演は本作が映画デビューとなるARATA。", "1999-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・特出しヒモ天国", "喜劇・特出しヒモ天国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "松竹で“女”シリーズを撮った森崎東が、初めて他社で撮った作品。サラリーマンの昭平はストリップ小屋で警察に捕まってしまう。そのことがきっかけになって、昭平はストリップ小屋の支配人に転職。一方、昭平を捕まえた刑事はストリッパーのヒモとなる。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Basic", "閉ざされた森", "", "", "http://image.eiga.k-img.com/images/movie/1278/315x210.jpg?2810", 98, "「ダイ・ハード」のジョン・マクティアナン監督が、熱帯のジャングルを舞台に描く二転三転サスペンス。レンジャー部隊教官が密林での訓練中に不審な死を遂げるが、関係者が証言するたびに、事態は次々に新たな様相を見せていく。いったい何が起きたのか。最後に思いもよらなかった真相が判明する。「パルプ・フィクション」コンビ、トラボルタが事件を捜査する元レンジャー隊員役、サミュエル・L・ジャクソンが死んだ教官役で共演。", "2003-09-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZOMBIE ２", "サンゲリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "カニバリズムを強烈に描いた残酷描写満載のゾンビもの。カリブ海のある島で発生した奇病は、その病いで死ぬと生き返り、人を襲うというものだった。ウジのたかった腐乱死体も不気味だが、なにより、人間の体を、ところかまわず噛みちぎるゾンビの襲い方がすさまじい。", "1979-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　つきことしらたま　ときめきダンシング", "それいけ！アンパンマン　つきことしらたま　ときめきダンシング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "やなせたかし原作の人気アニメーション劇場版第16作「それいけ！アンパンマン　夢猫の国のニャニイ」と同時上映の短編。", "2004-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DRAUGHTSMAN’S CONTRACT", "英国式庭園殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "P・グリーナウェイの長編第１作。17世紀末の英国南部。画家のネヴィルは、郷紳（ジェントリー）のハーバート家に招かれ、夫人のヴァージニアから、不在の主人が戻るまでに屋敷の絵を12枚描くことを頼まれる。ネヴィルは様々な条件を出した末に、この仕事を引き受けたが、彼の描こうとする構図の中に、何故か不在のハーバート氏のシャツやマントが紛れ込んでくる。これは、何の暗示なのか？　12枚の絵の完成後、ハーバート氏の死体が発見され、ネヴィルの疑問は、いよいよふくれ上がり……。上流階級の一家が、様々な策謀をめぐらすミステリー。謎が謎のまま残されるあたりが、作品に深みを与えている。", "1982-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAND THAT TIME FORGOT", "恐竜の島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "第一次大戦下、輸送船がUボートに撃沈された。救命ボートで逃げ出した数人は浮上したUボートを逆に乗っ取って漂流し、ある島へたどり着く。そこには太古の生物が生き続けていた……。「サンダーバード」のスタッフが担当したミニチュアが見もの。", "1975-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハヴァ、ナイスデー", "ハヴァ、ナイスデー", "", "", "http://ecx.images-amazon.com/images/I/51KHjLGLCoL._SL210_.jpg", 211, "ウェブサイト「短編.jp」から生まれた短編オムニバス映画。「ストロベリーショートケイクス」の矢崎仁司、「夜のピクニック」の長澤雅彦、「タカダワタル的」のタナダユキら総勢18人の気鋭クリエイターが集結し、「1日」をテーマにした物語を、24時間という制限時間内で撮りあげた。キャストには、モデルとして活躍するJOSI、「ナビィの恋」の村上淳、ミュージシャンの鈴木慶一ら多彩な顔ぶれがそろう。", "2006-10-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEYOND THE DREAM’S DOOR", "ヘルビヨンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "夜な夜な血みどろの悪夢に悩まされている大学生ベン。やがて夢の中の魔獣が現実世界に侵入、自分の存在に気付いた人間を襲い始める。ベンは心理学教室の助手とともに、魔獣との戦いを強いられる……。監督は、オハイオ大学映画科出身で、26歳の若さのJ・ウォルフェル。独特のスプラッタ演出にエロティック・シーンを加えている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが母の記", "わが母の記", "", "", "http://image.eiga.k-img.com/images/movie/56018/315x210.jpg?4658", 118, "井上靖の自伝的小説「わが母の記」3部作（講談社文芸文庫刊）を、「クライマーズ・ハイ」の原田眞人監督が映画化。役所広司、樹木希林、宮崎あおいら実力派キャストで10年間にわたる親子、家族の愛を描く。昭和39年、小説家の伊上洪作は、父が亡くなり母・八重の面倒を見ることになる。幼少期に母と離れて暮らしていたため距離を置いていた洪作だったが、妻や3人の娘、妹たちに支えられ、自身の幼いころの記憶と八重の思いに向き合うことに。八重は薄れゆく記憶の中で息子への愛を確かめ、洪作はそんな母を理解し、次第に受け入れられるようになっていく。第35回モントリオール世界映画祭ワールド・コンペティション部門で審査員特別グランプリを受賞。", "2012-04-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カフェ代官山", "カフェ代官山", "", "", "http://image.eiga.k-img.com/images/movie/53448/315x210.jpg?3638", 67, "「ボーイ・ミーツ・プサン」の武正晴監督がメガホンを取り、とあるカフェで繰り広げられる人間模様を描いた青春ドラマ。ダンスが得意なリーダーのヒビキ、牧師の息子でパティシエのサーディン、琴占いで何でも予言してしまうコトッチ。そんな個性的な面々が働くカフェに、夢と希望を胸に抱くパティシエ志望の青年ガクが修行に訪れる。主演には「テニスの王子様」の相葉弘樹をはじめ、大ブレイクが期待される4人の若手俳優が集結。", "2008-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちんちろまい", "ちんちろまい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "オール福岡ロケで、博多出身のタレントが総出演したローカル色豊かなコメディ。観光協会に務める善哉は転勤を嫌い、愛する博多に残るため、どんたく祭に併せて行われる県主催のハイテク祭プロジェクトに参加。そのころ、女スパイが強奪したCD-ROMが博多沖に流れついた。偶然これがハイテク祭に紛れこんだことから、善哉はマフィア絡みの騒動に巻きこまれ、さらに県庁のコンピューターがパニックに陥る……。アクションやロマンス、サスペンス、ミュージカルも飛び出すにぎやかなドラマが展開する一大エンターテインメント。", "2000-10-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("のぼうの城", "のぼうの城", "", "", "http://image.eiga.k-img.com/images/movie/55578/315x210.jpg?2879", 144, "第29回城戸賞を受賞した和田竜による脚本「忍ぶの城」を、「ゼロの焦点」の犬童一心監督と「ローレライ」の樋口真嗣監督が共同でメガホンをとり映画化。主演は野村萬斎、共演に佐藤浩市、成宮寛貴ほか。舞台は武蔵国忍城（埼玉県行田市）。“のぼう様（でくのぼうの意）”と領民から慕われる城代・成田長親は、天下統一を目指す豊臣秀吉方2万人の大軍を指揮した石田三成の水攻めに、わずか500人の兵で対抗する。", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画監督アンジェイ・ワイダ　祖国ポーランドを撮り続けた男", "映画監督アンジェイ・ワイダ　祖国ポーランドを撮り続けた男", "", "", "http://image.eiga.k-img.com/images/movie/55228/315x210.jpg?3557", 109, "「灰とダイヤモンド」「鉄の男」などの名作で知られるポーランドの巨匠、アンジェイ・ワイダ監督に迫るドキュメンタリー。社会主義政権下で、自由な表現を求め続けてきたワイダ監督の、長く孤独な戦いを描く。NHKで放送されたドキュメンタリー番組を劇場で公開するプロジェクト「This is REAL／NHK-BSドキュメンタリー・コレクション」にて上映。", "2010-02-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Spy Kids", "スパイキッズ", "", "", "http://image.eiga.k-img.com/images/movie/1668/315x210.jpg?3081", 88, "かつて凄腕のスパイだったグレゴリオとイングリットは、今や幸福な家庭のよき父と母。9年振りに捜査に乗り出した2人は敵に捕まり、彼らの娘と息子が彼らの救出に向かう。「デスペラード」「パラサイト」のロドリゲス監督の子供スパイ・アクション。スパイグッズに加え悪人により変形された怪人たちが多数登場。音楽はティム・バートン作品の常連ダニー・エルフマン。作中の悪人フループが司会者を務める子供番組の挿入歌も担当。", "2001-12-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Brothers Bloom", "ブラザーズ・ブルーム", "", "", "http://image.eiga.k-img.com/images/movie/1938/315x210.jpg?3292", 0, "「BRICK　ブリック」で脚光を浴びたライアン・ジョンソン監督作。エイドリアン・ブロディとマーク・ラファロ演じる詐欺師の兄弟が、ターゲットの未亡人（レイチェル・ワイズ）に心惹かれてしまったことからトラブルに巻き込まれる。「バベル」でオスカー助演女優賞にノミネートされた菊地凛子が女性詐欺師を演じる。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Nightmare On Elm Street", "エルム街の悪夢", "", "", "http://ecx.images-amazon.com/images/I/516NAFR4S9L._SL210_.jpg", 91, "エルム街に暮らすナンシーら高校生たちに異変が起こる。夢の中に鋭利な鉄の爪をつけた怪人が現れ、彼らを脅かすのだ。その後、友人のひとりティナが惨殺されるという事件が。一方、夢の恐怖は現実化し、ナンシーの体には傷まで残っていた。悪夢とともに殺人事件が続く中、ナンシーは怪人フレディにまつわる秘密を知り、彼と闘うことに……。夢の中のみならず、現実にも出現する殺人鬼の恐怖を描き、大ヒットしたホラーシリーズの第1弾。", "1986-05-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Roaring Twenties", "彼奴は顔役だ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "", "1955-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOREVER", "フォーエバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "S・ヤングが、美しい幽霊に扮したミステリー・タッチのファンタジー。MTVの腕利きディレクター、テッドは、ある日誘われるように豪華なマンションに入り込み、無声映画のスター、メアリーの幽霊と出会う。彼女は、知り合いの映画監督を殺した犯人を探すため、この世に来ていた。テッドは、メアリーの捜査に協力することになって……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRING ON THE NIGHT", "スティング ブルー・タートルの夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "元ポリスのボーカル、スティングが黒人ジャズ・ミュージシャンと組んで新たな活動を進めていく姿を捉えたドキュメンタリー。レコーディングやライヴ・シーンに加え、彼の妻の出産シーンと、それに立ち会うスティングの素顔なども盛り込まれた佳編。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST TIME I COMMITTED SUICIDE", "死にたいほどの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ヒッピーの教祖的存在だった、ニール・キャサディの若き日を描く青春群像劇。退廃的な生活をしている青年ニールは、傷つきやすく、自殺未遂の過去を持つ女性ジョアンと恋に落ちる。彼女との幸せな生活を夢見るようになった彼は、就職することを決意、面接のために友人宅に置いてあるスーツを取りに行く。ニールがいなくなることを極端に恐れるジョアンのためにも早く戻らなければならない。途中、かつての恋人に再会し、誘惑にも負けなかったニールだが、彼を快く思わない彼女の母親によって思わぬ事態に巻き込まれていく……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUST ONE OF THE GUYS", "彼女はハイスクール・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ジャーナリストを目指す女子高生テリー。だが容姿端麗が災いし、教師も友人もまったく取りあってくれない。憤慨したテリーは、男ならこんな差別は受けないとばかりに男子学生に変装。隣の学校へ意気揚々と転校するが……。「トッツィー」的発想が楽しめる痛快な学園もののロマンティック・コメディ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("同棲時代 —今日子と次郎—", "同棲時代 —今日子と次郎—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "1970年代初頭に大ブームを呼んだ上村一夫の劇画を新人・山根成之が演出した。広告代理店に勤めるOLが売れないイラストレーターと同棲を始める。やがて彼女は妊娠する……。由美かおるのヌードが話題となった。「続・同棲時代の愛—くらし—」も同年に作られた。DVDは「由美かおる　DVD-BOX」に収録。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンデルセン物語", "アンデルセン物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "アンデルセン靴店の息子ハンスとマッチ売りの少女エリサの住む町に、世界中の子供に夢を配って歩く眠りの精オーレおじさんがやって来た。おじさんはハンスの父親に靴を直してもらったお礼に、赤い革を置いていくが……。童話作家アンデルセンの少年時代に、彼が創作した名作童話の数々のエピソードを元ネタ的に織り交ぜながら描いた長編アニメーション映画。脚本には井上ひさしも参加している。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pineapple Express", "スモーキング・ハイ", "", "", "http://image.eiga.k-img.com/images/movie/53618/315x210.jpg?4243", 112, "「40歳の童貞男」のセス・ローゲン、「スパイダーマン」シリーズのジェームズ・フランコが主演するコメディ。マリファナ中毒のデールとソールは、ある日、“パイナップル・エクスプレス”という極上なマリファナを手に入れて大興奮。しかし、それを車中で吸っていたデールが偶然にも殺人事件を目撃してしまい、さらに焦ってマリファナを現場に投げ捨ててしまったことから身元が割れ、犯人から命を狙われる羽目になる……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRANGERS WHEN WE MEET", "逢う時はいつも他人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "妻子ある建築家ラリーと美しい人妻マギーとの許されない愛。宿命に引き裂かれる二人の哀しくも美しい大人のラブ・ストーリー。「暴力教室」の原作者E・ハンターが自身の同名小説を脚色。K・ダグラスとK・ノヴァク共演で映画化され、「恋におちて」などの不倫ドラマの先駆けとなった作品。DVDは「キム・ノヴァク DVDトリプルパック」に収録。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Red", "RED レッド", "", "", "http://image.eiga.k-img.com/images/movie/55798/315x210.jpg?1830", 111, "ウォーレン・エリス＆カリー・ハマーの同名グラフィック・ノベルを、ブルース・ウィリス、モーガン・フリーマン、ジョン・マルコビッチ、ヘレン・ミレンの豪華共演で映画化したアクション・コメディ。かつてCIAの工作員だったフランク、ジョー、マービン、ビクトリアの4人は、内部機密を知りすぎているという理由でCIAの暗殺対象者になってしまう。4人は生き残りをかけてCIA本部に侵入するが……。監督は「フライトプラン」のロベルト・シュベンケ。", "2011-01-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIABOLIQUEMENT VO＾TRE", "悪魔のようなあなた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "自動車事故で記憶喪失になった男が殺人事件に巻き込まれ、犯人に仕立てあげられる。記憶を取り戻した男は真相の究明に乗り出すが、やがて殺された男の妻を愛するようになり……。自動車事故で急逝したデュヴィヴィエ監督の遺作。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　みんな集まれ！アンパンマンワールド", "それいけ！アンパンマン　みんな集まれ！アンパンマンワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "やなせたかし原作の人気アニメーション劇場版第6作「それいけ！アンパンマン　リリカル☆マジカルまほうの学校」と同時上映の短編。「メロンパンナとあかちゃんまん」「こむすびまんとお祭りロボット」「すすめ！アンパンマン号」の3話構成。", "1994-07-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOWER OF TERROR", "ダイハード・ビクセン 地獄の巨乳戦士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ビバリーヒルズの高層ビルにある女性下着会社で、深夜５人の美女たちが残業をしていた。そこへ間違って配達されてきた荷物。中にはホラー研究家あての恐ろしい悪霊が封じ込められていた……。閉ざされたビルで襲ってくる正体不明の殺人鬼と美女たちの死闘をスリリングに描くホラー・アクション。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SANTA CLAUS: THE MOVIE", "サンタクロース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "子供たちの危機を、サンタクロースが救うというSFファンタジー。サンタクロースの人気をねたむ妖精パッチが、空を飛べるキャンディーを発売したことから、空中衝突してケガをする子供たちが続出し、街は大混乱に。しかもそれには、大爆発を起こす危険さえあったのだ……。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Diary of a Wimpy Kid: Rodrick Rules", "グレッグのおきて", "", "", "http://ecx.images-amazon.com/images/I/51IYcJkAbjL._SL210_.jpg", 0, "", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新世紀エヴァンゲリオン劇場版　シト新生", "新世紀エヴァンゲリオン劇場版　シト新生", "", "", "http://ecx.images-amazon.com/images/I/51KAKEN2CHL._SL210_.jpg", 99, "1995～96年にかけて放送された、庵野秀明監督によるTVアニメーション「新世紀エヴァンゲリオン」の劇場版。当初の予定では全26話のTV版のうち、1～24話の総集編となる「DEATH」編と、さまざまな物議をかもした25～26話を新たに描きなおす「REBIRTH」編の2部構成の予定だったが、製作の遅れにより「REBIRTH」編の途中まで（25話に相当する部分まで）のかたちで公開となった。のちに最後まで完成させた「新世紀エヴァンゲリオン劇場版 Air／まごころを、君に」が公開されている。", "1997-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANDANGO", "ファンダンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "大学卒業したての５人の若者が、“大人になる”前に、今一度、大胆にバカ騒ぎ＝ファンダンゴしようと旅に出る。誰もが一度は直面する青春時代の不安と焦燥を、さらりと描いた青春ドラマ。エルトン・ジョンなど、舞台となる1971年のヒット曲が懐かしい。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЛЕНИН　В　ЛОЛЫЦЕ", "ウラジーミル・レーニンの想い出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1914年の夏、祖国を離れポーランドに亡命したソ連の革命家、レーニンの姿を描く。オーストリアとハンガリー政府の官憲に敵国のスパイという名目で逮捕されたレーニン。彼は小さな田舎町の監獄の中で、様々な思いをめぐらす。“人間レーニン”の素顔に迫ったユトケーヴィッチ監督の力作。", "1965-01-01", "12,42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人のセックスを笑うな", "人のセックスを笑うな", "", "", "http://image.eiga.k-img.com/images/movie/53258/315x210.jpg?2125", 137, "第41回文藝賞に輝いた山崎ナオコーラの同名小説を、松山ケンイチ、永作博美、蒼井優ら豪華キャストで映画化した恋愛ドラマ。「犬猫」で高い評価を受けた井口奈己監督が、年の差カップルの切なくも可笑しい恋模様をリアルに描き出す。美術学校に通う19歳の青年みるめは、20歳年上の講師ユリに絵のモデルを頼まれ、彼女のアトリエでそのまま関係を持ってしまう。初めて経験する恋に舞い上がるみるめだったが、実はユリには夫がおり……。", "2008-01-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.STITCH", "MR.STITCH　悪魔の種子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「パルプ・フィクション」で数々の脚本賞を獲得したQ・タランティーノの盟友、R・エイヴァリーが「キリング・ゾーイ 破滅への銃弾」に続いて監督、脚本を手掛けた作品。狂信的な科学者ウェイクマン博士は、88人の人間から様々なパーツを取り出し、一体のクリーチャー、ラザロを完成させた。強靭な肉体と優秀な頭脳を持つラザロは、政府の最高機関によって究極の兵士として誕生したのだ。しかし、ラザロの身体を構成する死者たちの記憶がラザロを苦しめ始め、精神分析医のエリザベスはラザロに催眠治療を施した。そこで、エリザベスは、ラザロの口から意外な真実を知ることになる。特殊メイクは「ゾンビ」のメイクなどで知られるトム・サヴィーニが担当、作品に凄味を加えている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES AMANTS DU TAGE", "過去をもつ愛情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ポルトガルのリスボンで出会った男と女。だが、ピエールには妻殺しの、カスリーンには夫殺しの暗い過去があった……。アマリア・ロドリゲスの歌うファド『黒いはしけ』が二人の恋の悲しい宿命を暗示する。罪を背負った男女の哀しい運命を描いたメロドラマ。", "1954-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RESTAURANT", "レストラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "マンハッタンにほど近いニュージャージー州ホーボーケンの街で、レストランのバーテンダーをしている25歳の青年クリス。劇作家としての成功を夢見ながら、女性関係や人種差別などのトラブルに直面してしまう彼の葛藤を、洗練されたタッチで綴る青春映画。人気シンガー、L・ヒルが、主人公の元恋人役で出演している。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン／14番目の標的〈ターゲット〉", "名探偵コナン／14番目の標的〈ターゲット〉", "", "", "http://ecx.images-amazon.com/images/I/61lY7dNF5iL._SL210_.jpg", 99, "「名探偵コナン／時計じかけの摩天楼」から１年ぶりに作られたヒット・アニメ第２作。名探偵コナンの仲間たちが何者かに命を狙われ、現場にトランプが残されるという怪事件が起こる。真相を追って謎めいた海中レストランにたどり着いたコナンを、恐るべき罠が待ち受けていた。", "1998-04-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("湾岸バッド・ボーイ・ブルー", "湾岸バッド・ボーイ・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1981年発表時、名デザイナー、ハンス・ムートによる斬新な造形美が世界中で好評を博したオートバイ“GSX110OS KATANA”。生産が中止された今も一部で熱狂的支持を集めているこの名車を手にした一人の若者の青春像を描く。バイク同士のチェイスも織りまぜつつ、乾いたタッチで綴られる。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIDNIGHT OBSESSION", "プリズン X", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "トルコの空港で麻薬保持が発覚し、現地の刑務所に投獄されてしまったサラ。そこは、精神的にも肉体的にもこのうえない屈辱を強いられる無法地帯であった。サディストで変態の嘱託医、性的倒錯者の女看守長やレズの女囚など、あらゆる魔の手がサラを襲う。サラに好意を示していたタモーク大佐ですら、彼女の肉体を求めているだけだった……。刑務所内の狂気と暴力に立ち向かうヒロインの姿をエロティックに活写。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("10,000 DOLLARI PER UN MASSACRO", "二匹の流れ星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "西部劇版“巌流島の決闘”との触れ込みで日本公開されたマカロニ・ウエスタン。凄腕の賞金稼ぎ、ジャンゴが、さらわれた娘を救い出してほしいとの依頼を受ける。誘拐犯はコルトを操る無法者マヌエルだった。一度は友情が芽生えて駅馬車強盗を企てる二人だったが、マヌエルがジャンゴを裏切ったことで宿命的な対決の時を迎える。", "1967-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLOOD OF HEROES", "サルート・オブ・ザ・ジャガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「ブレードランナー」の脚本家D・ウェッブ・ピープルズが初監督した近未来アクション映画。2200年の荒廃した地球で人類を熱狂させている格闘ゲーム、“ジャガー”のプレーヤーとして闘いの旅を続けるチームのリーダー、サロウを通して、人間の尊厳が語られていく。彼は新米の女戦士ジョアン・チェンを一人前の戦士として育てるべく過酷な練習と実戦を課していった。しかし、そこから芽ばえる師弟愛にも似た感情にピリオドを打つがごとく、彼は次の戦いを求めて孤独の旅に出る。サロウに扮しているのは「ブレードランナー」で一気に大スターの仲間入りしたR・ハウアー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAS−OIL", "地獄の高速道路〈ハイウェイ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "しがない長距離トラックの運転手でありながら、5000万フランをめぐる銀行強盗事件に巻き込まれた男とギャング一味との闘いを、スリリングに描いたサスペンス映画。5000万フランを盗んだとして、ギャング一味から追われる男をJ・ギャバンが渋く演じる。また、彼の婚約者として登場するJ・モローの若々しい魅力も要チェック。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゆきゆきて、神軍", "ゆきゆきて、神軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "「さようならCP」「極私的エロス・恋愛1974」などを自主製作し、その衝撃的な映像で注目を集めた原一男によるドキュメンタリー。天皇にパチンコを発射した事件をはじめとして、戦争の責任をあくまで過激な手段で追及し、国家・社会といった共同体に挑戦し続けるアナーキスト・奥崎謙三を追い続けた作品。彼がニューギニア戦線で起きた兵隊同士の食人事件の疑惑を、何人もの証言者のもとに、すさまじい執念をもって食いさがり、真相をつきとめていく過程が中心となる。カメラの気迫がもたらすゾクゾクする面白さと、ズシンと重いあと味。“撮る行為”自体が平和な日常の裂け目に視線をうがって、思わぬ現実をあぶり出していく武器になることを再発見させてくれる秀作。ニューギニア当地で撮ったフィルムは当局に没収された。第37回ベルリン映画祭でカリガリ映画賞を獲得した。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Los Ojos de Julia", "ロスト・アイズ", "", "", "http://image.eiga.k-img.com/images/movie/56088/315x210.jpg?4726", 117, "「パンズ・ラビリンス」のギレルモ・デル・トロが製作を手がけるダークホラー。主演は同じくデル・トロ製作による「永遠のこどもたち」のベレン・ルエダ。角膜移植手術を終えたばかりの女性サラが、自宅地下室で首吊り死体となって発見される。姉の死に疑問を抱いたサラの双子の妹フリアは独自に調査を開始するが、気がつくと周囲に不穏な人影がちらつきはじめていた。同時にフリアの視力も次第に失われていき、徐々に暗闇に包まれていく……。", "2011-06-18", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あ・うん", "あ・うん", "", "", "http://ecx.images-amazon.com/images/I/21RHGE6ZF7L._SL210_.jpg", 114, "向田邦子の同名小説を高倉健主演、降旗康男監督で映画化。昭和初期の東京・山の手を舞台に2人の男と1人の女が織り成す人間模様を美しい映像で描き出す人間ドラマ。中小企業社長の門倉と安月給のサラリーマンの水田は性格も境遇も対照的ながら友情で結ばれていた。しかし門倉は水田の妻たみのことを密かに思い続けていて……。17年ぶりに銀幕復帰した富司純子（旧芸名・藤純子）、坂東英二が共演。", "1989-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MISSISSIPPI GAMBLER", "ミシシッピの賭博師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "正しい勝負をして賭博渡世を歩もうと決心するマークは、船の賭博場で実業家の息子ローランらを相手に大勝する。ローランが妹アンジェリクの首飾りで借金を精算したことからマークは彼女と知り合うが、彼女はマークに激しい反感を抱いていた。19世紀のミシシッピー川を舞台にした賭博師の物語。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポケットモンスター　水の都の護神〈まもりがみ〉・　ラティアスとラティオス", "ポケットモンスター　水の都の護神〈まもりがみ〉・　ラティアスとラティオス", "", "", "http://ecx.images-amazon.com/images/I/51Vgr74VmKL._SL210_.jpg", 90, "人気のポケモンシリーズの劇場版第５作。今回の冒険の舞台は、イタリアのベネチアをモチーフにした、世界で一番美しいと言われる水の都“アルトマーレ”。仲良し兄妹の新ポケモン“ラティオス”と“ラティアス”が守る秘宝“心のしずく”をめぐり、ピカチュウとサトシが事件に巻き込まれていく。", "2002-07-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Efter Bryllupet", "アフター・ウェディング", "", "", "http://image.eiga.k-img.com/images/movie/34268/315x210.jpg?4058", 119, "06年度アカデミー賞外国語映画賞にノミネートされ、すでにハリウッドデビュー作（ハル・ベリー主演「Things We Lost in the Fire」）も完成させているデンマークのスサンネ・ビア監督による人間ドラマ。インドで孤児の援助活動を行うヤコブは、祖国の実業家ヨルゲンから巨額の寄付金の申し出を受ける。デンマークに戻ったヤコブは、ヨルゲンの娘の結婚式に誘われ、思いがけない人と再会し、ヨルゲンの真の望みを知る……。主演は「007／カジノ・ロワイヤル」のマッツ・ミケルセン。", "2007-10-27", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版NARUTO−ナルト−／大活劇！雪姫忍法帖だってばよ!!", "劇場版NARUTO−ナルト−／大活劇！雪姫忍法帖だってばよ!!", "", "", "http://ecx.images-amazon.com/images/I/51SMHNT8GNL._SL210_.jpg", 84, "『週刊少年ジャンプ』で連載され、TVシリーズも製作された人気漫画が、オリジナル・ストーリーでスクリーンに登場。人気映画女優にして、超ワガママな富士風雪絵の護衛を任されたナルトたち。嫌がる雪絵を連れ、撮影舞台の“雪の国”へ辿り着いた彼らだったが、最強最悪の“雪忍三人衆”が登場して……。次第に明らかになっていく雪絵の隠された過去、そして雪忍たちの正体と、映画版ならではの奥深いストーリーが展開する。", "2004-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CENTER　STAGE", "センターステージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「クルーシブル」「英国万歳！」の名匠N・ハイトナーが、バレエに情熱を懸ける若者たちの栄光と挫折を描く青春ドラマ。名門バレエ団の練習生となり夢をふくらませるジョディは、そこで同じようにバレエに魅せられた若者たちとともに厳しいレッスンに明け暮れていた。厳しい母親の監視下で英才教育を受けてきた者、体格に恵まれずに伸び悩む者、それぞれ悩みを抱え懸命に努力している彼らは、レッスンの成果が問われる卒業公演に向けて全力を注ぐ。恋や友情のドラマを交えながら、成長していくダンサーたちの姿をドラマティック、かつさわやかに描く。プロのバレエ・ダンサーたちを起用した華麗なダンス・シーンを巧みに配して、登場人物の揺れ動く心情を物語り、そのドラマとの絶妙のバランスに監督の手腕が光る。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sophie Scholl-Die letzten Tage", "白バラの祈り　ゾフィー・ショル、最期の日々", "", "", "http://image.eiga.k-img.com/images/movie/1648/315x210.jpg?2338", 117, "1943年のミュンヘン。ナチスの敗北が迫る中、非暴力的レジスタンス運動を展開するドイツ人学生グルーブ、白バラ。その紅一点、21歳のゾフィーと仲間は、チラシを配布していたところを逮捕され、ナチスに協力することを拒み、6日後という異例な速さで処刑された。この実話を基に描く人間ドラマ。監督は「アンツ・イン・ザ・パンツ！」のマルク・ローテムント。第55回ベルリン国際映画祭銀熊賞、最優秀監督賞、最優秀女優賞を受賞。", "2006-01-28", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCORPIO FILE", "スコルピオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "殺し屋スコルピオは、親友のCIA情報部員クロスから、中東の某国首相の暗殺を依頼される。ところが、クロスは二重スパイだった……。友情や愛情さえも押しつぶしていく非情なスパイ戦を、硬質なタッチで描くポリティカル・アクション。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Devil's Own", "デビル", "", "", "http://ecx.images-amazon.com/images/I/417XsYOcsML._SL210_.jpg", 111, "IRAの特殊工作員「エンジェル」は、大規模なテロ計画を控えて武器調達のために「ローリー」という名前でニューヨークに潜伏していた。そのローリーが孤独な青年を装い、アイリッシュ系の警察官トムの家へ下宿することになる。ローリーはトムの家庭の暖かさに触れ、信頼関係を築いていくが、やがてトムがローリーの秘密に気がつき……。98年に交通事故で他界したパクラ監督の遺作。", "1997-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESCAPE TO ATHENA", "オフサイド7", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "第二次大戦末期におけるエーゲ海のロードス島を舞台にした、戦争アクション映画。古代美術の宝庫であり、戦略上の拠点ともなっているロードス島をめぐって、ナチス・ドイツとレジスタンスが命懸けの攻防を展開する。国際級スターの豪華な共演も話題に。", "1979-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAGANT　OF　LOVERS", "恋のページェント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "18世紀半ば、幼い折にロシアの女帝エリザベートから要請され、ドイツの皇女ソフィアはやがて成長し、世継の王子ピョートルの許に嫁ぐ。そして彼女の波乱の人生が始まった……。カテリーナ二世の日記をもとに、ジョゼフ・フォン・スタンバーグ監督がマレーネ・ディートリヒ主演で描く、女帝の愛と哀しみ、そして誇りに彩られた一代記。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEVEN FACES OF DR.LAO", "ラオ博士の７つの顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「タイム・マシン」などで知られるSF映画の巨匠パルの未公開作。ある小さな町に、中国人ラオ博士が率いる謎のサーカス団がやって来る。出し物はネス湖の怪獣、雪男と続くが、メドゥーサを見たとたん、町の人々は皆、石になってしまった……。SF映画ファン必見の傑作。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tulitikkutehtaan tytto", "マッチ工場の少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "フィンランドの田舎町。マッチ工場で働いて稼いだ僅かな金で母と義父を養う少女イリス。ある日彼女は、もらったばかりの給料で自分のドレスを買ってしまう。 怒った義父は彼女を殴り、母はドレスの返品を命じる。とうとう我慢できなくなった彼女は、家を飛び出してディスコへ向かい、そこで出会った男性と一夜をともにする。しかし彼女は彼にも裏切られ……。監督は「レニングラード・カウボーイズ」シリーズのアキ・カウリスマキ。", "1990-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("皇家師姐／IN THE LINE OF DUTY PART ２", "レディ・ハード 香港大捜査線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "港警察の女刑事アナは、知り合いのロンドン警察刑事がホテルで殺されたのをきっかけに、悪の組織と立ち向かう。しかし警察は組織のボスを恐れて手を引こうとし……。その後D＆B社の社長夫人となり、４年後の離婚まで一時引退することになるM・キングの主演作。J・シャム、T・ハークらが味のある脇役ぶりを見せる。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BETWEEN HEAVEB AND HELL", "ならず者部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "第二次大戦下。誤って部下を殺してしまった上官を殴ったため、通称“ならず者部隊”に転属となったサム・ギフォード。臆病な人間だった彼が、前線で危険にさらされているうち、真の勇気に目覚めていくまでを描く。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER　SOLEN", "太陽の誘〈いざな〉い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "1956年のスウェーデンを舞台に、不器用な中年男と都会からやってきた美しい女との愛の軌跡を描いた秀作。40歳にして女性経験がなく文字の読み書きも出来ない農夫オロフは、家政婦募集の広告を出す。彼のもとにやってきたのは、美しく洗練された女性エレンだった。まったく似つかわしくない二人だが、いつしか情熱的に愛し合うようになる……。オロフ、エレンのそれぞれの隠し事と友人エリックの絶妙な嘘。これらが、程よいエッセンスとなり、官能的な恋愛劇に切なさと緊張感をもたらしている。1999年サン・セバスチャン映画祭審査員大賞受賞。原作は、英国人作家H・E・ベイツの短編小説『小さな農場』。", "1998-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUSTING　CLIFF７", "クリフセブン 人質奪還指令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "危険な任務に挑むタフな女スパイの活躍を描いたアクション・ドラマ。愛娘を人質に取られた元CIAのヒロインが意を決して、最新兵器をめぐる重要機密クリフセブンの謎に迫っていく。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CERCLE ROUGE", "仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "メルヴィル監督が豪華キャストで描いたフィルム・ノワールの佳作。宝石強奪のために数人の強者が運命的な出会いをするが、事件は意外な結末を迎える……。男の愛と友情と裏切りを絶妙な演出で描き出す。ドロンとモンタンの初共演も話題を呼んだ。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ガンダムUC episode4「重力の井戸の底で」", "機動戦士ガンダムUC episode4「重力の井戸の底で」", "", "", "http://image.eiga.k-img.com/images/movie/56108/315x210.jpg?3551", 60, "「亡国のイージス」の福井晴敏による小説「機動戦士ガンダムUC」を全6話でアニメ化する第4話。「ラプラスの箱」をめぐる争乱は地上にも波及し、地球連邦政府首都ダカールが戦禍に巻き込まれる。連邦政府の中心人物であるローナン・マーセナスは、「箱」の開放を阻止するため、ロンド・ベル隊司令ブライト・ノアに接触。一方、地球に降りたバナージは「箱」につながる新たな情報を開示するためオーストラリアのトリントン基地を目指すが……。歴代ガンダム作品に登場してきた名キャラクターのブライト・ノアの声は、2006年に声優の鈴置洋孝が他界したため新たに成田剣が務める。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勝手にしやがれ!! 強奪計画", "勝手にしやがれ!! 強奪計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "便利屋稼業でせち辛いこの世をしのぐ藤田雄次と吉行耕作。依頼されるのはヤバイ仕事ばかりで、二人の行く所、必ずトラブルが付きまとう。ある日、雄次はヤクザに殴られ、倒れ込んだ幼稚園で、保母の涼子に一目惚れ。彼女が窮状にあることを知って力になろうとするが、またもトラブルの連続、あげくの果てにヤクザの麻薬取引に巻き込まれてしまう。小粋なセリフとテンポのよい展開、ハードボイルドより軽妙洒脱な“ハーフボイルド・シネマ”をうたう痛快活劇。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和残侠伝・死んで貰います", "昭和残侠伝・死んで貰います", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "シリーズ７作目にして最高傑作。渡世人・花田秀次郎は、深川の料亭“喜楽”に戻り、盲目の義母に素性を隠し見習いとして住み込む。秀次郎を陰から支えるのは板前頭の風間重吉だった。“喜楽”の乗っ取りをたくらむ新興博徒の駒井は卑劣な手段で“喜楽”の権利書を取り上げた……。堪忍袋の緒が切れた秀次郎はついに長ドスの封印を破る。大きなイチョウの樹の下で、傷ついた秀次郎と芸者の卵・幾江とが出会う冒頭のシーンは見事。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シャブ極道", "シャブ極道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 164, "シャブ中(覚醒剤中毒)で女好きのヤクザ・真壁は破天荒な性格で、大組織幹部の女・鈴子を奪い、自分が若頭をつとめる巌竜組まで乗っ取ってしまう。鈴子の内助の功や若頭・下村の機転に助けられ何とか危機を逃れていた巌竜組。しかし覚醒剤を主なしのぎにしていたため、シャブの使用禁止をうたう大組織・松田組との対立は深まるばかりだった。緊張が高まるなか、真壁は周囲の反対を無視してシャブの大きな取り引きを決めてしまい、ついに抗争に火がついた。仁侠映画に新しい要素を加えた熱気ある１作。公開時のタイトルは「シャブ極道」だがビデオ発売時、タイトルにビデオ倫理委員会からクレームが付き、改題。「大阪極道戦争　白の暴力」(前半・84mins.)と「大阪極道戦争　白のエクスタシー」(後半・80mins.)の２本に分けられている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TERMS OF ENDEARMENT", "愛と追憶の日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "ある時は姉妹のように、ある時は親友のように、お互いを支え合いながら生きてきた母と娘。この作品は、そんな強い絆で結ばれた二人の波乱の人生を、笑いと涙で感動的に綴ったドラマ。アメリカ人の“家庭回帰”というテーマの現代性や、俳優陣の好演が高く評価された。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIDGET", "Bridget〈ブリジット〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ファストフード・ファストウーマン」で高い評価を得た監督、主演コンビによる意欲作。次々と悲劇に見舞われる女性が、絶望的な状況に追い詰められていく……というハードな内容。だが、息子のためには危険を顧みないヒロインのたくましさ、官能的な美しさが、ドラマに魅力を与えている。ギャングに夫を殺されたブリジットは、母親不適格者と見なされ息子を里子に出されてしまう。何とか息子を取り戻そうと悪戦苦闘するが……。", "2005-04-09", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL DECAMERONE", "デカメロン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ボッカチオの有名な小説を、イタリアの鬼才パゾリーニが映画化。舞台を現代に置き換え、７つの挿話と狂言まわしの画家ジョットーのエピソードを加えたオムニバスにした。それぞれのタイトルは「ペルージャのマンドレウッチョ」「マゼットと尼僧」「ペロネッラ」「チャペレット」「カテリーナ」「リザベッタとロレンツォ」「コンパール・ピエトロ、ドンナ・ジェンマータとドン・ジャンニ」「ティゴッチョとメウッチョ」。DVDタイトルは「パゾリーニ・コレクション　デカメロン【オリジナル全長版】」。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kafka", "KAFKA／迷宮の悪夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "第1次大戦後のプラハ。「変身」を執筆中のカフカが同僚の謎の死の真相を追い、迷宮のような城に潜入する。そこにはムルナウ博士の恐怖の実験室があった……。官僚制機構の恐怖をテーマにしたSFスリラー。街を迷宮のようにとらえたカメラが秀逸である。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIMME SHELTER", "ローリング・ストーンズ・イン・ギミー・シェルター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1970年に行われたストーンズのオルタモントでのコンサートを記録した傑作ドキュメンタリー。数々のヒット曲を演奏している全盛期のストーンズの姿が映し出されるが、そのステージの途中で事件が起きる。会場警備を担当していた暴走族ヘルス・エンジェルスが、観客の黒人青年を刺殺したのだ。パニックに陥る会場。うろたえるメンバーたち。いったいなぜ黒人青年が殺されたのか？ 刺殺の瞬間を捉えたフィルムから、恐るべき事実が明らかになる……。ロック史上に残る惨劇として知られる事件を中心に、1970年代初頭の空気を生々しく伝える一作。ストーンズ・ファンならずとも一見の価値あり。フランシス・F・コッポラや名カメラマン、H・ウェクスラーの助手を務めていた頃のG・ルーカスが撮影に参加している。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウール100％", "ウール100％", "", "", "http://image.eiga.k-img.com/images/movie/33708/315x210.jpg?4697", 99, "ごみ屋敷に住む老姉妹とモノノケの少女との交流を描く大人のファンタジー。町で廃品を拾って来ては自分たちの屋敷に積み上げ続ける変わり者の老姉妹、梅さんと亀さん。ある日、彼女たちの元に赤い毛糸のセーターを編んではほどくことを繰り返す不思議な少女、アミナオシが現われる。監督はこれまでCMやテレビの短編アニメーションを手掛けてきた富永まい。老姉妹を岸田今日子と吉行和子が、アミナオシを「誰も知らない」の北浦愛が演じる。", "2006-10-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LEFT HANDED GUN", "左ききの拳銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "西部開拓史上に名高い、無法者ビリー・ザ・キッドの保安官殺しと彼の最期を、A・ペンが迫力あるタッチで描いた彼の出世作。12歳で人殺しをしたビリーは、生き倒れ寸前のところをある老人に救われる。この老人を町の保安官、副保安官、家畜商が結託して殺した。ビリーは恩義に報いるため、３人を次々に射殺していく。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FOREIGNER", "撃鉄 GEKITETZ　ワルシャワの標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“沈黙”シリーズのアクション俳優スティーヴン・セガールの主演作。入り組んだ陰謀のなかでタフに立ちまわる主人公に扮した、セガールのハードボイルド調の熱演に注目。パリに住む雇われスパイ、コールドは旧知の高官の依頼で、ある小包の運搬を引き受けた。簡単な任務のはずが、謎の襲撃者の出現で状況は一変。 数々の危機を乗り越えながら、コールドは小包の意外な秘密に迫る。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETROACTIVE", "リバース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "傷心の女刑事カレンがテキサスのハイウェイで事故を起こし、偶然通りがかったフランクとレアンヌ夫妻の車に乗せてもらう。ところが凶暴なフランクは、レアンヌの浮気に激怒し彼女を射殺。砂漠の研究所に逃げ込んだカレンは、若い男ブライアンが実験を進める時間逆行装置で過去に戻りフランクの凶行を食い止めようとするが、犠牲者が続出し……。女刑事とサイコ野郎の果てしない死闘が展開するバイオレンス・アクション。SFじみた要素を加味したドラマの設定はかなり強引だが、それを一気呵成に見せきるノンストップ演出が小気味いい。目的を達成するために何度も過去へのトリップを試みる不屈のヒロインを、モデル出身の美女K・トラヴィスが大熱演している。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MES　PETITES　AMOUREUSES", "ぼくの小さな恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "「ママと娼婦」で1983年カンヌ映画祭審査員特別賞ほかを受賞して話題になったジャン・ユスターシュが、自身の経験を元に描いた痛切な青春映画。両親の離婚で祖母とフランス南部の小さな村に住んでいた13歳のダニエル。突然母に連れ戻された彼が、街で様々な人々に出会い、性への目覚めなどの経験を重ねていく。少年が大人への一歩を踏み出す過程がみずみずしい。", "1974-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("むずかしい恋", "むずかしい恋", "", "", "http://image.eiga.k-img.com/images/movie/53998/315x210.jpg?3180", 102, "「贅沢な骨」「きょうのできごと／a day on the planet」で行定勲監督の脚本を手がけた益子昌一による初監督作品。出演に「銀色のシーズン」「ナイスの森」の水橋研二、「グミ・チョコレート・パイン」の璃乃亜、「俺たちに明日はないッス」の安藤サクラら。都会の片隅に佇むバー「NO-MU」。そこにやってくる様々な若者の恋を、バーテンダーのアキラがひっそりと見守る。", "2008-10-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SKY RIDERS", "スカイ・ライダーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "別れた妻と子供をテロリストに誘拐されたマケイブは、警察の無能ぶりをよそに、断崖絶壁の上にある犯人のアジトを見つけ出し、ハング・グライダーを駆って、救出作戦に挑む。ハング・グライダーによる空中撮影が快いアクション映画。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("てなもんや東海道", "てなもんや東海道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "気楽な旅を続ける時次郎と珍念坊主は、亀山城下でニセ祈祷師・風天仙人の側女にされかかっている娘を助けたのをきっかけに五厳寺の住職に収まる。やがて食客として飛び込んで来た清水次郎長を助けて、荒神山で安濃徳一家と闘う。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL PORTIERE DI NOTTE", "愛の嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "イタリアの女流監督L・カヴァーニがナチズムの狂気にもてあそばれた男女の悲劇を描いた作品。1957年冬のウィーン。かつてナチス親衛隊員だったマックスは、今はホテルの夜のフロント係として身元を隠して働いているが、ある日、収容所時代にもてあそんだルチアと再会する。当時の二人は、支配する者とされる者との関係を超えて、異様な愛の絆で結ばれていた。ルチアは今は有名な指揮者の妻に収まっていたが、再会したマックスとなぜか離れられなくなる。そして二人は再び愛の嵐に身を委ねていく……。ナチズムという狂気の中での頽廃に彩られた愛が官能的に描き出されている。", "1973-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RETURN OF THE LIVING DEAD", "バタリアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "現代のロサンゼルス。ある日、科学資料倉庫の奥深く保存されていた“謎の箱”からガスが噴き出し、醜悪なゾンビが出現。しかもそのガスは雨となり、墓場の死体をよみがえらせた……。凶暴だが、どこかユーモラスなゾンビたちが大暴れする、ホラー・コメディの快作。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HART’S　WAR", "ジャスティス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "第二次大戦期のナチスの捕虜収容所を舞台にしたサスペンス。1944年、米軍中尉ハートはナチスの捕虜収容所に送られる。ある日、同房の兵士が殺され、黒人兵士が容疑者として逮捕された。ナチスが仕組んだ茶番のような裁判で、ハートは容疑者の弁護を務めるが、捕虜のリーダー、マクナマラ大佐は、なぜかハートの調査を妨害。やがて意外な事実を突き止めたハートは、ある決断を迫られる……。人種差別や脱獄計画が複雑にからんだスリリングな物語を通して、人間の誇りを問いかける。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("目を閉じてギラギラ", "目を閉じてギラギラ", "", "", "http://image.eiga.k-img.com/images/movie/57198/315x210.jpg?4343", 0, "携帯向け動画配信サービス「BeeTV」で配信された哀川翔主演のドラマを劇場公開。暴力を好まず、人情味溢れる「お説教」を武器に裏社会を生き抜いてきた元ヤクザの希崎望は、貸金業「ホープ商会」を営んでいる。かつての天才球児で、今は定職に就かず借金まみれの生活を送っている青年・八木沢剛太は、希崎のお説教に胸を打たれ、希崎の下で人生を立て直そうとするのだが……。監督は「パビリオン山椒魚」「乱暴と待機」の冨永昌敬。", "2011-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陸軍落語兵", "陸軍落語兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "補充兵として軍隊に入った落語の前座４人組が、失敗を繰り返しながら教練所から中国大陸、そして最前線へと駆り出されていくさまを描いた兵隊喜劇。自らの軍隊経験をもとにした春風亭柳昇の原作を、「与太郎戦記」の弓削太郎が映画化。本編が彼の遺作になった。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’UOMO DELLA MANCHA", "ラ・マンチャの男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "1965年にブロードウェイで初演以来、大ヒットを続けたミュージカルを忠実に映画化。古い鎧かぶとに身を固め、老馬にまたがった騎士ドン・キホーテ。理想の幻影に生きる男にオトゥールが扮し、熱演。ラストの大合唱『見果てぬ夢』は壮観。", "1972-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京ゴッドファーザーズ", "東京ゴッドファーザーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "前作「千年女優」の全米配給権がドリームワークスに取得されるなど、国内外で高い評価を受けている今敏監督の最新作。東京に暮らすホームレスの３人組が、様々な事件に巻き込まれながらも、置き去りにされた赤ん坊の親探しに奔走する人情喜劇アニメショーン。物語を彩る複数のエピソードが伏線となり、ラストへ向けて収れんしていく展開は見事。クリスマスの夜、ゴミ捨て場に放置されていた赤ん坊を、ギン、ハナ、ミユキの３人組のホームレスが発見する。警察に任せるべきだというギンの意見を聞かずに、オカマのハナは、泣き叫ぶ赤ん坊を清子と名付け、ねぐらに保護する。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京キッド", "東京キッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "アメリカ帰りの美空ひばり主演のコメディ。内容は型どおりの親子スレ違い物語だが、ワキ役陣が笑わせてくれる。特にエノケンはアパート住人としての特別出演ながら、見振りだけ見ていても大笑いしてしまう。ハワイ・ロケのシーンもあり、ひばりの同名主題歌も大ヒットした。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恐怖分子", "恐怖分子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "台湾の首都・台北。カメラマンとその恋人、女流作家と医師の夫、その元恋人、不良少女、刑事……一本の間違い電話をきっかけに交錯する様々な人物たちが幾重にも錯綜し、やがて悲劇を巻き起こしていく。図らずも他人を傷つけ生きる都会人の不条理を、鋭利な視点と緻密な構成で描いた問題作。エドワード・ヤン監督を、一躍台湾ニューウェーブの旗手に持ち上げた現代感覚溢れる一編。", "1986-01-01", "53,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブラブラバンバン", "ブラブラバンバン", "", "", "http://image.eiga.k-img.com/images/movie/53388/315x210.jpg?2324", 93, "気持ちのいい音楽を聴くと気分が高揚して発情し、人格まで変わってしまう特異体質の女子高生・芹生百合子は、ひょんなことから指揮者として急ごしらえの吹奏楽部のメンバーを率い、コンクール出場を目指すことになるのだが……。弱小吹奏楽部が困難を乗り越えていく様子を描いた青春ラブコメディ。原作は、週刊ヤングサンデーに連載された柏木ハルコの同名漫画。本作の主題歌も歌う人気歌手の安良城紅が主演を務める。", "2008-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私の叔父さん", "私の叔父さん", "", "", "http://image.eiga.k-img.com/images/movie/57478/315x210.jpg?4574", 99, "直木賞作家・連城三紀彦が叔父と姪の禁じられた恋を描いた短編小説を、高橋克典主演で映画化。東京でカメラマンになるため修行していた構治のもとに、6つしか年が違わず兄妹のように育った姪の夕季子が訪れる。7年ぶりに再会した夕季子は美しい女性に成長し、2人は互いの気持ちを察しながら徐々に距離を縮めていくが……。ヒロインの夕季子役は「受験のシンデレラ」の寺島咲。監督は「竜二 Forever」「燃ゆるとき」の細野辰興。", "2012-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("力道山物語・怒涛の男", "力道山物語・怒涛の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "力道山本人の主演による自身の伝記物語。九州の貧しい家に生まれた百田少年は、相撲界に身を投じ力道山と名を改めてひたすら精進するが、角界のしがらみから土俵に別れを告げる。恩人の協力によってプロレス界に入り、持ち前の根性で次々に激闘を展開。ついに世界チャンピオンの鉄人、ルー・テーズに挑戦する……。古賀政男が音楽を担当している。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブンガワンソロ", "ブンガワンソロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1945年８月、ジャワのある村落へ３人の日本軍脱走兵がやって来た。彼らを牛小屋にかくまった家族の娘サリヤは日本人を嫌っていたが、やがて３人のうちの一人、深見を愛するようになる。しかし、脱走兵を追う憲兵たちが村に現れた……。デビューして４年目の市川崑監督、新東宝時代最後の作品。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一眉道人／VAMPIRE VS.VAMPIRE", "霊幻道士５ ベビーキョンシー対空飛ぶドラキュラ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "“霊幻道士”シリーズ第５作は、東洋のキョンシーと西洋のドラキュラの東西両怪物スターの対決がウリ。霊幻道士の呪法、魔除けが、果たしてドラキュラに通用するか!?　もちろんベビーキョンシーも登場する。SFXとアクションの“香港スタイル”も健在。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TALL GUY", "彼女がステキな理由〈ワケ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「ビーン」「ノッティングヒルの恋人」を手掛けたR・カーティスが脚本を手掛けたコメディ。アメリカからロンドンにやって来た売れないコメディアン、デクスターは、ある日出会った看護婦のケイトに一目惚れ。彼女とうまくいき、ミュージカル版『エレファント・マン』の主役という大役も舞い込むが……。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("６週間・プライヴェートモーメント", "６週間・プライヴェートモーメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "６週間の間、週に１度の演技トレーニングを経て選抜される日米合作映画の主役オーディション。会場には女優志願のアイドル、売れない俳優などが集まった。それぞれの思いを胸にビッグ・オーディションに参加する男女をドキュメンタリー・タッチで映し出した人間ドラマ。新山千春ら若手タレントが等身大の芸能人を演じて新鮮な表情を見せる。共同脚本・監督は演技学校を主宰する俳優・塩屋俊。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("捨て身のならず者", "捨て身のならず者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "高倉健が暴力団の実態を調査する三流週刊誌のトップ屋を演じた作品。監督は、のちに「居酒屋兆治」や「夜叉」でコンビを組んだ降旗康男。澤井信一郎が脚本を執筆している。一匹狼のトップ屋・矢島は、暴力団大和会の調査を進めるうち、逆に組織から殺人犯に仕立て上げられムショ行きにされてしまう。復讐に燃えながらムショ生活を送る彼のもとに、大和会会長・大和田の死が知らされ……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Secret Life of Bees", "リリィ、はちみつ色の秘密", "", "", "http://image.eiga.k-img.com/images/movie/54178/315x210.jpg?2369", 110, "14歳の夏、リリィは10年前に自らの過ちで失った母親の過去を知るべく旅立ちを決意する。亡き母の面影を求めてたどり着いた養蜂場の3姉妹のもとでひと夏を過ごすことになったリリィは、そこでさまざまな形の愛情を見つけていく。スー・モンク・キッドのベストセラー小説を映画化した人間ドラマ。ダコタ・ファニングと、クイーン・ラティファ、ジェニファー・ハドソン、アリシア・キーズ、ソフィー・オコネドーら黒人女優が共演。", "2009-03-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹下左膳・こけ猿の壷", "丹下左膳・こけ猿の壷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "林不忘の原作を衣笠貞之助が脚色し、彼の愛弟子の三隅研次が第一回作品として監督した。戦前の山中貞雄の名作のリメイクだが、大河内伝次郎に往年の冴えはなく、苦しい作品となった。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APRE｀S LA GUERRE", "フランスの友だち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "第二次世界大戦下、解放間近のフランス田舎町で、ドイツ軍を解放軍だと間違えた二人の少年のために、村は大惨事と化してしまう。あまりの罪深さから村を逃げ出した二人は、やがて負傷したドイツ兵と出会った。「フランスの思い出」のユベール監督が再び少年たちの思い出を描く感動の珠玉作。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シベリア超特急３", "シベリア超特急３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "“MIKE MIZNO”こと映画評論家、水野晴郎が放つミステリー・ロマンの第３作。今回は約60年の時を隔てた壮大なスケールの物語が展開、もちろん水野監督お得意の大ドンデン返しも炸裂する。1941年、山下将軍が乗り合わせたシベリア発の特急列車で奇怪な連続殺人事件が発生。事件は謎を残したまま61年間の時が流れるが、瀬戸内海の船上パーティーで同じような殺人が起こり……。“シベ超”シリーズの第４作は１日だけ舞台で上演され、その模様は現在ビデオ、DVDで見ることができる。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南國再見、南國", "憂鬱な楽園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "その日暮らしの中年男ガオは弟分のピィエンとその恋人のマーホァとともに南へのバイク旅行に出発するが……。根無し草のようにさまよう男女の場当たりな生き様を、物語性の欠落した客観的映像でスケッチしていく。フィルム・ノワールを思わせる張りつめた雰囲気と南国特有のアンニュイなムードが、見る者に不思議な高揚感を抱かせる異色作。日本からの出資を得て、名匠ホウ・シャオシエン監督が肩の力を抜いて撮りあげた青春ロードムービー。", "1996-01-01", "53,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARTARL LAWII：UNDERCOVER", "アンダーカバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "主演男優J・ウィンコットのアクションと肉体が売りの刑事もの。犯罪都市ロサンゼルスに赴任してきたショーンは、いきなり旧友の死に直面。事故死と見なされたその死に疑問を感じたショーンは、単独捜査を開始する。そこには麻薬と武器の密輸組織があった。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林少女", "少林少女", "", "", "http://image.eiga.k-img.com/images/movie/53298/315x210.jpg?3892", 107, "「少林サッカー」のチャウ・シンチーがエグゼクティブプロデューサーを務め、「踊る大捜査線」シリーズの亀山千広プロデューサーと本広克行監督が再タッグを組んだカンフー・アクション。亡き祖父の道場を継ぐため中国で少林拳を修行していた凛。ところが帰国すると道場は閉鎖され廃墟と化していた。道場再建を願う凛は、ひょんなことから大学のラクロスチームに入ることになり……。柴咲コウが1年間に及ぶトレーニングを積み本格アクションを披露。", "2008-04-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PART XIV 借金極道", "難波金融伝 ミナミの帝王　劇場版PART XIV 借金極道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "昔は大物だったものの、いまやシノギもままならない福永組組長の福永。台湾マフィアのワンリーから、偽造パッキーカードの取引話を持ち込まれ、一もうけを夢見て、銀次郎から多額の金を借りるが……。借金地獄に落ちた極道をトラブルから救う人気シリーズの劇場版第15作。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Air Guitar Nation", "エアギター　エピソード　ゼロ", "", "", "http://image.eiga.k-img.com/images/movie/34238/315x210.jpg?1603", 82, "ロック音楽に合わせてギターを弾く振りをするパフォーマンス“エアギター”。03年に設立されたアメリカ大会の模様とフィンランドで開催される世界大会の熱狂をカメラに収め、エアギターの魅力に迫るドキュメンタリー。個性的なエアギタリストたちによる様々なパフォーマンスが楽しめるほか、クイーンのブライアン・メイやレイジ・アゲインスト・ザ・マシーンのトム・モレロら有名ミュージシャンも登場する。", "2007-10-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COBRA VERDE", "コブラ・ヴェルデ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "風土に培われた神話、土俗的な伝説といった人類の根源ともいえるものを独自に追求してきたヘルツォーク監督がアフリカ奥地に潜入、奴隷制度という巨大な問題にメスを入れた作品。貧困の末に“コブラ・ヴェルデ”と呼ばれる山賊になり、やがて奴隷商人、さらには共和国の総督へと成り上がっていく男のてん末を描く。", "1987-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乙女物語２ お嬢様危機イッパツ！", "乙女物語２ お嬢様危機イッパツ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“乙女物語”シリーズ第２作。今回は政略結婚を迫られる財閥のお嬢さまと、彼女に恋したあまり女装して屋敷に潜り込んだ青年との逃避行をコミカルに描いている。大林組出身の内藤忠司の一般映画デビュー作で、大林宣彦がヒロインの父親役で出演しているほか、常連俳優の尾美としのり、竹内力らがゲストで顔を見せている。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前怪談", "喜劇・駅前怪談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "信玄の隠し湯がある小さな村に、レジャー・ランドを建てようと観光会社が乗り込んで来た。賛成派の地主たちと反対派の徳之助や孫作たち。温泉の権利を握っているおくま婆さんの孫娘が突然東京から帰って来て、権利を売ろうとして婆さんと対立するが……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EYES OF FIRE", "死霊の谷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "安住の地を求めて未開の地へと旅立ったスミス牧師をはじめとする数人の男女。幾多の危険に直面しながらもやっとの思いでたどりついた美しい谷。しかし、そこは恐ろしい悪魔がさまよう、死霊の谷であった。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スワンズソング", "スワンズソング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "妹を惨殺されたことを機に、田舎へ引っ越してきた少女、美奈とその母。しかし、忘れようとしていた悪夢は再び美奈に襲いかかっていく……。「殺し屋１」などの佐藤佐吉の脚本をもとに、自主映画出身の新進・笠木望が監督したサスペンス・ミステリー。アイドルを用いたティーン・ホラーという枠内で、映画的昂揚を醸し出そうと腐心する演出姿勢は好感が持てる。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Perfect Sence", "パーフェクト・センス", "", "", "http://image.eiga.k-img.com/images/movie/57098/315x210.jpg?4420", 92, "感染すると次第に五感が奪われていく奇病が蔓延する世界で、運命的な出会いを果たした男女の愛情を描くパニックドラマ。感染すると嗅覚を失う原因不明の病がイギリスから欧州各国へと広がり、感染症を研究する専門家のスーザンも何も分からず困惑する。そんなある日、スーザンは、感染症の影響で客足の途絶えたレストランでシェフのマイケルと出会うが、2人もまた病に感染し嗅覚を失ってしまう。そして人々は嗅覚に続き味覚、聴覚と次第に五感を失っていき、世界は荒廃していく。監督は「猟人日記」のデビッド・マッケンジー。主演にユアン・マクレガーとエバ・グリーン。", "2012-01-07", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊豆の踊子", "伊豆の踊子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "川端康成の同名小説の、４度目の映画化。今回は踊り子・かおる役に吉永小百合を迎え、相手役に高橋英樹を配し、さわやかな仕上がりになっている。宇野重吉扮する大学教授が過去を回想するという形で始まるところが他の作品とは異なり、ラストには回想が終わり、教え子の大学生が恋人とデートに行くのを宇野重吉が目を細めて見つめているというエンディングも異色。この恋人を吉永小百合が二役で演じており、清楚な踊り子と現代っ子の女性と好対照をなす構成が面白い。踊り子が天城を訪れた学生と碁をさす有名なシーンは、この作品のみ“はさみ将棋”に置き換えられ、高橋英樹のひょうひょうとした感じと相まって、ほのぼのとした雰囲気が漂う。西河克己はのちに、山口百恵主演で再び「伊豆の踊子」を作っている。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BALLAD OF THE SAD CAFE", "悲しき酒場のバラード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "フォークナーやカポーティと並ぶ南部文芸作家マッカラーズ原作の映画化。ならず者、大女、小男の３人が織り成す、いたちごっこのような奇妙な三角関係が、アメリカ南部特有の閉鎖的な世界の中で展開する。ハリウッド映画に代表されるようなリッチで開放的なイメージとは正反対の、もう一つのアメリカの素顔が渇いたタッチで描かれている。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Star Trek 2", "スター・トレック2（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　ROI　DANSE", "王は踊る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「カストラート」のG・コルビオ監督が、再び音楽を題材に撮り上げた豪華絢爛宮廷絵巻。優れたダンサーでもあった“太陽王”ルイ14世と、宮廷音楽家ジャン・バティスト・リュリ。17世紀のフランスを舞台に、ルイの権力掌握のために3000もの曲を書き続けたリュリの禁断の愛、芸術への情熱、劇作家モリエールの栄光と没落、そしてルイが絶対君主となるまでを、史実に基づき荘厳な映像と音楽にのせて描き出す。同年、「ピアニスト」でカンヌ映画祭主演男賞を獲得した美形俳優、ブノワ・マジメルが金粉に身を包んで圧倒的なバロック・ダンスを披露。ロケは本物のベルサイユ宮殿で行われた。芸術を心ゆくまで堪能できる贅沢な一品だ。", "2000-01-01", "37,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROMA’S WAR", "悪魔の毒々プラトーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "旅客機が墜落し、奇跡的に10数名の乗客が生き残った。助けを求めてさまようものの、そこが無人島と分かる。そして、そこには世界中のテロリストが集まり、ひそかに訓練を続けているのだった。乗客をCIAが送り込んだコマンドと勘違いするテロリストたち。乗客たちとの戦いも派手な戦闘スプラッタ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIREZ SUR LE PIANISTE", "ピアニストを撃て", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "パリのカフェでピアノを弾くシャルリーは、国際的に著名なピアニストだった。しかし、自分の成功の陰に、妻と興行主の関係があったことを知って、妻を自殺に追いやってしまった過去を引きずっていた。そんな彼に思いを寄せるウェイトレスのレナ。今度こそ幸せを手に入れようと思ったやさき、シャルリーの兄が悪事を働き、彼とレナは、その騒動に巻き込まれてしまう。流れ弾に当たったレナは、彼の腕の中で、息絶えた……。原作は暗黒小説で暗い幕切れだが、トリュフォーの弾けるような軽妙な演出で、テンポの良いシャレたサスペンス・ドラマに仕上がっている。劇中でのアズナブールのシャンソンも聴きどころ。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルビー・カイロ", "ルビー・カイロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ロスに住む平凡な主婦ベッシーの元に届いた夫の事故死の知らせ。遺体確認のため訪れたメキシコで、夫の秘密に気づいてしまった彼女は、暗号を秘めたカードを片手に世界をめぐる旅に出た。自分の知らない夫の真実の姿を探し求めて……。角川映画ハリウッド進出第１作として製作されたラブ・サスペンス。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAZARD ハザード", "HAZARD ハザード", "", "", "http://image.eiga.k-img.com/images/movie/33738/315x210.jpg?3103", 103, "「自殺サークル」「紀子の食卓」の鬼才・園子温監督がオダギリジョーを主演に迎え、ニューヨークを舞台に若者たちの危険な日常を描いた青春ドラマ。退屈な学生生活にうんざりしていた日本の青年シンは、大学の図書館で見つけた本で、ニューヨークの犯罪都市「HAZARD（ハザード）」の存在を知る。刺激を求めて単身ニューヨークへと渡ったシンは、そこで出会ったギャングスターのリーやタケダと行動を共にするようになるが……。", "2006-11-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森繁のやりくり社員", "森繁のやりくり社員", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "森繁演じる主人公は、東西放送広告社のプロデューサー・森岡茂。彼の仕事ぶりは芳しくなく、社長のカンナ女史ににらまれてばかり。そんな折、ポポナ化粧品に勤める森岡の妻・蓮子がミス・ポポナに選ばれ、同社提供の番組に出演、人気を博す。その結果、森岡は、”主夫”としての生活を始めるのだった……。夫妻の立場が逆転する痛快コメディ。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL NIDO", "エル・ニド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "妻と死に別れ、クラシック音楽とチェスを日課として暮らしていた初老の男が、ある日、こよなく小鳥を愛する13歳の少女と知り合う。二人は互いに惹かれていくが……。スペインの素朴な風景の中に、老人と少女の激しくも悲しい無償の愛を描いた作品。", "1980-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SMOOTH TALK", "スムース・トーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "15歳のコニーは、つかの間の夏休みを過ごしている。今こそ家庭や母親の束縛からも離れ、外に出て、ためらいがちな女への第一歩を踏み出す時がきた……。女流監督チョプラが15歳の多感な少女の姿を鮮やかに描き出したデビュー作。U.S.フィルムフェスティバル・グランプリ受賞作。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE YELLIW ROLLS ROYCE", "黄色いロールス・ロイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "1930～41年にかけての黄色いロールスロイスにまつわる３つの物語を綴ったオムニバス。1930年のロンドンでは国務大臣、1941年はアメリカの上流婦人と、代々の持ち主を主人公にした風俗ラブ・ロマンス。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TINIKLING OR “THE MADONNA AND THE DRAGON”", "デンジャーヒート 地獄の最前線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1986年２月、革命前夜のフィリピン。終わりなき内戦と政情不安のなか、フォト・ジャーナリストのパティとサイモンが撮ったスクープは、マルコス政権に壊滅的な打撃を与える重大スキャンダルだった。やがてそのフィルムをめぐって国軍、反政府ゲリラ、武器密輸商人たちが暗躍を始める。事件記者出身のフラーらしい、リアリティーあふれる社会派アクション。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOTHER　TERESA", "マザー・テレサ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "貧困にあえぐスラムの人々の救済に生涯を捧げ、ノーベル平和賞を受賞したマザー・テレサ。さまざまな困難にぶつかりながらも、強い信念で偉業を成し遂げていく姿は、伝説の人物と見られがちだが、本作では勇気あるひとりの女性として鮮やかに描かれている。この大役を演じることを熱望していたというオリヴィア・ハッセーが、ローマ法王に謁見を許されたほどの渾身の演技を見せ、マザー・テレサの人間像を見事に再現。内面からあふれ出る美を感じさせる名演が、感動を呼ぶ一作。インドのカトリックの女子校で教鞭をとっていたマザー・テレサは神の声を聞き、貧しい人々のために生涯を捧げる決心をする。彼女は“神の愛の宣教者会”を創立し、活動を始めるが……。", "2005-08-13", "4,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("木曜組曲", "木曜組曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "４年前、女流作家の重松時子が洋館で謎の死を遂げた。以来、ある月の木曜日を挟む３日間、５人の女たちが洋館に集い、宴を開くことが習慣になっていたが、今年は思いがけない事実が発覚し……。日本を代表する女優５人の競演を、篠原哲雄監督が見事にさばいたサスペンス映画。なかでも、久々の映画出演となった時子役、浅丘ルリ子の存在感が際立っている。どんでん返しのオン・パレードで贈る。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BACK TO SCHOOL", "バック・トゥ・スクール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "成金親父メロン氏の悩みは、勉強のできない息子と自分の中卒という学歴。両方一挙に解決せんとカネの力で息子の大学に入学、教授や学生を巻き込んでのやりたい放題で、キャンパス内が大騒動。日本では無名だがデンジャーフィールドのトンでるギャグが痛快な中年青春ドラマ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DECEIVERS", "デシーバーズ 暗黒の大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1825年、イギリス東インド会社で働く一人の若者は、この地に凶悪な秘密結社が存在していることを知る。正義に燃えた彼は、この組織を打倒することを誓う。なんとか変装して内部に潜入し、悪戦苦闘をするのだがついに正体を見破られてしまう。彼は必死の逃亡を図るが……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("予言", "予言", "", "", "http://ecx.images-amazon.com/images/I/2151TT0BC6L._SL210_.jpg", 95, "「感染」とともに“Jホラーシアター”の第１作として公開された戦慄のホラー。ホラー・コミック界の第一人者、つのだじろうの代表作『恐怖新聞』を三上博史、酒井法子を主演に据えて実写化した話題作だ。未来に起こる出来事を告げる“恐怖新聞”にほんろうされる人間の苦悩と苦闘が、背筋を凍らせる恐怖を呼び起こす。家族で帰省先から戻る途中、父親が電話ボックスで娘が事故死するという記事が掲載されている新聞を見つけた。その瞬間、娘が乗る車が大破して……。", "2004-10-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROTHER FROM SPACE", "帰ってきたE.T.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "突然飛来したUFOが置き捨てた数個のカプセル。その中に潜む、いたずら好きだが心優しいE.T.と、超能力を持つ盲目の女教師ジェニーとの交流を描いたSF映画。警察の手からE.T.を救おうという設定は、そのまま本家「E.T.」とそっくり。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("めし", "めし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "巨匠・成瀬巳喜男監督の初期の代表作。同名原作は林の未完の絶筆で、倦怠期にある夫婦がささいな出来事から次第に亀裂を深めていく様を描いており、脚色にあたっては、家を出た妻が夫のもとに戻り、平凡だが心安まる生活に幸福を見いだすという結末が補足されている。成瀬演出は、劇的で明快な主題を持つ物語を語るのではなく、日常生活のキメ細かい描写や、俳優たちの持ち味を最大限に引き出す手腕に高い評価が与えられているが、この作品にはまさにその真骨頂が見いだせる。ささやかで慎ましい夫婦の生活の匂いを感じさせる住居、妻を演じる原節子の微妙な表情の変化やたたずまい、それを受ける夫役の上原謙の抑えた芝居など、あらゆる要素が成瀬独特の作品世界を構成して圧巻である。名撮影監督・玉井正夫が初めて成瀬とコンビを組んだことでも重要な作品。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Oscar Wilde's An Ideal Husband", "理想の結婚", "", "", "http://image.eiga.k-img.com/images/movie/1858/315x210.jpg?3013", 100, "1895年のロンドン、華やかな上流社会。独身生活を謳歌するアーサー卿は、親友の妹との結婚に踏み切れない優柔不断男。ある日、かつてのアーサーの婚約者で今はウィ−ン社交界の華となったチーヴリー夫人がロンドンに戻ってくる。アーサーの友人、ロバート夫妻が催すパーティで夫人は意味深にロバートに接近。それはロバートの過去の秘密をネタに彼を脅迫し、自分が投資している計画を議会で後押しさせるためだった。", "2000-02-19", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEPTEMBER AFFAIR", "旅愁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "飛行機で知り合った男女が、予定を変更し、ナポリの空港に降り立つ。二人を置いて飛行機は飛び立ち墜落、彼らの名は死亡者リストに載せられた。互いにかつての生活を捨て、二人は愛し合うようになるのだが……。ナポリの美しい景観と、哀切を込めたテーマ曲が心に残るメロドラマ。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みだれ髪", "みだれ髪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "明治30年、深川の木材問屋の娘・夏子は、医師・光起と恋に落ちるが、病身の父と散財のため、彼をあきらめて芸者となる。夏子を慕う板前の愛吉は、常にかたわらで彼女を見守っていた。歳月が流れ、光起と夏子は再会するが……。泉鏡花の『三枚鏡』を衣笠貞之助が脚色・監督したメロドラマ。山本富士子が女の情念を好演。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　ツバサ・クロニクル　鳥カゴの国の姫君", "劇場版　ツバサ・クロニクル　鳥カゴの国の姫君", "", "", "http://ecx.images-amazon.com/images/I/31K7RNTMRHL._SL210_.jpg", 35, "女性漫画家集団・CLAMPによる大ヒットコミックを原作に、記憶を失った少女を救うため異世界へと旅立った少年たちの冒険を描いたファンタジーアニメの劇場版。異世界に飛び散ってしまったサクラの記憶の羽根を集めるため、次元を越えた旅を続ける小狼たち。ある日、彼らは人間が鳥をパートナーとして暮らす「鳥カゴの国」を訪れる。しかし、現在この国では、不思議な羽根の力を手に入れた国王が国民たちを弾圧していた……。", "2005-08-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("滾滾紅塵／RED DUST", "レッド・ダスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "日本による大陸への侵略、政府の降伏、そして共産党の対立。日中戦争まっただ中、激動のドラマに満ちていた時代の中国を背景に、著名女流小説家と、大日本帝国政府への協力者との悲劇的な愛の行方を描いた一大恋愛メロドラマ。ツイ・ハーク、アン・ホイらと同世代に属する香港ニューウェイブ監督の一人、イム・ホー監督作品。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HUKKLE", "ハックル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "世界各国の映画祭で話題をさらった、ハンガリーの新人監督パールフィ・ジョルジのデビュー作。映画学校に通っていた彼の卒業制作作品ながら全米劇場公開もされた異例の逸品だ。題名の“ハックル”とは、ハンガリー語でしゃっくりの音のこと。映画は、ある村のおじいさんの止まらないしゃっくりで始まり、サスペンスにも、はたまたブラック・コメディにもとれるような物語が展開。その意味深なストーリー、ユーモアを含んだ構成、天才的な感性を感じさせる映像手法のいずれもが目を見張る。ハンガリーのとある村。夜が明けると共に村人たちのいつもと変わらぬ一日が始まる。ところが穏やかな時間が進む中、猫が変死したり、謎の地震が発生したりと怪現象が起こり……。", "2005-10-22", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("市民ポリス69", "市民ポリス69", "", "", "http://image.eiga.k-img.com/images/movie/55948/315x210.jpg?2146", 102, "「特命係長只野仁」の柳沢きみおによる原作コミックを映画化。気弱な中年サラリーマンの芳一は、警察が軽犯罪を取り締まるため善良な一般市民から選出する「市民ポリス」の69人目に任命されるが、犯罪に巻き込まれることを恐れて逃げてばかり。しかし、ある日、仕事でコンビニで働く美少女・桃と出会ったことから、芳一の運命は大きく変化していく。ベテラン個性派俳優・酒井敏也が初主演。ヒロインに人気アイドルグループ「ももいろクローバー」の早見あかり。", "2011-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代好色伝 テロルの季節", "現代好色伝 テロルの季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "若松孝二監督の過激作。吉沢健扮する全学連のかつての闘士が団地に二人の女と住んでいる。二人の刑事が盗聴しているが、男は女たちとセックスを繰り返すばかりでついに刑事もあきらめて帰る。一人になった時、男は体中にダイナマイトを巻いて羽田空港へ突っ込んでいく。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海の鷹", "海の鷹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "この後、“男の紋章”シリーズでコンビを組むことになる高橋英樹と和泉雅子が主演した青春アクション。桂木回漕店の息子・秀男は、店が商売仇の谷海運によって、悪どい妨害を受けているのを知る。谷海運の社長、信彦は善人だが、店の実権は社員の室戸に奪われていた。秀男と信彦は手を組んで室戸と戦うことになり……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EYES OF LAURA MARS", "アイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ニューヨークの売れっ子ファッション写真家ローラが、暴力とセックスをテーマに写真集を出版。すると、彼女の周囲に殺人事件が続発。ローラがファインダー内に予知する殺人の幻覚と、ニューヨークの華やかなモード界を絡ませたサスペンス・ロマン。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIGRERO−A FILM THAT WAS NEVER MADE", "ティグレロ　撮られなかった映画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "1997年に他界したS・フラー監督が、1954年にジョン・ウェイン、エヴァ・ガードナー、タイロン・パワーの主演で撮る予定だったアマゾンが舞台の映画「ティグレロ」をめぐるドキュメンタリー。同じく南米の秘境を舞台とした作品「アマゾン」を1991年に製作したM・カウリスマキが、フラー本人とフラーが1950年代にカラジャ・インディアンを撮影したフィルムを伴い現地入り。二人の盟友J・ジャームッシュが狂言回しとして加わり、彼らの映画再発見の旅が綴られる。", "1993-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風雪講道館", "風雪講道館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "柔道の創始者、矢野正五郎を主人公に据えた男のドラマ。学習院で講師をしつつ、柔道の研究を続ける正五郎には敵も多かった。そのうちの一人、泉専太郎一派に襲われた彼は、これを撃退するが、新聞に悪く書かれてしまう。彼は学習院教師としての責任を問われるが、彼を狙う敵と闘わねばならぬことに。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タンポポ", "タンポポ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "とある町へふらりとやって来たタンクローリーのドライバーが、さびれたラーメン屋を女手一つで経営する美しい未亡人に惹かれ、そのラーメン屋を町一番の店に作り直して去っていくというストーリーを軸に、途中に13の短い食べ物にまつわるエピソードを盛り込んだユニークな作品。「お葬式」で鮮烈に映画監督としてデビューした異色俳優・伊丹十三の２作目。まるで西部劇「シェーン」を思わせる物語や、カウボーイにも似た山崎努の衣装で、“ラーメン・ウエスタン”とも呼ばれ、ニューヨーク、パリをはじめ海外でも大ヒット。伊丹作品にはおなじみの宮本信子のほか、加藤嘉をはじめとしたキャストも豪華。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CB4", "CB4", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ラップ・スターのガストが、麻薬所持で逮捕された。その間ラップ・グループCB4の人気が急上昇。怒ったガストが、彼らを落とし入れようと罠を仕掛けた。「９デイズ」のC・ロックが主演し、CB4本人たちのライヴも聞けるラップ・コメディ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Coco avant Chanel", "ココ・アヴァン・シャネル", "", "", "http://image.eiga.k-img.com/images/movie/54218/315x210.jpg?3455", 110, "「アメリ」のオドレイ・トトゥが、若き日のココ・シャネルを演じた伝記映画。監督は「おとぼけオーギュスタン」のアンヌ・フォンティーヌ。フランスの片田舎の孤児院で姉とともに育てられたガブリエルは、施設を出た後、寂れたナイトクラブの歌手やお針子として働いていた。そこで貴族エティエンヌ・バルザンに見初められ何不自由ない生活を手に入れるが、ありのままの自分を受け入れてくれるアーサー・カペルと運命的な恋に落ち、自らのファッション・スタイルを模索していく。", "2009-09-18", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NICOLAS", "ニコラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "雪山でのスキー合宿に参加した内気な男の子ニコラは、自分がおねしょをせずに済むかどうか心配でならない。厳格な父親への恐怖心から、恐ろしい夢想にふけるニコラ。そんなとき合宿所の近所で子供の死体が発見されるという事件が起こり……。C・ミレール監督が12歳の少年の心象風景を、現実と妄想を交錯させて描き上げた心理ミステリー。繊細な映像美とA・テクシエの既製ジャズ・ナンバーが巧みにサスペンスを盛り上げ、物語は悲劇的な方向に転がっていく。W・W・ジェイコブズ作の有名な怪奇小説『猿の手』が、ニコラが見る悪夢の一つのエピソードとして映像化されている。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARD TARGET", "ハード・ターゲット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "“香港ノワール”を代表するジョン・ウー監督の記念すべきハリウッド進出第一作。“人間狩り”に興じる凶悪殺人集団に立ち向かう男の戦いを描いたハード・アクション。行方不明の父を探す美人弁護士に協力する主人公を、バン・ダムが得意のマーシャル・アーツを駆使して熱演。バイク・スタント、銃撃戦、ヘリ対馬のチェイスと、ノンストップ・アクションで突っ走る。香港時代以上にスローモーションを多用した、ジョン・ウー監督ならではのアクション描写が見もの。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BALANCE", "愛しきは、女 ラ・バランス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "パリの場末の盛り場。この一帯を仕切るギャング組織と対峙する警察は、組織内に“ラ・バランス”と呼ばれるスパイを使って情報提供を強いていた。組織と警察に挟まれた一人の男の悲哀と、娼婦との純愛を美しく綴る。セザール賞主要４部門を独占した佳作。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長い長い殺人", "長い長い殺人", "", "", "http://image.eiga.k-img.com/images/movie/53608/315x210.jpg?2636", 135, "東京郊外で殺人事件が発生。被害者の男性は、車ではねられた上に激しく殴打されていた。男性に多額の保険金を掛けていた妻・法子と彼女の愛人である塚田が捜査線上に浮かび上がるが、2人には完璧なアリバイがあり……。登場人物の財布を通してひとつの殺人事件を浮かび上がらせるという斬新な手法でベストセラーとなった宮部みゆきの同名小説を映画化。キャストには長塚京三、仲村トオル、大森南朋ら実力派俳優が集結した。", "2008-05-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誘拐報道", "誘拐報道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "“さそり”シリーズなどで華麗な様式美を定着させた伊藤俊也が、本作では犯人側に視点を置いて、犯人とその家族、被害者の家族、報道関係の姿を、誘拐を通して重厚に描く。原作は1980年１月に宝塚市で起きた誘拐事件を取材したドキュメント。萩原健一がエキセントリックな犯人役となり好演。誘拐した子供に情を移すシークエンスは感動的。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("СИНЯЯ ПТИЦА／THE BLUE BIRD", "青い鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "米ソ初の合作によるメーテルリンクの有名な童話の映画化。幼い兄妹は、幸せを呼ぶ青い鳥を捜しに、魔法の帽子で姿を変えた光、火、水、パンなどを伴って冒険の旅に出かける。果たして青い鳥はどこに……。ミュージカル仕立ての楽しい児童映画。", "1976-01-01", "1,12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠野物語", "遠野物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "古くから伝承と民話の宝庫とされる、岩手県上閉伊郡の早池峰山麓から遠野に至る地方の話の集大成、『遠野物語』に基づいて作られた幻想的フィクション。岩手放送開局30周年を記念して製作された作品。伝承の暗い部分が主に強調されているが、物語性のある隠れた名作。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エクスドライバー the MOVIE", "エクスドライバー the MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "人気漫画家・藤島康介がアニメーション作品だけのために、初めて企画・原案・キャラクター原案を手掛けたOVA『エクスドライバー』の映画化。エクスドライバーレース世界大会開催の前夜、無人のクリーニングカートが暴走し路上に撒き散らされたオイルが燃えるという事件が発生。全世界が注目するレースを妨害する人物とは……。映画化に伴い主人公たちの乗るすべてのスーパーカーをCG化。OVAシリーズを遥かに凌ぐカー・アクションが堪能できる。外伝「エクスドライバー　Nina&Rei Danger Zone」も同時上映された。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙戦艦ヤマト　復活篇", "宇宙戦艦ヤマト　復活篇", "", "", "http://image.eiga.k-img.com/images/movie/54638/315x210.jpg?4690", 135, "74年にTV放映されて以来、根強い人気を誇るSFアニメ「宇宙戦艦ヤマト」の劇場版第5作。2217年、観測史上最大のブラックホールが太陽系に接近し、地球が飲み込まれることが確実となる。人類は地球から2万7000光年離れたサイマル星系へ大規模な移動を開始するが、謎の艦隊による攻撃を受け、次々と船団が宇宙の塵となっていく。この事態を解決すべく、伝説の男・古代進は宇宙戦艦ヤマトへ乗り込み、広大な宇宙へと旅立つ。", "2009-12-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE　AND　OTHER　CATASTROPHES", "ラヴ＆カタストロフィ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "男女５人の大学生が織り成す恋模様を、１日の出来事の中に描くラブ・コメディだ。共同生活をしているミアとアリスが、新しいルームメイトを探し始める。レズビアンのミアは恋人ダニと破局寸前のうえ、転部手続きに手まどい四苦八苦。一方、男性の好みにうるさいアリスはハンサムなアリにお熱だ。そんな二人の前に、同居を希望する男子学生マイケルが現れ……。ささいなことで喜んだり悩んだりする彼らの感情の動きを、リアルな会話の中に活写。細かいエピソードをつなぐ、軽やかでテンポのよい展開も心地好い。", "1996-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("家庭の事情", "家庭の事情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "吉村公三郎、新藤兼人のコンビは、数々の名作を作り上げてきたが、このホームドラマは、二人の手によるものとしては凡作の部類。退職した老サラリーマンが、その退職金を４人の娘に、平等に分け与える。父と４人の娘の、金の使い道を、５つのエピソードで綴っているが、ドラマとしてはどれもお手軽で、正月映画の顔見せ興行的な感じはぬぐえない。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("運が良けりゃ", "運が良けりゃ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "落語通の山田洋次が、落語の『ラクダの馬さん』『寝床』『黄金餅』『つけ馬』などを素材に描いた長屋喜劇。落語でもおなじみの熊さんにハナ肇、八っつぁんに犬塚弘などクレージー・キャッツのメンバーがバイタリティーあふれる庶民を怪演する。向島の裏長屋に住む、熊さん、八っつぁん、金貸しのおかん婆、クズ屋の久六、アンマの梅喜、熊さんの妹で“はきだめに鶴”のせいたちは、貧乏で酒とバクチに明け暮れる傍若無人の日々を送っている。ある日、家主から店賃値上げを言われて憤慨した熊さんは一計を図るが……。おかん婆が貯め込んだ金を、餅にくるんで食べ、死んでしまうくだりのブラックなギャグなども秀逸。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妻の愛人に会う", "妻の愛人に会う", "", "", "http://image.eiga.k-img.com/images/movie/53498/315x210.jpg?2479", 92, "江原道で小さなハンコ店を営んでいるテハンは、妻の浮気を確信し浮気相手のジュンシクがいるソウルを訪れる。テハンは2人の密会現場を押さえようと、正体を明かさないままジュンシクが運転するタクシーに乗り込んで江原道を目指すが……。韓国の新鋭キム・テシク監督が、男女の愛の不確かさを皮肉たっぷりに描いたブラック・コメディ。主人公テハンを演じるのは、演出家としても活躍する名バイプレーヤーのパク・クァンジョン。", "2008-04-19", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YANKS", "ヤンクス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "第二次世界大戦中、大陸出動に備えてイギリスの小都市に進駐していたアメリカ兵と町の人々との葛藤、男女間に芽ばえる愛情を描く。アメリカに移住した経験を持つシュレシンジャー監督が英米の国民感情の微妙な違いを的確に捉え映像化。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hotel Rwanda", "ホテル・ルワンダ", "", "", "http://image.eiga.k-img.com/images/movie/1868/315x210.jpg?3284", 122, "94年、アフリカのルワンダで民族間の対立が大虐殺に発展し、100日間で100万人もの人々が惨殺さる中、ルワンダの高級ホテルに勤めていた一人の男がホテルに1200人もの人々をかくまってその命を守り抜いた。この実話を「父の祈りを」でアイルランド紛争を描いた脚本家テリー・ジョージが映画化。主演は「トラフィック」のドン・チードル。国連兵士役でニック・ノルティ、報道カメラマン役でホアキン・フェニックスが共演。", "2006-01-14", "67,3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神☆ヴォイス", "神☆ヴォイス", "", "", "http://image.eiga.k-img.com/images/movie/57138/315x210.jpg?4505", 102, "声優を目指す若者たちの成長を、実際の人気声優たちが演じる青春ストーリー。大ヒットアニメ「リヴァリス」の主役を演じて人気を博していた声優の白池悠宙と久保寺辰真は、ある事件をきっかけに現役を引退してしまう。悠宙は小さな声優学校の講師を引き受けることになるが、待ち受けていたのは一癖も二癖もある生徒ばかりで……。主演は若手俳優の梶裕貴と羽多野渉。共演陣にも森久保祥太郎、浪川大輔、宮野真守ら人気の若手から、羽佐間道夫、千葉繁、キートン山田、山寺宏一らベテラン勢まで幅広い声優陣が集結している。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE QUATERMASS EXPERIMENT", "原子人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "イギリスBBC放送のTVシリーズ“クォーターマス”の映画化。ハマー・プロのSF怪奇映画の先鞭ともいうべき作品である。実験用ロケットQ1号が、原因不明の故障で墜落した。救助にかけつけると、生存者はキャルーン一人。他の者は宇宙服だけを残して消えていた。そして、収容されたキャルーンも、腕がサボテンのようにふくれあがって逃亡する。その腕に触れたものは、エネルギーを吸われて骨となるのだ。襲うたびに肥大する腕。彼は、ついに不定形の怪物と化す。ニュースキャスターが登場したり、取材さながらにハンドカメラが使われたり、ドキュメンタリー・タッチが恐怖を盛り上げる。", "1955-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イチジクコバチ", "イチジクコバチ", "", "", "http://ecx.images-amazon.com/images/I/41Ul8huiyFL._SL210_.jpg", 80, "青春と性をテーマにした「青春Hシリーズ」第7作。2人暮らしの父親・正夫に監禁され、学校にも行かれずネット売春で父親を養っているみつの元に、ある日、顧客の慎吾が突然訪れてくる。父親との関係を知った慎吾はみつを連れて逃げ出し、ラブホテルやカプセルホテルを行き来しながら2人で共同生活を始める。慎吾も母親を亡くしていることからみつに共感し、2人はひかれ合うが、そこへみつに捨てられたと怒り狂う正夫が押しかけてくる。", "2011-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パイパティローマ", "パイパティローマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "衛星チャンネル、WOWOW製作による〈J・MOVIE・WARS〉シリーズ第２期作品の一編。“パイパティローマ”とは、日本最南端の波照間島に伝わる伝説の理想郷。沖縄本島、石垣島、波照間島、蘭嶼（台湾）の雄大なロケーションを背景に、“パイパティローマ”を求めて旅する現代女性の姿を、独特の情感と不条理な場面の連続で描く。とも子は恋人の影を追って沖縄に降り立つが、そこで出会った不思議な人たちに導かれるまま南への旅を続けるうちに、次第にそれまでの自分から解放されていく……。元プリンセス・プリンセスのキーボード奏者、今野登茂子が女優に初挑戦し、主人公とも子をみずみずしく演じる。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Little Black Dress", "マイ・ブラック・ミニドレス", "", "", "http://image.eiga.k-img.com/images/movie/56938/315x210.jpg?2919", 108, "現実に悩みながらも前向きに歩んでいく4人の20代女性の姿を描いたガールズムービー。名門大学の演劇映画学科で出会ったユミン、ヘジ、スジン、ミニは学校を卒業すれば映画の主人公のような人生が開けると信じていた。時に夢と現実のギャップに悩みながらも互いを励ましあってきた4人だったが、ヘジがスターになると嫉妬心が生まれ、次第に友情にひびが入りはじめる……。「宮 Love in Palace」「コーヒープリンス1号店」で人気のユン・ウネら若手女優が主演するほか、人気K-POPグループ「U-KISS」のドンホも出演。韓国映画の名作や最新作を一挙上映する「K-MOVIEセレクション2011」にて上映。", "2011-09-17", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イヌワシ 風の砦", "イヌワシ 風の砦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "国の天然記念物であり、絶滅の恐れがあるとして特殊鳥類に指定されているイヌワシの生態を７年間にわたって追い続けたドキュメンタリー。『万葉集』に歌われているのをはじめ、数々の伝説を残すこの神秘な鳥の謎が解明され、彼らを危機に追いやる自然破壊の実態が告発される。監督は動物記録の大家・岩崎雅典。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スクライド　オルタレイション QUAN", "スクライド　オルタレイション QUAN", "", "", "http://image.eiga.k-img.com/images/movie/57068/315x210.jpg?1499", 90, "「無限のリヴァイアス」（1999）、「コードギアス　反逆のルルーシュ」（06）の谷口悟朗監督が手がけた人気TVアニメーション「スクライド」（01）の10周年を記念した特別版。新作パートの追加や全編新アフレコ、HDデジタルリマスターなどを施しTVシリーズを再構築した2部作の後編。ロストグラウンドの再隆起現象から8カ月後。カズマと劉鳳の力を利用しようと企む無常矜侍が、部下を率いてロストグラウンドに降り立つ。しかし戦う理由を見失ったカズマは荒野を放浪し、記憶をなくした劉鳳も集落に身を寄せていた……。", "2012-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オンナゴコロ", "オンナゴコロ", "", "", "http://image.eiga.k-img.com/images/movie/54298/315x210.jpg?3619", 100, "刺激のない日々を送る不感症のアコは、心の隙間を埋めるためにさまざまな男と関係を持つが、全ての感情を封印したアコのもとには虚しさが残るだけだった。男達から不感症のレッテルを貼られたアコは、いつしかダーツバーでダーツにはまり、自分の体を賭けて男達とダーツのゲームを始める……。「閉じる日」「BORDER LINE」の前田綾花が、生きる意味を貪欲に探し求める主人公を熱演。", "2009-03-07", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ねずみ小僧怪盗伝", "ねずみ小僧怪盗伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "野村芳太郎監督が率いるプロダクション・クラップボードの製作第１回作品。義賊ねずみ小僧は実は姉弟の二人一役だったという設定で、むっつり右門や遠山金四郎、唐人お吉、赤ひげ先生まで登場する時代劇コメディ。中村雅俊と小川真由美が江戸を舞台に大暴れ！", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("儀式", "儀式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "敗戦によっても揺らぐことのなかった家長制度に縛られて生きていくことを強いられた、戦後世代の若者たちの悲劇を描いた意欲作。個人が家に組み込まれ支配されるさまを、冠婚葬祭の儀式になぞらえて描き出す。混迷と動乱に満ちた昭和の時代と日本人の心情を探りながら、大島渚監督が戦後25年を総括する意味を込めた作品。すでに海外では評価の高かった大島監督の、国内での評価を不動にした傑作。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("急にたどりついてしまう", "急にたどりついてしまう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "立花信次が東京郊外の小さな町に来て２年。他人と距離を置くタイプの信次だが、仕事場のソーセージ工場では孤児として育った正夫に慕われ、アパートには素性の知れぬ男・衆木が居候していた。ある日アパートに帰った信次は、そこで見知らぬ女の子・リサと出会う。衆木と一緒に町を出る約束をしていたリサは、結局町に残り、やがて二人は惹かれ合うようになる。“いつのまにか大事な場面に立ち会っている”登場人物たちの息づかいを、詩人の福間健二がピンク映画の俊鋭たちの協力を得て描く。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("玉割り人ゆき", "玉割り人ゆき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "牧口雄二の監督デビュー作。昭和初期、遊廓に売られてきた娘に性交を教える玉割り人・ゆきを主人公に、京都島原の娼婦の世界を描く。川谷拓三が足抜きに失敗して、男根を切られる大工の役を熱演。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN BUFFALO", "アメリカン・バッファロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "現代アメリカを代表する劇作家D・マメットの名作戯曲を映像化。年代ものの硬貨をめぐる３人の男たちの、一日の葛藤を描きながら、人間の欲深さをペーソスとともに浮き彫りにする。古物屋のドニーは使い走りの黒人少年とともに、値打ちもわからず売った貴重な硬貨“アメリカン・バッファロー”を客から取り戻そうと画策。ところが、この計画に悪友ディーチがむりやり加わってきたことから、事態は思わぬ方向へ向かう。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ninth Gate", "ナインスゲート", "", "", "http://image.eiga.k-img.com/images/movie/1838/315x210.jpg?4193", 133, "辣腕の本の探偵コルソは、バルカン出版の社長ボリスから、最近彼が手に入れた1冊の本──17世紀の悪魔書「ナインスゲート」にまつわる仕事を依頼される。世界中に3冊しか存在していないこの伝説の本の残り2冊を探し出し、その真贋を確かめろというのだ。コルソはまず、ボリスにこの本を譲ったコレクター仲間を訪ねるが、彼は謎の自殺を遂げていた。危険を感じたコルソは本を友人バーニーに預けるが、翌日バーニーも本の挿し絵と同じ姿で殺される。", "2000-06-03", "2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サラリーマン悪党術", "サラリーマン悪党術", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "元学生運動闘士の伊達は広告会社に勤務しているが、出世の道も望めぬ身。妻もすでに彼への期待を捨てている。そんな伊達の楽しみといえば、大阪出張の折に出張費を浮かして支社の里美に会うこと。ところが、里美が伊達夫婦の隣室に引っ越してきて……。風刺喜劇に異彩を放つ須川監督のサラリーマン・コメディ。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INDIANA JONES AND THE TEMPLE OF DOOM", "インディ・ジョーンズ 魔宮の伝説", "", "", "http://ecx.images-amazon.com/images/I/51JqnxDammL._SL210_.jpg", 118, "世界的大ヒット作「レイダース／失われたアーク《聖櫃》」から３年後に製作された、シリーズ第２作。ルーカス＆スピルバーグというスーパー・ヒットメーカーの手による、冒険活劇である。主人公“インディアナ・ジョーンズ”には前作同様フォードが扮し、「スター・ウォーズ」シリーズのハン・ソロ役とともに、彼のキャラクターが遺憾なく発揮されハマリ役となった。物語は前作「レイダース…」の１年前にあたる1935年の上海から始まる。暗黒街のボスとの取引に訪れたインディはボスの罠にはめられ、命からがら脱出。相棒の中国人少年ショート・ラウンド、上海のナイトクラブで知り合った美女ウィリー・スコットとともに、インド奥地に逃げのびる。さびれはてた村にたどり着いたインディ一行は、村に襲いかかる邪教集団の存在を知り、村から奪われた聖なる石の奪回を頼まれる。かくしてインディは村の存亡を担ってジャングル奥地の魔宮を目指すのだった。DVDは「アドベンチャーズ・オブ・インディ・ジョーンズ コンプリートDVDコレクション」に収録。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROLL II", "トロル2 悪魔の森", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ファンタスティック・ホラー「トロル」の続編。不気味でリアルなクリーチャーデザインが見もの。ある一家が夏休みを田舎で過ごすためやってきた村、ニルボッグ。その村人たちは人間を餌食にする伝説の悪魔トロルだった。末っ子ジョシュアだけは、それを感じ家族に訴えるが、取り合ってもらえない……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飛ぶは天国、もぐるが地獄", "飛ぶは天国、もぐるが地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "「明日なき街角」以来となる若松孝二監督作。雪山に向かったバスが突然、謎の光に包まれ停止。乗り合わせていた乗客たちは、ようやくたどり着いた旅館で、謎の少年に導かれながら、凄絶な体験をすることになる。演劇集団“月蝕歌劇団”の高取英による脚本で、劇団メンバーも多数出演しており、モチーフや舞台設定はかなり演劇的。乗客の一グループである、新興宗教の女教祖と信者たちが大量殺りくを繰り広げるクライマックスが用意されているが、終始しなやかで幽玄な演出の効果もあり、おどろおどろしさはほとんど感じられない。極限状況の中、自殺した父親への執着と決別するバスガイドを演じた、小野真亜矢の美しさが印象に残る。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Emperor Waltz", "皇帝円舞曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "　商標登録された犬のパロディやフロイトによる犬の精神分析など、コメディ要素が散りばめられたミュージカル映画。アメリカ人の蓄音機セールスマンがウィーンにやってくる。珍しい蓄音機を爆弾と間違えられたり、貴族の娘に一目惚れしたりと飼い犬を巻き込んで一騒動起きる。伸びやかに歌い上げる、ビング・クロスビーの歌声は聴きごたえ十分。しかし、後に「ミュージカルを作る才能がない」と語ったとおりビリー・ワイルダーにとって唯一の音楽劇となった。", "1953-07-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA NOTTE DI SAN LORENZO", "サン・ロレンツォの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "イタリア、トスカーナ地方には、“愛する人のため、８月10日のサン・ロレンツォの日の夜に、流れ星に願いをかけると叶う”という言い伝えがある。この映画は若い母親が、その夜に子供を寝かしつけながら自分の子供時代の戦争を語る形式を取り、平和への願いを叶えようとしている。1944年の夏、ローマを解放して北上しつつある米軍に対し、ドイツ軍のファシストたちは米軍の進路上にある村の家々を爆破し、村人をだまして皆殺しにし、撤退しようとする。罠に感づいた長老に従い村人の半数が村を脱出するが……。平穏な村の人たちが、戦争のためにお互いを殺しあう悲劇を、誰が主人公ということもなく、ある時はユーモラスに、ある時は幻想的に、美しく、また残酷に描かれている。プロの俳優は二人だけで、それ以外は実際のトスカーナ地方の人々が家族ぐるみで出演している。", "1982-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あらかわ", "あらかわ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "荒川上流にある滝沢ダムから東京湾まで169キロに及ぶ荒川の水路をたどりながら、現在における川の様相と水問題を克明にルポしたドキュメンタリー。破壊された農民、漁民の生活や、膨張し続ける都市のコントラストが映し取られている。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARIS IS BURNING", "パリ、夜は眠らない。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ニューヨークのハーレムで夜ごと開かれる不思議なパーティー。黒人やラテン系といったマイノリティーのゲイたちが、上流階級の白人さながらに着飾り、ファッションやダンスを競い合っている。本作は“ボール”と呼ばれるこのパーティーと、そこに集まるゲイの生態を捉えたドキュメンタリー。ウォール街の青年実業家やハリウッド・スター、ヨーロッパの貴族になりきる彼らの姿には、独自の美意識と現実社会では決して得られない地位への羨望がみなぎっている。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The California Dolls（...All the Marbles ）", "カリフォルニア・ドールス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　女子プロレスリング・タッグチーム“カリフォルニア・ドールス”のアイリスとモリー、そしてマネージャーのハリーは旅を続けながら、地方で行なわれる試合に参戦していた。華麗な技と美貌を武器に闘うドールズだったが、道中はハリーとの口論が絶えない。そんな中、アイリスは王座決定戦に出場したい一心から悪徳興行主エディと一夜を明かしてしまう……。名匠ロバート・アルドリッチ監督の遺作となった笑いと涙の痛快スポーツ・ドラマ。日本の女子プロレスラー、ミミ萩原とジャンボ堀も出演。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN FIL A LA PATTE", "恋は足手まとい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "19世紀末のパリの上流階級を舞台にした、フランス映画らしいエスプリに富んだ恋愛コメディ。美しい歌姫と若い伯爵令嬢とのふた股をかけたプレイボーイのトラブルが、ベルエポックの時代の優雅な装飾品の数々と共に映し出される。恋人の歌姫リュセットに内緒で、伯爵令嬢と持参金目当ての婚約を決めた貧しいプレイボーイ、エドワール。彼はリュセットに別れ話を切り出せないまま、令嬢との婚約式に臨むが……。", "2006-06-24", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WOMAN ON THE BEACH", "浜辺の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "1894年、印象派画家オーギュスト・ルノワールの次男として生まれ、無声映画からトーキーへの変遷の中で数多くの秀作を残したJ・ルノワール。第二次世界大戦期をハリウッドで過ごした彼は５本のアメリカ映画を撮ったが、その最後を飾ったのがこの作品だ。主人公は沿岸警備兵バーネットと美貌の人妻ペギー。この二人の出会いが発端となり、彼女の盲目の夫トッドと愛人バーネットの激しい争いへと発展していく。全編を通して水や炎などの強烈なイメージが連続し、1940年代とは思えない斬新な演出が随所になされている。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画クレヨンしんちゃん　超時空！嵐を呼ぶオラの花嫁", "映画クレヨンしんちゃん　超時空！嵐を呼ぶオラの花嫁", "", "", "http://image.eiga.k-img.com/images/movie/55128/315x210.jpg?2372", 0, "劇場版「クレヨンしんちゃん」シリーズの第18弾。ある日、未来都市・ネオトキオからタイムマシンに乗ってしんのすけの未来の花嫁タミコがやって来る。タミコいわく、未来のしんのすけはタミコの実の父親でネオトキオの支配者の金有増蔵により、どこかへ連れ去られてしまうらしい。未来のしんのすけを助けるため、タミコとしんのすけはネオトキオへと向かうが……。", "2010-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヤクザVSマフィア", "ヤクザVSマフィア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "劇場公開された、東映×アメリカの１本。ロサンゼルスへと乗り込んだ、日本のヤクザ・天銅組の澤本。彼は、敵対するイタリア・マフィアから襲撃を受け、ニックという青年に助けられる。ニックと澤本は友情で結ばれるが、ニックの正体はFBI捜査官で……。石橋凌が、“侠気”を通すヤクザ・澤本を好演。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Raising Cain", "レイジング・ケイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "　児童心理学者のカーターは子育てをしながら、子供の心理を観察し続けていた。しかしその行動は次第に常軌を逸していく。そんなカーターを手助けする、双子の兄弟ケイン。一方、町では子供が行方不明になるという事件が起きていた。やがてカーターとケイン、そして彼らの父親にまつわる過去が明らかになっていくが……。多重人格を扱ったサイコ・サスペンス。デ・パルマ監督のトリッキーな演出が冴え、さらにジョン・リスゴーも怪演で迫る。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Scream 2", "スクリーム2", "", "", "http://ecx.images-amazon.com/images/I/51w6-CAMRLL._SL210_.jpg", 122, "恐るべき連続殺人事件から2年が経過。シドニーは女子大生になり、新たな生活を始めていた。一方、レポーターのゲイルは事件を本にまとめて出版。それはハリウッドで映画化されるが、あのマスクをかぶった若者たちで満員の試写会場でまたも殺人事件が。今度の犯人は誰だ？　監督クレイブン＆脚本ウィリアムソンが再び組んだ、恐ろしくもユーモラスな大ヒット・ホラーの続編。前作の生き残り組に、新キャストが参加。", "1998-08-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUCH", "Touch〈タッチ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "多くの映画人にも愛される人気作家E・レナードの小説を、P・シュレーダー監督が曲者のキャストをそろえて映画化したファンタジー。人の心を癒す力を持つハンサムな青年ジュヴナル。アル中患者のリハビリ・センターで働く彼は、音楽プロデューサーのリンと恋に落ちるが、その不思議な能力に目をつけたマスコミや詐欺師の思惑に巻き込まれていく。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES MISTONS", "あこがれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 26, "南フランスの田舎町。美しく官能的な娘ベルナデットは少年たちの憧れの的。彼らは木々の中を自転車でさっそうと駆け抜ける彼女を見つめ追いかける。ある日、恋人ジェラールとキスする彼女を見た少年たちは二人の仲に嫉妬する。ジェラールが登山し、彼らはいたずらの絵葉書を彼女に郵送。が、ジュラールは遭難し子供たちの遊びは終わらざるをえない。激しい論調で古い映画を批判し“フランス映画の墓掘り人”と呼ばれた若きトリュフォーが16ミリ習作「ある訪問」(1955)に続いて製作した中編。実質的デビュー作で、スカートをなびかせ自転車を走らせるラフォンの初々しいイメージや子供たちのいたずらぶりは、後のトリュフォー映画につながる。DVDには「大人は判ってくれない」が併録されている。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jane Eyre", "ジェーン・エア（2011）", "", "", "http://image.eiga.k-img.com/images/movie/57658/315x210.jpg?1685", 120, "これまでにも繰り返し映像化されてきたシャーロット・ブロンテの不朽の名作を、「アリス・イン・ワンダーランド」「永遠の僕たち」のミア・ワシコウスカ主演で映画化。監督は、長編デビュー作「闇の列車、光の旅」（2009）が高い評価を受けた日系米国人のキャリー・ジョージ・フクナガ。悲惨な子ども時代を過ごした孤児のジェーン・エアは、教師の資格を取り、ソーンフィールドという屋敷で住み込みの家庭教師の職を得る。晴れて新しい生活を手したジェーンは、屋敷の主人ロンチェスターと恋に落ちていくが、ロンチェスターにはある秘密があった……。ロンチェスター役に「X-MEN：ファースト・ジェネレーション」「SHAME シェイム」のマイケル・ファスベンダー。", "2012-06-02", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狂った果実", "狂った果実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "裕次郎の主演第１作、石原慎太郎が脚本、中平康があふれんばかりの鬼才ぶりを発揮して演出した記念すべき映画だ。“太陽族”と呼ばれた、暇をもてあましたかのような金持ちの子供たち。その兄弟と、一人の魅惑的な女をめぐり破滅的な物語が展開する。遊び慣れした兄に比べて純真な弟が、ある女と恋に落ちた。兄は女が米軍将校のオンリーであることを嗅ぎ付け、弟を想う気持ち半分と嫉妬半分で女を無理矢理自分のモノにする。弟を愛しつつも、力強い兄の肉体に惹かれていく女。ついに真実を知った弟は、兄と女を乗せたヨットを捜し、復讐の一打を与えるべく、夜の海にモーターボートを疾走させる……。天才的なまでのカットつなぎのセンスとスピードが素晴らしく、それが批評家時代のフランソワ・トリュフォーを熱狂させ、彼の推薦によって、この作品はシネマテークに保管される日本映画の第１号となった。“いい映画ができるための必要条件は存分な時間とフィルムではない。「狂った果実」は17日間で撮影された”という意味の言葉を、トリュフォーは若い監督への戒めとして語っている。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君が踊る、夏", "君が踊る、夏", "", "", "http://image.eiga.k-img.com/images/movie/54898/315x210.jpg?1441", 123, "プロカメラマンになることを夢見て上京した新平は、母の入院の知らせを受け、5年ぶりに故郷の高知へ帰省する。そこでは難病を患う元恋人・香織の妹さくらが、「よさこい祭りで新平と踊る」という5年前の約束を胸に、つらい闘病生活を送っていた。新平はさくらとの約束を果たすため、香織や親友の司を巻き込み、伝説のよさこいチーム「いちむじん」を再結成する。「デコトラの鷲」シリーズを手がける香月秀之監督が、実話を基によさこい祭りに情熱を燃やす若者たちの姿を描く青春ドラマ。主演に「赤い糸」の溝端淳平。", "2010-09-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("100発100中", "100発100中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "作家の都筑道夫と岡本監督が共同でシナリオを執筆、東宝娯楽路線の一翼、福田が演出した明朗アクション。国際秘密警察官、アンドリュー星野が香港で殺される。星野は生前、飛行機に乗り合わせた男にパスポートを盗まれ、その男は星野になりすましていた。以来、ニセ星野はプラスティック爆弾を東南アジアで売りさばく美女の殺し屋ユミに案内され、100挺の銃をめぐる国際密輸事件に巻き込まれていく。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢で逢いましょ", "夢で逢いましょ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "銀座にある“河辺プロ”という芸能プロダクションを舞台とした、芸能界の他愛ない裏側を描く青春歌謡映画。“河辺プロ”とはモチロン“渡辺プロ”のもじりである。梓みちよ、田辺靖雄、鹿内タカシ、水原弘、スマイリー小原といった懐しの面々がズラリと登場し、楽しませてくれる。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED CHERRY３", "赤いさくらんぼ３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "大胆な性描写の「新・赤いさくらんぼ」の舞台を現代に変え、さらに激しいシーンを盛り込んだのが本作。ある青年が、社長宅に押し入り夫人をレイプ。以来二人は関係を続け、やがて社長を殺害する。そして彼らはハネムーンへと旅立つが……。", "1990-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("智恵子抄", "智恵子抄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "詩人の高村は智恵子との出会いで、過去のすさんだ生活を清算し、生きる喜びを見出していく。しかし、二人の美しい愛の暮らしは長くは続かず、智恵子の精神は少しずつ狂い始める。原作の高村光太郎が病死して１年あまりの映画化で、話題を集めた。原節子が難しい役柄を好演している。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A NIGHTMARE ON ELM STREET5: THE DREAM CHILD", "エルム街の悪夢5　　ザ・ドリームチャイルド", "", "", "http://ecx.images-amazon.com/images/I/51HNK7TACZL._SL210_.jpg", 90, "第４作でフレディを倒した“ドリームマスター”少女アリスの妊娠を利用して、この世に再び生まれ落ちようとするフレディのたくらみを描く第５作。彼はアリスのお腹の中の胎児の夢に侵入し、赤ん坊を乗っ取ろうとするのだ。今回の特殊メイクは「ターミネーター」「コクーン」のデヴィッド・ミラーが担当。", "1990-05-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beyond Therapy", "ニューヨーカーの青い鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "　雑誌の恋人募集広告を通じ、フランス料理店レ・ブーションで出会ったブルースとプルーダンス。ところがブルースは、実はバイセクシャルで、ボブという男の恋人と同棲していた。それを聞いたプルーダンスとブルースのデートは散々なものに。その後、ふたりは再会を果たすことになるが……。風変わりな男女、そしてそのセラピストといったニューヨーカーたちが繰り広げる恋の騒動を描いたスクリューボール・コメディ。大評判となった同名戯曲を映画化。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メロデ", "メロデ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1995年に「冬の河童」を発表した風間志織が、22歳の時に発表したインディーズの秀作。高校時代からの友人“あきゃ”が実家に帰ったため、恋人からの手紙を受け取り彼女に届けている“ひーちゃん”。そんな二人の女の前に、関西人“くん”が現れ、３人は何となく離れられない関係となるのだった……。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECRET OF MY SUCCESS", "摩天楼〈ニューヨーク〉はバラ色に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "田舎からニューヨークにやって来た一人の若者が、大企業の中であの手この手を使ってトップに登り詰めていく青春サクセス・ストーリー。全編に良質なコミカル・シーンがあふれ、またM・J・フォックスの演技も小気味よく、なかなかシャレた味わいの作品に仕上がっている。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HA¨XAN", "魔女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "“これを作ったのは天才か狂人だ！”と観た者を興奮させ、カトリック教会を激怒させた問題作。“悪魔や魔術とは何かを扱った文化史的論説”と監督自らが語るフィルム・エッセンスでありながら、その内容はファンタスティックの一語につきる想像力ほとばしるもので、凡百のSFやホラーは束になってもこの白黒無声映画にかなわないだろう。前半はナレーションによって中世の悪魔観が語られ、後半はドラマ仕立てで悪魔や魔女が人間をたぶらかしたり、魔女狩りや宗教裁判が行われる様が描かれていく。どのシーンも厳密な時代考証に基づいたリアリズムあふれるもので、それだけに悪魔の饗宴や拷問の場面には異様な迫力がある。この作品が認められてハリウッドに招かれた監督のB・クリステンセンはもともとオペラ歌手兼俳優で、全編にわたり悪魔に扮して怪演を見せている。あまり上映される機会のない作品だが、映画ファンなら一度は目にしておきたい知られざる傑作だ。", "1922-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pirates of the Caribbean: The Curse of the Black Pearl", "パイレーツ・オブ・カリビアン　呪われた海賊たち", "", "", "http://ecx.images-amazon.com/images/I/512Q7dz16UL._SL210_.jpg", 143, "「カントリー・ベアーズ」に続くディズニーランドのアトラクションの映画化企画第2弾は「カリブの海賊」。月光の下では生きた白骨と化す呪われた海賊たち。その呪いを解く鍵となるペンダントを持つのは、総督の娘エリザベス。彼女が海賊達に連れ去られ、彼女に恋する青年ウィルは、海賊スパロウ船長に協力を仰いで、彼女の救出に向かう。監督はハリウッド版「ザ・リング」のゴア・ヴァービンスキー。ガイコツの海賊たちと俳優たちが剣で戦う戦闘シーンはILMが担当。", "2003-08-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pink Panther", "ピンクパンサー", "", "", "http://image.eiga.k-img.com/images/movie/1598/315x210.jpg?3236", 93, "ブレイク・エドワーズ監督＆ピーター・セラーズ主演の同名人気シリーズをスティーブ・マーティン主演でリメイク。監督は「ジャスト・マリッジ」のショーン・レビ。サッカーのフランス代表チームの監督が何者かに殺害され、彼が身につけていた高価なダイヤの指輪“ピンクパンサー”が盗まれる事件が発生。クルーゾー警部が捜査に向かう。クライブ・オーウェンと「トランスポーター」のジェイソン・ステイサムがカメオ出演。", "2006-05-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅芝居行進曲", "旅芝居行進曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "自ら旅回りの劇団を率いて活躍する、沢竜二の原案をもとに、九州を本拠地に旅回りを続ける一座の、泣き笑い人生を描いた人情喜劇。25歳の青年座長に古尾谷雅人、九州一のドサ回り一座の美女に阿木燿子が扮し、それぞれ熱演。各地の座長も総出で友情出演。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOY’S TOWN", "少年の町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "日本にも来日したことのあるフランガン神父の伝記映画。フランガンは、殺人犯が死刑を執行される直前に“自分が幼少時代に人の愛を知っていたら正道を歩いたろう”という告白を聞き、家なき少年を救うことに一生を捧げる。彼は僧正と親友の財政援助を受け、孤児院“少年の町”を設立して彼らの世話をする。初めは経営困難だった事業も次第に軌道に乗りだすが、“少年の町”の子供が銀行襲撃事件に巻き込まれたことから、神父の事業は困難に直面する。トレーシーは「老人と海」に続いてアカデミー主演男優賞を連続受賞。1941年に同じスタッフによる続編「感激の町」が作られている。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Biutiful", "BIUTIFUL ビューティフル", "", "", "http://image.eiga.k-img.com/images/movie/55743/315x210.jpg?1516", 148, "スペイン、バルセロナに暮らすウスバルは、2人の愛する子どもと情緒不安定でドラッグに頼る妻を養うため、麻薬取引や移民の不法労働の手配など、違法な仕事にも手を出してきた。そんなある日、自身が末期ガンにおかされていることを知ったウスバルは、家族にその事実を隠し通そうとするが……。「バベル」のアレハンドロ・ゴンサレス・イニャリトゥ監督作。ハビエル・バルデムが第83回米アカデミー主演男優賞にノミネートされた人間ドラマ。", "2011-06-25", "9,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METRO", "ネゴシエーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "E・マーフィが笑いの要素を捨てて挑んだハード・アクション。彼が演じるのはサンフランシスコの名物刑事スコット。人質をとった犯人と直接渡り合う交渉人（ネゴシエーター）として抜群の成績を上げていた彼の前に、コーダという殺人者が現れた。刑事を惨殺し、市内を逃げまくったあげくにスコットに逮捕されたコーダは彼への復讐を果たすために脱獄する。コーダに恋人を人質に取られたスコットの活躍が、手に汗握るシーンの連続で描かれていく。「デッドフォール」などの脚本家R・フェルドマンによるひねりの効いたシナリオも物語を盛り上げる。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー 8人ライダーVS銀河王", "仮面ライダー 8人ライダーVS銀河王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "特撮テレビシリーズ「仮面ライダー（スカイライダー）」の劇場版。宇宙征服を狙う異星人・銀河王が、水素エネルギーを開発した宇宙ステーションを襲撃。開発責任者の羅門博士は、愛犬エレナに水素エネルギーの方程式を覚えさせて脱出させる。エレナを追って地球へやって来た銀河王は、ネオショッカーと手を組んで行動を開始。スカイライダーは歴代7人の仮面ライダーと共に悪に立ちむかう。原作者の石ノ森章太郎が総監督として参加。", "1980-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT LIVES AGAIN", "悪魔の赤ちゃん２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "グロテスクな赤ちゃんが巻き起こす恐怖を描いた「悪魔の赤ちゃん」の続編。生まれたばかりの赤ちゃんが突如、医師と看護婦を殺害し、姿を消してしまうという奇妙な事件が発生。やがて、その子供が悪魔の申し子だということが判明し……。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BASURA バスーラ", "BASURA バスーラ", "", "", "http://image.eiga.k-img.com/images/movie/54573/315x210.jpg?2029", 105, "「忘れられた子供たち　スカベンジャー」「神の子たち」でフィリピンの貧しい子供たちの実態を見つめてきたドキュメンタリー作家・四ノ宮浩が、三度フィリピンを訪れ、スラム街・スモーキーマウンテンに暮らす子供たちをカメラに収めたドキュメンタリー。前作から8年が経過したマニラでは、以前と変わらず巨大なゴミ山でゴミを集めて生活する人々の現実に直面する。第1作に登場した当時16歳だったクリスティーナも今では5児の母となっていたが、子供は伝染病に、自身は結核に苦しんでいた……。", "2009-06-27", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼのぼの", "ぼのぼの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "原作者自らがメガホンを執った、人気マンガの長編アニメ化。ラッコの子供を中心に、森の世界の悲喜こもごもを描く内容だが、原作がそうであるように、動物キャラクターの可愛らしさより、自然の摂理や生きることの哲学が詰まった“大人向け”のアニメに仕上がっている。見たこともない大きな動物がやって来る！　そんな怪情報が、ぼのぼのの暮らす森の中に広まっていた。映画は、未知の存在を見に行ったぼのぼのとアライグマ、シマリスらの冒険と、宿命のライバルであるヒグマとスナドリネコの決闘を並行して描く。まるで禅問答のように繰り広げられる会話の数々。さらには、あくまでも二次元にこだわった画面構成に、いがらしみきおの才気と野心、“マンガの映像化”の可能性が示されている。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Confucius", "孔子の教え", "", "", "http://image.eiga.k-img.com/images/movie/56773/315x210.jpg?1782", 125, "中国・春秋時代の思想家・孔子の半生をチョウ・ユンファ主演で描いた歴史ドラマ。乱世を憂う孔子は新時代を築くため、小国・魯（ろ）の中で古い慣習を撤廃するなど改革をすすめて頭角を現す。しかし、やがてさまざまな政治的陰謀や障害に阻まれた孔子は、己の思想を実現すべく多くの弟子を率いて14年間に及ぶ諸国を巡る旅に出る。共演に「ウィンター・ソング」のジョウ・シュン。「愛にかける橋」の女性監督フー・メイがメガホンをとる。", "2011-11-12", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bandidas", "バンディダス", "", "", "http://ecx.images-amazon.com/images/I/51Kl24AYrWL._SL210_.jpg", 93, "ペネロペ・クルス＆サルマ・ハエックという2人のラテン系美人女優競演によるアクション西部劇。1848年のメキシコ。鉄道建設のためニューヨーク銀行が雇った殺し屋ジャクソンに、それぞれ父親を殺された農民の娘マリアと銀行頭取の娘サラ。2人は銀行に復讐するため、そして民衆を救うために銀行強盗になることを決意。元銀行強盗のビルや刑事クエンティンも巻き込んで、次々と襲撃を成功させていくが……。", "2006-01-01", "2,19,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("МАТЬ И СЫН／MUTTER UND SOHN", "マザー、サン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "ミニマルな映画表現を追求するロシアの鬼才A・ソクーロフが、二人きりの登場人物だけで描ききった愛の世界。人里離れた海辺の森で暮らす母と息子の青年。病気を患って死の訪れを感じている母を連れて、青年は美しい自然の中を散策していく……。草原を走る列車、海に浮かぶ白い帆船、樹木のざわめきなどを、特殊レンズによって平面的に捉えた映像は、まさしく“絵画”を思わせるもの。またソクーロフは、イメージを触発された画家として19世紀ドイツのカスパー・デヴィッド・フリードリヒの名を挙げている。", "1997-01-01", "7,24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("流れ板七人", "流れ板七人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "流れ板とは自分の腕前に意地と誇りを懸けて全国を渡り歩く料理職人のこと。元流れ板だった竜二は今は岡山で料理屋を営んでいたがある日、東京の老舗料亭・閑日楼の花板、精蔵からの手紙を受け取り上京する。しかし、精蔵が急死したこと、閑日楼が関西の料理人、鉾田に買収されそうになっているのを聞いた竜二は……。竜二と鉾田の料理対決をクライマックスに、料理に命を懸けた人間たちのドラマが繰り広げられていく。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERO AT LARGE", "ニューヨークの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ブロードウェイの近くの安アパートに住み、昼はオーディションを受け、夜はアルバイトに精を出す、俳優志望の人の良い青年スティーブと、美人でCM作りのスタッフとして働いているマーシャ。まったく異質の世界に生きている二人が、ふとしたことで知り合い、お互いの心の中に淡い恋心が芽生えるのだが……。さわやかで、甘くて、ちょっとホロリとさせられるニューヨークの恋人たちの愛の物語。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悲しいボーイフレンド", "悲しいボーイフレンド", "", "", "http://image.eiga.k-img.com/images/movie/54133/315x210.jpg?2307", 87, "映像（Cinema）と音楽（Music）のコラボレーションを目指す「CineMusica（シネムジカ）」シリーズ第6弾で、歌手・渡辺美里の同タイトルの曲にインスパイアされた大人の初恋物語。製薬会社に勤める37歳独身の岩津は、部下と上司に挟まれながら日々の仕事に追われている。そんな岩津の携帯電話に、ある日突然「卒業アルバムを見た」と謎の少女から電話が入り、彼女の強引な誘いに流されるまま、故郷の神戸へと向かうことになる。主演は「相棒」の寺脇康文、監督は「ブラブラバンバン」の草野陽花。", "2009-02-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私をみつめて", "私をみつめて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "記録映画の名手として知られる原一男がプロデューサーを務めた社会派ドキュメンタリー。まだ20代の新鋭、木村茂之監督が10年以上続くひきこもりから決別を図ろうとする大阪在住の女性、河合由美子の姿を追う。敢えてひきこもりの原因と対峙し、社会復帰を目指す彼女の姿が胸を打つ。合計10年以上ひきこもり生活を続けている由美子。自らを見つめ直し始めた彼女は、10年間顔を合わせていない父親や親友と会う。", "2005-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダイアモンドは傷つかない", "ダイアモンドは傷つかない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "予備校に通うヒロインが、大人の世界に憧れて妻と愛人を持つ中年講師の“第２の愛人”となるが、やがて自分自身の世界が別のところにあることに目覚めていく、という女性の自立をテーマにした作品。「スローなブギにしてくれ」に続き、山崎努が中年男を好演。田中美佐子のヌード・シーンも。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KITCHEN TOTO", "キッチン・トト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "独立戦争が台頭する1950年のケニア。白人家庭に下働きとして住み込んだ黒人少年は、その家の息子と意気投合、深い友情が芽ばえるが……。民族意識と友情の板ばさみで苦しむ少年をナイーブに描写。1987年東京国際映画祭ヤング・シネマ部門でさくらゴールド賞受賞。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOMEWARD BOUND: THE INCREDIBLE JOURNEY", "奇跡の旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "1963年の動物映画「三匹荒野を行く」のリメイク。母親が再婚することになり、３兄弟がかわいがっていたペット達は遠く離れた友人宅に預けられることに。老犬シャドウとブルドックのチャンス、そして気取り屋の猫サシーは、離れ離れになってしまった飼い主に再会するため、波乱の大冒険を繰り広げる。動物たちの声を演じるM・J・フォックス、オスカー俳優のD・アメチーとS・フィールドが、絶妙の掛け合いを披露。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無宿", "無宿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "前年度キネマ旬報ベストワンに輝いた「津軽じょんがら節」を観て感動した勝新太郎が、勝プロに斎藤耕一を招き、ロベール・アンリコ監督の「冒険者たち」をモチーフに作った作品。ヤクザ渡世に生きる高倉健演じる男と、宝探しを夢想する男の友情を、美しい自然をバックに描いた。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("多羅尾伴内・鬼面村の惨劇", "多羅尾伴内・鬼面村の惨劇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "クリーン・ヒットの第１作を受けて製作された２作目。信州赤石山脈のある村の豪農・雨宮家で次々に連続殺人が起こり、名探偵・多羅尾伴内が、事件の解明に乗り出す。オドロオドロしい設定は、旭のカラッとした個性を殺してしまい、凡作に終わった。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Happy Feet", "ハッピーフィート", "", "", "http://image.eiga.k-img.com/images/movie/33883/315x210.jpg?2342", 108, "南極を舞台にペンギンたちが歌って踊るミュージカルCGアニメ。歌でハートを伝えるペンギンの国に生まれたマンブルは、歌うことが大の苦手。その代わり、彼には天才的なダンスの才能があった。そんなマンブルを快く思わない長老たちは、彼の行為が国に災いを及ぼしたと決めつけ、彼を追放処分にしてしまう。主人公マンブルの声をイライジャ・ウッドが演じるほか、ニコール・キッドマン、ヒュー・ジャックマンら豪華声優陣が美声を披露。", "2007-03-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Des Hommes Et Des Dieux", "神々と男たち", "", "", "http://image.eiga.k-img.com/images/movie/55923/315x210.jpg?1585", 123, "1996年のアルジェリアで、7人のフランス人修道士がイスラム原理主義者とみられる武装グループにより誘拐・殺害された実在の事件を題材にしたヒューマンドラマ。第63回カンヌ国際映画祭審査員特別グランプリを獲得した。アルジェリア山間部にたたずむ僧院で、フランス人修道士たちは地元のイスラム教徒たちと宗派を越えた交流をしながら、平穏な毎日をおくっていた。しかし、アルジェリア軍と原理主義者による内戦が激化したことから、彼らの周囲にも暴力の影が忍び寄り始める。", "2011-03-05", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sweetest Thing", "クリスティーナの好きなコト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "キャメロン・ディアスがコメディ・モード全開で笑わせる下ネタ満載のラブ・コメディ。カジュアルな男関係を満喫してきた28歳のクリスティーナは本気の恋に臆病気味。そんな彼女の前に、運命の男ピーターが現れる。彼女の本気心にピンときた親友コートニーは、話をまとめようとするが、クリスティーナは素直になれず……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DAY OF THE DOLPHIN", "イルカの日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "イルカに人間の言葉を理解させようと研究する海洋動物学者。大統領暗殺をたくらむ機関がこのイルカに目をつけた。陰謀に気付いた博士は……。美しいフロリダの自然の中でサスペンスあふれるドラマが展開する。舌足らずの声で話すイルカの純粋さが印象的だ。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRINCE OF TIDES", "サウス・キャロライナ 愛と追憶の彼方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "双子の姉の自殺未遂の報を聞いて、サウス・キャロライナからニューヨークへ駆けつけたトムを、姉の掛かりつけの精神科医のスーザンが出迎えた。姉に取り憑いた狂気の根は何か？ 美しき女医は、トムの口述を介して一族の秘められた過去へ踏み込んでいく。ストライサンドが女医スーザンをしなやかに演じながら長大な原作を見事に映画化、サスペンスの要素も加わった見ごたえのある作品に仕上げた。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・ドリフターズのカモだ!!御用だ!!", "ザ・ドリフターズのカモだ!!御用だ!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "井狩長吉は20年もヒラ刑事生活を送るダメ男だが、チンピラのヒデオを弟のように可愛がっている。ある日宝石強盗事件が発生、ヒデオが宝石を犯人から横取りしてしまう。折りも折、ヒデオの姉・きん子が上京し、長吉はまたしても一目惚れ……。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あさってDANCE（2005）", "あさってDANCE（2005）", "", "", "http://ecx.images-amazon.com/images/I/514PPWN4HSL._SL210_.jpg", 0, "人気漫画家・山本直樹の同名コミックを、「プッシーキャット大作戦」の本田隆一監督のメガホンで実写化したエロティックラブコメディ。市民劇団に所属する貧乏大学生・寺山スエキチは、ひょんなことから亡き祖父の莫大な遺産を相続することに。ところが時を同じくして、謎の美女・日々野綾につきまとわれるようになり……。主人公スエキチに扮するのは、お笑いコンビ「ハイキングウォーキング」の松田洋昌。相方の鈴木Q太郎も特別出演する。", "2005-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONSPIRACY", "トラブル・シューター 過去を消す男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "少女のような娼婦をモーテルに呼んでは、肉欲にふける政府高官。彼にはかつて、関係を持ちながらも捨てて行き、自殺にまで追い込んだ女がいた。その報いか、彼のまわりで次々とミステリアスな出来事が起こり始め、事件解決のため、通称トラブルシューターと呼ばれる男が現れる。オカルト的味わいを合わせ持つ、財界スキャンダル映画。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dead", "ゾンビ大陸　アフリカン", "", "", "http://image.eiga.k-img.com/images/movie/57543/315x210.jpg?4590", 105, "アフリカの大自然の中で過酷なサバイバルが繰り広げられるゾンビ映画。アフリカ大陸でゾンビが大量発生し、駐留アメリカ軍が撤退を始める。米軍エンジニアのブライアンも最後の飛行機で脱出しようとするが、機内で乗客がゾンビ化し、飛行機が墜落してしまう。同じ頃、故郷の村がゾンビ化してしまった西アフリカ軍の兵士デンベレは、生き残った息子を探しに出かけ、その途中で墜落した飛行機から生存したブライアンと遭遇。2人は協力してゾンビ大陸と化したアフリカ大陸からの脱出を計る。", "2012-01-21", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄金のパートナー", "黄金のパートナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "フリー・カメラマンの彼とヨットに居候を決め込んだ女、そして友人の白バイ警官の３人組が、南太平洋に沈んだ10億円の金塊を求めてヨットで旅立つ、というアドベンチャー・ミステリー。ヒロインをはさんでの男同士の友情などが、さわやかに描かれる。水中写真で有名な中村征夫が水中撮影を担当している。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("障害者イズム", "障害者イズム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "40歳を目前にした男女３人の身体障害者を追ったドキュメンタリー。重度の障害により施設で20年間暮らす小池公男さんと、脳性麻痺を抱えながらも障害者の自立支援グループの会長を務める日原一郎、車椅子生活の中込恵美らの自立に向けた2000日をカメラがつぶさに見つめていく。様々な困難や問題に直面しながらも自立を目指して奔走する３人の姿がさわやかな感動を呼ぶ。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古今大戦秦俑情／A TERRA−COTTA WARRIOR", "テラコッタ・ウォリア 秦傭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "紀元前、秦の始皇帝の時代。始皇帝にその力を認められながら、宮廷内で汚れなき童女を愛してしまったために生き埋めにされてしまった蒙天放。だが彼は、その童女から不老長寿の秘薬をもらっていたのだ。2000年後、恋人の生まれ変わりを追い求め、彼は過去からタイム・スリップしてくる……。香港、中国のパワーを結集したスペクタクル・ロマン。", "1989-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIDAY THE 13TH PART2", "13日の金曜日 PART2", "", "", "http://ecx.images-amazon.com/images/I/51uELVVHuzL._SL210_.jpg", 86, "人気ホラー・シリーズ第２作。前作のラストで衝撃的なデビュー(？)を飾ったジェイソンが、同じキャンプ場を舞台に本格的な殺りく行為を繰り広げる。ロープやチェーンを使っての殺し方にも趣向を凝らし、前作以上の惨劇がスクリーンいっぱいに展開。", "1981-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAD MONKEY", "禁断のつぼみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「ベル・エポック」のF・トルエバ監督によるラブ・サスペンス。米国人の脚本家ダンは、気鋭監督の妹ジェニーに惹かれて、その監督のシナリオを引き受けた。やがて筆が進まず苦悩するダンの前に、挑発的な眼差しのジェニーが現れる。ダンは罠の予感を感じつつも彼女の誘惑にのってしまう……。スペイン映画祭ゴヤ賞で、作品賞など６部門を受賞している。", "1989-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ladies Man", "レディース★マン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "アメリカの人気コメディ番組「サタデー・ナイト・ライブ」でティム・メドウス演じるキャラクター、レディース・マンを主人公にしたコメディ。女性たちから大人気のラジオDJレオンは、下品すぎるトークが災いし、美人プロデューサーのジュリーと共にクビになってしまう。どん底の暮らしを送っていたある日、レオンの元に昔の恋人のひとりから手紙が届く。その内容は、「一生困らないほどの富と幸福をくれる」というものだったが……。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泣かせるぜ", "泣かせるぜ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "石原裕次郎と浅丘ルリ子のコンビに、新人時代の渡哲也が絡んだムードアクション。不運な事故で一等航海士の職を辞した響伸作は、若い二等航海士の白石浩とともに、計画的に船を爆破し保持金を搾取しようとたくらむ悪玉一味をやっつける。正統派ムードアクションに属する作品だが、ストーリー展開がまどろこしく佳作とはいいがたい。「夕陽の丘」以来１年半ぶりにコンビを復活した裕次郎とルリ子も今一つ精彩を欠く。しかし特筆すべきはデビュー間もない渡哲也。暴風雨の中、甲板で裕次郎と激しく殴り合うシーンでは、裕次郎と伍して一歩も引かず、大スターの片鱗を見せた。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOFT ロフト", "LOFT ロフト", "", "", "http://image.eiga.k-img.com/images/movie/1503/315x210.jpg?2126", 115, "「カリスマ」「CURE　キュア」の黒沢清監督が「ドッペルゲンガー」以来3年ぶりに作り上げたサスペンス・ホラー。新作を執筆するために郊外の一軒家に引っ越してきた小説家の礼子は、向かいの家に住む男が得体の知れない物体を運んでいるところを目撃する。その後、その得体の知れない物体がミイラであることを知った礼子は、正体不明の悪夢に苛まれる。主演は「嫌われ松子の一生」の中谷美紀と豊川悦司。", "2006-08-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　スイートプリキュア♪　とりもどせ！心がつなぐ奇跡のメロディ♪", "映画　スイートプリキュア♪　とりもどせ！心がつなぐ奇跡のメロディ♪", "", "", "http://image.eiga.k-img.com/images/movie/56023/315x210.jpg?4405", 70, "人気アニメーション「プリキュア」の劇場版11作目で、2011年からTV放送を開始した新シリーズ「スイートプリキュア」の映画化。ある日、全世界の音や音楽を生み出しているメイジャーランドから音楽がなくなり、世界中から音楽が消えてしまう事件が発生。音楽を消し去った張本人はメイジャーランドの女王アフロディテという噂が広がり、響や奏らは音楽を取り戻すため奮闘する。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAR　FRANKIE", "Dearフランキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "スコットランドの港町を舞台に、耳の不自由な少年と、その母親の日常を温かい視点で描いた人間ドラマの秀作。父の面影を追う少年の胸の内や、過去の重荷を背負う母の心の痛みをリアルに綴った、女流監督ショーナ・オーバックの丹念な描写や、ユーモアを交えた豊潤な語り口が光る。耳の不自由な息子に、船乗りの父からと偽って手紙を送るリジー。ある日、船が寄港することになり、父親役を演じてくれる男を探すが……。", "2005-06-25", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUTPOST", "アウトポスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ホラーの名手W・クレイヴンが製作総指揮を務めた、アクション・スリラー。荒涼とした砂漠の地底深く設置されたアメリカ政府の超機密生化学研究所を退職した元研究員ストックトンは、緊急事態の連絡を受け、研究所へと駆けつける。しかし、研究員は立て続けに謎の死を遂げ、研究所はすでに壊滅していた。研究所では死んだ細胞を再生するウイルスで、史上最強の戦闘兵器を作る実験をしていた。どうやら、そこで蘇った恐るべき“物”が研究員を死に至らしめたらしい。しかも、それは本格的に活動を開始している。一刻の猶予もままならない。人類を恐怖に叩き込む“物”との死闘が始まった。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仔犬ダンの物語", "仔犬ダンの物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "「野菊の墓」「Wの悲劇」などで、松田聖子や薬師丸ひろ子をトップ女優へと育て上げたベテラン、澤井信一郎が“モーニング娘。”を主演に迎え撮り上げたアイドル・ムービー。実話をもとに、目の不自由な、仔犬ダンと少女達の成長の姿を描く。父の転勤で東京から田舎町にやってきた真生。彼女はある日、同じ学校に通う千香が牛乳を万引きするのを目撃する。千香は捨てられていた仔犬、ダンのために万引きをしたのだった。団地の規則で犬を飼えない千香の必死な姿に、真生や団地に住む仲間たちはダンの里親探しを始めるが……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天草四郎時貞", "天草四郎時貞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "松竹を退社した大島渚監督が東映で制作した初の時代劇。1637年、キリシタン弾圧に苦しむ天草と島原のキリスト教徒が起こした島原の乱と、その指導者・天草四郎の人間像を暗く重々しいトーンの中に描く。島原の乱に1960年安保闘争敗北後の民衆の心境をオーバーラップさせた、東映時代劇映画の中でも異色な作品。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アディクトの優劣感", "アディクトの優劣感", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "セックス、ドラッグ、酒、音楽に溺れる若者たちの日常を描いた池間了至の同名小説を、連続撮影した静止画像をアニメーション加工する新手法“デジタル・フォトメーション”により映像化。34歳の誕生日を目前に、自ら命を絶った由季宏。それから1カ月後、由季宏の恋人・玲加は、残されたパソコンの中に遺書を発見する。そこには、セックスやドラッグにのめりこんでヒノコという女にひかれていく、玲加の知らない由季宏の姿があった。", "2007-12-22", "76,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("USED PEOPLE", "迷子の大人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "夫に死なれたパールは葬儀の場でイタリア人ジョーから23年に及ぶ片思いを告白される。しかしパールは、離婚した二人の娘、ピントのずれた姑、精神科に通う孫などに囲まれて恋愛どころではない。ジョーは持ち前の明るさでパールの一家にとけ込もうとするが……。超豪華キャストで描くヒューマン・コメディ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・社長千一夜", "続・社長千一夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "天草にホテル・パーランドの建設が決まった庄司観光では外人誘致策にあれこれ苦慮していた。次は富士山麓にホテルを建てる計画だ。庄司社長は大ハッスルだったが、ふとしたことでかつてのなじみ芸者・和歌代と再会して浮気の虫が騒ぎ出した。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("K-19: The Widowmaker", "K-19", "", "", "http://image.eiga.k-img.com/images/movie/1463/315x210.jpg?2344", 138, "冷戦下の61年、偵察任務のため出航したソ連の原子力潜水艦K-19で放射能漏洩事故が起こる。潜水艦という閉鎖空間、極限状況の中で、威圧的な新任艦長ボストリコフ、経験豊富な副官ボレーニン、そして老若乗組員たちそれぞれの想いが次第に顕わになっていく。「ハートブルー」「ストレンジ・デイズ」などの女流アクション映画監督キャサスリン・ビグローが、実話を元に映画化。撮影は「ファイト・クラブ」のジェフ・クローネンウェス。", "2002-12-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRACTICAL　MAGIC", "プラクティカル・マジック", "", "", "http://ecx.images-amazon.com/images/I/212THJS4HRL._SL210_.jpg", 104, "代々実用的な魔術（プラクティカル・マジック）を使える能力を受け継ぐオーウェン家に生まれた美人姉妹サリーとジリアンは、不思議な能力の代わりに、彼女たちが愛した男性は若死にしてしまうことを知っていた。恋に臆病なサリーと対称的に、恋多き女ジリアンは家を飛び出し、恋人と暮らしている。ところがある日、ジリアンから連絡が入った。恋人が暴力をふるうというのだ。駆けつけたサリーは妹を助けるために思わず魔術を使って男を殺してしまう……。事件をきっかけに出会ったサリーと刑事ゲイリーの恋の行方を綴る。S・ブロック、N・キッドマンの人気女優共演のファンタジック・ラブ・ストーリー。", "1999-05-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMMENT　CA　VA？", "うまくいってる？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "製作から四半世紀を経て公開されたゴダールの政治色の濃い作品。公私にわたるパートナー、アンヌ・マリー・ミエヴィルとともに1974年に設立した映像実験工房で、ヴィデオ技術を活用して政治教育を試みた実験的作品だ。ヒッピー風の青年が、恋人とデートを重ね、やがて同棲を決意する。一方、彼の父親である共産党系新聞社の編集長が、共産主義者がどのようにして情報を作り出すかを示すため、ヴィデオ映画製作に乗り出す。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMANCHE！", "コマンチ族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "1875年、大酋長クアナの率いるコマンチ族が、メキシコからリオ・グランデの河を渡りアメリカに入る。アメリカ政府から和平交渉の依頼を受けたインディアンのリードはクアナと会って交渉をまとめるが、やがて裏切り者が出て……。アメリカ・インディアンのコマンチ族と騎兵隊の激闘を描いた西部劇。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DADDY", "DADDY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "現代造形美術家、ニキ・ド・サンファルの幻想的な原作をポップ・アート感覚で描いたアニメーション。ある日、アニエスは電報を受け取る。彼女の最愛の父が死んだ。彼女は幼年期に住んでいた城に戻り、そこで子供の頃に読んだ絵本を見つけ出す。この挿絵から彼女は父との様々な思い出を呼び覚まし、自分の過去と遭遇するのだった。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEPRECHAUN", "レプリコーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "全米でヒットしたホラー・ファンタジー。アイルランドの伝説にある悪の妖精レプリコーン。その妖精から、百枚の金貨を手に入れたダンは、アメリカに持ち帰る。だが、レプリコーンが、金貨奪回にやって来て……。「ウィロー」のW・デイヴィスが、残酷だが憎めない悪の妖精を演じた。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("True Justice: Russian Crossing", "沈黙の宿命 TRUE JUSTICE PART1", "", "", "http://image.eiga.k-img.com/images/movie/56403/315x210.jpg?2828", 90, "米シアトルを舞台に警察の精鋭チームの活躍を描くスティーブン・セガール主演の新シリーズ第1弾。警察の特別捜査隊（SIU）に所属するイライジャ・ケインは、4人の部下を率いて麻薬の密売ルートを追っていた。ある日、逮捕した売人トーマスの取り調べを開始するが、その矢先に郊外で殺人事件が発生。トーマスの取り調べもままならぬうちに、殺人事件の捜査に駆り出されるが……。", "2011-07-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chandramukhi", "ラジニカーント★チャンドラムキ　踊る！アメリカ帰りのゴーストバスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "「ムトゥ 踊るマハラジャ」が日本でも大ブームを巻き起こしたインドの国民的スター、ラジニカーント主演作。今回ラジニカーントが扮するのは、結婚する親友のためにアメリカから帰国した精神科医。親友が購入した屋敷には、150年前に非業の死を遂げた踊り子チャンドラムキの霊が封じられていた。親友の妻に取り憑いた霊との戦いを、ワイヤーアクションやCGを駆使しながら描く。歌あり、踊りありのエンターテインメント作品。", "2006-08-26", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAD ABOVE WATER", "真夏の出来事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "美しい孤島を舞台に、ある男の死体をめぐって繰り広げられるミステリー。エリート判事のジョーイとその美しい若妻ナタリーが、バカンスを過ごすため孤島の別荘にやって来た。ところがある日、一人で留守番をしていたナタリーの前に、彼女のかつての恋人ケントが現れる。ヨリを戻そうと迫り、泥酔して寝込んでしまった彼は翌朝死体となって転がっていた。あわてたナタリーは死体を隠すが、ジョーイに男物の腕時計やウォッカのボトルが見つかってしまい、すべてを打ち明ける。島の管理人をしている青年ランスの目を気にしながら、死体の処分に殺気立つジョーイに恐怖を感じるナタリー。やがて、彼女はランスの家でケントの来訪を告げるカードを発見、彼にも疑惑の目を向け始める……。それぞれの思惑が絡み合い二転三転するストーリーが、ブラックな笑いを交えてスピーディーに展開する。次第に凶暴になっていくジョーイ役をH・カイテルが怪演。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Quiz Show", "クイズ・ショウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "1950年代にアメリカのテレビ界を震撼させた実話を、監督としても評価の高い名優ロバート・レッドフォードが映画化。アメリカで社会現象まで巻き起こした大人気クイズ番組“21”。チャンピオンであるハービーは、その圧倒的な知識で次々と勝ち進んでいった。しかし視聴率低下を危ぶむスポンサーが、チャンピオンをもっと見栄えの良い人物に変更するよう番組側に要求。そこでプロデューサーは、ある計画を実行するのだが……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("淫紋", "淫紋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "“ピンクの巨匠”といわれた若松監督が若松プロを設立、最も脂の乗りきった頃の作品。戦時中の金沢の名家を舞台に、義弟と関係してしまった女がその子を妊娠、古い因習をかなぐり捨てて彼女が子供とともにたくましく生きていく姿を描く。“ピンク界のソフィア・ローレン”と呼ばれた内田高子の主演作。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はソレを我慢できない", "男はソレを我慢できない", "", "", "http://image.eiga.k-img.com/images/movie/33583/315x210.jpg?4587", 91, "音楽、演劇、ファッションなどのカルチャー発信地であり、現在大規模な都市開発計画で転機を迎える街・下北沢を舞台に描かれる人情喜劇。7年の放浪の末に下北沢の実家「饅頭屋うさや」に戻ってきたDJタイガー（竹中直人）が、片思い中のマドンナ（鈴木京香）にいいところを見せようと、ソープランド建設計画に立ち向かうのだが……。監督を務めるのは自身も下北沢に拠点を置く「代官山物語／探偵誕生」の信藤三雄。", "2006-07-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WITCHES OF EASTWICK", "イーストウィックの魔女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "「マッドマックス」シリーズでおなじみのG・ミラー監督による、奇想天外なセクシャル・コメディ。魔女伝説が生き続けているニューイングランドの小さな村に住むセクシーな熟女３人。美貌で申し分ないのに、なぜか男運の悪い彼女たちの前に、人間を装ったダンディーな悪魔が現れ、ちょっと危ない“四角関係”が始まる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続々大番 怒涛篇", "続々大番 怒涛篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "第２作が製作されてから１年たたないうちに作られた。前２作と同じスタッフが集まっている。昭和13年、借金だらけの丑之助は百姓になろうと故郷へ帰るが、ヤミ商売に手を出す。しかし、運命は再び彼を株の世界に引き戻す。豪華な配役陣も前作から引き続いて出演、映画はヒットし続けた。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NORMA JEAN AND MARILYN", "ノーマ・ジーンとマリリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ハリウッドの伝説的女優マリリン・モンローの没後30周年を機にTVムービーとして企画された伝記ドラマ。孤独な少女時代を過ごし、女優としての成功を夢見る娘ノーマが、金髪のマリリン・モンローとして成功を収めながらも、スターゆえの苦悩に見舞われる姿を描く。無名時代の本名ノーマ・ジーンをA・ジャッドが、芸名のモンローに生まれ変わってからをM・ソルヴィーノが“二人一役”で熱演。銀幕のヒロインの悲しみに満ちた素顔に迫っている。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あなた買います", "あなた買います", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "プロ野球の選手獲得をめぐるどす黒い人間の欲望を描く、当時の野球ブームを反映した実録的作品。大学野球の逸材をめぐって展開する激しいスカウト合戦。札ビラや根まわしが飛び交う中に、スカウトする側とされる側の醜い欲望が交錯する。スポーツの神聖さとは裏腹な資本主義構造が浮かび上がる。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鴉片戦争／THE OPIUM WAR", "阿片戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "中国の名匠シェ・チンが、私財までなげうって完成させた入魂の歴史大作。19世紀の半ば、清の時代。時の道光皇帝より特命全権大使に任命された林則徐が、イギリスからの密輸アヘンの取り締まりに乗り出す。しかし、これを自由貿易の侵害だとしたイギリス側は、大軍を清に派遣。林則徐は大使の職を解かれてしまう……。1997年にようやく中国に返還された香港が、いかにしてイギリス領になったかという事の顛末を、様々な資料へのリサーチを経て描出。清が没収した２万箱のアヘンが廃棄される中盤のシーンも史実に基づいたもので、3000人のエキストラを動員して撮影が行われた。", "1997-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APPUNTI SU〈LA CITTA｀ DELLE DONNE〉DI FEDERICO FELLINI", "フェリーニの都", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "フェリーニの「女の都」の撮影現場にカメラを持ち込み撮り上げたドキュメンタリー。主演のマストロヤンニやスタッフ、加えて2663人の女性エキストラたちにインタビュー、人間フェリーニへの接近を試みる。「女の都」が決してフェミニズム映画ではないことを暗示する発言が興味深い。", "1980-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON THE SILVER GLOBE", "シルバー・グローブ 銀の惑星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 160, "1976年にあまりにも衝撃的かつ哲学的内容のため、政府から撮影中止処分を受け、それから10年を経て完成したという、いわくつきの作品。とある惑星に不時着した宇宙船。生き残った３人の飛行士はそこで暮らすことに。やがて子供たちが生まれ、成長していく。そしてその子孫たちは、新たな文明に接するのだった。", "1987-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jungle Fever", "ジャングル・フィーバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "裕福な黒人の建築家フリッパーは妻子ある身。彼は秘書としてイタリア系女性のアンジーを雇うが、いつしかふたりは不倫関係に。やがてそのことを周囲の人々が知るが、友人たちからは冷たい視線を浴び、家族からは縁を切られ、隣人たちからは避けられるようになる。その後、フリッパーとアンジーはアパートで暮らし始めるが……。異人種間の恋愛とそれが生む波紋を描いた問題作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARD RAIN", "フラッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「アビス」「バックドラフト」などの撮影監督M・ソロモン監督、「スピード」のG・ヨスト脚本によるパニック・アクション。記録的な豪雨が続き、大洪水に見舞われたインディアナ州の田舎町。ほとんどの住民が避難したこの町で、セキュリティ・ポリスのトムは銀行の預金の輸送を始める。しかし、途中で強盗団に襲われ、トムは現金を持って逃亡。攻防戦を繰り広げる彼らを、やがてダム決壊の最悪の事態が襲いかかる。浸水した建物内でのジェットスキーを使ったバトルに、水面下に沈んだ牢獄からの脱出劇と、全編で繰り広げられる水上＆水中アクションが見もの。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緋牡丹盗賊", "緋牡丹盗賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "時は、五代将軍・綱吉の時代。“生類憐みの令”によって苦しめられる庶民のため、この悪法に立ち向かう緋牡丹盗賊という義賊がいた。彼はある時、将軍のご落胤の証拠となる書き付けの争奪戦に巻き込まれていく……。嵐寛寿郎が、義賊に扮してさっそうとした立ち回りを披露する、娯楽時代劇。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空気人形", "空気人形", "", "", "http://image.eiga.k-img.com/images/movie/54423/315x210.jpg?3801", 116, "女性の「代用品」として作られた空気人形ののぞみに、ある朝「心」が芽生え、持ち主の秀雄が留守の間に街へ繰り出すようになる。そんなある日、レンタルビデオ店で働く青年・純一に出会い、密かに想いを寄せるようになった彼女は、その店でアルバイトとして働くことになるが……。「誰も知らない」「歩いても　歩いても」の是枝裕和監督が、業田良家の短編漫画「ゴーダ哲学堂　空気人形」を映画化。主演は韓国の人気女優ペ・ドゥナ。", "2009-09-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLYING DOWN TO RIO", "空中レビュー時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "あるダンスバンドが成功を収めるまでを描く、ロマンティックなダンス映画。後にコンビを組むアステア＝ロジャースが初めて共演した作品だが、ドラマの上ではまだ助演的存在に甘んじている。名曲『カリオカ』が大ヒットし、ダンスが有名になったほか、双翼機の上にたくさんの踊り子をのせてレビューをするというアイデアが面白い。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zona Sur", "ボリビア南方の地区にて", "", "", "http://image.eiga.k-img.com/images/movie/54963/315x210.jpg?4584", 108, "裕福な家族とその忠実な使用人の関係を通し、南米ボリビア社会をリアルに切り取った家族ドラマ。ボリビアの首都ラパス南部の高級住宅地に佇む1軒の豪邸を舞台に、そこで暮らす母親と3人の子供たちが先住民族出身の使用人たちと織り成す日常とやがて訪れる転落を、斬新なカメラワークを用いて描いていく。本作が長編第4作となるボリビア人監督フアン・カルロス・バルディビアが監督を務める。", "2009-10-18", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRITICAL CONDITION", "S.O.S.ドクター・ノーグッド！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "サギ師のラナハンがカモにしようとした男はなんと悪名高い街の大ボス。恐ろしい連中に追いかけられるハメになったラナハンは、精神異常者のフリをして病院に逃げ込んだが、そこでもてんやわんやの大騒動を繰り広げる。プライヤー主演の人情味豊かな抱腹絶倒コメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PRIMA NOTTE DI QUIETE", "高校教師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "イタリアの田舎町リミニに赴任したしがない中年高校教師ダニエーレが、クラスの若く美しい娘バニーナと激しい恋に落ちる。妻を捨て、破れた人生を取り戻そうとするが……。美男子で売ってきたドロンが無精ヒゲの悩める中年役に挑み、渋い味わいを醸し出した。", "1972-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画版　月組・東京公演　ミュージカル　スカーレット・ピンパーネル", "映画版　月組・東京公演　ミュージカル　スカーレット・ピンパーネル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "宝塚歌劇を最新鋭のデジタル技術でとらえ、スクリーンで上映する「タカラヅカレビューシネマ」の第3弾。2008年に上演されて好評を博し、2010年に月組・霧矢大夢のトップお披露目公演として再演された「スカーレット・ピンパーネル」を映像化。18世紀フランスを舞台に、無実の罪で革命政府に囚われた貴族たちを救いだす秘密結社を率いるイギリス貴族と、秘密結社の壊滅を狙う革命政府が繰りひろげる駆け引きを描く。", "2010-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("８００　BALAS", "マカロニ・ウエスタン800発の銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "60年代に世界中で大ブームとなった“マカロニ・ウエスタン”にオマージュを捧げた痛快アクション活劇。今や“ウエスタン村”と化した以前の撮影地を舞台に、乗っ取りから同地を守る往年のガンマンたちの奮闘をコミカルに描く。現代スペインの内情を突いた、シニカルな視点も小気味良い。かつて西部劇映画の撮影地として栄華を築いたウエスタン村。ある日、その村を閉鎖し、テーマパークを建設する話が持ち上がり……。", "2005-10-15", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAGYAROK／HUNGARIANS", "ハンガリアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1942年冬、雪に閉ざされたハンガリーの小さな村から、ファビアンとイロンカの夫婦をはじめとする４組の夫婦と二人の独身男がドイツへと旅立つ。一年間の契約で同盟国ドイツの農村に出稼ぎに行くのである。彼らはこれまで一度もハンガリーの村から出たことはなく、今度のドイツ行きで初めて外の世界に触れたのだ。だが、時は第二次大戦のさなか、しかもドイツが敗戦への坂を転がり始めた頃だった。彼らが初めて目にする悲惨で過酷な戦争の現実は、やがて彼らの運命を巻き込んでいく。大国の動向にほんろうされてきたハンガリーの姿を純情な農民の姿に重ねながら、その運命をリアリスティックに描いたゾルタンの力作。", "1977-01-01", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAMLET", "ハムレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 243, "K・ブラナーが監督、脚色、主演の３役をこなして長年、映像化を熱望していたシェイクスピアの名作に挑んだ大作。凝った美術と豪華キャストによって絢爛たる映像絵巻が繰り広げられる。デンマークの国王が急死し、国王の弟クローディアスが即位する。しかし、王子ハムレットは国王の亡霊にすべてはクローディアスの陰謀だと告げられ、復讐を決意する。周囲に固く心を閉ざしてしまったハムレットの変ぼうぶりに心を痛める婚約者オフィーリア。そんな彼女を顧みず復讐心を募らせていくハムレットに、やがてさらなる悲劇が襲いかかる……。「タイタニック」のK・ウィンスレットが、精神を病んでいくヒロイン、オフィーリアを熱演。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Boy A", "BOY A", "", "", "http://image.eiga.k-img.com/images/movie/53963/315x210.jpg?2126", 107, "「ダブリン上等！」のジョン・クローリー監督と脚本家マーク・オロウが再びタッグを組み、ひとりの青年が抱える心の傷や希望、孤独を描いた人間ドラマ。主演は「大いなる陰謀」のアンドリュー・ガーフィールド、共演に「マイ・ネーム・イズ・ジョー」のピーター・ミュランら。過去のある理由からジャックという新しい名前で人生を再出発した24歳の青年。運送業に就き、仲間や恋人にも恵まれていたのだが、過去を偽る日々に苦悩していた。", "2008-11-15", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀座の恋の物語", "銀座の恋の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "石原裕次郎、浅丘ルリ子主演、蔵原惟繕監督によるメロドラマの秀作。新進の画家、伴次郎は恋人、秋山久子との結婚を決意するが、久子は交通事故に遭って記憶を喪失してしまい、伴次郎の前から姿を消す。数年後ようやく久子を探し当てた伴次郎は、久子の記憶を取り戻そうと必死に努力を続けるが……。山田信夫と熊井啓の脚本は、久子の記憶を取り戻すまでの過程に二重、三重のひねりをきかせてあり、脚本構成の巧みさは心憎いばかり。この作品は「赤いハンカチ」で花開く裕次郎とルリ子のコンビによる一連のムード・アクションの前哨戦的な作品としても、興味深いものがある。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL　MERCENARIO", "豹／ジャガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「続・荒野の用心棒」のS・コルブッチ監督とF・ネロのコンビ作。革命に揺れるメキシコを舞台に、“ジャガー”という異名を持つ男の闘いを描くマカロニ・ウエスタンだ。大富豪に銀を輸送する際の護衛を依頼された傭兵スワルスキー。しかし鉱山はすでにパコ率いる革命軍に占拠されていた。やむなくコワルスキーはパコの側につくが、そこにライバルの殺し屋カーレーが大軍を率いて現れる。", "1968-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血を吸う薔薇", "血を吸う薔薇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "八ヶ岳山麓にある女子学園を舞台に、東京から赴任して来た青年教師が、夜毎女子学生を襲い、その生血をすする吸血鬼と対決する。“血を吸う”シリーズの最終作だが、第２作で初登場した岸田森の吸血鬼がここでも迫力十分の演技をみせる。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("進め！ジャガーズ・敵前上陸", "進め！ジャガーズ・敵前上陸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ビートルズの劇映画に影響された和製ポップス映画。脚本の中原弓彦は、作家・小林信彦のペンネーム。「気狂いピエロ」のパロディーあり、硫黄島で元日本兵の生き残り（南道郎が巧演）が出てきたりと、お遊びがあって楽しい。物語は、ジャガーズのリーダー・岡本信が密輸事件に巻きこまれ、女殺し屋集団に狙われるが、ほかのメンバーとともに逆襲に出るというもの。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本一の断絶男", "日本一の断絶男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "一流会社への就職を夢見る青年・丸山と、そのおさな友だちミミのもとに現れたナゾの男・日本一郎は、丸山とミミをさんざんカモにしたあげく、自分はとんとん拍子の運の良さで大金持ちになっていく……。植木等が主役というよりは狂言回しにまわった作品。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shorts", "ショーツ　魔法の石大作戦", "", "", "http://ecx.images-amazon.com/images/I/61to9qKgFdL._SL210_.jpg", 89, "どんな願いでもかなえてくれる「魔法の石」をめぐって繰りひろげられる騒動を、「スパイキッズ」シリーズのロバート・ロドリゲスのメガホンで描いた奇想天外なファンタジックアドベンチャー。世界中で話題を集める携帯型ハイテク機器「ブラック・ボックス」の開発で有名なブラック・フォールズの町。大人たちが機器の改良に夢中になる一方、いじめられっ子の少年トビーは虹色に輝く不思議な石を手に入れる。", "2009-01-01", "1,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Streamers", "ストリーマーズ／若き兵士たちの物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "　ベトナム戦争のさなか。軍兵舎で前線行きを待つ若きアメリカ兵たちはナーバスになっていた。大学出の真面目なビリー、ホモであることを公言しているリッチー、そして陽気なロジャーでさえも。そんな彼らの間に、自分の職務に不満を抱く黒人兵カーライルが割り込んでくる。戦争に対する恐怖、そして偏見や差別といったことが軋轢を生み、やがて思わぬ惨劇が……。同名舞台劇を忠実に映画化。緊張感あふれる会話劇が兵舎内という限定空間で展開。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　空の境界　第五章　矛盾螺旋", "劇場版　空の境界　第五章　矛盾螺旋", "", "", "http://image.eiga.k-img.com/images/movie/53663/315x210.jpg?3055", 115, "全7章からなる奈須きのこの小説「空の境界」を原作に、万物の死を見ることのできる能力「直死の魔眼」を持つ少女・両儀式の戦いを、全7部作でアニメ映画化した人気シリーズの第5弾。人間という存在に絶望し、万物の始まりにして終焉である“根源”を求めて死の蒐集を続ける男・荒耶宗蓮。彼のかつてのライバルである蒼崎橙子は、螺旋建築へ誘い込まれた両儀式を助けに向かい、荒耶との宿命の対決を迎える。", "2008-08-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("省港旗兵／LONG ARM OF THE LAW", "省港旗兵／九龍の獅子　— クーロン・ソルジャー —", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "宝石強奪のために香港へ密入国した５人の若者たちの破滅的な生きざまを描く。九龍城で初めて大ロケーションを敢行、キャスティングはすべてオーディションで選ぶなど、香港映画界としては新しいアプローチが多く採用された画期的な作品である。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("城取り", "城取り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "石原裕次郎が製作した司馬遼太郎原作による時代劇の大作。戦国時代末期、上杉景勝の心意気に惚れて味方についた前田慶次郎が、敵の伊達政宗の領内に潜入して重要な軍事拠点である出城を奪取する。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君の名は（総集編）", "君の名は（総集編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 185, "戦前の「愛染かつら」と並ぶ“スレ違いメロドラマ”の代表作。原作は1952年からNHKで放送された菊田一夫のラジオドラマ。放送が始まると女湯がカラになるという伝説を生んだメロドラマだったが、映画は３部作として作られた。東京大空襲下の数寄屋橋で出会った男女が互いに名も明かさず、半年後の再会を約束して別れたが、様々な障害のためになかなか出会うことができず、女は心ならずも他の男と結婚してしまう。やがてその結婚は破局を迎えるが……。舞台は日本全国に広がり、主題歌とともに岸恵子が首に巻いた長いストールが“真知子巻き”として流行。主人公の名を冠したキャラクター・グッズもいろいろ売り出された。完全版のTAPE（３巻・378分）も出ている。DVDタイトルは「君の名は　DVD-BOX」。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もういちど逢いたくて・星月童話", "もういちど逢いたくて・星月童話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "人気俳優、故レスリー・チャンと常盤貴子の共演が話題となったロマンティック・ラブ・ストーリー。婚約者を事故で亡くした瞳は結婚後に住むはずだった香港を訪れ、彼とよく似た男ガーポウと出会う。そして翌日、ケガをしたガーポウが瞳のアパートに転がり込んでくる。麻薬捜査官の彼は潜入捜査中に銃で撃たれたのだ。必死に看病する瞳は回復した彼に今までのいきさつを話し、一日だけ恋人の代わりにデートしてほしいと頼む……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トラック野郎・天下御免", "トラック野郎・天下御免", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "舞台は四国。巡礼姿の美しきデザイナー・和歌子にホの字の桃次郎。母親の病と借金返済のために好きでもない相手との結婚を決意した彼女を助けるべく、桃次郎は全財産を宇和島の闘牛になげうつが……。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVERBOY", "バイバイ、ママ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "個性派俳優、ケヴィン・ベーコンが初監督に挑んで作り上げた親子ドラマ。いつからか溝のできたシングルマザーと息子の関係が描き出される。豪華ハリウッドスターと堂々と渡り合う子役、ドミニク・スコット・ケイの演技に注目だ。幼い頃、両親から十分な愛情を得られなかったシングルマザーのエミリーは、息子のポールを外部と一切接触させずに育てる。だが、その過度な愛情をポールが嫌うようになり……。", "2006-04-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FALSCHE BEWEGUNG", "まわり道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "小さな町に住む作家志望の青年ビルヘルム。物語を書きたいのに書けない彼は母のすすめで列車に乗る。なぜか一緒に旅をすることになる不思議な人々−−口をきかない少女、元ナチスの老人、女優、自称詩人。ビルヘルムは物語を書き始められるのか……。「都会のアリス」に続いてベンダースが放つ“ロード・ムービー3部作”の第2作。", "1977-01-31", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VIE DES MORTS", "二十歳の死", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "52分の中編ながら本国フランスで一般公開され成功を収め、各国の映画祭に招かれたA・デプレシャンの監督デビュー作。フランス北部の田舎町で散弾銃による自殺を試み、昏睡状態に陥った青年パスカル。そんな彼の家に集まった家族や同年代の親戚たちの群像劇が語られる。日常と非日常の間を不安げにさまよう若者たちの姿を、繊細な感性でスケッチした青春映画。後のデプレシャン作品の常連となる若き役者たちがみずみずしい魅力を放っている。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DES　PLUMES　DANS　LA　TE＾TE", "心の羽根", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ベルギーの新人監督T・ドゥティエールの長編映画デビュー作。最愛の息子を失った若い母親の、その死を受け入れて自分を取り戻していくまでの“心の再生”を、移ろいゆく美しい自然とともに綴る感動のドラマ。監督の実生活上のパートナーでもあるS・ミュズールが、ヒロインを好演している。ブランシェは、夫のジャンピエールと息子アルチュールの３人で幸せな暮らしを送っていた。しかし、ある日突然アルチュールが死んでしまう。息子を失ったショックから立ち直れないブランシェは、次第に心を病んでいき……。", "2003-01-01", "37,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr.Socrates", "Mr.ソクラテス", "", "", "http://image.eiga.k-img.com/images/movie/33763/315x210.jpg?4747", 111, "謎の組織によって拉致され、刑事としての教育を強要された不良青年ドンヒョク。やがて凶悪犯罪担当の刑事になった彼は、組織が企てる恐るべき陰謀を知らされる。組織や警察との駆け引きを繰り広げながら成長していく主人公の姿を描くポリス・アクション。「マイ・リトル・ブライド」やドラマ「ラブストーリー・イン・ハーバード」で大ブレイクしたキム・レウォンがこれまでの甘い笑顔のイメージから一転し、ワイルドな魅力を披露している。", "2006-12-02", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蘇州河", "ふたりの人魚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "中国第六世代にあたるロウ・イエ監督が、ロッテルダム国際映画祭のタイガー・アワード、TOKYO FILMeX 2000のグランプリに輝くなど、国際的な評価を得た恋愛映画。現代の上海を生きるビデオ撮影屋の男が捉えた物憂げな映像を通して、ある青年と少女の数奇な恋の軌跡が語られていく。青年と恋に落ちるあどけない女の子と、ビデオ屋の男が魅了される妖艶な水中ダンサーという対照的なキャラクターを一人二役で演じ、女性の神秘を表現した主演女優ジョウ・シュンも脚光を浴びた。", "2000-01-01", "8,7,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・第三の極道・ III", "新・第三の極道・ III", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "関西最大の組織・藤堂組のなかでもその指導力で、一目置かれている存在の二代目武狭会会長の正木礼二郎。彼の元には、正木のためには命を投げ出すことも惜しくない男たちがいた。彼らは“第三の極道”と呼ばれ、ふだんはそれぞれ別の職業に就いていた。折しも、関西では台湾マフィアが暴れ回っていたのだが、そのボスもアジトも分からなかった。そしてそのなかの謎の男が、正木の前に不気味な影を投げかけてくるのだった。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Black Flag Live", "BLACK FLAG LIVE", "", "", "http://ecx.images-amazon.com/images/I/41HXm3g3tYL._SL210_.jpg", 55, "", "2011-10-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AV", "AV", "", "", "http://image.eiga.k-img.com/images/movie/56373/315x210.jpg?2771", 104, "香港の鬼才パン・ホーチョンの監督第8作「ドリーム・ホーム」（2010）の公開を記念して、ホーチョン監督の日本未公開作を一挙上映する特集上映「パン・ホーチョン、お前は誰だ!?」で公開。将来に夢も希望もないまま卒業を控えた大学生たちが、ナンパ目的で始めた映画製作の模様を描く青春群像劇。ローレンス・チョウ、デレク・ツァンら若手俳優ほか、日本のAV女優・天宮まなみも出演。", "2011-06-11", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABES IN TOYLAND", "おもちゃの国のクリスマス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "吹雪のクリスマス・イヴ、一人で留守番をしていたリサ。彼女は、メアリー姉さんを迎えに行き、その帰り道、ひょんなことから“おもちゃの国”に紛れ込む。そこでリサが見るファンタジーあふれる世界が、楽しく描かれたハッピーな作品。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青葉のころ・よいお年を２", "青葉のころ・よいお年を２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "福祉施設“元気な亀さん”で暮らすお年寄りたちの生活を見つめた記録映画「よいお年を」の続編。今回は、そこで働く若いヘルパーたちに重点を置いて、その暮らしをとらえていく。施設で働くスタッフ22人のうち、20代の者は12人。“老い”に接した彼らの驚きや戸惑い、喜びなどの感情の揺れをビビッドに描きながら、老人介護のあり方を真摯に問いかける。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キディ・グレイド　劇場版　第一部：KIDDY GRADE IGNITION 覚醒篇", "キディ・グレイド　劇場版　第一部：KIDDY GRADE IGNITION 覚醒篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "人気SFアニメ「キディ・グレイド」全24話に新作カットを加えて再編集した3部作の第1弾。人類が宇宙進出してから数世紀。過去の記憶を失った少女エクレールは、銀河通商関税機構・GOTTの受付嬢として働きながら、パートナーのリュミエールと共に、経済紛争を実力で解決する超能力エージェント“ESメンバー”として活躍していた。さまざまな特殊任務を遂行していくうち、エクレールは少しずつ記憶を取り戻していくが……。", "2007-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATHER'S LITTLE DIVIDEND", "可愛い配当", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "S・トレーシー、E・テイラーの共演による「花嫁の父」の続編。監督は前作同様V・ミネリ。娘のケイに子供が生まれることを聞いたバンクス。時の流れの早さをいまさらながらに実感する彼だったが、娘たちの住宅問題や生まれてくる赤ん坊の名前など、細々したことでまたも大騒動が展開する、古き良き時代のファミリー・ドラマ。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISSISSIPPI ONE", "サラ・ムーンのミシシッピー・ワン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "母親と二人暮らしの大人びた少女アレクサンドラは、学校の帰り道に、精神病で入院していた男デビッドに誘拐される。だが、各地を点々とするうちに二人の心は通じ合い……。世界的なファッション・カメラマン、ムーンが独特の色調で繊細な世界を表現した美しい映像叙情詩。光と影の織り成す不思議な色彩が魅力的だ。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE TEMPS D’UN BALLET", "アダージェット モーリス・ベジャールの時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "現代バレエ界最高の振付師ベジャールと、彼の主宰するベルギー国立20世紀バレエ団を追ったドキュメンタリー。練習風景、リハーサル、舞台光景に加えて、ベジャールへのインタビューも挿入。彼の素顔とともに、その絶大なる肉体讃美の思想を映し出した興味深い作品。ドンの踊る『ボレロ』が圧巻。", "1981-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TILLIE’S PUNCTURED ROMANCE", "醜女の深情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "チャップリンを映画界入りさせた無声喜劇の大物プロデューサー、M・セネットが監督した中編コメディ。デビュー翌年ながらすでに32本の作品に出演、同時に監督もこなしてきたチャップリンが、珍しく演技のみに専念した作品。物語は結婚詐欺師に扮したチャーリーの失敗を描く、実に他愛のないもの。ビデオのタイトル表記は「醜女の深情け」。", "1914-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eye in the Sky", "天使の眼、野獣の街", "", "", "http://image.eiga.k-img.com/images/movie/54273/315x210.jpg?3581", 90, "「エレクション」「PTU」などのジョニー・トー作品で脚本を手がけてきたヤウ・ナイホイの監督デビュー作で、ジョニー・トーが製作を担当。凶悪犯罪の容疑者たちの監視と追跡を専門とする香港警察刑事情報課・監視班に、新人女性警官ホーが配属される。上官の厳しい指導の下で追跡のエキスパートへと成長していくホーだったが、大胆に犯行を行う宝石強盗グループが現れ……。トー作品の常連、レオン・カーフェイ、サイモン・ヤムらが出演。", "2009-01-31", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミヨちゃんのためなら全員集合!!", "ミヨちゃんのためなら全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "漢方薬製造工場を経営する伊刈長吉は、工場が悪臭をまき散らすと町民の非難をあびる。しかし、工場周辺の土地を買い占め、ひともうけをたくらむ町のボスの悪事を暴き、名誉を挽回する。デビュー当時の発剌とした倍賞美津子が魅力的。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Tournament", "ザ・トーナメント", "", "", "http://image.eiga.k-img.com/images/movie/57603/315x210.jpg?2587", 95, "殺し屋たちがロンドンの街を舞台にサバイバルを繰り広げる姿を描いたバイオレンスアクション。2012年、五輪開催に沸くロンドンで世界一の殺し屋を決める殺人競技会「トーナメント」が開幕する。世界中から集まった30人の参加者が体内に発信機を埋め込まれ、街中に仕込まれたカメラの監視下で次々と殺しあっていく。そんな中、アル中で気弱な神父のマカボイが街の酒場で殺し屋たちと同じ発信機を飲まされてしまい、壮絶な殺し合いに巻き込まれてしまう。マカボイ役のロバート・カーライルほか、アジア一の殺し屋ライライ役でケリー・フー、前回大会優勝者のジョシュア役でビング・レイムスらが出演。", "2012-02-18", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ありがとう　「奈緒ちゃん」自立への25年", "ありがとう　「奈緒ちゃん」自立への25年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "てんかんと知的ハンディキャップをあわせ持つ少女・奈緒ちゃんの成長を、25年という長期間にわたって追い続けたドキュメンタリー。長くは生きられないのではないかと心配されていた奈緒ちゃんが、家族や地域の人々に囲まれてすくすくと成長していき、同時に周囲の人々にも大きな影響を及ぼしていく様子を描いていく。監督は「ルーペ　カメラマン瀬川順一の眼」などのドキュメンタリー作家で、奈緒ちゃんの伯父でもある伊勢真一。", "2006-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Grain in Ear", "キムチを売る女", "", "", "http://image.eiga.k-img.com/images/movie/33823/315x210.jpg?4798", 109, "現代中国文学を代表する作家としても知られるチャン・リュル監督による長編第2作。キムチの露天で生計を立てる朝鮮族（中国籍韓国人）の母子の日常を透明感あふれる映像美で描き出し、カンヌ国際映画祭を始め世界各地の映画祭で多数の賞を獲得した。中国北部の田舎町で息子と2人つつましく暮らすスンヒは、同じ朝鮮族の男性キムと意気投合し恋仲に。しかし彼の裏切りにより、スンヒは売春容疑で警察に連行されてしまう……。", "2007-01-28", "5,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("[Rec]", "REC レック", "", "", "http://image.eiga.k-img.com/images/movie/53553/315x210.jpg?3258", 77, "テレビのドキュメンタリー番組を制作中の女性レポーター・アンヘラは、通報を受けた消防隊に同行してとあるアパートを訪れる。そこには血まみれになって立ちつくす1人の老婆がいた。老婆は駆けつけた警察官を突然襲いはじめ……。閉ざされたアパートの中で想像を絶する恐怖に見舞われる人々の姿を、手持ちカメラ目線によるドキュメンタリー・タッチの映像で描き出し、本国スペインで大ブームを巻き起こしたパニック・ホラー。", "2008-06-14", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／KURO ARIRANG", "九老アリラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "苦しい生活の中でそれぞれの夢を抱きながら、ソウルの九老工業団地の縫製工場で働く男女。そんな彼らの日常が、過酷な労働条件で働いた仲間の事故によって大きな変化の時を迎える。高度経済成長の背後に隠された労働問題を緻密なリサーチで検証し、俊英パク・ジョンウォンが抑制したリアリズム演出でまとめ上げた社会派ヒューマン・ドラマ。働く若い女たちのホロ苦い青春模様を、いきいきと描く。", "1989-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バカリズム THE MOVIE", "バカリズム THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/58073/315x210.jpg?1967", 90, "お笑い芸人のバカリズムがほぼ1人で監督、脚本、主演を手がけたオムニバス映画。特殊能力を持つ孤独な少年・魔スノの復讐劇を描く学園サスペンス「魔スノが来たりて口笛を吹く」、現代サラリーマン社会に切り込んだ社会派ドラマ「（株）Rock」、感動ラブストーリーの「トップアイドルと交際する事への考察」、メンコに命がけな少年の活劇を描くアニメーション作品「メンコバトラーM」、2009年にテレビ東京系で放送された特撮ドラマを復活させた「帰ってきたバカリズムマン！」の5作品で構成される。", "2012-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fugitive Kind", "蛇皮の服を着た男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "　アメリカ南部の田舎町に、蛇皮の服を着た若い男バルが流れ着く。彼は町の女性たちと関わりを持つことに。やがて雑貨店の女主人と深い仲になるが、それを知った彼女の夫が逆上して……。古い因習に縛られた町で繰り広げられる男女の愛憎劇。テネシー・ウィリアムズの戯曲「地獄のオルフェ」を映画化。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝ声なき友", "あゝ声なき友", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "寅さん役の喜劇演技が有名だった渥美清だが、泣かせてくれる感動的な名演も披露している。本作はそんな作品群の一つで、巨匠・今井正監督が丹念な演出で観客を泣きの世界へと連れていってくれる。主人公・西山民次が病気で入院中に軍隊の仲間12名の兵は全員戦死。さらに民次の家族は原爆で全員死亡してしまい、敗戦を迎える。彼は仲間の遺書を持って日本に引き揚げ、各遺族の元へ遺書を届けようとする。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市の歌が聞える", "座頭市の歌が聞える", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "座頭市は一の宮で、町の人々を苦しめている板鼻権造一家を叩きつぶし、浪人・黒部玄八郎と剣を合わせる。第１作の平手造酒役で名演技をみせた天知茂が、黒部玄八郎役で再び座頭市と対決。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無法松の一生", "無法松の一生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "阪妻主演による戦前の名作の再映画化。第１回作品が当時の検閲によってカットされたのを不満とした稲垣浩が、同じ伊丹脚本をカラー、ワイド・スクリーンで新たに監督した。無法松の三船、吉岡夫人の高峰が好演、名作の誉れ高い前作と比べても甲乙つけがたい作品だ。第19回ヴェネチア映画祭金獅子賞受賞。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THERE’S SOMETHING ABOUT MARY", "メリーに首ったけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "オバカなギャグを満載したラブ・コメディ。気弱なテッドは高校時代の憧れの同級生メリーを、13年も忘れられずにいた。思い余った彼は、保険調査員のヒーリーにメリーの近況を調査させることに。ところが、調査を進めるうちにヒーリーも彼女の魅力にマイってしまい、テッドをだまして諦めさせようとする。ヒーリーの嘘に気付いたテッドはメリーに会いに出かけ、ヒーリーと彼女を争う。しかし、メリーに恋しているのは彼らだけではなかった……。ズボンのジッパーに男性器を挟む冒頭のエピソードを皮切りに、下ネタを含む過激なギャグが連続。しかし、何より魅力的なのはチャーミングなヒロイン、C・ディアス。下ネタをも中和してしまう、彼女の屈託のない笑顔が、さわやかな後味を与えてくれる。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("繪本猿飛佐助", "繪本猿飛佐助", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "林芙美子の絶筆となった『親馬鹿花合戦』の映画化。戸沢白雲斉の弟子から真田幸村の家来になった、忍術の達人・猿飛佐助。海野城の平賀源心、小県の室賀信俊の双方を敵とする真田家を守る、佐助の活躍が描かれる。演じるのが二枚目スターの水島道太郎だけに、女忍者と、悲運の娘との間で揺れる佐助のラブ・ストーリーをメインにしているのが異色。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レター　僕を忘れないで", "レター　僕を忘れないで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "パク・シニャン主演の韓国映画「手紙」（1997）をタイでリメイクしたラブストーリー。バンコクで暮らすキャリアウーマンのディウは、祖母の妹の葬儀のために訪れたチェンマイで、純朴な青年トンと出会う。互いにひかれあった2人は、約700キロの距離を超えて愛を育んでいく。そんなある日、親友の死をきっかけに心のバランスを崩してしまったディウは、都会を離れてトンが住むチェンマイへと移住。まもなく2人は結婚し、幸せな生活をスタートさせるが……。", "2007-10-13", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("何処へ", "何処へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "高橋英樹が傑作「けんかえれじい」で、はみだし中学生を演じる２年前に、教師役に挑んだ典型的な明朗青春ノリ石坂文学の一編。熱血教師が芸者たちにひたすらモテるという才能を生かし、校長の椅子をめぐる教職員の派閥争いを解決する。当時、“男の紋章”シリーズでやっと成功をつかんだ高橋の芸域を広げようと企画されたが、失敗に終わった。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラーメン大使", "ラーメン大使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "28年前の恩人を捜すために、はるばる中国から日本にやって来た王さんが巻き起こす珍騒動を描く。原作は『てなもんや三度笠』『どてらい男』などで知られる花登筺。とぼけた日本語を喋る中国人役は、まさにフランキー堺にピッタリ。彼の独壇場だ。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Red River", "赤い河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "1851年、ダンソンと相棒グルートは、テキサス北境近くでカリフォルニア行きの幌馬車隊から別れて赤い河へとやって来る。間もなく幌馬車隊がコマンチ族の襲撃を受け、恋人フェンも殺されてしまったことを知る。2人は生き残った少年マシューを連れてリオ・グランデ近くの牧草地に辿り着く。それから14年の歳月が流れ……。ホークス監督にとって初の本格的西部劇。", "1952-01-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE IRON HORSE", "アイアン・ホース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "巨匠J・フォードが29歳にして撮り上げ、見事に一流監督の仲間入りを果たした無声期の西部開拓叙事詩。当時のお金で45万ドルを投入、フォックス映画の社運を賭けた超大作である。東と西から同時に工事開始された大陸横断鉄道施設をめぐって、若い男女の恋、土地売買の陰謀、父の仇討ちと様々な物語が交錯する。", "1924-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAVANA", "ハバナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "1958年、革命前夜のキューバの首都ハバナ。さすらいのギャンブラー、ジャックは世界最大のカード・ゲームのためにやってきたのだが、美しい人妻ボビーと恋に落ちる。ジャックは人生のゲームのほうに翻弄されていき……。ポラック監督とレッドフォードが「愛と哀しみの果て」に続き、再び燃える愛を描く。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("U−４７　KAPITALEUTENANT　PRIEN", "U−47出撃せよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "潜水艦U−47の艦長、ギュンター・プリーンの伝記映画。第二次大戦中、大西洋の敵港に潜入し、敵艦を撃沈するなど輝かしい戦績をあげ、“スカパフローの牝牛”と称された希代の軍人の半生を綴る。", "1958-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いのちぼうにふろう", "いのちぼうにふろう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "黒澤明、木下惠介、市川崑とともに“四騎の会”を結成した小林正樹が、結成後の第１作として作った時代劇。題名の「いのちぼうにふろう」は、アウトローたちが若者の純愛にほだされて“命を棒にふろう”とまでして、恋を実らせるという物語からきている。安楽寺は四方を堀に囲まれた“島”に建っていた。そこは一膳飯屋だが、無頼漢たちが住みついた吹き溜まりでもあった。ある日、そこへ無銭飲食で袋叩きにあっていた青年が逃げ込んできた。青年は幼なじみの娘が女郎に売られるのを助けようとして、店の金に手をつけたのだった。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HUGO POOL", "HUGO　POOL　ヒューゴ・プール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "プール清掃会社を一人で切り盛りする少女ヒューゴは、忙しい夏の日、ギャンブル狂の母と麻薬中毒の父に仕方なく手伝いを頼む。プールの水を補給するため一人コロラド川へ車を走らせた父は、途中風変わりな青年を拾い、道中をともにする。一方、母とプール清掃に励むヒューゴは、ALSに冒された青年と親しくなっていく……。R・ダウニーJr.の父R・ダウニーが、ALS(筋萎縮性側索硬化症)のため36歳で亡くなった妻と共同で書いた脚本を映画化。奇妙な人々が出会い、それぞれが心を癒されていく過程を綴る異色の人間ドラマだ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Legend", "レジェンド／光と闇の伝説", "", "", "http://ecx.images-amazon.com/images/I/51C4Z49SX9L._SL210_.jpg", 94, "　妖精やユニコーンが住む森に、青年ジャックが暮らしていた。彼は地球を暗黒の世界にしようとたくらむ闇の魔王に囚われた王女リリーを救い出そうとする。妖怪を倒し、魔王の城に乗り込むジャックと仲間たち。世界に光を取り戻すための戦いが始まった！　映像派リドリー・スコット監督が壮大なスケールで描くファンタジー・ロマン。アメリカ公開版（89分）とヨーロッパ公開版の2バージョンが存在し、日本では後者で公開された。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("らせん", "らせん", "", "", "http://ecx.images-amazon.com/images/I/21M7G7RBWKL._SL210_.jpg", 97, "「リング」とともに連作された、鈴木光司のホラー小説の映画化作品。「リング」のラストで突然死んだ高山竜司の死体が、解剖室に運ばれるところから物語は始まる。解剖を担当した医師、安藤満男は高山のかつての同級生。奇妙な因縁は、安藤が高山の胃の内部から数字を羅列した紙切れを発見したことから、さらに混迷を深めていく。前作にも登場した中谷美紀や真田広之も再び出演。TVドラマ「沙粧妙子・最後の事件」の飯田譲治が、「リング」の中田秀夫とはまた違った作風で、事のてん末を描いていく。２本見ることで完成する“デュアル・ムービー”なる興行形態も話題を呼んだ。", "1998-01-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　BREED　APART", "ダブル・リアクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "FBI捜査官ベンの目の前で暗殺事件が起き、その犯人たちは犯行後自爆してしまう。たった一人生き残った若者ビリーを逮捕したベンは、彼がグリーリーという狂信的科学者に誘拐、監禁され、殺人兵器として育てられたのではないかと推察する。なぜなら、ベン自身が幼いころにグリーリーの実験台になった過去があったからだ。やがて脱走したビリーを追って、ベンはメキシコにあるグリーリーの秘密研究所に乗り込むが……。幼児を洗脳して殺人兵器に仕立て上げるマッド・サイエンティストとFBI捜査官の闘いを描いたサスペンス・アクション。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火星のカノン", "火星のカノン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "「冬の河童」で高い評価を得た風間志織の７年ぶりの作品。29歳の絹子は、妻子ある男性と不倫中。先の見えない恋を断ち切れずにいる彼女に元バイト仲間の女の子、聖子は何かと世話を焼く。実は聖子は絹子に想いを寄せていたのだった……。愛すること、愛されることの幸福感や心の痛み、やがて終わりが訪れる恋愛のあやうさを、きめ細やかな日常描写の中に描き出す。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE POSEIDON ADVENTURE", "ポセイドン・アドベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "豪華客船ポセイドン号が、大津波に襲われ転覆、一瞬のうちに数百人の生命が失われる大惨事となった。生き残った数名の死の脱出劇が即座に始まる……。ハリウッド映画の伝統“スペクタクル劇”の魅力を全編に盛り込んだパニック映画の傑作。上下逆さのセット・アイデアも抜群。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Querelle - Ein Pakt mit dem Teufel", "ケレル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "映画化不可能といわれ、シュレーター、ベルトルッチらも企画をあきらめたJ・ジュネの『ブレストの乱暴者』。大胆にもこの小説の映画化に取り組んだファスビンダーの遺作。ブレスト港は、警部マリオと城壁の上の淫売宿“ラ・フェリア”の主人ノノに支配されている。そこに現れた美しい水兵ケレルは男たちを魅了する危険に満ちた天使だ。彼は密輸のため手を組んだ水兵仲間を殺し、ノノとマリオそれぞれにとり入って地獄の快楽と暴力に目覚めさせる。その悪の臭いはノノの妻ジリアヌを虜にし、ケレルに身をまかそうとするが彼はとりあわない。彼は兄を殺そうとしたり、バイセクシュアルのジルを殺人犯に仕立てたり悪のかぎりを尽くしたあげく、彼を愛する上官セブロンとともに出航していく。汚辱に満ちた世界を描きながら、美しく、また人が人を求める切なさにあふれたこの映画の世界は不思議な親密さがあふれている。", "1982-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みだれ枕", "みだれ枕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "夭折した明治の女流作家、樋口一葉の『にごりえ』を、ピンク映画に仕立てた異色作。三崎港で相棒のタカと飲み屋を営む女リキが、落ちぶれたかつての情人、ゲンシチと、未練たっぷりに想い合う姿を描き出す。製作を務めたのは、木俣監督の息子でもある和泉聖治。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ非情史 血の決着", "やくざ非情史 血の決着", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ある東北の地方都市。国道工事利権の入札をめぐって、博徒吉井一家と、野丁場鳶の金沢一家がしのぎを削っていた。吉井は食客の一匹狼のヤクザ関東常に、金沢の親分・宗之助殺しを命じる。常は渡世の義理から心ならずも宗之助を刺殺する。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRIME OF MISS JEAN BRODIE", "ミス・ブロディの青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "イギリスの女性作家ミュリエル・スパークの小説をもとに、ロナルド・ニームが映画化した青春物語。1930年ごろのスコットランド。名門女子校に、一風変わった女教師、ミス・ブロディがやって来た。ところが新しい気風にあふれたミス・ブロディの教育方針は旧来の価値を尊重する学校側と相入れず、彼女は学園を去ることになるが……。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIAMI RHAPSODY", "マイアミ・ラプソディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "若きコピーライターのグウィンは、動物園で働く優しい青年マットと婚約中。二人は結婚して、尊敬する両親のような夫婦になりたいと思っていたが、ある時、両親が二人とも浮気をしているという衝撃的な事実を知ってしまう……。いくつかのカップルをモデルに、結婚後の恋愛と浮気の是非をコミカルに描く。父親を「ハリーとトント」「結婚しない女」などのP・マザースキー監督が演じていることと、スーパーモデルのN・キャンベルが出演しているのも話題だ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISS ME KATE", "キス・ミー・ケイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "トニー賞受賞の戯曲を原作にしたMGMミュージカル全盛期の傑作。劇中劇のドラマと、楽屋裏のドラマが同時進行する、原作舞台の持ち味を生かした巧みな演出が光る。座長兼俳優のフレッドが率いる劇団の新作ミュージカルができ上がった。シェイクスピアの喜劇『じゃじゃ馬ならし』をベースにした“キス・ミー・ケイト”だ。フレッドの相手役ケイトを演ずるのは別れた妻のリリー。役柄そのままのじゃじゃ馬女リリーは、ふとしたことからフレッドに腹を立て、本番中に私情を持ち込んだため、舞台の上はひと騒動となる。いまやスタンダード・ナンバーとなった『ソー・イン・ラブ』などコール・ポーターによる名曲14曲が全編に散りばめられているのも魅力の一つ。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我是誰", "WHO AM I？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ジャッキー・チェンが監督、脚本、主演を務めたアクション大作。任務中にヘリが墜落し、特殊工作員ジャッキーがアフリカの砂漠に放り出される。記憶を失った彼はカー・ラリーに参加中の日本人女性ユキの窮地を助け、その活躍をかぎつけた新聞記者クリスティーンに付け回されるハメに。一方、ジャッキーの生存を知ったなぞの組織が彼の命を狙って暗躍。危険を察したジャッキーは、二人の女性を巻き込みながら巨大な敵に立ち向かう。アフリカとオランダを股にかけた壮大なストーリーに、飛行機からの宙吊りや超高層ビルからのダイブなど、驚異のアクションが満載。山本未来が快活なヒロインを好演し、鮮やかなドライビング・テクニックも披露する。", "1998-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PASQUALINO SETTEBELLEZZE", "セブン・ビューティーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "第二次世界大戦から戦後に至るナポリの街を舞台の中心に、伊達男パスカリーノと彼の自慢の７人の妹たちが繰り広げる、しぶとく旺盛なサバイバル人生。前線行きを志願し、捕らわれて強制収容所に連行されたパスカリーノは、家族のために生き延びたい一心で、猛牛のような女収容所長に色仕掛けでとり入ろうとする。しかし、戦後、彼を迎えた故郷の妹たちは、一人残らず進駐軍相手の娼婦だった。巨匠フェリーニのもとでならした女性監督が描く、エネルギッシュで悲哀感に満ちたコメディの秀作。特に、パスカリーノが収容所で生き抜くために、仲間を処刑する役に応じようとして苦悶するシーンが凄絶だ。", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("病は気から 病院へ行こう２", "病は気から 病院へ行こう２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "25歳の美容師、祐子は末期ガンを宣告され、ホスピスで余生を送ることになる。持ち前の明るさで残る短い人生を精いっぱい生きようと決めた祐子は、ふとしたことからCM出演し、全国的な有名人になってしまう。治療を目的としない病棟、ホスピスのあり方を問いながら、涙と笑いで綴る感動の娯楽作。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE ZE｀BRE", "妻への恋文", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "倦怠期に見舞われた妻に、結婚初期の情熱を取り戻させようとする夫。彼は持ち前のユーモアを駆使して、妻に匿名のラブレターを送る。だが、それがいっそう妻の心をかき乱してしまう。「髪結いの亭主」を彷彿させる、おかしくもせつない恋愛喜劇。夫役に扮したT・レルミットによる妙演が光る。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("What Women Want", "ハート・オブ・ウーマン", "", "", "http://image.eiga.k-img.com/images/movie/1423/315x210.jpg?1267", 126, "広告代理店で働くニックは仕事にも女性にも自信満々のバツイチ独身男。ところが彼は、他社から引き抜かれた女性エグゼクティブ、ダーシーに狙っていた部長の座を奪われてしまう。そんなある日、ニックは自宅の風呂場で転倒したのがきっかけで、周りにいるあらゆる女性の考えていることが声となって聞こえるようになる。彼は他人に弱味を見せないダーシーの心の声を、図らずとも聞いてしまうのだが……。", "2001-01-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラ対メガロ", "ゴジラ対メガロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "海底王国・シートピアは王国の存亡を懸けて、守護神メガロと宇宙怪獣ガイガンを擁して地上文明撃滅を狙う。地上側ではゴジラとジェット・ジャガーが迎え撃ち壮絶な怪獣タッグマッチが繰り広げられる。なお、ジェット・ジャガーはTVで公募された新キャラクター。", "1973-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE STORY", "ある愛の詩", "", "", "http://ecx.images-amazon.com/images/I/51%2BCbaofXaL._SL210_.jpg", 100, "大ヒットを飛ばした純愛メロドラマ。ジェニーとオリヴァーは、大学の図書館で出会い、育ちの違いを超えて愛し合い結婚する。貧しいが幸福な日々。しかし、ジェニーは白血病だった……。F・レイ作曲の甘美なテーマ曲もヒット。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monster", "モンスター", "", "", "http://image.eiga.k-img.com/images/movie/1543/315x210.jpg?1392", 109, "86年のフロリダを舞台に、アメリカ史上初の女性連続殺人犯となったアイリーン・ウォーノスの実話を映画化。監督・脚本は撮影助手出身でこれが長編デビューとなるパティ・ジェンキンズ。貧しい家庭で虐待されて育ち、娼婦をして暮らすアイリーンは、同性愛の少女セルビーと出会い、彼女との生活費を稼ぐために殺人を重ねていく。主演のシャーリーズ・セロンは13キロ肥って特殊メイクで熱演、アカデミー主演女優賞を獲得。", "2004-09-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Happyダーツ", "Happyダーツ", "", "", "http://image.eiga.k-img.com/images/movie/53983/315x210.jpg?3156", 86, "30歳の派遣社員の美奈子は、仕事にも意欲がなく打ち込める趣味もないグータラな日々を送っていた。たまたま友人に連れて行かれたダーツバーで、イケメン店員の篠塚にダーツに誘われ、篠塚に一目ぼれしてしまう美奈子。篠塚に会いたい一心でダーツバーに通いだす美奈子だったが、バーに若くて美人なダーツ選手の八王子が現れ……。主演は辺見えみり、共演に「ギララの逆襲／洞爺湖サミット危機一髪」の加藤和樹、森泉ら。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHADE", "シェイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ラスベガスのカジノを舞台にしたギャンブル映画。男女３人の詐欺師チームが巧みにカードを操って大金を稼ぎ、伝説のギャンブラーとの大一番になだれ込んでいく様を描き出す。監督は実際にポーカーの達人で、本作の製作資金をギャンブルで稼いだという新人D・ニーマン。S・スタローンが無敗のギャンブラー役で渋い魅力を放っている。セコい詐欺師のミラーとセクシー美女ティファニーは、かつての仲間だった天才カード・ディーラー、ヴァーノンと合流。大掛かりなトリックで大金を手に入れる。ところが、その金はギャングのボスが持ち主だったことから命の危険が……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Seeker: The Dark is Rising", "光の六つのしるし", "", "", "http://image.eiga.k-img.com/images/movie/34253/315x210.jpg?1620", 99, "スーザン・クーパーの名作ファンタジー小説「闇の戦い」シリーズの第1部を映画化。14歳の誕生日を迎えたばかりの少年ウィルは、自分が“闇”の力から世界を守る使命を背負っていることを知らされる。勢力を拡げつつある“闇”に対抗するためには、異なる時代に散らばった6つの“光のしるし”を集め、“光”の力を完全なものにしなければならない。自らの運命を受け入れたウィルは、時空を超えて旅に出るが……。", "2007-12-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("路傍の石", "路傍の石", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "文部省が教化映画製作に乗り出した第１作。製作前年にあたる1937年１月から半年間新聞に連載された山本有三の小説を、田坂具隆が映画化。貧しい境遇に生まれた吾一少年が、生活のための厳しい丁稚奉公にもめげず、向学心を燃やして成長していくさまを描いている。田坂は、これを単に楽天的な立身出世物語にするのではなく、求道精神の担い手としての吾一少年を温かく見つめることによって、感銘の深いものにしている。1937年、田坂の「真実一路」でデビューした天才子役・片山明彦が吾一役を熱演。吾一の母には田坂夫人でもあった滝花久子が扮している。当時としては稀有な４週続映という大ヒットを飛ばした一編。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　HUTIE｀ME　JOUR", "八日目", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "妻子に愛想をつかされるほど仕事一筋のアリーが、ジョルジュというダウン症の青年を車で拾う。ジョルジュは母親と会うために施設を抜け出してきたのだった。しかしその母親はすでに死んでおり、ジョルジュとともに時を過ごすうちに、アリーの心にも忘れかけていた人間性がよみがえってくる。現代人に求められる他者への愛情をテーマとした感動作。「トト・ザ・ヒーロー」のJ・V・ドルマル監督が、旧約聖書の天地創造をもじったオープニングのファンタスティックな映像作りや叙情的な自然描写に手腕を発揮している。ジョルジュ役のP・デュケンヌは実際にダウン症にかかっており、その感性豊かな演技はカンヌ映画祭でも絶賛された。", "1996-01-01", "37,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トミカヒーロー　レスキューフォース　爆裂MOVIE マッハトレインをレスキューせよ！", "トミカヒーロー　レスキューフォース　爆裂MOVIE マッハトレインをレスキューせよ！", "", "", "http://image.eiga.k-img.com/images/movie/53993/315x210.jpg?4439", 0, "ミニカーブランド「トミカ」の乗り物が多数登場するTVシリーズ「トミカヒーローレスキューフォース」の劇場版。謎の組織「ネオテーラ」は“超災害”を世界各地に発生させ、人類滅亡を企んでいた。世界消防庁は5人の精鋭を集めて特別救助機動部隊「レスキューフォース」を結成し、ネオテーラにジャックされた世界を一周する特急列車“マッハトレイン”のレスキューを命じる。「爆走　トミカヒーローグランプリ」を同時上映。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("出所祝い", "出所祝い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "五社英雄監督、仲代達矢主演によるヤクザ映画。ある地方都市を舞台に、伐採した材木を運ぶ軽便鉄道の利権をめぐって二大ヤクザ組織がしのぎをけずる。この作品は東宝初のヤクザ映画で、本家東映のヤクザ映画と比べるとやはり違和感がある。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEIN KAMPF", "我が闘争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "第一次世界大戦直後の混乱から第二次世界大戦敗戦とヒトラーの死までを綴るドキュメンタリー。当時のニュース映画、スチール写真、ゲシュタポ教育用映画などの材料を編集し直し、記録性と訴える内容の激しさで貴重な作品となっている。タイトルはヒトラーの書いた著書名である。", "1960-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dune", "デューン／スーパープレミアム［砂の惑星・特別篇］", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 190, "“デューン”と呼ばれる砂の惑星アラキスを舞台に繰り広げられる勢力争いを、壮大なスケールで描いたSF大作。フランク・ハーバートによる大長編SF小説をデビッド・リンチ監督が映像化した劇場用映画「砂の惑星」を、10年の歳月を経てテレビ放映用に再編集したもの。新たに未公開シーンとナレーションが追加されている。しかしこの編集作業にリンチ監督は参加しておらず、本作の監督・脚本はアラン・スミシー名義となっている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談昇り竜", "怪談昇り竜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "石井輝男監督による“昇り竜”ものの第５作。立花明美は立花組の二代目を襲名するが、立花組の縄張りを狙う土橋組が再三いやがらせを続ける。怒った明美は、客人の助けを借りて土橋組組長を斬る。しかし、斬った組長の娘が、明美を仇としてつけ狙う。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AIR", "AIR", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "2000年、PCゲームとして発売されるやいなや一大ブームとなった「AIR」を劇場映画化。「あしたのジョー２」「エースをねらえ！」「劇場版とっとこハム太郎」など数々の名作を手がけてきた出崎統が監督を務め、緻密なストーリーと儚く美しい世界観がより迫力を増した作品に仕上がった。夏祭りの１週間前、観鈴は図書館で“翼人”の絵に惹かれた。同じ頃、神社に飾られた同じ絵に見入る旅芸人・往人と運命的に出会う。", "2005-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天国の大罪", "天国の大罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "人種のるつぼと化した近未来の東京を舞台に、女検事からマフィアの情婦へと、流転の道を歩む一人の女を描いたラブ・サスペンス。主役の衣畑遼子に、吉永小百合が扮し、母親役からアクションまで、多彩な演技を披露しているが、相手役の松方弘樹との組み合わせは今一つピンとこない。もう一人の相手役、チャイニーズ・マフィアに扮したオマー・シャリフは、演技自体は重厚だが、吉永とのコンビでは、年齢差がありすぎた。当初、これは「華の乱」（1988）のすぐあとに、松田優作と吉永の共演作として企画された作品だったが、松田の急死でかなわなかった。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サソリ・女囚701号", "サソリ・女囚701号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "篠原とおるの劇画ヒロインに扮した小松千春の大胆なヌードが話題を呼んだアクション作品。15年前に妹を誘拐され、父親が自殺するという悲惨な過去を持った女医、松島ナミの前に、妹を拉致した男が患者として現れた。怒りに震えた彼女は男をメスで刺すが、男は事切れる前に、妹を殺したのは共犯者だと言い残す。殺人罪で10年の刑を受けたナミは、刑務所内でリンチや性的虐待に耐え続けた。そんな彼女は無実の罪で死刑囚となったさゆりと出会う。そしてさゆりの死刑執行日、妹の復讐のため脱獄を計画するナミはさゆりを連れて刑務所を飛び出すが……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リアル鬼ごっこ3", "リアル鬼ごっこ3", "", "", "http://image.eiga.k-img.com/images/movie/57653/315x210.jpg?2575", 95, "山田悠介の同名ベストセラーを映画化して話題となったサバイバルアクション「リアル鬼ごっこ」（2007）、「リアル鬼ごっこ2」（10）に続き、新たに製作された3部作（「リアル鬼ごっこ3」「4」「5」）の1作目。前作までの「佐藤さん」に替わり、今度は「血液型B型」の人々が襲われる鬼ごっこが展開。3部作を通して同時刻に異なる場所で起こる3つの物語が描かれる。所属していた陸上部からドロップアウトしたことで、仲良しだったオンジと険悪になってしまったスグル。いつものように校舎の屋上で授業をサボっていたところ、血液型がB型の人間を狙う殺人鬼が学校を襲う。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLIGHT OF THE DOVE", "バックドア　告発白書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "国家安全保障局の元秘密工作員だったアレックスは、絶対に辞めることを許されない規律を破り、５年間の逃亡生活を送っている。一方、偶然彼女と出会ったウィルは、国家を相手に完全犯罪をたくらむアレックスと一緒に逃げるハメに。生きる目標を失っていたウィルは、彼女を守るために組織に対して罠を仕掛ける。行く手にあるのは自由か？　死か？　ハードなSMシーンやベッドシーンたっぷりのバイオレンス・アクション。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dickie Roberts: Former Child Star", "ディッキー・ロバーツ　俺は元子役スター", "", "", "http://ecx.images-amazon.com/images/I/519-dAttIYL._SL210_.jpg", 98, "元人気子役の青年の奮闘を描いたコメディドラマ。かつて子役スターとして名を馳せたディッキー・ロバーツは、35歳となった今では売れない役者として冴えない日々を送っていた。そんなある日、ロブ・ライナー監督の新作映画のオーディションを受けるが、「普通の少年時代」を経験していないから「普通の男」を演じることができないと指摘されたディッキーは、ある家族を雇って少年時代をやり直そうと思いつく。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古今笑叢", "韓国官能秘話 かまきり３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "人気コリアン・エロス“かまきり”シリーズの一編。村を追い出された青年ペジギと、嫁入りした先で夫に冷たくされ、家を出た美しい娘タルビ。二人はともに“かまきりの星”の下に生まれ、偶然にも旅先で知り合い愛し合うようになる……。", "1988-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROPHEYIII", "ゴッド・アーミーIII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "悪と善の天使同士の人知を超えたバトルを描く、SFスリラーシリーズの第３作。荒廃した街で、世界の滅亡を説く説法師ダニエルが銃弾に倒れた。昏睡状態に陥った彼の体に、人類を滅亡させようとする邪悪な天使ピリエルが新たな命を吹き込む。ダニエルの守護天使である大天使ガブリエルは、ビリエルの野望を阻止すべく奔走する。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("(秘)女郎市場", "(秘)女郎市場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "江戸は文化文政の頃。女衒の吉藤次がお新というやや頭の弱い娘を品川楼へ連れてきた。唯一の友人である牛のクロを殺されて逃げ出したお新は、責任を感じた吉藤次に連れ戻され、皆の前で彼と交わる。ところが、張りきり過ぎた吉藤次は、腹上死してしまう……。廓の風俗を描いた荒木一郎の小説『風流宿場女郎日記』の映画化。ロマンポルノ時代劇。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPACE JAM", "SPACE JAM", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "NBAのスーパー・プレーヤー、M・ジョーダンと、アメリカン・アニメのスーパー・スター、バッグス・バニーという夢の共演を、最新のSFX技術で達成したファンタジー。宇宙の果てにあるアミューズメント・パークの重役スワックハンマーは５人の手下ナードラックスに、地球で大人気のバッグス・バニーとその仲間たちを連れて来るよう命じる。ピンチのバッグス・バニーたちは、ナードラックスにバスケットボールでの対決を提案。そしてジョーダンに助けを求めるのだった……。監督はM・ジョーダンが出演した“ナイキ”など、5000本ものCFを手掛けてきたJ・ピトカ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プロゴルファー織部金次郎３ 飛べバーディー", "プロゴルファー織部金次郎３ 飛べバーディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "人気コミックを映画化したシリーズ第３作。プロゴルファー歴17年にして未だ優勝経験のない“織金”こと織部金次郎のところに旅の途中で出会った若い女子プロゴルファーの日向みずきが訪ねて来る。練習場で働くことになった彼女と親しくする織金を見て、恋人の桜子の心中は穏やかでない。一方織金は連敗が続き、出場資金も底をついてしまう。そんな中、空港閉鎖でゴルフ場に来られない選手が出たために、再びマッチプレー・トーナメントに挑戦することになり……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴキブリ刑事", "ゴキブリ刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ゴキブリ＝暴力団退治を専門とする刑事、鳴神涼の活躍を描いたハードボイルド・アクション。暴力には暴力をと、殴る蹴る、反抗したら撃ち殺すというスーパー刑事を渡哲也が好演。臨海工業都市で麻薬を扱う暴力団と、そのバックの工業会社の悪事を鳴神が暴く。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スーパースキャンダル", "スーパースキャンダル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "毎日のようにスポーツ紙のトップを飾るスキャンダル俳優・太田俊哉。それもそのはず、俊哉の裏の仕事は芸能人のゴシップを金で肩代わりする“スキャンダルの掃除屋”だったのだ。そんな俊哉が突然、ハリウッド帰りの大女優や大御所の名優など、豪華キャストが並ぶ大作映画の主役に抜擢された。しかし時が進むにつれ映画が、スタッフが、そして俊哉自身もスキャンダルにまみれていく……。SMAPなどの舞台プロデュースを手掛けてきた岡村俊一の初監督作品。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nightmare on the Elm Street", "エルム街の悪夢（2010）", "", "", "http://image.eiga.k-img.com/images/movie/54533/315x210.jpg?2073", 95, "夢の中から人々を襲う殺人鬼フレディ・クルーガーの殺戮を描く同名人気ホラーシリーズの第1作をマイケル・ベイ製作、ジャッキー・アール・ヘイリー主演でリメイク。エルム街に住む高校生のナンシー、クリスらは鉄の爪を持つ男に襲われる悪夢に毎晩うなされていた。そんな中、同じ悪夢にうなされていた仲間のひとりが現実に殺害され……。監督は、これまでにメタリカやグリーンデイなどのPVを手掛けてきたサミュエル・ベイヤー。", "2010-06-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東雲楼 女の乱", "東雲楼 女の乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "明治末期、九州・熊本一を誇った遊郭“東雲楼”を舞台に繰り広げられる、明治女たちのすさまじい生き方を描いた文芸大作。かたせ梨乃、斉藤慶子、南野陽子ら人気女優たちの華麗なる群像劇のなかに、女盛りの女将・鶴と、姉妹のように育ったお茶子頭（女中頭）の志津の哀しい運命が浮かび上がる。鶴はパトロンが事業で失敗したために女将の座を追われ、志津は情人の鉄道工夫に利用されて鶴の信用を失ってしまうが……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Let the Light One in", "ぼくのエリ 200歳の少女", "", "", "http://image.eiga.k-img.com/images/movie/55493/315x210.jpg?3013", 115, "永遠に年をとらないバンパイアの少女と、孤独な少年の交流を描いたヨン・アイビデ・リンドクビストのベストセラー小説「モールス」の映画化。内気で友達のいない12歳のオスカーは、隣の家に引っ越してきた不気味な少女エリに恋をする。しかしエリの正体は、人間の血を吸いながら町から町へと移り住み、200年間も生きながらえてきたバンパイアだった。2008年のトライベッカ国際映画祭で最優秀作品賞を受賞。", "2010-07-10", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋人はスナイパー", "恋人はスナイパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "テレビ朝日系で放映されたTVシリーズの映画版。脚本を“踊る大捜査線”シリーズの君塚良一が担当し、新たなアクション大作としてスクリーンに甦らせた。大のカンフー映画好きとして知られる内村光良が、伝説のスナイパー役を体当たりで熱演。刑事役の水野美紀とのコンビで、華麗なアクションを披露。また対立しながらも惹かれ合う、二人の駆け引きも見どころの一つだ。キャストも、竹中直人、田辺誠一といったTV版のレギュラー陣に、中村獅童、阿部寛らを加えた映画版ならではの豪華な顔ぶれ。いかりや長介の遺作でもある。ある日、首相官邸に国際犯罪組織・1211から“日本国民を誘拐した。５千億円用意しなければ国民を狙撃する”との脅迫電話がかかってくる。事件解決のため政府は、元1211の一員で伝説のスナイパー、ウォンを呼び寄せた。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO　GOOD　DEED", "ノー・グッド・シングス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ハードボイルドの大家ダシール・ハメットの原作に基づく犯罪ドラマ。悪党たちの欲望が複雑に絡み合い、ヒロイン、ミラ・ジョヴォヴィッチの魔性の存在感共々、スリリングな盛り上がりをみせる。刑事ジャックは、家出娘を捜索中に、強盗一味のアジトに紛れ込んでしまう。計画がバレたと思い込んだ一味は彼を監禁。自由を奪われたジャックは見張りの美女エリンを説得しようとするが……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sisterhood of the Traveling Pants 2", "旅するジーンズと19歳の旅立ち", "", "", "http://image.eiga.k-img.com/images/movie/53533/315x210.jpg?1568", 119, "アン・ブラッシュアーズのベストセラーシリーズ小説の映画化第2弾で、「旅するジーンズと16歳の夏」（2005）の続編。前作から3年、カルメン、リーナ、ティビー、ブリジットの幼馴染の4人は大学に入り、それぞれの道を歩み始める。大学に入って最初の夏、それぞれの場所で過ごす4人は恋人との新たな出会いや別れを通して成長していく。そしてそれぞれが体験した出来事を分かちあうことで、4人の絆と友情はさらに深まっていく。", "2008-08-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ring Two", "ザ・リング2", "", "", "http://image.eiga.k-img.com/images/movie/1703/315x210.jpg?4736", 110, "ハリウッド版「ザ・リング」の続編を、日本版シリーズ第1作「リング」の監督である中田秀夫がハリウッドで監督。日本版のドラマとはまったく別に、ハリウッド版の前作のその後を描く。前作の半年後、レイチェルと息子エイダンは小さな田舎町に引っ越して暮らしていたが、またも呪いのテープによる変死事件が発生。サマラがエイダンに乗り移ろうとするのを知ったレイチェルは、彼女の過去を再び調査して新たな事実を発見する。", "2005-06-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神様ヘルプ！", "神様ヘルプ！", "", "", "http://image.eiga.k-img.com/images/movie/55543/315x210.jpg?3378", 0, "劇団「アーバンフォレスト」主宰の穴吹一朗が手がけた舞台「Tower of Sugar」を原案に映画化したミステリー。「湾岸ミッドナイト THE MOVIE」「恋極星」の加藤和樹が、主人公と25年前の大量殺人犯の一人二役を演じる。お化け屋敷の演出を担当するホラープランナーのアツオは、とある廃校を訪れる。そこで自分とうりふたつの姿をした白衣の男の写真を見つけた瞬間、青い光に取りつかれて意識を失ってしまう。そこは25年前、悪魔に魂を売った男・西条に呪いをかけられた理科教師が大量殺人事件を起こした学校だった……。", "2010-08-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("危うし!!快傑黒頭巾", "危うし!!快傑黒頭巾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "勤皇派の長州藩が、長崎でゲーベル銃を購入。勤皇派に対抗しようとする幕府は、目付・松平主税介を長崎に派遣し清国の力を借りようとしていた。この幕府の動きを察知した黒頭巾もまた、長崎へ向かうが……。シリーズ最終作。幕末の動乱を駆けぬけた黒頭巾も、白馬とともにスクリーンから去った。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不信のとき", "不信のとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "有吉佐和子のベストセラー小説を映画化。商事会社の宣伝部員・浅井は、結婚10年になるが子供がいない。その彼の愛人マチ子が妊娠。無事マチ子が女の子を生んだ頃、今度は妻の直子が妊娠する。この愛人と妻が、浅井が入院した病院で鉢合わせしてしまう。一人の男をめぐり、女の火花を散らす妻と愛人を、岡田茉莉子と若尾文子が熱演。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOA: Dead or Alive", "DOA デッド・オア・アライブ", "", "", "http://image.eiga.k-img.com/images/movie/33843/315x210.jpg?2704", 86, "全世界で700万本以上の売り上げを記録した3D格闘ゲームを「トランスポーター」のコリー・ユン監督が映画化。世界最強のファイターを決定する格闘トーナメント“デッド・オア・アライブ”を舞台に、クールでセクシーな女性ファイターたちが激闘を繰り広げる。行方不明の兄を探す女忍者かすみを「シン・シティ」のデボン青木が演じるほか、彼女を守る忍者ハヤブサ役で、ケイン・コスギがハリウッド・デビューを果たした。", "2007-02-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAIR TRADE", "悪魔の帝国 シンジケート・ブレイク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "麻薬捜査官の娘とその友人たちが、シンジケートに誘拐される。だが彼女は国の救援を待たず、自らの命を懸けて武器を手に、シンジケートに挑んでいく。前半がマフィアと警察の大攻防戦、後半が密林での戦争アクションという、１本で２倍のエンターテインメントが楽しめる構成となっている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("思春の泉", "思春の泉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "TVドラマ『ザ・ガードマン』でブレイクし、その後山口百恵の“赤”シリーズでも大映TVドラマの代名詞的存在となった宇津井健の初出演映画。おなじみ石坂洋次郎原作の農村ドラマで、婆さん二人が若い男女をくっつけようと画策するが、そんなこととは関係なく二人は結ばれるといったおおらかな内容だ。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Invisible", "臨死", "", "", "http://ecx.images-amazon.com/images/I/51M4QnXMiFL._SL210_.jpg", 111, "臨死状態に陥った少年が、自らの死を止めるべく奮闘する姿を描いたサスペンススリラー。成績優秀で人気者の高校生ニックは、ふとしたことから不良グループに目をつけられてしまう。ある晩、不良たちから暴行を受けて森に置き去りにされたニックは、自分が臨死状態で幽体離脱していることに気づき……。主人公ニックに扮するのは、「宇宙戦争」でトム・クルーズの息子役を演じ脚光を浴びたジャスティン・チャットウィン。監督は「ブレイド」「バットマン」シリーズの脚本家として知られるデビッド・S・ゴイヤー。", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAUTREC", "葡萄酒色の人生　ロートレック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "名家の嫡男としての将来を捨て、破天荒な人生を送った天才画家ロートレックの伝記ドラマ。一流画家のモデル、シュザンヌとのラブ・ストーリーを中心に、モンマルトルの裏町に生きる人々やワインをこよなく愛したロートレックの36年間の生涯が語られる。ムーラン・ルージュやフレンチ・カンカンといった19世紀末の風俗を華やかに再現し、セザール賞では衣装、美術の２部門を制した。", "1998-01-01", "2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠い明日", "遠い明日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "殺人囚として獄中にいる父のえん罪を晴らすために北九州へ行った青年が、犯行現場階下の時計店主、元刑事、目撃者のOLなどを次々と味方に引き入れるが、やがて彼の前に次席検事正の影が立ちはだかる……。社会派ミステリーの体裁を取りながら、怠惰をかこつ青年が人間関係の深さとはかなさを体験していく。神代監督、異色の青春映画となった。原作は、A・J・クローニンの『地の果てまで』。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("激走トラッカー伝説", "激走トラッカー伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "全国を股にかける“アート・トラッカー”を主人公に、５日間で日本を一周するという伝説に挑戦する男たちの姿を痛快に描く。従来の東映の“トラック野郎”シリーズを思わせる人情劇だが、トラッカーたちの日常はよりリアルに描写。男気あふれる主人公の自衛隊崩れのトラッカーを渡辺裕之が好演。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL DESERTO DEI TARTARI", "タタール人の砂漠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "1907年イタリア。士官学校を卒業したばかりの青年ドローゴの着任の日が来た。赴任地は国境の砦バスティアーノである。そこには、かつて猛威をふるった狂暴な騎部武族タタール人の伝説があった。不毛な土地で姿なきタタール人の襲撃に怯える兵士たち。苦悶と焦燥のうちに年月が無為に過ぎ去っていく。中尉にまで昇進したドローゴだが、長期間にわたる要塞での生活は、彼の肉体と精神をむしばんでいた……。ヨーロッパ人が抱くアジアに対する潜在的恐怖感を背景に、錯綜する心理劇が展開する。1982年に52歳の若さで他界、その寡作ぶりで知られるV・ズルリーニの遺作。音楽はE・モリコーネ。", "1976-01-01", "4,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONKEY　BUSINESS", "いんちき商売", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "当時キートン、チャップリンらと並ぶ人気コメディアンとして活躍していたマルクス兄弟主演の傑作コメディ。てんやわんやのドタバタ喜劇は、彼らの専売特許。ニューヨークへ向け航海中の客船に密航者が４人同船。船中でいろいろ人たちを次々とカモりまくっていく彼らだが……。DVDは「ザ・ベスト・オブ・マルクス・ブラザース」に収録。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEPRISA，DEPRISA", "急げ、急げ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "マドリード市街で車を盗んで逃亡するパブロとメカ。二人は酒場に入り、そこで働くアンヘラを誘い出して仲間に加える。犯行を重ねながら郊外へと逃げる３人は、パブロの祖母の家にたどり着くが、すでに警察の手が回っていた……。ゴダールの「気狂いピエロ」さながらのストーリー展開に、軽快なスパニッシュ・ポップ・ナンバーが作品を盛り上げる。後の「血の婚礼」などへの転換点となったサウラ作品である。", "1980-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Midnight Movies: From the Margin to the Mainstream", "ミッドナイトムービー", "", "", "http://image.eiga.k-img.com/images/movie/33553/315x210.jpg?3527", 86, "1970年代初頭のアメリカで誕生し、アメリカン・ポップ・カルチャーに多大な影響を与えた“ミッドナイトムービー”と呼ばれるカルト映画にスポットを当てたドキュメンタリー。「ナイト・オブ・ザ・リビングデッド」のジョージ・A・ロメロ監督、「ピンク・フラミンゴ」のジョン・ウォーターズ監督、「イレイザーヘッド」のデビッド・リンチ監督ほか、製作スタッフや観客へのインタビューから当時の様子を探る。", "2006-07-15", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les infideles", "プレイヤー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "", "2012-06-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BURNING MOON", "バーニング・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "満月の夜。毎日を暴力とドラッグに明け暮れているピーターが、寝つかれない妹にある二つの物語を語る。第１話「ジュリアの恋」は、発狂したジュリアの恋人パーカーが、次々と殺人を繰り返し、ジュリアにサイコな愛を求める話。第２話の「純潔」は、イジメられっ子ユストスとカルト教団の犠牲者の話。そして、ピーターの話がクライマックスに達した時、戦慄のエピローグが彼らを襲う！　オムニバス形式で、超ハードなスプラッタが展開されるバイオレンス・ムービー。", "1992-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダーアギト PROJECT G4", "仮面ライダーアギト PROJECT G4", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "2001年の初頭からTV放送されていた「仮面ライダーアギト」の劇場版。今回のTVシリーズには、記憶喪失の青年、津上翔一が変身するアギト、警視庁が開発した強化スーツを着用したG3-X、葦原涼が変身する不完全体ギルスという３人の仮面ライダーが登場するが、劇場版には封印されていたG3-Xの進化形G4が現れる。人類の敵アンノウンの攻撃を受けた某研究施設から、超能力を発揮して逃げ延びた少年と少女。やがて二人は離れ離れになるが、少年は翔一と、少女は涼と巡り合う。そのころ、アンノウンの相次ぐ攻撃を前に、突如封印されていたG4が作動。アンノウンに立ち向かっていくのだが、それが大きな危機を招くことになる……。", "2001-09-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Say Anything", "セイ・エニシング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "キックボクシングに熱中するロイドは、卒業式の朝、優等生のダイアンに愛を告白する。ダイアンはイギリス留学を控えながらも、飾り気のないロイドに惹かれていき……。ティーンエイジャーの切なく、爽やかな恋愛を描いた青春ドラマ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風林火山", "風林火山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 166, "武田軍の名参謀・山本勘助の波乱の半生を描いた井上靖の同名小説を、時代劇の巨匠・稲垣浩が映画化した超大作。三船敏郎、中村錦之助、石原裕次郎とトップスターが勢ぞろいして大ヒットを記録した。三船プロの製作ということも話題を呼んだ。武田と上杉の川中島の戦いが一大クライマックスに。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SERVING SARA", "エリザベス・ハーレーの明るい離婚計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "離婚は先に通告したほうが有利というアメリカの法律を逆手に取って、先に相手に離婚通知を突きつけようとする大富豪夫婦のバトルを描いた爆笑ロマンチック・コメディ。主演は「オースティン・パワーズ」の美人女優エリザベス・ハーレーと、人気TVドラマ『フレンズ』のマシュー・ペリー。財産分与ゼロというムチャな離婚通知を渡されてしまった大富豪の妻サラは、先に通知した方が有利だと知って、一発逆転の奇策に出る。", "2005-11-19", "7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明日があるさ THE MOVIE", "明日があるさ THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "人気TVドラマシリーズの映画版。TV版では、とある総合商社を舞台に、落ちこぼれサラリーマンのふきだまり“営業13課”の面々の奮闘を描いていたが、映画では業界群像劇の側面は最小限に抑え、課を率いる浜田課長個人のドラマに焦点を絞り、男気あふれる物語に仕立てている。営業先で偶然出会った偏屈な老人から、お手製有人ロケット発射計画を聞かされた浜田課長が仕事も家族もないがしろにしながら、その夢の実現に向かってひた走る様をじっくり見つめる。周囲の非難を浴びつつも、我が正義を貫く主人公を、ダウンタウンの濱田雅功が好演。ドラマ版やTV『白線流し』の演出で知られ、本作が映画デビューになる岩本仁志がきめの細かいエピソードを積み重ね、奇想天外な筋書きを説得力のあるものにしている。松本人志も謎の医師役で登場、ストーリーとは無関係なギャグを浜田とのアドリブで披露。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("居酒屋兆治", "居酒屋兆治", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "山口瞳のベストセラーが原作。函館で居酒屋“兆治”を営む村野英治と女房の茂子。英治にはかつて、さよという恋人がいて、今は牧場主のもとに嫁ぎ幸福に暮らしているはずだった。ところが、ある日突然、開店前の“兆治”に顔を出す……。本筋と並行して描かれる“兆治”に出入りする人々のキャラクター描写も秀逸。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AROUND THE WORLD IN 80 DAYS", "80デイズ", "", "", "http://ecx.images-amazon.com/images/I/51XVCC75HEL._SL210_.jpg", 121, "冒険映画の名作「80日間世界一周」をリメイク。飛行機も電動列車もなかった19世紀、80日間で世界一周すると豪語したロンドンの発明家と新米の使用人が、それを証明するために奔走。フランス、トルコ、中国、アメリカを駆ける道中は波乱に満ち、スリルと笑いがギッシリ詰め込まれている。英国のコメディ俳優、S・クーガンとJ・チェンの掛け合いはもちろん、行く先々で登場するカメオ出演者の妙演も見どころ。とりわけシュワルツェネッガーの妙演は必見。", "2004-11-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生さぬ仲", "生さぬ仲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "漁業会社社長、渥美俊策は不況による経営不振から刑事事件を起こし、２度目の妻・真砂子と前妻の子、義理の母の一家は窮地に陥った。そんな時、アメリカで映画女優として成功した前妻・珠江が、帰国して……。1932年の「蝕める春」で一本立ちした成瀬巳喜男監督の長編第３作。娘をめぐる実母と継母の愛憎劇を、卓抜な心理描写で描く。", "1932-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雨よりせつなく", "雨よりせつなく", "", "", "http://ecx.images-amazon.com/images/I/41WW5R2JPYL._SL210_.jpg", 87, "女性誌やCMで活躍中の人気ファッションモデル、田波涼子が映画デビューを飾ったラブ・ストーリー。吉元由美の短編集『いつもなら泣かないのに』の映画化。30代を目前にして、仕事と結婚で揺れる女性の微妙な胸の内が綴られる。主人公の相手役を、出演作の続く西島秀俊が好演。またひと味違った魅力を放つ彼の演技にも注目だ。同じ会社に勤める倉沢とひょんなことから知り合った27歳の綾美。これまで仕事優先で生きてきた彼女だが、次第に彼に惹かれていく。", "2005-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("唐山大地震", "唐山大地震　想い続けた32年", "", "", "http://image.eiga.k-img.com/images/movie/55853/315x210.jpg?4519", 135, "20世紀最大の震災といわれる1976年の唐山大地震を題材にしたヒューマンドラマ。1976年7月28日、中国河北省の唐山市でマグニチュード7.8の直下型地震が発生。崩壊した家の下敷きになった子ども2人のうち1人しか救えないという絶望的な状況に陥った母親は、苦悶の末に息子を選ぶ。そしてその声は、ガレキの下の娘の耳にも届いていた。奇跡的に命を取り留めた娘は、養父母のもとで成長するが……。「女帝　エンペラー」のフォン・シャオガン監督がメガホンをとる。", "2010-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愉快な極道", "愉快な極道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "当時流行していた“トラック野郎”のタクシー運転手版を狙った人情喜劇。元は大阪ミナミの顔役だった石田岩次郎は、今は娘の道子のことを思って、タクシー運転手になっている。ある日、彼は美貌の女医・白坂由利子と知り合って、彼女に惚れるが……。かつての“極道”シリーズでならした若山富三郎が、意外にもタクシー運転手役に挑戦。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Song Is Born", "ヒット・パレード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「教授と美女」のリメイク。ゲイリー・クーパーの役柄を音楽学者に変えてダニー・ケイが演じている。前作の珍しい言葉がここでは珍しい音楽（ジャズ）となり、当時の大御所ミュージシャンを一堂に集めていることも魅力の映画である。リメイクにあたり、脚本が完成するまでに8人ものライターが関わったとされているが、クレジットには1人の名前も現れない。ホークスはこの企画にまったく興味がなかったが、興行は上々だった。", "1951-12-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WALLERS　LAST　TRIP", "ワラー最後の旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "鉄道の廃止が決まり、最後の点検に取り組む保線作業員ワラー。そんな彼の脳裏に、少年時代の思い出、徴兵された親友との別れ、禁じられた恋の記憶などが浮かんでは消えていく。ドイツの気鋭C・ワグナーが、黙々と一つの仕事に打ち込んできた孤独な老人の内面世界に踏み入ったヒューマン・ドラマ。現在のシーンをカラーで、回想シーンをモノクロで構成した映像世界が、静かに観る者の胸に迫ってくる。", "1988-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("做頭／HANDS IN THE HAIR", "愛さずにいられない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "華やかな過去が忘れられない人妻が、美貌の青年美容師と出会い、やがて禁断の恋に落ちる……。台湾のアイドルスター、ウォレス・フォの映画デビュー作。美熟女スター、ロザムンド・クワンを相手役に、激しいラブ・シーンを見せている。艶やかな男性遍歴を重ねた末に、気の弱い夫とわがままな娘との質素な家庭生活に落ち着いたエニー。しかし、馴染みの美容室で出会ったイケメン美容師、阿華と関係を持ってしまう。", "2006-09-30", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENTR’ACTE", "幕間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 14, "当時シャンゼリゼ劇場で上演されていたスウェーデン・バレエの“本日休演”の幕間で上映するために製作された、クレールの第２作。クレールと親交のあるダダイズム周辺の名士たち、ピカビア、サティ、マン・レイ、マルセル・デュシャンらが出演しているのも魅力。ビデオには「眠るパリ」が併録されている。", "1924-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE YARDS", "裏切り者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「リトル・オデッサ」のJ・グレイ監督による硬派な社会派サスペンス。刑期を終えた若者レオは叔父の経営する車両工場に就職し、政治家との裏取引に携わる親友ウィリーの助手となる。だが、他社の妨害工作も手掛けるウィリーが弾みで殺人を犯してしまい、そこに居合せたレオが容疑者に。レオはウィリーや叔父に助けを求めるが……。逃亡劇のスリルはもちろん、登場人物の苦悩を彫り深く捉えた感情描写も素晴らしい。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEARTBREAK HOTEL", "ハートブレイク・ホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "エルヴィス・プレスリーが、もし家族だったら？　永遠のスーパースターをテーマにしたファンタジー・ロマン。大胆不敵にも、エルヴィス・プレスリーを誘拐してしまった少年と家族が巻き起こす騒動を描く。現実から目をそらしている母親の目を覚ますため、少年はエルヴィスの誘拐を思いつくが……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まぼろしの邪馬台国", "まぼろしの邪馬台国", "", "", "http://image.eiga.k-img.com/images/movie/34223/315x210.jpg?1583", 0, "昭和40年代に邪馬台国ブームを巻き起こした盲目の文学者の宮崎康平と、彼を献身的に支えた妻の宮崎和子の軌跡を描いた夫婦の物語。監督は「包帯クラブ」「20世紀少年」の堤幸彦。竹中直人と吉永小百合が夫婦役を好演している。島原鉄道の社長であり郷土史研究家の康平は、和子がNHK福岡で担当する歴史番組に招かれる。康平は島原で観光バスによる新ビジネスを考えており、和子をバスガイドの指導員として島原に来るよう誘うのだった。", "2008-11-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さわこの恋２ 1000マイルも離れて", "さわこの恋２ 1000マイルも離れて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "美しい脚を持ち、“脚タレ”として売れっ子になりながら、逆にそれがコンプレックスとなって悩む藤谷さわこは、仕事から逃げ出してしまう。ルームメイト募集の記事を見て知り合った泰子と意気投合、居酒屋で身の上話をするさわこ。それを後ろで聞いていたのが、人気TVドラマの脚本家・吉田新平。彼は、さわこの話をもとにドラマを１本書き上げるのだったが……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハイキック・ガール！", "ハイキック・ガール！", "", "", "http://image.eiga.k-img.com/images/movie/54383/315x210.jpg?2190", 0, "松村空手道場へ通う圭は、天性の運動神経でどんな技でも軽々とこなすスーパー女子高生。しかし、師匠の松村に地味な型稽古ばかりやらされる茶帯の圭は、師匠に認めてもらえない悔しさから、強豪の空手家たちに挑戦してその黒帯を奪う“黒帯狩り”を始める……。", "2009-05-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カフーを待ちわびて", "カフーを待ちわびて", "", "", "http://image.eiga.k-img.com/images/movie/53713/315x210.jpg?2788", 121, "第1回「日本ラブストーリー大賞」を受賞した原田マハのデビュー小説を映画化。主演は「手紙」の玉山鉄二、「山のあなた／徳市の恋」のマイコ、監督は「ハブと拳骨」の中井庸友。ゆったりとした時が流れる沖縄の小島で雑貨屋を営む明青は、愛犬カフーと幸せに暮らしていた。そんなある日、幸と名乗る女性から「私をあなたのお嫁さんにして下さい」と書かれた手紙が届く。それは昔、明青が縁結びの神社で絵馬に書いたメッセージへの返事だった……。", "2009-02-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO HELL AND BACK", "地獄の戦線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "不幸な生い立ちと小柄な身体というハンディーを乗り越え、第二次世界大戦中の数々の功績により、24個もの勲章を授与された英雄、A・マーフィ。帰国後、映画スターとなった彼が自らの活躍をスクリーンで再現した真実の最前線物語である。父親に家出された少年マーフィは働く母を助けるため、テキサスの農場でつらく厳しい労働の日々を送っていた。1942年、18歳になった彼は空軍と海軍に志願するが、童顔で身体が小さいためか両方とも不合格。ようやく陸軍に入隊し、北アフリカ戦線に送られる。続いてヨーロッパ戦線に参加した彼は、戦友の死や数回の負傷を克服し幾多の戦功をあげた。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("USER FRIENDLY", "それ行け！ BOW‐WOW‐WOW", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1608年、探検家トーマスによって発見された島トカパルに住む人々は、誰もが美しく若さにあふれていた。その秘密は、木彫の犬を使った儀式にあるとトーマスは信じた。それから400年後の現代。その木彫の犬をめぐって、永遠の若さを求める人々が破天荒な争奪戦を展開する。全編、笑いと恐怖と超グロテスクな描写が連続するファンタスティック・ムービー。", "1989-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PAYS D’OU｀ JE VIENS", "遥かなる国から来た男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "カルネが初めて手掛けたカラー映画で、当時、シャンソン界で人気があったベコーを主役に、相手役にはやはり人気絶頂だったアルヌールを配した音楽的要素の強い作品。内気なピアニストになり代わって遠い国から来た男、給仕女の恋の行方を描くラブ・ストーリー。", "1956-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Forgetting Sarah Marshall", "寝取られ男のラブ♂バカンス", "", "", "http://image.eiga.k-img.com/images/movie/53683/315x210.jpg?4579", 111, "「40歳の童貞男」のジャド・アパトウが製作を務め、アパトウ作品の脚本を手掛けてきたニコラス・ストーラーが初監督したラブ・コメディ。「無ケーカクの命中男／ノックトアップ」のジェイソン・シーゲルと人気TVシリーズ「ヴェロニカ・マーズ」のクリステン・ベルが主演、ジェイソンは脚本も担当。退屈な日々を送る2流作曲家のピーターは、仕事で知り合い長年交際していた女優のサラに突然の別れを切り出される。そんなピーターは傷心旅行のハワイで、新しい恋人とバカンスを過ごすサラに出くわしてしまい……。", "2008-12-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("烏鴉与麻雀／CROWS　AND　SPAROWS", "カラスと雀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1949年、国民党支配末期の上海。横町のビルのオーナー、侯は、国民党政権が危なくなってきたのを見て、このビルから住人を追い払い、ビルを売って逃亡しようと考える。それを知った住人たちは……。解放直前の上海の雰囲気を濃厚に伝える傑作で「春の河、東へ流る」のチョン・チュンリーの独立監督デビュー作。", "1949-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔像", "魔像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "原作は『丹下左膳』で知られる林不忘の同名小説。美貌の妻を持ったがゆえに周囲からねたまれ、それがもとで人を斬った喬之助。彼が剣士十数名を向こうにまわし、戦う様を喬之助を助ける謎の浪人の活躍を交えて描く。阪妻の晩年を彩る豪華キャストの時代劇。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTO THE NIGHT", "眠れぬ夜のために", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "J・ランディス監督が初めて挑んだニュー・サスペンス。不眠症に悩んで眠れぬ夜のドライブに出た一人のエンジニアの前に、美女が救いを求めてきた。しかし彼女のバックには大掛かりな国際密輸組織が……。D・ボウイ、D・エイクロイド、R・ヴァディムらが、お遊び的に登場するのも見どころの一つ。シャレたタッチとゴージャスな大人のムードが加味された、ランディス監督が新境地を見せた一編。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOOD WIFE", "普通の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "オーストラリア映画界のホープ、ケン・キャメロン監督による長編第３作。1939年、恐慌に揺れるオーストラリアの小さな町を舞台に、貞淑な人妻マージが他の男との狂おしい愛欲の世界に溺れていくさまをじっくりと描く。", "1986-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NECESSARY ROUGHNESS", "スーパー・タッチダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "フットボールの名門高校の選手たちが薬物使用で全員追放され、残ったのは三軍選手ただ一人。チームを再建するために雇われた元名コーチはまず、スカウトを開始するが……。問題児、落ちこぼれの集団がどんどん勝ち進むという定石通りながら、やはり痛快な一作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セクシー地帯〈ライン〉", "セクシー地帯〈ライン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "石井輝男監督による犯罪劇“地帯（ライン）”シリーズの一編。恋人を殺され、さらに犯人の濡れ衣を着せられた商事会社社員、吉岡。偶然出会った女スリ、真弓とともに真相究明に乗り出した彼は、亡き恋人が関わっていた秘密クラブの存在を知り、その実態を追及する。石井作品の常連である、当時の人気グラマー女優、三原葉子が女スリをお色気たっぷりに好演。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボールZ　地球まるごと超決戦", "ドラゴンボールZ　地球まるごと超決戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "主人公・悟空をはじめ、個性的なキャラクターが見事なまでに弾ける、大人気スーパー・アニメの劇場版シリーズ第6弾だ。謎の宇宙人集団が地球征服を狙って襲来してきた。彼らは“神精樹”という全てを砂漠に変えてしまう木を、地球に植え付けようと画策していたのだ。悟空たちは必死に応戦するが……。DVDは『DRAGON BOX THE MOVIES』に収録。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄昏流星群／同窓会星団", "黄昏流星群／同窓会星団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "人気漫画家・弘兼憲史が『ビッグコミックオリジナル』に連載中の“黄昏流星群”シリーズの一編を映画化。妻を亡くした43歳のサラリーマン・砂川は、高校の同窓会で当時付き合っていた美智子と再会。お互いに独身であることを知り、二人は恋心を再燃させる。だが、それぞれの息子たちが、両親の交際を知らずに友情を育み……。伴侶を失って孤独を抱えていた中年の男女が恋に落ち、再び輝きを取り戻していく姿をしっとりと描く大人のラブ・ストーリー。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛する", "愛する", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "新生日活の第１弾製作作品として発表された真摯な人間ドラマ。『海と毒薬』『深い河』の映像化で遠藤周作作品にゆかりの深い熊井啓が、遠藤の小説『わたしが・棄てた・女』をもとに、ハンセン病をめぐる少女の愛と青春を描いている。製綿工場を経て、銀座のクラブで働いていた少女、森田ミツは、ある日腕に赤いアザを見つけ、大学病院で診察を受ける。病名はハンセン病だった。療養所へ移されることになった彼女は、失意のうちに恋人とも別れ、絶望感にさいなまれる。やがて診断が誤診と分かるが、診療所内の人々の温かい心に触れたミツは、そのままそこに留まろうとするのだった……。遠藤周作は酒井美紀が主演に決まった直後に、この世を去った。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STUART LITTLE2", "スチュアート・リトル2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "大ヒットを記録したファミリー・ピクチャーの続編。リトル家の一員として穏やかな日々を送るスチュアートは、悪の権化のファルコンに襲われ傷ついた小鳥のマーガロを助ける。マーガロを介抱するうちにスチュアートは今までに感じたことのないような不思議な感情を抱くが……。CG技術の向上により前回30万本の毛で覆われていたスチュアートの体毛は50万本に。毛並みの光沢の変化も表現され、よりリアルで可愛らしいヒーローの誕生と高い評価を得た。", "2002-07-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪塔傳", "怪塔傳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "鶴田浩二が、一人３役を演じた娯楽時代劇。18年前、将軍家の跡目争いのもつれから、将軍吉宗を狙撃し、失敗して果てた尾張藩士・安財数馬。その子、芳太郎は上田主殿によって命を助けられ、成人したら１万石で尾張藩へ復帰させるという、お墨付きをもらう。このお墨付きが隠された紅絵をめぐって、偽の芳太郎を仕立てようとする白塔一味、謎の青年剣士、菅野新四郎が戦いを繰り広げる。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Red 2", "レッド2（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大地の詩　留岡幸助物語", "大地の詩　留岡幸助物語", "", "", "http://image.eiga.k-img.com/images/movie/56083/315x210.jpg?3863", 116, "社会福祉法人北海道家庭学校の創始者で、児童自立支援に奔走した実在の人物・留岡幸助の人生を描いたドラマ。幼い頃から不平等な身分社会に苦しんできた幸助は、神学校を卒業し、牧師になる。やがて北海道・空知にある監獄の教誨師（きょうかいし）になり、犯罪の芽が幼少期に発することを悟った幸助は、少年たちの感化教育に力を注いでいく。「石井のおとうさんありがとう」「筆子・その愛　天使のピアノ」の山田火砂子監督作。村上弘明、工藤夕貴らが出演。", "2011-04-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Leo", "レオポルド・ブルームへの手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "アイルランドの文豪ジェイムズ・ジョイスの名作『ユリシーズ』に大幅な脚色を加え、孤独な少年、息子を愛せない母親、元囚人という3人の主人公のやり場のない思いを切なくも情緒豊かに綴る。大胆な発想から生まれた新たなストーリーは、名作とはまた違った深い感動を与えてくれる。囚人スティーヴンは、見知らぬ孤独な少年レオポルドへの手紙だけが心の支えだった。刑期を終え、彼に会う日を心待ちにしていたスティーヴンだが……。", "2005-02-19", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXCALIBUR", "エクスカリバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "イギリスのアーサー王伝説を映画化。湖の精霊から授けられた王者の剣“エクスカリバー”を手にしたアーサーが、円卓の騎士を従えて統一国家建設を目指す……。派手な特撮はないが、魔術と殺りくの“暗黒の時代”を詩情あふれる映像の積み重ねで幻想的に描き上げる。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜明け前", "夜明け前", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "“木曽路はすべて山の中である”という有名な書き出しの島崎藤村の同名小説の映画化。幕末の木曽の山中、馬篭の宿場町を舞台に、庄屋・青山半蔵の波乱に満ちた生涯を重厚なタッチで描く。半蔵に扮する滝沢修以下、劇団“民芸”総出演による歴史ドラマ。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWICE IN A LIFETIME", "燃えてふたたび", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "平穏な家庭を持ちながら人生の黄昏時を迎えた50歳を過ぎた男が、身を焦がすような恋に落ちる。家族の分裂も顧みず、恋に身を投じる男の苦悩と、誇りと、決断を描いた辛口のラブ・ストーリー。G・ハックマンが人生の酸いも甘いもわきまえた名演技を見せる。ポール・マッカートニーのタイトル曲も印象的。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARIS WHEN IT SIZZLES", "パリで一緒に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "脚本家の書くシナリオ通りにストーリーが進行するという面白い構成。フランスのJ・デュヴィヴィエ監督の「アンリエットの巴里祭」(1952)をヒントに作られた、巴里祭を舞台にした恋の物語。ホールデンの一人三役、ヘプバーンの二役が見ものの楽しめる一編。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Road to Nowhere", "果てなき路", "", "", "http://image.eiga.k-img.com/images/movie/57243/315x210.jpg?4236", 121, "アメリカン・ニューシネマの伝説的作品「断絶」（1971）のモンテ・ヘルマン監督が、「ヘルブレイン　血塗られた頭脳」（89）以来21年ぶりに手がけた長編監督作。若手映画監督を主人公に、映画製作の過程とそこで起こる事件を描く。実在の事件をもとにした映画の製作を始めた若きアメリカ人映画監督のミッチェル・ヘイブンは、オーディションで映画のヒロイン役にぴったりな女優ローレルと出会い、恋に落ちる。撮影が進むにつれて2人の仲は深まっていくが、ある日、撮影スタッフが滞在するホテルで銃声が鳴り響き……。", "2012-01-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い傷あとのブルース", "黒い傷あとのブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "小林旭のヒット曲をベースにした日活ムード・アクション。暗い過去を引きずり、復讐の鬼となったヤクザの悲哀を描き出す。主人公とのプラトニックな恋を演じるヒロイン役は当時17歳の吉永小百合。これが彼女と小林旭の唯一の共演作となった。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEX　IS　ZERO", "セックス イズ ゼロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "韓国産ホラー映画「ボイス」で人気女優の仲間入りをしたハ・ジウォン主演。今度は大胆な演技で悩殺するセクシー・コメディ。歌手としても人気のイム・チャンジョン、ミス・コリアンのハム・ソウォン、元スーパーモデルのユン・シフらセクシー美女軍団が登場する。大学のキャンパスを舞台に、エアロビ・サークルのお色気グループとSEXがしたくてたまらない男子学生らとの、過激なトークと行動の数々を繰り広げていく。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・悪名", "続・悪名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "満州事変の頃、大阪へ戻った朝吉とモートルの貞は、吉岡親分を窮地に陥れた松島長五郎を半殺しにする。松島一家の元締めは朝吉の度胸に惚れ込み、長五郎の縄張りと子分を押しつける。そんな折、朝吉に召集令状が届き、彼は意を決して組を解散。その甲斐虚しく、貞はチンピラに刺されて死んでしまう。貞が雨の中で刺されるシーンは見もの。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Rebirth", "愛の予感", "", "", "http://image.eiga.k-img.com/images/movie/34153/315x210.jpg?1498", 102, "「バッシング」で注目を集めた小林政広監督が、少女による同級生刺殺事件の被害者の父と加害者の母の絶望と再生を描き、日本人監督としては37年ぶりにロカルノ国際映画祭グランプリに輝いた人間ドラマ。14歳の少女が同級生の少女を刺殺した。生きる希望をなくした被害者の父・順一と、身を隠すように東京を離れた加害者の母・典子は、偶然にも北海道で再会し……。「殯の森」の渡辺真起子が典子を、小林監督自らが順一を演じる。", "2007-11-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ソクラテス", "ソクラテス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "プロレス界のカリスマ大仁田厚の初主演作。大阪通天閣界隈に今も残る遊廓に居候している通称ソクラテスは、弱い者の味方で、トラブルがあれば首を突っ込んでいた。今日も手形サギにあった印刷屋の社長のために黒松組に乗り込み、大暴れをして取り戻す。ところがソクラテスの男っぷりに組長が惚れ、自分の娘と結婚させ二代目を継がせようと考える。その頃、ソクラテスを慕っていた二人の少女がシャブの売人、立花によってシャブ漬けにされようとしていた。そして、立花は黒松組を乗っ取ろうとも考えていた。ソクラテスは少女たちを救い、黒松組を助けることができるのか？　森崎東監督に助監督として師事していた鶴巻日出雄の監督デビュー作で、森崎が脚本を書いている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妻", "妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "巨匠・成瀬巳喜男監督による「めし」「稲妻」に続く３度目の林文学の映画化。短編小説『茶色の目』を原作に、結婚10年になる平凡なサラリーマンの冷えきった夫婦生活を描く。華麗な令嬢役で名高い高峰が醜悪な妻を熱演、それを受ける上原の抑えた演技も見事。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A・LI・CE", "A・LI・CE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "月へ向かうツアー・シャトルが墜落し、かろうじて機体からの脱出に成功した少女・亜利寿。しかし、彼女が降り立ったそこは、何と30年後の世界だった。やがて彼女は、独裁者が作ったコンピューターによって荒廃した未来世界を救おうとするが……。CGを駆使して描かれるSFアニメーション映画。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飛龍猛将／DRAGONS　ARE　FOREVER", "サイクロンZ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "香港の人さえ足を踏み入れることをためらう香港ダークサイド。そこには、表面的には大企業だが、裏では、密貿易・人身売買・兵器や麻薬の生産をしている企業があった。弁護士ジャッキー・ドラゴンがその企業から弁護を依頼される。工場から流れ出た汚水が、近くの漁場を汚染したと、周辺住人は工場の操業停止に乗り出すが……。訴訟の代表者役のD・イップや、ジャッキーと仕事を頼むサモ・ハン・キンポー、ユン・ピョウが絡みストーリーが展開する。香港映画のエネルギーがみなぎる良質のコメディ＆アクション。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANS　MA　PEAU", "イン・マイ・スキン／人には言えない、私が本当にしたいこと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「まぼろし」「８人の女たち」などでフランソワ・オゾンとともに脚本を手掛けたマリナ・ドゥ・ヴァンの長編デビュー作。フランス公開時にL・ブニュエルやD・クローネンバーグ作品と比較された衝撃作だ。自傷行為によって快感を得る女性の姿を、淡々としたタッチで描出する。仕事も恋も順調なエステルが、ある日、転んで足に怪我をしてしまう。だが、痛みを感じることはなく、傷あとを自ら触って傷を広げていくうち、不思議な感覚に目覚めていく。彼女の自傷行為は、次第にエスカレートしていき……。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANCES WITH WOLVES", "ダンス・ウィズ・ウルブズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 181, "「アンタッチャブル」「フィールド・オブ・ドリームス」などで人気を得たK・コスナーが自ら製作し、監督まで務めた野心作。幼い頃に「西部開拓史」を見て西部の広大な空間に憧れ、いつの日か映画を作り草原を馬で駆け回りたいというコスナーの夢がついに実現。1863年、南北戦争の激戦地で負傷した北軍中尉ジョン・ダンバーは片足を切断されると思い込み、絶望して敵軍の中に単身飛び出すという自殺的行為をとる。が、ジョンは生還。思わぬことから英雄となり、足の傷も回復したジョンは勤務地の選択権を与えられ、開拓のフロンティアを見てみたいという望みをかなえるためにダコタ最西部の砦に赴く。しかしジョンが着いてみると砦には誰もいない。インディアンに襲われたのか？　一人で砦にとどまるジョンがひと月も過ごした頃、馬泥棒のインディアンが現れる。お互いに悪意がないと知ったジョンとインディアンの一族は、言葉が通じないながらもそれぞれを理解しようと近づき合っていく。後に未公開シーンを追加した４時間の完全版も公開された。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忠臣蔵外伝 四谷怪談", "忠臣蔵外伝 四谷怪談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "“忠臣蔵”と“四谷怪談”。この二つをミックスさせた異色の青春ラブ・ストーリー時代劇。民谷伊右衛門を赤穂浪士の一人に設定。その彼が、金と名誉のために恋人・お岩を捨て、浪士としての義を捨てて堕ちくいく様を、現実と幻想が交錯する映像のなかに描き出している。深作欣二監督としては、「赤穂城断絶」（1978）以来の“忠臣蔵”ものだ。お岩に扮した高岡早紀の体当たりの演技が見どころ。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES ZOZOS", "上級生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "フランスの片田舎の寄宿舎で生活する思春期の少年たちをさわやかに描いた作品。性に目覚めはじめた少年たちは、休暇を利用して憧れのスウェーデンに旅立つ……。彼らのズッコケ・セックス旅行を、「夫たち、妻たち、恋人たち」のトマ監督が屈託なくのびやかに活写。愛すべき青春映画。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("素晴らしき日曜日", "素晴らしき日曜日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "雨の日曜日、金もなく戦後の焼跡も生々しい街に出かけた若い恋人たち。楽しいはずのデートはことごとく厳しい現実に遭遇し、みじめな気持ちで落ち込むが、次第に明るい夢をとり戻していく。「わが青春に悔なし」に続く黒澤明監督作品。監督の持つ本質的な優しさ、感性の瑞々しさが素直に表現された印象深い作品である。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水のないプール", "水のないプール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "女房もちの地下鉄職員。昼はキップ切りや掃除、夜はうるさい女房の相手と、単調な日々の連続。だがそんな彼に、突然夢のような日々が訪れる。女の部屋にマスクをつけて忍び込み、大量のクロロホルムをまくと、女が意識を失い好きなように犯せることがわかったのだ。やがて男は職業も捨て去り……。内田裕也が狂気の主人公を演じた話題作。タイトルの“水のないプール”の映像が象徴的に使用されている。脇役陣もバラエティー豊か。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAGANINI", "パガニーニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「アギーレ・神の怒り」「ノスフェラトゥ」「フィツカラルド」などのヘルツォーク作品の主演でおなじみの怪優K・キンスキーが主演・監督・脚本の３役をこなす作品。キンスキー扮する18世紀前半に活躍したヴァイオリニスト、パガニーニの死に至る６年間の軌跡を綴る。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あずみ２／Death or Love", "あずみ２／Death or Love", "", "", "http://ecx.images-amazon.com/images/I/21DK21XSCGL._SL210_.jpg", 112, "連載が始まって10年以上経つ現在も読者から圧倒的な支持を受ける、小山ゆうの同名マンガを映画化した時代劇アクションの第２作。闘いを宿命づけられた刺客、あずみの運命が描かれる。前作に引き続き上戸彩があずみ役を等身大で体現、ますます磨きのかかった殺陣の演技は前作以上に見事だ。また、「ガメラ」シリーズの金子修介監督が、アクションとドラマのパートのバランスをうまくとり、ストーリーを構成。闘いに身を置く若者たちの心を深く見つめた、青春時代劇アクションに仕上がっている。徳川方の刺客となり闘いを続けるあずみは、残る標的の真田昌幸を追う。これに真田は最強の忍び、空如が率いる“上野甲賀衆”を雇い対抗。それでも使命のため、あずみは闘いを挑む。", "2005-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦場の女たち", "戦場の女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "第二次大戦で最も南の日本軍激戦地となったパプア・ニューギニア。ラバウルの地で知られる同戦線の女たちにスポットを当て、侵略された現地の女たち、日本軍の“従軍慰安婦”という日本やアジアの女たちの証言を、埋もれようとする戦史から浮上させたドキュメンタリー。監督の関口典子は、撮影前５年にわたり現地に住みついて土地の人々と生活をともにし、戦争が女たちに残した禍根を引き出すのに成功している。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門捕物帖 緋鹿の子異変", "右門捕物帖 緋鹿の子異変", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“片眼狼”に続くシリーズ２本目の中川信夫作品。猿若座の舞台で一座の花形役者が変死した。現場には縫い針や薬紙などの遺留品。さっそく、捜査に乗り出した右門は座内の隠された人間関係を暴いていく……。中川はあまりうまくいかなかったようで、本作を最後にシリーズから降りている。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLINK", "ブリンク　瞳が忘れない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "角膜移植をしたばかりの女性エマが、おぼろげな視界の中で連続殺人犯を目撃。だが、警察は彼女の不確かな証言を信じようとしない。やがて第２、第３の猟奇的な殺人事件が起こり、エマの身にも危険が迫る……。M・ストウが、恐怖にさらされるヒロインを熱演したサスペンス。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸福 Shiawase ", "幸福 Shiawase ", "", "", "http://image.eiga.k-img.com/images/movie/53933/315x210.jpg?1336", 106, "イラクで起きた日本人人質事件を題材にした「バッシング」の小林政広監督が描く、傷ついた大人たちのラブ・ストーリー。主演は「キッズ・リターン」「世界で一番美しい夜」の石橋凌、舞台女優で本作が映画初出演の桜井明美。北海道の勇払駅、閑散とした街のスナックで働く女が、公園で行き倒れている男を見つける。女は男を家に連れて帰り、一夜を共にする。会話もない2人の奇妙な共同生活が始まる。", "2008-09-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モスラ３・キングギドラ来襲", "モスラ３・キングギドラ来襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "新“モスラ”シリーズの完結編。日本の怪獣史の中で最強と言われるキングギドラと死闘を繰り広げる。１億３千年前に恐竜を絶滅させたキングギドラが再び地球に現れ、子供たちを連れ去っていく。地球の守護怪獣モスラが立ち向かうが、歯が立たない。そこでモスラはキングギドラがまだ若かった１億３千年前の白亜紀に旅立ち、子供たちを救うために戦いを挑む。１作目でレインボー・モスラ、２作目で水中モード・モスラと変身のバリエーションを増やしてきたモスラが、この３作目では強敵キングギドラを倒すために戦闘的な“鎧モスラ”に大変身を遂げる。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪太郎伝・悪い星の下でも", "悪太郎伝・悪い星の下でも", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "昭和初期の河内、父は飲んだくれだが自らは秀才という青年・鈴木重吉が、二人の少女の間で揺れるさまを、叙情的に描いた鈴木清順の佳作。「悪太郎」に始まり、「けんかえれじい」で結晶する清順流青春映画の中間を成す作品。美術は名コンビ、木村威夫。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Italian Job", "ミニミニ大作戦（1969）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "", "1969-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("安城家の舞踏会", "安城家の舞踏会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "安城家は、代々の名門華族。だが第二次大戦終結とともに、他の華族同様、没落の憂き目にさらされていた。家は抵当に入れられ、かつてこの家のお抱え運転手であった現運送会社社長からは令嬢・昭子に堂々と求愛される始末。これまでの価値観ではまったく通用しない時代がやってきたのである。そんな安城家の最後の舞踏会を通じて、180度違う新たな時代の到来を描いた、吉村公三郎の代表作。新藤兼人の脚本を得て、吉村は決定的な名声を確立する。全体にどこか日本離れした設定は、新藤が底辺にチェーホフの『桜の園』を利用したため。しかし、それがいささかも非現実的ドラマに陥らなかった点に、彼の真価があるといえよう。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FALL OF THE ROMAN EMPIRE", "ローマ帝国の滅亡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 180, "古代ローマ帝国を舞台に、異民族との戦乱に悩むアウレリウス皇帝を描くスペクタクル史劇。S・ブロンストン製作による70ミリの超大作で、A・ギネス、J・メイスン、O・シャリフ、S・ローレンらが出演するオールスター映画。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幕末青春グラフィティ Ronin坂本竜馬", "幕末青春グラフィティ Ronin坂本竜馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "武田鉄矢が“生涯の師”と仰ぐ坂本竜馬の激動の青春時代を、自ら熱演する大河時代劇。土佐藩脱藩後、長州の高杉晋作率いる奇兵隊と力を合わせ、４千の兵で幕府15万の兵と戦うまでを描く。高杉晋作に吉田拓郎、伊藤博文に伊武雅刀など、異色のキャスティング。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴースト　もういちど抱きしめたい", "ゴースト　もういちど抱きしめたい", "", "", "http://image.eiga.k-img.com/images/movie/54873/315x210.jpg?3315", 0, "世界的ヒットを記録した「ゴースト　ニューヨークの幻」（90）の舞台を日本に移したアジア版。松嶋菜々子と「宿命」のソン・スンホンが主演を務め、ウーピー・ゴールドバーグが演じた霊媒師役に樹木希林。「リング」「呪音」シリーズなど多くのJホラー作品を手がけてきた一瀬隆重がプロデュースを務める。会社経営者の七海はある日、陶芸の勉強をするため日本に留学している青年ジュノと出会い、恋に落ちる。しかし2人の幸せな生活が始まった矢先、七海は事件に巻き込まれ命を落とす。七海の魂は天国に行くことを拒否し、ゴーストとなってジュノのそばに留まることを選ぶ。", "2010-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOUT　OF　ASIA", "シャウト・オブ・アジア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "各国のミュージャンを訪ね、アジアの今を探る韓国のカリスマ・ミュージシャン、カン・サネの旅を追ったドキュメンタリー。朝鮮半島の分断に加え、テロや大国の支配など現実の中で生きる思いを刻んだ、“歌”の数々に熱いものを覚えずにはいられない。国境を越えたセッションの風景も感動的だ。アジア各国を訪ね歩くカン・サネ。日本、フィリピン、中国など各国のアーティストと交流を重ねる彼は、やがて新しい歌を生みだす。", "2005-04-23", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UM FILME FALADO", "永遠〈とわ〉の語らい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ポルトガルの巨匠M・ド・オリヴェイラの監督作。９.11のテロ事件をきっかけに、母と娘の船旅を通して西洋文化の展望を描く。インドにいる夫に会うため、娘と船旅に出たローザ。歴史教授の彼女は、地中海をめぐり、娘に人類の歴史を実際に見せたかったのだ。ある夜、船長に夕食へ招かれた二人。そこでは異国籍の女性たちが各々の言葉で語り合っていた。", "2003-01-01", "25,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TALL IN THE SADDLE", "拳銃の町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "見知らぬ町に雇われてきた牧童頭が、牧場利権やいかさま賭博に巻き込まれて陰謀を暴く、RKO時代のJ・ウェインの西部劇。汽車から颯爽と降りてくる登場シーンに始まり、すべてがキマッていてカッコイイ。女嫌いのウェインが痛快に活躍し、最後は結婚の憂き目に。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("決戦！ 南海の大怪獣　ゲゾラ・ガニメ・カメーバ", "決戦！ 南海の大怪獣　ゲゾラ・ガニメ・カメーバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "銀河の彼方からやって来た宇宙生物が、イカ、カニ、カメに乗り移って、ゲゾラ、ガニメ、カメーバの３匹の怪獣と化した。南洋の孤島にやってきた日本の生物学者たちは怪獣たちに遭遇し、凄絶な戦いに巻き込まれる。怪獣のデザインが少々安易。ビデオタイトルは「ゲゾラ・ガニメ・カメーバ　決戦！ 南海の大怪獣」。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("", "きみにしか聞こえない", "", "", "http://image.eiga.k-img.com/images/movie/1973/315x210.jpg?4164", 0, "", "2007-06-16", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FURUSATO 宇宙からみた世界遺産", "FURUSATO 宇宙からみた世界遺産", "", "", "http://image.eiga.k-img.com/images/movie/55503/315x210.jpg?4105", 0, "ニュージーランド、エジプト、日本にある自然・文化遺産を、その地に暮らす子どもたちの目線で描くデジタル3D映像。「おくりびと」の脚本家・小山薫堂が構成を手がける。宇宙航空研究開発機構JAXAの地球観測衛星「だいち」がとらえた地上700キロからの地球の映像と、ハイビジョンの約4倍もの解像度を誇る4K3Dデジタルカメラによる地上の実写映像を組み合わせ、世界遺産をスクリーンに映し出す。「おくりびと」の本木雅弘の長女・内田伽羅が出演している。", "2010-06-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CARPENTER", "甦える悪夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "古い家を買い取った若い夫婦が、リフォームのため大工を集める。ところがそのなかに、この家に取り憑かれている悪霊の大工がいて、毎夜、電動ノコギリ、ドリル、ハンマーなどあらゆる道具を使って若妻に襲いかかってくる……。大工道具を凶器に使った新手のホラー。", "1988-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Terminator 2: Judgment Day", "ターミネーター2", "", "", "http://ecx.images-amazon.com/images/I/51tByi5YRSL._SL210_.jpg", 137, "大ヒットした1984年製作の「ターミネーター」の続編。時代は、前作から10年後。未来の人間の指導者となる運命を背負ったジョン・コナーも少年に成長。そこに二体のターミネーターが、未来からやって来る。一体は彼を殺す使命を受けた液体金属でできた最新型T−1000、もう一体は、ジョンを守るために送られた前作と同タイプのT−800である。息子を守るため女戦士に変貌したジョンの母サラを加え、人類の未来を懸けた攻防が始まった……。今回、秀逸なのは、どんな物体にも変化する最新型T−1000のSFX。圧倒的に不利な状況の中で、ジョンとT−800に芽生える友情も見もの。L・ハミルトンの、男顔負けのアクションは、「エイリアン２」のシガニー・ウィーヴァーを彷彿とさせる。長尺版も発売された。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギララの逆襲　洞爺湖サミット危機一発", "ギララの逆襲　洞爺湖サミット危機一発", "", "", "http://image.eiga.k-img.com/images/movie/53493/315x210.jpg?2180", 98, "「ヅラ刑事」「日本以外全部沈没」など奇想天外な話題作を発表し続ける奇才・河崎実監督が特撮怪獣パニックに挑戦。怪獣ブーム真っ只中の67年に松竹が製作し現在もカルト的人気を誇る「宇宙大怪獣ギララ」を現代に蘇らせ、過激な風刺ネタを散りばめつつ、巨大怪獣の襲来によりパニックに陥った世界を活写する。加藤夏希、加藤和樹が主演を務めるほか、水野晴郎、ビートたけしら随所に登場する意外なゲストからも目が離せない。", "2008-07-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スクラップ・ストーリー　ある愛の物語", "スクラップ・ストーリー　ある愛の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "すべてが平等で、セックスも例外でない、と主張する14歳の少女・康子と大学生の“ボク”、それにビニ本カメラマン・良介の３人の奇妙な同棲生活を描く。気ままに暮らす少女役に、当時写真集やビデオ作品で人気のあった少女Mが扮し、タレント陣が脇を固める。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADVENTURES OF PRISCILLA，QUEEN OF THE DESERT", "プリシラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "３人のドラッグ・クィーン（女装のゲイ）たちが織り成す、コミカルで愛情あふれるロード・ムービー。性転換者のバーナッド、バイセクシュアルのミッチ、若くて世間知らずのフェリシアは年齢も人生経験も生き方も異なるショー・ガール３人組。シドニーから砂漠の真ん中にあるリゾート地でショーをするため“プリシラ号”と名付けたバスで3000キロの旅へと出発する。小さな町でのゲイへの偏見やアボリジニたちとの交流など、様々な出会いを通して彼らは本当の自分を知るのだった……。バーナッド役の個性派俳優T・スタンプが、華麗で気品に満ちあふれた演技を見せている。1994年カンヌ映画祭で上映され、高い評価を得、アカデミー賞でも主演男優賞をはじめ７部門でノミネートされ、衣装賞を受賞した。", "1994-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUFFALO BILL", "西部の王者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "アメリカ西部史に実在したバッファロー・ビルことウィリアム・F・コディの半生を描いた西部劇。鉄道建設を進める上院議員と、それを快く思わないインディアンとの争いの中に割って入ったバッファロー・ビル。ラブ・ロマンス、インディアンとの戦いと、見せ場もたっぷりで充実した出来を見せている。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRIFTERS", "流し網船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "“ドキュメンタリー”という名称の生みの親 J・グリアソンがイギリスのドキュメンタリー運動の先駆けとして発表した力作。ニシン漁船の操業と、帰港後の陸揚げの記録が意図的に芸術的構成美を排して描かれていく。対象を的確に捉えることから得られる生命感の中に、本当の美を見いだした記録映画のお手本。", "1929-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帰って来た極道", "帰って来た極道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "破天荒な暴れっぷりと、強烈な個性で見事に主演スターにカムバックした若山富三郎。久方ぶりにシャバに戻ってきた島村清吉は、古巣がすっかり天誠会に支配されていることを知り、仲間を集めて次々に縄張りを荒らし始める……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゼブラーマン　ゼブラシティの逆襲", "ゼブラーマン　ゼブラシティの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/54823/315x210.jpg?4208", 106, "幻のTVヒーローに憧れる冴えない男教師を描いた、哀川翔主演「ゼブラーマン」（04）の続編。前作に引き続き、監督を三池崇史、脚本を宮藤官九郎が務める。「時をかける少女」（10）の仲里依紗がゼブラーマンの宿敵ゼブラクイーンに扮する。2025年、全てに白黒をつける無法都市・ゼブラシティ。かつてヒーローとして地球を救った市川新市は記憶を失い、理由も分からぬまま警察に追われていた。一方、ゼブラクイーンとして人気を博すスーパーアイドルが、密かに世界征服を企んでいた。", "2010-05-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("変幻紫頭巾", "変幻紫頭巾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "名匠・加藤泰が脚本を執筆、当時、チャンバラ活劇で鳴らした工藤栄一がメガホンを執った。時は、天明４年春。老中田沼意次の策略で江戸市中にニセ紫頭巾が出現。江戸市民は、その悪業に紫頭巾を憎み出す。しかし、報竜太郎こと本物の紫頭巾の活躍で田沼の悪事は白日のもとにさらされ、すべては一見落着、紫はいずこともなく去って行く……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新網走番外地・流人岬の血斗", "新網走番外地・流人岬の血斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "マキノ雅広に続き降旗康男が監督。社会復帰への足がかりとして、造船所で働く囚人たちを扇動して暴動を起こさせようとたくらむ矢頭組に、怒りに燃えた末広勝治が殴り込む。降旗康男はこの後第13作をのぞくすべてのシリーズを監督する。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METALSTORM: THE DESTRUCTION OF JAREDSYN", "メタルストーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "砂の惑星レムリアを舞台に、世界征服をたくらむ闇の帝王シンと、未来世界の平和の守護者ジャックの死闘を描いた3D映画（公開時は2D)。一つ目種族の登場や、空飛ぶバイクに乗っての追跡シーンが見もの。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海に降る雪", "海に降る雪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "芥川賞作家・畑山博が描いた都会の片隅で知り合った男と女の同棲物語を、「八甲田山」などの助監督だった中田新一が映画化した作品。小説発表後、女優の出演志望や企画申し込みが殺到し、一時は原田美枝子が自ら主演・監督して製作に入ったというエピソードも。美保純の好演が光る。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jonas Brothers: The 3-D Concert Experience", "ジョナス・ブラザーズ　ザ・コンサート 3D", "", "", "http://image.eiga.k-img.com/images/movie/54443/315x210.jpg?3608", 76, "全米中のティーンから絶大な人気を誇るポップ・ロック・バンド“ジョナス・ブラザーズ”のコンサートや裏舞台の映像を、3Dで上映する音楽ドキュメンタリー。2008年、ジョー、ケビン、ニックの3兄弟からなる“ジョナス・ブラザーズ”は「バーニング・アップ」コンサート・ツアーを行い、100万人以上を動員した。そのツアーの模様と、バック・ステージ映像で3人の素顔に迫る。", "2009-06-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武闘の帝王", "武闘の帝王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "溝口敦の同名小説をベースにしたバイオレンス・アクション。武闘派組長をドラマの中心に据え、総長のポストを争うヤクザ組織の血まみれの内部抗争を描く。対立組織に組長を狙撃され、傘下のボス・加賀は愛人を潜入させて敵方の会長の首をとる。しかし、加賀の勢いを快しとしない一派が組織の内部にいて、火種は思わぬ方向に……。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガールスカウト", "ガールスカウト", "", "", "http://image.eiga.k-img.com/images/movie/54853/315x210.jpg?4431", 99, "「私の名前はキム・サムスン」のキム・ソナが3年ぶりにスクリーン復帰を果たしたクライム・コメディ。あらゆる財テクに手を出しては失敗ばかりしている30代のミギョン、無職の息子を養うためにスーパーで働く60代のイマン、夫を亡くし息子の手術費用工面に奔走する40代のポンスマン、一攫千金を夢見て宝くじに熱中する20代のウンジ。投資していた金を持ち逃げされてしまった彼女たちは、一致団結して犯人を追う。", "2009-12-05", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・座頭市物語", "新・座頭市物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "故郷に帰った市は、剣の師匠である弥十郎の妹・弥生と結婚しようとするが、弥十郎は許さない。金に目がくらみ悪の道に走った弥十郎に、市は捨て身の一撃を加える。この作品からシリーズはすべてカラーとなった。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KOSH BA KOSH", "コシュ・バ・コシュ　恋はロープウェイに乗って", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「少年、機関車に乗る」のB・フドイナザーロフによる、ユーモラスな恋愛劇。モスクワからタジキスタンへ帰郷した娘ミラは、父に賭博の借金のカタにされていたことを知り怒り心頭。そんな彼女に恋をしたのが若きロープウェイの操縦士ダレル。都会育ちのミラに振り回されつつ、ダレルは必死に彼女を追いかける……。内戦下のタジキスタンで撮影を敢行し、その現状を臨機応変に物語に反映。砲火の音が響く一方で、淡々と続く人々の生活を牧歌的なムードの中に描き、“愛は時や場所を選ばず”という主題を浮き彫りにする。題名はタジクの賭博用語で“勝ち負けなし”という意味で、ここでは主人公の恋模様を象徴。", "1993-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("驚天十二小時／THE LAST BLOOD", "ラスト・ブラッド 修羅を追え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「シティハンター」で、日本でも一躍その名を知られるようになったB・ウォン監督のハード・アクション・ムービー。シンガポールを歴訪する宗教家ダカ・ラマが、空港で日本のテロリストグループの襲撃を受け、その場に居合わせた女性リンとともに凶弾に倒れてしまう。手術には二人とも同一の特殊な血液を必要とした。香港警察から派遣されたルイと、リンの恋人ビィは唯一の輸血提供者を探すべく、限られた時間の中で奔走する……。人気俳優A・ラウが主人公ビィを好演。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("When I Turned Nine", "僕が9歳だったころ", "", "", "http://image.eiga.k-img.com/images/movie/1023/315x210.jpg?2843", 105, "1970年代の韓国。田舎町の小学校に通う9歳のヨミンは、ケンカも強く心優しいガキ大将で、両親のいないギジョンや、ヨミンに想いを寄せるワンパク少女のクムボクたちと、いつも一緒に遊び小学校ライフを満喫していた。そんなある日、大都会ソウルからウリムという女の子が転校してきた。彼女の登場に、ヨミンの心はときめくのだが……。韓国の大ベストセラー小説を映画化したノスタルジックな青春ストーリー。", "2006-02-04", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("童年往事", "童年往事／時の流れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "中国から台湾に移住した“外省人”である阿孝の両親は、『大陸反攻』（本土の争奪）を信じながら、相次ぎ病死。祖母や兄姉と暮らす阿孝は荒れて事件も起こすが、様々な体験を経て成長し、やがて両親たちとは違う“台湾人”としてのアイデンティティを確立していく。台湾激動の50～60年代を舞台に、一少年の目を通して台湾史を個人的に語っていく侯監督の自伝的作品。", "1985-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛇神魔殿", "蛇神魔殿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "推理作家・高木彬光の『蛇神様』を映画化した怪奇ミステリー風時代劇。江戸の夜に跳梁する黒覆面の一団によって豪商の一族が殺されていく。噂では蛇神様のたたりだという。その謎に挑むのは、風流若様・伊織とスリの十六夜お蓮、そして名奉行大岡越前守だ。監督は前年デビューですでに５本目の工藤栄一。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白い船", "白い船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "島根県の小学校の実話をもとにした人間ドラマ。島根県平田市の小学６年生、好平は教室の窓から、沖を行く白い船を目撃。調べてみると、それは定期フェリーであることが判明。担任の女性教師、静香の提案で生徒たちは船長宛の手紙を書き、海と陸の交流が始まる。やがて、近くで船を見たくなった好平は祖父の漁船を駆って海に出るが……。船に思いをはせる子供たちの心情を温かく描写。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DR.GIGGLES", "Dr.ギグルス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "偽者の医者が巻き起こす、恐怖の惨事を描いたゴシック・ホラー。生きた人間の心臓をえぐり出したことで、死刑となった医者。その息子エバンは、事件が元で発狂し、精神病院に入れられた。その後、自分を医者と思い込むようになった彼は病院を脱走し、自家製の手術道具を手に虐殺を繰り返していく。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRE AND ICE", "ファイアー&アイス 白銀の恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "スキーを始めたばかりのジョンは、白銀のゲレンデでテクニック抜群のスージーに出会い一目惚れする。それから一年。練習に練習を重ねたすえにプロ・スキーヤーになった彼は、ある日マンハッタンの街角でBMWに乗ったスージーを目撃する。スキー・シーンが見もののラブ・ロマンス。", "1987-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFFLICTION", "白い刻印", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "R・バンクスの『狩猟期』を「タクシードライバー」「救命士」の脚本家P・シュレーダーが映画化したヘビーな人間ドラマ。田舎町に住む警官ウェイドにとって人生は厳しかった。アル中の父親の暴力に怯えて暮らした子供時代、現在は妻と離婚。そんなある日、州の要人が狩りの最中に事故死する。背後に陰謀があると疑った彼は独自に捜査を始めるが……。運命を変えようともがけばもがくほど、惨憺たる方向へと事態は急変していく。ビデオタイトルは「アフリクション／白い刻印」。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("“X” ： THE MAN WITH THE X−RAY EYES", "X線の眼を持つ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ザビア博士の発明した目薬には、透視能力を与えるという副作用があった。自らを実験台にした博士は、徐々に強力になる透視能力に耐えきれず、ついには眼球をえぐり出そうとする。透視による奇怪な世界と、博士の歪んだ精神を視覚化した映像がショッキング。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大菩薩峠・竜神の巻", "大菩薩峠・竜神の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "京の島原での宇津木兵馬との対決の後、机龍之助は天誅組と行動をともにする。そのとき追っ手の投じた爆薬のために盲目となり、竜神の森へと逃亡。しかし、またしても兵馬に挑まれた龍之助は、断崖から足を滑らし滝壷へと姿を消す。DVDは「大菩薩峠　DVD-BOX」に収録。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOUR−SIDED TRIANGLE", "複製人間の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ドクター・ハーヴェイに可愛いがられていた美少女レナと、少年ロビンとビルは、10年後それぞれに成長し、故郷へ戻ってきた。大学で化学を学んだロビンとビルは、ドクターの援助を受け、さらに研究を続ける。それは生物をそっくりコピーしてしまう完全複製機を作ることだった。やがてレナが二人の研究を手伝うようになり、彼女はロビンを愛する。自分もレナを愛しているビルは、複製機でレナの人造人間を誕生させた。が、レナの人造人間は容姿だけでなく記憶もコピーされていた——人造人間もロビンを愛していたのだった……。恐怖映画の代表、英ハマー・フィルムの中でも“ミスター・ハマー”といわれるT・フィッシャーの演出が冴えるSF映画。", "1953-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAKED WARRIORS", "エンジェル・ウォリアーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ローマのコロシアムでは女奴隷同士による殺し合いが連日行われていた。しかし、殺されるのを待つだけの運命にさらされる女たちは、ついに反逆の戦いを始める。迫力のアマゾネス軍団が肉弾戦を繰り広げるセクシー・バイオレンス。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本暴力団・組長", "日本暴力団・組長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "沖中組の代貸・塚本は、８年の刑を終えて出所。組員の死とともに彼は組長に就任する。関東連合会と反目するシマ争いから組員が殺され、塚本はいよいよ殴り込みに……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sirta La Gal Ba", "風のささやき", "", "", "http://image.eiga.k-img.com/images/movie/54983/315x210.jpg?4617", 77, "フセイン政権下のイラク北部を舞台に、ひとりの郵便配達人の目を通してクルド人虐殺を描いた人間ドラマ。長年に渡ってクルド地域の山村を周ってきた郵便配達人バルダーは、武装ゲリラの指揮官から「生まれてくる子供の産声を録音してきて欲しい」と依頼される。彼は指揮官の家族が暮らす村を訪れるが、妊娠中の妻や子供たちはすでに遠く離れた渓谷へと避難しており……。監督は本作が長編デビューとなるイラン出身のシャフラム・アリーディ。", "2009-10-18", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LISA", "銀幕のメモワール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "フランスの若手スター、ブノワ・マジメルと大女優、ジャンヌ・モローが共演。第二次大戦とともに消息を絶ったユダヤ人の銀幕スターと恋人の間に隠された意外な真実が語られる。新人映画監督のサムは消息不明の戦前のスター、シルヴァン・マルソーを追うドキュメンタリーの制作に着手。残された少ない手掛かりから彼の恋人と思われるリザを見つけ出したサムは、彼女から思わぬ事実を知らされる。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARIA　FULL　OF　GRACE", "そして、ひと粒のひかり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "無名のコロンビア人女優がアカデミー主演女優賞にノミネートされて話題を呼んだ、感動の青春ドラマ。麻薬の運び屋に雇われた少女が過酷な体験を通じて成長していく姿を描く。主演のC・S・モレノの瑞々しい存在感と、社会派でありながら詩情に満ちた新人監督マーストンの演出が素晴らしい。妊娠した上に仕事も失ってしまった17歳のマリアは、切羽詰まって麻薬の運び屋になる。やがて彼女は胃の中にドラッグを隠し、N.Y.に飛び立つが……。", "2005-10-15", "1,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひとりかくれんぼ　劇場版", "ひとりかくれんぼ　劇場版", "", "", "http://image.eiga.k-img.com/images/movie/54553/315x210.jpg?1756", 0, "“現代のこっくりさん”として知られ、遊び方や体験談がインターネット上で広まった“ひとりかくれんぼ”を題材にしたホラー。ある日、深夜に行う降霊術“ひとりかくれんぼ”で人を呪い殺せるという噂を耳にしたりつ子は、父親を殺したいと話す同級生の相川に、一緒に試してみようと持ちかける。", "2009-05-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桜桃", "さくらんぼ　母ときた道", "", "", "http://image.eiga.k-img.com/images/movie/53573/315x210.jpg?1402", 107, "チャン・イーモウ監督作「初恋のきた道」で世界中を感動させた脚本家パオ・シーが、自らの幼少時代の体験を基に描いた母と娘の物語。美しい棚田が広がる雲南省の農村。知的障害を持つインタウは、暗い夜道で見つけた赤ん坊を家へと連れ帰る。赤ん坊にホンホンと名付けて大事に育て始めるインタウだったが、やがて成長したホンホンは、自分の母が他の人と違うことを恥ずかしく思うようになり……。インタウ役に「鳳凰／わが愛」のミャオ・プゥ。", "2008-11-01", "8,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Manchurian Candidate", "クライシス・オブ・アメリカ", "", "", "http://image.eiga.k-img.com/images/movie/1043/315x210.jpg?1288", 130, "湾岸戦争の英雄レイモンド・ショーは政界に乗り出し副大統領候補に。だが、戦争時に彼の上官だったマルコの脳裏には、ショーが語る戦争での出来事とは異なる記憶が甦ってくる。自分の記憶は間違っているのか。マルコは調査を始め、その背後にある巨大企業の陰謀を知る。原作はリチャード・コンドンの「影なき狙撃者」。同じ原作を62年にジョン・フランケンハイマー監督、フランク・シナトラ主演で映画化したのが「影なき狙撃者」。", "2005-03-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丘は花ざかり", "丘は花ざかり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1963年に吉永小百合主演でリメイクもされた、石坂洋次郎原作の青春メロドラマ。東洋評論社で働く香月美奈子は、姉・信子の家に同居していた。日々退屈をもて余す信子はPTA役員になり、同じ役員の石山に心惹かれていく。美奈子は、親子ほども歳が違う編集長を愛するようになり……。姉妹二組の恋の行方をさわやかに描いている。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHAMUS", "シェイマス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "アクション映画に異才を放つキューリック監督が、中年の私立探偵を主役にして描く都会派ハードボイルド。探偵マッコイのもとへ久しぶりにダイヤ盗難事件の捜査依頼が飛び込み、仕事を開始するが……。レイノルズのクールな探偵ぶりが出色のハードボイルド。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN INNOCENT MAN", "イノセントマン 仕組まれた罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "平凡な会社員ジミー・レインウッドは、刑事たち自らが犯したミスが原因で、まったく身に覚えのない罪を着せられ、３年間服役させられてしまう。凶悪な囚人たちに囲まれた生活は、ジミーの人生観を一変させる。やがて保釈されたジミーは、えん罪を晴らし元の生活を取り戻すため、復讐を開始する……。ジミーを演じるトム・セレックが「スリーメン＆ベビー」などのコミカルなイメージから一転しシリアスな演技をみせる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アマルフィ　女神の報酬", "アマルフィ　女神の報酬", "", "", "http://image.eiga.k-img.com/images/movie/54253/315x210.jpg?2511", 125, "開局50周年を迎えたフジテレビが、「踊る大捜査線」の織田裕二を主演に迎え、全編イタリアロケで製作したサスペンス大作。イタリアでのテロ予告を受け、ローマに派遣された外交官の黒田康作（織田）は、日本人少女誘拐事件に遭遇し、この事件に巻き込まれていく。誘拐事件はやがて、イタリア全土を襲う大規模テロへと発展していくが、黒田は事件の鍵が南イタリアのアマルフィにあることを知り、この街へと向かう。共演に天海祐希、戸田恵梨香、佐藤浩市ら。監督は「容疑者Xの献身」の西谷弘。", "2009-07-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男一匹ガキ大将", "男一匹ガキ大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "３度の飯より喧嘩が大好きな西海高校の暴れん坊番長・戸川万吉を主人公に、網元の娘・トモコとの恋や、西海高校乗っ取りをたくらむ東栄高校の番長・松川との対決を描いた青春喧嘩アクション。原作は『週刊少年ジャンプ』誌に連載された本宮ひろ志の人気劇画。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Heros", "変態ピエロ", "", "", "http://image.eiga.k-img.com/images/movie/54043/315x210.jpg?3247", 115, "テレビ局でエンターテイナーとして働くピエールは、仕事上では明るいキャラクターだったが、私生活では不眠に悩まされていた。ある日、彼は幼少の頃から憧れていた国民的歌手を誘拐し、アパートに監禁してしまう。監督はフランスの奇才ブルーノ・メルル、出演は「潜水服は蝶の夢を見る」のパトリック・シェネ、「クレールの刺繍」のジャッキー・ベロワイエら。", "2008-11-29", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEELS OF TERROR", "第27囚人戦車隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "兵役につくことで赦免された犯罪者や反体制者たちだけで作られたドイツの戦車隊。ドイツの小説をデンマークのプロデューサーがアメリカ人の監督で映画化。大作ではないが戦車隊の描写などなかなかリアルで、苛烈な戦場における男の友情が描かれている。", "1986-01-01", "1,18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪い奴ほどよく眠る", "悪い奴ほどよく眠る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "黒澤プロ設立第1作として監督が選んだテーマは、当時社会問題となっていた政治汚職。汚職事件の隠蔽工作により自殺に追い込まれた男の息子による復讐劇を通して、政界に根深くはびこる腐敗の構造にメスを入れた意欲作。極めて社会性の強いテーマでありながら、スリルとサスペンスを盛り込むことで十二分に娯楽映画として通用する作品。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("竹山ひとり旅", "竹山ひとり旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "３歳の頃、はしかをこじらせて半失明となってしまった高橋竹山の、津軽三味線に懸けた半生を描いた伝記映画。竹山自身が画面に登場してインタビューに答え、それから劇化された物語が展開するという、ドキュメンタリーとフィクションの枠を取り払った意欲作。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE NIGHT STAND", "ワン・ナイト・スタンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「リービング・ラスベガス」のM・フィギスが、ニューヨークを舞台に描くラブ・ストーリー。美しい妻と二人の子供に恵まれたマックスは、一流のCMディレクター。５年ぶりに再会した親友チャーリーがHIVに感染していることを知りショックを受けた彼は、それから間もなく、偶然知り合った女性カレンと運命的な恋に落ちる。１年後、末期症状となったチャーリーを見舞いに行った病院で、マックスは兄の妻となったカレンと再会する。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FALL TIME", "男たちの危険な午後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "アメリカ中西部の田舎町。高校を卒業したてのティムら３人は、最もインパクトのあるいたずらで世間をあっといわせようと計画を立てた。それは、父親の車に乗って15マイル離れた町の銀行の前へ行き、空砲でティムを射殺したふりをし、車のトランクに投げ込んで逃げる、というものであった。計画は成功したかに見えたが、トランクから出てきたのは、本物の銀行強盗のレオンであった。いたずらのせいで強盗を邪魔されたレオンと仲間のフローレンスは彼らに拷問を始め、代わりに銀行を襲うように脅迫する。どこか「スリーパーズ」（1996）を思わせる犯罪青春映画。M・ロークがスゴミを利かせている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女子学園 ヤバイ卒業", "女子学園 ヤバイ卒業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "純潔教育を主張する学園の生徒が乱交パーティに参加したことが発覚し、学園側は当の生徒を即刻退学処分に。しかし、この事件に刺激された忠江らが、ローティーンのセックス感度調査なるものを開始、そうこうするうち大人たちのみだらな性関係も明らかになってきて……。コミカルな味をプラスした、シリーズ第２作。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Killers", "キス＆キル", "", "", "http://image.eiga.k-img.com/images/movie/55553/315x210.jpg?2161", 101, "アシュトン・カッチャー＆キャサリン・ハイグル共演のアクション・コメディ。政府に雇われている凄腕の殺し屋スペンサー（カッチャー）は、南仏ニースでの勤務中にアメリカからの旅行者ジェニー（ハイグル）と出会い、すぐに恋に落ちる。結婚して3年、普通に暮らしていた2人だったが、ある日ジェニーが帰宅すると、スペンサーが急襲される。30歳になった自分の命に高額の賞金が賭けられていることを知ったスペンサーはジェニーとともに逃亡を図るが……。監督は「男と女の不都合な真実」のロバート・ルケティック。", "2010-12-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばり十八番 弁天小僧", "ひばり十八番 弁天小僧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "院主玄照和尚が私腹を肥やすための犠牲となり、人殺しの汚名をきせられ役人に追われる身となった寺小姓の菊之助。江戸へ逃げ帰り、生みの母親を頼るが、ただ一人の肉親は懸賞金ほしさに我が子を密告するのだった。世を呪い人を呪った菊之助は、名を弁天小僧と改め、人生の裏街道を歩き始める。当時、歌謡界のトップ・スターだったひばりが男まさりの演技を見せた会心作。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2 Days in Paris", "パリ、恋人たちの2日間", "", "", "http://image.eiga.k-img.com/images/movie/53283/315x210.jpg?2162", 101, "「恋人までの距離」「ビフォア・サンセット」のジュリー・デルピーが監督・脚本・主演を務めるロマンティック・コメディ。ニューヨーク在住のフランス人女性マリオンとアメリカ人男性ジャックは、恋人同士だが倦怠期を迎えていた。関係を盛り上げようと欧州旅行に出かけた2人は、その帰りにマリオンの実家があるパリを訪れる。言葉が通じない上に、今まで知らなかったマリオンの一面を見て戸惑うジャック。さらにマリオンの元彼が次々と現われ……。", "2008-05-24", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bend It Like Beckham", "ベッカムに恋して", "", "", "http://image.eiga.k-img.com/images/movie/1283/315x210.jpg?2344", 112, "主人公と同じインド系英国人監督チャーダが、サッカー選手になることを夢みる少女を活写。デビッド・ベッカム＆ビクトリア・ベッカムが特別出演。試写をマンチェスター・ユナイテッドの選手たちと見たベッカムは「ものすごく気に入った」とコメント。また、冒頭シーンに元サッカー選手で現解説者のゲイリー・リネカーが特別出演しているのも話題。音楽も、テキサス、メラニーCから、バリー・サグーなどのUKエイジアンまで多国籍のヒット曲が流れる。", "2003-04-19", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NUNS ON THE RUN", "ナンズ・オン・ザ・ラン 走れ！尼さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "一見ビジネスマン風のブライアンとチャーリーだが、実は大ボケ泥棒の二人。ところが、ボスの小言や泥棒稼業にもいいかげん飽きた彼らは、でかい仕事でたんまり金をいただき、ブラジルへの高飛びを狙っている。格好のターゲットが見つかり、いざ決行となるが、いろんな手違いから行き着いた先は、なんと修道院。ボスや香港マフィア、そして警察をも巻き込んでの一大逃亡劇が始まる。モンティ・パイソン一家のE・アイドルと、イギリスの名コメディアン、R・コルトレーンが出演したハチャメチャなブラック・コメディ。監督はTVのコメディ台本で鍛え上げ、舞台演出に小説にと活躍する才人、J・リン。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹下左膳 濡れ燕一刀流", "丹下左膳 濡れ燕一刀流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "幕府隠密総元締の愚楽老人と大岡越前守は、柳生家に巨万の宝のありかを示す、こけ猿の壺があることを知り、費用のかかる日光東照宮御修築奉行を柳生但馬守に命じる。しかし、柳生家の内実は火の車。これにつけ込む別所信濃守の陰謀を、但馬守の弟・源三郎と丹下左膳が打ち砕く。盲目の娘に純な恋心を寄せる、左膳の片想いの話が面白い。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サディスティック・ミカ・バンド", "サディスティック・ミカ・バンド", "", "", "http://image.eiga.k-img.com/images/movie/34243/315x210.jpg?1608", 72, "「タイムマシンにおねがい」など数々の名曲で70年代日本のロック・シーンに新たな風を吹き込み、海外でも高い評価を得たサディスティック・ミカ・バンド。ボーカルに木村カエラを迎えて再結成を果たした彼らが、2007年3月8日にNHKホールで行った1度きりのライブの模様を中心に、メンバーへのインタビューやレコーディング風景などを交えながらバンドの魅力に迫るドキュメンタリー。映画監督・井筒和幸が監修を務める。", "2007-10-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンの豪傑一代男", "エノケンの豪傑一代男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "昭和初期にロッパと並び一世を風靡したコメディアン、榎本健一ことエノケンが戦後主演した時代劇コメディ。ドタバタ調の展開とご都合主義を逆手に取ったユニークさなど、日本のバスター・キートンと呼ばれた彼が面目躍如ぶりを見せる。時代は戦国の世。徳川の家臣である主人公が松平家姉妹の争いの小事から、徳川と織田の対立という一大事まで解決してしまう。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REMBRANDT FECIT 1669", "レンブラント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "オランダの異色監督J・ステリングが、天才画家レンブラントの生涯を映画化。24歳の時、野心に燃えてアムステルダムに移り住み、若くして成功を収めたレンブラントの、あまり幸せとはいえない私生活、芸術的苦悩そして狂気を、バロック音楽をバックに描き出す。光と影のコントラストあざやかな映像美が絶妙な作品。", "1977-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANTONIA", "アントニア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1996年アカデミー最優秀外国語映画賞をはじめ、数々の賞に輝いた人間ドラマの秀作。オランダの美しい田園風景を背景に、４世代に渡る女性たちの大らかな生き方を描き出す。村人たちに慕われるアントニアは、広い心を持つ温かい女性。娘ダニエルに、かわいい孫テレーズも生まれた。しかし、ある少女の兄によるレイプ事件や、宗教の違いで結ばれることのなかった恋人たちの死など、村では不幸が相次ぐ。そんなある日、成長したテレーズに子供が生まれ、村は再び笑顔に包まれる。喜びと悲しみが繰り返される中、アントニアは自分の最期の時を予感する。悲哀とユーモアをにじませて、様々なエピソードを手際よく重ねる演出が見事。世間体や常識にとらわれることのなく築き上げる家族の絆、性別を超えた愛情の中に、生きることの素晴らしさを浮き彫りにする。ビデオ発売時タイトルは「アントニアの食卓」。", "1995-01-01", "70,37,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GENDARME SE MARIE", "ルイ・ド・フュネスの大結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "今は亡き、フランスの人気コメディアン、L・ド・フュネスの代表作。“警官”シリーズの「大混戦」「ニューヨーク大混戦」に続く第３作。サントロペ分署に勤める巡査長が、元警視長の未亡人と恋に落ちたことから街は上を下への大騒ぎとなる……。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("潮騒", "潮騒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "三島由紀夫の同名小説の２度目の映画化で、吉永小百合と浜田光夫の純愛コンビが主演。ギリシャの牧歌的な恋物語『ダフニスとクロエ』を下敷きにした原作は、荒々しい自然を背景にギリシャ的な美しい肉体を持った男女の素朴な愛が描かれているが、吉永小百合と浜田光夫の都会派コンビもまたフレッシュ。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAILOR BEWARE", "底抜け艦隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "D・マーティンとJ・ルイスの名コンビがシリーズ第５作目にして、日本初登場した記念すべき作品。女好きのアルと女嫌いのメルヴィンは、そろって海軍に入隊したが、要領のいいアルに対してメルヴィンは失敗ばかり。そんな彼らが航海訓練をすることになって……。無名時代のJ・ディーンが脇役出演している。コンビ作品は全16本ある。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AS　YOU　DESIRE　ME", "お気に召すまま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "大女優G・ガルボが「グランド・ホテル」と同年に主演したラブ・ストーリー。イタリアの文豪L・ピランデッロの戯曲をもとに、戦争で行方不明になった伯爵の妻そっくりの容貌を持つ歌手ザラが、周囲の思惑を乗り越えて彼との真実の愛に目覚めていくさまを綴る。映画監督としても活躍したE・V・シュトロハイムが、ヒロインを奪回しようとする作家役で助演している。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROWING WITH THE WIND", "幻の城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "19世紀の初頭。19歳のメアリーは、ロマン派詩人シェリーと駆け落ちをする。異母妹クレアを伴い、クレアの愛人である放蕩詩人バイロン卿の城にやって来た彼女は、一編の怪奇小説を書き始める。ホラーの古典『フランケンシュタイン』の作家メアリー・シェリーと二人の詩人らがたどった数奇な運命を、妖しく幻想的に綴った作品。", "1988-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふたりはプリキュア／　マックスハート", "ふたりはプリキュア／　マックスハート", "", "", "http://ecx.images-amazon.com/images/I/31RH6VDAXRL._SL210_.jpg", 72, "人気の美少女アニメがスクリーンに初登場。“光の使者プリキュア”となって活躍する二人の女の子に加え、新メンバーが登場し、劇場版としてよりパワーアップした内容になっている。希望の園の女王様役を務める工藤静香の声の出演にも注目。世界で一番美しいダイヤモンドを守っている希望の園。なぎさとほのかは、それを狙う悪のドックゾーンに立ち向かうため、希望の園へと旅立つ。", "2005-04-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOLLY DEAREST", "ドール・ハウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「チャイルド・プレイ」をはじめとする人形ホラーの中の１本。こちらの人形は“ドリー・ディアリスト”という女の子。考古学者によって誤って解き放たれた悪魔が、その“ドリー人形”に乗り移り、そして惨劇が……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Insider", "インサイダー", "", "", "http://image.eiga.k-img.com/images/movie/1833/315x210.jpg?2456", 158, "CBSの人気報道番組「60ミニッツ」のプロデューサー、バーグマンの元に匿名でタバコ会社の極秘書類が届けられた。バーグマンはこの書類を調べ、ワイガンドなる人物に行き当たる。ワイガンドは大手タバコ会社、B&W社の研究開発部門の副社長だったが、上層部と対立し解雇されていた。彼の握る秘密とは、タバコ業界の存在を根底から崩し得る決定的な証拠だった。ワイガンドがマスコミと接触したと知ったB&W社は、ワイガンドと彼の家族に圧力をかけ始める。", "2000-05-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Post Grad", "恋する履歴書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドン松五郎の生活", "ドン松五郎の生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ワープロを打つ天才犬・ドン松五郎が“すべての犬は人間の言葉が分かる”と打ったことから、人間たちがパニックに陥る様子をユーモアたっぷりに描いた作品。雑種のドンをはじめ、多種多様な犬の集団が疾走するシーンはなかなかスリリング。西村知美も愛らしい。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピーピー兄弟", "ピーピー兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "過激な漫才師コンビを主人公にしたコミカルな人間ドラマ。ストリップ小屋の舞台に立つ売れない漫才師コンビ、イクオとタツオの兄弟は下品な芸を買われて、TVに進出。ピー音で消された過激な発言が視聴者の大反響を呼び、彼らは一躍、人気者となる。しかし、より過激なネタを求められた兄弟が、実録ポルノ漫才をネタにしだしたことから、彼らの関係に亀裂が生じ……。大衆の下世話な好奇心を利用するマスコミを風刺。一方で、人と人とのつながりをとらえた温かい人情味が魅力となっている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bungaku on Film: The Lover's Exile", "文楽　冥途の飛脚", "", "", "http://image.eiga.k-img.com/images/movie/56143/315x210.jpg?2269", 87, "1979年、京都・太秦撮影所に作り上げた舞台セットで、竹本越路大夫、竹本文字大夫（現・竹本住大夫）、竹本織大夫（現・竹本綱大夫）ら人間国宝に指定された昭和を代表する名人たちの出演による近松門左衛門の人形浄瑠璃「冥途の飛脚」を上演。その模様をカメラに収めた。完成後、劇場公開されないまま長らく上映機会を失っていたが、2011年、オリジナルフィルムからデジタルリマスターを行い、32年の時を経てスクリーンで上映された。", "2011-03-05", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arthur Christmas", "アーサー・クリスマスの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/55373/315x210.jpg?4309", 101, "イギリスの名門アードマン・スタジオが米ソニー・ピクチャーズとタッグを組んで手がける3DCGアニメーション。北極の氷の下にはサンタクロースのオペレーションセンターが存在し、100万人の妖精がハイテクマシンを駆使して世界中の子どもたちにプレゼントを届けていた。しかしある年のクリスマス・イブ、1人の子どもにプレゼントを配り忘れるミスが発生。サンタの末っ子アーサー・クリスマスが、地球の反対側にある子どもの家までプレゼントを届けにいくことになるが……。", "2011-11-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オクトパス・アーミー シブヤで会いたい", "オクトパス・アーミー シブヤで会いたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "若者の街・渋谷を舞台に、夏休み最後の夜を過ごすストーリート・キッズたちの青春群像を描いた作品。実在する街やショップの中で等身大の若者がスクリーンに登場。そこに対立し合うキッズ同士の抗争や失踪したアイドルの一夜の恋、淡い恋心を抱く少年と少女のふれあいなどが同時進行で繰り広げられていく。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANIKA−MANIKA", "マニカの不思議な旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1930年代にインドで起きた実話をもとにした、輪廻転生がテーマの異色のラブ・ストーリー。インドの小さな漁村に住む娘マニカは、自分の前世を記憶していた。教師のダニエル神父は、彼女の眼を現実に向けようとするが、マニカは、前世の夫を捜す旅に出てしまう。ダニエルは、彼女を追うが……。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YEKBAR BARAYE HAMISHEH", "これを最後に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "妻のザーラと離婚し、日本に出稼ぎに行こうと考えたマハムードは、ザーラとともに裁判所に申請に行くが、妊娠をしていない証明書を持ってくるように言われる。二人が病院に行き検査をすると、ザーラが妊娠していることが判明。チケットも手配し、どうしても日本に行きたいマハムードは、病院を探し出しザーラに中絶させようとする。そこに、半狂乱の男が入り込んで来て、中絶のせいで妻が亡くなったと叫ぶ。それを聞いてマハムードは、自分にとってはザーラが一番大事だとあらためて思い病院を出る……。", "1992-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGELIQUE", "アンジェリク・はだしの女侯爵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "フランスのベストセラー小説を映画化し、大ヒットを記録した５作品のうちの第１話。17世紀のフランスを舞台に美しいアンジェリクの激動の半生を描いたこの歴史ロマンは、日本でも木原敏江のコミックや宝塚の舞台劇となった。由緒はあるが貧しい男爵の娘アンジェリクは恋人と引き裂かれ、変わり者のジョフレ侯爵と結婚させられる。彼女は次第に彼の真心に惹かれていくが、その矢先ジョフレは不当な裁判で火刑に。やがてかつての恋人ニコラと再会し、失意から立ち上がるアンジェリクだったが、そんな彼女に再び悲劇が襲いかかる……。", "1964-01-01", "2,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEFF", "ジェフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "A・ドロンが自ら製作・主演したフィルム・ノワール。監督は「さらば友よ」で、男の友情を情感たっぷりに描いてみせたJ・エルマン。宝石商を襲い、多額のダイヤモンドをせしめた暗黒街の住人たち。だが、そのなかの一人、ジェフが仲間を裏切ったことから、報復のための冷たい戦いが始まる……。", "1969-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("証人 Beast Stalker", "ビースト・ストーカー　証人", "", "", "http://image.eiga.k-img.com/images/movie/57813/315x210.jpg?1522", 108, "「密告・者」（2010）のダンテ・ラム監督とニコラス・ツェーのタッグで08年に製作されたクライムサスペンス。逃走中の犯罪者の車に銃弾を撃ち込み身柄を押さえた刑事トンだったが、車のトランクに閉じ込められていた少女が、トンの放った銃弾に当たり命を落としていた。少女は事件を担当していた女性検事アンの娘で、このことがきっかけでトンは心に深い傷を負う。しかし3カ月後、今度はアンのもう1人の娘リンがトンの目の前で誘拐される事件が発生し……。", "2012-04-07", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER JUNGE TO¨RLESS", "テルレスの青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "V・シュレンドルフの長編デビュー作。寄宿学校の醒めた生徒テルレスは、仲間たちのいじめに立ち会う。傍観者の立場を貫く彼だったが、加害者の残酷さにも被害者の卑屈さにも我慢できなかった……。ドイツの暗い歴史を浮き彫りにしたともとれる少年期の残虐な欲望をリアルに描く。", "1966-01-01", "7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAMLET", "ハムレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "シェイクスピアの四大悲劇の一つ『ハムレット』を、舞台を現代に置き換えて映像化した人間ドラマ。2000年のニューヨーク。マルチ・メディア企業の社長が死去した直後に、未亡人となった妻ガートルードは、夫の殺人容疑者で後継者の義弟クローディアスと再婚する。留学先から帰国した映画監督志望のハムレットは、父の死に疑惑を抱く。やがて相思相愛のオフィーリアや友人たちの心配をよそに、ハムレットは奇行を繰り返すようになっていく。E・ホークとJ・スタイルズが、全く新しい解釈によるハムレットとオフィーリアを好演。原作に忠実な古典的なセリフと、スタイリッシュな映像や現代的なファッションのミスマッチ感も斬新。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地球交響曲　ガイアシンフォニー　第五番", "地球交響曲　ガイアシンフォニー　第五番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "", "2004-08-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE JONES", "ラブ・ジョーンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "カメラマン志望の勝ち気な娘ニーナと、ちょっとキザな新進作家ダリウス。シカゴのナイトクラブで出会った二人が、ケンカでスレ違いながらも心惹かれていく様を綴るラブ・ストーリー。当時28歳の新人監督T・ウィッチャーが、都会に暮らす若き黒人の男女の関係を洗練されたセンスで描いている。ラップやヒップホップではなく、ムーディなソウル・ナンバーを全編に散りばめた音楽、素直になれない恋心を等身大の魅力で表現した若手俳優の演技も印象的。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋は緑の風の中", "恋は緑の風の中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "中学２年生の純一は同学年の雪子が好きになる。しかし雪子は引っ越ししていくことに。純一は雪子をなんとかして自分のそばに引きとどめようとするが、結局彼女は去ってしまう……。豊満な裸体が美しい原田美枝子のデビュー作で、最後までみずみずしい演出力を保った家城巳代治監督の遺作となった。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の幡随院", "花の幡随院", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ことごとく対立する旗本無頼・水野十郎左衛門を頭目とする白柄組と、町奴・幡随院長兵衛の一家。長兵衛一家の食客となった白井権八は、子分・極楽十三とともに吉原に遊びに行った折、白柄組と出会い乱闘になる。その時、十三は誤って水野の仲間・惣平を斬ってしまい50日の牢入りとなった。その頃、権八を仇と狙う別の兄弟がいて……。多彩に展開する任侠時代劇の佳品。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOUNG LOVE（LEMON POPSICLE ７）", "グローイング・アップ7　恋の卒業パーティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "人気シリーズの８作目（番外編があるので数字は７)。例によってベンジー、ボビー、ヒューイの３人は、アメリカン・ポップスにのって、海辺でナンパの大騒ぎ。９年間変わらない出演者たちも、さすがにトウが立ってきた。", "1987-01-01", "46,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヱヴァンゲリヲン新劇場版：？", "ヱヴァンゲリヲン新劇場版：？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PICKLED PUNK", "PICKLED PUNK", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "1993年のぴあフィルムフェスティバルで賛否両論を巻き起こし、結局審査員特別賞に落ち着いた問題作。ある男の暴力と殺人の道行きを、衝撃的かつ美しいショットの数々で写し出している。人生相談の声をナレーションに用いたり、「みちのくひとり旅」のパンク・ヴァージョンを効果的に挿入したりと、並外れたセンスの爆発が迫力を生んでいる。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE ON ONE", "ワン・オン・ワン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「ジェレミー」で人気を博したR・ベンソンが、20歳の時初めて書いた脚本に自ら主演した青春映画。大学のバスケット部にスカウトされ、コロラドの田舎町からやって来た高校の花形選手が、チームの練習にとけ込めず悩みながらも家庭教師の愛を得て、チームを勝利に導くまでを描く。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROOTS，ROCK，REGGAE", "ROOTS、ROCK、REGGAE／ルーツ・ロック・レゲエ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "レゲエが世界的に広まった1978年に製作された伝説のドキュメンタリー。レゲエ発祥の真実と、自由を求めるラスタマンたちのスピリットを浮き彫りにする。故ボブ・マーリィの全盛期のステージの模様などの貴重な映像は音楽ファン必見。ボブ・マーリィやジミー・クリフ、ジョー・ヒッグスらのレゲエ・アーティストがパフォーマンスを披露。さらにジャマイカのゲットー事情や政治・宗教を紹介し、レゲエの精神性に迫る。", "2006-04-29", "77,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Terminal", "ターミナル", "", "", "http://image.eiga.k-img.com/images/movie/1193/315x210.jpg?3129", 129, "スピルバーグ監督とトム・ハンクスが「プライベート・ライアン」「キャッチ・ミー・イフ・ユー・キャン」に続き3度目の顔合わせを果たした感動ドラマ。東ヨーロッパの小国からニューヨークの空港に着いたナボルスキーは、祖国で起きたクーデターのためパスポートが無効になり入国を拒否される。だが、彼にはどうしてもニューヨークで果たさなければならないある誓いがあった。彼は入国を目指して空港内に住み着き、さまざまな人々と出会っていく。", "2004-12-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オルガン", "オルガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "劇団・赤テントの看板女優であり、「鉄男／TETSUO」で衝撃的なヒロインを演じた藤原京が不二稿京と名を改めて初監督した作品。闇の臓器売買屋を追う沼田刑事はある夜アジトに潜入し、瀕死の青年が生きながらに臓器を取り出される残虐な光景を目にする。逮捕の混乱のなかで部屋に火がつき、沼田は一味を取り逃がすばかりか相棒の刑事も見殺しにしてしまう。数日後、生徒の失踪事件が起きた都内の某女子高校。その生物教師は、あの臓器売買屋で青年の腹を裂いていた男だった。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEVILS", "肉体の悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "17世紀のフランス。ルーダンの町には好色な司祭グランディエがいた。戒律の厳しい尼僧院の院長ジャンヌはグランディエを愛しているにもかかわらず、強い嫉妬から彼を妖術使いだと訴え、悪魔払いの式が行われることになる……。異才K・ラッセルが性と宗教という禁断のテーマに挑むパワフルな一編。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　WATERMELON　WOMAN", "ウォーターメロン・ウーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「GO fish」の黒人女性監督C・デュニエの長編デビュー作。恋に仕事に情熱を燃やすヒロインに監督自らが扮した自伝色濃い作品だ。レンタルビデオ屋でアルバイトをしながら映画作家を目指す25歳のシェリル。レズビアンの彼女は、1930年代にハリウッドで活躍した伝説の黒人女優に興味を抱き、ドキュメンタリーを製作することを決意する。ところが彼女が“ウォーターメロン”と呼ばれていたことしか分からず、資料集めは一苦労。そんなある日、シェリルはビデオ店の女性客ダイアナと恋に落ち……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本の熱い日々 謀殺・下山事件", "日本の熱い日々 謀殺・下山事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "アメリカの占領下にあった昭和24年７月５日に起こった下山事件を、大胆な推理で“謀殺”とした当時の朝日新聞社の著作をもとに、社会派・熊井啓が描いた作品。国鉄労働者の大量リストラを前にして、下山国鉄総裁が轢死体で発見された。疑問を抱いた記者の追及が始まる……。熱いドキュメンタリー・タッチで目が離せない。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIX PACK", "幸福のチェッカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "自動車修理工だった父を亡くして、引き取る親戚もいない６人の子供たち。ある時、今は落ちぶれたが昔は腕のいいカーレーサーだった男に出会った。子供たちは男に再び勝利のフラッグが振られる日がくることを望みはじめる。D・レインが６人の子供たちの長女を演じてお姉さんぶりを発揮する、心温まる作品。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECRETARY", "仮面秘書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "証券ブローカーを目指している女性秘書デイドラはある日、入社面接に訪れたエレンと知り合う。デイドラの裏工作で、エレンは採用され、ブローカーとして働き始める。表面上は親友のようにふるまう二人。しかし、デイドラには恐ろしいプランがあった。パソコン・ネットでエレンの過去を調べ、自分の昇進のためにエレンの暗い過去を利用しようとしていたのだ。やがて、エレンの周りで事件が続発する……。証券業界が舞台のサスペンス。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍ぶ糸", "忍ぶ糸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 168, "組紐づくりをやりたくて三重県・伊賀の名門、増住家を訪ねた貧しい少女・千賀。最初は相手にされなかったが、家の息子・洋三の口ききで雇ってもらうことに。二人は恋仲になるが、身分が違いすぎると反対されて……という古風な悲恋大メロドラマ。伝統工芸である組紐作りの過程が興味深く描かれている。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Punk's not Dead", "PUNK's NOT dEAD", "", "", "http://image.eiga.k-img.com/images/movie/34163/315x210.jpg?1512", 97, "80年代のDCハードコア・シーンで活躍した女性カメラマン、スーザン・ダイナー監督が自己資金で撮りあげたパンク・ドキュメンタリー。70年代に大ムーブメントを巻き起こしたオリジナル・パンクからハードコア、さらにメロコアやポップパンクまで、時代や音楽性を超えて総勢約100バンドをフィーチャー。インタビューや貴重なライブ映像を交えながら、30年以上に渡って進化し続けてきたパンクの存在意義を探っていく。", "2007-09-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEMOLITION U", "トイ・ソルジャー'97", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "C・ハイム演じる高校生とテロリスト集団の対決を描いた「トイ・ソルジャー'96」の続編となる痛快アクション。あれから一年、リッジフィールド大学に進学したレニーは同級生ジェニーに恋したり、平穏な生活を送っていた。だが、再び彼の前に化学兵器、濃縮VXを扱うテロリスト集団が出現。孤立無援のレニーは奇想天外なアイデアを駆使して、彼らに戦いを挑むのだった。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GABRIELA", "ガブリエラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ブラジルで酒場を経営するナシブは、ある日港で野性的な若い娘ガブリエラに一目惚れし、やがて結ばれるのだが、幸福な日々は長くは続かず……。女豹のような娘にしつこく食い下がる中年男を、M・マストロヤンニが好演した作品。", "1983-01-01", "17,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAN VA SHER", "パンと詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "姉が嫁いだ家で生活しているマジッドは、本を読むことが好きだったが、声を出して読むため、家でも図書館でも嫌がられていた。どうしても『レ・ミゼラブル』を読みたいマジッドは、義兄の本を持っていき、図書館の『レ・ミゼラブル』のカバーと取りかえて持って来てしまう。しかし、義兄の本には小切手がはさまっていたため、大騒ぎになる。マジッドは、本を借りた映画監督を追って撮影現場にまで行くが、そこでマジッドはテレビに出演することに……。アッバス・キアロスタミの「友だちのうちはどこ？」で助監督を務め、その製作過程を描いた『そして映画はつづく』の著者でもあるK・プールアハマッドの監督作品。", "1994-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TATOO〈刺青〉あり", "TATOO〈刺青〉あり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "実際の犯罪をベースにした作品。少年院あがりで“30歳になるまでに何かどでかいことをやったる”という思いを胸に生きる男・竹田明夫。彼の数々の職業遍歴と、三千代という女への異常なまでの執着から、ついには銀行襲撃を実行するに至るまでを、ピンク映画界出身の高橋伴明が粘っこく描き出した話題作。プロデューサーの井筒和幸、脚本の西岡琢也ともどもピンク界出身で、この映画はまさにピンク・パワー爆発の感がある。明夫を演じる宇崎竜童、三千代役で現・高橋伴明夫人の関根恵子らの熱演も見どころ。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHUTTER−KOD TID VINYARN", "心霊写真", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "本国タイで年間興行収入のトップを叩きだした心霊ホラー。オカルトの古典的題材である心霊写真をモチーフに、罪の意識に苛まれる主人公が体験する恐怖を描く。先の読めない物語展開の中、連続する恐怖映像の数々に背筋も凍る衝撃作。友人の結婚式に出席したカメラマンのタンと恋人のジェーンは、その帰り道、突然飛び出して来た女を車で跳ね飛ばしてしまう。その場を逃げたふたりだったが、以来、怪現象に襲われる。", "2006-05-20", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あやに愛しき", "あやに愛しき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "長年の貧困と過労に神経をむしばまれた妻が入院、病状は思わしくなく、ついには狂乱状態に陥る。しかし私小説作家の夫は、どんなに苦しくとも、どんなにつらくとも文学は止められぬと、妻の狂人ぶりを小説に発表しようとする。劇団民芸を主宰していた寺尾聰の亡き父・宇野重吉の第１回監督作。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("玩具修理者", "玩具修理者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 47, "アンティーク・トイが並ぶ店に、ある日サングラスをかけた若い女性が現れる。彼女は店で働く青年に、森の奥でどんなものでも直せる修理者がいたことを話し始める……。NHKディレクター出身のはくぶんが映画初監督した中編映画。美しくノスタルジックながらもどこか不気味なテイストを醸し出す撮影が妙味。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("土俗の乱声", "土俗の乱声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "1988年に公開された同監督の「神々の履歴書」の姉妹編ともいえる長編ドキュメタリー。中国、朝鮮半島、日本における数々の祭事に焦点を当て、そこから日本が今日までに受け継いできた文化や、習俗の根源を映し出していく。映画音楽界の名匠・伊福部昭が「お吟さま」（1978）以来、久々に手掛けた音楽も聴きもの。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グスコーブドリの伝記（1994）", "グスコーブドリの伝記（1994）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "", "1994-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bad Santa", "バッドサンタ", "", "", "http://image.eiga.k-img.com/images/movie/1273/315x210.jpg?4813", 91, "「ゴーストワールド」のテリー・ツワイゴフ監督が、ビリー・ボブ・ソーントンを主演に描く心温まる物語。アル中のウィリーは自分を社会の落ちこぼれだと考え、アルバイトでデパートのサンタクロースに扮しては、強盗を働く常習犯。だが、毎年盗んだ金を使い果たして、またサンタクロースに扮するはめになる。しかし、ボケ老人の祖母と暮らすいじめられっ子の肥満体少年と出会って、彼と交流するうちに、ウィリーの心は変化していく。", "2004-12-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢遊", "夢遊　スリープウォーカー", "", "", "http://image.eiga.k-img.com/images/movie/57363/315x210.jpg?4742", 104, "", "2011-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("携帯彼氏＋（プラス）", "携帯彼氏＋（プラス）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZUPPA DI PESCE", "魚のスープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "映画プロデューサーのアルベルトの一家はにぎやかで仲良し。だが娘アンナの結婚式前日にアルベルトの新作が大コケとなり、家財道具が差し押さえられ、式は台なしとなる。また下の娘イサベラは中年男と不倫に走り……。果たして“魚のスープ”のようなごった煮家族の行方は？　温かい家族愛にあふれたイタリア人情劇。", "1992-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くまちゃん", "くまちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「地球に落ちてきたくま」（1982）等、８ミリ映画時代から描き続けてきた、くまのぬいぐるみにそっくりの宇宙人くまちゃんのキャラクターを登場させた、小中和哉監督のファンタジー。物語は、20歳年下の恋人を持つ、中年の前衛彫刻家・昭雄が、恋人と結婚にゴールするまでを描出。くまちゃんは、二人の恋のキューピッド的な役割をする。川合千春が、ヒロイン役で映画デビュー。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘録おんな牢", "秘録おんな牢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "外界から閉ざされた女だけの世界で、彼女たちの赤裸々な人間性が現れていく様を描いた異色時代劇。乱闘、レズ、リンチ室など牢獄の中ならではの、女囚20数人の倒錯した欲望が渦巻く。安田道代が、無実の罪で入牢した旅芸人の純情娘を好演。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vacancy", "モーテル", "", "", "http://image.eiga.k-img.com/images/movie/53063/315x210.jpg?1840", 85, "殺人ビデオの標的にされてしまった夫婦が体験する恐怖を、ルーク・ウィルソンとケイト・ベッキンセール主演で描いたサスペンス・スリラー。離婚寸前の夫婦デビッドとエイミーは、車が故障したために田舎町の寂れたモーテルに宿を取る。部屋に置かれていたビデオテープをデビッドが再生すると、そこには残虐な殺人シーンが録画されていた。撮影現場が今まさに自分たちがいる部屋であることに気付いた2人は、モーテルから脱出しようとするが……。", "2007-11-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチバンMAX", "ガチバンMAX", "", "", "http://ecx.images-amazon.com/images/I/51rdvw%2ByD2L._SL210_.jpg", 0, "リアルなバトルシーンが人気のヤンキー・アクション「ガチバン」新シリーズの第1弾。歴代シリーズで主人公を演じた窪塚俊介、佐野和真に代わり、「十三人の刺客」などの若手注目株・窪田正孝が主演を務める。東京の西の外れに位置する若王子地区で最強と恐れられるヤンキー・黒永勇人は、不良道を突き進むべく高校を自主退学。ヤンキーの聖地・渋谷で頂点を極めたという不良の先輩・義男のもとを訪れるが……。", "2010-08-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAGNIFICENT MATADOR", "灼熱の勇者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "メキシコの名闘牛士とその後継者のレスエスをめぐる物語。大型画面をフルに生かした構図は闘牛のシーンを巧みに描き出す。社交界の美女に扮するM・オハラも“カラー女優”の呼び名にふさわしい美しさだ。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使行動III　魔女来日／CROSS THESE ANGEL３：HELL　TO　PAY……RETURN　OF　THE　IRON　EAGLE", "天使行動３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ムーン・リー主演のアクション・シリーズ第３作。巨大テロリスト組織“ダガー・チーム”に手を焼いている当局は“アイアン・エンジェル”の一員ムーン・リーに協力を依頼。彼女は日本人女性スナイパーになりすまし、潜入捜査を開始する。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高度7000米 恐怖の４時間", "高度7000米 恐怖の４時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "北海道に向かう飛行機の中に、強盗殺人犯、木田が紛れ込む。木田は拳銃を振り回し、乗客を恐怖に陥れるが、機長が機転を利かし、イチかバチかのトリックを試みる。海岸へ下降すると見せて一気に高度7000メートルまで上昇、木田を気絶させようとするのだが……。手に汗握るサスペンス・ドラマ。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEADLY FORCE", "デッドリー・コップ・クーパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ロス市民を脅かす、猟奇殺人事件に立ち向かうことができるのはストーニー・クーパー、もはや彼だけだ。元警官で鳴らした手腕、超人的精神力と頭脳を駆使し、クーパーと犯人の息詰まるアクションが展開する。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOTHRINGEN！", "ロートリンゲン！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "セザンヌの過激な絵画観に異色のドキュメンタリー手法で迫った「セザンヌ」「ルーヴル美術館訪問」が、ようやく日本でも鑑賞できるようになったD・ユイレ＆J・M・ストロープ監督コンビによるショート・フィルム。国家への帰属というテーマを取り上げたM・バレスの小説『コレット・ボドッシュ』の一部ストーリーを短縮し再構築した上に、小説の舞台となったロレーヌ地方の景観を映し出す構成。", "1994-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("清涼寺鐘聲／THE BELL OF THE QING LIANG TEMPLE", "乳泉村の子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "日中戦争終戦時、中国で生き別れとなってしまった母と幼い赤子。現地の小さな村で老婆に育てられたその赤ん坊は犬坊と名付けられ、聡明な少年に成長。やがて高僧となった彼は日本に渡り、実の母と対面を果たす。シェ・チンが、戦争孤児の問題をテーマにじっくりと描いた感動の物語。", "1991-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIN×NIN　忍者ハットリくん　THE　MOVIE", "NIN×NIN　忍者ハットリくん　THE　MOVIE", "", "", "http://ecx.images-amazon.com/images/I/41ZDDHDPH0L._SL210_.jpg", 101, "藤子不二雄Aの人気コミックを実写で映画化。香取慎吾が持ち前の明るさと愛嬌で、ハットリくんを好演。宿敵、ケムマキをガレッジセールのゴリが、盲目のヒロインを田中麗奈が演じる。主となった少年との友情と活躍を、笑い満載で軽やかに描出。特撮を駆使したユニークな忍術の数々も必見だ。忍者ハットリくんが最後の修行のため東京へやって来る。主となった小学３年生のケンイチ以外には姿を見せてはいけないという忍者の掟に従って、刺激的な毎日がスタート。だが、ケンイチの新担任として宿敵ケムマキが現れる。", "2004-08-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ex", "元カノ／カレ", "", "", "http://image.eiga.k-img.com/images/movie/55423/315x210.jpg?1613", 120, "さまざまなカップルの形を描いた恋愛群像劇。転勤で国境を越えた遠距離恋愛になってしまうカップル、離婚するにあたり互いに子供の親権を放棄しようとする夫婦、元恋人をあきらめきれない警官、結婚間近にかつての恋人に出会ってしまったスーパーの店員など、さまざまな恋愛模様を6つのエピソードで描く。イタリア映画祭2010にて上映。", "2010-04-30", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("結婚しようよ", "結婚しようよ", "", "", "http://image.eiga.k-img.com/images/movie/53313/315x210.jpg?2208", 120, "「半落ち」の佐々部清監督が、自身も大ファンだという吉田拓郎のヒット曲の数々に乗せて綴った家族の物語。家族揃っての夕食が日課になっている香取家の父・卓は、会社帰りに知り合った青年・充を夕食に招く。それからも度々香取家を訪れるようになった充は、やがて長女の詩織と恋に落ちる。それだけでも面白くない卓だったが、さらに次女の歌織もバンド活動で家を空けることが多くなり……。娘たちの親離れに戸惑う主人公を三宅裕司が好演。", "2008-02-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Principal", "暴力教室1988", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "集団リンチ、レイプ、麻薬売買、殺人——泣く子も黙る超危険高校に、校長として赴任してきた、これまた“素行不良”の落ちこぼれ教師リック。生徒たちとリックの“目には目を！”の凄絶な闘いの結末は？　不良教師リックのキャラクターに賛否両論の問題作。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A RAGE IN HARLEM", "レイジ・イン・ハーレム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "獄中デビューしたアメリカの天才黒人作家ハイムズの小説を原作にしたブラック・エンターテインメント・ムービー。1956年のハーレムを舞台に、黄金の入ったトランクをめぐる黒人たちの大抗争を、パワフルに、ドラマティックに描く。マイク・タイソンの元妻であるギブンスの悩殺的魅力、スクリーミン・ジェイ・ホーキンスのライヴを含めた1950年代音楽など、見どころが盛りだくさん。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Last Man Standing", "ラストマン・スタンディング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "　荒野の町ジェリコでは、ドイル率いるアイルランド系組織とストロッジ率いるイタリア系組織の2大ギャングが激しい抗争を繰り広げていた。そんな町に流れ着いた謎の男ジョンはストロッジによって用心棒として雇われることに。ストロッジの情婦から町の事情を探ったのち、ドイルの側に寝返るジョン。彼は2組の組織を激突させ、いずれも殲滅させようとするが……。黒澤明の「用心棒」を、舞台を禁酒法時代のアメリカ西部に置き換えてリメイク。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Moment to Remember", "私の頭の中の消しゴム", "", "", "http://image.eiga.k-img.com/images/movie/1473/315x210.jpg?3913", 117, "「ラブストーリー」「四月の雪」の人気女優ソン・イェジンと「MUSA／武士」のチョン・ウソン共演で描く純愛ストーリー。建設会社の社長の娘スジンは、建築家志望のチョルスと出会って恋に落ちて結婚、幸せな新婚生活を迎える。だが、しばらくするとスジンは物忘れがひどくなり、自分の家への道順も忘れてしまうようになる。病院で診察を受けたスジンは、若年性アルツハイマー症だと診断される。監督はこれがデビューとなるイ・ジェファン。", "2005-10-22", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PLAISIR", "快楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "モーパッサンによる『仮面の男』『メゾン・テリエ』『モデル』の３作の短編を映画化したオムニバス。なかでも娼婦たちが世話をした少女の初聖体拝受式にかけつける『メゾン・テリエ』は、移動撮影を駆使した素晴らしい映像とあいまって名編と語り継がれている。当時のフランス映画界のスターが顔をそろえた。", "1952-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Inglorious Bastards", "イングロリアス・バスターズ", "", "", "http://image.eiga.k-img.com/images/movie/53973/315x210.jpg?3142", 152, "1944年、ナチス占領下のパリ。ナチスに家族を殺された映画館主のショシャナは、ナチス高官が出席するプレミア上映会の夜、復讐を果たそうと計画を練る。一方、ナチス軍人を標的とするアルド・レイン中尉率いる連合軍の極秘部隊「イングロリアス・バスターズ」も、ヒトラー暗殺を企て映画館に潜入するが……。クエンティン・タランティーノ監督が、1978年の「地獄のバスターズ」に着想を得て製作した戦争ドラマ。ブラッド・ピットが主演。ナチス将校、ハンス・ランダ役のクリストフ・ワルツが第62回カンヌ映画祭男優賞、第82回アカデミー助演男優賞を受賞。", "2009-11-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRACKER　JACK　II", "クラッカージャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ロッキー山中、資産家たちを乗せた特別列車がベッカー率いるテロリスト集団に占拠される。彼を追い続ける刑事ジャックはヘリで追跡するが、列車はトンネル内で停車。ベッカーは爆弾で出口をふさぎ、人質たちを政府が建設した核シェルターに監禁する。彼らはハイテクを駆使して資産家の預金をすべて引き出し、皆殺しにするつもりなのだ。ジャックはたった一人でトンネルに侵入、人質救出に向かう。「ビバリーヒルズ・コップ」シリーズでは、頼りない刑事を演じるJ・ラインホールドが執念の男に扮したハード・アクション。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRIPLE BOGEY ON A PAR ５ HOLE", "トリプル・ボギー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ゴルフ場で殺されたレヴィ夫妻。残された個性的な３人の遺児たちの証言や、レヴィ夫人が残したホーム・ムービーをもとに、レポーターが追う事件の真相をユーモラスに描いていく。ドキュメンタリー風の視点で架空の事件を描く、ユニークな一編。アメリカでは「市民ケーン」と比較して論評されることもある。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TANNKA 短歌", "TANNKA 短歌", "", "", "http://image.eiga.k-img.com/images/movie/33733/315x210.jpg?3960", 102, "歌人・俵万智の処女小説「トリアングル」を黒谷友香主演で映画化。33歳のフリーライター薫里は、妻子あるカメラマンMと9年越しの不倫関係を続けている。仕事に恋にと充実した生活を送る薫里だったが、ある日年下のバイオリニスト圭とも関係を持ってしまう。薫里は優しく包み込んでくれるMと刺激を与えてくれる圭の間で揺れ動くが……。作詞家としてこれまで多くのヒットソングを生み出してきた阿木燿子の監督デビュー作。", "2006-11-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARDOR", "密愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "韓国のドキュメンタリー映画界を代表する女流監督ビョン・ヨンジュ初の劇場作品。女性ならではの視点と細やかな演出で、夫の裏切りから立ち直れない妻とどこか影のある青年医師が結んだ、４ヵ月限定の恋愛を濃密に描き出す。「シュリ」のキム・ユンジンが次第に愛に目覚めていくヒロインを好演。その新境地ともいうべき大胆で体を張った熱演は必見だ。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月", "月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "久しぶりに再会した母娘の葛藤を、人情味豊かに描いた人間ドラマ。祖母と死別した高校生の夏海は、離れて暮らしていた母親、遥のもとへ。ストリッパーの遥は突然の娘との生活にとまどい、夏海も母の仕事に反感を隠せない。やがて遥は恋人の借金に、夏海は初恋に、それぞれ思い悩むことに。ストリッパー役に体当たりで挑んだ黒木瞳の熱演も見もの。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRUMPY OLD MEN", "ラブリー・オールドメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「おかしな二人」の名コンビ、J・レモンとW・マッソーの５度目の共演によるラブ・コメディ。隣り合わせに住む老人ジョンとマックスは56年もの間、犬猿の仲を続けている。そんな彼らの家の向かいに、美貌の未亡人が引っ越して来た。彼女のハートをめぐって、老人二人の争いはますますエスカレートする。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("決戦紫禁之城／THE　DUEL", "決戦・紫禁城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「風雲／ストームライダーズ」のアンドリュー・ラウ監督が挑んだアクション時代劇。剣聖と呼ばれる皇族の葉弧城は自分の剣の腕を確かめるために、剣神と恐れられている西門吹雪との紫禁城での決戦を皇帝に申し出て、快諾される。城下が決戦の話題で盛り上がるなか、宦官を巻きこんだ連続殺人事件が発生。宮廷の密偵、龍龍九が捜査にあたるが、そこには決戦とかかわる陰謀が隠されていた。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Margin Call", "マージン・コール", "", "", "http://ecx.images-amazon.com/images/I/51f%2BgxPFYHL._SL210_.jpg", 106, "2008年の世界金融危機（リーマン・ショック）を題材に、ウォール街が崩壊へと向かう緊迫の24時間を金融マンたちの視点から描いた社会派サスペンス。ウォール街の投資会社で大量解雇が発生。その対象となったエリックは、意味深な言葉と共に後輩のアナリスト、ピーターにUSBメモリーを託す。その晩、USBメモリーに記録されたデータを調べたピーターは、会社倒産にも繋がる衝撃の事実を知る。キャストには、ケビン・スペイシーをはじめ実力派俳優が集結。第84回アカデミー賞で脚本賞にノミネート。", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スープ", "スープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELISA", "エリザ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "フレンチ・ロリータ、V・パラディの「白い婚礼」に続く２本目の主演映画。パリで自由気ままな生活を送っていた孤児院育ちのマリーは、ひそかに母と自分を捨てた父への復讐を心に誓っていた。母エリザを自殺に追い込んだという、会ったことのない父を捜す手掛かりは父から母に送られた一枚の絵葉書。そこには灯台の写真と、子供の頃に母がよく聞かせてくれた曲『エリザ』の旋律が記されていた。マリーは拳銃を手に、絵葉書の灯台のある港を目指す……。S・ゲンズブールの同名大ヒット曲を冠したこの映画は、彼に捧げられた作品となっている。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALICE", "アリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "チェコで活躍する人形アニメ作家J・シュヴァンクマイエルによる不思議な迷宮譚。アリス以外は物と人形が使われた、人形アニメーションが展開する。冒険好きな少女アリスが『不思議の国のアリス』を読んでいると突然、はく製のウサギが動き出した。そのあとを追ったアリスは本の通り不思議の国へ……。虫のように地を這う靴下などグロテスクな登場人物(？)たちが見もの。", "1988-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LIEUTENANT WORE SKIRTS", "スカートをはいた中尉さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "カリフォルニアのビバリーヒルズを舞台に、そこに住む映画人たちが軍隊招集騒動に巻き込まれていく姿をコミカルに描いたホーム・ドラマ。TVライターのグレゴリーに招集令状が舞い込み、妻のケイティは夫のあとを追うべく婦人将校に志願する。しかし、実際に入隊したのは、ケイティだけであった……。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("前橋ヴィジュアル系", "前橋ヴィジュアル系", "", "", "http://image.eiga.k-img.com/images/movie/56013/315x210.jpg?2455", 105, "昼間は姉夫婦の農作業を手伝い、いつかはプロのミュージシャンになることを夢みる青年タクシは、ビジュアル系バンド「プリンシパル」を結成して活動していたが、ライブの観客はせいぜい20人。そんなある日、リーダーのジュンが家業を継ぐためにバンドを抜けることとなり……。群馬県前橋市の田舎町を舞台に、ビジュアル系バンドでプロになる夢を追う青年の姿を描くドラマ。俳優・小説家としても活躍する大鶴義丹が原案・監督。", "2011-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN THE BEACH HOUSE", "避暑地のラブ・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "夏の終わり、海辺の貸し切りのビーチハウスを舞台に、そこへ集まってくる人々の出会いと別れを淡々と描いた作品。J・フォスター15歳の時の出演作。家族とともにビーチにやって来た美しい少女が美しい貴婦人に憧れを抱いたり、若者たちと短いアバンチュールを楽しむ。彼女にとってそれは貴重な青春の１ページとなる日々だった。", "1977-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Forrest Gump", "フォレスト・ガンプ　一期一会", "", "", "http://ecx.images-amazon.com/images/I/51jK4yVMutL._SL210_.jpg", 144, "頭は少し弱いが、誰にも負けない俊足と清らかな心をもった男フォレスト・ガンプの数奇な人生を、アメリカ現代史と重ねて描き出していくヒューマンドラマ。監督は「バック・トゥ・ザ・フューチャー」のロバート・ゼメキス。主演にトム・ハンクス。知能指数が人よりも劣っていたが、母親に普通の子どもと同じように育てられたフォレスト・ガンプは、小学校で優しく美しい少女ジェニーと運命的な出会いを果たす。俊足を買われてアメフト選手として入学した大学ではスター選手として活躍。卒業後は軍隊に入り、ベトナム戦争で仲間を救って勲章をもらい、除隊後はエビ漁を始めて大成功を収める。しかし、幼い頃から思い続けているジェニーとは再会と別れを繰り返し……。第67回アカデミー賞で作品賞ほか6部門を受賞。ハンクスは前年の「フィラデルフィア」に続き2年連続で主演男優賞を受賞した。", "1995-02-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SET THE NIGHT ON FIRE：LAMBADA", "ランバダ 青春に燃えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "世界中で大ヒットしたブラジル生まれのダンス・ミュージック“ランバダ”をエッセンスにした青春映画。ビバリーヒルズのエリート校に赴任してきた新任の高校教師。彼はドロップアウト組に勉強を教えるべく、ナイトクラブで“真夜中の教室”を開くのだが……。華麗なダンス・シーンが必見。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本侠花伝", "日本侠花伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 147, "それまで男を主人公とする映画の中で、様々な女を描いてきた加藤泰が、ここでは女を主人公として女のドラマを描いてく。当時、NHKの朝の連続ドラマで注目を集めていた真木洋子を主演に、男たちとの交わりを通して本当の“おんな”へと変貌していくヒロインの姿を追っていく。加藤泰によるオリジナル・ドラマで、浅丘ルリ子を主演に映画化が予定されていたが、真木洋子は気丈でエネルギッシュな女性を見事に演じ切った。受け立つ男優陣は加藤剛、北大路欣也、渡哲也と豪華なキャスト。最後にヤクザ者に恋をしてしまったヒロインは、自分もまた刃の血潮の入り乱れる世界に身を投じていく……。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フレフレ少女", "フレフレ少女", "", "", "http://image.eiga.k-img.com/images/movie/53583/315x210.jpg?2946", 114, "読書が趣味で空想の恋に夢中な乙女チック女子高生・桃子。野球部のエースに初めて本当の恋をした彼女は、彼を見守るため廃部寸前の応援団に入部するが、気合いも全然足りない落ちこぼれ応援団員ばかり。そんな応援団で、桃子はひょんなことから団長になってしまい……。「恋空」の新垣結衣が、男だらけのむさくるしい世界に飛び込み、学ラン＆ハチマキ姿の応援団長に扮する青春学園ムービー。監督は「ラブドガン」の渡辺謙作。", "2008-10-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PURSUED", "追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ウエスタンでも男の孤独と悩みも描かれた、味わい深いアクション劇。アメリカの良心を演じてきたR・ミッチャムが今回は複雑な役に挑戦。20世紀初めの西部劇的舞台となるニューメキシコで、孤児の主人公は父の友人の婦人に育てられて大人になった。そんな彼が婦人の娘と恋に落ちる一方で、婦人の息子を射殺してしまった。図らずも恩人たちと戦う羽目になる。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ノラゲキ！", "ノラゲキ！", "", "", "http://ecx.images-amazon.com/images/I/51id9AItykL._SL210_.jpg", 0, "実直そうな好青年、色気のある女、軍人あがりの中年男、幼い外見のオタク女、ナゾの老人、そして一匹のネコ。とある隔離された施設にいる5人と一匹は、ある日突然、施設の錠が開いて解放される。5人と一匹はなぜ解放されたのか理由もわからないまま、監獄からの脱出を試みるが、なかなか出口が見つからず……。「スチームボーイ」や「鉄コン筋クリート」に携わってきた安藤裕章が監督、「交響詩篇エウレカセブン」の佐藤大が脚本を手がけたオリジナルアニメーション。バンダイビジュアルの新作アニメを劇場で上映する「ANIME FES.“VS”」にて上映。", "2011-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夫婦善哉", "夫婦善哉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "原作は織田作之助の出世作で、「夫婦善哉」とは主人公夫婦が食べに行く法善寺境内のおしるこ屋の名物ぜんざいの名に由来する。監督は「泣蟲小僧」「雁」「雪国」などの文芸ものを得意とする豊田四郎。見事なセットは日本映画美術の第一人者、伊藤熹朔。撮影は「雁」に続いての三浦光雄が担当している。この作品で豊田四郎はスランプを脱し、森繁久彌と淡島千景には、出世作ともいうべき記念碑的作品となった。売れっ子芸者・蝶子は、化粧問屋の息子・柳吉と駆け落ちしたが、柳吉は勘当され二人の生活は困難に。蝶子は臨時雇いの芸者になって生活費を稼ぐが、柳吉が遊びに使ってしまう。やがて柳吉が妹から無心したお金と蝶子の貯金とで関東煮（＝おでん）屋を開店するが、柳吉が肝臓結核にかかったために店を手放す。柳吉が全快すると二人は再び借金してカフェを開く。二人が仲睦まじく法善寺にお参りし、いつものように“夫婦善哉”へ入っていくラストシーンに、取るに足らぬ男と女の二人三脚の情緒がにじむ。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWASHBUCKLER", "カリブの嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "久しぶりに作られたハリウッド版海賊映画。18世紀のカリブ海を舞台にした、海賊船ブラニー・コック号の船長レッドの愛と冒険の物語だ。帆船の疾走、いかにもそれらしき大砲の発砲、海賊たちの軽妙なチャンバラ・シーンと、見せ場いっぱいの娯楽作。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彼女が水着にきがえたら", "彼女が水着にきがえたら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ヒット作「私をスキーに連れてって」のホイチョイ・プロが、今度は海をテーマに作り上げたリゾート・ムービー。OLの真理子は初夏の湘南の海で、宝探しに夢をかける男たちと出会い、いつしか彼らの夢に引き込まれていく。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bound for Glory", "ウディ・ガスリー　わが心のふるさと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 148, "　アメリカの吟遊詩人として知られたフォーク歌手、ウディ・ガスリーの自伝を映画化。1936年、不況と砂嵐が吹きすさぶテキサス州。不満を抱えながら生きるウディは好きな音楽で身を立てたい一心から、年老いた両親と妻子を置いて放浪の旅に出た。貨車のタダ乗りをしてカリフォルニアへたどり着いたウディは難民キャンプに身を寄せ、やがてギターの弾き語りで貧しい人々を勇気づけていくことに……。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WHITE BUFFALO", "ホワイト・バッファロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "幻のスーパーモンスター“白い野牛”に、天才ガンマン、ワイルド・ビル・ヒコックやカスター将軍など、実在したアメリカのヒーローたちを絡ませ、史実に基づいたエピソードを描く、不思議なアイディアの西部劇。当時人気絶頂だったC・ブロンソンの魅力が全開。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("早射ち犬", "早射ち犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "鴨井大介は、ギター片手にその日暮らしの気ままな生活を送っていた。そんな時、同じアパートの運転手・五郎が現金強奪事件のぬれぎぬを着せられる。大介は、さっそく顔なじみの刑事と捜査を開始。新興宗教の天心精霊会が黒幕であることを突き止める。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mer om oss barn i Bullerbyn", "やかまし村の春・夏・秋・冬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "スウェーデンの小さな村で暮らす子どもたちを鮮やかに描き出した傑作「やかまし村の子どもたち」の続編となる作品。前作と同様、原作の作者であるアストリッド・リンドグレーンが自ら手掛けた脚本を、ラッセ・ハルストレム監督が映像化している。家が3軒しかないやかまし村で暮らす6人の子どもたち。クリスマスパーティや子どもたちだけでの年越し、そして子羊の誕生……。彼らが美しい自然の中で過ごす楽しい1年をいきいきと描く。", "1986-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLONDE FIST", "ブロンド・フィスト 炎のチャンプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "女性ボクサーが主人公のファイティング・アクション映画。気性の激しいロニーは、男にだまされ、子供と二人、失業保険で暮らしていた。だが、その保険も打ち切られ逆恨みした彼女は、事務員を殴って刑務所へ。だが、巨漢女ボスと対決して脱走、元ボクサーだった父を頼ってアメリカへと渡る。そこで女ボクシングに参加、１万ドルのファイト・マネーのかかった試合に挑む。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEGALEXANDROS", "アレクサンダー大王", "", "", "http://ecx.images-amazon.com/images/I/41CY7ERAVWL._SL210_.jpg", 208, "牢につながれたひとりの盗賊が手下の導きで脱獄する。漆黒の森の中、白馬の傍らで兜をかぶるとき、彼は“アレクサンダー大王”となった……ある義賊が故郷の小さな村で独裁者へと変貌していく姿をギリシャ歌劇の姿を借りて描きながら、ファシズムの実像に迫りその危険性を告発する、アンゲロプロス監督の壮大な歴史劇。黒澤明監督が感嘆した黒いマントに注目のヴェネチア映画祭金獅子賞受賞作。", "1982-03-20", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若大将対青大将", "若大将対青大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "加山雄三から“若大将”のニックネームをバトンタッチされた大矢茂が２代目若大将として登場する。８年がかりで京南大学を卒業した青大将こと石山新次郎は、美人タイピストの節子に一目惚れ。ライバルの田沼雄一をニューヨークへ転勤させるが……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DESERT RATS", "砂漠の鼠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "名匠R・ワイズが手掛けた戦場ドラマ。ナチのロンメル将軍が率いる戦車軍団が、連合軍の要衝トブルクを狙っていた。トブルクの守備は士気があがらないオーストラリア軍が受け持っていたが、そこへ英軍のマクロバーツ大尉が指揮官として着任する。兵隊のなかには彼の昔の恩師バートレットもいた。大尉は着任以来、きわめて厳しい態度で部下に臨み、私情を捨てることが勝ちにつながると確信していた。マクロバーツは戦功をあげるものの、やがて彼はドイツ軍の捕虜となってしまい……。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("踊る大捜査線 BAYSIDE SHAKEDOWN2", "踊る大捜査線 BAYSIDE SHAKEDOWN2", "", "", "http://ecx.images-amazon.com/images/I/21S20NCCXYL._SL210_.jpg", 120, "「踊る大捜査線 THE MOVIE2 レインボーブリッジを封鎖せよ！」のインターナショナルバージョン。オリジナル版とは編集が異なり、上映時間が120分に短縮。サウンドデザインをスカイウォーカー・サウンドが手がけた。", "2003-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大感傷仮面", "大感傷仮面", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "『ネ暗トピア』『ぼのぼの』などで知られるギャグ漫画家、いがらしみきおが自費で製作した16ミリの自主映画。泣くと突如として仮面のヒーローに変身してしまう、改造人間・田所の奇妙な生活ぶりを描く。青春映画、歌謡ドラマ、ヤクザ映画などの語り口を混ぜ合わせた、ちょっとヘンな小品。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST DAYS OF PATTON", "パットン将軍 最後の日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "G・C・スコットが、「パットン大戦車軍団」では描ききれなかったパットンのヒューマンな面を演じる。1945年12月21日に自動車事故で死んだパットンの、タフな軍人像とはまた違った、晩年の姿を描く人間ドラマ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paths of Glory", "突撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "　原作「栄光の小径」は、第1次世界大戦中処刑されたフランス人兵士の実際の裁判をもとに書かれた。”蟻塚”と呼ばれるドイツ軍の要所を２日間で陥落させることを命じられたダックス大佐。部下を思うダックスは無謀な任務に反対するが、作戦は実行に移される。結果失敗に終わり、責任をなすりつけられた3人の兵士が軍法会議にかけられる。戦争の持つ不条理さと戦争下においての非人間的な感情を描いた反戦映画。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが青春のアルカディア", "わが青春のアルカディア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "TVアニメ・シリーズ『宇宙海賊キャプテン・ハーロック』の劇場版。宇宙侵略者に捕らえられたハーロックは、トチローという名の男と出会い、奇縁で結ばれていることを知り……。のちに宇宙海賊として名を馳せる、ハーロックの若かりし頃を描いている。石原裕次郎が主人公の先祖役で声優に挑戦したのも話題となった。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ANGRY　RED　PLANET", "巨大アメーバの惑星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "火星を訪れた宇宙船乗組員を待ち受ける悪夢のような出来事を描き、カルト的な人気を博すSF映画。低予算ながら、フィルター処理による赤みがかった映像が異様なムードを演出。コウモリやクモをかけ合わせたような不気味なデザインの巨大クリーチャーも、一度見たら忘れられないインパクトがある。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("光復節特赦", "ジェイル・ブレーカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "青春アクション「アタック・ザ・ガス・ステーション」のキム・サンジン監督による、コミカルで奇想天外なロード・ムービー。コメディアン出身のチャ・スンウォン、「力動山」の名優ソル・ギョングがコンビを組んで大笑いさせる。模範囚が彼女の心変わりを知り、心をつなぎ留めるために相棒を連れて脱獄する。だが、塀の外で二人は特赦が下りたことを知る。そこで、刑務所に戻ろうとするのだが……。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エンドレス・ワルツ", "エンドレス・ワルツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1970年～80年代にかけて、女優としても活躍し、エキセントリックな小説でも一部で熱狂的な支持を得ていた鈴木いづみと、29歳の若さでこの世を去ったフリー・ジャズ界の天才・阿部薫の過激な愛憎を描く。二人は新宿の片隅で出会い、強く惹かれあい瞬く間に結婚。だが激しくストレートな二人の愛は、時にぶつかり合い、互いを傷つけ合う。その反面、愛の誓いにといづみは自らの足の小指を切り落としたりもする。やがて娘が生まれ、平穏な家庭を築いたように見えたが……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Escape from Alcatraz", "アルカトラズからの脱出", "", "", "http://ecx.images-amazon.com/images/I/51M%2B56gqGyL._SL210_.jpg", 112, "　1960年、厳格な所長のもと、水も漏らさぬ警備態勢がとられていたアルカトラズ島の刑務所に各州の刑務所で脱獄を繰り返したフランクが送り込まれる。凶悪な囚人を敵に回し、命を狙われながらも、大胆不敵な知能犯フランクは意気投合した仲間とともに脱獄計画を進めていった！　不可能と謳われたアルカトラズからの脱走事件、その実録小説を映画化。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER HA¨NDLER DER VIER JAHRESZEITEN", "四季を売る男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "外人部隊を除隊したハンスは野菜の行商人として、しがない日々を送りはじめる。家族や友人に軽蔑されたハンスは酒びたりになり、ある日、心臓の発作で倒れる。絶望したハンスは家族や親類を酒場に呼び、全員の目の前で自殺を図るが……。ニュー・ジャーマン・シネマの旗手R・W・ファスビンダー初期の傑作。", "1972-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌14　お遍路大パニック！", "釣りバカ日誌14　お遍路大パニック！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "気鋭・朝原雄三監督がメガホンを執り、ぐっとエンターテインメント性が増したお馴染みの人気シリーズ通算第16作目。今回は、スーさんがリフレッシュ休暇でお遍路めぐりをすることを思い立ったことから、ハマちゃんもそれに便乗。不思議なスケール感に満ちた一大騒動が繰り広げられる。朝原監督の「サラリーマン専科」で快演した三宅裕司も出演。仕事に追われる鈴木建設の社長・スーさんはリフレッシュ休暇をとり、四国お遍路巡りを思いつく。例によって無理矢理ハマちゃんも同行するが、もちろんハマちゃんの狙いは釣り。しかも、宿泊先の娘みさきは美人で……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE PIECE ワンピース THE MOVIE オマツリ男爵と秘密の島", "ONE PIECE ワンピース THE MOVIE オマツリ男爵と秘密の島", "", "", "http://ecx.images-amazon.com/images/I/61JTJySyZFL._SL210_.jpg", 90, "コミックス累計発行部数が1億冊を突破、テレビアニメもアメリカ進出を果たすなど快進撃を続ける人気シリーズの劇場版第6弾。航海の途中でオマツリ島の地図を見つけたルフィ海賊団は、地図の楽しそうな雰囲気に誘われて早速オマツリ島へ向かう。しかし、そこで彼らを待ちうけていたのは、ド派手な姿をしたオマツリ男爵による地獄の試練の数々だった。監督を務めるのは、「デジモンアドベンチャー」などの若手実力派・細田守。", "2005-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間の壁", "人間の壁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "石川達三の同名小説を、山本薩夫監督が自らのプロダクションで映画化した問題作。九州のさびれた小学校を舞台に、共稼ぎを理由に学校側から辞職を強要された女教師が、様々な妨害を受けながらも組会員として戦う姿を描く。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤胴鈴之助 黒雲谷の雷人", "赤胴鈴之助 黒雲谷の雷人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "主演を新人、桃山太郎にバトンタッチし、再びモノクロの中編仕立てでお目見えとなったシリーズ第８作。祖父の仇を追って秩父山麓に来た鈴之助は、月ノ輪城下に出没する雷人一味に遭遇し、秘宝“慈光石”が狙われていることを知る。偶然月ノ輪城の琴江姫を助けた彼は、否応なく６本の鉄の爪を持つ雷人たちの争いに巻き込まれていくが……。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("賭神／GOD　OF GAMBLERS", "ゴッド・ギャンブラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "実在したと噂される天才ギャンブラーの伝説をチョウ・ユンファ主演で映画化し、香港で大ヒットとなった作品。“賭神”と世界で恐れられるギャンブルの天才コウ。彼は事故で記憶喪失になり、その間に恋人をかつての部下に殺される。正気に戻ったコウは、復讐に乗り出すが……。アクション、コミカル、シリアスとチョウ・ユンファの多彩な演技をすべて盛り込んだ、彼の集大成ともいえる。ギャンブル・シーンでは、日活時代の小林旭を思わせる見事なカードさばきを披露。ラストの大バクチも見逃せない。DVDは13分の未公開シーンを追加した完全版がある。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Slow Dancing in the Big City", "ふたりでスロー・ダンスを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　新聞社のコラムニスト、ルーはニューヨークの底辺に生きる人々を優しく見つめる男。そんな彼は同じアパートの隣室に越してきた女性サラと知り合う。モダン・バレリーナのサラは厳しい練習を続けてきたが、それがたたって下肢を痛めていた。ルーと苦痛をこらえながら、大劇場での公演を目指すサラは次第に心を開いていくが……。大都会に生きる男女の愛、そして人々の絆を描いた感動作。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅", "修羅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "鶴屋南北の歌舞伎狂言『盟三五大切』を前衛映画の旗手・松本俊夫監督が翻案。主君の仇打ちのための資金をめぐる血で血を洗う怨念劇を、ストップ・モーションを使用するなどの斬新な映像処理で展開。とりわけ乳のみ児を殺害するクライマックスは凄絶。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In the Valley of Elah", "告発のとき", "", "", "http://image.eiga.k-img.com/images/movie/53363/315x210.jpg?2283", 121, "「クラッシュ」（05）のポール・ハギス監督・脚本によるサスペンスドラマ。イラクの最前線からアメリカに帰還したマイクは、その帰国直後に軍隊から脱走し、行方をくらます。マイクの父親の退役軍人ハンク（ジョーンズ）は、地元警察の女性捜査官（セロン）とともに息子の捜索を始めるが、真実を追い求めるにつれて、父親の知らない息子の素顔が明らかになっていく……。出演はトミー・リー・ジョーンズ、シャーリーズ・セロン、スーザン・サランドンら。", "2008-06-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワールドクラシック＠シネマ2011 バレエ「クラスコンサート」「ジゼル」　ボリショイ・バレエ", "ワールドクラシック＠シネマ2011 バレエ「クラスコンサート」「ジゼル」　ボリショイ・バレエ", "", "", "http://image.eiga.k-img.com/images/movie/56463/315x210.jpg?4701", 165, "世界各地の有名オペラハウス、バレエ団の上演を映画館のスクリーンで上映する「ワールドクラシック＠シネマ2011」（全8作品）の1作。世界最高峰のバレエ団として知られるロシアのボリショイ・バレエが、バレエのクラスの模様を再現した「クラスコンサート」と、アドルフ・アダン作曲による名作バレエ「ジゼル」を2本立てで上映する。", "2011-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生活秀／LIFE SHOW", "ションヤンの酒家〈みせ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「山の郵便配達」のジェンチイ監督作が近代化が進む大都会を舞台に撮り上げた女性映画だ。昔気質の人情が残る下町で料理屋を営むバツイチ美女が、逆境に負けずたくましく生きる姿を温かく綴る。主演のタオ・ホンの、大陸的なバイタリティーあふれる存在感が素晴らしく魅力的で、上海映画祭では最優秀女優賞に輝いた。下町の料理屋を営むションヤンは、頼りない兄や入院中の弟を支える気丈なバツイチ美女。決まった恋人はいないが、店に通い詰める無口な男とはいい雰囲気。そんなある日、店のある一角が再開発で取り壊されることになり……。", "2002-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("書道ガールズ!!　わたしたちの甲子園", "書道ガールズ!!　わたしたちの甲子園", "", "", "http://image.eiga.k-img.com/images/movie/55233/315x210.jpg?3696", 120, "全国の高校から集った書道部が書の腕とパフォーマンス競い合う「書道パフォーマンス甲子園」を題材にした青春映画。主演は成海璃子、共演に山下リオ、桜庭ななみら。監督は「マリと子犬の物語」の猪股隆一。書道家の父になかなか認めてもらえない書道部部長の里子は、部員たちと町おこしのための「書道パフォーマンス甲子園」の開催に向け立ち上がる。大人からの反対や部内のわだかまりなど、さまざまな問題を乗り越えていく女子高生たちの姿を描く。", "2010-05-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEN TALL MEN", "タルファ駐屯兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "砂漠の町タルファに駐屯するフランス外人部隊の中にいるアメリカ人軍曹が、リフ族の姫を捕らえたことから彼らとの戦いを展開させていくアクション。やがて軍曹と姫は深く愛し合うようになり、姫の許婚けとの一騎打ちへと発展していく。B・ランカスターの豪快なアクション演技も見ものだ。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Angel", "エンジェル", "", "", "http://image.eiga.k-img.com/images/movie/53113/315x210.jpg?4079", 119, "「8人の女たち」のフランソワ・オゾン監督が、若くして成功を手にした女流作家の波乱の人生を皮肉をこめて描いた女性映画。20世紀初頭のイギリス。幼い頃から上流階級の生活を夢見てきた食料品店の娘エンジェルは、豊かな想像力と文才を生かして16歳という若さで作家デビューを果たす。瞬く間に富と名声を手に入れ、思い描いてきた夢を実現したかに見えたエンジェルだったが、その先には思いがけない運命が待ち受けていた……。", "2007-12-08", "37,3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rollover", "華麗なる陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "殺された夫の後を継いで石油化学会社の社長となった元女優と、パートナーの会社再建のプロが、隠し口座から、オイルマネーを通しての銀行とアラブ産油国との陰謀を見つける。だが、アラブ側がドルを引き上げ大恐慌が…。金融パニックを描くミステリー・ドラマ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンのホームラン王", "エノケンのホームラン王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "エノケン映画主演50本記念およびエノケンプロダクションの独立を記念して製作された作品。大のジャイアンツ・ファンの肉屋の店員・健吉と、大のタイガース・ファンの魚屋は犬猿の仲。しかし健吉は魚屋の娘に恋してもいる。やがて彼はジャイアンツにマスコットとして迎えられ、各地を回るが……。当時のジャイアンツの監督・三原修をはじめ、川上哲治ほか選手たちが総出演しているのも見どころ。", "1948-01-01", "76", 0, NOW(), NOW());
