INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thieves Like Us", "ボウイ＆キーチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "　1930年代のミシシッピー。ボウイ、チカモー、Tダブの3人は刑務所を脱獄。その後、彼らはチカモーの親戚の家に身を隠し、銀行強盗の計画を練る。ボウイはチカモーの姪で、コーラばかり飲んでいる娘キーチと出会うことに。3人組は強盗に成功し、世間を騒がせる。そんな中、ボウイとキーチは愛し合い、やがてキーチは妊娠。しかしTダブが射殺され、チカモーは逮捕されるという事態に……。大恐慌の時代を背景に、若者たちの犯罪と愛を描いた青春映画。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("平成金融道・マルヒの女", "平成金融道・マルヒの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「平成金融道・裁き人」に続き、倒産回避のプロフェッショナルであるヒロイン、立花亜希子が活躍する一編。“借王〈シャッキング〉”シリーズでおなじみの和泉聖治監督と夏樹陽子がコンビを組んでいる。東京カウンセラントオフィス所長、亜希子は友人である君子の息子、健が就職先のベンチャー企業ミキ・グループで多額の個人債務を負わされたことを知り、解明に乗り出す。なんと、その背後にはかつて亜希子の両親を夜逃げに追い込んだ極悪非道な会社倒産屋、鬼塚がいた……。宍戸錠が久しぶりに鬼塚役で、ふてぶてしい悪役を好演。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男ともだち", "男ともだち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "東京で暮らす25歳のOLえりこは、そう深くは愛していない恋人・井手の子供を妊娠した。行く末を考えあぐね、学生時代に付き合っていた魚住への想いも捨てがたい彼女は、親友の美里と彼の故郷・四国を訪ねる。一方で井手が同時に別の女を妊娠させており、その女は堕胎を選んだという事実も知る……。見延典子原作による、劇団・第三舞台の筒井真理子の映画初主演作。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喋血街頭", "ワイルド・ブリット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "1967年、文化大革命とベトナム戦争の暗い影に揺れる香港。スラム街で育ったベン、フランク、ポールの3人は警察に追われる身となり、北ベトナムへ逃亡する。戦時下の混乱を利用してのし上がろうと、暗黒街のボスから金塊を奪取、再び逃避行が始まるが、3人の関係に次第に亀裂が生じはじめる……。青春映画さながらの前半部、戦場でのアクションシーン、帰郷後の復讐劇と見どころを凝縮した、ジョン・ウー監督による青春アクション大作。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN YESTERDAY", "ボーン・イエスタデイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "美人でスタイルもバツグンだけど世間のことにはまったく無関心な、不動産王の愛人ビリー。彼女に最低限の教養をつけさせようと、不動産王は経済ジャーナリストで大学でも教鞭をとるカタブツ男ポールを家庭教師として雇うが……。今風の政治問題を絡めた現代アメリカ版「マイ・フェア・レディ」。D・ジョンソンとM・グリフィスという実生活でもカップルだった二人が、肩の力を抜いた演技を見せるロマンティックなラブ・コメディ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOYS ON THE SIDE", "ボーイズ・オン・ザ・サイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "自由奔放なブルース・シンガー、真面目で一途なビジネス・ウーマン、恋が生きがいのセクシー・ガール。まったく違ったタイプの３人がひょんなことから一緒に旅をする。それぞれに事情を抱えていて、様々なアクシデントに見舞われるが、そんな旅の中で３人はお互いの人生に深くかかわっていく。「マグノリアの花たち」「グッバイガール」などで、時代を生きる女性を描いてきたH・ロス監督が、多面的でつかみどころのない現代の女性像をありのままにスクリーンに映し出していく。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOL’ABLE DAVID", "乗合馬車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "映画の父D・W・グリフィスが脚本第１稿を書き、子飼いの美男俳優R・バーセルメスが製作、主演した無声映画の名編。平和な村に居座る無法な一家に手ひどい目に遭わされ、村人たちに卑怯者呼ばわりされても、母の言を守って必死に耐え続ける青年デビッド。ついに彼も爆発する時がきた……。", "1922-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OSAKA　STORY", "大阪ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "アメリカ留学中に映画作りの面白さを知り、英国国立映画テレビ学校に入学した中田統一が、卒業制作として完成させたドキュメンタリー。映画は、ロンドン留学中の中田監督が、３年ぶりに大阪の実家に戻ることから始まる。金融業とパチンコ屋を営む在日韓国人の父、そして苦労しながら父の仕事を手伝う母。やがて父には、韓国に“もう一つの家族”がいることが明らかになる……。監督自身の複雑な家族の肖像を率直に語り明かし、バンクーバー国際映画祭の審査員特別賞に輝くなど、各国の映画祭で高い評価を得た。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("音符と昆布", "音符と昆布", "", "", "http://image.eiga.k-img.com/images/movie/53273/315x210.jpg?2193", 75, "映像（Cinema）と音楽（Music）のコラボレーションを目指す「CineMusica（シネムジカ）」シリーズ第4弾。自閉症の一種であるアスペルガー症候群の女性と、彼女の存在を知らずに育った妹との交流を描いたハートフル・ドラマ。父親と2人きりで暮らしていたフードコーディネーターのももの前に、ある日突然姉のかりんが現われる。かりんと一緒に暮らすことになったももは、アスペルガー症候群の彼女とのコミュニケーションに戸惑い、時には苛立ちながらも、少しずつ心を通わせていく。主演は「ジョゼと虎と魚たち」の池脇千鶴と「NANA2」の市川由衣。", "2008-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Good Bye Lenin!", "グッバイ、レーニン！", "", "", "http://image.eiga.k-img.com/images/movie/1493/315x210.jpg?3894", 121, "自国ドイツのアカデミー賞では9部門受賞、昨年の世界各地の映画祭でも話題を独占した感動コメディ。舞台は東ベルリン。アレックスの母親が心臓発作で昏睡状態に陥っている間にベルリンの壁が崩壊して社会が激変。母親は8カ月後に意識を取り戻すが、今度ショックを受けたら命が危ない。そこでアレックスは、母親になんとかベルリンの壁崩壊や社会の変化を隠そうと奮闘していく。音楽は「アメリ」のヤン・ティルセン。", "2004-02-21", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・旅と女と寅次郎", "男はつらいよ・旅と女と寅次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ちょうど芸能界を引退する直前だった都はるみをマドンナ役にフィーチャーした一編。“普通のおばさん”になりたくて佐渡へ失踪して来た演歌の女王・京はるみ。そんなこととはつゆ知らぬ寅次郎はせっせと傷心の彼女を元気づける。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大忠臣蔵", "大忠臣蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "殿中刃傷に及び、お家断絶、切腹の命を受けた主君・浅野内匠頭の無念を晴らすべく、赤穂四十七士を導いて吉良邸討ち入りを図る家老・大石内蔵助。歌舞伎の『仮名手本忠臣蔵』を丁寧に映画化した傑作時代劇。シネマスコープ、総天然色が当時の話題を呼んだ。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Man on Fire", "マイ・ボディガード", "", "", "http://image.eiga.k-img.com/images/movie/1553/315x210.jpg?2614", 146, "「エネミー・オブ・アメリカ」「スパイ・ゲーム」のトニー・スコット監督がA・J・クィネルのベストセラー「燃える男」を映画化。暗殺任務に明け暮れる日々に疲弊していた元CIAの特殊工作員クリーシーは、9歳の少女ビタの護衛を引き受け、彼女の純真さに癒されていく。そして彼女が誘拐犯に拉致されたとき、彼の過激な追跡が始まる。2度のアカデミー賞に輝くデンゼル・ワシントンと「アイ・アム・サム」の天才子役、ダコタ・ファニングが共演。", "2004-12-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sum of All Fears", "トータル・フィアーズ", "", "", "http://image.eiga.k-img.com/images/movie/1203/315x210.jpg?4080", 124, "ボストンのスーパーボウルの会場で核爆弾が爆発、爆弾の出荷元は新大統領が就任したばかりのロシアと判明、はたしてこれは世界大戦のCIAの序曲なのか。情報分析官ジャック・ライアンは真相の究明を命じられる。トム・クランシーの「恐怖の総和」の映画化。「レッド・オクトーバーを追え！」以前の若き日のジャック・ライアン役はベン・アフレック、その上司役にモーガン・フリーマンが扮する。監督は「フィールド・オブ・ドリームス」のフィル・アルデン・ロビンソン。", "2002-08-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アイドルを探せ", "アイドルを探せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "高校を卒業して花の短大生となったヒロインとその友人たちが、自分たちの“アイドル”である男の子を探し求めて青春のアバンチュールを楽しむ、というティーンエイジ・ロマンス。高校を卒業したばかりの菊池桃子が、まさにリアルタイムの役柄を演じた。DVDは「菊池桃子プレミアムコレクション“BRAND”」に収録。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Parapluies De Cherbourg", "シェルブールの雨傘　デジタルリマスター版", "", "", "http://image.eiga.k-img.com/images/movie/54203/315x210.jpg?3231", 91, "ジャック・ドゥミ監督＆カトリーヌ・ドヌーブ主演で第17回カンヌ国際映画祭パルムドールを受賞した1963年の悲恋映画のデジタルリマスター版。フランス北西部の港町シェルブール。自動車修理工の青年ギイと傘屋の娘ジュリビエーブは結婚を誓い合った恋人同士だったが、ギイに送られてきたアルジェリア戦争の徴兵令状が2人の人生を大きく翻弄する……。全ての台詞にメロディがついているミュージカルの傑作。", "2009-01-31", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("絆〈きずな〉", "絆〈きずな〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "根岸吉太郎が「乳房」以来、５年ぶりにメガホンを執った作品。暗い過去を背負ったヤクザ者の孤独な闘いと、悲しき運命の行方をミステリー・タッチで描く。ジャーナリストの殺人事件を捜査する佐古警部は、凶器の銃が10年前の未解決事件と同じものであることを突き止める。彼は捜査線上に浮かび上がった伊勢に不審を抱き、彼の身辺調査を開始する。一方、孤児として育った伊勢は、唯一の肉親である妹をひそかに見守っていた。音楽家として成功した彼女のコンサートが開かれる日、伊勢は身を案じる恋人を振り切ってある計画を実行する。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハーモニー　心をつなぐ歌", "ハーモニー　心をつなぐ歌", "", "", "http://image.eiga.k-img.com/images/movie/55473/315x210.jpg?3097", 115, "米テレビシリーズ「LOST」などで知られるキム・ユンジンが主演するヒューマンドラマ。18カ月後には子どもを養子に出さなければならないジョンヘや家族に見捨てられた死刑囚ムノクは、刑務所内で合唱団を結成することに。歌うことを通して人生をやり直そうとする彼女たちに、やがて奇跡が起こる。", "2011-01-22", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FEAST OF JULY", "七月の宴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ヴィクトリア王朝時代のイギリスを舞台に、運命にほんろうされる女性と彼女をめぐる一家の悲劇を描いた作品。帰らぬ恋人アーチの子供を身ごもり、独りさすらうベラ。その子供を死産し、埋葬した帰り、ベラはベンと出会い、ベンの一家に引き取られる。一家には、ジェッドとコン、マティと３人の息子がおり、べラに心を奪われたジェッドとコンは激しい争いを繰り広げる。一方、マティはベラをロンドンへ誘い出そうとし、誰の気持ちにも応えられないベラは苦悩の日々を送る。そんな時、偶然街でアーチの姿を見つけるのだった。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INFERNO", "インフェルノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "イタリアン・ホラーの鬼才アルジェントがアメリカで撮った作品。『三人の母』なる魔女について書かれた本にまつわる連続殺人事件を描いたショッカーだ。猫が人間を食いちぎったり、何百匹ものネズミに殺される老人など、アルジェントお得意の残酷描写が見もの。", "1980-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRITZ THE CAT", "フリッツ・ザ・キャット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "1960年代後半のアメリカに巻き起こった性革命、スチューデント・パワー、ヒッピーなどの社会現象を、擬人化された猫を用いて風刺たっぷりに描いたアニメーション。好奇心に満ちた主人公のフリッツが、ありとあらゆることに挑戦していく。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コラソン de メロン", "コラソン de メロン", "", "", "http://image.eiga.k-img.com/images/movie/53623/315x210.jpg?2236", 77, "映像（Cinema）と音楽（Music）のコラボレーションを目指す「CineMusica（シネムジカ）」シリーズ第5弾。OLの泉（井上和香）は、恋人ヒロミ（西川貴教）と同棲中だが、ヒロミは病気を理由に仕事もせず、完全に“ヒモ”状態。そんなある日、泉は会社をリストラされ、ヒロミに内緒で新しい仕事を探すがうまくいかない。そんな泉の苦労も知らず、「高級メロンが食べたい」と言うヒロミのために、泉はメロンを手に入れようと奔走するが……。映画初主演の井上和香と西川貴教（T.M.Revolution）によるラブストーリー。監督は「うた魂♪」の田中誠。", "2008-05-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Anna and the King of Siam", "アンナとシャム王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "19世紀半ば、シャムの国の王子たちの教育のため、未亡人のアンナがイギリスからやって来た。教育上のことで言い争いながらも、アンナとシャムの国王は深い友情で結ばれていく……。後にミュージカル化され、映画にもなった「王様と私」のオリジナル。I・ダンとR・ハリスンの情感豊かな演技が見もの。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAS EXPERIMENT", "es［エス］", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "物議を巻き起こした心理実験をヒントに、人間の心の闇を浮かび上がらせるサイコ・スリラー。模擬刑務所で２週間の心理実験が行われ、20人の男性が被験を志願。彼らは看守役と囚人役に分けられ、実験は始まった。だが、看守役の人々は次第に支配的かつ威圧的な態度を見せるようになり、囚人役は彼らの暴力に怯えるようになる。囚人役で唯一反抗的な態度をとってきた記者タレクは看守に容赦なく痛めつけられ、やがて実験の枠を超えた悲劇が起こる。状況次第で残酷にも卑屈にもなりうる、人間のギリギリの心理状態を伝えるドラマは緊迫感十分。", "2001-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A DRY WHITE SEASON", "白く渇いた季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "アパルトヘイト問題で揺れる南アフリカ共和国。非武装の黒人学生デモ隊に、警官隊がガス弾と銃弾を放つ。それを契機に始まった“スウェト暴動”を背景に、白人と黒人の二つの家族の苦悩と哀しみを描いた社会派ドラマ。演技派俳優をそろえた豪華キャストが、いっそうの深みと重厚さをかもし出している。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あした晴れるか", "あした晴れるか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "フィルム会社の宣伝部長から“東京探検”というテーマで仕事を依頼されたカメラマン三杉耕平。彼のいっさいの面倒を見る担当として宣伝部員の矢巻みはるがつくが、これが大変な才女で、耕平の苦手なタイプだった。やがて耕平の本格的な仕事が始まる。深川の不動尊や佃島の渡船場など、登場する東京の名所も見もの。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPERMAN", "スーパーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "1938年にコミックで登場以来、スーパーヒーローとして世界的に人気を博したスーパーマンの本格的な映画化。出生の秘密、幼少時代のエピソードも交え、デイリー・プラネット新聞社のクラーク・ケント＝スーパーマンの活躍を原作に即して描く。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Insomnia", "インソムニア", "", "", "http://image.eiga.k-img.com/images/movie/1453/315x210.jpg?2645", 119, "「メメント」のクリストファー・ノーランの長編第3作は97年の同名ノルウェー映画のリメイク。製作総指揮にスティーブン・ソダーバーグとジョージ・クルーニーが参加。白夜の季節のアラスカの小さな町で、全裸の少女の猟奇殺人事件が発生、ロサンゼルス警察のベテラン警部ドーマーと相棒は捜査に赴く。が、ドーマーは警察上層部から彼の内偵を命じられていた相棒を事故死させ不眠症に陥り、猟奇殺人犯は彼の弱みを知り取引きを申し出る。", "2002-09-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夕陽天使", "クローサー", "", "", "http://ecx.images-amazon.com/images/I/51N7EPP3CBL._SL210_.jpg", 113, "「トランスポーター」のスー・チー、「天使の涙」のカレン・モク、「少林サッカー」のビッキー・チャオら、アジアを代表する3人の女優たちが共演したスタイリッシュ・アクション。ITを駆使して暗殺を請け負っている美人姉妹リンとクワン、そして彼女たちを追う新人女性刑事コン。そんな3人が、ある事件をきっかけに手を組むことになり……。メガホンを取るのは、「トランスポーター」でハリウッド監督デビューを果たしたコリー・ユン。", "2003-03-29", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ観音・情女仁義", "やくざ観音・情女仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "滝に落ちて死にかけたところを救われ、僧になった清玄は、ある日、ヤクザに襲われた娘美沙子を助け、関係を結ぶ。美沙子が自分の異母妹と知った清玄だが、彼女が忘れられず、寺を出てヤクザに身を落としてしまう。ある日、清玄は仲間から美沙子の父を殺すよう頼まれるが……。傑作を連発していた1970年代の神代辰巳作品のなかでは異色すぎるせいか注目されない１本だが、森の中の幽幻なセックス・シーンを捉えたカメラワークや、のちに「ツィゴイネルワイゼン」を手掛ける田中陽造の性と死が強烈にたちこめる脚本、そして単なる美学に終らず、人間の悲哀をたっぷり塗り込んだ神代演出が冴えに冴えた隠れた名作だ。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西の王将 東の大将", "西の王将 東の大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "大学時代の東西の好敵手、西のウマ野郎と東のアンパン野郎。同じ商事会社に就職した二人が、互いに対抗意識をムキ出しにしながらも、見事な業績を収めていく様をドタバタのなかに描き出した喜劇。藤田まこと、谷啓ともに好演。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wrong Man", "間違えられた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　無実をはらそうと逃走を図るヒッチコックお得意の虚構サスペンスとは一線を画す作品。監督自らが冒頭に登場し「真実のストーリー」であることを告げている。金を借りるために生命保険会社へ出向いたマニーは、以前入った強盗犯と間違えられ拘束される。高額の保釈金を支払い、妻と共にアリバイ立証のため奔走するが……。鉄格子ごしの風景など、主人公の視点で構成されたカメラワークや、少ない台詞で無実の罪に混乱した悲壮感がリアルに描かれている。", "1957-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夕照街／SUNSET STREET", "夕照街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「喜劇・ピンぼけ家族」のワン・ハオウェイによる感動作。北京の古ぼけた横丁“夕照街”を舞台に、そこに住む様々な立場の庶民の悲哀を描く。退職した表装職人の鄭万全は北京伝統の味をよみがえらせた店をここで開業しようとしていた。一方、石頭は親孝行だが定職のない“待業青年”。鄭の店の開業を機にそこで働くことになっていたのだが……。", "1983-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE MAN REFUSED TO LET THEM DIE", "エアポート1994", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「大空港」に始まる「エアポート」シリーズとは直接関係ない、R・チェンバレン主演によるTVフィーチャーのパニックもの。北極近くの軍事基地へと向かった空軍輸送機が突然、行方不明になる。さっそく、レスキュー隊が、陸と空からの捜索を開始するが……。", "1993-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEMON SEED", "デモン・シード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "近い将来、人類が最も優れたコンピュータを製造したら、逆に人間はそれによって支配されるのではないかという、現実に起こりうる可能性をドラマ化した異色SF映画。「ダーリング」の名女優クリスティが、バイオ・コンピュータに襲われる人妻を熱演。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOW TO LOSE A GUY IN 10 DAYS", "10日間で男を上手にフル方法", "", "", "http://ecx.images-amazon.com/images/I/51M79GlzXFL._SL210_.jpg", 116, "雑誌のHOW TO記事を書くために、男性にわざとフラれようと試みる女性編集者の物語。人気若手女優ケイト・ハドソンのキュートなワガママぶりが笑いを呼び起こすロマンティック・コメディだ。編集者アンディが、失恋体験ネタの記事を書くことになる。実験台として選ばれた男性は、広告代理店に勤めるベン。すかさずアンディは、彼が愛想を尽かすようにと身勝手な言動を繰り返すが……。", "2003-08-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("のだめカンタービレ　最終楽章　後編", "のだめカンタービレ　最終楽章　後編", "", "", "http://image.eiga.k-img.com/images/movie/54303/315x210.jpg?1936", 0, "二ノ宮知子の人気コミックをTVドラマ化した「のだめカンタービレ」の劇場版2部作の後編。千秋と離れ離れに暮らすことになったのだめは、訪れたあるコンクールで「ラヴェル　ピアノ協奏曲」に心を奪われ、いつか千秋の指揮で演奏することを夢見る。しかし、千秋はその曲でピアニストのRuiと共演することに決まり、のだめにも他の指揮者との共演話が持ちかけられる。", "2010-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ARK OF THE SUN GOD", "アドベンチャー・アーク アポロンの秘宝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "世界のすべてを変えるほどの力を持つ笏を、盗みのプロがイギリス情報部からの依頼で、砂漠の奥の伝説の王の神殿へ探しに行く。しかし同じ笏を狙うアラブ王子の一味がいて……。スペクタクル・シーンは神殿の崩壊。地下都市で有名な奇岩そびえるトルコ中央部地方でのロケも効果を発揮している。", "1983-01-01", "4,32", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAD", "晩秋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "スピルバーグ率いるアンブリン・エンターテインメント製作による親と子、家族愛を描くヒューマン・ドラマ。妻子と別れて仕事に生きるジョンは“母倒れる”の報を受けて病院に駆けつけた。母の病状は回復に向かうが、やがて父親がガンであることが判明し、会社を辞めて看護するのだが……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("State of Play", "消されたヘッドライン", "", "", "http://image.eiga.k-img.com/images/movie/53233/315x210.jpg?4168", 127, "2003年にイギリスBBCで放送され、英国アカデミー賞などを受賞したミニシリーズ「ステート・オブ・プレイ／陰謀の構図」を、舞台をアメリカに置き換え映画化。監督は「ラストキング・オブ・スコットランド」のケビン・マクドナルド、主演にラッセル・クロウ、ベン・アフレック。ワシントンD.C.である国会議員の愛人が死亡し、自殺と報じられるが、地元新聞の記者カルは事件の裏側に迫る重大な証拠を発見。さらなる取材を試みるが……。", "2009-05-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新宿少年探偵団", "新宿少年探偵団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ジャニーズの３人と当時のアイドルが共演したSF仕立ての青春映画。探偵気取りの少年、壮助は新宿を騒がす謎の少女アリサに遭遇。彼女が残したペンダントを親友、謙太郎とともに調査し始める。一方、アリサは人気アイドルの美香に接近して……。機械獣や人造人間なども登場する奇想天外な設定だが、ドラマの中心はあくまでもそれぞれに悩みを抱える少年少女たちの心模様。壮助と謙太郎に扮した相葉雅紀と松本潤の仲むつまじさがストーリーに潤いを与えている。この年、TVドラマでブレイクした深田恭子が過食症に悩む中学生アイドルを好演。", "1998-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REAL MEN", "キツイ刑事〈デカ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "平凡で温和な会社員ボブが、まったく正反対の性格のマッチョ風のCIA局員に誘拐され、奇妙な珍道中をするはめとなるアクション・コメディ。ボブを演じるのは「スキンディープ」で注目を集めたJ・リッター。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自由学校", "自由学校", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "戦後の新聞連載小説で、最高の話題を集めた獅子文六の長編小説の映画化。ノンビリ型の夫が、快活で行動的な妻に“性格上の不一致”から家を追い出されてしまう。以来、夫婦の周りにはてんやわんやの出来事が次々と……。渋谷監督の戦後社会への風刺精神が冴えわたる。戦後初めての競作映画でもあり、大映版では吉村公三郎監督、木暮実千代・小野文春主演という顔合わせで公開された。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Two-Legged Horse", "二本足の馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2008-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("800 TWO LAP RUNNERS", "800 TWO LAP RUNNERS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "頭の中はエッチでいっぱいだが、ひょんなことから陸上部に入って好記録を出し続けるテキヤの息子・中沢龍二。そして同性愛の経験もあるちょっと陸上オタクなライバル・広瀬健治。800n走に賭ける、神奈川県下の二人の高校２年生を軸に、多感な思春期の男女の友情・愛情・性がクールなタッチで絡み合う。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今度の日曜日に", "今度の日曜日に", "", "", "http://image.eiga.k-img.com/images/movie/54373/315x210.jpg?3733", 105, "ソラは日本に留学中の憧れの先輩を追いかけ、韓国・ソウルから長野の大学に映像留学にやってくる。しかし先輩は家庭の事情で帰国することになり、ひとり日本に残されたソラの寂しさは募るばかりだった。そんなある日、ソラは大学のトイレでドジな用務員・松元と出会う。ソラは松元を大学の課題「興味の行方」の被写体に選び、取材を重ねていくが……。主演は歌舞伎役者の市川染五郎と韓国の人気歌手ユンナ。", "2009-04-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Long Riders", "ロング・ライダーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "　南北戦争直後の混乱期、元カントリル・ゲリラ隊のジェシー・ジェームズとその兄フランク、そしてヤンガー兄弟とミラー兄弟は銀行強盗と列車襲撃を繰り返す。人々はそんな強盗団を英雄視し、喝采を送る。一方、彼らを捕まえるべく、ピンカートン探偵社が追跡を開始。そしてのちの1876年、強盗団はノースフィールド銀行を襲うが、それが失敗に終わり……。無法者たちの鮮烈な生き様を描いたウェスタン。キーチ兄弟、キャラダイン兄弟、クエイド兄弟といった本物の兄弟俳優たちが共演したことも話題となった。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN RICKSHAW", "ミラクル・タイガー 魔界大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アクションにオカルトの要素を加味した怪奇サスペンス。バイトで人力車をひくスコットは雨の中、中国人の老婆を助ける。老婆は彼に悪魔教の創始者モートンに奪われた不老不死の力を持つ“神像”を取り戻す使命を与えた。それは、寅年生まれのスコットにしかできないことだった。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOLARBABIES", "太陽の七人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "強烈な太陽に照らされ、荒野と化した未来の地球。“支配者”の圧制に苦しめられてきた７人の孤児たちは偶然、地球に水と自由を与えるという伝説の球体“ボダイ”を発見するが……。「メル・ブルックスの大脱走」の製作＝監督コンビが手掛けた、まじめなジュブナイルSF。渋いキャストに注目したい一編。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOOKING FOR MR.GOODBAR", "ミスター グッドバーを探して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "J・ロスナー原作の映画化。昼はろうあ学校の教師、夜は街の“シングルバー”で男を誘っては孤独を癒すテレサ。セックスでしか自己の存在を確認できない現代女性の悲哀を描いたセンセーショナルな問題作。ラストの殺りくシーンが観る者に異様に迫る。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UKオペラ＠シネマ　ヘンデル：ジュリオ・チェーザレ", "UKオペラ＠シネマ　ヘンデル：ジュリオ・チェーザレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 227, "イギリスが誇る世界屈指の音楽祭・グラインドボーン音楽祭で2005年に上演されたヘンデルのバロック・オペラ「ジュリオ・チェーザレ」をデジタルシネマ化。誇り高きローマの将軍ジュリオ・チェーザレと、狡猾なエジプト女王クレオパトラを中心に繰りひろげられる政治的策略を題材にした物語が、巨匠ウィリアム・クリスティ指揮による格調高い演奏や、サラ・コノリーら注目の若手歌手たちによる魅力的なパフォーマンスで描かれる。", "2009-01-10", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREAT WHITE HOPE", "ボクサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "黒人で初の世界ヘビー級チャンピオンになったボクサー、ジャックは白人の女性エレノアと恋に落ちる。しかし黒人と白人の結婚を許さないアメリカ社会に二人はつまはじきにされ、ジャックは試合さえできなくなる……。「フィールド・オブ・ドリームス」のJ・E・ジョーンズが主演した、実話に基づく社会派の人間ドラマ。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風月", "花の影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "1920年代の上海と蘇州を舞台に、とある名家をめぐる男女の退廃的な恋愛模様を、エキゾチシズムあふれる壮麗なスケールで描くメロドラマ。 「さらば、わが愛／覇王別姫」のチェン・カイコー監督がその主演、レスリー・チャンとコン・リーを再び迎え、香港のウォン・カーワイ監督作品で知られる名カメラマン、クリストファー・ドイルと組んだ、華麗なる愛憎ドラマ大作。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風のなかで　むしのいのち　くさのいのち　もののいのち", "風のなかで　むしのいのち　くさのいのち　もののいのち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "東京都杉並区の住宅街にある中瀬幼稚園は、武蔵野の面影を色濃く残した屋敷林に囲まれている。世の中が変化しても、ここでは子どもたちが林の中でいつものように遊びを繰り返していた。園児の卒園までの1カ月間に密着し、子どもたちの命の輝きを描いたドキュメンタリー。", "2010-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("永訣〈わかれ〉", "永訣〈わかれ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "舟木一夫が主演した純愛メロドラマ。山口県の萩中学に超秀才・牧人がいた。牧人は女生徒たちの人気者。けれど、彼が見染めたのは断崖で泣く戦争未亡人の夕子だった。夕子は亡夫の後輩の海軍士官に愛されていた。その寂しさを、牧人は戦時の猛訓練にぶつけるが……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("家なき子", "家なき子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「同情するなら金をくれ！」という流行語を生み出し一世を風靡した、人気TVドラマの映画化。この１作で有名になった安達の“いじめられ度”の高さに注目！ クリスマス・イブの夜、少女すずは愛犬リュウのためにケーキを盗み、販売員につかまってしまう。彼女を助けたのは、孤児院を運営する磯貝という男だった。ホッとしたのもつかの間、実はこの男、身寄りのない子供たちを安く買っては団員としてこき使う、サーカスの団長だった。そしてサーカス小屋に連れて行かれたすずの前に意地悪な叔母・京子と、その娘・真弓が現れ、執拗ないじめが始まるのだった。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.ARKADIN", "秘められた過去", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ナポリの波止場で殺された男が死ぬ前にもらした名前“ミスター・アーカディン”。アーカディンは世界でも有数の実業家で大金持ちだった。その名を耳にしたストラッテンは、アーカディンをゆすろうと思って、スペインの彼の邸にいく。だが、アーカディンは逆にストラッテンに自分の過去を調べてほしいと持ちかける。記憶喪失で悩んでいるというのだ。アーカディンの過去を調べ出すストラッテン。だが、ストラッテンが会ったアーカディンの過去を知る人々が次々と殺されていく……。謎の無人飛行機が洋上を飛行していた迷宮入りの実話にヒントを得て、O・ウェルズの想像力で組み立てられたフィルム・ノワールの雰囲気をもつ映画。", "1955-01-01", "2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLAZE", "ブレイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "ストリッパーとリベラルなルイジアナ州知事の燃えるような恋。男65歳、女28歳。おまけに彼には妻がいた。世間の目からは“スキャンダル”以外のなにものでもなかったが、二人には正真正銘の恋愛だった。P・ニューマンが天衣無縫でチャーミングな政治家を好演している。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あじさいの歌", "あじさいの歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "封建的な父親と一緒に暮らす娘が商業デザイナーと知り合い、古い因習を捨てて、明るく開放的に変わっていくさまを描いた青春ドラマ。ラスト、裕次郎の手で芦川の古い邸宅が改築されて、モダンに生まれ変わるところは、映画全体のテーマとダブる。田坂具隆からバトンタッチした滝沢英輔監督による、石坂洋次郎もの。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真夏の地球", "真夏の地球", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "海辺の田舎町で育った３人の高校生が体験するひと夏の出来事をみずみずしく描いた青春映画の佳作。幼なじみの３人組・稔、順慶、鉄男の前に現れる“スイカ男”と避暑に訪れた美人姉妹・愛子と由子。そして愛子に熱を上げた３人は、ひょんなことからビーチバレー大会に出場するハメになるが……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クロサワ映画2011 笑いにできない恋がある", "クロサワ映画2011 笑いにできない恋がある", "", "", "http://image.eiga.k-img.com/images/movie/57083/315x210.jpg?2958", 95, "女性お笑いトリオ「森三中」のメンバーのうち、ただひとり未婚の黒沢かずこを主人公に女芸人の悲哀と葛藤を描き、2010年・第2回沖縄国際映画祭で2冠を達成した「クロサワ映画」の続編。クリスマス直前の12月23日、黒沢は芸人仲間の光浦靖子、大久保佳代子、椿鬼奴と行きつけのバーで憂さを晴らしていた。しかし、その後に大久保と2人で入った居酒屋で韓国人青年パク・ソンドルと出会い、積極的なアプローチを受けた黒沢はパクにひかれていくのだが……。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL MODERNO BARBA AZUL", "キートンの月ロケット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "無声映画の喜劇王キートンが、メキシコに渡って作った珍品コメディ。トーキーの出現によって没落していったキートンの、知られざる１本といえるだろう。内容は戦争でメキシコ人の捕虜になったキートンが、惑星間飛行のモルモットにされるというもの。", "1945-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Deep　Love／アユの物語", "Deep　Love／アユの物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "女子高生の間で人気沸騰し、2000万件のアクセスを記録した同名ケータイ連載小説の映画化。単行本化したシリーズも170万部を突破という異例の話題作を、原作者のYoshiが監督・脚本も務め、原作の世界観を余すところなく映像化し、若者の闇をリアルに表出した。路上シンガーで“I Wish”のボーカルでもある川嶋あいの音楽プロデューサーとしての参加も要注目。生きる意味を見出せず、無感情に援助交際を続ける17歳の女子高生アユ。しかし、捨て犬のパオや無条件に愛を注いでくれる見知らぬおばあちゃん、そして心臓病を患う義之との出会いによって、アユは少しずつ変わり始める。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大当り三代記", "大当り三代記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "大阪名代の浪花まんじゅうの老舗・天満屋は右七・左七の両番頭にのれん分けをした。本家を名のる右七と元祖を名のる左七両家の、明治から昭和までの大奮闘ライバル喜劇。出演者欄にはとても書ききれぬ豪華上方喜劇陣オールスター・キャストによる作品。シナリオは花登筐のオリジナルだ。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGHER LEARNING", "ハイヤー・ラーニング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "コロンビア大学を舞台に、カルト、洗脳、レイプ、ネオ・ナチ、人種差別など、現代のアメリカの若者が抱えた様々な問題を浮き彫りにする。全米各地から新入生がやって来る新学期。カリフォルニア出身のクリスティンも不安を胸に抱きつつ、初めての寮生活を始める。そんなある日、クリスティンは酔った黒人学生にレイプされそうになった。この事件をきっかけに生まれた白人と黒人の間の緊張、そしてクリスティン自身の女性問題運動への傾倒。やがて黒人グループとネオ・ナチ一派との対立が深まり、学内は大きな悲劇へと走り始める……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美男城", "美男城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "関ヶ原の戦いで徳川方が勝利を得た裏には、美濃の国・日坂の城主、伊能盛政の裏切りがあった。だが盛政の子、主馬之介にとって、“裏切り”という父の行為は死にまさるものであり、彼はひそかに父の殺害を心に決める。一方家康もまたその謀反を快く思わず、盛政と主馬之介暗殺を謀る。瀕死の父から出生の秘密を聞かされた主馬之介だが……。江戸時代初期の隠れたエピソードを捉えた時代劇。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("We Bought a Zoo", "幸せへのキセキ", "", "", "http://image.eiga.k-img.com/images/movie/57473/315x210.jpg?4763", 124, "閉鎖されていた動物園を買い取り家族とともに再建した英国人ジャーナリスト、ベンジャミン・ミーの回顧録をもとに、最愛の妻を亡くした男が動物園を立て直すことで悲しみを乗り越えていく姿を描いたヒューマンドラマ。監督は、「あの頃ペニー・レインと」のキャメロン・クロウ、主演はマット・デイモン。妻を亡くして半年がたつベンジャミンは、新しい場所で新しい人生を始めるため郊外に家を買う。悲嘆に暮れる14歳の息子と7歳の娘を連れて引越したベンジャミンは、家の隣に閉鎖中の動物園があることを知ると、ある思いを胸に動物園の再オープンを目指す決意をする。", "2012-06-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Parfum d'Yvonne", "イヴォンヌの香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "フランスのレマン湖畔の避暑地で、パリから徴兵を逃れてきたビクトールは、ゲイの医師ルネと美しい女性イボンヌに出会う。ロシア人伯爵と偽るビクトールは、イボンヌとすぐに愛し合うようになるが……。「仕立て屋の恋 」「橋の上の娘」のパトリス・ルコント監督が描くラブロマンス。ビクトールを演じるのは「可愛いだけじゃダメかしら」のイポリット・ジラルド。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES PORTES TOURNANTES", "回転扉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ニューヨークに流れてきたジャズ・ピアニスト、セレスト。人生も終盤にさしかかってきた彼女にとって心残りは、息子のことであった。幼い頃、別れたままの息子のために今まで書いた日記や写真を送った。それは自分自身のためでもあった。そして母から贈られたスーツケースを前に息子のブローデルは思い出にふけり始める……。重厚なタッチで綴られる人生ドラマ。", "1988-01-01", "2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死に花", "死に花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "心は若者となんら変わらない老人たち。死ぬまでにもうひと花咲かせようとする彼らの、人間味あふれる生き様が小気味よく描かれている。山崎努をはじめ個性派の名優たちが集結し、絶妙な掛け合いと深みのある演技力で魅力的な老人像を快活に体現した、新たな視点で老いを見つめた傑作だ。老人ホームで気ままに過ごす老人たち。ある日、仲間の一人が死を迎えた。遺品のノートには、穴を掘って銀行へ辿りつき、17億円を強奪するという衝撃の計画が記されていた。興奮した一同はその計画を実行しようと盛り上がる。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Box", "運命のボタン", "", "", "http://image.eiga.k-img.com/images/movie/54183/315x210.jpg?1897", 115, "「ドニー・ダーコ」の鬼才リチャード・ケリーが、リチャード・マシスンによる短編小説をキャメロン・ディアス主演で映画化したサスペンス。郊外の町に暮らすノーマとアーサーの夫婦のもとに、奇妙な赤いボタンが付いた箱が届く。その日の夕方、謎の男がノーマを訪れ、「24時間以内にボタンを押せば1億円が手に入るが、代わりに見知らぬ誰かが死ぬ」という究極の選択を提示する。苦しい生活から、夫婦はボタンを押すべきか否か悩むのだが……。共演はフランク・ランジェラ、ジェームズ・マースデン。", "2010-05-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("老人Z", "老人Z", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「AKIRA」に続いて大友克洋がアニメ映画に挑戦した、ブラックな笑いに満ちたロボット・アクション。高年齢化社会へ突入した近未来の東京で、厚生省が最新コンピュータを組み込んだ全自動看護ベッド・Z-001号を開発。ところがモニターに選ばれた87歳の寝たきり老人の意識が、ひょんなことからコンピュータを感化し、Z-001号は彼の願望をかなえようと暴走を始める。手当たりしだいに物を取りこみ、増殖を続けるZ-001号の起こす騒動は、老人を救おうとする看護学院の女子学生や厚生省の役人を巻き込んで、警察との激しいチェイスに発展していく。“老人問題”を逆手にとっての痛烈な風刺が、見事に決まっている。漫画家の江口寿史によるキャラクターデザインも話題に。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冬のハイヌミカゼ", "冬のハイヌミカゼ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "元ちとせのライブDVD『冬のハイヌミカゼ』の発売に先駆け、本編に未公開映像を加えて公開された劇場版。故郷・奄美大島での初ライブの模様を中心に、奄美の大自然や人々の暮らしが詩情あふれる映像で描かれていく。『ワダツミの木』『君ヲ想フ』といったヒット・シングルからインディーズ時代の名曲まで、全27曲を網羅した彼女の集大成とも呼べる作品だ。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春一番", "春一番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "割烹旅館“花霞”の看板娘・露子は、母に代わって店を切り盛りしていた。ある日、外務省勤務の治郎と恋に落ちるが、彼の母親に交際を反対される。露子の母の未亡人・民子が、極秘に妻子ある男性と深い仲であることが発覚したからだ。原作は芝木好子の『女家族』。女性ドラマのヒット作。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲキ×シネ「蛮幽鬼」", "ゲキ×シネ「蛮幽鬼」", "", "", "http://image.eiga.k-img.com/images/movie/55593/315x210.jpg?2931", 182, "劇団☆新感線が2009年に上演した時代活劇を、最新デジタル技術による迫力の映像と音響でスクリーン上映。ひとつの政権で統治され始めたばかりの島国・鳳来の国。無実の罪で監獄島に幽閉された伊達土門は、サジと名乗るナゾの男の助けを借りて脱獄に成功する。復しゅうを誓い行動を開始した土門の前に、かつての恋人・美古都が現われ……。上川隆也が主演を務めるほか、堺雅人、稲森いずみ、早乙女太一ら豪華キャストが結集。", "2010-10-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍兄虎弟／THE AMOUR OF GOOD", "サンダーアーム 龍兄虎弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "６種の“神の秘宝”をめぐって、他教滅亡をたくらむ邪教集団とジャッキー扮する“アジアの鷹”が激突する。撮影中ジャッキーが事故で重傷を負うというアクシデントに見舞われたが、無事完成。ジャッキーならではの超人的アクションも健在。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I CONFESS", "私は告白する", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "カナダのケベックが舞台。事件の真相と犯人を知りながら、殺人者の告白を明かせず苦しむ神父と、それを追及する刑事の執拗な姿を描くヒッチコックのスリラー。ポール・アンセルムの舞台劇の映画化。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飛渡捲雲山／MAGNIFICENT BODYGUARDS", "ジャッキー・チェンの燃えよ！飛龍神拳　怒りのプロジェクト・カンフー", "", "", "http://ecx.images-amazon.com/images/I/51F9SN58Z7L._SL210_.jpg", 93, "「蛇鶴八拳」などと並ぶジャッキー・チェンの時代伝奇シリーズの佳作。“重病人”を乗せた籠のボディガードを依頼された主人公が、行手を阻む山賊を相手に繰り広げるカンフー・アクションの妙技。そして迎える意外な結末。籠の中の“重病人”は一体何者か？　別題「立体3D映画　飛龍神拳」のビデオタイトルで発売されていたこともある。", "1978-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORONS FROM OUTER SPACE", "モロン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "イギリスTV界の人気コンビが脚本・主演するSFコメディ。ある夜、ロンドン郊外のハイウェイに不時着したUFOに乗っていたのは、史上随一の低能（＝モロン）エイリアン３人組だった!? パンク、ヘヴィメタ、映画のパロディなど、英国調の皮肉が堪能できる。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗黒街の弾痕", "暗黒街の弾痕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "東宝のお正月ドル箱興行として、三たび岡本喜八が監督した“暗黒街”もの。今回は“S活動”と称する産業スパイが描かれる。捕鯨砲練習所の章鹿次郎は、山合いの峠で高性能エンジンのテスト中に事故死した兄の死因を調べるため、大学時代の友人でトップ屋の須藤と調査を始める。高性能エンジンを開発した技師の小松は、兄の死には産業スパイが絡んでいると推察。調べていくうちに、経済研究所と暴力団能中組が浮かび上がった。今回も岡本喜八の演出は、スピーディーでシャープな冴えを見せ、特にギャグを盛り込んだ乱闘シーンや、見せ場の銃撃戦にユニークな才能をいかんなく発揮している。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMANUELLE　E　GLI　ULTIMI　CANNIBALI", "猟奇変態地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「カニバル」の後を追って製作された食人族映画。アマゾンの奥地に生息するといわれる食人の部族を追って、女性学者が男たちを引き連れて現地へ。だが、想像を超える残虐な現実が、彼らを待ち受けていた……。残酷描写とエロティシズムを配合したつくり。“褐色のエマニエル”として当時人気を呼んだL・ジェムサーがヒロインにふんしている。ビデオ発売時タイトル、DVDタイトルは「アマゾンの腹裂き族」。", "1977-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("「紅の拳銃」よ永遠に", "「紅の拳銃」よ永遠に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "赤木圭一郎主演の日活映画「紅の拳銃」を観たことをきっかけに、何の目的もなかった高校生たちが、憧れの彼女を主演に映画を作ろうと決意する……。日活芸術学院25周年を記念して、日活撮影所のプロ・スタッフと学院の生徒たちが集結して作り上げた青春映画。企画に賛同した宍戸錠ら撮影所になじみの深いスターたちも多数友情出演している。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪の下の炎", "雪の下の炎", "", "", "http://image.eiga.k-img.com/images/movie/54463/315x210.jpg?3860", 75, "平和的なデモを行なったという罪で投獄されたチベット僧パルデン・ギャツォの壮絶な半生を記録したドキュメンタリー。中国政府による33年間に渡る拷問を生き抜き、61歳で釈放されたパルデンは、中国当局による常軌を逸脱した拷問の実状を訴え、現在も亡命先のインドでチベットの独立と自由を求めて闘い続けている。監督は日本人女性ドキュメンタリー作家の楽真琴で、ダライ・ラマ14世の証言なども収録。", "2009-04-11", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　TEMPS　RETROUVE´", "見出された時・『失われた時を求めて』より", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 163, "映像化不可能とされるプルーストの不朽の名作を映画化した、芸術派の巨匠として名高いR・ルイスによる文芸ドラマ。晩年を迎えて病の床につく作家のマルセルが、過ぎ去った過去を回想することによって、自分自身に投げかけ続けたある問いの答えを見出していく。最終編「見出された時」にこそ、『失われた時を求めて』のすべての答えがあると確信したルイス監督が、フランス＆アメリカ映画界最高の豪華キャストとスタッフを総動員して、その世界を余すところなく描き、ヨーロッパ全土で前例のないほどのプルースト・ブームを巻き起こした。カルティエの特別協力による、20世紀初頭の豪華絢爛なアクセサリーがふんだんに使われているのも話題になった。", "1998-01-01", "2,25,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラ×モスラ×メカゴジラ／東京SOS", "ゴジラ×モスラ×メカゴジラ／東京SOS", "", "", "http://ecx.images-amazon.com/images/I/310A5T8TDTL._SL210_.jpg", 84, "日本映画界が誇る人気シリーズ“ゴジラ”の第27作。通算27作目を迎える本作では、根強い人気怪獣のモスラが再登場。ゴジラ、モスラ、メカゴジラの壮絶な三つ巴の闘いを描く。かつて「モスラ」(1961)で言語学者・中條を演じた小泉博が、42年ぶりに同役を演じたのも話題になった。一年前のゴジラとの闘いで損傷を受けた“機龍”は、修復作業に入っていた。“機龍”の整備士・中條義人は、叔父たちと休暇を別荘で過ごしていたが、突然インファント島に住む小美人たちが彼らの前に現われ、ある忠告をする。", "2003-12-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERCULES UNCHAINED", "ヘラクレスの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ゼウスの血を引く英雄ヘラクレスはギリシャを救ったあと、妻イオレ姫とユリシーズを伴い故郷テーベに戻った。しかしそこでは兄エテオクレスと弟ポリニセスが王位継承のことで反目し合っていた。仲介役を努めようとするヘラクレス。だが魔の泉に触れ記憶を失う。1957年に同じ監督、主演コンビで作られた「ヘラクレス」の続編。", "1959-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CHATTE", "女猫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "第二次大戦下のパリを舞台に“女猫”と呼ばれるレジスタンスの女スパイとナチの将校との恋を描く。女スパイ役に当時、人気絶頂のF・アルヌールがあたる。彼女の妖艶な美しさが印象的な、戦争犯罪メロドラマ。", "1958-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PORCILE", "豚小屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "中世の物語と現代の物語が交互に進行する寓話風の作品。中世を背景とする物語の方では、荒涼とした火山灰地を舞台に人を襲っては人肉を食う若者を描き、現代の物語では豚とのセックスにふけり、ついに豚に喰われてしまうブルジョワの青年を描く。グロテスクな物語の内に痛烈な文明批判が感じられる作品である。", "1969-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自転車吐息", "自転車吐息", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "PFF1987の第４回スカラシップに選ばれた詩人かつ自主映画作家の園子温監督が、その援助を受けて製作した自己再認識の青春映画。さえない毎日を送る20歳の大学浪人が、元旦の誕生日を間近に控え、走り出そうとするさまをアナーキーに描き出していく。“俺”と書かれた旗を掲げる主人公の姿がいさぎよい。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON’T TURN OUT THE LIGHT", "サベージ・バイオレンス 野獣襲撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "人質を誤って撃ち殺してしまった経歴を持つデビッドは、その後も罪の意識にさいなまれ、警官の職を離れて囚人の護送係として働いていた。ある日凶悪犯を護送していた彼は、犯人の愛人の罠にはまり逃がしてしまう。やっとの思いで追い詰めた犯人はデビッドの家に逃げ込み、彼の妻子を人質にとった。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　青の祓魔師（エクソシスト）（仮題）", "劇場版　青の祓魔師（エクソシスト）（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRIMES AND MISDEMEANORS", "ウディ・アレンの重罪と軽罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "年を追うごとにますます円熟した映画作りをするW・アレン。今回は神と罪と死をテーマに、人生の後半にさしかかった３人の男たちを描く。小さな罪＝浮気がやがて、大きな罪＝殺人へと導びかれていく。だが、殺人者は神に裁かれもしないし、神は殺人があったことさえ知らないようだ。結局人生の成功者はさらに成功していき、敗残者は惨めに負けるだけ。そんな人生の不条理を、辛らつに考察してみせる。今回、ウディと異色の顔合わせをするのはA・アルダ。切れ間のないハイパーなおしゃべりで他人を自分のペースに巻き込む敏腕プロデューサーを巧みに演じ、ニューヨーク批評家協会賞、全米批評家協会賞の助演男優賞を受賞した。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NINE LIVES", "アウト・オブ・タイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ボスニア紛争で戦場を経験した元陸軍特殊部隊員が、何者かによって潜在能力を覚醒させる薬物を投与される。それには神経系統を破壊する恐るべき副作用があった……。制限時間は8時間。限られた時間の中、甦る悪夢と闘いながら真相究明に乗り出す男の闘いを描いたサスペンス・アクションだ。元特殊部隊の隊員ディーンは、戦場で友人の死を目撃して以来、悪夢に悩まされていた。さらに、彼は何者かに強烈な幻覚剤を投与され……。", "2005-01-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿嫂／MOB SISTER", "姐御／ANEGO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "香港の黒社会を舞台に、二人のヒロインをめぐる数奇な運命を描く新感覚マフィア映画。前作「ベルベット・レイン」でも独自の感性を見せたウォン・ジンポー監督による、映像や音楽にもこだわったスタイリッシュな演出が光る。マフィア幹部の父を殺された少女フィービー。ある日、彼女の父親代わりだったマフィアのボスが殺された。留学先のアメリカから帰国した彼女は組織を継ぎ、抗争を収めようとするが……。", "2005-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Belfast, Maine", "メイン州ベルファスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 247, "", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Die fetten Jahre sind vorbei", "ベルリン、僕らの革命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "現代ベルリンを舞台に、理想に燃える若者たちの友情や恋を描いた青春ドラマ。情熱的で正義感の強いヤンと親友のピーターには、ある秘密があった。2人は不公平な現代社会へのレジスタンス活動として、「エデュケーターズ」と名乗り金持ちの邸宅への不法侵入を繰りかえしていたのだ。ある日、ヤンはピーターの恋人ユールにそのことを打ちあけるが……。主演は「グッバイ、レーニン！」のダニエル・ブリュール。", "2005-04-29", "7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("駿河遊侠伝 度胸がらす", "駿河遊侠伝 度胸がらす", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "清水港の親分になった次郎長は男っぷりと度胸の良さで貫禄十分、次第に子分も増えてきた。ある時、大恩を受けた五郎親分の死を知り、単身甲州に乗り込み、仇を討つが同時に深でを負ってしまう……。任侠時代劇の人気シリーズで、“次郎長”ものの第２作。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夕陽が呼んだ男", "夕陽が呼んだ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "自動車工場で貧しいながらも一所懸命働く健一、財閥の長男でエリートとして堅実な道を歩く優一、その弟で派手に遊びまわってばかりいる秀司。三者三様の生き方を描き、さわやかな感動を残す青春ドラマ。森田健作と尾崎奈々のいきいきとしたカップルが魅力。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The War of the Worlds", "宇宙戦争（1953）", "", "", "http://ecx.images-amazon.com/images/I/510YK1AQ1VL._SL210_.jpg", 85, "地球に襲来した火星人と人類の戦いを綴ったH・G・ウェルズの同名小説を、「地球最後の日」のジョージ・パル製作により実写映画化した名作SF映画。ロサンゼルス近郊に隕石のような物体が落下し、中から空飛ぶ円盤が出現。調査を引き受けた科学者クレイトンは、その物体が宇宙から飛来した戦闘兵器であることを知る。世界各地に着陸した円盤は、強力な熱光線で攻撃を開始。その圧倒的なパワーになすすべもない人類だったが……。", "1953-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOG STAR", "DOG STAR", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "瀬々敬久監督が豊川悦司、井川遥の顔合わせで撮り上げた、ファンタスティックで切ないラブ・ストーリー。交通事故で突然他界した人間のご主人様から、一つだけ願い事を叶えてやると告げられた盲導犬シロー。やがて彼は人間の姿を借りて、子犬の頃に自分を可愛がってくれたハルカの元を訪ね、彼女との報われない恋に落ちていく。犬の魂が宿った青年という風変わりな役どころに扮した豊川悦司のユーモラスな演技も見もの。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いれずみ突撃隊", "いれずみ突撃隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "東映で任侠映画のブームを作った石井輝男が久々に挑んだアクション映画。生まれついてのケンカ好きで、親からも見離されている衆木は、日露戦争の最前線に送られる。彼は古参兵と衝突し、その名は軍隊中に知れわたっていった。慰安婦のみどりはそんな彼に想いをよせていたが、二人とも意地っぱりでケンカばかりしていた。はみだし者の一匹狼が主役の異色戦争映画。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFRICA ADDIO", "ヤコペッティのさらばアフリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "ヤコペッティ監督が、アフリカ大陸にカメラを持ち込み、３年がかりで記録したドキュメンタリー。白人の支配から解放され、黒人国家へと脱皮していく姿を独特の視点から描く。音楽はヤコペッティ映画に欠かせぬL・オルトラーニ。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hell", "HELL", "", "", "http://image.eiga.k-img.com/images/movie/58103/315x210.jpg?3054", 90, "「2012」「インデペンデンス・デイ」のローランド・エメリッヒが、祖国ドイツで製作総指揮を務めたSFサバイバルスリラー。西暦2016年、太陽活動の変化により地球の大気温度が上昇し、水や作物が枯渇したことで人類の文明は崩壊。湧き水があると噂される山岳地帯を目指していたマリーとレオニーの姉妹は、突然何者かに襲われ、レオニーが連れ去られてしまう。後を追ったマリーはエリザベートという女に出会うが、エリザベートの荒れ果てた農場には旅行者が「食糧」として拉致監禁されており、レオニーもそこに捕らわれていた。エリザベートはマリーとレオニーを息子たちの花嫁にしようと目論んでいたが……。監督はドイツの新鋭ティム・フェールバウム。主演は「4分間のピアニスト」のハンナー・ヘルツシュプルンク。", "2012-06-30", "7,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハイティーン・ブギ", "ハイティーン・ブギ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "横浜の暴走族のリーダーに扮した近藤真彦が、愛する少女の願いで暴走族を抜け、駆け落ち同然の状態で同棲生活を始める姿を描く青春ドラマ。たのきんトリオが出演するシリーズの４作目。近藤真彦の活躍をメインにした作品で、主題歌も大ヒット。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("その男、凶暴につき", "その男、凶暴につき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ビートたけしの初監督作品。当初、深作欣二監督、ビートたけし主演で進められていた企画だったが、両者のスケジュールが折り合わず、たけしがメガホンを執ることになった。内容は、一匹狼の暴力刑事・我妻諒介が、親友を殺し、同僚を廃人にし、しかも妹を強姦した麻薬組織に単身挑んでいくというもの。少年たちの浮浪者殺人事件に始まる開幕から、カメラをどっしり構えた堂々の演出で、肉を裂き骨を砕く暴力の現場を、悪寒を呼ぶほどクールに切り取っている。暴力でしか自己表現できない刑事の、内面の空洞感が浮かび上がってくる演出が見事だ。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALIEN　ADVENTURE", "エイリアン・アドベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 37, "世界のアミューズメント・パークのライド映像の70％を手掛けるB・スタッセン監督による、全編フルデジタルCGで作られた3Dライド映画。日本ではアイマックスシアターで公開された。宇宙からやって来たおバカなエイリアン、グレゴリス族が地球の探査に乗り出すが、誤ってアミューズメント・パークに飛来。４種類の3D映像を体験することになる……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　KILLER　TONGUE", "キラークィーン 舌を巻く女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "強盗カップルのジョニーとキャンディが、仲間を裏切って大金をせしめる。ほとぼりが冷めるまでジョニーは刑務所に、キャンディはガソリンスタンドの修道院に身を潜めることにする。しかし、墜落した隕石のかけらを飲み込んでしまったキャンディは、なぜかエイリアンに変貌。長い舌を駆使して暴れ出した彼女をめぐり、砂漠の大騒動が繰り広げられる。絶妙のセンスでつけられた邦題からして、ナンセンスな内容がうかがえるスペイン製のSFコメディ。長い舌にナイスバディの美人エイリアン、オカマ４人組、尼僧、看守らが入り乱れてのドタバタ・バトルロイヤルを、頭を空っぽにして楽しむほかはない。", "1996-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("笑傲江湖II：東方不敗／SWORDSMANII", "スウォーズマン 女神伝説の章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ツイ・ハークがプロデュースした伝奇アクション。時は乱世の明の時代、秀吉への屈服を認めぬ武士たちが日本から中国南部へと逃れ、“日月教”を宗派と仰ぐその地の反乱軍、苗族と行動をともにしていた。陰謀の渦巻く世の中に嫌気が差していた剣士リンは、妹弟子のツァイツァイと放浪の身の上だ。旅の途中、リンは名前も知らぬ美女と出会い、たちまち心を奪われた。その一方、リンの兄弟弟子たちの間では、教主ヤンの失踪、苗族の内紛などで人心が揺れていた。リンたちの敵は、新しく教主に納まった東方不敗。しかし彼こそ、リンが出会った謎の美女の正体だった。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Guilty as Sin", "ギルティ／罪深き罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "　敏腕の美人弁護士ジェニファーは妻殺しの罪に問われているデビッドの弁護を請け負うことに。デビッドの魅力に惹きつけられたジェニファーは調査を進めるが、疑惑は深まるばかり。そして、ついには何もかもが彼の仕組んだ罠であることが明らかになる。デビッドを告発しようとするが、証拠も消されてしまい……。ひねりの効いた展開で見る者を惹きつけるサスペンス・スリラー。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿Q正伝／THE TRUE STORY OF AH Q", "阿Q正伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "魯迅の生誕100周年を記念して製作された魯迅原作ものの一作。20世紀初頭の農村を舞台に、貧しい農家に生まれた阿Qが、当時の封建的風土ゆえに悲惨な日々を送らざるを得ない姿を描いている。「菊豆」の名優リー・ウェイが趙大爺役で出演。1983年スイス国際喜劇映画祭で、阿Q役のヤン・シュンカイが最優秀主演男優賞を受賞。", "1981-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かなりあ　人生をうまく跳べない人2", "かなりあ　人生をうまく跳べない人2", "", "", "http://image.eiga.k-img.com/images/movie/57993/315x210.jpg?4651", 86, "「イヌゴエ」「タクミくんシリーズ」などを手がける横井健司監督によるサスペンスドラマ。2人の姉妹の間に生じた小さな亀裂から、5人の幼なじみを巻き込んだ愛憎劇が繰り広げられる。主演は人気グラビアアイドルの秋山莉奈。故・飯島愛の自伝小説を映画化した「プラトニック・セックス」（2001）でデビューし、11年に芸能界引退を発表した加賀美早紀も出演。加賀美にとって本作が引退作となった。", "2012-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Cotton Club", "コットンクラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "「アウトサイダー」「ランブルフィッシュ」と、青春映画を続けて手掛けていたF・F・コッポラ監督が作り上げた、見ごたえのある人間ドラマ。舞台は、禁酒法下の1920年代後半～1930年代にかけての、ハーレムにある豪華なナイトスポット。コルネット奏者からギャング映画スターになっていく若者を中心に、芸に、野望に、そして愛に命を懸ける人間の生きざまを、セピア色を基調とした味わい深い映像の中に、あざやかに浮かび上がらせている。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Electric Horseman", "出逢い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "CMタレントとして企業の操り人形になってしまっている元ロデオのチャンピオンが、ラスベガスのデモンストレーションの会場で自分と同じ境遇にある名馬と出会う。彼は馬を野生に戻そうと失踪し……。ポラックとレッドフォードのコンビが文明社会への批判を描く。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESTRY RIDES AGAIN", "砂塵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "悪徳の賭博師が支配する西部の町に、正義を呼び戻そうと若き保安官補がやって来る。孤軍奮闘するもついには賭博師と対決、賭博師の銃弾は、保安官補をかばう酒場の歌姫にあたってしまう……。ディートリヒが多面性を見せ、新境地を開いたといわれる作品。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチバン　アルティメイタム", "ガチバン　アルティメイタム", "", "", "http://image.eiga.k-img.com/images/movie/56923/315x210.jpg?3295", 75, "ヤンキー映画「ガチバン」シリーズ第11作。新宿歌舞伎町でヤクザに囲まれた勇人は、金ドクロの皮ベストを着たバイカー集団「ゴールドスカル」の一匹狼・銀次に助けられる。一方、少年院から出所した紋児は、自分を少年院送りにした男にかたをつけようと動き出すが、その相手こそが銀次だった。「ガチバンII」から「VI」まで主演を務めた佐野和真と、「ガチバンMAX」シリーズに主演した窪田正孝がダブル主演を務める。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真夏のオリオン", "真夏のオリオン", "", "", "http://image.eiga.k-img.com/images/movie/53873/315x210.jpg?1974", 0, "池上司の小説「雷撃震度一九・五」を、「亡国のイージス」「ローレライ」の福井晴敏がオリジナルの要素を加えて映画用に脚色、篠原哲雄監督がメガホンを取った戦争アクション。太平洋戦争末期、互いに姿の見えないまま激闘を繰り広げる日本海軍イ-77潜水艦と米国海軍駆逐艦パーシバルの乗組員たちの姿を描く。主演の玉木宏ほか、CHEMISTORYの堂珍嘉邦が映画初出演。戦時中と64年後の現代をつなぐ2人の女性を北川景子が1人2役で演じる。", "2009-06-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("波止場の賭博師", "波止場の賭博師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "小林旭がすご腕のギャンブラーに扮し、「カサブランカ」ばりのラブ・ストーリーを展開していく日活アクション。横浜のクラブ社長であり“逃がし屋”の異名を持つ賭博師の秋津五郎のもとに、一組のアベックが密航を依頼してきた。しかし、その女の方は秋津がかつて神戸で捨てた初恋の女だったのだ。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBIN HOOD：PRINCE OF THIEVES", "ロビン・フッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "十字軍の遠征から帰ったイギリス貴族の青年が、悪の支配する王国に平和を取り戻すべく大活躍する様を、壮大なスケールで描いたスペクタクル・アドベンチャー・ロマン。持ち前のヒロイズムで、ロビン・フッドというヒーローのカリスマ性を見事に醸し出したオスカー役者、コスナーの演技が光る。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太と翼の勇者たち", "ドラえもん のび太と翼の勇者たち", "", "", "http://ecx.images-amazon.com/images/I/21X3XK08JJL._SL210_.jpg", 90, "人気アニメ『ドラえもん』の劇場シリーズ第22作。鳥人間たちが住む世界“バードピア”を舞台に、鳥少年グースケとのび太らの冒険を、環境破壊に対する警鐘も込めながら描く。ある日、のび太たちの目の前で時空が歪み、その中から鳥人間が現れる。グースケというその人物は鳥人間の住む世界“バードピア”から地球に迷い込んできたのだ。のび太たちはグースケをかくまいながら彼を元の世界に戻そうとするが、そんな時、再び時空が歪み、のび太たちはバードピアに呑み込まれてしまう。一方、バードピアでは、環境破壊を続ける人類から鳥たちを守るため、人間世界に住むすべての鳥類をバードピアに移住させようとする人物がいた……。", "2001-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Tree of Life", "ツリー・オブ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/53203/315x210.jpg?1695", 138, "「天国の日々」「シン・レッド・ライン」のテレンス・マリック監督が、ブラッド・ピット、ショーン・ペンを主演に描くファンタジードラマ。1950年代半ば、オブライエン夫妻は中央テキサスの田舎町で幸せな結婚生活を送っていた。しかし夫婦の長男ジャックは、信仰にあつく男が成功するためには「力」が必要だと説く厳格な父と、子どもたちに深い愛情を注ぐ優しい母との間で葛藤（かっとう）する日々を送っていた。やがて大人になって成功したジャックは、自分の人生や生き方の根源となった少年時代に思いをはせる……。製作も務めたピットが厳格な父親に扮し、成長したジャックをペンが演じる。第64回カンヌ国際映画祭でパルムドールを受賞した。", "2011-08-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ME WITHOUT YOU", "ミー・ウィズアウト・ユー", "", "", "http://ecx.images-amazon.com/images/I/51VBGHKA6ML._SL210_.jpg", 100, "幼い頃から一緒に過ごしてきた女の子二人の1970～90年代の歩みをたどる青春映画。友情と敵対心が入り交じる女同士の微妙な関係が、時代を反映したロック＆ポップスに乗せて綴られる。家が隣同士の幼なじみとして育ったホリーとマリーナ。パンクに夢中になった多感な高校時代を経て、同じ大学に進んだ二人はルームメイトになる。しかし、偶然にも年上の男性ダニエルを同時に愛したことで関係に亀裂が生じてしまう。", "2003-04-19", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MINDWARP", "マインドワープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "核戦争後の荒廃した地上を避け、人類は地下深くコンピュータに管理された社会を築いていた。人々は単調な日々から逃避するため、首につけられたプラグを通して“幻想の現実”に浸る毎日を過ごしている。そんな中、その幻想を拒否したジュディは、地上に追放されてしまう。地上では恐ろしいミュータント人間が彼女に襲いかかってきた。危機一髪のところでストーバーという男に助けられるが……。不気味なミュータントなどの特殊メイクを担当したK.N.B.EFXグループは「エイリアン２」「プレデター」に参加したスタッフでもある。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Package", "ザ・パッケージ／暴かれた陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "　西ベルリンで行なわれていた米ソの平和条約締結会議で警備任務に就いていたギャラガー軍曹はワシントンまで囚人を護送することに。しかし何者かに襲われ、囚人に逃げられてしまう。やがてギャラガーは罠に落ち、窮地に追いやられる。別れた妻アイリーンの協力を得て、事実を究明しようとするが……。巨大な陰謀に巻き込まれていく軍曹の戦いを描いたポリティカル・スリラー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACK SCORPION", "黒い蠍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「キング・コング」「猿人ジョー・ヤング」のウィリス・H・オブライエンが特撮を担当したSFモンスター映画。メキシコの火山帯周辺でナゾの毒素による死者が発生。地質学者スコットらが首をひねるなか、想像を絶する大きさのサソリが牧場に出現し、サン・ロレンツォの町にもサソリの群れが現れる。やがてサソリはメキシコ・シティにも迫り……。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("НЕСКОЛЬКО ИНТЕРВЬЮЛО　ЛИЧНЫМВОЛРОСАМ", "インタビュアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "グルジアの首都トリビシ。新聞社の投書欄を担当する女性記者ソフィユは夫と二人の子供に囲まれ、忙しくも楽しい生活を過ごしていた。ジャーナリストとして自分の仕事に誇りを持ちつつ、家庭を大切にすべく全力で仕事に打ち込んでいた。そんなある日、彼女の目の前で、若い女性と楽しそうに過ごしている夫を目撃してしまった。夫がなぜ？　と原因を考えるが、何もわからない。そして、久しぶりに夫とデートをしても夫は楽しそうな素振りを見せなかった。彼女はその時、ある決意を固めるのであった……。仕事を持つ主婦の生きがいや苦悩を描いたソ連映画。", "1978-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サラリーマンNEO 劇場版（笑）", "サラリーマンNEO 劇場版（笑）", "", "", "http://image.eiga.k-img.com/images/movie/56313/315x210.jpg?2223", 108, "2004年に単発番組として放送されて反響を呼び、06年からレギュラー放送されているNHKのコント番組「サラリーマンNEO」の劇場版。社長命令により業界シェアナンバーワンを目指すことになった万年業界5位のNEOビール社。中西課長率いる営業一課では「無重力ビール」など珍妙なビールが次々と提案されるが、どれも商品化にこぎつけずにいた。そんなある日、新入社員の新城がその場しのぎで口にした企画がなぜかそのまま通ってしまい……。主演は小池徹平とTV版レギュラーキャストの生瀬勝久。その他、主題歌も歌う郷ひろみや「AKB48」の篠田麻里子らが出演。", "2011-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La marche de l'empereur", "皇帝ペンギン", "", "", "http://image.eiga.k-img.com/images/movie/1383/315x210.jpg?1279", 86, "マイナス40度の南極大陸に棲息する皇帝ペンギンの生態を情感豊かに描く感動ドラマ。05年にフランスで公開されて「WATARIDORI」「ディープ・ブルー」の10倍以上のヒットを記録。監督リュック・ジャケは動物行動学の研究者で、3人の仲間たちとともに南極で8880時間に渡る撮影を敢行。子供のためにエサを求めて100キロ以上移動する母親ペンギン、ブリザードの中を120日間も絶食しながら卵を暖める父親ペンギンなどを撮影。", "2005-07-16", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIN　QUERER", "フラミンゴの季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "サンダンス映画祭で好評を博したミステリアスな人間ドラマ。舞台は南米、パタゴニア。地の果てともいうべき、この荒涼とした土地にやってきた測量技師のマリオは、開発を望む地元の人々の期待に迎えられる。洋品店の妖艶な女主人や、少女を愛人として囲っている町の実力者、そして行方不明となった父親を探すインディオ。様々な人々とかかわるうちに、マリオは村の意外な事実を知る……。人間の欲望を静かに浮び上がらせた深みのあるドラマはもちろん、ゴツゴツした岩肌が露出した原野や寒々とした砂漠などの風景の枯れた味わいが、鮮烈な印象を残す。", "1997-01-01", "7,26,22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ФЕРЕНЦ ЛИСТ／FERENS LISZT", "フランツ・リスト 愛の夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 153, "ハンガリーを代表する作曲家でピアノの天才とうたわれたリストの生涯を描く伝記映画。９歳でデビューし19世紀前半に芸術の都パリの社交界で人気を博すリスト。華麗な女性関係や慈善演奏会、カロリーナ夫人との恋などが名曲『愛の夢』とともに繰り広げられる。", "1971-01-01", "12,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("弱虫〈チンピラ〉", "弱虫〈チンピラ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "立原あゆみの人気コミックを映像化したヤクザ映画。極道になりきれないチンピラの奮闘を、ペーソスを交えて描き出す。ヤクザの情婦に手を出したのが縁で、月島組組員の子分となった若者、修。気弱だが女性を惹きつける不思議な魅力を持った彼は、抗争の犠牲になりかけた組長の愛人、景子に助けを求められ、組への忠誠と愛情のはざまで苦悩する。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rock 'n' Roll High School", "ロックンロール・ハイスクール", "", "", "http://image.eiga.k-img.com/images/movie/32623/315x210.jpg?3653", 90, "NYパンクの重鎮ラモーンズが出演、ライブ映像も満載の爆裂ロックンロール青春映画。製作総指揮は、B級映画の巨匠ロジャー・コーマン。授業中にもかかわらず、教室のスピーカーからラモーンズのナンバーが流れ、生徒たちが踊りまくるビンス・ロンバルディ高校。ところが、新しく赴任して来た女性校長はロックを全面禁止にし、生徒たちを厳しく取り締まり始める。生徒のリフは、これに対抗するためにある計画を思いつくが……。2012年、ロジャー・コーマンの軌跡をたどったドキュメンタリー「コーマン帝国」（11）の公開にあわせて日本初公開。", "2012-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハルのふえ", "ハルのふえ", "", "", "http://image.eiga.k-img.com/images/movie/57343/315x210.jpg?3366", 48, "", "2011-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZUCKERBABY", "シュガーベイビー　ミュンヘン 男と女の夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "醜く太ったハイミスのマリアンネ。そんな彼女が、ある日、地下鉄の若くハンサムな運転手フーバーに一目惚れ。さっそく彼女の調査が始まり、その効が奏して、めでたくフーバーと仲良くなるが……。「バグダッド・カフェ」のアドロン、ゼーゲブレヒトのコンビが、現代の「醜女の深情け」をペーソスたっぷりに描く。", "1984-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEEP IN MY HEART", "我が心に君深く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "名曲『恋人よ我れに帰れ』、映画「The Desert Song」「皇太子の初恋」などで知られる大作曲家、シグムンド・ロムバーグの伝説映画。内容は単純なサクセス・ストーリーだがゲスト出演のメンバーがすごい一編だ。ジーン・ケリー、シド・チャリシー、ローズマリー・クルーニーらの歌と踊りが満載。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("呪怨　白い老女", "呪怨　白い老女", "", "", "http://image.eiga.k-img.com/images/movie/54593/315x210.jpg?2770", 0, "女子高生あかねの幼い頃の親友・未来の家で起こった一家惨殺事件。受験に失敗した息子が家族を惨殺したのち自殺したのだが、発見された息子の遺言テープには、未来の不気味な声も録音されていた。そして、霊感の強いあかねの前に小学生の姿の未来が現れ……。1999年にリリースされたビデオ版「呪怨」の誕生10周年記念作品で、「呪怨／黒い少女」と同時公開。シリーズの生みの親である清水崇監督が原案・監修、人気グラビアアイドルの南明奈が映画初主演。", "2009-06-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志・次郎長初旅", "次郎長三国志・次郎長初旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "庵原川のけんかのあと始末のため、次郎長はお蝶と夫婦約束の盃を交わして旅に出る。一行は旅の途中で、小料理屋を営む兄弟分の沼津の佐太郎の世話になる。ところが、佐太郎の博打癖で今では店も廃屋同然、酒の振る舞いもままならない。その窮状を佐太郎の女房は、亭主の大事な客人のためならと着物を売ってしのぐのだが……。そんなよくできた女房の計らいすら、博打の誘惑でまたもやフイにしてしまう堺左千夫扮する佐太郎の愚かさ加減。そして愚かだけどお人好しの亭主を結局憎めず、泣き笑いで許してしまう女房・お徳の愚かさ、人の好さ。庶民を代表するような夫婦の関係の機微がにじみ出たペーソス編で、次郎長一行も佐太郎の博打代に着物まですられたのに、この夫婦が愛おしく、裸のまま明るく旅空の下に駆け出していく。なお、本作で森繁久彌の森の石松が初登場する。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIJINSKY", "ニジンスキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "1912年のブダペスト。ロシア・バレエ団を主宰するディアギレフに見出された22歳のニジンスキーは、革命的な振り付けと踊りで、一躍時代の寵児となる。不世出のバレエ・ダンサーの天才と狂気を、伝記に基づいて忠実に再現しようとした野心作。", "1980-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SOUL OF A MAN", "ソウル・オブ・マン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "有名監督が参加した全７本のドキュメンタリー・シリーズ“THE BLUES Movie Project”からの一編。1999年の「ブエナ・ビスタ・ソシアル・クラブ」では古いキューバ音楽を復活させたヴェンダース監督が、アメリカのルーツ音楽“ブルーズ”の真髄に迫る。ルー・リード、べック、ニック・ケイヴほか多種多様なミュージシャンによる演奏シーンは必見。宇宙へ打ち上げられた無人探査船ボイジャー号には、地球外の知的生命体へのメッセージを込めて、一枚のレコードが搭載された。そのレコードに収録された古いブルーズ曲を足がかりに、３人の偉大なブルーズマンの足跡をたどる。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAUNTED SUMMER", "幽霊伝説 フランケンシュタイン誕生秘話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ある邸に集まった５人のイギリス人。彼らの中で詩人シェリーの妻メアリーと、邸の主治医ポリドリは、この夏の体験に触発されて、メアリーは『フランケンシュタイン』を、ポリドリはその生涯のうち唯一の作品『吸血鬼』を書き上げた。メアリーもこの処女作におよぶ作品は残していない。彼らの人生を大きく変える出来事とは何であったのか。ある夏のレマン湖での文学史に残る一夜を描く。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Aurore", "オーロラ", "", "", "http://image.eiga.k-img.com/images/movie/33773/315x210.jpg?4755", 96, "踊ることを禁じられた国を舞台に、踊りの才能に恵まれたオーロラ姫と貧しい画家との悲恋を描く。パリ・オペラ座の舞台裏にスポットを当てたドキュメンタリー「エトワール」のニルス・タベルニエ監督が、パリ・オペラ座のトップ・ダンサーたちの全面協力を得て、切ない恋の物語を美しく幻想的に描き出す。主演はオペラ座バレエ学校の新星、マルゴ・シャトリエ。相手役を、パリ・オペラ座のトップ・スター、ニコラ・ル・リッシュが演じる。", "2006-12-16", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE 焼肉 MOVIE プルコギ", "THE 焼肉 MOVIE プルコギ", "", "", "http://image.eiga.k-img.com/images/movie/33963/315x210.jpg?2178", 114, "究極の焼肉を求める料理人同士の対決を描いたフード・エンタテインメント・ムービー。北九州を舞台に、地元で絶大な人気を誇るホルモン店“プルコギ食堂”で修行中の青年タツジと、全国制覇を目論む大手焼肉チェーン“虎王”の御曹司トラオが、“白肉”対“赤肉”の焼肉バトルを繰り広げる。タツジに松田龍平、トラオにモデル出身のARATA、“プルコギ食堂”の看板娘ヨリに山田優が扮し、桃井かおり、田口トモロヲら個性派俳優陣が脇を固める。", "2007-05-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRIVE〈ドライブ〉", "DRIVE〈ドライブ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "毎日、午後１時にある交差点で美しいOLを見つめることが日課の営業マン、朝倉。彼の車に銀行強盗３人組が乗り込んでくる。しかし極度のカタブツである朝倉が頑なに制限速度を守ったため、彼らのドライブは思わぬ方向に転がっていく。ふとした偶然から不条理な状況に陥った男たちの物語を疾走感たっぷりに活写してきたSABUが、トラウマなどの悩みを抱える登場人物の運命を、幻想的なエピソードを盛り込んで描く長編第５作。主人公たちがひたすら破滅に向かうのではなく、ささやかな幸福を見いだしていく展開もSABU作品としては目新しい。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("築城せよ！", "築城せよ！", "", "", "http://image.eiga.k-img.com/images/movie/54473/315x210.jpg?3872", 0, "上方歌舞伎界のスター俳優・片岡愛之助が主演を務める異色コメディー。過疎化が進む現代の町・猿投に、400年前に自分の城を完成できぬまま無念の死を遂げた3人の戦国武将が蘇った。「築城せよ！」の号令の下、住民たちをも巻き込んだ巨大ダンボール城の建築が開始されるが、築城現場に工場建設を計画していた町長らが城の撤去に乗り出す。長編初監督となる古波津陽が、海外で高く評価された同名短編をセルフリメイク。", "2009-06-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新諸国物語 紅孔雀　第一篇 那智の小天狗", "新諸国物語 紅孔雀　第一篇 那智の小天狗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "南紀州の浜に住む代官・那智の嘉門は、ローマの聖者から授けられた紅孔雀の宝庫の鍵を持っていた。元海賊の綱の長者は、されこうべ党の妖術師・一角とともに、密かにそれを狙う。DVDは「新諸国物語　紅孔雀(1)」に収録。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHANTOMS", "ファントム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "コロラド州スノーフィールド町の住民400人が突如として姿を消した。町を離れていた姉妹が戻ってみると、そこにはつい先程まで人々が生活していた痕跡と、おぞましい死体などが残されている。やがて姉妹たちにも、身も凍る恐怖の魔手が……。モダン・ホラーの鬼才ディーン・クーンツが、自らの原作をもとに製作総指揮・脚本化したスリラーの問題作。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャングル大帝", "ジャングル大帝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "手塚治虫の名作漫画を映画化。TVシリーズを劇場映画用に再編集したが、特に重要なエピソードとして知られるTVの第1話と第41話を中心に構成されている。そのため、原作が持っていた平和へのメッセージ性が色濃くなった作品に。ジャングルの王者ライオンが人間に殺され、妊娠中の牝ライオンは捕まり動物園へ。その途中で産まれた子供は逃亡し、無法地帯となっているアフリカのジャングルへ帰り、平和のために戦うことに。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANFAN LA TULIPE", "花咲ける騎士道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "フランスの大スター、ジェラール・フィリップが主人公を演じた同名作をL・ベッソンが脚本・製作を担当しリメイク。オーソドックスな時代劇に現代風アレンジをうまく取り入れたスピーディーな冒険活劇に仕上がっている。アクションで見せつつ、P・クルスとV・ペレーズが演じる、情熱的な恋もロマンティックに展開。18世紀のフランス。女たらしのファンファンは、強制結婚からの逃走中に手相が見れるという美女に出会う。王女と結ばれる運命にあるという彼女の言葉を信じ、自ら兵に志願。しかし、その美女は募兵官の娘で占いは嘘だった。", "2003-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JANE AUSTEN IN MANHATTAN", "マンハッタンのジェイン・オースティン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ジェイン・オースティンの幻の戯曲が発見され、これを買いとった芸術資金援助機関が二人の異なるタイプの演出家を競わせることにした。古典派とアバンギャルド派の二人は、激しい争いをしていくのだった……。「ローズランド」「ニューヨークの奴隷たち」と並ぶ、J・アイヴォリーの“ニューヨーク三部作”の一つ。", "1980-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("源氏物語・あさきゆめみし／Lived in a Dream", "源氏物語・あさきゆめみし／Lived in a Dream", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "大和和紀の大ベストセラー少女漫画を唐十郎が脚色。「MISTY」に続いてこれが劇場公開第２作となる三枝健起が監督を務め、音楽を実兄の三枝成章が担当している。キャストすべてが現役宝塚歌劇団の女優陣によって演じられ、美の競演が話題となった。源氏は今、母と生き写しのような藤壺の宮の膝枕で眠っている。藤壺の宮は源氏の父の妻にあたる。幼少のころから彼は彼女を慕ってきたが、次第にその感情は愛へと変化していくのだった……。幼少時代から須磨に下るまでの若き光源氏を中心にいくつもの愛が綴られていく。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Funeral", "フューネラル　流血の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ニューヨークを拠点に活動する鬼才A・フェラーラが発表したディープなマフィア映画。1930年代、ニューヨーク近郊に暮らすテンピオ一家に、三男坊ジョニーの棺が運び込まれる。怒りに燃える冷徹な長男レイは犯人探しを始めるが、それは思わぬ流血事件の引き金になっていく……。凶暴な次男チェズを含めたマフィア３兄弟の人間性と、彼らを取り巻く女たちの関係が、フラッシュバックを用いた映像でたどられる。非情な世界に生きる男たちの宿命を凝縮した衝撃的なラストが圧巻。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ガンダムI", "機動戦士ガンダムI", "", "", "http://ecx.images-amazon.com/images/I/51TZ7GRWVTL._SL210_.jpg", 0, "1979～80年に放映されたTVアニメ「機動戦士ガンダム」を再編集した劇場3部作の第1部。宇宙世紀0079年、地球連邦政府と、その政策に不満を抱くジオン公国との間に戦争がぼっ発。その戦いに巻き込まれた少年アムロ・レイは、連邦軍が開発した最新モビルスーツ、ガンダムに偶然乗り込み、ジオン軍のモビルスーツを撃退する。連邦軍の多くの正規軍人が戦死したため、アムロはそのままガンダムのパイロットとして戦うことになる。", "1981-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("可愛い悪女", "可愛い悪女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "『週刊スクープ』の社長・長谷川は妻の浮気の腹イセに、遊びなれたカメラマン・マヤと浮気し妻を嫉妬させようとたくらむ。ところがそれがアダとなり、長谷川は妻と彼女の浮気相手に殺されてしまう。次に狙われるのはアリバイ工作の邪魔になるマヤだった……。モーテル、カーセックス——当時の性風俗を織り込んだエロティック・サスペンス。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAMPO 奥山バージョン", "RAMPO 奥山バージョン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "完成した作品に不満を持ったプロデューサーが、自らメガホンを取って全体の70％を撮り直し、再編集を施した改訂版。場面は多少前後するものの、基本的な構造は変わらないので、物語はオリジナルの“黛バージョン”を参照のこと。主な変更点は(1)冒頭に米映画「マイ・ライフ」などのブルース・J・ルービン監督による英語ナレーション追加　(2)「お勢登場」のアニメ化（監督は名倉靖博）(3)著名人を多数集めたパーティーとそれに続くホテルでの場面を追加　(4)サブリミナル効果に訴えるコマ単位の編集　(5)「１／fゆらぎ」を導入した美術セット　(6)伯爵邸での映画的趣向の強化　(7)主題歌にジャズの名曲「オール・オブ・ミー」を使用　(8)上映館にフェロモン入り香水をまいて観客の嗅覚を刺激する、など。改訂は主に“見せ物的な視点の導入”を意図しており、観客は随所ではぐらかされて困惑するほかない。映画というよりイベント性にこそ眼目があり、ある意味では一連の騒動そのものが、そのまま「RAMPO」だともいえる怪作。乱歩生誕、映画誕生、松竹創立100年をそれぞれ記念している。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADVENTURES OF HUCK FINN", "ハックフィンの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "おなじみのM・トゥエインの小説を、E・ウッドの主演で描いたキッズ・アドベンチャー・ストーリー。飲んだくれの父から逃れるため、引き取られていた老姉妹の家を飛び出し、冒険の旅に出たハックフィン。途中、逃亡黒人奴隷のジムと出会った彼は、二人して自由を目指し、あてのない旅を続ける……。E・ウッドの芸達者ぶりにも注目のファミリー・ドラマ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOLDEN SEAL", "ゴールデンシール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "北の果てアリューシャン列島が舞台。伝説の黄金のアザラシと友だちのいない少年との友情を中心に、アザラシを狙う大人たちの貪欲さと少年の純真さとの葛藤を描いた愛の寓話。厳しくも美しい極北の自然をバックに、心洗われるドラマが展開され、観る者の心を打つ。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("益子日和", "益子日和", "", "", "http://image.eiga.k-img.com/images/movie/57953/315x210.jpg?4832", 0, "2012年・第4回沖縄国際映画祭の企画「地域発信型プロジェクト」で製作された作品。栃木県益子町を舞台に、1人で日帰りの観光にきたOLの千尋が、ふと立ち寄ったカフェの気さくな店長に誘われ、益子焼の食器を持ち合った仲間たちの鍋パーティに参加して人々と触れ合っていく姿を描く。", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROM HELL TO VICTORY", "戦争と友情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "第二次大戦前夜の1939年８月24日。パリのレストランに集まった国籍の異なる６人の男女は、毎年同じ日にこの場所に集まることを誓う。敵と味方に分かれながらも、彼らは決して友情を忘れることはなかった。そして５年後……。友情をテーマにした人間ドラマ。", "1978-01-01", "3,2,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIOLENT CITY", "狼の挽歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "一匹狼の殺し屋が愛した女は、富と力を手に入れるためなら何でもする悪女だった。そのため殺し屋は幾度となく生命の危機にさらされるが、敢然と暗黒街の大ボスや悪徳弁護士に立ち向かっていく。人気絶頂期のブロンソンの代表的ハードボイルド・アクション。", "1970-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前弁当", "喜劇・駅前弁当", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "浜松駅前の老舗の駅弁屋“互笑亭”は未亡人の景子が弟の次郎と二人で経営しているが、次郎は極楽とんぼで店は景子にまかせきり。そんな時、亭主の３回忌に集まった旧友の織物会社社長・金太郎とストリップ小屋を経営する孫作から景子に話がもちこまれた。大阪の大資本家が店の拡張をバックアップしてもよいというのだ。久松静児が監督したシリーズ中の傑作。金太郎に森繁、孫作に伴、次郎に堺が扮する。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兄弟仁義 関東兄貴分", "兄弟仁義 関東兄貴分", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "お雪を足抜きにし、大滝一家に追われて新潟に逃れてきた清吉。嶋岡一家の身内になるが、嶋岡は、貧しい農家から女を買い、東京に売りとばしていた男だった。一方、清吉を追う大滝一家の客分、龍次は、女を助けようとする清吉の正義感にうたれ、彼に堅気になるよう説得する。だが嶋岡が卑怯な手段に出て……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悲しきヒットマン", "悲しきヒットマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "元山口組顧問弁護士・山之内幸夫の同名小説の映画化。巨大なヤクザ組織の中でヒットマン（殺し屋）としてしか生きられなかった男・高木昇が、極道入りを果たし、やがて対抗組織のドンを撃つまでの姿を描き出している。デビュー作「またまたあぶない刑事」で注目された一倉治雄の監督第２作。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLICE ACADEMY4: CITIZENS ON PATROL", "ポリスアカデミー4 市民パトロール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ご存じ“プッツン”警官たちの活躍（？）を描いたシリーズの第４作。今回は、定年で退官間近い“ポリスアカデミー”の校長ラサールが、長年の夢“市民自警団の育成”に着手。そして集まった、前にも増してとんでもない奴らと、卒業生たちが例によって珍騒動を起こす。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER STUDENT VON PRAG", "プラーグの大学生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "H・H・エーベルスの怪奇小説をもとにした怪奇映画の傑作。1913年、1936年にも映画化されたが、本作が最も有名である。19世紀のプラハを舞台に、悪魔と魂を売り渡す契約を結んだ野心家の貧乏学生が、契約の印として鏡からはがされた自らの鏡像に脅かされ、やがて破滅するまでを描く。ポーの小説『ウィリアム・ウィルソン』などにも通じる分身＝ドッペルゲンガーをテーマにした作品であり、「カリガリ博士」のC・ファイトが、学生とその影を見事に演じている。", "1926-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("楢山節考", "楢山節考", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "食糧不足のために、長男以外は結婚を認められず、70歳を過ぎた老人は子に背負われて、近くの楢山の頂きに捨てられなければならないという掟がある。山奥の寒村を舞台に、楢山の神様を信じ、捨てられることを神のもとに召されることだと喜び勇んでいる老婆と、母を捨てることに切なさを感じとまどう息子の姿を生類共棲、淘汰の自然観、運命観を込めて描く。1956年の中央公論新人賞に輝く、深沢七郎のデビュー小説の２度目の映画化。老婆役の坂本スミ子が、自らの歯を抜くなどして大熱演、1983年のカンヌ国際映画祭では、大島渚の「戦場のメリークリスマス」と激烈な争いを経て見事グランプリを獲得した。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("降霊", "降霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "霊能者の妻を持つ佐藤のトランクの中から、ある日瀕死の少女が発見された。看病の甲斐なく少女は死んでしまい、夫妻はその遺体を隠そうとするが……。ホラー映画の雄・黒沢清監督がTVムービーとして製作したサスペンス・ホラー・ドラマを劇場公開。役所広司をはじめとする実力派キャストの繊細な熱演とクールな演出との対比がいい。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EDWARD MUNCH", "ムンク 愛のレクイエム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 170, "19世紀後半に活躍した、表現主義の創始者的存在ムンク。彼の残した日記をもとに、この悲劇の画家の生涯を綴っていく。青年期に抱いた女性へのコンプレックス、彼の作風に大きな影響を与えたハイベルグ夫人とのロマンスなどを描きながらムンクの人間像に迫っていく。もちろん、『叫び』『マドンナ』など、彼の代表作も多数登場する。", "1976-01-01", "36,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JE T’AIME’ ，MOI NON PLUS", "ジュ・テーム・モア・ノン・プリュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "音楽家ゲンズブールの監督第１作。ゴミ運搬屋の青年二人はホモセクシャルの関係。ある日スナックで少年のような娘と出会い、複雑でグロテスクな三角関係が始まった……。現代人の“不毛な愛”を、エロティックな主題歌と独特の映像美で描いたスキャンダラスな一編。", "1975-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZEUS AND ROXANNE", "ゼウス＆ロクサーヌ　イルカにのった犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "犬とイルカの不思議な友情を綴るファミリー・ドラマ。音楽家テリーの愛犬ゼウスが、隣に住む海洋学者メアリーの研究対象であるイルカのロクサーヌと仲良しに。動物同士の交流に導かれてテリーとメアリーもお互いを意識するようになるが、メアリーのライバルの学者が思わぬ事件を引き起こす。オーディションで選ばれた芸達者な動物たちの愛らしい振る舞いも見もの。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Copie Conforme", "トスカーナの贋作", "", "", "http://image.eiga.k-img.com/images/movie/55823/315x210.jpg?4161", 106, "「友だちのうちはどこ？」「桜桃の味」などで知られる名匠アッバス・キアロスタミ監督が、初めて母国イランを離れて撮影したラブストーリー。イタリアの南トスカーナ地方の小さな村を訪れたイギリス人作家が、ギャラリーを経営するフランス人女性に出会う。2人はあるカフェの女主人に夫婦と勘違いされたことをきっかけに、まるで長年連れ添った夫婦であるかのように振る舞いはじめる。主演のジュリエット・ビノシュは、本作でカンヌ映画祭主演女優賞を受賞した。", "2011-02-19", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ZガンダムII A New Translation 恋人たち", "機動戦士ZガンダムII A New Translation 恋人たち", "", "", "http://ecx.images-amazon.com/images/I/512ASR8CS4L._SL210_.jpg", 98, "「機動戦士ガンダム」の続編として1985～86年に放映された「機動戦士Zガンダム」を、TV放映20周年を記念し劇場版として再構成した3部作の第2部。地球に降り立ったカミーユ・ビダンは、敵対するティターンズのパイロット、フォウ・ムラサメと出会い互いにひかれあうが、フォウは戦いのなかで命を落としてしまう。やがて宇宙に戻ったカミーユは、新たな力となるZ（ゼータ）ガンダムを手にする。", "2005-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("築地魚河岸三代目", "築地魚河岸三代目", "", "", "http://image.eiga.k-img.com/images/movie/34293/315x210.jpg?1664", 116, "“日本の台所”東京築地市場で繰り広げられる人間模様を描いた人気コミックを、大沢たかお、田中麗奈、伊原剛志ら実力派キャスト出演で実写映画化。エリート会社員の旬太郎は、恋人の実家である築地の仲卸店を手伝うことに。厳しいプロの世界で悪戦苦闘する旬太郎だったが、やがて会社を辞めて店を継ぐことを決心し……。映画「なんとなく、クリスタル」や多くのテレビドラマを手掛けてきた松原信吾監督がメガホンを取る。", "2008-06-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Minority Report", "マイノリティ・リポート", "", "", "http://image.eiga.k-img.com/images/movie/1123/315x210.jpg?2179", 145, "2054年のワシントン。プリコグと呼ばれる予知能力者が未来の殺人を予知、犯罪予防局が未然に逮捕することで犯罪が90％減少していた。犯罪予防局のアンダートンは最もそのシステムを信じていたが、自分が見知らぬ男を殺すことを予知され、一転して追われる身に。彼はプリコグの一人を連れて逃亡するが……。フィリップ・K・ディックの原作を、スピルバーグ監督、トム・クルーズ主演で映画化。撮影はスピルバーグ作品常連のヤヌス・カミンスキー。", "2002-12-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arrancame La Vida", "命を燃やして", "", "", "http://image.eiga.k-img.com/images/movie/54793/315x210.jpg?4343", 107, "アンヘレス・マストレッタの同名小説を壮大なスケールで映画化した歴史ドラマ。1930年代のメキシコ、15歳の美少女カタリーナは野心家の将軍アンドレスと出会い結婚する。権力を渇望するアンドレスが政治家へと転身する中で、カタリーナも自身の人生に目覚めていく。「アマロ神父の罪」「ワン・ミス・コール」のアナ・クラウディア・タランコンが主演を務め、「バッド・エデュケーション」のダニエル・ヒメネス・カチョらベテラン勢が脇を固める。", "2009-09-17", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誰が心にも龍は眠る", "誰が心にも龍は眠る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "役者や脚本家としても活躍する堀江慶監督が、「渋谷怪談」「渋谷怪談2」に続いて放ったミステリー・ホラー。記憶喪失となった少女が自分の封印された過去を模索するうちに、意外な事実に直面する。物語を脇からガッチリと支える津田寛治や遠藤憲一ら実力派キャストの怪演も見もの。幼少時代に街の湖で溺れて記憶喪失になったテルミ。12年後、幼なじみのユキオと溺れた理由を探り出した彼女は、意外な過去と直面する。", "2005-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GET CARTER", "追撃者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1971年のイギリス映画「狙撃者」のリメイク。ラスベガスの借金取り立て屋ジャックが、弟の葬儀に参列するため故郷シアトルへ。すぐさま弟が裏社会のイザコザによって謀殺されたと睨んだジャックは、危険な単独調査を繰り広げていく。S・スタローンが悲しい過去を持つ男の人生の再出発という十八番のテーマに取り組んだスタイリッシュなハードボイルド・アクション。雨に濡れたシアトルの情景描写が美しい。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thank You for Smoking", "サンキュー・スモーキング", "", "", "http://image.eiga.k-img.com/images/movie/1693/315x210.jpg?4029", 93, "タバコ業界のPRマンと嫌煙派の戦いを風刺したクリストファー・バックリーの小説「ニコチン・ウォーズ」（東京創元社刊）を、「ゴーストバスターズ」などで知られるアイバン・ライトマン監督の息子ジェイソン・ライトマンが映画化したブラックコメディ。「情報操作の王」と異名をとるタバコ団体のPRマンのニックは愛煙家の自由を守るべく、日頃から得意の話術を使って嫌煙家からのバッシングをかわしていたが、ある日突然、絶体絶命のピンチに直面する……。", "2006-10-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Sassy Girl", "猟奇的な彼女 in NY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "日本でもヒットした韓国映画「猟奇的な彼女」をハリウッド・リメイク。主演の“彼女”には「24」のエリシャ・カスバート。共演に「硫黄島からの手紙」のジェシー・ブラッドフォード。「世界でいちばん不運で幸せな私」のフランス人監督、ヤン・サミュエルがメガホンを取る。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("完結・佐々木小次郎巌流島決闘", "完結・佐々木小次郎巌流島決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「佐々木小次郎」３部作の完結編。恋人・奈美が琉球へ去り、小次郎はまた悲しい別れを経験するが、それもつかの間、宿敵・宮本武蔵との決戦に巌流島へと赴いていく。スマートな大谷＝小次郎と豪快な野人、三船＝武蔵のコントラストが魅力的。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGUS", "どんな時も", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "太った体格にコンプレックスを持つ男の子の恋物語を描いた青春映画。デブで外見のぱっとしないアンガスは、チアリーダーで学園一キュートなメリッサに片思い。彼女の恋人で乱暴者でいじわるなリックは、アンガスの気持ちを察したかのように、冬のダンス・パーティーでアンガスがキングになるよう策略し、クイーンのメリッサとダンスを踊らせようとするが……。思春期に誰もが経験する異性への憧れと揺れ動く気持ちを、コミカルにいきいきと綴る。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARK TOWER", "ゴーストインフェルノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "美貌の女性建築家キャロリンの作品である高層ビルで、次から次へと異常な殺人事件が発生する。事件の背後に人知を超えた霊的パワーが存在すると確信した捜査官のデニスは、キャロリンや霊能力者と組んで事件の核心に迫る。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANNEQUIN", "マネキン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "マネキン工場で働くジョナサンは、その芸術家（？）気質が災いして失敗ばかり。そんなある日、自分の作った美人のマネキンが突如人間に変身。彼女の持つ不思議な力のおかげで、デパートのウィンドー・デザインを手掛けることになり大成功を収めるのだが……。A・マッカーシーが主演したファンタスティック・コメディ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KEEP YOUR HANDS OFF MY DAUGHTER", "テンプテーション15才", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "早くに妻を亡くして、一人で二人の娘を育ててきたダグ。ある日、長女のケイティがステキな女に変身、ボーイフレンドを取っかえ引っかえ遊び回るようになったから大変。娘のヴァージンを守るべくダグの奮戦が始まる……。米TVの人気コメディアン、T・ダンザの主役デビュー作。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIDEOUS　KINKY", "グッバイ・モロッコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "精神分析の権威フロイトの孫娘E・フロイトが、少女時代の思い出にインスピレーションを受けて書き上げた小説を映画化。25歳のジュリアが二人の娘を連れてロンドンからモロッコにやって来る。不倫に疲れていた彼女はイスラム教の神秘的な魅力に引かれ、新たな人生を見い出そうとしていた。マラケシュの安アパートで暮らし始めた彼女は、貧しさから娘たちとの葛藤に苦悩する中、大道芸人のビラルと愛し合うようになるが……。心に傷を負った若い女性が異文化のなかで懸命に自分探しをする心の旅を、まぶしい太陽の光と情熱的な音楽があふれるエネルギッシュな国モロッコを舞台に描く感動作。母として女として、そして一人の人間として成長していくヒロインをK・ウィンスレットが熱演。", "1998-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸せの太鼓を響かせて INCLUSION", "幸せの太鼓を響かせて INCLUSION", "", "", "http://image.eiga.k-img.com/images/movie/55943/315x210.jpg?3891", 106, "知的障がいをもつ人々の生活を見つめ続ける小栗謙一監督による、「able」（2001）、「Host Town」（04）、「Believe」（05）に続くドキュメンタリー第4作。知的障がいのある人々が自立して暮らしている長崎県・コロニー雲仙で結成され、メンバー全員が知的障がいを抱える和太鼓奏者グループ「瑞宝太鼓」のメンバーやその家族、恋人たちがカメラと向き合い、自分たちが生きてきた道や社会について語る。また、世界的和太鼓奏者・時勝矢一路氏の指導のもと、メンバーが太鼓奏者として成長していく姿を追う。", "2011-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("団鬼六原作　鬼の花宴", "団鬼六原作　鬼の花宴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "「花と蛇」などで知られるSM文学界の巨匠・団鬼六の同名小説を映画化した官能ドラマ。関東新聞で営業部長を務める岡本は、貞淑な美人妻がいながら同僚の久美子と関係を持ってしまう。ある日、岡本は関東新聞の大手広告主である新興宗教・崇徳教の教祖・吉岡の過去のスキャンダルを久美子に話すが、翌日、その内容が記事となって関東新聞に掲載されてしまう。激怒した吉岡は、謝罪として岡本の妻・静代を巫女として差しだすよう要求する。", "2007-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Visitor", "扉をたたく人", "", "", "http://image.eiga.k-img.com/images/movie/53733/315x210.jpg?2818", 104, "愛妻に先立たれ、心を閉ざして孤独に暮らしていた大学教授のウォルターは、久々にニューヨークにある別宅のアパートを訪れるが、そこにはいつの間にか見知らぬ移民の男女が暮らしていた。シリア出身の青年タレクは“ジャンべ”というアフリカ発祥の太鼓の奏者であり、ウォルターはタレクからジャンベを習い始め、2人は次第に心を通わせていくが……。名脇役のリチャード・ジェンキンスが初主演を務め、第81回アカデミー主演男優賞候補となった。", "2009-06-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANOTHER COUNTRY", "アナザー・カントリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "厳格な規則のもとで優雅な英国紳士を育てるパブリック・スクール。その一つイートン校の寮生ガイ・ベネットは、着実にエリート・コースを歩んでいた。しかし、陰では美青年を愛する同性愛者でもある。そのことを快く思わない寮友が彼を弾圧。復讐に燃えるガイは、親友ジャドの共産主義思想に傾倒していく……。英国で実際に起こったスパイ事件の中心人物、ガイの青春時代をモデルにした舞台劇の映画化。舞台初演時にガイに扮したC・ファースがジャド役で好演している。特に日本では、“耽美派”少女たちの間でカルト的な人気を呼んだ１本。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOT SHOTS！PART DEUX", "ホット・ショット2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "大ヒット・コメディ映画の続編。我らがヒーロー、トッパーは湾岸戦争で捕虜になった米軍兵士を救出すべくペルシャ湾に向かった。行く手に待ち受ける敵や罠をものともせずトッパーは進む。まるで“ランボー”のように……。今回も「氷の微笑」をはじめ映画のパロディーが全編に満載されている。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("用愛捉伊人／YOU’RE MY DESTINY", "恋のカウントダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "香港に遊びに来ていた日本の社長令嬢は、街のディスコでユニークなバンドマンたちと知り合う。一方日本ではその令嬢の父親が死亡、４日以内に令嬢がサインしないと会社は悪どい副社長のものになってしまうことに。副社長は殺し屋を雇い、令嬢を監禁するため香港に向かわせるのだが……。日本と香港の二大アイドル歌手が共演した青春歌謡映画。", "1987-01-01", "76,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEVINOST BEZ ZSASTITE", "保護なき純潔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "1942年に製作されながらナチス・ドイツの検閲にあい、公開禁止となったセルビア映画があった。「保護なき純潔」というその映画の監督、主演を務めたのが幼き日のマカヴェイエフのヒーローでもあったアクロバット師のドラゴリューヴ・アレクシッチである。その「保護なき純潔」の断片と、作品にかかわった人々のコメントや、当時のニュースフィルム、そしてアレクシッチのその後の軌跡などを交錯させていきながら、当時の社会情勢をカリカチュアライズして描いていくマカヴェイエフの代表的傑作とされる。占領下にもかかわらず映画製作を敢行したアレクシッチに敬意を表しながら、純粋なる精神と愛の勝利をうたった映画愛にあふれる作品であるとして、現在に至るまで称賛を受け続けている。", "1968-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　炎神戦隊ゴーオンジャーVSゲキレンジャー", "劇場版　炎神戦隊ゴーオンジャーVSゲキレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/54323/315x210.jpg?1581", 55, "TVでおなじみのスーパー戦隊シリーズのヒーローたちが協力して悪と戦う、劇場オリジナルのシリーズ15作。炎神戦隊ゴーオンジャーが、激獣拳ビーストアーツを駆使して悪と戦う獣拳戦隊ゲキレンジャーとタッグを組み、悪の集団ガイアークと臨獣殿に挑む。", "2009-01-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿片台地・地獄部隊突撃せよ", "阿片台地・地獄部隊突撃せよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "北支戦線を舞台にして、横暴な上官の命令に反抗して自分の信念を貫く男を主人公に、軍用資金になる阿片の争奪をめぐる日中両軍の戦いが描かれる。つり橋一つで外界と隔絶された野戦地の極限状況を、緊迫感あふれる演出で見せる戦争アクションの傑作。主演の安藤昇が強烈な印象を残す。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KAUN", "ストーミー・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "嵐の夜、閉ざされた密室で男女３人が体験する恐るべき恐怖。インド映画界の鬼才ラム・ゴパル・ヴァルマが、たった３人のみの出演者、密室というユニークな手法を用い、斬新な映像美と巧みなドラマ展開で観る者を恐怖へ落とし入れたサイコ・スリラー。今までのインド映画にインサートされていた歌や踊りが一切ないのも特徴だ。", "1999-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MW ムウ", "MW ムウ", "", "", "http://image.eiga.k-img.com/images/movie/53763/315x210.jpg?2631", 129, "生誕80周年を迎えた手塚治虫の同名コミックを、玉木宏、山田孝之主演で映画化。16年前、ある島で島民全員が死亡する事件が発生し、政府によりその事実は隠匿される。事件から密かに生き延び、成長してエリート銀行員となった結城美智雄は、冷酷な殺人鬼という裏の顔を持ち、事件を引き起こした“MW”と呼ばれる存在を用いて世界の破滅を企む。もう1人の生存者で神父となった賀来裕太郎は、結城が繰り返す犯行を止められないことに苦悩するが……。", "2009-07-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦国魔神 ゴーショーグン", "戦国魔神 ゴーショーグン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "同名のTVシリーズより第17話『グットサンダー危機一髪』と第20話『宇宙中継これがドクーガだ』を、劇場用として公開。一部新作部分が描き加えられ、悪の秘密組織・ドクーガと戦う北条真吾ら３人の若者の活躍を、軽妙なタッチで綴っている。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンの近藤勇", "エノケンの近藤勇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "エノケンが一人二役を演じた、P・C・L初の時代劇。新選組隊長・近藤勇は加納という男を連れて祇園に行く。加納はそこで偶然愛人の雛菊に会う。彼女はこれから島原に売られていくということだ。近藤は二人が今後も会えるように資金を提供してやるが……。監督は、数多くのエノケンもの演出で評価の高い山本嘉次郎。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FI FI LA PLUME", "フィフィ大空をゆく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "A・ラモリス監督が、映像と音楽によって描いたファンタスティックな物語。時計泥棒がサーカス団の団長に見つかって、“鳥人”にされてしまうことに。フィフィという芸名ももらった彼は、サーカスの花形として活躍するようになる。やがて彼は、サーカスの娘、ミミと恋仲になるのだが……。", "1965-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("XX〈ダブル・エックス〉美しき凶器", "XX〈ダブル・エックス〉美しき凶器", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "大沢在昌の短編小説『ゆきどまりの女』の映画化。政界のフィクサー、国分の元で殺し屋の手配をしている吉沢。彼は、そればかりか用済みになった殺し屋を一軒家に誘い出し、妖艶な女を使って殺させていた。長年、吉沢の殺し屋をしてきた坂上は、そのカラクリに気づきながらも、女に惹かれていく……。謎の女を、オールヌードの体当り演技で演じた宮崎ますみが見もの。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pippi goes Aboard", "ピッピ船にのる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「ロッタちゃん」「やかまし村」シリーズでおなじみのアストリッド・リンドグレーンによる児童文学の名作を基に、本国スウェーデンのオル・ヘルボム監督が映画化。“世界一強い女の子”ピッピが巻き起こす大騒動をコミカルに描く。ポンコツ船を直して無人島へやって来たピッピ。仲間たちとの楽しい毎日はあっという間に過ぎ、やがてクリスマスが近づいてくる。クリスマスのお祝いに参加するため、ピッピは学校へ通うことになるが……。", "1970-01-01", "14,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAVAGE BEACH", "ピカソ・トリガー2　 サベージ・ビーチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "プレイメイトたちが多数出演し、ド派手なアクション・シーンを展開するセクシー・バイオレンスのシリーズ第２作。FBI捜査官の二人の女性が、太平洋の離島へ血清を空輸する途中、嵐に遭遇し、無人島へ不時着してしまう。その島は第二次大戦中、日本軍が黄金を隠匿したとされる島で、その黄金をめぐり戦いが繰り広げられる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大空の無法者", "大空の無法者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "とある小さな島の航空会社に腰を落ち着けることになった佐竹吾郎。だがこの航空会社には謎が多く、ある日社長に連れられて島を飛び立つと、なんと海上の貨物船から麻薬の入った木箱を吊り上げていた。吾郎はその様子を無線機で連絡、しかしそれが傍受され、保安官という彼の正体がバレてしまう。スリリングかつ軽快に展開するサスペンス・アクション。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BMX BANDITS", "BMXアドベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "BMX小僧のグースとP.J.は、銀行ギャング一味が襲撃用に隠していたトランシーバーを発見し、追いつ追われつの大活劇を展開。BMXの疾走感が、オーストラリアのさわやかな空気を伝えるようで、気持ちいい。", "1983-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zero WOMAN 警視庁０課の女", "Zero WOMAN 警視庁０課の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "“警視庁０課”それは、警察が表立って関与できないダーティーな仕事を行う“裏警察”のこと。主人公レイは、親代りである上司武藤の下、０課の危険任務に駆り出される日々を送っていた。しかし、捜査１課の小田とひき逃げ事件を目撃したことが彼女の人生を大きく変えた。ひき逃げ事件に執拗に食い下がった小田は惨殺され、レイは事件の背後に意外な人物が噛んでいることを知る。レイは権力という名の巨悪に向かって、捨て身の闘いに身を投じていく。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学校の怪談２", "学校の怪談２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1995年夏に公開され大ヒットした「学校の怪談」のシリーズ第２作。前作で主演した野村宏伸が別な役どころで登場する。塾の講師・理香は春休みの集中合宿のため、生徒を連れて故郷にやって来た。ところが宿泊場所の寺の隣にある南小学校には、“４月４日４時44分の伝説”という奇妙な噂があり……。岸田今日子の怪演で恐怖度がアップ。第１作をしのぐ興業収益をあげた。DVDは「学校の怪談　DVD-BOX」に収録。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天地雄心／ARMAGEDON", "アルマゲドン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "20世紀末を舞台に、全世界を混乱させた著名人の失踪事件の行方を描くSFパニック。人類の未来を握る人物として、世界中から選ばれた10名が一人また一人と失踪する。この不可解な事件は、神の手による人類選抜とウワサされた。そんな中、中国人で唯一選ばれた科学者ケンと護衛を任された香港警察のチウは、プラハで行われる“最後の審判”のイベントに出席。そこでケンは神の預言者と名乗る男と出会う……。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIN DE SEMANA PARA LOS MUERTOS", "悪魔の墓場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "イギリスの田舎町で、殺人犯に疑われた若者ジョージは意外な事実を知る。真犯人は共同墓地から蘇ったゾンビだったのだ……。ロメロ監督の「ナイト・オブ・ザ・リビング・デッド」の改作版ともいうべきホラー映画。本作は、害虫駆除機の放つ超音波で死者がよみがえる設定だ。", "1974-01-01", "9,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COVERED WAGON", "幌馬車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "遥かオレゴンを目指し、西部の荒野を一団の幌馬車隊が出発する。希望に満ちたこの旅も、数々の苦難のため脱落者や仲間割れが続出。そのうえ、白人に恨みを抱くインディアンが一行を皆殺しにすべく待ち構えていた……。壮大な自然の中に人間の愛憎を織り込み、無声映画史上に燦然と輝く名作西部劇。", "1923-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("СТАЛКЕР", "ストーカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 163, "地上にこつ然と出現したミステリアスな空間“ゾーン”。その奥には人間の願いを叶える部屋があるといわれ、この部屋への案内人はストーカーと呼ばれていた。「惑星ソラリス」で独自のSF空間を作り出したタルコフスキーが、「鏡」を経て再び取り組んだSF作品。ゾーンをとり巻く神秘的な生命の生成と躍動を具体的な現象ではなく、“水・雨・火”といった独自の映像言語を駆使することによって、見事に表現。視点によって変わるカラーとモノクロの色彩構成など、難解といわれながらも、自己の映像哲学と芸術性を追求し続けた同監督の、はてしない魅力にあふれた一編となっている。前作「鏡」同様、タルコフスキー監督の実父で詩人のアルセニー・タルコフスキーの詩が効果的に挿入されている。", "1979-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gentleman Prefer Blondes", "紳士は金髪がお好き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ローレライとドロシーはナイトクラブで踊り子をしながら玉の輿に乗ることを夢見ている。ローレライは金持ちの息子ガスをつかまえているが、彼の父親は2人の挙式に反対している。ローレライとドロシーはフランス旅行を決行して豪華客船に乗り込み、ガスの父は彼女の素行を調べるため私立探偵マローンを送り込み……。会社側は当初はミュージカルスター、ベティ・グレイブルを主演にと考えていたが、出演料が高額のため断念、「モンキー・ビジネス」にも出演し、この時点で出演料が安かったマリリン・モンローを起用したという。", "1953-08-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大奥十八景", "大奥十八景", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "東映久々の時代劇大作。世継ぎのない将軍・家綱が次々に女を漁り、悲劇が生まれるのが前半。後半は家綱の死後、彼の落としだねをめぐって陰謀が繰り広げられる。前半のエロス、後半の活劇と鈴木則文の持つ娯楽性が発揮されているが、全体の構成はいささか散漫。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MINNIE THE MOOCHER AND MANY MANY MORE", "ミニー・ザ・ムーチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "1920年代後半から30年代にかけて、その独特の声質とアクションで一世を風靡したC・キャロウェイ。彼が昔の仲間たちの足跡を訪ね歩くという構成でみせる音楽ドキュメンタリーで、当時の大変貴重なフィルムも紹介している。1920～30年代、ジャズのメッカとして注目されたハーレムでは、歴史に残る多くのアーチストが入れ替わり立ち替わり“コットン・クラブ”をはじめとする数々のスポットで、歌い演奏し踊っていた。L・アームストロング、D・エリントン、C・ベイシーの演奏場面が珍しい。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEBASTIANE", "セバスチャン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ギリシャ史劇『サン・セバスチャンの悲劇』をベースにしたD・ジャーマンの長編処女作。古代ローマを舞台に、前線に送られた兵士セバスチャンと司令官シベリウスの美しくも残酷なホモセクシャルの愛憎模様を描出。ダンスの演出を担当したL・ケンプ自身も出演し、冒頭で妖しげな踊りを披露している。", "1976-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIVE WIRE", "ライブ・ワイヤー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "胃酸に反応する液体爆弾で次々と要人を殺す、凶悪なテロ組織。捜査に乗り出したFBIのダニーは、事件の背後に武器輸出法案に反対する動きがあることを知る……。P・ブロスナンがタフガイぶりを披露するハード・アクション。SFXを駆使した人体爆破シーンは圧巻。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL SOGNO DELLA FARFALLA", "蝶の夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「肉体の悪魔」の鬼才、ベロッキオが描く、幻想的な一編。若い舞台俳優のマッシモは、14歳の時から古典劇やシェイクスピア劇のセリフ以外は使わなくなっていた。彼と心を通わしているのは、森に住む娘だけ。マッシモに才能を感じた演出家のジュリアーニは、詩人であるマッシモの母に、マッシモ自身をテーマにした脚本を依頼する。最初はためらった母は、マッシモの心を開かせられるかもと思い脚本を書き始める。同時に、マッシモの周囲の人たちは様々な方法で、彼の心を開かせようと努力を始めるが、彼が打ち解けるのは相変わらず森の娘だけだった。二人は、オートバイに乗り、娘の父のいるクレタ島に旅立つ。そして島に着いた時、大地震が起こる……。ビデオ発売時タイトルは「新 肉体の悪魔」。", "1994-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZOOKEN NEAR EILEEN", "アイリーンを探して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "愛する妻を交通事故で失った古本屋の主人フィリップ。彼の前に、死んだ妻と生き写しの謎の女アイリーンが現れ、こつ然と消え去った。彼女を追うフィリップの身に、危険な罠が……。ヒッチコックの影響が垣間みられるドイツ産のサスペンス。", "1986-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京は恋する", "東京は恋する", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "舟木一夫の歌謡メロドラマ。美大入学を夢みながら看板屋で働く青年・明夫は、街で見かけた少女に恋をするが、その娘・ミチコは、あろうことか高校時代の悪友でプロのバンドマンを目指す健次の恋人だった。彼女への気持ちを秘めつつ、明夫は誘われるままに健次のバンドで歌い始めるが……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT AND THE CITY", "ナイト・アンド・ザ・シティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "口先のうまさで世渡りする三文弁護士ハリーは、偶然出会ったボクシングの魅力に取り憑かれ、プロモーター業に乗り出す。ギャングに脅され、恋人も失いかけながら、ハリーは興業に全財産をつぎ込み、ロマンに賭ける……。良くも悪くもR・デ・ニーロの一人舞台となった、もの悲しくも滑稽なホロ苦い男のドラマ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAXi 4", "TAXi（4）", "", "", "http://image.eiga.k-img.com/images/movie/34143/315x210.jpg?2683", 91, "リュック・ベッソンが製作・脚本を手掛ける大ヒットカーアクションのシリーズ第4弾。ヨーロッパ全土から指名手配されている凶悪犯“ベルギーの怪物”を護送する任務に就いたエミリアン刑事。無事警察署に連行したのもつかの間、エミリアンのドジが原因で逃げられてしまう。エミリアンはスピード狂のタクシー運転手ダニエルの力を借りて、モナコへと逃亡した犯人を追うが……。監督は前作に続き「WASABI」のジェラール・クラブジック。", "2007-08-25", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLICE PYTHON.357", "真夜中の刑事 パイソン357", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "職務に忠実な中年の部長刑事は、ふとしたことから可愛らしいイタリア娘と知り合いになるが、彼女は上司の妾だった。ある晩、彼女は殺害され、出てくる証拠品はどれもが彼を犯人とするものばかりだった……。刑事の拳銃“パイソン357”を題名にした犯罪劇。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FINAL CUT", "ファイナルカット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "頻発する爆弾事件の対策のため、一人の男が処理班に加わった。彼の名はジョン・ピアース。かつては爆弾処理班で活躍していたが、ある事故をめぐるトラブルのために引退していたスペシャリストだ。しかし彼は、あまりの知識の豊富さのため、周囲から容疑者として疑われ始め……。卑劣な爆弾魔に立ち向かう男の活躍をスリリングに描いたアクション。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ROSE", "ローズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "ロックの歴史に残る偉大な女性シンガー、ジャニス・ジョプリンをモデルに、麻薬と過労でボロボロになりながらも、ステージではエネルギッシュに歌い叫び続けるロック・スター、ローズの激情の人生を描く。ローズに扮するミドラーが、見事な演技と歌いぶりを披露する。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NATA DI MARZO", "三月生まれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "イタリア・ミラノを舞台に、“３月生まれ”の女子学生フランチェスカのお天気屋ぶりをみずみずしく描いたロマンティック・コメディ。ミラノ大学に通う18歳のフランチェスカは、電車の中で知り合った建築技師サンドロと電撃結婚。ところが、新婚旅行から帰ってきた頃、早くも彼女の気まぐれな性格が現れ始め……。気まぐれなヒロインを演じるのは、「芽ばえ」「お嬢さん、お手やわらかに！」などの青春映画で当時、人気を呼んだJ・ササール。", "1958-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("我が人生最悪の時", "我が人生最悪の時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "横浜・黄金町にある映画館“横浜日劇”の２階に事務所を持つ、青年探偵・濱マイクの活躍を描いた、全３部作の第１作。マイクは、マージャン屋で知りあった台湾人、楊の兄貴を捜すことになる。だが、実は楊は台湾マフィアから、組織を裏切った兄を殺すために差し向けられた刺客だった……。林海象は、かつてプロデュースした、アジア諸国を舞台とするオムニバス・シリーズ“アジアン・ビート”の雰囲気と、往年の日活無国籍アクションの合体を試みている。全編モノクロの画面に、マイクのセンチメンタルなヒロイズムがうまく溶けあった。美術監修として名手・木村威夫が参加している。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　MONDE　SANS　SOLEIL", "太陽のとどかぬ世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "カンヌ国際映画祭で最高賞、アカデミー賞で最優秀長編ドキュメンタリー賞を獲得した監督長編デビュー作「沈黙の世界」より9年。満を持して発表した海洋冒険ドキュメンタリーの傑作。探検家で海洋学者でもあるクストー監督は、紅海に海底ステーションを建設し、人間が果たしてそこで生活できるのかという壮大なチャレンジを記録。深海の未知の世界をエスプリにあふれた映像で紡ぎ出す。", "1964-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ADOLESCENTE", "ジャンヌ・モローの思春期", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "舞台は第二次大戦が始まる直前のフランスの小さな村。思春期にさしかかった少女マリーのひと夏の多感なバカンス体験が、モロー監督自身の少女時代の記憶をまさぐるような細やかなタッチで綴られる。優しく少女を見守る老婆役シニョレの好演がひときわ光る。ベテラン女優J・モローの監督第２作で、ナレーションでも参加している。。", "1979-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHERE THE BULLETS FLY", "続・殺しのライセンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“殺しのライセンス”を持つ英国諜報部のキレ者、チャールズ・バインが大活躍するアクション・コメディの続編。軽くて丈夫な合金スプリウムの秘密を盗み出すため、敵方スパイのボス、エンジェルが動きだした。これを阻止せんと、英国諜報部はバインをエンジェルの元に送り込むが……。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Intermission in the Third Dimension", "休憩3D", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 8, "", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PEEPING TOM", "血を吸うカメラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "英国の鬼才監督M・パウエルの、実験精神あふれる異色サイコ・ホラー。死ぬ直前の恐怖に引きつった女の顔に魅せられた青年カメラマンは、カメラの三脚にナイフを忍ばせ、撮影しながら次々とモデルを刺殺、その瞬間をカメラに収める……。透明感あふれる映像で恐怖の美学を追求した秀作。", "1960-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA POIRSUITE IMPLACHABLE", "非情の標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "イタリアの石油王が殺され、ミロという男が逮捕される。ところが刑務所の副所長ピートの妻アンナが何者かに誘拐され、ピートの元にアンナとミロの交換を持ちかける脅迫電話が入った。やむなくピートはミロを脱獄させ、引き替え場所へと向かうのだが……。主人公とミロ、暗黒街の組織が繰り広げる攻防に、警察の腐敗というイタリア映画らしいモチーフが絡む犯罪アクション。", "1972-01-01", "4,2,7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猟人日記", "猟人日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "「にっぽん昆虫記」「月曜日のユカ」「赤い殺意」などと並ぶ日活エロス路線の力作。江戸川乱歩賞を獲得し、一躍、猟奇推理小説家として有名になったシャンソン歌手・戸川昌子の原作を浅野辰雄が脚色。しかも戸川昌子自ら主演するというセンセーショナルな話題をふりまいた。夜な夜なガールハントし、その猟の成果を“猟人日記”と題したノートに記していた男が巻き込まれる連続殺人事件。異常性生活のクールな描写、そして事件が解き明かされていくミステリーの面白さ。緻密なカメラワークとカット割りが、暗いエロティシズムと隙のないサスペンスを醸成していく。演出巧者・中平康の面目躍如。『流浪の民』が朗々と流れる薄暗い喫茶店や、猟色にとりつかれた男の過去の秘密の深層心理的フラッシュバックなど、宿命的ムードに貫かれた黒白撮影が見事だ。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あばれ街道", "あばれ街道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "もともと侠客佐野家一家の長男でありながら弟に跡目を譲って家出、役者としての修業の旅に出た三河団十郎。だが、彼の留守中に佐野家の縄張りを手に入れようとする御油の大五郎や鬼薊の権次らによって、弟・竹三郎や太夫元文蔵が殺される。故郷に帰ってきた団十郎だが……。市川右太衛門のお役者ヤクザもの。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Vie de château", "城の生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "　「地下鉄のザジ」等の脚本で名を馳せ、のちに「シラノ・ド・ベルジュラック」で成功をおさめたジャン＝ポール・ラプノーの監督作。長編デビュー作ながら、フランスの権威あるルイ・デリュック賞を受賞。ノルマンディ上陸作戦下、カトリーヌ・ドヌーブ演じる人妻を軸にドイツ兵の恋のさやあてが展開する。「シェルブールの雨傘」でスターの座を射止めたカトリーヌは、製作当時22歳だった。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("光と影（前・後編）", "光と影（前・後編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "松竹大船のシンボル的存在だった島津保次郎の東宝移籍第１作。防疫技師・弘吉とその許婚・佐保子、そして弘吉とふとしたことで親しくなった峰の三角関係を中心とした、ラブ・ストーリー。佐保子役は、当時から将来を嘱望されていた原節子。島津としては松竹時代ほどの才能が発揮出来なかった作品とされる。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Human Centipede II (Full Sequence)", "ムカデ人間2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "", "2012-12-31", "70,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARDWARE", "ハードウェア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "核戦争後の荒廃した北アメリカを舞台に、“ゾーン・トリッパー”と呼ばれる運命の戦士たちと、冷酷な殺人アンドロイド、マーク13の壮絶な戦いを描いた近未来SFホラー。「死霊のはらわた」「ターミネーター」「ブレードランナー」などの影響が顕著に表れている。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子寶夫婦", "子寶夫婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 59, "戦時中のスローガン“生めよ、殖やせよ”を皮肉った喜劇王・斎藤寅次郎の反国策喜劇映画。15人の子を持つ徳川夫婦の４女が同級生に“まるで犬みたい”と子だくさんの親を侮辱され、つい突き倒してしまう。その同級生が父の会社の社長の娘だったから、さあ大変……。ラストのご都合主義が残念。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE FEMME OU DEUX", "シガニー・ウィーバーの大発掘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "フランスの若き考古学者ジュリアンは、人類の祖先と目される200万年前の人骨を発見し、先端技術を駆使して体型復元する。その研究を援助しようとするマダム・ヘフナーを、彼は空港に出迎える。ところがそこからとんだ事件が始まって……。軽快なコメディ作品。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AKIKO あるダンサーの肖像", "AKIKO あるダンサーの肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "日本のモダン・ダンス界の第一人者、アキコ・カンダの“自分を撮ってほしい”という申し出によって実現したドキュメンタリー作品。彼女自身のナレーションで映画は始まり、厳しい稽古の風景から日常生活や家族のことなどが次々と語られていく。撮る者と撮られる者の幸福な出会いを感じさせ、すがすがしい後味を残す。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cybele ou les dimanches de ville d'Avray", "シベールの日曜日", "", "", "http://ecx.images-amazon.com/images/I/51mYH%2B5mRWL._SL210_.jpg", 116, "フランスの作家V・エシャスリオーの小説『ビル・ダブレの日曜日』を、ドカエのみずみずしいモノクロ撮影を得て映像化した、リリシズムあふれる名編。パイロットだったピエールは戦争経験の痛手で記憶喪失となり、小さな田舎町で看護婦をしている女性と暮らしていた。ピエールの毎日はあてもなく森をさまようことしかない。そんな時彼の前に現れる一人の少女シベール。父親に見捨てられたシベールとピエールは、日曜ごとに池の畔で無邪気な逢瀬を重ねる。しかし、ピエールを変質者扱いする大人たちが、二人を引き裂こうとして……。肉体を超えた恋の清らかさが忘れ難い一編。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A PLACE IN THE SUN", "陽のあたる場所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "貧しい家庭に育ち、出世を夢見る青年ジョージは、苦労の末に昇進と、前から惹かれていた金持ちの令嬢アンジェラとの結婚を約束される。ところが、仕事先で知り合った身よりのない娘アリスとも親しくなり、深い関係を持ってしまう。妊娠し、結婚をせまるアリスをうとましく思うジョージは、彼女を殺そうと計画、湖に連れ出すが……。スティーヴンス監督による、当時のベストセラー『アメリカの悲劇』の映画化。交通事故のあと、屈折した役の多かったクリフトを主役に、アメリカの夢の裏側を描き、数々の賞を受賞。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Raging Bull", "レイジング・ブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "悪に染まったサウス・ブロンクスのスラム街からはい上がり、不屈の闘魂で1949～51年までプロ・ボクシング世界ミドル級王座に君臨したジェイク・ラモッタ。その彼の栄光と破滅の人生を描いたボクシング映画の傑作。モノクロのファイト・シーンは圧巻。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hungry Wives", "ジョージ・A・ロメロ／悪魔の儀式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "　郊外に暮らす平凡な主婦ジョーンは仕事一辺倒の夫にもかまってもらえず、満たされない日々を送っていた。そんな彼女がやがて魔術を得るが……。現実と虚構が交錯するオカルト・ホラー。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("快感旅行", "快感旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "上野～金沢間の急行列車の車掌・坂本大作は35歳で未だ独身。列車ボーイの弟・健作には恋人がいるのに……。そんな時、上野の食堂のウェイトレス・光子に惚れられたが、趣味も性格も正反対の二人はすったもんだと相なる。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さよならニッポン！", "さよならニッポン！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "日本最南端の島・赤尾根古島は、台風の被害に遭いながらも政府の対応の悪さに憤りを感じて日本から独立を宣言。村長から大統領になった大瀬利は理想の国作りに向けて奔走するが、日本国政府はたかをくくって相手にしない。そんな中、独立を承認する国が増えていき北朝鮮が承認するとの噂も流れ、政府は島を包囲。窮地に立たされた大瀬利はアメリカに救いを求め世紀の大バクチを打つのだった……。日本から独立宣言をした島民の奮闘ぶりをコミカルに描く。緒形拳が「国会へ行こう！」に続き、ここでも政治改革に乗り出す村長を熱演している。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Napoleon Dynamite", "バス男", "", "", "http://ecx.images-amazon.com/images/I/51I0nR5lIUL._SL210_.jpg", 95, "", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鎧甲無敵瑪利亞／I LOVE MARIA", "ロボフォース 鉄甲無敵マリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ツイ・ハークが、日本のロボット・アニメにヒントを得て製作したSFアクション。凶悪な巨大ロボットが横行する近未来都市を舞台に、ロボットを操るギャングたちと人間が壮絶な闘いを繰り広げる。サリー・イップが改造ロボットに扮し大活躍。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千羽鶴", "千羽鶴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "人間の本能を赤裸々な描写で追求する増村保造が、日本的風土のなかで女性の美を描く川端文学を映画化。お茶の師匠と、その父親と関係のあった二人の女を中心に展開される色模様が描かれる。同じ新藤脚本にもかかわらず、吉村公三郎版とはまったく肌合いの異なったドライな仕上がりになっている。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キングコング対ゴジラ", "キングコング対ゴジラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "前作「ゴジラの逆襲」から７年を経て甦ったゴジラが、アメリカを代表するモンスター、キングコングと大格闘を展開する。北極の氷が溶け再び甦ったゴジラは、その帰巣本能から一路日本へ向かう。一方、南海ファロ島からTV局の手で眠らされ日本へ運ばれるキングコングは、近海で眠りから覚め日本へ上陸する。折から上陸していたゴジラは日光・中禅寺湖付近で衝突し……。東宝創立30周年記念映画として製作された、当時としては空前の超大作。日米を代表するモンスターであるゴジラとキングコングを対決させるというアイデアが大衆にアピールした。", "1962-08-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("精武門／Fists of Fury", "ドラゴン怒りの鉄拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1900年初頭の上海を舞台に、当時の日本帝国主義の圧政に対する中国人の反骨精神を、武術のドラマとして描いた作品。人気スター、B・リーの主演第２作。この地で一派をかまえる日本武術協会に、中国人青年チェンが雄々しく戦いを挑む姿が圧巻である。", "1972-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODY OF EVIDENCE", "BODY　ボディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "富豪の老人が若い愛人レベッカとの情事の最中に死亡、彼女に殺人の疑惑がかかる。彼女の弁護士についたフランクは事件に疑問を抱きながらも、次第にレベッカの魔性の虜に……。マレーネ・ディートリヒの名作「情婦」を意識したという、マドンナの過激なセックスシーンが話題の一編。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("岸和田少年愚連隊 血煙り純情篇", "岸和田少年愚連隊 血煙り純情篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "中場利一の自伝的小説の映画化第２作。前作のナインティナインに代わって、吉本興業の新星、千原兄弟が主演に当たっている。リイチとユウジは、高校を卒業してもケンカに明け暮れ、リイチの恋人リョーコは美容院で働き始める。ある日、元同級生の美女ナホミに愛を告白されたリイチは、成り行きで彼女と付き合い始める。ショックを受けたリョーコは長かった髪を切り落とし、ユウジもすっかり覇気のなくなったリイチに絶望してしまう……。ケンカのハードな描写の一方で、成長していく男女の青春ドラマがしんみりと展開。その中にもしっかり爆笑ネタを盛り込んだ、見どころ満載の佳作だ。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sin City", "シン・シティ", "", "", "http://image.eiga.k-img.com/images/movie/1173/315x210.jpg?3025", 124, "悪徳の街シン・シティを舞台に、誘拐犯から救った少女の身を案じる老警官や、一夜を共にした娼婦の仇討ちを決意する犯罪者など宿命に踊らされる男女たちを描くハードボイルド群像劇。「スパイ・キッズ」シリーズや「レジェンド・オブ・メキシコ／デスペラード」のロバート・ロドリゲス監督が、連作コミック「シン・シティ」を原作者フランク・ミラーを共同監督に招き、俳優以外はすべてCG映像というユニークな手法で映画化。", "2005-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワイルド7", "ワイルド7", "", "", "http://image.eiga.k-img.com/images/movie/56073/315x210.jpg?3878", 109, "1969～79年に「週刊少年キング」に連載された望月三起也の人気漫画を、「海猿」シリーズの羽住英一郎監督が実写映画化。暗い過去を背負った犯罪者から選ばれた7人の警察官（ワイルド7）が白バイに乗り、超法規的な存在として悪人を裁いていく姿を描くアクション。瑛太をはじめ、本作が映画初出演となる人気アイドルグループ「関ジャニ∞」の丸山隆平、椎名桔平、阿部力らがワイルド7に扮する。共演に深田恭子、中井貴一ら。", "2011-12-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les herbes folles", "風にそよぐ草", "", "", "http://image.eiga.k-img.com/images/movie/56173/315x210.jpg?4372", 104, "「アメリカの伯父さん」「恋するシャンソン」などで知られるフランスの巨匠アラン・レネが描く、初老の男女の運命的な恋の物語。歯科医のマルグリットはある日、引ったくりにあいバッグを持ち去られてしまう。駐車場の片隅に捨てられたバッグを拾った初老の紳士ジョルジュは、その中にあったマルグリットの小型飛行機操縦免許の写真を見てなにかを感じる。そうして知り合った2人はすれ違いを繰り返しながらも恋に落ちていくが、その関係は周囲を巻き込んで思わぬ方向へ転がり始める。主演は近年のレネ作品の常連、アンドレ・デュソリエとサビーヌ・アゼマ。", "2011-12-17", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ENGLISH PATIENT", "イングリッシュ・ペイシェント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 162, "第69回アカデミー賞で作品賞をはじめ９部門を制覇したロマンス大作。英国のブッカー賞を受賞したM・オンダーチェの原作『イギリス人の患者』をもとに、運命にほんろうされる男女の激しい恋の行方を描く。第二次大戦終戦間近の1944年。負傷したハンガリーの冒険家アルマシーが、カナダ人看護婦ハナの下に運ばれてくる。戦争によって愛する人を失い絶望していたハナの献身的な看護を受けるアルマシー。消えゆく意識の中で彼の頭によみがえったのは、最愛の人キャサリンと出会ったサハラ砂漠でのことだった……。1938年、アルマシーが地図を作るために訪れたアフリカの砂漠で出会った、英国人のキャサリン。互いに惹かれ合う二人だが、彼女には英国情報部員の夫がいた。許されぬ愛に苦悩しながらも密会を重ねる彼らに、やがて悲劇が襲いかかる。回想形式で綴られる物語は、主人公の愛の日々と看護婦ハナの再起のドラマとを巧みに絡ませて展開。官能的な美しさを湛える、壮大な砂漠の映像が印象的だ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADY WINDERMERE’S FAN", "ウィンダミア夫人の扇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "巨匠E・ルビッチのサイレント期の代表作。ロンドンの社交界に、スキャンダルで一時身を隠していたアーリーヌ夫人が戻ってきた。ちょうどその頃、社交界の華ウィンダミア夫人の誕生パーティーが開かれようとしていた。しかしアーリーヌ夫人のおかげでせっかくのパーティーもとんだ騒動に発展する……。", "1925-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITIZEN KANE", "市民ケーン", "", "", "http://ecx.images-amazon.com/images/I/51fkB%2BJ-MxL._SL210_.jpg", 119, "鬼才ウェルズ監督が、25歳の若さで発表した処女作で、絶大な権力者の秘めたる人生を斬新な映画手法で洞察した傑作。“ローズバット（ばらのつぼみ）”という謎の言葉を残して、新聞王ケーンが死んだ。ニュース映画の記者たちはこの言葉がケーンの波乱の人生を解明する鍵になると考え、取材を開始する。後見人、昔の親友、後妻などケーンの人生と交錯した人々の話から、次第にその一生が浮き彫りにされていく。それは、富と権力を欲しいままにしながらも、孤独で空虚な新聞王の姿であった……。この作品は、当時の新聞界の大立者ランドルフ・ハーストをモデルにしていたため、公開当時、物議を醸した。しかしケーンの人生をパズル状に配列した脚本の巧みさ、それを鮮明に画面に映し出す演出力、パン・フォーカスによる的確な撮影など、完成度の高さは比類ない。1987年ハリウッド百年祭記念の世界映画ベスト・テンの第一位にも選ばれた映画史上の金字塔。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の足あと", "女の足あと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "友だちの浜田の名前を偽った順介によって犯された娘・のぶは、浜田という男を探す。やっと見つけた浜田は一面識もない男だった……。渋谷実が乾いたタッチで描いたラブ・ロマンス。人間心理の複雑な葛藤を、のぶを演じた有馬稲子の名演を通して見せた意欲作。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JE´SUS DE MONTRE´AL", "モントリオールのジーザス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "モントリオールの丘の上で、ジーザス劇を演じる５人の若者たちの青春を心優しく描いた作品。斬新なアイデアで上演したジーザスの芝居を観た司祭は、脚本の変更を要求。しかし青年は要求を無視して、芝居を続行するが……。", "1989-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blood ブラッド", "Blood ブラッド", "", "", "http://image.eiga.k-img.com/images/movie/54483/315x210.jpg?2532", 85, "お蔵入り寸前の猟奇殺人事件を追う刑事・星野は、殺害されたメイドの雇い主・美夜子ロジュンベルクの古びた豪邸を訪ね、美夜子から犯人は黒沼右京という男だと告げられる。星野は右京のアジトに忍び込むが、そこで目にしたのは命乞いをする少女の首に食らいつき、血をむさぼる右京の姿だった……。妖艶なバンパイアを杉本彩が熱演。", "2009-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フライ,ダディ,フライ", "フライ,ダディ,フライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "「GO」の原作者として知られる金城一紀の同名小説を映画化した青春ドラマ。金城自身が脚本を手掛け、軟弱な父親に甘んじていた中年男とどこか人生を達観した高校生の交流、そして両者の成長が描かれる。V6の岡田准一が主人公のスンシンを熱演している。ボクサーの男に娘を暴行された鈴木。復讐を誓う彼は偶然出会った腕っ節の強い高校生のスンシンに指導を受け、格闘の特訓を積む。", "2005-07-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逆襲獄門砦", "逆襲獄門砦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "幕府崩壊前後の動乱期を背景に、雪国・江州の一天領地を支配する代官の悪政に対して農民が立ち上がる。『ウィリアム・テル』が原案になっており、片岡千恵蔵が実子・植木基晴とともに出演し厭世的な猟師を熱演。内田吐夢監督、片岡千恵蔵主演による時代劇の意欲作。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("村の写真集", "村の写真集", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "消えゆく村の美しさを残そうと写真を撮る頑固な男と息子の絆の修復のドラマを軸に、家族の温かさを描く感動作。第８回上海国際映画祭で最優秀作品賞を受賞、主演の藤竜也が最優秀男優賞に輝いた。舞台となる徳島県山間部の村の、自然に囲まれたのどかで情緒あふれる風景が、温もりあふれる物語を豊かに彩る。劇中に使われる写真家、立木義浩による作品の数々も必見だ。ダムの建設によってやがて消えることになった徳島県の小さな村。そこで暮らす写真屋の店主が、村のすべての家族写真を撮り始める。", "2005-04-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トランスミッション", "トランスミッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "デジタル映像とテクノ・ミュージックが融合した近未来ドラマ。サラリーマンの松戸は上司に昇進をほのめかされた直後に突然、昏倒。気づけば治療室に監禁され、脳にメスを入れられていた。夢なのか現実なのか、疑念の晴れない松戸を、さらなる人体実験が待ち受けていた……。６分に1000カットを詰めこんだ、クライマックスの目まぐるしい映像の切り換えはトリップ感覚満点。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朗かに歩め", "朗かに歩め", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "“ナイフの謙”と呼ばれるヤクザな若者は、ある日タイピストのやす江に一目惚れしてしまう。ふとしたことからやす江の妹、みつ子と知り合った謙は、３人でピクニックに出かける。ヤクザとは知らず謙に心惹かれていくやす江だが……。アメリカ映画に心酔していた小津安二郎が、バタ臭い設定で描いた、サイレンとの人情喜劇。", "1930-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("郷愁「幻なりき」より", "郷愁「幻なりき」より", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "大正から昭和にかけて活躍した人気女流作家、吉屋信子が1951年に発表した小説を、若かりし橋田寿賀子脚本で映画化。華僑の家に嫁いだ一人の女性が日本に帰郷した。日本国籍を捨てた彼女の姿を通して、母性愛とは何かを問う国際色豊かなメロドラマ。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血文字屋敷", "血文字屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "大友柳太朗が一人二役で復讐の剣をふるう時代劇。江戸小町と名高い園絵と祝言をあげた神尾喬之助は、恋仇でもあった番士組頭ら17人にことごとくいたぶられ、ついに怒りが爆発。喬之助と瓜二つの浪人・茨左近の力を借りて、一人また一人と番士たちの命を江戸の闇夜の中、奪っていくのであった。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GORGO", "怪獣ゴルゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "アイルランド沖で発見された怪獣は“ゴルゴ”と名付けらて、サーカスの見世物としてロンドンに運ばれる。しかし、“ゴルゴ”を救うため、その親で60メートルもの巨大怪獣がロンドンに現れた。日本の「ゴジラ」の影響を受けて作られた怪獣映画。", "1959-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長意外伝 灰神楽の三太郎", "次郎長意外伝 灰神楽の三太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "次郎長の子分にもまぬけな奴がいたという浪花節を映画にした、喜劇仕立ての時代劇。亡き森の石松を慕い、次郎長一家に入った灰神楽の三太郎は、ある日親分の使いで伊豆下田の銀兵衛親分のもとに行くことになるが、珍事件の連続。そんな彼の前に石松の形見のキセルから石松の亡霊が現れる。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEVEN WOMEN", "荒野の女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1935年の中国とモンゴルの国境地帯を舞台に、無法者の馬賊たちを相手に伝道と救済に尽くした７人の女たちを描いたジョン・フォードとしては珍しいタイプのドラマ。アメリカ隣保館周辺の治安維持を担当していた中国部隊がひきあげ、同館はカンたちの率いる馬賊に蹂躙される。女医カートライトは必死の覚悟でカンに立ち向かっていく……。N・ロフツの『中国風の終幕』を映画化したもので、J・フォード監督の遺作でもある。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Slagskampen", "謀殺の標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "物理学者ポール・マンデル博士が開発した原子力潜水艦レーザー探知器が、何者かに盗まれてしまう。捜査にあたったスティグはポール博士に疑問を持ち、監視役にトーマスをつける。巨大な陰謀、頭脳プレイとアクションを見せてくれるスパイもの。", "1984-01-01", "14,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Follow My Voice: With the Music of Hedwig", "ヴォイス・オブ・ヘドウィグ", "", "", "http://image.eiga.k-img.com/images/movie/34203/315x210.jpg?1561", 101, "ゲイやレズビアンといったセクシャル・マイノリティの若者たちのため、ニューヨークに設立されたハーベイ・ミルク・ハイスクール。その基本理念に賛同したジョン・キャメロン・ミッチェル監督やミュージシャンたちによるチャリティ・アルバムの制作過程を追うドキュメンタリー。オノ・ヨーコら個性派アーティストが「ヘドウィグ・アンド・アングリーインチ」の曲をカバーするほか、4人の生徒にスポットを当て、彼らの葛藤や思いを映し出していく。", "2007-09-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらば夏の光", "さらば夏の光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "日本のヌーヴェル・ヴァーグを牽引していた吉田喜重監督が、欧州7ヵ国ロケを敢行して撮り上げたロード・ムービー仕立ての異色ラブ・ロマンス。ヨーロッパ各地の美しい風景と美旋律のサウンドトラックに包まれて大人の恋が紡ぎ出される。リスボンで日本人の女と巡り会った主人公の男。彼女は人妻で、夫と共にパリで暮らしているという。ある日、彼女がノルマンディーへの二人旅に誘ってきた……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武器なき斗い", "武器なき斗い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "貧乏人の子だくさんで苦しむ労働者に、性教育、産児制限法を講じ、やがて大学教授から衆議院議員に当選した山本宣治の半生を描いた社会派ドラマ。1960年、安保で揺れる日本で革命志向の映画としてもてはやされた。右翼の凶刃に倒れた主人公の凄絶な半生が見もの。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("熱帯楽園倶楽部　THE TROPICAL PEOPLE", "熱帯楽園倶楽部　THE TROPICAL PEOPLE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「僕らはみんな生きている」の監督・脚本・撮影のトリオが再び結集、すべてタイ・ロケで行われた詐欺師コメディ。バンコクで添乗員をしている紺野みすずは、日本人相手のセコい詐欺師コンビ、飛田林＆ジョイと知り合う。ところが他人のミスで会社から大目玉を食らった彼女が二人に急接近、奇妙な三角関係になって……。ロケ効果を活かしたエキゾチックなタイの雰囲気と、“肩の力を抜いた演技”が楽しい。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高校さすらい派", "高校さすらい派", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "滝沢解＝芳谷圭児の同名劇画を、森崎東が自分の青春をダブらせて改作した青春映画。森田健作演じる少年院あがりの高校生・荒木勉が、学校側の理不尽なやり方に反発して講堂に篭城。仲間の病死などで挫折を経て旅に出る様を、ノスタルジックに描く。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAR　WENDY", "DEAR WENDY〈ディア・ウェンディ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "炭鉱町の少年少女が銃を手にしたことによって皮肉な運命をたどっていく様を描いた、西部劇スタイルの異色青春映画。脚本を担当したラース・フォン・トリアーが、「ドッグヴィル」に続いて“アメリカ”を痛烈に風刺。全編に流れるゾンビーズのナンバーが切ない悲劇をポップに彩っている。炭鉱町のひ弱な少年ディックと仲間が平和主義の秘密結社を結成。拳銃を手にして生まれ変わった彼らだが、思わぬ事件に巻き込まれる。", "2005-12-10", "18,7,2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ghost Writer", "ゴーストライター", "", "", "http://image.eiga.k-img.com/images/movie/56193/315x210.jpg?3013", 128, "元英国首相アダム・ラングの自伝執筆を依頼されたゴーストライターが、ラングの滞在する孤島を訪問。取材をしながら原稿を書き進めていくが、次第にラングの過去に違和感を抱き始める。さらには前任者の不可解な死のナゾに行き当たり、独自に調査を進めていくが、やがて国家を揺るがす恐ろしい秘密に触れてしまう。「チャイナタウン」「戦場のピアニスト」のロマン・ポランスキー監督が描く本格サスペンスで、第60回ベルリン国際映画祭銀熊賞（最優秀監督賞）を受賞。ユアン・マクレガー、ピアース・ブロスナンらが共演。", "2011-08-27", "2,7,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雷雨／THUNDER STORM", "雷雨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「未完の対局」への出演で日本でもよく知られるベテラン男優、スン・タオリンが自ら監督・脚本・主演の３役を務めた作品。1910年代の天津祖界。炭坑王として名をなした大周朴園の一族は、華麗な生活の裏で近親相姦に走り、崩壊への道を歩み始める。中国演劇界の第一人者、ツァオ・ユイの名作を映画化したもの。", "1984-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大坂城物語", "大坂城物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "関ケ原の戦いで一家を失った無法者・鬼の茂兵衛が、大阪冬の陣で豊臣家のためにひと働きしたあと、田舎へ帰っていく……。村上元三の原作を稲垣浩監督と三船敏郎のコンビで映画化したアクション時代劇。三船敏郎が鬼の茂兵衛を演じるが、ワンパターンの感は否めない。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXPECT NO MERCY", "サイバー・ウォーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "FBI捜査官のジャスティンは、最新のハイテク設備で注目を集めるマーシャル・アーツの養成機関“バーチャル・アーツ・アカデミー”への潜入を命じられる。先に潜入していたエリックとともに機関の裏を探るジャスティン。やがて彼らは、アカデミーがバーチャル・マシンによって大量の殺人戦士を養成し、巨額の利益を得ている事実をつかむ。そしてジャスティンは、機関の野望をつぶすためバーチャル・リアリティーの世界へと飛び込んだ。「ザ・フライ」「ハイランダー３」のフランソワ・オウブリーがSFXを担当。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少女たちの羅針盤", "少女たちの羅針盤", "", "", "http://image.eiga.k-img.com/images/movie/56103/315x210.jpg?3359", 113, "新進女優の舞利亜は、映画の撮影ため廃墟（はいきょ）となったホテルに赴くが、撮影開始直前になっても脚本が届かず、ホテルの壁に不気味な落書きをされる。誰かが自分を陥れようとしていると感じた舞利亜は、4年前の高校時代、友人たちと結成した女子高生劇団「羅針盤」で起きた殺人事件を回想する。現在と過去が交錯しながら事件の真犯人が暴かれていく青春ミステリー。成海璃子、忽那汐里ら人気若手女優が共演。監督は「西の森の魔女が死んだ」の長崎俊一。", "2011-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shattered Glass", "ニュースの天才", "", "", "http://image.eiga.k-img.com/images/movie/1643/315x210.jpg?1996", 94, "98年、政治雑誌「The New Republic」誌の最年少記者スティーブン・グラスはスクープを続々執筆して注目を浴びるが、彼の記事がすべて捏造だったことが判明する。この実話についてのピューリッツアー賞作家バズ・ビッシンジャーの記事を原案に、「ボルケーノ」「ジャスティス」などの脚本家ビリー・レイが脚本を執筆して監督デビュー。主演は「スター・ウォーズ　エピソード3」のダースベイダー役ヘイデン・クリステンセン。", "2004-11-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEON CITY", "バトルトラック2053", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "放射能が次々と人間をミュータント化させ、人間が住むことなど到底不可能になってしまった西暦2053年の地球。この汚染された地球を舞台に、同じ車に乗り合わせた８人の旅行者が、アメリカ西部の危険地帯を通って、安住の地ネオン・シティに到着するまでを描いた近未来SFアクション。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUERTE DE UN CICLISTA", "恐怖の逢びき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "陰影に富んだ画面構成で男女の偽善と恐怖を追及するサスペンス映画。逢びきの帰り、車で人をひき逃げしてしまった男女。事件の目撃者こそいなかったものの、罪の意識から男は自首しようと言い出す。だが、安定した結婚生活を守るため、女は男を謀殺しようとする。", "1955-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NIGHTCOMERS", "妖精たちの森", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "H・ジェームズの小説『ねじの回転』の二度目の映画化（前作は1961年ジャック・クレイトン監督)。イギリスの田舎、大富豪の邸宅で働く野卑で無学な男は、富豪夫妻の事故死によってみなし子になった子供たちを手なずけるのだが……。恐ろしい結末が余韻を残す。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホテル・ハイビスカス", "ホテル・ハイビスカス", "", "", "http://image.eiga.k-img.com/images/movie/1433/315x210.jpg?1748", 92, "「ナビィの恋」の中江裕司監督の4年振りの新作。仲宗根みいこの同名コミックを原作に、沖縄でホテルを経営す3世代同居家族を、小学3年生美恵子の目から描く。現地オーディションで3000人の中から選ばれた主人公役、蔵下穂波に加え、沖縄民謡界を代表する登川誠仁や照屋政雄、TV「ちゅらさん」の“沖縄のおばあ”役、平良とみらが出演。音楽は「ナビィの恋」の磯田健一郎が担当、「森の熊さん」などの童謡を沖縄流にアレンジ。", "2003-06-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新選組", "新選組", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "PCLと前進座が提携した第３回作品で、他の新選組ものとは違い、その非業な末路に焦点を当てている。鳥羽伏見の戦いで惨敗を喫し、江戸へ敗走する新選組一行。病める沖田総司、苦悩する近藤勇。それぞれの思いを込め、新選組最後の決戦場、甲州へと彼らは向かうが……。河原崎長十郎の重厚な演技が光る。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PSYCHO BEACH PARTY", "サイコ・ビーチ・パーティー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "16歳のフローレンス・フォレストと友達のバーディーンがB級映画を楽しんでいたドライブイン・シアターで突如、女性が喉をかき切られて殺害された。その後、殺人事件を忘れようと出かけたマリブ・ビーチでまたしても無残な死体が発見される。オフ・ブロードウェイのヒット・ステージをアレンジし、計算されたくだらなさを前面に押し出して映像化した知的なユーモア劇。『ビバリーヒルズ青春白書』のK・ロバートソン、「ダーマ＆グレッグ」のT・ギブソン、「サブリナ」のベス・ブロデリック、「バフィー／恋する十字架」のニコラス・ブレンダンなどなど、アメリカドラマ・ファンにうれしいキャスティングも魅力。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Curse of the Jade Scorpion", "スコルピオンの恋まじない", "", "", "http://image.eiga.k-img.com/images/movie/1633/315x210.jpg?1834", 101, "40年代のニューヨーク。昔かたぎの保険調査員ブリッグスと、徹底的な合理主義者の女性重役フィッツジェラルドは犬猿の仲。ところが、ある夜、インチキ魔術師のマジックショーで催眠術をかけられ、ある単語を聞くと、お互いに恋に落ちてしまうようになる。女性重役には「恋愛小説家」のオスカー女優ヘレン・ハント。ハワード・ホークスやビンセント・ミネリらの、ハリウッド黄金期の作品へのオマージュも多数散りばめられている。", "2002-12-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・網走番外地", "続・網走番外地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "第１作の予想外のヒットにより２週間たらずで撮影された。網走刑務所を出所した橘真一は、途中で銀行強盗事件に巻き込まれ犯人にされてしまい無実を証明すべく真犯人を追う。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネ響「マエストロ6」　サイモン・ラトル　ベルリン・フィルハーモニー管弦楽団", "シネ響「マエストロ6」　サイモン・ラトル　ベルリン・フィルハーモニー管弦楽団", "", "", "http://image.eiga.k-img.com/images/movie/55573/315x210.jpg?3536", 100, "映画館でクラシック音楽のコンサートを上映する「シネ響」の1作目。世界最高峰のオーケストラ「ベルリン・フィルハーモニー管弦楽団」の首席指揮者兼芸術監督を務める英国出身の指揮者サイモン・ラトルによる、大晦日公演「ジルベスター・コンサート」を収録。チャイコフスキーのバレエ音楽や、ラン・ランによるコンチェルトなどが見どころ。", "2010-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北国の旅情", "北国の旅情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "北国の駅に降りたった上村は、そのまま大学のクラスメイトの由子の実家を訪れた。由子は冬休みで帰省中に婚約したばかりであり、周囲の者はせっかくの良縁が駄目になるのではないかと心配する。男と女の友情と愛をさわやかに描く、西河克己監督お得意の青春映画。原作は石坂洋次郎。倉本聰が脚色に参加している。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リメンバー・ミー", "リメンバー・ミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ハム無線機を通じ、21年間の時を超えて交信する男女の恋の行方を描くラブ・ストーリー。女子大生のソウンは、兵役から戻った先輩、トンヒに思いを寄せていた。ある時、ひょんなことから無線機を手に入れたソウンは、電波を受信する。相手は、同じ大学の学生だというインだった。恋の悩みや世間話をするうちに、二人は時空を超えて交信していることが分かる……。決して実らない恋を知ったヒロインと、遠い過去にいる相手に淡い恋心を抱く青年のせつない物語が展開。丁寧なカメラワークと穏やかな音楽が作品を盛り上げる。また、本作が公開された2001年に日本でリメイク版が作られ、ヒロイン役のキム・ハヌルはそちらにもカメオ出演をしている。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘密戦隊ゴレンジャー　真赤な猛進撃！", "秘密戦隊ゴレンジャー　真赤な猛進撃！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "子どもたちから絶大な人気を集めた特撮テレビドラマ「秘密戦隊ゴレンジャー」の劇場版第3作。イーグルロケット研究所が軍艦仮面によって襲撃され、ロケットエンジンの設計図が奪われた。軍艦仮面はその設計図をもとに空飛ぶ戦艦を開発し、イーグル基地を攻撃。一方、キレンジャーの前に謎の美女・アヤが現われ……。テレビ版36話「真赤な猛進撃！動く要塞無敵艦隊」を再編集したもので、「東映まんがまつり」の1本として公開された。", "1976-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARCTIC HEAT", "レッド・プリズン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "トナカイを狩りにラップランドへ出かけた３人の米国青年は、誤ってソ連との国境を越えてしまう。兵隊に目撃された彼らはスパイ容疑で捕らえられ、連日激しい拷問にあう。そんなある日、青年の一人、サボイに謎の囚人から脱獄計画の話が……。ビデオ発売時タイトルは「死線からの脱出」。", "1985-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE BAL", "ル・バル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "パリの下町のダンス・ホール（ル・バル）に、盛装した男女が次々に集まってくる。互いに相手の気を引こうと必死だが、老いの兆しは隠せない。やがて音楽が始まり、踊るにつれて時代は過去へ。若返った男女の陽気なダンス。しかしホールにもファシズムの影が忍び寄り、時代は戦争へ。つらい体験を経た戦後、今度はアメリカ文化の波がホールに押し寄せる……。舞台はこのダンス・ホールだけ、セリフもいっさいなく、音楽と踊りだけで戦前から現代までの時代の流れと、そこに生きる人々の喜怒哀楽を表現した異色作。イタリア風に誇張された登場人物の性格描写、変色した記念写真を使ったカットつなぎなど、監督の巧みな演出とセンスの良さが光る。", "1983-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マンホールチルドレン", "マンホールチルドレン", "", "", "http://image.eiga.k-img.com/images/movie/55223/315x210.jpg?1357", 117, "1990年代、モンゴルの首都ウランバートルには、社会から見捨てられマンホールで暮らす子どもたちが大勢いた。冬には氷点下40度にもなる極寒の都市で、彼らは飢えと寒さと戦いながら必死に生き延びようとしていた。NHKで放送されたドキュメンタリー番組を劇場で公開するプロジェクト「This is REAL／NHK-BSドキュメンタリー・コレクション」にて上映。", "2010-02-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　アンパンマンとたのしい仲間たち", "それいけ！アンパンマン　アンパンマンとたのしい仲間たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "やなせたかし原作の人気アニメーション劇場版第11作「それいけ！アンパンマン　勇気の花がひらくとき」と同時上映の短編。", "1999-07-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明日やること　ゴミだし　愛想笑い　恋愛。", "明日やること　ゴミだし　愛想笑い　恋愛。", "", "", "http://image.eiga.k-img.com/images/movie/55583/315x210.jpg?2861", 95, "テレビ局ディレクターを目指す新人AD（アシスタントディレクター）の桜美散が、業界のくせものたちを相手に悪戦苦闘するコメディ。「おにいちゃんのハナビ」「神様のパズル」の谷村美月が冴えないADに扮する。共演に西田尚美、六角精児ら。人気バラエティ番組を多数手がける演出家・上利竜太が監督を務め、第2回沖縄国際映画祭の長編プログラム“Laugh部門”に出品。", "2010-08-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月下の若武者", "月下の若武者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 59, "織田信長の全盛時。濃尾の山深くに、各務十郎、由良三郎の二人が治める村があった。十郎の留守中、三郎は真刈の秀熊一家と争いごとを起こす。秀熊を逃したものの秀熊に無理矢理結婚されられそうな桔梗を助けた三郎は、彼女と恋に落ち、二人で村から逃げ出した。その後、村は復讐に燃える秀熊に焼かれてしまい……。中川信夫による、絢爛たる戦国絵巻。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雀鬼くずれ", "雀鬼くずれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "TVシリーズ『仮面ライダーアギト』で注目された要潤が主演の、麻雀にかける男たちの熱きドラマ。原作者は“雀聖”と呼ばれた男、阿佐田哲也。進路も定まらず麻雀に明け暮れる青年、折原は、雀荘で知り合った男キャブと組んでイカサマ麻雀を始める。最初は仲間のヒメの知り合いから順調に金を巻き上げる二人だったが、次第に行き詰まり仲間割れしてしまう。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざと抗争・実録安藤組", "やくざと抗争・実録安藤組", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "安藤昇の小説『極道一代・やくざと抗争』の映画化で、安藤昇自身が主演した。昭和初年頃、新宿の盛り場を舞台に、雑草のように生きていた通称“爆弾マッチ”と呼ばれた実在のヤクザを主人公に、彼と義兄弟の契りをかわした代貸・梅津との男の友情を描く。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOADED WEAPON １", "ローデッド・ウェポン １", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「リーサル・ウェポン」シリーズをベースに映画のパロディーを満載した悪ノリコメディ。命知らずのジャックと定年退職目前のベテラン、ルガーの二人の刑事はコンビを組んで麻薬事件の捜査を始める。しかし、彼らは行く先々でドジと混乱の山を築く……。ブルース・ウィリスをはじめゲストスターの出演も楽しみ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COURTESANS OF BOMBAY", "ボンベイの踊り子たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "歌と踊りだけで生計を立てている、ボンベイの女性たちとその家族が住む地区にカメラを据え、踊り子たちの生活をドキュメント・タッチで切り取った作品。J・アイヴォリー（本作では脚本を担当）とのコンビで知られるI・マーチャントの、数少ない監督作でもある。全編に流れるインド音楽と、彼女たちのダンス・シーンが圧巻。", "1982-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MILOU EN MAI", "五月のミル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "五月革命に揺れる1968年のフランスの田舎町に母の葬儀のために集まったブルジョワ家族。長男のミルは兄弟や親戚との再会を喜ぶが、遺産相続をきっかけに互いのエゴがぶつかり合いはじめる……。田舎ののびやかな自然と陽光を背景に、まるでルノワールの映画のように楽天的な物語を綴っていく映画叙情詩。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GENERAL", "キートン将軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "機関車ジェネラル号を恋人と同じくらい愛している機関士ジョニー・グレイ。南北戦争勃発後、北軍のスパイに恋人が乗っているジェネラル号を奪われ、彼は愛する者を取り戻そうとすさまじい奮闘を繰り広げる……。奔放なアイデアを満載した無声コメディ映画。「キートンの大列車追跡」、「キートンの大列車強盗」というタイトルで上映されたこともある。", "1926-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗黒街の顔役 十一人のギャング", "暗黒街の顔役 十一人のギャング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「ギャング対ギャング」で好評を博した石井輝男が、引き続いて発表したお得意のギャング・アクションもの。浜松市のある鋼管会社が従業員の給料として用意した現金を狙う権藤、別当たちの姿を描く。現金強奪作戦は万事うまくいったかに見えたのだか……。ギャングの権藤役は鶴田浩二。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏休みのような1ヵ月", "夏休みのような1ヵ月", "", "", "http://image.eiga.k-img.com/images/movie/54093/315x210.jpg?1350", 0, "「大奥　浮世悲恋」「アシンメトリー」のD-BOYS荒木宏文と、ミュージカル界で活躍する山崎育三郎が主演する青春映画。監督は「花ゲリラ」の川野浩司、脚本は「トワイライトシンドローム／デッドゴーランド」では監督を務めた安里麻里。サイクル便のメッセンジャーとして働く純也は、飛び降り自殺を考えていた真太郎に出会い、その後偶然に真太郎が原因で事故にあってしまう。事故がきっかけで2人は心を通わすようになるのだが……。", "2008-11-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スリー☆ポイント", "スリー☆ポイント", "", "", "http://image.eiga.k-img.com/images/movie/56153/315x210.jpg?3400", 117, "「闇のカーニバル」「聴かれた女」の山本政志監督が、京都、沖縄、東京を舞台にそれぞれ異なる手法で製作した中短編集。京都では内容を決めずに出演者のオーディションからリハーサル、撮影までを即興スタイルで行う。沖縄では山本監督が1人でカメラを回し、出会った人々や場所を記録。東京では村上淳、蒼井そら、渡辺大知の出演で、妻子を失った男と、男を慰めるために死んだ妻を演じる女のラブストーリーを描く。", "2011-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Night Falls on Manhattan", "NY検事局", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "　若き検事補ショーンは刑事である父リアムに重症を負わせた麻薬王ジョーダンの裁判に臨む。しかしジョーダンは法廷で警察の汚職を暴露。調査の結果、ショーンは父リアムの相棒が汚職に関与していたことを掴む。父への疑いが強くなり、苦悩するショーン。正義を貫こうとする彼はやがて窮地に……。理想に燃える検事補が厳しい現実に直面しながらも、正義のために戦う姿を描いた社会派サスペンス。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEEL", "超高層プロフェッショナル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ビル建築という危険度の高い仕事に挑む男たちの姿を描いたアクション・ドラマ。ハイエス・プラザ・ビルの組み立てをめぐってキャス建設と、社長の弟の会社が対立。３週間で９階を組み立てなければ、仕事はライバルの手に渡るため、キャス建築会社はプロ中のプロ、キャットン一家に建設を依頼するが……。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISS　CONGENIALITY２：ARMED　AND　FABULOUS", "デンジャラス・ビューティー２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "サンドラ・ブロックが製作・主演を兼任した人気コメディの続編。彼女扮する男っぽい性格の女性FBI捜査官が、今回はFBIの広報活動に駆り出され、セレブとして振る舞うことになる。鼻を鳴らして笑うなどの女らしくない振る舞いのギャグ化はもちろん、痛快なアクションも見逃せない。ミス・アメリカが誘拐された。FBIの広告塔になった捜査官グレイシーは、広報活動もソコソコに旧友の彼女を救うために奔走していく。", "2005-05-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("House of Wax", "蝋人形の館", "", "", "http://image.eiga.k-img.com/images/movie/1143/315x210.jpg?4087", 113, "ロバート・ゼメキスとジョエル・シルバーのホラー映画専門プロダクション、ダークキャッスルが53年の「肉の蝋人形」をリメイク。大学生のカーリーら6人の若者は、ドライブの途中で立ち寄った町の蝋人形館に足を踏み入れるが……。主演は人気ドラマ「24　TWENTY FOUR」でブレイクしたエリシャ・カスバート、監督のジャウム・コレット＝セラはミュージッククリップやCMのディレクターで、本作が劇場映画初監督。", "2005-10-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮本武蔵", "宮本武蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "戦前から何度も映画化された吉川英治の剣豪小説を稲垣浩監督と三船敏郎の名コンビで映画化した大作。関ヶ原の戦いに参加して敗れた武蔵が、沢庵和尚の教えで修行の旅に出るまでが描かれる。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("待ちぼうけの女", "待ちぼうけの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "マキノ雅広の戦後第４作にあたる作品で、脚本に新藤兼人が参加している。ある港町の、運送屋と宿屋を兼ねる“待帆荘”に泊まり合わせた疎開先からの引き揚げの一家と、復員軍人、得体の知れない女、待帆丸の船長とその家族たちのそれぞれの人生模様を美しく描く。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Animal Kingdom", "アニマル・キングダム", "", "", "http://image.eiga.k-img.com/images/movie/57283/315x210.jpg?4489", 113, "実在の犯罪一家をモデルに1人の少年の葛藤と成長を描いたクライムドラマ。オーストラリア、メルボルンに暮らす17歳の少年ジョシュアは、母の死により祖母ジャニーンの家に引き取られるが、その家に住む親族は強盗や麻薬売人などの犯罪者ばかり。真面目だったジョシュアも徐々に犯罪の世界へ引きずり込まれていく。一家を束ねる祖母ジャニーン役を演じたジャッキー・ウィーバーが、2010年・第83回アカデミー助演女優賞にノミネートされた。監督はナタリー・ポートマン製作・主演の「メタルヘッド」で脚本を手がけ、本作が初メガホンのデビッド・ミショッド。", "2012-01-21", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hatari！", "ハタリ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 159, "東アフリカのモメラ野獣ファームは、世界の動物園のために野生動物を生け捕りにするのが仕事だ。所長が犀に殺されて後を継いだ一人娘のブランディを、リーダー格のショーンを中心とした熟練ハンターたちが助けていた。そこにスイスから女性カメラマンが取材に来て……。タイトルの「ハタリ！」とはスワヒリ語で「危険！」の意味。", "1962-10-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRITTERS", "クリッター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "極悪非道で食欲旺盛な生物“クリッター”が銀河系刑務所を脱獄し、アメリカのカンザス州に上陸。やがて侵略を開始する。クリッターを倒すのは銀河系の仕事人、二人のトランスフォーマーか、それとも人類か……。パロディー続出のSFホラー・コメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Burning Plain", "あの日、欲望の大地で", "", "", "http://image.eiga.k-img.com/images/movie/54653/315x210.jpg?3199", 106, "「バベル」「21グラム」の脚本を手がけたギジェルモ・アリアガの長編監督デビュー作。行きずりの相手との情事を繰り返す荒んだ日々を送るポートランドのレストランマネージャー・シルビア。ある日、彼女のもとに娘と名乗る少女アリアが訪れる。突然の娘の出現に戸惑うシルビアの脳裏に若き日のニューメキシコでの過ちがよみがえる……。主演はシャーリーズ・セロン、キム・ベイシンガー。", "2009-09-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUMPING IRON II：THE WOMEN", "パンピン・アイアンII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "優勝賞金５万ドルの富と栄誉を賭けて激しい闘いを繰り広げる女性ボディビルダーたちを描く。パワー・トレーニングで見事な肉体美を創造した女性たちが、ラスベガスのシーザーパレスで、チャンピオンシップを競うシーンは圧巻。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE QUEEN OF SPADES", "スペードの女王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "賭博熱が異常に高まっていた1806年のペテルスブルグ社交界を舞台にした文豪プーシキンの短編の映画化。貧乏な大尉ヘルマンは、悪魔から魂と引き換えに必勝術を教わったという老婦人に近づき苦心の末秘伝を会得、全財産を賭けるが……。イギリス特有のミステリアスな小品。", "1949-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バックステージ", "バックステージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ショービジネスの世界で成功することを夢見る若者たちの青春ラブ・ストーリー。俳優養成所に通う悟は、役者としての将来に不安を感じていた。そんなある日、幼なじみで歌手を目指しているさやかと再会する。お互いに惹かれ合う二人は、一緒に夢を実現していくことを誓い合うが……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("United", "ユナイテッド　ミュンヘンの悲劇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "", "2012-07-07", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ARRIVAL", "アライバル 侵略者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "異星人と科学者の壮絶な闘いを描いたSFサスペンス。カリフォルニアの砂漠、オロ・バーレ宇宙観測所で働く科学者ゼイン・ジミンスキは、地球外生命体の存在を示す証拠を手にする。が、NASAでは信じてもらえなかったあげく、解雇処分となってしまう。単独で調査を進めていったゼインはやがて、メキシコのジャングル奥深くで、巨大なプラントを発見する。そこは地球の環境を変えてしまう恐るべき施設で、人間に姿を変えたエイリアンたちが働いていた。ゼインは地球を救うため、一人エイリアンに立ち向かっていく。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("声なき群唱 いま、この語り部たち", "声なき群唱 いま、この語り部たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "登場人物一人につき、手話を担当する黒衣をつけるという、新しい方法でろうあ者のための演劇をつくる劇団GMG。彼らが一つの戯曲を、上演するまでの過程を追ったセミ・ドキュメンタリー。なお、この劇団の主催者である大原秋年は1990年にろうあ者の悲劇を描いた「四つの終止符」を自主製作している。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い水", "赤い水", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "東海地方にある、平凡な町、ツバメ町。振興策を協議していた町長たちのもとに、耳よりな話が届く。地方事務所長の野沢が、酔っ払った勢いで、ツバメ町の“温泉郷建設案”を口走ったというのだ。町の有力者たちは、地価高騰を見込んで、土地の買い占めに奔走し出すが……。一つの地方の町を、日本の腐敗する政治の縮図として促えた、社会派監督、山本薩夫の風刺喜劇。芸達者な役者をそろえ、笑いを誘発する仕掛け。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ORGAZMO", "オーガズモ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "カルト・アニメ『サウスパーク』の生みの親であるT・パーカーが監督・主演を務めた、意欲あふれるセックス・コメディ。敬虔なモルモン教徒ジョー・ヤングはハリウッドでの成功を夢見ていた。ある日、布教活動の途中、ポルノ映画の撮影現場に出くわした彼は、監督マックス・オービソンに見初められ、“キャプテン・オーガズモ”の主演男優としてデビュー。映画は瞬く間に世界中でヒットを記録するが、ジョーはうぶな婚約者リサに仕事を辞めるよう哀願されて……。脳天気でスラップスティックな笑いが、全編を横溢。奇怪なコスチューム・プレイの一種としても楽しめる。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WIE　FEUER　UND　FLAMME", "ネレ＆キャプテン／壁をこえて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ドイツがまだ東と西に分断されていた1980年代を背景に描かれるラブ・ストーリー。秘密警察の監視と東西を隔てる高い壁に阻まれながら、なお貫かれた若い男女の愛が静かな感動を呼ぶ。1982年のベルリン。東西に分断されたこの街の西側に住むネレは、東で暮らす祖母が亡くなり、東ベルリンを訪問する。そこで彼女はパンク・ミュージシャンのキャプテンと出会い、やがて恋に落ちた二人は、壁を越えて愛を育んでいくが……。", "2001-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOS TARANTOS", "バルセロナ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "スペインのバルセロナを舞台に、独自の掟と習慣を守り続け互いに対立する、ジプシーの二つの勢力、タラント家とソロンゴ家の悲劇を描く。全編にフラメンコを取り入れた、『ロミオとジュリエット』バルセロナ版といえる悲恋物語。", "1961-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎 円月斬り", "眠狂四郎 円月斬り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "家斉の隠し子が将軍の世継ぎを狙って暗躍する。マザコンで刀剣マニアである彼は、狂四郎の“無想正宗”に魅了され、我がものにせんとたくらむ。狂四郎は刀のためし斬りにされた男の遺児とともに彼らの陰謀を断つ。脇役、特に女優に魅力が乏しく、話もご都合主義が目立つ。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Madagascar", "マダガスカル", "", "", "http://image.eiga.k-img.com/images/movie/1513/315x210.jpg?2915", 86, "ウッディ・アレンが主人公の声を担当したアニメ「アンツ」のエリック・ダーネル監督と、TV「Kablam!」などのトム・マクガース監督によるアドベンチャー・コメディ。ニューヨーク動物園で育った根っからの都会っ子4頭＝ライオン、シマウマ、キリン、カバが、ひょんなことから南の島マダガスカルに漂着してしまう。彼らははたして大自然の中で生き延びられるのか？　コメディの名手ベン・スティラーとクリス・ロックが声の共演。", "2005-08-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　LETTRE", "クレーヴの奥方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ポルトガルの名匠オリヴェイラが、ラファイエット夫人の恋愛小説を、現代に舞台を移して映画化。医者のクレーヴ伯爵に見初められ、理想的な結婚をした美しいカトリーヌ。だが、人気歌手アブルニョーザとの運命的な出会いにより、カトリーヌは真実の愛に気付いていく。ヒロイン役のC・マストロヤンニのゴージャスで品のある美しさが、物語に華を添えている。", "1999-01-01", "25,2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴーヤーちゃんぷるー", "ゴーヤーちゃんぷるー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "原作は第50回毎日児童小説コンクール最優秀賞作『まぶらいの島』。人間関係が希薄になった現代、ひきこもりの中学生ひろみが、沖縄の離島で心を解放していく温かな人間ドラマ。美しい自然と開放的な島民たちが心を和ませてくれる。学校でいじめられ、ひきこもり状態となったひろみ。どこにも居場所のない彼女は、唯一本音を話せるメル友のケンムンが住み、家を出て行った母の暮らす場所でもある西表島へと向かう。", "2006-06-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE HEAT", "白熱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ギャング映画と呼ばれるジャンルにおける古典的名作で、特にこの種の映画が量産された1940年代を代表する傑出した作品だ。強盗ギャング団のボス、コディは警察に追い詰められ、やむを得ず一時仲間を解散させて自らも単身イリノイ州へ逃げた。そこでコディは当局の目をあざむくため、ホテル強盗の犯人として自首する。しかし怪しいとにらんだ警察側も捜査官のビックを犯罪者に仕立てて刑務所に送り、コディをスパイさせる。ビックはコディの信頼を得て、ともに脱獄するが……。ギャング役の元祖ともいえるJ・キャグニーの壮絶な演技が見もの。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("めぞん一刻 完結篇", "めぞん一刻 完結篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "高橋留美子の同名原作をアニメ化して、大ヒットしたTVシリーズ『めぞん一刻』の続編にあたる劇場版。五代裕作と音無響子が二日後に挙式を控えている設定で、TVとはキャラクターのデザインが異なっている。明後日に結婚式を挙げるというのに、一刻館では宴会が開かれている。めぞん一刻の酒好きの住民たちに、無理やり誘われ参加していた五代だが、恋人・響子の帰りが遅いので、次第に落ち着かなくなってきて……。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANCESCO GIULLARE DI DIO", "神の道化師、フランチェスコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「無防備都市」「戦火のかなた」などのいわゆる“戦争３部作”以外は日本未公開作の多いR・ロッセリーニの最高傑作の１本。13世紀の聖者フランチェスコとその仲間の生活を、短いエピソードでつないだ物語。とりたてて何の事件が起こるわけでもないのだが、徹底して抑制された演出が静かな詩情ととぼけたユーモアを見事に醸し出し、この上もない幸福な気分にさせてくれる。また、小春日和、どしゃぶりの雨、横なぐりの雪などを美しく捉えた撮影も絶品。", "1950-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEART CONDITION", "私の愛したゴースト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "製作当時流行した“愛すべき幽霊映画”の一編。ヨレヨレの背広にくわえタバコのムーニーはロスでも有名な荒くれ刑事。片や高級スーツをビシッと着こなし、ベンツを乗り回すストーンは腕利きの黒人弁護士。この対照的な二人が手を組んで捜査に乗り出すことになり、しかも片方が幽霊だときたからさあ大変……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIME CUT", "ブラック・エース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "カンザスシティのボス、メリー・アンは敵対するシカゴの組織からやって来た殺し屋たちを“人肉ソーセージ”にして送り返していた。そこにスゴ腕の一匹狼が乗り込んでくるが……。二大アクション・スターが激突するバイオレンス映画。オスカー女優のS・スペイセクのデビュー作。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画女優", "映画女優", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "日本映画史に絶大なる足跡を残した不世出の大女優・田中絹代の生涯を描いた作品。16歳で蒲田撮影所に入り、数々の名作やヒット作に恵まれながらも、私生活では必ずしも幸福ではなかった彼女の姿を、吉永小百合が演じる。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jackass3D", "ジャッカス3D", "", "", "http://image.eiga.k-img.com/images/movie/55793/315x210.jpg?4202", 94, "体を張ったパフォーマンスと過激な下ネタのオンパレードで、世界に笑いと衝撃をもたらしたMTVの同名人気番組を3Dで映画化したシリーズ第3弾。今回も簡易仮設トイレを丸ごと逆バンジージャンプさせたり、ゴリラを家の中に放ったりと、やりたい放題のいたずらを仕掛ける。監督はシリーズ全作を手掛けるジェフ・トレメイン。プロデューサーはスパイク・ジョーンズと、番組ホストを務めるジョニー・ノックスビル。", "2011-05-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆破３秒前", "爆破３秒前", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "大藪春彦の原作『破壊指令No.1』を小林旭主演で映画化した、ハードボイルド・アクション。内務局諜報部の破壊工作員・矢吹は、戦時中に日本軍が某国から略奪した宝石を私物化した犯人を追うが、かつての同僚がボディガードとして雇われていた……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲキ×シネ　髑髏城の七人～アオドクロ", "ゲキ×シネ　髑髏城の七人～アオドクロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 200, "舞台映像をスクリーンで上映する《ゲキ×シネ》第２弾。劇団☆新感線の人気舞台『髑髏城の七人』を、歌舞伎俳優・市川染五郎主演で再演。2004年の春に『…アカドクロ』、秋に本作『…アオドクロ』と、同じ年に同じ舞台をまったく違うキャストで上演されたことも話題を呼んだ。“アカ”に比べて、徹底してエンタテインメント性にこだわったストーリー展開と、多数のカメラを使ったスピーディーな映像に引きつけられる。", "2005-02-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヅラ刑事（ヅラデカ）", "ヅラ刑事（ヅラデカ）", "", "", "http://image.eiga.k-img.com/images/movie/20143/315x210.jpg?3195", 80, "カツラを武器に悪と戦う刑事の活躍を描く、モト冬樹主演の異色ポリス・アクション。自らのカツラをブーメランのように投げる必殺技“モト・ヅラッガー”を駆使し、これまで数々の事件を解決してきたヅラ刑事。ある日、原子力発電所の核燃料がテロリストにより奪われるという事件が発生する。ヅラ刑事率いる花曲署の個性的な刑事たちが事態の解決に立ち上がるが……。メガホンを取るのは「いかレスラー」「コアラ課長」の鬼才・河崎実監督。", "2006-09-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サマードレス", "サマードレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 15, "友人と海にバカンスへやってきた青年ミック。海辺でルシアという女性と知り合ったミックは、ひょんなことから彼女の青いドレスを借ることになるが……。ロカルノ国際映画祭の新人監督賞を受賞したフランソワ・オゾンの短編で、ブラックだったり、なかばグロテスクだったりすることも多い初期のオゾン作品にあって微笑ましい一編。", "2004-05-25", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獄門島", "獄門島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "終戦間もない頃、引き揚げ船の中で死んだ男の遺書を友人から受け取った私立探偵・金田一耕助は、獄門島と呼ばれる島を訪れた。が、その直後、島で奇怪な連続殺人事件が起こる。やがて、金田一は事件のカギが、松尾芭蕉とその弟子・其角が詠んだ俳句にあることに気がつく。原作の持つおどろおどろしい妄念を生かしながら、映画化にあたって原作と犯人を変えたところが目新しい。脚本の久里子亭は推理小説ファンである市川崑と日高真也がアガサ・クリスティをもじってつけたペンネーム。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("shoes", "shoes", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "バレエダンサーになりたいと願う少女が、暴走した自分の夢に取り込まれてゆく姿を描く。モノクロ映像でノスタルジックな世界を演出するなど実験的な映像の数々にも注目。バレエダンサーを夢みるナオはバレエの入団テストで致命的な失敗をするが、一次選考を通過する。ナオはその理由が自分と肉体関係のある講師が便宜を図ったためであることを知り……。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EN ENDA NATT", "一夜〈いちや〉かぎり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "自分の出生の秘密を知らないで育ったバルデマールは、自分が貴族の私生児だったことを知らされる。そして貴族としての教育を受けるため実父の屋敷に移り住む。彼は実父の希望もあって、エヴァという女性を愛するようになるが、その愛が肉体的な場面にさしかかった時、エヴァに拒絶されてしまう。この時、バンデマールは自分は貴族にはなれないことを身をもって知らされたのだった。スウェーデン時代のバーグマンが初々しい。", "1939-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すくらんぶる・ハーツ／恋のソナタ", "すくらんぶる・ハーツ／恋のソナタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "お互いに浮気をしている複数の男女が、“愛人に回せ”と書かれた一通のチェーン・メールをきっかけに、究極のラブ・ゲームに巻き込まれていく。スピード感あふれる展開で、現代の若者の姿を描いたラブ・ストーリー。本作が映画初出演となる矢沢心が、繊細に揺れる恋心を絶妙に演じている。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺は待ってるぜ", "俺は待ってるぜ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "蔵原惟繕監督・石原慎太郎脚本そしてタフガイ・石原裕次郎主演による日活アクション初期の代表作。兄のあとを追ってブラジルへ行くことを夢みている主人公が、ふとしたことから兄はブラジル行きの船に乗るどころか、波止場で顔役に金のために殺されたことを知る。そして、単身、事務所に乗り込み顔役を壮絶な殴り合いの末ぶちのめす。ラストの裕次郎と二谷英明のファイト・シーンは、重量級の二人だけに迫力十分。この作品は蔵原惟繕の監督デビュー作であり、裕次郎にとっても次回作「嵐を呼ぶ男」でブレイクする前の大きなステップとなった作品。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AS TEARS GO BY", "いますぐ抱きしめたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "香港、九龍側北部に広がる大繁華街モンコックを根城とするギャングの一味アンディは、足を洗おうとしていたが、いつも弟分ジャッキーがもめごとを起こして、それはかなわない。彼は、美しい従姉妹マギーと出会い、彼女との新生活を夢みるようになるが……。暗黒街の出口を見つけようとするアンディ役を、A・ラウが巧演。W・カーウァイ監督のデビュー作。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Legal Eagles", "夜霧のマンハッタン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "マンハッタンを舞台に、地方検事補と女弁護士が18年前に起きた絵画消失事件の謎を追う。2人は次第に心を通わせていくが、焼死した画家の娘が地方検事補に恋をして……。軽いタッチのロマンチック・サスペンス。レッドフォードが「雨に唄えば」を歌い踊るシーンが印象的。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パラサイト・イヴ", "パラサイト・イヴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "瀬名秀明が薬学部研究者であった27歳の時に発表。第２回ホラー小説大賞を受賞し、その後、140万部を売り上げた同名のベストセラーを映画化したバイオ・ホラー。大学の薬学部に勤務する気鋭の生化学者・永島利明は、細胞中のミトコンドリアの研究で実績を上げていた。ある日、妻の聖美が交通事故で脳死状態となってしまう。彼女は生前、腎バンクに登録していたため、腎臓は麻理子という少女に移植されることになる。ところが、聖美を失った悲しみの余り狂気に走った利明は移植医に頼み込んで聖美の肝臓を譲り受け、その肝細胞に“Eve１”と名付けて培養。すると、日に日に細胞は増殖を始め、それと同時に麻理子の周囲でも異変が起き始めるのだった……。人類誕生以来、人間の細胞に寄生してきたミトコンドリアの暴走、人類への反乱を、最新のヴィジュアル・エフェクツを駆使した映像によって描き、恐怖の幻想を導き出している。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十月圍城", "孫文の義士団", "", "", "http://image.eiga.k-img.com/images/movie/55773/315x210.jpg?2220", 139, "清王朝末期の香港、腐敗した王朝打倒を掲げる革命家の孫文が、同志たちに武装蜂起を促すため密かに来航。王朝の西太后が仕向けた暗殺集団に対抗するため、孫文を護衛する義士団が結成される。義士団のメンバーは、各々が信じるもののために苛烈な戦いに身を投じていく。「ウォーロード　男たちの誓い」のピーター・チャンが製作、「アクシデンタル・スパイ」のテディ・チャン監督がメガホンをとる。ドニー・イェン、レオン・ライ、ファン・ビンビンらアジアのスターが結集したアクション大作。", "2011-04-16", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIPER", "殺人核弾頭 キングコブラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "元警官のトラヴィスは、弟の罪をかぶり無実の罪で10年間を刑務所で過ごした寡黙な男。今はテキサスの片田舎で自動車の修理工として働く彼の前に、昔の恋人ロンダが現れる。かつては激しく愛し合い、信じ合っていた二人だが、彼の服役中にロンダは弟フランクリンの恋人になっていた。フランクリンを助けてほしいと懇願されたトラヴィスは、いつの間にか麻薬組織とのトラブルに巻き込まれていく……。カリフォルニアの陽光のもとで繰り広げられるバイオレンス・ハード・アクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tucker and Dale vs Evil", "タッカーとデイル　史上最悪にツイてないヤツら", "", "", "http://image.eiga.k-img.com/images/movie/57583/315x210.jpg?4093", 88, "凶悪な殺人鬼と勘違いされてしまった2人の男が巻き込まれる事件を描いたスプラッターコメディ。気のいいタッカーとデイルは念願だった別荘を手に入れ、休暇を過ごしにやってくるが、近くでキャンプをしていた大学生グループから人里離れた山小屋に住む殺人鬼と勘違いされてしまう。さらに、2人が川で溺れかけていた女子大生を助けたことが誤解を生み、血まみれの死人が続出する事態へと陥っていく。", "2012-02-11", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夕闇ダリア", "夕闇ダリア", "", "", "http://image.eiga.k-img.com/images/movie/57483/315x210.jpg?3223", 70, "3人の女性監督が女性ならではの視線で「女性の恋とセックス」をテーマに撮り上げた「ポルノチック」シリーズの1作。「東南角部屋二階の女」（2008）で劇場映画監督デビューを果たした池田千尋が、恋に迷い幻想に惑わされる女性の姿を描く。花屋を営むかなえは店員の大谷と付き合っていたが、大谷には由起子という本命の恋人がいるため、別れなければと思いつつも、ひとりになることを恐れて別れられずにいた。そんなかなえの前に、やがて由起子の幻想が現れるようになり……。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("БРАТЬЯ КАРАМАЗОВЫ", "カラマーゾフの兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 227, "ドストエフスキーに造詣の深いプイリエフ監督が律義をきわめたアプローチを行った“本場”もの。長大な原作の中から兄弟たちの父親殺しと、長男ドミトリとグルーシェンカの愛に重点を置き、親子兄弟の葛藤を赤裸々に描く。この映画の撮影中プイリエフ監督が急死、ドミトリ役のM・ウリャーノフらがその志を継いで完成させた。モスクワ映画祭金賞受賞の作品。", "1968-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT OLD FEELING", "もう一度アイ・ラブ・ユー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "将来有望な政治家との結婚を目前に控えたモリーにとって、離婚した不仲の両親は悩みのタネ。案の定、14年も口をきいていない父と母は式場で大ゲンカを繰り広げるが、ひょんなことから彼らの愛が再燃し……。前夫と再会する女性に扮したB・ミドラーが、お得意のパワフルなコメディ演技を披露する恋愛ドラマ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キング・オブ・トーキョー・オ・フィウミ", "キング・オブ・トーキョー・オ・フィウミ", "", "", "http://image.eiga.k-img.com/images/movie/53883/315x210.jpg?3030", 104, "Jリーグが開幕した1992年に来日し、当時アマチュアリーグに属していたチームをJ1にまで導いたブラジル人サッカー選手アマラオの半生を追ったドキュメンタリー。ブラジルで送った少年時代から、“キング・オブ・トーキョー”と称えられるほどの活躍を見せた日本での現役時代、さらに故郷や日本のサッカー界のために奔走を続ける現在の活動などを、アマラオ本人や関係者へのインタビューを交えつつ映し出していく。", "2008-10-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真夜中の弥次さん喜多さん", "真夜中の弥次さん喜多さん", "", "", "http://image.eiga.k-img.com/images/movie/1753/315x210.jpg?3330", 124, "「木更津キャッツアイ」「GO」の人気脚本家・宮藤官九郎が「ピンポン」に続いて人気カルト・コミックの映画化に挑戦。今回は監督デビューも果たす。原作はしりあがり寿の「真夜中の弥次さん喜多さん」とその続編「弥次喜多in DEEP」。弥次さんは愛する喜多さんが「現実（リアル）が分からない」と言うのを聞いて、リアルを見つけ出すためオートバイでお伊勢参りへと旅立つ。原作者のしりあがり寿もたわあ麗満堂の店主役で出演。", "2005-04-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Grifters", "グリフターズ／詐欺師たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "アメリカの人気作家ジム・トンプソンによる同名小説を、「危険な関係」のスティーブン・フリアーズ監督が映画化。競馬のノミ屋で働く女詐欺師リリー、その息子のケチな詐欺師ロイ、そしてロイの恋人の女詐欺師マイラ。ロスを舞台に彼らが繰り広げる愛と憎しみのだまし合いをスタイリッシュな映像で描いたクライム・サスペンス。3人の詐欺師たちを、ジョン・キューザック、アンジェリカ・ヒューストン、アネット・ベニングが好演。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZORBA THE GREEK", "その男ゾルバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "亡父の遺産である亜炭の山を採掘しようとしたバジルは、現場監督としてギリシャ人のゾルバを雇う。その豪放で泰然とした人柄にやがてバジルは魅了されていく……。A・クインがゾルバに扮し、その自然体の演技が役にピッタリである。", "1964-01-01", "1,27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("情炎", "情炎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1966年に直木賞を受賞した立原正秋の小説『白い罌粟(けし)』を原作に、日本のヌーヴェル・ヴァーグを牽引していた吉田喜重監督が撮り上げた女性映画の意欲作。女性の性とその心理を斬新な演出によって映し出す。玉の輿に乗って結婚した女の夫には愛人がいた。ある日、亡き母の愛人だった男と再会し、妙に懐かしい気持ちになり、やがて……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遊び", "遊び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "増村保造監督による、野坂昭如の小説『心中弁天島』の映画化。町工場で働く貧しい少女とチンピラヤクザの少年が出会い、逃亡の果てに真実の愛をつかむまでをダイナミックに描く。デビューして間もない16歳の関根恵子の初々しい存在感が魅力。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mean Streets", "ミーン・ストリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "ニューヨークのイタリア人街で生まれ育ったチャーリーは、27歳になるのに定職もなく遊びまわっていたが、そんな自分に罪の意識を感じていた。親友のジョニーは酒や女やバクチに明け暮れ借金取りから逃げまわっていたが、ときどき突拍子もない行動で周囲を困らせていた。チャーリーは伯父からレストランを任され、恋人のテレサとまともな暮らしをしようとしていたが、そんな矢先、ジョニーが拳銃を乱射しているという知らせを受ける……。スコセッシ監督とデ・ニーロの出世作。夜のニューヨークの息詰まるような雰囲気の中、はけ口の見つからない青春のいら立ちを繊細に描く。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ghost daｎce", "ghost daｎce", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "インディーズムービー・フェスティバルでグランプリに輝いた山口円による初長編作。愛する人を亡くし、絶望の中で生きる人々の癒しと再生の物語が展開する。恋人を亡くした満は“ゴースト・ダンス”という儀式の存在を知る。間違えずに踊ると故人に再会できるというその踊りに参加する満だったが……。主演の新人女優・零や主題歌を担当したフォルクローレバンド“SISAY”の存在感など、自主映画を超えたクオリティーに圧倒される。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聖獣学園", "聖獣学園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "修道尼だった母の死に疑問を抱いた娘が、助修女として修道院に潜入。母が司祭に妊娠させられて自殺に追いやられたと知り、司祭に復讐を遂げていく。鈴木則文自身の同名劇画の映画化で、デビュー間もない頃の多岐川のヌードが話題になった。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bridesmaids", "ブライズメイズ　史上最悪のウェディングプラン", "", "", "http://image.eiga.k-img.com/images/movie/56483/315x210.jpg?1501", 125, "花嫁介添人（ブライズメイド）たちが巻き起こす騒動を描き、全米で興行収入1億ドルを突破するヒットとなったコメディ。仕事も恋愛もうまくゆかず人生に行き詰っていたアニーは、親友が婚約し、花嫁介添人のまとめ役をすることになる。崖っぷちな自分はもとより、それぞれ問題を抱えた一癖ある花嫁介添人たちをまとめるため奮闘するアニーだったが、ハプニングが続出し……。主人公アニーを演じるクリステン・ウィグは脚本も手がけ、第84回アカデミー脚本賞にノミネート。共演のメリッサ・マッカーシーも助演女優賞候補に挙がった。「無ケーカクの命中男　ノックトアップ」「40歳の童貞男」のジャド・アパトーが製作を担当。", "2012-04-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARLEY", "ハーレー 憎しみの暴走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "１台のハーレー・ダビッドソンを愛するその名も同じハーレーという少年が、幼い頃から悪の道を歩み続け、更生し生き抜いていく姿を描くドラマ。アメリカの自由の象徴でもあり、アメリカン・ニューシネマで反体制の象徴としても使われてきたバイクが、主人公の人生にオーバーラップする。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花嫁は十五才", "花嫁は十五才", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "熊本から東京に出て来た17歳のむく太は、父親と二人暮らしの15歳の女子高校生、弘子と出会う。お互いに惹かれているのにそっけない態度をとる二人。そんなある日、弘子の父が死んでしまう。むく太と弘子は結婚して一緒に住もうとするが、二人の年齢では法律上結婚できない。仕方なく、アパートで同棲を始めるが……。二人の純粋な愛と情熱を、当時の人気コンビ、和泉雅子・山内賢の主演で描く青春ドラマ。江崎実生の監督デビュー作でもある。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVER　AFTER", "エバー・アフター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "誰もが知っているおとぎ話『シンデレラ』をモチーフに作られたロマンティックなラブ・ストーリー。16世紀のフランスを舞台に、継母や血のつながらない姉らと暮らすお転婆な娘ダニエルが自らの力で運命を切り開き、王子ヘンリーと結ばれるまでを綴っていく。D・バリモアが勝ち気で颯爽としたニュー・タイプのシンデレラを演じている。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRADDOCK：MISSING IN ACTION III", "ブラドック 地獄のヒーロー３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "戦時下のベトナムを舞台に展開されるハード・アクション。消息不明となった兵士と、自分の妻子を本国アメリカに連れ戻すため、ブラドック大佐は再びベトナムへ向かった。見せ場となるアクション・シーンに加え親子の情愛なども盛り込み、新生面開拓に努めている。「地獄のヒーロー」「…地獄のヒーロー２」に続く、シリーズ第３作で最終作となった。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE CRUSH", "ブルークラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ハワイを舞台に、サーファーガールの奮闘を生き生きと描いた青春ドラマ。10メートルもの高波に乗り、巻き波をくぐり抜けるなどの、視覚効果に頼らないサーフィン・シーンは圧巻だ。女性サーファーのアンは地元ハワイでは、ちょっとは名の知れた存在。プロになることを夢見る彼女だったが、事故の際に芽生えた恐怖心を克服できずにいた。しかし、仲間に叱咤激励され、アンは子供の頃からの夢だった大会への出場を目指す。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カレーライスの女たち", "カレーライスの女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "若手映画作家たちにDVカメラを渡し、「自分」と「自分の世界」をテーマにした作品を撮ってもらうというCS放送の企画「シネアストの眼」シリーズの1本として、「あんにょんキムチ」で注目を集めた松江哲明監督が手がけたドキュメンタリー。松江監督が、女優、友達、彼女という3人の知人女性宅を訪問。女性たちにそれぞれ作ってもらったカレーライスを味わいながら、彼女たちと共に過ごすひと晩を映しだしていく。", "2006-05-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRAGNET", "本家・ドラグネット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1950年代のTVシリーズとして、強烈な印象のテーマ曲とともに一世を風靡した作品の映画化。監督・主演はJ・ウェッブ。いかにも刑事らしい風貌でシブく迫り、ドキュメンタリー・タッチの演出も見もの。舞台はロサンゼルス。手際良く事件を解決していく刑事の活躍を人間味たっぷりに見せる。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEDIA", "王女メディア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "愛する夫イアソンに裏切られ、その復讐のために最愛のわが子を殺す宿命の女メディア。このギリシャ神話の世界に着想を得て、パゾリーニは巧みな映像を作り上げている。メディアに扮するのは、20世紀最大のオペラ歌手といわれたM・カラス。", "1970-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("らもトリップ", "らもトリップ", "", "", "http://image.eiga.k-img.com/images/movie/57553/315x210.jpg?4704", 119, "小説家、ミュージシャン、劇作家など多彩なジャンルで活動し、2004年に死去した中島らもの軌跡を、東京芸術大学大学院映像研究科が映画化。中島の短編小説を映像化した「クロウリング・キング・スネイク」「微笑と唇のように結ばれて」「仔羊ドリー」のフィクションパートと、生前の中島と親交のあった竹中直人、古田新太、宇梶剛士、大槻ケンヂらが、故人との思い出のエピソードを披露するドキュメンタリーパート「らも語り」がミックスされている。「らも語り」は「SF サムライフィクション」「TAJOMARU」の中野裕之が構成・監督・編集を担当。", "2012-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おとし穴", "おとし穴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "不況に追い込まれた北九州炭鉱地帯で、互いに抗争する第一組合と第二組合。やがて２つの組合の双方に疑惑を持たせるような事件が次々と起こり、その影にはいつも、不思議な坑夫の姿があった……。安部公房の『煉獄』をベースにした、ドキュメンタリー出身・勅使河原宏の長編劇映画第１作。DVDは「勅使河原宏の世界　DVDコレクション 」に収録。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GHOSTS CAN’T DO IT", "ゴースト・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "初老の大富豪スコットは、不治の病を苦にし、若く美しい妻ケートを残して自殺をする。だが、自殺のため天国に入れないスコットは、幽霊となってケートの前に現れる。再会を喜ぶ二人だったが、肉体を持たないスコットは彼女を抱くこともできない。そこで、彼女に接近してきた若くてハンサムな男に目をつけたスコットは、彼女にその男を殺してもらい、その瞬間、男の体に入り込もうと……。A・クインが幽霊となった大富豪に扮し、B・デレクが若妻役、そして自ら製作も手掛けたラブ・ファンタジー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORSALINO", "ボルサリーノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "1930年代、南フランスの港町マルセイユ。ニヒルでダンディーなシフレディと、女好きでひょうきん者のカペラは、男の野望とロマンをヤクザの世界に求める。だが富と権力を手に入れた時、二人を待っていたものは……。フランス二大スターのコントラストが絶妙。軽快なテーマ曲が当時の雰囲気を醸し出す。", "1969-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("慌心假期", "夜間飛行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「流星」のJ・チャンが手掛けたサスペンス・タッチの女性ドラマ。プロバンスを行く旅の途中で知り合った日本人女性ミキと、香港の人妻ミシェル。かたや不倫の恋に迷い、かたや夫婦生活の行き詰まり苦悩する二人は、旅のなかで心を通わせる。やがて明らかになる意外な事実。さらに、残酷な運命が彼女たちを待ち受けていた……。異国で悲惨な体験を強いられるミキを、宝塚出身の純名りさが熱演。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PREDATOR", "プレデター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "捕虜となった政府要人を救出する任務を帯びて、ジャングルに派遣されたコマンド部隊。首尾よく捕虜を救出した彼らに、突如何者かが襲いかかる。それは、宇宙から来た未知の肉食獣だった……。“マッチョマン”シュワルツェネッガーとエイリアンの戦いを描くアクション映画。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花形選手", "花形選手", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "清水宏はセット撮影というよりも、むしろロケーション撮影に本領を発揮する。この作品も例外ではない。大学陸上部のライバル同士が、行軍での模擬演習においてもその足を競い合うのどかな作品。前半の30分が行軍場面だけで構成される大胆な作品。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラ2000・ミレニアム", "ゴジラ2000・ミレニアム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "４年ぶりに復活したシリーズ第23作。ゴジラが北海道に上陸し、ゴジラ予知ネットを主宰する篠田と新聞記者の由紀が現地調査を敢行する。その頃、茨城県鹿島灘で発見された直径200メートルの岩が自力で浮上し、中から巨大UFOが出現。やがてゴジラとUFOは新宿副都心で相まみえることになる。顔や背ビレがいっそう凶悪に変貌したゴジラのデザインに注目。", "1999-12-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRIKER", "炎の戦士 ストライカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ベトナム退役兵ジョン・ストレートは、数々の苦難を乗り越えて、ニカラグア政府に拘置された戦友のジャーナリストを救出。しかしジャーナリストがつかんだ秘密を知った時、彼はニカラグア軍と、この任務を与えた元上官の両方を敵にまわすことになる。ベトナム戦争の後遺症から暴力を嫌っている彼だが、権力者の暴力には暴力で対抗する決心をする……。B級アクション・スター、F・ザガリーノが大暴れ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Come and Get It", "大自然の凱歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1９世紀後半から２0世紀初頭のウィスコンシン。木材伐採と製紙工場で財をなすバーニーは、昔愛した女の遺児ロッタを年甲斐も無く愛し始める。だが、ロッタの心はバーニーの息子リチャードに向かい……。ゴールドウィンは黙って原作を改変したホークスを解雇、ウィリアム・ワイラーを後任にしたが、ワイラーが監督したのは最後の30分程度だと言われており、ホークス自身「彼は800フィート撮っただけだ。だから本当に私の映画なんだ」と公言している。", "1938-05-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE INCREDIBLY TRUE ADVENTURE OF TWO GIRLS IN LOVE", "２ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ハイスクール最後の年の二人の女の子のホロ苦い初恋物語。反抗的なおてんば娘でクラスメイトから変わり者と陰口をたたかれているランディ。彼女はレズビアンで、人妻のウェンディと関係を持っていたが、最近、しっくりいかなくなっていた。ある日、ランディは、同じ学校の美しい黒人の女の子イービーと知り合い、惹かれ合う。イービーは金持ちの娘でしかも頭が良かったが、ランディと付き合ううちに、彼女もまたレズビアン呼ばわりされるように。親密さを増した二人は、イービーの母が旅行に出た留守に、初めて結ばれるが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGEL", "殺人天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "N・ジョーダンが32歳の時に撮った監督デビュー作。北アイルランドのダンスホールで毎晩サックスを吹いているダニーはある晩、ホールにやって来た口の利けない少女と心を通わせる。しかし、二人はIRA（アイルランド共和国軍）の集金人と称する３人の男がバンドマネージャーから金を奪ったうえ、殺すところを目撃、ふらふらと現場に出てしまった少女も撃ち殺されてしまう。男たちはダンスホールも爆破し、立ち去る。怒りに燃えたダニーは取り憑かれたように復讐を開始するのだった。日本では、1984年PFFのイベントで「エンジェル」のタイトルで上映されたほか、1996年にアイルランド映画祭で上映された。", "1982-01-01", "33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版3D あたしンち　情熱のちょ～超能力♪母大暴走！", "劇場版3D あたしンち　情熱のちょ～超能力♪母大暴走！", "", "", "http://image.eiga.k-img.com/images/movie/55613/315x210.jpg?3479", 0, "テレビアニメ版も好評を博したけらえいこ原作の人気コミックを3D映画化。ある朝、突然カミナリに打たれて超能力を身につけてしまったタチバナ家の母。しかし彼女が手に入れた能力は、物を少しだけ動かせる程度の微妙なものだった。超能力を使って家事をこなしたり、“エスパーママン”に変身して街の人を助けたりする母だったが、家族からは冷たい目で見られるばかり。そんななか、超能力が大暴走してしまい……。", "2010-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Carriers", "フェーズ6", "", "", "http://image.eiga.k-img.com/images/movie/55353/315x210.jpg?4590", 85, "「スター・トレック」のクリス・パインが主演する、未知のウイルスによるパンデミックの恐怖を描くパニック・スリラー。スペインの新鋭アレックス＆デビッド・パストー兄弟がメガホンをとる。治療薬のない致死率100％のウィルスが世界中に蔓延し、ごく僅かに生き残った人間はゴーストタウンと化した街に暮らしていた。お調子者の兄ブライアンと真面目な弟ダニーは、感染を免れている可能性のある海岸を目指し、ブライアンの彼女ボビーとダニーの友人ケイトを連れ、車で旅に出る。しかし、4人の中に感染者が出ると、それぞれの本性が現れ始め……。", "2010-04-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ただいま　それぞれの居場所", "ただいま　それぞれの居場所", "", "", "http://image.eiga.k-img.com/images/movie/55213/315x210.jpg?3364", 96, "2000年4月1日に施行された介護保険制度以降に急増した介護サービスの実態に迫り、日夜奮闘する介護スタッフの姿を記録したドキュメンタリー。低賃金や人手不足などの問題がたびたび報じられる介護現場で、画一的な介護サービスのあり方にジレンマを感じているスタッフや、ようやく見つけた居場所として介護施設で余生を送る人々などを多数取材。監督は「ゆきゆきて、神軍」（87）、「アラカルト・カンパニー」などで助監督を務めた大宮浩一。", "2010-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トロッコ", "トロッコ", "", "", "http://image.eiga.k-img.com/images/movie/55193/315x210.jpg?1317", 116, "芥川龍之介の同名短編小説を、現代の台湾を舞台に翻案し、「殯の森」の尾野真千子を主演に映画化した家族の物語。共演に台湾の名優ホン・リウ、ブライアン・チャンほか。ある夏、敦は急死した台湾人の父親の遺灰を届けるため、日本人の母と弟と台湾の小さな村を訪れる。そこには日本語を話す優しい祖父がおり、父からもらった大切な写真に写るトロッコを一緒に探してくれた。雄大な台湾の自然のもと、それぞれが失いかけていた家族の絆を取り戻していく。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("葉問前傳", "イップ・マン　誕生", "", "", "http://image.eiga.k-img.com/images/movie/57753/315x210.jpg?4759", 100, "ブルース・リーの師として知られるイップ・マンの青年期を描いたカンフーアクション。生涯の伴侶となる女性との出会いや、ライバルでもあった義兄イップ・ティンチーとのエピソードなどが描かれる。1905年、中国広東省・佛山市。6歳のイップ・マンは義兄イップ・ティンチーとともに詠春拳の宗師チェン・ワースンに弟子入りし、妹弟子のメイワイらとともに修行を積む。やがて立派な青年に成長したイップ・マンは、副市長の愛娘チャン・ウィンセンと出会う。ウィンセンはイップ・マンを気にかけるが、メイワイもイップ・マンに淡い恋心を抱いていた。", "2012-03-17", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("賞金稼ぎ", "賞金稼ぎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "宝暦２年、オランダは最新式の銃を安価に提供する見返りに、無条件和親条約を幕府に求めたが、将軍家重はこれを拒絶した。家重の腹心・市兵衛はオランダの動きを探るために薩摩へと向かった。老中・牧野豊後守が放った隠密・陽炎を野川由美子が怪しい魅力たっぷりに演じている。監督は「非牡丹博徒・二代目襲名」の小沢茂弘。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("化粧師／kewaishi", "化粧師／kewaishi", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "石ノ森章太郎の同名漫画を映画化。大正時代のメイクアップ・アーティストを主人公に、化粧されることで生き生きとし始める女性たちの群像、主人公に隠された秘密を紡ぎ出す。主人公に想いを寄せるヒロインに菅野美穂をはじめ、池脇千鶴、柴咲コウら充実したキャスト陣が好演。これがデビュー作となる田中光敏監督も、時代風俗を軽やかに取り込んだ実直な演出を見せている。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEAMLESS　KIDZ　RULE", "孤独の中の光・Seamless", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "市川準監督の「大阪物語」にも顔を見せていたスティーヴン・セガールの息子、剣太郎セガールがアメリカで主演デビューを果たした青春映画。海賊ラジオ局のDJをしている日米ハーフの青年を主人公に、彼を慕う悩める若者たちの群像を描いていく。ドキュメント・タッチの映像で、舞台となるメルローズ・アベニューのファッションブティックの様子やレイヴパーティーの模様も捉えられている。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUN　WITH　DICK　AND　JANE", "ディック＆ジェーン 復讐は最高！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1976年作「おかしな泥棒・ディック＆ジェーン」を、爆笑SFコメディ「ギャラクシー・クエスト」のディーン・パリゾット監督がリメイク。ごく普通の夫婦が泥棒となって、会社を倒産させた社長の悪事を暴く痛快作だ。主演ジム・キャリーが本領発揮するコミカル演技が見もの。昇進した翌日に会社が倒産したディックは妻ジェーンとともに泥棒稼業に転職。さらに会社の金を持ち逃げした社長に復讐を企てる。", "2005-12-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Birthday Girl", "バースデイ・ガール", "", "", "http://image.eiga.k-img.com/images/movie/1293/315x210.jpg?4015", 94, "家族も恋人もいないマジメな銀行マンのジョンは、生活を変えようとインターネットで見つけたロシア人の花嫁を「注文」。やってきた花嫁はまったく英語が話せず、おまけに彼女の従兄弟だという怪しいロシア人2人がやってくる。実はこの3人には別の目的が。舞台出身の英国監督ジェズ・バターワースの第2作。怪しいロシア人役で「クリムゾン・リバー」の監督＆俳優コンビ、マチュー・カソビッツとバンサン・カッセルが共演。", "2006-11-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAX DUGAN RETURNS", "ニール・サイモンのキャッシュ・マン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "息子と二人暮らしのしっかり者ノーラの前に、20数年前に蒸発した父が突然現れる。彼は残り半年の命を孫と暮らしたいと、出所不明の大金を湯水のごとく使い始める……。ロス監督と劇作家サイモンのコンビが、笑いと涙の中に巧みに人生を織り込んだ佳品。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の歌が聴きたい", "風の歌が聴きたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 161, "聴覚に障害を持つ男女の出会いから結婚、そしてトライアスロンへの挑戦を描く実話の映画化。天宮良と中江有里が、全編手話で感情を豊かに表現。二人が鉄人レースのトライアスロンに挑む前向きな姿がさわやかな感動を呼ぶ。３歳の時の高熱がもとで耳が聞こえなくなった中学生の昌之は、ロックン・ロールが大好きな明るい青年。彼は、同じく耳が不自由な画家志望の女の子、奈美子と文通していた。高校生になって初めて出会い、その後も互いに励まし合ってきた二人は東京で再会し、結婚を決意。やがて、昌之が精力的に取り組んでいたトライアスロンに奈美子も参加するようになり、過酷なレースを通してさらに絆を深めていくのだった。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フォーク＆バレット　サヨウナラ戦争 TWILIGHT FILE V", "フォーク＆バレット　サヨウナラ戦争 TWILIGHT FILE V", "", "", "http://image.eiga.k-img.com/images/movie/54153/315x210.jpg?3872", 0, "オムニバスシリーズ「TWILIGHT FILE」第5弾の1作。退屈な日々を送る高校生の小夜は、バイト先のレストラン「ブバルディア」で働く風間に恋をしていた。そして小夜は現実世界の他に、ブバルディアの仲間たちとクローン人間と戦うという妄想世界にも生きていた。ふと我に変えると平凡な世界に戻るが、いつしか現実世界と別世界が重なり合っていき……。監督は「ミートボールマシン」の山本淳一、出演にお笑い芸人のはなわ、風間トオルほか。", "2008-11-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BERLIN CONSPIRACY", "ベルリン・クライシス 細菌兵器を追え！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ベルリンの壁崩壊——統一直前のドイツを舞台に、西（CIA）と東（東ベルリン国家公安省）のエージェントが、細菌兵器を奪ったテロリストグループに挑むサスペンス・アクション。ビデオ“V”シリーズのM・シンガーがCIA捜査官を演じ、アクションに加えてセクシャルなベッド・シーンも見せる。製作総指揮はB級映画の帝王こと、R・コーマン。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILK STOCKINGS", "絹の靴下", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "グレタ・ガルボ主演「ニノチカ」（1939）のブロードウェイ・ミュージカル版の映画化。パリのロシア人作曲家がハリウッド映画の作曲をするのを防ぐため、ソ連の政治委員ニノチカはパリに向かう。だが、彼女はアメリカのプロデューサーと恋仲になり……。アステアとチャリシーの名コンビ作。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WRAITH", "処刑ライダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "アリゾナの荒涼たる田舎町では、ロードパイレーツと名乗る不良集団が街道を支配していた。ある日、黒いターボを駆る全身黒ずくめの謎の男が現れ、彼らに勝負を挑むが……。西部劇の馬を車に変え、スリルとスピード感いっぱいに展開するハード・アクション編。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING OF KINGS", "キング・オブ・キングス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 163, "イエス・キリストの生涯を、N・レイ監督がスーパー・テクニラマで映画化。山上の垂訓、ゴルゴダの丘の磔刑、復活などのエピソードがきわめて感動的に語られる。撮影も美しい。聖書ものの映画化のなかでは成功した１本。ナレーターはO・ウェルズ。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファンキーハットの快男児", "ファンキーハットの快男児", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "深作欣二監督が、かけだしのころに手掛けた中編アクション。国産省局長の子息が誘拐された事件を追って、私立探偵の息子で遊び人の天下一郎が奔走。やがて産業会館建設の入札をめぐる建築会社同士の争い、利権をむさぼる政治家の陰謀が明らかになる。のちに深作作品のトレードマークとなる、手持ちカメラの疾走感あふれる映像がフィーチャーされている。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHERLOCK BONES ACE DETECTIVE", "探偵犬シャーロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "少年ビリーは別居中の発明家の父に会うために、カテリーナ島にやって来た。ある日、ビリーは怪我をしているスコットランド犬を見つけ、病院に連れていく。すると、この犬が人間の言葉で話しかけてきた。その犬はシャーロックという名前で、麻薬組織に捕まった仲間の刑事を助けたいと言っている。驚いたビリーは、父や獣医にその話をするが、もちろん信じてもらえない。シャーロックは、ビリーとしか話をしないのだ。大人から子供まで楽しめるアニマル・エンターテインメントムービー。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水滸傳之英雄本色", "水滸伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "『水滸伝』の英雄、林冲を主人公にしたコミカルなストーリー。皇帝の有能な臣下で人徳もあるリン・チャンは、部下の策略によって反逆罪の汚名を着せられ、追放される。彼の妻は、辱しめを受けまいと自殺を図り……。主演は「たまゆらの女〈ひと〉」のL・カーファイ。ビデオ、DVDタイトルは「水滸伝　男たちの挽歌」。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Birds", "鳥", "", "", "http://ecx.images-amazon.com/images/I/51W1qIGjH0L._SL210_.jpg", 119, "サンフランシスコの近郊にある漁村。ある日、一羽の鴎が若い女性の額を襲った。翌日、大群の鴎が押し寄せ、暖炉からは無数の小鳥が侵入してくる。ついに鳥の襲撃が始まり、人々は原因もわからぬまま逃げまどう。ヒッチコックがD・デュ・モーリアの短編をもとに撮り上げた恐怖映画の傑作。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOVING", "Oh！引っ越し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "R・プライヤーお得意の、おとぼけコメディ。ニュージャージーに住む情報技師アーロは、毎日おかしな隣人にいやがらせをされていたが、文句の一つも言えない始末。そんな折、突然会社をクビになり、3000キロも離れたアイダホへ転職することに。しかし引っ越し屋が隣人の仲間だったから、さぁ大変！", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("そんな無茶な！", "そんな無茶な！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「東京ゾンビ」の監督、「殺し屋1」「牛頭」の脚本などで知られるクリエイター、佐藤佐吉プロデュースによるオムニバス映画。佐藤に「そんな無茶な！」と思わせた企画にのみ、100万円を出資して製作。「プッシーキャット大作戦」の本田隆一、「猫目小僧」の井口昇、「東京ゾンビ」の原作者・花くまゆうさく、自主制作映画が国内外で高く評価されている真利子哲也ら、気鋭の監督たちによるバラエティ豊かな4作品を集めた。", "2007-09-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今夜星光燦爛／STARRY　IS　THE　NIGHT", "今夜星光燦爛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "香港動乱の前年の1966年、大学の教授チョンと教え子のチャイメイは関係をもつ。そして、彼女の妊娠と堕胎がスキャンダルになったことから、チョンは身重の妻とともにイギリスへ旅立ってしまう。それから20年後、独身のチャイメイは不良少年ティンアンと出会い、親子ほど年の違う彼と恋に落ちるが……。「女人、四十。」のアン・ホイが撮ったラブ・ストーリー。B・リンが女子学生、そして自分の意思で生きる大人の女性を好演している。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Apocalypto", "アポカリプト", "", "", "http://image.eiga.k-img.com/images/movie/1923/315x210.jpg?4138", 138, "「パッション」で世界に衝撃を与えたメル・ギブソン監督が、マヤ文明を舞台にひとりの若者の決死の逃走劇を描いたアクション・アドベンチャー。キャストには映画出演経験のない若者たちを起用し、全編マヤ語で撮影された。マヤ文明後期、中央アメリカのジャングルで妻や仲間たちと平和な暮らしを送っていたジャガーは、ある日マヤ帝国の傭兵の襲撃を受ける。都会に連れて行かれた彼らは、干ばつを鎮めるための生け贄にされそうになったところを何とか逃れるが……。", "2007-06-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女侠・夜叉の舞い", "女侠・夜叉の舞い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "“極道の妻たち”シリーズで知られる関本郁夫、高田宏治のコンビが、新人女優の妃今日子を迎えた女性任侠映画。幼い頃に極道の父を殺され、そして今、育ての親である極道一家、夜叉神会の親分を殺された舞。体の中に流れる極道の血を目覚めさせた彼女が、暗黒街の検察として裏社会の悪に立ち向かう。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chocolat", "ショコラ", "", "", "http://image.eiga.k-img.com/images/movie/1333/315x210.jpg?2896", 121, "冬のある日、フランスの小さな村に謎めいた女性ビアンヌが娘を連れて越してきた。母娘は村人が見たこともないような美味しそうなチョコレートであふれた店を開く。客の好みにピタリと合わせて勧められるチョコレートは人々を虜にし、村人はカトリックの断食期間にも教義に反しチョコレートを食べていた。村の指導者レノ伯爵はその事実に愕然となり、ビアンヌを村から追放しようとする。そんな時、ジプシーの青年ルーの船が村にやってきた。", "2001-04-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("島ノ唄 Thousands of Islands", "島ノ唄 Thousands of Islands", "", "", "http://image.eiga.k-img.com/images/movie/33593/315x210.jpg?2993", 93, "海外でも高い評価を受ける詩人、吉増剛造が、沖縄・奄美の島々を巡る旅を追うドキュメンタリー。20年間にわたって南の島々を巡り続けている吉増が、沖永良部島、加計呂麻島、沖縄本島、奄美大島を訪れる。アジアやアメリカの影響を受けた特有の文化を持ち、それぞれの“島ノ唄”を歌う島人たち。数々の出会いの中で、吉増の素晴らしい詩が創造されていく。監督は、テレビドキュメンタリーのディレクターとして活躍してきた伊藤憲。", "2006-08-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA RONDE", "輪舞 ラ・ロンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1900年のウィーンを舞台に、娼婦、兵隊、小間使いなど、十人十色の恋模様が映し出されていく。前のエピソードと次のエピソードをつなぐ人物を登場させ、最後には冒頭のエピソードの人間に戻っていくという、まさにまわりめぐる輪のような展開が見どころ。", "1950-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大奥", "大奥", "", "", "http://image.eiga.k-img.com/images/movie/33793/315x210.jpg?4773", 0, "江戸城大奥の女たちの壮絶な愛憎劇を描いたフジテレビ系の人気ドラマを、仲間由紀恵主演で映画化。江戸幕府7代将軍・家継の時代。大奥総取締の絵島は女中たちから信頼を得ていたが、歌舞伎役者の生島新五郎と運命の出会いを果たし、禁断の恋に落ちていく。共演はTVドラマにも出演していた松下由樹、浅野ゆう子ら。監督もTVシリーズ同様、林徹が担当。", "2006-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NORTH", "ノース ちいさな旅人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "自分に無関心な両親に不満のノース少年は、ある日、出会ったウサギ男との会話から子供のフリーエージェント制を思いつく。次の日、学校でフリーエージェントを宣言。それにマスコミが飛びつくわ、弁護士が裁判を勧めに来るわで、ノースは一躍有名人になってしまう。おまけに、裁判でフリーエージェントの権利を獲得、完璧な両親を探しに世界へと旅立っていく。撮影当時12歳のE・ウッドがノースを好演、B・ウィリス、K・ベイツらが脇を固めている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEEP END OF THE OCEAN", "ディープエンド・オブ・オーシャン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "J・ミチャードのベストセラー小説をもとに、家族の在り方を問い掛ける感動作。平穏な暮らしを送っていたカッパード家にある日事件が起きる。母ベスに連れられて同窓会の会場に来ていた３歳の次男ベンが突然姿を消したのだ。手掛かりがないまま９年の月日が流れ、苦悩から立ち直りつつあった家族は、偶然12歳になったベンと再会。だが、彼は別の家で幸せな暮らしを送っていた。ベスは何とか彼を連れ戻すことに成功するが、家族は戸惑いを隠せず、複雑な思いを募らせていく。企画段階から加わったM・ファイファーが我が子を深い愛で包み込む母親を熱演する。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剣に賭ける", "剣に賭ける", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "剣豪・千葉周作の若き日の姿を市川雷蔵が熱演する時代劇。剣の修練のみを考える周作は、江戸の剣聖・浅利又七郎道場に入門するが、門下生の高柳又四郎の“音無しの構え”に打ちのめされて以降、すさまじい執念で剣の腕を上げていく。しかし、彼にはかつて赤子を斬った罪の意識が深く心に残っていた。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN THE HEAT OF THE NIGHT", "夜の大捜査線", "", "", "http://ecx.images-amazon.com/images/I/51X65FQNYgL._SL210_.jpg", 105, "ミシシッピーの田舎町。黒人ゆえに殺人事件の容疑者となったニューヨークの腕利き刑事が、南部の強烈な人種偏見と闘いながら、事件を解決するまでを、鋭い人間描写と緊迫感に満ちた演出で描く犯罪映画の傑作。原作はJ・ボールの 『夜の熱気の中で』。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金色のガッシュベル!!／　メカバルカンの来襲", "金色のガッシュベル!!／　メカバルカンの来襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "TVアニメやゲームなどさまざまなジャンルに進出する人気コミックを前年に続いて映画化。魔界の子ガッシュと相棒の清麿が、謎の巨大ロボット軍団と壮大なバトルを繰り広げる。ガッシュの親友とソックリの巨大ロボ、メカバルカンとの交流など、感動ポイントも見逃せない。ガッシュの親友、バルカン300そっくりの巨大ロボが出現。最初は新しい親友だと喜んでいたのだが、軍団となってガッシュたちを襲い始める。", "2005-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dark Water", "ダーク・ウォーター", "", "", "http://image.eiga.k-img.com/images/movie/1353/315x210.jpg?3988", 105, "ニューヨークに住む離婚調停中のダリアと幼い娘セシリアは、マンハッタンから離れたルーズベルト島のアパートに引っ越す。ところが、その部屋の天井からは水が漏れ、周囲で奇妙な出来事が起こる。これは離婚や転居、新たな仕事のストレスを抱えるダリアの妄想なのか？　鈴木光司の小説「仄暗い水の底から」を、オスカー女優ジェニファー・コネリーを主演に「モーターサイクル・ダイアリーズ」のウォルター・サレス監督が映画化。音楽はデビッド・リンチ映画の常連アンジェロ・バダラメンティ。", "2005-11-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とどかずの町で", "とどかずの町で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "1990年の「吉祥寺夢影」に続く自主映画作家、大西功一の長編第２作。東京での仕事を捨て、故郷の函館に戻った29歳の功一。両親はすでに亡く、実家で一人きりの生活を始めた彼は、顔見知りのバーでユミコという女の子と出会う……。明確なストーリーの流れがないエッセイ風の静かな語り口で、青年のたゆたうような人生の断面が刻まれた恋愛映画。16ミリ・モノクロで捉えられた函館の町の叙情的なムードが胸にしみ入る。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Love You, Beth Cooper", "愛しのベス・クーパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Snapper", "スナッパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「ザ・コミットメンツ」のロディ・ドイルによる小説を、ドイル自身の脚本により映画化。ハリウッドで成功をおさめたスティーブン・フリアーズ監督が、再び祖国イギリスでメガホンを取る。ダブリン近郊の小さな町に住むカーリー家は賑やかな8人家族。ある日、未婚の長女シャロンが妊娠したと告白する。しかも彼女はお腹の子の父親を決して明かそうとしない。やがて、シャロンの相手は彼女の友人の父親ではないかという噂がたち……。", "1993-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD HEAT", "ゾンビ・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ロス警察のスゴ腕刑事コンビが大活躍するギャグもたっぷりのポリス・アクション。死体を蘇生させる装置を作り出した巨大犯罪組織の陰謀に巻き込まれた二人の刑事は、自らも不死身のゾンビとなって影の黒幕を追い詰めていく。1988年の東京ファンタスティック映画祭で上映され好評を得た作品。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三槍拍案驚奇", "女と銃と荒野の麺屋", "", "", "http://image.eiga.k-img.com/images/movie/56123/315x210.jpg?4398", 90, "「初恋のきた道」「HERO」のチャン・イーモウ監督が、コーエン兄弟監督作「ブラッド・シンプル」（1984）の舞台を中国に置き換えてリメイク。万里の長城の西の果てにある荒野の町で、中華麺屋を経営する強欲なワン。勝気な妻は給仕のリーと浮気し、密かに購入した銃で夫を殺害してからリーと一緒に逃げようと考える。その計画を知ったワンは、警察官のチャンに妻とリーの殺害を依頼するが、麺屋の金庫に眠る大金に目をつけていたチャンは、ワンの思惑とは異なる行動に出て……。", "2011-09-17", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TANGOS L’EXIL DE GARDEL", "タンゴ —ガルデルの亡命—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "伝説のタンゴ歌手ガルデルの生涯をモチーフにしたタンゴ・ミュージカルの上演を試みる、アルゼンチン亡命者たちのパリ生活を描く。バンドネオンの歯切れのよい調べが全編に流れ、人間国宝級のピアニスト、プグリエーセの演奏を観られるのがタンゴ・ファンにはうれしい。", "1985-01-01", "2,22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("集団殺人クラブ", "集団殺人クラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "単館上映ながら口コミで話題が広がり、海外の映画祭にも招待された和製スプラッタ・ホラー。いつもなんとなくツルんでいる５人の女子高生。ある日、その中の一人が起こした事件がきっかけで、５人は恐怖のどん底に突き落とされる。女子高生がチェーンソーで死体を切断するシーンなどが繰り返し映し出され、ショッキングだ。ナゾのゾンビ、タケゾウを個性派、遠藤憲一が悪ノリ気味の怪演で見せる。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GITANO", "ジターノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "フラメンコ・ミュージシャンのアンドレは、敵対するジプシーの女性ルシアと結婚する。しかし無実の罪を着せられ、獄中生活を送ることに。二年後、出所したアンドレをルシアは待っていなかった。彼女はアンドレを陥れた男の妻になっていたのだ……。フラメンコ・ダンサーとして世界的人気の“スペインの貴公子”J・コルテスが初主演した情熱的なジプシーの物語。", "2000-01-01", "9,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KILLERS", "殺人者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ヘミングウェイの同名短編小説を映画化したサスペンス・ドラマ。1964年にはドン・シーゲルによって「殺人者たち」としてリメイクされた。ガソリンスタンドに勤める男スウェードが二人組の殺し屋に殺害された。この事件に興味を抱いた保険会社の調査員リアダンが独自に捜査を開始。やがて、彼はスウェードがある犯罪に巻き込まれていたことを知る……。主演のB・ランカスターと、マフィアの情婦を演じたE・ガードナーの出世作。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Letters to Juliet", "ジュリエットからの手紙", "", "", "http://image.eiga.k-img.com/images/movie/55523/315x210.jpg?2693", 105, "「マンマ・ミーア！」のアマンダ・セイフライド主演の恋愛ドラマ。シェイクスピアの戯曲「ロミオとジュリエット」の舞台となったイタリア・ベローナを訪れたアメリカ人女性ソフィーは、ジュリエットへの恋愛アドバイスを求める手紙への返信を行うボランティアグループに出会う。そこで、ソフィーは50年前に書かれた手紙を見つけ、返信すると、手紙を書いた本人のクレアとその孫のチャーリーが現れ……。監督は「シャーロットのおくりもの」のゲイリー・ウィニック。共演にバネッサ・レッドグレーブ、ガエル・ガルシア・ベルナル。", "2011-05-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BONNE ANNE´E", "男と女の詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "綿密な計画で高級宝石店を襲撃したにもかかわらず、ある女に惚れたことから土壇場で手もとが狂い、一人逮捕されたシモン。６年の刑を終えた彼は、女の行方を捜すが……。現在と過去をモノクロとカラーで撮り分けた、ルルーシュらしい洒落た作品。", "1973-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZAHRADA", "ガーデン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "スロヴァキアのファンタジー作家、M・シュリークの代表作。何をやっても上手くいかないダメ男のヤクプ。父親に家を追われ、祖父が遺した廃墟同然の田舎屋敷に移り住んだ彼は、そこで不思議な少女と出会い……。シュールな物語と、トボケたユーモアが展開する。", "1995-01-01", "72,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十字路", "十字路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "江戸の裏街に姉と弟がうらぶれてひっそりと暮らしていた。弟は想いを寄せる矢場の女がもとで他の男と喧嘩となり、傷害事件を起こす。一方、借金を返済できない姉に女衒の老婆が身売りの話を持ちかける。そんな折、姉は、殺人を犯したと思い込んでいる弟をかばおうとするあまり、目明かしを殺してしまう。雪の夜道を逃げていく姉弟。弟は最後に、一目矢場の女に会いたいと去っていく。深夜の十字路には戻らぬ弟を一人さびしく待ち続ける姉の姿が残る。「狂った一頁」に続く衣笠貞之助の実験的なサイレント作品。ドイツ表現派的な装置の中に衣笠のテクニックが絢爛なまでに効果を上げる。国際的評価を獲得した最初の日本映画である。", "1928-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLLISION COURSE", "ベスト・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「ベスト・キッド」で空手の達人ミヤギを演じ、アカデミー助演男優賞にノミネートされるなど、一躍名声を得たN・P・モリタ主演の痛快ポリス・アクション。とある企業が極秘に開発したトランス・ミッションが何者かに盗まれた。捜査を依頼された敏腕刑事フジツカが事件の真相に迫っていく。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ghost World", "ゴーストワールド", "", "", "http://image.eiga.k-img.com/images/movie/1403/315x210.jpg?3437", 111, "70年代のカルト・コミック「フリッツ・ザ・キャット」の作者ロバート・クラムを描く「クラム」(94) などドキュメンタリーに定評のある監督ツワイゴフが劇映画に初挑戦。クラムとは長年の友人だったという彼が、今回、原作に選んだのは現代のオルタナ・コミックの代表的作品「ゴーストワールド」（翻 訳はpresspop gallery刊）。原作には登場しない中年おたく役でスティ ーブ・ブシェーミ、2人にいつもいじめられてる男の子役でブラッド・レンフロが出演。", "2001-07-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLEEPER", "スリーパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "200年前に胃潰瘍で入院し、そのまま凍結された男が目を覚ます。ロボットと間違えられて運び込まれたのは革命家ルナの家、未来の地球はセックスさえも機械化された管理社会だった……。アレン独特の風刺の効いたSFコメディ。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬猫", "犬猫", "", "", "http://ecx.images-amazon.com/images/I/215G2EVPA9L._SL210_.jpg", 84, "PFFアワード2001で企画（TBS）賞に輝いた８ミリ作品を、そのまま劇場公開。矢口史靖作品などに録音助手として参加してきた井口奈己の処女作で、対照的な女の子二人が互いに向き合うしかなくなる瞬間を積み重ねている。同棲相手のもとを飛び出したスズと、友達の留守を半年間預かることになったヨーコ。旧知の間柄ながら、いつも同じ男を好きになるので疎遠だった彼女たちがバッタリはち合わせしたことから、波紋が広がっていく。女性同士特有の言葉のやりとりや間の呼吸を、８ミリフィルムならではの感触の映像が包み込む。", "2002-03-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Joe Kidd", "シノーラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "土地の所有権をめぐって動揺する町、シノーラに流れ着いたジョー・キッド。囚われの身となった彼は、保安官おかかえのガンマンになることを条件に釈放されるが……。イーストウッドと「OK牧場の決斗」のスタージェス監督の西部劇ベテラン同士の初顔合わせ。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GRAND MEAULNES", "さすらいの青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "少年モーヌがある日、寄宿学校から失踪した。彼が汚れきった服装で帰ってきたのは二昼夜後のことだった……。モーヌの親友であり、校長の息子でもある、内向的な少年フランソワの目を通し、モーヌと、美しい娘のイヴォンヌとの出会い、結婚、別れ、そして、イヴォンヌの死までを描いた、ロマンチシズムあふれる青春映画の佳品である。原作はフランス青春小説の古典的名作、H・A・フルニエの『ル・グラン・モーヌ』。夢と現実の交錯を描き出すカメラワークが美しい。そしてB・フォセーがヒロインを演じる点も、一見の価値がある。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CTHULHU MANSION", "H・P・ラヴクラフトの　新・悪魔の儀式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "黒魔術のショーを演じている魔術師とその娘が、ある犯罪者グループに拉致される。犯人たちは魔術師の自宅である郊外の巨大な屋敷を、しばらく隠れ家として使うことにした。やがて彼らの前に不思議な怪奇現象が次々と起こり始める。それは魔術師が呼び出した魔物たちだった。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スリーカウント", "スリーカウント", "", "", "http://image.eiga.k-img.com/images/movie/54613/315x210.jpg?4073", 100, "女子プロレスの世界を舞台に、夢を追う女性たちの奮闘を描いた青春ムービー。かつて大ブームを巻き起こした女子プロレスも現在は客足が遠のき、スター選手が所属する埼玉ガールズも解散に追い込まれてしまう。夢を諦めきれない新人レスラーのまひるは、埼玉ガールズ再興を目指して活動を開始するが……。クランクインまでに本物のプロレスラーとしてデビューするという異例の条件をクリアした新人女優8人による迫力のプロレスシーンに注目。", "2009-06-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／GILSODOM", "キルソドム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "韓国の民族的題材を取り上げ世界的評価を得ているイム監督が、朝鮮戦争による南北分裂のため、離散してしまった家族の心の痛みを描いたヒューマン・ドラマ。幸せな家庭を築いていたファヨンは、離散家族捜しのテレビ番組を頼りに33年ぶりに生き別れた恋人ドンジンと再会する。二人は、同じく生き別れになった彼らの息子ソクチョルに会いに行くのだが、貧しくすさんだ生活を送る息子を目の当たりにして戸惑いを隠せなかった……。", "1985-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1999年の夏休み", "1999年の夏休み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "萩尾望都のマンガ『トーマの心臓』をモチーフにして、舞台を世紀末の、世間から隔絶された全寮制の学校の夏休みに移し、４人の少年の愛と憎しみを描いている。４人の少年はすべて少女によって演じられ、透明感ある幻想的な雰囲気を醸し出した。また、水原里絵は深津絵里の別名である。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺したのは誰だ", "殺したのは誰だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "自動車セールスマンの栄吉は老いたせいか仕事の成績が悪い。そんな時、車を故意にぶつけて保険金を詐取しようという仲間の誘いを受けたが、弱気の栄吉は断った。栄吉の代わりに決行した男は車の事故で死ぬ。新藤兼人のオリジナル・シナリオを中平康が演出。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DRESSER", "ドレッサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1980年３月にイギリスで初演されて以来、約二年にわたってロングランを記録した同名舞台劇の映画化。戦時下の英国のとある劇場を舞台に、シェイクスピア役者の座長とその付き人ドレッサーとの息詰まるやりとりを描く。上演間近のある日、座長が空襲のショックから精神錯乱に。だが公演は決行され、付き人は錯乱のおさまらない座長をなんとか舞台に立たせようと大わらわ。二人の絶妙のかけ合いが、おかしくも悲しく味わい深い。若いころ、王立演劇学校に学んだイエーツ監督の快心作。コートネイがゴールデン・グローブ主演男優賞を受賞。", "1983-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Comes A Horseman", "カムズ・ア・ホースマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "石油開発をたくらむ男から自分の牧場を守り抜こうとする女牧場主。彼女に手を貸すカウボーイが現れて……。「コールガール」「華麗なる陰謀」に続いてパクラ監督とフォンダがコンビを組んだ現代版ウェスタン。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STILL CRAZY", "スティル・クレイジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "再結成したロック・バンドの奮闘を描いたブリット・コメディ。1970年代の人気バンド、ストレンジ・フルーツが再結成することになり、メンバーに召集がかかった。落ちぶれていた彼らは再起に奮闘するが、解散時の対立を今なお引きずり仲違いは絶えない。そのうえ、中心人物ブライアンはすでに故人となっていた。心がバラバラのまま、バンドはツアーを開始するが……。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京ナイト", "東京ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "和泉雅子がおきゃんな主人公を演じた青春映画。現代っ子の舞妓・小はなは、母に反発して東京に家出してくる。彼女には駆け落ちした姉・雪枝の消息を追う目的もあった。知り合った気のいい青年・健とともに、勝ち抜きバンド合戦に出ることが決まった彼女は、歌の練習を続けながら姉を探すのだが……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピーカン夫婦", "ピーカン夫婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "「プウテンノツキ」の元木隆史監督によるユニークな艶笑ストーリー。大空の下でしかセックスできない体質の女性と結婚してしまった男の哀しくも楽しいセックス・ライフを描き出す。「リアリズムの宿」など、山下敦弘監督作品の常連俳優、山本剛史が主演し、山下自身も俳優として出演している。皮肉屋の尾藤は半裸で日光浴する光子と出会い、そのまま身体を交わしたことから、性格が一変。途端に明るくなり、光子と結婚するが……。", "2005-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("着信アリ", "着信アリ", "", "", "http://ecx.images-amazon.com/images/I/21NY1PA70XL._SL210_.jpg", 112, "三池崇史監督が、秋元康の原作を得て初めて挑んだ心霊ホラー。携帯電話に死を予告する電話を受けた者が、確実に死んでいく……。携帯電話を媒体にして広がっていく呪いの恐怖を、正統派の演出でジワジワと心理的にあおっていく。これが映画初主演となる柴咲コウが、怪事件の陰に隠された忌まわしい出来事と、自身のトラウマに向き合うヒロインを熱演。携帯電話に残された奇妙なメッセージ。発信元は自分の携帯番号。着信時刻は３日後。自分の悲鳴が録音されている。この電話を受けた者が次々と怪死していき、女子大生・由美は妹を亡くしている山下とともに調査を開始する。", "2004-01-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレイジー・コップ 捜査はせん！", "クレイジー・コップ 捜査はせん！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "警察国家日本でも最低・最悪のパープリン署、三ッ目署を舞台に、秩序も倫理も善悪もない“歩く破壊神”、クレイジー刑事、花村大吉と、彼に関わったがためにパニック状態に陥れられる人々の悲喜劇。異色で芸達者なキャストによるスラップスティック・コメディ。井筒和幸が監修に当たっている。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nowhere in Africa", "名もなきアフリカの地で", "", "", "http://image.eiga.k-img.com/images/movie/1243/315x210.jpg?2255", 141, "第2次世界大戦下、ナチスから逃れるため祖国ドイツから、アフリカへ渡ったユダヤ人一家。その幼い娘レギーナは、料理人である現地民オウアや、周囲の子どもたちとの交流の中で、たくましく成長していくが、彼女の両親には複雑な思いがあった。監督は聴覚に障害を持つ両親と暮らす少女を描く「ビヨンド・サイレンス」の女流監督カロリーヌ・リンク。長期ロケを敢行してアフリカの大地のさまざまな顔を映し出す。", "2003-08-10", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　連獅子", "シネマ歌舞伎　連獅子", "", "", "http://image.eiga.k-img.com/images/movie/54073/315x210.jpg?1834", 55, "歌舞伎の舞台公演をスクリーンで上映する「シネマ歌舞伎」の第7弾。「男はつらいよ」シリーズの山田洋次が監督を務め、2007年10月に新橋演舞場にて実の親子である中村勘三郎と勘太郎が獅子の親子を演じた。岡鬼太郎作の古典落語「らくだ」と2本立て上映。", "2008-12-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旗本退屈男 謎の珊瑚屋敷", "旗本退屈男 謎の珊瑚屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "両国に夢の家という飲み屋を開いた柳湯のお染が、突然行方不明になり、退屈男・早乙女主水之介は、部下にお染のひいき筋を洗わせる。やがてお染は回船問屋の主人と大根河岸に上がる……。中川信夫のハッタリを利かした演出がすっかりマンネリと化した映画に見どころを与えている。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LINK", "リンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「サイコ２」で注目されたオーストラリア出身の俊英R・フランクリンによるユニークなホラー。チンパンジーの研究をするイギリス人大学教授の助手を務めることになったアメリカ人学生ジェーン。教授の別荘で彼女を待っていたのは人語を理解するチンパンジー、リンクだった。ある日のことリンクの“処分”が決定。それ以来、教授の姿が消えてしまった……。何といっても見どころは、出演者の演技を喰ってしまうような実物のチンパンジーたちの演技。その指導を、ヒッチコックの「鳥」で鳥たちを見事に操ったレイ・バーウィックが担当。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボールZ　復活のフュージョン！！ 悟空とべジータ", "ドラゴンボールZ　復活のフュージョン！！ 悟空とべジータ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "劇場版シリーズ第15弾は、“'95春東映アニメフェア”の1本として封切られた。孫悟空がいるあの世と、孫悟飯、孫悟天がいるこの世の2世界に分かれて壮絶な戦いが繰り広げられる。一行は、悪い魂に汚染され化け物と化した赤鬼に加え、かつての宿敵ベジータと対決。一方、地上では死人が次々に蘇り、悟天たちを悩ませていたが……。DVDは『DRAGON BOX THE MOVIES』に収録。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWISTER", "ツイスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "撮影監督として長いキャリアを持つだけでなく、「スピード」で監督としてもその手腕を披露したJ・デ・ボンの第２作。子供時代に大竜巻により父親を亡くしたジョーは、大人になってから竜巻の観測チームのリーダーになっていた。そのジョーの所へ夫のビルが恋人メリッサとともにやって来る。彼は、離婚届にサインを求めにやって来たのだが、ジョーにドロシーと名付けられた竜巻観測機を見せられると心が騒ぎだしてしまうのだった。それはかつてビル自身が考案したもので、竜巻の中心に放出し、竜巻の構造を調べるというもの。ビルはジョーたちに同行し、竜巻を追うことにする。同じく、竜巻を追うチームがもう一つあった。それは、かつてビルやジョーの仲間だったジョーナスのチームだったが、彼の存在が、ビルに火を付けることになる。二つのチームは、競争して竜巻の中心に観測機を置くことになるが、竜巻は巨大で、簡単には近づくことができなかった……。暴れ回る巨大な竜巻が見もの。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOLGOTHA", "ゴルゴタの丘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "フランス映画全盛期を飾る、デュヴィヴィエ監督の秀作。彼は、もともと熱心なカトリック信者であったため、この企画は念願の企画といわれた。キリストの受難劇を、モノクロームの見事な映像美を駆使し、視覚化。J・ギャバンがピラト役を熱演している。", "1935-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PARTI 銭の一・ニ", "難波金融伝 ミナミの帝王　劇場版PARTI 銭の一・ニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "同名のオリジナルビデオ・シリーズを、映画版に発展させたもの。大阪ミナミ界隈を舞台に、ヤクザも黙る高利貸し・萬田銀次郎の活躍を、３部構成で描いている。「トイチの結婚」は、結婚詐欺師にだまされたOLが、銀次郎の助けで復讐する。「一千万円の女」では、クラブのホステスが、一千万円くれると言うので客と寝るが、客は銀次郎に頼んで約束を無効にする。その男が銀次郎への謝礼を拒んだために、萬田の怒り爆発。「銀次郎VS整理屋」は、会社を計画倒産させる整理屋と、銀次郎との戦い。竹内力が、型破りの高利貸しを熱演している。DVD・ビデオ版は第１・２話を収録。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仁義なき戦い・広島死闘篇", "仁義なき戦い・広島死闘篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "前作は主人公・広能がヤクザ社会に身を置いてから、抗争事件に疲れ、山守に盃を返すまでを、欲望渦巻く人間の集団劇として描いたのに対し、今回はその中で生きる若者にスポットを当てている。時代は第１作のラストからさかのぼり、朝鮮動乱。工員だった山中は博奕のトラブルがもとで刑務所に服役する。出所した山中は、村岡組組長の姪・靖子のところへ身を寄せ、村岡組の組員になる。やがて村岡組に盾つく大友との間で抗争が起こる。山中は広能たちと違って、戦争には行かなかった遅れてきた世代であり、その痛恨がいたるところに描かれている。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大無謂／６AM", "６AM", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "香港で絶大な人気を誇るアイドル・デュオ“Boy'z”初主演の青春ハチャメチャコメディ。疾走感あふれ、香港映画らしいアクションとユーモアがミックスした痛快作だ。カッコイイ男の子たちがいきいきと走り回る姿は、見ているだけで楽しい。食堂の店主とたった1ドルを巡って言い争っていた落ちこぼれ高校生のふたり。いつの間にか警察やマフィアも巻き込み、1ドルが30万ドルに！ そのお金で遊ぶふたりに身の危険が!?", "2006-06-03", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の紋章", "男の紋章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "大島組の組長・大島庄三郎の一人息子・竜次は、ヤクザ稼業を嫌って医者になるが、父が悪徳ヤクザに殺されたことを知り組へ戻り跡目をつぐ決心をする。しかし父の仇の悪徳ヤクザの黒幕である村田組の女組長・村田きよは、竜次の実母であった……。高橋英樹はデビュー当時、浜田光夫・吉永小百合・和泉雅子らとともに、グリーン・ラインの一員として青春映画に出演したが、今一つイメージにあわず、この作品が日活唯一の任侠スターとしての地位を確立させた。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUDI，THE RACING PIG", "ルディ　夢はレースで１等賞！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "子ブタが大活躍するドイツ版“ベイブ”ともいうべき作品。クジ引きで当たった子ブタのルディは、泥棒を追い払ったり、ママを迎えに行ったりと、今ではすっかり家族の一員。ブタのレースに出場することが決まって、大特訓に励んでいたルディだったが、いたずらが過ぎて町の人たちを怒らせてしまう。このままでは、ルディは養豚場に送られて、ソーセージにされちゃう！　家族ぐるみでのルディ救出作戦が始まった。", "1995-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Island Etude", "練習曲", "", "", "http://image.eiga.k-img.com/images/movie/54013/315x210.jpg?3199", 108, "台湾シネマ・コレクション2008上映作品。大学卒業を間近に控えた青年ミンシァンは1週間の自転車の旅に出たミンシャンは、旅を通して台湾の文化や歴史、人情を再発見していく。本国で大ヒットし、自転車で台湾一周をする人々が続出したという青春映画。聴覚障害のある主人公に、実際に聴覚障害を持つ映画初出演のトン・ミンシャン。監督は、「悲情城市」などのホウ・シャオシェン作品の撮影監督として知られるチェン・ホァイエン。", "2008-08-23", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CARROSSE D’OR", "黄金の馬車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ヴィヴァルディの曲にのせ、18世紀のスペイン植民地だった南米を舞台に、土地の総督とイタリア人旅芸人一座が織り成す幻想劇が展開する。イタリア喜劇の味わいを捉えたセリフ回し、細部まで行き届いたセット、それを生かすテクニカラーの素晴らしさなど、ルノワールの力量が全編に表われている。イタリアのチネチッタで撮影が行われているのがルノワールとしては異色。日本では長い間、未公開だったが1990年に公開された。", "1952-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UGLY", "アグリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "冷酷なシリアルキラーの心理をサスペンスフルに描いたサイコ・スリラー。連続殺人犯のサイモンは心神喪失とみなされ、精神病院に５年間監禁されていた。サイモンによると、彼の心に住みつく分身“アグリ”こそ殺人の真犯人らしい。面接に当たった女性精神分析医レンは、そんなサイモンに疑惑を覚えながらも、やがて衝撃的な事実を突き止め……。", "1997-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE VERLORENE EHRE DER KATHARINA BLUM", "カタリーナの失われた名誉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「テルレスの青春」「ブリキの太鼓」のシュレンドルフ監督が、ノーベル賞作家H・ベルの小説を映画化したもので、マスコミの軽薄さと残忍性を鋭く告発した問題作。仮装舞踏会で出会った青年と一夜をともにしたカタリーナは、翌朝、部屋に踏み込んできた武装警官隊によって彼が過激派の銀行強盗であったことを知り、取り調べを受ける。彼はすでに姿をくらましていたが、新聞は取り調べの内容だけでなく、彼女の前夫や病床の母親らにも強引に取材をしてデッチあげの中傷記事を掲げたため、彼女は世間から非難を浴び、母親は取材記者の影響で死に至る。激怒した彼女はその記者を射殺してしまう。記者の葬儀が、“尊い殉職者”として盛大に行われるラスト・シーンが重々しく観る者の胸を打つ。", "1975-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VALLEY OF THE KINGS", "王家の谷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "エジプト・ラホテップ王の墓を探すために夫のフィリップとともにカイロに赴いたアンは、アメリカの考古学者マークを加えて調査を開始する。しかし、フィリップの裏切りによって、残された二人は窮地に陥ってしまう。C・W・セラムの『神、墓、学者』の歴史的記述にヒントを得て製作した冒険映画。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四季・奈津子", "四季・奈津子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "博多郊外のとある一家の４人姉妹、波留子・奈津子・亜紀子・布由子。それぞれの生き方を横糸に、陽気な次女・奈津子がOL生活を捨てて上京しモデルになるという小波乱を縦糸に綴る。五木寛之のベストセラー小説を原作に、新世代のふわりと漂うような生活実感を捉えた、東監督得意の新感覚映画。脚本を使わずダイアローグ・ライターの粕谷日出美がセリフを手掛けている。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les enfants terribles", "恐るべき子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "J・コクトーが1929年に発表した小説を、「いぬ」「サムライ」など、後のフィルム・ノワールの名作を作り上げたJ・P・メルヴィルが映画化。彼は1700万フランを投じて完成させたこの作品により、一躍戦後フランスを代表する映画監督にのし上がった。エリザベートとポールは、二人だけの世界を持つ、強い愛情で結ばれた姉弟。この二人の間に、ジェラールという少年が入り込む。３人の関係はやがてモデルの仕事を始めたエリザベートのモデル仲間、アガトの登場で複雑化する。姉弟以外の異性に目を向けたエリザベートとポールは、知らぬ間に二人だけの世界を崩壊させていった……。ナレーションを、原作者コクトーが担当している。", "1949-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dead", "ザ・デッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "　1904年、雪の降るアイルランド・ダブリン。叔母姉妹が毎年催している舞踏会に訪れるガブリエルと彼の妻グレタ。たくさんの客が集まり、ダンスと音楽、酒と食事の宴は和やかに進む。やがてパーティはお開きとなったが、ホテルに戻ったガブリエルはグレタから意外な告白をされる……。87年に他界したジョン・ヒューストン監督の遺作。一時期アイルランドに暮らし、その地をこよなく愛したヒューストンがジョイスの短編集の一編を映画化。", "1987-01-01", "3,33,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺人ネット", "殺人ネット", "", "", "http://ecx.images-amazon.com/images/I/51EGJK1FV3L._SL210_.jpg", 90, "TVやグラビアで活躍する、新人アイドルが主演した戦慄のホラー。ある女子高生に突然届いた“殺人ネット”なるチェーン・メール。それは、名前を入れてその相手に送ると殺すことができる、という恐ろしいものだった。同級生や教師を巻き込んで猛威を振るう殺人メールの恐怖から、抜け出す術はあるのか!?", "2004-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Transformers: Dark of the Moon", "トランスフォーマー　ダークサイド・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/55153/315x210.jpg?1825", 154, "日本の人気玩具をもとに、マイケル・ベイ監督が実写化したSFアクションシリーズ第3弾。1969年7月20日、アポロ11号による人類初の月面着陸により、月の裏側に金属生命体＝トランスフォーマーの宇宙船が不時着していたことが発見された。NASAとアメリカ政府により長らく隠されていたその事実から、トランスフォーマーをめぐるナゾが明らかにされていく。シャイア・ラブーフらメインキャストが続投するなか、ヒロインは前2作のミーガン・フォックス演じるミカエラから、モデル出身の新鋭ロージー・ハンティントン＝ホワイトレイが演じる新キャラ、カーリーに交代。", "2011-07-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KID MILLIONS", "百万ドル小僧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "エスニック感覚あふれる情緒豊かなミュージカル・コメディ。ルドルフ・ヴァレンチノが出演した、一連の中近東ムード漂うアドベンチャー映画を彷彿とさせる。貧しい青年が、酋長の娘を救ったことで財産を相続し、一躍百万長者となる。それをねたむ人々からの数々のいやがらせもかわし、彼は幸運を手にしていく。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獨立時代", "エドワード・ヤンの恋愛時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "目覚しい経済発展を遂げた90年代の台湾。首都台北で生きる若者達が織りなす様々な人間模様を2日半という時間の中に凝縮し、時には可笑しく時には冷静な目で描いた青春群像劇。裕福なエリート階級に属しながら、空虚感漂う若者たちの乾いた心の葛藤を、スタイリッシュな映像感覚で見せる。", "1994-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("めぞん一刻", "めぞん一刻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "高橋留美子の同名人気漫画の映画化。時計塔のある木造アパート・一刻館には詮索好きの一の瀬夫人、のぞき趣味の四谷、スナックに勤める朱美、浪人生の五代が住んでいる。そこへ若くて美しい女性管理人がやって来て、ドタバタが始まる……。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAYBACK", "900万ドルの脱獄囚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "コカインの運搬・売買で刑務所に収容されていた主人公は見事、脱獄に成功するが、つまらない争いから再び警察に捕まる。彼が麻薬の運搬人だと気付いた保安官は“取引”を申し出るのだが……。カリフォルニアの田舎町を舞台に、格闘、カーチェイス、銃撃戦などアクション満載で描く、スタントマン出身ユーバンクスのデビュー作。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ГОРОД ЗЕРО", "ゼロシティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "モスクワから田舎町にやって来た男が体験する出来事をシュールなタッチで描く。とある田舎町に派遣された技師バラーモン。彼は、レストランで生首をかたどったケーキを出され、そのうえコックが自殺するのを目撃する。彼は、コック殺しの犯人として警察に捕まってしまうが……。混乱したソ連の国情を物語るような、整合性のない異常な世界を映し出した異色作。", "1990-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RETURN OF THE PINK PANTHER", "ピンク・パンサー２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「ピンクの豹」「暗闇でドッキリ」から12年ぶりに作られた続編で、爆笑刑事コメディ第３作。中近東のルガシュに事件発生。博物館で宝石が盗まれたのだ。さっそくクルーゾー警部が馳せ参じ、勇んで捜査を始めるが、ドジの連続！　ドレフュス署長は頭に血がのぼり……。セラーズvsロムの珍顔合わせが絶妙。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON　THE　OTHER　SIDE　OF　THE　BRIDGE", "愛にかける橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "中国人男性と恋に落ち、オーストリアから中国へ渡って激動の日々を過ごした白人女性の実話を基に描く大河ロマン。中国人社会で妻としての厳しい現実に耐える主人公に、日中戦争、文化大革命といった時代の荒波がさらに襲い掛かる。歴史にほんろうされながらも愛を貫いた女性の姿を、３年がかりで書き上げられた脚本を基に女流監督のH・メイが見事に映画化した。1931年、ウィーンの警察学校に留学した中国人青年マーは、教官の17歳の娘ファニーと恋に落ちる。だが、彼女の父が二人の結婚を反対。マーが帰国してから一年後、ファニーは単身中国へと渡るが、様々な試練に遭い……。", "2002-01-01", "8,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忘れるものか", "忘れるものか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "不可解な死を遂げたとされる親友の謎に迫る男を石原裕次郎が演じたサスペンス・アクション。親友の津村が死んだと聞かされた岡部は、京都に戻り調査を開始する。津村と敵対関係にあった暴力団・尾形一派を怪しいとにらんでいた彼は、やがて昔なじみの新聞記者から容疑者を教えられるが……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE PLUS ONE", "ワン・プラス・ワン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "", "1978-11-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUBIDA AL CIELO", "昇天峠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "死期の間近い母親の願いを聞いて、オリヴェリオは腹黒い兄たちに遺産を渡さないための遺言状を作るために出発する。ひどい田舎で、町までの唯一の交通機関はオンボロバス。大急ぎのオリヴェリオだが、はやる心に逆らうように想像を越えた事件が起こる。走り出すやいなやバスはパンク。ようやく修理して峠に向かうと天候は一変して霧がたちこめ、今まで一台としてなかった対向車が現れ行く手をふさぐ。さらに妊婦が産気づき、ようやく出産が終わって、走り出すとバスが川にはまってしまう。乗りあわせた男好きの新妻ラケルに誘惑されたオリヴェリオはあらぬ妄想にふけるうち今度は運転手が眠りたいと言いだす。代わって運転するオリヴェリオだが、ひと眠りした運転手は実家に全員を連れて立ち寄ると言いだす。すっかり苛立ったオリヴェリオはバスを借りようとするが……。奇想天外なトラブルが続くうち、悪夢のような不思議な世界が生まれてくるメキシコ時代のブニュエルの傑作。", "1951-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONLY YOU", "オンリー・ユー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ロマンティックなラブ・ストーリーを得意とするA・マッカーシー主演作。クリスマス・イヴにガールフレンドにフラれたクリスは、同じく男にフラれたアマンダと意気投合、常夏のリビエラへ向かった。ところが、アマンダは、とんでもない生意気女。そこへ、もう一人の美女クレアが現れて……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋する日曜日　私。恋した", "恋する日曜日　私。恋した", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "BS-iの人気ドラマシリーズ「恋する日曜日」の劇場版第2弾。喜納昌吉＆チャンプルーズの名曲「花～すべての人の心に花を」をテーマに、余命わずかな女子高生の旅を繊細なタッチで綴る。母をガンで亡くし、自らも余命3カ月を宣告された17歳のなぎさは、初恋の人・聡が暮らす海辺の町を訪れる。しかし聡は人妻との恋に溺れており……。第1弾に続き「やわらかい生活」の廣木隆一監督がメガホンを取る。人気若手女優の堀北真希がヒロインを好演。", "2007-06-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南の島のフリムン", "南の島のフリムン", "", "", "http://image.eiga.k-img.com/images/movie/54673/315x210.jpg?4166", 97, "お笑いコンビ「ガレッジセール」のゴリが監督＆主演を務め、故郷の沖縄を舞台に描くラブコメディ。養豚場で働く栄昇は、ある日、ポールダンサーのオレンジに一目ぼれしてしまう。しかしそこに横暴な米兵マックスが現れ、オレンジを賭けて決闘することになってしまう。謎の占い師や妖怪キジムーナなど、沖縄独特のキャラクターが多数登場。「フリムン」とは沖縄の方言で「愛すべきおバカさん」の意。", "2009-08-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("＋1 プラスワン Vol.3", "＋1 プラスワン Vol.3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "映画監督が提示した物語のラフスケッチをもとに、プロアマ問わずワークショップで集まった俳優たちが参加して作られるインディーズ短編映画集の第3弾。「時の香り　リメンバー・ミー」の山川直人監督、「非・バランス」の冨樫森監督、「月とキャベツ」の篠原哲雄監督、「ノン子36歳（家事手伝い）」の熊切和嘉監督が手がけた3編を上映。", "2010-07-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダンシング・チャップリン", "ダンシング・チャップリン", "", "", "http://image.eiga.k-img.com/images/movie/55913/315x210.jpg?4379", 131, "「Shall we ダンス？」「それでもボクはやってない」の周防正行監督が、フランスの名振付師ローラン・プティによるチャールズ・チャップリンを題材としたバレエ「ダンシング・チャップリン」の舞台を映画化。チャップリン役を務めるルイジ・ボニーノや日本の草刈民代ら、世界のトップダンサーが舞台に臨むまでの60日間を追った「アプローチ」と、本番の演目を収録した「バレエ」の2幕構成。", "2011-04-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LOOKING GLASS WAR", "鏡の国の戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "東西両陣営のスパイ戦の内幕をリアルに描き出したサスペンス映画の佳作。イギリス情報部によってスパイに仕立てられたポーランド人C・ジョーンズは、東ベルリンに潜入、新型ミサイルのデータを盗み出そうとするのだが……。原作はジョン・ル・カレの同名小説。DVDは「コロムビアトライスター ウォー・ムービー・コレクションBOX(2)」に収録。", "1969-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIGNORE　E　SIGNORI", "蜜がいっぱい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "イタリアを舞台に展開するオムニバスの艶笑喜劇。町いちばんの絶倫男に妻を誘惑された医師の一夜の奔走、妻子ある男が不倫の果てに町中を敵にまわしてしまう受難、愛娘に手を出したと商店主らを訴えるものの買収され逆に罪を問われる男の悲喜劇が描かれる。ジェルミ監督の軽妙な演出がさえ、カンヌ映画祭では「男と女」とグランプリを分けあった。", "1965-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い十人の女", "黒い十人の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "妻がありながら９人もの女と付き合っているTVプロデューサーの風松吉。そのせいで女たちの間ではいさかいが絶えず、やがて女たちの共謀によって風は射殺される。しかし、それは風と彼の妻が打った芝居だった……。御大、市川崑がミステリー趣味を感じさせる愛憎劇を、スタイリッシュなモノクロ映像で描いた異色作。愛人が９人という過剰さからかもし出される“笑い”、そして不吉な暗示でしめくくるラストまで巨匠のオフビートな映像感覚が楽しめる、不思議な味わいの作品だ。また、社会機構の歯車と化してしまう現状を、意志のない主人公を通して痛烈に風刺している点も見逃せない。若々しくキュートな女優を演じる岸恵子をはじめ、豪華女優陣の競演が乾いたドラマに華やかな印象を与えている。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOC", "ドク・ホリディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「荒野の決闘」など、何度も映画のなかで描かれているアメリカ西部の伝説的人物、ドク・ホリディとワイアット・アープの姿を忠実に描いた一編。トゥームストーンを牛耳り甘い汁を吸おうと考えているワイアット・アープは、ドクを呼び寄せ、保安官選挙を有利に進めようとするが、アープのことを快く思っていないアイク・クライトンとの戦いへと発展していく……。脚本を『ブルックリン物語』などで知られるコラムニスト、P・ハミルが書いている。アメリカン・ニューシネマの影響を受けたストーリーや構図など、ステレオタイプの西部劇とは一線を画した、異色のウエスタンだ。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿飛正傳／DAYS OF BEING WILD", "欲望の翼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1960年の香港。ある日、サッカー競技場の売店の売り子スーは、奔放で不思議な魅力を持つ青年ヨディと出会う。ヨディには同棲している恋人ミミがいるのだが、スーはそれでも彼のそばにいたいと願う。ヨディへの恋の失望でさまようスーを介抱する警官はスーを愛し始め……。若者たちの出会い、孤独、愛、そして別れをドラマティックに盛り上げるわけでもなく、スケッチ風に距離をおいて描くでもなく、文学的ともいうべき独特の語り口と構成で綴っていくまったく新しいタイプの青春映画。香港映画界の若手オールスター・キャストを得たこの作品で、W・カーウァイは一躍世界が注目する監督になった。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小さい逃亡者", "小さい逃亡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "10歳の川間健は孤児だったが、ある日叔父から健の父がモスクワに生存していると聞かされる。ボリショイサーカス団が来日した時、健は京都まで追いかけていき、ニクーリン夫妻にモスクワへ連れていってくれと頼むが、ちょっとした行き違いから、それはかなわなかった。仕方なく健はソ連船に忍び込んだ……。日本映画初の日ソ合作である。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Newcastle", "ブルー・ブルー・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/53443/315x210.jpg?2547", 107, "オーストラリアの港町で暮らす17歳のジェシー。サーフィン大会で優勝して町を飛び出すことを夢見ていた彼は、大会の予選でまさかの敗退を喫してしまう。ジェシーの仲間たちは、人生で初の挫折を味わった彼を励まそうとサーフ・トリップに連れ出すが……。サーフィンに熱中する若者たちの恋と友情を爽やかに描き出した青春ドラマ。サーフィンの醍醐味をたっぷり詰め込んだ迫力満点のライディング・シーンに注目。", "2008-06-07", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎忘れな草", "男はつらいよ・寅次郎忘れな草", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "マドンナとして通算４度の出演回数を誇る浅丘ルリ子を初めて迎えた一編。悪気はないのに法事をめちゃくちゃにしてしまった寅さんは、またまた周りの人々とケンカして北海道へ。網走の安キャバレーの歌姫・リリーと意気投合するが……。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paul", "宇宙人ポール", "", "", "http://image.eiga.k-img.com/images/movie/56163/315x210.jpg?2447", 104, "「ショーン・オブ・ザ・デッド」「ホット・ファズ　俺たちスーパーポリスメン！」のサイモン・ペッグとニック・フロストが主演・脚本を務め、「未知との遭遇」「E.T.」など名作SFへのオマージュを散りばめながら、陽気な宇宙人のポールと冴えない青年コンビの珍道中を描くコメディ。SFオタクのイギリス人青年クライブとグレアムは、全米最大のコミックイベント、コミコンと米中西部のUFOスポットをめぐる旅を楽しんでいた。その途中、ネバダ州のエリア51を通りかかった2人は、ポールと名乗る本物の宇宙人と遭遇。ポールを故郷に帰すため奮闘することになる。", "2011-12-23", "1,2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARTY", "マーティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ニューヨーク・ブロンクスで肉屋に勤めているマーティは、誠実な男なのだが醜男だという一点が気になって、いまだに母親と二人暮らし。ある日ダンス・パーティーに行った彼は、そこで同じく容姿に自信を持てず独身でいる女教師クララと知り合う。二人は翌日また電話で話すことを約束し、別れるのだが……。カンヌ映画祭グランプリを受賞した、D・マンのデビュー作。庶民的なロマンスを温かい視点で描く。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白い巨塔", "白い巨塔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "医学界の腐敗にメスを入れた山崎豊子の同名小説を山本薩夫が映像化した力作。大阪・浪速大医学部は東教授の定年を控え、その後任選挙に関心が集中していた。財前助教授は候補者の一人だが、東教授は彼を嫌い、東都大学の船尾教授の助言で、菊川教授を対抗馬に仕立て上げる。映画の前半は、この二つの陣営の金力、組織力にものを言わせた策謀が巧みに描かれる。後半は東教授のあと釜として教授の座についた財前が、誤診問題で訴えられ、今度は反対派の教授も医学界の名誉のため、財前を擁護するさまがスリリングに展開。モスクワ映画祭で銀賞を受賞した。のちに２度TVドラマ化され、共に高視聴率を挙げた。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Napapiirin sankarit", "ラップランド・オデッセイ", "", "", "http://image.eiga.k-img.com/images/movie/57253/315x210.jpg?4019", 94, "3人の男たちが次々とトラブルに見舞われる中で成長していく姿を描いたフィンランド版コミカルロードムービー。失業中のヤンネは、恋人イナリからデジタルチューナーを買うために渡されたお金を酒代に使ってしまう。怒ったイナリに「翌朝までにデジタルチューナーを買ってこなければ別れる」と宣告され、チューナーを買うために2人の仲間とともに200キロ離れた街へ向けて出発するが、その道中で次々と災難に襲われてしまう。最新のフィンランド映画5作品を一挙上映する「フィンランド映画祭2011」にて上映。", "2011-10-02", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Baaria", "シチリア！シチリア！", "", "", "http://image.eiga.k-img.com/images/movie/54993/315x210.jpg?3046", 151, "「ニュー・シネマ・パラダイス」の名匠ジュゼッペ・トルナトーレ監督が、自身の生まれ育ったシチリアの村バゲーリアを舞台にした自伝的作品。トルナトーレの父親をモデルにした主人公ペッピーノとその父親、息子という3世代の人生を通し、1930年代から1980年代へと至るシチリアの激動の現代史を描き出す。音楽にはトルナトーレ作品には欠かせない存在となったエンニオ・モリコーネ。", "2009-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦争と人間 第三部・完結編", "戦争と人間 第三部・完結編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 189, "標耕平は順子とひそかに結婚式を挙げ、大陸の戦場へ向かった。関東軍の方針を批判した俊介は、ソ満国境の最前線に投入される。ノモンハンの戦闘では、ソ連軍の圧倒的な物量の前に、日本軍は惨敗した。その頃、戦場から逃亡した耕平は、抗日運動に加わっていた。ビデオは前篇と後篇がある。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOM SAWYER", "トム・ソーヤの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "M・トウェインの高名な児童文学のミュージカル映画化。トム・ソーヤと親友の孤児ハックルベリー・フィンは、夜の墓地で殺人を目撃する。インディアン・ジョーが医者を殺し、のんだくれのマフにナイフを握らせて罪をなすりつけていたのだ。マフのために真相をしゃべったトムは、ジョーから命を狙われ……。名門出版社リーダーズ・ダイジェストがユナイトと提携して製作した。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPRING　IN　HOMETOWN", "スプリング・イン・ホームタウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "韓国のイ・グァンモ監督が世界各地の映画祭で絶賛された人間ドラマ。1998年の東京国際映画祭では「故郷の春」というタイトルで上映され、東京ゴールド賞に輝いた。朝鮮戦争の暗い影が田舎の村にまで忍び寄る1952年の韓国。好奇心旺盛だが内気な少年ソンミンを主人公に、戦争によって引き裂かれる人間の絆をじっくりと見据えていく。ロングショットの長回しを基調とした端正な映像美が、歴史の悲劇を静かに訴えかけてくる作品だ。", "1998-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JULIA AND JULIA", "ジュリア・ジュリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "幸せな結婚式を挙げ、ハネムーンに向かう途中、自動車事故に遭い夫を失ったジュリア。６年前の忌まわしい記憶もまだ消えぬある日、彼女は奇妙な体験をする。ドライブ中に急に発生した霧の中をくぐり抜けたところでタイム・トリップしてしまったのである。そこには死んだはずの夫と可愛い子供との生活があり、一方では彼女の愛人だと名乗る男が一家３人の幸福を破壊しようとしていた。二人の男の間で揺れるジュリアだが結局夫や子供とのしあわせな生活を望み、足手まといになった愛人を殺そうとする……。イタリアの幻想映画の旗手デル・モンテが、ハイビジョン・システムを導入して撮影したサスペンス・スリラー。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Chronicles of Narnia: The Voyage of the Dawn Treader", "ナルニア国物語　第3章：アスラン王と魔法の島", "", "", "http://image.eiga.k-img.com/images/movie/53723/315x210.jpg?3628", 112, "C・S・ルイス原作の大ヒットファンタジーシリーズ第3弾。監督は前2作のアンドリュー・アダムソンからマイケル・アプテッド（「ネル」「007 ワールド・イズ・ノット・イナフ」）にバトンタッチ。原作は「朝びらき丸 東の海へ」。後に“航海王”と呼ばれるようになるカスピアン王子が、再びナルニアにやってきたルーシーとエドマンド、そして彼らの従兄弟ユースチスと共に東の海を目指して航海に出るというもの。前作「カスピアン王子の角笛」で抜擢されたベン・バーンズが再度カスピアンを演じる。", "2011-02-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪竜大決戦", "怪竜大決戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "家老、結城大乗によって、父である近江城主、尾形左馬亮を殺された雷丸。飛騨の国に逃げた雷丸は、そこで蟇道人と出会い忍術を仕込まれた。ところがかつての弟子であり雷丸の父を殺した大蛇丸によって、蟇道人も殺害されてしまう。父と蟇道人の仇を討つべく、自雷也と名乗り近江へと雷丸は旅立った。当時の怪獣ブームにあやかった東映が、独自の味つけで描いた時代劇の怪獣バージョン。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SHATTERER", "シャタラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "日・伊合弁の自動車会社で、マフィアによる連続殺人事件が発生した。レーサーの木田光一は、ナビゲーターが殺されたことから、危険を顧みず復讐を誓い、その渦中に身を投じていく。吉川晃司主演によるハードボイルド映画。", "1987-01-01", "4,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おはなはん・第一部／第二部", "おはなはん・第一部／第二部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "勝気な女・はなが、明治・大正・昭和と時代の波にのまれながら、はつらつと生きていく姿を描いた人気TVドラマを映画化した作品。原作は実話にもとづいた小野田勇・林謙一の『おはなはん一代記』で、山田洋次が脚色している。TV人気のあおりで大ヒットした。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バカヤロー！４ YOU！お前のことだよ", "バカヤロー！４ YOU！お前のことだよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "オムニバス喜劇の第４作。前３作はともに４話形式だったが、今回は３話にして内容の充実を図った。ペンションオーナーの悪行を描く「泊ったら最後」、潔癖症の男とその妻の悲喜劇を描く「カラダだけの男」、ユン・ピョウ扮する中国人留学生が美女にメロメロにされてしまう「サギるなジャパン」と、それぞれ主人公が怒りを爆発させるまでのシチュエーションをコミカルに綴る。", "1991-09-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD ABOUT MUSIC", "アヴェ・マリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「オーケストラの少女」でアイドル的な人気を博したD・ダービンの可憐さが印象的な音楽映画。スイスの学校に入学した14歳の少女が、“理想の父親”というべきイギリス人指揮者とめぐり合うが、思わぬ事態に巻き込まれていく。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOLLOW GATE", "マッド・マーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "暴力的な父親によってゆがんだ幼年時代を送ってきた青年が、ハロウィンの夜に恐ろしい殺人鬼となって復讐を繰り返していく……。殺人鬼マークが登場、しかもハロウィンの夜が舞台となったホラー映画。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("化石", "化石", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 200, "ヨーロッパ周遊旅行に出かけた建設会社の社長が、自分がガンであり、あと１年あまりしか生きられないということを知る。そんな時彼は、フランスでのパーティーの席上、フランス人と結婚しているマルセラン夫人を知り、彼女のなかに自分が心の中に描く理想の女性像を見いだすのだが……。死と対決し、自問自答を繰り返す男の前にマルセラン夫人が幻想となって現れる。ガンを宣告され、死の近いことが確実と知って動揺する男の心象風景を見事に映像化した作品。TVで放映されたシリーズものを再編集、ロケ時に別に撮ったフィルムも使用し、映画用に十分考慮されている。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Black and White", "ブラック AND ホワイト", "", "", "http://ecx.images-amazon.com/images/I/51EC50P4BAL._SL210_.jpg", 99, "", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPAWN ： THE MOVIE", "スポーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1992年の発刊以来、世界中でベストセラーを記録しているアメリカン・コミックを映像化。CIAのアルは上官ウィンの罠にはまり爆死。地獄に落ちた彼は、魔界の支配者マレボルギアと取引し、魔界の軍隊を率いる指揮官“スポーン”となって現世へ戻る。だが、最愛の妻はすでに再婚していた。怒りを覚えたアルは、スポーンとしての特殊能力を駆使してマレボルギアとウィンへの復讐に乗り出す……。原作のダークな味わいを抑え、冒険アクションとしての要素を打ち出し、ファミリー層にアピールする作品となっている。スポーンの変身シーンや灼熱の地獄のヴィジュアルなど、SFXの見せ場も盛りだくさん。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("砂の上のロビンソン", "砂の上のロビンソン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "１年間、理想的家族としてモデルハウスで生活すれば、その家が自分たちの物になるという不動産会社のキャンペーンに乗ってモニターとなったサラリーマン家庭が、衆人の目にさらされ、マスコミに利用されて、異常な事態に巻き込まれていくという悲喜劇。「マルサの女」などで脇役として異彩を放っていた大地康雄の本格的初主演作。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pelican Brief", "ペリカン文書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "法学部の学生ダービー（ジュリア・ロバーツ）が最高裁判事の殺人事件を巡って大胆な仮説を打ち立てた。そのレポートがワシントンへ持ち込まれた時から、彼女の身に危険が迫る。仮説は一部真実を突いていたのだ。命を狙われる彼女はジャーナリスト（デンゼル・ワシントン）の協力を得て、事件の核心に迫るが……。ジョン・グリシャムの同名小説を映画化したサスペンス。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊波拉病毒／EBOLA　SYNDROME", "エボラ・シンドローム　悪魔の殺人ウィルス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「八仙飯店人肉饅頭」のスタッフ＆キャストによる、狂気とバイオレンスが炸裂する“極悪”パニック作品。香港で殺人を犯した後アフリカの某国に潜伏していた男が、エボラ出血熱で瀕死の女性をレイプ。ウイルスに感染したはずなのだが、男は危険な媒介として生き延び無軌道な行動に走っていく……。悪趣味な残虐シーンの連続が、観る者をボー然とさせる“世紀末”な１本。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("皆殺しのスキャット", "皆殺しのスキャット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "シカゴ仕込みのすご腕ガンマン・伊集院猛は、帰国の喜びもつかのま、父親を射殺されてしまう。犯行は敵対するヤクザ、佃興業の仕業とにらみ、証拠を求めて暴れまわる伊集院の前に、ウエスタン・ルックの殺し屋・シャチが現れる。バタ臭さが身上の異色ガン・ファイト・アクション。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A TIME TO DIE", "クライム TO ダイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "元ポルノ女優のT・ローズ主演のアクション映画。ジャッキーは、イメージアップを図る警察に雇われ、スクープカメラマンとして働いていたが、ある時、悪徳刑事の殺人現場を目撃してしまう。それは、昔ジャッキーにコカイン不法所持の濡れ衣を着せた刑事だった……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネ響「マエストロ6」　リッカルド・ムーティ　ベルリン・フィルハーモニー管弦楽団", "シネ響「マエストロ6」　リッカルド・ムーティ　ベルリン・フィルハーモニー管弦楽団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "映画館でクラシック音楽のコンサートを上映する「シネ響」の3作目。ベルリン・フィルが設立記念日の5月1日に毎年行うヨーロッパ・コンサートの2009年公演の模様を収録。欧州最古と言われるナポリのサン・カルロ劇場で開かれ、ナポリ出身の指揮者リッカルド・ムーティがタクトを振った。曲目はナポリに縁のあるジュゼッペ・ベルディのオペラ「運命の力」の序曲など。", "2010-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無頼・黒匕首", "無頼・黒匕首", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "藤田五郎は、義理のために、暴力団同士の抗争に巻き込まれ恋人・由利を死なせてしまう。数日後、先輩・三浦を訪ねた五郎は、彼の会社で働くことになるが、暴力団組織の魔手はここにも伸びていた……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霜花店", "霜花店（サンファジョム） 　運命、その愛", "", "", "http://image.eiga.k-img.com/images/movie/54843/315x210.jpg?2358", 133, "「マルチュク青春通り」のユ・ハがメガホンを取り、観客動員400万人以上の大ヒットを記録した歴史ロマン。後継者問題に揺れる高麗王朝。強大な権力を持つ元からの圧力が増していく中、王は子供の頃から信頼を寄せてきた護衛隊長ホンリムに、王妃との間に子をなすよう命令を下す。その時から、3人の運命は大きく狂いはじめ……。主演は「卑劣な街」のチョ・インソン、「カンナさん大成功です！」のチュ・ジンモ、ドラマ「朱蒙」のソン・ジヒョ。", "2010-02-06", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四畳半革命　白夜に死す", "四畳半革命　白夜に死す", "", "", "http://image.eiga.k-img.com/images/movie/53653/315x210.jpg?4321", 0, "激動の70年代を舞台に、学生運動に身を投じた若者たちの姿を描いた青春バイオレンス。学生グループに属しながら、思想も持たず暴力だけに自身の存在価値を見出していた直也。グループのリーダー、陽介の下で暴れまわっていた彼は、ある日誤って殺人を犯してしまう。その場を逃れた彼を、売春バーに住む娼婦・アッコが救い……。これまで舞台の演出家や俳優など演劇界で活躍し、短編映画の監督としても注目を集めた世志男（せしお）の長編監督デビュー作。", "2008-06-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAWS OF DECEPTION", "侵入者3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "若き一流弁護士がナゾめいた美女によって危険なワナに陥るバイオレンス・スリラー。両親をマフィアに殺害されたエヴァンは、弁護士になることを決意。勉学に励んでいたある日、美女エリーズに誘われて、情事に溺れるが彼女は突然姿を消す。それから５年後、一流弁護士になった彼の元に、エリーズが殺人の容疑がかかった夫の弁護を依頼しに来る。調査を開始したエヴァンは、彼女の本当の目的と両親殺害の恐るべき真相を突き止める。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SPIKES GANG", "スパイクス・ギャング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "さえない毎日を過ごしていた西部の百姓一家の３兄弟は、ある日、傷ついたアウトローを助ける。刺激的な毎日を夢みて、彼のもとで働くようになったものの、常に追っ手に追われる彼らにつきまとうのは、“死”の影だけだったという異色ウエスタン。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOWN AWAY", "クロス・ヒート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "C・ハイム、C・フェルドマンの二人のコリー４度目の共演作。18歳のリッチは兄とともに高級リゾート・クラブで働いている。強大な力を持つ謎の経営者、バウアーには17歳になるメガンという美しい娘がいた。メガンに恋心を抱くリッチであったが、実は彼女は欲しいものを手に入れるためには、どんなことでもやってのける魔性の女だった。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILVER STRAND", "ネイビーパッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "海軍エリート兵の妻たちのひそかな楽しみは、新兵との不倫。大佐夫人のミッシェルは、そんな火遊びには消極的だったが、生意気で向こう見ずな新兵ブライアンに出会い一変する。二人は密会を重ねるが、ブライアンを嫌っている上司に関係を知られてしまい……。元トップモデルのN・シェリダンが、妖艶な人妻を演じている。「アンドレ」のスコットランド出身監督J・ミラーと、「愛と青春の旅立ち」の脚本家D・D・スチュアートが組んだTVフィーチャー。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE LINE FEVER", "爆走トラック'76", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ベトナム帰りの若者が、一匹狼のトラック野郎として仕事を始めるが、巨大企業のいやがらせで次々と妨害に遭う。家に火をつけられ、妻は流産。怒りが頂点に達した若者は、トラックごと殴り込みをかけた。任侠映画パターンのカー・アクションもの。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEST OF MARTIAL ARTS", "ストロンゲスト 史上最強の映画スターは誰だ!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "様々な映画の格闘技の名場面を軸に、世界から選りすぐられたスターたちと、C・ラスロックら全米マーシャルアーツ・チャンピオンの談話を交互に構成していく、マーシャルアーツの歴史と現在。ジャッキー・チェンら、ゴールデン・ハーベストのドル箱スターはもちろん、J・C・ヴァン・ダムの初期の作品なども見られる。", "1990-01-01", "1,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL VIAJE", "ラテンアメリカ 光と影の詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "氷に閉ざされた最果ての町、アルゼンチン領ウスワイヤに住む16才の少年マルティン。規則だけが横行する廃虚同然の学校での生活や、親友や恋人との別れによって、彼の心はすさんでいく一方。義父のいる家も居心地が悪い。母のヘレナは、危険な政治活動に身をさらす夫ニコラスとの生活に疲れ10年前に離婚した。その後、父は単身ペルーへ。母に対する反発心、実父への思慕、自分の将来への希望と不安……。マルティンは自転車に乗り、父を捜すためラテンアメリカ縦断５万キロの旅に出発する。名匠ソラナスが幻想的映像とアイロニカルな笑いの中に、混とんとした社会を風刺した秀作。", "1992-01-01", "22,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hellboy II: The Golden Army", "ヘルボーイ　ゴールデン・アーミー", "", "", "http://image.eiga.k-img.com/images/movie/53823/315x210.jpg?2329", 119, "「パンズ・ラビリンス」のギレルモ・デル・トロ監督が、同名コミックを映画化したシリーズ第2弾。地獄生まれだが心優しいヘルボーイ（通称：レッド）は、相棒のエイブ（通称：ブルー）や恋人のリズらと超常現象捜査局“BPRD”の凄腕エージェントとして、秘密裏に怪事件の捜査と魔物退治にあたっていた。一方、闇の世界のヌアダ王子は地上の人間を抹消するため、不滅の鋼鉄集団“ゴールデン・アーミー”を復活させようとしていた……。", "2009-01-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bulworth", "ブルワース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "W・ベイティの監督、主演による政治風刺ドラマ。1996年、米大統領予備選で、クリントン大統領の対立候補となったブルワースは、マスコミにも選挙民にも無視されて絶望。ギャングに自分を殺してくれと頼んだ彼は、途端にコワい者知らずとなり、選挙演説では今まで言えなかった過激な本音を連発。たちまちブルワースへの支持は集まるが……。ラップに乗せて政治の腐敗を訴える、主人公の毒舌ぶりが痛快。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖女伝説'88", "妖女伝説'88", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "新作ファミコンのソフトづくりに没頭する天才青年が、ある日、謎の女性からパソコン通信を受ける。青年は美形の彼女に好意を持つが、彼女はすでに亡く、それは霊界からのメッセージであった。にっかつロマンポルノ黄金期の一翼を担った田中登監督が、新たなジャンルに挑戦した純愛サスペンス。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOPAZ", "トパーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "ヒッチコックの51本目監督作となったスパイ映画。1962年、キューバ危機など緊迫化する東西両陣営の背後で暗躍するスパイの活動が描かれる。亡命者からの情報で自分の属するフランスの情報組織の中に二重スパイがいることを知った主人公の苦悩がサスペンス・タッチで表現されている。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三代の盃", "三代の盃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "江戸築地伊三郎一家の三下、政吉は一日も早く親分の盃をもらう日を夢見ている。そんな折、銀座の新興勢力、仁右衛門一家が、築地一帯に異人相手の歓楽街を作り、伊三郎一家を追い出すことを計画。殴り込みに行ったが子供扱いで追い返された政吉は旅に出、３年後、いっぱしの姿になり築地に戻って来た。勝新が暴れる任侠アクションの隠れた名作。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代仁侠伝", "現代仁侠伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ボクシングの世界タイトルマッチをはじめ、国際的イベントを取り仕切る実業家、古場。かつてはヤクザだった彼は罪を被って服役した弟分に恩義を感じ、資金を援助し続けていた。だが、彼は組織の内部抗争に巻き込まれて殺されてしまう。そして、抗争の火の粉は古場にも降りかかり始め、度重なる組織の横暴にたまりかねた彼は、二度と戻らないと誓っていたヤクザの世界に足を踏み入れていくことになる。自己犠牲を厭わない義侠心を持ちながらクールな言動を見せる主人公を、奥田瑛二が熱演。ベテラン、降旗康男が、裏社会に身を投じていく男の葛藤と情念を描いたハード・ロマンである。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／THE MURMURING", "ナヌムの家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "女流監督ビョン・ヨンジュが27歳の時に、つらい過去を持つ元従軍慰安婦の心のつぶやきに耳を傾けたドキュメンタリー。ソウル市内の“ナヌムの家”(分かち合いの家という意味)で仏教団体の支援を受けながら共同生活を送る６人のハルモニ(老婆)の日常を綴る。映画は日本政府の正式謝罪と補償を求めて毎週水曜日に日本大使館の前で行うデモから始まり、炊事や洗濯、おしゃべりの合間に彼女たちが戦後受けた差別や苦しみをつぶやくように語る姿を温かいまなざしで捉えていく……。16ミリのドキュメンタリー映画で、口コミで人気を集めた話題作だ。1995年山形国際ドキュメンタリー映画祭小川紳介賞受賞作品。", "1995-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("てんびんの詩", "てんびんの詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "近江商人の家に生まれ、その後継ぎである少年・大作を主人公に、小学校を卒業したばかりの彼が、四苦八苦しながらも行商を通じて大人に成長していく過程を描いた作品。その内容から、ビジネスマンの新入社員研修用フィルムなどとしても使われている。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上海暇期／MY　AMERICAN　GRANDSON", "上海の休日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "上海で一人暮らしをする老人が、アメリカにいる息子夫婦の子供を預かることになる。だが、スケートボードを抱え、英語しか話さないアメリカナイズされた少年と老人は折り合いが悪く、ついに少年は家を飛び出してしまう……。香港からアン・ホイ監督を招いて、全編上海で撮影された台湾映画。カルチャー・ギャップをメインに、老人と孫の確執と和解をじっくりと見せていく。", "1991-01-01", "53,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドルフィン・スルー", "ドルフィン・スルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "フランシス・F・コッポラの「アウトサイダー」にインスパイアされたという真木蔵人の原案をもとにしたホロ苦い青春ドラマ。若者たちのやり場のない怒りや焦燥感をリアルに映し出す。渋谷をうろつくチーマーの誠は、サーフィンが得意で仲間からも慕われている兄・悟を尊敬している。ところが、図らずも兄の恋人・安奈の死にかかわってしまう。兄に対する重すぎる秘密を抱え心を閉ざした誠は、親友の純平だけが頼りだった。そんなある日、敵対するチームのリーダーによって、純平が重傷を負い……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUNINATTEN", "六月の夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "銃で恋人に撃たれたカーリンは、傷心のまま町を出る。そして名前を変え、３人の女友だちとストックホルムでひっそりと暮らしていた。しかし彼女を撃った恋人が再びカーリンの前に現れる。だが彼女は、女友だちオーサの婚約者を愛し始めていた。バーグマンはこの作品で、激しく、情感豊かなヒロインを演じ、女優として新たな境地を開拓したといわれる。", "1940-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SHADOW", "シャドー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1930年代にラジオのスリラー番組で人気を博したキャラクター“シャドー”の活躍を映画化した、ヒロイック・ファンタジー。過去の罪を償うため、正義を守る特殊な能力を授けられたインコー。姿なきヒーロー、シャドーに変身した彼が、原爆で世界を滅ぼそうとするジンギス・カンの末裔と戦いを繰り広げる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED ROSE WHITE ROSE", "赤い薔薇 白い薔薇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "香港、台湾を中心に圧倒的な人気を誇るアイリーン・チャン・ライヤー原作の小説を映画化。対照的な二人の女性の間で揺れる男の姿を官能的な映像美で描いていく。1930年代の上海。英国の留学先から帰国したエリート青年のチェンパオは、友人の妻で奔放で誘惑的なチャオルイ(赤い薔薇)と出会い、火遊びを重ねていく。だが、彼女の愛が本物となるとチャンパオは、自分の将来への不安から彼女の元を去り、従順さと清楚さを持ったイエンリー(白い薔薇)と結婚する。後に仕事で成功を収めた彼は、おとなしい妻に飽き足らず浮気を重ねていたがある日、妻の不倫を目撃してしまう……。", "1994-01-01", "53,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Veuve de Saint-Pierre", "サン・ピエールの生命（いのち）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "フランス領カナダ、サン・ピエール島で、酔った勢いで殺人を犯し、死刑を宣告されたよそ者の漁師ニール。島にギロチンが届くまでの間、島に駐屯する軍隊長ジャンとその妻マダム・ラの手伝いをすることになった彼は、次第に島の人々に受け入れられていく。監督は「仕立て屋の恋 」「橋の上の娘」のパトリス・ルコント。ジャンと交流を深めていくマダム・ラを演じるのは「ショコラ」のジュリエット・ビノシュ。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE UGLY AMERICAN", "侵略", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "南北に分かれている東南アジアの王国サルカン。アメリカは南サルカンを助けるため援助資金を注ぎ込み、そのことを共産主義者たちはアメリカ帝国主義のアジア征服だと主張した。新任のアメリカ大使はサルカンの民衆の指導者ディオンとは旧友だったが、彼も共産党の一員であると結論せざるを得なくなる。日米俳優共演による社会派ドラマ。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("携帯彼氏", "携帯彼氏", "", "", "http://image.eiga.k-img.com/images/movie/54643/315x210.jpg?3553", 102, "「恋空」「赤い糸」といったヒット作を生んだ人気携帯サイト“魔法のiらんど”発の同名小説を、「Life　天国で君に逢えたら」の若手注目女優・川島海荷主演で映画化したサスペンス・ラブストーリー。ダウンロードした人が次々と死を遂げていく恋愛シミュレーションゲーム「携帯彼氏」の謎に挑む女子高生の恐怖を描く。メガホンを取るのは本作が劇場長編デビューとなる女性監督・船曳真珠。「リアル鬼ごっこ」の柴田一成が脚本を手掛ける。", "2009-10-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vertical Limit", "バーティカル・リミット", "", "", "http://image.eiga.k-img.com/images/movie/1733/315x210.jpg?4398", 124, "登山中の事故により目の前で父親を失った兄妹。その後、兄ピーターは山に入ることを拒み写真家に、妹アニーは父の夢を追い登山家の道を選ぶ。そして今、アニーは標高8000メートルで氷のクレバスに閉じ込められてしまった。命が持ちこたえられる時間は22時間。知らせを受けたピーターは、妹の命を救うためK2へ再び足を踏み入れることを決意する。しかし、そこは想像を絶する“死の領域”だった……。", "2000-12-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Una Pura formalità", "記憶の扉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "　「ニュー・シネマ・パラダイス」のジュゼッペ・トルナトーレ監督が手掛けたサスペンスムービー。封印していた男の過去が徐々に暴かれていく過程を、密室を舞台に描いている。作家のオノフは雨漏りがひどい取調室で、一晩中殺人事件の尋問を受ける。しかし、彼の記憶は欠落していた。「グリーンカード」「レ・ミゼラブル」のジェラール・ドパルデューと、「戦場のピアニスト」でアカデミー監督賞を受賞したロマン・ポランスキーの重厚な心理戦が見物。", "1994-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチ☆ボーイ", "ガチ☆ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/53293/315x210.jpg?4286", 120, "劇団モダンスイマーズの舞台を、「タイヨウのうた」の小泉徳宏監督＆「憑神」の佐藤隆太主演で映画化した青春コメディ。眠るとその日の出来事を全て忘れてしまう障害を負い、生きる実感を失いつつあった大学生の五十嵐。コント的要素の強い学生プロレスに興味を持った彼は、熱い想いを胸にプロレス研究会に入部する。いくら練習しても試合の段取りを覚えられない五十嵐は、デビュー戦でいきなりガチンコ勝負をしてしまい……。", "2008-03-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hidalgo", "オーシャン・オブ・ファイヤー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "「ロード・オブ・ザ・リング」のアラゴルン役ヴィゴ・モーテンセンが、アラビアの砂漠を横断する過酷な騎馬レースに優勝した実在のカウボーイを演じるアドベンチャー。そのレースに、アラブの族長たちの権力争いと、アメリカ先住民と白人との混血である主人公のアイデンティティ探しが絡んでいく。監督は「ジュラシック・パークIII」のジョー・ジョンストン。族長役で「アラビアのロレンス」のオマー・シャリフ、その娘役でTV「ローン・ガンメン」のズレイカ・ロビンソンが共演。", "2004-04-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("НЕСКОЛЬКО ДНЕЙ ИЗ ЖИЗНИ И.И.ОБЛОМОВА", "オブローモフの生涯より", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "食べることと寝ること以外にはまったく興味を示さない、無気力なぐうたら貴族オブローモフの生活を、精力的に働く幼なじみのシュートリツと対照させながら描いた作品。N・ミハルコフが、のどかな風景をバックに美しい画面で淡々と綴る。", "1979-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arzak Rhapsody", "メビウス　アルザック・ラプソディ", "", "", "http://ecx.images-amazon.com/images/I/51zTgfSFSWL._SL210_.jpg", 52, "バンド・デシネの巨匠メビウスが、自らのコミック「アルザック」シリーズをアニメ化。反重力翼手竜に乗り不思議な世界をさすらう孤高の戦士アルザックの旅を、1エピソード3分30秒で描く短編シリーズ。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cats & Dogs: The Revenge of Kitty Galore", "キャッツ＆ドッグス　地球最大の肉球大戦争", "", "", "http://image.eiga.k-img.com/images/movie/54393/315x210.jpg?3315", 82, "実写とアニメーションが融合するSFコメディ「キャッツ＆ドッグス」の続編。かつてネコのスパイ組織「ニヤオ」でエリートエージェントとして活躍していたキティ・ガロアは、宿敵の犬たちを降伏させるだけでなく、かつてのネコ仲間までも配下に置き世界征服を企んでいた。仲間と人類を救うため、イヌとネコはしぶしぶ力を合わせることになる。声優にクリスティナ・アップルゲイト、マイケル・クラーク・ダンカン、ジェームズ・マースデン、ニック・ノルティら豪華俳優陣が結集する。", "2010-08-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Heist", "ザ・プロフェッショナル", "", "", "http://image.eiga.k-img.com/images/movie/1603/315x210.jpg?3951", 107, "若い妻と長年の仲間と共に、いつも見事な仕事をこなすプロの強盗ジョー。彼は、自分の顔が割れてしまったことをきっかけに引退を決意する。そして、最後に選んだ獲物は、空輸寸前の金塊だった。「ザ・ロイヤル・テネンバウムズ」で再評価のハックマンが久々の主演、「ハンニバル」の脚色（途中交代）、「スパニッシュ・プリズナー」（監督・脚本）などを手がけたマメットの最新作。全米で公開され初登場第5位を記録した。なお原題は「泥棒」の意味。", "2002-06-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VOCE DELLA LUNA", "ボイス・オブ・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "E・カヴァツォーニの『狂人たちの詩』をF・フェリーニが映画化。だが一貫したストーリーを持つわけではなく、フェリーニ的なイマジネーションをつなぎ合わせたコミカル・ファンタジーとでも言うべき作品。主人公サルヴィーニは、時折、月からの“声”が聞こえる。この不確かな“声”を待つサルヴィーニの心のさすらい、恋する女へのかなわぬ思い、地上の人々の乱痴気騒ぎ、妄想癖のある元知事との出会いなどが綴られていく。フェリーニの遺作となった。", "1990-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SALEM'S LOT: THE MOVIE", "死霊伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ニューイングランドのセイラムズ・ロット（架空の町)。久しぶりに故郷に戻ってきた小説家を迎えたのは、窓の外に浮かんだ青白い顔の少年だった。以来、町には霧が立ち込め、次々と死者が発見されるのだった。真相を突き止めるべく彼は調査をするのだが、犯人は現代によみがえったバンパイアであった……。ホラー小説の巨匠S・キングの『呪われた町』の映画化。数あるキングの映画化されたもののなかでも最高峰との呼び声高い傑作ホラー。200分のTV用映画として作られたが、劇場用に再編集された。現在DVDで見られるのは、TV用のオリジナル・バージョンだ。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("БЕШКЕМПИР", "あの娘と自転車に乗って", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "中央アジア、キルギスタン共和国初めての長編映画。A・アブディカリコフ監督がデビュー作とは思えない確かな演出で、自分自身の少年時代をモチーフに示唆に富んだモノクロ青春劇を繰り広げている。あるとき、自分が養子であることを知った少年が体験していく未知なる動揺と震え。仲間たちと泥遊びに興ずる一方、女の子に恋情を覚えたり、性に目覚めたりと、思春期らしいエピソードが丹念に綴られていく。主人公の感情を体現する凝ったパートカラー映像も効果的だが、少年たちのケンカを捉えたシークエンスは特に見事。臨場感と叙情が一体化した名場面となっている。なお主演は、監督の息子。", "1998-01-01", "77,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ANNE´E DE L’E´VEIL", "めざめの時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "インドシナ戦争がフランス国内に暗い影を投げかけていた1948年。幼年士官学校に学ぶ14歳の少年フランソワは、上級生からのいじめに悩まされていた。強くなりたい彼は、元ボクサーの小隊長にボクシングを教えてもらうようになる。やがて小隊長の家にも出入りするようになったフランソワは、小隊長の妻でイタリア女性のレナに、初めての性の手ほどきを受ける……。自伝作家としてフランスで著名なC・ジュリエのベストセラーを、G・コルビオが、思春期のアンビバレントな思いをすくいとりながら手際よく映画化。レナを演じるC・カゼッリの存在感にも注目。", "1991-01-01", "2,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUN WITH DICK AND JANE", "おかしな泥棒 ディック＆ジェーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "2005年にジム・キャリー主演でリメイクされた犯罪コメディのオリジナル版。シリアスとコミカルを使い分けていた名女優J・フォンダが思いっきり弾けた、スラップスティックな演技を披露する。航空宇宙会社に勤務していたディックはある日突然、リストラされてしまう。妻ジェーンと相談した結果、金貸しの元へと向かうが、そこで強盗事件に紛れてジェーンが金を盗み……。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOOMERANG", "影なき殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "コネチカット州フェアポート。この静かな平和な町である日、殺人事件が発生。殺されたのは、町の人々の信頼と尊敬を集めるランバート神父。地元警察署や町民が騒然となるなか、二人の容疑者が浮かび上がり逮捕されるのだが……。「紳士協定」「エデンの東」などで知られる巨匠E・カザンが、実際に起こった事件をセミ・ドキュメンタリー・タッチで撮り上げた社会派ドラマの佳作。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Patty Hearst", "テロリズムの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「市民ケーン」のモデルとして有名な新聞王ウィリアム・ハーストの孫娘、パティがテロ組織によって誘拐、その後彼らの一員となったため逮捕されるが、大統領からの特赦を受けたという“パティ・ハースト事件”。今なお謎を残すこの事件を、「タクシードライバー」などの脚本で知られるP・シュレーダーが映画化した作品。拉致され、暗い一室で恐怖に震えるパティの不安を光と影のシルエットで描写し、後半のバイオレンス・シーンは、まるで「タクシードライバー」で変身したデ・ニーロのように、パティの激しい変貌を見事に表現している。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポップコーンLOVE", "ポップコーンLOVE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "不思議感覚の恋愛模様を描いた同名小説を映画化したラブ・ストーリー。キャピキャピの女子高生によってファンクラブを結成された平凡な青年がそれによって恋人と危機に陥る「ファンクラブの女・インディーズキッズ」。行方不明になった恋人と再会した青年が、コールガールのように冷たい表情の彼女にいらつきながらも惹かれていく姿を描いた「プライベートコールガール」という２部構成のオムニバス。監督の二人がともに相米組出身の新人であるのに注目。特に後者の一編は、渋谷ロケが大きな効果を上げている。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE DREIGROSCHENOPER", "三文オペラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "ブレヒト、ワイルのコンビによる有名な喜歌劇の映画化。ロンドンの悪名高い謎の男が乞食の王様の娘ポリーのハートをつかみ、結婚を申し込んだが、父親は大反対。一家のゴタゴタをよそに二人は結婚披露宴を強行するが、その席には男を逮捕しようと狙う警視総監の姿。ポリーの両親は男をワナにかけるが……。", "1963-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Freddy VS. Jason", "フレディVSジェイソン", "", "", "http://image.eiga.k-img.com/images/movie/1063/315x210.jpg?3972", 98, "84年の「エルム街の悪夢」から計7作で恐怖を与えてきたフレディ・クルーガーと、80年の「13日の金曜日」から計10作で殺害を続けてきたジェイソン・ボーヒーズ。フレディ役は全作通して演じてきたロバート・イングランド、ジェイソン役は89年の「13日の金曜日Part8／ジェイソンN.Y.へ」でスタント・コーディネーターを担当、俳優としても活動するケン・カージンガー。監督は「チャイルド・プレイ／チャッキーの花嫁」のロニー・ユー。", "2003-10-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOYLENT GREEN", "ソイレント・グリーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "西暦2022年。地球上には人間があふれ食糧不足と大気汚染に悩まされていた。折から、ソイレント・グリーンという新しい食糧が生産されるが、それは人間の死体を加工して作った食糧だった……。ハリスンのSF小説『人間がいっぱい』をフライシャーが手堅い演出でまとめた近未来SF。アヴォリアッツ国際ファンタスティック映画祭グランプリ受賞。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD ATTITUDES", "フライング・キッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "スパルタ合宿に送られたはずの問題児５人が、飛行場で逃亡。そばにあった豪華自家用ジェットに乗り込んで隠れていたが、武装したハイジャッカー二人が、同じ飛行機に乗っていた！　互いに驚く間もなく、一番のワル、コズモの操縦で、すでに機体は空の上だった。コズモ役のE・ランドールは、ジョン・ヒューズが製作した「あぶない週末」で主役に抜擢された子役。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獄門帳", "獄門帳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "旗本・都築三之助の用人・三枝喬之助は、主人の妻さきと情を通じ、三之助を殺したとされ入牢している。御牢奉行・石出帯刀は、喬之助が無実だと感じて独自に調査を始めるが……。鶴田浩二が、すべての罪を背負いこもうとする喬之助を好演。監督の大曾根辰夫は、この作品から辰保と改名している。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PANDORA AND THE FLYING DUTCHMAN", "パンドラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "ギリシャ神話の『パンドラ』を17世紀の幽霊船伝説とミックスさせたメロドラマ。スペインの港町にパンドラという美しい女性が住んでいたが、彼女は男性に対して冷酷で、恋には縁遠い女性だった。そんな彼女がオランダ人の船長と恋に落ちたことで、これまで彼女の愛を獲得しようとしていた男たちが壮絶な決闘を始めてしまう。", "1950-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GET BACK", "ゲット・バック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "P・マッカートニーの、1989年に始まった10年ぶりのワールド・ツアーの模様を追うドキュメンタリー。ビートルズ時代の名曲からソロ・ナンバーまで、幅広い選曲でファンを喜ばせてくれる。監督は「HELP！ 四人はアイドル」などのビートルズ映画を手掛けたR・レスター。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tokyo Real", "Tokyo Real", "", "", "http://image.eiga.k-img.com/images/movie/34133/315x210.jpg?1475", 84, "300万アクセスを突破した大人気携帯小説を原作に、ドラッグに溺れる女子高生の恋愛をリアルに描いた衝撃作。友人と初めて行ったクラブで男に騙され、集団レイプされてしまった女子高生アヤ。その帰り道、渋谷の闇金で働く青年ナオヤと運命的な出会を果たした彼女は、彼と本気で愛し合うように。しかし彼女は、人間関係の悩みやレイプによるショックを紛らわすためドラッグに手を出し、次第に蝕まれていく……。", "2007-08-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("でべそ", "でべそ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1995年12月末日に幕を降ろした、ストリップの殿堂・渋谷道頓堀劇場の名物オーナー・矢野浩祐氏の波瀾万丈に満ちた半生を映画化。世間を踏み外した極道の矢部は、日本全国のストリップ小屋を巡業する一座の座長となった。最初は、女で食べていくのが嫌でたまらなかったが、天衣無縫なストリッパーたちと暮らすうちに、次第にストリップの世界に魅せられていく。やがて看板娘のストリッパー・マヤと恋に落ちる。性の世界に浸りながらも、純粋無垢な心を持つ主人公を、片岡鶴太郎が熱演。ちなみに“でべそ”とは、ストリップで客席に張り出した丸い舞台のこと。DVDタイトルは「でべそ DE ストリップ」。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The World According to Garp", "ガープの世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "子どもは欲しいけど男に支配されたくはない看護婦が、瀕死の負傷兵にまたがって子どもを授かる。主人公ガープは、こうしてこの世に生を受けた。ちょっと奇妙な人々に囲まれながら成長していく彼の数奇な運命を描いたヒューマンドラマ。映像化不可能と言われたジョン・アービングの自伝的ベストセラー小説を、「明日に向かって撃て！」の名匠ジョージ・ロイ・ヒル監督が見事に映画化。主人公ガープをロビン・ウィリアムズが演じる。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALWAYS", "オールウェイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "スピルバーグによる心温まるファンタジー・ロマン。森林火災消火隊のエース、ピートは恋人のドリンダに“愛してる”という一言を言えぬまま、友を救うために事故死してしまう。しかしピートは幽霊となって、もう一度ドリンダのもとに帰って来る。生きている間に言えなかった一言を言うために……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ムサシ", "ムサシ", "", "", "http://image.eiga.k-img.com/images/movie/55763/315x210.jpg?4530", 0, "井上ひさし脚本、蜷川幸雄演出のタッグで日本演劇界の話題を席巻し、ロンドンやニューヨークでも大好評を博した時代劇舞台「ムサシ」。藤原竜也と勝地涼が主演を務める「ロンドン・NYバージョン」で上演された同作の日本凱旋公演をデジタルシネマ化。巌流島の決闘から6年後。鎌倉の禅寺でついに憎き宮本武蔵（藤原）をとらえた佐々木小次郎（勝地）は、「今度こそ五分と五分で決着を」と武蔵に果し合い状を突きつける。", "2010-12-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE LORD FAUNTLEROY", "リトル・プリンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ご存じバーネット原作の『小公子』の映画化。伯爵家の世継ぎとしてニューヨークからやって来た少年セドリックに「チャンプ」の名子役シュローダー、頑固な老伯爵役にはイギリスの名優ギネスが扮し、ともに好演。美しい田園風景も見どころの一つ。", "1981-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・同棲時代", "新・同棲時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "『東京ラブストーリー』で知られる柴門ふみの同名短編集を映画化したオムニバス。６年ぶりに再会した高校の同級生に心揺らす平凡な主婦の姿を描いた「空に星がある限り」、同棲中のカップルの部屋に女の妹が転がり込んでの騒動を描く「いつか見たあなた」、微妙な恋に悩むOLの姿を描く「もう一度ウェディングベル」の３編。ヒロインにはそれぞれ富田靖子、相楽晴子、松下由樹が演じる。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANTOINE　ET　COLETTE／L’AMOUR　A｀　VINGT　ANS", "アントワーヌとコレット〈二十歳の恋〉より", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 31, "恋のアタックのやり方が分からない純情青年アントワーヌを描くシリーズの第２弾。ヌーヴェル・ヴァーグの中心的存在トリュフォー監督が、世界の５人の監督によるオムニバス作品のフランス編を担当。17歳の主人公は音楽会で美女に出会いひと目惚れ。何度も通ってようやく彼女と親しくなり、両親と同居する彼女のアパートの隣室に引っ越してきた。しかし、問題はそれからだった……。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春琴抄", "春琴抄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "明治初めの大阪・道修町を舞台に、わがままいっぱいに育った薬種問屋の盲目の娘・お琴と、彼女に影のように仕え、自らを殺して献身的に尽くす丁稚・佐助との倒錯的な愛を描いた作品。谷崎潤一郎原作の５度目の映画化で、百恵＝友和コンビの主演第５作。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("管制塔", "管制塔", "", "", "http://image.eiga.k-img.com/images/movie/56113/315x210.jpg?1990", 68, "「ソラニン」の三木孝浩監督が、北海道出身の人気バンド「Galileo Galilei（ガリレオ・ガリレイ）」の楽曲にインスパイアされて製作した青春ドラマ。日本の最北端・稚内市で生まれ育った中学生・藤田駈と転校生の滝本瑞穂は、ともに孤独感を抱いていたことから心を通わせる。2人は駈の家で見つけた古いギターをきっかけにバンドを結成するが……。ドラマ「クローンベイビー」などで活躍する山崎賢人と、映画「告白」で注目を集めた橋本愛が主演する。", "2011-04-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌18 ハマちゃんスーさん瀬戸の約束", "釣りバカ日誌18 ハマちゃんスーさん瀬戸の約束", "", "", "http://image.eiga.k-img.com/images/movie/34173/315x210.jpg?3178", 114, "シリーズ開始から20周年を迎えた国民的人気コメディの通算第20作目。鈴木建設の社長を退任し会長職に就くことになったスーさんが、突然行方不明になってしまう。スーさんの奥さんに頼まれたハマちゃんは、わずかな手がかりを元に岡山県へと向かう。無事スーさんと再会したハマちゃんだったが、2人は瀬戸内海のリゾート開発を巡る騒動に巻き込まれてしまい……。ゲスト出演は「武士の一分」の檀れい、高嶋政伸ほか。", "2007-09-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お役者文七捕物暦 蜘蛛の巣屋敷", "お役者文七捕物暦 蜘蛛の巣屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "世をすねて遊び人となっていた名代役者、播磨屋歌六の息子文七。ある夜、すべて死刑にされたはずの土蜘蛛と称する一味によって、老中に嫁ぐはずの娘の操が奪われるという事件が起こる。同時に文七の父、歌六も何者かに殺された。文七は役者上がりの経験を生かして変装によって姿を変え、事件の捜索に乗り出す。中村錦之助の文七捕物暦シリーズの第１作。ミステリーとしても楽しめる。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明日泣く", "明日泣く", "", "", "http://image.eiga.k-img.com/images/movie/57143/315x210.jpg?4470", 76, "「狂人日記」「麻雀放浪記」などで知られる昭和の無頼派作家・色川武大の短編小説を映画化。1970年代の「不良番長」シリーズなどで活躍した内藤誠監督が、86年の「スタア」以来25年ぶりにメガホンをとった。賭け麻雀ばかりのその日暮らしの生活をしていた小説家の武は、ある日、ジャズクラブで高校の同級生キッコと再会し、次第にひかれていく。クラブの客足が伸びずに苦しむキッコは、武に金を貸してほしいと頼むが、他人に貸せる金など持ち合わせていない武は大胆な勝負に挑むことに……。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOCK TREATMENT", "ショック・トリートメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ロック・ミュージカル「ロッキー・ホラー・ショー」の製作スタッフによる続編で、本邦未公開作品。視聴者参加の公開番組に駆り出されたジャネットとブラッドは、ファーリー・フレーバーの陰謀に巻き込まれ、ブラッドは精神病院に閉じ込められる……。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE HAPPY", "ラヴ・ハッピー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "明日の食事にも苦労するような貧しい劇団の一員ハーポ。ある日、彼はマーケットから缶詰を盗むが、その中にはロマノフ家のダイヤが隠されていたため、何者かに捕らえられて……。マルクス兄弟最後の作品となったセンチメンタルな喜劇。製作は女優のM・ピックフォード。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER BY NIGHT", "夜ごとの殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ニューヨークで起こる連続殺人事件を描くサイコミステリーの快作。その夜も若い女が犠牲となるが、現場には頭をケガした目撃者の青年アランが倒れていた。彼は警察から事情聴取を受けるが、記憶を失っており、担当刑事のカールは彼の記憶を取り戻させようとする。やがてアランは毎夜、自分が殺人を犯す夢にうなされる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Rocker", "ROCKER 40歳のロック☆デビュー", "", "", "http://image.eiga.k-img.com/images/movie/55143/315x210.jpg?3228", 103, "ロックバンド「ヴェスヴィオス」のドラマー・フィッシュは、バンドが大手レコード会社と契約するのと引き換えにバンドから追放されてしまう。バンドがブレイクする中、職もないフィッシュは姉の家に居候するニート生活を送っていたが、ひょんなことから甥のバンド「A.D.D.」の新メンバーになり、かつて失ったロックスターとなる夢に向かって奮起する。主演は人気コメディアンのレイン・ウィルソン、監督に「フル・モンティ」のピーター・カッタネオ。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高校大パニック", "高校大パニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "自主製作映画出身の石井聰亙と澤田幸弘の共同監督による学園バイオレンス・アクション。ある生徒の自殺をめぐり、学校側の態度に憤りを感じた生徒が銃を片手に校内にたてこもる。のちの石井聰亙のバイオレンス路線のきっかけともなったパワフルな作品。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("放浪記", "放浪記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "ベストセラーとなった林芙美子の自伝的小説を、久松静児が映画化。同作品は1935年に木村荘十二監督で、1962年に成瀬巳喜男監督で映画化されている。行商人の子として各地を歩き、カフェの女給をしながら詩作をするヒロインを角梨枝子が好演するが、夏川静枝主演の木村作品、高峰秀子主演の成瀬作品の味わいには及ばない。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("24 Hour Party People", "24アワー・パーティ・ピープル", "", "", "http://image.eiga.k-img.com/images/movie/1003/315x210.jpg?1589", 115, "マンチェスターを舞台に、70年代後半から90年代初頭にかけて隆盛を極めたレコード会社ファクトリーと、同社が経営するクラブ、ハシェンダを取り巻く人々の物語。ジョイ・ディビジョン、ニュー・オーダー、ハッピー・マンデーズなどのマンチェ出身バンドの破天荒な生き様が、ファクトリーの経営者で地元テレビ局のレポーターでもあるトニー・ウィルソンが語り部となって綴られる。", "2003-03-22", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ASSASSIN HABITE´ AU 21", "犯人は21番に住む", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「恐怖の報酬」「密告」などでフランスのサスペンス映画の第一人者として知られるH・G・クルーゾー監督のデビュー作。“デュラン”という異常者による連続殺人が起こったパリの街で、ウェン警視は犯人の名刺を手に入れる。警視は神父に扮して名刺の住所を頼りに、21区のアパートに踏み込んでいくのだが……。", "1942-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THRILL OF IT ALL", "スリルのすべて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "TVのショー番組ディレクター出身のN・ジュイソン監督が、TVコマーシャルを鋭く風刺したコメディ。石けん王にくどかれて一躍コマーシャルの女王となってしまった主婦とその夫の悲喜劇を、軽快なタッチで描いていく。妻役にはD・デイ、夫にはJ・ガーナーが扮している。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WOMEN IN LOVE", "恋する女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "D・H・ロレンスの有名な小説を、鬼才K・ラッセルが映画化。教師と彫刻家の姉妹は、それぞれに街の学者と炭鉱夫に恋をするが、二人の恋は周囲の人々には理解されなかった……。発表当時センセーションを巻き起こした原作をラッセルが見事に映像化し、主演のG・ジャクソンはアカデミー主演女優賞を受賞。", "1969-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山の讃歌 燃ゆる若者たち", "山の讃歌 燃ゆる若者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "妻との結婚を押しきったため、出世が遅れたと思い込んでいる財務省に勤める父・栄城。その３人の息子たちはエリート・コースを歩みながらも、封建的な家庭と官僚生活によって歪められていく自分たちを、もう一度人間らしく復活させようと、次々と山に登る決意をするのだが……。出世主義への風刺を込めた異色の山岳ドラマ。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十字街頭", "十字路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "不景気で失業者があふれる1930年代の上海。大学は出たものの、職探しに四苦八苦するラオチャオの下宿の隣部屋に、若い女性チーインが越して来た。板一枚の壁越しに、毎日言い争う二人。ある日、電車内で出会った彼らは隣人と知らずに恋に落ち……。中国映画界の大女優P・ヤンの出世作となった風俗喜劇。", "1936-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODY CHEMISTRY２", "ボディ・トーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "異常なサディストという、SEXコンプレックスを持つ刑事ダンは、故郷でかつての恋人ブレンダと再会。ダンはブレンダへの愛を募らせる一方で、悩みを解決すべく、ラジオ番組のSEXカウンセリングに電話する。ところが、女性カウンセラー、クレアも異常性愛者だった。ダンは危険な性の罠から脱け出そうとするが、すでに新たな殺人事件に深くはまり込もうとしていた……。愛と性、そして犯罪へと落ちていく男を描くエロティック・サスペンス。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE QUAI DES BRUMES", "霧の波止場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "港町ル・アーブル。外人部隊を脱走したジャンは、身を隠した酒場で会ったネリーと親しくなり、やがて愛し合うようになるが……。人生にいや気のさした男女が愛し合い、引き裂かれる運命を描いたカルネ＝プレヴェールのコンビによる詩的リアリズムの代表作。", "1938-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEMI レミ", "LEMI レミ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "女子大生タレント・ひろみは、ある日不思議な光を発する鳩の卵を発見。その卵の中から自分そっくりのエイリアンが現れ、彼女はエイリアンを使って学業と仕事を両立させようとするが……。長年、大林宣彦の助監督を務めた小倉洋によるエロティックSFコメディ。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時をかける少女（1997）", "時をかける少女（1997）", "", "", "http://ecx.images-amazon.com/images/I/310KA9C5C2L._SL210_.jpg", 106, "1983年に大林宣彦監督が映画化して主演の原田知世を一躍スターの座に押しあげた、筒井康隆原作の傑作SF小説を、大林監督版の製作を手がけた角川春樹が自らメガホンを取って再映画化。昭和40年。高校生の芳山和子は、学校の実験室でラベンダーのような香りをかいだ瞬間、気を失ってしまう。それ以来、和子はタイムトラベルの能力を身につけてしまい……。主演には新人の中本奈奈が抜てきされた。原田知世がナレーションを担当。", "1997-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅の花道", "修羅の花道", "", "", "http://ecx.images-amazon.com/images/I/51-ShRBAAvL._SL210_.jpg", 0, "昭和の裏社会で暗躍した稲川会2代目会長・石井隆匡の生涯を描いた山平重樹のノンフィクション小説「“最強の経済ヤクザ”と呼ばれた男」を、奥田瑛二主演で実写化した任侠ドラマ。第2次世界大戦直後の横須賀。仲間たちと暴れまわる毎日を送っていた石田隆匡は、かつて賭場で出会った押塚義八郎親分と再会する。押塚の侠気に惚れこんだ石田は、押塚組代貸として頭角を現わしていく。共演は千葉真一、哀川翔ほか。", "2012-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Drowning Pool", "新・動く標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "P・ニューマンが10年ぶりに探偵ルー・ハーパーを演じたハードボイルド・アクション。L・マクドナルドの書いた『魔のプール』が原作で、恐喝事件が発端となり、ある家庭に渦巻く陰謀が綴られていく。作品としての切れ味は、前作に比べるといささか落ちる。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビー・バップ・ハイスクール 高校与太郎 完結篇", "ビー・バップ・ハイスクール 高校与太郎 完結篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "相変わらず女三昧のヒロシと、中学生の喧嘩に巻き込まれ、下の奴らを押さえ切れずにアタマに立つ自信を失くしてしまったトオル。それぞれの青春の悩みが炸裂するなか、ボンタンをはいてスケートをするという極悪ツッパリ軍団、極東高校土木科が現れる。シリーズをにぎわせたキャラクターも再登場の完結篇。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽の法／　エル・カンターレへの道", "太陽の法／　エル・カンターレへの道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "製作総指揮を務める大川隆法のベストセラー小説をアニメーション化。宇宙の創造と人類誕生の神秘をスケール豊かに描き出す。１億２千万年前、サタンが反乱を起こし地上は混乱に陥る。至高の霊的存在、エル・カンターレは、自分の分身を地上に送り、新世紀の創造と新文明の構築を図る。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOOMERANG", "ブーメラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "化粧品会社の宣伝部社長マーカスは無類のプレイボーイ。女性を口説き、ベッド・インしては捨てるという日々を送っている。新しい上司になったやり手の美女ジャクリーンに目をつけた彼は、さっそくアタック。苦労の末、なんとか一夜をともにしたマーカスは、すっかり彼女に夢中になってしまう。しかし、ジャクリーンにとって、彼は遊びでしかなかった。つれない彼女の姿に今までの自分を見たマーカスは、すっかり落ち込んでしまい……。人気スター、E・マーフィ主演による、おしゃれなラブ・コメディ。ゴルチエやベルサーチ、イッセイ・ミヤケなど、世界のトップ・デザイナーによる衣装も見ものだ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE PIECE ワンピース　珍獣島のチョッパー王国", "ONE PIECE ワンピース　珍獣島のチョッパー王国", "", "", "http://ecx.images-amazon.com/images/I/61b8VLhWFzL._SL210_.jpg", 61, "コミック、テレビアニメともに幅広い人気を誇る海洋冒険ファンタジーの劇場版アニメ第3弾。珍獣たちが暮らす楽園・王冠島に向かって航海を続けていたルフィ海賊団。ところが、その途中で海底火山の噴火に巻きこまれ、トナカイの船医チョッパーだけが船から吹きとばされてしまう。王冠島にたどり着いたルフィたちはチョッパーを探しはじめるが、島では動物のツノに隠されているという秘宝を狙う海賊たちが動物たちを襲っていた。", "2002-03-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A｀ GAUCHE EN SORTANT DE L’ASCENSEUR", "エレベーターを降りて左", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "パリの高級アパルトマンを舞台に、隣同士に住む住人たちが繰り広げる、イキでおかしなフレンチ・ラブ・コメディ。男のむき出しの嫉妬のために、しょっちゅう争っては熱烈な仲直りをする“嵐のようなカップル”ポリスとエヴァ。ある日、ちょっとした誤解からエヴァが隣室の男と浮気していると勘違いしたポリスは……。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VACHE ET LE PRISONNIER", "牝牛と兵隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "第二次世界大戦下のドイツの農村。開戦と同時にドイツ軍の捕虜となったフランス人のシャルルは、４年間にもわたって農家で働かされ続けてきた。なんとか故郷に帰りたい彼は、脱走を試みるが……。フランスの喜劇王フェルナンデルの主演による奇想天外な脱走コメディ。牝牛を連れての珍妙なる脱走劇をほのぼのとした筆致で綴っている。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Avril", "アヴリルの恋", "", "", "http://image.eiga.k-img.com/images/movie/53083/315x210.jpg?1871", 96, "山奥の修道院で世俗を知らずに育った少女が、外の世界に足を踏み出すことで成長していく姿を綴ったフランス製ヒューマン・ドラマ。人里離れた修道院で暮らしているアブリルは、自分に双子の兄がいることを知らされる。兄を探すために2週間だけ修道院を離れた彼女は、南フランスの美しい自然の中で人々と触れ合いながら、初めて恋を知り……。主人公アブリルを演じるのは、「誰がバンビを殺したの？」のソフィー・カントン。", "2007-11-24", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時をかける少女（2006）", "時をかける少女（2006）", "", "", "http://image.eiga.k-img.com/images/movie/33423/315x210.jpg?4697", 104, "これまで何度もテレビドラマや映画で映像化されてきた筒井康隆の名作小説を初アニメ化。ひょんなことから過去へ行くことの出来る力を手に入れた真琴が、過去を変えてしまったために巻き起こる騒動を描く。監督は「ONE PIECE THE MOVIE　オマツリ男爵と秘密の島」の細田守。キャラクターデザインを「新世紀エヴァンゲリオン」の貞本義行が手がける。真琴の叔母、和子の声を演じるのは、声優初挑戦となる原沙知絵。", "2006-07-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATAL JUSTICE", "レディ・スコルピオン２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "CIAの中でも腕利きの暗殺者ダイアナは、本部から“マース”というコードネームを持つCIA内の指揮官の抹殺依頼を受ける。任務遂行に関しては、正確無比、非情なダイアナであったが、今回は意外な事実に直面する。“マース”こそ、彼女が幼い頃、生き別れとなった実の父親だったのだ。父娘の絆と巨大組織の闘いを描いたセクシー・バイオレンス・アクション。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女王密命", "皇帝密使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「Mr.Boo！」でおなじみのサミュエル・ホイが主演する“悪漢探偵”シリーズ第3作。女好きの大泥棒サムが、盗まれた女王陛下の宝石の奪回を依頼され、世界を股にかけて大活躍する。「007」など数々の映画パロディが登場したり、ソックリさんが出演したりと、コメディとアクション満載で描く娯楽作。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森崎書店の日々", "森崎書店の日々", "", "", "http://image.eiga.k-img.com/images/movie/55683/315x210.jpg?3303", 109, "第3回ちよだ文学賞で大賞を受賞した八木沢里志の小説を、人気モデルの菊池亜希子主演で映画化。同じ職場の恋人から別の女性と結婚することを打ち明けられた貴子は、ショックのあまり会社を辞めてしまう。ある日、部屋に閉じこもってばかりいた彼女のもとに、本の街・神保町で古書店を営む叔父から連絡が入る。叔父のもとに身を寄せることになった貴子は、そこで出会った人々との交流を通し、大人の女性へと成長を遂げていく。", "2010-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADDAMS FAMILY VALUES", "アダムス・ファミリー2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "オバケ一家のドタバタ騒動を描く、往年の人気TVシリーズ『アダムスのお化け一家』の映画化第２作。今回は、一家にピューバートという、兄や姉のイジメにも負けないオバケ赤ちゃんが加わり、活躍する。一家をバラバラにしようと画策する殺人鬼の女、デビーの手から、ピューバートが家族の危機を救うという内容。相変わらず、ギャグ満載でお笑いは快調。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("D", "D", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "外人部隊の戦士として世界各地の戦場を転戦してきた青年・川俣が、ロシアの特殊戦闘武装兵器“コンバットスーツ”をめぐり、地球や国家の危機に対処していく姿を描いたSFXハード・アクション。映画は、宇宙から飛来してきたアメーバをめぐる３つのエピソードで構成されている。川俣役の主演・長倉大介の熱演が光る。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOLLY MATRIMONY", "ホーリー・ウェディング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "マリリン・モンローに憧れるヤンキー娘のハバナはある日、恋人のピーターと遊園地の金庫から金を盗み、彼の生まれ育った宗教コロニーに身を隠す。そこで宗教の教えに従い結婚式を挙げるが、その直後に彼が交通事故で死亡。おまけに宗教上の理由から、彼の12歳の弟ジークと結婚するハメに……。12歳の新郎とヤンキー娘の結婚騒動を描いたロマンティック・コメディ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A WALK IN THE CLOUDS", "雲の中で散歩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "時は第二次世界大戦直後。戦地から帰還してきたものの妻との愛に幻滅し、一人サクラメント行きの汽車に乗るポール。そこで、妊娠中にもかかわらず、相手の男に捨てられ絶望している美女ヴィクトリアと出会う。二人はヴィクトリアの父親を納得させるために、彼女の実家で偽りの夫婦を演じる。幻想的なぶどう園に囲まれたこの地で、二人はやがて本当の恋に落ちるが……。本作で本格的なラブ・ロマンスに初挑戦のK・リーヴスが、ロマンティックな恋に身を焦がす若者をセクシーに演じている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はだしの青春", "はだしの青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "学生時代にボクシングの試合で相手を殺してしまったという暗い過去をもつ男が、新任教師として、とある高校にやって来る。彼に恋心を抱く高校生・鈴子は、不治の病を患っていた。市村泰一と石森史郎のコンビが得意とする青春ラブロマンス。熱血と純愛が交錯する。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GANTZ", "GANTZ", "", "", "http://image.eiga.k-img.com/images/movie/54943/315x210.jpg?3419", 130, "累計1200万部を販売した奥浩哉の人気SFコミックを、アイドルグループ「嵐」の二宮和也と松山ケンイチ主演で実写映画化した2部作の前編。共演に吉高由里子、本郷奏多、山田孝之ら人気若手俳優がそろい、「ホッタラケの島」の佐藤信介監督がメガホンをとる。駅のホームで偶然再会した幼なじみの玄野（二宮）と加藤（松山）は、線路に落ちた酔っ払いを助けようとして電車にひかれてしまう。見知らぬマンションの一室で目覚めた2人は、“GANTZ（ガンツ）”と呼ばれるナゾの黒い球体により、異形の“星人”と呼ばれる存在との死闘を強いられていく。", "2011-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TREASURE ISLAND", "オーソン・ウェルズ in 宝島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "R・L・スティーヴンソンの有名な冒険小説の映画化。資金難のO・ウェルズが、フランス、西ドイツ、スペインから製作費を調達して完成させた作品。ウェルズ自身がロング・ジョン・シルバーに扮し熱演したが、酷評されたロー・コスト作品。脚本のO・W・ジーベスはウェルズの別名。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A TIGER’S TALE", "サマーデイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "テキサスの小さな町の高校生ババ。シャーリーというガールフレンドはいるが、若いババには彼女の母親ローズの方がずっと素晴らしい女性に映る。いつしか愛があれば年の差なんての関係がシャーリーに知られるが……。監督のP・ダグラスは、カーク・ダグラスの息子。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WEBMASTER", "ウェブマスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "現実世界とコンピュータ・ワールドを駆ける冒険を、ゲーム感覚で描いたSFサスペンス。ギャングもどきの実力者ストイスに、コンピュータ・システムの用心棒として雇われた天才ハッカー、JB。ある日、ストイスの口座から大金が引き落とされ、容疑を向けられたJBは真犯人探しを命じられる。彼にあたえられた時間は35時間。次々と襲いかかる危機を乗り越えながら、JBは意外な真相にたどりつく。", "1998-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAMES", "悪魔のくちづけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "莫大な遺産を相続して優雅な日々を送るジェニファー。しかし彼女の前に突如、謎の女リサが現れ怪奇現象が次々と彼女を襲う。次第に追い詰められていくジェニファーだが……。K・ロスの初期作品。S・シニョレが悪女役を熱演するサイコ・サスペンス。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さんかく", "さんかく", "", "", "http://image.eiga.k-img.com/images/movie/55263/315x210.jpg?3611", 0, "「パッチギ！」「ROOKIES 卒業」などで知られる高岡蒼甫が、初主演を務めるラブコメディ。共演に田畑智子、「AKB48」の小野恵令奈。「純喫茶磯辺」の吉田恵輔がメガホンをとる。百瀬と佳代のカップルは、お互いに説明できないわだかまりを抱えたまま、だらだらと同せい生活を続けていた。そんなある日、佳代の中学生の妹・桃が夏休みの間を利用して2人の家に転がり込んでくる。桃の大人びた素振りに動揺する百瀬に対し、佳代は日に日に不信感を募らせていく。", "2010-06-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("斜陽のおもかげ", "斜陽のおもかげ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "太宰治の『斜陽』の中で描かれた彼の愛人の子である町子。その重い宿命を背負いつつ元気な彼女はある日、太宰の研究をしている大学生、谷山と出会い恋に落ちるが……。太宰治の遺児、太田治子の原作手記の映画化で、斎藤光正の初監督作品でもある。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE MOIS LE PLUS BEAU", "帰らざる橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "フランスの片田舎を流れる川。第二次世界大戦中、そこにはドイツ軍の進撃を想定してダイナマイトを仕掛けられた橋が、工事の途中で放置されていた。川で二分された小さな村を舞台に、戦争が生んだ悲劇をユニークなタッチで綴っていく。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN HOMME AMOUREUX", "ア・マン・イン・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「E.T.」のP・コヨーテ主演のラブ・ロマンス。アメリカの映画スター、スティーブは妻子を残して撮影のためローマにやって来た。そこで彼は新人女優ジェーンを相手に選ぶが、やがて二人は映画さながらに恋に落ちていく。だがジェーンは優しかった母の死をきっかけに、妻子を捨てられないスティーブと別れ、一人で強く生きていくことを決意する。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飲食男女", "恋人たちの食卓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "現代の台湾・台北を舞台に、三姉妹が繰り広げる三者三様の恋愛模様と、台湾5つ星ホテルのシェフである、彼女たちの父親が作り上げる料理の数々が織りなすホームドラマ。日曜日の晩餐は、中国料理の鉄人だった父の手料理を前に円卓をにぎわすのがこの一家の習わしだ。敬虔なクリスチャンである長女、航空会社勤務のキャリア・ウーマンの次女、女子大生で無邪気な三女。人間の根源的な欲求である“食欲”と“性欲”を通して、彼女たちの初恋や不倫、セックスに妊娠、結婚、父と娘、家族や人間関係、そして“人生”を描きあげた意欲作。", "1994-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("突入せよ！「あさま山荘」事件", "突入せよ！「あさま山荘」事件", "", "", "http://image.eiga.k-img.com/images/movie/1263/315x210.jpg?4156", 133, "ベストセラー・ドキュメンタリー「連合赤軍『あさま山荘』事件」（文芸春秋刊）を原作に「金融腐食列島　呪縛」の監督・原田眞人、主演・役所広司コンビで映画化。1972年2月19日、連合赤軍の兵士たちが、軽井沢の別荘、あさま山荘に管理人の妻を人質にして籠城。爆発処理のための欧米派遣から帰国したばかりの警察庁警備局付監査官、佐々は、この事件の指揮を命じられる。が、その攻防戦は、以後10日間に渡る長さに及ぶ。", "2002-05-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兄とその妹", "兄とその妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "監督の島津保次郎は「路上の霊魂」に助監督として参加した経歴を持ち、松竹のいわば第二世代の監督といえる。やがて松竹において、大ベテランの総領格的な存在となり、その門下からは五所平之助、豊田四郎、吉村公三郎、木下惠介、中村登などが輩出された。その島津の最高傑作とまでいわれるのが本作品。世渡りとか出世とか手管のことなどまるで考えない生真面目な会社員・敬介とその妻、そして貿易会社の秘書をしている敬介の妹という３人の登場人物が、敬介の会社での人間関係の軋れきの中で見せる三様の行動を活写する。当時、映画界最高のベスト・ドレッサーといわれた桑野通子の美しさにも注目したい。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASUAL SEX？", "カジュアル・セックス？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "エイズ怖さのあまり、セックス恐怖症にかかったステイシーとメリッサ。そこで、二人は健康的な男性を求めて、温泉とアスレチック施設を備えたリゾート・ホテルにやって来た。それぞれ安全(？)な男性とめぐり合い、明るいセックス・ライフが始まるはずが……。“カジュアル・セックス”なんてとても、というご時世に生きる女たちの悩みと友情をコミカルに描いた作品。I・ライトマンが製作総指揮にあたり、これが初監督作となるG・ロベールをサポートしている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TALES FROM THE GIMLI HOSPITAL", "ギムリ・ホスピタル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "カナダ映画界で、その奇異な作風のためか異端視されている奇才ガイ・マディン。その彼の長編デビュー作であるこの作品は確かにシュールでグロテスクな物語だ。19世紀末、天然痘にかかった漁師エイナーは、不潔きわまりないギムリ病院に入院し、陽気で人あたりのよいグンナーと知り合う。彼らは友情をはぐくむが、ある日エイナーがグンナーにハサミを貸したことから、おぞましい過去が露わになり……。", "1988-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まむしの兄弟・恐喝三億円", "まむしの兄弟・恐喝三億円", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ゴロ政は勝をはねた神洋交易の社長をゆすろうとゴルフ場ですごむ。ちょうど社長の李はボールにつめた麻薬を取引中であった。仕返しに出た勝はこのボールを持ち帰り、二人は用心棒の広津に追われるはめに。松方弘樹が野良犬のような用心棒・広津を熱演。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("板尾創路の脱獄王", "板尾創路の脱獄王", "", "", "http://image.eiga.k-img.com/images/movie/54683/315x210.jpg?4181", 94, "お笑い、俳優、歌手など幅広く活躍する板尾創路がメガホンを取り、脚本・主演も兼任する監督デビュー作。どんな牢も錠も物ともせずに100％脱獄を成功させる男・鈴木は、脱獄と再逮捕を繰り返し、次第に「脱獄王」の名をほしいままにしていく。彼は一体なぜ脱獄し続けるのか……？　國村隼、石坂浩二、阿藤快らベテラン俳優が出演するほか、ぼんちおさむ、オール巨人、木村祐一、宮迫博之などお笑い芸人も多数客演。", "2010-01-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bridge to Terabithia", "テラビシアにかける橋", "", "", "http://image.eiga.k-img.com/images/movie/53133/315x210.jpg?3006", 95, "77年に発表され、児童文学界のノーベル文学賞と言われる国際アンデルセン賞を受賞したキャサリン・パターソンの同名ファンタジー小説の映画化。学校にも家庭にもなじめない少年ジェスは、個性的であるがゆえにクラスで浮いてしまっている転校生の少女レスリーと親しくなる。2人は豊かな想像力で森の中に空想の国“テラビシア”を築くが……。監督は「ザ・シンプソンズ」「ラグラッツ」など、アメリカの人気アニメを手掛けてきたガボア・クスポ。", "2008-01-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SP 革命篇", "SP 革命篇", "", "", "http://image.eiga.k-img.com/images/movie/55483/315x210.jpg?2853", 128, "岡田准一主演の人気ドラマ「SP 警視庁警備部警護課第四係」のシリーズ完結編となる劇場版第2作。官房長官を狙ったテロ事件から2カ月。怪我も回復し通常業務に戻った井上（岡田）ら第4係のSPたちは、麻田内閣不信任案の採決が行われる国会議事堂での警備につく。一方、野望の実現に向けて動き出した尾形（堤真一）は、人生の全てを賭けた計画を実行に移そうとしていた。国会内の様子がテレビで生中継されるなか、テロリストたちが一斉に行動を開始する。", "2011-03-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("C.C. AND COMPANY", "C.C.ライダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1970年代特有のやるせない雰囲気が漂う、人間ドラマ。生きる目標を失い、やり場のない無力感をバイクにぶつける男たち。主人公のC.C.もそんな一人だったが、ある女性との出会いが彼に人生の再スタートを決意させる。しかし、かつてのバイク仲間たちは、離脱しようとする彼を許さなかった。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LICENSE TO DRIVE", "運転免許証", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "当時の全米アイドル、C・フェルドマンとC・ハイムが「ロストボーイ」に続いて共演した青春コメディ。高校生レスの夢は美少女メルセデスとドライブ・デートをすること。だが運転免許の試験に落ちてしまった彼は、合格したと嘘をつき、父親のキャデラックを拝借し、ドライブに出かけてしまう……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UCCELLACCI E UCCELLINI", "パゾリーニの鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "パゾリーニが「奇跡の丘」に次いで発表した寓話的作品。郊外の道を父親のトトと息子のニネットが歩いていく。途中、カラスが二人に合流し、聖フランチェスコの言いつけでチチッロ修道士が鳥たちに福音を伝えた話を語り始めた。トトとニネットは、なおも歩き続け、カラスは説教を続ける。だが、空腹だった二人は、カラスを捕まえて焼鳥にして食べてしまう。やがて二人は、チャップリンの映画のラストシーンのように歩き去っていく。パゾリーニの思想や内的独白を映像化したシネマ・エッセイ。ビデオ、DVDタイトルは「大きな鳥と小さな鳥」。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Times of Harvey Milk", "ハーヴェイ・ミルク", "", "", "http://image.eiga.k-img.com/images/movie/47713/315x210.jpg?1763", 87, "1984年第57回アカデミー長編ドキュメンタリー賞受賞作品。自らゲイを公言し、社会的弱者の権利獲得を訴えたサンフランシスコ市政執行委員ハービー・ミルクの活動を、1978年に同じ執行委員の男に暗殺された事件を中心に追っていく。1987年に初公開され、2009年春、同じハービー・ミルクを描き第81回アカデミー作品賞にノミネートされたガス・バン・サント監督作「ミルク」の公開にあわせてリバイバル上映。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ПУТЕШСТВП　МОЛОДОГО　КОМПОЗИТОРА／A　YOUNG　COMPOSER’S　ODESSAY", "若き作曲家の旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "グルジアがロシア領だった20世紀初頭、第一次ロシア革命の挫折感が漂う頃が舞台。首都チフリスの大学を卒業したばかりの若い作曲家ニクーシャは、グルジアの古い民謡を採集する旅に出た。道連れとなったレコは彼が革命組織の密使だと思い込み、行く先々で抵抗運動の仲間たちに引きあわせるが……。「ピロスマニ」で知られる旧ソ連グルジア共和国のG・シェンゲラーヤ監督が、古くから支配の歴史に苦しめられてきた祖国への熱い思いを持って描いている。", "1985-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("部屋とYシャツと私", "部屋とYシャツと私", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "メロディーとフレーズが多くの人の支持を得てミリオンセラーを突破する大ヒットとなった平松愛理の『部屋とYシャツと私』をモチーフに、結婚と恋愛の間で揺れる女性の気持ちを等身大に描いたラブ・ストーリー。ショーウインドーのディスプレイ・コーディネーターの美紀は、付き合って５年になる彼がいた。二人が結婚を意識した時、事件が起こる。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SEDUCTION OF JOE TYNAN", "或る上院議員の私生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "売り出し中の上院議員が、やがて大統領選出馬を決意するまでに起こるできごと、様々な愛と苦悩の日々を描く。男にとって、仕事、家庭、女性とは……。演技派A・アルダが、長年の念願である役柄を演じるため、脚本も手掛けた意欲作。ビデオ発売時タイトルは「ある上院議員の情事」。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("唐獅子株式会社", "唐獅子株式会社", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "新しもの好きの組の親分によって、通信社やビデオ会社、芸能会社など慣れない仕事をまかされて四苦八苦する昔気質のヤクザを主人公にしたコメディ映画。ヤクザ社会の杓子定規をパロディー化、横山やすしと木村一八が親子で共演している。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BREAKFAST CLUB", "ブレックファスト・クラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "全米の若者を熱狂させたヒューズ＆リングウォルドのコンビによるハイスクール・グラフィティー。土曜日に休日登校を命じられた５人の高校生が、境遇を語り合ううちに相互理解と友情を築き上げるまでを描く。ヒューズのリズミカルでシャープな演出が小気味良い。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シェンムー・ザ・ムービー", "シェンムー・ザ・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "海外でも好評を博した人気ゲーム・ソフト『シェンムー』の映画版。芭月流柔術の達人である父親を謎の男に殺されてしまった青年、涼。その仇討ちを決意した彼の闘いが、ゲーム版の製作者でもある鈴木裕が演出したCG映像の中に展開していく。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Anna and the King", "アンナと王様", "", "", "http://image.eiga.k-img.com/images/movie/1863/315x210.jpg?3424", 147, "1862年。未亡人のアンナは、10歳の息子をつれてシャム(現在のタイ)へやってきた。モンクット王に依頼され、王子たちに世界への視野を広める教育を施すためだ。シャムや王室について知識のないままやってきたアンナは、王を神のように崇めひれ伏す謁見の儀を拒否するが、王は「無礼だが勇気がある」と心にとめる。そしてアンナは、自国イギリスとあまりに違う環境のギャップに戸惑いながらも新しい生活を始める。", "2000-02-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にあんちゃん", "にあんちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "10歳の少女・安本末子の綴り方(＝日記）の映画化。昭和28年の春、不景気に覆われた佐賀県の小さな炭鉱町を舞台に、父母のいない４人兄弟が、貧しくてもけなげに生きる姿を描いている。今村監督の演出は重厚なリアリズムで貫かれていて、観客の涙を誘うようなセンチメンタルな描写を回避している。現地ロケをいかして、炭鉱町に生きる人々の姿を鮮やかに捉えている。栄養失調になりながらも明るく元気な末子を演じる前田暁子をはじめ、子役たちの好演も印象深い。今村監督はこの作品で文部大臣賞を受賞したが、このような賞をもらってはいけないと自戒し、「豚と軍艦」を製作したという。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PALMETTO", "パルメット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「ブリキの太鼓」の名匠V・シュレンドルフが撮ったフィルム・ノワール。日本では劇場未公開に終わったが、アメリカの実力派俳優たちが顔をそろえている。かつて故郷の町パルメットの不正を一掃しようとして、逆に投獄された新聞記者ハリー。彼は出所後、恋人がいるこの町に戻り、資産家の謎めいた美女レアから偽装誘拐計画を持ちかけられる。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雁来紅", "雁来紅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "台湾製茶東京支店の支配人・尾形は、社長・陳の初視察を台湾から迎えることになり大忙し。ところがそんな時に尾形はささいなことで夫婦げんかをし、妻は家出してしまう。パーティの日、尾形はタイピストのマチ子に妻役になってもらうが、そこに本当の妻が現れて……。入江プロの第１回オール・トーキー作品で、「潮」をもって日活を退いた鈴木重吉の入江プロ移籍第１作でもある。", "1934-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私の優しくない先輩", "私の優しくない先輩", "", "", "http://image.eiga.k-img.com/images/movie/54933/315x210.jpg?2306", 102, "「携帯彼氏」の川島海荷と、お笑いコンビ「はんにゃ」の金田哲が主演するラブコメディ。原作は「ちーちゃんは悠久の向こう」の日日日（あきら）。九州の小さな町に引っ越してきた17歳の女子高生・西表邪馬子は、イケメンの先輩・南愛治に一目ぼれし、すれ違っては妄想を膨らます日々を送っている。そんなある日、暑苦しい先輩・不破風和にラブレターを見られてしまい、勝手に“南くんへの告白大作戦”を仕組まれてしまう。", "2010-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Copying Beethoven", "敬愛なるベートーヴェン", "", "", "http://image.eiga.k-img.com/images/movie/1343/315x210.jpg?3843", 104, "「秘密の花園」「太陽と月に背いて」などで知られるポーランド出身の女流監督アニエスカ・ホランドが、天才作曲家ベートーべンの苦悩と愛を描いた歴史ドラマ。1824年のウィーン。“第九”の初演を4日後に控えたベートーべンのもとに、若い女性のコピスト（写譜師：作曲家が書いた楽譜を清書する職業）、アンナがやってくる。期待に反して女性がやってきたことに怒るベートーべンだったが、彼女の中に才能を見出し、次第にかけがえのない存在になっていく……。", "2006-12-09", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH SEASON", "ハイシーズン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ギリシャ・ロードス島の小村リンドス。写真家のキャサリンは、娘と二人暮らし。彫刻家の夫パトリックとは、深く愛し合いながらも、芸術観や人生観が違いすぎ、目下別居中だ。そんなある日、観光にやって来た新婚カップルの若い夫が、キャサリンに一目惚れして……。地中海の美しい景観が見ものの、粋な“恋のさや当て”物語。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRUMS ALONG THE MOHAWK", "モホークの太鼓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "独立戦争のさなかに結婚したギルバートとラナは、モホーク河の渓谷にあるギルバートの農場で開墾に精を出した。しかし、英国人コールドウェルが先導するインディアンたちの度重なる襲撃で、ギルバートの家は焼き払われてしまう。ジャーマン・フラットの砦で、彼らはインディアンと対決する時を迎えた。撮影はカラー、モノクロを同時に行い、それぞれのバージョンが存在する。日本での初公開時はモノクロだったが、ビデオはカラー版で発売されている。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海をみる", "海をみる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "パリで働く夫の帰りを待ちながら、海の美しい島に暮らすで生後10カ月の娘と2人で静かな日々を送っているサーシャ。そんなある日、彼女のもとにタチアナと名乗るバックパッカーの若い女性が現れ、2～3日の間、庭先にテントを張らせてほしいというのだが。", "2004-05-28", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIREFOX", "ファイヤーフォックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "アメリカ空軍の秘密部隊でミグ25の飛行訓練を受けた予備役軍人のミッチェルが、有事に備えて特別秘密司令を受ける。それはソ連で完成された、操縦することなく、パイロットの意志で操ることのできる、新鋭戦闘機ファイヤーフォックスを盗み出すという計画だった。SFXの雄アポジー社が総力を挙げたラストの空中戦シーンは見事。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LANDSCAPE SUICIDE", "ランドスケープ・スーサイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ランドスケープの作家として実験意欲に富んだ作品を発表してきた、J・ベニングが描く“風景の犯罪”。アメリカで実際に起こった二つの事件。一つはカリフォルニアで15歳の少女バーナデッド・プロッティが、自分より人気のあるクラスメイトをナイフで刺殺した事件、もう一つはウィスコンシン州で農夫エドワード・ゲインが、３年間で15人の女性を殺害した猟奇事件。この二つの殺人事件の犯人を俳優に演じさせ、その証言を再現しながら、カリフォルニアとウィスコンシンの現場の風景を交錯させていく。フィクションなのかノン・フィクションなのか、その境界を侵犯するかのようなユニークな異色作。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三毛猫ホームズの推理〈ディレクターズ・カット〉", "三毛猫ホームズの推理〈ディレクターズ・カット〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "1996年９月にTV放映され、好評を呼んだミステリー・ドラマの劇場公開バージョン。警視庁の刑事・片山は、函館の女子大で起きた殺人事件の調査に着手。事件には売春組織が関わっているようだが、捜査は難航するばかり。頭を悩ませる片山は、ある日“推理する”不思議な三毛猫と出会い……。赤川次郎作品の大林宣彦による映像化は「ふたり」「あした」に続いて３度目。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("英雄本色", "男たちの挽歌", "", "", "http://ecx.images-amazon.com/images/I/51O6Z8f%2B-GL._SL210_.jpg", 95, "それまでのカンフー映画の流れを一新し、“香港ノワール”の火付け役となった金字塔的作品。香港警察と国際ニセ札組織の対決を兄弟の反目と絆、男の友情を背景に描く。スローモーションを駆使したスタイリッシュなバイオレンス映像とチョウ・ユンファが撃ちまくる二丁拳銃の鮮やかさは、今なおアクションファンを唸らせる。キット役を初々しく好演した今は亡きレスリー・チャンにとっても、これが出世作となった。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真空地帯", "真空地帯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "陸軍刑務所から原隊復帰した木谷一等兵の反抗ぶりと、ひそかに彼を支持する曽田一等兵。皇軍の兵隊を作り出す内務班教育の非人間的な残酷さの中で、木谷は上官に復讐し脱走しようとするが失敗し、外地へ送られる。野間宏の小説を山本薩夫がリアルに描く。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Charlotte for Ever", "シャルロット・フォー・エヴァー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アルコール中毒の脚本家スタンとその娘シャルロットは、ひとつ屋根の下2人で暮らしていた。交通事故で母を亡くしたシャルロットは、事故に遭った車を運転していた父を憎んでいたが、スタンは近親相姦的な愛情をシャルロットに示すのだった。父スタンを演じるのは、監督、脚本、音楽も務めるセルジュ・ゲンズブール、そして娘をゲンズブールの実の娘、シャルロット・ゲンズブールが演じ話題となった。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モバイルハウスのつくりかた", "モバイルハウスのつくりかた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "", "2012-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PETER PAN", "ピーター・パン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "ロンドンに住むダーリング家の子供たち、ウェンディ、ジョン、マイケルはピーター・パンが大好き。ある晩、彼らの部屋に本物のピーターがやってきて、彼らはピーターと一緒に夢の国“ネバーランド”へ。大冒険が始まるのだった……。ディズニーの夢いっぱいのアニメ・ファンタジー。だれでもいつかは大人になっていくのだという寓話でもある。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四つの終止符", "四つの終止符", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "聴覚障害者の人々を対象にした活動で知られる劇団GMGが自主製作した西村京太郎原作の映画化。“母親殺し”の罪によって獄中で自殺したろうあ者の青年・晋一。そして、彼の無実を信じる祖父・進は、執念で真犯人を見つけ出すが……。身体障害者に対しての国家権力の横暴さを鋭く描く社会劇。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VOLAVERUNT", "裸のマハ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "フランシスコ・ゴヤの“裸のマハ”。この名画のモデルとされるアルバ公爵夫人の死をモチーフに、女たちの愛と憎悪、嫉妬と葛藤をスペインの巨匠ビガス・ルナが、ドラマティックに描く。ゴヤが総理大臣ゴドイの命を受けて世紀の名画“裸のマハ”を完成させたころ、社交界の華としてサロンに君臨していたアルバ公爵夫人が謎の急死を遂げる……。ハリウッドで活躍するP・クルスが、古巣スペインで大胆なヌードも厭わない体当たりの演技を披露。女優陣の火花散る共演も見もの。", "1999-01-01", "9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林寺十八銅人／THE 18 BRONZE MEN", "少林寺への道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "明朝末期。幼いころに少林寺に預けられた二人の少年が、父を殺した“鷹”と呼ばれる男への復讐を誓う。腕力強化、聴力集中、反射神経養成など、様々な修行に励む彼ら。ついに闘いの時がやってきたが……。パワフルでスピーディーな展開が快いカンフー・アクション。", "1981-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ある殺し屋", "ある殺し屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "もっぱら時代劇俳優として伝説的なまでの人気を博した市川雷蔵が、珍しく現代劇に取り組み、しかも殺し屋を演じるという作品。原作は藤原審爾の『前夜』で、普段は小料理屋の無口な板前だが、裏の顔は名を知られた殺人請負業で生きているという男の話である。暴力団・木村組から敵対するボス・大和田の殺人を２千万円で請け負った主人公・塩沢は競馬場、大和田邸、大和田の妾宅と大和田を狙ったが果たせず、遂に大和田主催のパーティーに芸人として潜入。畳針で一気に刺殺するというあざやかな仕事ぶりをみせる。しかし塩沢の報酬の横取りを狙う者がいた……。ニヒルなキャラクターにぴたりとはまった市川雷蔵の殺し屋ぶりが実に素敵だ。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("異域之末路英雄", "エンド・オブ・ザ・ロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "T・レオンが陰りのある軍人を演じた戦争ドラマ。第二次大戦後のビルマ国境付近には、祖国に見捨てられた国民党の兵士たちとその家族が孤立していた。若き将校ロンは軍人としての誇りを捨て、生き抜くために軍を去る決意をするが、その先には過酷な運命が待っていた。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUMBA", "ルムバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "戦前の大スター、G・ラフトの主演によるミュージカル調の作品で、彼の代表作「ボレロ」の続編にあたる。ハバナのダンサー、ジョーは富豪の娘と出会うが、彼女の恋人と衝突したことから店をクビに。やがてルンバを覚えた彼はブロードウェイの舞台に立つまでになるが……。実際に暗黒街でダンサーをしていたというラフトの見事なステップに注目。ビデオタイトルは「ルンバ」。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPPORT YOUR LOCAL SHERIFF", "夕陽に立つ保安官", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ゴールドラッシュで沸きかえる西部の町に忽然と現れた滅法強いガンファイターが町のダニを一掃するが……。「荒野の決闘」のクラントン一家を思わせるブレナンの悪役など、西部劇のパロディーも豊富なケネディの爆笑作。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GALLANT HOURS", "太平洋 紅に染まる時　山本元帥対ハルゼイ提督", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "好敵手といわれた山本五十六と、ハルゼイ提督。この日米両武将の性格を対比させながら、第二次大戦の様相をドラマティックに描いた戦争映画。飛行艇でガダルカナル島に向かっていたハルゼイ。その情報を入手した連合艦隊司令長官・山本五十六は、飛行艇の捕捉作戦に出る。だが不穏な動きを察知したハルゼイは、急遽、着陸地を変更、そこで彼は、南太平洋海域司令官任命の命令書を受け取る。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nikita", "ニキータ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "警官を射殺して捕らえられた麻薬中毒の少女ニキータは、政府の秘密機関に属する暗殺者になることを迫られる。3年後、特訓の末にプロの殺し屋になった彼女は恋に落ち、幸せな日々を望んで苦悩するのだった。監督は、「レオン」「フィフス・エレメント」のリュック・ベッソン。出演は、ニキータを演じ、当時、ベッソンの妻であったアンヌ・パリローほか、ジャン＝ユーグ・アングラードジャン・レノなど。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　刺青奇偶", "シネマ歌舞伎　刺青奇偶", "", "", "http://image.eiga.k-img.com/images/movie/54333/315x210.jpg?2800", 88, "歌舞伎の舞台公演をスクリーンで上映する「シネマ歌舞伎」の第10作。2008年4月に歌舞伎座で上演され大好評を得た、「瞼の母」「一本刀土俵入」の長谷川伸の代表作。出演は中村勘三郎、坂東玉三郎ほか。生来の博打好きで江戸を追われた半太郎が身投げした酌婦のお仲を助けたことで、2人は晴れて夫婦となるが、半太郎は一向に博打を止めることができず……。", "2009-02-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホッタラケの島　遥と魔法の鏡", "ホッタラケの島　遥と魔法の鏡", "", "", "http://image.eiga.k-img.com/images/movie/54113/315x210.jpg?2700", 98, "女子高生の遥はある日、幼い頃よく遊んでいた武蔵野の神社で、捨てられたおもちゃで遊ぶきつねを見つける。そのきつねを追って、人間たちがほったらかしたものでできた異世界「ホッタラケの島」に迷い込んだ遥は、そこでかつて大切にしていた、母親の形見である手鏡を探し始めるのだが……。監督は「砂時計」の佐藤信介、主人公・遥の声を「ハッピーフライト」の綾瀬はるかが担当。", "2009-08-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マカオの竜", "マカオの竜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "日本に運び込まれたヒンズー教の名宝“ヒマラヤの星”を買い戻すべく、香港から30万ドル入りのスーツケースをさげた“マカオの竜”がやってくる。日本の組織は執拗に彼の命をつけ狙うが……。動物作家にしては珍しい題材の戸川幸夫の小説をベースにして、日本を舞台にアドベンチャー・アクションが弾ける。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEPARATE LIVES", "多重人格", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ある日、元刑事のトムは、美しく聡明な大学教授ローレンに、“夜、私を監視して欲しい”と依頼される。ローレンを尾行したトムは、夜の彼女はまったく別人格であることを発見する。自らをレナと名乗る彼女は、ボンテージ・ファッションに身を包み、ディスコで男を挑発する。翌日、トムの報告にショックを受けたローレンは、トムにレナが犯したかもしれない殺人の証拠の銃を見せる。本当の自分を知りたいローレン、彼女の疑惑を晴らしたいトム。二人はやがて愛し合うようになるが、そこにレナが現れて……。「ターミネーター２」のL・ハミルトンが珍しく妖艶なヒロインを演じる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOU'VE GOT MAIL", "ユー・ガット・メール", "", "", "http://ecx.images-amazon.com/images/I/51GC2C5JQ9L._SL210_.jpg", 119, "ニューヨークで母から譲り受けた小さな絵本の店を経営しているキャスリーン。“ショップガール”というスクリーンネームをもつ彼女は顔も知らない“NY 152”というメール相手にほのかな恋心を抱いていた。そんな彼女の店の近くに大手書店チェーンの店がオープンすることになる。だが、彼女のライバルともなるその会社の御曹司ジョーこそが“NY 152”で、彼もメールでしか付き合いのない“ショップガール”に恋をしていたのだ……。メールという現代的小道具を通して結ばれていく男女の恋を綴るラブ・ストーリー。「めぐり逢えたら」の主演コンビと監督が再び手を組み、ツボを心得た演技、演出でハートウォームな恋の物語を作り上げている。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BloodRayne", "ブラッドレイン", "", "", "http://ecx.images-amazon.com/images/I/31MKK5GHTPL._SL210_.jpg", 0, "「ハウス・オブ・ザ・デッド」のドイツ人監督ウーベ・ボルが、世界的人気を誇る同名ゲームソフトを映画化したバンパイア・アクション。18世紀のルーマニア。バンパイアと人間のハーフ「ダムフィア」であるレインは、自分の父親が「最強のバンパイア」と恐れられるケイガンで、母親を殺した張本人であることを知る。レインはケイガンへの復しゅうを果たすべく、孤独な戦いに身を投じていく。主演は「ターミネーター3」のクリスタナ・ローケン。", "2006-04-29", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEMENTIA 13", "ディメンシャ13", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "コッポラが、コーマンの下で撮り上げた劇場用映画デビュー作。アイルランドのとある古城で、一人の娘が殺される。それから数十年後、娘のたたりであるかのように、城の住人が次々と惨殺されていく。低予算ながら、恐怖感をそそる演出、完璧な映像美に監督の才気がうかがえる。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エクスクロス　魔境伝説", "エクスクロス　魔境伝説", "", "", "http://image.eiga.k-img.com/images/movie/53093/315x210.jpg?1887", 90, "上甲宣之による小説「そのケータイはXXで」を、「バトル・ロワイアルII／鎮魂歌」の深作健太監督＆「デスノート」の大石哲也脚本で映画化。人里離れた温泉地“阿鹿里村”を訪れたしよりと愛子は、些細な喧嘩から別行動を取ることに。その夜、部屋で過ごしていたしよりの近くで携帯電話が鳴り響く。謎の人物から“逃げないと足を切り落とされる”と警告されて戸惑うしよりを、村人たちが次々と襲い始める……。主演は松下奈緒と鈴木亜美。", "2007-12-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bridge on the River Kwai", "戦場にかける橋", "", "", "http://ecx.images-amazon.com/images/I/51LRPaGAcLL._SL210_.jpg", 155, "ピエール・ブールの同名小説を名プロデューサー、サム・スピーゲルと巨匠デビッド・リーンのコンビで映画化した戦争大作。第2次世界大戦下の1943年、ビルマとタイの国境付近にある捕虜収容所を舞台に、捕虜となったイギリス人兵士と、彼らを利用して橋を造りたい日本軍人たちの対立と心の交流を描く。出演はウィリアム・ホールデン、アレック・ギネス、早川雪舟。57年度のアカデミー賞では作品賞、監督賞を含む7部門で受賞した。", "1957-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー龍騎／EPISODE FINAL", "仮面ライダー龍騎／EPISODE FINAL", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "主人公の仮面ライダー龍騎をはじめ、13人のライダーが現実世界の裏側にあるミラーワールドに棲むモンスターと闘い、たった一人の生き残りをかけて闘い合う姿を描くTVシリーズの映画版。公開時にTVでは中盤を放送中にもかかわらず、本作では物語の最終章を見せている。残りわずか数人となったライダーたち。彼らの運命を握る神崎士郎は生存バトルの刻限をあと３日と宣言する。そんな中、ミラーワールドの扉が開き、モンスターが現実世界に溢れ出してくる……。同時上映は人気TVシリーズの映画版、正義を守るハリケンジャーたちが、宇宙から来たプリンセスを救うために戦う「忍風戦隊ハリケンジャー シュシュッとTHE MOVIE」。", "2002-08-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("All The President's Men", "大統領の陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "ウォーターゲート事件の真相究明のため、命をかけた調査を開始した2人の若い新聞記者。謎の人物・ディープスロートの手がかりを頼りに、権力の腐敗に肉薄していくが、妨害の手が忍び寄り……。実際に取材を進めた新聞記者の著書「大統領の陰謀」の映画化。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Soeur Sourire", "シスタースマイル　ドミニクの歌", "", "", "http://image.eiga.k-img.com/images/movie/55203/315x210.jpg?2586", 124, "1960年代のベルギー。小さなパン屋で育った自由奔放なジャニーヌ・デッケルスは、両親への反発から修道院へ入る。やがてジャニーヌは、修道生活を送りながら、歌うシスター「シスター・スマイル」としてレコード・デビューを果たす。そして彼女の作った歌「ドミニク」は、世界的な大ヒットを記録するが……。「スパニッシュ・アパートメント」のセシル・ドゥ・フランスが実在のアーティストを好演。", "2010-07-03", "2,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Remember Me", "リメンバー・ミー", "", "", "http://image.eiga.k-img.com/images/movie/55393/315x210.jpg?3614", 113, "「トワイライト」シリーズのロバート・パティンソン主演のラブストーリー。2001年夏のニューヨーク。ある事件がきっかけで、家族とは離ればなれに暮らしている反抗的な青年タイラー（パティンソン）は鬱屈した日々を送る中、アリーという少女と出会い、恋に落ちるのだが……。監督は「ハリウッドランド」のアレン・コールター。共演に「LOST」のエミリー・デ・レイビン、ピアース・ブロスナン、クリス・クーパー。", "2011-08-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和残侠伝・唐獅子牡丹", "昭和残侠伝・唐獅子牡丹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "宇都宮の石切場を取り仕切る榊組は、親分を失ってから新興の左右田組に押され気味だった。榊組の親分を斬った花田秀次郎は罪の償いに幾度か榊組の窮地を救う。榊組の幹部・三上圭吾と秀次郎は左右田組に殴り込み、石切り場で死闘が展開される。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙快速船", "宇宙快速船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "千葉真一の主演第３作で、TV『ナショナル・キッド』の映画版を狙って作られた特撮もの。空陸両用の快速船に乗って活躍するスーパーヒーロー、アイアン・シャープと、海王星から来た、謎の宇宙船団との戦いを描いている。圧巻は、ラスト20分の宇宙船による都市破壊シーン。その臨場感あふれる合成場面の出来は、東宝特撮映画と比べても見劣りしない。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sileni", "ルナシー", "", "", "http://image.eiga.k-img.com/images/movie/33743/315x210.jpg?4730", 123, "チェコの鬼才ヤン・シュワンクマイエル監督が、30年の構想期間を経て完成させた“哲学的ホラー”。シュールレアリズムに傾倒した立体アニメーションや実写とアニメーションを融合させた長編映画で国際的に高い評価を得ているシュワンクマイエル監督。エドガー・アラン・ポーやマルキ・ド・サドにヒントを得たという本作では、精神病院で拘束服を着せられる悪夢にうなされ続ける男性が奇妙な世界へと引きずり込まれていく様子を独特の映像で描く。", "2006-11-18", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADRI DI BICICLETTE", "自転車泥棒", "", "", "http://ecx.images-amazon.com/images/I/51JPPQR8ABL._SL210_.jpg", 88, "第二次世界大戦後、イタリアン・ネオ・レアリスモの存在を全世界に知らしめた名作。戦後混乱の都会で、唯一の商売道具の自転車を盗まれた父子が、まる一日、その行方を捜し歩く。その父子の眼を通して当時の困窮するイタリア庶民の姿をリアルに描き出す。", "1948-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAMA DAMA", "カティの愛した人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "記録映画作家としても定評のあるフィルスマイヤーが、戦後のミュンヘンを舞台に、当時の世相を反映させながら描いた恋愛映画。暗く厳しい戦争の後、瓦礫の山と化したドイツ・ミュンヘン。それでも人々は強くたくましく働き、祖国の復興に努めていく。そうした労働の中心となったのは、やはり戦争で夫や子供を亡くしたたくさんの女たちだった。やがて進駐軍がやってくると、廃墟にはジャズが流れ、若者たちはダンスに夢中になる。", "1991-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃えろ！太陽", "燃えろ！太陽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "暴力行為、教師への侮辱、カンニングなど劣等生番付けで断然トップの生徒が、サッカー部を率いる英語教師の熱心な人生指導によって立ち直り、サッカー部の主将として活躍していくまでを描いた青春もの。「でっかい太陽」の続編となる青春スポ根ドラマ。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大怪獣決闘・ガメラ対バルゴン", "大怪獣決闘・ガメラ対バルゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "大映のドル箱“ガメラ”。隕石のおかげで地球に舞い戻ったガメラが、ニューギニア生まれの冷凍怪獣バルゴンに氷づけにされるが、琵琶湖水中で逆襲に転じる。この作品は「大魔神」との２本立てで大ヒットを記録した。湯浅憲明が特撮監督を担当。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lord of War", "ロード・オブ・ウォー", "", "", "http://image.eiga.k-img.com/images/movie/1113/315x210.jpg?2026", 122, "史上最大の武器商人と呼ばれた男の半生をニコラス・ケイジが演じる風刺アクション。ウクライナで生まれて家族といっしょにアメリカに渡ったユーリーは、やがて武器の売買に目をつけ、世界有数の武器商人に成り上がっていくが。監督は「ガタカ」「シモーヌ」のアンドリュー・ニコル。ユーリーの逮捕を狙うインターポールの刑事役でイーサン・ホーク、ユーリーが仕事に引き入れる弟役でジャレッド・レト、ベテラン武器商人役でイアン・ホルムらが共演。", "2005-12-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伴淳・アチャコ・夢声の活弁物語", "伴淳・アチャコ・夢声の活弁物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "無声映画の黄金時代の物語。当時の活動写真には必ず弁士と呼ばれる説明者がつき、話術巧みに映画を語っていた。その活弁に憧れて山形から大阪にやって来たズーズー弁の男が、あれやこれやの努力の末にようやく弁士になる。しかし、時代はサイレントからトーキーへ。職を失った弁士は旅回りの日々となる。映画の転換期を背景とした風俗人情喜劇。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUBLE TAP", "ザ・クリーナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ヒットメーカーのJ・シルヴァー、R・ドナーが設立したディケイド・ピクチャーズの第１回作品。暗い過去を持ち、ロサンゼルスにはびこる悪人ばかりを掃除していく一匹狼の殺し屋サイファ。麻薬組織に立ち向かう彼の孤独な闘いを、S・レイがいぶし銀の存在感で静かに熱演する。主人公と心を通わせる女捜査官に扮したブロンド美女、H・ロックリアも魅力的。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("甲虫王者ムシキング／　グレイテストチャンピオンへの道", "甲虫王者ムシキング／　グレイテストチャンピオンへの道", "", "", "http://ecx.images-amazon.com/images/I/51SMS7G0G3L._SL210_.jpg", 50, "子供たちの間で大ブームを起こしたアーケードゲームの中でも、カリスマ的人気を呼ぶ甲虫バトルゲーム『甲虫王者ムシキング』。TVアニメにもなった同作の劇場アニメ版。主人公は最高のムシバトラーを目指す未来ケントと相棒のムシキング。そんな二人の前に謎の少年、溝呂木シローとダークサイド・ネプチューンオオカブトという最強のライバルが現れ、雌雄をかけたムシバトルが繰り広げられる。その完全オリジナル・ストーリーに注目だ。ムシバトラーの未来ケントと甲虫王者ムシキングは最強を目指し、ムシバトルに明け暮れる。そんな彼らの前にダークサイド・ネプチューンオオカブトが出現。ケントは戦いを挑むが……。", "2005-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼の詩", "鬼の詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "明治末期、グロテスクなまでの珍芸で人気を得た芸人・桂馬喬の破格な人生を描き、直木賞を受賞した藤本義一の同名小説を、大映を離れてフリーとなった村野鐵太郎監督が借金しながら映画化。芸人の執念と映画人の執念が画面にだぶり、観る者を圧倒していく。主人公の妻役の片桐夕子が好演。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MACARTHUR THE REBEL GENERAL", "マッカーサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "第二次大戦中は連合軍総指令官として、戦後は、日本占領軍GHQの責任者、あるいはレッド・パージの推進者として、歴史に名をとどめたマッカーサー元帥の半生を描く。実際の記録写真も随所に挿入し、かなり史実に忠実に再現された異色戦争映画。ペックが、マッカーサー本人に勝る（？）男性的魅力を発揮。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("なで肩の狐", "なで肩の狐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "花村萬月の初期作品を、「フリージア・極道の墓場」の渡辺武監督が映画化したハードボイルド。元ヤクザの風来坊が、悪友から“預かり物”を頼まれたことから、再び危険な世界に舞い戻っていく。奇妙な成り行きから、空虚な結末までを描いている。ユーモア交じりの語り口で、ストレートな仁侠映画とは一線を画するストーリーが奏でられる。主人公と敵対する悪役を鶴見辰吾が怪演している。終盤における彼の踏んばりが、味わい深い“おかしみ”を生んでいる。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("StreetDance", "ストリートダンス TOP OF UK", "", "", "http://image.eiga.k-img.com/images/movie/57333/315x210.jpg?3806", 98, "ストリートダンス選手権で優勝を目指す若者たちの成長を描く青春ダンスムービー。ストリートダンサーのカーリーは、恋人ジェイの率いるダンスチームでUKストリートダンス選手権優勝を目指していたが、ジェイのチーム離脱や稽古場の喪失など困難が続く。そんなある日、バレエ学校の教師ヘレナが、生徒たちをチームに入れることを条件にバレエ学校のスタジオを稽古場として提供すると提案。スタイルの全く異なるストリートダンサーたちとクラシカルなバレエ学校の生徒たちの間には深い溝が横たわっていたが、次第に互いに影響を与え合い成長していく。イギリスの人気ブレイクダンサー、ジョージ・サンプソンやダンスパフォーマンス集団「ダイバーシティ」「フローレス」らトップダンサーが多数出演。", "2012-01-07", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("島育ち", "島育ち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "見渡すかぎりの青い海に囲まれた奄美の島を背景に、大島紬の織り子で純朴な島一番の美しい娘・しのぶと、東京から製糖工場の視察にやって来た素朴な男との愛情を描いた田端義夫のヒット・メロディの映画化。岩下志麻が可憐。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STROP", "天井", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 42, "「リンゴゲーム」「ひなぎく」などで知られるチェコの女性監督、V・ヒティロヴァが、映画大学（FAMU）の卒業製作として撮った処女作。人生に退屈しかかっているファッションモデルのマルタをヒロインに、夏休みという特権的な季節を背景にしながら、キラキラとした夏の光を、若者たちが一瞬みせるきらめきを、そしてしなやかな女の身振りを、モノクロのフィルムのなかに焼き付けた、まさにヌーヴェル・ヴァーグを継承するみずみずしさにあふれた佳作。友人の学生の一人として、当時の盟友であり、現在はチェコを代表する監督として活躍するE・メンツェルが登場する。", "1962-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("代紋・地獄の盃", "代紋・地獄の盃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「代紋・男で死にたい」の続編。前作で刺された梧井組の親分が全快するが、縄張りを狙う的場組が実力行使に出てくる。梧井組の代貸・鉄五郎がこれを阻止するというもの。小林旭が“片目の一本松”という、一匹狼のヤクザ役で客演。この年だけで11本の任侠映画に出演した高橋英樹の好演も見もの。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("梟の城", "梟の城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "1963年に「忍者秘帖・梟の城」として映画化されている司馬遼太郎の同名小説に、かつて司馬原作の「暗殺」を手掛けた篠田正浩が挑戦した忍者活劇。16世紀末期、豊臣秀吉暗殺の密命に自身の存在証明をかけた伊賀忍者の生き残り、葛篭重蔵の姿を追っていく。元忍者でありながら出世を望み、武士に転身した風間五平との戦いが物語の中心。血しぶき舞う殺陣場面など派手な見せ場はふんだんだが、二人の女忍者との恋模様や秀吉の本音なども織り込まれている。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風速40米", "風速40米", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "雑誌『平凡』に連載されていた松浦健郎の原作を、石原裕次郎と北原三枝のコンビで映画化したアクション大作。北大建築学科の学生・滝颯夫は小さな建設会社の工事部長の任にある父親が、競争相手の大会社に買収され、ビル工事を遅らせていることを知る。しかし父親は、自分が単に利用されているだけだと悟り、颯夫とともに突貫工事でビルを完成させる。蔵原惟繕監督の才気がまったく感じられない凡庸な作品だが、クライマックスの工事中のビルに、風速40米の台風が襲ってくるシーンは迫力十分で「鷹と鷲」（1957）の暴風雨シーンに続き日活特撮陣が再び技術の冴えをみせる。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Total Eclipse", "太陽と月に背いて", "", "", "http://ecx.images-amazon.com/images/I/51GFJZ566NL._SL210_.jpg", 112, "19世紀、フランスの天才詩人アルチュール・ランボーと、流行詩人ポール・ヴェルレーヌの破滅的な愛を描く。1871年、パリ。27歳で詩人としての名声を手にし、財産家の娘マチルダと幸せな結婚生活を送るヴェルレーヌの元に、ランボーから詩が届く。彼の天才ぶりに気付いたヴェルレーヌは16歳のランボーを呼び寄せて、家に迎え入れる。ヴェルレーヌは生活をともにするうちにランボーの才能と容姿に魅せられ、やがて肉体関係を結ぶ。二人は同性愛を育みながらヨーロッパ放浪の旅に出るが、妻マチルダとランボーの間で揺れ動くヴェルレーヌは酒浸りになり、二人の愛は破滅へと向かっていく。", "1996-10-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALAMO BAY", "アラモベイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1975年サイゴン没落後、多くのベトナム難民がアメリカに渡った。青年ディンもその一人。彼はテキサスのアラモの町でエビ漁に従事する仲間のところに落ち着く。雇い主の娘グローリーは親切だったが、漁業不振もあり、白人の漁師たちとの対立が表面化する。人種差別団体KKK団に煽動された漁師たちは、グローリーの恋人シャーンを先頭にベトナム難民を町から追い払う。だが、ディンは脅迫に屈せず残り、そのためシャーンたちの襲撃にあうが、その時、グローリーのピストルが火を吹く。アメリカに移住したL・マル監督が実際にあった事件をもとに描いた社会派ドラマ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("典子は、今", "典子は、今", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "26倍という難関を突破して熊本市役所に就職。サリドマイド児として両手がないままに生まれながら、訓練によって足を手と同じくらい使えるようにし、見事障害を克服した辻典子の生き方を劇映画化した作品。彼女本人の出演に心を打たれる。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秋聲旅日記", "秋聲旅日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "金沢が誇る文豪、徳田秋聲の『挿話』『町の踊り場』など４作品をもとに、青山真治がデジタルビデオで綴った“ある作家の放浪”。現代の金沢を舞台に明治に生きた文豪の心象風景を描出する静かなる意欲作で、たゆたうような時間感覚が素晴らしい。アーティストと客席と楽曲の境目が氷解した終盤のライブ・シークエンスも秀逸だ。金沢に近い空港に降り立った作家、徳田秋聲は、甥の辰之助とともに料亭、そして宿屋へ。宿屋の娘、お絹に心惹かれた彼は、離れの二階に部屋をとる。だが、秋聲は自分の思いを彼女に告げぬまま、故郷を後にするのだった。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Horrible Bosses", "モンスター上司", "", "", "http://image.eiga.k-img.com/images/movie/56623/315x210.jpg?3144", 98, "ニック、カート、デイルの3人は、それぞれ耐えがたい上司の下で毎日うんざりする日々を送っていた。自ら会社を辞めるつもりはない3人は、少しでも仕事をマシにするため、怪しげな酒飲みの元詐欺師のアドバイスを受けて「上司排除計画」を練る。しかし、いざ計画を実行に移すとそこには大きな落とし穴が……。ジェイソン・ベイトマン、チャーリー・デイ、ジェイソン・サダイキスが主演。3人の上司役でジェニファー・アニストン、コリン・ファレル、ケビン・スペイシーが共演するコメディ。", "2011-10-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WOLF", "ウルフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "狼男への変身という題材をラブ・ストーリーに仕立てたロマンティック・ホラー。狼に噛まれたことから人生が一変する、うだつの上がらぬ中年男ウィル。“野性”に目覚めた彼は、仕事にもやる気を取り戻し、社長の令嬢ローラと激しい恋に落ちる。そんな折、謎めいた殺人事件が続発。自分の仕業かもしれないという疑念に駆られたウィルは、理性と野性の間で苦悩することに……。ダークな色合いを基調にした映像がゴシック風の美しさを漂わせている。オスカー受賞歴のある名手リック・ベイカーが特殊メイクを担当。J・ニコルソンとM・ファイファーの、“美女と野獣”ともいえそうなスター共演もうれしい。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("半次郎", "半次郎", "", "", "http://image.eiga.k-img.com/images/movie/55643/315x210.jpg?4647", 0, "俳優・榎木孝明が企画・主演を務め、幕末から明治の動乱を西郷隆盛らとともに駆け抜けた薩摩藩士・中村半次郎の半生を映画化。並みはずれた剣の腕で西郷隆盛に重用されるようになった半次郎は、戊辰戦争での活躍が評価され明治新政府の陸軍少将に抜擢される。だが、新政府の腐敗に業を煮やした彼は、西郷の下野と共に薩摩へ帰郷。やがて西郷を擁して新政府に反旗を翻す。半次郎の盟友・弥一郎役に、本作が時代劇初挑戦となるEXILEのAKIRA。", "2010-10-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RED LANTERNS", "夜霧のしのび逢い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "封切当時、ムードあふれる題名とテーマ曲で大成功したギリシャ映画。港に面した歓楽街。そこに生活する様々な娼婦たちの愛と悲しみを描く。あまりにも有名なギターを使った主題歌は、実は“日本製”で、しばらくこの方式が流行したこともある。", "1963-01-01", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NANOU", "風の中の恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "フランス北部で、気ままな一人旅をするイギリス生まれの少女ナヌー。ある時、汽車の中で二人のフランス人男性と知り合った彼女は、そのうちの一人と恋仲になり、彼の住む村に行く。だが、いつしか自分が“外国人”であることを感じるようになる。繊細な女心を鮮やかに描いた、女流監督テンプルマンのスクリーン・デビュー作。", "1986-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("This property is condemned", "雨のニューオリンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "30年代のアメリカ南部の小さな町。鉄道従業員の不良分子摘発のためにやってきたオーウェンは、滞在する下宿屋の娘アルバと愛し合うようになる。そんなある日、解雇された従業員がオーウェンを袋叩きにしてしまう。オーウェンとアルバはニューオリンズへ旅立とうとするが……。テネシー・ウィリアムスの戯曲の映画化。脚本を手がけたコッポラに加え、レッドフォードやブロンソンの共演など、豪華な顔ぶれも見どころ。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YES", "愛をつづる詩〈うた〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「タンゴ・レッスン」のサリー・ポッター監督によるラブ・ストーリー。冷え切った夫婦生活に絶望する北アイルランド出身の中年女性と、祖国に幻滅してイギリスへと逃亡してきたレバノン人男性の愛の軌跡が描かれる。激動する世界情勢への監督のメッセージが込められたポエティックな台詞の数々が印象深い。冷めた夫婦関係に耐えられない妻。ストレスが溜まる一方の彼女はあるパーティで出会ったレバノン人と恋に落ち、安らぎを得るが……。", "2005-10-15", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海軍特別年少兵", "海軍特別年少兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "太平洋戦争末期、海軍史上最年少の少年兵たちが、防人として、祖国のためと信じ疑うことも許されずに死んでいった……。「ああ声なき友」の脚本家・鈴木尚之と再び組んだ今井正が、独特の詩情で語る、東宝“８・15”もの第６作。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("It Might Get Loud", "ゲット・ラウド　ジ・エッジ、ジミー・ペイジ、ジャック・ホワイト×ライフ×ギター", "", "", "http://image.eiga.k-img.com/images/movie/56823/315x210.jpg?3562", 97, "「U2」のジ・エッジ、「レッド・ツェッペリン」のジミー・ペイジ、「ザ・ホワイト・ストライプス」のジャック・ホワイトの3人のギタリストに迫ったドキュメンタリー。ロンドン、ダブリン、ナッシュビルと3人がそれぞれ自らのルーツとなる土地を訪れ、これまで歩んできた道を語るほか、3人が1つのステージに集いジャム・セッションを繰り広げる姿をカメラに収めた。監督はアカデミー賞を受賞した「不都合な真実」のデイビス・グッゲンハイム。", "2011-09-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明日の太陽", "明日の太陽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 7, "大島渚が松竹入社後に監督としてはじめて手がけた、松竹新人スター紹介用のショートフィルム。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刑事物語４ くろしおの詩", "刑事物語４ くろしおの詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "護送中の犯人に逃げられたため免職になった片山は、キャバレーに再就職。実は、麻薬に絡んだ殺人事件の捜査のために署長が仕組んだことだった。腕を買われた片山は山梨組の身内に。片山の素性を知る組長は、若頭の企みを阻止するよう片山に頼むのだった。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY ARCHITECT：A SON'S JOURNEY", "マイ・アーキテクト／ルイス・カーンを探して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "死後30年が経った今も世界中に影響を与え続ける建築家、ルイス・カーンの生涯をたどるドキュメンタリー。３つの家庭を持つなど、波乱に満ちた生涯を送り、建築界最後の巨匠と呼ばれた人物の偉大さと意外な素顔に触れられる１本だ。20世紀を代表する天才建築家であり、志半ばで謎の死を遂げた、ルイス・カーン。現代建築を語る上で欠かせない彼の数奇な人生を、友人らの証言を基に実の息子ナサニエル・カーンがたどる。", "2006-01-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森の石松", "森の石松", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "勝新太郎主演によるご存じ森の石松の一席。白塗りの二枚目時代劇スターとしてスタートした勝新太郎が、強烈な個性を発揮できず低迷していた時期の作品だが、森の石松というキャラクターは勝新にとって最初の手ごたえのある役であった。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　妖精リンリンのひみつ", "それいけ！アンパンマン　妖精リンリンのひみつ", "", "", "http://image.eiga.k-img.com/images/movie/53753/315x210.jpg?3711", 50, "子どもたちに根強い人気を誇るアニメ「それいけ！アンパンマン」の劇場版第20弾。アンパンマンの強さの秘密が\"勇気の花のジュース\"であることを知ったばいきんまんは、妖精リンリンが守っている勇気の花の台地を破壊してしまう。勇気の花を探す旅に出たリンリンとアンパンマンは、死神山でついに花を見つけるが、最強メカ・ズダダンダンに乗ったばいきんまんが現われ……。花の妖精リンリンを演じるのは「さくらん」の土屋アンナ。「ヒヤ・ヒヤ・ヒヤリコとばぶ・ばぶ・ばいきんまん」を併映。", "2008-07-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOW TO MURDER YOUR WIFE", "女房の殺し方教えます", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "自分が描いた作品を実際に試してみるのが趣味の売れっ子漫画家が、酔った勢いで結婚した新妻を使って、“女房殺し”の探偵ものを実行……。レモンが茶目っ気たっぷりに好演。笑いながらも思わず心打たれる人情喜劇である。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVA", "エヴァの匂い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "元坑夫で今は売り出し中の新進作家ティビィアンにはフランチェスカという婚約者がいるが、エヴァを知ってから仕事も婚約者も忘れ、激しくエヴァに溺れていく……。男を破滅させる魔性の女、エヴァをドラマティックに描いた名匠ロージーによるフィルム・ノワール。ビリー・ホリデイが歌う『柳よ泣いておくれ』が印象的。", "1962-01-01", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FIELD", "ザ・フィールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1930年代のアイルランドの寒村を舞台に、タイトル通り“土地（FIELD）”に対するある男の執着を描くことで、人間の業に鋭く切り込んだドラマ。土地にこだわる男に、R・ハリス、その土地をめぐってのライバルにT・ベレンジャーと、英米屈指のシブい俳優を配し、作品を盛り上げている。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("East of Eden", "エデンの東", "", "", "http://ecx.images-amazon.com/images/I/51N3xLrrdlL._SL210_.jpg", 115, "旧約聖書のカインとアベルの物語を下敷きにしたジョン・スタインベックの同名小説を、「欲望という名の電車」「波止場」の名匠エリア・カザン監督が映画化した青春ドラマ。1917年のアメリカ・カリフォルニア北部の町サリナスを舞台に、孤独な青年キャルの苦悩や家族との確執を描く。主演は本作が映画初出演となるジェームズ・ディーン。共演にジュリー・ハリス、ジョー・バン・フリート。音楽はレナード・ローゼンマン。", "1955-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES BONNES CAUSES", "黒い情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "心臓病の大金持ちデュプレ氏は、看護婦から注射を受けている最中に発作を起こして死亡する。夫人は看護婦を故意の殺人罪で起訴。看護婦の容疑は深まるのだが……。C・ジャックによるサスペンス・ドラマ。", "1963-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Tomb", "ザ・トゥーム（原題）", "", "", "http://image.eiga.k-img.com/images/movie/58013/315x210.jpg?2759", 0, "", "2014-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Congo river, au-dela des tenebres", "コンゴ・リバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "世界有数の熱帯雨林地帯を流れるアフリカの大動脈・コンゴ河を題材にしたドキュメンタリー。河口から世界最大の流域を誇る水源までの全長4371キロメートルを、コンゴ河の流路を発見した殖民開拓者スタンリーの軌跡をたどってさかのぼっていく。スタンリーやレオポルド2世、アフリカの指導者ルムンバやモブツといった歴史的人物に思いをはせつつ、流域で暮らす人々や厳しい自然、そして紛争による傷跡などをありのままに映しだしていく。", "2010-06-12", "37,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("温泉女医", "温泉女医", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "若尾文子が、知的な中にも色香を感じさせる女医に扮した人情喜劇。伊豆にある町・子宝温泉。ここで唯一の医者である藪内は、高血圧の老人。その代診に、美貌の女医・塩月イサオがやってくる。町で彼女は人気を集め、一方では藪内の息子・昌彦との恋も芽生えていき……。ミヤコ蝶々、南都雄二、林家三平らが、コメディ・リリーフとして出演。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LONG KISS GOODNIGHT", "ロング・キス・グッドナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "R・ハーリンが100億円という破格の製作費を投じて撮り上げたアクション大作。記憶喪失の主婦サマンサが交通事故の衝撃で過去の断片を思い出した。実は、彼女の正体は政府の秘密工作員。すべての記憶を取り戻すべく、彼女は私立探偵のミッチと旅に出る。だが、行く手にはサマンサの過去を知る何者かの陰謀が待ち受けていた……。クライマックスの爆破シーンをはじめとするスケールの大きな見せ場は、まさにハーリンの真骨頂。射撃と格闘の訓練を受けて、ほとんどのシーンをノースタントでこなしたヒロイン、G・デイヴィスの体当たりの熱演も見もの。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BANKER", "ザ・バンカー 薔薇の殺意", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "昼は銀行家としての顔を持ち、夜は殺人に情熱を傾ける億万長者。南米の古代宗教にどっぷりとつかり、武闘家としても超一流の腕を持つ彼は、生贄を追うごとく、執拗に殺人を繰り返す。野心いっぱいの女性レポーターと、ロス市警の威信をかけた刑事がこの男を追うのだが……。異色サスペンス・スリラー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前大学", "喜劇・駅前大学", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "かつての落ちこぼれ浪人生たちの20年後。徳之助は今や東台大学教授。孫作は駅前デパートの店長となり、三平は大学近くの雀荘のオヤジ。なぜかウマの合う３人組に大学の体育教師・次郎も加わってのてんやわんや。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Soul Surfer", "ソウル・サーファー", "", "", "http://image.eiga.k-img.com/images/movie/56283/315x210.jpg?4757", 106, "サメに襲われ左腕を失いながらもプロを目指して再起した実在のサーファー、ベサニー・ハミルトンの実話を映画化。2003年、ハワイの海とサーフィンをこよなく愛する13歳の少女ベサニーは、サーフィン中にサメに襲われ左腕を失ってしまう。すべてに絶望し、サーフィンを辞めて新しい生活を始めたベサニーだったが、どうしてもサーフィンを忘れることができず、再びプロサーファーを目指すことを決意。家族に支えられながら厳しい特訓を繰り返し、競技大会に出場する。主演は「テラビシアにかける橋」のアナソフィア・ロブ。", "2012-06-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HURRICANE", "ハリケーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "1937年に作られたスペクタクル映画の古典的大作「ハリケーン」のリメイク版。1920年代、アメリカ統治下のサモア諸島を舞台にした、アメリカ娘とサモア青年の禁じられた恋の物語。40億円もの製作費がかけられただけあって、ラストのハリケーン・シーンは壮観。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Big Doll House", "残酷女刑務所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "", "1972-02-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG GREEN", "ビッグ・グリーン　でこぼこイレブン大旋風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "スポーツ・コメディを得意とするディズニーが、アメリカでは比較的マイナーとされるサッカーを採り上げたファミリー・ピクチャー。テキサスの田舎の小学校に赴任してきたイギリス人女性教師アンナが、気のいい地元の保安官トムのサポートを得て、まるで覇気のない子供たちにサッカーの魅力を植え付けていく。弱小チームが地区大会で奇跡の快進撃を果たす展開は定石通りだが、目を覆うばかりの凡プレーを連発していた少年少女が華麗なボール回しを披露するクライマックスの試合シーンは痛快そのもの。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NUTTY　PROFESSORII／THE　KLUMPS", "ナッティ・プロフェッサー2／クランプ家の面々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "E・マーフィが７役に挑戦したことでも話題となった大ヒット・コメディの第２作。今回、エディは前作を上回る９役を演じている。前作で驚異のやせ薬を開発したクランプ教授は、その時誕生した別人格プレイボーイのバディ・ラブの幻影に悩まされていた。そんな彼が若返りの薬の開発に成功、同僚のデニースとの恋も順調と幸運の連続。そこでクランプはこの幸せを守るために、自分のなかの悪性の遺伝子バディを抽出する実験を行う。しかし、バディのDNAが自らを再生をして肉体を持ち、若返りの薬の奪取を計画。それだけでなく実験の後遺症でクランプの知能が低下していく。そのうえ、若返りの薬を飲んだクランプ一家の人々が騒動を起こし、事態は収拾不可能な大パニックに発展する……。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("濡れ髪喧嘩旅", "濡れ髪喧嘩旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "市川雷蔵主演によるコメディ・タッチの股旅もの。ヘッポコ役人の遠山金八郎は、悪代官の不正摘発の命を受けて旅立つが、つつもたせにひっかかり、おさらば伝次というお調子者に助けられる。かくて意気投合した二人の珍道中が始まる。市川雷蔵と川崎敬三がズッコケコンビを軽妙に演じる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("片目だけの恋", "片目だけの恋", "", "", "http://ecx.images-amazon.com/images/I/51SAPqyACBL._SL210_.jpg", 88, "日本映画界を代表する監督と新人監督たちが作品を競いあう「映画番長」シリーズ第2弾「エロス番長」の1本として、ピンク映画界の巨匠・渡辺護監督が放つ純愛ストーリー。幼い頃に母を亡くし、父と2人で暮らしている女子高生ユカは、9歳の頃からずっと、従姉妹の夫である大学講師・井上を密かに思い続けてきた。そんなある日、井上夫妻が近所に引越してきたことから、今まで押し殺してきたユカの欲望が燃えあがっていく。", "2004-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふたりはプリキュア／　マックスハート２　　雪空のともだち", "ふたりはプリキュア／　マックスハート２　　雪空のともだち", "", "", "http://ecx.images-amazon.com/images/I/617SC6M3XCL._SL210_.jpg", 70, "キッズ世代の女の子から熱烈な支持を受ける人気TVアニメの劇場版第２弾。光の使者“プリキュア”の３人がスキー旅行へ。世界に光を与える鳳凰の雛を発見した彼女たちが、その鳥をある陰謀を企む闇の使者から守るために大活躍する。物語の鍵を握るひなたなど、新キャラクターの活躍も注目。スキー旅行に出かけたひかりは、そこで綺麗な雛鳥を見つける。すると、その雛を狙い闇の使者が出現。彼女は雛を守ろうとするが……。", "2005-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRUE COLORS", "トゥルーカラーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「セイ・エニシング」などのさわやかな青年役を得意とするJ・キューザックが“悪”を演じ、「セックスと嘘とビデオテープ」などのエキセントリックなイメージのJ・スペイダーが“善”を演じる。同じ大学で法律を学ぶピーターとティムは厚い友情で結ばれていた。ピーターは政界へ、ティムは司法省へとそれぞれの道を歩む。しかし野心に満ちたピーターは、徐々に権力を身につけ、裏金に手を染め、ティムの恋人を奪い、司法省で働くティムをも利用、出世の階段を登り詰めようとする。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUADALCANAL DIARY", "ガダルカナル・ダイアリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "2006年に公開された「父親たちの星条旗」「硫黄島からの手紙」の舞台、硫黄島の戦いより前、真珠湾攻撃の後に繰り広げられたガダルカナル島での日米決戦を捉えた戦争映画。戦争中に製作されただけに、その迫力はリアリティーに満ちている。アメリカの海兵隊が島に上陸したが、山中に潜んでゲリラ戦を展開する日本軍に苦戦を強いられる。しかし……。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地の涯に生きるもの", "地の涯に生きるもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "動物文学の大家・戸川幸夫の『オホーツク老人』の映画化。厳寒の知床半島の冬、たった一人で漁師の番屋を守る老人・村田彦七。“留守番さん”と呼ばれ、ネズミ払いのネコだけを話し相手に、彼は幾度もこれまでの人生を反芻する。漁師として独立したあの日、無理矢理づくでの結婚を経て３人の息子に恵まれた様々な思い出。ある夏、死んだ息子の恋人だったという都会の娘・冴子が彼のもとを訪れ、懐かしい話をしてくれた……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЧУЧЕЛО／CHUCHELO", "転校生レナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "ペレストロイカ以前に旧ソ連の教育現場、少年少女たちのいじめの実態を鋭く描き、国内で大論争を展開、公開が２年も遅れたといういわくつきの問題作。モスクワからある地方都市の学校に転校してきた13歳の少女レナは、密告者の罪をかぶったために、クラス中の全員からのいじめを受けてしまう。", "1983-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空の穴", "空の穴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "「鬼畜大宴会」で賛否両論を巻き起こした熊切和嘉の第２作。前作とはガラリと印象を変えた切なく優しい人間ドラマだ。北海道の古びたドライブイン“空の穴”の料理人、市夫。淡々と毎日を過ごしていた彼はある日、旅行途中で恋人に捨てられた女性、妙子と出会い、彼女をバイトとして雇う。妙子に惹かれていく市夫。心が通じたように見えた二人だったが、妙子の元恋人を見つけた日から微妙に心がズレ始める。閉じこもっていた“穴”から這い出ようとする男。そんな市夫の不器用すぎる感情の機微を、寺島進がさりげなく好演。限りなく青い空に希望が広がるラストも印象的だ。第30回ロッテルダム国際映画祭で国際批評家連盟賞を受賞。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAISY MILLER", "デイジー・ミラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "H・ジェームズの古典的恋愛小説を、「ペーパー・ムーン」をヒットさせた直後のP・ボグダノヴィッチが正攻法で映画化。19世紀末のヨーロッパ。アメリカから単身やって来た一人の娘が、ヨーロッパ社交界の中でもまれながらも恋をし、そして自身を貫いて生きていく姿を綴る。全編イタリアにロケした美しい風景をバックに、世紀末の風俗を再現した華麗な美術（ベルトルッチ作品などで知られる、F・スカルフィオッティが担当）が見もの。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE JUNGLE BOOK", "ジャングル・ブック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "キプリング作『ジャングル・ブック』の中の『狼少年』の映画化。インドのジャングルで父親を喰い殺され、森に迷い込んで以来、狼によって育てられた少年の冒険物語。英国の名プロデューサー、A・コルダがハリウッドで製作した娯楽大作。", "1942-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONDO　TOPLESS", "モンド・トップレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "巨乳好きの異才ラス・メイヤーが最愛の美女たちをズラリと配し、物語性を一切排除して描く妄想的な怪作。砂漠や荒野、森の中で、バストもあらわの女性たちが、ただひたすら踊る。有名なストリッパーたちによるセクシーなダンスの合間に、キャリアやブラのサイズ、男の趣味などを語るインタビューがインサートされている。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兵隊やくざ・殴り込み", "兵隊やくざ・殴り込み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "奪われた軍旗を取り戻すために敵地に乗り込み、大活躍する大宮の姿を描く。有田とイメージのダブるインテリ少尉役を細川俊之が熱演。壮絶な死に様を見せてくれる。第７作目にして、“兵隊やくざ”シリーズもついにラストで終戦を迎える。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORCED VENGEANCE", "チャック・ノリスの地獄の復讐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "国際都市・香港を舞台に、巨大シンジケートを敵に回して大暴れするベトナム帰りの男を描いた痛快編。アクション映画界で活躍するC・ノリスの魅力が満載。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月の出の決闘", "月の出の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "凄腕の用心棒として名高い天堂小彌太が、親分である銚子の次郎吉の賭場を取り返す。そのころ、学者の大原幽学は私塾で農民たちに教育を施し、賭場の撲滅に取り組んでいた。小彌太はやくざ渡世に邪魔な幽学に斬りつけるが、逆に幽学の優れた人格を思い知らされることに……。「狐の呉れた赤ん坊」の名監督・丸根賛太郎が阪妻を主演に撮り上げた秀作時代劇。DVDは「阪東妻三郎傑作選　DVD-BOX」に収録。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Outlaw Josey Wales", "アウトロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "アメリカ合衆国建国200周年記念映画で、クリント・イーストウッド監督の第5作。アメリカ南北戦争末期。家族を北軍ゲリラに殺され、復讐の旅に出た南部の農民ジョージー・ウェールズの粘り強い戦いが描かれる。共演にソンドラ・ロック、ジョン・バーノン、チーフ・ダン・ジョージ。脚本は「ライト・スタッフ」「存在の耐えられない軽さ」のフィリップ・カウフマン。", "1976-08-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monkey Shines", "モンキー・シャイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "　青年アランは交通事故に遭い、半身不随の身になってしまう。そんな彼に、脳科学研究所に勤務する友人ジェフリーが猿をプレゼントする。エラと名づけられたその猿には秘密があった。人間の脳細胞のエキスを注入されたことで知能が高められていたのだ。やがてアランの周囲で奇怪な事件が。エラはアランの思考を感知し、それを実行していく。そしてアランにも変化が……。驚異の医療データに基づいて書かれた小説を映画化。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面学園", "仮面学園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "『ぼくらの七日間戦争』で知られる宗田理の小説を映画化、藤原竜也が妖しい美少年を演じた青春ミステリー。川村有季の通う光陽館高校で、仮面を着けて別人のように生まれ変わった生徒が増え始め、それは仮面ブームという社会現象にまで発展していく。そんな中、有季の中学時代の同級生で、仮面パーティーを開いていた殿村が殺される。有季は事件の真相究明に乗り出すが、そんな彼女の前に仮面作りに魅せられた美少年、堂島暁が現れ……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUNAR COP", "アストロ・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "2050年、地球は退廃を極め、選ばれた者だけが希望の地である月に送られていた。そんなある日、月人類の存亡をかけた先端技術“アラマンス”が反逆者によって奪われ地球に持ち去られた。その“アラマンス”を取り戻すべく月国家から派遣された警官ジョー・ブロディは、野蛮人が住む無法の地、地球に降り立った……。M・パレ主演の近未来バイオレンス・アクション。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日蓮", "日蓮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "鎌倉幕府の弾圧に抗して、迫害と殉教の生涯をひたむきに生きた情熱の人・日蓮上人の一代記を、日蓮宗の信者でもある永田雅一が、自らのプロダクションで製作。重々しく貫禄たっぷりに演じる萬屋錦之介の日蓮に注目。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A TOUCH OF CLASS", "ウィークエンド・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "妻子ある中年のサラリーマン、スティーヴはある日、離婚歴のある中年女性ビッキと知り合いになり、やがて二人は恋に落ちる。ウィークエンドごとにしのび会いを続ける二人だったが、些細な行き違いから亀裂が次第に深まっていく……。実ることのない恋をほろ苦く描いた恋愛映画の佳作。", "1973-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Revolver リボルバー", "Revolver リボルバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "佐藤正午の同名小説を荒井晴彦が脚色、ベテラン藤田敏八がフットワーク軽く演出している。鹿児島県警の巡査部長・清水は勤務中、公園で中年サラリーマンの阿久津に頭を殴られ拳銃を奪われる。この一挺のリボルバーをめぐって、様々な人々が織り成す人間模様。冒頭の海水浴場のシーンで、ゼイ肉のついたお腹を水着でさらけ出す沢田研二をはじめ、不安と疲労にさいなまれている連中を一つの画面に集合させておきながら、物語が進むにつれてバラバラに解き放っていく構成が見もの。日本映画には珍しい青春群像劇の成功作で、藤田監督は数多い登場人物を手ぎわよく整理している。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE MONDE DU SILENCE", "沈黙の世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "海洋学者として世界的に有名なJ・クストーが、後に「死刑台のエレベーター」で衝撃的なデビューを飾る鬼才ルイ・マルと組んで海中の世界を神秘的に捉えた傑作ドキュメンタリー。イルカの大群、沈んだ難破船、鯨の死体に群がるサメなど様々なイメージが美しい海中撮影によって詩的に綴られていく。", "1956-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天然性侵略と模造愛", "天然性侵略と模造愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "浅野忠信の初監督作品「トーリ」をさまざまな角度から分析したドキュメンタリー「ソラノ」を手掛けた山岡信貴監督の異色作。生物学者の父を持つ青年が、予想もしない事態に巻き込まれていく。ミニマムな設定からスタートしながら、人類滅亡の危機にまで発展するストーリーが斬新だ。不可思議なメッセージを受け取ってからというもの異常な事態に遭遇するナナセ。やがて彼は自分と瓜ふたつの人間が存在することを知る。", "2005-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hitman", "ヒットマン", "", "", "http://image.eiga.k-img.com/images/movie/53103/315x210.jpg?1902", 93, "冷徹な暗殺者のエージェント47は、標的であるロシア大統領を射殺するが、何者かの密告で、インターポールとロシア連邦保安庁の双方から追われる身に。一体、誰が自分を陥れたのか？　真犯人を探る47は、事件の鍵を握る美しい娼婦ニカと出会う。同名の人気ゲームを映画化したアクション・サスペンス。主人公“47”には「ダイ・ハード4.0」のティモシー・オリファント。ニカ役のオルガ・キュリレンコは、本作の後「007」のボンドガールに抜擢。", "2008-04-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HUDSON HAWK", "ハドソン・ホーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ちょっとドジだが盗みの腕はピカーの大泥棒、怪盗ハドソン・ホーク。10年の刑期を終えて出所した彼は、世界制覇をもくろむ夫妻の巧妙な罠にはめられ、バチカン博物館に眠るダ・ビンチのスケッチブックを盗み出すことに……。「ダイ・ハード」シリーズで名を上げたB・ウィリスが自ら企画したピカレスク・ロマン。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCHLUSSAKKORD", "第九交響楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "戦後のハリウッドで、メロドラマの巨匠となったダグラス・サーク監督のドイツ時代の代表作（なお、本作では、ドイツ時代の表記デトレフ・ジールクとクレジットされている）。冷え切った夫婦関係に悩む高名な指揮者ガルフェンベルクと、薄幸の女ハンナの愛を描く。音楽映画、母もの、サスペンスと様々なジャンルの要素を巧みに交ぜ合わせ、大いに泣かせる。また、ナチス・ドイツ時代のベルリン国立オペラ管弦楽団の演奏場面もあり、歴史的な映像資料としての価値も高い。", "1936-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Knockaround Guys", "ノックアラウンド・ガイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「トリプルX」などでたくましさを披露したヴィン・ディーゼルが、デニス・ホッパー、ジョン・マルコヴィッチらの名優と共演した、マフィアの世界に生きる男たちの物語。兄弟よりも強い絆で結ばれた４人の男たちの姿を、裏切りと殺し合いのゲームを通して描き出していく。名優たちに囲まれながらも物怖じしない風格を見せるディーゼルが印象的。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("藍より青く", "藍より青く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "第二次大戦も敗色が濃厚になってきた昭和19年の九州・天草。周一青年には、結婚を約束し合った真紀という女性がいた。しかし彼は徴兵検査に甲種合格してしまう……。短い青春を精いっぱい生きる男女の姿が、天草の美しい自然を背景に描き出された佳編。山田太一原作のNHKテレビ小説を映画化したもの。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー×仮面ライダー W＆ディケイドMOVIE大戦2010", "仮面ライダー×仮面ライダー W＆ディケイドMOVIE大戦2010", "", "", "http://image.eiga.k-img.com/images/movie/55023/315x210.jpg?1493", 0, "世界の破壊者となってしまった士（つかさ）こと「仮面ライダーディケイド」は、次々と歴代仮面ライダーたちを倒していく。一方、「仮面ライダーW」である翔太郎とフィリップが営む鳴海探偵事務所に、幽霊を捜してほしいという不思議な調査依頼が舞い込んでくる。調査を始めた2人の前に、この世を去ったはずの探偵の師匠・鳴海荘吉が突然現れ、「仮面ライダーW」の誕生の秘密が徐々に明かにされていく。", "2009-12-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LAST　WITNESS", "黒水仙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "“韓国のスピルバーグ”との異名を持つ名匠ぺ・チャンホが描くサスペンス。監督と同様に1980年代の韓国映画を牽引した一人でキーパーソンに扮する名優アン・ソンギの存在感が圧巻だ。ヤン・ダルスという男が殺害された。この事件を追う刑事のオは朝鮮戦争時、ダルスが脱走捕虜を捕らえる任務に就いていたことを知る。やがて彼は、一冊の色あせた日記から事件の裏に隠された悲しい事実にたどり着く。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AVANT LE DE´LUGE", "洪水の前", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "４人の少年と一人の少女の姿を通して現代人特有の無批判な世相を告発した作品。５人のティーンエイジャーは彼らだけの幸福な生活を夢見るが、現実に押し流され盗みを働く。しかもその際に誤って警官を射殺してしまい、やがて裁判にかけられることに……。M・ブラディ他、子役たちの新鮮な演技が光る。", "1954-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンチェイン", "アンチェイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "阪本順治の「王手」「ビリケン」で脚本を務め、「ポルノスター」で鮮烈な映画デビューを飾った豊田利晃の第２作。ボクサー４人の現役、引退、その後の姿を５年にわたって追ったドキュメンタリー。６敗１引き分け、一度も勝てずに引退したアンチェイン梶。暴走族上がりのガルーダ・テツや在日朝鮮人の永石麿、熱血漢の西林誠一郎ら、ボクサーたちの兄貴分として慕われていた彼は、労働者に職を紹介する手配師として人生の再出発をする。だが、ボクシングの後遺症から奇行に走り始め……。戦うことに取りつかれた男たちは、栄光とは無縁のアウトサイダー。人生のすべてを懸けて戦いに挑んだ彼らの凄まじいハングリー精神と、血縁関係を超えた熱い絆が観る者の胸を熱くする。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギリギリの女たち", "ギリギリの女たち", "", "", "http://image.eiga.k-img.com/images/movie/57353/315x210.jpg?4332", 101, "", "2012-08-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間魚雷 あゝ回天特別攻撃隊", "人間魚雷 あゝ回天特別攻撃隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "毎日新聞社編『人間魚雷回天特別攻撃隊員の手記』を原作にした、戦争秘話を描く群像ドラマの大作。特攻隊と並ぶ玉砕戦術として知られる人間魚雷に情熱を燃やした人間たちの物語。日本の敗色が深まる1943年、大里中尉は新兵器の構想を練っていた。共感した三島少尉は命中度の向上を示唆。二人は研究しながら友情を結ぶ。そうして兵器は軍部で採用、“回天”と名付けられた。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEADTIME STORIES", "デッドタイム・ストーリー おとぎ話は血の匂い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "タイトルは“ベッドタイム・ストーリー”のゴロあわせで、寝つかない悪ガキに３つのお話を語って聞かせるオムニバス形式。本来お伽話が持っている深層的な恐怖を、リアルな特殊メイクによってカリカチュアしている。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE´TECTIVE", "ゴダールの探偵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "パリのサン・ラザールにあるコンコルド・ホテル。そこに居住する探偵、夫婦、ボクシングのプロモーター、マフィアの４組のグループ。お互いの関係があまりはっきりしないまま、ゴダール特有の話術の中で、彼らはホテルの中で堂々めぐりのゲームを繰り広げる。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Favela Rising", "ファヴェーラの丘", "", "", "http://image.eiga.k-img.com/images/movie/53523/315x210.jpg?2514", 81, "麻薬と暴力がはびこるリオ・デ・ジャネイロのスラム街\"ファベーラ\"。ギャングや腐敗した警察によって大切な人々を殺された元麻薬密売人のアンデルソン・サーは、街の子供たちに自分と同じ悲劇を繰り返して欲しくないという思いから、仲間と共に社会活動グループ\"アフロレゲエ\"を結成。音楽やダンスといった文化的な活動を通し、希望を見失ったファベーラの子供たちに正しい道を示そうとする彼らの姿を追ったドキュメンタリー。", "2008-04-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO　NUKES", "NO NUKES", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "原子力発電反対を主旨として、1979年９月19日～23日までマディソン・スクエア・ガーデンにて行われ“第２のウッドストック”とも称されたロック・フェスティバルを収めた音楽ドキュメンタリー映画。撮影は「夜の大捜査線」などの名匠H・ウェクスラー。出演メンバーもB・スプリングスティーンをはじめ、錚々たる顔ぶれである。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BILLABONG　ODYSSEY", "ビラボン・オデッセイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "最先端の天候予測テクノロジーを駆使して、地球規模でビッグウェーブを探し出しては、命がけの波乗りに挑戦するサーファーたちの冒険を描いたスポーツ・ドキュメンタリー。ヨーロッパ、タヒチ、ハワイと世界を股にかけるチャレンジ精神と、美しくも荒々しい大自然の映像に圧倒される。超一流のサーファーたちが結成したグループ“デルタフォース・オブ・サーフィン”が、最高のビッグウェーブを求めて、世界中を巡り歩く。", "2005-07-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤線", "赤線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "気鋭の映像クリエーターとして活動を続ける奥秀太郎が、昭和の伝説“赤線”を舞台に描く、耽美で猥雑でデタラメなSFラブ・ストーリー。“赤線”とは、終戦直後から1950年代まで存在していた公認の売春街の通称。戦後の混沌の象徴であり、溝口健二監督の「赤線地帯」など数々の傑作を生む原動力にもなった。過去の再現とは一線を画す極彩色のキッチュな“バーチャル昭和”を闊歩するのは、本作が映画初主演となった中村獅童。野田秀樹や荒川良々、今奈良孝行といった、奥監督のホームグラウンドである演劇界の俳優も大挙出演し、暴れ放題の怪演を披露している。連続強姦罪で投獄されていた“赤線地帯の女斬り”イゾウが出所した。“夏だ、新しい恋がしたい”と勝手な思いを抱くイゾウは娼婦のシズモと再会する。一方、作家のカフウもシズモに惚れ込み、娼館キムラヤに通いつめていた。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dancing at the Blue Iguana", "ブルー・イグアナの夜", "", "", "http://image.eiga.k-img.com/images/movie/1303/315x210.jpg?3934", 124, "カリフォルニア南部の町はずれのストリップ・クラブで働くショーガール5人。人気者のエンジェル、ワイルドなジョーなどステージで見せる顔の裏側に、彼女たちはそれぞれの人生を抱えていた。監督は「イル・ポスティーノ」のマイケル・ラドフォード。5人の女優に即興で演技をさせ、それをもとに俳優たちと共に登場人物たちのキャラクターを作るという異色の作劇術で製作。", "2000-01-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チョコレート・アンダーグラウンド", "チョコレート・アンダーグラウンド", "", "", "http://image.eiga.k-img.com/images/movie/54233/315x210.jpg?4053", 87, "イギリスの人気作家アレックス・シアラーのファンタジー小説「bootleg」を原作に、プロダクションI.Gが製作した携帯配信用オリジナルアニメを劇場公開。約20分の新作カットの追加などが行われている。ある日突然、健全健康党は体に悪いとして“チョコレート禁止法”を発令する。ハントリーとスマッジャーの2人の青年はその法律に反発し、仲間の協力を得てチョコレートの密造・密売を始めるのだが……。", "2009-01-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダークシステム", "ダークシステム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2010-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE FEMME DOUCE", "やさしい女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ドストエフスキーの短編小説の映画化。ドアが軋み、ベランダでテーブルが倒れ、ひとりの女が窓から飛び降りた。自殺してしまった若い妻の死体を前に、二年間の夫婦生活を振り返り、彼女の不可解な自殺の原因を探ろうとする夫。思い出の中の彼女の瞳は何を語っていたのか……。見る者の想像力にすべてを委ねるストイックな視線で、本作がデビューとなった16歳のドミニク・サンダを追う、ブレッソン初のカラー作品。", "1986-03-29", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FEELING LOVE（DIEGO！）", "フィーリング・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "スイミング・クラブで働くことになったディエゴは14歳。コーチの指導で水泳を始めた彼は、メキメキと上達していった。コーチの妹クラウディアとの恋も芽ばえた頃、ディエゴは不治の病に襲われる……。ヒット曲『愛のフィーリング』が効果的に使われている。", "1978-01-01", "4,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("27 Dresses", "幸せになるための27のドレス", "", "", "http://image.eiga.k-img.com/images/movie/53403/315x210.jpg?2347", 111, "花嫁付添い人として他人の結婚式を成功させることに生きがいを感じてきたジェーン。ところが自分の恋はさっぱり上手くいかず、想いを寄せていた上司が自分の妹と結婚することに。そんな彼女の前に新聞記者のケビンが現われ……。大ヒットドラマ「グレイズ・アナトミー」のキャサリン・ハイグルと「ステップ・アップ」のアン・フレッチャー監督がタッグを組んだラブコメディ。「プラダを着た悪魔」のアライン・ブロッシュ・マッケンナが脚本を担当。", "2008-05-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ДА ЗДРАВСТВУЕТ МЕКСИКА！／QUE　VIVA　MEXICO", "メキシコ万歳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "エイゼンシュテインが1931年にアメリカで手掛けた未完のドキュメンタリー作品を、半世紀を経て生存するスタッフが編集し、完成させたもの。「プロローグ」「竜舌蘭」など４エピソードで構成される。ショットごとに第三世界への鮮烈で力強い讃歌が胸を打つ。", "1979-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SE´RIEUX COMME LE PLAISIR", "まじめに愛して！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "キュートなパリジェンヌと彼女をめぐる二人の男の話。３人は仲良く共同生活を送り、旅に出た時などはそれぞれが夫婦に見えたりするほどだが、10年後、３人の間に子供が生まれて……。おしゃれなラブ・コメディ。", "1974-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'HOMME DE RIO", "リオの男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "アドリアンは８日間の休暇をもらい、恋人のアニュスに会いに出かけた。ところが、彼女は何者かに誘拐されてしまう。それより先、パリのシャイヨー宮からアマゾンの像が盗まれるという事件が起きた。この像はアニュスの父がブラジルで発掘したものだった。誘拐事件とのかかわりを察知したアドリアンは、アニュスを追ってブラジルのリオに向かったが……。ベルモンドのユーモアたっぷりの個性とブロカの歯切れのいい演出が全編を圧倒し、最後まで息をつかせないアクション・コメディの傑作。ニューヨーク批評家協会の外国語映画賞に輝き、世界中で大ヒットしたため、続いて「カトマンズの男」が作られた。", "1963-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A THIN LINE BETWEEN LOVE ＆ HATE", "バッドフェロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「バッドボーイズ」の喜劇俳優M・ローレンスが監督、主演を手掛けたコメディ。ナイトクラブを仕切るダーネルは大の遊び人。ある日、彼は美女ブランディをナンパして一夜を過ごす。ダーネルには火遊びに過ぎなかったが、浮気した夫を殺したコワい過去を持つブランディは、次第に彼の前で本性を現わし……。災難に見舞われたプレイボーイに扮する、ローレンスの妙演が見もの。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エスパイ", "エスパイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "小松左京の同名小説を映画化したサイキック・アクション。世界の要人暗殺を企てる悪のエスパー集団と、正義のエスパイとの死闘を描く。特撮を中野昭慶が担当、由美かおるのエロティックな踊りなどもあって、陽気で楽しい仕上がりになっている。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Un animal, des animaux", "動物、動物たち", "", "", "http://image.eiga.k-img.com/images/movie/53373/315x210.jpg?2299", 59, "「パリ・ルーヴル美術館の秘密」「ぼくの好きな先生」などで知られるフランスのドキュメンタリー映画監督ニコラ・フィリベールが94年に手掛けた作品。パリにある世界最大級の科学博物館・フランス国立自然史博物館を訪れ、25年に渡って閉鎖されていた動物学大ギャラリーの大規模な改修工事の模様を記録。リニューアル・オープンに向けて数万もの標本や剥製が復活していく様子を映し出す。サンフランシスコ国際映画祭グランプリ受賞作。", "2008-02-02", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Deaf", "聴覚障害", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 164, "", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("This Must Be the Place", "きっと　ここが帰る場所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "名優ショーン・ペンが引きこもりのロックシンガーに扮し、亡き父の思いをたどってアメリカ横断の旅に出る姿を描くドラマ。人気絶頂の最中に表舞台を去り、アイルランド・ダブリンの広大な邸宅で穏やかな日々を過ごしていたロックスターのシャイアンのもとに、故郷アメリカから30年以上も会っていない父親が危篤との報せが届く。飛行機嫌いなシャイアンは船でニューヨークに戻るが臨終には間に合わず、ユダヤ人だった父が元ナチス親衛隊の男を探していたことを知ると、父にかわって男を探す旅に出る。元「トーキング・ヘッズ」のデビッド・バーンが本人役で出演し、音楽も担当。タイトルも「トーキング・ヘッズ」の同名曲からとられている。監督は「イル・ディーヴォ」「愛の果てへの旅」のパオロ・ソレンティーノ。", "2012-06-30", "4,2,33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遊侠三国志 鉄火の花道", "遊侠三国志 鉄火の花道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "石原裕次郎・小林旭・高橋英樹・浅丘ルリ子の４大スター競演による日活任侠アクション。裕次郎と旭の共演は「錆びたナイフ」以来10年ぶりだが、裕次郎の死によりこれが最後の共演となってしまった。すっかり動きの鈍くなった裕次郎を尻目に旭がもうけ役で場面をさらう。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GETTING EVEN", "怒りの戦士 スーパー・コマンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ベトナム帰還兵の刑事、ダンディとエバンスが、多発する売春婦殺人事件を追ってニューヨークからバンコクへと舞台を移して活躍するアクション映画。前半はニューヨークを舞台にした刑事サスペンス、後半は東南アジアを舞台にした戦争アクションと、１本で２本分が楽しめる。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浅太郎鴉", "浅太郎鴉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "のちに名コンビとなる、三隅研次監督と市川雷蔵が、初めて顔を合わせた作品。国定忠治の子分・板割浅太郎は、伯父・勘介の息子・徳二郎を探して旅に出た。須坂の山寺で開かれた賭場で、徳二郎に出会ったが、そのすぐあと、徳二郎はイカサマ博打の分け前でもめ、親分の喜太郎に殺される。浅太郎は、徳二郎の復讐に、喜太郎の所へ赴くが……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA DECIMA VITTIMA", "華麗なる殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "人気SF作家R・シェクリイの『七番目の犠牲者』の映画化。21世紀の近未来社会では、人間の闘争本能を満足させて世界の平和を保とうと、殺人をスポーツとして公認。あちこちで知恵と腕力を駆使した“殺人ゲーム”が展開する。ブラック・ユーモアの効いた奇想天外なSF映画。", "1965-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひめゆり", "ひめゆり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "太平洋戦争末期、住民たちをも巻きこんだ激戦が繰りひろげられた沖縄で、14歳から19歳の女学生たちが戦場に駆りだされ、看護活動に従事した末にその多くが命を落とした。これまでにも映画やドラマで繰りかえし描かれてきた彼女たち「ひめゆり学徒隊」の真実に迫ったドキュメンタリー。13年間にわたって記録された生存者22人の貴重な証言を通し、少女たちに負わされた過酷な運命が浮かびあがってくる。", "2007-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Double", "顔のないスパイ", "", "", "http://image.eiga.k-img.com/images/movie/57413/315x210.jpg?3570", 98, "リチャード・ギア、トファー・グレイスの主演で描くサスペンスアクション。米ワシントンで上院議員が殺害される事件が発生し、その手口から死んだとされている旧ソ連の伝説のスパイ、カシウスの存在が浮上する。米ソ冷戦時代にCIAで活躍した元諜報部員ポールは、事件の謎を解明するために呼び戻され、若きFBI捜査官ギアリーと組んで捜査にあたることに。捜査が進むにつれカシウスの存在が明らかになっていくが、あらゆる状況がカシウスの正体はポール本人であることを示しており……。監督は「ウォンテッド」の脚本を手がけたマイケル・ブラント。", "2012-02-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Piano Tuner of Earthquakes", "ピアノチューナー・オブ・アースクエイク", "", "", "http://image.eiga.k-img.com/images/movie/53803/315x210.jpg?2922", 99, "カルト的人気を誇るパペットアニメ「ストリート・オブ・クロコダイル」のクエイ兄弟が、アルフォ・ビオイ＝カサーレスの「モレルの発明」とレーモン・ルーセルの「ロクス・ソルス」を原案に、アニメーションと実写を融合させて幻想的に描き出す狂気のドラマ。美貌の歌姫マルヴィーナに魅せられ、彼女を誘拐して自らの演奏機械人形コレクションに加えようと企む科学者ドロス。彼の計画に気づいた調律師フェリスベルトは、マリヴィーナ救出を試みるが……。", "2008-10-18", "3,7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Balls of Fury", "燃えよ！ピンポン", "", "", "http://image.eiga.k-img.com/images/movie/34183/315x210.jpg?1536", 90, "「ナイトミュージアム」の脚本家ロバート・ベン・ガラント＆トーマス・レノンが\"卓球\"をモチーフに放つ爆笑コメディ。FBIの指令を受けた元天才卓球少年が、裏社会で行われている卓球世界大会に潜入。負ければ殺されるというデスマッチ・トーナメントに挑む。名優クリストファー・ウォーケン、「ダイ・ハード4.0」のマギー・Q、テレビドラマ「HEROES／ヒーローズ」のマシ・オカらが扮するひと癖もふた癖もあるキャラクターに注目。", "2008-03-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水戸黄門", "水戸黄門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "片岡千恵蔵、市川右太衛門以下オールスター・キャストによるシリーズ第13作。由井正雪残党は、江戸の各地に放火し世情を騒がすが、水戸黄門主従のめざましい活躍によって残党一味はすべて捕えられる。助さん、格さんに東千代之介と中村賀津雄。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陸軍中野学校", "陸軍中野学校", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "昭和13年、陸軍少尉・椎名次郎は草薙中佐と名乗る男から奇妙な口頭試問を受ける。そして１週間後、次郎のほか17名の陸軍少尉が靖国神社近くのバラックに集まった。そこは、諜報部員を養成する機関、陸軍中野学校だったのだ。その日から次郎たちは親兄弟との接触を禁じられ、軍事教練、外交問題から女の悦ばせ方まで特別のスパイ教育を受けさせられる……。シリーズ１作目は、中野学校の実態をクールに描いたドキュメント風の作品。増村演出は、主人公をヒーローとして描くのではなく、時代や組織にのみ込まれた犠牲者として位置づけている。市川雷蔵の冷徹な魅力にも注目。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Red Rock West", "レッドロック 裏切りの銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "テキサスの田舎町レッドロックに流れ着いたマイケル。殺し屋に間違えられた彼は地主から妻殺しを、当の妻からは夫殺しを依頼された。報酬だけを得て町を逃げ出そうとするマイケルだったが、そんな彼の前に本物の殺し屋が現れ……。荒涼とした架空の町を舞台に、人々の欲望が交錯するサスペンス。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES NUITS FAUVES", "野性の夜に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "映画監督、小説家、歌手と多方面に才能を発揮しながら1993年にエイズで死去したC・コラールの自伝的作品。バイセクシャルのジャンは夜ごと男の体を求めてパリの町をさまようが、ある日ローラという娘と出会い、それまでになかった心のときめきを覚える。ジャンはローラに自分がエイズだと告げることができないまま彼女と結ばれてしまう。やがてジャンは真実を告白しようと決心するが、果たして愛は不治の病に勝つことができるのだろうか……。フランス本国で大絶賛を浴びたセンセーショナルなヒット作。型にはまらぬダイナミックな画面からC・コラールの愛と人生への執念がヒシヒシと伝わってくる。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL BACIO DI TOSCA", "トスカの接吻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ミラノに実在する老いた音楽家のための施設を舞台に、往年のサラ・スクデーリ、ジョヴァンニ・プリゲドゥほかオペラ・スターたちが全盛期を回顧して語り、歌う。スタイルはドキュメンタリーだが、オペラという壮大なフィクションのなかで今も生き続ける人々によって、虚構と現実のない交ざった時空間が現出している。", "1984-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROIS　CHAMBRES　A　MANHATTAN", "マンハッタンの哀愁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「天井桟敷の人々」「嘆きのテレーズ」などで知られる名匠M・カルネによる大人のラブ・ロマンス。妻に裏切られたフランソワと、外交官の夫と離婚したばかりのケイ。マンハッタンのバーで出会った二人は、孤独をなぐさめ合うかのように惹かれ合っていく。だが、ケイの娘が病気になり、彼女は夫のいるメキシコに行くことになり……。マル・ウォルドロンによるジャズナンバーが、しっとりとロマンティックにムードを盛り上げる。", "1965-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火垂るの墓", "火垂るの墓", "", "", "http://ecx.images-amazon.com/images/I/51oZtjHdfJL._SL210_.jpg", 90, "昭和20年６月５日、神戸。14歳の清太と４歳の節子の兄妹は、父が出征中のこの日の空襲で母と家を失い、未亡人のおばのもとを訪ねる。そこでの暮らしもやがて邪魔者扱いされるようになり、二人は防空壕に移り住むが栄養失調で衰弱した節子は、敗戦とともにその命の灯を消してしまう。そして、すべての希望を失った清太もまた……。原作は野坂昭如が、自らの体験をもとに書いた直木賞受賞の同名小説。終戦前後の日本人の生活を克明にスクリーンに再現し、その過酷な状況の中で自分たちの生き方を貫こうとした兄妹のたどる運命を、冷徹に見据えている。汚れ弱っていく幼女の姿を、ありのままに描ききったリアリズムと、その対極で闇を飛ぶホタルの光に人の命を象徴的に重ね合わせたアニメーション。現実と幻想が恐るべき密度で解けあった、これはまぎれもない傑作といえるだろう。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルパン三世 バビロンの黄金伝説", "ルパン三世 バビロンの黄金伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "モンキー・パンチ原作のTV新シリーズを監修した、鈴木清順を監督に据えての劇場用第３作。最初のシリーズからTV版に参加し、「ルパン三世 PART III」では作画監修を務めた青木悠三が作画監督を担当。そのため絵柄についても、「PART III」に一番近い作品となった。伝説の“バビロンの財宝”を狙うルパンは、同じくそれを探すニューヨークのマフィアのボス、マルチアーノに接近。その手掛かりとなる粘土板・通称マンハッタン文書の盗み撮りに成功する。だが文書を解読して向かったバビロンの遺跡発掘現場には、財宝はなく、文書の発掘されたニューヨークにあることが判明する。さっそくニューヨークに駆けつけたルパンと仲間たちだったが、その地下からとんでもないものが出現し……。スラップスティック感覚で展開する物語は、最後に至るや、もう奇想天外。今回は、銭形警部に国際警察の５人の美人警官が部下として従っている。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UP CLOSE＆PERSONAL", "アンカーウーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "ニュース番組のアンカーウーマンを夢みて、マイアミのローカル局に入社したタリー。入社早々ダサい服装で失笑を買うが、そんなことなどものともせずひたむきに仕事に打ち込む。懸命なアピールのもとお天気キャスターとしてその第一歩を踏み出したタリーは、敏腕プロデュサーのウォーレンに認められる。彼女はウォーレンのサポートの下、失敗を繰り返しながら、トップへと登りつめていく。さらに刑務所のレポートに行ったタリーは、そこで囚人たちの暴動をスクープし、全国ネットのアンカーウーマンの地位を獲得するのだが……。M・ファイファーとR・レッドフォードが共演したサクセス・ストーリー。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Unknown", "アンノウン", "", "", "http://image.eiga.k-img.com/images/movie/33723/315x210.jpg?4711", 85, "記憶を失ったまま廃棄工場に閉じ込められた5人の男たちが、自分が何者かもわからずに生き残りを賭けて闘う様子を描いたシチュエーション・スリラー。「パッション」のジム・カビーゼル、「恋愛小説家」のグレッグ・キニア、「父親たちの星条旗」のバリー・ペッパーら個性的な俳優陣の演技が光る。メガホンを取るのは、これまでジェシカ・シンプソンなどのミュージック・クリップを手掛けてきたクリエイター、サイモン・ブランド。", "2006-11-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青い乳房", "青い乳房", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "清順の前作「踏みはずした春」に続く日活無軌道青春映画第２作。小林旭は再び不良少年を演じる。内容は一種の家庭崩壊劇で、継母の過去の強姦、エロ映画製作、恐喝、痴話ゲンカなど不道徳のオンパレードであるが、清順は題材に流されず、独自の映像美で画面を切り取っている。原作名は「嘆きの乳房」。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("選挙", "選挙", "", "", "http://image.eiga.k-img.com/images/movie/1953/315x210.jpg?2637", 120, "2005年秋に自民党公認候補として川崎市議会議員補欠選挙に出馬した切手コイン商・山内和彦氏の選挙活動を追ったドキュメンタリー。成りゆきから立候補することになった山内氏が、「電柱にもおじぎせよ！」を合言葉とする過酷な“どぶ板選挙”に挑む姿に密着。巨大政党・自民党が“政治の素人”を公認候補に仕立てあげ、選挙戦を展開していく過程を、ナレーションや音楽を排した映像で捉え、日本の民主主義の実態を浮き彫りにしていく。", "2006-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tower Heist", "ペントハウス", "", "", "http://image.eiga.k-img.com/images/movie/56723/315x210.jpg?3605", 104, "ベン・スティラーとエディ・マーフィが初共演し、大富豪にすべてを奪われたビルの使用人たちがチームを組んで復讐する姿を描くアクションコメディ。ニューヨーク、マンハッタンの一等地に建てられた超高級マンション「ザ・タワー」最上階のペントハウスに暮らす大富豪のアーサー・ショウが、2000万ドルの詐欺容疑で逮捕され、使用人たちの財産も騙し取られていたことが発覚する。ザ・タワーの管理人として働くジョシュは使用人たちとチームを組み、厳重なセキュリティに守られたペントハウスに忍び込んでショウの隠し財産を奪い取るという計画を練るが……。監督は「ラッシュアワー」のブレット・ラトナー。共演にケイシー・アフレック、マシュー・ブロデリック、ティア・レオーニ、ガボリー・シディベ。", "2012-02-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サビ男サビ女", "サビ男サビ女", "", "", "http://image.eiga.k-img.com/images/movie/55733/315x210.jpg?1269", 0, "いつも突飛な行動を取ってしまう個性的な人々“サビ男＆サビ女”を描いたオムニバス。「書道ガールズ!!」の桜庭ななみが他人の応援を生きがいにする女子大生に扮する「ハゲマシガールズ」、片思いの彼女のために女装する高校生を「BECK」の中村蒼が熱演する「Boy? meets girl.」、お笑い芸人の友近がクレーム魔に扮する「くれえむないと！」、行き場のないリストラ男性を次々と拾ってしまう主婦を小泉今日子が演じる「せびろやしき」の4編から構成される。", "2011-01-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かえるのうた", "かえるのうた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "東京・下北沢を舞台に、対照的な二人の平凡な女の子が、不器用に生きる姿をリアルに描き出す。朱美は女癖の悪い同居人・良男を殴り、逃げ込んだ漫画喫茶でキョウコと出会った。彼女の家に転がり込んだ朱美は、援助交際に誘われるが……。生きる実感を掴めない現代の若者たちの空虚感をファンタジックに綴り、最後には幸福感を与えてくれる。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIGHTROPE", "タイトロープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "毒々しいネオンが輝くニューオーリンズの街を舞台に、猟奇的娼婦殺害事件を追うブロック刑事の活躍を描く。犯人の手口を知り、近づいていくにつれ、ブロックは自分と犯人の類似性に悩まされる。イーストウッドが、弟子のタッグルに監督させた一編。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cyborg Cop", "サイボーグコップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "麻薬捜査官だったジャックの元に送られた、兄フィリップからの手紙とコカインの包み。カリブの小島にいる兄の身を案じたジャックはそこに向かうが、フィリップはマッド・サイエンティストの手によって殺人サイボーグにされていた……。マーシャルアーツの達人、D・ブラッドリー主演によるSFアクション。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラ・ヴァルス", "ラ・ヴァルス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "実相寺昭雄監督が撮り上げたアダルトビデオの大作を、キネコ化して劇場公開した異色作。強姦を訴え出た女性と、加害者たる男の供述に食い違いが生じ、男の弁護士は独自に調査を開始するが、曼陀羅のごとき現代社会は真相をうやむやにしてしまう。現代版「羅生門」ともいうべき一編。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Stranger Among Us", "刑事エデン／追跡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　ニューヨーク。ユダヤ教ハシド派のコミュニティで、高額のダイヤモンドが消え、研磨職人が行方不明になるという事件が起こる。捜査に乗り出した女刑事エミリー・エデンはやがてその死体を発見。その後、彼女はハシディムに扮し、潜入捜査を開始する。宗派の厳格な規律に戸惑う一方、エミリーは指導者レバの息子アリエルと心を通わせていくが……。閉鎖的な世界で起こった事件に挑む女刑事の捜査、そしてロマンスを描く。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いつものように", "いつものように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "バイク便のバイトをしているタカセが、荷物受取先のイラストレーター・ナナエと知り合い、タカセの友人・コウノを交えて居酒屋へ。３人はすっかり意気投合し、次第にコウノとナナエはお互いを意識し始める……。けんもち聡監督の劇場映画デビュー作。江古田を中心に都内各地を転々としながらも、全編変わらぬゆったりとした語り口で、平凡な25歳の若者３人の一昼夜の出来事を描き出す。ナチュラルな中にも繊細さがうかがえるセリフの数々が、ぎこちない感情の揺れを浮かび上がらせて絶妙だ。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い文化住宅の初子", "赤い文化住宅の初子", "", "", "http://image.eiga.k-img.com/images/movie/1813/315x210.jpg?3053", 100, "", "2007-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピアノの森", "ピアノの森", "", "", "http://image.eiga.k-img.com/images/movie/34073/315x210.jpg?1406", 0, "「花田少年史」でも知られる漫画家・一色まことの大ヒットコミックをアニメ映画化。“ピアノの森”で育った天才的なピアノの才能を持つ少年・一ノ瀬海と、ピアニストの家系に生まれた少年・雨宮修平の友情と成長を描く。海の声を上戸彩、修平の声を神木隆之介が演じ、池脇千鶴、宮迫博之ほか豪華声優陣が脇を固める。アニメーション制作を手掛けるのは「時をかける少女」「パプリカ」など質の高い作品を生み出し続けているマッドハウス。", "2007-07-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("息子の結婚", "息子の結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "義母とうまくいかず、父にも反抗的な実業家の先妻の子・進一。ひょんなことから知り合った女・ユリエに勧められて、彼はジャズ喫茶のボーイとして働き出す。そんな時、父は息子に黙ってその店に融資するのだが……。川口浩・若尾文子、川崎敬三・叶順子の二組のカップルと親子が織り成す青春明朗編。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIME TO KILL", "エネミー・ウォー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "第二次大戦下、エチオピアに進駐したイタリア軍。エンリコ中尉は、事故にあった隊を離れ一人キャンプに向かう途中、現地の少女と出会う。ある夜、洞窟で彼女と愛を確かめ合っていた時、突然獣に襲われ、エンリコの撃った銃弾は、岩にはね返り少女の身体を貫く。彼女が助からないと知ったエンリコは自らの手で彼女を殺した。その後、彼女が風土病だったことを知り、殺した罪と病の恐怖に追い詰められていく。演技派N・ケイジが、戦争の狂気の中で悩み苦しむ主人公を熱演する。旧ビデオタイトルは「タイム・トゥ・キル 愛と勇気の戦場」。", "1991-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Get Crazy", "ゲット・クレイジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「ロックンロール・ハイスクール」のアラン・アーカッシュ監督が贈る、奇想天外なロックンロール・コメディ。1982年の大晦日、LAでは史上最大規模のニューイヤー・コンサートが開催されようとしていた。そこにコンサートを妨害しようとする大物プロモーターが現れて……。「時計じかけのオレンジ」のマルコム・マクダウェルがロック・スターを熱演。また、元ベルベット・アンダーグラウンドのルー・リードも出演している。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIDDLER ON THE ROOF", "屋根の上のバイオリン弾き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 172, "ウクライナの貧しいユダヤ人一家。父親のテビエは信心深く伝統の枠の中で生きてきた。ところがある日、５人娘の長女が相談もなしに結婚を決めてしまう。ユダヤ人弾圧の始まるなか、次女も革命家の家庭教師を追ってシベリアへ。三女はロシア青年と駆け落ちし、次々に伝統からはずれて飛び出してゆく。そしてついにユダヤ人国外追放。去っていくテビエたちのあとを、いつも屋根の上で哀しげな曲を奏でていたバイオリン弾きがついていく……。有名なロングラン・ミュージカルの映画化で、バイオリン独奏をアイザック・スターンが担当。娘が嫁ぐ日、明日への希望を祈って歌われる名曲『サンライズ・サンセット』が感動的。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Do-ga-ni", "トガニ　幼き瞳の告発", "", "", "http://image.eiga.k-img.com/images/movie/57693/315x210.jpg?4692", 125, "", "2012-08-04", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FARAWAY, SO CLOSE!", "時の翼にのって／ファラウェイ・ソー・クロース！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 147, "ベンダースのヒット作「ベルリン・天使の詩」の続編。天使カシエルは、親友ダミエルが去っていったベルリンの街で、壁崩壊後に様々な思いに揺れる人々の心を覗いて過ごしていた。ある日、ふとしたことから人間世界に降りることがかなったカシエルはダミエルを訪ねるが、自分にはそんな幸せな生活は想像できなかった。天使や堕天使たちに見守られながら、人間としては無力なカシエルの苦悩の日々が始まる……。", "1994-05-31", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALLAN QUARTERMAIN AND THE　LOST CITY OF THE GOLD", "キング・ソロモンの秘宝２ 幻の黄金都市を求めて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「レイダース／失われたアーク《聖櫃》」や「ロマンシング・ストーン」の亜流として作られヒットした１作目と同じ出演者による続編。行方不明になった弟を捜すため、結婚の準備をしていたアランとジェシーは東アフリカにある幻の黄金都市を目指して、再び冒険の旅に出る。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADDIO，FRATELLO CRUDELE", "さらば美しき人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「愛の嵐」以来、退廃美女の代名詞となったランプリングの初主演作品。16世紀末の北イタリアが舞台。兄と妹が禁断の恋に身をこがし、宴席で心臓を刺し貫くという衝撃的な悲劇のラブ・ストーリー。C・ランプリングが、世紀末の香りを漂わせ魅力的だ。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TROUBLE WITH HARRY", "ハリーの災難", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "紅葉映えるバーモントの森に男の死体が横たわっていた。これをめぐって繰り広げられるユーモラスなこの物語は、監督自身の言葉“殺人は喜劇である”そのもの。ロケーションの美しさと洗練された演出が絶妙。これでデビューしたマクレーンの軽妙な演技も光る。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歡楽叮嗜／HAPPY DIN DON", "新 Mr.Boo！ お熱いのがお好き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "売れないバンドマンのファットは、偶然マフィアの麻薬取引現場を目撃してしまう。そのために命を狙われるファットは、ツアー中の女性バンドに女装して潜り込み、バンコクを脱出する。しかしバンドのシンガーに恋した彼は男に戻ってデートをするが、女装した彼に惚れた金持ちの男にも追い回され……。ビリー・ワイルダーの名作を、香港流にリメイク。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トーキョードリフター", "トーキョードリフター", "", "", "http://image.eiga.k-img.com/images/movie/57223/315x210.jpg?4260", 72, "東日本大震災直後の2011年5月、節電や放射線への見えない恐怖により意気消沈した東京の街を撮影したドキュメンタリー。ミュージシャンの前野健太が吉祥寺で行った路上ゲリラライブの模様をワンカットで撮影した異色作「ライブテーブ」（2009）で、第22回東京国際映画祭「日本映画・ある視点部門」作品賞を受賞した松江哲明監督が、再び前野を主演に迎え、ネオンが消えて暗く沈んだ夜の新宿、渋谷などを歌いさすらう前野の姿を追う。", "2011-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風雲児 織田信長", "風雲児 織田信長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "中村錦之助主演、河野寿一監督による時代劇の佳作。群雄割拠の戦国時代。狂人とののしられながらも自己の信念に生きた信長は、斉藤道三を手なずけ、強敵・今川義元を桶狭間の合戦で討ち破る。中村錦之助が織田信長を熱演している。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喧嘩鴉", "喧嘩鴉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "高田浩吉が、博打もうまけりゃ腕もたつという旅人、いろはの伊太郎を演じた娯楽時代劇。子供の頃からのケンカ友だち、伊太郎と新三は、一緒に旅に出るが途中で仲違いをしてしまう。伊太郎は、落ち目の松井田一家の客分となったが、松井田の縄張りを新興勢力の富岡一家が狙っていた。その富岡には、新三が客分となっていて……。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stop-Loss", "ストップ・ロス　戦火の逃亡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("その木戸を通って", "その木戸を通って", "", "", "http://image.eiga.k-img.com/images/movie/54033/315x210.jpg?3232", 92, "2008年2月に急逝した「どら平太」「犬神家の一族」などで知られる巨匠・市川崑が、1993年に日本初の長編ハイビジョンドラマとして製作した作品を劇場公開。浅野ゆう子、中井貴一、故・フランキー堺ほか市川作品ゆかりの豪華キャストが出演している。城勤めの武士・平松正四郎は、平松家を再興する当主に選ばれ、城代家老の娘との縁組も決まり、忙しい日々を送っていた。そんなある日、記憶喪失の娘が突然平松家に迷い込んでくる。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chuyen cua Pao", "モン族の少女　パオの物語", "", "", "http://image.eiga.k-img.com/images/movie/34093/315x210.jpg?1429", 97, "ベトナムの最北部に暮らす少数民族モン族の村を舞台に、少女の初恋と彼女の生みの母、育ての母の数奇な運命を描いた人間ドラマ。「青いパパイヤの香り」「夏至」など俳優として活躍してきたゴー・クアン・ハーイが初メガホンを取り、社会主義国ベトナム初のインディペンデント映画として製作された。主人公の少女パオを演じるのは、ハリウッド映画「愛の落日」でヒロインを演じた国際派女優ドー・ティ・ハーイ・イエン。", "2007-07-28", "60", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スパイ・ゾルゲ", "スパイ・ゾルゲ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 182, "「梟の城」の篠田正浩が10年以上も温めていた企画を引退作品として映画化。昭和初期の日本で実際に暗躍したロシア人スパイ、ゾルゲに迫ることで、激動の昭和史を浮かび上がらせる。昭和８年。日本の対ソ戦略を探るためにドイツ新聞記者として日本に潜入したゾルゲは、ドイツ大使館から次々と日本の機密情報を手に入れる。彼は４人の諜報員とともに活動を続けるが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ТИХИЕ СТРАПИЦЫ／WHISPERING PAGES", "静かなる一頁　19世紀ロシアの散文をモチーフに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "ドストエフスキーの『罪と罰』をはじめとする19世紀ロシア文学の世界が、SF的な映像の中に散りばめられた、現在ロシアを代表する映像作家A・ソクーロフの意欲作。老婆を殺した孤独な青年と、娼婦に身を落としながらも神を信じる薄幸の少女との出会いを核に、水中都市、塔から次々と飛び下りる人々、恐竜の彫像など様々なイメージが重なっていく。マーラーの歌曲『亡き子をしのぶ歌』が、世紀末的な作品世界を包み込む。", "1993-01-01", "24,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hills Have Eyes", "ヒルズ・ハブ・アイズ", "", "", "http://image.eiga.k-img.com/images/movie/34213/315x210.jpg?2220", 107, "ホラー映画界の巨匠ウェス・クレイブンが77年に手掛けた「サランドラ」を、「ハイテンション」のアレクサンドル・アジャ監督がリメイクした衝撃のスラッシャー・ムービー。トレーラーで砂漠を横断中のカーター一家。ところが、近道をしようとしたのが災いし、荒地の真ん中で立ち往生してしまう。その近くには核実験の影響で突然変異を起こした恐ろしい食人集団が潜んでおり、何も知らないカーター一家に忍び寄っていた……。", "2007-09-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWENTYFOUR SEVEN", "トゥエンティフォー・セブン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "25歳の新鋭S・メドウスが自身の体験をもとに長編デビューを果たしたホロ苦い青春ドラマ。イングランド中央部の町ノッティンガム。あてもなく退屈な日々を送る不良少年たちのために、中年男ダーシーがボクシング・ジムを始める。強引に誘われていやいやトレーニングを開始した少年たちは、次第にダーシーの熱心さに後押しされ、眠っていた情熱に目覚めていく。ところが、待ち望んだ試合の日、事件が発生。ダーシーは町を去ることになり……。少年たちが生きる輝きを取り戻していく様を、モノクロのシャープな映像の中に映し出す。「モナリザ」などの名優B・ホスキンスが、少年たちに希望を与える純粋な中年男を好演している。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOE　SOMEBODY", "リーマン・ジョー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "昇進の見込みのない中堅社員のジョーは、妻に逃げられ人生諦めムード。おまけに後輩社員にノックアウトされる失態を愛娘の前で演じてしまう。しかし、美しい同僚の一言で勝手に自己改革意識に目覚めたジョーは、肉体改造に励みリベンジに燃え始める……。一発逆転を賭けて“スーパー・サラリーマン”に変身していく男の恋愛模様と親子愛を描いたハートフル・コメディ。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HONEYMOON IN VEGAS", "ハネムーン・イン・ベガス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "恋人のベッツィと結婚式を挙げるため、ラスベガスを訪れたジャック。だが、中年ギャンブラーの罠にはまった彼は、賭けの負け分としてベッツィを提供してしまう。ハワイに連れ去られた恋人を取り戻そうと、ジャックは奔走するが……。プレスリーのそっくりさんも多数登場する、にぎやかなラブ・コメディ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鞍馬天狗 角兵衛獅子", "鞍馬天狗 角兵衛獅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "鞍馬天狗は御用聞・隼の長七に折檻を受ける角兵衛獅子の杉作を助ける。そして、宿敵・近藤勇との一戦。またしても勝敗はつかず、西郷吉之助の命を受けて鞍馬天狗は一路江戸へ馬を走らせる。杉作に扮した美空ひばりがアラカンを食う。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("御用牙・鬼の半蔵やわ肌小判", "御用牙・鬼の半蔵やわ肌小判", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "シリーズ３作目は、２作目の監督・増村保造が脚本を担当。１、２作目ほどの圧倒的なパワーはないが、それなりに楽しめる。江戸城のお濠ばたに出るという幽霊のウワサを聞いた半蔵は、その裏に盗まれた小判が隠されていることに気づくのだが……。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本侠客伝 浪花篇", "日本侠客伝 浪花篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "横浜日東組の代貸・藤川宗次は、作業中に死んだ弟の骨を引き取りに大阪の浪花運送に現れた。浪花運送は半田組の仕事を奪おうといやがらせを繰り返している。宗次は半田組に味方し、その窮地を救う。が、沖仲仕・寅松を殺され、宗次の怒りは爆発する。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばりのびっくり五十三次", "ひばりのびっくり五十三次", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "デビュー直後の野村芳太郎監督による時代喜劇。有名な清水次郎長の“森の石松代参”のエピソードを軸に、高田浩吉演じる旅がらす、美空ひばりのちゃっかり娘ら、様々な登場人物たちが織りなす道中記ものである。高田が突然のど自慢に出たり、主演コンビが歌って踊るサービスも満点。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ", "男はつらいよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "世界的な長寿シリーズの記念すべき第１作。中学の時に親とケンカして家を飛び出した車寅次郎は、全国を渡り歩く香具師になった。両親の死を風の便りに知った寅次郎が、矢切の渡しから故郷・柴又へ里帰りを果たし、妹・さくらの結婚を助けようとするが、見合いの席を一人でぶち壊してしまう。そして寅さん自身は帝釈天のお嬢さんに想いを寄せるが、残念ながら失恋に終わる。シリーズの基本パターンはほぼ出そろったが、唯一寅さんのキャラクターがまだ洗練されておらず、かなり怒りっぽいチンピラの印象が強い。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沈黙の背信 TRUE JUSTICE PART3", "沈黙の背信 TRUE JUSTICE PART3", "", "", "http://ecx.images-amazon.com/images/I/61qYimzta4L._SL210_.jpg", 90, "スティーブン・セガール主演のクライム・アクション・シリーズ「TRUE JUSTICE」の第3弾。シアトルのクラブで謎の劇薬を摂取した女性が死亡し、ケイン率いる特別捜査班“SIU”が捜査を開始した。麻薬捜査班から事件に関する情報を入手していたSIU捜査官が何者かに襲われたことから、ケインは警察内に裏切り者がいると確信。やがて、部下のメイソンが劇薬密売組織に捕まったことを知ったケインは、罠と知りつつも救出に向かう。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナイル", "ナイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "エジプト考古学の第一人者、吉村作治の原作をもとに、エジプトの秘宝をめぐる争奪戦を描くサスペンス・ドラマ。エジプトで研究を続けていた考古学者が誘拐され、彼の友人である国際通信局に務める見城が現地へ飛ぶ。彼は学者が残した謎のメールをもとに捜査を開始、事件の裏に美術品の国際密輸組織との関係を突き止める。やがて彼は死んだ妻と酷似したエジプト人女性と出会い、組織の秘密を握る彼女とともに巨大な陰謀に立ち向かう。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paradise", "楽園", "", "", "http://image.eiga.k-img.com/images/movie/55413/315x210.jpg?2896", 110, "日本人脚本家と韓国のスター俳優がタッグを組む、日韓共同ドラマプロジェクト「テレシネマ7」シリーズ。「いま、会いにゆきます」の岡田惠和が脚本を担当。刑務所から出所したミギョンは行くあてもなく、目にとまったチラシに書かれた“最後の楽園・ハナ島リゾート”を目指すことにする。島の小学校で教師をしているイルホは、島行きの船上でミギョンを見かけ、なぜか彼女に心ひかれていた。やがてミギョンは偽名を使って島の小学校の給食室で働き始めるが、イルホはそれに気づきながらも温かく見守っていた。", "2010-05-29", "5,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUSHWHACKED", "サバイバル・ガイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ホーム・アローン」でおなじみのD・スターン主演の山岳コメディ。宅配便屋のマックスはある日突然、身に覚えのない殺人の罪を着せられ指名手配になってしまう。わけもわからずジープを盗み逃亡するマックス。しかしその車がボーイスカウトの隊長のものだったことから、彼は隊長に間違われ、子供たちを連れて登山をするハメに。おまけに目的の山は、ベテランでも登るのが難しいといわれる、“デビルスピーク”だった……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒豹天下／THE BLACK PANTHER WARRIORS", "黒豹天下 ブラックパンサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "銃、爆破、カード、コンピュータなどのエキスパートが集まった７人のプロ集団が、警察署内にある“シルバーボックス”なる箱をめぐり、怪人物と虚々実々の争奪戦を繰り広げる。一見シリアスと思わせるが、L・カーファイらの個人芸が楽しめるアクション・コメディ。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("重振精武門", "フィスト・オブ・フューリー／復活！ドラゴン怒りの鉄拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ブルース・リー主演作「ドラゴン怒りの鉄拳」の後日談的な内容のアクション。日本軍の手から武術道場、精武門の危機を救った陳真は、故郷の雲南省に身を潜めた。しかし、陳真を仇と狙っている日本の武術家一派は、陳真の暗殺の機会をうかがっていた。彼らの執拗な挑発に、陳真は怒りを爆発させ……。中国国際ジーグンドー連盟の設立者、セキ・テンリュウが主人公を熱演。ビデオタイトルは「復活　ドラゴン怒りの鉄拳　重振精武門」。", "2002-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DESERTER", "デザーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "復讐の鬼と化した男の執念の嵐が吹き荒れるウエスタン・アクション。アパッチに妻を殺され復讐を誓うカーター大尉は、職を辞し、自らアパッチ族に姿を変え、本物のアパッチを容赦なく射殺してまわっていた。一方神出鬼没のアパッチ族に手を焼く新任の総司令官マイルズは、特別攻撃隊を組織するため周囲の反対を押し切ってカーターを復帰させる。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Mummy: Tomb of the Dragon Emperor", "ハムナプトラ3 呪われた皇帝の秘宝", "", "", "http://image.eiga.k-img.com/images/movie/53483/315x210.jpg?2286", 112, "人気アドベンチャーシリーズ第3弾。監督は前2作のスティーブン・ソマーズから「トリプルX」「ステルス」のロブ・コーエンに、ヒロインのエブリン役はレイチェル・ワイズからマリア・ベロにバトンタッチ。ジェット・リー、ミシェル・ヨーらアジアスターも出演。1946年、リックとエブリンのオコーネル夫婦は上海で遺跡の発掘に没頭する息子アレックスと再会するが、アレックスの発掘した皇帝のミイラが、2000年余りの時を超えて蘇ってしまい……。", "2008-08-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シャッフル", "シャッフル", "", "", "http://image.eiga.k-img.com/images/movie/56383/315x210.jpg?3433", 119, "TVドラマ「ザ・クイズショウ」「深夜食堂」の及川拓郎監督が、自身が作・演出を手がけた劇団スパイスガーデンの同名舞台を映画化。日給200万円というモニター調査に身分も職業もバラバラの4人の男が集まり、担当者を名乗る男が現れ調査が始まる。実は5人は銀行強盗の一味で、強奪した金のありかをただひとり知る男が記憶喪失になったため、男の記憶を呼び覚ます手段としてこの計画が練られたのだが、そこへ想定外の人物が現れ、事態は二転三転としていく。", "2011-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOYS　WILL　BE　BOYS，BOYS　WILL　BE　MEN", "少年義勇兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "1941年12月８日未明から昼前まで、日本軍とタイ軍の戦闘がタイ領内の各地で繰り広げられ、湾岸の町チュンポーンでは少年義勇兵たちが戦闘に参加していた。この作品では少年義勇兵に参加した高校生たちの青春を描く。1941年５月、戦争勃発に備えて若い義勇兵が募られ、チュンポーンに住むマールットと級友のプラユットも志願する。姉とその夫である日本人と暮らすマールット、副知事の息子プラユットの二人が、同時に一人の女学生に恋したためいさかいを起こしたり、祖国を守るもの同士として絆を深めたりする様を、厳しい訓練の日々の中に綴っていく。", "2000-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЧЕРНОБЫЛЬ−ХРОНИКАТРУДНЫХ НЕДЕЛЬ", "チェルノブイリ・クライシス 史上最悪の原発事故", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "1986年４月の原発事故発生直後から５ヵ月間、３人のソ連人スタッフが撮影を続けたドキュメンタリー。崩れ落ちた４号炉、肌をむき出しのまま作業する男たち、放射能に感光した白い画面……。戦慄の事実が、明かされる。ちなみに監督は放射能障害で死亡している。", "1986-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勲章", "勲章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "元陸軍中将・岡部は再軍備促進団体“あけぼの会”の会長にされるが、やがて彼は昔の暴君ぶりを発揮し始める。岡部の息子は父の勲章を愛人に与え、愛人はその勲章を犬にぶら下げた。岡部の家庭は崩壊し、“あけぼの会”の運動も失敗する。岡部は勲章を嘲ける息子を殺し、自らも死地を求め歩き出す。渋谷実の問題作。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VICE ACADEMY PART２", "バイス・アカデミー２ L.A.風俗特捜隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "売春組織、未成年ポルノなどの性犯罪がはびこる大都会L.A.を舞台に、ポルノの女王G・リン・アレン扮するボディコン刑事が活躍するアクション映画。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GLORY GUYS", "栄光の野郎ども", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "白人の横暴な圧力に耐えかねたシャイアン族が蜂起したため、ドニファン砦には第三騎兵隊の総力が結集される。ハーロッド大尉もそこへ赴く。しかし、現地で指揮を執っているのが６年前、自分の名誉のために多くの新参兵を犠牲にしたマッケーブ将軍とわかり不快をあらわにする。S・ペキンパーの脚本による西部劇。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Own Private Idaho", "マイ・プライベート・アイダホ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "リバー・フェニックスとキアヌ・リーブスの共演で話題を呼んだ青春ロードムービー。路上に立ち、男性に体を売って暮らしている青年マイク。緊張すると突然意識を失ってしまう奇病を持つ彼は、自分を捨てた行方不明の母親を捜すため、市長の息子で、同じく男娼のスコットとともに故郷アイダホへと向かう。同性愛、近親相姦、ドラッグといったショッキングな内容を、詩的な映像を駆使して描いたガス・バン・サント監督初期の傑作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後勝利／LAST VICTORY", "最後勝利", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "刑務所入りする兄弟分の女房と愛人の世話をするハメになった主人公のホン。ヤクザ稼業も板につかず、お人よしのホンは必死で二人の姐さんを守る。しかもお互いの存在を相手に知られてはならないため、彼の苦労は並大抵ではなかった。香港で大ヒットしたラブ・コメディ。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("九月の冗談クラブバンド", "九月の冗談クラブバンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "自主映画出身の長崎俊一がATGの“ニュー1000万円路線”で撮った、初の劇場用映画。かつて暴走族の間で伝説的な存在だったリョウは、仲間の徹司の事故死を機にバイクを捨てた。しかし、他の暴走族たちは、徹司の一周忌にリョウの伝説がよみがえると信じている……。一周忌は９月。その日に向けてリョウを除く人々のエネルギーは高まっていく……。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOE VERSUS THE VOLCANO", "ジョー、満月の島へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "S・スピルバーグ、K・ケネディ、F・マーシャル製作総指揮のロマンティック・コメディ。人気スター俳優、T・ハンクスと、この作品で一人３役をこなすかわいくて“いい女”のM・ライアンが珍道中を繰り広げる。もとは一流ビジネスマンだったのに今はしがないサラリーマン、加えてあと半年の命と宣告をされたジョー。“どうせ長生きできないなら”と開き直り、億万長者ととんでもない契約を交わし南の島へ旅立つが……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Yogi Bear", "ヨギ＆ブーブー　わんぱく大作戦", "", "", "http://image.eiga.k-img.com/images/movie/55403/315x210.jpg?1524", 80, "日本では「クマゴロー」として知られる人気TVアニメを、実写とアニメを合成させた3D作品として映画化。ヨギが長年住んでいるジェリーストーン公園が、財政難により閉園の危機に。ヨギは相棒のブーブーとともに閉園を阻止するため、ありとあらゆる手を尽くすが……。声の出演はヨギにダン・エイクロイド、ブーブーにジャスティン・ティンバーレイク。監督は「センター・オブ・ジ・アース」のエリック・ブレビグ。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今日からヒットマン", "今日からヒットマン", "", "", "http://image.eiga.k-img.com/images/movie/54813/315x210.jpg?4369", 0, "平凡な営業マンと凄腕のヒットマンの二重生活をコメディタッチに描いたむとうひろしの同名コミックを、武田真治主演で映画化。共演に星野真里、森下悠里。新婚のサラリーマン十吉は、ひょんなことから伝説のヒットマン「二丁」の名を継ぐことになってしまう。美人の妻に支えながら送る新婚生活も、謎の美女が持ち込む仕事によってトラブル続きとなり……。", "2009-09-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南十字星", "南十字星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "太平洋戦争のシンガポール攻略戦で攻撃に失敗して捕虜となった後、参謀本部によって斬首刑の断を下されたイギリス・オーストラリア軍の指揮官と、通訳を委嘱された日本の外務省情報官の友情を描いた作品。オーストラリアとの初の合作。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Casablanca", "カサブランカ", "", "", "http://ecx.images-amazon.com/images/I/51Gn57pZdwL._SL210_.jpg", 103, "ハンフリー・ボガートによる名台詞「君の瞳に乾杯」で知られる、映画史上屈指の名作。第2次世界大戦下の1941年、アメリカへ行くためには必ず通らなければならない寄港地だったフランス領モロッコのカサブランカ。そこで酒場を営むアメリカ人リック（ボガート）の元に、パリで突然消えてしまった恋人のイルザが、夫で反ナチス活動家のラズロを伴って現れる……。共演にイングリッド・バーグマン。監督はマイケル・カーティス。42年度のアカデミー賞では作品賞、監督賞、脚本賞の3部門で受賞した。", "1946-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’EAU A｀ LA BOUCHE", "唇〈くち〉によだれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ヌーヴェル・ヴァーグ系の映画批評家J・D・ヴァルクローズが手掛けたコミカルな監督デビュー作。1959年の夏。他界したアンリエット婦人の遺産を相続するため、孫娘ミレナと従兄弟のジャン・ポール、セラフィーヌが屋敷に集合する。セラフィーヌの恋人であるロベールは、ジャン・ポールの到着が遅れていることにつけ込んで、自分がジャン・ポールだとミレナらに言いふらす。美しいメイドのプリュダンスも加わり、彼らは入り組んだ恋のさや当てに興じていく。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("4 Luni, 3 Saptamani si 2 Zile", "4ヶ月、3週と2日", "", "", "http://image.eiga.k-img.com/images/movie/53393/315x210.jpg?2332", 113, "チャウシェスク独裁政権によって個人の自由が制限されていた80年代のルーマニア。大学生のオティリアは、ルームメイトのガビツァの違法中絶を手助けするべく準備を進めていた。ところが、手術当日に思わぬ問題が発生する。ルーマニアの新鋭クリスティアン・ムンジウ監督が、ヒロインたちの長い1日をリアリズムに徹底した映像で描き切り、2007年カンヌ国際映画祭で最高賞パルムドールを獲得した社会派ドラマ。", "2008-03-01", "56", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL FRULLO DEL PASSERO", "モーメント・オブ・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ある男が交通事故で死亡。彼には同年輩の友人ガブリエーレと年の離れた愛人シルバーナがいたが、彼女はまだ本当の愛を知らずにいた。実業家であるガブリエーレは彼女の新しいパトロンになりたいと申し出るのだが……。未成熟な主人公が性愛に目覚めていく様子を幻想的に描いた作品。", "1988-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SALMONBERRIES", "サーモンベリーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ある日、アラスカのカッツビューという町に、カッツという名の男のような容姿をした20歳の女性が現れる。両親を知らないカッツは自分の過去を探すため、町に来たのだった。そして、東ベルリンから壁を越えてやって来たロスウィータという女性と出会う。互いに不幸な過去を持つ二人は、特別な感情を抱き始めた。P・アドロン監督の独特な色彩感覚と風景描写が美しい。1991年モントリオール世界映画祭でグランプリ・アメリカを受賞。", "1991-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("この世の外へ　クラブ進駐軍", "この世の外へ　クラブ進駐軍", "", "", "http://image.eiga.k-img.com/images/movie/1483/315x210.jpg?3665", 123, "「どついたるねん」で監督デビュー、近年は「顔」「KT」「ぼくんち」と話題作の続く阪本順治監督が、第2次世界大戦2年後の東京を舞台に、ジャズバンドを結成した5人の青年たちを描く青春群像ドラマ。全編を彩るのは「モナリザ」「A列車で行こう」など日本のジャズ黎明期の名曲の数々。弟を日本兵に殺された進駐軍基地の軍曹役で「マイ・ネーム・イズ・ジョー」など監督としても活躍するイギリス俳優ピーター・ムランが共演。", "2004-02-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nordwand", "アイガー北壁", "", "", "http://image.eiga.k-img.com/images/movie/55103/315x210.jpg?4574", 127, "前人未踏のアルプス連峰の難所・アイガー北壁に挑んだ、実在の若き登山家たちの軌跡を描く。1936年のナチス政権下、政府は国家の優越性を世界に示すため、ドイツ人がアルプスの名峰・アイガーの北壁を初登頂することを熱望していた。そんな国家の期待を背負い、4人のドイツ人青年とオーストリア人青年が北壁登頂に挑むが、彼らを過酷な運命が待ち受けていた。", "2010-03-20", "7,77,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アコークロー", "アコークロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "沖縄で語り継がれる妖怪キジムナーの伝説をモチーフにしたホラー。東京でのつらい過去から逃げるように、恋人の浩市が住む沖縄へやって来た美咲。浩市や彼の友人たちと共に平和な毎日を送り始める彼女だったが、彼らと共に思わぬ事件に巻き込まれてしまう。それをきっかけに彼らの精神は徐々に異常を来たし始め……。監督の岸本司をはじめ、スタッフの多くを沖縄出身者で固めた純沖縄産作品。ヒロインを演じるのは人気タレントの田丸麻紀。", "2007-06-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Exodus", "戦火のナージャ", "", "", "http://image.eiga.k-img.com/images/movie/55903/315x210.jpg?3359", 150, "ロシアの巨匠ニキータ・ミハルコフの代表作で、カンヌ国際映画祭グランプリ、アカデミー外国語映画賞をダブル受賞した「太陽に灼かれて」（1994）の続編。1934年5月、KGB幹部のドミートリ大佐は、モスクワのスターリン私邸に呼び出され、銃殺刑にされたはずの元英雄アレクセイを捜索するよう命じられる。ドミートリは複雑な思いにかられながらも、アレクセイの消息をたどる。第2次大戦下のソ連を舞台に、男女3人の数奇な愛憎を描く戦争ドラマ。", "2011-04-16", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL　GATTOPARDO", "山猫【イタリア語・完全復元版】", "", "", "http://ecx.images-amazon.com/images/I/51KFMN3WXVL._SL210_.jpg", 186, "巨匠ヴィスコンティが撮った一大傑作が国家予算をもとに完璧に復元され2005年に日本でも公開された。監督の体験を基に、没落する19世紀後半の貴族社会の人物像を浮き彫りにしてみせる。フランスの名優A・ドロンと、イタリアのセクシー女優C・カルディナーレの華麗なる共演も見もの。名門の公爵は新興勢力に押され没落寸前だが、毅然と生きていた。ある日、彼は甥を連れて別荘に出かけ、村の人々と出会うことに……。", "1963-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女渡世人・おたの申します", "女渡世人・おたの申します", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "藤純子の“女渡世人”シリーズの一編。監督は１作目の小沢茂弘から山下耕作にバトンタッチ。女主人公に想いを寄せる流れ者が言う“姐さん、ヤクザは日蔭の花だ。日蔭の花が日向に咲こうなんて考えたら、てめえがつらくなるだけですよ”の名セリフが印象的。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水霊　ミズチ", "水霊　ミズチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "田中啓文の同名ベストセラー小説をもとに、水を介して広がっていく死の恐怖を描いたホラー映画。震度3から4の地震が頻発する東京都西部エリアで、不可解な自殺事件が相次いで発生する。その原因が水にあることを突きとめた新聞記者・響子は、水道局に勤める元夫・岡に相談を持ちかけるが、岡は水道水の安全性を主張するばかり。やがて、水をめぐる惨劇はさらなる拡大を続けていく。主演は「大停電の夜に」の井川遥。", "2006-05-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE RETOUR DE DON CAMILLO", "ドン・カミロ頑張る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "フランスの巨匠デュヴィヴィエが撮った'51年「陽気なドン・カミロ」の続編。皆を励ます司祭ドン・カミロの活躍を描くヒューマン・ストーリーだ。前作で町を去り別の場所に赴任したドンだが、前作でいがみ合った町長が町民の声を聞いてドンを再び町に呼び戻した。同じ頃、街の堤防建設計画が滞っていたため大雨が続いたある日、ついに町に水が溢れ出した。", "1953-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISS ARIZONA", "ミス・アリゾナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "伊、独の二大俳優を配して、ショウビジネス界に生きる男女の姿を描いた人間ドラマ。1920年代から第二次世界大戦にわたる激動のヨーロッパ。夫を殺され、幼い息子とともに歌手としてカムバックしたミッツィとショービズ界を愛するサンドールは、ブタペストにクラブ“アリゾナ”を開く。商売はうまくいっていたが、やがてナチス・ドイツが台頭してきて世情は不安定になり……。男の哀愁を漂わせるM・マストロヤンニが好演。", "1988-01-01", "4,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飛び出したお嬢さん", "飛び出したお嬢さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "西陣問屋伊勢重の娘・時子は、親の決めた縁談を嫌い、ミルクホールを営むお由のところへ転がり込む。翌朝より、お由と隣人の清吉は、時子の傍若無人の生活に巻き込まれて大騒ぎ……。斎藤良輔＝中山隆三のオリジナル脚本を渋谷実が監督した人情喜劇。お嬢さま役の水戸光子のはつらつとした個性が魅力。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("絶響／SWAN SONG", "絶響", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "広東音楽界で才能豊かな音楽家として称賛されていた区老枢は、その革新性ゆえに新中国成立以来、不遇の日々を送っている。生前のあらゆる努力はむくわれないのだが……。「太陽雨」で知られるニューウェーブ監督、チャン・ツーミンのデビュー作。トリノ国際映画祭青年映画コンペ部門審査員特別賞受賞。", "1985-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Orfeu", "オルフェ", "", "", "http://image.eiga.k-img.com/images/movie/1583/315x210.jpg?4290", 110, "“カリオカの丘”に生まれ育ったオルフェは神をも嫉妬させる程の才能を持つ若きミュージシャン。彼はカーニバルのカリオカ・チームのリーダーとして、今年も優勝を目指し練習に励んでいた。カーニバルを数日後に控えたある日、オルフェは叔母を訪ねてやってきた少女ユリディスと出会い、たちまち恋に落ちる。彼は家族も友人もカーニバルさえも捨てて、彼女と共にこの地を出て行く決心をするが……。", "2000-09-09", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("弾丸ランナー", "弾丸ランナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "職場のストレスと悪夢のような失恋をきっかけに自分を変えようと決心した安田は、冴えない人生から飛び出すために銀行強盗を計画。しかしマスクを買いに立ち寄ったコンビニで、ミュージシャンを目指すシャブ中毒の店員・相沢になりゆきで発砲してしまう。逆上した相沢に銃を奪われ、追いかけられる安田。そこへ、自分の尻込みから組長と兄貴分を死なせ苦悩するヤクザ・武田が遭遇。相沢にぶつかり転倒した武田の心で何かがはじけ、彼もまた走り出す。逃げる安田。安田を追う相沢。相沢を追う武田。爆走を続ける３人の“弾丸”は、やがて快楽の世界へと……。「ワールド・アパートメント・ホラー」「ゼイラム２」などに出演している俳優、サブの初監督作品。独特の語り口で、３人の男の“突破口”に鮮やかに迫っている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHAT’S UP DOC？", "おかしなおかしな大追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "博識多才なオテンバ娘と、音楽理論学会に乗り込んできた田舎青年がまったく同じ形の４つのカバンのおかげで、とんでもない大事件に巻き込まれるコメディ。サンフランシスコを舞台にしたボグダノヴィッチ監督の快作。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("インフィニティ∞波の上の甲虫", "インフィニティ∞波の上の甲虫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "雅楽界の貴公子、東儀秀樹が映画初主演を務め、音楽プロデュースも手掛けたファンタジー。複雑なメタ・フィクション構造を持つ、いとうせいこうの同名小説の映画化だ。南の楽園、ボラカイ島を訪れた作家、高橋の日常と、彼が執筆する小説の主人公タカハシの行動が同時進行で展開。いつしかその現実と虚構がお互いを浸食し、入れ替わっていく過程を描き出す。奥菜恵が二人の主人公の前に現れる娼婦と美少女を一人二役で演じている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("童貞物語", "童貞物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "４人の童貞少年たちが、夏の海水浴場を舞台に何とか女の子をものにしようと、懸命にアタックしては失敗。だが、失神して病院へ担ぎ込まれた仲間の一人は、そこで幼なじみの看護婦に出会って……。“グローイング・アップ”シリーズをベースにした青春映画。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Expendables", "エクスペンダブルズ", "", "", "http://image.eiga.k-img.com/images/movie/55323/315x210.jpg?3008", 103, "シルベスター・スタローン、ジェット・リー、ジェイソン・ステイサムら新旧のアクションスターが集結した戦争アクション大作。自らをエクスペンダブルズ（消耗品）と名乗る凄腕傭兵部隊は、ソマリアの凶悪な武装海賊を討伐したあと、南米のビレーナという島国の軍事独裁政権を打倒するために現地へと赴く。だが、そこではかつてない危機が彼らを待ち受けていた……。共演にミッキー・ローク、ドルフ・ラングレンほか。ブルース・ウィリス、アーノルド・シュワルツェネッガーもカメオ出演。監督・脚本はスタローン。", "2010-10-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("侍ニッポン", "侍ニッポン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "徳川末期、大老・井伊直弼の開国強行策に世情は雑然としていた。直弼と妾の間の子・新納鶴千代は攘夷派にそそのかされ、父とも知らず直弼を襲う。父・阪東妻三郎が演じた新納鶴千代役を今回はその遺児・田村高廣が演じた。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RETURN OF CAPTAIN INVINCIBLE", "キャプテン・ザ・ヒーロー 悪人は許さない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "超能力のスーパーマンとして一世を風靡したキャプテンも、今は引退を余儀なくされ失意の日々を送っていた。だが、悪の帝王ミスター・ミッドナイトに手を焼く大統領からカムバックを要請され、キャプテンは一念発起。復活したヒーロー、キャプテンの活躍をパロディー・タッチで描いたSFアクション。", "1983-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT’S ENTERTAINMENTIII", "ザッツ・エンタテインメントPART３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "MGMのミュージカル作品より、今や伝説と化した名場面、そして未収録シーンを選りすぐったシリーズの第３作。MGM創立70周年を記念した本作では、舞台裏や撮影現場などのビハインド・シーンを中心に、使われることのなかったアウトテイクなどを収録。G・ケリーやJ・アリスンなど、MGMミュージカルにゆかりの深いスターたち９人がホストとして登場し、それぞれのミュージカル・ナンバーとシーンを紹介していく。登場するミュージカル・ナンバーは全部で62曲。「雨に唄えば」のD・レイノルズの歌唱シーン、「バンド・ワゴン」でのS・チャリシーの歌とダンス、1951年版「ショウボート」でのL・ホーンの歌唱シーン。「イースター・パレード」でのJ・ガーランドの歌唱シーンなど、ファンにはこたえられないアウトテイクがたっぷり収められている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海賊版＝BOOTLEG FILM", "海賊版＝BOOTLEG FILM", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "「CLOSING　TIME」に続く小林政広の監督第２作。白黒シネマスコープというノスタルジックな画面構成を用いて、再び映画への愛を奏でている。ヤクザと刑事ながら親友同士の二人の男。彼らが互いに愛した一人の女の葬儀に向かう車中で、いさかいの心情を炸裂させていく。物語は血なまぐさいが、北海道ロケを生かした映像は雪のようにどっしりとやわらかいタッチ。行き場を失った主人公たちのさえない末路を、やさしく見つめている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAMBO KINGS", "マンボ・キングス わが心のマリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "マンボ・ブームの火つけ役、セサール＆ネスター・カスティーヨ兄弟の物語。キューバ生まれのマンボを引っさげてアメリカに乗り込んできた二人の兄弟愛、そして彼らを取り巻く男女愛、家族愛をラテン・ミュージックを背景に描く。ティト・プエンテ（本人の役で出演)、セリア・クルースらN.Y.ラテン・シーンの大御所が参加し、その素晴らしい才能を披露しているのも見逃せない。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAZON MAUDIT", "彼女の彼は、彼女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "夫と子供に囲まれて平凡に暮らす専業主婦のロリ。ある日、車の故障で彼女の家にレズバーのDJ、マジョリーが立ち寄る。水道修理をしてもらったことから男まさりのマジョリーにロリは魅せられ、二人は恋愛関係に。浮気を繰り返す夫への見せしめの意味もあって彼女はマジョリーにのめり込んでいき、一家は崩壊の危機に陥る。シニカルな笑いとウイットに富んだセリフで、夫婦の絆のもろさを暴いた恋愛コメディ。本国フランスでは大ヒットを飛ばし、1995年セザール賞脚本賞を受賞した。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KLEINHOFF HOTEL", "ホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "パリ上流社会に暮らし、上品にしつけられた貞淑な人妻が、ホテルで他人の情事をのぞき見するうち、自分の中に眠っていた官能の叫びが目覚めはじめる。そんな人妻を、地下組織の若者が淫らな愛欲の虜にしていく……。「O嬢の物語」のクレリーが、再び妖艶な魅力を発揮した官能の物語。", "1977-01-01", "4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD END", "デッド・エンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ニューヨークはイースト・サイドに帰ってきたギャング、ベビーフェイス・マーチン。下町の子供たちの間ではヒーローとなっていた彼だが、幼なじみの友人とのトラブルから惨めに死んでいく……。ボギーの初期の代表作。この作品に出演した子役たちはその後“デッド・エンド・キッズ”として大活躍した。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A BIGGER SPLASH", "彼と彼 とても大きな水しぶき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1970年に、33歳の若さですでに“回顧展”を開くまでに成功した画家、D・ホックニー。1971年夏から２年半にわたる彼の姿を、ドキュメンタリーとドラマを組み合わせた形で綴っていく。モデルで彼のボーイフレンドだった青年が、ホックニーのもとを去り、失意のドン底から立ち直るまでをJ・ハザン監督が生き生きと描写している。", "1974-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EDDY DUCHIN STORY", "愛情物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1930～40年代にわたり甘美な演奏で全米を風靡したピアニスト兼バンドリーダー、エディ・デューティンを主人公とした音楽伝記映画。両親の反対を押し切ってピアニストになろうとニューヨークへ出たエディは、良家の令嬢マージョリーのバックアップのおかげで幸運なデビューを飾る。やがて二人は結婚、人気も上り、幸福を迎えるが、息子ピーターが生まれるとマージョリーは急死。一転して不幸のどん底につき落とされたエディの生活は荒れ、仕事も手につかず、悲しみを忘れるために海軍に入る。復員した時ピーターは10歳、美しいチキタに育てられていた。エディはチキタと結婚して幸福をとり戻したのだが……。主題曲『トゥ・ラヴ・アゲイン』をはじめ、デキシーやチャールストン全盛期の名曲を挿入した名編。T・パワーの代表作の一つ。なおピアノの陰の演奏はカーメン・キャバレロ。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帰郷", "帰郷", "", "", "http://ecx.images-amazon.com/images/I/21WQW09FEZL._SL210_.jpg", 82, "ひょんなことから元恋人の子どもと過ごすことになってしまった男が、戸惑いながらも絆を深めていく姿を描いたハートウォーミングドラマ。再婚することになった母親の結婚式に出席するため故郷を訪れた晴男は、かつての恋人・深雪と久々に再会する。ところが、なぜか深雪は幼い娘チハルを残して姿を消してしまい……。主演は「Dolls（ドールズ）」の西島秀俊。共演に「ハッシュ！」以来2年ぶりの女優復帰となる片岡礼子。", "2005-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TENDER HOOKS", "バブルガム・ラバーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "苦しく孤独な境遇に育った一人の女性が思い切って都会へと出て、一人の若者と運命的な出会いを果たす。彼女はそこで初めて素敵な恋を知り、自分の本当の人生とは何なのかを悟っていくのだった。女性の心の自立と恋を通して、初めて知っていく人生の意味を描いた、さわやかな青春ロマン。", "1988-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お前の母ちゃん BITCH！", "お前の母ちゃん BITCH！", "", "", "http://image.eiga.k-img.com/images/movie/55673/315x210.jpg?4173", 70, "漫画家、作家、女優など幅広い分野で活躍する内田春菊が初監督に挑む官能映画で、新しい性愛の可能性を探る恋愛映画の特集「Love＆Eros CINEMA COLLECTION」の第1作。大学生の五月雨ユウは、駅の片隅で周囲の目も気にせずいちゃつくカップルを目撃する。その翌日、バイト先のカラオケボックスに昨日のカップルの片割れの女、芙美恵が現れるが、彼女は子供やママ友らしい女性たちと一緒だった。人妻かもしれない芙美恵に興味を持ち始めたユウは、偶然参加した合コンで彼女と再会し、そのままラブホテルへ行くことになる……。", "2010-09-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和やくざ系図 長崎の顔", "昭和やくざ系図 長崎の顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "長崎を舞台に、落ちぶれた一家の三代目を襲名した主人公が、土地を牛耳る悪玉一家と闘う。嵐寛寿郎と安藤昇が珍しく日活映画に出演。出番は少ないが、貫禄十分の演技をみせる。名作「拳銃は俺のパスポート」の野村孝監督としては平凡な仕上がり。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARK COMMAND", "暗黒の命令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「駅馬車」のウェイン＆トレヴァーのコンビが再共演し、悪役にW・ピジョンを据えたリパブリック社の大作西部劇。南北戦争下のカンザス州ローレンス。保安官ボブは、もとは真面目な牧師だった強盗団のボス、ピジョン相手に町を守る。クライマックス・シーンの迫力は興奮もの。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANCHESCA　PAGE", "踊るのよ、フランチェスカ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ブロードウェイのスターを目指す母娘の奮闘を描いたミュージカル。ステージママのリタは、娘のフランチェスカを舞台劇にデビューさせようと奔走。なんとか役を射止めるが、芝居のプロデューサーは公演をわざと失敗させて投資金を着服しようとしていた……。コミカルな展開、はつらつとした音楽に加えて、リタ役のドラッグクィーン、V・J・マーマンの怪演も見もの。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BANDITI A MILANO", "ミラノの銀行強盗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1967年の９月25日に、実際にミラノで起こった銀行強盗事件をもとに映画化された犯罪映画。イタリア中の銀行を襲い続ける強盗一味。だがある時、ちょっとした時間のロスから警察の追撃を受けるはめに。街中で起こる銃撃戦、検問も無視した壮絶な逃亡劇。だが、仲間の一人が警察に捕まってしまう……。", "1968-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Year One", "紀元1年が、こんなんだったら!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROAD TO ZANZIBAR", "アフリカ珍道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "クロスビーとホープのコンビが活躍する“珍道中”シリーズの第2弾。ドロシーを含めた3人組による愉快な歌唱披露シーンも。コメディがベースだが、「ターザン」のパロディやサプライズの連続、加えて音楽映画的なフレイバーも。二人がアフリカへ渡って食人族に襲われたり、笑えるゴリラとのバトルあり、魔女が絡んできて驚いたりのさまざまな騒動に巻き込まれる。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Clockers", "クロッカーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "　ブルックリン、経営する飲食店を隠れ蓑にしてドラッグの密売を行なっていた男が射殺されるという事件が起こった。やがて妻子持ちで働き者のビクターという男が自首するが、殺人課の刑事クラインは不審なものを感じ、捜査を続行する。まもなくビクターの弟で、ロドニーという元締めの配下でドラッグを売りさばいていた青年ストライクの存在が浮かび上がる。クラインは執拗にストライクを追うことに……。ドラッグ、そして銃による犯罪の影響とその背景をえぐり出すサスペンス。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAM SUFFIT", "サム★サフィ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ジプシー風の自由気ままな生活にウンザリしたエバは、マジメな生活こそいちばん刺激的だと考え、家政婦や役所仕事を始める。ゲイの芸術家ピーターも彼女の考えに同意し、パリの街での“普通なこと”探しが展開される……。注目の女流監督V・テヴネが描くオシャレでヘンテコな青春映画。", "1992-01-01", "2,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("This Charming Girl", "チャーミング・ガール", "", "", "http://image.eiga.k-img.com/images/movie/33683/315x210.jpg?4674", 98, "“第2のキム・ギドク”と注目される韓国の新鋭イ・ユンギ監督が、幼い頃の心の傷を胸に秘めながら生きる女性の日常を丁寧に描いたヒューマン・ドラマ。郵便局で働く29歳のチョンヘは、マンションで1人暮らしする自立した女性。職場の同僚とも一定の距離を置き、変化のない毎日を送っていた。ある日彼女は、マンションの下に捨てられていた猫を飼い始める。このちょっとした変化が、彼女の中の何かを少しずつ変えていくのだった……。", "2006-10-07", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Johnny Mad Dog", "ジョニー・マッド・ドッグ", "", "", "http://image.eiga.k-img.com/images/movie/55333/315x210.jpg?1507", 93, "相次ぐ内戦で混乱を極めるアフリカ・リベリア共和国。リーダーのジョニー率いる少年兵のコマンド部隊「マッド・ドッグ」は、強盗、レイプ、無差別虐殺など、非道の限りを尽くして人々を恐怖に陥れていた。ジョニーたちの侵攻により街を追われた少女ラオコレは、戦争で足を失った父親を助けに家へ向かうが……。フランスの新鋭ジャン＝ステファーヌ・ソベールの長編デビュー作。内戦で戦った元少年兵らをキャスティングし、リアルな芝居を引き出している。", "2010-04-17", "2,37,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("至尊無敵之新哥傅奇２ ／CASINO TYCOON ２", "アンディ・ラウ カジノタイクーン２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "A・ラウ主演のシリーズ第２作。マカオの賭博王となったシンは、テレビ女優のユンと恋に落ちる。そんなある日、シンの娘のアークが留学先から帰国、恋人のチェンをシンに紹介する。チェンが気に入ったシンは、彼を自分の会社に入れ、重要なポストにつける。しかし、チェンはシンの宿敵のチンの一人息子であり、父の仇を討つために、シンに近づいたのだった……。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SUN SHINES BRIGHT", "太陽は光り輝く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "南部魂のかたまりで、勇気と誠意でケンタッキーのある町を治めるプリースト判事。しかし選挙にライバルである北部派のメイドウ弁護士が立候補した。町ではプリーストに不利な事件ばかり起こり、誰の目にもメイドウ勝利は確実と思われたが……。J・フォードが力を込めて描く西部社会劇の名編。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("堂口故事／TONGS", "チャイナタウン・ウォリアーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ギャングとの縄張り争いに燃える兄弟の姿を、アクションたっぷりに描いた作品。成功を夢見て香港からニューヨークへやって来たポールとミッキー。二人は伯父の住むチャイナタウンへと身を寄せるが、そこで待っていたのは貧しい生活。あげく二人は徐々に悪の世界に染まっていく。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Fleuve aux Grandes Eaux", "大いなる河の流れ", "", "", "http://image.eiga.k-img.com/images/movie/42933/315x210.jpg?3792", 24, "人間と自然をテーマとした作品を手がけ、アカデミー短編アニメーション賞を2度受賞したカナダ在住のアニメーション作家フレデリック・バックの傑作短編4本をまとめ、ニュープリント、新字幕で公開する「フレデリック・バックの世界」にて上映。北米を流れるセント・ローレンス河を舞台に、河に生息する生命の強さと、生態系を破壊し続ける人間の愚かさを描く。第66回米アカデミー短編アニメーション賞にノミネートされた。", "2011-07-02", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逃がれの街", "逃がれの街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "平凡な善意の男が、偶然に巻き込まれた殺人事件から、ヤクザの男たちを殺していくようになるプロセスを描いたサスペンス・ドラマ。トラック運転手の男が、追い詰められて刑事の弾丸に射たれて死ぬまでを描く。水谷豊がストイックな演技を披露。ハードボイルド作家の北方謙三の出世作が原作。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cursed", "ウェス・クレイヴン’s・カースド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「スクリーム」などで知られるホラーの鬼才W・クレイヴンが手がけたスリラー。狼の呪いをかけられた、姉弟のサバイバルが描かれる。人間から狼に変わる様をじっくり見せるVFX映像は圧巻。「僕のニューヨークライフ」のC・リッチによる絶叫演技も緊迫感をあおる。エリーとジミーの姉弟が、満月の夜に正体不明の獣に遭遇する。以来、周囲で不気味な事件が続発。さらに彼らの肉体にも異変が生じ始め……。", "2006-03-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUST THE WAY YOU ARE", "クリスティ・マクニコルの　白いロマンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "片足が不自由なために、人生に自信を失っている一人の少女が、アルプス白銀の世界で、様々なロマンスとめぐり会い、やがて確かな愛をつかんでいく……。当時のトップアイドル、マクニコルが大人への脱皮を試みた、さわやかな青春ドラマ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN　PARAISO　BAJO　LAS　ESTRELLAS", "ビバ！ビバ！キューバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "キューバの有名キャバレーを舞台に繰り広げられる恋愛大騒動劇。スター・ダンサーを目指す娘シシーは青年セルヒートと恋に落ちるが、誤解から喧嘩別れする。オーディションを受けたキャバレー“トロピカーナ”の舞台監督で、父親の昔の恋敵アルマンドの誘惑に乗り、やがて彼女はトップダンサーへ。誤解を解こうとするセルヒートだが、彼には重大な出生の秘密が隠されていた。恋人、親子、友人や元恋人に元夫婦……と、入り乱れっぱなしの人間関係。次から次へと発覚する事実と秘密。唖然とするくらい強引な展開は爆笑もの。ついツッコミたくなるほど感情のまま突っ走るラテン気質の彼らの行動が、明るいキューバ音楽に乗せてコミカルに描かれる。", "1999-01-01", "49,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAINT−EX", "星の王子さまを探して　サン＝テグジュペリ～魂の軌跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "『星の王子さま』の作家アントワーヌ・ド・サン・テグジュペリの生涯を、『星の王子さま』の主人公に重ね合わせて描いたファンタジー。1900年、貴族の家に生まれたサン・テグジュペリは、幼い頃から大空を飛ぶことを夢見て成長し、航空郵便社に就職する。だが、妻コンスエロは危険が伴う飛行士の仕事には反対で二人の仲は次第に険悪になっていく。さらには生活に困窮し、墜落事故にも遭うが、それでも彼の大空に懸ける情熱は変わらなかった。やがて第二次世界大戦が始まり、彼はフランス軍最高齢のパイロットとして飛び立っていく。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rambo: First Blood Part II", "ランボー／怒りの脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　閉鎖的な田舎町で警察を相手に激闘を繰り広げたベトナム帰還兵ランボーは刑務所に服役していた。そんな彼が、ベトナムで行方不明になった兵士（MIA）の調査という特別任務に就くことを条件に釈放される。現地での戦闘行為は厳禁とされていたが、米兵捕虜が虐待されているところを見たランボーは命令を破り、救出に乗り出す。今再びランボーの戦争が始まった！　大ヒット・アクションシリーズの第2弾。超人ランボーの活躍がよりド派手になった。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("谷中暮色", "谷中暮色", "", "", "http://image.eiga.k-img.com/images/movie/54833/315x210.jpg?4399", 120, "東京下町の谷中でホームビデオの修復活動をしているかおりは、昭和32年に焼失したという谷中五重塔を記録したフィルムの存在を知る。寺院、霊園、伝統工芸職人などに取材を試みながら、ありし日の塔に思いを馳せるかおりは、ある日地元のチンピラ・久喜と出会い、ひょんなことから一緒に五重塔のフィルムを探すことになる。", "2009-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPARROWS", "雀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "“アメリカの恋人”と呼ばれたアメリカ映画サイレント時代の大女優M・ピックフォードの代表作。農夫のグライムスは、孤児を誘拐しては、底なし沼に囲まれた農園に監禁して過酷な労働をさせていた。この世の地獄ともいえるこの農園の子供たちの唯一の心の慰めは、聖書を読んで聞かせてくれるモリーという娘だった。彼女はいつかこの農園から逃げ出そうと子供たちを励ましている。ある日グライムスは大金持ちの子供をさらってしまい、父親の通報で警察の手がグライムスの身辺に迫った。証拠隠滅のため、グライムスは子供たちを底なし沼に沈めようとするがそれを察したモリーは農園脱出を図る……。", "1926-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Village", "ヴィレッジ", "", "", "http://ecx.images-amazon.com/images/I/51ZRPDEW7NL._SL210_.jpg", 108, "「サイン」のM・ナイト・シャマラン監督の最新作。1897年、米ペンシルバニアの小さな村。村の周囲を取り囲む森には恐ろしい存在がいるため、村人が森に入ることはタブーとされていた。だが、村で皮を剥いだ動物の死骸が発見され、誰かがタブーを破ったのではないかという疑惑が持ち上がる。ロン・ハワード監督の実娘ブライス・ダラス・ハワードがヒロイン役。撮影はコーエン兄弟監督作の常連ロジャー・ディーキンスが担当。", "2004-09-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("由美香", "由美香", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "自主映画出身で、意欲的なAV作品を連発している平野勝之監督が、まるで私小説のようなタッチを見せた味わい深いビデオ・ドキュメンタリー。愛人でもあるAV女優・林由美香とともに日本最北端の場所＝北海道、礼文島を目指す自転車旅行の模様が収められている。東京・新宿を出発点に、実に1052キロもの行程。タイヤがパンクすれば、気持ちもパンクする。そんな風にうつろい続ける二人の心境が、さりげなく浮き彫りになっていく。作為も過剰さも感じさせず長尺を見せ切る力量もさることながら、林由美香の女優としての存在感が抜きんでている。「わくわく不倫旅行」のタイトルでAV作品としてリリースされたが、セックス・シーンはきわめて少ない。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Mark of Zorro", "快傑ゾロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "有名な冒険談を映画化した痛快活劇。スペイン領時代のカリフォルニアに、マドリードに留学していた青年ディエゴが帰ってきた。だが、市長だった父は辞職し、街の人々は新市長と横暴な大尉が課した重税に苦しんでいた。やがてディエゴは覆面の騎士ゾロとなって、この悪に挑み、民衆の英雄となっていく。T・パワーの華麗な剣さばきが光る。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Girl's BOX ラバーズ☆ハイ", "Girl's BOX ラバーズ☆ハイ", "", "", "http://image.eiga.k-img.com/images/movie/53473/315x210.jpg?3644", 100, "長谷部優、長澤奈央、星井七瀬ら注目のアイドルが集結したガールズ・ムービー。歌手に憧れる優亜は、母の反対を押し切って上京しオーディションを受ける。合格して喜んだのもつかの間、実は騙されていたことを知った彼女は、オーディションで出会ったナオミに助けられ、埠頭の倉庫を改造したライブスポット“Girl's Box”へと導かれる。そこでは優亜と同じように輝きを求める少女たちが懸命に働いており……。", "2008-03-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続サラリーマン出世太閤記", "続サラリーマン出世太閤記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "地方工場への転勤を命じられた木下秀吉は到着早々、工場落成祝賀会を自分の歓迎会と間違えて大失敗。そんな彼にブローカー、黒田が近づいて丸め込もうとする。だが秀吉はその手に乗らず、機械の発注を太洋製機に依頼する。ところが納期を過ぎても機械は届かず、彼が太洋製機を訪ねると……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Punisher: War Zone", "パニッシャー：ウォー・ゾーン", "", "", "http://image.eiga.k-img.com/images/movie/54293/315x210.jpg?4521", 107, "マーベル・コミックの人気ヒーロー「パニッシャー」を実写映画化したバイオレンス・アクション。法に代わって悪人を裁く私刑執行人“パニッシャー”ことフランクは、新たなターゲットである凶悪犯ビリーを襲撃。無残な姿に変わり果てたビリーは、“ジグソウ”と名を変えてパニッシャーへの復讐を誓う。主演はTVドラマ「ローマ」で注目されたレイ・スティーブンソン。女流監督レクシー・アレクサンダーがメガホンを取る。", "2009-04-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤ちょうちん", "赤ちょうちん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "フォーク・グループ“かぐや姫”の同名ヒット曲をモチーフに作られた哀しい青春映画。都会の片隅で知り合った政行と幸枝は愛し合い、同棲を始める。だが、変な中年男に転がりこまれたり、赤ん坊が生まれたり、近所のいやがらせにあったりと、そのたびに引っ越しを繰り返す。そして４度目の引っ越し先で、幸枝は気がふれて配達の米屋にけがをさせてしまう。精神病院に入れられた幸枝をあとに、政行は子供を連れて５度目の引っ越しをする……。単なる恋愛劇ではなく、精神的フリークスの集まった群集劇としても秀逸だ。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪の断章−情熱−", "雪の断章−情熱−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "二人の男に囲まれて育ったみなしごの少女が、殺人事件に巻き込まれ、大人になっていく姿を描く。冒頭での雪の降るセット内で主役の二人が出会う長い長いワンシーン・ワンカットは、相米演出の真骨頂で、そのパワーには驚くばかりである。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OKLAHOMA！", "オクラホマ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "1943年の人気ミュージカルを屋外ロケで映画化。農場の娘ローリーと牧童カーリーの恋模様を中心に描く。ほとんどのシーンにロジャース＆ハマースタインによる名曲『Oh,Whata Beautiful Mornin』『People Will Say We're in Love』などがあふれ、振付けも素晴らしい。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARNOSAUR", "恐竜カルノザウルス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「ジュラシック・パーク」の公開に先駆けて1993年５月に全米公開された、B級映画の巨匠、R・コーマン製作総指揮の恐竜映画。現代によみがえった恐竜が、次々と人間を襲う様は、かなりショッキングで、コーマン作品の面目躍如といったところ。恐竜たちの造形も「ジュラシック・パーク」とは一味違い、モンスターっぽいものになっている。ちなみに「ジュラシック・パーク」の主演、ローラ・ダーンは、本作のメイン・キャスト、D・ラッドの娘。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIRL　SHY", "猛進ロイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "名喜劇役者H・ロイド主演のハートフルなラブ・コメディ。内気で女性と口もきけない青年ロイドが、独自の研究をもとに“恋愛の秘訣”という本を書き上げた。出版社に向かう途中で出会った富豪令嬢メリーに恋をするが、いつものごとく思いを伝えられずに別れた。出版社からも相手にされず、彼は田舎に帰ることを決意。だが、メリーが結婚すると知り、あわてて式場へと向かう。", "1924-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Duel at Silver Creek", "抜き射ち二挺拳銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "　金の採掘場を荒らすギャングによって父親を殺されたルークは仇を討つために町へ行く。やがて彼は保安官タイロンの助手となり、捜査を続ける。そしてついに強盗団の首領の正体が明らかになるが……。拳銃の名手を主人公にしたアクションの名匠ドン・シーゲルの西部劇。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("侠女　上下集／A TOUCH OF ZEN", "侠女 第一部 チンルー砦の戦い　第二部 最後の法力", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "明王朝の時代、陜西省の小さな町に住む若い学者グーは謎の美女と恋に落ちる。彼女は時の権力闘争の中で父を殺され復讐を誓う身の上だった……。竹ヤブで展開される急降下のアクション・シーンなど、雄大なスケールとキン・フー監督ならではの映像世界により香港映画を一躍、国際的レベルに引き上げた傑作。", "1971-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PONG ２", "新 桑の葉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "前作「桑の葉」で韓国土着のエロスを描いて見せたイ・ドゥヨン監督が、再び女の悲しみを、今度は日本統治時代のなかに綴った一編。1920年代の末期、日本統治下の朝鮮。正直に生きていながらも、美人の妻が男たちの誘惑に乗らないことから、独立運動活動家であるとして村の男から密告されてしまったチグン。彼の気立てのいい妻は、嘆き、苦悩するが……。厳しい時代に生きなければならなかった女の性と悲しみを、エロティックなシーンをふんだんに挿入しながら描いている。また、これが映画デビュー作となる新人、カン・ムニョンの肢体も見ごたえ十分。", "1989-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Marathon Man", "マラソンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "大学生ベイブの兄ドグは、元ナチの宝石商ゼルの運び屋をしていた。しかし、ゼルは裏切ったドグを殺害。ベイブを捕まえて、宝石の所在を聞くために拷問する……。歯を痛めつける拷問が何とも不気味。ユダヤ人とナチの因縁をベースにしたサスペンス・ドラマ。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUTH OF ST.LOUIS", "セントルイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "南北戦争さなかのセントルイス。キップ、チャーリー、リーの３人は経営する牧場をゲリラに焼き払われ、リーは南軍に志願、キップとチャーリーは武器密輸業へと、それぞれ別の道を歩むことに。だがその間にキップの婚約者デボラは、リーと深い仲になっていく……。西部劇の名手R・エンライト監督作品。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喧嘩高校軍団　特攻！國士義塾VS.朝高", "喧嘩高校軍団　特攻！國士義塾VS.朝高", "", "", "http://image.eiga.k-img.com/images/movie/54503/315x210.jpg?2469", 115, "1970年代後半、完全なる縦社会の國士義塾高校應援団に入団した山下と星山は、“新入生肝試し”として猛者の集う朝川高校へ“勲章（バッジ）狩り”へ向かうが、無残にも返り討ちにされる。ボクシングトレーナーの段田に助けられた2人は、段田がボクシングを教える朝川高校の次期総番の呼び声も高いキム・ジャンクに遭遇してしまう。", "2009-04-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火宅の人", "火宅の人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "長年温めていた企画を、深作欣二が思いのたけをぶつけて映画化。自らがファミリー・アクション映画というように、文芸作とは思えないほどのテンポとスピード感あふれる上質の人間ドラマとなり、興行的にも好成績を残した。先妻に先立たれた桂一雄は、ヨリ子を後妻に。けれども、子供が障害児になってから妻は怪しげな宗教に凝りだす。一雄は新劇女優の恵子の虜となりアパートで同棲するが、恵子の浮気のため喧嘩がたえない。東京を逃げ出した一雄は旅先で薄幸の女と知り合う……。放浪の作家・檀一雄の名作を雄大な風景をバックに撮影。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どですかでん", "どですかでん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "山本周五郎の小説『季節のない街』を映画化した黒澤明監督初のカラー作品。戦後のスラムを舞台に、電車バカの六ちゃんを取り巻く市井の人々のエピソードが、互いに入り組みながらユーモラスかつ幻想的に展開してゆく。木下惠介、市川崑、小林正樹そして黒澤明という日本を代表する巨匠４人で結成した“四騎の会”の第１回作品。これまでギラギラした人間像を描き続けてきた黒澤監督が、本作では彼本来のやさしさに立ち戻り、市井の人々へ向けて温かいまなざしを投げかけ、見る者の心を和ませる。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("河童皿太郎　信念のクレーマー", "河童皿太郎　信念のクレーマー", "", "", "http://image.eiga.k-img.com/images/movie/57373/315x210.jpg?2537", 20, "芸能事務所イトーカンパニー所属の俳優たちが出演するショートムービーをネット配信している「イトーチューブ」で公開された作品を映画館で上映する「イトーチューブ Shrot Movie Colleciton」の1本。緑の肌にリュック型の甲羅を背負い、クレーマーとしても世間をにぎわせる河童皿太郎のクレーム活動と恋を描く。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超級計劃／ONCE A COP", "プロジェクトS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「ポリス・ストーリー３」の番外編とも言える作品。今回の主役はジャッキーとともに活躍したM・キング扮する女刑事ヤン。中国国家保安局のヤンは、香港で多発する凶悪犯罪シンジケートを壊滅するため香港警察の招きを受ける。彼女は着任早々強盗団と一戦を交えるが、その中に恋人のチェンが加わっていた。強盗団を追い詰めたヤンはやがて真実を知り、チェンとの対決を決心する。特別出演として、女装したジャッキーが登場。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("They shoot horses, don't they?", "ひとりぼっちの青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "大恐慌の30年代、当時流行していたマラソン・ダンスのコンテストには、高額の賞金を求めて様々な男女が参加していた。身重の女、年をとった船乗り、そして、ふとしたきっかけでパートナーを組むことになったロバートとグロリアもいた。だが過酷なコンテストに続々と脱落者が出て……。コンテストの主催者に扮したギグ・ヤングはアカデミー賞助演男優賞を獲得した。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH RISK", "ダーティー・ソルジャー 野良犬軍団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "麻薬密輸地帯を支配するボス、セラノの財産500万ドルを狙う５人の男たち。命知らずの彼らは“野良犬軍団”と自ら称し、綿密な強奪計画をもとにセラノの隠れ住む南米コロンビアへと乗り込んだ。南米奥地を舞台に展開する本格的冒険アクション。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三匹の悪党", "三匹の悪党", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "イカサマ博徒の半次郎と三郎はとある漁師町にやって来る。その町ではヤクザの舟留屋が高利貸しをして人々を苦しめていた。二人は舟留屋３代目襲名披露の賭場に乗り込み、まんまと大金をせしめる。これをかぎつけた流れ者、一本松と二人の攻防が展開するアクション作品。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セイクリッドセブン　銀月（しろがね）の翼", "セイクリッドセブン　銀月（しろがね）の翼", "", "", "http://image.eiga.k-img.com/images/movie/57613/315x210.jpg?3446", 60, "2011年7月～9月にTV放送されたサンライズ制作のオリジナルアニメ「セイクリッドセブン」の特別編集版。全12話のTVシリーズを、新作シーンも追加して再構成。主人公・丹童子アルマのライバルとなる輝島ナイトの視点から、謎の研究機関の所長・研美悠士との戦いを描く。特殊な力をもつ石「セイクリッドセブン」により能力者「セイクリッドテイカー」となった孤独な高校生・丹童子アルマと藍羽財団の令嬢ルリは、強力な力をもつ石の魔物「悪石（アシ）」に立ち向かう。やがて2人は巨大な陰謀に巻き込まれ、そこへもう1人のセイクリッドテイカーの少年・輝島ナイトが現れる。", "2012-01-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KARAOKE／人生紙一重", "KARAOKE／人生紙一重", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "今や世界の共通語となった“カラオケ”の生みの親、井上大佑の半生を描いた知られざるサクセス・ストーリー。ロカビリーに憧れ、音楽で生きていくという夢を抱き、挫折しながらも熱い魂で突き進んだ無名の偉人。俳優のみならず、ミュージシャンとしても活躍する押尾学が等身大で演じている。音楽で生計を立てたい大佑。ある日、音痴の外れたキーやテンポに合わせた伴奏が大ウケ。ジュークボックスの改造版を思いつく。", "2005-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("P.S. I Love You", "P.S.アイラヴユー", "", "", "http://image.eiga.k-img.com/images/movie/53243/315x210.jpg?2104", 126, "アイルランド元首相の娘セシリア・アハーンによる同名ベストセラー小説をオスカー女優ヒラリー・スワンク＆ジェラルド・バトラー主演で映画化。最愛の夫を突然亡くし、家に引きこもっていたホリー。しかし30歳の誕生日に夫のメッセージが録音されたテープが届き、その後テープの予告通りに夫から消印のない手紙が届き始める……。監督・脚本は「マディソン郡の橋」の脚本家リチャード・ラグラベネーズ。", "2008-10-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN AND OUT OF FASHION", "イン＆アウト・オブ・ファッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "写真家、映画監督、グラフィック・デザイナーなど様々な肩書きを持つW・クラインが、自らの半生を振り返るドキュメンタリー。イヴ・サン・ローランを始め著名人が語る彼の素顔や、モード界の最先端を歩いた彼の写真や衣装デザインなどを紹介しつつ、その半生を回顧する。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人でなしの恋", "人でなしの恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "江戸川乱歩の短編小説の映画化。昭和初期、美術学校に通っていた京子は初恋の講師と結婚し、幸せな日々を送っていた。しかし婚礼から半年たったある夜、目を覚ますと夫の姿が見当らない。夜な夜な床を空ける夫を不信に思い後をつけて土蔵に行くと、そこからは夫と女の激しい吐息が聞こえてくるのだった……。CMディレクター松浦雅子の監督デビュー作。女性ならではの繊細なタッチで愛に戸惑う切ない女心をしっとりと描く。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("300 MIL TIL HIMLEN", "天国への300マイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1985年、民主化前のポーランドで実際にあった事件を下敷きにしたロード・ムービー。ポーランドの田舎町。14歳のイェンドレクと11歳のグジェゴジェの兄弟は、腐敗した故郷を捨て、まだ見ぬ国へと旅立つことを決意する。トラックの下に潜り込み、国境を越えようとする兄弟たち。一時は離れ離れになり、ようやくめぐり合った二人。厳しい検問を通り過ぎ、兄弟はなんとかデンマークへと脱出することに成功する……。", "1989-01-01", "42,18,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アイ・ラヴ・フレンズ", "アイ・ラヴ・フレンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "“ろうあ者と聴者がともにつくる映画”として成功を収めた「アイ・ラブ・ユー」の流れを受け継ぐヒューマン・ドラマ。カメラマンの美樹は、交通事故で子供を死なせ、罪の意識にさいなまれている青年・柴田と出会う。苦しむ彼を励ますために美樹は、ある方法を思いつく。前作でろうあ者の女優として注目を浴びた忍足亜希子が、活動的な現代女性を生き生きと演じている。京都シネメセナ第２回助成作品。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BARFLY", "バーフライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "M・ロークが、従来の“ダンディで格好いい”イメージをかなぐり捨て、アル中の売れない作家ヘンリー役に挑戦したエキセントリックなラブ・ストーリー。ロサンゼルスのダウンタウンのとある酒場。感性が鋭いゆえに、小市民的生活に背を向け、酒びたりの生活を送る若き作家ヘンリーは、やはり人生や社会を嫌悪するかのように酒をあおる中年女性のワンダと出会い、互いに強く惹かれるが……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blizna", "傷跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「トリコロール」三部作で知られるポーランドの巨匠クシシュトフ・キエシロフスキー監督。本作は、ドキュメンタリー監督として活躍していたキエシロフスキーによる初の劇場長編作。ポーランドの地方都市オレツコでの工場建設をめぐり、地元住民と工場運営側が対立。その狭間に立つ、オレツコ出身でありながら、工場の監督官に任命されたベトナシュの苦悩を描く。", "", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("押切", "押切", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "内向的な美少年・押切トオルは、不気味な男が死体を埋める夢を見続けていた。そんなある日、昔トオルの一家が暮らしていた屋敷で、怪現象が起きていることを知らされた彼は、屋敷の調査に乗り出すが……。伊藤潤二のコミックを原作にしたパラレル・ワールド恐怖憚。撮影から仕上げまでをハイビジョンで行っている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("9", "9 ナイン 9番目の奇妙な人形", "", "", "http://image.eiga.k-img.com/images/movie/54903/315x210.jpg?3887", 80, "麻布を縫い合わせて作られた人形が、古びた研究室で目を覚ます。腹部に大きなジッパー、背中に“9”というマークがあるが、自分が何者か分からない人形は、果てしなく広がる廃墟の世界へと歩き出す。すると、背中に“2”と描かれた自分と同じような人形に出会うが、2人は突如、巨大な機械獣に襲われる……。2005年のアカデミー賞にノミネートされた同名短編アニメーションを長編として映画化。監督は新鋭シェーン・アッカー、製作にティム・バートンとティムール・ベクマンベトフ。", "2010-05-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LILIES", "百合の伝説　シモンとヴァリエ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "カナダの人気作家M・M・ブシャルドの小説『LILIES』を著者自身の脚色で映画化し、同国のアカデミー賞にあたるジニー賞で最優秀作品賞を含む４部門を制した愛の物語。舞台は20世紀初頭、北ケベックの修道院。伯爵夫人を母に持つ少年ヴァリエと御者の息子シモンが、友情を超えた愛で結ばれながらも、周囲の無理解によって悲劇的な運命をたどっていく。思春期の少年の同性愛を描く耽美的な作品で、40年後に年老いて再会した主人公たちの回想で一部始終が語られることにより、ドラマの残酷な美しさが鮮烈に浮き彫りにされている。", "1996-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エロチックな関係", "エロチックな関係", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "レイモン・マルローの『春の自殺者』を翻案したハードボイルドなロマンポルノ。浮気の調査を依頼された売れない私立探偵が、連続殺人事件に巻き込まれていく。凝ったセリフや小道具が粋な印象を与え、従来のバイオレンス・ポルノとは一味も二味も違う作品に仕上がっている。探偵役に内田裕也。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Elvis", "ザ・シンガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "　アメリカを代表する大スター、エルビス・プレスリー。その青春時代、そして芸能界での活躍の裏側を描く伝記ドラマ。150分のテレビ映画を再編集したもの。1969年、ライブ・ステージを控えたエルビスが過去を振り返る。母グラディスの愛情を受けて育ったエルビスは学生時代から女の子たちの人気者。高校卒業後にレコードを録音。曲のヒット、テレビ出演と順調にキャリアを積んだのち、軍隊に召集されることに……。カート・ラッセルがエルビス役で熱演。", "1979-01-01", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あしたの私のつくり方", "あしたの私のつくり方", "", "", "http://image.eiga.k-img.com/images/movie/33953/315x210.jpg?1307", 97, "「神童」など話題作への出演が相次ぐ成海璃子主演の青春ドラマ。学校でも家でも周囲に自分を合わせてしまう女子高生・寿梨は、優等生からイジメられっ子に変わってしまった小・中学校時代の同級生・日南子に、偽名で携帯メールを送り始める。“本当の自分”と“偽りの自分”との間で葛藤し成長していく思春期の少女たちの心情を、「トニー滝谷」の市川準監督がみずみずしいタッチで描き出す。共演はアイドルグループAKB48の前田敦子。", "2007-04-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARMEN JONES", "カルメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ビゼーの歌劇『カルメン』を、O・プレミンジャーがオール黒人キャストで映画化した異色ミュージカル。第二次大戦中、黒人部隊の伍長ジョーは、近くのパラシュート工場で働くカルメンに誘惑され、その情熱の虜になっていく。O・ハマースタイン II世の舞台劇が下敷きとなっている。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　OUTLAW", "ならず者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「アビエイター」で描かれた航空王＆映画製作者のH・ヒューズが監督も兼任したウエスタン。お尋ね者ビリー・ザ・キッド、医者兼ギャンブラーのドク・ホリデイ、保安官パット・ギャレットなど、後発の西部劇でも取り上げられた人気キャラクターがずらり。グラマー女優J・ラッセルが渇いた男たちのドラマに華を添える。それぞれの男たちがプライドを懸け、熱砂舞う砂漠で壮絶なガン・ファイトを繰り広げる。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2011-2012 ワーグナー「ニーベルングの指環」　第2夜「ジークフリート」", "METライブビューイング2011-2012 ワーグナー「ニーベルングの指環」　第2夜「ジークフリート」", "", "", "http://image.eiga.k-img.com/images/movie/57163/315x210.jpg?3817", 356, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を映画館で上映する「METライブビューイング」2011～12年シーズンの3作目。ワーグナーの全4部からなる大作オペラ「ニーベルングの指環」の第3部。ジークムントとジークリンデの遺児ジークフリートは、世界を支配できる「指環」に呪いをかけたアルベリヒの弟ミーメに育てられた。ミーメは、大蛇に姿を変えた巨人ファーフナーが持つ指環をジークフリートに奪わせようと企むが、名剣ノートゥングを鍛え上げたジークフリートは、大蛇から指環を奪うとミーメも手にかけてしまう。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Felicia's Journey", "フェリシアの旅", "", "", "http://image.eiga.k-img.com/images/movie/1853/315x210.jpg?2913", 116, "アイルランドに住む17歳の少女フェリシアは、行き先も告げずに去って行ってしまった恋人を探すため、海を渡ってイギリス・バーミンガムにやってくる。見知らぬ 土地で途方に暮れる彼女に親切に声をかけたのは、大きな邸に一人で住む太った中年男ヒルディッチだった。だが、この気のよさそうな男には誰にも知らない秘密の顔があった。そして、一度も愛された事のない男は、自分を救ってくれる少女を待っていた……。", "2000-03-18", "3,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASTLE FREAK", "キャッスル・フリーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「ZOMBIO　死霊のしたたり」を手掛けたS・ゴードンによるスプラッタ・ムービー。古城を相続するために、妻スーザンと目の不自由な娘レベッカを連れてイタリアへ渡ったアメリカ人のジョン。彼は城に着くなり、世話人のアグネーゼから“この城は悪魔に取り憑かれている”と警告される。戯言だと取り合わなかったジョンだが、身の回りで不可解なことが頻発し、次第にアグネーゼの忠告を信じ始める。そしてついに、城の迷路の奥に、人間の姿をした化け物が住み着いているのを発見してしまう。化け物はレベッカを我がものにしようと狙っており、邪魔者を、次々に殺していく……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Serpent and the Rainbow", "ゾンビ伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　学者のデニスは死者を甦らせる秘薬を求めて南米ハイチを訪れる。秘密警察が暗躍する中、デニスは禁断の領域へと足を踏み入れ、やっとのことで人をゾンビに変える薬を手に入れる。しかしブードゥの呪いが人々を死へと至らしめ、やがてデニスも生きながら葬られることに……。実在の人類学者によるノンフィクションを映画化。ブードゥ教の魔術が生む恐怖を描いた異色のリアル・ホラー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワイルド・ゼロ", "ワイルド・ゼロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "海外でも人気のあるロック・バンド、ギターウルフの主演による異色活劇。SF、アクション、ホラーに純愛、ロックンロールと、ごった煮のB級テイストが楽しめるパワフルな娯楽作となっている。とある町に謎のゾンビ軍団が出現。ギターウルフの追っかけをしている少年エースは、偶然知り合った美少女トビオを救うために奮闘。一方、ツアーでこの町を訪れたギターウルフは悪徳プローモーターやソンビ、さらにはUFOを相手に一大バトルを繰り広げる。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents kiss!　「あとのまつり」", "桃まつり presents kiss!　「あとのまつり」", "", "", "http://image.eiga.k-img.com/images/movie/57913/315x210.jpg?2820", 18, "", "2009-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の慕情", "風の慕情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "時折、文通があるだけで、顔も知らない姉に会おうとオーストラリアに向かった主人公、長谷由布子。しかし降り立ったシドニー空港に彼女の姿はなく、由布子はその消息を探るが……。オーストラリア、マニラにロケしたエキゾチック・サスペンス。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("淑女と髯", "淑女と髯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "大学の剣道部主将・岡島は、そのヒゲ面が災いして就職試験に落ちても全く動じることのない剛の者。しかし、あるオフィスガールに忠告されてヒゲを剃ってみたら、一転してスマートな二枚目と化してしまい、周囲の女性たちからモテモテの存在となっていくが……。サイレント映画時代の小津安二郎監督が最も得意としたナンセンス喜劇の代表格ともいえる秀作。", "1931-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エイジアン・ブルー 浮島丸サコン", "エイジアン・ブルー 浮島丸サコン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "1945年８月24日、青森での厳しい労働から解放され、帰国の途にあった数千人の朝鮮人たちを乗せた輸送船、浮島丸が京都・舞鶴湾で爆沈した。戦後50年を経て、在日韓国・朝鮮人の大学講師・林は、教え子の姉妹とともに当時を知る人々の話を聞いていくうち、韓国・朝鮮人たちが置かれた過酷な環境を追体験する……。平安遷都1200年記念映画を作るべく、京都市民が始めた製作支援募金運動に支えられた作品。“戦後50年を問う”という視点が貫かれ、浮島丸事件を通して反戦の痛みと重さが伝わる。堀川弘通が６年ぶりに監督に当たり、ここでも誠実さと堅実な力量を発揮している。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大夜逃 夜逃げ屋本舗３", "大夜逃 夜逃げ屋本舗３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "“夜逃げ屋本舗”のシリーズ第３作。今回の敵は、不良債権の回収代行業者。彼らは、“三食昼寝付きで借金チャラ”と甘い言葉で負債者たちを誘って１ヵ所に住まわせ、過酷な労働を強制、毎月の給料から家賃・生活費を引いた上で残額を返済に充てさせている。そんな“負債者たちの強制収容所”の住人30人を一気に夜逃げさせることはできるのか？　シリーズ史上空前の大脱出劇。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はだかっ子", "はだかっ子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 147, "母と二人だけの貧しい生活を送りながらも元気に生きていく少年の姿を描いたもの。近藤健の同名児童文学を映画化したもので、脚色は田坂監督の前作「親鸞」に続いて成沢昌茂が担当している。東映が量産体制確立のため設けた第二東映系統が、次第に行き詰まり“ニュー東映”と改称したわずか10ヵ月ほどの間に作られた作品。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぐるりのこと。", "ぐるりのこと。", "", "", "http://image.eiga.k-img.com/images/movie/53383/315x210.jpg?2316", 140, "「二十才の微熱」「渚のシンドバット」の橋口亮輔監督が「ハッシュ！」以来6年ぶりにメガホンを取った人間ドラマ。バブル崩壊から21世紀にかけて社会が激変した時代を背景に、実際に起こった犯罪・事件を織り込みながら、ある夫婦が辿る希望と再生の10年間を描く。主演はともに映画初主演となる木村多江とリリー・フランキー。共演に倍賞美津子、寺島進、柄本明、寺田農ほか。", "2008-06-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フリージア・極道の墓場", "フリージア・極道の墓場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "引退した伝説のヒットマンと、憧れの彼を第一線に復帰させようと執念を燃やす青年の確執を、哀感込めて描いていく。三浦友和が主人公を静かに熱演。彼のためなら飲めない酒も無理して飲むという憎めない悪役に扮した哀川翔のほのかなユーモアも心に残る。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霊界の扉　ストリートビュー", "霊界の扉　ストリートビュー", "", "", "http://image.eiga.k-img.com/images/movie/56863/315x210.jpg?3896", 76, "インターネット上でささやかれる都市伝説に遭遇してしまったヒロインの恐怖を描くホラー。ある日、自宅の部屋から妹の真菜が突然いなくなってしまった亜子は、大学の親友・佐知とともに、真菜がいなくなったときに開かれていたパソコンのストリートビューの画面をもとに妹を捜しはじめる。最初に目をつけた古民家に向かうも、これといった手がかりがつかめなかった亜子は、公園で不思議な少女・舞と出会い……。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画クレヨンしんちゃん　嵐を呼ぶ歌うケツだけ爆弾！", "映画クレヨンしんちゃん　嵐を呼ぶ歌うケツだけ爆弾！", "", "", "http://image.eiga.k-img.com/images/movie/33933/315x210.jpg?3421", 102, "人気アニメ「クレヨンしんちゃん」の劇場版第15弾。野原家の愛犬シロのお尻に“ケツだけ星人”が落とした爆弾がくっついてしまった。地球を吹き飛ばすほどの破壊力を持つその爆弾を巡り、宇宙監視センター“UNTI”と美人テロ集団“ひなぎく歌劇団”が追跡劇を繰り広げる。一方、UNTIが爆弾をシロごとロケットに乗せて宇宙で爆破させようとしていることを知ったしんのすけは、シロを連れて逃亡するが……。", "2007-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEVERENDING STORY", "ネバーエンディング・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「U・ボート」で勇名を馳せたW・ペーターゼン監督が、M・エンデのファンタジー小説を2700万ドルの巨費を投じて映画化。少年バスチアンが、一冊の本の中に描かれた世界へ入り込み、その世界の危機を救うため大活躍をする。最新のSFXを駆使して作られた数々のクリーチャーたちが評判を呼んだ。リマールによる同名の主題歌も世界中で大ヒットを記録した。", "1984-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SENTINEL", "センチネル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "邦題『悪魔の見張り』というオカルト・スリラー小説の映画化。ニューヨークのトップモデルのアリスンが新しく借りたアパートは、実は悪魔の棲む家であった。異様な住人たちはアリスンを地獄へ誘い込もうと様々な行動をとるのだが……。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dreamgirls", "ドリームガールズ", "", "", "http://image.eiga.k-img.com/images/movie/1053/315x210.jpg?1299", 131, "81年暮れにニューヨーク・ブロードウェイで産声をあげた大ヒットミュージカルをオスカー受賞作「シカゴ」（02）の脚本家ビル・コンドンが映画化。60～70年代のモータウンサウンド隆盛期を背景に、3人の女性シンガーたちのサクセスストーリーとその後の人生が描かれる。主演はオスカー俳優ジェイミー・フォックス、ビヨンセ、エディ・マーフィ。第79回アカデミー賞最多の6部門8ノミネート。", "2007-02-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まらそん侍", "まらそん侍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "戦前のナンセンス時代劇の系譜を継ぐ佳作。家老のおてんば娘を手に入れようと、若侍たちが遠足（とおあし）の儀と呼ばれるマラソンにしのぎを削る。これに泥棒のトニー谷が絡み、レースは大混乱。大泉滉のライトな走りっぷりが爆笑をさそい、トニー谷も最高だ。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEFENDING YOUR LIFE", "あなたの死後にご用心！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "２度のアカデミー賞受賞女優M・ストリープのハートウォーミングなコメディ。交通事故で死んだダニエルは、ジャッジメント・シティという裁判の町に送り込まれる。ここは生前の行いを審査して被告を次の世界に進めるか、再び下界に戻すかを決める所。そこでダニエルは知的な女性ジュリアと恋に落ちてしまった。彼女と過ごす日々は、文字通り夢のよう。この生活を続けるにはなんとしても裁判に勝たなければいけない。だが、いつも苦難を避けて生きてきたダニエルにとって、勝ち目はない。判決の日は刻々と迫っていた。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOBODY’S PERFECT!!", "いつも一緒にいて欲しい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "テニス部の可愛い女の子シェリーに接近するため、女装してステファニーと名乗り、まんまとシェリーのルームメイトになったスティーブン。だがルームメイトとして同棲生活を続けるうち、女の子の気持ちを身をもって理解するようになって……。恋する学生の青春騒動を描いたロマンティック・コメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OH，WHAT A NIGHT", "抱きしめたい夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "17歳のエリックはある日、美しい人妻ベラが裸で水浴びしているところを偶然のぞき見してしまう。その官能的な光景に目を奪われ、彼女に思いを寄せ始めるが……。『オンリー・ユー』などのオールディーズ・ナンバーに乗せて、年上の女性との一夏の経験を描いた青春映画。", "1992-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不良番長", "不良番長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "新宿を根城に、いかなる組織にも属さず、恐喝・婦女暴行は朝飯前の極悪不良グループ、カポネ団の死闘を描く長寿シリーズ第１作。日活の“野良猫ロック”シリーズに先駆けたこの“不良グループ”ものの原典はアメリカのヘルス・エンジェルを扱った「地獄の天使」にあるということだ。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("涙の連絡船", "涙の連絡船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "桜島で観光客を相手におみやげ店と喫茶店をやっている娘・はるみ。彼女は兄が船長をしている連絡船で働く男・赤木健一にひそかに想いを寄せているのだが、なかなか相手にしてもらえず……。都はるみの大ヒット曲をメロドラマ仕立てで描いた歌謡映画。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マクロスプラス／MOVIE EDITION", "マクロスプラス／MOVIE EDITION", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "宇宙のとある惑星エデン。そこでは次期主力戦闘機の最終選定試験が行われていた。かつて親友だったイサムとガルドが戦闘機のテストパイロットに選ばれるが、二人は何故か衝突を繰り返してばかり。二人の間で思いの揺れるミュンは、毎日心を痛めていた。ある日、コンピューターが生んだバーチャルアイドル、シャロンが彼らの思いをその体に取り込んでしまい……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANTASIA 2000", "ファンタジア2000", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ウォルト・ディズニーが1940年に製作した不朽の名作「ファンタジア」。そのアニメーションとクラシックを融合させるという画期的な試みにディズニーの新世代のクリエイターたちが挑戦。オリジナルの一編「魔法使いの弟子」をデジタル復元したほか、新たに７つのパートを製作し、映像と音楽の共演を実現させた。名指揮者ジェームズ・レバインの指揮によるシカゴ交響楽団の演奏も話題となった。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　SILENCES　DU　PALAIS", "ある歌い女〈うたいめ〉の思い出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "王宮の召使の娘アリヤは、王制が廃止された現在は酒場の歌手として生計を立てている。アリヤは妊娠しているが、恋人は出産を望んでいない。そんな時、実父のように可愛がってくれた皇太子のシド・アリの訃報が届く。10年ぶりに訪れた王宮で、アリヤは母と過ごした幼い頃の記憶をたどっていく……。1950年代のチュニジア王政最後の王宮を舞台に、王宮に使える召使として生きた母と娘の葛藤を描いた人間ドラマ。過去と向き合った娘が、母の生き様を認め強く生きる決心を抱くまでをチュニジア出身の女性監督が、抑制の効いた表現のなかに情熱的な成熟を感じさせる演出で見せる。チュニジア音楽の復権者といわれるアヌアル・ブラヒムによる、劇中に流れるウードの調べも秀逸。", "1994-01-01", "77,2", 0, NOW(), NOW());
