INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BERNIE", "ベルニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "赤ん坊の頃から30年間、孤児院で育った男ベルニーが施設を旅立つ。自分が生後二週間でゴミ箱から発見された捨て子だったと知った彼は、様々な出生の秘密を想像しながら、本当の両親の行方を突き止めていく……。1964年生まれのフランス人俳優A・デュポンテルが初監督し、脚本、主演を兼任したブラックな愛と笑いの家族ドラマ。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Touch the Sound: A Sound Journey with Evelyn Glennie", "Touch the Sound タッチ・ザ・サウンド", "", "", "http://ecx.images-amazon.com/images/I/51efoEn8HcL._SL210_.jpg", 100, "聴覚障害を持ちながらも2度にわたってグラミー賞に輝いたスコットランド出身の女性パーカッショニスト、エブリン・グレニーを追ったドキュメンタリー。体のあらゆる感覚を使って音を感じているというエブリンに密着取材。彼女の不思議な音の世界を、美しい映像とともに体感することができる。アバンギャルド・ロックの巨匠フレッド・フリスや創作和太鼓集団・鬼太鼓座とのセッションなど、貴重な演奏シーンも満載。", "2006-03-11", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLIDING　DOORS", "スライディング・ドア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ヒロインが電車に乗ることができた場合と乗り遅れた場合の、２パターンのドラマが同時進行するユニークなラブ・ストーリー。電車に乗ったヘレンは、同棲中の恋人の浮気現場に遭遇。新規の事業に打ち込むことで失恋の痛手を和らげ、やがて新しい恋に出会う。一方、乗り遅れた場合のヘレンは、恋人の浮気を知らずに済むが、薄々それを感じとり、ストレスをため込んでしまう……。G・パルトロウがヒロインを演じ分け、恋に揺れる女性の心理をヴィヴィッドに体現している。", "1997-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Thing", "遊星からの物体X", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "南極の大雪原。一匹の犬がアメリカの観測隊基地に逃げ込む。しかし犬の正体は10万年前に宇宙から飛来した生命体だった。氷の中から甦った生命体は地球上の生物に襲いかかり、それと同化していた。犠牲者が続出する中、隊員たちは互いのことを生命体ではないかと疑い始める……。リアルでグロテスクな生命体のVFXが話題となったSFホラー。ハワード・ホークス製作の古典的名作「遊星よりの物体X」のリメイク作品。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さすらいの恋人 眩暈", "さすらいの恋人 眩暈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "都会の片隅の公園で出会った男と女。女は何やら傷心のようだが、男は女を自分の部屋へ連れて行き、その夜二人は結ばれる。男はその後、自分たちのセックス・ショーで金儲けを企むが……。冒頭シーンから、アンニュイな空気を漂わせながらもそれぞれの孤独を感じさせる小沼監督の演出力。けだるい雰囲気を醸し出す小川恵の起用とともに、哀愁感に包まれたラブ・ストーリーが魅力の秀作。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちゃんこ", "ちゃんこ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "実話を基にした爽快な青春スポーツ・ムービー。バイトと勉強に明け暮れる女子大生の由香は、相撲部主将の留学生カブレラに勧誘されて、ぶつかり稽古に参加。相撲の魅力に目覚めて入部するが、部員はカブレラと由香の二人しかおらず……。廃部寸前の相撲部を救おうと、女子大生と外国人留学生が大奮闘。いわゆるスポーツ群像劇とは一線を画し、孤独な魂を持つ学生たちが己のプライドを懸けて土俵に踏み込んでいく姿を活写している。ありきたりの感動ドラマとは、ひと味もふた味も違う硬派な余韻が新鮮だ。", "2006-03-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("涼宮ハルヒの消失", "涼宮ハルヒの消失", "", "", "http://image.eiga.k-img.com/images/movie/55063/315x210.jpg?1359", 0, "テレビアニメ化され人気を博した、谷川流によるライトノベル「涼宮ハルヒ」シリーズの劇場版。“宇宙人や超能力者を探し出して遊ぶ”という目的で「SOS団」を設立した涼宮ハルヒは、美少女だが変わり者の女子高生。クリスマス間近のある日、「SOS団」のメンバー・キョンが学校に行くと、後ろの席にいるはずのハルヒがいない。代わりにハルヒの席には、かつてキョンを殺そうとしていた朝倉が座っていた……。", "2010-02-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lady", "The Lady ひき裂かれた愛", "", "", "http://image.eiga.k-img.com/images/movie/57673/315x210.jpg?3280", 0, "ビルマ（現ミャンマー）の民主化のために活動を続けているアウンサンスーチーの半生をリュック・ベッソン監督、ミシェル・ヨー主演で映画化。", "2012-07-21", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Cat", "The Cat ザ・キャット", "", "", "http://image.eiga.k-img.com/images/movie/58063/315x210.jpg?2512", 105, "ドラマ「トキメキ☆成均館スキャンダル」「シティーハンター in Seoul」などで人気の韓国若手女優パク・ミニョンが映画初出演で主演を務めたホラー。幼い頃のトラウマで閉所恐怖症に悩むソヨンは、ペットショップで動物たちに癒されながらトリマーとして働いていた。ある日、ソヨンが担当した猫のビダンを迎えにきた飼い主がエレベーターで謎の死を遂げ、残された猫を引き取った友人までもが変死を遂げる。これらの不可解な事件と並行して、ソヨンは不思議な幼い少女の幻覚が見えるようになり……。", "2012-05-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RESURRECTION", "レザレクション 復活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "交通事故で生死の境をさまよい、奇跡的に命を取り留めた主人公のエレナ。彼女はそれ以来、手で触れるだけで、病人やケガ人を治す不思議な力を身につける。人々は神の復活のごとくエレナを迎え、時がたつにつれ熱狂的な支持者が増えていく。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEN LITTLE INDIANS", "アガサ・クリスティー サファリ殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "A・クリスティの傑作『そして誰もいなくなった』の４回目のリメイク作。1930年代、東アフリカのサファリ・キャンプを舞台に、互いに初対面の10人が、何者かに一人ずつ殺されるたびに、テーブルに置かれた10体の木彫りのインディアン人形の首もなくなる怪事件が起こる。多彩な殺人方法が展開するサスペンス・ミステリー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COUNTRY", "カントリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "大恐慌以来の農業危機といわれた、当時の米国農業の経営難を描いた問題作。物語は、アメリカ中西部・アイオワ州の農家の働き者の農婦が、不作や借金、役立たずの夫など、数々の困難な問題を抱えながら、それを乗り越えていくさまを描くというもの。ラングが農婦を熱演。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Quattro Mosche Di Velluto GrigIo", "4匹の蝿", "", "", "http://image.eiga.k-img.com/images/movie/55443/315x210.jpg?2445", 101, "イタリアン・ホラーの巨匠ダリオ・アルジェントによる「歓びの毒牙」（1969）、「わたしは目撃者」（70）に続く動物3部作の最終章。73年に日本で初公開され、37年ぶりとなる10年にリバイバル上映される。ロックバンドの若きドラマー、ロベルトは、執ように自分をつけ回す正体不明の中年男に悩まされていた。そしてある夜、その男と激しい口論になったロベルトは、あやまって男を刺し殺してしまう。その瞬間、不気味な仮面の男に現場の写真を撮られ、その事件をきっかけにロベルトの周囲で不可解な殺人事件が起こり始める……。", "2010-06-19", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RESONANCE", "レゾナンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 11, "ゲイに対する差別・偏見を暴力という形でぶつけてくるゲイ・バッシング。自らがその犠牲となった経験から肉体的な暴力に対する心理的リアクションを映像化した短編。俳優の言葉に頼らず、ダンス、ジェスチャー、モノローグに音楽を加えることで、ゲイ・バッシングによる静かなる波紋を描き出している。なお、ビデオは「ルッキング・フォー・ラングストン」に収録されている。", "1991-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Shout", "ザ・シャウト／さまよえる幻響", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「出発」「早春」などで知られるポーランドの鬼才イエジー・スコリモフスキによる異色作。ある精神病院で行われたクリケット大会で、スコアラーを務めるアンソニーが謎めいた患者の話を聞くことに。それは叫び声によって人間を殺すことができる男についての異常な話だった……。一種のスリラー映画とも哲学ドラマとも見なせる奇妙な作品。太古の超能力を身につけた男が、浜辺の羊たちを皆殺しにするシーンの迫力に圧倒される。", "1978-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・深夜族", "喜劇・深夜族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "伴淳三郎主演の爆笑コメディ。重盛幸子は昼は店員、夜は内緒でコールガールをしている。彼女の父親・平三もまた守衛のふりをしているが実はポン引きの名人。そんな平三に久一が弟子入りし、騒動がもち上がる……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAY", "MAY〈メイ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "恐怖、笑い、哀感が入り交じったユニークなホラー映画。母親からもらった人形のスージーが唯一の友達という、風変わりな女の子メイ。人付き合いの苦手な彼女は、仲良くなった同僚と上手くいかず、恋をしても相手に避けられたり……。そんなメイは“友達がいないなら造ればいい”と、恋人や同僚たちから美しい身体のパーツを集め完璧な“友達”を造ろうとする。身の毛もよだつストーリーだが、監督のL・マッキーは孤独ゆえの狂気を、無垢で残酷な愛の物語として描き出している。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kamataki", "窯焚 KAMATAKI", "", "", "http://image.eiga.k-img.com/images/movie/53353/315x210.jpg?3898", 110, "これまでも日本に関わる作品を数多く手掛けてきたカナダのクロード・ガニオン監督が、陶芸の一過程である“釜焚き”を題材に描いたヒューマン・ドラマ。22歳の日系カナダ人ケンは、生きる希望を失い飛び降り自殺を図るが奇跡的に命をとりとめる。彼は母の計らいで、亡き父の兄で日本に住む著名な陶芸家・琢磨の元に滞在することになるが……。第29回モントリオール国際映画祭で最優秀監督賞をはじめ5部門に輝いた。", "2008-02-23", "76,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("He Got Game", "ラストゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "　天才的な才能を持つバスケットボール・プレイヤー、ジーザスは高校卒業を控え、大学進学か、プロ入りするか、その選択に悩んでいた。そんな折、父親ジェイクがジーザスの前に現れる。母親の死にまつわる事件で服役していたジェイクは裏取引によって仮出所したのだ。ジーザスは父に対して冷たい態度をとる一方、厳しい現実を知ることになるが……。父子の絆、そしてスポーツ界の暗部を描くシリアスドラマ。バスケットのスター選手も多数ゲスト出演している。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南京 戦線後方記録映画", "南京 戦線後方記録映画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "1937年。蒋介石軍の抵抗を制して日本軍が占領した南京。激戦の跡も生々しい城壁、中国軍捕虜のうつろな表情、つかの間の休息を得て再び前線に向かう日本軍兵士の行軍などをリアルに捉え、戦争の勝者と敗者の隔たりを超えた悲哀を映し出している。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Invisible Waves", "インビジブル・ウェーブ", "", "", "http://image.eiga.k-img.com/images/movie/1883/315x210.jpg?4254", 115, "「地球で最後のふたり」（04）の浅野忠信、ペンエーグ・ラッタナルアーン監督、クリストファー・ドイル（撮影）によるノワール風のロード・ムービー。不倫関係にあったボスの妻を殺し、逃亡の旅に出た料理人キョウジの魂の漂流とその運命を描く。共演は「オールド・ボーイ」のカン・ヘジョン、「インファナル・アフェア」のエリック・ツァンら。", "2007-05-26", "23,70,6,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CIGALA", "スキャンドール 禁じられた体験", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "性欲の強い母、好奇心でいっぱいの娘、この二人のすさまじいばかりの葛藤を描くイタリア映画。放浪の末、ある町のレストラン経営者と結ばれたウイルマ。旅の途中で知り合った若い女友だちと、若い頃生んだ娘も呼び寄せて女３人、男一人の生活が始まった。", "1980-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若き日は悲し", "若き日は悲し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "貧しいが明るく育つ園田一郎、恵子の兄妹。恵子には音楽学校へ通いたいという夢があったが、一郎の親友で恵まれた暮らしをする正二と結婚することによってその夢がかないそうになる。だが一郎はひそかに恵子を愛し始めていた。実は、彼らは本当の兄妹ではなかった。美空ひばり主演の悲恋メロドラマ。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裏切りの暗黒街", "裏切りの暗黒街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "極東商事社長・岩崎が財界の黒幕・大和田から麻薬を買うためにそろえた金５億。矢島、田熊、松木、小池の４人は、見事その強奪に成功する。社長と黒幕は必死にその金を取り戻そうとし、犯行を目撃した少年を人質に取って矢島たちを脅す。メロドラマ・アクションの名手・降旗康男が初期に監督したノワール・ギャングもの。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOODLESS", "BLOODLESS", "", "", "http://image.eiga.k-img.com/images/movie/57773/315x210.jpg?2645", 45, "成績不振の罰のため休日の学校で清掃をしていた女子高生・月子と陽子は、突然現れた「チョコレート」と名乗る学生集団に拘束されてしまう。彼らはエジプトで起こったFecebook革命に触発され、自分たちもインターネットを介して革命を起こそうとしていたが……。「水霊　ミズチ」「END CALL」の山本清史監督が、アクションやサスペンスの要素も盛り込んで手がけたオリジナル作。山本監督と交流があり、互いにライバルと認め合う寺内康太郎監督（「ほんとうにあった怖い話」「BOYS LOVE」）のオリジナル作「菊地朔太郎之英雄的行為」と同時上映。", "2012-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRAINSTORM", "ブレインストーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "女性科学者リリアンらによって開発された新型マシーンは、人間の持つ思考、記憶、視聴覚などの全思考意識の体験感覚を他人と共有できるという画期的なものだったが……。「2001年宇宙の旅」「未知との遭遇」の特撮で有名なD・トランブルが、最新SFXを駆使したSF。N・ウッドは撮影中のボート転覆事故により水死。本作が遺作となった。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERO", "HERO", "", "", "http://image.eiga.k-img.com/images/movie/1903/315x210.jpg?2078", 130, "01年に放映され、シリーズ平均視聴率34.3％を記録し、フジテレビの歴代ナンバーワンヒットとなったTVドラマの映画版。沖縄、北海道、山口を経て、6年ぶりに東京地検城西支部へと舞い戻った久利生公平検事（木村拓哉）の活躍を描く。久利生に立ちはだかる敏腕弁護士・蒲生に松本幸四郎が扮する他、森田一義、イ・ビョンホンがゲスト出演。監督・脚本はTVシリーズと同じ鈴木雅之と福田靖。", "2007-09-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道戦国志 不動", "極道戦国志 不動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "成績優秀な高校生を装いながら、その裏では九州を支配する極道組織・九州仁王会の中心一派・不動一家の若頭でもある不動力。彼は冷徹な頭脳と行動力を武器に、腐りきった極道社会を一掃するという大胆不敵な計画を胸に秘めていた。力は計画の一歩として、ひそかに養成した暗殺者を使い、九州仁王会の最高幹部たちを次々と葬り去っていく。一方、日本の裏社会を手中に収め、世界制覇をも画策する神戸夜叉組組長・能間大元は、野望の障害となる力の暗殺を企てる。過激な暴力描写で成人指定を受けたバイオレンス作品。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRANDEUR ET DE´CADANCE D’UN PETIT COMMERCE DU CINE´MA", "映画というささやかな　商売の栄華と衰退", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "J・L・ゴダールが、フランスのペイTV、カナル・プリュス用に製作した作品で、日本ではホール公開された。映画会社を舞台に、オーディションを繰り返す映画監督と資金繰りに奔走するプロデューサー、さらには女優に憧れるその妻という３人を主人公にしながら、映画に死んでいった者たちの姿を描く。主演はトリュフォー映画になくてはならなかったJ・P・レオー、プロデューサー役にはヌーヴェル・ヴァーグ派と同世代の映画監督J・P・モッキーが扮し、ゴダール自身も顔を見せる。映画の下敷きとなったのは、J・H・チェイスの小説『ソフト・センター』だが、タイトルはバルザックの短編『セザール・ビロトーの栄枯盛衰』をもじっている。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLIPPER’S NEW ADVENTURE", "頑張れフリッパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "新しく国道が建設されるため、住みかがなくなったイルカのフリッパーと暮らすためサンディは家出をする。無人島にたどり着いた彼らは楽しく暮らし始めた。ところが、そこには３人の脱獄囚が隠れていて、バカンスを楽しんでいた一家のヨットを奪い取ろうとする……。人気TVシリーズ「フリッパー」の映画化第２作。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渚の白い家", "渚の白い家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "夫の転職に伴ってハワイで暮らす妻が、疑惑と妄想の果てに夫への愛を失い、当地の青年と激しい恋に落ちるというサスペンス・メロドラマ。ヒロイン、浅丘ルリ子の恋人役として登場の名高達郎はこれがデビュー作。浅丘ルリ子のファッションも見もの。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POUR LA PEAU D’UN FLIC", "危険なささやき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "私立探偵シュカは、ある日老婦人から盲目の娘を捜してくれとの依頼を受ける。だが、調査を始めたとたんシュカは何者かに襲われ、依頼人も殺害されてしまう。謎の殺人者に追われながら闇から闇へ犯人を追い詰めていく彼の運命はいかに……。A・ドロンが監督、主演したハードボイルド・アクション。", "1981-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("100,000 DOLLARS AU SOLEIL", "太陽の下の10万ドル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "陽光眩しい北アフリカを舞台に展開する痛快犯罪アクション。10万ドルの武器を輸送中に強奪したトラック運転手と、執拗に彼を追う運送会社の社長の攻防劇を、骨太なタッチで描く。豪快で男気あふれる主人公をJ・P・ベルモンドが軽妙かつ個性的に演じる。", "1964-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Reset２／足軽・ashigaru", "Reset２／足軽・ashigaru", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "脚本なしのワンカットで撮影され、海外でも反響を呼んだ「Reset」のシリーズ第２作。現代劇だった前作から一転、戦国時代に舞台を設定して、戦の前の緊張をリアルに切り取っていく。1600年9月15日、日本史上最大の合戦が行われようとしている関ケ原。故郷の敦賀を後にした若者３人組は戦に参加するため、それぞれの胸にある思いを秘めて、この地へやって来るが……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENOUGH", "イナフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "ジェニファー・ロペス主演によるサスペンス・スリラー。幸せな生活を送っていた妻が、豹変した夫のストーカー行為の恐怖にさらされる。建設会社を経営するミッチと結婚したスリムは、すぐ子宝にも恵まれ順風満帆な生活を送っていた。しかし夫の強い愛情が歪んだ方向へと向かう。暴行を受けるようになった彼女は離婚を決断するが、夫は同意せず……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新吾二十番勝負・完結篇", "新吾二十番勝負・完結篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "恩師の仇・弥次郎を斬り山を降りる新吾は、僧侶・弘徳に会い“すべてを救う道は剣ではなく将軍家の息子になることだ”と諭される。しかし彼は、野盗一味に不覚をとり、その巣窟に連れ込まれる。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOR　EVER　MOZART", "フォーエヴァー・モーツアルト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "演劇、戦争、映画、音楽の４つのパートから構成されるJ・L・ゴダール監督作。「フォーエヴァー・ゴダール」シリーズとして上映された。久々に新作の監督を依頼されたビッキーは、娘カミーユのことが心配だった。彼女はビッキーの妹の息子ジェロームとともに、戦火のサラエボで演劇を上演するというのだ。仕方なく、ビッキーは娘たちと一緒に旅に出るが……。", "1996-01-01", "2,26,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("平手造酒", "平手造酒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "悲劇の剣客、平手造酒の半生を描いた作品。千葉周作門下として将来を期待されていた平手造酒だが、仕官の道は閉ざされ恋人は嫁に行きと、事がうまく運ばず、ついには酒びたりとなって喀血する。彼の腕を見込んだ渡世人・笹川の繁蔵は、平手を用心棒に雇った。そして繁蔵と飯岡の助五郎との間でケンカが持ち上がり……。山村聰が、自分の意に反して、孤独の中へ落ち込んでいく平手を悲愴感タップリに演じている。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE／JUICE", "LOVE／JUICE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "フジテレビの深夜番組『つんくタウン』から生まれた作品。監督・脚本は名匠、新藤兼人の孫娘である新藤風。真っ赤な一軒家で共同生活を送るノーマルな女の子、今日子と同性愛者の女の子、千夏。友人として暮らしていた二人だったが、今日子が熱帯魚屋の青年に恋したとき、千夏は彼女に思いを寄せ始める……。これが映画監督としてのデビュー作となる新藤風が、微妙な関係に陥った二人の揺らぐ心をセンシティブな映像で捉えていく。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("給的信／MY　FATHER　IS　A　HERO", "D&D／完全黙秘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "世界へと活動の範囲を広げるリー・リンチェイがジェット・リーと改名して臨んだ第１作。犯罪組織壊滅のためなら世界のどこにでも潜入し、常にトップ・シークレットの任務を遂行する中国政府の機密エージェント、クン・ウェイ。ある日、香港警察をも敵に回し、犯罪組織で侵入捜査を行うウェイのもとに妻の死の知らせが届く。残された息子に思いをはせながら、家庭より任務を優先させざるをえない彼は孤立感に思い悩む。そんな折、犯罪組織が彼の息子を襲う。香港警察、犯罪組織、そして息子にさえ我が身を欺かなければならないウェイの怒りが爆発した。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JERSEY GIRL", "世界で一番パパが好き！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ベン・アフレックがシングル・ファーザーに扮するハートフル・ドラマ。娘との平凡な暮らしに喜びを感じる一方で、かつての仕事に復帰したいという情熱を抱えた男の葛藤を体現している。当時ベンと恋人同士だったジェニファー・ロペスが、妻役で出演している点にも注目を。妻が出産と同時に他界し、生まれた娘を周囲に助けられながら育てるオリー。彼は愛する娘のために良き父親になろうと奮闘するが……。", "2005-03-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タクミくんシリーズ　そして春風にささやいて", "タクミくんシリーズ　そして春風にささやいて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "シリーズ累計400万部の大ヒットを記録したごとうしのぶによるボーイズラブ小説「タクミくんシリーズ」の第1作を実写映画化。名門男子校を舞台に、少年たちが織りなす切ない恋模様を描く。人里離れた山中に建つ全寮制の男子高校・祠堂学院。2年生に進級したタクミは、学校のアイドル的存在であるギイとルームメイトになる。過去のトラウマで“人間接触嫌悪症”になってしまった託生だが、ギイの深い愛情によって少しずつ心を開いていく。", "2007-12-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lions for Lambs", "大いなる陰謀", "", "", "http://image.eiga.k-img.com/images/movie/53073/315x210.jpg?1857", 92, "トム・クルーズが自らエグゼクティブを務める新生ユナイテッド・アーティスツの第1回作品。ロバート・レッドフォード、メリル・ストリープ、クルーズの3人を主演に、対テロ戦争を推し進める若き政治家（クルーズ）と彼の考え方に疑問を抱く女性ジャーナリスト（ストリープ）、そして教え子を戦地へと送り出してしまった大学教授（レッドフォード）のそれぞれの立場や思惑を描きながら、現在のアメリカの対外戦略の問題点をあぶり出す。監督は本作で長編7作目となるレッドフォード。", "2008-04-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太の魔界大冒険", "ドラえもん のび太の魔界大冒険", "", "", "http://ecx.images-amazon.com/images/I/61QRypB9AQL._SL210_.jpg", 98, "“魔法が使いたい！”と言い出したのび太が、もしもボックスを使って現実世界を魔法の世界に変えてしまった。そんなある日、ドラえもんたちは魔法を研究する博士と、娘の美夜子に出会う。博士の話によると“魔界”が地球に接近しており、魔王を倒さなければ地球が滅亡するという。のび太たちは悪の大魔王デマオンに挑んでいくが……。", "1984-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hanna", "ハンナ", "", "", "http://image.eiga.k-img.com/images/movie/56063/315x210.jpg?4526", 111, "「つぐない」でアカデミー助演女優賞にノミネートされたシアーシャ・ローナンが、同作のジョー・ライト監督と再びタッグを組んだサスペンスアクション。フィンランドの山奥で元CIA工作員の父に戦闘技術を徹底的にたたき込まれて育った16歳の少女ハンナは、ある任務のためヨーロッパを旅立つ。しかし、父のかつての同僚でCIA捜査官のマリッサがハンナを執ように追跡する。父親役でエリック・バナ、ハンナにまつわる秘密のカギを握るマリッサ役でケイト・ブランシェットが共演。", "2011-08-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASSASSIN（S）", "アサシンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "「憎しみ」のM・カソヴィッツが手掛けたバイオレンス・ドラマ。老暗殺者バグネルに見いだされ、その後継者となったチンピラのマックス。経験を積むものの、彼は無感情で人を殺せるほど冷淡になりきれない。やがてマックスは13歳の少年を助手にする。彼は、TVとゲームで育ったため、いともあっさりと“仕事”をやり遂げた。やがてバグネルは、新たに少年を後継者として育てようとするが……。暗殺の描写の生々しさもさることながら、人間の感情を無にしてしまうメディアの“暴力”もショッキング。低年齢化する凶悪犯罪の根源を見据えた衝撃作だ。", "1997-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hudsucker Proxy", "未来は今", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "1950年代の古き良きアメリカを舞台に、陰謀に巻き込まれて大企業の社長になった青年が起こす奇跡を描いた、コーエン兄弟によるファンタジック・コメディ。大企業ハッドサッカー社の社長が自殺した。ここぞとばかりに会社乗っ取りを企てる重役マスバーガーは、入社したばかりのバーンズを社長の座に据えて自分の思い通りに操ろうと考える。ところが、バーンズが出したアイデアによって思いがけず大ヒット商品が生まれてしまい……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パタリロ！ スターダスト計画", "パタリロ！ スターダスト計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "世界各地でダイヤモンド盗難事件が多発。そんな折、宇宙ロケットのアポロ11号が乗っ取られてしまう……。凍ったダイヤで雲を凍らせ、巨大なヒョウを地上に降らせて人類滅亡を狙う悪の“スターダスト計画”が始まったのだ。そんな破天荒な計画を阻止するために、パタリロ王とバンコラン少佐が犯罪組織と戦うことに。ギャグ・タッチのアクション攻撃に思わず笑える奇想天外な超絶アニメ。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOONSHINE HIGHWAY", "ダーク・ハイウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "闇商売に手を染める男たちの欲望が交錯する、ハードボイルド風のドラマ。禁酒法が制定されていた1950年代のテネシーを舞台に、ムーンシャイン・ハイウェイと呼ばれる密造酒ルートを駆けめぐる敏腕ドライバーの奔走を、愛憎模様を交えてサスペンスフルに描く。迫力あるカーチェイス・シーンが大きな見せ場である。鬼才D・クローネンバーグが俳優としてチラリと顔を見せている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　NACION　CLANDESTINA", "地下の民", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "ボリビアの映画製作者集団“ウカマウ”の代表的存在のホルヘ・サンヒネス監督による７本目の長編。ボリビアの首都、ラパスで子供用の棺桶作りで生計を立てるセバスチャン。彼は長年、暮らしたラパスを離れて、故郷のウィルカニ村に戻る決意をした。その直後、軍事クーデターが勃発して、街では銃撃戦が始まる。彼が故郷へ向かう途中、様々な過去が蘇ってくる。", "1988-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("必殺！III 裏か表か", "必殺！III 裏か表か", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "１作、２作目は集団対集団という戦いで、ストーリー的な散漫さが目立った。しかし３作目は、主役の中村主水を孤立させることによって、物語としても芯の通ったものになっている。江戸の両替商、ひいては日本の経済の元を握る影の男・真妙屋を敵に回した主水が、社会的に窮地に追い込まれる。ラストは、仕事人仲間の助けを借りて真妙屋の本拠地へ乗り込んでいく。TVシリーズ、約50本の演出を手掛けた工藤栄一の演出が冴え、シリーズ中出色の出来となった。特に最後の殺し合いは、彼一流の集団時代劇の大チャンバラが展開する。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("巨人と玩具", "巨人と玩具", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "現代のマスコミ社会を痛烈に戯画化した、開高健の同名の小説の映画化。ある製菓会社の敏腕宣伝課長とその部下が、街で拾った虫歯だらけの小娘をCMキャラクターに起用し、アイドルに仕立て上げる。しかし、やがて裏切られるハメに陥り、課長は過労から吐血して倒れ、部下は一人サンドイッチマンとなって街頭を歩いていく……。タイトルバックから増村のスピーディーな演出が横溢。エネルギッシュに自己主張する登場人物の衝撃性もあって、新しい時代を開く新感覚の日本映画の誕生、と注目を集めた。野添ひとみの屈託のない可憐らしさが魅力でもある作品。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tva killar och en tjej", "幸せな僕たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "スウェーデンが世界に誇る名匠ラッセ・ハルストレム監督が、大ブレイクした「マイライフ・アズ・ア・ドッグ」の直前に手掛けたラブ・コメディ。学生時代いつも一緒に行動していた仲良し3人組。卒業後、紅一点のアンナとクラッセは結婚したが、トマスとは長い間疎遠になっていた。17年後に再会した彼らは大いに盛り上がり、学生時代に戻ったかのような錯覚に陥る。そして、友人の結婚式でオペレッタを上演することになるのだが……。", "1983-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHASING AMY", "チェイシング・エイミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "親友バンキーとコミックを共作している売れっ子マンガ家のホールデンは、ある日、ナイトクラブで出会った同業者アリッサに一目惚れ。ところが彼女は自他ともに認めるレズだった。ショックを受けながらもアリッサを諦めきれないホールデンはアタックを開始。その情熱に惹かれて、アリッサの心も揺れ動くのだが……。レズで男性経験豊富な女の子と彼女に恋してしまったマジメな青年、そして彼にひそかに思いを寄せるゲイの親友の恋と友情を、インディーズ界で活躍しているK・スミスが軽やかに描出。セックスに関する会話ではドキリとする発言が飛び交い、若者たちの悩める心情をユーモアを交えてリアルに浮かび上がらせている。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOO　HOT　TO　HANDLE", "地球を駈ける男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ニュース映画のカメラマン、クリスとライバル社のビルは上海で女性飛行士アルマと出会う。クリスはアルマを口説き落としてニューヨーク本社に帰り、彼女をお抱えパイロットにするが、３人がひと悶着起こしたことから全員クビになってしまう。南米で行方不明になった兄を探すことがアルマの望みだと知ったクリスとビルは機材を売り払い、３人で南米に渡る……。ハリウッドのスター、C・ゲーブルとM・ロイが共演したロマンティック・コメディ。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pearl Harbor", "パール・ハーバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 183, "陸軍航空隊の若き精鋭パイロット、レイフとダニーは兄弟同然に育った親友同士。ある日2人は美しい看護婦イブリンと出会い、レイフはイブリンと恋に落ちる。だが世界中に戦火が広がる中、理想に燃えるレイフは中立国の志願兵で組織されたイーグル飛行隊に自ら参加し英国へと旅立つ。間もなくダニーとイブリンはハワイ・オアフ島へ配属され、戦争など嘘のような平和な日々を過ごすが、ほどなく2人の元にレイフの訃報が届く。", "2001-07-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太平洋のかつぎ屋", "太平洋のかつぎ屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "腕利きパイロット・哲次は、パートナーのジムの失敗を一人でかぶって解雇される。航空大学の教官となった哲次だが、自分もパイロットとしてもう一度飛びたいという思いを捨てきれない。が、ある日救助物資輸送のため、哲次は台風の中を飛ぶことになった……。小林旭主演の痛快な航空アクション。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Anvil! The Story of Anvil", "アンヴィル！　夢を諦めきれない男たち", "", "", "http://image.eiga.k-img.com/images/movie/54883/315x210.jpg?2088", 81, "1973年にカナダで結成され、「メタリカ」「スレイヤー」「アンスラックス」といった多くの人気バンドに絶大な影響を与えながら、自身はブレイクすることなく世間から忘れ去られた不遇のメタルバンド「アンヴィル」。かつて彼らのツアーにローディとして参加した経験を持つ「ターミナル」の脚本家サーシャ・ガバシがメガホンを取り、現在も夢をあきらめずに地元でバンド活動を続ける彼らの姿を追った音楽ドキュメンタリー。", "2009-10-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・命のお値段", "喜劇・命のお値段", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "刑期を終え、出所した二人の男は、互いにまともな仕事につこうと別れるが、１年後ふとしたことで偽医者、偽教師となって再会。ちょうどそのころ食品公害が発生し、二人は公害発生源の食品会社に殴り込みをかける。交通戦争、医療制度問題、公害問題といった時事的問題を扱った前田陽一のシリアス調の喜劇。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひなたぼっこ", "ひなたぼっこ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "公立の普通高校に通う知的障害者の若者たちの姿を、１年半にわたり記録したドキュメンタリー。様々な関門をくぐって高校に入学した知的障害者の若者たち。健常者と言われる同級生たちとともに学校生活を送る彼らは、クラブ活動や文化祭などに参加しながら生き生きとした毎日を過ごす。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAREER　GIRLS", "キャリア・ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "イギリスの鬼才M・リーが「秘密と嘘」に続いて撮り上げた、二人の女性の友情の物語。1980年代に青春を過ごした、大学時代のルームメイト同士であるハンナとアニーが６年ぶりに再会。互いに別々の生き方を歩んだことを認識しつつも、彼女たちの心はキャンパス・ライフの思い出へと飛ぶ。辛らつな口調のハンナと繊細なアニーだが、ともにロック・バンド、キュアーが大好きで奇妙にウマが合った。そんな過去を振り返った後に、彼女たちは大学時代に知り合った男たちと不思議な再会を果たす……。現在の姿と過去の回想を、ユーモアとノスタルジーを込めて描写。確かな時の流れを感じさせながら人生を“キャリア”として捉えた、ポジティブな物語が胸に染みる。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒の報告書", "黒の報告書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "佐賀潜の江戸川乱歩賞受賞作『華やかな死体』の映画化。若い検事が食品会社社長殺害事件を担当し、悪徳弁護士や集団偽証を向こうにまわし、正義と真実を求めて苦闘する。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガキンチョ★ROCK", "ガキンチョ★ROCK", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "若手お笑い芸人コンビ、キングコングとロザンの４人を主演に、「パコダテ人」「棒たおし！」の前田哲監督が撮り上げた青春コメディ。インディーズ・バンドを結成した幼馴染みの４人の若者が、様々な困難に直面しながらも奮闘する姿を、関西ならではの爆笑ネタ満載で描いた好編。ロックバンド“ガキンチョ★ROCK”を組むコウメイ、チンク、源内、セミマルの４人は幼馴染みで、メジャーデビューを目指していた。有名になれば、共通のマドンナ比呂美ちゃんに再び会えると信じ込む４人だったが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Black Emperor　ゴッド・スピード・ユー！", "Black Emperor　ゴッド・スピード・ユー！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "柳町光男の初期の長編ドキュメンタリー。暴走族の一大勢力グループ“ブラックエンペラー”の姿を批判的というのではなく、むしろ彼らの内なる視点から捉えている。それまで無名だった柳町の名はこの作品で一躍有名になった。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DERNIER ME´TRO", "終電車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "ナチス占領下のパリを舞台に、演劇の灯を守る演劇人たちの公私にわたるエキサイティングな生活を描いたメロドラマ。亡命者の夫を持つ美貌の妻マリオンと新人俳優ベルナールとのひそかな恋愛が詩情豊かに、かつスリリングに綴られる佳作。", "1981-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Entre tinieblas", "バチ当たり修道院の最期", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ペドロ・アルモドバル監督初期の代表作で、強烈な個性を持つ罰当たりな尼僧たちが繰り広げるハチャメチャな騒動を描いたブラック・コメディ。恋人を麻薬中毒で死なせてしまったクラブ歌手ヨランダは、ある修道院に駆け込む。ところがそこは、資金難で閉鎖寸前、しかも院長は麻薬中毒というとんでもない修道院だった。おまけに尼僧たちはトラを飼っていたり、官能小説を書いていたりとやりたい放題。やがて閉鎖の日が近付いて来て……。", "1983-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’EQUIPIER", "灯台守の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ブルターニュ海岸の神秘的な風景をバックに展開するラブ・ストーリー。住み込みの灯台守が自らの手で明かりを灯し、海の安全を守っていた1960年代。ある戦争帰還兵が辺境の地であるウエサン島の灯台守に加わり、人妻との許されぬ恋に落ちていく様を、ヒロインの夫との葛藤を絡めて描く。監督は「パリ空港の人々」「マドモワゼル」のフィリップ・リオレ。厳しい気象条件の下、ウエサン島の灯台で実際に撮影を行い、過酷な重労働に従事した男たちの生き様と、切ない大人の恋物語を紡ぎ上げた。ケルト人の子孫たちが暮らすブルターニュ地方のウエサン島。よそ者の青年アントワーヌは、村人に冷たく扱われながらも灯台守として認められる。やがて彼は仲間の妻と恋に落ちてしまう。", "2005-11-05", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リアル鬼ごっこ2", "リアル鬼ごっこ2", "", "", "http://image.eiga.k-img.com/images/movie/55123/315x210.jpg?1602", 107, "山田悠介のベストセラー小説を映画化した「リアル鬼ごっこ」の続編。前作に引き続き、監督とオリジナル脚本を柴田一成、主演を石田卓也が務める。佐藤という名字の人々が鬼に捕まると死刑になってしまう殺人ゲーム「リアル鬼ごっこ」を勝ち抜いた佐藤翼。それから半年後、翼は妹の愛や幼なじみの洋らと、謎の独裁者に対抗するレジスタンスとなっていた。そんなある日、再び彼らは何者かが仕掛けた新たな殺人ゲームに強制的に参加させられてしまう……。", "2010-06-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARD TO KILL", "ハード・トゥ・キル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "元FBIの秘密工作員という経歴を持つS・セガールが主演したアクション映画。政府腐敗にまつわる事件を追っていた刑事メイソン・ストームは、何者かに妻子を殺害され、自らも銃弾を浴びて昏睡状態に陥る。それから７年、奇跡的に回復したメイソンは、復讐のために立ち上がるが……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("梅香里〈メヒャンニ〉", "梅香里〈メヒャンニ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "「ゆんたんざ沖縄」「しがらきから吹いてくる風」の西山正啓監督によるドキュメンタリー。1951年、米軍により戦闘機爆撃演習場とされた朝鮮半島の中央部にある小さな漁村“梅香里”。農業や漁業などの生活手段を奪われ、米軍機による誤射・誤爆などが現在も続く村の住民たちの苦悩を浮き彫りにする。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOUNG EINSTEIN", "ヤング・アインシュタイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "相対性理論を構築した、あのアルバート・アインシュタインの青春を奇想天外なアイデアで、コメディ・タッチの冒険映画として描いた作品。タスマニアのりんご園で育ったアルバートはひょんなことから“E=mc2 ”の理論を発見。そしてそれを引っ提げ都会に行く道中で、科学者マリー・キュリーと出会う。", "1988-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("箱の中の女 処女いけにえ", "箱の中の女 処女いけにえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "1984年秋にカリファルニアで実際に起こった事件をもとに、異才・小沼勝とガイラのコンビが描く強烈なSM映画。女性ハイカーが、ある日乗せてもらった車は、異常性欲に狂う夫婦が運転するものだった。夫婦に監禁された女は、あらゆる方法でいたぶられ……。にっかつがロマンXとして売り出したハードポルノ第１作。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Silence", "サイレンス", "", "", "http://image.eiga.k-img.com/images/movie/1653/315x210.jpg?2406", 76, "タジキスタンの田舎町に母親と暮らす盲目の少年コルシッドは10歳。物を触って音を感じる彼は一人音の世界に生き、美しい音を求めて道に迷うこともしばしば。美しい少女ナデレーは毎日、調律師である彼を楽器職人の工房まで送り届けていた。ある日、コルシッド親子は家賃の滞納で家を追い出されそうになり、母親は親方へ借金を頼むよう彼に言付けるが、バスの中で音楽家の演奏に聞き惚れた彼はまたも仕事に遅れ、とうとうクビになってしまう。", "2000-12-02", "15,2,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンドロメディア", "アンドロメディア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "人気グループ・SPEED（2000年３月解散）が初主演を果たしたSFファンタジー。CGを多用して描くスリリングなドラマに、切ないロマンスを絡ませて描く。舞は幼なじみのユウに淡い恋心を抱いている普通の女子高生。同級生のリカと洋子、メール友達のナオと楽しい毎日を送っていた。そんなある日、彼女は交通事故で他界する。悲しみに暮れる天才プログラマーの父は、研究中のプログラムで舞の脳をコンピューターに移し、舞をアイとしてネット上に蘇らせることに成功する。だが、世界征服をたくらむデジタルウェア会社が、このプログラムに目を付け、アイを手に入れようと画策。ユウたちはアイを守るため、パソコンを持って逃亡するが……。逃亡に協力する男子生徒でDA PUMPが出演。彼らが劇中で披露するライブ・シーンに加え、人気カメラマンC・ドイルの怪演も見もの。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERO THE ONLY PERSON IN THIS UNHAPPY WORLD", "HERO THE ONLY PERSON IN THIS UNHAPPY WORLD", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ヒーローとなった青年の葛藤（かっとう）や苦悩を描いた異色のヒーロー映画。組織内で異端とされる研究員・龍崎仁に改造され、善悪の感情を持たない新人類として生まれ変わった奥山純太。「怪人」と呼ばれる実験体を処分する仕事に従事し空虚な毎日を送る純太だったが、世間では「怪人を倒すナゾのヒーロー」として知れわたる。そんなある日、純太は組織から派遣された少女・水越鈴と共に、ある男の監視を命じられるが……。", "2006-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Social Network", "ソーシャル・ネットワーク", "", "", "http://image.eiga.k-img.com/images/movie/55273/315x210.jpg?2075", 120, "世界最大のソーシャルネットワーキングサイト「Facebook」創設者マーク・ザッカーバーグの半生を、鬼才デビッド・フィンチャーが映画化。2003年、ハーバード大学に通う19歳のマークは、親友のエドゥアルドとともに学内の友人を増やすためのネットワーキング・サービスを開発する。そのサービスは瞬く間に他校でも評判となり、ファイル共有サイト「ナップスター」創設者のショーン・パーカーとの出会いを経て、社会現象を巻き起こすほどの巨大サイトへと急成長を遂げるが……。主演は「イカとクジラ」のジェシー・アイゼンバーグ。共演にジャスティン・ティンバーレイク、新スパイダーマンに抜擢されたアンドリュー・ガーフィルドら。", "2011-01-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Rite", "ザ・ライト　エクソシストの真実", "", "", "http://image.eiga.k-img.com/images/movie/55973/315x210.jpg?4332", 112, "平凡な青年がエクソシストになるまでの過程を追い、現在もバチカン公認の職業として実在するエクソシストの真実を明らかにした衝撃作。信仰を見失ってしまったアメリカの神学生マイケルは、恩師のすすめでローマへとわたり、バチカンのエクソシスト養成講座を受け始める。異端ながら一流のエクソシストと称されるルーカス神父の悪魔ばらいを手伝うことになり、数々の驚くべき出来事を目の当たりにする。名優アンソニー・ホプキンスがルーカス神父を怪演。", "2011-04-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VOICES", "ふたりだけの微笑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "父の家業を手伝いながら歌手を目指す青年と、ダンサーを夢見るろうあの少女の愛情を、音楽と、音のない世界との対比の中に描く。マンハッタンの高層ビル群をのぞむ、ニュージャージーの下町に住む人々の生活が巧みに織り込まれた心温まる佳作。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCKY IV", "ロッキー４ 炎の友情", "", "", "http://ecx.images-amazon.com/images/I/51ruoUyVOAL._SL210_.jpg", 91, "“ロッキー”シリーズ第４作。今回は宿敵だったアポロをリングで失ったロッキーが、その相手であるソ連のボクサー“殺人マシーン”ドラゴを叩きのめすまでの物語。米・ソ両国の対決を十分に意識したシチュエーションが、アメリカ国民を熱狂させ、空前の大ヒットとなった。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOR A FEW LOUSY DOLLARS", "ルージーダラーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "チンピラとギャング、殺し屋の絡み合う欲望を描いたバイオレンス活劇。犯罪組織の大物トニーの店に押し入った強盗犯３人組が、獲物を誰かに先取りされた上にギャングに監禁された。大金を取り戻すことを条件に解放された彼らは高飛びを決意。激怒したトニーは３人組を追うが、そのころ殺し屋がトニーを狙っていた……。クライマックスの壮絶な銃撃戦が圧巻。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EN KVINNAS ANSIKTE", "女の顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アンナは、顔にあるヤケドの跡のせいで、暗い少女時代を過ごした。しかし有能な外科医のおかげで美しい女性に生まれ変わる。これがきっかけとなり、彼女は性格も穏やかになるのだが、手術前からかかわっていた殺人事件に巻き込まれ窮地に立つ。ジョーン・クロフォードでリメイクされた1941年の同名作品のオリジナル。バーグマンの演技力が問われた作品。", "1938-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Attack", "攻撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "　第2次大戦下のベルギー。米軍のある中隊では、無能な中隊長クーニー大尉のミスによって多くの兵士が戦死。その上官バートレット大佐は有力者でもあるクーニーの父のコネで出世を目論んでいたために責任を追求しようとしない。そんな中、小隊長コスタ中尉が不満を募らせていく。やがて中隊に新たな命令が下るが……。軍隊を痛烈に批判した異色の戦争映画。反骨の名匠アルドリッチ監督の力作。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Inland Empire", "インランド・エンパイア", "", "", "http://image.eiga.k-img.com/images/movie/1943/315x210.jpg?4148", 180, "鬼才デビッド・リンチが「マルホランド・ドライブ」以来5年ぶりに放つ長編映画。ポーランド映画「47」のリメイク「暗い明日の空の上で」に出演することになった女優のニッキー。相手役の男優と私生活でも関係を持つようになった彼女は、次第に現実と映画の区別がつかなくなり……。ニッキーの私生活と劇中映画、ロスト・ガールや謎のウサギ人間など、いくつもの世界が複雑に絡み合い、前作にもまして不条理なリンチ・ワールドが展開していく。", "2007-07-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("限りなき前進", "限りなき前進", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "小津安二郎原案、八木保太郎脚色、内田吐夢監督、小杉勇主演という豪華な顔合わせによる社会派ドラマの力作で、1937年度のキネマ旬報ベスト・テンの１位に輝いた。定年間近の老社員・野々宮保吉は、出世の見込みがまったくなく、婚期の近い娘の将来のことを考えると気が狂いそうな不安に襲われる。そしてついに彼の疲弊した精神は異常をきたし、半狂乱のまま会社で醜態を演じるが、娘と恋人の青年がかけつけ彼を連れ帰るのだった……。この作品は当初、小津安二郎が「愉しき哉保吉君」という題で映画化しようと考えていたが、あまりに暗い内容のため会社（松竹大船）の許可が出なかったという裏話も。現存するフィルムは後半のクライマックスが抜け落ちた改編版。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CINDERFELLA", "底抜けシンデレラ野郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "シンデレラ物語の主人公を男に置き換えた、喜劇王J・ルイスの主演作。父の死以来、意地悪な継母とその連れ子たちにコキ使われる青年フェラ。ある日、モラビア国の皇女が婿探しにやってきた。継母は財産目当てで息子を皇女の夫にしようと、舞踏会へ。暖炉掃除を命じられた留守番のフェラの前に、やがて魔法使いが現われ……。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES AMITIE´S PARTICULIE｀RES", "悲しみの天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "侯爵家の一人息子ジョルジュは15歳になり、ミッション・スクールに転入する。閉鎖的なまでに規律の厳しい寄宿舎は、彼にとって別世界だった。ある日、アレクサンドルという美しい下級生が彼の前に現れ、二人はお互いに惹かれ合うものを感じる。やがて友情は、プラトニックな関係へと発展する（原題は“特別な友情”の意）が、それは許されざる愛だった……。少年愛を美しく描いた名作。文芸映画の名手ドラノワが、ミッション・スクールの雰囲気を見事に伝え、日本の漫画家、萩尾望都に名作『トーマの心臓』を描かせたことでも有名。アレクサンドル役のD・オードパンの無垢な美しさが光る。ビデオ、DVD発売時タイトルは「寄宿舎 ～悲しみの天使～」。", "1964-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARK HORSE", "特攻記者フォックス　スクープを狙え！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "変装の名人にして敏腕記者のニック・フォックスの活躍を描いたアクション映画。ニューズ・ポイント誌の記者ニックは、編集長でもあり恋人でもあるアリスや、相棒のカメラマン、マッキーと組んで、数々のスクープ、特ダネをものにしてきた。次にニックが目をつけたのは、民間のサバイバル訓練基地でのクーデターの陰謀であった。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠山の金さん　御存じいれずみ判官", "遠山の金さん　御存じいれずみ判官", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "金さんの父・景晋が抜け荷買いの疑いで長崎奉行の地位を追われる。真相究明に乗り出した金さんは、抜け荷買いの黒幕・雪翁を、将軍・家慶御覧の能狂言会で見事に裁いてみせる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ご挨拶", "ご挨拶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "冠婚葬祭やパーティーなど、日常で使われる“挨拶”をモチーフに、今を生きるタイプの違う各世代の女性が、それぞれの人生と直面しながら奮闘する姿を、３人の監督が独自の視点で描いたオムニバス。演出を「グッバイ・ママ」の脚本家・寺田敏雄をはじめ、市川準、モモイ・カオリ（桃井かおり）が担当。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last House on the Left", "鮮血の美学", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "　少女マリーと友人は凶悪な殺人犯によって強姦された末、殺害されてしまう。マリーの両親であるコリンウッド夫妻は娘の帰りが遅いことを心配し、警察に捜索願を出すが、相手にされない。やがて夫妻は、たまたま家に泊めた男たちがマリーを殺害したことを知り……。「処女の泉」をもとに、ウェス・クレイブンが初監督を務めた残虐ホラー。アメリカの一部地域で大反響を呼び、やがて全米公開へと至った衝撃作。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JERSEY GIRL", "ロックン・ルージュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "都会のエリート青年に恋をしたニュージャージーの元気娘トビー。金持ちの恋人がいる彼の心を、なんとか自分に向けさせようとする彼女の奮闘ぶりがコミカルに展開。J・ガーツが主演したテンポの良いラブ・ロマンス。恋敵をS・リーが憎々しげに演じる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TROUBLE WIHT GIRLS", "トラブル・ウィズ・ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "E・プレスリー主演によるミュージカル・タッチのほのぼのコメディ。円熟味を増した彼の余裕のパフォーマンスと演技が楽しめる一編だ。1920年代のアメリカの片田舎に、旅芸人一座がやって来た。順調に公演が続いていたある日、殺人事件が起こり団員が逮捕されてしまう。追い詰められた団長だが、見事な秘策を披露することに……。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELDEN　IN　TIROL", "ヒーロー・イン・チロル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「ミュラー探偵事務所」のN・リストによる珍品コメディ。「ミュラー…」の主人公でもあった“マックス・ミュラー”が、チロリアン・ロック・ミュージカルのヒーロー“マックス”として再び登場。今回もベタなギャグを駆使し、歌い踊りまくる。加えて、ヤギも喋ればニワトリも歌う……と、その突き抜けた笑いのセンスが、スケールアップ。物語は、絵のように美しいヒーロー村をアルプスいちの観光地に仕立てようとたくらむの野心家の村長から、村を守るためにマックスが命を懸けて立ち上がる、というもの。ちなみに、チロリアン・ロックとは、ヨーデルをロックのリズムに乗せて歌うこと。", "1998-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京裁判", "東京裁判", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 277, "“東京裁判”と呼ばれた極東国際軍事裁判が、第二次世界大戦後の昭和23年、市ヶ谷の旧陸軍省参謀本部において開廷された。太平洋戦争の戦犯を裁くこの裁判の模様を収めた50万フィートにも及ぶフィルムは、裁判より25年後にアメリカ政府によって公開された。その長大なフィルムをもとに、歴史の流れの中に“東京裁判”の持つ意味と、戦争、国家といったテーマを問いかけた記録映画である。同じ戦争裁判を描いたスタンリー・クレイマーの「ニュールンベルグ裁判」がドラマだったのに対して、この作品はすべてが生々しい事実であり、その画面の迫力は圧倒的だ。戦争責任の所在を追いつつ“果して人は戦争を裁くことができるのか”、といったことまで考えさせる力作。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GODFATHERS AND SONS", "ゴッドファーザー＆サン", "", "", "http://ecx.images-amazon.com/images/I/51CR5MG192L._SL210_.jpg", 100, "“THE BLUES Movie Project”の１本として製作された音楽ドキュメンタリー。マディ・ウォーターズらを擁して一時代を築いたシカゴの名門レーベル“チェス・レコード”の歴史をたどりつつ、パブリック・エネミーのチャックDら若きヒップホップ・アーティストとの世代を超えた交流を綴る。黒人音楽のルーツを探るチャックDら現代のヒップホップ・アーティストたち。彼らとブルース・ミュージシャンとの共演が実現する過程を描く。", "2004-10-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZENDEGI EDAME´ DA＾RAD／AND LIFE GOES ON…", "そして人生はつづく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1990年、イラン北部を大地震が襲い、コケール村は壊滅的な被害を受ける。この村を舞台に前作「友だちのうちはどこ？」を撮ったA・キアロスタミ監督とその息子が、映画に主演した兄弟の安否を尋ねて旅する。一見、ドキュメンタリー映画に見えながら、出演者がしゃべる一字一句までキアロスタミ監督の脚本にあったもの。事実、「友だちのうちはどこ？」で少年を道案内する老人を演じた男は、撮影の舞台裏をバラすセリフを口にする。そうした中にあって、瓦礫の山となった村で生き延びる村人たちの姿が感動的だ。じゅうたんを掘り起こすため、一人で悪戦苦闘する老婆、お皿を洗う少女たち、テレビでサッカーを見るためアンテナを立てる若者。そのどれもが、生きている喜びにあふれた表情をふと漏らす。そして最後の、ジグザグの坂道をゆっくりと上っていく車の映像など、人生の旅を続けるロード・ムービーの傑作と呼んでいいだろう。", "1992-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いつかA列車〈トレイン〉に乗って", "いつかA列車〈トレイン〉に乗って", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "作詞界の大御所・荒木とよひさの監督デビュー作。名匠・内田吐夢監督作「たそがれ酒場」をもとに、とあるジャズ・バーに集まる人々の人間模様をジャズの名演奏とともに綴る。ハリウッド・デビュー作「キル・ビル」では日本語で通した栗山千明が、達者な英語力を発揮。タイトルはデューク・エリントンの演奏で有名なジャズのスタンダード曲をもじったもの。昭和の面影を残すジャズバー“A-TRAIN”には、ジャズに魅せられた人々が次々とやってくる。シングルマザーの歌手・アンナ、楽屋に住み込み作曲の勉強に励む健一。それぞれの人生が一夜の内に交錯していく。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UGLY", "UGLY", "", "", "http://image.eiga.k-img.com/images/movie/57233/315x210.jpg?1976", 100, "窪塚洋介主演で、とある若者たちの等身大の日常とその裏に隠された出来事を全編パリを舞台に描き出したドラマ。なんとなく日々を生きているカメラマンの青年カズヤはパリに住む異母兄弟の聡を訪ねるが、そこに聡の姿はなく、一緒に暮らしていたルームメイトたちも聡について何も語らなかった。そんなある日、カズヤはふとしたことから日本人女性の理恵と出会い、理恵の口から数週間前に聡が自殺していたことを聞かされる。監督は、数々のPVなどを手がけ、劇場長編作はこれが2作目の新鋭・柿本ケンサクと、ホウ・シャオシェンやジャ・ジャンクー作品の音楽も手がける半野喜弘。", "2011-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WORKING GIRL", "ワーキング・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "やる気はあるが出世コースにのれないOLのテスは、上司が事故で入院したチャンスを利用して、自分の提案した合併計画を自ら実行しようと奔走する。アメリカの証券業界を舞台に、ビジネスと恋に懸けたOLの奮戦ぶりをシャレた感覚で描いたロマンティック・コメディ。女性同士の対決が見もの。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEALING BESS", "STOMPの愛しの掃除機", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "イギリス出身の人気集団、STOMP。その魅力的なステージングを、彼らの生みの親が映画に応用した一編。伝説の掃除機を求めて奔走する若者が巻き起こす騒動を、デッキブラシやバケツでリズムを刻むSTOMPのパフォーマンスと共に活写。思わず体が動き出す、五感に響く異色コメディだ。産業スパイの父親の下で働くジャック・ベントレーは、伝説の掃除機の技術を盗んで応用するため、工場のあった街へと向かうが……。", "2005-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SPANISH PRISONER", "スパニッシュ・プリズナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "才気あふれる劇作家D・マメットが演出を務めた、先の読めない巧妙なサスペンス。会社に莫大な利益をもたらす発明をし、その商談のためにカリブを訪れた有能なビジネスマン、ジョー。そんな彼に、実業家と名乗る中年男ジミーが接近してきた。会社の冷遇に不満をこぼすジョーは、弁護士を紹介するというジミーと親しくなるが、その裏側には予想もしない罠が潜んでいた……。タイトルの“スパニッシュ・プリズナー”とは古典的な詐欺の手口を指す陰語。二重三重に仕組まれた罠によってジワジワと窮地に追い込まれていく主人公の焦燥がリアルに伝わってくる。コメディ俳優S・マーティンがウサン臭げなジミーに扮し、シリアスな演技で新生面を披露。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("琉球カウボーイ、よろしくゴザイマス。", "琉球カウボーイ、よろしくゴザイマス。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "純沖縄産の映画を国内外に向けて発信するプロジェクト「琉球カウボーイフィルムス」の第1弾として製作されたオムニバス作品。沖縄の年中行事・清明祭を舞台に描かれるファンタジー「See Me？」、宅配ピザ店で働く青年と不思議な少女が織りなすラブコメディ「Happy☆Pizza」、伝説の空手家と少年の交流を実話を基に描いた「マサーおじいの傘」の3話で構成。沖縄喜劇界の女王・仲田幸子がナビゲーターとして登場。", "2007-11-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNITED TRASH", "ユナイテッド・トラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "「ドイツチェーンソー大量虐殺」で知られる奇才C・シュリンゲンズィーフが手掛けた、エログロなムードの怪作。アフリカに赴任したドイツ人将校の妻が黒人の赤ん坊を出産。ペーターと名づけられたその子は、奇妙にも頭がペニスの形をしていた。やがて狂気の神父によって救世主に祭り上げられたペーターは、さらなる騒ぎに巻き込まれる……。セックスとバイオレンスの狂騒、反米主義というテーマに、シュリンゲンズィーフのアナーキーなこだわりが感じられる。", "1995-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女必殺拳", "女必殺拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "日本と香港の混血娘・李紅竜は、行方不明になった香港警察麻薬Gメンの兄を探して日本へ。やがて、麻薬組織の魔手が李紅竜に伸びて……。志穂美悦子が初主演とは思えぬはつらつとした演技を披露する。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A WALK TO REMEMBER", "ウォーク・トゥ・リメンバー", "", "", "http://ecx.images-amazon.com/images/I/21YVSXEZW4L._SL210_.jpg", 102, "「メッセージ・イン・ア・ボトル」の著者、ニコラス・スパークスの大ベストセラーを映画化。「ウェディング・プランナー」のアダム・シャンクマンが淡く切ない恋物語として端正に描く。ノースカロライナ州の小さな港町で暮らすランドン。両親の離婚以来、何かにつけて問題を起こす彼は、学校の懲罰で慈善活動に参加することに。その活動で彼は何事にも誠実なジェイミーと出会い、徐々に自身を取り戻す。やがて二人は互いに思いを寄せるようなるが、ジェイミーは病に冒されていた。", "2003-01-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLORY", "グローリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "南北戦争において、アメリカ史上初めて組織された北軍黒人部隊と、彼らを指揮する若き白人将校の戦いを壮大なスケールで描く戦争スペクタクル。あくまでも白人の視点から語られているために、偽善的に感じてしまう箇所も前半は目立つ。しかし、戦争の勝敗を決したとまでいわれる、激戦地フォートワグナーに出兵することが決まった兵士たちが、その前夜に歌い出すゴスペルから始まり、当日の行進、突撃、玉砕に至るまでセリフを極力排した映像と音楽の融合に、陶酔感がいやおうなく高まる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海へ See You", "海へ See You", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 174, "世界で最も過酷なレース、パリ～ダカール・ラリーを背景に、メカニック兼ドライバーの主人公、スポンサーの依頼でレースに参加する人気歌手、彼を追いかけてきた女性歌手らが繰り広げる人間模様を描く。倉本聰脚本による高倉健主演映画の３作目。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Argo", "アルゴ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Live Freaky Die Freaky", "リブ・フリーキー！ダイ・フリーキー！", "", "", "http://image.eiga.k-img.com/images/movie/33513/315x210.jpg?4537", 75, "1960年代後半にアメリカ全土を震撼させた、カルト集団マンソン・ファミリーによる女優シャロン・テート殺人事件を、パペット・アニメーションで映画化したスプラッター・パンク・ミュージカル。人気パンクバンド、ランシドのティム・アームストロングが製作を手掛け、声優陣にはグリーン・デイのビリー・ジョー・アームストロングや初期LAパンク・シーンの中心的バンドXのジョン・ドーら、豪華ミュージシャンが名を連ねる。", "2006-07-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("六ヶ所村ラプソディー", "六ヶ所村ラプソディー", "", "", "http://image.eiga.k-img.com/images/movie/56343/315x210.jpg?3858", 119, "青森県の六ヶ所村に建設された使用済み核燃料処理施設の周辺地域で暮らす人々を取材したドキュメンタリー。全国から核廃棄物が集まってくる六ヶ所村で、常に核問題と向きあいながら生きている住民たちにスポットを当て、それぞれの選択と取り組みを見つめていく。監督は、前作「ヒバクシャ　世界の終わりに」で、世界各地の被爆者の真実を描いて高く評価されたドキュメンタリー作家・鎌仲ひとみ。", "2006-10-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泣きたいときのクスリ", "泣きたいときのクスリ", "", "", "http://image.eiga.k-img.com/images/movie/54213/315x210.jpg?4003", 81, "ラジオドラマが原作のオロナインH軟膏55周年記念作品。電車で号泣する中年男性を見つめる5人の乗客——心に傷のある青年、仕事に疲れきったOL、新人駅員、泣けないサラリーマン、迷える女子高生。彼らの身にもまた、涙を流さずにはいられない事件が迫っていた……。それぞれが織り成す人間ドラマを描く。出演は大東俊介、戸田菜穂、袴田吉彦、遠藤憲一、佐津川愛美、北浦愛ほか。", "2009-01-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SLIPPER AND THE ROSE（THE LOVE STORY OF CINDERELLA）", "シンデレラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "名作童話を、シャーマン兄弟による美しい旋律に乗せて映画化したミュージカル大作。K・モアほか英国の実力派キャスト共演が楽しい。美術・衣装が変にリアルなメルヘンの世界。", "1976-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キッズ", "キッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "基地のある港町のアウトロー地帯を舞台に、盗まれたヤミ製造の拳銃の行方を追って、早見優扮する歌手見習いの少女が男まさりの大アクションを繰り広げるハードボイルド・タッチの作品。映画初主演、早見優の体当たりの演技と元気の良さが売りもの。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シャイン・オン　トラベローグ・オブ・アウトレイジ", "シャイン・オン　トラベローグ・オブ・アウトレイジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1987年のデビュー以来、日本を代表するヘビーメタルのバンドとして活躍する「Outrage」のドキュメンタリー。98年に脱退したボーカルの橋本直樹が2008年、10年ぶりにバンドに復帰。09年にはニューアルバム「OUTRAGE」を完成させ、10年、東京・渋谷BOXXを皮切りに「JAPAN TOUR 2010 “RISE”」をスタートさせる。40代に突入してもなお精力的に活動するメンバーに密着し、貴重な素顔にも迫る。", "2010-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fog of War: Eleven Lessons from the Life of Robert S. McNamara", "フォッグ・オブ・ウォー　マクナマラ元米国防長官の告白", "", "", "http://image.eiga.k-img.com/images/movie/1393/315x210.jpg?4670", 107, "03年アカデミー賞長編ドキュメンタリー映画賞を受賞した話題作。ハーバード大学院卒、フォード社社長、ケネディとジョンソン政権下で国防長官、さらに世界銀行総裁を務め、現在85歳になったロバート・S・マクナマラ氏が、11の教訓とともに自らの体験を語り、キューバ・ミサイル危機やベトナム戦争の秘話を明かしていく。ロサンゼルス批評家協会賞、ナショナル・ボード・オブ・レビューでもドキュメンタリー映画賞を受賞。", "2004-09-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beloved", "愛されし者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 172, "　南北戦争後、奴隷だった黒人女性セスはオハイオへ逃れ、その地で娘のデンバーと暮らしていた。そんなセスは奴隷仲間だったポールと再会し、3人で一緒に生活することに。やがてセスの前にビラブドと名乗る娘が現れるが……。奴隷制度による犠牲となった女性の苦悩と愛を描くドラマ。ピューリッツァー賞に輝いた小説の映画化作品。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOWNHILL RACER", "白銀のレーサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "O・ホールの小説の映画化。ヨーロッパ各地を転戦するアメリカのスキー・チームに参加した青年が、あるレースをきっかけに今までの生意気な態度を改めて、オリンピックに優勝するまでを描いた青春スポーツ映画。若きレッドフォードの好青年ぶりと華麗なスキー・シーン、白銀の景色が見ものだ。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("影向こうの灯り", "影向こうの灯り", "", "", "http://image.eiga.k-img.com/images/movie/56793/315x210.jpg?3631", 70, "会社員の慎太郎にはつき合って8年、同せいを始めて3年になる恋人・智子がいたが、ある日、智子は些細な出来事がきっかけで交通事故にあい、足が不自由になってしまう。自分のせいで事故にあったと後悔する慎太郎は、智子の小さな変化にも気づかなかったが、それは事故以前から2人が避けてきたわだかまりから生まれたものだった。突然の不幸をきっかけに関係が変化していく男女の姿をリアルにつづる。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春の構図", "青春の構図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "高校時代のバスケット部のチームメイト吉川ケイの活躍に刺激を受けた明昭大の谷口美穂と浅井洋子。さっそく、部員募集を大々的に行い、メンバーを集めた。そして吉川の所属する女子体育大打倒を目指すのだが……。３人の女子大生が恋やスポーツを通じて成長していく姿をさわやかに描いた、青春女性アイドル映画。曽野綾子の同名小説の映画化。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("行き止まりの挽歌 ブレイクアウト", "行き止まりの挽歌 ブレイクアウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "新宿歌舞伎町を舞台に、アウトローと化していくはぐれ刑事の姿をハードなタッチで描き出した、新生にっかつロッポニカの第２作。栗本薫の原作を柏原寛司が脚色。“遊戯”シリーズの村川透が息せくようなライブ感覚のアクション演出を見せている。役者陣も好演。和製ハードボイルドの佳作に仕上がった。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母情", "母情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "お人好しの女主人公は父親の異なる子供を３人生んだが、その子供たちを親類縁者に預け、自分は新規まき直しをしようと考え、寝小便ぐせの男の子以外は預けて旅に出る。だが、峠の茶屋の老婆の意見に目を覚まされ、３人の子供を自らの手で育てていくことを決心する。清川虹子初主演による、心優しい作品。徳川夢声、山田五十鈴、古川緑波が特別出演。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前火山", "喜劇・駅前火山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "西郷隆盛の曽孫・次郎は桜島一体を観光地にしようと、地熱発電所を作るための海底ボーリングを計画した。次郎に肩入れする鹿児島屋の社長・徳之助と反対する網元の孫作。この話に二人のペテン師も目をつけ……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El Cantante", "エル・カンタンテ", "", "", "http://image.eiga.k-img.com/images/movie/54603/315x210.jpg?4056", 106, "サルサの黄金時代を築いた天才歌手、エクトル・ラボーの生涯を映画化。ジェニファー・ロペスが製作とエクトルの妻プチ役を務め、夫のマーク・アンソニーがエクトルを熱演。成功を夢見て、故郷プエルトリコからニューヨークにやって来た17歳のエクトルは、ナイトクラブでその歌唱力を発揮、瞬く間にスター歌手の仲間入りを果たす。しかし、私生活では酒とドラッグに溺れ、愛する家族を失い、やがて自殺未遂にまで自分を追い詰めていく。", "2009-07-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’EFFRONTE´E", "なまいきシャルロット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "C・ミレールが、セルジュ・ゲンズブールの娘、シャルロットを起用し、思春期を迎えた13歳の少女の揺れ動く心を描いた作品。７月のパリ。夏のバカンスで、シャルロットは自分と同じ年の天才ピアニスト、クララと出会い、彼女の心は羨望と憧れの両極に揺れ動く。セザール賞新人女優賞に輝いたシャルロットが好演。", "1985-01-01", "2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まなざしの旅　土本典昭と大津幸四郎", "まなざしの旅　土本典昭と大津幸四郎", "", "", "http://image.eiga.k-img.com/images/movie/56613/315x210.jpg?4058", 90, "「水俣　患者さんとその世界」（1971）などで水俣病と向き合い続けたドキュメンタリー作家・土本典昭と、「パルチザン前史」（69）や一連の水俣シリーズで土本を支えてきた名カメラマン・大津幸四郎に迫るドキュメンタリー。映画美学校の受講生による大津へのインタビューや、生前に記録された土本の証言や映像、60～70年代の作品映像を交えて構成される。", "2011-06-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EGON SCHIELE", "エゴン・シーレ 愛欲と陶酔の日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "人間の肉体を赤裸々に描くオーストリアの画家、エゴン・シーレの伝記映画。少女誘拐罪で24日間拘置され、ワイセツとして絵を焼かれ失意に沈むシーレ。そして彼のモデル兼愛人のバリー、後にシーレの妻となるエディットとのかかわりを描く。", "1980-01-01", "7,77,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2010-2011 ドニゼッティ「ランメルモールのルチア」", "METライブビューイング2010-2011 ドニゼッティ「ランメルモールのルチア」", "", "", "http://image.eiga.k-img.com/images/movie/56473/315x210.jpg?1839", 206, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を劇場のスクリーンで上映する「METライブビューイング」2010～11年シーズンの8作目で、イタリアの作曲家ドニゼッティの人気オペラ。17世紀スコットランドのランメルモール地方。領主エンリーコの妹ルチアは、兄が領地を奪ったエドガルドと愛し合っていた。しかし、経済的困窮からエンリーコはルチアを高官アルトゥーロと政略結婚させようとする。永遠の愛を誓ったルチアとエドガルドは……。", "2011-04-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT TRAP", "ナイト・トラップ ～戦慄の銃弾～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "一人の娼婦が、アパートの窓から転落死した事件を追う殺人課刑事ターナー。逃走する犯人を廃墟ビルに追い詰め、その背中に銃弾を浴びせたはずが、犯人は不敵な笑いを残し消えてしまう。仲間からも犯人の存在すら認めてもらえず、次第に孤立していくターナーだったが、一方では連続殺人が続いていた。殺しても死なない、姿なき不気味な殺人鬼をM・アイアンサイドが演じ、相変わらず主役を食った圧倒的な存在感をみせる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ポケットモンスター　ダイヤモンド＆パール　ギラティナと氷空（そら）の花束　シェイミ", "劇場版ポケットモンスター　ダイヤモンド＆パール　ギラティナと氷空（そら）の花束　シェイミ", "", "", "http://image.eiga.k-img.com/images/movie/53303/315x210.jpg?2392", 100, "子供たちに絶大な人気を誇るアニメ版「ポケットモンスター」の新シリーズ「ダイヤモンド＆パール」の劇場版第2弾。ディアルガとパルキアの戦いによって時間と空間にゆがみが生じたため、この世界と鏡のような関係にある“反転世界”に異変が起こった。激怒した反転世界の主・ギラティナはディアルガを反転世界に引きずり込もうとする。一方、サトシたちは旅の途中で出会ったポケモン、シェイミとグラシデアの花畑を探すことになり……。", "2008-07-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラマン", "ラマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「ヴァイブレータ」の廣木隆一が、やまだないとの同名漫画を映画化。自分の中にある不安を消し去るために、男３人の愛人となった17歳の少女の１年間を官能的に描く。田口トモロヲ、大杉漣、村上淳という個性派陣を相手に大胆なラブ・シーンに挑んだ安藤希が、繊細に揺れ動く乙女心を表現。17歳の少女が２人の男たちと１年間の愛人契約を結ぶ。身体だけの関係を続けるうち、彼女は次第に彼らの中の哀しみを知ることになる。", "2005-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Primal", "共喰山", "", "", "http://image.eiga.k-img.com/images/movie/56043/315x210.jpg?3097", 84, "古代壁画に興味を抱くアーニャは、友人5人を誘って森深くの洞くつを目指す。その道中、蛭（ひる）に襲われた仲間のメルが高熱を出し、無数の牙を持つ食人鬼へと変ぼうしてしまう。オーストラリア映画界の伝説的ホラー「ロング・ウィークエンド」（1978）で知られるコリン・エッグルストン監督の息子、ジョシュ・リードの初監督作。食人鬼と人間の死闘を描くバイオレンスホラー。", "2011-05-07", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トキワ荘の青春", "トキワ荘の青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "漫画の神様・手塚治虫が住み、彼に憧れる若い漫画家たちが集まり、貧乏で食べるものがなくても若さと漫画への情熱であふれていた東京・椎名町のアパート“トキワ荘”。そこに暮らす若者たちは“新漫画党”を結成し、日夜、新しい漫画について熱い議論を交わしていく……。赤塚不二夫、石ノ森章太郎、藤子不二雄ら著名な漫画家を生み出した“トキワ荘”を舞台に、漫画家を目指した青年たちの青春を描く。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOWNTIME", "ダウンタイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "エレベーターに閉じ込められた男女の脱出劇を描く、異様な緊迫感に満ちたパニック劇。飛び降り自殺を図ろうとした女性クリシーを救った元刑事の心理学者ロブは、彼女のことが気になり翌日アパートを訪ねる。だが、不良少年たちの悪戯から、二人は老朽化したエレベーターに閉じ込められてしまう。酸素の減少や、突然急降下するエレベーターから、彼らはあらゆる方法で脱出を図るのだが……。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("えんこえれじー　浅草哀歌", "えんこえれじー　浅草哀歌", "", "", "http://ecx.images-amazon.com/images/I/31fPPnpDaRL._SL210_.jpg", 0, "懐かしさと新しさが共存する東京の下町・浅草で生きる女性たちのドラマをつづったオムニバス映画。三社祭を背景に描いたラブコメディ「ばかの街」、女の子の人力車ひきと乗客との交流をコミカルに描いた「走る街」、京都の舞妓さんならぬ浅草の振袖さんに憧れる女性がお座敷デビューするまでの奮闘を描いた「粧いの街」の3話を収録。監督は「デコトラの鷲」シリーズの製作を手がけた須藤為五郎と、「夢の祭」の松田彰。", "2008-06-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAUVAISES　FRE´QUENTATIONS", "デルフィーヌの場合", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "真面目な15歳の少女デルフィーヌが、大人びた同級生に誘われてクラブに足を踏み入れる。そこで出会ったロランという不良っぽい少年に夢中になったデルフィーヌは、彼の夢を叶えてやるため娼婦まがいの汚れた行為に身を投じていく。1991年のパリで実際に起こったスキャンダラスな事件をもとに、ティーンエージャーの危うい行動を描いた辛口の青春映画。新人女優M・フォルジェのつぶらな瞳が印象的だ。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("重案實録O記／ORGANISED CRIME ＆ TRIAD BUREAU", "野獣特捜隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "警視リー率いる犯罪特捜隊は、連続強奪事件の犯罪グループを一掃すべくアジトを襲撃するが、組織のボスである主犯トンを取り逃がしてしまう。執拗な追撃を続ける特捜隊はトンが潜伏する島に向かうが、過剰な銃撃戦が一般市民を巻き込んでしまったため撤退命令が下される。内部スパイの存在を知り怒りが頂点に達したリーは、組織から押収した金で島全体を買収し、香港を怒濤の戦場へと変えていく……。「愛と復讐の挽歌」をはじめ刑事役を得意とする、D・リー製作・主演の実話をもとにしたハード・アクション。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怒りの海", "怒りの海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "東京帝国大学の教授を兼任しながら、日本海軍主要艦のほとんどの製造を手掛け、やがて帝大総長になって1943年に没した、“軍艦の父”平賀譲をモデルにした伝記映画。ワシントン条約によって、国別に主力艦の比率を決められ、廃棄艦を撃沈するシーンが前半のハイライト。特撮監督は、円谷英二。大河内伝次郎が平賀に扮し、風格ある演技を見せた。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝ同期の桜", "あゝ同期の桜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "海軍飛行予備学生一四期会編『あゝ同期の桜　帰らざる青春の手記』をもとに、中島貞夫監督が当時の東映若手オールスターを起用して演出した戦争青春映画。特攻機が敵空母に突入する寸前でストップモーションとなり“この瞬間、彼らはまだ生きていた”とテロップが入るあたりに、中島の意欲とポリシーが感じられる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ENGLISHMAN WHO WENT UP A HILL BUT CAME DOWN A MOUNTAIN", "ウェールズの山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ヨーロッパが第一次大戦で疲弊していた1917年のある日、ウェールズの小さな村にアンソンとジョージという二人のイングランド人が、村の自慢である“フェノン・ガルウ”という山を測量するためにやって来る。ところが測ってみると、この山が地図に載るには６メートル足りないということで村は大騒ぎ。村一番の山が“丘”と見なされた村人たちは、足りない分の土を盛って山にしようと団結する。明日には村を発つという二人の引き留め作戦が始まった。引き留めるために差し向けられた美女ベティに、ほのかな恋心を抱くアンソン。果たして頂上に土が盛られ“フェノン・ガルウ”は晴れて山となることができるのか……。ほのぼのとした味わいが好感を呼んだ１本。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT CERTAIN FEELING", "すてきな気持", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "“珍道中もの”でおなじみ、B・ホープの主演によるラブ・コメディ。腕はいいのに雇い主の前に出ると気分が悪くなってしまうという奇病のマンガ家フランシスを中心に、人気マンガ家のラリー、そしてフランシスの元ワイフで今やラリーのフィアンセとなったダンリースとの三角関係をコミカルに描く。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUN FOR COVER", "追われる男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "N・レイ監督が名優J・キャグニーを主演に据えた西部劇。無実の罪で刑務所送りとなり、ようやく刑期を終えて出所して来たマット。流れて来た小さな町でシェリフに迎えられた彼だったが、無法者が銀行を襲ったのをきっかけに、前科者だということがバレ、苦境に立たされる。そして、無法者たちとの最後の対決のときが……。レイらしい緊密な演出が魅力。暗黒街ものが得意なキャグニーの西部男ぶりも、意外なほどにすんなりハマッている。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Red Sands", "レッド・サンズ　呪われた兵士たち ", "", "", "http://image.eiga.k-img.com/images/movie/54583/315x210.jpg?1998", 89, "激しい戦闘が続くアフガニスタンで活動を続けていた米兵部隊は、憂さ晴らしに古代の石造を射撃練習の的にして破壊してしまう。その翌日から不可思議な事件が彼らの周りで起こり始め、更には自然が彼らに対し猛威をふるい始める……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パートナーズ", "パートナーズ", "", "", "http://image.eiga.k-img.com/images/movie/55653/315x210.jpg?1727", 119, "知人の死をきっかけに盲導犬訓練士を目指しはじめた19歳の剛は、初めて担当することになった訓練犬チエとの関係をうまく築けず悩んでいた。剛はパピーウォーカーの長谷川家でチエをかわいがっていた10歳の長女・美羽に相談してみることに。そんなある日、2人の前にライブ中の事故で視力を失ったロックシンガー・真琴が現れる。「GSワンダーランド」の浅利陽介が主演を務める。", "2010-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONCE UPON A TIME IN THE MIDLANDS", "家族のかたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "R・エヴァンスとR・カーライルの共演で描く、シニカルな家族ドラマ。ある青年にプロポーズされたばかりのバツイチ女性のもとに、元亭主が舞い戻ってきたことから、とんだ騒動が巻き起こる。現代のイギリス人家庭が抱えがちな問題を鋭い視点で切り取り、ユニークな家族劇に仕立て上げたS・メドウス監督の手腕が秀逸だ。気弱だけど心優しいデックは、バツイチの恋人シャーリーと彼女の娘を心から愛している。彼は意を決し、視聴者参加型番組で彼女にプロポーズをする。ところが、あっさり断られたあげく、町にシャーリーの元夫が現れ……。", "2002-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POWWOW HIGHWAY", "パウワウ・ハイウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "モンタナ州シャイアン特別居留地。図体のでかいノンビリ屋のフィルバートは、夢のお告げに従い、“気高い戦士”になるべく旅に出ようと、怒りっぽい政治活動家のバディを誘う。折しもバディは姉が誤認逮捕され、サンタフェへ向かうところ。かくして二人のインディアンの青年は、南を目指して旅に出る……。対照的な男たちの旅を温かく綴った人間ドラマ。", "1988-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACK ORCHID", "黒い蘭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "アメリカのイタリア人街に住み、子供を持つ若い後家ローズと、一人のしがないやもめ男フランクとの結婚をめぐる小市民ドラマ。J・ステファノがもともとTVドラマとして書き下ろした原作を、これが日本初登場となるM・リット監督が映画化した作品で、“庶民的”なS・ローレンも魅力。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEXAS ACROSS THE RIVER", "テキサス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "開拓時代のアメリカ西部。無実の罪を着せられてお尋ね者となった公爵のドンは、開拓村の牛飼いのサムに射撃の腕を見込まれて雇われる。しかしドンはトラブルを起こす名人で、ある日彼はインディアン娘ロネッタの危機を救ったが、それを誘拐と勘違いしたコマンチ族が開拓村に復讐にやって来て……。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEDROOM EYES II", "ベッドルーム・アイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "夫婦の間の疑惑が呼び起こす悲劇を描くエロティック・サスペンス。ハリーは、妻の浮気現場を目撃したことにより、自らも女との情事に溺れていく。ある日彼は、女の部屋で刑務所を出所したばかりの男に命を狙われ、からくも逃げ出す。現場の留守番電話に残された妻の声にハリーは疑惑を強めるが、そんなさなか、彼を襲った男も何者かに殺される。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿波おどり 鳴門の海賊", "阿波おどり 鳴門の海賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "弟の許嫁の美貌に目がくらんだ家老・広幡平佐衛門のために、海賊の汚名を着せられ阿波踊りの日に処刑された阿波の豪商・十郎兵衛。それからというもの、毎年盆踊りが近づくと家老の家の門に“十郎兵衛必ず参上”という貼り紙が貼られるようになる。そして７年。道中合羽に三度笠の男が現れた。観世光太とは名脚本家・山上伊太郎の変名で、これはマキノ雅広と山上の最後のコンビ作「阿波の踊子」のリメイク版。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Agora", "アレクサンドリア", "", "", "http://image.eiga.k-img.com/images/movie/55843/315x210.jpg?2666", 127, "オスカー女優のレイチェル・ワイズが、4世紀に実在した女性天文学者ヒュパティアを演じる伝記映画。「海を飛ぶ夢」（2005）のアレハンドロ・アメナーバル監督がメガホンをとる。舞台は激動のローマ帝国末期、エジプト・アレクサンドリア。明せきな頭脳をもった美しい女性天文学者ヒュパティアは、身分や立場にとらわれることなく、多くの弟子たちに熱心な講義を行っていた。しかし、科学を否定するキリスト教徒と学者たちの間で激しい対立が起こり、やがてその問題の矛先はヒュパティアに向けられてしまう。本国スペインでゴヤ賞7部門を受賞し、大ヒットを記録した。", "2011-03-05", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("双生児", "双生児", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "鬼才、塚本晋也が、持ち前の重量感あふれるスタイリッシュな映像感覚を、江戸川乱歩の同名短編を原作とする時代劇の世界に持ち込んだ意欲作。明治末期、美しい妻、りんをめとり、大徳寺医院の跡取りとして順風満帆の日々を送る雪雄。そんな彼の前に悲惨な運命をたどった双子の捨吉が出現し、りんを巻き込んで壮絶な愛と憎しみの三角関係がうねり出す。雪雄が暮らす上流階級と捨吉が生き抜いてきた貧民窟のコントラストを、鮮烈な衣装やメイクでビジュアル化。本木雅弘が演じ分ける二人の男の間で揺れ動くヒロインを、映画初出演のりょうが圧倒的なインパクトで演じている。第４回釜山国際映画祭で観客賞を受賞。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dawn of the Dead", "ゾンビ", "", "", "http://ecx.images-amazon.com/images/I/51A0PE2MPPL._SL210_.jpg", 115, "　惑星から降り注いだ光線によって地球上の死者が復活。その群れは生者に襲いかかり、次々と数を増やしていく。テレビ局員のスティーブンと恋人フランシーン、そしてSWAT隊員のロジャーとピーターは郊外のショッピング・センターに避難するが、やがてそこにも死者の群れが侵入し……。ジョージ・A・ロメロ監督のゾンビ・シリーズ第2弾。「ゾンビ」には、アメリカ公開版、ダリオ・アルジェント監修によるイタリア版、そしてアルジェント版をもとにした79年の日本公開版、さらにディレクターズカット版、といった複数のバージョンが存在する。", "1978-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRAYER OF THE ROLLERBOYS", "ローラーボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "近未来の荒廃したニューヨーク。ローラースケートをはいた少年グループ“ローラーボーイズ”はギャング同様の悪事を働いていた。その首領、ゲイリーとかつて親しい仲にあった少年グリフィンは彼らに戦いを挑んでいくが……。アメリカで流行した“ローラーブレード”をフィーチャーした、ハイム主演の近未来アクション。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("順流逆流", "ドリフト", "", "", "http://image.eiga.k-img.com/images/movie/1373/315x210.jpg?4024", 112, "21歳の青年タイラーの夢は、大金を手に入れ恋人と南国の楽園で暮らすこと。手っ取り早く金を稼ぐため闇社会のボディーガード組織の一員となった彼は、妻と人生をやり直すべく南米から香港へ戻ってきた元傭兵のジャックと出会う。しかし彼の所属していた傭兵組織は今や非情なヒットマン組織へと変貌し、彼の更正を許さなかった。妻の命と引き替えに、通商界の大物ハンの暗殺を強制されるジャック。だがハンの警護に当たるのはタイラーの組織だった。", "2001-06-02", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらば夏の光よ", "さらば夏の光よ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "遠藤周作の同名小説を、青春映画では定評のある山根成之監督が、人気歌手・郷ひろみを主演に起用して映画化した辛口の青春映画。南条宏と野呂文平は、風貌も性格もまったく対照的だがなぜか気が合った。二人はハンバーガー・ショップで働く美しいウェイトレス、戸田京子を見初め同時に愛するが、宏は野呂の熱意に打たれ京子を譲る。山根成之監督が等身大の青春像をじっくりと情熱を込めて描きあげ、単なるアイドル映画に終わらせなかった。主演の郷ひろみは、山根成之の演技指導を受けてひとひねりしたキャラクターをうまく生かすことができた。が、何といっても戸田京子に扮した秋吉久美子の好演が光る。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("先生を流産させる会", "先生を流産させる会", "", "", "http://image.eiga.k-img.com/images/movie/57983/315x210.jpg?3685", 62, "2009年に愛知県半田市の中学校で起こった実際の事件をもとに映画化。郊外の女子中学校に勤める教員のサワコが妊娠し、退屈な毎日に刺激を求めていた生徒たちが色めき立つ。しかし、複雑な家庭環境に育ったミヅキらのグループは、サワコが性交渉をもったことを汚らわしく思い、眉をひそめる。ミヅキらは「先生を流産させる会」を結成し、理科室で盗んだ薬品をサワコの給食に混入するなど嫌がらせを始めるが……。スラムダンス映画祭で上映された「牛乳王子」（08）ほか、これまでいくつかの短編を手がけてきた内藤瑛亮監督の長編第1作。", "2012-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("命果てる日まで", "命果てる日まで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "男女の三角関係を、哀切な感覚で描いた松竹大船調の悲しいラブ・ストーリー。社会派ミステリー作品も発表していた野村監督は一方で、本作と同様のメロドラマも数多く撮り上げた。デザイナーとして活躍する健は、自分の作品を盗作した京都の織物屋へ抗議に行ったところ、その店の娘・綾子に惹かれてしまう。健には彼女がいたが、やがて綾子が婚約中であることを知り……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BARROCO", "バロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「フリーダ・カーロ」で一躍注目されたP・ルデュク監督が、A・カルペンティエールの小説『バロック協奏曲』をもとに描いた、音楽と映像の一大絵巻は、一人の男の夢想という形をとり、歌で綴られる。４つの大陸と６世紀の時間を飛び越える幻想的な旅の始まりだ……。壮大なスケールと難解さを誇るカルペンティエールの小説を映画化するにあたり、ルデュク監督は、セリフではなく、音楽それ自体に語らせるという手法を採った。そのためクラシックや伝統音楽からポピュラーソングまで、ありとあらゆる音楽が混然一体となって映画の中に詰め込まれている。加えて夢幻的な映像など、タイトルにたがわぬバロック的作品となった。", "1989-01-01", "9,49", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージーの怪盗ジバコ", "クレージーの怪盗ジバコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "北杜夫の原作を映画化。人相・年令・国籍すべて不明の国際的大怪盗ジバコが義経の黄金の太刀を盗むと予告来日するが、国際窃盗団WCWC一味に横取りされる。怒ったジバコは一味をやっつけ警察にひき渡す。クレージー・キャッツ全員で演じる７人１役のジバコが見もの。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK OUT EFFECT", "バニシング・エアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "管制塔で働くグレイのコンピュータが二秒の間突然消えた。その時、上空では二機の飛行機がコントロールを失って衝突。 200人余りの乗客が死亡する事故となった。国家運輸安全委員会の調査主任ジョンは事故の調査を進めるうちに、乗客名簿の中に恋人の名前を見つけ打ちひしがれるが、ついに質の悪い点検員たちに問題があることを突き止める。だが、そのころ、グレイは事故の責任者としてマスコミの追及を受け、左遷させられてしまう……。ハイテクで支配された航空コントロール・システムが制御不能になったときの恐怖を描くサスペンス。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スラッカーズ　傷だらけの友情", "スラッカーズ　傷だらけの友情", "", "", "http://image.eiga.k-img.com/images/movie/55073/315x210.jpg?4150", 90, "不良高校生の青春を描く「スラッカーズ」の第2弾。監督は「バカは2回海を渡る」の渡邊貴文。高校1年生の尾崎ゲンは、ケンカで頂点を極めるべく、不良がのさぼる高校を次々と制覇していた。だが、ゴリキと呼ばれる豪腕の番長が仕切る羽木高校に転入し、ゴリキにケンカを売ったところ、全く歯が立たない。リベンジを誓う尾崎だったが、甲子園を目指す野球部員のゴリキは練習に忙しく、尾崎はタイマンをはるために野球部に入部する。", "2009-12-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍術御前試合", "忍術御前試合", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "東映の名時代劇監督・沢島忠のデビューを飾る忍術映画。大阪城の隠し砦の絵図面を守るため、甲賀忍術の名人戸沢白雲斎の一子・虎若丸が百地三太夫や石川五右衛門を敵にまわし、秘術の限りを尽くす。沢島は時間のかかる特撮を要するこの作品を10日間の徹夜で完成させ、次に念願の「一心太助」を撮ることになる。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ツナグ", "ツナグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE UPSIDE OF ANGER", "ママが泣いた日", "", "", "http://ecx.images-amazon.com/images/I/21d6cb5Ud0L._SL210_.jpg", 117, "夫が突然姿を消してしまったことに怒りを抑えられない中年女性と年頃の4人の娘との関係を、美しい四季の移り変わりをバックに描出。ケヴィン・コスナーが一家と触れ合う元野球選手のラジオDJに扮し、味のある演技を見せている。突然消えた夫が愛人と逃げたと確信するテリー。事情を知った中年男デニーは彼女の４人の娘とも親しくなり、しばしば家を出入りするようになる。そんなある日、意外な真実が明らかになる。", "2006-06-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血色清晨／THE BLOODY MORNING", "血祭りの朝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ある朝、農村の青年教師が衆人監視のもとに二人の男に殺された。売買婚で嫁がせた妹と関係していたという濡れ衣のために……。コロンビアのノーベル賞作家、ガルシア・マルケスの『予告された殺人の記録』を下敷に中国の農村の封建性、閉鎖性による立ち遅れを赤裸々に描いた問題作。1992年の三大陸映画祭で大賞を受賞している。", "1990-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チェーン", "チェーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "グラビア・アイドル、小向美奈子の映画デビュー作。「蛇女」などで知られる清水厚監督の下、携帯電話の占いメールが恐怖を巻き起こすホラーで、絶叫ヒロインを好演する。かつてホテルだった部屋で少年たちが怪死。死体は異常なねじれ方をしていた。やがて伝染病のように頻発する若者たちの突然死。ミステリー作家の神田は独自のリサーチを開始するが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WAR", "８月のメモワール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "1970年のミシシッピー。貧しい家庭に育ったステュとリディアの双子の兄妹は、ベトナム戦争から帰還した父と再会する。一方、彼らはクラスメイトとともにツリーハウスを作ろうとするが、それを妨害する悪ガキたちが現れて……。アメリカ南部を舞台に、ベトナム戦争後の混乱と家庭の絆を描いた感動作。忘れられないひと夏の思い出を、妹リディアの回想録であざやかに綴る。良き父親役をK・コスナーが好演。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白蛇伝", "白蛇伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "戦後設立された日動映画社（旧・日本動画社）が東映に吸収され、東映動画株式会社として新たに発足。その初の劇場用長編作品となった、“東映長編動画”の第１作。製作当時はアニメーターも、日動映画社時代から活躍していた大工原章と森康二を除いて、みな素人という状態だったが、劇場用作品としては水準以上の完成度に達していて、当時のスタッフの情熱をうかがうことができる。声は森繁久弥と宮城まり子が声色を使いわけて、登場するキャラクターすべてを演じきった。白蛇の化身・白娘（パイニャン）は、中国西湖に暮す若者・許仙を愛していた。妖怪を憎む法海和尚はこれを許さず、二人の仲を裂こうとする。だが白娘は、誤って死んだ許仙に命を与え、不死の命を捨てて人間になってしまう。法海和尚はそれを知って、ついに二人の仲を許すのだった……。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ten Minutes Older: The Trumpet", "10ミニッツ・オールダー　人生のメビウス", "", "", "http://image.eiga.k-img.com/images/movie/1223/315x210.jpg?1263", 92, "「10ミニッツ・オールダー　イデアの森」と合わせて、現代の名監督15人が「長さは10分間」「テーマは時間」という条件で競作。ヴェンダース作品のプロデューサーとして知られる、ウルリッヒ・フィルスバーグが96年に立案、監督たちの賛同を得て実現したもの。どの作品もプリプロダクションに数週間、撮影に1週間、ポストプロダクションに1カ月で製作された。「イデアの森」にはゴダール、ベルトルッチ、シュレンドルフらが参加。", "2003-12-13", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOO　BEAUTIFUL　TO　LIE", "彼女を信じないでください", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「オオカミの誘惑」が話題を呼んだ若手韓流スター、カン・ドンウォンが主演を務めるロマンティック・コメディ。ひょんなことから出会った薬剤師の箱入り息子と女詐欺師の恋の行方が、コミカルに描かれる。これまでになかったドン臭い青年役で、コメディ演技を披露するドンウォンに注目だ。女詐欺師のヨンジュはひょんなことから薬剤師のヒチョルの恋人を装い、彼の実家に居座ることに。真意の読めないヨンジュに頭を悩ますヒチョルだったが……。", "2005-05-28", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WHOLE TEN YARDS", "隣のヒットマンズ／全弾発射", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "B・ウィリス主演のブラック・コメディ「隣のヒットマン」の続編。前作で足を洗ったはずの伝説の殺し屋・ジミーが、元妻の誘拐事件をきっかけにマシンガンを再び手に取って大暴れ！ 今回は「恋しくて」のH・ドイッチが監督を担当し、前作以上のシニカルな笑いを演出。また、おなじみの俳優陣による息の合った演技合戦も見逃せない。偽装工作によって、４年前に死んだことになっている伝説の殺し屋ジミーは、今では引退して家事に熱中する日々を過ごしていた。そんなある日、元妻のシンシアが誘拐されたとジミーの友人で歯科医のオズから連絡が入り……。", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー THE NEXT", "仮面ライダー THE NEXT", "", "", "http://image.eiga.k-img.com/images/movie/34283/315x210.jpg?1474", 0, "「仮面ライダー」シリーズの原点となった初期エピソードを、石ノ森章太郎の原作コミックをベースに新たに映画化した「仮面ライダー THE FIRST」の続編。仮面ライダー1号こと本郷猛が秘密結社ショッカーの陰謀を阻止するため立ち上がってから2年。謎の惨殺死体が連続して発見され世間を騒がせていた。そんな中、高校教師として暮らしている本郷の前に、高い能力を持つショッカーの改造人間・仮面ライダーV3が現われ……。", "2007-10-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ET　MOURIR　DE　PLAISIR", "血とバラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "耽美的な映像美学を追求するR・ヴァディムが、レ・ファニュの『吸血鬼カーミラ』を映画化した怪奇ロマン。名手C・ルノワールが手掛けためくるめく色彩美を得て、芸術的なヴァンパイア・ムービーに仕上げている。第二次大戦後のローマ近郊。かつて吸血鬼が住んでいたという古城の主レオポルドは、美しき恋人ジョルジアとの結婚を控えていた。しかし仮面舞踏会の夜、ジョルジアの幸福を妬む従妹カーミラの前に、忌まわしい女吸血鬼ミラルカの墓が出現。それ以来、カーミラの周囲で奇怪な出来事が続発する。モノクロームをベースに血だけを赤く染めたパートカラーのショットが鮮烈。", "1960-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海女の化物屋敷", "海女の化物屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "巨万の富が眠る海底の墓地をめぐって、海女部落を襲う数々の戦慄。新東宝お得意の海女ものだが、怪奇趣味を加味しているのが目新しい。ヌード拒否事件で退社した前田通子のあとを受けて三原葉子が、グラマラスな肉体をスクリーンで十二分に披露してくれる。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN FLIC", "リスボン特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "銀行襲撃に端を発し、固い友情の絆で結ばれながらも対決の運命に向かっていく二人の男、パリ警察の鬼刑事と、夜の世界に君臨する顔役。そしてその陰で生きる哀しい女の運命を描くフィルム・ノワール。メルヴィル監督の遺作。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美女缶", "美女缶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "役者の夢を諦めて新居に引っ越してきた健太郎は、男前には程遠いオタク系の隣人・富岡の家を大勢の美女が出入りするのを目撃。不可解に思った彼は謎を探るうち、“美女缶”なる不思議な缶の存在を知る。その缶詰は美女を生み出す夢のアイテムだった！　男の妄想を物語にしたような、キテレツな設定が目を引き、最後には切ないドンデン返しが待ち構える。新鋭・筧昌也監督が手掛けた本作は、2003年のゆうばり国際ファンタスティック映画祭のオフシアター部門でグランプリを受賞。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("C，A　COMMENCE　AUJOURD’HUI", "今日から始まる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "北フランスの炭鉱町を舞台に、弱者を切り捨てようとする現代の教育・福祉や家族の在り方に鋭く問題を投げかける愛と希望の感動作。幼稚園の園長ダニエルは、赤ん坊と娘を置いて走り去ってしまった母親の元へ子供たちを送り届ける。そこで見たものは電気も水道も止められた、暖房のない悲惨な生活の実態だった。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lost in La Mancha", "ロスト・イン・ラ・マンチャ", "", "", "http://ecx.images-amazon.com/images/I/41Q8YKN4JQL._SL210_.jpg", 93, "ロケの大洪水や主演俳優の入院など、トラブルが続出、クランクイン６日にして制作中止に追い込まれたテリー・ギリアム監督の「ドン・キホーテ」。暗礁に乗り上げたこの作品の製作過程を捉える。超大作映画が崩壊していく一部始終が明かされると同時に、撮影現場の真の裏側が伝えられており、ファンとしては嬉しさと悲しさが半分、複雑な心境に陥るメイキングだ。", "2001-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABANDON", "ケイティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "米国ではティーン世代に絶大な人気を博したケイティ・ホームズの主演で描いた、サスペンス・スリラー。「トラフィック」でアカデミー賞脚色賞を受賞したスティーヴン・ギャガンが初監督に挑み、彼ならではの練りに練られた緊迫のストーリーが進展する。恋人のエンブリーが２年前に失踪して以来、心の整理がつかない大学生のケイティ。ある日、彼女は事件を追う刑事のハンドラーと出会い、次第に心を通わせるうちに安らぎを得ていく。ところがその矢先、新たな失踪事件が起きる。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Country Strong", "カントリー・ストロング", "", "", "http://ecx.images-amazon.com/images/I/51BbmUNdzXL._SL210_.jpg", 112, "カントリーミュージックの世界を舞台に一人の女性の心の再生を描いた人間ドラマ。人気上昇中のシンガー・ソングライターであるボーは、心身不安定で酒に溺れてしまった人気女性歌手ケリーと出会う。2人はケリーのマネージャーで夫のジェームズ、そしてミスコン上がりの新人女性歌手とともに、ケリーの復活を賭けたライブツアーに出るが……。出演はケリーにグウィネス・パルトロウ、ボーに「トロン・レガシー」のギャレット・ヘドランド。監督は「The Greatest」を手がけたシャナ・フェステ。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Story Telling", "ストーリーテリング", "", "", "http://image.eiga.k-img.com/images/movie/1673/315x210.jpg?4189", 87, "小説家志望の女子大生、その脳性小児マヒの彼氏、ドキュメンタリー映画監督志望の靴屋の店員、その被写体になるおちこぼれ高校生、その弟の小学5年の優等生などなどが入り乱れ、浮き上がっては見失われる、フィクションとノンフィクションの境界線。監督＆脚本は「ウェルカム・ドールハウス」「ハピネス」のトッド・ソロンズ。音楽はベル・アンド・セバスチャンとシャダー・トゥ・シンクのネーサン・ラーソンが担当。", "2002-11-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("非常線の女", "非常線の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アメリカ映画に大きく影響を受けた小津安二郎が撮った和製ギャング映画。衣装、美術など、どこを見てもアメリカ映画そのもので主役の岡譲二のバタ臭い容貌も日本映画にはない味を出している。肝心のヒロイン、田中絹代だけがこれ以上ない日本的な顔をしているのはご愛嬌か。タイピストの時子は社長令息から高価な指輪を贈られるが、彼女には他に恋人があった。街のチンピラを取り仕切るボクサーくずれの襄二である。ある日、襄二はかたぎの娘・和子と知り合い、心惹かれる。時子は嫉妬に燃え始め……。長く忘れられていた作品だが小津を語るうえで必見だ。", "1933-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女子学生 危険な遊戯", "女子学生 危険な遊戯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "ピンク映画の名手・中村幻児監督による女子大生ストーリー。お互いに束縛しない約束で同居している現代的なカップルが、妊娠を機にそれぞれの浮気相手に別れ話を持ちかける。“男と女は肉欲のゲーム”といい切るしたたかでさばけたヒロインが魅力的。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使を誘惑", "天使を誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "百恵＝友和コンビが“恋人宣言”した直後に製作された、二人の11本目の出演作。同棲を続ける男女が、あいまいな日常の中から確かさのようなものを見つけるまでを描く。映画は二人の結婚を暗示して終わり、現実の二人とさりげなく結びつけた。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地球でたったふたり", "地球でたったふたり", "", "", "http://image.eiga.k-img.com/images/movie/53773/315x210.jpg?2963", 118, "血の繋がりのない姉妹のアイとユイは、父親の暴力に耐えながらも強い絆で結ばれていた。やがて両親の離婚により離別することになった2人は、家出をして東京へと向かう。お金もない路上暮らしの末、アイは売春目的で話しかけてきた男の鞄を盗んでしまうが、その男は暴力団の会計士で、2人は組織に追われることになり……。監督・脚本は「ガチャポン」「トップレス」の内田英治。", "2008-09-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOOTMEN", "タップ・ドッグス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "世界中で人気を博した同名舞台の映画化。オーストラリアの鉄鋼都市ニューキャッスルを舞台に、一人の青年が独自のスタイルによるタップ・ダンスのショーを成功させていく姿を、恋や友情を絡めながら描く青春映画。全編に散りばめられたダンス・シーンは、躍動感に溢れていて見ごたえがある。監督は舞台版の生みの親で演出家のD・ペリー。", "2000-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("インストール", "インストール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "『蹴りたい背中』で芥川賞作家となった綿矢りさが、17歳で発表したベストセラー小説を映画化。登校拒否の女子高生とおませな男子小学生が、エロチャットのバイトにはまっていく日々をポップな映像で描き出す。押入れのなかの冒険を経験することで自分を“インストール”していく主人公を演じるのは上戸彩。天才子役、神木隆之介とのちょっとエッチなやりとりにも、アイドル女優のきらめきを存分に感じさせている。ドラマやバラエティーなどで活躍してきた片岡Kの監督デビュー作。高校生の朝子は、学校に通わない日々を続けていた。そんなある日、朝子は、彼女が捨てたパソコンを拾い、親に内緒でネットに繋いでいた男子小学生から、エロチャットのバイトの誘いを受ける。", "2004-12-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CHINOISE，OU PLUTO＾T A｀ LA CHINOISE", "中国女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "中国紅衛兵と文化大革命を1967年のパリの中に描く、というテーマで、ゴダールが政治を本格的に作品の表面に押し出した画期的な１編。それまでもゴダールは映画を政治とのかかわりの中で作り続けてきたが、この作品ではドラマ性が以前の作品とは比べものにならぬほど稀薄になっており、全編政治的アジテーションというような感じさえ受ける。大人たちが夏のバカンスに出かけた留守の家に集った５人の若者は、みな熱狂的な毛沢東信奉者。やがて彼らの思想はゲリラ革命運動からテロリズムへと発展していくかにみえたが……。共産主義の象徴である赤の使い方が印象的。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A BRIDGE TOO FAR", "遠すぎた橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 176, "ノルマンディー上陸作戦から３ヵ月後の1944年９月、連合軍が企てた大胆不敵な“マーケット・ガーデン作戦”を豪華スターの競演で描く壮大な戦争スペクタクル。史上空前の空陸大共同作戦が、すべて失敗に終わるまでを、壮大なスケールで描く。結局、イギリスからアメリカへのデモンストレーションでしかなかったこの無意味な戦闘の数々を、R・アッテンボローはあくまでもクールに捉えている。", "1977-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Last Exit to Brooklyn", "ブルックリン最終出口", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "文壇、立法者、読者を賛否両論の渦に巻き込んだH・セルビーJr.のベストセラー小説の映画化。ブルックリンにたむろし、仕事もせず、金を巻きあげて暮らすストリート・ギャングの女と、ブルックリンで働く労働者の男。いくつかのエピソードが交錯するなか、ブルックリンという街の姿があざやかに描き出されていく。", "1989-01-01", "7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あらくれ", "あらくれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ハードボイルド作品で知られる長谷部安春が監督した任侠アクション。弟分の太郎を頼って石川県小松市にやって来た鬼頭は、同地区に勢力を伸ばしてきた関西ヤクザ・すみれ会に世話になることになった。しかし、すみれ会の悪業を知った鬼頭は、彼らの宿敵・鉄五郎と組み、凄絶な戦いを挑んでいく。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("侠女", "侠女　第二部：最後の法力", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "京劇をもとにした様式美のなかに計算されたアクションシーンを盛り込み、見るものを飽きさせない。特に竹林での決闘シーンは有名。光と色彩にあふれたキン・フーの映像美学の極地が表現されている。キン・フー武侠映画でおなじみのヒロイン、シュー・フォンは演技・アクションともすばらしい。単なる復讐劇に終わらぬ起伏に満ちた夢幻物語は、ついに一つの仏教的な哲学世界へと結実されていく。", "", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("完全なる飼育　メイド、for you", "完全なる飼育　メイド、for you", "", "", "http://image.eiga.k-img.com/images/movie/54913/315x210.jpg?1643", 100, "実在の事件に基づいた松田美智子の小説「女子高校生誘拐飼育事件」を原作とするシリーズ第7弾で、初の3D作品。監督は深作欣二の長男・深作健太。冴えない漫画喫茶の店長・椛島は、メイド喫茶で働くメイドの苺に恋をしていた。メイド喫茶に通いつめるうち、椛島は苺が自分に好意を寄せていると思い込み、自分の店に連れ込み監禁してしまう。最初は椛島を拒絶していた苺も、次第に心を開いていき……。", "2010-01-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地雷を踏んだらサヨウナラ", "地雷を踏んだらサヨウナラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "カメラマン、沢田教一のドキュメンタリー「SAWADA」を手掛けた五十嵐匠が、若き戦争カメラマン、一ノ瀬泰造の半生を描く。1972年、民族解放軍クメール・ルージュと政府軍の抗争が激化するカンボジア。25歳の戦争カメラマン、一ノ瀬泰造はスクープをねらっていた。仲間や彼を慕っていた幼い少年たちが次々と命を落とすなか、泰造はますます戦場を写すことの使命にかられていく。姉の結婚式で一時帰国するも、すぐ戦場へと舞い戻り、常に彼の心を捕らえて放さなかった解放軍の聖域アンコールワットへと向かうのだった……。浅野忠信が戦場に身を投じる青年の情熱を見事に表現。時折挿入される泰造から母への手紙が、当時の心境を生々しく伝える。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勝利者たち", "勝利者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "往年のスターたちを集め、“社長”シリーズなどで知られる職人監督、松林宗恵が撮り上げた、日本初の“ゲートボール映画”。倒産寸前の造り酒屋の社長が、大富豪に融資を申し込んだところ、ゲートボール日本グランプリでの優勝を条件に、融資するという。社長は、６人の昔カタギの職人を集め、ゲートボールの特訓を開始するが……。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RECKLESS", "俺たちの明日（1983）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "オハイオ州の小さな町を舞台に、ここから早く抜け出したいと思っているバイク好きの高校生と、そんな彼に思いを寄せる裕福な家の娘のロマンスを、きわめてまじめなタッチで描いた硬派な青春映画。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　籠釣瓶花街酔醒", "シネマ歌舞伎　籠釣瓶花街酔醒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-09-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER IN PARADISE", "楽園の殺人者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "N.Y.で連続殺人犯に執拗に狙われ、妻までも殺された刑事ラスキー。犯人は挙げたものの身も心もボロボロにされ、今はハワイで酒に溺れる生活を一人送っていた。ところが、そのハワイでN.Y.と同じ手口の殺人が起こり始める……。姿なき殺人鬼と刑事の対決を描くミステリー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUMPIN’AT　THE　BONEYARD", "ブロンクス　破滅の銃声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "荒れ果てた生活から抜け出そうともがく兄弟の姿を描く人間ドラマ。離婚したばかりのマニーは仕事もなく、精神的に疲れ切っていた。そんなある日、弟のダニーがドラッグの金欲しさに彼の家に忍び込む。マニーは堕落した弟の姿に絶望するが、彼を救おうと施設に入るよう説得。その熱意が通じ、ダニーも人生をやり直すために決意を固めるのだが……。弟を思いやる一方で、結婚生活を破綻させ、わが子にも会えない兄の苦悩を、T・ロスが人間味たっぷりに表現。彼らを見守る男をS・L・ジャクソンが演じる。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE´SIRE´E", "デジレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ナポレオンと初恋の女を描く異色ラブ・ストーリー。彼女の名はデジレ。デジレが17歳の時から書き始めた日記によって、二人の人生が綴られていく。栄光を手に入れたナポレオンに裏切られたデジレは、やがてワーテルローの戦いに敗れた彼に降伏を説いていく。ナポレオンに扮するのは、M・ブランド。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RUNESTONE", "ヘルガイバー 魔獣大戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "バイキング伝説にまつわる凶獣と、数百年来その凶獣と戦い続ける伝説の勇者との対決を描いたファンタスティック・アドベンチャー。とある炭坑で巨大な石碑が発掘される。実は北欧に伝わる恐るべき凶獣フェンリルが封じ込められていた。よみがえった凶獣は考古学者の体を乗っ取り、醜悪で凶暴な姿を現わす。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FLINTSTONES", "フリントストーン モダン石器時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "恐竜のブルドーザー、マンモスの鼻のシャワー、石のコインの自動販売機、そんな石器文明社会に暮らす平凡な男が、ある日、会社の副社長に任命された……。アメリカの人気アニメの映画化で、SFXやCGを駆使して作り上げられたポップな石器時代の世界は「ジュラシック・パーク」顔負けの必見もの。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クライムブローカー 仮面の誘惑", "クライムブローカー 仮面の誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "外では女性裁判官、内では良妻賢母の顔を持つホリー。そんな彼女には誰も知らないもう一つの顔がある。それは完全な犯罪を練り、プロに実行させる“クライム・ブローカー”としての顔だ。その顔を日本から来た犯罪心理学者の岡崎だけが見破り、彼女に接近を図るが……。加藤雅也が本格的に世界進出を果たした作品。フランスの国際派女優J・ビセットと互角に張りあった演技を見せてくれる。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マザー・テレサと生きる", "マザー・テレサと生きる", "", "", "http://image.eiga.k-img.com/images/movie/55113/315x210.jpg?4712", 73, "「マザー・テレサとその世界」「マザー・テレサの祈り／生命それは愛」の千葉茂樹監督が、マザー・テレサ生誕100年を記念して製作したドキュメンタリー。2010年開催の「マザー・テレサ映画祭」にて上映。1997年に逝去したマザー・テレサが、生涯3度に渡り訪れた日本。マザー・テレサの後継者シスター・ニルマラ総長の特別許可のもと、彼女の精神が日本でどのように受け継がれているのかを追う。", "2010-01-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八仙飯店之人肉叉焼飽／THE　UNTOLD　STORY", "八仙飯店之人肉饅頭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "実際に起きた猟奇犯罪を映画化したスプラッタ・ホラー。当時、香港映画界ではこの種の作品がブームとなっていたが、本作品はそのなかでも質が高い映画で、A・ウォンは香港電影金像奨で最優秀主演男優賞を受賞している。香港で殺人を犯したウォンはマカオに逃げ込み、料理店で働くことに。だが、些細な事から店主一家を殺してしまった彼は死体をミンチにして饅頭を作って、客たちに売ってしまい、店主から店を譲り受けたと言って商売を続ける。そのあとも彼は気に食わない人間を殺しては饅頭にしていったが……。死体をさばくシーンの生々しい描写、取り憑かれたようなウォンの表情が恐怖感を一層あおる。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("找楽／FOR FUN", "北京好日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "たくさんの人が行き交う北京の片隅で京劇に興じる老人（京劇専門の劇場の管理人の職を退いたハン老人、元共産党幹部で女形を得意とするワンヨウ……）たち。街中の一角が彼らの練習場所であったが、ハン老人の骨おりで市からクラブ活動用の部屋を借りることができる。しかし、老人たちは仲違いを起こし、再びまた街中へと帰っていく……。オープニング、ゆっくりと滑っていくカメラに動きを合わせるかのような『マック・ザ・ナイフ』のメロディーにまず、ベルナルド・ベルトルッチ監督の下で修行を積んだというニン・イン監督のセンスの良さが見える。さらに、まるで今、そこにいるかのような老人たちの生き生きとした表情を切り取った映像も見事だ。素人を起用したとは思えぬ確かな存在感と、人間的なおかしみが、老人たち一人一人の表情からこぼれ落ちる。", "1992-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　GINGKO　BED", "銀杏〈いちょう〉のベッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「シュリ」のカン・ジェギュ監督のデビュー作で、同作で主演を務めた韓国のトップスター、ハン・ソッキュの出世作。千年の時空を超え、愛と憎しみの因縁にほんろうされる男女の姿を、ドラマチックに描き出す。後に続編に当たる「燃ゆる月」が製作された。画家で大学講師のスヒョンは、外科医の恋人ソニョンと幸せな生活を送っていた。だが、路地で銀杏の木で作られたベッドを拾った日から、次々と不思議な出来事に遭遇。何者かに命を狙われた彼は、古代の衣装を身にまとった美女ミダンに救われる。ミダンの話によると、スヒョンはミダンと恋仲だった古代の宮廷楽士の生まれかわりで、楽士は嫉妬に狂ったファン将軍に殺されたらしい。スヒョンの拾ったベッドによって、それぞれの魂が現代に復活してしまったのだ。", "1996-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私刑", "私刑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "当時、エノケンものなど喜劇の多かった中川監督が作った現代アクション。菅原一家の子分・梅若と清吉は、お加代をめぐって対立する。清吉はカタギとなってお加代と一緒になろうとするが、梅若は彼を非情な罠にかける。裏切られた清吉とお加代は世間の目から身を隠し、逃れていくのだが……。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN UOMO DA RISPETTARE", "ザ・ビッグマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "３年間の刑を終えて出所してきた男が、これが最後と誓って金庫破りに挑むが、一緒に組んだ相棒の若者と自分の妻が実は密通していることを知り、ラストには怒りの引き金に手がかかる……。ダグラスとジェンマが共演したアクションもの。", "1972-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROADWAY BOUND", "ブロードウェイ・バウンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "第二次世界大戦が終わって間もない頃のブルックリン。いつかブロードウェイに出てコメディ作家になることを夢見ているユジーンとスタンリーの兄弟が、結婚生活の破綻の予感に震える両親とトロツキストの祖父とともに暮らしていた。そんなある日、兄のスタンリーがラジオのコントの台本書きの仕事を持ち帰って来る。両親のいさかいが聞こえてくる家で、徹夜して台本を書き上げた二人。いよいよそのコントがラジオで放送される日がやって来た……。「想い出のブライトン・ビーチ」「ブルースが聞こえる」と続くN・サイモンの半自伝的戯曲三部作の完結編。家から巣立っていく兄弟（弟ユジーンはサイモン自身の姿である）を物語の縦軸に置きながら、ユダヤ系家族の姿を、軽やかなノスタルジーを込めて描いた家庭劇となっている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("洗黒銭／TIGER CAGE ２", "タイガー・コネクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "カンフー・シーンや銃撃戦に加え、カーチェイスやチャンバラまで多彩なアクションを盛り込んだ香港映画。汚れた金を“洗浄”するためロスから香港にやって来たデビッドは、金の洗浄役、弁護士ウィルソンの罠にはまり、金を奪われる。偶然居合わせた刑事アレンと弁護士メアリーは、ウィルソン一派に追われるハメとなった。今や孤立したデビッドも加わって、３人は黒幕ウィルソンに立ち向かう。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLOB", "ブロブ 宇宙からの不明物体", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "宇宙空間のかなたから人工衛星とともに落ちてきた生命体“ブロブ”。それは、アメリカの田舎町で平和に過ごす人々を次々と飲み込み力を増大させていった……。S・マックィーン主演で作られた「マックィーンの絶対の危機」のリメイク作品。最新のSFXを駆使したスピード感あふれる映像が秀逸。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勝手にしやがれ!! 英雄計画", "勝手にしやがれ!! 英雄計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ヤクザ追放の市民運動に巻き込まれた雄次と耕作。やがて、運動の首謀者の悪行を知った雄次は、狙撃事件の濡れ衣を着せられ住み慣れた街にいられなくなるが……。大胆な長回しショットも映える、シリーズ最終作。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SANS MOBILE APPARENT", "刑事キャレラ 10＋１の追撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "エド・マクベイン原作の『87分署』シリーズをフランス人監督P・ラブロが映画化。舞台をニューヨークからニースに移して、連続殺人事件を追うキャレラ警部の活躍を描く。現代の病んだ社会を織り込んだ、上質のハードボイルド作品。", "1972-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤穂城断絶", "赤穂城断絶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 159, "元禄14年に起こった赤穂四十七士による吉良邸討ち入り事件、いわゆる“忠臣蔵”の25回目の映画化。大石内蔵助を萬屋錦之介、浅野内匠頭を西郷輝彦、吉良上野介を金子信雄が演じる。従来の美談調に終わらせることなく、赤穂城断絶から討ち入りまでの630日を「仁義なき戦い」の深作監督らしく実録ふうに描くことに徹し、萬屋錦之介らの好演を得ていい仕上がりになった。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("この首一万石", "この首一万石", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "武家の奉公人である下郎の悲劇を描いて封建社会の身分制度や武士道礼賛の気風を痛烈に批判した、伊藤大輔監督の時代劇。1920年の「下郎」、そのリメイクである1955年の「下郎の首」に続く３部作の一編。美男スターで売り出した大川橋蔵のみずみずしい演技と二役を演じる江利チエミの愛らしさが魅力。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INNOCENT STEPS", "ダンサーの純情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "韓国で国民的人気を誇るムン・グニョンが主演を務めた恋愛ドラマ。失意のダンサーとパートナーとなった少女の切ない愛を描く。ミュージカル出身の新星、パク・コニョンの華麗なダンスも見どころ。才能を開花できないでいるダンサーのヨンセは、中国人の少女チェリンを新パートナーにする。素人のチェリンだが瞬く間にダンスを体得。ヨンセにとって最高のパートナーになるが……。", "2006-04-15", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pervert's Guide to Cinema Presentes by Slavoj Zizek", "スラヴォイ・ジジェクによる倒錯的映画ガイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "思想家で精神分析家のスラボイ・ジジェクが、アルフレッド・ヒッチコックの「めまい」、デビッド・リンチの「ブルー・ベルベット」、ジョージ・ルーカスの「スター・ウォーズ　エピソード3 シスの復讐」など、40本以上の歴史的名作の映像を引用しながら、映画の新しい見方を提示するドキュメンタリーフィルム。", "2010-04-24", "3,77,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いそぎんちゃく", "いそぎんちゃく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "時のセクシー女優、渥美マリの妖艶な魅力を生かした女性ドラマ。肉体を武器にしてのし上がる女性の生き方を鮮烈に描く。クリーニング店の店主に抱かれて男を知った、若い浜子。手切れ金をもらって店を飛び出した彼女は、小料理屋の女中として老人を弄び、次にピンクサロン勤めを始めてからは、なじみ客に貢がせる日々を送るが……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵯峨野の宿", "嵯峨野の宿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "数奇な運命にもてあそばれながらも、健気に生きていく人形作家・篠原良子。美術商・平垣との出会いから別れまでが、美しい嵯峨野の風景をバックに情緒たっぷりに描かれている。三原じゅん子の大胆なヌードが話題になった。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CORRIDORS　OF　TIME", "ビジター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "フランスで大ヒットを飛ばした「おかしなおかしな訪問者」の続編。J・レノ、C・クラヴィエの迷コンビが、またしてもナンセンスなギャグを連発する。前作のラストで、めでたく中世に帰還したゴッドフロワ伯爵に新たな難題が発生。婚約者の家宝紛失で結婚が危うくなった彼は、お騒がせ男の下僕ジャクイユを従えて再び20世紀に飛ぶことになる。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kingdom of Heaven", "キングダム・オブ・ヘブン", "", "", "http://image.eiga.k-img.com/images/movie/1103/315x210.jpg?3473", 145, "妻子を亡くし、失意の中にいた若き鍛冶屋バリアンは、父ゴッドフリーの意志を継ぎ、十字軍の騎士としての道を歩みはじめる……。「グラディエーター」のリドリー・スコット監督が、オーランド・ブルームを主演に迎えて描く歴史超大作。バリアンの運命の女性となるシビラは、ベルトルッチ監督の「ドリーマーズ」でデビューした、フランス人女優エバ・グリーン。その他、リーアム・ニーソン、ジェレミー・アイアンズ、デビッド・シューリスなど演技派が多数出演。", "2005-05-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピーナッツ", "ピーナッツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "“ウッチャンナンチャン”のウッチャンこと内村光良の記念すべき映画監督デビュー作。富士山の見える地元に久々に戻ったスポーツライターの秋吉。ところが、愛する地元は再開発に揺れ、かつて在籍した草野球チーム“富士沢ピーナッツ”は消滅の危機に陥っていた。 そこで彼は元チームメイトを招集するが……。人気お笑い芸人たちがそれぞれの個性を存分に発揮。笑いと優しさがあふれる温かいドラマだ。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE REAL GLORY", "暁の討伐隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "20世紀初頭のフィリピンを舞台に、山地に住む凶悪なモロ族を恐れる住民たちに救いの手をさしのべる米軍守備隊の活躍を描いたアクション。モロ族のリーダーを駒井哲が演じているのは、1930年代ハリウッド映画ならではのご都合主義。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の舞", "風の舞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 59, "ハンセン病患者を社会から強制隔離する“らい予防法”のため、57年間にも渡り瀬戸内海での療養所生活を余儀なくされた詩人の塔和子が、作詩を通じて過酷な現実と闘ってきた歴史を追うドキュメンタリー。女優の吉永小百合が塔和子の詩を朗読する。1996年に廃止になったハンセン病患者を社会から強制隔離する“らい予防法”。15歳で発病し、瀬戸内海の大島青松園に隔離された詩人の塔和子は、生きることの尊さを詩に託し、療養所生活を送る人々の思いを伝える。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BASEKETBALL", "ベースケットボール　裸の球〈タマ〉を持つ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「裸の銃を持つ男」のD・ザッカー監督が手掛けたコメディ。大リーガーになる夢も果たせず、ただの落ちこぼれとなったクープとリマーが、バスケと野球を組み合わせた“ベースケット・ボール”なる新ゲームを考案。これがブームを巻き起こし、二人はプロ選手としてスターとなるが、そこに悪徳オーナーの魔の手が迫り……。人気アニメ「サウスパーク」の生みの親T・パーカーとM・ストーンが、オバカな主人公コンビを妙演。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tradalskaren", "心の森", "", "", "http://image.eiga.k-img.com/images/movie/55013/315x210.jpg?4664", 76, "都会での暮らしに疲れてスウェーデンの美しい森へやって来た3人の映画撮影クルーと1匹の犬が、ひと夏の間に樹上にキャビンハウスを建てるまでを記録したドキュメンタリー。家づくりに奮闘する様子を追いながら近隣に住む人々に話を聞き、森に対する彼らの深い愛情を感じ取るとともに、社会学者や宗教家といった専門家へのインタビューを通し、スウェーデンにおける人間と木との共存の歴史を明らかにしていく。", "2009-10-19", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("絶不低頭／FIRST SHOT", "九龍大捜査線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "法と秩序の腐敗した1970年代初頭の香港。警察とマフィアの汚職の実態を憂いた新任の総督は、配下のカーに真相究明を命じた。カーは巡査部長のウォンらの協力のもと、マフィアの黒幕ラクが汚職の元凶であることを知る。汚職摘発機関ICAC（内務調査局）が設立され、ウォンたちはラクとその組織を追い詰めていく。「男たちの挽歌」シリーズのティ・ロン、人気女優M・チャンらがハードなアクションを展開する“香港版「アンタッチャブル」”。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Species", "スピーシーズ 種の起源", "", "", "http://ecx.images-amazon.com/images/I/51CSe777g4L._SL210_.jpg", 109, "DNA操作の実験から生まれた新種の生命体が、人類を存亡の危機に陥れるさまを描いたSF大作。20年前に、ある研究グループが宇宙に向けて発信したメッセージに対し、不思議なDNA配列とそれを人類のDNAに結合する説明を指示した応答があった。サビアー・フィッチの科学者チームは、ユタ砂漠の地下にある秘密基地で実験を開始。わずか数週間で誕生した赤ん坊は人間の女性とまったく変わらないのだが……。「エイリアン」のH・R・ギーガーをはじめとするベテランチームがSFXを担当し、迫力ある映像を作り出している。", "1995-11-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STATE　OF　DOGS", "ステイト・オブ・ドッグス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "一匹の犬の目線から、現代社会の実状を見つめたハートフルなドラマ。モンゴルの首都ウラン・バートルの路地に住んでいた野良犬のバッサル。“犬は死んだら人間に生まれ変わる”という言い伝えがモンゴルにはあるが、バッサルはハンターに撃たれて死んだために人間には生まれ変わりたくないと思う。そこで、自分の記憶をたどる旅へ出ることにする。", "1998-01-01", "57,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京オンリーピック", "東京オンリーピック", "", "", "http://image.eiga.k-img.com/images/movie/53793/315x210.jpg?2908", 0, "大ヒットDVD「スキージャンプ・ペア」の真島理一郎が総監督を務め、国内外の個性派クリエイターたちがアニメ、CG、パペット、実写など様々な映像表現を駆使して“ありえない”架空のスポーツ競技を創造。マラソン新種目「女子ヘルマラソン」、絶叫系新種目「サムライコール」、トライアスロンの新形態「ホームアスロン」、キングオブバカ決定戦「バカ近代四種」など、抱腹絶倒の競技映像が展開される。", "2008-08-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("国定忠治", "国定忠治", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "片岡千恵蔵が国定忠治を演じる痛快編。悪代官を斬って赤城の山にたてこもった忠治は、やがて無数の敵方に囲まれて味方を失い、赤子を抱えて信州路を落ちのびていく。昔の恋人に赤子を預けて、再び一人旅を続ける忠治。その道中、彼は女子供を売買する悪党相手に30人を斬りまくるのだった。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Six Days Seven Nights", "6デイズ／7ナイツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "南の楽園でバカンスを過ごそうと恋人とやってきた雑誌編集者ロビン。だがロビンに急な仕事が入り、偏屈なバイロット、クインの操縦するおんぼろセスナ機で戻ることに。その途中、嵐で無人島に不時着。2人は何かと反発しあいながらも様々な危機を乗り越えていき……。冒険活劇を織り込んだロマンティック・コメディ。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雨月物語", "雨月物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "上田秋成の『雨月物語』から『朝茅が宿』『蛇性の婬』の２編を取り出して脚色された溝口健二の名作。戦国時代、琵琶湖にほど近い村に住む源十郎は、戦乱に乗じ一獲千金を狙い、自らの焼いた陶器を売ろうとして、侍になることを夢見る義弟の藤兵衛、そして反対するそれぞれの女房を連れて琵琶湖を渡る。その後、離れ離れになった彼らのうち、藤兵衛は大将の拾い首をして出世を果たすが、妻の阿浜は娼婦に堕ちてしまう。一方、妻・宮木が子とともに決死の覚悟で村へ戻ろうとする中、源十郎は死霊に見そめられ悦楽の日々を送っていた……。全編に宮川一夫のカメラが冴えわたり、特に源十郎と死霊・若狭のシークエンスにおいては幽幻妖美の世界がたぐいまれな映像美として結実している。若狭を演じた京マチ子の美しさ、彼女に仕える老女・右近の不気味なシルエットは忘れられない印象を残す。対する宮木役の田中絹代は母性の優しさを感じさせる好演。第14回ヴェネチア映画祭銀獅子賞を受賞した。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BABY OF MACON", "ベイビー・オブ・マコン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "P・グリーナウェイによる問題作。17世紀のイタリア。町の劇場で、『マコンの赤児』と題された劇が幕を開けた。それは、不妊症が流行する時代に醜い妊婦から生まれた子供が、金のためにその姉に利用され、救世主に祭り上げられるというもの。舞台袖の特別席には権力者のコシモらの一行が陣取り、舞台にしゃしゃり出ては大声で感想をもらす。やがて、舞台上と観客席の境界があいまいになっていき……。格調高い映像美で描かれた物語の中に、マスコミにほんろうされやすい現代社会の風潮を浮き彫りにする。スキャンダラスな結末が、いかにもグリーナウェイらしい。", "1993-01-01", "3,7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Master and Commander: The Far Side of the World", "マスター・アンド・コマンダー", "", "", "http://ecx.images-amazon.com/images/I/51XToWkOpOL._SL210_.jpg", 139, "パトリック・オブライエンの海洋冒険小説「英国海軍の雄　ジャック・オーブリー」（ハヤカワ文庫）シリーズを原作に「いまを生きる」「トゥルーマン・ショー」のピーター・ウィアー監督が映画化。19世紀初頭、ナポレオン率いるフランスと交戦中の英国海軍。不敗神話を誇る伝説の英国軍艦長ジャック・オーブリーは、その情熱と誇りある生きざまを通じて、10歳の少年から老人までを含む総勢約130人の乗組員たちを率いていく。", "2004-02-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La vallee", "ラ・ヴァレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「完全犯罪クラブ」（2002）などハリウッドで活躍するイラン出身のバーベット・シュローダー監督が、1972年に発表した長編第2作。未開の地を舞台に、1人の女性が生の喜びを発見するまでの旅を描く。極楽鳥の羽を探し求めてニューギニアを旅していたパリのブルジョワ夫人ビビアーヌ。途中で出会った冒険家の青年に興味を抱いた彼女は、探検隊と共に幻の地を目指すという彼に同行することになり……。シュローデル監督の処女作「モア」に続き、ピンク・フロイドが音楽を担当。", "2007-11-03", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Capricorn One", "カプリコン・1", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "　打ち上げ寸前だった有人火星宇宙船カプリコン1の3人の乗組員が船外に連れ出される。宇宙船の故障が発覚したものの、それを公表できず、やむなく関係当局は大掛かりなセットを組んで、その成功をでっち上げることに。中継映像から不審なものを感じた新聞記者コールフィールドは調査を開始。一方、3人の乗組員たちは闇に葬られようとしていた！　全世界をあざむこうとする陰謀に立ち向かう者たちの戦いを描くサスペンス巨編。", "1977-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Shawshank Redemption", "ショーシャンクの空に", "", "", "http://ecx.images-amazon.com/images/I/4190HoEglDL._SL210_.jpg", 143, "スティーブン・キングの中編「刑務所のリタ・ヘイワース」をティム・ロビンス＆モーガン・フリーマン主演で映画化した人間ドラマ。長年ショーシャンク刑務所に入っている囚人レッド（フリーマン）と無実の罪で収監された元銀行副頭取アンディ（ロビンス）の友情を軸に、アンディが巻き起こす数々の奇跡が描かれる。監督・脚本は本作で長編映画デビューを果たしたフランク・ダラボン。94年度のアカデミー賞では作品賞を含む7部門でノミネートされたが無冠に終わった。", "1995-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版キノの旅 the Beautiful World 病気の国 For You", "劇場版キノの旅 the Beautiful World 病気の国 For You", "", "", "http://image.eiga.k-img.com/images/movie/33943/315x210.jpg?4568", 0, "人間キノとしゃべる二輪車エルメスの不思議な旅を綴る時雨沢恵一の人気ライトノベルの劇場版アニメ第2弾。科学が発達した国を訪れたキノとエルメス。安定した環境の巨大ドームの中で暮らしている国民たちは、ドームの外に存在し、選ばれたエリートだけが住むことを許される“開拓地”に強い憧れを抱いていた。厳重な消毒を受けてドームの中へ足を踏み入れたキノとエルメスは、そこでイナーシャという名の少女と出会う。", "2007-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大江戸五人男", "大江戸五人男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "松竹映画30周年記念と銘打って製作された時代劇の大作で、伊藤大輔にとっても映画生活30年目の仕事となった。有名な講談に題材を取り、時代劇の大スター・阪東妻三郎と市川右太衛門を配した。映画化にあたっては、これに岡本綺堂の『番町皿屋敷』の主人公・青山播磨の性格とエピソードを盛り込んでいる。徳川家光の治世下の太平の世、冷遇をなげく旗本の不平不満はいろいろの形で現れていた。水野十郎左衛門を組頭とする白柄組が芝居の邪魔をした時、その場を取りなしたのは町奴・幡随院長兵衛。以来、白柄組は幡随院一家を目の仇とするようになった。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("笑うイラク魂・民の声を聞け", "笑うイラク魂・民の声を聞け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "自らを放浪記者と呼び、世界60ヵ国を取材した経歴を持つジャーナリスト、吉岡逸夫が撮り上げたドキュメント。「アフガン戦場の旅」で戦火のアフガニスタンに潜入した彼が、今回は国連査察の進むイラクを取材。首都のバグダッドなどから、現地で暮らすイラク人の生の声を届ける。緊迫した状況にありながら、悲壮感などみじんもない彼らの笑顔と本音が興味深い。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Apart Together", "再会の食卓", "", "", "http://image.eiga.k-img.com/images/movie/55803/315x210.jpg?4240", 0, "上海のある家族の食卓を通して中国と台湾の複雑な関係を浮き彫りにし、ベルリン国際映画祭で銀熊賞（最優秀脚本賞）に輝いたヒューマン・ドラマ。上海で暮らすユィアーは、生き別れた夫イェンションが40数年ぶりに台湾から帰るとの報せを受ける。すでに再婚して新しい家族を築いていた彼女は、戸惑いながらも元夫を食事に招いて精一杯のもてなしをするが、彼から台湾で一緒に暮らそうと誘われてしまう。「トゥヤーの結婚」のワン・チュアンアン監督がメガホンをとる。", "2011-02-05", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の妻たち 赫い絆", "極道の妻たち 赫い絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "シリーズ第８作は、大阪のヤクザ一家に生まれ育った生粋の“極妻”が主役。組長の老父の引退とともに、跡目を継いだ若頭補佐と結婚して、組の姐となった堂本きわ。彼女をめぐって、夫、組内の極道、その妻たちとのあつれき、巨大な事業利権に絡んだ敵対組織との抗争、企業との駆け引きが繰り広げられる。その濃厚にして華麗な対立劇と愛憎劇を締め括るのは、きわの“決着は、わてがとらしてもらいます”の決め台詞。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Slumdog Millionaire", "スラムドッグ＄ミリオネア", "", "", "http://image.eiga.k-img.com/images/movie/54163/315x210.jpg?4753", 120, "「トレインスポッティング」のダニー・ボイル監督が、スラム育ちの青年の運命と過酷な半生を疾走感あふれる演出で描いた人間ドラマ。インド・ムンバイのスラム街出身で無学の青年ジャマールは、TV番組「クイズ＄ミリオネア」で最終問題までたどり着き、一夜にして億万長者となるチャンスをつかむが、不正を疑われてしまう……。インドの外交官ビカス・スワラップによる原作小説を、「フル・モンティ」のサイモン・ボーフォイが脚色。第81回アカデミー賞では作品賞、監督賞ほか最多8部門を受賞した。", "2009-04-18", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Almost Famous", "あの頃ペニー・レインと", "", "", "http://image.eiga.k-img.com/images/movie/1253/315x210.jpg?2912", 123, "ウィリアムは15歳。小さい頃は弁護士を目指す秀才だったが、4年前に家出した姉が残したレコードがきっかけでロックの世界にのめり込んでいた。伝説的ロック・ライター、レスター・バングスに売り込んで取材の仕事を得たウィリアムは、ライブの楽屋を訪ね売り出し中のバンド“スティルウォーター”に取材を試みる。バンドへの熱い思いを語ってメンバーに気に入られ、楽屋へのフリーパスを得た彼は、バンドのグルーピーの中でも一際目立つ少女、ペニー・レインに一目惚れする。", "2001-03-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stalag 17", "第十七捕虜収容所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "　原作は、ナチス監視下のアメリカ軍捕虜収容所を舞台にしたブロードウェイのヒット劇。皆の協力のもと、脱走を試みた2人の捕虜が射殺される。仲間内から裏切り者がいるとの声が上がり、一匹狼のセフトンは真っ先に疑いの目を向けられる。なんとか疑惑を晴らそうと、ドイツ軍のスパイ探しをするセフトンだったが……。主演のウィリアム・ホールデンは、今作でオスカー俳優の仲間入りを果たし、その後も目覚ましい活躍を遂げる。しかし、1981年不慮の事故でこの世を去った悲劇の俳優である。", "1954-02-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STORY OF VERNON AND IRENE CASTLE", "カッスル夫妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "実在のダンサー夫妻、バーノンとアイリーンの半生をアステアとロジャースのコンビで描いた伝記ミュージカル。1911年、沿岸で週末を過ごすショー・ダンサーのバーノンは、溺れかけた子犬を一緒に助けたことからアイリーンと恋に落ち、結婚。その日から、カッスル夫妻のダンス人生が始まる。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONTY PYTHON’S AND NOW FORSOMETHING COMPLETELY DIFFERENT", "モンティ・パイソン・アンド・ナウ", "", "", "http://ecx.images-amazon.com/images/I/41SG3ECR97L._SL210_.jpg", 90, "「モンティ・パイソン・アンド・ホーリー・グレイル」の続編的な爆笑パロディ映画。さる高級レストランを舞台に繰り広げられるシュールなシチュエーション・コメディや、牛乳配達屋の悲喜劇など、モンティ・パイソンの独壇場とも言うべきブラックな笑いを満載している。", "1975-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEANNE LA PUCELLE I", "ジャンヌ／愛と自由の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "リベット監督がジャンヌ・ダルクを描いた歴史大作の前編。（後編は「薔薇の十字架」）英雄としてでなく、ひとりの少女として成長する少女の姿をサンドリーヌ・ボネールが熱演。神の啓示を受け、国を救わんと軍に志願したジャンヌ。厳しい審問の末にようやく兵を任された彼女が見事オルレアンを解放するまでの苦難と栄光の日々……。", "1995-04-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIVE DAYS ONE SUMMER", "氷壁の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "アルプス登山にやって来た二人の男女。もう中年にさしかかっている男ダグラスとまだ幼なさすら感じられる若い娘ケイト。二人は夫婦のようにふるまってはいるが、実は関係を結んでしまった伯父と姪であった。この登山で体験する出来事や、好意を寄せてくるスイス人ガイド、ヨハンとの出会いは確実にケイトの心を変えていった。そして運命の５日目、ダグラスとヨハンは二人きりで頂上を目指した……。構想30年、ジンネマン監督執念の映画化だけに演出は手堅い。二人の関係がうまく回想で処理されている脚色、アルプスの山々を美しく捉えたロトゥンノのカメラとスタッフ陣も一流の域。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Earth", "アース", "", "", "http://image.eiga.k-img.com/images/movie/53253/315x210.jpg?2117", 98, "日本でも驚異的ヒットを記録した海洋ドキュメンタリー「ディープ・ブルー」のスタッフが再結集し、5年の歳月を費やして完成させた自然ドキュメンタリー。舞台を地球全体に拡げ、北極から熱帯、深海まで全世界200カ所以上で撮影を敢行。ホッキョクグマ、アフリカゾウ、ザトウクジラといった様々な生き物が厳しい自然の中で紡ぎ出す命のドラマを最新鋭の技術で捉え、ベルリン・フィルハーモニー管弦楽団のフルオーケストラに乗せて映し出す。", "2008-01-12", "7,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A SIMPLE TWIST OF FATE", "パパとマチルダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "音楽教師のマイケルは、最愛の妻に裏切られたことがきっかけで、世間との関係を断ち切り自分の殻に閉じこもってしまう。唯一の生きがいとしてコツコツためた金貨をそっくり盗まれてしまった彼の前に、ある大雪の晩、ヨチヨチ歩きの女の子が現れる。マイケルはその子を養女にし、マチルダと名付けて父親としての愛を注ぐことで昔の明るさを取り戻していく。ところが、マチルダの実の父として地元の名士ニューランドが名乗りを上げ、親権を主張してきたのだ。財力に物を言わせて裁判を有利に進めようとするニューランドに対し、血縁を超えた親子の愛情を訴えるマイケル。そして判決の日がやって来た……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES QUATRE CENTS COUPS", "大人は判ってくれない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ヌーヴェル・ヴァーグの旗手トリュフォーの自伝的色彩の濃い長編デビュー作。12歳の少年アントワーヌ・ドワネルは、学校をさぼり、叱られると家出し、金がなくなると盗みをはたらき、ついに感化院に……。多感な少年の心情と行動がみずみずしい映像で描かれる。1989年にはトリュフォー自身の再編集による、初公開時より３分長いオリジナル・バージョンが公開された。DVDは短編「あこがれ」が併録されている。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本女侠伝・激斗ひめゆり岬", "日本女侠伝・激斗ひめゆり岬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "復興途上の沖繩を舞台にした一編。運送会社の女経営者・与那嶺ゆりは、母親の郷里新城部落の悲惨な現状を立て直そうとする。しかし、悪らつな岩松組の妨害にあい仕事ははかどらない。岩松組の客分として迎えられた中上鉄は、岩松組の卑劣なやり口に反発し、ゆりの側につく。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月光の夏", "月光の夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "出撃前の最後の思い出に、ピアノを弾いた特攻隊員の実話を映画化した人間ドラマ。昭和20年初夏。佐賀県にある鳥栖小学校のピアノ係をしていた吉岡公子のところに、二人の特攻隊員が訪れる。彼らは、出撃前にピアノを弾きたいと言い、一人はベートーヴェンの『月光』を、もう一人は『海ゆかば』を歌って去っていった……。映画はさらに、二人のうちの生き残った特攻隊員にスポットを当て、戦争時の抑圧された青春像を描いていく。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PRIMA ANGELI´CA", "従妹アンヘリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "20年前に亡くなった母の遺骨を、共同墓地から家族の墓地に移すことにしたルイス。そのために教会を訪れた彼は、従姉妹のアンヘリカと偶然、再会する。その後、彼女の家に滞在することになり、彼女と過ごした少年時代のことを思い返すのだが……。内戦のなかでの初恋を物語の主軸に、過去と現在をさまよう中年男の姿を描いている。", "1973-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCREAM BLACULA SCREAM", "吸血鬼ブラキュラの復活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "黒人の吸血鬼が登場する「吸血鬼ブラキュラ」の続編。マイアミの大きな屋敷で奇怪な黒魔術を行う黒人に突然、吸血鬼ブラキュラが取り憑いた。ブードゥー教の秘儀を身につけた黒人女性がブラキュラとの闘いに挑む。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("For Keeps?", "この愛に生きて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　ダーシィとスタンは大学進学を目指す高校生カップル。ところがある時、ダーシィの妊娠が発覚。彼らの両親も混乱したが、やがて当のダーシィとスタンは結婚の決意を固める。その後、ダーシィは女の子を出産。しかし生活は厳しく、ふたりの間にはヒビが。そしてダーシィは自分のためにスタンが大学行きを諦めたことを知り……。十代で親になったカップルの愛の行方を描く。80年代青春映画のアイドル、モリー・リングウォルドが母親役に挑戦。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トワイライツ", "トワイライツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "劇団“少年王者舘”を主宰する天野天街が、愛知芸術文化センターと愛知文化情報センターの資金を得て初監督した16ミリ映画。トウヤという変わった名を持つ少年が、“死”の一瞬に垣間見たノスタルジックな幻影を、残酷とも甘美ともつかぬ奇妙な静謐の中に描いている。祭壇のある緋毛氈の部屋、棺桶、白塗りの人々などが次々に登場し、論理を越えた“大胆なイメージの連鎖”が往年の無声映画を思わせる。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ECCO IL FINIMONDO", "世界のはらわた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ニューヨーク無法地帯、バワリーやハーレムで起こる悲惨な日常風景。交通事故の肉体バラバラ凄絶現場記録。食用動物を食べ、倒錯したセックスを繰り返し、無痛分娩をして、人工心臓をはめ込まれ、抹殺されていく人間などなど、現代の狂気を次々に暴いていく。", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マイ・バック・ページ", "マイ・バック・ページ", "", "", "http://image.eiga.k-img.com/images/movie/55513/315x210.jpg?4321", 141, "元・朝日新聞社記者の川本三郎によるノンフィクションを、妻夫木聡、松山ケンイチの若手演技派初共演で映画化した社会派青春ドラマ。1960年代後半の学生運動を舞台に、理想に燃える若手ジャーナリスト・沢田と、革命を目指す学生活動家・梅山との出会い、立場の異なる2人がそれぞれの理想を追い求めて葛藤（かっとう）し、激動する時代を駆け抜けていく姿を描く。監督は「リンダリンダリンダ」「天然コケッコー」の山下敦弘。", "2011-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤胴鈴之助 新月塔の妖鬼", "赤胴鈴之助 新月塔の妖鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "カラー総天然色による一編。鬼首十郎太を首領とする鬼面党は幕府を倒し、天下を我がものにしようとたくらんでいた。幕府は鬼面党捕縛を千葉周作に命じるが、彼は病床の身。急いで鈴之助を呼び寄せ、鈴之助は竜巻雷之進とともに鬼面党と闘う。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TSY タイムスリップヤンキー", "TSY タイムスリップヤンキー", "", "", "http://image.eiga.k-img.com/images/movie/57633/315x210.jpg?1714", 89, "人気お笑いコンビ「ピース」の綾部祐二が初主演を務め、過去にタイムスリップしてしまった高校生が自分の未来を変えるために奔走する姿を描いた青春ムービー。金髪＆腰パンでいまどきの高校生の翔太朗は、ひょんなことから過去へタイムスリップしてしまい、高校生時代のツッパリヤンキーの父親と母親に出会う。この機会に自分の未来を変えようと考えた翔太朗だったが、そこへ翔太朗を追いかけてタイムスリップしてきた彼女の愛里沙が現れて事態はますます混迷し……。", "2012-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宵闇せまれば", "宵闇せまれば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 44, "大島渚のシナリオを得て、鬼才・実相寺昭雄が描く観念的ドラマ。下宿の一室で遊びにあきた女一人と男子学生３人が、部屋にガスを充満させ、誰が最後までガマンできるかを競うゲームを始める。44分の中編だが濃厚な演出で見ごたえのある作品だ。DVDは「青い沼の女十中・短編集」に併録。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXCESSIVE FORCE", "リーサル・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "マーシャルアーツの名手、T・I・グリフィスのダイナミックな立ち回りが冴える刑事アクション。テリーはマフィアのボスを決死の潜入捜査で逮捕したが、不起訴処分に。それどころか、釈放されたマフィアは何者かに殺されてしまった。やがて仲間の刑事たちも次々と殺され、テリーにその容疑が……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子連れ狼・三途の川の乳母車", "子連れ狼・三途の川の乳母車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "大五郎を乗せた箱車を押して流浪の旅を続ける拝一刀は、黒鍬小角によって放たれた別式女（女ボディガードのこと）８人の襲撃を迎え撃つ。前作では目立たなかった大五郎が、ここでは父・拝一刀を助けて大活躍。若山富三郎の殺陣もますます快調。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Naked Kitchen", "キッチン 3人のレシピ", "", "", "http://image.eiga.k-img.com/images/movie/54453/315x210.jpg?3360", 102, "「アンティーク／西洋骨董洋菓子店」のチュ・ジフン主演によるラブストーリー。優しい夫サンインと甘い新婚生活を送るモレは、ある日こっそり忍び込んだ画廊で不思議な青年と出会い関係を持ってしまう。名前も知らない彼との情事を正直にサンインに告白し謝るモレ。しかし、レストランを開店することになったサンインが連れてきたシェフは、あの時の青年ドゥレだった……。共演に「甘い人生」のシン・ミナ、「女は男の未来だ」のキム・テウ。", "2009-10-03", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秀子の応援団長", "秀子の応援団長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "当時、少女スターとして活躍していた高峰秀子が、弱少プロ野球球団の応援歌を作って見事チームを優勝へと導く青春映画。アイドルもののお手本のような明るく楽しい仕上がりで、当時のプロ野球チームが応援出演。戦時体制に呼応しているようで、実はすべての暗い雰囲気を吹き飛ばす健全パワーを持ち得た快作だ。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎 円月殺法", "眠狂四郎 円月殺法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "雷蔵の死により12作に及んだ“眠狂四郎”シリーズは終了したが、その３ヵ月後、早くも松方弘樹主演によって復活した。ベテラン監督・森一生も“狂四郎”シリーズは初めての演出。将軍・家斉の世継ぎ・家慶になりすました双子の弟の、陰謀に巻き込まれる狂四郎の活躍を描く。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("六人の暗殺者", "六人の暗殺者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "島田正吾・辰巳柳太郎以下、新国劇総出演による幕末もの。時は幕末、坂本竜馬と中岡慎太郎が、謎の６人組によって暗殺される。僚友・伊吹武四郎は復讐を誓い、暗殺者を捜し出していく……。戦前からの時代劇のベテラン・滝沢英輔監督が手堅くまとめている。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みづうみ", "みづうみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "それぞれ苦悩を抱える4人の男女の葛藤と再生を描いたダークな人間ドラマ。不倫相手の娘・夕美に自殺を見届けてもらうため、夕美を連れだして湖へとやって来た鞆絵。自殺に失敗して引きかえそうとしたその時、橋から誰かが飛び降りるのを見てしまう。飛び降りた青年を思わず助けてしまった鞆絵は、その青年を車に乗せて街へと向かう。鞆絵と青年がスーパーに寄っている間に夕美が車内で待っていると、突然、見知らぬ女が乗りこんできて車を発進させ……。", "2007-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金語楼の大番頭", "金語楼の大番頭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "喜劇俳優、作家、脚本家としても活躍した落語家、柳家金語楼がガンコだが働き者の大番頭を演じる人情喜劇。柳屋旅館の大番頭、金語楼は先代の遺志を継いで旅館を守っていた。そこへ若旦那の信一が、デパート・ガールの秋子と結婚したいと、親代わりの金語楼に相談に来る。いったんは反対した金語楼だったが……。仲居のお銀に扮した清川虹子の好演が光る。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISS SADIE THOMPSON", "雨に濡れた欲情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "モームの小説『雨』の映画化。南洋にある島にたどり着いた船には、奔放な娘サディと、島から一切の悪を駆逐しようとする社会革命主義者のデビッドソンが乗り合わせていた。サディは島にいた荒くれ軍曹オハラと急速に親しくなっていったが、デビッドソンはオハラの旧悪をサディに暴露し、彼女に更生を強く説得する。やがて彼女は立ち直るが……。DVDは「リタ・ヘイワース フィルム・コレクション」に収録。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EAT MY DUST！", "レーシング・ブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「バックドラフト」の監督R・ハワードが俳優時代に主演したカーチェイス・アクション。レーサーの夢を持つ高校生の少年が、女の子の注目を集めようとしてレース場から車を盗み出す。だが、それがバレて、彼は保安官の父親から追いかけられるハメに陥る。車好きの少年が巻き起こす騒動を描く。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BRAVADOS", "無頼の群", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "留守中に妻を殺して去っていった４人の流れ者を捜す一人の男の復讐譚を、大いなる西部を背景に描いたウエスタン。H・キング監督が、G・ペックを主演に据え、一味違った内省的な西部劇に仕立てあげている。犯人捜しの過程など、サスペンスフルなストーリー展開も見もの。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'uomo delle stelle", "明日を夢見て", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　「ニュー・シネマ・パラダイス」のジュゼッペ・トルナトーレ監督が、映画への愛情と魅力を再びスクリーンに映し出した作品。映画監督を名乗るジョーが、オーディションをするためにシチリアの田舎町にやってくる。カメラを構えた彼の前に浮き足立った人々が集まってくるが、彼は参加費をだまし取る詐欺師だった。ベネチア映画祭審査員特別賞受賞。ジョーを演じたセルジオ・カステリットは2004年、ペネロペ・クルス主演「赤いアモーレ」で初メガホンを取った。", "1995-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COPYCAT", "コピーキャット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "女刑事と犯罪精神分析医の二人の女と、連続殺人犯との対決を描くサイコ・スリラー。犯罪者の精神病理を研究しているヘレンはかつて、精神異常者のダリルから命を狙われ、以来アゴラフォビア（屋外恐怖症）にかかっていた。そんな折、サンフランシスコで実際に起きた殺人事件をコピーした連続殺人事件が発生し、ヘレンは自分を襲った犯人ダリルを疑う。ヘレンは担当のモナハン刑事に電話をし、二人は協力して捜査にあたることになる。しかし、彼女たちにも魔の手が迫ってくる。果たして真犯人は誰か？　ヘレン役のS・ウィーヴァー、モナハン役のH・ハンターの熱演と、衝撃のラストシーンが話題を呼んだ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Coracoes Sujos", "汚れた心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "", "2012-08-31", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTIMACY", "インティマシー／親密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "「王妃マルゴ」「愛する者よ、列車に乗れ」などのP・シェローが、「マイ・ビューティフル・ランドレット」の脚本家としても知られるH・クレイシの小説『ぼくは静かに揺れ動く』を映画化。互いの素性を知らずに肉体だけの関係を続ける男女の心の動きを見つめる愛の物語だ。１年前に離婚したバーテンダーのジェイは水曜日ごとにジェイの家にやってくる女クレアと言葉も交わすことなくセックスを重ねている。体だけの関係と割り切っていたはずだが、ジェイはクレアに惹かれ始め、ある日、彼女を尾行する。そこで見たのは、小さな劇場の舞台に立つ女優としての彼女のもう一つの顔だった。セックスから始まった男女の恋の行方を、大胆な性描写を交えて描く。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Untraceable", "ブラックサイト", "", "", "http://image.eiga.k-img.com/images/movie/53423/315x210.jpg?2374", 100, "ネット犯罪を専門とする女性FBI捜査官ジェニファーは、殺人現場をライブ中継するウェブサイトの存在を知る。しかもそのサイトには、アクセス数が伸びるほど殺害までの時間が早まるという巧妙な仕掛けがあった。やがて、捜査を進めるジェニファーの周囲にも犯人の魔の手が伸び……。正義感に燃える知的でタフなヒロインをダイアン・レインが熱演。監督は「真実の行方」「悪魔を憐れむ歌」のグレゴリー・ホブリット。", "2008-04-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOST　SOULS", "ロスト・ソウルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「プライベート・ライアン」「シンドラーのリスト」でアカデミー最優秀撮影賞を受賞したJ・カミンスキーの長編初監督となるオカルト・ホラー。女優メグ・ライアンが製作を担当している。悪魔祓いの儀式に立ち会ったマヤは、悪魔に憑かれた男が綴った謎の暗号を手に入れる。暗号を解読した彼女は、大いなる悪魔が復活のために宿る人間としてピーター・ケルソンという男を指名したことを知った。独自の調査でピーターの居所をつかんだマヤは彼にすべてを話すが、彼は信じようとはしない。だが、ピーターの周りで不可解な現象が起こり始め、マヤは幻覚に襲われながらも彼を救うため祈りを捧げるが……。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　AWFUL　TRUTH", "新婚道中記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ジェリーとルーシーの夫婦が別居することになり、ジェリーは社交界の花形であるバーバラと婚約してしまう。ルーシーは何とかその婚約を破棄させようとするが……。アカデミー賞で作品賞など６部門にノミネートされ、L・マッケリーが監督賞に輝いた軽妙なラブ・コメディ。DVDは「ケイリー・グラント 生誕100周年記念BOX」に収録。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last Picture Show", "ラスト・ショー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "テキサスの小さな町アナリーンを舞台に、朝鮮戦争のために出征していく若者と、映画館の閉鎖を通して、さびれていく町の姿と並行して失われていく青春の悲しみを描いた哀愁漂う人間ドラマ。良きアメリカやフロンティアの夢を若者に淡々と語るB・ジョンソンの演技は強く心を揺さぶる。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間交差点〈ヒューマンスクランブル〉 道", "人間交差点〈ヒューマンスクランブル〉 道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "全国を渡り歩く鳶の息子で、８歳になる健一は、父に連れられて入った小料理屋で、初めて少女、織江と出会う。以後、数年に一度、雨降る夏の日に出会いと別れを繰り返し、思いを募らせる二人。だが、彼らは悲しい運命で結ばれていた……。磯村一路の手による、情感豊かな悲恋物語。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stealth", "ステルス", "", "", "http://image.eiga.k-img.com/images/movie/1183/315x210.jpg?2227", 120, "人工知能を搭載した最新鋭の無人ステルス戦闘機が暴走し、米海軍エースパイロットのベン、カーラ、ヘンリーの3人が、阻止するために飛び立つ。「ワイルド・スピード」「トリプルX」のロブ・コーエン監督による航空機アクション・エンタテインメント。VFXをデジタル・ドメインが担当し、迫力のエア・バトルを再現。ジョシュ・ルーカス、ジェシカ・ビールに加え、「Ray／レイ」でオスカーを受賞したジェイミー・フォックスが主演する。", "2005-10-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A TASTE OF HONEY", "蜜の味", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「ラブド・ワン」など、イギリスの“怒れる若者たち”の姿を描いて英映画界に新風をもたらしたT・リチャードソン監督が、S・デラニーの小説をもとに綴る苦い青春ストーリー。奇妙な共同生活を続ける母娘、ヘレンとジョー。彼女たちはそれぞれに男を見つけ、別々の人生を歩み出そうとするが、再び奇妙な共同生活へと戻っていく……。", "1961-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD MATCH", "ブラッドマッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "一人の青年が、殺された兄から奪われたチャンピオンベルトを取り戻すため、４人のファイターと肉弾戦を繰り広げる、格闘アクション。全米プロ空手（マーシャルアーツ）のキングともいわれるベニー・ユキーデも出演し、自らアクション・シーンの演出も手掛けている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナイチンゲーロ", "ナイチンゲーロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "グラビアアイドルを中心に女性のみが出演するホラーシリーズ「おんなだらけのこわいはなし」の第2弾。夏休みに海辺の宿舎へとやって来た看護学校の女生徒5人組。仲良くはしゃぐ彼女たちだったが、実はそれぞれが他のメンバーには言えない秘密を抱えていた。やがて1人がクラゲに刺されたのをきっかけに、5人は想像を絶する恐怖に巻きこまれていく。監督は、鈴木清順らのもとで助監督を務めてきた新人・末永賢。", "2006-03-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NINE MONTHS", "９か月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「ホーム・アローン」「ミセス・ダウト」のヒットメーカー、C・コロンバス監督が描くロマンティック・コメディ。フランス映画「愛するための第９章」のリメイクでもある。子供専門の精神科医サミュエルと、バレエ・インストラクターのレベッカは５年越しの恋人。仕事に恋愛に順風満帆の日々を送り幸せの絶頂にいたサミュエルだが、ある日レベッカから妊娠したことを告げられる。職業柄、子供嫌いの彼はパニックに陥るが、レベッカの母になる決心は固く、次第に彼女のペースに巻き込まれていく。「フォー・ウェディング」で全米の観客を魅了したH・グラントのハリウッド進出作第１作。ロシアからやって来た産婦人科医役のR・ウィリアムズが笑わせる。C・ランバートが製作総指揮に名を連ねている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鍵泥棒のメソッド", "鍵泥棒のメソッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AGNES OF GOD", "アグネス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ブロードウェイでトニー賞を獲得したヒット戯曲の映画化。男子禁制の修道院で発見された赤ん坊の絞殺死体。容疑者とされた美しい尼僧には、妊娠、出産の記憶がまるでない。彼女の精神鑑定に訪れた女医の理性の前に、宗教と信仰という神秘の世界が立ちはだかる。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I，DOLL", "I，DOLL", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "1959年にアメリカに登場して以来、多くの人々に愛され続ける“バービー人形”人気の周辺事情を追ったドキュメンタリー。時代とともにファッションや容姿を新たにするバービー人形の歴史をたどりつつ、関係者やコレクター、バービーに憧れるあまり拒食症となったファンらの証言を絡め、社会に与えた様々な影響を浮き彫りにする。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cloudy with A Chance of Meatballs", "くもりときどきミートボール", "", "", "http://image.eiga.k-img.com/images/movie/54003/315x210.jpg?4046", 81, "偉大な発明家になることを夢見るフリント・ロックウッドだったが、風変わりな彼の発明は家族や町中の人に迷惑をかけていた。ある日、水を食べ物に変えることができる「食べ物マシーン」を発明したフリントだったが、不慮の事故でマシーンが空へ飛んでいってしまい、それから豪雨とともに大量のチーズバーガーが降ってくる。町の人々は喜んだが、日に日に降ってくる食べ物が巨大化していき……。ジュディ・バレットによるべストセラー児童書を映画化した3Dアニメーション。", "2009-09-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("疑惑", "疑惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "死者に３億円の保険金がかけられていたという実際の事件にヒントを得た、松本清張の同名小説の映画化。野村監督作品の傑作の一つ。清張自身が脚色を施し、容疑者も弁護士も女性に変えて、女同士の感情の葛藤という要素を付加している。桃井かおりと新聞記者役の柄本明が好演。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE´DE´E D’ANVERS", "デデという娼婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "情夫マルコにひきずられ、フランスからベルギーのアントワープの酒場に流れてきた娼婦デデは、ここでフランチェスコという貨物船の船長と知りあう。彼は武器の密輸をしていたが、マルコを嫌うデデに同情し、二人の仲は急速に発展する。マルコはこれを知りフランチェスコを殺すが、デデに復讐される。「狂気の孤独」のY・アレグレ監督の日本初登場作品。", "1947-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SPANISH MAIN", "海賊バラクーダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "RKO映画初のカラー作品。17世紀中頃のカリブ海を舞台に、暴君的な太守に支配されたスペインの圧政に反抗して、オランダの船長から一転、海賊バラクーダとなった男の復讐と冒険の物語。「カサブランカ」でバーグマンの夫を演じたP・ヘンリードが主役を好演。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホタル", "ホタル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "東映創立50周年記念作品として製作された人間ドラマ。「鉄道員〈ぽっぽや〉」の監督＆主演コンビが放つ感動作。激動の時代をくぐり抜け、今は港町で静かな生活を送る山岡と妻。そんな山岡の元に、藤枝という男の訃報が入る。藤枝は、山岡とともに特攻隊の生き残りだった。この報を受けて、山岡はある女性から韓国へ行ってほしいと頼まれる。彼女は特攻機で空に散っていった男たちを見守り続け“知覧の母”と呼ばれた山本富子だ。山岡は、戦死したかつての友の故郷、韓国を訪れることになるが……。戦争により心に深い傷を負った夫婦の出会いと運命を、おだやかなまなざしで紡ぎあげていく。高倉健の“語り継がなければ忘れ去られることも、映画なら残していける”という言葉から生まれたこの作品には、新しい時代を生きる人々への痛切なメッセージが込められている。また、高倉主演作には欠かせない名カメラマン、木村大作による映像美も忘れがたい。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Water", "Water", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 28, "芥川賞作家・吉田修一が1996年に手がけた同名処女作を自ら脚色し、監督デビューを果たした青春ドラマ。長崎の高校で水泳部に所属している幼なじみの凌雲と圭一郎。高校生活最後の県大会に向けて練習に励む彼らの間に、微妙な空気が流れはじめる。進学のために上京する圭一郎と、実家の酒屋を継ぐと決めた凌雲。今までずっと近くにいた圭一郎が離れて行くことに気持ちがざわつく凌雲は、圭一郎に対して大胆な行動に出る。", "2007-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mercenary for Justice", "沈黙の傭兵", "", "", "http://image.eiga.k-img.com/images/movie/33753/315x210.jpg?4739", 96, "スティーブン・セガール主演の“沈黙シリーズ”最新作。アフリカでの任務中、CIAの裏切りによって親友を亡くした腕利きの傭兵シーガーは、人質に取られた親友の家族の命と引き換えに新たなミッションを命じられる。それは、脱獄不可能とされる南アフリカの刑務所から、4日以内に大物武器商人の息子を脱獄させるというものだった……。メガホンを取るのは「沈黙の脱獄」も手掛けたドン・E・ファンルロイ監督。", "2006-11-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・ショックス 世界の目撃者", "ザ・ショックス 世界の目撃者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "スペース・シャトル“チャレンジャー”の爆発や、リオ・デ・ジャネイロの高層ビル火災、コロンビア火山の大噴火など、世界中からショッキングな実写フィルムを集め、編集したドキュメンタリー。衝撃シーンだけにとどまらず、ユーモアやファッションにも目を向けている。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はしれ！わくわくアンパンマングランプリ", "はしれ！わくわくアンパンマングランプリ", "", "", "http://image.eiga.k-img.com/images/movie/56883/315x210.jpg?1540", 20, "やなせたかし原作の人気アニメーション劇場版第22作「それいけ！アンパンマン　ブラックノーズと魔法の歌」と同時上映の短編。", "2010-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VOYAGE", "デスクルーズ 欲望の嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "地中海を舞台にした、サスペンス・スリラー。建築家のモーガンは、高校の同窓会で再会した歯科医のギルと意気投合。互いに夫婦で、地中海でのヨット・クルージングを楽しむことにした。ところが、ギル夫妻の態度が海上で豹変。彼らは、自分たちの殺人罪を、モーガン夫婦に身代わりさせようとしていたのだ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　京鹿子娘二人道成寺", "シネマ歌舞伎　京鹿子娘二人道成寺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "歌舞伎の舞台公演をスクリーンでデジタル上映する「シネマ歌舞伎」シリーズ第3弾。2006年2月に歌舞伎座で上演された坂東玉三郎、尾上菊之助による名舞台「京鹿子娘二人道成寺」をデジタルシネマ化。紀伊国の道成寺では、新しい釣鐘の供養が行なわれている。恋人・安珍への激しい思いから大蛇となりはてた清姫により、安珍をかくまった道成寺の釣鐘は焼き払われてしまったのだ。そこへ美しい白拍子花子が現われ、舞を踊ることを条件に供養への参列を許される。しかし、彼女の正体は清姫の怨霊だった。", "2007-01-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST BULLET", "最後の弾丸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "日本・オーストラリアの合作映画であるにもかかわらず、日本では未公開の作品。1945年７月、ボルネオのジャングルでは、オーストラリア軍に攻めたてられた日本軍が捨て身の攻撃を仕掛けていた。激しい戦闘が繰り広げられ、生き残ったのは、新米のオーストラリア兵スタンレーと狙撃の名手の日本兵、山村だけだった。生き残るには相手を殺すしかない。命を懸けた二人の戦いが始まる。", "1995-01-01", "76,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("20世紀ノスタルジア", "20世紀ノスタルジア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "人気アイドル、広末涼子の記念すべき映画デビュー作。「初国知所之天皇」「百代の過客」などの自主映画作品で知られる原將人の劇映画デビュー作でもある。ある日、自らを宇宙人と名乗る少年、片岡徹に出会った高校２年生の遠山杏。地球が滅びるまでをビデオに収めるという彼の“地球観察レポート”に戸惑いながらも、彼女はその見事なカメラワークに惹かれ、行動をともにする。高層ビル、高速道路、東京タワーなど、あらゆる情景をビデオに収めて夏を過ごしていった二人は、やがて“映画”の結末で意見を分かつ。そして徹は未編集テープを残し、父親のいるオーストラリアへ旅立ってしまう……。広末が歌声を披露するミュージカル・シーンをはじめ、映画愛をさわやかにうたい上げるユニークな青春映画。杏の父親役で、映画監督の根岸吉太郎が顔を見せている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN OF THE MUSKETEERS", "新・三銃士 華麗なる勇者の冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「三銃士」(1973)、「四銃士」(1975)に続いて、その20年後を描いたのがこの作品。監督は前２作と同じR・レスター。母国フランスの危機に、大活躍した四銃士（前作と同じキャスト）に加え、今回は三銃士の一人アトスの息子ラウルが新たに登場する。テンポのよいアクションに、ラウルの淡くはかない恋も絡めた巨編。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA SOLITUDE DU CHANTEUR DE FOND", "本質的な歌手の孤独", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "1973年９月に起きた南米チリのクーデターにより亡命させられた人たちのために、Y・モンタンが1974年に開いたリサイタルの稽古の様子を収めたドキュメンタリー。ナレーションなどはなく、モンタンの表情を追い続け、モンタージュとクローズアップの連続により、モンタンの内面的な情熱とその孤独な姿を表していく。1996年に初公開された。", "1974-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAN　YU", "藍宇／情熱の嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "民主化の動きが活性化し、天安門事件に揺れる1980年代後半の北京を舞台に、深く愛し合う男たちの純愛を綴る。原作はネット上において匿名で発表され、中国大陸での最新ゲイ文化として広まった話題作。自らもカミングアウトしている「異邦人たち」のS・クワン監督が、繊細な男たちの心情を美しく切なく描き出している。1988年、北京。バイセクシャルの青年実業家、捍東は、貧乏学生、藍宇を金で買う。一夜限りのはずが純粋に愛し合い、一緒に暮らすようになる。1989年６月４日、些細な喧嘩で家を出た藍宇は、天安門広場に向かう……。 ビデオ、DVDタイトルは「情熱の嵐／LAN YU」。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KRULL", "銀河伝説クルール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "双子の太陽が輝く惑星クルール。魔王ビーストはこの不思議な惑星を手に入れるべく、リサ姫を誘拐。姫の婚約者コルウィン王子は怒りに燃えて立ち上がった……。勇猛果敢な王子を新星K・マーシャルが熱演。アペニン山脈などヨーロッパ・ロケが光るSF活劇。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BASKET CASE3", "バスケットケース3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「バスケットケース２」に続き、ヘネンロッターがまたしても描く、フリークスたちの悲劇。フリークスに理解を示す医者のルースは、彼らのプライバシーを守るため、ドゥエインとベリアルらのフリークス一団をバスに乗せ、南部の町へ旅立つ。そこで12人のベビー・ベリアルをバスケットに生み落とすのだが、心ない人間たちが彼らを奪い取ってしまった。。DVDは「バスケット・ケースBOX」に収録。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIDEON'S DAY", "ジョン・フォード ギデオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "J・フォードがロンドン警視庁のギデオン警部のある一日を描いた作品。地味ながら緻密な演技力で様々な人間模様を浮き彫りにした佳作だ。強盗事件、殺人犯がロンドンに潜入したという情報、麻薬汚職の同僚の事故死……。一見平穏に見えるロンドンの街もギデオン警部にとっては犯罪の連続だった。", "1959-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽が弾ける日", "太陽が弾ける日", "", "", "http://image.eiga.k-img.com/images/movie/34313/315x210.jpg?1690", 118, "Vシネマからコメディ映画まで幅広い活躍を見せる小沢仁志の俳優25周年記念作品として、オール・フィリピン・ロケで製作されたアクション映画。フィリピンのスラム街で兄妹のように育った日系人トニー、マックス、レイ。大人になってもドン底の生活から抜け出せずにいたトニーは、ボスであるアイクを殺して麻薬密売の権利を手に入れる。暴力の限りを尽くして闇社会でのし上がっていくトニー。彼の行動はやがてマックスとレイをも巻き込んでいき……。", "2007-11-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A LETTER TO THREE WIVES", "三人の妻への手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "“あなたたちのご主人のうちの一人と駆け落ちします”。こんな手紙が町いちばんの美女から３人の妻たちに届いた。心あたりがある３人の妻たちの回想を通して、夫より稼ぎが多く立場が逆転している妻、金目当てで結婚したものの満たされない生活を送る妻の横行する、1940年代アメリカを皮肉ったコメディ。巨匠マンキーウィッツ監督らしいウイットとアイロニーに富んだ台詞まわし、回想シーンと現実がテンポよく交錯する演出は洗練されており、アカデミー監督賞、脚本賞受賞はうなずける。また、手紙の主アディ役のセレステ・ホルムが最後まで姿を見せず、狂言まわし的役目を果たすのもシャレた趣向だ。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私は二歳", "私は二歳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "松田道雄の育児評論の映画化。２歳の赤ちゃんが主役という、映画史的にも珍しい作品ができあがった。その赤ちゃんは3200人のなかから選ばれた鈴木博雄ちゃん。団地に住む若い夫婦に待望の男の子が生まれた。両親はもとよりお婆さんまでも大騒ぎ、といった日常を淡々と綴った作品だが、なによりユニークなのは赤ちゃんの独白の形でナレーションが入ること。赤ん坊は果して両親の愛情をどう見ているのか、という視点が面白い。題材が意表を突いた上に、一種の社会批評の観点も兼ね備えていて、キネマ旬報ベストワンに輝いた。ちなみに市川監督は同４位に「破戒」も入選するなど大活躍の1962年だった。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUES HARP", "BLUES HARP", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "三池崇史監督が持ち前の威勢のいい演出で、男たちの活気あふれる友情劇を見せ切った一本。ブルース・ハープの演奏を認められメジャー・デビューのきっかけをつかんだ青年の行方と、野心的なヤクザの皮肉な凋落を、エモーション豊かに描き出す。情感ある映像と、テンポ重視の編集。そこに寡黙なたたずまいに色気を感じさせる池内博之、ホモセクシャルな感情を硬質な表情の中に滲ませた田辺誠一の妙演が加わり、見ごたえのある青春映画に仕上がっている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Peace ピース", "Peace ピース", "", "", "http://image.eiga.k-img.com/images/movie/56213/315x210.jpg?3899", 75, "「選挙」（2007）、「精神」（08）が各国映画祭で話題となった想田和弘監督が、岡山県に暮らす人々やネコの何気ない日常を追ったドキュメンタリー。想田監督の妻の実家・柏木家に住み着いた野良ネコのグループと、そこに突然現れた“泥棒ネコ”との確執、死について考えるうちに兵隊としての記憶がよみがえる91歳の独居老人・橋本や、橋本をボランティア同然に介護・支援しながらも、自分たちにも迫る老いを感じる柏木夫妻らの姿をありのままにとらえ、生きることの難しさと可能性に思いをめぐらせる。", "2011-07-16", "76,1,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二人の恋人", "二人の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "井手俊郎のオリジナル脚本を森谷司郎が演出した東宝青春路線の一編。秀才の兄と家出癖のある弟が、今は亡き兄の婚約者にそっくりな少女と出会い恋の火花を散らす。死んだ婚約者と“二人の恋人”の二役を酒井和歌子が好演、端正ですがすがしい印象を残している。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAST MAN STANDING", "刑事ベルモア 共謀者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ロサンゼルスの刑事ベルモアは、銀行を襲ったギャング団を逮捕する。ボスの名はスネーク。しかしなぜかスネークはあっという間に釈放され、押収した大金も消えていた。同僚のシーグローブを疑うベルモアは、独自に調査を開始する。そんな折、シーグローブは別の事件でベルモアの相棒ドクを射殺し、スネークと共謀してベルモアに濡れ衣を着せる。怒りに燃えるベルモアの孤独な戦いが始まる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONEGIN", "オネーギンの恋文", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "享楽的な貴族オネーギンは、彼を愛する田舎娘タチヤーナの思いを困惑しつつも拒絶してしまうが、６年後、二人は思わぬ再会を果たす……。ロシアの文豪プーシキンの自伝的小説を、R・ファインズが自らの製作総指揮・主演で映画化。監督のマーサは、彼の実妹にあたる。耽美的な映像と、L・タイラーの正統的美しさが際立つ作品。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Belle Toujours", "夜顔", "", "", "http://image.eiga.k-img.com/images/movie/53143/315x210.jpg?4822", 70, "ポルトガルの巨匠マノエル・ド・オリベイラが、67年のルイス・ブニュエル監督作「昼顔」の登場人物たちの38年後を描いた作品。パリで偶然再会したアンリとかつての友人の妻セブリーヌ。逃げようとするセブリーヌに何とか近づいたアンリは、過去の真実を打ち明けるという口実で食事の約束を取りつけるが……。ミシェル・ピコリが再びアンリ役で登場する一方、前作でカトリーヌ・ドヌーブが演じたセブリーヌには「アイドルたち」のビュル・オジエが扮する。", "2007-12-15", "25,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さびしんぼう", "さびしんぼう", "", "", "http://ecx.images-amazon.com/images/I/514ED9EKXSL._SL210_.jpg", 112, "「転校生」「時をかける少女」に続き、大林宣彦の故郷・広島県尾道を舞台に、少年と少女の初恋物語が繰り広げられる。ヒロキはひそかに恋心を抱いている美少女に“さびしんぼう”という名をつけて、いつも遠くから眺めていた。そんなヒロキの前に、突然現れた道化姿の女の子。彼女も自分のことを“さびしんぼう”と名乗る。ヒロキは煙たがったが、道化の“さびしんぼう”は度々現れて、騒動を起こすのだった。そんな折、ついにあの美少女と話をする機会がやってくる……。大林映画のなかでも特に思春期の感傷に彩られた作品。二人の“さびしんぼう”と別れるシーンは、それぞれロマンティックで感動的だ。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜霧のブルース", "夜霧のブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "菊田一夫の原作を石原裕次郎と浅丘ルリ子主演、野村孝の監督で映画化したムード・アクションの代表作。横浜港、船の荷役を牛耳る野上組は商売仇の岡部組を叩きつぶして得意の絶頂にあった。そんな時、西脇と名乗る男が野上に会見を申し込んでくる。殺気立つ子分たちの前で西脇は話を始める……。主人公の長い回想のあとにクライマックスがくるという大胆な構成のなかに、主役の男女のひたむきな愛を盛り込んだ一編。叙情派・野村孝の演出が冴えわたり、日活アクション史上に残る秀作に仕上がった。菊田一夫の原作は、1947年に松竹で大曽根辰夫監督、水島道太郎と月丘夢路主演により、「地獄の顔」という題でも映画化されている。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MUJER DE BENJAMIN", "ベンハミンの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "恋した少女をさらい家に閉じ込めてしまった中年男と、その少女とのギクシャクした恋愛をユーモラスに描く。舞台はメキシコの田舎町。少し頭の足りない中年男ベンハミンは、ある日、姉ミカエラに無理やり連れていかれた教会で、美少女ナティビダに出会う。彼女に恋したベンハミンは仲間たちにあおられ、ナティビダをさらって家に閉じ込めてしまう。厳格な母親の元に帰りたくない彼女はベンハミンの家に留まり、彼の気持ちをうまく操って家の中で実権を握るが……。1991年モントリオール国際映画祭新人監督賞を受賞したC・カレラ監督の弱冠28歳の時のデビュー作。", "1991-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLEOPATRA", "クレオパトラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 243, "天下の美女クレオパトラの伝説の映画化。紀元前48年、内乱の続くエジプトに侵攻したローマ帝国の闘将シーザーは、エジプトの女王クレオパトラの美しさに恋の虜となる。そしてアントニーもまた……。巨額の製作費を投じた豪華絢爛たるスペクタクル超大作。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1952 IVAN AND ALEXANDRA", "ぼくと彼女のために", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "1952年のブルガリア。ピオニール（共産主義的人間育成組織）に属していたイワンは、同級生のアレクサンドラに夢中になる。だが彼女はエリート階級の娘で、イワンには目もくれない。ある日、アレクサンドラの父が失脚、政府に逮捕され、彼女は“裏切り者の娘”としてピオニールから除名される。スターリニズム時代のブルガリア社会の姿を、主人公の少年少女を通してユニークに描いた作品。", "1988-01-01", "65", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA RAGAZZA CON LA VALIGIA", "鞄を持った女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "最愛の夫を失い、その後に本気で愛した男マルチェロにも捨てられたアイーダ。そんな失意の彼女に献身的に尽くしたのは、マルチェロの弟ロレンツォだった。だが、若い彼の将来を思い身を引くアイーダ……。ほろ苦く、切ない大人の愛を描いたドラマ。", "1961-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銭形平次捕物控・八人の花嫁", "銭形平次捕物控・八人の花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "初のシネスコ版。江戸、洲崎五万坪の埋立地に、八竜王の杜が建立されることになり、その落慶式に８人の小町娘が選ばれるが、娘たちが相次いで殺される。平次はガラッ八を引き連れ捜査に乗り出す。榎本健一がガラッ八に扮した。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏に生れる", "夏に生れる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "ドキュメンタリーにはじまり、そこにフィクションが交錯していく異色作。兄嫁が出産しそうなことを知った映像作家の村上賢司は、その模様を撮影しようとするが、なかなか子供は誕生しない。やがて賢司はヒマつぶしに入ったテレクラに熱中し、アポをとった女の子と会うのだが……。デジタルカメラによる撮影は実験映画風だが、監督自身の情けない姿が笑いを誘う、なんともユーモラスな作品となっている。ゆうばり国際冒険・ファンタスティック映画祭1999オフシアター部門でグランプリを受賞。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スペーストラベラーズ", "スペーストラベラーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "「踊る大捜査線　THE MOVIE」の本広克行が、お笑い集団ジョビジョバの舞台『ジョビジョバ大ピンチ』をもとに完成させた密室劇。閉店間際の銀行に押し入った強盗３人組と、人質になった銀行員や客たちが次第に奇妙な絆を形成していく様を、笑いと人情たっぷりに描き出していく。個性豊かな登場人物たちの結束をタイトルにあるようなアニメ世界に結びつけながらも、最後にはオーソドックスな銀行強盗もののセオリーに着地させ、観客の涙を誘う展開になっている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒蜥蜴", "黒蜥蜴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "怪盗・黒蜥蜴と明智小五郎との戦いを描く、三島由紀夫の戯曲の２度目の映画化。丸山（現・美輪）明宏のために三島が書き改めたという劇だけに、丸山の女装をはじめ、彼女（？）の魅力が十二分に発揮されている作品。名探偵はサシミのツマという感じ。三島自身が特別出演している。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LONGEST DAY", "史上最大の作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 178, "第二次世界大戦のハイライト、ノルマンディー上陸作戦の全貌をパノラマ形式の壮大なスケールで描いた戦争大作。作戦はベルレーヌの詩の一節をもって開始され、決戦の火ぶたが切って落とされた。製作者ザナックが４人の監督を使い当時で36億円の巨費をかけて作り上げたオールスター・キャスト映画。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASSAULT ON A QUEEN", "クイーン・メリー号襲撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "金塊とともに沈んだ船を探す元潜水艦高官のマークと５人の仲間たち。だが彼らは金塊を積んだ船ではなく、ドイツ軍の潜水艦を使って、20世紀の海賊になることを思いつくが……。アメリカ、イギリス、イタリアの国際スターを集めた、スリリングな異色海洋アクション。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Long Day's Journey Into Night", "夜への長い旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 174, "　1912年、コネチカット。かつてシェイクスピア俳優だったジェームズもこの近年は金のために商業演劇で同じ役を繰り返していた。彼の妻メリーが療養所から戻るが、引っ込み思案の彼女はひとりで過ごすことが多く、夫を苛立たせていく。息子で長男のジェイミーは父のあとを継ごうとするが、酒に溺れてしまう。結核に冒された弟エドモンドは家族への複雑な気持ちを抱えていた……。ユージン・オニールの舞台劇を映画化。心に秘密を抱える一家の愛憎を描く。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("39 刑法第三十九条", "39 刑法第三十九条", "", "", "http://ecx.images-amazon.com/images/I/4125YXYZAPL._SL210_.jpg", 133, "都内で起きた夫婦惨殺事件。逮捕された劇団員の若者は、あっさり犯行を認めたが殺意を否定。やがて裁判が始まり、おとなしいはずの若者の人格は一変し、奇怪な言動を連発し始める。「心身喪失者の行為はこれを罰しない。心身耗弱者の行為はその刑を減刑する」と記された刑法第三十九条をモチーフにしたサイコ・サスペンス。", "1999-05-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道記者", "極道記者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「スキンレス・ナイト」で注目された望月六郎が描く、渋めの人間ドラマ。ノミ屋をハメる計画に乗ったギャンブルに目がない競馬記者の、ヤクザ相手の大勝負をドラマティックに描く。主演の奥田瑛二が、「ありふれた愛に関する調書」に続いて泥くさい魅力を発揮。手に汗握るギャンブル・シーンも見ものだ。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かあちゃん結婚しろよ", "かあちゃん結婚しろよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "港町で飲み屋を営んでいるみつよは、中学１年の息子と二人暮らし。かねてから心惹かれていた純朴な漁師・武との縁談が持ちあがり、息子もそれを喜ぶが、そんな時、別れた夫が復縁を迫って現れた……。五所平之助得意の庶民人情ドラマで、豪華なキャストで撮られた心にしみる一編。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A TALE OF TWO CITIES", "二都物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "ディケンズ原作の映画化。革命期のパリとロンドンを舞台に繰り広げられる、男女の愛と友情の物語。美貌の女性ルシーをめぐる、弁護士のシドニーとフランスの亡命貴族チャールズの恋のさや当てが、革命の動乱の中に劇的に描かれた佳作。", "1957-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISTER FROST", "ミスターフロスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "J・ゴールドブラムが、現代によみがえった悪魔に扮した、サスペンス・ホラー。24人もの人間を殺して捕まった、殺人鬼フロスト。彼は、二年間、精神病院をたらい回しにされたが、その素姓は謎のままだった。ある日、St.クレア病院に送り込まれたフロストは、女医サラに、自分の正体を語り始める……。", "1990-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’UNE CHANTE， L’AUTRE PAS", "歌う女・歌わない女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1962年冬のパリで出会った歌手志望のポムと未婚の母のシュザンヌ。それから10年、歌う女と歌わない女の二人の人生は、仕事、育児、恋、結婚、社会活動……と過ぎていく。女性監督ヴァルダが女性の新しい生き方を優しく見つめた女性のための物語。", "1976-01-01", "2,37,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Bloody Valentine 3D", "ブラッディ・バレンタイン 3D", "", "", "http://image.eiga.k-img.com/images/movie/54143/315x210.jpg?4514", 95, "1979年のカナダ産ホラー「血のバレンタイン」をデジタル3D映像でリメイク。アメリカ郊外の町ハーモニーで炭鉱夫として働くトムは、5人の同僚を炭鉱に閉じ込める大事故を起こし町を離れる。それから10年後、トムはバレンタインの日に再び町を訪れるが、その晩、炭鉱夫のマスクをつけてつるはしで武装した殺人鬼が町に現われ……。主人公トム役に人気TVシリーズ「スーパーナチュラル」のジェンセン・アクレス。", "2009-02-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE BITTEREN TRA¨NEN DER PETRA VON KANT", "ペトラ・フォン・カントの苦い涙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ニュージャーマン・シネマをヴェンダースとともに代表する、R・W・ファスビンダーが自作の舞台劇を映画化した壮絶な密室劇。二度の離婚歴のあるファッション・デザイナー、ペトラ・フォン・カントは秘書とともに異様な装飾のアパートに住んでいた。そこにかつて外国で知り合ったカーリンという女がやって来る。ペトラはカーリンに惹かれていくのだが……。ペトラ役はファスビンダー舞台劇の常連、M・カルステンセン。", "1972-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SoundtracK", "SoundtracK", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "幼いころ両親を目の前で殺された兄の志音と妹の美砂。やがて時は経ち、美砂はヴァイオリニストになるが交通事故で死亡。さらに数年後、志音の前に美砂そっくりの女ミサが現れる……。人気アーティスト、SUGIZO主演のスピリチュアル・ファンタジー。ドラマを語るというより、幽玄的ヴィジュアルと音楽でドラマを感じさせる手法が取られている。二役を演じる柴咲コウが好演。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シャイなあんちくしょう", "シャイなあんちくしょう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "議員秘書だった父親が汚職の罪を被り、失意のうちに渡米した主人公が、４年後、モトクロス・レーサーとなって帰国。そこで父の突然の死に遭い、再び事件に巻き込まれてしまった彼は復讐を決意する……。「稲村ジェーン」で主演デビューを飾った加勢大周が、よりハードな魅力を見せる。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恐喝こそわが人生", "恐喝こそわが人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "深作欣二が松竹へ出張して監督したチンピラ映画。新宿のチンピラが、ふとしたきっかけから恐喝屋としてのし上がっていくが、図にのって政財界のボスを恐喝し、殺し屋に殺される。この頃低迷していた松方弘樹が、イキのいいチンピラを好演して注目された。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("White", "ホワイト", "", "", "http://image.eiga.k-img.com/images/movie/56853/315x210.jpg?3182", 106, "2011年9月に日本デビューの人気K-POPガールズグループ「T-ARA」のウンジョンの初主演作。売れないガールズグループ「ピンク・ドールズ」のリーダー、ウンジュは、偶然見つけた過去のアイドルの歌を「ホワイト」というタイトルでリメイクして売り出したところ大ヒット。グループは一躍人気者になるが、センターポジションに立ちメインボーカルを務めたメンバーが次々と悲惨な事故に巻き込まれていく。ウンジュは事故の原因が「ホワイト」にあるのではと疑い始めるが……。「2PM」「AFTERSCHOOL」など人気K-POPグループも出演。", "2011-09-17", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("萌の朱雀", "萌の朱雀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1997年度カンヌ映画祭の監督週間に出品され、新人監督賞に当たるカメラ・ドール賞を獲得。それまで「につつまれて」「かたつもり」といった自主映画で高い評価を得てきた河瀬直美が、独特の映像センスを見事に開花させ、輝かしい劇場用映画デビューを飾った。舞台は奈良県南部の山間部。そのさびれた土地には、鉄道誘致に全力を尽くしたものの報われず、うつろな毎日を送っていた中年男性がいた。心の隙間が埋められず、彼はある日失踪。残された家族の姿、そして来るべきそれぞれの選択が描かれる。出演者は、父親役の國村隼以外はほとんどが素人。セリフも極力排した物語が、静かに、やがて豊潤に、家族の絆の温もりや別れの哀惜をうたいあげる。劇中で紹介される父親が撮ったという８ミリ映像は、河瀬自身が撮影したもの。その部分だけでも河瀬の類いまれな感受性が発見できるだろう。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gangland", "ギャングランド　カポネが最も恐れた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1920年代末期のアメリカで暗黒街を牛耳っていたドン、アル・カポネの右腕として活躍した拳銃の名手バーン・ミラーの生きざまを描いた作品。冷酷非情なミラーは、法も、人も、そしてカポネさえも恐れなかった……。S・グレンの渋い演技が光る。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("How to Be", "真夜中のギタリスト", "", "", "http://ecx.images-amazon.com/images/I/51i7vUaOMkL._SL210_.jpg", 85, "", "2008-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不撓不屈", "不撓不屈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "1人の税理士と国税庁による7年間にも及ぶ闘争「飯塚事件」を題材にした高杉良の同名ベストセラーを、「次郎物語」の森川時久監督が映画化した社会派ドラマ。会計事務所を営む税理士・飯塚毅は、顧客である中小企業の経営者たちに勧めていた節税対策を国税局に否定され、脱税指導の嫌疑をかけられてしまう。不撓（ふとう）不屈の精神と家族に支えられて、真っ向から権力に闘いを挑む飯塚だったが……。", "2006-06-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUDDY　BOY", "バディ・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ヒッチコック作品を彷彿させるサイコ・サスペンス。母と二人暮らしのフランシスは、向かいに住む女性グロリアの部屋の覗きを楽しんでいる。ある日、ひょんなことから親しくなり、二人は恋仲に。しかし、部屋から覗く彼女は愛の言葉とは裏腹に奔放に男と寝て、またベジタリアンでありながら血のしたたるような肉を食べていた。ショックを受けるフランシス。だが、そんな彼にも人に言えない秘密が……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAS，FOOD，LODGING", "ガス・フード・ロジング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ニュー・メキシコ州の砂漠にある小さな町に暮らす、母親と二人の娘の物語。数年前に夫に逃げられウエイトレスをしながら娘を育てているノラ。初体験が輪姦だったことを隠して、男に身をまかせている17歳のトルーディ。親友に片思い中の15歳のシェード。トルーディの前に、初めて優しく扱ってくれるダンクが現れ、恋に落ちる。シェードにも心を許し合える男が現れるのだが……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POJDTE，PANE，BUDEME　SI　HRAT！", "ぼくらと遊ぼう！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "チェコのアニメーション作家ブジェチスラフ・ポヤルによる短編アニメーション。いつも強気の大きなクマと、ちょっとシャイな小さなクマが遭遇する出来事をほのぼのと描いていく。セミ・パペットアニメーションと呼ばれる技法を駆使し画面全体に不思議な質感が現れる。1965年から1973年にかけ、シリーズで計11本が制作された。", "1965-01-01", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The New World", "ニュー・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/1563/315x210.jpg?3617", 136, "アメリカで最も有名な伝説といわれる英国人ジョン・スミスとネイティブ・アメリカンの王の娘ポカホンタスのラブストーリーを、「天国の日々」「シン・レッド・ライン」など哲学的な内容と荘厳な映像美で知られるテレンス・マリック監督が、彼らの愛と魂の彷徨を描いたドラマとして再構成。05年度アカデミー賞ノミネートを受けるなど、各方面で絶賛された撮影は「アリ」「スリーピー・ホロウ」のエマニュエル・ルベツキが担当。", "2006-04-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Сказка о царе Салтан", "サルタン王物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "プーシキンの叙事詩をベースに、ソ連の名匠プトゥシコが撮り上げたファンタスティックなドラマ。美しいアニメのような、イマジネーション豊かな映像美に酔える秀作だ。冒頭の3姉妹の夢の話からイメージは広がり、サルタン王の世界へ。反逆者扱いを受けた王妃と王子は王から追放され、ある島に着いて白鳥を救った。すると、白鳥の王女は二人を不思議な魔法の国へと連れていく。", "1966-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火まつり", "火まつり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "柳町光男が、作家・中上健次の脚本を得て、自然と人間、そして神が織りなす日本の土着的な神話の世界と現実を融合させた野心作。北大路が演じる達男は、熊野で木こりを生業とする男で、自らを山の女神の恋人と称するように、自然の呼吸と一つになった人間である。彼には、妻と二人の子供がいるが、かつての女・基視子が東京から流れてきて再び関係を結ぶ。しかし、基視子は土地の男を騙して東京へ逃げ、達男は火まつりの夜に一家を猟銃で殺し、自らも死ぬ。達男が山で嵐にみまわれ、彼が木を愛撫するとそれがピタリとやむといった不可思議な現象が、劇中にはさまれ、文明と日本の精神的なバック・ボーンが渾然となった様子が、柳町独特の骨太なタッチで描かれる。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猫と庄造と二人のをんな", "猫と庄造と二人のをんな", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "原作は谷崎潤一郎の同名の小説で、谷崎自身が経験した三角関係の恋愛事件がモチーフにされている。監督は前年「夫婦善哉」で名匠の名を確立した豊田四郎。主役にその「夫婦善哉」で絶妙な演技を示した森繁久彌を再び起用している。庄造の生活は愛猫リリーを中心に動いているといっていいほどで、異常なまでの愛情をリリーに注いでいる。母親は庄造の前妻を追い出したが、今また叔父が彼の娘を庄造にと縁談を持ちかけてくるや、その持参金に目をつけて早くも承知する。しかし当の庄造は新妻がリリーを追い出して以来、色や金などそっちのけで、リリーのことが気が気でならない。甲斐性なしの男をめぐって母、先妻、後妻が欲や嫉妬に絡んで凄烈なやりとりを繰り広げる人間喜劇である。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MALIZA", "青い体験", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「毛皮のビーナス」で注目されたセクシー女優L・アントネッリを起用し、お色気たっぷりに思春期の少年の性を描いた作品。シシリーのとある町を舞台に、母を亡くし男４人だけとなったイグナツィオ一家のもとに、若くてお色気たっぷりのお手伝いさんアンジェラがやって来る。次々と一家の男は彼女に熱をあげ、初めは彼女に反感を抱いていたが、ついには彼女と初体験をしてしまうという少年ニーノを主人公に描き、世界的に大ヒットを記録した。", "1973-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Power of One", "パワー・オブ・ワン", "", "", "http://ecx.images-amazon.com/images/I/51D2BPltGbL._SL210_.jpg", 127, "1930年代の南アフリカ。幼くして両親を亡くしたイギリス人少年PKは、忌まわしい差別や迫害に直面しながら、ドイツ人ピアニストのドクから音楽と人生を学ぶ。その後、収容所に通うようになったPKはズール族のピートと出会い、彼のもとでボクシングを習うことに。やがてPKは青年へと成長。そんな中、彼は出会いと別れを繰り返し、人種差別という過酷な現実に立ち向かっていく……。アフリカの大自然の中で、たくましく育っていく孤児の姿を壮大なスケールで描く。", "1993-04-30", "1,11,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY NEW GUN", "マイ・ニュー・ガン あぶない若妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "平凡な主婦が、夫から護身用の拳銃をプレゼントされてから、夫の事故、そして不倫と様々なハプニングにみまわれるコメディ。美少女からセクシーな女と成長していったD・レインが、今回は主婦役に挑戦。日常からハミ出していく人妻をコミカルに演じている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蜜月", "蜜月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "野菜市場の仲買店でアルバイトをしながら大学に通い文壇を目指す青年と、文芸誌の女性編集者との、今にも壊れそうな愛と生活を、1960年安保のあとの時代を背景に描いた作品。オーディションで選ばれた中村久美がデビュー、佐藤浩市が好演。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COME　SEPTEMBER", "９月になれば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "トラブル続きの恋のバカンスを描く、おしゃれかつ爽快なラブ・ロマンス・コメディ。ラブコメを得意にするマリガン監督の手腕に加え、ゴールデン・グローブ賞で“世界でもっとも好かれた男優・女優賞”をW受賞したロック＆ジーナの演技が光る。イタリアとアメリカ在住で遠距離恋愛をする二人。男がローマの彼女を訪ねたが、彼女は別の男との結婚を内緒で進めていた……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("へばの", "へばの", "", "", "http://image.eiga.k-img.com/images/movie/54223/315x210.jpg?2530", 81, "青森県六ヶ所村に住む紀美は、再処理工場で働く治との結婚を間近に控え、ささやかな幸せを感じながら暮らしていた。しかしある日、治は作業中にプルトニウムの内部被曝に襲われてしまい、紀美の父親は2人の間に生まれる子供に被曝の悪影響が出ることを案じ、結婚に反対し始める。それでも紀美は治との結婚を願うが、紀美の想いを打ち砕くように治は突然姿を消してしまう。監督は本作が初長編デビューとなる木村文洋。", "2009-01-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ショコキ！", "ショコキ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "お笑い集団ジョビジョバのリーダー、マギーの初監督作。停止したエレベーター内で起こる騒動をキテレツなエピソード満載で描くコメディだ。高層ビルのエレベーターに閉じ込められた見知らぬ他人同士の男女６人。地味なOL、オシャベリなピザ屋、疲れたサラリーマン、勘違い業界人男、平凡なおばさん、そして挙動不審の中年男。閉鎖された空間で、６人は次第に常軌を逸していく。そして隣のエレベーターには、ジョビジョバのメンバーがやはり閉じ込められており、自分たち主演の密室劇を映画化する構想で盛り上がっていた。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("序の舞", "序の舞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "明治時代に画家として、また未婚の母としてひたむきに生き、女性として初めて文化勲章を受けた上村松園をモデルに、その一生を描いた作品。メロドラマになりがちなテーマを、中島貞夫が清楚で気品あふれる画風で骨太の女性映画に仕上げた。名取裕子が好演。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐を突っ切るジェット機", "嵐を突っ切るジェット機", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1960～62年に作られた、小林旭の“パイロット”もの４本のうちの１本。榊は航空自衛隊アクロバット・チームの一員。彼の兄、英雄は私設航空集団“太平洋”を経営していたが、窮境の渦中にあり、麻薬商人・劉の仕事を請け負ってしまう。榊は劉と英雄の間のイザコザに巻き込まれて……。ラストの劉と英雄の空中戦は、迫力タップリ。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Swimming Pool", "スイミング・プール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "新作の筆が振るわないイギリスの人気ミステリー作家、サラは、出版社の社長ジョンの勧めで、彼が所有する南仏の別荘にやってくる。静かな土地と自然に囲まれ、執筆活動を始めるサラだが、そこにジョンの娘と名乗るジュリーが突然現れる。夜な夜な違う男を連れ込むジュリーに当初は辟易したサラだったが、彼女の奔放な魅力に注目し、ジュリーを題材にした物語を書こうとするが……。「8人の女たち」のフランソワ・オゾン監督が、シャーロット・ランプリング、リュディビーヌ・サニエという新旧の人気女優を主演に描くミステリー。", "2004-05-15", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA RAGAZZA DI TRIESTE", "トリエステから来た女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "作家でもある監督のカンパニーレ自身の小説を映画化した寓話風のラブ・ストーリー。中年の漫画家ディーノは、海で溺れかけた女、ニコルと恋に落ちる。しかし、ニコルは精神病患者で、そのことを知ったディーノはやがて彼女を避けるようになり……。O・ムーティがもろく神秘的な女を好演する。", "1983-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("営業100万回", "営業100万回", "", "", "http://image.eiga.k-img.com/images/movie/57943/315x210.jpg?3479", 0, "2012年・第4回沖縄国際映画祭の長編プログラム「Laugh」部門にて上映。", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON’T LOOK NOW", "赤い影", "", "", "http://ecx.images-amazon.com/images/I/41DwfXaURuL._SL210_.jpg", 110, "水の都市ヴェネチアを舞台に、映像派ローグが官能的に描くオカルト風のサスペンス。考古学者ジョンと美しい妻ローラはヴェネチアで盲目の霊媒師に出会い、水死した娘の幻影につきまとわれる。赤い色彩、回想のモンタージュが秀逸な異色作。", "1973-01-01", "3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("河内カルメン", "河内カルメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "女が美貌を武器に、したたかになっていく過程を描いた今東光の原作が、独特の清順ワールドの中に再構成された異色作。河内に生まれたセクシー娘は、町の男たちに強姦されたことと、病弱な父親を顧みず生臭坊主を家に連れ込む母親にいや気がさしたことが原因で、家を出て大阪に向かう。ここで彼女はホステスをしながら、様々な男たちと出会い別れ、成長していく……。日常シーンに突如として現れる火・水などの奇妙な清順的モチーフが、モノクロ映像と相まって、オカルト的なまでの夢幻的趣を出す。またひょろ長いアパートの明と暗を効果的に用いた演出を多くの評論家が称賛している。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BILLION DOLLAR BRAIN", "10億ドルの頭脳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「チャタレイ夫人の恋人」K・ラッセルの処女作。レン・デントンの小説をもとにしたスパイ・アクションとなっている。MI−5を辞め、探偵事務所を開いたハリー・パーマー。ある日かかってきた電話から、またもスパイたちの非情な世界に帰った彼は、国際的な悪の組織“10億ドルの頭脳”を相手に闘うはめになる……。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nightmare Circus", "悪魔の調教師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "　核実験によって父親を廃人にされ、精神に異常をきたしたアンドレは女性たちを監禁し、異様な調教と虐待を繰り返していた。そんな彼は車で旅をしていたショーガールたちを新たな生贄として拉致するが……。現在はロマンティックな作風で知られるアラン・ルドルフが無名時代に監督した猟奇ホラー。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLAME IT ON RIO", "アバンチュール・イン・リオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "情熱的で開放的なバカンスの名所リオで繰り広げられる、お色気たっぷりのラブ・コメディ。親友の愛娘からアタックされた中年男にM・ケインが扮し、D・ムーアが清楚なセミヌードで話題になった。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AL DI LA DELLE NUVOLE: PAR DELA LES NUAGES", "愛のめぐりあい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "M・アントニオーニ自身の短編小説を映画化、４つの話とプロローグ、挿話、エピローグからなり、メインの４話をアントニオーニが、その他の部分をW・ヴェンダースが監督をしている。第１話は「ありえない恋の物語」。旅先で出会った魅力的な女とその後も女を愛し続けた青年が、数年後に再会し、一夜をともにするが、身体に触れることなく、ただ見つめるだけで去って行った。第２話は「女と犯罪」。映画監督が港町で美しい女を見つめていると、その女が近づいてきて、自分は父親を殺したが無罪になったと言い、今夜会おうと誘ってくる。その晩、彼は彼女の部屋を訪れ、熱い一夜を送り、明け方部屋を出るのだった。第３話は「私を探さないで」。パリで偶然出会った男と女が関係を続けて３年になる。男の妻パトリツィアはあきらめて一人暮らしを始める。その頃、出張から帰ってきた実業家は、妻が消え、家の中が空っぽになっているのに驚く。パトリツィアと実業家が知り合い、二人はお互いの境遇に共感を覚えるのだった。第４話は「死んだ瞬間」。青年が若い女性を見初めてあとを追うが、彼女は修道院に入ると言って去ってしまう。", "1995-01-01", "2,7,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELLRAISER: BLOODLINE", "ヘルレイザー4", "", "", "http://ecx.images-amazon.com/images/I/31N66K68BXL._SL210_.jpg", 85, "怪奇幻想作家C・バーカーの製作総指揮によるホラー・シリーズの第４作。地獄の扉を開ける“パズルボックス”の起源をさかのぼり、18世紀と現代、22世紀にまたがる、魔道士ピンヘッドと人間の戦いが繰り広げられる。頭部にピンを突き立てたピンヘッドのダークかつシュールな存在感に、１作目への原点回帰がうかがえる。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談新耳袋　ノブヒロさん", "怪談新耳袋　ノブヒロさん", "", "", "http://image.eiga.k-img.com/images/movie/33573/315x210.jpg?2085", 89, "実話に基づいた怪談集「新耳袋」を原作とするテレビドラマシリーズ「怪談新耳袋」の劇場版第3弾。原作の中でも特に怖いといわれるエピソードを、内山理名主演で映像化。シングルマザーの悦子は、ある日仕事で出会った画家ノブヒロから絵のモデルを依頼され、彼のアトリエに通い始める。ところがある晩、ノブヒロが不可解な死を遂げる。謎を解くために彼のアトリエに忍び込んだ悦子は、そこで驚くべき事実を知るのだった……。", "2006-07-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紀ノ川（前編・後編）", "紀ノ川（前編・後編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 172, "有吉佐和子の長編小説をもとに、明治、大正、昭和の三代にわたる女の年代記を、紀ノ川の流れに託して描く。明治32年、紀本花は紀州の旧家・真谷家に嫁ぐ。花の長女文緒は進歩的な風潮に染まり、真谷家の旧弊な家風に柔順な母親に反発を感じていた。そんな文緒も、昭和の激動のなかで長女・華子を育て、常識をわきまえた中年女性になっていく。夫の何度目かの法要を盛大に営んだ花は、華子に髪をすいてもらいながらその生涯を閉じる。伝統の女性路線の建て直しをはかる松竹が力を注いだ文芸大作。紀州の自然を成島東一郎が澄明な映像で捉え、中村登監督の頂点をなす秀作となった。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EACH DAWN I DIE", "我暁に死す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "J・キャグニー、G・ラフトという、ギャング映画の２大スターが競演した犯罪アクション。無実の罪で20年の刑に処せられた新聞記者と終身犯の男が刑務所で出会い、友情で結ばれる。新聞記者を自由の身にしようと、男は暴動を起こし、やがて看守対囚人の激しい銃撃戦が始まる。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明治大帝と日清戦争", "明治大帝と日清戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "劇場公開タイトルは「天皇・皇后と日清戦争」。朝鮮の独立を助けるべく（？）清国に宣戦を布告した日本が見事に大勝利を収めたあと、露・仏・独３ヵ国の干渉により、遼東半島の領有権を放棄するまでのお話。平和主義者として描かれる明治天皇を中心に、講談調で進められていく、大スペクタクル映画の珍品である。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VA’DOVE　TI　PORTA　IL　CUORE", "心のおもむくままに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "本国イタリアをはじめ世界中で大ベストセラーとなった、女流作家タマーロの小説を映像化。死んだ祖母が孫娘に遺した一冊の日記。そこには祖母の秘められた悲恋、祖母と母の葛藤、孫娘の出生の秘密が綿々と綴られていた……。３世代の女性の姿を描きながら、人生の喜びと苦悩をさわやかに捉えていく。", "1995-01-01", "4,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋に唄えば♪", "恋に唄えば♪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "金子修介監督が映画初出演の優香を主演に据えて作り上げた、幸福感あふれるラブ・ストーリー。彼氏にフラれたばかりのOLが、目の前に突然現れた魔法使いに恋の復活をお願い。それほど能力は高くない魔法使いと一緒に、元カレを追ってオーストラリアへ。そこで破局の意外な真相を知る。オーストラリアでロケされたミュージカル・シークエンスはダイナミック。クライマックスを彩る歌と踊りのきらびやかな場面には恋のミラクルが充満している。アグレッシブな優香を相手に、絶妙のボケをかます竹中直人が快演。終盤で突然登場する篠原ともえも、ファンシーな魅力を最大限に発揮し、映画のボルテージを大いに盛り上げている。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日常", "日常", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "賑やかな大阪の街で生きるストリートミュージシャンや婦人警官、そしてサラリーマンたち。そんな彼らの普段と変わらない１日がスタートする……。大阪の街を舞台に、一見退屈な日々だけど、かけがえのない無数の喜怒哀楽がつまっている“日常”を描くヒューマン・ドラマ。ケンドーコバヤシや次長課長、友近や南海キャンディーズのしずちゃんなど、吉本興業の若手芸人が続々と登場。彼らのいつもとはひと味違う表情にも注目だ。", "2006-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Challenge", "最後のサムライ／ザ・チャレンジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "　ボクサーのリックは日本刀を運ぶ仕事を引き受け、京都へ向かう。それを待っていたのは、ヨシダという男だった。彼と弟のヒデオが繰り広げる骨肉の争いに巻き込まれながら、やがて武士道に目覚めていくリック。そんな折、ヨシダの娘アキコがヒデオに誘拐されるという事件が。リックとヨシダが救出に向かうが……。名匠ジョン・フランケンハイマーが三船敏郎、稲葉義男、宮口精二らを起用し、日本ロケを敢行して作り上げたアクション。", "1982-01-01", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beverly Hills Cop", "ビバリーヒルズ・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "好漢E・マーフィが型破りな刑事に扮し、ハチャメチャな活躍をするヒット作。デトロイト市警の若い刑事アクセルは、いつも仕事のうえで上司のトッド警部と衝突ばかりしていた。ある日、ロスから訪ねてきた幼友だちが殺し屋に殺されるという事件が起きる。目の前で親友を殺されたアクセルは、単独で捜査するため上司の命令に背いてロスに乗り込んだ。ビバリーヒルズの高級画廊に出入りする怪しい男たち、やがて麻薬密輸事件も浮かび上がってくるが……。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Holiday", "ホリデイ", "", "", "http://image.eiga.k-img.com/images/movie/33893/315x210.jpg?1256", 135, "キャメロン・ディアス、ジュード・ロウ、ケイト・ウィンスレット、ジャック・ブラックら豪華スターが競演するロマンティック・コメディ。ハリウッドで映画予告編製作会社を経営するアマンダと、ロンドンで新聞社に勤めるアイリス。それぞれ失恋したばかりの2人は、お互いの家や車を交換して休暇を過ごす“ホーム・エクスチェンジ”をすることによって、人生の新たな一歩を踏み出していく。監督は「恋愛適齢期」のナンシー・メイヤーズ。", "2007-03-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Star Trek", "スター・トレック（2009）", "", "", "http://image.eiga.k-img.com/images/movie/53183/315x210.jpg?2024", 126, "「M:i:III」のJ・J・エイブラムス監督が、同名人気シリーズを新たに描き出したSFアドベンチャー。伝説のキャプテンを父に持つジェームズ・T・カークが惑星連邦艦隊へ入隊。新造されたUSSエンタープライズへ乗り込み、数々の苦難を乗り越えキャプテンになるまでの成長が描かれる。主人公カークに新星クリス・パイン、スポックに「HEROES／ヒーローズ」のザッカリー・クイント。共演にエリック・バナ、カール・アーバン、ゾーイ・サルダナほか。", "2009-05-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FEDERICO FELLINI’S INTERVISTA", "インテルビスタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "チネチッタのスタジオを愛するフェリーニが、チネチッタ創立50周年を記念して撮った作品。映画は、新作を撮影中のフェリーニに日本のTV局取材班がインタビュー(イタリア語でインテルビスタ)するところから始まる。フェリーニは、1940年頃は駆け出しの記者で、スター女優ディーバへのインタビューのため、初めてチネチッタを訪れた。昔話をはさみながら、新作の撮影も進行していく。ある日、フェリーニはマストロヤンニを伴ってエクバーグの別荘へ遊びに行き、二人が27年前に共演した「甘い生活」を観る。老い、太ったエクバーグが、若く美しい自分を見て涙するシーンは感動的で、まさにこのシーンのために撮られた映画と思えるほど味わい深い。当時の映画製作風景と現在進行中の新作とが交錯する、映画についての映画でありながら、かつての「８1/2」とは別の姿勢で、映画への思いを描いている秀作。モスクワ映画祭グランプリを受賞。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRINGER", "ストリンガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "天性の嗅覚で事件を追うフリー・ジャーナリストのフランク。彼は常に現場に出没し、警察とギャングの間に身を置いていた。ある時、ロス市警の警察官と知り合い意気投合、二人はチームを組んだ。そこで偶然“ダウンタウンの切り裂き魔”と呼ばれる連続殺人犯を追う事に……。夜のL.A.を舞台にしたサスペンス・アクション。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WIZARD OF LONELINESS", "さよなら魔法使い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "第二次大戦下のアメリカ、バーモントの片田舎に住む少年ウェンドル。自分を魔法使いに見立てて、空想の中で空を飛ぶこの孤独な少年は、周囲の大人たちとふれあいながら、少しずつ成長していく。「刑事ジョン・ブック／目撃者」の子役として脚光を浴びたL・ハース主演。繊細な感情の機微が淡々と綴られていく。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUEULE D’AMOUR", "愛欲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "フランスの鬼才、J・グレミヨン監督作で、「望郷」のコンビ、J・ギャバンとM・バランが同年に再び共演したペシミズム漂う佳編。“女たらし”とあだ名される除隊兵ルシアンは、カンヌで知り合った美貌の女マドレーヌがバクチで大損したのを見て、自らの遺産１万フランを吐き出して助けてやる。それでてっきり彼女をモノにできると思ったのだが……。追えば逃げる、得れば空しき悪女の末路。そして彼女にたぶらかされた、哀れな男の物語。監督・脚本・俳優はフランス勢、撮影、音楽などはドイツ勢。独・ウーファ撮影所で撮られたというちょっと変わったフランス映画。", "1937-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Another Year", "家族の庭", "", "", "http://image.eiga.k-img.com/images/movie/57043/315x210.jpg?3090", 130, "「秘密と嘘」「ヴェラ・ドレイク」のマイク・リー監督が手がけ、第83回アカデミー賞で脚本賞にノミネートされたヒューマンドラマ。初老の夫婦トムとジェリーは、それぞれに打ち込める仕事と休日の家庭菜園にいそしみ、弁護士の息子ジョーも親思いの好青年で幸福な人生を送っていた。ジェリーの同僚メアリーは男運に恵まれない境遇を嘆き、ジェリーの幸せをうらやんでいたが、トムの旧友ケンがメアリーに興味をもち……。それぞれに喜びや悲しみを抱えた人々がトムとジェリーの庭に集まり、語らうことで前向きに生きようとする姿を描き出していく。", "2011-11-05", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ANDROMEDA STRAIN", "アンドロメダ…", "", "", "http://ecx.images-amazon.com/images/I/51auwFFMXdL._SL210_.jpg", 130, "ニューメキシコの片田舎に人工衛星が落下。近くの町は、赤ん坊とアル中の老人を除き、死滅してしまった。彼らだけが助かったのはなぜか？　衛星に付着し、宇宙からやって来た病原体と、科学者たちの死闘が始まる……。緊迫したドキュメンタリー・タッチが見事。特殊撮影は、ダグラス・トランブルとジェームズ・ショート。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DISTANCE ディスタンス", "DISTANCE ディスタンス", "", "", "http://ecx.images-amazon.com/images/I/5159M76EAPL._SL210_.jpg", 132, "3年前にカルト教団が起こした殺人事件の加害者遺族である4人の男女は、1年に1度再会し、遺灰が沈む湖で手を合わせることにしていた。その年、湖から帰ろうとする4人の車がなくなるというアクシデントに見舞われた彼らは、同じくバイクをなくした元信者の青年とともに、かつて信者たちが暮らしていたロッジで一夜を過ごすことになる。監督は「ワンダフルライフ」の是枝裕和。出演はARATA、伊勢谷友介、寺島進、浅野忠信ほか。", "2001-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Walker", "ウォーカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「シド・アンド・ナンシー」のアレックス・コックス監督が、実在した独裁者ウィリアム・ウォーカーの半生を、ブラック・ユーモアをまじえて描いた問題作。19世紀半ば、アメリカの植民地政策の一環として内紛の続くニカラグアへ派遣されたウィリアム・ウォーカー。“58人の不死隊”を率いて全土を制圧した彼は、自らが大統領に就任して独裁政治を開始するが……。狂気にとりつかれた男ウォーカーを、名優エド・ハリスが熱演。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Back-up Plan", "バックアップ・プラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ジェニファー・ロペス主演のラブコメディ。子供は欲しいがなかなかいい男に巡り会うことなく、人工授精での出産を決意したゾーイ（ロペス）だったが、その人工授精の当日、ついに理想の男性スタンと出会う。ゾーイは人工授精したことを隠しながら、スタンとデートを重ねるが……。監督は「ウーマン・オン・トップ」やTVシリーズ「シックス・フィート・アンダー」などの製作・監督を務めてきたアラン・ポール。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HIT LIST", "ヒットリスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ピーターの依頼により数々の殺しを行って来た、殺し屋のチャーリーは今度の仕事を最後に足を洗う決心をしていた。その仕事とは、殺しではなくジョーディーという美しい女性のボディガードだった。チャーリーは、彼女の魅力の虜になるのだった……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOLIES BOURGEOISES", "ブルース・ダーンのザ・ツイスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "失業中のライター、ウィリアムとクレアのパリでの新婚生活は、二人の周囲に入り乱れる男女のおかげで散々なものに。C・シャブロルがA・マーグレットらの大スターを使い、男女の機微を描き出したロマンティック・コメディ。", "1976-01-01", "2,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かげろう絵図", "かげろう絵図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "松本清張の同名小説を、名匠・衣笠貞之助が映画化。徳川十二代将軍・家慶の頃、隠居した前将軍の権勢が今だ幅を利かせているなかで、世継ぎ問題をめぐり、腐敗堕落した大奥と幕府内部が繰り広げる派閥抗争を描く。大道具や小道具にこだわる衣笠の注文を受け、美術担当の西岡善信が、江戸城内や市街をぜいたくなセットで再現している。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROM　THE　JOURNALS　OF　JEAN　SEBERG", "ジーン・セバーグの日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「悲しみよこんにちは」「勝手にしやがれ」などで一世を風靡しながらも、1979年に不可解な死を遂げたジーン・セバーグの歩みを、日記形式でたどったドキュメンタリー。セバーグと同世代の女優ジェーン・フォンダ、ヴァネッサ・レッドグレーヴとの共通点を探りながら、1960～70年代の映画から印象的なシーンをピックアップ。単なる評伝に留まらず、一種の女優論というべきユニークな作品になっている。DVDは「ジーン・セバーグ・コンプリート」に収録。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gone in 60 Seconds", "60セカンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "メンフィスはかつて、どんな車でも60秒で盗み出す伝説の車泥棒だった。今ではそんな世界から足を洗った彼だったが、弟が巨大組織の事件に巻き込まれ事態は急変。弟の命を救うため、彼はわずか24時間で最高級車50台を盗み出すという、常識では不可能な仕事を引き受けた。この空前の大仕事に、技を極めたエキスパート達がメンフィスの元に集結。だが、不穏な動きを察知した警察は大捜査綱を設置して宿敵メンフィスを待ち受けていた……。", "2000-09-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Usual Suspects", "ユージュアル・サスペクツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "アメリカ、日本で大ヒットした、当時、29歳だったB・シンガーの第２作。コカイン取引現場で、コカイン密輸船が大爆発を起こし、27人が死亡し、大量のコカインと9100万ドルが消えた。捜査官はただ一人無傷で生き残ったキントを尋問する。キントは、事件を操っているのは、伝説の大物ギャング“カイザー・ソゼ”であることを告げる。ソゼが襲撃の実行犯として、別事件の容疑者５人を集めたというのだ。しかし、爆破によって死体は判別不能で、ソゼの生存も定かではない。果たしてカイザー・ソゼとは何者なのか？　大金とコカインの行方は？", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUJOURD’HUI PEUT−E＾TRE", "木洩れ日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "名匠フェリーニの良き伴侶としても知られる名女優G・マシーナが、70歳にして初めて出演したフランス映画。行方不明のままの息子に会うために、住み慣れた屋敷の売却広告を出した老婆と、彼女を気づかう家族の交流がほのぼのと描かれていく。優しい光をたたえた映像でしっとりと綴られた家族劇。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hell Ride", "ヘルライド", "", "", "http://image.eiga.k-img.com/images/movie/53923/315x210.jpg?3071", 84, "「パルプ・フィクション」「キル・ビル」のクエンティン・タランティーノが製作総指揮を務め、バイカー・ギャング映画のカルト俳優ラリー・ビショップが監督＆主演するバイオレンス・アクション。共演にビニー・ジョーンズ、デニス・ホッパーほか。バイカー・チーム“ヴィクターズ”は対抗グループにメンバーのひとりを殺され、復讐の旅を決意する。", "2009-01-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("樹の海", "樹の海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "自殺の名所として名高い富士山麓、青木ヶ原樹海を舞台にした群像劇。樹海に足を踏み入れた人々の4つのエピソードが展開していく。生と死の狭間に立った登場人物に扮する萩原聖人や池内博之らキャストの熱演と、死と負の題材を希望のドラマに昇華させた新鋭、瀧本智行監督の脚本が見事だ。樹海に棄てられた公金横領犯の朝倉、過去の過ちに捕らわれた映子、自殺者を調査する探偵の三枝。樹海に集った彼らは自らの人生と対峙する。", "2005-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゼロの焦点（2009）", "ゼロの焦点（2009）", "", "", "http://image.eiga.k-img.com/images/movie/54313/315x210.jpg?4533", 131, "松本清張の長編推理小説を「ジョゼと虎と魚たち」の犬童一心が映画化。主演に広末涼子、中谷美紀、木村多江。結婚式の7日後に消息不明となった夫・憲一の足跡を追って北陸・金沢を訪れた禎子は、そこで憲一の得意先だった社長夫人の佐知子、受付嬢の久子に出会う。一方、憲一に関わりのある人物が被害者となる殺人事件が相次いで起こり始める。", "2009-11-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROADWAY MELODY OF 1938", "踊る不夜城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "金持ちの娘とブロードウェイで劇を成功させたい男の恋愛騒動を描くミュージカル・コメディで、「踊るブロードウェイ」「踊るアメリカ艦隊」に続くE・パウエルとR・デル・ルース監督の３度目のコンビ作。J・ガーランドの『Dear Mr.Gable』、S・タッカーの『Your Broadway And My Broadway』は必聴。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("親鸞・続親鸞", "親鸞・続親鸞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 276, "日活から東映に移った田坂具隆の移籍第１作。吉川英治が1935～36年に発表した同名連載小説の映画化で、タイトルどおり親鸞の姿をその宗教への姿勢や女性との関係を中心に描いたもの。全盛期の中村錦之助が親鸞を演じている。音楽は“ゴジラ”シリーズで一躍有名になった伊福部昭。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版イナズマイレブンGO 究極の絆　グリフォン", "劇場版イナズマイレブンGO 究極の絆　グリフォン", "", "", "http://image.eiga.k-img.com/images/movie/56743/315x210.jpg?1924", 91, "ニンテンドーDS用の人気サッカーRPGを原作としたアニメの新シリーズ「イナズマイレブンGO」の劇場版。円堂守をキャプテンとしたイナズマジャパンが世界制覇を成し遂げてから10年。サッカー界の様相は一変し、全国のサッカーは「フィフスセクター」という管理組織により勝敗までもが管理される世界になっていた。サッカーに対して情熱を燃やす少年・松風天馬は、雷門サッカー部の仲間とともに本当のサッカーを取り戻すため激闘を繰り広げる。", "2011-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅之介斬魔剣 妖魔伝説", "修羅之介斬魔剣 妖魔伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "京本政樹が美ぼうの剣士に扮するスーパー時代劇。江戸初期、最強の剣士・修羅之介が大藩の姫・真夕を救出。ところが、それ以来、彼は失われた秘宝をめぐる戦いに巻き込まれる。豊臣家に滅ぼされた北条家に関係し、その秘宝を狙うくノ一や独眼竜・伊達政宗、邪剣士群を操る怪僧らが入り乱れる中、修羅之介の剣技が舞う。「ウルトラマンゼアス」や「モスラ」を手掛けたオムニバス・ジャパンのSFXを駆使した殺りくシーンが見もの。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Three Businessmen", "スリー・ビジネスメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "「シド・アンド・ナンシー」「ストレート・トゥ・ヘル」の鬼才アレックス・コックスが監督・主演をつとめる、ブラックユーモアをちりばめたファンタジックなロード・ムービー。リバプールのホテルで出会った2人のビジネスマン。一緒に食事をするためレストランを探すことになった彼らは、アムステルダム、香港、東京をさまよい続け、やがて砂漠にたどり着く……。東京のシーンで永瀬正敏や田口トモロヲが出演したことでも話題に。", "", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WRONG WOMAN", "間違えられた女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "何者かによって自分が殺人犯に陥れられる恐怖を描いたサスペンス・スリラー。メラニーと勤め先の社長ヘンリーは、初めて会った時から互いに惹かれ合う。その夜、誘われるままに社長の自宅を訪ねたものの、不倫がイヤで家を飛び出してしまうメラニー。しかし次の日、社長は死体で発見され、容疑はメラニーにかかってきた。身に覚えのない証拠が次々に上がり、容疑は深まるばかり。潔白は自分で証明するしかないと、警察から追われる身になってまで真犯人を捜すメラニーだが、その先に待つのは絶体絶命の窮地だった。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STARCRYSTAL", "スタークリスタル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「スタンド・バイ・ミー」「逃亡者」などのイントロビジョン（合成効果）を手掛け、ハリウッド映画界で最も斬新な視覚効果監督として知られるW・メサの初監督作品。遥か宇宙の彼方で行われているラスト・スターウォーズ。宇宙を征服するのに必要なのは、すべての生命の源である伝説の水晶スタークリスタル。それを手にすべく、宇宙征服をたくらむ悪の帝王。そして宇宙の存亡を守るべく、最後の望みを託された最強の女戦士ラデラ。宇宙の未来を懸けた最後の戦いが始まる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いけちゃんとぼく", "いけちゃんとぼく", "", "", "http://image.eiga.k-img.com/images/movie/54193/315x210.jpg?3465", 107, "人気漫画家・西原理恵子の同名絵本を実写映画化。CGで表現された不思議な生物“いけちゃん”の声を蒼井優が担当する。いじめられっ子のヨシオのそばにはいつもヨシオにしか見えない謎のいきもの“いけちゃん”がいて、ヨシオのことを温かく見守っていた。しかし、そんなヨシオがたくましく青年へと成長していくと、いけちゃんは自分の正体をヨシオに伝えようとする。", "2009-06-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OH，HEAVENLY DOG！", "名探偵ベンジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "名犬ベンジーのカワイイ演技が人気のシリーズ。「ベンジー」「ベンジーの愛」に続く、第３作は、ベンジーの恋を絡めた、大人向けロマンティック・ミステリー。自分を殺した犯人を捜すために天国からやって来たという私立探偵の霊が、ベンジーに乗り移る。人間の意識を持ったベンジーが、美人ジャーナリストとともに、謎を解くために大活躍！", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花月佳期／LOVE IN THE TIME OF TWILIGHT", "トワイライト・ランデブー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ヒットメーカー、T・ハークが広東オペラに材を得たファンタジー。後に「天使の涙」で注目を集めるC・ヤンが主演している。夜の町で偶然出会ったヤンとコンの二人だったが、ささいな誤解からお互いの印象は最悪だった。その後、コンは銀行強盗の片棒を担がされ、命を落とし、電灯が点ったところしか出られない幽霊になる。コンはヤンの所に現れ、一緒に過去に行き、自分の命を取り戻して欲しいと頼む。一度は断ったものの、結局は協力することになり、一緒に計画を進めるうちにお互いの印象は変わる。苦労のすえ、二人は過去を変えることに成功するが、愛し合うようになった二人は、この世から消えなくてはならなかった……。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おいしい殺し方 A Delicious Way to Kill", "おいしい殺し方 A Delicious Way to Kill", "", "", "http://image.eiga.k-img.com/images/movie/33613/315x210.jpg?4127", 106, "今や日本演劇界を代表する存在となったケラリーノ・サンドロビッチが奥菜恵を主演に迎えて贈る、サスペンスタッチのナンセンス・コメディ。BSフジ、GyaOで放映されて大人気を博し、劇場公開用に再編集された。料理が下手なことを理由に男からふられてばかりいるユカは、人気料理研究家の東大寺ハルキが講師を務める料理教室にしぶしぶ通い始める。ところがその矢先、東大寺が自宅マンションから謎の飛び降り自殺を遂げる。", "2006-08-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KEYS TO FREEDOM", "自由の代償・ラビリンスを脱出せよ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1997年の中国返還を控えた香港を舞台にしたサスペンス・アクション。多くの香港人は外国への移住を希望しても叶えられず、出国できるのは資産や技術の条件を満たす少数の人間であった。この揺れ動く地に、サンフランシスコからやって来たミッチャムは、理由もなくマフィアに狙われる。彼は自分が何か秘密の鍵を握らされていると考え、事件を探り始める。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("挽歌", "挽歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "無名でありながら、発表されるやたちまちベストセラーになった原田康子の同名小説を、原作に忠実に撮り上げたラブ・ロマンス。関節炎を患って以来、空虚な日々を過ごす怜子は中年の建築技師・桂木と知り合い、少しずつ心をほぐしていく……。北海道の自然をバックに、適役の久我美子が美しい。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEEN WOLF TOO", "ティーン・ウルフ２ ぼくのいとこも狼だった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「ティーン・ウルフ」の続編。主演は前作のM・J・フォックスからJ・ベートマンにバトンタッチ。今度はスポーツ苦手のガリ勉少年。ところが奨学金がボクシングとセットになっていたため仕方なくリングに。ところがKO寸前に、狼に変身し……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("動脈列島", "動脈列島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "新幹線がもたらす公害に抗議し、これを爆破しようと狙う若者と、それを阻止する警察側との息詰まる駆け引きを描いた極上のサスペンス映画。日本推理作家協会賞を受賞した清水一行の原作を、増村保造がスピーディーな画面展開で映像化。近藤正臣が迫真の演技を見せる。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("叛乱", "叛乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "立野信之の原作を菊島隆三脚色、佐分利信監督で映画化した昭和史もの。昭和11年２月26日、近衛兵連隊の皇道派青年将校たちによって、首相をはじめ政府の要人たちが殺された“二・二六事件”を正攻法で描く。撮影中に佐分利信が病に倒れ阿部豊があとを引き継ぎ完成させた。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LONELY GUY", "スティーブ・マーティンのロンリー・ガイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "女房に逃げられて、独身生活を余儀なくされたラリーは、同じような境遇の仲間を誘ってシングルズ・バーでナンパしようと繰り出したが、あえなく失敗。このままでは情けないと奮起するが……。ニューヨークを舞台にしたハイセンス・コメディ。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents すき　「口腔盗聴器」", "桃まつり presents すき　「口腔盗聴器」", "", "", "http://image.eiga.k-img.com/images/movie/57883/315x210.jpg?3613", 27, "若手女性監督による映画製作・上映集団「桃まつり」が、「好き」をテーマに女性監督たちが撮り上げた短編作品を上映する「桃まつり presents すき」の1本。歯科医の男が青春時代に恋心を抱いていた女と再会するが、すでに女からは当時の輝きは消えていた。一方、女の娘の少女にかつての女の面影を見出した男は、決して受け入れてもらえないとわかりながらも、自分なりの方法で少女との距離を縮めていく。監督は、脚本家としても多数の作品を手がける上原三由樹。「弐のすき」として「春まで十日間」（ステファニー・コルク監督）、「最後のタンゴ」（熊谷まどか監督）と同時上映。", "2012-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT OF THE SCARECROW", "アーバン・ハーベスト３　ザ・スケアクロウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "かかしの怪人スケアクロウが、怨みを持つ一族に襲いかかるホラー映画。ハンフォード町の町長ウィリアムの娘クレアは、ボーイフレンドのディロンを伴って久々に実家に戻ってきた。折しもその日は、町で公民館の起工式が行われ、一族集まっての夕食会が開かれた。だがその夜、叔父のジョージが納屋で惨殺されたのを皮切りに、一族に邪悪な怪人スケアクロウが襲いかかり、最後の生き残りとなったクレアとディロンは必死に抵抗しながら、かつて一族が犯した罪を解き明かしていく……。メイクアップを「エルム街の悪夢」のデヴィッド・ミラーが担当している。公開時のタイトルは「ザ・スケアクロウ」。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("近松物語", "近松物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "近松門左衛門原作の『大経師昔暦』を脚色した溝口健二の名作。ふとした偶然で窮地に立たされた男女がやがて真実の愛に目覚め、捕らえられるが嘆くことなく刑場に惹かれていく。いかにも溝口らしい題材を様式的描写の中にパッショネイティブに描ききって、彼の代表作の一つとした。特に家内制手工業を営む経師屋の室内で展開される前半部分は、溝口演出の極致といえる見事なアンサンブル。また、溝口が常に批判してきた長谷川一夫の起用は、かつてない緊張感のもとに最高の演技を引き出すことに成功している。宮中の経巻表装を職とし、傍ら暦の刊行権をも握る“大経師内匠”の手代・茂兵衛は内儀・おさんの兄に銀一貫目を融通したことがばれ、主人の以春に空室に閉じ込められてしまう。おさんは茂兵衛を救うため、かねて夫が言い寄っていた女中のお玉と寝所を取りかえ動かぬ証拠を押さえようとする。ところが深夜忍んできたのはお玉に別れを告げに来た茂兵衛で……。戦前から溝口作品を担当してきた彼の盟友ともいえる水谷浩が美術を担当している。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I, Robot", "アイ，ロボット", "", "", "http://image.eiga.k-img.com/images/movie/1093/315x210.jpg?2235", 115, "人間とロボットが共存する2035年のシカゴ。ひとりの科学者が謎の死を遂げ、刑事デルは一体のロボットに疑念を抱いて捜査を進めるが……。「ロボット3原則」を提唱したアイザック・アシモフの「われはロボット」にインスパイアされて作られたアクション・スリラー。監督は「クロウ／飛翔伝説」「ダークシティ」のアレックス・プロヤス。「ビューティフル・マインド」のアキバ・ゴールズマンが共同脚本を担当。", "2004-09-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRE WITH FIRE", "禁じられた恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "カトリック系全寮制女子校に学ぶ少女と、少年鑑別所に送られた青年の激しい愛の行方を描く。女子学園の厳格な規律のもとで暮らすリサは、ある日、少年鑑別所暮らしの青年ジョーと知り合う。なんとか彼に心を通わせたいと願うリサは、近隣者に奉仕せよ、という学校のポリシーを盾に、少年鑑別所の少年たちをパーティーに招く計画を提案するのだった。ビデオ発売時タイトルは「ファイヤー・ウィズ・ファイヤー／禁じられた恋」。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("見鬼／THE　EYE", "the EYE【アイ】", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "タイを拠点に活動する「レイン」のパン兄弟が監督したサスペンス・ホラー。幽霊にまとわりつかれるヒロインの恐怖と心の再生を描き、トム・クルーズがリメイク権を買ったことも話題になった。角膜手術によって視力を取り戻した女の子、マン。ところが、それ以来たびたび不審な人影を目撃するようになった彼女は、それらが幽霊だと知る。マンは心理療法士とともに角膜の提供者についての調査を始めるのだが……。", "2001-01-01", "6,23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("衛斯理藍血人", "ブルー・エンカウンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「X−ファイル」や「メン・イン・ブラック」を彷彿とさせる香港製のSF活劇。善悪のエイリアンやFBIが入り乱れる愛と闘いのドラマが、アメリカ・ロケによるパワフルな映像で展開する。国連の地球外生物分析局で働く青年ウェズリーが、行方不明の弟を探している美しき異星人ティンガイと出会う。やがてFBIの手を逃れたウェズリーとティンガイは、凶悪なワーロック星人との決死の闘いに身を投じていく。", "2002-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LYDIA", "リディアと四人の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ボストンの旧家の娘リディアをめぐる４人の男との恋を描いた、「舞踏会の手帖」を思わせる作品。年老いたリディアを中心に３人の老友たちが、青春の日々の恋の思い出を語り合う形式で、過去と現在、幻想と現実が交錯しながら物語が展開する。デュヴィヴィエ独特の感性が光る一編だ。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUBURBAN COMMANDO", "マイホーム・コマンドー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "宇宙最強の戦士シェップは、悪の独裁将軍の戦艦を爆破する任務を終え、しばしの休暇を地球で送ることにする。気の弱い建築家チャーリーの家に下宿し、地球人として生活を始めた彼だが、やがて情無用の賞金稼ぎが宇宙からやって来て……。プロレスラー、H・ホーガンがパワフルに暴れ回るホーム・コメディ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("K−９", "K−９／友情に輝く星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "張り込みを続けていたサンディエゴ市警の車が、突如現れたヘリコプターからのマシンガン乱射によって爆発炎上した。狙われたドゥーリー刑事は、間一髪で助かった。彼は麻薬取締官で、5000万ドルの麻薬を動かす大物密売人ライマンを追っていたのだが、組織に狙われ始めたのだ。ライマンの報復にドゥーリーは“K−9”課の捜査犬をパートナーに、ライマンへの挑戦を開始した。相棒の名は“ジェリー・リー”というシェパード犬。刑事と麻薬捜査犬という珍コンビの活躍を、ユーモアとアクションで描いたコメディ。犬の名演技が見もの。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEFIANCE", "摩天楼ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ニューヨークのロウアー・イーストサイドの薄汚れた街を背景に、そこに巣喰う船乗り、酒にウサを求める男、したたかな浮浪児たちなどをバイオレンス・タッチで描く。J・フリン監督の、硬質な演出が見ごたえ十分。ギャング団に戦いを挑む航海士に扮したJ・M・ヴィンセントのタフガイぶりにも注目したい。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.NORTH", "ミスター・ノース～風をはこんだ男～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1926年、富豪が多く住むニューポートの町で、イエール大卒のセオフィラス・ノースが金持ち相手の珍商売を始める。金を貯めてモーター・ツアーをするためだ。風変わりな若者ミスター・ノースと、町の人々との交流を描くこの作品は、J・ヒューストンの脚本作品としては最後の仕事となったもので、演出を息子のダニーが手掛けている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無防備", "無防備", "", "", "http://image.eiga.k-img.com/images/movie/54723/315x210.jpg?4677", 88, "ぴあフィルムフェスティバル出身の新鋭・市原昌秀監督が、ある女の心が再生していくまでの葛藤（かっとう）を描き、釜山国際映画祭で最優秀賞を受賞するなど国内外で高く評価された人間ドラマ。田舎町のプラスチック工場で働く30代の主婦・律子は、夫との関係も冷えきり、職場と自宅を往復するだけの単調な毎日を送っていた。そんな律子の前に妊婦の千夏が現われたことから、律子が心の中に抱えていた闇が徐々に浮かびあがっていく。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bugsy Malone", "ダウンタウン物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "登場人物は全て子どもで銃撃戦はパイ投げという異色のギャング・ミュージカル。1930年代、禁酒法下のニューヨーク。暗黒街では、ファット・サム一味とダンディ・ダン一味による血で血を洗う抗争が繰り広げられていた。そこへやって来た一匹狼のバグジー・マローン。彼はサムに気に入られ、一味の仲間入りをするが……。アラン・パーカー監督の長編デビュー作。当時14歳とは思えない、ジョディ・フォスターの妖艶な姿も必見。", "1976-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃太郎侍", "桃太郎侍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "1952年に長谷川一夫主演で映画化された山手樹一郎の同名小説の再映画化。双子を忌む武家の風習から里子に出され、成人して無類の剣の使い手となった桃太郎。そのころ生家の若木家では次席家老の陰謀により、若殿新之助が危機にさらされていた。桃太郎は身代わりとなって敵に立ち向かうことを決意する。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE LA PART DES COPAINS", "夜の訪問者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "南フランスの港町。妻と娘の３人で平和に暮らしている釣り船商の男の前に、突然朝鮮戦争時代の悪の仲間が現れる。娘を人質にとられ、麻薬を運べと脅かされるが……。家族の危機に苦悩しつつ闘う男のサスペンス・アクション。リチャード・マシスンのスリラー小説の映画化。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マレー戦記 進撃の記録", "マレー戦記 進撃の記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "1942年に封切られ大反響を呼んだ、マレー・シンガポール攻略戦争の実態を劇的に捉えた迫力の戦記映画。昭和16年12月、日本軍がシンゴラへ上陸し、マレー・シンガポール攻略戦争が始まった。山下泰文とパーシバル両将軍の会見など貴重な映像も記録している。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Roger ＆ Me", "ロジャー＆ミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「華氏911」「ボウリング・フォー・コロンバイン」で知られるマイケル・ムーア監督の初期ドキュメンタリー作品。巨大企業ゼネラル・モータース（GM）の工場町としてかつて栄えたミシガン州フリントは、ムーアの故郷。工業の閉鎖のために急速に荒廃していくその町の様子に直面した彼は、GM社の会長ロジャー・スミスにその現状を突きつけようと奔走するが……。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sixth Sense", "シックス・センス", "", "", "http://ecx.images-amazon.com/images/I/418%2BmplyhOL._SL210_.jpg", 107, "小児精神科医マルコムはその道の第一人者として活躍していたが、10年前に一人の少年を救えなかったことが心の傷として残り、それが妻アンナとの間に深い溝を作っていた。そんなある日、彼の家に昔の患者だった青年が侵入、彼は銃弾を浴びてしまう。それから数ヵ月後、“復活した”マルコムは８歳の少年コールを担当することになる。何かにおびえ続けるコールの心を、じっくりと解きほぐしていったマルコムは彼から“死んだ人が見える”という驚くべき告白を受ける……。人間の五感を越えた第六感覚をもつ少年と精神科医の交流というヒューマン・ドラマを根底に流しながら、少年の不思議な体験を綴った新感覚のスリラー。抑制の利いたB・ウィリスの演技が光る。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸の名物男 一心太助", "江戸の名物男 一心太助", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "中村錦之助が一心太助と三代将軍家光の二役を演じ分ける痛快娯楽時代劇。ある日、太助は将軍家光の参詣を邪魔してしまった母子を身を持って助けたことから、天下の御意見番こと大久保彦左衛門と主従の関係を結ぶ。その頃、旗本と大名の間に争いが絶えず、太助も騒ぎに巻き込まれていく。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地下鉄（メトロ）に乗って", "地下鉄（メトロ）に乗って", "", "", "http://image.eiga.k-img.com/images/movie/33703/315x210.jpg?4692", 121, "人気作家・浅田次郎の同名小説を堤真一主演で映画化。営業マンの長谷部真次は、仕事帰りの地下鉄の駅で、父が倒れたとの連絡を受ける。気難しく威圧的な父とは高校卒業と同時に絶縁しており、長い間顔を合わせていなかった。地下道を歩きながら、父とケンカして家を飛び出したまま帰らぬ人となった兄を思い出す真次。そんな彼の前を、亡き兄に似た人影が横切る。後を追って地上へ出ると、そこは兄が死んだ昭和39年の東京だった……。", "2006-10-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オシャレ魔女　ラブ and ベリー　しあわせのまほう", "オシャレ魔女　ラブ and ベリー　しあわせのまほう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "オシャレやダンスといった要素を盛り込み、女の子に絶大な人気を誇るカードゲーム“オシャレ魔女 ラブ and ベリー”初の劇場版アニメ。オシャレ魔法学園の入学試験を受けたものの、追加試験を言い渡されてしまったラブとベリー。試験の内容は“1人の女の子を魔法で幸せにする”というもの。人間界で歌手を目指す内気な少女ユミに出会った2人は、彼女に自信を持たせようと奮闘する。ゲスト声優としてお笑いコンビの北陽が出演。", "2007-03-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Boss'n Up", "ボッスン・ナップ", "", "", "http://ecx.images-amazon.com/images/I/51eqSEyS7RL._SL210_.jpg", 90, "人気ラッパーのスヌープ・ドッグが製作総指揮、主演、音楽を手がけたヒップホップ・ムービー。スーパーの店員として冴えない毎日を送っていた青年コーデ。天性のカリスマ性を備え、女性の心をたやすくつかむことのできるコーデは、スーパーでも女性客からすぐに気に入られていた。そんな才能を伝説のピンプ（売春斡旋業）、オレンジ・ジュースに見こまれたコーデは、運命の女・シャルドネとともにピンプとして頭角を現わしていくが……。", "2007-02-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("突然、嵐のように", "突然、嵐のように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "同棲相手の入院費を捻出するため、仲間と恐喝を始めた男。だが、ヤクザの情婦を脅してしまい、恋人を捨てて姿を消す。それから半年後、駅前で二人は偶然にも再会するのだが……。今を精いっぱい生きようとして、試行錯誤を繰り返す若者たちの姿を愛情深く描いた青春映画。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("看護婦のオヤジがんばる", "看護婦のオヤジがんばる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "夜勤が多く、生活も不規則になりがちで、妊娠中絶も多い看護婦。その看護婦と結婚して自分も働きながら育児も引き受けた男の奮戦記。深刻な問題になりがちなテーマを、神山監督は涙あり笑いありの、さわやかであと味の良い佳作に仕上げている。前田吟は1968年「ドレイ工場」以来の主演作品となった。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLEAN，SHAVEN", "クリーン、シェーブン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "分裂症の男の数日間を追った異色スリラー。現実と狂気の世界でさまよいながら、精神に異常をきたしていく男の姿をショッキングな映像で描く。療養所生活から故郷に戻った精神分裂症のピーター・ウィンター。愛娘ニコールの姿はそこになく、彼は娘の行方を探し始めるが、ピーターが訪問する先々で少女の遺体が発見され、警察は連続殺人事件の犯人として彼を追う。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霧の旗", "霧の旗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "柳田桐子は殺人事件の容疑者として逮捕された兄の無実を信じ、高名な弁護士・大塚に弁護を依頼するが、貧しさゆえに相手にされない。そして兄は死刑になってしまう。その時から桐子の、理不尽ともいえる復讐心に火がついた。1977年に山口百恵主演によるリメイクが作られている。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACES　GO　PLACESII", "悪漢探偵2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "E・ツァン監督による、人気シリーズの第２作。S・ホイらおなじみの迷探偵３人を、次から次へと襲う秘密兵器。殺人ロボットあり、ロボット要塞あり。今回は悪役のボスに、日本の倉田保昭が扮し、スケール・アップに協力している。", "1983-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GATE A TRUE STORY", "GATE A TRUE STORY", "", "", "http://image.eiga.k-img.com/images/movie/53863/315x210.jpg?2065", 104, "2度と原爆の悲劇を起こさないと願った、日本のある僧侶たちの祈りの2500キロメートルの行脚を記録したドキュメンタリー。60年間燃やし続けていた広島の原爆で起きた火を、原爆が生まれた場所である アメリカの“トリニティーサイト” へ戻し、そこで消し去ることで負の連鎖の断絶を訴える。ナビゲーターはマーティン・シ−ン、日本版のナレーションは松嶋菜々子。", "2008-07-19", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIMIC", "ミミック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ニューヨークの地下にうごめく、進化した昆虫の猛威を描いたバイオ・スリラー。昆虫学者のスーザンは、流行する疫病の防止のために新種の昆虫“ユダの血統”を開発。３年後、街の地下で絶滅したはずの“ユダ”に似た昆虫の死骸が見つかった。真相を探り始めたスーザンたちは地下に潜入。そんな彼らに、変態を遂げて巨大化したユダが襲いかかり……。迷路のような暗闇の地下世界と、陰影に富んだ映像で恐怖のドラマを演出。巨大なゴキブリを思わせるクリーチャーの造形も不気味この上ない。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rue des plaisirs", "歓楽通り", "", "", "http://image.eiga.k-img.com/images/movie/1623/315x210.jpg?4201", 91, "「髪結いの亭主」「橋の上の娘」のパトリス・ルコント監督の新作。舞台は、45年のパリ最後の娼館。この館で娼婦の息子として生まれ、娼婦たちの身の回りの世話だけをして生きてきた中年男プチ＝ルイ。彼は歌手を夢見る若い娼婦マリオンと出会う。マリオン役は、イブ・サンローランの最期のミューズとして知られる、トップモデル出身のレティシア・カスタ。モーリス・シャバリエ、ミスタンゲットなどのシャンソンが全編を彩る。", "2003-03-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAKE A HARD RIDE", "ワイルドトレイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "８万6000ドルもの大金をテキサスからメキシコまで運ぶことになった二人の男。当然ながらこのニュースは人々に知れわたり、次々と賞金稼ぎの悪党どもがやって来ては襲い、二人がそれを迎え撃つ。イタリア、アメリカ合作の痛快セミ・マカロニ・ウエスタン。大金を狙うガンマンにL・V・クリーフが扮している。", "1975-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沓掛時次郎", "沓掛時次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "一宿一飯の恩義から何のうらみもない六ツ田の三蔵を斬った沓掛時次郎は、三蔵から身重の妻・おきぬと子供の太郎吉を、おきぬの実家に送ることを頼まれ承知する。おきぬは時次郎が憎くて仕方がなかったが……。新国劇の役者が勢ぞろいした日活時代劇の第２作。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAKE OF DEATH", "レクイエム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "最愛の妻を殺された元マフィアの復讐を、ドラマチックに描いたバイオレンス・アクション。アクション俳優ジャン・クロード・ヴァン・ダムが主人公に扮して、得意の肉弾バトルを熱演している。夜のハイウェイを疾走するカーチェイスや、港での銃撃戦などの壮絶な見せ場も満載だ。愛妻をチャイニーズ・マフィアに殺された元ギャングのベン。密入国した中国人少女から情報を得た彼は、仲間とともに敵地に乗り込む。", "2005-03-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　MAGDALENE　SISTERS", "マグダレンの祈り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "俳優ピーター・ミュランがヴェネチア映画祭金獅子賞を得た監督第２作。実在したカトリックの更生施設“マグダレン修道院”で、自由なき日々を過ごした女性たちの苦しみをリアルに描き出す。何の罪も犯していないのに修道院送りにされたマーガレット、バーナデット、ローズ。彼女たちは心の歪んだシスターに“ふしだらな女”と決めつけられ、理不尽な虐待を受ける。刑務所以上に過酷な日々を耐え続けるのだが……。", "2002-01-01", "3,33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大日本帝国", "大日本帝国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 181, "大日本帝国と呼んだ昭和11年から20年の敗戦までの時代を、国家、指導者、庶民の３面から描いた２部からなる大作。三浦、あおい、西郷ら兵士に扮する俳優たちがいずれも戦争の悲痛さを好演しているが、作品題名から、戦争賛美だとして製作・上映反対運動もあった。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Valhalla Rising", "ヴァルハラ・ライジング", "", "", "http://image.eiga.k-img.com/images/movie/57793/315x210.jpg?3200", 93, "「ドライヴ」（2011）が第64回カンヌ国際映画祭監督賞を受賞し、一躍世界の注目の的となったデンマーク出身のニコラス・ウィンディング・レフン監督が、09年に手がけたバイオレンスアクション。北欧神話をベースに片目の戦士ワン・アイの戦いを描く。主演はレフン監督のデビュー作でカルト的人気の「プッシャー」3部作に続き、「007 カジノ・ロワイヤル」のマッツ・ミケルセンが務める。超自然的な力を持つ片目の戦士ワン・アイは、スコットランド人の族長バデルのもとに捕らわれていたが、バデルを殺して脱走する。旅の途中で聖地エルサレムを目指すバイキングの船に乗り込み、深い霧に包まれた航海の果てに見知らぬ大陸へたどりついたワン・アイは、そこで本当の自分を知る。", "2012-04-07", "18,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冷静と情熱のあいだ", "冷静と情熱のあいだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "江國香織と辻仁成の共作小説を映画化。順正は絵画修復士の修行のために滞在中のイタリア・フィレンツェで、つらい別れをしたかつての恋人、あおいに再会。平静を装うが、会うたびに熱い想いが蘇ってくる二人。そして“30歳の誕生日にフィレンツェのドゥオモで会おう”という10年前に交わした約束をお互いに思い出すのだった……。芸術と歴史の国イタリアでふんだんにロケを行い、エンヤのヴォーカルに乗せて贈るラブ・ストーリー。若い女性の共感を得て、大ヒットを記録した。映画初出演にして初主演の竹野内豊と、日本でも人気を博す香港のトップスター、ケリー・チャンの共演も話題になった。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Don't Be Afraid of the Dark", "ダーク・フェアリー", "", "", "http://image.eiga.k-img.com/images/movie/57113/315x210.jpg?2145", 100, "田舎の屋敷に巣食う魔物の恐怖を描いたTVムービー「地下室の魔物」（1973）を、「パンズ・ラビリンス」のギレルモ・デル・トロの製作・脚本でリメイクしたサスペンススリラー。欧米に伝わる邪悪な妖精“トゥースフェアリー”を題材に、心を閉ざした少女とその家族が体験する未知の恐怖を描く。両親が離婚し、父アレックスと父の新しい恋人キムとともにロード・アイランドの古い屋敷に引越してきた少女サリーは、封印されていた秘密の地下室を見つける。地下室から聞こえる謎の声に導かれ、サリーは得体の知れない妖精を呼び起こしてしまうが……。「バットマン」の作画なども手がけるコミック作家のトロイ・ニクシーが初監督を務める。", "2012-01-21", "1,11,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆弾男といわれるあいつ", "爆弾男といわれるあいつ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "現金輸送車強奪事件を旭とぼん太のデコボコ・コンビが捜査に乗り出すが、事件の裏には意外な人物が……。長谷部安春の「俺にさわると危ないぜ」に続く監督第２作でシリーズの最高作。藤竜也がチョイ役で出演。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Il conformista", "暗殺の森", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　アルベルト・モラビアの「孤独な青年」を原作に、過去の罪に捕われファシストにならざるを得なかった男の悲哀を描く。幼い頃、自分を犯そうとした男を射殺してしまったマルチェッロは、今だに罪の意識が消えずにいた。ある日、彼に反ファシズムのクアドリ教授暗殺の命が下る。好奇の目に晒されながらも優雅に踊る女同士のダンスシーン、雪の降り積もった森での暗殺シーンなどベルナルド・ベルトルッチならではの映像美が見事。", "1972-09-02", "4,2,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春の鐘", "春の鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "古陶磁器の美の世界に魅せられ、奈良で美術館館長をしている男と陶工家の娘、その男に顧みられず肉欲に溺れる妻と医師、という二組の不倫の愛を軸に、男女の情念の葛藤を描いた大人のラブロマンス。相反する二人の女を、三田・古手川が好演。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUNNY FARM", "ファニー・ファーム 勝手にユートピア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ユートピアのような田舎での生活を夢見て都会からやって来た作家夫婦が、想像とあまりに違う田舎の生活にアタフタするコメディ。本が売れたのを機に、田舎へ引っ越すことを決意した男。だが、引っ越し当日から災難続きで、華麗な暮らしの夢はだんだん崩れていく。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女教師 私生活", "女教師 私生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "独自の唯美的な映像感覚で初期ロマンポルノのエース的存在だった田中登が、女性による一般公募入選作のシナリオを映画化。奔放でスキャンダラスな女教師の性生活を描いたものだが、官憲を意識した修整のため公開が延びた。アグネス・チャンの『ひなげしの花』が流れる中でのセックスシーンが話題になった。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白夫人の妖恋", "白夫人の妖恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "豊田四郎監督・円谷英二特技監督による、中国伝説『白蛇伝』を原作にした東宝のカラー特撮映画の第１作。西湖のほとりの住む若者は謎の美女に恋するが、彼女は白蛇の化身であった……。円谷英二による迫力満点の特撮シーンが見もの。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DR. GOLDFOOT AND THE BIKINI MACHINE", "ビキニ・マシン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ビキニ美女姿のロボットが男たちを悩ませるセクシャル・コメディの怪作。プレイボーイもエリート・スパイもきりきり舞いさせられる。フランケンシュタイン博士ならぬ悪の発明博士は、ビキニ姿のロボットを密かに製造していた。ハンサムなトッドも“彼女”に求婚する始末だ。スパイのクレイグはロボット製造疑惑を追って、トッドと共に博士の隠れ家に潜入する。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("3:10 To Yuma", "決断の3時10分", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "タイムリミット型のサスペンスを盛り込んだウエスタン。「真昼の決闘」と同じく、50年代西部劇に新しいテイストを取り入れた異色作だ。G・フォードのアウトローぶりにも注目。アリゾナで、駅馬車を襲撃する強盗団のボスを保安官が逮捕した。だが、遠くにある刑務所まで護送する必要が……。そこで貧農のダンがその仕事を引き受けたが、ボスの子分たちが行く手を阻む。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラQザ・ムービー 星の伝説", "ウルトラQザ・ムービー 星の伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "日本のTV・SFを代表する名作『ウルトラQ』を、新しいスタッフ・キャストで20数年ぶりに復活させた話題作。自然破壊の警鐘を鳴らす古代伝説の謎を追う、３人の活躍を描いていく。初めて“ウルトラQ”を手掛ける実相寺演出は、観念的すぎたといえるだろう。TVでおなじみのテーマ曲が流れないのが残念。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The School of Rock", "スクール・オブ・ロック", "", "", "http://image.eiga.k-img.com/images/movie/1163/315x210.jpg?3747", 108, "落ちこぼれのミュージシャン、デューイは、良家の子女が通う小学校へ代用教師として潜り込み、毎日子供たちにロックを教え込む。あげくの果てに、学校に内緒でバンド・コンテストにも出場しようと企むが……。監督は「恋人たちの距離」「ウェイキング・ライフ」のリチャード・リンクレイター。主演は実際にバンド活動も行っているジャック・ブラック。劇中にはレッド・ツェッペリン、ザ・フー、クリーム、ドアーズほか、ロックの名曲が多数登場。", "2004-04-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おったまげ人魚物語", "おったまげ人魚物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "「おったまげ村物語」に次ぐ堀内真直監督の喜劇で、主演も前作同様、伴淳三郎が起用されている。ある平和な漁村で起こる男女関係の大騒動を伴淳三郎扮する画家があれやこれやの手管を使って収拾をつける様子を描く。色事師を演じる渥美清が笑わせる。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　MOOMIN　FAMILY", "ムーミン〈パペットアニメーション〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 630, "日本ではTVアニメとして人気の“ムーミン”をパペット（人形）アニメーションで描き出す温もり豊かな短編集。原作者でもあるフィンランドの作家トーヴェ・ヤンソンが監修に携わり、色彩豊かな大自然を背景に物語が綴られていく。人気キャラクター、スナフキンやニョロニョロなど、個性豊かな登場人物たちを一手に引き受けた岸田今日子の芸達者な声優ぶりも見どころ。VHSは36話までがレンタル、DVDは劇場公開された36話を含む全78話を収録したBOX仕様で発売された。", "1979-01-01", "42,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ODD　COUPLEII", "おかしな二人2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "N・サイモンが自らの舞台劇を脚色、J・レモン、W・マッソーの共演で映画化したシチュエーション・コメディの傑作「おかしな二人」の30年ぶりの続編。几帳面なフィリックスとずぼらなオスカー。30年前にひょんなことから共同生活を送り、いがみ合い、反発していた二人が再会することになる。なんとフィリックスの娘とオスカーの息子が結婚することになったのだ。ロサンゼルスの空港で落ち合った二人は式が行われるサンマリーノという小さな町へ向かうが、次から次へと災難に遭遇するはめに……。J・レモンとW・マッソーが息の合った掛け合いを見せ、ほのぼのとした笑いを生み出している。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("重役の椅子", "重役の椅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "源氏鶏太の小説を“サラリーマン出世太閤記”シリーズの筧正典が映画化。極東商事の総務部長が急死し、総務部次長の船田は葬儀の準備中に彼がやり残した仕事を片づけなければならないほどの忙しさ。そのなか彼は部長の愛人と出会い、妻子がありながら彼女の友人、英子と恋に落ちてしまう。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本沈没", "日本沈没", "", "", "http://ecx.images-amazon.com/images/I/21BBFJ2PYYL._SL210_.jpg", 144, "小松左京の同名ベストセラーを映画化し、社会現象を巻き起こした特撮パニック大作。小笠原諸島のとある島が突如として姿を消した。小野寺の操縦する潜水艇に乗って調査に向かった田所博士は、海底に重大な異変が起きているのを発見し、近いうちに日本が海底に沈むという恐るべき予測にたどり着く。やがて、日本各地で大地震や火山の噴火が起こりはじめ……。黒澤明ら名匠たちの下で経験を積んだ森谷司郎がメガホンを取る。", "1973-12-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴト師株式会社 悪徳ホールをぶっ潰せ！", "ゴト師株式会社 悪徳ホールをぶっ潰せ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "悪徳パチンコ店潰しを大金で請け負う非合法集団“ゴト師株式会社”の活躍を痛快に描いた、同名劇画の映画化。美人社長の依頼で対立するパチンコ店の脱税を暴いた彼らが、実は依頼人の罠にはまったことを知り、逆襲に乗り出す。一度はのぞいてみたいパチンコ業界のリアルな裏事情や、裏技の数々を満載。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEN OF WAR", "メン・オブ・ウォー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "南太平洋の孤島の鉱脈を島民から奪うため、財団が傭兵を島に送る。しかし傭兵たちは温かい島民の迎えに、逆に島を守ることを決心。財団側が再び送り込んだ戦争のプロたちを倒すため、傭兵軍は立ち上がった。インディーズの旗手、J・セイルズが初めてアクション作品の脚本を担当。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Two Mules for Sister Sara", "真昼の死闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "　メキシコの荒地。流れ者の凄腕ガンマン、ホーガンは暴漢に襲われていた尼僧のサラを助ける。メキシコの革命軍に雇われたホーガンはサラとともに旅することに。フランス駐屯軍に詳しいサラは怪我を負ったホーガンを助け、さらにはフランス軍の補給列車を爆破する手助けまでする。そんな中、サラの意外な正体が発覚し……。クリント・イーストウッドとシャーリー・マクレーンのやり取りがユーモラスで楽しめる。", "1970-01-01", "1,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONCORDE AFFAIR", "コンコルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "夢の超音速旅客機コンコルド導入をめぐるシンジケートの陰謀と、追跡するTVレポーターの活躍を描いたパニック映画。英国航空が全面協力し、各種工場・研究所を開放、技術面でもすべての資料・フィルムを提供したことで、画面はリアリティー抜群。", "1979-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火火〈ひび〉", "火火〈ひび〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "女性陶芸家の先駆者であり、息子の白血病を機に骨髄バンク運動を始めた実在の女性・神山清子。実話を基に、芸術家として母として女として、力強く生きる彼女の半生を描いた渾身の一作。清子には田中裕子が扮し、極貧の中でもひるまず凛然と生きる女性像を体当たりで熱演している。陶芸家の清子は女手一つで二人の子供を育てながら、長年の夢だった穴窯による自然釉を成功。しかし、息子が白血病で倒れ……。", "2005-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤いダイヤ", "赤いダイヤ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "“赤いダイヤ”＝あずき相場で一獲千金を狙う人々を描いた喜劇。米軍の放出物資で大損した若きブローカー木塚慶太は、あずきの相場師・森弦に助けられて再起を誓う。ところがお金に弱い、美女にはヒジ鉄、宿敵“売りの松崎”にもまんまと一杯食わされ、慶太は一世一代の大バクチを打つハメに……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gangs of New York", "ギャング・オブ・ニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/1073/315x210.jpg?1393", 168, "マーティン・スコセッシ監督が70年代に同名ルポに出会って以来、映画化を希望してきた作品が遂に完成。同時多発テロの影響などで公開が1年延期となった話題作。1846年、移民たちがそれぞれギャング団を結成し、縄張り争いに明け暮れるニューヨーク。目の前で父を殺された少年アムステルダムは、15年後、父を殺したギャング団のボス、ビルへの復讐を果たそうとする。街もまた、大きな変革期を迎えようとしていた。", "2002-12-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEUF MOIS", "愛するための第9章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "キャリアウーマンのマチルドと精神分析医のサミュエルは、自由な同棲生活を送るカップル。ところが突然、マチルドの妊娠が発覚。いつまで経っても父親になる決心がつかないサミュエルに愛想をつかしたマチルドは、友人宅へと移り住む。そんなある日、サミュエルは妊婦の夫が体験できる疑似陣痛を体験。マチルドへの愛の深さを確信し、二人は一緒に生活を始め、やがて臨月を迎えるが……。理想的な同棲生活を送るカップルに起こった妊娠騒動をコミカルに描いたラブ・コメディ。ヒュー・グラント主演で製作された「９か月」は、本作をリメイクしたもの。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nightwatching", "レンブラントの夜警", "", "", "http://image.eiga.k-img.com/images/movie/53123/315x210.jpg?4621", 139, "「コックと泥棒、その妻と愛人」などの鬼才ピーター・グリーナウェイ監督が、画家レンブラントが転落するきっかけとなった名画“夜警”の謎に挑むミステリー。17世紀オランダ。肖像画家として成功を収めたレンブラントは、富と名声、さらに待望の男子まで授かり、まさに人生の絶頂にいた。ある日、彼の元にアムステルダム市警団の肖像画の依頼が舞い込む。下調べのために団員たちに近づいたレンブラントは、金と欲望にまみれた彼らの実態を知る。", "2008-01-12", "10,2,7,42,70,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("交渉人 THE MOVIE タイムリミット　高度10000mの頭脳戦", "交渉人 THE MOVIE タイムリミット　高度10000mの頭脳戦", "", "", "http://image.eiga.k-img.com/images/movie/54773/315x210.jpg?2953", 0, "2008年に放映された米倉涼子主演のTVドラマ「交渉人／THE NEGOTIATOR」の劇場版。TV版のキャストに加え、反町隆史、林遣都、成宮寛貴、柳葉敏郎、橋爪功、津川雅彦がゲスト出演する。現場の最前線で活躍する女交渉人の宇佐木は、偶然空港で出会った不遇な青年を追い、無理やり飛行機に乗り込む。しかし、その飛行機がハイジャックされてしまい、宇佐木は凶悪なハイジャック犯らと壮絶な頭脳線を繰り広げる。", "2010-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUT OF REACH", "ICHIGEKI／一撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "東欧の人身売買組織と戦う元敏腕エージェントの活躍を描いたクライム・アクション。東欧の人身売買の実態に心を痛めたというアクション俳優スティーヴン・セガールが、自ら企画を立ち上げ、怒りの奮闘を熱演。マーシャルアーツはもちろん、剣のバトルにも挑んだ体当たりの見せ場の連続に注目。文通相手である少女の安否を気遣い、ワルシャワに飛んだ元秘密工作員ランシング。そこで人身売買組織の存在を知った彼は、この巨悪に決死の戦いを挑んでいく。", "2005-02-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAVE THE TIGER", "セイヴ・ザ・タイガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "服飾会社を興し成功した、元ベトナム帰還兵のハリー・ストーナー。今やビバリーヒルズに邸宅を構えるまでになった彼だが、毎朝目覚めるたびに疲労を感じる年になっていた。しかも会社は経営危機。そんな時、彼はふと車に乗せた娘に、違った世界が広がるのを感じる……。J・G・アヴィルドセン監督がじんわり描く一人の男の生き様、そしてベトナム戦争後のアメリカ現代史。哀愁を漂わせたJ・レモンの名演も捨て難い魅力をにじませている。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霊幻少女／LING FANG SAOG NEE", "霊幻少女 帰ってきたテンテン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "キャラクター・グッズまで売られ、大ブームを起こした“キョンシー”シリーズ。その中でアイドルとなったテンテンちゃんが、スクリーンにカムバック。魔界からよみがえった最強最悪の魔教主、クマラを倒すため、テンテンと師匠の金爺さんが立ち上がるSFX伝奇アクション。", "1992-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLINDMAN", "盲目ガンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "盲目ながらもライフルの腕前は一流の男が、炭坑夫の花嫁50人を鉱山に届ける仕事を請け負う。しかし彼はまんまと山賊ドミンゴ一味の罠にはまって花嫁たちを奪われ、捨て身の奪回作戦を繰り広げることに。マカロニ・ウエスタンならではの奇想天外な設定が楽しい娯楽アクション。主演のT・アンソニーが脚本にも参加し、元ビートルズのR・スターが山賊のボスの弟に扮して悪役をこなしている。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春ロマンスシート 青草に坐す", "青春ロマンスシート 青草に坐す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "美空ひばりが主演した家族愛と男女愛をミックスしたラブ・ストーリー。船乗りの新庄啓作と妻、文子には二人の子があった。娘、翠は快活な女学生。彼女には雄一というボーイフレンドがおり、いつしか清純な恋が芽ばえていた。ある日テニスの試合があり、翠と雄一は大阪に行く。そして翠は、父の啓作に小泉千代という愛人がいるのを知るのだった。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("那山　那人　那狗", "山の郵便配達", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1980年代の中国南方の険しい山岳地帯。初老の郵便配達人は、その厳しさから足を患い長年勤めてきた仕事を息子に引継ぐことに。それは、一日40キロもの山道を３日間もかけて郵便物を集配する過酷な仕事。息子は新しい仕事を一人で始めたかったが、父の最後の同行を拒否できずにいた……。緑あふれる山々を背景に、それぞれの思いで郵便物を運ぶ親子の姿が印象深い。派手さこそないが、じんわりと心に響く秀作。チャン・イーモウ監督の「あの子を探して」が圧倒的優位の中、1999年中国金鶏賞作品賞、主演男優賞に輝いた。", "1999-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOOLS RUSH IN", "愛さずにはいられない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ニューヨークのナイトクラブ経営会社で働くアレックスは、新店舗をオープンさせるためにラスベガスにやって来る。到着早々、彼はメキシコ女性のイザベルと惹かれ合い、一夜をともにするが、翌朝、彼女はベッドから消えていた。それから３ヵ月後、イザベルがアレックスの前に現れ、子供ができたと告げる。けなげな彼女の姿に惚れ直した彼は結婚を決意。二人は新婚生活を始めるが……。知り合って間もないニューヨーカーとメキシカンのカップルがカルチャー・ギャップを乗り越え、運命と愛の力を信じるようになるまでを描くハートウォーミングなラブ・ストーリー。「デスペラード」の妖艶な美女役で注目を浴びたS・ハエックが、恋を貫こうとする一途な女性を可愛らしく演じている。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ДРАМА НА ОХОТЕ", "狩場の悲劇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "一人の女を真剣に愛する貧しい男の思いを描いた、サスペンス・タッチの人間ドラマ。裕福な暮らしに憧れる貧しい娘オーレニカは村の領主の執事と結婚。だが、愛のない結婚生活は破局を迎え、オーレニカは領主との情事に溺れていく。そんなある日、森の中で何者かによって殺された彼女の死体が発見された……。", "1978-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎心の旅路", "男はつらいよ・寅次郎心の旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "これまで日本各地を旅してきた寅次郎が、初めて海外へ飛んだシリーズ第41作。田舎の駅で心身症のサラリーマンを元気づけたのをきっかけに、寅次郎は彼の出張先ウィーンへ。そこでガイドの久美子に出会い、いつもの恋騒動が巻き起こる……。本作で、竹下景子が３作目のマドンナ役に挑戦。ウィーンの観光名所を巧みに生かした寅さん異文化奮闘記。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beauty うつくしいもの", "Beauty うつくしいもの", "", "", "http://image.eiga.k-img.com/images/movie/54403/315x210.jpg?3069", 109, "長野県・伊那谷で2世紀以上に渡って受け継がれている村歌舞伎をテーマに、激動の時代の中で歌舞伎に生涯を捧げた2人の男の絆を描いた人間ドラマ。昭和10年、伊那路村で暮らす少年・半次は、村歌舞伎で舞踊を舞う雪夫の姿に感動し、自身も歌舞伎役者の道へ進むことを決意する。やがて彼は雪夫と共に村の看板役者となるが、太平洋戦争真っ只中の昭和19年、2人に召集令状が届き……。上方歌舞伎の名門・松嶋屋の片岡孝太郎と片岡愛之助が2人の主人公を熱演。", "2009-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gardiens de l'ordre", "スフィンクス（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/55343/315x210.jpg?2454", 120, "「スパニッシュ・アパートメント」で注目を浴びたセシル・ドゥ・フランスが主演するクライム・サスペンス。監督は「ブルー・レクイエム」のニコラ・ブークリエフ。警官のシモンとジュリーは、理由もなく殺人を犯したエリート男性に怪我を負わせてしまう。その男により虚偽の告発を受けた2人は、自らの潔白を証明するため、ある薬物の捜索を開始する。フランス映画際2010にて上映。", "2010-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PIKOO", "ピクー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 26, "インドの巨匠、S・レイ監督が、フランスのテレビ局の依頼で製作した短編。インド人の上流家庭の一日をスケッチ、そのなかに母と父の不和、母の情事、祖父の死などを盛り込み、ピクーという少年の目を通して暗示的に映し出している。", "1981-01-01", "2,21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA JETE´E", "ラ・ジュテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 29, "第三次世界大戦後のパリ、廃墟と化した地上を逃れ、生存者たちは地下に住処を求めていた。彼らは人類の滅亡を危ぶみ、時間を思いのままにすべく日夜実験を繰り返している。今回実験台に選ばれたのは過去に強い執着を持つ一人の男。彼は、少年の日に見たオルリー空港での惨劇、そしてある女の表情を忘れられずにいた……。全編静止画のみで撮り上げられた“ヌーヴェル・ヴァーグ右岸派”C・マルケル監督によるSF映画の傑作。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("けものの眠り", "けものの眠り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "菊村到の同名小説を鈴木清順がセミ・ドキュメンタリー・タッチで描いた異色のアクション映画。勤勉実直な初老のサラリーマンが麻薬組織に買収され、麻薬密輸に関与してしまう。やがて、だまされていたことに気づいた彼は単身アジトに乗り込んでボスを射殺、アジトに火をつけ自らも炎の中に身を投じる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デッドクリフ", "デッドクリフ", "", "", "http://image.eiga.k-img.com/images/movie/55813/315x210.jpg?1790", 85, "ロッククライミングに訪れた若者たちが想像を絶する恐怖に見舞われるフランスのホラー。大自然に囲まれた山脈へロッククライミングにやって来た5人の男女。彼らは上級者カップルのフレッドとカリーヌに先導されて断崖絶壁を登りはじめるが、初心者のルイックだけは恐怖と戦いながら仲間たちについていくのがやっとだった。そんななか、山奥に潜んでいた正体不明の大男が彼らに突然襲いかかる。", "2011-01-08", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("READY TO FIGHT", "仁義なき街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "マカロニ・ウエスタンのスター、G・ジェンマ主演のイタリアン・フィルム・ノワール。仲間と組織に裏切られて消息を絶っていた男ロッキーは、一匹狼の復讐鬼と化して街へ戻ってきた。そして昔の友人の忠告も聞かずに彼は目に見えない巨大な組織へ捨て身の戦いを挑んでいく。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cassandra's Dream", "ウディ・アレンの夢と犯罪", "", "", "http://image.eiga.k-img.com/images/movie/55053/315x210.jpg?3961", 108, "「マッチポイント」「タロットカード殺人事件」に続くウッディ・アレン監督のロンドン3部作の最終章。主演にユアン・マクレガーとコリン・ファレル、共演に「フィクサー」のトム・ウィルキンソンら。ホテルへの投資で成功を収めようする野心家の兄イアン（マクレガー）と、酒とギャンブルを愛する気ままな弟テリー（ファレル）は、それぞれの理由で経済的に追い詰められていた。そこに資産家の叔父が現れ、資金援助を条件に元同僚の暗殺を依頼してくる。", "2010-03-20", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEEPERS CREPERS", "フランシス・フォード・コッポラ／ジーパーズ・クリーパーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "鬼才コッポラの製作会社ゾーエトロープの製作によるホラー。春休みを利用して車で帰省する姉弟が田舎町を飛ばしている途中、謎のトラックに追撃される。からくも逃げ延びた姉弟は、トラック運転手の隠れ家で無数の死体を発見。警察に駆け込んだ彼らは、23年に一度、行方不明者が続出するという都市伝説を知らされ……。「激突」を思わせる路上のサスペンスから一転、後半はモンスター・ホラーへ。タイトルとなったスタンダード・ナンバーが効果的にフィーチャーされ、不気味なムードを煽る。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VIE DE FAMILLE", "家族生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "男には離婚歴がある。現在の妻には連れ子がいて、母娘は彼の存在でいつも気まずくなっていた。その日も彼のひげそり用のカミソリの所在をめぐって口論となり、娘はヒステリーを起こして家を出て行ってしまった。男はそれから前妻の家へ行き、寄宿舎に入るという実の娘を、プラド美術館へ行こうとスペインへ誘い出す。旅の間、父は娘にある課題を出す。娘がシナリオを考え、父がそれに従ってビデオ・カメラで撮影をしようというのだ。娘は様々なストーリーを父に聞かせるが、次第に物語は父と娘の関係に深く入り込んでくる。父の存在を否定しようとする娘に、彼は最後の課題を伝える。ビデオ・カメラの前に彼女を座らせ、父に対する真実の告白をさせようというのだ。そして彼女は泣きながら真実を語り始めた……。愛から逃亡しようと試みながらも、結局その愛から逃げ出し得ない人々の弱さと激しさを描き続けて来たJ・ドワイヨンの一つの頂点ともいえる傑作。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　COUNT　OF　MONTE　CRISTO", "モンテ・クリスト伯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "『巌窟王』の別名でも知られる、フランスの文豪アレクサンドル・デュマの同名小説の映画化。19世紀のパリを舞台に、親友フェルナン・モンデーゴの陰謀により、13年もの間、牢獄に幽閉されていたエドモン・ダンテスの復讐物語を壮大なスケールで描く文芸大作。卑劣で鼻持ちならない金持ちの子息フェルナンを、「メメント」のG・ピアースが好演している。ビデオ、DVDタイトルは「モンテ・クリスト−厳窟王−」。", "2002-01-01", "3,33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHTFLYERS", "デーモン・ギャラクシー 魔女伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "時は21世紀。知的生物ボルクリンの調査のため、８人のエキスパートがアバロン宇宙空港に集結、ナイトフライヤー号に乗り込んだ。しかし、その宇宙船には不思議なことが多々あった。まず、彼らの前に姿を現すはずの船長はホログラフの映像のみ。そして、各ブースの隅には、彼らを監視しているかのように赤いライトがともっていた。乗組員たちは、調査を始め、ナイトフライヤー号が一つの生命体であることを突き止めるのだが……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOLEIL ROUGE", "レッド・サン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1870年の西部を舞台に、日米修好の任務を帯びた日本国大使の侍と、二人のガンマンが宝刀をめぐって繰り広げる異色のウエスタン。世界の３大スターの初顔合わせで大ヒットした。日本刀対ピストルという奇想天外な対決が、最大の見どころ。", "1971-01-01", "2,4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("D2: THE MIGHTY DUCKS", "D2 マイティ・ダック　飛べないアヒル2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "落ちこぼれ少年アイスホッケー・チーム“ダックス”の快進撃を描いたスポ根コメディ「飛べないアヒル」の続編。ダックスを奇跡の優勝に導いたゴードンは、現役のリンクに復帰したが、成功を目前に無念の負傷。傷心を抱えて故郷に帰った彼を待っていたのは、チームUSAのコーチ就任の誘いだった。さっそくダックスの選手たちに加えて全米から新メンバーを集め、猛特訓を開始する。世界大会は幸先のよいスタートを切ることができ、ゴードンもチームもスター気取りだったが、慢心から３回戦で思わぬ惨敗。チームは最後の１戦にすべてを懸けるのだった。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MOUNTAIN MEN", "ワイオミング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1830年代のロッキー山脈を舞台に、過酷な自然にもめげず、動物の毛皮を獲って暮らす山男たちを描く。インディアンの抗争に巻き込まれ“人間狩りゲーム”をけしかけられたり、インディアンの娘と恋をしたり……。スリルとユーモアにあふれた山岳西部劇。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOBSON'S CHOICE", "ホブスンの婿選び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1890年代。ランカシャーの典型的なホブスン靴店のオヤジはヤモメ、大酒飲みで、守銭奴ときている。３人娘たちは父親に愛想をつかし、オヤジと別れて大成功。オヤジもやがて悔い改める。ホブスンに扮したロートンが楽しいかぎりの名演を見せるコメディ。", "1954-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUNGO 日本文学シネマ　魔術", "BUNGO 日本文学シネマ　魔術", "", "", "http://ecx.images-amazon.com/images/I/51YXh4PG3tL._SL210_.jpg", 30, "芥川龍之介が実在の魔術師をモデルにつづった異色短編を、「フリージア」の熊切和嘉監督が塚本高史主演で映像化。ある時雨の降る晩、若き魔術師ミスラの家を訪れた竜一。ハッサン・カンという婆羅門の秘宝を習得した魔術師の技に感嘆した竜一は、自分にも教えてくれるよう頼みこむ。そんな竜一に、まずは欲を捨てるよう話す魔術師だったが……。文豪たちの短編を映像化したテレビドラマ「BUNGO」シリーズの1本。", "2010-05-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAWNMOWER MAN2 BEYOND CYBERSPACE", "バーチャル・ウォーズ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ピアース・ブロスナンが主演した「バーチャル・ウォーズ」（1992)の続編。バーチャル・リアリティーの人体実験が生んだ青年ジョーブは瀕死の重体からよみがえり、ロサンゼルスにあるバーチャル・ライト社に入社。社長ウォーカーが巨大なシステムを作り、インターネットを通じて世界征服をたくらむ計画を知る。これを利用して自分が支配者になろうとするジョーブは、システムに必要なチップの開発者トレイスと会うが野望を見抜かれ、二人は死闘を繰り広げていく。リアルでスケール感のあるCG描写も見もの。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAIRYTALE−A TRUE STORY", "フェアリーテイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "二人の少女が撮った写真に妖精が写っているとして、研究者の間で論争を巻き起こした1917年の“コティングリー妖精事件”をもとにしたハートフルなファンタジー。第一次大戦下のイギリス。８歳の少女フランシスは、父が戦地で行方不明となり、12歳の従姉妹エルシーの家にやって来る。秘密の森で遊んでいた二人はある日、妖精を写真に収めることに成功する。大人たちは信じなかったが、作家のコナン・ドイルはそれを本物と確信し、誌上で発表。二人はたちまち世間の注目の的になってしまい……。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔法遣いに大切なこと", "魔法遣いに大切なこと", "", "", "http://image.eiga.k-img.com/images/movie/53643/315x210.jpg?2687", 100, "アニメや小説などでヒットした山田典枝が原案の青春ファンタジーを映画化。監督は「落語娘」「櫻の園」の中原俊、主演に12代“リハウスガール”の山下リオ、「天然コケッコー」「ブラブラバンバン」の岡田将生。“魔法遣い”が当たり前に存在する日本、ソラと豪太は魔法士になるための魔法士研修で出会う。魔法をうまく使えない豪太はソラに反発していたが、根気強く魔法を教えてくれるソラの気持ちが通じ、2人は恋に落ちるのだが……。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きんぴら", "きんぴら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "金曜日の夜８時、TVの電波ジャックをする奴が現れた。この“きんぴら（金曜日のぴらにあ）”と呼ばれる海賊放送団と、違法電波取締官との戦いを描く。取締官に仲村トオル、“きんぴら”のオーナーに大竹しのぶを配し、ニュータイプの活劇が展開する。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pripyat", "プリピャチ", "", "", "http://image.eiga.k-img.com/images/movie/57743/315x210.jpg?3146", 100, "「いのちの食べかた」（2005）のニコラウス・ゲイハルター監督が、1999年に発表したドキュメンタリー。チェルノブイリ原子力発電所事故から12年が過ぎた98年、原発から約4キロに位置する街、プリピャチで暮らす人々の姿をとらえた。86年の事故以降、原発の周辺30キロは立ち入り制限区域となったが、生まれ育った場所で生きたいとプリピャチに戻ってきた老夫婦がいた。放射能の危険を知りながらも、自分たちの畑で耕した作物や川でとれる魚を食べて生活するプリピャチの人々のインタビューを、ナレーションや音楽を排したモノクロ映像でつづる。", "2012-03-03", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hard Goodbyes : My Father", "パパにさよならできるまで", "", "", "http://image.eiga.k-img.com/images/movie/33863/315x210.jpg?4831", 114, "1969年のギリシャを舞台に、父親の死を乗り越えて成長していく少年の姿を描いた感動作。アテネに住む10歳の少年イリアスは、父親と一緒にアポロ11号の月面着陸の中継を見ることを楽しみにしていた。ところが、父親は交通事故で帰らぬ人となってしまう。イリアスは大好きな父親の死を受け入れることができず……。主演のヨルゴス・カラヤニスは、ロカルノ国際映画祭主演男優賞を史上最年少で受賞するという快挙を成し遂げた。", "2007-02-24", "27,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒーローショー", "ヒーローショー", "", "", "http://image.eiga.k-img.com/images/movie/55253/315x210.jpg?4317", 134, "「パッチギ！」の井筒和幸監督が、お笑いコンビ「ジャルジャル」の後藤淳平、福徳秀介を主演に描く青春映画。目標もないまま自堕落な生活を送る専門学生のユウキは、ヒーローショーの悪役バイトを始めることにする。ある日、バイト仲間のノボルがユウキの先輩・剛志の彼女を寝取ったことで、ヒーローショーの舞台上で2人は殴り合いを始めてしまう。その後も剛志はユウキも含め悪友たちを招集し、ノボルをゆすろうとするが、ノボルも自衛隊上がりの悪友を集め報復に出る。====================「ヒーローショー」の頭文字“HEROSHOW”から始まる、8サイト連動キャンペーン8文字のキーワードを集め、レアプレゼントをゲットしよう！eiga.comのキーワードは　「春」詳しくはオフィシャルサイトキャンペーンページへ！====================", "2010-05-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SR サイタマノラッパー2 女子ラッパー☆傷だらけのライム", "SR サイタマノラッパー2 女子ラッパー☆傷だらけのライム", "", "", "http://image.eiga.k-img.com/images/movie/55383/315x210.jpg?2916", 95, "ラッパーを目指す田舎の若者の奮闘を描いた青春映画「サイタマノラッパー」の続編。本作では群馬の山奥を舞台に、女子ラッパーたちの苦悩と成長を描く。高校時代はラップグループを作るほどヒップホップ音楽に夢中だったアユムも、卒業後は群馬の実家で家業を手伝いながら、退屈な毎日を過ごしていた。そんなある日、アユムはかつてのラッパー仲間ミッツらと再会し、一夜限りのライブを実現させようとする。しかし、仕事や結婚などの現実的な問題が、彼女たちをどんどん追い詰めていく。", "2010-06-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OLIVIER OLIVIER", "オリヴィエ オリヴィエ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "おつかいに出かけたまま、謎の失踪を遂げた少年オリヴィエ。彼を溺愛する母親は半狂乱になり、長女のナディーヌにあたり散らす始末。父はアフリカに出張で出かけ、家庭は崩壊してしまった。それから６年後、オリヴィエは男娼窟で発見される……。女流監督A・ホランドによる家族の愛憎劇。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おいら女蛮（スケバン）", "おいら女蛮（スケバン）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "永井豪原作によるお色気アクションコミックを、「猫目小僧」の井口昇監督が実写映画化。美少女のようなルックスにコンプレックスを抱え、ケンカばかり繰りかえしてきた不良少年・女蛮子（すけ・ばんじ）。そんな息子を見かねた父親により儀亜留（ギャル）学園に女子高生として転入させられた蛮子は、お嬢様女子高生の市持子と仲よくなるが……。主演を務めるのは「くりいむレモン　亜美の日記」の亜紗美と、「18禁」の桃瀬えみる。", "2006-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("寒椿", "寒椿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "昭和初期の高知。博打狂いの父のために陽暉桜に売られた芸妓、牡丹をめぐる人間模様を、高知を二分する激しい選挙戦を絡めて描く。「鬼龍院花子の生涯」「夜汽車」などでおなじみの宮尾登美子の原作の映画化。南野陽子が牡丹役をまさに体当たりの演技で演じる。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Eye", "アイズ", "", "", "http://image.eiga.k-img.com/images/movie/53453/315x210.jpg?2419", 97, "タイのダニー＆オキサイド・パン兄弟が2001年に発表したホラー「The EYE／アイ」を、「ファンタスティック・フォー」「シン・シティ」のジャシカ・アルバ主演でハリウッドリメイク。盲目のバイオリニスト、シドニーは姉の勧めで角膜移植手術を受けるが、視力が回復すると自分にしか見えていないものがあることに気付く。その謎は角膜のドナーにあると考えたシドニーは、真相を探るうちにドナーの悲しい衝撃的な過去を知る。", "2008-11-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I QUATTRO DELL’APOCALISSE", "荒野の処刑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "L・フルチ監督が手掛けた残酷ウエスタン。正義の名のもとに行われた大虐殺から逃れたギャンブラーと娼婦、墓掘り人夫、アル中の４人。奇妙な友情で結ばれた彼らの行く手には、様々な苦難が待っていた……。", "1975-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小判鮫 お役者仁義", "小判鮫 お役者仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "美空ひばりの主演で、名作「雪之丞変化」(1935年、衣笠貞之助監督）をリメイクした娯楽時代劇。亡き父の意趣返しのため、暴利をむさぼる極悪商人を討つ人気歌舞伎役者・雪之丞と、江戸を騒がす義賊・闇太郎の波乱万丈の活躍を描いている。おもしろいのは長谷川一夫版(1963年、市川崑監督）にならってか、ひばりも二役をこなすのだが、それが闇太郎ではなく彼に惚れた軽業師のお七を演じるという点。また女性ファンを当て込んで、林与一に天下の二枚目怪盗をやらせる商売のうまさは、全盛期の東映のお家芸ではあるが、シネスコ画面を巧みに使ってまさに緩急自在、右に左に人物を目まぐるしく配置する沢島監督の手腕も快調な痛快編。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO CACTH A THIEF", "泥棒成金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "宝石泥棒のロビーは今はリビエラで悠々自適の身。ところが彼の名をかたる泥棒が出没して抜き差しならない事態に……。ヒッチコック最盛期の１本で、軽妙洒落な演出が光るコミカル・ラブ・サスペンス。“クールビューティ”G・ケリーが圧倒的に美しい。", "1955-01-01", "1", 0, NOW(), NOW());
