INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEIZURE", "邪悪の女王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ファンタジー作家のエドムンドは、自分の悪夢に登場する魔物たちを題材に創作活動を続けていた。そんなある日、彼のイマジネーションの産物であった魔物が現実の世界に姿を現し、友人を巻き込んで残酷なサバイバル・ゲームを始めてしまう。", "1974-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若親分出獄", "若親分出獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "第１作で大浜のボス・太田黒伊蔵を斬って下獄した若親分・南条武は、大正天皇即位の恩赦により６年ぶりに出獄した。しかし武のいない間に大浜は九州からの流れ者・中新門一味に支配されていた。一度はヤクザ渡世から足を洗おうとした武だが、中新門の背後に海軍汚職の黒幕が絡んでいることを知り……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖女伝説セイレーンX 魔性の誘惑", "妖女伝説セイレーンX 魔性の誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "完璧な肉体で男たちを虜にし、その精を吸いつくして永遠に生きつづける魔性の女・セイレーンを描いたVシネマの人気エロティックホラーを映画化。深夜のお色気番組の撮影のため、とある湖にやって来たロケ隊の一行。突然の豪雨で撮影を続けられなくなった彼らは、謎めいた美女・麗華が営むペンションに宿泊することになるが……。セイレーンに扮するのは、本作が映画初主演となるセクシー女優・麻美ゆま。", "2008-05-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒトリマケ", "ヒトリマケ", "", "", "http://image.eiga.k-img.com/images/movie/54023/315x210.jpg?4506", 118, "参加者がそれぞれ抱える借金をひとつにまとめ、負けた者がその全てを背負い込むというゲーム“ヒトリマケ”に挑むことになった6人の男女。参加者たちは話し合いの末に1日1回投票を行い、5人の意見が一致すれば“ヒトリマケ”が決定する。欲望渦巻く駆け引きの中で、参加者たちの精神は次第に追い詰められていく。清水ミチコ、お笑いコンビのスピードワゴンらキャスト、スタッフ全員がノーギャラで参加したという異色作。", "2008-10-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺人拳２", "殺人拳２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "空手の達人である一匹狼・剣琢磨に、千葉真一が扮した「激突！殺人拳」に続く“殺人拳”ものの第２作。一匹狼の剣と、空手道場の館長、それに国際犯罪組織のボスが絡んでの三つ巴の死闘を描く。和製“ブルース・リー”ものといえるが、やたらと残虐シーンが多い。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DODSWORTH", "孔雀夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ノーベル文学賞受賞者であるS・ルイスの原作をもとに、S・ハワードが劇化した大ヒット舞台劇を、脚本はハワード、演出には新鋭ワイラー（当時34歳）があたって映画化した、1930年代を代表する傑作。舞台同様主演はヒューストン。アメリカ中西部の工業都市ジーニスで、20年間自動車会社を経営してきた自動車王ダズワースは、会社を手離し、娘も嫁にやり、妻と二人で第二の人生を楽しもうとヨーロッパ旅行に出かける。しかし妻はパリで知り合った美術評論家を相手に失った青春を取り戻そうとする。妻がちやほやされるのは金が目当てだと気付くダズワースは彼女をたしなめるが、彼女は離婚を主張。結局、美術評論家とは結婚できないとわかり夫のもとへ帰る妻だが、一度できた溝は簡単には埋まらない。一方ダズワースは以前からの知り合いで、一度結婚に失敗しているイーディスこそ、第二の人生をともに歩む女性だと悟る。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HONEY，I BLEW UP THE KID", "ジャイアント・ベビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "子供たちをアリンコ大に縮めてしまった「ミクロキッズ」の続編的作品。天才発明家サリンスキー博士は物体拡大光線を開発するが、今度は二才半の息子を34メートルの巨人にしてしまう。巨大ベビーははしゃぎ回って街を破壊し、やがてラスベガスに……。笑いとサスペンスを満載したSFコメディ。DVDタイトルは「ジャイアント・ベビー／ミクロキッズ２」。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香港大夜総会 タッチ＆マギー", "香港大夜総会 タッチ＆マギー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "SMAPの香取慎吾が映画初主演を果たしたコメディ。全国誌デビューを夢見て、香港の暗部へ切り込もうと張り切った新人カメラマンの良一は、香港に着いた早々、逆に犯罪現場を目撃して追われるハメになる。同行したゲイのルポライターの雄作に言われるまま、クラブ“迷城夜総会”のマジックショーに飛び入り出演した良一だったが、女装から解放されることはなかった。いまや女流芸人マギーとなった良一は、雄作の挙動にもビクビクしなければならず、クラブで一目惚れしたコーラの雄作への熱い視線を気にしつつ、ショーを続けていた。彼らは香港を無事脱出することができるのか？良一のコーラへの恋は実るのか？　香取の女装もさることながら、ゲイのルポライター役の岸谷五朗もなかなかの熱演。歌姫コーラを演じるのは、香港の女優アニタ・ユン。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PETIT CRIMINEL", "ピストルと少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "マルクは酒浸りの母とともに、南仏の海辺の町セートに暮らしていた。ある日、彼は偶然から、死んだはずの姉が近隣の町で暮らしていると知る。姉会いたさにマルクはピストルを握り、途中出会った刑事に車を運転させながら姉の元へ向かう。そしてやっと出会った姉ナタリーは、弟を哀れに思い、３人でセートへ戻ることにする……。ヒステリックなまでにぶつかり合う男女の愛憎を描いてきたJ・ドワイヨンが、新たな一面を見せた青春ドラマ。孤独に打ちひしがれながら、素直にふるまえない少年の、移ろいやすい心情をみずみずしく捉え、1990年代版「大人は判ってくれない」とも言えそうな作品となっている。ベルリン映画祭で国際批評家賞を受賞。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Primate", "霊長類", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("のど自慢", "のど自慢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "群馬県・桐生市。今、街はあの人気番組“のど自慢”がやって来るとあって沸き返っていた。そんなところに帰郷したのが売れない演歌歌手・赤城麗子。落ちていた予選出場ハガキを手に入れた彼女は、一度は大勢のお客の前で歌ってみたいと願い、予選に出場する。そして、何をやってもうまくいかず、３人の娘と妻を抱えながら現在は焼き鳥チェーン店の研修を受けている荒木圭介。不倫の恋をしている姉を心の中で応援し続けている高校生、高橋里香。この３人を中心に“のど自慢”に出場する人間たちの悲喜こもごものドラマを、井筒和幸は丹念に描写。抑えた演出によってしみじみと心に染み込む人間賛歌に仕上げている。また、出場者が歌う歌の選曲もすばらしく、特に自閉症気味の孫を預かる耕太郎老人に扮した北村和夫の『上を向いて歩こう』が胸を打つ。歌謡ヒット・パレードに託した群像ドラマ。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　百獣戦隊ガオレンジャー　火の山、吼える", "劇場版　百獣戦隊ガオレンジャー　火の山、吼える", "", "", "http://ecx.images-amazon.com/images/I/316BW83TK1L._SL210_.jpg", 28, "百獣の王をモチーフにしたスーパー戦隊シリーズの25作目「百獣戦隊ガオレンジャー」の劇場版。次元嵐に巻きこまれ、3匹の邪悪なオルグが支配する異世界の火山島にたどり着いたガオレンジャーたち。自由を求めて戦うレジスタンスのリーダー・魁人や、囚われの身となっていた島の皇女・伊莉耶と出会ったガオレンジャーたちは、オルグとの戦いに身を投じる。そんな彼らの前に、巨大化したオルグが立ちはだかり……。", "2001-09-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サヨナライツカ", "サヨナライツカ", "", "", "http://image.eiga.k-img.com/images/movie/53703/315x210.jpg?1624", 134, "辻仁成のベストセラー恋愛小説を、辻の妻・中山美穂を主演に映画化。中山の主演は「東京日和」（97）以来12年ぶりとなる。監督は「私の頭の中の消しゴム」のイ・ジェハン。1975年のバンコク、ひとり自由気ままに暮らす沓子（中山）は、日本から赴任してきたエリートビジネスマンの豊（西島秀俊）に出会う。激しく惹かれ合う2人だったが、豊には日本で彼の帰りを待つ婚約者がいた。", "2010-01-23", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野獣死すべし", "野獣死すべし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1959年に仲代達矢主演で映画化された大藪春彦の同名小説の再映画化。村川透＝松田優作の、のりにのっているハードボイルド・コンビによる痛快な一編。戦地を渡り歩いた元カメラマンが、管理社会に犯罪で挑むピカレスク・ロマンである。これが出世作となった鹿賀丈史の悪役ぶりが光る。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUZE HEURES D’HORLOGE", "脱獄十二時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "フランスのある港町に３人の脱獄犯が逃げて来た。その一人コペッキーの情婦が、彼から預かった30万フランを持ってこの町にいたのだ。その金で海外逃亡を図る彼らだが、警察の追っ手は彼らに迫っていた……。朝６時から夕方６時までの12時間の中に展開するドラマを緊迫感あふれるタッチで描く。ビデオ発売時タイトルは「脱獄12時間」。", "1958-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野性の証明", "野性の証明", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "東北山中の山村で、村人のほとんどが殺されるという大量虐殺事件が発生する。頼子という少女が一人生き残るが、頼子は“青い服を着た男”ということ以外いっさいの記憶を失っていた。当時山中で単独踏破訓練をしていた自衛隊員・味沢は頼子を養子として引きとり、退役して静かに暮らしていた。が、ある事故がもとで暗い過去が再び二人にしのびよってくる。予知能力のある神秘的な少女・頼子を当時新人の薬師丸ひろ子が演じ、のちに大人気アイドルとなるきっかけとなった。大量殺人事件をめぐるナゾ、もの言わぬ頼子のいちずに思いつめた表情、そして凄絶なラストまで、息をつかせぬ演出で飽きさせない好編。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さすらいは俺の運命", "さすらいは俺の運命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ヤクザ仲間の争いで恋人・利子を失った氷室は、２度とダイスは持たないと心に誓った。だが利子の姉・咲江が経営するレストランが、彼女を自分の女にしようとする町の実力者・貴島の陰謀で膨大な借金を抱えていると知った時、彼は再びダイスを握る決心をする……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("over8", "over8", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "映画の新しい可能性を追求するプロジェクト「over8」の第1弾として制作されたオムニバス・ムービー。近未来の物語を8ミリフィルムで撮影し、デジタルビデオで編集するという条件のもと、「PRISM」の福島拓哉、俳優としても活躍する川野弘毅、「女」「鵜野」の前田弘二ら、インディペンデント界で注目を集める9人の若手監督が競作。出演は「春眠り世田谷」の紀伊修平、「非・バランス」の派谷恵美ほか。", "2007-01-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COCKTAIL", "カクテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ベストセラー小説をT・クルーズ主演で映画化。就職に失敗して、ひょんなことからバーテンダーとなった青年の成功と挫折を、歌あり恋ありで描く青春ドラマ。挿入歌『ココモ』は、ビーチボーイズ22年ぶりのNo.1ヒットとなった。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STORM OVER THE NILE", "ナイルを襲う嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "T・ヤングとZ・コルダが共同監督したアドベンチャー。アフリカのスーダン砂漠を舞台に、出征直前に軍隊をやめ、裏切り者の烙印を押された若者の恋と冒険をスケール大きく描く。A・E・W・メイスンの冒険小説、『四枚の羽根』の３度目の映画化。", "1955-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOCTOR DETROIT", "ダン・アイクロイドのDr.デトロイトを探せ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "クリスはまじめだが、少し変な若き大学教授。組織のボスに上納金を納めなければならないポン引きウォーカーは、その言い訳にドクター・デトロイトという架空の人物をデッチあげる。ふとしたことから隣り合わせになったこの二人が繰り広げる、ギャグ満載のコメディ。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mad Dog and Glory", "恋に落ちたら…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "臆病な刑事ドビーは強盗からギャングのボスを救ったことから、その情婦のグローリーと知り合う。彼女と恋に落ちたドビーは、ボスの手からグローリーを奪おうとするが……。J・マクノートン監督による一風変わったラブ・コメディ。笑いと哀感が絶妙にブレンドされた愛すべき一作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THOMAS　AND　THE　MAGIC　RAILROAD", "きかんしゃトーマス・劇場版／　魔法の線路", "", "", "http://ecx.images-amazon.com/images/I/519C79QAQ3L._SL210_.jpg", 84, "世界100ヶ国以上で放送されたTVシリーズ『きかんしゃトーマスとなかまたち』を劇場用に映画化したファミリー・ピクチャー。おしゃべり好きの蒸気機関車トーマスたちが、元気いっぱいに走り回るソドー島。ここに平和をかき乱す悪のディーゼル機関車が現れ、美少女リリーやトーマスらが大奮闘を繰り広げる。CGを用いてヴィジュアル化したファンタスティックなメルヘン世界が魅力の１本。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("走れ！イチロー", "走れ！イチロー", "", "", "http://image.eiga.k-img.com/images/movie/1443/315x210.jpg?2332", 105, "2000年4月某日。神戸グリーンスタジアム、オリックス対西武戦。スタンドには元社会人野球選手のサラリーマン、石川市郎が一人娘のこよみと立っていた。震災の際に3カ月間神戸で復興事業に関っていた市郎は、家出した妻・鈴子を追って神戸まで来たのだ。その鈴子はスタンドの別の場所で高校時代のソフトボールのコーチ早川と試合を見ている。解説席では、解説中の作家・奥手川に近寄ろうとするアーティスト、睦美の姿があった。そしてその時、スタジアムのあちこちで様々なドラマが始まろうとしていた……。", "2001-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ji Qi Xia", "カンフー・サイボーグ", "", "", "http://image.eiga.k-img.com/images/movie/54973/315x210.jpg?4748", 103, "「黒薔薇VS黒薔薇」の鬼才ジェフ・ラウがメガホンを取ったSFアクション・コメディ。2046年、田舎の警察署に務めるダーチュンの元に、デミンというサイボーグの警官が配属される。同僚たちには正体を隠したまま、次々と難事件を解決していくデミン。そんなある日、同じくサイボーグのK88が暴走しているとの情報が入り……。主演は香港の人気アイドル、アレックス・フォン。「レッドクリフ」のフー・ジュンら豪華俳優陣が脇を固める。", "2009-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON’T BOTHER TO KNOCK", "ノックは無用", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "パイロットの婚約者を飛行機事故で亡くして以来、精神に異常をきたしてしまい、同じパイロットのジェッドを死んだ恋人と勘違いしたり、子守りをするはずの女の子に猿ぐつわをはめてしまったりと、狂気の行動を続けて周囲を困惑させていくが……。M・モンローの異常者演技が見もの。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("中華学校の子供たち", "中華学校の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/53943/315x210.jpg?3098", 86, "「ヨコハマメリー」のプロデューサー・片岡希の初監督作品で、日本の中華学校に通う子どもたちを追ったドキュメンタリー。在日華僑・華人の教育と文化の伝達の場として日本に中華学校が設立されて百余年だが、現在日本国内にある中華学校はわずか5校である。本作では横浜山手中華学校小学部1年生を3年間にわたり記録し、日本語と中国語の両方を話す子どもたちの日常を映し出す。", "2008-11-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇能鴻一郎の 濡れて打つ", "宇能鴻一郎の 濡れて打つ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "ロマンポルノの持つ暗いイメージを破壊して、その後のにっかつ作品のライト・ポルノ化を決定づけた金子修介監督のデビュー作。少女マンガの名作『エースをねらえ！』をパロディーにしたかのごとく、全編をコミック調で描ききっている。金子監督はこの作品で1984年ヨコハマ映画祭新人監督賞を受賞した。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CAPORAL EPINGLE´", "捕えられた伍長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1940年６月、コンピエーニュの森で独・仏休戦協定が調印された。フランスはドイツに降伏したのだ。この協定によって、それまでフランス国内の捕虜収容所にいたフランス軍の捕虜は、ドイツに移送されることになった。それを知った伍長は、仲間のパテールとバロシェとともに脱走を試みるが、何度試みても失敗に終わる。仲間の間には次第にあきらめムードが漂うが、伍長だけはくじけない。そしてついに、伍長はパテールとともにドイツ娘エリカの手助けで脱走に成功、無事パリにたどりつく。「大いなる幻影」から20余年、J・ルノワール監督が再び取り組んだ脱走劇。二つの作品を比較してみるのも面白い。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ムーンライト・ジェリーフィッシュ", "ムーンライト・ジェリーフィッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "映画以外にもTVやCM、PV界などマルチに活躍する新鋭映像作家、鶴見昴介が、自ら脚本を手掛けた映画デビュー作。藤原竜也、木村了、岡本綾の３人を主演に、切ない愛と悲しい宿命の物語を描き出す。幼くして両親を亡くしたセイジは、紫外線を浴びると皮膚ガンを発症するという難病を患った知的障害の弟・ミチオの面倒を一人で看ていた。暴力団員として治療費を稼ぐセイジはある日、バーで酔っ払いに絡まれた女性・ケイコを助ける。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONVICT COWBOY", "傷だらけのカーボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "大都会に生きる孤独な男たちを描いた「真夜中のカーボーイ」の後日譚的な作品。ずばぬけたロデオの才能を持ちながらも、終身刑で服役中のライは刑務所内でも一目置かれる存在だった。ある日ライの刑務所に、若く有能なカーボーイ、クレイが傷害事件で投獄されてくる。ライとクレイはお互いに反発を感じながらも、ロデオを通して接近し、心の絆を築いていく。しかし、クレイは所内の麻薬組織に、運び屋として目をつけられていた。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ORDER", "悪霊喰", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「ROCK YOU！〈ロック・ユー！〉」で中世騎士とロック音楽を融合させたB・ヘルゲランド監督が、中世期に発祥し、現代にも伝えられているカトリックの異端の儀式“罪食い”をモチーフに描いたオカルト・ホラー。古都ローマで撮影されたダークな映像も宗教的な恐ろしさを盛り上げている。ニューヨークの若き司祭、アレックスは、恩師の急死に不審を抱き、遺体が発見されたローマで調査を開始する。ローマ教会の妨害を受けながらも少しずつ浮かび上がる真相。それは中世から伝わる禁断の儀式“罪食い”の存在だった。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADRI DI SAPONETTE", "シャボン泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ネオ・レアリスモ映画の傑作「自転車泥棒」を土台に、イタリア映画界注目の新鋭、M・ニケティ監督が描くパロディ・コメディ。高名な映画監督ニケティの新作、「シャボン泥棒」が放映されようとしているテレビ局。高慢ちきな批評家は自分がどう映るかだけを気にし、映画はそっちのけ。いよいよニケティの「シャボン泥棒」がオン・エアされることになるが、映画とCF、しかも現実世界まで入り交じって、彼が描こうとした映画世界とは、似ても似つかぬものとなってしまう。最近はやりのテーマではあるが、切れのいいユーモアと多層な世界の混交、加えて臆面もない名作のパロディなどにより、ニケティ独自の世界を築き上げている。モスクワ映画祭金賞受賞。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　MATCHMAKER", "恋はワンダフル!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アイルランドの片田舎の素朴な風景をバックに繰り広げられるラブ・コメディ。上院議員の秘書を務めるアメリカ人女性が、議員のルーツ探しのためにアイルランドを訪問。お見合いフェスティバルを開催中の村人たちのお節介に助けられ、かけがえのない恋を見つけていく。「好きと言えなくて」のJ・ガラファロが、ひそかな結婚願望を抱く30歳のキャリアウーマンを好演。", "1997-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CA´NH DO＾N｀G HOANG", "無人の野", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ベトナム戦争末期の1972年。アメリカ軍は南ベトナムのデルタ地帯を“無人地帯”として解放軍同士の連絡を断つ作戦に出た。強制的に村を追われた村人たち。だが、解放軍の連絡員を務める農民のソアと妻、その幼い子供だけはこの地を離れなかった。しかし、アメリカ軍は彼らに情け容赦ない攻撃を仕掛けてくる……。戦争のなかで自分たちの暮らしを守っていこうとする一家の姿を描いた感動的な一編。陰影に富んだモノクロの映像も素晴らしい。モスクワ映画祭で金賞を受賞した。", "1980-01-01", "60", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL ABRAZO PARTIDO", "僕と未来とブエノスアイレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "南米ブエノスアイレスに住む移民たちの生活を、ユーモアと愛情を持って見つめた人間ドラマ。舞台のガレリアと呼ばれるアーケード商店街は、移民がひしめき合って暮らす町。未来が見えず、この町を去ろうとしている東欧系青年を主人公にして、周囲の複雑な人間模様を描いている。ウディ・アレンにたとえられる素朴で知的なユーモアのセンスを見せ付けた、新鋭ダニエル・ブルマンの演出は軽妙で忘れがたい。温かみのあるタッチが絶賛され、'04年のベルリン国際映画祭ではみごとに審査員特別賞を受賞。母の店を手伝う青年アリエルの夢は祖父母の故郷ポーランドへ移り住むこと。しかし、このところ彼の周囲の人々の様子がおかしい。やがてアリエルの前に、意外な人物が現われ……。", "2006-01-14", "22,2,4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢幻琉球・つるヘンリー", "夢幻琉球・つるヘンリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「ウンタマギルー」以来10年ぶりとなる高嶺剛の長編作品。全編がデジタルビデオで撮影されており、ビデオ映像の生々しさと、日常と地続きにある特異なファンタジー世界を見事に融合させている。沖縄の民謡歌手つるが拾ったシナリオ『ラブーの恋』を、息子ヘンリーと一緒に映画化しようと悪戦苦闘するストーリーが展開。実際に民謡歌手である主演、大城美佐子の自由奔放なたたずまいが、映画を風通しよくしている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("任侠柔一代", "任侠柔一代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「姿三四郎」で知られる富田常雄の原作による任侠柔道映画。講道館四天王の一人、音羽大介は、佃一家に刺された仏の喜三郎を助けたことがもとで破門されてしまう。大介は喜三郎のために人夫までやりながら治療費を稼ぎ、喜三郎は大介の破門を解いてもらうよう講道館館長の矢野正五郎を頼っていく。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOMANI", "明日、陽はふたたび", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "1997年にイタリアで実際に起きた大地震を題材にした人間ドラマ。イタリアのウンブリア地方の古都で、夜半に大地震が発生する。美しい街並みのカッキアーノの住民は、苛酷な避難生活を余儀なくされる。家を失い、プライバシーのない共同生活の中で副町長パオロの一家や小学校教師ベティ、教会のフレスコ画の修復にやってきたイギリス人のアンドリューらは、自分たちの人生をそれぞれに見つめ直していく。", "2000-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飢餓海峡", "飢餓海峡", "", "", "http://ecx.images-amazon.com/images/I/310641EF6HL._SL210_.jpg", 183, "青函連絡船・洞爺丸が台風のため、転覆し、多くの犠牲者を出した実話に基づいて書かれた水上勉の小説を映画化。見事なスリラー形式の運命劇として内田吐夢の代表作となった。“日本人全体を覆う飢餓状況を象徴する”ため、内田監督は特殊な映像処理を試み、主人公たちの心理の陰影をうまく描写。また、推理として現れるシーンでもレリーフの効果を出すなど、苦労のあとが見られる。まだ戦後の混乱冷めやらぬ頃、台風が津軽海峡を襲い、青函連絡船が沈没。だが収容した死体は乗客名簿より２名多かった。転覆事故のどさくさにまぎれた殺人事件の犯人を、老刑事・弓坂は10年に渡って追い続けていた……。“偶然”にほんろうされる人間の数奇な運命を、主人公の心の中に潜む善と悪を通して描く。当時、フィルム・カット事件から、内田監督が東映を退社したいわくつきの作品でもある。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITIZEN　TOXIE：THE　TOXIC　AVENGER　IV", "悪魔の毒々モンスター　新世紀絶叫バトル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1980年代にカルトな人気を得たホラー・コメディが11年ぶりに復活。醜悪な姿の正義ヒーロー、毒々モンスターが、平和なトロマヴィルの町を襲った極悪おむつマフィアに立ち向かおうとした時、爆発のショックで悪の反世界に飛ばされる。一方、元の世界では悪の毒々モンスターが暴れ回り、毒々モンスターの妻を犯してしまう。果たして毒々モンスターは元の世界に戻り、敵を倒すことができるのだろうか？", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("稲妻草紙", "稲妻草紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "船来源三郎は、持って生まれた一本気から、横暴な家老を殴って脱藩する。藩から上意討ちの命を受けた有馬又十郎は、源三郎のあとを追うが、その人柄に惚れ命令書を破り捨てて、いずこかへ去っていく。なお、題名の由来は、稲垣監督と妻三郎から一文字ずつを採ったという。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KIRLIAN FORCE", "謎の完全殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ドラゴンの像が彫られ、宝石が埋め込まれている謎のメダリオン。これを見た者は謎の怪死を遂げる。メダリオンの力を借りて、無実の罪を着せられたアーノルドは、復讐を開始。モーガン警部はこれを阻止しようとするが……。犯罪とオカルトを絡ませた、異色のサスペンス映画。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rebellion: The Litvinenko Case", "暗殺　リトビネンコ事件", "", "", "http://image.eiga.k-img.com/images/movie/53163/315x210.jpg?1998", 110, "2006年11月23日、チェチェン戦争にまつわるプーチン政権とロシア連邦保安庁FSBの腐敗を告発していた元FSB中佐アレクサンドル・リトビネンコが、亡命先のイギリスで暗殺された。リトビネンコの友人であったロシア人監督アンドレイ・ネクラーソフが、彼が殺されなければならなかった理由を探る衝撃のドキュメンタリー。生前のインタビューや関係者らの証言を通し、ロシア政府が抱える深い闇を浮かび上がらせていく。", "2007-12-22", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Children of Men", "トゥモロー・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/1713/315x210.jpg?4040", 109, "英ミステリ界の女王P・D・ジェイムズのベストセラー「人類の子供たち」を「ハリー・ポッターとアズカバンの囚人」「天国の口、終りの楽園。」のアルフォンソ・キュアロン監督が映画化。人類に子供が生まれなくなった西暦2027年。何の目的もなく働いていた国家官僚のセオが、ある日突然、何者かによって拉致される。セオを拉致したのはセオの元妻ジュリアンが率いる反政府組織で、世界がひっくり返るような秘密を掴んでいた……。", "2006-11-18", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録・阿部定", "実録・阿部定", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "昭和11年に起きたあまりにも有名な猟奇的犯罪、“阿部定”事件の完全な映画化。四畳半という限られた舞台で、４月23日から５月７日、さらに５月12日から局部切除に到る18日までの阿部定と、被害者となった彼女の奉公先の料亭の主人・吉蔵との愛憎劇を展開させている。日活ロマンポルノ史上、十指に入る秀作。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAX: OR THE DISCOVERY OF TELEVISION AMONG THE BEES", "WAX 蜜蜂テレビの発見", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "実写とCGを融合した、新しいタイプのエレクトロニック・シネマ。飼育していた蜜蜂によって、頭の中に水晶体を埋め込まれたジェイコブ。その中から発せられる映像を通じて、彼は死者の世界や、自分の未来を見てしまうが……。サイバーパンク風のムードにあふれた映像が圧倒的。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花のおもかげ", "花のおもかげ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "貧しいピアニストの清と、バレリーナの卵・幸子はお互いにほのかな愛情を感じていた。しかしある日、清はかつてのピアノの先生・藤子に再会する。少年時代の憧れの女性であり、未亡人になった今も美しさを失わない藤子を前に清の心は揺れる……。名匠・家城巳代治が詩情豊かに描き上げたメロドラマ。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ДИКАЯ　ОХОТА　КОРОЛЯ　СТАХА", "スタフ王の野蛮な狩り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "19世紀最後の年、ベロルシアの小さな村を民俗学者アンドレイが民話取材のため訪れる。その村には貴族に虐殺された農民の英雄スタフ王の呪いにまつわる言い伝えがあった。アンドレイは呪われた貴族の子孫の館に滞在することになるが……。恐怖と優しさに満ちた物語を美しい映像で描いていく幻想映画。", "1979-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUGGLES OF RED GAP", "人生は四十二から", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "英国生まれの執事ラグルスが、ポーカーの賭けの代償として新しい主人に召し抱えられる。が、新しい主人は、やることなすこと西部気質まる出しのヤンキー男。彼は執事を“友人”扱いして故郷へ連れ帰るが……。名優として名高いC・ロートン演じる執事が、次第に改心していく様が面白い人間喜劇。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fantastic Voyage", "ミクロの決死圏", "", "", "http://ecx.images-amazon.com/images/I/51VpW1uzs-L._SL210_.jpg", 101, "アメリカ亡命の直後に襲撃され脳内出血となり、外科手術が不可能となった要人を救うため、米政府は医療チームを潜水艦ごとミクロ化し、体内に送り込んで手術する作戦を決行する。だが、脳外科医、循環器専門医、潜水艦艦長、諜報員ら5人のチームは60分以内に患部を探し出し、手術を完了しなければならなかった……。「海底二万哩」のリチャード・フライシャー監督によるSF特撮映画の名作。出演はスティーブン・ボイド、ラクエル・ウェルチ。アカデミー賞では美術賞および視覚効果賞を受賞した。", "1966-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEN HARRY MET SALLY", "恋人たちの予感", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "出会ってから５年後に再会し、さらに５年後の再会を経て美しい友情で結ばれた男女が、セックスによってその友情が壊れるのを恐れ、笑いあり涙ありの努力を繰り返す。セックス抜きで男女の愛情は成立し得るかというテーマを、ニューヨーク・マンハッタンの四季を背景に描いたR・ライナーの自伝的ラブ・ストーリー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎わが道をゆく", "男はつらいよ・寅次郎わが道をゆく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "さくらの幼友だちで“SKD”の踊り子・奈々子に夢中になった寅さんは、毎日浅草国際劇場へ通いづめ。そのうえ、九州で知り合った青年が寅を頼って上京。彼も一緒になってレビュー狂いになってしまう。寅は徐々に“恋の指南役”へと立場を変えつつある。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ghostbusters", "ゴーストバスターズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "超常現象を研究する3人の科学者が幽霊退治屋の珍商売を始めるSFコメディの傑作。次々に起こるゴースト騒動と「ゴーストバスターズ」の活躍をSFXで描く。クライマックスのマシュマロマンが最高。主題歌もヒットした。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MILLENNIUM", "ミレニアム〔1000年紀〕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "飛行機事故で全員死亡の大事故が勃発、原因究明のため現地に赴いた主任調査官ビルは、そこでルイーズという女と知り合う。彼女は1000年の未来からやって来た工作員だった。自らの生殖能力を失った未来人は、人口を維持するために、飛行機事故で死ぬ直前の人々を未来へ送り込もうとしていたのだ。人気SF作家J・バーリーの原作・脚本によるタイム・トラベルSF。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドッジGO！GO！", "ドッジGO！GO！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ドッジボールを通して日本と韓国の子供たちの友情を描くハートウォーミング・ストーリー。弱小ドッジボールチーム“みなとドッジキッズ”に入っているゆきこは、同じ町の強敵チームと最後の対戦をするため欠けてしまったメンバー探しに奔走中。そんな時、父親の都合で韓国に移住することになってしまう。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("輪舞曲〈ロンド〉", "輪舞曲〈ロンド〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "助監督として様々な現場に携わった喜屋武靖がメガホンを執り監督デビューを飾ったラブ・ストーリー。喫茶店の店長をする香織は、友人に誘われてコンパに参加、ミュージシャンの邦彦と知り合う。場を盛り上げようとした邦彦は怪しげな惚れ薬を出し、香織に飲ませる。数日後の夜、ふと香織が気付くと、邦彦が演奏している広場に立っていた。さらに、邦彦が演奏していたのは５年前に死んだ香織の恋人、俊樹が演奏していた曲だった……。偶然出会った二人の間によみがえる思いを丹念に描く好編。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESCAPE FROM THE PLANET OF THE APES", "新・猿の惑星", "", "", "http://ecx.images-amazon.com/images/I/414BHMJ4NYL._SL210_.jpg", 97, "「猿の惑星」シリーズの第３作で、脚本は前作と同じ。最終兵器で滅亡する未来の地球からタイムトラベルした３匹の猿人は、1970年代の地球に到着。彼らから未来社会の猿による人間支配を知った科学者は、猿族抹殺を図るため、彼らを射殺する……。", "1971-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超時空要塞マクロス　愛・おぼえていますか", "超時空要塞マクロス　愛・おぼえていますか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "地球に異星人の巨大宇宙船が漂着してから、10年後の2009年。宇宙では男だけのゼントラーディ軍と女だけのメルトランディ軍との、巨人族同士の戦いが続き、その余波を受けて地球は破滅された。修復された巨大宇宙船、マクロスに乗った６万人弱の人々だけが、かろうじて地球を脱出。その中では統合軍の少年パイロット、一条輝と、戦闘管制官の早瀬未沙、そしてアイドル歌手のリン・ミンメイの微妙な三角関係も始まっていた。そしてミンメイの歌が、巨人族の心に文化の記憶を呼び覚ました時、二大勢力はその力のバランスを崩し始めていく……。宇宙でのマクロ・スケールのメカ・ファイトと同時に、若者三人のミクロなラブ・ストーリーを描きながら、両者を見事にシンクロさせてしまうという離れ技。オリジナルTVシリーズの劇場用リメイク版として作られたこのSFアニメは、その極端なドラマゆえに、1980年代を飾る傑作と呼ぶにふさわしい。メカの緻密な設定、描写もさることながら、真の魅力はそれではない。少女の歌う歌が心理兵器となって、敵の大宇宙艦隊を蹴散らしていく壮絶なクライマックス。それが生と死の究極の讃歌であるからこそ、観た者は酔わずにはいられないのだ。現在のビデオ、DVDでは劇場公開時にはなかった“幻のエンディング”を収録した完全版が出ている。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談新耳袋／幽霊マンション", "怪談新耳袋／幽霊マンション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "TVシリーズも好評を呼んだ、怪異収集家の木原浩勝と中山市朗の共著による怪談集『新耳袋』を原作にしたホラーの劇場版第2弾。今回は、『新耳袋』の中でも屈指の逸話、京都のとあるいわくつきのマンションで起きた恐怖のエピソードが語られる。マンションに取り憑く少女の霊に隠された事実が明かされると共に、二転三転する物語は衝撃の連続。また、『ケータイ刑事 銭形泪』の黒川芽以が恐怖に慄くヒロインを大熱演。数々の人気TVドラマを手掛けてきた吉田秋生監督の巧妙な演出も恐怖心を煽る。17歳の少女、愛美は父と共に古びたマンションに引っ越す。だが、そこは30年前に行方不明になった少女の霊が取り憑き、住人が何人も呪い殺されている恐怖のマンションで……。", "2005-08-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Late Bloomers", "最高の人生をあなたと", "", "", "http://image.eiga.k-img.com/images/movie/57393/315x210.jpg?2548", 90, "英ロンドンを舞台に、60歳を過ぎて第二の人生を歩み始めた女性が理想の人生を模索しながら奮闘する姿を描いたヒューマンドラマ。建築家の夫アダムと30年間の結婚生活を送ってきたメアリーは、気がつけば60歳。子どもたちも独立して孫にも恵まれたが、ふとしたきっかけで、これまでの人生や夫婦の老後について思いをめぐらせるようになる。メアリーが老いを実感する一方で、アダムは若手スタッフとの共同作業に熱意を燃やし、2人の仲は次第にぎこちなくなっていくが……。監督は、名匠コスタ＝ガブラスの娘で「ぜんぶ、フィデルのせい」（2006）で長編デビューしたジュリー・ガブラス。", "2012-02-04", "2,37,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE ANGEL CAFE", "ブルーエンジェル・カフェ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ナイトクラブ“ブルーエンジェル・カフェ”で歌う人気女性シンガーと、次期知事候補の男とのスキャンダラスな愛を描くエロティック・ラブ・ストーリー。はじめはゲームにすぎなかった二人の恋は、やがて男の妻や政治問題をも巻き込むシリアスなものへと変わっていく。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男たちの詩", "男たちの詩", "", "", "http://image.eiga.k-img.com/images/movie/53953/315x210.jpg?3115", 81, "「SF　サムライ・フィクション」「RED SHADOW　赤影」の中野裕之監督が、白い物を見るとアイロンをかけたくなるヤクザの姿を通して男のケジメの世界を描き、第59回カンヌ国際映画祭ヤング批評家賞を受賞した「アイロン」を筆頭に、中野監督作「午後三時三分十五秒の観覧車」「灯台」、兼重淳監督作「スパゲッティナポリタン」、芹澤康久監督作「富士とドーナツ」の全5編からなる短編オムニバス。2008年9月6日初公開。09年1月17日に再公開。", "2008-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Virgin Suicides", "ヴァージン・スーサイズ", "", "", "http://image.eiga.k-img.com/images/movie/1843/315x210.jpg?1809", 98, "ヘビトンボが郊外の街の上空を覆う6月。リズボン家の美しい年子の5人姉妹は、近所の少年たちの一番の興味の的だった。姉妹の厳格な母親ミセス・リズボンは、娘たちの外出を殆ど禁じ、野暮ったくて慎み深い格好を強制していた。ある日13歳の末娘セシリアが手首を切って自殺を図る。なんとか一命は取り止めるが、精神科医から彼女の外界との接触の少なさが原因と指摘されたミスター・リズボンは妻を説得し、ホームパーティを開いて男の子たちを招待する。", "2000-04-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブコメ", "ラブコメ", "", "", "http://image.eiga.k-img.com/images/movie/55633/315x210.jpg?1857", 0, "「天国の本屋」でコンビを組んだ松久淳＋田中渉による同名小説を、香里奈主演で映画化。共演に田中圭、北乃きい、渡部篤郎ら。「ROOKIES 卒業」の平川雄一朗がメガホンをとる。彼氏いない暦1年11カ月の真紀恵は、美人だけど男勝りな花屋の店長。17年ぶりに再会したアニメ脚本家の美晴との素直になれない恋愛模模様が描かれる。花屋とキャバ嬢のアルバイトをかけ持つ涼子（北乃）と人気声優の西島（渡部）は、そんな2人のもどかしい姿を冷やかしながらも温かく見守っていた。", "2010-09-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜のバラを消せ", "夜のバラを消せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "舛田利雄監督と石原裕次郎のコンビによるムード・アクション。政界の黒幕から指令を受けた徳川新六は、政治粛正の名のもとに、腐敗した政治家どもを自らの肉体を武器にして次々に失脚させていく。しかし、彼はやがて、自分が操り人形でしかないことに気がつく……。日活初出演の由美かおるがキュートな魅力を見せる。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ど根性物語 銭の踊り", "ど根性物語 銭の踊り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "腕っぷしの強い江戸っ子・八百はある日、殺し屋グループからスカウトされる。社会の悪を消すというグループの指令に賛同して仲間になった八百だが、やがてあまりに冷徹な仲間の仕事についてゆけず、ついには自らグループと対決することになる……。絶頂期の市川崑が描くアクション映画。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Prairie Home Companion", "今宵、フィッツジェラルド劇場で", "", "", "http://image.eiga.k-img.com/images/movie/33873/315x210.jpg?1239", 105, "2006年11月に81歳でこの世を去った巨匠ロバート・アルトマンの遺作となった群像劇。アメリカの人気公開ラジオ番組“プレイリー・ホーム・コンパニオン”をモデルに、番組が打ち切られる最後のショーの舞台裏を、アルトマン監督らしいユーモアを織り交ぜながら綴る。撮影は実際のフィッツジェラルド劇場で行なわれ、その舞台上でメリル・ストリープ、リンジー・ローハンらオールスターキャストが吹替えなしで熱唱する。", "2007-03-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANAPARASTASI", "再現", "", "", "http://ecx.images-amazon.com/images/I/418N4AJ45PL._SL210_.jpg", 98, "ギリシャの巨匠T・アンゲロプロスが、自分の全てがここにあると公言する長編デビュー作。ミステリー的な意匠を施しながら、ギリシャの歴史とそれに揉まれた人物像を淡々と描き抜く。ロング・ショットの長回しはこの頃から冴えを見せる。ドイツへの出稼ぎから故郷に帰ってきた男が何者かに殺された。検察局、事件記者、映画そのものの三つの視点から真相に迫っていく。", "1970-01-01", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BILLY BATHGATE", "ビリー・バスゲイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1930年代、ギャングの勢力争いが激化するニューヨーク。絶大な権力を持っていたボス、ダッチ・シュルツは血気盛んな青年ビリー・バスゲイトを子分にし、目をかけた。ビリーはギャング世界の明暗を見ながら一人の男に成長していく……。実在のギャングたちの凄絶な生きざまをモデルに描く重厚な力作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("李小龍的生與死／Bruce Lee: The Man and the Legend", "ブルース・リーの生と死", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "カンフー・アクションのスーパースター、ブルース・リーの死の直後に本国、香港で製作されたドキュメンタリー。1973年に32歳で世を去るまでのリーの生涯を、代表作のハイライト・シーンや、貴重なフィルム、スチールを織り交ぜながらたどっていく。リーと親交の深かったスティーヴ・マックイーンやジェームズ・コバーン、チャック・ノリスらハリウッド・スターの証言など、興味深い映像を満載。", "1973-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私の婚活恋愛術", "私の婚活恋愛術", "", "", "http://image.eiga.k-img.com/images/movie/54633/315x210.jpg?4103", 90, "リュック・ベッソンとデビッド・フィンチャーが製作総指揮を務め、「ジャスト・マリッジ」のブリタニー・マーフィが主演するロマンチック・コメディ。UKヴォーグ誌でアシスタントとして働くエミリーは、恋のキューピッド役になることが大好きなお調子者。ある日、ハンサムなカメラマン・パオロがオフィスにやって来ると、エミリーは親友でゲイのピーターとパオロの仲を取り持とうと奔走するが……。", "2006-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last of The Mohicans", "ラスト・オブ・モヒカン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "18世紀半ば、植民地戦争を背景に、英国人開拓者の孤児だったモヒカン族の青年ホークアイが、フランス側につくヒューロン族に襲われた英国大佐令嬢姉妹を救う。何度も映画化されたジェームズ・F・クーパーの小説の再映画化。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GWENDOLINE", "ゴールド・パピヨン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ヒロインの名をとった『グエンドリンの冒険』というフランスのコミックの映画化。黄金蝶を追って行方不明になった父を捜して、彼女は魔都へと向かう……。「エマニエル夫人」でカメラマンから映画監督への転身を果たしたジャカン監督のエロティック・アクション。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ORCHESTRE", "完全版・　リプチンスキーのオーケストラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "ポーランドからアメリカに渡り、実験映画やミュージック・ビデオを製作しているZ・リプチンスキー。これは、全編ハイビジョンを使った彼の野心作で、６つのパートから成るイメージの万華鏡である。それぞれクラシックの名曲にのせ、ハイビジョンならではの合成技術を駆使した寓話的エピソードが綴られていく。", "1990-01-01", "1,2,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獣の戯れ", "獣の戯れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "三島由紀夫の同名小説を原作に、ある女と男の奇妙な共同生活が描かれる恋愛悲劇の問題作。当時大映のエース女優であった若尾文子が、危うい壊れかけの女を、抑えた演技で好演した。優子と逸平の夫婦と、逸平が社長を務める会社でアルバイトする学生、幸二が主な登場人物。逸平は度々、優子に家庭内暴力を振るっていた。それを見かねた幸二が、逸平を殴り、半身不随の身にしてしまう。やがて幸二が出所後、3人は同居することに。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DISTANT VOICES，SILENT LIVES", "遠い声、静かな暮らし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "イギリスの田舎町リバプールに住む５人の家族の歴史を描き、その愛憎を静かに浮き彫りにした作品。父への思いを描いた「遠い声」と息子の結婚までの物語「静かな暮らし」の２部構成で、それぞれの場面は年代順に並べられずスケッチ風にインサートされる独特な作風。地味ながら強い印象を残す異色作。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あした元気にな～れ！／半分のさつまいも", "あした元気にな～れ！／半分のさつまいも", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "大空襲によって焼け野原となった戦後の東京を舞台に、天涯孤独になりながらも未来を信じて生きる兄妹の姿を描いた長編アニメーション。原作は、故・林家三平の妻でもあるエッセイストの海老名香葉子。ナレーションに吉永小百合、主人公の少女かよ子役に上戸彩と、声優の顔ぶれも豪華。東京大空襲で家族を失った喜三郎とかよ子の兄妹。二人は伯母の家に預けられるが、伯母夫婦と折り合いの悪い喜三郎は行方不明に……。", "2005-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EFFROYABLES　JARDINS", "ピエロの赤い鼻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「クリクリのいた夏」の名匠J・ベッケル監督が描いた、感動の人間ドラマ。ピエロに扮して町の人々を笑わせる中年教師の、その道化を演じる理由に隠された秘話が明かされる。「奇人たちの晩餐会」のJ・ヴィユレや「クリムゾン・リバー２／黙示録の天使たち」のB・マジメルら演技派の競演が実現。各々個性を発揮した彼らの競演も印象深い。1960年代のフランスの田舎町。小学校教師ジャックは、毎週日曜日、ピエロの格好で町の人たちを楽しませていた。息子リュシアは、そんな父の事が大嫌いだった。しかし、父の真意には第二次世界大戦期のある出来事があった。", "2003-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRINCE WHO WAS A THIEF", "盗賊王子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "S・ドライサー原作の映画化。王位を狙うムスタファによって暗殺されかけた幼い王子ジュルナは、盗賊ユセフによって救われ、腕利きの盗賊として成長する。ある日、ムスタファの娘が結納の際に贈られたファティマの真珠が小娘の盗賊ティナに盗まれる。ジュルナはムスタファの娘に真珠を見つけ出すよう頼まれるが……。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tinker Bell and the Lost Treasure", "ティンカー・ベルと月の石", "", "", "http://image.eiga.k-img.com/images/movie/54563/315x210.jpg?2083", 0, "ディズニーの名作アニメ「ピーター・パン」に登場する妖精ティンカー・ベルの活躍を描くCGアニメの第2弾。もの作りの妖精であるティンカー・ベルは、ピクシーホロウに伝わる宝物「月の石」を納めるための「聖なる杖」を作るという重要な仕事を任される。ところが「月の石」から突然魔力が失われてしまったことから、ネバーランドは滅亡の危機に。そこでティンカー・ベルは、願いを叶えてくれる鏡が眠るという海賊の難破船を目指して旅に出る。", "2009-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ТРИ СЕСТЫ", "三人姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "帝政ロシア時代の末期、革命の中に生きた美人３人姉妹の生活を叙情的に描く。チェーホフの世界を見事に映画化したサムソーノフ監督は1955年に同じくチェーホフ原作の処女作「浮気な女」で、第16回ヴェネチア映画祭銀獅子賞を受賞している。", "1963-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRENCH DRESSING", "フレンチ・ドレッシング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "すっかりさびれた海辺の保養地ゴームレイ。ジャックは、愛すべきこの町をなんとか活気ある町に戻そうと、映画祭の開催を企画する。“フランスのセックス・シンボルを招いて話題を集めよう”と広報にも精を出し、市長の承認も得て順風満帆に見えたこの企画も、ライバル地の妨害などにあって思わぬトラブルを招いてしまう。K・ラッセルの長編第１作にあたるこの作品は、ラッセルの「エルガー」を見たプロデューサーが“ジャック・タチ・スタイルの映画を”と持ちかけた企画であった。このためラッセルはタチの作品を繰り返し見、“叙情的作品なのに、単なるコメディとしか受け入れられない不幸な映画”と後に回想している。彼のフィルモグラフィの中においても、この「フレンチ・ドレッシング」は愛すべき作品として、ほかを圧倒している。", "1964-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野良犬", "野良犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "巨匠・黒澤明監督が初めて本格的な犯罪サスペンスに挑んだ意欲作。暑い夏の日の午後。若い刑事村上は射撃練習を終え、満員のバスに乗り込み帰路につく。しかし、車内でコルト銃を盗まれたことに気づき、慌てて犯人らしき男を追うが結局路地裏で見失う。コルトには実弾が7発。村上の必死の捜索もむなしく、やがてそのコルトを使った強盗事件が起きてしまう。窮地に追い込まれた村上は老練な刑事佐藤の助けを借り、コルトの行方を追うのだった……。真夏の都会を覆う息苦しいほどの灼熱の空気が緊迫感を生み出し、切れ味鋭い演出が目を見張る。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ruzhaye Sabz", "グリーン・デイズ", "", "", "http://image.eiga.k-img.com/images/movie/55033/315x210.jpg?4692", 72, "2007年に発表した「子供の情景」が世界各地の映画祭で話題を集め、数々の賞を獲得したイランの若手女性監督ハナ・マフマルバフによる長編第3作。2009年6月に行われたイラン大統領選挙を背景に、心に傷を抱える若い女性の苦悩を描いたドラマと、市民たちによる激しいデモを捉えたドキュメンタリー映像とを融合させ、現在のテヘランが抱える問題を浮き彫りにする。第66回ベネチア国際映画祭、第10回東京フィルメックス上映作品。", "2009-11-25", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE´RE｀SE RAQUIN", "嘆きのテレーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ゾラ原作『テレーズ・ラカン』を現代風に脚色した作品。リヨンで商売を営むテレーズは、病弱な夫をよそに、頑健な運転手と恋仲に。三角関係から生じる殺人事件と当事者たちの運命が、カルネの冷徹な演出で見事に描かれた名作。", "1952-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Misterios de Lisboa", "ミステリーズ・オブ・リスボン（英題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自縄自縛の私", "自縄自縛の私", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les parapluies de Cherbourg", "シェルブールの雨傘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "結婚を誓い合った恋人同士。だが、男に送られてきた徴兵令状が、二人の人生を大きく変えてしまう……。シェルブールの美しい風景と全編に流れる甘美で哀愁漂うメロディーが、観るたびに新たな涙を誘うミュージカルの傑作。若きドヌーヴの美貌も見ごたえ十分。", "1964-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺にさわると危ないぜ", "俺にさわると危ないぜ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "日活ニュー・アクションの雄、長谷部安春監督のデビュー作。主人公・小林旭はベトナム帰りの報道カメラマン。彼の恋人の亡父が戦時中、莫大な金塊をどこかへ隠したという噂を聞きつけた国際ギャングとブラック・タイツ団なる女賊が争奪戦を繰り広げる。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NATURAL　CITY", "ナチュラル・シティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "終末感漂う2080年の未来世界を映像化した韓国発のSFアクション。舞台となるのは、かつてのソウルである巨大都市“メッカ・ライン・シティ”。アンドロイドの犯罪が多発するこの街で、人間の捜査官とアンドロイドの女性が禁断の恋に落ちていく様を描き出す。「ユリョン」で注目されたミン・ビョンチョン監督がデジタル技術を駆使し、韓国版「ブレードランナー」とも言うべきスタイリッシュなSF世界を創出した。廃棄処分を恐れたアンドロイドの犯罪で荒廃した未来都市。有害アンドロイドの除去を担当する捜査官Rは、一人のショーガールに心を奪われる。しかし彼女はアンドロイドだった……。", "2005-07-30", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Loveless", "ラブレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ウィレム・デフォーが映画初主演を果たしたバイカー・アクション。リーゼントに革ジャン、サングラスを身にまとい、ハーレーにまたがる男たち。リーダーのビンス率いる彼らは、デイトナのレース会場へ向かう途中で小さな町に立ち寄る。平和な町に突如現われたバイカー軍団。暴力、セックス、ロックンロールに明け暮れる彼らのおかげで、町は大混乱におちいり……。80年代ネオ・ロカビリー界の重鎮ロバート・ゴードンも出演。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A POZDRAVUJI VLASTOVSKY", "マルシカの金曜日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "第二次大戦直前のチェコスロバキアを舞台に、祖国のためにレジスタンス活動に身を投じ、22歳で処刑された実在の女性マルシカの姿を描いた作品。1942年、死刑判決を受けドイツ軍の刑務所に送られた彼女が綴った日記をもとに、処刑されるまでの獄中生活とレジスタンスの思い出を織り交ぜて映画化したもの。", "1972-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hide and Seek", "ハイド・アンド・シーク　暗闇のかくれんぼ", "", "", "http://image.eiga.k-img.com/images/movie/23363/315x210.jpg?2669", 102, "精神科医デビッドの妻アリソンが自殺し、9歳の娘エミリーは周囲に心を閉ざし、“チャーリー”という友人を持つようになる。デビッドは“チャーリー”を、心に痛みを持つ子供が生み出した想像上の友人だと考えるが、チャーリーのイタズラは次第にエスカレートしていく。名子役ダコタ・ファニングと名優ロバート・デ・ニーロが競演。デ・ニーロの同僚役でファムケ・ヤンセン、彼が心惹かれる隣人役でエリザベス・シューが共演。", "2005-04-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NATHALIE．．．", "恍惚〈こうこつ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "フランスの女性監督A・フォンテーヌが現代人の愛を見据えた意欲作。長年連れ添ってきた夫の不貞を知った妻が、娼婦に彼と関係するよう依頼することに。こうして始まった危うい三角関係の行方が描かれる。一見、過激な設定だが、内容は愛のモラルを真摯に問うもの。コミュニケーション不足が囁かれる現代人を鋭く見つめたラストに、衝撃を受ける人は少なくないはずだ。F・アルダン、E・ベアール、G・ドパルデューという名優３人の競演も見もの。", "2003-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVERY WHICH WAY BUT LOOSE", "ダーティファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "刑事もの、ウエスタンの路線に新趣向を、というわけで、イーストウッドはトラック運転手、腕っぷし一本の喧嘩野郎という設定。コメディ・タッチで、相棒のオランウータンのコミカルな“猿技”がおもしろい。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミラクルバナナ", "ミラクルバナナ", "", "", "http://image.eiga.k-img.com/images/movie/29513/315x210.jpg?2776", 105, "バナナの木から紙を作るという実在のプロジェクトにヒントを得て製作された感動作。大使館の派遣員としてハイチ共和国に赴任した三島幸子は、政情不安のため貧困にあえぐ国民の姿を目の当たりにし、ショックを受ける。ある日、バナナから紙ができることを知った幸子は、今まで捨てられてきたバナナの木を再利用して紙を作ることを思いつき、プロジェクトを立ち上げるが……。監督・脚本は「守ってあげたい！」「白い船」の錦織良成。", "2006-09-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PEOPLE VS.LARRY FLYNT", "ラリー・フリント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "ポルノ雑誌『ハスラー』を創刊した実在の風雲児ラリー・フリントの生き様を、鬼才O・ストーンと巨匠M・フォアマンが映画化。1970年代、オハイオ州のストリップ・クラブのオーナー、フリントが、ヌード写真入り新聞の成功に気を良くし、出版界に進出する。過激なグラビアとスキャンダル記事をフィーチャーした新雑誌『ハスラー』は売れに売れ、フリントは一躍大金持ちになるが、世間の良識派の反感を買った彼は法廷闘争に身を投じていく。法廷に星条旗のオムツ姿で乗り込むなど、やりたい放題のポルノ王の反骨精神が、やがてアメリカの言論の自由を問う大問題に移行していく展開がユニーク。フリント役のW・ハレルソンはアカデミー主演男優賞にノミネートされ、ロックシンガーのC・ラヴがパンクなルックスでフリントの妻アルシアを好演している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("孔雀城の花嫁", "孔雀城の花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「鳳城の花嫁」「鴬城の花嫁」に続く花嫁ものの第３作。将軍家の息女と結婚したものの、何かと現れる将軍家の位やしきたりにすっかり腹を立てた松平図書頭信直。かといって姫を突き返せばお家取つぶし。そんな信直の前に一人の山男が現れ“俺ならうまく仕込んでやる”と大言壮語、かくして姫と山男とその家族による奇妙な共同生活が始まる。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I LOVE RED LION", "I LOVE RED LION", "", "", "http://ecx.images-amazon.com/images/I/51Uzyg4GKIL._SL210_.jpg", 63, "里見要次郎、都若丸ら、大衆演劇界の人気スターが勢ぞろいしたアクションドラマ。スナックやキャバクラが軒を連ねる繁華街の裏通りにオープンしたラーメン屋「赤獅子亭」では、店主の倫太郎が、着物に花柄エプロン、コック帽という奇妙な姿で暖簾（のれん）を掲げている。次々と訪れる客たちによって、倫太郎の過去が次第に明らかになっていき……。共演は、魔法のiらんど「幼なじみ」の磯貝奈美、「童貞放浪記」の神楽坂恵ほか。", "2009-09-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Someone to Watch over Me", "誰かに見られてる", "", "", "http://ecx.images-amazon.com/images/I/51D7heVsrfL._SL210_.jpg", 107, "　ニューヨーク、社交界の華として知られる女性クレアは殺人事件の現場を目撃したために命を狙われることに。そんな彼女の護衛任務に着いたのは、妻子持ちの刑事マイク。生まれも育ちも全く異なるクレアとマイクはやがて心も体も結ばれる。一方、凶悪な犯人はマイクの妻子を人質に取るが……。陰湿な殺人犯の恐怖と不倫愛の行方を描いたサスペンス。スタイリッシュな映像とガーシュインの名曲が切なくもロマンティックなドラマを盛り上げる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月姫系図", "月姫系図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "武田勝頼の遺宝ををめぐる浪人と旗本娘の活躍を描いた伝奇娯楽アドベンチャー。遺宝の秘密は“風林火山”を象る４枚の枝折に託され旗本娘の雪絵が“風”を浪人の進太郎が“山”を持っていた。だが“林”の枝折を持つ奉行が忍者を使ってすべての枝折を集めようとする。謎は能面をつけた女“月姫”のみが知っているのだが……。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROTHER BEAR", "ブラザー・ベア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "熊の姿に変えられた青年と、母とはぐれた子熊の冒険を描く感動作。雄大で神秘的な大自然の中、無邪気な子熊との触れ合いによって、青年は愛の尊さを学んでいく。動物たちのユニークなキャラクターが笑いを誘うが、物語は力強くさわやかな感動を呼ぶ。J・フェニックスが主人公キナイ役で声優に初挑戦した。太古の昔。成人を迎えたキナイは、ふざけて一頭の熊を追い回したため、長兄を死なせてしまう。怒ったキナイは熊を殺害。その瞬間、彼は熊の姿に変えられてしまう。戸惑うキナイは母熊を探す子熊コーダと出会い、ともに旅に出る。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZIEGFELD GIRL", "美人劇場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "当時ブロードウェイを席巻していた“ジーグフェルド・フォリーズ”の華々しいレヴューと同時に、ショー・ガールたちの恋と人生を描いたミュージカル・ラブ・ロマンス。ミュージカル映画の華J・ガーランドを中心に、ルンバを歌って踊るシーンなどため息ものの仕上がり。３人の踊り子がショーに採用され人気を博し、地方巡業に向かう。その内の一人は仲間の弟と恋に落ちて……。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("集結號", "戦場のレクイエム", "", "", "http://image.eiga.k-img.com/images/movie/54083/315x210.jpg?3310", 124, "「女帝／エンペラー」のフォン・シャオガン監督が、ひとりの兵士を通して中国内戦の悲劇を描いた戦争映画。1948年、中国では毛沢東率いる人民解放軍と国民党軍が熾烈な内戦を繰り広げていた。最前線から1人だけ生き延びた人民解放軍の連隊長グー・ズーティは、自分が撤退命令のラッパを聞き逃したせいで仲間を失ったと自責の念にかられていた。「イノセントワールド　天下無賊」のチャン・ハンユーが主演。", "2009-01-17", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("30／thirty", "30／thirty", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "坂上忍が自身の小説『男が優しく眠る時』をもとに、初監督に挑んだ青春ドラマ。自分の生き方に迷いが生じ戸惑う青年の姿を、優しく見つめている。30歳を目前に控えた猛は、不眠症に悩まされ悶々とした日々を送っている。順調だったはずの生活にも息苦しさを感じ始める彼は、親友の自殺でさらに追い詰められていく。そんなある日、彼は美少女、冴と彼女が兄と呼ぶ朔と知り合い、彼らの不思議な魅力に引かれていく……。宅麻伸や佐藤浩市ら、演技派陣が脇を固める。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢中人／DREAM LOVERS", "夢中人〈ゆめなかびと〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "2000年前の秦王朝時代、強く愛し合いながらも王朝によって離れ離れにされた兵馬俑工芸家の夫婦がいた。彼らの魂は繰り返し出会い、現代では音楽家のソンイェと女性デザイナーのイエ・シャに宿る。そして二人が出会った時、悲劇の幕が開く。チョウ・ユンファ主演による、時空を超えた愛の物語。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORT SAGANNE", "フォート・サガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 182, "ガルデルの原作をもとにした長編スペクタクル。20世紀初頭、フランスの植民地拡大戦争をバックに、若き士官シャルル・サガンの愛と栄光の日々を描く。サガン役ドパルデューをめぐる二人の女に、マルソー、ドヌーヴの新旧美人女優が扮し、その色香をかけた競演ぶりが見もの。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I VITELLONI", "青春群像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "フェリーニという映像作家の秘密をさぐるとき、重要な鍵となる初期作品。北イタリアの港の見えるある小都市を舞台に、どこにでもいる５人の若者たちを描きながら、フェリーニ自身の思春期を語っている。女遊びに身をやつすファウスト、姉からいまだに小遣いをせびっているアルベルト、怠惰な生活から脱出したいモラルド、そして作家志望のレオポルド。彼らは毎日をぐうたらに過ごし、目的なしに生きている。そんなグループに嫌気が差したレオポルドは、一人ローマに向けて出発する。原題は“乳離れできないでいる仔牛”という意味で、フェリーニが育ったリミニの町ではのらくら青年たちをこう呼ぶ。", "1953-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREAT MOUSE DETECTIVE", "オリビアちゃんの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ヴィクトリア朝のロンドンを舞台に、ネズミたちの活躍を描くディズニー・アニメ。おもちゃ作りの天才である父親を誘拐されたオリビアは、名探偵バジルに助けを求める。バジルは親友の医者、ドーソン博士とともに捜査に乗り出し、やがて、女王の地位を狙う悪徳教授の存在を突き止める。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カイジ2 人生奪回ゲーム", "カイジ2 人生奪回ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/55953/315x210.jpg?2312", 133, "福本伸行の人気コミックを藤原竜也主演で実写映画化した「カイジ　人生逆転ゲーム」（2009）のシリーズ第2作。原作でも人気のエピソード「欲望の沼」をベースに、映画オリジナルのゲームを加えて描く。多額の借金を帳消しにしたカイジだったが、またしても借金まみれになってしまい、当たれば10億円以上を稼げるモンスターパチンコ台「沼」に挑戦することに。裏カジノの支配人で最大のライバルでもある一条聖也が裏で操る「沼」を攻略するため、カイジは同じ負け組仲間を集めてゲームに挑むが……。", "2011-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマンティガ　THE FINAL ODYSSEY", "ウルトラマンティガ　THE FINAL ODYSSEY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "人気特撮TVドラマの最終回から２年後という設定で、ヒーローの最後の戦いを劇場用映画として描いた真の最終話。超古代遺跡から邪悪な３巨人が蘇った。レナとの結婚を控えるダイゴことウルトラマンティガは彼らに戦いを挑むが、やがて自分がかつて彼らの仲間であったことを気づかされ、苦悩する。一方、ダイゴを愛するレナは……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("われに撃つ用意あり READY TO SHOOT", "われに撃つ用意あり READY TO SHOOT", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "新宿・歌舞伎町を舞台に、ヤクザ絡みの犯罪に巻き込まれた元全共闘闘士の戦いを描いたハードボイルド・ロマン。郷田克彦はスナックのマスターで、20年間続けた店を閉めようとしている。閉店パーティーには全共闘当時の仲間が集まった。そこにもう一人珍客が到来。肉体関係を迫る戸田組組長を撃ち殺し、ヤクザと警察の両方に追われるヤン・メイランだ。郷田は彼女をかくまうが……。原田芳雄、桃井かおりが絶妙のコンビネーションを見せる最後の殴り込みシーンは、まさにこの二人ならでは。新宿の熱気を伝える、スローモーションを多用したカメラワークにも注目。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パローレ", "パローレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "「パコダテ人」「ガキンチョ★ROCK」の前田哲監督が、人気お笑いコンビ“くりぃむしちゅー”の有田哲平と上田晋也を主演に撮り上げた異色の“幽霊”映画。夜な夜な幽霊たちが集まるという架空のクラブを舞台に、ゴーストに扮した有田と上田が、普段バラエティーで見せる毒々しい持ち味そのままに、ドラマを繰り広げる。優柔不断な山田は、交際中の幸子にプロポーズできずにいた。恋愛経験の乏しい二人の関係は、思うように進展しない。ある時、二人はひょんなことから六本木のラテン・クラブに入るが、そこは、幽霊たちが集まるクラブだった。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Romeo and Juliet", "ロミオとジュリエット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "何度も映画化されている有名なシェイクスピア悲劇の、戦後初のカラーによる映画化。15世紀中頃、イタリアのベロナ。名家キャピレット家の仮装舞踏会に、長年犬猿の仲であるモンターギュ家のロミオが潜り込み、一人娘ジュリエットに一目惚れ、二人は恋に落ちる。が、ロミオはその帰りに決闘の末に殺人を犯し、領主から追放される。一方、領主の甥との結婚が決まり、思い余ったジュリエットは神父に相談し、42時間仮死状態になる秘薬を服用するが……。公募されたシェントールが素晴らしい演技で絶賛を浴びた。イタリアン・ネオ・レアリスモの手法を大胆に採り入れたカステラーニの演出は、1954年度ヴェネチア映画祭金獅子賞を受賞。", "1954-01-01", "3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOU WERE NEVER LOVELIER", "晴れて今宵は", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "往年の大スター、フレッド・アステアとリタ・ヘイワースが共演したミュージカル。ニューヨークの人気ダンサー、ロバートは、休暇でブエノスアイレスを訪れる。そこで彼は内気な少女マリアと出会い、ある誤解から彼女に好意を寄せられるのだが……。トラブルだらけの恋の騒動を歌と踊りでロマンティックに綴る。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Titanic", "タイタニック", "", "", "http://ecx.images-amazon.com/images/I/51ryfgyvmWL._SL210_.jpg", 189, "J・キャメロンが、20世紀最大の悲劇ともいわれる“タイタニック号事件”の映画化に挑んだ超大作。1912年4月10日、かつてない威容を誇る豪華客船タイタニック号が、イギリスのサザンプトン港からニューヨークに向けて出発。出港直前にチケットを入手した画家志望の若者ジャックは、婚約者のいる名家の令嬢ローズとの許されぬ恋に落ちる。しかし14日深夜、タイタニック号の船首が北大西洋の氷河に衝突。浸水で船が傾き始めるなか、乗客たちは極限のパニックに陥る……。史上最高の製作費２億ドルをつぎ込んだ映像のスペクタクル度は、まさに空前絶後。事件のリサーチに並々ならぬ執念を燃やしたキャメロンは、全長236メートルのタイタニック号をほぼ原寸サイズでスタジオに再現し、衝突から沈没までの一時間強のパニック状況をほとんど時間的省略をせずに描き上げた。日米のみならず世界的規模で特大ヒットし、かなりの長尺にもかかわらず興行記録を大きく塗り替え、アカデミー賞も制した。", "1997-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("未来日記", "未来日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "バラエティー番組『ウンナンのホントコ！』でとりわけ人気の高かったコーナーの映画版。一般から公募された男女が、あらかじめ未来に起こる事が書かれている“未来日記”の内容通りにドラマのような恋愛を演じていく、という恋愛ドキュメンタリー。雄大な自然が美しい島根の小さな島を舞台に、都会にコンプレックスを抱いている島の男子高校生と自分に自信がない都会の女子高校生、そして、人に必要とされているか不安な都会の男子高校生が恋物語を演じていく。３人は峠のバス停で出会い、“未来日記”に書かれているそれぞれの目標の達成を目指しながら、一方で、時にはつらく、時には切ない様々な困難を乗り越えていく。そして、その過程で、次第にドラマを超える抑えきれない３人の感情を、GLAYの『SPECIAL THANKS』が盛り上げる。プロデュースをウッチャンナンチャン自身が務めた。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FASTEST GUN ALIVE", "必殺の一弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "早撃ち名人を父に持った男の宿命を描いた西部劇。名人の父から拳銃の撃ち方を教わり、早撃ちでは父以上の腕を持つようになりながら、生来の弱気から暴漢に襲われた父を救えずに見殺しにしてしまったジョージ。彼は自分が早撃ちであることがわかると町の平和を乱すからと町を去ろうとするが、無頼漢のハロルドがその噂を聞き付けてしまう。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Serpent", "蛇男", "", "", "http://image.eiga.k-img.com/images/movie/53543/315x210.jpg?2544", 119, "「ぼくの妻はシャルロット・ゲンズブール」のイバン・アタル＆「ロング・エンゲージメント」のクロビス・コルニアック主演によるフランス産サスペンス・スリラー。「赤と黒の接吻」のエリック・バルビエ監督がメガホンを取る。資産家の娘である妻と離婚協議を続けているカメラマンのバンサンは、ゆすり屋ジョゼフの策略にはまり殺人犯に仕立て上げられてしまう。やがてバンサンはジョゼフの目的が金でなく自分への復讐であったことを知る。", "2008-03-29", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEARTBURN", "心みだれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "映画の原作は、“ウォーターゲート事件”で名を馳せた辣腕記者カール・バーンスタインと著者エフロンとの、実際の結婚生活をモデルにしている。離婚経験者同士の結婚、出産、夫の浮気と破局が、速いテンポと明るいタッチで描かれ、軽妙な一編となった。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　PROMESSE", "イゴールの約束", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1996年のカンヌ映画祭の監督週間に出品され、国際芸術映画評論連盟賞を受賞した厳しくも切ない人間ドラマ。リュックとジャン・ピエールのダルデンヌ兄弟はドキュメンタリーで培った技術を駆使し、構図にとらわれない俳優中心のカメラワークで、リアリズムと緊張感あふれる映像作りに成功している。撮影当時14歳のJ・レニエは、これが映画デビュー作。移民労働者の売人である父と、貧しい移民との間で揺れるイゴールをみずみずしく演じた。主人公イゴールは、ベルギーの自動車修理工場で働きながら、父親ロジェの稼業も手伝う15歳の少年。父親の仕事は、違法外国人労働者の売買。移民の住んでいるアパートの家賃の取り立てや、違法滞在の外国人を引き渡しをするなど、ヤクザな行為の数々を罪の意識もなくこなしていた。そんなある日、移民者アミドゥが転落死するという事件が起きる。警察沙汰になるのを恐れたロジェは、アミドゥの死体をセメントで埋めるが、彼の行方を聞く妻のアシタにイゴールは気がとがめてならない。やがてロジェがアシタを売春婦として売り飛ばそうとしているのを知ると、イゴールはアシタとともに逃亡を図るのだが……。", "1996-01-01", "37,2,58", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUGH GUYS", "タフガイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "大胆不敵な手口で知られた伝説的強盗二人が30年ぶりにシャバへ戻って来た。だが、世の中はすっかり様変わり、二人は世の中の変貌に驚き自分たちがもう若くないことを知らされるが、最後の男気を懸けて列車乗っ取りを計画する。ハリー役のB・ランカスターとアーチー役K・ダグラスのコンビは、「OK牧場の決斗」以来30年ぶりの顔合わせで、ともに男の中の男といった役柄で売ってきただけに、時代のギャップに立たたされたこの設定が妙に生きている。監督のJ・カニューは「愛に向って走れ」でもK・ダグラスと組み、彼の持ち味を出している。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鋼の錬金術師　嘆きの丘（ミロス）の聖なる星", "鋼の錬金術師　嘆きの丘（ミロス）の聖なる星", "", "", "http://image.eiga.k-img.com/images/movie/55883/315x210.jpg?4815", 110, "2003年、09年と2度にわたりTVアニメ化された荒川弘による人気コミック「鋼の錬金術師」を映画化。アメストリスの首都セントラルにある刑務所から脱獄したメルビン・ボイジャーが操る強力な錬金術に興味を抱いたエドワードとアルフォンスのエルリック兄弟は、メルビンの後を追い、かつて「ミロス」と呼ばれた街テーブルシティにたどり着く。2人はそこで出会ったジュリアという少女から、街に隠された血ぬられた歴史を知る。脚本を「ホワイトアウト」などで知られる小説家の真保裕一が担当。", "2011-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HETS", "もだえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "高校生のヤンは、カリギュラと呼ばれる陰険な教師にいじめられていた。そんな彼が雑貨屋の娘に恋をした。だが、既に彼女はカリギュラに犯されていたのだった……。残忍な教師に引き裂かれる若者の悲劇。スウェーデンの巨匠、I・ベルイマンが脚本家として名を馳せた作品。", "1944-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARRYING　THE　MAFIA", "大変な結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "エリート青年とヤクザ一家の娘の波乱含みの恋を綴ったラブ・コメディ。突然の出会いから思わぬ運命をたどる主役の男女を、チョン・ジュノとキム・ジョンウンが好演。韓国では500万人以上を動員した大ヒット作だ。IT企業のエリート社員デソが、ヤクザ一家の娘ジンギョンと同じベッドで目を覚ます。ジンギョンの兄たちは烈火のごとく怒り出すが……。", "2005-07-23", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("処女宝", "処女宝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "前年「銀座カンカン娘」をヒットさせた島監督が作ったメロドラマ。真弓は劇作家の船田と愛し合っていたが、豪華な生活に惹かれて金持ちの義太郎と結婚する。傷ついた船田を勇気づけたのは真弓の妹・真金であった。船田は真金に励まされ、次々と作品を書くようになり、名声を得る。高杉と高峰が性格の違う姉妹を好演。のちのTV放映時には「競う美女姉妹」と改題された。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITIZEN X", "ロシア52人虐殺犯　チカチーロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "８年間に52人を殺害したロシアの殺人鬼チカチーロをモデルに、残虐な殺人鬼の凶行と犯人逮捕に執念を燃やす捜査官の苦闘を描く。1982年、森の中から９歳の少女が死体となって発見されたのを機に、女性や子供の惨殺死体が相次いで発見された。捜査官のブラコンは一連の犯行が同一犯であるとみて捜査を進めていき、犯人の手掛かりを得る。しかし、上層部は模範的な共産党員であったチカチーロを調べもせず釈放してしまう……。もとはTV映画だったが、日本では劇場公開された。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シェアハウス", "シェアハウス", "", "", "http://image.eiga.k-img.com/images/movie/56963/315x210.jpg?2561", 104, "湘南を舞台に性格も世代も異なる4人の女性が、一軒の家を共有して生活を始めたことから生まれるドラマを描く。海辺のカフェの常連客で、夫と娘夫婦に先立たれた有希子、売れないケータイ小説家の麗子、JA勤務で独身の花恵に、海で溺れかけていたところを助けられた女性まひるを加えた4人は、ふとしたきっかけで共同生活を始める。それぞれに悩みを抱えていた4人だったが、新しい生活が少しずつ変化をもたらしていく。主人公の4人の女性を演じるのは吉行和子、佐伯めぐみ、浅田美代子、木野花。そのほか、2011年9月で「モーニング娘。」を卒業した高橋愛と、「モーニング娘。」9期生の譜久村聖、生田衣梨奈、鞘師里保、鈴木香音も出演。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEST　IN　SHOW", "ドッグ・ショウ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ドッグ・ショーに集まる飼い主たちの個性と犬たちの妙演を擬似ドキュメント風に捉え、ゴールデン・グローブ賞最優秀作品にノミネートされるなど、高い評価を受けたコメディ。全米で最も名高いドッグ・ショーである“メイフラワー・ドッグ・ショー”に各地から様々な人間と犬たちが集まってくる。上昇志向のかたまりの弁護士夫婦、気弱な旦那に明るい妻という性格正反対夫婦に、犬に得意の腹話術で話しかける男、ボケ気味の億万長者と若妻の夫婦などなど。それぞれがさまざまな思惑を抱いてペンシルベニア州の会場に集結、いよいよドッグ・ショーが開催されるが……。「スパイラル・タップ」「みんなのうた」など、擬似ドキュメンタリーの名手としても知られるC・ゲストが手腕を発揮。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Las Vegas Story", "犯罪都市", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ギャンブル好きの夫に連れられ、ラスベガスへやって来たリンダ。かつてこの地でクラブ歌手として働いていた彼女は、兵士として海外に派遣されたまま行方知れずになっていた元婚約者デイブと再会する。彼はラスベガスに戻り、刑事として活躍していた。翌朝、クラブの支配人が遺体となって発見される。支配人から借金をしていたリンダの夫が容疑者として拘束されるが……。監督は「キング・ソロモン」のロバート・スティーブンソン。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRASH", "秘密指令クラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "巨大闇金融組織に挑む男の姿を描いたアクション。世界的な闇金融組織と、その裏に潜む麻薬組織に立ち向かう女性秘密捜査官のマデリン・ジェントリーは、組織のボス、カーロスにたて突く若い男ケイシー・ウッズと出会う。カーロス一味との対決に手を組んだ二人は、偶然組織の秘密が隠されたディスクを手に入れる。だが、次々に関係者は殺されていき、真相究明に乗り出す彼らにもカーロスの魔の手が忍び寄ってくる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン／　迷宮の十字路〈クロスロード〉", "名探偵コナン／　迷宮の十字路〈クロスロード〉", "", "", "http://ecx.images-amazon.com/images/I/513UkPDUpGL._SL210_.jpg", 107, "安定した人気を誇るアニメ・シリーズの劇場版第７作。歴史がらみのミステリアスで複雑な難事件に挑むコナンたちの活躍を、秀逸なトリックを用意しながらスリリングに追う。京都の由緒あるお寺の住職からある仕事を依頼された毛利小五郎は、コナン、蘭らを連れてそこを訪れた。彼らはそこで事件に巻き込まれる。真相の鍵は、手毬唄、義経と弁慶、翁の面、水晶の玉に隠されているようだが……。", "2003-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L.A.STORY", "L.A.ストーリー 恋が降る街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "互いに恋愛の悲劇的側面だけを見てきたばかりに、いつまでたっても素直に思いを伝え合うことができないでいる男と女。そんな彼らの前に現れた“恋の天使”は、なんとフリー・ウェイの道路情報を知らせる交通標識。ある夜、車を走らせている男に、“彼”は謎めいたメッセージを送ってくる。愛することに臆病になってしまった大人たちに贈るファンタスティックなラブ・ストーリー。ビデオタイトルは「L.A.ストーリー」。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MIRACLE OF MORGAN’S CREEK", "モーガン・クリークの奇跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "エルンスト・ルビッチと並び、ハリウッド正調コメディを得意としたP・スタージェス監督の、ノリのいい喜劇。第二次世界大戦中のキャンプ地、モーガン・クリーク。トルーティはパーティーで出会った女性と一夜をともに過ごす。それがもとで彼女は妊娠してしまうが、ひそかに思いを寄せていた幼なじみのノーバルが、その子の親になることを申し出る……。ストーリーそのものは他愛ないラブ・ストーリーだが、スタージェスのコメディ・センスがそこかしこに炸裂。ヒトラーやムッソリーニまでもギャグで登場させ、絶妙のテンポで物語が語られていく。喜劇映画の楽しみが満喫できる一編。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COFFEE　AND　CIGARETTES", "コーヒー＆シガレッツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ジム・ジャームッシュが18年に渡って撮り貯めてきた11の短編からなるオムニバス。コーヒーと煙草を口にして対面する人々の会話の風景が、淡々と映し出される。必要以上に気を遣ったり、相手に話を合わせようとして振り回されたりなどの気まずいシチュエーションから、ジャームッシュならではのドライなユーモアがにじみ出る。モノクロのスタイリッシュな映像や、演技派俳優に加えて人気ミュージシャンを配したキャスティングの妙にも注目。メンフィスを旅する姉弟、ふたりのミュージシャン、人気女優と従妹、米国で会った英国人俳優たち。コーヒーとタバコを味わいながら彼らの会話は少しずつ歯車が狂い出し……。", "2005-04-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOLDEN BOWL", "金色の嘘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "イギリスの文豪H・ジェームズの長編小説『金色の盃』をJ・アイヴォリーが映画化。19世紀初頭のロンドンを舞台に、億万長者アダムとその娘マギー、彼女と結婚したアメリーゴ公爵と彼のかつての恋人シャーロットが繰り広げる、複雑に絡み合った男女の愛憎劇。長大な原作を、アイヴォリーが手馴れた職人技で上手くまとめあげている。華麗な衣装に身を包んだ、シャーロット役のU・サーマンのゴージャスな美貌も見もの。　", "2000-01-01", "3,1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILENT THUNDER", "サイレントサンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "アメリカ中西部で実際に起きた事件をもとにした映画。破壊と殺りくをくり返す謎のトラック対復讐に燃える男の戦いを描いたアクション。トラック・ドライバーのクロードのもとに、家出した息子の死の知らせが届く。そこに悪意ある殺人の匂いを感じたクロードは、仲間の情報から不可解な一台のトラックの存在を知る。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("里山の学校", "里山の学校", "", "", "http://image.eiga.k-img.com/images/movie/56433/315x210.jpg?1404", 103, "千葉県にある木更津社会館保育園の園児たちが成長していく過程を追ったドキュメンタリー「里山っ子たち」（2008）、「Little Challengers 小さな挑戦者たち」（08）に続く「里山っ子」シリーズ第3弾。前2作の保育園児たちに代わり、小学生たちが千葉県木更津市の里山で毎週土曜日に開かれている学童保育「土曜学校」でのさまざまな出来事を通して成長していく姿を追う。", "2011-07-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("産業スパイ", "産業スパイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "一匹狼の産業スパイに梅宮辰夫が扮した工藤栄一監督によるスパイ・アクション。建設会社の社長の席を狙う調査室長から、ライバル社が発明した新型エンジンの設計機密を探るよう依頼された産業スパイの木暮。しかしその依頼主も実は同業者であった。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER THE TUSCAN SUN", "トスカーナの休日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "F・メイズのベストセラー小説をもとに描く、傷ついた女性の再生物語。離婚という悲劇に打ちのめされた女性作家が、友情に支えられ、人々の温かい心に触れるうちに、次第に立ち直っていく。そんなヒロインをD・レインがみずみずしい演技で好演。舞台となるトスカーナの美しい自然、陽光降り注ぐまばゆい風景がもう一つの主役だ。突然夫に離婚を言い渡された作家フランシスは、気晴らしにトスカーナにやって来る。彼女は、築300年の売家に一目惚れし、衝動買い。修復に取り組み始めた彼女は、地元の人々との交流を通して、次第に活力を取り戻していく。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やがて…春", "やがて…春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "東京のとある小学校の５年生のクラスを舞台に、ちょっとだらしない男の子と、田舎から転校してきたばかりのなまりの強い女の子が、クラスの中でいじめられ、追いつめられていく……。そのプロセスをきめ細かいタッチで描き、教育的配慮も十分な児童映画。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pida huivista kiinni, Tatjana", "愛しのタチアナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "2人の無口な中年男と2人の外国人女性の奇妙な旅を、アキ・カウリスマキ監督独特のタッチで描いたロードムービー。60年代フィンランド。コーヒー中毒の仕立て屋バルトと、彼の友人でロックンローラー気取りの修理工レイノは、退屈な田舎町を捨てて旅に出る。途中出会ったエストニア人とロシア人の女性2人組を港まで送ることになるが、彼らは会話することもなく、ただひたすら旅を続ける。そしてついに港に到着した4人は……。", "1994-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RECONSTRUCTION", "恋に落ちる確率", "", "", "http://ecx.images-amazon.com/images/I/21QDC1J6C2L._SL210_.jpg", 92, "“運命の相手のために、自分のすべてを捨てられるのか？”。そんな恋愛における究極の選択をテーマに、偶然出会った女性に運命を感じた男の愛の行方を描いたラブ・ストーリー。冒頭に登場する謎の手品師から始まり、物語は随所にトリックが仕組まれた構成で、現在、過去、未来がクロスしたような一風変わったストーリーが展開していく。手掛けたデンマークの新鋭C・ボー監督は、“ラース・フォン・トリアー以来の才能”と絶賛された。", "2004-12-11", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEST GUY", "BEST GUY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "航空自衛隊千歳基地。そこで３年振りにベストガイ（技術、知力、体力、人格、すべてにおいて優秀なパイロットの称号）を選ぶことに。F-15戦闘機パイロット・梶谷はそんなタイトルには無関心であった。女性ビデオディレクターとの恋、飛行班長との確執を交えて梶谷が成長する姿を描いた日本版「トップガン」。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Priest", "プリースト", "", "", "http://image.eiga.k-img.com/images/movie/55293/315x210.jpg?2156", 87, "何世紀にもわたりバンパイアと戦い続けていた人類は、高い防壁に囲まれ、教会の司祭たちが住民を支配する都市ディストピアで暮らしていた。教会の司祭で伝説の戦士と名高いプリーストは、ある日、街の外で人々がバンパイアに襲われているという話を聞く。バンパイアは絶滅したと主張する教会の妨害を受けながらも、プリーストは街の外へ出てバンパイアの巣くつへと向かう。主演ポール・ベタニーとスコット・スチュワート監督が、「レギオン」に続きタッグを組んだ3Dアクション。", "2011-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志・第三部", "次郎長三国志・第三部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "石松と三五郎は、旅の途中に金を持ち逃げされ、行き倒れになっていた力士・八尾ヶ岳久六の一行を連れて次郎長一家へ転がり込む。次郎長は一行を救うために相撲興行と花会を催すが、一家の急成長をよしとしない甲州の黒駒勝蔵との関係が険悪になる。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハーレム・バレンタイン・デイ", "ハーレム・バレンタイン・デイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "長谷川和彦を中心にフリーの監督９人が結成したディレクターズ・カンパニーの旗上げ製作によるピンク３本立ての１本。近未来を舞台にしたトリップ感覚に満ちたバイオレンス・ポルノで、永遠の不良少年・泉谷しげるが監督・脚本・音楽の３役を兼ねた。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Matrix Reloaded", "マトリックス・リローデッド", "", "", "http://image.eiga.k-img.com/images/movie/1153/315x210.jpg?3447", 138, "世界中で大ヒットした「マトリックス」は「リローデッド」「レボリューションズ」の3部作となってはじめて完結する。第1作からそのまま続く「2」「3」は前作のスタッフ、キャストが再結集して同時撮影。自ら高くしてしまった「VFX映画のスタンダード」のハードルを、果たして自身がクリアできるのか、今や世界中の注目を集める話題作。", "2003-06-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・大安旅行", "喜劇・大安旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "大阪発南紀行きの列車は新婚カップルで超満員。車掌の並木大作は、恋人が働く新宮駅の寿司屋に寄るのが唯一の楽しみで……。ヒロインの新珠は、東宝“社長”シリーズなどで、喜劇はお手のもの。ここでものちのTVドラマ『細うで繁盛記』などとは違った、軽い演技を披露している。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やじきた道中　てれすこ", "やじきた道中　てれすこ", "", "", "http://image.eiga.k-img.com/images/movie/34123/315x210.jpg?1461", 108, "“弥次さん喜多さん”コンビと花魁が繰り広げる珍道中を、数々の落語ネタを散りばめながら描いた人情喜劇。弥次喜多コンビに扮するのは、46年ぶりに映画主演を務める歌舞伎役者・中村勘三郎と個性派俳優の柄本明。さらに小泉今日子がヒロインを演じる。品川の遊郭で久々に再開した弥次さんと喜多さんは、売れっ子花魁・お喜乃の足抜けを手伝うことに。同じ頃、大阪では謎の生物“てれすこ”が捕獲され大騒ぎとなっていた……。", "2007-11-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刺青　背負う女", "刺青　背負う女", "", "", "http://image.eiga.k-img.com/images/movie/54623/315x210.jpg?1482", 0, "これまで幾度も映画化されてきた谷崎潤一郎の短編処女作「刺青」を、「ベロニカは死ぬことにした」の堀江慶が映画化。家族や不倫相手との関係に悩む編集者の真由美は、ある日本画展の取材で「魚濫観巫音（ぎょらんかんぜのん）」という絵に惹かれ、同じようにその絵に見入っていた純子という刺青の彫師と知り合う。そしてその夜、ふとしたことから出会ったタケシというチンピラの背中に純子の彫った刺青を見つけた真由美は、運命的なものを感じる。", "2009-06-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PUNITION", "飼育の部屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "20歳のブリットはドイツ南部のババリアから夢を求め、憧れの地パリにやって来たブロンド娘。そこで画廊のオーナー、マニエルを知るが、彼の本業は“飼育の部屋”と呼ばれる特殊な売春宿の経営だった。金のためにそこで働くようになり、次第に心と身体がむしばまれていくブリットだが……。R・ボーランジェが脚本を手掛けたエロティック・ドラマ。", "1978-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flashbacks of a Fool", "フラッシュバック", "", "", "http://ecx.images-amazon.com/images/I/51AMsDLWYUL._SL210_.jpg", 114, "", "2008-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春花夢露／A　DRIFTING　LIFE", "浮草人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "俳優としても活躍する台湾の気鋭リン・チェンシェンが、ツァイ・ミンリャン監督作品でおなじみのリー・カンションを主演に据えて撮り上げた長編デビュー作。監督の故郷である台湾東部の農村地帯が舞台になっている。農作業中に妻を亡くし、失意のあまり二人の子供を両親に預けて放浪生活に出た青年クン。彼の運命を見据えながら、家族や人生といった普遍的なテーマがじっくり描かれていく。1996年の東京国際映画祭ヤングシネマ部門でシルバー賞を獲得。", "1996-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狂い咲きサンダーロード", "狂い咲きサンダーロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "幻の街・サンダーロードを舞台に、右翼と暴走族の連合軍にたった一人で戦いを挑む少年の姿を描くSFバイオレンス・アクション。監督・石井聰亙の名を知らしめた作品で、ストーリーの語り口は未熟だが、短いカットを積み重ねたスピーディーなアクション・シーンが個性的。大学の卒業製作として作られた16ミリ映画だったが、東映の目にとまり35ミリにブロウ・アップ、劇場公開された。シャブ中の少年、ゲイの右翼、マッドボンバーなど破滅的な人物が数多く登場するが、映画は底知れぬパワーが画面からにじみ出てくる前向きな作品だ。名バイプレーヤー、山田辰夫が主役デビューを飾った。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUST　VISITING", "マイ・ラブリー・フィアンセ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ジャン・レノがフランスで主演したコメディ「おかしなおかしな訪問者」をハリウッドでリメイク。中世から現代のN.Y.にタイムスリップした騎士が大騒動を巻き起こす。魔女の呪いにかけられたチボールト伯爵が、誤って妻を刺し殺してしまう。過去を取り戻そうと魔術師を呼び魔法の薬を飲んだ彼は、何故か現代にタイムスリップしてしまう。そこで彼は、亡き妻にそっくりの女性に出会うが……。フランス本国ではコメディ作品に多く出演しているレノ。彼のコミカル・センスに合わせて作られたというプロットで、見事にその実力を見せている。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地下室", "地下室", "", "", "http://image.eiga.k-img.com/images/movie/55083/315x210.jpg?4768", 92, "大手商社に勤めるOLの高梨美沙子は、いまだ独身を貫く41歳。ある日美沙子は、オフィスに出入りするメッセンジャーの恵介と偶然に出会い、この男を運命の人と思い込む。20歳の年の差をもろともせず、強引に恵介の気を引こうとする美沙子の言動はエスカレートし、ついには地下室に恵介を監禁してしまう。主演は元「シェイプアップガールズ」の中島史恵、「ふぞろいな秘密」の河合龍之介。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRAN D’ARRE´T", "別れのスキャット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ミラノに住むダビッドは大富豪の息子。彼は、自分のために１年前に自殺してしまった娘のことで罪の意識にさいなまれ続けていた。彼の話を聞いた医師ルカは、娘の自殺の裏に犯罪の影を感じ、事の真相を追及していく……。", "1969-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若様やくざ", "若様やくざ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "宮津藩松平家から、将軍より贈られた千鳥の香炉が盗まれた。盗んだのは、大名・風早主税之亮から頼まれた、二代目鼠小僧。だが、風早が報酬を渋ったため、再び香炉は鼠小僧に奪われる。そして、その行方を追う謎の侍が出現するが……。時代劇の定石、秘宝争奪戦に、鼠小僧を絡めた、痛快時代劇。ビデオ発売時のタイトル「橋蔵の若様やくざ」。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Hole in the Head", "波も涙も暖かい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "巨匠フランク・キャプラが8年ぶりにメガホンをとり、フランク・シナトラが主演をつとめたコメディタッチのヒューマンドラマ。マイアミで小さなホテルを経営するトニーは、妻に先立たれ、幼い息子アニーと2人で暮らしていた。ところが彼は金や女にだらしがないダメ男で、若いガールフレンドと遊びまわっているうちに借金がかさみ、ホテルは経営不振に陥ってしまう。なんとかホテルを再建しようと、資金集めに奔走するのだが……。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Music Cubana", "ミュージック・クバーナ", "", "", "http://image.eiga.k-img.com/images/movie/33503/315x210.jpg?4527", 88, "ビム・ベンダースが、「ブエナ・ビスタ・ソシアル・クラブ」の撮影時に多くの若手ミュージシャンからアプローチを受けて誕生した、キューバ音楽の伝統と今を映し出す音楽ドキュメンタリー映画。06年3月に逝去したキューバ・ミュージック界の重鎮、ピオ・レイバが本人役で登場するほか、若手ミュージシャンの日常に焦点を当て、バンド“ザ・サンズ・オブ・キューバ”の結成から東京公演を果たすまでを描く。", "2006-07-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("本場ぢょしこうマニュアル　初恋微熱篇", "本場ぢょしこうマニュアル　初恋微熱篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "『ヤングマガジン』に連載された有間しのぶ原作の同名マンガの映画化で、女子校に通う３人の少女たちの恋と友情が、同じ相手を好きになってしまう悲喜劇、知的で美人な先輩に同性愛的思慕などのエピソードを折り込み、ハチャメチャな学園を舞台に展開されている。女の子同士のセックスをめぐる会話が映画中ポンポンと飛び出すが、下品な感じがなく爽快感さえ覚えさせて、笑いの中にもさわやかな感動を与えてくれる青春映画に仕上がっている。工藤夕貴が演じる主人公の家庭内もうまく描かれていて、親子の間で交わされる会話も微笑ましくて快い。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("What Lies Beneath", "ホワット・ライズ・ビニース", "", "", "http://image.eiga.k-img.com/images/movie/1743/315x210.jpg?3967", 130, "バーモントの美しい湖のほとりに建つ瀟洒な家。娘を大学へと送り出したノーマン教授と妻のクレアは二人で幸せに暮らしていた。最近隣に引っ越してきたフューアー夫妻はケンカが絶えず、心配して隣家を訪ねたクレアはフューアーの妻メアリーから「夫が恐い」と打ち明けられる。数日後、隣家からメアリーは姿を消し、それと同時にクレアの周りでは奇妙な出来事が起こりはじめる。", "2000-12-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀幕版　スシ王子！　ニューヨークへ行く", "銀幕版　スシ王子！　ニューヨークへ行く", "", "", "http://image.eiga.k-img.com/images/movie/53343/315x210.jpg?3927", 114, "“スシ王子”の異名を取る若き天才寿司職人の米寿司は、マンハッタンで店を出すシャリの達人・俵源五郎に弟子入りするためニューヨークへ。しかし、源五郎の寿司屋「八十八」は、ニューヨークの寿司屋を牛耳るミスター・リンと配下のペペロンチーノ一味に狙われていた……。「トリック」「自虐の詩」の堤幸彦監督が手掛けたTVドラマ「スシ王子！」に続く劇場版。“スシ王子”こと米寿司役の堂本光一（KinKi Kids）は、本作が映画単独初主演。", "2008-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　BODY", "抹殺者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "イエス・キリストの遺骨をめぐる争奪戦を描くサスペンス・アクション。聖地エルサレムでキリストのものと思われる遺骨が発見される。調査を依頼された元情報部員の神父マットは、女性考古学者シャロンとともに世紀の発見を裏付けるために奔走。だが二人は、その遺骨を狙うバチカン、イスラエル、パレスチナ、それぞれの思惑が入り乱れる抗争に巻き込まれ……。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOWN CAME A BLACKBIRD", "闇に抱かれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "異国で監禁生活を強いられた女性の苦悩を描く社会派サスペンス。「ジュラシック・パーク」のL・ダーンが製作総指揮とともに主演を兼任し、熱演をみせる。米国の女性記者ヘレンは中米の軍事政権を取材中、秘密警察に逮捕され、一年後に変わり果てた姿で帰国。仕事に没頭する彼女だったが、心の傷は癒えない。そんなある日、ヘレンは拷問の後遺症を治療する医療センターの存在を知り……。ビデオタイトルは「ローラ・ダーン　闇に抱かれて」。", "1996-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母子草", "母子草", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "血のつながらない先妻の子二人と実の子一人を、女手一つで育ててきた女性・小沢しげとその子供たちの姿を、富士山麓のとある町を舞台に描き出した人情物語。1942年に田坂具隆が監督した同名作品の再映画化。俳優・山村聰が「沙羅の花の峠」以来、４年振りにメガホンを取った。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Casino Royale", "007 カジノ・ロワイヤル（2006）", "", "", "http://image.eiga.k-img.com/images/movie/33783/315x210.jpg?4112", 144, "6代目ジェームズ・ボンドにシリーズ初となる金髪碧眼のダニエル・クレイグ（「ミュンヘン」「レイヤー・ケーキ」）を迎え、イアン・フレミングによる最初の原作を元にジェームズ・ボンドが007になるまでを描くシリーズ第21作。脚本には「クラッシュ」「父親たちの星条旗」のポール・ハギスが参加。監督は第17作「ゴールデンアイ」以来の再登板となったマーティン・キャンベル。", "2006-12-01", "3,1,43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古都憂愁姉いもうと", "古都憂愁姉いもうと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "三隅研次監督による、数少ない現代劇の女性映画。京都で老舗の料亭ののれんを守るきよ子は、結婚に失敗し、今では店のことと、妹・ひさ子のことだけを考えている。そのひさ子の婚約者、明男が、きよ子に愛を打ち明けた。きよ子は苦悩しながらも、明男と一夜の過ちを犯して……。藤村志保が、初主演ながら、きよ子役を見事に演じた。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しあわせのかおり", "しあわせのかおり", "", "", "http://image.eiga.k-img.com/images/movie/53333/315x210.jpg?2234", 124, "年老いた中国出身の名料理人の王と、王に弟子入りするシングルマザーの貴子の血縁を超えた絆を描くドラマ。誠実だが不器用な王を「アカルイミライ」の藤達也が、仕事を辞めてまで王に弟子入りする貴子を「嫌われ松子の一生」の中谷美紀が好演。金沢の港町にある小さな中国料理店を営む王は、ある日突然病に倒れ、料理を続けるには難しい体になってしまう。デパートの営業で訪れた際に王の料理に魅了された貴子は店の事情を知ると、料理を教えてほしいと王に申し出る。", "2008-10-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("學校覇王／SCHOOL　DAYS", "金城武 スクール・デイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "金城武の主演による学園アクション。不良グループが抗争を繰り広げる、暴力に支配されたハイスクールに、一人の転校生が出現。彼の謎めいた行動は、校内の荒んだ空気に少しずつ変化をもたらしていく。金城はクールな振る舞いとハードな立ち回りを演じるとともに、主題歌も歌い、シンガーとしての魅力もアピール。", "1995-01-01", "53,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADVENTURES OF A YOUNG MAN", "青年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "E・ヘミングウェイの短編小説を社会派のM・リット監督が叙情的に映画化した青春映画。ミシガン州の若者ニックは、自由を求めてニューヨークへと旅立っていった。やがて第一次大戦が勃発し、従軍する看護婦のロザンナと深く愛し合うようになるのだが……。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Where the Truth Lies", "秘密のかけら", "", "", "http://image.eiga.k-img.com/images/movie/1083/315x210.jpg?1701", 108, "「スウィート・ヒアアフター」でカンヌ映画祭グランプリを受賞したカナダの鬼才アトム・エゴヤン監督が、1950年代のショービズ界の光と闇を描く異色サスペンス。「ミスティック・リバー」のケビン・ベーコン、「真珠の耳飾りの少女」のコリン・ファース、「ビッグ・フィッシュ」のアリソン・ローマンが共演。舞台で不思議の国のアリスに扮した少女がジェファーソン・エアプレインの曲「ホワイト・ラビット」で踊る場面がある。", "2005-12-23", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blood Oath", "アンボンで何が裁かれたか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1942年、日本軍は戦略的に非常に重要なポイントとなるインドネシア諸島アンボンに侵攻、そこには屈強なオーストラリア兵らがいたが、わずか６日間で陥落した。それから３年半、捕虜たちには長く苦しい日々が続いた……。終戦後、このアンボン島での出来事の裁判を担当したオーストラリア人検事の実体験をベースに、いくつかの事実に基づいて描いた衝撃の戦争裁判もの。", "1990-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA DISCRE｀TE", "恋愛小説ができるまで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "パリの左岸、美しいサンジェルマンの街を舞台に、恋愛をゲームに見立て、わざと悲劇に終わらせてその小説を書かせようとするあくどい書店店主と、それを実行に移す青年の物語。恋人の発見の仕方、誘惑するテクニック、ヤマ場の作り方など、“パリ版恋愛マニュアル”になっているところがミソ。ヴェネチア映画祭で国際批評家賞ほかを受賞。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GATHERING", "ギャザリング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「オスカー・ワイルド」のB・ギルバート監督による異色のサスペンス・スリラー。舞台となるのは謎の教会が発見されたイギリスの田舎町。この町を訪れたアメリカ人のキャシーが事故で記憶を失い、それ以来、悪夢に見舞われる。この悪夢はやがて教会に眠る“ギャザリング＝集う者たち”と呼ばれる人々が背負った恐るべき宿命と合致。ラストにその衝撃の真相が明かされる。二転三転する展開はもちろんのこと、張り詰めた情景描写と冷たい空気の漂う映像がさらに恐怖感を高める。また、どこか陰のあるヒロインをC・リッチが繊細に表現する。", "2002-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAROONED", "宇宙からの脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "宇宙船“アイアンマン１号”に異常事態発生！　地球へ戻るための推進ロケットが点火しないのだ。次第に残り少なくなっていく船内の酸素。３人の飛行士の不安と恐怖が募っていく……。設定は興味深いが、極限状態における宇宙飛行士の心理・行動が、型通りなのがやや物足りない。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CAPTIVES", "愛に囚われて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ジョン・ヒーリーの自伝を基に描いたTVドラマ『The Grass Arena』の映画化。妻殺しで服役中の囚人と監獄の歯科医師が、刑務所内ではぐくむ恋を描いたラブ・ストーリー。夫の不倫が原因で離婚したばかりの歯科医レイチェルは生活のため、週２回刑務所の診療所に通うようになる。好奇の視線を投げかける囚人たちの中で、人懐っこいフィリップの笑顔が彼女の救いだった。やがて二人は恋に落ちる。出所を間近に控え、週１度夜間大学に通うフィリップと禁じられた愛を深めていくが、ある日レイチェルは彼が口論の末、妻を殺害したことを知ってしまう。気持ちのすれ違う二人に麻薬売人の囚人、タウラーが近づき……。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("踏みはずした春", "踏みはずした春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "清順の太陽族青春映画の第１作。実在の青少年非行矯正ボランティア団体の若い女に左幸子が扮し、使命感と少年への思慕との間で揺れる姿を好演している。清順演出は、説教臭いストーリーをモダンなリズムで飄々と描いている。若き小林旭のふてくされたアウトローぶりが楽しい。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIVIDE／ディバイド", "DIVIDE／ディバイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "「ロスト・バイ・デッド」で23歳にしてデビューを果たした新鋭、辻岡正人監督の第２作。無法地帯の東京に紛れ込んだ女子高生の姿をリアルに描き出す。母親を病死で失った高校生の紗希は、行き場を失う。そんな彼女にとって唯一の望みは、幼い頃に離れ離れになった妹と再会すること。暴力、売春、犯罪に満ちた東京で、さまざまな厳しい現実に直面しながらも彼女は必死に妹を探そうとするが……。", "2006-05-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NASSERDDIN SHAN ACTOR、−CINEMA", "ワンス・アポン・ア・タイム、シネマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "イラン映画史へのオマージュ的作品で、イラン映画の重要な作品が断片的に登場してくる。1900年、王宮を撮影しようとして撮影技師が捕まる。危うくギロチンにかけられそうになるが、映画を見た王様がスクリーンの中のヒロインに惚れてしまい、撮影技師は毎日、眠ることも許されず映画を上映し続けるのだった。", "1992-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ガンダムUC episode3「ラプラスの亡霊」", "機動戦士ガンダムUC episode3「ラプラスの亡霊」", "", "", "http://image.eiga.k-img.com/images/movie/56003/315x210.jpg?1808", 59, "「亡国のイージス」の福井晴敏による小説「機動戦士ガンダムUC」のアニメ化第3弾。ミネバを守るため、ユニコーンガンダムで出撃したバナージ・リンクスは、フル・フロンタルのシナンジュとの戦いで捕えられ、ネオ・ジオン残党軍「袖付き」の拠点である資源衛星・パラオに身柄を拘束されてしまう。バナージ救出のため、連邦軍のダグザ中佐率いる特殊部隊エコーズとネェル・アーガマとの連携によるパラオ攻略戦が開始される。", "2011-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファイナル・ファイト 最後の一撃", "ファイナル・ファイト 最後の一撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "香港及び東南アジアでは知らぬ者なしとまで伝えられる、国際派アクション・スター倉田保昭、構想10年のファイティング・ムービー。愛弟子をリングで殺された元空手チャンピオンが、カムバックしてリングに上るその闘志を描いていく。カンフー映画でならしたアンジェラ・マオばりのアクションを披露する新人・早瀬恵子が魅力的。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Old Dogs", "オールド・ドッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("肉体の門", "肉体の門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "「仁義なき戦い」などの笠原和夫脚本による５度目の映画化。戦後の廃墟を舞台にたくましく生き抜く女たちの姿を描く。女を撮らせたら右に出る者なしの五社英雄による痛快な一編。かたせ梨乃や名取裕子などの女優陣による“ヌード合戦”も話題に。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE BLE´ EN HERBE", "青い麦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "コレット女史の小説に基づき、思春期の揺れ動く少年少女の世界を映画化した佳編。第一次大戦後間もない北フランスの避暑地の海岸。16歳になるフィル少年と15歳の美しい少女バンカ。二人は夏の間一つ屋根の下で兄妹のように暮らす。映画を見たり、泳いだりして毎日を過ごすうちに恋人同士のような感情が芽ばえても決しておかしくなかった。ところがある日、少年は美しい白衣のマダムに出会い……。美しい音楽、白黒の印象的な撮影、青春の甘く苦い思い出が蘇る。", "1953-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しあわせのパン", "しあわせのパン", "", "", "http://image.eiga.k-img.com/images/movie/56093/315x210.jpg?2612", 114, "原田知世と大泉洋が主演し、北海道・洞爺湖のほとりの小さな町・月浦を舞台に、宿泊設備を備えたオーベルジュ式のパンカフェを営む夫婦と、店を訪れる人々の人生を四季の移ろいとともに描いたハートウォーミングドラマ。りえと尚の水縞夫妻は東京から北海道・月浦に移住し、パンカフェ「マーニ」を開く。尚がパンを焼き、りえがそれに合ったコーヒーと料理を出すマーニには、北海道から出ることができない青年や口のきけない少女とその父親、思い出の地を再訪した老夫婦などさまざまな人々がやってくる。", "2012-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("去年煙花特別多／THE　LONGEST　SUMMER", "花火降る夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "F・チャン監督による「メイド・イン・ホンコン」に次ぐ、“返還三部作”の第２作。返還で撤退する駐留英国軍から置き去りにされ、失業した香港人兵士のガーインは、再就職の保証もないまま不況の街に放り出される。弟の手引きで、マフィアの世界に入ったガーインは、かつての部下だった４人の仲間と銀行強盗を画策するが……。返還当時の香港を捉えたドキュメント映像が各所に散りばめられ、フィクションと不思議に絡み合い鮮烈な印象を残す。", "1998-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空の大怪獣ラドン", "空の大怪獣ラドン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "東宝が「ゴジラ」を凌ぐ２億円（うち特撮費は１億２千万円）の巨費を投じて製作した日本初のカラー怪獣映画の力作。大気中の放射性元素の増加により、阿蘇の地下洞窟に卵のまま眠っていた太古の翼手竜ラドンが孵化し、地震による大陥没地から地上へ飛び出して……。ラドンの登場シーン、ジェット戦闘機隊との大空中戦、福岡市内の破壊シーン、そしてラストの阿蘇山噴火によるラドンの最期と、終生航空映画を作ることを夢見ていた円谷英二の大空への憧れが十二分に発揮されており、名特撮シーンがたっぷり盛り込まれている。本多猪四郎監督は特撮とドラマのスピーディーなカッティングを心がけ、かつてないスピード感あふれる怪獣映画に仕上げた。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("", "ベビー・シッターズ・クラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ベビーシッターを始めた女の子たちの奮闘ぶりを、女性監督が温かいタッチで描いた作品。離婚した母親と再婚相手、そして４人の妹たちと暮らす13歳の少女クリスティは、仲間７人で結成したベビー・シッターズ・クラブのリーダー。クラブで企画したサマーキャンプも順調に始まったものの、クリスティの心はどこか重い。突然街に戻った実の父親と会えるようになった一", "", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Right Stuff", "ライトスタッフ", "", "", "http://ecx.images-amazon.com/images/I/51PEHr1iOXL._SL210_.jpg", 193, "トム・ウルフによる同名ベストセラー・ノンフィクションを、フィリップ・カウフマン監督が映画化した群像ドラマ。59年から63年にかけて実施されたマーキュリー計画（アメリカ初の有人宇宙飛行計画）の飛行士に選ばれた7人の男たちの友情や苦悩と、47年に人類で初めて音速の壁を破った孤高の戦闘機パイロット、チャック・イェーガーの生き方が対照的に描かれる。出演はサム・シェパード、スコット・グレン、エド・ハリス、デニス・クエイドほか。", "1984-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CONCERT FOR BANGLADESH", "バングラディシュ・コンサート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1971年８月１日、ニューヨークのマジソン・スクエア・ガーデンで行われた、バングラディシュ救済コンサートのドキュメンタリー映画。ジョージ・ハリスンやエリック・クラプトン、ボブ・ディランほかロック界の大物が総出演し“ウッドストック”以来の大イベントとなった。ハリスンの『バングラデシュ』も世界的に大ヒット。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・社長漫遊記", "続・社長漫遊記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "太陽ペイントの堂本社長、山中部長、木村課長の３人は九州出張旅行中。社長は奥さんを呼び寄せてサービスしている。そのころライバル・東西塗料の首脳が取引拡大を狙って長崎へ向かったとの情報。負けてはならじと３人組も急行するが……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Dog, the Thief", "俺の犬は大泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "イタズラ好きの犬が巻き起こす騒動を描いたコメディ作品。ラジオで交通情報の番組を担当している青年ジャックは、ひょんなことからセントバーナード犬のバラバスと相棒になる。ところがバラバスはイタズラばかりやりたい放題で、挙句の果てに泥棒が盗んだ宝石を勝手に持ってきてしまう。ジャックに横取りされたと勘違いした泥棒は、バラバスを誘拐して脅迫電話をかけるが……。監督は「メリー・ポピンズ」のロバート・スティーブンソン。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RACING WITH THE MOON", "月を追いかけて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "第二次世界大戦中のアメリカを舞台にした青春映画。ボウリングやローラースケート、ビリヤードなど、1940年代当時の若者たちの風俗を採り入れながら、主人公たちが大人へと脱皮していく姿を、透明感のある映像と音楽で詩情豊かに描き出している。主役にS・ペン、恋人役にE・マクガヴァンが共演。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("献身", "献身", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "電車の中で知り合った女・朝子の美しさに惹かれた検事の柏木。だが彼女は麻薬取り扱いの容疑で拘置されている男の妾だった。他人の目から見れば無償とも思える愛情をその男にひたむきに傾ける彼女を知って、柏木はますます激しい愛の炎を燃やす。丹羽文雄の新聞連載小説が原作の文芸メロドラマ。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OMEGA　DOME", "オメガ・ドーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "最終戦争によって人類が絶滅した未来社会を舞台に繰り広げられるSFアクション。“オメガ・ドーム”と呼ばれるアンドロイドが、廃墟の町に流れ着いた。町では二つのロボット集団が対立。オメガ・ドームは二つの組織の間を渡り歩きながら、壮絶な戦いをくぐり抜ける。やがてオメガ・ドームの失われたプログラムがよみがえった時、殺りくはさらに激化し……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初恋", "初恋", "", "", "http://image.eiga.k-img.com/images/movie/54493/315x210.jpg?3905", 52, "「トリコロール」3部作、「デカローグ」など数々の傑作を遺したポーランドの巨匠クシシュトフ・キエシロフスキーが1974年に手掛けたドキュメンタリー。妊娠をきっかけに20歳の学生と結婚することになった17歳の少女ヤチカの姿を追う。キエシロフスキーがドキュメンタリー作家として活躍していた時代の作品だが、ドラマ効果を出すためにかなりの演出が加えられている。クラクフ短編映画祭グランプリ受賞。", "2009-06-20", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東テキヤ一家 喧嘩火祭り", "関東テキヤ一家 喧嘩火祭り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "岐阜の裸祭りにやってきて矢崎一家にわらじを脱いだ国分たちは、次の市が開かれる秩父までのネタの運送を引き受ける。だが、途中で裏に手を回す岐阜の大藪一家にまんまとネタを奪われてしまう。国分はなんとかネタを取り返すものの、今度は秩父の槌田一家が現れて……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASTER OF THE WORLD", "空飛ぶ戦闘艦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "J・ヴェルヌのSF小説『征服者ロビュール』の映画化。19世紀末、新型気球の処女飛行に出かけたストロックたちは、世界征服をたくらむロバーが操る空飛ぶ戦闘艦に捕まる……。“空想科学”と呼ぶにふさわしい、夢とロマンに満ちた冒険アクション映画。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の女たち", "夜の女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "戦後間もない頃の大阪のパンパン（街娼婦）たちの様々な人生を点描する溝口健二の得意ジャンルの力作。この作品は、溝口が戦中から続いていた低迷状態からようやく抜け出す足がかりとなった。田中絹代が初の汚れ役に挑んでいる。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIVA LAS VEGAS", "ラスベガス万才", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "プレスリー映画の白眉。これまでの歌謡路線とは異なり、ミュージカルの要素が色濃く出た作品。レーサーのラッキーはラスベガスのレースに出るため、賭博で金もうけをもくろむのだが……。マーグレットとはこれが初共演。プレスリーは『ビバ・ラスベガス』『テキサスの黄色いバラ』など９曲を歌う。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／EVERYBODY　HAS　SECRETS", "誰にでも秘密がある", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "イ・ビョンホンとチェ・ジウが共演したラブ・ストーリー。女性ならば誰もが心奪われる完璧な美男子と、性格の異なる美人姉妹３人との秘密の関係を描く。ドロドロの愛憎劇と思いきや、意外なオチが用意され、物語はファンタジー・テイストのコミカルな仕上がりになっている。夫と倦怠期の長女、極端に奥手なニ女、自由奔放な三女の３人姉妹が、二枚目実業家スヒョンに恋をする。やがて彼らの関係は思わぬ方向に……。", "2004-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハレンチ学園 タックル・キッスの巻", "ハレンチ学園 タックル・キッスの巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "林功の第１回監督作品。聖ハレンチ学園は財政難のため存亡の危機に陥るが、ヒゲゴジラの故郷に宝の山があるという情報に、教師と生徒たちの間に壮絶な争奪戦が展開される。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAYONARA", "サヨナラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 147, "朝鮮戦線で活躍した米空軍のグルーバー少佐は、婚約者アイリーンの父でもあるウェブスター将軍のはからいで、日本で内地勤務することになった。将軍は日本にアイリーンを呼び寄せていたが、久々に再会した二人の仲はしっくりいかなかった。そんな折、グルーバーは歌劇の花形スター、ハナオギと出会い、その美しさに心打たれる。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("看海的日子／A FLOWER IN THE RAINING NIGHT", "海をみつめる日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "娼婦の生活に嫌けがさした白梅は、客の一人の子供を宿して故郷の村に帰り、自分だけで子供を育てようとする。村人の生活になんとか解けこもうとする彼女は、泥にまみれて畑を耕し、やがて男の子を産む……。丁寧なカメラワークと静かな語り口が好感を与える台湾映画復興期を代表する秀作である。", "1983-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Season of the Horse", "白い馬の季節", "", "", "http://image.eiga.k-img.com/images/movie/34233/315x210.jpg?1596", 105, "砂漠化が進み伝統文化が失われつつある中国・内モンゴルで生きる家族の姿を描いたヒューマンドラマ。内モンゴル出身の俳優ニンツァイが初メガホンを取り、自ら主演・脚本も手掛けた。多くの遊牧民が町へと移り住む中、荒れ果てた草原に残ったウルゲン一家。妻は唯一の財産である老馬を売って町で暮らそうとウルゲンに訴える。しかし、騎馬民族としての誇りを捨てられないウルゲンは、愛馬を手放して遊牧生活をやめることが受け入れられず……。", "2007-10-06", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ТРЯСИНА", "君たちのことは忘れない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "第二次大戦下、長男が戦地で行方不明中の農婦マトリョーナは、次男までも戦場に駆り出されようとするが、新兵派遣の駅が空襲にあったことから、次男を軍から隠し通そうとする。チュフライ監督が1978年に撮りながらも、軍当局の指示で上映を禁じられていた、いわくつきの問題作である。", "1978-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROMENONS−NOUS　DANS　LES　BOIS", "赤ずきんの森", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "フランスで公開されると同時に大ヒットしたティーンエイジャー・ホラー。８人の学生劇団員がアクセルという老紳士が住む森の古城に呼び寄せられる。アクセルは彼らに、幼い孫の誕生祝いに『赤ずきん』の芝居を演じてほしいというのだ。しかし彼らは古城を訪れた刑事からある事件について知らされる。それは、森の中に美しい女性ばかりを狙う連続殺人鬼がさまよっているということだった。やがて、まるで『赤ずきん』の物語のように一人、また一人と殺人鬼の餌食になっていき……。当時28歳のL・デルプランクが監督し、フランスで人気の若手俳優たちが共演した一編。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使のはらわた 赤い教室", "天使のはらわた 赤い教室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "ポルノ雑誌の編集者・村木は温泉町で見たブルー・フィルムの女、名美に惚れてしまう。実はそのフィルム、名美が昔、教室で強姦されて撮られたもの。忌まわしい過去を振り捨てようと、男を避けてラブホテルの受付嬢に身をやつす彼女に、村木は運命的な出会いをする。ところが、二人の逢い引きの約束は、村木が警察に引っ張られてご破算。事情を知らずに雨の公園で待ち続けた名美は、男への絶望を深めて奈落の底に堕ちていく……。行きずりの男とのわが身を削るような交情シーンを、カメラは粘りつくような長回しで捉え、女の転落への意志を描き出す。にっかつロマンポルノの一つの頂点をなす、激しくも切ない秀作。水原ゆう紀の捨て身の熱演や、赤らんだ人肌を不安定な構図で映し出す撮影も特筆もの。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市御用旅", "座頭市御用旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "座頭市は旅の空で死んだ母親の子を、父・佐太郎に渡すべく塩原にやって来るが、佐太郎はおらず妹・八重に渡す。そこに札つきのヤクザ・鉄五郎が乗り込んできて八重の身体を狙い、座頭市を亡き者にしようとする……。監督は、「不知火検校」で当時、影の薄い二枚目俳優だった勝新太郎を、人間味あふれる魅力的な俳優として開花させた森一生。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Yuki et Nina", "ユキとニナ", "", "", "http://image.eiga.k-img.com/images/movie/54363/315x210.jpg?4496", 93, "フランス人の父と日本人の母を持ち、パリで暮らす9歳の女の子・ユキは、ある日、両親が離婚しようとしていることを知り、母からは日本で暮らそうと告げられる。親友のニナと別れたくないユキは、ニナとともに家出を決意する。しかし2人は森の中で迷子になってしまい……。「不完全なふたり」「パリ・ジュテーム」の諏訪敦彦と、「愛のあとに」「イヴォンヌの香り」の名優イポリット・ジラルドが共同監督を務める。", "2010-01-23", "2,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH RIDE", "ホーンテッド　ハイウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "日本映画の海外セールスを行っている会社イレブンアーツが、初めて製作を手がけたサスペンス・ホラー。妻の死体を車に乗せ、夜道を疾走する男の恐怖を描く。主人公の愛人に扮した吉川ひなののアメリカ映画デビュー作でもある。愛人との関係を知った妻を、はずみで殺してしまったグレッグ。彼は妻の死体を車に乗せてハイウェイを走るうちに、極度の動揺と疲労のせいで現実と悪夢の区別がつかなくなっていく。", "2005-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stellet Licht", "静かな光", "", "", "http://image.eiga.k-img.com/images/movie/55003/315x210.jpg?4647", 144, "「ハポン」「バトル・イン・ヘブン」のメキシコ人監督カルロス・レイガダスによる長編第3作で、2007年のカンヌ国際映画祭で審査員賞を受賞した人間ドラマ。メキシコ・チワワ州で厳しい戒律を守りながら自給自足の暮らしを送るメノナイト派の平穏なコミュニティを舞台に、妻と7人の子供がありながら他の女性に恋をしてしまった男性が村人たちの間に巻き起こす異変を、大自然の美しい映像とともに映し出す。", "2009-10-22", "19,70,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFTERNOON", "Afternoon 昼下りの媚薬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ニューヨークの報道記者コートニーは、妻コニーを伴い、ブードゥー教の取材のためニューオリンズにやって来る。実は、コニーに怪しげな儀式を見学させて、性的興奮をあおるのが目的だ。到着早々、行動を開始するコートニーだが……。人妻コニーを、V・デミーがセクシーに演じるエロティックなラブ・ミステリー。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旋風〈かぜ〉の用心棒", "旋風〈かぜ〉の用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "黒澤明監督の名作「用心棒」を現代風にアレンジして人気を博した同名深夜アニメを、オリジナル・ストーリーで実写映画化。若き風来坊、谺丈二の活躍を描いた痛快ヒーロー活劇だ。とある荒涼とした港町。そこでは原子力発電所の建設を巡り、賛成派と反対派が激しく対立していた。その町に流れ着いた正体不明の若い男、谺丈二は、利権争いに巻き込まれる。しかし彼はある人物の消息を探っていたのだった。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一粒の種　真太陽の島の大合唱", "一粒の種　真太陽の島の大合唱", "", "", "http://image.eiga.k-img.com/images/movie/57503/315x210.jpg?4727", 29, "吉本興業が主催する2011年・第3回沖縄国際映画祭の企画「地域発信型プロジェクト」で製作された6本の短編作品を、「東」と「西」の3本ずつに分けて劇場公開する「沖縄国際映画祭　地域発信型映画」の「西」の1本。沖縄県・宮古島出身の看護師・高橋尚子さんと、同郷のミュージシャン下地勇が、ある末期ガン患者が残した言葉から作った歌「一粒の種」。同じく宮古島出身の歌手・砂川恵理歌が歌った同曲は、人間の生と死を見つめ、多くの人々に感動を与える。砂川と宮古島の人々が「一粒の種」を合唱する一日を追ったドキュメンタリー。", "2011-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロボジー", "ロボジー", "", "", "http://image.eiga.k-img.com/images/movie/56813/315x210.jpg?2056", 111, "「ウォーターボーイズ」「ハッピーフライト」の矢口史靖監督がロボットを題材に描くオリジナルのコメディドラマ。弱小家電メーカー・木村電器で働く小林、太田、長井の3人は、企業広告を目的に二足歩行のロボット「ニュー潮風」を開発していたが、発表直前に不慮の事故でロボットが大破。その場しのぎで、一人暮らしの頑固老人・鈴木にロボットの中に入ってもらうことにする。しかし、鈴木の勝手な活躍によりニュー潮風は大評判になり、たちまち世界中から注目を浴びてしまう。ミッキー・カーチスが「五十嵐信次」名義で主演し、ロボットおたくの女子大生役で吉高由里子が共演。ロボット開発を担当する3人組に濱田岳、川合正悟（チャンカワイ）、川島潤哉。", "2012-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乱暴者の世界", "乱暴者の世界", "", "", "http://image.eiga.k-img.com/images/movie/55603/315x210.jpg?1382", 0, "「ポチの告白」の高橋玄監督が1989年に手がけた自主制作映画を、「ワイルドナイト」の中田圭監督がリメイクしたSF青春ドラマ。ライブハウスで働く恵介は、ロック歌手を目指す恋人・あゆみと一緒に暮らしている。ある日、人の心を操る能力をもった青年カミオが現われ、あゆみを奪い去ってしまう。カミオの能力によって周囲の人々から存在を忘れ去られてしまった恵介は、たった1人でカミオに立ち向かうが……。", "2010-10-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林門", "ジャッキー・チェンの秘龍拳／少林門", "", "", "http://ecx.images-amazon.com/images/I/511-r0Q7ocL._SL210_.jpg", 99, "かつて少林寺の修行僧だったシャオヘンは全国制覇をたくらみ、少林寺の人間を次々と抹殺していった。十年後、難を逃れ身を隠していた高僧は、息子をシャオヘン討伐に送り出すが……。いまやハリウッドでの活躍めざましいジョン・ウー監督がエネルギッシュに放つ、本格派カンフー・アクション。香港カンフー映画の黄金時代を創り上げたジャッキー・チェンやサモ・ハンらがキレのあるカンフー・アクションを見せる。", "1974-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸川乱歩劇場 押繪と旅する男", "江戸川乱歩劇場 押繪と旅する男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "列車の中で出会った老人から、“私”は奇妙な話を聞かされた。それは昭和初期、浅草十二階の上から遠眼鏡で見た美しい娘に心を奪われ、ついに“押絵”の中に入ってしまった男の物語なのだが……。名バイプレーヤーの浜村純を主演に迎え、演劇界の奇才・飴屋法水など異色メンバーがそろった怪作。1994年に乱歩生誕100年記念作として公開された。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瞼の母", "瞼の母", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "東映の時代劇スター・中村錦之助主演、長谷川伸原作による股旅ものとして「関の弥太ッぺ」「沓掛時次郎・遊侠一匹」とともに、名作として記憶されるべき一本。東映が時代劇路線から任侠映画路線へと移行した時期の作品で、錦之助主演による長谷川伸ものの第１作にあたる。錦之助が扮する渡世人・番場の忠太郎が、別れ別れになった母親を探し求めて諸国を旅する……という、いまさら説明不要の母恋物語。メイン・ストーリーのほかに配されたエピソードも丹念に演出されている。忠太郎が実母に出会うまでに知り合う３人の母親像が夏川静江、浪花千栄子、沢村貞子によって演じられ、子を思う母の心が胸を打つ。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("51（ウーイー）　世界で一番小さく生まれたパンダ", "51（ウーイー）　世界で一番小さく生まれたパンダ", "", "", "http://image.eiga.k-img.com/images/movie/57643/315x210.jpg?2115", 80, "2006年、中国・成都のパンダ研究基地に誕生した体重わずか51グラムのパンダの赤ちゃん「ウーイー」の成長を追ったドキュメンタリー。通常の3分の1しか体重のない、史上最も小さなパンダの赤ちゃんとして生まれたウーイー。その命をつなぐべく奔走する飼育員たちの姿や、ウーイーの成長記録とともに、想像妊娠や代理母、育児拒否といった知られざるパンダの子育ての実態や生態にもスポットライトを当てる。", "2012-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画館落語　かもめ亭", "映画館落語　かもめ亭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-06-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("六三制愚連隊", "六三制愚連隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "亡き父の遺産を父が愛し続けた女性に渡すため、日本にやってきた二世の少年・ヘンリー岡田。知人の紹介で聖トーマス高校に入学した彼は、学校をシキる土佐犬と呼ばれる乱暴学生が麻薬売買に絡んでいることを知る。そして父の愛した女の息子が、組織にかかわっていることも……。西河克己の青春アクションもの。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CURDLED", "フェティッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "殺人事件フェチの女の子が、殺人鬼に思いを寄せる姿を描いたブラック・コメディ。３度の食事よりはるかに殺人に興味があるガブリエラは、趣味が高じて殺人現場専門の清掃会社に就職。その頃マイアミでは、富豪女性ばかりを狙う殺人鬼“ブルー・ブラッド・キラー”が連続殺人で世間を騒がせていた。ガブリエラは事件のスクラップブックまで作り、彼に思いこがれていた。そんなある日、念願だった殺人鬼の殺人現場清掃の仕事がまわってくる。マジメ一辺倒なガブリエラのボーイフレンドは彼女の趣味が理解できず現場を見て立ち去るが、殺人再現のダンスを踊る彼女の前に憧れの殺人鬼が現れ……。元々は短編映画だったが、クエンティン・タランティーノが気に入り、長編映画として作り直した作品。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴーストライターホテル", "ゴーストライターホテル", "", "", "http://image.eiga.k-img.com/images/movie/57663/315x210.jpg?2788", 81, "文豪の幽霊たちと作家志望の青年が繰り広げる騒動を描いたコメディドラマ。小説家を目指す内海は、かつて文豪たちがお忍びで宿泊したというホテル「本天堂」にやってくる。しかしそこは、執筆目的で宿泊したものの作品を書き上げることができなかった作家たちの叫びがこだまする幽霊ホテルだった。ホテルの清掃員として働きながら執筆を試みる内海の前に、文豪たちの幽霊が現れて……。主演は「花より男子」「インシテミル」の阿部力。世界のナベアツが夏目漱石、ケンドーコバヤシが宮沢賢治、カンニング竹山が江戸川乱歩など、人気お笑い芸人が文豪の幽霊を演じる。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOGUS", "僕のボーガス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ベテラン、N・ジュイソンによる心温まるファンタジー・コメディ。母子家庭の幼い少年アルバートは、母親を事故で失い、母親の親友ハリエットに引き取られることになる。ところが、キャリアウーマンの彼女にとって、小さな子供はお荷物。新しい環境にもなじめず寂しさを募らせるアルバートは、空想上の友人ボーガスだけが心の支えだ。目に見えない誰かと言葉を交わすアルバートを心配し、愛情を感じ始めるハリエットもまた、自分自身が心の壁を作っていることに気付くのだった。時折、現れてはおどけた態度で少年を励ますボーガスを、フランスの名優G・ドパルデューがコミカルに演じ、作品に夢と暖かみを与える。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tony Manero", "トニー・マネロ", "", "", "http://image.eiga.k-img.com/images/movie/54063/315x210.jpg?3278", 98, "第5回スペイン・ラテンアメリカ映画祭上映作品。2008年最優秀チリ映画賞受賞。1978年、チリの首都サンチアゴではピノチェト軍事政権の独裁で、反政府派の市民が駆逐されていた。ラウルは「サタデーナイトフィーバー」でジョン・トラボルタ演じるトニー・マネロに異常なまでの憧れを抱き、仲間とバーのショーで踊る中年男。社会や人間関係に背を向け、自分のアイドルに病的に傾倒してゆくラウルの姿を通じ、チリの暗黒時代の絶望とアイデンティティの喪失を描く。", "2008-09-13", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Young Victoria", "ヴィクトリア女王　世紀の愛", "", "", "http://image.eiga.k-img.com/images/movie/54893/315x210.jpg?2045", 100, "18歳で女王に即位し、ビクトリア朝と呼ばれるイギリスの繁栄時代を築いたビクトリア女王の若き日々を描く伝記映画。主演は「プラダを着た悪魔」のエミリー・ブラント。1837年、ウィリアム王が病に倒れ、王位継承権を持つビクトリアは王室の権力争いに巻き込まれていた。従弟のアルバートも次期女王の夫の地位を狙う1人であったが、2人は激しい恋に落ち、結婚することになる。しかし、女王となったビクトリアには母親との確執や政治家との駆け引きなど、さまざまな困難が待ち受けていた。", "2009-12-26", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モノクロームの少女", "モノクロームの少女", "", "", "http://image.eiga.k-img.com/images/movie/54703/315x210.jpg?4208", 99, "美しい自然の広がる新潟県長岡市を舞台に繰り広げられる青春ファンタジー。3年前の大地震で失った大事なキーホルダーを探すため、幼なじみの広志とともに壊れたままの中学校校舎跡を訪れたくるみ。そこで2人は古い木箱に入った写真を発見するが、写真の中で哀しげな表情を浮かべていた少女が突然、2人に向かって語り始める。ある青年に会いたいと言う少女の願いを叶えるため、くるみたちはその青年を探すことにするが……。", "2009-08-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXECUTIVE ACTION", "ダラスの熱い日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "史上最悪の暗殺事件として人々の記憶に長く残る、ダラスでのアメリカ大統領ジョン・F・ケネディ暗殺の真相に迫る作品。タカ派の男たちが暗殺を計画、用意周到な準備を着々と行い実行に移すまでをドキュメンタリー・タッチで描く。真相はいまだに謎が多く解明されていないが、一つの答えをこの作品から読み取ることができる。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("農奴／SERFS", "農奴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "代々農奴として苦しめられてきた家に生まれたチャンパは両親をムチで殺され、自らも農奴生活に耐える日々を送っていた。そこに解放軍がやって来て、チャンパたち農奴も反封建に目覚めていく……。チャンパ役は、実際に17年間農奴生活を送っていたワン・トイ。監督は1922年生まれの第二世代監督、リー・チュン。", "1963-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Finding Nemo", "ファインディング・ニモ", "", "", "http://ecx.images-amazon.com/images/I/51mYxaMCSFL._SL210_.jpg", 104, "オーストラリアの海に住むカクレクマノミのマーリンは、ひとり息子ニモを育てるシングル・ファーザー。ある日、ニモが人間のボートに近づいてさらわれてしまい、マーリンは物忘れの激しい魚ドリーの協力を得て、ニモを探す旅に出る。一方、ニモも人間に入れられた水槽の中で新たな友人たちと出会い、彼らに助けられて必死に逃げ出そうとしていた。「トイ・ストーリー」シリーズ、「バグズ・ライフ」「モンスターズ・インク」の脚本家アンドリュー・スタントンが監督業に進出。", "2003-12-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABY SITTER−UN MALEDETTO PASTICCIO", "危険なめぐり逢い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "愛人に捨てられ街に飛び出したアンと、彼女をはねたタクシーに乗っていたミッシェル。ローマで偶然出会った二人の若い女が、悪夢のような運命に巻き込まれるミステリー・ロマン。フランシス・レイの華麗なメロディーが全編を彩った作品。", "1975-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O.C. and Stiggs", "突撃！O・Cとスティッグス／お笑い黙示録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "　ふたりの高校生、O・Cとスティッグスはシュワブ一家に悪戯を繰り返す。一家が経営する保険会社が退職年金を取り消し、そのせいで祖父と一緒に暮らすO・Cも迷惑をこうむったからだ。やがてO・Cとスティッグスはさらにとんでもない悪戯作戦を立てるが……。鬼才アルトマン監督が人気雑誌「ナショナル・ランプーン」に登場するキャラクターを映画化。アメリカ社会を徹底的に皮肉った青春コメディ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOULEVARD", "並木道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "父とケンカをして家を飛び出した少年ジョルジュは、古いアパルトマンの屋根裏に住み込んでいる。そこには様々な人が住んでいたが、ジョルジュは美しい踊り子、ジェニーに思いを寄せていた。しかしあえなく失恋した彼は、傷心を慰めてくれたマリエッタと恋に落ち……。パリに生きる庶民の生活を、情感豊かに描き上げた佳作。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("顔役暁に死す", "顔役暁に死す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "1950年の「暗黒街」に始まる東宝の“暗黒街”ものの一編。アクション映画で名高い岡本喜八監督が大薮春彦の『火制地帯』を翻案、スピーディーで歯切れの良い独特の語り口で、地方暴力団の抗争を描いている。主演の加山雄三をはじめ、岡本組の俳優たちがみな好演。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLESH＋BLOOD", "グレート・ウォリアーズ　欲望の剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "貴族の傭兵マーチンは、仕事を成功させたのにもかかわらず、報酬も与えられずに追放されてしまう。怒ったマーチンは貴族たちに復讐を誓う。「ロボコップ」のP・ヴァーホーヴェン監督が戦いに明け暮れる中世ヨーロッパを舞台に、欲望渦巻く男たちの権力争いをダイナミックに描いたスペクタクル・ロマン。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やりすぎコンパニオンとアタシ物語", "やりすぎコンパニオンとアタシ物語", "", "", "http://image.eiga.k-img.com/images/movie/56873/315x210.jpg?4709", 0, "2007年～08年に「週刊ヤングマガジン」に連載された平本アキラの同名コミックを実写映画化。温泉街で新人コンパニオンとして働く18歳の上杉やりすと北条しづかが、温泉街にはびこる悪を退治していく様子をお色気たっぷりに描くアクションコメディ。元気で天然のやりすは、巨乳でしっかり者のしづかに勧められて飲んだ温泉水の影響で、超人に変身する体質になってしまう。温泉街を渡り歩いていた2人はある日、謎の黒服集団の襲撃を受ける。やりすが温泉水を飲んで「やりすぎコンパニオン」に変身し、敵を撃退していくが……。", "2011-09-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕と彼女の×××", "僕と彼女の×××", "", "", "http://ecx.images-amazon.com/images/I/3193GH2S9YL._SL210_.jpg", 0, "森永あいのコミック「僕と彼女の×××（ペケ3つ）」を、「ekiden」の浜本正機監督＆「花より男子」のサタケミキオ脚本で実写映画化した青春ラブコメディ。地味で真面目な男子高生・上原あきらは、同じクラスの男まさりな美少女・桃井菜々子に片思い中。ところがある日、菜々子の祖父・萬造が発明した機械によって、あきらと菜々子の中身が入れ替わってしまい……。主演を務めるのは、「妖怪大戦争」の高橋真唯と「パッチギ！」の塩谷瞬。", "2005-10-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("＠ベイビーメール", "＠ベイビーメール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ホラー小説界に新風を吹き込む山田悠介の同名ベストセラー小説を映画化。現代人にとって欠かせないアイテムとなった携帯電話をキーに、予想もしない恐怖のドラマが進展する。数々の心霊ドキュメントを手掛ける中村義洋が監督を担当。その緻密な恐怖描写でスリリングなホラーに仕上がった。恋人、朱美のもとに妙なメールが届いた教師の雅斗。このメールに嫌な胸騒ぎを覚える雅斗は、生徒の飛鳥から呪いのメールの噂を聞く。", "2005-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Largo Winch II", "ラルゴ・ウィンチ　裏切りと陰謀", "", "", "http://image.eiga.k-img.com/images/movie/56943/315x210.jpg?3829", 114, "若き大富豪ラルゴ・ウィンチの世界を股にかけた冒険を描くフランスの人気コミックシリーズを実写映画化。父親が暗殺され、若きラルゴは巨大財閥W（ウィンチ）グループ総帥の座に収まることに。しかし、権力を望まないラルゴは全遺産を慈善事業に投資しようとするが、その直前にミャンマーで3年前に起こった虐殺事件への関与を疑われ、拘束されてしまう。無実を証明するため、ラルゴは事件の裏側に隠された秘密口座「パンドラ」の謎を追う。主演は仏映画界の若手トメル・シスレー。ラルゴを追う検察官役に「氷の微笑」のシャロン・ストーン。", "2011-10-29", "2,37,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　SHOT　HEARD　AROUND　THE　WORLD．", "世界に轟いた銃声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "アメリカや日本をはじめ、全世界に衝撃が走った、日本人留学生の服部剛丈君射殺事件に迫るドキュメンタリー。文化の違いや言葉の壁が引き起こした悲劇と報道されたこの事件を、加害者ピアーズの尋問や服部君の友人たちによる証言、弁護士の考えなどを交えながら淡々と検証していく。このような事件がいつでも起こりうる、正常とは言い難い銃社会アメリカの苦悩は深い。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gallow Walker", "ギャロウ・ウォーカー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2010-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANKENSTEIN", "フランケンシュタイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "幾度となく映画化された古典的名作を原作にしたSFホラー。生命創造に野心を燃やす科学者フランケンシュタインは、ツギハギだらけのモンスターに生命を与えることに成功。だが、モンスターは逃亡してしまい、途中盲目の老人と出会って言葉や優しい心を教えられる。やがてフランケンシュタインの屋敷にモンスターは戻るが、誤って彼の弟を殺してしまい……。原作に忠実でありながらも、モンスターがフランケンシュタインの分身という設定で両者の恐怖や哀しみがリアルに描かれている。TVフィーチャー作品。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("主狼奇案／SENTENCED TO HANG", "実録 狼たちの挽歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "高度成長期以前の貧しかった香港で、平凡な３人の若者が、いかにして誘拐殺人の凶悪事件を起こすに至ったか……。1960年代初めに起きた有名な事件をもとに映画化した犯罪ドラマ。貧しい環境で育ちながらも、固い友情で結ばれたワイ、キン、チャンの３人。ある時、親類ウォンの会社で働くワイが、ウォンの息子にいびられ、ひどい仕打ちを受ける。仕返しに３人はその息子を誘拐、抵抗した息子を殺してしまう。開き直った彼らは、息子の耳を切り取り、ウォンに送りつけるが……。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初雪の恋　ヴァージン・スノー", "初雪の恋　ヴァージン・スノー", "", "", "http://image.eiga.k-img.com/images/movie/33973/315x210.jpg?1322", 101, "「王の男」のイ・ジュンギと「NANA」の宮崎あおい共演による日韓合作ラブストーリー。日本の高校に転校してきた韓国人青年と同級生の少女との純愛を、京都とソウルを舞台に美しい映像で綴る。大学教授の父親に連れられて日本にやって来たミンは、転校先の高校に通う七重にひと目惚れする。ミンの誠実さに少しずつ心を動かされていく七重。やがて2人はある約束を交わすが、七重がミンの前から姿を消してしまい……。", "2007-05-12", "76,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸川乱歩全集・恐怖奇形人間", "江戸川乱歩全集・恐怖奇形人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "江戸川乱歩原作の『パノラマ島奇談』ほか数編を石井輝男監督が映画化した“エログロ”ものの最終作。夢野久作の『ドグラ・マグラ』風な導入部から近親相姦の兄妹が花火の筒から火花とともに空中に四散するラストまで、石井輝男独自の世界が展開される。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEADLY TRIANGLE", "殺しのシュプール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "全米選抜バイアスロンチームの一人が、練習中、雪山で撃ち殺された。元オリンピックのスキー選手で、今は保安官のビルが捜査を担当することになる。チームの全員に殺害動機があり、しかも大量のライフル銃強奪事件が続発。捜査の先には、意外な真相が待っていた。豪快なスキー・チェイスが楽しめるサスペンス・アクション映画。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANNA　WUNDER", "アンナ・まなざしの向こうに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ドイツの新人監督が1960年代の田舎町を舞台に撮った家族劇。オーディションで選ばれた新人子役アリス・ディーケリングが、父親と会う日を夢見て前向きに生きる少女をみずみずしく演じる。幼い弟とともにすくすく育つ11歳の少女アンナの悩みは、アルコールに溺れる日々を過ごす母親のこと。そんなある日、アンナは母親から戦争で死んだはずの父親が生きていると聞かされる。", "2000-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Domino", "ドミノ", "", "", "http://image.eiga.k-img.com/images/movie/1363/315x210.jpg?4505", 127, "俳優とスーパーモデルの両親を持ち、モデルとして活躍するが飽きたらず、賞金稼ぎとして活動、今年の6月に自宅の浴槽で謎の死を遂げたドミノ・ハーヴェイ。この実在の人物をモデルに「パイレーツ・オブ・カリビアン」の人気女優キーラ・ナイトレイが熱演するアクション大作。監督は「エネミー・オブ・アメリカ」のトニー・スコット。共演はルーシー・リュー、ジャクリーン・ビセット、ミッキー・ローク、クリストファー・ウォーケンら。", "2005-10-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スレイヤーズ", "スレイヤーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 59, "セクシーだが時々笑わせてくれる、天才魔導士リナとナーガの活躍を描くSFファンタジー・アニメの劇場版第1作。リナとナーガは温泉島へ。過去と現在がシンクロする不思議なその島に着くと早速、彼女たちは怪しげな男たちに立ち向かう。さらに王様の依頼で、手強い魔物を相手にすることに。何とその正体は小さなカエルだったが、恐るべきパワーを持っていた……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE RITUAL", "愛のかたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1981年パリで起きた人肉嗜食事件(日本人留学生がオランダ人女子学生を殺し、その肉を食べたという事件)をベースにした作品。ヴァレリーはミュージカル・スターを目指し、レッスンとアルバイトに明け暮れていた。そんなある日、日本人の留学生、優朗に出会う。彼の哲学に共感して、“融合の極限”に興味を持つが……。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("接吻泥棒", "接吻泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "名匠、川島雄三による青春ドラマ。交通事故で気絶した女の子に、男が口移しに水を飲ませている写真が、週刊誌の表紙を飾った。男はボクシングのチャンピオン、女の子は名門高校の女生徒だ。彼女は大胆な男に反発を感じながらも、次第に惹かれていく。原作者の石原慎太郎が本人役で出演。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thelma & Louise", "テルマ＆ルイーズ", "", "", "http://ecx.images-amazon.com/images/I/51EVRQwNTPL._SL210_.jpg", 128, "　平凡な主婦のテルマとウェイトレスのルイーズは週末のドライブ旅行に出発する。しかしその途中、テルマが男に襲われるという事件が。あわやレイプというところで、助けに入ったルイーズが彼を拳銃で射殺。ふたりの女性たちは逃避行を続けながら、人生を謳歌していく。一方、刑事ハルがふたりを追うが……。犯罪者となった女ふたりの冒険と友情を描くロードムービー。", "1991-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Luz Silenciosa", "静かな光", "", "", "http://image.eiga.k-img.com/images/movie/54053/315x210.jpg?3261", 142, "第5回スペイン・ラテンアメリカ映画祭上映作品。2007年カンヌ国際映画祭審査員賞受賞。メキシコのチワワ州で、自然の営みと共存し、自給自足の村で素朴に生活する宗教一派メノナイト。その古風だが平和な村で、妻と7人の子どもと暮らしていたジョアンは、許されない恋をしてしまう……。", "2008-09-12", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRAND HOTEL", "グランド・ホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "限られた時間と場所に様々な人々を登場させる作劇法である“グランド・ホテル形式”の語源となった作品。当時のMGMの５大スターの競演が話題を呼んだ豪華版。人気が凋落して無気力なロシアのバレリーナ、男爵と称しているものの実は盗賊の男、生きるためなら何でもしようとする女性速記者、事業が危機に瀕している実業家、健康を害しこの世の名残りに贅沢をしに来た男の５人が、ベルリンのグランド・ホテルで一日半のうちに様々な人生模様を繰り広げる。バウムの原作小説をアメリカで舞台化した戯曲に基づき映画化。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八州遊侠伝 源太あばれ笠", "八州遊侠伝 源太あばれ笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「白鷺三味線」（1955）に続く、“八州遊伝”もの第２作。悪役人・大月忠馬の手から逃れた大岡源太郎。忠馬は、源太郎の恋人・お町をさらい、さらにお町の父・喜左衛門の命を狙う。かつて、忠馬は喜左衛門とともに幕府へ献上する、財宝と名器・白鷺三味線を奪った過去があり、事のすべてを知っている者を殺そうとしていたのだ。源太郎は、忠馬の悪事に立ち向かう……。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLOOMY　SUNDAY", "暗い日曜日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "人々を自殺に導くといわれ、英国では放送禁止にまでなったという呪われたシャンソンの名曲『暗い日曜日』。この曲の誕生にまつわる話をベースにした大人のラブ・ストーリー。第二次大戦間近、1930年代末のブダペスト。あるレストランのオーナーとその恋人、そこに現れた無名のピアニスト。激動の時代と運命にほんろうされながら、奇妙な三角関係に陥る男女の愛と哀しみをしっとりと描く。", "1999-01-01", "7,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海の上の君は、いつも笑顔。", "海の上の君は、いつも笑顔。", "", "", "http://image.eiga.k-img.com/images/movie/54343/315x210.jpg?3647", 100, "「ライフ・オン・ザ・ロングボード」「サンシャインデイズ」などで海やサーフィンを愛する人々を描き続けてきた喜多一郎監督が、「神様のパズル」の谷村美月を主演に迎えた青春ドラマ。湘南で暮らす女子高生・汀は、思いを寄せる同級生・陸に話しかけられ、サーフィンが得意だと嘘をついてしまう。すぐにサーフィンの練習を開始する汀だったが、あっという間に波に飲まれて兄の形見であるボードを無くしてしまい……。", "2009-05-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOMFIELD", "ストライカー 愛と栄光のフィールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "実力派俳優のR・ハリスが監督・主演の一人二役をこなした作品。、己の体力と、年齢の限界を感じてしまい、引退かそれとも再起かで苦悩するスポーツ選手の姿を描いている。故R・シュナイダーも出演。", "1969-01-01", "46,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大空に乾杯", "大空に乾杯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "吉永小百合がスチュワーデスに扮し、お馴染みの浜田光夫とも恋をする青春映画。二人のコンビ作としてはそれぞれ、ユニークなキャラクター設定が面白い。厳しい訓練を経てゆり子はプロとなり初フライトを無事済ませた。2度目には財界の大物の孫を助ける働きぶり。後日、その孫との縁談を持ち込まれるが、彼女の心は別の人にあった。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’E´TERNEL RETOUR", "悲恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "トリスタンとイゾルデの伝説を現代に置きかえて映画化した悲恋ドラマ。愛し合ってはいけない男女が、媚薬をのまされて熱烈な恋に落ちてしまうが、無理矢理引き離された後、二人は“死”によって永遠の愛を得るというお話。憂いを秘めたマレーが好演した。", "1943-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEFTIG　OG　BEGEISTRET", "歌え！フィッシャーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "北欧ノルウェーの小さな漁業の町ベルレヴォーグの自慢は、90年の歴史を誇る男声合唱団。メンバーは、96歳と87歳の“ストランド・ボーイズ”と呼ばれる兄弟を中心にした個性あふれる30人。そんな彼らの元にロシアからコンサートの招待状が届き……。本国ノルウェーでは、１年以上に及ぶロングラン・ヒットを記録したドキュメンタリー。映画のヒットにより合唱団の人気は国内だけに留まらず、ニューヨークをはじめ、全米５都市をめぐるコンサートまで実現させた。", "2001-01-01", "36", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Third Man", "第三の男", "", "", "http://ecx.images-amazon.com/images/I/51zVOE6M-kL._SL210_.jpg", 105, "イギリス人作家グレアム・グリーンのオリジナル脚本を名匠キャロル・リードが映画化したフィルムノワール。第2次大戦終戦直後、米英仏ソの四カ国による分割統治下にあったウィーンに親友ハリー・ライムを訪ねてきたアメリカ人作家のホリー。だが、ハリーの家に着くと守衛からハリーは交通事故で死亡したと告げられる。腑に落ちないホリーはウィーン中の関係者をあたり、真相究明に奔走するが……。出演はジョセフ・コットン、アリダ・バリ、そして謎の男ハリー・ライムにオーソン・ウェルズ。49年のカンヌ国際映画祭ではグランプリを受賞。", "1952-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Couples Retreat", "カップルズ・リトリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pretty Woman", "プリティ・ウーマン", "", "", "http://ecx.images-amazon.com/images/I/41SF4A26SWL._SL210_.jpg", 119, "ハリウッドの娼婦ビビアンは偶然知り合ったウォール街の実業家エドワードに1晩買われる。ビビアンに興味を持ったエドワードは１週間の契約を結ぶ。エドワードにとってはほんの気まぐれ、ビビアンにとっては最高のお客。その2人がいつしか惹かれ合い……。軽妙なタッチで綴ったシンデレラ・ストーリー。娼婦から淑女まで、ジュリア・ロバーツの変身ぶりも見どころ。", "1990-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポケットモンスター　幻のポケモン・ルギア爆誕", "ポケットモンスター　幻のポケモン・ルギア爆誕", "", "", "http://ecx.images-amazon.com/images/I/61hmPnPPVWL._SL210_.jpg", 82, "「…ミュウツーの逆襲」に続くシリーズ第２作。謎のコレクター、ジラルダンが火のポケモン“ファイヤー”をゲットしたことから、雷のポケモン“サンダー”と氷のポケモン“フリーザー”も加わった三つどもえの戦いが勃発。オレンジ諸島の旅を続けるサトシとピカチュウたち一行も、巻き込まれていく。前作同様、“幻のキャラクター”ルギアが登場。南海の大冒険物語を大いに盛り上げる。", "1999-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬の首輪とコロッケと", "犬の首輪とコロッケと", "", "", "http://image.eiga.k-img.com/images/movie/57463/315x210.jpg?3785", 85, "芸人・役者として活躍する長原成樹が、自伝小説「犬の首輪とコロッケと　セキとズイホウの30年」を自らのメガホンで映画化した監督第1作。大阪・生野区で毎日食卓にコロッケとキムチしか出さない在日韓国人の父親のもとに生まれたセイキは、ケンカや窃盗に明け暮れ、少年院に入れられてしまう。やがて少年院を出たセイキは、友人から紹介されたミチコのために真面目に生きることを決意。芸人を目指していた相棒のタツヤに誘われお笑いの道へ進むが、そんなセイキに対してミチコはある秘密を隠していた……。", "2012-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VROOM", "ブルーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "人口の大半は失業中という、イギリス北部の小さな町。ジェイクとリンジは、せっせと節約しハンドメイドの自動車を完成。ジェイクのガールフレンド、スーザンを誘い、３人は自由を求めて旅立った……。“ブルーム”とは車のエンジン音のこと。苦境にあえぎながらも夢を失うまいとする現代イギリスの若者像を、ビビッドに描いた青春映画。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蜂の巣の子供たち", "蜂の巣の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "無類の子供好きの清水宏には子供がなかった。そんなこともあり、戦争孤児と呼ばれる浮浪児たちを引き取り自ら面倒を見た。かくて“蜂の巣”という映画プロダクションを作り、その浮浪児たちと素人俳優たちを出演させ、私財を投じて製作したのが本作品である。復員した島村は帰るべき家も肉親もなく、下関駅前にただぼんやりと立っていた。ひょんなことから８人組の浮浪児たちと、引揚者の女性・夏木と知り合う。皆同じような立場にあったので、心に通い合うものがあった。一同は別々にあてのない放浪の旅を広島へ向けて開始する。素人俳優たちのリアルな演技、オールロケによる撮影など、清水の狙いの見事さを示す素晴らしい作品である。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プルメリアの伝説・天国のキッス", "プルメリアの伝説・天国のキッス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ホテル・チェーンの社長の息子と見合いをするが、ウィンド・サーフィンに情熱を燃やす造船所の臨時工員を愛してしまった女子大生の、悲劇的な結末を描いた純愛メロドラマ。「野菊の墓」に続く松田聖子の主演第２作で、ハワイの青い海を背景にしている。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殴者", "殴者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "明治初期に行われていた裏社会の格闘技を題材に、アウトローたちの生き様を描いた硬派ドラマ。主演を務める玉木宏の、抑制の中にも情熱を秘めたキャラクターの熱演は見もの。人気格闘技団体PRIDEのスター選手たちによる、肉弾相打つ凄まじいバトルの熱演も目が離せない。闇世界の格闘賭博“殴合”を仕切るヤクザの手下、暗雷。敵対組織との騒動を治めるため、彼は格闘で決着をつけることを提案するが……。", "2005-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZANDALEE", "ザンダリーという女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "確かな愛に支えられていたはずのザンダリーとティエリーの夫婦。だが仕事に疲れたティエリーは、昔のようにザンダリーを満足させることができず、少しずつ二人の間のすき間が広がり始める。そんな時、ザンダリーはティエリーの幼なじみジョニーと出会う。女性が真の愛の喜びを知る過程と結末を、衝撃的でエロティックに描いた官能サスペンス。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("苦役列車", "苦役列車", "", "", "http://image.eiga.k-img.com/images/movie/57563/315x210.jpg?3467", 112, "西村賢太による2010年・第144回芥川賞作を「天然コケッコー」「マイ・バック・ページ」の山下敦弘監督が映画化。昭和の終わりの酒と風俗におぼれる日雇い労働の青年の姿を通して、孤独や窮乏、生きる力について描き出していく。1987年、中卒で19歳の北町貫多は、日当5500円の日雇い労働でその日暮らしの生活を続けていた。生来の素行の悪さと性犯罪者だった父をもつ引け目から友人も恋人もいない貫多だったが、ある出会いによって大きく変化していく。主演は「世界の中心で、愛をさけぶ」「モテキ」の森山未來。貫多が港湾労働で知り合い、貫多に変化をもたらしていく専門学校生・日下部正二に高良健吾が扮する。映画オリジナルのヒロイン・桜井康子役で「AKB48」の前田敦子も出演。", "2012-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラストシーン", "ラストシーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ある映画撮影スタジオを舞台に、過去と現在を交錯させて描く感動の人間ドラマ。映画への愛が込められた一作。1960年代に活躍していたスター俳優、三原健は、コンビを組んでいたスター女優が引退したことから次第に落ち目になっていく。やがて自暴自棄になった三原のもとに、妻の訃報が届く。そして現在の同じスタジオ。人気TVの映画版を撮影中に、代役として一人の老人がやってくる。それは変わり果てた三原だった……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Joyful Noise", "ジョイフル♪ノイズ", "", "", "http://image.eiga.k-img.com/images/movie/57723/315x210.jpg?4460", 118, "「シカゴ」のクイーン・ラティファとカントリー歌手の大御所ドリー・パートンが共演する音楽コメディ。アメリカ南部・ジョージア州の小さな町パカショー。人々は不況にあえぐ中、教会の自慢の聖歌隊が全米聖歌隊コンクール「ジョイフル・ノイズ」で優勝することに希望を託していた。しかし、聖歌隊の中心的メンバーのバイ・ローズとG・G・スパロウがひょんなことから対立し、聖歌隊は分裂の危機に陥ってしまう。マイケル・ジャクソンやスティービー・ワンダーのヒット曲が多数登場。", "2012-04-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHAT THE MOON SAW", "リトル・シンドバッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "内気なスティーブン少年はヒロインを演じる女優のエマに憧れ、おばあちゃんの働く劇場に毎日芝居“シンドバッドの最後の冒険”を観に行っていた。そんなある日、物語の中の魔王ザカリーが現実に現れ、エマを夢の世界へ連れ去ってしまう。その時、スティーブンがシンドバッドに変身し、エマを助けるために夢の世界に飛び込むのだった……。", "1988-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tanaka-san Will Not Do Callisthenics", "田中さんはラジオ体操をしない", "", "", "http://image.eiga.k-img.com/images/movie/56353/315x210.jpg?3811", 75, "アマチュアのシンガーソングライターとして活動している田中哲朗さんは、25年前、毎朝のラジオ体操を拒否したことから大手電機会社を解雇される。それから25年間、田中さんは毎朝会社の前で抗議の歌を歌い、たった1人で戦い続けていた。そんな田中さんの姿に興味を抱いたオーストラリアの女性監督マリー・デロフスキーが、田中さんに密着したドキュメンタリー。外国人から見た田中さんの姿を通し、現代の日本をユーモアたっぷりに映し出す。", "2011-07-02", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAN INTO WOMAN", "マン・イントゥ・ウーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "少年の頃からずっと、男であることに苦痛を感じてきたゲーリー。彼は36歳で遂に性転換手術を受ける決意をした。この映画はそんな彼の姿を３年間にわたって追いかけたドキュメンタリーだ。細かな手術を重ねながら女性的な外見になっていく彼と、戸惑いを隠せない家族や同僚。フェミニンな白いドレスを着てはしゃぎながらも、様々な葛藤を抱えたゲーリーの苦悩がストレートに映し出されている。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢はバラ色に", "夢はバラ色に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "専務に意見してしまったことから窓際族の“今一課”に飛ばされてしまったアパレル会社社員の幸一。そんな中、米国帰りの社長令嬢が営業本部長として就任、これを機に出世を目指す幸一は……。和製「摩天楼はバラ色に」ともいえるサクセス・ストーリー。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("S-94", "S-94", "", "", "http://image.eiga.k-img.com/images/movie/57123/315x210.jpg?2469", 30, "「the hiding 潜伏」の福居ショウジン監督が手がけたSFバイオレンス。謎のウィルス「S-94」の感染拡大で人類の大半が死滅した2010年。奇跡的に生き残った美羽とアリスは地下シェルターにこもって暮らしていたが、ある日、周馬と名乗る男から助けを求める無線が入る。周馬は「S-94」に感染しない抗体をもっており、そのことがきっかけでアリスも同じ抗体をもっていることが発覚。周馬とアリスはひかれあっていくが、嫉妬心にかられた美羽が2人に襲いかかり、事態は凄惨なサバイバルに発展していく。", "2011-09-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上を向いて歩こう", "上を向いて歩こう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "アメリカでもNo.1になった坂本九の大ヒット曲『上を向いて歩こう』に想を得た歌謡メロドラマ。主演は坂本自身、監督は日活アクションの数々の名作を生みだした舛田利雄で、少年鑑別所を脱走した若者が音楽を通じて更生の道を歩み出す過程を生き生きと描いている。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUSUTINE", "マルキ・ド・サド 第三の悪徳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "マルキ・ド・サドの『ジュスティーヌあるいは美徳の不幸』をエロティックに映画化。17世紀、父の破産によって修道院を出ることになったジュスティーヌが、性の倒錯の渦へと巻き込まれていく姿を描いていく。主演は、「新・青い体験」にも出演し、英アンドリュー王子と浮名を流したクー・スターク。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE MACHT DER BILDER： LENI RIEFENSTAHL", "レニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 182, "ヒトラーの庇護のもと、世界映画史上に燦然と輝く1936年、ベルリン・オリンピック大会の記録映画「民族の祭典」「美の祭典」やナチ党大会の記録映画「意志の勝利」を監督した女性レニ・リーフェンシュタール。監督自らが行うレニへのインタビューを軸に、彼女の主演した映画や「民族の祭典」のメイキング・フィルムなど、未公開の映像をふんだんに挿入。才能に恵まれながらも“ナチの協力者”というレッテルとの戦いを強いられた彼女の人生の軌跡をたどっていく。", "1993-01-01", "7,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スーパーGUNレディ ワニ分署", "スーパーGUNレディ ワニ分署", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "警視庁の秘密捜査組織、通称ワニ分署の女刑事・火野三夏は、新任の相棒・加倉リンと組んで航空機汚職に絡んだ殺人事件に挑む。汚職問題は商社から国会にまで及び、もみ消し工作によって二人も命を狙われる……。人気劇画の映画化に、カバー・ガールの横山、キックボクサーのジャンボかおるが堂々と挑んだ。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("薄れゆく記憶の中で", "薄れゆく記憶の中で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "将来、何をするのかが分からない高３の和彦。ひょんなことから、ブ厚いメガネをかけている同じクラスの香織の素顔を見た彼は魅了され交際を申し込むが……。岐阜の持つ美しい自然を背景に高校生の初恋を描く。篠田和幸が５年間温めていた自らのシナリオをベースに撮り上げた第１回作品。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISS THEM FOR ME", "よろめき休暇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "第二次大戦下の真珠湾海軍航空基地の勇士３人が、母艦帰着までの６日間、サンフランシスコで羽根をのばそうとして、恋のドタバタ騒動を巻き起こしていく。C・グラント、R・ウォルストン、L・ブライデンの３人組がズッコケ・トリオと化す、S・ドーネン監督のドタバタ・コメディ。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Legend of 1900", "海の上のピアニスト", "", "", "http://image.eiga.k-img.com/images/movie/1793/315x210.jpg?3030", 125, "1900年。豪華客船ヴァージニアン号の機関士ダニーは、ダンスホールのピアノの上に置き去りにされた赤ん坊を見つけ、その子に“ナインティーン・ハンドレッド”と名付けて自分で育て始める。船という揺りかごですくすくと成長したナインティーン・ハンドレッド。ある晩、乗客たちは世にも美しいピアノの旋律を耳にする。ダンスホールのピアノに座って弾いていたのは、ナインティーン・ハンドレッドだった。", "1999-12-18", "4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSEGUEST", "ハウスゲスト　あんただ～れ？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ギャングから逃れるために、弁護士ゲーリーの車に乗り込んだケビン。身分を偽ったまま、なりゆきで高校での講演も何とかこなし、ゲーリーの家へ。裕福なゲーリー家では、パーティーまで開いてもらい、ちゃかり家族とも打ち解け、仲良くなってしまう。しかし、彼らに嘘をついていることが心苦しくなったケビンは、ゲーリー家を立ち去ろうと決心するが……。全米で人気のコメディアン、シンバッドのトボけた演技が楽しめるドタバタ・コメディ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("欲望", "欲望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "「はつ恋」の篠原哲雄監督が、小池真理子の同名小説を映画化。事故により若くして性的不能となった男と、彼を誰よりも理解する女性が、肉体を超えて結ばれる姿を繊細に描き出す。これまでもさまざまな役柄で独特の存在感を放ってきた村上淳が性的不能者という難役で、ひと味違う面を見せる。旧友の結婚披露パーティーで同級生の秋葉と再会した類子。秋葉が性的不能者と知る類子だが、次第に彼に強い結びつきを感じるようになる。", "2005-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続二等兵物語・南方孤島の巻", "続二等兵物語・南方孤島の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "昭和19年、南洋の孤島に立てこもって日本軍は必死の抗戦を続けていた。わが古山源吉と柳一太郎の両二等兵は、敵襲に加えて古参組の召使い同様の待遇に不満タラタラ。二人は傷ついたアメリカ軍人を助けたことから銃殺を宣言される……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KUNG−FU MASTER！", "カンフー・マスター！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "中年女性が、15歳年下の娘の同級生と恋に落ちるラブ・ストーリー。40歳のマリーは、娘ルシーの誕生パーティーで、ルシーの同級生ジュリアンに恋をする。TVゲーム“カンフー・マスター”に凝っているマリーとジュリアンは、それがきっかけで接近する。ところが、二人の関係がルシーにバレてしまい……。J・バーキンとC・ゲンズブールの母娘が共演。ヒロインと恋に落ちる少年はA・ヴァルダの息子M・ドゥミ。。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("竜火／DORAGON　HEAT", "ドラゴンヒート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "俳優や歌手として活躍する香港の異才E・コットが「初恋」に次いで手掛けたロード・ムービー。東京で出会った香港出身の男と札幌出身の少女。“天安門広場で写真が撮りたい”と思い立った二人は、北京へと旅立つ。だが、立ち寄った香港で二人は思わぬ事態に巻き込まれていく。主演は女性ミュージシャンのTHE ME。ほかに、A・ユンやK・モクら香港のスターが大挙出演。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の四季", "女の四季", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "丹羽文雄の風俗小説『かしまの情』の映画化。戦後の東京に上京してきた引揚者・境万亀を中心に、ある一軒家で共同生活する貧しくも強く生きる人々の姿をユーモアとペーソスたっぷりに描いている。戦中から戦後にかけて不振を続けていた豊田四郎が初めて脚本に八住利雄を迎え、復調のきっかけとなった作品。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PATERNITY", "結婚しない男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "結婚の高年齢化が指摘される昨今、“結婚しない男”も珍しくない。しかしこの映画の主人公バディは、40歳を過ぎた働き盛りに、なんと子供が欲しくてたまらなくなってしまう。結婚はさておき、自分の子を生んでくれる最良の女性を探そうと決意するが……。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Charlie's Angels", "チャーリーズ・エンジェル", "", "", "http://image.eiga.k-img.com/images/movie/1033/315x210.jpg?2057", 98, "チャーリー探偵事務所に在籍する美人探偵たちに、誘拐されたノックス・テクノロジー社の創立者、エリック・ノックスと、彼が開発した高性能の音声追跡ソフトを奪回する任務が下された。相棒のボスレーと共に、捜査に乗り出したエンジェルたちは、最も危険な潜入捜査を開始する。", "2000-11-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRINGO", "グリンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "N.Y.の一角に巣食う、ドラッグ中毒者の実態を克明に捉えたドキュメンタリー。J・スペイスリーをはじめ、本物のジャンキーたちが登場し、薬と暴力に明け暮れる自分たちの日常を告白する。バックに流れるヒップホップ・サウンドも印象的。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Home Alone", "ホーム・アローン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "クリスマス休暇をパリで過ごすことになった一家15人。出発の日、あわてて家を出たお母さんは飛行機の中で何かを忘れたことに気付く。８歳の息子ケビンだった。その夜、ケビンたった一人しかいなくなった家の中に泥棒が入ってきて……。身近な家庭用品を護身用の武器に、泥棒と悪戦苦闘する少年の姿がユーモラスなファミリー・アドベンチャー・コメディ。当時10歳のM・カルキン君を一躍スターダムにのし上げた。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NET", "ジェット機M7号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "英空軍の秘密兵器研究所を舞台に、緊迫のスパイ戦を描いた航空映画。研究所の科学者ヒースレイは、M７号という超高空用ジェット機を開発するが、スパイの暗躍によって試験飛行は失敗する。空中機内でのサスペンス・アクションが圧巻。", "1953-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画版　ふたりエッチ", "映画版　ふたりエッチ", "", "", "http://image.eiga.k-img.com/images/movie/56503/315x210.jpg?4618", 74, "1997年より連載の克・亜樹による長寿ラブコメ漫画を、人気グラビアアイドルの森下悠里主演で実写映画化。小野田優良と真は2人そろって性体験がなく、お互いにそのことを知らずにお見合いで結婚した。初夜を迎えたものの初心者同士で上手にできない2人は、「本当の夫婦」を目指してステップアップを決心するが……。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEAD", "ザ・デッド 「ダブリン市民」より", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「マルタの鷹」から「女と男の名誉」まで、数多くの名作を発表してきたJ・ヒューストンの遺作となった作品。“赤狩り”を逃れて長らくアイルランドに暮らし、“第二の故郷”と呼ぶまでに同地を愛した同監督が、そこに生きる人々を通して人生の悲哀を、格調高く、詩的に美しく描いている。1940年のダブリン、中年にさしかかったガブリエル夫妻は、年一回恒例のささやかな舞踏会へと出席する。ピアノ演奏や詩の朗読、ダンス、そして和やかな会食。平和なひとときが過ぎ、やがて帰路についたガブリエルは、思いもかけず妻グレタから“ある告白”を受けることになる……。原作はJ・ジョイスの同名短編。監督の娘であるA・ヒューストンが出演し、息子のT・ヒューストンが脚本を担当している。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRASH AND BURN", "ジャンクウォーズ 2035", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "2035年、荒廃した地球は多国籍企業ユニコムによって支配されていた。そんな企業の独裁ぶりを、電波を使って糾弾するレイサン一派。だがある日、彼らの放送局に人間そっくりの殺し屋アンドロイドが送り込まれて……。近未来の地球を舞台にしたSFアクション。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Sound of Thunder", "サウンド・オブ・サンダー", "", "", "http://image.eiga.k-img.com/images/movie/1663/315x210.jpg?3628", 102, "「タイム・コップ」「カプリコン1」のピーター・ハイアムズ監督が、人気SF作家レイ・ブラッドベリの短編を映画化。時間旅行ツアー隊を率いるライヤー博士役に「マクマレン兄弟」「彼女は最高」などの小品を監督するほか「プライベート・ライアン」などに出演し、俳優兼監督として活躍するエドワード・バーンズ。その雇い主役に「ガンジー」「砂と霧の家」のベン・キングズレー、ライヤー博士の協力者となる物理学者役として「ブレイブハート」「スパイ・ゲーム」のキャサリン・マコーマックが共演。", "2006-03-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("テケテケ2", "テケテケ2", "", "", "http://image.eiga.k-img.com/images/movie/54413/315x210.jpg?4193", 72, "「テケテケ」の続編。テケテケから奇跡的に逃れることが出来た加奈は、あの事件から1年後、再びテケテケの標的にされ、その餌食となってしまう。通学途中に可奈の事件現場を通り過ぎた親友同士の菜月と玲子は、学校で友人たちとテケテケの噂ばなしで盛り上がるが……。監督は前作に引き続き白石晃士、主演は「うた魂♪」「赤い糸」の岩田さゆり。", "2009-03-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTERNAL AFFAIRS", "背徳の囁き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "R・ギアとA・ガルシアの二人が、悪と善に分かれて熱い闘いを繰り広げる刑事ドラマ。ロス市警の刑事デニスは、職権を乱用し、若い警官を操って、悪の世界に手を染めていた。そのデニスを探るため内務調査班の警官レイモンドがやって来て……。ビデオ発売時タイトルは「インターナル・アフェア　背徳の囁き」。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NECK ネック", "NECK ネック", "", "", "http://image.eiga.k-img.com/images/movie/55093/315x210.jpg?3358", 96, "人気覆面作家の舞城王太郎が、映像化を視野に入れて書き下ろしたストーリーを映画化。相武紗季が映画初主演。共演に溝端淳平、栗山千明、平岡祐太ら。大学のマドンナ・真山杉奈に恋する首藤友和は、玉砕を覚悟で杉菜に告白をする。すると友和は彼女の研究室に呼び出され、そこに置かれた大きな木箱に入れられてしまう。その、首だけ出る木箱「ネックマシーン」はお化けを作り出そうとしている杉奈の研究道具で、友和はその実験台として選ばれたのだった。", "2010-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花と怒涛", "花と怒涛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「関東無宿」に続く小林旭と鈴木清順のコンビによる任侠アクション。ストーリー以上に、大正中期の浅草のセットが印象的である。幅広のオペラ・ハットに黒マントという、“ペラゴロ・スタイル”の殺し屋に扮する川地民夫が強烈なインパクトを残し、主役の旭をすっかり食ってしまった。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL OUT", "愛と欲望の銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "若い女を人質にとって逃走した銀行強盗。女は襲った銀行のオーナーの娘だった。逃避行の果て、二人はやがて恋に落ちていく。だが、男は次第に生きることに疲れていき、女は頼りにならなくなった男を虐待し、征服していくのだった……。", "1990-01-01", "2,7,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天下御免", "天下御免", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "渡辺邦男監督、松本幸四郎主演による娯楽時代劇。８代将軍・吉宗の頃、山内伊賀亮の天下取りの野望から御落胤に仕立て上げられた天一坊が、大岡越前守の執拗な追求に化けの皮をはがされる。伊賀亮を演じた森美樹は、長身とエキゾチックな顔立ちで人気を博したが、1960年末にガス中毒で死去した。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Another アナザー", "Another アナザー", "", "", "http://image.eiga.k-img.com/images/movie/56033/315x210.jpg?2314", 109, "1998年春、15歳の榊原恒一は、ある地方都市の学校へ転校するが、早々に持病の発作が起こり病院へ担ぎこまれる。症状も無事に回復したある日、病院のエレベーターで眼帯をした美少女と出会った恒一は、思わず見入ってしまうが、少女は地下の霊安室で不可解な言葉を残して消えてしまった。ベストセラー作家・綾辻行人の原作小説を映画化したホラーミステリー。主演はドラマ「クローンベイビー」の山崎賢人と「告白」の橋本愛。「オトシモノ」の古澤健監督がメガホンをとる。", "2012-08-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SATURDAY NIGHT AND SUNDAY MORNING", "土曜の夜と日曜の朝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "『怒れる若者たち』を代表するA・シリトーの同名小説をもとに描かれた社会派ドラマ。イギリスの工業都市ノッティンガムを舞台に、工場で働く青年のやり場のない怒りと、友人の妻とのただれた情事を描く。T・リチャードソン監督が製作を担当。そのリチャードソンに見い出された、K・ライス監督のデビュー作でもある。", "1960-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朋の時間・母たちの季節", "朋の時間・母たちの季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "重症心身障害者の人々のために作られた日本で初めてのデイアクティビティー（日中活動）施設、訪問の家“朋”の歩みを３年半にわたって記録。「梅香里〈メヒャンニ〉」の西山正啓監督がソーシャルワーカーの取り組みと、施設に通う母と子の強い絆をつぶさに見つめる。地域社会と積極的にかかわりを持つことで笑顔を取り戻していく障害者たちの姿が感動的。これからの福祉ついても考えさせられる１本だ。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・三等重役／亭主教育の巻", "新・三等重役／亭主教育の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "世界電機の沢村専務は、秘書・章子と結婚する。一方外遊中の坂口社長が、会社一の株主・鶴子未亡人の長女・理美子と一緒に帰国。鶴子は大阪実業家の息子を婿の候補者にと言い出すが、理美子にはほかに恋人がいた。沢村夫妻の奮戦でことはまるく収まり、沢村も社長に就任することに。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("連理枝", "連理の枝", "", "", "http://image.eiga.k-img.com/images/movie/1613/315x210.jpg?1737", 107, "「冬のソナタ」「天国の階段」のチェ・ジウと「オオカミの誘惑」のチョ・ハンソンが共演する純愛ストーリー。プレイボーイの青年実業家ミンスは、雨の中で出会って病院まで送り届けた若い女性へウォンに心惹かれ、彼女が置き忘れたケータイを手掛かりに彼女を捜す。やがてミンスはへウォンと再会するが、彼女は不治の病で残された時間はわずかだった。タイトルの「連理の枝」とは、2本の樹の枝が絡み合って1本の樹のようになったもののこと。", "2006-04-15", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画ドラえもん　のび太と奇跡の島　アニマルアドベンチャー", "映画ドラえもん　のび太と奇跡の島　アニマルアドベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/56933/315x210.jpg?3435", 100, "絶滅したはずの動物たちが暮らす不思議な島を舞台に、ドラえもんと仲間たちが冒険を繰り広げる姿を描く劇場用オリジナル長編作。「タイムトリモチ」で絶滅した鳥モアを現代につれてきてしまったのび太とドラえもんは、モアを連れて絶滅動物を保護しているベレーガモンド島へ赴く。島のジャングルに暮らすロッコロ族の少年で、のび太とそっくりなダッケに出会った2人だったが、そこへ島を外界から守る黄金のカブトムシ「ゴールデンヘラクレス」を狙う悪徳商人シャーマンが襲ってきて……。前年公開の「新・のび太と鉄人兵団　はばたけ天使たち」にゲスト出演もした福山雅治が主題歌を担当。", "2012-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASK OF DEATH", "マスク・オブ・デス　潜入捜査", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "顔を変え、危険な潜入操査に挑む刑事の活躍を描いたアクション。偶然、マフィアとFBIの銃撃戦に巻き込まれてしまった休暇中の刑事マッケンナ。彼はその銃撃戦で妻を失い、自らも瀕死の重傷を負う。顔面に深い傷を受け、整形手術が必要なマッケンナにFBIは、ある特別任務を要請する。それは、彼と背格好がそっくりで、銃撃戦で死亡したマフィアの構成員メイソンになりすまし、強奪された核ミサイルの軌道データが入ったマイクロチップを取り戻すというものだった。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHEAP DETECTIVE", "名探偵再登場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「名探偵登場」のスタッフで、前作に引き続き、名優ピーター・フォークを主人公にした探偵コメディ。1940年代のサンフランシスコ。そこにちっぽけな事務所を構えるル・ペキンポー探偵。珍事件や難事件を抱えているところにまたもや大事件が舞い込んだ……。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE QUIET AMERICAN", "愛の落日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "文豪G・グリーンの長編小説『おとなしいアメリカ人』を政情不安定な1950年代のベトナム・サイゴンを舞台に映画化。初老のイギリス人特派員を主人公に、愛人である若いベトナム人女性、物静かなアメリカ人青年との微妙な三角関係を描出したラブ・サスペンスだ。さらにベトナムのエキゾチックな匂いや、政治的謀略も絡むミステリー映画としての緊迫感を湛えた映像世界は、まさしく大人の味わい。本作でアカデミー主演男優賞にノミネートされた名優、M・ケインの懐の深い演技も見逃せない。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("手討", "手討", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "旗本の源次郎はアクビが原因で切腹させられ、その事件を契機に旗本と大名は対立する。旗本たちは“白柄組”を結成して、いたるところで大名と衝突。播磨はこれを憂い、自らこれを抑えようと白柄組の頭領となるが、白柄組と加賀守の大名行列との間で大乱闘が起こり、彼は責任をとることに……。二派の対立によって展開する娯楽時代活劇。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒侠Ⅱ", "ブラックマスク2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "人体改造手術と洗脳によって無敵の戦士となった後、正義のヒーローとなった“ブラックマスク”の活躍を描いた近未来アクション第2弾。前作ではジェット・リーが主演したが本作はメンバーを一新し、ツイ・ハーク監督の描く新しいブラックマスクが、遺伝子操作をめぐる闇の組織の陰謀に立ち向かう。「マトリックス」のユエン・ウーピンがアクション監督を務めた話題の近未来VFXアクション作品。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クイール", "クイール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ドラマ化もされたノンフィクション『盲導犬クイールの一生』を、犬好きだという崔洋一監督が映画化。生い立ちから他界までを丹念に描く正攻法の演出で、愛くるしい子犬が主人を落ち着いてリードする名盲導犬にまで成長する姿を再現している。頑なな視覚障害者を小林薫が妙演し、クイールを見守る訓練士を椎名桔平が好演。犬と人間のツーショットにも名場面が多い。盲導犬になる素質を見込まれた一匹のラブラドール・レトリバー。おっとりした性格の彼はクイールと名付けられ、訓練士によって立派な盲導犬に育てられる。やがてクイールは、頑固だが心優しいパートナーと信頼関係を育んでいく。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA RESA DEI CONTI （THE BIG GUNDOWN）", "復讐のガンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "テキサスでは知らぬ者はいないというほどその名を鳴り響かせていた“正義のガンマン”ジョナサン。野心家のブロックストンは彼の名を使って鉄道建設を有利に進めようとするが、そんな折に殺人事件が発生する。犯人追跡に向かうジョナサンだが……。L・V・クリーフ主演のマカロニ・ウエスタン。DVDは「マカロニウエスタン コレクション リー・ヴァン・クリーフ ボックス」に収録。", "1968-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超電子バイオマン", "超電子バイオマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 44, "狂気の天才科学者ドクターマン率いる新帝国ギアから地球を守るべく立ちあがった5人の若者の活躍を描く特撮テレビシリーズの劇場版。バイオベースの秘密の場所が記された地図をドクターマンが狙っていると知ったバイオマンたちは、バイオターボに乗りこんで出動する。ニセの地図でおびき寄せたビッグスリーやジューノイド五獣士を相手に快進撃を続けるバイオマンたちだったが、ドクターマンが差し向けた最強メカ・カニカンスに苦戦を強いられる。", "1984-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nanga Parbat", "ヒマラヤ　運命の山", "", "", "http://image.eiga.k-img.com/images/movie/56603/315x210.jpg?2866", 104, "1970年6月、若き登山家メスナー兄弟はヒマラヤ山脈のナンガ・パルバット（標高8125m）への初登頂を果たす。しかし登頂成功から1週間後、キャンプ地に戻ってきたのは兄ラインホルトだけで、弟ギュンターの姿はなかった。下山途中の兄弟に起こったある悲劇が、ドイツの国中を騒がすスキャンダルへと発展していく。ナンガ・パルバット初登頂者をめぐる裁判のためこれまで語られていなかった事実を、メスナー本人の協力により映画化。", "2011-08-06", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若い東京の屋根の下", "若い東京の屋根の下", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "吉永と浜田の“日活グリーン・ライン”コンビによる青春映画。両親と弟の４人暮らしの蕗子の家に、大学生の良平が下宿する。蕗子には、幸吉という男友だちがいたが、良平との間にも、ほのかな恋が芽生え始める。そんな時、良平の友人である女性の口利きで、蕗子の父の再就職先が決まった。蕗子は、良平の女友だちが気になって……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花影", "花影", "", "", "http://image.eiga.k-img.com/images/movie/53413/315x210.jpg?2362", 88, "韓国の人気俳優キム・レウォンと「不夜城」の山本未來共演によるラブストーリー。「傷だらけの天使」など昭和のテレビ界を担った脚本家・市川森一と新人監督の河合勇人が手を組み、在日韓国人3世のヒロインが恋や仕事を通して成長する姿をファンタジックに綴る。ジュエリーデザイナーの尚美は仕事で釜山を訪れるが、大失態を演じ商談が白紙になってしまう。予定を変更して祖先の墓参りに行くことにした彼女は、そこで小学校教師スンウと出会う。", "2008-03-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD COMPANY", "9デイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "CIA諜報員オークスとケヴィンは、プラハでニセの取引をでっち上げてロシア人マフィアからポータブル核爆弾を入手しようとしていた。しかし、取引10日前、その核爆弾を狙うテロリストにケヴィンが殺されてしまう。オークスは作戦を続行するため、ケヴィンの双子の弟であるしがない便利屋ジェイクに彼を演じさせることにする。ジェイクはCIAのノウハウや洗練された動きを身につけて取引の日を迎えるが、例のテロリスト一味が核爆弾を奪い去ってしまう。彼らがニューヨークを標的にしていることを知ったジェイクとオークスはアメリカに舞い戻り……。アタッシュケース・サイズの小型核爆弾をめぐる攻防を描いたサスペンス・アクション。A・ホプキンスがアクション・シーンに挑んでいるのも見もの。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アルプスの若大将", "アルプスの若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "京南大学スキー部主将・田沼雄一は、建築学の論文が認められヨーロッパ旅行に招待される。夢のアルプス。彼はそこで航空会社のローマ事務所員・岸澄子と知り合い、楽しい時を過ごす。帰国後、猛練習でスキー選手権に優勝。雄一は再びアルプスへ……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("相馬看花　第一部　奪われた土地の記憶", "相馬看花　第一部　奪われた土地の記憶", "", "", "http://image.eiga.k-img.com/images/movie/58023/315x210.jpg?2134", 109, "「花と兵隊」の松林要樹監督が、2011年3月11日の東日本大震災で事故を起こした福島第一原子力発電所から20キロ圏に位置する福島県南相馬市原町区江井（えねい）地区でカメラをまわしたドキュメンタリー。11年4月3日、支援物資を運ぶ友人と南相馬市へ向かった松林監督は、そこで市議会議員の田中京子さんと出会う。松林監督は田中さんと行動をともにしながら避難所や被災地をまわり、逆境のなかでも生まれるユーモアや男女の機微など、人々の様子を映し出していく。", "2012-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版魔法先生ネギま！ ANIME FINAL", "劇場版魔法先生ネギま！ ANIME FINAL", "", "", "http://image.eiga.k-img.com/images/movie/56493/315x210.jpg?3883", 0, "女子中学校の担任になった魔法使いの少年ネギが、個性的な女子生徒に囲まれながら成長していく姿を描き、TVアニメや実写ドラマ化もされた赤松健の人気コミック「魔法少女ネギま！」の初の劇場用アニメーション。父に会いたいという思いを胸に魔法世界へと向かったネギの前に、宿敵フェイトが立ちはだかる。敗北したネギはフェイトに対抗するため「闇の魔法（マギア・エレベア）」を会得。魔法世界の秘密を知ったネギらは、世界の存亡をかけた戦いに挑む。「劇場版ハヤテのごとく！」と同時上映。", "2011-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEVERED TIES", "ミュータント・ハンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "科学者ハリソンはトカゲと人間の遺伝子から新薬“プラスミド”を作り出す。ある日、彼を溺愛する母とその恋人ボーン博士との争いで、ハリソンは右腕を切断。さっそく新薬を試したところ、腕はすぐに生えてきた。ところが蘇生したその右腕は、体を離れ暴れ出し、次々と惨劇が起こす。主人公を利用し、ひともうけをたくらむボーン博士を、ベテラン俳優 O・リードが演じている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Only Angels Have Wings", "コンドル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "南米バランカに到着した踊り子のボニーは郵便物の空輸会社のボス、ジェフと知り合う。ジェフは結婚に失敗して以来、女性不信に陥っていた。最初は冷ややかなジェフに反感を抱くボニーだが、危険と隣合せの職業に命を賭ける姿を見て、心惹かれていく……。作品は大成功を収め、当時ほぼ無名であったリタ・ヘイワースにとってはスターダムへの足がかりとなった。", "1940-02-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("To Have and Have Not", "脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1940年、フランス降伏直後、ビシー政権下のマルチニック島フォールド・フランス。釣り船の船長をしているアメリカ人ハリーは、ホテルの支配人ジェラールからフランス奪還を狙うドゴール派への協力を持ちかけられるが、ハリーは政治闘争への関与を拒み……。ホークスが長らく望んでいたヘミングウェイ原作の映画化。ホークスの妻が雑誌のモデルをしていたローレン・バコールを見つけて夫に推薦、主演に大抜擢したという。", "1947-11-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Take Shelter", "テイク・シェルター", "", "", "http://image.eiga.k-img.com/images/movie/57103/315x210.jpg?2126", 120, "異常気象襲来に対する恐怖にかられ錯乱する男の狂気を描いたサイコスリラー。田舎町で妻と娘と幸せに暮らしていたカーティスは、異常気象に襲われる悪夢を見て以来、その恐怖にとりつかれてしまう。避難用のシェルター作りに没頭するカーティスに対し、家族や周囲の人々は次第に不信感を募らせていくが……。「レボリューショナリー・ロード　燃え尽きるまで」でオスカー候補になったマイケル・シャノンがカーティス役を怪演。妻のサマンサ役に「ツリー・オブ・ライフ」のジェシカ・チャステイン。", "2012-03-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Reindeer Games", "レインディア・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "　刑期を終えて出所したルーディは同房だった男ニックの文通相手アシュリーと会うことに。彼女の魅力に心を奪われてしまったルーディは自分がニックであると偽り、ふたりでひと時を過ごす。しかし彼らの前にアシュリーの兄ガブリエルが出現。凶暴なこの男はニックを巻き込んだ強盗を計画していた。正体を偽っていたルーディはガブリエルの一味に引きずり込まれてしまうが……。罠にはまった主人公の危機を緊張感満点に描くサスペンス。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE SCANDALE", "殺意", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "フランスの大富豪クリスチーヌは、世界的に有名なシャンペン会社を経営しているが、その商標権は、夫の親友ポールの名義になっていた。彼女は会社をアメリカの事業団に売脚しようとしていたが、そのためには商標権も一緒でなければならない。彼女はポールを、夫とともにハンブルク旅行に出発させるが、そこで奇妙な殺人事件が連続して……。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("広域暴力 流血の縄張", "広域暴力 流血の縄張", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ヤクザ連合組織、関東桜田会が警察の圧力を逃れるため偽装解散。しかし、傘下の一家がこれを承知せず、桜田会と関西ヤクザとの標的になる。一家の幹部になるのが小林旭で、仲間を殺され、ラストに雨と泥と血まみれになりながら殴り込みをかける場面が印象的。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANPO", "ANPO", "", "", "http://image.eiga.k-img.com/images/movie/55623/315x210.jpg?2761", 89, "日本の現代アートを通して、日米安全保障条約以降の日本とアメリカの関係を見つめ直したドキュメンタリー。絵画、写真、アニメーションなど、安保問題に真正面から向き合ったさまざまなジャンルの作品を、アーティストたちへのインタビューを交えながら紹介していく。監督・プロデュースを務めるのは、日本で生まれ育ったアメリカ人女性で、07年公開の映画「TOKKO／特攻」のプロデュースを手掛けたリンダ・ホーグランド。", "2010-09-18", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドキュメンタリー　頭脳警察", "ドキュメンタリー　頭脳警察", "", "", "http://image.eiga.k-img.com/images/movie/54743/315x210.jpg?4265", 315, "1969年、当時19歳のPANTAとTOSHIを中心に結成されたロックバンド「頭脳警察」は、歌詞の過激さから反体制バンドとして問題視されていたが、1975年の学生運動の終焉とともに突然バンドは解散した。ライブ、レコーディング、プライベートを記録した映像で構成される、総尺5時間を越える音楽ドキュメンタリー。監督は「感染列島」の瀬々敬久。", "2009-11-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カクトウ便 Vol.3 そして、世界の終わり", "カクトウ便 Vol.3 そして、世界の終わり", "", "", "http://image.eiga.k-img.com/images/movie/53513/315x210.jpg?2009", 90, "裏の運び屋“カクトウ便”リーダーの女子高生ゆきとメンバーで元傭兵の藤岡の前に、カクトウ便メンバーに加えて欲しいという少女・玲が現われる。厳しい訓練を重ねる中で少しずつ惹かれあっていく玲と藤岡。遂に初仕事を任された玲だったが、配達物のフロッピーディスクには、人類の命運を左右するほどの恐るべき陰謀が隠されていた。人気アイドル共演で描く本格アクションのシリーズ第3弾。出演は「すんドめ」の次原かなほか。", "2008-03-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YESTERDAY", "イエスタデイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1967年、カナダのモントリオール。美術専攻の女子大生と、留学中のアメリカ人医学生がホッケーの試合で知り合い、恋に落ちる。しかし思わぬ事態で退校した医学生にベトナム出兵の令状が……。ビートルズの名曲が流れる感動のラストで大いに泣かせる、カナダ製の青春ラブ・ストーリー。", "1979-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIDE OF CHUCKY", "チャイルド・プレイ／チャッキーの花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "殺人鬼の魂が乗り移った人形・チャッキーの凶行を描く、人気ホラー・シリーズの第4作。前作でバラバラになった悪魔の人形・チャッキーは、人間時代の恋人・ティファニーの手により復元される。蘇ったチャッキーは彼女を殺し、その魂を花嫁人形に封じ込める。2人は人間として再生するため、あるカップルを新しい肉体に定めて行動を開始する……。残虐シーンに加えて人形同士のセックス・シーンなど、ブラック・ユーモアの濃厚さはシリーズ随一。", "1999-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WALKABOUT", "美しき冒険旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1968年に「パフォーマンス」で監督デビューしたN・ローグが、オーストラリアの原野を舞台に撮影兼任で撮り上げた第２作。都会育ちの少女とその弟は父の自殺の巻きぞえになって砂漠にとり残されてしまう。そこで出会ったのが原住民の少年。彼は風習に従って一年間一人で砂漠を放浪していたのだった。３人は次第に仲良くなっていくが……。ビデオ、DVDタイトルは「WALKABOUT 美しき冒険旅行」。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("このすばらしきせかい", "このすばらしきせかい", "", "", "http://image.eiga.k-img.com/images/movie/33693/315x210.jpg?2534", 72, "引きこもりの少年と、彼の家に居候することになった叔父の交流を描いたハートウォーミング・コメディ。東京郊外の一軒家で家族と暮らす高校生の涼一は、学校で同級生に怪我をさせて以来、家に引きこもりテレビゲームばかりやっている。そんなある日、家も金もなくした変わり者の叔父・清が彼の家に転がり込んで来た。はじめのうちはマイペースな叔父に振り回されてうんざりする涼一だったが……。監督は本作が長編第1作目となる沖田修一。", "2006-10-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FRENCH LIEUTENANT’S WOMAN", "フランス軍中尉の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "イギリスの現代作家ファウルズの同名ベストセラーの映画化。ある港町で、アナとマイク共演の“フランス軍中尉の女”と題する映画の撮影が行われている。19世紀の中ごろ、フランス人の中尉と愛し合い捨てられたがゆえに、人々から娼婦呼ばわりされる女と、婚約者がありながら彼女に恋をする若い考古学者の物語である。一方、実生活でも、アナとマイクは不倫の仲。撮影が進むうちに、映画中の男女の関係が次第に現実の二人にオーバー・ラップして……。ストリープとアイアンズがともに一人二役を力演。時間と空間を巧みに切り替え、ありきたりの愛欲のドラマを見事に芸術作品として映像化した、“映画中映画”の佳作。", "1981-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lodger", "下宿人（2008）", "", "", "http://image.eiga.k-img.com/images/movie/54763/315x210.jpg?3115", 95, "巨匠アルフレッド・ヒッチコックの監督デビュー作となった同名傑作スリラーを、「ダ・ヴィンチ・コード」のアルフレッド・モリナ、「L.A.コンフィデンシャル」のサイモン・ベイカーを起用してリメイク。西ハリウッドで連続する売春婦殺人事件を追っていた刑事のマニングは、犯人の手口は19世紀ロンドンの猟奇殺人犯「切り裂きジャック」に模倣していることに気付く。一方、ハンサムで物静かな作家マルコムは、その挙動不審な言動が周囲を心配させていた。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Doomsday", "ドゥームズデイ", "", "", "http://image.eiga.k-img.com/images/movie/53593/315x210.jpg?2617", 110, "2035年、かつて人類を滅亡の危機に追い詰めた感染ウイルスが再び猛威を振るい始める。緊急対策を練る政府は、ウイルスに侵された街で奇跡的に生き延びている生存者の存在を知り、そこに抗ウイルス剤があると考え、そこに1人の女戦士を送り込む。監督は「ディセント」のニール・マーシャル、主演は「アンダーワールド／ビギンズ」でバンパイアの女戦士を演じたローナ・ミトラ。", "2009-09-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝひめゆりの塔", "あゝひめゆりの塔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "今井正の「ひめゆりの塔」と同じ太平洋戦争沖縄戦に臨時看護婦部隊として従軍し、全員が戦死した沖縄師範女子生徒と県立第一高等女学校で結成された、ひめゆり学徒隊を描いた作品。今井版はリアリズム描写で貫かれているが、この舛田利雄版では吉永小百合と浜田光夫という青春映画のコンビを配し叙情的に処理している。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOPPELGANGER", "ドッペルゲンガー —憎悪の化身—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "D・バリモアが成熟した女を演じるエロティックなSFXホラー。若手映画作家ピーターは、ルームメイトになった美しいホーリーに一目惚れ。だが恥ずかしがり屋で純粋な彼女には、時として豹変するもう一つの顔があった。虐殺と破壊に狂い、情欲をむさぼる女。彼女自身はそれを否定し、自分の中には容姿も声も自分と同じ“ドッペルゲンガー”が存在すると告白する。果たしてホーリーは二重人格者なのか？　ピーターは彼女のために、危険な捜査に乗り出す。主演のG・ニューバーンがアヴォリアッツ国際ファンタスティック映画祭で主演男優賞を受賞した。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・女売り出します", "喜劇・女売り出します", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ふとしたことから“新宿芸能社”に出入りするようになるスリの少女・浮子が、スリの指技を生かした“手品ストリッパー”として売れっ子になるが、スリ仲間の武が浮子をとり返しにきた……。体を張って生きるストリッパーの泣き笑いがコミカルに描かれていて、相変わらず好調。特に米倉斉加年が演じるスリの武が素晴らしく、ヤクザに指をつめられながらも、いつもの口ぐせの“笑わせるぜ、まったく”と呟き、相手からすった財布を差し出す場面など浮子に対する想いの情も絡んで、森崎喜劇ならではの感動を与えてくれる。かあさん役も、中村メイコ、左幸子から市原悦子へと変わった。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MODULATIONS", "モジュレーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "エレクトロ・ミュージックの歴史をたどった音楽ドキュメンタリー。G・P・オリッジをはじめとする革新的なアーティストやカリスマDJが大挙出演し、影響を受けたミュージシャンや自身の音楽観について証言。彼らの発言を通して、マイクロフォン、アンプ、エレキギターの登場からハウス、ヒップホップ、テクノへといたる音楽シーンの流れを検証していく。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dogma", "ドグマ", "", "", "http://image.eiga.k-img.com/images/movie/1823/315x210.jpg?3740", 129, "その昔、神に背いて天界を追放された天使ロキとバートルビー。1000年もの間悶々と人間界で暮らしていた彼らは、ニュージャージーの神父が提案した“すべての罪が清められる日”を利用し、自分達も天界へ帰ろうと計画する。それが世界の消滅を招くことを知った神の秘書天使メタトロンはキリストの末裔ベサニーを引っ張り出し、ロキたちを阻止するため預言者ジェイ＆サイレント・ボブらと共にニュージャージーへ向かわせる。", "2000-07-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("溺れる魚", "溺れる魚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "斬新な映像で観る者を圧倒する堤幸彦が、戸梶圭太の同名小説を映画化したハイテンション・サスペンス。警察の内部犯罪を捜査する特別監察官室は、大手フィルムメーカー、ダイトーとの癒着の疑惑がある石巻警部を以前からマーク。その内偵捜査を懲戒免職寸前の刑事、白州勝彦と秋吉宗貴に命じる。そのころ、ダイトーは謎の人間“溺れる魚”から脅迫を受けていたが、彼は金品を要求せず、幹部クラスの人間に屈辱的行為を強制するだけだった。一方、石巻警部を追う白州と秋吉は気鋭のグラフィック・アーティスト、岡部哲晃の存在を突き止める……。切れ味鋭いカメラワークとユニークなキャラクターを配した、堤幸彦の世界が満載。落ちこぼれ刑事たちの無軌道な捜査ぶりが時にシリアス、時にコミカルに展開していく。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vera Cruz", "ヴェラクルス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "　南北戦争直後のメキシコ。そこに流れ着いた南軍少佐のトレーンは無法者のエリンと知り合う。やがてふたりはメキシコ皇帝の命を受け、伯爵夫人の乗る馬車を港町ヴェラクルスに送り届けることに。ところがその馬車には300万ドルの金貨が隠されていた……。金貨をめぐって繰り広げられる男たちの闘いを描いた痛快な西部劇。クーパーとふてぶてしい悪党を演じたランカスターの対決シーンも映画史に残る名場面として知られている。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄の夜は真紅だぜ", "地獄の夜は真紅だぜ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "アサヒサーカスの目玉、オートバイの曲芸“地獄の夜”のさなかに三田光五郎が何者かに射殺された。サーカスは警察の目の光る中、興行を続け、流れ者の伸二を一員に加え、オートバイ曲芸で人気を盛り返した。ちょうどその頃、アサヒサーカスの乗っ取りをたくらむタカラサーカスの片岡が沖合で麻薬取引を始めていた……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("壇の浦夜枕合戦記", "壇の浦夜枕合戦記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "もっぱら恋の道一筋に励む義経が、捕えた天皇の生母、建礼門院にぞっこん惚れ込み、何とかこれをものにしようとする姿をコミカルに描く異色時代劇ポルノ。最高裁でわいせつとされた原本『壇の浦夜合戦』のタイトルに“枕”の一字をつけ加えている。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("High School Musical 2", "ハイスクール・ミュージカル2", "", "", "http://ecx.images-amazon.com/images/I/61wGiYluUdL._SL210_.jpg", 111, "世界中で社会現象を巻き起こしたディズニー・チャンネルのオリジナル・テレビ映画「ハイスクール・ミュージカル」の続編。待ちに待った夏休み。トロイとガブリエラ、そしてワイルド・キャッツのメンバーたちは、シャーペイの父親が経営するカントリー・クラブでアルバイトをすることに。クラブで毎年開催されているタレントショーの優勝をトロイとガブリエラに奪われてしまうと考えたシャーペイは、2人を引き離そうと手をつくすが……。", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("７月７日、晴れ", "７月７日、晴れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "超人気アーティストと平凡な会社員の恋を描くラブ・ストーリー。仲間とキャンプに来ていた健太は、突然、現れた美少女に“今度デートしよう”と電話番号を手のひらにメモされる。町に戻った健太はその少女が国際的な超メジャーアーティスト・望月ひなたであることに気がつく。半信半疑で電話をしてみると、ひなたは約束の場所にリムジンに乗ってやって来た。ひなたは誕生日の７月７日に一度も天の川を見たことがないと言い、健太はキャンプにひなたを誘うのだが……。全編に、DREAMS COME TRUEの音楽が前編を彩っている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REMEMBRANCE OF THINGS FAST", "リメンバランス～記憶の高速スキャン～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "世界衛星ステーションとモニター上にスキャンされるバーチャルな“嘘の映像”の世界で、３人の女装の男たちがそれぞれのごく私的体験である“本当の話”をし始める。果たして、何が本当で、何が嘘なのか。デレク・ジャーマン作品のスタッフ（編集やデザインを担当）であるJ・メイブリー監督作品。", "1993-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すっ飛び五十三次", "すっ飛び五十三次", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "伴淳、アチャコ、ダイマルラケット、トニー谷と往年の喜劇・演芸人が出演した娯楽時代劇。富くじに当たったことから東海道の旅に出た弥次さん、喜多さん。二人はその道中、鼠小僧次郎吉そっくりの殿様に出会ったり、女中に夜ばいをかけたりの大騒ぎ。しかも殿様が次郎吉になり済まして、騒ぎはさらにふくらんで……。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Diamonds Are Forever", "007／ダイヤモンドは永遠に", "", "", "http://ecx.images-amazon.com/images/I/510D-aamvuL._SL210_.jpg", 120, "レーゼンビーの不評で再びコネリーの登場となったシリーズ第7作。ダイヤの密輸組織を捜査するため潜入したボンドは、宿敵スペクターの首領ブロフェルドが地球征服を狙ってワシントンDCの爆破を計画していることを知る……。", "1971-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新しい神様", "新しい神様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "反天皇制の立場をとる土屋豊監督が、民族派右翼バンド“維新赤誠塾”のメンバーである伊藤秀人と雨宮処凛（あまみやかりん）を取材したドキュメンタリー。政治思想の点ではまったく相入れない両者が、社会と個人のかかわりを率直に語り合ううちに共鳴していく過程が映し出される。また本作で出色なのは、幼い頃からイジメを受けて家出や自殺未遂を繰り返してきたという雨宮処凛の人間性。監督からビデオカメラを手渡された彼女が、北朝鮮訪問などの衝撃的な体験を経て、自らの揺れる心境を赤裸々にさらけ出す姿は深い感動を呼び起こす。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAUNTED SPOOKS", "ロイドの化物退治", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "失恋のあげく、何度も自殺を試るが必ず失敗してしまうロイド。一方、ミルドレットは、既婚でなければもらえない祖父の遺産を抱えて大弱り。弁護士の計らいで結婚した二人は、財産を渡したくない叔父が待つ、インチキ幽霊屋敷へ……。漫画チックな髪の毛総立ちの場面も登場する、ロイドの無声短編。", "1920-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続　愛と誠", "続　愛と誠", "", "", "http://ecx.images-amazon.com/images/I/61aHaLPxqzL._SL210_.jpg", 89, "不良高校生・太賀誠と純真なお嬢様・早乙女愛が繰りひろげる純愛を描いた、梶原一騎・原作、ながやす巧・画による名作コミック「愛と誠」を実写映画化したシリーズ3部作の第2作。不良高校として知られる花園実業に転校した誠は、学園を牛耳るスケバングループに宣戦布告。一方、愛も誠の後を追って花園学園に転入してくるが、誠は影の大番長を探し出すことに夢中で……。前作に続き、「同棲時代」シリーズの山根成之がメガホンを取る。", "1975-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUNSET", "キャデラック・カウボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "草創期ハリウッド。西部劇の人気スターが保安官ワイアット・アープに扮することになり、アープ本人がハリウッドに招かれることになった。が、謎の連続殺人事件が起こり彼らも巻き込まれることに……。第１回のアカデミー賞授賞式など黄金期ハリウッドの一幕を見事に再現したコメディ調のサスペンス。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUCKING　ÅMÅL", "ショー・ミー・ラヴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "スウェーデン映画祭など、各国の映画祭で多くの賞を受賞。孤独や不安を抱える少女たちの心情をリアルに捉えた青春ストーリーだ。スウェーデンの小さな町。14歳の少女エリンは退屈な学校にも友達にもウンザリ気味。一方、16歳のアグネスは周囲になじめず、自分自身にも嫌気が差している。そんな二人が出会い意気投合、思いつきで都会を目指してヒッチハイクの旅に出るが……。互いに影響を受け、他人の目を気にせずに自分の道を切り開こうとする二人が、一大決心をして第一歩を踏み出すクライマックスが何とも爽快だ。", "1998-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新しい靴を買わなくちゃ", "新しい靴を買わなくちゃ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mister Roberts", "ミスタア・ロバーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "太平洋戦争のさなか、オンボロ輸送船の乗組員たちは戦闘に参加できずダレた生活を送っていた。貨物係のロバーツはことあるごとに艦長と衝突してばかり。ある日、近くの島に美人看護婦がやって来て、ロバーツは島への上陸を計画するが……。個性豊かな役者をそろえ、海軍のおおらかな生活を描いた痛快喜劇。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Man's Favorite Sport?", "男性の好きなスポーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "釣り道具のセールスマンが、釣りの経験も無いのに、釣りの本を書いてベストセラー作家になってしまい、観光地の釣り競技会に招かれる。大会でのおかしな奮闘ぶりと恋の騒動を描くライト・コメディ。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕らのカヌーができるまで", "僕らのカヌーができるまで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ドキュメンタリー番組「グレートジャーニー」で知られる探検家・関野吉晴が、教え子である武蔵野美術大学の学生たちとともに挑んだカヌーづくりの模様を追ったドキュメンタリー。ある日、学生たちに「自然から採取した材料だけで手づくりのカヌーをつくる」という計画を持ちかけた関野。日本とインドネシアを股にかけた壮大な計画に興味を抱いて参加した若者たちが、“ものをつくる”という行為を通して様々なことを学んでいく姿を映しだしていく。", "2010-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La teta asustada", "悲しみのミルク ", "", "", "http://image.eiga.k-img.com/images/movie/55043/315x210.jpg?3094", 97, "80年代から90年代にかけてのペルー内戦を背景に、戦争によるトラウマに苦悩しながらもたくましく生きようとする女性たちの姿を描いたヒューマン・ドラマ。ゲリラによるレイプ被害に遭った母親に育てられ、母乳を通してその悲しみが伝わってしまったために奇病にかかってしまう女性の日常を静かに見つめる。メガホンを取ったのは本作が長編第2作となるペルーの若手女性監督クラウディア・リョサ。第59回ベルリン映画祭で金熊賞に輝いた。", "2011-04-02", "59", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents うそ　「1-2-3-4」", "桃まつり presents うそ　「1-2-3-4」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "", "2010-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケン・笠置のお染久松", "エノケン・笠置のお染久松", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "喜劇王“エノケン”こと榎本健一が主演する歌謡コメディ。久松は、大店で丁稚奉公をしている身。店の娘お染は、久松を見初めて猛烈にアタックするが、久松は身分不相応と、尻込み気味だった。しかも久松には、故郷に兄妹同然に育った許嫁がいて……。歌を交えながら、久松とお染の恋模様を描く。笠置シヅ子のおてんばぶりも必見。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Evening", "いつか眠りにつく前に", "", "", "http://image.eiga.k-img.com/images/movie/34053/315x210.jpg?1384", 117, "人生の最期を迎えようとしている老婦人アンを見守る2人の娘。その時、アンは娘たちの知らない男の名前を口にする。それは1950年代、まだアンが歌手を夢見ていた24歳の時、たった2日間だけ出会った男との記憶だった。しかし、そこには思わぬ悲劇が隠されていた……。スーザン・マイノットによる全米ベストセラー小説を、原作者自身と、「めぐりあう時間たち」のマイケル・カニンガムが脚色。豪華女優共演で描かれる母子2世代に受け継がれるドラマ。", "2008-02-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFTER THE SHOCK", "未来警察マッド・ポリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "政府軍警察と反対制派が熾烈な争いを繰り返している近未来の地球で、日本刀を武器とする一匹狼が、宇宙から来た友好的な異星人美女を救出すべく闘いを開始するSFバイオレンス・アクション。R・タンブリンなどのスターが総出演して脇を固めている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プロヴァンスの恋", "プロヴァンスの恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "　南フランスの田園地帯を舞台に繰り広げられる、ロマンチック・ラブストーリー。「シラノ・ド・ベルジュラック」で世界中の絶賛をあびたジャン＝ポール・ラプノーが、自然美豊かに映像化。1832年夏、夫のもとへ帰るポーリーヌと共に旅を続けるイタリアの騎士アンジェロ。いつしか2人の間に、ほのかな恋心が生まれるが……。原作は「木を植えた男」で知られるジャン・ジオノ。セザール賞撮影賞、音響賞受賞。", "", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二百三高地", "二百三高地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 181, "日露戦争最大の激戦地となった旅順の二百三高地の攻防戦を中心とした戦争スペクタクル。戦闘の合間合間に敵味方が互いに健闘をたたえ合う場面や、愚かな指揮に疑問を持つこともなく犠牲を増やしていく過程など、さだまさしの歌とともに悲哀を高めている。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH BEFORE DISHONOR", "デス・ミッション～怒りの戦場～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "反政府テロが繰り返される中近東の国、ジャマール。新任の駐在副使に同行し、ジャマールを訪れた海兵隊軍曹バーンズはさっそくテロリストの襲撃を受ける。合法的鎮圧を命ずるアメリカ大使の意見も聞かず、バーンズはテロ組織の本拠地へ決死の大襲撃をかける。アメリカ海兵隊魂を描き出したアクションで、「地獄の黙示録」や「レイダース」のスタント監督を務めたT・J・レナードのデビュー作。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Our Town", "俺たちの街", "", "", "http://image.eiga.k-img.com/images/movie/54123/315x210.jpg?3362", 114, "「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。連続女性殺人鬼とその模倣犯、そして彼らを追う刑事のスリリングな対決を描くサイコサスペンス。ミュージカル「ヘドウィック」のオ・マンソク、「ヨコヅナ・マドンナ」のリュ・ドックファンらが出演。女性が十字架型に吊るされる連続殺人事件の捜査に行き詰る刑事チェシン。その頃、推理小説家キョンジュは家賃を催促しにきた大家を殺害し、連続殺人を真似て隠蔽を企てるが、真犯人のヒョイは自身の模倣犯を探し始め……。", "2008-10-25", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Romeo and Juliet", "ロミオとジュリエット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "シェイクスピアの有名悲劇を、豪華なセットと衣装で映画化したラブ・ストーリー。モンタギュー家のロミオとキャピュレット家のジュリエットが恋に落ちるが、両家は長年敵対する間柄。そんな中で、二人が愛を誓いながらも悲劇的な結末を迎えるまでを描く。純愛を貫く主人公二人を演じたN・シアラ、R・ハワードがともに30代であることも話題となった。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("石内尋常高等小学校　花は散れども", "石内尋常高等小学校　花は散れども", "", "", "http://image.eiga.k-img.com/images/movie/53693/315x210.jpg?2757", 118, "「三文役者」「陸に上がった軍艦」等で知られる本作公開時96歳の映画監督・新藤兼人の半自伝的映画。大正の終わり、広島の山奥にある石内尋常小学校に生徒想いの熱血教師・市川がいた。良人、みどり、三吉は市川を慕っていたが、卒業と共に3人は離別してしまう。30年後、市川の定年祝いに戦争をはさんで集まる同級生にはそれぞれの苦労があった。そして良人は黙って村を出たことをみどりに迫られ……。", "2008-09-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NAKED KISS", "裸のキッス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "悪どいヒモを殴り倒して小さな田舎町にたどり着いた娼婦ケリーは、障害児病院の看護婦として更生しようと決める。子供たちとも心が通い、婚約者もできてケリーの前途は祝福されたように見えたが、結婚の当日に呪われた事実が発覚し……。B級映画の巨匠S・フラーの最高作とも呼ばれる衝撃の一編。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瀧の白糸", "瀧の白糸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "水芸人である滝の白糸は、旅興行の先で知り合った苦学生・村越欣弥の、人に媚びないきりりとした性格と将来への夢に惚れこみ、貧乏な村越のために仕送りをして法律を学ばせる。しかし、苦労して借りた金が奪われ、逆上した白糸は高利貸しを刺殺して金を奪い、捕らわれる。彼女を裁くために法廷に立ったのは検事として立派に成長し出世した村越自身だった。昔の恩人を裁くジレンマに苦しむ村越も、ついに法に従い白糸に死刑を宣告する……。名匠・溝口健二が泉鏡花の原作をフラッシュ・バックなどの手法を多用して鮮烈に描いた意欲作。傷んだプリントしか残されていないのが残念。", "1933-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不脱襪的人／A FISHY STORY", "仔猫のように抱きしめて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1960年代の香港。青年お抱え運転手兼雇い主の奥さんの愛人。彼の夢は自分の車を持って、個人タクシーを始めることだった。階下に越してきた少女は女優の卵。二人は恋に落ち、一緒に暮らすことになる。1960年代の懐かしいナンバーにのせて、若者の夢と悲哀を切なく甘く描いたラブ・ストーリー。少女役のM・チャンは、コケティシュな魅力で香港アカデミー賞主演女優賞を受賞した。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("湘南爆走族", "湘南爆走族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "同名マンガの映画化で、山田監督のデビュー作。心優しい暴走族“湘南五人衆”の涙と笑いの友情物語が“横浜御迦”との対決を軸に描かれている。“五人衆”がマンガとそっくりな容姿とアクションで、大いに笑わせる。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIGI", "恋の手ほどき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "10個のオスカーを獲得した、V・ミネリ監督の傑作ミュージカル。おばあさんに育てられた少女ジジは、金持ちのラシュイユのパーティーで社交界にデビュー。そこで出会ったラシュイユの甥ガストンと、互いに惹かれ合うのだが……。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVIL UNDER THE SUN", "地中海殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「ナイル殺人事件」に続くユスチノフ＝ポワロの第２作。イギリスの田園での殺人事件にはじまり、舞台は地中海に浮かぶ孤島のリゾート・ホテルへ……。A・クリスティの推理劇が、凝った舞台装置と美しい風景の中で展開。ユスチノフの巧みな腹芸も印象深い。", "1982-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hwang Jin-yi", "ファン・ジニ　映画版", "", "", "http://image.eiga.k-img.com/images/movie/53843/315x210.jpg?4485", 141, "16世紀の朝鮮王朝で“最高の名妓”と称されたファン・ジニの人生を、「僕の、世界の中心は、君だ。」のソン・ヘギョ主演で映画化。貴族の娘として育ったチニは、自分の実母が下女であると知り、彼女と同じ妓生（キーセン）の道を選ぶ。家を出ることになった彼女は、 幼なじみのノミを最初の男性として身体を捧げる。それから数年後、チニはその美貌と知性で稀代の名妓となる。ファン・ジニがただ1人愛した男ノミに「オールド・ボーイ」のユ・ジテ。", "2008-09-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AL DI LA DEL BENE E DEL MALE", "ルー・サロメ／善悪の彼岸 〈ノーカット版〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "1977年の文芸エロス大作「善悪の彼岸」がノーカット版になって2006年にリバイバル公開された。哲学者ニーチェら数多くの偉人に愛された女傑ルー・サロメと、彼女に惹かれて破滅していった男たちの悲劇を描く。妖しい美貌で世界中を魅了した美女ドミニク・サンダの、サロメになりきった大熱演に心を奪われる。哲学者パウルは奔放な女性ルーに求婚する。ところがルーは男二人、女一人の共同生活を提案。友人を誘い、奇妙な生活が始まる。", "2006-02-18", "4,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HORROR SHOW", "ホラー・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ハリウッドが送り出した数々の怪物が登場する作品を集めた名場面集。ホストにパーキンスを起用、その歴史をひもといていく。フランケンシュタイン、狼男、ドラキュラから半魚人、ハエ男に至るまで、おなじみのキャラクターが総登場。マニアには興味深い。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄火場破り", "鉄火場破り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "黄金の腕ゆえに苦悩する稀代の壷振り親子の半生を中心に、場面の大半を白熱する鉄火場シーンと息づまるアクションで埋めつくした娯楽大作。父の敵“カミソリの龍”を演じる山茶花究が好演。高品格も脇役ながら、シブ味のある演技を披露。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("影なき声", "影なき声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "社会派ミステリー・ブームのなか、鈴木清順が、巨匠・松本清張の原作を得て演出したミステリー映画。清順としては初期の習作の一編だが、アクションのカッティング処理や、一見無意味とも言えるオブジェの突然の画面挿入など、後年の清順美学の片鱗がうかがえる。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WITCH BOAD3: THE POSSESSION", "パラサイト・ゲーム～心霊交信", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "霊界と交信できる“降霊盤”を手に入れたブライアンは、未来を予知する能力を身につける。しかしその代償として、彼は邪悪な霊に寄生されてしまうのだった。自分の子孫を残すために、悪霊はブライアンの肉体を使って妻ジュリーや、彼女の女友だちに襲いかかる。異変に気付いたジュリーは、夫を取り戻すため果敢にも霊に立ち向かうのだが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OUTFIT", "マシンガンウォーズ 暗黒街の野獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1930年代のニューヨークで幅を利かせる悪名高きギャング、ダッチ・シュルツとレッグス・ダイアモンド。ともに武闘派ギャングとして知られる両者の壮絶な抗争を、おとり捜査に乗り出したFBI捜査官の暗躍を絡めてエネルギッシュに描いていく。すべて本物の銃器を使用したという、リアルな銃撃戦は迫力満点。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rebel Without a Cause", "理由なき反抗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「エデンの東」に続くJ・ディーンの主演第２作。赤のスウィングトップにブルージーンズというディーンの決定的かつ伝説的なイメージは、この作品のコスチュームによるものである。主人公は、両親との交流の不在から非行に走る17歳の少年ジム。度胸だめしのチキン・レースを暴走少年グループに挑まれた転校生の彼は、勝負に勝つが、相手が崖から車ごと転落するという事故に遭遇。やがて警察に追われる身となり、ある悲劇を招いてしまう。少年期特有の孤独と気概と不甲斐なさを同時に体現するディーンは、まさにハマリ役。N・レイならではの色彩設計も際立ち、特にジムがたてこもるプラネタリウム館におけるシークエンスは異様な迫力に満ちている。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しゃったぁず・4", "しゃったぁず・4", "", "", "http://image.eiga.k-img.com/images/movie/55563/315x210.jpg?3235", 0, "さびれた商店街にある酒屋の跡取り息子の邦夫は、母親の死をきっかけに地元の新潟に帰るが、うだつのあがらない邦夫に愛想を尽かした妻の陽子は、子どもを連れて東京に戻ってしまう。陽子に離婚を突きつけられたことで自分のふがいなさを痛感した邦夫は、父の倉三とともに商店街の活性化を目指す。新潟県十日町市で開催された「大地の芸術祭越後妻有アートトリエンナーレ2009」で上映され話題を集め、10年に劇場公開が決定。池内万作がダメ息子を熱演。", "2010-06-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("完全なる飼育・赤い殺意", "完全なる飼育・赤い殺意", "", "", "http://ecx.images-amazon.com/images/I/51Z1TTP3NKL._SL210_.jpg", 99, "監禁された少女と、その当事者である男の歪んだ愛情を描く人気シリーズの第６作。殺人を犯して逃亡する青年が、農家で監禁された少女を発見。そんな彼らと、少女を“飼育”している男の愛憎のドラマが、緊迫感をみなぎらせて展開していく。「明日なき街角」以来、７年ぶりにメガホンを執ったエロス＆バイオレンスの異才、若松孝二の濃密な語り口に注目。ホストの関本文也は闇賭博の借金返済に困り、持ち掛けられた殺人の依頼を遂行する。殺害現場を見られた関本は必死の逃亡を図り、一軒の農家に転がり込んだ。するとそこには、異常におびえた様子の明子という少女がいて……。", "2004-09-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GROSSE FATIGUE", "他人のそら似", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「仕立て屋の恋」で一躍日本で有名となったM・ブランが、監督・脚本・主演を務めたシニカルなコメディ。人気俳優で映画監督のミシェル・ブランはある日、まったく身に覚えがないのに20年来の女友だちをレイプした罪で警察に捕まる。友人の女優キャロル・ブーケの証言によってその疑いは晴れるが、どうやら彼と瓜二つのニセ者がミシェルの名を偽って、悪事を働いているらしい。そこで本物の彼はC・ブーケとともに真相解明に乗り出すのだが……。M・ブラン、C・ブーケだけでなく、P・ノワレらフランス映画界のスター俳優が、多数実名で登場しているのも見もの。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bourne Ultimatum", "ボーン・アルティメイタム", "", "", "http://image.eiga.k-img.com/images/movie/34083/315x210.jpg?1831", 115, "マット・デイモンが記憶を失った暗殺者ジェイソン・ボーンに扮する大ヒット・アクションのシリーズ完結篇。イギリスの鬼才ポール・グリーングラス監督が前作に続いてメガホンを取り、ボーンの孤独な戦いをスリリングに描き出す。自らの失われた過去を取り戻すべく奔走するボーンは、CIAの“トレッドストーン計画”についての記事を書いた新聞記者に接触するためロンドンへと向かう。だが、そこにはCIAの現地要員が待ち受けており……。", "2007-11-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pina 3D", "Pina ピナ・バウシュ　踊り続けるいのち", "", "", "http://image.eiga.k-img.com/images/movie/56953/315x210.jpg?2569", 104, "2009年に逝去したドイツの天才舞踊家ピナ・バウシュの世界を、「ベルリン・天使の詩」「ブエナ・ビスタ・ソシアル・クラブ」のビム・ベンダース監督が3Dで撮影したドキュメンタリー。バウシュが生前から映画のために選んでいた「カフェ・ミュラー」「春の祭典」「フルムーン」「コンタクトホーフ」の4つの舞台のほか、バウシュが芸術監督を務めていたドイツ・ブッパタール舞踏団のダンサーたちが街中や自然の中で繰り広げるパフォーマンスや、在りし日のバウシュの映像、ダンサーたちからの追悼のメッセージが収められている。", "2012-02-25", "7,2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鯉名の銀平・雪の渡り鳥", "鯉名の銀平・雪の渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "長谷川伸の名作戯曲に、当時人気絶頂だった阪妻が挑んだサイレント作品。伊豆下田で売り出し中の銀平は、惚れた娘が親友に想いを寄せていることに打ちのめされて旅立つ。時が経ち、下田に舞い戻った彼は親友夫婦のためにひと肌脱ぐことに……。阪妻主演作の量産体制にあった同プロの渡り鳥叙情編。", "1931-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES MISE´RABLES", "レ・ミゼラブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 186, "V・ユゴーの名作『レ・ミゼラブル』のフランスでの４度目の映画化。シャノワ監督は正攻法で原作を忠実に捉え、1934年に製作された３部作と並び称される大作に撮り上げた。日本公開時は３時間10分（本編は３時間50分）の短縮版。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒侠", "ブラック・マスク〈黒侠〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「HERO」のジェット・リー主演、ツイ・ハーク製作、脚本による近未来アクション。洗脳と肉体改造で無敵の戦士を作る秘密組織、701部隊から一人の男が脱走する。洗脳をまぬがれた彼は黒い仮面をつけた“ブラック・マスク”となって暗躍。屈強な部隊と政府の恐るべき陰謀に立ち向かう。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("СУДЪБА ЧЕЛОВЕКА", "人間の運命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "飢饉で家族を失い天涯孤独となったソコロフは、同じ境遇の娘イリーナと結婚し幸福な生活を手に入れるが、それもつかの間、第二次大戦が勃発し、彼は妻子と別れて戦線に向かう。ドイツ軍の捕虜になりながらも故郷のイリーナの面影を胸に生き抜くソコロフだったが……。全世界の絶賛を受けた感動の人間ドラマ。モスクワ映画祭グランプリ受賞。", "1959-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Loft", "LOFT 完全なる嘘（トリック）", "", "", "http://image.eiga.k-img.com/images/movie/57593/315x210.jpg?3587", 111, "ベルギーでヒットしたミステリーサスペンス「ロフト.」（2008）をオランダでリメイク。建築家のマティアス、精神科医のバルト、エンジニアのロベルト、セールスマンのウィレム、実業家のトムの5人は、それぞれ妻に内緒で新築マンションの最上階のロフトルームを共有し、情事にふける場所として使用していた。ある朝、ロベルトが部屋に入ると、1人の女がベッドに手錠をかけられ血まみれになって死んでいた。あわてて集まった5人は、それぞれのアリバイを確認しながら犯人を探し始めるのだが……。", "2012-04-21", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLASH GORDON", "フラッシュ・ゴードン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「スーパーマン」と並んで高い人気を誇っていたSFコミックスの再映画化作。地球を襲う怪現象は惑星モンゴから発せられる放射能のせいだと知った科学者が、フットボール選手のフラッシュを送り、悪の皇帝ミンと対決させるというアドベンチャー。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄・平成侠客伝", "鉄・平成侠客伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「大怪獣東京に現る」でデビューした宮坂武志監督による仁侠アクション。「制覇」「報復」「チャカ２」と主演作が続く竹内力が、時代に乗り遅れた昔気質のヤクザ者を快演している。義理人情に厚いヤクザ、万二郎の組は壊滅寸前。そんな彼の前に、流れ者の鉄が現われた。仁義を重んじる鉄の男気に惚れた万二郎は、彼と結託。敵対組織との抗争に立ち向かっていく。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　GENERAL　DIED　AT　DAWN", "将軍暁に死す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "支那で、住民に重税を加える暴君ヤンに反発し民衆が立ち上がる。ちょうどその頃、支那にいた米国人のオハラは、民衆の浄財を引き受け武器を調達するために上海へと向かうが、ヤンがオハラの行動を察知、ヤン、オハラ、武器商人たちが激しい金の争奪戦を繰り広げる。G・クーパーが陰のあるオハラ役を熱演する戦争人間ドラマ。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("婚期", "婚期", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "実業家・唐沢卓夫は家庭をかえりみず、妾を囲う身。妻の静は卓夫の二人の妹・波子と鳩子のイビリに耐えつつ、逆襲を狙っている。そんなある日、静のもとに差出人不明の手紙が舞い込んで……。女流脚本家・水木洋子のオリジナル・シナリオを吉村公三郎が監督したホーム・コメディ。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANNY THE DOG", "ダニー・ザ・ドッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ジェット・リーが孤独な男を演じるアクション・ドラマ。殺人マシーンとして調教されてきた男が盲目のピアニストと出会い、初めて人を愛することを知る。眠っていた人間の感情の目覚めをエモーショナルに描出する、力強い愛の物語を得意とするベッソン節は健在。5歳の時に誘拐され、犬のような扱いを受けて育てられたダニー。成長した彼は、愛を知らない冷酷無比な殺人マシーンとなっていた。", "2005-06-25", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A CHORUS LINE", "コーラスライン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "1975年、ブロードウェイで幕を開けロングラン記録を更新したミュージカルを完全映画化。オーディションを控え、踊りに青春のすべてを賭けたダンサーたちが、おのおのの熱い思いを吐露していくさまを描いた一編。ドラマの核をなす17人のダンサー役には、いずれも日本ではあまり名を知られていない新人たちが顔をそろえ、明日のスターを夢見る役に挑んでいる。監督は「ガンジー」で力量を発揮したR・アッテンボロー。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE TRIO INFERNAL", "地獄の貴婦人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1930年代にフランスで実際に起こった知能犯の保険金殺人事件をヒントに、人間の内に潜む、欲望と狂気をリアルにえぐり出した問題作。二人の姉妹とその仲間の男が、凄惨をきわめた殺人を次々と実行する場面もスキャンダラスな話題となった。", "1974-01-01", "2,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCARLET DIVA", "スカーレット・ディーバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "“スカーレット・ディーバ”という異名でカリスマ的な人気を博す女優アンナ。常に華やかなスポットライトを浴びる彼女だったが、私生活ではやるせない孤独感に打ちひしがれていた。セックスとドラッグに溺れるアンナは、やがて若いミュージシャンを心から愛し、彼の子供を身ごもるのだが……。イタリアの人気女優A・アルジェントが、父親ダリオのプロデュースの下で手掛けた長編初監督作。彼女自身を思わせる有名アクトレスの苦悩を、赤裸々かつ切実に描き上げている。", "2000-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I'm Still Here", "容疑者、ホアキン・フェニックス", "", "", "http://image.eiga.k-img.com/images/movie/57863/315x210.jpg?3711", 108, "俳優ホアキン・フェニックスが自ら仕掛けたドッキリで、歌手転向を発表しラッパーを目指す自身の姿と、その様子に驚かされる人々の様子をとらえたドキュメンタリー。俳優引退を宣言をし、パパラッチから逃れて引きこもり生活を送りながら、ラッパーに転身しようと奮闘するフェニックスを映し出す一方で、その挙動不審な態度にブルース・ウィリスやジャック・ニコルソン、ベン・スティラーら大物ハリウッドスターが次々とだまされる様子が収められている。ベン・アフレックの実弟ケイシー・アフレックの初監督作。", "2012-04-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("県警対組織暴力", "県警対組織暴力", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「仁義なき戦い」のメンバーが、暴力を警察側からの視点で生々しく描いた傑作。刑事・久能と大原組の広谷との友情を軸に、警察からヤクザに寝返る刑事、正義派に見せながら権力と癒着する刑事など、社会に巣食う悪の実態をリアルに映し出す。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rough Cut and Ready Dubbed", "ラフ・カット＆レディ・ダブド", "", "", "http://ecx.images-amazon.com/images/I/51WOArWSHML._SL210_.jpg", 61, "1978年～81年のUKパンク、ニューウェーブ黎明期を描いたドキュメンタリー。“パンクは終わった”と言われはじめた1978年、当時20歳だった2人の若者が撮影を開始し、4年の歳月をかけて完成。スティッフ・リトル・フィンガーズ、コックニー・リジェクツといったミュージシャンやストリートの若者たちへのインタビュー、貴重なライブ映像を中心に、新たなパンク・ムーブメントが生まれる瞬間を切り取っていく。日本では2007年に初公開。", "2007-01-20", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　SWEET　HEREAFTER", "スウィート・ヒアアフター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "悲劇が降りかかった小さな町のその後を、繊細なタッチで描き出したヒューマン・ドラマ。雪に包まれた小さな町で、22人の子供を乗せたスクールバスが湖に転落した。それから二年後、初老の弁護士スティーヴンスが集団訴訟を起こすため、この町にやって来る。しかし、町の人々は消極的で唯一生き残った少女ニコールも固く口を閉ざしてしまっている。やがて、事件の真相とともに、町の人々の複雑な人間関係や秘密が明らかになり、ニコールは証言することを誓うのだが……。「アララトの聖母」のA・エゴヤンが、童話『ハルメンの笛ふき』のエッセンスを巧みに取り入れ、胸に染み入る悲しみと絶望、癒しのドラマを作り上げた。雪景色の冷たく透明感に満ちた映像も印象的だ。", "1997-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESPERATE JOURNEY", "戦場を駆ける男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "英国爆撃機が、ドイツの対空砲火を浴びて不時着。生き残った５名のパイロットはナチに捕らえられるが、決死の脱出を図り、ベルリンへ逃れて空き家の地下室に身を隠す。一人が重傷を負い、ケーテという薬屋の娘に救われるが、そこにはすでにゲシュタポの手が及んでいて……。剣劇スターのフリンと、後の第40代アメリカ大統領に就任したレーガン主演による戦争アクション。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BATON バトン", "BATON バトン", "", "", "http://ecx.images-amazon.com/images/I/51peQHgqDpL._SL210_.jpg", 0, "岩井俊二がプロデュースと脚本を手がけ、「あずみ」「スカイハイ」の北村隆平監督がメガホンをとったSFアニメーション。人間とロボットが共存する惑星を舞台に、密航者が持っていた謎のOS「サイファ」をめぐる事件に巻きこまれていく少年アポロと少女ミカルの冒険を描く。声優には市原隼人、上戸彩ら豪華俳優陣を起用。2009年に開催された横浜開港150周年イベント「開国博Y150」で上映され話題を呼んだ。", "2009-04-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("半妖乳娘／THE DEMON WET NURSE", "ラスト・エロティック・キングダム／後宮佳麗三千人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "絢爛豪華な宮廷世界での、美しい娘の性遍歴を描くエロティック大作。ある男と知り合ったことがきっかけで宮廷入りした娘が、皇后に愛され愛欲の虜になっていく。さらに皇帝もが、彼女を側室に求めるが……。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わさお", "わさお", "", "", "http://image.eiga.k-img.com/images/movie/55713/315x210.jpg?3650", 116, "ブサイクだけど愛らしい“ブサかわ犬”として人気の秋田犬・わさおが、映画初出演を果たした人情ドラマ。青森県鯵ヶ沢町でイカ焼き屋を営むセツ子（薬師丸ひろ子）のもとに、フサフサの白い毛に覆われた大きな秋田犬が疲れ果てた姿でたどり着く。セツ子は犬にわさおと名づけて飼い始めるが、野生育ちのわさおはなかなか懐こうとしない。それでも愛情を注ぎ続けるセツ子に、いつしかわさおも心を開いていく。", "2011-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUSHING　TIN", "狂っちゃいないぜ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "航空交通管制で働く男たちのドラマ。一日に9000機の飛行機の離発着の安全を確保しているニューヨークの管制官たちは、ハードな仕事で神経を擦り減らしていた。それは自分をベストの管制官だと自負しているニックも同じだった。そんな彼の前に新入りのラッセルが現れる。最高の腕をもち、強烈な個性を放つ彼にニックはライバル心を燃やす。そして、ニックがラッセルの妻と、ラッセルがニックの妻と怪しい関係になった時、二人の敵対心はさらに燃え上がるのだった……。仕事と人間関係のストレスにさいなまれた男の葛藤と絆を、ユーモアを交えながらサスペンスフルに描き出す。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初恋　夏の記憶", "初恋　夏の記憶", "", "", "http://image.eiga.k-img.com/images/movie/54243/315x210.jpg?3184", 105, "家庭の事情で東京から美しい山間の町に引っ越してきた佑介は、継母と暮らす自由奔放な美少女・梨生に出会い、その不思議な魅力に強く惹かれていく。一方の梨生は、佑介の父親である軍司に恋心を抱いており……。文豪イワン・ツルゲーネフの同名原作を、現代の日本に置き換えて映画化。主演は「バッテリー」の山田健太、多岐川裕美の一人娘・多岐川華子。", "2009-03-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VINCENT", "ビンセント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 6, "T・バートンがディズニー・スタジオに在籍していた1982年に製作した短編アニメーション。1996年に「ジャイアント・ピーチ」の併映という形で初公開された。ヴィンセント少年の夢は、怪奇映画スターのヴィンセント・プライスになること。その夢はいつしか彼を不気味な世界へと誘うのだった。ヴィンセント・プライス本人がナレーションを担当している。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Somewhere in Time", "ある日どこかで", "", "", "http://ecx.images-amazon.com/images/I/51quLV2fzrL._SL210_.jpg", 103, "「地球最後の男オメガマン」「激突！」などで知られるSF作家リチャード・マシスンの同名小説を映画化したタイムトラベル・ファンタジー。1972年、母校で自作舞台の初演を迎えていた新進劇作家のリチャードは、白髪の老婦人から古い金時計を渡される。その8年後、母校を再訪した彼は街のホテルにかかっていた肖像画に心を奪われる。そこに描かれた美女は、かつての老婦人の若き日の姿だった……。主演は「スーパーマン」のクリストファー・リーブ。監督は「ジョーズ2」のヤノット・シュワルツ。", "1981-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1918", "1918", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "二度のアカデミー賞に輝く名シナリオライター、フートの自伝的物語の映画化。テキサス州の架空の町ハリソンを舞台に、戦争と疫病が彼らにもたらした悲しみが綴られていく。フート自身の手によってブロードウェイで舞台化され、ヒットした後に、ほとんど同じ出演者によって映画化された。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仔鹿物語", "仔鹿物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "アクション派として知られる澤田幸弘が、北海道の釧路湿原を舞台にエゾシカと少年の交流を感動的に描くファミリー・ピクチャー。６月のある日、母を亡くした仔鹿を拾い、育てる決心をする健一。だが、両親に見つかってしまい、山に帰すようしかられるが、健一の熱意に負けた両親は、秋まで家で育てることを許す……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母を恋はずや", "母を恋はずや", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "大黒柱の父親が急死し、経済的に没落せざるを得ない母子家庭の生活は、日ごとに厳しくなっていく。やがて母の愛情が不公平であることに気づいた兄は、弟や母とけんかして家を出る。だが兄は実は亡き父の前妻との間の子だった……。母子の愛情を、小津特有の細やかな画面の中に描ききった秀作。現存フィルムには欠落部分がある。", "1934-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOLPHIN　GLIDE", "ドルフィン・グライド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "水中撮影の名匠、ジョージ・グリノーが、イルカの目線から見た水面下や波間の世界を撮影。癒し効果を狙った作品とは違い、独特の発想が生んだアート性の強い作品だ。自由自在に泳ぎ回るイルカになった気分で、スピード感溢れる海の旅を体感できる。水面を華麗に飛び、自由に泳ぐイルカ。10年間、イルカと共に波に乗ってきたグリノーが捉えた、イルカの見た美しい風景が映し出される。", "2005-08-06", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("報復〈劇場版〉", "報復〈劇場版〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1998年にオリジナルビデオとして発表された同名作を、やはり竹内力主演でリメイクした見ごたえある密室劇。借金でクビが回らなくなった者たちを集めて“カンボジアン・ルーレット”という殺し合いゲームをさせ、ギャンブルの対象としている秘密のカジノ。そこに送り込まれた元刑事が、命を削る数日間の過酷な駆け引きの中で、数年前のトラウマに決着をつけていく。互いの生き残りをかけて、談合と裏切りを繰り返す一筋縄ではいかない面々。寺島進、光石研、遠藤憲一といった個性派たちが存在感たっぷりにアウトサイダーを演じ、熱い火花を散らしている。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN GIGOLO", "アメリカン・ジゴロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "ビバリーヒルズの高級ジゴロ、ジュリアンは金持ち女に飽き飽きしていたが、ある日、上院議員夫人ミシェルと出会い愛し始める。しかしジュリアンは殺人事件に巻き込まれ……。小津やブレッソンの研究家でもあるP・シュレーダー監督らしい静かなタッチの犯罪ドラマ。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOUNG FRANKENSTEIN", "ヤング・フランケンシュタイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1930年代のユニバーサルのヒット・シリーズ“フランケンシュタイン”のパロディー。フランケンシュタイン博士のひ孫に、遺産が与えられることになり、故郷に戻った彼が祖父にならってまたまた怪物を創造するというギャグ満載のハチャメチャ・コメディ。有名作品のパロディーを得意としたM・ブルックスがメガホンを執っている。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIRTH OF A NATION", "國民の創生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "アメリカ映画史上初めての長編劇映画であり、カット・バックやラスト・ミニッツ・レスキューなど、ドラマと混然一体となった映画の技法に時代を画した問題作。空前のスケールで描かれる南北戦争の動乱の中で、北軍と南軍に引き裂かれていくキャメロン家とストーンマン家の息子たち、娘たち。その運命、その人間ドラマ。戦後の混乱と悲惨な生活。続いて起こるリンカーンの暗殺。黒人たちの制裁に立ち上がるKKK（クー・クラックス・クラン)。壮大な叙事詩は当時全米の人々の熱狂を引き起こし、巨大な利益をもたらした。その一方、あまりにも過激な人種差別観はすでに公開当時から世論の非難を浴びた。この映画を史上最高の映画と呼んだ主演のL・ギッシュは、1987年の「八月の鯨」まで映画の歴史を生き続けた名優。なおリンカーンの暗殺者に扮しているのは、後の監督R・ウォルシュ。", "1915-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETURN TO PEYTON PLACE", "青春の旅情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "「青春物語」でニューヨークへ旅立って行った少女アリソンのその後を、名優J・フェラーが演出。キャストは一新されている。処女小説が出版されることになったアリソンは、出版社の社長リュイスと愛し合うようになるが、故郷のスキャンダルを暴露した小説の内容に地元の人々はカンカンとなり……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIVING IN OBLIVION", "リビング・イン・オブリビオン　悪夢の撮影日誌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "低予算映画の撮影中に巻き起こる騒動を描いた異色ドラマ。低予算映画「リビング・イン・オブリビオン」を撮影中のニック・レヴィ監督。重要シーンの撮影を控え、緊迫するニックだが、雑音が入ったり、カメラマンが吐き気をもよおしたりと、撮影はままならない。そしてラブ・シーンにさしかかると、主演男優チャドのわがままが始まり、女優とカメラマンの愛憎劇もあって撮影現場は大混乱。続行不可能かと思った時、精神病院にいるはずのニックの母親が現れ……。1995年サンダンス・フィルム・フェスティバルでドラマ部門脚本賞を受賞。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELMER GANTRY", "エルマー・ガントリー 魅せられた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "B・ランカスターがアカデミー主演男優賞を受賞したヒューマン大作。口八丁手八丁のセールスマン・エルマーは、“聖処女”と呼ばれる宗教団体の女教祖に一目惚れしてしまい、彼女の気を惹こうと団体きっての弁士となる。しかし、いつしか彼の過去の悪業も暴露されてしまう……。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROXANNE", "愛しのロクサーヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "S・マーティンが、名作戯曲『シラノ・ド・ベルジュラック』をモチーフに創り上げたほのぼのコメディ。とある平和な町の消防団長C・Dは、大きく醜い“鼻”にコンプレックスを持っていた。ある日、町にやって来た美人の天文学者ロクサーヌと知り合ったC・Dは、彼女に一目惚れしてしまう。しかし、C・Dの友人の若い消防士クリスも彼女に熱を上げてしまった。自分の醜さを知っているC・Dはクリスのために、愛の橋渡し役を買って出るのだが……。S・マーティン独特のシチュエーション・ギャグと台詞の妙が見事にマッチした作品。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINAL　DESTINATION　２", "デッドコースター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "逃れられない死の運命を描いた全米ヒット作「ファイナル・デスティネーション」の続編。意表を突いてやって来る死の凄惨な描写はもちろん、死の運命に抵抗する人々の物語もスリリングだ。ハイウェイで悲惨な事故が発生。少女キンバリーは予知夢によって事故を逃れたが、その後、彼女のおかげで救われた人々が不審な死を遂げる。それが定められた“運命”の仕業と知ったとき、彼女は生存者たちと運命を変えようとするが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STREET SMART", "N.Y.ストリート・スマート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アメリカン・ニューシネマの後期の代表作「スケアクロウ」を監督したジェリー・シャッツバーグが、「スーパーマン」のC・リーヴ主演で撮ったサスペンス。マスコミの巨大な力にほんろうされるジャーナリストのジョナサンは、特ダネ記事を割り当ててもらうためポン引きの記事をでっち上げるが、その記事が恐ろしい殺人事件を引き起こす。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人生劇場", "人生劇場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "明治の気概に生きた父と子の、波乱に富んだ生涯を描いた、尾崎士郎不朽の名作の映画化。主人公・青成瓢吉と、彼を取り巻く人々の青春期にスポットをあて、原作の『青春編』『愛欲編』『残侠編』を一つにまとめた、３人の監督による共同演出の一編。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SMOKEY BITES THE DUST", "クレイジーポリス大追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "米国南部、アーカンサスの片田舎。いつもは静かなこの町に突如として大騒ぎが勃発した。保安官ターナーの一人娘ペギーに恋する若者ロスコーが、彼女をさらって逃走したのだ。頑固親父ターナーは、パトカーで猛然と追跡を開始する。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stormbreaker", "アレックス・ライダー", "", "", "http://image.eiga.k-img.com/images/movie/34273/315x210.jpg?1313", 93, "少年スパイ、アレックスの活躍を描いたイギリスの人気小説を映画化。諜報機関MI6の諜報員である伯父から、気付かない内にスパイとしての英才教育を受けて育った少年アレックス。伯父の死をきっかけにその事実を知った彼は、MI6のスカウトでスパイとして活動することになり……。主演は本作が映画初出演となる新鋭アレックス・ペティファー。共演にはユアン・マクレガー、ミッキー・ロークら豪華キャストが顔を揃える。", "2007-10-27", "7,1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンフェア the answer", "アンフェア the answer", "", "", "http://image.eiga.k-img.com/images/movie/55893/315x210.jpg?3811", 109, "篠原涼子主演で2006年に連続ドラマが放送、07年には劇場版も公開された刑事サスペンスの4年ぶり劇場版第2作。検挙率ナンバーワンを誇る敏腕刑事・雪平夏見は、北海道・紋別の地で連続殺人事件の容疑をかけられてしまう。さらには警察の内部機密が隠されているUSBメモリをめぐって元夫や同僚、上司、検察、凶悪犯罪者らに追われることになり、雪平は緊迫する逃避行を繰り広げる。監督は前作の脚本も手がけた「K-20 怪人二十面相・伝」の佐藤嗣麻子。", "2011-09-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／PEOPLE IN THE DARKNESS", "暗闇の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "原作者イ・ドンチョルのソウルの娼婦街での実体験をもとに、ある娼婦の遍歴を描く。歌手を夢みて地方からソウルにやって来たナンエ。彼女は悪徳芸能プロにだまされ、ドサ回りの歌劇団に入るが、チンピラと結ばれて幸福をつかむ。だが、夫と、生まれた子供が死亡。絶望の中で、彼女は娼婦に身を落としていく……。底辺に生き、数々の試練に見舞われながらも、無垢な心を失わなかったヒロインをナ・ヨンヒが好演。", "1981-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AMOUR EN FUITE", "逃げ去る恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "トリュフォー監督の自伝的要素の強い、“アントワーヌ・ドワネル”シリーズ完結編。「大人は判ってくれない」の主人公アントワーヌもいまや中年。30年にわたる恋人たちとの交遊が、回顧的、感傷的に語られる。", "1978-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　大江戸りびんぐでっど", "シネマ歌舞伎　大江戸りびんぐでっど", "", "", "http://image.eiga.k-img.com/images/movie/55783/315x210.jpg?3568", 103, "歌舞伎の舞台公演をスクリーンでデジタル上映する「シネマ歌舞伎」シリーズ第12弾。宮藤官九郎が演出を手がけた異色歌舞伎で、市川染五郎、中村七之助ら豪華キャストが顔をそろえる。時は江戸時代。くさや汁を浴びた死人が“ぞんび”として生き返り、江戸の町は大騒ぎとなる。くさやの名産地である新島出身の半助は、くさや汁を体に塗ることで彼らを従わせ、人間の代わりに“ぞんび”を雇う人材派遣会社「はけんや半助」を起業する。", "2010-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PORKY’S II：THE NEXT DAY", "ポーキーズ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "青春の屈折とは無縁のネアカ青春コメディ第２作。監督・主要キャストは変わらず。前作で“ピー・ウィー”君の童貞喪失を手助けしたお色気ギャル、ウェンディが、今度は人種偏見に満ちた大人たちを色仕掛けでやっつける。ポップな曲とエッチ場面満載。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("症例X", "症例X", "", "", "http://image.eiga.k-img.com/images/movie/56903/315x210.jpg?1344", 67, "第20回PFFスカラシップによる商業デビュー作「家族X」（2011）を完成させた吉田光希監督が2008年に手がけた自主制作映画で、介護を題材に親子の関係を見つめるドラマ。本作がPFFアワード2008で審査員特別賞を受賞し、PFFスカラシップの権利を獲得した。36歳の謙一は、統合失調症を患い最近は認知症も進行している70歳の母・敏江と2人で暮らしている。敏江はいつしか終日を床の中で過ごすようになり、謙一は仕事と介護だけを繰り返す生活を続けていく。", "2011-10-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忠直卿行状記", "忠直卿行状記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "内外からその英邁ぶりをうたわれ、賞賛を浴びていた松平忠直。しかし、家臣の内緒話から、生まれてこの方自分の身に注がれてきた賞賛は偽りであり、家臣の追従であることを知った忠直は、暴虐非道を極め始める。菊池寛のおなじみの小説の映画化。市川雷蔵が孤独でかげりのある忠直役を好演する。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グシャノビンヅメ", "グシャノビンヅメ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「深夜臓器」で第２回インディーズムービー・フェスティバルのグランプリを受賞した新鋭、山口洋輝監督の劇場用映画初監督作品。独創的な世界観と、強烈なキャラクターで紡ぎ出される映像美で、人間の本質を鋭く見据えた衝撃作。徹底したインディペンデント体制のもとで撮り上げた本作からは、山口監督の作品に対する強いこだわりが感じ取れる。何百階層にも渡って、重なり連なる居住区を移動するための交通機関“移動機筒”が網羅する地区に住んでいる17歳の藤崎ルキノ。ある朝、ルキノは、久々の学校に登校するため、その“移動機筒”に乗り込むのだったが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN　POP", "アメリカン・ポップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「フリッツ・ザ・キャット」「指輪物語」のアニメーション作家R・バクシが、実写で撮影した人物の動きをなぞるロートスコープ・アニメという独特の技法を用いて完成させた作品。あるユダヤ人家族の家系をたどりながら、ジャズ、フォーク、ロックなどの音楽の変遷を、各時代の風俗を織り交ぜて描く壮大なアメリカ近代史。デューク・エリントン、チャーリー・パーカー、エルヴィス・プレスリー、ジミ・ヘンドリックスらの音楽が使用され、アーティスト本人を思わせるキャラクターも登場する。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("To Kill a Mockingbird", "アラバマ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "1930年代。アラバマ州の田舎町で弁護士の父親と平穏な日々を送るジェムとスカウトの幼い兄弟。だが、父親が裁判で黒人の弁護を引き受けたことから……。ハーパー・リーのピュリッツァー賞受賞小説『ものまね鳥を殺すには』を映画化した社会派ドラマ。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸の悪太郎", "江戸の悪太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "嵐寛寿郎が正義の浪人に扮して長屋の人を助ける娯楽時代劇。長屋で私塾を開く島崎三四郎は、皆の尊敬の的。彼はある日、三吉という少年を拾うが、この三吉、実は百万長者の娘。実家から捜索の手が伸びる一方で、長屋取り壊しの陰謀をたくらむ牧山典膳の陰謀も起こる。三四郎は、三吉と長屋のために立ち上がる。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星守る犬", "星守る犬", "", "", "http://image.eiga.k-img.com/images/movie/55693/315x210.jpg?2115", 128, "村上たかしのベストセラーコミックを、西田敏行主演で映画化。北海道の田舎町で、山中に放置された自動車から中年男性とその飼いイヌの遺体が発見される。身元不明の遺体を引き取る市役所勤務の青年の調査により、失業から一家離散を経てホームレスとなった男性が、たった一匹の愛犬ハッピーと旅に出ていた過去が明らかにされていく。監督は「イキガミ」「スープ・オペラ」の瀧本智行。男性の足跡をたどる青年役の玉山鉄二ほか、川島海荷、余貴美子らが出演。", "2011-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NANA", "女優ナナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "E・ゾラ原作による文芸大作。ナナの波乱に富んだ生涯を適役の美人女優M・キャロルを得て、エロティックに、ユーモラスに、またアクションを交えながら見ごたえのある娯楽作品に仕立て上げている。フランス映画らしい味の典型的な大衆受けを狙ったもの。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Knut and Friends", "クヌート", "", "", "http://image.eiga.k-img.com/images/movie/54543/315x210.jpg?3970", 92, "人工哺育で育ったホッキョクグマ、クヌートの成長を見つめるドキュメンタリー。2006年12月、ドイツのベルリン動物園で双子のホッキョクグマが産まれるが、母クマの育児放棄により1頭は生後まもなく死亡、残された1頭は飼育員トーマスの手で育てられることになる。人工哺育に対して賛否両論が巻き起こる中、クヌートと名付けられた子グマはすくすくと成長していき、温暖化が原因で絶滅の危機に瀕しているホッキョクグマへの関心を促す環境大使に任命される。", "2009-07-25", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼくと、ぼくらの夏", "ぼくと、ぼくらの夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "第４回サントリーミステリー大賞の受賞作を映画化したミステリー・タッチの青春ドラマ。ある地方の学園都市。テキ屋の一人娘、高校２年の酒井麻子。そして刑事の息子・戸川春。二人が同級生の入水自殺事件を独自で捜査する姿を、グループサウンズ全盛時のヒット曲をバックに描いていく。沢田研二をはじめ、かつてのグループサウンズのメンバーも、大挙出演。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lady for a Day", "一日だけの淑女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ニューヨークでリンゴを売って暮らしている貧しい女アニー。彼女はやっとの思いで愛娘ルイーズをスペインへ留学させているのだが、ある日、ルイーズが婚約者である伯爵の子息を連れて帰国することに。娘に恥をかかせたくないアニーは、仲間たちの協力を得て1日だけ上流階級の貴婦人を装うことになるのだが……。監督のフランク・キャプラは、後に「ポケット一杯の幸福」というタイトルで本作をセルフ・リメイクしている。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女咲かせます", "女咲かせます", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "万引き常習の女怪盗が貧しいチェロ弾きに恋をした。なんとか彼に楽器を贈ってやりたい彼女は仲間を集めてデパートの売上金強奪を企てる。奔放で色っぽい松坂とクソ真面目な役所の対照が面白い。うす汚れた脇役たちの宴会シーンなどに、森崎流バイタリティーを感じさせる。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　プリキュアオールスターズDX2 希望の光☆レインボージュエルを守れ！", "映画　プリキュアオールスターズDX2 希望の光☆レインボージュエルを守れ！", "", "", "http://image.eiga.k-img.com/images/movie/55163/315x210.jpg?2112", 0, "女の子たちに絶大な人気を誇る「プリキュア」シリーズの劇場版。中学2年生の花咲つぼみは、コフレとシプレを探して海上の遊園地・フェアリーパークへと向かう。そこでつぼみは無事にコフレとシプレを見つけるが、突然パーク内に怪しげな影が現れ、レインボージュエルを守るため17人のプリキュアたちが次々と登場する。", "2010-03-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Goya's Ghosts", "宮廷画家ゴヤは見た", "", "", "http://image.eiga.k-img.com/images/movie/53893/315x210.jpg?3045", 114, "「カッコーの巣の上で」「アマデウス」のオスカー監督ミロス・フォアマンが、18世紀末スペインの宮廷画家ゴヤの目を通し、絵のモデルとなった神父と少女が辿る数奇な運命を描いた人間ドラマ。肖像画を描いてもらうためゴヤのアトリエを訪れたロレンソ神父は、そこで見た絵の中の少女イネスの美しさに心を奪われる。ところがある日、イネスは無実の罪で異端審問所に囚われてしまい……。製作は3度のオスカーに輝くソウル・ゼインツ。脚本はブニュエル作品で知られる大家ジャン＝クロード・カリエール。", "2008-10-04", "1,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花子の日記", "花子の日記", "", "", "http://image.eiga.k-img.com/images/movie/56683/315x210.jpg?1709", 105, "わずか20人足らずの人口に対して500頭の肉牛が飼育されている瀬戸内海の小豊島を舞台に繰り広げられるサスペンスコメディ。主演をNHK連続テレビ小説「ウェルカメ」の倉科カナが務める。牛の研究ばかりで家族を省みない父・吾郎を嫌っていた花子だったが、ある日、吾郎が5年に一度の肉牛大会で大賞を受賞する。一方、日本品種のイチゴの遺伝子を盗んで儲けた韓国マフィアのリュは、次の狙いをブランド和牛に定め、何も知らない韓国人親子をだまして香川に送り込むが……。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い夕陽の渡り鳥", "赤い夕陽の渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "ギターを背に“渡り鳥”滝伸次が会津磐梯山の麓に現れ、牧場主を助けて、牧場乗っ取りをたくらむ悪玉一味を叩きつぶす。宍戸錠がライバル“ハジキの政”として登場し、大坂志郎が珍しく悪玉に扮する。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・へそくり社長", "続・へそくり社長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "社員慰労会の席で大株主・赤倉による社員更迭の策動の情報を耳にした社長。部下の小森をスパイに仕立てたが、社長も大株主も小唄師匠・小鈴の共通のオトモダチと分かり、奥様たちも絡んでてんやわんや……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版 TIGER & BUNNY The Beginning", "劇場版 TIGER & BUNNY The Beginning", "", "", "http://image.eiga.k-img.com/images/movie/57823/315x210.jpg?2149", 0, "", "2012-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("麗人", "麗人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "原節子と藤田進の名コンビによる戦後第１作のメロドラマ。没落華族の娘・圭子は、資本家と政略結婚させられるが、やがて彼の非道な経営方針に気づき、家を飛び出して労働運動家との恋とともに自由に目覚めていく。まさに原節子のために企画されたような、可憐なヒロイン像がはかなくも美しい作品だ。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LORNA DOONE", "激情の断崖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "父をドーン家の人間に殺された、少年ジョンは復讐を果たすべく、一人で城に乗り込むがケガを負ってしまう。城主の孫娘ローフの看護を受けるが、そこで生涯のライバルとなる彼女のいとこ、カーバーと出会い格闘となってしまう……。R・D・ブラックモア作のイギリス古典文学の映画化。", "1951-01-01", "1", 0, NOW(), NOW());
