INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tazza: The High Rollers", "タチャ　イカサマ師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "賭博の達人“タチャ”たちの華麗なる世界を、「マラソン」などの演技派俳優チョ・スンウ主演で描いたクライムアクション。小さな工場で働く青年ゴニは、3年間こつこつと貯めてきたお金を花札賭博で一瞬にして失ってしまう。その勝負がイカサマだったと知ったゴニは、復讐を果たすため伝説のタチャ、ピョンに弟子入りするが……。デビュー作「ビッグ・スウィンドル！」で注目を浴びたチェ・ドンフンの監督第2作。", "2008-08-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガクドリ", "ガクドリ", "", "", "http://image.eiga.k-img.com/images/movie/55933/315x210.jpg?2982", 89, "学生限定のドリフト大会・全日本学生ドリフト王座決定戦＝通称“学ドリ”をテーマに描く青春ドラマ。一浪の末、赤城山大学に合格したマサキは自動車教習所に通い始め、美人教官の菜々子に一目ぼれする。菜々子が赤城山大学の卒業生で、自動車部OGとして今でも部に顔を出すことを知ったマサキは、さっそく自動車部へ入部。ドリフトどころか自動車のことすらろくに知らなかったマサキだが、先輩や幼なじみと切磋琢磨し、次第に頭角を現していく。", "2011-04-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LEAGUE　OF　GENTLEMEN", "紳士同盟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "軍をリストラされ、退屈な日々を過ごす男８名が、社会への復讐として銀行強盗を計画。やがて彼らは軍の弾薬庫から武器を盗み、にわか仕込みの訓練を経て、目的の銀行へと向かうのだが……。イギリス映画界の名優が勢ぞろいした、イキでスタイリッシュなサスペンス映画。サン・セバスチャン国際映画祭最優秀男優賞受賞。", "1960-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NORTH　STAR", "フリーズ　地獄の相続人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "アラスカの町ノームを牛耳る悪徳鉱業家マクレノンが、鉱脈が眠る洞窟を手に入れるため、所有者ハドソンを殺害しようとする。何とか危機を逃れたハドソンは、執拗に追跡してくるマクレノン一味を相手に、最後の闘いを挑むのだった。C・ランバートがインディアンの血を引く勇者に扮した冒険アクション。J・カーンのふてぶてしい悪役ぶりと、極北の地にロケを敢行した雄大な自然美も見もの。", "1995-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超高層ホテル殺人事件", "超高層ホテル殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "副都心として大躍進を始めた頃の、新宿の超高層ビル群を舞台に、巨大なホテルの経営権をめぐって起こる連続殺人事件と、事件にまつわる人々の人間模様を描いたサスペンス・ミステリー。森村誠一の同名小説が原作で、彼の作品の最初の映画化。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・爬虫類", "喜劇・爬虫類", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "渥美清を筆頭におなじみ喜劇陣が顔をそろえたコメディ。金髪美人のヌードで稼ぎまくっているドサ回り劇団。ある日、警察の風紀係に一同御用となる。だが転んでもただでは起きない男たちのこと……。伴淳三郎、上田吉二郎らが脇を固めている。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Viva! Love", "ビバ！ラブ", "", "", "http://image.eiga.k-img.com/images/movie/55243/315x210.jpg?3717", 100, "カラオケ屋と下宿を営む50歳のボンスンは、「女」としての自分は捨て、「妻」として「母」として、夫と娘と平凡に暮らしていた。そんなある日、下宿人のクサンと婚約していた娘が、突然家出をしてしまう。ボンスンは、ヤケになり泥酔していたクサンを介抱しているうちに、彼と一夜を共にしてしまう……。2010年の「真！韓国映画祭」にて上映。", "2010-02-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CURFEW", "カーフュー ～戦慄の脱獄囚～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "冷静なタッチで犯罪者の異常心理を描くバイオレンス。死刑囚二人組が脱獄。自分たちを有罪にした精神鑑定医や裁判官を次々惨殺し、最後に担当検事宅で一家全員を監禁、なぶりはじめる。そこへちょうど帰ってきた娘のステファニーは、両親を助けるため、殺人鬼に死闘を挑むが……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COP", "ザ・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "J・ウッズが主演・製作を兼ねた作品。ロス郊外で起きた殺人事件を追うホプキンス刑事は、それが15年にわたる連続殺人事件と関連しているとにらむ。だが捜査中に参考人を誤って射殺して停職処分に。それでも彼はたった一人で犯人を追い詰めていく。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪魔を見た", "悪魔を見た", "", "", "http://image.eiga.k-img.com/images/movie/55873/315x210.jpg?4331", 144, "イ・ビョンホンと「オールド・ボーイ」などで知られる実力派チェ・ミンシクが共演するクライム・サスペンス。残忍な連続殺人犯ギョンチョル（ミンシク）に婚約者を惨殺された国家情報院捜査官スヒョン（ビョンホン）。復しゅうの鬼と化したスヒョンは、犯人に婚約者と同じ苦しみを与えるべく、執ようなまでに追いつめていく。「甘い生活」「グッド・バッド・ウィアード」でもビョンホンと組んだキム・ジウン監督がメガホンをとる。", "2011-02-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gordos", "デブたち", "", "", "http://image.eiga.k-img.com/images/movie/55133/315x210.jpg?1242", 120, "肥満者向けのセラピーに参加する人々の、5つのストーリーが交錯するコメディ。さまざまな人々が参加するグループセラピーでは、肥満となった理由を多角的に捉え、あらゆる問題提起と解決に結び付けていく。監督は「蒼ざめた官能」で第21回ゴヤ賞新人監督賞を受賞したダニエル・サンチェス・アレバロ。「スペイン映画祭2009」にて上映。", "2009-12-08", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YAWARA！", "YAWARA！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "『ビッグコミック・スピリッツ』に連載された人気マンガを、市川崑監督などのもとで助監督を務めていた吉田一夫が映像化。高校３年生の猪熊柔は、天才的な柔道の資質をもっていたが、フツーの女の子の青春をエンジョイしたいと思っている。しかし祖父の滋悟郎はなにがなんでも、柔にオリンピックで金メダルをとらせるつもりでいた。浅香唯が恋と柔道に青春を賭ける主人公をさわやかに演じている。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIVE EASY PIECES", "ファイブ・イージー・ピーセス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "若者のドロップアウトを描くことの多かったアメリカン・ニューシネマで、中年男の生き方をテーマにした異色作。カリフォルニア南部の石油採掘現場で働く中年男ボビーは、良家の御曹司だが、自堕落な生活が身についてしまっていた。ある日、同棲中のガールフレンドが妊娠したことを知り、これをきっかけにまともな生活に戻ろうとして、実家に戻ってくるが……。題名の「ファイブ・イージー・ピーセス」とは、簡単にピアノで弾ける５つの曲のことで、ピアノを習っていたボビーが、それも弾けないほどに普通の生活から離れてしまっていたという意味。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談", "怪談", "", "", "http://image.eiga.k-img.com/images/movie/34063/315x210.jpg?1397", 119, "「ザ・リング2」でハリウッド・デビューを果たした中田秀夫監督が、落語家・三遊亭円朝の怪談噺「真景累ケ淵」を映画化。江戸の町で出会った煙草売りの新吉と、三味線の師匠・豊志賀。2人は激しい恋に落ちるが、次第に心がすれ違っていく。ある時、言い争いの末に誤って顔に深い傷を負ってしまった豊志賀は、「この後女房を持てば、必ずやとり殺す」と書き残してこの世を去る。そして、残された新吉と彼を取り巻く女たちの運命は、想像もつかない方向へ……。", "2007-08-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARKED FOR DEATH", "死の標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "親友を失った責任から引退を決心した米連邦麻薬取締局（DEA）の腕利き捜査官。だが故郷のシカゴも麻薬汚染にむしばまれており、彼は組織のボスと対決するためジャマイカへ飛ぶ。剣道、太極拳、カンフーとあらゆる武道をこなすセガールが体当たりのアクションに挑む痛快作。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("寛美の三等社員", "寛美の三等社員", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "徳島の大興行主の息子・鳴門太郎は５歳で母と生き別れ、12歳で父と死別。今では大阪の紡績会社に勤める身だ。会社は業務拡張を見込み、ミナミのキャバレー“ムーラン・ルージュ”の買収をめぐってライバルと争奪戦を繰り広げていた。敵方はキャバレーの経営者・信子の娘と自社顧問の息子との政略結婚をはかり、取り引きを有利に進める。ところが信子と太郎が実の母子だと発覚し……。当時、松竹新喜劇のプリンスとして、舞台やTVを席巻した寛美主演のサラリーマンもの。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("食堂かたつむり", "食堂かたつむり", "", "", "http://image.eiga.k-img.com/images/movie/54663/315x210.jpg?4495", 119, "失恋のショックから心因性失声症になってしまった倫子は、自由奔放な母の暮らす田舎へ戻り、「食堂かたつむり」を開くことにする。決まったメニューはなく、1日1組のお客のためにイメージを膨らませて料理を作るうち、倫子は徐々に元気を取り戻していく。やがて、倫子の食堂で食事をすると願いがかなうという噂が町中に広まっていき……。「ウール100％」の富永まいが、小川糸のベストセラー小説を映画化。主演は柴咲コウ、共演に余貴美子、ブラザートム、志田未来ほか。", "2010-02-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SKI PARTY", "スキー・パーティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "スキー場からカリフォルニアのビーチまでを舞台にした、二人のスポーツ万能学生のガールハント大作戦。主演は人気歌手として一時代を築いたF・アヴァロン。ゲストスターとして“キング・オブ・ソウル”ことジェームズ・ブラウンと、当時人気を誇っていたレスリー・ゴーアが顔を見せる。お気楽な青春娯楽映画。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brotherhood", "ブラザーフッド", "", "", "http://image.eiga.k-img.com/images/movie/1313/315x210.jpg?1939", 148, "「シュリ」のカン・ジェギュ監督が4年ぶりにメガホンを取った野心作。公開59日目にして「シルミド」を破り1200万人の動員新記録を樹立した大ヒット映画。1950年に勃発した朝鮮戦争。強制的に徴兵されたと弟ジンソク（ウォンビン）を守るため兄ジンテ（チャン・ドンゴン）もまた兵役を志願する。凄まじい戦闘の中、危険な任務に身を投じていく兄と、その姿に戸惑うジンソク。2人の溝が決定的になった時、さらなる悲劇が彼らを待ちかまえるのだった。", "2004-06-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chopin. Pragnienie Milosci", "ショパン　愛と哀しみの旋律", "", "", "http://image.eiga.k-img.com/images/movie/55833/315x210.jpg?2624", 126, "ポーランドの天才作曲家フレデリック・ショパンの激動の半生と、女流作家ジョルジュ・サンドとの愛を描いた伝記映画。若き作曲家ショパンは、帝政ロシアの圧制にあえぐ祖国ポーランドを離れ、仏パリへとたどり着く。なかなか才能が認められず挫折を味わっていたが、人気ピアニストのフランツ・リストによる計らいで、ついに念願のパリ社交界デビューを果たす。そんなある日、ショパンは恋多き女として知られる作家ジョルジュ・サンドと出会い、たちまち恋に落ちる。", "2011-03-05", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Quarantine", "REC レック　ザ・クアランティン ", "", "", "http://image.eiga.k-img.com/images/movie/54693/315x210.jpg?3891", 89, "ある夜、TV局レポーターのアンジェラはロサンゼルス消防士の密着取材のため、彼らのレスキューに同行し、老朽化した小さなアパートを訪れる。そこで何かに感染したために狂人となった老婆が警官に襲い掛かり、アンジェラたちは警官を救出するも、アパートは警察により封鎖され脱出不可能となっていた。TVクルーが撮影したビデオテープだけに、その夜の凄惨な映像が克明に記録されていた。主演は「エミリー・ローズ」のジェニファー・カーペンター。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("望郷子守唄", "望郷子守唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "昭和初期、北九州・小倉の暴れ者がなぜか東京の近衛連隊に入隊、そして除隊後に巻き起こす笑いとアクションの数々を描いていく。高倉健の豪快な暴れん坊ぶりが見ものの一つ。また悪党一味に騙されたり、ドジを踏んだり、母親に頭が上がらなかったりの彼の純情ぶりがたまらない。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　BOY　AND　HIS　DOG", "少年と犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "同名小説を原作にした、カルトにしてバイオレンスなSFアドベンチャー。サム・ペキンパー監督の「ワイルドバンチ」などに出演し、名バイプレーヤーとして知られる俳優L・Q・ジョーンズの初監督作品。核戦争で荒廃した2024年の地球を舞台に、超能力犬とその飼い主の少年が、少女らと関係しながら活躍していく冒険ストーリー。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PETE KELLY’S BLUES", "皆殺しのトランペット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "トランペッターの孤独な闘いを描いた硬派アクション。酒法時代のカンザスシティー。闇酒場を経営し、ジャズ楽団のマネージメントをしているフラン・マッカーグは、悪どい商売のやり方から、楽団員たちみんなに嫌われていた。しかし、彼に逆らえば墓場送り。コルネット吹きのピート・ケリーは、マッカーグに対し、一人戦いを挑む……。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HUSBANDS AND WIVES", "夫たち、妻たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "二組の夫婦の葛藤を描きながら愛の本質を見つめた、W・アレンの監督第22作。子供を持たないゲイブとジュディのロス夫婦は結婚10年目。ある日、彼らは親友のジャックとサリー夫婦に離婚を打ち明けられる。この事件に動揺したロス夫婦は、自らの愛情問題を見直すことに。やがて、それぞれの前に魅力的な異性が現れ……。登場人物への架空のインタビューや、激しく動き回るカメラの長回しなど、タッチはドキュメンタリー風。即興風の演出が、リアルなムードをかもし出している。長年の恋人関係に終止符を打ったアレンとM・ファローが、皮肉にもロス夫婦に扮している。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Kite Runner", "君のためなら千回でも", "", "", "http://image.eiga.k-img.com/images/movie/53153/315x210.jpg?1982", 129, "1970年代のアフガニスタン。兄弟のように育ったアミールと召使いの子ハッサンだが、街中が沸き立つ凧揚げトーナメントの日、アミールはハッサンに対してある“裏切り”を働いてしまう。やがてソ連がアフガンに侵攻を開始、アミールは心にしこりを残したまま、父と共にアメリカに亡命する。「チョコレート」「ネバーランド」のマーク・フォースター監督が、アフガン出身の作家カーレド・ホッセイニの原作小説を映画化したヒューマンドラマ。", "2008-02-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Durval Discos", "ドゥルヴァル・レコード", "", "", "http://image.eiga.k-img.com/images/movie/54783/315x210.jpg?4328", 97, "ひょんなことから事件に巻き込まれてしまった青年とその母親の姿をユーモアたっぷりに描いたコメディ。サンパウロで小さなレコード店を細々と営むドゥルヴァルは、年老いた母親と2人で暮らしながら冴えない毎日を送っていた。そんなある日、彼が雇った家政婦が、5歳の娘キキを残して姿を消してしまう。やがてドゥルヴァルと母親は、テレビのニュース番組でキキが警察に追われていることを知り……。", "2009-10-05", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("汚れた英雄", "汚れた英雄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "それまでプロデューサーだった角川春樹の監督第１回作品。原作はかなりの長編で内容的にもアクの強いものだったが、これをジゴロものに仕立て、全編を実にファッショナブルな感覚で映像化した。また日本映画始まって以来の本格的オートバイ・レース映画でもある。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUBY", "ジャック・ルビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "現職のアメリカ大統領に公式ステートメントを発表させたオリヴァー・ストーン監督の「JFK」と同じくケネディ暗殺をとり上げた本作品は、暗殺犯オズワルドを殺害したジャック・ルビーの生涯を描いている。D・アイエロが主役のルビーを熱演。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お祭り野郎 魚河岸の兄弟分", "お祭り野郎 魚河岸の兄弟分", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "東京の下町を舞台に魚河岸で働く気っぷの良い男・梶木勝男の活躍を描く人情喜劇。お祭り狂いの勝男が、不治の病に冒された娘・久美に、祭のミコシを担せて、最後の楽しい思い出を作ってやるという内容。プログラム・ピクチャーの雄・鈴木則文の作品で、楽しく見られるドラマ映画。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAR TREK III：THE SEARCH FOR SPOCK", "スター・トレック３ ミスター・スポックを探せ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "シリーズ第３作。今回の見どころは、カーク提督たちと強大な軍事力を誇るクリンゴン帝国の武将クルーグとの一騎打ち。さらに、前作で犠牲となったスポックの生命反応が再び動き出すという法外な展開も見もの。ニモイの初監督作品として興味あふれる一編。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOOR", "DOOR", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "セールスマンを冷たく追い返してしまった美しい人妻が、その男に執拗なまでにつきまとわれ、やがて恐ろしい一夜を体験するというサスペンス・スリラー。撮影中は“別居”して仕事に打ち込んだという監督・高橋伴明、主演・高橋恵子夫妻による力作。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHISUM", "チザム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "開拓期のニューメキシコ。広大な牧畜王国を築いたチザムという男がいた。町の実力者マーフィは、縄張り拡大のためチザムの牧場を手に入れようと数々の悪らつな手段を試みる。二人の対決は、やがて町全体を巻き込む大死闘へと発展する……。脚本も演出もすべてが定石通りといえる、西部劇の教科書的作品。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　レイトン教授と永遠の歌姫", "映画　レイトン教授と永遠の歌姫", "", "", "http://image.eiga.k-img.com/images/movie/54263/315x210.jpg?4738", 100, "携帯ゲーム機・ニンテンドーDSのゲームソフトとして販売され、シリーズ累計730万本を突破した人気推理ゲームの映画化。声優に大泉洋、堀北真希、相武紗季、渡部篤郎ら豪華キャストが揃う。ある日、レイトンのもとに教え子のオペラ歌手・カトレーンからオペラのチケットが同封された手紙が届く。「1年前に亡くなった友人のミリーナが、『永遠の命を手に入れた』と言って現れた」という相談を受けたレイトンは、助手のルークとレミとともに、事件の謎を解くためオペラ劇場へと向かう。", "2009-12-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("峠を渡る若い風", "峠を渡る若い風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "鈴木清順＝和田浩治コンビの青春映画。放浪学生と旅芸人の一座の交流を描く。作品中、主人公の白いシャツに氷水のシロップがかけられると、シャツの色がイチゴ色やメロン色に照明で変わるというシーンがあり、のちの清順美学の片鱗が見られる。人情喜劇風の生活感とパロディ・アクション風の軽みがミックスした珍品。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("That Certain Thing", "呑気な商売", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "後の巨匠フランク・キャプラ監督によるコメディ・ドラマ。ホテルでの仕事で得たわずかな収入で家族を養うモリーは、玉の輿を夢見ていた。ある日彼女は、レストラン経営者の息子アンディと出会う。2人はたちまち恋に落ち、その日のうちに結婚。ようやく夢がかなったかに思えたモリーだったが、アンディの父親は2人の結婚に猛反対し、彼への仕送りをすべて止めてしまう。アンディは愛するモリーとともに弁当会社を始めるが……。", "1928-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("壁男", "壁男", "", "", "http://image.eiga.k-img.com/images/movie/34193/315x210.jpg?1549", 98, "カルト的人気を誇る漫画家・諸星大二郎の短編を映画化した異色ホラー。主演はTVドラマや舞台などで活躍する堺雅人と小野真弓。TVの情報バラエティ番組でレポーターを務める響子の元に届いた匿名の手紙。それは、壁の中からいつもこちら側を見ているという“壁男”に関する投書だった。番組での紹介をきっかけに“壁男”の噂は日本中に広まっていく。響子の恋人のカメラマン仁科は“壁男”に異常なまでの興味を示すようになり……。", "2007-09-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三里塚 辺田部落", "三里塚 辺田部落", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "「1000年刻みの日時計・牧野村物語」の小川紳介が、空港建設に揺れる成田・三里塚に焦点を当てて撮り続けてきた“三里塚”シリーズの第６作。空港公団による農民たちの切り崩しが進むなか、最後まで固く結束を守ってきた辺田部落の人々の姿を記録したもの。この部落からもついに警察に連行されていくメンバーが出て、村は苦しい雰囲気に包まれるが……。いわゆる闘争の場面より村人たちの日常の記録に重点を置いた作品で、スタッフたちがこの部落に腰を落ち着けて撮り続けるという方法論が、見事に結実したものとして、多大な評価を得た。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("借王２〈シャッキング〉", "借王２〈シャッキング〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "抜群のチームワークを誇る３人組の詐欺計画が展開する、マネーゲーム活劇の第２作。借金の返済を迫られた銀行員と中年刑事、クラブのママが再集結。10億円の強奪を計画した彼らが、金満政治家と、その背後に控える暴力団に頭脳プレーで闘いを挑む。ベテラン、和泉聖治と「不法滞在」の香月秀之が共同で演出に当たっている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN LOVE AND WAR", "ラブ・アンド・ウォー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "文豪ヘミングウェイの若き日の秘められた恋を、彼の友人らが記したリサーチ本に基づいて映画化。1918年、第一次大戦末期のイタリア。小説家志望のアメリカ人新聞記者アーネストは、傷病兵運搬車の運転手として同地に渡っていた。爆撃を受けた彼は、赤十字病院へ。そこで美しい看護婦アグネスと出会い、恋に落ちる。心と体が結ばれながらも、それぞれの事情で離れ離れになった二人は、文通を続けていたが……。この作家の気難しい性癖は、この悲恋に起因すると思わせるラストだが、さて真偽のほどは？", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIMAL RAGE", "ザンゴリラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ウイルス感染によって、野獣へと変貌していく人間の恐怖を描いたモンスター・ホラー。フロリダ大学の新聞部で活躍するサムとダフィは、ある教授の秘密の実験を探ろうとするのだが、ダフィはそこで猿に腕を噛まれてしまう。それが恐るべき変身のはじまりだった。やがてキャンパスは血に染まっていく。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SILENT TOUCH", "巨人と青年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "隠遁生活を送る頑固な老作曲家と、彼の創作意欲に再び火をつけようと奔走する音大生の交流をじっくりと描いた人間ドラマ。マックス・V・シドーが大作曲家に扮し、貫禄十分の演技を披露。第５回東京国際映画祭で主演男優賞に輝いた。オーケストラが流麗な交響曲を奏でるクライマックスは圧巻。", "1992-01-01", "3,42,18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wanna be FREE！ 東京ガール", "Wanna be FREE！ 東京ガール", "", "", "http://image.eiga.k-img.com/images/movie/33603/315x210.jpg?2450", 91, "カリスマモデルの上原奈美が映画初主演を果たした青春ドラマ。携帯恋愛ストーリーサイト「100シーンの恋」で配信され話題となった作品を映画化。携帯ブログがきっかけで、人気ファッション誌の読者モデルとして活躍することになった女子高生リン。華やかな世界の裏側に広がるモデルたちの複雑な人間関係に振り回されながらも、少しずつ大人の女性になっていくリンの姿を描く。桜井裕美、臼田あさ美ほか人気モデルが多数出演。", "2006-08-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WISHING　STAIRS", "狐怪談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「女校怪談」「少女たちの遺言」に続く韓国の人気ホラー・シリーズ“女校怪談”の第３作。舞台となるのは、狐の霊が宿っているといわれる階段がある寄宿学校。バレエ部に所属する美少女たちの屈折した思いが、血塗られた惨劇を引き起こしていく様を描く。前２作同様オーディションで選ばれた新人女優たちが、10代の女の子の不安定な感情を初々しく表現している。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("追日／A CHINESE LEGEND", "チャイニーズ・レジェンド 魔界英雄伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "清楚なジョイ・ウォンと肉体派といわれるチョン・マンという香港映画を代表する女優が共演するSFX映画。悪魔王の世界征服を夢で見た剣客イーロンは、師匠の指示に従い旅に出る。途中、美しい娘ムンシュと出会い恋に落ちるが、彼女は悪魔王の婚約者であった。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("破戒", "破戒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "島崎藤村の同名の原作の映画化はすでに木下惠介の手によるものがある。こちらは市川崑の演出によるもので、厳しいリアリズムに貫かれた作品に仕上がっている。丑松に市川雷蔵を、猪子蓮太郎に三國連太郎を配し、お志保にはこれがデビュー作で、原作者の藤村と役名から名付けられた藤村志保が起用されている。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cheburashka", "チェブラーシカ（2010）", "", "", "http://image.eiga.k-img.com/images/movie/55173/315x210.jpg?4140", 63, "パペットアニメの巨匠ロマン・カチャーノフが生み出したロシアの国民的アニメ「チェブラーシカ」。日本でも大人気の同作品の27年ぶりとなる新作が、ロシアのオリジナル版スタッフの協力を得て、日本を中心に製作された。旧作をリメイクした第1話、サーカスにあこがれる少女マーシャとチェブラーシカの交流を描いた第2話と第3話から構成される。チェブラーシカの声を演じるのは人気子役の大橋のぞみ。", "2010-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUCHEZ PAS AU GRISBI", "現金〈ゲンナマ〉に手を出すな", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "初老のギャング、マックスとリトンの二人は、最後の仕事としてオルリー空港から５千万フランの金塊を盗み出す。仕事はうまく成功したが、リトンがクラブの女ジョジに話してしまったため、ジョジの情夫で麻薬密売人アンジェロの知るところとなる。アンジェロはリトンを拉致して金塊と交換条件を出してくる。マックスはリトンとの友情から交換条件をのまざるをえない。だが、取引の場でのアンジェロの卑劣な仕打ちを知ったマックスは怒り、彼のマシンガンが火を吹く……。J・ギャバンが初老のギャングを演じる哀愁の漂うギャング映画の秀作。主題曲の『グリスビーのブルース』もヒットした。", "1954-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("РАБА　ЛЮБВИ／A　SLAVE　OF　LOVE", "愛の奴隷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1918年のロシア革命混乱期。政治に無関心だった女優オリガが、『愛の奴隷』という映画の撮影中にカメラマンのポトツキーと親しくなる。共産党員である彼に教えられてオリガの心は変化を見せる。しかし、革命の波はロケ地である黒海沿岸の町まで押し寄せ、ポトツキーは憲兵隊に射殺され、オリガも追われる。ミハルコフの出世作。", "1976-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("零〈ゼロ〉", "零〈ゼロ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "太平洋戦争末期、日本軍の最終手段“玉砕戦法”といわれる“神風特別特攻隊”。片道の燃料だけを積んだ航空機に若きパイロットが乗り込み、敵に体当たりし、自らの命ともども砕け散る。“飛行機の上では死なない”という持論を持つ龍太郎をはじめ、明日を夢見ることを許されない若者たちが、極限の精神状態のなかでもなお友情や恋愛をはぐくむ様が切なくも勇ましい。太平洋戦争終戦間際、口は悪いが根は優しいエースパイロット、長谷川龍太郎上飛曹たちの前に、新しく若き隊長、久我少尉が就任した。ウマの合わない龍太郎と久我が衝突を繰り返す中、夜間突撃部隊としての編成命令が下される。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STOLEN AIRSHIP", "盗まれた飛行船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "トリック映画の巨匠ゼマン監督が描くファンタスティックな世界。19世紀末、ある都会で、少年たちが飛行船に乗って旅立ってしまう。彼らの冒険旅行の始まりだ。ヴェルヌ小説でおなじみのネモ船長も登場。実写と書割りを組み合わせ、淡い色彩の人工着色と精密な映像は、温かさと遊び心を感じさせて秀逸。", "1966-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あぶない刑事リターンズ", "あぶない刑事リターンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1986～87年に放映され人気を呼び、その後映画化され３本の作品が作られた“あぶない刑事”が７年ぶりに登場。出演陣もほとんど前回と同じであり、監督も第３作を手掛けた村川透が担当している。横浜港署の刑事、タカとユージの目の前で雑居ビルが爆破される。犯人としてユージが５年前に逮捕した爆弾マニアの唐木が浮かび上がる。唐木の部屋を捜索した結果、その日の３時にホテルで開かれている銀行協会の頭取会議を襲うことが判明。タカとユージたちはホテルに急行し、何とか頭取たちの命は守れたが、10億円と新人刑事のトラまでが連れ去られる。そして彼らは旧ソ連製のミサイルを所有していることも分かる。犯人たちの目的は何か分からぬまま、タカとユージの追跡が始まるのだった。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("電信柱エレミの恋", "電信柱エレミの恋", "", "", "http://image.eiga.k-img.com/images/movie/54923/315x210.jpg?4531", 0, "映像作家・中田秀人率いる映像制作集団「ソバットシアター」によるストップモーション・アニメ。満月の夜に故障した電信柱のエレミは、電力会社から派遣された作業員タカハシに修理してもらう。するとどうしてもタカハシと話してみたくなり、エレミは人間のフリをして彼に電話をかけてしまう。毎日電話するうちに心を通わせていくエレミとタカハシだったが、そんなエレミの行為が電信柱の世界で問題となってしまい……。", "2009-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMMANUELLE ６", "Emmanuelle６ カリブの熱い夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "記憶喪失となって南米ベネズエラから帰ってきたエマニエル。彼女は失われた記憶を取り戻すために心理療法を受けるが、やがて奴隷市場で売られかけた恐怖体験など、官能的なセックスの数々の記憶がよみがえってくる。モデル出身の新星N・エールが４代目エマニエルを演じている。ビデオ・DVDタイトルは「エマニエル／カリブの熱い夜」。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TALES FROM THE DARKSIDE：THE MOVIE", "フロム・ザ・ダーク・サイド ザ・ムービー ３つの闇の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ドイル原作によるミイラ再生譚、キング原作、ロメロ脚本の殺し屋と殺人猫との壮絶な戦い、怪物に魂を売り渡した貧しい画家の物語……。TVの人気シリーズを劇場用に製作したオムニバス・ホラーで、人喰い女に捕まった少年が物語を語っていくという繋ぎも面白い。アヴォリアッツ国際ファンタスティック映画祭でグランプリを受賞した。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICA AMERICA", "アメリカ アメリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 168, "E・カザンが、移民として苦闘した叔父をモデルに描く叙事詩的大作。19世紀末のトルコで、自由の国アメリカを夢みて働き続ける貧しいギリシャ青年スタブロス。彼は盗人に金を奪われ、意に沿わぬ結婚を強要され、革命運動に巻き込まれて重傷を負ってしまう。それでも、憧れの新大陸を目指す彼を、いつしか周りの者は“アメリカ・アメリカ”と呼ぶ……。カザンが幼い頃から聞き親しんだといわれる“身内の武勇伝”だが、単にそれに止まらない重厚な大河ドラマの風格がある作品。なお1969年には本作の後日譚となる「アレンジメント／愛の旋律」も製作されている。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙大怪獣ドゴラ", "宇宙大怪獣ドゴラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "宇宙から飛来したナゾのクラゲ状半透明の怪物・ドゴラが地球を襲った。地上の電気を吸い上げエネルギーとするこの怪物のために、都市や海上の船などは壊滅状態となる。半透明の怪獣のため、従来のぬいぐるみでは表現できない特撮がユニーク。特技監督は円谷英二。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Losing Isaiah", "代理人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "愛する我が子の親権をめぐる法廷サスペンス。ゴミ捨て場に置き去りにされ、母を麻薬中毒で逮捕されてしまった黒人の赤ん坊・イザヤ。彼が運ばれた病院で看護にあたったマーガレットは、健気に生きるイザヤの姿に胸を打たれ養子に迎える。裕福な家庭で愛情いっぱいに育つイザヤ。しかし更生した母親のカイラがイザヤの消息をつかみ、親権奪回の訴訟を起こした。満ち足りた環境を訴えるマーガレットと、黒人が白人家庭で育つのは不適当だとするカイラの主張は、法廷で真っ向から対立する。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パイルドライバー", "パイルドライバー", "", "", "http://image.eiga.k-img.com/images/movie/33813/315x210.jpg?4790", 99, "ひょんなことからヤクザ養成学校に足を踏み入れてしまった青年の姿を、ミュージカルシーンやアニメーションを散りばめながら描く青春コメディ。何をやってもダメな青年ソーイチは、自分を変えるため“人生のスペシャリスト養成学校”を受験する。しかしその実態はヤクザの幹部養成学校。彼は押し売りやドスの使い方など様々な難問を課せられるが、ある日居座りを命じられた差し押さえ物件が、街でひと目惚れした少女の家だと知り……。", "2007-01-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARIS JAMAIS VU", "パリの空の詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「赤い風船」や「素晴らしい風船旅行」でメルヘン・タッチの素晴らしい映像を見せてくれたラモリスがパリの街を空から捉えた短編。美しいパリの名所をヘリビジョンを駆使して撮影。地上から見るのとは全く違った角度で造形美を映し、まことに魅力的だ。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("世界で一番美しい夜", "世界で一番美しい夜", "", "", "http://image.eiga.k-img.com/images/movie/53563/315x210.jpg?4754", 160, "日本の西のはずれにある“要村”が、日本一の出生率を誇る村として政府から表彰されることになった。その発端は今から14年前、新聞記者の水野一八がこの村に左遷してきたことから始まる。彼は夫を殺害したと噂されるスナックの美人ママ・輝子から不思議な話を聞かされ……。「暗いところで待ち合わせ」の天願大介監督による奇想天外な群像ドラマ。主演に田口トモロヲ、共演に本作が映画初出演の元宝塚女優・月船さららほか。", "2008-05-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Aviator", "アビエイター", "", "", "http://image.eiga.k-img.com/images/movie/1013/315x210.jpg?3922", 169, "「ギャング・オブ・ニューヨーク」の監督＆主演コンビ、マーティン・スコセッシとレオナルド・ディカプリオが、実在の富豪ハワード・ヒューズの半生を映画化。ヒューズの最速の飛行機と映画製作に賭けた情熱を描く。本年度アカデミー賞に最多11部門でノミネート、ケイト・ブランシェットの助演女優賞、撮影賞、編集賞、美術賞、衣装賞の5部門を受賞。作品賞、監督賞、レオナルド・ディカプリオの主演男優賞は惜しくも受賞ならなかった。", "2005-03-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血染の代紋", "血染の代紋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "傑作“仁義なき戦い”シリーズの名コンビ深作欣二監督と、菅原文太が初めて顔を合わせた記念すべき作品。横浜のスラム街の立退きをめぐる暴力団同士の抗争に、最後まで抗し抜いた新組長の血染めの抵抗が描かれる。東映実録路線の先駆的な作品。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST TEMPTATION OF CHRIST", "最後の誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 163, "神の子イエスではない“人間イエス”の、愛と欲情に悩む姿を赤裸々に描き出し、世界中に物議をかもした超問題ヒット作。紀元前一世紀のパレスチナ。神からの啓示を待ちわびるナザレのイエスは、旧友ユダの密告でローマ軍に捕らえられ、ゴルゴダの丘で十字架にはりつけられるが……。瀕死のイエスが見た生々しい幻覚とは？　“最後の誘惑”を映すラスト40分が圧倒的。聖書のイメージとは一味違うイエスを、W・デフォーがストイックにかつパッショネイトに熱演し、イエスに対する斬新で過激な新解釈に挑戦。見ごたえのある“人間”ドラマに仕上げたスコセッシの才気はさすがである。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("櫻の園", "櫻の園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "原作は、四季の移ろいに合わせて女子高演劇部の人間関係を描いた吉田秋生のマンガ。映画ではこれを、チェーホフの『桜の園』の舞台開演2時間前から幕が開くまでに限定し、原作の味わいを凝縮した。オーディションで選ばれた演劇部員役の22人を、見事なまでにさばいた中原俊の演出、春の陽光に少女の輝きを合わせて映し込んだカメラも光る。満開の桜に包まれた私立櫻華学園。創立記念日恒例の『桜の園』の上演を控えたある日、しっかり者の演劇部部長、由布子はいきなりパーマをかけてくる。主役の倉持知世子は、初の女役にとまどいを隠せない。部員の紀子は、前日タバコを吸って補導され、芝居の上演を危うくした。最後のリハーサルに余念のない稽古場で、3人の心が微妙に触れ合って……。由布子と知世子が舞台衣装を着て記念撮影をする場面で、陰から見つめる紀子のさびしげな笑み。少女たちの憧れが絡み合う名シーンとなっている。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トビラを開くのは誰？", "トビラを開くのは誰？", "", "", "http://image.eiga.k-img.com/images/movie/57433/315x210.jpg?2862", 15, "無名の若手監督たちが作り上げた15分の短編10作品を特集上映する「NO NAME FILMS」の1本。母と病院へ向かうバスに乗る10歳の少年・光は、車内で退屈しのぎに風船で遊んでいると不思議な体験をする。やがて病院についた光が目にしたものは……。監督は、大阪芸術大学在学中に山下敦弘監督作「ばかのハコ船」にスタッフとして参加するなどし、初長編監督作「−×− マイナス・カケル・マイナス」（2010）がローマ国際映画祭に出品された伊月肇。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌くらべ荒神山", "歌くらべ荒神山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "有名な清水次郎長の荒神山の決戦を舞台に、ヤクザ渡世に憧れて子分になった元左官職人・三太が、ヤクザの世界の強欲さをかいま見て盃を返すまでを描いた喜劇調時代劇。監督は“フーテンの寅”の名前の由来になったことでも有名な喜劇王・斎藤寅次郎。三太に田端義夫、吉良の仁吉に高田浩吉が扮している。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VENEXIANA", "薔薇の貴婦人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "16世紀の舞台劇の映画化。16世紀のベネチアに外国人の旅人がやって来る。このハンサムな若者をめぐって未亡人の貴婦人と若い人妻が繰り広げる恋の駆け引き。ボロニーニのエロチシズムあふれるコスチューム映画。", "1984-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITY OF HOPE", "希望の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "悪徳政治家と街のスラム化によって崩壊寸前のハドソンシティに住む青年ニックは、人生の目的を見いだせず仕事を辞め、不良仲間とともに盗みをはたらく。が、犯行は失敗に終わり、一人逮捕を逃れたニックは行き場を失い、追い詰められていく……。現代社会を鋭くえぐり、アメリカの真の姿をさらけだした問題作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富江", "富江", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ホラー・コミックの旗手、伊藤潤二の代表作を映画化。20歳の月子は３年前の交通事故の後遺症に悩まされ、精神科医に通っている。彼女は事故の前後３ヵ月の記憶を失っていた。そのころ、ある高校のクラスで自殺や精神崩壊などの怪事件が続出。事件と前後して、このクラスの川上富江という女生徒が失踪していた。やがて、この女性と同姓同名の美女が、月子の恋人のバイト先に出現。この時から、月子の周囲で奇怪な事件が続発する……。男たちに“殺してしまいたい”という衝動を引き起こさせるナゾの女・富江のキャラクターがなんとも不気味。この魔性の美女を、菅野美穂が怪しげに演じている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VITAL SIGNS", "愛と青春の鼓動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "医学校に学ぶ５人の男女が織り成す愛と葛藤の青春ドラマ。外科医志望のマイケルは、小児科医を目指すジーナの心を射止める。しかし、彼女は外科医のバレンタインと交際をしており、怒ったバレンタインによってマイケルは窮地に立たされてしまう。しかし、そこを救ったのは、マイケルと反目しあっていた妻子持ちのローズだった。小児科志望のスザンヌや上昇志向の強いヘインズも加わって、彼らは青春の喜びや悲しみに接しながら次第に成長していくのだった。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海がきこえる", "海がきこえる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "東京の大学に通う拓が、クラス会に出席するため、故郷・高知に帰省することになった。その飛行機の中で、彼は中学・高校時代の切ない思い出の数々を振り返っていく……。氷室冴子の同名小説を原作に、スタジオジブリの当時の若手スタッフたちの手で製作した長編アニメーション。そもそもはテレビ放映用に製作され、1990年にオンエアされたものだが、好評につき1993年に劇場公開された。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eraserhead", "イレイザーヘッド　デジタル・リマスター版", "", "", "http://image.eiga.k-img.com/images/movie/54283/315x210.jpg?3774", 89, "鬼才デビッド・リンチ監督のデビュー作を、13万枚もの画像修正で高画質に蘇らせたデジタル・リマスター版。消しゴムのような髪形から“イレイザーヘッド”と呼ばれるヘンリーは、恋人から肢体が不自由な赤ん坊を産んだことを告白され、恋人との結婚を決意する。ところが彼女はおぞましい形相の赤ん坊に耐え切れずにやがて家を出てしまい、残されたヘンリーは1人で赤ん坊を育てることになるが……。", "2009-02-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Casanova", "カサノバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "アラン・ドロン、マルチェロ・マストロヤンニ、ドナルド・サザーランドなど、過去に様々なスターが演じてきた史上最も有名なプレイボーイ、ジャコモ・カサノバ役に「ブロークバック・マウンテン」のヒース・レジャーが挑んだ恋愛コメディ。監督は「ショコラ」「サイダーハウス・ルール」の名匠ラッセ・ハルストレム。シエナ・ミラー、ジェレミー・アイアンズ、レナ・オリンらが共演。", "2006-06-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("その街のこども　劇場版", "その街のこども　劇場版", "", "", "http://image.eiga.k-img.com/images/movie/55723/315x210.jpg?4354", 0, "阪神・淡路大震災から15年目にあたる2010年1月17日にNHKで放送されたドラマを、新たな映像を加えて再編集した劇場版。主演は実際に震災を体験している森山未來と佐藤江梨子。子どものころに震災を体験し、現在は東京で暮らす勇治と美夏。2人は“追悼のつどい”が行われる前日に神戸で偶然知り合い、15年目の朝を迎えるまで時間をともにすることに。復興を遂げた真夜中の神戸で、これまで語ることのできなかった2人の思いがあふれ出す。", "2011-01-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE VIEUX FUSIL", "追想", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "第二次大戦下のフランスの小都市モントバン。ナチス・ドイツの動向に胸騒ぎを感じた医師のジュリアンは、妻と娘を故郷の小村に疎開させる。数日後、村を訪れた彼の前には酸鼻をきわめた光景が広がっていた。射殺された娘、火炎放射器で焼きつくされ、石垣に染みついた黒影と化した妻。ジュリアンは復讐に立ち上がる……。うららかな光を浴びて一家で自転車をこぎ出す幸福なイメージ（シュナイダーのこぼれるような笑顔の魅惑！）と、冷酷非情な復讐鬼と化して火炎放射器を操るジュリアンの姿が対比的に交錯。戦争の無残さを、声にならない痛切な叫びで告発する。温厚な小市民が変貌するさまを、ノワレが好演。", "1975-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fuori dal mondo", "もうひとつの世界", "", "", "http://image.eiga.k-img.com/images/movie/55433/315x210.jpg?1630", 100, "「青春の形見」「ぼくの瞳の光」のジュゼッペ・ピッチョーニ監督が見知らぬ男女の交流を描く人間ドラマ。主演はマルゲリータ・ブイとシルビオ・オルランド。生涯を修道生活に捧げる覚悟でいる修道女のカテリーナと、クリーニング店を経営する独身のエルネストは、ある日偶然に知り合う。それぞれに孤独を抱えた2人は、共通の世界を広げていく。イタリア映画祭2010にて上映。", "2010-04-29", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BANDIT　QUEEN", "女盗賊プーラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1980年代の初頭、群盗を引き連れてインド北部を中心に荒らし回った実在の女義賊プーラン・デヴィの闘いを描いた一作。低層カーストの娘として生まれ、社会的差別、性的虐待を受けて育ってきた彼女がやがて義賊となってカースト制、女性差別という社会の壁に立ち向かっていく様を映し出す。", "1994-01-01", "21,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cry Uncle!", "泣く女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "　私立探偵のジェイクは富豪のドミニクに雇われ、娼婦でポルノ女優だった女性の殺害事件を調査し始める。彼女はドミニクを脅迫していたというが……。のちに「ロッキー」でアカデミー賞に輝くジョン・G・アビルドセン監督が手がけたエロティック・コメディ。日本では成人映画として公開された。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOONTRAP", "ムーントラップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "特撮工房マジック・ランターン・プロを主宰するR・ダイクの監督デビュー作。いかなる機械や有機体をも自分の体に取り込み、ロボット化させる機械生命体の謎を解くため月へ向かう人類の冒険と戦いを描いていく。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TRENCH", "ザ・トレンチ〈塹壕〉", "", "", "http://ecx.images-amazon.com/images/I/31PHEPNZEGL._SL210_.jpg", 95, "第一次世界大戦下、わずか二時間で６万人の兵士の命が奪われたフランス北部ソンムの戦い。20世紀最大の愚行とも呼ばれるこの戦いの前夜48時間を、塹壕の中で恐怖と緊張と不安の時を過ごす英国軍青年兵士の視点を通して描くヒューマン戦争映画。「チャーリー」「グッドマン・イン・アフリカ」などの脚本家の監督デビュー作。", "2001-08-04", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白昼の死角", "白昼の死角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 154, "戦後の混乱を背景に、東大出身のエリートたちが行った数々の完全犯罪を描くピカレスク・ロマン。悪漢、悪がヒーローになる映画。善悪の対決を、悪の側から切り取ったところが新鮮だ。特に悪のヒーローが死なず海外へ逃亡してしまうというラストは、実に映画的で痛快。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　BATTLE　OF　MIDWAY", "ミッドウェイ海戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "太平洋戦争の戦況における、大きな転機となったミッドウェイ海戦の模様を、従軍していた名匠J・フォードの手でフィルムに収めた短編ドキュメンタリー。ヘンリー・フォンダをはじめとするフォード作品の名優たちがナレーションを担当。アカデミー短編ドキュメンタリー賞を受賞し、「怒りの葡萄」「わが谷は緑なりき」に続きフォードは３年連続オスカーの栄冠に輝いた。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rob-B-Hood", "プロジェクトBB", "", "", "http://image.eiga.k-img.com/images/movie/33913/315x210.jpg?2227", 126, "ジャッキー・チェン、ユン・ピョウ、マイケル・ホイら香港映画界を代表するスターが共演するアクション・コメディ。サンダル、フリーパス、大家の3人は、人を傷つけないことをポリシーに活動する泥棒グループ。そんな彼らが、ひょんなことから大富豪の赤ちゃんを誘拐する仕事を引き受けることに。誘拐は成功したものの、警察の検問に引っかかり大家が交通違反で逮捕されてしまう。サンダルとフリーパスは赤ちゃんを連れて逃げ出すが……。", "2007-04-07", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トーキョー×エロティカ", "トーキョー×エロティカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "1995年、街中に撒かれた毒ガスでケンヂが死んだ。1997年、ケンヂの恋人ハルカが殺される。そして2002年、前世の記憶を失ったケンヂとハルカの前にピンクのウサギ＝死に神が現れ、彼らがいかに死ぬかを知らせる……。無名都市トーキョーを舞台に時間軸を交錯させながら、世紀が変わっても何ら変わることのない閉塞的社会の闇を撃ち、そこから生きる力を観客に見いださせるという、瀬々敬久の集大成的作品。デジカメをフィルムにブローアップさせた映像の荒々しさが、テーマの強烈さをより際立たせている。ピンク映画やインディーズ映画界の雄たるキャスト、それぞれの好演も見逃せない。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("独立愚連隊", "独立愚連隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "岡本喜八の名を一躍知らしめた、ウエスタン調の戦争映画。脚本は岡本が助監督時代に書いたもので、これが認められて監督に昇進した。終戦間近の北支戦線。荒木は弟の死因を究明するため、従軍記者を装って、山岳地帯で敵と対峙している日本軍の警備隊へやって来た。この隊は通称“独立愚連隊”と呼ばれ、各隊のクズばかり集められた隊だった。荒木は、生前弟が使っていた部屋の壁から何発もの銃弾を発見。ますます弟の死に陰謀がからんでいることを確信する。岡本の演出は、コミカルな味に優れ、時にアクション・シーンの展開に胸のすく歯切れを見せて、そこから戦争の愚劣さが鮮やかに浮かび上がった。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄", "地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "奇才、石井輝男が死後の世界をビジュアル化した怪作。幼女連続殺人犯や非道な新興宗教団、保険金詐欺事件を起こした女性など、世間を賑わせた実在の凶悪犯を思わせる罪人たちが、地獄で責め苦を味わう。舌を抜かれ、業火で焼かれ、八つ裂きにされるなどのグロテスクな描写には、石井監督ならではのモンドな美学が充満。日本映画史上初のヌードを披露した前田通子が閻魔大王役で、42年ぶりに映画出演を果たした。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAISA´", "戦火のかなた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "イタリアン・ネオ・レアリスモの代表作。連合軍のイタリア上陸直前からイタリア解放までの出来事を取材した６つの挿話からなる。少年に靴を盗まれた米黒人兵の話、連合軍ローマ入城の折に知り合った米兵と再会する若い娼婦の話、戦火をくぐり抜けて昔の恋人を捜しに行く米軍看護婦の話などなど。南から北へ連合軍の北上とともに展開するこれらのエピソードは、どれも静かな語り口ながら戦争の悲惨さを訴え、特にラストのポー河での独軍によるパルチザン処刑の場面は、痛烈に戦争の罪業を告発している。「無防備都市」と並んでロッセリーニの名を世界に広めた戦争叙事詩の名作。", "1946-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("卍舞 III", "卍舞 III", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "キャストを入れ替えたシリーズ第３作。大店のお蝶は、恋人・黒川玄之進の陰謀により、ゴロツキにナタで斬りつけられ谷底へ突き落とされた。権蔵という男に助けられ、一命をとりとめたお蝶は女郎屋で働きながら男の行方を探し、復讐の機会をうかがっていた。そんな中、過去の仕打ちに実の妹・おかよが絡んでいたことを知る。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("積木の箱", "積木の箱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "三浦綾子の新聞連載小説を名匠・増村保造監督が映画化。姉と信じていた女と父との関係を覗き見てしまった15歳の少年の性の目覚めと自立を描く。複雑な人間関係を描き分けた演出力が見事。少年にとっての女神とも言うべき若尾文子のまろやかな美しさが印象的。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALICE IN WONDERLAND", "ふしぎの国のアリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "L・キャロルのベストセラー童話をもとにしたディズニー・アニメ。白うさぎを追いかけて入った穴から迷い込んだワンダーランド。少女アリスはチェシャ猫やドードー鳥など、風変わりですてきな動物たちと、ドキドキ・ワクワクの冒険を体験していく。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時の輝き", "時の輝き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "女子小・中・高生たちに圧倒的な支持を得、1993年度文庫売り上げNo.１の座を獲得した折原みとの同名小説の映画化。神埼由花は、看護実習先の病院で３年ぶりに初恋の相手ジュンチと出会い、思いを通じることができる。実習も終わり、ジュンチも退院、楽しい夏休みを満喫していた。しかし、９月になってすぐ、ジュンチが再び入院してしまう。彼は不治の病である骨肉腫に冒されていたのだ。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("この胸いっぱいの愛を", "この胸いっぱいの愛を", "", "", "http://ecx.images-amazon.com/images/I/51E57W10ANL._SL210_.jpg", 130, "大ヒット作「黄泉〈よみ〉がえり」を手がけた原作・梶尾真治、監督・塩田明彦の名コンビが、再び挑んだ感涙のラブ・ストーリー。20年前にタイムスリップしてしまった4人の男女が、過去にひとつだけやり残した願いを叶えようとする姿を、切ないタッチで描き出す。「黄泉〈よみ〉がえり」の主題歌をヒットさせた柴咲コウが、本作でも主題歌を担当。実際に映画を見て、本人自ら歌詞を書き下ろしている。出張で少年時代を過ごした町を訪れた比呂志は、自分が20年前にタイムスリップしていることに気づく。彼はかつて憧れた女性・和美と再会し、彼女を病死という運命から救おうとするが……。", "2005-10-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("九十九本目の生娘", "九十九本目の生娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "北上川上流の白山部落では、10年に１度の火づくりの祭りの日が近づいていた。99本目の刀を生娘の生血で鍛えるために、若い娘がさらわれる……。エログロといわれた大蔵貢時代の新東宝でも一、二を争うキワモノ映画で、当時は肩身の狭かったSMファンをひそかに喜ばせた。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG BUSINESS", "ビッグ・ビジネス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "N.Y.の大企業モラマックス社の女会長セイディとローズは、あまり似ていない双子の姉妹。それもそのはず、二人は同じ日に同じ病院で生まれた別の双子と、片方が入れ違ってしまった姉妹なのだ。一方、もう片方の双子は、ケンタッキーにあるモラマックスの子会社に勤め、田舎暮らし。ひょんなことから、この二組の双子がN.Y.のホテルに同宿することになり、ホテルは大混乱に……。怪女優(？)B・ミドラーとベテラン、L・トムリンが、それぞれ一人二役を演じた爆笑コメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cloverfield", "クローバーフィールド HAKAISHA", "", "", "http://image.eiga.k-img.com/images/movie/53263/315x210.jpg?1470", 85, "「LOST」のJ・J・エイブラムス製作によるパニック・ムービー。正体不明の何者かに襲われたニューヨークの壮絶な一夜を、現場に居合わせた人物の視点から、ハンディカムで撮影されたドキュメンタリータッチの映像で臨場感たっぷりに描く。ニューヨーク、ダウンタウン。友人たちと楽しい一時を過ごしていた青年ロブだったが、その時、街が突然の爆音に揺れる。慌てて外に飛び出した彼らの目の前に、無惨に破壊された自由の女神像の頭部が転げ落ちてきた。", "2008-04-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pawnbroker", "質屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "　ニューヨークで質屋を営むユダヤ人のソルはナチの強制収容所で妻子を殺され、それ以来人間不信に陥っていた。ソルを尊敬し、その助手として働いていた若者ジーサスは金欲しさも手伝って、チンピラたちとともに自分の勤める質屋から金を盗み出そうとするが……。心を閉ざしていた男が絶望から立ち直っていく姿を感動的に描いた社会派ドラマ。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEED PEOPLE", "デビル・シード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "地球に堕ちてきたエイリアンが、人間を次々と襲い、その意識を支配し、やがて植物と人間の融合体を作り上げるというショッキングなSFホラー。隕石群が落下してコメット・バレーに調査のためやって来たドクター・ローラー。その周辺では奇妙な現象が多発していた。調査が進み、ローラーは恐ろしい発見をする。隕石のかけらは実はエイリアンの種で、それらが成長をすると人間の体内に潜入、その意識をも支配するのだった……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Heros de la Famille", "輝ける女たち", "", "", "http://image.eiga.k-img.com/images/movie/33923/315x210.jpg?1281", 103, "カトリーヌ・ドヌーブ、エマニュエル・ベアールというフランス映画界を代表する2人の女優が共演するヒューマン・ドラマ。南仏ニースにあるキャバレー“青いオウム”のオーナー、ガブリエルの死をきっかけに、彼を父親のように慕っていたマジシャンのニッキーやその元妻アリスら、疎遠になっていたファミリーが再び集まった。過去の秘密や現在の問題など様々な悩みを抱える彼らが繰り広げる人間模様を、懐かしいヒット曲を散りばめながら描く。", "2007-04-14", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONFESSIONS OF A SERIAL KILLER", "アメリカの惨劇　188人を私刑にした男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "実在の事件をもとに、ある連続殺人犯の告白をセミドキュメンタリー・タッチで描く。1984年に逮捕されたヘンリー・リー・ルーカス。彼はヒッチハイカーを中心に、360人もの行きずり殺人を告白。“殺しは呼吸と同じく必要なもの”と誇り、何の動機もなしに人を殺す彼の姿は、まさに現代の切り裂きジャックだ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いちご同盟", "いちご同盟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "三田誠広の同名小説を映画化。「悪役パパ」「今日から俺は！」の鹿島勤が自ら脚色を施し、それまでの作品からは判断できなかった豊潤な作家性を披露した感動の思春期ドラマだ。中学３年生の良一は、ピアニストの道へ進みたいのに、どうしても自分の才能に自信が持てない。自殺した少年の詩に心を揺り動かされていた彼は、ある日、野球部のエース羽根木に、病に伏している少女、直美を紹介される。ガンに冒された直美を羽根木とともに勇気づけるうち、ほのかな恋心を抱く良一だったが、運命は哀しい結末を用意していた……。タイトルの“いちご同盟”とは、15歳の少年二人の決意を示す言葉。少女との恋を経て、確かな心の成長を遂げる主人公の姿が胸を打つ。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後の博徒", "最後の博徒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "広島・呉を舞台に、広島中のヤクザを敵に回して、非情な世界へと飛び込んでいく男の姿を描く。「修羅の群れ」のコンビ、山下耕作と村尾昭が組み、キャストも多彩な顔ぶれがそろった。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("See No Evil", "シー・ノー・イーヴル　肉鉤のいけにえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "エンターテインメント性の高いパフォーマンスで知られるアメリカの人気プロレス団体WWEが、スーパースター“ケイン”ことグレン・ジェイコブズ主演で放つ拷問スプラッター・ホラー。減刑のための労働奉仕として、老朽化の進む廃ホテルの修繕をすることになった10代の軽犯罪者たち。しかし、彼らの他には2人の監察官と女主人しかいないはずのホテルには、肉鉤チェーンを振り回す恐ろしい殺人鬼ジェイコブが潜んでいた……。", "2007-01-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チョルラの詩（うた）", "チョルラの詩（うた）", "", "", "http://image.eiga.k-img.com/images/movie/55453/315x210.jpg?4599", 102, "「トロッコ」の川口浩史監督がメガホンをとった日韓合作ラブストーリー。1987年、日本の学校で非常勤講師として詩を教えている在日韓国人の幸久は、祖父の葬式に参列するため数年ぶりに故郷の韓国・チョルラナムドを訪れる。幸久は久々に従兄弟のカンスと再会するが、幼なじみのソンエがチョルラに戻ってきたことで、3人は微妙な三角関係に陥ってしまう。", "2010-06-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OSCAR　AND　LUCINDA", "オスカーとルシンダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "賭け事に魅せられたイギリスの神学生オスカーは周囲の反感を買い、償いとしてオーストラリアへ伝道に向かうことを決意。現地に向かう船の中でルシンダという女性と出会う。オーストラリアでガラス工場を経営する彼女も賭け事に魅せられた人間であった。親しくなるにつれ、オスカーは彼女が相談相手のハセット牧師を愛していると思い込む。そして、ハセットは奥地の教会へと赴任してしまった。その後もルシンダへの愛、賭け事をする自分への中傷に悩み抜いた彼は、彼女の工場で作られたガラスの教会をハセットに届けるという人生最大の賭けに挑戦することになる……。古い価値観が残るヴィクトリア朝時代を背景に、ギャンブルのために社会から偏見の目を向けられる二人の出会いと恋を、ウイットに包んで描く感動のドラマ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr. Brooks", "Mr.ブルックス　完璧なる殺人鬼", "", "", "http://image.eiga.k-img.com/images/movie/1913/315x210.jpg?1983", 120, "家族思いの大物実業家と残忍な連続殺人鬼という2つの顔を持つアール・ブルックス。2年ぶりに殺人を犯した彼だったが、その一部始終が犯行現場の向かいのアパートに住む青年に撮影されてしまっていた。さらに女性刑事アトウッドの捜査の手もブルックスに迫っており……。連続殺人鬼役にケビン・コスナー、女性刑事役にデミ・ムーアが扮する。メガホンを取るのは「スタンド・バイ・ミー」の脚本家ブルース・A・エバンス。", "2008-05-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お嬢さん乾杯", "お嬢さん乾杯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "木下惠介が「破戒」に次いで撮り上げた作品。木下としては初の本格喜劇である。敗戦による社会体制の変化に伴い華族制度は廃され、いわゆる“斜陽族”が登場し、その一方では新興成金が生まれたりした。そうした世相にアイデアを得て作られたのが本作品である。自動車修理業で成功した圭三に元華族の令嬢・泰子との縁談がもち上がる。圭三は“提灯に釣鐘だ”として最初は断わるが、仕方なくお見合いするハメになる。ところが圭三は泰子に会ったとたん一目惚れ。圭三には雲の上の美女に思われた泰子の方も結婚を承諾。こうして成金と没落華族のチグハグなやり取りが始まる。この明るく快テンポの都会喜劇は、封切り当時大ヒットした。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("究竟の地　岩崎鬼剣舞の一年", "究竟の地　岩崎鬼剣舞の一年", "", "", "http://image.eiga.k-img.com/images/movie/57703/315x210.jpg?2941", 128, "岩手県北上市の農村地域に古くから伝わる民俗芸能で、国の重要無形民俗文化財にも指定されている「鬼剣舞（おにけんばい）」に携わる人々や、地域住民の生活を1年間にわたり追い続けたドキュメンタリー。少子化により人口が減少し続けている地域で、庭元（家元）が踊った最後になるであろう舞の様子や、他界した剣舞の大師匠の葬儀で剣舞作法の念仏と祈りの舞が披露された様子など、貴重なシーンの数々が収められている。", "2012-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("捜査線 LINE OVER", "捜査線 LINE OVER", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "俳優の大沢樹生が企画、プロデュース、主演を務める異色の刑事アクション。借金の取りたてに追われ、急場をしのぐために引ったくりに手を染めた刑事・田代は、偶然通りかかったネネにその現場を写真に撮られてしまう。一方、元警官の芹沢が関わっているカルト宗教「エターナルエントロピー」の犯罪を探る高寺は、署内に潜むスパイの調査を田代に依頼するが……。共演は「約束の地に咲く花」の鈴木一功、「ルナハイツ2」の夏川純ほか。", "2010-08-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナビィの恋", "ナビィの恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "異色ロード・ムービー「パイパティローマ」で才気を見せた中江裕司の長編第２作。前作のような特異な時空間創出を抑え、オキナワン・ミュージカルともいうべき陽性の恋愛劇を展開している。物語の中心は、老女ナビィが60年前の初恋相手と駆け落ちするというもの。彼女の純情が、沖縄の粟国島に里帰りしたOLの孫の視線から紡がれていく。ナビィの気持ちを知りながら、飄々と日常を送る夫に扮した沖縄民謡の大御所、登川誠仁の自然体が忘れがたい。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANGEROUS LOVE", "ロス女刑事 危険な罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "恋人探しのデート・クラブで、連続強姦殺人事件が発生する。そこは自己紹介ビデオを交換して、気に入った相手を求めるといったビデオ時代を反映したデート・クラブだった。さっそく捜査に乗り出したロス女刑事クリス。彼女には不倫関係にある上司がおり……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEHOLD A PALE HORSE", "日曜日には鼠を殺せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "ジンネマンらしい、ヒューマン・タッチのサスペンス。スペイン内乱時にゲリラのリーダーだったマヌエルは、国境を越えてフランスに亡命、それから20年の月日がたった。警察署長ヴィニョラスは、マヌエルを捕まえることだけを生きがいにしてきたが、国外にいる彼に手出しはできない。そんな折、マヌエルの母が重体というニュースが入った。だが母は息子が罠にかかるのを感じ、フランスに旅立つ神父に息子を来させるなと託して息を引き取る……。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHE，　UN　HOMBRE　DE　ESTE　MUNDO", "チェ・ゲバラ／人々のために", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "傑出した革命家として歴史にその名を刻み、1967年にボリビアで死亡したチェ・ゲバラの公私に渡る人物像を浮き彫りにするドキュメンタリー。かつてゲバラと寝食を共にした元兵士らが出演し、地位や名誉に執着せず自由を求める人々のために闘い続けたゲバラの人柄、指揮官としての資質について語り明かす。生前のゲバラの姿を収めた貴重な映像資料も多数収録されている。", "1999-01-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／MY　OWN　BREATHING", "息づかい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "「ナヌムの家」「ナヌムの家II」のビョン・ヨンジュが、前２作同様日本軍の従軍慰安婦として苦難の半生を送ってきたハルモニ(老婆)たちに目を向けた意欲作。今回は、自身も従軍慰安婦だったイ・ヨンスをインタビュアーに、フィリピン、韓国のソウルやテグなどでそれぞれの生き方を模索するハルモニの現在を映し出している。", "1999-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fighter", "ザ・ファイター", "", "", "http://image.eiga.k-img.com/images/movie/54103/315x210.jpg?2616", 115, "「スリー・キングス」のデビッド・O・ラッセル監督が、マーク・ウォールバーグとクリスチャン・ベールを主演に迎え、名ボクサー、ミッキー・ウォードと彼の異父兄ディッキー・エクランドの絆を描いた実録ドラマ。1980年代のマサチューセッツ州ローウェル。米ボクシング界のスター、シュガー・レイ・レナードと拳を交わしたことのあるディッキー（ベール）は街の英雄だったが、戦いに敗れたことから麻薬に手を染め、投獄される。そんな兄の陰でミッキー（ウォールバーグ）は早くからアマチュアボクサーとして実績を積み、頭角を現すが……。クリスチャン・ベールとメリッサ・レオが第83回米アカデミー賞で助演男優賞、助演女優賞を受賞した。", "2011-03-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名桜", "悪名桜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "大阪周辺の繁華街で焼きとり屋をやる朝吉と清次は、殴り込んできた愚連隊の少年を更正させようと、ABCクラブに出陣。続いて大鯛組に乗り込んだ二人は、組を叩きつぶす。市原悦子が朝吉の押しかけ女房になる田舎女を好演。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ME´CAANIQUES　CE´LESTES", "恋の力学", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "オペラの歌姫を夢みる少女の姿を、色鮮やかな映像で描くサクセス・ストーリー。オペラ歌手になる夢を捨てきれず結婚式を飛び出してパリにやって来たアナ。友人の住むアパートに転がり込むが、ルームメイトのセレステにことごとく意地悪をされ、映画監督に売り込むチャンスも奪われてしまう。自信喪失したところを優しく慰めてくれたゲイのボーイ、アルマンの協力で、再びやる気を取り戻すアナだったが、ひょんなことから彼と偽装結婚することになり……。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌S〈スペシャル〉", "釣りバカ日誌S〈スペシャル〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "“男はつらいよ”シリーズの併映作品として出発した“釣りバカ”シリーズ。登場以来、正月興行の配収大幅アップの余勢を駆って、お盆休み一本立ての新バージョンが生まれた。今回も建設会社のダメ社員・ハマちゃんと社長のスーさんとの釣りバカコンビは健在だが、ハマちゃんの愛妻・みち子さんとスーさんの不倫疑惑がこの名コンビ最大の危機を呼ぶのがミソ。５年ぶりのメガホンとなる森崎東監督は、西田敏行のダブダブの肉体を目一杯使いこなした猪突猛進型アクションに、突然できてしまった妻との距離を埋めようともがく超愛妻家の感情をあふれさせる。今までのシリーズとは一味違う骨太の人情喜劇。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤目四十八瀧心中未遂", "赤目四十八瀧心中未遂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 159, "鈴木清順や阪本順治作品のプロデューサーとして名をはせてきた荒戸源次郎が、映画人生を懸けて完成させた監督第２作。大阪の裏町で出会った男と女の許されぬ逃亡を精緻な筆致で綴った渾身の作でありながら、画面の隅々には映画ならではのユーモアや粋がたっぷり詰まり、優雅な風が吹く。生活のすべてを捨て、尼崎のもつ焼き屋に流れ着いた生島与一は、暗くて古いアパートの一室で黙々と臓物をさばき、それを串に刺していた。隣人で謎の女、綾に心惹かれる彼は、ある夜、彼女からの劇的な誘惑に遭遇することになる。綾役の寺島しのぶが披露した体当たりのオール・ヌードも話題を集め、日本アカデミー主演女優賞をはじめ国内の数々の賞に輝いた。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SONG OF LOVE", "愛の調べ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "シューマンの妻クララの伝記物語。天才ピアニストの評判高いクララは父親の門弟シューマンと恋に落ち、親の反対を押し切って結婚。夫を世に出すため、自分の才能を殺して夫に献身的につくすが……。ルービンシュタインのピアノ・ソロが魅力の音楽映画の秀作。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THEY CALL ME MACHO WOMAN", "マッチョ♀ウーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "麻薬の密造グループに誘拐監禁された、平凡な女性スーザンが、セクシーでハードな“マッチョ”ウーマンに変身し、復讐を開始する……という、奇想天外なサスペンス。敵に向かって、クギやオノを投げるという、これまた珍妙なアクションが展開。主演のD・スウィーニーのセクシーでマッチョなボディラインに注目。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若さま侍捕物手帖 地獄の皿屋敷", "若さま侍捕物手帖 地獄の皿屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "大川橋蔵主演による“若さま侍捕物帖”シリーズ。若さまは、将軍家から拝領した呉須の皿を狙う黒装束の一団と闘う。この作品はデビュー間もない大川橋蔵の初主演作。この作品は、同シリーズ「べらんめえ活人剣」と２部作で封切られた。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TODAY YOU DIE", "沈黙の脱獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "S・セガール扮する義賊の、脱獄と復讐の行方を描くサスペンス・アクション。セガールと人気ヒップホップ・ユニット、“ノーティ・バイ・ネイチャー”のトレッチとの軽妙なかけあいも見どころだ。泥棒のハーランは、恋人のために足を洗うことを決意。ところが、何も知らされず現金強奪事件の片棒を担がされ、投獄されてしまう。自分を罠にハメた憎き黒幕への復讐に燃える彼は、囚人アイス・クールと共に前代未聞の脱獄計画に挑む。", "2006-03-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Economics of Happiness", "幸せの経済学", "", "", "http://image.eiga.k-img.com/images/movie/56363/315x210.jpg?2199", 68, "30年前までは外国人立ち入り禁止区域だったヒマラヤの辺境ラダックは、急速な近代化の波にのまれ、伝統的な生活スタイルが一変。人と自然とのかかわりや人と人とのつながりが希薄になり、伝統文化に対する誇りまでもが失われていった。消費文化に翻弄されるラダックの人々の姿を通して、世界中の環境活動家たちがグローバリゼーションの負の側面を指摘し、本当の豊かさとは何かを問いかけるドキュメンタリー。", "2011-05-21", "1,77,2,7,3,11,21,23,76,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道追踪／THE　ZODIAC　KILLERS", "極道追踪 暴龍 in 歌舞伎町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "東京で暮らす香港留学生ベンはホステスとして働く中国人女性ティンランに恋するが、彼女には暴力団員の浅野という恋人がいた。しかし、その浅野が抗争に巻き込まれて殺されてしまう。死に際の彼から事件の真相を吹き込んだテープを受け取ったティンランはベンに助けを求めるが……。監督アン・ホイが「客途秋恨」の日本ロケの時に知った実際の事件をもとに、逃避行を繰り広げる二人の姿を描いたサスペンス。浅野役の石田純一ほか、日本人俳優も多数出演している。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOTANY BAY", "流刑の大陸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "18世紀末、新天地建設のためイギリスからオーストラリアへと、囚人たちが護送されることになった。ところが腹黒い船長に対し、無実の罪でオーストラリア行きとなったヒュー・タラントは反乱、脱出を図るが……。大海原に展開する一大アドベンチャー・ストーリー。主演のA・ラッドが好演を見せる。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲロッパ！", "ゲロッパ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "毒舌評論家としてもおなじみの井筒和幸監督が描いた人情コメディ。収監を目前に控え、引退を決意した組長のために、ソウルの帝王ジェームズ・ブラウンの誘拐を目論む子分たちの奮闘劇を、組長父娘の物語も交えながらパワフルに追いかける。数日後に収監されることが決まっている羽原組組長、羽原大介の心残りは、25年前に生き別れた娘・かおりとの再会、そしてジェームズ・ブラウンの名古屋公演だった。それを知った弟分、金山はJB誘拐を計画するが……。父娘の再会がクライマックスに。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("闘牛に賭ける男", "闘牛に賭ける男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "大々的なスペイン・ロケによる石原裕次郎・北原三枝主演のメロドラマ。闘牛興行を日本で開催することに命を賭ける男の物語。舛田利雄のメリハリのきいた演出にスペイン・ロケが効果的に生かされている。裕次郎と北原三枝の共演は、二人の結婚により、これが最後となった。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE BIG MAN", "小さな巨人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "西部開拓時代を121歳まで生きた老人の見聞録。カスター将軍は狂気の殺人者だったとか、ビル・ヒコックは臆病な卑怯者だったとか、新しい視点で様々なエピソードを描いていて興味深い。ホフマンの凝ったメイクと熱演が見もの。", "1970-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にっぽん戦後史　マダムおんぼろの生活", "にっぽん戦後史　マダムおんぼろの生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "横須賀のバー“おんぼろ”のマダムに今村監督自らインタビューし、すさまじい女の戦後史を浮き彫りにする。挿入されるニュース映像が効果的。日本の戦後を生きてきた個人の歴史と社会史とを、映像の上でドッキングさせた意欲作。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("制覇", "制覇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "日本最大のヤクザ組織の３代目狙撃事件に端を発し、激化した抗争のドラマを、揺れ動くヤクザの家族を中心に描ききった中島貞夫の超大作。三船・菅原・若山といったヤクザ映画の常連俳優たちを、岡田茉莉子が貫禄の演技でビシリと締めている。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一見鍾情／SAUSALITO", "ひとめ惚れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「ロアン・リンユィ　阮玲玉」で国際的女優となったマギー・チャンと香港の人気スター、レオン・ライ主演によるラブ・ストーリー。サンフランシスコで10歳の息子と暮らすシングルマザー、エレンはタクシードライバーとして働いていた。ある日、エレンが友人とバーに飲みに行くと、再起をめざす天才プログラマーのマイクと出会う。エレンとマイクは初対面で互いに惹かれ合い、一夜の関係を持ってしまうのだが……。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AND NOW THE SCREAMING STARTS", "スクリーミング 夜歩く手首", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "恐怖映画の殿堂アミカス・プロ製作のブリティッシュ・ホラー。美しい新妻が古い館に嫁いでくる。ところがそこは呪われた恐怖の館だった。血を流す肖像画、窓の外に立つ眼のない男。そして歩き回る手首……。新妻は恐怖で次第に狂っていく。指先までデリケートな動きを見せる“手首”の特撮が見事。", "1973-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲキ×シネ「薔薇とサムライ」", "ゲキ×シネ「薔薇とサムライ」", "", "", "http://image.eiga.k-img.com/images/movie/56053/315x210.jpg?3820", 196, "「劇団☆新感線」による演劇を映画館で上映する「ゲキ×シネ」第9作。17世紀、ヨーロッパのとある小国。海賊の石川五右衛門は、意気投合した女海賊アンヌの用心棒を引き受ける。しかし、アンヌは先王の隠された娘だったことが発覚。城の兵士に連行され、王として即位することを強要される。事件の裏に陰謀をかぎとった五右衛門は、アンヌを助け出すため王国に乗り込む。主演は2008年に上演された「五右衛門ロック」の古田新太。ヒロイン、アンヌ役に天海祐希。", "2011-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER　THE　SKIN", "アンダー・ザ・スキン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "イギリスの女性監督C・アドラーが手掛けた鮮烈なデビュー作。愛する母親の死によって、深い絶望感に打ちひしがれた19歳の少女アイリス。一人暮らしを始めたことでますます孤独になった彼女は、行きずりの男たちとのセックスに溺れ、唯一の肉親である姉ローズとも激しく対立していく。スーパー16ミリの手持ちカメラによるざらついた映像が、ひたすら堕ちていくヒロインの運命を生々しいリアリティーで活写。映画初主演を飾ったS・モートンが体当たりの演技を披露し、かすかな希望の光が見えるラストが感動を呼ぶ。", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("堕落天使／FALLEN ANGELS", "天使の涙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「恋する惑星」の際に用意していたエピソードをW・カーウァイが発展させた、エモーショナルな群像劇。普通の生活に憧れる殺し屋、美貌の女エージェント、殺し屋に絡む元気な金髪娘。この三角関係に、口は利けないが破天荒な性格の青年と、失恋したばかりの女性の恋模様がさり気なく絡んでいく。カーウァイならではの“すれ違い”のドラマはさらにシンプルを極め、発作的な映像のコラージュがこれまで以上に絶妙な効果を上げている。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAIGON COMMANDOS", "地獄からの復讐 コマンドー・ウルフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ベトナム戦争も終結に近い1970年のサイゴンが舞台。ストライカー率いる米軍憲兵隊の捜査チームは、暗躍する麻薬組織を追及していた。捜査が進むにつれ次第に黒幕の正体は明らかになり……。最前線から遠く離れた憲兵隊の活躍ぶりを描写。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("世界最強の格闘技・殺人空手", "世界最強の格闘技・殺人空手", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "昭和48年、大塚剛の手によって創設された“プロ空手”。そのすさまじいまでの、まさしく死をかけた闘いと、一撃必殺の技を求めて集まった男たちの集団を、世界各国の格闘技の紹介を交えて記録したドキュメンタリー。プロ空手の試合はもとより、一人一人の修行と鍛練の模様を紹介するシーンがすごい。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GTO", "GTO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1998年に放映された人気TVドラマシリーズの劇場版。元暴走族の型破りな教師、鬼塚英吉の活躍を描くものだが、彼が北海道の高校に代用教員として出かけるという内容だけに、番外編としての色合いが濃い。親友を失った悲しみから反抗を繰り返す理事長の娘と、彼女に想いを寄せる牧場の息子の縁結びに奔走したり、スクープ狙いの勘違い女性新聞記者の追跡をかわしたりと、鬼塚大忙しのエピソードが繰り広げられる。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ムツゴロウの結婚記", "ムツゴロウの結婚記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "同級生はみんな一流会社へ就職していくが、大の動物好きで、動物たちと一緒に孤島で動物の島を作って住むことが夢だというムツゴロウは、好きな彼女との結婚もままならない。それでも二人は夢に向かって走っていくのだった。明るく楽天的な青春像を描いた好編。ムツゴロウこと、畑正憲原作の映画化。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("探偵はBARにいる", "探偵はBARにいる", "", "", "http://image.eiga.k-img.com/images/movie/55983/315x210.jpg?4293", 125, "作家・東直己のデビュー作「探偵はバーにいる」を1作目とする「ススキノ探偵シリーズ」の第2作「バーにかかってきた電話」を映画化。札幌の歓楽街ススキノで活躍する探偵のもとに、コンドウキョウコと名乗るナゾの女から「ある男に会い、彼にひとつ質問してほしい」という依頼が舞い込む。簡単な依頼のはずが、探偵はその直後に命を狙われ、不可解な事件に巻き込まれていく。主人公の探偵に大泉洋、相棒に松田龍平。そのほか小雪、西田敏行らが共演。", "2011-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WARNING SIGN", "バイオ・インフェルノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "あるバイオ研究所で研究中の細菌が漏れ、所内に閉じ込められた従業員たちは、感染の恐怖におののく……。バイオテクノロジーの進歩著しい現代、“細菌兵器”の持つ恐ろしさを描いた作品。いかにもB級スリラーらしいお膳立てが功を奏している。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'illusionniste", "イリュージョニスト", "", "", "http://image.eiga.k-img.com/images/movie/55863/315x210.jpg?2432", 80, "フランスの喜劇王ジャック・タチが娘のためにのこした脚本を、「ベルヴィル・ランデブー」のシルバン・ショメ監督がアニメ映画化。舞台は1950年代のパリ。初老の手品師タチシェフは、場末のバーで時代遅れの手品を披露しながら細々と暮らしていた。ある日、スコットランドの離島にたどり着き、そこで貧しい少女アリスと出会う。タチシェフを魔法使いだと信じこみ慕うアリスと、生き別れた娘の面影を重ねるタチシェフ。2人はエジンバラで一緒に暮らし始めるが……。", "2011-03-26", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドライビング・ハイ！", "ドライビング・ハイ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "量産ツーリング・カーで行われるN1レース。過去にこのレースで優勝経験があり、現在は一線を退いている普通のOLがスピードを超越した一瞬の快感（ドライビング・ハイ）を忘れられず、カムバックを果たすが……。「私を抱いてそしてキスして」で演技派女優としての道を開いた南野陽子がヒロインを熱演。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABAR: THE MOVIE", "ぞうのババール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "フランスの代表的絵本作家J・ド・ブリュノフが1931年に生み出した、ユーモラスなキャラクター、ババール。ぞうの国の王様である彼が繰り広げる様々な冒険を描いた絵本シリーズの一編をアニメーション化。狂暴なサイにさらわれた、ぞうの国の住人たちを助けるために知恵を働かせるババールの活躍を描く。", "1989-01-01", "2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN　CHINA　THEY　EAT　DOGS，DON’T　THEY？", "ゼイ・イート・ドッグス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "妻とともに平凡な人生を送るアービッド。しかし、勤め先の銀行を襲った強盗を偶然にも取り押さえてしまった時から、その日常は一変した。一躍ヒーローとなったアービッドだが、犯行の目的が人工授精により子供を授かりたいと願う夫婦の手術代稼ぎだった事実を知り、罪悪感に苦しめられることとなる。思い悩んだ彼は夫婦のために、犯罪歴のある兄が企てた現金輸送車強奪計画に加わることになるが……。若者を中心に熱狂的な支持を受け、本国デンマークでは大ヒットを記録したアクション・ムービー。", "1999-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COSMIC RESCUE", "COSMIC RESCUE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "Coming Centuryが初主演するSF青春映画。宇宙のレスキュー隊員トリオが政府の巨大な陰謀に巻き込まれる姿を、「LOVE SONG」「修羅雪姫」の佐藤信介監督が軽快に紡ぐ。2053年、宇宙空間で救助活動を行うコスミック・レスキュー、第89師団“チーム南條”の３人は宇宙ゴミを拾う毎日を送っていた。あるとき、彼らは座礁船からのSOSをキャッチして駆けつけるが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A KID IN KING ARTHUR’S COURT", "タイムマスター　時空〈とき〉をかける少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "野球チーム“レサダ騎士団”に所属する野球少年カルヴィンは、ある日、突然時空に放り出されて、中世のイギリスへ。そこで彼を待っていたのは『アーサー王と円卓の騎士』に出てくる魔法使いのマーリンだった。マーリンは、野球チームの名前のせいでカルヴィンを高名な騎士と勘違いし、窮地に立たされているアーサー王の助っ人として、彼を呼び出したのだった。カルヴィンはアーサー王を助けることができたら20世紀に帰れるという条件でマーリンに協力することになる。マーク・トゥエインの小説を原案にした、ユーモアあふれるファンタジー。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Memories of Murder", "殺人の追憶", "", "", "http://image.eiga.k-img.com/images/movie/1533/315x210.jpg?1969", 130, "韓国で実際に起きた未解決殺人事件をリアルな演出で映画化。86年、ソウル近郊の農村で、同じ手口による若い女性の惨殺事件が連続して発生。地元の刑事パク・トゥマンとソウル市警から派遣された刑事ソ・テユンは対立しながらも捜査を続け、有力な容疑者を捕らえるのだが。監督ポン・ジョノは99年に「ほえる犬は噛まない」でデビュー、2作目の本作で韓国のアカデミー賞・大鐘賞の作品賞・監督賞・主演男優賞・照明賞を受賞。", "2004-03-27", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL RITORNO DI RINGO", "続・荒野の１ドル銀貨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "G・ジェンマが、お得意のアクロバットや早射ちガンプレイを駆使して活躍する傑作マカロニ・ウエスタン。南北戦争帰りのガンマンが、妻と子を救うため、右手を負傷しながらも左手でガンプレイを習得し、復讐するまでを痛快に描く。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花ひらく娘たち", "花ひらく娘たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "吉永小百合をはじめ日活若手の俳優が総出演したオールスター・キャストの青春映画。地味でおしとやかな姉と、対照的に派手で活発、ボーイフレンドにも事欠かない妹。この二人の見合いが重なってしまったことから、てんやわんやの大騒動が始まる……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PONT DU NORD", "北の橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "ビュル・オジェが娘パスカルと共演、摩訶不思議な展開で見る者を煙に巻くリベット版・現代の『ドン・キホーテ』物語。少女バチストの乗ったバイクの前に突然、刑務所から出所したばかりの女テロリストが現れた。閉所恐怖症で建物に入れない女のために、彼女の昔の恋人との連絡係を引き受けるバチスト。手に入れた地図と双六の謎を解きながらパリの街をさまようふたりの身に、不可解な事件がふりかかる……。", "1993-07-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOANNA", "ジョアンナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "本当の恋を見つけるためにロンドンへとやって来た19歳の娘ジョアンナ。王立美術学校に通うようになった彼女の、様々な恋の遍歴を描いた、キャッチーでポップな青春映画の快作。ボーイフレンドの家を泊まり歩き、絵の先生を誘惑、そして余命いくばくもない青年貴族との語らいなど、今日を精いっぱい生きるジョアンナの姿に、1960年代ののびのびとした空気が反映されている。主演は南アフリカ出身で、この映画の中で黒人と寝たために本国に帰れなくなってしまったというエピソードを残すG・ウェイト。彼女のハツラツとした魅力に加え、青年貴族のD・サザーランドの怪演も見もの。全編に流れる音楽はR・マッケンが作曲、ウォーカー・ブラザースのスコット・ウォーカーが歌っている。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パンツの穴 本牧ベイでクソくらえ", "パンツの穴 本牧ベイでクソくらえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ロンドンから10年振りに故郷・本牧に帰ってきたヤクザ組長の娘に一目惚れしてしまった呉服屋の息子。実は彼こそ、幼い日の彼女の初恋の相手でもあったのだが……。要所にシリーズならではの“性春”エピソードを交えながらも、青春のときめきとほろ苦さを基本に置くことを忘れていないのがうれしい。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上方苦界草紙", "上方苦界草紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "村野鐵太郎が「鬼の詩」に続いて、再び藤本義一の原作に取り組み、明治時代の上方芸人の生き様を描いた意欲作。伊勢参宮の旅人でにぎわう伊勢路で人気を博した“お杉、お玉”と呼ばれる旅芸人姉妹の旅を、義父との愛憎関係を軸に綴っていく。姉妹に扮する葉山レイコと原田和代の力演が見もの。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INDISCREET", "無分別", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "独身主義を通す舞台の人気女優と、妻ある身と偽る中年男が恋に落ち、誤解し合ったり、ウソをつき合ったりの“いい年をして分別のない恋愛騒動”を繰り広げる。グラント、バーグマンのコンビが絶妙。ロマンティックな大人向けラブ・コメディ。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YEAR OF THE HORSE", "イヤー・オブ・ザ・ホース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "前作「デッドマン」で怒涛の生弾きで音楽をつけたニール・ヤングと、彼のバンド、クレイジー・ホースの1996年のワールドツアーに同行したジャームッシュ監督によるドキュメンタリー。8ミリで撮影されたライブ映像や過去の演奏シーンのほか、メンバーへの爆笑インタビューや家族のコメントを散りばめながら、監督自身が1ファンとしてツアーに参加している興奮と喜びがひしひしと伝わる1作。", "1998-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パラ族　パラパラじゃないか！", "パラ族　パラパラじゃないか！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "パラパラに熱中する若者たちの奮闘を描いた青春コメディ。フリーターの青年フジタは、便利屋のアルバイトとして多忙な日々を送っていた。そんなある日、バイト先の社長ジャイロさんが行方不明となり、フジタはジャイロさんの娘のパラ子と共に探索をはじめる。唯一の手がかりである携帯サイト「パラ族」を通して4人の若者たちと出会ったフジタは、サイト存続を賭けたパラパラバトルに巻きこまれていく。", "2006-12-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOLA RENNT", "ラン・ローラ・ラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ユニークなストーリー展開と奔放な映像表現が話題となった、ドイツ生まれの快作。午前11時40分、女の子ローラのもとに、麻薬取引で得た大金を失い途方に暮れる恋人からの電話が。正午までに金を取り戻さなければ、組織は彼に恐ろしい制裁を加えるに違いない。制限時間20分という限られた時間のなか、ローラは10万マルクという大金を作るためにベルリンの街を駆ける……。金策に失敗しては時間が巻き戻る、20分間のスリリングな物語３パターンが展開。わずかなタイミングのズレによって、前回とは違う出来事が起こる。アニメやモノクロ、ビデオ映像が入り乱れたシャープな映像、疾走するテクノ・ミュージックも刺激的だが、何より赤い髪を振り乱して、ひた走るヒロインの姿が鮮烈。", "1998-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GO NOW", "GO　NOW", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "多発性硬化症という難病に侵された青年が、恋人に支えられながら生きる希望を見出していく姿を描いたラブ・ストーリー。ブリストルで暮らすサッカー好きの美術装飾職人ニックは、ある日クラブで出会ったカレンという女性と恋に落ちる。2人は一緒に暮らし始め、幸せな毎日を送っていたが、ニックには恐ろしい病魔が忍び寄っていた……。イギリスのマイケル・ウィンターボトム監督が「バタフライ・キス」に続けて贈る長編第2作。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛の町", "愛の町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "田坂具隆監督の現存する唯一の無声映画。「五人の斥候兵」「土と兵隊」などの軍事映画で知られる田坂監督だが、この作品は当時彼がしばしば手掛けていたメロドラマ・ジャンルに属するもの。両親と死別した娘が大陸から本土に帰国。ただ一人の身内である祖父のもとに身を寄せるが、彼こそは父を勘当した本人で……。山本嘉次郎が翻案、脚本を担当している。", "1928-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER KONGRESS TANZT", "會議は踊る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ドイツのオペレッタ映画のなかでも最高傑作といわれる作品。1814年、ナポレオン失脚後のウィーン会議を舞台に、ロシアの賢君アレクサンダー一世の公式行事と、お忍びの恋を軽快に描く。馬車の上で歌う『ただ一度だけ』の移動撮影が映画史上の名場面として記憶される。", "1931-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PAJAMA GAME", "パジャマ・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "G・アボットがブロードウェイで上演して、1000回近い公演で大ヒットしたミュージカルの映画化。アイオワ州のパジャマ製造工場では、賃金を他の工場並みに上げろ、とストライキが始まる。労組の女闘士ベーブは組合を率いて立ち上がるが、ストを収拾させるために送り込まれた若い新工場長のシドとやり合ううち、彼との間に恋が芽ばえ……。原色を多用したきらびやかな画面と、合唱中心のにぎやかなミュージカル・ナンバーでぐいぐいと盛り上がっていく高揚感は素晴らしく、そのノリは1950年代ハリウッド・ミュージカルのなかでも白眉だろう。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMANCE DE PARIS", "パリのロマンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "主人公は母思いの良い息子。だが、シャンソンを歌う時には何もかも忘れてしまうほど。そんな彼の熱意を知ったレビューの監督は、新しい出し物に彼の出番を作り、デビューのチャンスを与える。ドイツ占領下に作られた徹底的に明るいコメディ。1996年に初めて日本公開された。", "1941-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROKEN　JULIE", "令嬢ジュリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "19世紀末、北欧の白夜の短い夜を楽しむ人々。ある者たちは踊り狂い、またある男女は片隅で肉体を絡ませ合っていた。そんな中、伯爵家の一人娘ジュリーは下男のジャンを挑発的な態度で誘い、その夜、彼女はジャンに身を任せてしまう……。滅びようとしている貴族社会を背景に、階級の異なる男女の愛憎と軽蔑、そして悲しい恋の結末を過去と現在を交錯させながら見事に描き、第４回カンヌ映画祭グランプリに輝いた名作。", "1951-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Jane Austen Book Club", "ジェイン・オースティンの読書会", "", "", "http://image.eiga.k-img.com/images/movie/53195/315x210.jpg?2040", 105, "「高慢と偏見」などで知られる作家ジェイン・オースティンの小説を読んで感想を語り合う“読書会”に集った男女6人の人間模様を綴るヒューマン・ドラマ。恋愛、結婚、友情などそれぞれの悩みを抱える彼らが、オースティンの小説を通して自らの人生と向き合う姿をユーモアたっぷりに描き出す。メガホンを取るのは、「SAYURI」「若草物語」の脚本家で今回が長編監督デビューとなるロビン・スウィコード。", "2008-04-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE PACIFIC STORIES", "BLUE PACIFIC STORIES", "", "", "http://image.eiga.k-img.com/images/movie/54735/315x210.jpg?4252", 95, "人気アーティストのAI、土屋アンナ、Microがそれぞれ監督を務め、海をモチーフに製作したオムニバス映画。ロサンゼルスを舞台にしたダンスムービー「TAKE ACTION」（AI監督）、人類滅亡後の地上で暮らす魚たちの世界を描いたファンタジー「フィッシュ・ボーン」（土屋アンナ監督）、成宮寛貴を主演に迎えたサーフィン映画「カモミールの羽」（Micro監督）で構成される。劇中の音楽やエンディングテーマなども、それぞれ監督自身が手がける。", "2009-08-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUTHLESS PEOPLE", "殺したい女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "仕事が大成功し大金持ちになったサムの唯一の悩みは下品でデブの女房バーバラ。そんな折、バーバラがサムを憎む若い夫婦に誘拐され身代金を要求してきた。これ幸いと女房を放ったらかすサムだったが……。バーバラ役のミドラーの怪演が爆笑もののコメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コタンの口笛", "コタンの口笛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "第１回未明文学賞、第５回サンケイ児童出版文化賞を受賞した同名小説の映画化で、監督は巨匠・成瀬巳喜男。アイヌの姉妹が人種差別や貧困にめげずけなげに生きる姿が、橋本忍の構成力のある脚本によりドラマティックに描かれる。成瀬監督としては異色の作品。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前飯店", "喜劇・駅前飯店", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "主人公３人が中国人に扮する。新橋・雷燕飯店の徳と横浜のラーメン屋・孫、それに貿易商の周は仲が良い。だがコックだった周の父親が不老長寿の酒の製造法を書いた文献をどこかに残していると聞いて、彼らは発見に血まなこになる。徳に森繁、孫に伴、周に堺が扮する。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Diary of a Wimpy Kid", "グレッグのダメ日記", "", "", "http://ecx.images-amazon.com/images/I/51YhoqGMNGL._SL210_.jpg", 94, "全世界で累計2400万部を売り上げたジェフ・キニー原作のコミック小説シリーズ「グレッグのダメ日記」を映画化したコメディドラマ。ダメ中学生グレッグ・へフリーと彼の同級生、そして親たちの生態が面白可笑しく描かれる。監督は「ホテル・バディーズ　ワンちゃん救出大作戦」のトール・フロイデンタール。出演はグレッグにザッカリー・ゴードン。グレッグの両親にスティーブ・ザーン、レイチェル・ハリス。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROLLERCOASTER", "ジェット・ローラー・コースター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "遊園地の花形ジェットコースター。それに、爆弾が仕掛けられたことから、犯人と追跡する男たちのスリリングなストーリーが展開する……。小ぶりなパニック映画に、刑事ドラマの要素を加えた娯楽作品。抑えめの追跡劇のなかで、爆破シーンだけは迫力十分。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("となりのボブ・マーリィ", "となりのボブ・マーリィ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "作家としても活躍している俳優の大鶴義丹が初監督した作品。予備校へ通うため東京で一人暮らしを始めたマサミは、ある日、アパートの隣人の黒人と知り合う。彼はボブ・マーリィに似ていて、ボブ・マーリィを崇拝しているマサミは接近していくが、彼が麻薬の密売をしている所を目撃し、ショックを受けるのだった……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER STERN VON AFRIKA", "撃墜王アフリカの星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "第二次大戦中のドイツで、対米空軍との空中戦で撃墜記録を誇ったハンス・ヨアヒム・マルセイユの戦歴、恋、そして墜落死までの生涯を描く。現存するメッサーシュミットが戦闘シーンで使用されている。", "1957-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マメシバ一郎3D", "マメシバ一郎3D", "", "", "http://image.eiga.k-img.com/images/movie/57195/315x210.jpg?3517", 59, "アラフォーのニートと豆柴犬の凸凹コンビが繰り広げる奮闘を描いた「幼獣マメシバ」（2009）の続編。37年間無職の芝二郎は、豆柴犬の一郎と叔父の重男が触れ合う動画「老人と犬」をネットに投稿して話題になり、写真集出版の話まで舞い込んでくるが、そんな評判もおかまいなしに引きこもり生活を満喫していた。しかし、重男が「一郎のお友達探し」としてお見合い話を持ち込み、さらには二郎が一郎を虐待していると勘違いした女性が押しかけてくるなど、徐々に生活がかき乱されていき……。主演は前作に続き個性派俳優の佐藤二朗が務める。", "2012-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("W.E.", "W.E.（原題）", "", "", "http://image.eiga.k-img.com/images/movie/57725/315x210.jpg?3863", 119, "マドンナの監督第2作。1930年代、「王冠を賭けた恋」として世間を騒がせた英国王エドワード8世とその妻ウォリス・シンプソンのロマンスと、現代ニューヨークに暮らすひとりの女性の物語を交錯させて描く。ニューヨークに住む女性ウォリーは、子どもを欲しない夫との夫婦関係に悩んでいた。そんなある日、エドワード8世とウォリスをテーマにした展覧会に足を運んだウォリーは、同じ女性としてウォリスの生き方にひかれていく。しかし、自由奔放ですべてを手にしたと思われるウォリスにも、知られざる苦悩があった。", "2012-10-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　クロサギ", "映画　クロサギ", "", "", "http://image.eiga.k-img.com/images/movie/53295/315x210.jpg?3268", 127, "人気コミックを原作に、詐欺師のみを狙う詐欺師“クロサギ”の活躍を描いたテレビドラマの映画版。父親が詐欺に遭い、一家心中の末にただ1人生き残った青年・黒崎。復讐のためクロサギになった彼は、新たなターゲット・石垣の調査を開始する。しかし石垣は、何者かと共謀して日本経済を揺るがすほどの巨大詐欺を企んでいた。映画初主演となる山下智久らテレビ版のキャストに加え、竹中直人、大地真央ら豪華ゲストが参加。", "2008-03-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("椿山課長の七日間", "椿山課長の七日間", "", "", "http://image.eiga.k-img.com/images/movie/33745/315x210.jpg?4732", 118, "人気作家・浅田次郎の最高傑作と称されるベストセラー小説を「子ぎつねヘレン」の河野圭太監督が映像化した、笑いと涙のハートフル・ドラマ。仕事のことや家族のことなど多くの未練を現世に残したまま突然死してしまった椿山課長。姿を変えることを条件に3日間だけよみがえることを許された彼は、生前は気付くことのなかった家族の想いや愛を知る。主人公・椿山課長に西田敏行、絶世の美女となってよみがえった椿山課長に伊東美咲が扮する。", "2006-11-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Art of Fighting", "ケンカの技術", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「うつせみ」の若手注目俳優ジェヒが主演を務める青春アクションドラマ。工業高校に通う気弱な青年ビョンテは、不良グループに目をつけられてしまい、殴られてばかりの悲惨な毎日を送っていた。そんなある日、図書館で変わり者の中年男パンスと出会ったビョンテは、パンスが実は15年前に全国制覇を成しとげたケンカの達人だと知り、弟子入りを志願。パンスのもとでケンカの技術を学ぶべく奮闘するが……。", "2008-08-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEPT AWAY", "スウェプト・アウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ガイ・リッチー監督がマドンナを主演に据えて、イタリア映画「流されて…」(1974)をリメイク。文明と隔絶した孤島を舞台に、高慢なブルジョワ夫人とたくましい船乗りの青年の情熱的な愛を描き出す。地中海をクルーズ中のアメリカ人女性アンバーが、イタリア人の若い船員ジュゼッペとともに無人島に漂着する。傲慢だったアンバーはジュゼッペを愚弄していたが、いつしか二人の立場は逆転し……。", "2002-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELLO KIDS！ がんばれ子どもたち", "HELLO KIDS！ がんばれ子どもたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "養護施設“ねむの木学園”の園長である宮城まり子が、自ら脚本を書き、監督・製作した“ねむの木学園”記録映画の４作目。今回は、学園の子供たちが“障害を持つ人たちの芸術祭”に招かれてニューヨークへ行き、ハーレムの黒人たちと交流、園生によるミュージカル公演が実現するまでを描く。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIVE CORNERS", "ファイブ・コーナーズ／危険な天使たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1964年、ブロンクスの下町に出所したハインツが帰って来る。彼はペットショップを営むリンダに気があり、昔襲ったことがある。しかし、その時助けてくれたハリーも今や非暴力主義者となっていた。彼女の恋人も身を案じていたが……。ハインツを中心に様々な人間像を浮き彫りにするサスペンス作品。", "1988-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAFT　PUNK　＆　LEIJI　MATSUMOTO’S　INTERSTELLA　５５５５：THE　５TORY　OF　THE　５ECRET　５TAR　５YSTEM", "インターステラ5555", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "人気音楽ユニット“ダフト・パンク”と日本漫画界の重鎮、松本零士のコラボレートによるアニメ作。彼らのアルバム『ディスカバリー』の14曲が14話となり、一つの物語が展開していく。太陽系外の惑星で大活躍する４人のミュージシャンが誘拐された。その黒幕は音楽ビジネス界で名を轟かす大物マネージャー。新たなヒットを狙う彼は４人の肌の色を変え、過去の記憶を奪い取り、最強ユニット“ザ・クレッシェンドールズ”としてデビューさせる。", "2003-01-01", "2,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あなた好みの", "あなた好みの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "花嫁学校を経営する女性嫌いの男と、そのお目付け役として雇われた女性との愛と葛藤を描く歌謡ドラマ。その男、武志は生徒たちに男性上位の教育方針を提示してひんしゅくを買うが、賞品をつけてこれを解決する。一方、お目付け役のえみ子は、武志の父親から彼の花嫁候補の人物調査を頼まれていた。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Frankenweenie", "フランケンウィニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 29, "「バットマン」「シザーハンズ」のティム・バートン監督が長編デビュー前にメガホンをとったモノクロの短編作品。少年フランケンシュタインは、科学の実験をヒントに、交通事故で死んだ愛犬スパーキーを蘇らせることに成功した。しかし、スパーキーの恐ろしい姿に町の人々は大騒ぎをする。日本では、東京と大阪でのみ「ナイトメアー・ビフォア・クリスマス」と同時公開された。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PIED PIPER", "ハメルンの笛吹き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ドイツに伝わる寓話の映画化。中世のドイツが舞台。ペストを逃れてやって来た旅芸人の一座と、途中で仲間入りした笛吹きの若者がハメルンの町にたどり着く……。ドノヴァンの笛の音が美しく、それに操られるネズミ群の特撮も見事なファンタスティック・ポエジー。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOVERS ＆ SHAKERS", "映画の作り方教えます", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "つまらないネタをいかに面白く見せるかと悩むハリウッド映画人を描いたシニカル・コメディ。「ミッドナイト・ラン」でメジャーとなったC・グローディンが財産をなげうって製作。S・マーティンや、「ビッグ」で監督としても評判となったコメディエンヌ、ペニー・マーシャルらが友情出演している。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ごめん", "ごめん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "大阪に住む小学６年生のセイくんは、京都の中２の少女ナオちゃんに一目惚れ。歳の差も、住んでいる距離も何のその、ようやく初デートにまでこぎつけ、不思議な関係を築きあげていくが……。主演二人の初々しくも元気ある存在感が好もしいキッズ・ラブ・ストーリー。相米慎二の愛弟子・冨樫森監督らしく、長回しを始め至るところに相米映画へのオマージュもたっぷり。また、少年の思春期の目覚めが実にリアルかつ繊細に描かれている。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛憎三都錦絵", "愛憎三都錦絵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "戦前に約10本の作品を残す児井英男監督のデビュー作。黒川＝花井のフレッシュ・コンビによる幕末仇討ち道中もの。黒川の気が弱い侍ぶりがなんともおかしいが、作品としては平板な出来である。初公開時の題名は「敵討三都錦絵」であった。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN NINJA", "アメリカン忍者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "米陸軍兵士ジョニーは、基地指令官が裏で兵器密売にかかわっていることを知る。幼少より武術を身につけているジョニーは、上官の操るニンジャ軍団につけ狙われ、対決を迫られるのだった。当時のニンジャ・ブームに乗って製作された、アメリカ人が主人公のアクション映画。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Four Brothers", "フォー・ブラザーズ　狼たちの誓い", "", "", "http://image.eiga.k-img.com/images/movie/1395/315x210.jpg?3524", 108, "養母を殺害された義理の兄弟たち4人が復讐を誓い、その犯人を探し出していく。監督は「シャフト」「ワイルド・スピードX2」のジョン・シングルトン。肌の色が違う4人兄弟を演じるのは「猿の惑星」のマーク・ウォールバーグ、「ワイルド・スピードX2」のタイリース、人気ユニットアウトキャストのアンドレ3000、「プライド／栄光への絆」のギャレット・ヘドランド。サントラはマービン・ゲイらのモータウン音楽が満載。", "2005-11-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRAM STOKER'S DRACULA", "ドラキュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "400年の時を越え、最愛の妻の面影を求めて彷徨うドラキュラ伯爵と、その妻に生き写しの女性ミナ、叶うはずもない二人の恋の行方は……。原作は怪奇小説の古典、B・ストーカーの『吸血鬼ドラキュラ』で、鬼才F・F・コッポラが原典の戦慄とエロスを、大胆な手法と最新技術により現代によみがえらせている。斬新な衣装をデザインしたのは石岡瑛子。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎の休日", "男はつらいよ・寅次郎の休日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "後藤久美子が、マドンナ役を前作に続いて連投した作品。寅の甥、満男の憧れの少女・泉が愛人と同棲している父親を探しに九州へと向かう。これに満男がついて行ったから、“とらや”は大騒ぎに……。女性にフラれることで、共感を覚える伯父と甥という展開は、寅と満男に新たな関係を生み出した。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONSTER IN THE CLOSET", "モンスター・イン・ザ・クローゼット 暗闇の悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "クローゼットの中からしか現れない怪物を退治するため、人々が大騒ぎするパロディ感覚あふれたコミカル・ホラー映画。“毒々”シリーズで有名なトロマ映画の珍品。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("錆びたナイフ", "錆びたナイフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "石原慎太郎の原作を舛田利雄監督と石原裕次郎の黄金コンビによって映画化した、日活アクションの古典的名作。地方都市の市長選挙にからむ殺人事件を３人のチンピラが目撃する。錠は金に困って組織をゆするが逆に殺されてしまう。裕次郎と旭はカタギになって小さな酒場を経営しているが、組織に嗅ぎつけられて旭が殺され、怒りに燃えた裕次郎は組織と対決する。裕次郎がダンプカーで逃げる悪玉のボスを、同じくダンプカーで追うクライマックスは、アクション派・舛田利雄のダイナミックな演出が冴え、悪玉ボス役の杉浦直樹も熱演。また、石原裕次郎と小林旭が初めて本格的な共演をした点でも、歴史的な価値がある作品。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大失恋。", "大失恋。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ベイエリアのアミューズメントパークを舞台に描く、意欲的な恋愛映画。ダブルデートする若者やパークの企画部の女性、観覧車で出会った男女、顔を隠した占い師と女性客、結婚詐欺師と女刑事、誘拐犯の青年と誘拐された少女などをめぐる８つのエピソードが同時進行的に展開していく。。メリハリのきいた設定と、若手スターの豪華かつバランスのとれた配役で、単純なオムニバスを越えた軽快なエンターテインメントに仕上がっている。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dynamic:01: The Best of DavidLynch.com", "ザ・ベスト・オブ・デイヴィッド・リンチ・ドット・コム", "", "", "http://image.eiga.k-img.com/images/movie/54285/315x210.jpg?3599", 120, "「ブルーベルベット」「マルホランド・ドライブ」の鬼才デイビッド・リンチが自ら立ち上げた会員制ウェブサイト“DAVIDLYNCH.COM”にて発表された短編作品。「THE DARKENED ROOM」、「BOAT」、「LAMP」、「OUT YONDER-NEIGHBOR BOY」、「INDUSTRIAL SOUDSCAPE」、「BUG CRAWLS」、「INTERVALOMETER EXPERIMENTS」を上映。", "2009-02-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOLIE｀RE", "モリエール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 235, "17世紀フランスの大喜劇作家モリエールの生涯とその時代を描いた大作。文字通り舞台で死んだモリエールの凄絶な生きざまを、前衛的演劇集団・太陽劇団の主宰ムヌーシュキン女史は、当時の社会、風俗とともにドキュメンタリー・タッチで壮大に再現する。", "1978-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("АРАБЕСКИ НА ТЕМУ ПИРОСМАНИ", "ピロスマニのアラベスク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "パラジャーノフが、ロシア・ナイーブ派の天才画家ニコ・ピロスマニに捧げたオマージュ。1862年にグルジアに生まれ、放浪の生活を送った後に、孤独な生涯を閉じたピロスマニ。映画はグルジア国立美術館にある彼の作品を紹介しながら、絵の中の人物を模した俳優たちがパフォーマンスを繰り広げる。", "1985-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダニエル・シュミットの大野一雄", "ダニエル・シュミットの大野一雄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 15, "舞踏家として独特な世界を作る大野一雄の舞う姿を、スイス人監督のD・シュミットが収めた短編作品。大野のヨーロッパ公演を見たD・シュミットが日本での大野の舞踏を記録することを希望、R・ベルタとともに来日し、撮影した。レインボーブリッジをバックに晴海埠頭で踊るシーンは素晴らしい美しさである。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋極道", "恋極道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "元山口組弁護士、山之内幸夫が実際の事件をモデルに原作を書いた劇画を、「新・悲しきヒットマン」「鬼火」と彼の原作の映像化を手掛けてきた望月六郎が映画化。衰退の一途をたどる組のために体を張って生きている大隈組行動隊長、田坂金一は若頭、松井の頼みでヒットマンを引き受けた。そんな矢先、彼は純粋無垢な女子大生、洋子と出会う。彼女を“神様がくれた最後の天使”と思い込んだ田坂はいつしか彼女とあてもない逃避行を始めていた……。いつしか訪れるであろう洋子との別れを感じながら、日々の糧を得るために覚せい剤の密売に手を染め、不安を消すためにバクチに興じる中年ヤクザ、田坂。彼と、いつまでも二人で暮らすことを願う洋子が繰り広げる逃避行と愛の日々を描く。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あぜ道のダンディ", "あぜ道のダンディ", "", "", "http://image.eiga.k-img.com/images/movie/55665/315x210.jpg?2234", 106, "妻に先立たれて15年になる50歳の淳一は、自分がガンではないかという疑いを抱く。2人の子どもたちには弱音を吐けない淳一は、中学時代からの親友・真田に胸の内を明かす。さえない中年の男やもめが、見栄を張りながらもダンディズムを貫き、一生懸命に生きる姿を描く。日本映画界の名脇役・光石研が、デビュー作「博多っ子純情」（1978）以来約32年ぶりに主演を果たした。共演に田口トモロヲ、監督は「川の底からこんにちは」で注目を集めた石井裕也。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポテチ", "ポテチ", "", "", "http://image.eiga.k-img.com/images/movie/57065/315x210.jpg?3701", 68, "人気作家・伊坂幸太郎の同名中編小説（新潮文庫刊「フィッシュストーリー」所収）を中村義洋監督、濱田岳主演で映画化。同じ年、同じ日、同じ街で生まれたプロ野球のスター選手・尾崎と、空き巣を生業とする凡人の今村が運命に翻ろうされながらも、家族や恋人を巻き込んで目に見えない強い絆によってつながれていく姿を描く。舞台は伊坂作品おなじみの仙台。音楽は、同じく伊坂原作×中村監督作品の「フィッシュストーリー」「ゴールデンスランバー」も担当した斉藤和義。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Calamari Union", "カラマリ・ユニオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "フィンランドのアキ・カウリスマキ監督が贈る、クレイジーなロードムービー。全員の名前がフランクという15人の男たち。カラマリ・ユニオン（イカ墨同名）のメンバーである彼らは、退屈な街を捨て、街の反対側にあるという理想郷エイラを目指して旅立つ。不思議な男たちの旅を、ロックテイストをちりばめて皮肉たっぷりに描いたこの作品は、カウリスマキ監督の代表作「レニングラード・カウボーイズ」シリーズの原点ともいえる。", "1985-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARPER", "動く標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "ハードボイルド作家L・マクドナルドの同名小説を、P・ニューマンの主演で映画化。ニューマンがタフな探偵に扮し、欲に絡んだ大富豪の失踪事件を解決していく。原作で“リュー・アーチャー”だった探偵の名前は、ニューマンの意向で“ルー・ハーパー”に改められた。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("孫中山／DR.SUN　YAT−SEN", "孫文", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "中国革命の父といわれる孫文の生誕120年を記念して製作された歴史大作。祖国の改革のために闘った彼の波乱の半生を、日中混合のスタッフ、キャストで描き出す。清朝末期の1895年。孫文は人々を貧困から救うため、広州で蜂起するが失敗。その後、華僑や亡命先の日本で宮崎滔天、犬養毅らの支持を得て、1911年、辛亥革命で勝利を収める。中華民国発足の際には臨時大統領に就任するも、すぐに袁世凱に地位を奪われ、再び日本に亡命。仲間の裏切りに遭いながらも国民党を改組し、革命のさなかの1925年、ガンで倒れる。", "1986-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SALMER　FRA　KJOKKENET", "キッチン・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1950年代初頭のノルウェーを舞台にした不思議な味わいのドラマ。田舎で一人暮らしをしている老人の家に、独身男性の台所での生活ぶりを探る調査員がやってくる。“いかなる交流もしてはならない”という規則のもと、監視する者とされる者の間に友情が芽生えていく様を綴る。B・ハーメル監督の斬新なアイデアと素朴な語り口に魅了される１本。", "2003-01-01", "36,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・百点満点", "喜劇・百点満点", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "森繁久彌の芸能生活40周年記念映画。かつて“社長”シリーズや“駅前”シリーズで活躍したスターたちが総出演し、演出も“社長”シリーズのベテラン・松林宗恵である。三流予備校校長と出来の悪い生徒たちとの交流をユーモラスに描いている。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LILO　＆　STITCH", "リロ＆スティッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "宇宙からやって来たエイリアンと女の子の交流を描いたディズニー・アニメ。凶暴さを次第に失い、愛を学んでいくエイリアンスティッチのキャラクターに加えて、水彩画を導入したソフトなタッチの背景など、映像も温かく魅力的だ。ハワイで姉ナニと暮らす５歳の少女リロ。ナニは、孤独な彼女のために犬を飼うことにするが、リロが気に入ったのは犬ではなく、なんと６本足の怪生物。その正体は、実は遺伝子操作によって生まれたエイリアンだった。スティッチと名づけられたが、彼は破壊しか知らない生物で……・。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRONACA FAMILIARE", "家族日誌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "運に見離されたように生きる貧しい兄弟の足跡を、淡々と追っていく。植字工をしながらジャーナリストを目指す兄エンリコと、体力も意志も弱い弟ロレンツォの兄弟二人の青春は、あまりにも悲しいもので、人生の重さを感じさせずにはおかない。", "1962-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMOUREUSE", "愛されすぎて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "フランソワ・トリュフォーの名作「突然炎のごとく」の原作となったアンリ・ピエール・ロシェの小説を基に、J・ドワイヨン監督が自由に翻案した恋愛ドラマ。同じ年頃のアントワーヌを恋人に持つジャーナリストのマリー。映画監督のポールからインタビューを受けた彼女は、まさにその日、彼と恋に落ちる。だが、同棲しているアントワーヌとも別れることができない彼女は、ポールとアントワーヌ、二人の男の間で揺れ動き続ける。閉じられた空間での登場人物たちの心の葛藤を描き続けてきたドワイヨンは、それを恋愛の閉塞状況での葛藤へとあざやかに置き換えてみせる。マリーを間にはさみながら葛藤をあらわにしていく男たち。極限状態にある剥き出しにされた恋愛の様々な表情が、ドワイヨンの確かな演出により、絡めとられている。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIVATE LESSONS", "プライベイト・レッスン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "S・クリステルが妖艶な魅力を発揮する、エロティックでコミカルな青春映画。メイドとして雇われた美しい女性が、性に興味津々の15歳の少年に“秘密のレッスン”を施す。だが、その裏には大金をめぐる陰謀が……。1993年には日米合作で、SMAPの稲垣吾郎、ジョアンナ・パクラを主演に「プライベート・レッスン」のタイトルでリメイクされた。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEVER DIE ALONE", "ネバー・ダイ・アローン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "獄中で執筆した小説で衝撃的なデビューを飾り、ブラック・カルチャーの担い手と評判を呼びながらも1974年に何者かに射殺されたドナルド・ゴインズ。悲劇的な最期を遂げた伝説の犯罪作家が遺した同名小説を、人気ラッパーのDMXの主演で映画化。犯罪まみれの人生にケリをつけるべく故郷に帰ってきた主人公キング・デビッドの人生を、まるでストリートの神話を語るように描き出したのは、スパイク・リー作品の撮影監督としても知られる鬼才E・ディッカーソン。黒人ラッパーが主演するアクション映画が量産される昨今にあって、あえて硬派な人間ドラマに仕上げている。筋金入りのワルだったキング・デビッドが故郷の町に帰って来た。しかし裏組織の殺し屋に撃たれ、病院まで運んでくれた白人のポールに全財産を譲って死亡する。ポールはデビッドが自らの極道人生を吹き込んだテープを発見し……。", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KITTY FOYLE", "恋愛手帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "もっと上流の生活を夢みる働き者の娘が、婚約中の医者を捨てて富豪の息子との結婚を決意するが、つき合ってみれば軽薄な男。しかし彼と別れた時はすでに妊娠中だった。そして、死産の後、医者との愛こそ真実と知る。ダンス映画で鳴らしたロジャースの見事な演技派変身作。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("代行のススメ", "代行のススメ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "運転代行業を営む家族の心の交流をつづったハートウォーミング・ドラマ。小学校で代理教師として働く木村カヨは、任期を終えて子どもたちと離れ離れになると同時に、前の彼女と復縁するという夫と離婚して実家に戻ることに。もう誰かの代わりになどなりたくないと願うカヨだったが、病に倒れた母親の代わりに家業の運転代行業を手伝うことになる。2009年7月に死去した名優・山田辰夫の最後の主演作となった。", "2009-10-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四畳半襖の裏張り", "四畳半襖の裏張り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "永井荷風の作といわれる『四畳半襖の下張り』を神代辰巳が監督した日活ロマンポルノ。密室で展開する男女の色ごとにカメラを据えながら、そこから大正という“時代”を浮かび上がらせようとした。その点では密室でセックスを繰り広げる男女から、戦争に突入していく昭和の時代を照射した、田中登の「実録阿部定」や大島渚の「愛のコリーダ」との類似点を見いだすことも可能だ。ストーリーは、芸者・袖子とその客・信介の情事、花枝と花丸の主従、芸者・夕子とシベリア出兵を控えた恋人との情事の３本が、並行に描かれている。大正時代の風俗描写も的確。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bad Boys", "バッドボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "性格が正反対の刑事二人組の活躍を描いたアクション映画。犯罪都市マイアミである夜、警察内部に保管されていた１億ドル相当の麻薬が跡形もなく消えるという市警始まって以来の窃盗事件が発生した。マスコミに悟られずに72時間以内に奪還しなければならないという任務を受けたマーカスとマイクのコンビは、捜査に乗り出すのだが……。ダイアナ・キングの『シャイ・ガイ』をはじめ、1990年代の豪華ヒット曲が満載。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビタースイート", "ビタースイート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "結婚間近の若い女と、ぼんやり離婚を考えている中年男。男がシェフを務めるリストランテで唐突に結ばれた二人が、それぞれの岐路に立つ姿をじっくり見つめた一編。かつて望月六郎の「スキンレスナイト」で名演を見せた石川KIN(均)が深みあるキャラクターを体現し、またその妻役の故・林由美香が過去の過ちを真っ直ぐに受け止める女を好演している。役所に婚姻届を取りに行ったその日の夜、祥子は衝動的にイタリアンのシェフ、工藤と体を重ねる。工藤には妻、恵子がいたが、彼女を親友、吉田から奪った過去にさいなまれていた。", "2005-12-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無垢なモノ my simple things", "無垢なモノ my simple things", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ヤクザの男と自閉症の青年が織りなす心の交流を描いた舞台「無垢なもの」を映画化したヒューマンドラマ。敵対組織の組長の襲撃に失敗し、追われる身となってしまったヤクザのアーちゃん。深手を負って街をさまよっていたアーちゃんを助けた自閉症の青年まさしは、意識がうつろなアーちゃんを背負って自宅へ連れて帰ることに。アーちゃんは純真無垢なまさしと接するうちに、今まで忘れていた大切な何かを思いだしていく。", "2007-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どぶ", "どぶ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "終戦直後の横浜・鶴見川のほとりの湿地帯に建ち並ぶバラック作りの部落を、新藤兼人が実際に取材し映画化した作品。河童沼のルンペン部落に住む失業中の頭の弱い女、競輪狂の男、労働争議そっちのけの工員、新興宗教にすがる老婆、元新劇の名優だったと自称する男たちの人間模様を描く。「どん底」を思わせる美しい映画。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホラ吹き太閤記", "ホラ吹き太閤記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "戦国時代を無責任時代と見たて、木下藤吉郎（後の豊臣秀吉）をホラ吹き無責任男に設定した時代劇コメディ。主演は当然、植木等。“ブワッーと行こうぜ、ブワッーと”というかけ声とともに、あれよあれよと昇進していく藤吉郎が痛快。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EDGE OF THE CITY", "暴力波止場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ニューヨークの波止場へと流れて来た過去を持つ男が、自分の過去に打ち勝つまでを描いたアクション・ドラマ。波止場の人足たちを牛耳る横暴な男との確執、そして黒人の組頭との友情など、これが映画初監督となるM・リットの演出が随所に冴えを見せる。主演の青年を、J・カサヴェテスが好演。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("御金蔵破り", "御金蔵破り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "大川橋蔵と片岡千恵蔵共演による石井輝男監督初の時代劇。牢内で知り合った旗本くずれのヤクザ・半次と老盗賊・富蔵は、江戸城内の御金蔵破りを計画するが……。ジャン・ギャバンとアラン・ドロン共演の「地下室のメロディー」を下敷きにした作品で、石井監督お得意のハッタリのきいた趣向も用意されている。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE PARTY２", "ハウス・パーティ～パジャマでシェイクヒップ！～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "町の教会の奨学金で大学入学の念願がかなった黒人青年、キッド。ところが、彼とのデュオでレコードヒットを夢見る友人のプレイが、入学金の小切手を他人の手に渡してしまう。キッドは、やむなくアルバイトを始めるが、なかなか金はたまらない。そこで一獲千金を狙って、ラップミュージックでノリノリのキャンパス・パーティーを企画するのだが……。ラップデュオ、キッドゥン・プレイを主演の二人に起用。ファンキーなラップサウンドそのものの、キレのあるスラップスティック・コメディ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMISTAD", "アミスタッド", "", "", "http://ecx.images-amazon.com/images/I/51pGM2udTAL._SL210_.jpg", 155, "スピルバーグが奴隷解放宣言の24年前、1839年に起きた“アミスタッド号事件”を克明に映画化した歴史社会派作品。スペイン人に奴隷として買われ、ラ・アミスタッド号で運ばれていた53人の黒人たちが、自分たちが殺されるという噂を信じて反乱を起こし、船員たちを殺して船を乗っ取る。それから２ヵ月後、船はアメリカの沿岸警備隊に捕まり、黒人たちはアメリカで海賊行為と殺人の罪で裁判を受けるのだった。だが、スペイン船籍の船だったことからスペイン女王が彼らの返却を希望、生き残った船員たちは自分たちの奴隷だと訴え、沿岸警備隊は謝礼として奴隷たちの所有権を主張する……。人権を無視して繰り広げられる裁判の愚かさと空しさを描くとともに、その中で生まれる若き弁護士と黒人リーダーのささやかなコミュニケーションを、史実に沿って感動的に綴っていく。最終的に黒人たちを救う元大統領ジョン・クインシー・アダムズに扮したA・ホプキンスの熱演が印象的。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Landlord", "真夜中の青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "　大富豪の息子エルガーは黒人が暮らすスラム街のアパートの家主になる。住人たちを理解しようと懸命になるエルガーは、やがてダンサーのレイニーに恋をする。一方、住人のコピーが逮捕され、その妻ファニーを慰めていたエルガーは彼女と関係してしまう。その後、コピーは釈放されるが、ファニーの妊娠が発覚し……。スラム街での経験によって成長していく青年の姿をユーモラスに描いたドラマ。「夜の大捜査線」でアカデミー編集賞を得たハル・アシュビーがノーマン・ジュイソンの代理で初監督にあたった。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MATADOR", "マタドール　炎のレクイエム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "事故のため引退してからも、死への緊張感が忘れられない元闘牛士は、セックスの最中に相手の女性を殺すことに喜びを得ていた。ふとしたことで知り合った女性弁護士に心惹かれるが、実は彼女にも同じような性癖があった。“性と死の快楽”をセンセーショナルに描いたラブ・サスペンス。スペインの奇才P・アルモドヴァルにとって、これが日本初公開作に。ビデオ発売時タイトルは「マタドール」。", "1986-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まだ楽園", "まだ楽園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "自主映画ながら各方面で高い評価を受け、一般公開の運びとなった注目作。幼い頃に殺された母親の眠る故郷へと旅立った雄二と友人の俊。彼らはさまざまな人物に遭遇しながら、雄二の故郷へたどり着く。俊はある女と出会い、一方の雄二はある目的のための機会を窺っていたが……。果てしなく続くかのような旅に出る主人公たちに、ほぼ素人の役者を起用。それゆえに醸し出される強烈な存在感が、確かな余韻を残す。", "2006-05-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BURNING SECRET", "ウィーンに燃えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "戦火の臭いもまだ消えやらぬ1919年のウィーン。山里の療養所に喘息の息子を抱えたアメリカ大使夫人がやって来る。小さい息子は療養所の男爵とすぐに仲良しになったが、夫人は男爵に燃えるような感情を抱き、やがて少年は大人のずるさ、したたかさに徐々に気付いていく。全編に流れる情感あふれる音楽が素晴らしい、A・バーキンの長編映画監督第１作。", "1988-01-01", "3,1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Marcello, una vita dolce", "マルチェロ・マストロヤンニ　甘い追憶", "", "", "http://image.eiga.k-img.com/images/movie/34035/315x210.jpg?1368", 102, "没後10年を迎えたイタリアの名優、マルチェロ・マストロヤンニの素顔に迫るドキュメンタリー。フェデリコ・フェリーニやルキノ・ビスコンティといった巨匠たちと組んで活躍し、スクリーンの外でも大女優たちと甘い日々を過ごしたマストロヤンニ。少年のような純粋さを持つ彼の真実の姿を、母親の異なる娘バルバラとキアラや名だたる映画人たちへのインタビュー、さらに貴重な未公開映像などを交えながら映し出していく。", "2007-06-30", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rumba", "ルンバ！", "", "", "http://image.eiga.k-img.com/images/movie/55495/315x210.jpg?1294", 77, "ベルギーで道化師として活動していたドミニク・アベル＆フィオナ・ゴードン夫妻による長編監督第2作。ベルギーの田舎町で暮らす小学校教師の夫妻ドムとフィオナは、休日にはダンスをたしなむなど幸せな人生を送っていたが、ダンス競技会で優勝した帰りに自殺願望の男を避けようとして大けがをする。記憶を失ったドムと左足を失ったフィオナはやがて学校もクビになってしまい……。「アイスバーグ！」と同時上映。", "2010-07-31", "37,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD AND　WINE", "ブラッド&ワイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "欲望と憎悪が絡み合う、義父と息子の対決を描いたサスペンス。放蕩なワイン・ディーラー、アレックスは宝石強盗に成功。ところが、盗み出した高価なネックレスを妻が持ち出してしまった。彼女を事故死させたアレックスに、彼を嫌っていた義理の息子ジェイソンは怒りを抱いて立ち向かい……。硬派なドラマを、ワインや宝石などの小道具を散りばめたスタイリッシュな語り口で演出。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やま猫作戦", "やま猫作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "広大な中国大陸で北支最前線を死守する大田原部隊は、竜ゲリラ隊の襲撃に手を焼いていた。そんな折、平良少尉がゲリラに捕らえられ、一色中尉と若葉少尉が救助のためにゲリラ砦に潜入する。彼らが見た強敵・竜将軍の正体とは、日本軍に援助を続けていた、親日家の李洪白だった……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rodeo Drive〈ロデオドライブ〉", "Rodeo Drive〈ロデオドライブ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "自主映画シーンで注目を集めてきた新鋭、加納周典の手による犯罪群像劇。もつれいく人間模様をとらえたスリリングな展開に加えて、暴走するチンピラを演じた北村一輝の怪演も見もの。しがない探偵ミノルは家出したヤクザの娘を捜索することに。何とか見つけ出した彼だったが、ヤク中でトラブルメーカーのチンピラ、ケンと出くわしたことから、災難に巻き込まれていく。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANHATTAN BY NUMBERS", "マンハッタン・バイ・ナンバーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "イラン映画界にあって国際的に評価の高い作品を撮り続けた後、アメリカに移住したA・ナデリがマンハッタンをさすらう男の一日を追ったロード・ムービー。クリスマスを目前に首になった新聞記者のジョージは、音信不通の友人を捜してハーレム、タイムズ・スクエア、ウォール街とマンハッタンを北から南へと歩き続ける。ジョージの姿を追いながら、カメラはマンハッタンにあふれる様々な人種の様々な生活を映し出していく。マンハッタンに生活する人たちのリアルな息づかいが画面から伝わってくる、ちょっと変わった作品。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLE´MENT", "なぜ彼女は愛しすぎたのか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "30歳の独身女性が13歳の美少年との恋に溺れていく。かなり危うい設定の物語を通して、恋愛の本質をあぶり出した作品。といっても主人公が17歳も年下の男の子に心奪われ、ストーカーじみた行動にまで走る理由を探った精神分析映画ではない。生々しい質感のデジカム映像で映し出されるのは、抜き差しならない状況に転落し、時には大人びた少年にほんろうされるヒロインの破滅的なもがき。しかもその恋愛地獄に堕ちる主人公を、監督のE・ベルコ自身が演じている。恋愛映画の本場たるフランスでも、これほどショッキングな作品は滅多に作られないだろう。恋人との関係も仕事も順調だが、なぜか満たされない30歳のマリオン。ある日、彼女は親戚の誕生日パーティーで13歳の少年クレマンと出会う。二人は年の差を超えて惹かれ合い、やがて体の関係を持つようになるのだが……。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRAINDEAD", "ブレインデッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "マザコン青年ライオネルはいつもママの言いなり。ある日、呪われた珍獣ラットモンキーに噛まれたママは次第にゾンビに姿を変えていく。暴れ出したママの犠牲者もまたゾンビと化していき、ライオネルはゾンビ集団との対決を決意する……。ラスト20分間に映画史上最多(？)の血しぶきが飛び散るカルト・ホラー。1993年のアヴォリアッツ国際ファンタスティック映画祭でグランプリを受賞。", "1992-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASANOVA BROWN", "クーパーの花婿物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "G・クーパーには珍しい、艶笑コメディ編。結婚間近の内気な大学教授ブラウンは、別れた愛人が自分の子供を生んだと聞いてびっくり。しかも彼女は子供を養子に出すつもりらしい。焦った彼は、その子を誘拐して涙ぐましい父性愛を発揮する。が、それは、３人の女をめぐるややこしい騒動の始まりだった……。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Amants du Pont-Neuf", "ポンヌフの恋人　ニュープリント版／HDリマスター版", "", "", "http://image.eiga.k-img.com/images/movie/55905/315x210.jpg?1398", 125, "フランスの鬼才レオス・カラックス監督が、「ボーイ・ミーツ・ガール」「汚れた血」に続く“アレックス3部作”完結編として1991年に手がけた名作ラブストーリーのニュープリント版。パリのポンヌフ橋で暮らす天涯孤独な大道芸人アレックスは、失明の危機と失恋による心の傷に絶望する女子画学生ミシェルと出会う。2人は恋に落ちるが、ミシェルには両親から捜索願いが出されていた。", "2011-01-29", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("病院はきらいだ", "病院はきらいだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "記録映画の第一人者として知られる女流監督・時枝俊江の一作で、自宅で暮らしたいと願うお年寄りにこたえようとする老人福祉の形態を追ったドキュメンタリー。老人の在宅ケアを実施している長野県、佐久総合病院を舞台に、老人たちと、その世話をする地域スタッフの姿を映し出した問題作。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEUTE NACHT ODER NIE", "今宵かぎりは…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "年に一度のネポムークの祭り。この日を祝ってボヘミアの貴族たちは一晩かぎり、召使と主人の役割を交換する。ひなびた城館の広間で、テーブルにつく召使たち。給仕役は主人たちだ。やがて旅芸人の演目が始まる。甘くノスタルジックな恋の歌、官能的なサロメのダンス。バイオリンの音に合わせて、召使と主人たちが広間を行き来し、混じり合う。感情ぬきの無言の交換。そしてすべては再び元に戻っていく。緩慢な動きに終始する登場人物たちを、据えっ放しのカメラで捉え続ける一貫した演出スタイルが、観る者を夢魔的な時空に運び去り……。スイスの異才シュミットのデビューを飾る、倒錯美の起爆剤。", "1972-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("茜色の約束　サンバ do 金魚", "茜色の約束　サンバ do 金魚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL NIGHT LONG", "恋のドラッグストア・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "社長を殴ったため降格され、終夜営業の大型ドラッグストアで働くようになったまじめな中年男ジョージ。やがて彼はささやかではあるが、小さな幸福を家族の中に見いだし、人生の意味や本当の愛情を知るようになる。ジョージを演じるG・ハックマンに加え、その妻役にはB・ストライサンドという豪華なキャスティングによるヒューマンなドラマ。フランス語教師役として、A・ジラルドも顔を見せる。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O Brother, Where Art Thou？", "オー・ブラザー！", "", "", "http://image.eiga.k-img.com/images/movie/1135/315x210.jpg?1735", 108, "ジョージ・クルーニーを主演に、30年代のミシシッピーの片田舎で故郷を目指す3人の脱獄囚の旅を描く、コーエン兄弟版「オデュッセイア」。撮影は「バートン・フィンク」以来彼らと組む名手ロジャー・ディーキンス。音楽は60年代から活躍してきたアメリカポピュラー音楽界の大御所T・ボーン・バーネットが担当。ゴールデングローブ賞主演男優賞受賞、アカデミー賞の脚色賞、撮影賞ノミネート。", "2000-10-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("再会", "再会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "身寄りのない姉弟である葉子と賢の別離の悲しみと、それを乗り越えて新たな旅立ちへと向かう青春ドラマ。母の代わりであり恋人でもあった姉と別れ、不安な先行きを自分の足で歩み始める青年の揺れる心理を、抒情を込めて描いている。とりわけ横浜港にロケした別れのシーンが秀麗。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い鯨と白い蛇", "赤い鯨と白い蛇", "", "", "http://image.eiga.k-img.com/images/movie/33755/315x210.jpg?4741", 102, "テレビドラマの演出家として数々の名作を手掛けてきたせんぼんよしこが、78歳にして映画監督に初挑戦。孫娘と共に息子夫婦の家に向かっていた雨見保江は、その途中、戦時中に疎開していた館山の古い民家を訪れる。そこには家主の女性・光子が小学生の娘と2人で、失踪した夫を待ちながら暮らしていた。さらに以前この家で暮らしていたという美土里もやって来て……。偶然集まった世代も境遇も異なる5人の女性たちの人生を丁寧に描き出す。", "2006-11-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ライフ・イズ・デッド", "ライフ・イズ・デッド", "", "", "http://image.eiga.k-img.com/images/movie/57615/315x210.jpg?2660", 93, "ウィルスに感染しゾンビ化していく青年の日々を描いた古泉智浩の同名コミックを実写映画化。感染するとゾンビになってしまうアンデッド・ウィルス（UDV）が世界中に蔓延した近未来。日本のある地方都市に暮らす赤星逝雄も、高校卒業間際にUDV感染してしまい、そのせいで就職もできずニートになる。感染者に対する社会の扱いもひどく、ストレスで逝雄の症状はますます悪化し、ゾンビ化が進行していく。両親や妹、恋人や友人らも逝雄のために尽力するが……。主人公・逝雄を演じるのは若手俳優集団「D2」の荒井敦史。ヒロインは沖縄出身の若手女優ヒガリノ。", "2012-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD MAX２", "マッドマックス２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "舞台を近未来に設定したSF力作。中東の産油地帯が戦争で消滅、パニックに陥った地上の戦いが描かれている。石油が金よりも、命よりも価値のある世界で、人々は石油を得るため殺し合いを続ける。第１作で暴走族に最愛の妻子を殺されたマックスは、砂漠に孤立する精油所の存在を知る。彼は単身そこに殴り込み、脱出を望む人たちの手助けをする。見どころは残忍な暴走族とマックスの対決。彼にとっては片目を失うほどの激戦が展開する。世紀末のペシミズムに彩られた作品。ドライなオーストラリアの風土を感じさせる、バイオレンス映画だ。アヴォリアッツ国際ファンタスティック映画祭で、グランプリを受賞した。", "1981-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙からのメッセージ", "宇宙からのメッセージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "太陽系から200万光年の彼方にある惑星ジルーシアが、ガバナス人の侵略を受けて危機に瀕し、長老の孫娘・エテラリーダは地球から８人の勇者を連れて戻ってくる。しかし、ガバナスの要塞を破壊するには、惑星自体を爆破するしか方法がなかった……。「スター・ウォーズ」を意識した和製SFで、深作監督の異色作。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEADLY COMPANIONS", "荒野のガンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1960～70年代、スローモーションを使ったバイオレンス・シーンなど、独自の映像美を作り上げたS・ペキンパー監督。TVシリーズ“ガン・スモーク”や『ライフルマン』を手掛けたあと、映画に進出、これが劇場映画デビュー作である。主人公が復讐相手を捜し当て、その相手とともに強盗団を作り、やがては復讐のチャンスを狙う……。まさにペキンパー好みのストーリー。ビデオ発売時タイトルは「ワイルド・リベンジ 復讐の荒野」。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こちら55号 応答せよ！危機百発", "こちら55号 応答せよ！危機百発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "それぞれ欽ちゃんと二郎さんと呼ばれたお笑いコンビ、コント55号が主演した超絶コメディ。本作は野村監督が自身の名作「張込み」をパロッた遊び心も披露した。初恋の相手が忘れられない萩本欽助と、その初恋相手と結婚した蟹形平次郎が刑事コンビとなりヤクザ抗争の珍事件を捜査する。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EUREKA〈ユリイカ〉", "EUREKA〈ユリイカ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 217, "九州の地方都市でバスジャック事件が発生し、運転手の沢井、小中学生の兄妹、直樹と梢だけが生き残る。その２年後、町に戻ってきた沢井は、兄妹と彼らの従兄である秋彦とともに共同生活を始める。しかし地元を騒がせる連続殺人事件の容疑が沢井に向けられ、彼ら４人はバスに乗って町を旅立つのだった……。モノクロ、シネマスコープ、上映時間３時間37分という商業性を度外視したフォーマットによる青山真治の長編第７作。バスジャック事件の悪夢から逃れられない人々の旅を通して、彼らの心の再生を描くと同時に生きることの意味を探求していく。本作で採用されたクロマティックB&Wという特殊なモノクロ映像は、白黒のネガフィルムをカラーポジにプリントしたもの。田村正毅のカメラによる北九州の荒涼とした風景が、登場人物の壮絶な内面的葛藤と呼応し、観る者を混沌とした精神世界へと引き入れる。奇しくも映画完成後に現実のバスジャック事件が発生し、本作が注目を浴びる一つのきっかけとなった。ラストの一部はカラーになっている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPLENDOR", "生活への道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "名門とは名ばかりで、すっかり落ちぶれている上流家庭を舞台に、夫と妻が、互いの不倫を黙認するという、奇妙なシチュエーションで描く恋愛心理劇。不倫が、名門家庭の生活を守るという異常な環境の家族が織り成すドラマだが、やがて妻の不倫をネタに脅迫する人物が現れ、一家は窮地に立たされる。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sympathy for Lady Vengence", "親切なクムジャさん", "", "", "http://image.eiga.k-img.com/images/movie/1485/315x210.jpg?4312", 114, "「復讐者に憐れみを」「オールド・ボーイ」に続くパク・チャヌク監督の“復讐3部作”完結編。「JSA」の美人女優イ・ヨンエ演じるクムジャは、幼い娘を人質にとられて自分がやっていない幼児誘拐殺人の罪を認め、13年間服役することに。彼女は刑務所で誰にでもやさしく接して“親切なクムジャさん”と呼ばれるようになるが、自分に罪を負わせた真犯人への復讐を決意していた。共演は「オールド・ボーイ」のチェ・ミンシク。", "2005-11-12", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Proxima Salida", "今夜、列車は走る", "", "", "http://image.eiga.k-img.com/images/movie/53525/315x210.jpg?1526", 110, "鉄道の民営化により多くの人々が職を失った90年代のアルゼンチン。とある小さな町でも路線の廃止が決定し、労使交渉にあたっていた組合代表が自ら命を絶った。彼の兄カルロスと4人の仲間たちは、補償金と引き換えに自主退職を迫られながらも抵抗を続けていたのだが……。監督・脚本を手掛けるのはアルゼンチンの新鋭ニコラス・トゥオッツォ。出演は「トーク・トゥ・ハー」のダリオ・グランディネティほか。", "2008-04-12", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大暴れ五十三次", "大暴れ五十三次", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "江戸で評判の義賊・鼠小僧次郎吉と、北町奉行、大見雅楽頭の息子・銀八郎。にせの鼠小僧が捕らわれるまで江戸を離れた次郎吉と、遊興三昧がたたって父に勘当された銀八郎は箱根の関所でバッタリ遭遇。すっかり意気投合した二人は弥次さん、喜多さんと名前も改め、気ままな旅に出る。マキノ監督の道中もので、1938年の「弥次喜多道中記」のリメイク。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bridge／この橋の向こうに", "Bridge／この橋の向こうに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "2003年初の劇場作品として公開された「Rodeo Drive〈ロデオドライブ〉」の新鋭、加納周典監督が、「ウルトラマンコスモス」の市瀬秀和など若手俳優たちを起用して撮り上げたロード・ムービー。失踪した恋人を捜すために旅に出た男とその友人が、様々な人々との出会いを通じて自分たちの人生を振り返る過程を、若手俳優らの自然体の演技で綴られる好編ドラマだ。朝起きると恋人の薫がいないことに気づいた満作は、親友の雛子に相談する。雛子は友人のひかりを呼び、３人で話し合う。そして、彼らが出した結論は、薫を捜しに旅に出ることだった。やがて、３人は捜索の旅に出るが……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("波", "波", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "『朝日新聞』に連載された山本有三の小説を中村登が脚色・監督している。見並行介は芸者になった昔の教え子が仕事に堪えきれず逃げ込んできたのを、いじらしく思い結婚する。しかし間もなく彼女は違う男に誘惑され、家出してしまう。新聞小説が原作だけに、次から次へと主人公たちのすれ違い結ばれていく恋愛模様が語られていく。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOW THE WEST WAS WON", "西部開拓史", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 166, "1830年代から半世紀にわたる西部開拓史を叙事詩的に５つのエピソードで綴ったシネラマ大作。インディアンの襲撃、ならず者と保安官の対決、野牛の暴走など、西部劇ならではのダイナミックなアクションが全編に展開される。３人のベテラン監督がオールスター・キャストで描いた娯楽作。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マタギ", "マタギ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "秋田の自然とともに暮らす、滅びいく狩猟の民・マタギの生活の細部を、巨大熊への復讐劇という形を装って再現した人文地誌的作品。後藤俊夫はこれが第２回監督作だが、師・山本薩夫ゆずりのガッチリとしたカットの積み重ねで手堅くまとめている。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAR AND AWAY", "遥かなる大地へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "すべての人に夢と希望を与える未来の地として誕生した19世紀のアメリカ。ヨーロッパを中心に多くの人々が移り住んだが、ジョセフとシャノンという若い男女もそんな中の一組だった。二人は何のあてもなく、ただ自分たちの力を信じてアメリカに渡って来たが……。大国誕生へ向けて歩み始めたアメリカを背景に、激しく生きた男女の姿を綴るアドベンチャー・ラブロマン。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Good, the Bad, the Weird", "グッド・バッド・ウィアード", "", "", "http://image.eiga.k-img.com/images/movie/54665/315x210.jpg?4154", 129, "日本による朝鮮統治が行われていた1930年代。満州（現在の中国東北地方）の荒野を舞台に、金のためなら手段を選ばない凄腕の賞金稼ぎ、プライドの高い冷酷なギャングのボス、間抜けだがしたたかなコソ泥の3人が、日本軍の残した1枚の宝の地図をめぐって争奪戦を始める。マカロニ・ウェスタンの名作「続・夕陽のガンマン」にインスパイアされて製作された韓国製ウェスタンで、人気俳優ソン・ガンホ、イ・ビョンホン、チョン・ウソンが主演。監督は「反則王」のキム・ジウン。", "2009-08-29", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("芸者VS忍者", "芸者VS忍者", "", "", "http://image.eiga.k-img.com/images/movie/53885/315x210.jpg?3404", 80, "「お姉チャンバラ」などのアクション監督を手掛けた小原剛が初メガホンを取った時代劇ソードアクション。密かに守り継がれてきた山部の剣の継承者である芸者・山部琴乃は、父の敵・片桐兵衛を追う旅に出る。女忍者率いる刺客たちとの死闘の末、ついに片桐の元に辿り着いた琴乃は、片桐の口から衝撃の事実を聞かされる。主演はオーディションで2000人のアクション女優の中から選ばれた期待の新人・佃井皆美。", "2008-09-27", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DISTANT THUNDER", "ラルフ・マッチオ　ディスタント・サンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ベトナム戦争で心に傷を負った男たちが帰国後も社会復帰できず、人との接触を避けて山奥で生活している。マークもその一人だ。マークは仲間の自殺をきっかけに、18歳の息子ジャックと会う決心をする。“今さら会いたいなんて……”。父からの手紙はジャックを悩ませるが、父と息子はついに再会する。R・マッチオが繊細な演技を見せる人間ドラマ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eastern Plays", "ソフィアの夜明け", "", "", "http://image.eiga.k-img.com/images/movie/54955/315x210.jpg?3380", 89, "ブルガリアの首都ソフィア郊外。ネオナチ組織に加入したばかりの青年ゲオルギは、仲間たちと共にトルコ人旅行者を襲撃するが、偶然にもその現場を目撃し被害者を救ったのは、長い間音信不通だった元アルコール依存症の兄イツォだった……。第22回東京国際映画祭で最高賞の東京サクラグランプリ、カメン・カレフ監督が最優秀監督賞、本作のクランクアップ直前に急逝した兄役のフリスト・フリストフが最優秀男優賞を受賞した。", "2009-01-01", "65", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　COMUNIDAD", "みんなのしあわせ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "不動産会社で分譲アパートのセールスをしているジュリアが、マドリードのマンションで死亡した老人の大金を発見。しかし、以前からその金を狙っていたマンションの住民たちがジュリアの前に立ちはだかる。スペインのゴヤ賞で15部門にノミネートされたブラック・コメディ。グロテスクな猟奇描写、ユーモア、ヒット作のパロディーが巧みに散りばめられ、A・デ・ラ・イグレシア監督の悪趣味が炸裂する怪作だ。ビデオ、DVDタイトルは「13（サーティーン） みんなのしあわせ」。", "2000-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南へ走れ、海の道を！", "南へ走れ、海の道を！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "南の海と太陽の輝く沖縄を舞台に、弟をヤクザに殴殺されたフィリピン帰りの兄が、単身ヤクザ組織に乗り込み復讐を挑む姿を描いたアクション作品。ヤクザ組織の幹部として、萩原健一が寡黙な雰囲気で貫禄を発揮。安田成美が大人の演技を見せる。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mars Attacks!", "マーズ・アタック！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "火星人来襲！　友好的に出迎えた地球人を相手に、火星人の大虐殺が始まった。「バットマン」「シザーハンズ」のティム・バートンによるSFコメディ。二役務めるジャック・ニコルソンほか、グレン・クローズ アネット・ベニング、ピアース・ブロスナン、マイケル・J・フォックス、ナタリー・ポートマン、トム・ジョーンズなど豪華キャストが出演。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Darwin's Nightmare", "ダーウィンの悪夢", "", "", "http://image.eiga.k-img.com/images/movie/33795/315x210.jpg?1360", 112, "アフリカ・ビクトリア湖に何者かが放った外来魚ナイルパーチによって、湖の生態系に狂いが生じ、湖周辺のみならず、世界の産業や暮らしを変えていく様子を克明に描いた衝撃のドキュメンタリー。一つの大魚によって湖が激変する様子が、大国主導のグローバリゼーションによって変わっていく地球の姿を連想させる。監督は93年より、世界各地でドキュメンタリーを製作してきたフーベルト・ザウパー。", "2006-12-23", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD OR ALIVE", "荒野の大活劇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "父親が残した遺産をめぐって、二人の兄弟が巻き起こすスピード感あふれるアクション・ウエスタン。ギャグの連発の中で、主演のG・ジェンマが見せるスタント・アクションが光る。D・テッサリ監督との息もピッタリで、「荒野の用心棒」のパロディー版として大いに笑える。", "1969-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dead Zone", "デッドゾーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "　教師のジョニーは恋人サラとのデートの帰りに事故に遭い、昏睡状態に。5年後、目を覚ましたジョニーには手に触れた者の未来と過去を見通す超能力が身についていた。ジョニーの能力は凄惨な殺人事件を解決に導いたが、その一方で彼自身を疲弊させていくことに。やがてジョニーは上院議員候補スティルソンにまつわる恐るべきビジョンを目の当たりにするが……。超能力者の孤独な戦いと悲劇を描いたサスペンス・スリラー。", "1987-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山犬", "山犬", "", "", "http://image.eiga.k-img.com/images/movie/57995/315x210.jpg?2290", 61, "2011年のぴあフィルムフェスティバル（PFFアワード2011）にノミネートされた、人間心理を鋭く描いたドラマ。09年に米テキサス州のアメリカ陸軍基地で起きた銃乱射事件の背景を取り入れ、事件を取り巻く人々の事件後の人生を映し出していく。監督は、多摩美術大学在学中に本作を手がけた新鋭・佐藤考太郎。", "2012-05-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARRIE", "黄昏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "19世紀末。田舎からシカゴへ働きに出た娘キャリーを、一流料理店の支配人ハーストウッドが見そめた。しかし、ハーストウッドは、強欲な妻の尻に敷かれ、離婚もままならない。そこで二人は店の金を盗みニューヨークへと駆け落ちするが……。W・ワイラーが監督した悲恋ドラマの秀作。坂道を石が転がるように、堕ちていくハーストウッドを、名優L・オリヴィエが好演。可憐な娘キャリーに扮するのはJ・ジョーンズ。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST AMERICAN VIRGIN", "グローイング・アップ　ラスト・バージン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「グローイング・アップ」シリーズの番外編で、６作目の後に作られた。今回は現代のロサンゼルスを舞台に、いつもと違うフレッシュな３人組による、涙と笑いの青春物語。学校一の美女をめぐる駆け引きも、結局女心の不可解さにはかなわない、というちょっぴりホロ苦いラストが印象的。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("可愛い悪魔 いいものあげる", "可愛い悪魔 いいものあげる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "オナペット女優No.1と言われた肉体派渥美マリが、自立した女性のふてぶてしさを好演した作品。幼い頃に両親と死に別れ、芸者置屋の女将に育てられた石川ゆみの夢は、いつか豪華なクラブを持つこと。その目標のため、彼女は自分の若い体を武器に、社長の愛人として、男を渡り歩いていく。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドレミファ娘の血は騒ぐ", "ドレミファ娘の血は騒ぐ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "黒沢清監督が「神田川淫乱戦争」に次いで撮った作品。高校時代の先輩を慕って上京した秋子は、某大学心理学ゼミの“恥じらい理論”の研究家・平山教授によって実験材料とされる。にっかつでオクラになった「女子大生恥ずかしゼミナール」に追加撮影、再編集を施した改作バージョン。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("残酷・異常・虐待物語／元禄女系図", "残酷・異常・虐待物語／元禄女系図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "オドロオドロしいタイトルに粉うことなき内容の異色エロティック時代劇。３話構成のオムニバスで、男にだまされた吉原のおいと、淫乱症のおちせ、そして藩主の子を身ごもったおみつという、元禄時代に虐げられた３人の女性の物語が町医者の視点から描かれる。“カルトの帝王”こと石井輝男監督がエログロテイストの題材を、娯楽色たっぷりに撮り上げた。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山口組三代目", "山口組三代目", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "山口組三代目組長・田岡一雄の自伝をもとにして作られた“山口組”ものの第１作。実話としての生々しさはなく、ちょっと性急な任侠ものに仕上がっている。若き日の田岡一雄を演じる高倉健が、菅原文太と対決する雪のシーンは特に印象的。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミスターどん兵衛", "ミスターどん兵衛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "職人監督・夢野新吾とその助監督のコンビを主人公に、映画界の権威主義を皮肉りながら、映画づくりにまつわる悲喜こもごものエピソードを描いた、山城新伍のワンマン映画。作品題名は、当時この主役コンビが出演して人気のあったカップめんのCFから。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE HARD", "ダイ・ハード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "クリスマス・イヴの晩。ロスの空中にそびえ立つ完成間際の超高層ビルが、13人のテロリストに占拠された。30階で開かれていたパーティーに、たまたま立ち寄って事件に巻き込まれたニューヨークの刑事マックレーン。彼は、目的達成のためには手段を選ばぬ残虐・非情なテロリストたちを相手に、孤立無援の戦いを挑むが……。映画ファンを興奮と驚愕のるつぼに陥れたアクション超大作。地上30階の楼閣を戦場にしたユニークな発想、ミサイル弾や最新鋭銃器を駆使した緊張感みなぎる斬新な戦闘シーンに話題集中。B・ウィリス扮するマックレーンの、人間味あふれる“ダイ・ハード・マン（＝絶対くたばらない奴）”ぶりがたまらない。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("その場所に女ありて", "その場所に女ありて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "広告会社を舞台に、ライバル会社との激しい企業戦争の渦中に生きる野心あふれるヒロインの姿を通して、社会の非情さを鮮烈に描いた異色作。代表作「紀ノ川」などで知られる女優、司葉子が目的のためには手段を選ばない仕事一筋のOLを好演。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新網走番外地・大森林の決斗", "新網走番外地・大森林の決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "木材の独り占めをたくらむ嵐田産業にムショ仲間を殺された末広勝治は、怒りに燃えて刑務所を脱獄し、嵐田産業へ殴り込む。日活から出張出演した宍戸錠が、切れのいいアクションを見せる。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ADVENTURES　OF　PLUTO　NASH", "プルート・ナッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「マイティ・ジョー」のロン・アンダーウッド監督による、近未来の月を舞台にしたSFアクション・コメディ。エディ・マーフィ扮する熱血主人公が謎の男の画策する全土支配の阻止に挑む。2087年の月。ナイトクラブのオーナー、プルートは店が順調で大成功を収めていた。ところがギャングのモーガンが彼に店の売却を要求。強気のプルートは拒否するが、モーガンは月の全支配を目論む謎の男とつながっていて……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("危情少女／DON’T　BE　YOUNG", "危情少女／嵐嵐〈ランラン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「ふたりの人魚」でロッテルダム映画祭のグランプリに輝いたL・イエによるホラー映画。とある古びたアパートを舞台に、夜な夜な悪夢に苛まれる主人公の、現実と幻想の間で次第に混乱していく恐怖が描かれる。チャン・イーモウ監督作品「キープ・クール」でヒロインを演じたチュイ・インが、恐怖に慄く主人公ランランを初々しい魅力で熱演。毎日悪夢にうなされるランランは、その夢の記憶をたどり、原因を突き止めようとする。彼女は、夢に出てきた古いアパートを探し当てるが、謎は次第に深まっていった。自殺した母からのメッセージ、父の失踪、自分の命をねらう何者かの存在……。奇怪な出来事に苦しむ彼女は、次第に現実と夢とが混乱。やがて、そのアパートに隠された彼女の恐ろしい秘密が明らかになる。", "1995-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　BU＾CHE", "ブッシュ・ド・ノエル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "クリスマスをテーマにしたヒューマンドラマ。フランスを代表するS・アゼマ、E・ベアール、C・ゲンズブールが三姉妹を演じたことで話題になった作品。クリスマスを目前に控えたある日、母の二番目の夫の葬式が行われ、３人の娘たちが集まった。母と別れた父スタニスラスと暮らすロシア民謡歌手の長女ルーバは、妻子もちの恋人と12年もの交際を続けている。ブルジョワ気取りの主婦である次女ソニアは、恒例になったクリスマス・パーティーのことで頭を悩ませていた。反抗的で不器用な三女ミラは仕事ばかりの毎日で、私生活にある種の寂しさを感じていた。葬式の後、母と３人の娘たちは４人で食事をして、それぞれの家に帰ったのだが、イブの前日、スタニスラスが発作を起こし、病院に運ばれてしまう。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喧嘩太郎", "喧嘩太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "源氏鶏太の原作を得て石原裕次郎が主演した、「天下を取る」に続く“サラリーマン”もの。大百商事なる商社に入社した、“喧嘩太郎”の異名をとる暴れん坊の宇野太郎が、持ち前の正義感で会社の中に巣食う悪を暴く。本編を含む、裕次郎の初期の４本の“サラリーマン”ものは、いずれも娯楽作品として及第点。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泡姫専家／THE　PICK　UP　ARTIST", "金城武のピックアップ・アーティスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "金城武主演のロマンティック・コメディ。夢想家の青年ダーリが勤める会社に、アメリカの本社から女上司がやって来る。美しい彼女に心を奪われたダーリが帰宅してみると、そこに彼女の姿が。何と彼女は父親の再婚相手の娘だったのだ。複雑な状況に悩みつつも、彼は恋のために奮闘する。", "1996-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REGGAE SUNSPLASH !! JAMAICA", "ボブ・マーリィ／ラスト・ライブ・イン・ジャマイカレゲエ・サンスプラッシュ'79", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "すでにこの世を去った伝説的二大レゲエ・スターのB・マーリィとP・トッシュ、今では大御所として君臨するS・ワールド、グラミー受賞者バーニング・スピアらが登場する、1979年、ジャマイカのモンゴベイで開催された第２回“レゲエ・サンスプラッシュ”フェスティバルの様子を描く。ライヴのほかに、レゲエという音楽やガンジャ（マリファナ)、ラスタファリアン信仰などについてのインタビューや、首都キングストンのスラムでのドキュメンタリー映像なども織り込んだ、貴重な映像。なかでも、B・マーリィの国葬シーンは感動的だ。", "1994-01-01", "77,7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("この子を残して", "この子を残して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "研究の過程でレントゲンを多量に浴び過ぎ、余命いくばくもない医師。長崎に原爆が投下された時、彼は病院にいて助かるが、結局は二人の子供を残して死ぬ運命にある。その気持ちをまとめたベストセラー本を映画化。木下監督の描く反戦と親子の愛情物語。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWIST", "ツイスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "1950年代にアメリカで巻き起こった“ツイスト”ブームを追うドキュメンタリー。チャビー・チェッカーやハンク・バラードら、当時の人気シンガーやTV番組のダンサーへのインタビュー、貴重な記録フィルムを通して、その熱狂を検証する。ツイストから派生した“モンキー”“フライ”等の、今となっては笑える奇妙な踊りも紹介される。", "1992-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジーン・ワルツ", "ジーン・ワルツ", "", "", "http://image.eiga.k-img.com/images/movie/55095/315x210.jpg?2954", 111, "「チームバチスタの栄光」「ジェネラル・ルージュの凱旋」などで知られる現役医師作家・海堂尊のベストセラー小説を映画化した医療ミステリー。「約三十の嘘」「NANA」の大谷健太郎監督がメガホンをとり、菅野美穂が主演を務める。帝華大学病院の医師で顕微授精のスペシャリスト・曾根崎理恵は、非常勤で廃院寸前の小さな産婦人科医院「マリアクリニック」の院長代理を務めていた。しかし、大学病院のエリート医師・清川吾郎に、そこで国内でタブー視されている遺伝子技術を用いた代理母出産を行ったという疑惑をかけられてしまう。", "2011-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUSASHI", "MUSASHI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "若き日の宮本武蔵の実体に迫るアクション・ドラマ。二十歳の時、武蔵は父の仇である無二斎と果たし合いをし、仇を取ることができる。その後、壱岐の島に渡った武蔵は、島で松浦党という和冦が略奪の限りを尽くすのを目にする。武蔵は、松浦党に立ち向かう決意をするのだったが……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THUNDER BAY", "雷鳴の湾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "海軍を除隊したスティーブとジョニーは海底油田のある港町にやって来る。しかし町では漁民と石油会社が激しく対立。石油櫓爆破事件などが起こる中、スティーブとジョニーは漁民と会社側を和解させようとするのだが……。社会派ドラマではあるが、後半の油田から海水が噴出する場面などは並の特撮映画以上の迫力。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Scarlet Letter", "スカーレット・レター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "アメリカ文学界の不朽の名作『緋文字』を映画化。植民地時代のアメリカを舞台に、古い因習に立ち向かい、真実の愛を貫き通す女性の姿を描いた感動作。17世紀アメリカ、ヘスター・プリンは夫ロジャーより一足早く、敬虔なピューリタン（清教徒）入植地ニューエルサレムにイギリスからやって来た。自由奔放な彼女は厳格な町では受け入れられずにいたが、町の若き牧師アーサー・ディムズデイルと出会い惹かれ合う。そんな時、夫ロジャーの乗る船が沈没したことを聞き、二人の愛は燃え上がる。そして彼女はアーサーの子供を身ごもってしまうのだが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市喧嘩旅", "座頭市喧嘩旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "市は旅の途中、手ごめにしようとした若殿にケガを負わされて逃げている娘を救い、一緒に旅を続けるが、娘は悪党一味にさらわれてしまう。市は単身乗り込んで悪党一味を斬り、娘を救い出す。市と対決する強力なライバルがいないのは寂しい。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A NIGHTMARE ON ELM STREET ２：FREDDY’S REVENGE", "エルム街の悪夢2　フレディの復讐", "", "", "http://ecx.images-amazon.com/images/I/51ZJYE1V4CL._SL210_.jpg", 86, "前作から５年。エルム街の“例の家”に引っ越して来たウォルシュ家の長男ジェシーは、夜ごと悪夢に悩まされていた。ある日彼は、前の住人ナンシーの日記を発見する……。不気味な鉄爪と不快な高笑い。ナイトメア・ホラーのヒーロー、殺人鬼フレディの形相に磨きがかかる第２作。", "1987-01-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌舞伎役者 片岡仁佐衛門", "歌舞伎役者 片岡仁佐衛門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 488, "現役で舞台に立っている歌舞伎界の最長老・十三代目片岡仁佐衛門を記録した、８時間に及ぶ長編ドキュメンタリー。1987年、仁佐衛門85歳のときから1991年、88歳までの４年間にわたり、折にふれて撮影したものを、全５作品にまとめている。「薄墨の桜」「AKIKO−あるダンサーの肖像」などで高い評価を受けた女性記録映画作家、羽田澄子監督が力強く描く。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Keys to Tulsa", "ブラック・メール　脅迫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "一通の手紙によって事件に巻き込まれていく男の恐怖を、様々な人間関係を交錯させて描くサスペンス。女にフラれたうえに、富豪の母親にも見捨てられ、勤め先の新聞社もクビになったリクター。そんな彼のもとを別れた恋人ビッキーが訪れ、夫ロニからの手紙を渡す。黒人婦警殺害事件の犯人を知っているロニは、それをネタに犯人をゆするつもりだ。やがて、リクターはロニに事件を目撃したストリッパー、チェリーを紹介されるが……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAISE−MOI", "ベーゼ・モア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "ふとした口論から同居人を絞め殺してしまった娼婦のナディーヌと、３人の男にレイプされたことをきっかけに兄を射殺してしまったマニュ。ひょんなことから出会った二人の女は、やがて次々と男たちを殺害していく。本国フランスで、わずか公開後一週間で上映禁止処分を受け、裁判にまで発展した衝撃的かつ残酷な快楽美に満ちたクライム・ムービー。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Old Boy", "オールド・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/1575/315x210.jpg?3954", 120, "「JSA」のパク・チャヌク監督が日本製同名コミックを原作に映画化。タランティーノが審査委員長を務めた本年のカンヌ映画祭でグランプリを受賞、米ユニバーサルがリメイク権を取得した話題作。理由も分からないまま15年間も監禁されていた男オ・デス。突然解放された彼の前に謎の男ウジンが出現、5日間で彼が監禁された理由を解き明かせと迫る。「シュリ」のチェ・ミンシク、「バタフライ」のカン・ヘジョンら人気俳優が共演。", "2004-11-06", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MYSTERIOUS ISLAND", "SF巨大生物の島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "南北戦争のさなか、北軍の砦を気球で脱出した南軍の５人は、巨大生物の棲息する奇怪な島に漂着する。実は、この島の巨大なニワトリやカニは、ネモ艦長が食糧問題解決のために作り出した産物であった。J・ヴェルヌ原作の『神秘の島』の映画化作品。特殊撮影はレイ・ハリーハウゼンが担当している。", "1961-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Infestation", "ビッグ・バグズ・パニック", "", "", "http://image.eiga.k-img.com/images/movie/54935/315x210.jpg?3925", 91, "いつも自堕落な青年・クーパーは、父親のコネで入社した会社でも怠慢を繰り返し、ある日クビを宣告されてしまう。その瞬間、クーパーは意識を失ってしまい、目覚めたときにはオフィス中の人々がクモの糸で全身を覆われていた。クーパーは巨大な虫の襲撃を交わしながら、上司の娘・サラを救出すべく奮闘するが……。主演は「ウィッチマウンテン／地図から消された山」のクリス・マークエット。", "2009-11-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Presidio", "プレシディオの男たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "　サンフランシスコにあるプレシディオ軍事基地で女性隊員殺害事件が起こる。捜査にあたる市警察のオースティン警部はかつての上司だったコールドウェル大佐と再会するが、捜査の過程でふたりは対立を繰り返すことに。事件の裏側に潜む真実が明らかになっていく一方、オースティンはコールドウェルの娘ドナと恋に落ちて……。真犯人を追う男たちの戦い、そしてロマンスを描いたサスペンス・アクション。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブドール　抱きしめたい！", "ラブドール　抱きしめたい！", "", "", "http://image.eiga.k-img.com/images/movie/54895/315x210.jpg?3196", 70, "近年の日本で飛躍的な発展を遂げ、もはやアートといわれるまでに進化したリアルな性的愛玩人形、ラブドールの可憐な姿を、東京のさまざまな風景と共に美しい映像でつづったドキュメンタリー。是枝裕和監督作「空気人形」でも撮影協力したラブドールの老舗・オリエント工業の人気ドールが総出演。異才・村上賢司監督が「ラブホテル・コレクション　甘い記憶」に続いて贈る「ラブ・ドキュメンタリー」シリーズの第2弾。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGEL", "アンゲロス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "アテネ郊外の貧しい家庭に育ったホモセクシャルの青年アンゲロス。彼はミカイルという水兵に恋をし同棲を始めるが、実はミカイルは悪名高き男娼だった。彼に言われるまま客を取っていたアンゲロスは、ある日自分が、単に利用されているだけだったことを知る。怒りに燃え、彼はミカイルの胸にナイフをつき刺した……。同性愛者たちの壮絶な愛憎を描いたギリシャ映画。", "1982-01-01", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EGG AND I", "卵と私", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "都会育ちの新婚夫婦が、ひょんなことから片田舎で養鶏場を営みながら暮らすことになる。荒れ果てた小屋の改造に始まり、慣れぬ仕事に四苦八苦の二人だが“成功”という夢に向かってたくましく邁進する。涙と笑いで綴ったサクセス・ストーリー。大家族の一家や田舎小町など、ユニークな脇役がドラマを彩っている。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Basic Instinct 2", "氷の微笑2", "", "", "http://image.eiga.k-img.com/images/movie/33735/315x210.jpg?4723", 114, "シャロン・ストーンがセクシーな悪女を演じ大ヒットを記録したエロティック・サスペンス「氷の微笑」の14年ぶりとなる続編。実生活でも身の周りで犯罪が多発することで知られる人気犯罪小説家キャサリン・トラメル。ある日、彼女が運転する車が川に飛び込み、同乗していた男性が死亡するという事件が起こった。捜査を担当するロイ・ウォッシュバーン刑事は、精神科医のマイケル・グラス博士に彼女の精神鑑定を依頼するが……。", "2006-11-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばりの子守唄", "ひばりの子守唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ドイツのベストセラー小説『二人のロッテ』を原作にした、美空ひばり主演作としては珍しいタイプの翻案もの。ひばりの一人二役が光る、家族ドラマの名作。夏の林間学校で知り合った二人の娘はそっくりだったが、実は双子だった。幼い頃に父母が離婚したため離れ離れになっていたのだ。そこで、彼女たちは入れ替わって各家に帰り、家族復縁の道を探る。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FAVOR，THE WATCH，AND THE VERY BIG FISH", "美女と時計とアブナイお願い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "米、英、仏の個性派スターが、パリを舞台に、おかしくも哀しい、スリリングなラブ・ストーリーを展開する。魅力的な美女シビル、カメラマンのルイ、ピアニストという３人の男女が織り成す、誤解とすれ違いをコミカルに描く。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN BEAU MONSTRE", "雨のエトランゼ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ナタリーはある夜、真向かいのベランダから女が飛び降りるのを目撃した。そのベランダにはその女の夫アランが立っていた。ナタリーの証言で事件は自殺ということで落着したが、その夜ナタリーはアランからデートに誘われる。アランの魅力に虜となったナタリーはやがてアランと結婚するが……。パリとロンドンを舞台に、暗い過去を持つ男とファッション・モデルとの異常な愛憎を描いていく。主演はヴィスコンティ作品でおなじみのH・バーガー。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Corman's World: Exploits of a Hollywood Rebel", "コーマン帝国", "", "", "http://image.eiga.k-img.com/images/movie/57275/315x210.jpg?1267", 91, "「インディペンデント映画の神」「B級映画の帝王」とも称される名プロデューサーで映画監督のロジャー・コーマンの人生を描いたドキュメンタリー。「リトル・ショップ・オブ・ホラーズ」（1960／監督・製作）や「血まみれギャングママ」（70／監督）、「デス・レース2000年」（75／製作）など、監督として約50本、プロデューサーとしては400本以上もの作品を世に送り出してきたコーマンは、「早く、安く、利益を出す」という理念のもと、奇抜なアイデアで作品を連発。ジャック・ニコルソン、フランシス・フォード・コッポラ、ロバート・デ・ニーロ、マーティン・スコセッシ、クエンティン・タランティーノらがコーマンの率いる低予算作品の現場から巣立っていった。そんなコーマン門下生らのエピソードや代表作のフッテージとともにコーマンの軌跡を追う。", "2012-04-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　WORLD　IS　NOT　ENOUGH", "ワールド・イズ・ノット・イナフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "P・ブロスナン＝ジェームズ・ボンドもこれが３作目となる人気アクション・シリーズ第19作。石油王がM16内で爆死する事件が発生。ボンドは石油王の娘、エレクトラを警護するためにカスピ海へ飛ぶ。そこでは、テロ組織のリーダー、レナードがロシアの基地から核弾頭を奪い、パイプライン壊滅を企んでいた……。謎の石油王の娘、エレクトラ役にS・マルソー、ボンドガールにD・リチャーズ、敵役にR・カーライルといった豪華な顔ぶれを迎え、20世紀最後を飾るにふさわしい迫力あるアクションが展開される。シリーズ最多出演の名レギュラー、兵器係Qことデズモンド・リューウェリンは本作の撮影後に事故死し、これが遺作となった。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("連合艦隊司令長官・山本五十六", "連合艦隊司令長官・山本五十六", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "日本海軍史上最高の名提督とうたわれる山本五十六の人間像が、真珠湾奇襲、ミッドウェイ海空戦などの５大作戦を通じて描写された。三船敏郎が山本五十六に扮し貫禄をみせる。この作品の興行的な成功により東宝は翌年から戦記大作路線を正式にスタートした。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nos Enfants Nous Accuseront", "未来の食卓", "", "", "http://image.eiga.k-img.com/images/movie/54485/315x210.jpg?1245", 112, "フランスの小さな村を通じ、子供の未来を脅かす食物汚染や環境汚染を訴えるドキュメンタリー。フランス南部に位置するバルジャック村では、村長と村民の熱心な働きかけにより、小学校の給食を全てオーガニック化するという前例のない施策が講じられている。国民の健康よりも生産者や企業の利益を優先する現代の食産業の実態に真正面から触れ、人間と自然の調和をスクリーンに美しく描き出した意欲作。", "2009-08-08", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神倫妙深手多多／THE　POPMAN", "ザ・ポップマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "幼なじみだが正反対な性格のリチャードとチャビーが、それぞれ泥棒と警官となって久しぶりに再会。チャビーに宝石強盗犯に間違えられたリチャードだったが、話を聞いてみると真犯人は他にいるらしい。チャビーはリチャードとともに捜査に乗り出し、真犯人である犯罪組織に戦いを挑む。香港の名バイプレーヤー、R・ンが主演したコミカル・アクション。", "1979-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きらい・じゃないよ２", "きらい・じゃないよ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1991年、８ミリ映画「きらい・じゃないよ」で映画監督としてデビュー、その独特のタッチが話題を呼んだ脚本家・内田栄一の監督第２作。架空の“百年まち”に迷い込んだ青年クーを主人公に、彼が町で出会う、不思議な少女ねり、妹の七月など、映像的なエモーションの中に住む人々とのエピソードを綴っている。七月役の藤本あや乃が好演。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三つの愛", "三つの愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "知恵遅れの息子をもつ学者夫婦、貧しさから結婚のできない若い恋人たち、妻に裏切られたことから神に仕える身になった神父、家族のためにタダ奉公に出された少年……。様々な人生にある人々が、力強く生きていく姿を描く。ベートーベンの『第九』をテーマ曲にしている。小林正樹が自身のシナリオで初めて演出した。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Random Hearts", "ランダム・ハーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "ワシントンD.C.警察の内務調査室巡査部長のダッチは飛行機事故で最愛の妻を失う。しかも事故には妻の不倫の形跡が見つかる。事故に疑惑を抱き、調査をすすめるダッチは不倫相手の妻と接触。お互い伴侶を亡くした上、彼らに裏切られていた二人はやがて惹かれあう。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STARGATE", "スターゲイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "「インデペンデンス・デイ」で大ヒットを飛ばしたR・エメリッヒによるSF大作。1928年、巨大な“環”の形をした遺跡が発見される。遺跡には解読不明の象形記号が刻まれていたが、その存在は軍によって闇に葬られる。時は流れて1994年、考古学者ダニエルは“ピラミッドは定説よりもはるか昔に建てられた”という学説を発表。彼は、ある老婦人から石板の古代文字の解読を依頼される。謎を次々と解くダニエルにやがて秘密が明かされる。その石板こそ、異世界へと通じる“スターゲイト”を起動させる鍵であった。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かた破り道中記", "かた破り道中記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "時の関西喜劇人協会の会員たちが総出演した喜劇。NHKの公開対面番組で近藤勇のひ孫と対面した130歳の老人・吉川経春が、若き日において近藤勇と刀を交えんとした時の話を語りだす。吉川役の伴淳三郎、近藤勇およびそのひ孫の二役を演じる森繁久彌をはじめ、これ以上は望めないほど豪華キャストである。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOING IN STYLE", "お達者コメディ シルバー・ギャング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「ミッドナイト・ラン」「ジョー・ブラックをよろしく」など、寡作でありながらユニークな作風で知られるM・ブレスト監督が、銀行強盗をたくらむ３人の老人たちを主人公に描いたコメディの未公開作。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほんとにあった！呪いのビデオTHE MOVIE２", "ほんとにあった！呪いのビデオTHE MOVIE２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "人気ホラー・シリーズの劇場版第２作。老人の交通事故現場を収めたビデオが投稿された。そのビデオには、入っているはずのないナゾの声が録音されていた。スタッフはこの声の正体を探ろうと、渋谷でアンケートを実施。しかし、声のナゾは解明されなかった。やがて、調査を続けるスタッフは、ある廃村の恐ろしい伝説にたどり着く。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Disco", "DISCO ディスコ", "", "", "http://image.eiga.k-img.com/images/movie/53995/315x210.jpg?4389", 103, "40歳の中年の男たちがダンスコンテストに挑戦する大人の青春ドラマ。フランスの有名コメディアンのフランク・デュボスク、「8人の女たち」のエマニュエル・ベアール、名優ジェラール・ドパルデューらが出演。ノルマンディーの港町でしがない暮らしを送るディディエは、かつてはディスコフロアを圧巻したトリオ“ビー・キング”のリーダーだった。ある日、ディスココンテストの賞品が海外旅行だと知ったディティエは、家族とのバカンスのためにダンスの猛特訓を始める。", "2008-11-15", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Abraham Lincoln: Vampire Hunter", "エイブラハム・リンカーン　バンパイア・ハンター（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Raising Helen", "プリティ・ヘレン", "", "", "http://ecx.images-amazon.com/images/I/31ADRQXXXSL._SL210_.jpg", 119, "敏腕モデルエージェントのヘレンはプライベートでも気ままなシングルライフを満喫中。そんなある日、姉夫婦が交通事故で他界。姉の遺志によると、残された子供たちをヘレンに任せたいという。ヘレンは生意気な3人の子供相手に悪戦苦闘することになるが……。ケイト・ハドソンが本当の幸せや自分の生きる道を見つけていくヘレンをキュートに演じている。", "2006-03-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SENTIMENTAL JOURNEY", "センチメンタル・ジャーニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ビルとジュリーは人もうらやむ仲の良い夫婦だったが、ある日、妻のジュリーが急病で倒れる。ジュリーは孤児を引き取って育てたいと夫に打ち明け、夫婦は少女ヒティを養子にする。やがてジュリーは心臓マヒでこの世を去る。残されたビルはヒティの中に妻の面影を見るようになるが……。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONRACK", "コンラック先生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "美しい自然に囲まれた孤島の小学校に、白人の教師コンロイが赴任してきた。無気力な生徒たちを相手に彼は、音楽を手掛かりとして少しずつ勉強を教えていく。やがて心を開いた生徒たちも彼を“コンラック先生”と慕いはじめるようになるが、大人からはコンロイに反対する者が現れ……。心温まる佳作。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オペラ座の怪人 25周年記念公演 in ロンドン", "オペラ座の怪人 25周年記念公演 in ロンドン", "", "", "http://image.eiga.k-img.com/images/movie/57185/315x210.jpg?2068", 175, "パリ・オペラ座の地下に住む怪人と歌姫クリスティーヌの悲恋を描いたガストン・ルルー原作、アンドリュー・ロイド＝ウェバー作曲による大ヒットミュージカル「オペラ座の怪人」の25周年記念公演（2011年10月1日～2日、英ロンドン・ロイヤル・アルバート・ホール）を映画館で上映。19世紀のパリ・オペラ座。醜い顔を仮面で隠しオペラ座の地下深くに住む怪人は、コーラスガールのクリスティーヌに心を寄せる。怪人は自身を「音楽の天使」と称し、夜ごとクリスティーヌに音楽を教えていたが、クリスティーヌは再会した幼なじみのラウルと愛し合うようになり……。", "2011-10-21", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パチンコ物語", "パチンコ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "タタキ上げのパチンコ店のオヤジと、そんな父に反発しながらも近代経営を推し進めようとする息子との交流を人情味たっぷりに描き出すホームドラマ。パチンコ業界の実情にスポットを当てている。古尾谷雅人の独特の存在感がいい。財津一郎のジョーク“キビシーイ！”を久々に聞くことができる。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESCAPE FROM HELL", "女囚SEX集団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "凶悪犯を収容するために、ジャングルの奥地に作られたプリズン・キャンプは、残忍で好色な所長とサディストの女看守に仕切られた、女囚ばかりのハーレムであった。この生き地獄からの脱走を企てる女囚たちの行く末は……。エロスとバイオレンスに彩られた衝撃の作品。", "1980-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パラダイスビュー", "パラダイスビュー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "沖縄を撮り続ける高嶺監督が、本土復帰前の沖縄を舞台に、現実と幻想を絡めて描いた、ファンタジー要素いっぱいの不思議な雰囲気の作品。登場人物全員が琉球語を話すため、字幕スーパー入りとなっている。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　VE´RITE´　SI　JE　MENS", "原色パリ図鑑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "パリ二区で暮らす移民たちの人間模様をいきいきと描き、フランスで大ヒットしたサクセス・ストーリー。失業中のエディは、衣料品会社社長のビクトールに、彼と同じユダヤ人と間違えられ雇われることになる。陽気な同僚たちに囲まれて新生活をスタートさせた彼は、ビクトールの娘サンドラに一目惚れ。彼女を振り向かせようと、ますます仕事に精を出すようになったエディは、ビクトールと意見の食い違いで衝突し、独立を目指すようになるが……。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Grands ducs", "パトリス・ルコントの大喝采", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "パトリス・ルコント監督のコメディ作品。初老の役者、ビクトール、エディ、ジョルジュは、巡業芝居に売り込みに行き、見事参加することになった。しかし3人は揃いも揃って大根役者で、様々な騒動を巻き起こすのだった。個性的な3人の役者を「ニュー・シネマ・パラダイス」のフィリップ・ノワレ、「髪結いの亭主」のジャン・ロシュフォール、「イヴォンヌの香り」のジャン＝ピエール・マリエールが演じている。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ディアローグ・對話より墜落～ある人妻の追跡調査～", "ディアローグ・對話より墜落～ある人妻の追跡調査～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "実相寺昭雄監督と加賀恵子主演によるエロス3部作の最終章。ミステリー色を加えた官能サスペンス・ドラマが展開する。無名時代のトヨエツこと豊川悦司も出演している。夫は妻の浮気調査を興信所に依頼したが、その結果は浮気の兆候なし。だが、夫は別れたいがため、部下に妻と浮気させていた。一方、妻は浮気の証拠をネタに興信所の所長からゆすられていた……。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CORBEAU", "密告", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "あるフランスの小さな田舎町に“カラス”の名前でバラまかれる密告の手紙。人々の間に大きな波紋が生まれ、ついにはリンチにまで発展した。不幸の原因を引き起こした犯人は誰か、そのねらいは……。H・G・クルーゾーがドイツ占領下で作った心理サスペンス。", "1943-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボール　最強への道", "ドラゴンボール　最強への道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "“東映アニメフェア”のプログラム・ピクチャーとして10年間続いた人気シリーズも遂に、本作の第17弾にしてフィナーレを飾った。7つのドラゴンボールを集めると何でも願いが叶うという、ゲーム的シチュエーションの基に、多彩なキャラクターが弾ける冒険活劇が展開。原作コミックとTVアニメの初期シリーズをリメイクした内容だが、集大成的なスケールが感じられる完結編。DVDは『DRAGON BOX THE MOVIES』に収録。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RICHARD III", "リチャード三世", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "傑作として名高い同名戯曲を、1930年代の設定に置き換えて映像化した異色のシェイクスピア劇。内乱の末にイギリスを治めた国王エドワード４世から、王位を奪おうとする、その弟リチャード。偽装結婚、親類の殺害と非道の限りを尽くして王位についた彼の成功と没落を描く。冒頭の戦車の突入シーンをはじめ派手なアクションを盛り込んだ、パワフルなスペクタクルが魅力。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Truth or Dare", "トゥルースorデア　密室デスゲーム", "", "", "http://image.eiga.k-img.com/images/movie/57705/315x210.jpg?1723", 96, "欧米版「王様ゲーム」を題材に、密室で繰り広げられる命がけの駆け引きを描いた心理サスペンススリラー。指名された人は、どんな秘密も告白する「真実」か、耐えがたい試練であってもチャレンジする「挑戦」かを選ぶパーティゲーム「Truth or Dare（真実か挑戦か）」。大学生仲間のエレノア、ポール、ジェマ、クリス、ルークの5人は、同級生フェリックスからパーティに招待され田舎の豪華な屋敷に集まるが、フェリックスは5カ月前に起きたある事件のため、すでに自殺していた。5人を出迎えたのはフェリックスの兄ジャスティンで、ジャスティンは弟を自殺に追い込んだ犯人を探し出すため5人を山小屋に監禁し、壮絶な「Truth or Dare（真実か挑戦か）」を仕掛ける。", "2012-03-17", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREATEST SHOW ON EARTH", "地上最大のショウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 152, "戦後、デミル監督が発表した「サムソンとデリラ」「十戒」と並ぶ大作。世界最大のサーカス団に集う様々な人間ドラマが、華麗な舞台とともに語られる。スリルと陶酔のサーカスに映画スペクタクルの本質を盛り込んだ見ごたえのある１本。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子連れ狼・親の心子の心", "子連れ狼・親の心子の心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "尾張藩士加藤某の妻に、夫の仇である別式女のお雪を斬るべく依頼された拝一刀は、柳生の執拗な襲撃をかわしながら、お雪の隠れ場に向かう。そしてお雪のうらみを果たしたあと、彼女を斬る。日活アクション出身の斎藤武市の演出は、スピーディーだが重量感に欠けた。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TransAmerica", "トランスアメリカ", "", "", "http://image.eiga.k-img.com/images/movie/1715/315x210.jpg?3311", 103, "性転換を翌週に控えた中年男性とその息子の奇妙なアメリカ大陸横断（＝トランスアメリカ）の旅をユーモラスに描いたロードムービー。主演のフェリシティ・ハフマンがアカデミー賞に主演女優賞ノミネートされた他、ゴールデン・グローブ賞主演女優賞など、多数の映画賞を受賞。監督・脚本は本作で長編デビューを果たした新鋭ダンカン・タッカー。 製作総指揮はハフマンの夫のウィリアム・H・メイシー。", "2006-07-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("葉問", "イップ・マン　序章", "", "", "http://image.eiga.k-img.com/images/movie/56165/315x210.jpg?1888", 0, "1930年代の中国広東省・佛山。武術館の師範に勝ったイップ・マンは、町一番の武術家として知られるようになる。しかし1938年、日中戦争により町が日本軍に占領され、日本兵たちに武術を教えることを拒否したイップ・マンは、空手の名手である日本軍将校・三浦と対決することになる。ブルース・リーの師として知られるイップ・マン（葉問）の生涯を描くアクション。2010年には続編「イップ・マン　葉問」も製作された。", "2011-02-19", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高校生心中 純愛", "高校生心中 純愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "恋人同士の高校生が、二人の愛を貫くために雪山で自ら命を絶つ純愛映画。高校２年生の由夫と洋子は人もうらやむさわやかなカップルだった。ところがある日、由夫の兄が父を殺すという事件が起き、洋子の親は二人の交際を禁止。二人は駆け落ちをするが、すぐに警察に引き戻されてしまう……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西遊記大結局之仙履奇縁／A　CHINESE　ODYSSEY　PART２　CINDELLERA", "チャイニーズ・オデッセイ　PART2−永遠の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "人間に転生した孫悟空の恋の行方を描いた第２部。ジンジンを生き返らせるために、月光寶盒を使ってタイムスリップしたチンポウは、500年前に到着し、釈迦如来の灯芯ジーハに月光寶盒を奪われる。チンポウは自分に気のあるジーハの心を利用して月光寶盒を取り戻そうとするが、ジーハは牛魔王に捕らえられ月光寶盒も奪われてしまう。そんな中ジンジンと出会えた彼は、500年後の二人のことを告げ求婚する。だが、ジーハの愛の深さに気付いたチンポウは、彼女を牛魔王から救うため、人間の心を捨て真の孫悟空へと再生するのだった。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRONCO BILLY", "ブロンコ・ビリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "イーストウッド監督・主演作の第７作。今回は、西部を巡回する“ワイルド・ウエスト・ショー”の座長で、人情味あふれるビリーに扮し、お転婆娘との恋あり、カウボーイと酒場での大喧嘩ありと、定石通りだがツボを得た演出で、楽しめる作品になっている。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIFT HORSE", "封鎖作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アメリカの老朽駆逐艦がイギリス海軍へ譲渡された。やがてそれはUボート基地封鎖作戦の切り札としてナチス軍港へ向かう。本物の駆逐艦を使用し、作戦にたずさわる男たちの人間模様をリアルに描く戦争ドラマ。", "1952-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("涙そうそう", "涙そうそう", "", "", "http://image.eiga.k-img.com/images/movie/33425/315x210.jpg?3670", 118, "日本中で愛されている名曲「涙そうそう」をモチーフに、「いま、会いにゆきます」の土井裕泰監督が手掛けた感動ドラマ。沖縄で生まれ育った血のつながらない兄妹が織りなす、切ない愛の物語を描く。素朴で優しい兄・洋太郎を人気俳優の妻夫木聡が、兄の愛情を一身に受けてまっすぐに育った妹・カオルを「タッチ」「ラフ」の長澤まさみが好演。また、彼らを取り巻く人々を、小泉今日子、麻生久美子、塚本高史ら豪華俳優陣が演じる。", "2006-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　MAISON　DE　JEAN−PIERRE　RAYNAUD　1969−1993", "ジャン＝ピエール・レイノーの家　1969−1993", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 31, "フランスの現代美術家J・P・レイノーが1969年に制作を始めた自宅兼アトリエでもある白いタイルの“家”が、度重なる改装による変容を遂げ、1993年にレイノー自身の手で破壊されていく模様を捉えたドキュメンタリー。1993年３月に家の破壊＝再生を決意したレイノーが、アート・ドキュメントの分野で活躍する女性監督M・ポルトに記録を依頼。破壊直前に行われたインタビューを通して、家にまつわる様々なエピソードが明かされていく。日本では1996年の第２回アート・ドキュメンタリー映画祭で上映された。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("10RILLINGTON PLACE", "10番街の殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "リリントン・プレイス10番街に住む元警官のクリスティーは、残虐な手口で売春婦を中心に若い女性を次々と殺害するが、やがて部屋から死体を発見され逮捕されるに至る。ロンドンで実際に起こった歴史的事件をリアルに再現したサスペンス映画。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OSWALD", "Hello！ オズワルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "全米でTV放映され、大人も子供も夢中にさせている人気アニメ。好奇心旺盛なタコの男の子オズワルドの冒険を描く。ほのぼのとしたキャラクターやビジュアル世界、音楽の魅力も印象的だ。タコのオズワルドが子犬のウィニーとかくれんぼ。だがウィニーは見つからず、オズワルドは途方に暮れてしまい……。「かくれんぼ」ほか全４話。日本では吹替え版が紹介された。", "2001-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hot Spot", "ホット・スポット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "テキサスの田舎町に流れ着いた男ハリーは、さびれた中古車屋のセールスマンの職を得て町にとどまることになる。社長の妻ドリーはどこか暗い影をひきずるハリーに心惹かれて彼を誘惑するが、ハリーは彼女と密会するかたわら、中古車屋で働く若い娘グロリアにも近づいていく。そんなある日、ハリーはふとしたきっかけで町の銀行の警備が無防備なことを知り、その銀行を襲撃する計画を立てる。計画は完璧に思われたが、実は思わぬ落とし穴が……。俳優、監督としてハリウッドでは異色の存在であるD・ホッパーが、20年越しの構想を映画化したフィルム・ノワール。全体に漂うけだるい雰囲気がたまらない傑作。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STRATTON STORY", "甦る熱球", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "テキサス州の片田舎の草野球の投手が、酒で道を誤って浮浪者同然になった元職業野球の一流選手に認められ、猛特訓の末にシカゴ・ホワイトソックスへ入団を許されて輝かしい戦績をあげるが、趣味のウサギ狩りで自分の暴発弾を脚に受け、義足となる。しかし、不屈の闘志と妻の献身的な愛に助けられ、見事カムバック。招待されたオールスター戦で驚異の勝利をつかむ。これは実在した義足投手モンティ・ストラットンの伝記の映画化。スポーツ映画に見えるが本質は夫婦の愛情ドラマで、「グレン・ミラー物語」に先立つJ・スチュアートとJ・アリスンの好演が感動を呼ぶ。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("憑神", "憑神", "", "", "http://image.eiga.k-img.com/images/movie/34025/315x210.jpg?1362", 107, "「鉄道員」の原作者・浅田次郎と降旗康男監督が8年ぶりにタッグを組み、主演に妻夫木聡を迎えて贈る時代劇。幕末の江戸を舞台に、災いの神々に取り憑かれてしまった男の運命をコミカルに描く。才能はあるもののツキに見放された下級武士・別所彦四郎は、祈る稲荷を間違えたことから、貧乏神・疫病神・死神に取り憑かれるハメに。次々と襲い掛かる災難に翻弄されながらも、彦四郎は自身の人生の意義を見出していく。", "2007-06-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガールフレンド：ストラトス", "ガールフレンド：ストラトス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "前作「ミートボールマシン」で一部の観客に支持を得た山本淳一の監督作。「修羅雪姫」の長曽我部蓉子を愛のために闘うヒロインに迎え、ラブ、アクション、ユーモアを交えた１本だ。両親に捨てられた過去を引きずり、素直に人を愛することができない町子。そんなある日、真理という女の子に心惹かれた町子は、真理の彼氏の卑劣な本性を知り、彼との対決を決意する。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SURVIVOR", "ジャンボ・墜落／ザ・サバイバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "日本未公開ながらマニアの間で噂が噂を呼んでいた隠れた名作ホラー。イギリスのホラー作家J・ハーバートの原作をベースに、戦慄のドラマが展開する。監督は「サスペリアPART２」で主演を務めたD・ヘミングス。墜落して爆発炎上したジャンボ機から無傷で生還したのは機長だけ。やがて彼の周りで怪事件が発生する。", "1981-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE MY LIFE ラブ・マイ・ライフ", "LOVE MY LIFE ラブ・マイ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "", "2006-12-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姿三四郎", "姿三四郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "黒澤明の記念すべき監督デビュー作品。明治15年、柔術を志した三四郎は矢野正五郎によって柔道の素晴らしさを知り、その門下に入る。幾多の試練を乗り越え、一人前の柔道家になっていく姿を繊細かつダイナミックに描いた痛快娯楽作品。豪快な試合描写や嵐の決闘シーンが否応なしに見る者の心を高揚させる。戦後の消失により、残念なことに現存するフィルムは、公開翌年の44年に上映されたカットバージョンのみである。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("笑う蛙", "笑う蛙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "勤務先の金を横領し、逃亡者となった元エリート銀行マンの倉沢逸平。彼は長らく使っていなかった別荘に身を隠そうとするが、そこで妻、涼子と鉢合わせしてしまう。とりあえず狭苦しい納戸で寝起きすることになった逸平は、涼子と現在の恋人の情事を覗き見するハメに。やがて涼子の母親や刑事がやってきて……。直木賞作家、藤田宜永の長編小説『虜』の映画化。前半は官能的なシチュエーションで夫婦の欲望と嫉妬を描き、後半は演技巧者たちのアンサンブルで笑いを生み出す娯楽性豊かな悲喜劇だ。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CRUSH", "ダリアン　美しき狂気", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "恋する少女の狂気を描いたサイコ・サスペンス。裕福な家庭に育ち、美貌と知性を兼ね備えた14歳の娘ダリアンは、ジャーナリストの青年に夢中。だが、彼に年相応の恋人がいると知った時、彼女は思い余って残虐な行為に走る。ダリアンに扮したA・シルヴァーストーンの小悪魔的な魅力が印象的。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Young Savages", "明日なき十代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　十代の少年たちによる犯罪が多発するニューヨークの貧民街。知事選に立候補中のダンはその根絶に懸命だった。ダンの部下である検事補のベルは三人の少年たちが起こした殺人事件を担当することに。同じイタリア系貧民の出身だったこともあり、少年たちに同情を寄せるベル。そんな彼の身にやがて危険が……。フランケンハイマー監督が注目を浴びるきっかけになった社会派ドラマ。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大事件だよ全員集合!!", "大事件だよ全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "荒井注最後の出演作として知られる“全員集合!!”シリーズの第12作。“いかりや探偵事務所”の所長・伊刈長吉は、CIAの東京支部長だとホラを吹きヒデオら４人を部下にする。やがて高知の港町の大コンビナート建設をめぐる悪徳企業の陰謀を察知。彼らは調査に乗り出すが、あっさり捕まってしまう……。何といっても見どころは、チリ紙交換屋に扮した志村の登場シーン。ほんの端役だが、それだけでも十分笑える流し目（のちの“東村山音頭”を思わせる）と、タバコを指に挟んだ仕草が絶品。そして、いかりやは何と火葬場で焼かれてしまう!?　今では考えられない往年のブラック＆ワイルドな演技がさく裂。“おしゃまんべ”なストーリーがテンポよく展開していくドリフ映画の一つ。また“６人目のドリフ”、たかしまあきひこの音楽も聞き逃せない。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鞍馬天狗 天狗廻状", "鞍馬天狗 天狗廻状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "宗像左近は娘の止めるのも聞かず、老骨の身で倒幕の浪士狩りに狂奔。しかし、左近はいつも天狗廻状なる投書のために、先まわりした新選組に獲物を奪われてしまう。ところが、当の鞍馬天狗はまったく預かり知らぬことだった……。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIFE　AND　DEBT", "ジャマイカ・楽園の真実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "楽園のような島ジャマイカの実情を捉えたドキュメンタリー。イギリスから独立後、国際通貨基金(IMF)からの借金の返済に苦しみ続けているジャマイカ。多くの観光客が訪れる裏で、貧困にあえぐ現地人たちの真の姿を追い、国際的な貸付機関が発展途上国に及ぼす影響を鋭く見つめる。カリブ海に浮かぶ楽園、ジャマイカ。しかしその中では、国際通貨基金の高金利と厳しい制約により、低賃金で働く人々の姿があった。", "2005-07-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL RI´O Y LA MUERTE", "河と死", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "灼熱のメキシコで繰り広げられる際限なき復讐劇。かつて起こった家長同士の殺し合いが現在に至るまで尾を引く、川沿いにある町サンタ・ビビアナ。若き医師ヘラルド・アンギラノは、重病のためメキシコシティの病院に入院していた。そこへ突然仇敵であるメンチャカ家のロムロが現れ、決闘を申し込む。退院した彼を待っていたのは、案の定、無益な復讐ゲームだった。メキシコの風習に材を得た作品で、ブニュエル作品としては初めての日本公開作となった。", "1954-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("足にさわった女", "足にさわった女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "和田夏十と市川崑のコンビが脚本を書き、増村保造が監督した沢田撫松の原作の３度目の映画化。走行中の特急列車に乗り合わせた美人で足の美しい女スリ・塩沢さやと、彼女の逮捕をもくろむスリ専門の刑事・北八平太の追い駈けっこを、軽妙なタッチで描く。大女優・京マチ子のコメディエンヌぶりも見もの。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRESH", "フレッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "12歳にして、ドラッグ・ディーラーとしての地位を確立した少年の生き様を描いた社会派サスペンス。ニューヨーク・ブルックリンに住むマイケルは、“フレッシュ”の名で通るドラッグ・ディーラー。麻薬の売買はビジネスと捉えていた彼が、相次ぐ友人の死により、自分の生き方に疑問を持ち始める。やがてマイケルは、密売組織を相手に大胆な罠を仕掛けるのだが……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHANTOM OF THE OPERA ２", "オペラ座の怪人２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ロシアの花形バレリーナ、スベトラーナと恋人アンソニーは、ブロードウェイでの公演中、事故を起こしてしまう。アンソニーは顔にひどい傷を負い、スベトラーナは半身不随となり30年がたった。二人がそれぞれ振付師とマネージング・ディレクターを務めるバレエ団で、次々と殺人が起きる。団員の一人ジェシカが、記者アレックスとともに事件を探るうち、アンソニー夫妻の過去の謎が明らかになる。「エルム街の悪夢」シリーズの殺人鬼フレディ＝R・イングランド主演のサイコ・スリラー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠山の金さん さくら判官", "遠山の金さん さくら判官", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "シリーズ最終作。北町奉行・遠山金四郎は、屋敷の前で不思議な虚無僧に出会う。屋敷の中では、父・景晋が血に染まって倒れていた。幸い命は取り留めたものの、父は幽霊に斬られたと言うばかり……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARTIR REVENIR", "遠い日の家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ラフマニノフのピアノ協奏曲第２番にのせて進行し、最終楽章がそのままラスト・シーンになるという構成の大河ドラマ。ゲシュタポの手から逃れ、田舎に住む友人宅に隠れたユダヤ人一家のしばしの平和な生活と、やがて密告によって一家が収容所に入れられる悲劇を、生き残った娘の回想で綴る。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gonzo: The Life and Work of Dr. Hunter S. Thompson", "GONZO ならず者ジャーナリスト、ハンター・S・トンプソンのすべて", "", "", "http://image.eiga.k-img.com/images/movie/54355/315x210.jpg?3313", 120, "2005年に拳銃自殺した伝説のジャーナリスト、ハンター・S・トンプソンの生き様に迫ったドキュメンタリー。当時を知る人物たちによるインタビューなどを交え、トンプソンがもっとも挑発的で多くの作品を残した1965年～75年にフォーカスをあてる。トンプソン原作のテリー・ギリアム監督作「ラスベガスをやっつけろ」でトンプソン役を演じたジョニー・デップがナレーションを担当。監督は「エンロン　巨大企業はいかにして崩壊したのか？」のアレックス・ギブニー。", "2011-02-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN THE COLD OF THE NIGHT", "セクシーナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "カメラマンのスコットはある夢を見る。カリフォルニアの豪邸で、エキゾチックな美女を何度となくなぶり殺す夢だ。夢から覚めた彼は精神分析医に相談、休暇を取ることにするが、その彼の前に夢の女性本人が現れる。しかし、すべては仕掛けられた罠だった……。ファッショナブルでセクシーなミステリー・ロマン。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅の贈りもの　明日へ", "旅の贈りもの　明日へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポケットモンスター・アドバンスジェネレーション／七夜の願い星ジラーチ", "ポケットモンスター・アドバンスジェネレーション／七夜の願い星ジラーチ", "", "", "http://ecx.images-amazon.com/images/I/61VgC%2BYUh1L._SL210_.jpg", 105, "ポケットモンスターの劇場映画通算第６作にして、新シリーズ第１作。サトシとピカチュウ以外のキャラクターや基本設定を一新した新シリーズ構成になっている。千年に一度、７日間だけ現れるすい星とともに旅を続けていたサトシたち一行が、マジシャンたちに出会ったことからある伝説に直面することになる。", "2003-07-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Legends of the Fall", "レジェンド・オブ・フォール 果てしなき想い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "第七騎兵隊の大佐だったウィリアム・ラドローは、除隊後はインディアンを虐殺した過去を忘れるため３人の息子たちと牧場を営んでいた。成長した息子たちは、勃発した第一次世界大戦への従軍を決意する。三男のサミュエルの婚約者スザンナは、次男のトリスタンに“彼を戦場に行かせないで”と頼む。トリスタンはひそかにスザンナを愛していたが、弟の身は自分が守ると約束して戦地へと赴く。しかしサミュエルは敵の急襲に遭って命を落としてしまう。傷心を抱えて故郷に帰って来たトリスタンをスザンナは慰め、その夜二人は結ばれるが……。トリスタンが自然や狩りについて学んだ老インディアンの回想形式により、壮大な現代アメリカ史を背景にした一家の波乱のドラマが描かれていく。B・ピットがトリスタンを熱演。ビデオタイトルは「レジェンド・オブ・フォール」。", "1995-05-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METAMORPHOSIS", "デッドリー・スポーン２／惑星からの物体X", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "極秘に宇宙からの生命体を研究しているタロス社に勤めるフォスター博士は、誤ってミュータントに腕をかまれる。その時から彼の体は地球外生物の病原菌に冒され、奇怪に変身していく。人間の体を吸収し、巨大な牙と触手を持つ怪物のグロテスクな姿が不気味なSFXホラー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　CAPITAN", "快傑キャピタン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ルイ13世統治下のフランスを舞台にした痛快剣豪活劇。剣使いの名手キャピタンは、親友を助けるための戦で重傷を負った。傷の回復後、親友の困窮を告げに彼はパリの大臣の元へ向かう。途中、哲学者で詩人だがイタズラ好きの男と美女ジゼルに出会い行動をともにする。だがパリに到着した途端、ジゼルがさらわれてしまう……。J・マレーの色男ぶりがたまらない逸品。", "1960-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆笑青春列車", "爆笑青春列車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "チンドン屋のアルバイトをしている学生仲間３人は、中学時代の恩師が貧乏ながらも貧しい人々のために託児所を開設しようとしていることを知り、これに協力すべく立ち上がる。監督は戦前からの喜劇映画の第一人者・斎藤寅次郎。アチャコ、高島忠夫、田端義夫、シミキン他、新東宝喜劇人総出演のコメディ。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FURY", "激怒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "資金がなく、恋人との結婚を先送りにしていたジョー。ようやく用意できた金を手に恋人の元へと向かった彼だったが、途中で誘拐犯に間違われ投獄されてしまう。街の住人は犯人が捕まったと知り暴徒化し、ジョーのいる拘置所に放火するが……。表現派を代表するF・ラングが、米国に渡って初めて撮りあげた重厚な人間ドラマ。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EGYMA´SRA NEZVE", "アナザウエイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "愛し合いながらも社会につぶされていく二人の女性の姿を、悲しく、そして美しく描いた秀作。二人の出会いは互いの人生を大きく変えるものだった。その一人、エバは、ジャーナリストとして党の恥部を暴く記事を書き、国外脱出を試みるのだが……。", "1982-01-01", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プープーの物語", "プープーの物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "出版社“リトル・モア”が、テアトル新宿でのレイト・ロングラン公開を前提に新人監督の４作品を製作したシリーズの第１作。子豚のプープーの墓参りのために旅に出た、気ままな女の子フウとスズ。ゲイ・カップル、ゴルファー、ナゾの怪人トランクマンらと出くわしながらも、大平原を青いオープン・カーで疾走する彼女たちのエキセントリックな冒険を描く。監督は鈴木清順作品のスタッフとして腕を磨いた渡辺謙作。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒の天使 Vol.２", "黒の天使 Vol.２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "天海祐希に主役がバトンタッチされた石井隆監督のハードボイルド・アクション第２作。凄腕の女ヒットマン、魔世が、ある暴力団幹部の暗殺に失敗した。その現場で夫を殺された花屋の女、かつて魔世の危ないところを救った男との宿命的な人間模様が絡み合い、魔世は組織との過酷な闘いに挑んでいく。心に傷を負ったヒロインを天海が体当たりで熱演。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THIS WORLD， THEN THE FIREWORKS", "ファイヤーワークス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「ゲッタウェイ」「アフター・ダーク」などの原作者としても知られる人気作家J・トンプソンの小説を、ダークなテイストたっぷりに映画化したフィルム・ノワール。詐欺や売春で生計を立てる双子の兄妹を、B・ゼーン、G・ガーションという“濃い顔”の二人がハマリ役で演じている。幼い頃のトラウマを抱え、近親相姦にも似た歪んだ愛で結ばれたマーティとキャロル。マーティは美人婦警のロイスを手込めにし、彼女のビーチハウスを手に入れようとするが、ふとしたことから彼の計画は狂い出す。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Super Size Me", "スーパーサイズ・ミー", "", "", "http://image.eiga.k-img.com/images/movie/1185/315x210.jpg?1275", 96, "監督が自分自身を実験台に、1日3食をマクドナルドのファーストフードにしたら人体にはどんな影響があるかを体験していく異色ドキュメンタリー。肥満症に悩むティーンエイジャーがマクドナルド社を訴え、同社が安全性を主張するニュースを見た監督スパーロックは、「30日間、1日3食、マクドナルドにあるものしか食べない」「スーパーサイズを勧められたら断らない」などの4つのルールを決めてこの過激な実験に挑戦していく。", "2004-12-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウクレレ PAITITI THE MOVIE", "ウクレレ PAITITI THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "女優・洞口依子と映像作家・石田英範で結成されたウクレレ・ユニット「パイティティ」のドキュメンタリー。パイティティの沖縄ツアーの模様や、本作の原口智生監督が舞台美術を手がけたジャズクラブでのライブ、2004年に子宮頸ガンと診断された洞口が、ウクレレや仲間たちによって立ち直っていく姿、映画監督の黒沢清らがパイティティを語る姿などが収められている。", "2010-09-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRU¨HLINGS SINFONIE", "クララ・シューマン物語　哀愁のトロイメライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "『トロイメライ』や『謝肉祭』で知られる音楽家シューマンと、その妻でピアニストだったクララの激しい恋物語を華麗な名曲をバックに綴る。豪華絢爛な19世紀の雰囲気を見事に再現。当時では珍しい東西ドイツ合作映画。", "1983-01-01", "7,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("九二神鷲之痴心情長劍／SAVIOUR OF SOUL II", "アンディ・ラウ 神鳥聖剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "香港の人気俳優A・ラウ主演の冒険活劇。触れると永遠の命を得られるという伝説の“処女の氷”を求め、雪山へと旅立つ剣の達人チェンヤンの物語。氷の精に扮したR・クワンの美しさは捨て難いが、前半のコメディと後半のラブ・ストーリーのまとめ方は今一つ。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nico (Above the Law)", "刑事ニコ／法の死角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "　元CIAの特殊工作員で、合気道の達人でもあるシカゴ市警の刑事ニコは麻薬取引に絡む捜査に乗り出す。しかし逮捕した犯人が釈放されることに。相棒の女刑事ジャクソンとともに捜査を続行するニコだったが、そんな彼と因縁のあったゼーガンの暗躍が明らかになり……。巨大麻薬組織に挑む凄腕刑事の活躍を描くアクションドラマ。この作品でデビューを飾ったスティーブン・セガールは一躍アクション界の人気スターとなった。", "1988-01-01", "1,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新諸国物語 紅孔雀　第三篇 月の白骨城", "新諸国物語 紅孔雀　第三篇 月の白骨城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "妖術師の一党・怪童、豪傑らが“紅孔雀”をめぐる大乱戦を繰り広げる少年向け冒険活劇シリーズの第３部。主人公・那智の小四郎が絶体絶命の危機に陥るストーリー展開と、新キャラクターの戸がくれ老人が見どころ。DVDは「新諸国物語　紅孔雀(1)」に収録。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い雨", "黒い雨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "井伏鱒二のベストセラー小説をもとに、今村昌平監督が原爆被爆者の悲劇を庶民の普通の生活を通して淡々と綴った力作。美しいモノクロームの映像が静かに戦争の真実を映し出す。1945年８月６日、広島。高丸矢須子は瀬戸内海の小舟の上で強烈な閃光を見た。その直後晴れた空はたちまち暗くなり、矢須子は黒い雨を身体中に浴びてしまう。被爆後遺症の村人たちに小沢昭一や三木のり平といったベテラン・コメディアンを配し、悲惨な物語をどこか喜劇的に描いている。矢須子が風呂場で髪をとかし髪が抜け落ちるシーンで、矢須子役の田中好子が浮かべる笑いは衝撃的だ。カンヌ映画祭ではグランプリを本命視されていたが、惜しくも逃した。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sevendays", "セブンデイズ", "", "", "http://image.eiga.k-img.com/images/movie/54595/315x210.jpg?4047", 125, "「シュリ」「LOST」のキム・ユンジン主演によるサスペンス映画。幼い娘と2人で暮らす敏腕弁護士ジヨン。ある日彼女は娘の運動会に参加するが、目を離した隙に娘を何者かに誘拐されてしまう。犯人からの要求は、死刑判決が確実とされている殺人犯の無罪を裁判で勝ち取ること。愛する娘を救うため、事件の調査を開始するジヨンだったが……。監督はホラー映画「鬘／かつら」のウォン・シニョン。", "2009-07-11", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆発！スケ番☆ハンターズ　総括殴り込み作戦篇", "爆発！スケ番☆ハンターズ　総括殴り込み作戦篇", "", "", "http://image.eiga.k-img.com/images/movie/55385/315x210.jpg?4618", 90, "裏切り者のジュンコに殺されたはずのアサミが、町に戻ってくる。しかし、地元ヤクザ・小龍会が牛耳る町はすっかりと様変わりし、ジュンコは“スケ番狩り”と称した殺りくを繰り返していた。アサミはジュンコを止めるべく、加勢した4人の“やくざハンターズ”とともに、壮絶な決闘を挑む。監督はショートフィルム「やくざハンター」で注目を浴びた中平一史、主演に「片腕マシンガール」の亜紗美。", "2010-05-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ВОКЗАЛ ДЛЯ ДВОИХ", "ふたりの駅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "物語は、人身事故を起こした妻の代わりに服役しているピアニスト、プラトンの回想から始まる。とある駅の待合室で、ひょんなことから３日間足止めをくらうはめになったプラトンと、夫と別れ一人で家族を養うウェイトレスのベーラが知り合う。ユーモアや鋭い風刺を交えながら、苦い過去を持つ二人の大人の愛。そして庶民の哀歓を感動的に描いた一編。本国、ソ連で大ヒットを記録した作品。", "1983-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISS ANNIE ROONEY", "シャーリー・テンプルの夢みるお年頃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "年金生活の祖父、発明家の父と３人で暮らしている貧困な少女アニー。ところがある日、大金持ちの資産家の息子マーティを好きになってしまい……。愛くるしい笑顔と演技力で一躍人気者になったハリウッドの名子役、S・テンプル主演のロマンティック・ミュージカル。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("果醤／JAM", "Jam", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "エドワード・ヤン監督作品のスタッフとしてキャリアを積んだC・イーウェン監督の長編デビュー作。台北で車を盗みながら気ままな生活を送るカイとジャジャ。殺人事件に使われた車を盗んでしまった彼らは、車の持ち主である映画制作会社の美しき副社長シュエと出会う。ジャジャはカイに淡い思いを抱いていたが、カイは年上のシュエとデートを重ねていき……。少年少女のナイーブな恋物語を中心に、いくつものエピソードが時間軸を行き来しながら交錯していく凝った構成。", "1998-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Out of Africa", "愛と哀しみの果て", "", "", "http://ecx.images-amazon.com/images/I/51FlTdhT5QL._SL210_.jpg", 161, "デンマークの資産家の娘カレンはスウェーデンの貴族と結婚し、ケニアへ渡る。コーヒー農園を経営することになったカレンは様々な困難にみまわれる。彼女はいつしか英国の冒険家と愛し合うようになり……。アイザック・ディネーセンの自伝を映画化した一大ロマンス。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAIE　UND　KLEINE　FISHE", "鮫と小魚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "第二次大戦下のドイツ軍掃海艇アルバトロス号。戦いのなかで鍛え上げられていく、若き兵士タイヒマン少尉候補生とその仲間たちの友情や哀切を描き出す。戦後西ドイツのベストセラー小説を映画化。", "1957-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAIR", "ヘアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ベトナム戦争に徴兵され、入隊の前にニューヨーク見物に来た田舎の青年が、ヒッピーと出会い、自由な生き方を発見する。1960年代後半、ラジカルなメッセージと全裸シーンで話題となった反戦ミュージカルを、M・フォアマンが映画化。主題曲『アクエリアス』も大ヒット。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crimewave", "XYZマーダーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "　殺人容疑で電気椅子に座るハメになった青年ビックはそこへ至ることになった騒動を回想する。彼が勤める警備会社の経営者がネズミから人間まで何でも始末するという殺し屋コンビに殺害されてしまう。その後、殺し屋たちはビックが想いを寄せるナンシーを狙う。ビックは彼女を救うべく激闘を展開するが……。前作「死霊のはらわた」で成功を収めたサム・ライミ監督が盟友ジョエル＆イーサン・コーエンと組んだアクション・コメディ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Cat in the Hat", "ハットしてキャット", "", "", "http://ecx.images-amazon.com/images/I/51F92BHDvhL._SL210_.jpg", 82, "アメリカの国民的絵本作家ドクター・スースの原作を、「オースティン・パワーズ」シリーズのマイク・マイヤーズ＆「アイ・アム・サム」の名子役ダコタ・ファニング共演で映画化したファンタジックコメディ。シングルマザーの母と暮らすいたずらっ子の兄コンラッドと優等生の妹サリー。ある日、留守番を任された兄妹の前に、何でも出てくる不思議なシルクハットをかぶったお調子者のネコが現われて大騒動を巻き起こす。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Krótki Film o Milosci", "愛に関する短いフィルム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "郵便局員の青年トメクは、毎晩向かいのアパートに住む年上の女性マグダを覗き見していた。ある日、トメクが届けた偽の為替通知書を手に郵便局を訪れたマグダは、詐欺師呼ばわりされてしまう。責任を感じたトメクは、マグダを追いかけ自分の罪を告白する。「トリコロール」三部作で知られるポーランドの巨匠クシシュトフ・キエシロフスキー監督のTVシリーズ「デカローグ」第6話のロングバージョン。", "", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・人間失格", "新・人間失格", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "東京を脱出し、津軽で流浪の日々を送る青年・城山。おかま、コールガール、金の亡者など様々な人間が嵐のように通り過ぎていくなか、彼は酒とヤクにおぼれ、自滅への階段を一段、一段と降りていく。映像化が困難といわれた太宰治の名作を素材に、現代の風俗を織り込んで描いたTV界出身の新人監督の意欲作。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PREMIERS DE´SIRS", "エマニュエル・ベアール 青い性 処女喪失", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "孤島に流れ着いた３人の美少女。そこで芽生えた性への“青い衝動”をみずみずしく描く。主演の少女の一人は、「美しき諍い女」で見事な裸体と演技を披露したE・ベアール。ベアールはこの作品で主演デビューをはたした。「美しき諍い女」の成熟した裸体とは一味違う彼女の“青い性”の魅力が見どころ。「妖精たちのプレリュード　朝霧に香る甘い旋律」というタイトルでビデオ化されたこともある。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL THAT JAZZ", "オール・ザット・ジャズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "ブロードウェイ・ミュージカルの演出や振付けを行う初老の男を主人公に繰り広げられるストーリー。著名な振付師でもあるB・フォッシー監督の自伝的映画。ショー・ビジネスの世界を彩る圧倒的なダンス・シーンが印象深く迫ってくる魅力ある作品。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PTU", "PTU", "", "", "http://ecx.images-amazon.com/images/I/412FWJAFSCL._SL210_.jpg", 88, "機動隊員や刑事、ギャングらの一夜の交錯する運命を描いた、ハードボイルドな雰囲気の犯罪ドラマ。鬼才ジョニー・トーが、夜の街のいかがわしい人間模様を切り取っていく。バイオレンスやギャグの突発的な挿入や、夜の闇にきらめく光をとらえた映像センスにも注目。拳銃を紛失した刑事、巡回中の機動隊、特捜刑事、抗争中のギャング。夜の街を駆ける彼らは、やがてそれぞれの思惑を抱えて激突し……。", "2005-04-23", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不良番長・送り狼", "不良番長・送り狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "内藤誠が監督デビューを飾った“不良番長”シリーズの第４作。神坂が率いるカポネ団は、デートクラブを始めるが、赤線廃止以来の伝統を誇る同業者・紅バラ会と対立するはめに。紅バラ会の後ろ楯・関東挺身会の圧力に屈するが、ビル乗っ取りをめぐって再び挺身会と対決する。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hamlet liikemaailmassa", "ハムレット・ゴーズ・ビジネス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "シェイクスピアの戯曲「ハムレット」を題材に、現代社会における企業乗っ取りをサスペンスタッチで描いたブラック・コメディ。フィンランドの鬼才アキ・カウリスマキが製作・監督・脚本をつとめる。ある企業の社長が何者かに殺害された。その息子ハムレットは父の残した会社を継ごうとするが、彼の伯父が会社の乗っ取りを企んでいた。ハムレットは会社の権利を守るため、ある商品の世界的独占権を武器に伯父と対立するのだが……。", "1987-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おしどり囃子", "おしどり囃子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "神楽や獅子舞を舞うシーンが随所に登場する、仇討ちをテーマにした時代劇。ロード・ムービー的な股旅ものの要素も入った、ひばり主演の痛快娯楽作。神楽舞いの菊次と料亭の娘は両思いだった。ある日、菊次は旗本にいじめられる父親を前に舞を披露することで助けたが、師匠から破門され放浪の旅へ。一方、父は旗本の罪を着せられ切腹。娘は菊次に父の死を知らせようと後を追う。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BARBARELLA", "バーバレラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "フランスでベストセラーとなった、フォレストのSFエロティック劇画の映画化。宇宙暦の紀元４万年、宇宙破壊光線を完成させた悪党デュラン＝デュランを追ってとある惑星に向かったバーバレラ。彼女を襲う数々の危機、陰険な罠……。バーバレラの拷問に使われるセックス・マシーンに注目！", "1967-01-01", "2,4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花街の母", "花街の母", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "金田たつえの同名ヒット曲をモチーフにした、母娘の情愛を描いたメロドラマ。清原とよ子は、一人娘の明子を、他人にも当人にも歳の離れた妹と偽って育てている、温泉場の芸者。母娘に幸福な時が訪れたかと思うと、刑務所帰りの明子の父が、それをブチ壊すという日々が続く。やがて明子は高校生になり、とよ子が実の母だと知って……。予算５千万円の小品ながら、西河監督による正攻法の演出は、さすが。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("草刈り十字軍", "草刈り十字軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "昭和49年、富山県のある廃村からスタートした除草剤の空中散布反対運動は、人々の手によって雑草を刈る“草刈り十字軍”として成長。現在も全国の若者たちの熱い支援を受けて続いている。その草創期の苦労や情熱を通して、自然といかに付き合うべきか、またボランティアの真の在り方をすべての現代人に問いかける。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MELODY／S.W.A.L.K.", "小さな恋のメロディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "イギリスのパブリック・スクールに通う少年少女の恋の物語。相思相愛のダニエルとメロディーは、学校をさぼって海に行く。それを先生に叱られた二人は、思わず“結婚します!!”と告げる……。思春期特有の初々しい感性をさわやかなタッチで描いた快作。ビージーズのテーマ曲『メロディ・フェア』も絶品。", "1970-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vampire in Brooklyn", "ヴァンパイア・イン・ブルックリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "　ノスフェラトゥ一族の末裔マクシミリアンは同じ吸血鬼族の女性を捜し求めて、バミューダ諸島からアメリカへ渡る。ブルックリンに上陸した彼はギャングに命を狙われていたチンピラ、ジュリアスを家来にして捜索を開始。一方、お目当ての女性リタは、自分が人間と吸血鬼のハーフであることなど知る由もなく、刑事として活躍していた。そんな彼女の前にマクシミリアンが出現するが……。人気者エディ・マーフィとホラー界の鬼才ウェス・クレイブンが組んだコミカルな吸血鬼映画。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Trial", "トライアル　審判", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「ツイン・ピークス」などで謎めいた個性が人気を博したK・マクラクラン主演によるカフカ原作の不条理の世界。舞台は1912年のプラハ。銀行員のヨーゼフ・Kは、30歳の誕生日に突然何者かによって逮捕された。まったく身に覚えのないヨーゼフは事件の真相を探ろうとするが、彼の前には巨大な権力の迷宮が待ち受けていた。", "1992-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("波濤を越える渡り鳥", "波濤を越える渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "滝伸次は行方不明の兄を探すためタイのバンコクに渡り、そこで日本軍が戦時中に埋めた貴金属を奪おうとする悪玉と対決する。香港・バンコクに大ロケーションが敢行されたが、シリーズ特有のロマネスクな魅力が失われてしまった。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("How to Train Your Dragon", "ヒックとドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/54255/315x210.jpg?3123", 98, "「リロ＆スティッチ」のディーン・デュボア＆クリス・サンダース監督が、クレシッダ・コーウェルによる同名児童小説を3Dアニメとして映画化したファンタジー・コメディ。弱虫なバイキングの少年ヒックはある日、傷を負ったドラゴンと出会う。天敵であるはずの2人は秘密の友情を育み、やがてバイキング一族の未来をも変えてしまう奇跡を起こす。主人公ヒックの声に「トロピック・サンダー」「ミリオンダラー・ベイビー」のジェイ・バルチェル。その他の声の出演にジェラルド・バトラー、ジョナ・ヒルら。", "2010-08-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SHOP AROUND THE CORNER", "街角 桃色の店", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ビリー・ワイルダーの師匠にあたるルビッチの大人向けの恋愛喜劇。ハンガリーの首都ブダペストが舞台。街の雑貨店で販売主任のような地位にいるクラリックはその店にやって来て強引に店員になってしまったクララと、なにかと反目しあっている。そんなある日、クラリックはかねてから文通をしてきた女性と初めて会うことになった……。ハンガリーのN・ラズロの戯曲を原作にしたハート・ウォーミングなラブ・ストーリー。「ユー・ガット・メール」は、本作の設定をEメールに置き換えたリメイク版。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リアル鬼ごっこ5", "リアル鬼ごっこ5", "", "", "http://image.eiga.k-img.com/images/movie/57655/315x210.jpg?2742", 74, "人気作家・山田悠介のベストセラーを映画化したサバイバルアクション「リアル鬼ごっこ」（2007）、「リアル鬼ごっこ2」（10）に続き、新たに製作された3部作（「リアル鬼ごっこ3」「4」「5」）の3作目。「血液型B型」の人間が襲われる鬼ごっこを描き、3部作を通して同時刻に異なる場所で展開される3つのストーリーが交錯する。仕事ができないダメな新入社員の大地が残業をしていたところ、血液型がB型の人間を襲う殺人鬼が現れ、オフィスが戦場と化す。その混乱の最中、恋心を抱いていた先輩女子社員・希の危機を救った大地だったが、希の恋人・朔也の罠にはまり……。", "2012-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOPO　GIGIO，　LA　GUERRA　DEL　MISSILE", "トッポ・ジージョのボタン戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "巨匠、市川昆監督が1967年に製作したパペット映画。今も多くの人々の心を掴む人気キャラクター、トッポ・ジージョが謎のボスの悪巧みから世界を救うため大活躍する。市川監督のもと、イタリアと日本の精鋭スタッフが集結。なかでもジージョの生みの親であるM・ベレゴの操演が見事。そのまるで生きているようなジージョの動きには驚かされるはずだ。小さなアパートで一人暮らしをしているトッポ・ジージョは、二匹のノミに悩まされていた。我慢できずに家を飛び出したジージョは、街で赤い風船に出会う。二人で夜の街を歩いていると、銀行ギャングの事件に巻き込まれ……。", "1967-01-01", "76,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版BLEACH ブリーチ Fade to Black 君の名を呼ぶ", "劇場版BLEACH ブリーチ Fade to Black 君の名を呼ぶ", "", "", "http://image.eiga.k-img.com/images/movie/53805/315x210.jpg?4365", 0, "海外でも人気が高い久保帯人のアクション漫画の映画化第3弾。ひょんなことから悪霊退治の死神になってしまった高校生・黒崎一護の戦いを描く。ある日、謎の2つの影が瀞霊廷の技術開発局を襲い、巨大な霊子爆発によって瀞霊廷は壊滅した。現世で異変を感じた一護はソウルソサエティへ向かうが、そこで待ち受けていたのは、一護そしてルキアまでもの記憶を失った恋次たちだった……。", "2008-12-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沈黙の挽歌 TRUE JUSTICE PART5", "沈黙の挽歌 TRUE JUSTICE PART5", "", "", "http://ecx.images-amazon.com/images/I/5169DRXrWLL._SL210_.jpg", 90, "特別捜査班「SIU」の奮闘をスティーブン・セガール主演で描いたクライム・アクション・シリーズ「TRUE JUSTICE」の第5弾。日本ヤクザが中国マフィアと手を組み、敵対する南米系マフィアを襲撃した。情報を得て抗争現場に駆けつけたSIUリーダーのケインは、ヤクザの1人を射殺する。ケインがヤクザと中国マフィアによる計画について捜査を進める中、銀行強盗事件が発生。銀行に立てこもる犯人から、交渉人としてケインが指名され……。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOMMARLEK", "夏の遊び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "亡き恋人とのひと夏の思い出にふけるバレリーナの回想と郷愁……。名匠I・ベルイマン自ら“一番好きな作品”に挙げる自信作。後の名作「第七の封印」「野いちご」などでもコンビを組んだG・フィッシャーのカメラが美しく、スウェーデンの夏の風景の中に青春の喜びや夢を見事に切り取って見せる。", "1951-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お富と切られ与三郎", "お富と切られ与三郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "以前は深川で芸者をやり、今は土地の親分の妾にされている女・お富と、彼女を見染めたため親分にメッタ斬りにされ、海へ投げ込まれた男・与三郎の悲恋を描いた時代劇。復讐劇も加えて壮絶な仕上がりに。全身に無数の傷を負いながらも与三郎は生きのび“切られ与三郎”として江戸に現れるが……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蕨野行〈わらびのこう〉", "蕨野行〈わらびのこう〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "「四万十川」の恩地日出夫監督が芥川賞作家、村田喜代子の同名小説を映画化した渾身の力作。江戸時代、60歳以上の老人だけが住むという村を舞台に、そこで生きる人々の姿を、季節の移り変わりとともに、じっくり見つめていく。腰のすわった演出とともに、「黒い雨」の市原悦子、近年は三池崇史作品でおなじみの石橋蓮司ら、ベテラン陣の妙演が光る。江戸時代中期、その村には一つの約束事があった。60歳を越えたら村を出て、人里離れた原野、蕨野の丘に移り住むことだった。山の雪が解けだす春、８人の老人が蕨野入りする。そのうちの一人、床屋の姑・レンは村で暮らす嫁・ヌイとの交流を続けるが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最遥遠的距離", "遠い道のり", "", "", "http://image.eiga.k-img.com/images/movie/54015/315x210.jpg?3202", 113, "台湾シネマ・コレクション2008上映作品。「言えない秘密」のグイ・ルンメイが主演する切ないロードムービー。先の見えない不倫の恋愛に悩むシャオユンは、毎日のように以前の住人宛に送られてくる手紙とメッセージが録音されたテープの音源を探す旅に出る。去った恋人にテープを送り続けるシャオタン、心を病む精神科医のツァイ、それぞれが再生を求めて旅に出る。", "2008-09-09", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ДУХОВНЫЕ ГОЛОСА／SPIRITUAL VOICES", "精神〈こころ〉の声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 328, "ロシアの鬼才A・N・ソクーロフが、内戦状態が続く中央アジア・タジキスタンを撮影した５部構成のドキュメンタリー。1994～95年にかけて戦場近くに長期滞在した彼は、季節の移り変わりとともに戦場の若い兵士たちを撮影。戦争の政治的な背景は語られず、故郷を離れた兵士たちが料理や洗濯をし、時に戦場に出かけるといった日常が描き出されている。副題は“戦争からの日記”と付けられ、ソクーロフの戦争への思いが映像で綴られた作品だ。", "1995-01-01", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AKHA", "愛と哀しみのアカ族　タイ人もお手上げ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "気ままな旅をしていた日本人青年の洋がたどり着いたのは、タイ北部アカ族の村。すぐに村人とうちとけた洋は、村長から少女アシューをバンコクまで送り届けることを頼まれる。天真爛漫の二人は象、徒歩、バス、電車を乗り継いでバンコクへと向かう。だが、二人は知らない間に麻薬の運搬に巻き込まれ、おまけにバンコクで訪ねたアシューの姉アミーは麻薬組織の手先として働いていて……。麻薬騒動に巻き込まれた日本人青年とタイ人少女の珍道中を描いた冒険活劇。", "1994-01-01", "23,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REVENGE OF THE PINK PANTHER", "ピンク・パンサー４", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "おなじみ、エドワーズ＆セラーズ・コンビによるシリーズ第５作。フレンチ・コネクションのフィクサーからやり玉に挙げられたクルーゾー警部は、得意の変装術を武器に、一路香港へ……。中国人からゴッドファーザーまで、いちだんと磨きのかかったセラーズの変装ぶりが一興。DVDは「ピンク・パンサー フィルム・コレクション」に収録。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MAMAN ET LA PUTAIN", "ママと娼婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 220, "定職も人生の目標もないうえ、女にもだらしないアレクサンドルは、ブティックを経営している年上の女マリーと同棲し、無為な日々を送っている。マリーはアレクサンドルを息子のように愛し、度重なる彼の浮気にも寛容な態度で接していた。ある日、アレクサンドルは行きつけのカフェでベロニカという女の子に声をかけ、部屋に戻った彼はマリーに女の子をナンパした話をする。こうしてベロニカはマリーとアレクサンドルの生活に入り込んで来る。アレクサンドル役をJ・P・レオーが好演。1983年にカンヌ映画祭で審査員特別賞を受賞した作品だが、日本では長年公開されず1996年に初めて公開された。", "1973-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　PETRIFIED　FOREST", "化石の森", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "“ボギー”の愛称で親しまれたH・ボガートが悪のヒーロー役を好演したサスペンスに満ちた監禁ドラマ。B・デイビスの正統派女優としての美貌にも魅了される。作家が当てのない旅に出て、その途中のサービス・ステーションを経営する父娘に出会い、娘にほのかに恋を抱く。やがて逃亡中のギャング一味と出会い、拉致監禁されしまい危険な目に遭遇するが……。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DALLAS CONNECTION", "ピカソ・トリガー ダラス・コネクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "『ペントハウス』や『プレイボーイ』のグラビアを飾ったセクシー美女たちが、世界を股にかけて繰り広げるミステリー・アクション。麻薬組織、武器密輸組織を壊滅に追い込むためのプロジェクト“ダラス・コネクション”のため、兵器探知システムの開発を担当した科学者たちが、何者かによって殺されてる。残る一人の科学者を危機一髪で助けたFBI捜査官たちに、国際犯罪組織に操られた美しき３人の殺し屋の恐るべき罠が次々と襲いかかる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESCALATION", "エスカレーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "自由な生活を愛しヒッピーとなった息子に自分の事業を継がせようと、実業家の父親が雇った精神分析医、カルラマリア。彼女はグラマラスな肉体を武器に息子をコントロールし、もはや自分の言いなりと化した彼を使って会社乗っ取りを図る……。", "1967-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あした", "あした", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "「ふたり」に続く大林宣彦監督の新・尾道三部作の第２作。小型客船呼子丸が乗客９人を乗せて行方を絶ってから３ヵ月たったある日、家族や恋人を失った人々の元に、“今夜午前０時、呼子浜で待っている”という不思議なメッセージが届く。半信半疑ながらも浜に向かう人たち。そこに偶然居合わせた旅行中の女子大生、法子は、孫に会いに来たヤクザの親分の子分が幼なじみの貢であることを知る。消えた人と残された人との約束、法子と貢との約束、それぞれの思いを胸に午前０時を待つ。やがて約束の時間となり、海面から呼子丸の姿が現れ……。束の間の再会と永遠の別れを体験する彼らの、多彩な人間ドラマを描いたファンタジー。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GAMBLER AND THE LADY", "賭博師と淑女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "一文無しでイギリスへやって来たジム・フォスターは、持ち前の機転と賭けごとでの才能で大成功をおさめる。ついには、ナイトクラブとサラブレッドを所有するまでになるが、妥協を許さない彼には、敵も多かった。一度は金と権力をすべて手中にした男が、人生の賭けに破れ、再び無一文に。彼の迎える末路は果たしてどうなるのか……。", "1953-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もうDEBUなんて言わせない！", "もうDEBUなんて言わせない！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "スポーツ用品メーカーに勤める青山雛は、デブ体型に悩む内気な女の子。そんな彼女が会社の新製品であるスポーツウェアのキャンペーン・モデルに選ばれる。しかし、それは彼女にダイエットをさせ、使用前と使用後の体型を比較させて商品をPRしようというものだった。最初は断ったものの、結婚詐欺に遭った彼女は、男たちを見返してやるという執念でダイエットを始める……。彼女の涙ぐましい減量の日々と、二枚目カメラマンとの恋の行方を描く。ヒロインを演じる青山雛が撮影に合わせて、実際に４ヵ月で15キロ、ダイエットしたのも話題となった。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日常恐怖劇場・　オモヒノタマ／念珠", "日常恐怖劇場・　オモヒノタマ／念珠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "オダギリジョー、竹中直人、加藤雅也、光石研、松重豊といった日本映画界を支える面々が続々登場するホラー・オムニバス。都市伝説を過剰にデフォルメしたような視点とタッチで、新しい恐怖を呼び起こすエピソード９編が収録されている。いずれも救いのない話ばかりだが、この後味の悪さ、イヤ～な余韻こそが、ホラーファンにはたまらない醍醐味になっている。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SECRET OF ROAN INISH", "フィオナの海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ロザリー・K・フライの同名小説を、舞台をスコットランドからアイルランドに変えて映画化した感動作。ケルト民族の神話をモチーフに、家族の絆、人間と自然のかかわりを、名手H・ウェクスラーの幻想的な映像の中に綴る。1940年代、母親を病で失った幼い少女フィオナが、アイルランド北西部の海岸で暮らす祖父母の元に引き取られる。フィオナの一家が暮らしていた島ローン・イニッシュは、今では無人となりアザラシの島になっていた。祖父から島にまつわる伝説や弟ジェミーがゆりかごごと海にさらわれた話を聞いたフィオナは、弟がまだ生きていると信じ、島へ帰ることを望むようになる。やがて従兄弟とともに島を訪れた彼女は、そこで弟の姿を目撃するが……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヘッドフォンララバイ", "ヘッドフォンララバイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "正義感の強いレイ、不良の汚名を着ているが超高校級ランナーとしての実力を持つ転校生・豊、苦学生でレイの家に居候を決め込んでいるボクトツ。この３人が、高校駅伝競技を通じて固い友情で結ばれていくまでを描いた青春スポーツ・アイドル映画。シブがき隊の主演第２作。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・ボディガード", "ザ・ボディガード", "", "", "http://image.eiga.k-img.com/images/movie/55245/315x210.jpg?1391", 81, "出演作180本を超えるベテラン・松方弘樹と人気歌手の島谷ひとみ共演によるサスペンス・アクション。任務のために人を殺めてしまった元SPの黒沢（松方）は、余生を一人静かに暮らそうとしていたある日、政界の大物から新進気鋭の芸術家AYA（島谷）の警護依頼を受ける。彼女が世間の注目を浴びるほど、彼女への脅迫が増えていたのだ。渋々依頼を承諾した黒沢は任務につくが、AYAへの度重なる脅迫がエスカレートしていき……。監督は「一生、遊んで暮らしたい」の金澤克次。", "2010-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Out for a Kill", "沈黙の標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "考古学者のロバートは武道の達人にして、中国古美術品専門の元泥棒。麻薬捜査のおとりとなって愛妻を死なせた彼は、非道な麻薬組織や捜査局への復讐に挑む。一人、また一人と敵を倒していく緊迫した展開の中、S・セガールお得意のマーシャルアーツ・アクションが豪快に映える。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幻覚", "幻覚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「捨て犬」に続き竹内力が製作総指揮を務めた本作は、幻想と現実を混在させたホラー。奥深い森にある一軒の山小屋を舞台に、薬物中毒の女性を巡って巻き起こる不可解な現象を、身の毛もよだつ戦慄映像の連続で描く。「新・影の軍団」の宮坂武志監督による、男気あふれる大胆な演出が光る。薬物中毒になった兄貴分の彼女の“毒消し”を命じられたタツヤは、仲間と共に、深い森に佇む一軒の山小屋へとやって来たのだが……。", "2005-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Unforgiven", "許されざるもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "映画学科に通う無名の新人監督ユン・ジョンビンが、軍隊経験によって変化した自身の内面や、韓国社会に蔓延（まんえん）する軍隊式権力構造をテーマに撮りあげた人間ドラマ。模範的な軍隊生活を送り、除隊を間近に控えた兵長テジョン。そんな彼の元に、中学時代の同級生スンヨンが新兵として入隊してくる。上下関係が厳しい軍隊特有の不条理さに納得できず、トラブルばかり起こすスンヨン。テジョンはそんなスンヨンをかばい続けるが……。", "2007-03-10", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しのぶの明日", "しのぶの明日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "明るく器量のいい下町の評判娘が、交通事故により両眼を失明、絶望の底から盲導犬とともに明日に向かって生き抜いていく姿を描いた感動作。叱咤激励しながらマンツーマン方式で歩行訓練をする紺野美沙子と勝野洋の演技が印象的。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("闇打つ心臓", "闇打つ心臓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "長崎俊一監督が23年前の作品を自らリメイク。単なる焼き直しにせず、前作に主演した内藤剛志と室井滋が23年後を演じ、若い男女の物語と並列させたり、メタフィクション的なドキュメンタリー映像を織り込むなど実験性も興味深い。自分たちの子供を殺して逃亡したリンゴォと伊奈子が23年ぶりに再会。一方、同じく子供を殺して逃げる若いカップルが、とあるアパートの一室でかつての二人のように息を潜めていた……。", "2006-04-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　KLANSMAN", "クランスマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "初期の「007」シリーズで有名な職人監督T・ヤングが、白人至上主義集団クー・クラックス・クラン（KKK）を題材に撮り上げたバイオレンス映画。公民権運動集会を開く黒人とKKKが対立するアラバマ州の町。選挙を控える地元の保安官らは事態の沈静化を試みるが、KKKによる黒人への暴力は激しさを増していく。KKKに敢然と立ち向かう黒人青年をO・J・シンプソンが熱演している。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("清河〈チョンハー〉への道", "清河〈チョンハー〉への道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "自らを“コリアン・ジャパニーズ”と呼ぶ、在日２世のブルース・シンガー、新井英一。全48番、45分にも及ぶ大曲『清河への道』を引っさげ、亡き父の故郷・清河でのツアーに至るまでの苦難に満ちた道のりを捉えていく。大きく魂を揺さぶるその歌声に、彼の人生そのものが感じられるはずだ。福岡の下町で、３人兄弟の末っ子として生まれた新井。父親が結核のため、母の手一つで育てられた彼は、29歳で念願の歌手デビューを果たす。", "2004-11-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新宿馬鹿物語", "新宿馬鹿物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "半村良の原作を神代辰巳が脚色し、“全員集合!!”シリーズなどの喜劇映画を数多く撮っている渡辺祐介が演出にあたっている。40歳近くになる新宿のバーのマスターは独身だが、ある雨の日、さしていた傘の中に和服姿の美女が飛び込んできた。マスターはこの女に一目惚れするが……。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BILITIS", "ビリティス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“淡い光の中に立つ裸体の少女”といった美しい写真で人気のあるカメラマン、ハミルトンの監督第１作。思春期を迎えた少女ビリティスの愛と性の葛藤を描く。少女の幼い性、彼女が垣間見る大人の世界、プラトニック・ラブなどを、監督独特のタッチで甘美に映像化。ビデオ発売時タイトルは「柔かい肌 禁じられた幼性」。", "1977-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MINNESOTA　CLAY", "ミネソタ無頼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "視力を失いかけている男、ミネソタ・クレイが刑務所を脱獄する。やがて無実を証明するため故郷に戻ったクレイは、ならず者一味のボス、フォックスと対面し、彼こそが自分を陥れた張本人だと知る。目の難病に冒されたガンマンの苦闘を描き、マカロニ・ウエスタン版「座頭市」とも呼ばれる西部劇。鬼才S・コルブッチの初期の秀作で、主人公が音を頼りに敵を倒していくクライマックスが圧巻。", "1964-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山びこ学校", "山びこ学校", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "無着成恭が編集して、当時大ベストセラーになった作文集『山びこ学校』を原作として映画化したもので、脚本の八木保太郎が自ら設立した独立プロ“八木保太郎プロダクション”の第１回作品である。山村で起こる生徒と先生、村人とのふれあいを、ドキュメンタリー的なタッチで描いたさわやかな一編。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天才えりちゃん 金魚を食べた", "天才えりちゃん 金魚を食べた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "６歳でSF童話大賞を受賞した竹下龍之助の作品をアニメ化。龍之助は、５歳下の妹えりに不思議な能力があると信じている。雨が降りそうな日は髪が逆立って、天気予報を当てたりするからだ。そんなある日、金魚のピオが、えりの口の中に入ってしまい大騒ぎになってしまう……。大好きなサツマイモを食べ過ぎたえりが月まで飛んでいってしまったり、自由奔放な発想がユニーク。冒険心たっぷりの心温まるファンタジーだ。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONSIEUR　BATIGNOLE", "バティニョールおじさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "パトリス・ルコントの「タンデム」などで知られる俳優ジェラール・ジュニョが監督・主演した味のある人間ドラマ。ナチス占領下のパリで、ユダヤ人の子供をかくまった平凡な男の奮闘を見つめる。1942年。肉屋バティニョールの娘婿の密告によって、隣人のユダヤ人バーンスタイン一家は連行される。運良く一家の息子シモンはそこから逃げ出し、バティニョールの下にやって来るが……。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　虹のピラミッド", "それいけ！アンパンマン　虹のピラミッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "TVでもおなじみのアニメ“アンパンマン”シリーズの劇場版第９作。豊かな自然に恵まれ、きれいな虹を作り出す虹の星の住人、アメちゃんと出会ったアンパンマンたちは星に案内されるが、そこはすっかり砂漠に変わってしまっていた。すべては虹の玉を盗んだ、ばいきんまんと彼らの仕業だと知ったアンパンマンは、彼らに戦いを挑む。", "1997-07-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いつもより素敵な夜に", "いつもより素敵な夜に", "", "", "http://image.eiga.k-img.com/images/movie/55805/315x210.jpg?4521", 70, "6人の監督が新しい性愛の可能性を探る特集「Love＆Eros CINEMA COLLECTION」の1作。父親のアンティークショップを経営する美砂は、そこで出会った信次と結婚し、平凡ながらも幸せに暮らしていた。出版社に勤める信次は小説家になる夢をあきらめきれず、“トキオ”という名を名乗って美砂にメールを送る。身に覚えのない男からのメールを無視していた美砂だが、次第にトキオにひかれていき、後ろめたさから目隠しをしてトキオに合うようになる。", "2010-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺し屋＆嘘つき娘　SHOOT，MY DARLIN’", "殺し屋＆嘘つき娘　SHOOT，MY DARLIN’", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「SCORE」でプロデューサーも手掛けた小沢仁志が初監督に挑み、長期フィリピン・ロケを敢行して作り上げたハードボイルド・タッチのアクション映画。殺し屋ジョーカーはエージェントの依頼で組織に捕らわれている中国娘、麗華をエル・レイという町までガードする仕事を引き受ける。まず、麗華を救出したジョーカーは、彼女と日本人サルの運転するタクシーに乗り込み、逃亡を始めた。だが、ジョーカーから逃れたい麗華はサルと共謀して彼を振り切ることを計画。作戦は成功するが、実はサルは組織の人間だった……。低予算ながら作り手たちの情熱がほとばしるような激しい銃撃戦、カーチェイスが展開していく。“アジアの妖精”ビビアン・スーの日本映画デビュー作でもある。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　PLANET", "ザ・プラネット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "写真家、建築家など18名のアーティストによって製作されたアニメーション・オムニバス。アルゼンチン音響派を代表する音楽家、フェルナンド・カブサッキが作曲した『The Planet』を基に、色彩豊かなイメージが多用な手法で表現される。チョークで描かれたカラフルな子供たち、夜の街を照らすフラッシュライトなどが、油絵やシンプルな直線など、様々な手法で描かれる。", "2001-01-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEVERENDING STORY III", "ネバーエンディング・ストーリー３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "M・エンデのベストセラー『はてしない物語』を原作とするシリーズ第３作。中学生になったバスチアンは、転校した学校でも不良少年たちから嫌がらせを受け、学校の図書館に逃げ込む。そこで、昔、魔法の本『ネバーエンディング・ストーリー』をくれた古本屋のコレアンダーと再会した。今は図書館の司書となっているコレアンダーは、もう一度『ネバーエンディング・ストーリー』を読むことを勧める。読み終えたと思っていた物語には、まだ続きがあったのだ。バスチアンがファンタージェンへ戻ると、不良グループも彼を追って来て、“邪悪な心”で世界を汚そうとしていた。バスチアンはファンタージェンの統括者から魔法の首飾りを授けられ、平和と秩序を守る使命を受ける。", "1994-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウォーターメロン", "ウォーターメロン", "", "", "http://ecx.images-amazon.com/images/I/51WR3ARR21L._SL210_.jpg", 65, "「プッシーキャット大作戦」の本田隆一監督が、自分の巨乳に悩む妻と虚言癖を持つ夫の奇妙な夫婦愛を描いたエロティック・ラブストーリー。大きすぎる胸にコンプレックスを抱えて生きてきた千香は、初対面で胸を見なかった初めての男性・博士と出会う。巨乳に興味がないという博士にひかれて結婚を決めた千香だったが、実は博士には虚言癖があった。主演は人気セクシー女優の紅月ルナと、「リアリズムの宿」の山本浩司。", "2005-02-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABOVE　US　THE　WAVES", "潜水戦隊帰投せず", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "第二次世界大戦での、英国海軍特攻部隊の活躍を描いた海戦映画。ノルウェーの港に碇泊中のドイツ巨大戦艦ティルピッツ号を撃沈すべく、英国海軍は人間魚雷を仕掛けるが、失敗に終わる。再び超小型潜水艦３隻で作戦を実行し、１隻が敵に大打撃を与えることに成功。だが、小型潜水艦も大破して壮絶な最期を遂げる……。ドキュメンタリー・タッチの重厚な演出が光る戦争映画の秀作。", "1955-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガメラ対宇宙怪獣バイラス", "ガメラ対宇宙怪獣バイラス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "ゴジラ同様すっかり善人(？)になったガメラが活躍。宇宙怪獣バイラスによって脳波コントロール器を仕込まれたガメラは、東京を暴れまわるが、二人の少年の活躍でコントロールが解け、バイラスを激闘の末、海底に沈める。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おあずけ", "おあずけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "不思議な超能力を持つ二人の女子高生が恋した狼人間“おあずけ”。そして彼を創り出し、死に追いやった悪意に満ちた天才科学者と彼女らとの対決を描いたサイキック青春ファンタジー。内田琢磨は、名匠・内田叶夢の息子で本作で監督デビュー。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雷撃隊出動", "雷撃隊出動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“雷撃とは死ぬことなり”という海軍省報道部の企画による戦意昂揚映画。３人の海軍少佐を中心に、航空兵が魚雷を積んで敵空母に突入するまでが描かれる。公開は敗戦色濃い1944年12月７日。もはや戦勝ムードなど出せるわけもなく、ひたすら死臭漂うこの作品に観客さえも感傷的になっていたという。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鏡の女たち", "鏡の女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "名匠・吉田喜重監督が長年熟考してきた広島の原爆問題に取り組み、「嵐が丘」以来15年ぶりに発表した新作。20数年ぶりに再会を果たした３世代の女性を通して、親子の愛情と絆を描く。24年前に失踪した娘・美和とおぼしき記憶喪失の女性を探しだした愛。彼女は記憶を甦らせるよう、この女性と美和が置き去りにした娘・夏来の３人でかつて暮らした広島へと向かう。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIDE ＆ SEEK", "コード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「フランキー・ザ・フライ」のダリル・ハンナ主演によるサイコホラー。アンとジャックの夫妻は幸福な生活を送っていたが、子宝に恵まれずに悩んでいた。彼らは産婦人科を訪ねて、人工授精の処置を受けることにする。アンは妊娠に成功し、あとは出産を待つばかりとなったのだが、ある日、彼女は何者かに襲われ、拉致、監禁されてしまう。そこにいたのは産婦人科の医者とその妻だった。彼らの目的とは？", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pianomania", "ピアノマニア", "", "", "http://image.eiga.k-img.com/images/movie/57335/315x210.jpg?4260", 97, "ピアニストを影から支える調律師にスポットライトを当てたドキュメンタリー。フランスの名ピアニスト、ピエール＝ロラン・エマールが、バッハ晩年の未完の傑作「フーガの技法」を録音することになり、演奏するピアノにスタインウェイ社の逸品「245番」を選ぶ。スタインウェイ社の技術主任でドイツ人調律師のシュテファンは、バッハ時代の古楽器を研究し、エマールからの細かい注文にも丹念にこたえながらピアノをたくみに調整していく。究極の響きを求めるピアニストと調律師の共同作業を追う一方で、ラン・ラン、ティル・フェルナー、アルフレート・ブレンデルら、シュテファンに絶大な信頼を寄せる名演奏家たちの貴重なリハーサル風景も盛り込まれる。", "2012-01-21", "77,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIFTY FIFTY", "50／50　フィフティ・フィフティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ゲリラ部隊を率いて、東南アジア某国に潜入した傭兵フレンチと、同国治安指令官ジェークはかつてともに戦った仲だった。今、敵味方となった二人に、CIAのエージェントが、極秘任務を依頼してくる。独裁政権の転覆と、前大統領の復権という政治的駆け引きに、否応なく巻き込まれる二人だが……。「アメリカン・グラフィティ」「アンタッチャブル」の俳優C・M・スミスの監督デビュー作。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLOOD BEAST TERROR", "吸血蛾クレア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "昆虫学の権威、マリンジャー教授の助手をしていた二人の学生が無残な死体となって発見された。マリンジャー教授はその死体の血液が異常に少なくなっていることに疑問を抱き、一人捜査を開始するが、やがて信じられないような事実に直面し……。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅姉妹", "紅姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "SM小説の大御所、団鬼六が自らの小説を映画化。悪徳高利貸しでSMクラブを経営する川村が逮捕される。川村はしにせ料亭の婿養子、精二に多額の借金をさせ、精二の妻とその妹を脅して意のままにすることを企んだのだ。調査が進むうち、川村の巧妙な手口と禁断の世界が暴露されていく。もともとはオリジナルビデオ用に製作された２本を１本に再編集し、劇場公開された。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妻と女の間", "妻と女の間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "瀬戸内晴美の同名小説をもとにした、豊田四郎と市川崑という異色の組み合わせによる共同監督作品。京都の紙問屋に生まれ育った４人姉妹のそれぞれの生き方を優しく描いている。物語の中心となる４姉妹には三田佳子、大空真弓、酒井和歌子、仁科明子といった女優陣が配されている。和服デザイナーの長女は年下のテレビ・ディレクターと関係を持っている。次女は婿をとって家業を継いでいるが、夫の女性関係に悩みが尽きない。結婚して団地住まいの三女はアルバイト先の雑誌編集者と関係を持っている。四女は独身で長女の家に暮らし、それとは知らずにテレビ・ディレクターに恋心を感じている。以上、４人４様の恋愛模様が艶やかに展開される。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BY DAWN'S EARLY LIGHT", "ラスト・カウントダウン 大統領の選択", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「ヒドゥン」でアヴォリアッツ国際ファンタスティック映画祭グランプリを受賞したJ・ショルダー監督が手掛けた近未来戦争アクション。ある日ソ連領内に正体不明のミサイルが打ち込まれ、アメリカからの攻撃と断定したソ連側が反撃を開始、核戦争への不安が高まる。しかしミサイルはソ連国内のクーデターによるものであった……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Road to Wellville", "ケロッグ博士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "コーンフレークの発明者として有名なケロッグ博士の療養施設で繰り広げられるドタバタ騒動を描くブラックコメディ。ケロッグ博士を信奉する妻に連れられ、博士の療養施設に入った男性。しかし彼は、徹底された菜食主義に禁欲生活という健康法についていけず……。T・コラゲッサン・ボイルによる実話に基づいた小説を「ザ・コミットメンツ」のアラン・パーカー監督が映画化。不気味なケロッグ博士をアンソニー・ホプキンスが怪演。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Manhunter", "刑事グラハム　凍りついた欲望", "", "", "http://ecx.images-amazon.com/images/I/21QWNDQA43L._SL210_.jpg", 120, "元FBI捜査官グラハムが、猟奇事件の捜査を要請され、刑務所にいるレクター博士を訪ねる。グラハムとレクターの記事を見た犯人は、レクターと連絡を取り、その記者を殺害。グラハムは殺害をつなぐ手がかりから犯人を追い詰める……。ハンニバル・レクターが登場するトマス・ハリス原作「レッド・ドラゴン」の映画化だが、「羊たちの沈黙」のヒット後、再発売されたビデオは改題された。", "1988-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大鹿村騒動記", "大鹿村騒動記", "", "", "http://image.eiga.k-img.com/images/movie/55985/315x210.jpg?4489", 93, "「どついたるねん」「亡国のイージス」の阪本順治監督と原田芳雄がタッグを組み、長野県・大鹿村に伝わる村歌舞伎と、そこにかかわる人々の悲喜こもごもを描く。シカ料理専門店を営む風祭善の前に、18年前に駆け落ちをして村を離れた妻・貴子と幼なじみの能村治が現れる。貴子は前頭葉に疾患を抱えて記憶を失っており、善はそんな貴子を治ともども店に住まわせる。村では善も出演する大鹿歌舞伎の公演を目前に控えていたが、貴子は18年前に演じた役のセリフだけは記憶しており、舞台上で善と向き合うときだけ、昔の姿に戻る。", "2011-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COOL SURFACE", "氷の仮面", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「ターミネーター２」の“T−1000”役で知られるR・パトリックが人間に潜む狂気を熱演するサスペンス。無名の女優ダニーとの情熱的な恋をもとにした、若手作家ジャービスの小説の映画化が決定。そのヒロインにダニーが選ばれたことから、ジャービスの胸に彼女に利用されたという疑惑が持ち上がる……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA TERRA TREMA", "揺れる大地・海の挿話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 160, "L・ヴィスコンティによるオールロケ、オール素人俳優で撮られたネオ・レアリスモを代表する１本。また、後期のヴィスコンティのきらびやかなスタイルとは対照的な、ゴツゴツとした骨太の演出が見られる上でも実に興味深い一編である。舞台は貧しいシチリア島の漁村。村の網元に搾取されて貧しい生活を送る漁民たちを見かねて、若者アントニオは組合を作ろうと呼びかける。網元の手下の妨害で警察に逮捕されたアントニオ。釈放されたアントニオは村人がもう自分の言うことに耳を傾けないのを知り、一人で網元から独立しようと決心する。彼の仕事はうまくいくかに見えたが……。デビュー前のフランチェスコ・ロージとフランコ・ゼフィレッリが助監督を務めている。DVDは「ルキノ・ヴィスコンティDVD−BOX１」に収録。", "1948-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちんころ海女っ子", "ちんころ海女っ子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "富永一朗の原案を前田陽一が演出したお色気喜劇。東京の近くに浮かぶ為朝島の観光会長は、海女に怪しげな水中レビューをさせ、観光を売りものにしてこの島を日本のハワイにしようと企んでいた。しかし海女たちはこれに猛反対。その観光会長と海女たちのすったもんだに、ストリッパーが絡んで大騒ぎとなる。元ノーマン・メイラー夫人、ホキ徳田が出演しているのも珍しい。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クロネズミ", "クロネズミ", "", "", "http://image.eiga.k-img.com/images/movie/55515/315x210.jpg?1546", 0, "エイベックスが同社の新人俳優やアーティストを起用し製作する青春映画シリーズ「ニュースター・シネマ・コレクション」の第2弾。自殺したはずのアスカから送られてきた携帯メールの指示で、深夜の学校に6人の同級生が集められる。そこに血だらけのネズミのマスクをかぶったナゾの人物が現れ、アスカの復しゅうが始まる……。主演に日テレジェニック2009に選ばれた米村美咲が起用され、「バトル・ロワイアル」シリーズの深作健太監督がメガホンをとる。", "2010-06-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅の伝説", "修羅の伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "利権争奪のためには、人の命など何とも思わない企業と政治家に対し立ち向かっていく昔気質のヤクザの姿を描く。小林旭主演のヤクザ映画。旭とは初共演の平幹二朗が“マル暴”担当の刑事を演じている。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Snezhnaya Koroleva", "雪の女王　新訳版", "", "", "http://image.eiga.k-img.com/images/movie/53145/315x210.jpg?1970", 65, "アンデルセンの物語を原作に、57年に旧ソビエト連邦で製作された傑作アニメーション。北国の小さな町で幸せな日々を送っていた少年カイと幼なじみの少女ゲルダ。ところがある冬の日、カイは恐ろしい雪の女王に呪いをかけられ、喜びも痛みもないという氷の宮殿へ連れ去られてしまう。暖かくなっても帰って来ないカイを心配したゲルダは、彼を探す旅に出る。ロシア語オリジナル版を新たに翻訳した“新訳版”として上映。", "2007-12-15", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸川乱歩の陰獣", "江戸川乱歩の陰獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "江戸川乱歩の怪奇・妖艶な世界を、職人・加藤泰が様式美を強調した絵作りで完全映画化。美貌の人妻・静子に届いた一通の脅迫状。そこには淫らな夫婦の痴態までが克明に記されていた。差出人は異色の推理作家・大江春泥。怯えた彼女は、春泥と対立する本格推理作家・寒川に助けを求める。寒川は快く引き受け春泥の足どりを追うが、見つからない。そこに第２の脅迫状が届き、予告通り静子の夫・六郎が隅田川の船着場に死体で浮かび上がる。さらに調査を続ける寒川は、いつしか静子と恋に落ち、深い関係になる……。映像、美術的面白さと同時に、乱歩らしい二転三転するストーリーも楽しめる本格推理劇。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nannerl, la soeur de Mozart", "ナンネル・モーツァルト　哀しみの旅路", "", "", "http://image.eiga.k-img.com/images/movie/55895/315x210.jpg?1823", 120, "18世紀を代表する作曲家ボルフガング・アマデウス・モーツァルトの姉、マリア・アンナ（通称ナンネル）の数奇な運命を描いたドラマ。3歳年下の弟が“神童”と絶賛される一方、14歳のナンネルも音楽の才能を開花させていた。ある時、ベルサイユ宮殿に招かれバイオリンを披露したナンネルは、皇太子ルイ・フェルディナンと出会い、恋に落ちる。皇太子に才能を認められ、作曲をすすめられるナンネルだったが、当時の欧州では女性が作曲家になることは許されていなかった。", "2011-04-09", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEPPING OUT", "ステッピング・アウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ミネリ、グリーン、ウォルターズといずれも舞台出身の演技派俳優をそろえたヒューマン・ドラマ。R・ハリスの大ヒット戯曲を彼自らが脚色したもので、ニューヨークのタップ教室に通う８人の生徒たちの日常をさりげなく綴っている。タップ教師のメイビス役を、舞台でも同役に扮したミネリが演じ、歌に踊りに本領を発揮。偏屈なピアノ演奏者役のS・ウィンタースにも注目。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jackie Brown", "ジャッキー・ブラウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "Q・タランティーノが「フォー・ルームス」以来、３年ぶりに撮り上げた長編で、ハードボイルド・タッチの人間ドラマ。拳銃密売人オデールに頼まれて現金密輸もこなす中年スチュワーデス、ジャッキー・ブラウン。彼女が連邦捜査官に目をつけられたことから、彼女やオデール、彼らにかかわる人間たちの運命が大きく変わっていく……。主人公に、1970年代からブラック・シネマやB級映画で活躍してきたP・グリアーをキャスティングするあたりが、いかにもタランティーノらしい。そのほか、オデール役のS・L・ジャクソン、キレた演技を見せるR・デ・ニーロ、今までのイメージを払拭してはすっぱな女を演じるB・フォンダなど、それぞれが個性的な演技を披露。ユーモアあふれるセリフ、鮮烈な暴力シーンを交えたタランティーノ調と相まって魅惑的な物語を作り上げている。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IMITATION　OF　LIFE", "模倣の人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "白人と黒人の二人の母親の友情、それぞれの娘が成長とともに直面する人種問題を描き、1934年アカデミー作品賞にノミネートされた秀作ドラマ。３歳の娘を一人で育てるベアトリスは、同じ年頃の娘を持つシングルマザーの黒人女性デリラと親しくなる。ベアトリスはデリラの協力で女実業家として成功するが、それぞれの娘たちが人種の問題で互いを嫌悪し合うようになり……。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Air Force", "空軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "1941年12月6日、B-17の9機編隊がハワイに向けての定期飛行に飛び立った。4番機メアリー・アンには操縦士クインキャノン大尉以下8人が乗り組んだ。翌日、彼らは日本による真珠湾攻撃を知る……。ハワイ真珠湾攻撃の1周年にあたる1942年12月7日に公開しようという計画で製作された戦意高揚映画。撮影が大幅に遅れ、公開は1943年2月に延期されたものの結果的には成功を収め、ワーナー作品の興行ランキングでは年間第4位となった。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DIVORCE", "ル・ディヴォース／パリに恋して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "J・アイヴォリーが、ベストセラー小説を映画化したロマンティック・コメディ。芸術の街パリを舞台に、米国人姉妹が出会いと別れを経て洗練された女性へと成長していく姿を描き出す。モラルやセックス、ファッションなど、米仏のカルチャー・ギャップが引き起こすすれ違いがユニークなスパイスに。米国人姉妹を「21グラム」で絶賛されたN・ワッツと人気女優K・ハドソンが生き生きと演じている。", "2003-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　DOLL　MASTER", "人形霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "持ち主に捨てられた人形が引き起こす殺人事件を描くコリアン・ホラー。大小さまざまな人形が飾られた森のギャラリーを舞台に、そこを訪れた若い男女５人の恐怖体験をスリリングに語り明かす。謎めいた美少女に扮したイム・ウンギョンの神秘的なたたずまいにも注目を。彫刻家ヘミらが人形師ジェウォンのギャラリーに招かれる。到着間もなく奇怪な出来事が続発し、ゲスト５人の意外な共通点が明らかに……。", "2005-07-16", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESCAPE TO WITCH MOUNTAIN", "星の国から来た仲間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "不思議な力を持つ二人の子供が、自分たちの出生の謎を探ろうとするが、この二人の力を自分のものにして悪事に利用しようとする悪漢が彼らを追う……。子供たちに夢を与えるウォルト・ディズニー・プロダクションが手掛けたSFファンタジー。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ulzana's Raid", "ワイルド・アパッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　西部開拓時代の末期。アパッチ族の凶暴な戦士ウルザナが、息子や部下を引き連れ、居留地から逃亡。騎兵隊を率いる若き指揮官デブイン中尉と、その補佐役でアパッチ族に詳しいベテランの斥候マッキントッシュが討伐に向かう。マッキントッシュの予想通り、ウルザナ一味は先々で凄惨な殺戮を繰り返していくが……。ロバート・アルドリッチ監督とバート・ランカスターが再び手を組んだ西部劇。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あいつと私", "あいつと私", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "やり手の女美容師は、出世をエサに弟子の美容師を、不倫で生んだ息子のセックスのはけ口に。その息子は大学で授業中、突然“夜の女を買った”と叫び、そこからひと騒動が起こる。石坂洋次郎の青春小説に材を取って、恋愛とセックスをあっけらかんと描く。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たまの映画", "たまの映画", "", "", "http://image.eiga.k-img.com/images/movie/55605/315x210.jpg?2561", 0, "1990年のメジャーデビューシングル「さよなら人類」で一世を風びし、03年に解散したバンド「たま」の元メンバーである石川浩司、滝本晃司、知久寿焼にスポットを当てたドキュメンタリー。時代や環境が変化していくなかでもスタイルを変えず、メディア露出よりも生のライブに重点を置いて活動し続ける彼らの姿を追う。メガホンをとるのは本作が劇場長編デビューとなる今泉力哉監督。", "2010-12-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ABYSS SPECIAL EDITION", "アビス 完全版", "", "", "http://ecx.images-amazon.com/images/I/51tFndx0LXL._SL210_.jpg", 171, "根強いファンを持つ「アビス」のオリジナル版から削除されていた30分間を、J・キャメロン監督自らの手で復元した待望の完全版。復元されたシーンの最大の見ものは、海底都市浮上にともなう大津波襲来の驚異的なSFX。CGを駆使したこの壮大さはマニアならずとも必見。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京人間喜劇", "東京人間喜劇", "", "", "http://image.eiga.k-img.com/images/movie/56685/315x210.jpg?4658", 140, "劇作家・平田オリザの主宰する劇団青年団の演出部に所属する深田晃司監督が、フランスの文豪バルザックの作品群「人間喜劇」に着想を得て手がけた長編映画。ダンサーのサインを求めるファンの女性2人が雨の夜の街を駆け抜ける「白猫」、アマチュアカメラマンの女性が開いた個展の一日を通じて友情への期待と失望を描く「写真」、右腕を事故で失った夫とその妻との間に横たわる溝を描き出す「右腕」の3編で構成。", "2011-07-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Critters 3", "クリッター3", "", "", "http://ecx.images-amazon.com/images/I/519mVvxRlbL._SL210_.jpg", 90, "目にするものを片っ端から食い荒らし、増殖してバウンティ・ハンターたちをてこずらせる愛すべき毛玉怪物クリッターの姿を描いたシリーズ第３作。ひそかに産みつけられたクリッターの卵が孵化し、町は再びクリッター・パニックに。前作よりもパワーアップしたクリッターたちの暴れっぷりが見もの。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIFE STINKS", "メル・ブルックス　逆転人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "大金持ちの坊っちゃんとして生まれ、ぜいたく三昧に暮らしてきたビジネスの天才が、ひょんなことからロサンゼルスのスラム街で暮らすことに。金もコネも身分も捨てて、ダウンタウンでのサバイバル生活を体験する彼だが……。「スペースボール」以来４年ぶり、喜劇王ブルックスが出演した人情コメディ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXPLORERS", "エクスプロラーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "SF好きの少年ベン、ウォルフガング、ダレンの３人が体験する不思議な出来事を、ほのぼのとしたタッチで綴ったSF映画。謎の宇宙人からのメッセージを頼りに作り上げた宇宙船サンダー・ロード号に乗って、大宇宙へ旅立つ彼らの行方は……。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2011-2012 ヘンデル「ロデリンダ」", "METライブビューイング2011-2012 ヘンデル「ロデリンダ」", "", "", "http://image.eiga.k-img.com/images/movie/57165/315x210.jpg?3573", 241, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を映画館で上映する「METライブビューイング」2011～12年シーズンの5作目。バッハと並びバロック後期を代表する作曲家として知られるヘンデルが手がけた傑作オペラ。7世紀の北イタリア、ランゴバルト王国。ミラノ王ベルタリードの妃ロデリンダは、夫の戦死の報を受け悲嘆に暮れていた。新たに王座についたグリモアルドはロデリンダとの結婚を望むが、その時、死んだはずのベルタリードがロデリンダの前に現れる。", "2012-01-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAVAGES", "野蛮人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "未開のジャングルに住む原始種族、ザ・マッド・ピープル。ある儀式のさなか、上空から降ってきたクリケットのボールを追ううちに彼らは、朽ち果てた屋敷にたどり着いた。今まで見たことのない絵画や調度品、そしてオモチャに触れた彼らは……。アイヴォリーが一風変わった設定で描く文明批判色の強い作品。", "1972-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("卑弥呼", "卑弥呼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ナゾの耶馬台国の世界を、女王ヒミコを中心に描き、土方巽の舞踊などで様式美あふれる映像に仕上げた古代の物語。異母弟タケヒコを愛するようになった女王ヒミコ。しかしタケヒコは遠い国から自分を慕ってやって来たアダヒメを愛するようになり……。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHTWISH", "ナイトウィッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "大学の超心理学の実験室。オカルト現象の研究者を養成するため、教授のプログラムした恐ろしい夢を見ることで、研究生たちは恐怖に打ち勝つ訓練を受ける。やがて教授の狂気が徐々に暴かれていくが……。夢か現実か、果てしない恐怖の迷路が続く異色のサイコ・スリラー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHORE", "ボンデージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ダウンタウンの片隅に立つ娼婦リズの一日を通して、暴力、レイプ、変質者、浮浪者、ヒモ、ドラッグ、アル中など、彼女を取り巻く病んだ都市の異常な状況が克明に描かれていく。レゲエ・ミュージックを効果的に使った、K・ラッセルの現代社会に対する強烈な怒りのメッセージ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Soul Kitchen", "ソウル・キッチン", "", "", "http://image.eiga.k-img.com/images/movie/55195/315x210.jpg?2281", 99, "「愛より強く」「そして、私たちは愛に帰る」で世界的に高く評価された、ドイツのファティ・アキン監督による群像コメディ。ハンブルクで暮らす青年ジノスは、経営するレストランがうまくいかなかったり、愛する恋人が上海に行ってしまったりと、不運続きの日々を過ごしていた。そんなある日、新しく雇ったシェフの料理が評判を呼び、店は大繁盛となる。そこでジノは、店を兄に任せて上海へ向かおうとするが……。2009年ベネチア国際映画祭で審査員特別賞を受賞した。", "2011-01-22", "7,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長青春篇 つっぱり清水港", "次郎長青春篇 つっぱり清水港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "松竹喜劇映画の強力な担い手の一人、前田陽一監督による作品。バクチ好きの青年・次郎長が、気の合う仲間たちを数人集めて、やがて絶大なヤクザ一家を形成するまでを描く。次郎長はじめ、鬼吉、石松、小政・大政・直吉・大五郎といったおなじみの７人だけで、数十人の手下を連れた銀蔵一家へ殴り込んだ時の、ぶざまながらも懸命につっぱって活躍する姿がさわやか。また、清水一家のメンバーを若手俳優に交じって吉本興業のお笑いタレントたちが好演している。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOEMON", "GOEMON", "", "", "http://image.eiga.k-img.com/images/movie/53155/315x210.jpg?3146", 128, "「CASSHERN」の紀里谷和明が同作以来5年ぶりに手がけた監督第2作。織田信長、明智光秀が相次いで倒れ、豊臣秀吉が政権を握った1582年、金持ちから盗んだ物を庶民に分け与える義賊・石川五右衛門は、ある重大な秘密が隠された財宝を盗んでしまい、その秘密を追う石田三成や徳川家康らとの戦いに巻き込まれていく。五右衛門役に江口洋介ほか、大沢たかお、広末涼子ら豪華キャストが集う。デジタル技術を駆使して描かれる戦国時代の世界観も見どころ。", "2009-05-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コロの大さんぽ", "コロの大さんぽ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 15, "宮崎駿が館主を務める三鷹の森ジブリ美術館にある「土星座」にて公開された、スタジオジブリのオリジナル短編アニメーション。監督、脚本、原作は宮崎駿。家を抜け出した子犬のコロは、飼い主のサワ子を追いかけようとして迷子になってしまう。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Happy Feet Two", "ハッピーフィート2 踊るペンギンレスキュー隊", "", "", "http://image.eiga.k-img.com/images/movie/55405/315x210.jpg?4242", 100, "第79回アカデミー賞で長編アニメーション賞を受賞した「ハッピーフィート」（2006）の続編。タップダンスが得意なペンギン、マンブルの悩みは、息子エリックがダンス嫌いなこと。ダンスが苦手なエリックは父の下から逃げ出してしまうが、そんな時、ペンギンたちが暮らすエンペラーランドが巨大な氷山の倒壊に巻き込まれ、母親のグロリアや仲間たちが閉じ込められてしまう。エンペラーランドの外にいたマンブルやエリックらは仲間たちを救うために奔走。エリックは父マンブルの勇敢な姿に大切なことを学んでいく。", "2011-11-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ORDET", "奇跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "デンマークが誇る巨匠C・T・ドライエルの名作。1930年代デンマークのユトランド半島。裕福な大農場経営者モルテン・ボーエンは敬虔なキリスト教徒（グルンビー派）で３人の息子がいた。長男は勤勉だが信仰心が薄く、次男ヨハネスは神学を学びすぎて正気を失っていた。しかも、三男の恋人は対立宗派（インナーミッション派）の仕立屋ペーターの一人娘。二人の結婚は宗教論争を引き起こし平和な家に波紋が起こる。信仰厚い長男の嫁インガは一家の支えだったが、難産の末に息をひきとる。やがて理性を取り戻したヨハネスは生命の復活を神に祈る。黒白美学の頂点を極めた映像が秀逸。原題は『言葉』。", "1955-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Verdict", "評決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "　過去の一件が原因でどん底の日々を送っていた中年弁護士のポールは医療ミス事件の裁判にかつぎ出されることに。被害者を目のあたりにしたポールは正義に目覚め、病院側が申し出た示談を蹴ってしまう。やがて病院側は辣腕弁護士コンキャノンを雇い、ポールと法廷で争うことに。ポールは裁判の準備を進める一方、ローラという女性と親しくなるが……。正義を貫こうとする弁護士の戦いを描いた法廷ドラマ。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Identity", "“アイデンティティー”", "", "", "http://image.eiga.k-img.com/images/movie/1445/315x210.jpg?4266", 90, "豪雨のために交通が遮断され、さびれたモーテルに泊まることになった10人の男女は、若いカップル、わがままな女優とそのマネージャー、囚人と彼を護送する刑事、子供連れの夫婦など。まもなく女優が行方不明になり、その頭部が乾燥機の中で発見される。犯人は誰なのか？　その目的は？　謎の犯人は殺人を続けていく。監督は「17歳のカルテ」のジェームズ・マンゴールド。脚本は「キラー・スノーマン」のマイケル・クーニー。", "2003-10-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOM ET LOLA", "トムとローラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "トムとローラという９歳の少年と少女。他の子供たちと何ら変わることのない二人だったが、彼らは外界に対する反応や抵抗力がなく、細菌から守るためプラスティック・カプセルの中に住んでいる。ある日二人はこのカプセルを抜け出て、夢にまで見た外界へと飛び出していく。詩情あふれるアドベンチャー・ファンタジー・シネマの傑作。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Universal Soldier: Regeneration", "ユニバーサル・ソルジャー：リジェネレーション", "", "", "http://image.eiga.k-img.com/images/movie/55285/315x210.jpg?4207", 97, "「インデペンデンス・デイ」「2012」のローランド・エメリッヒ監督が、ジャン・クロード＝バン・ダムとドルフ・ラングレンを主演に描いた大ヒットSFアクション「ユニバーサル・ソルジャー」（92）の続編。新鋭ジョン・ハイアムズがメガホンをとる。最先端の兵士再生プログラムNGUによって誕生したスーパー・ソルジャーを擁するチェチェン民族主義のテロリストが、ロシア首相の子息を誘拐し、チェルノブイリ原子力発電所を占拠する。初期兵士再生プログラムから生まれた最強兵士リュック（バン・ダム）は、立ちはだかるNGUの最強ソルジャーと、凍冬眠から目覚めた旧敵スコット（ラングレン）との対決に臨む。", "2010-06-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Midnight FM", "ミッドナイトFM", "", "", "http://image.eiga.k-img.com/images/movie/57745/315x210.jpg?2414", 106, "「夏物語」のスエと「オールド・ボーイ」のユ・ジテが共演したサイコスリラー。人気ラジオ番組でDJを務めるソニョンは、娘の病気治療に専念するため番組を降板することになる。最後の番組放送は順調に進んでいたが、放送中に見ず知らずの男から「指示通りに曲をかけなければ家族を殺す」という脅迫電話がかかってくる。同時にソニョンの携帯電話に送られてきた映像には、捕らわれた家族の姿が映し出されていた……。主演のスエは、本作で大鐘賞と並ぶ韓国の2大映画賞「青龍映画賞」で主演女優賞を受賞。冷酷な殺人鬼役にユ・ジテ。", "2012-05-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Street Fighter: The Legend of Chun-Li", "ストリートファイター　ザ・レジェンド・オブ・チュンリー", "", "", "http://image.eiga.k-img.com/images/movie/54045/315x210.jpg?3774", 97, "1987年の第1作から全世界で累計2500万本を売り上げた大ヒットゲーム「ストリートファイター」の実写映画化で、主人公はシリーズの人気キャラクター・春麗（チュンリー）。幼い頃に父親が拉致され、母親を失い、天涯孤独となった春麗は、ストリートファイターとして成長し、父をさらった秘密結社“シャドルー”に立ち向かう。監督は「ロミオ・マスト・ダイ」のアンジェイ・バートコウィアク、春麗役はTVドラマ「ヤング・スーパーマン」のクリスティン・クルック。", "2009-02-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHILDREN OF THE REVOLUTION", "革命の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "オーストラリア映画界の有能なスタッフ、俳優が結集。“革命”に人生を捧げ運命にほんろうされた人々の愛憎模様を、大胆な着想で綴るブラック・コメディ。1951年、スターリンに熱烈なファンレターを送り、クレムリンに招待された革命家ジョーン。ところが一夜をともにしたスターリンを腹上死させてしまう。スターリンの子供を身ごもって無事オーストラリアに帰国した彼女だったが、やがて生まれた子供ジョーは父親ゆずりの独裁者ぶりを発揮し始める。", "1996-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蟻の街のマリア", "蟻の街のマリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "“蟻の街”と呼ばれる浅草近くのバタヤ街に、何不自由なく育った娘が“何でもする”と自治組織に飛び込んでくる。熱心なカトリック教徒の彼女を、初めはただの気まぐれと見ていた街の者も、やがてマリアと呼び愛し始める……。当時のマスコミをにぎわせた実話を基に映画化した、感動の一編。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GHOSTS　OF　MISSISSIPPI", "ゴースト オブ ミシシッピー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "R・ライナーが実際に起こった殺人事件の再審の記録を映画化した法廷サスペンス。1960年代のミシシッピー、黒人公民権運動家エバーズが家族の目の前で射殺されるが、容疑者の白人至上主義者ベックウィズは陪審員判決で審理無効となって釈放されたばかりか、住民の英雄となってしまう。それから30年後、エリートコースを歩む検事補ボビーは例の事件でベックウィズを釈放した判事の娘と結婚していた。そんな彼の元に、エバーズの未亡人マーリーが現れ、事件の再審を求める。事件に興味を持ったボビーは再調査を始めるが、妻は激怒し、二人は離婚することに。それでも、ボビーは真相究明を続けるが……。ボビーの執念と法廷での真理追究をヒューマニズムあふれるタッチで描く力作。ベックウィズを演じたJ・ウッズがアカデミー助演男優賞にノミネートされた。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOUNTY HUNTER", "バウンティ・ハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "保釈中に逃亡した犯罪人を追い詰めて逮捕するという“現代の賞金稼ぎ”デューク。ベトナムで戦友だった男の死を個人的に調べるため、中西部のとある町にやって来た彼は、その鍵を握る町の保安官ベネットと出会う。アクションスター、ギンティが監督、脚本、主演とフル回転。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Creepshow", "クリープショー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "　スティーブン・キングとジョージ・A・ロメロが組んだオムニバス・ホラー。少年が夢中になっている俗悪ホラー・コミックの世界が再現される。娘に殺された父親の復讐劇、謎の隕石によって植物化していく農夫の悪夢、浮気した男女を海辺に埋める夫の末路、禁断の箱をめぐる惨劇、潔癖症の男に襲いかかるゴキブリの恐怖、そしてプロローグとエピローグで構成。スティーブン・キングは出演も務め、自滅していく農夫役を楽しそうに演じている。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Full Frontal", "フル・フロンタル", "", "", "http://ecx.images-amazon.com/images/I/21GPDW9E84L._SL210_.jpg", 101, "「オーシャンズ12」のS・ソダーバーグが描いた群像コメディ。映画業界を取り巻く人々の、不器用でシュールな日常をジュリア・ロバーツら豪華キャストで描き出す。冒険好きのソダーバーグは、本作の大部分をDVカメラで撮影。出演者には衣装は自前、現場には一人で車を運転してくることなど、アンチ・ハリウッド的な10個のルールが課せられたとか。今日は映画プロデューサー、ガスの誕生日。パーティーに招待されている脚本家のカールは、副業の雑誌編集の仕事がいきなりクビになる。その頃カールの義妹でマッサージ師のリンダは、相手がガスとは知らずマッサージをしていたが……。", "2003-12-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざの詩", "やくざの詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "小林旭が、殺された恋人の復讐を誓う男を演じたアクション・ドラマ。犯人を探す手がかりとして、スペイン製の銃ゲルニカの銃弾が、効果的に使われる。恋人を殺した犯人を探すため、ピアノ弾きと称して佐伯組にもぐり込んだ哲也。彼は、狙う相手が、かつて自分が命を救った拳銃ブローカー、相川一郎の弟・次郎だと知り……。垂水悟郎が次郎を怪演。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Baby Mama", "ベイビーママ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("押忍!! 空手部", "押忍!! 空手部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ありとあらゆるワルどもが幅を利かせる殺伐とした街・大阪に、超ヘビー級の肉体と圧倒的なパワーを持つスーパーヒーローが登場。だがこの男、バリバリの硬派ながら人情にもろく……。同名コミックを原作に、ハード・アクションの雄、村川透が描いた、硬派青春ドラマ。肉体派松田勝（現・松田優）の怪演が見もの。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wicker Man", "ウィッカーマン", "", "", "http://image.eiga.k-img.com/images/movie/34155/315x210.jpg?1501", 101, "73年に製作されたイギリスの名作カルト映画を、ニコラス・ケイジ主演でリメイクしたサスペンス・スリラー。白バイ警官のメイラスの元に、8年前に失踪した婚約者ウィローから手紙が届いた。彼女の故郷である孤島サマーズアイルで、娘のローワンが行方不明になったというのだ。メイラスは島を訪れ捜査を開始するが、よそ者を嫌う島民たちは非協力的で真相を語ろうとしない。やがてメイラスは、島に秘められた衝撃の事実を知る……。", "2007-09-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ПРИБПИЖЕНИЕ К АПОКАЛИСИСУ ЧЕРНОБЫЛЬ РЯДОМ", "チェルノブイリ黙示録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "1986年４月26日に発生した史上最大規模のチェルノブイリ原発事故を直後から追ったドキュメント、「チェルノブイリ・シンドローム」のスタッフが新たに作り上げた作品。事故の真相を知らずに危険な作業にあたった人々や被爆した人々の“５年後”を生々しく記録している。", "1990-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SURVIVING THE GAME", "サバイビング・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "“人間狩り”を題材にしたサバイバル・アクション。人生に絶望してホームレスとなったメイスンはある日、狩猟のガイドの仕事を紹介される。だが、そこで彼に与えられた本当の仕事は、“獲物”になること。冷酷な６人のハンターに追われるメイスンは必死の逃亡の中で、やがて生きる情熱を取り戻すが……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Midnight Meat Train", "ミッドナイト・ミート・トレイン", "", "", "http://ecx.images-amazon.com/images/I/51BbtIMg6zL._SL210_.jpg", 0, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢見通りの人々", "夢見通りの人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "宮本輝の同名小説を森崎東が映画化した人情喜劇。詩人志望の青年と美容師見習いの女性、そして元ヤクザでいかつい肉屋の長男の三角関係を中心に、大阪・鶴橋の商店街“夢見通り”に住む、ひと癖もふた癖もある愛すべき欠陥人間たちの群像を描き出している。劇団SETの小倉久寛が初主演に挑戦。さえない詩人のタマゴと嫌われ者の肉屋が、美容師見習い嬢をともに好きになって大げんかするものの、ともにふられて慰め合う。そんなシーンに森崎監督一流のペーソスがにじむ。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("温泉ポン引女中", "温泉ポン引女中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "石井輝男監督門下の荒井美三雄が、第１回作品として監督した“温泉”もの。南紀白浜の温泉旅館の女中頭・イク代は、おかかえのポン引き女中に売春をやらせて宿は大繁盛。しかし関東ヤクザ・昇龍会が乗り込んできてキャバレーを開業し客を取られるが、イク代はへこたれない。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マリーの獲物", "マリーの獲物", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "『アクションコミックス』連載の同名漫画を映画化。この世の醜悪を葬り去ることを生業とする、女賞金稼ぎマリーの活躍をエロスとバイオレンスたっぷりに描く。マリーに香港マフィア、拱組織を壊滅させるという仕事が来る。拱は25年前、マリーの父・矢島と宝石の密輸をしていたが、ビルマ王朝に伝わるスタールビーを巡り仲間割れ、おまけに、マリーの母・リサをレイプし、殺していた。母への恨みをはらすためマリーは引き金を引く……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たけくらべ", "たけくらべ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "樋口一葉の短編小説を映画化。明治時代の吉原界隈。遊女を姉にもつ美登利は竜華寺の信如に淡い想いを寄せているが、やがて時が来て吉原に入っていく。遊廓界隈の少年少女の心情を、名匠・五所平之助監督が繊細に描いている。“歌わない”ひばりが好演。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GO-CON！", "GO-CON！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "深夜番組『つんくタウン』の若い才能を発掘するという企画から生まれたラブ・コメディ。毎週末、合コンをセッティングして楽しんでいる男たち、汰一、浩、甲斐の３人。リーダーの汰一はフェロモン、浩は若さと可愛さ、甲斐は冬季オリンピックの金メダリストという各々の武器をフルに生かして女をゲットしてきた。そんな彼らが真剣に愛を見つめるときがきて……。プロデューサーはフジテレビで数々のヒット・ドラマをプロデュースしてきた栗原美和子。ドラマ制作スタッフだった新谷暢之が初監督に挑戦。日本固有の文化“合コン”を舞台にして、男と女のドラマを描き出していく。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Letters from Iwo Jima", "硫黄島からの手紙", "", "", "http://image.eiga.k-img.com/images/movie/33345/315x210.jpg?3001", 141, "クリント・イーストウッド監督が、太平洋戦争最大の激戦だったといわれる硫黄島の戦いを日米双方の視点から描く映画史上初の2部作。アメリカ側から硫黄島を描いた「父親たちの星条旗」と対をなす本作は、硫黄島の戦いに参加した一人の若き日本軍兵士の目を通して、約2万2千人の日本軍を率いたアメリカ帰りの名将・栗林忠道中将らの戦いを描く。主演の栗林中将に渡辺謙、その他二宮和也、伊原剛志、加瀬亮、中村獅童がそれぞれ日本軍兵士として出演。", "2006-12-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花と野盗の群れ", "花と野盗の群れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "嵯峨野一帯を暴れ回る若き野盗・源の太郎坊。彼には父を殺した豊臣家を葬ろうとする野望があった。やがて徳川の大阪攻めが始まり、太郎坊は徳川方に混じるが、正体がバレ捕まってしまう。仇は死んでしまい、目的を失ってしまった太郎坊だが……。関ヶ原の戦いを背景にした時代劇アクション。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents kiss!　「クシコスポスト」", "桃まつり presents kiss!　「クシコスポスト」", "", "", "http://image.eiga.k-img.com/images/movie/57915/315x210.jpg?1928", 25, "", "2009-03-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CINE´MA DE NORTE TEMPS：ABBAS KIAROSTAMI VE´RITE´ ET SONGE", "ドキュメント／キアロスタミの世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "「そして人生はつづく」「友だちのうちはどこ？」などで知られるイランの映画監督アッバス・キアロスタミのインタビューを、「夜の天使」などの監督J・P・リモザンが行ったドキュメンタリー。「そして人生はつづく」に映し出された行程をたどり、そして「友だちのうちはどこ？」に出演した少年を訪ねながら、キアロスタミの映画についての考えが紹介されていく。もともとは、フランスのテレビ局が各国の映画監督を若手監督がインタビューするというシリーズの１本として作られ、日本では1995年に劇場公開された。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Aliens vs. Predator: Requiem", "AVP2 エイリアンズVS.プレデター", "", "", "http://image.eiga.k-img.com/images/movie/53075/315x210.jpg?3669", 0, "エイリアンとプレデターの2大人気モンスターが激突し、話題となった「エイリアンVS.プレデター」の続編。前作のラストでプレデターの体内に産みつけられたエイリアンの卵が成長し、両種の特徴を受け継いだ新種のエイリアン“プレデリアン”が誕生。コロラドの森に墜落したプレデターの宇宙船から、プレデリアンやプレデター、そして宇宙船内に潜伏していたエイリアンたちが次々と地上に降り立ち、人間たちを巻き込んで壮絶な戦いを繰り広げる。", "2007-12-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("孩子王", "子供たちの王様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "文化大革命の嵐吹き荒れる頃の中国。山間部の貧しい農村の中学校に、ひとりの若者が“下放”され教師としてやって来る。生徒たちは学ぶ喜びを知らず、その子供たちにイデオロギーを教え込むだけの教育に、教師は疑問を感じはじめる。彼は自分なりの教育方法を探し、子供達に表現するという事の喜びを教えていく……。詩的で象徴性に満ちた映像で、文革の過ちを指摘する秀逸な作品である。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("牡丹燈籠", "牡丹燈籠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "社会派・山本薩夫が取り組んだ唯一の怪談映画。三遊亭圓朝が創り上げた古典的傑作を“恐怖”よりも、“愛”“情念”を全面に押し出した恋物語として映像化した。恋しい男のもとに夜な夜な現れる妖艶な美女幽霊の凄まじい執念を描く。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FERRIS BUELLER’S DAY OFF", "フェリスはある朝突然に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "その日もシカゴは快晴だった。こんな日に学校へ行くなんてバカげたこと。親を軽くだまくらかして、フェリスはいつもの通り親友のキャメロンを誘って街へ繰り出す。エスケープの常習犯にイラつくルーニー校長も、これ以上は許すまいと彼をしょっぴくために街へ捜索に出かけるのだった。パレードに飛び入りしロックを歌い踊るフェリスたちに、追っ手が迫る。M・ブロデリックがちゃめっけたっぷりに、パレードでビートルズの『ツイスト・アンド・シャウト』を歌うシーンは圧巻。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT HAPPENED AT THE WORLD’S FAIR", "ヤング・ヤング・パレード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "女に弱い男とポーカーに目のない男。そんな、空の便利屋パイロット二人組が、シアトルの世界博を背景に繰り広げる青春コメディ。悪者にだまされて、いったんカナダへの密輸を引き受けるが、真相を知った二人は乱闘の末、悪者を倒す。プレスリーは10曲を歌う。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛は惜しみなく", "愛は惜しみなく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "川内康範の原作による日活歌謡メロドラマ。父とともに人形劇団を率いて九州地方を旅するミサは、兎の人形の靴を拾ったことが縁で大学ボクシングの選手である年男と知り合い、いつしか恋に落ちていく。一方で、ミサはひょんなことから歌手デビューが決まり、次第に売れっ子になっていくのだが……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("痴漢男", "痴漢男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「電車男」に続いて、ネット掲示板2ちゃんねるから派生した物語を映像化。痴漢に間違えられたことから恋愛に目覚め、童貞卒業を目指す大学生の奮闘を、ユーモラスに描く。2ちゃんねるでのアドバイスを得て暴走気味に行動する主人公の面白さと、不器用ながら一途な恋心の切なさが魅力。痴漢に間違えられたオタク青年、金太。これが契機となって恋に目覚めるも恋愛経験のない彼は、ネット掲示板に助言を求めるが……。", "2005-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLUE ANGEL", "嘆きの天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "マレーネ・ディートリヒの主演で名高い「嘆きの天使」を、E・ドミトリクが再映画化。ドイツのある町の高校教師ラートは、キャバレー“嘆きの天使”の歌姫ローラ・ローラに心奪われ、次第に堕落していく……。スウェーデン出身のM・ブリットが、妖艶なローラ・ローラ役を演じている。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Renard et L'enfant", "きつねと私の12か月", "", "", "http://image.eiga.k-img.com/images/movie/53535/315x210.jpg?2532", 96, "アカデミー賞長編ドキュメンタリー賞を受賞した「皇帝ペンギン」のリュック・ジャケ監督による長編第2作で、初のフィクションドラマ。監督自身の幼少期の体験をもとに、きつねと少女の交流を、豊かな自然と四季の移ろいとともに描く。学校からの帰り道、少女リラは美しい野生のきつねに出会い、テトゥと名付ける。テトゥに会うため毎日森に通うようになったリラ。その想いに応えるように、最初は警戒心を抱いていたテトゥも次第に距離を縮めていくが……。", "2009-01-10", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギンギラ太陽's はじめてモノ語り　ボーン・トゥ・ラン　夢の超特急", "ギンギラ太陽's はじめてモノ語り　ボーン・トゥ・ラン　夢の超特急", "", "", "http://image.eiga.k-img.com/images/movie/56955/315x210.jpg?1855", 45, "福岡を拠点に活動し、さまざまな「モノ」を擬人化した舞台で人気の劇団「ギンギラ太陽's」。同劇団の代表作で、新幹線たちが主役の「BORN TO RUN」を親子で楽しめるよう45分間に再構成して新たに撮影。映画館で上映する。東北、九州、山陽、東海の各新幹線は自分こそが一番速いとケンカばかり。それをみていた0系新幹線のゼロ長老は、かつて奮闘した初代0系新幹線たちの物語を語って聞かせる。", "2011-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GHARE BAIRE", "家と世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "「大地のうた」３部作で知られるインドの代表的監督S・レイが、1981年の「遠い道」に続いて発表した作品。原作は、アジアで初めてのノーベル文学賞受賞詩人として名高いタゴールの同名小説。1900年初頭、ベンガル分割令に揺れるインドを舞台に、理想主義者で誠実な夫と、その友人で野心に燃える活動家、そして二人の間を揺れながら自立していく女性の３人が繰り広げる葛藤のドラマを、レイ監督らしい堅実でリアリズムあふれる演出で描き上げている。", "1984-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bande a part", "はなればなれに", "", "", "http://image.eiga.k-img.com/images/movie/1275/315x210.jpg?1431", 96, "肌をさす冷気がこころよい冬のパリ。推理小説マニアのフランツとアルチュールは北欧からやってきた美しいオディールに一目惚れした。フランツとアルチュールは大の親友だが、性格は正反対。金はないがヒマだけはあるのが共通点だった。そんな3人がなぜか、冬のある日、セーヌの支流マルヌ川沿いの邸に眠る大金の強奪計画を実行することに。計画は二転三転、やがてついに作戦決行の日がやってくるのだが……。", "2001-02-03", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIRECT HIT", "ダイレクト・ヒット 殺し屋のバラード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "凄腕の殺し屋とCIAとの対立を描くアクション映画。CIAの仕事をしていた殺し屋のジョンは、今度の仕事を最後に足を洗う決心をしていた。そこへCIA長官からサバンナという若い女性の殺害を依頼される。彼女は上院議員の秘密を握り、脅迫していたというのだ。しかし、ジョンが調査したところ彼女は無実で、それはCIAの罠だとわかる。怒りに燃えるジョンはCIAへ銃口を向けるのだった。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE NOTTI DI CABIRIA", "カビリアの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "カビリアはローマの夜の女。人のよい彼女は何度も男にだまされて泣きをみるが、人を信じて疑わない無垢な魂をもっていた。夜ごと男たちに拾われ、ある時は有名な映画スターに会ったり、ある時は教会で敬虔な気持ちに襲われたり。そんなある日、彼女の前にオスカルという青年が現れる。カビリアはオスカルに夢中になり彼から結婚を申し込まれると、家を売り払ってすべてを金に替えてしまう。だがオスカルもこれまでの男と同じだった。泣きくずれるカビリア。それでもその泣き顔にいつしか微笑みが戻ってくる。無垢な魂の遍歴を優しく描いたペーソスの漂う名作。マシーナの演技が素晴らしい。", "1957-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUB DOWN", "サブダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "北極海での軍事演習に向かうアメリカ海軍の原子力潜水艦ポートランドに、民間の科学者リック、ローラ、ラインハルトが同乗する。ところが３人が小型艇での海洋調査中、国籍不明の潜水艦がポートランドに激突。危うく難を逃れたリックらは、酸素の残り少ないポートランドに戻り、決死のサバイバルを繰り広げる。現実に起こりうる軍事的な非常事態を、潜水艦内の息詰まるような密閉空間を舞台に描くサスペンス。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Immortals", "インモータルズ　神々の戦い", "", "", "http://image.eiga.k-img.com/images/movie/56105/315x210.jpg?1662", 111, "「ザ・セル」「落下の王国」のターセム・シン監督がギリシャ神話を題材に描くアクションスペクタクル。世界支配を企むハイペリオンに対し、主神ゼウスにより鍛えられた勇者テセウスが立ち上がる。ポセイドン、アテナらオリンポスの神々の力を借り戦うテセウスだったが、ハイペリオンはオリンポスの神々によりかつて地底に封印されたタイタン族を復活させてしまう。主演は「マン・オブ・スティール」（2013）で新たなスーパーマン役にも抜てきされたヘンリー・カビル。", "2011-11-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUNO　AND　THE　PAYCOCK", "ジュノーと孔雀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ヒッチコック監督初期の劇場未公開作。サスペンスではなく、人妻の苦悩を描いた悲劇ドラマだ。冒頭での被弾シーンをはじめ、機関銃の効果音の使い方などに監督のセンスが見え隠れする。革命運動が激しいダブリンに住む、夫妻とその息子と娘の４人家族。だが、まともに働いているのは妻だけで、夫は酒浸りで息子は罪を犯して指名手配中の身。そんな時、幸運らしき話が舞い込む。", "1930-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GENUINE RISK", "ザ・リスク 甘く危険な賭け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "若きギャンブラーの破滅的な恋を描くハードボイルド・サスペンス。金を手に入れると競馬場で使い果たす、根っからのギャンブル好きのヘンリー。とあるバーで美人と知りあい、二人は次第に深い関係に。競馬以上に恋が人生最大のギャンブルとなってしまった彼だが、やがて女のすすめで組織的なゆすりの仕事を引き受ける。ところがそこには恐ろしい罠が待っていた。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dresden", "ドレスデン、運命の日", "", "", "http://image.eiga.k-img.com/images/movie/33935/315x210.jpg?1962", 150, "第2次世界大戦末期、連合軍の爆撃により壊滅的な被害を受けたドイツ東部の都市ドレスデンの悲劇を本国ドイツで映画化した戦争叙事詩。ドレスデンの病院で働くドイツ人看護婦と、そこに身を潜めたイギリス人兵士とのラブストーリーを中心に、一夜にして運命が激変してしまった人々の姿を描き出す。監督は、ベルリンの壁を題材に描いたサスペンス・ドラマ「トンネル」で注目を浴びたローランド・ズゾ・リヒター。", "2007-04-21", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IRMA　VEPP", "イルマ・ヴェップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "サイレント期の連続活劇「吸血ギャング団」がリメイクされることになり、香港のアクション女優マギーが主役に選ばれる。ところがマギーがパリに到着してみると、監督のルネがスランプに陥り撮影はまったくはかどらない。途方に暮れたマギーは、「吸血ギャング団」の主人公、怪盗イルマ・ヴェップの衣装に身を包んでホテルを抜け出すのだった。アジアのトップ女優M・チャンが実名で登場し、キャットウーマンを思わせるピチピチのコスチュームでパリの街を駆けめぐるというきわめてユニークな作品。早撮りと即興演出によるルポルタージュ調の映像が効果を発揮し、異国を訪れたヒロインの戸惑いがライブ感豊かに活写されている。監督はホウ・シャオシエンに関するドキュメンタリーを手掛けるなど、アジア映画に深い関心を寄せる俊英O・アサヤス。また、J・P・レオーが悩める映画監督ルネをハマリ役で演じており、ラストで明かされるキッチュな劇中映像もお楽しみ。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仁義なき戦い・頂上作戦", "仁義なき戦い・頂上作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "昭和38年、広島は西日本広域暴力団・明石組と神和会の代理戦争の場と化していた。相次ぐ抗争事件への市民の批判から、警察は頂上作戦を敷く。明石組系の打本組と広能組、神和会系の山守組の双方は激しく対立。広能の組員が槙原の組員に射殺されたことから抗争は一段と激化する。一方、山守組系の早川組の組員が女を巡り、打本組の組員を射殺したため、明石組が大挙して広島へなだれ込む。しかし、老獪な山守の画策によって広能は別件逮捕され、形勢は逆転する……。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルイズ その旅立ち", "ルイズ その旅立ち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "市民運動に尽力した故・伊藤ルイ（ルイズ）の生涯をたどるドキュメンタリー。憲兵に虐殺された社会主義運動家・大杉栄と伊藤野枝を両親に持ち、人権擁護のために草の根的な運動を行なったルイズ。逆境をはねのけて、自らの信念に従い続けた彼女の力強い生き方を浮き彫りにする。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOLDIER OF FORTUNE", "一獲千金を夢みる男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "中国で行方不明になったカメラマンの夫を探しに、妻のジェーンは香港にやって来た。やがて彼女は、中国人相手の武器密売でボロもうけをしているアメリカ人のハンクを頼り、ハンクもまた彼女に惹かれて夫の捜査に乗り出す。香港を舞台に、中国の収容所からの奪還作戦を描くサスペンス・アクション。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("硝子のジョニー・　野獣のように見えて", "硝子のジョニー・　野獣のように見えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "蔵原惟繕監督と山田信夫脚本のコンビによる日活アクションの類型をはずれた異色作。北海道の北端・稚内の昆布採りの娘・みふねは、貧しさのため人買いの秋本に売られるが、途中で逃げ出し見知らぬ男・ジョーに救われる。秋本・みふね・ジョーの３人は運命の糸に結ばれ、やがてみふねの故郷・稚内で巡り会うことになるが……。この作品は公開当時、白痴のヒロインをめぐる二人の男という人物設定の類似からフェデリコ・フェリーニの「道」（1954）の模倣とみなされていたが、心ある映画ファンならこの映画の真意をくみ取ることができるはず。主演の３人はいずれも好演だが、特に白痴のヒロインに扮する芦川いづみが素晴らしい存在感を示した。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドットハック　セカイの向こうに", "ドットハック　セカイの向こうに", "", "", "http://image.eiga.k-img.com/images/movie/57095/315x210.jpg?3830", 111, "ゲーム、OVA、TVアニメ、コミックなどさまざまなメディアで展開されている「.hack（ドットハック）」プロジェクトの初となる劇場用3Dアニメーション。世界中がネットワークで接続された西暦2024年。日本の地方都市に暮らす14歳の少女そらは、友人たちに誘われて、世間で流行しているネットワークゲーム「THE WORLD」に参加。ゲーム中に起こった事件を発端に、現実と仮想空間の2つの世界を巻き込んだ冒険に繰り出す。キャラクター原案を「新世紀エヴァンゲリオン」の貞本義行が担当。脚本は「機動警察パトレイバー」の伊藤和典。桜庭ななみ、松坂桃李、田中圭ら若手俳優が声の出演を務める。", "2012-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER BY PROXY", "殺しの代理人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "深酒のためホテルのロビーで酔いつぶれていたケイシーは、美しい女性から奇妙な仕事を依頼される。翌朝、目覚めたケイシーの目の前にはイーゼルにかけられた昨晩の女性の肖像画と、“資産家殺害される、相続人の女性失踪!!”の新聞記事があった。自分が事件に巻き込まれていることに気付いたケイシーは、真相を追求しようとするが……。スリリングな展開とラストのどんでん返し、ハマー・フィルム作品には珍しいロマンティックなサスペンス。", "1955-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zombieland", "ゾンビランド", "", "", "http://image.eiga.k-img.com/images/movie/54435/315x210.jpg?3236", 88, "新型ウィルスの感染拡大により、人類の大半がゾンビと化してしまった世界。引きこもりの青年コロンバスは、“生き残るための32のルール”を作り、それを実践して生き延びていた。そんな中、コロンバスは最強のゾンビハンター・タラハシーや、美少女詐欺師姉妹ウィチタとリトルロックに出会い、ゾンビのいない夢の遊園地を目指すが……。ウッディ・ハレルソン、ジェシー・アイゼンバーグ主演のホラーコメディ。共演にアビゲイル・ブレスリン、ビル・マーレイ。", "2010-07-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YYK論争・永遠の“誤解”", "YYK論争・永遠の“誤解”", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "タイトルの“YYK”とは、義経、頼朝、清盛の頭文字。この３名に義経の母・常磐御前が加わり、自分たちの因縁の関係を、ある時は激論を闘わせながら、またある時は和やかに談笑しながら振り返っていく。現在の視点から歴史を検証する一種のディスカッション・ドラマだが、このドラマを撮影しているスタッフ・キャストの悲哀に満ちた製作現場を同時に写し出すという多重構成をとっている。その結果、奇妙な日本人論よりも、日本映画界の貧しさへの批判が強く印象に残る、風変わりな作品となった。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANSIKTE MOT ANSIKTE", "鏡の中の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ベルイマン監督が現代人の孤独と絶望を、魅力あふれる映像で描いた作品。ウルマン扮する主人公の精神分析医を通して、彼女の幼児体験や罪の意識、老いに対する恐怖が様々な人間関係の中から表われてくる。", "1976-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents すき　「さめざめ」", "桃まつり presents すき　「さめざめ」", "", "", "http://image.eiga.k-img.com/images/movie/57885/315x210.jpg?1815", 20, "若手女性監督による映画製作・上映集団「桃まつり」が、「好き」をテーマに女性監督たちが撮り上げた短編作品を上映する「桃まつり presents すき」の1本。4年間一緒に暮らしてきた夏子と卓だったが、アパートの更新時期が迫ると夏子は「更新はしない」と言い、ひとりで新居を探し始める。理由がわからない卓は困惑し、日が経つにつれて夏子の機嫌は悪くなっていくが……。監督は、「ゼロ年代全景」（2009）の1本として上映された「茜さす部屋」の星崎久美子。「参のすき」として「LAST SHOW」（佐藤麻衣子監督）、「SAI-KAI」（名倉愛監督）と同時上映。", "2012-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COYOTE RUN", "バニシング・ヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ベトナム戦争の後遺症で、重度のアルコール依存症になっているパーシングは、臨時雇いの警官としてオクラホマ州カーニバルにやって来た。しかし、着任早々麻薬取引に絡んだ銃撃戦が勃発。勢力拡大を狙う闇組織が、暗黒街のボスを襲ったのだ。パーシングは、この闇組織のリーダーを見て愕然とする。その男こそ、かつて戦場で彼を罠にはめ、殺そうとした元戦友のサンティアだったのだ。パーシングはサンティアを逮捕すべく、カンザス・シティーへ出発する。二人の男の宿命を描くアクション映画。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mongolian Ping Pong", "モンゴリアン・ピンポン", "", "", "http://image.eiga.k-img.com/images/movie/33955/315x210.jpg?1308", 105, "中国国内にある行政区“内モンゴル”の大自然の中で暮らす子供たちが、生まれて初めて見たピンポン球を巡って繰り広げるハートウォーミングな冒険物語。広大な草原地帯に住む少年ビリグは、川で小さな白い球を発見する。それが“卓球”というスポーツに使われる大事な球だということを聞いた彼と2人の友人は、球を返そうと思い立ち北京を目指す。監督は「香火」で2003年の東京フィルメックス最優秀作品賞に輝いた中国の新鋭ニン・ハオ。", "2007-04-28", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELLER IN PINK TIGHTS", "西部に賭ける女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "名優A・クインの渋い演技と、イタリアの大女優S・ローレンのセクシーな魅力が調和したユニークな西部劇。メインのウエスタンのドラマの中に、芝居一座の劇を挿入する作りなどが新鮮。1880年代の西部のある町に、借金だらけの芝居一座がやって来て興行を打ち大当たりに。だが、借金取りが追いかけてきたため別の町へと逃げ出したが、そこでも波乱が待っていた。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナース夏子の熱い夏", "ナース夏子の熱い夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "障害を抱える観客が官能映画を映画館で楽しめるよう、女性による副音声や日本語字幕を付けたバリアフリー仕様の映画を製作・上映する「エロバリ」プロジェクトの第1作。妻がケガで入院した病院の看護師・夏子に一目ぼれしてしまった一樹は、妻が入院中にデートを繰り返し、ある日ついに一線を越えてしまう。しかし、次第に夏子の影に怪しい男が見え隠れし……。", "2010-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Gangster", "アメリカン・ギャングスター", "", "", "http://image.eiga.k-img.com/images/movie/53055/315x210.jpg?1827", 157, "「24」「ダ・ヴィンチ・コード」のプロデューサー、ブライアン・グレイザーと「ブレードランナー」「グラディエーター」の巨匠リドリー・スコット監督の2人が手を組んだ実録犯罪ドラマ。60年代末から70年代初頭にかけてのニューヨーク・ハーレムにアフロ・アメリカン、フランク・ルーカス（デンゼル・ワシントン）が作り上げた麻薬王国の興亡と彼を追う刑事リッチー・ロバーツ（ラッセル・クロウ）の執念の捜査が描かれる。共演はキウェテル・イジョフォー、ジョシュ・ブローリンら。", "2008-02-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("C’EST LA VIE", "セ・ラ・ヴィ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "いとこの家族とともに、海辺の町でバカンスを楽しんでいた13歳の娘フレデリック。ところが、そこで彼女は両親の離婚を知る……。女性監督D・キュリスが繊細なタッチで綴った青春ドラマ。大人への階段を上り始めた少女の、ひと夏の悲しい出来事が、甘酸っぱい初恋模様を交えて描かれる。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PERILS OF PAULINE", "ポーリンの危難", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1910年代に全世界を沸かせたシリアル（連続活劇）映画を代表する、全20話の無声短編連作。当時は週替わりで２巻ずつ上映され、女主人公が絶体絶命のピンチを迎えると“幕”、翌週は再びその繰り返しという図式。たわいのないものだが、特に本作は“連続活劇の女王”と呼ばれたP・ホワイトを誕生させた記念すべきシリーズ。ポーリン嬢を亡き者にすれば莫大な遺産を手に入れることができる悪漢オーウェンの奸計によって、まさにハラハラドキドキの世界が展開する。なお、以前発売されたビデオには澤登翠による活弁トーキー版が収録されていた。“遺産”シリーズの別題でも日本公開されている。", "1914-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小川の辺", "小川の辺", "", "", "http://image.eiga.k-img.com/images/movie/55715/315x210.jpg?1320", 103, "「真夏のオリオン」「つむじ風食堂の夜」の篠原哲雄監督が、藤沢周平の短編小説を映画化した時代劇。同じく藤沢原作の「山桜」で篠原監督とタッグを組んだ東山紀之が主演する。藩命で妹の田鶴が嫁いだ佐久間森衛を討たねばならなくなった戌井朔之助が、肉親の情愛と藩命の間で苦悩する姿を描く。ヒロインの田鶴役で菊地凛子、朔之助の道中に付き従う若党の新蔵役で勝地涼らが共演する。", "2011-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PALMY DAYS", "突貫勘太", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "いやいやながらニセ占い師一味の助手となったカンター。この一味はトンマなお客の製パン業者からごっそり財産を巻き上げる計画を立てていたが、ひょんなことから有能な経営コンサルタントと間違えられたカンターが、巨大な製パン所をまかされることになって大騒動。歌あり美女ありの爆笑もの。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕達急行 A列車で行こう", "僕達急行 A列車で行こう", "", "", "http://image.eiga.k-img.com/images/movie/55815/315x210.jpg?2262", 117, "「間宮兄弟」「武士の家計簿」の森田芳光監督が、鉄道オタク＝“鉄ちゃん”の青年2人を主人公に描くオリジナルのハートウォーミングコメディ。大手企業に勤めるマイペース男子の小町と、経営危機を迎えつつある実家の鉄工所勤務の小玉は、性格も仕事も異なるが、共通の趣味である鉄道を通じて出会い、“鉄っちゃん”ぶりで周囲をあきれさせながらも、幸せの輪を広げていく。小町役の松山ケンイチと小玉役の瑛太が初共演を果たす。劇場公開前の2011年12月20日に他界した森田監督の遺作となった。", "2012-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひぃ・ふぅ・みぃ", "ひぃ・ふぅ・みぃ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "肖像画の中の三姉妹ひぃ、ふぅ、みぃは絵画の世界を飛び出して大はしゃぎ。ところがもとの絵の世界に戻るためのおまじないを忘れてしまったために、町は大騒動。にっかつ“ほほえみマドンナ”３人組の第１回主演作。ちびっ子ギャング、中村ゆうじ、ポップコーンら、当時のお笑いのニュー・ウェーブが脇を固める。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天国から来た男たち", "天国から来た男たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "13年ぶりの映画主演となる吉川晃司が、会社と妻に裏切られ、フィリピンの刑務所生活を強いられる元エリート・サラリーマンを演じた奇想天外で痛快無比なヒューマン・ドラマ。一流商社マンの早坂幸平は、出張先のフィリピンで麻薬不法所持のえん罪で逮捕される。問答無用で現地の刑務所に収容された彼が、そこで見たものは金さえあば自由が買えるという現状だった。幸平は、刑務所長と共謀して麻薬売買で金もうけをしている日本人など、一癖も二癖もある連中と過ごしているうちに、ここが心休まるパラダイスだったことに気付く……。実話をもとにした本作は、フィリピン・オールロケ。本物の刑務所を丸ごと借りて行われた撮影では、本物の看守や囚人たちも多数エキストラ出演している。山崎務、大塚寧々、及川光博、竹中直人らの豪華出演陣に加え、フィリピン映画界の大スター、モンスーン・デル・ロザリオも大物詐欺師として出演している点にも注目したい。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天河伝説殺人事件", "天河伝説殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "内田康夫の同名推理小説を映画化したミステリー大作。事件を解決していくルポライター浅見光彦役に榎木孝明が扮し、さらに市川監督一連の“金田一”シリーズの警部役でおなじみの加藤武が、そのままのキャラクターで出演している。現地へ浅見が行って事件に遭遇する旅情ミステリー。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛しき日々よ", "愛しき日々よ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "瀬戸内海の海辺の田舎町を舞台に、火葬場で働く一人の青年の姿を通して、死の凝視、母子相姦、老人問題など商業映画がタブー視しがちなテーマを選んだ意欲作。原作は重兼芳子の第81回芥川賞受賞作。歌手・もんたよしのりが本名で映画初出演。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("年ごろ", "年ごろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "大学受験を控えた少女の、揺れ動く心を描いた青春映画。年上でプレイボーイの男に魅かれる“年ごろ”の少女を内藤洋子が可憐に演じる。また、当時人気絶頂のブルー・コメッツが特別出演。全編を通じて甘いラブソングの数々を聞かせてくれる。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の姐・玲子", "極道の姐・玲子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ヤクザ者に惚れた女性の愛と戦いを描く任侠ドラマ。富岡組の組長が敵対する北野組の狙撃を受けて植物人間同様の体に。彼の妻、玲子はそれでも献身的な愛情を捧げながら組を切り盛りするが、北野組の悪行はそれに止まらず……。アクションやラブ・シーンにも挑んだ川島なお美の体当たりの演技が見もの。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サラリーマン専科 単身赴任", "サラリーマン専科 単身赴任", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "三宅裕司主演で贈る「サラリーマン専科」の第２作。日向事務機器の総務部庶務課課長の石橋万作は、大阪支社に単身赴任することになる。慣れぬ大阪で疲れ切って部屋に帰った万作のところへ、１匹の猫が入り込んで来る。猫の飼い主は、隣の部屋の美佐子で、一目で万作は心を動かされる。そしてある夜、取り乱した美佐子が万作の部屋にやって来て、“一緒に暮らそう”と言い、万作は動揺するのだった……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふれあうまち　向島・オッテンゼン物語", "ふれあうまち　向島・オッテンゼン物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "墨田区向島の豊かな街づくりと、ドイツ・ハンブルク市の街・オッテンゼンとの国際交流を描いた16ミリドキュメンタリー作品。開発が進み、ますます無気質な大都会になりつつある東京で、人の温もりを失わない街づくりを目指す向島。さらにこの住民たちは世界にも目を広げ、ドイツの街との交流にも積極的に取り組んでいる。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hansel and Gretel: Witch Hunters", "ヘンゼル＆グレーテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "グリム童話「ヘンゼルとグレーテル」のその後を描く。", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Paul: Chasing Sound", "レス・ポールの伝説", "", "", "http://image.eiga.k-img.com/images/movie/53775/315x210.jpg?2883", 90, "今やエレキ・ギターの代名詞ともなった\"レスポール\"の生みの親として知られ、ギタリストとしても5度のグラミー賞に輝いた経験を持つレス・ポールの人生に迫ったドキュメンタリー。93歳になった現在もニューヨークのジャズクラブで週1回ライブを行っているレス・ポール本人へのインタビューのほか、BBキング、ポール・マッカートニー、キース・リチャーズら錚々たる顔ぶれのアーティストたちが彼の魅力を語る。", "2008-08-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘剣破り", "秘剣破り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "市川雷蔵の傑作「薄桜記」の再映画化。名匠・伊藤大輔が新たに脚本を書き、監督も森一生から池広一夫に代わっている。主演は大映初出演の松方弘樹だが、このあと「眠狂四郎」「若親分」と雷蔵のシリーズを継承していった。剣と愛に生きた男の壮絶な忠臣蔵裏面史。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMEO　MUST　DIE", "ロミオ・マスト・ダイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「マトリックス」のプロデューサー、J・シルヴァーが「マトリックス」についで手掛けたバイオレンス・アクション。香港のアクションスター、ジェット・リーにとっては「リーサル・ウェポン４」に続くハリウッド映画であり、初の主演作となった。オークランドで対立を続けてきた黒人系ギャングとアジア系ギャングの抗争が激化する中、アジア系ギャングのボスの息子ポーが殺される。香港の刑務所で服役中のポーの兄、ハンは弟の死を知らされ、事件の真相を解明するべく、刑務所を脱獄し、アメリカへやって来るのだが、そこで彼を待ち受けていたのは、黒人ギャングと謎の殺し屋軍団だった。「マトリックス」で話題になったVFXをさらに進化させた、衝撃をレントゲンで透視したように再現する“X−RAYバイオレンス”が圧巻。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さよなら列車", "さよなら列車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "東京に出て歌手になることを夢見て牧場で働く妹。内情の苦しい牧場を立ち直らせるため、裕福な木材店の息子と結婚させられようとしている姉。二人の姿を追いながら『アラ見てたのね』『アンコ椿は恋の花』『馬鹿っちょ出船』などのヒット曲で綴った歌謡映画。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FORTUNE", "おかしなレディ・キラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ダテ男のニッキーは財産目当てで資産家の娘を誘惑し、見事結婚にまでこぎつけるが、結婚式の日、花婿になっていたのはサエない男オスカーだった。実はニッキーは前の妻との離婚がまだ成立しておらず、とりあえずオスカーと結婚させておいて、そのあとで別れさせようという腹積もりだったのだ。奇妙な３人組は、こうしてニッキーの離婚が成立するまでおかしな共同生活を強いられるが……。ベイティとニコルソンという意外な顔合わせが楽しい。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・大風呂敷", "喜劇・大風呂敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "馬六は恋する秋代が金持ちの島野と結婚すると聞いて大ショック。島野は自分の住む淡路島を君主国家にしようと独立運動を起こしていた。馬六は、それならば金を貯めて淡路島を島ごと買ってしまおうとたくらむ。ベトナムの戦場からスタートする意外性と、スケールの大きなホラ話が楽しい。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("竜馬を斬った男", "竜馬を斬った男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "坂本竜馬の命を奪った幕末テロリスト、会津藩士・佐々木只三郎の半生を描いた作品。萩原健一念願の企画だけに、演技に入る熱も並のものではない。その熱演を名匠・山下耕作が受けとめて、骨の通った時代劇となった。藤谷美和子が地味ながら印象的。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秋桜残香〈コスモスザンカ〉", "秋桜残香〈コスモスザンカ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "終戦60年を記念して制作された短編ストーリー。秋に美しく開花する“秋桜”をモチーフに、最期を迎えようとしている一人の老人のある想いを綴る。茨城県真壁郡の風光明媚な自然を背景に、戦中の記憶と最後の想いを繋ぐ永遠の恋物語が感動を誘う。82年間の生涯最期の時を迎えようとしている加藤秋次郎。しかし秋次郎には、昔愛した女性との間に果たせないでいる約束があり……。", "2005-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Cell", "ザ・セル", "", "", "http://image.eiga.k-img.com/images/movie/1695/315x210.jpg?3756", 109, "心理学者キャサリンの研究所では、最先端技術により人の潜在意識に入り精神病を治療する実験を続けていた。一方、女性の異常連続殺人を捜査していたFBIは、犯人スターガーとあるビデオを発見する。ビデオには、被害者の女性がガラス張りの独房（セル）に閉じ込められ、セルを満たす水の中で溺れ死ぬ様が記録されていた。しかしセルの所在を知るただ一人、スターガーは分裂症発作で昏睡状態に陥っている。捜査陣は、スターガーの脳に入りセルの場所を捜して欲しいとキャサリンに依頼する。", "2001-03-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("庭にお願い", "庭にお願い", "", "", "http://image.eiga.k-img.com/images/movie/55955/315x210.jpg?2254", 78, "独自の世界観で多くの人々から熱い支持を受ける福岡県在住のミュージシャン、倉地久美夫を追ったドキュメンタリー。「パビリオン山椒魚」「パンドラの匣」の冨永昌敬監督が、ドキュメンタリーに初挑戦する。菊地成孔、外山明をメンバーに迎えた「倉地久美夫トリオ」による白熱のライブ映像をはじめ、貴重なアーカイブ映像や関係者らへのインタビューを交えながら、その魅力に迫っていく。", "2011-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASTAWAY", "漂流者 —2人だけの島—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "これまでの自分の生き方に疑問を持った男女が、夢とロマンを胸に無人島での生活を始めた。だが、大自然の厳しさを前にして、二人の意見に食い違いが生じていく。原作者のアーヴィンが自ら無人島で過ごした一年間の体験を、「地球に落ちて来た男」「マリリンとアインシュタイン」など、独特の映像美でファンを魅了する現代イギリス映画界の異才ローグが映像化した日本未公開作。まばゆい光の中、O・リードとA・ドノホーが繰り広げる男と女の新しい関係が見もの。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブルーシンフォニー　ジャック・マイヨールの愛した海", "ブルーシンフォニー　ジャック・マイヨールの愛した海", "", "", "http://image.eiga.k-img.com/images/movie/54775/315x210.jpg?2926", 90, "フリーダイビングの世界で活躍し、世界中を旅した海洋冒険家ジャック・マイヨールの素顔に迫るドキュメンタリー。地球上の自然を知り尽くしたジャックは、晩年になると日本の唐津をたびたび訪れた。ジャック唐津で暮らす人々の交流を通じて得られたエピソードをもとに、これまで明かされなかったジャックの生涯に迫る。", "2009-08-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("侠花列伝 襲名賭博", "侠花列伝 襲名賭博", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "昭和初期の上州湯治場・鹿沢を舞台に、芸者と博徒の叶わぬ恋を描く任侠純愛映画。追われの身のヤクザ・柿沢は芸者の志満に危ないところを助けられ、二人は恋に落ちる。しかし、志満は温泉を仕切る向田組の若棟梁の許婚であった。やがて若棟梁は柿沢の気骨に打たれ、身を引くのだが……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALAMITY JANE", "カラミティ・ジェーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "アメリカ西部史に名高い女傑、カラミティ・ジェーンの青春を恋と歌で綴ったミュージカル。19世紀中頃の西部の町。男勝りの射撃の腕を持ち、カラミティ（厄病神）とあだ名されるお転婆娘がいた。親友のワイルド・ビル・ヒコックは、女らしくない彼女を単なる友人としか思っていなかったが、カラミティは人知れずヒコックのことを愛しており……。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女体渦巻島", "女体渦巻島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "密輸組織が牛耳る孤島に、ある日謎の男が現れ、かつての恋人と再会を果たす。しかし、女は既に麻薬に侵されていた。自分の女を奪いクスリ浸けにした組織のボスに復讐を誓う男と、彼との突然の再会に心揺れ動かせる女……。石井輝男監督が新東宝時代に撮り上げた、エロティシズムとモダニズムあふれる無国籍アクション活劇。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白鷺", "白鷺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "料亭辰巳家の箱入り娘として評判のお篠が、画家との恋を経て、古道具屋、待合の女中、そして芸者へと流されていく悲劇を描いた、泉鏡花原作の映画化。脚色は当時の島津作品を連続して担当していた新人の山形雄策。のちに衣笠貞之助の監督・脚本、山本富士子主演によって再映画化された。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUCK: Four Letter Film", "FUCK　ファック", "", "", "http://image.eiga.k-img.com/images/movie/34315/315x210.jpg?1692", 90, "世界一有名な放送禁止用語“FUCK”を徹底研究し、アメリカにおける“言論の自由”に迫るドキュメンタリー映画。政治家、言語学者、ミュージシャンなど各分野の専門家たちから話を聞くほか、ハリウッド映画で初めて“FUCK”を使用したシーンや歴代大統領の“FUCK”発言といった映像資料も満載。2度のアカデミー賞ノミネート経験を持つカルトアニメの巨匠ビル・プリンプトンが手掛けたアニメーション部分にも注目。", "2007-11-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shrek the Third", "シュレック3", "", "", "http://image.eiga.k-img.com/images/movie/1815/315x210.jpg?3042", 93, "おとぎの国を舞台に、心優しき怪物シュレックの活躍をパロディ満載で描いた大ヒットCGアニメの第3弾。幸せな新婚生活を送っていたシュレックとフィオナ姫だったが、フィオナ姫の父であるハロルド国王が病に倒れ、後継者としてシュレックが指名されてしまう。国王になどなりたくないシュレックは、もうひとりの王位継承者アーサーを探す旅に出る。その頃、王座を狙うチャーミング王子は、おとぎ話の悪役たちと共に王国を乗っ取ろうとしていた……。", "2007-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霧のマンハッタン", "霧のマンハッタン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "音信不通の恩師の娘を探すためにニューヨークへやって来たTVディレクターが、彼女を探す過程で様々な変わった人々に出会う。ユニークなミュージカル公演で知られる東京キッドブラザースの自主製作映画。当時劇団に所属していた柴田が歌って踊る。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シングルガール", "シングルガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "家庭生活と結婚への願望は持ちながらも、意地もあってシングル族を続ける女の姿をスケッチした作品。原作者・落合恵子の製作会社への抗議、桃井かおりと村川監督の衝突など、トラブル続きでマスコミを賑わせた、いわくつきの映画でもある。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD IN THE COUNTRY", "嵐の季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "執行猶予となって伯父の家に預けられたグレンには、ベティというガールフレンドがいたが、彼女の父親はグレンを嫌い、家へ寄せ付けなかった。くさったグレンはふとしたはずみに伯父の家の娘ノリーンと関係を結んでしまい、伯父から娘との結婚を迫られると家を飛び出す。女精神科医アイリーンが彼の相談にのるが……。プレスリー主演の青春もの。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lords of Dogtown", "ロード・オブ・ドッグタウン", "", "", "http://image.eiga.k-img.com/images/movie/1505/315x210.jpg?3330", 107, "70年代、わずか3年でスケートボードの世界を一新させ、全米の若者たちが熱狂するカルチャーに変えた伝説のスケボー集団“Z-BOYS”。彼らの若き日々を実話を基に描く青春ドラマ。脚本は、このオリジナル・メンバーで本作にも登場するステイシー・ペラルタ本人が担当。「エレファント」のジョン・ロビンソン、「イノセント・ボーイズ」のエミール・ハーシュ、「Haven」のビクター・ラサックら注目の若手俳優が共演。", "2005-12-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY BEST FRIEND’S WEDDING", "ベスト・フレンズ・ウェディング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "昔の恋人の結婚に揺れる女心を、ユーモラスに描いたロマンティック・コメディ。キャリアウーマンのジュリアンは、大学時代の恋人マイケルから結婚の知らせを受ける。その時、マイケルをまだ愛していることに気付いた彼女は、花嫁の付き添い人という立場を利用して、彼の心を取り戻そうとする。ところが、マイケルの婚約者キミーは性格もルックスも抜群で、しかも富豪の令嬢。それでもジュリアンは、友人の力を借りて花婿の強奪に奔走し……。「ミュリエルの結婚」のホーガン監督が、ヒロインの心の成長をビビッドに描出。ディオンヌ・ワーウィックのヒット曲『小さな願い』を効果的に配した、テンポのよい演出が光る。J・ロバーツのキュートなコメディエンヌぶりも快調だ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MRS.DOUBTFIRE", "ミセス・ダウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "人並みはずれて子煩悩な父親ダニエルが、妻と離婚したため子供と離れて暮らすことになる。悩んだダニエルは女装して家政婦になりすまし、妻の家で働くことにするが……。千の顔を持つ名優と評されるR・ウィリアムズが女装演技に挑戦し、圧倒的な芸達者ぶりを見せつける笑いと涙のホーム・ドラマ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Watchmen", "ウォッチメン", "", "", "http://image.eiga.k-img.com/images/movie/53935/315x210.jpg?3086", 163, "ベトナム戦争、キューバ危機など数々の事件の陰には、人々を見守る“ウォッチメン”と呼ばれる監視者の存在があった。しかし、1985年のニューヨーク、米ソ間の緊迫状態が続く中、政府により活動が禁じられていたウォッチメンの1人、エドワード・ブレイクが暗殺され……。1986年に発表され、ヒューゴー賞を受賞するなど、高い評価を受けたアラン・ムーアの名作グラフィックノベルを、「300／スリーハンドレッド」のザック・スナイダー監督が映画化。", "2009-03-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cinderella Man", "シンデレラマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "「ビューティフル・マインド」のロン・ハワード監督とラッセル・クロウが再び組んで実在したボクサーを描く感動ドラマ。1930年代の大不況下のアメリカ。負傷のためプロボクシングの世界から引退して肉体労働者となっていたジムは、愛する妻と子供のため、勝ち目のない新進ボクサーとの試合を引き受ける。ジムを見守る妻役でレニー・ゼルウィガー、マネージャー役で「サイドウェイ」に主演したポール・ジアマッティが共演。", "2005-09-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Neighbors", "ネイバーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　郊外の住宅街に移り住むことにしたアールとその妻イーニド。彼らはのんびりした生活を満喫しようとするが、そのお隣にビックと妻ラモーナが引っ越してきたことから事態は一変。人の迷惑など全く省みないビックとアブナイお色気を振りまくラモーナに、アールは振り回されるハメに。彼は平和な生活を取り戻すことができるのか？　「ブルース・ブラザース」のベルーシとエイクロイドが組んだドタバタコメディ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PETIT MONDE DE DON CAMILLO", "陽気なドン・カミロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ガレスキのベストセラーの映画化。親友同士でありながら、ことごとく対立する共産党員の町長ペポネと反共派の司祭ドン・カミロ。二人の対立と友情をコミカルに描いた風刺喜劇の傑作。", "1951-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLUNY BROWN", "ルービッチュの小間使　クルーニー・ブラウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "第二次大戦前に洒落た都会派コメディの快作をいくつも撮り上げた巨匠E・ルビッチ。戦後初めて発表した本作が彼の遺作となった。最後の作品にふさわしく、洗練された円熟の境地を示している。さらに、共に名優のボワイエとジェニファーが見事なダンディー＆淑女を好演。英国貴族社会で自由に生きる娘が、運命の人に巡り会うまでをドラマチックに描き出す。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHILD’S PLAY ２", "チャイルド・プレイ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "殺人鬼の呪いを吹き込まれたセルロイド人形が人間を襲う……。大ヒットした前作のスタッフが再結集した続編。殺人人形のイメージを改善しようとやっきになる人形会社の社長は、黒コゲになった人形チャッキーを復元調査するがどこにも異常はない。だが人形にはいまだに殺人鬼の魂が宿ったままだったのだ……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dinner for Schmucks", "奇人たちの晩餐会 USA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "フランシス・ベベール監督の仏映画「奇人たちの晩餐会」（98）を、スティーブ・カレル＆ポール・ラッド主演でハリウッドリメイクしたコメディ。会社で出世コースを歩むティム（ラッド）はボスが毎月開催する風変わりな晩餐会に出席することになる。その晩餐会とは、出席者それぞれが奇人変人をひとりずつ連れてきて、その可笑しさを競い合うという悪趣味なものだった。ティムは誰を連れて行くか迷っていたところ、たまたま車で轢いてしまった中年男性バリー（カレル）を連れて行くことにするが……。監督は「ミート・ザ・ペアレンツ」シリーズのジェイ・ローチ。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELEKTRA", "エレクトラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ヒット作「デアデビル」のヒロイン、エレクトラを主人公としてスピンオフしたアクション。善の戦士として得意の武術を繰り出す彼女の、悪の刺客との死闘を描く。スタイリッシュな格闘描写に加え、黒魔術を駆使し、刺青の動物を自在に操るなどの敵キャラの奇想天外な面白さも見逃せない。とある組織の下で暗殺者として暗躍するエレクトラ。ある父娘を助けた彼女は、彼らを捜し求める悪の組織の刺客と戦いを繰り広げる。", "2005-06-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狼の時代", "狼の時代", "", "", "http://image.eiga.k-img.com/images/movie/55755/315x210.jpg?4126", 81, "青春と性をテーマにした「青春Hシリーズ」第4作。「不灯港」で国際的に高い評価を得た内藤隆嗣監督がメガホンをとる。高層マンションに住むデイトレイダーの麗子は、経済的に余裕はあるが心は満たされない日々をおくっていた。一方、肉体労働で生計を立てる二郎は愛情に飢えていた。全く異なる世界に暮らす2人は、本能的に激しく互いを求め合う。", "2010-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr. URDER", "Mr.マーダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "D・クーンツのベストセラー小説を映画化したサスペンス。妻子に囲まれ幸せな日々を送っていた人気作家マーティーが、殺人の濡れ衣を着せられる。身の潔白を証明しようとする彼だが、自分と瓜二つの殺人鬼が存在することを知りがく然とする。一体、この男は何者なのか？　米国政府の不審な動き、秘密兵器の開発などの巨大な陰謀が見え隠れする事件には、恐るべき真実が隠されていた。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("純愛", "純愛　JUN-AI", "", "", "http://image.eiga.k-img.com/images/movie/34135/315x210.jpg?1478", 115, "中国を拠点に活動している小林桂子が製作総指揮・脚本・主演の3役を務め、8年の歳月を費やして製作した人間ドラマ。戦後の中国本土に残った日本人夫婦の愛と哀しい運命を描く。1945年、太平洋戦争の終結を迎えた中国・東北地方。多くの開拓団民と一緒に中国本土に置き去りにされ、瀕死の状態だった小学校教諭の俊介と保健婦の愛は、ある中国人親子に助けられる。だが、彼らを助けた中国人親子の家族は日本人兵に殺されていた……。", "2007-08-18", "8,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA HISTORIA OFICIAL", "オフィシャル・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "軍事政権下のアルゼンチン。多くの一般人が反政府的という名目で弾圧され、孤児は闇で売買までされたという事実に基づいた作品。夫の連れてきた養女の出生に疑問を持った妻が、体制によって隠ぺいされた真実の歴史に迫る。名女優アレアンドロの熱演が光る問題作。", "1985-01-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEW HAL＆BONS", "NEW HAL＆BONS", "", "", "http://ecx.images-amazon.com/images/I/51wFzQYjGOL._SL210_.jpg", 44, "2001年発行のDVDマガジン「Grasshoppa！」に連載されて話題を集めた脱力系CGアニメ「HAL＆BONS」の劇場版。ヒマ玉星に住む犬のコンビ、ハルとボンスは、毎日ビールを飲みながら幸せに暮らしていた。そんな彼らに、しゃべる餅・モチ君がインタビューをしにやって来て、居候することになってしまい……。「茶の味」の石井克人が原作・監督・脚本を手がけるほか、ハルの声も担当している。", "2006-05-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIO IN THE LAND OF FAR AWAY", "ミオとミラミス　勇者の剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "『長くつ下のピッピ』を生んだスウェーデンのベストセラー作家、A・リンドグレーン原作の『ミオよ、わたしのミオ』の映画化作品。“はるかな国”で捕らわれの身となった人々を救うため、孤児の少年が不思議な世界へと旅立つファンタジー・アドベンチャー。", "1987-01-01", "14,36,12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海炭市叙景", "海炭市叙景", "", "", "http://image.eiga.k-img.com/images/movie/55785/315x210.jpg?4173", 152, "90年に自ら命を絶った不遇の作家・佐藤泰志の連作小説を、「鬼畜大宴会」の熊切和嘉監督が映画化。谷村美月、加瀬亮、小林薫ら演技派俳優陣が結集する。佐藤の故郷である函館をモデルにした“海炭市”が舞台。造船所からリストラされた貧しい兄妹、立ち退きを拒否する老婆、妻の裏切りに傷つくプラネタリウムで働く中年男、事業と家庭に問題を抱える若社長、息子に避けられ続ける路面電車の運転士など、地方都市の憂うつと再生を繊細なタッチで描きだす。", "2010-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女優", "女優", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "新劇草創期の女優・松井須磨子と文芸協会の主催者・島村抱月との恋愛事件を題材にした作品。愛し合いながらも社会に強く批判され、はかなく死を迎える須磨子を山田五十鈴が熱演。同時期、溝口健二が「女優須磨子の恋」を作ったが、世評は本編の方が高かった。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I　AM　A　DANCER", "ヌレエフ　I AM A DANCER", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ニジンスキーと並んで20世紀最高のバレエ・ダンサーと称されるルドルフ・ヌレエフのパフォーマンスを記録したドキュメンタリー。彼の最も脂の乗った時期といわれる1972年ごろの舞台から『椿姫』『眠れる森の美女』などの公演とトレーニング風景を収録している。“野獣”と表現された、そのパワフルなバレエに酔える作品だ。なお、1974年の日本初公開時のタイトルは「華麗なるバレエ」。", "1972-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンネの日記", "アンネの日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "『アンネの日記』の世界初のアニメ映画。いつ捕らえられるか分からない恐怖に怯える隠れ家での不自由な生活。そんななかでも明るさや希望を失わず、ひたむきに生きたアンネの姿を力強く描き、生きる喜びと青春の輝き、そして戦争の恐ろしさを訴える。ユダヤ系の作家ロジャー・パルバースが脚本に参加。音楽を「ピアノ・レッスン」などでも知られるマイケル・ナイマンが手掛けている。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents 真夜中の宴　「たんぽぽ」", "桃まつり presents 真夜中の宴　「たんぽぽ」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 18, "", "2008-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CEUX　QUI　M’AIMENT　PRENDRONT　LE　TRAIN", "愛する者よ、列車に乗れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「王妃マルゴ」のP・シェローによる群像劇。亡き画家の葬儀に参列するため、親類、縁者たちが同じ列車に乗り込む。甥は別居中の妻と再会して戸惑い、ゲイだった故人の恋人たちはそれぞれの心中を探り合う。葬儀後、故人の家に集まった彼らは、それぞれの苦悩やわだかまりを露呈させていく……。結婚や恋愛、家族の絆で悩みを抱える人々の複雑な人間模様を、じっくりと描き出す。悲しみの中から生きる喜びが浮かび上がり、じんわりと温かい気持ちが漂ってくる秀作だ。カリスマ的魅力で人望も厚かった画家と双子の弟のふた役を演じるJ・L・トランティニャンをはじめ、名優たちが集結。V・ペレーズが美しい女装を披露している。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTERLUDE", "間奏曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "古都ザルツブルクとミュンヘンを舞台にしたメロドラマの名作。主人公は、文化部の仕事を担当するためにドイツへやって来たアメリカ女性。ホールでふとめぐり会った若い指揮者と恋に落ちた彼女が、やがて悲しい運命にもてあそばれていくさまを、モーツァルト、リスト、ブラームスなどの名曲をバックに、切なく描き上げていく。ヒロインを演じるのはJ・アリスン。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Motel Hell", "地獄のモーテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "モーテルの宿泊客を捕まえては、キャベツと一緒に裏庭に埋めておき、食べごろ（!?）になったら人肉ソーセージにしてしまう……。恐るべきプッツン中年兄弟が主人公のアブないホラー作品。怪人K・コナーのカルト作。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いぬかみっ！ THE MOVIE 特命霊的捜査官・仮名史郎っ！", "いぬかみっ！ THE MOVIE 特命霊的捜査官・仮名史郎っ！", "", "", "http://image.eiga.k-img.com/images/movie/33945/315x210.jpg?1300", 0, "女の子とお金が大好きな落ちこぼれ犬神使いの川平啓太と、彼と契約を交わした破壊好きで嫉妬深い犬神のようこが繰り広げるドタバタ騒動を描いた人気ライトノベルをアニメ映画化。2007年、変態処罰法が施行されたことにより、啓太たちが暮らす吉日市は防護服を着た謎の部隊に包囲されてしまった。変態の疑いをかけられた者が次々と連行されていく中、啓太とようこも“A級変態”として追われるハメになり……。", "2007-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE E´POQUE FORMIDABLE…", "パリの天使たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ホームレスのユニークな人間模様をペーソスたっぷりに描いたハートフル・コメディ。突然会社をクビになったベテランセールスマンのベルティ。クビになったことを家族に隠すために嘘を重ねるが、とうとう妻にバレて大喧嘩。家を飛び出し、パリの路上で生活を始める。そこで彼は、“先生”と呼ばれる医者くずれのホームレスとその仲間たちと出会い、自由で束縛のないホームレスの生活に惹かれていくのだった……。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY COUSIN VINNY", "いとこのビニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "司法試験に６年目にして、やっとパスし、弁護士になったばかりのビニーに初仕事がきた。殺人容疑のかかったいとこからの依頼だ。ビニーはさっそくド派手な美女モナリサを同伴して、保守的なアラバマの法廷に向かった……。見どころはビニー役のJ・ペシのスラング連発のはみ出しぶり。社会批判を含んだ痛快コメディだ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHADOW OF A DOUBT", "新・疑惑の影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ヒッチコックの傑作「疑惑の影」のリメイク版。二枚目俳優M・ハーモンが犯人を演じるサイコ・サスペンス。田舎町に住む娘チャーリー・ニュートンは、自分と同じ名のチャーリー叔父さんが久々に遊びに来ると知り大喜び。頭が良くユーモアのある叔父に、幼い頃から恋焦がれていた。ところが、彼の正体は、実は連続殺人鬼であった。偶然にもその証拠を握ったチャーリーに、叔父の魔の手が……。TVフィーチャー作品。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wild Bunch", "ワイルドバンチ", "", "", "http://ecx.images-amazon.com/images/I/51Z8NDJJTSL._SL210_.jpg", 137, "バイオレンス映画の巨匠サム・ペキンパー監督が、20世紀初頭のメキシコを舞台に滅びゆくアウトローたちの姿を描いた傑作西部劇。1913年、アメリカ・テキサスでの銀行強盗に失敗したパイクら強盗団はメキシコに逃走する。旧友ソーントンら賞金稼ぎに追われる中、メキシコ政府のマパッチ将軍の依頼で米軍用列車から武器を強奪するパイクたちだったが、マパッチの裏切りに遭い、仲間を人質に取られてしまう……。スローモーションに細かいカットを挿入するペキンパーのアクション演出は後続に大きな影響を与えた。出演はウィリアム・ホールデン、アーネスト・ボーグナイン。", "1969-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐を呼ぶ男", "嵐を呼ぶ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "石原裕次郎の大ヒット作を渡哲也主演でリメイク。裕次郎とは違った明るくカラッとした個性が生き、舛田利雄の演出も快調。キャスティングでは弟役の藤竜也、相手役の芦川いづみが、ともにみずみずしい演技を披露している。主題歌の間奏に流れるセリフも前作と違っている。ビデオ・タイトルは「渡哲也の嵐を呼ぶ男」。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("襤褸の旗", "襤褸の旗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "病魔に倒れたが見事に克服、再起をかけて自主製作した巨匠・吉村公三郎監督の後期の代表作。明治期に足尾銅山鉱毒事件の闘いに命を賭けた田中正造の凄烈な生涯を描く。純粋でいちずな人間像が監督自身の不屈の決意を示して堂々たる迫力で見せる名編。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無認可保育園　歌舞伎町　ひよこ組！", "無認可保育園　歌舞伎町　ひよこ組！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "ひょんなことからラジオDJになってしまったヤクザの奮闘を、小沢仁志主演で描いた任侠（にんきょう）コメディ「FM89.3MHz」の続編。ヤクザDJとして歌舞伎町の人々から熱い支持を得ていた工藤準次。そんなある日、大川組の組長がこの世を去った。準次に残された遺言は、組長が生前に運営していた保育園・歌舞伎町ひよこ園の園長になること。仕方なく引き受ける準次だったが、子どもの扱いに慣れない彼は自由奔放な園児たちに戸惑うばかりで……。", "2007-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブドガン", "ラブドガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「プープーの物語」の渡辺謙作の６年ぶりとなる監督第２作。かつて師弟関係にあった殺し屋二人が対決を迎えるまでの旅模様を軸に、殺しを依頼する少女、殺し屋志望の青年の胸中を交えながら、特異な映像文体で綴る。ケレン味たっぷりでありながら、独特の湿度や情緒に満たされた一編。孤高の殺し屋を演じる永瀬正敏のストイックなたたずまいが印象的だ。殺し屋・葉山田は、幼い頃に殺された両親の復讐のためにさすらっていると、スクーターに乗った少女、観幸と出会う。スクーターを貸すように言うと拒絶され、葉山田が赤い銃をつきつけると、観幸は心臓を撃ってくれと言うが……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛇イチゴ", "蛇イチゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "是枝裕和作品などで助監督を務めてきた西川美和の初監督作。これがデビューとはとても思えない腰のすわった演出で、放蕩息子の帰還がある家族の亀裂をあからさまにする情景を凝視する。リストラされて借金がかさむ父・芳郎。ボケた父親の介護で円形脱毛症の母・章子。娘・倫子は“イイ子”な自分を守りながら、教師として学校に通っていた。だが祖父が突然、他界。葬儀のため、勘当されていた放蕩者の長男が帰り、一家の雲行きが怪しくなる。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ordinary People", "普通の人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "名優ロバート・レッドフォードの監督デビュー作にして、1980年度アカデミー賞4部門（作品・監督・助演男優・脚色賞）に輝いた傑作ヒューマン・ドラマ。ごく普通の中流家庭であるジャレット一家。お互いに尊重し合い、家族4人で幸せな毎日を送っていた彼らに、長男の事故死と次男の自殺未遂という悲劇が降りかかる。そしてこの出来事をきっかけに、信頼しあっていたはずの家族の歯車が少しずつ狂いはじめるのだった……。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hannibal", "ハンニバル", "", "", "http://image.eiga.k-img.com/images/movie/1075/315x210.jpg?3900", 131, "全米を震撼させたバッファロー・ビル事件から10年。レクター博士のヒントで犯人を逮捕したクラリスは、FBIのベテラン捜査官となっていた。しかし、麻薬密売人イベルダの逮捕の際、激しい銃撃戦の末に彼女を射殺したクラリスは、マスコミの非難を浴びFBI内部でも厳しい追求を受ける。一方、レクター博士はイタリアに渡り、“フェル”博士としてフィレンツェの名家の蔵書を司る職に就いていた。", "2001-04-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒帯 KURO-OBI", "黒帯 KURO-OBI", "", "", "http://image.eiga.k-img.com/images/movie/34245/315x210.jpg?4112", 95, "真の強さを求めて激動の時代を駆け抜けた男たちの熱い闘いを、2人の空手家を主演に迎えて描いた本格アクション。「らせん」の飯田譲治が脚本を手掛け、「8月のクリスマス」の長崎俊一がメガホンを取る。昭和初期、空手家・柴原英賢の下で修行に励んでいた義龍・大観・長英。しかし英賢は、継承の証である黒帯を誰に残すか明言しないまま亡くなってしまう。やがて、空手を軍力に利用しようと考える憲兵隊が3人に近づき……。", "2007-10-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("式部物語", "式部物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「千利休 本覺坊遺文」の熊井啓が、前作と同じスタッフで作り上げた愛の叙情詩。秋元松代の名作戯曲を題材に、愛を求めてさまよう男の姿を描いている。事故によって童心に返ってしまった男、豊市。彼を妻と母が献身的に支えていたが、豊市はある日出会った“和泉教会”の尼僧・智修尼に魅せられてしまう。和泉式部の68代目と名乗る智修尼によって、豊市は正気を取り戻していくが……。奥田瑛二が豊市に扮し、熱演。モントリオール映画祭で最優秀芸術貢献賞を受賞している。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志・勢揃い清水港", "次郎長三国志・勢揃い清水港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "この編で次郎長一家の面々が勢ぞろいする。次郎長は石松と三五郎の口利きで、行き倒れの力士・八尾ヶ岳の一行を助けるために相撲興行を打ち、同時に花会を催す。しかし、ふとしたことから黒駒の勝蔵との関係が険悪に……。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRACES　OF　A　DRAGON：JACKIE　CHAN　＆　HIS　LOST　FAMILY", "失われた龍の系譜／　トレース・オブ・ア・ドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ジャッキー・チェンの両親が、長年隠していた秘密を明かすドキュメンタリー。ジャッキー自身も知らなかったという兄姉がいた事実や両親の苦難の若き日が、父親自身の口から語られる。「宋家の三姉妹」のメイベル・チャンが監督を務め、時代に翻弄された人々の数奇な運命を浮き彫りにする。ジャッキーの両親は再婚同士で、それぞれに子供がいた。しかし日中戦争や中国内戦の動乱の中、彼らは香港への避難を余儀なくされる。", "2005-03-05", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUBILEE", "ジュビリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1587年のある冬の嵐の夜、偉大な魔術師であり、錬金術学者でもあるジョン・ディー博士の家に招かれたエリザベス女王。彼女は、その晩、未来のすべてを見る旅に出発。案内役のエンジェル・アリエルとたどり着いたところは、未来のロンドンだったというSF。", "1978-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARIE OCTOBRE", "自殺への契約書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "J・ロベールの小説を映画化。戦後15年ぶりに再会した、かつてのレジスタンスの仲間10人。一人の女闘士の言葉から、リーダーの銃殺事件の真相究明が始まるが……。大広間という限定された空間のなかで、人間の様々な人生や苦悩をシリアスに描き出した心理サスペンス・ドラマ。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("3 Women", "三人の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "　カリフォルニアにある老人患者専門のリハビリ・センターにやってきた娘ピンキーは、看護婦ミリーに付き添い、見習いとして働き始める。やがてふたりは不思議な絵を描いている女性ウィリーの夫が経営するアパートで生活し始める。そんな折、ミリーから邪魔者扱いされ、部屋を追い出されたピンキーが自殺を図るという事件が。彼女は一命を取りとめるが、その後、性格が一変して……。3人の女たちの姿をミステリアスな映像美で綴る。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON JUAN 1973 OU SI DON JUAN ETAIT UNE FEMME", "ドンファン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "バルドーの育ての親であるR・ヴァディム監督が、私生活さながら彼女を女ドン・ファンに仕立て上げ、R・オッセンやJ・バーキンを相手に官能の日々を繰り広げさせるセンセーショナルな作品。パリの高級マンションに住み、男たちをその毒牙にかけていくバルドーが魔性の魅力を放っている。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stealing beauty", "魅せられて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "　今作の製作を挟んで主演作が相ついで公開されたリブ・タイラーだが、なかでも一躍名を広めた作品である。少女から女性への成長を遂げる姿をみずみずしく演じている。19歳のルーシーは、自殺した母親の死をきっかけにイタリアのトスカーナにやってくる。それは、実父を探す旅でもあった。ベルナルド・ベルトルッチにとっては、15年ぶりにイタリアを舞台にした作品。また、今作で「恋に落ちたシェイクスピア」のジョセフ・ファインズがスクリーンデビューを飾っている。", "1996-01-01", "4,1,3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE VEILS OF BAGDAD", "バグダッドの黄金", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "16世紀半ば、バグダッドはオスマン帝国の統治者トルコ大帝スレマンの支配下にあったが、総督ハマンと家老カシムは、山地の部族を手なずけて秘密裏に謀反を狙っていた。ある日、カシムが山地で賊に襲われた時、たくましい男アンターが現れて彼の一行を救う。だがこれはカシムが仕組んだトリックだった。バグダッドへ行って首尾よくカシムの私兵となったアンターは、秘密を探ろうとするが……。G・シャーマン監督の娯楽作。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("豚と軍艦", "豚と軍艦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "社会の下層を生き抜く人間たちの欲望のエネルギーを賛嘆し、かつ笑い飛ばす、ふてぶてしい作風で異彩を放った今村昌平監督初期の秀作。米軍基地に隣接する横須賀を舞台に、チンピラの欣太がヤクザ組織にほんろうされ、機関銃を乱射するに至るさまを、重厚に辛らつに描き出す。美も醜も共々にかみ砕く今村演出の力業は充実のきわみを見せ、いわゆる“重喜劇”のスタイルを本作で確立した。ヤクザ組織の子分・人斬り鉄次役の丹波哲郎が、虚勢ばかり張る小心者を快演。胃病をガンと思いこんで鉄道自殺をはかるが果たせず、保険会社の大看板にしがみつくという抱腹絶倒の名シーンを残した。欣太の恋人役・吉村実子はこれがデビュー作。重要な役どころに捨て身で挑み、清新な演技を見せた。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和残侠伝・血染の唐獅子", "昭和残侠伝・血染の唐獅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "鳶政の組頭・花田秀次郎と阿久津組の代貸・風間重吉は親友だったが、鳶政と阿久津組は博覧会場の工事をめぐって敵対していた。鳶政の纏が阿久津の手に渡る事件が起き、両者の対立は深まる。執拗な妨害に秀次郎は殴り込みを決意し、破門された風間も助っ人に。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ANDERSON PLATOON", "アンダーソン・プラトーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "1966年、ベトナム戦争は泥沼の状態になっていた。この年の前半までにベトナムに派遣されたアメリカ軍は28万人。それに対して、南ベトナムのベトコンと北ベトナム軍は、メコン・デルタの水田地帯、北部の丘陵地帯、そしてジャングルと、南ベトナム全土にわたってゲリラ戦で対抗した。本作はベトナム戦争の現実をアメリカ軍の側から撮ったドキュメンタリー映画。黒人士官のアンダーソン中尉率いるプラトーン(小隊)に寄り集まった出身や人種の異なる兵士たちが死の危険にさらされながら闘う姿が生々しく記録されている。オリヴァー・ストーン監督の「プラトーン」の原型となった作品である。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一心太助 男の中の男一匹", "一心太助 男の中の男一匹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "一心太助が花嫁をめとる一編。当初は本作を完結編にしようとしたため彦左を死なせている。沢島得意の庶民の日常描写を太助の新婚生活で発揮。また十八番の快テンポの演出で、濡れ衣を着せられた商人の処刑寸前に駆けつける場面を活写したり、沢島流青春時代劇は依然好調だ。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("卓球温泉", "卓球温泉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "さびれた温泉街の救済に乗り出した主婦の奮闘を描くハートフル・コメディ。単調な日々に疑問を抱き家出した平凡な主婦、園子は、かつて新婚旅行で訪れた温泉宿へ。だが、その街は観光客の足が遠のき、活気を失っていた。そんななか、宿で卓球台を見つけた園子は街を復興すべく、卓球イベントを企画する……。卓球のラリーにコミュニケーションを例えた、人間味あふれるドラマが展開。普通の主婦を演じた松坂慶子の、はつらつとした姿が印象的だ。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シベリア超特急５", "シベリア超特急５", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "映画評論家、水野晴郎がメガホンを執る人気シリーズの最新作。大陸を疾走する列車内を舞台に、殺人ミステリーと宝の地図を巡る攻防が繰り広げられる。12分におよぶカメラの長回しや名作にオマージュを捧げたショットなど、凝った作りが見どころ。最後のドンデン返しも、お見逃しなく。第二次大戦を左右するといわれる宝の地図。その確保を命じられシベリア超特急に乗り込んだ山下将軍は、またも殺人事件に遭遇し……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DROWNING MONA", "MONA(モナ)／彼女が殺された理由〈わけ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "アメリカ東部の小さな町で、中年女性モナがハドソン河で死んでいるのが発見された。自動車事故だと思われたが、警察官ジェフが捜査を進めるうち、殺人の疑いが濃厚になってくる。不倫中の夫や、母親をレイプする息子、暴力に脅える使用人、レズの友人ら、町の多くの人々が、彼女に殺意を抱いていたことが判明し……。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("making of LOVE", "making of LOVE", "", "", "http://image.eiga.k-img.com/images/movie/55635/315x210.jpg?3851", 84, "青春と性をテーマにした「青春Hシリーズ」第1作。「オトシモノ」「華鬼」など数多くのホラー作品を手がける古澤健が監督と主演を務める。監督のフルサワは、翔太ら数人のスタッフとともに“愛”をテーマにした自主映画を製作することにする。フルサワは撮影中にすれ違った美女ゆかりを起用しようと接近するが、彼女はさまざまな男たちとのかかわりをビデオに記録するという変わった趣味を持っていた。", "2010-08-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母御殿", "母御殿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1950年代に量産された三益愛子の“母もの”の１本。原田甲斐を首格とする“伊達騒動”を題材に、藩の実権を奪い取ろうとする甲斐の手から、幼い藩主・鶴千代を守り通す女性・政岡の活躍を描いている。ラストに鶴千代の身代わりになって、政岡の息子が毒殺されるシーンが涙を誘う。ビデオ発売時タイトルは「伊達騒動　母御殿」。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUCH THE SOUND", "タッチ・ザ・サウンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "聴覚をほとんど失い、身体全体で音を感じる天才パーカッショニスト、エヴリン・グレニーの音楽に迫る記録映画。日常の中のさまざまな音を、音楽へと変貌させていく過程が圧巻。ギタリスト、フレッド・フリスらとの共演も素晴らしい。グレニーとフレッド・フリスは共演CDを録音するために、ドイツの廃工場を訪れ、置き去りにされた機械や手すりなどあらゆるものを楽器にして音を紡いでいく。", "2004-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極楽坊主", "極楽坊主", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "諸国を修行している好田丈胤は老師を訪ねるが、その老師は今は亡く、後継ぎ息子は博奕に明け暮れ、寺は荒れ放題。寺を再建しようと乗り出す丈胤だが、逆に寺の財産をヤクザに巻き上げられてしまい……。コメディ・タッチで描かれた異色のアクション映画。宍戸錠扮する好色坊主のキャラクターが強烈。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TERMINAL VOYAGE", "アウター・スペース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "宇宙船ボイジャー号は、人類存続の使命をおび、８名の乗務員を乗せて惑星トライオンに向かっていた。ところが事故が発生し、船長が死亡。技術担当の女性乗務員ジノビッツは、その死を何者かの手によるものではないかと疑い始める。さらに２名の乗組員の死で、残された乗務員たちは極度の緊張状態に陥る。果たして単なる事故なのか、誰かの陰謀なのか……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CANNIBAL HOLOCAUST", "食人族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ブラジルとペルーの国境、“グリーン・インフェルノ”と呼ばれるアマゾン上流地域を背景に、ドキュメンタリー・タッチで数々の残虐描写を見せてくれるヒット映画。", "1979-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戒厳令の夜", "戒厳令の夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "博多の古びたバーで、偶然に発見された１枚の“幻の絵”によって、闇に葬られたはずの政治的陰謀が一斉に明るみに出る。五木寛之の同名ベストセラー小説を、ジョー山中の歌唱に乗せて展開させた壮大なミステリー・ロマン。怪老人役で鶴田浩二が熱演。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pa-Pa-Pa ザ★ムービー　パーマン", "Pa-Pa-Pa ザ★ムービー　パーマン", "", "", "http://ecx.images-amazon.com/images/I/319DG991STL._SL210_.jpg", 30, "1960年代と80年代の2度にわたってアニメ化され、いずれも当時の子どもたちから大人気を博した藤子・F・不二雄原作の漫画「パーマン」を、劇場版としてよみがえらせた短編アニメ。偶然出会った謎の人物・バードマンから、地球の平和を守る“パーマン”に任命された平凡な小学生・みつお。同じくパーマンとなった3人の仲間たちとともに活躍しはじめたみつおの前に、“全日本悪者連盟”の理事長・ドン石川が立ちはだかる。", "2003-03-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名十八番", "悪名十八番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "服役していた朝吉は、兄・辰吉の奔走で仮出所。辰吉は土地浄化のため市会議員選挙に立候補するが、土地の悪徳ボス・中沢に大ケガをさせられる。仮出所の身ゆえ朝吉は我慢するが、中沢の嫌がらせは止まらず、ついに清次とともに殴り込む。勝新太郎と田宮二郎最後のコンビ作品。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE SKIES", "ブルー・スカイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "B・クロスビー、F・アステアが共演、A・バーリンの曲32曲を全編に散りばめたミュージカル。ラジオのアナウンサー、ジェッドは番組である実話の回想を語り始める。第一次大戦後、ダンサーのジェッドは踊り子メアリーに好意を抱いていたが、彼女は彼の友人であるナイトクラブ経営者ジョニーと恋に落ち、結婚する。ジョニーは今まで道楽のようにやっていたナイトクラブの経営も真剣にやると誓うが、性格は直らず、店を次々と潰しては都会を点々とする生活を続け、ついにメアリーは子供を連れて別居を決意する……。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウホッホ探険隊", "ウホッホ探険隊", "", "", "http://ecx.images-amazon.com/images/I/21CQMXQ3PSL._SL210_.jpg", 105, "前作「ひとひらの雪」で腰のすわった都会派ぶりを発揮、改めて注目された根岸吉太郎が、森田芳光の脚本を得て演出したホーム・ドラマの力作。インタビュアーの仕事をこなし、中学生と小学生の二人の息子と暮らしている登起子は、単身赴任中の夫から愛人ができたことを告げられる。やがて離婚を決意した彼女は子供たちのバックアップを得て、明るい母子家庭を築きあげていくが、ある日、夫がやって来て女と別れたことを告白するが……。根岸はこのかなり裕福な中流家庭を確かな現代感覚をもって描いており、これが成功の主因と言えよう。無機的な雰囲気、船に乗り電車で荒地を越えて行く夫の研究所の地理など、どことなく森田の「家族ゲーム」を思わせるが、この映画に息づくのはまぎれもない等身大の人間たち。解体しつつある家庭の新しい絆を、不安のうちに求めている現代人たちが見事に描かれている。室内を縦横に動きまわる時にトリッキーなカメラ・ワークが実に面白く、十朱幸代や子役など俳優陣も好演。", "1986-10-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("3.11 日常", "3.11 日常", "", "", "http://image.eiga.k-img.com/images/movie/57545/315x210.jpg?3437", 75, "2011年3月11日の東日本大震災・福島第一原子力発電所事故発生後の世界における生き方を問い、反原発活動を行ってきた京都大学原子炉実験所・小出裕章助教へのインタビューを中心に構成したドキュメンタリー。震災直後に被災地でボランティア活動を行った女優の水野美紀、ボランティア演奏を行った「ソウル・フラワー・ユニオン」の中川敬ら著名人への取材も収められている。", "2011-12-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遥かなる国の歌", "遥かなる国の歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "神戸でクインテットを結成、東京でジャズバンドとして売り出した山川らのもとに、一人の混血青年が訪ねてくる。彼は母親に会いたい一心で、何も知らずに香港から日本へ麻薬を届け、組織に追われる身だった。山川らは青年をバンドのメンバーに加え、歌を歌わせることで母を捜す手伝いをしようとするが、皮肉にも組織に見つかってしまって……。全編にジャズが響く歌謡アクション。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嫌われ松子の一生", "嫌われ松子の一生", "", "", "http://image.eiga.k-img.com/images/movie/1525/315x210.jpg?4461", 130, "「下妻物語」の中島哲也が山田宗樹の同名小説を映画化。昭和22年、福岡県に生まれた松子は明るい少女時代を経て中学教師となるが、ある事件が元で失業。その後は男性遍歴を重ねるたびに不幸になり、ついにはソープ嬢となって殺人を起こし、刑務所に服役してしまう。柄本明、木村カエラ、柴咲コウ、武田真治、土屋アンナ、宮藤官九郎、劇団ひとりなどの人気者たちが大挙出演。", "2006-05-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("よいお年を　福祉施設「元気な亀さん」の記録", "よいお年を　福祉施設「元気な亀さん」の記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "埼玉県下にある無認可の福祉施設“元気な亀さん”に暮らす老人たちを１年間にわたって撮影したドキュメンタリー。“元気な亀さん”の老人たちはほとんどが痴呆症だが、園になかなか溶け込めないお爺さんもいれば、子供好きなお婆さん、歌うのが好きなお婆さんなど、一人一人は個性的だ。老人福祉をただ老人だけのものではなく、観る者すべての問題であることを教えてくれる。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花田少年史　幽霊と秘密のトンネル", "花田少年史　幽霊と秘密のトンネル", "", "", "http://image.eiga.k-img.com/images/movie/33435/315x210.jpg?4471", 123, "一色まことによる大人気コミックを「ALWAYS 三丁目の夕日」の名子役・須賀健太主演で実写映画化。町で評判のわんぱく坊主・花田一路は、貧しいながらも幸せな毎日を送っていたが、ある日、トラックにはねられて意識不明に。なんとか一命をとりとめたものの、それ以来、一路には幽霊が見えるという能力が備わってしまう。幽霊たちに次々と悩みを相談され困惑する一路。そんな彼の元に、実の父親と名乗る男の幽霊が現われ……。", "2006-08-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ATTACK　THE　GAS　STATION！", "アタック・ザ・ガス・ステーション！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "韓国で大ヒットした痛快クライム・コメディ。目標も見当たらず退屈を持て余す若者４人が、何となくガソリンスタンドを襲撃。従業員を人質にたてこもるが、社長は頑なに金を払うことを拒否。何も知らずにやって来た人々を巻き込んでうっ憤晴らしをしていた彼らは、やがて警官隊との大乱闘に突入していく。たてこもり事件の緊張感にブラックな笑いを散りばめて、若者たちの暴走をポップに描き出す。", "1999-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISERY", "ミザリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "“ミザリー”というシリーズ小説で人気作家となったポール・シェルダンは、コロラド山中の山荘に引きこもり、自叙伝的小説を執筆する。彼は“ミザリー”シリーズにはもういや気がさしていたのだ。山荘からの帰り道酔って崖から転落してしまったポールは、奇跡的に命を取り留め、付近に住む元看護婦のアニーに救われる。アニーは“ミザリー”の熱狂的愛読者だった。しかし、小説の主人公が死を迎える最終巻を読み終えたアニーは、ポールに、実は主人公は死んでいなかったという続編の書き足しをさせるために、ついには恐るべき拷問を加え始める……。へたな怪物もまっ青の恐るべき演技で（？）K・ベイツがオスカーを獲得したサイコ・スリラー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hamburger Hill", "ハンバーガー・ヒル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "人体がバラバラになりミンチ肉のようにされてしまうほど、激烈な戦闘が行われたという南ベトナムの937高地、通称“ハンバーガー・ヒル”。1969年のその高地奪回作戦の10日間にわたる死闘を、全員20歳前後という若者だけの部隊を主人公にして迫真の映像で描く。戦争シーンのすさまじさにも圧倒されるが、ほとんど無名の若手俳優陣14名の、存在感あふれる演技が作品を盛り立て、良質の人間ドラマとなっている。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE JACKAL", "ジャッカル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "B・ウィリスとR・ギアの二大スターが激突するアクション大作。冷酷非情な殺し屋にウィリス、闘志を内に秘めた元テロリストにギアが扮し、世界を股にかけた男たちの戦いが展開する。ロシア情報局とアメリカ連邦捜査局によって弟を殺されたチェチェン・マフィアのボスが、復讐のため伝説の殺し屋を雇う。この正体不明の男ジャッカルが動き出したという情報をつかんだFBIは、獄中の元IRA幹部でスゴ腕の暗殺者デクランに、釈放と引き換えに捜査協力を要請する。ジャッカルの標的は誰なのか？　依然としてその目的が分からないまま、デクランは行動を開始。やがて、二人の暗殺者の壮絶な戦いが始まった……。ウィリスが次々と披露する変装や、携帯電話やノートパソコンを駆使したハイテク兵器の数々も見もの。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Deficit", "太陽のかけら", "", "", "http://image.eiga.k-img.com/images/movie/54055/315x210.jpg?3264", 80, "第5回スペイン・ラテンアメリカ映画祭上映作品。「バベル」「ブラインドネス」のガエル・ガルシア・ベルナルが初監督、主演するメキシコの格差社会の闇を描いたドラマ。政治家の息子クリストバルは、富裕層の子供たちを招き、メキシコシティ郊外の豪華別荘でパーティを開く。別荘の管理人一家や庭師をよそに、裕福な家庭で育った青年たちは酒とドラッグの饗宴を楽しむが、そこで予想外のハプニングが起こる。", "2007-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A COUNTESS FROM HONG KONG", "伯爵夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ホノルルへ向かう外交官のオグデンは、船中で密航者の娘ナターシャと出会う。彼女は不幸な身の上で、アメリカに行くしか望みがないという。彼女が船に乗っている秘密をできるだけ守ってあげたいオグデンは、心ならずも彼女と船中で共同生活をするようになって……。チャップリン監督81本目にして初のカラー作品で、彼の子供たちも顔を見せている。また、これが遺作となった。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GONIN２", "GONIN２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "５人の男たちの壮絶な戦いを描いて話題を集めたバイオレンス・アクションの第２作。今回は５人の女性を主人公に、バトルを繰り広げる。男に捨てられた蘭、人生に絶望した売春婦のさゆり、夫に浮気された志保、セクハラを受けた早紀は、新宿の宝石店で、中嶋組・ヤクザ３人の強盗現場に遭遇した。たまたま居合わせた彼女たちに店員のちひろが加わり、強盗から時価10億円の宝石を横取り逃亡。改装中のディスコに身を隠す。一方、中嶋組との借金問題で妻が暴行され自殺してしまった工場経営者の外山正道は復讐に燃えながら、妻が欲しがっていた猫目石の指輪を探していた。やがて５人の隠れるディスコに、暴力団の追っ手と外山が現れ……。公開時には、昼間はアニメを上映し、最終１回のみ上映するという方法を取り、話題を呼んだ。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今、僕は", "今、僕は", "", "", "http://image.eiga.k-img.com/images/movie/54375/315x210.jpg?3736", 87, "母親と2人で暮らす20歳の悟は、仕事にも学校にも行かないニート状態。部屋に引きこもり、ゲームや漫画、ジャンクフード以外を拒否して暮らしていたが、ある朝、母親の友人・藤澤が現れ、半ば強引に悟を仕事場へ連れて行く。藤澤は悟の社会復帰のため努力するが、予期せぬ悲劇が悟を狂気へと押しやっていく……。若手・竹馬靖具が主演・監督を務める現代の若者の脱落・再生を描いた意欲作。", "2009-02-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LITTLE RED MONKEY", "脱走者を狙え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "亡命中の高名な科学者が、ロンドンで姿を消した。謎に包まれたこの事件の手がかりは、現場に残された一匹の小さな赤い猿だった。やがて、調査を開始した当局の捜査線上に、恐るべき組織の存在が浮かび上がる。「人間の絆」など多くの名作を世に送り出したイギリスの中堅、K・ヒューズ監督のサスペンス・スリラー。", "1956-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALIVE", "生きてこそ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "アンデス山中に旅客機が墜落、33人が助かる。彼らは酷寒の雪の中で捜索隊を待つがやがて食料も尽き、疲労と飢餓から仲間はだんだん衰弱していく。死が目前に迫った時、彼らは死んだ仲間の肉を食べて生きのびようと決断する。ドキュメンタリー「アンデスの聖餐」でも知られる実話をもとに人間の生への執念を感動的に描くドラマ。「アラクノフォビア」で劇映画デビューを果たしたF・マーシャル監督第２作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE ME TENDER", "やさしく愛して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "プレスリーの映画デビュー作。南北戦争直後。南軍将校バンスが終戦で故郷に帰ってみると、恋人キャシーは弟と結婚していた……。恋と金のトラブルや兄弟愛を描いた西部劇。名曲『ラブ・ミー・テンダー』ほか４曲が挿入されている。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAVID COPPERFRIEND", "孤児ダビド物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "後に「孤独の旅路」としてリメイクされたディケンズの自伝的小説の映画化。出生前に父に先立たれたデビッドは無慈悲な義父の仕打ちに耐えかねて家出し、苛酷な体験を重ねる。やがて魅力的な女性ドラと恋に落ちて結婚するが、幸福も長くは続かず……。「若草物語」の名匠G・キューカーが演出を務め、ドラマティックな物語に仕上げている。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPRING BREAK", "スプリングブレイク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "乱交パーティー、マリファナ、ビキニギャル。フロリダのリゾート・タウンを舞台に繰り広げられる大乱痴気騒動。若者たちの奔放な愛と性を赤裸々に描いている。「13日の金曜日」のS・S・カニンガム監督が描いた青春コメディ。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・東京流れ者　海は真赤な恋の色", "続・東京流れ者　海は真赤な恋の色", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "渡哲也扮する不死鳥の哲が、高知に流れてきて、波止場の荷役の権利を狙う悪玉一味と対決する。タイトルから分かる通り、鈴木清順監督の異色作「東京流れ者」の続編。だが、監督が青春ドラマ畑の森永健次郎では多くを望めなかった。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("快盗ルビイ", "快盗ルビイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「麻雀放浪記」で見事な監督デビューを飾った和田誠の第２作。“快盗ルビイ”を名乗り、いっぱしの泥棒を気どる女の子と彼女に振り回されるドジでまぬけな純情サラリーマンが、何度かのヤマと失敗を繰り返したあげく、結ばれるまでをコミカルに綴っていく。短編ミステリーの名手、H・スレッサーの原作を和田誠自身が脚色し、ミュージカル・シーンも盛り込んだおシャレな犯罪喜劇に仕上げている。練り上げたコンテに基づいた、全盛期ハリウッド映画を思わせる職人的演出。ちなみに、劇中で歌われる歌も和田誠自身の作詞、作曲だ。主演の小泉今日子は、キュートなすまし屋で行動派のヒロインになりきり好演。その他、公開当時にはあえて秘密にされた脇役陣が、効果的に起用され、楽しませてくれる。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KENNY ＆ COMPANY", "ボーイズ・ボーイズ ケニーと仲間たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "遊び大好き少年ケニーの目下の悩みは愛犬の病気と鑑別所帰りのガキ大将と大好きなマーシーのこと。愛犬は安楽死させられ、マーシーにも愛想をつかされた彼は、親友二人の助けを借りた頭脳プレイでガキ大将を鑑別所へ送り返す。ローティーンの青春をノスタルジックに生き生きと描いた佳編。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("True Confessions", "告白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "兄トムは警部、弟デズは僧侶。1962年に再会した二人は1948年の出来事を回想する。アイリッシュの兄弟はポルノ女優惨殺事件をめぐって対立し、トムの捜査の過程でデズの教会内部の黒い内幕が暴かれていき……。トムは事件を解決するが二人とも苦い挫折感を味わう。デ・ニーロ、デュヴァル共演の渋いサスペンス・ドラマ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Francesco", "フランチェスコ　ノーカット完全版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 157, "中世イタリアの聖人フランチェスコの生涯を、ミッキー・ローク主演で描いた伝記ドラマ。12世紀末のイタリア。裕福な商家に生まれ育った青年フランチェスコは、戦争で捕虜となった際に目にした福音書や、十字軍の遠征で経験した病人との出会いによって人生観を一変させる。すべてを貧者に与えてスラムで暮らしはじめたフランチェスコの元に、徐々に賛同者が集まりはじめるが……。公開当時にカットされた40カ所以上のシーンを余すところなく復元したノーカット完全版。", "2008-02-02", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST MESSAGE 海猿", "THE LAST MESSAGE 海猿", "", "", "http://image.eiga.k-img.com/images/movie/54785/315x210.jpg?2850", 129, "海難現場の最前線を描き、大ヒットを記録した「海猿」シリーズの完結編で初の3D作品。シリーズを通じ、監督を羽住英一郎、主演を伊藤英明が務める。大型台風が接近するなか、巨額を投じた国家プロジェクトの天然ガスプラント「レガリア」で事故が発生する。潜水士の仙崎大輔は、設計主任の桜木らとともに施設へ向かうが、救出作業中に突然の爆発に襲われ、施設内に取り残されてしまう。大輔は知り合ったばかりの第七管区の服部とバディを組み、帰還の道を探る。", "2010-09-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APHRODITE", "聖女アフロディーテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "華麗な映像で繰り広げられるエロスの世界。カプリスキーの美しい裸体と官能の演技も見どころである。第一次大戦前夜のエーゲ海に浮かぶヨットで、古代ギリシャの衣装に身を包んだ男女がエロスを求めて忘却の彼方へと更けていくさまを妖艶に描く。", "1982-01-01", "26,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN LOVED", "UN LOVED", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "中編「宇宙貨物船レムナント６」で話題を集めた万田邦敏監督の、劇場用長編デビュー作。ストイックに生きる光子は、地位にも金にも恵まれた勝野と付き合うが、価値観の違いから二人の間に徐々に亀裂が走り始める。そんな折、光子は同じアパートに越してきた若者・下川に恋を寄せ始めるが……。自分のライフ・スタイルを変えようと妥協することが絶対にできず、かたくなにハードボイルドのような生き様を貫く女性の姿が誇らしくも悲しい秀作。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VERY IMPORTANT PERSON", "謎の要人悠々逃亡！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "粗野で無愛想なイギリスの科学研究所の所長がTV番組で、戦争中の“大脱走”体験を披露する。肥大漢の所長が、警戒厳しいナチの収容所を悠々と脱走したが……。人を喰ったストーリーと笑いで見せるサスペンス・コメディ。", "1961-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("13 Tzameti", "13 ザメッティ", "", "", "http://image.eiga.k-img.com/images/movie/33915/315x210.jpg?1625", 93, "暗い森の奥深くで13人の男たちが大金を賭けて繰り広げる集団ロシアン・ルーレット。次々と現われる“13”という数字に導かれ、悪夢のようなそのゲームに巻き込まれていく青年の恐怖をモノクロ映像で描き出し、ベネチア国際映画祭やサンダンス映画祭をはじめ世界各地の映画祭で絶賛された衝撃作。監督・脚本はグルジア出身の新鋭ゲラ・バブルアニ。同監督のメガホンによるハリウッド版リメイクも決定している。", "2007-04-07", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("なごり雪", "なごり雪", "", "", "http://ecx.images-amazon.com/images/I/212EA6P6DGL._SL210_.jpg", 111, "50歳を迎えようとしている祐作は、故郷・大分県臼杵(うすき)市に住む親友に水田の妻、雪子が危篤という知らせを受け、28年ぶりに故郷へ戻る。かつて、３人はいつも一緒だった。そして、雪子は祐作を愛していた……。大林宣彦監督が伊勢正三の同名曲をモチーフに、青春とその惜別の痛みを28年の時の流れの中で現代日本が見失ったものとも照らし合わせながら描き得た、意欲的な叙情詩。あえて当時の撮影機材を用い、セリフの発声に至るまで気を配りながら当時を再現し、現代との対比を際立たせている。伊勢の故郷でもある臼杵から全国に発信する臼杵映画としても注目を集めた。", "2002-09-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Mothman Prophecies", "プロフェシー", "", "", "http://image.eiga.k-img.com/images/movie/1605/315x210.jpg?3892", 118, "ワシントンポスト紙の記者クラインは、妻が「あれを見た？」という謎の言葉を残して事故死したことから、妻の見たものの正体を調査していき、驚きべき事実に直面する。原作はジョン・A・キールの「プロフェシー」（ソニー・マガジンズ刊）。監督はMTV出身、「インディアナ・ポリスの青春」「隣人は静かに笑う」のマーク・ペリントン。「真実の行方」でギアの元恋人を演じたローラ・リネイが調査に協力する巡査部長役で再共演。", "2002-10-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京公園", "東京公園", "", "", "http://image.eiga.k-img.com/images/movie/55965/315x210.jpg?1552", 119, "青山真治監督の「サッド　ヴァケイション」以来4年ぶりとなる新作。「東京バンドワゴン」でブレイクした小路幸也の小説が原作で、主演の三浦春馬はカメラマン志望の大学生・光司を演じる。公園で家族写真を撮り続ける光司のもとへ、「彼女を尾行して写真を撮ってほしい」という依頼が舞い込んだことで、ゆるやかな距離でつながっていた女性たちとのあいまいな関係が微妙に変化していく。人間関係の間で揺れ動く繊細な役どころで新境地を開拓した三浦とともに物語を彩るのは、榮倉奈々、小西真奈美、井川遥。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("House of the Rising Sun", "ライジング・サン　裏切りの代償", "", "", "http://image.eiga.k-img.com/images/movie/58015/315x210.jpg?4169", 90, "アメリカの人気プロレス団体WWEに所属し、3度のヘビー級王座に輝き2010年に引退したバティスタ（デビッド・バウティスタ）が初主演を務めたクライムサスペンス。元刑事で刑務所から出所して間もないレイは、違法カジノを運営するストリップクラブで用心棒をしていたが、ある日、クラブが強盗に襲われる。元同僚の協力もあり、犯人はレイがかつて逮捕した者たちだと判明するが、逆に共犯を疑われてしまう。身の潔白を証明しようとしたレイだが、強盗犯は何者かによって殺害されてしまい、警察からも追われる身となってしまう。", "2012-05-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PARTIX 保険金横領", "難波金融伝 ミナミの帝王　劇場版PARTIX 保険金横領", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "空き巣に入られた骨董屋の植木は保険金を請求。しかし悪徳保険調査員に逆にゆすられてしまう。アメリカに逃げた植木を銀次郎も追い……。天王寺大原作の人気漫画を映像化した金融ドラマの劇場版第10作。ロサンゼルスでシリーズ初の海外ロケも敢行された。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GENDARME ET LES EXTRA—TERRESTRES", "ルイ・ド・フュネス　サントロペ大混戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "フランス映画界の喜劇スター、ド・フュネスの“警官”シリーズ第５作のドタバタ喜劇。バカンス客でにぎわう夏のサントロペにUFOが出現、あわや宇宙人の襲撃かと、フュネス副署長を先頭に大捜査が始まり、サントロペの町は大混乱となる……。", "1979-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("АННА ПАВЛОВА", "アンナ・パブロワ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "９歳の時に母と観たチャイコフスキーの名作『眠れる森の美女』が、アンナの人生を変えた。世界中を公演し、バレエのためにすべてを捧げた女性の50年の生涯を描いた作品。彼女の生地ソ連と、彼女を育てた英国の合作というのも奇しき縁といったところか。", "1983-01-01", "12,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Drumline", "ドラムライン", "", "", "http://image.eiga.k-img.com/images/movie/1375/315x210.jpg?4650", 119, "スポーツ競技のハーフタイムに技を競い合う、マーチング・バンドを舞台に描く熱血青春ドラマ。ニューヨークのハーレムに住むデボンは、離婚して家を出た父の血を引くドラムの天才。奨学金で進学しマーチングバンドに入るが、上級生ショーンと対立してしまう。監督チャールズ・ストーン3世は、リビング・カラーやパブリック・エネミーのクリップ監督を経てハーレムが舞台の犯罪映画「Paid in Full」で映画デビュー、本作が第2作の新鋭。", "2004-04-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARCHANGEL", "アークエンジェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ロシア革命を舞台に、一人の外人兵士を中心に展開する愛と精神の錯乱を、モノクロサイレントの表現主義的技法で描くG・マディンのワンマン映画。革命の嵐が吹き荒れる第一次世界大戦下のロシア。カナダ人中尉ジョンは、亡き恋人に瓜二つのロシア人看護婦ベロンカと出会う。彼はベロンカに結婚を申し込むが、それは“愛している”から、というわけではなかった。彼女もまた狂っていたのだった……。", "1988-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こまねこのクリスマス　迷子になったプレゼント", "こまねこのクリスマス　迷子になったプレゼント", "", "", "http://image.eiga.k-img.com/images/movie/54925/315x210.jpg?1910", 20, "ネコが主人公のコマ撮りアニメ「こまねこ」シリーズの第2弾。ものづくりが大好きな子猫のこまちゃんは、山の上でおじいと一緒に暮らしながら、クリスマスの日に両親に会えることを楽しみにしていた。ある日、両親からクリスマスのプレゼントが届くが、そこには「クリスマスに帰れない」と書かれた手紙も同封されていた。落ち込むこまちゃんを励まそうと、発明好きな少年ラジボーがある提案をする。", "2009-11-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("絵のない夢", "絵のない夢", "", "", "http://image.eiga.k-img.com/images/movie/56615/315x210.jpg?4699", 83, "青春と性をテーマに監督たちが独自の個性で自由に製作する「青春H2（セカンド）」シリーズの3作目。目の不自由な一郎は、ビジュアル系バンドの追っかけをしている真琴と出会い系サイトで知り合う。真琴はバンドのボーカルに貢ぐため売春に手を出しており、一郎が持つ大金を盗もうとするがばれてしまう。奔放な真琴が気に入った一郎は、帰るところのない真琴と共同生活を始めるが……。監督は、東京藝術大学映像研究科出身の新鋭・長谷部大輔。", "2011-07-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("K2", "K2／ハロルドとテイラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "弁護士テイラーと物理学者ハロルド。職業だけでなく性格も正反対の彼らだったが、“登山”という共通の趣味を持ち、強い友情で結ばれていた。ある日彼らは、世界的に有名な登山者が率いる山岳隊を雪崩から救ったことがきっかけで、それまで目標として夢見てきた世界第2位の高峰、K2への登頂に参加することになる。実際にヒマラヤ等で撮影された美しい山岳風景と迫力の登山シーンは必見。日本人登山者役で、藤岡弘が出演している。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　RE´PE´TITION", "彼女たちの時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "港町の演劇クラブに所属する幼なじみのナタリーとルイーズがケンカ別れ。その10年後、ナタリーは舞台女優としてキャリアを積み、ルイーズは結婚して平穏な人生を送っていた。再会した二人は旧交を温めたのもつかの間、激しい葛藤を繰り広げていく。友情、嫉妬、憎悪などが入り交じる女性二人の複雑な心理を掘り下げた人間ドラマ。E・ベアールとカナダの実力派女優P・ブシェールの鬼気迫る演技合戦が見もの。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポールダンシングボーイ☆ず", "ポールダンシングボーイ☆ず", "", "", "http://image.eiga.k-img.com/images/movie/56155/315x210.jpg?4362", 85, "若手俳優集団「D-BOYS」の弟分「D2」が主演する青春映画。芸人修行中のアツシとシンタロウ、リストラされたサラリーマンのヤマグチ、関西出身ホームレスのカミツル、モテないことに悩むヨウイチロウ、東北なまりがとれないミツヤら冴えない男たちが、だまし取られたお金を取り戻すため、男だけのポールダンスショーを開くことを決意する。素人同然の男たちが、ショーを成功させるために悪戦苦闘しながらも成長していく姿を描く。監督は「デスノート」「ばかもの」の金子修介。", "2011-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("闇の子供たち", "闇の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/53595/315x210.jpg?2621", 138, "「血と骨」「タクシー狂躁曲」などで知られる梁石日の同名長編小説を「KT」「亡国のイージス」「魂萌え！」の阪本順治監督が映画化。タイの裏社会で行われている幼児売春、人身売買、臓器密売の実態と、その闇に迫る新聞記者（江口洋介）、NGO職員（宮崎あおい）、フリーカメラマン（妻夫木聡）の奮闘がサスペンスフルに描かれる。共演は佐藤浩市、豊原功補、塩見三省、鈴木砂羽ら。", "2008-08-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUNG HO", "ガン・ホー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "不況のため失業者が急増したアメリカのとある地方都市。起死回生の策として日本の自動車工場誘致を立案。アレやコレやの方法でなんとか日本の自動車会社も了解するのだが……。日米経済摩擦をテーマにしたR・ハワード監督によるコメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デジモンセイバーズ3D デジタルワールド危機イッパツ！", "デジモンセイバーズ3D デジタルワールド危機イッパツ！", "", "", "http://image.eiga.k-img.com/images/movie/54805/315x210.jpg?2814", 0, "デジモンのアニメーション・シリーズ第5弾。2006年7月よりサンリオピューロランドで2年間上映された作品。デジタルワールドへ探検に出かけた人気デジモンのアグモン、ガオモン、ララモンが、次々と起こるハプニングを乗り越えて、現実世界へ戻ろうと奮闘する姿を描く。短編3Dアニメーションを集めた「とびだす！3D東映アニメ祭り」で上映。", "2009-10-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ED GEIN", "エド・ゲイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1957年、全米を震撼させた猟奇殺人犯エド・ゲイン。「サイコ」や「羊たちの沈黙」など数多くの恐怖映画の原点になったともいわれる男が、どのようにして殺人者となったかを、事実に基づいて描き出す戦慄のノンフィクション・ホラー。敬虔過ぎるクリスチャンの母親に育てられた内向的な青年エドは、母の死を境に異常な行動に走るようになる。そんなある日、町で女性が失踪。不審に思った警官がエドの部屋に踏み込むと、そこには死体で作られた家具や装飾品がびっしりと飾られていた……。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女はバス停で服を着替えた", "女はバス停で服を着替えた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「NAGISA／なぎさ」の小沼勝監督が、北海道を舞台に描く愛のドラマ。艶やかな表情を見せるヒロインの戸田菜穂と名バイプレーヤー、遠藤憲一の抑えた演技が印象に残る。好きなダンスを辞め、東京から故郷の北海道に戻った充。再出発を図る彼は修業を積み、そば屋の開店を目指す。そんな彼の元に、死んだ弟の妻で元ダンス・パートナーの瑞枝がやってくる。突然の彼女の出現に充は心を乱されて……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dream Home", "ドリーム・ホーム", "", "", "http://image.eiga.k-img.com/images/movie/55945/315x210.jpg?1404", 96, "香港、ビクトリア・ハーバー沿いに立つ高級高層マンションで警備員が絞殺される。その後もマンションの住民に対して惨劇を繰り返す犯人は、金融機関に勤めるチェンだった。ごく普通のOLに過ぎないチェンが猟奇的な犯行を繰り返す裏には、香港人の給与と高騰する地価という不条理な社会状況が大きく関与していた。香港の鬼才パン・ホーチョン監督が現代社会への風刺を交えながら描くサスペンス。", "2011-05-28", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ZONE", "緊急接近ZONE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "B級アクションでおなじみのR・ダヴィの主演作。湾岸戦争の英雄パイロットだったラウディは、上司と対立して軍隊を辞め、今はマリファナの空の運び屋をしている。彼の優れた操縦技術に目をつけたCIA特殊部隊のディックは、部下の女性調査官を使ってラウディを殺人犯に仕立て上げ、取引を申し出る。その取引とは、東欧の核マフィアのボス、ローサーの暗殺と彼の秘密基地の爆破。東欧ラビアに潜入したラウディは、地元の少年マイクの助力を得てローサーの身辺捜査を開始するが、そのことを知ったローサーは次々にラウディに刺客を差し向ける……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIXEN！", "女豹ビクセン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "アメリカ・インディペンデント界のカルト的存在、R・メイヤーによるエロティック・ファンタジー。カナダのツアー・コンダクターの妻ビクセンは、義弟や警官やツアー客など、おかまいなしに誘惑する自由奔放な女。黒人や共産主義者までも巻き込んで、美しい大自然の中に一大セックス騒動が展開する……。DVDタイトルは「ヴィクセン」。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hellraiser: Revelations", "ヘルレイザー：レベレーション", "", "", "http://image.eiga.k-img.com/images/movie/57585/315x210.jpg?3666", 75, "クライブ・バーカーの小説「ヘルバウンド・ハート」を映画化した「ヘル・レイザー」（1987）のシリーズ第9作。究極の快楽を手に入れられるという「ルマルシャンの箱」が引き起こす、魔道士ピンヘッドの恐怖を描くサディスティックスリラー。メキシコにやってきた青年ニコとスティーブンは、バーで酒をあおり羽目を外していると、謎の男に箱を渡される。その箱には絶頂をもたらす何かが入っているというが、宿で箱を開いた途端、周囲が怪しい光に包まれ、この世のものとは思えない恐ろしい男たちが出現する。", "2012-03-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クワイエットルームにようこそ", "クワイエットルームにようこそ", "", "", "http://image.eiga.k-img.com/images/movie/34255/315x210.jpg?4076", 118, "「恋の門」（94）で長編映画監督デビューを飾った松尾スズキが、芥川賞候補にもなった自作小説を自ら映画化。アルコールと睡眠薬を大量摂取してしまい精神病院の閉鎖病棟に強制入院させられてしまったフリーライター・明日香の絶望と再生の14日間を描く。主人公・明日香に映画主演は「BEAT」（98／宮本亜門監督）以来となる内田有紀。共演に大竹しのぶ、蒼井優、妻夫木聡、宮藤官九郎ら。", "2007-10-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Handmaid's Tale", "侍女の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ニュージャーマン・シネマの旗手として活躍し、今やドイツを代表する監督となったV・シュレンドルフ監督が、初めてアメリカ資本で撮った作品。近未来の北アメリカ大陸。右翼信奉者の手によってアメリカは崩壊し、新しくギリアッド連邦が建設された。しかし、環境汚染や原発事故などにより不妊症の女性が急増。そこで政府は、単なる子供を生むだけの女性（侍女）を養成し、それによって国家の安定を保っていた。だが、こうした社会制度を嫌い、国外逃亡を試みる人々もいた。ケイトもそうした人々の一人だったが、国境で捕らえられ、侍女として再教育される……。カナダの女性作家、M・アトウッドの小説をほぼ忠実に映画化したものだが、フェミニズムと現代社会への警告を込めた作品となっている。主演は監督トニー・リチャードソンを父に、そして大女優ヴァネッサ・レッドグレーヴを母に持つN・リチャードソン。ビデオ発売時タイトルは「闇の聖母／侍女の物語」。", "1990-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monsters vs. Aliens", "モンスターVSエイリアン", "", "", "http://image.eiga.k-img.com/images/movie/54235/315x210.jpg?3526", 94, "「シュレック」「マダガスカル」などの人気シリーズを手がけるドリームワークス・アニメーションによる3Dアニメ。リース・ウィザースプーン、キーファー・サザーランドら豪華俳優が声優として参加している。待ちに待った結婚式の日、空から突然降ってきた隕石と接触してしまい、身体が15メートルに巨大化してしまったスーザンは、新種のモンスターとして他のモンスターたちと政府の秘密基地に監禁されるが、謎のエイリアン・ロボットが地球に飛来し……。", "2009-07-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIAN’S SONG", "ブライアンズ・ソング 友情のタッチ・ダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "プロ・フットボール・チーム“シカゴ・ベアーズ”に所属する二人の選手の友情を描いた感動の物語。黒人のゲールと白人選手のブライアンは、互いにライバル意識を燃やしながらも、家族ぐるみで親しい交際を続けていた。そんなある日、ゲールはブライアンが重度の肺ガンに冒されていることを知る……。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOKYO EYES", "TOKYO EYES", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1980年代に「夜の天使」「天使の接吻」という２本の青春映画で一部に熱狂的ファンを得たJ・P・リモザン監督が、10年ぶりに発表した長編第４作。東京ロケに参加したクルーのうちカメラマン以外すべて日本人という異色のプロジェクトだ。分厚いレンズのメガネをかけた青年による連続発砲事件が世間を騒がす。刑事の兄を持つ少女Hinanoは、電車の中で偶然出会った犯人Kに接近し、愛にも似た感情で結ばれていくが……。すべての事象がヴァーチャルな日常を生きるイマドキの青年、天真爛漫な元気少女という異質な二人のラブ・ストーリーを通して描かれるのは、リモザン監督が強く興味をひかれた現代都市TOKYOの特異な曖昧さ。言葉の通じない監督との共同作業の下、主演の武田真治、吉川ひなのがユニークなボディ・ランゲージでそれぞれの個性を発揮し、終盤にはビートたけしが情けないヤクザ役で登場する。公開当時、洋画として上映された。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Extraordinary Measures", "小さな命が呼ぶとき", "", "", "http://image.eiga.k-img.com/images/movie/55255/315x210.jpg?4275", 109, "ブレンダン・フレイザー＆ハリソン・フォード主演による実話ベースの医療ドラマ。3人の子供に恵まれ、幸せに暮らすジョン（フレイザー）とアイリーンのクロウリー夫妻だったが、生まれたばかりの赤ん坊と1歳の娘が、難病のポンペ病に冒されていることを知る。ジョンは子供たちの治療に専念するため会社を辞め、型破りな薬学者ストーンヒル博士（フォード）に協力を求める。監督は「ベガスの恋に勝つルール」のトム・ボーン。共演にケリー・ラッセル。", "2010-07-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("運命じゃない人", "運命じゃない人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "恋人に逃げられたサラリーマンと、婚約破棄で希望を失った女の子。そんな二人の出会いを描きつつ、その背後で密かに進行していた大金絡みの犯罪の一部始終を語り明かす。新人監督、内田けんじがパズル感覚の構成で、男女5人の一夜の人間模様を軽やかに活写。笑いとスリル満載の娯楽作だ。偶然出会った真紀という女の子を泊めてやることになったサラリーマン、宮田。しかし彼のマンションには意外な人物が出入りしていて……。第14回PFFスカラシップ作品。", "2005-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("微熱 MY LOVE", "微熱 MY LOVE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "街でスカウトされて突然人気アイドルタレントになってしまった女子大生と、彼女の周囲に妨害されながらも、その恋を貫こうと奮闘する大学生との青春模様を描いた村生ミオの人気コミックの映画化。プロ・ビーチバレーの選手として人気の川合俊一の実弟である川合ちかしが、主人公のライバル役で映画デビューしている。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLENGARRY GLEN ROSS", "摩天楼を夢みて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "熾烈な営業争いが繰り広げられている大手不動産会社のセールス部門。そこで勃発した盗難事件から、セールスマンたちの人生の影の部分が暴かれていく。J・レモン、A・パチーノら、円熟した名優たちの演技合戦も見ものの社会派人間ドラマ。J・フォーリーが緻密な演出を見せる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談鴛鴦帳", "怪談鴛鴦帳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "エロ・グロを売り物にした、新興キネマの女優陣総出演の怪談映画。無礼打ちにされた母の仇に侮辱され、そのうえ毒薬によってふた目と見られない顔となり殺害された女・時鳥の、怨霊となっての復讐を描く。“化け猫女優”として名を成した鈴木澄子が時鳥に扮し、鬼気迫る怪演を見せるほか、仇役を演じる梅村蓉子の悪役ぶりも出色。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　ROUGE ET LE　NOIR", "赤と黒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1820年代のフランス。大工の息子に生まれながら、才能に恵まれ野心に燃えた青年ジュリアンは、一歩ずつ夢を実現していくが……。立身出世を夢見て恋と野心に生きる若者を描いた文豪スタンダールの小説の映画化。赤は軍人、黒は僧侶という出世の道を指す。", "1954-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("必殺５ 黄金の血", "必殺５ 黄金の血", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1991年10月より再びTVシリーズも始まった人気時代劇の第５作。己れの出世のために罪のない町人を虫ケラのよう殺し、金相場のハネ上げで甘い汁を吸おうとする勘定奉行を相手に仕事人たちの正義の刃が光る。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fast Company", "クローネンバーグのファイヤーボール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "　チーム“ファスト・カンパニー”のレーサー、ラッキーマンことロニーはドラッグレース界の人気者。彼は悪徳スポンサーのフィルに反抗したため、レース界から追放されてしまうが……。カーマニアでもあるクローネンバーグ監督が手がけたレース映画。そのフィルモグラフィの中では、あまりにも異色。", "1979-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lotta 2 - Lotta flyttar hemifran", "ロッタちゃん　はじめてのおつかい", "", "", "http://image.eiga.k-img.com/images/movie/1765/315x210.jpg?1433", 86, "ロッタちゃんは5歳、ニイマン家の次女。ある朝、おかあさんが出してくれたセーターがチクチクするといってハサミで切り刻んでしまった彼女は、なんだか気まずくなってお隣のベルイおばさんの家へ家出してしまう。おばさんの家の2階を借りて自分の部屋を作り、一人暮らしを宣言。だけどそんな彼女も夜になるとやっぱり心細い。お化けが出そうで泣き出しそうになったとき、ようやくパパが迎えに来て…。", "2000-01-15", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CROW", "クロウ −飛翔伝説−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1980年代アメリカでカルト的な人気を博したコミック・ヒーローを、ブルース・リーの息子ブランドンが演じたSFアクション。退廃と欲望の渦巻く未来都市の片隅、ロック・ミュージシャンのエリック・ドレイバンは、悪の帝王トップ・ダラー一味によって恋人のシェリーとともに惨殺される。しかしエリックは、冥界とこの世を結ぶカラスの力を得てよみがえり、二人の愛を引き裂いた犯人たちへ復讐を開始する。ダラーはエリックの神秘的なパワーを手に入れようと、シェリーのよき友人だったサラを誘拐して罠を仕掛ける。エリックはサラを救うため、ダラー一味が待ちぶせる教会へと向かう。本作品の撮影中に、リーは発砲事故により死亡、残りのシーンは撮影済みのフィルムを利用したCGによるデジタル合成で処理されている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PROPHECY", "プロフェシー　ゴッド・アーミー　全米公開版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1994年に世界に先駆け日本で公開された「ゴッド・アーミー　悪の天使」のアメリカ公開作。冒頭シーンの撮り直しや、新たにSFXシーンを加えて、神と悪魔の争いを描く。神の存在を否定し、僧職から刑事になったトーマスはロサンゼルスで不可解な事件を担当する。殺された被害者は人間ではなく、人類滅亡を防ぐために天使シモンが送り込んだ使者だった。人類滅亡の鍵となる邪悪な魂をめぐり、天使と悪魔、人間が戦いを開始する……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("笹笛お紋", "笹笛お紋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "中仙道で乱暴されかかった娘、お小夜を救ったお紋は、彼女の父が鬼権一家に無理難題を吹っかけられ窮しているのを知る。お紋は父娘のために一肌脱ぐことにし、賭場を荒らしたりするが、鬼権一家の手に落ちてしまう。棚下照生の劇画をもとに、笹を武器に闘う女渡世人、お紋の活躍を描く。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITY BY THE SEA", "容疑者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "刑事の息子が殺人容疑で指命手配された。父として息子を捜すのか、刑事として容疑者を追うのか。揺れる心と親子愛をサスペンス・タッチで描いた骨太のドラマ。ピュリッツァー賞受賞作家で、報道ジャーナリストのマイケル・マッカラリーの取材記事をベースにしている。ブルックリン・ビーチに麻薬密売人の他殺体が上がる。ニューヨーク市警のヴィンセントは殺害場所が自分の故郷ロングアイランドであることを突き止め、14年ぶりに足を踏み入れる。実父が誘拐犯として死刑になり、離婚した妻子を置いてきた街だ。やがて息子ジョーイが有力容疑者として浮かび上がり、捜査から外されたヴィンセントは、かつての家族を長いこと放っておいた罪の意識から、警察よりも先にジョーイを捕まえようと奔走するのだが……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦争を知らない子供たち", "戦争を知らない子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "作詞・北山修、作曲・杉田二郎、歌・ジローズによる同名のヒット曲をもとに大和屋竺と藤田俊八が脚本を担当した作品である。いわゆるシラケ世代といわれた時代の高校生、一郎・博・麗子は停学処分反対デモを起こしたが学生たちに無視され挫折してしまう。３人はなんとはなしに当てのない旅に出るのだが……。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SH○GUN", "将軍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "もとは1975年出版のベストセラー小説が原作の12時間のTVミニ・シリーズで、これを再編集して日本では劇場公開された。高視聴率をマークし、“ショーグン現象”と呼ばれる日本ブームをアメリカに起こした作品。1598年、日本に漂着したイギリス人、ブラックソーンは、戦国大名、虎長のもとで日本の戦国時代の戦いの嵐に巻き込まれていく。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼婆", "鬼婆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "戦国時代に生きる娘と義母の物語。とはいうものの落武者を殺してはその武具・甲ちゅうを売りさばいて生きる女二人の話である。ところが娘の夫の友人が帰郷し娘と激しく燃える。それに嫉妬した母は般若の面をつけ二人を恐怖に陥れる。が、その般若の面はとれなくなる、という一種の和製ホラー。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本製少年", "日本製少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1990年代日本の精神的廃墟のなかでもがく青春像を描く、インディペンデント・フィルム。父親を殺そうとした過去を持つ田中大和。説明不能の衝動から事件を起こした大和は、浦安の安アパートで孤立していた。バイト探しを続ける彼は、池袋の駅前でティッシュを配る少女・薫と出会う。一瞬の内に互いの孤独と絶望感を感じ取る二人。薫の心臓にはペースメーカーが埋まっており、間もなく切れる電池の交換を拒否している。薫の事務所は売春とトルエンの売買を本業とし、社長の松浦は少女たちを奇妙な家族的雰囲気でまとめていた。破滅の道行きをたどる大和と薫。松浦は射殺され、犯人とされる大和を“18歳の少女を連れて逃走中の模様……”とマスコミが伝えていた。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAZE★爆熱時空・　天変脅威の大巨人〈ジャイアント〉", "MAZE★爆熱時空・　天変脅威の大巨人〈ジャイアント〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "アニメ・ファンの熱烈な支持を受ける、あかほりさとるの原作による人気アニメの劇場版。昼は正義の美女だが、夜になるとエッチな野獣男に変身してしまう主人公メイズ。伝説の武器“いにしえの巨人”を求めて、彼女（彼）と仲間たちの一行が宿敵シックやゴージャスとバトルを繰り広げる。お色気とギャグのブレンドが魅力。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オーディション", "オーディション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "村上龍の小説を映像化したサイコスリラー。ビデオ会社社長・青山は悪友に勧められ、女優オーディションの名目で再婚相手を探すことに。そこで清楚な麻美と出会い、交流を深める。だが謎の過去を持つ彼女は、やがて青山にサディスティックな愛情を向けはじめる……。愛した男の体に針を刺し、脚を切断するヒロインの歪んだ愛情表現が衝撃的。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いつも二人", "いつも二人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「トラック野郎」シリーズなどのベテラン俳優・愛川欽也が、製作・監督・脚本・主演の4役を務めたラブストーリー。浅草の場末の劇場を訪れた善三は、そこで出会ったストリッパーの恵子に心を奪われてしまう。劇場に通い詰めたあげく仕事をクビになった善三は、マネージャーとして恵子のそばにいることに。しかし、恵子はいつも危険な香りのする男とばかり付きあっていた。そんなある日、恵子は善三の考えで、所属する興行会社からの独立を決意する。", "2008-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Double Vie De Veronique", "ふたりのベロニカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "同じ日の同じ時刻に生まれ、同じ名前と顔を持つ2人の女性がいた。ポーランドとパリに住む2人のベロニカはそれぞれの人生を送りながらも、お互いの存在を感じていた。監督は、「トリコロール」三部作で知られるポーランドの巨匠クシシュトフ・キエシロフスキー。本作で2人のベロニカを演じた主演のイレーヌ・ジャコブは、91年カンヌ映画祭主演女優賞を受賞した。", "", "2,42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お父さんのバックドロップ", "お父さんのバックドロップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "故・中島らもが子供向けに書いた短編小説を15年の時を経て映像化。売れないプロレスラーの父を持ち、プロレスが大嫌いな少年は、父のことを恥ずかしく思っていた。だが、息子に認めてもらいたい一心の父親は、捨て身の挑戦を試みる。映画初主演の宇梶剛士が、男気あふれる父親を好演。天才子役、神木隆之介との絶妙な掛け合いで、笑いと涙を誘う。母親が他界し、中年プロレスラーである父、牛之助とともに、祖父の暮らすボロアパートに引っ越してきた一雄。プロレス が大嫌いな一雄は、レスラー仲間たちと引越し祝いに焼肉屋で盛り上がる父にうんざりしていたが……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAGDALENA VOM TEUFEL BESESSEN", "ヘルスネーク 淫蛇〈エロヘビ〉の交〈まぐ〉わり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "美しい17歳の少女マグダレーナが、不気味な発作に襲われた。以来彼女の様子は一変、汚い言葉を連発したあげく、皆の目の前でオナニーまで始める。彼女を診察した精神科医フォーク教授は、彼女の肉体にいる悪魔の黒蛇が原因と知り……。", "1974-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUNDAY’S CHILDREN", "日曜日のピュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "1920年代のスウェーデンの田園地帯を舞台に、８歳になったばかりの少年イングマル（愛称はピュ）のひと夏の思い出と、初老を迎えた現在の姿とをフラッシュバックさせながら描いたヒューマン・ドラマ。I・ベルイマンがその幼少期を綴った脚本を、息子のダニエルが初映画化。田園を背景に淡々と語られる物語は、北欧児童映画の伝統的手法にのっとって描かれている。", "1992-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Terry Fox Story", "テリー・フォックス物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ガンのために右脚を切断した青年テリーが、ガン研究の資金を集めるため、また病気の子供たちに勇気を与えるためにカナダをマラソンで横断しようとする……。実話に基づく感動作。主演のフライアー自身も身障者。", "1983-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Afropunk: The 'Rock n Roll Nigger' Experience", "アフロ・パンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "アメリカで拡大を続ける黒人パンク・シーンを題材にしたロック・ドキュメンタリー。自身もアフロ・パンカーのジェームズ・スプーナー監督が、白人ばかりのパンク・シーンの中で自らのアイデンティティを模索する黒人の若者たちの葛藤（かっとう）の日々を追う。初期「バッド・ブレインズ」のライブ映像をはじめ、「フィッシュボーン」「TVオン・ザ・レディオ」らアフロ・パンクを代表するバンドへのインタビューなど貴重な資料も満載。", "2007-08-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLIPPER", "フリッパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "懐かしのTVドラマ『わんぱくフリッパー』が生まれるきっかけとなった、家族向けの海洋アドベンチャー映画。パパ、ママなどはTV版と違っているが、主役のサンディ少年は同じL・ハルピンが演じている。フロリダのキース諸島。漁師の父リックスが留守の間に、息子のサンディは傷ついたイルカを見つけ、フリッパーと名付けて家のいけすに隠す。だが父親は帰るなり怒り出し、イルカを海に逃がしてしまった。人間の言葉を理解する賢いフリッパーとすっかり友達になっていたサンディは一人、海に出るのだが……。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("２／デュオ", "２／デュオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "数々のインディペンデント作品の助監督やTVドキュメンタリーの演出をこなしてきた諏訪敦彦の監督デビュー作。ブティックに勤める若い娘・優と俳優志望の青年・圭。ささいな日常の歯車が狂ったことから、二人のアパートでの同棲生活が破綻をきたすまでを描き出す。何度も練り直した脚本を結局破棄し、10ページほどの簡単なストーリーラインだけを用意して撮影を敢行。その結果、一見ぶっきらぼうな映像の中に即興ゆえの緊張感が刻み込まれ、観ていてヒリヒリするような肌触りの恋愛映画に仕上がった。監督いわく、男と女を暗示させる「２／デュオ」というタイトルには、虚構と現実、抽象と具象といった映画が持つ様々な二重性の意味が込められているという。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("That's カンニング！　史上最大の作戦？", "That's カンニング！　史上最大の作戦？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "安室奈美恵とTOKIOの山口達也共演で贈る学園コメディ。学生寮の存続をかけて、落ちこぼれ学生たちがカンニング攻防戦を繰り広げる。名門・日本理科大学の学生寮シグマハウスの住人は、木村見次以外はみな落ちこぼればかり。当然試験もカンニングでクリアしていたが、寮生の一人・亀井のカンニングがばれ、次回の試験で寮生たちが良い成績を取れなかったら寮は閉鎖するという決定が下される。だがこれは、寮を壊しその跡地にホテルを建設しようとする右田教授の陰謀だと分かり、寮生たちはコンピュータ・ハッキング、ビデオ、超小型カメラなどハイテクを駆使したカンニングで試験に挑む。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天然コケッコー", "天然コケッコー", "", "", "http://image.eiga.k-img.com/images/movie/34085/315x210.jpg?4007", 121, "くらもちふさこの名作コミックを、「リンダリンダリンダ」の山下敦弘監督が実写映画化。自然豊かな田舎町を舞台に、少女の淡い初恋と成長を爽やかに描き出す。中学2年生の右田そよが通っているのは、小中学生合わせても生徒がたった6人しかいない分校。ある日そこに、東京からの転校生・大沢広海がやって来る。クールでとっつきにくい雰囲気の大沢に、そよは次第に恋心を抱き始め……。脚本は「ジョゼと虎と魚たち」の渡辺あや。", "2007-07-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE FANG", "ホワイトファング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "19世紀のゴールドラッシュにわくアラスカに、父に会いにやって来た少年ジャックは、父の死を知り、一人で大自然の猛威の中に生きることになる。彼の前に現れたのは、やはり親を失った孤独な犬だった。J・ロンドンの名作『白い牙』を原作に生命の尊さをうたい上げた作品。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INDOCHINE", "インドシナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 159, "インドシナがフランス領だった時代。ゴム農園を所有するフランス人女性エリアーヌは、ベトナム人を養女とし、ベトナムの皇族に嫁がせ、孫らとの静かな余生を夢見ていた。しかし、エリアーヌの愛人であるフランス人将校に娘が恋してしまったことから、運命は皮肉な方向に向かっていく。一人の女性の波乱に満ちた人生を描く大河ドラマ。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眉山", "眉山", "", "", "http://image.eiga.k-img.com/images/movie/33965/315x210.jpg?3017", 120, "さだまさしのベストセラー小説を「ジョゼと虎と魚たち」の犬童一心監督が映画化した感動ドラマ。病に倒れた母を看病するために徳島へと帰郷した咲子は、母が末期ガンに侵されていることを聞き愕然とする。やがて医師の寺澤と出会った彼女は、彼の協力を得て、今まで知らなかった母の人生を知る。母の秘められた想いを知り成長していくヒロイン・咲子に松嶋菜々子、咲子の母・龍子に10年ぶりの映画出演となる宮本信子が扮する。", "2007-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHE’S THE ONE", "彼女は最高", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "R・レッドフォードが新しく設立した映画製作会社サウス・フォーク・ピクチャーズの第１回作品でもあり、レッドフォードが製作総指揮を務めている。監督は俳優としても活躍するE・バーンズ。消防士を退職し、ニューヨーク郊外で悠々自適の暮らしをしているフィッツパトリックには二人の息子がいた。長男のミッキーはしがないタクシー運転手だが、弟フランシスはウォール街で成功していた。ミッキーはある日車に乗せた旧友のホープと意気投合し、電撃結婚をする。二人の生活は苦しく、ホープはアルバイトを続けていた。しかし彼女にはフランスに留学したいという夢があった。そんな時、ミッキーのタクシーに３年前に婚約解消をしたヘザーが乗り込んで来る。実は、ヘザーは今フランシスと不倫中であり、偶然、ミッキーがそのことを知ってしまい……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Artist", "アーティスト", "", "", "http://image.eiga.k-img.com/images/movie/57525/315x210.jpg?3601", 101, "2011年・第64回カンヌ国際映画祭で主演男優賞を受賞した白黒＆サイレントのラブストーリー。舞台は1927年のハリウッド。スター俳優のジョージ・バレンタインは若い端役女優のペピー・ミラーを見初めてスターへと導くが、折しも映画産業は無声からトーキーのへの移行期。無声映画に固執し続けるジョージが落ちぶれていく一方で、ペピーはスターダムを駆け上がっていく。監督は06年の第19回東京国際映画祭グランプリ受賞作「OSS 117 私を愛したカフェオーレ」のミシェル・アザナビシウス。第84回アカデミー賞では作品賞、監督賞、主演男優賞ほか5部門を受賞。フランス映画として初の米アカデミー作品賞受賞作となった。", "2012-04-07", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAT CHASER", "キャット・チェイサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "退屈だが平穏な日々を過ごす男と、裕福だが愛のない人形のような生活の呪縛から逃れようとする女。この二人が出会ったことに始まるマイアミ～ドミニカ間をまたにかけた一大サスペンスを描いた、E・レナードの代表的傑作の映画化。タフガイ、美女、悪党など、人物描写の面白さも見どころ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bellamy", "刑事ベラミー", "", "", "http://image.eiga.k-img.com/images/movie/57665/315x210.jpg?3831", 110, "ヌーベル・バーグを代表する名匠クロード・シャブロルの遺作となった心理サスペンス。休暇中で妻と穏やかな毎日を過ごしていたベラミー警視は、ノエル・ジャンティと名乗る怪しい男から呼び出され、モーテルで男と会う。ノエルは1枚の写真をベラミーに見せ、写真に写る男を殺したと告白するが、その男はノエルと瓜二つだった。不審に思ったベラミーがノエルの身辺を調べていくうちに、世間を騒がせた保険金詐欺事件の意外な真相へとつながっていく。タイトルロールを演じるのは名優ジェラール・ドパルデュー。", "2012-04-21", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRUTH OR DARE ： IN BED WITH MADONNA", "イン・ベッド・ウィズ・マドンナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "1990年５月よりスタートした“ブロンド・アンビション・ツアー”における豪華で迫力あるコンサート・シーンと、オフ・ステージの表情で構成された、マドンナのプロパガンダ的舞台裏ドキュメンタリー。豪華多彩な特別出演陣とマドンナとのかかわりが痛快だ。マドンナのすべてが凝縮されている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("借王４〈シャッキング〉", "借王４〈シャッキング〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "絶妙のチームワークで悪党から大金をむしり取る、３人組の活躍を描く金融エンターテインメントのシリーズ第４作。株で穴を空けた顧客に悪徳金融を紹介して、骨までしゃぶり尽くす悪徳証券会社に、莫大な借金を抱えた３人組が巨大製薬会社を巻き込んで、壮大な詐欺計画を仕組む。１、２作目を手掛けた和泉聖治がメガホンを取っている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY BEAUTIFUL JOE", "マイ・ビューティフル・ジョー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ギャンブル狂のシングル・マザーと生真面目な中年男が幸せをつかむまでを描く感動ドラマ。プロデューサーとしても活躍するシャロン・ストーンが、男運のない母親役で新境地を見せる。長年連れ添った妻に別れを告げらるなど、不幸が重なったジョーは気分転換に競馬場へ。そこで彼は元ストリッパーのハッシュと出会い、何となく興味を抱く。一方、借金まみれのハッシュは彼から金をだまし取ろうとする。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CINCINNATI KID", "シンシナティ・キッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "R・ジェサップの小説をジュイソンが監督した傑作。賭博師の話で、マックィーン版の「ハスラー」といった印象の映画。シンシナティ・キッドはポーカーの名手として自他ともに認める賭博師。その彼のもとに名人、老ランシーが現れ、二人は名人位をかけて対決する。ポーカーの勝負のシーンが、緊迫した雰囲気をよく伝え、相手役のエドワード・G・ロビンソンの老巧な名演も見もの。また闘鶏場面やレイ・チャールズ熱唱の主題歌など、ニューオリンズの熱い雰囲気が、映画全体から伝わってくる。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東のエデン　劇場版I The King of Eden", "東のエデン　劇場版I The King of Eden", "", "", "http://image.eiga.k-img.com/images/movie/54525/315x210.jpg?2396", 82, "2009年4月～6月に放送された人気TVアニメの完結編となる劇場版2部作の第1部。監督は「攻殻機動隊　STAND ALONE COMPLEX」の神山健治。2011年2月19日、日本を襲った60発のミサイル攻撃を誰にも知られることなく阻止した滝沢朗は、その半年後、こつ然と姿を消す。彼を見守ってきた森美咲は、滝沢が残した謎の携帯電話「ノブレス携帯」にあった情報から、滝沢がいると思われるニューヨークへ向かうが……。", "2009-11-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL DIAVOLO IN CORPO", "肉体の悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "R・ラディゲの原作を自由に翻案した官能的野心作。高校生のアンドレアは、ジュリアと出会い、激しい恋に落ちる。ジュリアには、公判中のテロリストの婚約者がいたが、ジュリアとアンドレアは、ジュリアと婚約者のために用意された新居で何度も会い続けるのだった。やがて二人の関係は、ジュリアの婚約者の母親プルチーニ夫人に知られ、激怒した夫人はアンドレアの父親ライモンディ教授に通報。教授は精神分析医で、ジュリアは彼の患者だった。父親の“ジュリアは狂っている”という言葉も、もはや二人の恋を引き裂くことはできない……。M・デートメルスの大胆な演技が論議を呼んだ。G・ランチのカメラも特筆に値する。", "1986-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姐御", "姐御", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "磯島組幹部の紺野淳一は親分から跡目相続を頼まれたことから、同格の影山のうらみを買う。やがて淳一は影山の刺客につけ狙われるようになり、縄張りも次々と奪われていく。そして、ついにだまし討ちに倒れた淳一のため、兄貴分の正貴が立ち上がった……。“渡り鳥”シリーズの斎藤武市による任侠もの。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hachiko: A Dog's Story", "HACHI 約束の犬", "", "", "http://image.eiga.k-img.com/images/movie/54325/315x210.jpg?4789", 93, "日本でも1987年に「ハチ公物語」として映画化された、亡き飼い主を駅前で待ち続けた忠犬として知られる秋田犬ハチの物語を、リチャード・ギア主演、「ギルバート・グレイプ」「サイダーハウス・ルール」のラッセ・ハルストレム監督で映画化。迷子の秋田犬を拾い、ハチと名付けて飼い始めた大学教授のパーカー。ハチは毎日夕方5時に駅前でパーカーの帰りを待つようになるが、ある日、パーカーは大学の講義中に倒れ、帰らぬ人に……。", "2009-08-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美女と怪竜", "美女と怪竜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "歌舞伎十八番の中の一つ“鳴神”をもとに新藤兼人が脚色し、吉村公三郎が監督した異色作。この作品は、前進座の創立25周年を記念して企画されたもので、河原崎長十郎以下前進座一同が総出演し、手慣れた演技を見せる。また、乙羽信子が雲の絶間姫に扮して妖艶さをふりまく。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・図々しい奴", "続・図々しい奴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "第二次世界大戦前を捉えた前作の続編は、戦争に参加しても“図々しさ”を忘れない主人公の面白い生き方が、やはり笑わせてくれる快作となった。羊かん屋の名前が“虎屋”という、「男はつらいよ」シリーズより先行しているセンスなど、瀬川監督の優れた喜劇性を感じさせる。事業の失敗、大戦、敗戦後と、どこまでも図々しく金儲けに生きる主人公の姿に笑い涙が止まらない。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOR BETTER FOR WORSE", "ダーク・ボガードの求婚物語 −エンゲージング・ストーリー −", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「ナバロンの要塞」などの大作で知られるトンプソンが、結婚にまつわる男女の感情や、父娘の心の機微を描いた作品。アンはある日恋人のトニーからプロポーズされ、大喜び。しかし、彼女の父は、トニーが無職で貯金もないために好感がもてない。そこで彼はトニーにある条件を出すのだが……。", "1954-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("福沢諭吉", "福沢諭吉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "幕末から明治の激動の時代に生きた福沢諭吉の半生を描いたヒューマン・ドラマ大作。1835年、中津藩の下級武士の家に生まれた諭吉は、やがて仲間とともに蘭学を志し長崎へ留学。彼はそこで頭角を現し、蘭学塾を開くが……。若手俳優の育成に定評の澤井信一郎が、若き福沢諭吉とその周りに集まる塾生たちとの青春群像劇として仕上げた歴史ドラマ。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MERCHANT OF DEATH", "復讐の処刑コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "復讐に燃えるアウトロー刑事と麻薬組織の死闘をダイナミックに描いたハード・アクション。少年時代に目の前で両親と妹を惨殺された刑事ジム・ランドル。育ての親でもあった警部が追跡中のドラッグ・ディーラーに射殺されたことから、ジムはおぼろげな過去の記憶をよみがえらせ巨大組織に復讐を誓う。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN WITH TWO BRAINS", "2つの頭脳を持つ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "人気コメディアン、S・マーティンと、K・ターナーが共演したスリラー・コメディ。二人の役どころは少々頭のおかしい天才外科医と、彼に恋する色情狂の美女。ところが医者の方は、心やさしい脳ミソ（だけの女だが、生きていてテレパシーで話す）に惚れている。そこで彼は考えた。色情女に“脳ミソ美人”を移植してしまえばいい……。というワケで、ローン・レンジャーとトーントンもゲスト出演したりするなか、三角関係のロマンスが展開していく。「ペニーズ・フロム・ヘブン」「四つ数えろ」のマーティン＆ライナーの笑激コンビが、ウィットとペーソス、パロディーの極致を存分に味あわせてくれる。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POINT BLANK", "殺しの分け前 ポイント・ブランク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "R・スタークの“悪党パーカー”シリーズ第１作『人狩り』を、リアルなハードボイルド・タッチで映像化した傑作。自分を裏切った親友を、獲物を狙う鷹のような鋭さで追い詰める男になりきったマーヴィンの快演と、ドライな緊張感をぐいぐいと盛り上げるブアマンの演出が絶妙だ。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もっとしなやかに　もっとしたたかに", "もっとしなやかに　もっとしたたかに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1978年度城戸賞に入選作した小林竜雄の脚本をもとに、藤田監督が現代の壊れやすいニューファミリーを淡々と描く。「赤ちょうちん」「妹」の系譜に連なる作品。風間が吐く“くたばれ！ニューファミリー”の台詞が、1970年代後期の時代の気分を感じさせる。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不夜城", "不夜城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "上海マフィアや北京マフィア、台湾社会が入り乱れる新宿歌舞伎町。その中で台湾人と日本人のハーフ、リウは一匹狼として生きていた。ある日、彼のかつての相棒フーチュンがこの街に帰って来たという噂が流れる。上海マフィアのボスはリウを呼び出し、自分の部下を殺して逃げたフーチュンを３日以内に見つけ出すように命じた。できなければ死が待つのみ。そんなリウの元に、フーチュンの愛人だったという謎の女、夏美が現れる。リウは彼女とともにフーチュンの足取りを追うが……。馳星周のベストセラー小説を、“アジアの顔”とも呼べる金城武を配して映画化した、愛と裏切りが交錯するハードボイルド・サスペンス。歌舞伎町で繰り広げられる人間たちの愛憎を生々しく浮かび上がらせる。第18回香港電影金像奨で最優秀美術賞を受賞している。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Death Race", "デス・レース", "", "", "http://image.eiga.k-img.com/images/movie/53845/315x210.jpg?4294", 105, "1975年に公開されたデビッド・キャラダイン、シルベスター・スタローン主演「デス・レース2000年」を、「バイオハザード」のポール・W・S・アンダーソン監督、「トランスポーター」のジェイソン・ステイサム主演でリメイク。2012年のアメリカ。無実の罪を着せられ、孤島の刑務所に収容された元レーサーのジェンセンは、刑務所内で凶悪犯たちが武装マシンに乗って繰り広げる殺人レース“デス・レース”への参加を強要される。", "2008-11-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いぬのえいが", "いぬのえいが", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "“犬との生活”をテーマに、犬と人の愛情溢れるやりとりを見つめたヒット作。「ジョゼと虎と魚たち」の犬童一心監督をはじめ映像作家７人の手掛けた各編が、リレー形式でつながれ、物語が進展していく。各監督が描き出す犬と人の心温まるエピソードに、ときに癒され、ときに胸を締め付けられること必至。幼い頃から飼っていた愛犬マリモを亡くした美香。あまりの悲しさに、もう犬は飼いたくないと思っていたが……。（「ねえ、マリモ」）", "2005-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南海の花束", "南海の花束", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "戦時中の日本軍南方戦線基地を舞台に、南方航空路の開拓に挺身する人々の友情、責任感、犠牲的精神などを愛国的見地に立って、壮大なスケールで描いたヒューマン・ドラマ。ハリウッドで俳優として活躍していた阿部豊監督が、“アメリカ仕込み”と評されるドライなタッチで演出し、全編にわたって観る者を魅了する。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霊−リョン−", "霊−リョン−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "記憶を失った女子大生が奇怪な連続変死事件に巻き込まれ、自らの驚くべき過去と向き合っていく様を描いたコリアン・ホラー。“水”をモチーフにした見せ場が恐怖感を煽ると共に、主演女優キム・ハヌルの清楚なイメージを逆手にとった衝撃的なストーリー展開が戦慄を呼ぶ。記憶を失くしたまま大学に通うジウォンの友人たちが謎の死を遂げていく。自らの過去を探り出した彼女が、やがて知る真実とは……。", "2005-08-20", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gossip", "ゴシップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "大学生３人が自分たちが作り出したスキャンダルによって破滅していく姿を、若手スターの共演で描くサスペンス・タッチの青春ドラマ。一流大学に通うデリック、キャシー、トラヴィスは、噂が広がるメカニズムを解明するため計画を企てる。彼らは一人の女子学生をターゲットにし、彼女がレイプされたというゴシップを流し、予想以上の周囲の反応を面白がって見ていた。ところが、事態は次第に深刻化していき……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RANG−E　KHODA／THE　COLOUR　OF　PARADISE", "太陽は、ぼくの瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「運動靴と赤い金魚」のマジット・マジディ監督が盲目の少年の心を優しく見つめた感動作。モハマドはテヘランの盲学校に通う８歳の少年。モハマドの母は５年前に他界したが、大自然の中で、太陽の光や草木の匂いに包まれて、心やさしく育っていった。そんな中、学校が夏休みに入ったある日、モハマドの父が迎えにやって来る。不機嫌な父にはある考えがあったのだった。", "1999-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOSE　RIZAL", "ホセ・リサール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 178, "フィリピン独立運動の理論的指導者だったホセ・リサールの短くも劇的な生涯を、フィリピン映画界を代表する女流監督がドラマティックに描いた歴史大作。19世紀末、スペイン植民地下のフィリピンでホセ・リサールが祖国の独立を願って書いた二冊の書は、大きな反響を呼ぶ。リサールを脅威に感じたスペイン当局は、罪状をでっち上げ国家反逆罪で監獄に投獄。弁護士らの必死の努力も叶わず、リサールは35歳の若さで銃殺されてしまう……。本作は、フィリピン映画初の本格的日本公開作品としても話題となった。", "1998-01-01", "35", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTERSECTION", "わかれ路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "高名な建築家ヴィンセントは、16年連れ添った才色兼備の妻サリーと離婚。不倫の関係にあった女性記者オリヴィアとの結婚準備に入った。ところが、いざとなるとサリーにも未練が残る。多感な年頃に差しかかった一人娘のこともあり、彼の心は揺れた……。回想形式を多用したラブ・ストーリーで、ラストまで全体が極度にドラマティック。こじれた三角関係が、意表を突いた苦いハッピーエンドで締めくくられる。名手ジグモンドの撮影は“絵のよう”という形容を虚しくさせるほど絶品。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rambling Rose", "ランブリング・ローズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "アメリカ南部で平穏に暮らす一家に、田舎娘ローズがお手伝いさんとしてやって来る。家族との関係もうまくいき、彼女の新たな人生は順調にスタートしたかに見えた。しかし、彼女には、性欲過多という自分ではどうにもならない性癖があった。あるがままの自然体で生きるローズと、その生き方を優しく見つめる家族との心の交流を描いた秀作ドラマ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THIS EARTH IS MINE", "太陽の谷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "禁酒法時代のカリフォルニア。当地で大ブドウ園を営むフィリップ・ランボーにはジョンという孫がいた。だがジョンには私生児として生まれたという隠された事実があった。根っからの反逆児に育った彼は、やがて叔父の娘に結婚を申し込む……。R・ハドソン主演の一大ロマン。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EIGHT MEN OUT", "エイト・メン・アウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "1919年のワールド・シリーズまで快調に勝ち進んできたシカゴ・ホワイトソックス。チームは伝説のスラッガー“シューレス”ジョー・ジャクソンをはじめ強者ぞろい。ところが第一戦で大方の予想に反して惨敗する。そしてにわかに八百長試合ではないかというウワサが広まった。全米を騒がせた有名な“ブラック・ソックス・スキャンダル”をテーマに、野球を通して男の友情を描いた作品。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どっちにするの。", "どっちにするの。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アメリカ本社のコンピュータ・トラブルで突然、経営不振のオモチャ会社の副社長になってしまったOL・桑田伸子。彼女が、会社再建に向けて奮闘する様を、同僚、先輩社員との恋模様も交えて描いたシンデレラ・ストーリー。赤川次郎の『女社長に乾杯！』を「1999年の夏休み」の金子修介が脚色、映像化。奇抜な物語をコミカル・タッチでさらりとこなしている。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("完全なる飼育・愛の40日", "完全なる飼育・愛の40日", "", "", "http://ecx.images-amazon.com/images/I/21SMKGV71JL._SL210_.jpg", 89, "竹中直人、小島聖共演で話題を呼んだ「完全なる飼育」の第２作。心理カウンセラーの赤井は若い女性、晴香から援助交際を申し込まれる。数日後、同意のもとに催眠療法を試みると晴香が語り始めたのは、彼女が高校二年の秋に起きた凄まじい出来事だった……。オーディションで選ばれた深海理絵が激しい濡れ場に体当たりで挑戦し、話題となった。", "2001-06-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　MAN　APART", "ブルドッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "愛妻を殺された麻薬捜査官の復讐を描くバイオレンス・アクション。「トリプルX」のタフガイ、V・ディーゼルが主人公に扮して、復讐のためなら悪党にも接近する、壮絶な闘争心を持つ男を熱演する。謎に満ちた敵の正体に迫る、ミステリアスで意外性に富んだ展開も見どころ。「交渉人」のF・G・グレイ監督による、スリリングな語り口がさえ渡る。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("炮打雙燈／RED FIRECRACKER GREEN FIRECRACKER", "哀戀花火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "チュンチーは、黄河のほとりで300年間爆竹を商う老舗の若き女主人。男の跡継ぎのないその家で、男として育てられ、男装を通してきた。絵師のニウパオに出会い、女としての激情に衝き動かされるが、恋に落ちた二人の前に老舗の掟が立ちはだかる。街の長老たちの「爆竹合戦に勝った者に結婚を許そう」という言葉に、ニウパオは命を懸けて決戦に挑む。パワフルでスペクタクルな映像、強烈な情熱と自由な精神を持ったヒロインとヒーロー、全編にみなぎる緊張とテンポの良さ——、愛が火花散り、哀切漂う鮮烈な恋物語である。ベルリン映画祭青年論壇賞を受賞。", "1993-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEALING　HARVARD", "キャンパス・クレージー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "おバカな二人組が、自ら企てた大金強奪計画から大災難を引き起こす爆笑コメディ。脚本は「アナライズ・ユー」を手掛けたピーター・トーラン。結婚間近で幸せなジョン。だが、姉から見せられた昔のビデオに“姪の大学の全費用を払う”と話す自分の姿が。なんとその姪が、先日ハーバード大学に合格したのだ。かくして大金が必要になった彼は、つい変わり者のダブに相談してしまい……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHO　IS　CLETIS　TOUT？", "クライム＆ダイヤモンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "映画マニアの殺し屋ジムに捕らえられたトレヴァーは、何かいい話を聞かせてくれたら命を助けてやるという彼との約束に応じ、ある宝石強盗事件の話を始める……。全編に映画のパロディーやエッセンスを詰め込みつつも、マニア内輪受けに終わることなく、渋いフィルム・ノワールとして機能していくという、ユニークなクライム・ムービー。ジム役のティム・アレンの映画オタクぶりも実に楽しい。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("世界を賭ける恋", "世界を賭ける恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "日活の製作再開５周年記念作品で、武者小路実篤の名作『愛と死』を石原裕次郎と浅丘ルリ子のコンビで映画化した純愛メロドラマ。新進建築家の村岡は、仕事で３ヵ月間ヨーロッパに滞在することになる。日本に残した恋人の夏子とはお互いに毎日手紙を書き愛をはぐくむが、村岡が帰国の途中に夏子は急死する。日本映画初の本格的ヨーロッパ・ロケーションが敢行されたが、この古臭いメロドラマを活性化させるには至らなかった。当時は主に小林旭と共演していた浅丘ルリ子が、石原裕次郎と初めて本格的な共演をした作品。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GAUNTLET", "ガントレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ラスベガスからアリゾナ州フェニックスまで、重要事件の証人の護送を命じられた警官。その護送の途中、不可解な事件が連続して起こる。フェニックスに行けば殺されるとおびえる証人。警官隊が待ち受けるなか、バスに乗りこんだ彼らが、数千発の弾丸の雨に突っ込むラストが強烈。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE BEWILDERED PEOPLE IN THE NIGHT", "途方に暮れる三人の夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "日系三世のインディペンデント映画作家グレッグ・アラキの長編第１作。女一人に男二人、その間に生まれたバイセクシュアルな三角関係。互いに愛情を求めながら、混乱し傷つけあう不器用な愛情の行方が、モノクロームのザラついた映像と何も起こらないロサンゼルスの夜を舞台に綴られていく。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボディガード牙", "ボディガード牙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "千葉真一主演の空手アクション“ボディガード牙”ものの第１作。アメリカでの空手修行を終えた牙直人は、帰国後ボディガード業を開設。彼は、パトロンだったボスの死によって大量の麻薬を手に入れた女を、マフィアの手から守ることになる……。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大戦隊ゴーグルファイブ　合成怪獣モグラモズー登場", "大戦隊ゴーグルファイブ　合成怪獣モグラモズー登場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "暗黒科学帝国デスダークに立ちむかう5人のヒーロー、ゴーグルファイブの闘いを描く特撮テレビシリーズのオリジナル劇場版。デスダークが恐るべき超高分子破壊砲で日本壊滅を狙っていると知ったゴーグルファイブ。計画を阻止するべく捜査に乗りだした彼らの前に、デスダークから脱走してきたザゾリヤ博士が現われる。ニセの情報で地獄が原へとおびき出されたゴーグルファイブに、合成怪獣モグラモズーが襲いかかり……。", "1982-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Keiko", "Keiko", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "カナダの映画作家、C・ガニオンが平凡な日本人女性の生き方をドキュメンタリー・タッチで描く。ほとんどアドリブで演技陣には素人を起用した。ケイコは23才のOL。家族のもとを離れ自由な一人暮らしをエンジョイしている。しかし、最近は恋人もいず、単調な毎日にいや気が差していた。その沈んだ気持ちのまま、ケイコは高校時代の恩師と酒を飲み、バージンを失う。そして、ある日ハンサムなカメラマンに出会い、恋に落ちる。彼女は夢中になるが、男にとってケイコは単なるセックスの対象でしかなかった……。外国人から見た日本人というのは、時に滑稽に映りがちだが、本作は実にリアルに日本人像を浮かび上がらせている。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOLD RUSH", "チャップリンの黄金狂時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "チャップリンの作品のなかでも最高傑作といわれる１編で、初の長編映画でもある。ゴールド・ラッシュにわくアラスカを舞台に描く放浪者チャーリーの一獲千金の夢と恋の物語。チャップリンがアラスカの写真にヒントを得て映画化したというエピソードも残されている。数々の名場面があるが、なかでも山小屋で飢えたチャーリーと相棒の二人がドタ靴を煮て食べるシーンはあまりにも有名だ。靴の上皮と底をナイフとフォークで巧みに分けて靴ひもをスパゲティのように皿にのせ、まるで豪華なディナーを食べるように見せる場面はチャップリンの至芸である。吹雪で飛ばされかかった山小屋のなか、あやうく崖っぷちに引っかかっているとも知らず、歩きまわり、その度に小屋が傾きかけるスリリングなシーンも強烈な印象を残す。ウイットとスリルに富み、抱腹絶倒の場面を作り出すチャップリンの才能がワン・シークエンスごとにあふれている。以前発売されたビデオには「給料日」が併録されていた。", "1925-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PINERO", "ピニェロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "実在したラテンのカリスマ・アーティスト、ミゲル・ピニェロの短くも壮絶な人生を、キューバ系アメリカ人監督のレオン・イチャソが映画化。劇作家、俳優、詩人の顔を持つ一方、犯罪歴が多数あり、ジャンキーでもあった型破りな男ピニェロの人間像に迫る。「キャットウーマン」のB・ブラットが、ピニェロ役に挑戦し、入魂の演技を披露する。移民の子として育ったピニェロは、服役と出所を繰り返す毎日だった。ある時ピニェロは、仲間の勧めで服役体験をもとにした戯曲をリリースし、大成功を収める。しかし、華やかなショー・ビジネスの世界には馴染めず……。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春風沈酔的晩上", "スプリング・フィーバー", "", "", "http://image.eiga.k-img.com/images/movie/55045/315x210.jpg?4654", 115, "天安門事件を題材にした「天安門、恋人たち」で2006年に中国当局から5年間の映画製作禁止を言い渡されたロウ・イエ監督が、当局の目をかいくぐって手持ちカメラによるゲリラ撮影で撮りあげた心理サスペンス。現代の南京を舞台に、2人の男と1人の女が繰り広げる錯綜した関係をスリリングに描き出す。カンヌ国際映画祭で脚本賞を受賞したが、タブーとされる同性愛を扱っていることから中国では上映禁止となった。", "2009-01-01", "8,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ó Pai Ó", "オーパイオー", "", "", "http://image.eiga.k-img.com/images/movie/54065/315x210.jpg?3282", 98, "第5回スペイン・ラテンアメリカ映画祭上映作品。ブラジルの黒人奴隷の歴史を象徴する町サルバドルのカーニバル最終日は、貧しくも陽気に生きる人々で賑わっていた。しかしカーニバル嫌いの厳格な大家がカーニバルに反対し、断水を決行して大騒ぎに……。歌や踊りや恋で賑わうカーニバルの1日を華やかに鮮明に描く。", "2008-09-14", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUTINY", "カリブの反乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "19世紀初頭、１千万ドルの金塊に群がる海の男たちと謎の美女。そして彼らを狙う英国海軍、果たして金塊は誰の手中に？　アメリカ海軍の誕生秘話を盛り込みながら展開していく海洋アドベンチャー。出演者の一人、A・ランズベリーはため息が出るほどの美しさである。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGHLANDER２−THE QUICKENING", "ハイランダー２ 甦る戦士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "今や人間になって暮らすかつての不死身の超人ハイランダー、マクラウドのもとに、彼の故郷惑星ザイストから暗殺者が送り込まれた。再びパワーを得たマクラウドは、宇宙の未来を懸けて惑星ザイストの悪の将軍カターナとの対決に臨む。華麗なSFXを駆使して描く、アクション満載の本格派SFファンタジー。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Golden Age of Czech Puppet Animation", "チェコ人形アニメの巨匠たち", "", "", "http://image.eiga.k-img.com/images/movie/54185/315x210.jpg?3452", 68, "日本未公開の作品や貴重な制作過程の映像などを中心に、チェコアニメ界の巨匠たちの情熱や創意工夫に迫るドキュメンタリー。人形劇が社会風刺や体制告発を代弁するメディアとして活躍したチェコで、独自の発展を遂げたパペット（人形）アニメーション。イジー・トルンカ、カレル・ゼマン、ヤン・シュバンクマイエルら時代に翻弄された作家たちの歴史を、珠玉の作品群と振り返る。", "2008-12-20", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RETOUR A｀ LA VIE", "人生への回帰", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "第二次世界大戦からの帰還をテーマにした、４人の監督たちによるオムニバス映画。瀕死の状態で帰郷した女の遺産をめぐる物語を描いたA・カイヤットの裁判劇「エマの場合」、戦争から戻っても職がないためホテルのバーテンダーとして働く男の日常をコミカルに描いたG・ランパンの「アントワーヌの場合」、ドイツの収容所から生還した男が、フランスで捕虜となったドイツ兵と対峙するH・G・クルーゾー監督の「ジャンの場合」、故郷に帰ったものの妻には逃げられ、アパートは他人に占領されてと、大災難男の悲喜劇を描いたJ・ドレビル監督の「ルネの場合」、ドイツ人女性と結婚した男と村人の葛藤を描いた同じくドレビルの「ルイの場合」の全５話で構成。それぞれ、フランス映画の一時代を支えた巨匠たちの競作が興味深い。", "1949-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウィークエンド・シャッフル", "ウィークエンド・シャッフル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "筒井康隆のスラップスティック小説を、ピンク映画界の雄・中村幻児が映画化した野心作。異色のキャスティングに加え、主題歌をフランスのセルジュ・ゲンズブールが作曲するなど、顔合わせの妙を楽しめる。ある郊外住宅地の週末。一見平和な家庭・斑猫家に、突然嵐のように事件が持ち上がる。息子は誘拐され、妻はセールスマン転じて強盗に変身した男に犯され、押しかけてきた妻の女子大生時代の友人たちは、その主人になりすました強盗と酒だ、ダンスだの乱痴気騒ぎを繰り広げて……。平和な日常の中に潜む狂気を拡大して描き、日本映画には珍しく乾いたブラックな笑いを誘うが、一部サービス過剰なところが目立つのが惜しまれる。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("8 Seconds", "エイト・セカンズ／伝説の８秒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　ロデオチャンピオン、レイン・フロストの生涯を、献身的な女性ケリーとの結婚やタフ・ヘドマンらライバルたちとの交流、そして恐るべき猛牛レッドロックへの挑戦といったエピソードを織り交ぜながら描く。テレビシリーズ「ビバリーヒルズ高校白書」で人気者になったルーク・ペリーがフロストを熱演する。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Speed", "スピード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "通勤バスに時限爆弾を仕掛けた爆弾魔が巨額の身代金を要求。そのバスは50マイル（80キロ）以下に減速すると自動的に爆発する仕組みになっている。行動派の若きSWAT隊員ジャックは、多くの乗客を乗せたこのバスに乗り込み、次々と襲いかかるトラブルに立ち向かう……。「ダイ・ハード」「氷の微笑」などの撮影監督、J・デ・ボンが監督デビューを飾ったノンストップのアクション。冒頭、爆弾の仕掛けられたエレベーターでのパニックから、メインとなるバスの疾走まで、危機また危機の連続は緊張感たっぷり。K・リーヴス扮する主人公を苦しめる、D・ホッパーの老獪な悪役ぶりも光る。共演のS・ブロックが本作でブレイク、以後主演作品が続いた。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("荒海に挑む男一匹　紀の国屋文左衛門", "荒海に挑む男一匹　紀の国屋文左衛門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "江戸時代の名商人を捉えた人情もの。時は元祿、豊臣残党の蜂起を憂慮した５代将軍・綱吉の命により、武器弾薬密貿易の温床といわれる紀州回漕問屋一同が処置を迫られた。一同の束ねをする紀の国屋文左衛門が自首して出ようとするが、息子の文平がそれを止め、自ら父の身代わりとなって江戸へ出る。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOW I WON THE WAR", "ジョン・レノンの僕の戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "J・レノンが単独出演した作品。ビートルズ映画で監督を務めたR・レスターがメガホンを執り、戦争の愚かさを、ブラックユーモアで笑い飛ばしている。時は、第二次世界大戦のさなか。イギリス軍のある中隊は、とんでもない命令を受ける。その任務とは、敵陣の向こう側に、VIPのためのクリケット場を作ること。万事がこんな調子の中隊は、北アフリカ、フランス、ドイツと転戦する間に犠牲者が増え続ける。", "1967-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Psycho", "アメリカン・サイコ", "", "", "http://image.eiga.k-img.com/images/movie/1255/315x210.jpg?2948", 102, "80年代、好景気に沸くニューヨーク。ウォール街にある証券会社のエリートとして誰もが羨む贅沢な生活を送るパトリック・ベイトマン。高級フラットに住んでデザイナーズ・スーツに身を包み、完璧な体型を維持するハンサムなヤッピー。社会的な成功をすべて手に入れたかに見えた彼だったが、物質では満たされない心の乾きを感じるようになっていた。次第に目立ち始める奇行、そしてついには殺人への衝動が抑え切れなくなり……。", "2001-05-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIME UNDER FIRE", "ディープクライシス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "精鋭戦士 155名が乗り込んだ最新鋭大型原子力潜水艦アラバマ号が大西洋のバミューダ海域を潜行している時に、突然大きな揺れが起こり、それは大きな衝撃に変わった。パニック状態になった乗組員たちは想像を絶する光景を目の当たりにする……。時間の裂け目に落ちてしまった彼らの運命を、SFXシーン満載で描くSFサスペンス。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stop Making Sense", "ストップ・メイキング・センス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "　アメリカの人気ロックバンド、トーキング・ヘッズが1983年に行なったツアーの模様を収めたライブ・フィルム。黒子をセット変えに起用するなどユニークなステージをシンプルにまとめ、人気を博した。99年にはドルビー・デジタル音響のリマスター版が作られた。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Charlie St. Cloud", "きみがくれた未来 ", "", "", "http://image.eiga.k-img.com/images/movie/55365/315x210.jpg?4342", 0, "「ハイスクール・ミュージカル」のザック・エフロンが、最愛の弟を失った兄の葛藤（かっとう）と再生を繊細に演じたヒューマン・ドラマ。弟サムを交通事故で亡くしたことに罪の意識を持つチャーリーの前に、死んだはずのサムが姿を現す。兄弟の日課だったキャッチボールを続けることをサムに誓ったチャーリーは、自らの将来を犠牲にしてまで弟との約束を守り続けるが……。監督は「セブンティーン・アゲイン」でもエフロンと組んだバー・スティアーズ。", "2010-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Meduzot", "ジェリーフィッシュ", "", "", "http://image.eiga.k-img.com/images/movie/53385/315x210.jpg?2320", 82, "イスラエルの美しい海辺の街を舞台に、人々の心の機微を繊細なタッチで切り取ったヒューマン・ドラマ。それぞれ作家としても活躍するエドガー・ケレットとシーラ・ゲフェンが初メガホンを取り、2007年カンヌ国際映画祭で最優秀新人監督賞を受賞した。恋愛も仕事も失敗続きの女性バティアは、ある日海辺で浮き輪をつけた少女に出会う。ひと言も話さないまま後をついて来る少女を仕方なく自宅に連れて帰るバティアだったが……。", "2008-03-15", "46,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SALOME’S LAST DANCE", "ケン・ラッセルのサロメ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "新約聖書に描かれたヘロデ王、ヘロデヤとその娘、そしてバプテスマのヨハネの挿話をもとに、オスカー・ワイルドが一幕ものにまとめ上げた『サロメ』。これまで、何度となく映画化されてきたこの物語を、K・ラッセルが長編第15作として撮り上げた。しかし、その内容は原作に忠実ではなく、友人の男娼の館に招待されたワイルドの眼の前で、当時上演禁止となった『サロメ』の舞台が繰り広げられるというもの。しかも、そこに登場するサロメ像に大胆にキャラクター変更がなされているところが、ラッセルらしい。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BOHE｀ME", "ラ・ボエーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "プッチーニのオペラでおなじみの題材による無声期のメロ・ドラマ。19世紀のパリを舞台に、若い劇作家の生活を必死に支え、彼の成功を知らずに衰弱死していく薄幸の娘の姿を描く。育ての親のD・W・グリフィス監督と訣別し、当時最高の契約金で移籍したL・ギッシュのMGM映画第１作でもある。", "1926-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KILLING STREETS", "キリングストリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ベイルートで反アメリカのテロリスト集団に捕らわれた兄を救うべく、壮絶な戦いに挑む双子の弟。M・パレが、二役でこの兄弟を演じたバイオレンス。B級アクションの名プロデューサー、M・ゴーランが、フォト・ジャーナリスト出身の新人監督を起用して製作した。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悲しき60才", "悲しき60才", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "坂本九と森山加代子の名曲がズラリと並んだ青春歌謡映画。ショー構成のクレジットに青島幸男の名前がある。ジェリー・ルイスのようなジェリー藤尾やダニー飯田とパラダイスキングが出てくるナベプロものの１本。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　TE＾TE　D’UN　HOMME", "モンパルナスの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "フランスの名匠J・デュヴィヴィエがG・シムノンの『男の頭』を映画化した探偵もの。アパートで中年女性が殺害され、フェリアという男が逮捕される。しかしメグレ探偵は事件に不審を抱き、真犯人がいると確信する。やがて、捜査線上に医学生ラディックが浮かび上がるが、彼は結核に冒され余命いくばくもなかった……。", "1933-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Seeking Justice", "ハングリー・ラビット", "", "", "http://image.eiga.k-img.com/images/movie/57865/315x210.jpg?3564", 106, "殺人の罪を着せられた男が闇の組織に単身挑む姿を、ニコラス・ケイジ主演で描くサスペンスアクション。監督は「バンク・ジョブ」「世界最速のインディアン」のロジャー・ドナルドソン。妻が何者かに暴行され怒りに震えていた高校教師ウィルは、「奥さんを襲った相手を代わりに殺してやる」と言って近づいてきた謎の男サイモンの提案を受け入れてしまう。しかし半年後、今度は自分が他人の代わりに人を殺すよう強要される。それを拒否したウィルは殺人の罪を着せられて追われる身となり、謎の巨大な組織と戦うことになる。共演に人気TVシリーズ「マッドメン」のジャニュアリー・ジョーンズ、「メメント」のガイ・ピアース。", "2012-06-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED SCORPION ２", "レッド・スコルピオン2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ヒトラーやムッソリーニを崇拝し、アーリア人による世界征服を企てる狂信的な白人至上主義者のケンドリック。彼はその昔キリストの脇腹を刺したといわれ、所有者に魔力を与えるという伝説の“運命の槍”を入手し、要人大量暗殺計画を実行しようとする。ケンドリックの野望を阻止するべく、ニック・ストーン率いる新生“レッド・スコルピオン”が立ち上がった。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飼育の部屋", "飼育の部屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "郵便配達人の松男は綿密に計画を練って短大生・まどかを誘拐、自宅のアパートに監禁する。恐怖の日々の中、まどかは部屋にあった心理学の本を発見し、“人質が誘拐犯に愛情を示す傾向がある”という記述を読んで愕然とする。彼女は自分の中に異常な心理が芽生えていくのを感じ、これは戦いだと決心する……。社会問題となった女性拉致監禁事件を新たな切り口でとらえ、犯人と被害者の心的相互依存症＝ストックホルム症候群の真相をえぐる問題作。これが２作目となったキム・テグワン監督の丹念な心理描写が光る。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("湖の琴", "湖の琴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "近江の国の糸とり工場で働く、さく。やがて宇吉という男と愛し合うようになるが、ある日京都から来た三味線の師匠・桐屋紋左衛門も彼女に一目惚れ。さくを京都に連れていってしまう……。愛し合う男女に襲いかかる悲劇を切々と描いた作品。同じ水上勉原作「五番町夕霧楼」とともに、晩年の田坂の代表作とされる。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAPTAIN RON", "キャプテン・ロン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "M・ショートがマジメだが少し抜けてるお父さん役、元ディズニー映画の子役出身のK・ラッセルが型破りな船長役という冒険活劇風のコメディ。叔父の遺産で、由緒あるヨットを譲り受けたハービーは、平凡な日々からの脱出と家族の絆を深めるために、ヨットをカリブの島からマイアミまで自分で運ぶ決心をする。だが島で彼らを待っていたのは、ボロボロのヨットと、風変わりで超過激な船長“キャプテン・ロン”だった。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WONDERFUL　DAYS", "ワンダフルデイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "製作費13億円、5年の歳月をかけて制作された韓国のSFアニメを、クリエイティブ集団ガイナックスが日本語版として製作。演出、脚本には「王立宇宙軍・オネアミスの翼」の山賀博之が当たった。さまざまな技術を組み合わせて構成された画期的な映像で、夢と希望を求める人々の闘いを描く。西暦2142年。大気汚染から防護された都市エコバンの人々と、荒廃した町に追いやられた難民たちの闘いが激化し……。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN WITH ONE RED SHOE", "赤い靴をはいた男の子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "気のいいバイオリニスト、リチャードは、ひょんなことからCIAの内ゲバに巻き込まれてしまう。妖艶な女性諜報員に誘惑されたり、あげくには何者かに命を狙われるなど散々な目に……。T・ハンクス主演による痛快なアクション・コメディ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蜘蛛男", "蜘蛛男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "江戸川乱歩原作によるミステリー映画。怪人蜘蛛男によって次々に美女が誘拐されるという事件が発生する。名探偵・明智小五郎は得意の推理を駆使して事件の謎を解明し、美女たちを救い出す。「姿三四郎」などで知られる肉体派の藤田進の明智小五郎役が異彩を放っている。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("図書館戦争　革命のつばさ", "図書館戦争　革命のつばさ", "", "", "http://image.eiga.k-img.com/images/movie/57685/315x210.jpg?2996", 0, "作家・有川浩の人気シリーズ小説で、2008年にTVアニメ版も放送された「図書館戦争」を映画化。公序良俗を乱す表現を取り締まるため制定された「メディア良化法」の下で行われる不当な検閲に対し、良書を守るため戦う「図書隊」所属の笠原郁と堂上篤は、デート中に緊急招集がかかってしまう。続発するテロ事件の手口が小説家・当麻蔵人の作品内容と酷似していることから、メディア良化委員会が当麻を捕えようとしており、郁と堂上は当麻の身辺警護を任される。しかし、良化隊との戦いのなかで堂上が重傷を負ってしまい……。アニメーション制作を手がけるプロダクションI.Gほか、メインスタッフ、キャストもTV版から続投。", "2012-06-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿修羅城の瞳", "阿修羅城の瞳", "", "", "http://image.eiga.k-img.com/images/movie/1265/315x210.jpg?3680", 119, "2000年に上演されて話題を呼んだ劇団☆新感線と松竹のコラボレート舞台作品「阿修羅城の瞳」を映画化。町人文化が栄えた江戸の文化文政期。恋をすると鬼たちの王・阿修羅に生まれ変わってしまうという宿命を負う少女つばきは、幕府の鬼祓い機関の副長を務める元舞台役者・病葉出門と出会う。七代目市川染五郎が映画初主演。監督は「陰陽師」シリーズの滝田洋二郎。音楽は「カウボーイ・ビバップ」などアニメで活躍する菅野よう子。", "2005-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY　LITTLE　BRIDE", "マイ・リトル・ブライド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "キュートな十代の女の子アイドル、ムン・グニョンが主演し、韓国で大ヒットしたラブ・コメディ。大鐘賞の新人男優・女優賞をW受賞した秀作だ。世間知らずな女子高生ボウン。しかし幼なじみの大学生サンミンが留学先から帰国したことで思わぬ運命が訪れる……。両家に大きな発言力を持つボウンの祖父の命令で、彼らは“戸籍だけの結婚”をさせられてしまう。そんな事実を隠しながら学園生活を謳歌する彼女の前に、教育実習生としてサンミンが現れる。", "2005-06-18", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIAMONDS", "DIAMONDS", "", "", "http://image.eiga.k-img.com/images/movie/56235/315x210.jpg?2336", 120, "東京・六本木を活動拠点とするアイドルグループ「キラポジョ」主演のドキュメンタリー。2009年秋「キラキラ六本木女学院」のグループ名で活動を開始し、10年夏、正式名称を「キラポジョ」としてメジャーデビュー。日中韓3カ国の共同事業「100万本の植林活動」公認の「植林親善大使」に就任するなど、「エコ活動アイドル」としても活躍した。しかし、その内情はメンバー間の分裂や脱退、引き抜きなどで混乱。メンバーたちは一からやり直そうと再スタートを誓うが、そんな矢先、あるメンバーが衝撃の告白をする。", "2011-04-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE YOUNG ANIMALS", "ヤング・アニマル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アメリカ人の心に巣食う有色人種偏見、それによって生じる感情的な対立と暴力行為、差別への不満から展開される学園紛争、病めるハイティーンの無軌道な生態など。メキシコ国境に近いカルフォルニア州南部の小さな田舎町にある高校を舞台に、アメリカが抱える諸問題を鋭く描いた社会派の学園ドラマ。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AROUND THE WORLD IN 80 DAYS", "80日間世界一周", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 169, "1872年のロンドン。まだ飛行機もない時代にある紳士が“80日間あれば世界一周できる”と言ったことから２万ポンドを賭けた大冒険が始まる。40人を超えるゲスト・スターの豪華な顔ぶれと大規模なロケーションも話題になったアドベンチャーの古典的名作。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベクシル 2077日本鎖国", "ベクシル 2077日本鎖国", "", "", "http://image.eiga.k-img.com/images/movie/1975/315x210.jpg?2519", 109, "2067年、バイオテクノロジーとロボット技術の急激な発達が世界から危険視された日本は、ハイテク技術を駆使して鎖国を断行。それから10年後の2077年、米国特殊部隊所属のベクシルは、鎖国以後、誰も足を踏み入れたことのなかった日本への潜入に成功するが……。「ピンポン」の曽利文彦監督が、モーションキャプチャーを駆使して描く3DCGアニメーション。本作で声優初挑戦となる黒木メイサがヒロインの声を担当。", "2007-08-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Perez Family", "太陽に抱かれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "希望を求めてキューバからアメリカに渡った男女の群像劇。政治犯として20年間もキューバの捕虜収容所に入れられていた男フアンが、妻子が住むマイアミへの帰還を果たす。しかし妻カルメラは、すでにフアンを愛しながらも遠い存在と感じており、フアンは船で知り合った陽気な娼婦ドティと新たな人生を踏み出そうとする……。家族を愛し抜く男が、身寄りのないキューバ移民たちとニセの“家族”を形成していく下りに、厳しい現実がもたらす悲哀と人間愛のたくましさという相反する側面がうかがえる。M・トメイが日焼けしたムッチリ・ボディの南国娘に変貌。彼女の見上げた役者根性が、作品に笑いと活気を吹き込んでいる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I’LL DO ANYTHING", "ハリウッド・トラブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "ハリウッドの内幕を背景に、売れない俳優と６歳の娘の交流を描いたハートフル・コメディ。俳優のマットは実力がありながらも時流に乗れず失業状態。そんな中、別れた妻が逮捕されたために６歳の娘ジーニーを引き取るはめになり、“仕方なく”マットは映画会社の重役バークの専属運転手という仕事に就く。ある日、バークの会社で働くキャシーの企画が通り、マットはスクリーンテストを受け、合格するのだが……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("合家歡／MR.COCONUT", "ミスター・ココナッツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "中国奥地のジャングルのような村から、妹に招かれて香港に上京してきたミスター・ココナッツ。しかし、都会生活になじめぬ彼は、妹一家から煙たがられ、仕事に就けばヘマをしてすぐクビになり……。香港版「クロコダイル・ダンディー」かと思いきや、さにあらず、笑って泣かせる展開を見せる人情喜劇。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUGRATS　IN　PARIS−THE　MOVIE", "ラグラッツのパリ探検隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "全米で放送開始から10年を経た現在も、世界中の子供に絶大な人気を誇るTVアニメ『ラグラッツ』シリーズの映画版第２作。リーダー格のトミーや小心者のチャッキー、目立ちたがり屋のアンジェリカら、大の仲良しの遊び仲間６人組。パリのデラックスな最新テーマパーク“ユーロレプターランド”に集合したちびっ子たちが、悪徳支配人の陰謀を阻止するために大活躍する。ユーモアと遊び心にあふれた楽しい一作。", "2000-04-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い谷間の決斗", "赤い谷間の決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "石原裕次郎と渡哲也共演による日活のお正月映画として公開された一作。北海道を舞台に、石切場を暴力に訴えて奪い取ろうとする地方ボスと闘う人夫頭と東京から流れて来た大学生との友情を描く。デビュー間もない渡哲也が裕次郎の胸を借りるといった感じになった。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANARCHISTS", "アナーキスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "韓国映画では珍しく中国を舞台にした、オール・ロケによるノワール・アクション。「オールド・ボーイ」「親切なクムジャさん」などで知られる鬼才パク・チャヌクが共同脚本を務めている注目の一作。「ブラザーフッド」のチャン・ドンゴンが抗日組織のアナーキスト＝無政府主義者の愛と悲しみを力強く体現する。1920年代の上海で、日本軍に家族を殺された少年が抗日組織のメンバーと出会った。少年は彼らと共にモスクワ行きの汽車に乗る……。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("闇を横切れ", "闇を横切れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "サスペンスの名手でもある増村監督が手がけた松本清張ばりの社会派ミステリー。ある地方の市長選挙がらみや、都市計画にまつわる汚職など、社会派に似合う記号を散りばめて、新聞記者が連続殺人事件を追うスタイルを採っている。ストリッパーが殺され、その死体の傍に泥酔した市長選立候補者が倒れていた。ある警官の話から誰かに依頼された殺し屋が浮かび上がる。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAGE", "怒りの逃亡者 RAGE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "国境警備隊員を名乗る男たちに秘密謀報機関の研究所へ連行された教師アレックスは、そこで大勢の不法移民が実験台にされている様子を目撃。それは、人間を獰猛化させる科学物質を用いて、意志を持たない“殺人兵士軍団”を作るというものだった。科学物質を注射されながらも、渾身の力と意志で研究所を脱出するアレックス。その後を執拗に追ってくる一味。孤立無援となったアレックスの悪夢のような逃亡と闘いが始まった。どこか『仮面ライダー』を思わせる設定が楽しいハード・アクション。ビデオ発売時タイトルは「ラン・ダウン／怒りの逃亡者」。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Scam", "作戦 THE SCAM", "", "", "http://image.eiga.k-img.com/images/movie/54845/315x210.jpg?4753", 119, "人気韓流スターのパク・ヨンハがデイトレーダーに扮し、仲間たちと一攫千金の勝負に挑むサスペンス・ドラマ。株に手を出して借金を背負ってしまったヒョンスは、その失敗を糧に独学で相場を学びプロの個人投資家となる。やがて元ヤクザのジョングから腕を買われた彼は、エグゼクティブたちの資産を管理するソヨンや仕手株界のエースであるミンヒョン、大株主のチャンジュら凄腕のメンバーが顔を揃える巨額の仕手戦に参加することになる。", "2009-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("異母兄弟", "異母兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "異常なまでに厳しい軍人はその家に仕えていた大女中を手込めにし、女中は身ごもる。妻に先立たれていた軍人はこの女中を後妻として迎えるが……。軍国主義に歪められた人物の性格を浮き彫りにし、同時に女の一生のドラマとして描ききった、家城巳代治の文芸大作。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛するとき、愛されるとき", "愛するとき、愛されるとき", "", "", "http://image.eiga.k-img.com/images/movie/55675/315x210.jpg?2180", 70, "6人の気鋭監督が競作した「Love & Eros CINEMA COLLECTION」シリーズの1作として、「ヘヴンズストーリー」の瀬々敬久監督が撮りあげた官能ドラマ。田舎町で暮らす平凡な事務員・佑子には、「魔法使いゆこりん」としてブログを書くという別の顔があった。ある日、ネット上に露出プレイ写真をばら撒かれた妹を助けるため犯人らしき男に会いに行った佑子は、交換条件として野外で淫らな写真を撮られることになり……。", "2010-10-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOSQUITO SQUADRON", "モスキート爆撃隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "第二次大戦で英国民を恐怖の底に陥れた、ドイツ軍のロケット弾V3号を破壊するために編成された“モスキート爆撃隊”の決死の活躍を描いた戦争映画。ベテラン・パイロットのクィントにドイツの新兵器V3の開発を食い止める命令が下る。捕虜を脱出させることには何とか成功した彼だが、彼の機は森の中に不時着してしまう。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Samurai Avenger: The Blind Wolf", "サムライ・アベンジャー　復讐剣　盲狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "アメリカの映像業界でキャリアを積んできた光武蔵人監督が自ら主演を務めて撮りあげ、世界各地の映画祭で評判を呼んだ異色バイオレンスアクション。犯罪組織のボスに妻と娘を惨殺されて自らも両目を失った男が、盲目の剣士「盲狼」となって復讐に乗りだす姿を、マカロニウエスタン、チャンバラ、ゾンビ、スプラッタといった要素を盛りこみながら描く。「パルプ・フィクション」のアマンダ・プラマーが特別出演。", "2010-08-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リング0 バースデイ", "リング0 バースデイ", "", "", "http://image.eiga.k-img.com/images/movie/1865/315x210.jpg?4370", 99, "女優を目指し、日々舞台の稽古に励む貞子。そんな中、劇団の看板女優が怪死を遂げ、次の公演の主役に新人の貞子が抜擢される。稽古場ではその後も怪現象が続き、貞子のせいだと噂が立つ。そんな貞子を音響担当の遠山だけがかばう。一方、新聞記者の宮地は密かに貞子の過去を探っていた。婚約者を貞子の母・志津子の超能力の公開実験で失った彼女は貞子への復讐に燃えていたのだ……。", "2000-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAR OF THE BIRDS", "鳥たちの戦争 オリバーとオリビア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "カラスのファーギンに鳥たちが脅かされる森で、スズメのベティは別々に助けたカッコウの子供、オリバーとオリビアを育て始める。やがて、同じく両親をファーギンに殺された子ネズミたちと知りあったオリバーとオリビアは、ともにファーギン打倒を計画。大人たちの無関心のなかで、彼らの孤立無援の戦いが始まる。デンマークで物議を醸した児童文学を、シニカルでペシミスティックに描いた異色アニメ。ファミリー・アニメ的な絵柄とは裏腹に、鳥の社会を人間社会に見立てての諷刺の毒が、たっぷりと盛り込まれている。カンヌ映画祭ジュニア部門グランプリに輝いた秀作だ。", "1990-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIVING ON TOKYO TIME", "リビング・オン・TOKYO・タイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "日系３世スティーヴン・オカザキ監督の初の長編劇映画。サンフランシスコのリトル・トーキョーを舞台に、日本からの留学生キョウコと、日系アメリカ人ケンの偽装結婚、恋、別れを淡々と描く。日本人をはじめとする、アメリカの“東洋人”の日常を、ごく自然に、ユーモアと若干の皮肉を込めて映し出した、シリアス・コメディ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACROSS THE GREAT DIVIDE", "ロッキーを越えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "オレゴンにある遺産の土地を受け取るために、ロッキー山脈を西へ向かう12歳と８歳の姉弟。後見人にお金を持ち逃げされて途方に暮れる彼らは、親切なギャンブラーの助けを得て、自然の猛威と戦いながら数々の難局を乗り切っていく。「アドベンチャー・ファミリー」のキャストによるファミリー映画。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗くなるまで待てない！", "暗くなるまで待てない！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "京都府立医科大学に在学中だった大森一樹が、８ミリの長編「明日に向かって走れない！」「死ぬにはまにあわない！」に続いて撮った16ミリ作品。「冒険者たち」を下敷きに、暗くなるまで待てずに出没してしまう吸血鬼の映画を撮る学生たちの姿を描く。大森の映画への愛が切々と伝わってくる好編だ。タイトルは映画館での上映が待ち遠しいとの意味も。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN DIMANCHE A｀ LA CAMPAGNE", "田舎の日曜日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1912年、初秋のパリ郊外。初老の風景画家ラドミラルは自然にとり囲まれた家に家政婦のメルセデスと二人で住んでいる。今日は日曜日。いつものようにパリから息子ゴンザグが嫁のマリーと３人の孫を連れてやって来る日だ。息子夫婦が到着すると、陽光を浴びて樹木が美しく輝く広い庭に子供たちの陽気な声がこだまする。そこに不意に娘のイレーヌが立ち寄る。イレーヌはラドミラル自慢の娘で、好奇心に満ち、いつも恋をしていた。ラドミラルを囲んだひさびさの家族の団らんの中で田舎の日曜日が過ぎていく……。優しい色調に彩られた自然を背景に、古き良き時代の美しい一日を描いた牧歌的な作品。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金魂巻", "金魂巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "人気職業31種類を金持ちとビンボーのまっぷたつに分け、“金・ビ”の流行語を生んで一億総中流階級意識をくすぐった、1984年のベストセラーの映画化。ある産院でいたずらによって入れ替えられた金とビの男の子。28年がたち、二人の人生はまったく逆の方向へ進む。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("光る女", "光る女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "鬼才・相米慎二が作り上げたファンタジー・ラブ・アクションとでも言うべき異色作。北海道から東京へ、かつての恋人を捜しにやって来た山男が、怪しげなクラブのオーナーに拾われ、そのクラブの歌姫と恋に落ちる。かつての恋人はオーナーの愛人になっており、彼女を廃人にされた山男はオーナーを殺し、やがて歌姫と結ばれるというもの。相米慎二らしい映像の飛躍が全編に見られる。船の上で歌う秋吉の『蘇州夜曲』が快く、印象的。プロレスラー・武藤の出演も話題になった。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タクミくんシリーズ　あの、晴れた青空", "タクミくんシリーズ　あの、晴れた青空", "", "", "http://image.eiga.k-img.com/images/movie/56435/315x210.jpg?3840", 89, "ごとうしのぶによる人気ボーイズラブ小説を映画化した「タクミくんシリーズ」5作目で、シリーズ第3作「美貌のディテイル」に続くストーリー。全寮制男子校・祠堂学院の3年生になったタクミとギイの恋は、タクミが他の生徒からいじめを受けないようにと、人目を忍んだ恋になっていた。そんな中、タクミは兄の命日6月15日の墓参りにギイを誘うか悩んでいたところ、ギイから一緒に行きたいという申し出を受ける。しかし喜んだのもつかの間、ギイは当日に行われる生徒会の行事に参加しなければならなくなり、2人はすれ違っていくが……。", "2011-08-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("想い出のボレロ", "想い出のボレロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "佐々木康＝高峰三枝子＝万城目正トリオが「懐しのブルース」「別れのタンゴ」に続いて作った戦後第３作。歌手・恵美子は婚約者・浩一の率いる楽団とともに楽しい地方巡業の途にあった。二人は結婚し、以前恵美子にプロポーズした寺田もその結婚を心より祝福し、お祝いを贈った。しかし寺田の友情を誤解した浩一は楽団から去ってしまう。だが恵美子は妊娠していることがわかり……。高峰の妹・麻梨子は映画初出演。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　シャボン玉のプルン", "それいけ！アンパンマン　シャボン玉のプルン", "", "", "http://image.eiga.k-img.com/images/movie/34065/315x210.jpg?3535", 50, "子供たちに大人気のアニメ「アンパンマン」の劇場版第19作。シャボン玉城で暮らすプルンの悩みは、他の女の子たちのような大きなシャボン玉を作れないこと。ある日彼女は、クリームパンダと一緒に森の谷底に落ちてしまったところをアンパンマンに助けられる。ところがシャボン玉城に帰ってみると、そこはばいきんまんが発明した気味の悪いシャボン玉に支配されていて……。ヒロイン・プルンの声を水野真紀が担当する。", "2007-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BONE DADDY", "ボーン・ダディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "元検死官の人気作家パーマーの元に、生きた人間の体から抜いたと見られる一片の骨が届いた。数日後、骨を抜き取られた惨殺死体が発見される。特別捜査官として捜査に加わったパーマーは“ボーン・ダディ”と名乗る犯人を追うが、犯人は大胆にもパーマーの最愛の者の殺人を予告する……。猟奇的なムードに包まれたサイコ・スリラー。パーマーに扮した個性派R・ハウアーの熱演が光る。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KINGDOM OF THE SPIDERS", "巨大クモ軍団の襲撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "エサを失ったタランチュラの大群がアリゾナの町を襲撃した。農薬撒布や焼き打ちのかいもなく、人々は毒で殺されたあとクモの糸でぐるぐる巻きにされエサとなっていく。グロテスクなムードが漂う異色のパニック映画。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LEGACY", "レガシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "４世紀も前に火刑に処せられた女性が、自分の霊魂の相続人としてある女を選んでしまうというオカルト映画。選ばれた女にはK・ロスが扮し、「卒業」などとは違った味わいを見せている。監督は、「スター・ウォーズ／ジェダイの復讐」を後に発表するTV出身のR・マーカンド。", "1978-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版NARUTO ナルト　そよ風伝　ナルトと魔神と3つのお願いだってばよ!!", "劇場版NARUTO ナルト　そよ風伝　ナルトと魔神と3つのお願いだってばよ!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 14, "人気コミックの劇場版アニメーション第7作「劇場版NARUTO ナルト　疾風伝　ザ・ロストタワー」と同時上映の短編。", "2010-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Man with the Golden Gun", "007／黄金銃を持つ男", "", "", "http://ecx.images-amazon.com/images/I/51-m2EgHkfL._SL210_.jpg", 120, "KGBやマフィアを相手に100万ドルで殺しを引き受ける、“黄金銃を持つ男”スカラマンガ。香港、タイ、シンガポールを舞台に太陽熱エネルギーを利用した装置“ソレックス”をスカラマンガから奪取するためボンドが活躍する。シリーズ第9作。", "1974-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANNE OF THE THOUSAND DAYS", "1000日のアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "1526年、英国王ヘンリー８世は舞踏会で美しい娘アン・ブーリンに出会い、子供がなく別居中のキャサリン王妃と別れて再婚を願う。しかし、ローマ法王は離婚を認めず、彼女は不貞罪に。1920年にエルンスト・ルビッチが「デセプション」として映画化している。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAUVAIS ESPRIT", "赤ちゃんの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "見た目こそかわいいが胸の内に復讐心をたぎらせた赤ん坊が巻き起こす騒動を、ブラックな笑いと共に描いたコメディ。生後間もないベビーを出演させ、さらにCGで赤子らしからぬ表情をも加えた、映像の面白さが光る。フランス映画らしいエスプリや、スタイリッシュなタッチにも注目。デザインを盗作した建築会社社長ポレルの車に轢かれ、命を落とした建築家シモン。ポレルの息子に生まれ変わった彼は復讐を企てる。", "2006-01-14", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　３　WORLDS　OF　GULLIVER", "ガリバーの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ジョナサン・スウィフトの名作ファンタジーの実写映画化。イギリスから小人の国リリパット、そして巨人の国ブロブディングナグへと訪れるガリヴァーの冒険を描いていく。特撮はストップ・モーション・アニメ＝ダイナメーションの巨匠レイ・ハリーハウゼンだが、ここでは合成作業に精力を注ぎ、人形アニメ技術を駆使しているのはリスとワニの二つのシークエンスのみという、彼のフィルモグラフィーの中では異色作となっている。", "1960-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHADOW DANCING", "シャドー・ダンシング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ダンサーを目指すジェシカは内気な性格だった。が、あるオーディションを受けた彼女は、まるで人が変わったように情熱的に踊り、性格も一変する。実は50年前に謎の死を遂げた人気ダンサーの霊に取り憑かれていたのだ。華やかさの裏側に激しい欲望と競争を合わせ持つ劇場を舞台に描く。", "1988-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FEMME FLIC", "ミュウ・ミュウの女刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ミュウ・ミュウが、正義感あふれる女刑事に扮し、少女売春組織に果敢に立ち向かうサスペンス・アクション。美人で、まだ20代の警部補が、自分が担当した事件をきっかけに巨大な少女売春組織の存在に気付く。だが、権力の力により、彼女は職を追われてしまう。", "1979-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARTIANS GO HOME！", "火星人 ゴーホーム！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "10億人の火星人が地球に押しかけ、あちこちに出現してギャグを飛ばし、地球人をパニックに陥れるSFコメディ。テレパシーとテレポートを得意とする彼らのせいで、地球は大混乱。果たして彼らをうまく火星へ帰す道はあるのか？　火星人はすべてコメディアンというムチャクチャな設定が笑える。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Coisa Mais Linda: Historias e Casos da Bossa Nova", "ディス・イズ・ボサノヴァ", "", "", "http://image.eiga.k-img.com/images/movie/34105/315x210.jpg?1441", 129, "1950年代末にブラジルで生まれたボサノバの魅力に迫るドキュメンタリー。現在も活躍するボサノバ界の巨匠カルロス・リラとホベルト・メネスカルが語り手となってボサノバゆかりの地を訪れ、様々なエピソードを披露する。さらにジョアン・ドナートやワンダ・サーといったミュージシャンたちによる演奏や、ボサノバ創成期に活躍した偉大なミュージシャンらの貴重な映像を織り交ぜながら、その歴史を振り返る。", "2007-08-04", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Man Who Knew Too Much", "暗殺者の家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "　娘を誘拐された夫婦の苦悩を、ヒッチコックならではのユーモアとサスペンス仕立てで描いた作品。休暇で娘とスイスを訪れたローレンス夫妻はフランス人のルイと親しくなるが、直後彼は殺害されてしまう。瀕死のルイから国家機密を聞かされた一家は、口封じのため新たなターゲットにされる。息つく間もない展開や妻のジルが射撃の腕前を披露する伏線など、クライマックスへの期待が否が応でも高まる。22年後、「知りすぎていた男」のタイトルで監督自らリメイクしている。", "1935-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOLDIER", "ソルジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「ブレードランナー」の脚本家D・W・ピープルズのシナリオを、「バイオハザード」のP・アンダーソン監督が映像化した近未来アクション。生まれた時から軍に“人間兵器”として育てられた無敵の兵士が、遺伝子段階からエリートとして養成された新世代ソルジャーの出現によって、お払い箱に。辺境の星で静かに暮らすコミューンに助けられた彼は人々を守るため、軍事演習に訪れた新世代軍に戦いを挑む。キレのあるアクションと逆転劇のカタルシスが噛み合い、小品ながら見ごたえを感じさせる作品に仕上がった。言葉少なに肉体でタフネスを表現した主演のK・ラッセルの熱演に注目。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PELLE EROBREREN", "ペレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "デンマークの文豪M・A・ネクセが著した大河小説『勝利者ペレ』の中の『幼年時代』をデンマークの気鋭監督ビレ・アウグストが映画化した、美しくも厳しい作品。19世紀末のデンマーク。スウェーデンから夢と自由を求めて多くの貧しい人々が着のみ着のままで移住してくる。そんな人々の中に９歳の少年ペレとその父ラッセがいる。二人はやっとのことで“石の農園”の管理人に雇われ、農園の牛小屋の一隅に住居を与えられるが、農園の一日の仕事は厳しく、朝早くから夜遅くまで働かされる。使用人たちはみんな異国の人々で、ペレもしだいに慣れてくる。そんなペレの目に、夢と自由のデンマークでの生活の厳しい現実が次第にはっきりしてくる……。北欧の雄大な自然と四季の移り変わり、そのなかで生活する人々の厳しさ、とくに厳冬の身も凍るような寒さといった自然と生活を少年の目で捉える。今から100年ほど前の人々の生きる姿が生々しく伝わってくる一大叙事詩である。", "1987-01-01", "18,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Death and the Maiden", "死と処女〈おとめ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "嵐の夜、夫ジェラルドの帰りを待っていたポーリナは、パンクして立ち往生して困っていた彼を送ってくれた医師のロベルトの声を聞いて、表情をこわばらせた。かつて反独裁政権の学生運動に参加していた彼女は、誘拐監禁され、シューベルトの『死と処女』が流れる中、ある男に凌辱されるという暗い過去を持っていた。目隠しをされていたので顔を見ることはできなかったが、その声は深い傷となって心に残った。そして、今、その声の持ち主が目の前に現れたのだ……。世界13ヵ国で翻訳されたA・ドーフマンの戯曲を映画化した、R・ポランスキーの心理サスペンス。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ソウル", "ソウル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "逃亡犯を韓国まで護送した帰り道、現金輸送車強奪に遭遇した日本人刑事。犯人を追ったものの、すんでのところで取り逃がし、唯一の目撃者となった彼は72時間だけ滞在を許される。血気盛んなソウル市警の刑事とコンビを組むことになり、衝突しながらも、やがて連続して発生する強奪事件の真相に近づいていく。韓国ロケと「シュリ」のアクション監督ジョン・ドゥホンの起用によって、膨大な火薬量による銃撃戦が展開。日本映画とは思えぬ“痛い”音響は迫力満点。また、これが映画デビューとなったTOKIOの長瀬智也が、肩ひじ張らない演技で、成り行き上懸命になる主人公を熱演している。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獨臂拳王大戦血滴子／ONE　ARMED　BOXER　VS．　FLYING　GUILLOTINE", "片腕カンフー対空とぶギロチン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ブルース・リー以前のカンフー・スター、J・ウォング。彼の当たり役となった、片腕の武術家の活躍を描くアクション「片腕ドラゴン」の続編。清朝時代、愛国者である片腕の拳士、兪は革命のために清朝派の拳士を倒す。その復讐に立ち上がったのが、鎖につながれた刀付きの鉄球を操る盲目の師匠だった。強力な武器に対抗すべく、兪は秘策を練り……。タランティーノが「キル・ビル」でオマージュを捧げたことから、再評価の波が高まった。", "1975-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Michael Clayton", "フィクサー", "", "", "http://image.eiga.k-img.com/images/movie/34265/315x210.jpg?3226", 120, "ニューヨーク最大の法律事務所に勤めるマイケル・クレイトン（クルーニー）は、事務所のボス直属の部下で、最も汚い仕事を担当するフィクサー（＝もみ消し屋）。ある日、事務所内最高の敏腕弁護士が巨額の企業訴訟問題の係争中に相手側に寝返ってしまい、法廷内で問題を起こす。いつものように問題の“もみ消し”にかかるマイケルだったが、事の真相を知り、人生の岐路に立たされる……。第80回アカデミー賞では作品賞を含む7部門にノミネートされ、ティルダ・スウィントンが助演女優賞を受賞した。", "2008-04-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tuya's Marriage", "トゥヤーの結婚", "", "", "http://image.eiga.k-img.com/images/movie/53275/315x210.jpg?2151", 96, "草原の砂漠化が進む中国の内モンゴル自治区。ダイナマイト事故で下半身不随になってしまった夫バータルと2人の子供を抱えるトゥヤーは、朝から晩まで過酷な労働に追われていた。自分がトゥヤーの重荷になっていると感じたバータルは、彼女に離婚を提案。トゥヤーもそれに同意するが、彼女の条件は、自分や子供だけではなくバータルも家族として受け入れてくれる再婚相手を探すことだった……。07年度ベルリン国際映画祭グランプリ受賞作。", "2008-02-23", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PATHFINDER", "ホワイトウイザード 極光伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "古代ラップ伝説に基づき、ノルウェー映画界が総力を結集して製作したスペクタクル大作。1000年も前のノルウェーの最北端、ラップランド地方に住むラップ族の少年が、部落の人々を救うべく、恐るべき侵入者チュート族を相手にして勇敢に戦い抜いていく姿を、壮大な大自然を背景に語っていく。", "1987-01-01", "36", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cradle Will Rock", "クレイドル・ウィル・ロック", "", "", "http://image.eiga.k-img.com/images/movie/1345/315x210.jpg?4323", 134, "1930年代、大恐慌がアメリカ全土を襲った時代。22歳のオーソン・ウェルズは“権力に立ち向かう表現の自由”が主題の舞台劇「ゆりかごは揺れる」の演出を担当することになる。政府が支援するこの公演で見事主役を射止めたのは、女優を夢見る貧しい少女オリーブ。公演に関わる様々な人々の思惑が入り乱れる中、稽古は続いた。しかし政府は「非米的な演劇を広めている」ことを理由にこの公演の中止を決定する。", "2000-10-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("早春二月／EARLY SPRING IN FEBRUARY", "早春二月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "柔石の小説『二月』を原作にした、一人の男の生き様を描く人間ドラマ。中国で'19年に起こった“五四運動”直後の時代が背景で、舞台は芙蓉鎮。'87年の傑作「芙蓉鎮」の文化大革命時代の話へとつながっていくような物語だ。封建的思想が依然こびりついている中国で、高邁な志を持った青年がある町に教師として赴任してきた。やがてその町で友人の妹と恋に落ちる。", "1963-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠る男", "眠る男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "群馬県が人口200万人突破を記念して製作した静ひつな人間ドラマ。同県出身の小栗康平が監督に当たっている。韓国のアン・ソンギ、インドネシアのクリスティン・ハキムと国際的なキャスティングも話題を呼んだ。温泉“月の湯”の湧く山間の町・一筋町。その農家に、山から落ちて意識不明になった“眠る男”拓次がいた。同級生の上村は言葉を返すこともない拓次に語りかけるうち、山奥でひっそり暮らしていた老夫婦のことを思い出す。スナックで働くティアは、町になじむにつれ、少しずつ拓次のことを知っていく。やがて訪れる拓次の死。神社で能が演じられた日、ティアは山へと分け入る。空が白む頃、彼女と上村との出会いが待っていた。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忠臣蔵 花の巻・雪の巻", "忠臣蔵 花の巻・雪の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 233, "大曽根辰夫の正攻法の演出による“松竹オールスター版”。大石内蔵助を松本幸四郎（８代目）が、浅野内匠頭を高田浩吉が演じ、ともに熱の入った演技をみせる。本懐を遂げた赤穂四十七士が、主君の墓に参るラストの名調子は圧巻。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・フーテンの寅", "男はつらいよ・フーテンの寅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "森崎東監督による一編。旅先での寅次郎を中心にしている点が山田洋次のものと少々異なる。湯の山温泉の旅館で番頭をつとめるハメになった寅さんは、おかみに想いを寄せるが例によって片想い。シリーズの中では異色作だが、森崎＝山田両者がミックスした味わいはなかなかのもの。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЗАВТРА БЫЛА ВОЙНА", "翌日戦争が始まった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ソビエトの地方都市に住む16歳の少女イスクラ。仲良し３人組ジーナ、ビーカとともに、楽しい学校生活を送っていた彼女だったが、スターリンの粛清が、少女たちの幸せな日々を奪い去ってしまう。新人監督Y・カラーシクが、ソ連暗黒の時代に青春を過ごした人々へ、哀惜を込めて綴る青春レクイエム。", "1987-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本暴力団・組長くずれ", "日本暴力団・組長くずれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "新人の高桑信が監督。大阪の広域暴力団・城西会は、東京への進出の足掛かりとして横浜に神奈川興業という芸能会社を設立。幹部の渋沢を送り込み、クラブやバーの経営者を次々に傘下に収めていく。しかし、寺島徹だけは彼らの軍門に下らなかった……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鼓書芸人／THE STREET PLAYERS", "鼓書芸人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "一人が弦楽器で伴奏し、一人が太鼓を打ちながら物語を歌う、という二人組の大衆芸“鼓書”。日本の侵略時代だった1938～45年にかけての重慶を舞台に、その鼓書芸人として生きる父親と娘の悲哀の生涯を描いた作品。藍色の色調、終始霧に包まれた重慶の街など、画面から醸し出す独特の重圧感は、チュアンチュアン作品ならではだ。", "1988-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹下左膳 飛燕居合斬り", "丹下左膳 飛燕居合斬り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "林不忘の原作を五社英雄監督・中村錦之助主演で映画化したアクション時代劇。将軍・徳川吉宗の時世、黄金百万両が隠された“こけ猿の壷”をめぐって柳生一族、将軍の側近、愚楽老人一味そして丹下左膳が激しい争奪戦を繰り広げる。豪快なアクション時代劇では定評のある五社英雄監督は、随所に見せ場を盛り込んで楽しませてくれるが、中村錦之助の丹下左膳は力演ながらミス・キャスト気味であった。林不忘創作による隻眼隻手の怪人・丹下左膳は、大河内伝次郎の一世一代の当たり役としてあまりに有名だが、戦後は大友柳太朗の豪快無比な丹下左膳が当たり役となりシリーズ化された。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バッシング", "バッシング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "2005年のカンヌ国際映画祭でも大きな反響を呼んだ社会派ドラマ。2004年のイラクで起きた“日本人人質事件”をテーマに、周囲からバッシングを受ける女性の孤独と決別を描く。中東の戦時国でボランティア活動中、武装勢力に拉致された有子。事なきを得て解放された彼女だったが、帰国後、世間からの誹謗中傷にさらされる。その冷たい仕打ちは家族にも及び……。", "2006-06-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("父ありき", "父ありき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "妻を失い、男手一つで息子を育ててきた男と、その父の平凡ではあるが、誠実な生き方を心から尊敬する息子の成長を追った物語。信州の地方都市の中学教師は、修学旅行先で生徒を一人溺死させてしまい、責任を感じて辞職。二人暮らしの息子を中学の寄宿舎に預けて一人上京する。やがて息子は成長し大学を出て、父親と同じように仙台で教員となる。父子は時々、温泉に出かけては再会を楽しんでいる。息子は、自分も東京に転職して一緒に暮らしたいと思うようになるが、父親に今の仕事を大事にしろと悟される……。父子で釣りをする有名なシーンをはじめ、小津を語る時に必ず引き合いに出される名作。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("邊走邊唱", "人生は琴の弦のように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "中国に古来からある三弦琴の旅芸人一座。“神さま”と呼ばれる盲目の老師は、琴の弦を千本弾き切れば目が見えるようになる、という先代の教えに忠実に修行の日々を重ねていた。その盲目の弟子、シートウは師匠の教えに背き、立ち寄った村の娘・蘭秀と恋に落ちてしまう……。旅芸人と彼らを取り巻く村人の姿をとおして、生きることの意味、希望を持つことの尊さを、琴の美しい旋律とともに問いかける。黄河流域の広大な大自然を舞台に名匠チェン・カイコーが描く壮大な叙情ロマン。", "1991-01-01", "8,76,7,2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In Search of the Castaways", "難破船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ジュール・ベルヌの最高傑作「海底二万里」「神秘の島」と並ぶ3部作の第1部である長編冒険小説「グラント船長の子供たち」の映画化作品。遭難したまま行方不明になっていたグラント船長からの手紙が発見された。船長の2人の子供たちと地理学者パガネルは、貴族グレナバンの船で船長を探す旅に出る。彼らは大地震や大洪水を乗り越えて、ある島にたどり着くが……。監督は「キング・ソロモン」の名匠ロバート・スティーブンソン。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スリーデイボーイズ", "スリーデイボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/54645/315x210.jpg?2959", 90, "第9回インディーズムービー・フェスティバルでグランプリを獲得した夏目大一朗監督の劇場用長編第1作。人里離れた山奥の寺から、3人の修行僧が脱走を図った。寺の跡取り息子である知朗、ラッパーを夢見る清遂、そして女性修行僧の有子だ。追手を振り切ろうと必死で逃げる彼らに、次から次へと災難が降りかかる。実は彼らの脱走の裏には、それぞれ深い事情があり……。出演は「デスノート」シリーズの清水伸、AKB48の宮澤佐江ほか。", "2009-07-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRIME LORDS", "クライムロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "中堅刑事ラグレンジは親友の息子である若手刑事ルソーとパートナーになった。さっそく二人は国際的犯罪組織を追うことになる。黒幕は中国系の老人リン。L.A.でリンを逃した二人は、リンの本拠地、香港へ向かう。ラグレンジ役で出演のW・クロフォード監督は多くの作品で出演・製作・脚本を手掛ける人物。SFXファンタジー・アカデミー賞の金のリボン賞をはじめとする数々の受賞歴をもつ。", "1989-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUN　SILENT， RUN　DEEP", "深く静かに潜航せよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "巨匠R・ワイズが、潜水艦VS駆逐艦の緊迫感あふれる戦闘シーンを盛り込んで仕上げた戦争映画の佳作。日本軍の駆逐艦に敗北を喫し、からくも生き延びた軍人リチャードソン。新たな潜水艦を与えられ、宿敵との再戦に燃える彼の苦闘が、それに反目する部下たちとの人間模様を絡めて描かれる。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REST IN PEACES", "悪霊伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "突然の伯母の死で、スペインにある不思議な屋敷へ夫を伴ってやって来たヘレン。しかし、その屋敷に住んでいる人々は世にも恐ろしいゾンビだった……。「風と共に散る」でアカデミー助演女優賞に輝いたD・マローンが伯母役で出演。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wir sind die Nacht", "ブラッディ・パーティ", "", "", "http://image.eiga.k-img.com/images/movie/57225/315x210.jpg?2275", 100, "男の血をすすって生きる女バンパイア集団の破滅と再生を描くドイツ発のスリラー。ベルリンで盗みを働きながら生きてきたレナは、ある日、ナイトクラブで出会ったルイーズに首筋をかまれバンパイアになってしまう。鮮血を欲し、太陽の下で暮らせない体に当初は戸惑うレアだったが、以前とは比べ物にならないほど美しくなった自分の姿に次第に陶酔していく。ルイーズと仲間のシャルロッテ、ノラとともに夜な夜なパーティ三昧の日々を送るレアだったが、そこへレアを追っていた潜入捜査官が現れ……。主演は「パフューム　ある人殺しの物語」のカロリーネ・ヘルフルト。", "2011-11-26", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST TYCOON", "ラスト・タイクーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "フィッツジェラルドの未完の遺作の映画化。ハリウッドきっての若手プロデューサーが亡妻そっくりの小娘に一目惚れするが、彼女には婚約者がいた。一方、彼の独走を嫌う撮影所長によって、彼はプロデューサーの座を追われてしまう……。1930年代のハリウッドの内幕をノスタルジックに綴ったドラマ。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CRISE", "女と男の危機", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "幸福に暮らす敏腕弁護士ヴィクトルを突然襲った危機。目覚めると妻は家出しており、会社からは一方的な解雇通知が。途方に暮れる彼は友人に救いを求めるが、彼らもまたヴィクトル以上に深刻な悩みを抱えていた……。女流監督C・セローが、現代人の苦悩を華やかに綴ったコメディ。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新男の紋章・若親分誕生", "新男の紋章・若親分誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "“男の紋章”シリーズで好評を博した高橋英樹の新シリーズ化を狙った第１作だが、興行的にふるわずこれ１作で終わった。政党の大物の書生・政吉は、主人を殺され自分の非力さを悟り、ヤクザ修行の旅に出て苦しい修行の末、一人前のヤクザとなって帰ってくる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　SLINGSHOT", "イノセント・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "10歳の少年の性の目覚めをノスタルジックに描き、本国のアカデミー賞の最優秀作品賞を受賞した思春期もの。1920年代のストックホルム、社会主義者の父、ユダヤ人の母、ボクサー志願の兄と暮らすローランド。彼は社会主義の集会に出た父が警官隊に暴力で応戦する姿を見たり、母が闇市で売っている違法のコンドームをコブつき風船として子供たちに売ったりと様々な経験をしていく。「マイ・ライフ・アズ・ア・ドッグ」の製作者ベルエンダールと音楽のイスフェルトが参加している。", "1994-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　JUNIPER　TREE", "ビョークの「ネズの木」・グリム童話より", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "舞台は中世アイスランドの片田舎。魔女裁判によって母を失い、方々を彷徨っていた姉妹が、ある父子に出会い、姉はその男の妻となる。しかし、息子の方は母となった姉になじむことなく、妹と心を寄せ合うようになり……。アイスランドの歌姫ビョークが妹役で映画初主演した、残酷かつ哀切、そしてノスタルジックな空気が漂うダーク・ファンタジー。", "1986-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メカゴジラの逆襲", "メカゴジラの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "前作でゴジラに敗れ去ったメカゴジラが、今回は主役のゴジラを押しのけタイトルロールとして復帰。ブラックホールの第３惑星人はメカゴジラを再建造し、さらに新怪獣チタノザウルスを仲間にして逆襲に転じるのだった。メカゴジラの強烈なキャラクターにゴジラは今回も圧倒される。", "1975-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Absent Minded Professor", "うっかり博士の大発明　フラバァ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1997年にロビン・ウィリアムズ主演でリメイクされた「フラバー」のオリジナル作品。大学教授のブレナード博士は、研究に熱中しすぎて自分の結婚式を3回もすっぽかしてしまうほどのうっかり者。そんな彼が研究中に偶然発明した不思議な物体“フラバー”を巡って繰り広げられる騒動を描くファンタジック・コメディ。メガホンを取るのは、「メリー・ポピンズ」等、多くの実写版ディズニー映画を手掛けたロバート・スティーブンソン。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLASS OF NUKE’ EM HIGH", "悪魔の毒々ハイスクール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "核エネルギーの研究所から、放射能に汚染された水が漏出。その水を何も知らずに飲んでしまった高校生数人が、次々と口や鼻からドロドロの液体を吐き出し……。胸がむかつくようなおどろおどろしいシーンが連続のスプラッタ・ホラー。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Chronicles of Riddick", "リディック", "", "", "http://image.eiga.k-img.com/images/movie/1155/315x210.jpg?2282", 115, "本作のデビッド・トゥーヒーが監督・脚本を務めた「ピッチブラック」の登場人物リディックを主人公に、彼の新たな冒険を描くSFアクション大作。賞金稼ぎに追われて旧友の住む星に向かった宇宙最強の脱獄犯リディックは、軍事力で宇宙の支配を目論む集団ネクロモンガーと遭遇する。リディック役は前作と同じヴィン・ディーゼル。ネクロモンガーの副官役で「ロード・オブ・ザ・リング」のエオメル役、カール・アーバンが出演。", "2004-08-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("好き", "好き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "“好き”をテーマに、「チャーシュー麺」「波」「テンカウント」の３話で構成された、オムニバス形式の恋愛ストーリー。1958年の横浜を舞台に、集団就職で上京した少女を描いている。「東京マリーゴールド」の田中麗奈が、時代設定の異なる３人のヒロインを熱演している。ビデオ、DVDタイトルは「クリックシネマ　好き」。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Meat", "肉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋人たちの時刻", "恋人たちの時刻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「野菊の墓」「Wの悲劇」など少女を描くことでは定評のある澤井信一郎監督の作品。河合美智子と野村宏伸を主演に、愛にすさんだ現代の恋人たちの姿を描く。北海道の海がもの悲しく、大貫妙子の歌う主題歌も切ない。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白い野望", "白い野望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "主任看護婦との愛のもつれ、色仕掛けで近づく製薬会社のセールス・レディ、病院内での激烈な権力争い——才能と野望にあふれるエリート脳外科医を主人公に、彼を取り巻く様々な人間模様を描いた社会派サスペンス。医学界の内実もチラリと垣間見られる。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿弥陀堂だより", "阿弥陀堂だより", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "黒澤明の下で助監督等を務めていた小泉堯史が、高い評価を受けた初監督作「雨あがる」に続き撮り上げたヒューマン・ドラマ。パニック障害という心の病に冒された女医、美智子が、夫の故郷である信州の山村に夫婦で移り住む。自然に囲まれたシンプルな暮らし、素朴な村人との交流によって和らいでいく夫婦の心の軌跡を、繊細に綴っていく。約一年間に及ぶ撮影によって映し出されるのは、光と緑にあふれる、四季折々の懐かしい大自然の風景。その美しさは見事なもので、まるで心が洗われるような感覚になる。人間の本来持っている“善”の側面をしみじみと味わえる良心作。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モーニング・ムーンは粗雑に", "モーニング・ムーンは粗雑に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "港ヨコハマを舞台に、音楽スタジオで偶然知り合った学生歌手と、ミュージシャンの青年が織りなす24時間のドラマを淡々とスケッチし、サザンオールスターズの音楽で彩った桑田佳祐の企画による青春映画。映画館系列ではなく、各地の演奏会ホールをネットワークにした新しい方式で上映された。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太と雲の王国", "ドラえもん のび太と雲の王国", "", "", "http://ecx.images-amazon.com/images/I/51AAVPGEH8L._SL210_.jpg", 100, "天国は雲の上にある、と信じるのび太のため、ドラえもんが雲の上の王国作りを提案する。やがて夢の国が完成するが、のび太たちは他に“天上界”という世界があることを知る。しかし、そこに住む天上人は、環境を破壊し続ける地上の人間に対して罰を与えようとしていた。感電して故障してしまったドラえもん、捕まったジャイアンたち。たった一人残されたのび太の運命は？", "1992-03-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("哀憑歌 GUN-KYU", "哀憑歌 GUN-KYU", "", "", "http://ecx.images-amazon.com/images/I/51QQ545AKBL._SL210_.jpg", 75, "", "2009-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INDIA", "インディア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "イタリアの巨匠、R・ロッセリーニが大まかな構想だけを持ってインドに渡り、現地で出会った人々や、体験した事件を取り入れながら作っていったドキュメンタリー。象使い、ダム建造者、猿回しの猿などの姿を捉えながら文明と自然のせめぎ合うインドに人間の生と死を寓話的に描き込んだ傑作。", "1958-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BARB WIRE", "バーブ・ワイヤー／ブロンド美女戦記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "『プレイボーイ』のグラビア・ガール出身のP・アンダーソンが、グラマラスな肢体を惜し気もなくさらした痛快活劇。2017年。かつての恋人のため、文字通り一肌脱いだ女賞金稼ぎが派手なガンプレイで無軌道な悪党どもに立ち向かっていく。ラース・フォン・トリアー作品でおなじみの怪優U・キアーが好演。ビデオ発売時タイトル、DVDタイトルは「バーブ・ワイヤー」。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIGHTNING FORCE", "ライトニング・フォース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "特殊コマンド部隊が、ハイテク・ヘリと武器を使ってテロリストに立ち向かうアクション・ムービー。製作総指揮のニューフェルドは「レッド・オクトーバーを追え！」の大ヒットでパラマウントを代表するプロデューサーとなり、R・レーメと組んでハリソン・フォードの「パトリオット・ゲーム」もヒットさせた。続いてH・フォードのトム・クランシー原作“ジャック・ライアン”シリーズを手掛けている。TVフィーチャー作品。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("着信アリFinal", "着信アリFinal", "", "", "http://ecx.images-amazon.com/images/I/21mFeM182jL._SL210_.jpg", 104, "秋元康原作による大ヒットホラーシリーズの最終作。高校の修学旅行で韓国を訪れた草間えみりは、ネットで知りあった韓国の青年アンジヌに会うのを楽しみにする一方で、友人の明日香がイジメを理由に修学旅行に参加しないことを残念に思っていた。やがて、女子生徒の１人が“死の着メロ”を受けとり、ナゾの死を遂げる。クラスメイトたちがパニックに陥る中、送られてきた死の着メロを誰かに転送すれば死の運命から逃れられることがわかり……。", "2006-06-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Boat That Rocked", "パイレーツ・ロック", "", "", "http://image.eiga.k-img.com/images/movie/54705/315x210.jpg?3767", 135, "ビートルズやローリング・ストーンズが人気を博し、ブリティッシュ・ロックが全盛期を迎えた1966年のイギリス。領海外に停泊した船からロックを24時間流し続け、人々に熱狂的に支持された「海賊ラジオ局」を舞台に、ポップ・ミュージックに情熱と愛情を注ぐDJたちと、彼らとともに暮らす人々を描いた群像劇。監督・脚本は「ラブ・アクチュアリー」のリチャード・カーティス。フィリップ・シーモア・ホフマン、リス・エバンスらが個性的なDJに扮する。", "2009-10-24", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAMNSTAD", "愛欲の港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "元船員のヨスタは、港で投身自殺しようとした少女ベリトを救い、後に再会したことから親しく付き合うようになる。二人は恋に落ち、週末に海辺のホテルへ出かけるが、ベリトはそこで感化院時代の友人に会い、堕胎の費用をせがまれる。暗い過去が明るみに出たことで、ヨスタとの恋も終わりを告げたと思い込んだベリトは、彼のもとを去るのだが……。I・ベルイマンの初期作品。シンプルなメロドラマをドキュメンタリー・タッチで描いた意欲作。", "1948-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUTLAW BLUES", "アウトローブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "カントリー好きの囚人が刑務所慰問に来たカントリー＆ウエスタン歌手の前で歌った自作の歌が、出所してみるとそのスターの歌として大ヒットしていた。頭にきた男は歌手の元を訪れるが、誤って歌手を撃ってしまい追われる身となる。P・フォンダの歌が心に残る快作。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フリーター", "フリーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "公開当時、社会問題になっていたフリーターたちの青春を、様々な面白いアルバイトを紹介しながら描いていく群像人間ドラマ。監督は「純」で若者の複雑な心理を描き好評を得た横山博人で、好感のもてる一編になっている。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NORMA RAE", "ノーマ・レイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "無知でだらしのない女工員ノーマ・レイが、紡績組合の活動家の影響で次第に活動にのめり込み、組合結成へと奮闘する姿を描く。主人公が活動を通して自分自身をつかみ、成長していく過程を、フィールドがじっくりと演じきる。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヤンキー烈風隊", "ヤンキー烈風隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "『月刊少年マガジン』連載の同名漫画を映画化したツッパリ青春アクション。伝説の暴走族“烈風隊”を再建し、二代目総長となった男・紋之丞と、ヤクザをバックにしたライバルの族“ブラックツェッペリン”の抗争を描く。脚本は「新・悲しきヒットマン」でデビューした演劇界出身の森岡利行が担当。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Married Life", "あぁ、結婚生活", "", "", "http://image.eiga.k-img.com/images/movie/53735/315x210.jpg?2821", 90, "夫婦の裏側をユーモアを交えて描いたラブ・ストーリー。「カポーティ」「キングダム／見えざる敵」のクリス・クーパー、「007」シリーズのピアース・ブロスナン、「きみに読む物語」「幸せのポートレート」のレイチェル・マクアダムスらが出演。1940年代アメリカ、会社経営者のハリーは長年連れ添った妻に隠れ、ブロンド美女のケイに恋をしていた。ハリーは離婚について親友のリチャードに相談するが、リチャードがケイに一目ぼれしてしまい……。", "2008-09-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Some", "サム SOME", "", "", "http://image.eiga.k-img.com/images/movie/57735/315x210.jpg?4326", 117, "「カル」のチャン・ユニョン監督が、現実とデジャブ（既視感）を交錯させて描く新感覚サスペンス。証拠物件の麻薬が移送中に何者かに奪われ、オ・バンジャン刑事に容疑がかけられる。バンジャンの無実を信じる後輩刑事のカン・ソンジュは真犯人を探すが、疑わしい人間が次々に死亡していく。そんな時、ソンジュは容疑者の友人であるユジンに出会う。初対面のはずの2人はなぜか互いに見覚えがあり、ユジンはソンジュが死亡するデジャブを見る。", "2012-01-21", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤木圭一郎は生きている　激流に生きる男", "赤木圭一郎は生きている　激流に生きる男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 46, "トニーこと赤木圭一郎は「激流に生きる男」の撮影中の昭和36年２月14日休憩中に撮影所内でゴーカートを運転し、ハンドルを切りそこねコンクリート塀に激突して入院。１週間後の21日、21才の若さで死亡した。この作品は、吉田憲二の監修によって、トニーの生前撮影された未完成のフィルムと故人ゆかりの人々の座談会の２部構成になっている。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOCK OF THE BUSHVELD", "サバンナからの風 冒犬ジョック物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "19世紀末、南フランスのトランスバール地方に、金鉱を求めて多くの若者たちが押し寄せた。そのなかの一人、パーシィは採掘現場で生活用品を運搬する仕事についていたが、ある日醜い仔犬と出会う。やがて仔犬は勇敢で頼りがいのある成犬となる。ともに苦境を乗り越えていく、人間と動物の姿を描く実話の映画化。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bustin' Down the Door", "バスティン・ダウン・ザ・ドア", "", "", "http://image.eiga.k-img.com/images/movie/54625/315x210.jpg?4092", 96, "1970年代のハワイ・オアフ島ノースショアを舞台に、プロサーファーという職業を初めて確立した男たちの軌跡を辿ったドキュメンタリー。南アフリカやオーストラリアから夢を追ってノースショアへとやって来た6人の若者たちが、地元サーファーたちとの抗争を乗り越えてサーフィン界に革命を起こすまでの闘いを、当事者たちの証言や貴重な映像とともに振り返る。ナレーションを担当するのは実力派俳優エドワード・ノートン。", "2009-08-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Untouchables: Capone Rising", "アンタッチャブル　カポネ・ライジング（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHINE", "シャイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "オーストラリア出身の実在の天才ピアニスト、デビッド・ヘルフゴットの半生をモデルに、苦難を乗り越え演奏家として再起するまでを描いた感動作。幼年時代から天才ピアニストとしての才能を発揮したデビッド。父親から過剰な愛を受けた彼だが、父親の反対を押し切りロンドンへの留学を果たす。教授にも恵まれ順調に見えたが、繊細な神経ゆえ神経に異常をきたしてしまう。10数年後、精神病院を出た彼は、ワイン・バーで演奏を開始する……。", "1995-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rocky", "ロッキー", "", "", "http://ecx.images-amazon.com/images/I/516O48FdAsL._SL210_.jpg", 120, "　青年ロッキーはフィラデルフィアのスラム街に暮らす4回戦ボクサー。ある時、世界チャンピオンのアポロがロッキーを対戦相手に指名してきた。もし15ラウンド終わってもリングに立っていることができたら、自分がただの三流ボクサーでないことを証明できる……ロッキーは愛する女性エイドリアンのために死闘を繰り広げることに！　無名だったスタローンは主人公さながらのアメリカン・ドリームを成し遂げ、この一作で大スターに。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TVO", "TVO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "他人の心を読み取る超能力を持つ少女・五月は、姉の死にまつわる謎を追って、姉の恋人だったクラブ歌手の孝に近づく。しかし、そこで姉の意外な素顔を知った五月は……。「ノット・サティスファイド」などの自主映画で注目された太田達也の商業映画デビュー作で、透明感あふれる映像が魅力的である。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奇々怪々俺は誰だ!?", "奇々怪々俺は誰だ!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "自分が自分であることを否定されてしまうことから起こる悲喜劇を描く、ユニークな作品。平凡な会社員・鈴木太郎は、ある日突然、もう一人の鈴木太郎が現れたことで、会社の同僚ばかりでなく妻子からも、自分の存在を否定されてしまう。家に帰るともう一人の太郎がおり、故郷に帰ればもう一人の太郎の写真が飾ってある。一体俺は誰なんだっ!?　というお話。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　TRUE　STORY　OF　JESSE　JAMES", "無法の王者　ジェシイ・ジェイムス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "西部開拓史に名を残す実在の大強盗ジェシィ・ジェイムス。何度も映画化されてきた彼の波乱の半生を、「理由なき反抗」「北京の55日」などのベテラン、N・レイが豪華キャストで描くウエスタン。ジェシーが悪の道に足を踏み入れ、壮絶な死を遂げるまでを回想を交えて綴る。ジェシーをR・ワグナーが好演。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千姫御殿", "千姫御殿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "豊臣家が滅亡した大阪城落城の折、助け出された家康の孫・千姫の後半生を描く悲恋物語。本多家へ再嫁した千姫は吉田御殿にこもり遊興にふけっていたが、招き入れられた男は翌日死体となって池に浮かぶのだった。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONSIEUR　IBRAHIM　ET　LES　FLEURS　DU　CORAN", "イブラヒムおじさんと　コーランの花たち", "", "", "http://ecx.images-amazon.com/images/I/41YF6RXM76L._SL210_.jpg", 95, "ヴェネチア映画祭をはじめ、世界各国で話題を呼んだヒューマン・ドラマ。パリの裏町を舞台に、孤独なユダヤ人少年とトルコ移民の老人の、人種も年齢差も越えた心の交流が綴られる。どこか「ニュー・シネマ・パラダイス」を彷彿とさせる物語は、人生における出会いと別れを丹念に描写。何ものにも代えがたい時間を共有する二人の姿が、じんわりと温かい感動を運ぶ。心優しき老人イブラヒムに扮する「アラビアのロレンス」の名優、O・シャリフの熟練の演技も味わい深い。", "2004-11-20", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GEDO ： THE　FATAL　BLADE", "GEDO／外道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "勢力拡大のためにL.A.へ赴いた末松組の若頭・堂本は、L.A.を仕切るビッグ・ボス、ブロンソンの襲撃に失敗して逮捕されるが、連行中に出会ったサエミと逃亡を図る。ヤクザ、マフィア、警察の三つ巴の闘いを描いたハード・アクション。日米混合のキャスティングの妙も楽しめる。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWITCHBACK", "スイッチバック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「ダイ・ハード」「逃亡者」などの脚本家J・スチュアートが監督デビューを飾ったサスペンス・アクション。逃亡中のシリアル・キラーによって愛する息子を誘拐されてしまったFBI捜査官フランク。上層部の警告を無視して執念の捜査を続ける彼は、西部の田舎町でついに犯人の手がかりを見つけていく。まったく派手さはないが、素性を隠してひょうひょうと西部の日常に溶け込んだ犯人のキャラクター描写が興味をそそる一編。主人公と犯人の行動をそれぞれ丹念に追ったストーリー展開にも、何とも渋い味わいがある。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blood Red", "ブラッド・レッド 復讐の銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "J・ロバーツの映画デビュー作。主演は、彼女の実兄E・ロバーツ。1895年のカリフォルニアにイタリア・シシリーから移民してきたコロジェロー家が、土地の権利をめぐって町の実力者と対立。コロジェロは殺され、長男のマルコは復讐を誓うが……。強欲で残忍な町の実力者を、D・ホッパーが演じる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kes", "ケス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "1960年代前半のイギリス、ヨークシャー地方を舞台に、労働者階級の少年とハヤブサの交流を、厳しさとあたたかさの同居する繊細なタッチで描いた、イギリスの名匠ケン・ローチ監督初期の傑作。母と兄と3人で暮らす15歳の少年ビリーは、ある日見つけたハヤブサの巣からヒナを持ち帰り、ケスと名付けて飼い始める。学校では教師からも同級生からもひどい扱いを受ける彼は、ケスと過ごす時間だけが唯一の楽しみとなっていく……。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霧笛が俺を呼んでいる", "霧笛が俺を呼んでいる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "赤木圭一郎が歌う主題歌がヒットしたムーディーな日活アクション映画。下敷きはイギリス映画「第三の男」であるが、舞台を霧に包まれた港町に設定することで、独特の雰囲気を醸し出している。冒頭からラストシーンに至るまで、この霧と霧笛のイメージは繰り返し画面に登場し、圭一郎とヒロイン・芦川いづみの淡い恋とミステリアスな事件の重要な道具立てとなっている。船のエンジン故障で陸に上がることになった航海士・杉は旧友の浜崎を訪ねて、彼が２週間前に突堤で溺死体で発見されたことを知る……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREEN BERETS", "グリーン・ベレー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "ベトナム戦時下。“グリーン・ベレー”の異名で名高いアメリカ特殊部隊のベトナム・ダナン地区での活躍を描く戦争映画。R・ムーアの同名ベストセラー小説を、J・ウェインのパトジャック・プロが製作。タカ派映画といわれ話題となった。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しずかなあやしい午後に", "しずかなあやしい午後に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "椎名誠が企画した短編３本によるオムニバス映画。第１話は「ガクの絵本」（30分）。男とカヌー犬のガクがキャンプをしている所へ、突然、暗闇から若い女が現れ、男を連れ去り、ガクは取り残されてしまう。カヌーイストの野田知佑と、その飼い犬でカヌー犬が出演する「ガクの冒険」の続編的作品で、和田誠が監督している。第２話は椎名自身が監督し、自作『地下生活者』と林政明の『林さんチャーハンの秘密』を映画化した「遠灘鮫腹海岸」（30分）。海岸に軽ワゴン車が入ってくるが、砂に車輪を取られて、動けなくなる。辺りに人影もなく、あきらめた男はリュックから中華鍋とご飯を取り出し、チャーハンを作り出す。さあ、食べようとして、ふと顔を上げると……。第３話はイラストレーター・沢野ひとしの絵をアニメ化した「スイカを買った」（白黒・11分）。ある夏の暑い午後、スイカを下げたワニ眼くんが列車に乗って、海辺の村に帰っていく。グラフィックデザイナーの太田和彦が監督している。DVDは「ホネ・フィルム式活動寫眞全記録　椎名誠　DVD-BOX」に収録。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カードキャプターさくら", "カードキャプターさくら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "魔力を秘めたカードを集めるためにバトルを展開する、小学生の少女さくらの活躍を描きTVアニメにもなった人気漫画の劇場版。商店街の福引で香港旅行を引き当てたさくらは兄や友人たちとともに香港へと旅立つ。華やかな町並みにはしゃぐさくらだったが、夜、不思議な夢を見る……。過去より連綿と続く魔力の街、香港を舞台に、さくらが驚くべき魔物と戦いを繰り広げていく。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUR　ME｀S　LEVRES", "リード・マイ・リップス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "2002年のセザール賞で最有力の「アメリ」のオドレイ・トトゥを抑えて、主演女優賞に輝いたのが主人公カルマ役のエマニュエル・ドゥヴォス。難聴を患う地味なOL、カルマと刑務所あがりのチンピラ。なんの接点も共通項も持たないが、ともに孤独に苛まれた男と女が出会う。女の難聴ゆえの特技“読唇術”が二人を強く結びつけ、スリリングな体験によって地味な女が輝き始める。", "2001-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　LETTER　TO　TRUE", "トゥルーへの手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "9.11テロ事件以後の世界情勢に懸念を抱く写真家、ブルース・ウェバーが撮った映像詩。トゥルーというゴールデン・レトリバーの愛犬に宛てて手紙を書くという形で、ウェバーの個人的な思い出などの多彩なエピソードが紡がれる。犬たちが浜辺で戯れるショットの美しさに心癒される一編だ。9.11テロ発生時に、旅先で自宅の犬たちの安否が心配になったウェバー。それをきっかけに彼は愛犬トゥルーへの手紙を書き始める。", "2005-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The International", "ザ・バンク　堕ちた巨像", "", "", "http://image.eiga.k-img.com/images/movie/53615/315x210.jpg?2936", 117, "「ラン・ローラ・ラン」「パフューム／ある人殺しの物語」のトム・ティクバ監督によるアクションスリラー。世界中から莫大な資金が集まる欧州の巨大銀行IBBCに、ある違法行為の疑惑が持ち上がる。インターポール捜査官サリンジャーとニューヨーク検事局のホイットマン検事補は共同で捜査に乗り出すが、手がかりとなる人物が次々に暗殺されてしまう……。出演はクライブ・オーウェン、ナオミ・ワッツ、アーミン・ミューラー＝スタール。", "2009-04-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SILENT PARTNER", "サイレント・パートナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「L.A.コンフィデンシャル」などの名匠C・ハンソン脚本によるひねりの効いたサスペンス・ドラマ。銀行強盗を利用して私腹を肥やした銀行員が、その強盗から血も凍るような脅迫を受け、そして次々と陰湿な事件が起きる……。", "1978-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オリヲン座からの招待状", "オリヲン座からの招待状", "", "", "http://image.eiga.k-img.com/images/movie/34295/315x210.jpg?1667", 116, "人気作家・浅田次郎による短編集「鉄道員」の最終話として所収された同名作を、「たそがれ清兵衛」の宮沢りえと「それでもボクはやってない」の加瀬亮主演で映画化。昭和30年代、京都の映画館・オリヲン座の館主である松蔵が病に倒れ、やがてこの世を去る。松蔵の妻トヨは、彼の弟子であった留吉と共に映画館を引き継ぐことを決意。映画産業が傾きはじめる中、貧しさに耐えながらも必死に映画館を守り抜こうとする2人だったが……。", "2007-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("47 Ronin", "47RONIN", "", "", "http://image.eiga.k-img.com/images/movie/56095/315x210.jpg?2535", 0, "日本の「忠臣蔵」をモチーフに、キアヌ・リーブス主演、新鋭カール・リンシュ監督で描く3Dファンタジーアクション。はぐれ者のカイ（リーブス）は、主君を殺した吉良（浅野忠信）に復しゅうを誓う大石（真田広之）に力を貸すこととなる。カイと大石率いる47人の浪人たちは、仇討ちのためともに数々の戦いや試練を乗り越えていく。真田、浅野のほか、本作でハリウッドデビューの柴咲コウ、赤西仁、「バベル」の菊地凛子が参加する。", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Godsend", "アダム　神の使い　悪魔の子", "", "", "http://image.eiga.k-img.com/images/movie/33705/315x210.jpg?4694", 102, "クローン人間を題材にしたサイコ・スリラー。8歳になったばかりの息子を事故で失ったダンカン夫妻。悲しみに暮れる彼らの前に現われた遺伝子学者ウェルズは、クローン技術を用いてアダムを再生させる計画を持ちかける。夫妻は迷いながらも提案を受け入れ、新たに誕生したアダムと共に幸せな生活を取り戻したかに見えた。しかし8歳の誕生日を迎えた頃からアダムに異変が起こり始める。名優ロバート・デ・ニーロが遺伝子学者ウェルズを怪演。", "2006-10-07", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A｀ NOS AMOURS", "愛の記念に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "シュザンヌは16歳の高校生。好きなリュックがいながら、彼女は愛を求めるように次々と新しい恋人を作っていく。そんな彼女がもとで、両親の不和は大きくなり、家族は崩壊の危機にさらされる……。思春期の娘に見られる夢と不安をいきいきと描いた青春映画。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In the Bedroom", "イン・ザ・ベッドルーム", "", "", "http://image.eiga.k-img.com/images/movie/1285/315x210.jpg?4723", 131, "小さな町の開業医マットとニース夫妻の息子フランクは、近所に住む年上の女性ナタリーと恋に落ちるが、彼女は暴力をふるう夫リチャードと離婚交渉中だった。ある日、リチャードと遭遇したフランクは、争いの中で銃殺されてしまう。息子を失った絶望を経て、マットとニースはあることを決意する。アカデミー主要5部門ノミネート、シシー・スペイセクがゴールデン・グローブ賞ほかで主演女優賞を受賞。監督トッド・フィールドは本作がデビュー作。", "2002-08-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2010-2011 R.シュトラウス「カプリッチョ」", "METライブビューイング2010-2011 R.シュトラウス「カプリッチョ」", "", "", "http://image.eiga.k-img.com/images/movie/56475/315x210.jpg?1272", 166, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を劇場のスクリーンで上映する「METライブビューイング」2010～11年シーズンの10作目で、リヒャルト・シュトラウスが生涯で最後に作曲したオペラ。詩人のオリビエと音楽家のフラマンは、若くして夫を亡くした伯爵夫人にそろって恋をしていた。2人は夫人がどちらを選ぶかを話すうちに、「言葉」と「音楽」はどちらが重要かの議論に発展。やがて2人はそれぞれに愛を告白するが、夫人は「皆でオペラを作ってほしい」と提案する。", "2011-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("英雄本色Ⅲ 夕陽之歌", "アゲイン／明日への誓い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "香港ノワールの傑作「男たちの挽歌シリーズ」の第3作。前2作の監督ジョン・ウーからバトンタッチ、ベトナム戦争末期のサイゴンを舞台に、運命の糸に繰られる三人の男女の愛と死を描くシリーズ完結編。第1作で壮絶な死を遂げたマークの若き日に焦点をあて、戦火のサイゴンに燃える恋を、男同士の友情と戦いを織り交ぜて描きあげる。チョウ・ユンファの相手役に今は亡き香港のトップスター、アニタ・ムイ、敵のボス役に時任三郎が扮するのも見どころ。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MEN’S CLUB", "メンズクラブ ～真夜中の情事～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "“女”を話のネタに集まってきた７人の男たち。お互いそれほど顔見知りでもないのに、酔いにまかせて自らの女性体験を語っていく。男だけの宴は佳境を迎え、次に行き着く先は秘密クラブ。扉を開くとそこには男たちの欲望を満たすべく官能の夜が待っている。クセのある役者をズラリとそろえて、舞台劇の趣もある個性的な作品。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAS DEUTSCHE KETTENSAGEN MASSAKER", "ドイツチェーンソー 大量虐殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "ドイツ映画界の問題児C・シュリンゲンズィーフが手掛けた、「悪魔のいけにえ」を思わせるバイオレンス・ホラー。東西ドイツの統一後、多くの東独人が西側へ流入。だが、これに目をつけた西独の食肉工場一家が、東側から来た人々を次々と殺害してはソーセージの材料にしていた……。汚らしいまでに血と臓物にまみれた映像が、なんともショッキング。", "1990-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("肉布団", "肉布団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "田舎の牧場で働く青年二人組がガールハントをしようと上京、だが悪い女にだまされたり、サド趣味のママに犯されたり、ゲイにひっかかったり、あげくにお金まで盗まれたりとさんざんな目にあって……。大胆な演出で評判をとった向井寛監督のコメディ・タッチのピンク映画。青年役の久保新二が味を出す。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("徳川一族の崩壊", "徳川一族の崩壊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "300年にわたる徳川政権が終焉していくさまを、会津藩主・松平容保と長州の桂小五郎との対立を軸に据えて描く大型時代劇。薩長同盟、“蛤御門の変”など実際の事件を追いながら、東映ならではの娯楽性を重視した作りになっている。物語は、松平容保が京都守護職に就任し、朝廷を懐柔しようとするがそれが実らず、徳川最後の将軍・慶喜は大政奉還。容保は会津藩を率いて、勝利のない戦いに向かっていくというもの。全編これ、チャンバラ映画の醍醐味ともいえる派手な剣戟シーンが見どころ。それに加えて容保や桂の家族との絡みなどに、山下耕作一流の情感あふれるタッチが見られ、面白さは十分。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の紋章・風雲双ツ竜", "男の紋章・風雲双ツ竜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "竜次は父の仇を殺したあと、自責の念にかられて寺にこもるが、母の率いる村田組の危機を知り寺を出て悪玉を倒す。４年ぶりにメスを持った竜次は射たれて傷ついた母の手術を行う。この作品によって主人公と母との確執は決着を見る。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BE´BERT ET L’OMNIBUS", "わんぱく旋風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「わんぱく戦争」で評判を取ったY・ロベール監督が、前作と同じキャスト＆スタッフで撮った痛快な一編。郊外からパリのデパートへ買い物にやって来たベベールたち一家。ところがイタズラばかりしていたベベールが迷子になってしまう。随所に見られるユーモアやアナーキスティックなギャグが楽しい。また、ブリジット・フォンテーヌらとのコラボレーションで知られるシャンソン歌手、ジャック・イジュランが長男、チーノ役で顔を見せている。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("化石の森", "化石の森", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "石原慎太郎の同名小説を篠田正浩が映画化。大学病院のインターン・治夫が、ふとしたことから英子という女性と出会い、殺人や不倫など、ドロドロした人間関係のもつれの中に堕ちていく様を描いている。篠田作品として見れば、物語を整理するのが精いっぱいで、本来の映像センスや、演出のキレがないのが残念。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'heure D'ete", "夏時間の庭", "", "", "http://image.eiga.k-img.com/images/movie/54365/315x210.jpg?3721", 102, "画家だった大叔父のアトリエに1人で暮らしていた母が突然亡くなり、その3人の子供たちにパリ郊外の広大な屋敷と庭、貴重な美術品コレクションが遺産として残される。3人は遺産の相続処理を進める中で、思い出の詰まった家への愛着と、経済的に厳しい現実とのジレンマに向き合うことになる。出演はジュリエット・ビノシュ、シャルル・ベルリング、ジェレミー・レニエ。監督は「イルマ・ヴェップ」のオリビエ・アサイヤス。", "2009-05-16", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Unfaithful", "運命の女", "", "", "http://image.eiga.k-img.com/images/movie/1725/315x210.jpg?4775", 124, "会社を経営する夫エドワードと9歳の息子と平穏な日々を過ごしていた専業主婦のコニー。が、ある日、ブックディーラーのフランス青年ポールと出会い、情事を重ねるようになる。やがて彼女の夫もそれを知るが。監督は「ナインハーフ」（86）、「危険な情事」（87）などのエロティック・サスペンスで知られるエイドリアン・ライン。リチャード・ギアとダイアン・レインが「コットン・クラブ」（84）以来18年振りの共演。", "2003-01-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DIVORCE OF LADY X", "淑女は離婚がお好き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "偶然の成り行きから一夜を過ごした、ハンサム弁護士と謎の女性が巻き起こす、ちょっと危ない騒動を描いた大人のラブ・コメディ。オリヴィエとオベロンの「嵐が丘」の名コンビが、次第に恋に落ちていく二人を英国流のソフィスティケートされたタッチで好演。", "1937-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA TRAVESTIE", "変身する女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "30歳の女弁護士ニコルは、まわりの男たちとの情事に満足できず、いつしか男装の快楽を覚えて、女たちをも愛し始めるようになってしまう……。両性具有的な魅力を備え持つ女優、ザブーの個性を最大限に生かした異色のラブ・ストーリー。男と女、二つの性が一人の肉体の中で交錯するセクシーな作品だ。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("われ幻の魚を見たり", "われ幻の魚を見たり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "古来、１匹の魚もいなかった十和田湖でヒメマスの養殖を成功させた和井内貞行をモデルとする一代記。教科書で和井内氏を知った伊藤大輔は伝記映画化に着手したが、共同脚本を依頼した麻止俊夫が独自のシナリオを発表したため企画は頓挫。10余年後、伊藤が史実から離れて物語を創作、本作が実現した。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Pie", "アメリカン・パイ", "", "", "http://image.eiga.k-img.com/images/movie/1825/315x210.jpg?2375", 96, "ジム、ケビン、オズ、フィンチはフツーの高校生。みんな頭の中はセックスで一杯なのに未だに童貞だ。おいしい出会いを期待してパーティに出かけたジムたちだったが、収穫もなく虚しい一夜を過ごすことに。さらにモテない男シャーマンが童貞を捨てたと聞きショックを受けた4人は協定を結ぶことにする。それは「卒業までに女の子と寝ること」。3週間後のプロムを目指し、それぞれのロスト・バージン作戦が始まった！", "2000-07-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Evil Dead 2: Dead by Dawn", "死霊のはらわた II", "", "", "http://ecx.images-amazon.com/images/I/51utFfbhJ3L._SL210_.jpg", 85, "　アッシュとリンダは森の中の廃屋で“死者の書”を発見し、700年間封印されていた死霊を甦らせてしまう。それに乗り移られたリンダはゾンビに。乗り移られた右腕を切断し、そこにチェーンソーを装着したアッシュは死霊と血みどろの闘いを繰り広げる……。スケールアップした前作のリメイク作品だが、VFXはより緻密に、そして映像技術・物語はかなりブッ飛び、ハイ・テンションに。ライミ監督のコメディセンスと主演ブルース・キャンベルの怪演も魅力のスプラッタ・ホラー。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I　WANT　YOU", "I WANT YOU あなたが欲しい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "M・ウィンターボトム監督が、クシシュトフ・キェシロフスキ作品などでフィルターを多用した独特のトーンの映像を創造してきたカメラマン、S・イジャックを招き、新たなアプローチを実践した愛憎劇。恋人の父親を殺害し、９年ぶりに出所した青年マーティン。故郷の町ヘイブンに戻って来た彼だが、因縁の元恋人ヘレンは美容師をしており、他の男と付き合っていた。そんな二人の関係は、盗聴マニアの難民少年らを媒介にますます歪み、ついには決定的な悲劇を迎える。主役二人の感情と肉体がひたすらスレ違い続ける中、物語のエモーションを高めるのはエルヴィス・コステロのタイトル・ナンバー。港町の情景や登場人物の肉体を官能的に捉えた映像美と相まって、ウィンターボトム作品としては珍しくムード先行のフィルム・ノワールに仕上がっている。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("用心棒稼業", "用心棒稼業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "宍戸錠と二谷英明の“ろくでなしコンビ”によるコメディ・アクション“稼業”ものの第２作。殺し屋・ジョーと悪徳保険屋・藤木は、お互いの利益のために協力して、熱海のキャバレー“ラスベガス”の権利書をめぐる悪玉同士の抗争を終結させる。冒頭のタイトルで、宍戸錠がデキシー調のジャズに合わせて拳銃の曲撃ちを見せるシーンは楽しませてくれるが、第１作「ろくでなし稼業」でのノリにノリまくったエースのジョーと、ダンプガイ・二谷の名コンビもここでは、ギャグやアクションも不発気味で今一つ冴えない。結局この“稼業”ものは、第３作「助っ人稼業」で打ち止めとなる。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天体小説　乙桜学園祭2", "天体小説　乙桜学園祭2", "", "", "http://image.eiga.k-img.com/images/movie/54835/315x210.jpg?3622", 86, "「イノセントワールド」などで知られる女流作家・桜井亜美と、「GOTH」「ZOO」など映画化作品も多い人気作家・安達寛高（乙一）が監督を務める、「星」と「本」をキーワードにしたオムニバス。桜井は孤独な少年少女を描いた自作「PLANETARIUM」を、安達はオリジナル書き下ろし脚本「一周忌物語」を映画化。", "2009-09-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("涙を、獅子のたて髪に", "涙を、獅子のたて髪に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "“松竹ヌーヴェル・ヴァーグ”の一人、篠田正浩が監督した青春ドラマ。脚本には寺山修司が参加している。舞台は、横浜。日雇い港湾労務者からピンハネして生計を立てている港のダニ・サブは一人の少女ユキと出会い、恋に落ちた。しかし、彼はそうとは知らずに彼女の父親を殺してしまう……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Condemned", "監獄島", "", "", "http://image.eiga.k-img.com/images/movie/55025/315x210.jpg?4682", 113, "WWEの人気レスラー、スティーブ・オースティンが映画初主演を果たしたサバイバル・アクション。あるTVプロデューサーが企画する番組のため、絶海の孤島に10人の死刑囚が集められた。番組の内容は、30時間という制限時間の中で最後の1人になるまで殺し合いを続けさせ、その模様をインターネットで中継するというもの。世界中の視聴者が釘づけになる中、凶悪な死刑囚たちが生き残りをかけた激闘を開始する。", "2009-12-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE BEAU MARIAGE", "美しき結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "E・ロメールの“喜劇とことわざ”シリーズの第２作。画家の愛人を持つ女子大生が、結婚に向けて奔走するさまを描く。パリの大学に通うサビーヌは、愛人のシモンや友だちのクラリスに突然結婚を宣言。彼女には相手のあてなどなかったが、結婚は愛する人とするものというクラリスの忠告も耳には入らなかった。そんな中、クラリスの弟ニコラの結婚式で紹介されたクラリスのいとこエドモンは、35歳独身で弁護士と理想にぴったりの相手だった……。フランスシネマ大賞受賞作品。DVDは「エリック・ロメール コレクション(4)」に収録。", "1981-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大河の一滴", "大河の一滴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "五木寛之のベストセラー・エッセイをもとにした感動の人間ドラマ。東京で働くごく普通の女性、雪子が、ロシアで知り合ったロシア人青年、ニコライと東京で再会。トランペッターとしてオーケストラのオーディションを受けている彼を応援する雪子は、次第に恋心を募らせていく。そんなある日、父が病に倒れ、雪子は故郷の金沢に帰ることになる。ロシア人青年の夢に自分の希望を託すヒロインの心の軌跡を、心優しい幼なじみの青年との交流、父との関係、親友の悲劇などのエピソードを絡めて丹念に追っていく。トランペットの美しい調べが、哀しみや切なさを包み込む。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TAKING OF BEVERLY HILLS", "ビバリーヒルズを乗っ取れ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "夢の街ビバリーヒルズに深夜タンクローリーが横転し、猛毒ガスが流れ出す事故が起こる。しかしそれは強盗団の陰謀だった。住民が避難した街で略奪を始める強盗団に対して、地元のプロフットボール選手と警官が二人きりの戦いを挑む……。「ダイ・ハード」を思わせる豪快かつ過激なバイオレンス・アクション。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("錯位／DISOLATION", "スタンド・イン 続黒砲事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "1985年の「黒砲事件」で中国映画界の話題を集めたホワン監督の第２作。ロボット研究の第一人者でありながら、無駄な会議に追われ研究にうち込めない主人公は、自分そっくりのロボットを作り会議に出席させる。だが、ロボットは次第に命令に背き、逆に主人公を操りはじめ……。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RANCHO NOTORIOUS", "無頼の谷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "男たちの戦いが炸裂する西部劇の隠れた名作。オーストリアからハリウッドに渡って来たF・ラング監督と、ドイツ出身の大女優マレーネが組んだ会心作。マレーネは酒場の歌手上がりの女牧場主という難役を妖艶に演じた。1860年代のワイオミングで、主人公の婚約者が二人の強盗に殺されてしまう。復讐に燃えて彼らを探し、メキシコ近くの牧場に行き着いた彼は、ついに事件の鍵を握る男と接触する。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUTA", "BUTA", "", "", "http://image.eiga.k-img.com/images/movie/57975/315x210.jpg?3718", 25, "アニメーションの制作環境の問題解決や振興を図ることを目的とした文化庁の若手アニメーター育成プロジェクト「アニメミライ」2011年度制作の4作品のうちの1本。生活費を稼ぐために海賊トラフグ一味の用心棒を引き受けたBUTAは、海賊船に捕らわれていたキツネの逃亡劇に巻き込まれる。欲望に忠実でバイタリティ溢れるBUTAと、心は純粋だが世間を知らないキツネの逃避行は大騒動に発展し……。個性的な動物たちが繰り広げる冒険活劇で、制作は「ルパン三世」シリーズや「NEMO ニモ」のテレコム・アニメーション。監督は「NEMO ニモ」のアニメーションディレクターとして知られる友永和秀。", "2012-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鏡の中の野心", "鏡の中の野心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "美容界の最大手、谷本学園の校長の香子は、更なる飛躍を求めて新宿への進出を狙っていた。しかし、美容連合会会長らに先を越されてしまう。そんな彼女の前に謎の男、瀬木山が現れる。彼は香子に、運動費と引き換えに新宿のサロンの権利を取り戻すと言うが……。ネガ紛失のため、長らく日の目を見なかった幻のサスペンス映画。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG NIGHT", "シェフとギャルソン,リストランテの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「ターミナル」のS・トゥッチが、従兄弟のJ・トロピアーノと共同執筆した脚本を、C・スコットとともに監督。米国でレストランを経営するイタリア人兄弟と、様々な人間模様を交えて描く心温まる人間ドラマだ。1950年代のニュージャージーで、プリモとセコンドのイタリア移民の兄弟がイタリアン・レストランを開く。ところが、本格的な味にこだわる彼らの店は、米国人にはなかなか受け入れられない。そんなある日、向かいにあるレストランを成功させた支配人が、友人の有名な音楽家を二人の店に招待する計画を持ち掛ける。さっそく彼らは、知り合いを集めて豪華な晩餐会のために奔走するのだが……。兄弟の心の交流と人々との触れ合いを、時にユーモラスに、時に切なく映し出す。次々と出される色鮮やかな料理の数々も魅力だ。ビデオ発売時タイトルは「リストランテの夜」。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STORY OF DRUNKEN MASTER", "女カンフー 魔柳拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "父の仇を討つため、酔拳が得意な師に弟子入りした娘。彼女は酒場でアクロバットのアルバイトをしながら、修行を積んで技を磨き、身体の柔らかさを最大限に発揮する独自の拳法を体得する。珍しく女性を主人公にしたカンフー師弟もの。", "1979-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moonlight Mile", "ムーンライト・マイル", "", "", "http://image.eiga.k-img.com/images/movie/1545/315x210.jpg?3286", 116, "70年代初頭の米マサチューセッツ州。結婚直前に婚約者が急死したジョー。娘を失った婚約者の両親は、ジョーに自分たちと同居することを勧める。しかしジョーには悩みがあった。実は死の直前に彼女との婚約は破棄されていたのだ。監督は「シティ・オブ・エンジェル」のブラッド・シルバーリング。監督自身、89年に恋人の女優レベッカ・シェーファーをストーカーによる銃撃によって失うという経験を持つ。原題はローリング・ストーンズの曲名から。", "2003-06-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAR TREK V：THE FINAL FRONTIER", "スター・トレック5 新たなる未知へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "人気TVシリーズ「スター・トレック」劇場版の第５作。宇宙中立地帯にある平和の星ニンバス IIIに反乱が発生し、管轄する３人の代表者が人質となった。何の資源もないこの惑星を狙う犯人の目的は何か。カーク艦長、スポックらは再建されたUSSエンタープライズでニンバス IIIへ向かう。カーク艦長役のW・シャトナーがこの作品で監督デビューを果たした。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Together", "北京ヴァイオリン", "", "", "http://image.eiga.k-img.com/images/movie/1735/315x210.jpg?1996", 117, "監督は「さらばわが愛／覇王別姫」（93）でカンヌ映画祭パルムドール受賞のチェン・カイコー。「人生は琴の弦のように」（91）でも音楽を描いた彼が、今度は中国クラシック界を舞台に、バイオリンを愛する13歳の少年の成長を描く。この少年役は実際に6歳からバイオリンを始めたタン・ユン少年を監督が全国コンクールで発見して抜擢。演奏場面の音は、出演もしている中国の名バイオリニスト、リー・チュアンユンの演奏。", "2003-04-26", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴力街", "暴力街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "安藤昇・小林旭・丹波哲郎・菅原文太の４大スター顔合わせによるヤクザ映画。関東の東菊会と、関西の西日本連合。抗争を続ける二つの組織の間で、カタギの生活を送っていた元東菊会の江川は、平穏な日々を破壊される。監督は、骨太の演出で知られる五社英雄。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MIDSUMMER NIGHT’S DREAM", "真夏の夜の夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "演劇界のみならず、映画、音楽、絵画と多岐にわたってその特異な才能を発揮するL・ケンプ。彼が演出・主演して話題を呼んだ舞台を映画化した作品。原作は、かの有名な、シェイクスピアの傑作喜劇。ある夏の夜、森の妖精パックがほんの気まぐれから振りまいた恋の媚薬で巻き起こったラブ・ハプニング。翌日に婚礼を控えた恋人たちは、別々の相手に魅せられ、妖精の女王タイターニアまでが、一角獣にされた職人に一目惚れ……。パック役をリンゼイが、妖精ならぬ小悪魔のような様相で怪演。ゴージャスで夢幻的な映像に、毒気と背徳のイメージ漂う、妖しくも甘美なマジカル・ファンタジー。", "1984-01-01", "9,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IM LAUF DER ZEIT", "さすらい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 176, "映画館を巡り映写機を修理して回るブルーノ。大型ワゴンでロックを聴きながらの気ままな旅を続けるある日、彼の目の前で一台のワーゲンが猛スピードで川に突っ込んだ。ずぶぬれで脱出してきた男を乗せて、ワゴンは東西ドイツ国境沿いをゆく……。主人公たちと一緒に旅をしながら即興で演出したという自由な精神が炸裂する、ベンダースの“ロード・ムービー3部作”の第3作。", "1977-01-27", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("波浮の港", "波浮の港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "吉永小百合＝浜田光夫コンビによる青春ドラマ。婚約者のある娘に惚れてしまった隆一。相手の明代は、隆一の父が愛しながらも結ばれることのなかった昔の恋人の娘だった。父は隆一に自分と同じ運命を歩かせたくないと思い“奪いとれ！”と勇気づけるのだが……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長脇差忠臣蔵", "長脇差忠臣蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "徳川幕府に崩壊の兆しが見え始めた時代を背景に、浜松城主に親分を殺された子分たちが、仇討ちに立ち上がる姿を描いたヤクザ版忠臣蔵。大石内蔵助同様、時節を待つ間、密偵を繰り出し、仇の状況を調べたあと、組員40数名を引き連れて浜松城に乗り込んでいく堀の内喜三郎の姿が綴られている。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRACE OF MY HEART", "グレイス・オブ・マイ・ハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "「ガス・フード・ロジング」の女性監督A・アンダースによる人間ドラマ。音楽業界に飛び込んだ女性の夢と挫折、愛と葛藤の物語がユーモラスに展開する。1950年代末、歌手志望のエドナはソングライターとしてデビュー。成功の一方で、結婚、出産、離婚とプライベートでも多忙をきわめるが、彼女は自分の歌を吹き込む夢をあきらめず……。キャロル・キングを思わせるヒロイン像や、ビーチ・ボーイズ風のバンドの登場など、音楽ファンをニヤリとさせるエピソードが満載。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE LITTLE INDIAN", "リトル・インディアン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "敵軍に捕らわれた騎兵隊の士官が、砂漠でインディアンの少年に助けられながら、脱出を図る。しかし彼らの行く手には、様々な障害が待ち受けていた……。ディズニー映画にしては珍しいコメディ要素の強いアドベンチャー・ストーリー。当時10歳だったJ・フォスターが出演しているのにも注目。", "1973-01-01", "1", 0, NOW(), NOW());
