INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Merci Pour le Chocolat", "甘い罠", "", "", "http://image.eiga.k-img.com/images/movie/56195/315x210.jpg?1981", 100, "2010年に他界した仏ヌーベルバーグの巨匠クロード・シャブロルの日本未公開作品を連続公開する「映画の國名作選II クロード・シャブロル未公開傑作選」にて上映。米作家シャーロット・アームストロングによる「見えない蜘蛛の巣」をもとに、人間の心の奥底に潜む善と悪の葛藤（かっとう）を、リストのピアノ曲「葬送」をはじめとしたクラシック音楽の名曲にのせて描きだす心理犯罪ドラマ。", "2011-05-21", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("快傑黒頭巾", "快傑黒頭巾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "幕末の江戸を舞台に江戸城内抜穴に関する暗号図をめぐって、倒幕派の黒頭巾とこれを追う浪人目付・青江下野、新選組らが血みどろの争奪戦を展開する。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La derniere lettre", "最後の手紙", "", "", "http://image.eiga.k-img.com/images/movie/57315/315x210.jpg?4482", 62, "1967年のデビュー以降、現代アメリカ社会を独自の視点で見つめ続けてきたドキュメンタリー作家フレデリック・ワイズマン監督が、ワシーリー・グロスマンの小説「人生と運命」の1章を映画化した劇映画。コメディ・フランセーズの女優カトリーヌ・サミーのためにワイズマン監督が自ら脚色した。1941年、ウクライナ。ナチによるユダヤ人迫害の恐怖が渦巻く中、年老いた女医アンナがナチの手を逃れた息子に宛てた手紙を口述筆記し、その姿が1人の人間の弱さや強さ、尊厳を浮かび上がらせていく。2011年、ワイズマン監督作を一挙上映する「フレデリック・ワイズマンのすべて」にて日本初公開。", "2001-01-01", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA GRANDE SEDUCTION", "大いなる休暇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "島の繁栄のため一致団結した島民たちの涙ぐましい奮闘を描いたハートフル・コメディ。悪気のない嘘を重ねる島民たちと、そんな彼らの思惑も知らずに素朴な人柄に魅かれていく都会育ちの医師。両者の奇妙なやり取りに思わず吹き出し、最後にはじんわりと温かい気持ちになる愛すべき作品だ。カナダの小さな島に工場誘致の話が持ち上がる。条件は島に医師がいること。島民たちは一人の医師を島に定住させようと奔走する。", "2005-06-04", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("背徳のメス", "背徳のメス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "黒岩重吾の直木賞受賞作を野村芳太郎監督が演出したサスペンス・ドラマ。宗教団体の資金で運営されている大阪の病院を舞台に、金と欲望にまみれた人間たちが、互いに殺意を抱きながら生きていく姿を赤裸々に描いていく。田村高廣が「白い巨塔」と正反対の役柄で、女に溺れ汚れきった医師を演じている。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬死にせしもの", "犬死にせしもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "終戦直後、瀬戸内海を荒らし回った海賊たちの青春を描く海洋アクション。昭和22年、復員兵の重左は、戦友で今は海賊をやっている鬼庄に出会い、その仲間に加わる。仕事は順調だったが、嫁入り船から花嫁を奪ったことが瀬戸内の大親分・花万にばれて狙われるハメに。嫁ぎ先から、花万に彼女を取り返すようにと依頼がきたのだ。だが、政略結婚の犠牲となるところだった花嫁・洋子は重左と恋に落ち、故郷・竹田津へ逃げようとする。そこを花万の追っ手に見つかり、重左たちとの激しい戦いが始まる……。アクションもさることながら、一人一人死んでいく仲間たちの描き方が鮮烈だ。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さよならみどりちゃん", "さよならみどりちゃん", "", "", "http://ecx.images-amazon.com/images/I/31S5QDJS1KL._SL210_.jpg", 90, "南Q太の人気コミックを、「まぶだち」「ロボコン」などで高い評価を得た古厩智之が映画化。きれいごとばかりではない恋のかたちを、ハッとするほどリアルな心情描写で描いた切ないラブ・ストーリーだ。若手注目株の星野真里が、ヌードも辞さない熱演で揺れ動く乙女心を健気に熱演している。みどりちゃんという彼女がいるユタカを好きになってしまったゆうこ。彼女はユタカに嫌われたくなくて言いなりになっていたが……。", "2005-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRAVELLER", "フェイク ディール　偽札", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「許されざる者」「スピード２」のカメラマン、J・N・グリーンの初監督作品。製作も兼ねたB・パクストン演じる中年詐欺師の大勝負を描く。ケチな詐欺師ボッキーは死んだ兄の息子パットを相棒にして小さな仕事を重ねていたが、自分たちにだまされたために職を失ったジーンのことが気にかかってしまう。ボッキーは彼女の娘の病気を治す金を手に入れるため、ニセ札を使ったトリックでマフィアから大金を巻き上げようとするが……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APRILE", "ナンニ・モレッティのエイプリル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "“ミケーレ”という名の主人公の映画を撮り続けてきたN・モレッティが、彼自身の名前で主演した私的なコメディ。映画監督である神経過敏な主人公の子育て奮闘記を日記形式で綴るシネ・エッセイで、モレッティ作品ならではのユーモアとアイロニーが存分に楽しめる作品になっている。1995年に妻シルヴィアが妊娠して大喜びのモレッティ。つい自分が父親にふさわしいか考えてしまう彼は、翌年生まれた赤ちゃんにピエトロと名付け、必要以上に子育てに悪戦苦闘していく。", "1998-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rudo y Cursi", "ルドandクルシ", "", "", "http://image.eiga.k-img.com/images/movie/55055/315x210.jpg?3424", 101, "「天国の口、終りの楽園。」の脚本家カルロス・キュアロンがメガホンをとり、同作の主演コンビ、ガエル・ガルシア・ベルナルとディエゴ・ルナを再び起用して描くヒューマンドラマ。アルフォンソ・キュアロン、アレハンドロ・ゴンサレス・イニャリトゥ、ギレルモ・デル・トロらメキシコを代表する名匠たちが製作を務める。メキシコの片田舎にあるバナナ農園で働く兄弟ベトとタトは、サッカーに明け暮れる日々を送っていた。ある日、そんな2人の姿がスカウトマンの目に留まり、大都会メキシコシティ行きを賭けたPK対決をすることになる。", "2010-02-20", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛怨峡", "愛怨峡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "トルストイの『復活』をヒントにした溝口健二得意の女性もので、名作の誉れ高い一作。旅館の一人息子の謙吉と、そこで働く女中おふみは、謙吉の父から結婚の許しをもらえず、東京へ駆け落ちする。だが、東京での貧しい暮らしに耐えきれず、謙吉は実家に戻ってしまう。残されたおふみには謙吉の子が……。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADVENTURES OF BARON MUNCHAUSEN", "バロン", "", "", "http://ecx.images-amazon.com/images/I/51zlT5eRE2L._SL210_.jpg", 126, "ドイツで語り伝えられた有名な物語『空想男爵の冒険』の映画化。製作費75億円という巨費を投じて作られた圧倒的な映像の世界は観る者の創造力を快く裏切り、興味は尽きるところがない。時は18世紀。トルコ軍支配下にある城塞を救おうとする老男爵ミューヒハウゼンが、とてつもない能力を持った昔のお供の者たちを探し求めていく冒険の数々。月の世界、地底の王国、さらには巨大な魚の腹の中と時間、空間を飛び越えてファンタスティックな光景を洪水のようにあふれさせる。一つ一つの造形は創造力に富み、幻想的な美しさは絶品。なお原題のようにイギリスではこの男爵のことはマンチョーゼンと呼ばれている。ギリアム作品ならではのブラックな笑いも随所に盛り込まれ、大人のおとぎ話ともいうべき圧倒的な映像世界を作り上げている。", "1989-01-01", "3,1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵ホームズ1　青い紅玉（ルビー）の巻", "名探偵ホームズ1　青い紅玉（ルビー）の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 24, "「名探偵ホームズ」は、宮崎駿が監督、演出などを務め、1984年からテレビ朝日系で放送されたテレビアニメシリーズ。本作と「海底の財宝の巻」がテレビ放送に先駆け「風の谷のナウシカ」と同時上映された。コナン・ドイルの「シャーロック・ホームズ」をもとにしているが、登場人物はすべて犬になっている。ホームズのライバル、モロアッチ教授は宝石店から青い紅玉を盗み出すため、怪鳥型飛行機で現れて街に大パニックを起こす。", "", "76,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("待合室", "待合室", "", "", "http://image.eiga.k-img.com/images/movie/33725/315x210.jpg?4713", 107, "東北の小さな駅の待合室に置かれた1冊のノートを巡る感動の実話を映画化。岩手県の山間部にある寒村。この地を訪れた旅人たちは、いつしか駅の待合室にあるノートに自らの悩みを綴るようになっていた。愛する娘と夫を相次いで失って以来、駅前でひとり酒屋を営んでいる夏井和代は、旅人たちの書き込みに返事を書き続けるが……。ヒロイン・和代を富司純子が、彼女の若き日を寺島しのぶが演じ、母娘で映画初共演を果たしている。", "2006-11-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILL THE LOVE", "彼らだけの世界 〈インターナショナル・ヴァージョン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "韓国トップスター、イ・ビョンホンが、「灼熱の屋上」のチョン・ソンギョンと織りなした異色の純愛ラブ・ストーリー。運命的な出逢いを果たしたギャングとダンサーの激しい恋を、本国韓国で物議を醸した過激な性描写と共に描いた衝撃作。父親の命令でアメリカ留学したラブは、期待に反してギャングになってしまう。組織の命令を受け帰国するが、ナイトクラブで出会ったダンサーのチュニャンと激しい恋に落ちていき……。インターナショナルバージョンは権利上の都合により、韓国上映版と比べて本編の映像、音楽に一部変更、カットされている。", "1996-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("19ナインティーン", "19ナインティーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1998年の東京。街は未来からやって来た19体の怪物によって次々と破壊されていったが、同じく未来から３人のタイムパトローラーが現れ、見事地球は危機を脱出する。1980年代のアイドル、少年隊を主人公にしたSFアドベンチャー。さんま、鶴太郎らがゲスト出演。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Girlfight", "ガールファイト", "", "", "http://image.eiga.k-img.com/images/movie/1405/315x210.jpg?1879", 110, "ダイアナはブルックリンに住む女子高生。母が自殺して以来、父と弟と3人で暮らしている。日々やり場のない怒りを抱えていた彼女だったが、ある日弟が通うボクシングジムに立ち寄ったとき、ボクシングこそが自分の求めていた“何か”だと確信する。そこで初めて自分の価値を見出し、恋人に出会い、かつてない充足感を覚えるダイアナ。だが、ようやくボクサーとしての実力をつけリーグ戦を勝ち進む彼女に、あろうことか次の対戦相手が恋人エイドリアンに決まったとの知らせが届く。", "2001-05-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Returner〈リターナー〉", "Returner〈リターナー〉", "", "", "http://ecx.images-amazon.com/images/I/51VDNNVH7ML._SL210_.jpg", 118, "裏稼業で生きる一匹狼のミヤモトは、ある取り引きのさなか、突然現れた少女ミリのせいで、長年追ってきた溝口を取り逃がしてしまう。2084年から来たというミリの話では、未来の地球は宇宙生物ダグラによって侵略され、人類は死滅間近。生き残るためには最初に飛来した１匹を殺さなければならないというのだ。戦争勃発の運命の日まであと３日。すでに地球に侵入している最初のダグラが溝口にさらわれたことを知った二人は、溝口の元に急行する。体感時間を20倍に引き延ばせる装置や擬態宇宙船などのユニークなアイテムや、大胆なアクションなどVFXを満載した映像が迫力満点。監督は「ジュブナイル」の山崎貴。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MADE IN AMERICA", "メイド・イン・アメリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "精子バンクの提供で娘を生んだ黒人のサラは10数年後、精子の提供者が白人であることを知り、怒り心頭。娘の父親ハルと反目し合いながらも、サラは彼に恋心を抱き始める……。W・ゴールドバーグのノッてる演技が笑えるロマンティック・コメディ。共演者T・ダンソンと彼女の、映画さながらの恋も話題になった。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEST OF THE BEST2", "帝王伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ラスベガスで連夜行われる、地下世界のストリート・ファイト。勝者には名誉とカジノの所有権が与えられ、敗れた者には死が待ち受けている。この試合で親友を殺されたトミーとアレックスは、敵を討つために連戦連勝の格闘王に戦いを挑む。壮絶な肉弾戦がダイナミックに展開する異種格闘技アクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("平成ジレンマ", "平成ジレンマ", "", "", "http://image.eiga.k-img.com/images/movie/55825/315x210.jpg?1398", 98, "1970年代末から80年代初めに訓練生の死亡や行方不明事件で世間を騒然とさせ、日本教育機関における体罰禁止のきっかけとなった「戸塚ヨットスクール」に焦点を当てたドキュメンタリー。戸塚ヨットスクールを取材エリアに持つ東海テレビが、事件当時からの長期取材を敢行。時代を象徴する事件を起こした「戸塚ヨットスクール」のその後の30年と、厳しい体罰が影を潜めた同校の現在を追い、現代社会が抱えるジレンマを浮かび上がらせていく。", "2011-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MIGHTY QUINN", "刑事クイン 妖術師の島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "FBIでの研修を終えてカリブ海に浮かぶ島の警察署長となったクイン。ある日、リゾートホテルでアメリカの実業家が殺されたとの知らせが飛び込む。現場の状況からクインは、旧友の悪党マウビーが犯人だとにらむのだが……。A・H・Z・カーの同名ミステリーを映画化。全編に流れるカラフルなレゲエ・ナンバーが心地よい。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE UGLY DACHSHUND", "猛犬ご注意", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "大の犬好きの夫妻が、知人からグレートデンの子犬を預かる。ちょうど飼い犬のダックスフンド、ダンケが子供を生んだところだったので夫妻はダンケにその子犬を育てさせることにする。グレートデンは犬の王様。彼はブルータスと名付けられたが、ブルータスはダンケたちと過ごすうちに自分もダックスフンドの仲間だと思い込み始めて……。犬たちの擬人化もほほえましいコメディ。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジョセフ・ロージー／　四つの名を持つ男", "ジョセフ・ロージー／　四つの名を持つ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "学生時代にジョセフ・ロージーの「緑色の髪の少年」「エヴァの匂い」を見て衝撃を受けたという中田秀夫監督が５年をかけて製作したドキュメンタリー。“赤狩り”でヨーロッパを亡命し、３つの変名を用いて監督業を続けた呪われた作家ロージー。彼にゆかりあるスタッフ、キャストと家族へのインタビューを織り込み、「コンクリート・ジャングル」「できごと」「非情の時」などの名場面を紹介。人間の孤独や屈折した心理を好んで採り上げた異色の映画作家ロージーの複雑な人間像が浮き彫りにされていく。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Castle Keep", "大反撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "第２次大戦中の1944年、バルジ大作戦の前夜、ベルギー国境近くのある古城にアメリカ部隊がやってきて駐屯する。彼らの存在に気づいたドイツ軍が攻撃を仕掛けてくるが……。篭城したアメリカ軍歩兵小隊の死闘を描く異色の戦争アクション。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("B.スクランブル", "B.スクランブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "“アクション・カメラ＝盗み撮り”に情熱を燃やす３浪の青年と、風俗雑誌の編集をしている女子大生。二人のラブ・ストーリーを軸に、風俗産業のメッカ、新宿に生きる人々の姿をセミドキュメンタリー・タッチで捉えた作品。ミッキー・カーチス、ディック・ミネなど異色のキャストが面白い。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAIN EVENT", "メーン・イベント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "香水製造会社を経営するじゃじゃ馬女社長が、ふとしたことから今は引退の身のボクサーを再びリングに上がらせることになり、一騒動が巻き起こる。ストライサンドとオニールという魅力ある顔合わせで綴るロマンティック・コメディ。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤胴鈴之助 三つ目の鳥人", "赤胴鈴之助 三つ目の鳥人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "森一生監督によるシリーズ第７作。江戸の町に三つ目の鳥人が現れ、戌年生まれの子供を次々とさらい、赤胴鈴之助が剣術指南を勤める江戸町奉行・土井安房守の若殿・鶴千代もさらわれてしまう。鈴之助は鳥人の隠れ家であるお化け屋敷に忍び込む。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("B計画／EXTREME　CRISIS", "ホーク／B計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ジャッキー・チェン主演の「デッド・ヒート」で知られる澤田謙也が、自らの原案・製作によって完成させた香港＝日本合作アクション。日本警察SAT（特殊部隊）隊長、鷹見は凶行を繰り返すカルト教団本部に突入したものの反撃に遭い、部下を多数失った。教団は香港に逃れるが、教祖は現地で逮捕されてしまう。ところが鷹見が香港政府に教祖引渡しの交渉を続けていた日本領事館に、教団のテロリストが乱入。領事を射殺した彼らを、鷹見は香港警察の刑事ケンと組んで追いかける。やがてテロリストは化学者を拉致し完成させた化学兵器を抱えて、TV局を占拠し、教祖の釈放を要求するが……。某宗教団体をモデルにしていたため、日本公開が遅れていた一作。「雨あがる」の三船史郎が化学者役で出演。", "1998-01-01", "6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("変態家族・兄貴の嫁さん （お嫁さん日和）", "変態家族・兄貴の嫁さん （お嫁さん日和）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "黒沢清監督が撮った劇場用映画第１作「神田川淫乱戦争」は今やカルト的な作品として知られているが、その「神田川淫乱戦争」と同年に製作された本作もまたカルト中のカルトとして1980年代前半の日本映画界を語るうえで欠かすことのできない超異色作である。冒頭からいきなり富士山のマークが映し出され、それに続き東京タワーのショット、そして結婚式に急ぐ人々……と、これは全編小津映画のパロディーというか、オマージュで埋め尽くされたポルノ・コメディ。小津からの引用が必ず、全シーン、全ショットのどこかにあり、それを見つけ出すのも楽しみの一つ。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青い街の狼", "青い街の狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "二谷英明が、麻薬Gメンに扮したアクション映画。富士山上空で、謎の爆破を遂げた飛行機事故の裏に、陰謀が隠されているとにらんだ麻薬Gメンの五郎は、殺し屋と称して組織のボス、矢代の所に入り込む。そこで五郎は、事件の鍵を握る男、秀南白と出会うが……。1962～63年、それまで助演だった二谷が、赤木圭一郎の死によって主演に昇格した頃の１本。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・マジックアワー", "ザ・マジックアワー", "", "", "http://image.eiga.k-img.com/images/movie/34115/315x210.jpg?2250", 136, "港町・守加護（すかご）を牛耳るギャングのボス・天塩の愛人に手を出してしまった備後は、命を助けてもらう代わりに伝説の殺し屋・デラ富樫を連れて来ることを命じられる。追い込まれた備後は三流役者の村田を雇い、映画撮影だと思い込ませて殺し屋を演じさせるが……。「THE有頂天ホテル」などのヒットメーカー、三谷幸喜が監督・脚本を手掛けるクライム・コメディ。キャストには佐藤浩市、妻夫木聡、深津絵里ら豪華な顔ぶれが集結。", "2008-06-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二人の世界", "二人の世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "石原裕次郎主演の日活ムード・アクション。北条修一は身に覚えのない罪をきせられ、やむなく国外へ逃亡していたが、あと４日で時効という時になってフィリピン人・バルガスと名乗って帰国する。彼は帰国の船上で戸川玲子と知り合う。長崎に着くと、北条は玲子に置き手紙をし、汚名を晴らすために真犯人の捜査に乗り出す。玲子は北条の過去に強い興味を抱いて北条を追う。もう一人、スクープをものにせんとして、雑誌記者・川瀬も北条を追跡中。だが証人は次々と消されていた。主人公の過去を取り戻し、失われた自己の存在証明を得ようとする姿を、情感をたたえたムーディーな雰囲気で描いた佳作。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一胆二力三功夫／FIST＆GUTS", "ガッツ・フィスト 魔宮拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「少林寺三十六房」のリュウ・チャーフィがひょうきんな面も見せるカンフー映画。伝家の秘宝の行方を探す青年が、仲間二人と魔宮に迷い込む。そこで殺人マシーンに襲われた３人は合体拳を編み出して突破、ついに宝を隠した宿敵と対面することになる。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE BONHEUR EST DANS LE PRE｀", "しあわせはどこに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "家族から疎まれ、仕事もままならない初老の男が、予期せぬ幸せをつかむまでを描いたハートフル・ドラマ。トイレ用品の製造工場を営むフランシスは、妻と娘に忌み嫌われたうえに、工場も傾きかけ、心配のたえない日々を送っていた。そんなある日、フランシスは親友ジェラールと65歳の誕生日を祝っている最中に突然倒れ、緊急病院へと運ばれる。だが、大事にいたらずやがて元気になった彼は、家族と行方不明の公開捜査番組を見ていると、失踪中の夫だという映像に彼とよく似た男の顔が映し出され……。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLASH OF THE TITANS", "タイタンの戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ギリシャ神話に登場する若き勇者ペルセウスが、美しい王女アンドロメダとの愛を成就させるまでを描いた冒険ファンタジー。SFX担当レイ・ハリーハウゼンのダイナメーション方式による数々の怪獣モデルアニメの特撮合成シーンは大きな見どころ。", "1981-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユキエ", "ユキエ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "戦争花嫁としてアメリカのルイジアナ州バトンルージュに渡った日本人女性ユキエ。それから40年の歳月が流れ、彼女は元空軍パイロットの夫・リチャードとの間に二人の息子をもうけ、愛に満ちた生活を送っていた。ところが、そんなユキエにアルツハイマー病の症状が現れ、彼女は自分の息子の顔すら判別できなくなっていく……。これが監督デビュー作となる松井久子が、芥川賞小説『寂寥郊野』を４年かけて映画化。老いゆく中で不治の病に直面した夫婦の愛を、抑えの効いた演出でじっくり描き切った。現地ロケによるルイジアナの美しい湿地帯の風景がドラマに詩情を吹き込み、ラスト・シーンには名曲『ユー・アー・マイ・サンシャイン』の歌声が切なく響く。倍賞美津子、B・スヴェンソンの名演も忘れがたい。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE．．．EXTREMES", "美しい夜、残酷な朝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "アジアを代表する監督が結集した特異なオムニバス。いずれも成功者の裏側にあるダークな真実を、それぞれのこだわりで追究している。クリストファー・ドイル撮影による「dumplings」、三池崇史初の静かな映画「box」、そしてイ・ビョンホンがノーギャラで主演した「cut」と、豪華な3本が揃った。順風満帆のキャリアを歩み始めている若手映画監督リュ・ジホが帰宅すると、愛する妻ミランが映画のエキストラを自称する男に捕らえられていた。いったい、なぜ？(「cut」)", "2005-05-14", "6,76,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Genius Party ジーニアス・パーティ", "Genius Party ジーニアス・パーティ", "", "", "http://image.eiga.k-img.com/images/movie/1945/315x210.jpg?2242", 104, "「鉄コン筋クリート」のSTUDIO4℃によるオムニバス・アニメ。監督には「アニマトリックス」の二村秀樹、「マインド・ゲーム」の湯浅政明、「カウボーイビバップ」の渡辺信一郎ら、日本アニメ界を牽引する7人のクリエイターが集結。また、声優として「誰も知らない」でカンヌ国際映画祭主演男優賞に輝いた柳楽優弥と、「バベル」でアカデミー賞助演女優賞にノミネートされた菊地凛子が初共演することでも話題に。", "2007-07-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神探朱古刀／CHOCOLATE INSPECTOR", "新 Mr.Boo！ 香港チョココップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "マイケルとリッキーのホイ兄弟がドタバタコメディを繰り広げる、シリーズ第４作。うだつが上がらず同僚からも馬鹿にされているチョコレート警部補とその部下タルト刑事。この二人が行方不明になったテレビスターの息子を捜索することになるのだが……。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASH WEDNESDAY", "別離", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "夫の愛を取り戻すために全身美容の整形手術を受ける妻役に、初老を迎えつつあるテイラーが挑んだ一編。しかも、若返りや見た目の美しさでは、夫の愛は取り戻せない……。手術前、手術、手術後と変化していく美容整形の経過が見ものである。", "1973-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORCES OF NATURE", "恋は嵐のように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "マリッジ・ブルーの青年と自由奔放な女性が、次々とトラブルに巻き込まれる波乱の旅で結ばれるロマンティック・コメディ。コピーライターのベンが結婚式のため、ニューヨークからサバナへ向かうが、飛行機が離陸に失敗。そのため隣に座っていた女性サラと旅をともにすることになる。ところが、事故や悪天候と信じられないようなアクシデントが続発。ともに困難を乗り越えていくうち、純情青年のベンと明るく大胆なサラは互いに惹かれ合うが、ベンの結婚式の日が刻一刻と迫る。人生の岐路に立ち、戸惑う青年をB・アフレックが誠実感たっぷりに、彼をほんろうする美女をS・ブロックが生き生きと好演。ハリケーンが直撃したり、ひょうが降るシーンなどの、CGを駆使した幻想的な映像も印象的。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUBY　CAIRO", "ルビー・カイロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ロスに住む平凡な主婦ベッシーの元に届いた夫の事故死の知らせ。遺体確認のため訪れたメキシコで、夫の秘密に気付いてしまった彼女は、暗号を秘めたカードを片手に世界をめぐる旅に出た。自分の知らない夫の真実の姿を探し求めて……。角川映画ハリウッド進出第１作として製作されたラブ・サスペンス。", "1992-01-01", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAMMA ROMA", "マンマ・ローマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ローマの下層庶民に焦点を当てたパゾリーニ初期の代表傑作。娼婦から足を洗ったマンマ・ローマは、田舎で育てられた息子のエットーレを引き取って一緒に暮らすことにする。息子と暮らす所へ、彼女の昔のヒモ、カルミネがやって来て金をせびる。マンマ・ローマは再び街頭に立つようになる。一方、仲間の少年たちと盗みを働いていたエットーレは、今度は病院に盗みに入った。だが、盗みは失敗し、ベッドに縛りつけられて放置され、病死する。エットーレの死を殉難として描き出すところにパゾリーニの面目がある。", "1962-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES TRIBULATIONS D'UN CHINOIS EN CHINE", "カトマンズの男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "J・ヴェルヌの小説をJ・P・ベルモンド主演で映画化したアクション・コメディ。香港、ネパール、マレーシアを舞台に、スリルとスピード、お色気、エスプリと、すベての要素が盛り込まれている。満ち足りた生活に空しさを覚え、巨額の保険金を掛けて自殺志願をした男が、ストリッパーの女と出会ったことから命が惜しくなり、自分の命を狙う相手から逃げまわる。", "1965-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メキシコ無宿", "メキシコ無宿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "メキシコ・ロケによる宍戸錠主演の、和製ウエスタンのりのガン・ファイト娯楽活劇。錠扮する主人公は、悪玉に土地を奪われ、殺人者の汚名を着せられたメキシコ人の友人の身の潔白を証明するためにメキシコにやってくる。錠は苦難の末、悪玉を捕え、友人の潔白を証明する。殺し屋に扮した藤村有弘が怪演。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Poulet aux prunes", "チキン・ウィズ・プラムズ（英題）", "", "", "http://image.eiga.k-img.com/images/movie/58005/315x210.jpg?3244", 0, "", "2012-11-30", "2,7,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZELIG", "カメレオンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "人種国籍を問わずあらゆる人間に変身してしまう、という特異体質を持つ男が巻き起こすコメディ。1920年代を背景に、当時のニュースフィルムを引用して、ベーブ・ルースやヒトラーとも“共演”する主人公ゼリグの姿が圧巻。これは人間の存在証明への問いかけである。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Basketball Diaries", "バスケットボール・ダイアリーズ", "", "", "http://ecx.images-amazon.com/images/I/51IdM2MGgdL._SL210_.jpg", 102, "1978年に出版され、全米でベストセラーとなったJ・キャロルの青春文学の傑作『マンハッタン少年日記』の映画化。マンハッタンのミッションスクールに通うジムたちは、問題児の烙印を押されてはいたものの、バスケットに夢中な他愛のない不良少年にすぎなかった。しかし好奇心から始めたドラッグが、彼らをより深みへと落としていく。服用が見つかったジムは退学となり、母親にも愛想をつかされホームレスに。さらにドラッグ取引のいざこざから、殺人事件にまで巻き込まれてしまう。L・ディカプリオがジムを好演。", "1996-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファザーレス・父なき時代", "ファザーレス・父なき時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "日本映画学校ドキュメンタリー・ゼミの卒業制作作品として作られた記録映画。本音で家族と対峙し、新たな人生を踏み出していく少年の姿が感動を呼ぶ。主人公の村石雅也は、ほとんど学校に通わず行きずりの中年男たちと関係を持ち、ナイフで自分の肉体を傷つける日々を過ごしていた。幼い頃に母親が離婚し、義父に反発して家出した過去がある彼は、カメラとともに故郷の長野に向かい、母親、義父、そして自分を捨てた父親にストレートな疑問や不満をぶつけていく。マンハイム国際映画祭ドキュメンタリー部門でグランプリを受賞するなど、各国の映画祭で高い評価を得た作品。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダブルベッド", "ダブルベッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "30代前半のテレビ・ディレクターとその大学時代の友人ら、“若い中年”をめぐる日常を描いたドラマ。夫を裏切る妻、友人を裏切る男、恋人を裏切る女など、入り乱れた人間の愛僧の世界を藤田監督が淡々と描写した一編。柄本明、岸部一徳が好演している。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Find Me Guilty", "コネクション　マフィアたちの法廷", "", "", "http://image.eiga.k-img.com/images/movie/57595/315x210.jpg?1786", 124, "「十二人の怒れる男」「評決」の名匠シドニー・ルメットが、ビン・ディーゼルを主演に迎えた法廷劇。1987年から88年の21カ月間におよび、アメリカ史上最長の刑事裁判として歴史に残るマフィアのルッケーゼ・ファミリー裁判を映画化した。ニュージャージーの悪名高いマフィアの一味、ルッケーゼ・ファミリーに属するジャッキー・ディノーシオは、麻薬取引の現場を押さえられ逮捕される。刑期を短くするため仲間を売るように検事から司法取引を持ちかけられるが、仲間を裏切ることが許せないジャッキーは取引を拒否。裁判では、被告人でありながら自らの弁護も行うという驚きの行動に出る。", "2012-05-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALBINO　ALLIGATOR", "アルビノ・アリゲーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ハリウッドの個性派俳優K・スペイシーの初監督作。ニューオリンズの会社事務所に押し入った強盗犯３人が、別の武器密輸事件を張り込んでいた捜査官をひき殺して逃走。彼らは地下のバーに身を隠すが、瞬く間に警官隊に包囲されてしまう。たった一つの出口を前に彼らは人質をとるが……。舞台出身の実力派俳優がメガホンを執っただけあって、1920年代風のクラシックな雰囲気のバーに舞台を限定した密室劇の中に、個性的なキャストの持ち味が巧みに生かされている。犯人や人質が模索する地下からの脱出法や、最後に誰が生き残るのかというミステリー仕立てのスリルがラストぎりぎりまで持続。緊迫した状況の下、登場人物の秘密や嘘が明らかになっていく脚本の妙も見逃せない。ちなみにタイトルは、突然変異の色素異常で生まれた白いワニのことで“生け贄”の寓意を持っている。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANDRA", "ハンドラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "女だけの武装軍団を率いる金髪の戦士、ハンドラ。この部族には、生殖のために交わるが男児は捨てるという掟がある。そのハンドラが、女を奴隷とし男の圧政が敷かれた“雄牛の町”に乗り込んで大活躍する。女性の時代のセクシー・アクション。", "1984-01-01", "9,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メトレス", "メトレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "渡辺淳一の小説を映画化した女性ドラマ。“メトレス”とはフランス語で（女性の）主人、転じて愛人の意味も持つ言葉だが、ここでは妻子ある男性と恋愛関係にありながら、仕事を持って自立している女性を指す。高級フランス料理店でソムリエをしている片桐修子は妻子ある大学教授と不倫の関係にあった。結婚に対しての不安もあったが、そんな時に“メトレス”という言葉に出会い、結婚に縛られず、会える時だけ燃えるメトレスでありたいと考えるようになる……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今日、恋をはじめます", "今日、恋をはじめます", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "TVアニメ化もされた水波風南の人気少女コミックを、今回が映画初主演の武井咲と「アントキノイノチ」「麒麟の翼　劇場版・新参者」などで活躍する松坂桃李のフレッシュな主演コンビで実写映画化。結婚するまで貞操を守ろうという信念を抱いた古風でガリ勉な女子高生・日比野つばきと、つばきを「昭和女」と呼んでバカにし、ファーストキスを奪ってしまう現代風な男・椿京汰の恋模様を描き出していく。監督は「オトシモノ」の古澤健。", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("松川事件", "松川事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 162, "労働争議の激しい時代に起きた歴史的事件を、社会派監督・山本薩夫が映画化。昭和24年、東北本線・松川駅付近で発生した列車脱線事故を起こしたとされ、労組から20名が逮捕される。が、13年に渡る法廷での闘いにより検察の陰謀だったことがわかる。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dragon Age", "ドラゴンエイジ　ブラッドメイジの聖戦", "", "", "http://image.eiga.k-img.com/images/movie/57245/315x210.jpg?3658", 89, "2009年に世界で600万本を売り上げた人気RPGゲーム「Dragon Age: Origins」（発売バイオウェア／販売エレクトリック・アーツ）を、「ピンポン」「ベクシル 2077日本鎖国」の曽利文彦監督がCGアニメーション映画化。世界を統治するチャントリー（教会）に仕える騎士カサンドラは、チャントリーをめぐる巨大な陰謀の鍵となる少女の存在を知ってしまったことから、邪悪な魔法使い「ブラッドメイジ」の集団に襲われる。師匠のバイロンの犠牲でその場を切り抜けたものの、汚名を着せられ追われる身となったカサンドラは、魔法使いのガリアンとともに真相を求めて戦いに身を投じる。声優に栗山千明、谷原章介、GACKT。", "2012-02-11", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("X Y ＆ ZEE", "ある愛のすべて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ロンドンを舞台に、子供のいない倦怠期の夫婦の前に現れた若くナイーブな女ステラをめぐる、女と男の異様な三角関係を描いたドラマ。", "1972-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("杏っ子", "杏っ子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "室生犀星の小説を翻案した成瀬巳喜男監督の名作。高名な作家の娘が文学青年と結婚するが、夫が自分の才能を信じて売れない小説を書き続けるため生活が困窮し、夫婦仲も冷めていくという物語を成瀬独特の淡々とした作風で描いている。暗く起伏に乏しい内容でありながら、その映画的展開は圧倒的に素晴らしい。名声は天地の開きがありながら、同じ志を抱く作家としてのライバル意識を燃やす杏子の父と夫が、庭に隔てた障子越しに執筆中の互いの姿を気にし合うシーンなど、さりげないドラマの肌合いを捉える成瀬演出の白眉である。物語とは直接関係のない細部の卓抜な描写も印象的。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("盲獣VS一寸法師", "盲獣VS一寸法師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「恐怖奇形人間」の鬼才・石井輝男監督が、30数年ぶりに再び江戸川乱歩の原作を映画化した怪作ミステリー。『盲獣』と『一寸法師』の二つの原作を融合し、新たな乱歩ワールドを作り出した。またイラストレーターのリリー・フランキーが、俳優として出演するなど異色のキャスティングも見もの。三文小説家の小林紋三は、浅草レビューのスター水木蘭子の舞台を見物に行った。その帰り道に紋三は、子供の体に大人の頭を持った“一寸法師”に遭遇する。その一寸法師に興味を抱いた紋三は、独自に調査を開始するが……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Innisfree", "イニスフリー", "", "", "http://image.eiga.k-img.com/images/movie/55015/315x210.jpg?4667", 110, "ジョン・フォード監督、ジョン・ウェイン主演による1952年の傑作映画「静かなる男」の舞台となったアイルランドの小さな村イニスフリーをテーマにしたドキュメンタリー。現在も撮影当時の面影を残す村の様子や村人たちの素朴な表情を、「静かなる男」の映像・音声や作品の世界観を再現したフィクションなどを交えながら捉えていく。監督は名匠ビクトル・エリセの後継者と言われるスペインの俊英ホセ・ルイス・ゲリン。", "2009-10-18", "9,2,33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刀", "ブレード／刀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "亡き父の形見である折れた刀を武器に、仇に挑む青年の壮絶な戦いを描いた時代劇アクション。刀鍛冶場で働くテンゴンは、ある日父が剣の名手ルンに殺されたことを知り、復讐の旅に出る。ところが、あとを追って来た刀匠の娘リンが盗賊に襲われ、彼女を助けようとたテンゴンは死闘の末、右腕を失ってしまう。傷ついたテンゴンは剣術書をもとにひとり剣技の特訓を開始、ルンとの宿命の闘いに挑む。ふたりの青年をめぐる愛憎劇と、闘いに挑む男たちの熱きドラマが壮大なスケールで展開。華麗な剣さばきと、強烈な残酷描写の壮絶な決闘シーンは見るものを圧倒する。ツイ・ハーク監督渾身の一作。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANKY PANKY", "ハンキー・パンキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "追われる美女を助けたことから国際的な陰謀に巻き込まれる建築家のダン。彼の前に謎の女性ケイトが現れ、軍事機密に関するテープを狙う者がいることを告げる。彼らはテープを探して奔走するが、やがてケイトが敵の手に落ちてしまい……。「スター・クレイジー」の監督、主演コンビによるドタバタ喜劇。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市鉄火旅", "座頭市鉄火旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "例によって座頭市が、悪徳親分をこらしめるというストーリーだが、クライマックスの大殺陣で樽の中に入れられグルグル回された市が“盲にゃまわる眼がねェ！”と啖呵をきって、樽の中から樽ごと敵を斬り捨てるという趣向は面白い。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I'm a Cyborg, But That's OK", "サイボーグでも大丈夫", "", "", "http://image.eiga.k-img.com/images/movie/34055/315x210.jpg?1386", 107, "「オールド・ボーイ」のパク・チャヌク監督が、精神クリニックを舞台に患者同士の恋を描いたファンタジック・ラブコメディ。妄想病患者の青年イルスンが入院している新世界精神クリニックに、自分のことをサイボーグだと信じる少女ヨングンがやって来た。イルスンは彼女に興味を抱き、その行動を観察し始めるのだが……。主演は人気歌手のRain（ピ）ことチョン・ジフンと「箪笥＜たんす＞」のイム・スジョン。", "2007-09-15", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOOTLIGHT　PARADE", "フットライト・パレード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "トーキーの流行によって失職したミュージカルの演出家ケントは、二人の資本家と組んで映画が始まる前のレビューを手掛け人気を博す。だが、資本主の裏切りや、女性問題で私生活はトラブルが続出。そんな中、代役で出演した舞台が成功、いつも献身的に尽くしてくれた秘書ナンへの愛にやっと気が付くのだった……。J・キャグニーが若かりし頃に培ったダンスの腕前を披露。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・仁義なき戦い／謀殺", "新・仁義なき戦い／謀殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「ゴーストシャウト」の高橋克典がインテリ・ヤクザを好演するアウトロー活劇。頭脳派の弟分と武闘派の兄貴分。二人の友情と訣別を軸に、激化する跡目争いの行方を硬質な叙情で描ききる。大阪、尾田組の二代目候補は、ビジネスの目利き矢萩と、その兄貴分で血気盛んな藤巻と目されていた。まだ引退したくはない組長の尾田は、二人が共倒れになるよう裏で糸を引いていく。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOO TIRED TO DIE", "ニューヨーク・デイドリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "金城武が米国で初主演を果たした異色作。M・ソルヴィーノ扮する死神に出会った彼が自身の運命に向き合い、アイデンティティーを模索する姿を、様々な人種の歴史的背景を織り交ぜながら描く。両親の仕送りでニューヨークで暮らす日本人青年ケンジの前に、度々夢に出てきた美女デスが現れる。彼女の正体は死神で、ケンジはあと12時間の命と告げられ、彼は戸惑いを隠せない。その後、ドイツ人女性との恋に破れたり、アジア人女性に心惹かれるなどして街をさまよう彼は、やがて生まれて初めて自分の意思で、ある決断をする。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フラガール", "フラガール", "", "", "http://image.eiga.k-img.com/images/movie/1435/315x210.jpg?3893", 120, "昭和40年代初頭に福島県いわき市の町興しとして作られた“常磐ハワイアンセンター”の誕生秘話を映画化した感動コメディ。石炭から石油へとパワーシフトがされる中、いわき市の炭坑も次々と閉山。そこで市民たちは、町興しとして“常磐ハワイアンセンター”の建設を計画。施設の目玉として、フラダンスショーを取り入れることになり、東京からはダンス教師を呼び寄せ、町からは踊り子を集めてレッスンを始めようとするが……。監督は「69 sixty nine」の李相日。", "2006-09-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIGADE DES MOEURS", "パリ特捜刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "フランス娯楽映画のベテラン、マックス・ペカスが、流行のスプラッタ描写も取り入れて描いたポリス・アクション。娼婦の連続殺人事件の犯人一味を追っていくうちに刑事のジェラールは、妹を殺され、恋人を人質に取られてしまう。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代やくざ・与太者仁義", "現代やくざ・与太者仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "貧民窟で育った浩一・五郎・徹の３兄弟は、それぞれの夢を求めて都会の中で生きてきた。やがて、五郎が４年ぶりで東京へ戻ると、恋人の美佐は暴力団幹部の兄・浩一の妻になっており、弟は仕事の失敗で組から追われる身となっていた……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hereafter", "ヒア　アフター", "", "", "http://image.eiga.k-img.com/images/movie/55325/315x210.jpg?3524", 129, "巨匠クリント・イーストウッドが、死後の世界にとらわれてしまった3人の人間の苦悩と解放を描いたヒューマン・ドラマ。サンフランシスコに住む元霊能者で肉体労働者のジョージ、臨死体験をしたパリ在住のジャーナリスト・マリー、兄を亡くしたロンドン在住の小学生マーカスの3人が、互いの問いかけに導かれるようにめぐり会い、生きる喜びを見出していく姿を描く。脚本は「クイーン」「フロスト×ニクソン」のピーター・モーガン。出演はマット・デイモン、セシル・ドゥ・フランス。", "2011-02-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恐怖のヤッちゃん", "恐怖のヤッちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ラジオ番組『三宅裕司のヤングパラダイス』の１コーナー“あなたも体験！恐怖のヤッちゃん”に寄せられたエピソードを中心に、新興学園都市に引っ越してきたヤッちゃん軍団のハチャメチャぶりを描いたギャグ映画。目つきの悪い(!?)素人が多数参加。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太平洋の翼", "太平洋の翼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "敗戦色が濃くなり始めた頃。無敵を誇った零戦も米軍の新鋭機の前に歯が立たず、大本営は特攻隊による攻撃しかないと主張した。一方、千田航空参謀は新鋭機紫電改を完成させ、精鋭を集め制空権の奪還を計画した。３人の優秀な戦闘機乗員が紫電改を駆って米軍に向かっていくが……。戦争映画大作にして航空アクションの会心作。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER　UNTERGANG", "ヒトラー／最期の12日間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "アドルフ・ヒトラーの秘書だった女性の告白を基に、史上最悪の独裁者の実像に迫った問題作。ドイツ映画界がタブー視してきたヒトラーの“人間性”を探り、ヨーロッパで賛否両論を呼び起こした。ヒトラーの外見、話し方などを徹底的に研究した名優ブルーノ・ガンツの渾身の演技も圧巻。第二次大戦末期、ソ連軍の猛攻によってドイツ軍は疲弊し、ヒトラーと側近は地下要塞に避難。ヒトラーはひたすら反撃命令を下すが……。", "2005-07-09", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カケラ", "カケラ", "", "", "http://image.eiga.k-img.com/images/movie/54945/315x210.jpg?4558", 107, "奥田瑛二の長女・安藤モモ子の初監督作で、原作は桜沢エリカによる少女コミック「ラブ・ヴァイブス」。主演は「愛のむきだし」の満島ひかり、中村映里子。平凡な女子大生のハルは、セックスにしか興味のない彼氏との心の通じ合わない関係に疑問を抱いていた。そんなある日、ハルは喫茶店で、人の身体のパーツを作るメディカルアーティストのリコに出会う。ともに時間を過ごすうち、リコは大胆にもハルに好意を寄せるが、ハルはそんなリコに戸惑いを隠せずにいた。", "2010-04-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青い車", "青い車", "", "", "http://ecx.images-amazon.com/images/I/41PjEKj4yfL._SL210_.jpg", 90, "よしもとよしともの同名漫画を「タイムレスメロディ」の奥原浩志が映画化。アイデンティティーを求めてさまよう若者特有の繊細な心情を、静かな映像の中、リアルに描き出す。ARATA、麻生久美子、宮崎あおいら若手俳優陣が、言葉では言い表せない複雑な心の声を仕草や視線で巧みに表現。漠然としたイラ立ちを抱えている25歳のリチオは、恋人アケミの妹このみと関係を持ってしまう。ある日、アケミが交通事故で死亡し……。", "2004-11-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOY　OF　MADNESS", "ハナのアフガンノート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "イランの名監督モフセン・マフマルバフの末娘ハナが、13歳にして隣国アフガニスタンで撮った異色ドキュメンタリー。22歳の映画監督サミラ・マフマルバフは、新作を撮るためにアフガニスタンのカブールで主人公の父親役を探していた。出演を依頼したハジという老人は始め気軽に了承したが、途中から何かと理由をつけて役を降りてしまい……。元々姉サミラの新作「午後の五時」のメイキングとして始まった企画だったが、その内容はメイキングだけにとどまらず、戦後を生きるアフガンの人々の混乱、怖れ、好奇心、希望を深く切り取った作品に仕上がっている。", "2003-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELIZABETH", "エリザベス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "アカデミー賞で作品賞を始め６部門にノミネート。「女盗賊プーラン」で国際的に注目されたインド人監督S・カプールが、生涯独身を通し、“ヴァージン・クイーン”と呼ばれたエリザベス一世の若き日を描く傑作だ。新教と旧教の対立が激化する16世紀のイングランド。私生児として生まれたエリザベスは、義姉の死後、25歳で女王の座につく。旧教徒派が女王失脚を画策する中、彼女は国教を新教に統一、毅然とした態度で国を統一していく。多くの敵に囲まれた死と隣り合わせの中で女王として威厳を保ち続ける一方、彼女は主馬頭のロバート・ダドリーとの愛を深めていくのだが……。裏切りと陰謀、恋と野望が交錯するドラマを、力強くかつスピーディーに描く手際のよい演出が光る。運命にほんろうされながら国民の偶像となる決意をする主人公を、C・ブランシェットが冷たい美しさの中に風格を漂わせて熱演。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エコエコアザラク B-page", "エコエコアザラク B-page", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "人気コミック「エコエコアザラク」の劇場版第6弾。同時公開の「R-page」に続き、黒魔術を駆使する主人公・黒井ミサが“破壊の悪魔・エゼキエル”に立ち向かう姿を描く。エゼキエルとの戦いが繰り広げられた町を再び訪れたミサは、バラバラ死体が発見される事件が続発していることを知らされる。その犯人は、1年前に起きた事故で命を落としたはずのカリスマモデル、ミリスではないかと噂されており……。", "2006-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lawrence of Arabia", "アラビアのロレンス", "", "", "http://ecx.images-amazon.com/images/I/51ENhJArtYL._SL210_.jpg", 202, "アラブの民から英雄と称えられたT・E・ロレンスの激動の半生を「戦場にかける橋」の製作サム・スピーゲル＆デビッド・リーン監督が壮大なスケールで映画化した歴史スペクタクル。利権を狙って南下するトルコ軍とアラブ人が対立する第1次大戦中のアラビア半島に、大英帝国が介入。英国の天才戦略家ロレンス（ピーター・オトゥール）はばらばらだったアラブ人を統率してトルコ軍にゲリラ戦を仕掛けていくが……。62年度のアカデミー賞では作品賞、監督賞を含む7部門で受賞した。", "1963-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLASH GORDON: THE PERILFROM PLANET MONGO", "フラッシュ・ゴードン 謎の惑星モンゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "悪の権化ミンが再び姿を現わし、惑星モンゴの緑園の地アーボリア王国を狙う。ひそかにモンゴ星へ侵入したフラッシュ・ゴードンたちは、なんとか計画を阻止、自らミンの宮殿へと向かい戦いを挑む。登場と当時に11ヵ国語に訳され世界を魅了したSFコミックスの映画化で、日本でも人気を博した。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空山霊雨", "空山霊雨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "気品ある独特の映像美で世界的評価を受けるキン・フーの傑作。時代は明朝中期、とある仏教寺院を舞台に経典の奪い合いを繰り広げる人間たちのドラマアクション。同年制作の「山中傳奇」とほぼ同じロケ地で同時撮影された。映像美に娯楽アクションをほどよく織り交ぜた、完成度の高い作品である。", "1979-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裸足のピクニック", "裸足のピクニック", "", "", "http://ecx.images-amazon.com/images/I/213EYJ6QZ1L._SL210_.jpg", 92, "８ミリ映画「雨女」で、1990年度PFFアワード・グランプリを受賞した、矢口史靖監督の劇場用映画デビュー作（第７回PFFスカラシップ作品でもある）。大学生とつき合いながら、親に内緒で自動車教習所にも通う、今時の普通の女子高校生、鈴木純子。彼女が定期券でのキセル乗車発覚をキッカケに、次から次へと不幸にみまわれる様を、快テンポで描いたコメディ。低予算（２千万円）映画の持つ負のリスクをはるかに飛び越え、映画の面白さをラストまで持続させる矢口監督の演出は実に見事。どんな不幸な状況でも生命力を感じさせる、純子に扮した芹川砂織も魅力的。", "1993-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("出来ごころ", "出来ごころ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "坂本武演じる、どこか頼りないが人情には厚い男、喜八を主人公にした作品が小津安二郎には多くあり、いわいる“喜八もの”と呼ばれているが、この「出来ごころ」はその第１作にあたる。小津は、サイレント期はナンセンスなドタバタ・コメディを多く手掛け、後期は家族の人間関係を中心に独自のドラマを生み出したが、この“喜八もの”はその過度期にあたるといえるだろう。長屋に住む気のいい男、喜八は、ある日いわくありげな少女・春江が道をさ迷っているのを見つけ、めし屋のおかみに預ける。喜八とその相棒、次郎は次第に春江に心惹かれていく。小津の中期を代表する名作である。", "1933-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死闘の伝説", "死闘の伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "木下作品としては珍しくアクション映画的要素を持った作品。太平洋戦争末期の北海道を舞台に、疎開してきた園部家の娘に村長の息子との縁談が持ち込まれる。だが村長の息子は大陸で残虐行為をしていたため、園部家はこれを断る。以来、村人たちの園部家への嫌がらせが始まり……。異色の戦争寓話でもある。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES MAL PARTIS", "続 個人教授", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "第二次大戦末期、15歳のドニ少年は病院慰問の帰りに美しい尼僧クロティルドに出会う。彼女は10歳も年上だが、二人の間には愛が芽ばえ、やがて甘美な暮らしが始まる……。ロッシー監督自身の処女小説をもとにした自伝的色彩の強い作品。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHE’S SO LOVELY", "シーズ・ソー・ラヴリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "インディペンデント映画の雄J・カサヴェテスが遺したシナリオを、息子のニックが映画化したラブ・ストーリー。荒れた生活を送りながらも深い絆で結ばれたエディとモーリーン。ある日、モーリーンが隣人にレイプされたことに激怒したエディは、精神病院に収容されてしまう。やがて10年の歳月が流れ、退院したエディはモーリーンが別の男と家庭を持っていることを知る……。ストーリーだけを読めば重苦しい内容だが、映画はエキセントリックなユーモアが漂う仕上がり。とりわけ三角関係に陥った主役３人が顔を合わせるクライマックスは、冗談のようなシチュエーションがコミカルに描かれ、結末も奇妙に晴れやかな後味を残すものになっている。", "1997-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メサイア", "メサイア", "", "", "http://image.eiga.k-img.com/images/movie/56785/315x210.jpg?1409", 92, "ライトノベル作家・高殿円の原案によるコミック「メサイア　聖域蝟集」（角川書店刊）を、「デスノート」の金子修介監督が実写映画化。国のため命をかけて任務を遂行するスパイ「サクラ」の一員である海棠鋭利は、テロリストとされる島田朝和の弟・文和を監視するため全寮制高校「聖永学園」に潜入。指令どおりに文和に近づくが、次第に文和に対して友情を抱きはじめる。そんな折、文和に朝和から暗号めいたメールが届き……。脚本を「機動戦士ガンダム00」の黒田洋介が担当。主演は若手俳優集団「D2」の荒井敦史。", "2011-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニッポンの大家族 Saiko! The Large family 放送禁止　劇場版", "ニッポンの大家族 Saiko! The Large family 放送禁止　劇場版", "", "", "http://image.eiga.k-img.com/images/movie/54685/315x210.jpg?3242", 0, "「アリゾナの子ら」「サハラおじさん」など、世界中の家族をテーマにドキュメンタリー製作を行うカナダのベロニカ・アディソン監督が、深刻な小子化問題を抱える日本の家族を描いたフェイク・ドキュメンタリー。埼玉県所沢市で暮らす大家族・浦一家は、一見明るい健康的な家庭に見えるが、長女の家出や長男のひきこもり、家庭内暴力など様々な問題を抱えていた。", "2009-07-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN ON THE FOURTH OF JULY", "7月4日に生まれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "ベトナム戦争の英雄として名を刻んだロン・コヴィック。だが祖国のために戦った彼は、負傷がもとで車椅子の余生を強いられる。かつて愛した祖国に強い怒りを抱くロン。戦争はいったい何だったのか……。「プラトーン」に続いて、ストーン監督が描くもう一つの傷ついたアメリカ。実話を映画化した話題作。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents うそ　「カノジョは大丈夫」", "桃まつり presents うそ　「カノジョは大丈夫」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 32, "", "2010-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADULTE｀RE：MODE D’EMPLOI", "不倫の公式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ベルトラン・タヴェルニエの「ラウンド・ミッドナイト」やアンジェイ・ワイダの「ヴィルコの娘たち」に出演するなど、役者としても活躍していたC・パスカルが手掛けた異色の恋愛ドラマ。ともに建築家であるブリュノとファビアンヌの夫婦は、参加したコンペの結果待ちでイライラしていた。ブリュノはプレッシャーから強い刺激を求め、アングラな風俗クラブで怪しいセックスにひたる。一方、ファビアンヌは、夫の親友シモンと一夜をともにしてしまう。そんな妻の行為に気付いたブリュノは、妻の浮気は許しつつも、親友の裏切りは許せなかった。やがて、彼らのモメ事を冷淡に眺めていたシモンの同居人サラも騒動に巻き込まれていき……。セックスと愛、それに友情について起こる４人の男女の葛藤を、24時間の出来事の中に淡々と描く。作品完成後の翌年、入院先の病院で監督が自殺したことが大きく話題となった。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EARTH VS THE SPIDER", "吸血原子蜘蛛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "1950年代に巨大怪獣もの、特に大グモ映画が流行したことがあったが、なかでもこのジャンルの第一人者であるB・I・ゴードンによる本作はその決定版といえよう。ある町に突然変異した巨大グモが出現。一時、仮死状態で捕らえられたクモは、町に運びこまれたとたん生き返って暴れ出し、町中を破壊し始めた。DVDは「アーコフ・ライブラリー 怪奇モンスターズ・セレクション(2)」に収録。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タカダワタル的ゼロ", "タカダワタル的ゼロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "カリスマ的人気を誇りながら05年に急逝したフォークシンガー、高田渡。03年に制作されたドキュメンタリー映画「タカダワタル的」の原点となる01年大晦日の年越しライブを中心に、再び彼の魅力に迫ったドキュメンタリー。これが最後となった泉谷しげるとの本格共演や、約30年ぶりに発見された72年のドキュメンタリー「吉祥寺発赤い電車」の貴重な演奏シーンなど見所満載。監督は「口裂け女」の白石晃士。", "2008-05-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUST　MARRIED", "ジャスト・マリッジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "全米初登場１位を獲得したロマンティック・コメディ。ビーチでの偶然の出会いから恋に落ち、あれよあれよと結婚式を挙げた若い男女が、ハネムーン先のヨーロッパで次々とトラブルに見舞われていく。主演カップル、A・カッチャー&B・マーフィの親近感あふれるコミカル演技が魅力的だ。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジョジョの奇妙な冒険　ファントムブラッド", "ジョジョの奇妙な冒険　ファントムブラッド", "", "", "http://image.eiga.k-img.com/images/movie/33855/315x210.jpg?4824", 90, "累計発行部数7000万部を突破した荒木飛呂彦の人気コミック「ジョジョの奇妙な冒険」。“人間賛歌”をテーマに描かれ続けてきた壮大な物語の原点となる第1部「ファントムブラッド」をアニメ映画化。19世紀末のイギリスを舞台に、太陽のエネルギー“波紋”の力を持つ貴族ジョナサン・ジョースターと、古代アステカ文明に伝わる“石仮面”の力で邪悪な吸血鬼と化したディオ・ブランドーの因縁の対決を描く。", "2007-02-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROADCAST NEWS", "ブロードキャスト・ニュース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "「愛と追憶の日々」でアカデミー賞５部門を制覇したブルックス監督が、４年をかけて完成させた意欲作。今、世界中で最高の視聴率を誇っているニュース番組の数々。ここ、ワシントンにあるニュース専門のネットワークTV局では、多勢のスタッフたちが戦場さながらの多忙な毎日を送っていた。なかでも手腕を発揮するのが、若手女性プロデューサー、ジェーンと、相棒の気鋭ライター＆レポーター、アローンだった。そんな二人の前に、ある日、偶然と幸運に恵まれレポーターに昇進したトムが出現。ジェーンは、スマートでカリスマ的魅力を持つトムに心惹かれ、彼女を心ひそかに慕うアローンは面白くない。一方トムは、初めての仕事で大成功を収め、一躍レポーターのスター的存在となるが……。知られざるTV局の内幕ドラマに、セックスの介在しないトライアングル・ラブを絡めた巧妙な演出に監督の才が光る。ニューヨーク批評家協会最優秀作品賞ほか５部門を受賞。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTO THE SUN", "イントゥ・ザ・サン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アクション・スター、スティーヴン・セガールが日本で大暴れを演じた犯罪活劇。怖いもの知らずの凶暴な新興ヤクザを相手に、日本語をまじえて熱演をみせる。大沢たかお、寺尾聰ら日本の演技派アクターとの共演に加え、日本語のセリフやセガール映画には珍しいプロポーズ・シーンにも注目。都知事候補暗殺事件を追う元CIA工作員トラヴィス。財界や裏社会から情報を得た彼は、やがて凶暴な新興ヤクザと対決することに……。", "2005-11-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Falbalas", "偽れる装い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「穴」「現金に手を出すな」など、ヌーベル・バーグの開祖とも言えるフランスの名匠ジャック・ベッケル監督の長編第3作。パリで評判の婦人服デザイナーでプレイボーイのフィリップは、友人ダニエルの婚約者ミシュリーヌに恋をする。ダニエルがリヨンへ行っている間に2人は結ばれるが、想いがすれ違い、ミシュリーヌは結局ダニエルの元に戻ってしまう。そして彼女は自分の花嫁衣裳のデザインをフィリップに依頼するのだが……。", "1945-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L change the WorLd", "L change the WorLd", "", "", "http://image.eiga.k-img.com/images/movie/53265/315x210.jpg?2213", 129, "週間少年ジャンプ（集英社）に連載された人気コミックを映画化し、大ヒットを記録した「デスノート」「デスノート the Last name」の天才探偵Lが主人公のスピンオフ。L役は「デスノート」でブレイクした松山ケンイチが引き続き演じる。監督は「ザ・リング」の中田秀夫。世界を巻き込んだ“キラ事件”を解決するため、ある決断を下したLに残された時間は23日間。そんな彼に、人類の存亡を賭けた新たな事件が舞い込んでくるのだが……。", "2008-02-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUNCTION", "ジャンクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "貧しい白人労働者のルイスは、大金持ちの黒人工場主トーマスの偏見とささいな勘違いから、突然クビを言い渡される。その結果、職だけでなく家や愛する家族をも失い、絶望の淵に立たされたルイスは、トーマスから金を奪おうと彼を誘拐。だが、あてもなく町をさまよう二人の間に奇妙な友情が生まれ始めるのだった。アメリカ社会に根強く残る人種差別を逆手にとり、黒人に差別される白人という発想で描いたアクションドラマ。「ブルックリン最終出口」の脚本で知られる日系のD・ナカノの初監督作品。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Total Recall", "トータル・リコール", "", "", "http://ecx.images-amazon.com/images/I/21G55TS9VWL._SL210_.jpg", 113, "フィリップ・K・ディックの短編小説「追憶売ります」をもとに、ポール・バーホーベン監督、アーノルド・シュワルツェネッガー主演で描いたSFアクション大作。西暦2084年、地球の植民地となっていた火星では、エネルギー鉱山の採掘を仕切るコーヘイゲンとそれに対抗する反乱分子の小競り合いが続いていた。一方、地球に暮らす肉体労働者のダニエル・クエイドは、毎晩行ったこともない火星の夢を見てうなされていた。夢が気になるクエイドは「火星旅行の記憶を売る」というリコール社のサービスを受けることに。しかし、それをきっかけに今の自分の記憶が植えつけられた偽物であり、本当の自分はコーヘイゲンの片腕の諜報員ハウザーだったと知る。クエイドは真相を知るため火星に旅立つが、真実を隠匿するコーヘイゲンに命を狙われ……。", "1990-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Meet Dave", "デイブは宇宙船", "", "", "http://ecx.images-amazon.com/images/I/51ZA5qZVxuL._SL210_.jpg", 91, "人間の男そっくりな宇宙船に乗って地球に降り立った小さな宇宙人たちが巻きおこす騒動を、エディ・マーフィ主演で描いたSFコメディ。ある晩、ニューヨークで暮らす少年ジョシュの部屋に小さな隕石が落ちてくる。それから3カ月後、今度は人間型の宇宙船が自由の女神の近くに落下。その中には、故郷である惑星ニルの危機を救うため地球へやって来た100人の小さな宇宙人がいた。やがて彼らは、偶然にもジョシュと彼の母ジーナに出会う。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUGH ENOUGH", "ザ・ファイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "カントリー歌手を目指すロングは、バーでタチの悪い客をノックアウト。それがもとでアマチュア・ボクシングのプロモーターに目をかけられ“歌う戦士”として人気を博す。そのロングにも八百長の罠が仕掛けられる。好漢W・オーツの遺作となった快作。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("卍〈まんじ〉", "卍〈まんじ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "谷崎潤一郎の名作エロス文学を、異才・井口昇が映画化。かつて若尾文子や樋口可南子が演じた同性愛にのめりこむヒロインに扮したのは、写真家・荒木経惟とのコラボレーションでも知られる秋桜子と「花と蛇２」の不二子。原作の世界を極端なまでに突き詰めた独特の語り口が印象的だ。縁談を破談にするために、自分の恋人のように振る舞ってほしいと妖艶な女・光子に頼まれた人妻・園子。意気投合した二人は同性愛者を演じるうち、いつしかその関係にのめりこんでいく。それを知った園子の夫は、彼女に光子と会うことを禁ずるが……。", "2006-03-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長意外伝 大暴れ次郎長一家", "次郎長意外伝 大暴れ次郎長一家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "灰神楽の三太郎のドシな活躍ぶりを描くお笑いシリーズの第３作。廻船問屋の駿河屋を密貿易の罪に陥れた番頭を三太郎が探すまでの騒動が展開する「裸宿中の巻」。そして、次郎長一家が吉良の仁吉の喧嘩を助けることになり、三太郎が大暴れをする「荒神山の巻」の２話構成になっている。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林寺拳法", "少林寺拳法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "和製カンフー・スター第１号の千葉真一が“喧嘩坊主”といわれた宗道臣に扮し、敗戦の傷痕生々しい日本に、少林寺拳法を定着させていく姿を描いたアクション作品。縦横無尽にバッタバッタと悪をなぎ倒す千葉のカンフー・シーンは必見。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クライマーズ・ハイ", "クライマーズ・ハイ", "", "", "http://image.eiga.k-img.com/images/movie/53505/315x210.jpg?2488", 145, "1985年8月12日、乗客乗員524名を乗せた日本航空123便が、群馬県多野郡上野村の御巣鷹山に墜落。群馬の有力地方新聞・北関東新聞社の記者・悠木は、事件の担当デスクに任命され、混乱する状況や次第に露わになっていく社内の人間関係の軋轢に押しつぶされそうになりながらも、未曾有の大惨事の真実を伝えるために奔走するが……。ベストセラー作家・横山秀夫の原作小説を、「突入せよ！『あさま山荘』事件」の原田眞人監督が映画化。主演は堤真一、堺雅人ら。", "2008-07-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲンセンカン主人", "ゲンセンカン主人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "カルト的な人気を誇る漫画家、つげ義春の原作を、14年ぶりに映画界に復帰した石井輝男が映像化。売れない漫画家の淡々とした日常を、４つのエピソードに分けて、エロチシズムを漂わせながら描き出す。大のつげファンだという佐野史郎が、主人公に扮して独特の個性を発揮。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUR OF DUTY", "NAM 地獄の突破口", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1967年のベトナム。その最前線で戦うアメリカ軍小隊が、鬼軍曹ジークとラスティ中尉のもと厳しい訓練をする日々を送っていた。しかしそんな日々の中で彼らは人間の尊厳や死、敵味方を越えた温かい友情を知ることになる。監督以下TV畑のスタッフで製作された青春群像劇。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤穂浪士", "赤穂浪士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "東映が設立10周年を記念して、オールスター・キャストで製作した忠臣蔵。大石内蔵助に片岡千恵蔵、内蔵助と友情で結ばれる吉良方の知恵袋・千坂兵部を市川右太衛門が演じる。松田定次は、世評に名高い1956年版をはじめ、1959年、本編と、戦後３度“忠臣蔵”ものを手掛けている。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES NUITS DE LA PLEINE LUNE", "満月の夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "フランス・ヌーヴェル・ヴァーグの大御所ロメールが描く、ファッショナブルでセンシティブな現代パリっ子恋愛事情。“喜劇とことわざ”シリーズ第４作。愛に飢えながら、束縛を嫌うルイーズは、パリに自分だけの部屋を持つ。ところが彼氏に恋人ができたことを知って……。独特の長回しカメラと洗練された台詞が、パリの空気を醸し出すフランス映画の真骨頂。P・オジェの遺作。DVDは「エリック・ロメール コレクション(5)」に収録。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUICK SILVER", "クイックシルバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "若き株式仲買人ジャックは、一日のうちに全財産を失い、競走用自転車に股がりスピードに命を懸けるメッセンジャー・ボーイに転身した……。「フットルース」で人気を得たK・ベーコンが、スピード感あふれる自転車テクニックを披露、新しい魅力を見せた青春ドラマ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("首席執行官", "CEO 最高経営責任者", "", "", "http://ecx.images-amazon.com/images/I/314295A1Y1L._SL210_.jpg", 116, "世界中でその名を知られる中国の大手家電メーカー「ハイアール」の最高経営責任者チャン・ルエミンの成功までの軌跡をドキュメンタリータッチで描いた社会派ドラマ。倒産寸前だった町工場を、独創的な経営手腕によってわずか17年で世界的ブランドへと成長させた若き最高経営責任者の苦悩と挑戦の日々をドラマチックに描きだす。メガホンを取るのは、「古井戸」「變臉 （へんめん）」のウー・ティエンミン監督。", "2004-12-11", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あおげば尊し", "あおげば尊し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "カリスマ放送作家兼タレントのテリー伊藤が、役者として映画初主演を果たした感動作。末期ガンの宣告を受けた父親を、光一は自宅で介護することに決める。そんな中、彼が受け持つ小学校５年のクラスでは“死”が話題となっていた……。“命の重さと教育”というシリアスなテーマを、静けさと温もりが交錯する卓越した画面作りで紡ぎ出す。", "2006-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Senso", "夏の嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "　オペラ的な要素とリアリズムを融合した絢爛豪華な時代絵巻。19世紀後半、オーストリア支配下のベネチアで出会った伯爵夫人と美しい青年将校。青年に恋をした伯爵夫人は、彼のために軍資金を横流しする。一途な愛を貫く伯爵夫人を演じたアリダ・バリは、ハリウッドでも活躍し、「第三の男」の恋人役で知られる。青年将校役は、ルキノ・ビスコンティの初監督作「郵便配達は二度ベルを鳴らす」で流れ者の主人公を演じたマッシモ・ジロッティ。", "1954-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WAR ROOM", "クリントンを大統領にした男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1992年、クリントンの大統領選挙キャンペーンの舞台裏に入り込み、予備選挙から約10ヵ月後の当選祝賀パーティまでを記録したドキュメンタリー。選挙参謀 J・カービルと報道担当のG・ステファノポロスを中心に、民主党全国大会、ジョージ・ブッシュやロス・ペローとの討論、そして選挙間際の異常なまでに緊迫した日々に向けて作戦を立て、実行していく様を追う。アーカンソー州知事というだけで知名度の低かったクリントンが、なぜ現職ブッシュを破り、勝利を収めたかが当時、表に出ることのなかった二人の活躍の中に描かれる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジーンズブルース・明日なき無頼派", "ジーンズブルース・明日なき無頼派", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "中島貞夫監督、梶芽衣子・渡瀬恒彦主演の青春アクション。水商売あがりの女・聖子と高利貸しを殺して奪った金を独り占めした次郎。偶然出会った二人が、金の強奪を続け、最後は警察の銃弾に倒れるまでを描いた、東映版「俺たちに明日はない」のキャラ“ボニーとクライド”。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PSYCHO", "サイコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "女が恋人のために４万ドルの金を横領し、一人の青年が管理するさびれたモーテルに投宿する。そこで悪夢のような事件が始まった……。異常心理をテーマにしたヒッチコックの恐怖スリラーで、最後のハッとさせる結末まで、そのテクニックの冴えは見事。公開当時、ラスト30分間、観客の入場を止めたことでも話題になった。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPRING BEARS LOVE", "春の日のクマは好きですか？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "CM界出身の新人監督がポップな演出を披露したロマンチック・コメディ。主人公は、夢見がちでつい常識外れの行動をとってしまうスーパー店員の女の子。そんな乙女チックなヒロインが現実と妄想の間を行き来する様が笑いを誘う1本だ。スーパーのレジ係として働くヒョンチェは、幼なじみのドンハの恋心に気づかないちょっと鈍感な女の子。彼女は図書館の画集に愛のメッセージを書いた謎の人物を探し始めるが……。", "2006-06-10", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSEKEEPING", "シルビーの帰郷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "米大陸北部の小さな町を舞台に、自由奔放に生きる女シルビーと、彼女の姪である二人の少女の奇妙な交流を、ユーモラスに愛情深く描出。常識をはみ出し夢に生きる彼女たちの姿が共感を呼ぶ。第２回東京国際映画祭で最優秀脚本賞・審査員特別賞に輝いた。", "1987-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REALITY BITES", "リアリティ・バイツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "すべてが混沌とした1990年代、あらゆる選択肢の中で自らの価値観を見いだそうとする若者たちの姿をリアルに描いた“ジェネレーションX・ムービー”。物語は、優秀な成績で大学を卒業し、TV局のADとして働くリレイナを中心に、その友人でGAPで働くビッキー、定職に就かない男友だちトロイとサミーの４人の日常を描いていく。リレイナは、マンネリな仕事にうんざりしながらも、同世代の本当の姿を表現したドキュメンタリーを作ることを夢にしているのだが……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕は妹に恋をする", "僕は妹に恋をする", "", "", "http://image.eiga.k-img.com/images/movie/33815/315x210.jpg?4791", 122, "累計600万部を記録した青木琴美の同名コミックを、松本潤＆榮倉奈々主演で映画化。高校3年生の郁は、幼い頃からずっと仲の良かった双子の兄・頼の態度が最近冷たくなったことに悩んでいた。ある夜、頼はベッドで眠る郁にキスし、自分の本当の想いを打ち明ける。郁も頼の気持ちを受け入れ、ふたりは結ばれるが……。報われることのない禁断の恋に落ちた兄妹の、切なくも美しいラブ・ストーリー。監督は「blue」の安藤尋。", "2007-01-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香港パラダイス", "香港パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "香港を旅行中のツアー・コンダクターが、秘宝の盗難騒ぎに巻き込まれていくコミカルなアクション映画。斉藤由貴がTV『スケバン刑事』以来久々の地に着いたアクションを披露し、そのドタバタぶりが魅力的な作品となった。ひとくせもふたくせもある俳優たちを要所要所に散りばめた金子修介演出も快調だ。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORTADERO ＆ FILEMON THE BIG ADVENTURE", "モルタデロとフィレモン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "スペイン国民に40年以上も親しまれている人気コミックを初めて実写映画化し、同国で爆発的な大ヒットを飛ばしたスパイ・コメディ。エゲツないギャグ満載の怪作ながら、スペインのアカデミー賞たるゴヤ賞で５部門を制した型破りな一編。人間のやる気をなくす最新兵器DDTが盗まれ、おバカな独裁者の手に渡る。諜報機関TIAは敏腕捜査官を招くが、迷惑者のモルタデロとフィレモンがしゃしゃり出てきてしまい……。", "2006-04-15", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ACCOMPAGNATRICE", "伴奏者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ドイツ占領下の大戦中のパリ。オペラ歌手イレーヌのピアノ伴奏者ソフィはイレーヌに憧れながらも聴衆から喝采を受けることのない自分の立場に不公平を感じていた。ソフィは複雑な愛憎を抱えたまま、国外脱出するイレーヌと運命をともにする……。トリュフォーの後継者とされるC・ミレール入魂の人間讚歌。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENTRAPMENT", "エントラップメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "伝説の美術品泥棒マックを捕まえるために、保険会社の有能な女性調査員ジンは罠を仕掛けることにする。ロンドンに飛んだ彼女は同業者と偽って彼に接近、二人で新たな窃盗をやり遂げるが、それは単なる小手調べに過ぎなかった。ジンはマックに新たな計画を打ち明ける。それはコンピュータの2000年問題に対処するためICB銀行がシステムを変更する2000年１月１日に間隙を縫ってマレーシア支店から80億ドルを自分の口座に移そうというものだった……。追う者と追われる者だった二人がパートナーとなって計画する大胆な犯罪と、彼らの間の駆け引きをロマンスを絡めながら描いた洒落たサスペンス。ヒロインを演じたC・Z・ジョーンズのしなやかな演技も見どころ。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HALLOWEEN III：SEASON OF THE WITCH", "ハロウィンIII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "病院で、ある患者を残忍な手口で殺し、逃げる途中に駐車場で車もろとも爆破され死んだ犯人。ことのすべてを目撃した医師が事件を追うと、ハロウィンのマスクを作る邪悪な男に突き当たった。J・カーペンターの「ハロウィン」に続くシリーズで劇場未公開の第３作。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊豆の踊子", "伊豆の踊子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "原作は川端康成の同名の小説で、何度も映画化されているが、本作品はその最初のものである。監督は当時小津安二郎とともに松竹蒲田撮影所の若手ホープと目された五所平之助。脚本は五所ばかりでなく小津、斎藤寅次郎などの代表作を手掛け、新進気鋭の脚本家として鳴らした伏見晁。撮影は叙情的な軟調の撮影では第一人者の小原譲治があたっている。五所は喜劇やメロドラマなども数多くこなしているが、ことに田園を背景とした叙情作品に独自の境地を生み出した。この作品でも詩情あふれる小原譲治の撮影を得て、淡々とした味わい深いこの文学作品をきわめて情感豊かな美しい作品に仕立て上げている。休みを利用して伊豆を徒歩で旅している学生・水原は天城街道で旅芸人の一行と知り合った。一緒に歩くうちに水原は旅芸人の中の踊り子・薫の初々しさに魅かれ、また薫も水原の優しさに心打たれ、二人の間には淡い恋愛感情が芽ばえる。やがて一行は下田に到着し、船で東京へ帰る水原を薫は目に涙をためて見送るのだった。無声映画だが、活弁版のビデオ（IVC）も出ている。", "1933-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クルシメさん", "クルシメさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "好きになった相手を傷つけたくなる奇病を持つ女と、身体に秘密を持つ女。バイト先で出会った二人は、次第に仲を深めていくのだが……。主人公二人の愛情と葛藤を繊細に描き込み、せつなくも、救われるラストシーンまでには、笑い、アクション、陰湿な人間関係、そしてB級ホラー的要素が盛り込まれている。まさにエンターテインメント映画といえるだろう。監督は、AV監督・AV男優・舞台役者・劇作家・ライターなど、多方面で活躍する奇才・井口昇。自主映画ながら、各映画祭に招待・出品され、劇場での公開も繰り返し行われた話題作。ビデオ、DVDは「アトピー刑事」「私とクルシメさん」「俺の空」「わびしゃび」の長短編集とともに「クルシメさん・アトピー刑事　愛の井口昇劇場　1988-2003」に収録。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファイト★ガールズ", "ファイト★ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "リングで繰り広げられる戦いに挑む女性たちの姿を描くファイティング・アクション。経済崩壊した近未来の東京を舞台に、女同士の激しいバトルが炸裂する。無差別ファイティング・ショーの会場に、ふらりと現れた女性、ケイ。一目で彼女を気に入った女性オーナーは、女性だけが戦う“ガール・ファイト”を企画。元ボクサーのジャムをコーチに向かえ、ケイはリングへと向かう。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Too Tough to Die: A Tribute to Johnny Ramone", "TOO TOUGH TO DIE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "世界の音楽シーンに絶大な影響を与えてきたニューヨーク出身のパンクバンド「ラモーンズ」。2004年9月12日、ロサンゼルスで開催されたラモーンズの30周年記念トリビュート・ライブの模様を記録したドキュメンタリー。「レッド・ホット・チリ・ペッパーズ」ら超豪華メンバーがラモーンズのカバーを披露する迫力のライブ映像を中心に、ミュージシャンたちがラモーンズへの熱い想いを語るインタビュー映像などを収録。", "2007-09-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹下左膳", "丹下左膳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "丹下左膳といえばやはり戦前の伊藤大輔監督の「シェイは丹下、名はシャゼン」という独特のセリフ回しの大河内伝次郎版にとどめを刺すわけだが、本作品は監督には時代劇に定評のある松田定次が、左膳には阪東妻三郎がそれぞれ起用されて再映画化された。莫大な埋蔵金のありかを示す地図を隠してあるという“こけ猿の茶壷”をめぐって柳生源三郎、司馬一刀斉、左膳が争奪戦を繰り広げるおなじみの話。この作品のあと1950～60年代にかけて続々と丹下左膳ものが製作された。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEYOND THE LAW", "風の無法者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "二人の仲間とともに無法の限りをつくしていたカドリップは、ある事件をきっかけにノバセックという男と知り合う。鉱山で銀の延べ棒を送り出すことになり、それを盗賊団が襲った。激しい銃撃戦の末、盗賊団から延べ棒を奪ったカドリップとノバックだが、今度はかつての二人の仲間がピストルを持って銀の延べ棒を渡せとカドリップに迫るのだった。「夕陽のガンマン」のL・ヴァン・クリーフが珍しく主演した一編。", "1967-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンの森の石松", "エノケンの森の石松", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "不世出の喜劇王といわれる当時のドル箱スター・エノケンと名匠・中川信夫監督が組んだ第１作。森の石松に扮したエノケンが繰り広げる珍道中を、三味線で弾く『アロハオエ』が飛び出したり、移りいく風景のショットに浪曲を流すといった新鮮な手法で描く。特別出演の金語楼との掛け合いは爆笑。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHERE EAGLES DARE", "荒鷲の要塞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 157, "第二次大戦中、鷲の城と呼ばれる独軍強力要塞にアメリカの将軍が囚われ、英国情報部の隊長以下６名と米軍特殊部隊の中尉が救出に。だが、作戦はスパイ割り出しのため意図的に漏洩し……。スリルの連続するストーリーが魅力的な戦争冒険アクション。", "1968-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Come with the Rain", "アイ・カム・ウィズ・ザ・レイン", "", "", "http://image.eiga.k-img.com/images/movie/34185/315x210.jpg?1539", 114, "木村拓哉、ジョシュ・ハートネット、イ・ビョンホンという日米韓のスターが共演するサスペンス。他人の痛みを我が身に引き受けることができる不思議な力を持つ男シタオが失踪し、捜索を依頼された探偵クラインは、彼の足跡を追ってロサンゼルスからフィリピン、香港へと渡る。そこでクラインは、香港マフィアのボス、ス・ドンポもシタオを探していることを知るが……。監督は「青いパパイヤの香り」のトラン・アン・ユン。", "2009-06-06", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泣きぬれた人形", "泣きぬれた人形", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "当時、歌手・俳優として頂点へ向かいつつあった美空ひばりの魅力を、全編に取り入れた人間ドラマ。ガード下で貧しいながらも仲良く暮らす隆治とアヤ子の兄妹。だが、隆治はまちがって警察に捕まり、アヤ子は一人残される。スリのきみ子と知り合ったことがキッカケで、アヤ子は歌の才能に目覚めるのだが……。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPERMAN III", "スーパーマン III 電子の要塞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "シリーズ第３作。超高性能コンピュータを操作して、世界支配をたくらむ悪漢とスーパーマンが対決。敵は思考や行動なども読んでしまう恐るべきマシーンを駆使してスーパーマンの弱点を知りつくし、戦いを挑む。両者のスケールの大きい戦闘場面が見もの。リーヴの堂に入ったスーパーマンぶりも魅力。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOGAN’S RUN", "2300年未来への旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "23世紀の未来社会に繰り広げられるSF冒険70ミリ大作。そこは人口抑制政策のため、人々は30歳までしか延命が許されない、恐るべきコンピュータ支配下の管理社会だった。危機感を抱いた青年ローガンは、都市からの脱出を試みる。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海燕ホテル・ブルー", "海燕ホテル・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/57405/315x210.jpg?3474", 84, "ハードボイルド作家・船戸与一の同名小説を「実録・連合赤軍　あさま山荘への道程（みち）」「キャタピラー」の若松孝二監督が映画化した密室劇。獄中で殺された仲間の復讐を胸に誓い、ひとりの男が北国の刑務所を出所する。7年前に裏切った仲間に落とし前をつけさせるため男が向かった先で待ち受けていたものとは……。地曵豪、井浦新（ARATA）ら若松組の常連俳優に加え、モデルで女優の片山瞳が物語の鍵となる謎の女を演じる。", "2012-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実尾島", "シルミド SILMIDO", "", "", "http://image.eiga.k-img.com/images/movie/1655/315x210.jpg?3308", 135, "韓国政府による金日成暗殺計画と、その工作部隊の反乱事件＝シルミド事件を「ツー・コップス」のカン・ウソク監督が映画化。68年、北朝鮮による韓国大統領府襲撃未遂事件が発生。韓国政府はその報復として金日成暗殺計画を立案。シルミド島に死刑囚らを集め、特殊工作員として過激な訓練をさせる。が、彼らがいよいよ暗殺計画を実行しようとしたとき、政府の対北政策は大きく変化していた。主演は「オアシス」のソル・ギョング。", "2004-06-05", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美わしき歳月", "美わしき歳月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "前年の「この広い空のどこかに」と同じ監督・脚本・主演カルテットによる青春ロマン。祖母とともに花屋を営む娘と、彼女の死んだ兄の友人たちが織りなす恋と友情の物語が展開していく。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・夜霧にむせぶ寅次郎", "男はつらいよ・夜霧にむせぶ寅次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "霧深い釧路で出会った渡り鳥美容師の小暮風子と意気投合した寅さん。彼女の恋人であるサーカスのオートバイ乗りから引き離そうと奮闘する。“とらや”の隣人・タコ社長の娘役で美保純が初登場。以後、人気キャラクターとなる。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANALYZE　THIS", "アナライズ・ミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "マフィア役を得意とするR・デ・ニーロが、セルフ・パロディー的な怪演を披露するヒューマン・コメディ。彼が演じるのは、原因不明の発作に悩むマフィアのボス、ポール。このままではライバル組織や部下へのメンツが立たない彼と、しぶしぶ治療を引き受けることになった平凡な精神科医ベンのおかしな友情劇が展開していく。ふとしたことで泣いたり、怒り出すデ・ニーロのコミカル演技が絶妙。しがらみ多き現代社会ではマフィアもストレスに苦しむという愉快な１本だ。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すみれ人形", "すみれ人形", "", "", "http://ecx.images-amazon.com/images/I/41vq1mMjRwL._SL210_.jpg", 63, "失踪した少女すみれを捜し求める男たちの物語を、幻想的な映像でつづった官能ドラマ。5年前に男に襲われ、切断された右腕だけを残して姿を消した少女すみれ。彼女の兄・文月は、「すみれ」と名づけた人形を操って寄席で腹話術をしながら妹を捜し続けていた。そんなある日、妹に良く似た女を見かけて後を追った文月は、森の奥で妹の元恋人・螢介と再会する。新鋭・金子雅和監督が映画美学校の卒業制作として手がけた長編デビュー作。", "2008-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAHNFRIED／RICHARD ＆ COSIMA", "ワーグナーとコジマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "これまで数多くの映画に登場してきた19世紀の天才作曲家リヒャルト・ワーグナー。そして夫のある身でありながら、ワーグナーのもとへ走ったフランツ・リストの娘コジマ。彼女はワーグナーに永遠の愛を誓って以来15年間、妻として仕えながら彼を支配しようとしたが、彼の生活に愛人が途絶えることはなかった……。二人の愛と憎しみに満ちた結婚生活を描きながら、作曲家というより奔放に生きた人間としてのワーグナーに迫っていく。主演は「ベルリン・天使の詩」にも出演していたO・ザンダー。", "1986-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゆめこの大冒険", "ゆめこの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "女宝石泥棒・ゆめことピクニックの一行、そして謎の男が巻き起こすスラップスティック・コメディ。全編白黒サイレントで撮られた作品で、そのとどまるところを知らぬアクションのリズムは昔の連続活劇を思わせる。ミニチュア・セットまで作り上げた、にぎやかな一編。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画ドラえもん　新・のび太の宇宙開拓史", "映画ドラえもん　新・のび太の宇宙開拓史", "", "", "http://image.eiga.k-img.com/images/movie/53875/315x210.jpg?1330", 98, "1981年の劇場版第2作「のび太の宇宙開拓史」をリメイク。ある日、超空間のねじれにより、のび太の部屋の畳の下と宇宙の彼方にあるコーヤコーヤ星がつながってしまう。コーヤコーヤ星は豊かな自然と不思議な動物達で溢れていたが、コーヤコーヤ星を狙うガルタイト鉱業はある恐ろしい計画を進めていた……。ゲスト声優として「しゃべれども　しゃべれども」の香里奈、「パコと魔法の絵本」のアヤカ・ウィルソンらが参加。", "2009-03-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIAS CONTADOS", "時間切れの愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1995年のスペイン・アカデミー賞で作品・監督など８部門を受賞したラブ・ストーリー。服役中の夫を待ちながら、ドラックに溺れ娼婦まがいの生活を送っているチャロ。ある日、彼女の隣室に、報道写真家を装うバスク分離運動のテロリスト、アントニオが越してきた。すさんだ生活を送る周囲を尻目に、ひそかにテロ活動をするアントニオとチャロは、急速に惹かれ合い関係を持つ。だがその矢先、テレビでアントニオがかかわっていたテロ事件が報道され、チャロが彼の正体を知ったことから二人には悲劇が訪れるのだった。", "1994-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍鳳智多星", "あの日にかえりたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "2003年に急逝したL・チャンがデビュー初期に主演した刑事アクション＆熱血青春ドラマ。いまや世界的な映画監督として知られるW・カーウァイが共同脚本で参加している。麻薬密売事件を追う刑事だが、台湾からやって来た刑事を間違って逮捕し、代償にもてなすことに。そんな彼らはある日、スリの姉妹と知り合う。やがて彼女たちが殺し屋のサイフをスったことから組織に狙われ……。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Brown Bunny", "ブラウン・バニー", "", "", "http://image.eiga.k-img.com/images/movie/1315/315x210.jpg?2972", 90, "250ccクラスのバイクレーサー、バドは、東海岸でのレースを終え、次のレースが開催されるカリフォルニアへと移動中。その道中でさまざまな人々に出会うが、彼の心は愛していながら別れてしまったかつての恋人デイジーへの思いで溢れていた。ヴィンセント・ギャロが脚本、撮影も含めてほとんどすべてを担当、03年のカンヌ国際映画祭に出品されて激しい論争を巻き起こした話題作。カンヌ版、全米公開版とは編集が違う日本版。", "2003-11-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORCE 10 FROM NAVARONE", "ナバロンの嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "傑作「ナバロンの要塞」から17年ぶりに製作された続編。米軍特殊部隊の次なる使命はドイツ軍に包囲されたユーゴのパルチザン7000名の救出……。豪華なオールスターキャスト、ダム爆破シーンなど前作以上のスペクタクルも見ものである。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BASTARD　OUT　OF　CAROLINA", "冷たい一瞬〈とき〉を抱いて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "名匠ジョン・ヒューストンを父に持つ演技派女優A・ヒューストンの初監督作。母が交通事故で昏睡中に、この世に生を受けた私生児ボーン。母の再婚により、彼女は人並みの家庭を手に入れたかに見えたが、やがてこの継父はボーンに暴力を振るい出す……。薄幸の少女がたどる数奇な運命を描きつつ、幼児虐待や近親相姦という問題をシリアスに見つめる。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心霊", "心霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "自他ともに認める“霊界案内人”稲川淳二が、自らの経験や取材をもとに霊現象を映像化。オムニバス形式で３つのストーリーを紹介する。「旧日本軍の病院」は、新米の看護婦が消えるというウワサがある病院の話。「首なし地蔵」は、戦国の世、貧しい母親が金欲しさに恐ろしい賭けをしてしまう話で、稲川自身が監督している。「サーファーの死」は、稲川自らの経験談をもとにしたストーリー。海水浴客でにぎわう湘南海岸。そこへやって来た男女４人の恐ろしい体験とは……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("限りなく透明に近いブルー", "限りなく透明に近いブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "立川の米軍基地に近い福生の町を舞台に、麻薬と乱交のセックスに明け暮れる青年たちの無機質で空虚な日常を描いた作品。第75回芥川賞受賞作品で、ベストセラーになった村上龍の同名小説が原作。村上自身が映画用に脚色して作品づくりに臨んでいる。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FILLE DANS LA VITRINE", "飾り窓の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "落盤事故でともに坑道内に閉じ込められたことから友人になったヴァンサンとフレデリック。救助を待ち受ける間に彼らは、もし助けられたらアムステルダムの夜の街へ遊びに行こうと約束する。数日後、約束の地・アムステルダムで、フレデリックはすでに“顔”となっていた。世間知らずのヴァンサンはそこで美しい娼婦エルスと知り合う。炭鉱夫と娼婦の愛を描いたラブ・ロマンス。", "1961-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("危いことなら銭になる", "危いことなら銭になる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "紙幣用のスカシ入り和紙10億８千万円相当が強奪された。ガラスのジョー、計算機の哲、ブル健の３悪人は、紙を盗んだ奴らにニセ金作りの名人を高く売り込もうとたくらむが……。原作は都筑道夫の『紙の罠』。これを中平康が演出するアクション喜劇。宍戸錠扮する、ガラスをこする音にはまったく弱い、ガラスのジョーというキャラクターが笑わせる。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とむらい師たち", "とむらい師たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "奇抜な着想と卓越した筆力で、第58回直木賞を受賞した野坂昭如の同名小説を三隅研次監督と勝新太郎のコンビで映画化した異色作。年商700億にものぼるという葬儀業界に目をつけた男たちが、葬儀コンサルタントなるものを開業するが……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH WISH", "狼よさらば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "妻子を町のダニに殺害された平凡な中産階級の男が、銃を携えて夜のニューヨークを歩き出す。犯人探しでもなく、復讐のために手当たりしだい、町のダニ掃除をしていく。そんな彼を世間は歓迎する。ウィナーとブロンソン・コンビの手慣れたアクションで、この後、“デスウィッシュ”シリーズ全５作が作られた。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WINNING ２", "マイ・ウェイ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ヒットした第１作に続いて今回も、初老の建築会社社長と彼の家族をめぐってドラマは展開される。仕事一途な生き方をやめて家族愛に目覚めた主人公。次々と襲う不幸にめげず、大陸横断150キロ・マラソンに挑む、その姿を主題歌『マイ・ウェイ』が美しく彩る。", "1977-01-01", "67", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陸軍航空戦記 ビルマ編", "陸軍航空戦記 ビルマ編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ビルマ作戦における陸軍の活躍を綴る。イギリスに占領されたビルマ各地に幾度も繰り返される空軍の爆撃。決死の撮影によって映し出された大迫力の空中戦が話題となった航空記録映画の代表作。滑走路建設に自ら協力するビルマ人などの映像も映し出されている。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い賭博師・ダイスで殺せ", "黒い賭博師・ダイスで殺せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "神戸の友人のもとに身を寄せていた氷室浩次は、マカオ賭博団の“殺し屋”の異名を持つギャンブラー、ヌイ・サップの挑戦を受け、瀬戸内海の毒ガス島で1000万円を賭けて勝負を開始する……。二谷英明がヌイ・サップに扮し手慣れたライバルぶり。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELL IN THE PACIFIC", "太平洋の地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "太平洋戦争末期、太平洋の孤島を舞台に、そこで偶然に出会い、お互い生存のために共同生活をよぎなくされた日本兵と米国兵の確執を描く。思想信条も異なる二人の男たちの内に秘めた“静かな戦争”をブアマンがシャープな映像感覚で捉えた異色の戦争映画。", "1968-01-01", "1,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PONETTE", "ポネット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「家族生活」「15歳の無秩序な妖精」などで、少女の揺れ動く心情を繊細に描いたJ・ドワイヨンによる感動作。母親の死に直面した４歳の少女が現実を受け入れるまでを、少女の視点を通して描く。純粋でひたむきな姿の主人公をきわめて自然体で演じたV・ティヴィソルの好演、子供たちの素直で残酷な会話が持つリアリズムが、作品にみずみずしさを与えている。プロヴァンスの田舎町。母親を自動車事故で亡くしたポネットは、死の意味が分からない。いつかママに会えると信じ続け放心状態でいる彼女を、パパは“ママは天国に行って、もう戻ってこない”と慰める。ママと話をしようと、様々な努力を試みるポネットは、何も変わらない現実に心を痛めるが、やがてそんな彼女に奇跡が訪れる……。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMERALD CITY", "最も危険な悪女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "華やかな映画界で脚本家としての成功を収め、妻と二人の子供に囲まれた幸せな生活を送るコリーン。しかし、ありふれた日常に疑問を持ち始めた彼は、何一つ不自由のない生活を捨て、家族とともに大都会シドニーへ移住する。新天地での生活も軌道に乗り、順調に見えた彼の人生だが、あるパーティーで知り合った美女ヘレンによって、次第にその歯車が狂わされていく。氷の仮面の下に隠された彼女の真の目的とは……。「冷たい月を抱く女」で悪女女優としての新境地を切り開いたN・キッドマンが、欲望と権力抗争の渦巻く映画業界を舞台に、研ぎ澄まされた冷徹な魅力を見せる。", "1988-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("その夜の妻", "その夜の妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "病気のわが子の治療費のために強盗を働いた夫。その夫を待ち続ける妻。そして夫を追う刑事。３者がほぼ一晩、アパートの一室で対峙していくという、小津安二郎監督がサイレント映画時代に撮ったサスペンス映画。アメリカ映画に傾倒していた小津監督ならではの洒落たモダニズム・タッチが妙味。一方で、ヒューマニズムの発露を怠っていないあたりも、この監督らしい秀作である。", "1930-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミラーマンREFLEX", "ミラーマンREFLEX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "70年代に人気を博した円谷プロの特撮ヒーローを、現代的なアレンジを加えて映画化。鏡の向こうの世界から現実世界を守るミラーマンの活躍を、最先端の映像技術を駆使して描く。鏡の中の死者の国“幽世”から帰ってきた影山鑑は、そこから侵入する邪仙たちを退治して暮らしていた。しかし、鑑の兄弟子だった男が“幽世”を利用して陰謀を企てていて……。日本文化に根ざした伝奇ファンタジーの味付けを加えつつ、現代的な世界観を創り上げている。", "2006-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bullet to the Head", "バレット・トゥ・ザ・ヘッド（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-11-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪の夜の決闘", "雪の夜の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "島流しとなった我孫子の長次は、恩赦をうけて25年ぶりに家に帰る途中、重病の女の臨終を看取り、遺児・三吉を父親のもとに届ける約束をする。道すがら三吉は長次になつくが、三吉の父とは、高利貸しと女郎屋を兼ねる極悪人だった……。名匠、衣笠貞之助監督が代表作「地獄門」に次いで撮った時代劇。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バックダンサーズ！", "バックダンサーズ！", "", "", "http://image.eiga.k-img.com/images/movie/33635/315x210.jpg?2020", 117, "平山あや、hiro、ソニン、サエコ主演で贈る青春ダンス・ムービー。人気絶頂のボーカリストの電撃引退によって行き場を失った4人のバックダンサー。夢も性格もバラバラの彼女たちが、戸惑ったり悩んだりしながらもスターダムへと駆け上がっていく姿を爽やかに描く。「東京ラブストーリー」や「ロングバケーション」など、多くの人気テレビドラマの演出を手掛けてきた永山耕三が、本作で劇場映画監督デビューを果たした。", "2006-09-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IF　I　WERE　KING", "放浪の王者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "中世フランスを舞台にした歴史ドラマ。飢饉と戦の敗戦に沈むルイ11世の時代、憂国の詩人フランソワは苦しむ民衆のために立ち上がり、食料倉庫を襲撃。やがて警視総監をはずみで殺害した彼は、“一週間以内に国を救わなければ断罪”という王の命令を受け入れる。そんなある日、フランソワは王妃の侍女と恋に落ち……。後の名匠P・スタージェスが脚本を手掛けている。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL　VIAJE　DE　CAROL", "キャロルの初恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "'38年、内戦中のスペインを舞台に、アメリカからやって来た少女の、淡い初恋と非情な別れを描いたホロ苦い物語。恋に胸をときめかせながら、両親が抱える事情、戦時下の過酷な現実を冷静にとらえる12歳の少女の姿を描く。演じるクララ・ラゴの中性的な魅力、つぶらで力強い瞳が印象的だ。内戦下のスペイン。アメリカ育ちの12歳のキャロルはワンパク少年と恋に落ち、楽しい日々を送っていた。だが、戦争の影が迫り……。", "2005-01-22", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUCRE｀CE BORGIA", "ボルジア家の毒薬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "15世紀ローマの史上名高いルクレチア・ボルジアの半生を映画化した作品。兄チェーザレの政策の犠牲となってナポリのアラゴン公と結婚させられたボルジア家のルクレチア。しかし、それは彼女の不幸の人生の始まりだった……。", "1952-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I WANT TO LIVE！", "私は死にたくない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "実在の女囚、B・グレアムの手記をもとにした、社会派ドラマ。独り者の老婦人を殺害したとして、前科者二人とともに逮捕されたバーバラ。彼女には、売春や偽証の過去があり警察は、最初からバーバラを犯人だと決めてかかる。彼女は無罪を主張したが、アリバイを証明できる夫は行方不明になっていて……。バーバラに扮した、S・ヘイワードが熱演。ジョニー・マンデルによるモダン・ジャズの音楽も印象的だ。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRAIGHT TIME", "ストレート・タイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "刑務所を仮出獄した男が地道に人生を歩もうとするのもつかの間、誤認逮捕で留置されてしまう。身の潔白が証明され釈放されるが、今度は彼が、そんな世間に対して復讐を開始する……。D・ホフマンが自らのプロダクションを率いて製作にも参加した意欲作。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハッシュ！", "ハッシュ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "「渚のシンドバッド」に続く橋口亮輔の監督第３作。まだ付き合い始めて間もないゲイ・カップルの間に、割り込んできた一人の女。彼女は“結婚しなくてもいいから子供が欲しい”と懇願。二人の男は戸惑いながらも事態を受け入れていく。人生の起死回生をかけた女の思いつきを起動力に物語は展開するが、ゲイを都合のいい存在としてとらえた作品でも、子育てをめぐる理想論的な内容でもない。ただ家族となる予感を秘めた人間関係を、映画はつぶさに真摯に見つめる。登場人物の感情の成り行きや熱いメッセージに寄りかかるのではなく、室内会話劇の醍醐味に向き合った終盤のシークエンスは監督のテクニックの向上と作家としての熟成を深く感じさせて、圧巻。虚飾を排した形相で登場する片岡礼子も、ラストで嗚咽する田辺誠一も、ともに観る者の共感と涙を誘う。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・大奥(秘)物語", "続・大奥(秘)物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "中島貞夫監督による「大奥秘物語」の続編。将軍・家治の急死による将軍交代劇を背景に、尼屋敷に閉じ込められたお手付中臈たちが、非人間的な生活から悲劇的な死に追い込まれていく過程が描かれる。丁寧に作られた前作と比べて、急製造の感は否めない。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BANANA BUSTERS", "笑ウぽりすまん 爆烈！ドラッグ大捜査線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "自らを“敏腕刑事”と豪語するモーテンセンは、実は周囲の迷惑お構いなしのアブナイ刑事。その彼が、あろうことか史上最大の麻薬密輸の潜入捜査に加わった。潜入先の病院で老人たちと踊り狂い、ガソリンスタンドを爆発させ、軍から戦車を盗み……彼のアブノーマルぶりは、とどまるところを知らなかった。ギャグとアクション入り乱れての軽薄コメディ。", "1990-01-01", "1,18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔法少女リリカルなのは The MOVIE 2nd A's", "魔法少女リリカルなのは The MOVIE 2nd A's", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Maradona by Kusturica", "マラドーナ", "", "", "http://image.eiga.k-img.com/images/movie/54885/315x210.jpg?4477", 91, "アルゼンチンの英雄としてその名を轟かせ、「神の子」と呼ばれた伝説のサッカー選手、ディエゴ・アルマンド・マラドーナの激動の半生に、「アンダーグラウンド」の巨匠エミール・クストリッツァが迫るドキュメンタリー。貧しい家庭に育ちながらスターへと登り詰めたマラドーナが、薬物スキャンダルを乗り越えた経緯や、自らの思想、消えることのないサッカーへの情熱を語る。", "2009-12-12", "9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Standard Operating Procedure", "スタンダード・オペレーティング・プロシージャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲーム☆アクション", "ゲーム☆アクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ゲーム好きの冴えない男がプレイする風変わりなアクションゲームの世界を描いた異色作。何をやっても上手くいかないダメ男・岩丸秀三は、仮想世界の中で理想のヒーローになれるアクションゲームに熱中していた。ある日、1本のゲームを手に入れた秀三は、主人公のおじさんが太ったゾンビを倒していくという内容にツッコミを入れながらプレイを進めていくが……。ヒーロー役に「仮面ライダーV3」の宮内洋、敵ボス役に「人造人間キカイダー」の伴大介。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リメインズ 美しき勇者たち", "リメインズ 美しき勇者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "千葉真一が主宰するJACの創立20周年を記念して製作された冒険アクション。大正末期の北国の山村を舞台に、“赤マダラ”と呼ばれる人喰い熊と、５人の腕利きマタギ衆との壮絶な戦いを描き、随所にJACお得意のアクション・シーンが盛り込まれている。“音楽監督”真田広之のセンスも出色。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒社會", "エレクション", "", "", "http://image.eiga.k-img.com/images/movie/53315/315x210.jpg?2211", 101, "香港最大の暴力団の会長選挙を背景に、トップを狙う男達の仁義なき戦いを描いたジョニー・トー監督の犯罪群像劇。2年に一度行われる香港最大のマフィア「和連勝会」の会長選挙戦。冷静沈着なロク（サイモン・ヤム）が次期会長に選ばれるが、組の稼ぎ頭で、選挙に敗れたディー（レオン・カーフェイ）との間で、会長だけが手にすることが出来るという「竜頭棍」の争奪戦が始まる。出演は「愛人／ラマン」レオン・カーフェイ、「PTU」のサイモン・ヤムら。", "2007-01-20", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVA　PERON　THE　TRUE　STORY", "エバ・ペロン エビータの真実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "マドンナ主演の「エビータ」でも描かれた、アルゼンチンの大統領夫人エバ・ペロンの最期の二年間を描く伝記映画。本国アルゼンチンが政府の全面支援の下、史実に基づいて製作。33歳の若さでこの世を去った“エビータ”を、アルゼンチンの人気女優E・ゴリスが熱演している。私生児として生まれたエバは、強靭な精神力でアルゼンチン大統領の妻の座まで登りつめる。反対派から成り上がり女優と蔑まれても、国民の支持を勝ち取った彼女は、次期副大統領として夫とともに国を統治することを決意するが……。", "1996-01-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボールZ　とびっきりの最強対最強", "ドラゴンボールZ　とびっきりの最強対最強", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 47, "人気キャラクターが活躍する冒険アクション・アニメの劇場版シリーズ第8弾。かつて悟空の父を倒した敵フリーザの兄クウラが、悟空に殺された弟の仇を討ちに襲いかかってくる。復讐心に燃える両者の壮絶なバトルが展開。DVDは『DRAGON BOX THE MOVIES』に収録。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖艶毒婦伝・般若のお百", "妖艶毒婦伝・般若のお百", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "浅草・阿波徳一座の花形太夫・お百は、浪人・新九郎たちと組んで佐渡から江戸に送られる金塊の強奪を企てるが……。お百に扮する初主演の宮園純子が、全裸の水浴シーンやすさまじい拷問シーンに大奮闘。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAVALCADE", "カヴァルケード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1899年から1929年にわたる大英帝国の側面史ともいえる、ノエル・カワードの舞台劇を映画化した大河ドラマ。南アフリカに出征したマリヨット家の当主ロバートと従僕のブリッジスは勲功を土産に凱旋し、ブリッジスは酒場を営むようになる。やがてロバートの次男がブリッジスの娘と婚約するなど、両家の交友が描かれていく。初公開時タイトルは「大帝国行進曲」。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長靴をはいた猫　80日間世界一周", "長靴をはいた猫　80日間世界一周", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "「長靴をはいた猫」(1969)「ながぐつ三銃士」(1972)に続いて、猫のペロが大活躍するアクション・コメディ。ブタの大金持ち、グルーモンと賭けをして、80日間以内の世界一周旅行に挑戦したペロが、グルーモンの差し向けたガリガリ博士の妨害をかわしつつ旅を続ける。珍妙なメカが次々と登場し、盛り上がりには少々欠けるものの、アニメ版「グレートレース」といった趣の作品に仕上がった。もちろん、名作『80日間世界一周』がヒントに。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Great Discovery", "トーマスをすくえ!!　ミステリーマウンテン", "", "", "http://image.eiga.k-img.com/images/movie/54465/315x210.jpg?2864", 60, "世界中の子供たちから愛され続けているイギリスの人気番組「きかんしゃトーマス」の劇場版。ある日トーマスは、ソドー島の山奥で忘れられた街を発見。島の記念日までに街を復旧させることになり、新しい機関車スタンリーがトーマスたちを手伝いにやってくる。瞬く間に島の人気者となったスタンリーにやきもちを焼いたトーマスは、彼にいたずらを仕掛けるが失敗。仲間の信用を失ってひとりぼっちになってしまい……。", "2009-04-25", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅番区／RUMBLE IN THE BRONX", "レッド・ブロンクス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "香港映画最大級の製作費（約14億円）を投じて作られたアクション・ムービー。ニューヨークのブロンクスで休暇を過ごす刑事クーン。ダイヤ密売シンジケートによって、町はすっかり荒廃していた。クーンの周囲にもその魔の手は忍び寄り、彼は巨大な悪に立ち向かっていく……。猛スピードで爆走するホバークラフトでの水上スキー、ビルの屋上から向かいのビルへのダイビングなど、スタントなしのジャッキーのアクションが見もの。全編英語セリフによるアメリカ公開版（90分）のビデオもある。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MRS.MINIVER", "ミニヴァー夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "第二次大戦中、ロンドン郊外の小さな町で夫と３人の子供たちと幸福な生活をしていたミニヴァー夫人。美しく聡明な英国女性の鏡ともいわれる夫人が、戦争の惨渦に巻き込まれながら生き抜いていく様子を描くヒューマン・ドラマ。空襲によって引き起こされる業火、周囲の人々の死。そんな悲惨な状況を目のあたりにしながらも、秩序と明るさを失わずにけなげに気丈に生きる英国のオーディナリー・ピープルたち。そんなヒューマニティーあふれる物語を名匠W・ワイラーが演出する。チャーチル首相に“この映画は戦時の英国民を救った”といわしめた珠玉の名作。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BloodRayne: Deliverance", "ブラッドレインII", "", "", "http://ecx.images-amazon.com/images/I/51vQgoqmeDL._SL210_.jpg", 99, "「アローン・イン・ザ・ダーク」のウーベ・ボル監督が、人気ゲームを実写映画化したバンパイアアクションの第2作。人間と吸血鬼の間に生まれた吸血鬼ハンターのレイン。ルーマニアからアメリカへと渡った彼女は、とある町でバンパイアの犠牲となった死体を発見する。実はその町は、吸血鬼ビリー・ザ・キッドに支配されており……。前作のクリスタナ・ローケンに代わり、「DOA デッド・オア・アライブ」のナターシャ・マルテが主人公レインに扮する。", "2007-01-01", "10,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PART III 愛人契約", "難波金融伝 ミナミの帝王　劇場版PART III 愛人契約", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "『週刊漫画ゴラク』で絶大な人気を誇り、ビデオ・シリーズでも好調を博していた同名作を映画化。ヤクザも一目置く大阪一の高利貸し萬田銀次郎から大金を借りた大学生が逃亡した。彼を追い込むうちに、女と金をエサに若者の弱みにつけこむ出張ホスト会社の存在が明らかになっていく。暴利をむさぼる出張ホスト会社の黒幕に、銀次郎が得意の頭脳戦で対決に挑む。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAKTUB THE LAW OF THE DESERT", "デザート・ソルジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "CIAの傭兵で、テロと人質救出のスペシャリストが、女性実業家の依頼で、誘拐された彼女の息子を救出することになる。舞台をモロッコへと移し、民族間の闘争に巻き込まれ事態はさらに複雑になる。冷酷な殺人鬼などの印象が強いR・ハウアーが孤高のヒーローを演じた、ハードボイルド風のアクション映画。", "1991-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAR OUT MAN", "ファーアウトマンだ!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "永遠のヒッピー・ミュージシャン、ファーアウトマン。1960年代はヒーローだったが愛妻が息子を連れて家出したあの日以来、彼の頭の中は1960年代のままパタリと止まってしまった。心配した彼の娘はなんとか彼の頭を1990年代の現代に戻そうとするのだが……。人気コメディアン、チョンが笑いのパワーを爆発させるサイケデリック・コメディ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画の都", "映画の都", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1989年に山形県で開催された、アジアで初の“国際ドキュメンタリー映画祭”の模様を丹念に追ったドキュメンタリー。この映画祭のために世界各国から集まったドキュメンタリー作家たちのインタビューや、観客とのディスカッションを交えながら、互いに異なった文化や民族を超えて理解し合おうとする映画人たちの姿をパワフルに映し出していく。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Romeo + Juliet", "ロミオ＆ジュリエット", "", "", "http://ecx.images-amazon.com/images/I/61y09m8xnzL._SL210_.jpg", 120, "", "1997-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Snatch", "スナッチ", "", "", "http://image.eiga.k-img.com/images/movie/1175/315x210.jpg?4399", 102, "ベルギーの宝石業者から86カラットのダイヤを奪った強盗団の1人、フランキー・フォー・フィンガーズ。彼の役目はダイヤをニューヨークのボス、アビーに届けること。途中のロンドンで盗品をさばく予定の彼に、仲間はボリスという男を訪ねるように言う。一方、非合法ボクシングのプロモーター、ターキッシュは流浪民からトレーラーを買うよう相棒のトミーを遣いに行かせるが、不良品をつかまされトラブルになる。", "2001-03-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HUNDSTAGE", "ドッグ・デイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "2001年のヴェネチア映画祭で審査員特別賞に輝いたオーストリアの鬼才ウルリヒ・ザイドル監督による群像劇。「アメリカン・ビューティー」などに共通する“郊外の住宅街に潜む狂気”というテーマを、より過激で容赦ないタッチで掘り下げた異色作だ。離婚後も同居する夫婦、家政婦のストリップを眺める老人らが暮らす住宅街。そこにヒッチハイクを繰り返す奇妙な女性が現れるが、彼女は車を傷つけた“犯人”として住民の暴行を受け……。", "2006-05-06", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Banlieue 13", "アルティメット", "", "", "http://image.eiga.k-img.com/images/movie/33555/315x210.jpg?3479", 85, "リュック・ベッソンが製作・脚本を手掛け、スタントやCGを一切使わずに撮影されたリアルアクション・ムービー。近未来のパリ。危険地域バンリュー13で暮らす青年レイトは、ドラッグ一掃のため、街を牛耳るタハ一味に立ち向かう。ところが作戦は失敗し、逆に愛する妹を奪われてしまう。その6ヶ月後、政府が作った時限爆弾をタハ一味が強奪。解除を命じられた敏腕捜査官ダミアンは、レイトと共にタハ一味のアジトへ乗り込むが……。", "2006-07-15", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("結婚", "結婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "３人の監督が３つの結婚物語を描くオムニバス。大金持ちの青年と普通のOLの駆け引きをロマンティック・コメディ調に。売れない男優とその後輩との結婚騒動をドタバタ仕立てに。昔の恋人を待つ女と彼女を見守る男の愛の行方をしっとりとした大人のドラマに、とバラエティーに富んだ愛の物語３本立て。なかでも、セット撮影の効果を生かした鈴木清順監督作品が面白い。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PENNIES FROM HEAVEN", "ペニーズ・フロム・ヘブン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "大不況の嵐が吹き荒れる1930年代前半のアメリカで楽譜のセールスマンが味わうロマンとファンタジー。ミュージカル・ナンバーはビング・クロスビー、フレッド・アステアら当時のオリジナル盤を起用（一部は映像も）した実にユニークな作りが注目される。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("運命人間", "運命人間", "", "", "http://ecx.images-amazon.com/images/I/319FESKBTDL._SL210_.jpg", 85, "日本映画界を代表する監督と新人監督たちが競作する「映画番長」シリーズの第3弾「ホラー番長」の1作で、「完全なる飼育　愛の40日」の西山洋市監督が「極道恐怖大劇場　牛頭」の佐藤佐吉による脚本を映像化した不条理コメディ。ペット探偵の田宮は、ある日パチンコ屋で見知らぬ男から「その台出ますよ」と告げられ、その通り大当たりを出す。その日から田宮は、自らを予知能力者と名乗るその男・毛利の奇妙な予言に翻弄されていく。", "2004-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗花／THE LONGEST NITE", "ロンゲストナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "T・レオンが珍しくダーティなアウトロー刑事に扮したハードボイルド・サスペンス。２大勢力が激しく対立するマカオで、黒社会の顔役ケイに懸賞首が懸けられたという噂が流れる。その噂の出所を調べるよう頼まれた悪徳刑事サムは、職権を利用した強引な捜査を進めていくが、自分自身が罠にハマッていることを知る。事件の鍵を握るスキンヘッドの男に扮して、ラウ・チンワンが不気味な存在感を披露。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("８.１", "８.１", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "インターネットでロングラン配信を達成したホラー映画。原案は、『リアル鬼ごっこ』でデビュー以来若年層に多大な支持を得る23歳の新鋭作家、山田悠介。主演の黒川智花はドラマ『雨と夢のあとに』で注目を集めた若手有望株。力強い瞳で主人公の心理を表し、恐怖心をあおる。とあるトンネルで、子供たちが自ら車の前に飛び込むようにして死亡する事故が相次ぐ。彼らは皆なぜか1989年8月1日生まれだった。", "2005-07-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("踊る大捜査線 THE MOVIE 3 ヤツらを解放せよ！", "踊る大捜査線 THE MOVIE 3 ヤツらを解放せよ！", "", "", "http://image.eiga.k-img.com/images/movie/55165/315x210.jpg?3151", 141, "織田裕二主演の大ヒットシリーズ7年ぶりの第3弾。前作に引き続き、君塚良一が脚本を、本広克行が監督を務める。係長に昇進した青島俊作刑事は、出世直後に殺人事件・爆弾魔・バスジャック・強盗など、8つの難事件に直面する。2004年に逝去した故いかりや長介が演じた和久平八郎は、病死した設定となっている。", "2010-07-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAS ARCHE NOAH PRINZIP", "スペースノア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1997年、爆発した宇宙ステーションから一人の男が帰還した。宇宙で何があったのか？　地球の気象をコントロールする“フロリダ・ラブ”に、地上から執拗な放射線照射指令が届く。地上はどうなっているのか？　孤立した宇宙ステーションに不安が広がり……。「デイ・アフター・トゥモロー」のR・エメリッヒ監督が、ミュンヘン映像大学の卒業作品として制作したSF映画。1997年には完全版が「ディストラクション　地球滅亡」というタイトルで公開された。ビデオ発売時タイトルは「ディストラクション／地球滅亡　スペースノア・インターナショナルバージョン」。", "1984-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IERI，OGGI，DOMANI", "昨日・今日・明日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "オムニバス形式で３話からなる、セックスの風味の効いた大らかな人情喜劇。第１話は刑を免れるために四六時中妊娠している腹ぼて女の話。第２話は未経験の若者を浮気相手にした大金持ちの人妻の話。第３話は神学生が夢中になった人のいい高級コールガールの話。", "1963-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　超・仮面ライダー電王＆ディケイド NEOジェネレーションズ　鬼ヶ島の戦艦", "劇場版　超・仮面ライダー電王＆ディケイド NEOジェネレーションズ　鬼ヶ島の戦艦", "", "", "http://image.eiga.k-img.com/images/movie/54455/315x210.jpg?2770", 85, "TV放映終了後も根強い人気を誇る「仮面ライダー電王」の劇場版第4弾。鬼ヶ島伝説が残る平和な村に、過去からやって来た鬼の一団・オニ一族が出現。彼らを追って良太郎や幸太郎も駆けつけるが、時間の歪みのせいで良太郎は子供の姿となり、モモタロスともはぐれてしまう。良太郎たちは時間の歪みを直すため、時空を超えた鬼退治の旅を開始する。2009年1月より放映開始された「仮面ライダーディケイド」も登場。", "2009-05-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("闘茶 tea fight", "闘茶 tea fight", "", "", "http://image.eiga.k-img.com/images/movie/53365/315x210.jpg?3868", 102, "老舗の茶屋の主人・八木圭は、一族に代々伝わる“黒金茶の呪い”と妻の死が関係していると信じこみ、茶に関わることを拒むように。そんな父を心配していた娘の美希子は、蔵で眠っていた古い書物の中に呪いを解く方法を発見。その鍵となる\"雄黒金茶\"を求めて台湾へと旅立つ。中国で生まれたお茶の競技\"闘茶\"を題材に、香川照之、戸田恵梨香、エリック・ツァンらアジア各国の実力派俳優が豪華共演を果たした痛快エンタテインメント。", "2008-07-12", "76,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それでもヤクザはやってくる TWILIGHT FILE IV", "それでもヤクザはやってくる TWILIGHT FILE IV", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "オムニバスシリーズ「TWILIGHT FILE」第4弾の1作。経営する飲食店で暴力団による不当な圧力に正攻法で対峙（たいじ）した宮本照夫の実話を、ベテラン俳優・宅麻伸主演で映画化。昭和54年。それまでいくつかの飲食店を経営してきた経験を持つ宮本は、川崎にカラオケスナック「ナポレオン」を開店する。店は順調に成績を伸ばしていくが、その一方で不良客や暴力団とのトラブルが続発。非暴力を信念に数々の問題に立ち向かっていく宮本には、実は少年時代の悲しい過去があり……。", "2007-10-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ZERO BOYS", "悪魔のサバイバル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "深い森の中のログ・キャビンに泊まり込んだ若者たちの恐怖の体験を描くホラーもの。山中へとツーリングに出かけた５人の男女は、人気のないログ・キャビンを発見し、そこに泊まることにする。だがやがてキャビンのトランクからは女の死体が、冷蔵庫からは切り落とされた生首が発見され、彼らにも姿なき殺人鬼の罠が徐々に迫ってくる。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("なぞの転校生", "なぞの転校生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "NHKで放映された伝説のSFドラマを、主人公を女子高校生の設定に変えて映画化。ミステリアスな雰囲気の転校生に同級生にはない魅力を感じたヒロインが、時空を越える能力“次元ジャンプ”を彼女から学び、世界の危機と対峙していく。ファンタジーとしての側面より、思考する少女たちに焦点が当てられており、苦味を伴った青春映画になっている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("理髪店主のかなしみ", "理髪店主のかなしみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "脚フェチ・マゾヒスティックな理髪店主の店には色々な人がやって来る。運転手付きの黒塗りの車に乗って毎日髭を剃ってもらいに来る猥談好きの男・坂口、SMの女王様に首輪をはめられている同業者・杉浦などなど……。ある日、靴屋の美人店員・麗子に恋した理髪店主は彼女の愛人・坂口を殺す完全犯罪を企てる妄想の世界にのめり込んで行く。アブノーマルな世界を描きながらも男の純情を哀しくコミカルに綴った異色作。エンディング・テーマを歌う“ばちかぶり”は、本作の主演・田口トモロヲ率いる伝説のパンク・バンド。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブハンター 熱い肌", "ラブハンター 熱い肌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "日活ロマンポルノ草創期から終焉までを走り抜けた、隠れた名監督・小沼勝の初期の作品。かつて自動車事故で夫を不能にした上流夫人が、再び事故で愛人を死なせた時、それを目撃した若者に脅迫され、夫を忘れて彼との情事にふける。小沼得意の倒錯的な性の世界が圧巻。脚本の萩冬彦は小沼啓一のペンネーム。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("外人球団", "外人球団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "韓国で人気のマンガを映画化した作品。ヘソンとドンタクは、アマチュア時代からのライバル。二人はプロ野球へ進むが、ヘソンは肩をこわしてしまう。そこに鬼監督ソンが現れ、ヘソンら６人の落ちこぼれを集めて猛特訓する。そして再びプロ野球へ挑戦する……。", "1986-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAXI", "TAXi", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "L・ベッソンが製作と脚本を手掛け、元レーサーで車のCFディレクターでもある、G・ピレスが監督を務めた痛快カー・アクション。タクシー・ドライバーのダニエルは改造した愛車“プジョー406”で街中を飛ばしまくるスピード狂。ある日、運転免許試験に８回も落ちているマヌケな刑事エミリアンにスピード違反で捕まったダニエルは、違反取り消しを条件に捜査に協力するハメに。犯人は赤いベンツで銀行を襲撃したドイツ人強盗団“メルセデス”。車大好き青年と頼りない刑事の凸凹コンビは、奇想天外な作戦で犯人逮捕に乗り出す。ボタン一つで様々に変形する改造プジョーや、一流レーサーたちが時速250キロで駆け抜ける実写のみのカーチェイスがとにかく豪快。捜査のかたわらにロマンスにも励む主人公二人のセリフのやり取りもユーモラスだ。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TASTE OF CHERRY", "桜桃の味", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "今村昌平の「うなぎ」とカンヌ映画祭パルム・ドールを分け合った、鬼才キアロスタミの人間ドラマ。彼ならではのドキュメント・タッチで、ある自殺志願者の行動を淡々と綴っていく。職を求める失業者の群れをかき分け、車を走らせる男バディ。彼は多額の報酬と引き換えに自殺の手伝いをしてくれる人間を探しているのだが、車に同乗して彼の話を聞いた人々は、みな一様に断る。それでもめげないバディは、博物館で働く初老の男と話をつけ……。悪戦苦闘の果てに生の意味を見いだす主人公の心の旅を、淡々とした語り口で描写。人間を見つめる優しい視点にキアロスタミ作品ならではの温かさと深い味わいがある。", "1997-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASA　DE　LOS　BABYS", "カーサ・エスペランサ／赤ちゃんたちの家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "養子縁組を実現させるため、南米某国を訪れた６人の女性たち。孤児院の赤ん坊との養子縁組が許可されるのを待つ間、彼女たちはそれぞれの複雑な事情を知ることになり……。貧しくても活力に溢れた南の地で、なんとか自分の力で幸せを掴もうとする彼女たちの姿が、じっくりと描かれている。マーシャ・ゲイ・ハーデン、リリ・テイラー、マギー・ギレンホールら演技派女優たちのアンサンブルも見もの。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN SENOR MUY VIEJO CON UNAS ALAS ENORMES", "大きな翼を持った老人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ラテンアメリカ文学の巨星、G・G・マルケスの同名短編小説の映画化。監督のF・ビリはマルケスのローマの映画実験センター時代の旧友で、マルケス自身が脚本に協力している。カリブのひなびた漁村に現れた大きな翼を持った老人。土地の老婆が彼を、天使だと思ったことから一大騒動が起こって……。ヴェネチア映画祭音楽賞に輝いた音楽も魅力の一つ。", "1988-01-01", "49,4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARE TO KE", "HARE TO KE〈ハレとケ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "“三里塚”シリーズや「ニッポン国　古屋敷村」などで知られる小川紳介プロダクションの活動をドイツの女性監督が追ったドキュメンタリー映画。小川プロの牧野村での生活と活動に加えて、小川紳介や田村正毅、スタッフの家族などにもインタビューして、小川プロの映画製作の実態に迫る。", "1988-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真人間", "真人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "浅草の与太者“役者の英吉”が、出征した友の信頼に応えるため、更生を約束するという物語を描いた時局映画。原作はあるものの、1939年に公開されたフリッツ・ラング監督作品「真人間」を下敷きにしている。新興キネマ俳優学校出身の二枚目俳優・若原雅夫が英吉に扮し、当時のあるべき国民の姿を熱演。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT’S DANCING！", "ザッツ・ダンシング！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "古今東西、ありとあらゆるダンス・シーンのフィルムから、傑作名作ばかりを抜き出して集めた、ダンス版「ザッツ・エンタテインメント」。G・ケリーからマイケル・ジャクソンまで100本の映画を通して、ダンスの発展と歴史が浮かび上がる。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大氷原", "大氷原", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "終戦直後、日本人の裏切りでソ連兵に処刑された両親のうらみを晴らすため、鉄次という男が流氷に乗って網走にやって来た。敵の行方は不明のまま時がたち、鉄次はハンターとして名を上げていく。だが、探す相手は意外なところに……。北海道の大自然を舞台に豪快に展開するアクションもの。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SWARM", "スウォーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "テキサスの軍事基地で通信センターの機能が突然マヒし、勤務員が全員死亡。近郊の空はミツバチの大群で覆われ、町一つが壊滅してしまった。恐怖に怯えるアメリカ国民。ミツバチの大群＝スウォームVS人間の闘いを描いた典型的なパニック映画。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕らのワンダフルデイズ ", "僕らのワンダフルデイズ ", "", "", "http://image.eiga.k-img.com/images/movie/54615/315x210.jpg?4076", 112, "平凡なサラリーマンの藤岡は、自分が末期ガンに侵されていて余命が半年ほどしかないことを知ってしまう。藤岡はこれまでの半生を振り返り、自分が最も輝いていた高校時代のバンド活動を思い出す。藤岡はバンドを再結成するため、かつてのバンドメンバーたちを訪ねるが……。主演は竹中直人、監督は「ジュテーム／わたしはけもの」の星田良子。", "2009-11-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REPLICANT", "レプリカント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "アクション俳優J・C・ヴァン・ダムが善と悪の二役を演じた近未来活劇。世間を騒がす殺人鬼を逮捕するため、現場の遺留品から作られたクローン人間。無垢な彼は残虐な事件にとまどいながらも、元刑事とともにシリアルキラーと攻防を繰り広げる。クライマックスのヴァン・ダムVSヴァン・ダムの対決シーンは圧巻。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUEEN　KONG", "クイーン・コング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "特撮映画の古典的名作「キング・コング」のパロディーだが、製作当時、ジェシカ・ラング主演で作られていたリメイクの製作サイドからあらゆる手を使って公開を阻止されお蔵入りとなっていた、いわくつきの珍品。監督もスタッフも女性ばかりの映画クルーが、ロンドンで見つけた青年をスターとして売り出そうと、アフリカのジャングルで新作を撮影することに。そこへ現れたのが体長20メートルのメスゴリラ“クイーン・コング”だった……。2001年秋の日本公開では、広川太一郎と小原乃梨子ら豪華声優陣による日本語吹き替えバージョンで上映された。ベタベタなギャグとセリフの嵐に思わず苦笑。", "1976-01-01", "4,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Elvira Madigan", "みじかくも美しく燃え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "19世紀のスウェーデンで実際に起った事件を映画化。既婚者の伯爵と美しい綱渡り芸人エルビラ。恋に落ちた2人は、周囲の抑制を振り切って駆け落ちする。逃亡生活の末、運も金も尽きてしまった彼らは、ある決断をする……。衝撃のラストシーンと、全編に流れるモーツァルトのピアノ協奏曲第21番が話題を呼び、当時日本でもヒットを飛ばした悲恋物語。ヒロインを熱演したピア・デゲルマルクが、カンヌ国際映画祭主演女優賞を獲得。", "1967-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘密結社　鷹の爪 THE MOVIE II 私を愛した黒烏龍茶", "秘密結社　鷹の爪 THE MOVIE II 私を愛した黒烏龍茶", "", "", "http://image.eiga.k-img.com/images/movie/53495/315x210.jpg?2943", 98, "クリエイターのFROGMANが個人制作してWEBで人気を博し、初のFLASHアニメのTVシリーズとして放送された「秘密結社　鷹の爪」の劇場版第2作。世界征服を企むベンチャー秘密結社“鷹の爪”は、宿敵DXファイターに対抗するためネットの世界に潜り込むことが出来る装置を開発するが……。「ドラゴンボール」の野沢雅子など豪華声優が意外なかたちで出演する。「古墳ギャルのコフィー／12人の怒れる古墳たち」を同時上映。", "2008-05-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAR WASH", "カー・ウォッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ロックとやるせないギャグを散りばめた、軽快なノリの人生讃歌映画。白人が経営するロサンゼルスの洗車場。働くのは黒人、インディアンにメキシカン。バカやって明るくふるまわなきゃ、人生やってらんないゼという、一癖も二癖もある連中のエピソードが楽しく絡む。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KID", "キッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "サイレント時代に作られたチャップリン初の長編映画。ロンドンの下町、男に捨てられた若い娘が、赤ん坊の処理に困ったあげく金持ちの車の中に置き去りにする。街の浮浪者チャップリンがその子を拾い育てることになる。５年後、成長した子供はキッドと呼ばれ、ハンチングに破れたセーター姿で登場。チャップリンを助け、インチキ商売に精を出す。そこへ今や人気オペラ歌手の生母が現れて、母子はめでたく再会、チャップリンとともに３人で暮らすことになる。最初に出てくる字幕“皆さんはこの映画をほほえみと、そして一粒の涙とともにご覧になるでしょう”が語るように、愛と笑いに満ちた感動作。", "1921-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rasmus Pa Luffen", "ラスムスくんの幸せをさがして", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「長くつ下のピッピ」「ロッタちゃん」シリーズで知られる、スウェーデンが世界に誇る人気児童文学作家アストリッド・リンドグレーンによる原作「さすらいの孤児ラスムス」を、作者自身が脚本を手掛け映画化。孤児院で暮らす少年ラスムスは、自分の里親になってくれる人を探すために、孤児院を抜け出して旅に出る。その途中で自由気ままな風来坊オスカルに出会い、一緒に行動することに。こうして、2人の楽しい旅が始まるが……。", "1981-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はじけ鳳仙花—わが筑豊わが朝鮮—", "はじけ鳳仙花—わが筑豊わが朝鮮—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "戦時中、九州の炭鉱に強制連行されて来て、酷使されながら死んでいった朝鮮人労働者の実態を描き続ける画家・富山妙子の絵と詞を用いて、日本人の残虐さと権力への怒りを突きつけるドキュメンタリー。女史は自分の顔を鏡に映して、加害者たる日本人の残忍な顔を描き、死者の恨みの声を聞く巫女となっていく。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hearts of Darkness: A Filmmaker's Apocalypse", "ハート・オブ・ダークネス　コッポラの黙示録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "フランシス・F・コッポラの問題作「地獄の黙示録」の製作現場を捉えたドキュメンタリー。ジャングルの奥地で展開された撮影は、資金や物資調達の困難から、スタッフを襲う台風や病気、そしてロケ地付近で起こったフィリピン内戦などによりまさに地獄と化していた。そんな悪夢の中、コッポラは狂気とも思える情熱で撮影を進める。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青梅竹馬／TAIPEI　STORY", "幼馴染み タイペイ・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "事業に失敗し、挫折を味わう建築家と、会社をクビになったキャリアウーマン。幼なじみの彼らは、ともにアメリカに移住する夢を抱くが、その先には新たな挫折が……。変わりゆく台北の町を舞台に、閉塞的な状況に置かれた男女の行き止まりを描いた、エドワード・ヤンによる辛口の人間ドラマ。ホウ・シャオシエン監督が俳優として出演。", "1985-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の一生", "女の一生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "モーパッサンの同名小説を新藤兼人が翻案・監督。京都の寺に生まれた藤子は、牛肉屋山崎亭の真太郎に嫁ぐが、夫に裏切られ、藤子の不幸な一生が始まった。原作の舞台ノルマンディーを京都に置き換え、太平洋戦争を織り込むなど、新藤の脚色は巧みであったが、物語の暗さのせいか観客には受け入れられなかった。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Multi-Handicapped", "多重障害", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もっと熱いぞ！猫ヶ谷!!", "もっと熱いぞ！猫ヶ谷!!", "", "", "http://image.eiga.k-img.com/images/movie/57605/315x210.jpg?3733", 0, "秋月三佳、綾乃美花、衛藤美彩ら11人のミスマガジンが、水着姿で1話ずつヒロインを務める青春コメディドラマを映画館で3D上映。エコ対策の一環で「男性はアロハシャツ、女性は水着着用」という究極のクールビズ条例を制定した日本一暑い町・猫ヶ谷市にある女子高を舞台に、生徒たちに水着着用を推奨する生徒会メンバーの奮闘を描く。原作は「週刊ヤングマガジン」に連載された克・亜樹のコミック「熱いぞ！猫ヶ谷!!」。2011年10月から名古屋テレビ（メ～テレ）ほかで放送された全10話を2話ずつに分けて3D上映する。", "2012-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BORN TO FIGHT", "マシンガン・ソルジャー 戦場の狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "サム・ウッドは元アメリカ兵。今は東南アジアのとある町で堕落した生活を送っていた。ある日、マリリンという美人記者が彼のところへ訪れ、取材に協力してほしいと頼まれて、ベトナムへ赴く。やがて、マリリンはかつての上官の娘であり、また上官は収容所に捕らわれていることが明らかになり……。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三匹の野良犬", "三匹の野良犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "小林旭扮する主人公は、保税倉庫からダイヤを強奪して警備員を殺した犯人の濡れ衣を着せられ、投獄される。彼は、復讐に燃えて脱獄するが……。旭に協力する金庫破りの名人、金持ちのプレイボーイと日活のスター３人をそろえたアクション作品。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画クレヨンしんちゃん　嵐を呼ぶ！オラと宇宙のプリンセス", "映画クレヨンしんちゃん　嵐を呼ぶ！オラと宇宙のプリンセス", "", "", "http://image.eiga.k-img.com/images/movie/57425/315x210.jpg?3385", 100, "臼井儀人原作の人気アニメーション劇場版第20作。映画化20周年記念としてシリーズ初の宇宙を舞台にした冒険が描かれる。ある日、ささいなことで妹のひまわりと喧嘩したしんのすけは、突然現れた謎の男2人組に言われるまま「ひまわりをお姫様として預ける」という契約書にサインしてしまう。UFOに吸い込まれヒマワリ星へと連れて行かれた野原一家は、しんのすけがサインした契約書に基づきひまわりが星のお姫様にならなければ、地球が消滅するということを知る。しかし、ひまわりがお姫様になれば、家族とは一生離れ離れにならなければならず……。", "2012-04-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鯨神", "鯨神", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "今や官能小説の雄ともいうべき宇能鴻一郎の若き日の芥川賞受賞作『裁かれる越前守』の映画化。メルヴィルの『白鯨』を思わせる骨太な作品で、日本では育ちにくいジャンルを、大映時代劇の中堅・田中徳三が正攻法で演出し、一応の成果を得たのは注目に価する。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIRDS OF PREY", "爆発ジェット・ヘリ500", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ヘリコプターを操縦しながらラジオへ交通情報を流している男が、たまたま上空から目撃した銀行強盗の逃走用ヘリを、ソルトレイク市からユタ峡谷へと追い続ける。彼が常に連絡をとる警部にR・ミーカー。飛行中のヘリの給油や息詰まるヘリ・チェイスなどを満載した航空アクション。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Juno", "JUNO ジュノ", "", "", "http://image.eiga.k-img.com/images/movie/53245/315x210.jpg?4373", 96, "第80回アカデミー賞で作品賞、監督賞、主演女優賞にノミネート、脚本賞を受賞したコメディタッチの人間ドラマ。16歳の女子高生ジュノは同じクラスのポーリーと関係を持ち、予定外の妊娠をしてしまう。生まれてくる赤ちゃんに完璧な両親を見つけようとするジュノは、養子を望むローリング夫妻を見つけるが……。主人公ジュノに「ハードキャンディ」のエレン・ペイジ。監督は「サンキュー・スモーキング」のジェイソン・ライトマン。", "2008-06-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARMEN", "カルメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 149, "ジョルジュ・ビゼーの名作『カルメン』を忠実に再現したオペラ映画。アンダルシアのタバコ工場で働く情熱的で奔放な女カルメンに魅せられた衛兵ドン・ホセは、彼女にほんろうされ身を持ち崩していく……。アントニオ・ガデス振付けによる情熱的なスペイン舞踊が見もの。", "1984-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虹をつかむ男 南国奮斗篇", "虹をつかむ男 南国奮斗篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "映画をこよなく愛する映画館主、銀活男と人々の交流を描く人情喜劇第２作。今回は活男と彼を慕う青年、亮のそれぞれの恋の行方を、笑いと涙を交えて温かく綴る。亮は仕事をやめ、経営難に陥った活男を訪ねて奄美群島へやって来る。移動上映を手伝うことになった亮は、一人息子を女手一つで育てる節子と恋に落ちるが、気性の激しい彼女の兄とことごとく衝突。一方、活男はかつて愛した女性、松江と上映会で再会、恋心を再燃させてしまう……。出演作が続く哀川翔の熱演が光る一編。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAYING ALIVE", "ステイン・アライブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "大ヒット作「サタデー・ナイト・フィーバー」の続編。主人公トニーがブロードウェイのダンサーを目指す、その野望を描いたサクセス・ストーリーの一編。スタローンが監督と脚本を手掛けている。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("城市特警／THE BIG HEAT", "城市特警", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "断続的に右手が痺れるという原因不明の病気に冒され、退職を考えていた香港警察の辣腕刑事ウォンは、長年彼の情報屋を務めていたウィリーがマレーシアで惨殺されたと聞き、この事件を最後に引退することを決意する。捜査が進むにつれ、ウォンは事件の背後にアジア最大のコカイン密売マフィアの存在を知る。「チャイニーズ・ゴースト・ストーリー」シリーズのコンビ、T・ハークとJ・ウォンが組んだバイオレンス・ポリス・アクション。爆風で飛び散る肉片や焼け焦げる人体など、ショッキングな殺りくシーンがSFX技術でリアルに映像化されている。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEATSEEKER", "アルティメット・マシーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「サイボーグ」のA・ピュンが手掛けた近未来アクション。刺激を求める人々の支持を受ける過激なデス・ファイト。強さだけを求めて自らの肉体を改造した“サイバー”と呼ばれる戦士たちが極限バトルを繰り広げる一方で、背後では利権をめぐる企業間の争いが起こっていた。その渦中に身を投じた誇り高き戦士オブライエンは、鍛えぬかれた生身の体だけを武器に、サイバーとの戦いに挑むのだが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拳銃０号", "拳銃０号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "アメリカ人の観光客が落とした一丁の拳銃が、屑屋から始まりギャング、スリ、尼そして最後は心中しようとする若いカップルと、幾人もの手を転々としていくという一種のオムニバス形式の意欲作。出演場面は少ないが、赤木圭一郎の本格的なデビュー作である。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHANTOM SOLDIERS", "ファントム・ソルジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "激戦が続くベトナム戦争下の北ベトナム国境付近。密林で暗躍する謎の殺人軍団“ファントム・ソルジャー”を追う米軍グリーンベレー部隊のマイケル中尉が、突然姿を消す。彼を救出するために兄は現地に向かうが……。実際にベトナムで使われた兵器を使用した戦争アクションで、爆破シーンも強烈。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SKYJACKED", "ハイジャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ハイジャックが深刻な国際問題となりつつある1970年代初めに作られた、パニック・サスペンス。ロサンゼルスから飛び立ったボーイング707型機が乗っ取られる。アンカレッジで一部乗客を降ろし、指示に従ってモスクワへ向かうが……。社会性を備えた見ごたえある娯楽映画。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Closing the Ring", "あの日の指輪を待つきみへ", "", "", "http://image.eiga.k-img.com/images/movie/53335/315x210.jpg?2237", 118, "長年連れ添った夫を亡くしたばかりのアメリカ人エセルの元に、彼女の名前が刻まれた指輪がアイルランドで見つかったという知らせが届く。彼女は50年間心の奥底に眠らせていた運命の愛に決着をつけるべくアイルランドへと旅立つ。「ガンジー」のオスカー監督リチャード・アッテンボローが実話を基に描いた感動のラブ・ストーリー。エセルを名女優シャーリー・マクレーン、若き日の彼女をテレビシリーズ「The OC」のミーシャ・バートンが演じる。", "2008-07-19", "3,10,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙ショーへようこそ", "宇宙ショーへようこそ", "", "", "http://image.eiga.k-img.com/images/movie/55235/315x210.jpg?3655", 136, "美しい自然に囲まれた山奥の村川村。周、康二、倫子、清、町から転校してきた夏紀と、小学校の全校生徒はたった5人しかいないが、みんな仲良く毎日を過ごしていた。ある日、夏休み恒例の合宿で集まった5人は、いなくなったウサギのぴょん吉を探しに裏山へ向かうが、そこで見つけたものは巨大なミステリーサークルと怪我をした犬だった。5人に手当てを受けたその犬は、惑星プラネット・ワンからやってきた宇宙人だった。TVアニメ「かみちゅ！」で脚光を浴びた舛成孝二監督の劇場長編デビュー作。", "2010-06-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PUPPET MASTERII", "パペット・マスター2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "前作「パペット・マスター」に登場した５体のパペット（人形）に、鉄人バーベQなる新メンバーを加えた人形ホラー。６体の人形たちが、必殺仕置人よろしくそれぞれ個性的な殺し方で人間たちを襲う。喉から殺人ヒルを出す必殺軍団紅一点のディープスローター、ひたすら頭のドリルをねじ込むだけのドリラー将軍etc……。前作でパペット・エフェクト（人形効果）を担当したD・アレンが監督。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("國訛道中笠", "國訛道中笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "年貢を工面するため娘・お初をかたに山形屋藤蔵から百両を借り受けた嘉右ヱ門は、帰途、その金を山形屋の手下に奪われた。女郎屋でありながら十手を預かり、こうした悪行をはたらく山形屋を、赤城山をあとにした旅姿の国定忠治が懲らしめる……。国定忠治の有名なエピソードを描いた伊藤大輔脚本、仁科熊彦監督の時代活劇。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばり・チエミの弥次喜多道中", "ひばり・チエミの弥次喜多道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ひばりとチエミの駆け合いが楽しい、マゲものミュージカル。ひょんなことから麻薬密売事件に巻き込まれた下町娘・お君とおとし。世の中の矛盾に嫌気がさした二人は、弥次郎兵衛、喜多八と名を代え、東海道を旅することにした。だが、市場拡大を狙う密売団と彼らを追う秋月七之丞も東海道を下ってくる……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本の仁義", "日本の仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「やくざ戦争・日本の首領」（1977）に続いて製作された実録ヤクザ映画の大作。多彩な人間模様を織り交ぜながら、政財界ぐるみの激烈な暴力団抗争を描く。仁義を通すべく人殺しの道を突っ走る菅原の狂おしさが、沈着な鶴田浩二との対比でくっきりと浮かび上がる。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE MANS", "栄光のル・マン", "", "", "http://ecx.images-amazon.com/images/I/51Ttv5iIVCL._SL210_.jpg", 109, "毎年フランスの小都市ル・マンで行われる、過酷な“24時間耐久レース”を舞台にした作品。マイクは、去年の大事故で命を落としかけながらも、再び“ル・マン”に人生を懸ける……。マルチ分割など様々な映像技巧を駆使し、レースの迫力をダイナミックに再現。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("隣人１３号", "隣人１３号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "中村獅童と小栗旬が“二人一役”を演じる超ハードなバイオレンス・サスペンス。原作はカリスマ的人気を誇る井上三太の同名コミック。元イジメられっ子の人格が二つに分裂し、小学校時代のイジメっ子を殺そうとつけ狙う。あまりにも凶暴な人格を演じる中村獅童が強烈。昔イジメられっ子だった村崎十三の体内に芽生えた、“13号”というもう一つの人格が暴走。かつてのイジメっ子への壮絶な復讐が始まる。", "2005-04-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BAD NEWS BEARS GO TO JAPAN", "かんばれ！ベアーズ 大旋風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "大ヒットした熱血野球コメディのシリーズ第３作。うさん臭い芸能エージェントのマーヴィンに半ばだまされるようなかたちで、日本に遠征することになったベアーズが野球のみならず、なんとアントニオ猪木を相手にプロレスでも大活躍。日本チームの監督との友情によって、マーヴィンも心を入れかえていく。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("影の軍団・服部半蔵", "影の軍団・服部半蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "家光の死後、世継ぎ争いに揺れる徳川幕府の裏舞台で、実力者の下で影のように暗躍する服部家の忍者（上家と下家の二つの家系がある）と、甲賀軍団との死闘を描く時代劇。工藤栄一のダイナミックな演出、奇抜なコスチュームなど、徹底した娯楽性が面白い。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVE OF DESTRUCTION", "EVE〈イヴ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "敵対国で特殊任務を遂行するために造られた軍事用人造人間“イヴ・エイス”が、テスト中に脱走。彼女の生みの親でもある博士やテロ対策の専門家らが追跡を開始するが……。女性版“ターミネーター”ともいえるニューキャラクター“イヴ”の悲しき殺人マシンぶりが見ものの女性アクション。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WIND WALKER", "ウィンドウォーカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "18世紀のアメリカ西部を舞台に、インディアンの部族間の戦いを描くファンタジー・ロマン。若かりし頃、クロウ族に妻を殺され、双子の息子の一人を奪われた戦士。やがて時を経て永遠の眠りについた彼は、宿敵クロウ族の襲撃の前に、伝説の英雄“ウィンドウォーカー”となってよみがえる……。名優T・ハワードが老戦士を堂々と演じている。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Ballon Rouge", "赤い風船　デジタル・リマスター版", "", "", "http://image.eiga.k-img.com/images/movie/53685/315x210.jpg?4094", 35, "フランスのアルベール・ラモリス監督が56年に発表し、カンヌ国際映画祭パルムドールに輝いた名作ファンタジー。街灯に赤い風船が引っ掛かっているのを見つけた少年パスカルは、そこから風船を取って大切に扱いはじめる。やがて風船はパスカルの行く先々に勝手についてくるようになるが、いたずらっ子たちの標的にされてしまい……。シンプルなストーリーとセリフによって描かれる詩情豊かな世界がデジタル・リマスターで鮮やかに甦る。「白い馬」（1952年／40分）も同時上映される。", "2008-07-26", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("E.YAZAWA ROCK", "E.YAZAWA ROCK", "", "", "http://image.eiga.k-img.com/images/movie/54765/315x210.jpg?2151", 0, "1972年、平和を歌うフォークが全盛期の日本音楽シーンに、リーゼントに革ジャンというファッションでロックバンド「キャロル」を率いてデビューした矢沢永吉。バンド解散後もソロ・アーティストとして絶大なる人気を誇る矢沢の生き様に迫るドキュメンタリー。レコーディング、リハーサル、筋力トレーニングにいたるまで密着し、さまざまな角度から矢沢の素顔を映し出す。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOURNEY INTO THE BEYOND", "超常現象の世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "科学で解明できない超常現象を世界各国から集めたドキュメンタリー・フィルム。イタリアのコズミック・パワーを用いた小児麻痺の治療、フィリピンの手を触れずに患部を切開し、ウミを出す心霊手術、そして圧巻は、西アフリカの黒人祈祷師が行う空中浮揚の儀式。", "1975-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAN MAN MAN", "人類狂気時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "地球上で最も面白い種族“人類”。その狂気の部分をテーマに、約３年の時間をかけて製作したイタリア得意の残酷ドキュメンタリー。南米ガイアナにおける人民寺院の900人集団自殺、ギリシャの牛殺し祭り、ザイールの白人大量虐殺など、世界の奇習、風習、残虐事件など20数エピソードで構成。", "1977-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WONDERWALL", "ワンダーウォール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "J・バーキンがイギリス時代に出演したロマンティック・コメディ。変わり者だが有能な教授オスカーが研究に没頭中、隣室の騒音に腹を立て、目覚まし時計を壁にぶつける。そこに開いた小さな穴をのぞくと、美しく若い女性の姿が目に飛び込んできた。すっかり彼女の魅力に取り憑かれた彼は、研究を放り出して壁中に穴を開けて隣の部屋を観察。やがて、彼女のボーイフレンドと対決するという夢まで見るようになるのだが……。ビートルズのG・ハリスンが、初のソロ活動で音楽を担当している。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JESUS", "JESUS〈ジーザス〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "『新約聖書』の“ルカの福音書”に基づき、イエス・キリストの生涯を飾ることなく再現して描くドキュメント・タッチの伝記映画。アカデミー賞やカンヌ受賞作を多く世に送り出してきたプロデューサー、ジョン・ヘイマン指揮の下、５年にわたる調査と研究を重ね、実際にキリストがいた場所で撮影。衣装や考証なども徹底して当時の様子を再現している。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狼少年", "狼少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "“あの世を見てみたい”という欲望に取り憑かれた旅人・国男はある夜、人魂に導かれるように山深い謎の村へ足を踏み入れる。そこには、霊や幻が見えると言う姉と、彼女を真っ向から否定する弟が住んでいた。やがて狂気に陥った姉は村の男を誘惑しては殺し始める……。異色の新人監督、丹内心道が、幻想の世界をさまよう悲劇を独創的な映像美で描き出した前衛的作品。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水戸黄門漫遊記〈総集編〉", "水戸黄門漫遊記〈総集編〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "当時人気の漫才コンビ、エンタツ・アチャコの主演映画。それに柳家金語楼が加わって、水戸黄門と助さん格さんならぬ、そのニセ者を演じる。日本のドタバタ・コメディにその人ありといわれた斎藤寅次郎監督の代表作の一つだ。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛のうつし絵（SHUNGA STORY）", "愛のうつし絵（SHUNGA STORY）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "歌麿、北斎、国貞、春信ら時代絵師たちの膨大な数の浮世絵春画から傑作300枚をセレクト、スクリーンに再現し、ナレーションの市原悦子が江戸の女たちの心をうたい上げた作品。人間の性の瞬間を表情豊かに描きながらも闇に封じこめられていた浮世絵春画に、今あらためて光を当てる。作家、山口洋子がナレーション構成を手掛けている。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渋谷", "渋谷", "", "", "http://image.eiga.k-img.com/images/movie/55135/315x210.jpg?2289", 78, "「東京漂流」「メメント・モリ」などで知られる写真家の藤原新也による、渋谷を舞台にしたフォトエッセイの映画化。主演は「鈍獣」の佐津川愛美、「クローズZERO 2」の綾野剛。監督は「青空のルーレット」の西谷真一。駆け出しのフリーカメラマン・水澤は、渋谷の少女たちの写真を撮り、記事を書くことを仕事としていた。ある日、渋谷の街角で母親を怒鳴りつける少女に出会い、事情を聞こうと彼女の働くファッションヘルスを訪ねるが……。", "2010-01-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAVID BYRNE LIVE FILM BETWEEN THE TEETH", "ビトウィーン・ザ・ティース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "デヴィッド・バーンの1992年ニュー・ジャージ州カウント・ベイシー・シアターでのステージを収めたライブフィルム。これまで決して演奏されることのなかった、トーキング・ヘッズ時代の楽曲がソロとして初めて披露された貴重なライブだ。バーン自らが演出を担当、臨場感あふれる仕上がりとなっている。『ウェル』『ライ・トゥ・ミー』ほか、全15曲収録。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("友の碑／白梅学徒の沖縄戦", "友の碑／白梅学徒の沖縄戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "太平洋戦争末期の沖縄戦で多くの犠牲者を出した、“白梅学徒”と呼ばれる学徒看護隊のドキュメンタリー。軍属として野戦病院に配属された女学生たちの壮絶な体験を、生存者の貴重な証言から振り返る。彼女たちが勤務した現場を訪れ、戦中当時を回想、戦争の理不尽さを静かに力強く訴える。太平洋戦争末期の'45年、補助看護婦として56名が従軍。その生存者たちがかつての現場に赴き、胸の奥にしまっていた思いを語る。", "2005-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キスより簡単", "キスより簡単", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "石坂啓の同名コミックを原作に、若松孝二監督が全共闘世代の立場から現代の青春を描いた異色作。女子大生の泉まあこは、父親がそれぞれ違う四人姉妹の三女で、自分の父親が誰であるかもわからずに育ち、自由な恋愛、気楽なセックスを楽しんでいる。現在のまあこの相手は、自分の父親候補の一人、シンちゃんの息子・スズナリだ。彼女は近親相姦の危険さえヘッチャラ。しかし、彼女が本当に愛してしまったのは、やはり父親候補のロクさんだった……。映画はやがてこのロクさん役の原田芳雄に焦点を当て、いつしか戦い続ける男の賛歌を奏で始めていく。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Angel-A", "アンジェラ", "", "", "http://image.eiga.k-img.com/images/movie/1005/315x210.jpg?3698", 90, "48時間後には命を奪われているかもしれない男、アンドレが、人生を悲観してアレクサンドル三世橋からセーヌ河に身を投げようとしていたとき、突然現れた謎の美女アンジェラが、「あなたと同じことをする」といって河に飛び込むが……。リュック・ベッソン6年ぶりの監督作で、音楽をノルウェーの人気ボーカリスト、アンニャ・ガルバレクが担当。撮影は「ニキータ」「レオン」など、ほとんどのベッソン作品を手がけるティエリー・アルボガスト。", "2006-05-13", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dead of Night", "夢の中の恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ロンドン郊外の広大な邸宅。ウォルターはそこのオーナーに理由のわからぬまま招待される。そこに集まった人たちは、不思議な体験をしていて、一人一人がその体験について話し始める。そしてウォルターは初対面のはずの彼らにどこかで会っていたことに気付くのだった。英国の監督４人による、ホラー・アンソロジー。", "1945-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("越後獅子祭", "越後獅子祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "当時、松竹がコンビとして定着させようとしていた田村、瑳峨共演による娯楽時代劇。旅人・片目の半四郎は、敵と間違えて、浪人・駅沢番十郎に斬りかかり、番十郎の仇・浅井朝之助を逃がしてしまう。その後、旅回り一座の用心棒になった番十郎と半四郎は再会。二人は友情で結ばれる。だが、一座の座長・小陣と結婚させられそうな相手が朝之助と知って……。父、阪東妻三郎ばりの田村の立ち回りが見もの。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("継承盃", "継承盃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ヤクザものとは縁のなさそうな大森一樹を起用した、異色の東映ヤクザ映画。証券業界から転職した主人公の視点から、ヤクザ世界のしきたりをめぐる悲喜こもごもを企業ドラマを思わせる構成で、軽快に描いていく。元営業マンの才を生かして、金の取り立てに冴えを見せる吉成。まだ新入りの彼が、組長引き継ぎの儀式“継承式”を成功させるために奔走するハメになる。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAWS2", "ジョーズ2 ", "", "", "http://ecx.images-amazon.com/images/I/517NK9MCAXL._SL210_.jpg", 116, "大ヒット・パニック映画のシリーズ第２作。マサチューセッツの小さな海水浴場アミティを突然襲った巨大な人喰いザメ。あれから３年、新しいホテルもオープンし、街は活気を取り戻した。その記念式典の翌朝、スカイダイビング中の二人が行方不明になる。続いて水上スキーの少女も。警察署長ブロディが再び人喰いザメと対決する。おなじみJ・ウィリアムズの効果的なテーマ曲が恐怖心をあおる。シャイダーの好演、サメの視線で描かれる水中撮影（特に水上スキーを追うシーン）など前作を踏襲することなく見せ場もたっぷり。DVDは「ジョーズ２，３＆４コレクション」に収録。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARIA'S LOVERS", "マリアの恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ペンシルバニアの田舎町。戦場から帰還したイバンは、以前から思いを寄せるマリアに求婚、二人は結婚するが……。愛するがゆえに彼女の前では不能となる男と、夫に純潔を捧げながらも官能の誘惑にさいなまれる女のエキセントリックなラブ・ストーリー。キンスキーの透明感ある美しさが魅力。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT TOUCH OF MINK", "ミンクの手ざわり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "グラントとデイのコンビによるロマンティック・コメディ。大金持ちで独身のシェーンは名うてのプレイボーイ。そんな彼が、田舎からニューヨークへ上京してきたソバカス美人のキャシーに手を出したことから、珍騒動が巻き起こる。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COME BACK CHARLESTON BLUE", "ハーレム愚連隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "荒っぽいが常に陽気でバイタリティーあふれる黒人刑事“墓掘りジョーンズ”と“棺桶ジョンソン”の名コンビが、ハーレムを舞台に暴れまわるアクション・コメディ。ハーレムでカミソリを使った奇怪な連続殺人事件が起こる。さっそく捜査にあたった黒人刑事コンビは、カメラマンの青年に目をつけるが……。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVEN CAN WAIT", "天国から来たチャンピオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "H・シーガルの舞台劇を映画化した「幽霊紐育を歩く」の再映画化。プロアメリカン・フットボールの有望選手ジョーは、突然、自動車事故で死亡し、天国へ逝ってしまう。新米天国案内人の早とちりで、彼の天国への招聘を50年も早めてしまったのだ。間違いに気付いた時、ジョーの肉体はすでに灰になっていた。そこで彼は青年実業家の体に乗り移るが……。ハートウォーミングなタッチで綴ったファンタジー。ベイティ、クリスティなどの多才な個性派たちがさわやかな好演を見せる。後に「レッズ」でアカデミー監督賞に輝くベイティの豊かな才能を予感させるに十分な佳編。D・グルーシンのサックス音楽も素晴らしい。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・新悪名", "続・新悪名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "戦後のヤミ市を追放された朝吉は、大阪で女剣劇一座に横ヤリを入れている演芸館主・玉島に話をつけにいき、そこで用心棒に雇われている清次と再会する。興行は無事終わるが、一座のギャラが持ち逃げされてしまう。朝吉は自分の体をカタに借金するが……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("告白", "告白", "", "", "http://image.eiga.k-img.com/images/movie/55065/315x210.jpg?4666", 106, "2009年本屋大賞を受賞した湊かなえのミステリー小説を、「下妻物語」「嫌われ松子の一生」の中島哲也監督が松たか子主演で映画化したサスペンスドラマ。ある中学校の1年B組の担任を務める女性教師の森口（松）は、愛娘を学校のプールで殺害される。警察は事故死と判断するが、森口は学年末の終業式の日に、犯人はクラスの中にいると生徒たちに告げる。共演に岡田将生、木村佳乃。", "2010-06-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("European Vacation", "ナショナル・ランプーンズ・ヨーロピアン・ヴァケーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "　グリスウォルド一家はテレビのゲームショーで優勝し、ヨーロッパ旅行へ出発する。イギリス、フランス、ドイツ、イタリアと旅していくが、行く先々でトラブルが。しかもイタリア・ローマでは誘拐事件まで起こり……。83年の「ホリデーロード4000キロ」に続く、チェビー・チェイス、ビバリー・ダンジェロ共演のコメディシリーズ第2弾。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("No Direction Home: Bob Dylan", "ボブ・ディラン：　ノー・ディレクション・ホーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 201, "マーティン・スコセッシが久しぶりに手がけた音楽ドキュメントの秀作。'66年、自身の音楽を“エレキ化”したことで舞台上で観客から“裏切り者”と、ののしられたボブ・ディランの肖像を再構築する。デビュー以後、社会的メッセンジャーとしての役割を担っていた表現者の“決断”に目頭が熱くなる。23歳にして社会的影響力のあるフォーク歌手だったボブ・ディラン。彼が突然、エレキ・ギターを手にした'66年前後の“バッシング”を描く。", "2005-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METEO", "バーチャル・ジャッカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "近未来の東欧のとある都市。巨大工場の跡地に住み着いていた気象学者のエッカーマンは、その友人のベルリオズ、ベローと現在の境遇から逃げ出そうと、コンピュータを使った犯罪計画を実行に移す……。一日中バスタブにつかり白昼夢にふけるエッカーマン、暴力に明け暮れるベルリオズとベロー。対照的ながら、どちらも危ない３人組と、彼らを待ち受ける異様な事件が見もの。", "1990-01-01", "3,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌８", "釣りバカ日誌８", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ハマちゃんとスーさんの釣りバカコンビが珍騒動を繰り広げるシリーズ第９作。亡き友人・榊原の三回忌に出席できなかったスーさんは、休日に訪れた榊原家で、娘で外科医の和美と再会するやいなや、大腸検査を勧められる。結果、悪性のポリープと診断されるが、早期発見のため一命を取り留める。一方、ハマちゃんには、湯川省平こと通称“博士”と呼ばれる新たな弟子ができていた。そんな中、お礼を兼ねてスーさんは釣りが趣味という和美を誘い、ハマちゃんと約束していた福島県いわきでの投げ釣りに出かける。そこで和美は“博士”と意気投合するが……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Foreign Correspondent", "海外特派員", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "　ドイツの台頭で戦争の危機が迫っていた1939年、ニューヨークの新聞記者ジョニーは欧州へ派遣される。戦争回避のキーマンである大物政治家を追いやってきたアムステルダムで、暗殺現場に遭遇。犯人を追跡し、意外な事実を突き止めたことから彼自身も命を狙われるようになる。美術は「風と共に去りぬ」で知られるウィリアム・キャメロン・メンジーズ。150万ドルの製作費を投じ、大掛かりなセットのもと撮影された。飛行機の撃墜シーンなど撮影技術も見事である。", "1976-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大蛇王／KING OF SNAKE", "大蛇大戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "あらゆる生命体を巨大化させるバイオシステムを、ふとしたきっかけで拾ってしまった少女テンテン。何も知らない彼女は、その装置の中にペットの蛇モスラを入れてしまう。その装置を狙うある組織にテンテンが誘拐され、巨大化したモスラは彼女を救うため、街に姿を現わす。ビデオ発売時タイトルは「大蛇王 HONG KONG 崩壊の序曲」。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MUSIC BOX", "極楽ピアノ騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "ピアノを階段の上にある家まで運び上げなければならない、二人の配達員の悪戦苦闘ぶりを描いたサイレント・コメディ。ローレル＆ハーディの快調なコメディ・シーンに加えて、歌やダンスも披露するという、サービス満点の一作。ローレル＆ハーディの最もいい部分が出た作品でもある。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桜の森の満開の下", "桜の森の満開の下", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "坂口安吾の短編小説を、篠田監督が耽美的手法で映画化。王朝時代、山里に住む山賊が、惚れた都の女の言いなりになり、人間の首の収集を続ける。一面満開の桜の下で、男に背負われた女が異形の山姥に変貌するラストの幻覚シーンが異様に美しい。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乱歩地獄", "乱歩地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "耽美かつ幻想的な怪奇譚がいまも熱狂的な支持を集める文豪・江戸川乱歩の世界を、4人の映像クリエイターによって実写化した風変わりなオムニバス映画。PV界の俊英・竹内スグル、乱歩の小説になじみの深いベテラン、実相寺昭雄など、世代も出自も異なる個性豊かな監督陣が顔をそろえており、個性派俳優・浅野忠信が全部の作品に出演する大活躍を見せている。ほかのキャストも、松田龍平、成宮寛貴、緒川たまきなど若手を中心にした豪華なメンツ。斬新な解釈の乱歩ワールドに身をゆだねたい。名探偵・明智小五郎は、妻が療養中の鎌倉で起きた連続殺人に興味を持つ。それは、鏡作りに取り憑かれた美青年、透と情交を持った女性が次々と変死するというものだった（「鏡地獄」）。", "2005-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オンシン不通 Line Disconnect", "オンシン不通 Line Disconnect", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "", "2011-01-01", "76,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PORK CHOP HILL", "勝利なき戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "数多くの戦争映画を作っているマイルストン監督が、朝鮮戦線における通称ポーク・チョップ・ヒルの戦闘に取材して作った戦争映画。軍事評論家マーシャルの実録小説がもとになっている。戦局を重大に左右しかねないポーク・チョップ・ヒルの攻防。和平ムードで士気のあがらない部下を率いて、クレモンズ隊長は、激烈な砲火の中を進んでいく。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLADE TRINITY", "ブレイド3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "人間と吸血鬼の血を引くヴァンパイア・ハンター、ブレイドの最後の死闘が繰り広げられる人気シリーズの完結編。敵を倒した後のポーズもキマる、主人公のヒロイックな暴れっぷりがますます冴える。UVレーザー銃や、手首やブーツから飛び出すナイフなど、ユニークな秘密兵器の活躍にも注目。吸血鬼集団の罠に落ち、窮地に立たされたブレイド。人間の戦士集団の協力を得た彼は、吸血鬼の始祖を復活させた敵の陰謀に立ち向かう。", "2005-05-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES SEINS DE GLACE", "愛人関係", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "南仏ニースを舞台にした謎の美女をめぐるミステリー。ペギーは、かつて夫殺しの罪に問われるが敏腕弁護士マルクの弁護によって無罪判決を得、その後、マルクの保護下で優雅に暮らしている。そんなペギーに恋するテレビ作家のフランソワ。だが、ペギーの近辺で次々と殺人事件が起こり、フランソワも負傷する。ついに警察は、ペギーを逮捕するとマルク弁護士に宣告する。その頃、ペギーはフランソワと新しい生活を始めようとしていた。だが突然、カミソリを手にフランソワに襲いかかるペギー。その場に駆けつけたマルクは……。当時、愛人関係であったドロンとダルクの共演で話題を呼んだ作品。", "1973-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドンを撃った男", "ドンを撃った男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "実在のチンピラをモデルに描く仁侠活劇。巨大組織に逆らい、大物組長を銃殺。３万人の極道を敵に回した伝説の男の生き様を、的場浩司がギラギラした個性で熱演している。血気盛んな若者、成田が暴力団、西成義賊団の一員になった。だが、熾烈な抗争でボスを失った義賊団はあえなく解散。兄貴分を殺された成田は、復讐に燃え、報復をたくらむ……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おそいひと", "おそいひと", "", "", "http://image.eiga.k-img.com/images/movie/53095/315x210.jpg?1870", 83, "重度の身体障害者が犯罪を犯すというショッキングな内容から、日本での一般公開が見送られてきた問題作。実際に脳性麻痺を持つ住田雅清が、本名のまま主人公を演じる。電動車椅子で移動しボイスマシーンで会話する障害者の住田の元に、介護を経験したいという女子大生・敦子が現われる。その頃から、住田はある種の違和感を抱くようになり……。監督は「NN-891102」など危険なテーマに挑み続ける柴田剛。", "2007-12-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("出張", "出張", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "東北地方に出張の途中、ゲリラと称する一団に拉致された中年サラリーマンを主人公に、サラリーマンの悲哀を描き出したブラックな笑いがいっぱいの悲喜劇。若松プロで脚本家や助監督として活躍し、「ニュージャック＆ベティ」でデビュー、以来TVの脚本などを手掛けていた沖島勲の21年ぶりの作品。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Mist", "ミスト", "", "", "http://image.eiga.k-img.com/images/movie/53105/315x210.jpg?1906", 125, "「ショーシャンクの空に」「グリーンマイル」でスティーブン・キングの世界を見事に映画化したフランク・ダラボン監督が、映像化不可能と言われていたキングの傑作中篇「霧」に挑んだ意欲作。激しい嵐が過ぎ去った町に不気味な深い霧が立ち込め、住民たちは身動きが取れなくなってしまう。やがて霧の中に潜んだ正体不明の生物が彼らを襲いはじめ……。原作とは異なる衝撃のラストが全米公開時に大きな話題を呼んだ。", "2008-05-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ВОСКРЕСЕНИЕ", "復活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 208, "陪審員として裁判に参加した公爵は、被告が10年前に自分が犯した娘であることを知る。彼が良心の呵責にさいなまれながらも、やがて改心するまでを描く、トルストイの同名小説の忠実な映画化。幸薄くも気丈なカチューシャをショーミナが熱演。広大なロシアの風景もロケ効果満点。", "1961-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VANYA ON THE 42ND STREET", "42丁目のワーニャ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "1995年11月に死去したL・マルの遺作となったドキュメンタリー趣向のドラマ。ニューヨーク42丁目にあるニュー・アムステルダム・シアターで行われるチェーホフ原作の『ワーニャ叔父さん』の通し稽古の様子を淡々と追っていく。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春祭／SACRIFICED YOURTH", "青春祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "中国・雲南省のタイ族の村を舞台とした作品。都会育ちの17歳の少女・李純は下放され、雲南省の辺境の村にたどり着く。そこで彼女はタイ族の人々から温かく迎えられ、この美しい村の生活を楽しむ。やがて隣村の下放されていた青年と、タイ族の青年の両方から愛されるようになった彼女は……。", "1985-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おしゃべり社長", "おしゃべり社長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "平和玩具社長・大前田の海外出張のため、代理社長に選ばれたのは元社員で今はルンペンの早乙女修。就任早々改革の嵐と長～いおしゃべり訓辞に社員たちはキリキリまい。新製品“笑う玩具”の売れいきやいかに？", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEDITERRANEO", "エーゲ海の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "第二次世界大戦たけなわの1941年。エーゲ海に浮かぶ小さな島に、落ちこぼればかりが集まった８人のイタリア軍の小隊が上陸した。“ここはイタリア人の墓場だ”と書かれた落書きを発見した彼らは、こわごわ島へと乗り込むが、やがて出会った島民たちは親切このうえない。すっかり島の生活にも慣れ、時のたつのも忘れて牧歌的な雰囲気のなかで暮らしていた彼らだったが、やがて戦争が終結する日がやってきた……。監督はナンニ・モレッティ、ダニエル・ルケティらとともにイタリアン・ニューシネマの旗手と目されたG・サルヴァトレス。美しくフォトジェニックなエーゲ海の景観が印象的だ。", "1991-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吉祥寺の朝日奈くん", "吉祥寺の朝日奈くん", "", "", "http://image.eiga.k-img.com/images/movie/57155/315x210.jpg?4830", 91, "東京・吉祥寺の全編ロケで撮り上げられた青春恋愛劇。「百瀬、こっちを向いて。」の中田永一による原作小説を映画化した。役者への夢も破れ、吉祥寺の街でバイトをしながら暮らしている朝日奈の前に、ひとりの女性が現れる。人妻で子持ちの彼女に恋をしてしまった朝日奈は思い悩むが、その恋の結末には思わぬどんでん返しが待ち受けていた。主演は「仮面ライダーW」の桐山漣。共演に「私は猫ストーカー」の星野真里。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COAL MINER’S DAUGHTER", "歌え！ロレッタ・愛のために", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "13歳で結婚、18歳で４児の母となり、歌手として活躍。やがて“シンガー・ソングライターの女王”に……。米国のポップ歌手、ロレッタ・リンの自伝をもとに描く波乱万丈の半生記。スペイセクの名演は、1980年度の映画各賞を総ナメにした。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Loft", "ロフト.", "", "", "http://image.eiga.k-img.com/images/movie/54865/315x210.jpg?3779", 117, "08年にベルギーで大ヒットした密室を舞台にしたサスペンス。建築家のビンセント、その友人のマルニクス、ルク、精神科医のクリス、クリスの義弟のフィリップは、妻や恋人に隠れて新築マンション最上階にあるロフトルームを情事部屋として共有していた。ある日、ルクが部屋に入ると、ベッドに手錠をかけられ血まみれで死んでいる女がいた。慌てて部屋に集まった5人の男たちは、互いのアリバイを探りながら犯人探しを始めるが……。", "2009-11-20", "37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タリラリラン高校生", "タリラリラン高校生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "大学進学のため、友達から銭ゲバと呼ばれるほどアルバイトに精を出している秀才と、高校生とは思えないほどの不良が友情で結ばれ、予備校の授業料強奪計画を立てる。それは不良にとって父の死後、母と関係している異母兄への復讐なのだ。犯罪と男の友情ほかが交錯する“アメリカン・ニューシネマ”を意識した作品。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Partir", "旅立ち（原題）", "", "", "http://image.eiga.k-img.com/images/movie/55345/315x210.jpg?2500", 85, "主演は「フォー・ウェディング」「イングリッシュ・ペイシェント」のクリスティン・スコット・トーマス、監督は「彼女たちの時間」のカトリーヌ・コルシニ。スザンヌは医者の夫と2人の子供と南仏に暮らす専業主婦。何不自由ない暮らしを送るも、スザンナは退屈な日々に鬱屈し、育児のために諦めた運動療法士の仕事を再開することにする。カウンセリングルームの増築が始まると、スザンヌは工事を担当する男イバンと強く惹かれ合うようになる。フランス映画際2010にて上映。", "2009-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SCORPION KING", "スコーピオン・キング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "大ヒット冒険映画「ハムナプトラ2／黄金のピラミッド」に登場したキャラクター、スコーピオン・キングを主人公にしたスペクタクル・アクション。主演は米国の人気プロレス団体WWE(元WWF)のスーパースター、ザ・ロックが演じている。舞台は5000年前のエジプト。戦乱に明け暮れるゴモラの都で、邪悪なパワーを使って遊牧民の絶滅を図る支配者と、一人の戦士スコーピオン・キングの戦いが繰り広げられていく。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EVOLVER", "エボルバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ロボットの反乱を現代風に描いたスリラー。ゲームの優勝賞品としてカイルに贈られた戦闘ロボット“エボルバー”には、アメリカ国防総省の極秘プログラムが搭載されていた。対戦ごとに素早く、賢くなっていくエボルバー。カイルはエボルバーの放つ弾丸に、鉄球が混じっているのに気付く。エボルバーは、敵を残らず殺すために“進化する殺人兵器”としてプログラムされていたのだ。やがて友人を殺し、妹を襲い、開発者まで殺害したエボルバーは、レーザー砲を使うまでに進化し、カイルに容赦なく襲いかかる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORTRESS", "フォートレス 未来要塞からの脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "国外逃亡に失敗し、ハイテク・システムを擁する強制収容所に入れられてしまったブレニック夫妻。ブレニックは監房の仲間たちとともに、妻を救い出して決死の脱獄を試みる。脱出不可能とされる刑務所からの逃亡劇を描いた近未来SFアクション。C・ランバートが、タフな主人公役に挑戦。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Heaven's Soldiers", "天軍", "", "", "http://image.eiga.k-img.com/images/movie/33655/315x210.jpg?4572", 106, "韓国と北朝鮮の兵士が朝鮮時代にタイムスリップする、韓国版「戦国自衛隊」ともいえるSF大作。南北共同開発による核兵器をアメリカに引き渡すことに反対した北朝鮮人民軍将校カン・ミンギルは、核兵器を奪って逃亡。追跡する韓国軍将校パク・チョンウとの激しい戦いの途中、彼らは16世紀にタイムスリップしてしまう。ハリウッドでも活躍するベテラン俳優パク・チュンフン、人気テレビドラマ「ホテリアー」のキム・スンウらが出演。", "2006-09-23", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELL AND HIGH WATER", "地獄と高潮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "S・フラー監督が、原爆をテーマに描くアクション映画。北氷洋の共産軍基地を潜水艦で偵察にいった元海軍士官が、原爆爆発装置を積んだ貨物船を尾行するうちに、中国軍の恐るべき陰謀に直面していく。「拾った女」に続いて、R・ウィドマークがフラー作品に主演している。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陸軍中野学校・竜三号指令", "陸軍中野学校・竜三号指令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "昭和15年、和平交渉をすべく上海に向かった日高大佐一行は何者かに襲撃され全員死亡。現場に落ちていた１枚の銀貨から、椎名は情報をもらした敵スパイを発見する……。舞台を日本から中国・上海に移し、アクション面でのスケールアップが見られる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Illusionist", "幻影師アイゼンハイム", "", "", "http://image.eiga.k-img.com/images/movie/53545/315x210.jpg?2547", 109, "19世紀末のウィーン。天才幻影師アイゼンハイムの評判を聞きつけた皇太子は、婚約者のソフィを連れて劇場を訪れる。実はアイゼンハイムとソフィの間には、愛し合いながらも身分違いのために引き裂かれたという切ない過去があった。ほどなくしてソフィが皇太子邸で謎の死を遂げ……。ピューリッツァー賞作家スティーブン・ミルハウザーの短編をエドワード・ノートン主演で映画化。全米公開時には口コミで人気が広がり大ヒットを記録した。", "2008-05-24", "1,43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dnevnoy Dozor", "デイ・ウォッチ", "", "", "http://image.eiga.k-img.com/images/movie/53355/315x210.jpg?2270", 131, "光と闇の戦いを最新VFX満載の壮大なスケールで描き、本国ロシアで記録的大ヒットを叩き出したダーク・ファンタジー「ナイト・ウォッチ」の続編。闇の者を監視する光の者“ナイト・ウォッチ”のアントンは、研修生のスベトラーナとパトロール中に一般人が襲撃されたとの報せを受け、現場のVDNH駅へと向かう。しかしその犯人は、かつてアントンの行いのせいで闇の者となる道を選んだ息子イゴールだった。", "2008-02-16", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GORILLE VOUS SALUE BIEN", "情報〈ネタ〉は俺が貰った", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "胸囲140センチという巨漢秘密警察隊員、通称ゴリラが、国連進駐外交使節を装うスパイ団を追って、素手で機関銃に立ち向かったり、両手で自動車を宙に持ち上げたりと大活躍。不敵な面構えと性格演技でフィルム・ノワールの売り出し中の名脇役だったヴァンチュラの主演昇格作。そのタフネスなキャラクターが小気味よいギャング映画。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("10 Things I Hate About You", "ヒース・レジャーの恋のからさわぎ", "", "", "http://image.eiga.k-img.com/images/movie/55315/315x210.jpg?1473", 97, "自身の死後に公開された「ダークナイト」（2009）で、アカデミー助演男優賞を受賞するなど、その演技力が高く評価されながら、2008年に急性薬物中毒で急逝したヒース・レジャーの初主演作品。転校生のキャメロンは、登校初日に学園のアイドル・ビアンカに一目ぼれし、何とか彼女をデートに誘おうとする。しかし、ビアンカの父親に、姉・カトリーナが先に誰かとデートしないとビアンカのデートを許可しないと言われ、学校のはみ出し者パトリック（レジャー）を雇ってカトリーナをデートに誘わせようとするが……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOG　SOLDIERS", "ドッグ・ソルジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "スコットランドに伝わる伝説の怪物、人狼を登場させたスリラー。臓物が飛び出すバイオレンスに加え、生存者たちが必死になればなるほど状況がひどくなる描写のブラックな笑いが刺激的た。山奥で演習を行っていたイギリス軍の小隊を、謎の獣の群れが襲撃。生き残った隊員たちは、山中の無人の農家に身を隠す。ところが、そこは彼らを襲った怪物＝人狼の棲み家だった。", "2001-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUMMER OF FEAR／STRANGER IN OUR HOUSE", "ウェス・クレーブンの戦慄の夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "自動車事故で両親を亡くした従妹のジュレアがレイチェルの家に引き取られてきた夏、次々と奇怪な現象が起こりはじめる。「エルム街の悪夢」 「ゾンビ伝説」などで知られるW・クレイヴン監督が、「エクソシスト」のL・ブレアを主演に迎えて撮り上げたオカルト・ホラー。TVフィーチャー作品。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BANANAS", "ウディ・アレンのバナナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "監督デビュー作「泥棒野郎」に続く、W・アレンの自作自演コメディ。“テレビ的”と評される初期作品だが、ここでも興味の中心はプロットにあって映像はきわめて淡白なものだ。主人公は金ない・さえない・当然モテないという三重苦の青年メリッシュ。エコロジストの娘を口説くきっかけにするため、彼はバナナぐらいしか産業のない南米の小国の革命軍募集に応じたが、南米でも彼はドジばかり。ところがこの地で革命が実現し、つけヒゲが似合うばかりに彼が初代大統領に選ばれてしまったから大変。援助要請のために、国賓として故国に向かうハメに……。無名時代のS・スタローンが脇役で出演している。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHE？", "ポランスキーの欲望の館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ポランスキーがイタリアで撮ったエロティック・コメディ。ヒッチハイクをしているアメリカ娘は、リビエラの、とあるヴィラに紛れ込む。そこには好色なうえに、どこかまともでない男たちが住んでいた……。明るいエロチシズムとブラック・ユーモアあふれる痛快作。", "1972-01-01", "4,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kuchisake 口裂け", "Kuchisake 口裂け", "", "", "http://ecx.images-amazon.com/images/I/31C063E5BRL._SL210_.jpg", 63, "麻田真夕、藍山みなみ、華沢レモンら人気セクシー女優6人が共演したエロティックホラー。出版社で働く洋子は、夫の朝霧や腹違いの妹・和美と3人で暮らしている。ある日、洋子は、口裂け女にまつわる都市伝説の取材を任されるが、前任者の加藤は3日前から行方をくらませていた。一方、和美の彼氏・孝次と浮気相手のみさおは、口裂け女が出ると噂される廃病院に忍びこみ、何かに憑かれたようにセックスをはじめるが……。", "2005-09-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アイランズ／島々", "アイランズ／島々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「トルペド航空隊」などで知られるS・D・アラノヴィッチ監督の呼びかけに応じて製作された、日露合作による北方領土ドキュメンタリー。映画は２部構成となっており、日本側の大塚汎監督の手になる「昭和」とアラノヴィッチが監督した「平成」に分かれる（全体の構成はアラノヴィッチが担当）。最初の「昭和」では、失われた島に対する元島民の思いが語られ、そして「平成」では当時、北方四島に住むロシア人たちの姿を追っていく。前半部、元島民の表情を徹底的に狙った映像はそれほどの感動を呼ぶことはないが、後半部にさしかかるや映画は一気に生気を帯びる。島々の荒涼たる風景と濁った光、そしてそこに生きる人々の感動的なモノローグ……。打ち捨てられた島々に生きねばならない悲劇をことさらに嘆いてみせることなく、それに甘んじて生きていく彼らの姿を切り取ったカメラの素晴らしさ。O・カラバイチュークの音楽も印象深い。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　BRYLCREEM　BOYS", "友情の翼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "第二次世界大戦時の実話に基づくヒューマン・ドラマ。舞台は、中立国アイルランドのダブリンにある捕虜収容所。連合軍、ドイツ軍の捕虜を平等に扱う、この収容所にはパブやスポーツ施設、競馬場もあった。戦火とは無縁の、そんなのどかな空気の中で、カナダ人飛行士マイルスは、一人の娘をめぐってドイツ軍のパイロット、ルドルフと対立。戦場なら敵同士となるものの、やがて彼らの間には奇妙な友情が芽生えていく……。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Berlin Now", "ベルリン・美しき夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "電気ドリルやチェーンソーを手に破壊的なインダストリアル・ミュージックを紡ぎ出し、1980年代のミュージック・シーンに衝撃を与えた伝説のバンド、アイン・シュテュルツェンデ・ノイバウテンを始め、ドイツで活躍するアーティストたちのライブ・シーン等を収録したドキュメンタリー作品。「パンク・イン・ロンドン」「恋のロックバルーン」等の作品を手掛けたボルフガング・ビュルトと、シシィ・ケリングが共同でメガホンを取る。", "1985-01-01", "45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES JEUX SONT FAITS", "賭はなされた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "革命運動で殺された男と、夫に毒殺された女は死者登録所で出会い恋に落ちる。本来二人は、現世で結ばれるはずだったことを知り……。実存主義で知られるサルトルの原作を映画化。現世と来世にまたがって展開する幻想の物語である。", "1947-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風雲千両船", "風雲千両船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "東宝創立20周年記念映画。稲垣浩とのコンビが多い大谷友右衛門が、幕末の砲撃手という、異色の役柄を演じる。かつて幕府の砲手として一目置かれた黛三五郎は、持ち前の豪胆さが災いして、落ちぶれて長崎で花火師になる。ある日、ケンカで傷を受けた黛は、ギヤマン亭のマダム・鈴江に助けられた。薩長も幕府も、鈴江の手の内にある外国船を狙っていて、黛はその争奪戦に巻き込まれる。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Outlander", "アウトランダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・スパイダースの大進撃", "ザ・スパイダースの大進撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ザ・スパイダースの７人組が帰国してからというもの、彼らの身辺では物騒な事件の連続。どうやら犯罪組織から狙われているらしい……。香港ショー・ブラザースに出向いてしばしばメガホンを執っていた時代の中平康が演出した、最後の日活作品。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("娘三羽烏", "娘三羽烏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "佳代子・恵美・敬子、大親友のOL３人組それぞれの恋模様を描いた、松竹お得意の三羽烏青春喜劇。新婚旅行の行き先が八丈島というあたりに時代を感じるものの、今なら逆に新鮮だ。監督は松竹メロドラマや青春喜劇路線作を数多く手掛けた穂積利昌。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Star Light", "Star Light", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "タレント養成所を舞台にした青春ダンス・ムービー。ヒップホップ・ユニット、w-indsを筆頭に人気ダンスグループが多数出演。未来のスターを目指す少年少女たちのひたむきな情熱を描き出す。スターを夢見てタレント養成所に通う大親友の郁子とこず絵。二人は努力が実り、とある映画のオーディションの選抜メンバーに選ばれる。だが、些細な誤解から二人は仲違いしてしまう。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARRIAGE ON THE ROCKS", "結婚専科", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "シナトラとマーティンの多くのTVショーを演出したドノヒュー監督が、その名コンビを起用して描いたピンク・コメディ。一流会社の社長と副社長。二人は親友だったが、社長の妻ヴァレリーが結婚記念日の当日に、夫と別れたいと言い出して……。気ままな女心を演じるD・カーが好演。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("岸和田少年愚連隊　岸和田少年野球団", "岸和田少年愚連隊　岸和田少年野球団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "中場利一の原作をもとにした“岸和田少年愚連隊”シリーズ第４作。1970年、岸和田の不良少年リイチの親友ガスとユウジ。野球に夢中だった二人は、モテモテの優等生に敵意を感じるが、彼の豪腕を買い仲間に引き入れる。ところが、彼の持病が悪化し……。ハチャメチャだけどちょっぴり切ない少年時代の思い出を綴る青春コメディ。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bridget Jones: The Edge of Reason", "ブリジット・ジョーンズの日記　きれそうなわたしの12か月", "", "", "http://image.eiga.k-img.com/images/movie/1025/315x210.jpg?2861", 108, "2001年の大ヒット作「ブリジット・ジョーンズの日記」の続編を同じ主演陣で映画化。監督は「3人のエンジェル」「輝きの海」のビーバン・ギドロンに交替。前作の6週間後、弁護士のマークとの交際を始めて幸せいっぱいのブリジットだったが、マークと彼の美人アシスタント、レベッカの仲が怪しいとの噂を聞いて不安になり、さらに誤解が重なってマークと大ゲンカしてしまう。主演のレニー・ゼルウィガーは前作以上の体重増を敢行。", "2005-03-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふざけろ！", "ふざけろ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "現在は活動休止中のコント・グループ“B-21スペシャル”の初主演作で、お人好しの銀行強盗と、その人質になった客や銀行員、そして彼らを包囲した警官隊との珍妙なやり取りをコミカルに描いたナンセンス・コメディ。コメディアン・玉川良一の愛弟子である玉川長太が初メガホンを執っている。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はだしのゲン２", "はだしのゲン２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "太平洋戦争がもたらした悲劇にもめげずに、たくましく生きていく少年とその家族の姿を捉えた名作アニメの第２弾。原爆の爪跡が残る広島の町で、ゲンは小学４年生となり楽しく暮らしていたが、母が被爆の後遺症で病気になってしまう。そこで、闇市で出会った孤児たちとお金を稼ごうとするのだが……。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CUTTING EDGE", "冬の恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "銀盤の華、フィギュア・スケートを題材にした青春ドラマ。英才教育を受けた天才スケーター、ケイトはその勝気な性格から、パートナーを幾度となく替えていた。そんな彼女と新たにコンビを組むのは、元ホッケー選手のダグ。持ち前のガッツで、彼はフィギュアの腕を上げていくが、やはりケイトとの相性は最悪。何度も衝突し合う二人だが、お互いのひたむきなスケートへの情熱に心惹かれ始め……。オリンピックを目指す若い男女の青春を、スピード感あふれるスケート・シーンを交えてドラマティックに演出。監督のP・M・グレイザーは本作を“現代版「じゃじゃ馬ならし」だ”と語っている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AND YOU THOUGHT YOUR PARENTS WERE WEIRD！", "ニューマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ハロウィンの夜を舞台に描くファンタスティック・コメディ。町でも評判の発明好きの兄弟、ジョシュとマック。数年前に父親を事故死で失ったが、母サラとともに楽しい日々を過ごしていた。そんな時、彼らが学生発明コンテストに出品した万能型ロボットに、ひょんなことから父親の魂が乗り移ってしまう……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("R.O.T.O.R.", "ロボポリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "西暦2000年も間近な近未来。多発する凶悪犯罪に対し、警察はロボットによる警察部隊を研究開発する。しかし、開発機器の故障というアクシデントにより勝手に動き出し、容赦なく処刑を繰り返すロボット。これを食い止めようとする科学者との間に闘いが展開されていく。本来市民の安全を保障するべき警官が、襲いかかってくるというアイデアがユニークなアクション作品。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・社長外遊記", "続・社長外遊記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "丸急デパートのハワイ支店設立のため現地に乗り込んでいる風間社長たち。社長は日本料理屋さくら亭のマダム・紀代子にぞっこんで仕事は人まかせ。部下たちも穴場巡りに忙しい。ライバル福助屋との勝負はいかに？", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FEMME FARDE´E", "厚化粧の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "F・サガンの小説を映画化した、豪華客船上でのブルジョワたちのラブ・ゲーム。プリマドンナ、指揮者、女優、サギ師など、一癖ありそうな人々が乗り込んだ“ナルシス号”。彼らは航海中、恋のパートナーを替え、旅の恋愛を楽しんでいたが……。J・モローをはじめ、豪華なキャストも見もの。", "1990-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("薔薇の婚礼・真夜中に交わした約束", "薔薇の婚礼・真夜中に交わした約束", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ヴィジュアル系バンドのカリスマ的存在マリスミゼルの４人が主演するということで話題になった作品。彼らの象徴ともいえる“薔薇”“ドラキュラ”をテーマに贈るヴァンパイア映画。永遠の花嫁を捜し求めるドラキュラ伯爵と、愛のために自らの肉体を悪に捧げた青年の孤独と苦悩を描く。もちろん音楽も彼らが手掛け、耽美かつ幻想的なムードがたっぷり。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まむしと青大将", "まむしと青大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ゴロ政は、ムショを出て名古屋に繰り込むが、ベビー・フェイスの健のインチキ麻雀にひっかかりスッテンテン。政は健を追って大阪へ行き勝と再会し、東邦連合会から３千万円を強奪する。個性派俳優を絡ませた見せ場が新味。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女囚さそり第41雑居房", "女囚さそり第41雑居房", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "演出は前作に引き続き伊藤俊也。法務省の巡閲官の面前で、郷田所長を襲ったナミは、憎悪に燃える郷田に徹底的にシゴかれる。しかし、ナミは女囚のボス・大塚らとすきをついて脱走を企てる。女囚たちの逃避行と追う看守たち。やがて女囚たちは観光バスを乗っ取って人質を取り、包囲網を突破しようとする。女囚たちに白い着物を着せて正座させ、歌舞伎のスタイルで一人一人の罪状を紹介するなど、前作の様式美をさらに推し進め、シリーズ中最も耽美な作品となった。また本作では、さそりに仮託された社会から見捨てられた女たちの“怨”が、より鮮明になっている。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("七人の弔（とむらい）", "七人の弔（とむらい）", "", "", "http://image.eiga.k-img.com/images/movie/1235/315x210.jpg?3125", 107, "脚本＆主演映画「生きない」がロカルノ国際映画祭特別賞を受賞したダンカンの監督デビュー作。夏休みのキャンプにやってきた7組の親子たち。食事会や山登りなどをこなす彼らだが、実はここは親たちが子供の臓器を売買するための取引場所だった。やがて子供たちもその真相に気づいていく。15歳の新人モデルの川原真琴と柳生みゆらが出演。「いま、会いにゆきます」の松谷卓が音楽を担当。モスクワ国際映画祭に出品されたのも話題。", "2005-08-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンの孫悟空（孫悟空・前後編）", "エノケンの孫悟空（孫悟空・前後編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "「ちゃっきり金太」「どんぐり頓兵衛」などに代表される山本嘉次郎＝エノケンのコンビが贈る戦前最後の作品。日劇ダンシング・チームのレビューが散りばめられたドタバタ喜劇。中でもエノケンとのトンでるかけ合いは笑える。日米開戦直前の作品にもかかわらずポパイのほうれん草のギャグもある。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レッツゴー！若大将", "レッツゴー！若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "京南大学サッカー部のキャプテン・田沼雄一は、全日本チームの一員に選ばれ香港に遠征。そこで香港娘に惚れられ、恋人・澄子はまたしても大むくれとなる。香港の人気女優・陳曼玲が共演。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROFESSIONAL AFFAIR", "裸の訪問者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "マフィアの大物から、消えた200万ドルの捜査を依頼された私立探偵のタイラーは、事件の裏にハリウッド・マダム、ヘザーの存在をつかむ。タイラーは彼女に接近するが、次第に彼女の美貌と肉体にほんろうされていく……。ハリウッド・マダムとは、ハリウッドでの成功を夢見る女の子を、ショービジネス界の大物に紹介するビジネスのこと。アメリカで、実際に起こった事件をベースに、ショービズ界のダークサイドを描き出したセクシャル・サスペンス。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほしをつぐもの", "ほしをつぐもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "北野武が「その男、凶暴につき」から一転して、子供に観せるべく企画した昭和へのレクイエム・ファンタジー。上司から繰り上げ定年を持ちかけられたオモチャ・メーカー勤務の中年男が、突如昏睡状態に陥った。生死をさまよいつつ、彼は戦時中の幼児体験の記憶を思い起こし始めていく……。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BELLS NOTRE DAME", "ノートルダムの鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "V・ユゴーの名作『ノートルダム・ド・パリ』をアニメ化したディズニー長編第34作。パリのノートルダム大聖堂の鐘楼にカジモドという男が住んでいた。カジモドは、子供の頃に両親を亡くし、フロロー判事に引き取られたのだが、その醜い容貌を嫌った判事が、鐘楼に閉じ込めてしまったのだ。祭りの日、カジモドは生まれて初めて鐘楼を抜け出し、町に出てしまう。そこで彼は、情熱的に踊るエスメラルダという女に魅了される。その時、道化の王コンテストが始まり、カジモドはエスメラルダに引っ張られて、舞台へ。幸か不幸か、カジモドは優勝し観客たちも称賛するが、彼の醜い顔は仮面を被っているのではなく素顔だと知って大喜び。あざけり笑い出す。逃げるようにその場を離れるカジモドを、エスメラルダだけはかばってくれるのだった。しかし、そのことを怒ったフロロー判事がエスメラルダを逮捕しようと……。カジモドの声をT・ハルスが、エスメラルダの声をD・ムーアが担当している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPERA DO MALANDRO", "三文オペラ リオ1941", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ブレヒトの『三文オペラ』をもとに、ブラジル音楽界を代表するC・ブアルキがミュージカル化。リオの下町に住む“マランドロ（＝やくざ者）”と顔役ルーの娘との色恋沙汰を中心に、バイタリティーあふれる人々の生き方が描かれる。南米のエキゾチックなムードも満点。", "1986-01-01", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伴淳・森繁の糞尿譚", "伴淳・森繁の糞尿譚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "第６回芥川賞を受賞した火野葦平原作を伴淳三郎と森繁久彌の名コンビの主演で映画化。監督・脚本はのちに「砂の器」を生み出す野村芳太郎と橋本忍の名コンビ。社会の醜い欲望の渦中に巻き込まれていく馬鹿正直な男の喜怒哀楽が描かれる。社会風刺を込めた痛快喜劇で、題名通り全編糞尿をめぐる騒動が展開する。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五番町夕霧楼", "五番町夕霧楼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "水上勉の同名小説の２度目の映画化。京都・西陣の夕霧楼で芸妓になった夕子と、彼女の幼なじみの修行僧・正順との許されない愛を、正順の金閣寺放火をクライマックスに描く。松坂慶子のセクシーさが全編を覆い、詩情豊かな前作（1963）とは味わいが違う。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐", "嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "島崎藤村の小説を稲垣浩が人情劇風にアレンジした力作。４人の子供たちを残して妻に先立たれた大学教授が、男手一つで子供を育てていく20年の記録。４人の子供たちを演じる山本康、大塚国夫、久保明、雪村いづみが好演している。稲垣浩は、笠智衆扮する父親像に自身の父親のイメージを重ねているようだ。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COSI　RIDEVANO", "いつか来た道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "1958年、シチリア島から大都市トリノへ出てきた文盲の男ジョヴァンニは、学校へ通う弟ピエトロのためにつらいゴミ集めの仕事も厭わない。だがピエトロは学業をおろそかにし、すぐにも働きたいと言う。労働の厳しさを説くジョヴァンニの前からピエトロは姿を消す。二年後、教師になったピエトロは兄と再会するが、過酷な試練が二人を待ち受けていた……。高度成長期のイタリアを背景に、田舎から都会へ希望を持って上京した兄弟の運命と絆を、アイロニカルな視点を交えて綴るドラマ。６年間のある一日を一つの章として、“到着”“裏切り”“金”“手紙”“血”“家族”の６章仕立てで構成している。", "1998-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("精霊流し", "精霊流し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "さだまさしが自身の名曲『精霊流し』をモチーフに書き綴った自伝的小説を映画化。生みの母と育ての母、二人の母が心にしまっていた秘密を知って心を揺らす青年の物語だ。主人公の周囲で起きるエピソード、それを通した青年の心の動きを丁寧に描き、切ない感動作に仕上げた。クライマックスの盛大な精霊流しのシーンは涙なしには見られない。母の願いに応えるべく、バイオリン修行のために幼くして一人上京した雅彦。彼は、鎌倉にいる叔母の節子の家で暮らし始める。時は過ぎ、大学生になった雅彦は、バイオリンよりアルバイトに明け暮れる毎日を送るようになっていた……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRIZE", "逆転", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "ノーベル賞授賞式をテーマにしたアーヴィング・ウォーレスのベストセラー小説の映画化。文学賞受賞者クレイブは、物理学賞を受賞したストラトマン博士の言動に不審を抱き調査を始めるが、そこには東側による巨大な陰謀が隠されていた……。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TEXAS CHAINSAW MASSACRE", "悪魔のいけにえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "T・フーパーの衝撃的デビュー作。グロテスクかつ深遠な恐怖描写の連続で大ヒットし、以後のホラー映画に多大な影響を与えた。モデルは「サイコ」と同じ、ウィスコンシンに実在した殺人鬼エド・ゲイン。ドライブ旅行を楽しむ５人の若者が、テキサスの小さな町でガス欠になり、１軒の家を訪ねたことから惨劇が始まる。ドアを開けるなり、人間の皮の面をかぶり、血に汚れた屠殺用エプロンをつけた大男に襲われ、若者たちは次々に殺される。唯一生き残ったサリーは必死で逃げるが……。低予算のため、特殊メイクでリアルに虐殺を見せるわけではない。が、大男の迫力と、たたみかける演出で息をつかせない。恐怖にカッと見開いた女の瞳孔をアップで追うシーンなど、それだけでショック。また、朝日を浴び大男がチェーンソーを振り回すラストも目に焼きついて離れない。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wen Die Gotter Lieben", "モーツァルトの恋", "", "", "http://image.eiga.k-img.com/images/movie/56145/315x210.jpg?4086", 111, "モーツァルトの愛や青春、苦悩を描く伝記映画。1951年にはカール・ハートル監督自身による英語版リメイク作が日本公開されたが、48年製作のオリジナル版は2011年日本初公開となる。宮廷付き指揮者になることを夢見ていた若きモーツァルトは、ウェーバー家の娘コンスタンツェと結婚する。しかし、モーツァルトが本当に愛しているのはコンスタンツェの姉で歌手のルイーゼだった。「フィガロの結婚」「ドン・ジョヴァンニ」など傑作を次々と発表するモーツァルトの成功の陰には、ヒロイン役を歌うルイーゼの存在があった。", "2011-05-14", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE F.B.I. STORY", "連邦警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "1924年、まだ弱体のFBIに入局した青年が、アメリカ犯罪史上に名高い数々の事件に挑戦しながらFBIとともに成熟していく姿を、彼の回想形式で綴る。妻や子への愛情、同僚への友情も織り込んで、人間ドラマとしても深みのある作品になっている。原作はピュリッツァー賞を受賞したドン・ホワイトヘッドのノンフィクション小説。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Final Destination 5", "ファイナル・デッドブリッジ", "", "", "http://image.eiga.k-img.com/images/movie/56675/315x210.jpg?1827", 92, "逃れられない死の運命に翻弄される若者たちの姿をショッキングシーン満載に描く人気ホラーサスペンス「ファイナル・デスティネーション」シリーズ第5作。橋の崩落事故を予見したサムは危険を知らせ、間一髪で8人が事故から生き残った。しかし事故犠牲者の葬儀に現れた謎の男から「死は決してだまされない」と告げられ、8人は次々と予測不能なトラップに襲われる。死の恐怖に怯えるサムらの前に再び現れた男は、「他人に死を贈る」ことで死の運命から逃れられると言うが……。", "2011-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブラック・ジャック", "ブラック・ジャック", "", "", "http://ecx.images-amazon.com/images/I/51-Ly9fjARL._SL210_.jpg", 94, "数々の作品を世に送り出した天才漫画家・手塚治虫の作品のなかでも、特に人気の高い同名コミックを映画化した劇場用アニメ。無免許で巨額な治療費を請求する天才外科医ブラック・ジャックは、以前治療したことのある少女の新たな病気の診察のためロンドンへ飛ぶ。しかし少女は死亡し、その遺体を解剖した彼は愕然とする。14歳の少女の臓器は、まるで90歳の老人のもののように使い古されていたのだ……。", "1996-11-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La piel que habito", "私が、生きる肌", "", "", "http://image.eiga.k-img.com/images/movie/57715/315x210.jpg?3682", 120, "ペドロ・アルモドバル監督とアントニオ・バンデラスが「アタメ」（1989）以来22年ぶりにタッグを組み、最愛の妻を亡くし禁断の実験に没頭する形成外科医と、数奇な運命をたどるヒロインの姿を描く問題作。画期的な人工皮膚の開発に執念を燃やす形成外科医ロベルは、かつて非業の死を遂げた最愛の妻を救えるはずだった「完璧な肌」を創造することを夢見ていた。良心の呵責や倫理観も失ったロベルは、ひとりの女性を監禁して実験台にし、人工皮膚を移植して妻そっくりの美女を作り上げていく。", "2012-05-26", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はい！もしもし、大塚薬局ですが", "はい！もしもし、大塚薬局ですが", "", "", "http://image.eiga.k-img.com/images/movie/56045/315x210.jpg?1957", 48, "恋もお洒落もとっくの昔に忘れてしまった薬局の女店長・真名美が、片思いに悩む女子中学生・恵美と知り合う。恵美の姿に、同級生に夢中になっていた25年前の自分の姿を重ねた真名美は、恋することの嬉しさや切なさを思い出し、恵美の思いが成就することを願うようになる。2008年東京ネットムービーフェスティバルで受賞した勝又悠監督が、同映画祭の支援を受けて製作した青春ドラマ。恵美役で人気アイドルグループ「AKB48」の小林香菜が映画初主演。", "2011-04-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HULLABALOO OVER GEORGIE AND BONNIE'S PICTURES", "マハラジャ／優雅なる苦悩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "貴重な細密画のコレクションを所有するインドの若きマハラジャ（大君)、ジョージのもとに、アメリカ人のヘブンと友人リンを伴ったインド系イギリス人レディ・ジーが到着した。細密画を芸術として愛し、自分の人生の一部として考えるジョージに対し、妹のボニーは、金銭的な価値のみを追求していた。細密画を手に入れるため、ヘブンはボニーに、ジーはリンを介してジョージに近づこうとする。地元の画商シュリを交えて、争奪戦はクリスマス・イヴに佳境を迎える。1961年に、インドの細密画についてのドキュメンタリー「The Sword and the Flute」を製作した経験をもつ、J・アイヴォリーの監督作品。", "1978-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monsieur Hire", "仕立て屋の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "殺人事件の容疑者として浮かび上がった、前科を持つ仕立て屋の男イール。しかし彼は犯人ではなく目撃者だった。孤独な生活を送るイールは、向かのアパートに住むアリスを覗き見ているうちに彼女に恋をし、そしてある日、犯行の現場を見てしまったのだった。監督は「髪結いの亭主 」のパトリス・ルコント、音楽は「ピアノ・レッスン」のマイケル・ナイマン。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Family Man", "天使のくれた時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "「レッド・ドラゴン」のB・ラトナー監督が、N・ケイジを主演に据えて描くファンタスティックなラブ・ストーリー。ウォール街で成功を収めた大手金融会社社長のジャックは、リッチで自由な独身生活を満喫している。そんな彼がクリスマスの朝、見知らぬ家で目覚める。隣に寝ているのは13年前に別れた恋人ケイトだった……。もう一つの人生を経験するチャンスを与えられた男の姿を通して、愛の尊さをロマンティックに描く。無縁だと思っていた家族の温かさに触れ、人間らしさを取り戻していく主人公をN・ケイジが好演。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エビルロード Evil Rood 悪魔の十字架", "エビルロード Evil Rood 悪魔の十字架", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "中世の魔女の血を引く「魔女系女優」として活動する深月ユリアが製作・主演したスプラッター・ホラー。義母から受けた虐待が原因で、夢と現実を混同するようになってしまったマリアは、夢の中で魂を乗っ取られた神父と修道院院長が修道女たちを殺害する残忍な儀式を目撃してしまい……。", "2010-12-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILENT TRIGGER", "スナイパー 狙撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "シューターは黒幕のスーパーバイザーから依頼されて世界各地で殺しを行う暗殺のプロ。彼は獲物が何者で、なぜ殺さねばならないか知らないし、興味もない。わかっているのは失敗すれば自分が殺されることだけ。今度の仕事はビルに侵入して、夜明けのハイウェイを走る車を撃破すること。だが、彼を目掛けて空から謎のSWAT特殊部隊が舞い降りて来て……。D・ラングレン主演のB級活劇。ビデオ発売時タイトルは「SNIPER／スナイパー」。", "1996-01-01", "3,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カフェ・ソウル", "カフェ・ソウル", "", "", "http://image.eiga.k-img.com/images/movie/54635/315x210.jpg?4107", 94, "人気韓流ドラマ「宮／Love in Palace」でブレイクしたJohn-Hoonと、「春琴抄（2008）」など映画を中心に活躍する斎藤工がW主演する、ソウルを舞台にした日韓合作の青春ドラマ。監督は「ボーイ・ミーツ・プサン」の武正晴。ソウルの片隅にひっそりと佇む伝統菓子店「牡丹堂」に偶然やってきたフード・ルポライターの井坂は、寡黙な店主サンウが一人で切り盛りするその店の味に魅了されるが、店は地元のヤクザから立ち退きを迫られていた。そこにミュージシャンを夢見て家を出たサンウの弟・サンヒョクが現れるが……。", "2009-07-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wizard of Oz", "オズの魔法使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "大竜巻に巻き上げられて、魔法の国へと迷い込んでしまったドロシー。故郷のカンサスへ帰るためには、エメラルド・シティーに住むオズの魔法使いに会いに行かなければならない。従者は、臆病ライオンに脳のないカカシ、そして心のないブリキ人形。知恵と勇気と優しさで、困難を切りぬけていく一行の不思議な冒険……。夢と楽しさにあふれたミュージカル・ファンタジーの傑作で、当時16歳のジュディを一躍スターに押し上げ、その名を不滅にした。アカデミー賞の主題歌賞を得た『オーバー・ザ・レインボー』は、今でもスタンダード・ナンバーとして親しまれている。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLERKS", "クラークス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "総予算２万7000ドルという超ローバジェットで撮られたインディペンデント映画。舞台はニュージャージー州アズベリー・パークのとあるコンビニエンス・ストア。ここでバイトをしている青年ダンテのもとに、次々と客がやって来ては去って行く。来るのは退屈しのぎの客や悪友たちばかり……。12時間の間に起こる出来事をアンチクライマックス的に描いた元気のいい野心作。製作・監督・脚本・撮影・編集を兼ねるK・スミス自身のコンビニでのバイト経験がもとになっている。1994年のサンダンス・フィルム・フェスティバル、ドラマ部門においてフィルムメーカー賞を受賞。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猫のように", "猫のように", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "幼い頃のシスターコンプレックスを引きずったまま、いまだに大人になりきれない女子大生の妹と、結婚を機にこれまでの生活を一新しようとする姉。そんな二人の異常ともいえる姉妹関係を綴った青春映画。監督と脚本は、TV版『桃尻娘』『ボクの女に手を出すな』のコンビ、中原俊と斎藤博。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天上草原／HEAVENLY　GRASSLAND", "天上草原", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "舞台は、モンゴル高原地帯の内モンゴル自治区。心に傷を持った漢族の少年が、大自然とそこに住む素朴で大らかな人々の心に触れ、障害を乗り越えていく。本作はモンゴル族によって作られた数少ないモンゴル映画で、その生活や民族性、自然の情景が、忠実に愛情深く映し出されている。母に捨てられて失語症になったフーツは、父の友人のモンゴル族と共に草原生活を始めた。最初は困惑するが次第に心癒されていく。", "2005-03-12", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("永山裕子　果物と紫陽花を描く", "永山裕子　果物と紫陽花を描く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "みずみずしい独特の色彩の作品で、多くの水彩画ファンの支持を得る画家・永山裕子の創作の過程に密着したドキュメンタリーシリーズ第2作。アジサイやリンゴ、ブドウといった果実から、ランプやキャンドルなど様々なモチーフを描く制作過程を収録。「形の見方・捉え方のコツ」をテーマに、デッサンについての詳しい解説なども行われる。", "2010-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MACHINE", "ザ・マシーン 私のなかの殺人者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "脳外科医のマルクは連続殺人犯の異常心理の探究に情熱を傾けている。殺人者の心理を実体験するため彼が開発したのは、人間の脳を分析し、他人の脳と入れ替えることのできるマシーン。その人体実験第一号に、４人の女性を惨殺した殺人鬼ジトが選ばれる。脳の転送実験は成功、マルクの心はジトの身体の中に、ジトの心はマルクの身体の中に入り込む。実験を終え、元に戻ろうとした時、ジトの心が拒否。マルクの体を支配したジトの脳は、新たな殺りくを始めるのだった。マルク役のG・ドパルデューが戦慄のサイコキラーぶりを披露している。", "1994-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君は裸足の神を見たか", "君は裸足の神を見たか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "日本映画学校の卒業生を含めた学生の脚本・演出による作品で、親友同士の間に割って入ってきた美少女をめぐり、死に至るまでの精神的苦悩を味わう内気な高校生の姿を描く。四方を山に囲まれた秋田の角館で全編をロケーション。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Night of the Living Dead", "ナイト・オブ・ザ・リビング・デッド／ゾンビの誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　墓参りにやってきた兄妹ジョージとバーバラ。そんなふたりに、甦った死体が襲いかかる。ジョージは格闘の末に死亡し、近くの家に逃げ込んだバーバラは他の避難者たちと合流する。しかし一行は死者の襲撃を前にしながら対立。ゾンビの群れは容赦なく押し寄せてくるが……。緩慢な動きながら、生者を襲うことで限りなく増殖し続け、脳を破壊しない限り滅びないゾンビ、そしてその襲撃に立ち向かう人々を描いたホラーシリーズ第1作。製作時の社会・世相を反映した物語はそのたびに恐怖と衝撃を呼んでいる。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Death Race 2000", "デス・レース2000年", "", "", "http://image.eiga.k-img.com/images/movie/46975/315x210.jpg?4328", 80, "ロジャー・コーマン製作によるSFアクション。西暦2000年、独裁国家となったアメリカでは大陸横断レース「デス・レース」に国民が熱狂していた。そのレースは、走行中に人間を殺していくことでポイントが加算される文字通りの死のレースで、参加するレーサーたちは壮絶な死闘を繰り広げる。主人公の覆面レーサー、フランケンシュタインにデビッド・キャラダイン。「ロッキー」（1976）以前のまだ無名だったシルベスター・スタローンもレーサーの1人で出演している。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("熱風", "熱風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "戦争の勝利の鍵といわれた鉄の生産増強をテーマに、銃後の産業戦士たちの決死の努力を描いた戦意昂揚映画。“赤いセシル・B・デミル”こと山本薩夫監督までもが、このような作品を作らされていたことに驚く。皮肉なことに、彼はこの時代のPR色の強い作品群によって、その名を大いに高めていった。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Resident Evil: Afterlife", "バイオハザードIV アフターライフ", "", "", "http://image.eiga.k-img.com/images/movie/55205/315x210.jpg?1691", 97, "ミラ・ジョボビッチ主演の大ヒットアクションシリーズ「バイオハザード」の第4弾。シリーズ初の3Dでの映画化で、第1作で監督を務めたポール・W・S・アンダーソンが再びメガホンをとる。ゾンビウイルスの拡散により荒廃した世界を舞台に、アリスは世界中を旅しながら、数少ない生存者を探していた。やがて、ロサンゼルスにウイルスに侵されていない安全な場所が存在するという情報を得て向かうが、そこはアンデッドたちによって支配されていた……。", "2010-09-10", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SILENCE OF THE HAMS", "羊たちの沈没", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "「羊たちの沈黙」と「サイコ」をベースにしたパロディー映画。若きFBI捜査官ジョー・ディー・フォスターは異常殺人犯を追うことになった。一方、ジョーの婚約者ジェーンは、上司とその秘書から金を盗んで結婚資金を増やすことを決意し、街を出て“モーテル墓地”へと向かった。そうとは知らず、ジェーンの姉は妹が行方不明になったとジョーに知らせ……。「サイコ」「氷の微笑」「羊たちの沈黙」「スリラー」等のサスペンス、ホラーの名場面がパロディとなって続々と登場する。ジョー・ダンテ、ジョン・カーペンター、メル・ブルックスらが一瞬顔を見せるのも笑わせる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("МУЖИКИ！", "新しい家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "元海軍のパーベルはかつての恋人の死を知らされる。彼女は実はパーベルとの間の子供を生んで育てていた。自分の娘ポリーナと初めて会ったパーベルは、その子に昔の恋人の面影を見て、引き取ろうと決意するが……。女流監督I・バーヴィッチが、優しくかつ骨太なタッチで家族のあり方を問う感動のドラマ。", "1981-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冒険者カミカゼ", "冒険者カミカゼ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "大学をクビになった元オリンピック選手の大学職員と、不正入学の学生が、大学の現金輸送車を襲い大金の入手に成功。かぎつけたヤクザ一家と一進一退の攻防戦を繰り広げるが……。陸・海・空とテンポよく場面を展開させたアクションたっぷりの青春冒険映画。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fantastic Mr. Fox", "ファンタスティック Mr. Fox", "", "", "http://image.eiga.k-img.com/images/movie/53175/315x210.jpg?1464", 88, "ロアルド・ダールの「すばらしき父さん狐」を、「ザ・ロイヤル・テネンバウムズ」「ダージリン急行」のウェス・アンダーソン監督が、ストップモーション・アニメで映画化。意地悪で愚かな金持ちの農場主3人の裏をかいて、農場のニワトリやアヒルを失敬する頭のいい父さん狐と、その家族を描く。", "2011-03-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COSI´ COME SEI", "今のままでいて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "N・キンスキーをトップスターに押し上げたメロドラマ。妻子ある中年紳士と16歳の女子学生が、ふとしたきっかけで深く愛し合うようになる。しかし男は友人の言葉から、少女が実の娘ではないかという疑惑を抱く……。宿命的な愛を描く、詩情あふれる一編。", "1978-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若草物語", "若草物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "若い後妻を持った父を気遣って、東京へ嫁いで行った長女を頼りに、そろって家出した次女、三女、四女の三姉妹。長女は仕方なく３人のためにアパートを借りてやるが、それぞれにいろいろな事件が持ち上がって……。豪華スター勢ぞろいの正月興行作となった女性青春ドラマ。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANTONIETA", "アントニエッタ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1931年、パリのノートルダム寺院で情熱的な女、アントニエッタはピストル自殺した。現代の作家アンナは、メキシコへ飛び、メキシコ革命の時代に生きたアントニエッタの人生の軌跡を追う。エレガントな雰囲気の文芸映画。", "1982-01-01", "2,19,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アワモリ君乾杯！", "アワモリ君乾杯！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "秋好馨の人気漫画の映画化。予備校に通っている、アワモリ九とカバ山大学は大の親友。二人はある日、小犬を拾うが、とても家では飼えない。その犬を抱いていった娘・加代子に一目惚れしたアワモリ。早慶大学生と偽って彼女と交際を始めるが……。坂本九のユーモラスなキャラクターが前面に出されている。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京日和", "東京日和", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "写真家の荒木経惟と陽子夫人による同名フォトエッセイに感銘を受けた竹中直人監督が、中山美穂を相手役に据えて撮り上げた、ある夫婦のラブ・ストーリー。勤め先にウソをついて失踪したり、ちょっと情緒不安定なところのある妻ヨーコを思いやるカメラマンの島津。そんな二人の純愛の軌跡を、ドラマティックなストーリー展開を避けつつ、ささやかな日常シーンの積み重ねで心優しく綴っている。また、後半二人が夫婦仲を取り戻そうと旅立つ柳川市のシーン以外は、月島界隈の何気ない路地から有楽町のガード下、世田谷線、丸の内のオフィス街など、東京各所の風景が美しい映像でスケッチされている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("55 DAYS AT PEKING", "北京の55日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "1900年、北京における民族的な集団、義和団の乱をテーマに、居留民の保護にあたるアメリカ軍の少佐、ロシアの貴婦人、イギリス公使、日本軍守備隊長、西太后らが織り成すドラマ。スペインに建てた北京のセットが見もの。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春光乍洩／HAPPY TOGETHER", "ブエノスアイレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「天使の涙」に続いて発表したW・カーウァイの作品。惹かれ合いながらも傷つけ合う男同士の愛を、アルゼンチンを舞台に描く。恋人同士のウィンとファイが、南米アルゼンチンへとやって来る。何度となく別れを繰り返してきた二人は、ここでもささいなことで口論となった。やがて彼らはウィンが働くタンゴ・バーで再会。傷を負ったファイを癒すウィンは、彼のパスポートを隠し、再びケンカとなる。中華料理店で働き始めたウィンは、チャンという青年と親しくなるが、彼は兵役前に“世界を見るため”に南米南端の岬へ旅立つ。ファイから何度も電話があったが、彼とやり直す気がないウィンは一人、イグアスの滝を目指すのだった……。アストル・ピアソラの切なくも情熱的なタンゴのメロディーが、映画を効果的に彩る。加えてカエターノ・ヴェローゾ、フランク・ザッパなど相変わらず選曲にはセンスを発揮しているが、緩急を欠いた映像表現は、やや単調な趣も。", "1997-01-01", "6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銭形平次", "銭形平次", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "長谷川一夫が演じて以来、銭形平次はずっと彼のハマリ役だったが、TVシリーズで大川橋蔵の平次が登場してからは人気を二分するようになった。目明かし銭形平次の誕生からその初手柄までを、ダイナミックな映像で描く。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八州遊侠伝 男の盃", "八州遊侠伝 男の盃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "国定忠治処刑の噂が流れて１年。源次と名のる旅人が祭りに沸く上州磯部温泉に舞い込み、旅籠みのやにワラジを脱いだ。そこの美しい女中・お志乃は土地のヤクザ松五郎一家に狙われていたが、危ういところを源次に助けられる。そんな折、忠治が実は生きているらしいとの噂が流れてきた……。マキノ雅広監督の“国定忠治もの”の一編。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Strangers", "ストレンジャーズ　戦慄の訪問者", "", "", "http://image.eiga.k-img.com/images/movie/53765/315x210.jpg?2618", 85, "新鋭ブライアン・ベルチノが監督・脚本を務め、「アルマゲドン」「ハルク」のリブ・タイラーが謎の訪問者からの襲撃に追いつめられる主人公を演じたスリラー。友人の結婚式から帰宅したクリスティンとジェームズは、明け方の4時に突然ドアをノックする音を聞く。不審に思いながらもドアを開けると、「タマラはいますか？」と尋ねる少女が1人立っていた……。", "2009-04-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続鋼鉄の巨人", "続鋼鉄の巨人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "X14ウラニウムでボース爆弾を完成させたアトムAB団は、本拠地メラネシア大使館に政府高官を招き、その席上でボース爆弾の完成を発表し、日本に無条件降伏させようとする。しかしスーパー・ジャイアンツによってアトムAB団は壊滅してしまう……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Interpreter", "ザ・インタープリター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "アフリカのマトボ共和国出身のシルビアは、現地のクー語の通訳として国連本部で働いている。ある日、彼女はマトボの大統領暗殺計画に関する会話を偶然聞いてしまう。それ以来、危険がつきまとう彼女の身を守るため、シークレットサービスが送り込まれる。そのうちの一人、ケラーはシルビアの言動に疑惑を持ちはじめ……。暴力の連鎖の悲劇を描いたポリティカル・サスペンス。", "2005-05-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Soloist", "路上のソリスト", "", "", "http://image.eiga.k-img.com/images/movie/54095/315x210.jpg?3326", 117, "「プライドと偏見」「つぐない」のジョー・ライト監督が、米ロサンゼルスを舞台に2人の男の友情を描いた人間ドラマ。ロサンゼルス・タイムズの記者スティーブ・ロペスは、ある日路上で2弦しかないバイオリンを弾くホームレスを見つける。その男の名はナサニエルといい、かつては将来を嘱望された天才チェロ奏者だった……。主演はジェイミー・フォックスとロバート・ダウニー・Jr.。脚本は「エリン・ブロコビッチ」「イン・ハー・シューズ」のスザンナ・グラント。", "2009-05-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新英雄本色／RETURN TO A BETTER TOMORROW", "男たちの挽歌４", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "チンピラのタイハは、暗黒街の若きボス、チョンと出会い友情で結ばれる。手を組んだ二人は次第に頭角を現し、黒社会の頂点へとのし上がっていく。そんな二人を邪魔に思う兄貴分のウェイは殺し屋を放ち、暗殺を企てる。しかし、魔の手は彼らだけでなく、タイハの娘やチョンの恋人にまでおよび……。1994年度の香港音楽界の賞を総なめにしたE・チェンが主演を務めている。“男たちの挽歌”シリーズとは無関係。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Night People", "Night People", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍少爺／DRAGON　LORD", "ドラゴンロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ジャッキー・チェンが監督・脚本・主演の３役をこなしたアクション映画。名家の御曹司で、武道とスポーツと遊びの天才ルンが、ひょんなことから国宝密輸組織の存在を知り、彼らと戦いを繰り広げていく。シャトルを蹴り合うゲームなど、アスレチックな動きを取り入れた多彩なアクションを楽しむことができる。", "1982-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地下情／LOVE　UNTO　WASTE", "チョウ・ユンファの地下情～追いつめられた殺意～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "歌も踊りもお手のもののチョウ・ユンファが熱血刑事に扮した意欲作。有名になるために台湾から香港にやって来た３人の女性は、夢破れて失意の日々を送っていた。その中の一人が殺害され、捜査にあたった刑事は彼女の意外な一面を発見する……。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA NOIA／THE EMPTY CANVAS", "禁じられた抱擁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "モラヴィアの官能的な長編小説『倦怠』の映画化。いやしがたい倦怠感にとらわれた画家のディーノは、17歳のモデル、チェチリアと知り合う。奔放なチェチリアとの肉体関係を続けながら、彼女の捉えどころのなさに焦燥し、激しい嫉妬に駆られるディーノは、やがて……。", "1963-01-01", "4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浪人市場・朝やけ天狗", "浪人市場・朝やけ天狗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "南町奉行の矢部は、悪人たちの陰謀によって失脚させられ、新たに彼らの仲間、鳥居がその職に就いた。矢部の腹心、山川忠介は職を辞し、浪人市場に身を置く。鳥居の悪政は市井の人々を苦しめ、忠介に心酔する長屋の住人はついに立ち上がり、忠介自身も江戸城西の丸再建に絡む鳥居らの悪事を暴く。勧善懲悪の図式を絵にしてみせた時代活劇。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kinatay", "キナタイ　マニラ・アンダーグラウンド", "", "", "http://image.eiga.k-img.com/images/movie/56805/315x210.jpg?2883", 110, "カンヌ、ベルリン、ベネチアの世界3大映画祭の受賞作を中心に、各地の映画祭で話題になりながらも日本未公開だった作品を一挙上映する「三大映画祭週間2011」にて上映。貧乏な警察学校の生徒のペッピングは、恋人と生まれてくる子どものために麻薬の売買などにも手を出していき、裏社会への道を転げ落ちていく。やがて自らの内に潜むどす黒い殺意に悩み……。2009年・第62回カンヌ国際映画祭監督賞受賞作。監督は、前作「Serbis」（08・日本未公開）がフィリピン映画として初めてカンヌのコンペティションに選出されたプリランテ・メンドーサ。", "2011-08-13", "2,35", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’A＾GE D’OR", "黄金時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "モド扮する男と、リスが演じる侯爵の娘との間にかよう“狂おしい愛”の描写。宗教、慈悲、純潔、男女間の愛欲、儀礼、社会通念といったものに対して、ブニュエル特有の痛烈な批判を投げかけた、トーキー初期のアヴァンギャルド映画の代表作。シュールレアリスム画家のダリとブニュエルが共同で脚本を書いた。", "1930-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("呪怨", "呪怨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "高橋洋、黒沢清らを唸らせたオリジナルビデオの劇場版。ビデオ版と同じ一軒家を舞台に、新たな呪いの惨劇が展開する。奥菜恵、伊東美咲ほか売れっ子女優たちの恐怖におののく演技も必見だ。かつて忌まわしい惨殺事件が起こった郊外の一軒家。この家に引っ越してきた若夫婦、老人介護のボランティアをしている女子大生、元刑事とその娘らの身に次々とおぞましい怪現象がふりかかる。この家で起きた９年前の出来事とは……？。ハリウッドでも同監督でリメイクされた。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOW TO SUCCEED IN BUSINESS WITHOUT REALLY TRYING", "努力しないで出世する方法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "人を出し抜いて要領よく出世しようとたくらむ青年フィンチは、社長の愛人を利用して社内でのし上がっていく。失敗もなんのその、要領とずうずうしさだけではい上がっていく男を描いた痛快なサクセス・コメディ。大ヒット舞台劇の映画化作品で、ミュージカル仕立ての構成も楽しく、ストーリーも飽きさせない。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THUMB　WARS", "親指スター・ウォーズ", "", "", "http://ecx.images-amazon.com/images/I/51MHQPED22L._SL210_.jpg", 54, "「スター・ウォーズ」の第１作（エピソード４）を“親指”キャストでパロディー化。宇宙戦争に巻き込まれた若者ローク・グランドランナーの、おかしな冒険が描かれる。CG合成で作られた親指たちの表情が愛らしくも、どこか不気味で妙味。宇宙戦のSFXシーンは元ネタに忠実で、意外にも迫力のある見せ場となっている。ビデオ、DVDタイトルは「親指ウォーズ」。", "2000-02-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRAPLESS", "ストラップレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "信じていた愛を見失ってしまう姉と、奔放な愛の生活の果てに妊娠してしまう妹。二人の女性が心の支えを見失い（＝ストラップレス)、精神的危機に陥りながらも、それぞれの自分を再確認していく女性映画。イギリスの劇作家でもあり「ウェザビー」で監督デビューを果たしたD・ヘアーの第４作。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Over the Hedge", "森のリトル・ギャング", "", "", "http://image.eiga.k-img.com/images/movie/1585/315x210.jpg?2525", 84, "「シュレック」「マダガスカル」のドリームワークス・アニメーションによる新作フルCGアニメ。人間たちにより、住処である森の大部分を奪われてしまった動物たちが人間の住宅地に食料をもとめにいくが、そこで、動物たちはポテトチップスなどのジャンクフードの味に目覚めてしまう……。声の出演にブルース・ウィリス（吹き替え版は役所広司）、スティーブ・カレル、アブリル・ラビーンら。原題: Over the Hedge", "2006-08-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE BEL INDIFFE´RENT", "冷淡な美男子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 29, "J・コクトーがエディット・ピアフのために書き下ろした一幕劇の映画化。ホテルの一室を舞台に、愛人エミールに対する女Cの嫉妬といさかい、そして別れを描く。エミールは終始無言で、Cの一人芝居の形となっている。ドキュメンタリー映画でデビューし注目されたJ・ドゥミの初の劇映画であり、スタッフにはその後のフランス映画を代表する映画人たちが名を連ねている。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Don't Look Up", "THE JOYUREI 女優霊", "", "", "http://image.eiga.k-img.com/images/movie/55915/315x210.jpg?4609", 86, "中田秀夫監督の「女優霊」をハリウッドリメイク。落ち目の映画監督マーカスは、突如襲ってくる幻覚に悩まされながらも、それをヒントに映画を製作していた。そんなマーカスに新作のオファーが舞い込み、ドラキュラ生誕の地・トランシルバニア高原にある古びた撮影スタジオに向かう。しかし撮影が始まると、映るはずのない女性の人影や機材の故障などのトラブルが続く。", "2011-01-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEREVER YOU ARE", "悲愴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "第二次世界大戦が始まろうとしているポーランドを舞台に、若き外交官夫妻の愛と悲劇を描いた作品。「太陽の年」でヴェネチア映画祭金獅子賞を受賞したポーランドの名匠K・ザヌーシが戦争によって引き裂かれた愛を綴る。", "1988-01-01", "3,42,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'EMPIRE DE LA PASSION", "愛の亡霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "復員兵の青年と関係を持つ26歳も歳の離れた人力車夫の人妻。男女の仲を断ち切れない女は、ついに青年を共謀してその夫を殺害し、井戸に投げ捨てる。しかしやがて夫の亡霊が姿を現わし、夜な夜な彼らを苦しめはじめる……。山村の四季を見事に描き出す素晴らしい美術と撮影で描き出す愛と生の寓話。「愛のコリーダ」でスキャンダラスな話題をふりまいた大島監督が、再び海外資本で大胆なエロスをテーマに挑んだ問題作。", "1978-01-01", "76,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天国逆子／THE DAY THE SUN TRNED COLD", "息子の告発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "凍てつく冬の朝、若い男が殺人事件の捜査を依頼するために警察署に入っていった。彼が依頼したのは、10年前に起こった実の父親の殺人事件、しかも容疑者は母だというのだ。彼の母親は不倫をしているとの噂が村に広まり、それを聞いた父親が暴力をふるっていた。その後父は病気で倒れ、二回の入院後あっけなく死んだ。彼は、母親が父の食事に白い粉をふりかけるのを目撃していたのだ。14歳の時に病死した父の死因に疑惑を抱き続けてきた青年が、10年後に実の母を告発したという中国で実際に起こった事件をイム監督が映画化。1994年東京国際映画祭京都大会でグランプリと監督賞を受賞した。", "1994-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どたんば", "どたんば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "もとは菊島隆三の書いたTVドラマで、これを橋本忍が映画用に脚色し直し、監督には名匠・内田吐夢があたっている。美濃平野の小さな炭鉱で起こった落盤事故をモチーフに、坑内に閉じ込められた５人の坑夫と、救助作業に専念する所長や韓国人の姿を、サスペンスあふれる演出で描く迫力たっぷりの作品。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京少女", "東京少女", "", "", "http://image.eiga.k-img.com/images/movie/53405/315x210.jpg?2349", 98, "100年の時を越えて繋がった携帯電話がもたらす切ない恋を綴ったファンタジック・ラブストーリー。SF作家を夢見る高校生の未歩は、地震をきっかけに携帯電話をなくしてしまう。取り戻すために自分の番号に掛けてみると、電話に出たのはなんと明治時代に生きる小説家志望の青年・時次郎だった。電話を通して様々なことを語り合う内に、2人の距離は縮まっていき……。「天然コケッコー」の夏帆がヒロインを爽やかに演じる。", "2008-02-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅姿鼠小僧", "旅姿鼠小僧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "自分を盗みの世界に誘ったくせに、どたん場で金を持ち逃げした憎い男・直治郎を追って、旅姿を装った義賊・鼠小僧次郎吉が義賊の剣を振るう娯楽時代劇。瓦の上で大立ち回りなど息をもつかせぬ演出が秀逸。鶴田浩二・伊藤久哉が見事なコンビネーションを披露。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天菩薩／BUDDHA’S LOCK", "天菩薩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「太陽に暴かれて」のイム・ホー監督による凄絶な実話の映画化。第二次大戦中、抗日戦支援のため中国の山岳地帯にやって来たアメリカ軍将校が、人買いの一団にさらわれ、10年もの歳月を奴隷としてこき使われる。文明人が“家畜”として扱われる運命の残酷さが重厚なタッチで描かれる。", "1986-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAIN　DANCE", "チェイン・ジャンクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "M・アイアンサイドが製作総指揮・脚本を兼ねたアクション作品。あらゆる犯罪に手を染めるJ・T・ブレイクがマウンテン・ヒル刑務所に収容される。そこは、所長や看守たちの汚職、暴行、受刑者との癒着が横行する悪の温床だった。エスカレートする受刑者への暴力に、ブレイクらが立ち上がるのだった。", "1991-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUNSHINE", "サンシャイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "20歳の若さで世を去ったジャクリーン・ヘルトンが、テープレコーダーに遺した日記をもとにして描いたラブ・ストーリー。自由を愛する若者たちが集まる広場で、サムとケイトは出会った。新入りのサムをケイトは温かく迎え入れ、いつしか二人は愛し合う。ケイトは別れた夫の子供を身ごもっていたが、サムはかまわずに赤ん坊と３人で生活を始める。やがてケイトを不治の病が襲う……。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東流れ者", "関東流れ者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "義理と人情で任侠道を押し通す立花組組長・立花は、縄張りを狙う侠友連盟の阿部によって殺される。立花組の滝村清次は噴怒のドスを握り、阿部を刺し殺す。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PER QUALCHE DOLLARO IN PIU｀", "夕陽のガンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "ニューメキシコを舞台に、おたずね者を殺しては賞金を稼ぐガンマン二人が、凶悪な強盗団のリーダーをめぐってハチ合わせする。レオーネ、イーストウッド・コンビの第２作でマカロニ・ウエスタンの代表作。エンニオ・モリコーネの主題歌もヒット。", "1965-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dom za vesanje", "ジプシーのとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "ユーゴスラビアのジプシー村で家族とともに暮らす少年ベルハン。彼が成長していく姿を通して、流浪の民ジプシーたちの生活を美しい映像でリアルに描いた傑作。出演者には本物のジプシーを多数起用し、劇中は彼らの言葉であるロマニ語が用いられている。メガホンを取るのは、前作「パパは、出張中！」でカンヌ国際映画祭グランプリを受賞したエミール・クストリッツァ監督で、本作では同映画祭の最優秀監督賞を受賞している。", "", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAILHOUSE ROCK", "監獄ロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "殺人罪で監獄へ入ったエルヴィスが、ギターを覚え、ロック・スターとして人気者になるというストーリーで、タイトル・ソングをはじめ、プレスリーの懐かしい歌が聴ける。彼主演のアイドル映画では、最も出来のいい作品の一つといえるだろう。主題歌『監獄ロック』も大ヒット。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dr.Strangelove: How I Learned to Stop Worrying and Love the Bomb", "博士の異常な愛情／または私は如何にして心配するのを止めて水爆を愛するようになったか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "東西冷戦の最中に撮影され、戦争が世界の破滅を導くことをシニカルに描いたブラックコメディ。米国空軍のリッパー将軍の指示でソ連に向けて核攻撃が開始される。英国空軍のマンドレーク大佐が爆撃機の撤退を説得する一方、ソ連は核爆発によって誘発される地球破壊装置の存在を明らかにする。「ロリータ」「ピンクパンサー」シリーズのピーター・セラーズが英軍大佐、マフリー米大統領、表題でもあるストレンジラブ博士の3役を見事に演じている。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雲南故事", "雲南物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1945年、敗戦によって、肉親と離れ離れになり、一人中国に取り残された加藤樹子。彼女は、雲南省の少数民族、哈尼(ハニ)族の軍人夏沙(カサ)と結婚する。二人は哈尼族の村へ帰るが、夏沙は間もなく病死してしまう。再び一人になった樹子は村の風習に従い夏沙の弟、夏洛(カラク)と結婚し、ようやく平穏な日々を送るようになる。しかしそんな樹子のもとにも文革の嵐が忍び寄っていた。辺境の地、雲南省で生きる日本人女性の実話をもとに、彼女の数奇な人生と夫婦の愛を淡々と描き出す。", "1994-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サインはV", "サインはV", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "人気漫画を実写TVドラマ化し、アニメの『アタックNo.１』とともにバレーボールの一大ブームを巻き起こした同名のスポ根ドラマの映画版。骨肉腫に倒れるジュン役の范文雀をはじめ、TVと同じ出演者が青春をバレーに懸ける女性たちを熱演。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血斗", "血斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "舛田利雄監督、小林旭、高橋英樹共演による任侠アクション「対決」の姉妹編的作品。山辰一家が傍若無人にふるまう町に流れてきた二人の男が、協力して一家を叩きつぶす。小林旭が前作に引き続きコミカルな一匹狼・満州常を演じて新境地を見せる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRUTH　OR　CONSEQUENCES，N.M.", "気まぐれな狂気", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "個性派俳優K・サザーランドの初監督作。破滅へと向かう若き犯罪者たちの逃避行を、二転三転するスリリングなストーリー展開で描く犯罪ドラマだ。出所したばかりのレイモンドは、刑務所仲間のカーティスと相棒マーカス、そして恋人アディとともに強盗を計画する。レイモンドはその資金でアディと幸せな家庭を築き、犯罪から足を洗うつもりだった。ところが、キレやすいカーティスが殺人を犯してしまったことで予定が狂い始める。偶然出会ったカップルを人質に逃亡する彼らを、さらなる悪夢が待ち受けていた……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Charley Varrick", "突破口！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "　悪党チャーリーと数名の仲間がニューメキシコにある銀行を襲撃。銃撃戦の末、大金を奪うが、やがてチャーリーはその金がマフィアの隠し金だったことに気づく。金の奪還を目論むマフィアのボスは凄腕の殺し屋モリーを雇い、追跡を開始。モリーは高飛びの準備を進めていたチャーリーに迫るが……。ワル同士の対決をスリリングに描いた犯罪サスペンス。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸福", "ハピネス", "", "", "http://image.eiga.k-img.com/images/movie/54115/315x210.jpg?3349", 124, "「八月のクリスマス」「四月の雪」のホ・ジノ監督が、「サイボーグでも大丈夫」のイム・スジョンと「甘い人生」のファン・ジョンミョンを主演に描くラブストーリー。肝硬変を患った男ヨンスは、派手な都会生活を捨てて田舎の療養所に入所する。そこで重病に冒される女性ウニと出会い、陽気なウニに励まされ元気を取り戻すが……。2008年9月の「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」で上映後、単独で劇場公開。", "2007-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOME 愛しの座敷わらし", "HOME 愛しの座敷わらし", "", "", "http://image.eiga.k-img.com/images/movie/56385/315x210.jpg?4166", 109, "直木賞候補にもなった荻原浩の人気小説「愛しの座敷わらし」を、水谷豊の28年ぶり単独主演で映画化。東京から岩手の古民家に引越してきた一家が、不思議な座敷わらしと出会うことで、それぞれが自身を見つめなおし、家族のきずなを再生していく姿を描く。家でも会社でも居場所がない晃一を中心に、それぞれが悩みや不満を抱えている高橋家の5人は、晃一の転勤で岩手県の築100年以上の古民家に引越す。ようやく田舎暮らしにも慣れてきたある日、着物を着た不思議な子どもに出会い……。水谷主演で大ヒットした「相棒」シリーズの和泉聖治監督がメガホンをとる。", "2012-04-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EYES OF A STRANGER", "他人の眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "突然平和が破られた街の人間模様を描く社会派サスペンス。ニューヨーク郊外の静かな湾沿いの街で、若い女性ばかりを狙う連続殺人事件が発生。テレビのニュースキャスター、ジューンははかどらない捜査にいらだち、自分の番組を通じて情報提供を呼びかけるのだが……。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Exorcism of Emily Rose", "エミリー・ローズ", "", "", "http://image.eiga.k-img.com/images/movie/1055/315x210.jpg?4480", 120, "悪魔にとり憑かれた女子大生エミリー・ローズにムーア神父が悪魔祓いを施すが、エミリーは死亡。ムーアは過失致死罪の容疑で裁判にかけられ、野心的な女性弁護士エリンが彼の弁護を担当するが……。実話を基に描いたオカルト・ホラーで、全米初登場ナンバーワンを記録。監督は「ヘルレイザー／ゲート・オブ・インフェルノ」のスコット・デリクソン。撮影は「ブラッド・ワーク」以降のクリント・イーストウッド作品を手がけるトム・スターン。", "2007-03-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Intouchables", "最強のふたり", "", "", "http://image.eiga.k-img.com/images/movie/57365/315x210.jpg?4434", 112, "パラグライダーの事故で首から下が麻痺してしまった富豪の男と、介護役として男に雇われた刑務所を出たばかりの黒人青年の交流を、笑いと涙を交えて描く実話がもとのドラマ。2011年・第24回東京国際映画祭で東京サクラグランプリ（最優秀作品賞）と最優秀男優賞をダブル受賞した。", "2012-09-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カミカゼ野郎 真昼の決斗", "カミカゼ野郎 真昼の決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "雪山で宝石商の殺害現場に遭遇した女好きのパイロット、健。セスナ機を台湾に送る仕事を受けた彼は、怪しげな男たちの警告を無視して現地へと飛ぶ。そこに待ち受けていたのは、第二次大戦期に民間から徴収された二百億円相当のダイヤをめぐる陰謀。この一件には、亡き健の父も関わっていた……。台湾でロケ撮影された、深作欣二監督のアクション。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KRAYS", "ザ・クレイズ 冷血の絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "懸命に戦い、そしてその戦いに勝つことを母親から厳しく教え込まれた双子の兄弟が、暗黒帝国を牛耳るドンに育っていくまでの紆余曲折を描く。時ならぬアメリカ映画のマフィア・ブームに触発された、英国マフィアの実態を描いたブリティッシュ・ノワール。1980年代に人気を博したロック・バンド、スパンダー・バレエのケンプ兄弟が、双子の役にチャレンジしている。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それでも花は咲いていく", "それでも花は咲いていく", "", "", "http://image.eiga.k-img.com/images/movie/56115/315x210.jpg?4793", 117, "ものまね芸人で俳優としても活躍する前田健が、2009年に書き下ろした処女小説を自身初のメガホンで映画化。教え子との禁じられた恋で罪を背負った有名進学塾の元講師の男、醜い容姿のために人から拒絶され、他人の部屋に侵入することに生きがいを感じる男、最愛の母親を突然亡くしてぼう然と日々を送る男。それぞれの問題を抱えた3人の男たちの内面を繊細に描き出す。", "2011-05-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴト師株式会社III", "ゴト師株式会社III", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "新潟のパチンコ店にイカサマ師が入ったことを知ったゴト師株式会社のメンバーは、調査のため新潟へ。しかし、その留守に仲間の一人が何者かにさらわれる。リーダー君島は、仲間を助けるため、敵のアジトに乗り込む。そこにいたのは、死んだと思われていたかつての仲間だった。仲間たちにより救出された君島は、かつての仲間とパチンコで勝負をする。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMMANDO", "コマンドー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ボディービル界でミスター・ユニバースのタイトルを７回獲得するなど、本物の筋肉美で圧倒するシュワルツェネッガー。この作品では娘を誘拐されながらもテロリストへの協力を拒否する、元特殊作戦部隊のリーダーに扮し、胸のすくようなアクションを見せる。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘密戦隊ゴレンジャー", "秘密戦隊ゴレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "戦隊ヒーローの元祖「秘密戦隊ゴレンジャー」のテレビ版第6話「赤い謎！スパイルートを海に追え」を、「東映まんがまつり」公開用に再編集した劇場版第1作。イーグル情報局の設計図を入手した鉄輪仮面は、体をバラバラにする特技を駆使して潜入に成功、ゴレンジャーの基地の場所が記録されたマイクロフィルムを奪い去る。これを知ったゴレンジャーたちは、マイクロフィルムを取り戻すべく捜索を開始するが……。", "1975-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COUNTERPOINT", "誇り高き戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "米軍慰問協会の演奏旅行のため、交響管弦楽団を率いてベルギーを訪れた世界的に有名なアメリカの指揮者エバンス。だが、一行が初めて演奏会を開いた夜、ドイツ軍の総反撃が始まり、全員が捕虜にされる。しかし、敵の中にも音楽をこよなく愛する将軍シラーがいた。シラーはエバンスに演奏会を命じるが、エバンスは敵兵のために指揮することを拒む。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("相棒　劇場版　絶体絶命！42.195km 東京ビッグシティマラソン", "相棒　劇場版　絶体絶命！42.195km 東京ビッグシティマラソン", "", "", "http://image.eiga.k-img.com/images/movie/53325/315x210.jpg?4214", 117, "警視庁特命係に所属する名コンビの奮闘を描き好評を博したテレビドラマの劇場版。とある女性議員の警護を命じられた右京と薫は、爆発物で狙われた彼女を危機一髪で救う。事件が連続殺人の一部だと突き止めた2人は捜査を開始。やがて、犯人のターゲットが18万もの人々が集う“東京ビッグシティマラソン”であることを知る。息の合った掛け合いを見せる水谷豊＆寺脇康文の主演コンビをはじめ、テレビでおなじみのキャスト・スタッフが再集結。", "2008-05-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほたるの星", "ほたるの星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「ドリームメーカー」の菅原浩志監督が、実話をもとに撮り上げた感動のヒューマン・ドラマ。山口県の小学校を舞台に、激減したほたるを蘇らせようと立ち上がった新米教師と生徒たちの心温まる交流が描かれる。柳井の白壁の町並みや、油谷町の棚田といった山口を代表する美しい景観と、映画初出演の子供たちが見せるナチュラルな演技が印象的だ。働きながら、やっとの思いで小学校の教師になった三輪元だったが、現実の教員生活は想像以上に厳しかった。ある日、生徒たちを連れて課外授業を行った元は、ほたる飼育計画を思いつき、実行に移し始めるのだが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REDS", "レッズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 195, "ロシア革命の現場に立ち合い、歴史的なルポルタージュ『世界を震憾させた十日間』を書いたジャーナリスト、ジョン・リードの伝記映画。新聞記者だったリードは、労働運動の高まりの中で政治活動に目覚めていく。彼のパートナーは、夫を捨てて彼と自由な同棲関係を持つ、進歩派女性ルイーズ。友人は劇作家のユージン・オニールら、リベラル派の人々。第一時世界大戦からロシア革命へと、時代の変革を目の当たりにしたリードは、アメリカに戻り、共産主義運動を推し進めようとするのだが……。理想の実現をはかろうとして、現実の壁の前で苦悩する主人公を、製作・監督・脚本・主演の四役をこなしたベイティが熱演。現代史の中に生きた人間の一大ロマン大作。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES ANNE´ES LUMIE｀RES", "光年のかなた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アイルランドの田舎町。パブで働く青年ジョナスは、ヨシュカという老人に魅せられて、彼の家に訪れる。ヨシュカは情熱的な人物だが、世間からは変人扱いされていた。彼はジョナスに次々と試練を与え、その最後に生きた鷲を捕まえてくるように言う。ジョナスは荒野を旅し、大きな鷲を捕まえる。ヨシュカはそんなジョナスを見て、彼を秘密の納屋に連れていく。そこには巨大な人工翼があった。そしてある夜、ヨシュカはその人工翼をつけ、光年の彼方に飛び立っていく。人里離れた荒野で人類が失った夢を追い求める老人と青年のシュールで不思議な探求譚。", "1980-01-01", "2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Throne of Blood", "蜘蛛巣城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "シェイクスピアの「マクベス」を日本の戦国時代に置き換え描いた、戦国武将の一大悲劇。謀叛を起こした敵を討ち城主の危機を救った鷲津武時は、帰城途中に出会った老婆から不思議な予言を聞く。その予言通りに大将に任ぜられると、今度は妻にそそのかされて主を殺害、自ら城主の地位に着く。黒澤監督は、欲望に刈られた魂が繰り返す殺戮と狂気を、能の様式美に乗せて見事に描いていく。三船＝マクベスが無数の矢に曝されるラストシーンは圧巻。", "1957-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たべるきしない", "たべるきしない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 19, "「ぴちょんくん」など話題のCMを数多く手がけてきたCMディレクター・伊藤由美子が初メガホンを取り、失恋を通して成長していく女の子の心模様をリアルにつづったショートフィルム。愛する彼氏の竹中君と別れてしまった21歳のナオ。ひとりでも生きていける強い女になりたいと願うナオだったが、竹中のことがなかなか忘れられず……。ヒロインを演じるのは、テレビ版「世界の中心で、愛をさけぶ」などの若手注目女優・綾瀬はるか。", "2006-01-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE DEMON Y LAS INVASORAS", "ブルーデーモンvs侵略者　女エイリアンの襲来", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "地球征服を企てるエイリアン美女軍団が襲来。次々と男たちが姿を消すなか、正義のヒーロー、ブルーデーモンが登場。地球を守るため侵略者たちと戦う。勧善懲悪、奇想天外なメキシコ版B級SF映画。ちなみにブルーデーモンは、ルチャ・リブレ（メキシカン・スタイルのプロレス）の実在のスター選手だ。", "1982-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Golden Earrings", "黄金の耳飾り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1946年、ロンドン。ホテルで平穏に暮らすイギリス諜報部員の元に一組の金のイヤリングが届いた。それは彼が、第二次世界大戦中、ナチス・ドイツから毒ガス情報を隠蔽する危険な任務に就いていた時、協力を得ていた美しいジプシー女のものだった……。妖花M・ディートリヒが、英国人スパイを助けるジプシー役を演じるサスペンス・ストーリー。“赤狩り”でハリウッドを追われた名脚本家、A・ポロンスキーの脚本をM・ライゼンが映画化した異色作。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O　PRINCI´PIO　DA　INCERTEZA", "家宝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "90歳を越えてもなお現役のマノエル・ド・オリヴェイラの監督作品。ポルトガルの美しい景観を背景に、幸せを追い求める一人の女性と彼女を取り巻く男女の恋の行方を描く。良家の息子、アントニオに嫁いだカミーラは、彼がいかがわしい娼館や酒場を経営するヴァネッサと不倫していることを黙認していた。なぜならカミーラもアントニオと旧知の仲であるジョゼに思いを寄せていて……。", "2002-01-01", "25,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COURAGE MOUNTAIN", "チャーリー・シーン　アルプスを越えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "1915年、アルプスで育ったハイジは15歳になりイタリアの女学校へ進学することになった。一方、幼なじみのペーターも軍隊生活に入る。二人が新たな道を歩き始めた時、第一次大戦が勃発、女学校は閉鎖、ハイジたちは悪名高い孤児院へ。孤児院での生活は悲惨極まりなく、ハイジは仲間と脱走、冬のアルプスを越えスイスに戻ることにした。アニメでおなじみ「アルプスの少女ハイジ」の物語。ハイジ役のJ・ケイトンはマーティン・スコセッシ監督に見いだされ、「最後の誘惑」のイエスを誘惑する天使役でデビュー。本作撮影時は14歳である。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Secuestrados", "スペイン一家監禁事件", "", "", "http://image.eiga.k-img.com/images/movie/56415/315x210.jpg?1379", 85, "強盗により平和な生活を破壊された家族が直面する恐怖を描く密室型サスペンス。働き盛りの夫ハイメと妻マルタ、娘イサは一家そろって郊外の新興住宅地へ移り住む。しかし引越しを終えた夜、覆面を被った3人の男たちが押し入り、金品強奪を企てる。繰り返される暴力になすすべもなく捕われの身となったハイメらは、過酷な一夜を送ることになる。2010年オースチン・ファンタスティック映画祭で最優秀ホラー賞を受賞。監督は本作が長編2作目のスペインの新鋭ミゲル・アンヘラ・ビバス。", "2011-07-02", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES MISE´RABLES", "レ・ミゼラブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "「愛の風景」の名匠B・アウグストによる、V・ユゴーの名作文学の映像化。19年間投獄されていたジャン・バルジャンは老司教との出会いによって、荒んだ生き方を改め、９年後、民衆思いの良き市長となった。ある日、町に新任の警察署長ジャベールが赴任。かつて徒刑場の監視人をしていた彼は、バルジャンの過去に気付き、執拗に彼を追い詰める……。バルジャンの善との格闘、ジャベールとの関係に重点を置き、骨のある人間ドラマに仕立てている。バルジャン役のL・ニーソン、ジャベールを演じる「シャイン」のJ・ラッシュの共演も見ごたえ十分。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DINOSAUR", "ダイナソー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "ディズニーが最新技術を駆使して製作したスペクタクル・アドベンチャー。6500万年前の白亜紀。卵のときに翼竜に連れ去られキツネザルの孤島で育ったイグアノドンのアラダー。心優しい彼はキツネザルたちを家族のように思い、平穏な日々を送っていた。そんなある日、隕石群の落下によって大地は焼き尽くされ、多くの生物が死滅する。かろうじて生き延びたアラダーは、キツネザルたちと生命の楽園を求めて旅に出発。途中で出会った仲間の恐竜たちと合流するが、冷酷なリーダー、クローンと対立することになる。実写とCGを融合した驚異の映像で恐竜の世界をダイナミックに描き出す。恐竜の視点から見た景色や、リアルな質感を感じさせる30種以上もの恐竜たちの精密な動きなども圧巻。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("崖の上のポニョ", "崖の上のポニョ", "", "", "http://image.eiga.k-img.com/images/movie/53305/315x210.jpg?4534", 101, "宮崎駿監督が「ハウルの動く城」以来4年ぶりに手掛けた長編アニメーション。海辺の町で暮らす5歳の少年・宗介は、クラゲに乗って家出した魚の子ども・ポニョに出会う。すぐに仲良くなる彼らだったが、ポニョはかつて人間だった父・フジモトによって海に連れ戻されてしまう。ポニョは父の魔法を盗んで再び宗介のもとを目指すが……。アンデルセン童話「人魚姫」をモチーフに、人間になりたい魚と少年の心温まる交流を描いたファンタジー。", "2008-07-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIRTY MARY CRAZY LARRY", "ダーティ・メリー、クレイジー・ラリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "レースカーを買う金欲しさに、スーパーマーケットを襲い15万ドルを奪って逃げた二人の男ラリーとディーク。そして、ひょんなことから二人と行動をともにする女メリー。事件の連絡を受けた警察は、ヘリコプターとパトカーによる空陸捜査網を敷き、犯人を追うが……。目を見はるほど徹底したカーチェイス映画。主演はP・フォンダ、彼らを執拗に追いかける警部をV・モローが演じている。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本一のワルノリ男", "日本一のワルノリ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "植木等と、ドリフターズの加藤茶を合わせて売り出そうとした作品。加藤茶は植木等に迷惑をかける出来のわるい教え子となり、植木等はここでは従来の“無責任男”ではなくとことん責任をみないと気がすまない男を演じている。久しぶりに浜美枝が共演した。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOUNG MR.LINCOLN", "若き日のリンカン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "J・フォード監督による、第16代米国大統領リンカーンの青春記。とはいえ堅苦しい偉人伝ではなく、敏腕弁護士として活躍していた“若き日の”大統領の姿を描いた一種の法廷ドラマである。独立記念日に起こったある警官殺害事件で、犯人として逮捕されたクレイ兄弟の弁護を引き受けるリンカーン。怒り狂って兄弟をリンチにかけようとする群衆をなだめた彼は、不利な裁判にもかかわらず、常に慈愛と公正をもって任に当たる。だが目撃者キャスの証言で、ついに兄弟の有罪は確定的になる……。演出、撮影から俳優の演技までが巧みに練られ、特にH・フォンダの持ち味が良く出た好編。リバイバル時、ビデオ、DVDタイトルは「若き日のリンカーン」。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鳥人", "鳥人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ライト兄弟に先んじて、江戸時代に初めて空を飛んだ男、表具紙・幸吉の物語。空を飛ぶ夢に取りつかれた幸吉が世間の冷たい目にも、また自らの貧窮にも負けず空飛ぶ機械を作成し、大空に舞うまでを描く。監督は、「春秋一刀流」でデビューし、のちに阪東妻三郎主演の傑作、「孤の呉れた赤ん坊」を残す丸根賛太郎。軽妙洒脱ながらも、情感豊かな演出で鬼才とうたわれた丸根だが、本編ではこの実話に大真面目に取り組んでいる。当時日本の俳優として初の二等飛行士免許を取得した嵐寛寿郎が、空飛ぶ男・幸吉を熱演。そして幸吉を助けてその夢に殉ずる浪人・経堂十兵衛役の大邦一公が絶品。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PE´PE´ LE MOKO", "望郷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "舞台は、フランスの植民地アルジェリアの首都アルジェのカスバ。パリから逃れてきた凶悪犯ペペ・ル・モコは、ギャビーという美しい女と知り合い熱烈な恋をする。だが、嫉妬に狂ったペペの情婦が彼を警察に密告してしまう。ギャバンの名声を決定づけたデュヴィヴィエの傑作。", "1937-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらばモスクワ愚連隊", "さらばモスクワ愚連隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "かつてはジャズピアノの名プレーヤーであり、今は音楽プロモーターをしている北見英二は、日本のジャズバンドをソ連に送るという計画を引き受け、モスクワに発つ。しかし、その裏には政治的陰謀が……。五木寛之のデビュー作を原作にした、ポリティカル・サスペンス。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sleepless in Seattle", "めぐり逢えたら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　トム・ハンクスとメグ・ライアンが2度目の共演を果たしたハートフル・ストーリー。シアトルに住むサムは、妻に先立たれ孤独な日々を過ごしていた。父を励まそうと、8歳の息子がラジオ番組で「パパに新しい奥さんを……」とリクエストする。切ないメッセージに心を動かされたのは、ボルチモアの新聞記者アニーだった。劇中、アニーがケイリー・グラント主演の「めぐり逢い」を観て感動したり、2人がエンパイア・ステート・ビルで出会ったりと名作の引用を使ったニクい演出も。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEPTEMBER SONGS", "セプテンバー・ソングス 9月のクルト・ヴァイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「三文オペラ」で知られる作曲家クルト・ヴァイルの曲を集めて、1985年に作られたアルバム『LOST IN THE STARS：THE Music of Kurt Weill』にインスパイアされたL・ワインスティーンが映画化した作品。約９年の準備期間を経て作られたこの映画にはE・コステロ、N・ケイヴらが参加、最後にはアルバムにも参加しているL・リードが『セプテンバー・ソング』を歌い、締めくくっている。", "1995-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新宿泥棒日記", "新宿泥棒日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "60年代の日本において、アンダーグラウンドの発信地にして政治活動の中心地であった新宿の息吹を、セミ・ドキュメンタリータッチで描いた異色作。横尾忠則、唐十郎など、当時の日本のカルチャー・シーンを代表するアーティストが多数実名で出演。手持ちカメラを駆使したリアリティーあふれる映像が、幻想と現実が交錯した難解な世界を作り上げる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SON OF DEACULA", "夜の悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "吸血鬼ホラーのルーツ的シリーズの第３弾。1950年に日本公開され、多くの観客を恐怖で凍りつかせた一作。ドラキュラ伯爵は名前を変えアメリカの南部の町に逃亡した。そこで、彼氏と別れた富豪の娘と出会い結婚することに。だが、娘は吸血鬼になって不老不死のパワーをゲットし、元彼と永遠に結ばれたい狙いがあった。それを知らない元彼がドラキュラに決闘を挑む。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOUT", "過ぎゆく夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ロックがまだ危険視されていた1950年代半ばのテキサスを舞台に、反抗心あふれる少年たちの、人との出会いと友情、ロックとのかかわり、初めての恋愛などを通して彼らの変化と成長を描いた青春映画。アース・トーンのセピア色を基調とした画面、時代の精神を反映させたオリジナル・ロックなど、全編にノスタルジックな雰囲気が漂う。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺の血が騒ぐ", "俺の血が騒ぐ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "石原裕次郎主演の「鷲と鷹」にも通じる、船を舞台にした海洋ミステリーの佳作。船長をしていた父が、何者かに殺されたことを知った笠原邦夫は、父の仇の手掛かりを求めて、玄海丸に乗り込む。船員は皆ひとクセある連中ばかり。なかでも、邦夫の元婚約者で、今は弟・明の恋人、節子の父親である船医が怪しいと邦夫はにらむ。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARK BLUE WORLD", "ダーク・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "「コーリャ　愛のプラハ」の名匠スヴィエラークがスタッフを再結集させ、通常のチェコ映画のおよそ10倍の製作費を投じて作り上げた反戦映画。第二次世界大戦下、ドイツに支配された祖国を捨ててイギリス空軍に入隊したチェコ人パイロット二人の友情が感動的に描かれる。スケールの大きい、迫力の空中戦シーンが秀逸。監督の父で「コーリャ…」の主演俳優ズディニェクが脚本を執筆している。スタジオジブリ提供作品。", "2001-01-01", "43,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("２／２", "２／２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "中島みゆきの人気ステージとして知られる“夜会”でも上演された、彼女の同名小説を映画化した切ないラブ・ストーリー。辛い過去を持つ女性と、そこから彼女を救おうとする男が数奇な運命に巻き込まれていく。原作者の中島も認める劇場版ならではのアイデアが散りばめられた物語が秀逸だ。編集者の莉花はイラストレーターの矢沢と出会い、付き合うようになる。ところがそれを機に彼女の周りで不可解な事件が起き始め……。", "2005-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAS　SUR　LA　BOUCHE", "巴里の恋愛協奏曲〈コンチェルト〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「恋するシャンソン」のA・レネ監督による、モダニズムの花咲く頃のパリを舞台にした本格ミュージカル。男女７人の思いが交差するラブ・ゲームが繰り広げられる。80歳を超えるレネ監督が黄金期のミュージカルの魅力を伝えながらも、現代に通用するオペレッタの世界を構築。豪華な20年代衣装、美術品のようなセット、全編を流れるミュージカル・ナンバーなど、どれもが光輝く。フランス演劇界、映画界の実力派俳優たちが披露する美声もすばらしい。潔癖な実業家の夫ジョルジュに離婚歴があることをひた隠している妻のジルベルト。そんなある日、夫の客人が夫婦の邸宅を訪れる。ところがその人物は彼女が離婚したエリックで……。", "2004-12-25", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRAZIL", "未来世紀ブラジル", "", "", "http://ecx.images-amazon.com/images/I/512hUgRI0xL._SL210_.jpg", 135, "20世紀のどこかの国。管理の行き届いたコンピュータ社会（その割に機械類はアンティーク風）で、主人公サムは情報省記録局の小役人だ。彼は有能だが機械にうんざり、美女を救うスーパーマンになった自分の夢を見ている。ある朝、局の玄関で夢の中の美女とそっくりの女ジルを見かけたサムは、彼女を追ううち巨大な悪夢のなかに吸い込まれていく。事の起こりは、ある役人が叩き潰したハエによる印刷ミス。神出鬼没の反政府テロリスト、タトルと善良な靴職人バトル氏が間違って逮捕されたことから、サムは処理を任されるが、誤認逮捕のもみ消しは混乱を極め、さらに部屋にはダクトやチューブがあふれ、デパートでは爆弾事件が起きる。やがて犯罪者として“洗脳”されることになるサムの頭には自由への脱出の夢、そして軽快なラテン音楽『ブラジル』が反響する。T・ギリアムの壮大な悪夢的イマジネーションに脱帽。製作サイドと監督の闘争を記録した『バトル・オブ・ブラジル』なる本もあるほどのカルト作品である。", "1985-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGELS WITH DIRTY FACES", "汚れた顔の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "刑務所から出所したギャングのロッキーは、かつて自分がかばってやった弁護士が自分を殺そうとしているのを知り、逆に射殺した。逮捕され死刑の判決を受けながら平然としているロッキーの態度に、少年たちはギャングへの憧れと崇拝の念を抱く。牧師となって貧民街の少年たちの面倒を見ていたロッキーの幼友だちジェリーは、少年たちの将来を危ぶみ、ロッキーを訪ねて、処刑の時にわざと臆病にふるまうよう懇願する。その場はにべもなく拒否したロッキーだったが……。ギャング映画の変質を告げた良心的内容の名作。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MESSAGE IN A BOTTLE", "メッセージ・イン・ア・ボトル", "", "", "http://ecx.images-amazon.com/images/I/51q2ToDGzFL._SL210_.jpg", 131, "人気作家N・スパークスのベストセラー小説を映画化したロマンティックなラブ・ストーリー。離婚したばかりのシングル・マザーで新聞記者のテレーサが、浜辺でボトルに詰められた手紙を見つける。そこには、ある男性の亡き妻への思いが綴られていた。誠実な文面に心を動かされたテレーサは、手紙を新聞に掲載し、寄せられた情報から送り主ギャレットの居場所を突き止める。やがて二人は恋に落ちるが、ギャレットは亡き妻の面影をぬぐい去ることができず……。心に傷を負った男女が互いの存在に心癒されていく姿を、美しい映像のなかに情感豊かに描き出す。二人の恋の行方を案じる主人公の父親役のP・ニューマンが存在感を放つ。", "1999-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乱世児女／SHANGHAI，SHANGHAI", "愛と欲望の街−上海セレナーデ−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "時代は1930年代、エリートの兄を頼って大都会、上海に出てきたチャンホは事件に巻き込まれる。500万ドル略奪の現場に居合わせてしまったのだ。しかもその金は革命党の活動資金であり、兄も革命党にかかわっていた。チャンホと兄、そして恋人チャピの背後に不気味に現れたのは暗黒街のボス、チンであった。チンは３人を罠に落とそうとたくらむ。ユン・ピョウがヒーローを演じる香港映画オールスター・キャストのアクション。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPOTLIGHT", "SPOTLIGHT", "", "", "http://image.eiga.k-img.com/images/movie/57955/315x210.jpg?4103", 0, "2012年・第4回沖縄国際映画祭の企画「地域発信型プロジェクト」で製作された作品。昭和初期の東京のキャバレーを舞台に、ダンサーになるという夢を忘れてしまった女性が、1人の少女との出会いから再び夢に立ち向かっていく姿を描く。主演は女性お笑い芸人の渡辺直美。キャバレーのトップダンサー役に、人気アイドルグループ「AKB48」とその派生ユニット「フレンチ・キス」で活躍する柏木由紀、高城亜紀、倉持明日香。「ももいろクローバーZ」の百田夏菜子も出演する。", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Owl and the Sparrow", "地球でいちばん幸せな場所", "", "", "http://image.eiga.k-img.com/images/movie/53705/315x210.jpg?2774", 98, "意地悪な叔父の村工場を飛び出し、ベトナム最大の都市ホーチミンにやってきた10歳の少女トゥイ。食費を稼ぐため街頭で花売りを始めたトゥイは、フライトアテンダントのランと動物園の象の飼育係ハイに出会い、どこか寂しそうなランと親切で優しいハイを引き合わせるが……。ベトナム系アメリカ人のセテファン・ゴーガー監督が、路上で暮らしながらもたくましく明るく生きるベトナムの子供の姿を描いたハートフル・ストーリー。ロサンゼルス映画祭観客賞受賞。", "2008-08-02", "1,60", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAQOYQATSI：LIFE　AS　WAR", "ナコイカッツィ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "G・レジオ監督が、S・ソダーバーグ全面協力のもと、「コヤニスカッティ」「ポワカッツィ」に続いて完成させた、“カッツィ”３部作の最終章。今回、テーマに掲げられたのは“戦争”と“テクノロジー”。前２作同様、ナレーションやセリフは一切なく、彼独自の感性でつながれた多様なデジタル映像と、P・グラスが奏でる音楽だけで、錯綜する現代が表現される。唯一無比といえる映像世界は体感の価値あり。21世紀を迎えてもなお消えることない戦争。映画はこれまでの歴史で幾度となく繰り返されてきた戦争と暴力の愚行を伝える。と同時に、日々、目覚しい発達を遂げるテクノロジーのパワーで益々混迷をきわめていく現代社会を映し出す。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VOLLMOND", "最後通告", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "長編劇映画としては「山の焚火」以来13年ぶりとなるF・M・ムーラー監督作品。満月の夜、10歳の子供が男女６人ずつ謎の失踪を遂げる。誘拐か？　家出か？　困惑する親や警察たちを尻目にやがて、それぞれの家庭に同じ文面の手紙が届く。そこには“僕らは地球の幸せを望む”という意味不明のメッセージが記されていた……。メディア問題もはらんだ現実的な題材を、どんどん寓話的に展開。観客に問いかけるかのような結末まで、奇妙に安定感を欠いた映像文体で幻惑する。", "1998-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OCEAN TRIBE", "オーシャン・トライブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "1991年に若くしてガンで他界したボブ・クックの実話をもとに、ユーモアと美しいサーフィン・シーンを交えて描いた感動のロード・ムービー。ガンに冒されたボブを４人のサーフィン仲間はメキシコへのサーフ・トリップに誘うが、放射線療法で髪を失ったボブは自分の変わり果てた姿を友人たちに見せたがらない。彼の心を思いやった仲間たちは坊主頭になって彼を旅に連れ出すのだった……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("音の城♪音の海 SOUND to MUSIC", "音の城♪音の海 SOUND to MUSIC", "", "", "http://image.eiga.k-img.com/images/movie/55355/315x210.jpg?2459", 90, "自閉症、ダウン症、ウィリアムズ症候群など、知的障害を持つ人々による即興演奏について研究する学生たちが始めた「音遊びの会」。知的障害者と音楽家たちのコミュニケーションを通し、全ての人にとって自由な音楽を模索するドキュメンタリー。監督は新鋭ドキュメンタリー作家の服部智行。", "2010-05-29", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　TRUFFES", "ボクサー・最後の挑戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "J・レノが1995年に主演した人情味豊かなロード・ムービー。人生を変えようと決意した弱小ボクサーが、しょぼくれ詐欺師を相棒に南仏をめざす。女好きのボクサーと、まだ童貞の中年詐欺師。凸凹コンビの自動車旅行が、カラッとした悲哀とともに、あくまでもコメディ・テイストで紡がれていく。ありそうでなかったレノのキャラクターも新鮮な一本。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("28 Weeks Later", "28週後...", "", "", "http://image.eiga.k-img.com/images/movie/1915/315x210.jpg?4374", 104, "新種のウィルスにより凶暴化した人間たちが次々と生存者を襲う大ヒットホラー「28日後...」の続編。前作のダニー・ボイル監督に代わり、「10億分の1の男」のファン・カルロス・フレスナディージョがメガホンを取る。ウィルス発生から28週後。アメリカ軍の介入によってウィルス根絶宣言が出されたロンドンでは、ようやく復興の兆しが見え始めていた。ところが、ある人物の体内にウィルスが潜伏しており……。", "2008-01-19", "3,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MODERN GIRLS", "シンシア・ギブのモダン・ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "退屈な生活に飽き飽きする３人の女の子。男の子をひっかけるために夜のディスコへ繰り出して行くが、ひょんなことから一人のウブで内気な青年が彼女たちの仲間に加わる。イイ男はいないかと品定めしているところへ、なんと憧れのロック・スターが現れたから大変!? 当時のアイドル、C・ギブ主演の青春コメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山桜", "山桜", "", "", "http://image.eiga.k-img.com/images/movie/53425/315x210.jpg?2509", 99, "江戸時代後期。嫁ぎ先で辛い日々を送っていた野江は、叔母の墓参りからの帰り道にある山桜の下で、かつて彼女に縁談を申し込んだ武士・弥一郎に出会う。彼が自分を気にかけてくれていたことを知り元気づけられる野江。しかし半年後、弥一郎が思わぬ事件を起こす。時代小説の巨匠・藤沢周平の短編を「地下鉄（メトロ）に乗って」の篠原哲雄監督が映画化。田中麗奈と東山紀之が主演を務め、檀ふみ、村井国夫、富司純子ら実力派が脇を固める。", "2008-05-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野球狂の詩", "野球狂の詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "万年最下位チームにやってきた、魔球“ドリーム・ボール”を擁する美少女サウスポー・水原勇気。そんなヒロインのの活躍を描いた水島新司による野球マンガの映画化。うだつのあがらぬ若手選手を演じる男優陣が魅力的。可憐なイメージで売り出した木之内みどりが主人公に扮する。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bedroom Window", "窓・ベッドルームの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "　建築家のテリーは社長夫人のシルビアと不倫関係にあった。ある夜、シルビアはテリーの部屋の窓からレイプ事件を目撃。名乗ることができないシルビアの代わりに、テリーが目撃者であると主張し、法廷で証言をすることに。しかし彼が真の目撃者でないことが明らかになってしまう。容疑者だったヘンダーソンは釈放されたのち、シルビアを殺害。そしてその容疑がテリーにかかり……。予期せぬ事件に巻き込まれた青年が意外な協力者とともに真犯人を追う姿を描いたサスペンス。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUSIK I MO¨RKER", "闇の中の音楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "兵士のベンクトは射撃訓練中の事故で失明し、音楽で生きてゆこうと考えるが音楽学校の受験に失敗、レストランのピアノ弾きに落ちぶれた。彼の前に現れたのは、以前ベンクトの叔母のところで女中をしていたイングリッド。彼女は今や音楽学校の奨学生だった……。巨匠、I・ベルイマン初期の秀作。", "1947-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHRISTMAS COMES TO WILLOW CREEK", "カリフォルニア・エクスプレス　クリスマス・イブの奇跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "カリフォルニアからアラスカの山奥に、クリスマスプレゼントを乗せてひた走るカリフォルニア・エクスプレス。そして闇の中から不思議な山男が現れ、奇跡を呼び起こす……。素敵な夢と感動を与え、クリスマスに向けた心温まるファミリー・ドラマ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最好的時光", "百年恋歌", "", "", "http://image.eiga.k-img.com/images/movie/1705/315x210.jpg?4035", 131, "「悲情城市」「戯夢人生」のホウ・シャオシェン監督による恋愛オムニバス。1966年、ビリヤード場で働く少女と兵役に向かう青年。1911年、辛亥革命前夜に愛を交す高官と芸妓。2005年、大都会で傷つきながら惹かれあう写真家と歌手。3つの異なる時代で一組の男女が出会いを繰り返し、宿命の恋に落ちる。少女、芸妓、歌手と3様の女性を演じたのは「ミレニアム・マンボ」のスー・チー。「愛の神、エロス」のチャン・チェンがその相手を務める。", "2006-10-21", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tjorven, Batsman och Moses", "わたしたちの島で　わんぱくアザラシのモーセ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「長くつ下のピッピ」で知られるスウェーデンの人気児童文学作家アストリッド・リンドグレーンの原作を基に、のどかな島で繰り広げられる、子どもたちとアザラシの交流を描く。ウミガラス島で暮らす、ちょっとドジでおてんばな女の子ショルベン。動物好きの彼女は愛犬ボーツマンといつも一緒に過ごしている。ある日、漁師から1頭のアザラシを譲ってもらった彼女は、“モーセ”と名付けたそのアザラシを友達と一緒に飼い始めるが……。", "1964-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　WOMAN’S　TALE", "ある老女の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1991年のオーストラリア・アカデミー賞最優秀主演女優賞を受賞した感動作。死に直面した一人の老女の姿を通して、人生の素晴らしさと愛の尊さをうたい上げる。隣室の孤独な老人ビリーの面倒を見ながら一人暮らしをしている78歳のマーサは、明るく心優しい女性。時々訪れる地域看護婦のアンナとは固い絆で結ばれており、彼女とその恋人の不倫のために部屋を提供したりもしている。そんなある日、ビリーが一人寂しく亡くなった。ショックを受け倒れたマーサも、末期の肺ガンであることを告げられる。それでも気丈さを失わない彼女は、アンナの協力を得て、住み慣れた家で最期の時を迎える決意をするのだった。主人公を演じるのは英国やオーストラリアで活躍したベテラン女優S・フローランス。旧知の間柄である監督が彼女のために撮り上げた。自身も病に冒されていた彼女は撮影終了後に亡くなっている。", "1991-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("E LA NAVE VA", "そして船は行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "フェリーニ監督の第19作。冒頭から、映画の歴史をワンシーンに凝縮して見せ、圧倒する。第一次世界大戦前夜、ナポリ湾から出港した豪華客船に乗り合わせた芸術家、貴族、そして救済された亡命難民などの運命が、独特のオペラ・タッチで描かれる。沈みゆく船に万感の思いが込められた、フェリーニの集大成的作品。D・フィオレッティの船内の装飾美術を効果的に映し出した、ロトゥンノの撮影は相変わらず見事。", "1983-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボクの女に手を出すな", "ボクの女に手を出すな", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "孤児院育ちで非行歴のあるツッパリ娘が、ひょんなことから大富豪のお坊ちゃんの家庭教師を依頼され、なぜか大事件に巻き込まれていくという青春サスペンス・ドラマ。「生徒諸君！」に続く、小泉今日子の主演第２作。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KINGDOM", "キングダム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 279, "L・フォン・トリアーが「奇跡の海」に先駆けて手掛け、本国デンマークではカルト的人気を博したTVシリーズの編集版。長尺だが、巨大病院を舞台に展開するサスペンスを悪夢のような迷宮感覚で描き、一瞬も飽きさせない。キングダム病院の医師たちはそれぞれ勝手なことをしていた。ある日、入院患者のドルッセ夫人が少女の幽霊を目撃し、彼女は幽霊探しを始める。その途中、病院内の謎が次々に明らかになっていき、しかも医師たちの秘密組織“キングダムの息子たち”の存在までがわかるのだった。", "1994-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pulse", "パルス", "", "", "http://image.eiga.k-img.com/images/movie/53165/315x210.jpg?4435", 86, "インターネットを介して拡がる怪奇現象を描いた黒澤清監督作「回路」を、ホラー映画界の巨匠ウェス・クレイブンの脚本でハリウッド・リメイク。女子大生のマティは、元恋人ジョシュの様子がおかしいことに気付く。心配して彼の元を訪れるマティだったが、ジョシュは彼女を隣の部屋に残し、首を吊って自殺。彼の死に疑問を感じたマティは、仲間たちと真相を追うことに。やがて彼らは、マティが使っていたパソコンに手がかりがあることを突き止め……。", "2007-12-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バトルヒーター", "バトルヒーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "BAKUFU-SLUMPのパッパラー河合が主演したコミック・ホラー。リサイクル用の電機製品を集めていた古池は、ある日古びたコタツを拾い、アパートに持ち帰る。しかしそのコタツは世にも恐ろしい“人喰いコタツ”。封印を解かれた人喰いコタツはアパートの住民たちを熾烈な戦いに巻き込んでいく……。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CASSHERN", "CASSHERN", "", "", "http://image.eiga.k-img.com/images/movie/1325/315x210.jpg?2520", 141, "竜の子プロダクションのアニメ「新造人間キャシャーン」を、宇多田ヒカルのミュージック・ビデオで知られ、その夫でもある紀里谷和明が実写映画化。CGスーパーバイザーに庄野晴彦、VFXスーパーバイザーに「ドラゴンヘッド」の木村俊幸、バトル・シーン絵コンテに「ガメラ」の樋口真嗣、プロダクション・デザインに「あずみ」の林田裕至が参加。遺伝子工学の権威、東博士は、戦死した一人息子、鉄也を新造人間として甦らせるが……。", "2004-04-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STUD", "ザ・スタッド／堕落への情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "自分の肉体を武器に上流階級にのし上がろうとする若者“スタッド（種馬）”は、上流階級のあまりの退廃ぶりにいや気がさし逃げ出してしまう……。かつてのスキャンダル女優コリンズが、ジゴロに溺れる女を、実に淫らに演じて話題になった。", "1978-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談・蛇女", "怪談・蛇女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "明治の初め、小作人の弥作は地主の馬車でひき殺され、その妻、すえも地主に鞭打たれたのがもとで死んでしまう。それ以来地主の家にはすえの亡霊が現れるようになり、地主一家は次々と悲惨な最期を遂げていく……。「東海道四谷怪談」など怪談ものを得意とした中川信夫監督が手掛けた怪奇の世界。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・翔んでる寅次郎", "男はつらいよ・翔んでる寅次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "北海道で出会った一人旅を楽しむ娘。その後、彼女は会社社長令息との結婚式を途中で抜け出し、寅を頼って柴又までやって来てしまった……。当時“翔んでる”女優のイメージが最も強かった桃井かおりをマドンナに起用した一編。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Underground", "アンダーグラウンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 171, "1941年、ナチスドイツがユーゴスラビアに侵攻した。ベオグラードに住む武器商人マルコは、祖父の屋敷の地下に避難民たちを匿い、そこで武器をつくらせて闇市場に流していた。そして戦争が終結した後も、彼は地下に匿った人々にそのことを知らせずに武器の製造を続けさせ、巨万の富を築いていく……。エミール・クストリッツァ監督の最高傑作とも言われる本作は、1995年度のカンヌ国際映画祭でグランプリを受賞している。", "1995-01-01", "2,7,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("だんらんにっぽん　愛知・南医療生協の奇跡", "だんらんにっぽん　愛知・南医療生協の奇跡", "", "", "http://image.eiga.k-img.com/images/movie/57755/315x210.jpg?2776", 118, "名古屋市南区にある「南医療生協」の活動を通じて、無縁社会が叫ばれる現代でも失われていない人と人とのつながりを明らかにしていくドキュメンタリー。1959年の伊勢湾台風により甚大な被害を受けた名古屋市南区に61年に誕生した医療生協は、創設当時308人だった会員が6万人を超えるまでに成長した。それだけ多くの会員を抱えながらも権力構造に陥らず、ひとりひとりの権利や尊厳を尊重しながら地域づくりを目指す南医療生協の活動を追う。", "2012-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Prinsessa", "プリンセス", "", "", "http://image.eiga.k-img.com/images/movie/57255/315x210.jpg?4052", 100, "1945年、統合失調症を伴う躁うつ病と診断され、精神病院に入院させられたアンナは、自らを「プリンセス」と称し、王妃のように振る舞っていた。最初は戸惑っていた周囲の患者や病院スタッフも、次第にアンナを「プリンセス」として受け入れるようになり、病院の雰囲気は明るくなっていく。しかし、担当医のヨハンだけは徹底してアンナを患者扱いし、無理な治療を施そうとする。実在した女性アンナ・ラッパライネンの半生を描いたヒューマンドラマ。最新のフィンランド映画5作品を一挙上映する「フィンランド映画祭2011」にて上映。", "2011-10-02", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FIENDISH PLOT OF DR.FU MANCHU", "天才悪魔 フー・マンチュー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "世界的な宝石泥棒フー・マンチューの次なる獲物は、長寿の秘薬の原料となるダイヤ“キャナリー”。この宝石をめぐり、ライバルのスミス警部との丁々発止の攻防戦が展開する。異色俳優P・セラーズが、二役を演じるギャグ満載のコメディ映画。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nails", "ネイルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ハリウッドの異端児D・ホッパーが、無鉄砲な刑事に扮したアクション。相棒を麻薬組織に殺され、怒りに震えるはみだし刑事ネイルズは、無軌道ともいえる捜査を開始する。殺し屋に命を狙われた彼は、元相棒のペドロと再び組み、組織のボスの追跡に乗り出す。だが、組織はネイルズの妻を人質にとるのだった……。４文字言葉を連発するホッパーの、テンションの高い演技が「ブルー・ベルベット」を思わせる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAGHS−E　KHAK", "ダンス・オブ・ダスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "砂漠の村で煉瓦を作って暮らしている11歳の少年と、村にやってきた季節労働者の娘。二人のほのかな交流と別れを、一切の台詞を排除し、映像と現実音だけで紡いで描いていく、イラン映画界の雄A・ジャリリ監督の意欲作。本国では上映を禁じられ、世界中の映画祭で絶賛されることでようやく陽の目を見た作品。", "1998-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旧支配者のキャロル", "旧支配者のキャロル", "", "", "http://image.eiga.k-img.com/images/movie/58045/315x210.jpg?3787", 47, "トラッシュ・カルチャーの専門誌「TRASH-UP!!」と3人の映画監督がコラボレーションした上映プログラム「コラボ・モンスターズ!!」の1作。映画学校の卒業制作で監督に選ばれたみゆきは、講師であこがれの女優でもあるナオミに出演を依頼するが、撮影現場でナオミから出される課題の数々は過酷を極め……。監督は、「リング」の脚本や「恐怖」「おろち」などで知られる高橋洋。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1941", "1941", "", "", "http://ecx.images-amazon.com/images/I/51rN6ja409L._SL210_.jpg", 119, "1941年12月13日、あのパール・ハーバー攻撃直後、カリフォルニアの海岸に大日本帝国の潜水艦が出現。三船敏郎扮する艦長の命令によるハリウッド攻撃で、ロサンゼルスの街は大混乱となる。ベルーシとエイクロイドのコンビがあばれまくり、スピルバーグ監督がパロディー・シーンを散りばめて描く奇想天外なドタバタ・コメディ。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Oceans", "オーシャンズ", "", "", "http://image.eiga.k-img.com/images/movie/54165/315x210.jpg?4411", 103, "「WATARIDORI」でさまざまな渡り鳥の生態を記録したジャック・ペランが、世界中の海とそこに暮らす生命体を革新的な映像美で描く海洋ドキュメンタリー。日本版ナレーションを宮沢りえが担当する。ハンドウイルカの大群、ザドウクジラの捕食、5万匹に及ぶクモガニの交尾、ウミガメの孵化など、自然界で起きる奇跡的なシーンを多数収録。", "2010-01-22", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　MILLION　DOLLAR　HOTEL", "ミリオンダラー・ホテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "U2のボノによる原案を、彼と親交の深いW・ヴェンダース監督が映像化したラブ・ストーリー。ロサンゼルスのダウンタウンに建つミリオンダラー・ホテルでは、社会をはみ出した住人たちが勝手気ままな生活を営んでいた。軽い知的障害を持つ青年トムトムは、心を閉ざした女の子エロイーズに恋をする。そんなときトムトムの親友イジーが怪死した事件を追うFBI捜査官スキナーが現れ、ホテルの日常に波風が立ち始める……。当初は未来を舞台にしたSF大作として構想されたが、資金などの問題で設定を現代に変更。しかし完成した映画はそうした裏事情を感じさせないみずみずしい仕上がりで、リアリズムとファンタジー、喜劇と悲劇が混在する映像世界の中に、主人公トムトムのヒロインに対する“無償の愛”がロマンチシズム豊かに刻み込まれている。脊髄の病気に冒されたスキナーに扮したM・ギブソンの怪演も印象的。", "2000-01-01", "7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONTY PYTHON’S THE MEANING OF LIFE", "モンティ・パイソン 人生狂騒曲", "", "", "http://ecx.images-amazon.com/images/I/513AYN2G3CL._SL210_.jpg", 107, "“人生とは何ぞや”という深遠で大げさなテーマを掲げ、人間の誕生から死までの諸相をスケッチ風に綴っていく……。スーパー・ナンセンス・ギャグ集団“モンティ・パイソン”のブラックな魅力が盛りだくさん。相変わらずの意外な展開、バカげたギャグ、無意味に(？)豪華な映像などなど。アニメーションと特撮を担当するのは、T・ギリアム。", "1983-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Prince of Persia: The Sands of Time", "プリンス・オブ・ペルシャ　時間の砂", "", "", "http://image.eiga.k-img.com/images/movie/54515/315x210.jpg?2270", 115, "「パイレーツ・オブ・カリビアン」シリーズのプロデューサー、ジェリー・ブラッカイマーが、04年発売の同名TVゲームを実写映画化したアクション・アドベンチャー。13世紀末の中近東を舞台に、手に入れた者は数分だけ過去に戻って過ちを修正することができる「時間のダガー（短剣）」を巡って、ペルシアの王子ダスタンや高官ニザムらが争奪戦を繰り広げる。出演はジェイク・ギレンホール、ジェマ・アータートン、ベン・キングズレー。監督は「ハリー・ポッターと炎のゴブレット」「フェイク」のマイク・ニューウェル。", "2010-05-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CABLE GUY", "ケーブルガイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "不動産会社に勤める典型的なヤッピーのスティーブンは、引っ越してきたアパートでケーブルTVに加入。工事にやってきたケーブル設置工は妙になれなれしい態度だったが、お人好しのスティーブンは好意をむげにできない。しかし、それが彼の悲劇の始まりだった……。ケーブルガイにはスラングで迷惑な奴という意味がある。J・キャリーが人につきまとう不気味な男を、ハイテンションの大バカ演技で大熱演したナンセンス・コメディ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("光に叛く者", "光に叛く者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "孤独な青年ロバートは、ある晩、一人の娘を誘い、カフェで飲んでいた。その娘がある男に侮辱され、ロバートは思わず男を殴り倒すと、打ち所が悪く、男は死んでしまう。死んだ男は市の有力者の息子だった。ロバートは懲役10年の刑を受ける。そして6年、ロバートは刑務所生活に憔悴しきっていた……。マーティン・フラビン作の舞台劇の映画化。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VE´RITE´S ET MENSONGES／“F”", "オーソン・ウェルズのフェイク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "“フェイク”の天才ウェルズが、虚実を織り交ぜ、話術巧みにペテンと詐欺を物語った“贋物映画”の傑作。イビサ島には、二人の有名なフェイカーがいる。一人は、ピカソ、マティス、モディリアーニなど一流どころ専門の贋作画家ホーリー。かたや、そのホーリーの伝記『贋作』の著者アーヴィング。大富豪ハワード・ヒューズのニセ自伝を発表して話題となった男だ。インタビューがウソだったばかりか、その当時ヒューズそのものが生存していたかどうかも怪しい。圧巻は、ピカソに22枚の肖像画を描かせて持ち去った、美女オヤ・コダールのエピソード……。何が真実で何が嘘なのか、それを見きわめることよりも、そんな裏表の世界で戯れる楽しさを教えてくれる。", "1975-01-01", "2,7,15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BRIDE OF REーANIMATOR", "死霊のしたたり2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "コミカル・タッチのスプラッタ・ホラー映画「ZOMBIO　死霊のしたたり」の続編。ハーバートが起こした事件から一年。真相を知るハーバートが軍医として行ったペルーで、死者蘇生の薬を発見する。さらにハーバートは毎晩死体を盗み出し、縫い合わせて理想の女性を創ろうとしていた。ラストのスクリーミング・マッド・ジョージがその手腕を発揮したクリーチャーの大群が見どころ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bedevilled", "ビー・デビル", "", "", "http://image.eiga.k-img.com/images/movie/55875/315x210.jpg?2451", 115, "都会生活に疲れた銀行員のヘウォンは、子どもの頃に暮らした住民わずか9人の静かな島を訪れ、幼なじみのキム・ボンナムと再会。ボンナムは日常的に村の老人たちに重労働を強いられ、夫や男たちからも暴力を受けており、ヘウォンに助けを求めるが、ある日、恐ろしい惨劇が幕を開けてしまう。鬼才キム・ギドクのもとで助監督を務めたチャン・チョルス監督の長編デビュー作で、人間の絶望や憎しみを容赦なく描くバイオレンスサスペンス。", "2011-03-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Manifesto", "マニフェスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1920年代のヨーロッパの架空の町バルトハイムを舞台に、王を倒すためにやって来た美しい娘と、その周囲の人々が巻き起こす騒動を描いたブラック・コメディ。支配者となった王を倒すため、故郷に舞い戻ったスベトラーナ。だが、恋人でもあり同志でもある教師は王暗殺計画者として逮捕され、人体実験をするサナトリウム送りに。苦労して拳銃をサナトリウムに届けるスベトラーナだが……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Made in Heaven", "メイド・イン・ヘブン", "", "", "http://ecx.images-amazon.com/images/I/41SBD1853JL._SL210_.jpg", 103, "　命を落とし、天国へ行った青年マイクはそこでアニーという女性とめぐり合い、恋に落ちる。しかし、やがてアニーは地上へ降りることに。マイクは天国の管理人に頼み込み、アニーを追って地上へ戻る。しかし別人に生まれ変わったふたりにはかつて愛しあった記憶が無かった……。「スタンド・バイ・ミー」の脚本家コンビによるファンタジックな物語。洗練されたタッチで運命の恋を描く。ニール・ヤングやトム・ペティといったミュージシャンたちがゲスト出演している。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Whale Hunter", "鯨とり　ナドヤカンダ", "", "", "http://image.eiga.k-img.com/images/movie/43955/315x210.jpg?3124", 112, "内気でうだつのあがらない大学生ピョンテは、ひょんなことから知り合ったインテリ路上生活者の“親分”に連れられて売春宿に行く。そこで言葉を失った少女チュンジャに出会ったピョンテは、彼女を連れ出し故郷に送り届けようとする。韓国の国民的俳優アン・ソンギ主演で大ヒットを記録したロードムービー。日本では「鯨とり・コレサニャン」の邦題で1988年に劇場公開。2011年「韓国ニュー・ウェーブ、再発見」にてリバイバル上映。", "2011-07-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLER CROCODILE ２", "キラー・クロコダイル　怒りの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "巨大ワニの恐怖を描いた1988年の未公開作「キラー・クロコダイル」の続編。前作から二年後。かつて問題になった放射性廃棄物がどうなったのか島に取材にやってきた女性記者が、人喰いワニに襲われ行方不明に。彼女を襲ったのは、二年前に凄絶な死闘を繰り広げた、あのキラー・クロコダイルの子だった……。", "1990-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NIGHT AND THE MOMENT", "欲望の華", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“地上で最も魅惑的な作家”そして“愛の師”とも評される作家C・フィスの知的な官能小説を映像化した作品。貴婦人の別荘で繰り広げられる愛の駆け引きの中、退廃的な愛に溺れる男と女。最後に己の欲望を満たすのは果たしてどちらなのか？　R・オリンの妖艶な演技で、禁断のエロスというオトナのテーマに迫る作品。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("弥太郎笠・前後編", "弥太郎笠・前後編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 159, "子母沢寛の原作をマキノ雅広監督、鶴田浩二主演で映画化した任侠時代劇の大作。武士を嫌ってヤクザになった、りゃんこの弥太郎は渡世上、世話になった松井田宿の親分が悪玉ヤクザに殺されたのを旅先で知り、取って返し悪玉ヤクザをたたっ斬る。独立した鶴田浩二が主宰する新生プロと、新東宝の第１回提携作品。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moon", "月に囚われた男", "", "", "http://image.eiga.k-img.com/images/movie/55145/315x210.jpg?3555", 97, "デビッド・ボウイの息子ダンカン・ジョーンズの長編初監督作となるSF映画。「フロスト×ニクソン」のサム・ロックウェルが3役を演じ、ケビン・スペイシーが声のみで出演。燃料資源を使い果たした未来の地球。3年契約のもと、たった1人で月に赴任した宇宙飛行士のサムは、燃料を詰めたポッドを地球に送る単調な毎日を過ごす。愛する家族との再会を待ちわびながら、孤独な生活に耐えるサムだったが、ある日不注意から事故を起こしてしまう。診療室で目を覚ましたサムは、そこで自分と瓜二つの男に会う。", "2010-04-10", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WITHNAIL AND I", "ウィズネルと僕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1960年代の申し子のような俳優コンビ。貧乏な共同生活には何の進展もなく、かといってスターになる夢もあきらめきれない二人は、しばしの息抜きにと、とある別荘に出かけることにする。一つの時代の終幕と、二人の青春時代の終わりを、皮肉でしんらつなユーモアと微妙に重ね合わせて描くホロ苦いコメディ。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAUTIFUL GIRLS", "ビューティフル・ガールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "高校の卒業10周年の同窓会に出席するために、ニューヨークから故郷ナイツリッジに帰郷したピアニストのウィリーは、親友たちと再会する。恋人がいながら人妻と不倫関係にあるトミー、恋人にふられたばかりのポールと、みんな人生の帰路で悩みを抱え、ウィリーもまた才能に限界を感じ始めていた。そんなある日、ウィリーは13歳の美少女マーティと出会い、彼女に惹かれていくが……。20代後半の大人になり切れない男たちが人生の岐路に立たされ、傷つき迷いながら、大切なものを見つけていく青春群像劇。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPIDER　FOREST", "スパイダー・フォレスト／懺悔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "芸術性の高い映画作りに定評のある韓国人監督ソン・イルゴンが撮り上げたスリラー。不可解な事故により14日間の記憶を失った男が体験する恐怖を、サスペンスフルに映像化。映画の舞台となった森の異質な雰囲気にも圧倒される。交通事故で重傷を負い、脳の手術を受けたカン・ミン。彼は14日間の昏睡の後、目を覚ますとほとんどの記憶を失くしていた……。", "2005-04-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブレーキ", "ブレーキ", "", "", "http://ecx.images-amazon.com/images/I/51D5Wd%2BYWEL._SL210_.jpg", 80, "「リアル鬼ごっこ」などの人気作家・山田悠介の同名小説を、俳優としても知られる大鶴義丹監督が映画化したサバイバルアクション。整備工場で働く青年タカノブは、ある日突然何者かに拉致され、目が覚めると車の運転席に座らされていた。やがて車内のスピーカーから男の声が流れ、目の前に伸びる20キロの道路を時速100キロで走りぬけること、そしてブレーキを踏めば監禁された恋人の体に毒薬が流されることを告げる。", "2009-09-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mercury Man", "マーキュリーマン", "", "", "http://image.eiga.k-img.com/images/movie/53675/315x210.jpg?2729", 106, "凶悪テロリスト、ウサマ・アリの脱獄に巻き込まれた消防士のチャーンは、古代から伝わる秘宝“太陽の護符”の力を体内に注ぎ込まれ、灼熱の超人マーキュリーマンとして生まれ変わる。驚異のパワーを手に入れた彼は無差別殺人を企むウサマを止めるべく立ち上がるが……。最新VFXとムエタイ・アクションを融合させたタイ発の本格ヒーロー・アクション。「七人のマッハ!!!!!!!」のパンナー・リットグライがアクション監督を務める。", "2008-06-28", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Date with an Angel", "天使とデート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "空から落ちてきた天使が、人間の青年と恋をする、ファンタスティックでほんのり心温まるロマンティック・コメディ。フライドポテトが大好きで、ハイヒールを履けばコロリとこける天使、E・ベアールの天真爛漫な美しさが圧倒的。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ある探偵の憂鬱", "ある探偵の憂鬱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "ある初老の婦人が暮らすマンションの一室を、見張ってほしいとの依頼を受けた探偵は、監視を続けていくうちに、部屋に時折現れる若い女性に心惹かれていく。やがて、依頼人から「女を殺してくれ」という手紙が届いた……。北野武や原田眞人作品の助監督を務めていた矢城潤一が、自主製作して完成させた幻想的かつミステリアスなサスペンス映画。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEVIL'S ANGELS", "デビルズ・エンジェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "バイク族の聖域“壁の穴”に向かい、町から町へ旅を続けるザ・スカルズに対し、市民たちは町の自衛のために立ち上がる……。彼らの緊張と闘争をアクション場面の連続で見せるなか、新しい生き方を模索するリーダーの姿を浮き彫りにした作品。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH−FREQUENCY", "デッド・チャンネル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「生きてこそ」のV・スパーノ主演による異色のサスペンス。アルプスの高地にある衛星通信の地上中継局に勤務しているピーター。彼は大西洋の向こうに住んでいる少年・デニーとのアマチュア無線を楽しんでいた。ある日、中継局の電波が殺人事件の現場を捉えた。ピーターとデニーはこの事件の謎の究明に乗り出すが……。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東緋桜一家", "関東緋桜一家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "明治末期、美貌と男まさりの侠気で人気を呼んでいる柳橋の鉄火芸者が、柳橋に賭場を開いて縄張りを拡張しようと悪らつな手段を講じる悪徳ヤクザ一家を、仲間たちの協力を得て打ち倒す。この作品は藤純子の90本目の作品で、彼女のデビュー作「八州遊侠伝 男の盃」（1963）を監督し、女優・藤純子の育ての親ともいうべきマキノ雅広弘監督がメガホンを取っている。マキノ雅広は見せ場を心得たソツのない仕事ぶりで観客をタップリ楽しませて、興行的に大ヒットを記録、藤純子に引退の花道を飾らせた。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青い海原", "青い海原", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "美空ひばりと春日八郎がヒットソングを歌いまくる青春歌謡映画。ひばりと高倉健の顔合わせも見ものだ。同僚の遺言を胸に秘め、陸に上がった船乗りの健次。やがて同僚の娘・はるみが働く港の酒場を訪ねた彼は、港のボスの計略から彼女を守ろうとする。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FULL MOON HIGH", "ハイ・スクール・ウルフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "アメリカン・フットボールの花形選手としてハイスクールで人気のトニー。彼は森の中でモンスターに襲われて以来、体に変調をきたし、狼男になってしまう。行方をくらました彼は、20年後にトニーの息子と名乗り、昔の若さのまま現れる……。当時、流行した狼男ものの一編。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROOM AT THE TOP", "年上の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "イギリス北部の地方都市。青年ジョーは、上流社会の仲間入りを果たそうと野心に燃え街の有力者の娘に接近する。それがかなわぬと、不幸な結婚に悩む人妻へ……。失意の青年と年上の女との恋とその破局を、冴えたタッチで描く力作。", "1959-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MARIE´E E´TAIT EN NOIR", "黒衣の花嫁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "コートダジュールで独身生活を楽しんでいた男、その近くの町の銀行員、若手政治家……と連続殺人が起こる。その現場にはいつも謎の女性がいた。彼女の目的は何か……。トリュフォーによるサスペンス・ミステリーで、連続殺人犯に扮するJ・モローの冷たくさめた演技が作品に妖艶な凄みを加えている。", "1968-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MON PE｀RE CE HE´ROS", "さよならモンペール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "父とともに冬のバカンスを楽しむ14歳のベロは、ちょっと背伸びして、ハンサムな高校生と恋に落ちる。大人として見られたい一心で彼女は父のことを愛人だと嘘をつくが……。ヒロインに扮したM・ジランのフレッシュな魅力がはじける青春映画。G・ドパルデューが父親役を好演。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘録・長崎おんな牢", "秘録・長崎おんな牢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "国原俊明監督の「おんな牢秘図」に続く“おんな牢”もので、太田昭和の監督第１作。長崎のおんな牢を舞台に、無実の罪で投獄された混血娘・おみつを襲う様々な苦難が、ポルノ・タッチで描かれる。大映最後のスター・川崎あかねが、おみつに扮して熱演。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブラックスクール　裏黒 RIGURO", "ブラックスクール　裏黒 RIGURO", "", "", "http://image.eiga.k-img.com/images/movie/56565/315x210.jpg?4567", 88, "幼いころから暗殺者として鍛え上げられた6人の高校生たちは「イレイザー」と呼ばれ、失敗すれば自分たちが消されるというナゾの組織に監視されながら指令をこなしていた。一方、不可解な連続殺人事件の捜査に乗り出した警察は、殺人現場に高校生がいたという情報をつかむ。捜査は大詰めを迎えたが、そんな時、警察内部にスパイがいるという噂が立ちはじめ……。「魁!!男塾」や「ハイキック・ガール！」でアクション俳優としても活動する佳本周也監督が、高校生の殺し屋たちの戦いを描く。", "2011-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("絞殺", "絞殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "有名高校に通う息子が突然暴力をふるい始め、やがて家族が命の危険を感じるほどまでにエスカレートしたため、父親はやむなく息子を絞殺する。実際に起こった事件をもとに、いわゆる“家庭内暴力”を扱った問題作。単なる時事映画に終わらず一つの家庭が完全に崩壊していく様子を克明に描いている。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MIRACLE WORKER", "奇跡の人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "生後19ヵ月の時にひどい熱病を患い、目と耳と口が不自由な三重苦となったが、それを克服して社会福祉に献身した偉人ヘレン・ケラー。その少女時代、自らも盲目の不屈の女性教師サリヴァンが彼女に指文字で言葉を教え、新しい世界を開かせるまでを描いた、感動のヒューマン・ドラマ。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京ギャング対香港ギャング", "東京ギャング対香港ギャング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "麻薬取り引きをめぐり、東京ギャングと香港ギャングが香港～東京を股にかけて死闘を繰り広げていく。東映初の香港ロケ作品で、徹底した盗み撮りで香港の街を活写した画面は思いのほかに迫力がある。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLD FEVER", "コールド・フィーバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ジム・ジャームッシュなどを世に送り出したプロデューサー、J・スタークが、「ミステリー・トレイン」に続いて再び永瀬正敏を起用したファンタスティック・ロード・ムービー。監督は「春にして君を想う」で世界に知られたF・T・フリドリクソン。東京の水産会社に勤める平田アツシは、楽しみにしていたハワイでのバカンスを祖父の説得であきらめ、両親を供養する旅に出かける。彼の両親はアイスランドの人里離れた渓谷で、事故に遭い死んだのだ。極寒の地で平田を待ち受けていたのは、災難続きの横断旅行。しかしそこでの出来事や出会った人々は、彼の中の何かを確実に変えていく。", "1995-01-01", "77,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神偸諜影／DOWNTOWN　TORPEDOES", "ダウンタウン・シャドー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "金城武をはじめ、香港の若手スターが結集したスパイ・アクション。世界中を飛び回る国際スパイ・コンビ、ジャッカルとキャッシュが、香港公安局情報部に巨大組織の重要機密の強奪を依頼される。半ば強制的に仕事を押しつけられた二人は、政府から派遣された女スパイとコンピュータのプロとともに組織に潜入。間一髪で機密を手に入れた彼らだが、恐るべきワナが待ち受けていた……。ハイテクを駆使した潜入シーンの緊張感や、小気味良いアクションの連続で観客を魅了する痛快作。金城とチャン・シウチョンのユーモラスなセリフのやりとりも軽妙。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A", "A", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "1994年６月の松本サリン事件、1995年３月の地下鉄サリン事件などで世間を震撼させたオウム真理教問題を、一連のマスコミ報道とはまったく異なる角度から検証したドキュメンタリー。といってもオウムの犯罪そのものを追及する作品ではなく、教団の広報部副部長、荒木浩の多忙な日常業務を追いかける形で進行する本作は、強引な取材攻勢をかけるマスコミなどの周辺状況を克明に記録。若きオウム信者へのインタビューも収録され、きわめて異例の“密着取材”が行われただけでも驚異的だが、あくまで中立的な視点を保とうとするカメラは、オウムの背後に存在する社会の闇をおぼろげながら浮き上がらせていく。監督は、オウム問題に対する姿勢の違いから制作会社を退社し、フリーディレクターとなった森達也。のちに海外向けに再編集を施した“インターナショナル版”も公開された。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIQUID SKY", "リキッド・スカイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "マンハッタンに現れたUFO。これがなんと、ヘロインやセックスでオルガスムスに達した時に脳内に発生するエネルギーを吸いとってしまう“スペースヴァンパイア”もどきの吸精鬼。もっとも、話の中心となるのは吸われる側のファッション・モデルやミュージシャンなどのパンクな連中だ。非常に中性的な（主演のカーライルは男性にも扮している）人々の、メタリックな世紀末的退廃ぶりが、ソラリゼーションのような実験的映像のうちに展開していく。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハーツ・アンド・マインズ　ベトナム戦争の真実", "ハーツ・アンド・マインズ　ベトナム戦争の真実", "", "", "http://image.eiga.k-img.com/images/movie/55465/315x210.jpg?2057", 112, "「地獄の黙示録」「ディア・ハンター」といったベトナム戦争映画に多大な影響を与えた傑作ドキュメンタリー。エゴイスティックな米政治家、元米兵、ベトナム難民など戦争を体験した様々な層の人間からの証言を積み重ね、無意味な戦争の真実を浮かび上がらせていく。監督は、米TV各局で数々のドキュメンタリーを手掛けてきたピーター・デイビス。1975年のアカデミー賞で長編ドキュメンタリー賞を受賞。", "2010-06-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARADIES", "パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "お嬢様育ちのアンゲリカは夫との関係に倦怠感を抱き、二人の間に刺激を加えようと幼なじみのロッテを夫に紹介する。即席の三角関係を作ろうというのだ。ブスのロッテが相手なら夫も本気になることはないはずだったが……。笑うに笑えぬ毒を含んだ“昼メロ・パロディー”ともいえる異色コメディ。", "1986-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イエローキッド", "イエローキッド", "", "", "http://image.eiga.k-img.com/images/movie/55075/315x210.jpg?4757", 106, "国内外で多数の映画賞を受賞してきたインディーズ映画界の雄、真利子哲也監督が、製作費200万円で作り上げた長編劇場映画デビュー作。都会の片隅で、痴呆を患った祖母とともに夢も希望もない生活を送るボクサー志望の青年・田村は、自分が通うボクシングジムで、気鋭の漫画家・服部と出会う。服部は新作「イエローキッド」のモデルを田村に決めて描き始めるが、やがて、服部の描く漫画の世界と田村が生きる現実世界が交錯し始める……。", "2010-01-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋のカウントダウン", "恋のカウントダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "香港に遊びに来ていた日本の社長令嬢は、街のディスコでユニークなバンドマンたちと知り合う。一方、日本ではその令嬢の父親が死亡、４日以内に令嬢がサインしないと会社は悪どい副社長のものになってしまうことに。副社長は殺し屋を雇い、令嬢を監禁するため香港に向かわせるのだが……。公開当時、日本と香港の２大アイドル歌手共演が話題となった青春歌謡映画。日本と香港の合作作品。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NANA2", "NANA2", "", "", "http://image.eiga.k-img.com/images/movie/33765/315x210.jpg?3410", 130, "大人気コミックを実写映画化しメガヒットを記録した「NANA」の続編。大崎ナナと“ハチ”こと小松奈々の出会いや同居生活を描いた前作に続き、ナナのバンドのデビューやハチの妊娠など、さまざまな試練を乗り越えながら成長していくふたりのNANAの姿を描き出す。ハチ役が宮崎あおいから「サイレン」の市川由衣に、ナナの恋人レン役が松田龍平からNHKドラマ「わかば」の姜暢雄に変更するなど、一部キャストをリニューアル。", "2006-12-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天上人とアクト人　最後の戦い", "天上人とアクト人　最後の戦い", "", "", "http://image.eiga.k-img.com/images/movie/54575/315x210.jpg?3626", 0, "現代世界より遥か未来に存在する天上世界で、日高ユメミはいつからかその世界を「見る力」を持つようになっていた。魔導国の王ムントと共に、 万能の力“アクト”が尽きつつある天上界へと向かったユメミは、魔導国民の歓迎を受けるも、天上界の危機はすぐそこに迫っていた。「涼宮ハルヒの憂鬱」などのテレビアニメを手がける京都アニメーションが、オリジナルで製作したアニメ「MUNTO ムント」の劇場版。", "2009-04-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パーティ＆party TWILIGHT FILE IX", "パーティ＆party TWILIGHT FILE IX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "オムニバスシリーズ「TWILIGHT FILE」第9弾。人気アイドルグループ「AKB48」の内田眞由美が映画初出演。", "2012-11-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("河童", "河童", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "報道カメラマン、雄太は、15年ぶりに再会した息子に宙に浮く奇妙なボールを見せる。驚く息子に雄太は、“尻子玉”と呼ばれるそのボールにまつわる話を語り始めた。“俺は子供の頃、河童を見た……”。元・米米CLUBのカールスモーキー石井が石井竜也の名で監督デビューしたヒューマン・ファンタジー。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カリーナの林檎　チェルノブイリの森", "カリーナの林檎　チェルノブイリの森", "", "", "http://image.eiga.k-img.com/images/movie/56925/315x210.jpg?4285", 109, "チェルノブイリ原発事故がもたらした悲劇を描く劇映画。「少女カリーナに捧ぐ」として2004年に一度完成するも公開の目処がたたずにいた作品を再編集し、「カリーナの林檎　チェルノブイリの森」として再度完成。福島第一原子力発電所の事故が起こった2011年に劇場公開が実現。チェルノブイリ原発のあるウクライナの隣国ベラルーシに暮らす少女カリーナは、母が入院し、父はロシアに出稼ぎにいったため親戚の家に預けられる。都会の生活になじめないカリーナは、原発周辺の居住禁止区域に近い田舎の村で暮らす祖母の家へ思いをはせるが、やがて祖母も病気になり、母の症状も悪化。そしてついに自身も病に倒れてしまう……。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緋牡丹博徒・一宿一飯", "緋牡丹博徒・一宿一飯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "シリーズの生みの親ともいうべき鈴木則文が監督したシリーズ第２作。恩義ある戸ヶ崎一家が、農民を困らせる笠松一家のために全滅したと聞いたお竜は、戸ヶ崎一家・二代目を助けるべく一路四国から上州へ。悪らつな笠松一家に殴り込みをかける。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プラチナデータ", "プラチナデータ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "二宮和也（嵐）と豊川悦司の共演で、東野圭吾の同名小説を映画化したサスペンス大作。政府が水面下で収集した国民のDNAデータ「プラチナデータ」をもとに犯罪捜査が行われ、検挙率が大幅に上昇した2017年。科学者の神楽は、DNA捜査の専門家として警察庁の特殊捜査機関「特殊解析研究所」に所属していた。そんなある日、DNA捜査のシステム開発者の殺害事件が発生。現場から神楽のDNAデータが検出される。身に覚えのない神楽は逃亡し、ベテラン刑事の浅間が神楽を追跡するが……。自信家の天才科学者・神楽を二宮、DNA捜査に疑問を抱き昔ながらの捜査にこだわる浅間刑事を豊川が演じる。監督は「ハゲタカ」の大友啓史。", "2013-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE INCREDIBLE JOURNEY", "三匹荒野を行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "カナダの女流作家のベストセラーを映画化したディズニー・プロお得意の動物映画。犬二匹（ルーアとボジャー）とシャム猫一匹（テーオ）が主人の家から320キロも離れた家に預けられるが、カナダの山あり川ありの荒野を冒険しながら、わが家に帰り着くまでを描く。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Welcome to L.A.", "ロサンゼルスそれぞれの愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "　作曲家のキャロルはヨーロッパから富豪の父親カールが住むロサンゼルスに帰ってくる。キャロルは様々な女性たちと関係を持つが……。ロサンゼルスを舞台に、様々な男女の愛のもつれと孤独を綴る群像劇。アラン・ルドルフが監督を、そして彼がスタッフとして参加した「ナッシュビル」「ビッグ・アメリカン」のロバート・アルトマンが製作を務めた。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("純情", "純情", "", "", "http://image.eiga.k-img.com/images/movie/55595/315x210.jpg?2799", 78, "富士山ひょうたの同名コミックを、「仮面ライダー響鬼」の栩原楽人と「タクミくんシリーズ　虹色の硝子」の高橋優太主演で映画化したボーイズ・ラブ映画。ライターの戸崎は、取材先で高校時代の初恋相手・倉田と思わぬ再会を果たす。その晩、戸崎は戸惑いながらも倉田に抱かれてしまう。2人の関係はその後も続き、強引かと思えば優しい態度を見せる倉田に、戸崎はますますひかれていく。監督は「愛の言霊」の金田敬。", "2010-09-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fur: An Imaginary Portrait of Diane Arbus", "毛皮のエロス／ダイアン・アーバス　幻想のポートレイト", "", "", "http://image.eiga.k-img.com/images/movie/33985/315x210.jpg?1332", 122, "オスカー女優ニコール・キッドマンが伝説の女性写真家ダイアン・アーバスに扮したエロティック・ドラマ。過激な題材や独創的な写真技術で現代写真におけるポートレイト写真の意味を変革したと言われるダイアン・アーバスをモデルに、内気で従順な妻が個性あふれる芸術家へと転身を遂げる様子を、虚実入り混ぜながら描き出す。監督は前作「セクレタリー」で高い評価を受けた奇才スティーブン・シャインバーグ。", "2007-05-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAASHHA", "バーシャ！踊る夕陽のビッグボス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 152, "日本でも「ムトゥ 踊るマハラジャ」などで知られるインド映画のスーパースター、ラジニカーント主演のエンターテインメント・ムービー。マドラスでオートリクシャーの運転手をしているマニカムだが、その正体はインドの武闘派ファミリーのビッグボス、バーシャ。訳あって兄弟分を引き連れ、足を洗ってしがない商売をしているのだ。だが、巨大マフィアのボスの度重なる悪事についに怒り爆発。今一度バーシャに戻って大暴れする。", "1994-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("網走番外地・北海編", "網走番外地・北海編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "再び北海道へ戻っての作品。仲間の保釈金を得るためにトラック運送をかって出た橘真一は、積荷が阿片だということを知り、すきをみて麻薬団を叩きつぶす。名作「駅馬車」をベースに、１台のトラックに舞台を限定した作品。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー×仮面ライダー　オーズ＆ダブル feat. スカル MOVIE大戦CORE（コア） ", "仮面ライダー×仮面ライダー　オーズ＆ダブル feat. スカル MOVIE大戦CORE（コア） ", "", "", "http://image.eiga.k-img.com/images/movie/55775/315x210.jpg?1807", 0, "平成仮面ライダーシリーズ第11弾「仮面ライダーW」＆第12弾「仮面ライダーオーズ　000」の劇場版。吉川晃司扮する仮面ライダースカルの誕生秘話が明かされる「仮面ライダースカル　メッセージforダブル」、織田信長のミイラが人造人間となって蘇る「仮面ライダーオーズ　ノブナガの欲望」、オーズ＆Wが過去の仮面ライダーたちの記憶を利用した巨大な敵・仮面ライダーコアと激闘を繰り広げる「MOVIE大戦CORE」の3話で構成される。", "2010-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幽霊vs宇宙人", "幽霊vs宇宙人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「呪怨」の清水崇監督と、ホラー短編TVシリーズ「怪談新耳袋」などで活躍中の豊島圭介監督が、“幽霊”と“宇宙人”をテーマに撮った４作品。人一倍怖がりの高校生がTVの怪奇番組を見てしまい、悪夢のような一夜を過ごす「怪談／こっちを見ないで…」、宇宙人に身体を乗っ取られた一家のトラブルを描く「食卓の宇宙人」など、不条理な恐怖と笑いたっぷりの作品集。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金融腐蝕列島・呪縛", "金融腐蝕列島・呪縛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "高杉良のベストセラー小説をもとにした経済ドラマ。危機的状況に陥った大手都市銀行の裏側に迫る痛快作だ。朝日中央銀行の300億円にものぼる不正融資疑惑が浮上。だが、実権を握る元会長の佐々木相談役を始め、上層部には全く危機感がなく、責任を回避しようとするばかり。その姿勢に怒りを覚えた企画本部長の北野らミドル４人組は内部の改革に立ち上がるが……。腐敗した銀行の再生に挑む主人公に役所広司、彼と対立する義父でもある相談役に仲代達矢が扮し、激論を交わすシーンで鬼気迫る演技を披露。クライマックスの株主総会の場面など、混乱した業界の実態を躍動感あふれるカメラワークで描出し、奮闘する男たちの熱意をリアルに伝える。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("３つの雲", "３つの雲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 13, "世界的に見ても珍しい木炭画を駆使したアニメーションの作り手、辻直之監督による短編集。“雲”をモチーフに、人間、自然、社会を考察した３つの幻想的なエピソードが展開していく。ワンカットに１枚の紙を使い、絵を描き足しながらコマ撮りを行ったという独特の質感の映像美に注目。男子中学生がノートにスケッチしていた雲が突然動き出し、生徒たちを変容させていく「雲を見ていたら」など、３つの短編で構成。", "2005-08-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Proposition", "プロポジション　血の誓約", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "19世紀、開拓時代のオーストラリア。先住民アボリジニと開拓移民が血で血を洗う争いを繰り広げる最中、悪名高いバーンズ兄弟の次男チャーリーと三男マイクが警官に捕らえられる。そこで、チャーリーは警官から「マイクの死刑を免れたいなら、長兄アーサーの首を引き渡せ」と迫られる。チャーリーは葛藤しながらも現在は疎遠になっているアーサーを捜す旅に出るが……。出演は主人公チャーリーにガイ・ピアースのほか、レイ・ウィンストン、エミリー・ワトソン。脚本・音楽にニック・ケイブ。", "2005-01-01", "11,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("∀ガンダムI 地球光", "∀ガンダムI 地球光", "", "", "http://ecx.images-amazon.com/images/I/51MYGTP2DKL._SL210_.jpg", 128, "ガンダム20周年を記念し、シリーズの生みの親である富野由悠季が新たに手がけたTVアニメ「∀（ターンエー）ガンダム」（1999～2000）の総集編2部作の前編。かつて荒廃した地球から月に移住した人類の末えい＝ムーンレィスは、地球への帰還を望むが、地球に住む人々との軋轢（あつれき）から戦争に発展。ムーンレィスの少年ロラン・セアックは、地球側のモビルスーツ、∀ガンダムのパイロットとなり、ムーンレィスを相手に戦うこととなるが。", "2002-02-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ALBERO DEGLI ZOCCOLI", "木靴の樹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 187, "「偽りの晩餐」「聖なる酔っぱらいの伝説」のE・オルミの名を世界的に広めた傑作。舞台は19世紀末、北イタリアの農村。小作人として働くバティスティ一家の息子ミネクは弟のために、川のほとりに並ぶポプラの木を一本切って木靴を作った。ところが、そのポプラは地主の所有物。これが原因で、一家は農場を追われてしまう。エピソードを連ねてじっくりと描かれる、貧しい農夫たちの人間模様。ネオ・リアリスモの継承者と呼ばれたオルミの、素朴ながら重厚なタッチが冴える。今にも死にそうな牛の世話をする農夫の祈りが実って、奇跡的に牛が回復するエピソードは感動的。", "1978-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・GONIN", "新・GONIN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "石井隆の「GONIN」「GONIN２」とは無関係の群像ヤクザ映画。大阪で勃発した二大暴力組織の抗争が、香港に飛び火。抗争の火種となった組員と、ままならぬ状況から脱会した若頭補佐が結託し、共通の敵を打破するために激烈な死闘を繰り広げる。宅間伸が珍しく若頭補佐というヤクザ役に挑戦。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAMILLE CLAUDEL", "カミーユ・クローデル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "19世紀末のパリ。女性が社会的に認められていないこの時期に、自由な人生を求めて彫刻家ロダンに師事する若い女性カミーユ。彼女も彫刻家であったが、世間からロダンの愛人のモデルとしか見られないことに屈辱感を覚えていく……。原作に惚れ込んで映画化権を獲得、主演したアジャーニの意欲作。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mama's Boy", "ママ男", "", "", "http://ecx.images-amazon.com/images/I/51swmKEOVoL._SL210_.jpg", 0, "", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博士の愛した数式", "博士の愛した数式", "", "", "http://image.eiga.k-img.com/images/movie/1415/315x210.jpg?1694", 117, "「阿弥陀堂だより」「雨あがる」の小泉堯史監督、寺尾聰主演で小川洋子の同名小説を映画化。家政婦として働くシングル・マザーの杏子が、今度お世話をすることになったのは、ケンブリッジ大学で数学を学んでいたが、交通事故の後遺症で記憶が80分しかもたなくなってしまった老博士。杏子とその息子は博士の人柄と、彼の語る数式の美しさに魅了され、3人は次第にうち解けていくが、やがて博士の痛ましい過去が明らかになっていく。", "2006-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Elle s'appelle Sabine", "彼女の名はサビーヌ", "", "", "http://image.eiga.k-img.com/images/movie/54155/315x210.jpg?3410", 85, "「冬の旅」「仕立て屋の恋」の主演女優サンドリーヌ・ボネールが、25年の歳月をかけて自閉症の妹を撮影した初監督作品のドキュメンタリー。11人兄弟のサンドリーヌには、陽気で芸術的才能の豊かな1歳年下の妹サビーヌがいた。自閉症であることで馬鹿にされたり、姉妹兄弟が自立して家を出ていくと、サビーヌの孤立感は増していき、28歳時には精神病院に入院することになる。5年後、サビーヌは変わり果てた姿で退院する。", "2009-02-14", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEREMY", "ジェレミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ニューヨークの芸術学校を舞台に、チェロ奏者を目指す15歳の少年ジェレミーとバレリーナを目指す16歳のスーザンとの初恋を描いた小品。深く愛し合うようになった二人だが、やがてスーザンの父は仕事の都合でデトロイトに呼び戻されることになり……。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神々の履歴書", "神々の履歴書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "常に民衆の立場から民衆のもつエネルギーを描いてきた寡作な作家・前田憲二が、20年の構想の末、完成させた長編ドキュメンタリー。日本の古代遺跡と朝鮮半島に残る古墳の類似性を指摘して、日本文化の源が朝鮮にあるのではないかと問いかける。国文学者の萱沼紀子ら多くの文化人が製作に協力した、雄大なスケールの異色作。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The War Lover", "戦う翼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "第二次大戦中の1943年、英国にあるアメリカ第８空軍基地。リクソン大尉を機長とする通称“女体号”はこの出撃が終われば帰国できるはずだった。しかし、殺りくと破壊の戦争に生き甲斐を感じているリクソンと良識派の副操縦士ボーランド中尉が対立、そうこうするうち、機は敵弾を浴びる。J・ハーシーの『戦争を愛する者』を原作にした戦争もの。DVDは「コロムビアトライスター ウォー・ムービー・コレクションBOX(1)」に収録。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海を渡る波止場の風", "海を渡る波止場の風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "宍戸錠が前作の悪役からライバル役へと昇格した。大金を積んだセスナとともに消えた弟を探して、鹿児島に現れた浩次。彼は弟の婚約者と一緒に背後にいる敵の正体を暴くが……。“渡り鳥”シリーズに比べると話は暗いが、錠のコミカルな金庫破りは旭との掛け合いとともに一見の価値あり。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("将軍と参謀と兵", "将軍と参謀と兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1941年５月の山西省の大包囲殲滅作戦を扱い、苦難を乗り越えて黄河河岸に進出する日本軍の姿を勇ましく描いた国策映画。作戦をめぐる参謀たちの激論など、指令部の内部を描いた当時としては異色の戦争映画。カール・リッター監督のドイツ映画「最後の一兵まで」の影響がうかがえる。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天旋地恋", "スイート・ムーンライト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "東京を舞台に、香港からやってきた若い男女二組のカップルの失恋や新しい恋をハートフルに描いた青春ラブ・ストーリー。メールで恋人のジューンが病気だと知ったケイは、彼女のいる日本へ。ところが、このメールが送られたのは半年も前のことだったため、時すでに遅く、ジューンは看病してくれたケイの友人のサムとすでにいいムードだった。落ち込むケイを励ましたのは、日本の芸能界を目指しているキキだった……。香港若手スターたちが喋るたどたどしい日本語が何ともキュート。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST BOYSCOUT", "ラスト・ボーイスカウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "タフガイ、B・ウィリスが私立探偵に扮したバイオレンス・アクション。大人気のプロ・フットボール界の裏でうごめく黒幕たちの陰謀。それが、かつての自分を負け犬へと追いやった事件につながっていると知った私立探偵ジョーが、元フットボール選手の協力を得て、陰謀の阻止に命を懸ける。その相棒役に扮するのは、TV界で活躍する人気コメディアン、D・ウェイアンズ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GAMES OF DESIRE", "O夫人の背徳４", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "人妻の不倫と覗き趣味を描いていくシリーズの一編。今回は文豪モーパッサンの発禁小説の映画化で、イタリアン・エロスの巨匠といわれるパスカル・フェネッティの演出はポルノ顔負けのすごさである。大文豪の後妻となった若妻O夫人は、夫が性的に不能なため若い肉体を抑えられず、毎夜、街に出ては情事を繰り返し……。", "1990-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("つむじ風", "つむじ風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "東へ走ってはホラを吹き、西へ行っては女を口説く一見田舎青年の陣内陣太郎を主人公にした爆笑喜劇。風のように現れ風のように去っていく陣太郎をめぐって、様々な人間模様が展開される。のちの“寅さん”の原型のような作品でもある。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARQUISE", "女優マルキーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "17世紀のフランスを舞台に、実在した舞台女優マルキーズ・デュ・パルクの人生を描く人間ドラマ。踊り子から劇団の女優となり、国王ルイ14世や新進劇作家ラシーヌの心を捉えたマルキーズ。ラシーヌの劇団で花形女優として成功するものの、そんな彼女を思わぬ悲劇が待ち受けていた……。S・マルソーが、女優の情熱とプライドを力強く体現。", "1997-01-01", "2,4,26,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森の向う側", "森の向う側", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "ベストセラー作家・村上春樹の短編を忠実に映画化。海辺のリゾートホテルを舞台に、男と女の心のふれあいを乾いた演出で描く野村惠一監督のデビュー作。抑制の効いたセリフ回し、落ちついた音楽と美術など、原作のイメージを壊さない配慮が行き届いた大人のドラマ。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("自虐の詩", "自虐の詩", "", "", "http://image.eiga.k-img.com/images/movie/34215/315x210.jpg?1575", 115, "無職で大酒飲みで乱暴者の夫イサオと、大阪の下町で貧乏生活を送る幸恵。気に入らないことがあれば、すぐにちゃぶ台をひっくり返し、肝心なことは何も話さず、いつもだんまりなイサオだが、それでも彼と一緒なら幸恵は幸せだったが……。業田良家の人気4コマ漫画「自虐の詩」を、「トリック」の堤幸彦が映画化。「嫌われ松子の一生」の中谷美紀が再び薄幸のヒロインを演じ、パンチパーマの乱暴夫イサオを阿部寛が演じる。", "2007-10-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CORBARI", "特攻大戦線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "第二次大戦末期のイタリア戦線で、強大なファシスト軍と戦うパルチザンの若きリーダー、コルバリ。社会改革をも強行しようとする彼は、有産階級の反感を買い、次第に窮地に立たされていった……。G・ジェンマが異色な役柄を演じる一編。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kauas pilvet karkaavat", "浮き雲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "電車の運転手ラウリとその妻でレストランの給仕長イロナは、不況のあおりを受けて同時期に失業してしまう。2人は職探しを始めるが、なかなかうまくいかず……。フィンランドのアキ・カウリスマキ監督が、皮肉をちりばめた独特のタッチで描く。出演者にはカティ・オウティネンやカリ・バーナネンら、カウリスマキ作品の常連が勢揃い。また本作の撮影前にこの世を去ったマッティ・ペロンパーも、写真というかたちで特別出演している。", "1996-01-01", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COURAGE UNDER FIRE", "戦火の勇気", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "湾岸戦争のさなか、誤って味方の戦車を砲撃し、親友のボイヤー大尉を死に追いやってしまったサーリング大佐。ワシントンに戻った彼は、軍の勲章やセレモニーを扱う部署で事務職を命じられる。親友を殺した罪の意識に苦しみながら、史上初の女性名誉勲章の候補者で、救助隊の大尉だったカレン・ウォールデンの殉死経緯の調査を開始する。だが、部下たちの証言はことごとく食い違い、カレンの死の真相は謎を深めていくのだった。ハリウッド初の湾岸戦争を題材にした人間ドラマ。戦場での悲劇のシーンを交えながら、サスペンス・タッチで陰謀の真相に迫っていく。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЦАРЕВНА—ЛЯГУШКА／FROG—PRINCESS", "蛙になったお姫さま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "ソビエト・アニメの草分け的存在M・ツェハノフスキーの代表作。日本でもなじみの深いロシア民話をもとに、魔法使いによって醜い蛙に姿を変えられてしまった美しい姫と、彼女の元に矢を放ち、いやいやながら結婚することになった若き王子の恋物語が展開していく。幻想的な色彩美にあふれた映像が魅力的。", "1954-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A SMILE LIKE YOURS", "スマイル・ライク・ユアーズ　緊急！子づくり宣言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "なかなか子供に恵まれない若夫婦が、子作りに悪戦苦闘するコメディ。ジェニファーとダニーは、周囲もうらやむほどの仲良し夫婦。彼らの悩みは赤ちゃんができないこと。アロマテラピーの店を経営するジェニファーは媚薬の香水を開発したり、ムード満点のディナーを演出するなどして子作りに励むが効果はナシ。そんなある日、ダニーと美人上司の浮気疑惑が浮上し……。妻の母親役でシャーリー・マクレーンがゲスト出演している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("БЛОКАЛА", "レニングラード攻防戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 184, "1941～45年にかけて戦史に名高いレニングラード攻防戦の900日を、２部に分けて描いた戦争映画。第１部はドイツ軍が国境を越えてなだれ込んできた1941年末までを労働者イワンの生活を中心に、第２部はジューコフ将軍の活躍を中心に描いている。70ミリの大作。", "1974-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("見鬼10／THE EYE−INFINITY", "the EYE３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "幽霊が見えるようになった人間の恐怖の体験を描いた、アジアン・ホラーのシリーズ第3作。ゴーストに遭遇しようとした若者たちに降りかかる災難を、ブラックユーモアと共に描く。「藍色夏恋…」のチェン・ボーリンが若者のひとりを熱演。遊び心から、幽霊が見えるとされる10の方法を試そうとした5人の若者たち。だが、そのうちのひとりが消息を絶ってしまった。残された4人も次第に霊の影に脅かされるようになり……。", "2006-04-29", "6,23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUN CRAZY Episode４／ 用心棒の鎮魂歌〈レクイエム〉", "GUN CRAZY Episode４／ 用心棒の鎮魂歌〈レクイエム〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "「バトルロワイアル２」の加藤夏希が主演するハード・アクション。東南アジアの某国。日系企業が開いたパーティ会場にゲリラ軍が乱入、支社長の娘・恵理が誘拐されてしまった。日本でこのニュースを知った親友の美紀は、恵理を救出しようと敵のアジトに乗り込む決意をする。計画を知った傭兵や元自衛官らが協力しようと美紀の元に集結、彼らは現地へと向かう。DVDは加藤夏希、原史奈の２バージョンが発売されている。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間の條件 第１部・純愛篇／第２部・激怒篇", "人間の條件 第１部・純愛篇／第２部・激怒篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 203, "愛する妻とともに、決して楽ではないが幸せな日々を送っていた梶。だが、そんな彼らに戦争の影が迫っていた。ある日、梶は召集令状を受け取る……。シリーズ導入部のここでは夫婦の像がメインに押し出され、続く悲劇とのあざやかなコントラストを成す。DVDは「人間の條件DVD-BOX 全６巻」に収録。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monga", "モンガに散る", "", "", "http://image.eiga.k-img.com/images/movie/55735/315x210.jpg?3665", 141, "1980年代の台北・モンガを舞台に、裏社会に足を踏み入れた若者たちのきずなと運命を描いた人間ドラマ。繁華街の裏側でいくつもの組織が抗争を繰り広げるモンガの街。高校生のモスキートは、街で最高の権力を握るヨウカウ組親分の息子・ドラゴン率いる不良グループと仲良くなり、彼らとともにケンカに明け暮れる日々を過ごしていた。やがて、彼らは激化をたどるモンガの抗争に巻き込まれていく。「ビバ！監督人生!!」のニウ・チェンザー監督がメガホンをとる。", "2010-12-18", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MAISON SOUS LES ARBRES", "パリは霧にぬれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "アメリカの新鋭作家として注目されたカヴァーノの産業スパイ小説を、フランス映画の巨匠クレマンが映画化。舞台はパリ。電子工学の仕事をしていたアメリカ人の男が産業スパイ事件に巻き込まれ、二人の子供が誘拐される……。夫婦間の断絶も絡ませたシリアスタッチのサスペンス・ドラマ。", "1971-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("親馬鹿大将", "親馬鹿大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "柳家金語楼と三益愛子が絶妙なコンビネーションを見せる明朗喜劇。金に目のない大山夫婦は、金融業を営みインフレで苦しむ人を操っていた。ところが、100万円の偽札をつかまされたあげく、空巣に入られて無一文に。金に汚ない親を、うとましく思っていた二人の子供はこれを喜び、一家は一つになって大山家再建に乗り出していく。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Furry Vengeance", "アニマル・ウォーズ　森林帝国の逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「ハムナプトラ」シリーズのブレンダン・フレイザー主演によるドタバタコメディ。不動産開発業者のダン（フレイザー）は、オレゴンの山奥に巨大商業施設と住宅分譲地を作る計画を立てる。だが、アライグマをリーダーとする森の動物たちは危険を察知し、ダンの計画をあの手この手で阻止しようと奮闘する。監督は「クルーエル・インテンションズ」「クリスティーナの好きなこと」のロジャー・カンブル。共演にブルック・シールズ。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE REIVERS", "華麗なる週末", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "20世紀初頭。アメリカ南部の町で初めて自動車を買った男の、11歳になる息子の忘れ得ぬ冒険。それは自動車好きの使用人たちとのひそかなドライブ旅行。少年の回想で綴るアメリカ南部の夏を描いた、フォークナー原作の心温まる物語の映画化。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLOODS OF FEAR", "大洪水", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "『サタデー・イブニング・ポスト』に掲載された小説の映画化。アメリカ東部の田舎町、どしゃぶりの雨が降りしきるなか、ドナヴァンは女を救う。そしてもう一人の男も助けて一軒家に逃げこんだ。看守、囚人、謎の男と女が繰り広げるサスペンス・タッチの一編。", "1958-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Impact Point", "インパクト・ポイント　狙われたビーチの妖精", "", "", "http://image.eiga.k-img.com/images/movie/54585/315x210.jpg?1985", 85, "米TVシリーズ「ビバリーヒルズ高校白書」のブライアン・オースティン・グリーンが出演するサスペンス。ビーチバレーの人気選手ケリーは、ホールデンと名乗る謎のレポーターに出会い、ケリーのファンだと話す彼に徐々に惹かれていく。しかし、その頃からビーチバレーの選手ばかりが狙われる殺人事件が次々と起こり始め……。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JA　ZUSTER　NEE　ZUSTER", "クリビアにおまかせ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "看護婦クリビアと彼女が運営する療養所の個性豊かな住人たちの愉快な毎日を、キッチュでオシャレな映像と歌と踊り満載で描き出す。オランダ発のファンタジックなミュージカル・コメディ。私設の療養所を運営するクリビアは、人のいい住人たちと楽しい毎日を送っているが、個性の強すぎる彼らを嫌う大家のボーデフォルだけが悩みのタネ。ところが、住人の一人が開発した薬の効果で、突然ボーデフォルが善人になり……。", "2002-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アヒルの子", "アヒルの子", "", "", "http://image.eiga.k-img.com/images/movie/55415/315x210.jpg?1602", 92, "「ゆきゆきて、神軍」で知られるドキュメンタリー作家・原一男総指揮のもと、小野さやか監督が日本映画学校の卒業制作として製作したドキュメンタリー。東京でひとり暮らしをしながら専門学校に通う小野は、5歳のときに農業を中心とした理想社会を形成するコミューン団体「ヤマギシ会」幼年部に1年間預けられた過去を持つ。親に捨てたれた記憶としてその過去に苦しみ、それ以降、家族の前で偽りの“いい子”を演じてきた。小野は自らを鬱屈（うっくつ）から開放するため、家族ひとりひとりと正面から対じすることを決意する。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROSS MY HEART", "ハート泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“子持ち”の美人キャシーと、ちょっと頼りないが優しい男デビッド。お互いが自分に不利な面を隠しながらも、二人が本当の愛を得るまでを描くちょっぴりセクシーな大人のラブ・コメディ。「白いドレスの女」「偶然の旅行者」の監督、L・カスダンが製作を担当している。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tarbosaurus", "タルボサウルス（原題）", "", "", "http://image.eiga.k-img.com/images/movie/55475/315x210.jpg?1496", 0, "", "2012-12-31", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sex,lies and Videotape", "セックスと嘘とビデオテープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ルイジアナ州の小都市。弁護士のジョンは理想的な家庭を築いているかに見えるが、実は妻の奔放な妹と関係していた。そんな折、学生時代の友人が訪れ、ビデオカメラで性的対話をしようという。心優しき変態者の来訪により、それぞれの男女が自己崩壊と自己発見のスリルを味わっていく。ソダーバーグ監督デビュー作。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Transsiberian", "暴走特急　シベリアン・エクスプレス", "", "", "http://ecx.images-amazon.com/images/I/61hPBKsG9VL._SL210_.jpg", 111, "", "2008-01-01", "3,7,9,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUGUSTIN", "おとぼけオーギュスタン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "オーギュスタンはある作品のオーディションを受けるため、キャスティング・プロダクションにやってくる。彼は担当のマダム・シュラに自己紹介とこれまでのキャリアについて話し始めた。一度聞いたら忘れられない妙な話し方が、オーギュスタンのスタイルなのだ。彼は“受付のフロアーボーイ”という役柄のオーディションを受けることに決定、さっそく町に飛び出し、ホテルに行ってボーイの練習をさせてくれるよう頼みこむが……。珍妙な魅力の主人公が活躍するフレンチ・コメディ。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOLDEN VOYAGE OF SINBAD", "シンドバッド黄金の航海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "『アラビアンナイト』の主人公の一人、シンドバッドの冒険旅行を描いたSFファンタジー。アラビア支配をたくらむ悪漢クーラを相手にシンドバッドが大活躍。半人半馬の怪獣ケンタウルスなどの神話上の生き物が、アニメを使った鮮やかな特撮効果で生き生きと描かれる。", "1973-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("九龍冰室", "冷戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "“欲望の街 古惑仔”シリーズのイーキン・チェン主演のバトル・アクション。かつて暗黒街で名を馳せたロンが７年の刑期を終えて香港に戻ってくる。平和な暮らしを望む彼は、かつての恋人ヘレンとの間にできた息子シウロンが現れたことで、その思いを一層強くした。しかし、彼に昔のような暗黒街の英雄に戻ってほしいと願うヘレンは顔役フォーサンをだまし込んで策を練る。結局ヘレンはロンの説得に足を洗うことを決意するが、だまされたと知ったフォーサンはロン抹殺を画策するのだった……。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ERA NOTTE A ROMA", "ローマで夜だった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "1943年、第二次世界大戦の戦火に揺れるイタリア。エスペリアは修道尼に変装してヤミ商売に手を出していたが、ある日連合軍側の脱走兵３人と出会い、彼らをかくまう。すでにナチによるローマ侵攻が始まっており、レジスタンスに参加した者は次々と捕らえられ、ある者は銃殺された。もはやエスペリアたちの身の保障もなくなり、彼女たちの必死の逃亡生活が始まるが……。「無防備都市」「戦火のかなた」「ドイツ零戦」の戦争３部作を発表、一躍“ネオ・レアリスモ”の運動とともにその名を世界に知らしめたR・ロッセリーニが低迷期を迎えようとしていたころの作品。フェリーニやアントニオーニの台頭で“過去の人”にならんとしていたロッセリーニ。この作品以降の彼は興業的失敗を繰り返し、テレビの仕事中心に移行していった。", "1960-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOME ALONE3", "ホーム・アローン3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "マコーレー・カルキンに替わって、A・D・リンツを起用して作られたキッズ・コメディの第３作。今回も少年が泥棒相手に大活躍を繰り広げる。シカゴ郊外の住宅街に住むアレックス少年は雪かきをしたお礼として近所に住むおばさんからリモコン・カーを受け取る。だが、その中にはペーター率いる国際的ハイテク犯罪集団が盗んだマイクロ・チップが隠されていた。ペーターたち４人はチップを取り返そうとアレックスの家を捜し出す。怪しい人間が家の周りをうろつくのを見たアレックスは警察に電話するが、誰も信じてくれない。そして水疱瘡にかかったアレックスが一人で留守番をしている家に、ペーターたちが忍び込もうとするが……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カラー・オブ・ライフ", "カラー・オブ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "深夜放送で若者を中心にカルト的な人気を呼んだTV番組『バミリオン・プレジャー・ナイト』の映画版。“ライフ”をテーマにした19本の短編が収められたオムニバス作品で、シュールでナンセンスな世界が繰り広げられる。マネキン一家、フーコン・ファミリーの日常を描いた「フーコン・ファミリー」、ゾンビの母娘のハイテンション・ストーリー「ゾンビ・ファミリー」など。監督は現代アート界で活躍中の石橋義正。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("畫中仙", "ジョイ・ウォンの新チャイニーズ・ゴースト・ストーリー 画中仙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "中国の伝統的怪異譚である“画中の美女と青年のこの世ならぬ恋”を描いたファンタジー映画。「チャイニーズ・ゴースト・ストーリー」のスタッフが再び集結し、J・ウォンも再度、美人幽霊を演じているが、シリーズ作品ではない。またユン・ピョウが、妖怪に追われる二人を救出する道士役で出演。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜霧よ今夜も有難う", "夜霧よ今夜も有難う", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ボギーとバーグマンの「カサブランカ」を日活が翻案して、石原裕次郎と浅丘ルリ子で映画化した。横浜でナイトクラブを経営している相良の前に、かつての恋人が突然姿を現し、彼女の夫である東南アジア出身革命指導者・グエンの密出国に力を貸してくれという。最初は冷たく断わっていた相良だが、やがて……。そろそろ青年期から離れるところにさしかかっていた裕次郎（当時33歳）をうまく使ったムード・アクション。構成は「カサブランカ」そのままだが、情感たっぷりの名セリフが裕次郎にふさわしく散りばめられていた。同名の主題歌は映画に先行して大ヒット。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADIEU　PHILIPPINE", "アデュー・フィリピーヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "フランスのヌーヴェル・ヴァーグの一作となった幻の青春映画。ゴダール監督に見いだされて本作で長編デビューを果たしたJ・ロジエ監督だが、青春の光と影をタイトに捉えて目を見張る画面を創出した。19歳のイケメンの青年は、どちらもかわいくて明朗な二人のイギリス人少女に出会う。そんな二人につきまとわれる彼は、クールなまま。やがてコルシカ島へ遊びに行った青年だが……。", "1961-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les cent et une nuits de Simon Cinéma", "百一夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "映画に人生を捧げた老人をめぐり、大スターと映画界に憧れる若者たちが巧妙な駆け引きを繰り広げるドラマ。映画とともに人生を歩み、パピルス(文化遺産)のあだ名を持つムッシュ・シネマも今年で百歳を迎え、ボケぎみ。ムッシュ・シネマの大事な記憶を刺激するために、101日間、彼と映画の話をするアルバイトにカミーユは選ばれる。ムッシュ・シネマを見舞いに日毎やって来る大スターに興奮する彼女はある日、彼のただ一人の遺産相続人である曾孫が行方不明であることを知る。恋人で映画監督志望のミカと、偽の曾孫を仕立てあげ財産を奪おうとするが……。A・ドロンはじめ大挙出演する大スターも見もの。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("素敵な夜、ボクにください", "素敵な夜、ボクにください", "", "", "http://image.eiga.k-img.com/images/movie/33865/315x210.jpg?4832", 104, "トリノオリンピックで注目を集めたカーリングをテーマに、日本人女性と韓国人男性の恋愛を描くラブコメディ。売れない女優のいづみは、韓流スターのカン・スヒョンと一夜を共にする。しかし、実は彼はカン・スヒョンそっくりの韓国人カーリング選手ジンイルだった。騙されたことに腹を立てたいづみは、成り行きでカーリングチームを結成。コーチとしてジンイルを指名する。ジンイル役に、ドラマ「ホテリアー」でブレイクしたキム・スンウ。", "2007-02-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("saru phase three", "saru phase three", "", "", "http://ecx.images-amazon.com/images/I/41cI5Kg6BBL._SL210_.jpg", 97, "治験アルバイトの恐ろしい実態を描いた「サル」で衝撃のデビューを果たした葉山陽一郎監督が、実際に起きた抗がん剤事件をモチーフに撮りあげた続編。バイクショップで整備士として働く25歳の稔は、ガンに冒され手術を受けるが、すぐにリンパへの転移が見つかり抗がん剤の投与を始めることに。やがて嘔吐、皮疹、血尿といった激しい副作用に悩まされるようになった稔は、徐々に病院への不信感を募らせていく。", "2007-02-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花より男子", "花より男子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "フジテレビが贈る“ぼくたちの映画”シリーズ第１作。2005年にもTVドラマ化された同名人気コミックを、アイドル内田有紀の主演で映画化。牧野つくしは庶民の子ながら、超名門校・英徳学園に通っている。リッチで高飛車な学生たちから執拗にいじめられても、めげることなく持ち前のパワーで対抗する。そんなつくしに、学園を牛耳るお坊ちゃん軍団“花の４人組”＝F4も一目置くようになる。いつしかF4のリーダー・道明寺司はつくしに惹かれ始め、一方つくしは美少年・花沢類に心を奪われる。揺れ動くつくしの恋心。そんな時、道明寺から学園のビッグ・イベント、ダンス・パーティーで、一緒に踊ろうと誘われるが……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les amants du Flore", "サルトルとボーヴォワール　哲学と愛", "", "", "http://image.eiga.k-img.com/images/movie/57105/315x210.jpg?4170", 105, "事実上の夫婦として公私にわたり影響を与えあった哲学者ジャン＝ポール・サルトルとシモーヌ・ド・ボーボワールの知られざる愛憎の軌跡を描いたドラマ。1929年、パリ大学で出会ったサルトルとボーボワールはひかれ合い、大学を卒業後に共同生活を始める。サルトルは互いに愛し合いながらも、他の関係も認め合うという自由恋愛を提案。結婚か独身しか女性に選択肢のない社会に疑問を抱いていたボーボワールは、その提案を受け入れるが……。ボーボワール役に「シャネル＆ストラヴィンスキー」のアナ・ムグラリス。", "2011-11-26", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REX 恐竜物語", "REX 恐竜物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "洞窟の奥から発見された古代の恐竜の卵の孵化実験が成功した。古生物学者の娘・千恵は生まれた恐竜の赤ちゃんをレックスと名づけ母親代わりに育てることになった。しかし、レックスでひともうけをたくらむマスコミや政治家の手が千恵のもとに迫ってきた……。現代社会への鋭い批判も込めたファミリー・ファンタジー。ダイアローグ・ライターとして、人気脚本家の内館牧子が参加。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO BROTHERS", "トゥー・ブラザーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「スターリングラード」の巨匠、J・J・アノー監督作。アンコールワットの遺跡を舞台に、数奇な運命により引き裂かれ、その後、引き合わされた２匹のトラと一人の少年の深い絆と友情が描かれる。幼いトラと少年が触れ合うシーンはどこまでも自然で感動的。トラたちの人間顔負けの名演も見どころだ。カンボジアで生まれた双子のトラ、クルマとサンガは仲良く成長していた。しかし、クルマは冒険家エイダンに捕まり、村で村長にサーカスへ売られてしまう。一方サンガも、狩猟会のために罠に落とされ捕獲されてしまい……。", "2004-09-18", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLOR OF NIGHT", "薔薇の素顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "B・ウィリスが精神科医を演じる異色のサイコ・サスペンス。精神分析医のボブが、何者かに惨殺された。ボブから“自分は患者の誰かから命を狙われている”と告げられていた同僚のキャパは、事件の現場から帰る途中、軽い衝突事故に巻き込まれる。車に乗っていたのは、ローズという美しい少女だった。ローズの妖しい魅力の虜となり、愛欲にのめり込んでいくキャパ。ボブが残した本の中から一枚の写真を見つけたキャパは、髪形やメイクは違っていてもそれは紛れもなくローズであること、そしてローズが患者の全員と関係を持っていたことを知る。それも別の名前、別の人格を持った女として……。ローズは多重人格だったのだ。「愛人／ラマン」に続いての映画出演となったJ・マーチがローズの妖しい魅力を見事に演じている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あばれ獅子", "あばれ獅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "放蕩無頼で庶民の英雄として親しまれ、また恐れられた勝海舟の父・勝小吉の型破りな人生を描いた作品。日本経済新聞に３年間連載された子母沢寛の『勝海舟』を原作とし、阪東妻三郎の最後の出演映画でもある。ビデオタイトルは「勝海舟より　あばれ獅子」。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("茉莉花開／JASMINE WOMEN", "ジャスミンの花開く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "30年代から80年代の激動の上海を舞台に、数奇な運命の中で力強く生き抜く三世代の女性を、アジアのスター女優チャン・ツィイーがひとり三役で熱演。世代ごとに全く違う姿を見せ、彼女の見事な表現力と存在感が彩り豊かに光っている。30年代、妊娠によって女優への夢を失った茉。50年代、茉の娘・莉は、結婚するが不妊症のために精神を病んだ。そして80年代、ひとりで娘を産もうとする茉の養女・花の姿があった。", "2006-06-17", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JONATHAN LIVINGSTON SEAGULL", "かもめのジョナサン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ただエサを取るだけの生活に飽き足らず、より速く、より高く飛ぶ術を習得しようと試みるカモメのジョナサン。群れの長老たちから異端者として追放されるが、放浪の旅のなかで新たな世界観をつかむ。現代人の心をカモメに託し映像化。人間を一人も登場させない徹底した演出が快い。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四人目の淑女", "四人目の淑女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "戦地より６年ぶりに帰還した道田和夫は、焼土と化した東京で、音楽学校時代の女友だちを訪ねる。だが、高い志に燃えていた彼女たちは、今や金のために身も心も売り払っているありさまで、和夫は深く失望する。そんな時、ついに会うことのできなかった女友だちの一人・好江が和夫のことを思いながら死んでいった……。新藤兼人のオリジナル脚本を渋谷実が監督した人間ドラマ。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE　TRUE　THING", "母の眠り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "ピュリッツァー賞に輝くジャーナリスト、A・クィンドレンの小説を映画化した家族ドラマ。田舎町の婦人会の付き合いに喜びを感じ、黙々と主婦業をこなしてきた中年女性ケイトが重病を患った。都会の記者として慌ただしい日々を送る娘エレンは、彼女の世話をするために帰郷し、どうしようもなく平凡に思えた母親の人生の意義を理解していく。名女優M・ストリープがアカデミー賞にノミネートされた迫真の演技を披露。「青いドレスの女」で知られるC・フランクリン監督がメロドラマ的な甘さを抑えた好演出を見せ、母親の死因をめぐるミステリーの要素も含むドラマを巧みにまとめ上げている。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONDOVINO", "モンドヴィーノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "ソムリエの資格を持つ監督が、伝統と革新とがせめぎ合うワイン業界の現状をリポートした渾身のドキュメンタリー。ワインの本場ヨーロッパから北米、南米まで世界各地で取材を敢行。個性豊かな人々たちが集うワイン作りの世界が、いつしか世界の現状とダブって見える奥行きが興味深い。大衆好みのワインで市場を席巻するアメリカのモンダヴィ一家。旧来の伝統を守るフランスのワイン職人との間に確執が生まれ……。", "2005-10-29", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　だだんだんとふたごの星", "それいけ！アンパンマン　だだんだんとふたごの星", "", "", "http://image.eiga.k-img.com/images/movie/54565/315x210.jpg?3834", 50, "人気アニメ「それいけ！アンパンマン」の劇場版シリーズ21作目。ある日、ふたごの星の妖精キララとキラリがケンカのはずみで地上に落ちてくる。湖のほとりに落ちたキララを助けたアンパンマンは、ばいきんまんと黒い星の子ギラリが操る巨大ロボット、だだんだんから夜空の星を守るため、キララたちと力を合わせて立ち向かう。「ばいきんまんVSバイキンマン」を併映。", "2009-07-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOVIE HUSTLE", "MOVIE HUSTLE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "５人の新進映像作家がそれぞれ、活躍中の若手アイドル・俳優を主演に迎えて短編を製作。片想い、友情、約束といったテーマの下、見る者の心を温めてくれる、元気で“ハッスル”な物語が綴られていく。女優志望の香織は、昏睡状態の恋人が気になりオーディションに集中できない。そんなある日、不思議なメールが届く。（「White Illumination」）", "2005-01-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゼブラーマン", "ゼブラーマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "哀川翔主演100本記念作。三池崇史＆宮藤官九郎という人気の監督と脚本家の顔合わせも注目された。幻のTVヒーローへの憧れが高じて、自ら“復活”させてしまったサエない教師を、哀川が力演。かつてない役どころながら、どこを切っても“彼らしさ”が充満する、アバンギャルドな痛快娯楽活劇になっている。小学校教師の市川新市は、職場でも家庭でもサエない男。誰にも尊敬されない新市の憧れは、子供の頃テレビで観たヒーロー“ゼブラーマン”だった。そんな中、新市の暮らす横浜市八千代区に、不可解な事件が起こり始める。ザ・ハイロウズによる主題歌『日曜日よりの使者』も絶妙に映画を盛り上げる。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("零戦黒雲一家", "零戦黒雲一家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "石原裕次郎としては珍しい戦争アクション。第二次大戦末期、ソロモン郡島の小島に孤立した航空分遺隊に中尉の裕次郎が着任し、二谷英明の下士官と対立しながら、無法者ぞろいの連隊を見事に統率する。舛田利雄の豪快な演出が光る一編。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIFEBOAT", "救命艇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "第二次大戦中、１隻の商船がドイツ軍潜水艦に撃沈され、生き残った男女７人が小さな救命艇に乗り込む。そこへ商船爆発のあおりを受け、こちらも沈没してしまった潜水艦のドイツ兵が泳ぎ着く。彼を救うべきか見捨てるべきか悩む７人だが、救命艇を操作できるのはそのドイツ兵のみで、全員の運命が彼にゆだねられることになる……。小さな救命艇の中のみでドラマが展開し、カメラも外に出ることがないヒッチコックの実験的作品。また、「下宿人」（1926）以降、必ず自作中に登場したヒッチコックが、この限られたボートの中でアッと驚く登場を見せている。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アートフル・ドヂャース", "アートフル・ドヂャース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "N.Y.在住で、同地で撮影された細谷佳史監督の「スリーピー・ヘッズ」で助監督を務めた保田卓夫がオールN.Y.ロケで完成させた監督デビュー作。ストリートで売れない絵を売っている画家志望の優一郎、インターネットでポルノ小説を執筆中の圭介、自称“天才”のギタリスト、勉。ブルックリンで暮らす彼らのもとに、日本から優一郎の幼なじみ紗於里がやって来た。彼女の目的はN.Y.に住んでいる姉、綾乃の様子を探ることなのだが……。物語は行方不明の綾乃をめぐって展開するが、映画のメインはあくまでも、３人の日本人青年のライフスタイル。気負わず、流されず、異国で“自分らしく”生きる彼らの姿には、監督自身の体験が反映されているのだとか。主演陣はいずれも好演しているが、とりわけ音楽も手掛けたシアターブルックの佐藤タイジの存在感は抜群。綾乃に扮した裕木奈江も出番は少ないが、印象的なたたずまいを見せている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACK COBRA ２", "ブラック・コブラ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“ブラック・コブラ”ことシカゴ市警の一匹狼ロバート・マローン警部補の活躍を描くシリーズ第２作。今回はインター・ポールの派遣員としてフィリピン・マニラに飛ばされたマローンが、テロリストの陰謀に立ち向かう勇姿を描いていく。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PROCE｀S", "審判", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "カフカの同名小説の初めての映画化。ある朝突然、当局によって“有罪”を宣告された大会社の副部長ヨーゼフ・K。だが検察官も刑事も彼の罪状を知らず、身柄を拘禁する必要もないという。自由の身のまま一挙手一投足を監視され、次第に疲弊していくK。呼び出された法廷は大群衆がひしめき合う廃劇場で、とても官僚とは思えぬ下品な司直が無意味なおしゃべりをするばかり。さらに伯父の勧めでとある高名な弁護士を訪ねたKは、それから現実なのか空想なのかわからない奇妙な人間たちの間を往復したあげくに爆殺される……。不条理小説の名作に、O・ウェルズが挑んだ一編。何もかもが歪んだ世界で、主人公がまさに小突き回される姿には戦慄を覚えずにはいられない。長身のA・パーキンスと、体積で圧倒する巨漢ウェルズが対話する場面など、繊細なカメラワークも出色。", "1963-01-01", "2,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A-Kuei and His Magic Hammer", "アークエとガッチンポー THE MOVIE アークエと魔法のハンマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "台湾で暮らす少年アークエと不思議な仲間たちが繰りひろげるシュールな日常を描いたテレビアニメ「アークエとガッチンポー」の劇場版。ある日、アークエの家に不思議なハンマーが届けられた。そのハンマーで頭を叩くと、いつもぼんやりしていたアークエがひょう変。ずる賢い知恵を手に入れたアークエは、周囲の人々にいたずらを繰りかえし、スプリングタウンは大混乱に陥ってしまう。そんな中、街に謎の怪獣が現われ……。", "2005-04-29", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAIGRET TEND UN PIE｀GE", "殺人鬼に罠をかけろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "パリ第４区で次々に発生する連続殺人事件。捜査に乗り出したパリ警視庁のメグレは、インテリア・デザイナーのマルセルとその妻イヴォンヌの行動に疑いを持ちはじめる……。ギャバンの人気シリーズの一編。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋文日和", "恋文日和", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "女性漫画家、ジョージ朝倉の原作コミックの中から、人気の高い『あたしをしらないキミへ』『雪に咲く花』『イカルスの恋人たち』に各エピソードをまたぐ『便せん日和』を加えた４編を、４人の新進監督が映像化したオムニバス。姿が見えない文通、悩みを記した手紙、恋人への遺書、出せずじまいのラブレターと、好きな相手に文字で想いを伝えることの温かみが、たおやかに綴られる。ケータイやメールでは決して育めない、手紙ならではの恋愛感情の変化に心揺さぶられる珠玉の感動作。", "2004-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クローズZERO", "クローズZERO", "", "", "http://image.eiga.k-img.com/images/movie/34275/315x210.jpg?1837", 130, "少年チャンピオンでの連載終了後も根強い人気を誇る高橋ヒロシのコミック「クローズ」をオリジナルストーリーで実写映画化。原作より過去に遡り、不良たちが集まる鈴蘭男子高校で勃発した史上最大の抗争を描く。幾つもの派閥が勢力争いを繰り広げている鈴蘭男子高校。現在の最大勢力は、3年の芹沢が率いる一派だ。そこに鈴蘭制覇を狙う転入生・滝谷が現われ……。鬼才・三池崇史がメガホンを取り、小栗旬、山田孝之、黒木メイサら豪華キャストが集結。", "2007-10-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Control", "コントロール", "", "", "http://image.eiga.k-img.com/images/movie/53395/315x210.jpg?3661", 110, "絶望的な歌詞や独創的な曲調で70年代末のUKロック・シーンに衝撃を与えたポスト・パンク・バンド、ジョイ・ディヴィジョン。そのボーカリストとして絶頂期にありながら、23歳という若さで自ら命を絶ったイアン・カーティスの半生を、これまで多くのミュージシャンを撮り続けてきた写真家アントン・コービンが映画化。主人公イアン役には、自身もバンド活動を行う若手俳優サム・ライリーが抜擢された。", "2008-03-15", "3,1,11,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Fille Coupee En Deux", "引き裂かれた女", "", "", "http://image.eiga.k-img.com/images/movie/55935/315x210.jpg?1739", 115, "2010年に他界した仏ヌーベルバーグの巨匠クロード・シャブロル監督が、07年に手がけたサスペンス。20世紀初頭のアメリカで起こった「スタンフォード・ホワイト殺害事件」をベースに、年齢も性格も異なる2人の男に愛されたヒロイン、ガブリエルが、歪んだ恋愛関係に溺れ、自分を見失っていく様子を描く。主演は「スイミング・プール」のリュディビーヌ・サニエ。ガブリエルを愛する2人の男に、「ピアニスト」のブノワ・マジメル、仏映画界のベテラン俳優フランソワ・ベルレアン。", "2011-04-09", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("226", "226", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "プロデューサーの奥山和由が放つ異色青春映画の大作。２・26事件当日の夜から事件終結までの４日間を、五社監督は持ち味のケレン味を抑えて熱っぽく描き出す。“狂い続けてやる！”と叫ぶ三浦友和ほか、青年将校を演じる役者たちの熱演にもかかわらず、オールスター・キャストでそろえた大物俳優たちは、いずれも特別出演の域を出ていない。また、青年将校たちとその妻たちの描写がやや図式的な感がある。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Girl in Every Port", "港々に女あり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "世界中の港々に馴染みの女たちを抱えていた不定期船の水夫スパイク。だが、今度の航海では行く先々で不敵な色男の水夫ビルに先を越され、女たちをとられてしまう。最初は対立していた二人はいつしか親友となるのだが、美女ゴディバを巡ってまた大喧嘩となり…。本国アメリカのみならずヨーロッパでもヒットし、ルイーズ・ブルックスの美しさと監督ホークスの名を多くの人に広めた作品。", "1928-08-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THINGS YOU CAN TELL JUST BY LOOKING AT HER", "彼女を見ればわかること", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "年老いた母の面倒を見ながら男性からの電話を待ち続ける女医、他人の運命は完璧に言い当てられるのに、自分のことは冷静に判断できないタロット占い師、不倫に心揺れる銀行の女性支店長、わざと奔放に振る舞う恋多き盲目の美女……。一見、自立していて幸せそうに見える女性たちの心の底の痛みや孤独、希望をすくい上げた珠玉のオムニバス。低予算のインディペンデント作品でありながらハリウッドの実力派、個性派の女優がこぞって出演を熱望した作品。彼女たちは自分が主人公となるエピソード以外にもそれぞれが同じ役柄で出演している。ラストシーンまで見事に人の輪がつながっている脚本が秀逸だ。監督はノーベル文学賞作家ガルシア・マルケスの息子R・ガルシア。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　HONEYMOON　KILLERS", "ハネムーン・キラーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "実在の犯罪者カップルをモデルにした、鮮烈なラブ・ストーリー。1940年代、太りぎみで独身の中年看護婦マーサは母親の薦めで、嫌々ながら文通クラブに入会。それが縁でレイという男と恋に落ちるが、実は彼は結婚詐欺師。事実を知ったマーサは彼を失うことを恐れて詐欺の共犯者となることを選び、ついには殺人をも犯す。女好きのレイとの関係は揺らいでいくが、彼との逃亡生活のなかで、彼女は次第に犯罪の主導権を握るようになる……。女性の心理を鋭く浮び上がらせた物語は題材の衝撃性に反して、切なさを呼び起こす。フランソワ・トリュフォー監督に“アメリカ映画で、最も好きな作品”と言わしめた。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Chronicles of Narnia: Prince Caspian", "ナルニア国物語　第2章：カスピアン王子の角笛", "", "", "http://image.eiga.k-img.com/images/movie/53345/315x210.jpg?3580", 145, "ペベンシー4兄弟が白い魔女を倒し平和を取り戻してから1300年後のナルニア。魔法の国はテルマール人によって占領され、ナルニアの民は迫害の末に森の奥深くで息を潜めていた。一方、テルマール人の王子カスピアンは、王位を狙う叔父に暗殺されそうになり命からがら逃げのびる。森へと追い込まれた彼は“伝説の4人の王”を呼び戻すことができるという魔法の角笛を吹き……。C・S・ルイスによる名作児童文学の実写映画化第2弾。", "2008-05-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("砂の女", "砂の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "読売文学賞を受賞した小説を、原作者の安部公房自らが脚色。映像化は困難であると思われた観念的な原作を、勅使河原監督は見事なモノクロームの映像化に成功している。カンヌ映画祭審査員特別賞をはじめ、数多くの賞を受賞した力作。砂丘地帯に昆虫採集に来た教師が一夜の宿にと、砂の穴の中にある後家の家に泊まる。穴はたえず砂が崩れ落ち、女は終日その砂をかき出している。男は自分がその労働力として囚われの身になったことを知り愕然とし、脱出を図るが果たせない。そして女と欲情で結ばれ、住人に同化していく……。文明の囚人となった人間を象徴的に描き、痛烈なショックを与える前衛的な作品である。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンを探して", "アンを探して", "", "", "http://image.eiga.k-img.com/images/movie/54755/315x210.jpg?4284", 105, "不朽の名作「赤毛のアン」の舞台であるカナダのプリンス・エドワード島を訪れた少女のひと夏の成長を綴った青春ドラマ。亡き祖母・静香の初恋相手であるカナダ人兵士を探すため、たった1人でプリンス・エドワード島にやって来た17歳の杏里。悲しみに閉ざされていた彼女の心は、島で暮らす人々との温かい交流を通して少しずつ癒されていく。主演は石橋貴明の実娘・穂のか。", "2009-10-31", "76,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BATTLE FOR THE PLANET OF THE APES", "最後の猿の惑星", "", "", "http://ecx.images-amazon.com/images/I/51P90mpGCmL._SL210_.jpg", 87, "「猿の惑星」シリーズの第５作で最終編。猿と人間の核戦争よって両者はほとんど全滅した。わずかに生き残った猿たちは、猿と人間が手をつなぎ共存していくべき地球の復興に全力をあげる。ジョン・ヒューストン監督が猿に扮して特別出演する。", "1973-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("よるべなき男の仕事・殺し", "よるべなき男の仕事・殺し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "藤原審爾の同名小説の映画化で、“殺し”という仕事を淡々と行う若き殺し屋と、その冷酷さをかぎつけ戦いを挑む刑事との対決をストイックに描いた村川透ひさびさのハードボイルド・ロマン。加藤雅也が悲しくも美しい冷酷な表情を持つ悪のヒーロー像を熱演。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Truth About Charlie", "シャレード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "　エーゲ海のバカンスからパリへ帰国したレジーナ。ところが家は荒らされ、その上、離婚寸前にあった夫チャーリーは何者かに殺害されてしまう。レジーナは大使館員バーソロミューからチャーリーの秘密を知らされ、そのあげく怪しい3人組に脅迫される始末。バカンス先で出会ったジョシュアが彼女を助けてくれたが、しかし彼にも秘密があった。はたして事件の真相は？　オードリー・ヘップバーン、ケイリー・グラントが主演した名作「シャレード」の現代版リメイク。", "2002-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAPID　ASSAULT", "スティール シャークス２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "テロリスト集団のリーダー、ラーズ・ライナークは大西洋にある海底研究基地で致死能力の高い劇薬を完成。これを海底に設置し、合衆国政府を脅迫する。政府は海軍特殊部隊のデッカーとCIA特別諜報員フィリップスに事件の解決を指令。二人は何とか海底基地に潜入するが、そこで思わぬ事態が起こる……。魚雷戦ありの迫力の海底バトル・アクション。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・べらんめえ芸者", "続・べらんめえ芸者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "第１作と同スタッフによる、美空ひばりの名物シリーズ続編。今はなき柳橋の有名な待合の娘、芸者の小花は男勝りのちゃきちゃきの江戸っ子。テレビ工場の見学をきっかけに、ある電気メーカーの乗っ取り騒動に巻き込まれた小花は、義噴にかられてパーティーの席上で小気味良いタンカを切った……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST GREAT WARRIOR", "スクワント 伝説の勇者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "時は1600年代初頭、現在のニューイングランドにはネイティブ・アメリカンのパトゥセット族が暮らしていた。ある日、イギリスの商船が酋長の息子スクワントを拉致したことから、悲劇は始まった。外国の土産として、イングランドのプリマスへと連行されたスクワントは何とか逃走し、故郷へと戻るが、“新大陸”と称して自分たちの土地へやって来る白人の群れを目の当たりにする……。アメリカでは、学校教育にも取り入れられているほど有名なアメリカ先住民とイギリス開拓者の実話をもとに作られたヒューマン・ドラマ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE REVENGE", "ブルー・リベンジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "元全米女子空手チャンピオンのC・ラスロックと、「アンタッチャブル」の殺し屋役が印象深い怪優B・ドラゴが共演するサスペンス・アクション。ロサンゼルスの高級ホテルの宝石店で数千万ドルのダイヤが奪われた。犯人はダイヤを、偶然居合わせた空手チャンピオンのスーザンの荷物に隠す。そうとは知らず夫とともにジャカルタへ向かうスーザンだったが……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON THE DOUBLE", "ダニー・ケイの替え玉作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "第二次大戦下のスパイ騒動を面白おかしく描く、D・ケイ主演のおとぼけサスペンス・コメディ。連合軍基地司令官マッケンジー将軍にそっくりの兵士アーニーは、将軍の替え玉にされてしまう。おまけにナチスのスパイによって誘拐されてしまい……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOY WITH GREEN HAIR", "緑色の髪の少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「できごと」「召使」など個性あふれる作品で知られる異才J・ロージーの処女作。警察に保護された坊主頭の少年が、児童心理学者に自らの体験を物語っていく。彼はロンドンの上流家庭で幸福な日々を送っていたが、戦争が始まったために疎開する。やがて両親からの音信が絶え、彼はアメリカの芸人について渡米することになる。町の人たちと親しくなり、学校にも入った少年だが、ふとしたことで自分が戦災孤児であることを知る。悲しさと恐怖のためか、あくる朝、彼の髪の毛はあざやかな緑色に変わってしまう……。その内容から共産主義思想を持った作品として、当時RKOの実権を握っていたハワード・ヒューズに公開を拒まれ、プロデューサーのD・シャーリーが退社をかけた主張によって公開にこぎつけた、いわくつきの作品。ロージーもやがて“赤狩り”の波に追われてヨーロッパに逃げる運命になる。「パリ、テキサス」などにも出演しているD・ストックウェルの名子役ぶりが印象的。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("七人の刑事", "七人の刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "同名のTVドラマを映画化した刑事もので、７人の刑事はテレビと同じメンバー（堀・芦田・菅原・佐藤・城所・美川・天田）が扮した。戸山ヶ原でコールガールが殺され、若い恋人たちが事件に巻き込まれる……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愚か者・傷だらけの天使", "愚か者・傷だらけの天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「傷だらけの天使」の“前日談”ともいうべき姉妹編。真木蔵人が相棒の豊川悦司に出会うまでの物語を綴る。写真学校をドロップアウトした久はひょんなことから万引き常習犯の主婦と知り合い、彼女から行方不明の息子を探すバイトを持ちかけられる。主婦が作るオムライスが物語の小道具として絶妙な効果を発揮。真木蔵人ものびのびとした個性で映画の豊かな印象に貢献している。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レディ・ジョーカー", "レディ・ジョーカー", "", "", "http://ecx.images-amazon.com/images/I/21RKC92X0BL._SL210_.jpg", 121, "「OUT」の平山秀幸監督が、高村薫の同名ベストセラー小説を映画化。大胆不敵にして緻密な犯罪に出た犯行グループと、彼らのターゲットとなった企業、警察の思惑が入り乱れ、濃密なサスペンス劇が進展していく。事件の裏にある悲劇が少しずつ明かされる物語は、やがて衝撃のクライマックスへと突入。エンターテインメント性と社会派の魅力を併せ持つ、奥深いドラマに仕上がった。主人公に扮する渡哲也や期待の大型新人、徳重聡ら豪華なキャストも期待を裏切らない好演をみせる。ビール会社の社長が誘拐される事件が発生。“レディ・ジョーカー”を名乗る犯人グループから脅迫状が会社へ届く。数日後、社長は解放されるが、今度は会社への脅迫が始まり……。", "2004-12-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Expatriate", "ジ・エクスパトリエイト（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-06-30", "1,10,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カルメン故郷に帰る", "カルメン故郷に帰る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "東京でストリッパーをしているリリィ・カルメンこと、おきんは仲間のマヤ朱実を連れて里帰りする。おきんと朱実の派手な服装と突飛な行動で村は大騒ぎ。おきんの父は熱を出して寝込んでしまう。しかし、カルメンもマヤも自分たちは芸術家であると信じているので“芸術のために”ストリップ公演をこの村で敢行。大成功を収めて意気揚々と東京へ帰っていく。本作品は日本初の総天然色の長編映画である。しかも使われたフィルムは国産のフジカラー。この国家偉業的作品が木下惠介の手にゆだねられた経緯は、日本映画監督協会が当時最も景気良好だった松竹に要請し、その松竹で興行的に最も安定した力を持っていたのが木下だったことによる。木下得意のユーモア作品で、小林正樹、松山善三、川頭義郎の３人が助監督についている。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hit So Hard", "HIT SO HARD", "", "", "http://image.eiga.k-img.com/images/movie/58055/315x210.jpg?3760", 103, "故カート・コバーンの妻コートニー・ラブが率いるバンド「HOLE」の元ドラマーで、1990年代のアメリカン・オルタナティブ・ロックシーンを駆け抜けた女性ドラマー、パティ・シュメルの半生を描いたドキュメンタリー。92年から98年まで「HOLE」でドラムを担当していたパティだったが、少女期から始まっていたアルコール依存症、薬物中毒、同性愛者であるが故の苦悩などにより、どん底の生活を味わう。その波乱の人生を、パティ本人が撮りためていたホームビデオの映像をもとに描く。映像の中には、カートとコートニーの未公開映像や、2人が共作した未発表曲の演奏シーンなども含まれている。", "2012-04-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二重心臓", "二重心臓", "", "", "http://image.eiga.k-img.com/images/movie/53975/315x210.jpg?3145", 80, "幻想文学の巨匠・夢野久作の同名小説を、「白椿」の秋原正俊監督＆畑野ひろ子主演で映画化したミステリードラマ。ある朝、呉服橋劇場の興行主が刺殺体で発見された。事件の犯人が自首したと聞かされた新米女性刑事の文月は、真犯人が別にいるのではないかと疑問を抱きはじめる。一方、被害者の遺産を相続した美しい娘・呉羽は、彼女を犯人だと疑う劇場の支配人・笠から、口止めの条件として結婚を迫られ……。", "2008-10-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸の朝風", "江戸の朝風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "いいなずけの織女を狙う悪党・内藤甚吾に海に突き落とされた黒潮太郎は、３人組の密輸船の海賊に拾われる。だが彼らの船も嵐で難破。それぞれ、娘や妹のことを気にかけて、無人島で死んでいった３人の海賊の遺志を、太郎は継いでやろうと江戸へ出るが……。片岡千恵蔵主演による娯楽時代劇。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CUBA", "さらばキューバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1958年、カストロに打倒される寸前のバチスタ政権に軍事顧問として雇われた英国人ロバートは、初恋の女性アレックスと再会するが……。不穏な情勢の中で、思いを募らせていく男と女を、S・コネリー、B・アダムスの二人が好演している。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若親分兇状旅", "若親分兇状旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "海軍以来の親友・高木少佐の自決の謎を探るべく、軍港に近い町にやって来た南条武。彼はかつての高木の部下・金杉と、東西汽船の社長・丸屋との武器空輸が、高木の死に関係あることを突きとめる。「ある殺し屋」などで知られる森一生によるシリーズ第７作。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATHER OF THE BRIDE", "花嫁の父", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "一人娘を嫁に出す父親の複雑な気持ちをユーモアを交えながら描く父と娘の物語。トレーシーが、怒ったり、笑ったり、涙を流したりと父親の心理を巧みに演じている。当時18歳だったテイラーが輝くばかりに美しい。続編「可愛い配当」も製作された。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄門", "地獄門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "イーストマン・カラーを使用した最初の作品。原作は菊池寛の『袈裟の良人』で、1950年の「羅生門」のヴェネチア映画祭金獅子賞受賞により勢いに乗る大映が、海外受けを狙って作った作品。時は平清盛全盛の頃、清盛の留守を狙い反清盛派が都で乱を起こす。平康忠は上皇とその御妹上・西門院を救うため、身代わりの女・袈裟を荷車に乗せ、その警護を盛遠に任せる。盛遠は任務を果たすが、袈裟に惚れ、彼女の夫との間に確執が起こる……。絢爛たる時代絵巻といった印象で、色鮮やかな美術や衣裳は、色彩指導に画伯の和田三造を起用しただけに、見事である。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LADY TAKES A FLYER", "世界を駈ける恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アメリカ空軍の元大佐マイクは、旧友であるパイロット養成学校の教官アル、そこの生徒である女流パイロット、マギーの３人で払い下げ飛行機の空輸会社を作り上げた。世界中を飛びまわるうちに、マイクとマギーは恋に落ちていく……。J・アーノルドが荒っぽいパイロットの世界に描くラブ・ストーリー。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOST IN SPACE", "ロスト・イン・スペース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "1960年代に人気を呼んだTVシリーズ“宇宙家族ロビンソン”をリメイクしたSFアトベンチャー。2058年、人類は異星への移住計画を進め、そのモデル家族としてロビンソン博士の一家が宇宙に飛び立った。ところが、反乱軍の陰謀によって一家は銀河系の外に放り出され、広大な宇宙で迷子になってしまう。その後、彼らは不気味な宇宙船内や、時間の歪んだ謎の星で冒険を繰り広げ……。危機が連続する冒険活劇の基本を踏まえて、ワクワクするような物語を創造。スリリングな展開に加えて家族愛のエピソードも盛り込まれ、温かみを感じさせるSFドラマとなっている。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上海 支那事変後方記録", "上海 支那事変後方記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "陸軍省が国民の戦意昂揚に役立てようとして画策、製作したドキュメンタリー映画。しかし、事実をあくまで冷静に捉えたカメラ、生々しい現地録音、疲れ果てた行軍兵士たち、さらにはそれを見守る中国人たちの冷たい眼……。と製作者の意図に反した作品に仕上がっている。戦場の厭戦気分を鋭い眼力でつかんだ日本記録映画の畏父・亀井文夫の真骨頂。なお、亀井のクレジットは構成となっている。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プランゼット", "プランゼット", "", "", "http://image.eiga.k-img.com/images/movie/55265/315x210.jpg?1422", 53, "2053年の地球。人類は宇宙から突然現れた謎の生命体の侵略により、人口の大半を失ってしまう。人類の反撃はことごとく失敗し、日本方面軍・富士基地は敵拠点を攻撃する最終作戦「プランゼット」計画を実行に移す。基地の死守を命じられたロボット兵器の搭乗員・明嶋大志は、その作戦を前にたった1人の家族である妹のこよみを安全な火星へと避難させるが……。監督は「惑星大怪獣ネガドン」の粟津順。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シルバー假面", "シルバー假面", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "1970年代にテレビ放映された特撮ヒーロードラマ「シルバー仮面」を、舞台を現代から大正時代に移してリメイク。3話構成で、本作の公開直前に急逝した実相寺昭雄が総監修と第１話の監督を担当。大正9年の東京で、不可解な美女連続殺人事件が発生。諜報機関から密命を受けた本郷大尉は、捜査の途中で日独ハーフの美女ザビーネと出会う。本郷がクモ型宇宙人に襲われると、ザビーネは銀色の超人・シルバー假面となって宇宙人を撃退する。", "2006-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lost Harmony ロストハーモニー", "Lost Harmony ロストハーモニー", "", "", "http://image.eiga.k-img.com/images/movie/56945/315x210.jpg?4657", 87, "広瀬アリス、吉谷彩子、高畑充希ら注目の若手女優が出演するホラーサスペンス。コンクールに向けた強化合宿にやってきた南ヶ丘女学院合唱部の生徒たちは、想定外の事情で当初の宿泊所が変更になり、湖畔のコテージに宿泊することに。仲間内で流行している携帯タロット占いで当番を決めるなど盛り上がる合唱部の面々だったが、突然、占いの画面に人体の一部らしき不気味な画像が表示される。やがて練習を開始した合唱部のメンバーが1人また1人と何者かに襲われはじめ、14年前に起こったある事件の隠された秘密が浮かび上がってくる。", "2011-12-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GRASS IS GREENER", "芝生は緑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "H・ウィリアムズ夫妻原作の舞台劇を映画化。英国の大邸宅に住む夫婦のもとに、アメリカの石油王が来訪。彼が間違えて夫人の私室に入ってしまったからさぁ大変……。他愛ない恋のさやあてゲームを、洗練された語り口で綴ったコメディ・ドラマの佳作。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("満員電車", "満員電車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「プーさん」「億万長者」に続く市川＝和田の夫婦コンビによる風刺喜劇。人間のあふれた日本。社会人１年生の青年は、父親が発狂し、彼自身も様々な病気や失業と闘いながら生きていくが、次第に落ちぶれていく。軽いタッチで封切り当時は“映画漫画”といわれた。特に、冒頭の大学の卒業式のシーンはまさに漫画的。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋する弁当男子 TWILIGHT FILE VIII", "恋する弁当男子 TWILIGHT FILE VIII", "", "", "http://image.eiga.k-img.com/images/movie/57085/315x210.jpg?3378", 74, "オムニバスシリーズ「TWILIGHT FILE」第8弾の1作。ひょんなことから弁当作りを始めた主人公が、料理対決に挑む姿を描く青春ストーリー。過去に食べたある弁当がトラウマになり、何事もあきらめながら生きている新米雑誌編集者の亮は、高校時代の同級生・佐伯小夜に偶然再会。そのことをきっかけに少しずつ輝きを取り戻していく。そんなある日、亮はフードライターの行きつけの店へ連れていかれ……。主演は斉藤祥太。共演に元「モーニング娘。」の飯田圭織。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ムーランルージュの青春", "ムーランルージュの青春", "", "", "http://image.eiga.k-img.com/images/movie/56605/315x210.jpg?2606", 109, "1931年（昭和6年）の大みそかに開館し、51年の閉館までの20年間にわたってさまざまな芝居やレビューを上演した劇場「ムーランルージュ新宿座」にまつわるドキュメンタリー。明日待子、森繁久弥、三崎千恵子ら多くの名優を輩出した同劇場について、元ムーラン女優の大空千尋ら当時を知る関係者の貴重な証言や資料をもとに構成される。", "2011-09-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅の覇道", "修羅の覇道", "", "", "http://image.eiga.k-img.com/images/movie/56285/315x210.jpg?2933", 71, "任侠（にんきょう）エンタテインメント「修羅の統一」シリーズの新章。眞皇会の5代目会長・松田源治が引退を表明。京浜一家を率いる内星竜也が、理事長の職を全うした功績からも、跡目を継ぐ最有力候補と目される。しかし、内星の若さなどを理由に反対する者も少なくはなく、新たな抗争が始まる。主演はドラマ「相棒」「水戸黄門」などにも出演した原田龍二。共演に竹中直人、哀川翔らが顔をそろえる。", "2011-04-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN AMERICAN TAIL", "アメリカ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "アメリカへ渡るロシアのネズミ一家。その途中で離れ離れになったネズミのファイベルがたどる冒険を、楽しい数々の歌で綴った長編アニメーション。当時、アメリカ長編アニメ映画史上最大のヒット作となった、ディズニーに対するスピルバーグのオマージュ的作品。リンダ・ロンシュタットの歌った主題歌がアカデミー賞を受賞した。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Adventures of Tintin: The Secret of the Unicorn", "タンタンの冒険　ユニコーン号の秘密", "", "", "http://image.eiga.k-img.com/images/movie/55155/315x210.jpg?3349", 107, "世界各国を飛び回る少年記者タンタンと、相棒の白いフォックステリアのスノーウィが繰り広げる冒険を描き、世界中で親しまれているベルギーの漫画家エルジェによる「タンタンの冒険旅行」を3D映画化。スティーブン・スピルバーグがメガホンをとり、ピーター・ジャクソンが製作を務める。17世紀に洋上でこつ然と消えた帆船「ユニコーン号」の模型を偶然手にしたタンタンが、模型のマストに隠された暗号を解きユニコーン号の財宝を手に入れようとする者たちに追われながら冒険に出る姿を描く。", "2011-12-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トワイライトシンドローム　デッドクルーズ", "トワイライトシンドローム　デッドクルーズ", "", "", "http://image.eiga.k-img.com/images/movie/53755/315x210.jpg?3357", 0, "人気ホラーアドベンチャーゲーム「トワイライトシンドローム」から生まれたノンストップアクションホラー。「リアル鬼ごっこ」の監督・柴田一成がプロデュース、「オトシモノ」の古澤健が監督・脚本を担当。高校時代の友人と船旅に出かける6人の若者たち。船上でいじめられっこの絵里が現実さえも変えてしまう悪夢のようなゲームを見つけ、軽い気持ちからいじっめっこの優子を殺してしまい……。", "2008-08-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ali Baba et les quarante voleurs", "アラブの盗賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "説話集「アラビアンナイト」の「アリババと40人の盗賊」を、「現金に手を出すな」の名匠ジャック・ベッケル監督が映画化。ある日、盗賊団の首領アブドゥラが「開け、ゴマ！」という合言葉を唱えて洞窟に入っていくところを目撃したアリババ。同じように合言葉を唱えて盗賊団のアジトに潜入した彼は、中に大量の金銀財宝が隠されているのを発見する。彼は大喜びでその財宝を持ち帰り、大金持ちになるのだが……。", "1954-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超能力者", "超能力者", "", "", "http://image.eiga.k-img.com/images/movie/56825/315x210.jpg?2329", 113, "「デュエリスト」「義兄弟」のカン・ドンウォンと「白夜行」のコ・スのダブル主演で超能力者と超能力の通じない男の運命的な対決を描く。人々を意のままに操ることができる超能力を持つ男チョインは、その力ゆえに孤独な人生を歩んでいた。ある日、金を盗みに小さな質屋に入ったチョインは、そこで働くごく普通の青年ギュナムに超能力が通じないことに慌て、誤って店主を殺害してしまう。ギュナムは世話になった店主の仇討ちに燃えてチョインを追い、チョインもまた唯一秘密を知るギュナムの命を狙うが……。", "2012-03-10", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HERO", "ヒーロー／靴をなくした天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "炎上する飛行機事故の渦中に、偶然そこに居合わせた名も知れぬ人物が危機に瀕する乗客たちを救出して消えた。乗客の一人だったTVレポーターは、この謎の人物を希代の英雄として追跡しようとする。が、その人物はただのしがないコソ泥だった……。世間が待望し、マスコミが祭り上げるヒーロー・イメージと現実との落差を通して、自由と民主主義を謳うアメリカ社会をアイロニカルに見つめ直す風刺コメディ。ビデオ、DVDタイトルは「ヒーロー 靴をなくした天使」。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新選組", "新選組", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "市川崑らしい実験精神で構築された異色の“新選組”談。黒鉄ヒロシの漫画をウレタンボードに貼って、人形のように立たせ、役者に見立てる。アニメーションとも紙芝居とも異なる“奥行きある平面性”で、おなじみ近藤勇、土方歳三、沖田総司らの血気と破滅を追っていく。モノトーンの画面に飛び散る真っ赤な血しぶき、瞬間挿入される実写や写真。光と影の技術を駆使した緩急見事な演出から、映画ならではの気品と醍醐味が匂い立つ。声優陣も豪華で、特に土方を妙演する中井貴一と、語り口で酔わせるナレーターの江守徹はお見事。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fugitive", "逃亡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "　優秀な外科医キンブルは妻殺しの容疑で逮捕されてしまうが、護送途中に起こった事故に乗じて逃亡を図る。自らの潔白を証明するため、片腕の男を捜し始めるキンブル。一方、連邦保安官ジェラードがキンブルの追跡を開始。逃亡を続けるキンブルは真犯人を見つけ出すことができるのか？　往年の人気テレビドラマを映画化。トミー・リー・ジョーンズ演じるジェラードを主人公にしたスピンオフ作品「追跡者」も製作された。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("気まぐれ冠者", "気まぐれ冠者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "伊丹万作の22本に及ぶ監督作品のうち、今見られるものが３本しかないという事態は日本映画界最大の悲劇の一つであるが、本作は翌年の名作「赤西蠣太」に匹敵する意義を持つ貴重な作品である。サイレント時代の伝説的作品「国士無双」をはじめとする伊丹のナンセンス・コメディの系譜を今に残す唯一の作品であるからだ。戦国時代、ある国のバカ殿が、好戦的な隣国を抑えるために気まぐれ冠者を送り込む。彼は闘鶏を国中にはやらせて戦意喪失に成功する、というばかばかしい話をチープなセットとストロー級の軽い演出で描き切る。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ストライクウィッチーズ劇場版", "ストライクウィッチーズ劇場版", "", "", "http://image.eiga.k-img.com/images/movie/56035/315x210.jpg?1910", 0, "2008年、10年に計2シーズンがTV放送されたほか、小説、コミック、OVAなどメディアミックス展開している人気アニメーションの劇場版。魔法力を持つ「ウィッチ」と呼ばれる10代の少女たちが、異形の侵略者から人々を守るため戦う姿を描く。第501統合戦闘航空団・通称「ストライクウィッチーズ」はその役目を終えて解散し、メンバーのウィッチたちは各地へ散っていた。その中のひとり、宮藤芳佳は、先の戦いで魔法力を失っていたが、人々を救うため医師の道を目指して勉学に励んでいた。欧州留学が決まり、最先端の医学が学べると喜んだ芳佳だったが、欧州では不穏な動きが広がりはじめ……。", "2012-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bringing Up Baby", "赤ちゃん教育", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "若き古生物学者デビッドは、勤め先の博物館に百万ドルの寄付をしてくれるランダム夫人の弁護士に会いにゴルフ場へ向かうが、そこに突然現れたスーザンという娘のためにゴルフと夕食は台無しになってしまう。翌日からデビッドはスーザンに振り回されるハメになり……。今やホークスのスクリューボールコメディの中でも代表作と言われる作品だが、公開当時は興行的には失敗しており、時を経て次第に真価が理解されるようになった傑作。", "1938-08-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STEPFATHER", "W〈ダブル〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "理想のアメリカン・ファミリーを夢見るヘンリーは、夢破れると殺人鬼と化し、一家を滅亡させてしまうという男である。そのヘンリーが、スーザンとステファニー母娘のところへ家族として迎え入れられる。継父のことを好きになれないステファニーは、ヘンリーの身辺調査を始めた。そのことを知ったヘンリーは、理想が音をたてて崩れていくのを感じていた。そして彼は殺人鬼へと変貌する。ヒッチコックの「サイコ」を想起させる、二重人格を扱った本作は、一流のサスペンスとして高い評価を得た。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若さま侍捕物帖 魔の死美人屋敷", "若さま侍捕物帖 魔の死美人屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "大奥で女中をする、おいくが実家の蔵で殺されるという事件が起きる。さっそく捜査に乗り出した若さまは、第一発見者の文次郎が犯人を知っているとにらむが、彼も殺されてしまう。そんな折、将軍の妾おえんの父・阿部伊予之助の命で捜査が打ち切られ……。若さまが幕府転覆の陰謀を暴く推理時代劇。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("草房子", "草ぶきの学校", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "中国の人気作家ツァオ・ウェンシュアンの自伝的小説を映画化した感動作。農村を舞台に、哀しみや喜びなど、様々な経験を通して成長していく少年の姿をみずみずしく描き出す。1962年、農村にある草ぶき屋根の学校に通うサンサンは、校長でもある父に甘えることができず寂しい思いをしている。何やら秘密を抱えた転校生の少女や、家が破産して学校をやめた子、先生の悲恋など、数々の出来事を経て、彼は少しずつ大人になっていく。", "1999-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　SECONDO　JUNGLE　BOOK　MOUGLI　AND　BALOO", "ジャングル・ブック　少年モーグリの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "R・キプリングの有名小説をもとにしたファミリー向けアドベンチャー作品。1890年のインド、ジャングルの奥深くで動物たちと暮らしていた少年モーグリは、英国人のスカウトマン、ハリスンに姿を見られてしまう。やがて、伝説の古代都市で、モーグリと彼をサーカスに売り飛ばそうとするハリスン、そしてギャング猿軍団の三つ巴の戦いが始まるのだった……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Angry Hills", "怒りの丘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　1941年、アメリカの海外特派員マイクはギリシャの指導者からレジスタンスの名簿を託される。やがてマイクはナチスに狙われることに……。アルドリッチ監督がハリウッドを離れて作った作品。", "1959-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AGENCY", "THE エージェンシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "無意識下のうちに受け手の消費者を洗脳してしまうサブリミナル・アド。デオドラント・スプレーのCFに、対立する州知事候補妨害のアニメを紛れ込ませようという恐るべき謀略。ユニークな題材をアクション仕立てで展開した意欲的な作品。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG CHILL", "再会の時", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1960年代後半、平和と正義と愛の名のもと、反体制運動にのめり込み、強い連帯意識で結ばれていたミシガン大学の７人の学生たち。卒業後、彼らの大半が社会的成功を収めていた。しかし、友人の葬式で10年ぶりに再会すると、それぞれの境遇、人生観がまったく変わってしまったことを肌で確認し合い、新たな連帯感を抱いていく。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三本指の男", "三本指の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "スクリーンに名探偵・金田一耕助が初登場した記念すべき第１作。原作は、これもまた金田一が初めて小説に登場した『本陣殺人事件』である。中仙道の旧家・久保銀造の姪・克子と、名門一柳家の御曹司・賢造との間に縁談が持ち上がる。しかし、謎の“三本指の男”から、これを邪魔しようとする脅迫状が舞い込んだ……。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Het Nieuwe Rijksmuseum", "ようこそ、アムステルダム国立美術館へ", "", "", "http://image.eiga.k-img.com/images/movie/55485/315x210.jpg?2579", 120, "オランダの女流ドキュメンタリー作家ウケ・ホーヘンダイクが、トラブル続きの改築工事で何年も閉館状態のアムステルダム国立美術館の舞台裏に迫る。2004年、レンブラントの「夜警」やフェルメールの「牛乳を注ぐ女」など数々の傑作を所蔵する同美術館は、開館以来の大規模な改築工事を始める。スペイン人建築家“クルス＆オルティス”が新美術館の設計を担当することになるが、美術館を貫く交通路が閉鎖されることに市民からの猛反発が起こり、計画はとん挫してしまう。", "2010-08-21", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Panic in Needle Park", "哀しみの街かど", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ファッション・カメラマン出身のJ・シャッツバーグ監督がニューヨーク・オール・ロケで描き出す、若い男女の転落の青春ドラマ。自由と幸福を夢見ながらも、男が麻薬常習者であったことから、女も麻薬に取り憑かれていき、ついに女は麻薬欲しさのあまり男の兄にまで体を売るようになってしまう。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ落語　落語研究会　昭和の名人　参", "シネマ落語　落語研究会　昭和の名人　参", "", "", "http://image.eiga.k-img.com/images/movie/56935/315x210.jpg?3315", 134, "東京・国立劇場小劇場で毎月開催されているTBS「落語研究会」の映像を映画館のスクリーンで上映する「シネマ落語」第3作。桂吉朝の「不動坊」（2003）、5代目三遊亭圓楽の「助六伝」（1982）、3代目古今亭志ん朝の「三方一両損」（88）、10代目金原亭馬生の「鰍沢」（75）を上映し、昭和の噺家たちの貴重な名演を堪能できる。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILLOW", "ウィロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "神話と魔法の国を舞台に、暗黒の世を救う“赤ん坊”を助けるウィローと放浪剣士マッドマーティガンが、悪の女王と対決するまでの活躍を描いたSFXアドベンチャー。ILMによる見事な特撮と、ノンストップ・ストーリーに注目。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FE＾TE A｀ HENRIETTE", "アンリエットの巴里祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "名匠デュヴィヴィエが軽妙なタッチで描く、コメディの快作。片や犯罪や流血ざたが大好き、片やロマンティックで純情なお話が大好きという二人の性格の異なるシナリオ作家が、共同でシナリオをまとめるよう依頼されたことから起こる珍騒動を描く。シャレっ気たっぷりで楽しめる一編。1963年に「パリで一緒に」というタイトルでウィリアム・ホールデン、オードリー・ヘプバーンの共演でリメイクされた。", "1952-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Girl's Life", "Girl's Life", "", "", "http://ecx.images-amazon.com/images/I/61TfvzPK6CL._SL210_.jpg", 83, "ファッション誌「小悪魔ageha」の専属モデル、桜井莉菜が映画初主演に挑んだガールズムービー。家族に見放され、彼氏にも裏切られてしまった一之瀬ハルカは、唯一の味方である亀のジミーを連れて上京する。しかし、派手すぎるファッションや住所不定が災いして仕事が見つからず、ネットカフェ難民となってしまう。そんなある日、キャバクラの求人広告を見つけたハルカは、すがる思いで入店を決意するが……。", "2009-12-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Saw VI", "ソウ6", "", "", "http://image.eiga.k-img.com/images/movie/54875/315x210.jpg?3822", 90, "大ヒット・スリラー「ソウ」シリーズの第6弾。前作でFBI捜査官のストラムが死体となって見つかるが、指紋が検出されなかったことから殺人鬼・ジグソウの後継者はストラムではないかという憶測が立てられる。しかし、ストラムの上司・エリクソンは刑事ホフマンを疑って接近していた。監督はシリーズを通して編集を担当してきたケビン・グルタート。", "2009-11-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MIND SNATCHERS", "クリストファー・ウォーケンの　マインド・スナッチャー　～狂気の人体実験～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "政府の保護のもと、ベトナム帰還兵を対象に行われている特殊療法の実態は恐るべき人体実験だった。理性を破壊し人間を完全な兵士に造り変えようとするマッド・サイエンティストの陰謀に気付いた一人の男が戦いを挑む。C・ウォーケンの初主演作。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The List of Adrian Messenger", "秘密殺人計画書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　莫大な財産を狙い、次々と殺人を繰り返していく犯人。元イギリス情報局の調査官アンソニーは捜査を開始するが、犯人は巧みにその身分を隠していた……。凶悪な犯罪者を追う男の活躍を描いたサスペンス・ドラマ。トニー・カーティス、カーク・ダグラス、バート・ランカスター、ロバート・ミッチャム、フランク・シナトラといった豪華なゲストスターたちが変装して出演。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("賭神３之少年賭神／GOD　OF　GAMBLERS　３−THE　EARLY　STAGE", "ゴッド・ギャンブラー 賭神伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「ゴッド・ギャンブラー」「ゴッド・ギャンブラー　完結編」に続く、シリーズ第３作。賭博の胴元の娘セブンはある賭場で、コウ・チャンという青年と出会う。彼こそ、後に“ゴッド・ギャンブラー”と呼ばれるギャンブルの天才だった。セブン、そして彼女のボディガードのドラゴンと友情で結ばれるようになったコウは彼らとともに、第１回全世界賭神位決定戦に参加。だが、優勝目前、マフィアのボスから負けるように命令されたコウはこれを拒絶したため、銃で撃たれて瀕死の重傷を負う。セブンの看病を受けながらコウは戦い続けるが……。", "1997-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HOUSE OF THE SPIRITS", "愛と精霊の家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "南米のある富農一家の、20世紀初頭から50年に及ぶ喜びと苦悩の年代記。貧しい金鉱掘りから一代で国会議員にまでなった頑迷な牧場主エステバンを軸に、超能力を持つ妻のクララ、主の教えのままに寂しく死んだその姉フェルラ、共産革命の時代を生きる反抗的な娘ブランカなどの挿話が描かれる。南米が舞台だが、ハリウッドスターを配してセリフは英語、ロケはポルトガル、セット内は北欧で撮影されたという珍しい作品。", "1993-01-01", "7,18,25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヱヴァンゲリヲン新劇場版：Q", "ヱヴァンゲリヲン新劇場版：Q", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "社会現象を巻き起こした人気TVアニメ「新世紀エヴァンゲリオン」を新たに描きなおす「新劇場版」シリーズ第3作。", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄騎兵、跳んだ", "鉄騎兵、跳んだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "モトクロスに青春を懸ける青年が、傷つきながらも危険なレースに臨み、トップを目指して爆走する姿を、ロマンスを絡めて描いたスポーツ青春映画。ジョー山中が作詞作曲、松田優作が歌った主題歌が印象的。ラストの凄絶なバトルも見逃せない。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・団体列車", "喜劇・団体列車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "四国の小さな駅に働く山川彦一は30歳で独身、しかも助役試験の万年落第生。そんな彼が美しい小学校教師に一目惚れしてしまい……。未亡人の女教師に純情をかたむける男を、渥美清が熱演。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Daredevil", "デアデビル", "", "", "http://image.eiga.k-img.com/images/movie/1045/315x210.jpg?3086", 103, "監督はジョン・アービング原作の「サイモン・バーチ」のマーク・スティーブン・ジョンソンだが、アクション監督は「マトリックス」のユエン・ウーピンの弟で、「マトリックス」の「2」「3」を兄と共に手がけるユエン・チョンヤン。彼は「チャーリーズ・エンジェル」シリーズも担当している。撮影は「ワイルド・スピード」のエリクソン・コア。売れっ子コリン・ファレルが、すぐキレるナイフの達人ブルズアイ（＝大当たり）役を怪演。人気コミック作家、スタン・リーとフランク・ミラーのカメオ出演もファンは要チェック。", "2007-04-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КИН−ДЗА−ДЗА／KIN−DZA−DZA", "不思議惑星キン・ザ・ザ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "旧ソ連グルジア共和国出身の監督が撮った、何とも奇妙な趣のSF空想コメディ。街中で裸足の男が持っていた空間転移装置によって、キン・ザ・ザ星雲の砂漠の星ブリュクにワープさせられた建築技師と学生。彼らはマッチが貴重な価値を持つこの惑星で、いい加減な宇宙人たちに手を焼きながらも、何とか地球に帰ろうと策を練るのだが……。ストーリーからは資本主義への風刺のような視点も読みとれるが、この映画のチャーム・ポイントは、砂漠の向こうから飛んで来る飛行物体などのアバンギャルドでチープなメカ・デザインにある。なぜか礼儀にうるさい宇宙人たちが発する“クー”という奇声もいちいちシュールで楽しい。あまりにも全体的にのんびりしているが、一度見たら忘れられない作品であることは確かだ。", "1983-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SOLDIER", "ザ・ソルジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ワシントン、ベルリン、モスクワ、テルアビブの４都市を舞台に、世界を破壊させようとする国際テロリスト・グループに立ち向かう、コード・ネーム“ザ・ソルジャー”の活躍を、劇画タッチで描いたバイオレンス・アクションの大作。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あの鷹巣町の　その後", "あの鷹巣町の　その後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 180, "日本一の福祉の町といわれた秋田県鷹巣町の“その後”に迫ったドキュメンタリー。1991年に町長となった岩川徹氏のもとで高レベルの高齢者福祉を築きあげてきた鷹巣町。しかし2003年の町長選挙で岩川氏が大敗し、「福祉のやりすぎだ」という新町長によって町の福祉政策は大きく方向転換していく。これまで10年以上にわたって鷹巣町を取材してきた羽田澄子監督が町の驚くべき変化をとらえ、日本の福祉が直面している様々な問題を浮き彫りにしていく。", "2006-03-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Robopocalypse", "ロボポカリプス（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Once in a Lifetime", "ペレを買った男", "", "", "http://image.eiga.k-img.com/images/movie/53115/315x210.jpg?1921", 97, "70年代、ペレやベッケンバウアーら世界中のスーパースターを集め、サッカー後進国アメリカに一大ブームを巻き起こしたクラブチーム“ニューヨーク・コスモス”。メディア王スティーブ・ロスが仕掛けた伝説のドリームチームの栄光と崩壊を描くドキュメンタリー。監督は「リヴ・フォーエヴァー」のジョン・ダウアーと「ライディング・ジャイアンツ」の編集を手掛けたポール・クロウダー。ナレーションをマット・ディロンが担当している。", "2007-12-08", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Human Stain", "白いカラス", "", "", "http://image.eiga.k-img.com/images/movie/1745/315x210.jpg?2408", 108, "人種問題を描くフィリップ・ロスのベストセラー「ヒューマン・ステイン」を「クレイマー、クレイマー」「プレイス・イン・ザ・ハート」のロバート・ベントン監督が映画化。98年の米マサチューセッツ州。名門大学の教授コールマンは、黒人学生への発言を人種差別だと批判されて辞職に追い込まれる。失意の日々を送る彼は、掃除婦フォーニアと出会って恋に落ち、誰にも言えなかった秘密を彼女に告白することを決意する。", "2004-06-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森繁の新婚旅行", "森繁の新婚旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "東京から愛媛の田舎に左遷されてきた新聞記者の森文吾は、その町の名家で平清盛の子孫という娘と結婚し、東京に新婚旅行にやって来る。だが高校の修学旅行の団体が一緒について来てしまう……。森繁久彌が最もノッている頃のテンポのよいコメディ。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In Time", "TIME タイム", "", "", "http://image.eiga.k-img.com/images/movie/57415/315x210.jpg?2348", 109, "「ガタカ」のアンドリュー・ニコル監督が、ジャスティン・ティンバーレイクとアマンダ・セイフライドを主演に迎えて描くSFアクションサスペンス。科学技術の進歩によりすべての人間の成長が25歳で止まり、そこから先は左腕に埋め込まれた体内時計「ボディ・クロック」が示す余命時間だけ生きることができる近未来。貧困層には余命時間が23時間しかない一方で、富裕層は永遠にも近い時間を手にする格差社会が生まれていた。ある日、ひとりの男から100年の時間を譲り受けた貧困層の青年ウィルは、その時間を使って富裕層が暮らす地域に潜入。大富豪の娘シルビアと出会い、時間監視局員（タイムキーパー）の追跡を受けながらも、時間に支配された世界の謎に迫っていく。", "2012-02-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ASSAULT", "追想のかなた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 149, "ナチ占領下のオランダの小村、殺人事件を目撃したことで、一家がナチに連行されてしまい離散したという、忌まわしい過去を胸に秘め生きてきた男が、ある老人と出会ったことから、その事件に隠されていた意外な真実を知るというサスペンス・タッチのドラマ。", "1986-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("未来は僕らの手の中!!", "未来は僕らの手の中!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "中学受験のために塾通いをさせられている小６の宏一。彼の楽しみは、友人の崇、弟の伸二とともに作り上げた廃墟の秘密基地で遊ぶことだった。さらに転校生の女の子、あきらが加わり基地は４人のオアシスとなった。その基地のある廃墟がデパート建設のために取り壊されることを知った彼らは、土地を買収すべく５億６千万円を盗む計画を企て……。監督は、金秀吉はじめ、数多く若手監督を育てている日本映画学校出身の木村啓作。当初、卒業製作として作られた本作は、資金不足から在学中に完成せず、卒業後約４ヵ月を経てようやく完成したもの。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LACOMBE LUCIEN", "ルシアンの青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "第二次大戦末期のフランスの片田舎を舞台とした青春映画の名作。病院の掃除夫のルシアンは、ふとしたことからゲシュタポの手先となる。そんな彼がユダヤ人の美しい娘フランスに恋したことからスペインを目指しての逃避行が始まる。平凡な若者の運命を狂わせる戦争の悲劇。新人のブレーズとクレマンの演技がみずみずしい。", "1973-01-01", "2,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雨あがる", "雨あがる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "故・黒澤明が最後まで映画化を目指した企画を28年間助監督として師事した小泉堯史が完成させた心温まる時代劇。強い剣豪ながら仕官できない浪人・三沢伊兵衛とその妻たよの泊まる安宿には、雨があがるのを鬱々と待つ人々が大勢いた。心根の優しい伊兵衛は、賭け試合で金を都合し酒や食べ物を振る舞い人々の心を和ませる。あくる日、侍同士の果し合いに遭遇し懸命に彼らを止めた伊兵衛に、藩から剣術指南番の話が起こる……。黒澤組スタッフとゆかりのキャストが集結して、小泉の監督デビューを支え、黒澤の残した覚え書き通り、“見終わって晴れ晴れとした気持ちになるような作品”に仕上げた。また、多くの黒澤作品の音楽を担当した佐藤勝の遺作ともなった。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Triangle", "トライアングル", "", "", "http://image.eiga.k-img.com/images/movie/56425/315x210.jpg?3959", 99, "ヨットセーリング中に嵐に襲われたジェスらは、目の前に現れた大型客船に命からがら乗り込み難を逃れる。しかし、船内には人がいた形跡があるものの誰もおらず、探索していると覆面をした人物が現れ次々と命を奪われていく。1人生き残り甲板に逃げ出したジェスは、転覆したヨットから客船に向かって助けを求める自分たちの姿を目撃する。「0：34 レイジ34フン」のクリストファー・スミス監督が、豪華客船を舞台に描くシチュエーションスリラー。", "2011-08-06", "3,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hot Times", "ホット・タイムズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "リチャード・ギア主演の「ブレスレス」や、ロックンローラー、ジェリー・リー・ルイスの波乱に満ちた人生をデニス・クエイド主演で映画化した「グレート・ボールズ・オブ・ファイヤー」等の作品で知られるジム・マクブライド監督が1974年に手掛けた、ちょっとエッチなB級学園コメディ。ロスト・バージンに取り憑かれ、頭の中は女の子のことでいっぱいの男子高校生アーチーが仲間たちと繰り広げるドタバタ騒動を描く。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARIUS　ET　JEANNETTE", "マルセイユの恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "フランスでヒットした大人のラブ・ストーリー。監督夫人でもあるA・アスカリッドが、明るく懸命に生きるシングルマザーを好演してセザール賞主演女優賞に輝いた。マルセイユの下町エスタック。二人の子供を抱えるジャネットが、セメント工場で守衛をしている男マリウスと出会う。勝ち気なジャネットは無愛想なマリウスと度々口論を繰り返すが、彼のさりげない優しさに触れ、心を通い合わせていく。二人は友人や子供たちに見守られながら愛を育んでいくが、ある日、マリウスが姿を消す……。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blow Out", "ミッドナイトクロス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "　B級恐怖映画の音響効果マン、ジャックは風の音を録音中に自動車事故を目撃し、川に落ちた車からサリーという若い女性を救助する。彼は、同乗していた死亡者が次期大統領候補だったことを知り、さらに録音テープに収められた音から、これが狙撃によって起きた事件だと気づく。やがてサリーの正体も明るみになるが……。陰謀に巻き込まれながらも、真相を究明しようと奔走する主人公の姿を描いたサスペンス・スリラー。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A CAIXA", "階段通りの人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「アブラハム渓谷」で名高いオリヴェイラ監督が、一つの箱をめぐって貧しい人々の間で起こる悲喜劇を描く。舞台は現代のリスボン、“階段通り”と呼ばれる貧民街に住む人々の話題は、最近急に羽振りのよくなった盲目の老人のこと。どうやら、どこからか授かった箱に、通行人が恵みの金を入れてくれ裕福になったらしい。周囲の人々の嫉妬心が高まる中、その箱が誰かに盗まれるという事件が起こり……。", "1994-01-01", "25,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほとけ", "ほとけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ミュージジャン、作家の辻仁成が３度目のメガホンを執った寓話風の物語。港街に住むライは怒りや悲しみを表に出したことがなく、周囲から“ほとけ”と呼ばれるお人好し。毎日、鉄屑を集めて、巨大な仏像を作ろうとしていた。そんな彼が薄幸のマッサージ師ユマに恋をするが……。辻が青春時代を過ごした函館でロケを敢行し、透明感あふれる映像を見せる。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　OVERTURE", "風の前奏曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "タイに伝わる古典楽器“ラナート”の名手として、今もタイ国民の心にその名が刻まれるソーン・シラパバンレーンの伝記を基にした人間ドラマ。卓越した演奏技術と温かい人柄で多くの人に愛された彼の生涯が丹念に描かれる。本国では社会現象となるほど空前の大ヒットを記録した感動作だ。タイで昔から愛される伝統楽器“ラナート”の奏者を、幼い頃から志すソーン。多くの挫折を乗り越えながら彼は、その道を究めていく。", "2005-12-03", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREAKING GLASS", "ブレイキング・グラス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "デヴィッド・ボウイやT−REXのプロデューサー、T・ヴィスコンティが音楽を担当した本格的青春ロックムービー。名もなきシンガーだったケイトとマネージャー志望の青年が出会い、二人はスターの座を目指しながら愛し合うようになっていく。音楽業界の内幕をえぐりながら、青春の苦さを描く。", "1980-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Room with a View", "眺めのいい部屋", "", "", "http://ecx.images-amazon.com/images/I/51kjRapPwtL._SL210_.jpg", 115, "E・M・フォースター原作の同名小説をイギリスの名匠ジェームズ・アイボリー監督が映画化した恋愛ドラマ。まだ封建的な思想が強かった20世紀初頭。フィレンツェを訪れたイギリス名家の令嬢ルーシーは、ホテルの“眺めのいい部屋”を譲ってくれた青年ジョージから情熱的なアプローチを受ける。だが、階級や身分の違いを気にしたルーシーはジョージの思いを受け入れずに、イギリスに帰国。貴族の青年セシルと婚約するが……。出演はヘレナ・ボナム＝カーター、ジュリアン・サンズ、ダニエル・デイ＝ルイス。", "1987-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍神 LEGENDARY DRAGON", "龍神 LEGENDARY DRAGON", "", "", "http://ecx.images-amazon.com/images/I/6133BYVFX1L._SL210_.jpg", 108, "「週刊実話」で連載されて話題を集めた小説「龍神」を、原作者の高木淳也が自ら監督・主演・脚本・製作総指揮を務めて映画化した任侠アクション。やくざ組織「東神会」の構成員・鷹月は、敵対組織の幹部を射殺して懲役18年の実刑判決を受ける。長い獄中生活を終えて出所した彼は、時代の流れによってやくざ組織が弱体化している現実を目の当たりにし、街を牛耳るチャイニーズ・マフィアにひとり立ち向かっていく。", "2004-12-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A FEW GOOD MEN", "ア・フュー・グッドメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "キューバの海軍基地で殺人事件が起こった。若き海軍弁護士キャフィーはその事件に軍の腐敗構造が絡んでいることを突きとめる。しかしそれを暴くためには、法廷で海軍全体を敵に回さなければならない。四面楚歌のキャフィーに、果たして勝ち目はあるのか……。単なる法廷ものを超えた、見ごたえある一級の人間ドラマ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大尉の娘", "大尉の娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "大学教授、演劇活動などの経歴を経て1934年映画界入り、1935年「長崎留学生」で監督デビューした女性映画の名手・野淵昶による第３作。1914年公開のドイツ映画「憲兵モエビウス」が日本の新派に翻訳されたものを、再映画化した作品。信州の田舎村を舞台に、退役軍人の娘が村長の息子の子を宿したにもかかわらず、冷たくあしらわれていく様を描く。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("草食系男子。", "草食系男子。", "", "", "http://image.eiga.k-img.com/images/movie/55215/315x210.jpg?1343", 70, "2009年に流行した内気で消極的な男性を形容する言葉「草食系男子」をモチーフにしたコメディ。主演は﨑本大海。デザイン会社に勤務する新人Webデザイナーの草野羊一は、世話好きの心優しき青年。次々と勝気な女性に誘惑される羊一に、同僚で親友のひかりは密かに恋心を寄せていた。", "2010-02-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEETHOVEN’S ２ND", "ベートーベン2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "勇気と愛嬌を兼ね備えたオスのセントバーナード犬ベートーベンが活躍する第２作。飼い主のニュートン家は、みんな大忙し。やむなく単独散歩をきめこんだベートーベンは、ピンクのリボンをつけたメス犬と運命的な出会いを果たし、子供まで出来てしまう。ところが、メス犬の性悪な飼い主が子犬を売りさばこうとするのだ。ベートーベンはついに怒り……。犬もまた家族のために戦う、というファミリー・ピクチャー。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士Zガンダム A New Translation 星を継ぐ者", "機動戦士Zガンダム A New Translation 星を継ぐ者", "", "", "http://ecx.images-amazon.com/images/I/517FP46NA4L._SL210_.jpg", 95, "「機動戦士ガンダム」の続編として1985～86年に放映された「機動戦士Zガンダム」を、TV放映20周年を記念し劇場版として再構成した3部作の第1部。一年戦争終結から7年後の宇宙世紀0086年、ジオン残党狩りと称してスペースノイドに圧制をしく連邦軍のエリート部隊ティターンズと、それに対抗する反連邦組織エゥーゴとの抗争に巻き込まれた少年カミーユ・ビダンは、成り行きからエゥーゴに参加し、戦うことに。", "2005-05-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Laberinto de pasiones", "セクシリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「オール・アバウト・マイ・マザー」のペドロ・アルモドバル監督初期のエロティック・コメディ。情熱の国スペインの首都マドリッド。ロックミュージシャンでニンフォマニア（淫乱症）の女セクシリアは、ある日ライブで出会った青年リサにひと目ぼれしてしまう。ところが彼は実は亡命してきたティラン国の皇太子で、しかもゲイだった……。まだブレイク前のアントニオ・バンデラスが出演している他、監督自身も女装して歌を披露している。", "1982-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monsieur Lazhar", "ぼくたちのムッシュ・ラザール", "", "", "http://image.eiga.k-img.com/images/movie/57835/315x210.jpg?3366", 94, "カナダ・モントリオールの小学校を舞台に、担任教師の死に揺れ動く子どもたちと、そこへやってきた風変わりな代用教員との交流を描いた人間ドラマ。", "2012-08-31", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴルゴ13", "ゴルゴ13", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "さいとうたかをの人気劇画を映画化。世界最高の殺し屋ゴルゴ13を、高倉健が演じる。ゴルゴの標的は中近東のシンジケートのボス、ゴア。ゴルゴのアーマライトは彼を仕止めることができるのか……。健さん以外は、すべてイラン人。吹き替え処理が完成度を下げた。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Imaginarium of Doctor Parnassus", "Dr.パルナサスの鏡", "", "", "http://image.eiga.k-img.com/images/movie/53215/315x210.jpg?3302", 124, "「未来世紀ブラジル」のテリー・ギリアム監督によるファンタジードラマ。人々の隠れた欲望を形にする魔法の鏡「イマジナリウム」を出し物に一座を率いて旅するパルナサス博士は、かつて一人娘を16歳の誕生日に悪魔に差し出すことを条件に永遠の命を手に入れていた。娘の16歳の誕生日が3日後に迫る中、博士は新たに一座に加わった青年トニーとともに最後の賭けに出る。出演はパルナサス博士にクリストファー・プラマー、トニーに本作撮影途中で急死したヒース・レジャー。", "2010-01-23", "3,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arena", "キリング・ゲーム", "", "", "http://ecx.images-amazon.com/images/I/51QXdU25BRL._SL210_.jpg", 94, "", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の妻たち・地獄の道づれ", "極道の妻たち・地獄の道づれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "高島礼子が主演する“極道の妻たち”新シリーズの第４作。全国の極道が団結して連合組織を結成しようとしていたが、山背組組長代行の掛川はこれに反対したため罠にはめられ、服役してしまう。そして彼に代わってその妻、律子が山背組を束ねることに。連合側の圧力に山背組が揺らぐ中、律子が妹分としてかわいがる美智留の夫が殺される。その裏には組の若頭補佐、千田とその妻、槙子の陰謀が。槙子は、律子への嫉妬から夫をけしかけて成り上がろうとしていたのだ。やがて裏切りのすべてを知った高島＝極妻が怒りの反撃に出る。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPICEWORLD THE MOVIE", "スパイス・ザ・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1996年夏にデビューし、またたく間にポップ・シーンの頂点に登り詰めた英国の女性グループ“スパイス・ガールズ”の初主演作。彼女たちの５日間の物語を、ヒット曲を全編に散りばめてアップテンポに描く。E・ジョンやエルヴィス・コステロら豪華なミュージシャンたちがカメオ出演。ロイヤル・アルバート・ホールでの初ライヴを控えたスパイス・ガールズの５人は、リハーサルやパーティーで大忙し。明るさとパワーで仕事をこなしていた彼女たちだが、ライヴ前夜マネージャーと衝突して失踪してしまう。果たして、全世界が注目するライヴの日を無事迎えることができるのか!?", "1997-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朝の波紋", "朝の波紋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "高見順の新聞小説を五所平之助監督・高峰秀子主演で映画化した風俗メロドラマの快作。一流貿易会社の有能な社長秘書が、ライバル会社に勤める頼りない青年と知り合い、次第に彼の人柄に惹れていく。フランスを旅行していた高峰秀子の帰国第１作で、五所平之助が軽いタッチで演出した。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刑務所の中", "刑務所の中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "自身の服役体験を描いた花輪和一のベストセラー・コミックを映像化。人間クサいドラマには定評のある崔洋一が、刑務所暮らしの意外にノンビリした面にスポットを当てて、その中の人間模様をユーモラスに見つめている。銃刀法で捕まった花輪は厳しい規則に縛られながらも、のらりくらりと過ごしている。雑居房で見つかった陰毛の落とし主探し、作業場での労働、食事や家族に関する雑談。毎日が淡々と流れていくなかで、ある日、懲罰房に入れられた花輪は意外な発見をする…。ひょうひょうとした主人公を山崎努が妙演。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lady Ice", "ダイヤモンド・コネクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "避暑地マイアミを舞台に暗躍するダイヤ密売組織。私立探偵アンディはその組織の一角に社長令嬢の存在を発見する。バハマ諸島の風光をバックにした恋と犯罪のサスペンス・アクション。オニールの華麗なファッションが見もの。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画 Yes！プリキュア5 鏡の国のミラクル大冒険！", "映画 Yes！プリキュア5 鏡の国のミラクル大冒険！", "", "", "http://image.eiga.k-img.com/images/movie/53065/315x210.jpg?2535", 70, "女の子たちに大人気のテレビアニメ「Yes!プリキュア5」の劇場版。西洋風テーマパークに遊びに来た5人のプリキュアたちは、王子様姿に変身したココとナッツにエスコートされてお姫様気分を味わっていた。すると突然、世界征服を企む悪者シャドウによって、ココとナッツが鏡の国へさらわれてしまう。助けに向かったプリキュアたちの前に、彼女たちとそっくりな敵が現われ……。ゲスト声優として双子のお笑い芸人、ザ・たっちが参加。", "2007-11-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAWMPS！", "ハンプス 走れ！ラクダ騎兵隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1850年代、古き良き西部に馬ならぬラクダに乗りかえた騎兵隊が、次から次へと爆笑の砂煙を巻き上げながら砂漠を疾走する。製作・監督は全世界に「ベンジー」ブームを巻き起こし、大衆の心をしっかりつかんだJ・キャンプ。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Back to the Future", "バック・トゥ・ザ・フューチャー", "", "", "http://ecx.images-amazon.com/images/I/51kJ2oPDJQL._SL210_.jpg", 116, "スピルバーグが総指揮にあたりゼメキスが息もピッタリの演出をしたSFコメディの快作。始まりは1985年、高校生マーティはブラウン博士の開発した愛車デロリアンに乗り込む。これは、次元転位装置によって、時速140キロに速度が達するとタイム・スリップができる不思議な車だ。彼は一瞬のうちに1955年の世界へ。そこで若き日の自分の両親に会う。しかし、若き父親は内気で、未来の母に思いを打ち明けられず、恋に落ちる気配がない。これでは、将来彼らの子供となるマーティの存在が危ない！　そこで彼は……。奇想天外なストーリーを才気あふれるタッチで綴り、多くのファンの心をつかんだエンターテインメント作品。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE ＆ PASSION", "ラブ＆パッション 情事の虜たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "３年ぶりにバカンスでイタリアを訪れた倦怠期を迎えた一組の夫婦。彼らはそれぞれかつての恋人との再会に心躍らせ、妻は男の面影を求めて焦がれる思いを手紙に書き綴り、夫は奔放な恋人を伴い出張旅行に出かける。それぞれの情事に溺れる彼らだが……。たぎるような情念に満ちた大人のラブ・ストーリー。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Somebody to Love", "サムバディ・トゥ・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "イースト・ロサンゼルスのダンスクラブで、一曲１ドルで客の相手をする“タクシー・ダンサー”のメルセデス。彼女の夢はハリウッドで女優になること。ある日、店にメキシコ系の若者アーネストが現れる。彼はメルセデスの踊る姿に一目で虜となり無償の愛を捧げるが、夢を追い続ける彼女は彼のことなど眼中にない。ボーイフレンドのハリーにどうしても１万ドルが必要だと泣きつかれたメルセデスは、アーネストに何気なく１万ドルのことを話す。アーネストはメルセデスのために１万ドルを稼ごうと殺しの仕事を引き受けるのだが……。H・カイテル、A・クイン、S・ブシェーミら個性派俳優に加えて、S・フラー、Q・タランティーノ監督も出演している異色作。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IMMEDIATE FAMILY", "この愛の行方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "子供に恵まれない以外はすべてのことに順調なスペクター夫婦。養子をもらうことを決意した彼らは、産みの親として17歳の少女ルーシーを選んだ。無事に男の子を出産した彼女だったが、自分のお腹を痛めた子供を渡すことができず自ら育てる決心をする。だが父親であるサムは、仕事を失うなど子供を育てられる環境が作れなかった。社会派作品で知られるJ・カプランが里親制度について描いた心温まる作品。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大日向村", "大日向村", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "当時、すでに数々の文芸作品を手掛け成功を収めていた豊田四郎が、農民文学者の和田伝の原作により「土」の八木隆一郎と組み、前進座の俳優を使った、のちの農村映画のさきがけともなった傑作。満州に最初の日本移民による分村を作った、信州の一村の人々が、移住に踏みきるまでの人間模様を繊細かつ伸びやかに描く。だが、実態は農民の入植も当時の大日本帝国の意を体した侵略行為である。映画は農民の開拓者精神を賛美するのみで、今見ると居心地の悪さは否めない。豊田もそれに気付かぬはずもなく、後年この国策映画を撮らされた悔いを言葉にしている。42年後にその後の大日向村を描いた記録映画が作られた。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　HAUTES　SOLITUDES", "孤高", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "“ヌーヴェル・ヴァーグの恐るべき子供”として弱冠16歳から監督としての経歴を重ね、独自の映像感覚で多くのファンを持つP・ガレル。彼が、妻であるヴェルヴェット・アンダーグラウンドの歌姫ニコとジーン・セバーグという愛した女二人の姿を見つめ続けた作品。音声もなく、ストーリーもない映像の中で、ニコとジーン・セバーグが話し、笑い、泣き、時には自殺への衝動に突き動かされる姿を映し出していく。", "1974-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Next", "NEXT ネクスト", "", "", "http://image.eiga.k-img.com/images/movie/1775/315x210.jpg?4247", 95, "2分先の未来が見える能力を持つ男クリスは、米国内での核爆発を目論むテロリストの捜査に協力するようFBIに迫られる。最初は渋ったクリスだが、いつも見る未来に現れる女性が事件に巻き込まれることを知り、捜査に協力することに……。「ブレードランナー」「マイノリティ・リポート」の原作者フィリップ・K・ディックの短編小説「ゴールデン・マン」をニコラス・ケイジ製作・主演で映画化。監督は「007／ダイ・アナザー・デイ」のリー・タマホリ。", "2008-04-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RICH STUFF", "ラウラ・アントネッリのアモーレ・アバンチュール／水着もモラルも脱ぎ捨てて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "エーゲ海のほとりにある高級リゾート地。そこに集まるのは、性の快楽を求める男たち。日頃のウップンを晴らしに来た億万長者。言葉で表わせないほどイヤラシイことができると聞いてやって来た貴族。そして女たちはイイ男とのアバンチュールを求めている。セクシー女優競演のエロティック・ムービー。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("You Will Meet a Tall Dark Stranger", "ユー・ウィル・ミート・ア・トール・ダーク・ストレンジャー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TANGANYIKA", "タンガニイカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "タンガニイカの原始林で、伐採の許可を得て仕事を始めたジョンは、思わぬ障害にぶつかる。湖の近くにいる凶悪なヌクムビ族が、精神異常のイギリス人、マクラッケンに煽動され白人とみれば殺そうとするのだ。ジョンは狩猟隊を組織してマクラッケンを捕らえに出発、途中重傷を負っているダンや、ヌクムビ族と応戦している動物学者の妹らを救出する。しかし、ダンはマクラッケンの実の弟だった……。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE WATER SUMMER", "グレート・ストリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "山岳トレーニングを目的とする過酷なサマー・キャンプに挑戦した４人の少年と、彼らをリードする鬼コーチを描いたアドベンチャー・ドラマ。「フットルース」のK・ベーコンや「グーニーズ」のS・アスティンらが共演している。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPECTERS", "スペクターズ 鮮血のローマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ホラー映画の名優、D・プレザンス主演のイタリアン・スプラッタ・ムービー。ある日、ローマ郊外の遺跡で新たに洞窟が発見される。調査に向かったのが、大学で考古学の研究を続けるラスキー教授。ラスキーは洞窟の入り口で石版に刻まれた言葉を目にして戦慄が走る。そこには悪魔の到来が告げられていたのだ。さらに調査を進めると、悪霊を封じ込めたというこの遺跡にまつわる伝説が判明する。地下断面の解析で棺を発見し、地中深くまで潜るラスキー。彼の手が棺の蓋に触れたとき……。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の王国", "風の王国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "大阪を中心に作品を作り続ける三原光尋の青春映画。弱小高校野球部のピッチャー正男は、昼間は野球に、夜はエッチ・ビデオにと、青春を楽しんでいた。３年の春が来て、今年こそは甲子園だと意気込む正男の前に年上の女性・彩子が現れ……。関西の小劇団の役者が多数出演している。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の牙", "夜の牙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "石原裕次郎と井上梅次監督のコンビによるアクション・スリラー。ガード下で診療所を営む医師・杉浦は、ふとしたことから自分の戸籍が抹消されていることを知る。彼が内幕を探っていくうちに、裏で遺産相続が絡んでいることが明らかになる。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JFK", "JFK", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "いまだにその真相が明らかにされないケネディ大統領暗殺事件。その謎にO・ストーンが、ニューオリンズの地方検事ジム・ギャリソンの調査を足がかりにして大胆に挑む問題作。1963年11月ダラスで起きた大統領暗殺事件は、オズワルドという男の単独犯行として解決されたかに見えた。しかしその事件はあまりにも多くの疑問と矛盾を残したままだった。不透明なベールの向こうに隠された真の犯人は誰なのか？ そしてその動機は？　地方検事ギャリソンは炎のような執念で事件の核心に迫っていく……。実際のニュースなどを交えながら観る者をグイグイと引き込む演出力はさすが。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女組長", "女組長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "明治末期。組長である父を何者かに殺された芸者の澄は、父の仇を探そうと決意する。マキノ雅広監督の女任侠もので、江波杏子が槍を仕込んだ木を持って華麗に木遣りくずしを踊りながら、仇の命を狙うシーンが見もの。特別出演の伴淳三郎とフランキー堺の町人コンビが笑わせる。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("White Noise", "サイレントノイズ", "", "", "http://image.eiga.k-img.com/images/movie/33645/315x210.jpg?1860", 101, "電子機器の雑音を通して死者が生者にメッセージを送る超常現象“EVP”を題材にした新感覚のサスペンス・スリラー。最愛の妻アンナを事故で失った建築家ジョナサン。ある日彼の前に、アンナからのメッセージを預かっているという謎の男レイモンドが現われる。レイモンドから聞いたEVPの話を、はじめはまったく信じなかったジョナサンだったが……。主人公ジョナサンを演じるのは、「バットマン」「ビートルジュース」のマイケル・キートン。", "2006-09-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOME OF THE BRAVE", "ローリー・アンダーソン 0&1", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "パフォーミング・アーティストの第一人者として日本でも人気の高いL・アンダーソンが監督・脚本・音楽を手掛けるライブ・ステージ・ムービー。“言葉は口から口へ伝わる伝染病だ”と唱えるW・S・バロウズの考え方に共感したアンダーソンが、“言葉ビールス”に冒された現代人に贈るシニカルなメッセージでもある。自ら開発した弓に磁気テープを張ったバイオリンによる演奏、体を叩くと様々な音の出るドラム・スーツ、音声を男の声に変えるハーモナイザーなど電子器具を駆使し、ダンスやバックに映る映像、詩や音楽をミックスし、新しいスタイルのステージを見せつける。曲目もアメリカの異常なまでのNo.1主義やコンピュータを皮肉る『0&1』をはじめ、『グッド・イブニング』『エクセレント・バーズ』など。パーカッションには坂本龍一とも共演したこともあるD・V・ティーゲムが参加し、ゲストとしてバロウズも元気な姿を見せてくれる。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN RAMPAGE", "ショットガン・ポリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ロサンゼルス警察の敏腕女刑事サマンサは、相棒ライアンとともに麻薬密売組織を追っていた。しかし大胆に捜査を進め、奇襲攻撃を仕掛けるサマンサとライアンの動向は、なぜか敵の組織に筒抜けだった。女だてらにハードなアクションで犯罪組織とわたり合う、タフなブロンド美人刑事が魅力。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LORNA", "ローナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "グラマーな人妻ローナをめぐる男たちの物語。川で泳いでいたローナのまえに脱獄囚が現われ、彼女を荒々しく犯す。最初は抵抗していたローナだったが、やがては心を通わせ、彼を家に招きいれた。そこに彼女の夫と、その仕事仲間である粗野なチンピラたちがやってきて……。エロスの異才R・メイヤーが、愛欲のドラマを演出。日本公開時タイトルは「肉体の罠」。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渋滞", "渋滞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "正月を瀬戸内海の故郷で迎えようとマイカー帰省する都会のサラリーマン一家が、渋滞に巻き込まれながらも、その道中で様々な出会いと出来事を経験していくという異色ホームドラマ。今までハードな役柄の多かった萩原健一が、本作では一転して普通の父親像を好演している。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Disappearance of Alice Creed", "アリス・クリードの失踪", "", "", "http://image.eiga.k-img.com/images/movie/56125/315x210.jpg?3628", 101, "富豪の父親をもつ20代の女性アリス・クリードは、2人組の男に誘拐され密室にとらわれる。男たちは多額の身代金を要求しようと企むが、次第に3人の関係がねじれていき、完ぺきだったはずの計画がほころびはじめる。誘拐された1人の女と犯人の2人の男が、密室を舞台に繰り広げるサスペンス。監督は「ディセント2」脚本家のジェイ・ブレイクソン。出演は「007 慰めの報酬」のジェマ・アータートン、「SWEET SIXTEEN」のマーティン・コムストン、「ハンコック」のエディ・マーサン。", "2011-06-11", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("１・２の三四郎", "１・２の三四郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "小林まこと原作の同名漫画を格闘家、佐竹雅昭の主演で贈る熱血青春映画にして格闘もの。世界一のプロレスラーを目指して上京してきた東三四郎が、激しいトレーニングを積み、プロレス界のスター天草健太郎と戦うまでを熱く描く。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　TANGO　LESSON", "タンゴ・レッスン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "世界的脚光を浴びた「オルランド」から５年、女性監督S・ポッターが自身の体験をもとに描く愛の物語。監督以下、ほとんどの出演者が自分自身を演じている。タンゴ・ダンサー、パブロ・ベロンのダンスに魅せられたサリーは、彼にレッスンを受けることになる。タンゴへの情熱が高まると同時に、サリーのパブロへの愛も深まっていく。彼をもっと知りたいと思うサリーに対して、私生活とタンゴを割り切るパブロ。サリーは彼の過去に嫉妬し、パブロはステージ上での彼女のダンスが気に入らず激しく叱咤する。本心をぶつけ合う二人は心を通わせ、サリーはパブロを主演にダンスの映画を撮り始める。魅惑的なタンゴに人生と愛を重ね合わせる映画を、名手R・ミューラーの流麗な映像美が包み込む。パブロの振付けによるダンスを彩る、アストロ・ピアソラやカルロス・ガルデル、アニバル・トロイロらのタンゴの名曲も印象的だ。", "1997-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIDAY THE 13TH: THE FINAL CHAPTER", "13日の金曜日・完結編", "", "", "http://ecx.images-amazon.com/images/I/519NBFkGi5L._SL210_.jpg", 91, "シリーズ第４作。例によって例のごとく、死んでも死なないジェイソンが死体安置所から湖に戻って大殺りく。「スタンド・バイ・ミー」のC・フェルドマン扮するトミー少年がそれに立ち向かう。", "1984-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Redbelt", "レッドベルト　傷だらけのファイター", "", "", "http://image.eiga.k-img.com/images/movie/53715/315x210.jpg?3636", 99, "「郵便配達は二度ベルを鳴らす」「評決」の脚本家として知られるデビッド・マメットが監督、「アメリカン・ギャングスター」のキウェテル・イジョフォーが主演する格闘アクション。賞金目当ての格闘技大会には出場せず、妻と道場を営みながら細々と生活していた柔術マスターのマイクは、ある事件に巻き込まれたことから、借金の返済と名誉を取り戻すため、リングに上がる決意をする。共演に「ラースと、その彼女」のエミリー・モーティマー。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUDGMENT", "ジャッジメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "未成年者の凶悪犯罪が急増する1998年。女裁判官チェルシーは暴力主義を否定し続けてきたが、法廷にマシンガンを持った女性が乱入したことを機に、職務に疑問を感じ、自ら銃を手に犯罪に立ち向かう。脇役陣を、K・ブラック、E・グールド等のベテランが固め、名優アンソニー・クインの三男、F・クインも出演。ありふれた題材のSFアクションながら、俳優の演技が楽しめる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊那の勘太郎（伊那節仁義）", "伊那の勘太郎（伊那節仁義）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "土地の親分との争いで姿を消した勘太郎。３年ぶりに天狗党とともに故郷の伊那へ帰ってきた……。股旅ものといっても、ヤクザ者の勘太郎が天狗党に加わって勤王運動の仲間入りするところが戦時下の作品らしい。主題歌「勘太郎月夜唄」も大ヒットした。なお、戦後は「伊那節仁義」の題名で再公開されている。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("病院坂の首縊りの家", "病院坂の首縊りの家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "横溝正史原作、市川崑監督による1970年代“名探偵・金田一耕助”ものの最後の作品。原作においても、金田一耕助はこの事件の解決を最後に行方不明になる。パスポートの写真を撮るために本条という古くからの写真館を訪ねた金田一耕助は、偶然結婚記念日の写真撮影を依頼しに来た娘と出会い、死人のような面影に不審を抱く。彼女の指定した“病院坂の首縊りの家”と呼ばれる古い邸に写真館の店主とともに様子を見に行くが、そこでは惨劇が繰り広げられていた……。シャンデリアの鎖に風鈴のようにぶら下がった生首、そして首の顎ヒゲには血まみれの短冊。ショッキングなシーンの連続。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ludwig", "ルードウィヒ／神々の黄昏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 184, "　18歳で国王に即位し、40歳で謎の死を遂げたルードウィヒ2世の生涯を綴った歴史大作。ルードウィヒが傾倒する音楽家ワーグナーと世間の批判、オーストリア皇后への実らぬ初恋、戦争からの逃避など、波乱に満ちた人生の末路にあったものとは……。ルキノ・ビスコンティのドイツ3部作の完結編にあたり、撮影後、病に倒れながらも編集作業を行った。初公開から9年後、彼のスタッフの手により240分の復元完全版も作られた。", "1980-11-08", "4,2,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夕暮まで", "夕暮まで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "黒木和雄が「原子力戦争」のあと、２年ぶりに手掛けた作品である。同名の吉行淳之介のベストセラー小説を原作に、いかにも黒木和雄らしい映像で視覚化している。女子大生と中年男との奇妙な恋愛感情を描いたもので、これを桃井かおりと伊丹十三が演ずることで話題をふりまき、“夕暮れ族”なる流行語も生まれた。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TROUBLE WITH DICK", "ディックの奇妙な日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "G・ウォルコウ監督が、アメリカSF界の巨匠として知られるP・K・ディックに捧げたファンタスティック・ムービー。スランプに悩む駆け出しのSF作家が現実と小説世界が重なるという幻覚に襲われていく。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("周末情人／WEEKEND LOVER", "デッド・エンド／最後の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "リーという女の子を守るため同級生を殺してしまったアシーが、９年の刑期を終え町に帰ってみると、リーは新しい恋人ララと生活を始めていた。それを知ったアシーはやり場のない思いを感じるのだった。ティエン・チュアンチュアン門下生のロウ・イエが上海の若者たちの恋愛模様を描いている。", "1994-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天保水滸伝", "天保水滸伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "天保の頃、大利根を挟んで対立していた、飯岡助五郎一家と笹川繁蔵一家の虚々実々の駆け引きを背景に、飲んだくれの腕きき男・平手造酒や人々から慕われていた新興勢力の繁蔵一党の活躍を描いた娯楽時代劇。ヤクザ映画の元祖的な作品でもある。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARKNESS　FALLS", "黒の怨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "抜けた乳歯を枕元に置くと、硬貨一枚と交換する“トゥース・フェアリー”という欧米ではポピュラーな妖精伝説と、古くから伝わる“魔女狩り”伝説をドッキングしたホラー映画。光を極度に嫌悪し、暗闇の中でのみ縦横無尽に飛び回る悪霊を、スピード感たっぷりに描いて恐怖感をあおる。小さな港町ダークネス・フォールズには、150年前に無実の罪で虐殺された老女マチルダ・ディクソンの怨念が街をさまよい、姿を見た者は殺される伝説があった。ある夜、少年カイルは偶然にも、彼女の姿を見てしまい……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bullet", "ハード・ブレット／仁義なき銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「ビギナーズ」等の作品で知られるMTV界の鬼才ジュリアン・テンプル監督が、ミッキー・ロークを主演に迎えて放つスタイリッシュなバイオレンス・アクション。刑務所から出所したばかりの元野球選手ブレット。迎えに来た弟や親友と共に故郷ブルックリンへ向かった彼は、その途中、麻薬密売組織とトラブルを起こしてしまう。そしてこのことから、彼は街で最大のドラッグ・ディーラー、タンクから命を狙われるようになり……。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バブルへGO!!　タイムマシンはドラム式", "バブルへGO!!　タイムマシンはドラム式", "", "", "http://image.eiga.k-img.com/images/movie/33845/315x210.jpg?4431", 116, "「私をスキーに連れてって」のホイチョイ・プロダクションズが8年ぶりに贈る最新作。巨額の負債を抱え破綻寸前に陥った日本経済を救うため、財務省大臣官房経済政策課の下川路功はある極秘プロジェクトを進めていた。それは、1990年にタイムスリップしてバブル崩壊を食い止めようというものだった……。プロジェクトのリーダー下川に阿部寛、彼によってバブル絶頂期の東京に送りこまれるフリーター真弓に広末涼子が扮する。", "2007-02-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Perfume: The Story of a Murderer", "パフューム　ある人殺しの物語", "", "", "http://image.eiga.k-img.com/images/movie/1595/315x210.jpg?3684", 147, "パトリック・ジュースキントによるベストセラー小説を映画化したサスペンスドラマ。18世紀のパリ。魚市場で生み捨てられたジャン＝バティスト・グルヌイユは、超人的な嗅覚を持っていた。ある日、街で出会った女性の香りに取り憑かれた彼は、その香りを再現するために香水調合師に弟子入りする。やがて、パリでは若く美しい女性ばかりを狙った連続殺人事件が発生し……。監督は「ラン・ローラ・ラン」のトム・ティクバ。", "2007-03-03", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In the Attic", "屋根裏のポムネンカ", "", "", "http://image.eiga.k-img.com/images/movie/54345/315x210.jpg?3693", 72, "屋根裏に住みついたガラクタたちの世界をユーモアたっぷりに綴ったチェコ発の長編人形アニメーション。屋根裏部屋で平和な暮らしを送っていた不思議なおもちゃたち。ところがある日、突然現われた悪の支配者フラヴァによって、青い目の人形ポムネンカがさらわれてしまう。ポムネンカを取り戻すため、おもちゃたちは未知の世界へと飛び出すが……。監督・脚本・美術を手掛けるのは、チェコアニメ界を代表するアーティストとしても知られるイジー・バルタ。", "2009-08-01", "76,43,72", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無防備都市", "ファム・ファタール", "", "", "http://image.eiga.k-img.com/images/movie/54125/315x210.jpg?3365", 112, "「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。国際的スリ組織の女ボスと、スリ事件を担当する敏腕刑事の禁断の恋を描く犯罪アクション。「四月の雪」のソン・イェジンが華麗で妖艶なスリを、「白い巨塔」のキム・ミョンミンが家族に犯罪者を持つという過去と戦うカリスマ刑事役を熱演。凶悪事件を扱う広域捜査隊のデヨンは、ヤクザと繋がりを持つ企業型スリ事件を担当することになるが、組織に潜伏中、偶然救出した美しい女スリのチャンミに一目惚れしてしまい…。", "2008-10-11", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Train", "大列車作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "　ドイツ占領下のパリ。敗戦の色濃いナチス・ドイツ軍はフランスの名画略奪を計画。美術館長ピラールからその旨を聞いたフランス国鉄の操車係長ラビッシュは鉄道員たちと一致団結し、輸送を妨害する。レジスタンスたちによる妨害工作が成功する一方、犠牲者も続出。ラビッシュは奔走するが……。命がけの危険な作戦をダイナミックに描いたアクション大作。", "1964-01-01", "1,2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチバンIII 武闘宣戦", "ガチバンIII 武闘宣戦", "", "", "http://ecx.images-amazon.com/images/I/61DDBQ%2BF-ML._SL210_.jpg", 75, "", "2009-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("11人いる！", "11人いる！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "様々な惑星国家が、星間連盟を組織して共存する未来の宇宙。コスモ・アカデミーの入学試験を勝ち抜いた宇宙の若者たちが、最後に10人１組になり、漂流宇宙船に乗り込んだ。いつのまにか一人増えているというアクシデントを抱えながら、受験生たちは53日間にわたる耐久サバイバルに挑む。過酷な試験が続くなかで、11人は対立と協調の間で揺れ動くが……。原作は荻尾望都のSFサスペンス・コミック。11人の登場人物、それも別種の異星人が繰り広げる綿密なドラマを、ベテランスタッフが混乱を押さえて映像化している。11人のなかでも雌雄未分化のヴェネ星人・フロルと、シベリース星の少年・タダを主人公に、両者の恋も絡めて展開する推理劇は、最後まで飽きさせない。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月光仮面・第１部", "月光仮面・第１部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "川内康範の『少年クラブ』連載、TV連続放送劇を原作者自身が脚色し少年ものでは定評がある小林恒夫が監督した冒険活劇“月光仮面”シリーズの第１作。どくろ仮面によって奪われた“HOジョー発爆弾”を月よりの使者、正義の味方・月光仮面が追跡する。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLICE ACADEMY ６：CITY UNDER SIEGE", "新ポリスアカデミー バトルロイヤル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "1984年以来、毎年１本のペースで続いているシリーズ第６作。回を重ねるごとに新たなキャラクターが加わったり、またマホニー役のスティーヴ・グッテンバーグが卒業したりと、顔ぶれも変わってきたが、本作では拳銃狂タックルベリーの息子が登場する。街を荒らすギャング団に立ち向かうことになったポリ・アカ軍団。おなじみの珍作戦が展開される。DVDタイトルは「ポリスアカデミー６　バトルロイヤル」。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BALLET", "BALLET／アメリカン・バレエ・シアターの世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 170, "数々の傑作ドキュメンタリーを輩出するフレデリック・ワイズマン監督が、60年以上の歴史を誇る国際的バレエ団“アメリカン・バレエ・シアター(＝ABT) ”の裏側に迫った。厳しくも熱気に満ちた本拠地ニューヨークでの練習風景からワールドツアーまでをつぶさに追う。華やかな舞台の裏で苦悩するダンサー、リラックスした彼らのオフショット、金銭交渉に声を荒げる芸術監督の姿など、スタッフやダンサーの素顔や知られざるABTの実態に興味は尽きない。なかでも、セシル・B・デミルの姪である名振付師アグネス・デミルが車椅子で指導する晩年の姿を映した映像は貴重。華麗な舞台映像も臨場感たっぷりで、２時間50分という大作ながら、まったく飽きることがない。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES AMANTS", "恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "夫にも愛人にも満足しないジャンヌは、ふと知り合った若者と陶酔の一夜を過ごす。モローとボリーのあまりにもロマンティックなラブ・シーンが有名な、L・マル監督の「死刑台のエレベーター」に続く第２作。愛と美の世界を、完璧なまでに映像化した秀作。", "1958-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEEL MAGNOLIAS", "マグノリアの花たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ルイジアナ州の小さな町に住む６人の女性たちの愛と友情、生と死が繰り返される人生の機微を心優しく描くH・ロス監督の秀作。ハリウッドの名花６女優が、狭い美容院の中、一堂に会してよもやま話に花を咲かせるシーンを何度となく展開させるあたりは壮観ですらある。その中で物語の中心となるのは、S・フィールド扮する母親とJ・ロバーツが扮する病弱な娘との親子愛。死を決して出産しようとする娘の安否を気づかう母の姿が実に見事である。霊感女優のS・マクレーンが信仰嫌いの嫌われ者という設定も凝っている。G・ドルリューの音楽も優しい。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユンカース・カム・ヒア", "ユンカース・カム・ヒア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ミュージシャン、木根尚登の原作を映像化した感動作。共働きの両親とすれ違いの生活を送る小学六年生のひろみ。そんな彼女の支えは、人間の言葉をしゃべる不思議な犬、ユンカースの存在だった。ある日、ひろみは両親が離婚の相談をしているのを知り、ますます気分が沈んでしまう。そんな時、ユンカースが“自分は三つの奇跡を起こすことができる”とひろみに告げる……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shooter", "ザ・シューター　極大射程", "", "", "http://image.eiga.k-img.com/images/movie/1885/315x210.jpg?1401", 125, "スティーブン・ハンター原作のベストセラー「極大射程」（93）を「トレーニング・デイ」のアントワン・フークワ監督が映画化。山奥で静かな生活を送っていた元海兵隊のスナイパー、ボブ・リー・スワガー（マーク・ウォールバーグ）の元に、ある日、大統領暗殺計画が発覚したので阻止してほしいとの依頼が舞い込む。だが、それはボブを陥れるための陰謀だった……。共演にダニー・グローバー、マイケル・ペーニャら。", "2007-06-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜びは創りだすもの　ターシャ・テューダー四季の庭", "喜びは創りだすもの　ターシャ・テューダー四季の庭", "", "", "http://image.eiga.k-img.com/images/movie/55225/315x210.jpg?3602", 110, "2008年6月に亡くなった絵本作家ターシャ・テューダーさんは、アメリカ・バーモント州の山中に自らが作り出した夢のように美しい庭で暮らしていた。世界中の庭師が憧れ、四季折々に花が咲き乱れる庭で、ゆったりと営まれる素朴な暮らしを見つめる。NHKで放送されたドキュメンタリー番組を劇場で公開するプロジェクト「This is REAL／NHK-BSドキュメンタリー・コレクション」にて上映。", "2010-02-06", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Detonator", "デトネーター", "", "", "http://image.eiga.k-img.com/images/movie/33665/315x210.jpg?2134", 95, "「ブレイド」シリーズのウェズリー・スナイプス主演によるスパイ・アクション。核兵器の密輸組織に立ち向かうCIA捜査官の活躍を描く。ルーマニアのブカレストで武器密売の潜入捜査を行なっていた捜査官グリフ。しかし密告により捜査は失敗、銃撃戦の末、グリフは地元警察に拘束されてしまう。そんな彼に、ロシア人美女の護送という新たな任務が与えられるが……。「ザ・マークスマン」に続く“スナイプスの大運動会”第2弾。", "2006-09-30", "1,56", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Smurfs", "スマーフ", "", "", "http://image.eiga.k-img.com/images/movie/55685/315x210.jpg?3997", 102, "1958年にベルギーの漫画家のピエール・クリフォールが生み出し、80年代には「森のスマーフ」として米ハンナ・バーベラ・プロダクションによりTVアニメーション化もされた人気キャラクターを、実写とCGアニメーションを融合させて映画化。小さな青い妖精スマーフたちは、悪の魔法使いガーガメルに村を追われ、突然開いた大きな穴に飲み込まれてニューヨークへ放り出されてしまう。リンゴ3個分の背丈しかないスマーフたちが、村へ戻るために大都会で繰り広げる冒険を描く。監督は「ビバリーヒルズ・チワワ」のラージャ・ゴスネル。", "2011-09-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEP KIDS", "マイ・スイートファミリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "両親が離婚、再婚を繰り返したため、親や兄弟がどんどん増えて複雑な関係になってしまった子供たち。いつも取り残された気になる彼らが家出や非行に走るのも仕方のないこと。しかし、家族は家族、やがて一つに結ばれていく。製作総指揮のメリッサ・ゴダードが、弟の卒業式で複数の父親、母親と遭遇したというエピソードからヒントを得て映画化されたファミリー・ドラマ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍術児雷也", "忍術児雷也", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "鯨波照忠の策謀にはまり、更科城を失った尾形家の遺児、周馬が、ガマ仙人から秘術を授かる。“児雷也”として生まれ変わった彼は宿敵、鯨波に迫るが、恐るべき大蛇丸がその前に立ちはだかる。奇想天外な忍術合戦を映像化した特撮時代劇。若山富三郎のデビュー作でもある。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORTAL KOMBAT", "モータル・コンバット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "最新SFXとノンストップのアクション映像が合体した体感デジタルムービー。中国人武道家のリュウは、伝説の格闘神ライデンに出会い、香港で行われる格闘技トーナメントに出るように言われる。ハリウッドスターのケイジもそのトーナメントに出場するため、香港に向かっていた。リュウとケイジが参加するトーナメントは、次の試合に地球の格闘戦士が負ければ、世界は魔界のものになってしまうというものだった。リュウらは、地球を守るために戦うのだったが……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONCE A THIEF", "泥棒を消せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "サンフランシスコで殺人事件が発生、犯人の一味としてエディが警部に逮捕される。だが、この警部はエディに個人的うらみを持っていて、ことあるごとに彼の邪魔をしていたのだった。当然証拠不十分で釈放、やがてエディのやくざな兄、ウォルターが、宝石泥棒を計画する。A・ドロン主演のサスペンス・アクション。", "1965-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続やくざ坊主", "続やくざ坊主", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "前作のヒットにより製作された続編。今回は房総のとある門前町が舞台で、金座の利権に群がる悪党相手に乞食坊主・竜全が大活躍。勝新の出世作「不知火検校」に明朗さをつけ加えた破戒坊主ぶりがなかなかいい。海女の品定めシーンなど、エロティックな見せ場もある。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTRIGUE", "背徳の火遊び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "イタリアの郊外にある大邸宅に住む未亡人のエヴァは、女盛りの肉体を持て余す毎日だった。しかし、パリの娼館で使われていた赤いソファを手に入れてからは、不思議と性の炎が燃えあがり、次々と男も現れ始めて……。セックスによって結ばれた３人の若者と一人の未亡人の恋の行方をエロティックに描く。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("光の帝国", "光の帝国", "", "", "http://image.eiga.k-img.com/images/movie/54675/315x210.jpg?2490", 76, "演劇集団キャラメルボックスによる短編演劇、ハーフタイムシアターをデジタル記録した作品。小学4年生の光紀と彼の家族は、読んだ作品を完璧に暗記できる能力を持っていた。ある日、光紀は学校から帰る途中で一人の老人と仲良くなるが、体調を崩した老人は入院してしまう。老人を見舞った光紀が老人に触れると、彼の人生の記憶が様々な映像となって光紀に流れ込んでくる。原作は「夜のピクニック」などで知られる恩田陸。", "2009-07-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮本武蔵・決闘巌流島", "宮本武蔵・決闘巌流島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "“宮本武蔵”シリーズの完結編。前２作ではいささか手ぬるかった稲垣浩の演出も、さすがに巌流島での宮本武蔵と佐々木小次郎の大一番は、大いに堪能させてくれる。鶴田浩二の佐々木小次郎はまさにハマリ役で、武蔵役の三船敏郎を食うようなスゴ味を発揮。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Soul Men", "ソウルメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "サミュエル・L・ジャクソンとバーニー・マックがブラック・ミュージシャンを演じるロードムービー。伝説のソウルバンド「マーカス・フックス＆リアル・ディール」は、マーカスがソロ活動を始めたことで解散を迎える。残されたルイスとフロイドは「リアル・ディール」として活動を始めるが、ヒット曲に恵まれずにケンカ別れをしてしまう。それから30年後、マーカスの訃報と追悼コンサートの依頼が舞い込んでくる。2人は「リアル・ディール」を再結成し、ニューヨークのアポロシアターを目指す。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JACK AND SARAH", "赤ちゃんにバンザイ!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ある日突然、シングルパパになった男と、ベビーシッターの若い女性が子育てに奮闘するラブ・コメディ。出産で妻を亡くした弁護士ジャックは、小さな赤ん坊を抱えてパニック状態。途方に暮れていた時、ウエイトレスのエイミーをベビーシッターに雇うことになる。ところが、彼女も子守未経験。赤ちゃんに振り回される二人は、ケンカを繰り返しながらも、互いに惹かれていき……。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Black Dahlia", "ブラック・ダリア", "", "", "http://image.eiga.k-img.com/images/movie/1295/315x210.jpg?3816", 121, "ジェームズ・エルロイによる“暗黒のLA4部作”の第1作を「アンタッチャブル」「殺しのドレス」のブライアン・デ・パルマが映画化。1947年、ロサンゼルス郊外の空き地で身体の腰から下が切断されている黒づくめの女性の死体が見つかる。後に「ブラック・ダリア事件」と呼ばれたこの事件に2人の刑事が取り憑かれていく……。", "2006-10-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学校の怪談４", "学校の怪談４", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "夏の人気シリーズの第４作。監督は第１、２作の平山秀幸が務めた。恒と弥恵の兄妹は夏休みを利用して親戚が住む海辺の町にやって来たが、彼らの周りで地元の子供たちが神隠しに遭う事件が連続して起こる……。このシリーズの特徴でもあったユニークな妖怪たちは登場せず、津波の犠牲になった子供たちの霊と現代の子供たちの絡みが描かれる。DVDは「学校の怪談　DVD-BOX」に収録。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THUNDERBIRDS SIX", "サンダーバード６号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "劇場版「サンダーバード」（1967）と同じスタッフで製作された続編。国際救助隊の科学技師ブレーンズが設計した豪華飛行船スカイシップ１号をめぐって、ブラック・ファントムとの間で死闘が始まる。危機一髪の救助隊は、炎上落下するスカイシップ１号から脱出、旧式で二枚翼の飛行機に乗り移る。実はこれこそが、ブレーンズが新たに設計したサンダーバード６号だったのだ。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЗЛЕГИЯ ИЗ РОССИИ", "ロシアン・エレジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "A・ソクーロフが生と死をテーマに撮り上げたドキュメンタリー映画。チャイコフスキーの曲をバックに前世紀末から20世紀初頭のボルガ川沿岸の人々の写真が次々と映し出される。そして、第一次世界大戦時の戦闘場面が続く。画面は一切の説明をすることなく、様々な映像を続けることによって、ロシアの現実をえぐり出していく。", "1993-01-01", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男なら夢を見ろ", "男なら夢を見ろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "幼なじみの夏雄と健太郎。10年後、健太郎は法学部の学生に、夏雄はヤクザの兄貴分になっていた。そんなとき、健太郎の育ての親、小野寺刑事が夏雄の親分のからみで殺される。さらに時がたち、夏雄は組の幹部に出世、一方の健太郎は検察官になっていた。二人の男の生き様を対比させた、石原裕次郎主演のアクション・ドラマ。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLISH　WEDDING", "自由な女神たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "デトロイトに暮らすポーランド移民の一家が、それぞれ悩み、傷つきながら絆を深めていく過程をコミカルに描くヒューマン・ドラマ。パン職人の父ボレックと気丈な母ヤドヴィカの娘ハーラは、自由奔放に恋を楽しむ無邪気な15歳。ヤドヴィカには金持ちのロマンという愛人がいたが、ボレックはそれを見て見ぬふりをしていた。そんな両親の姿を見て心を痛めるハーラは、やがて一人の青年に恋をして妊娠。それを機に家族は自分自身を見つめ直し、本当に大切なものを見いだしていく。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("True Grit", "勇気ある追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "片目の保安官コグバーンは、父を殺された少女マティに復讐のため雇われる。マティとコグバーン、そして若くして腕の立つテキサス・レンジャー、ラ・ボーフは、犯人トムを探す旅に出る。インディアン地区でトムを発見、死闘の末彼を殺すが、ラ・ボーフも命を落とす。好評を得て、後年キャサリン・ヘプバーンを迎えて続編も製作された、ウエスタンの傑作。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドッペルゲンガー", "ドッペルゲンガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「CURE」から「回路」まで組んでいる黒沢清監督と役所広司による驚愕必至の超異色作。見たら死ぬと言われる“もう一人の自分”に出会ったことから、人生を一変させられる悩めるエリート科学研究者の姿を、スラップスティック的な暴走テンポで描き出す。画面分割やズームアップなど、従来の黒沢映画にはなかった新機軸が軽やかな戦慄を呼ぶ好編だ。スランプでノイローゼ気味の研究者・早崎。ある日、彼は“もう一人の自分”と出会う。それを妄想だと思い込んだ早崎だが、“分身”は早坂のために陰で動き始める。自分とは対照的に伸び伸びと生きるもう一人の自分に、やがて早崎は殺意を抱く。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大菩薩峠", "大菩薩峠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "過去何度か映画化された中里介山の仏教思想の強い小説を、アクション派・岡本喜八監督が合理的な解釈で映画化した意欲作。仲代達矢扮する机龍之助が、巡礼の亡霊に狂わされて新選組を延々斬りまくるラスト・シークエンスは迫力十分で、アメリカでも公開され評判になった。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Charlie's Angels: Full Throttle", "チャーリーズ・エンジェル　フルスロットル", "", "", "http://image.eiga.k-img.com/images/movie/1065/315x210.jpg?1944", 106, "おなじみ3人のエンジェルの今回の敵は、デミ・ムーア扮する彼女たちの先輩。監督は前作でハリウッドに進出したCM出身のマックGが続投、武術指導も続投で、兄ウーピンとともに「マトリックス」に参加したユエン・チョンヤン。映画パロディも満載で「サウンド・オブ・ミュージック」「フラッシュ・ダンス」「ピンク・パンサー」の主題歌から「サイコ」「めまい」などの効果音、「ケープ・フィアー」「M:I-2」のネタも登場。", "2003-06-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT HAS A THOUSAND EYES", "夜は千の眼を持つ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "C・ウールリッチの小説を、J・ファローが映画化した異色のサスペンス編。不思議な予知能力を持つ男トライトンは、奇術師として生計を立てていたが、仲間にそそのかされてギャンブルに手を出し、大もうけする。ところが、出産によって婚約者が死ぬことを予知した彼は、彼女の前から去り、一人ひっそりと生きていこうとするが……。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES FRUITS DE LA PASSION", "上海異人娼館 チャイナドール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "P・レアージュの『O嬢の物語』を、寺山修司が1920年代の香港、上海に舞台に置き換えて映画化した作品。ステファン卿に連れられて娼館にやって来たO嬢が倒錯的な性体験を過ごすさまを、寺山独特の前衛的な手法で幻想的に綴っていく。", "1981-01-01", "76,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Into the Wild", "イントゥ・ザ・ワイルド", "", "", "http://image.eiga.k-img.com/images/movie/53235/315x210.jpg?2091", 140, "「インディアン・ランナー」「クロッシング・ガード」のショーン・ペン監督が実話に基づくジョン・クラカワーのノンフィクション「荒野へ」を映画化。恵まれた環境で育ちながらも、人生に不満を抱えていた青年がアメリカを横断。その果てにたどり着いたアラスカの荒野で死ぬまでの心の軌跡を描く。主演は「ロード・オブ・ドッグタウン」「スピード・レーサー」のエミール・ハーシュ。共演にマーシャ・ゲイ・ハーデン、ウィリアム・ハート、キャサリン・キーナー、ビンス・ボーンら。", "2008-09-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瀬戸内少年野球団・青春編／ 最後の楽園", "瀬戸内少年野球団・青春編／ 最後の楽園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "篠田正浩監督の「瀬戸内少年野球団」の続編にあたり、前作の級長・武女・バラケツの３人が成長して上京。1950年代後半の東京を舞台にそれぞれの青春を生きていく。三村晴彦の冴えた演出と、鷲尾いさ子の新鮮な魅力に注目。DVDは「瀬戸内三部作 メモリアルDVD-BOX」に収録。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("", "サウスパーク／無修正映画版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "コロラド州サウスパーク。おなじみの小学生4 人組は、大好きなカナダのお下劣コメディアン主演 の映画 (R指定なので小学生は鑑賞禁止) をズルして観ることに成功。映画で覚えたののしり言葉を 得意になって使うようになった。が、これに不安を覚えたPTAは、ののしり言葉を抑制するための 新兵器“Vチップ”を子供の脳に埋め込み、さらには映画の製作国カナダを糾弾、やがて事態はア メリカとカナダとの全面戦争に突入する……！", "2000-08-12", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京市街戦", "東京市街戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "秀作「紅の流れ星」に続く渡哲也の主演作。終戦直後の東京の闇市を舞台に、善玉のテキヤ一家と悪玉の新興暴力団の抗争を描いたアクション編。西村の演出は今一つ歯切れが悪いが、渡哲也は飛行服姿の復員兵として登場し、カッコいいところを見せる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バッテリー", "バッテリー", "", "", "http://image.eiga.k-img.com/images/movie/33875/315x210.jpg?4281", 119, "累計380万部を突破した、あさのあつこのベストセラー小説を「陰陽師」シリーズの滝田洋二郎監督が映画化。ピッチャーとしての自分の才能に絶対的な自信を持つ原田巧は、中学入学を目前に控えた春休み、父の転勤で岡山県に引越してくる。彼はそこで同級生のキャッチャー永倉豪と出会い、バッテリーを組むことを熱望される。しかし、彼らが入った新田東中学の野球部は、厳しい監督の指導のもと徹底的に管理されていた……。", "2007-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAURA E AMORE", "三人姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "チェーホフの名作をベースに、舞台を現代のイタリアに置き換え、美しい３人姉妹を通して女性であることの苦悩と希望を描く女性ドラマ。アルダン、スカッキ、それにゴリノの共演が見どころの、女流監督による女性映画。", "1988-01-01", "4,7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピラミッドの彼方に ホワイト・ライオン伝説", "ピラミッドの彼方に ホワイト・ライオン伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "百獣の王ライオンのなかで最も気高く、人間に絶対服従しないという幻の猛獣ホワイト・ライオン。この獣を捜して消息を絶ったという母のあとを追い、少女マリアは少年モジャとホワイト・ライオンを求めて旅に出る。アフリカの自然も美しい冒険ファンタジー。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD MAN", "デッドマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "19世紀の西部に会計士ウィリアム・ブレイクがやってきた。痴話喧嘩に巻き込まれて胸に銃弾を受けたブレイクは、追っ手から逃げる山の中でネイティブ・アメリカンのノーボディーと出会う。死の時が迫る……。「ダウン・バイ・ロー」でやり残したモノクロ撮影をとことん追求したかったというジャームッシュ監督がふたたびミューラーを起用、ジョニー・デップを主演に迎えた。ニール・ヤングの即興音楽が壮絶な美しい作品。", "1995-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香港厨神　大阪撻一餐", "大阪プロレス飯店", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "香港と大阪を舞台に展開するコメディ。香港で料理人修業中のキーは、大阪にいる、いい加減な兄マイのアイデアに乗り、プロレスとレストランを融合させた店の経営に着手。そこで巻き起こる珍騒動がナンセンスなギャグとともに描かれる。 サモ・ハン・キンポーの息子ティミー・ハンが主人公キーに扮し、コメディ・センスを発揮。父親との共演も見ものだ。香港で料理人を目指すキーは、父の遺産として金の延べ棒を受け取る。自分勝手な兄マイは、遺産を元手に“大阪プロレス”のテーマレストランを開店。ところが、ライバル店の妨害に遭い、地球征服を目指す謎の組織までが現れる……。", "2004-01-01", "6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　BIG　COMBO", "ビッグ・コンボ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "公開当時「暴力団」というタイトルで知られる名作フィルム・ノワール。一癖も二癖もある魅力的なキャラクターがずらり。一人の刑事が犯罪シンジケートのボスを調査していたが、ボスの情婦を追跡するうちに、次第に彼女に惹かれていく。やがて、彼は組織の手下たちに拉致され拷問を受けるはめに……。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MYSTIC PIZZA", "ミスティック・ピザ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "太めの女主人秘伝のソースが自慢のピザハウス“ミスティック・ピザ”で働く、３人の女の子のひと夏の乙女チックなときめき体験を軽快に、そして少し切なく描く青春映画。J・ロバーツをはじめ３人の女優たちの個性が光る。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le locataire", "テナント 恐怖を借りた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "「吸血鬼」「チャイナタウン」と並んで、ポランスキー監督自らが怪演する、第一級のスリラー。フランス国籍のポーランド人、トレルコフスキーはパリの下町にアパルトマンを借りる。前住人の女性は投身自殺していたが、さして気にもとめなかった。だが、入居の日から様々な幻覚を見、強迫観念を抱き始める。残された前住人の服をつけ、化粧をしたトレルコフスキーは、彼女も“彼ら”に追い詰められたに違いないと思い込む。主人公の住む部屋に悪霊などが取り憑いて具象化されると昨今流行のSFXホラーとなるが、ここでは近所の住民による目に見えない主人公の強迫観念に絞り込んで、観る者の心胆を寒からしめるあたりは見事。「燃えよドラゴン」を見て欲情する、少々醜女っぽく撮られたI・アジャーニもいい。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸福", "幸福", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "E・マクベインの“87分署”シリーズの一編『クレアが死んでいる』を市川崑が翻案したミステリー。拳銃乱射事件の巻き添えで、刑事の恋人が死亡。担当した刑事たちは、犯人追及の過程で、様々な人間模様に遭遇する。シルバー・トーンの色彩が印象的。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女賭博師", "女賭博師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "主人公の女賭博師は、一人の男をめぐって素人上がりの女賭博師と対立し、一方ヤクザの縄張り争いの犠牲になって死んだ父の仇を討つ。川口小枝が恋仇の女賭博師に扮し熱演する。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLUB PARADISE", "クラブ・パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ケガで退職したシカゴの消防士ジャックは、ひょんなことからカリブ海の小島にあるオンボロホテルのオーナーになる。だが客の入りが芳しくないため、詐欺まがいの一大キャンペーンを行い……。役者たちのキャラクターを生かしたギャグが連続するドタバタ・コメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hawaii", "ハワイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 200, "ハワイの歴史とそこに生きる人々の年代記を描いたジェームズ・A・ミッチェナーの小説を、「サウンド・オブ・ミュージック」のジュリー・アンドリュース主演で映画化。キリスト教を布教するために未開の地ハワイにやってきた宣教師アブナーとその妻ジェルーシャ。2人は古くから島に伝わる習慣やアメリカ資本主義による圧力などと闘いながらも根気強く布教活動を続け、宣教師としての任期が終了した後も島にとどまることを決意する。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EHI　AMICO　C’E　SABATA，HAI　CHIUSO", "西部悪人伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "西部の町ドハティで銀行強盗が発生。ただ一人この事件を察知したサバタは、無法者たちを倒して10万ドル入りの金庫を取り戻す。しかし事件の首謀者たちが証拠隠滅を図り、サバタに襲いかかってくる。L・V・クリーフが異色のヒーローに扮したマカロニ・ウエスタン。バンジョーに銃を仕込んだ殺し屋役のW・バーガーが見せ場を作る。後に同じ主人公を据えた「大西部無頼列伝」「西部決闘史」が製作された。", "1970-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DET REGNAR PA VAR KA¨RLEK", "われらの恋に雨が降る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ある日駅で知り合い、恋に落ちたダービッドとマッギは、一緒に暮らしていこうと決心し、旅を始める。仕事も金もなく途方に暮れかけていた二人は、ある奇妙な老人の勧めで変人たちばかりが住む村の農園で働くことになったが……。若き日のI・ベルイマンが撮ったフィルム・ノワール風の長編第２作。", "1946-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAT’S EYE", "CAT’S EYE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1980年代に人気を博し、TVアニメ・シリーズ化もされた北条司のコミックを、“私立探偵　濱マイク”シリーズの林海象が映画化。昼は喫茶店を営み、夜は世間を騒がす絵画泥棒キャッツ・アイに変身する美人３姉妹、泪、瞳、愛が、幼い頃に失踪した父の消息を捜し求め、国際的贋作シンジケート、紅龍団と戦いを繰り広げていく。３姉妹に扮した内田有紀、稲森いずみ、藤原紀香がレザースーツを着こなし、セクシーな大泥棒を生き生きと演じている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA GRANDE BOUFFE", "最後の晩餐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "生きることに絶望した４人の男がパリ郊外の邸宅に集まって来る。レストランのシェフのウーゴ、パイロットのマルチェロ、プロデューサーのミシェル、判事のフィリップ。４人は毎日、酒池肉林の宴を繰り広げ、食べ過ぎで一人、また一人と死んでいく。生命力を失った男たちのグロテスクさ、退廃した現代文明への強烈な風刺が全編に貫かれている。", "1973-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les enfants du siecle", "年下のひと", "", "", "http://image.eiga.k-img.com/images/movie/1845/315x210.jpg?4323", 138, "1830年代パリ。女性作家サンドはタブーとされたものを堂々と描き、その内容と共に自身もスキャンダラスな存在として世間を騒がせていた。一方、天才詩人ミュッセは文壇の寵児と人々の注目を浴びていたが、彼は理想もなく放蕩に明け暮れる日々を送っていた。ある日、サンドは朗読会で批評家から激しく罵倒される。そして、動揺したサンドを優しく慰めたのはミュッセだった。これがきっかけとなり、二人は互いの作品を読み、書簡を交すようになる。", "2000-04-29", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Prizzi's Honor", "女と男の名誉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "　ニューヨークのマフィア、プリッツイ家の一員チャーリーはファミリーの結婚式で出会った美女アイリーンに一目惚れ。しかしその後、チャーリーはアイリーンがフリーの殺し屋であることを知る。それでもチャーリーの気持ちは変わらなかった。やがて彼はアイリーンと結婚することになるが……。マフィアの世界に生きる女と男の恋、そしてその行方を、ブラックユーモアを織り交ぜて描いたドラマ。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("出草之歌", "出草之歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "靖国神社に祀られた祖先の魂を取り戻すため、抗議活動を続ける台湾の音楽集団と女性政治家の姿を追った記録映画。10以上も存在する台湾の原住民たちの現状に迫ると同時に、靖国問題の一端も垣間見ることができる骨太な社会派作品。靖国神社には日本軍として戦地に赴いた台湾原住民による軍隊も祀られている。タイヤル族の台湾立法院委員チワス・アリは祖先の魂を取り戻すため、靖国神社へ抗議活動を続けるが……。", "2006-06-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Meerkats", "ミーアキャット", "", "", "http://image.eiga.k-img.com/images/movie/53785/315x210.jpg?2895", 85, "ドキュメンタリー映像の世界的権威として知られるBBCフィルムズとBBCナチュラル・ヒストリー・ユニットが共同製作。アフリカ、カラハリ砂漠に生きるミーアキャットの生態を追ったネイチャー・ドキュメンタリー。天敵であるワシやヘビとの追走劇や、餌となるサソリとの格闘、アフリカの灼熱の大地など、体長7～30センチのミーアキャットの視点でとらえた映像が見どころ。ナレーションを務めるのは、本作が最後の仕事となった名優ポール・ニューマン。", "2009-01-10", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SATYRICON", "サテリコン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "まだキリスト教が浸透する以前、ローマは退廃の極に達し、人々は快楽のみを追い求める生活に明け暮れていた。学生のエンコルピオとアシルトは、美少年ジトンを奪い合う恋仇同士。しかし恋に破れ絶望したエンコルピオはトルマルキオの宴に出かけ、ローマの快楽を代表する酒池肉林を目撃する。狂乱の宴をあとにしたエンコルピオは、貴族の少年狩りにつかまり、軍船の中でアシルトと再会する。彼らが見たものは、やはり退廃しきった貴族たちの姿だった……。古代ローマの詩人ペトロニウスの原作を、映像の魔術師フェリーニが壮大な画面の中に展開したおぞましくも絢爛な一大叙事詩。", "1970-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ネイキッドボーイズ・ショートムービーvol.1", "ネイキッドボーイズ・ショートムービーvol.1", "", "", "http://image.eiga.k-img.com/images/movie/56485/315x210.jpg?4443", 35, "ミュージカルやダンス、朗読劇、エチュード、コント、トークなどステージ上でのライブパフォーマンスを中心として活動している若手俳優集団「Naked Boyz」が主演するショートムービー集。ニコニコ動画やTwitterを題材とした「自分生中継」「最後のつぶやき」をダブルキャストで各2本ずつ製作。計4本を上映。「自分生中継」のヒロインには、アイドルグループ「AKB48」の藤江れいなが扮する。", "2011-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CHA＾TEAU DE MA ME｀RE", "プロヴァンス物語 マルセルのお城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "フランスの文豪M・パニョルの自伝的小説を映画化した青春映画。父への尊敬を追った「プロヴァンス物語　マルセルの夏」に対し、こちらでは母の優しさを綴っている。プロヴァンスの丘の別荘に向かうために、途中にある３つのお城の庭を横切ろうとするマルセル一家。怖がる母の手を引く少年マルセルの姿がさわやかな感動を呼ぶ。DVDは「マルセル スペシャルエディション」に収録。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血煙り笠", "血煙り笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "妹を殺した甲府勤番の息子を斬って旅に出た藤五郎。勤番に彼を殺すように依頼されながら、斬ろうともしない浪人、甚十郎。それに遊び人、松五郎を加えた奇妙な顔ぶれの３人が旅を続ける。三島の宿に泊まった３人は、窮地に陥っている藤五郎の恩人の娘と出会い、彼女のために一肌脱ぐのだが……。股旅ものと人情ものを融合させた時代劇。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裏ゴト師", "裏ゴト師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "パチンコの基盤であるロムを操作することを仕事とするカバン屋の復讐劇を描く。パチンコメーカーのプログラマーだった狩野は、３年前何者かの策略により、逮捕されそうになる。あやうく逃げることができたが、刑事の弓削を傷つけ、逃亡生活を送ることになる。親友・神埼の協力により、自分を陥れた男を調べ、伝説の裏ゴト師・南条が怪しいことが分かる。カバン屋稼業をしながら南条のあとを追う狩野。そして、パチンコ店“ドリーム”を舞台に、狩野、弓削、南条らの戦いが始まるのだった。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まぼろし城 第三部", "まぼろし城 第三部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "豊臣家の残党“まぼろし党”と徳川公儀隠密との対決を描くシリーズ完結編「木曾路の凱歌」の映画化。前２作でまぼろし側に財宝のありかを示す山絵図を奪われた公儀隠密が、ここで一気に攻勢に転じる。全編が見せ場の連続。主演の原健作と監督の組田彰造は、翌年「天兵童子」でもコンビを組んでいる。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RISO AMARO", "にがい米", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "イタリアン・ネオ・レアリスモの話題作。北イタリアのポー河流域の水田地帯に、毎年出稼ぎにやって来る女性労働者たちの姿をセミ・ドキュメンタリー・タッチで描く。マンガーノの肉体美と健康なエロティシズムもあって世界的に大ヒット、彼女を一躍トップスターにした。", "1948-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Banquet", "女帝　エンペラー", "", "", "http://image.eiga.k-img.com/images/movie/1905/315x210.jpg?2454", 131, "アジアを代表する女優チャン・ツィイー主演による絢爛豪華な歴史ドラマ。「ハッピー・フューネラル」のフォン・シャオガン監督が、シェイクスピアの戯曲「ハムレット」を基に、古代中国の宮廷で繰り広げられる愛憎劇を描き出す。皇帝である兄を殺して王位を奪ったリー。夫を殺された王妃ワンは、密かに想いを寄せていた皇太子ウールアンまでもが狙われていることを知り、彼を守るために新皇帝リーと結婚するが……。", "2007-06-02", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無頼漢", "無頼漢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "河竹黙阿弥の歌舞伎劇「天衣粉上野初花」（くもにまごううえののはつはな）の映画化。“天保六花撰”の面々が活躍する。河内山宗俊が贋僧に化けて大名屋敷に乗り込み、質屋の娘を救出した上に金をせしめるくだりは痛快。脚本は篠田作品と相性の良かった寺山修司。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ceiling Zero", "無限の青空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "フェデラル航空のニューアーク支局。責任者のジェイクは、旧友ディジーがデンバーから異動してくることを知る。ディジーは有能なパイロットだったが、数々の規則違反で信用ゼロの問題児だった……。航空貨物の定期便を飛ばす会社のオフィスや管制室を舞台に、ここに出入りする様々なスタッフたちの命がけの日々を描いている。", "1936-08-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Geronimo: An American Legend", "ジェロニモ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　1885年、２０年近くにわたって合衆国騎兵隊と戦い続けていたアパッチ族の族長ジェロニモが投降し、ゲイトウッド中尉とデイビス少尉がその護送にあたることに。やがてアパッチ族は保留地で新たな生活を始める。ところが些細な事から騎兵隊が武力行使を行ったため、ジェロニモは暴動を起こし、仲間たちとともに逃走。ゲイトウッドとデイビス、さらにベテランの偵察隊長シーバーらが追跡を開始するが……。ジェロニモを誇り高き勇者として描いたウェスタン・アクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Joe", "ジョー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "　広告代理店の重役コンプトンはドラッグ中毒の娘メリッサが店を破壊したあげくに入院したことを知らされ、怒りのあまり、彼女と同棲していたドラッグの売人を殺してしまう。その後、コンプトンは冷静さを取り戻すべく入ったバーで労働者風の中年男ジョーと知り合う。戦争経験のあるジョーは若者に対する憤りを露わにし、コンプトンと意気投合。その上、彼の殺人に気づいてしまう。そんなジョーとコンプトンの奇妙な友情が始まるが……。世代の相違が生む断絶を描いたシリアスドラマ。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PSYCH−OUT", "嵐の青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "当時のドラッグ＆ヒッピー・カルチャーに肉薄するアメリカン・ニューシネマ風の知られざる名作。J・ニコルソンのハイテンションな熱演、後に「イージー・ライダー」でブレイクする名手L・コヴァックスの幻想的なカメラワークなどが光る青春ムービー。舞台は1960年代のLSD発祥の地サンフランシスコ・ヘイトアシュベリー。サイケデリック・ロックに乗って衝撃のラストまで疾走する。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("組織暴力", "組織暴力", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "利権をめぐって激しい対立をする赤松組と矢東組。関西から、赤松組のバックアップをうけた新生会も乗り込んできた。連合軍に対抗すべく矢東組は武器集めを始めていく。そこに組織解体を求め警察が介入してくるのだが……。拳銃密売をめぐる暴力団同士の闘いと警察の暗闇をリアルなタッチで描いた作品。黒幕に扮して月形龍之介が貫禄をみせる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE HOUR WITH YOU", "君とひととき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "E・ルビッチの代表作「結婚哲学」のトーキー版と言える作品。いかにもルビッチ風といった感じのソフィスティケーテッド・コメディ。主人公の医師夫婦は幸せな結婚生活を送っていたが、妻の友人が夫を誘惑し始めたことで、恋のトラブルが起きるのだった。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARDNERS", "底抜け西部へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "おなじみマーティン、ルイスの底抜けコンビが、今回は西部の天地にひと騒動を巻き起こす傑作コメディ。互いに父を賊に殺されたウェイドとスリム。二人は一人前の青年に成長したが、折しも父を殺した賊の息子たちが町で悪事を働き始める。ウェイドは“殺し屋ジョーンズ”と名前を変えて彼らに近づくが……。シリーズ第17作にあたる。この次に作られた18作目「底抜けのるかそるか」がコンビの最終作になった。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MASCULIN−FE´MININ", "男性・女性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "21歳の青年ポールは、喫茶店で知り合った歌手志望のマドレーヌに恋をして、そのアパートにころがり込む。街には、金で胸を見せる少女や、反戦を訴え焼身自殺する若者たちが……。1960年代のパリっ子たちが、何を考え、どのような言動をしているかを“15の明白な事実”からシネマ・ヴェリテ型式で綴った、ゴダールの佳作。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・ニッポン珍商売", "続・ニッポン珍商売", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ネタ屋、万歳屋、悪酔屋、示談屋、もてさせ屋、断り屋、離婚屋、身代わり屋——いずれも劣らぬしたたか者で無税の珍商売で食っている面々。署長にたっぷりしぼられて彼らの税金取りに奔走する税務署員と、彼らとの攻防戦をコミカルに描いた喜劇。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Troll Hunter", "トロール・ハンター", "", "", "http://image.eiga.k-img.com/images/movie/57495/315x210.jpg?1879", 103, "トーベ・ヤンソンが生んだ人気キャラクター「ムーミン」のモデルとしても知られる、北欧に伝わる伝説の生物トロールの生態に迫るフェイクドキュメンタリー。学校の課題で密猟事件にまつわるドキュメンタリーを制作していたノルウェイの大学生3人が、偶然にもトロールらしき生物をカメラにとらえることに成功する。さらに、夜な夜な森に姿を現す謎の男ハンスの後をつけると、そこには本物のトロールがいた。ハンスはノルウェイの政府機関「トロール保安機関（TSS）」に雇われたハンターで、トロールの存在を民間人から隠し通すことが任務だったが……。", "2012-03-24", "36", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Valentine's Day", "バレンタインデー", "", "", "http://image.eiga.k-img.com/images/movie/54715/315x210.jpg?3474", 117, "「プリティ・ウーマン」「プリティ・プリンセス」のゲイリー・マーシャル監督による恋愛群像劇。ロサンゼルスを舞台に、2月14日のバレンタイン・デイをめぐって、新たに生まれるカップル、別れるカップルがそれぞれに交錯しながら恋に一喜一憂する姿が描かれる。出演はジェシカ・アルバ、ブラッドリー・クーパー、アン・ハサウェイ、ジュリア・ロバーツ、ジェイミー・フォックス、アシュトン・カッチャーほか。", "2010-02-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEET LIES", "スウィート・ライズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "元アラン・ドロン夫人のN・ドロンがメガホンを執ったコミカルなラブ・ストーリー。保険金詐欺を追ってパリにやって来たアメリカ人保険調査員ピーター。彼をめぐって二人のパリジェンヌが恋の火花を散らす。BGMはT・ジョーンズ、ロバート・パーマーというおしゃれな顔ぶれ。D・シャピュイのみずみずしい撮影が素晴らしい。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bloody Sunday", "ブラディ・サンデー", "", "", "http://ecx.images-amazon.com/images/I/510CGV79DVL._SL210_.jpg", 111, "1972年1月30日、北アイルランド・デリーで起こった「血の日曜日事件」をドキュメンタリータッチで再現した社会派ドラマ。2002年・第52回ベルリン国際映画祭で、「千と千尋の神隠し」とともに金熊賞を受賞した。", "2002-01-01", "3,33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A STUDY IN SCARLET", "緋色の研究", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "ドイルの“シャーロック・ホームズ”シリーズのなかでも名作にあたる原作を巧みに映像化。霧にけむるロンドンを舞台に、性格俳優のオーウェンがホームズに扮し、連続殺人事件の謎解きに挑む。A・メイ・ウォンや、駒井哲ら東洋人俳優の出演も見どころ。", "1933-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOMESTIC　DISTURBANCE", "ドメスティック・フィアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "フランクの元妻スーザンが実業家リックと再婚した。しかし、リックの正体は凶悪な犯罪者であり、彼がかつての仲間を殺害する現場をフランクの息子ダニーが目撃してしまう……。別れた最愛の息子のために命を懸ける父親の姿を、俊英ハロルド・ベッカー監督がタイトにまとめ上げたサスペンス・スリラー。J・トラヴォルタのリアルな演技は必見。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE KINDRED", "キンドレッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "新しい生命の創造。それは神と、女性のみに与えられた特権だ。その特権に嫉妬したマッド・サイエンティストのロイドは、人間と節足軟体動物（!?）が一つになった、世にも恐ろしい“キンドレッド”を生んでしまう。SF怪奇ものの一編。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("毎日かあさん", "毎日かあさん", "", "", "http://image.eiga.k-img.com/images/movie/55615/315x210.jpg?3108", 0, "人気漫画家・西原理恵子が毎日新聞に連載するコミックを実写映画化。「かぞくのひけつ」の小林聖太郎監督がメガホンをとり、実生活で元夫婦だった小泉今日子と永瀬正敏が夫婦役を演じる。西原家で実際に起こったエピソードをもとに、主人公の漫画家・サイバラリエコ（小泉）と元戦場カメラマンの夫カモシダユタカ（永瀬）、6歳の息子と4歳の娘が繰り広げる日常をコミカルに描く。", "2011-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スマグラー　おまえの未来を運べ", "スマグラー　おまえの未来を運べ", "", "", "http://image.eiga.k-img.com/images/movie/55925/315x210.jpg?3978", 114, "「闇金ウシジマくん」で知られる真鍋昌平のコミック「SMUGGLER」を、「鮫肌男と桃尻女」の石井克人監督が実写映画化。役者志望のフリーター・砧涼介は、借金返済のために日給5万円の高額アルバイト「運び屋（スマグラー）」を始めるが、運んでいるものが暴力団組長の死体と知り、暗殺者に追われることに……。主演は妻夫木聡。永瀬正敏、松雪泰子、満島ひかり、安藤政信らが共演。", "2011-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エクスマキナ", "エクスマキナ", "", "", "http://image.eiga.k-img.com/images/movie/1955/315x210.jpg?2328", 105, "続発するテロ事件の解決に奔走する特殊部隊所属のデュナン。そんな時、彼女の公私に渡るパートナーで、全身をサイボーグ化したブリアレオスが彼女をかばって重傷を負ってしまうのだが……。モーションキャプチャーを取り入れた3Dアニメ「アップルシード」（04）のシリーズ最新作。原作は士郎正宗（「攻殻機動隊」）のコミックで、ジョン・ウーがプロデュースを担当。衣裳デザインにプラダ、音楽監修に細野晴臣など豪華スタッフが参加。", "2007-10-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("追悼のざわめき", "追悼のざわめき", "", "", "http://image.eiga.k-img.com/images/movie/34165/315x210.jpg?1826", 150, "1988年の初公開時、衝撃的な内容に賛否両論を巻き起こした伝説のカルトムービー。大阪の廃墟ビルで暮らす孤独な青年・誠は、愛するマネキン“菜穂子”に惨殺した女性の生殖器を埋め込み、“愛の結晶”が生まれることを夢見ている。やがて何かに導かれるように、様々な人間が廃墟ビルを訪れ始め……。2007年、鮮明なハイビジョン映像とデジタル音響で甦らせたデジタルリマスター版でリバイバル。11年、再上映。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　PROFESSION　OF　ARMS", "ジョヴァンニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「聖なる酔っぱらいの伝説」の名匠E・オルミが、若くして名を馳せた武将ジョヴァンニの波乱に満ちた生涯を描く歴史ドラマ。16世紀のルネッサンスの技術革新で、一対一の対決から、大砲による無差別攻撃へ変遷した闘いの歴史を通じて、人間の生と死の美学を見つめ直した力作だ。16世紀初頭。神聖ローマ帝国軍は、ローマに攻め込もうとしていた。しかし、ジョヴァンニ率いる教皇軍はその進軍を阻止しようとする。", "2004-11-13", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON THE WATERFRONT", "波止場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "E・カザンのセミ・ドキュメンタリー・タッチの力強い演出とM・ブランドの清新な演技で、アカデミー賞８部門を受賞した1954年を代表する話題作。ボクサーくずれの与太者テリーは自分の兄チャーリーが沖仲仕を牛耳る親分ジョニーの指示で仲間を殺すのを目撃、その妹イディが悲しむ姿に動かされる。ジョニーは口止めと嫌がらせを続けるが、チャーリーが殺されるに及んで、テリーは法廷の証言台に立ち、イディには愛を打ち明けた。仲間のボイコットにもめげずに、テリーは単身先頭に立ち、仲間を目覚めさせる。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JULIA HAS TWO LOVERS", "ジュリアと2人の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "同棲を始めて数年になるジュリアとジャック。ある日、ジャックがプロポーズをするものの、返答できないジュリア。気まずい雰囲気のなか、電話のベルが鳴った……。結婚を考える恋人との生活の前に魅力的な一人の男が現れたらどうするか？　女性の求める真実の愛を描くロマンティック・コメディ。ビデオ発売時タイトルは「ラブ・ファイル」。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOODBYE，MR.CHIPS", "チップス先生さようなら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "英国作家ヒルトンの代表作をミュージカル映画化。生徒たちが敬遠するカタブツ者のチップス先生が、突然ミュージカルの美人女優と結婚したからサァ大変!? 校長やPTAは大騒ぎ。逆に生徒たちとチップス夫婦は大の仲良しになる……。オトゥールの名演と12曲の美しいナンバーが忘れ難い。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ПОТОМОК ЧИНГИС−ХАНА／STORM　OVER　ASIA", "アジアの嵐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ロシア革命の余波が押し寄せるモンゴル。遊牧民の青年バイールは英国兵といさかいになり、山岳地帯に逃げ込む。彼はそこでパルチザンの隊長と知り合い、やがてイギリス軍に立ち向かう反乱軍を率いる運命を歩み始め……。エイゼンシュタインと並び称されるソ連革命映画の旗手V・プドフキンの代表作。", "1929-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PETER PAN", "ピーター・パン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "子供から大人にまで愛され続ける“ピーター・パン”。永遠のファンタジーといわれる同作が舞台初演100周年の記念すべき2004年に実写化された。最新テクノロジーを駆使して創り上げられたネバーランドのイマジネーションあふれる世界は斬新。原作の持つ魅力を最大限に引き出した壮大な物語も見ごたえ十分だ。小説家を夢みる13歳の少女、ウェンディー・ダーリング。ある晩、彼女が部屋で寝ていると、ピーター・パンとティンカー・ベルが忍び込む。驚いたウェンディーに対して、ピーターは、海賊と妖精が住むネバーランド行きを誘う。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍者戦隊カクレンジャー", "忍者戦隊カクレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "現代に復活した妖怪と忍者の末裔たちの闘いを描いたスーパー戦隊シリーズ18作目の劇場版。新築の一軒家で暮らす由美子と茂の姉弟は、両親は不在がちながらも2人で仲良く幸せな毎日を送っていた。そんなある日、由美子と茂の家に奇妙な2人組がやって来る。その正体は、姉弟の家を自分のコレクションにしようと狙う大入道が放ったひとつ目小僧だった。カクレンジャーたちは姉弟を妖怪たちの魔の手から守るべく立ちあがるが……。", "1994-04-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("別れぬ理由", "別れぬ理由", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "外科医の夫と雑誌記者であるその妻は、それぞれ相手の目を盗んで人妻と、同僚の年下男性と浮気をしている。顔を合わせるとそれぞれにしらばくれながらの、綱渡りの浮気合戦をコメディ・タッチで描いた快作。三田佳子、南條玲子の艶技にR指定のついた作品だ。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ILLUMINATA", "天井桟敷のみだらな人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "俳優J・タトゥーロの監督第２作は、20世紀初頭のN.Y.を背景に、とある劇団の人間模様を綴った群像劇。個性派スターの悪ノリ演技が見ものだ。若い役者が舞台上でダウンし、お蔵入りしていた脚本を上演しようと試みる劇作家。ところがバックステージでは、大物スター女優、男色家の中年、劇場オーナーらが身勝手な振る舞いを連発していく。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEMONS ２", "デモンズ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "イタリアン・ホラー界の鬼才ダリオ・アルジェントが、再びL・バーヴァと組んで手掛けた「デモンズ」の続編。今回はTVモニターからデモンズが次々と出現、高層マンションを舞台に絶望的な戦いが展開する。", "1986-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帰って来た女必殺拳", "帰って来た女必殺拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "幼友だちを探しに香港から横浜にやって来たヒロインが、友たちが麻薬密輸組織のボスによって麻薬患者にされていることを知り怒りを爆発させる。山本麟一が、麻薬密売組織のボスを迫力満点に怪演している。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("濡れ髪三度笠", "濡れ髪三度笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "市川雷蔵の主演作を数多く手掛けた田中徳三監督の作品で、これは雷蔵にしては珍しいユーモアと諷刺のきいた道中もの。旅ガラスの濡れ髪の半次郎は、鉄火肌のお蔦、のんきな弥次さん、喜多さんと旅を続けていたが、ある宿で将軍家斉の38番目の若君・長野助と出会い、命を助ける。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN ROULETTE", "アメリカン・ルーレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "内戦の続く故国をあとにイギリスへ亡命してきた中南米某国の若き大統領カルロス。彼が暗殺者グループとの戦いや同志である女性ケイトとの愛などを通じて人間的に成長していく姿を描く。A・ガルシアの若き日の主演作で、サスペンスフルな好編。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WHOLE NINE YARDS", "隣のヒットマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "モントリオールの歯科医オズは義父が遺した借金のために生活は苦しく、妻との間も冷え切っていた。そんな夫妻の隣に引っ越してきたのがシカゴを拠点にしていた伝説の殺し屋ジミー・チュデスキ。妻はオズに、ジミーを父の仇と狙うマフィアのボス、ヤンニに彼の居場所を密告して発見料をもらうことを提案。オズはシカゴに飛ぶが、そこでジミーの妻シンシアと出会う。ジミーに命を狙われているという彼女に同情し、一夜をともにしたオズは本気でシンシアに恋してしまう……。お人よしのオズの周りに渦巻く殺意の嵐と殺人狂想を、バカバカしくもセンス良く捉えたブラック・コメディ。B・ウィリスが終始強面でジミーを演じることで笑いを取っている。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　ばいきんまんの逆襲", "それいけ！アンパンマン　ばいきんまんの逆襲", "", "", "http://ecx.images-amazon.com/images/I/51F5WnEdzqL._SL210_.jpg", 70, "アンパンマンと仲間たちの活躍を描く人気アニメの劇場版第2弾。ある日バイキン仙人から、不思議な力を持つ「メコイスの壺（つぼ）」の話を聞かされたばいきんまんは、壺が眠っているというピラミッドへと向かう。同じくメコイスの壺を探していたヤーダ姫が先に壺を発見するが、ばいきんまんによって奪われてしまう。取り戻そうとするヤーダ姫とアンパンマンたちの前に、壺の力によって巨大なばいきん大魔王となったばいきんまんが立ちはだかり……。", "1990-07-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("１０２　BANGKOK　ROBBERY", "デッドライン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1990年代後半のタイの経済の悪化を背景に、国家転覆を図るテロリストと警察軍の攻防を描いた本格派のアクション。バンコク市内での大規模なロケや、大量の火薬を投入した銃撃戦など、スケールの大きな見せ場に圧倒される。復讐や使命感など、熱い思いに彩られた人間ドラマも見応え十分だ。国家に冷遇された特殊部隊員が爆弾テロを計画。これを阻止すべく、タイ警察が奔走する。だが、やがて一味の意外な目的が発覚し……。", "2005-10-22", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花はんめ", "花はんめ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "在日韓国・朝鮮人のはんめ（在日のおばあちゃん）たちの日常を、在日２世の金聖雄監督が４年間追ったドキュメンタリー映画。80歳を越えたはんめたちが、歴史の渦に飲み込まれながら様々な苦労の末に手に入れた現在の穏やかな“日常”を、金聖雄監督が愛情あふれる目線で綴った好編。在日韓国・朝鮮人が多く暮らす街、神奈川県川崎市桜本。同区の路地裏の狭いアパートの一室では、皆から慕われる孫分玉さんを囲んで、今日もおばあちゃんたちが集う。そして、キムチとナムルが並ぶ食卓を囲み、楽しく語らう。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE JOURNAL D'UNE FEMME DE CHAMBRE", "小間使の日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ブニュエル映画として初めて上映禁止にもスキャンダルにもならず世界的にヒットした、製作のシルベルマン、脚本のカリエールとの記念すべき第1作。小間使セレスティーヌがパリからノルマンディーの田舎へやってきた。典型的なブルジョワ生活を送る家人の口うるさい日々に、セレスティーヌは嫌悪感を抱き始める。そんな時、残忍な殺人事件が起こる。犯人をつきとめようとするセレスティーヌの好奇心の行方は……。", "1966-04-30", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("硯床／INK STONE", "硯〈すずり〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "不能の夫の願い。それは使用人とセックスさせてでも妻に子宝を授かることだった……。初めは嫌がったが、やがて愛欲に溺れていく新妻と、嫉妬に燃える夫が迎える奇妙なてん末を、ブラック・ユーモアたっぷりに綴っていく。タイトルは“ベッド”にもなる巨大な“すずり”を意味しており、中国らしい艶笑話の強烈なオチとなっている。", "1996-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN IN BLACK", "黒服の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "資産家ヘンリー・クレーブリングは、財産目当ての二番目の妻バーサによって殺される。そこへ先妻の娘ジョーンが戻ってきた。ジョーンの味方は、召使のハドソン老人ただ一人。ところが、そのハドソンも殺害されてしまった。やがてバーサは、ジョーンに恐怖を与え、財産を放棄させようと計画、亡きヘンリーの霊を呼びよせようと偽りの交霊を行う。だが、そこに現れたのは……。ミステリアスな謎と、オカルティックなムードで展開するイギリス、ハマー・フィルムの作品。", "1950-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA GRANDE ILLUSION", "大いなる幻影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "第一次世界大戦下、フランス軍のマレシャル中尉とボアルデュ大尉はドイツ軍の捕虜となる。そして捕虜収容所からの脱走……。出身階級や身分や貧富などの差が何の意味もなくなる戦争。その残酷な戦争における人間と人間の絆をヒューマニズム豊かに描いた名作。", "1937-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シャ乱Qの演歌の花道", "シャ乱Qの演歌の花道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ピンク映画時代にはスラップスティックな喜劇を量産していた滝田洋二郎が、手掛がけた豪快で破天荒なサクセス・コメディ。シャ乱Qのつんくを主演に迎え、彼の持ち味を見事に生かしながら、金と女のために成り上がろうと奮闘する青年の姿を快調に描いていく。売れないバンドのボーカル、乱之介が弱小芸能プロダクションにスカウトされた。ところが、やるのはロックではなく、演歌。野望のために割り切った乱之介は、ひょんなことから“覆面歌手”として一世を風靡していく……。細かいエピソードを積み重ね、主人公の無責任ぶりに勢いをつけ、ラストにはアッと驚く結末を用意。ツボを押さえた笑いと娯楽映画の基本をふまえた展開で、大いに楽しめる内容だ。事務所のサエない先輩役の尾藤イサオや、気丈すぎるヒロイン瀬戸朝香らの好演も見逃せない。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DR.JEKYLL ＆ MR.HYDE", "狂へる悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 63, "飲めば人格が豹変するという秘薬を発明したジキル博士は、自らその薬の実験台となり、狂暴化して街をさまよう……。有名なR・スティーヴンソンの小説『ジキル博士とハイド氏』をもとに、何度も映画化されたホラー映画の元祖といえる無声映画。J・バリモアの演じるハイド氏の壮絶な演技は今も語り草で、その瞳に宿る狂気は背筋が寒くなるほど。", "1920-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人生劇場・第一部／第二部", "人生劇場・第一部／第二部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 216, "豪家の一人息子で東京の大学に出ている瓢吉という男が、大学で総長夫人像の建設反対運動を展開。吹岡、夏村といった友人とともに戦うが、お袖という女をめぐる三角関係や運動の敗退などで散りぢりとなってしまう。やがて瓢吉と吹岡は文学を志し、ライバル関係となるが……。尾崎士郎のおなじみの原作を、俳優の佐分利信が監督にあたり、当時の東映社長・大川博が直接製作にあたった芸術祭参加作品。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHAT A WAY TO GO！", "何という生き方！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "落ちぶれた人を必ず幸福にするヒロインと男たちの“災難”をユーモラスに描いたコメディ。当時のラブコメの女王ともいえるマクレーンが、チャキチャキした演技で魅せる。お金持ちが嫌いなルイザはリッチな彼氏をふって、やる気のない男と結婚した。すると、夫は商売が繁盛し働き過ぎで死亡。再婚相手も金持ちになり死亡。その連鎖が次々と……。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いわさきちひろ 27歳の旅立ち", "いわさきちひろ 27歳の旅立ち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "", "2012-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若い仲間たち・　うちら祇園の舞妓はん", "若い仲間たち・　うちら祇園の舞妓はん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "祇園の舞妓・トリ子と、彼女とは双子の姉妹でありながら祇園を飛び出して人気歌手になったヒナ子。それに３人娘のミエ子・ゆかり・マリ子らが一人の男性を相手に繰り広げるドラマをミュージカル仕立てで描いた作品。ザ・ピーナッツはじめ、当時の流行歌手が勢ぞろい。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姿なき追跡者", "姿なき追跡者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "復讐に燃える男の正体探しをメインにした、アクション作品。秋津組の跡目相続人、黒住俊道が殺された。ボスの秋津は残った幹部に、黒住の弟・健次を相続人とし、彼が必ず復讐に来ると遺言して死ぬ。まもなく、秋津組の縄張りに、ニセ刑事の島亮太郎と殺し屋の吾郎が現れる。幹部たちは、二人のどちらかが健次に違いないとにらむのだが……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOON FLEET", "ムーンフリート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "18世紀のイギリス南部の村ムーンフリートは植民地戦争のあおりで密輸が横行している。かつてその村の名家だったモフーン家の子孫の少年ジョンは、家を再興しようと大志を抱くが……。イギリスでは知らぬ者はいないという有名な冒険小説を原作に、名匠F・ラングが映画化した娯楽大作。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("田禹治", "チョン・ウチ　時空道士", "", "", "http://image.eiga.k-img.com/images/movie/56205/315x210.jpg?2110", 136, "神の力が宿る笛が2つに割れ、その欠片を手にした道士ファダムは、もう1つの欠片を持つチョングァン大師を殺害し、その弟子チョン・ウチに罪を着せる。チョン・ウチは仙人たちから掛け軸に封印されてしまうが、その間際にファダムから笛を奪い返す。500年後の現代、封印が解けた妖怪たちを倒すため、仙人たちはチョン・ウチを復活させるが……。韓国の古典英雄小説「田禹治伝」をもとにしたヒーローアクション。主演は「義兄弟」「デュエリスト」のカン・ドンウォン。", "2011-07-02", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冬の日", "冬の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "人形作家、川本喜八郎が世界中のアニメ作家に声をかけて実現した、総勢35名によるアニメ・コラボレーション。松尾芭蕉の『芭蕉七部集』の『冬の日』で試みられた“連句”という句を詠みつないでいく手法で、計36本の短編アニメが連鎖する。ユーリ・ノルシュテインから高畑勲にいたる顔ぶれは壮観の一言。岸田今日子による朗読、池辺晋一郎の音楽も素晴らしい。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("待っていた極道", "待っていた極道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "大阪釜ヶ崎を仕切る島村清吉は、労働者アパート建設を公約して当選しながら実行しない真弓田を追って東京を訪問。浅草・山谷の小仏一家の親分・小堀と兄弟分の盃を交わす。やがて、山谷の労働者を酷使する悪徳ヤクザがのさばりだして、怒りに燃える島村が叩きのめす。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女番長", "女番長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "大阪梅田を舞台に、対立する女番長・関東小政と魔耶が、共通の敵である地元の暴力団・北竜会へ一致協力して殴り込みをかけ、最後はダイナマイトでぶっ飛ばす。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAXIMUM OVERDRIVE", "地獄のデビルトラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "短編集『深夜勤務』の中の一編、『トラック』をS・キング自らが初監督した作品。1987年、大接近した彗星の影響で地球上の機械が意思を持ち人間に反乱を起こし始める。ドライブインに閉じ込められ、大型トラックの軍団に包囲された人々は勇気と知恵の限りをつくして脱出を図る。なかなか物量をかけているがアメリカでは興行大敗。日本でも小規模の公開に終わった。音楽を手掛けたのは、キングお気に入りのAC/DC。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伝説の舞姫・崔承喜〈チェ・スンヒ〉／金梅子〈キム・メジャ〉が追う民族の心", "伝説の舞姫・崔承喜〈チェ・スンヒ〉／金梅子〈キム・メジャ〉が追う民族の心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1911年にソウルで生まれ、1930年代に“世紀の舞姫”として世界的に一世を風靡したチェ・スンヒ。1960年代以降消息不明となった彼女の足跡を、現在韓国舞踏界で第一人者として活躍中のキム・メジャが訪ねるという趣向のドキュメンタリー。監督は「ルイズ　その旅立ち」で知られる記録映画作家、藤原智子。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUNNYMAN", "ファニーマン 血染めのジョーカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "神秘的なイギリス人のカラム・チャンスはポーカーで負け、先祖代々の家をレコード・プロデューサーのマックス・テイラーに取られてしまう。マックスは家族を連れ館に遊びに出かけたが、そこでは残虐なファニーマンの狂気の世界が繰り広げられるのだった……。狂気の目玉刺し、必殺脳髄押し出しなど、残虐シーンが見もののスプラッタ・ホラー。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Prom Night（2008）", "プロムナイト（2008）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チャカ２", "チャカ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "持病持ちのヒットマンの純愛を描いた「チャカ・LONELY HITMAN」の前日談。ハンバーガーショップで銃撃戦が勃発。ヤクザの島は、店員の青年、貴志にかくまわれる。やがて、貴志の死んだ父親と島の思わぬ因縁が明かるみになり……。旧式のリボルバーをめぐる、男たちの宿命。切ない人間模様が、前作同様、渡辺武監督、竹内力主演のコンビで、センチメンタルに綴られていく。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I，THE JURY", "探偵マイク・ハマー 俺が掟だ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "M・スピレーンのハードボイルド小説の映画化。親友が巻き込まれた殺人事件を追う探偵マイク・ハマーの活躍ぶりを描く。L・コーエンの脚本、ヘフロンの演出とも水準を超えているが、ハマー役のアサンテがいかにもミス・キャスト。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小原庄助さん", "小原庄助さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "富士山麓に住む“小原庄助さん”こと杉本左平太は、先祖伝来の家屋敷や田畑を村で、一番持っていた。村人は次から次へと寄附金や頼みごとをし、左平太は生来の人の良さから彼の財産を人々に分け与えた。村人から村長選挙に立候補することを頼まれればしぶしぶ承諾し、演説会では相手候補の応援をしてしまう。こうして左平太は財産を使い果たす。ある夜、強盗が押し入ったが左平太は“遅かったね、もう少し早ければ間に合ったのに”と言い、酒をすすめるのだった……。有名な民謡にヒントを得たユーモアあふれる傑作。主人公の左平太には、監督・清水宏その人が投影されており、剣戟スター、大河内伝次郎が巧演、岸松雄の脚本も、清水宏の長所を生かすコツを心得ている。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANGER ZONE", "バックラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アフリカの某共和国の鉱山で働くモーガンのところに、武器商人時代の仲間スコットが有毒廃棄物の缶を持ち込んでくる。そして缶の一つが流出し、村が全滅したために、モーガンはクビを切られてしまう。それから一年後、モーガンは国防担当官デュポンから残りの廃棄物の捜索を依頼され、国連のキム博士と行動をともにするうち、廃棄物が紛失した核燃料であることを知る……。主人公と密輸組織との攻防を描くサスペンス・アクション。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER THE ETERNAL SKY", "チンギス・ハーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "12～13世紀、モンゴルに君臨した英雄チンギス・ハーンの数奇な生涯を描いた作品。不幸な少年時代から宿敵タタール族との決戦、義兄弟との対決を経てモンゴルを統一したテムジン（チンギス・ハーンの幼名）が、やがて中国から東ヨーロッパにまたがる大帝国を築くまでを、壮大なスケールで描く。万里の長城からモンゴルの大草原、シルクロード、そして地中海まで、大規模なロケを敢行し、参加した人馬はのべ10万騎と、まさに“壮大”という言葉がぴったり。合戦シーンも迫力にあふれ、見ごたえのある娯楽作に仕上がっている。監督は大作「マンドハイ」に続き、再びダイナミックな演出を見せるB・バルジンニャム。", "1991-01-01", "57", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KICK BOXER", "キックボクサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "敬愛する全米キックボクシング・ヘビー級チャンピオンだった兄を、容赦ない過剰攻撃で下半身不随に追い込み、さらにその愛する恋人までも傷つけた地上最強最悪のキックボクサーに対し、弟が敢然と立ち向かっていく。ベルギー出身の肉体派俳優、J・C・ヴァン・ダムが、痛快無比な荒技を披露してくれるバトル・アクション。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誰〈た〉がために", "誰〈た〉がために", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "新人・日向寺太郎監督が現代日本が抱える問題に果敢に挑んだ、聡明かつ野心的なデビュー作。妊娠中の妻を惨殺された青年が抱く、少年法によって保護された犯人への怒りを見つめ、その鮮烈なラストと共に、見る者に深い余韻を与える。人間の可能性を全身で演じた浅野忠信の名演も絶品。東京の下町で写真館を営む民郎は、熱愛の末に結ばれた亜弥子と結婚。しかし妊娠中の彼女は見知らぬ少年に惨殺されてしまい、民郎は怒りに苛まれる。", "2005-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Time That Remains", "時の彼方へ", "", "", "http://image.eiga.k-img.com/images/movie/54985/315x210.jpg?4620", 109, "パレスチナ問題を扱ったコメディ「D.I.」でカンヌ映画祭2冠に輝いたエリア・スレイマン監督が、イスラエルで生きるパレスチナ人家族を父と息子の2代に渡って描いた半自伝的ドラマ。ナザレ降伏前の1948年から1970年、1980年を経て現在に至るまで4つの時代ごとの家族の姿を通し、イスラエル建国から現代に及ぶパレスチナ史を浮かび上がらせていく。現代パートには成長した息子役としてスレイマン監督自身が出演している。", "2009-10-18", "2,3,4,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Boondock Saints", "処刑人", "", "", "http://image.eiga.k-img.com/images/movie/1305/315x210.jpg?3716", 110, "サウスボストンの精肉工場に勤めるコナーとマーフィーのマクマナス兄弟。2人は行きつけのバーでロシアン・マフィアと騒動を起こした末、正当防衛で彼らを殺してしまう。無罪放免となった兄弟は、その夜神からのある啓示を受ける。「悪なる者を滅ぼし、善なる者を栄えさせよ──」出所した2人は早速大量の武器を購入し、マフィアの幹部や殺し屋などを次々と“処刑”していく。", "2001-02-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIRTY WEEKEND", "ダーティ・ウィークエンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "恋人の浮気が原因で、田舎町でひっそりと暮らし始めたベラ。だが、向かいに住む男が彼女を監視し、いたずら電話までかけてくる始末。怒りが頂点に達したベラは男を撲殺。やがて、彼女は言い寄る男すべてに怒りを向け始める……。イギリスでベストセラーとなった小説をもとに描かれる、センセーショナルなサスペンス。", "1993-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBOT　JOX", "ロボ・ジョックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "巨大ロボットのバトルを描くSFアクション。舞台は核戦争によって荒廃した地球。米ソ両陣営は、巨大ロボットによる一対一の戦いで領土を奪い合い、ロボットを操るパイロットは“ロボ・ジョックス”と呼ばれ、国民的な英雄に祭り上げられていた。米国の花形ロボ・ジョックス、アキリスは戦いのさなかの事故で、一度は引退を決意するが……。S・ゴードン監督は日本のロボット・アニメに影響を受けて本作を作ったとのこと。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ガンダムF91", "機動戦士ガンダムF91", "", "", "http://ecx.images-amazon.com/images/I/511YM23EMKL._SL210_.jpg", 115, "富野由悠季監督ら「機動戦士ガンダム」のメインスタッフが再結集。新たな時代設定と登場人物により描かれた劇場オリジナル作品。宇宙世紀0123年、平和に慣れきり腐敗した地球連邦政府に対し、コスモ貴族主義を掲げるクロスボーン・バンガード（CV）が宣戦を布告。民間人の少年シーブック・アノーはその混乱のなか、連邦軍の最新鋭モビルスーツ、F91に乗り戦うことになる。しかし、アノーの前に幼なじみのセシリーがCVの女王ベラ・ロナとして現れ……。", "1991-03-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STACY", "STACY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "15歳から17歳までの少女が次々とゾンビになっていくという現象が起こり、世界中がパニックに陥る。彼女たちはステーシーと呼ばれ、165ヵ所以上切り刻まないと死ぬことはない。そんな血と殺りくの世界で、ステーシー化が迫る詠子と渋川は出会い恋に落ちるが……。大槻ケンヂ原作の新感覚ホラー＆ラブ・ストーリー。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日が暮れても彼女と歩いてた", "日が暮れても彼女と歩いてた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "女の子同士の複雑な友情やすれ違いをつづったビターな青春ドラマ。ファミレスで働くサヤカと絵里は、外見も性格も正反対なのに大の仲良し。しかし、絵里のアメリカ留学が決まったことで、2人の別れの時が近づいていた。そして絵里の出発の日が迫ったある日、ささいな事件から2人の気持ちが離ればなれになってしまい……。監督は、ぴあフィルムフェスティバル2003で注目を集めた「あのコがいねぇ」の高柳元気。", "2007-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("loudQUIETloud : A Film about the Pixeies", "ピクシーズ　ラウド・クァイエット・ラウド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "初期オルタナティブロックシーンを牽引し、轟音ギターと独特のポップセンスで、ニルバーナのカート・コバーンなど後のミュージシャンたちに多大な影響を与えたピクシーズ。1992年の解散から12年の空白を経た2004年に行なわれた奇跡の再結成ツアーの模様を捉えたドキュメンタリー。多くのファンを魅了するステージはもちろん、ツアー中に起こった様々な出来事を赤裸々に綴ることで、メンバーたちの素顔に迫る。", "2007-02-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こほろぎ嬢", "こほろぎ嬢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "昭和初期に多数の傑作を生みだしながら、その後半生が長らく謎とされてきた幻の女流作家・尾崎翠。1998年に尾崎の代表作「第七官界彷徨」を彼女の晩年の姿とともに映画化して世界各国で高く評価された浜野佐知監督が、尾崎が筆を置く直前に手がけた3本の短編「歩行」「こほろぎ嬢」「地下室アントンの一夜」を連作として映像化。どこか風変わりで孤独な登場人物たちが織りなす幻想的な恋の物語を描く。", "2007-01-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROADKILL", "ロードキル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "地方巡業中のロック・グループ、ドキュメンタリー映画作家、希望をなくしたスター、連続殺人の準備をする青年、性に憧れる少年。様々な男たちと出会いながら一人旅を続ける女が、次第に自分に目覚めていくさまを描く。社会からはみ出したアウトローたちを主人公にした異色のロード・ムービー。", "1989-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("年をとった鰐", "年をとった鰐", "", "", "http://image.eiga.k-img.com/images/movie/33595/315x210.jpg?2774", 13, "「頭山」でアカデミー賞短編アニメーション部門にノミネートされた山村浩二が、フランスの童話作家レオポルド・ショボーの「年をとったワニの話」を映像化。ピラミッド建立の頃から生きている年老いたワニ。魚もとれなくなってしまったワニは、近くにいた自分のひ孫を食べてしまう。一族から追い出され、海へたどり着いたワニを救ったのは、優しいタコの女の子。しかしお腹が空いたワニは、我慢できずに彼女の足を毎晩1本ずつ食べていく。", "2006-08-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春の蹉跌", "青春の蹉跌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "石川達三の小説を原作にしているが、アメリカ映画「陽のあたる場所」を思い起こさせる内容である。社会的野心を抱く主人公が、金持ちの伯父の娘である従妹との結婚のため、妊娠した恋人が邪魔になり、雪山で絞殺して死体を埋めるが警察の手が延びて……。冒頭、海辺のレストランをローラー・スケートですべりながら椅子をかたづける、ショーケンの姿が青春のやるせなさを漂わせている。以降、桃井かおりを背負って心中で民謡をつぶやきながら雪山の斜面をダラダラとすべる長い場面から鮮烈なラストのストップ・モーションまで、青春の虚脱感を神代監督が即興的な演出を織りまぜつつ巧みに表現していてる。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("High Fidelity", "ハイ・フィデリティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "シカゴで中古レコード店を経営するロブは、アルバイトの音楽ジャンキー2人と音楽談義に花を咲かせる毎日。音楽に関しては人並み以上の愛情を自負する彼だが、こと恋愛に関しては失敗ばかり。最近も恋人ローラにフラれたばかりだ。強がってみても、日増しに強まっていく彼女への想い。そこで彼は“これまで辛かった失恋体験トップ5”をリストアップ、自分を捨てた女性たちを訪ね“自分の何がいけなかったのか”を問いただそうとする。", "2001-03-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE YOUNG AMERICANS", "プレイデッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "個性派H・カイテルの主演によるフィルム・ノワール調の刑事ドラマ。麻薬王を追ってロンドンを訪れたニューヨークの敏腕刑事とおとり捜査に担ぎ出されたディスコで働く若者の葛藤を、バイオレンス・シーンを交えてサスペンスフルに描き出す。アイスランドの歌姫ビョークが歌う主題歌も作品のクールなムードをもり立てる。", "1993-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BURIED ALIVE", "エドガー・アラン・ポー 早すぎた埋葬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "E・A・ポーの同名怪奇小説を現代風にアレンジして映画化。原作とは異なり、舞台を少女鑑別所に移し替えて、閉鎖された空間で幻想の恐怖のドラマが展開されていく。新任の鑑別所教師ジャネットは、幻覚の中で少女失踪事件の真相を知ってしまい……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ダイブ!!", "ダイブ!!", "", "", "http://image.eiga.k-img.com/images/movie/53375/315x210.jpg?3099", 116, "幼い頃、同じ年代の要一の飛び込みに惹きつけられてダイビングクラブに通い始めた知季。現在中学生の彼は、経営悪化で存続の危機に立たされたクラブを救うため、アメリカ帰りの美人コーチの下で仲間たちと一緒にオリンピックを目指すことになり……。直木賞作家・森絵都のベストセラー小説を、「バッテリー」の林遣都、本作が映画初出演の溝端淳平ら若手イケメン俳優共演で映画化。監督は「ニライカナイからの手紙」の熊澤尚人。", "2008-06-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("平成名探偵・阪田京介", "平成名探偵・阪田京介", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "“アホの坂田”でお馴染みのお笑い芸人、坂田利夫が大マジメな演技で望んだ初主演映画。坂田演じるのは、元大阪府警の敏腕刑事で、現在探偵をしている男、阪田京介。阪田の行きつけのうどん屋の主人は元鍵師で、阪田に自分の娘・知草のボディーガードを頼むが、知草が何者かに誘拐されてしまう。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏の秘密", "夏の秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "過去を消すために次々と殺人を犯す実の母の存在によって、普通の高校生活を送っていた娘の青春に、徐々に暗い影がさしていく。ティーン・モデル出身の３人組、パンジー主演の青春サスペンス・メロドラマ。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TALENT FOR THE GAME", "ドリーム・ゲーム 夢を追う男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「打撃王」（1942）から「ナチュラル」（1984）、そして「メジャーリーグ」（1989）と傑作の多い野球映画。この映画も大リーグ“カリフォルニア・エンジェルス”を舞台にしたその１本だ。スカウトマンのバージルは、金もうけ主義の新オーナーからクビを宣告される。最後のチャンスとして、スカウトの必要性を納得させるだけの選手を探してくることに。偶然立ち寄った町で、草野球のピッチャー、サミーに目をつけるが……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三十九枚の年賀状", "三十九枚の年賀状", "", "", "http://image.eiga.k-img.com/images/movie/54655/315x210.jpg?2851", 114, "第2次世界大戦後の宮崎を舞台に、青年兵と少女の純愛を描く青春ドラマ。太平洋戦争終結の3日前、青年兵の河村とユリ枝は恋に落ち、視線を交わすだけで愛を育んでいた。時代に翻弄されながらも、無償の愛を貫こうとする2人だったが……。青年時代の河村とユリ枝を若手俳優の颯太と夏未エレナ、大人になった2人を麻生祐未と風間トオルが演じる。監督はCM作家の図師三千男。", "2009-06-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABY BABY BABY！", "BABY BABY BABY！", "", "", "http://image.eiga.k-img.com/images/movie/53865/315x210.jpg?3001", 119, "観月ありさ、松下由樹、神田うのら、人気TVドラマ「ナースのお仕事」のキャスト・スタッフが再結集し、妊娠・出産をテーマに描いたハートフルコメディ。大手出版社に勤める雑誌編集者の佐々木陽子は、編集長就任のチャンスをつかんだ矢先、予定外の妊娠が発覚。戸惑いながらも産婦人科を訪れた彼女は、ベテラン妊婦の春江や元モデルのレイナら様々な事情を抱える人々との交流を通し、出産の喜びや難しさを学んでいく。", "2009-05-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高山下的花環／WREATHS AT THE FOOT OF THE MOUNTAIN", "戦場に捧げる花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 148, "中越国境戦争を背景とした人気小説『高山下的花環』を映画化。連隊長・梁三喜が休暇に出ようという日、新任政治指導員の趙蒙生が配属されてくる。彼はいわゆるモヤシっ子。党の実力者である母が息子の経歴にハクをつけるため送り込んだものだった。やがて連隊は最前線任務に就くことになり……。百花奨最優秀作品賞受賞作。", "1984-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男が爆発する", "男が爆発する", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "八ヶ岳のふもとの農場を継いだ若者・謙作は事故がもとで東京の病院に入院する。入院費に困り、父と離婚した母を訪ねるが、女社長の母にはほかに息子がいた。そして謙作は異父兄弟がたくらむ八ヶ岳の土地買収計画に巻きこまれていく……。地方を舞台にした、裕次郎＝舛田利雄の名コンビで描くアクションもの。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("起毛球了", "恋愛回遊魚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "30歳目前のダメ男と魅力的な自称レズビアン高校生の、今どきの恋愛模様をスマートで都会的な眼差しで描いたラブ・ストーリー。大胆なアングルや意表をつく色使い、優しさと切なさが漂う空気感は、これまでの台湾映画界には見られない作風。監督のウー・ミーセンは、この作品の成功によって台湾映画次世代のクリエーターとして注目を浴びた。", "2000-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("岸壁の母", "岸壁の母", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "二葉百合子の同名曲のヒットに便乗して、端野いせ原作『未帰還兵の母』を映画化。戦地へ行ったまま帰らぬ息子を待ちつづける母親の姿を描く。中村玉緒が昭和初期の頃の若い母親から戦後の鬼気せまる白髪の老母までを演じ、母の息子への愛の執念を感じさせる。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OMEGA MAN", "地球最後の男 オメガマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "中ソ間で起きた細菌戦争で人類が次々と死滅し、かろうじて生存した者も醜い容貌に変わってしまう。この事態を救うべく、科学者ヘストンが立ち上がるのだが……。1977年のプラハを想定して、C・ヘストン自らが企画し映画化にこぎつけた意欲作。原作は「激突！」のR・マシスン。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しあわせ家族計画", "しあわせ家族計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "家族のために奮闘する父親の姿を描く涙と笑いのホーム・コメディ。父、富士夫が会社をリストラされてしまい、一家は妻の実家に転がり込むことになる。再就職のあてもなく肩身の狭い思いをしていた富士夫に舞い込んだのは、家族の夢のために課題に挑戦するTV番組『しあわせ家族計画』への出演。彼はみんなの期待を背負ってピアノの特訓を開始するが……。頼りない父親に扮する三浦友和ほか、豪華キャストが絶妙のアンサンブルを披露する。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAPPY NEW '49", "ハッピー'49", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "1948年、ユーゴはソビエト＝スターリンと訣別、独自の社会主義路線を歩み始める。ソビエトからの引き揚げ者が、続々と駅に着く。ボダもその一人だった。懐かしいはずの我が家の荒廃ぶりに驚くボダ……。激動の社会で生きる人々の姿をホロ苦くも切なく描く。", "1986-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOONEY　TUNES　BACK　IN　ACTION", "ルーニー・テューンズ：バック・イン・アクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ワーナー・ブラザースの人気アニメ『ルーニー・テューンズ』のキャラクターたちがハリウッド俳優と共演。宿命のライバル、バックス・バニーとダフィー・ダックが手を取り合い、“ハムナプトラ”シリーズのB・フレイザー扮する警備員らとともに大冒険を繰り広げる。実写とアニメを完璧に合体させた「グレムリン」のJ・ダンテ監督の手腕も見事。ワーナー・ブラザースの副社長ケイトにクビにされたNo.2スター、ダフィー・ダックとガードマンのD.J.。彼らは秘宝のありかを知っているため悪漢に誘拐されたD.J.の父親を救出すべく、世界各国を飛び回り大冒険を繰り広げる。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("石井輝男 FAN CLUB", "石井輝男 FAN CLUB", "", "", "http://ecx.images-amazon.com/images/I/51lLZJt7RML._SL210_.jpg", 80, "「網走番外地」シリーズなどのヒット作を手がける一方で、数々のカルト的名作を世に送りだした石井輝男監督。2005年に81歳でこの世を去った石井の遺作で、リリー・フランキーら異色キャストの共演でも話題を集めた「盲獣VS一寸法師」の撮影現場を、「鬼畜大宴会」の奇才・熊切義和監督が撮影したドキュメンタリー。最晩年の様子や演出風景などを追い、“キング・オブ・カルト”として多くの熱狂的ファンに支持されてきた石井の素顔に迫る。", "2006-08-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APOLLO 13", "アポロ13", "", "", "http://ecx.images-amazon.com/images/I/41G67VHKYBL._SL210_.jpg", 141, "月への飛行中に爆発事故を起こした、アポロ13号の救出作戦を描いた作品。アポロ11号の月着陸成功から９ヵ月後の1970年４月、アポロ13号は３人の宇宙飛行士を乗せて打ち上げられた。しかし、月まであと一歩というところで爆発事故に遭遇、その影響で電力供給システムが壊滅的ダメージを受けてしまう。このまま飛行を続ければ地球には戻れないという状況で、NASAは総力を結集して３人の宇宙飛行士の救出にかかる……。監督のR・ハワードはおおげさになりがちなドラマを、綿密な取材と抑えられた演出により好感の持てる作品に仕上げた。また、コンピュータを使ったデジタル処理により、発射するロケットのアップなどが作り出されているのにも注目。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CUISINE AU BEURRE", "バター料理騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "フランス喜劇全盛期の1963年に発表された、G・グランジェ監督の作品。バター料理をめぐって奔走する人々の滑稽な姿を描いている。エスプリと笑いのなかにも、ホロリと泣かせるシリアスなシーンを織り込んだ、ユニークな仕上がり。", "1963-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("落第はしたけれど", "落第はしたけれど", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "小津安二郎得意のカレッジものの１作。卒業試験を前に、猛勉強やカンニング作戦に精を出す学生たち。喫茶店の娘、小夜子に励まされながら卒業を目指して頑張る学生たちだが、さて試験の結果は……。サイレントとは思えないほど、いきいきと騒がしい雰囲気で撮られた傑作コメディ。", "1930-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL　CORAZON　DEL　GUERRERO", "クィーン＆ウォリアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "伝説の魔法の国で、トレジャーハンターのベルダールは貴重な石を盗んだ罪で“千の目の教団”に呪いをかけられ、彼は見たことのない別世界で、16歳の少年ラモンとして生まれ変わっていた。そしてラモンがはまっているロールプレイング・ゲームの中で戦う理想のヒーローこそ、かつてのベルダールだった……。交錯する虚構と現実の中で、呪いを解くために魔術師を殺さなければならないラモンの試練と闘いを、デジタル技術を駆使して描くファンタスティック・アドベンチャー。", "2000-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vier Minuten", "4分間のピアニスト", "", "", "http://image.eiga.k-img.com/images/movie/34305/315x210.jpg?2435", 115, "刑務所に囚われた天才ピアニストと女性ピアノ教師の交流を描き、本国ドイツで大ヒットを記録した人間ドラマ。年老いたピアノ教師クリューガーは、刑務所に服役中の少女ジェニーがピアノの才能に恵まれていることを見抜き特別レッスンを始める。ジェニーは自分の才能を信じてくれるクリューガーに対し徐々に心を開いていくが、コンテスト決勝を目前にしたある日、暴力事件を起こしてピアノを禁じられてしまい……。", "2007-11-10", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嗤う伊右衛門〈わらういえもん〉", "嗤う伊右衛門〈わらういえもん〉", "", "", "http://ecx.images-amazon.com/images/I/21A489Z4BDL._SL210_.jpg", 128, "人気ミステリー作家、京極夏彦の同名小説を、演劇界の第一人者である蜷川幸雄が映画化。古典的名作である『四谷怪談』をベースに、激しく求め合いながらも引き裂かれていく伊右衛門とお岩の運命を、狂気とエロスをダイナミックに絡めて描く。「ラスト・サムライ」の小雪が、お岩役に挑戦して話題を呼んだ。浪人暮らしに身を落とした伊右衛門は、静かに真面目な生活を送っていた。ある時、伊右衛門に民谷家への婿入り話が持ち上がる。ほどなく、夫婦となった伊右衛門とお岩は、お互いを理解し、深い愛情で結ばれていくのだが……。", "2004-02-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("潮騒", "潮騒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "南の小島で芽ばえた、少女と青年の愛を描く三島由紀夫原作の５度目の映画化。青山京子、吉永小百合、小野里みどり、山口百恵に次ぐヒロインには、堀ちえみを起用。時代をあえて原作の発表時（昭和29年）に戻し、当時のヒット曲を流している。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひぐらしのなく頃に　誓", "ひぐらしのなく頃に　誓", "", "", "http://image.eiga.k-img.com/images/movie/54215/315x210.jpg?3723", 0, "ゲーム、アニメ、小説など多様なメディアで展開され熱狂的なファンを持つ「ひぐらしのなく頃に」の実写映画版第2作。両親の離婚の原因が自分にあると思い込み、精神的バランスを崩した少女レナ。父と一緒に雛見沢村へ引越してきた彼女は、東京からやってきた圭一らと仲良くなり、楽しい毎日を過ごし始める。そんなある日、彼女は父の新しい恋人が財産目当てであることを知り……。前作に続き「富江」シリーズの及川中が監督を務める。", "2009-04-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHILDREN OF NATURE", "春にして君を想う", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "アイスランドの荒涼たる風景をバックに、老いることと人生の最後の輝きを見つめる。都会の生活に失望し、至れり尽くせりの老人ホームにも息の詰まる思いを抱いた老農夫が、幼なじみの女性を連れて失踪。二人を乗せた盗難ジープが追うパトカーの目前でこつ然と消えてから、映画は老いた心を静かに映すメルヘンのような終局を迎える。荒れ果てた故郷に、死期が迫った老人の記憶がサイレント・フィルムの形でよみがえる、という演出。ラストに天使の役で「ベルリン・天使の詩」のB・ガンツが印象的に登場する。", "1991-01-01", "77,7,36", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("破事兒", "些細なこと", "", "", "http://image.eiga.k-img.com/images/movie/56375/315x210.jpg?1655", 90, "香港の鬼才パン・ホーチョンの監督第8作「ドリーム・ホーム」（2010）の公開を記念して、ホーチョン監督の日本未公開作を一挙上映する特集上映「パン・ホーチョン、お前は誰だ!?」で公開。ホーチョン監督が21歳の時に執筆した短編小説を自ら映画化。けん怠期を迎えた中年夫婦、見習いの殺し屋、恋する女子高生などに起こる“些細な出来事”をテーマに全7話で構成。ジリアン・チョン、イーソン・チャン、チャップマン・トウらホーチョン監督作でおなじみのキャストが集う。", "2011-06-18", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUO VADIS", "クオ・ヴァディス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 171, "西暦64年、暴君ネロ支配下のローマ。キリスト教の娘リジアを愛した兵士マーカスの恋物語に、キリスト教迫害、ローマ炎上などの歴史的事件が絡む。野蛮、残虐さが強調され過ぎだが、700万ドルの巨費を投じたスペクタクル・シーンが見もののMGMの超大作。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("搶銭夫妻", "いつも心の中に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "“Mr.BOO！”シリーズで一世を風靡したM・ホイが、ガンに冒された男を人間味豊かに演じたハートウォーミング・コメディ。ガンを告知された人気TVキャスターが、落ち込むどころか、逆に負けん気を発揮して仕事に猛進。妻子には病気を隠していたが上司には知られ、やがて彼の闘病生活はドキュメンタリーとしてTVショーで放映されることになってしまう。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Deuce Bigalow: Male Gigolo", "デュース・ビガロウ、激安ジゴロ!?", "", "", "http://ecx.images-amazon.com/images/I/519TT3AV7QL._SL210_.jpg", 89, "アダム・サンドラー製作総指揮によるコメディ。冴えない毎日を送っていた水槽掃除屋のデュース・ビガロウはある日、高級ジゴロの豪邸の留守番を任されるが、高価な水槽を壊してしまう。修理代を稼ぐため、豪邸の主人に成り代わってジゴロの仕事を引きうけたデュースは、変わり者の客たちの相手をするうちに次第に人気ジゴロとなっていく。主演、脚本を担当するのは「サタデー・ナイト・ライブ」出身のコメディアン、ロブ・シュナイダー。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("激動の昭和史・軍閥", "激動の昭和史・軍閥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "東宝の“激動の昭和史”ものの第２作。二・二六事件を契機に、軍部の政治進出に拍車がかかり、着々と総力戦体制が作りあげられていく。やがて東条内閣が成立し、戦線は太平洋へと拡大……。記録フィルムを随所に挿入し、ドラマとの融合を図る。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mar Adentro", "海を飛ぶ夢", "", "", "http://image.eiga.k-img.com/images/movie/1165/315x210.jpg?4070", 125, "「オープン・ユア・アイズ」「アザーズ」を手がけたアメナーバル監督と、「夜になる前に」のハビエル・バルデムのスペイン・コンビによる問題作。25歳の時から事故で30年もの間全身麻痺に陥っている男ラモン・サンペドロが、カトリック社会のスペインで、尊厳死を勝ち取ろうと裁判を起こしたラモンが書いた自伝を映画化。主演のバルデムが5時間にも渡るメイクで老け役に挑戦したことも話題になった。ベネチア映画祭主演男優賞、ゴールデングローブ賞及びアカデミー賞の外国語映画賞を共に受賞。", "2007-04-16", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brick", "BRICK ブリック", "", "", "http://image.eiga.k-img.com/images/movie/33925/315x210.jpg?4815", 110, "カリフォルニアの高校を舞台に、元恋人の謎の死の真相を探る青年の姿をスリリングに描いたミステリー。サンクレメンテ高校に通うブレンダンは、学校の排水溝の近くで元恋人エミリーの遺体を発見する。事件前、エミリーの不可解な行動を不審に思っていた彼は、真相を突き止めようと調査を始めるが……。1930年代アメリカの探偵小説のスタイルを引用した斬新な語り口が注目を浴び、サンダンス映画祭で審査員特別賞を受賞した。", "2007-04-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Cenerentola", "World Classics＠CINEMA チェネレントラ", "", "", "http://image.eiga.k-img.com/images/movie/55105/315x210.jpg?2767", 155, "世界各地のオペラやバレエを映画館の大画面で堪能できる「World Classics＠CINEMA」シリーズの1作として、イギリス・グラインドボーン音楽祭で史上最高の一夜と絶賛された2005年上演のオペラ「チェネレントラ」をデジタルシネマ化。誰もが知る物語「シンデレラ」をベースに、魔法使いは哲学者に、ガラスの靴は腕輪に置きかえ、哀れな少女が王子に見初められて妃になるまでの顛末をコミカルに描く。", "2010-03-06", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛のむきだし", "愛のむきだし", "", "", "http://image.eiga.k-img.com/images/movie/53645/315x210.jpg?2190", 237, "「自転車吐息」「紀子の食卓」の鬼才・園子温が237分の長尺で描く奇想天外なラブストーリー。主演は映画初出演で主演を飾るAAA（トリプル・エー）のボーカル西島隆弘、ヒロインを元Folderの満島ゆかりが演じる。共演に安藤サクラ、渡部篤郎ほか。敬虔なクリスチャン一家で育ったユウは、神父の父に毎日懺悔を強要される日々を送っている。“罪作り”のため女性の股間ばかり狙う盗撮を繰り返すユウは、ある日、ヨーコという少女に出会い一目で恋に落ちるが……。", "2009-01-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Underworld: Awakening", "アンダーワールド　覚醒", "", "", "http://image.eiga.k-img.com/images/movie/55275/315x210.jpg?4362", 88, "ケイト・ベッキンセールの主演で、バンパイア（吸血鬼）族とライカン（狼男）族の種族の存亡をかけた戦いを描いた人気アクションシリーズの第4作。バンパイア族の処刑人セリーンは、ライカン族のみならず同族のバンパイアからも追われた末に人間に捕らえられ、12年もの間眠りについていた。戦いはセリーンが眠っている間も続き、バンパイアとライカンの力を欲する人間も加わった三つ巴の様相を呈していた。眠りから覚めたセリーンは、自らの復讐とひとりの少女を救うため、混迷した戦いの場へと身を投じる。監督はスウェーデン出身の新鋭モリス・モーリンド。", "2012-02-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("About Elly", "彼女が消えた浜辺", "", "", "http://image.eiga.k-img.com/images/movie/55545/315x210.jpg?1815", 116, "第59回ベルリン国際映画祭銀熊賞を受賞したイランのミステリー。セピデーは離婚したばかりの男友だちに紹介しようと、幼稚園の教師エリを誘い、カスピ海沿岸へバカンスに出かける。美しい浜辺にある古い別荘にたどり着き、穏やかで楽しいひとときを過ごす。しかし翌朝、エリはこつ然と姿を消し、浜辺では子どもがおぼれていた。捜索が始まり、セピデーはエリのことを名前以外、何も知らなかったことに気づく。", "2010-09-11", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA TETA Y LA LUNA", "おっぱいとお月さま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「ハモンハモン」で世界的なヒットを飛ばしたB・ルナ監督が描く、おっぱいに恋した少年の物語。祭りで行われるカステリェール（人間タワー）の頂点に登る役目のテテ少年は、生まれたばかりの弟にママのおっぱいを奪われて、ふてくされている。そんなテテが、見世物小屋のフランス女、エストリッタのおっぱいを見て以来彼女に恋をしてしまう。おまけに兄貴分のミゲールまでがエストリッタに恋する始末。やがてカステリェールが近づいて来た。テテは前の晩にカステリェールでの成功を月に願うのだった。", "1993-01-01", "9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOR THOSE WHO THINK YOUNG", "踊れ！サーフィン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1964年製作当時の最新水着ファッションを見せながら、アメリカの豊かな学生生活を陽気に楽しく描いた青春映画。J・ダーレン、P・ティフィンなど当時売り出し中の若手スターが総出演している。なお、原題は当時のペプシ・コーラのキャッチフレーズである。", "1964-01-01", "1", 0, NOW(), NOW());
