INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("After the Sunset", "ダイヤモンド・イン・パラダイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ピアース・ブロスナンが大泥棒を小粋に演じるクライム・アクション。伝説の秘宝“ナポレオン・ダイヤモンド”の3つのうち二つを盗み出した大泥棒マックスは、引退して恋人と共にバハマの島で優雅な生活を送っていた。ところが、停泊中の船に最後のひとつが展示されるとの噂が！　マックスを追うFBI捜査官や、ダイヤをねらうギャングも現れ、平穏な生活は一変。恋人に反対されるものの、マックスの中で昔の血を騒ぎ出し……。", "2006-02-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヤカオランの春／　あるアフガン家族の肖像", "ヤカオランの春／　あるアフガン家族の肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "アフガニスタンのバーミヤン州ヤカオラン出身の家族の半生を、同国の現代史と共に見つめたドキュメンタリー。祖国を担う子供たちの教育に人生を捧げようとした一人の教師とその家族。彼らが難民とならざるを得なかった苦難の道のりを通じ、尊ぶべき平和や人権の意味を問う。内戦の激戦地として大打撃を受けたヤカオラン。アリ・アクバル、タジワール夫妻は、生き延びるために故郷を逃れ、難民となる。", "2005-04-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mies vailla menneisyytta", "過去のない男", "", "", "http://image.eiga.k-img.com/images/movie/1465/315x210.jpg?3097", 97, "夜行列車でヘルシンキに着いた男が、暴漢に襲われて重傷を負い、極貧の一家に拾われて命は取り留めるが、記憶喪失に。日雇い労働をして暮らすようになった彼は、救世軍で働く女性イルマと出会い、心を通わせていく。カウリスマキ監督作の常連ヒロイン女優オウティネンが、本作で02年カンヌ映画祭主演女優賞を受賞。フィンランドのムード歌謡＝イスケルマの名曲の数々とともに、監督がファンだと公言するクレイジーケンバンドの「ハワイの夜」が挿入歌として登場。", "2003-03-15", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Massa'ot James Be'eretz Hakodesh", "ジェイムズ聖地（エルサレム）へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "聖地エルサレムへ巡礼の旅に出かけた純朴なアフリカ人青年の受難を描いた、イスラエル製シニカル・コメディ。アフリカの小さな村で次期牧師に選ばれた青年ジェイムズは、村人たちの期待を背負って聖地エルサレムへ巡礼の旅に出る。勘違いから不法労働者として働かされるハメになったジェイムズは、イスラエルの資本主義社会の中でずる賢さを身につけていくが……。プロデュースを手がけるのは、「パラダイス・ナウ」のアミール・ハレル。", "2007-06-23", "46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DR.DOLITTLE", "ドクター・ドリトル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ヒュー・ロフティングの名作『ドリトル先生物語』を現代風にアレンジし、映画化したほのぼのコメディ。車で轢きそうになった犬が“気をつけろ！ ”と怒鳴ったのを聞いて以来、動物と会話ができるようになった病院の医師ジョン・ドリトル。彼が動物たちと繰り広げる騒動、そして彼と妻や子供たちの交流を描く。動物の気持ちを理解する心優しき人間像をE・マーフィがヒューマニズム豊かに演じ、大ヒットを飛ばした。アルコール依存症のサル、自殺願望のトラなど 100を越える動物たちの名演が笑いを呼ぶ。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EN LEKTION I KA¨RLEK", "愛のレッスン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「第七の封印」を発表する以前、1950年代、喜劇時代のベルイマン作品の中の１本。結婚16年目の婦人科医夫婦の浮気騒動をユーモラスに、人間への愛情豊かに描く。ウディ・アレンがこの頃のベルイマン作品を自作のお手本にしていることはよく知られている。", "1954-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Cochon de Gaza", "ガザを飛ぶブタ", "", "", "http://image.eiga.k-img.com/images/movie/57345/315x210.jpg?1549", 99, "パレスチナの貧しい漁師がブタを釣り上げたことから起こる騒動を描いたハートフルコメディ。パレスチナ人の漁師ジャファールは、ある日、漁の網にブタがからまっているのを発見。イスラム教では不浄のものとされているブタを、当局に見つかる前になんとか手放そうと考えるが、なかなか買い手がつかず……。主演は「迷子の警察音楽隊」のサッソン・ガーベイ。2011年・第24回東京国際映画祭で観客賞を受賞した。", "2010-01-01", "2,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOUNG MAN WITH A HORN", "情熱の狂想曲（ラプソディ）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "1920年代に活躍した名コルネット奏者、ビックス・バイダーベックの半生を描いたジャズ映画の佳作。名ピアニスト、H・カーマイケルやD・デイなどの出演も魅力。ビックスのナンバーや実在のジャズメンとの交流なども見どころだ。ちなみに、ビックスの半生を描いた音楽映画として、プピ・アバティ監督の「ジャズ・ミー・ブルース」もある。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場リミックス版　アップルシード XIII 預言", "劇場リミックス版　アップルシード XIII 預言", "", "", "http://image.eiga.k-img.com/images/movie/56215/315x210.jpg?2755", 0, "「APPLESEED」（2004）、「エクスマキナ」（07）と2度にわたって映画化もされた士郎正宗の人気コミックを、新たに全13話でアニメーション化。そのストーリー後半をリミックスして製作した劇場版。巨大人工都市オリュンポスの治安を守るES.W.A.T.隊のデュナン・ナッツとブリアレオスの遭遇した事件を先導していたのは、かつての友人ディアだった。技術国家ポセイドンからの独立を宣言し、テロを敢行しようとしていたディアの目論見はすんでのところで阻止され、オリュンポスは平穏を取り戻したかのように見えた。しかし、デュナンとブリアレオスの前に再びディアが現れ……。", "2011-10-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阪妻 阪東妻三郎の生涯", "阪妻 阪東妻三郎の生涯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "名場面集と証言で綴る、不世出のチャンバラ・スター、阪東妻三郎の評伝ドキュメンタリー。マツダ映画社所蔵の珍しい阪妻の無声映画や、実子・田村高廣が語る父の思い出。さらには少年期の田村正和も映し出された阪妻一家のプライベート８ミリ・フィルムも登場。ファンには必見の１本である。映画評論家、佐藤忠男がインタビュアーを務めている。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿呆船 さかしまの巡礼", "阿呆船 さかしまの巡礼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "1983年に大阪で行われた“劇団維新派”の公演「月光のシャドウホール」の模様を、自由な解釈で再構築した異色ドキュメンタリー作品。フランスの著名な思想家、ミッシェル・フーコーの『狂気の歴史』の一節から“狂人の船”に着想を得、独自の映像世界が展開される。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARNIE", "マーニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "盗癖のある妻と、彼女を救おうとする夫の心理的葛藤を描いた、ヒッチコック一流のサイコ・サスペンス。求人広告を見てやって来た若い女は逃亡中の女泥棒だった。マークはそれを知りつつ彼女を雇い、やがて二人は結婚するが……。ヘドレンの陰影のある美しさ、S・コネリーのハンサムぶりも魅力的。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血は渇いてる", "血は渇いてる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "“松竹ヌーヴェル・ヴァーグ”の旗手・吉田喜重の監督第２作。会社のクビ切りに抗議し、自殺未遂をした男が保険会社に利用され、マスコミにほんろうされて本当に自殺する。コマーシャリズム批判の形を取りながら、モラルのあり方の理想主義を反転させて描いている。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初笑い寛永御前試合", "初笑い寛永御前試合", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "士気高揚のため御前試合を開くことにした将軍家光。おかげでさびれた一心斎の道場も大繁盛、さっそく出場資格の皆伝書を持ち、一心斎の娘にも慕われている正直者の市兵衛が選ばれる。しかし、これを快く思わない同門の玄達が邪魔に入って……。花菱アチャコ以下、有名喜劇人が一堂に会した、時代喜劇。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAUGHTY　MARIETTA", "浮かれ姫君", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "８本の映画で共演し、様々な名作を世に送り出したJ・マクドナルドとN・エディの初コンビ作で、V・ハーバート作曲のオペレッタの映画化。舞台はルイ王朝華やかなりしパリ。叔父の勧める公爵との結婚をいやがり、女中になりすまして船で家出してしまった侯爵令嬢マリー姫。やがてその船は海賊に襲われるが、その危機をウォリントン大尉の率いる傭兵に救われたマリー姫は、大尉と恋に落ちていくが……。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLAVES OF NEW YORK", "ニューヨークの奴隷たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "目まぐるしく流行が変わるヤング・カルチャーのメッカ、マンハッタンに生きる若き芸術家たちの苦悩と現実を描いていく。帽子デザイナーのエレノアは、マンハッタンでの様々な体験の末、自分が都市に埋もれつつあることに気付いていく。原作はT・ジャノウィッツの全米ベストセラー小説。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小生怕怕／TILL DEATH DO WE SCARE", "魔界天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「霊幻道士」同様、奇抜な発想で笑いの渦に巻き込む香港製のギャグ・ホラー映画。結ばれた相手は必ず死ぬという、不幸な星のもとに生まれた美女、アイリーン。たて続けに３人の夫に先立たれ悲しむ彼女を、死んだ３人が幸せにしようと悪戦苦闘!?", "1982-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONSTANCE", "コンスタンス 魅せられて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1948年のオークランドで教鞭を取っていたコンスタンスは、大の映画狂が高じて女優への夢を捨て切れず、ついに教師を退任する。金持ちの息子や誠実なリチャードとの恋の遍歴を経て、ハリウッドのカメラマンと知り合う機会にめぐまれる。しかしそれが悲劇のはじまりだった。各国で高い評価を得た感動作。", "1984-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("×ゲーム", "×ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/55625/315x210.jpg?2326", 0, "山田悠介のサスペンス小説をオリジナル脚本で映画化。赤いブリキ箱にいじめの内容を書いたクジを入れ、引いた内容を実行する“×ゲーム”が行われていた小学校の教師が12年後、突然の自殺を遂げた。他殺を疑う当時の生徒・英明は、事件の真相を探るうち、12年前とは比べものにならないほど残忍な“×ゲーム”に巻き込まれていく。主演はD-BOYSの荒木宏文。「お姉チャンバラ」の福田陽平がメガホンを取る。", "2010-09-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRISONER OF LOVE", "プリズナー・オブ・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "スーパー・モデル、ナオミ・キャンベルの初主演作。ニューヨークのマフィアの一員であるジョニーは、仲間が犯した殺人の目撃者トレイシーを始末するように命じられる。だが、彼女に思いを寄せていたジョニーは組織を裏切り、彼女を監禁、理想の女に“飼育”しようと考えるのだが……。監禁された女と、彼女の魅力の虜になっていく男。二人の駆け引き、愛と欲望をスリリングに描いた官能サスペンス。", "1999-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPIKLENCI　SLASTI／CONSPIRATORS　OF　PLEASURE", "悦楽共犯者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "チェコのアート・アニメの大御所J・シュヴァンクマイエルが、「ファウスト」に続いて撮り上げたライブ・アクション主体の長編映画。現代のプラハで生活を営む雑誌店の主人や客、TVキャスター、郵便配達婦、刑事らが微妙に絡み合う群像劇なのだが、悪趣味で名高い鬼才が“人間の快楽”をテーマに取り組んだ作品だけに、その奇想天外な内容は凡人の想像の域を超えるほどアナーキーだ。怪鳥のハリボテを製作して悦びに震える男、鯉がうごめくバケツに足を突っ込んでエクスタシーを感じる美女など、人間が己の欲望を満たすための多彩なテクニックを、手品師さながらに披露。このブラックなセックス・ファンタジーを笑い飛ばすか、顔をしかめるかは見る人の感性次第といえそうだ。", "1996-01-01", "43,3,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Anchorman: The Legend of Ron Burgundy", "俺たちニュースキャスター", "", "", "http://ecx.images-amazon.com/images/I/51MBbuvgK8L._SL210_.jpg", 94, "1970年代のテレビ業界を舞台に、おバカなニュースキャスターたちが巻きおこす騒動を描いた、ウィル・フェレル主演によるコメディ。サンディエゴのローカル局に務めるニュースキャスターのロンは、人気があるのをいいことにやりたい放題の日々を送っていた。ところがある日、野心家の女性ベロニカが入社してきたことから、他局をも巻きこんだバトルが勃発する。ジャック・ブラック、ベン・スティラー、ティム・ロビンスら豪華俳優陣のカメオ出演にも注目。", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Forever the Moment", "私たちの生涯最高の瞬間", "", "", "http://image.eiga.k-img.com/images/movie/53825/315x210.jpg?3669", 0, "2004年のアテネオリンピックで銀メダルを獲得した韓国女子ハンドボールチームの実話を映画化し、韓国で大ヒットを記録したスポーツドラマ。オリンピック2連覇の立役者として活躍したハンドボール選手ミスクは、現在は所属チームの解散によってハンドボールをあきらめ、大型スーパーでアルバイトする日々を送っていた。そんなある日、韓国代表チームの監督代行に就任したヘギョンは、チームの戦力を補うためにミスクらベテラン選手たちを呼び集める。", "2008-08-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Diva", "ディーバ", "", "", "http://ecx.images-amazon.com/images/I/51Xn3ayrlJL._SL210_.jpg", 118, "「ベティ・ブルー」のジャン＝ジャック・ベネックス監督による長編第1作。歌の女神を意味するディーバと呼ばれる黒人歌手、シンシアに憧れる郵便配達人の青年ジュール。決してレコードを出さない彼女のコンサートを盗み録りしていた彼は、複雑に絡み合った犯罪に巻き込まれていく。本国フランスで、3年にわたるロングランを記録した大ヒット作品。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天下第一拳／THE KING BOXER", "キング・ボクサー　大逆転", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「燃えよドラゴン」などの香港製格闘映画ブームの中で登場してきたバトル・ムービーの一作。空手選手権大会での決闘をクライマックスに据えた、爽快なアクション作品だ。大会を控えて師匠の命令を受け、都の道場で修業を積むことになったジ・ハオだが、ライバル道場が差し向けた日本の剣士に両手を切られてしまう。しかし、女旅芸人に助けられ再び猛特訓を開始する。", "1972-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／LA　BELLE", "寵愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ヌードモデルをしている女と暮らす男。しかし、女は携帯電話が鳴るとすぐにどこかへ消えてしまい、やがてまたフラリと現れる。他に愛する男がいることを知りながら、それでも彼女を愛そうとする彼だったが……。男女の性愛を、浮遊感と透明感あふれるロマンティックな映像で見せるラブ・ストーリー。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA RIVIE｀RE DU HIBOU", "ふくろうの河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 28, "南北戦争のさなか、北軍の手によって鉄橋の上で絞首刑にされようとしている男がいる。男は助かりたいと天に祈る。処刑の命令が下りた瞬間、男をつないでいた綱が切れ男は眼下の急流に落ちた。かろうじて助かった男は妻子のもとに帰るが……。R・アンリコが綴る美しい映像詩。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Knightriders", "ナイトライダーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "　ビリーとその仲間たちの一座は中世の鎧に身を包み、オートバイのショーを披露しながら町から町へ旅をしている。アーサー王さながらのリーダー、ビリーは独自の理想を抱えていたが、警察とのもめごと、ショービジネス界への誘惑、金銭的問題などが起こり、やがて一座の間にはヒビが……。ホラー映画の巨匠ロメロ監督が手がけた非ホラーの異色ドラマ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("曉の門出", "曉の門出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "戦場より帰還した傷病兵・川村新太郎は、意中の恋人が親友の妻になっていることを知り、故郷を捨てて東京に出る。彼はそこで失明した上官や戦争未亡人が強く生きていく姿に接し、再び故郷の農村に戻って親友と和解する。軍事保護院後援の時局映画。田を守る農民の姿に戦場を重ね、国民精神を鼓舞しようと計った。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("London Boulevard", "ロンドン・ブルバード LAST BODYGUARD", "", "", "http://image.eiga.k-img.com/images/movie/57235/315x210.jpg?1287", 104, "「ディパーテッド」の脚本家ウィリアム・モナハンが初メガホンをとったノワールサスペンス。ハリウッド映画の古典「サンセット大通り」（1950）を下敷きにしたアイルランド人作家ケン・ブルーエンの「ロンドン・ブールヴァード」（新潮社刊）を映画化した。3年間の刑期を終え出所したミッチェルは、裏社会から足を洗うことを決意。ふとしたきっかけで元女優シャーロットの身辺警護の仕事を引き受ける。しかし、ミッチェルの度胸と腕前を気に入ったギャングのボスが、ミッチェルを配下に置こうと執ようにつきまとい、そのことからミッチェルに心を許したシャーロットもまた危険に巻き込まれていく。主演はコリン・ファレルとキーラ・ナイトレイ。", "2011-12-17", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ATTACK OF THE KILLER TOMATOES", "アタック・オブ・ザ・キラートマト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "トマトが人間を襲う前代未聞のSFパニック。「ジョーズ」をはじめ、映画のパロディーが連射され、出演者や原作のクレジットまでも信用できない冗談映画だ。ある日、一人の主婦が台所でトマトに襲われ、トマトジュースを飲んだ男はもがき死ぬ。トマトの反乱が始まったのだ。直径３メートルの巨大トマトが転がり、水中でもトマトが人を襲う。警察も軍も歯が立たず、フジノ博士発明のロボットも欠陥品だった。そして期待の特殊部隊は、本当に特殊な者ばかりで役に立たない。しかし、最後はトマトの弱点を知り、人間は危機を逃れるのだが……。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　BARBER　OF　SIBERIA", "シベリアの理髪師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 162, "ロシアの名匠N・ミハルコフが製作費4500万ドルを投じて、運命の恋を壮大なスケールで描く。1885年、帝政ロシア。モスクワへ向かう列車の中で出会った米国人女性ジェーンとロシア人の士官候補生アンドレイ。シベリアの森林開墾に従事している発明家のために、彼女は資金調達をしようと士官学校のラドロフ将軍を訪ねる。そこで再会したジェーンとアンドレイは激しい恋に落ちるが、運命のいたずらが二人を悲劇へと誘う。シベリアからプラハ、ポルトガルへと一大ロケーションを敢行し、雄大な景色の中に20年にわたる愛の軌跡を描き出す。ときにコミカルで軽やかに、ときに情熱的で切なく、ドラマティックな物語をつむぎ上げたミハルコフは、ロシア皇帝アレクサンドル三世として出演もしている。", "1999-01-01", "2,24,4,43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("麻雀放浪記", "麻雀放浪記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ボスと手下と敵、この三者の人間関係しかないギャンブラーの世界。そこに友情と愛を求めて生きていく少年・哲の姿を描いて大ベストセラーとなった阿佐田哲也の同名小説の第１部を、イラストレーター・和田誠が監督第１作として映画化。「Wの悲劇」などの名匠・澤井信一郎が共同脚本を担当し、全編をモノクロで撮影した意欲作で、敗戦直後の臨場感を見事に描き出した。ドサ健、出目徳など原作のキャラクターに鹿賀丈史、高品格らがそれぞれの個性をぶつけて迫る。主演の真田広之は麻雀は素人だったが、訓練をつんで高度な“つみこみ技”を修得、画面で見せてくれる。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪華葬刺し", "雪華葬刺し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "“女の肌はセックスのさなかに最も美しく輝く”とする異端の刺青師・彫経によって、性交中のほてった肌に針を入れられる、図書館に勤める女。1980年代風に表現をエスカレートさせた“刺青”もの。パリで先行上映されて話題を呼び、各国から引きあいがあった。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1＋1＝11（イチプラスイチハイチイチ）", "1＋1＝11（イチプラスイチハイチイチ）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-06-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鏡山競艶録", "鏡山競艶録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "伊達騒動と並ぶお家狂言、加賀騒動に草履打復讐譚を加えた歌舞伎ネタ。監督は、「影法師」「雄呂血」など、一連の阪東妻三郎主演作で知られた名脚本家、寿々喜多呂九平で、1930年から監督に転じ、34本の作品を残した。本編は、1933年の同監督作のリメイクで、キャスト全員が女性という、女優顔見世映画である。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("素浪人百万石", "素浪人百万石", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "浪人の山川忠介は愚連隊、弁天一家に襲われていた娘、美乃を救う。彼は美乃を送り届けるが彼女の父、土橋久兵衛は何者かに惨殺されたあとだった。忠介は美乃の周りに起こる事件の謎を追ううちに、彼女の出生の秘密を知る……。市川右太衛門が正義の浪人に扮しての痛快娯楽時代劇。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("腰抜け二刀流", "腰抜け二刀流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "新宿の盛り場“紅葉屋”のお六は、器量・勇気・唄の才能と三拍子そろった人気者。ふとしたことから旅に出た彼女は、途中で理由も分からず小さな子供を預かるはめに……。「グロリア」ばりのストーリー展開に、ロード・ムービーとミュージカル・コメディの要素を加味した明るい映画で、「腰抜け二挺拳銃」を完全に自家薬籠中のものとした並木鏡太郎監督の快作。原案・稲垣浩、脚本は山中貞雄一派の三村伸太郎。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春かけおち篇", "青春かけおち篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "結婚を反対されたわけでもなく、障害があったわけでもないのに“かけおち”という古典的な行為で愛を確かめようとする恋人たち。大竹しのぶがヒステリックなわがまま娘を、風間杜夫が優柔不断な男を、それぞれの持ち味を出しきって演じた。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE YEARLING", "子鹿物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "フロリダ北部の森林地帯で農作を営むバクスター一家。幼い息子のジョディは、ある日、森の中で子鹿を見つけ、フラッグと名付けてかわいがる。だが、やがてフラッグとの悲しい別れがやって来る。ピュリッツァー賞を受賞した小説の映画化。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUGS〈バグ〉", "BUGS〈バグ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "地質異常の影響で巨大化したフナムシが人間を襲うパニック・ホラー。リゾート開発を進める離島でマリン・フェスティバルが開催されることになる。しかし、活気づく島では、変死事件が続出。取材に来ていたアナウンサー・由紀とクルーの滝田は、大地震発生を予告した地震学者・田所から異常な自然現象の発生について聞かされ事件との関連を調査するが、島の人々は真相究明に消極的だった。そんな中、巨大化したフナムシの大群が現れる……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Raspberry Reich", "ラズベリー・ライヒ", "", "", "http://image.eiga.k-img.com/images/movie/34235/315x210.jpg?1599", 90, "「スーパー8 1/2」「スキン・フリック」など数々の問題作を生み出してきたブルース・ラ・ブルース監督が“テロリズムと性の革命”というテーマに挑み、サンダンス映画祭をはじめ世界各地の映画祭で大反響を呼んだ衝撃作。ドイツ赤軍に憧れる女性グルドンは、政治集団“ラズベリー・ライヒ”のリーダーとして、性の革命を起こすべく活動に励んでいた。その頃、組織のメンバーたちが資産家シュミットの息子を誘拐し……。", "2007-10-06", "7,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAVE THE LAST DANCE", "セイブ・ザ・ラストダンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "全米でスマッシュ・ヒットとなったさわやかな青春ダンス・ムービー。バレエが何より大好きな少女サラ。だが名門ジュリアード音楽院のオーディションの最中に、最愛の母を交通事故で亡くしたことでバレエを諦めてしまう。幼い頃に別れた父親と住み始め、シカゴの高校でヒップホップに出会ったサラは、次第にその魅力にはまっていく。バレエとヒップホップをミックスしたダンス、そしてクライマックスのダンス・シーンは圧巻。「モナリザ・スマイル」のJ・スタイルズの熱演も見逃せない。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAFETY LAST", "ロイドの要心無用", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "デパート店員のロイドは、ビルの壁面を登ることにかけては天才的な友人（名前もずばり“ビル”！）を、店の宣伝に利用する妙案を思いつく。しかし当日、友人が逃げたために、なんと彼がビル登りをするハメに……。大時計の針にぶら下がって大弱りのモダンボーイ＝ロイド。チャップリン、キートンと並ぶ無声映画の３大喜劇王の一人、H・ロイドのイメージを決定づけた最高傑作がこれだ。高所恐怖症の観客を心の底から怖がらせ、ハラの底から笑わせるスリルとギャグの錬金術。1920年の「化物退治」の時に、右手親指と人差し指を失っているにもかかわらずビルを登るロイドの、凄絶なプロ根性には驚かされる。", "1923-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BYE BYE BLUES", "ブルースに抱かれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "軍医の夫や一人息子と楽しい日々を過ごしていたデイジー。だが第二次大戦が始まり夫は戦地へ、デイジーと息子は故郷のカナダへ。やがて夫は戦地で行方不明になり、自活するためデイジーはバンド歌手として舞台に立つ。そこで彼女は一人の青年と出会い……。戦争が生んだ男と女の恋の悲劇を、カナダの美しい自然をバックに描くラブ・ストーリー。", "1989-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAGTIME", "ラグタイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 156, "20世紀初頭のアメリカ。東欧から移民が大量に流れ込み、混沌とした状況にあった時代のニューヨークに生きた人々、マジソン・スクエア・ガーデンを設計した建築家スタンリー・ホワイト、個人的な恨みからホワイトを射殺する富豪の御曹子ハリー・K・リー、モデルとしても注目を集めるリーの妻イブリン・ネスビット、黒人への人種偏見に傷つけられテロ活動に走る黒人ピアニスト、コールハウス・ウォーカーJr.。こういった実在の人物を登場させつつ、虚実とりまぜて描いたアメリカの一大絵巻。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秋津温泉", "秋津温泉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "藤原審爾の小説を、岡田茉莉子が企画し、吉田喜重が脚色・監督した作品で、「ろくでなし」での鮮烈デビュー後やや低迷していた吉田の代表作となった。敗戦の直前、岡山県の山奥の温泉“秋津荘”に結核の治療でやって来た青年が、宿の娘の看病で一命をとりとめる。敗戦の知らせを聞いて、涙を流す純情で無邪気な娘に、暗い時代に絶望していた青年は、感動を覚え精神的にも救われた思いをする。その後も、彼は幾年かおきに秋津を訪れるが、年を追うごとに、堕落した俗悪な中年男に変貌していく。女も旅館の経営に行きづまり、時の流れを受け入れられずついには心中を図るが……。戦中戦後をくぐり抜けてきた男と女の情念を、美しい日本の四季を背景に描いた本作は、これまでの、松竹メロドラマの偶然のすれ違いによって結ばれないという定型を、思想的精神的なすれ違いに置き換えるという吉田の意図によって、戦後日本人の歩みをも表現。吉田と岡田はのちに結婚して、二人で数多くの作品を作ることになる。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOMEONE SPECIAL", "小さな恋のステップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "韓流純愛映画とは一線を画すシュールでポップなラブ・ストーリー。冴えないプロ野球2軍選手トン・チソンと、風変わりだけど一途に彼を思い続ける女性ハン・イヨン。ふたりの心が通い合う様を独特の演出で綴っている。「英語完全征服」で注目を集めたイ・ナヨンが最高にキュートだ。サエない２軍外野手のチソンは余命３ヵ月と宣告され、思い切り泥酔。気づくとホテルで、顔なじみの女バーテンダーと一緒にいた。実は彼女は、チソンの近所に住んでおり、10年間ずっと彼に想いを寄せていたのだった……。", "2006-02-04", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("罠 THE TRAP", "罠 THE TRAP", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "林海象監督、永瀬正敏主演の“私立探偵濱マイク”シリーズ第３作。横浜・黄金町で連続して美女が殺される。被害者は皆、花柄のワンピースを着せられていた。濱マイクの恋人・百合子も襲われ、危ういところをマイクが助ける。しかし、証拠の品にマイクの指紋が付いていたことから、警察はマイクが犯人ではないかと疑う。マイクは探偵仲間の協力で真犯人を探し始めるのだが……。今回は異常な猟奇殺人をテーマとしたサイコ・スリラーとなっている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr. Deeds Goes to Town", "オペラハット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "遺産相続を巡る騒動に巻き込まれる善良な男の姿を、コメディタッチで描いた社会派ドラマ。田舎町で幸せな毎日を送っていたディーズの元に、大富豪である叔父の遺産が転がり込む。しかし、ニューヨークへやって来た彼を待ち受けていたのは金目当ての汚い連中ばかり。彼は正体を隠して接近してきた女性記者ベイブと恋に落ちるが……。監督のフランク・キャプラは本作で、「或る夜の出来事」に続き2度目のアカデミー監督賞を受賞。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COMEDIANS", "危険な旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 160, "独裁政権下のハイチに４人の外国人が船で上陸する。だが、彼らの行動はことごとく秘密警察に監視される。外国人の一人で実業家のブラウンは、上陸早々人妻との情事を楽しんだりしていたが、反政府急進グループのリーダーから軍事顧問になることを要請され、やがて勝ち目のない戦いに挑んでいく。当時、夫婦だったE・テイラーとR・バートンが共演したが、やや大味な戦時下のメロドラマに終わった。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROCODILE DUNDEE IN LOS ANGELS", "クロコダイル・ダンディー in L.A.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "世界で一番のTV放送回数を誇る、人気コメディ・シリーズ14年ぶりの第３作。超マイペース男、ミック・ダンディーがロサンゼルスを舞台に繰り広げる騒動を描く。妻スーの仕事でロスへ向かうことになったミックは、彼女が仕事の間、息子のマイキーと観光へ出かける。しかし、彼の行く先々で珍騒動が持ち上がってしまう。", "2001-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画「紙兎ロペ」　つか、夏休みラスイチってマジっすか!?", "映画「紙兎ロペ」　つか、夏休みラスイチってマジっすか!?", "", "", "http://image.eiga.k-img.com/images/movie/57075/315x210.jpg?4524", 85, "全国のTOHOシネマズで幕間に上映されている脱力系ショートアニメを長編化した劇場版。紙兎のロペと紙リスのアキラ先輩が、夏休み最後の日（ラスイチ）に巻き起こす騒動をシュールに描き、アキラ先輩の姉役で「AKB48」の篠田麻里子が声優を務める。夏休み最後の日まで自由研究に手をつけずにいたアキラ先輩とロペは、アキラ先輩の姉のピアスを壊してしまう。ピアスの代わりにしようと美しいイヤリングを拾うが、それが美術館から盗まれた「クレオパトラの涙」だったことから、2人は強盗団に追われることになってしまう。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRACULA　II：ASCENSION", "ドラキュリアII／鮮血の狩人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「エルム街の悪夢」「スクリーム」のW・クレイヴンが製作総指揮を務めたヴァンパイア・アクション「ドラキュリア」の続編。前作で滅びたはずのドラキュリアが、血に触れたことにより復活。ヴァンパイア抹殺に燃える神父との壮絶な死闘を描く。J・S・リーが神父役を熱演している。医学部生エリザベスの指先の切り傷から表出した血液に反応して、再び活性化を始めたドラキュリアの焼死体。この謎の現象を実験で解明しようとするエリザベスたちだったが、ドラキュリアは、たちまちその力を回復し始め……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SECRET CEREMONY", "秘密の儀式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "最愛の娘を失って以来そのショックから立ち直れぬ日々を送っている中年娼婦レオノーラ。ある日彼女はチェンチという不思議な少女と出会う。チェンチはレオノーラを“マミー”と呼び、彼女と自分の邸宅で暮らすことまでするのだが……。J・ロージーならではの複雑な心理ドラマ。", "1968-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VELVET DREAMS", "女狐の欲望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "男性誌『プレイボーイ』のプレイメイトだったブロンド美女、C・シャワー主演のラブ・サスペンス。恋愛小説を手掛ける人気女流作家ローラは、そのエロスに満ちた自作の中で自分の欲望をも満たしていた。最低の男とわかっていても、別れた恋人を忘れることができずに、肉欲の世界に溺れていく……。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマン", "ウルトラマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "昭和47年からテレビで放映された“ウルトラマン”シリーズの中から、実相寺昭雄が監督した５本をリストアップ。それを再編集して、オムニバス風につないだ作品。亡霊怪獣シーボーズや、二次元怪獣ガバドンなど、怪獣側の立ち場に立ち、彼らの感情を描いていくという、実相寺独特のタッチが見られる。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISSION GALACTICA: THE CYLON ATTACK", "宇宙空母ギャラクティカ サイロン・アタック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "機械人間サイロンズの猛攻撃でギャラクティカが絶体絶命の危機にさらされるところから始まる「宇宙空母ギャラクティカ」の続編。ギャラクティカに生き残った隊員は地球への脱出を図るが……。空母ペガサスの登場に加え、前作をしのぐSFXシーンがふんだんに登場。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いれずみ無残", "いれずみ無残", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "白い肌に絢爛たる“天女の羽衣”を彫り込んだ女・小夜を主人公に、それに魅せられてパトロンになった会社社長の殿村、小夜と同じ彫り師の手による刺青を持った男・新之助らが虚々実々の駆け引きを繰り広げる異色ロマン。後半のどんでん返しが見もの。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日露戦争勝利の秘史・　敵中横断三百里", "日露戦争勝利の秘史・　敵中横断三百里", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "戦前『少年倶楽部』に連載された山中峯太郎の実録小説の映画化。明治38年、第２軍騎兵団第９連隊の建川中尉以下５名が、敵の陣地を探るために雪の満州平野の中を進んでいた。彼らは寒さと闘いながら敵の馬賊と出会い、敵の軍隊を特定、命賭けで本部への帰途を急ぐが……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誇り高き挑戦", "誇り高き挑戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "鉄鋼業界紙の記者・黒木は、ある大企業が東南アジア某国の反動政府に武器輸出を行っていることを嗅ぎつける。さらに戦後GHQの諜報部員として黒木にリンチを加えた男・高山が大きく関与していることも知る。大新聞も警視庁も沈黙を守るなか、高山を追い詰めることに成功する黒木だが、高山は危険を感じた背後の組織に消されてしまう……。テンポの良いサスペンス・ドラマに兵器産業の復活やCIAの暗躍などの社会問題を折り込んだ、深作監督の出世作。戦後民主主義への幻想と失望が色濃く出ていて、鶴田浩二が終始サングラスをかけたまま、ニヒルな絶望に沈んでいるのが強い印象を残す。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BADUK", "バダック・砂漠の少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「運動靴と赤い金魚」で注目を浴びた、イラン人監督M・マジディのデビュー作。悪徳密輸商人アブドラーの元で働く少年ジャファル。ある夜、アブドラーの家から逃亡したジャファルは、パキスタンの奴隷商人に売り飛ばされた妹を探しに国境を越える。だが、アブドラーの追っ手が迫り……。親を亡くした過酷な状況の中、たくましく前向きに生き抜く子供たちの姿をリアルに捉えた感動作。ちなみに“バダック”とは国境沿いで物品を運ぶ運び手の意味。", "1992-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EUROPA", "ヨーロッパ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "第二次世界大戦後のドイツで、父の祖国復興のため寝台車両の見習い車掌として汽車に乗り込むことになったドイツ系アメリカ人がたどる戦争の深い傷痕。全編黄金色に輝く異色作「エレメント・オブ・クライム」や、神の視点を取り入れた「奇跡の海」などで熱狂的なファンを獲得しているデンマークの俊才、フォン・トリアーの作品。まるで迷宮に入り込んだかのような特異な映像美が見もの。", "1991-01-01", "18,2,7,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CREATURES FROM THE ABYSS", "深海からの物体X", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "洋上に浮かぶ一隻の船を舞台にしたSFサスペンス・ホラー。セーリングをしていた５人の若者は放棄されたと思われる船を見つけ、乗り込む。５人はそれぞれ船内を探検するが、ある者は魚に襲われ、ある者は魚怪物に変身し、４人が死んでいく。生き残ったマイクはコンピュータの資料から、放射能に汚染されたプランクトンを食べた深海魚たちが未知の怪物に生まれ変わったのを知る……。", "1999-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダーBLACK 恐怖！悪魔峠の怪人館", "仮面ライダーBLACK 恐怖！悪魔峠の怪人館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "特撮テレビドラマ「仮面ライダーBLACK」の劇場版第2弾。暗黒結社ゴルゴムの世紀王シャドームーンは、豊かな自然の広がる北海道夕張市を占拠して世界征服の拠点を築きあげようと企んでいた。そんな中、ゴルゴムに利用されていた牧野博士が脱走を図り、博士を助けた光太郎が夕張の異変に気づく。現地へと向かった光太郎の前に、かつて倒された怪人の霊を人間と合体させて甦らせた亡霊怪人たちが現われ……。", "1988-07-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("士魂魔道　大龍巻", "士魂魔道　大龍巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "徳川家対豊臣家の攻防を軸に据えた時代劇に、タイトル通りの特撮による“大龍巻”を巻き起こす、パニック・スペクタクルの要素を加えた大作。大阪・夏の陣で、徳川勢の攻撃を受け豊臣方の大坂城は陥落した。徳川方に寝返った者もいたが、主人公たち3人や侍女らは市中へと紛れ逃げ延びた。その後、裏切り者や忍者たちと関わっていくが……。ラスト近くの龍巻シーンが圧巻。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("未来の記録", "未来の記録", "", "", "http://image.eiga.k-img.com/images/movie/56355/315x210.jpg?3560", 92, "デジタルシネマの普及と次代を担うクリエイターの発掘を目指す「SKIPシティ国際Dシネマ映画祭」で上映・エントリーされた良質な作品を劇場公開する「SKIPシティ　Dシネマ　プロジェクト」の第1弾作品。2010年の同映画祭コンペティション部門に出品された。理想の学校を始めるため、2人の男女がかつてフリースクールとして使われていた古い家屋にやってくる。男は過去の記憶を消し去りたい元教師だったが、女が1冊の古いノートを開いて過去をのぞいてしまい……。劇団「大人計画」などで活躍する舞台俳優・岸建太朗の長編初監督作。", "2011-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アクエリアンエイジ　劇場版", "アクエリアンエイジ　劇場版", "", "", "http://image.eiga.k-img.com/images/movie/53475/315x210.jpg?4074", 83, "アニメ化もされ人気を呼んだトレーディングカードゲームを、舞台「テニスの王子様」の桜田通ら注目の若手俳優共演で実写映画化。高校生の要は、自分が黒い羽根を持つ獣の一族\"ダークロア\"の遺伝子を受け継いでいることを知らされる。それを裏付けるように、彼の背中には羽根が生え始めようとしていた。世界には他にも特殊な遺伝子を持つ人々がおり、激しい勢力争いを繰り広げているのだという。要もいつしか彼らの戦いに巻き込まれていき……。", "2008-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Getaway", "ゲッタウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "銀行強盗の仲間割れでボスを殺したマッコイは妻とともにひたすらメキシコへ向かって逃げる。今まで映画の世界で犯罪者が逃亡に成功することはタブーになっていたが、この作品のカップルは見事に国境を越える。国境近くのホテルで、追ってきた６人の殺し屋たちとの大銃撃戦の描写はさすがペキンパーならではの迫力。悪役に扮したA・レッティエリの個性も光る。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勢揃い関八州", "勢揃い関八州", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "関八州を思いのままにしようとたくらむ八州取締役・山中誠一郎とその力を利用して縄張りを広げようとする飯岡助五郎。彼らを倒すため、再び赤城山に帰ってきた忠治以下、国定一家の面々と次郎長の命で忠治を訪ねてきた森の石松の活躍を描いた任侠もの。結束信二のオリジナル脚本をメロドラマを得意とした佐々木康が手慣れた演出でこなした。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Callas Assoluta", "マリア・カラスの真実", "", "", "http://image.eiga.k-img.com/images/movie/54425/315x210.jpg?3805", 98, "オペラ界の伝説的な歌姫マリア・カラスの生い立ちから成功をつかむまで、本人の言葉や映像と共にその足跡を追うドキュメンタリー。華やかな成功とスキャンダルに彩られたカラスの人生を、1人の繊細な女性として丹念に描き出す。ドキュメンタリー作家のベテラン、フィリップ・コーリーが監督を務める。", "2009-03-28", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本暗黒史 血の抗争", "日本暗黒史 血の抗争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "工藤栄一監督・安藤昇主演による“日本暗黒史”もの第１作。終戦直後、河上はごろつきを集め河上組を組織して闇屋一家を壊滅させ、市の支配権を握る。さらに名古屋に侵攻しようとするが、激しい抵抗にあい白昼戦に……。河上はついに逮捕される。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版 CLANNAD クラナド", "劇場版 CLANNAD クラナド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "04年に発売され人気を呼んだ恋愛アドベンチャーゲームを、「エースをねらえ！」など数々の名作アニメを手掛けてきた出崎統監督が映画化。父親との確執を抱える不良少年・岡崎朋也は、学校へと続く坂道の下で古賀渚という少女に出会う。病気で休学していた彼女は、学校に行く勇気が持てずにいた。朋也はそんな彼女に、学校で楽しみを見つければ良いのではないかとアドバイス。その言葉に励まされた渚は、廃部になった演劇部の再建を思い立つ。", "2007-09-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛇姫様　第１部千太郎あで姿／第２部緋牡丹地獄／　第３部恋慕花道", "蛇姫様　第１部千太郎あで姿／第２部緋牡丹地獄／　第３部恋慕花道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "川口松太郎の有名な大衆小説２度目の映画化。「紅孔雀」（５部作）、「月笛口笛」（３部作）と同じく、当時流行った連続活劇で、監督の河野寿一はこの年から６年かけて「里見八犬伝」５部作を作り続けた。物語の骨子は、病の父に代わり鳥山藩三万石を預かる琴姫が、剣の名手・千太郎の助けで悪家老を討つという単純なものだが、プロットは波乱万丈だ。悪家老の証拠となる皿の争奪戦。千太郎の活躍。姫の彼への恋慕。一転して追われる千太郎は旅一座に逃げ込み、一躍大スターに。彼に恋する三味線弾き・お島の登場。横恋慕した用心棒は彼を殺そうと悪家老と結託……と際限なく展開する。なお、オリジナルの衣笠版は千太郎とお島の恋が中心となった。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONEY TALKS", "ランナウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「フィフス・エレメント」のオカマのDJ役で脚光を浴びたC・タッカー主演のアクション・コメディ。得意のマシンガン・トークでギャグを連発するとともに、ダイブや銃撃戦などのアクションを体当たりで演じている。密輸犯が護送車から脱走し、偶然同じ手錠につながれていたケチな詐欺師フランクリンも、これに乗じて逃走。ところが、警察はフランクリンを脱獄した凶悪犯として指名手配したから、さあ大変。無実を証明するため、彼は落ち目のTVレポーターを巻き込んで、危険な賭けに出る。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DISCRETION ASSURED", "略奪～甘く危険な悪女〈おんな〉たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "サンパウロ郊外の豪邸に住むアメリカ人ビジネスマンのトレバーは、妻との関係は冷え切り、同僚ペイジとの情事にのめり込んでいた。そして、彼はギャラリーで知り合った美女ミランダとも関係を持ってしまう。トレバーとの不倫が本気の愛に変わっていたペイジは、新しい愛人に激しく嫉妬。密会を邪魔するような行動にまで出始めた頃、ペイジが自宅で死体となって発見される。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アルゼンチンババア", "アルゼンチンババア", "", "", "http://image.eiga.k-img.com/images/movie/33895/315x210.jpg?1258", 112, "人気作家よしもとばななの傑作小説を映画化した感動作。大好きな母を病気で亡くした17歳のみつこ。母を愛していた墓石彫りの父は、その日から行方をくらましてしまう。それから半年後、みつこは町外れで暮らす変わり者の女性“アルゼンチンババア”のもとに父がいることを知る。父を取り戻すため、彼女はアルゼンチンババアの屋敷に乗り込むが……。主人公の父娘を役所広司と堀北真希が、アルゼンチンババアを鈴木京香が演じる。", "2007-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE FLAMME DANS MON COEUR", "わが心の炎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "女優メルセデスは、“夢を与える者”であると同時に、自分を愛する男にとっても夢のような女でありたかった。メルセデスを主人公に、女性の側から決して成就することのない愛の姿、愛を失うことで豊かになれる姿をA・タネールが描く。", "1987-01-01", "26,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ムラサキカガミ", "ムラサキカガミ", "", "", "http://image.eiga.k-img.com/images/movie/55455/315x210.jpg?1660", 63, "10年前、ある女子高生が「夜の11時35分、大鏡の前で“紫の鏡”と5回唱えると、焼けただれた少女が現れる」という噂話を試し、顔面の皮をはがされたという。そんな学校に伝わる都市伝説に、感性の鋭い美奈子は胸騒ぎを覚えていた。そしてある日、合宿で学校に寝泊りしていた美奈子は、友人の絵里に強引に噂の大鏡の前へ連れて行かれてしまう……。主演は「山形スクリーム」の紗綾、共演に「ホテルチェルシー」の長澤奈央ほか。", "2010-06-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルパン三世　くたばれ！ノストラダムス", "ルパン三世　くたばれ！ノストラダムス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "８年ぶりに製作されたシリーズ第５作。ルパンの代名詞的存在だった山田康雄が1995年３月に亡くなったため、ルパンのものまねで定評のある栗田貫一が代わりにルパンの声を務めている。ノストラダムス教団の教祖を名乗るライズリーが次々と予言を的中させていたが、その裏には予言を実現させる特殊部隊の存在があった。ルパンはたまたま乗った飛行機で、峰不二子とダグラス財閥の一人娘ジュリアと出会うが、その飛行機がハイジャックされ、特殊部隊のクリスにジュリアがさらわれてしまう。クリスの狙いはダグラス財閥の金庫に眠る本当の予言書。ジュリアを助け、予言書を手にするために、ルパンはノストラダムス教団に戦いを挑むのだった。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月よりの使者", "月よりの使者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "戦前、人気絶頂の入江たか子と高田稔の共演で作られ、大ヒットとなった作品の、戦後２度目のリメイク。療養所に働く道子は、患者の弘田に求愛されるが、病人の看護が忙しく結ばれなかった。弘田は別の女と結婚し、道子は二人の家庭へ彼の妻の看護をするために派遣される……。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ANDERSON TAPES", "盗聴作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "L・サンダースのベストセラー小説の映画化。７人の犯罪プロフェッショナルが、それぞれの特技を生かしてニューヨークの豪華マンションで強奪を働く。名匠ルメット監督が、コネリー、バルサムらを起用して描いた痛快犯罪アクション。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CREATURE FROM THE BLACK LAGOON", "大アマゾンの半魚人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "アマゾン奥地のブラック・ラグーンに棲息していた半魚人を倒錯的エロティシズムとサスペンスで描いた、SFホラーの佳作。半魚人の全身をなかなか見せず、手足を小出しに緊張を高めていく演出は「ジョーズ」の先駆をなすものといえる。DVDは「ユニバーサル・モンスター・コレクション【緑】」に収録。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Good Will Hunting", "グッド・ウィル・ハンティング／旅立ち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "天才並みの頭脳を持ちながら、幼児期のトラウマが原因で周囲に心を閉ざし非行に走る青年と、妻に先立たれ人生を見失った精神分析医との心の交流を描いた感動作。本作で脚本家デビューを飾ったマット・デイモンとベン・アフレックが見事にアカデミー脚本賞を獲得したことで話題に。また、孤独な精神分析医を演じたロビン・ウィリアムズも助演男優賞を獲得している。監督は「ドラッグストア・カウボーイ」の鬼才ガス・バン・サント。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夕陽の丘", "夕陽の丘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "菊村到の原作『男の歌』を石原裕次郎と浅丘ルリ子のコンビで映画化。ヤクザ・篠原健次は、兄貴分の森川が服役中、森川の情婦・聖子と恋仲になりそれを密告しようとした男を射殺して、聖子の妹・易子のいる函館に身を隠す。やがて森川が聖子を伴って函館に現れる……。石原裕次郎のヒット曲にのって、恋とアクションがほどよく盛り込まれた、いわゆる“ムード・アクション”という呼称は、この作品から正式に定着した。この頃、清純なヒロイン役から演技的にもかなりの成長を遂げていた浅丘ルリ子が、一人の男を同時に愛してしまう姉と妹という二役を見事に演じ分けた。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PLANE｀TE SAUVAGE", "ファンタスティック・プラネット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "ウルの小説『オム族がいっぱい』を下地に、巨大生物に支配された星の出来事を描いたSFアニメーションの名作。巨大なドラーグ族によって支配された惑星。ここでは、人間は虫けら同然の扱いを受けている。人間の赤ん坊テールはドラーグ族の娘のペットとなった。やがて成長した彼は、ドラーグ族が使用している学習機を盗んで逃亡。人間たちは、この学習機を利用してドラーグ族への抵抗を試みる……。奇妙な動植物の生態を捉えた、オリジナリティーあふれる世界が展開。幻想的な映像が絶賛され、アニメ作品として初めて、カンヌ映画祭特別賞を受賞した。", "1973-01-01", "43,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACROSS THE WILD MISSOURI", "ミズーリ横断", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "19世紀初頭のアメリカ・ロッキー山中を舞台に、インディアンの娘と結婚した一人の白人男を描いた異色の西部劇。インディアンと白人たちとの対立を背景にしながら、それを乗り越え、インディアンたちとともに暮らすことを決意する男をC・ゲーブルが熱演。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DOGS OF WAR", "戦争の犬たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "『ジャッカルの日』『オデッサ・ファイル』などのベストセラー作家F・フォーサイスの小説を映画化。西アフリカの独裁国を舞台に、英国の大資本から政権転覆を依頼された４人の戦争プロフェッショナルが暗躍するアクション・アドベンチャー。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魍魎の匣", "魍魎の匣", "", "", "http://image.eiga.k-img.com/images/movie/53085/315x210.jpg?3394", 133, "戦後間もない1952年、東京。世間では謎の美少女連続殺害事件が発生し、不幸をハコに閉じ込めるという怪しげな宗教団体が勢いを増していた。それぞれの謎を追っていた探偵・榎木津や作家・関口、記者・敦子、木場刑事らは、真相解明を求めて京極堂のもとに集う。京極夏彦のベストセラーシリーズの映画化第2弾で、前作に続いて主人公・京極堂を堤真一が演じ、阿部寛、宮迫博之、田中麗奈らも続投。監督は「突入せよ！『あさま山荘』事件」の原田眞人。", "2007-12-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUCK SOUP", "我輩はカモである", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "フリードニア共和国は財政難に陥りディスデル夫人に2000万ドルの調達を依頼したが、夫人はルーファスが独裁者に任命されなくては貸せないと断る。かくしてルーファスが宰相に任命された。隣国のトレンティノはフリードニアを自国の手中に収めようとチコリニとピンキイの二人をスパイとして送り込むが、チコリニは陸軍大臣に任命されてしまった。トレンティノは自分がディズデル夫人と結婚してフリードニアの実権を握ろうとするのだが……。末弟ゼッポを加えた４兄弟最後の作品。マルクス映画全作品の白眉として永遠に記憶される傑作。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING OF THE GYPSIES", "キング・オブ・ジプシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "５世紀頃、中央インドを離れたのをきっかけに流浪の旅を続け、世界各地に移り住むようになったといわれる民族“ジプシー”。ニューヨークに住む彼ら一族の権威ある称号“キング・オブ・ジプシー”をめぐる争いを描いた、ジプシー版「ゴッドファーザー」。「バラキ」の原作者であるP・マーズは、実際に会って取材したジプシーと元ニューヨーク市警警部の話からこの物語を構成したとのことで、リアリティーは十分。独特の慣習を持つジプシーの生活をうかがい知ることができる異色作。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("侠女十三妹／SUPER LADY", "侠女十三妹 カンフー艶舞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "親の仇、紀献唐を討つため、つらく苦しい修行の数々に耐えた十三妹は、様々な試練を経て、ついに悲願の復讐を果たす。3Dカメラを駆使したダイナミックな映像が素晴らしい、カンフー・アクション立体映画の大作。", "1986-01-01", "8,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Salvador", "サルバドールの朝", "", "", "http://image.eiga.k-img.com/images/movie/34125/315x210.jpg?4170", 135, "1970年代初頭のスペイン。独裁政権に対し反旗を翻す地下組織に属するサルバドールは、闘争資金を得るために銀行強盗を繰り返していた。やがて警察との銃撃戦が起き、その混乱の中で逮捕されたサルバドールは死刑を求刑されるが……。「グッバイ、レーニン！」のダニエル・ブリュールが、自由のために戦い25歳の若さで処刑される主人公サルバドールを演じるヒューマンドラマ。共演は「トーク・トゥ・ハー」のレオノール・ワトリング。", "2007-09-22", "9,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三大怪獣 地球最大の決戦", "三大怪獣 地球最大の決戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "5000年前に地球に飛来し、地球人と同化したらしい謎の金星人予言者によって、不思議な出来事が次々と予言されていく。九州・阿蘇の火口からはラドンが、横浜港沖ではゴジラが、そして黒部渓谷の霞沢に落下した隕石からは地球を滅ぼしにやって来たキングギドラが現れ、それぞれ東京に向かう。この危機に防衛大臣は、インファント島の小美人にモスラの協力を要請、モスラはゴジラとラドンを説得し、一致団結してキングギドラに闘いを挑むのだが……。シリーズ中“最強の敵”といわれるキングギドラが、縦横無尽に暴れ回る痛快怪獣映画。対決シーンは大迫力。", "1964-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Precious Life", "いのちの子ども", "", "", "http://image.eiga.k-img.com/images/movie/56345/315x210.jpg?3755", 90, "イスラエルとパレスチナの紛争を背景に、その対立の中で翻弄される小さな命をめぐるドキュメンタリー。余命を宣告されたアラブ人の赤ん坊が、封鎖されたパレスチナ自治区ガザ地区からイスラエルの病院に運びこまれる。イスラエル人医師ソメフはガザ地区の最前線で20年以上取材を続けるイスラエル人のTVジャーナリスト、エルダールとともに、民族や宗教の対立をこえ、赤ん坊を救うために立ち上がるが、行く手にはさまざまな困難が待ち受けていた……。", "2011-07-16", "1,46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr. Woodcock", "Mr.ウッドコック　史上最悪の体育教師", "", "", "http://ecx.images-amazon.com/images/I/51cUdXCzOAL._SL210_.jpg", 89, "", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("憲兵", "憲兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "凄腕の若き憲兵曹長・宮崎は、漢口で暴漢に襲われていた中国人女性・葉白蘭を救ったことから、彼女に愛され、人生と職務の歯車を狂わされていく。葉白蘭の正体は、中国軍の諜報員だった……。『ウルトラセブン』の隊長役でおなじみの中山昭二が、新人時代に主演した異色スパイ・メロドラマ。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RING OF FIRE", "リング・オブ・ファイア 炎の鉄拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "キックボクシング元世界チャンピオン、D・ウィルソン主演の“格闘技”アクション。若き医師ジョニーは、自ら目撃した強盗事件の犯人の一人を手術することになった。しかし手術は失敗。ジョニーは、強盗団から、逆恨みを受け、フィアンセを誘拐されてしまう。警察の助けを借りず、フィアンセの奪回を決意したジョニーは、単身強盗団に戦いを挑んでいく……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブタがいた教室", "ブタがいた教室", "", "", "http://image.eiga.k-img.com/images/movie/53795/315x210.jpg?1346", 109, "新米教師の星先生は、小学6年生の26人の生徒たちと、卒業までの1年間“食べる約束”で子ブタを飼い始めるが、子供たちはブタをPちゃんと名づけて世話をするうちに愛情が芽生え始め、やがてPちゃんを「食べる」「食べない」でクラスの意見は真っ二つに……。1990～93年に大阪の小学校で実際に行われた授業を原案に、命を育むことや、生命とは何かを問いただす。妻夫木聡が初の教師役に挑戦。監督は「ドルフィンブルー」の前田哲。", "2008-11-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昆虫大戦争", "昆虫大戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "お家芸の女性路線が不調で模索期の松竹が苦しまぎれに発表したSF怪奇映画。核実験の脅威と意思を持った昆虫が、人類を襲う恐怖と並行して描かれる。「忘れ得ぬ慕情」の大型台風上陸の特撮で、日本映画特殊撮影賞を受賞した川上景司が特撮を担当。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEPMOM", "グッドナイト・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "J・ロバーツとS・サランドンがともに製作総指揮にも名を連ね、初共演した家族ドラマ。弁護士の恋人と同棲しているファッション・カメラマンのイザベルは、彼の二人の子供に気に入られようと一生懸命。ところが、12歳という難しい年頃のアンナは反抗的、７歳のベンはわんぱく盛りで、仕事との両立に苦労が絶えない。そんな彼女とは違って、３年前に離婚した先妻ジャッキーは完璧に母親の役割をこなし、子供たちにも信頼されている。二人の母親は子育ての問題で衝突を繰り返していたが、やがてジャッキーの発ガンをきっかけに互いに理解を深めていく……。継母と実母という異なる立場の二人の母親の姿を通して、家族の在り方をシビアな視点とユーモアを交えて温かく見つめる。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛の流刑地", "愛の流刑地", "", "", "http://image.eiga.k-img.com/images/movie/1245/315x210.jpg?1660", 125, "日本経済新聞で連載され、社会現象まで巻き起こした渡辺淳一原作の同名ベストセラー小説を、豊川悦司＆寺島しのぶ共演で映画化。かつての人気恋愛小説家・村尾菊治は、ある朝、情事の果てに相手の女性・入江冬香を絞殺し逮捕される。事件を担当する女性検事は、「愛しているから殺した」という菊治の言葉に困惑しながらも2人の過去を探っていく……。監督は本作で長編映画監督デビューを飾ったベテランTV演出家の鶴橋康夫。", "2007-01-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Cassandra Crossing", "カサンドラ・クロス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "　ジュネーブにある国際保健機構に侵入した過激派ゲリラが研究中の伝染性病原菌を浴びたまま逃亡。追跡調査を開始したアメリカ陸軍情報部のマッケンジー大佐はゲリラが大陸横断列車に乗り込んだことを掴む。客を乗せたまま密閉された列車はコース変更し、カサンドラ・クロスと呼ばれる鉄橋へ向かうことに。大佐は細菌の処理と事件の隠蔽をたくらんでいたのだ。チェンバレン博士を始めとする乗客たちは抵抗を試みるが……。オールスター・キャストによるパニック・サスペンス。", "1976-01-01", "4,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネ響「マエストロ6」　ロリン・マゼール　ニューヨーク・フィルハーモニック", "シネ響「マエストロ6」　ロリン・マゼール　ニューヨーク・フィルハーモニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "映画館でクラシック音楽のコンサートを上映する「シネ響」の5作目。2008年、史上初めてアメリカのオーケストラが北朝鮮に招かれて開かれた歴史的コンサートの模様を収録。ロリン・マゼール率いるニューヨーク・フィルハーモニックが、東平壌大劇場でワーグナーの「ローエングリン」第3幕への前奏曲や、ドボルザークの「新世界より」などを演奏した。", "2011-01-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Man on Wire", "マン・オン・ワイヤー", "", "", "http://image.eiga.k-img.com/images/movie/54135/315x210.jpg?3381", 95, "1974年8月7日、フランスの大道芸人フィリップ・プティは、地上411メートルに達するニューヨークのワールド・トレード・センターの2つのタワー間で、命綱もつけずに綱渡りを始める。中止されないように、全てを事前に計画して……。当時の映像や計画を支えた友人らのインタビューを交え、“史上、最も美しい犯罪”とも言われた綱渡りの様子を追うドキュメンタリー。第81回アカデミー賞で長編ドキュメンタリー賞を受賞した。", "2009-06-13", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THEY CALL ME TRINITY", "風来坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "“悪魔の右手と左手”という、いかにも恐ろしげな異名を持つ兄弟が、フラリと訪れた町で弱い者いじめをしているボス相手に騒動を巻き起こしていく。いたるところに、過去の西部劇のパロディーが登場し、血わき肉躍る、というよりは痛快爆笑ウエスタンである。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("億万長者になった男", "億万長者になった男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1980年代のアイドルたちが同窓会ノリで集結した、ラブ・ストーリー含みの人間ドラマ。哲也、明夫、和美の３人は幼なじみ。哲也は30歳までに億万長者になることを目標に商社で働いていたが、会社に疑問を感じ、上司と退社をし新会社を設立した。その頃、明夫も自分の未来をカメラに託す決心をし、哲也の恋人でOLの和美も昇進が決まっていた。しかし資金調達のために、哲也は資産家の娘さおりと結婚してしまう。やがてなかなか軌道にのらなかった会社が急に好転し始める。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンフェア the movie", "アンフェア the movie", "", "", "http://image.eiga.k-img.com/images/movie/33885/315x210.jpg?3596", 112, "篠原涼子がシングルマザーの敏腕刑事を演じ大ヒットを記録したテレビドラマの劇場版。女刑事・雪平は、娘の美央が入院中の警察病院がテロリストに占拠されたことを知る。犯人は警察庁長官を人質にとり、警察庁の裏金80億円を2時間以内に用意することを要求。愛娘を救うため、雪平は病院へと向かうが……。テレビ版のキャストに加え、スペシャル版に引き続いての登場となる江口洋介、さらに椎名桔平、成宮寛貴ら豪華俳優陣が顔を揃える。", "2007-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("３on３〈スリー・オン・スリー〉", "３on３〈スリー・オン・スリー〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ヒップホップのスター、ラッパ我リヤが初主演した近未来活劇。合法的な殺し合いと化したバスケット、３on３に挑む、ならず者トリオの奮闘を描く。音楽にはもちろん、ヒップホップを満載。20XX年、荒廃した日本ではルール無用の暴力バスケット、通称Dボールがブームに。ある日、脱獄した３人組が、故郷の町に戻ってきた。旧知の神父から町の苦境を知らされた彼らはDボールで賞金を稼ぎ、貧しい人を救おうとするが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CITTA｀ DELLE DONNE", "女の都", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "列車の中で巨大なバストの女と乗り合わせたスナポラツ。その女を追って列車を降りた彼は、フェミニスト、洗濯女、パンク娘など次から次へと女たちにほんろうされていく……。フェリーニ、マストロヤンニのコンビが女の都の迷宮で織り成す楽しい夢の映画。", "1980-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY SUMMER STORY", "トラブル・ファミリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "アメリカの片田舎、汚染された湖に生き残る唯一の魚の捕獲にてんやわんやの町で、一家そろって熱い闘いを繰り広げているのが、パーカーファミリーだ。パパは、騒音にたまりかねて隣の大家族に宣戦布告、10歳のラルフィーは近所の悪ガキと連日ケンカゴマの死闘に明け暮れ、ママも負けじと不正所得に反対する大暴動を先導してブタ箱入り。家族全員すぐに熱くなる血統のパーカーファミリー、その人騒がせな一家の一夏の出来事を描く、心温まるホーム・コメディ。マコーレー・カルキンの二人の弟が出演している。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("髑髏城の七人", "髑髏城の七人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREATEST STORY EVER TOLD", "偉大な生涯の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 199, "イエス・キリストの生涯を、巨匠G・スティーヴンスが自ら製作して描き上げた超大作。イエスの誕生から伝道生活、エルサレム入城、ゴルゴダの丘の処刑、そして復活までが力強く描かれていく。スティーヴンスが宗教界のリーダーの助言と協力を得て、脚本を執筆した。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Walk the Line", "ウォーク・ザ・ライン　君につづく道", "", "", "http://image.eiga.k-img.com/images/movie/1215/315x210.jpg?4347", 136, "50年代にロカビリーの黄金時代を築いた実在の歌手ジョニー・キャッシュと、運命の女性ジューン・カーターとの10年間に及ぶ愛を描く感動作。監督は「17歳のカルテ」「“アイデンティティー”」のジェームズ・マンゴールド。撮影は「サイドウェイ」のパパマイケル。ゴールデングローブ賞コメディ／ミュージカル部門の作品賞、男優賞、女優賞を受賞。アカデミー賞の主演男優賞、主演女優賞、衣裳デザイン賞ほか計5部門にノミネート。", "2006-02-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロケーション", "ロケーション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ピンク映画のロケーションに右往左往して走り回る撮影隊の姿を、涙と笑いで綴った作品。映画の撮影隊が主人公ということで虚構の中に虚構が生まれ、それを映画の中のカメラマンが追いかけるという構成が面白い。劇中、急きょ主役に抜てきされる美保純が暗さを秘めた演技で好演。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sex and the City 2", "セックス・アンド・ザ・シティ2", "", "", "http://image.eiga.k-img.com/images/movie/54535/315x210.jpg?2775", 147, "ニューヨークに暮らす4人の独身女性の日常を赤裸々に描き、日本でも大ブームとなった同名TVシリーズの劇場版第2弾。サラ・ジェシカ・パーカーら主要キャストと、監督のマイケル・パトリック・キングが再集結。4人がそれぞれのハッピーエンドを迎えた前作から2年。キャリーは幸せながらもロマンスの失われた結婚生活に疑問を抱き始め、ほかの3人もそれぞれ子育てや仕事や恋愛に悩んでいた。そんな折り、4人はアラブの砂漠へ逃避行の旅に出る。そこでキャリーは、かつての恋人エイダンと再会する。", "2010-06-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キラー・ヴァージンロード", "キラー・ヴァージンロード", "", "", "http://image.eiga.k-img.com/images/movie/54175/315x210.jpg?4650", 97, "結婚したい女と死にたい女の奇妙な逃亡劇を描いたコメディで、岸谷五朗の初監督作品。主演に上野樹里、木村佳乃。結婚式を目前に控えたドジなOL・ひろ子は、ひょんなことから大家さんを殺害してしまう。何としても結婚式を無事に迎えたいひろ子は、死体をスーツケースに隠し、富士の樹海へと車を走らせる。そこへ自殺願望の塊・福子が飛び出してきて・・・・・・。", "2009-09-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MILLION DOLLAR MYSTERY", "おかしなおかしな成金大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アリゾナの荒野にある食堂で、初老の男が“400万ドルを橋に隠した”と言って死んだ。それを聞いた店の主人と９人の客はいっせいに飛び出すと、その橋を目指した。そこへFBIまで現れて……。ひっそりと公開されたが、フライシャーが衰えを感じさせぬ痛快な演出を発揮したコメディ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秉愛", "長江にいきる　秉愛の物語", "", "", "http://image.eiga.k-img.com/images/movie/54305/315x210.jpg?4438", 117, "中国一の大河・長江でダム建設工事が推し進められ、周辺の海抜の低い家々の住人達は移住を余儀なくされていた。体の弱い夫と育ち盛りの子供たちを養うビンアイは、移住に頑固に抵抗するが、一家は次第に追い詰められていく……。学もコネもなく、ただつつましやかに暮らしていた女性ビンアイが、移住計画を推し進める国に対し、抵抗し続けた7年間を記録したドキュメンタリー。", "2009-02-21", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Superstar", "スーパースター　爆笑スター誕生計画", "", "", "http://ecx.images-amazon.com/images/I/21E3BK39HZL._SL210_.jpg", 82, "アメリカの人気コメディ番組「サタデー・ナイト・ライブ」のモリー・シャノン主演による青春コメディ。眼鏡とダサいファッションに身を包んだ冴えない女子高生メアリー。男の子との最高のキスを夢みる彼女は、スーパースターになるべくハリウッド映画のエキストラを目指して奮闘を開始するが……。「キャリー」「フェーム」といった名作のパロディシーンが随所に散りばめられている。共演に「オースティン・パワーズ」シリーズのウィル・フェレル。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CYCLO", "シクロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "ベトナム系フランス人のT・A・ユンの第２作。前作「青いパパイアの香り」はフランスで撮られているが、本作は故郷ベトナムで撮った作品。シクロ（輪タク）運転手の青年は、商売道具のシクロを盗まれたことをきっかけに、ヤクザの情婦となっている姉を頼って、ヤクザ社会に入る。麻薬の売人をしたり、敵対組織と抗争を経験するうちに、彼はヤクザの社会に染まっていく。一方、姉の男で、“詩人”と呼ばれている彼の兄貴分は、破滅的な日々を送っていた。", "1995-01-01", "2,60,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本最大の顔役", "日本最大の顔役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "小林旭扮する主人公は、港湾都市を支配する組の三代目を襲名し、新興暴力団やヤクザ組織を次々に退けて勢力を伸ばしていく。長谷部安春監督の力作「広域暴力・流血の縄張」の姉妹編的な作品。バイオレンス描写などドキドキの展開で、最後はスカッとする。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Faute des Fleurs ̶ a portrait of Kazuki Tomokawa, Musicians of Our Times Episode 2", "花々の過失", "", "", "http://image.eiga.k-img.com/images/movie/55885/315x210.jpg?3274", 70, "詩人、歌手、画家、競輪解説者、エッセイスト、俳優など、さまざまな顔をもつフォークシンガー友川カズキの半生を、気鋭のフランス人映像作家ビンセント・ムーン監督が描くドキュメンタリー。ムーン監督は、友川を崇拝するひとりの日本人から映画製作を提案され、来日して取材を開始する。大島渚監督や三池崇監督など多くの映画人を魅了してきた友川に密着するうち、やがて実の息子との間にあるせつない過去が明らかになる。", "2010-12-18", "76,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("影を慕いて", "影を慕いて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "音楽を通して対抗する男たちを、緊張感をはらんで描いたドラマ。加賀俊男は作曲家、尾形の弟子だったが、尾形はもう一人の弟子、小村を可愛がっていた。音楽コンクールでも実力の劣る小村を推す尾形。失意の念のため気力の失せた加賀だったが友人らの励ましによって流行歌の作曲の道を目指す。だがそこでも尾形の妨害に遭ってしまう。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PICTURE PERFECT", "ピクチャー・パーフェクト　彼女が彼に決めた理由", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "広告代理店に勤める28歳のケイトは才能があるにもかかわらず、独身だからという理由で出世の道が閉ざされている。不運な彼女を救うため同僚が、ケイトが友人の結婚式で出会ったニックという男と婚約したと社長にウソの報告をしてしまう。作戦が図に当たり、大きなチャンスを手にしたケイト。そんな彼女に長年憧れていた会社の先輩サムが接近、同時にニセの婚約者ニックともいいムードになるが……。理想の男と心優しい男、二人の間で揺れ動く彼女の心を捉えたロマンティック・コメディ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラヂオの時間", "ラヂオの時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "人気脚本家、三谷幸喜が初めてメガホンを執った密室コメディ。深夜のラジオ局を舞台に、本番中の相次ぐハプニングでパニックに陥っていく人々が大騒動を展開。個性的な登場人物たちによる絶妙の間合いのセリフから、じんわりとにじむユーモアが冴えわたる。主婦のみや子は初めて書いた脚本がラジオドラマに採用され緊張気味。生放送を直前に控えたその時、主演の大女優が役名をリツコからメアリー・ジェーンに変えたいと言い出す。機嫌を損ねたくないプロデューサー牛島は、早速、ほかの登場人物も外国人名に変更。それを機に、出演者たちは口々に不満を漏らし始める。牛島が場を取り持つうち、メロドラマはやがてアクションものへと変わっていき……。第48回ベルリン映画祭で特別表彰を受けた。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月とチェリー", "月とチェリー", "", "", "http://ecx.images-amazon.com/images/I/21RS14J451L._SL210_.jpg", 82, "「タカダワタル的」のタナダユキ監督によるエロティック・ラブコメディ。大学に入ったばかりの田所健一は、優柔不断な性格が災いして童貞なのに官能小説サークルに所属するハメに。やがて健一は、すでに官能小説家としてデビューしている同サークルの女性・真山に、小説のネタとして童貞を奪われてしまう。バイト先の茜にひかれつつ、真山のことも気になってしまう田所だったが……。6人の監督がエロスをテーマに撮りあげた「ラブコレクションシリーズ」の1作。", "2004-12-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREGORY’S GIRL", "グレゴリーズ ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "恥ずかしがり屋で内気なグレゴリーは、自分の代わりにサッカーチームに入った男まさりの少女ドロシーに一目惚れ。ところが彼女は学校中の人気者でなかなか声がかけられない。ようやくデートにこぎつけたその日、ドロシーを待つグレゴリーの前に、次々と違う女の子が現れる……。青春の一コマを思い起こさせてくれる恋愛映画。", "1980-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("珍品堂主人", "珍品堂主人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "文芸作品の映画化に多大な才を発揮した豊田四郎が、井伏鱒二の同名原作に材を得て取り組んだ喜劇映画。鑑定にかけてはプロ顔負けの実力を持ち、人々から“珍品堂”とまで呼ばれている加納夏麿が、料理屋経営に乗り出し成功してから、周りの人間の様々な計略に引っかかり料理屋を追い出されるまでを描く。脚本は「女の四季」「或る女」で豊田と組んだ名コンビ、八住利雄。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEEP　BLUE", "ディープ・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "約４年半の歳月をかけて撮り上げられた海洋ドキュメンタリー。地球の表面積の約70％を占める海の知られざる世界をカメラが映し出す。自らが発光体となり深海で暮らす生物、約３ヵ月間断食して卵を守る皇帝ペンギンなど、様々な生き物の決定的瞬間を捉えた映像は驚きの連続。それらの映像をより印象的に彩るベルリン・フィルハーモニー管弦楽団の音楽も素晴らしい。すべての生命の源である海は、神秘と脅威に満ちている。アシカを奇襲するシャチ、浜辺を覆いつくすカニ、光のない深海では、奇々怪々な魚たちが泳ぐ。普段は決して目にすることのない脅威の映像が、生命の連鎖を伝えてくれる。", "2003-01-01", "3,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIGHT FIRE WITH FIRE", "ファイアーストーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ワイオミング州刑務所の囚人シェイは山火事を起こし、その中を仲間とともに脱走。途中、鳥類学者ジェニファーを人質に取って逃亡を続ける。森林消火隊のジェシーは彼らを見つけ、猛火の中を追跡するが……。「ダンス・ウィズ・ウルブズ」のカメラマン、D・セムラーが初監督に挑んだノンストップ・アクション。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING LEAR", "ゴダールのリア王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ゴダール流の解釈で描くシェイクスピア作『リア王』の映画化。シェイクスピアの子孫の青年作家が、旅先で出会った奇妙な父娘を、『リア王』の登場人物に見立てストーリーが綴られる。編集マン役のW・アレン、ヒロインのM・リングウォルドら、個性的な配役にも注目したい。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("САДКО", "虹の世界のサトコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "コルサコフの歌劇「サトコ」を大胆にアレンジして作られた壮大なファンタジー・ドラマ。湖の水中の様子や大暴風雨など、スペクタクルな特撮シーンも随所に盛り込まれている。吟遊詩人のサトコはある街を自由と平和のユートピアにしようとしたが、街の人々から笑い者にされてしまう。落ち込んでいる時に、竜宮姫から船を授かり、サトコは幸福の鳥を探す世界航海の旅へ……。", "1952-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武闘派刑事 銀座警察", "武闘派刑事 銀座警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "北芝健、政岡としや原作の人気漫画『銀座警察』の映画化作品。銀座警察強行犯係刑事・犬宮良は、正義感は強いが無鉄砲で型破りな武闘派刑事。主催するマリファナ・パーティーを犬宮に邪魔された少女エリーは、様々な方法で仕返しを試みるが、ことごとく失敗。知り合いの芸能プロダクション社長の大沢に金を積み、仕返しを依頼する。しかし事態は思わぬ方向に発展し、エリーがチャイニーズ・マフィアの人質になってしまう。エリーの父から依頼を受けた銀座警察の面々は、プロフェッショナル相手の闘いを余儀なくされてしまい……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("透明天狗", "透明天狗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "無実の罪で死んだ父の恨みを晴らすため、“透明天狗”となった息子が次々と悪人を始末していく。現代劇でしか登場しなかった“透明人間”を時代劇に持ってくるという発想の柔軟性が面白い。特撮よりも、設定の着眼点に注目したい作品だ。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SO　CLOSE　TO　PARADISE", "ルアンの歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1980年代末、社会が激変する中国。田舎から都会に出て、ヤクザな仕事に手を染めるガオピンが、裏切りに遭って大金を奪われる。弟分のトンツーはガオピンとともに、裏切り者の居場所を突き止めるため、場末のクラブ歌手ルアンホンを誘拐するが……。中国第６世代のワン・シャオシュアイ監督が手掛けた痛切な人間ドラマの傑作。前２作で中国政府のブラックリストに載った彼にとって、これが初の“公認”作品となった。やるせない男女の孤独や悲しみを実直に描き、都会の“闇”の部分をフィルム・ノワール風のムードで浮き彫りにした映像感覚は注目に値する。", "1998-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO RODE TOGETHER", "馬上の二人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「駅馬車」「荒野の決闘」「黄色いリボン」など名作を数多く世に出している西部劇の神様J・フォードの異色西部劇。世をすねた不良保安官のガスリー・マケーブは、旧友の騎兵隊中尉ゲイリーとともに、コマンチ族の土地に潜入する。二人は何年か前にコマンチ族にさらわれた白人の少年と娘を救出しようとしていたのだ。だが、二人がようやくの思いで救い出した少年と娘は、もはや白人社会に溶け込めないほどインディアンの生活と風俗に慣れ親しんでいた……。インディアンにさらわれた白人の子供を助け出すという点では「捜索者」と同じ。ここではスチュアートとウィドマークの二人の西部の男の友情が見もの。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("石井のおとうさんありがとう", "石井のおとうさんありがとう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "まだ福祉という言葉もなかった明治時代に約3000人の孤児を救い、日本初の孤児院を設立した石井十次の生涯を描いた感動作。彼を演じたのは、“マツケン”の愛称で知られるTV時代劇『暴れん坊将軍』の松平健。孤児たちに無償の愛を捧げながら困難と立ち向かう偉人の姿を、貫禄たっぷりに演じている。日系ブラジル人の洋子は祖父の他界を機に自分のルーツを探るため、日本へ。そこで祖父が育った岡山孤児院と石井十次の存在を知る。", "2005-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPLIT DECISIONS", "傷だらけの青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「リバー・ランズ・スルー・イット」のC・シェーファー主演による青春映画。祖父、父、３代にわたるボクサー家に生まれたエディ。彼はアマチュアの星として次期五輪の有力候補だったが、父の命令に背きプロとなった兄が八百長試合がもとで殺され、そのためエディもプロに転向。兄を殺した組織に復讐を挑んでいく。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Guerra conjugal", "夫婦間戦争", "", "", "http://image.eiga.k-img.com/images/movie/55865/315x210.jpg?1716", 88, "1960年代に起きたブラジルの映画運動「シネマ・ノーボ」の一端を担った「マクナイーマ」などで知られる鬼才ジョアキン・ペドロ・デ・アンドラーデ監督が、75年に手がけたコメディ。ブラジル南部の町クチバを舞台に、依頼人の女性を誘惑しようとする弁護士、究極の快楽を与えてくれる女性を探す男性、ケンカの絶えない初老の夫婦という3組の男女が織りなす物語を、ブラジル社会への鋭い批判を盛り込みながら描く。", "2010-12-04", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPANISH ROSE", "ボディーハード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "免職となった密輸捜査官ジャックは、人妻エヴァのボディガードを引き受ける。エヴァの夫は悪名高き貿易商で、その犯罪を探るというのがジャックの目的だった。しかし、エヴァの妖艶な魅力に、ジャックは次第に魅せられてしまう……。ダイナミックなアクションの中にセクシーなシーンを散りばめた娯楽活劇。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATHSTALKER MATCH OF TITANS", "デスストーカー 最後の闘い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "“勇者ストーカー”シリーズの第４作。邪悪な女王が支配する城で開かれた、世界一の闘士を決定するトーナメントにデスストーカーが出場、旅の途中で助けた女闘士とともに、世界征服を狙う女王の陰謀をくじく。長剣をふりまわす重量級のアクション。ライオンのようなマスクをつけた獣人の登場など、ヒロイック・ファンタジーらしい要素がたっぷり。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兇状流れドス", "兇状流れドス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "時代劇の名匠・三隅研次が初めて撮ったヤクザ映画。市川雷蔵のあとがま的立場にあった松方弘樹が彼本来のキャラクターに戻り、喜々として演じている。大正の末、すご腕だが女好きのトッポイ流れ者・銀次が港の新旧ヤクザ抗争に巻き込まれ、町のダニを一掃していく。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鎗火", "ザ・ミッション／非情の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "香港黒社会でボディーガードの任務を与えられた男たちの姿を、実力派俳優の競演で描くハードボイルド・アクション。黒社会のボス、ブンが何者かに命を狙われる。危険を感じた彼は、弟に命じてボディーガードを招集させる。様々な過去を持つ５人の男たちに与えられた命令は、彼を狙った真犯人を見つけだすことだった。全く面識のなかった彼らは衝突を繰り返しながらも、次第にお互いの距離を縮めていくが、そんな中、彼らに新たなミッションが言い渡されて……。晩年の黒澤映画に影響を受けたというジョニー・トー監督は、ワビサビを取り入れた静謐な中にもスリリングなアクションを盛り込み、香港の映画賞を総ナメにした。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("We Feed the World", "ありあまるごちそう", "", "", "http://image.eiga.k-img.com/images/movie/55725/315x210.jpg?4760", 96, "利益追求とコスト削減が生み出した流通のグローバル化によって、世界の需要と供給のバランスが大きく崩れた現状を訴える食のドキュメンタリー。飢餓問題の第一人者ジャン・ジグレール教授や大手食品会社、さらに漁師、農家、家畜業者など世界中で取材を行い、大量生産によって見えなくなってしまった食の流れと、その先に存在する飢餓という現実を浮かび上がらせていく。監督はオーストリアのドキュメンタリー作家エルビン・バーゲンホーファー。", "2011-02-19", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Une vie meilleure", "より良き人生", "", "", "http://image.eiga.k-img.com/images/movie/57355/315x210.jpg?2532", 110, "", "2011-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HURLEVENT", "嵐が丘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "何度も映画化されたエミリー・ブロンテの不朽の名作を、1930年代のフランスに舞台を置き換えて映画化。地主の令嬢カトリーヌと父の養子ロックの愛憎物語を原作に忠実な文芸ものとして描いた、リベット監督作品の中では異色の1本。仏語版原作挿絵用のバルテュスのデッサン画に想を得たという企画で、絵画のように美しい画面を実現したのは名手レナート・ベルタ。", "1996-08-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新兵隊やくざ・火線", "新兵隊やくざ・火線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "北支の最前線、北井小隊に転属した大宮と有田が、悪らつな鬼軍曹・神永と敵対する。大映倒産後、勝プロ製作・東宝配給によって新たに甦った９作目はカラーになって内容もグレード・アップ。特に、アクション・シーンのスケールの大きさに注目。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kika", "キカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "メイクアップ・アーティスト、キカの周囲には、なぜか風変わりな人々が集まる。彼女の恋人でカメラマンのラモン、ラモンの義父でキカの元恋人ニコラス、女性テレビレポーターのアンドレア、ポルノ男優ポール、レズビアンのメイド……。ラモンの母が遂げた謎の自殺を巡り、強烈な個性を持つ人々が複雑に入り乱れていく。監督は、スペインの鬼才ペドロ・アルモドバル。ジャン＝ポール・ゴルチエによる奇抜な衣装が話題を呼んだ。", "1993-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Donnie Darko", "ドニー・ダーコ", "", "", "http://image.eiga.k-img.com/images/movie/1365/315x210.jpg?3719", 113, "88年、米マサチューセッツの小さな町。精神科医に通う投薬治療中の高校生ドニーの前に、ある夜、銀色のウサギが現れ、あと28日で世界が終わると告げる。それからドニーの周囲で奇妙なできごとが起きていく。仕掛けのあるドラマ作りが話題。脚本を読んだドリュー・バリモアが製作を担当して出演、ノア・ワイリー、キャサリン・ロス、パトリック・スウェイジらが共演。音楽はデュラン・デュランなど80年代のヒット曲多数。", "2002-08-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TUBE　TALES", "チューブ・テイルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "“チューブ”の愛称で親しまれるロンドンの地下鉄をモチーフにした９つの挿話から成るオムニバス映画。ロンドンの週刊誌『タイム・アウト』での物語の募集には3000以上の応募があったが、その中から笑いあり、涙あり、夢ありの様々な題材を激選。イギリス出身の人気俳優、E・マクレガーやJ・ロウが初監督に挑戦しているのも話題に。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chatroom", "CHATROOM チャットルーム", "", "", "http://image.eiga.k-img.com/images/movie/56015/315x210.jpg?4450", 97, "自傷癖を持ち、インターネットで見られる他人の自殺映像にはけ口を求めていた青年ウィリアムは、ネットのチャットルームを通じて出会ったジムがうつ病だということを知り、言葉巧みにジムを自殺に追い込もうとする。ウィリアムの行動に気づいた他のチャット仲間は、ジムを救おうとするが……。「リング」の中田秀夫監督が英国で製作した海外進出第2作。主演は「キック・アス」「ノーウェアボーイ　ひとりぼっちのあいつ」のアーロン・ジョンソン。", "2011-03-19", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EDGE OF DOOM", "恐怖の一夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "大都会の貧しい一角に建つ教会を舞台に、冷たい現実世界に起きる殺人を描いたドラマ。花屋に勤め、病身の母を養う青年マーチン。やがて母を亡くした彼は、信仰の厚かった母のため、教会で葬式を行おうとするが、神父に断られる。逆上した彼は神父を殺害し、警察へ連行されるのだが……。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Promets Moi", "ウェディング・ベルを鳴らせ！", "", "", "http://image.eiga.k-img.com/images/movie/53835/315x210.jpg?2958", 127, "「パパは、出張中！」「アンダーグラウンド」で2度のカンヌ映画祭パルムドールに輝く巨匠エミール・クストリッツァによるラブコメディ。セルビアの農村でのどかに暮らす少年ツァーネは、余命をわずかと悟った祖父との約束を守るため、都会の街へ花嫁探しに出かける。そこでツァーネは、可憐な美女ヤスナに一目ぼれし、あらゆる手でヤスナを振り向かせようとするのだが……。", "2009-04-25", "44,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍びの者", "忍びの者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "大泥棒として有名な石川五右衛門を、権力に反逆した下忍として描いた村山知義の同名小説の映画化。監督は社会派の巨匠・山本薩夫があたり、リアルで豪快なアクションと権力の道具として生きるしかない下忍の反逆の叫びを描き出し、時代劇映画に新たな地平を切り拓いた。戦国末期、全国制覇の野望に燃える織田信長は延暦寺、石山本願寺など宗門の掃討に取りかかる。伊賀の国、百地三太夫配下の下忍・石川五右衛門は抜群の技術を誇り、仲間から一目置かれる存在だ。天台、真言修験僧の流れを汲む忍者たちは信長暗殺の密命を受けるが、なぜか五右衛門だけは残されてしまう……。三太夫役の伊藤雄之助の迫力ある熱演が見もの。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PASS THE AMMO", "アーメン・ホラー・ショー～霊感商法をぶっとばせ～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "新興宗教団体の悪どい霊感商法の裏側を鋭くえぐり出した異色ブラック・コメディ。神の名の下に、信仰心の厚い善良なる人々から金を巻き上げている某宗教団体。その本拠地に強盗が入り、伝導活動の要である宗教TV番組を電波ジャックしてしまったから、サァ大変。伝導師も信者も混乱また混乱の大パニックに！　邦題は、伝導師に扮するT・カリーの代表作「ロッキー・ホラー・ショー」のもじりで、ホラー映画のノリは皆無。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北の螢", "北の螢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "明治初期の北海道・石狩原野を舞台に、夢と野望に命を賭ける男たちと、燃えたつ女たちの愛と憎しみを描いたスペクタクル・ロマン。北海道開拓にかかわる残酷物語で、高田宏治のオリジナル脚本。岩下志麻の熱演が作品に幅をもたせている。2004年の「北の零年」へのヒントにもなった１作。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やわらかい生活", "やわらかい生活", "", "", "http://image.eiga.k-img.com/images/movie/33535/315x210.jpg?4550", 126, "「ヴァイブレータ」で04年の映画賞を総なめにした廣木隆一監督＆荒井晴彦（脚本）＆寺島しのぶ（主演）のトリオが再び作り上げた女性のドラマ。東京・蒲田を舞台に、阪神大震災で両親を亡くしたショックで、大企業を退職した35歳、独身のヒロインの日常をユーモラスかつリアルに描く。原作は昨年芥川賞を受賞した絲山秋子のデビュー作「イッツ・オンリー・トーク」。", "2006-06-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゼラチンシルバーLOVE", "ゼラチンシルバーLOVE", "", "", "http://image.eiga.k-img.com/images/movie/53575/315x210.jpg?4753", 87, "写真家として活躍する操上和美の初監督作品。主演は「息子」「隠し剣　鬼の爪」の永瀬正敏と、「たそがれ清兵衛」「トニー滝谷」の宮沢りえ、主題歌は井上陽水が手がける。男は運河を隔てた向かいの部屋に住む女を常に盗撮していた。女は規則的に読書をし、ゆで卵を食べ、着飾って出かけていく。いつしか女に心を奪われていく男は、女を執拗に追い続けるのだが……。", "2009-03-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひとひらの雪", "ひとひらの雪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "家庭を捨てて、20も歳下の秘書を恋人にしている中年男のエゴイズムと、やるせなさを描いて、ベストセラーになった渡辺淳一の小説の映画化。恋に生きる中年男の姿を、根岸吉太郎監督が情感を込めて描く。秋吉久美子が熟れたヌードを披露する。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MUSIC OF CHANCE", "ミュージック・オブ・チャンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "現代アメリカ文学の旗手であり、秀作「スモーク」の原作者でもあるP・オースターの小説を映画化。父親の遺産を手にした元消防士のナッシュは旅の途中、負け知らずのギャンブラー、ポッツィと出会う。その勝負強さからイカサマ師と勘違いされたポッツィと、彼に全財産を託したナッシュは勝負のため富豪宅へと向かう。順調に勝ち進むポッツィだが、ナッシュが模型の人形を盗むと同時にツキに見離され、無一文。彼らは富豪宅の城壁を築く仕事をするはめになる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひだるか", "ひだるか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "戦後最大の労働運動“三井三池争議”を題材に、転換期を迎えた現代を生き抜くヒントを探ろうとする社会派ドラマ。デジタル化の波を受けた地方TV局キャスターの自立のドラマに三池のテーマを重ね、現代人の歩むべき進路を問い直す。福岡中央TVに勤める陽子はキャスターとして活躍していた。ところが局が存亡の危機を迎え、組合が分裂。それが父が体験した“三井三池争議”に重なり、彼女は当時のことを調べ始める。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宋家皇朝／THE SOONG SISTERS", "宋家の三姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "近代中国史を動かした実在の三姉妹の人生を描いた大河ドラマ。清王朝から中華民国へと至る激動期に幼少期を過ごした名家、宋家の三姉妹。長女は財閥の御曹司に嫁ぎ、次女は革命家として有名な孫文の妻となった。だが、亡き孫文の遺志を継いで運動に精力を傾けていた次女は、強硬的な方針をとる権力者・蒋介石や、その妻となった三女と対立してしまう……。芯の強い生き方を通した姉妹に扮する、３大女優の熱演に魅了される。", "1997-01-01", "6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鴛鴦歌合戦", "鴛鴦歌合戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "一部のファンの間でカルト・ムービーとなっている時代劇ミュージカルの傑作。太平洋戦争へ日本が傾斜していく暗い時代、戦争協力映画が多く作られていたなかで、マキノはひたすら軽快・軽妙な映画を作っていた。日本のミュージカル映画はあまり成功していないが、この映画は例外的に成功した作品といえるだろう。貧乏浪人・浅井礼三郎は、長屋の隣家の娘・お春と恋仲だった。だが、礼三郎に想いを寄せる娘が他にも二人。そのおとみと藤尾はどちらもゆずらず、歌合戦にこと寄せて、皆が集まれば喧嘩が絶えない。ところが峯沢丹波守という陽気な殿様がお春に一目惚れし、お屋敷へ差し出すよう申しつけた。お春の父はそれを断わるが……。“ボクは陽気な殿様…”と歌いながら登場してくるディック・ミネといい、珍しく渋い歌声を聞かせてくれる名優・志村喬といい、思わず爆笑し、感心してしまう場面がいっぱい。映画とはこんなにも観る者を幸福にしてくれるのかと改めて感じさせてくれる一編。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヘルズエンジェルス", "ヘルズエンジェルス", "", "", "http://image.eiga.k-img.com/images/movie/54335/315x210.jpg?2646", 0, "ヒロモト森一と韮沢靖がコラボレートした同名コミックをアニメーション制作会社のマッドハウスが映画化。監督は、「スレイヤーズごうじゃす」の作画監督の山川吉樹。元気が取り柄の少女・天鐘りんねは猫を助けた際の不慮の事故により、地獄の学校“三途ノ川学園”に転校することに。学園長ヘルヴィスに自分が死亡したと伝えられるりんねだが、元の世界に戻る方法を模索しながら落ちこぼれのクラスメイトたちと徐々に仲良くなっていく。", "2009-02-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROM NIGHT", "プロムナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "年に一度行われるプロムナイト（学年末パーティー)。楽しいはずのこの夜だが、正体不明の殺人鬼が学園のあちこちで血みどろの殺人を重ねていく。犯人は精神異常者なのか？　それとも……。グラフィック・デザイナー出身のP・リンチ監督が描く学園ホラー映画。", "1980-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鮮血の手型", "鮮血の手型", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "同年『東京キッド』をヒットさせた直後の天才少女歌手・美空ひばりが主演した、娯楽時代劇。ある日、浪人・塙左近は、流しの姉妹が武士たちに襲われているのを助け、妹のみどりを家に連れ帰る。その日から、細川家の江戸屋敷で連続殺人が発生。八丁掘では、その犯人を左近だと思うが……。当初、阪東妻三郎主演作として企画されたが、彼が病気になり、主役が交代した。ビデオタイトルは「左近捕物帳」", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER SCHUH DES MAINTU", "マニトの靴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ドイツで自国製映画としては歴代No.1のヒットなったウエスタン・コメディ。白人とアパッチの珍妙なやりとり、下ネタも飛び出すおバカなギャグなど笑いどころが盛りだくさん！ 開拓時代の西部。借金で部族を追われたアパッチ族のアバハチは、義兄弟となった白人のレンジャーとともに先祖のお宝を探す旅に出る。だが、この宝を狙って悪徳保安官が暗躍。地図を奪われたアバハチとレンジャーは逆転の秘策を練り……。DVDタイトルは「荒野のマニト」。", "2001-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PART XII 逆転相続", "難波金融伝 ミナミの帝王　劇場版PART XII 逆転相続", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "高利貸し・銀次郎の活躍を描いた人気金融シリーズ。売れない演歌歌手、悟郎は、銀次郎からの借金で自らのCDを買い占めて歌手としての地位を保っていた。そんな中、彼に岩崎という男から投資の話が舞い込むが、そこには思わぬ罠が潜んでいた。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("調布空港", "調布空港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "伊豆諸島と東京を結ぶローカル空港・調布空港を舞台に、人生に追いつめられた人々が織りなす人間模様をつづったハートウォーミング・ドラマ。遠距離恋愛中の恋人と連絡が取れず神津島からやってきた女性、神津島の実家から“父危篤”の報せを受けたエリート会社員、ギャンブルが原因で妻に逃げられたタクシー運転手。まったく接点のなかった彼らの人生が調布空港で交差し、それぞれ決断の時を迎えるまでを描いていく。", "2006-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十三通目の手紙", "十三通目の手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "とあるジャズバーで、偶然にもある事件の真相に迫ることになった14人の男女の姿を描き出す密室推理劇。劇団“御成門”が2002年に初演し、その後再演もされた舞台劇を映画化したサスペンスフルな作品。自称“探偵”や編集者、そして芸能プロダクションの敏腕プロデューサーなどの個性的な面々が繰り広げる心理戦が熱い１本だ。都会の片隅にあるジャズバー“バードランド”に、いつもと同じように編集者など常連客が集まっていた。マスターの久保が書いた小説が新人賞候補になった記念の特別な夜だったのだが、探偵と名乗る見知らぬ男がやって来て……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二重被爆　語り部・山口彊の遺言", "二重被爆　語り部・山口彊の遺言", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "1945年8月、広島と長崎で2度にわたって被爆した「二重被爆者」として知られ、2010年1月に93歳でこの世を去った山口彊氏の晩年をとらえたドキュメンタリー。2005年より自らの体験を後世に伝える語り部としての活動を開始した山口氏が、末期の胃がんに冒されながらも反核や平和を訴え続ける姿を追うほか、被爆を題材にした映画をつくりたいというジェームズ・キャメロン監督との交流なども映し出していく。", "2011-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("69", "6ixtynin9〈シックスティナイン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "タイの新鋭、P・ラッタナルアーン監督の犯罪映画。不況の続くタイ、ファイナンス会社で秘書として働くトゥムは、突然解雇されてしまう。翌朝、まだリストラのショックから立ち直れない彼女のアパートのドアの前にラーメンのダンボール箱が置いてある。部屋で中を開けてみると、そこには100万バーツの大金が入っていた。すると突然、ヤクザの二人組が彼女の部屋に押し入ってきた……。", "1999-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Universal Soldier: The Return", "ユニバーサル・ソルジャー：ザ・リターン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "戦死しながら、記憶や感情のない究極の兵士として蘇生させられたユニバーサル・ソルジャー＝US。その対決を描いたアクション大作「ユニバーサル・ソルジャー」の続編。前作で人間の心を取り戻したUSのリュックは政府特別プロジェクトのテクニカル・エキスパートの任務に就いていた。そのプロジェクトとは、聡明で機敏な新型US2500を作り上げるものだった。だが、この計画が中止になることを知ったスーパーコンピュータは自らのシステムを守るために、US2500を操って人間に宣戦布告。初代USであるリュックはこれを鎮めるために戦いに挑むのだった……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PERRI", "ペリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "一匹のリスの成長物語を、実際の動物を使って描き出したディズニーの動物映画。静かな森で暮らすリス一家。だがある日父リスが、テンの襲撃から家族を守るために犠牲となって死ぬ。母リスによって安全な場所に移された子リスは、様々な動物たちの協力によって成長していく。リスの幻想的な夢の場面が印象深い。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅がゆく２ 戦争勃発", "修羅がゆく２ 戦争勃発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "わずかな組員とともに新宿に事務所を構える本郷組の本郷流一は、全国制覇への野望に燃えていた。一方、本郷のライバルの岸田組組長の伊能は、伊豆の大浜組の縄張りを奪い取ろうと攻撃を仕掛けていた。窮地に立った大浜は、関東木曜会に助けを求めに行くが、その途中、伊能の刺客に襲われる。そこを助けたのが、本郷の兄弟分の京本だった。それをきっかけに、本郷と伊能の全国制覇をかけての対決が始まるのだった……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIORGIO ARMANI: A MAN FOR ALL SEASONS", "アルマーニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "ファッション界に君臨する超大物デザイナー、ジョルジオ・アルマーニに１年間密着して作られたドキュメンタリー。デザイナーとしての精力的な活動はもちろん、エリック・クラプトン、シュワルツェネッガーといったセレブとの交流、ビジネスマンとしての才能など多彩な角度からの取材を敢行。プライベートをほとんど明かさないことで知られるアルマーニの貴重な素顔も収められている。", "2000-01-01", "3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Assault on Precinct 13", "ジョン・カーペンターの 要塞警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "　ストリートギャングに命を狙われている男が引越し作業中の警察署に逃げ込んできた。武装した追っ手はあっという間に建物を包囲。人員や武器弾薬もわずかな警察署は外部との通信手段も遮断され、孤立無援状態に。警部補のイーサンは獄中の犯罪者ナポレオンと組み、署内に立てこもって敵と戦うが……。ジョン・カーペンター監督が敬愛するハワード・ホークスの「リオ・ブラボー」をもとに作り上げたアクション・スリラー。05年にはリメイク作「アサルト１３／要塞警察」も製作された。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SNIPER２", "山猫は眠らない2／狙撃手の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "トム・ベレンジャーがいぶし銀の演技を披露するサスペンス・アクションの続編が10年ぶりに登場。自らのルールに従って過酷なミッションを遂行する狙撃手の生きざまをスリリングに描き出す。海兵隊を除隊し、今は狩猟ガイドをしているスゴ腕の狙撃手ベケットが、新たな任務を依頼される。バルカン半島でイスラム教徒抹殺を行っている将軍の暗殺を任されたベケットは、死刑囚コールを相棒に現地へ乗り込むが……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IO GILDA", "ギルダ 暗黒街の情婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "35歳という若さでニューヨークの犯罪組織のボスになった男マックス。ハンサムで色好みの彼だったが、高級クラブの花形ダンサーであるギルダを一目見た時から、彼女との肉欲の世界へ落ちていった。ニューヨークの暗黒街でうごめく男と女の姿を描いたエロティック・サスペンス。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夫が見た「女の小箱」より", "夫が見た「女の小箱」より", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "会社の株式課長の重責を担う川代。彼は今、会社の株が何者かによって買い占められようとしていることに頭を悩ませていた。そんな彼の妻・那美子は、家庭をほったらかされた腹いせに、バー“２・３”へ遊びにいく。そのバーの経営者・石塚こそが、川代の会社乗っ取りをたくらむ張本人で……。川代と妻・那美子、そして石塚とその秘書、バーのマダム。彼らが繰り広げる、陰謀と愛欲が入りまじった心理戦を、増村保造が持ち前のカット構成の技法を駆使して描く。増村＝若尾コンビの代表作。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A3D ayumi hamasaki ARENA TOUR 2009 A NEXT LEVEL", "A3D ayumi hamasaki ARENA TOUR 2009 A NEXT LEVEL", "", "", "http://image.eiga.k-img.com/images/movie/55645/315x210.jpg?2255", 0, "人気アーティストの浜崎あゆみが2009年4～10月の7カ月にわたって行った、全国12都市で約30万人を動員したアリーナツアー「ayumi hamasaki ARENA TOUR 2009 NEXT LEVEL」の模様を記録した3D映像。ポップに演出された大ヒット曲の数々が、臨場感たっぷりにスクリーンに映し出される。", "2010-08-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ刑罰史 私刑！", "やくざ刑罰史 私刑！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「無頼平野」「ねじ式」「地獄」など、近年、再び精力的な活動を続けている映像作家・石井輝男。その個性的な作風で知られる彼が、ヤクザ社会の非情な“掟”に肉薄。凄惨なリンチ場面を生々しく再現した３つのエピソードを通して、人間の残酷性を浮き彫りにしていく異色オムニバス。撮影は、名作「緋牡丹博徒・花札勝負」の古谷伸が務めた。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丑三つの村", "丑三つの村", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「八つ墓村」のベースにもなった1938年に岡山県の山奥で発生した実話を基にするバイオレンス・サスペンス。病身のため兵役に漏れ、暗い日々を送る小集落の若者が、閉鎖社会の恐ろしい習俗から起こる村の人間の行動を嫌い始め、ついには武器を手にして30名もの大虐殺を敢行する。殺りくシーンが生々しい。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とんかつ大将", "とんかつ大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "死後、その作品が再評価されている川島雄三の松竹大船時代の初期作品。“とんかつ大将”と呼ばれ親しまれている青年医師・荒木勇作は、自分の住む長屋が悪徳弁護士・大岩のたくらみで取り壊されるのを阻止しようと、反対運動を起こす。しかし、勇作は自分の父の選挙運動のために、長屋の住民を利用しているのだと中傷されたことから孤立していく。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ISLAND　OF　DR.　MOREAU", "D.N.A.ドクター・モローの島", "", "", "http://ecx.images-amazon.com/images/I/51leuE7IS%2BL._SL210_.jpg", 99, "南国の孤島の研究室で、天才遺伝子学者モロー博士が、脅威の遺伝子操作の研究を行っていた。それは、動物と人とのD.N.A.を配合し、新しい生物を作り出そうというものだった……。SFXに「ターミネーター２」「ジュラシック・パーク」などのスタン・ウィンストンを迎えたのをはじめ、ハリウッドの実力派スタッフ、キャストを結集して、SFの古典『モロー博士の島』を最新の映像技術で再現。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クロサワ映画", "クロサワ映画", "", "", "http://image.eiga.k-img.com/images/movie/55585/315x210.jpg?4008", 93, "女性お笑いトリオ「森三中」のメンバーのなかで、ただひとり未婚の黒沢かずこ。そんな黒沢はある日、パーティ会場でイケメン俳優の渋江譲二に声をかけられ、たちまち恋に落ちる。2人の仲は徐々に深まっていくかに見えたが……。女芸人の悲哀と葛藤（かっとう）を描き、第2回沖縄国際映画祭の長編プログラム“Laugh部門”グランプリ＆審査員特別賞を受賞した。キャストのほぼ全員が本人役で出演している。", "2010-08-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVY PETTING", "ヘビー・ペッティング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "1950年代のアメリカのティーンエイジャーのセックス感覚を、当時のニュース、TVフィルムや「暴力教室」「理由なき反抗」などの映画の断片と、詩人のアレン・ギンズバーグや当時10代だったミュージシャンのデヴィッド・バーンらが自分の性体験を語る様を交錯させて描いていく、ユニークなドキュメンタリー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("河内のオッサンの唄 よう来たのワレ", "河内のオッサンの唄 よう来たのワレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "前作が予想以上に大ヒットしたことから急きょ作られた続編。主要スタッフ・キャストは前作と同様ながら、その封切り終了から本作の完成まで、わずか１ヵ月足らず。そのため必ずしも成功作とは言いがたいものになってしまった。ただ、田中邦衛と伊佐山ひろ子が、ピラニア軍団を食う怪演を見せている。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA STAZIONE", "殺意のサン・マルコ駅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "嵐の夜、駅の夜勤の男に美女が救いを求めてくる。彼女は凶暴な男に追われていた。電話線が切られ、外部との接触を絶たれた二人の恐怖の夜が始まる……。３年間もロングランを続けた舞台劇を同じ俳優で映画化。監督・脚本・主演の３役をこなすルビーニが手腕を見せる新感覚のイタリア製サスペンス。", "1990-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shortbus", "ショートバス", "", "", "http://image.eiga.k-img.com/images/movie/34145/315x210.jpg?1489", 101, "「ヘドウィグ・アンド・アングリーインチ」で衝撃のデビューを果たしたジョン・キャメロン・ミッチェル監督が、“愛”と“セックス”をテーマに描いた人間ドラマ。人に言えない悩みを抱えるカップルカウンセラーのソフィア、彼女の元へ相談に訪れたハンサムなゲイのカップル、ジェイムズとジェイミー、孤独を抱えるSM女王セベリン……。愛を求めて彷徨う彼らは、ニューヨークのサロン“ショートバス”にたどり着く。", "2007-08-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私たちが好きだったこと", "私たちが好きだったこと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "宮本輝の小説に惚れ込んだ岸谷五朗が企画、主演した佳作。倍率76倍という高層公団マンションに当たった照明デザイナーの与志と、ネパールにしか生息しない蝶を撮ることを夢見るカメラマン・佐竹。彼らは酒場で、不安神経症を抱えるOL・愛子と愛に傷つきながら懸命に生きる美容師・曜子という二人の女性に出会う。そして、４人は２LDKの部屋で奇妙な共同生活を送ることに。２年間の生活の中で彼らはお互いを想い、二つの愛が生まれていくが……。青春も終わりに近づいた４人の男女が純粋な愛と時間を重ねていく、大人のための青春映画。「バタアシ金魚」「きらきらひかる」「トイレの花子さん」と１作ごとに高い評価を得てきた松岡錠司監督が、彼らの心の機微を春の日ざしのような温かさで見つめていく。不安神経症を抱え、自分に自信を持てないでいる愛子を、夏川結衣が繊細に演じている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BREAK UP", "赤い標的・THE BREAK UP", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ドメスティック・バイオレンス(＝家庭内暴力)を題材にして、B・フォンダが演じる夫の暴力に苦しむ女性の運命を描くサスペンス。夫フランキーに何年も暴力を振るわれ続けているジミーは聴力をほとんど失うほどの傷害を受けていた。そんなある日、フランキーの車が渓谷の下で見つかり、車内から黒焦げの死体が発見される。これを知ったジミーは喜びを隠せなかったが、同時に自分が夫殺しの容疑者になっていることを知った。刑事の目を盗んで逃げ出したジミーは妹シェリーの家に向かうが、そこで彼女の死体を発見。シェリーがフランキーと不倫していた証拠も見つけてしまう。動揺したジミーは母親を訪ねるが、そこでフランキーが生きているという情報を得る……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロックンロール★ダイエット！", "ロックンロール★ダイエット！", "", "", "http://image.eiga.k-img.com/images/movie/53745/315x210.jpg?2836", 102, "コラムニスト・中丸謙一朗による異色ダイエット本「ロックンロール・ダイエット」を映画化。かつてはギンギンのロックン・ローラーだったロック居酒屋のマスター、42歳のガン（嶋）は今では万年ジャージ姿のメタボリックで家族にもバカにされている。そんなガンの店に謎のギター女・奈々（長澤）が現れ、「ブクブク太っているのはロックじゃない！」とガンを挑発すると、ガンは再びステージに立つため過酷なダイエットを始める。", "2008-09-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BACKLASH", "六番目の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "アリゾナの奥地で５人の白人旅行者がインディアンに虐殺される。被害者を自分の父と思ったジムは現場へと急行、そこでキャロルと出会う。彼女もまた、被害者が自分の恋人ではないかと思い現場にやって来たのだった。やがて彼らは、旅行隊に６人目の男がいたらしいことを知る。グルーヴァーの原作をもとにした、探偵趣味満点の西部劇。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雑兵物語", "雑兵物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "戦国時代、荒れはてた寒村に足軽の徴発がきた。女までも男装させられて頭数をそろえたあと、一行は戦に参加するが、両軍の勝敗をバクチのタネにしたり、敵方の旗を盗んできて味方の難を避けたりと、ハチャメチャなノリで戦乱を駆け抜けていく。池広一夫監督のスラップスティック描写が楽しい時代劇。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUT FEU，TOUT FLAMME", "イザベル・アジャーニ 炎のごとく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "“ファム・ファタール”的な役柄の多い I・アジャーニだが、本作では大臣秘書という知的でエレガントな役に扮している。パリに住む３姉妹のもとへ、15年間も放浪を続けていた父親が戻ってくる。彼は、カジノ建設のために家財を売り飛ばし始める。娘たちも、いつしか父の危険な冒険に巻き込まれていく。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAKEOUT", "張り込み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "アメリカ西海岸西北端の港町、シアトル。刑事クリスとビルは、脱獄した凶悪犯を捕らえるために、その恋人の家の張り込みを命じられた。つまらぬ仕事だったが、なんとクリスはその張り込み相手の美女マリアに一目惚れしてしまう。電話屋になりすましてマリアの家に上がり込んだり、偶然を装ってマーケットで出くわしたり、と奇妙な恋愛劇が始まって、そこになんと犯人が舞い戻ったから一大事。笑いとスリルの大騒動が展開する。魚市場や木材工場での、おかしくも凄絶なアクション。職人監督、J・バダムらしい刑事コメディ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("晩菊", "晩菊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "林芙美子の３つの短編小説『晩菊』『水仙』『白鷺』を一つにまとめて翻案した成瀬巳喜男監督の名作。それぞれのエピソードがユーモアのうちにほのかな哀感を漂わせて描かれる。とりわけ杉村春子扮する主人公が、以前燃えるような恋をした男の来訪を、最後の輝きを賭けて迎えるシーンには、男が金を借りに来たことを悟った杉村の心の動きが見事に表われて圧巻である。４人の中年女に、いずれ劣らぬ芸達者をそろえたことが作品に深みを加え、成瀬監督らしい色を出している。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ケンタとジュンとカヨちゃんの国", "ケンタとジュンとカヨちゃんの国", "", "", "http://image.eiga.k-img.com/images/movie/54905/315x210.jpg?4815", 131, "孤児院で兄弟のように育ったケンタとジュンは、ひたすら壁を壊すだけの解体工事の仕事（はつり）で生計を立てていた。低賃金で重労働という厳しい環境に加え、陰険ないじめが横行する現実に苛立つ2人は、所属する会社の事務所をぶち壊して、ケンタの兄がいる北を目指し旅に出るが……。出演は、ケンタに松田翔太、ジュンに高良健吾、カヨに安藤サクラ。監督・脚本は「ゲルマニウムの夜」の大森立嗣。", "2010-06-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAN WITH A GUN AKA HIRED FOR KILLING", "裏切", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "往年のフィルム・ノワールを彷彿させるサスペンス・ドラマ。殺し屋のジョン・ハーディンは、高利貸しのジャックに雇われ、彼の妻でありハーディンの愛人でもあるレナの殺しを依頼される。ハーディンはレナにこの計画を打ち明け、逆にレナから双子の妹キャシーの殺しを頼まれる。罪のないキャシーを殺すことのできなかったハーディンは、レナから命を狙われ、間一髪のところで切り抜ける。ジャックとレナの罠が待ち受けているとも知らずハーディンは町へと向かう……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRANZ　SCHUBERT　NOTTURNO", "夜曲〈ノットゥルノ〉　シューベルト愛の鼓動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 177, "1928年11月19日に31歳の若さでこの世を去った作曲家フランツ・シューベルト。彼が死に至るまでの５年間を、詩情豊かな映像美の中に映し出す。体に不調を来して以来、不安に苛まれるシューベルトは友人ショーバーらに支えられながら作曲に没頭していた。『シューベルティアーデ』などを生み出し、その才能に惹かれた女性たちに取り囲まれても、純粋な愛を求める彼の心は満たされることはなかった。孤独感を募らせる中、ついに彼は愛する女性に出会い、彼女への思いは『死と乙女』に託される。しかし、病魔は着々と彼の体をむしばんでいた……。全編にシューベルトの名曲を配し、苦悩に満ちたシューベルトの知られざる過去を浮き彫りにする。", "1987-01-01", "2,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LONG SHIPS", "長い船団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "伝説をもとにしたF・ベンツォンの同名小説を映画化した作品。初期十字軍が回教徒から奪ってきた黄金を溶かして鋳造したと伝えられる、聖ジェームズの鐘をめぐるバイキングとムーア人の激烈な戦いを、宗教色濃く描いていく。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tarzan", "ターザン", "", "", "http://image.eiga.k-img.com/images/movie/1795/315x210.jpg?3316", 88, "嵐で船が難破し、アフリカの未開のジャングルに漂着した英国貴族の夫婦とその乳飲み子。一家が密林で生きていく決意をしたその矢先、夫婦はヒョウのサボーに襲われ殺されてしまう。一方、ゴリラの群れを率いるボス、カーチャックとその妻カーラの産まれたばかりの子供もまた、サボーの犠牲となっていた。奇跡的に助かった人間の赤ん坊を見つけたカーラは、この赤ん坊を育てようと強く決意し、“ターザン”と名付ける。", "1999-12-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遠い雲", "遠い雲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "休暇で帰郷した圭三は、家のために市会議員の息子と結婚した初恋の娘・冬子と再会する。彼女は未亡人となっており、二人の間に若き日の思い出がよみがえる……。飛騨高山の美しい自然を背景に、大人の男女の恋愛を繊細なタッチで抒情豊かに描いた木下惠介作品。揺れ動く女心を高峰秀子が見事に演じる。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA SCOUMOUNE", "ラ・スクムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "J・ジョヴァンニが若い頃、ペルピニヤン・サンテ監獄の牢獄生活で知った男をモデルにして書いた小説を自ら脚色、監督した佳作。大戦前の1933～46年のフランス解放直後にいたるマルセイユの暗黒街に生きた男の友情を描いたフィルム・ノワール。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とっておきVirgin'Love！　童貞物語３", "とっておきVirgin'Love！　童貞物語３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "“童貞物語”シリーズ第３作。渋カジ族”と呼ばれる４人の若者のちょっとエッチな私生活を、コミカルに描き出した青春ラブ・ストーリーだ。このシリーズおなじみの、少年たちにエールを贈る女性役には、当時TV、雑誌で活躍していた本田理沙、石野陽子のほか、AV女優の黒木香、樹ますみらが扮している。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録・北海道やくざ戦争／逆縁", "実録・北海道やくざ戦争／逆縁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "戦後の北海道における極道史の一端を映画化した任侠映画。故郷の札幌に舞い戻った片岡が、ススキノ一帯を縄張りにしている実力者、三原の舎弟になる。片岡は愚連隊を次々と駆逐して三原の札幌制圧に貢献。しかし北海道に巨大勢力を築いた穂高一家が、三原一派への抗争を仕掛けてくる。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・タイガース 世界はボクらを待っている", "ザ・タイガース 世界はボクらを待っている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "アンドロメダ星の王女・シルビィは宇宙ドライブの途中、折しも公演中のザ・タイガースのサウンドの迫力に飛行不能となり日本劇場に不時着。ファンの波に巻き込まれ王女は失神したがジュリーに助けられる。王女を連れ戻そうとするアンドロメダ警察とザ・タイガースの攻防戦が繰り広げられる。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野菊の墓", "野菊の墓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "澤井信一郎の監督第１作で、松田聖子の映画デビュー作。歌人・伊藤左千夫の原作は、これが３度目の映画化である。木下惠介による最初の映画化と同様、巡礼の老人の回想という形で物語は始まる。醤油製造業の旧家に生まれた政夫は、病弱な母の世話をするいとこの民子に恋心を抱く。しかし、世間の醜聞を恐れて二人は引き裂かれ、民子は無理矢理に結婚させられ、やがて帰らぬ人となる。澤井監督は新人らしからぬ落ち着いた演出によって、この古風な物語をしっとりと落ち着いた叙情的作品に仕上げた。この後、薬師丸ひろ子、原田知世、石原真理子、後藤久美子と澤井は次々にアイドルを起用し、彼女たちの新生面を引き出す魅力的な作品を作っていく。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("100万人の娘たち", "100万人の娘たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "観光地、宮崎とタイアップして製作された五所平之助監督の青春映画。宮崎交通に勤めるバスガイド姉妹と、観光ホテルに勤める青年との三角関係、姉の死による恋愛の破局といった物語を、観光名所を紹介した映像をふんだんに盛り込んで描く。バスガイドの制服に身を包んだ若き日の岩下志麻のみずみずしい演技が光る。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PEACEKEEPER", "ピースキーパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "空軍上層部の命令を無視して難民に救援物資を降下したクロス大佐。ヒーローとしてマスコミに迎えられた彼への新たな任務は、支持率アップを図る大統領と行動をともにし、核発射装置のブリーフケースを守ることだった。ところがホテルで武装集団に襲われたクロスはケースを奪われ、孤独な奪回作戦を繰り広げるはめになる。ミサイル基地を舞台に、D・ラングレンがテロリストとの激烈な闘いを熱演するハード・アクション。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sphinx", "スフィンクス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "古代エジプトのセティ1世の墓を巡って繰り広げられる陰謀を描いたサスペンス。監督、製作総指揮は「猿の惑星」「パットン大戦車軍団」のフランクリン・J・シャフナー。セティ1世の墓の建築技師メネフタを研究するため、カイロに訪れた女性考古学者エリカ。彼女は古美術商ハムディの店で、メネフタの名が刻印されたセティ1世の黄金像を発見するが、その直後、店が何者かの襲撃を受けてハムディは殺害、黄金像も奪われてしまう。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONQUEST", "征服", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "大女優G・ガルボが、皇帝ナポレオンとの愛に身を焦がすヒロインを演じた歴史ロマン。ナポレオン率いるフランス軍がロシア軍を打ち破り、ポーランドに進撃した。そこでナポレオンはマリーという伯爵夫人に心惹かれる。マリーは彼に反発しながらも、祖国のために我が身を犠牲にすることを決意する。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ORGANIZATION", "夜の大捜査線 霧のストレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "腕利きの黒人警部“バージル・ティッブス”が活躍するヒット・シリーズの第３作。麻薬で人生をゆがめられ、家族を奪われた６人の若者が、工場に隠された麻薬の一部を奪い、自分たちを苦しめた組織に挑戦する。事情を知ったティッブスも、ひそかに彼らを応援する。キャラクター性の高い秀作アクション。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MESSAGE TO LOVE／ISLE OF MUSIC FESTIVAL 1970", "ワイト島 1970 ～輝かしきロックの残像～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1970年８月28～30日の３日間、イギリス・ワイト島に60万人を集めて行われたロック史上の伝説となっているコンサートの様子を記録したドキュメンタリー映画。出演は、直後の９月18日に死亡したジミ・ヘンドリックス、ドラッグの影響から太り、ヒゲも伸び放題のジム・モリソン率いるザ・ドアーズ、これがデビュー・パフォーマンスとなるエマーソン・レイク＆パーマー、そして、チック・コリア、キース・ジャレット、自ら最強と呼ぶバンドを従えたジャズの帝王M・デイヴィスなど。死亡したり、解散した者も多く、また1960年代ロックシーンの熱狂を知るためにも貴重な作品である。", "1995-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ATTENTAT", "フランスの陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "中近東のある国では軍人が政権を握っていたが、左派のリーダーの存在が目の上のコブ。軍事政権の安定化のためフランスやアメリカの右派グループはリーダーを暗殺すべく、その旧友を操って罠にかける……。目的のためには手段を選ばない非情な政治の世界を豪華キャストで描いたサスペンス。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("百變星君／SIXTY　MILLION　DOLLAR　MAN", "ミラクルMASKマン　恋の大変身", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "香港の喜劇スター、C・シンチーの主演によるナンセンス・コメディ。ハワイに留学中、日本人ヤクザのボスの情婦をナンパしたことから惨殺された若者シン。怪しげな生物学教授の手術を受けた彼は、変身能力を持つサイボーグに生まれ変わり……。「パルプ・フィクション」をはじめとするパロディーのオンパレードも楽しいが、なぜか電化製品にばかり変身する主人公のオバカっぷりは文句なしに爆笑モノ。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEMESIS", "ネメシス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "人工臓器の発達でサイボーグ化した人間たちの暴力がはびこる近未来。ロス警察のアレックスも捜査中のケガからサイボーグとなってしまう。失意のうちに密輸組織の一員にまで身を落とした彼は、やがて壮絶な抗争に巻き込まれていくが……。機械と人間の狭間で苦悩する男の死闘を描いたSFバイオレンス。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("皆月", "皆月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "吉川英治文学新人賞に輝いた花村萬月の同名小説を映画化した恋愛劇。「鬼火」などで知られるアウトロー映画の名手、望月六郎監督が、ミジメな現実から這い上がる男女の姿を乾いたタッチで綴る。凄まじいセックス・シーンに吉本多香美が挑戦している。“みんな月でした”という謎めいた置き手紙を残し、全財産を持って家出した人妻、沙夜子。取り残された設計士の夫・諏訪は、コンピュータいじりだけが得意な冴えない中年男だった。なぜか自分の世話を焼いてくれるヤクザ者の義弟から、ソープ嬢・由美をあてがわれた諏訪は、彼女を愛するようになり、すべてを清算するため北陸の沙夜子の元へと旅立つ。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Girl with the Dragon Tattoo", "ドラゴン・タトゥーの女", "", "", "http://image.eiga.k-img.com/images/movie/56065/315x210.jpg?2786", 158, "スティーグ・ラーソンの世界的ベストセラーを映画化したスウェーデン映画「ミレニアム　ドラゴン・タトゥーの女」（2009）を、「セブン」「ソーシャル・ネットワーク」のデビッド・フィンチャー監督がハリウッドリメイクしたミステリーサスペンス。経済誌「ミレニアム」の発行責任者で経済ジャーナリストのミカエルは、資産家のヘンリック・バンゲルから40年前に起こった少女ハリエットの失踪事件の真相追究を依頼される。ミカエルは、背中にドラゴンのタトゥをした天才ハッカーのリスベットとともに捜査を進めていくが、その中でバンゲル家に隠された闇に迫っていく。主演はダニエル・クレイグと「ソーシャル・ネットワーク」のルーニー・マーラ。", "2012-02-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トイレット", "トイレット", "", "", "http://image.eiga.k-img.com/images/movie/55335/315x210.jpg?3671", 109, "「かもめ食堂」「めがね」など独特の作風で知られる荻上直子監督が、全編カナダ・トロントで撮影した家族ドラマ。プラモデルオタクのレイ、引きこもりピアニストのモーリー、エアギターを生きがいとする女子大生リサの3兄妹は、お互いに干渉せず、それぞれマイペースに暮らしていた。しかし、母親の死をきっかけに家にやってきたナゾの祖母“ばーちゃん”との交流により、家族のきずなを取り戻していく。荻上作品の常連であるもたいまさこが、不思議な“ばーちゃん”を演じる。", "2010-08-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水兵さん", "水兵さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "海軍に志願した息子と父の心の絆を通して、立派な海軍兵が誕生するまでを描いたヒューマンドラマ。いわゆる戦意昂揚映画の１本だが、東宝などと比べてアクションもののノウハウを持ってなかった松竹では、こうした市井の生活や人情を通して戦争賛美を訴えることで、かろうじて軍部の意向に沿い、戦時中も会社を存続させえたのであった。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花月佳期", "トワイライト・ランデヴー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ヒットメーカー、ツイ・ハーク監督が広東オペラにヒントを得たファンタスティック・ラブ・コメディー。夜の町で偶然であったヤンとコン。ささいな誤解からお互いの印象は最悪だったが、次に出会った時は、なんとコンは幽霊になって現われた。非業の死を遂げたコンは、一緒に過去に行き自分の命を取り戻して欲しいと頼むが……。時空を超えて愛し合う運命に陥った恋人たちの姿を切なく描くタイムスリップ・ロマンス。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人魚伝説", "人魚伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "池田敏春がにっかつを離れて撮った初の作品で、同名劇画の映画化。物語は、原発を誘致しようとする一派の策略によって、漁師の夫を殺された妻が、復讐していくというもの。この映画はそのストーリーよりも、白都真理扮する妻・みぎわが、復讐のために次々と男たちを殺していくバイオレンス・シーンで得られるカタルシス、そしてリアリティーを取り払った、映画ならではの映像的な飛翔などに見どころがある。白都真理の熱演は特筆ものでオール・ヌードも辞さず体当たりで演技を披露し、ヤクザに抱かれながら、ナイフで刺し殺し、全身に返り血を浴びる時の凄絶なる美しさはとくに印象的。ラストの、みぎわが海とたわむれる美しい水中撮影も見もの。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INSTITUTE BENJAMENTA", "ベンヤメンタ学院", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "実験的な人形アニメーション「ストリート・オブ・クロコダイル」で、世界的に絶大な人気を誇るクエイ兄弟の第１回長編作品。迷宮のような回廊がある学園を舞台に、実写とミニチュアを融合した幻想的な映像で、養成学校の不思議な日常を描く。執事の養成学校ベンヤメンタ学院。荒れ果てたこの学院に入学したヤーコプは、一つのレッスンを際限なく繰り返していくうちに、謎めいた経営者ベンヤメンタ氏とその妹である教師リーザの隠された秘密を暴き出そうと企てる。やがてリーザはヤーコプを秘密の部屋に招き入れるが……。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVERBOY", "愛の宅配 ピザ・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ピザ屋でバイトを始めたランディは、ひょんなことで知り合ったアレックスという女性に誘われるままにベッド・インする。彼女は彼の優しさに感激し、欲求不満の富豪夫人たちにピザ屋の電話番号を教えてしまう。調子にノった彼は宅配ジゴロを開業して……。恋人にフラれた青年のひと夏の経験を描く青春ラブ・コメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Life and Times of Judge Roy Bean", "ロイ・ビーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "　19世紀末、ならず者ロイ・ビーンがテキサスの町に流れ着く。彼は酒場を法廷に改造し、判事として君臨することに。女優リリー嬢への憧れに胸を躍らせる一方、悪党や殺し屋を容赦なく裁いていくビーン。そんな彼の前に弁護士を名乗る男が現れ、次第に様相が変わり始める。やがてビーンは父親になることに……。実在した名物判事ロイ・ビーンの生き様を、ユーモラスかつノスタルジックに描いた異色ウェスタン。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAVEN", "ザ・コマンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1970年代に圧倒的な人気を誇った元祖タフ・ガイ、B・レイノルズが凄腕のダーティー・ヒーローに扮したB級アクション。超高性能の暗号解読機をめぐって、元傭兵の二人の男がスリリングな対決を繰り広げる。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プロゴルファー織部金次郎５　愛しのロストボール", "プロゴルファー織部金次郎５　愛しのロストボール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "シリーズ初の海外ロケを敢行した人情喜劇の第５作。織金とツアーをともにしたマレーシア人選手が桜子に一目惚れ。この選手は、本国でも指折りの大富豪の御曹司。その強引さに負け、桜子は異国へと旅立ってしまった。折りしも、アジア・ツアーへの出場を決めた織金は、勝利と愛のためにマレーシアのグリーンに立つ。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OLD SALMON 海を見つめて　過ぎた時間", "OLD SALMON 海を見つめて　過ぎた時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 29, "鈴木清順、熊川哲といった名匠たちの作品を支えてきた美術監督・木村威夫が、独自の感性と美学で撮りあげた音楽映画。劇中にセリフは一切使用せず、木村監督自らが作詞した音楽や美しい映像によって物語が展開していく。日本を代表するシャンソン歌手・深緑夏代が圧倒的な存在感を見せるほか、タンゴ界で注目を集めるバイオリニスト・喜多直毅、ジャズボーカリストのMooneyら、個性豊かなミュージシャンたちが出演している。", "2006-06-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOTHER’S BOY", "マザーズボーイ 危険な再会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "愛する夫と子供たちを置き去りにしたまま家出をしたジュード。３年後に突然、戻って来た彼女に、再婚を決意した夫の態度は冷たい。怒った彼女は長男のケスを利用して、新たな家庭の幸福を破壊しようとする……。J・L・カーティスが妖艶な悪女を熱演するサイコ・サスペンス。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("後備甜心", "パティシエの恋", "", "", "http://image.eiga.k-img.com/images/movie/53945/315x210.jpg?3102", 94, "香港のイタリアンレストランを舞台に織り成されるロマンティック・コメディ。イタリアンレストランを営むジルは、プレイボーイの彼氏チーオンにとって1番の存在になることを夢見ている。そんな彼女の店で働くことになったシェフのジャックもまた、2人の男の間で煮え切らない態度を続ける恋人に悩まされていた。出演は「ディバージェンス／運命の交差点」のイーキン・チェン、「カルマ」のカリーナ・ラムほか。", "2008-10-18", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NNIGHTHAWKS", "ナイトホークス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "国際的テロリストと彼を追うニューヨーク市警のおとり捜査員たち。その息詰まるような対決を、ニューヨーク、ロンドン、パリなどを舞台に描いたアクション大作。スタローンの精悍な活躍ぶりと11週間にわたる各地長期ロケの効果が見もの。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MULTIPLICITY", "クローンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "クローン製造という世界が関心を抱く科学技術を風刺した爆笑コメディ。仕事と家庭のトラブルで妻子への家族サービスもままならない中年男ダグが、偶然知り合った博士のクローン実験に協力し、もう一体の自分を手に入れる。それでも忙しさが解消されないダグは、すかさず二つめのクローンを作ってもらうが、やがてクローンたちが勝手に３つめのクローンを作ってしまい、大混乱に陥ってしまう！　M・キートンが“一人４役”を演じて大奮闘。４人のキートンを同一画面に収めた視覚効果を、大御所リチャード・エドランドが担当している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thief", "ザ・クラッカー／真夜中のアウトロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "一匹狼のすご腕金庫破りが結婚して足を洗おうとするが、彼を利用しようとする犯罪組織のボスは次々と卑劣な罠を仕掛けて来る……。スタイリッシュな映像が魅力の犯罪映画。マイケル・マン監督の劇場長編映画デビュー作。製作はジェリー・ブラッカイマー。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOOD MORNING，MISS DOVE", "美わしき思い出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ニューイングランドの小さな町で、36年の長きにわたって教鞭を執っているダヴ先生。この町の若い人たちは皆、彼女の教え子だ。ある日彼女は突如背中に痛みを感じ、やがて右足が麻痺していることに気付く。先生は、かつて自分の金時計を無断で分解し、それを元通りに組み立てた教え子のベイカーに手術を依頼する……。小都市の学童教育に半生を捧げた女性教師の姿を描く心温まるドラマ。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("舶来仁義・カポネの舎弟", "舶来仁義・カポネの舎弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "アル・カポネのもとで修行を積んだ日系二世の殺し屋・カポネ栗山は、フランクとジョオの二人の子分を引き連れて帰国し、日本古来の任侠道を守るために国際秘密結社や日本ギャング団と闘う。ヤクザ映画とギャング映画が出会って化学反応を起こしたような娯楽作品。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("再会", "再会", "", "", "http://ecx.images-amazon.com/images/I/51aawo1lftL._SL210_.jpg", 72, "青春と性をテーマにした「青春Hシリーズ」第10作。教授ともめたことが原因で大学の研究職を辞した陽一は、予備校の臨時講師をして暮らしている。30歳を過ぎ、長年つきあっている恋人からも結婚を迫られ、今後の身の振り方で悩んでいた陽一はある日、高校時代に思いを寄せていた月夜と再会し、当時の恋心が再燃する。月夜は高校時代の若さを保ったままだったが、その秘密とは……。", "2011-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新スパイガール大作戦　惑星からの侵略者", "新スパイガール大作戦　惑星からの侵略者", "", "", "http://image.eiga.k-img.com/images/movie/53725/315x210.jpg?4522", 73, "人気グラビアアイドルたちが女スパイに扮するセクシーアクションのシリーズ第5弾。UFOが頻繁に目撃されていた真鶴半島で殺人事件が発生した。遺体がミイラ化していたことから3人のスパイガールが捜査を任されるが、現場近くにある宇宙科学研究所の所長・清原を叔父に持つ005は単独捜査を開始。しかし、清原は謎の失踪を遂げてしまう。やがて005の前に、ゴールドの光に覆われた女・ゴールドレディが現われ……。", "2008-05-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WESTERN", "ニノの空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "M・ポワリエ監督が無名の俳優たちを起用して撮ったロード・ムービー。B・サンドヴァルのリズミカルなギター音楽が、心の奥底で愛を求める男たちの友情劇を盛り立てる。カタロニア系スペイン人のパコが、靴のセールス中に拾ったヒッチハイカーに車を盗まれたあげく、会社をクビになった。その恨みの相手、自称イタリア産ロシア人のニノと再会したパコは彼を殴り飛ばして病院送りに。ところが、不思議と意気投合した二人はブルターニュ地方を気ままに旅していく。ビデオタイトルは「幸せさがしの旅日記　ニノの空」。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマンコスモス２／THE BLUE PLANET", "ウルトラマンコスモス２／THE BLUE PLANET", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "テレビ放映された『ウルトラマンコスモス』のその後の世界を描いた作品。チームEYESの隊員だった主人公の春野ムサシはアストロノーツ（宇宙飛行士）となっていた。宇宙のかなた、惑星ジュランに向かった彼は、何者かの手によって作られた怪獣兵器スコーピスに襲撃されるが、天空から降臨した“光”に救われる。地球に帰還したムサシは幼なじみの結婚式に出席するためサイパンを訪れ、不思議な少女シャウに出会う。そんな時、スコーピスが飛来。シャウは必死に立ち向かっていく…。杉浦太陽の登場しない「…ムサシ（13才）少年編」も公開された。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER SOMMER DES SAMURAI", "ベルリン忠臣蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "主君の仇を討つべく、見事討ち入りを果たした赤穂浪士の忠義話で知られる「忠臣蔵」。お話はその主人公なる大石内蔵助をはじめとする四十七士が、現代のベルリンによみがえり、ドイツ政界に君臨する黒幕相手に大騒動を繰り広げるというもの。", "1985-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LETHAL JUSTICE", "リーサル・ジャスティス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ある静かな町に３人の惨殺犯が侵入した。一人は銃撃戦で即死し、一人は降伏、残る一人は逃亡した。だが、逃亡中の犯人は降伏した仲間が、実は警察の独自の方法によって死の制裁を受けたことを知る。表向きには犯罪のない町は警察の暴力によって支配されていたのだ。逃亡中の男は、この事実をジャーナリストに暴露することを思いつくのだが……。警察・犯人・記者の対決を追うバイオレンスもの。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CYBERJACK", "サイバージャック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "武装したテロ集団“サイバー・ジャックス”は、新しいコンピュータ・ウイルス撃退ウイルスを手に入れ、世界を征服するために研究所を占拠する。リーダーのナッシムは自分の体内にウイルスを採り込み、あらゆるインターネットをハッキングしていく。そんな彼らに、偶然研究所に居合わせた元警官ニックが戦いを挑む。もしも、さらにハイテク化した未来で、テロリストに全世界のコンピュータをジャックされたら——という恐怖を、最新のSFXを駆使して映画化したハードアクション・ストーリー。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD JAKE BLOOD SALVAGE", "マッド・ジェイク　処刑の診療台", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "あのマイク・タイソンに“耳をかじられた男”E・ホリフィールドが、最強のボクサーとして出演しているショッキング・ホラー。罪なき人々を襲い、狂気の大手術を施す殺人鬼ジェイク。ある日、死んだ妻と瓜二つの美少女エイプリルとその家族が、彼に誘われるままにさびれた館を訪れるのだが……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACKSMITH", "キートンの鍛治屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "人より馬の言う(？)ことをきく鍛治屋キートン。彼が新式の鞍を発明したり（バネがついていて“揺れない”けれども落馬しやすい欠点あり)、口うるさい親方や、気に入らないお客にはすかさずひと泡吹かせ、あげく彼らに追われて逃げ回る騒動。なじみのスタッフ＆キャストだけに、ギャグの切れ味が鋭いサイレント映画。", "1922-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒猫２刺殺葉利欽／BLACK CAT2", "レディ・ソルジャー エリツィン暗殺指令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "エリツィンの命を守るため、暗殺秘密組織との戦いに立ち向かう、女サイボーグ戦士の物語。香港映画ではお決まりの“ソックリさん”（もちろんエリツィンの）も登場。サイボーグ同士の恋あり、スリルとアクション、爆破シーンの派手さなど単純なストーリーにデコレーションいっぱいの香港アクション。「黒い女豹」の続編で、基本設定は香港版「ニキータ」。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女ざむらい只今参上", "女ざむらい只今参上", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "美空ひばりが、男装の美剣士に扮した娯楽時代劇。時は幕末。江戸の商人、小田屋の娘・お春は、美人だが大の剣術好き。行く末を案じた父親は、知り合いの桂小五郎に頼み、お春を京の禁裏へ奉公させることにする。ところがお春は、京都への密書を持ち、春之輔と名前を変えて若武者姿で危険な道中に出てしまい……。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE PALAC", "ぼくの美しい人だから", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "妻を亡くしたエリート・ビジネスマンと、ハンバーガー・ショップで働く中年の女。年齢も、育ちも、教養もすべて不釣り合いな二人の恋は一夜かぎりのはずだった。だが翌日、男は女の部屋のドアを叩く。どうしても彼女が欲しかったから……。全米ベストセラーを映画化した大人のラブ・ストーリー。結ばれるはずのない男女が、純粋な愛を育んでいく過程をきめ細やかに綴っている。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東無宿", "関東無宿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "映像派・鈴木清順と美術・木村威夫が生んだ様式美あふれる任侠映画。平林たい子の原作『地底の歌』はすでに1956年に野口博志監督の手によって映画化されているので、これが２度目となった。今回の映画化では、小林旭に眉を太く描かせるなどして歌舞伎の様式を取り入れ、主人公の情念の象徴である赤を中心にした色彩設計がなされている。特に、クライマックスで主人公が敵方の賭場で相手を斬った瞬間、背後の襖がスローモーションで倒れていき、真っ赤なホリゾントが画面一面に映るシーンは、まさに清順＝木村の真骨頂だ。伊豆組の幹部・鶴田は、吉田組に押されている組のことで親分と意見が対立。ある日、鶴田は賭場で出会った女が有名なイカサマ博奕打ちの女であることを知った。しかし、そうとは知りつつも鶴田はその女に惹かれていく。そして、その女の亭主であるイカサマ博奕打ちと勝負がしたいと思うのだった……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mission to Mars", "ミッション・トゥ・マーズ", "", "", "http://image.eiga.k-img.com/images/movie/1835/315x210.jpg?4379", 114, "", "2000-05-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENSIGN PULVER", "ミスター・パルバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "南太平洋の大海原を舞台にしたコメディ。第二次大戦も終わりに近づいた南太平洋の海原をいく一艘の貨物船。気難し屋で、船員たちから煙たがられている船長と、貨物船に乗り合わせていた海軍のパルバー少尉の二人は、偶然にもボートで小さな島に流されてしまう……。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狼の生活", "狼の生活", "", "", "http://image.eiga.k-img.com/images/movie/57845/315x210.jpg?1241", 90, "青春と性をテーマに監督たちが独自の個性で自由に製作する「青春H2（セカンド）」シリーズの10作目。ぴあフィルムフェスティバル出身で長編デビュー作「不灯港」が高い評価を受けた内藤隆嗣監督が描くロードムービー。勤めていたゲームセンターが閉店してしまった二郎は、バイクを走らせ東北へと向かう。東日本大震災の生々しい傷跡を目にした二郎は、行くあてのない旅の中でさまざまな人と出会う。しかし、かつての恋人すみれのことが忘れられず、すみれに会いに行くが……。", "2012-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COVEK NIJE TICA，LJUBAVNI FILM", "人間は鳥ではない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "大胆な性描写、過激な政治批判、アバンギャルドな手法などで既成の映画概念を覆したユーゴスラビアの鬼才マカヴェイエフのデビュー作。ある機械技師とその部下の生活を描きながら、官僚体制を批判した内容で、ゴダールやエイゼンシュテインに影響を受けた作風はカンヌ映画祭でも話題になった。", "1965-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新古惑仔之少年激闘篇／YOUNG＆DANGEROUS，THE PREQUEL", "硝子のジェネレーション・　香港少年激闘団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "香港で大ヒットした「欲望の街・古惑仔」シリーズの前日談的な青春映画。行き場を失った少年のやるせなさを、N・ツェーがナイーブに体現している。高校を退学させられたナンは３人の仲間とともに、暴力団幹部の舎弟に。兄貴分の男らしさに心を動かされた彼らは抗争や悪事に明け暮れ、後戻りのできない道に足を踏み入れていく。", "1998-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Garfield : A Tail of Two Kitties", "ガーフィールド2", "", "", "http://image.eiga.k-img.com/images/movie/33685/315x210.jpg?4676", 78, "世界中で愛され続ける人気キャラクター、ネコのガーフィールドの活躍を描くCGアニメの第2弾。飼い主ジョン・アーバックルと彼の恋人リズ、相棒犬オーディと共にイギリスへやって来たガーフィールド。お城を相続した王族ネコと入れ替わってしまったガーフィールドは、執事や召使に囲まれた優雅な暮らしを手に入れるが……。前作に続き、ガーフィールドの声を「ロスト・イン・トランスレーション」のビル・マーレイが担当している。", "2006-10-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ОКТЯБРЬ", "十月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "モンタージュ理論で知られるS・エイゼンシュテインが、ソビエト革命10周年記念作品として製作したもの。1917年２月から10月にわたるソビエト革命の激動を、その中心人物レーニンの姿を中心に、より先鋭化されたモンタージュ技法で構成している。1968年にサウンド版が製作された。", "1928-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("掘るまいか", "掘るまいか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "本作は、大地震によって被害を受けた新潟県山古志村にある長さ約900mの手堀りトンネルのできるまでを追ったドキュメンタリーだ。ツルハシだけで掘り続けた農民たちの16年の軌跡からは、かつての農村に広がる素晴らしい自治の力を感じることができる。豪雪の山村、新潟県山古志村。そこには、ツルハシ一つで16年の歳月をかけてトンネル掘りに立ち向かった村民たちがいた。", "2004-12-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREEDOM STRIKE", "フリーダム・ストライク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "米空海軍全面協力の下に作られたミリタリー・アクション。湾岸戦争から数年後、アメリカ大統領とシリア大統領の和平条約調印式が行われようとしていたが、潜伏していたシリア人テロ組織が発砲、シリア大統領がケガを負う。シリアは情報操作を行い、この事件の責任はアメリカにあると非難。第三次世界大戦勃発の危機が訪れる。国連政府軍が誇る特別急襲部隊フリーダム・ストライクは、シリアの核攻撃を阻止するため出動するのだった……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チェケラッチョ!!", "チェケラッチョ!!", "", "", "http://ecx.images-amazon.com/images/I/51BXZ3PH7ML._SL210_.jpg", 117, "沖縄を舞台に、ヒップホップバンドを組んだ高校生たちの恋や友情を爽やかに描いた青春ストーリー。沖縄で生まれ育った高校生の透、暁、哲雄、そして透に恋心を抱く唯は、ある日、人気バンド「ワーカホリック」のライブを見て感激し、自分たちもバンドを結成。楽器経験もないのに、次回のワーカホリックのライブに前座として出演することになってしまう。主人公・透役に、「偶然にも最悪な少年」の市原隼人。", "2006-04-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四谷怪談・前篇／後篇", "四谷怪談・前篇／後篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 158, "木下監督は、鶴屋南北の原作にかなり手を加え、お岩の亡霊は伊右衛門の良心の呵責による妄想だという新解釈を与えている。また、この作品を日本の伝統的な絵巻物という視点で捉え、全編を俯瞰撮影の長回しで撮った異色作品。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢魔", "夢魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ゆうばり映画祭のグランプリに輝いたビデオ作品「魔王街」の廣木隆一による、官能的なサイコ・ドラマ。少年期のエロティックなトラウマを抱えるバス運転手と、それと同じ出来事を夢の中で見た奔放なOL。バスの中で偶然、出会った彼らが甘美な夢を実現に移していくなかで、やがて現実との境界があいまいになっていく様を幻想を織り交ぜて耽美的に演出する。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ON NE MEURT QUE ２ FOIS", "トレンチコートの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "冬の深い雪の朝。駅に捨てられていた中年のピアニストの死体。彼のアパートに残された一本のテープ。ピアニスト殺人事件を追うパリ市警のスタニランド刑事の前に現れる意外な事件の素顔。殺人事件を発端に、男と女の愛のからくりを描いたミステリー・ロマン。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秋天的童話／AN AUTUMN’S TALE", "誰かがあなたを愛してる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "演劇の勉強のために香港からニューヨークへとやって来たジェニファーと、チャイナタウンでやくざな暮らしを送っていた遠縁の男サンパンの出会いと別れをホロ苦く描いたラブ・ストーリー。女流監督らしい細やかな感性と、香港人の目で見たニューヨークの街の生き生きとした様子が観る者の共感を呼ぶ。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Mexican", "ザ・メキシカン", "", "", "http://image.eiga.k-img.com/images/movie/1535/315x210.jpg?4320", 123, "最近ツキに見放されている運び屋のジェリーは、組織の命を受け、世にも美しいアンティークの拳銃“メキシカン”を受け取るためにメキシコに旅立った。その拳銃には、「恋人達の仲を永遠に引き裂く」という呪われた伝説が語り継がれているという。しかし彼は現地でドジを踏み銃を奪われてしまう。ジェリーが裏切ったと睨んだ組織は、恋人サマンサを人質に取り、さらに追手をメキシコへと差し向けるが、銃を狙っているのは実は彼らだけではなかった……。", "2001-04-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録・連合赤軍　あさま山荘への道程（みち）", "実録・連合赤軍　あさま山荘への道程（みち）", "", "", "http://image.eiga.k-img.com/images/movie/53445/315x210.jpg?2193", 190, "1972年2月、連合赤軍の5人の若者たちが軽井沢のあさま山荘に10日間に渡って立てこもり、警察と銃撃戦を繰り広げた\"あさま山荘事件\"。日本中に衝撃を与えた、あの事件に至るまでの連合赤軍崩壊の過程を鬼才・若松孝ニ監督がドキュメンタリータッチで描いた群像劇。昨年の第20回東京国際映画祭では「日本映画・ある視点」部門の作品賞を受賞。また、08年ベルリン国際映画祭では最優秀アジア映画賞と国際芸術映画評論連盟賞のダブル受賞を果たした。", "2008-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOING TIME ON MAPLED DRIVE", "ジム・キャリーinロングウェイ・ホーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "カーター家の末っ子マットが婚約者を連れて来ることになり、結婚したカレンも夫とともに帰郷。一見、平和な家族だが、完璧主義者の父は、大学を中退したアル中気味の長男ティムや貧乏な芸術写真家と結婚したカレンに失望しており、それぞれの間には深い亀裂があった。そんな父の期待を一身に受けているのがイエール大学に進んだマットだったが、そのマットには誰にも話せない秘密があった……。家族の対立を淡々としたタッチで綴った人間ドラマ。J・キャリーが長男ティムに扮し、シリアスな芝居を見せている。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GATOR", "ゲイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "B・レイノルズが主演した1973年の「白熱」の続編。彼は、この作品で初めて監督も務め、スタントマン抜きの撮影をこなした。またアメリカ南部で不正の限りを尽くす悪徳ボスに単身挑む主人公ゲイターを、小気味よく演じている。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忠烈図", "忠烈図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "明の時代、日本の海賊「倭冦」が中国南部の沿岸を荒らしまわっていた。明皇帝の勅命を受けた李将軍は腕利きの７人を配下に選び、圧倒的多数の倭寇に闘いを挑む。香港版「7人の侍」の異名をとる、剣劇アクションの代表巨編。ラストの林のなかでの死闘、海岸におけるすさまじい斬り合いはキン・フー活劇の真骨頂といえる。武術指導のサモ・ハンが日本人サムライ役でも出演している。", "1975-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAMILY", "FAMILY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "横浜のサービス業界を牛耳る三輪は、裏では日本の闇経済を仕切る秘密結社“ジャパン・マフィア”の幹部でもあった。ある日、ヤクザ組織の総長が殺害され、その犯人が三輪の弟であることが発覚するが……。真樹日佐夫原作の人気コミックを、鬼才・三池崇史監督のメガホンでエネルギッシュに映画化した極道アクション映画。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("のれん一代 女侠", "のれん一代 女侠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "木屋源の一人娘・美彌は、父に代わって店を切り盛りしていた。彼女は番頭の浅吉と恋仲であったが、身分の違いから交際を父に反対され、浅吉は東京を去っていく。一方、美彌は腹違いの弟に店を継がせようとするが、そこで問題が発生する……。美空ひばりが主演したキリリとした女性映画。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("矢沢永吉　RUN&RUN", "矢沢永吉　RUN&RUN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "キャロル解散後、ソロ・デビューしたロック・ミュージシャン、永ちゃんこと矢沢永吉の姿を捉えた貴重なロック・ドキュメンタリー。1979年の名古屋球場での白熱のコンサートの模様をメインに、アメリカ・ロケでの永ちゃんの様子、オフ・タイムに自宅でくつろぐショットなどで構成されている。当時のYAZAWAの心境とメッセージが詰まったファン必見の一本。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHERE ANGELS FEAR TO TREAD", "天使も許さぬ恋ゆえに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「モーリス」「眺めのいい部屋」「ハワーズ・エンド」の原作者E・M・フォースターが、1905年に書いた処女作の映画化。イギリス上流階級の女性とイタリア人青年との激しくもはかない恋を描いたロマンティック・ラブ・ストーリー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUR OF THE GUNS", "墓石と決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "西部劇に幾度となく採り上げられてきた、OK牧場の決闘。これに関係した７人の男たちのその後を描いた後日譚。生き残ったアープ兄弟とクラントン一味との対決を、ドク・ホリデイの活躍も絡め、見せ場たっぷりに描いている。史実を徹底研究した物語展開、西部劇の名匠J・スタージェス入魂の作品となった。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Beautiful Laundrette", "マイ・ビューティフル・ランドレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "パキスタン人青年とイギリス人青年の間に芽生えた男同士の愛と友情を描くヒューマンドラマ。当初はイギリスの民放局チャンネル4の為に製作された作品だが、劇場公開されて話題を呼び、アカデミー脚本賞にノミネートされるなど国際的にも高い評価を得た。ロンドンのアパートで父と暮らすパキスタン人オマール。彼は町で偶然再会した幼なじみのジョニーと共に、実業家の叔父ナセルから任されたコインランドリーの経営を始めるが……。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い影法師", "赤い影法師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "時は徳川幕府、家光が将軍の時代。石田三成の娘、母影と彼女が服部半蔵に犯された時、身ごもった子、若影の忍者母子は仇討ちのため家光の命を狙っていた。やがて彼らは三成の隠し軍資金のありかが記された剣をめぐって半蔵らと攻防を繰り広げることに……。『週刊文春』に掲載された柴田錬三郎の小説の映画化。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誰かさんと誰かさんが全員集合!!", "誰かさんと誰かさんが全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "岩下志麻がマドンナに扮しての一編。碇田長吉の営む大日本無念塾で学ぶ４人の塾生は、反抗すると“忍耐・服従・献身”の塾三カ条を唱える長吉に竹刀でしごかれる。そこに結城令子という美女が現れ、長吉たちはたまらず熱をあげて……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUN，　ANGEL，　RUN！", "明日なき野郎ども", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「悪魔の追跡」のJ・スターレット監督が、ヒッピーや暴走族の生態をリアルに描いたバイク・アクション。１万ドルの報酬ほしさにヘルス・エンジェルスの仲間を裏切ったアンジェロ。恋人ローリーとともに逃走した彼は、元レーサーの助けを得て身を隠すが、ボスのロン率いる追っ手がじわじわと迫ってくる。主演はバイカー映画の常連俳優ウィリアム・スミス。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OVER THE LINE", "檻の中の情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "才色兼備の大学教授と刑務所の囚人との禁断の恋を描くエロティック・サスペンス。エレーンは刑務所の受刑者たちに、英文学の指導をする大学教授。彼女はひそかに、囚人の一人、ジョーに思いを寄せていた。ある日、ジョーに誘われ、自分に対する欲望を告げられたエレーンは、危険と知りつつジョーとの情事にはまっていく。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・逆転旅行", "喜劇・逆転旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "長谷川吾一は31歳独身の国鉄マン。恋するかおりにプロポーズしたが、彼女の出した条件は、国鉄をやめて故郷のリンゴ園を継ぐことであった。国鉄を愛する吾一は、迷いに迷って……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN　GOD’S　HANDS", "イン・ゴッズ・ハンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "サーフィンを題材にした、ロマンあふれる青春映画。ビッグ・ウェーブを追って旅をする、３人の男たちの友情と恋、冒険をドキュメント・タッチで描く。実在するトップ・サーファーたちを主演に起用。スタントをいっさい使わずに神業のようなサーフ・シーンを収めた、ダイナミックな映像に目を奪われる。元X−JAPANのヨシキが提供した楽曲の起用も話題に。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA TULIPE NOIRE", "黒いチューリップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "1789年、フランス革命の真っ只中。貴族や役人を襲って財宝を奪う怪盗“黒いチューリップ”が出現。彼が顔に傷を負うと、瓜二つの顔をした弟が代わって義賊として活躍する。名匠C・ジャックが監督、A・ドロンが一人二役を演じた冒険アクション。", "1963-01-01", "2,4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE IN−LAWS", "あきれたあきれた大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "A・アーキンがP・フォークとの共演を熱望し、脚本探しから製作総指揮までを務めた爆笑コメディ。クレイジーなシークレット・エージェントが堅物の歯医者を国際的な陰謀劇に巻き込んでしまう……。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽陽", "ヤンヤン", "", "", "http://image.eiga.k-img.com/images/movie/54975/315x210.jpg?4603", 112, "「シーディンの夏」「一年之初」のチェン・ヨウチェ監督が、台湾の人気女優サンドリーナ・ピナを主演に描く青春映画。陸上部の仲間で良きライバルのヤンヤンとシャオルー。ヤンヤンの母がシャオルーの父でもある陸上部のコーチと再婚したことから2人は義姉妹となるが、シャオルーのボーイフレンドを巡って気まずい状況になってしまう。家を飛び出したヤンヤンは、知人の紹介で芸能界に入ることになり……。", "2009-10-19", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乱暴と待機", "乱暴と待機", "", "", "http://image.eiga.k-img.com/images/movie/55125/315x210.jpg?4725", 97, "「腑抜けども、悲しみの愛を見せろ」の原作者として知られる劇作家・ 本谷有希子の同名戯曲を、浅野忠信主演、「パビリオン山椒魚」の冨永昌敬監督で映画化。互いに好意を持ちながらも、天井裏から覗く、覗かれるという関係しか築けない複雑な男女の愛を、近所に引っ越してきた若夫婦の視点から描くコメディドラマ。共演に美波、小池栄子、山田孝之。", "2010-10-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("盛り場仁義", "盛り場仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "秀雄、温州、オイチョの３人組ヤクザが、昔懐かしの盛り場に帰ってきた。そこは現在新興勢力の誠心会があこぎなやり方で支配を続けており、秀雄たちは“力には力を”とばかりに、誠心会に殴り込みをかけていく。助っ人となる女賭博師を演じる梶芽衣子の妖艶な美しさがドラマに華を添える。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の妻たち 決着〈けじめ〉", "極道の妻たち 決着〈けじめ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "12年続いた大ヒット・シリーズの完結編。大阪南部の３つの組を傘下に持つ井出組。ある日、一人の組長が射殺され、井出組の組長が逮捕された事で、長年にわたり築き上げた４つの組の安定した関係が崩れていく。井出の妻、春日は夫の無実を証明しようとする。やがて、組内部に裏切り者の存在が浮かび上がり……。夫と組のために戦う春日に岩下志麻、その手助けをする綾子にかたせ梨乃とシリーズに欠かせない女優たちが完結編にふさわしい熱演を見せる。他、映画初出演のトミーズ雅や愛川欽也、中条きよしらベテラン勢が脇を固めている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かさぶた姫", "かさぶた姫", "", "", "http://image.eiga.k-img.com/images/movie/54245/315x210.jpg?4114", 112, "「ニュースター・シネマ・コレクション」として、エイベックスが同社の新人を起用し製作した青春映画の第1弾。姫子には“かさぶた剥がしフェチ”という人に知られたくない秘密の嗜好があった。ある日、沙紀とムイと参加した合コンで、ほっけの背骨剥がしを姫子が楽しんでいると、男子グループの光一がその行為に興味を持ち、2人は惹かれあうのだが……。主演は「ブラブラバンバン」の近野成美、「カフェ代官山」の相葉弘樹。", "2009-01-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN TAXI POUR TOBROUK", "地獄の決死隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1942年８月の北アフリカ戦線。フランスの小隊がトブルクにあるドイツの拠点に突撃し敵を壊滅させる。だがフランス側の被害も甚大で、生き延びたのはたった４人だけであった。途中ドイツ軍兵士一名を拾い、彼らは前方に広がる壮大な砂漠を横断するため歩み出す……。戦争の非情さを静かに訴える戦争ドラマの秀作。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("平穏", "平穏", "", "", "http://image.eiga.k-img.com/images/movie/54495/315x210.jpg?3908", 82, "ポーランドの巨匠クシシュトフ・キエシロフスキーが1976年に手掛けた作品。3年の刑期を終えて出所したばかりの青年。小さな町で新たな生活を始めた彼には「女、子供、マイホーム」というささやかな夢があったが、現実は厳しく……。ポーランド政府の検閲によって製作当時は上映が差し止められ、1980年に初めて一般公開された。主演は「アマチュア」「トリコロール／白の愛」など多くのキエシロフスキー作品に出演するイェジ・シュトゥル。", "2009-06-22", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄腕巨人", "鉄腕巨人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "人気プロレスラー、力道山が映画初出演した珍品。老博士の発明を盗もうとする悪漢をやっつけるため、力道山と少年が力を合わせて大活躍する。都会へ出てきた力道山が前世紀の怪獣と間違えられて、警察に追われるエピソードがおかしい。レスラーの遠藤幸吉、駿河海らも出演している。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEFORE AND AFTER", "判決前夜　ビフォア・アンド・アフター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "殺人事件の容疑者となった息子の無実を証明しようとする両親の姿を通して、家族の葛藤を描く心理サスペンス。マーサという少女が殺された夜、キャロラインとベンの夫婦の家に、警察がやって来る。17歳になる息子のジェイコブに疑いがあるというのだ。捜査令状がまだ出ていなかったため、ベンは警察を追い出す。そして、ベンは、ジェイコブの車の中から血のついたジャッキを発見、血を洗い落としてしまう。やがて、本格的な捜査が始まり、ジェイコブとマーサが付き合っていたことがわかり、ジェイコブは逮捕される。保釈された彼は、両親に対して自分が殺したこと、そして正当防衛であったことを告白するのだった。証拠を隠滅したから有罪にはならないと主張するベンに対して、真実こそがジェイコブを助けると反論するキャロライン。両親の対立が、ジェイコブをますます苦しめていき……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗黒街大通り", "暗黒街大通り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "二大ヤクザ組織の抗争で父を殺された３兄弟は、組の再興と父の仇を求めて狂犬のように暴れまくる。高倉健の無鉄砲だが情にもろい長男ぶりが印象的。この年“日本侠客伝”シリーズがスタートし、“健さん”はスター街道を走り出す。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASSASSINS", "暗殺者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "暗殺のプロの壮絶な戦いをスリリングに描いたアクション大作。リチャード・ラスはどんな標的をも逃がさない超一流の暗殺者。謎の男“ザ・コントラクター”の指示でターゲットの富豪ブランチを殺そうとした時、一瞬早くベインという殺し屋がブランチを射殺してしまう。ラスはタクシーの運転手に扮し逃げるベインを捕まえ、契約を横取りした理由を問い詰めるが、彼はチェスの動きを表す謎の言葉を残して消えてしまう……。A・バンデラスがS・スタローンと互角に渡り合う熱演を見せている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Leonera", "檻の中", "", "", "http://image.eiga.k-img.com/images/movie/54795/315x210.jpg?4346", 113, "アルゼンチンの注目若手監督パブロ・トラペーロがメガホンを取り、刑務所内での出産・育児に挑む女性の苦悩を描いたヒューマンドラマ。妊娠中のフリアがある朝目覚めると、ボーイフレンドのラミロが血まみれに、さらにナウエルが死体となって倒れていた。殺人容疑で投獄された彼女は刑務所で息子を出産し、自らの手で育てようとするが……。ラミロ役に「300」「チェ」などの人気ブラジル人俳優ロドリゴ・サントロ。", "2009-09-20", "22,5,17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kongen av Bastoy", "孤島の王", "", "", "http://image.eiga.k-img.com/images/movie/57035/315x210.jpg?2240", 117, "かつて問題児たちの更正施設が建てられていたノルウェーのバストイ島を舞台に、少年たちの脱走劇を事実にもとづき描いたサスペンス。1915年、外界と隔絶されたバストイ島にエーリングという非行少年が送り込まれてくる。島の矯正院では過酷な重労働や教育者たちによる性的虐待が行われており、反発するエーリングの態度が抑圧された少年たちの心を突き動かし、反乱の引き金となる。尊大な王のごとく振る舞う院長にスウェーデンの名優ステラン・スカルスガルドが扮する。", "2012-04-28", "36,2,14,42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Die Another Day", "007 ダイ・アナザー・デイ", "", "", "http://image.eiga.k-img.com/images/movie/1855/315x210.jpg?4241", 133, "", "2003-03-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("See How She Runs", "マイ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "シェイプ・アップのためにジョギングを始めた、二人の娘を持つ中年の女教師。そんな彼女が家庭や教え子たちの愛に支えられ、ボストン・マラソンに参加して完走を果たすまでを、さわやかな感動を盛り込んで描いていく。J・ウッドワードがヒロインを熱演。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・兄弟仁義", "新・兄弟仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "中野一家の親分、頼吉は跡目を息子の頼介ではなく、大竜こと竜太郎に継がせることにする。この決定を快く思わぬ頼介は代貸らと組んで、大竜と彼の兄弟分、中竜と小竜を仲たがいさせようと計画。この罠にはまった中竜が殺され、残った大竜と小竜は仇討ちのため頼介らに戦いを挑む。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄に真紅な花が咲く", "地獄に真紅な花が咲く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "拳銃の名手とトランプを操る賭博師、この瓜二つの顔を持つ男二人が、巨大な陰謀に挑んでいくさまを描いたギャング・アクション。鶴田浩二が巧みにそれぞれのキャラクターを使い分けて、一人二役をさっそうと演じている。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神探父子兵／IN THE BLOOD", "香港極道 野獣刑事〈デカ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "香港警察総監の息子で、特捜班のエリートと、彼の親友で、念願かなって特捜班入りとなった男の、友情と戦いの日々を描くポリスアクション。麻薬組織壊滅に情熱を燃やす二人の活躍が綴られる。アンディ・ラウがキレのあるアクション演技を披露。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Surf's Up", "サーフズ・アップ", "", "", "http://image.eiga.k-img.com/images/movie/1925/315x210.jpg?2046", 85, "周囲からの称賛と尊敬に憧れる新人サーファーのコディは、“サーフィン・ワールドカップ”で優勝すれば、夢が叶うと信じていたが、老いぼれサーファーのギークと出会い、試合で勝った者だけが必ずしもチャンピオンとは限らないということに気付く。競争激しいペンギンのサーフィン界で奮闘する1匹のイワトビペンギンの姿を、ドキュメンタリー風の演出で描いた長編CGアニメ。声の出演は「トランスフォーマー」のシャイア・ラブーフやジェフ・ブリッジスら。", "2007-12-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　THREE　CABALLEROS", "三人の騎士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "南米を舞台にした1942年製作のアニメ「ラテン・アメリカの旅」の続編。ドナルド・ダックが、バースデー・プレゼントに“ブラジル”という本をもらう。早速彼は、ブラジルの友達ホセ・キャリオカと、メキシコの友達パンチートとともに南米の旅へ出発する。陽気な歌と踊りで軽快に描くアニメと実写映像の合成作品。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mon Meilleur Ami", "ぼくの大切なともだち", "", "", "http://image.eiga.k-img.com/images/movie/53455/315x210.jpg?3060", 96, "自分の誕生日パーティで友人たちから「お前の葬式には誰も行かない」と言われてしまった美術商フランソワ。彼らの前に10日以内に親友を連れてくると約束したフランソワは、偶然出会ったタクシー運転手ブリュノの助けを借りて親友探しを始めるが……。フランスの名匠パトリス・ルコント監督が、中年男性の友情をユーモラスに描いた人間ドラマ。主演は「あるいは裏切りという名の犬」のダニエル・オートゥイユと「戦場のアリア」のダニー・ブーン。", "2008-06-14", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STEPFORD WIVES", "ステップフォード・ワイフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「ローズマリーの赤ちゃん」で知られるアイラ・レヴィンの小説を映画化したミステリアス・コメディ。全てがパーフェクトな町に引っ越してきた夫婦を待ち受けるものとは……？　町の秘密を暴こうと奮闘するヒロインをニコール・キッドマンが快活に演じる。TV局をクビになった女性プロデューサーが、夫とともに新しい町での再出発を試みる。ところが、その美しい町にはある秘密が……。", "2005-02-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rosso come il cielo", "ミルコのひかり", "", "", "http://image.eiga.k-img.com/images/movie/34175/315x210.jpg?1526", 100, "「輝ける青春」などを手掛けたイタリア映画界屈指のサウンド・デザイナー、ミルコ・メンカッチの幼少時代の実話を基に、盲目の少年が“音”を通して新しい世界を見つけ出すまでを描いた伝記ドラマ。映画を愛する10歳の少年ミルコは、思いがけない事故で視力を失ってしまう。全寮制の盲学校に転校させられ、周囲に心を閉ざすミルコ。そんな彼に再び希望の光をもたらしたのは、1台の古ぼけたテープレコーダーだった……。", "2007-09-08", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Walkower", "不戦勝", "", "", "http://image.eiga.k-img.com/images/movie/55005/315x210.jpg?4651", 78, "「アンナと過ごした4日間」で東京国際映画祭審査員特別賞を受賞したポーランドの名匠イエジー・スコリモフスキが、1965年に手掛けた長編第2作。前年に発表した長編デビュー作「身分証明書」から6年後の設定で、ボクサーになった主人公アンジェイの試合前の数時間を描く。前作に続きスコリモフスキ自身がアンジェイを演じる。クシシュトフ・キエシロフスキー監督作「傷跡」で主演を務めたフランシツェク・ピエツカが端役で出演。", "2009-10-21", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("火焼島／ISLAND OF FIRE", "炎の大捜査線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "極悪囚の集まる悪名高い監獄、火焼島で出会った４人の男たち。謎の事件が続発する監獄を出るためには、彼らは要人暗殺の狙撃手にならなねばならない。死を前にして彼らは固く団結する……。ジャッキー・チェンをはじめレオン・カーファイ、サモ・ハン・キンポーと豪華キャスト競演のアクション巨編。", "1991-01-01", "6,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トイレの花子さん", "トイレの花子さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "坂本拓也の通う本町小学校の周辺では、小学生の連続殺人事件が頻発。拓也の妹なつみはその犯人を“こっくりさん”で占っていた。“こっくりさん”が告げる犯人は“は・な・こ”。そして、次の被害者はなつみだという。驚いたなつみは拓也に相談するが相手にされない。そんな時、絶世の美少女冴子が転校してくる……。どこからともなく沸き起こり、今や全国的に有名になったトイレに住み着く幽霊“トイレの花子さん”の映画化。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE CRAZY SUMMER", "ワン・クレイジー・サマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "代々バスケットボールの名選手という一家に育ったものの一流になりきれず、マンガ家を志す青年。しゃれたラブ・ストーリーを作るため、まずは本物の恋愛をとバカンスに出かけるが……。おかしな若者たちが大騒動を展開する。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちーちゃんは悠久の向こう", "ちーちゃんは悠久の向こう", "", "", "http://image.eiga.k-img.com/images/movie/53255/315x210.jpg?2120", 94, "注目のライトノベル作家・日日日（あきら）のデビュー作を、アニメ映画「時をかける少女」でヒロインの声を務めた仲里依紗と「バッテリー」の林遣都主演で映画化。幼なじみのちーちゃんとモンちゃんは、高校に入っても大の仲良し。家庭に問題を抱えるモンちゃんにとって、ちーちゃんは心を許せる唯一の存在だった。ある日、モンちゃんはオカルト好きのちーちゃんに付き合わされ、学校に伝わる七不思議を調べることになり……。", "2008-01-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love Actually", "ラブ・アクチュアリー", "", "", "http://image.eiga.k-img.com/images/movie/1115/315x210.jpg?3998", 135, "「ブリジット・ジョーンズの日記」「ノッティング・ヒルの恋人」「ビーン」の脚本家兼製作者リチャード・カーティスの監督デビュー作。クリスマス間近のロンドンを舞台に、英国首相からプータローまで、老人から11歳の少年まで、さまざまな職業と年齢の男女19人が織りなす群像ラブ・ストーリー。米国大統領役のビリー・ボブ・ソーントン、老いた元ロックスター役のビル・ナイ、店員役のローワン・アトキンソンなど個性派俳優も共演。", "2004-02-07", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIBAK　MIDNIGHT　DANCERS", "真夜中のダンサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "欲望と暴力が渦巻く都市マニラの実態を描き、本国フィリピンでは上映中止に追い込まれた作品。学校を辞め、二人の兄に従ってゲイバーで働き始めた青年サニー。しかし店は警察の手入れで被害を受け、麻薬中毒のホームレスが助けを求めて現れたことから、サニーの家族に暗い影が忍び寄る。", "1994-01-01", "35", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVER COME BACK", "恋人よ帰れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ニューヨークの広告代理店を舞台に、恋のさや当てを描いた都会派のラブ・コメディ。ラムゼー商会の専務ウェブスターは、仕事のためなら手段を選ばない辣腕家として広告業界では通っている。ところが、ついうっかりと口を滑らせたことから新商品を開発せねばならなくなり、おまけにライバル会社の女社長も絡んで大騒動が展開する。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボクが病気になった理由", "ボクが病気になった理由", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ガン、糖尿病、高血圧という三大成人病をテーマにした、３話から成るオムニバス・コメディ。第１話「マイ・スウィート・リトル・キャンサー」は、ガン・ノイローゼになった男が、銃を持って証券会社に乱入。彼の担当だった女医が、医学的にガンではないと衆人環視の中で、犯人を説得する。第２話は「ランゲルハンス・コネクション」は、グルメ番組を担当した、TVの美人キャスターが、糖尿病に。だが、視聴率が良くて番組を辞められない……。第３話「ハイパーテンション・ロード」は、航空会社の手違いで、一夜だけホテルで同室になった高血圧の男と、チェリストの女が、翌朝、必死で名古屋から東京へ向かう珍道中を描く。J・ヒューズの「大災難」を思わせる、渡邊孝好監督による第３話が出色。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青・chong", "青・chong", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "朝鮮学校に通うテソンは野球とケンカに明け暮れる高校生。朝鮮人としての誇りを持つよう教育されてきた彼だが、姉が日本人との結婚話を持ち出したことに動揺を隠せない。やがてテソンが所属する野球部の高野連加盟が決定し、彼の周辺は慌ただしくなっていく……。李相日監督が日本映画学校の卒業制作として撮った作品で、PFFアワード2000でグランプリなど四つの賞を得た青春映画。朝鮮学校出身である監督自身の実体験に基づき、在日三世の少年の成長、周囲の現実とのギャップを鮮やかに映し出す。朝鮮学校という珍しい舞台設定だけでなく、巧みなカメラの構図や編集、ユーモアのセンスにも見るべきものがある。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Iggy & The Stooges: Raw Power Live - In the Hands of the Fans", "実演！淫力魔人　イギー＆ザ・ストゥージズ", "", "", "http://image.eiga.k-img.com/images/movie/57645/315x210.jpg?1238", 81, "「イギー・ポップ＆ザ・ストゥージズ」が1973年に発表した名盤パンク「淫力魔人」に収録された全曲を実演し、そのライブの模様を公募で選ばれた6人のファンがそれぞれのカメラで収めた映像で構成されたライブドキュメンタリー。「淫力魔人」収録曲のほか、アルバム「THE STOOGES」や「FUN HOUSE」からの名曲も含めた全16曲を披露。", "2012-02-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOS OLVIDADOS", "忘れられた人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "メキシコ・シティのスラム街に住む貧しい子供たちのボスだったハイボが、感化院を脱走して街に戻って来た。不良仲間でありながらまだ悪に染まり切っていないペドロは、母の悲しみを知りながらも、盲人を痛めつけて金を奪い取るなど犯罪を続けるハイボたちにつき合っている。しかしある日、自分が感化院に入れられたのは仲間のフリアンの密告のせいだと思い込んだハイボが、ペドロの目の前でフリアンを殴り殺す。その夜、夢にうなされたペドロはまじめに働こうと心に誓うが、そのやさき、ハイボの盗みの罪を着せられて感化院に送られるハメに……。強烈な映像で当時のメキシコを鋭くえぐったブニュエル中期の代表作。", "1950-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OLIONS BELT", "ソ連機密地帯／オライオンズ・ベルトを追え!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1985年、ノルウェーの遊覧船乗務員とソ連人数名との銃撃戦が突如勃発。ようやく生き延びた船員トムは、ノルウェー政府に駆け込むが……。政治と歴史の裏の駆け引きの中で操られる一人の男を描くアクション・サスペンス。", "1985-01-01", "36", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MERCURY　RISING", "マーキュリー・ライジング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "精神に障害をもつ少年サイモンは偶然から、“マーキュリー”と呼ばれる国家の極秘コードを解読してしまう。それを知った国家安全保障局のメンバーはサイモンの抹殺を計画。家を襲撃し、彼の両親を殺害する。生き残ったサイモンを助けたのはFBIのアートだった。事件の裏に大きな陰謀の匂いを嗅ぎ取った彼はサイモンを守りながら真相を究明していく……。B・ウィリス演じる落ち目のFBI捜査員と少年サイモンの交流を交えながら繰り広げられるサスペンス・アクション。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alpha Dog", "アルファ・ドッグ　破滅へのカウントダウン", "", "", "http://ecx.images-amazon.com/images/I/51wOtuXU6xL._SL210_.jpg", 117, "", "2006-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("特警屠龍／TIGER CAGE", "タイガー刑事〈でか〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "麻薬事件を解決した後に開かれたパーティーの席で、ギャングに狙撃された刑事の復讐を誓い、その恋人と仲間たちが繰り広げる戦いを描く。探偵や、殺し屋、警察、ヤクの売人が入り乱れての大乱戦を展開。銃撃戦、カーチェイス、カンフーと、息詰まるアクション・シーンが繰り広げられる。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KID GALAHAD", "恋のKOパンチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "陸軍を除隊したウォルターは故郷に戻りボクサーになる。だが、マネージャーの妹と結婚の約束をしたために、八百長試合をさせられて……。歌あり、アクションありの1960年代の典型的なアイドル青春映画。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅の流れ星", "紅の流れ星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1958年に舛田利雄監督が石原裕次郎主演で撮った「赤い波止場」のリメイク。しかし、ラストはゴダールの「勝手にしやがれ」をモデルに大幅に改変してあり、前作の「赤い波止場」がデュヴィヴィエの「望郷」からモチーフを借りてきていることを加えると、３本の映画のモチーフで仕上がっていることになる。ただし、それはあくまでモチーフだけであり、作品としてはロマンチシズムと軽さがシャープな映像として結実していた。加島組の組長を高速道路で射殺した五郎は、神戸の関興業に身を寄せて１年が経とうとしていた。五郎は退屈さをもてあましながらも酒と女には何不自由なく過ごしていたが、殺し屋や刑事は五郎のことを狙っている。ある日、関と取引していた宝石商が行方不明になり、その婚約者だという啓子が訪ねてきた。五郎は啓子に惹かれるものを感じるが……。渡哲也はベルモンドばりに、ことあるごとにルリ子に寝たいと口説き、それまでの日活スターにない人物像を作り上げた。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Crossing Guard", "クロッシング・ガード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "愛する娘を交通事故で亡くした父親。加害者への復讐にとりつかれた彼は、6年間の刑期を終えて出所したばかりの男性の元へと向かう。彼らが決着をつけるまでの、それぞれの苦悩に満ちた3日間をサスペンスタッチで描いたヒューマンドラマ。俳優としても第一線で活躍するショーン・ペンが、同じくアメリカを代表する名優ジャック・ニコルソンと、「インディアン・ランナー」のデビッド・モースを主演に迎えて贈る監督・脚本第2作。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOTO LE HE´ROS", "トト・ザ・ヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ベルギー出身の新人J・V・ドルマルが、1991年カンヌ映画祭でカメラドールを受賞し、センセーショナルなデビューを飾った初監督作品。老境にさしかかった主人公の回想形式でその生涯を絶妙なタッチで描き出し、世界中の映画ファンの喝采を浴びた。とある老人ホームの一室。トマ老人は自分の生涯をふり返りながら、“復讐”を決定する。少年時代、大好きだったパパと愛する姉アリスの悲しい死。青年時代、アリスの面影を漂わせた人妻エヴリーヌとの悲しい恋。“それもこれもみんな隣家の息子アルフレッドのせいだ”。そう思い続けて生きてきたトマ。そして彼はピストルを手にアルフレッドの家へ向かう……。", "1991-01-01", "37,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THINKING BIG", "テオとマーティの素敵な世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "ちょっと内気で心優しい７歳の少年マーティは、家ではかまってもらえず、学校ではガキ大将にいじめられる毎日。そんな彼の唯一の友だちであるヌイグルミの熊のテオが、ある日突然しゃべりだし、マーティを大人の世界へと誘っていく。幼い少年の憧れをファンタスティックに包み上げたミラクル・ムービー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CODE／暗号", "THE CODE／暗号", "", "", "http://image.eiga.k-img.com/images/movie/34225/315x210.jpg?2174", 124, "「私立探偵　濱マイク」の林海象監督による探偵ドラマ「探偵事務所5」シリーズの劇場版第2弾。天才的な暗号解読の才能を持つ探偵507の元に、上海支部から新たな暗号解読の依頼が届く。これまで見たこともない複雑な暗号に魅入られた彼は上海へと向かい、依頼人である歌姫・美蘭に接触するが……。歌舞伎役者・尾上菊之助がアクション現代劇に初挑戦で新境地を開拓。共演には稲森いずみ、宍戸錠、松方弘樹、佐野史郎ら豪華キャストが揃う。", "2009-05-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIST FIGHTER", "バトル・ファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "かつて無二の親友を葬り去ったストリート・ファイターを追い求める男の執念を描くファイティング・ムービー。主人公には数々の西部劇や、B級アクションの主役として名を馳せたG・リベロ。「ガンヘッド」や「クライシス2050」など日本映画への出演が目立っていたB・バーキがヒロイン。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NICO ICON", "ニコ・イコン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "初期ヴェルヴェット・アンダーグラウンドの歌姫であり、女優としても活躍したニコにまつわるドキュメンタリー。1988年にイビザ島で亡くなった彼女の謎に包まれた生涯を検証していく。華やかな恋愛遍歴やドラッグ癖の一方で、多くのアーティストの才能を刺激し続けたニコ。自分の存在理由を探し求めた彼女の、あまりにも強烈な意志を浮き彫りにする。", "1995-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MILAREPA", "ミラレパ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "11世紀のチベットに実在した聖人ミラレパの青年時代のエピソードと、ミラレパを研究する現代の学生レオの内なる旅を交錯させたカヴァーニの異色作。大学で東洋学を専攻するレオは、研究を目的に東洋へ旅立とうとする教授夫妻の車に同乗して空港へ向かう。ところが、一行は途中で事故を起こし、深手を負った教授は無意識のうちにミラレパの名を呼ぶ。これに応えるようにレオは、ミラレパの苦難の旅を語り始める……。舞台は一転して現代のヨーロッパから中世のチベットに移り、ミラレパとなったレオは、ミラレパの生を追体験するという大胆な構成。映像の美しさが際立った力作。", "1973-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TRAP", "野獣の抱擁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "荒涼たるカナダの未開の地に取り残された二人っきりの男女が、掘っ建て小屋に住み、大自然と戦いながら生き抜く姿を描くサバイバル・アクション。女は粗野な男の前で固く心を閉ざしていたが、いつしか男の孤独な姿に惹かれていく。", "1966-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WELCOME TO THE DOLLHOUSE", "ウェルカム・ドールハウス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ジュニアハイスクール７年生のドーンは、学校では男の子からブス、女の子からはレズと呼ばれて嫌われていたうえ、先生からもいつも叱られていた。家でも父親は無関心で、母親は妹をひいき。ドーンは、いつも彼女をいじめるクラスメイトのブランドンが、実は彼女のことを好きなことをわかっていた。しかし、彼女は年上のスティーヴに恋心を抱いているけど、相手にされない……。風変わりな女の子の日常を描いた作品で、1996年のサンダンス映画祭審査員大賞(＝グランプリ)、ベルリン映画祭国際批評家連盟賞を受賞した。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COP−OUT", "コップ・アウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "悪徳警官の汚名を着せられた弟の無実の罪を晴らすため、刑事の兄が単独で敵に挑むというアクション映画。ある日突然、刑事トムは市長の息子殺しの犯人として逮捕される。マスコミから糾弾され、警察からも見放されたトムを一人信じるのは、やはり刑事である兄のボビーだった。捨て身の捜査の結果、組織のボスが市長自身と知ったボビーは……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜來香", "夜來香", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "二枚目スター、上原謙を主役に据えた悲恋と悲運の物語。華北の戦線で出会った軍医の三郎と慰安婦の秋子は一夜をともにする。それから５年後。神戸の製薬会社で働く三郎は、戦地で負った傷のために失明しかけていた。そんな折、彼は秋子と再会するが、彼女が自分のために身を売ろうとするのを知り……。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO FOR THE SEESAW", "すれちがいの街角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ネブラスカからニューヨークへとやって来た中年の弁護士ライアンは、あるパーティーで、20歳も年の離れた女ギテルと知り合う。妻と別れ、新生活を始めようとしていた彼は、ギテルの家に居候するようになるが……。1960年代のビート文化を背景に、R・ワイズが、R・ミッチャムとS・マクレーンという二大スターを得て描く都会派のラブ・コメディ。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ろくでなし", "ろくでなし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "大島渚や篠田正浩とともに、松竹ヌーヴェル・ヴァーグの到来を告げた吉田喜重のデビュー作。会社社長の息子・秋山俊夫、北島淳、森下、藤枝の４人は、秋山の父の秘書・郁子の銀行帰りを襲い現金の入った鞄を奪う。しかし、すぐさま金を返すと、郁子を車から降ろした。彼らにとってはこれは遊びなのだ。数日後、淳は郁子をパーティーに誘い関係を持つ。やがて藤枝が渡米することになり、彼らは再び銀行帰りの郁子を襲うが……。吉田は青春のやり場のない焦燥感の形を借りて、新しいモラルの台頭を鮮烈に描いた。封切り当時、ゴダールの「勝手にしやがれ」との類似を指摘された。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STORMY WEATHER", "ストーミー・ウェザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "第二次大戦に出征した黒人兵の慰問映画として作られたオール・ブラック・キャストによるミュージカル。伝説的黒人エンターテイナーたちが総出演する価値ある１本。登場するのは史上最高の黒人女優L・ホーン、タップの名手ミスター“ボージャングル”ことB・ロビンソン、天才ファッツ・ウォーラー、さらにC・キャロウェイ、ニコラス・ブラザースなどのそうそうたるメンバー。ヨーロッパ戦線から帰ったばかりの文無し兵士ビルは古い友だちでナイトクラブ歌手のセリーナとともに、ひょんなことからショービズ界で大成功を収め、二人の恋も実るという物語。タイトルになっている『ストーミー・ウェザー』は、この映画でも使われているジャズの名曲中の名曲。全編見ごたえあるミュージカル・ナンバーで綴られる。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIDNIGHT CROSSING", "ミッドナイト・クロッシング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "水兵モーリーはキューバ沖のある島に強奪した200万ドルを隠した。それから28年後、実業家として成功した彼は、緑内障の妻と、秘書夫妻を伴いその島へ向かう。４人の男女のあくなき欲望が複雑に絡みあう海洋サスペンス・ロマン。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KWAHERI", "クワヘリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "アフリカ大陸のショッキングな実像を、探検家M・カーターが４年７ヵ月の歳月を費やして、自ら撮影したドキュメント・フィルム。高地に住むキシイ族による麻酔も消毒薬も使わない開頭手術や、子孫繁栄のため処女を生にえにするトボキ族の暗黒儀式など、目を覆うばかりの残酷シーンが連続する。ビデオ発売時タイトルは「残酷大陸クワヘリ」。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AIRPLANE！", "フライング・ハイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "映画、CM、TVのパロディー集「ケンタッキー・フライド・ムービー」で大受けに受けたチームが再度挑戦するパロディ映画。ジェット機の中で食中毒発生、パイロットを失った旅客機はどこへ行く？　という珍妙なストーリーがギャグいっぱいに描かれる。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京プレイボーイクラブ", "東京プレイボーイクラブ", "", "", "http://image.eiga.k-img.com/images/movie/56325/315x210.jpg?3049", 96, "2010年ゆうばり国際ファンタスティック映画祭で、新人監督を対象としたオフシアター部門でグランプリを受賞した奥田庸介監督の劇場デビュー作。職場のトラブルが原因で東京郊外の小さな町に流れてきた勝利は、昔の仲間・成吉が経営する場末のラウンジ「東京プレイボーイクラブ」で働き始める。一方、東京プレイボーイクラブのボーイ・貴弘と付き合っているエリ子は、変化のない日常に疑問を感じはじめていた。そんなある日、勝利が地元の若者と起こしたケンカが原因で、3人は思いがけないトラブルに巻き込まれ……。主演は大森南朋、光石研、臼田あさ美。", "2012-02-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すべてが狂ってる", "すべてが狂ってる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "松竹ヌーヴェル・ヴァーグを意識して企画、破滅的な若者たちを乾いたカメラアイに捉えた鈴木清順監督作品。モデル出身の新人・禰津良子の奇妙な存在感が見もの。吉永小百合がワンカット、アップで出演している。アクションを積み重ねていく彼の演出スタイルは、変節する時代の表面に浮上してきた若者像をつかむのにきわめて有効であった。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flammen og Citronen", "誰がため", "", "", "http://image.eiga.k-img.com/images/movie/54725/315x210.jpg?4238", 136, "1944年、ナチス占領下のデンマーク。レジスタンス組織の一員として、国を裏切った者たちを暗殺する任務を遂行していたフラメンとシトロンだったが、ある任務をきっかけに、組織に内通者がいることを知る。疑心暗鬼に苛まれながらも、祖国のために戦い続ける2人は……。史実を映画化し、デンマーク国内で大ヒットを記録したサスペンスドラマ。主演は「青い棘」のトゥーレ・リントハート、「007／カジノ・ロワイヤル」のマッツ・ミケルセン。", "2009-12-19", "18,43,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前漫画", "喜劇・駅前漫画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "次郎はマンガ家の先生。弟子を使って仕事に忙しい。一方、オモチャ工場を経営する孫作と紙箱屋の三平は親友だが仕事はまるでダメ。これに有名な童画家・徳之助が加わり、駅前商店街の騒動をコミカルに描いていく。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TALES　OF　BEATRIX　POTTER", "ピーターラビットと仲間たち／ザ・バレエ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "世界中で愛され続けるベストセラー絵本の世界を映像化したユニークなバレエ劇。英国バレエ界の巨匠F・アシュトンが振り付けを担当し、ロイヤル・バレエ団のダンサーたちが動物キャラクターの着ぐるみ姿で出演するという豪華な布陣だ。ピーターラビットの絵本から『アヒルのジマイマのおはなし』『ジェレミー・フィッシャーどんのおはなし』など５つの物語をチョイスし、メドレー形式で進行。動物たちの仕草を俊敏かつ細やかに体現した踊り、そのまま絵本から抜け出したようなコスチュームが愛くるしい。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wreck-It Ralph", "レック・イット・ラルフ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仇討", "仇討", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "武士道の重圧から逃れようとしてあがきながら、ついに果たせず死んでいく軽輩の侍・新八の姿を、彼の精神的な葛藤を中心に描いた時代劇の力作。播州の小藩、無役の武士・新八は武器庫点検のいさかいから上司を斬殺。彼は仇として上司の弟たちから次々と狙われる。ラストのダイナミックな大立ち回りは、錦之助の力演で迫力十分。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I　CRUDELI", "黄金の棺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "映画評論家の故・淀川長治が名付けたと言われる、イタリアの西部劇こと“マカロニウエスタン”の隠れた名作。「続・荒野の用心棒」を手がけたS・コルブッチ監督が、「第三の男」のJ・コットンを主演に迎えて撮り上げた。哀愁のサントラを手掛けたのは巨匠モリコーネ。舞台はアメリカ。南北戦争が終わった隙を突いて北軍から大金を奪った男が、息子と共に故郷を目指す。", "1966-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ケルベロス 地獄の番犬", "ケルベロス 地獄の番犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "近未来の東京。滅ぼされた首都警察の中核部隊ケルベロス隊員の乾が、国外逃亡した都々目の跡を追って出国。その道中、彼の愛人タンミーと出会い……。「うる星やつら」「機動警察パトレイバー」などアニメで知られる押井守が「紅い眼鏡」の続編として放つ実写第２作。DVDは「押井守シネマ・トリロジー／初期実写作品集 」に収録。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大幹部 ケリをつけろ", "大幹部 ケリをつけろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "大羽根組の幹部・朝倉竜次はヤクザ稼業から足を洗うための交換条件として、高畑組の組長暗殺を命令される。けれども、これに失敗し彼は２年の刑に。刑を終えて出所した竜次は、２年前と様子が一変していることに気づくのだった。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・極道記者 逃げ馬伝説", "新・極道記者 逃げ馬伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ギャンブルの醍醐味と、競馬記者の生き様をハードボイルドに描くシリーズ第３作。スポーツ新聞で競馬予想のコラムを担当しながら自らもギャンブルの虜になってしまった極道記者・松崎。彼の前に、鉄砲玉の田坂、性的不感症の女教師・手塚、無頼派作家の麻生魁など、彼に負けず劣らずのギャンブラーたちが現れる。自分の担当編集者であり、松崎の別れた妻・真理子に想いを寄せる麻生は、松崎へのライバル心から勝負を申し込み、二人は一番大事にしているものを賭けて、競馬12番勝負に挑むが……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ガンダムIII めぐりあい宇宙編", "機動戦士ガンダムIII めぐりあい宇宙編", "", "", "http://ecx.images-amazon.com/images/I/51X8N8P9C7L._SL210_.jpg", 141, "1979～80年に放映されたTVアニメ「機動戦士ガンダム」を再編集した劇場3部作の第3部。再び宇宙へ戻ったアムロ・レイは、変わり果てた父との再会や、宿敵シャア・アズナブル、運命の女性ララァ・スンとの出会いを経験。そして、戦争が最終局面を迎えるなか、アムロは宇宙環境に適応した新人類「ニュータイプ」としての才能を開花させていく。", "1982-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MILLION DOLLAR MERMAID", "百万弗の人魚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1910～25年頃に水中レビューの第一人者として活躍したアネット・ケラーマンの青春時代を描いた伝記映画。小児マヒを克服したケラーマンは、生地オーストラリアで水泳のチャンピオンになり、ロンドンではテームズ河の遠泳に成功。そんな彼女を興行師ジェームズが大スターに育てあげる。“水着の女王”E・ウィリアムズが熱演。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRANGE ENCOUNTERS", "サイコボックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "西ドイツ映画界が作り出した異色のオムニバス・ホラー。奇妙なプレゼントにまつわる恐怖「マーラム～恐怖の贈りもの」、自分そっくりの女と対決する主婦を描く「ジュリアに殺される」ほか、「秘密のチェスゲーム」「スウィート・ホーム」の４編。アメリカ作品にはないヨーロッパ的な味わいがポイント。", "1987-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMME　UNE　IMAGE", "みんな誰かの愛しい人", "", "", "http://ecx.images-amazon.com/images/I/313B9GMCT8L._SL210_.jpg", 111, "女優、脚本家、監督の顔を持つフランスの才女A・ジャウイ。太めの体型で誰にも相手にされないと思い込む女の子や、芽の出ない小説家の夫を抱える音楽教師ら、ちょっと悩みを抱え、少しだけ愛情に飢えた人々。そんな彼らの悲喜こもごもを、ジャウイならではの手法で描くユニークな群像劇だ。前作「ムッシュ・カステラの恋」同様に、練り込まれた構成と登場人物が交わす軽妙洒脱な会話が絶妙。言い寄ってくる男はいつも有名作家の父が目当てなことに傷付くロリータ、何の取り柄もない彼女は、自分を認めてもらおうと得意な歌の教室に通い出す。しかし、一番理解してほしい父はまったく興味を示さない……。", "2004-10-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Assassination of Richard Nixon", "リチャード・ニクソン暗殺を企てた男", "", "", "http://image.eiga.k-img.com/images/movie/1615/315x210.jpg?3976", 96, "74年に起きたニクソン大統領暗殺未遂事件を踏まえて描く社会派ドラマ。オフィス機器のセールスマンである40代のサムは、売り上げが伸びず、妻にも家出されて、苦悩する日々を過ごす中で、ウォーターゲート事件の報道に接して、ニクソン大統領の暗殺を考えるようになる。製作に「ハリー・ポッターとアズカバンの囚人」の監督アルフォンソ・キュアロンや、「サイドウェイ」のアレクサンダー・ペインが参加しているのも話題。", "2005-06-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Slaughterhouse-Five", "スローターハウス5", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "時間を超越してさまよう男性を描いたカート・ボネガードの同名SF小説を、「明日に向かって撃て！」のジョージ・ロイ・ヒル監督が映画化。第2次世界大戦後、実業家として成功をおさめたビリー・ピルグリム。自らの意思と関係なく自分の過去・現在・未来をとびまわるようになった彼は、戦争での捕虜体験、飛行機の墜落事故、ドレスデンでの大空襲など、さまざまな出来事のなかをさまよう。1972年度、カンヌ国際映画祭審査員賞受賞。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Streets of Fire", "ストリート・オブ・ファイヤー", "", "", "http://ecx.images-amazon.com/images/I/51ulDwWHugL._SL210_.jpg", 93, "　ロック歌手のエレンがレイブン率いるストリート・ギャングによって誘拐された。それを知ったエレンの元恋人トムが街に帰ってくる。トムは女兵士マッコイ、そしてエレンのマネージャー、ビリーとともにエレン救出に乗り出すことに。ショットガンを手に、レイブンのアジトを急襲するトムだったが……。硬派ウォルター・ヒル監督が“ロックンロールの寓話”と銘打ったアクション活劇。娯楽映画の面白さをフルに詰め込んだ快作。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LION OF THE DESERT", "砂漠のライオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 175, "第二次世界大戦勃発の直前、ムッソリーニ率いるイタリア軍は、北アフリカの地にローマ帝国を再興すべく、立ちはだかるベドウィンに対し総攻撃を開始した……。リビアの英雄オマール・ムフタールの波乱の半生を、アラブ人アッカド監督が描いた作品。", "1981-01-01", "1,47", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHILDREN OF THE CORNII FINAL SACRIFICE", "死の収穫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "アメリカを代表するベストセラー作家、S・キングの短編『とうもろこし畑の子供たち』の二度目の映画化。ガトリンという町で起きた不気味な事件。町の大人すべてが惨殺死体で発見された。その事件を取材に来たタブロイド紙の記者ジョン・ギャレットとその息子ダニー。二人は、残された子供たちに不審な影を感じ、トウモロコシ畑に関する謎の存在に気付く。そうしているうちにも隣町の大人たちが一人、また一人と惨殺されていく……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使のはらわた 赤い淫画", "天使のはらわた 赤い淫画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "都会に住む若い女の求めてかなわぬ孤独と自暴を、スタイリッシュかつ官能的に撮り上げたロマンポルノの佳作。デパート勤めの名美は、ビニ本のアルバイトがバレて会社をクビに。街に放り出された彼女は、ビニ本の姿に魅せられたという男・村木に遭遇する。男たちに絶望した名美だが、もう一度だけこの男を信じてみようとする……。にじむネオン、雨のジャングル・ジム、血染めの階段など、湿気を帯びたカラー映像が、女のくぐもった情念をあざやかに映し出す。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐になるまで待って", "嵐になるまで待って", "", "", "http://image.eiga.k-img.com/images/movie/54295/315x210.jpg?3579", 124, "2008年8月に上演された劇団キャラメルボックスの舞台をデジタル映像化。8台の映画用カメラを駆使して、役者たちの表情を細やかに捉える。アニメ声優のオーディションに合格したユーリは、顔合わせの席で俳優の高杉と作曲家の波多野が激しく言い争う場面に遭遇。波多野の妹・雪絵にまで暴力を振るおうとする高杉を波多野が必死の形相で制止するが、その時ユーリには「死んでしまえ！」という波多野の“もう1つの声”が聞こえ……。", "2009-02-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATHER GOOSE", "がちょうのおやじ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "第二次大戦下、南太平洋の孤島で気ままな暮らしをしていた人間嫌いの男が、連合軍の作戦に協力するはめになった。やがて彼は、７人の子供たちと美しい女性を日本軍から守るため奮闘していく。R・ネルソン監督が戦時下の緊張感を巧みに利用して描くロマンティック・コメディ。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAGIC SWORD", "魔法の剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「戦慄！プルトニウム人間」「巨大生物の島」などで知られるB・I・ゴードン監督によるファンタジー活劇。魔女に育てられた若者ジョージは、美しいヘレン姫に淡い恋心を抱いていた。そんなときエレンが悪の魔法使いにさらわれ、悲嘆に暮れる王様は彼女を救った者を婿にすると宣言。ジョージは魔法の剣を手にし、魔法使い退治に立ち上がる。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生物みなトモダチ・パート２ 教育篇 ～トリ・ムシ・サカナの子守歌", "生物みなトモダチ・パート２ 教育篇 ～トリ・ムシ・サカナの子守歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 156, "名作「上海」「戦ふ兵隊」を作った文字通り日本の記録映画界を代表する反骨の作家・亀井文夫の最後の作品。自ら本作を遺言と語った彼は、独自の視点で現代の諸問題（受験・中絶・核など）を提示し、メッセージを突きつける。また、副題が「鳥になった人間（亀井）のシネ・エッセイ」とあり、この映画は飛躍する映像のコラージュで成り立っている。脚本を詩で書いてその通りに撮らせ、現場へ行かずに編集室で映画を作る亀井の真骨頂を示すが、それは“メッセージのため強引にカットをねじ伏せる”という批判の対象にもなった。彼の強靭な批判精神と不屈の意志は、衆目が認めるところだ。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メシア／伝えられし者たち", "メシア／伝えられし者たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "大胆な仮説で宮本武蔵像の常識を覆した「巌流島」の千葉誠治監督が、「ALIVE」の下村勇二をアクション監督に据えて映画化したSFアクション。琉球王朝時代から続く一族の当主で超能力を持った少女が、沖縄を舞台に敵対する一族との因縁の闘いに挑む。映画初主演となる森田彩華がみずみずしい演技を披露する。琉球王朝時代から続く一族、南主守の当主で超能力を持つ少女・紀紗は、高校進学を控えていた。しかし、南主守に対して長年の恨みを持っている北主守の鎬が現れ、一族の正当な伝承者にのみ受け継がれる巻物を奪い取ろうと企む。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ISOLA・多重人格少女", "ISOLA・多重人格少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1995年の阪神・淡路大震災を背景にしたサイコ・サスペンス。人の心が読める不思議な能力を持つ由香里が、ボランティアで神戸を訪れた。そこで彼女は、悲惨な生い立ちのせいで12もの人格を心に宿した少女、千尋と出会う。やがて由香里は、千尋の中に邪悪な13番目の人格が存在していることに気づく。これが演技初体験となった黒澤明監督の孫娘、黒澤優が、心に深い傷を持つ少女を好演。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CODE……DEATH", "プレデターゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "パトロール中、セスナ機が森林に墜落するのを目撃したアギラとジャガーは現場へと向かい、そこで200万ドルと最新軍用兵器、虐殺死体を発見。警察本部に無線連絡をするが何者かに遮断され、密林で孤立してしまう。一方、ジャングルで独立国を築く大佐もこの情報を聞きつけ、凶暴なゲリラ部隊を密林へと向かわせるのだった……。密林を舞台に警官と凶悪ゲリラ部隊との死闘を描いたコンバット・アクション。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄金バット", "黄金バット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "戦前、少年冒険雑誌で人気を呼んだ永松健夫の原作を映画化した特撮映画。怪人ナゾーは惑星イカルスの軌道を変え、地球に衝突させようとするが、蘇った黄金バットによってナゾーの野望は砕かれる。折からのアニメ・ブームで、翌年にはアニメ版が製作された。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beaches", "フォエバー・フレンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "貧しいが歌手を目指すCCと、箱入り娘で弁護士志望のヒラリーは親友同士。だが、それぞれの夢が叶った時、2人は互いの考え方、生き方の違いに気づき始め……。境遇の違う2人の女性の30年に渡る交流を描いた友情物語。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　SOUL　GUARDIANS", "ソウル・ガーディアンズ／退魔録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "実際に韓国で起きたカルト教団の集団自殺をモチーフにしたベストセラー小説の映像化。人間世界を支配しようとする悪魔と、これに対抗して戦う３人の退魔師の対決を、CGと特撮を駆使して描いたスペクタル・アクション。悪の力から悲運の女性を守るために命を懸けて戦う退魔師を韓国の国民的スター、アン・ソンギが熱演している。", "2000-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("九降風", "九月に降る風", "", "", "http://image.eiga.k-img.com/images/movie/54555/315x210.jpg?4052", 107, "1996年の台湾郊外・新竹を舞台にした高校生の青春群像劇。監督は本作が長編デビューとなるトム・リン。プロ野球に夢中な男子高生7人組、プレイボーイのイェン、地味だが心優しいタン、義理人情に厚いヤオシン、おしゃべりなチョンハンらは、放課後につるんでは問題を起こし、教官に呼び出される日々を送っていた。そんなある日、イェンの浮気が原因でグループは仲たがいをしてしまう……。", "2009-08-29", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bay Boy", "キーファー・サザーランドの　ベイ・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "将来のことを考え始めたり恋に悩んだりする思春期の少年。ある日彼らは、心を惹かれている美人姉妹の父親が殺人を犯す現場を目撃してしまう……。デビュー間もないK・サザーランドとイングマール・ベルイマン作品の常連、名優L・ウルマンの異色の顔合わせで描いた感動の一編。", "1985-01-01", "10,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE DARLING", "リトル・ダーリング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "サマー・キャンプで出会った15歳の少女エンジェルとフェリスのひと夏の体験物語。なにかと反目しあう二人は、ついにロスト・バージンを懸けて“男あさり”に乗り出すのだが……。マクニコルの伸びやかな演技が光る、思春期映画の佳作。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花くらべ狸道中", "花くらべ狸道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "狸の国の大王選挙のため党主の代理で江戸に行くことになった雷吉狸と新助狸。敵の党からは刺客が放たれた。阿波から江戸まで、二人の珍道中が繰り広げられる。ミュージカル仕立てで軽快に撮り上げられたコメディ・タッチの好編で、勝と市川が二役を演じる。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("加地等がいた　僕の歌を聴いとくれ", "加地等がいた　僕の歌を聴いとくれ", "", "", "http://image.eiga.k-img.com/images/movie/57795/315x210.jpg?4556", 0, "2011年2月に40歳の若さで他界したシンガーソングライター・加地等の晩年を映し出したドキュメンタリー。大阪でのバンド活動を解散し、フォークギターを手に弾き語りを始めた加地は37歳で上京するが、酒に酔って右手を負傷。ギターが弾けずに自暴自棄になった加地は、アルコールに依存し、精神のバランスを崩していく。39歳の誕生日に復活コンサートが企画されるが、ライブ直前、加地はどこかへ姿を消してしまい……。上京してから他界するまでの2年半の加地の姿をとらえる。", "2012-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("kitchen キッチン", "kitchen キッチン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "吉本ばななのベストセラー小説を、舞台を香港に変えてリメイク。「息子の告発」「太陽に暴かれて」などの社会派ドラマを手掛けてきたイム・ホーが、詩的な映像美で包み込む。最愛の祖母を亡くしたアギーは、祖母の知り合いの青年ルイと彼の母エマの家に身を寄せる。親切な二人に優しく迎えられながらも、彼女は自分の殻に閉じこもり、お気に入りの場所のキッチンで一人過ごしていた。そんな彼女が少しずつ心を開くようになり、３人の楽しい共同生活が始まったある日、エマに悲劇が起こる……。富田靖子が「南京の基督」に続いて、全編広東語でヒロインを熱演する。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MOTORCYCLETTE", "あの胸にもういちど", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "学者ダニエルと知り合い、関係をもった若い娘レベッカ。彼女は、他の男との結婚後もダニエルと不倫の関係を続けていたが……。革ジャンを裸身にまといハーレー1200で愛人のもとに走るヒロインの姿が鮮烈で魅惑的。原作はマンディアルグの官能小説『オートバイ』。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BALZAC　ET　LA　PETITE　TAILLEUSE　CHINOISE", "小さな中国のお針子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "監督を務めるダイ・シージエが世界30ヵ国で翻訳された自身のベストセラー処女小説を自ら映画化。お針子役に扮する中国の人気若手女優ジョウ・シュンの清楚な佇まいが印象に残る。文化大革命期の中国。反革命分子の子と見なされたマーとルオは、再教育を受けるため山深い村へ送られる。西洋文明とは無縁のこの村で住人とともに働き始めた二人は、仕立て屋の孫娘で文盲の美しいお針子と出会う。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KISS KISS， BANG BANG", "キスキス，バンバン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「リーサル・ウェポン」の脚本家シェーン・ブラックが初監督したサスペンス・コメディ。マヌケな泥棒とゲイの私立探偵が繰り広げる騒動をスリリングに描く。雪ダルマ式に膨れ上がるトラブル描写と粋な会話の応酬が小気味良い痛快作。N.Y.でシノギを削るコソ泥のハリーは、ひょんなことから俳優に間違えられハリウッドへ。探偵映画のオーディションに通った彼は、役作りのため私立探偵の事務所へ行くが……。DVDタイトルは「キスキス，バンバン／L.A.的殺人事件」。", "2006-04-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHUCK NORRIS IN HELLBOUND", "ヘルバウンド 地獄のヒーロー5", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "心臓をえぐり取るという残酷な殺人を犯した犯人を追うシカゴ警察の刑事シャター。得体の知れない邪悪なパワーを感じた彼は、やがてその犯人が悪魔の化身“プロサタノス”であることを知るが……。アクション俳優C・ノリスが、何とハルマゲドンをもたらす悪霊を相手に大立ち回りを演じるオカルト活劇。“地獄のヒーロー”シリーズとは別物。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEP　INTO　LIQUID", "ステップ・イントゥ・リキッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "サーフィン映画の傑作「エンドレスサマー」「エンドレスサマーII」に続き、ブルース・ブラウンの実子であるデイナがサーフィンの魅力を余すことなく伝えたドキュメンタリー。世界一の波に挑む伝説のサーファーや親子の絆を取り戻すベトナム帰還兵など、波から波へと受け継がれる物語が、サーフィンの魅力と感動を伝えてくれる。超有名サーファーから名もなきサーファーまでを訪ねて、世界の海をめぐる旅。28年間毎日欠かさず波に乗り続ける男や、宗教紛争で引き裂かれた子供たちの心をサーフィンで結び付けようとする兄弟。そこには波の数だけ物語があり……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAWADA サワダ", "SAWADA サワダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1965年にベトナムにわたって戦争の写真を撮り続け、1966年にピュリッツァー賞を受賞。その後もベトナム戦争の非道を追い続け、1970年にプノンペンで狙撃され絶命したカメラマン、沢田教一の生と死に迫った秀作ドキュメンタリー。短い生涯を全うした彼の軌跡、人間性を、同僚や先輩、世界的ジャーナリストの証言で細やかに浮かび上がらせていく。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SPY IN THE GREEN HAT", "0011ナポレオン・ソロ対シカゴ・ギャング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "人気TVシリーズの劇場用映画。メキシコ湾流の流れを変えて、欧米を氷原に変えようと企む悪の組織スラッシュと、それを阻止せんとする情報局員たちの戦いを描く。“007”で知られるフレミングが創作したもう一人のヒーロー、ナポレオン・ソロが大活躍。彼の甘いマスクも魅力的。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RETURN OF A MAN CALLED HORSE", "サウス・ダコタの戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「馬と呼ばれた男」の続編にあたるヒロイックな活劇。イエローハンド・スー族の一員となった英国貴族モーガンはいったん帰国するが、仲間を守るために再び渡米。悪い白人に操られた敵部族の奴隷となって働く仲間を、生き残りの部族民とともに救い出す。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十六歳のマリンブルー", "十六歳のマリンブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "“いちご族”と呼ばれる10代の若者たちの人生観や価値観、ライフスタイルを、16歳の一人の少女の青春グラフィティーを通して描いた作品。夏の湘南・江ノ島。高校の演劇部に所属するえみは偶然ジミーという男の子と出会う。ふいに気が合った二人だが、ジミーはある薬の中毒患者で……。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WE’RE NO ANGELS", "俺たちは天使じゃない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "マイケル・カーティス監督による、1955年製作の同名映画をリメイク。1935年のアメリカを舞台に、刑務所から脱走したコソ泥二人が、神父になりすまして逃げるうちに、眠っていた良心に目覚めていく様をユーモラスに描いている。製作総指揮も務めたデ・ニーロが、トボけた味わいで好演。一本気な相棒役のS・ペンとのコンビぶりもなかなかのもの。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN AMERICAN WERE WOLF IN LONDON", "狼男アメリカン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "イギリスを徒歩で旅行しているアメリカの青年二人が狼に襲われる。生き残った一人は入院先の看護婦と仲良くなってまずはメデタシ。ところが満月の夜がきて、彼は……。変身シーンのSFXが見もの。恋や友情に悩む青年の姿に哀愁がにじむ新感覚ホラー。特殊メイクを名手、リック・ベイカーが担当している。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zombie Strippers", "ゾンビ・ストリッパーズ", "", "", "http://image.eiga.k-img.com/images/movie/53955/315x210.jpg?2233", 94, "近未来のアメリカ。慢性的な兵力不足を打開するため、米軍は死者を甦らせる危険なウイルスを開発するが、そのウイルスが研究所で蔓延。次々と感染者が増殖してしまう。掃討作戦で研究所に乗り込んだ兵士も感染し、抹殺されることを恐れた彼らが逃げ込んだ先は違法ストリップ・クラブだった……。主演にポルノ女優のジェナ・ジェイムソン、「エルム街の悪夢」(84）のフレディ役で知られるロバート・イングランドがストリップ・クラブのオーナー役を演じている。", "2008-10-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("監督・ばんざい！", "監督・ばんざい！", "", "", "http://image.eiga.k-img.com/images/movie/1895/315x210.jpg?4124", 104, "北野武監督が自ら主演を務め、スランプに陥った映画監督の姿をパロディやギャグを散りばめながら描いた異色作。得意なギャング映画を“2度と撮らない”と宣言してしまったキタノ監督は、小津安二郎風人情劇、ラブ・ストーリー、ホラー、時代劇、SFなどあらゆるジャンルに挑戦するが、どれも予期せぬハプニングによってことごとく失敗してしまう……。", "2007-06-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT TAKES TWO", "ひとりっ娘2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アメリカのTV界で人気を博した、キュートな双子オルセン姉妹を主演に据えたファミリー・コメディ。孤児院で育ったアマンダは、自分とそっくりな少女アリッサと出会う。アリッサは父親と二人暮らしで、父の財産を狙う女の存在に頭を痛めていた。お互いになり替わったアマンダとアリッサは正反対の立場を楽しみながらも、やがて自分たちの幸福のために一計を案じ……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秋桜 コスモス", "秋桜 コスモス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "福島県本宮町の母親たちが資金を集めて製作した「こころの山脈」に続き、“本宮方式”で作られた感動編。南米で事故に遭い、輸血によってHIVに感染した17歳の少女と、そのクラスメイトとの友情を中心に、偏見と闘う強い意志や、親子の愛情、望郷の思い、命の尊さを浮かび上がらせる。安達太良山の自然を捉えた叙情的な映像に、温かい人間模様が溶け込んだ良心作。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("息もできない長いKISS", "息もできない長いKISS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ボリビア移民のヒットマン山崎は、在日ホテトル嬢のユリカと一晩を過ごし、激しい恋に落ちる。そして翌日のユリカの客は、何と山崎の狙う標的だった……。ビデオやCMなどで活躍のキム・テグワン監督の映画デビュー作。卓抜したシチュエーションと映像センス、そして聖女のごときヒロインを演じるかとうあつきが素晴らしい。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金閣寺", "金閣寺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "耽美派で知られる映像作家・高林陽一が三島由紀夫の名高い傑作を翻案した作品。吃音コンプレックスをもつ若者が永遠の美をたたえた金閣寺に火を放ち、生まれて初めての自由を得るまでを、壮麗な映像で描く。大量の金箔を用いたラストの炎上シーンが圧巻である。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Invincible", "神に選ばれし無敵の男", "", "", "http://image.eiga.k-img.com/images/movie/1455/315x210.jpg?1401", 130, "かつてニュー・ジャーマン・シネマの騎手と呼ばれた「アギーレ／神の怒り」「フィッツカラルド」のヘルツォーク監督の新作は、ドキュメンタリー以外では10年振り。実話に基づき、ナチス台頭下のベルリンを舞台にユダヤ人の怪力芸人、元は実直な鍛冶屋の息子シジェを描く。シジェを演じたのは俳優ではなく、実際に重量挙げの選手であるヨウコ・アホラ。彼が恋するピアニスト役も俳優ではなく、ロシア出身の国際的ピアニスト、アンナ・ゴラーリ。オカルト芸人ハヌッセン役をティム・ロスが好演。", "2003-05-24", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Core", "ザ・コア", "", "", "http://image.eiga.k-img.com/images/movie/1195/315x210.jpg?3941", 134, "25万年に一度訪れる中心核の回転停止により、磁場が狂った地球では、スペースシャトルが制御不能になるなどの異常現象が相次ぐ。このままでは、有害な宇宙放射線を防いでいる地球の電磁界が失われ、人類は1年以内に滅亡してしまう……。未曾有の危機に見舞われた人類の存亡を賭けた戦いを描くパニック・アクション大作。", "2003-06-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOMETIHNG FOR JOEY", "ジョーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "白血病にむしばまれた幼い弟ジョーイの応援にこたえようと、優勝をめざすフットボール選手ジョン。美しい兄弟愛の実話を過度の感傷に陥ることなく、さわやかに描いた小品。『警部マクロード』で売り出した新人監督のTVムービーだが、日本では劇場で大ヒット。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUNGO 日本文学シネマ　富美子の足", "BUNGO 日本文学シネマ　富美子の足", "", "", "http://ecx.images-amazon.com/images/I/51uEYmQ106L._SL210_.jpg", 30, "谷崎潤一郎の短編小説を、「天国はまだ遠く」の加藤ローサ主演で映像化。芸者の富美子を妾（めかけ）として囲う老人・塚越は、富美子の美しい足に魅せられてしまう。ある日、塚越は美術学校の学生である青年・宇之吉に、富美子をモデルにした絵を描いてもらうことに。絵を描くうち、宇之吉は富美子の足に自身の幼少時の記憶を重ねていく。文豪たちの傑作短編を豪華キャストで映像化したテレビドラマ「BUNGO」シリーズの1作。", "2010-05-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新座頭市物語・笠間の血祭り", "新座頭市物語・笠間の血祭り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "生れ故郷の笠間に帰った座頭市は、百姓を苦しめる土地のヤクザと悪徳代官を相手に大暴れする。このあと、勝プロ製作によるTVシリーズが作られた。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裸の島", "裸の島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "瀬戸内海に浮かぶ周囲約500メートルの小島に千太とトヨ夫婦、彼らの二人の子供が生活している。島には段々畑があって夫婦は夜明けから日没まで黙々と畑仕事に励む。島には水がなく、大きな島から桶に水を汲んでは小舟で運び、畑に水をかけねばならない。日射しが強く土は水を一瞬にしてすい込んでしまう。自然と闘いながら生きていく人間の姿を描く新藤兼人の詩編。スタッフ、俳優全10数名の小さな撮影隊が瀬戸内の小島にこもり、主人公たちのように黙々と撮影を続けていった。セリフをいっさい排し、映像だけで語るというサイレント映画的な実験意欲に満ちた作品。黒田清己の撮影、林光の音楽も素晴らしい。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死霊波", "死霊波", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "新鋭・葉山陽一郎が手がけたサイコ・ミステリー。TV映像に紛れ込む、怨念と憎悪が生み出した“死霊波”。見る者を死の世界へ引きずり込むというこの電波を巡って、ある少女の一家に隠された驚愕の過去が明かされていく。貫地谷しほりが、怨霊の恐怖に慄くヒロインを迫真の演技で体現している。TV番組のディレクター・臼井は、霊に憑かれたという少女の家を訪問。お祓いを始めた途端、彼女は“ごめんなさい、私が悪いの”と謎の言葉を発する。", "2005-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROMIS…JURE！", "クロス・マイ・ハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "12歳のピエールはメメ・アモンのことが好きでたまらない。しかし彼女には意中の男の子がいて、ピエールがどんなにがんばっても思いは伝わらない。そんな時、ピエールの家に一人のドイツ兵が押し入ってくる。イザベル・アジャーニやパスカル・オジェなどの出演映画を監督していた、J・モネの本邦初登場作品。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("信子", "信子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "獅子文六原作による“女性版・坊ちゃん”といった感じの作品である。因襲的で権威主義的な女学校に赴任してきた体操の教師が、ものおじしない快活さで学校を明るく変えていく。当時湿ったメロドラマばかりに出ていた高峰三枝子がイメージチェンジし、作品としても成功している。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHADOWLANDS", "永遠の愛に生きて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "英国の童話作家C・S・ルイスとアメリカの詩人ジョイ・グレシャムの、年齢と文化を越えた愛の姿を、実話に基づいて描いたラブ・ストーリー。壮年の作家ルイスはファンの女性ジョイからの訪問を受ける。師弟関係はやがて男女の愛へ変わるが、ジョイが不治の病に倒れてしまうのだった……。簡潔な語り口が感動を呼ぶ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが生涯のかがやける日", "わが生涯のかがやける日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "暗黒街の用心棒でかつて青年将校の反乱グループのリーダーであった男を、その男に父を殺されたことを知らぬ娘が愛してしまう。戦争によって深い悲しみを背負った人々の姿を、混乱した社会を背景に描いた作品。当時の米軍検閲によって大幅に修正された。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("粋な風来坊", "粋な風来坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "徳川三百年の天下が崩壊し、明治維新の黎明が告げられる頃、さしもの次郎長一家も時代の流れには勝てず、家計は火の車であった。そんな折、長らく勘当を受けている次郎長の息子・清太郎と江戸の佐藤塾で学んだ小池文之進が清水一家を訪れ、ヤクザ稼業の精算を説く……。名匠マキノ雅広監督の娯楽時代劇。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPIDER LABYRINTH", "呪いの迷宮　ラビリンス・イン・ザ・ダーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "古代宗教の研究を行っているウィットモア教授は、恩師ロート教授をブダペストに訪ねた。しかしロートの様子も町全体の雰囲気もおかしく、町を操る邪悪な宗教にロートの研究が絡んでいることを知る。そしてその宗教とは巨大なクモを信仰するという不気味なものだった。悪魔的宗教に魅入られた人々の恐怖を描くホラー作品。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロト", "ロト", "", "", "http://image.eiga.k-img.com/images/movie/55085/315x210.jpg?3111", 63, "「砂の影」（2007）の甲斐田祐輔監督が04年に製作した短編作品。警備員として働く青年・山形は、職場の待機所で寝泊りしているが、叔父で上司の吉田から何かと小言ばかり言われていた。そんなある日、辞めた警備員の後任として、寡黙な男・北島が入ってくる。対照的で決して交わらないように見える山形と北島だったが、薄い布1枚で隔てられた部屋で暮らすうちに互いに少しずつ変化していく。やがて山形は、北島の行動の異変に気づき……。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("An American Haunting", "アメリカン・ホーンティング", "", "", "http://image.eiga.k-img.com/images/movie/53515/315x210.jpg?2505", 83, "アメリカで唯一“悪霊の仕業”として記録が残る実在の殺人事件を「ダンジョン＆ドラゴン」のコートニー・ソロモン監督が映画化したオカルト・ホラー。テネシーの古い屋敷で暮らす少女ジェーンは、毎晩のように見る悪夢に悩まされていた。かつてこの屋敷に住んでいたベル家は、魔女と噂される隣人バッツとトラブルを起こしており……。「パフューム／ある人殺しの物語」のレイチェル・ハード＝ウッドがジェーンとベル家の娘の2役を演じる。", "2008-02-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAX MONROE:LOOSE CANNON", "ルース・キャノン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "銃撃戦とカーチェイス大量放出の刑事アクション。ロサンゼルス警察のマックスは、型破りな捜査方法で仲間からも反感を買っていた。パートナーのチャーリーですら彼に疑問を持っていたが、本人はチャーリーを無二の親友と思っている。ある日、二人は銃撃戦の末、チャーリーが重傷を負い犯人に取り囲まれてしまう。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブラジルから来たおじいちゃん", "ブラジルから来たおじいちゃん", "", "", "http://image.eiga.k-img.com/images/movie/53855/315x210.jpg?2987", 59, "92歳日系ブラジル人の紺野堅一を追ったドキュメンタリー。紺野は73年前に不況真只中の日本から単身ブラジルに渡り、文化も言葉も違う国で10回も職業を変え、破産の経験もしたが、今ではサンパウロで悠々自適な生活を送っている。そして今、紺野は毎年日本にやってきては出稼ぎの日本在住ブラジル人たちを訪ね、彼らの苦労や将来について話し合う。監督は「ルッキング・フォー・フミコ」で70年代の日本の女性運動を描いた栗原奈名子。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAX WORK II", "ワックスワーク２　失われた時空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "邪悪な“手”によって、父親殺しで有罪となるサラ。マークは彼女の無罪を証明するため、すべての鍵を握るサー・ウィルフレッドの屋敷へ向かう。ここで彼は偶然にも時間の扉を開ける。フランケンシュタインからゾンビ、ゴジラまで、あらゆるファンタスティック映画のエッセンスを盛り込んだアドベンチャー・ムービー。ビデオ発売時タイトルは「ワックスワーク　時空の戦士」。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JANDARA", "ジャンダラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "タイで30年以上読み継がれる、愛と官能のロングセラー小説を映画化。「ナンナーク」のノンスィー・ニミブット監督が、トラウマを拭いきれない男がたどる禁断の性遍歴を大胆に描き出す。出産の際に母親が絶命したことで生まれたジャンダラは、父親から恨み嫌われる。以後、心の拠りどころのない彼は母の愛情に憧れながら成長。時が経ち、青年となった彼は再び父と衝突し、驚愕すべき自らの出生の秘密を明かされる。", "2001-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHRISTO　AND　JEANNE−CLAUDE　VERHULLTER　REICHSTAG　１９７１−９５　“DEM　DEUTSCHEN　VOLKE”", "議事堂を梱包する", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "N.Y.を拠点に活動する芸術家クリスト＆ジャンヌ・クロードがベルリンの旧帝国議会議事堂を梱包するという、一大プロジェクトを記録したドキュメンタリー。ドイツが東西に分断されていた1971年にスタートし、実現までに24年の月日を費やした、この前衛アートの完成までの道のりを追う。政治家たちの説得に奔走するクリストらの姿を通して、芸術と政治のかかわりを浮き彫りに。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Repo Man", "レポマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "パンク・スピリット溢れるイギリスの鬼才アレックス・コックス監督のデビュー作。仕事をクビになり退屈な日々を送っていた青年オットー。ある日彼は、ローン未払いの客の車を取り立てる“レポマン”という職業の男と知り合い、仲間として働くことになる。そして高額の懸賞金が掛かった車を差し押さえるが、その中には宇宙人の死体が入っていた……。イギー・ポップによるテーマ曲をはじめ、全編に散りばめられた80年代パンクの数々も必聴。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海の金魚", "海の金魚", "", "", "http://image.eiga.k-img.com/images/movie/55175/315x210.jpg?2473", 116, "鹿児島の錦江湾で毎年開催されている「鹿児島カップ火山めぐりヨットレース」に参加する、5人の高校生たちの姿を描いた青春ドラマ。主演は「愛流通センター」の入来茉里、「ジャージの二人」の田中あさみ。父親が海で行方不明となって以来、父のヨットでひとり暮らすようになった少女ミオと、ヨットの練習中に親友を事故で亡くしたヨットの天才キヨミ。2人はそれぞれの悩みを乗り越えるため、3人のチームメイトに支えられながら、過酷なヨットレースに挑戦する。", "2010-04-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まんだら屋の良太", "まんだら屋の良太", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "『漫画サンデー』に連載され、第10回漫画家協会賞を受賞した同名漫画の映画化。九州・九鬼谷温泉の旅館“まんだら屋”を舞台にして、そこの一人息子で高校三年生の良太と、彼の仲間たちの青春を描く作品。若者たちの性への目覚めをコミカルに描いた小品。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Due occhi diabolici（Two Evil Eyes）", "マスターズ・オブ・ホラー／悪夢の狂宴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「ヴァルドマー事件の真相」　強欲な妻は良心に欠けた医師と組み、余命幾ばくもない大富豪バルドマーから財産を巻き上げようとするが……。", "1990-01-01", "4,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("完全な遊戯", "完全な遊戯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "『太陽の季節』で太陽族ブームを巻き起こした石原慎太郎が、自ら“太陽族の終点”を描いたという原作を職人・舛田利雄が監督。話は、いくら学生時代に無茶をしても、結局のところ若者は職に就き、世間の中に埋没していくというもの。当時与えた衝撃力が現在通用するかという点では、多少の疑問も。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Semi-Pro", "俺たちダンクシューター", "", "", "http://image.eiga.k-img.com/images/movie/53565/315x210.jpg?2577", 90, "「俺たちフィギュアスケーター」のウィル・フェレルによる痛快スポ根コメディ。1970年代前半のプロバスケットボールリーグは、競技性重視のNBAとエンタメ主義のABAが人気を二分していた。ABAのダメチーム「フリント・トロピックス」のオーナー兼コーチ兼選手でソウルシンガーのジャッキー・ムーンは、過去の栄光にすがりながらも何とか試合を盛り上げようと奔走するが……。共演にウディ・ハレルソン。", "2008-08-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("No Strings Attached", "抱きたいカンケイ", "", "", "http://image.eiga.k-img.com/images/movie/55795/315x210.jpg?4468", 108, "ナタリー・ポートマン＆アシュトン・カッチャー共演のラブコメディ。都市の病院に勤務する医者のエマ（ポートマン）は、日々の仕事に追われ、恋愛をする暇のない独身女性。そんなある日、ひょんなことから長年の親友アダム（カッチャー）と一線を超え、肉体関係を持ってしまう。2人は恋愛感情も嫉妬もデートもない、カラダだけの関係をしばらく続けていたが、そのうちお互いの気持ちに変化が生じて……。監督は「ゴーストバスターズ」のアイバン・ライトマン。共演にケビン・クライン。", "2011-04-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SEARCH", "山河遥かなり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "大戦直後のドイツを舞台にした感動のドラマ。ナチスへの嫌悪と恐怖から失語症となった少年と、心優しい米兵の魂のふれあいを軸に、息子を必死に捜し求める母の姿を交錯させていく。名匠F・ジンネマンによるスイス製作のMGM映画で、M・クリフトが名優ぶりを発揮している。", "1948-01-01", "26,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪党社員遊侠伝", "悪党社員遊侠伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "漫才、落語界のお笑い陣を動員させたサラリーマン喜劇。日の丸不動産の悪党社員である牧、なべ、小野は横浜営業所に左遷されるが、そこには悪党社員の模範ともいうべき立川がいた。この４人の悪党ぶりと、それが効を奏してのうのうと出世していく様子を描く、かなり毒のある作品。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Io, Don Giovanni", "ドン・ジョヴァンニ　天才劇作家とモーツァルトの出会い", "", "", "http://image.eiga.k-img.com/images/movie/55305/315x210.jpg?2782", 0, "1763年、元聖職者の劇作家ダ・ポンテは、放蕩生活が原因でベネチアを追放され、新天地を求めてウィーンへとやって来る。そこでダ・ポンテは、友人カサノバから新人作曲家のモーツァルトを紹介される。オペラに興味を持っていたモーツァルトは、詩人として評価の高いダ・ポンテに台本を依頼し、やがて「フィガロの結婚」が完成する。次回作としてダ・ポンテが着手したのは、半自伝的な女たらしの物語「ドン・ジョヴァンニ」だった。モーツァルトを刺激し続けた、稀代の劇作家ロレンツォ・ダ・ポンテの知られざる姿を描く。", "2010-04-10", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHOUANS！", "ソフィー・マルソーの愛、革命に生きて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 204, "18世紀のフランス革命を描いた一大歴史叙事詩。ケルファデック伯爵の息子オーレル、養子のセリーヌ、タルカンは３人仲良く育つ。やがてフランス革命の勃発を機に、タルカンはパリへ、オーレルは新大陸アメリカへと旅立つ。やがて革命後の混乱のなか、３人は再会し、自分が信じる道を進んでいく。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POISON", "ポイズン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "サンダンス・フィルム・フェスティバルでグランプリに輝いた異色作。刑務所内の愛憎を描いた“ゲイ”、空へと消えた少年の姿を追う疑似ドキュメンタリー“ヒーロー”、伝染病に感染した科学者の悲劇を描く“ホラー”。これら３つのエピソードが入り乱れて展開し、異端を排斥する現代社会の構造を批判していく。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タカダワタル的", "タカダワタル的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "デビュー以来35年間の長きにわたってマイペースな活動を続けてきた伝説のフォークミュージシャン、故高田渡の素顔に迫るドキュメンタリー。高田渡に惚れ込んだ俳優の柄本明が、ドキュメンタリー製作の発起人となり、またナビゲーターとして出演も果たしている。穏やかな笑いを振りまく高田渡の人柄に興味を抱かずにはいられない、摩訶不思議な魅力を持った一本。フォーク全盛の1970年に開催された“中津川フォークジャンボリー”で歌う高田渡。それから30年余が過ぎ、高田渡は吉祥寺を拠点にして独自のやり方で音楽活動を続けていた。その私生活に５ヵ月密着して見えてきたものとは……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("七七事変：JULY ７ INCIDENT", "沈黙の鉄橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "日中戦争の発端となった“蘆溝橋事件”を壮大なスケールで描いた戦争スペクタクル。1937年。中国国内では、国民政府と共産党が対立を続けていた。一方日本軍は華北地方に進出し、北京に駐屯する第29軍との緊張を高めていた。そして運命の７月７日の深夜、一発の銃声が響きわたる。行方不明になった兵士の捜索を口実に、日本軍は要塞内部の捜索を要求。拒否した中国側との間に武力衝突が発生したのだ。そして以後８年にも及ぶ、世界大戦へと歴史の歯車は回っていくのだった。", "1995-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLOB", "マックィーンの絶対の危機", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "宇宙から飛来したゼリー状の不気味な生物と人類の壮絶な死闘を描いたSF映画。偶然、町はずれの山中で謎の生物に遭遇する青年スティーブ。宇宙生物の執拗な追跡を逃れ、九死に一生を得る彼であったが……。S・マックィーンの熱演が見もの。1988年に「ブロブ」のタイトルでリメイクされている。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Relatos", "ストーリーズ", "", "", "http://image.eiga.k-img.com/images/movie/54965/315x210.jpg?4587", 122, "主婦として平凡な生活を送りながら短編小説を書いているロサリオ。夜になると湧き上がってくる恐怖心に悩まされている彼女は、セラピストのアドバイスで自分の作品を出版社に持ち込むことに。ストーリーを紡ぐことによって、ロサリオは少しずつ自身の内面と向き合っていき……。ロサリオの日常に彼女が書いたストーリーを効果的に挿入することによって独自の世界観が構築され、やがて大きな感動が訪れるヒューマン・ドラマ。", "2009-10-17", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレヨンしんちゃん／嵐を呼ぶ！夕陽のカスカベボーイズ", "クレヨンしんちゃん／嵐を呼ぶ！夕陽のカスカベボーイズ", "", "", "http://ecx.images-amazon.com/images/I/61dGKIGKFPL._SL210_.jpg", 96, "GW恒例となった人気アニメ・シリーズの第12作。無敵の幼稚園児しんちゃんが、今回は家族や友だちともども、西部劇映画の中にまぎれ込んでしまう。ナゾに満ちた世界で、失われていく現世の記憶をたぐりよせつつ奮闘する、彼の大冒険がスリリングに展開。奇想天外なギャグはもちろん、マカロニウエスタンにオマージュを捧げたというビジュアルにも注目。しんのすけは、映画館で遊んでいたが、いつの間にか自分以外の友だちがいなくなってしまう。その夜、しんのすけは、友達を探しに再び映画館にやって来るが、西部劇の映画が上映されると、その映画の世界にスリップしてしまう。", "2004-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAHRENHEIT 451", "華氏451", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "“映像の詩人”トリュフォーと“SFの詩人”ブラッドベリの顔合わせで、読書を禁じ、人々の思想を管理しようとする近未来社会を描いた傑作SF。本を禁止した社会で、発覚した本を焼却する焚書隊の一員である主人公は、本を隠し持っているとされる一人の女性と恋に落ちる。彼女を通して“本”の魅力を知った男は、初めて精神的な豊かさを味わう……。芸術・精神文化にはてしない憧憬を抱くトリュフォー監督が、物質主義・全体主義社会への痛烈な批判を試みた問題作。ローグ撮影によるあざやかな色彩表現も手伝い、それまでの作品とは、一味違った仕上がりをみせる実験的意欲作。", "1966-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲゲゲの女房", "ゲゲゲの女房", "", "", "http://image.eiga.k-img.com/images/movie/54605/315x210.jpg?1295", 119, "「ゲゲゲの鬼太郎」をはじめ数々の名作を生み出してきた漫画家・水木しげるの妻・武良布枝が夫婦の歩みを記した自伝を映画化。見合いからわずか5日で結婚したしげると布枝が、どん底の貧乏生活を乗り越えて人気漫画家となるまでの物語を、昭和の懐かしい風景とともにつづっていく。「紀子の食卓」の吹石一恵が布枝に、人気脚本家・演出家として知られる宮藤官九郎がしげるに扮する。監督は「私は猫ストーカー」の鈴木卓爾。", "2010-11-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン　漆黒の追跡者（チェイサー）", "名探偵コナン　漆黒の追跡者（チェイサー）", "", "", "http://image.eiga.k-img.com/images/movie/54315/315x210.jpg?1655", 110, "コミック部数1億2000万部を突破した人気シリーズ「名探偵コナン」の劇場版アニメ第13弾。東京、神奈川、静岡、長野で6件の殺人事件が発生。いずれも被害者の近くに麻雀牌が残されていたことから連続殺人事件と断定され、探偵の小五郎も警察の捜査会議に参加することに。宿敵“黒の組織”のメンバーが会議に潜入していたことに気付いたコナンは、単独捜査に乗り出すが……。ミュージシャンのDAIGOがゲスト声優として参加。", "2009-04-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CURSE OF FRANKENSTEIN", "フランケンシュタインの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "イギリスのハマー・プロ製作のホラー映画の記念すべき第１作。T・フィッシャー監督、P・カッシング、C・リーのトリオの出発点でもあり、“フランケンシュタイン”シリーズの第１作でもある。", "1957-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Born into Brothels: Calcutta's Red Light Kids", "未来を写した子どもたち", "", "", "http://image.eiga.k-img.com/images/movie/53985/315x210.jpg?3160", 85, "インドのカルカッタの売春窟で暮らす子供たちを見つめたドキュメンタリー。第77回アカデミー賞最優秀ドキュメンタリー賞受賞。売春窟に暮らす子供たちは売春婦になるように運命づけられている。売春窟に取材で訪れた写真家のザナ・ブリスキは、子供たちの運命を目の当たりにし、彼らを何とか救い出そうと写真教室を開く。子供たちは初めてファインダー越しに見る外の世界を、夢と希望で写真に写し出していく。", "2008-11-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Get Him to the Greek", "寝取り男はロック★スター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "2008年のヒット作「寝取られ男のラブ♂バカンス」からのスピンオフ映画。レコード会社のアシスタントとして勤務するアーロンは、社長にロンドンからロックスターのアルダス・スノーを72時間以内にロサンゼルスに呼び寄せて、アルダスが10年前に伝説的なパフォーマンスを行った有名なグリークシアターで、復活ライブをさせるよう命じられるが……。出演はジョナ・ヒル、ラッセル・ブランド、ローズ・バーン。監督は前作同様ニコラス・ストーラー。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒いドレスの女", "黒いドレスの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "原田知世が大人の女優への脱皮を図り、北方謙三のハードボイルドな世界に挑戦。国外に逃亡しようとするヤクザ、それを助けるバーテンダー。二人の間に、危険な謎を秘めた女が現れ、物語は展開する。過去を持つ女を演じるには知世は役不足だったが、菅原文太は久しぶりのヤクザを演じ、すご味の利いた演技を披露した。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EDDIE MACON’S RUN", "愛に向って走れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "無実の罪を着せられた男が、愛する妻と息子に会うため刑務所を脱走。メキシコ国境までの道のりを、ひたすら走り続ける。獲物を探す狩人のごとく、囚人護送人の執拗な追跡が始まる。果たしてたどりつけるのか……。過激なカーチェイスが展開するアクションの連続が見もの。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猛龍　Dragon Squad", "ドラゴン・スクワッド", "", "", "http://image.eiga.k-img.com/images/movie/33695/315x210.jpg?4685", 111, "国際テロリストに戦いを挑む5人の国際警察官の姿を描いた、香港版「Gメン'75」ともいうべきポリス・アクション。ある重要裁判の証人として、香港裏社会の実力者タイガー・デュンの弟パンサーが出廷することになる。世界各国から集められた5人の精鋭がその護送にあたるが……。主演はF4のバネス・ウーと「インファナル・アフェア」シリーズのショーン・ユー。共演には「カンフーハッスル」のホァン・シェンイーら豪華キャストが集結。", "2006-10-14", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A TREE GROWS IN BROOKLYN", "ブルックリン横丁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "20世紀初頭のニューヨークの下町に暮らす、貧しい一家の哀歓を描く、E・カザン監督のデビュー作。飲んだくれの父のお陰で苦労が絶えない、アイルランド移民のノーラン家の人々の姿を、長女フランシーの視点から綴る。叔母の離婚騒動のあれこれ、父の哀しい最期、そして妹の誕生……。悲喜こもごもの出来事の中に、家族愛がきらめく名編。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AGENT　CODY　BANKS", "エージェント・コーディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "マドンナが製作総指揮を務め、アメリカでヒットしたスパイ・アクション。15歳の敏腕CIAエージェントが難事件の解決に挑む。主人公を演じるのは、アメリカで人気の子役スター、F・ムニッズ。ごく普通の生活を送る15歳の少年コーディ・バンクスには、家族も知らない秘密があった。彼は、CIAの優秀なティーン・エージェントだったのだ。ある時、女性エージェントが、コーディに重大な任務を伝えに来るが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SERVANT", "召使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "裕福な貴族青年トニーは貴族の娘スーザンと結婚するため南米からロンドンに戻るが、雇った召使バレットに惚れ込んでいっさいを任せてしまう。バレットは愛人を妹と称して邸に住み込ませ、トニーを誘惑させたり、麻薬を教えたりして堕落させ、ついに生活は破滅状態になる。ホモセクシュアル、主人と召使の階級逆転、イギリス貴族の哀れな末路をスキャンダラスに描いて公開当時物議を醸した。硬派ロージー監督と劇作家H・ピンターはこれが初顔合わせ。", "1963-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LION KING", "ライオン・キング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「美女と野獣」「アラジン」とヒット作を生み出し、1990年代に入って第２の黄金期を迎えたともいわれるディズニー・アニメのスタッフが、アフリカの大平原を舞台に描く愛と勇気の物語。ジャングルの王として君臨するライオン、ムファサに息子シンバが生まれた。ムファサの弟スカーは、王の座を狙い、ムファサ暗殺をたくらむ。まんまと罠にはまったムファサは絶命。シンバは故郷を追われ、王国は荒廃した。シンバは父の仇を討つことができるのか？　ミュージカル・シーンをふんだんに盛り込んだ軽快な構成と、W・ゴールドバーグ、J・アイアンズら名優が声を吹き替えた表情豊かなキャラクターが楽しいヒット作。後に続編、番外編（いずれも未公開）が製作された。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スレイヤーズごぅじゃす", "スレイヤーズごぅじゃす", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 32, "ズッコケ魔導士コンビ、リナとナーガの活躍を描くSFファンタジー・アニメの劇場版シリーズ第4作。彼女たちのコミカルなやり取りやアクションも、よりゴージャスに。リナとナーガの目前に突然、上空からドラゴンに乗った少女が降り立ち、トラブルを巻き起こす。戦闘モードに入ったリナとナーガだが……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCK AND ROLL SHOW", "ロックンロール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ロックン・ロールの黄金期に人気の絶頂をきわめた歌手を一堂に集めた“ロック・アンド・ロール・ショウ”のライブ・フィルム。ロンドンのサッカー競技場ウェンブリー・エンパイア・スタジアムに数十万の観客を集め、11時間30分にわたって行われた。M・ジャガーも顔を出すファン必見の１本。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES SE´DUCTEURS／I SEDUTTORIDELLA DOMENICA", "サンデー・ラバーズ／…というわけで恋の策略おしえます!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "４大都市を舞台に、それぞれの国の監督が描くセクシー・コメディ。タクシー運転手の不倫が招く受難（ロンドン編)、倒産寸前の会社を救おうと立ち上がる男の純愛（パリ編)、セックス恐怖症の男の哀れさ（ロサンゼルス編)、初老の男が昔のガールフレンドに電話をかけまくるおかしさ（ローマ編）の４編からなるオムニバス映画になっている。", "1980-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙の法則", "宇宙の法則", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "家業を継ぐべく、裸一貫故郷へ戻った主人公が、人生の再スタートの場に臨んで味わう悩み、苦汁、情熱などを織り込んだ井筒和幸監督による人間ドラマ。自分のブランドをもつほどのトップ・デザイナー・本木良明。彼は、親父の死を機会に、名前も恋人も捨てて、家業の機屋を継ぐために故郷の愛知県・一宮へと帰る。母親や妹のとまどい、協力を申し出る実業家や友人、兄の離婚騒ぎなど、多彩な人々とのふれあいを経て、良明が迷いからふっきれた時、彼に突然の死が……。自分に素直に生きようと努める良明。その心がピュアになることで“宇宙の法則”という死に絡めとられるさまを、井筒が静かな語り口の中に描ききっている。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tropic Thunder", "トロピック・サンダー　史上最低の作戦", "", "", "http://image.eiga.k-img.com/images/movie/53225/315x210.jpg?4714", 107, "ベン・スティラー、ロバート・ダウニー・Jr.、ジャック・ブラックの3人が落ち目の映画スターに扮したアクションコメディ。東南アジアで撮影中の戦争映画「トロピック・サンダー」は主演スター3人のわがままにより撮影が大幅に遅延。業を煮やした監督のコックバーンはスター3人を含むメインキャストを騙してジャングル奥地に連行し、ゲリラ撮影を敢行するが……。スティラーが製作・原案・脚本・監督・主演の一人5役を担当。", "2008-11-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・仁義の墓場", "新・仁義の墓場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "深作欣二監督の傑作「仁義の墓場」で描かれた実在の極道、石川力夫の生涯を、三池崇史が映像化。度胸の良さを買われてヤクザとなった若者、石松は殺人罪で服役。５年の刑期を終えた後も激情的な性格は直らず、誤解から組長を射殺して逃亡するハメになり、さらに狂犬のように暴走的な行為に走る……。凶暴なヤクザ像をバイオレンスたっぷりに描きつつ、一本気な愛の逸話を交え、エモーショナルな作品に仕上げている。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ももいろそらを", "ももいろそらを", "", "", "http://image.eiga.k-img.com/images/movie/57455/315x210.jpg?1529", 112, "本作が長編デビューの新鋭・小林啓一監督が、大金を拾った女子高生と友人が巻き起こすアクシデントを通して、現代に生きる若者の瑞々しい表情を全編モノクロームの映像で描いた青春ドラマ。新聞の採点を日課にしている高校生の川島いずみは、ある日、大金の入った財布を拾う。財布の持ち主を無事に探し当てたいずみだったが、そこから事態は思わぬ方向へ動き始め……。2011年・第24回東京国際映画祭「日本映画・ある視点」部門で作品賞を受賞した。", "2011-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDERGROUND ACES", "パーキング・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "M・グリフィスや、“ポリスアカデミー”シリーズの“人間効果音”で人気のM・ウィンスローらが出演しているコメディ作品。ビバリーヒルズの高級ホテルの駐車場。そこで働く人間たちはハチャメチャな連中ばかり。そんな彼らが、客の恋のキューピッド役を買って出たため大騒動に……。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Scalphunters", "インディアン狩り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "バート・ランカスター扮する猟師がインディアンに捕らえられ、黒人奴隷と引換えに毛皮を奪われる。怒った猟師は黒人奴隷と共にインディアンの後を追うが、毛皮の行方はインディアンから盗賊へと二転三転。そして黒人奴隷は猟師よりもはるかに教養が高く……。軽いタッチのウェスタン・コメディ。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANNA", "アンナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "1960年代のパリを舞台に、恋騒動をポップに綴ったミュージカル・コメディ。広告会社の若社長セルジュが、広告写真の片隅に写っていた女性にひと目惚れ。一方、パリに上京したばかりの当の女性アンナは、セルジュの会社で職を見つけた。そんなこととはつゆ知らず、セルジュはTVや広告を駆使して憧れの女性を見つけ出そうとするが……。ゲンズブールの歌を含む音楽や、A・カリーナのカラフルなファッションも見どころ。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL SUR", "エル・スール −南−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "闇の中で時計の音がする。夜明けに少女エストレリャは父の思い出をたどり始める。家の風見のカモメはいつも南（エル・スール）をさして動かなかった。父は霊力のある振り子を使って水脈を見つけることができた。聖体拝受式の時に一緒に踊った父。父を思うとき、その横顔には謎めいた深い悲しみが影を落としていた……。エル・スール、若い日を過ごし、そしてそこを捨てた父の故郷。娘を通して見た父親の肖像にスペイン内戦の影が浮かび上がる。回想を間にはさみながら、ヒロインの成長と父親の苦悩を、深味のある美しい映像で綴った、静かな感動を誘う作品だ。名作「ミツバチのささやき」以来、V・エリセが10年ぶりに取り組んだ第２作。", "1983-01-01", "9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー響鬼〈ひびき〉と　７人の戦鬼", "仮面ライダー響鬼〈ひびき〉と　７人の戦鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "“和”のテイストあふれる異色の設定と展開で、ヒーロー活劇の枠を超えて大きな話題を呼んでいるTVシリーズの劇場版。舞台を戦国時代に設定し、全国各地の5人の“ご当地ライダー”が登場するのも楽しみだ。戦国時代、まだ“鬼”と呼ばれていた仮面ライダーは、悪の手から人間たちを守ったことにより協力関係を築くようになっていく。", "2005-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌女おぼえ書", "歌女おぼえ書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "旅の女役者、歌女は、奇妙な縁から借金を残して四散した問屋の息子と婚約することになる。彼が上京するため、義弟妹を一時あずかることになった歌女は、自分の婚約者に許嫁がいたことを知る……。物語はありきたりな人情ものメロドラマではあるが、さらりとした演出でさわやかなあと味が残る佳作。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLAM", "SLAM", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ワシントンDCにある低所得者住宅ドッジ・シティーを舞台に、ドラッグとギャングの抗争に巻き込まれる青年たちの姿を描く。マリファナを売って生計を立てているレイは、レコード・レーベルを始めるという夢を抱いていた。そんなある日、親友のマイクが敵対するギャングに襲われて重傷を負い、その場から立ち去ろうとしたレイは投獄されてしまう。獄中での囚人同士の対立、劣悪な生活環境に行き場のない怒りを感じる彼は、その思いを詩で表現する。様々な人種が集うポエトリー・カフェで、詩の朗読によって鬱屈した思いをぶつける若者たちの心の叫びが胸に響く。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AMOROUS ADVENTURES OF MOLL FLANDERS", "モール・フランダースの愛の冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "孤児院で育ち、市長の家で召使いをする少女、モール・フランダース。結婚するという約束でその家の長男に身を任せるが、金貨をもらっただけ。今度は次男と一緒になるが深酒がたたって次男はあっけなく死亡、一文無しの未亡人になった彼女は……。封建社会にほんろうされつつ、ついに幸せをつかんだ女性の物語。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江梨子", "江梨子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "伊豆の田舎から姉の嫁ぎ先に越してきた高校生の茂が、同じ伊豆出身の江梨子と出会う。やがて、彼は孤児という逆境の中で明るく生きる彼女と恋に落ちるが、無理解な大人たちによって引き裂かれる……。橋幸夫の大ヒット曲『江梨子』を基に、橋自身が主演した悲恋物語。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("豪快三人男", "豪快三人男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "関ヵ原合戦後、めでたく改め役の試験にパスして任官した大矢主水（ダイヤモンド）、島根兼四郎（すまね、かんにんしろ）、石川郷右衛門の“豪快３人男”が、大阪夏の陣で華々しい武勲を立てるまでを描いたコメディ・タッチのサクセス・ストーリー。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIO—FORCE １ SPECIES", "バイオ・スピーシーズ　新生命体誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "時は2003年。月へ向けて、バイオ遺伝子実験の廃棄物を搭載したロケットが打ち上げられたが、失敗し墜落。米陸軍特殊部隊・生物班が廃棄物の処分のために派遣された。墜落地点にたどり着き、任務を無事果たしたかに見えたその時、部隊を率いるホリンジャー中尉が突然、部下に向かって機関銃を乱射し始める。危うく一命を取り留めたトロッター軍曹は、まったく別の生き物に変貌してしまったホリンジャーを見て驚愕する。実はその廃棄物は、軍部が極秘に実験していた殺人マシーンを造り出すためのDNAウイルスだった。ホリンジャーが一人で、怪物と化した隊員に挑むSFバイオレンス。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WAR OF THE ROSES", "ローズ家の戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "“ロマンシング・ストーン”シリーズの主演トリオが３度息の合ったコンビぶりを見せるすさまじい離婚戦争。ひとクセある俳優D・デヴィートの「鬼ママを殺せ」に続く２作目の監督長編劇映画で、凝ったカメラ・アングルが出色の出来。結婚17年たって夫にこれ以上我慢できない妻と、彼女に未練たっぷりの夫のエスカレートするいやがらせの数々。もはや戦争状態となった二人の闘いを、ブラックな笑いの中に描く。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bottle Dream", "ボトル・ドリーム　カリフォルニアワインの奇跡", "", "", "http://ecx.images-amazon.com/images/I/51MkwsXodIL._SL210_.jpg", 109, "ワインファンの間で語り継がれてきたパリ・テイスティング事件をモチーフに描き、全米でスマッシュヒットを記録したヒューマンドラマ。1976年、パリのワイン評論家スパリエは、良質なワインを求めてカリフォルニアのワイン生産地ナパバレーを訪れる。そこでスパリエは、最高のシャルドネを作ろうと奮闘するワイナリー経営者ジムとその息子ボーに出会い……。出演は「アンストッパブル」のクリス・パイン、「ハリー・ポッター」シリーズのアラン・リックマンほか。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BODYGUARD", "ボディガード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "男は世界でも屈指のボディガード。女は華やかなトップスター。一通の脅迫状を皮切りに、彼女に忍び寄る死の影。やがて守ること、守られることが二人の愛の証しとなっていく……。人気俳優K・コスナーと、映画初出演のW・ヒューストンという異色の顔合わせで描いたサスペンス・ラブ・ストーリー。主題歌との相乗効果もあり、日本では本国アメリカ以上の大ヒットに。", "1992-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWOON", "恍惚　ヴァレンティノより美しい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1920年代のシカゴで実際に起きた事件をもとに、ホモセクシャルの青年二人の愛憎を、退廃的な映像美で描く。小さな犯罪を繰り返すことに飽きたネイサンとリチャード。お互いの愛を確認するかのように、二人は子供を誘拐し、殺害する。だが、この稚拙な犯行は、たちまち警察の知るところとなる……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPER８1/2", "スーパー８1/2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "落ちぶれたゲイ・ポルノスターのバイオグラフィーを、女性新進映画作家の目を通して綴るポップなインディーズ映画。主人公はカナダ映画界の異端児B・ラ・ブルース監督自身が演じ、アンディ・ウォーホルの映画をパロった自作自演のゲイ・ポルノ映画を盛り込みながら、1970年代的なキッチュなセンスで描く。ポルノ業界から追放されたブルースを映画に撮ろうとする女性監督のグギー。二人はそれぞれの思惑を抱きながら撮影に入るが……。", "1994-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("辛亥革命", "1911", "", "", "http://image.eiga.k-img.com/images/movie/56505/315x210.jpg?2640", 122, "ジャッキー・チェンの映画出演100本目で、中華民国建国のきっかけとなった1911年の辛亥革命から100年を記念して製作された歴史大作。衰退する清王朝を憂い、新しい国を作るため立ち上がった孫文の参謀を務める黄興は、革命軍を率い総督府に攻めこむ。しかし、事前に情報を入手していた朝廷側に厳戒態勢を敷かれ、反乱は失敗に終わる。多くの命を失った黄興らは戦意を喪失していくが……。「レッドクリフ」の撮影監督を務めたチャン・リーがメガホンをとり、ジャッキーが総監督も務める。", "2011-11-05", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("eatrip", "eatrip", "", "", "http://image.eiga.k-img.com/images/movie/53905/315x210.jpg?3062", 78, "食のトータル・コーディネートを提案するフードディレクターの野村友里が、人と食の関連性を描いたドキュメンタリー。世代や職業を問わず、様々な人へ食に関するインタビューを敢行し、現代人の食生活を多面的に見つめる。浅野忠信、UA、内田也哉子ら芸能界の著名人も多数出演。", "2009-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花影", "花影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "大岡昇平の小説を川島雄三が映画化した作品。銀座のバーの女と彼女をとりまく男たち。夜の街に生きる女の哀しみと、彼女が死に近づいていくプロセスが描かれる。主演の池内淳子は長いブランクのあとの映画界復活第１作だったが、難しい役をさりげなくこなしている。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGELS", "エンジェルス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "少年ロジャーにとって、去っていく父親を取り戻すためのジンクス、それは最下位チームのエンジェルスがペナントレースで優勝することだった。彼の必死の祈りが伝わったのか、アル率いる天使たちが夢をかなえようとスタジアムで大活躍し始める。C・ロイドが陽気で茶目っ気のある天使を好演。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セクシードリンク大作戦／神様のくれた酒", "セクシードリンク大作戦／神様のくれた酒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "映像作家、本田隆一が、「殺し屋１」の脚本家、佐藤佐吉を迎えて描くドタバタ・コメディ。10代にしてアル中になり悩む少女の再起を、奇想天外なエピソードを交えて綴っていく。アルコール依存症のミキ。20歳の誕生日を機にアルコールを断つ決心をした彼女は、一日酒を我慢すれば二度と酒を見たくなくなるが、一滴でも口にすると顔中の血管が切れ、顔面が爆発するという謎の漢方薬を服用する。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("九ちゃん刀を抜いて", "九ちゃん刀を抜いて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "1986年の日航機墜落事故で惜しくも命を落とした坂本九の全盛期＝1960年代の代表作。岡本一平の小説『刀を抜いて』の映画化。この話は1939年に日活シネマとマキノプロの共作で作られたことがある。飯を食うのも面倒臭いものぐさ三五郎はとうとう親に勘当され、開き直って侠客となるべく江戸へ向かう。ひょんなことから幡随院長兵衛の一の子分・唐犬権兵衛のそのまた子分にとりたてられた三五郎は、吉原の人気花魁・高窓太夫をめぐる恋と意地の争奪戦に巻き込まれて……。陽気で少々おっちょこちょいといった坂本九のキャラクターをベテラン・マキノ雅広がうまく引き出している。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last Days of Frankie the Fly", "フランキー・ザ・フライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "D・ホッパーが映画製作の夢を抱き続けるギャングというユニークな役どころに扮したフィルム・ノワール。ロサンゼルスの裏世界を仕切るサルにコキ使われながら、しがない人生を過ごしてきたフランキー。彼はポルノ監督のジョーイに映画作りへの協力を頼むが、そのことをサルに知られたために悲惨な事件が起こる……。自分をハエ呼ばわりするボスに、映画を作ることで復讐を挑む主人公を、ホッパーが味わい深く好演。彼と心を通わせるポルノ女優にD・ハンナが扮している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Taking of Pelham 123", "サブウェイ123 激突", "", "", "http://image.eiga.k-img.com/images/movie/53205/315x210.jpg?4330", 105, "1974年にも「サブウェイ・パニック」として映画化されたジョン・ゴーディのベストセラーを、トニー・スコット監督＆デンゼル・ワシントンが4度目のコンビで再映画化。冷徹な犯人グループのリーダー役でジョン・トラボルタが共演。ニューヨークの地下鉄ペラム123号が乗っ取られ、犯人グループは乗客を人質に身代金1000万ドルを要求。地下鉄運行指令室に勤務するガーバーは、犯人からの無線を受けたことから、そのまま交渉役を務めることになり……。", "2009-09-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENTER THE BLOOD RING", "闘鬼 TOHKI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ロサンゼルスのアンダーグラウンド・アリーナには、マーシャルアーツの頂点を極めようと、ファイターたちが結集している。ルークは、脳腫瘍を患う息子の治療費を稼ぐために、そのリングにあがった。勝って賞金を手にするためには、メキシコ、オランダ、日本など世界中からやって来る強豪を倒さなくてはならなかった……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAST TIMES AT RIDGEMONT HIGH", "初体験／リッジモント・ハイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "南カリフォルニアのリッジモント高校に通うリンダとステーシーは、キャンパスもバイトも一緒の大の仲良しコンビ。好奇心にあふれた彼女たちの青春を軽いタッチで描いた学園もの。ジャクソン・ブラウンをはじめとするロック・ミュージシャンによるオリジナル24曲が楽しい。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRESH HORSES", "想い出のジュエル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "オフ・ブロードウェイで人気を博した戯曲の映画化。社交界に出入りするリッチな大学生マット。彼はある日、ジュエルという女性と出会い、彼女に惹かれていく。しかし、マットにはフィアンセがおり、ジュエルには結婚した経験があった。M・リングウォルドが初の汚れ役に挑戦。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・女の泣きどころ", "喜劇・女の泣きどころ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "酒を飲んでは失敗し、男でも失敗で、日本全国あちこちの小屋を転々と渡り歩くさすらいのストリッパー二人組。“キミたちには向上心がないのか！”と怒鳴られても、ドジぶりは相変わらずで……。太地喜和子の“酔態”ぶりが艶っぽい。喜劇・女シリーズを受け継いだコメディ。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メゾン・ド・ヒミコ", "メゾン・ド・ヒミコ", "", "", "http://image.eiga.k-img.com/images/movie/1515/315x210.jpg?4293", 131, "「ジョゼと虎と魚たち」の監督・犬童一心と脚本・渡辺あやのコンビ作第2弾。ゲイの父親が家を出て、母は病死し、ひとりで暮らす沙織。彼女のもとに、ある日、父親の恋人である青年が訪ねてくる。青年は沙織に彼女の父親は癌で死期が近いことを告げ、父親が経営するゲイのための老人ホーム、メゾン・ド・ヒミコで働かないかと誘う。最初は借金返済のために手伝いを始めた沙織だったが……。人気俳優オダギリジョーと柴咲コウが初共演。", "2005-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女王蜂の怒り", "女王蜂の怒り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "神戸の波止場をはさんで海堂組と竜神組はことごとく対立していた。竜神組の副長・剛田は海堂組の女親分・ゆりの体とシマを狙っていたが、“ハリケーンの政”と名乗る流れ者がゆりを救う。石井輝男の粋な演出が冴えわたる。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nang Mai", "ニンフ", "", "", "http://image.eiga.k-img.com/images/movie/55035/315x210.jpg?4695", 94, "浅野忠信主演作「地球で最後のふたり」「インビジブル・ウェーブ」を手掛けたことで日本でも知名度の高いタイ人監督ペンエーグ・ラッタナルアーンによるファンタジック・ドラマ。キャリアウーマンのメイは、すっかり冷めた関係となっているカメラマンの夫に連れられて森へキャンプに訪れる。彼女を置いて森の奥深くへと入り込んでしまった夫は、神秘的な森の中で奇妙な体験に見舞われ……。カンヌ国際映画祭「ある視点」部門にて上映。", "2009-11-25", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLASSE TOUS RISQUES", "墓場なき野郎ども", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "J・ジョヴァンニの小説をもとに、C・ソーテが映画化したフィルム・ノワール。欠席裁判で死刑を宣告され、フランス中を逃げ回る二人のギャング、レイモンとアベル。相棒のレイモンが倒れ、妻のテレーズも死んだ。二人の子供を抱えたアベルは、なおも逃亡を続ける……。J・P・ベルモンドとL・ヴァンチュラが、暗黒街の男の生きざまを、雰囲気たっぷりに演じている。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wages of Fear (Sorcerer)", "恐怖の報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "　南アメリカにある油田が反政府ゲリラの仕業で爆発。この火災を鎮火させるため、ニトログリセリンを用いることに。それをトラックに積み、現地まで運ぶという危険極まりない作業を、ドミンゲスを始めとする4人の男たちが報酬目当てで請け負った。彼らは皆犯罪に関わった悪党ばかり。悪路を行く男たちの運命は？　アンリ・ジョルジュ・クルーゾー監督の同名作をリメイク。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZEPPELIN", "ツェッペリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "第一次世界大戦中にドイツ軍が兵器として完成させた巨大飛行船をめぐり、イギリスの勇者が決死のスパイ戦を繰り広げる、という戦争スペクタクル。ドイツ系英国人のジェフリー中尉は、ダブルスパイを装って敵陣に乗り込み、ツェッペリン計画の首謀者に迫る。彼はそこで飛行船の設計を担当した平和主義者のアルツシェル夫妻に出会い、敵側の将校にもただ終戦を願う者があるのを知って少なからず驚く。が、従順すぎるジェフリーに不審を抱いたドイツ側は、彼をツェッペリン号に乗せて英国本土爆撃の道案内を強要する。味方に連絡するすべもなく、刻一刻と迫る目的地。果たして彼と英国の命運やいかに？", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Greetings", "ロバート・デ・ニーロの ブルーマンハッタン／BLUE　MANHATTAN　II・黄昏のニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "　60年代、ニューヨーク・グリニッチビレッジを舞台に、シャイなポール、奇人のロイド、フィルムメーカーのジョンといった3人の若者たちの姿を通じて、徴兵、セックス、ケネディ暗殺、そしてカウンターカルチャーといったテーマを風刺したオフビート・コメディ。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大奥　百花繚乱", "大奥　百花繚乱", "", "", "http://image.eiga.k-img.com/images/movie/54025/315x210.jpg?3218", 0, "若手俳優集団D-BOYSが主演を務める大河ロマンシリーズ第2部。「シャカリキ」の鈴木裕樹が徳川三代将軍の家光を演じている。寛永20年、江戸城にある男子禁制の“裏大奥”と呼ばれる一角があった。お春は大奥へ向かう途中で家光と出会い、見初められるが、家光の側室から反感をかってしまい……。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("婚約指輪", "婚約指輪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "病弱で療養中の夫に代わって銀座で貴金属店を営む妻が、夫を担当するたくましい医者に惹かれていく。この作品の製作された1950年に一時渡米（俳優としては戦後初）し、話題となった田中絹代の帰国第１作。彼女がよろめく相手の医者役には、デビューまもない三船敏郎が抜擢された。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUCH ZWERGE HABEN KLEIN ANGEFANGEN", "小人の饗宴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "荒野の中にある小人のための福祉施設。その施設で、ある日、懲戒処分を受けた13人の小人たちが反乱を起こす。彼らは教師をやっつけ、施設を破壊し、アナーキーな乱痴気騒ぎに興じる……。常なる世界の秩序と管理に対する異なる世界の反乱を描いた異色作。", "1970-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小さなスナック", "小さなスナック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "当時、大ヒットしたザ・パープル・シャドーズの『小さなスナック』をモチーフにした青春もの。学生、GS、カバーガール等がたむろする青山の小さなスナック。人待ち顔の可憐な娘・美樹に一人の男が惹かれ、恋をする。しかし、男を置いて美樹は事故死。おまけに彼女には中年のパトロンがいた……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博徒", "博徒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "東映ヤクザ映画製作２年目にして誕生したヒットシリーズ。天知茂扮する近代的かつ野心的な一家の代貸が昔ながらの組を迫害。忍耐の糸が切れた男は博徒の誇りを守るため殴り込む。単純な物語を迂余曲折に練り上げた脚本と、閉鎖的社会を彩るキャストが見もの。同年に高倉健の「日本侠客伝」も公開された。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROJECT GENESIS", "プロジェクト・ジェネシス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "2130年、上官ハードマンの謀反にあった宇宙輸送艦のキャプテン・パスカルは、エイリアンに攻撃され無人の死の惑星に不時着する。時同じくして、敵対するエイリアンの女性もこの星に不時着。次第に心を打ち解けて行く二人は、新たな生命を生み出す決意を固めた……。異色のSFメロドラマのTVフィーチャー。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BRAVE ONE", "黒い牡牛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "メキシコ農村の一少年がひたむきに育てあげた牛が、闘牛場へ連れ去られるが、愛の力で取り戻される。原作者のR・リッチが“赤狩り”のブラックリストにあったダルトン・トランボであることが1970年代に入って公となった。主題歌もヒット。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いずれの森か青き海", "いずれの森か青き海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「坂の上のマリア」の瀬木直貴監督が、自身の故郷である三重県四日市市を舞台に描くハート・ウォーミングな一篇。工業都市に暮らす16歳の少女の日常を軸に、地方都市を取り巻く様々な問題を浮き彫りにする。三重県の四日市。コンビナート近くの海沿い地区に住む、女子高生アオイは外国のメールフレンドに、自分の街は海に面していてリゾート地のようだと嘘を付いていた。やがてメールフレンドが来日することになり……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Changeling", "チェンジリング", "", "", "http://image.eiga.k-img.com/images/movie/53185/315x210.jpg?2544", 142, "1928年のロサンゼルスを舞台に、誘拐された息子の生還を祈る母親（アンジェリーナ・ジョリー）の闘いを描くクリント・イーストウッド監督によるサスペンスドラマ。息子は無事に警察に保護されるが、実の子でないと疑念を抱いた母親が、腐敗した警察に頼らずに自ら息子の行方を捜して行動を起こし、同時に市長や警察機構を告発する。共演にジョン・マルコビッチ。", "2009-02-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("縄張はもらった", "縄張はもらった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "長谷部安春監督・小林旭主演によるニュー・アクションの代表作。旭扮する主人公は、崩壊寸前の組を立て直すために大組織の命令で、二つの組が対立する地方都市へ鉄砲玉として送り込まれる。主人公は精鋭を集めてゲリラ戦を展開する。藤竜也が脇役ながら、もうけ役で印象に残る。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブルーフィルムの女", "ブルーフィルムの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "かつて松竹のスターだった藤井貢を出演させた向井寛監督のピンク映画の傑作。父親の借金を背負った娘は、その返済のために好色そうな紳士と寝てはそのシーンを８ミリに撮り、恐喝して多額の金を得るのだが……。父親と娘の近親相姦を、鮮烈にユーモラスに描く。藤井がその父親役を見事に巧演。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沓掛時次郎・遊侠一匹", "沓掛時次郎・遊侠一匹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "戦後の股旅映画の最高傑作といえる作品。一宿一飯の恩義のために斬った男の女房と子供を連れて故郷の沓掛村に向かった時次郎が、ヤクザ渡世の掟の愚かさを知りつつ、瀕死の重病にかかった男の女房のために再びドスを握り、戦いの中に自ら飛び込んでいく。ヤクザの世界の非情さが時次郎の目を通して描かれていて、朝吉という一本気なヤクザが手柄を立てようとして逆に惨殺されてしまう場面は強烈な印象を残す。怒った時次郎が相手を次々に切り殺すところにも、加藤監督の怒りに満ちたまなざしを感じずにはいられない。時次郎役の中村錦之助は絶妙の名演で、殺した男の女房に惚れながらも自分の気持ちを押さえつけるところは、宿屋の女将に心中を語る名場面に見事に結実されている。また池内淳子も好演し、時次郎の想いを受け止めながらも、惚れていけない人と自ら言い聞かせて姿を隠すところなど涙を流さずにはいられない。男と女の大恋愛映画としても心を打つ。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Teza", "テザ　慟哭の大地", "", "", "http://image.eiga.k-img.com/images/movie/56225/315x210.jpg?2330", 140, "1970年代に医者を志し故郷エチオピアを離れてドイツに留学していたアンベルブルは、外国での人種差別や皇帝から取って代わった故国の軍事独裁政権を憂い、およそ20年ぶりに故郷の村へと帰ってくる。そこでナゾの女性アザヌと出会ったアンベルブルは、忘れられない過去を思い出す……。「三千年の収穫」などで高い評価を受けるエチオピア人監督ハイレ・ゲリマが、激動の70年代から20年にわたる祖国の光と影を描く。", "2011-06-18", "77,7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Punch-Drunk Love", "パンチドランク・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/1145/315x210.jpg?3369", 95, "「ブギーナイツ」「マグノリア」のポール・トーマス・アンダーソン監督の第4作。食品会社のマイレージ・キャンペーンの盲点を突き、プリンを大量に買い込んで金儲けした男の実話を元に、7人の姉に抑圧されっぱなしの真面目だがサエない男が、運命の恋に出会っての大騒動を描く。アダム・サンドラー扮する主人公と恋人の純愛場面で流れる曲は、ロバート・アルトマン監督の「ポパイ」でオリーブ役シェリー・デュバルが歌った歌。", "2003-07-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Disclosure", "ディスクロージャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "女性上司から部下の男性へのセクシャル・ハラスメントを背景に、企業内に渦巻く陰謀に巻き込まれていく男の姿を描いたマイケル・クライトンによる同名小説を、「レインマン」のバリー・レビンソン監督が映画化したサスペンス・ドラマ。ハイテク企業に勤めるトムの元に現れた新しい上司は、かつての恋人メレディスだった。ある日メレディスの誘惑を拒否したトムは、逆上した彼女から身に覚えのないセクハラで訴えられてしまい……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TAMING OF THE SHREW／LA BISBETICA DOMATA", "じゃじゃ馬ならし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "16世紀北イタリアのパドバ。ベローナの紳士ペトルーキオはじゃじゃ馬娘カタリーナと結婚するが……。シェイクスピアの原作をゼフィレッリ監督がテクニカラーで映画化。リズ＆バートンの夫婦騒動の感も。ミュージカル化された「キス・ミー・ケイト」との比較も面白い。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINAL SCORE", "皆殺しの挽歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "東南アジアの一角を舞台に展開されるハード・アクション。ベトナム帰還兵リチャードは様々な戦歴を残し、今は軍の中枢で働いていたが、ある日政財界の秘密計画を知り、妻子を惨殺されてしまう。怒りに燃え、巨大な組織への復讐を誓う彼。ミサイルを積んだバトル・バイクを駆って、復讐は始まった。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SPIRAL STAIRCASE", "らせん階段", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "突然の火災で夫と子供を失い、それを見た衝撃と恐怖から口がきけなくなった人妻が、姿なき殺人鬼に追い詰められていくという心理サスペンス。ロバート・シオドマク監督により1945年にも映画化されている。夫と子供を失ったヘレンの家の近隣で、５人の障害者ばかりが次々と殺される。口がきけなくなったヘレンの身にも魔手が忍び寄ってきて……。", "1975-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風を聴く　台湾・九ふん物語", "風を聴く　台湾・九ふん物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "台湾北部に位置する街・九ふん。かつて9戸だけの小さな集落だったと言われるこの街は、1890年に金脈が発見されたことをきっかけに人々が集まりはじめ、目覚しい発展を遂げていく。しかし、太平洋戦争や中国国民党による圧政を経て金鉱は徐々に衰退し、1971年についに閉山を迎えてしまう。この街で生まれ育った1人の男性の証言を基に、時代に翻弄された九ふんの歴史を辿るドキュメンタリー。九ふん生まれの父を持つ女優・一青妙がナレーションを担当。", "2007-10-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A BREED APART", "ルトガー・ハウアー　孤高の戦士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ノース・カロライナの湖の孤島。大自然をこよなく愛し、かたくななまでに破壊から守ろうとする男と、対岸で貸ボート屋を営む未亡人の前に、やはり自然を愛しながらも、金のために貴重な白頭鷲の卵を狙う男が現れて……。ハウアー、ターナー、ブースが繰り広げるアクション・アドベンチャー。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うん、何？", "うん、何？", "", "", "http://image.eiga.k-img.com/images/movie/53965/315x210.jpg?3131", 123, "島根県出身の錦織良成監督が「白い船」に続いて撮りあげた島根3部作の第2弾。雲南市で暮らす高校3年生の鉄郎は、父の工場で作られる牛乳を入院中の母の元へ毎朝届けている。そんな彼の現在の悩み事は、卒業後の進路と同級生の多賀子への恋心。クラスメイトの裕二や久美子は鉄郎の恋を実らせようと協力するが……。神話の伝承地が点在する雲南の地を舞台に、そこで生きる人々の日常を紡ぎだしたヒューマンドラマ。", "2008-11-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　身替座禅", "シネマ歌舞伎　身替座禅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "歌舞伎の舞台公演をスクリーンでデジタル上映する「シネマ歌舞伎」シリーズ第11弾で、山蔭右京が正妻・玉の井に隠れて逢引を重ねる狂言舞踊。出演に中村勘三郎、市川染五郎、坂東三津五郎ほか。「蜘蛛の拍子舞」と2本立て上映。", "2010-05-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACK CAULDRON", "コルドロン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "騎士を夢みる少年ターランが、驚異の力を持った巨大な壺“ブラック・コルドロン”をめぐり、仲間とともに邪悪な魔王ホーンド・キングと闘う、波乱万丈の武勇伝。子供たちに愛と夢と勇気を与えてくれるディズニーらしい作りで、完成度の高い本格的なアニメ映画。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROPPO RISCHIO PER UN UOMO SOLO", "ゴールデン・ボーイ　危機また危機", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "F−1レースに出場したカーレーサーが、偶然ある事故に巻き込まれたことから殺人と麻薬密輸容疑をかけられ、警察と組織から追われるハメに。無実の罪を晴らすため、男はお得意のカー・チェイスで悪人たちを蹴散らす。G・ジェンマの華麗なアクションが見どころ。", "1973-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("となりのトトロ", "となりのトトロ", "", "", "http://ecx.images-amazon.com/images/I/51gbhIkUBqL._SL210_.jpg", 88, "宮崎駿が監督、原作、脚本を務めた、スタジオジブリの劇場用アニメーション作品。入院中の母のため、考古学者の父とともに、田舎の一軒家へ引っ越してきたサツキとメイ。近所の少年にお化け屋敷と呼ばれたその家には、不思議な生き物が住んでいた……。幼い姉妹と美しい日本の自然、そして奇妙な生き物たちとの交流を描き、幅広い世代に支持され続けているファンタジー。", "1988-04-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STRONGEST MAN IN THE WORLD", "世界最強の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "コーンフレークを食べたデクスターは、突然、力持ちに。化学の授業で偶然にできた、人間に超体力を与える新薬をめぐって、コーンフレーク会社と大学が入り乱れてのてんやわんやの大騒ぎ。いかにもディズニー映画らしい夢いっぱいのSFコメディ。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美空ひばりの花笠道中", "美空ひばりの花笠道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "観世光太の原作を美空ひばり主演で映画化したコミカルなタッチの娯楽時代劇。江戸は両国の矢場の娘・お君と天下の素浪人・緋鯉の寛太は恋仲だが、浜松七万石のお家騒動に巻き込まれる。美空ひばりが若衆と町娘という得意の二役を演じ、大活躍を見せてくれる。ビデオ、DVDタイトルは「花笠道中」。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("玉蒲團之偸情寶鑑／SEX＆ZEN", "SEX＆禅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "中国三代禁書の一つ『玉蒲団』に基づくエロティック・コメディ。中国、元の時代。学を志す一方でセックスの探求にも熱心な若者メイは、純朴な新妻に四十八手を仕込み、性の虜に。その後、旅に出た彼は性豪への対抗心を燃やして馬のナニを移植するが、倒錯したセックスの果てに全精力を使い果たす。機能回復を試みて娼館に行ってみると、そこには娼婦となった妻がいた……。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chicago", "シカゴ", "", "", "http://image.eiga.k-img.com/images/movie/1035/315x210.jpg?1910", 113, "本年度アカデミー賞で最多12部門13ノミネート、作品賞、助演女優賞ほか最多6部門受賞作。ボブ・フォッシー振り付けの同名ヒット・ミュージカルを、ブロードウェイの人気舞台演出家ロブ・マーシャルが映画化。脚本は「ゴッズ・アンド・モンスター」でオスカー受賞のビル・コンドン。舞台は1920年代のシカゴ。ミュージカルのスターを夢見るロキシーは、愛人を射殺して刑務所に送られるが、そのスキャンダルを利用してスターになろうとする。", "2003-04-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Devil", "デビル", "", "", "http://image.eiga.k-img.com/images/movie/55745/315x210.jpg?2893", 80, "M・ナイト・シャマラン製作・原案による超自然スリラー3部作「The Night Chronicles project」の第1作。アメリカの大都市にある高層ビル内のエレベーターに5人の男女が閉じ込められる。一見、普通の事故かと思われたが、エレベーター内では不可解なことが連続して発生。やがて、乗客5人のうちのひとりが人間ではなく悪魔だということがわかり……。監督はスペイン製ホラー「REC レック」のハリウッドリメイク版「REC レック　ザ・クアランティン」を手がけたジョン・エリック・ドゥードル。", "2011-07-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ТИХИЙ АОН", "静かなるドン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 341, "ノーベル文学賞に輝くショーロホフの原作を正攻法で映画化した、上映時間５時間41分に及ぶ超大作。革命の嵐の中で出会った男と女の運命的な愛が、広大なロシアの大地を舞台に展開する。1958年度カルロ・バリ国際映画祭グランプリ受賞をはじめ、数多くの映画賞を受賞した映画史上名高い作品。", "1958-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・社長繁盛記", "続・社長繁盛記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "全社一丸となって若返りを図る高山物産で、体力テストが行われることになった。高山社長は好成績を上げたが、無理がたたって翌日倒れてしまった。美人マダムと浮気旅行にしけ込もうとしていた矢先のことで残念無念。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆発！暴走族", "爆発！暴走族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "岩城滉一主演のアクション・シリーズ第１作。ふだんは面白くもない仕事で息をつまらせ、土曜の夜ともなると町中をバイクで疾走することに生きがいを感じる一匹狼のライダー。暴走族との対立、プロ・ライダーとの友情、女王気どりの女とのセックスなど盛りだくさんの内容で彼の日常を描く。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鮫肌男と桃尻女", "鮫肌男と桃尻女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "望月峯太郎の同名マンガを、望月原作の「バタアシ金魚」で映画デビューした浅野忠信を主演に据えて映画化。組織の金を持ち逃げした男、鮫肌と不幸な境遇から抜け出そうとする少女トシコの逃避行を、オフビートなタッチで追いかけていく。組織との対決を山中のプチホテルで迎えるクライマックスには、クエンティン・タランティーノ作品からの影響が顕著。悪役キャラクターや美術セットの造形、さらには画面を息づかせるヴィジュアルセンスなどに、ポップで硬直したユーモアが漂っている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES BRONZE´S", "レ・ブロンゼ　日焼けした連中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "フランス人御用達のリゾート地、アフリカのコートジボワールのリゾート・クラブには、今日も様々な男女が、様々な思惑を持って訪れている。企業の中間管理職のベルナールは、クラブで、先に来ていた妻ナタリーと合流する。しかしベルナールとの不倫に悩む秘書のジジも、恋の苦しみを癒すためにやって来る。プレイボーイの医師ジェロームは、マリンスポーツを楽しみながらガールハントに精を出し、引っ込み思案のクリスチーヌはそんな彼に思いを寄せている。一流のリゾートを舞台に、いろんな愛情が交錯するスタイリッシュなコメディ。主題歌をセルジュ・ゲンズブールが歌っている。長い間未公開だったが、1996年に初めて劇場公開された。", "1978-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くりいむレモン　旅のおわり", "くりいむレモン　旅のおわり", "", "", "http://image.eiga.k-img.com/images/movie/53485/315x210.jpg?2258", 73, "アダルトアニメの草分け的存在で、かつて山下敦弘監督によって実写映画化もされた「くりいむレモン」を基にした青春ラブストーリー。長野から修学旅行で東京へやって来たアヤは、自由行動の時間を使って、両親の離婚で離ればなれになった義兄に会いにいく。アヤには付き合っているノブがいたが、どうしても義兄のことを考えてしまう自分の気持ちを、義兄に会って確かめたかったのだが……。数々の短編で評価を得てきた前田弘二監督の長編デビュー作。", "2008-02-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺たちに墓はない", "俺たちに墓はない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ムショ帰りの島、その弟分・ヒコ、一匹狼の滝田という３人が金と女をめぐって欲望をぶつけ合うアクション映画の佳作。３人が奇妙な友情で結ばれながらも、両雄並び立たずのたとえの通り、殺し合いに向かう様を、澤田監督はユーモアを交え、パワフルに描いている。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Theショートフィルムズ　みんな、はじめはコドモだった", "Theショートフィルムズ　みんな、はじめはコドモだった", "", "", "http://image.eiga.k-img.com/images/movie/54265/315x210.jpg?4686", 92, "「パッチギ！」の井筒和幸、「悲しき天使」の大森一樹、「血と骨」の崔洋一、「闇の子供たち」の阪本順治、「フラガール」の李相日が監督する5編の短編からなるオムニバス映画。出演に光石研、高岡早紀、佐藤隆太、岸部一徳、小泉今日子、樹木希林、佐藤浩一、藤竜也、宮藤官九郎ほか。", "2008-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DAYLIGHT", "デイライト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "マンハッタンとニュージャージーを結ぶ海底トンネルで事故が起き、大爆発が発生。トンネルの中に出勤途中の数百人が閉じ込められる。トンネルには有毒ガスと煙が蔓延し、さらにトンネルそのものが崩れ始める。このままいけばハドソン川の水が入り込む恐れがあった。状況が悪化するなか、元EMS（緊急医療班）のチーフだったキットが、危険を顧みず現場に向かう。救出作業にかかる時間は早くても７時間、しかし、中の酸素は３時間でなくなってしまう。限られた時間の中でキット・ラトゥーラは決して諦めなかったが……。1970年代に大ブームを巻き起こしたパニック・ムービーを思わせるS・スタローン主演の群像劇。監督は「トリプルX」のR・コーエン。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージーのぶちゃむくれ大発見", "クレージーのぶちゃむくれ大発見", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "東西電気の花川戸大五郎を中心とする７人は、いずれもコンピュータによって運に見放された。事態を好転させるためには、間違って振り込まれたホステス好子の金が必要。しかし、行方不明になった彼女を捜しあてると、好子は殺害されていた。彼らは、好子を美人ロボットとしてよみがえらせるが……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WET GOLD", "青い誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "平凡な生活に退屈していたローラは、数百万ドルの金塊を積んだ沈没船の話を聞き、金塊を探すため、バハマ沖に船を出す。しかし海上で金塊をめぐる者同士の対立が激化し、とうとう仲間割れが……。シールズの成長した肢体がエロティックで、目に眩しい。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("虹の中のレモン", "虹の中のレモン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "歌謡青春映画の雄・斎藤監督と人気G.S.“ザ・ビレッジ・シンガーズ”が息の合うところを見せた連作の１本。主人が蒸発、息子が家出し、娘・民子とその友人・エミ、それに老僕だけとなった大邸宅を、民子たちは施設の子供たちに開放する。そして、ビレッジ・シンガーズの歌を聴きたいという子供たちの願いをかなえるべく、一計をはかるが……。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLAN 9 FROM OUTER SPACE", "プラン9・フロム・アウタースペース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "1980年出版の『ザ・ゴールデン・ターキー・アワーズ』で“史上最低の映画”に選ばれたエド・ウッド監督の代表作。宇宙の支配者の命令で、人間に戦争をやめるよう忠告に来た宇宙人。しかしアメリカ政府は宇宙人の存在を認めず、彼らに会おうとはしなかった。宇宙人たちは仕方なく“第９計画”の実行を決める。その計画とは、死者をよみがえらせ、ゾンビを操って地球を征服しようというものだった。低予算とはいえ、バカバカしいストーリーとセリフ回し、素人並の演技、あまりにも稚拙な特撮と、すべてが低レベルな怪作。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOOSE CANNONS", "キャノンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ナチ残党の黒幕を暴く極秘フィルムを追うタフガイ刑事マックの相棒として精神病院帰りの捜査官がついたが、この男、危機になるとTVのキャラクターのものまねをはじめるという発作癖があった……。「激突！」などで有名なR・マシスンとその息子が脚本にあたり、最後までテンポのよいドラマ運びで楽しい作品になっている。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("〔Focus〕", "〔Focus〕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "1994年の“さっぽろ映像セミナー”に入選した新和男の脚本『起承転々』を映画化。岩井ディレクターを中心とするニュース番組のクルーは、ある企画で盗聴マニアのフリーター・金村を取材することになった。実際の盗聴現場に立ち会い取材を続けるTVクルーたち。やがて彼らは、一つの事件に巻き込まれていく……。この作品が劇映画監督デビューとなる井坂聡が、テレビカメラの目を通して現代の病巣を描いたリアル・カルト・サスペンス。第47回ベルリン映画祭NETPAC賞(ベスト・アジア映画)を受賞した。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホッテントットエプロン-スケッチ", "ホッテントットエプロン-スケッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "「眠り姫」「マリッジリング」の七里圭監督による異色ファンタジー。少女がたどる心の旅を、全てのセリフを排除した幻想的な映像でつづる。隠れた場所に醜いアザのある少女・里香。ある日、ねずみ色のフードをかぶった笛吹き男を見かけた彼女は、その音色に引き寄せられるように、不思議な世界へと迷い込んでいく。ヒロイン役に「ココロとカラダ」の阿久根裕子。音楽を手がけるのは、七里監督と長年にわたってコンビを組んできた侘美秀俊。", "2007-12-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽・海を染めるとき", "太陽・海を染めるとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "マイトガイ・小林旭と舛田利雄監督のコンビによる痛快娯楽アクション。主人公は外国航路の船員で、ある港で商船大学の同級生と２年ぶりの再会をするが、旧友は処女航海に失敗し、今はヤクザになっていた。主人公は彼のために悪玉と戦う。浅丘ルリ子が男勝りの娘を演じ、イキな啖呵を切るのが可愛らしい。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KEETJE TIPPEL", "情婦ケティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "運命に流され変わっていく一人の女性、ケティ。うらぶれた売春宿の娼婦から大富豪の妻になるまでを描いたラブ・ロマンス。ケティと出会う男の一人、ヒューゴを、ハリウッドに進出する前のR・ハウアーが、好演している。", "1975-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日野日出志の　ザ・ホラー怪奇劇場第一夜", "日野日出志の　ザ・ホラー怪奇劇場第一夜", "", "", "http://ecx.images-amazon.com/images/I/21VG3CNWMCL._SL210_.jpg", 144, "世界にその名を轟かせるカリスマ怪奇漫画家、日野日出志の代表６作品を６人の新鋭監督が映画化したオムニバス・ホラー。“第一夜”では「地獄小僧」、「わたしの赤ちゃん」、「怪奇！　死人少女」の３作品が登場する。衝撃的な描写と鮮烈なメッセージで多くのファンを魅了した伝説の作品が、実写の世界でどう生まれ変わるか、ファンならずとも注目だ。ビデオ、DVDは単品での発売もされている。", "2004-10-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばりの森の石松", "ひばりの森の石松", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "美空ひばりが森の石松に扮する娯楽時代劇。男役だけでなく、ちゃんとその娘役も用意された一人二役なのが面白い。一人の旅がらすを助けたことから清水一家と知り合った石松が、仇討ちの助太刀や丸亀藩のお家騒動に巻き込まれたりと、ズッコケながらも大活躍していく。清水次郎長には若山富三郎が扮している。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エクスタシーの涙・恥淫", "エクスタシーの涙・恥淫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "クィーアフィルム（ゲイ映画）で国際的に知られる大木裕之が、初めて異性愛に取り組んだ実験的ピンク映画。高知のとある古い屋敷に住む父母、兄妹の４人家族。彼らは皆、自分が宇宙人だと考えていたが、それは誰にも秘密にしていた。父が同性愛者であることを嫌う長男トモキだったが、いつしか職場の先輩の恋人と結ばれるようになった。長女アキコは恋人のケンとセックスを重ねている。物語はそんな一家の現世と来世を行き来しつつ、やがて家族が新たな解放に向けて動き出す……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOODLUM", "奴らに深き眠りを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "豪華な俳優陣をキャスティングして作られた実録ギャング映画。1930年代のN.Y.ハーレムに、出所したばかりの黒人青年エルスワース“バンピー”ジョンスンが戻って来る。彼は数当て賭博（ナンバーズ）を牛耳る女王ステファニーの配下となったが、賭博の権利を奪いたい悪名高いダッチ・シュルツが圧力をかけてきた。やがてステファニーが逮捕され、跡目を継いだバンピーは犯罪王ラッキー・ルチアーノと手を組み、ついにダッチとの全面抗争に突入していく……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("哥", "哥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「無常」「曼陀羅」に続く実相寺昭雄の３部作の完結編。旧家の３人の息子、書生、召し使いらの人間関係の葛藤が、ついにはカタストロフィーを招き、一家は崩壊の過程をたどる。人間の内面を探る実相寺監督の観念的な世界が色濃く出された作品。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホテルビーナス", "ホテルビーナス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "TV番組『チョナン・カン』から派生した群像劇。登場人物が韓国語を話す以外は、どこまでも無国籍な風情をたたえた状況設定の中、とある“ホテル”で暮らすワケアリの人々の悲喜こもごもを丹念に描いていく。独特の質感があるモノクロ映像に不思議な魅力がある。心に傷を抱えた者たちが暮らす“ホテルビーナス”。隣接したカフェで働きながら住人たちの世話係を務めるチョナンは、心を閉ざす幼い少女と触れ合おうする。そのうちに、チョナンや住人たちは自分自身と向き合うことになり……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DZIEJE GRZECHU", "罪物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "世紀末のワルシャワ、若い娘エヴァは彼女の家に新しく来た下宿人ウカシュに恋をした。だが男には離婚に踏みきれぬ妻があり、エヴァのもとを離れていく。彼を追い家を出るエヴァ。だが彼女を待ち受けるのはあまりにも過酷な運命だった……。エロティックなシーンもふんだんに描かれる文芸ドラマ。", "1979-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIRACLE ON 34TH STREET", "三十四丁目の奇蹟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "メイシー百貨店の感謝祭パレードにサンタクロースとして雇われた白ヒゲの老人クリスは、“私は実は本当のサンタクロースなのです”と言い出した。大論争の末、サンタの存在を信じる子供たちの夢が実って、ついに奇跡が起きる。アメリカではクリスマス・シーズンの定番映画として根強いファンを持つ、ハート・ウォーミングな映画。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE THUNDER", "ブルーサンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "軍部の陰謀をかぎつけたロス市警のパイロット、マーフィ。彼は、対テロリスト用に作られた高性能武装ヘリコプター\"ブルーサンダー\"を奪い、巨大な犯罪計画の阻止に挑む……。ビルの谷間をぬって行われるクライマックスの空中戦は、度肝をぬく迫力である。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I LOVE YOU， ALICE B.TOKLAS", "太ももに蝶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ロサンゼルスでビジネスが成功、いまや何一つ不自由のない身となった男がひょんなことからヒッピー熱にうかれ始め、婚約も解消、ヒッピー族の仲間に加わって自由気ままな生活を始めるが……。ヒッピーの生態を素材に、P・セラーズの主演で描いたコメディ。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAIN OF COMMAND", "ファイナル・コマンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "事件は、石油大国クミールにある米企業、ウエスタン・オイル社が何者かに襲撃を受けたことで始まった。クミール解放機構の犯行に見せかけた事件だが、その裏にはこの国の乗っ取りを狙うウエスタン・オイル社長、ブルースターの陰謀があった。米諜報部員で、この会社に技術顧問の名目でひそかに送り込まれていたメリル・ロスも事件に遭遇。社員たちが次々と惨殺されていくのを目にし、敵の殺し屋ローリングへの復讐を決意する。しかしロスもまた陰謀に巻き込まれていることを知ったとき、復讐は巨大な陰謀を暴くための戦いになり、壮絶なバトルへと発展した。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRATELLO SOLE，SORELLA LUNA", "ブラザー・サン シスター・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "12世紀のイタリアの町アシジを舞台に、富裕な商人の息子フランチェスコが宗教に目覚め、聖人と呼ばれるようになるまでを描く。F・ゼフィレッリ監督の美しい自然描写が素晴らしい、初々しく気品に満ちた青春映画。", "1972-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("P．O．BOX", "郵便屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「カリギュラ」の巨匠ティント・ブラスがマスコミに実際に送られてくる女性たちの手紙や写真、ビデオテープからアイデアを得て描き出す、世の女たちのエロティックな空想の世界。陽気にシニカルに、そして愉快に解放的に“性”を表現することによって、セックスを純粋で無垢な歓びの源として捉えている。「肉屋」のアウレリオ・グリマルディが脚本を担当。", "1995-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRIBUTE", "マイ・ハート マイ・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "原作『トリビュート』は、1978年J・レモンがブロードウェイで主演した舞台劇で、映画でも彼は同じ役柄に挑戦。仕事を転々としてきた中年男が、医師から余命いくばくもないと宣告を受ける。事情を知った前妻と息子は、長年のわだかまりを捨て、彼を見守る……。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("URBAN LEGENDS: FINAL CUT", "ルール2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "美しい自然に囲まれたアルパイン大学では、学生たちが名誉ある“ヒッチコック賞”をめざして映画作りに励んでいた。その一人、エイミーは都市伝説をモチーフにしたサイコ・スリラーの撮影に取りかかるが、彼女の周囲で連続殺人事件が発生する。前作からスタッフ＆キャストを一新したティーン向けサスペンス・ホラーの第２作。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日曜大工のすすめ", "日曜大工のすすめ", "", "", "http://image.eiga.k-img.com/images/movie/57435/315x210.jpg?3058", 15, "無名の若手監督たちが作り上げた15分の短編10作品を特集上映する「NO NAME FILMS」の1本。毎週末に日曜大工にはげむ男は、淡々と大工仕事をこなすことで日々のストレスを発散していた。そんなある日、日曜大工を始めたいという女が現れ、男は喜んで大工技術を教える。しかし、女にはある目的があり……。監督は、CG・アニメーション・実写など幅広く映像制作を手がけ、ぴあフィルムフェスティバルや文化庁メディア芸術祭でも受賞経験のある吉野耕平。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'empire Des Sens", "愛のコリーダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "昭和11年に起きた「阿部定事件」を題材に、大島監督が男女の愛の極致を描く。性交場面が日本では十分に表現できず、フィルムをフランスに直送し編集するという新システムで完成させた。日本公開版は当然修整が加えられたが、芸術か猥褻か表現の自由をめぐって裁判事件にまで発展した問題作。海外では1976年のカンヌ映画祭で上映され、芸術作品として高い評価を受けた。なお、2000年には初公開時にカットされたフッテージをほぼ完全に復元した「愛のコリーダ2000」が12月に公開された。", "1976-01-01", "76,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THEY SHALL HAVE MUSIC", "彼等に音楽を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "カーネギーホールのコンサート・チケットを拾った不良少年が、ハイフェッツのバイオリン演奏を聞いて感動し、町の小さな音楽学校で勉強する気になる。しかし、その学校が破産寸前と知り、仲間の子供たちを集めてハイフェッツに慈善コンサートを依頼。学校を立て直そうと奮闘する。クラシック音楽ファン必見の一編。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUICE", "ジュース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「マルコムX」までのスパイク・リーの全作品で撮影を手掛けたカメラマン、E・R・ディッカーソンの初監督作。“ジュース”とは黒人世界における“尊敬”の意味。音楽好きの高校生クインシーは３人の仲間とつるんでいた。その中の一人で、血の気の多いビショップがストア強盗を計画し、クインシーもしぶしぶ計画に参加することに。ところが、ビショップがストアの主人を射殺したことからほかの３人と口論になり、仲間の一人も射殺してしまった。暴力で成り上がろうとする彼を、クインシーは敬遠し始める。しかし、ビショップは口封じのため、クインシーの命を狙っていた……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二等兵物語・死んだら神様の巻", "二等兵物語・死んだら神様の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "シリーズ第５作にして初のカラー作品。昭和18年、理髪師・古山源吉と柳一太郎は山根中隊に招集をうける。過酷な訓練が続くなか、二人は隊長らが御用商人と通じて軍の物資を横流ししている事実をつかむ。松竹が誇る肉体派女優・泉京子が出演。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANJALI", "アンジャリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 147, "「ボンベイ」などで知られるインド映画界の名匠マニラトナムが、躍動感あふれるミュージカル・シーンとファンタスティックなSFXを盛り込んで完成させた感動作。知的障害と虚弱体質を抱え、施設から家族のもとに帰ってきた少女アンジャリ。どこまでもピュアな心を持つ彼女が、周囲の戸惑いや偏見にも負けず、奇跡を巻き起こしていく姿を綴る。", "1990-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SECRETS", "シークレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "結婚９年目を迎え、互いに倦怠を感じている夫婦。二人はそれぞれ浮気の相手を見つけて情事にふける。小学生の一人娘も初めてのキスを経験。家族はそれぞれが自分だけの秘密を胸に暮らしていくのだった……。J・ビセットがヌードを初めて披露し、当時大きな話題を呼んだ不倫ドラマ。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アバター", "アバター", "", "", "http://image.eiga.k-img.com/images/movie/56005/315x210.jpg?3430", 92, "「リアル鬼ごっこ」「×ゲーム」などで若年層から支持を集める山田悠介の原作小説を映画化。主演は、「告白」に出演し注目を浴びた橋本愛。クラスで一番地味で目立たない存在の高校2年生・阿武隈道子は、人気のSNSサイト「アバQ」に入会し、ネット上の自身の分身である“アバター”に固執していく。アバQのレアアイテムを入手するため、犯罪や殺人にも手を染めていく道子は、次第に仮想と現実の境界線を見失っていく。", "2011-04-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BANDAGE バンデイジ", "BANDAGE バンデイジ", "", "", "http://image.eiga.k-img.com/images/movie/54205/315x210.jpg?2745", 119, "人気音楽プロデューサー・小林武史が監督を務め、岩井俊二がプロデュースと脚本を手がける青春映画。原作は菅知香の携帯小説「グッドドリームス」。アイドルグループ「KAT-TUN」の赤西仁が映画初主演を果たす。バンドブーム全盛期の1990年代、女子高生のアサコ（北乃きい）は天才的な音楽センスを持つユキヤ（高良健吾）率いる「LANDS」というバンドのファンになり、飛び込んだ楽屋でバンドメンバーと仲良くなる。アサコを気に入ったボーカルのナツ（赤西仁）は、バンドの練習スタジオへアサコを連れて行くが……。", "2010-01-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本侠客伝 血斗神田祭り", "日本侠客伝 血斗神田祭り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "老舗を誇った神田の呉服問屋“澤せい”も、このところ商売が思わしくなく、借金がかさむ一方だった。澤せいの土地家屋を狙う大貫一家は、若旦那の伸夫から権利書を取り上げ、伸夫もろとも澤せいを焼き払った。纒持ちの新三をはじめ、神田“よ組”の連中は澤せいの再建に力を貸す。大貫は訴訟を妨害するため、伸夫の女房・花恵を連れ去る。新三は花恵を救うため大貫一家に乗り込んだ。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("世界はときどき美しい", "世界はときどき美しい", "", "", "http://image.eiga.k-img.com/images/movie/33905/315x210.jpg?1266", 70, "ありふれた日常の中で葛藤しながら生きる等身大の男女の姿を美しい映像で切り取った5つの短編からなるアンソロジー。これまでミュージック・ビデオやドキュメンタリーを手掛け、本作で劇場長編デビューを果たした御法川修監督が、全編8ミリフィルムで撮影した柔らかく繊細な映像で綴る。メインキャストには松田龍平、市川実日子、モデル出身の片山瞳といった若手から、松田美由紀、柄本明らベテランまで実力派が顔を揃える。", "2007-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RENGADE", "ビッグ・ファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "イタリア製作の西部劇風アクション映画。気ままな放浪生活を送る一匹狼が、旧友の土地を守って悪者と壮絶な戦いを繰り広げる。主役にマカロニ・ウエスタンで活躍したT・ヒル。一緒に旅する少年には彼の実の息子が扮しており、ヘルス・エンジェルスやアーミッシュも登場する。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROAD TO SINGAPORE", "シンガポール珍道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "“道中”シリーズの第1弾にふさわしく、クロスビー、ホープ、ラムーアのトリオによる歌の披露といい、二転三転するコミカルなストーリー展開といい、シリーズ化の要素を兼ね備えた逸品となった。父親の会社を継ぎたくない、結婚もしたくない主人公がドロップアウトし、親友と共にぶらりと旅へ。シンガポール付近の島にやって来た二人は共に島の娘に一目ぼれしてしまう。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ばけもの模様", "ばけもの模様", "", "", "http://image.eiga.k-img.com/images/movie/53635/315x210.jpg?1275", 93, "長編デビュー作「剥き出しにっぽん」が高く評価され、国内外から注目を集めている新鋭・石井裕也監督の長編第4作。井原西鶴の言葉\"人はばけもの、世にないものはなし\"をモチーフに、愚かな行動を取り続けてしまう人々の姿を通して人間の哀しい本性を暴きだす。息子を亡くした順子はショックから奇行に走るようになり、夫に愛想を尽かされてしまう。彼女は自分に想いを寄せる世之介を強引に誘い、彼のメロンパン移動販売車で旅に出るが……。", "2008-06-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("3Dミュージカルショー　ハローキティとオズの魔法の国", "3Dミュージカルショー　ハローキティとオズの魔法の国", "", "", "http://image.eiga.k-img.com/images/movie/56335/315x210.jpg?3570", 47, "ハローキティ誕生35周年記念として2009年からサンリオピューロランド（東京・多摩市）で上演されたミュージカルショーを3Dで劇場上映。キティが誕生日にもらった絵本「オズの魔法使い」を開くと突然、竜巻が発生。マンチキンの国に吹き飛ばされてしまう。キティは、そこで出会った脳みそのないカカシ、ハートのないブリキのきこり、勇気のないライオンと一緒に、なんでも願いをかなえてくれる魔法使いオズがいるエメラルド・シティを目指して旅に出る。演出、作曲、振り付けなどに宝塚歌劇団のスタッフが参加。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PANIC", "パニック／脳壊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "中年の危機を題材にした犯罪劇。家業の殺し屋を継いだ中年男アレックスは仕事を辞めたいが、ガンコ者の父は許してくれない。妻にも仕事を打ち明けられず、セラピストに通いだす始末。そんなある日、アレックスは診療所の待ち合い室で、若い女性サラと出会い恋してしまう……。気弱な殺し屋という設定が醸し出すトボけたユーモアと、人間の欲望や孤独を見つめたシリアスな味が同居した異色作。ビデオ、DVDタイトルは「脳壊」。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トラック野郎・男一匹桃次郎", "トラック野郎・男一匹桃次郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "今は亡き夏目雅子が新人として注目されていた頃の出演作。剣道三段の女子大生・雅子と知り合った桃次郎はすっかりホの字。だが彼女の恋人の存在を知り、一転してサポート役を努め、恋人の乗る旅客機が鹿児島空港を離れる前に二人を会わせるべく、雅子をトラックに乗せて九州路をぶっ飛ばす。正月映画とあって堺正章、左とん平、長門勇、湯原昌幸らお笑い陣総出演と、シリーズ中最もにぎやかな一編となった。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("七つの顔の女", "七つの顔の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "岩下志麻が強盗団の女ボスを演じるという奇想天外なアクション・コメディで監督には前田陽一が当たっている。城所雪子は新しい仕事を計画し、仲間である４人の金庫破りのプロを集めると、日東繊維の金庫に眠る５億円のウラ金を強奪すべく実行に移った……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・大誘拐", "喜劇・大誘拐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "長距離通勤で知り合った男女４人組がお婆さんを誘拐する。ところが、日頃から家族に不満を持つお婆さんに逆に振りまわされ……。前田監督の演出は渋谷実ゆずりの社会を諷刺する精神で貫かれているが、硬質な中にもほのぼのとしたタッチをにじませ、夢見心地にさせる力を持っている。天藤真の推理小説で、のちに岡本喜八監督により映画化される「大誘拐」をヒントにしている。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rois et Reine", "キングス＆クイーン", "", "", "http://image.eiga.k-img.com/images/movie/1105/315x210.jpg?3381", 150, "約5年ぶりとなるデプレシャン監督の新作は悲劇と喜劇を行き来しながら、一組の自由奔放な男女とその周辺を描く2部構成のコメディドラマ。パリで画廊を営むノラは、死別した夫との間の子供エリアスを自分の父親のところに預けているが、その父親がガンで余命幾ばくも無いことを知る。そこで、ノラは1年前まで同棲していた恋人イスマエルにエリアスを養子にしてもらおうと頼みに行くが……。", "2006-06-17", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VOYAGE　TO　THE　BOTTOM　OF　THE　SEA", "地球の危機", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「ポセイドン・アドベンチャー」「タワーリング・インフェルノ」などで知られるパニック映画の大物プロデューサー兼監督I・アレンの初期のSF作品。後にTVドラマ『原潜シービュー号』の元ネタとなった。地球を取り巻くバン・アレン帯が突然、燃え始め、地上の温度が急上昇。灼熱地獄で苦しむ人々を救うため、原子力潜水艦シービュー号の面々は、核ミサイルを駆使した秘策に打って出る。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAR DOG", "ウォードッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ヨーロッパ最大の兵器メーカー、ワルサー社とスウェーデン政府が共同製作したバイオレンス・アクション。近未来のアメリカを舞台に、CIAとペンタゴンが結託して作った殺人部隊の一員にさせられた弟を救い出すため、たった一人で基地に乗り込んで行く男の活躍をハードなタッチで描く。", "1987-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生きたい", "生きたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "深刻な老人問題を軽妙な視点から描き上げた、ベテラン新藤兼人らしい冴えが感じられる意欲作。ボケの進行する老父と、ソウウツ病を抱える独身の娘。二人暮らしを送る彼らの一種絶望的な状況に、姥捨て伝説の再現映像を重ね合わせながら、その行方を描破する。父娘のやりとりから滲み出るのは、ありきたりな悲哀よりも、どうにもならないからこそ生まれるユーモアの数々。苦渋に満ちていながら、それをあえてカラッと映し出す様に、人間観察の巧みさが息づいている。バーの非情なママに扮した大谷直子や、娘の親友を演じる宮崎淑子の好演も、閉塞感に終始しがちなこうした物語を風通しの良いものにしている。第21回モスクワ国際映画祭では、グランプリをはじめ、国際批評家連盟賞、ロシア映画批評家賞を受賞。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shark Night 3D", "シャーク・ナイト3D（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("素ッ裸の年令", "素ッ裸の年令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "鈴木清順がデビュー間もない赤木圭一郎と組んだ青春映画。盗みを生業としながらも明るくしたたかに生きる少年たちの運命共同体。ギャングから思わぬ大金を手にした時、彼らの結束が揺らぐ。リーダーの少年の裏切り、そして死。赤木の個性とクールな清順演出が印象的な苦い味の佳作。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Real Steel", "リアル・スティール", "", "", "http://image.eiga.k-img.com/images/movie/55975/315x210.jpg?3697", 127, "ヒュー・ジャックマン主演、「ナイトミュージアム」のショーン・レビ監督で、ロボット格闘技を通じて父子が絆を取り戻していく姿を描く。2020年、リモコンで遠隔操作されたロボット同士が戦う“ロボット格闘技”が大流行。プロボクサーからロボット格闘技の世界に身を転じたチャーリーは、スクラップ寸前のロボットを闇試合に出場させて一攫千金を夢見ていた。そんなある日、離婚のため離れて暮らしていた11歳の息子マックスを預かることになり、慣れない父子の共同生活が始まるが、廃工場で旧式ロボット「ATOM」を発見したことから2人の運命が大きく変わっていく。共演に「ハート・ロッカー」のエバンジェリン・リリー、アンソニー・マッキー。", "2011-12-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REUNION", "リユニオン 再会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ユダヤ人の少年ハンスと、ドイツ貴族の血をひく少年コンラディアン。二人は親友だったが、やがてナチスの進出によって引き裂かれ、ハンスはアメリカへ渡る。半世紀後、再びドイツに戻ってきたハンスは、そこで旧友コンラディアンの意外な消息を知る……。人種間のあつれきと、それを超えた少年同士の友情を描いたヒューマン・ドラマ。", "1989-01-01", "2,7,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽への脱出", "太陽への脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "石原裕次郎が「何か面白いことないか」に続いて主演した激烈アクション映画。主人公の兵器メーカーの社員はバンコクで、会社側に脅迫されて死の商人の出先機関として活動している。しかし、相棒が殺されて怒った主人公は帰国して兵器工場を爆破し、自らも息絶える。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAUTIFUL", "ビューティフル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "母娘の絆の再生をユーモラスに描く、名女優S・フィールドの初監督作。両親の愛情に恵まれずに育ったモナは、幼い頃からミス・アメリカになることを夢見ている。若い頃に生んだ娘ヴァネッサを親友の子供として育ててもらい、人生のすべてをコンテストに捧げてきた彼女だが……。ヒロインの美に執着する異様な情熱と、コンテストの裏側で繰り広げられる激烈な争いをブラック・ユーモアを交えて描きながら、切っても切れない母娘の絆の強さを浮き上がらせる。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tale of Cinema", "映画館の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「気まぐれな唇」のホン・サンス監督が、劇中映画と現実の世界が交錯していく様子を2部構成で描いた純愛ストーリー。大学入試を終えたばかりの青年サンウォンは、中学時代の初恋の女性ヨンシルと街で偶然再会を果たし……。そんな内容の映画を見終えたばかりの監督志望の青年ドンスは、その映画に出演していた女優ヨンシルを劇場の前で見かけ……。出演は「殺人の追憶」のキム・サンギョン、「美しき野獣」のオム・ジウォンほか。", "2007-03-31", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fast Times at Ridgemont High", "初体験／リッジモント・ハイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "　南カリフォルニア、リッジモント高校に通うステーシーは初体験に憧れるティーンエイジャー。同じファースト・フード店でバイトをしているリンダからアドバイスを受け、彼女はついにロストバージンに成功。やがて、そんなステーシーに恋焦がれていたマークが近づいてくるが……。80年代の高校生たちを描いた学園コメディ。キャメロン・クロウが書き下ろした小説を自ら脚本化。ショーン・ペンをはじめ、当時まだ無名だった俳優たちが大挙して出演。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lord of the Rings: The Two Towers", "ロード・オブ・ザ・リング　二つの塔", "", "", "http://image.eiga.k-img.com/images/movie/1805/315x210.jpg?2622", 179, "1作目「ロード・オブ・ザ・リング」からそのまま続く第2部。フロドとサム、メリーとピピン、アラゴルンとレゴラスとギムリ、3手に別れた仲間たちのその後の旅が描かれていく。セオデン王、ファラミアなど新登場キャラだけでなく、話題のゴラムはじめ、人間と樹木の間の生物エント族、ナグズルの乗る生物などなど新クリーチャーも大挙登場。オークから人間まで、総数なんと20万に及ぶ大群衆がヘルム峡谷の大合戦で激闘を繰り広げる。", "2003-02-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春の夢", "春の夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "社員たちのスト突入を目前にした製薬会社社長・奥平。ただでさえ頭の痛いこの時期に、焼き芋屋のじいさんが、屋敷の居間で脳溢血で倒れてしまった。病気が病気だけに、むやみに動かすこともできず、じいさんを奥平の屋敷で寝かせることになるのだが……。木下惠介が、自らの脚本を映画化した風刺喜劇。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄コン筋クリート", "鉄コン筋クリート", "", "", "http://image.eiga.k-img.com/images/movie/33415/315x210.jpg?4479", 111, "93年から94年にかけて「ビッグスピリッツ」誌で連載された松本大洋原作の人気漫画を「アニマトリックス」ほかハリウッドでVFXスペシャリストとして活躍するマイケル・アリアス監督がアニメ映画化。義理と人情の“宝町”を舞台に、縦横無尽に飛び回る2人の少年クロとシロの活躍を描く。声の出演は、イーストウッド監督作「硫黄島からの手紙」で海外デビューを果たした二宮和也と「フラガール」の蒼井優。", "2006-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Telefon", "テレフォン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "　冷戦時代、KGBは電話による指令で破壊活動を行なうように洗脳した51人の工作員をアメリカに送り込んでいた。その51人の情報を握ったKGBのダルチムスキーがアメリカに潜入し、テロ行為を開始。KGB本部はこの事態を収拾するため、ダルチムスキー殺害を計画し、ボルゾフ少佐をアメリカに派遣する。ボルゾフは同局員のバーバラとともにダルチムスキーを追うが……。巨匠ドン・シーゲル監督とチャールズ・ブロンソンが組んだスパイ・アクション。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG SHOTS", "ビッグ・ショット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "優等生オビーとスラム街の一匹狼スカム、このまったく対照的な二人の子供が出会い、友情が芽ばえる。トップ・ハットにサングラスの二人は、盗んだメルセデス・ベンツがもとで事件に巻き込まれ、大騒動を起こす……。「ゴーストバスターズ」などのヒットメーカー、I・ライトマンが製作総指揮を担当した、軽快でホットな秀作。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雨鱒の川", "雨鱒の川", "", "", "http://ecx.images-amazon.com/images/I/517P738R36L._SL210_.jpg", 113, "「船を降りたら彼女の島」「解夏」の磯村一路監督が、川上健一の同名小説を映画化したラブ・ストーリー。心優しい青年と耳の聞こえない少女のハンデを超えた愛が描かれる。北海道の雄大な自然の中でまだ若い二人がはぐくむ相手への強い想い。その一途な愛の形が静かな感動を運ぶ。両主人公に扮する玉木宏と綾瀬はるかの好演も光る。幼い頃から一緒に過ごしてきた、絵の得意な心平と耳の聞こえない小百合。深い絆で結ばれる二人だが、絵が認められた心平は地元・北海道から東京へ。その間に小百合は別の男性との結婚話を親にまとめられる。", "2004-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crazy Waiting", "待ちくたびれて", "", "", "http://image.eiga.k-img.com/images/movie/56745/315x210.jpg?2763", 110, "6歳の年の差カップル、熱愛中の大学生カップルなど4組の男女が登場。それぞれが男性の兵役義務による入隊で離れ離れになることから起こるドラマを描いたオムニバス。ドラマ「美男（イケメン）ですね」（2009）、「メリは外泊中」（10）などでブレイクした韓国の人気俳優チャン・グンソクの出演作を上映する「チャン・グンソク祭」で日本劇場初公開。", "2011-07-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pledge", "プレッジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "映画監督としても評価の高い、演技派俳優ショーン・ペンの監督第3作。退職寸前の老刑事ジェリーが、引退当日に起こった少女惨殺事件の被害者である両親と交わした一つの約束＝プレッジ。だが使命感は、いつしか独りよがりの妄想に変化し、ジェリーは行き過ぎた行動を取るようになる。サスペンスの形を取りながら、思い詰めた人間が壊れていくさまを悲哀感たっぶりに描き出す人間ドラマ。目をギラつかせ、老いた男の哀しみを毛穴からも放出しているようなJ・ニコルソンの存在感に圧倒される。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルナハイツ", "ルナハイツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "これまで主にTV界で手腕を振るってきた初山恭洋監督が、星里もちるの人気同名コミックを映画化。異性に恋愛感情を抱けない女性といまどき珍しい硬派な青年を主人公に、コミカルな恋愛模様が展開する。主人公を演じるのはグラビア・アイドルの安田美沙子。今回が映画初主演となる彼女だが、風変わりな恋愛観を持つヒロイン役で、TVやグラビアとはまた違う一面を披露してくれる。相手役を演じた若手実力派、柏原収史の妙演も絶妙だ。一軒家を建て入居寸前に婚約者に逃げられた隼人。そんな彼のマイホームがすったもんだの末、会社の女子寮に。かくして隼人と、同僚のまどりら女性4人のおかしな共同生活が始まる。", "2005-12-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SKE48 3Dシネマライブ vol.1 「制服の芽」公演2011", "SKE48 3Dシネマライブ vol.1 「制服の芽」公演2011", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "2008年10月にデビューし、名古屋・栄を拠点に活動するアイドルグループ「SKE48」から、松井珠理奈、松井玲奈ら人気メンバーが所属するチームSの公演「制服の芽」を最新3D＆5.1chサラウンドで劇場上映。公演のオープニングからエンディングまで全17曲を完全収録する。", "2011-02-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タバコイ　タバコで始まる恋物語", "タバコイ　タバコで始まる恋物語", "", "", "http://image.eiga.k-img.com/images/movie/57945/315x210.jpg?2771", 0, "2012年・第4回沖縄国際映画祭の長編プログラム「Laugh」部門にて上映。", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浮草", "浮草", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ほとんどの作品を松竹で撮った小津安二郎監督が大映で撮った唯一の作品。溝口健二と“いつか大映で１本撮る”と約束していたという。撮影もずっと一緒に仕事をしてきた厚田雄春ではなく、溝口作品には欠かせないカメラマン・宮川一夫を起用。戦前の1934年に同監督が撮った「浮草物語」のリメイクである。ドサ回りの芝居の座長が昔の女のいる田舎町に巡業に。その女には座長が生ませた子供がいるが、その子は座長をおじさんだと思っている。しかし、座長の情婦である女優が昔の女に嫉妬、妹分の女優に座長の息子を誘惑させる。京マチ子の情念むきだしの演技も小津世界にうまく溶け込んで印象的。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHAKES THE CLOWN", "殺人ピエロ狂騒曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "アル中ですさんだ日を送っていたピエロのシェイクス。そんなシェイクスが、殺人犯の濡れ衣を着せられたことから一念発起し、真犯人探しを通じて立ち直っていくコメディ・ドラマ。シェイクス役には、独特のトーンの声が印象的な人気コメディアン、B・ゴールドスウェイトを起用。あのロビン・ウィリアムズもノン・クレジットで出演している。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎 炎情剣", "眠狂四郎 炎情剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "健気な武家の未亡人の仇討ちを助けたことから狂四郎は海賊・鳥羽水軍の財宝を横取りした藤堂家の陰謀に巻き込まれてしまう。海賊の末裔たちが次々と倒されていくなかで、狂四郎は藤堂家に立ち向かう。山寺の長い回廊での果たし合いなど三隅研次の腰の据わった演出ぶりが見もの。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裸の銃弾", "裸の銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "初期には“ピンク映画の巨匠”と呼ばれるほど作品を撮り続けてきた若松孝二監督だが、中には本作のようにスタイリッシュなノワールなど、作家性で勝負する作品も撮っていた。鈴木清順の「殺しの烙印」風に、観客のイメージを喚起する映像ショットが心憎いカルトな一作だ。ある殺し屋の奇妙なストーリーが展開する。『若松孝二初期傑作選DVD-BOX2』に収録。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("痴漢白書 劇場版II　真冬のキッス", "痴漢白書 劇場版II　真冬のキッス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "エロティックな描写の中に恋愛劇を映す人気シリーズの劇場版第２作。10万円で付き合わないかと若い女を誘う痴漢男。借金に苦しむ恋人リュウイチのためにマチコは彼に身を任せる。一方、彼女の恋仇サヨコはリュウイチに身も心も捧げ、強盗までして彼を助けようとするのだった……。男と女の情念を濃密なタッチで描く。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Secret Window", "シークレット・ウインドウ", "", "", "http://image.eiga.k-img.com/images/movie/1635/315x210.jpg?1655", 96, "「パニック・ルーム」「スパイダーマン」の脚本家として有名なデビッド・コープがスティーブン・キングの中編ミステリー「秘密の窓、秘密の庭」（「ランゴリアーズ」収録）をジョニー・デップとジョン・タトゥーロの共演で描く。人気作家レイニーの前に、彼が自分の小説を盗んだと糾弾する男シューターが出現。身に覚えのない言いがかりに困惑するレイニーだが、その日から彼の周囲で奇妙な出来事が続出する。", "2004-10-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("丹波哲郎の大霊界２ 死んだらおどろいた!!", "丹波哲郎の大霊界２ 死んだらおどろいた!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "霊界のガイドブック的要素の強かった前作に対し、ドラマ的趣向が盛りだくさんの応用編ともいえるシリーズPART２。無実の罪で死刑になった男が霊界に着いたら、そこがあまりにも素晴らしかったので、真犯人にお礼を言いに行こうとする驚くべきストーリー展開!? ハイビジョン駆使のSFXが大いに見どころだ。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADDICTED　TO　LOVE", "恋におぼれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "小さな町に住む天文学者サムはいきなり別れの手紙をよこした恋人リンダを追ってニューヨークへ。捜し当てた彼女はアントンというフランス人と同棲中だった。二人が住むアパートメントの向かいにあるボロアパートの一室に住み着き、彼らの行動を一日中監視することにしたサムはアントンの前の恋人マギーと出会う。M・ライアンお得意のハートフルなラブ・コメディ。アントンとリンダの仲を裂くために協力し合ううちに、二人の間に恋が芽生えていく過程を、ユーモアとペーソスを交えて描く。監督は「アフター・アワーズ」「ぼくの美しい人だから」などで知られる俳優のG・ダンで、これが長編監督デビュー作となった。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワールドクラシック＠シネマ2011 バレエ「コッペリア」　パリ・オペラ座バレエ", "ワールドクラシック＠シネマ2011 バレエ「コッペリア」　パリ・オペラ座バレエ", "", "", "http://image.eiga.k-img.com/images/movie/56465/315x210.jpg?2533", 101, "世界各地の有名オペラハウス、バレエ団の上演を映画館のスクリーンで上映する「ワールドクラシック＠シネマ2011」（全8作品）の1作。伝統のパリ・オペラ座バレエによる上演で、ドイツの幻想小説家E・T・A・ホルマンの「砂男」をもとに、フランスの作曲家レオ・ドリーブが手がけた名作バレエ。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("情義心／FURY", "シティー・オブ・フューリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "兄弟のように強い絆で結ばれた３人の男たちが、ある男の手引きで一獲千金を狙ってニセ札の取引に手を染める。だがその男の裏切りに合って警察から追われることになり、事件の黒幕へ闘いを挑んでいく。香港ノワールの流れをくむハードボイルド・アクション。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太の南海大冒険", "ドラえもん のび太の南海大冒険", "", "", "http://ecx.images-amazon.com/images/I/315YHK7YCAL._SL210_.jpg", 91, "名作『宝島』を読んで感動したのび太は冒険がしたいと、いつものようにドラえもんにせがむ。22世紀のバーチャルマシン“伝説復元機”を使って、のび太とドラえもん、そしてジャイアン、スネ夫、しずかちゃんの５人は海の冒険に出かけることに。人魚や大海ヘビ、ポセイドンまで登場し、スリル満点の旅を満喫していた彼らだったが、突然時空間が乱れ、本物の大嵐に襲われる……。", "1998-03-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Citadelle Assiegee", "バグズ・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/53605/315x210.jpg?2632", 82, "西アフリカ・サバンナを舞台に、数メートルにも及ぶ巨大な蟻塚を築き上げるオオキノコシロアリと、その蟻塚に迫る凶暴な肉食性サスライアリの2千万匹もの大群が繰り広げる3日間の攻防戦をドラマティックに描いたドキュメンタリー。監督はフランスのドキュメンタリー作家フィリップ・カルデロン。最新鋭のマクロ撮影用レンズ“ボロスコープ”によって隅々まで鮮明に映し出された迫力満点の映像に圧倒される。", "2008-06-28", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星になった少年 Shining Boy and Little Randy", "星になった少年 Shining Boy and Little Randy", "", "", "http://image.eiga.k-img.com/images/movie/1645/315x210.jpg?4528", 113, "04年の「誰も知らない」に主演、第57回カンヌ映画祭において日本人初＆史上最年少で最優秀男優賞を受賞した柳楽優弥の新作。彼が演じるのは、日本人初の＜象使い＞を目指しながら20歳で夭折した実在の青年、坂本哲夢。父親が経営する動物プロダクションで、動物たちといっしょに暮らす哲夢は、子象と出会ったことから、タイの象使い学校に留学して学び、日本にも象の楽園を作りたいと思うようになる。蒼井優が恋人役で共演。", "2005-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tarnation", "ターネーション", "", "", "http://image.eiga.k-img.com/images/movie/1685/315x210.jpg?4156", 92, "ひとりの青年が11歳の頃から撮り貯めた膨大なフィルムと写真で31歳までの人生の軌跡を描く異色ドキュメンタリー。ジョナサンは、かつてモデルだった母の精神病、家を捨てた父、幼い頃に受けた虐待の記憶、ゲイであること、離人症という病についてなど自分の総てを描いていく。全米批評家協会賞最優秀ノンフィクション映画賞、ボストン映画批評家協会賞最優秀初監督作品賞、ロサンゼルス映画祭最優秀ドキュメンタリー賞受賞。", "2005-08-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どんてん生活", "どんてん生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「鬼畜大宴会」の助監督をステップにして山下敦弘が撮り上げた長編監督デビュー作。パチンコの好きな無職の青年・努が出会った風変わりな男・紀世彦。裏ビデオのダビングという彼の仕事を手伝ううちに、努の日常は少しずつ変わっていく。曇天のように淀んだ日常をハスに構えず正面から描写。登場人物に寄り添うような視線が温かく心地よい。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雲南の少女　ルオマの初恋", "雲南の少女　ルオマの初恋", "", "", "http://image.eiga.k-img.com/images/movie/1935/315x210.jpg?4145", 90, "", "2007-06-16", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃えよピンポン", "燃えよピンポン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "関西インディーズ界の雄、三原光尋監督が放つ爆笑コメディ。底なしのエネルギーを誇るなにわドリンクの営業ウーマン、美弥子が、恋敵でもある天満ボトラーズの真央子に異常なライバル心を燃やし、卓球大会での決戦になだれ込んでいく。型破りなOLに扮した高田聖子が、クライマックスではブルース・リーばりのカンフー技を見せつけるなど、これでもかの怪演を連発。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le roi de l'évasion", "キング・オブ・エスケープ", "", "", "http://image.eiga.k-img.com/images/movie/54995/315x210.jpg?4635", 93, "独自の作風で世界中に根強いファンを持つフランスの鬼才アラン・ギロディ監督が、ゲイの中年男性と少女の逃避行を描いた衝撃作。フランスの田舎町でセールスマンとして働く43歳の同性愛者アルマンは、ある晩若者たちにレイプされそうになっていた16歳の美少女カルリを救う。恋に落ちてしまったアルマンとカルリは駆け落ちするが、彼らは激怒したカルリの父親、さらに警察からも追われることになり……。", "2009-10-18", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Spider", "スパイダー　少年は蜘蛛にキスをする", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "20数年に渡り精神病院に入院していた男が退院、少年時代を過ごした町に帰郷する。その日から、彼の脳裏に少年時代の記憶が少しずつ甦ってくる。「そうだ、僕の愛する母は、父とその愛人に殺されたのだ……」。「グロテスク」などで知られる英国のカルト作家パトリック・マグラアの同名小説（ハヤカワepi文庫）を、「裸のランチ」「クラッシュ」とカルト小説の映画化に挑戦してきたクローネンバーグ監督が映画化。", "2003-03-29", "2,10,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イヌゴエ", "イヌゴエ", "", "", "http://ecx.images-amazon.com/images/I/31R0T1HNRHL._SL210_.jpg", 100, "オッサン声で不謹慎なことを喋りまくるフレンチブルドッグと、犬が大嫌いの男が織り成す奇妙な共同生活を、笑いと切なさを込めて描いた異色ファンタジー。犬の声を怪優、遠藤憲一が担当している。敏感な嗅覚を活かして“臭気判定士”として働く直喜は、父親からフレンチブルドッグのペスを強引に預けられる。ペスが発する臭いに耐えられず友人に預けようとするが、驚くことにペスは関西弁で喋り出し……。", "2006-02-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("移動ラブホテル (秘)めろめろ", "移動ラブホテル (秘)めろめろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "1970年代にピンク映画を量産した、エロスの名手・中村幻児監督による男性版デリヘル物語。表向きは運転代行会社、実際は女性客向けの売春あっせん会社である“親切レンタカー”を舞台に、３人の男性従業員が様々な女性たちと珍騒動を巻き起こす。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Laitakaupungin valot", "街のあかり", "", "", "http://image.eiga.k-img.com/images/movie/34045/315x210.jpg?1375", 78, "フィンランドの名匠アキ・カウリスマキによる“敗者3部作”の最終章となる人間ドラマ。ヘルシンキの街の片隅で生きる孤独な男が、人を愛することによって人間性を回復していくさまを描き出す。恋人も友人もいない夜警員コイスティネンは、カフェで声を掛けてきた美しい女ミルヤに恋をする。しかし彼女はマフィアが送り込んだ情婦だった。強盗の罪を擦りつけられたコイスティネンは逮捕され、1年間の服役を言い渡されてしまう。", "2007-07-07", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("包帯クラブ", "包帯クラブ", "", "", "http://image.eiga.k-img.com/images/movie/34095/315x210.jpg?2895", 118, "天童荒太によるベストセラー小説を「明日の記憶」の堤幸彦監督が映画化した青春ドラマ。ある日ふと病院の屋上のフェンスに登った女子高生ワラは、奇妙な関西弁の少年ディノに声を掛けられる。ディノがワラの手首からほどけ落ちた包帯をフェンスに巻きつけてワラの心の傷を“手当て”すると、ワラは不思議と癒された気分に。彼女はディノや親友のタンシオらと共に“包帯クラブ”を結成。依頼者が心に傷を負った場所に、次々と包帯を巻きつけていく。", "2007-09-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の喧嘩状", "花の喧嘩状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "南国の漁場では古座一家と川尻一家の縄張り争いが絶えない。仲裁のために古座にやって来た明神の半太郎、三吉、音松の３人は、川尻一家の無法ぶりに怒るが、それはすべて古座の親分、権太郎の仕組んだものだった。仲間を殺され、古座一家に単身殴り込む半太郎を二枚目スター、長谷川一夫が演じる、勧善懲悪の任侠もの。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Amelia", "アメリア　永遠の翼", "", "", "http://image.eiga.k-img.com/images/movie/54815/315x210.jpg?2948", 111, "1928年に女性として初めて大西洋を横断した伝説の飛行士アメリア・イヤハートの生涯を描く伝記映画。「レインマン」のロン・バスが脚本を手がけ、「その名にちなんで」のミーラ・ナーイル監督がメガホンをとる。「ボーイズ・ドント・クライ」「ミリオンダラー・ベイビー」で2度のアカデミー主演女優賞を受賞したヒラリー・スワンクがタイトルロールを演じ、リチャード・ギア、ユアン・マクレガーが脇を固める。華々しい偉業を成し遂げたアメリアは、世界中の人々を魅了した。そして夫ジョージの支えのもと、アメリアは世界一周飛行という最後の挑戦に挑む。", "2010-11-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GORDY", "ぼくはゴーディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ヒット作「ベイブ」同様、ブタが主人公のファミリー映画。パパとママと５匹の兄弟がトラックで連れさられてしまった子ブタのゴーディは、家族捜しの旅に出て、カントリーシンガーの少女ジニーと出会う。パーティーで演奏するジニーは、食品会社ロイス産業の社長の孫ハンキーと友だちになる。誤ってプールに落ちたハンキーを助けたゴーディは、マスコミに取り上げられ一躍ヒーローに。ハンキーにゴーディを譲るジニー。ゴーディに目をつけたロイス社長は、彼をCFに起用するが、裏でゴーディを消そうとたくらむヤツらがいた。オーストラリア産の「ベイブ」にシニカルな視点があったのに対し、こちらはディズニーらしく家族愛を全面に出している。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("肥龍渦江／ENTER THE FAT DRAGON", "燃えよデブゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "香港映画界の巨漢サモ・ハンが、日本での人気を決定づけたパロディ快作。田舎出の太っちょ青年ウォン・ロン（「ドラゴンへの道」の主人公はタン・ロン）が、中華レストランの用心棒として悪者たちから町を守るという物語で、ブルース・リーの主演映画の名場面を巧みに継ぎはぎしつつ笑わせる。この作品のヒットで“デブゴン”が彼の愛称になり、以降、旧作の公開時にはシリーズのごとくその名が繰り返し冠せられたが、それらは本来独立した作品。英語題名はもちろん「燃えよドラゴン（゛Enter the Dragon\"）」の、中国語題名は「ドラゴンへの道（猛竜渦江）」をもじったもの。", "1980-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The King and the Clown", "王の男", "", "", "http://image.eiga.k-img.com/images/movie/1475/315x210.jpg?3914", 122, "数々の賞を総なめにした舞台劇を原作に、2人の大道芸人と韓国史上最悪の暴君と呼ばれたヨンサングンの運命を描く歴史ドラマ。16世紀初頭の韓国。幼なじみの旅芸人チャンセンとコンギルは国一番の芸人になるという決意を胸に都・漢陽にやってくる。宮廷を皮肉った芸により人気者となった2人だったが、王の側近に捕られ、「王を笑わせることが出来なければ処刑する」と言い渡される……。05年韓国において歴史を塗り替える大ヒットを記録、大鐘賞では作品賞を含む10部門で受賞。", "2006-12-09", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LONE WOLF MCQUADE", "テキサスSWAT", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "一匹狼のテキサス・レンジャーの超人的な活躍をヒロイックに謳い上げる痛快なアクション映画。タフな男マッケードが娘を誘拐した武器商人の密輸基地へ……。クライマックスはノリスとキャラダインの一大カラテ対決。マカロニ・ウエスタン風の悪ノリが楽しい。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEDOTTA E ABBANDONATA", "誘惑されて棄てられて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ペピーノはマチルデの婚約者だが、ある日、衝動的に彼女の妹アネーゼを強姦。父親は怒ってアネーゼとの結婚を約束させるのだが……。男女関係の哀歓を、社会風刺を込めて描いたP・ジェルミ監督のコメディ映画。同監督の“シシリー”シリーズと呼ばれる一編。", "1963-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野獣死すべし 復讐篇", "野獣死すべし 復讐篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "大藪春彦の傑作小説をモチーフに撮られたハードボイルド・ロマン。幼い頃、目の前で両親を惨殺された伊達邦彦は静かに行動を開始した。銀行強盗で得た金で銃を買った彼は新東組の若頭、組長を次々と抹殺していく。そして、最終目標である市会議員の矢島に接近した邦彦はその秘書、貴美子と出会う。かつて矢島の息子、雅之にレイプされた彼女は、邦彦に彼を殺してくれと頼むのだが……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEND　ME　NO　FLOWERS", "花は贈らないで！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "R・ハドソンとD・デイの二大スター共演で描くハートフル・コメディ。気弱な男ジョージは、病院で自分が余命幾ばくもないと勘違いしてしまう。彼は親友の弁護士に打ち明け、最愛の妻ジュディのために再婚相手を見つけるように依頼。やがて彼の不審な行動から、妻は彼が浮気していると思い込んでしまい……。愛するがゆえに陥る夫婦の騒動を名匠N・ジュイソンが軽やかなタッチで描く。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A DAY AT THE RACES", "マルクス一番乗り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "大ヒット作「オペラは踊る」に次ぐマルクス兄弟MGM移籍第２作。舞台をオペラから競馬場に移し、チコとハーポが保安官相手にちゃっかりお金を巻きあげたり、グルーチョがデタラメな診察をしたりと、例によって３兄弟が荒唐無稽のギャグを連発。ミア・ファローの母M・オサリヴァンの出演も話題。DVDは「マルクス・ブラザーズ コレクターズ・ボックス」に収録。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まぐろのしっぽ", "まぐろのしっぽ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "シャ乱Qのつんくが出演する深夜番組『つんくタウン』が新しい才能を発掘しようと、１千万円での映画制作企画を実施。そこに応募してきた日大芸術学部中退の小林正和が脚本を執筆、日芸の現役学生、OBが中心となって作り上げた映画。その前後に彼らの撮影風景をつけた形で公開された。物語の主人公は自分の会社の倒産で多額の借金を背負い、１週間後にまぐろ漁船に乗ることになっている男、梶原涼太。一軒の家でその日が来るのを、妹の響と待ち続ける彼に別れた妻や昔の恋人、高校時代からの親友が訪ねて来る……。日芸OBの渡辺監督が、無力感に捕らわれた梶原のカウントダウンの日々を静謐な映像の中に淡々と綴っていく。梶原を見張る金融会社の下っ端社員を演じる、お笑いコンビ“よゐこ”の濱口が味のある演技を見せる。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEN DINOSAURS RULED THE EARTH", "恐竜時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "紀元前100万年の原始時代の地球。自然の怒りを鎮めるため、いけにえにされそうになった美女サンナは、恐怖のあまり逃げ出す。恐竜の谷に迷い込んだ彼女は、生まれたばかりの恐竜の赤ん坊とすっかり意気投合し、そこに住みついてしまう。", "1969-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピョコタン・プロファイル", "ピョコタン・プロファイル", "", "", "http://image.eiga.k-img.com/images/movie/54145/315x210.jpg?1412", 102, "怪しい旅行代理店で卒業旅行を計画していた女子高生のさよりとつみれは、ゴムニク諸島でピョコタンという生物を捕獲する賞金つきのツアーを見つける。つみれを人質にとられ、ツアーに参加せざるを得なくなったさよりは、危険を覚悟しゴムニク諸島へ旅立つ。「片腕マシンガール」の木嶋のりこらグラビアアイドルが多数出演。監督は本作がデビュー作となる梶野竜太郎。", "2008-11-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Uncovered", "レタッチ　裸の微笑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "人気ミステリー作家アルトゥーロ・ペレス＝レベルトによる小説「フランドルの呪画」を、「ブレスレス」のジム・マクブライド監督がケイト・ベッキンセイル主演で映画化したエロティック・サスペンス。チェスを題材にした絵画の修復を依頼された若き修復家ジュリアは、その中に“騎士を殺したのは誰だ”というメッセージが隠されているのを発見する。謎を解き明かそうと動く彼女の周囲で、次々と不可解な殺人事件が起こりはじめ……。", "1994-01-01", "3,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARY OF SCOTLAND", "メアリー・オブ・スコットランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "父の死にともない、生後数日でスコットランド女王となったメアリー。政治的思惑の絡んだ結婚を重ね、様々な陰謀に操られ、最期には断首刑に処せられた薄幸の女王の一生をドラマティックに描く。美貌で多才であるがゆえに運命にほんろうされてしまうヒロインに、K・ヘプバーンが扮する。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("週刊バビロン", "週刊バビロン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ゴシップ系女性週刊誌編集部を舞台に、芸能界の裏事情を赤裸々に綴った苦味あるコメディ。廃刊寸前の『週刊バビロン』で部数倍増のためスクープを狙うしがない中年記者が、政治家のスキャンダル封じに遭い、それをなんとか突破しようとする。色と欲にまみれた世界の裏側で、一筋縄ではいかない生き様を強いられる登場人物たちを活写。「サラリーマン専科」の三宅裕司がめずらしくダーティーな主人公に扮し、セックス・シーンも披露している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HORSE　FEATHERS", "御冗談でショ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "喜劇の天才マルクス・ブラザース主演によるドタバタコメディ。グルーチョが総長を務める大学に、新入生のゼッポ、チコ、ハーポがやってきて起こる大騒動。高尚な学問もアメフト・チーム結成の課外活動もギャグと化す。そしてライバル学校とのアメフト試合の日、誘拐騒ぎが起こったうえに、チームは敵に圧倒的なリードを許し……。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("むっつり右門捕物帖 鬼面屋敷", "むっつり右門捕物帖 鬼面屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "浪人風の男が斬殺される連続殺人が発生。被害者は皆、ひと太刀で殺されている。右門が調べてみると、死んだのはすべて、今はお家断絶になっている大久保石見守の関係者。石見守は黄金を秘蔵していたと言われ、それが事件の糸口と右門は目星をつける……。アラカンとエノケン。ベテランコンビのかけあいも見どころ。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEAST FROM 20,000 FATHOMS", "原子怪獣現わる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "人形特撮の神様R・ハリーハウゼンの映画デビュー作で、“ゴジラ”や“ガメラ”の原形となった作品。アメリカの軍と科学者による水爆実験で、北極の氷河の底から、１億年前の怪獣がよみがえる。怪獣は帰巣本能でニューヨークへ向かうが、全身に放射能を浴びているため、その血が流れただけで全市が危険にさらされることになるのだった。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Viva Joe Strummer: The Clash and Beyond", "VIVA JOE STRUMMER ビバ・ジョー・ストラマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "", "2006-11-25", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PEACEMAKER", "エイリアン・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "姿形は人間そのものだが、能力は人間をはるかに超え、地球上に存在する武器ではかなわないという不死身の凶悪犯を追うため、地球から数万光年彼方に存在する惑星の犯罪捜査官“ピースメーカー”に出撃命令が下る。アクション、サスペンス、ホラー、ロマンス、娯楽映画のエッセンスを満載したノンストップSFアクション。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hot Tub Time Machine", "オフロでGO!!!!!　タイムマシンはジェット式", "", "", "http://ecx.images-amazon.com/images/I/61S58k29FkL._SL210_.jpg", 100, "ジョン・キューザック製作・主演のSFコメディ。アダム（キューザック）ら4人の男は、バケーションとしてスキーリゾートにやってくる。いつものように部屋で酒を飲んで過ごす4人だったが、ホテルの屋外バスタブに入りながら飲んで、翌朝起きると、24年前の1986年にタイムスリップしていた。4人は自らの過去、そして未来を変えるために様々なことにトライするが……。監督は「ハイ・フィデリティ」の脚本をキューザックとともに手掛けたスティーブ・ピンク。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Snow White and the Huntsman", "スノーホワイト", "", "", "http://image.eiga.k-img.com/images/movie/56725/315x210.jpg?3897", 0, "グリム童話の名作「白雪姫」を題材に、悪の女王と戦うヒロインの姿を描いたアクションアドベンチャー。己の権力と美貌を脅かす若き継娘スノーホワイトを抹殺するため、邪悪な女王は狩人を刺客として送り込む。しかし、スノーホワイトはその裏をかいて狩人から戦いの術を学び、悪の女王に対抗する。主人公スノーホワイト役に「トワイライト」シリーズのクリステン・スチュワート、悪の女王役にシャーリーズ・セロン。そのほか「マイティ・ソー」のクリス・ヘムズワース、「パイレーツ・オブ・カリビアン　生命の泉」のサム・クラフリンら若手俳優が共演。監督はCMディレクター出身の新鋭ルパート・サンダースが務め、衣装を「アリス・イン・ワンダーランド」のコリーン・アトウッドが担当。", "2012-06-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワンピース　フィルム（仮題）", "ワンピース　フィルム（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLIND SIDE", "ビジター 欲望の死角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "R・ハウアーが、幸せな夫婦に恐怖をもたらす、謎の男を演じたサスペンス。ロサンゼルスで家具の販売会社を営むダグとリンの夫婦は、メキシコに工場を移転させるため、現地に赴くが、そこで警官を車でひき逃げしてしまう。ロスに戻った二人の所に、メキシコからシェルという男がやって来る。彼は、事故を知っている素振りをして、夫婦の生活に入り込む……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("路傍の石", "路傍の石", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "明治末期。吾一少年は成績優秀だが、借金のかたに奉公に出される。その家の子女、秋太郎とおきぬはかつての同級生。二人は主人とともに吾一につらくあたるのだった。まもなく母のおりんが病で死ぬが、家を出たまま父は帰ってこなかった。すべてに幻滅した吾一は、新たな人生を求めて東京行きの列車に乗る……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("危ない話", "危ない話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ディレクターズ・カンパニーの３監督が、それぞれ、現代社会に潜む“危うさ”をテーマに作ったオムニバス。第１話は、ヤクザの溜まり場になっているクラブに来た酔客・竹中直人の災難、第３話は完全犯罪をたくらむ男女を青春映画タッチで描く。中でも出色は、黒沢清の第２話で、正体不明の二人組に狙われた男の戦いを、ホラー映画のトーンで描いている。なお、当初は長谷川和彦が監督の一人に選ばれていた。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La domenica specialmente", "夜ごとの夢／イタリア幻想譚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "　脚本も担当したトニーノ・グエッラの短編小説を3人のイタリア人監督が映画化。第1話「青い犬」、第2話「特別な日曜日」、第3話「炎の中の雪」、エピローグ＆プロローグのオムニバス形式で構成されている。なかでもジュゼッペ・トルナトーレ監督の「青い犬」は秀逸。犬嫌いのアルフレードは、野良犬にまとわりつかれうんざりしていたが、いつしか奇妙な愛情が芽生え……。主演は「ニュー・シネマ・パラダイス」「イル・ポスティーノ」のフィリップ・ノワレ。", "1991-01-01", "4,2,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沖田総司", "沖田総司", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "草刈正雄が、明るく飄々とした新しいタイプの沖田総司を好演した青春時代劇。ストーリーは、新選組の設立から鳥羽伏見の戦いを経て、沖田が25歳で夭逝するまでを描いている。沖田の恋人となる、おちさを演じる真野響子の初々しい魅力も見どころの一つ。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬と猫と人間と", "犬と猫と人間と", "", "", "http://image.eiga.k-img.com/images/movie/54745/315x210.jpg?4269", 118, "空前のペットブームの影響で、日本国内で年間30万頭以上の犬猫が処分されている実態に迫るドキュメンタリー。監督は「あしがらさん」で路上生活者暮らしを記録した飯田基晴。地道に捨て猫の世話をしてきた猫好き老人が、「動物を大切に思ってもらえるような映画を作ってほしい」と飯田に話を持ちかけたことが企画の発端となり、飯田は処分を行う行政施設、保護を行う動物愛護団体などを訪ね、動物を取り巻く人間模様をカメラに収めていく。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逆鱗組七人衆", "逆鱗組七人衆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "人気作家・嵐山光三郎の同名タイトルの小説を映画化した任侠コメディ。お受験を控えた愛娘の志望校の危機を救うため、組の仲間と共に奮起するしがないヤクザのドタバタ劇を描く。引退していたルビー・モレノが女優復帰したほか、元Winkの鈴木早智子ら話題性のあるキャストにも注目。“有限会社逆鱗組”は、神奈川県を本拠とする暴力団。組長の浅田彰義を筆頭に総勢7人のやくざが、今日もセコいシノギに精を出すが……。", "2005-09-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MORTE HA FATTO L’UOVO", "殺しを呼ぶ卵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "マルコは近代的な巨大養鶏場を経営していたが、実権を握る妻アンナに劣等感を感じ、若い愛人との関係に癒しを求めていた。そんなある日、ついにマルコはアンナ殺害を企てるのだが、その完全犯罪は思わぬ陰惨な事態へと発展していく。サディズムやコンプレックスなど、登場人物の屈折した心理をえぐりつつ、随所に異常な残酷趣味を盛り込んだサスペンス・ミステリー。当時スウェーデン出身の新人E・オーリンの美貌も目を惹いた。", "1968-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Boxcar Bertha", "明日に処刑を…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "1930年代大不況の嵐が吹き荒れるアメリカ・アーカンサスが舞台。女性オーガナイザー、バーサと下層階級出身の労働者ビルは自らの青春を確認するかのごとく列車強盗を重ねていく……。スコセッシの1973年の傑作「ミーン・ストリート」の前に作られた力作で、その才気は十分うかがえる。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すずらん・少女萌の物語", "すずらん・少女萌の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1999年の春から秋にかけて放映されたドラマ『すずらん』の映画化作品。TVでは描かれなかったエピソードで、オーソドックスな母娘の再会劇を展開している。昭和10年、冬の北海道、明日萌。11年前、この駅に捨てられ駅長の次郎に育てられ成長した萌は、ひょんなことから秀次という青年に命を救われる。萌は秀次と交流を深めるが、町では秀次が父親殺しの逃亡犯という噂が広まっていた。一方、次郎は萌の実の母親フキから萌宛ての小包を受け取るが……。「BLUES　HARP」「カリスマ」と寡黙なキャラクターで持ち味を発揮した池内博之が秀次を演じ、映画のキーポイントになっている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("New Year's Eve", "ニューイヤーズ・イブ", "", "", "http://image.eiga.k-img.com/images/movie/56275/315x210.jpg?4783", 118, "「プリティ・ウーマン」のゲイリー・マーシャル監督が、「バレンタインデー」（2010）に続いて描くオールスターキャストの恋愛群像劇。大みそかのニューヨークを舞台に、死期が迫った老人と看護師の交流、1年前に出会った女性が忘れられない男、偶然再会した元カップルなど8組の男女の姿が描かれる。「バレンタインデー」のアシュトン・カッチャー、ジェシカ・ビールをはじめ、ハル・ベリー、ジョン・ボン・ジョビ、アビゲイル・ブレスリン、ロバート・デ・ニーロらが出演。", "2011-12-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO BE THE BEST", "ハードブロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ラスベガスを舞台にしたマーシャルアーツ・ムービー。エリックはマーシャルアーツ世界選手権大会アメリカ・チームの一員。白熱する激戦の中、順当に勝ち進んでいくが、実は敵は外部にいた。試合の賭けを牛耳る街の黒幕ロジャースだった。ロジャースはエリックの婚約者を誘拐し、父親を脅迫していた。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブレイクブレイド　第五章「死線ノ涯」", "ブレイクブレイド　第五章「死線ノ涯」", "", "", "http://image.eiga.k-img.com/images/movie/55765/315x210.jpg?3181", 0, "吉永裕ノ介の人気コミックを劇場版アニメ化したシリーズ全6部作の第5弾。アラカン荒野で対決したバルド大隊とボルキュス大隊は両隊ともに甚大な犠牲を払い、決着のつかぬまま一時の退却を余儀なくされる。そんななか、アテネス軍の一部がライガットの故郷を襲撃するとの報せが入る。弟のレガッツを救うべく村へと急ぐライガットの前に、アテネス軍最強のゴゥレム・ヒュケリオンを操るボルキュス将軍が立ちはだかる。", "2011-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CANDYMAN", "キャンディマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "都会の人々が語りつぐ怪談“都市伝説”を研究中の大学生ヘレンの周囲で、殺人事件が次々と起こる。これは“キャンディマン”と呼ばれる伝説の殺人鬼の仕業なのか。殺人容疑をかけられたヘレンは、やがて伝説の正体を目撃してしまう……。恐怖のなかに深い悲しみをたたえた官能的なロマンティック・ホラー。1993年アヴォリアッツ国際ファンタスティック映画祭で、主演女優賞（V・マドセン)、音楽賞、観客賞を受賞。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幸福の鐘", "幸福の鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ベルリン映画祭で最優秀アジア映画賞を受賞したSABU監督の注目作。失業した工員が、あてもなく歩き始めた先で様々な人々と出会い、次々に事件に巻き込まれていくことを通して、真の幸福の意味について再考するまでを描く。ラストに至るまでセリフの全くない主人公を、表情や動きで演じるSABU監督作品の常連・寺島進の静かで力強い演技が印象的。突然の工場閉鎖で職を失った工員の五十嵐は、作業服のままあてどもなく歩き始める。途方に暮れて土手に腰を下ろす五十嵐は、目の前にいた強面のヤクザの身の上話に耳を貸す。しかし、会話が終わるとヤクザは息絶えてしまう。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Days of Thunder", "デイズ・オブ・サンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「トップガン」の製作者、監督、そして主演が再集結。ストックカー・レースに賭ける青年の姿を、迫力のレースシーンを盛り込んで描く青春ロマン。天性の才能を持つ青年コールはレースカーのビルダーとして名を博したハリーの教えを受け、そのドライブテクニックを磨いていく。悪童ぶりも徐々にエスカレートする一方、クラッシュ事故に巻き込まれてしまうコール。やがて彼は女医クレアとの出会い、そしてライバルとの争いを経て、デイトナ・レース挑戦の決意を固める……。", "1990-06-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("情熱のルンバ", "情熱のルンバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "戦前の「純情二重奏」、戦後の「懐しのブルース」「別れのタンゴ」「想い出のボレロ」に続く佐々木＝高峰＝万城目トリオの歌謡映画。斜陽族の令嬢・薬王寺旬子は母を助けるためにキャバレーの歌姫をして、昼は学生という暮らし。ミュージシャン・宮原は旬子を愛していたが、彼女は資産家の息子と結婚する……。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREASE", "グリース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "髪をグリースで固めた不良少年たちのリーダー、ダニー。お嬢さんタイプのサンディは、彼を憎からず想っているが、今一つ理解できないでいる。そんな二人の恋模様を、派手なダンス・シーンを交えて描く青春ミュージカル。ジョン・トラヴォルタとオリヴィア・ニュートン・ジョンの息の合った演技が見もの。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さまよう刃", "さまよう刃", "", "", "http://image.eiga.k-img.com/images/movie/54545/315x210.jpg?3973", 112, "「秘密」「容疑者Xの献身」など映画化が相次ぐ東野圭吾のベストセラー小説を、行定勲監督作品の脚本を手掛けてきた益子昌一が映画化。主演は「半落ち」の寺尾聰、共演に竹野内豊、伊藤四朗ほか。妻に先立たれ、一人娘の成長だけを楽しみに生きていた長峰は、ある日突然、残虐な少年犯罪により娘を失う。絶望の日々を送る長峰のもとに、謎の人物から犯人の正体を告げる電話が入る。", "2009-10-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お姉ちゃん、弟といく", "お姉ちゃん、弟といく", "", "", "http://image.eiga.k-img.com/images/movie/55525/315x210.jpg?3894", 42, "人気ドラマ「時効警察」などの個性派女優・江口のりこ主演による異色の青春ドラマ。東京郊外のアパートで友達の沙希と同居生活を送る森下なおの25歳の誕生日に、実家を飛びだしてきたという弟・康太郎が訪ねてくる。なおは康太郎をしばらく泊めることにするが、その晩、弟が自分のパンツに顔をうずめている現場を目撃してしまう。なおは弟の行動に戸惑いながらも、不思議な快感を覚えてしまい……。", "2010-06-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alice Doesn't Live Here Anymore", "アリスの恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "初めて日本に紹介されたM・スコセッシの作品。34歳の子連れの主婦が夫に死なれ、女性として２度目のチャンスをつかむまでを描いた秀作。全編、これ主演のE・バースティンの独壇場で、気の強さ、可愛らしさ、優しさを持った中年女性を魅力的に演じている。チョイ役で出てくるJ・フォスターの存在感も見もの。アリスは平凡な主婦だった。だが、ある日夫に死なれ、幼い頃からの夢だった歌手になりたいと、一人息子のトムとともに故郷のモンタレーに旅立つ。道中は多難。アリスに言いよって来る男もいたが、これが若い妻を持つくわせ者。やっとその男から逃げ出し、トゥーソンでウェイトレスの職を見つける。やがて、彼女の前にデビッドという農場主が現れて……。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEAVE HER TO HEAVEN", "哀愁の湖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "女の異常心理を描いた心理サスペンスにして、一方的な偏愛によるメロドラマ性もある作品。流行作家の主人公はエレンと結婚したが、彼女は独占欲と嫉妬心が強いため徐々に狂っていく。溺れる主人公の弟を見殺しにしたのをはじめ、自分の腹違いの妹と夫の仲を疑い、挙げ句の果てには、妊娠したのに自ら事故を装い子供を死産にしたりする。その凄まじさに唖然となる一作。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES SOUS−DOUE´S EN VACANCES", "ザ・カンニング２ アルバイト情報", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「ザ・カンニング［IQ＝０］」の続編で、今回は学生たちが恋やアルバイトに大騒ぎする姿を描いていく。前作で見事に大学入学資格試験を突破したベベルは、アルバイト先で出会ったクロディーヌと意気投合。だが、同じく彼女を気に入ったプレイボーイの人気歌手マンフィスが、二人の仲を壊そうとする。ベベルは悪友たちの助けを借りてクロディーヌを取り戻そうとするのだが……。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん　のび太とロボット王国〈キングダム〉", "ドラえもん　のび太とロボット王国〈キングダム〉", "", "", "http://ecx.images-amazon.com/images/I/31DQE0CGA8L._SL210_.jpg", 81, "テレビでおなじみのドラえもんとのび太たちが、様々な活躍を繰り広げる劇場版シリーズの第23弾。人間とロボットが一緒に住む不思議な世界、ロボット王国。しかし、ロボット嫌いの王女はロボットたちの感情を奪う命令を下す。そんな国から逃げ出したロボット少年・ポコは時空の関係から地球にやってくる。ポコと出会ったドラえもんたちは、彼らを救うために立ち上がるのだが…。「ザ☆ドラえもんズ　ゴール！ゴール！ゴール!!」「ぼくの生まれた日」が同時上映された。", "2002-03-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELGA", "女体の神秘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "マイクロ・レンズによる胎児の成長、出産記録、授乳シーン、子供に対する性教育のあり方などを、ユーモラスなシーンを織り交ぜながら描いた、マジメな性教育作品。受精から生命の誕生までが克明に描かれている。", "1968-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRETTY BOY FLOYD", "悪の実力者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "アメリカ犯罪史上に残る凶悪犯、プリティ・ボーイ・フロイドことチャールズ・アーサー・フロイドの半生を描く。1930年代の大恐慌に揺れるアメリカを背景に、冷徹非情な犯罪を重ねる男の姿と、その男をじわじわと追い詰めていく連邦警察との闘いが、迫真の銃撃戦などとともに綴られていく。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BULLY", "BULLY〈ブリー〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「アナザー・デイ・イン・パラダイス」に続くラリー・クラークの監督第３作。いじめっ子(ブリー)がリンチ虐殺されるという衝撃の実話をもとに、ティーンエイジャーの憂うつな日常を描破していく。南フロリダ。ボビーは幼なじみのマーティをこれまで同様、いじめていた。マーティと恋に落ちたリサにはそれが許せず、仲間のアリやドニーらとボビー殺しを考える。そして計画を成功させるため、プロの殺し屋をひきずり込み……。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Girl 6", "ガール6", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "　生活費を稼ぐためにテレフォン・セックス・センターで仕事を始めた女優ジュディ。ガール6と名づけられた彼女は様々なタイプの女性になれることにハマり込んでいく。常連客もでき、ナンバーワンの売れっ子になったが、幻想はエスカレートして……。自分のアイデンティティを模索しながら、やがて新たな一歩を踏み出していく女性の姿を描くコメディ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STIR OF ECHOES", "エコーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "カルト的人気を誇る怪奇SF作家リチャード・マシスンの小説『渦まく谺〈こだま〉』を映画化。ある日突然、霊感に目覚めてしまった男が、失踪した少女の幽霊を目撃し、彼女の死の真相に迫っていく。悪夢や幻覚に苦しむ主人公に扮したケヴィン・ベーコンの迫真の演技に引き込まれる一編だ。トムは義姉に催眠術をかけられ、霊感に目覚める。そんな彼の前に失踪した少女サマンサの霊が現れ、何かを伝えようとするが……。", "2005-09-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドリームメーカー", "ドリームメーカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "人気グループ、DA PUMPの辺土名一茶と元SPEEDの上原多香子の共演による青春ラブ・ストーリー。音楽が大好きな高校生マサトが、偶然入った小さなレコードレンタル店で貴重盤の山を見付け大興奮。強引にバイトで雇ってもらうことになった彼は、近所に開店した大型レンタル店から客を取り戻そうと、ディスコ・パーティーを企画する。だが、準備が進むにつれ、彼を支えてくれた店の女の子、美希の持病の心臓病が悪化し……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MON CHER SUJET", "私の愛するテーマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ジャン・リュック・ゴダール監督の夫人としても知られるドキュメンタリー映画作家のA・M・ミエヴィル監督、初の長編劇映画。オペラ歌手を目指す娘、二人のボーイフレンドとの交際を続ける母、長年連れ添った夫の死を看取る祖母。三者三世代の女性の生き方を、流麗なタッチで描いていく。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録外伝 大阪電撃作戦", "実録外伝 大阪電撃作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“仁義なき戦い”シリーズの大ヒットによって火がついた東映実録路線の異色作。ひたすら暴力的でエネルギッシュな深作欣二に対し、中島貞夫の狂暴さの中に秘めたシニカルな視線が、この作品に奇妙な味を残している。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE WEISSE ROSE", "白バラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "反戦ビラを各地にばらまき、反ヒトラーを唱えた学生たちによるレジスタンス“白バラ”。これは、第二次世界大戦中に実際に起きた反体制運動を、ドキュメンタリー・タッチで綴った作品。ごく平凡な学生たちがなぜ過激な反戦運動に走ったのか、その純粋なまでの動機から確固たる決意までを、それぞれの鮮烈な青春像を絡めながら緊張感豊かに描き上げていく。監督は、1970年のベルリン映画祭で波紋を巻き起こした問題作「O・K」などで知られるM・フェアホーヘン。", "1982-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大阪の章二クン", "大阪の章二クン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "朝日新聞北海道支社主催の“女性の小説”で入賞を果たした、札幌在住のOLによる体験談を映像化。旅行先で恋した男が忘れられず、沖縄へ向かうOLのひとみ。途中下車した大阪で、彼女は同じ旅で知り合った年下の朗らかな青年・章二と再会。彼とのさわやかな交流が、次第にひとみの心に変化をもたらす。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダーZO", "仮面ライダーZO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "初代から数えて13代目の仮面ライダーが活躍する劇場用映画。自らの形を自由に変えられ、体を破壊されても再生できるネオ生命体、怪人ドラス。この怪物に、麻生勝（実は仮面ライダーZO）が立ち向かう……。かつてのTV版からみればSFXも進歩。新たなる仮面ライダーの世界が生まれている。", "1993-04-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("放浪三昧", "放浪三昧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "監督・稲垣浩、脚本・伊丹万作のコンビによるサイレント映画の第２作。チャンバラ活劇のルーツ的作品。伊達主水は妻を死に追いやったかつての恋仇を斬り、一子・小太郎と放浪の旅に出る。京では、主水は勤皇の志士と疑われ新撰組に狙われる。誘拐された小太郎を無事救い出し、主水父子はまた旅を続けていく。", "1928-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("“私”を生きる", "“私”を生きる", "", "", "http://image.eiga.k-img.com/images/movie/57575/315x210.jpg?1626", 138, "卒業式や入学式で「日の丸」「君が代」を強制する流れに抗ったことで不利益を被りながらも、自らの信念を貫く元教員たちの生き様を追ったドキュメンタリー。「君が代」不起立による処分の取消しを求める元家庭科教員・根津公子さんや、「君が代」のピアノ伴奏を断ったことなどから訓戒処分を受けた元音楽教員・佐藤美和子さん、学校の言論の自由などをめぐり東京都に損害賠償を求めて訴訟中の都立三鷹高校元校長・土肥信雄さんらの姿を通して、「自分が自分であり続ける」生き方とはなにかを見つめなおしていく。", "2012-01-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADVENTURES OF ROBIN HOOD", "ロビンフッドの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "おなじみのロビン・フッドが大活躍する痛快活劇。1935年「海賊ブラッド」の大ヒットにより、一躍ワーナーの剣劇アクションのドル箱スターとして売り出したE・フリンがM・カーティス、O・デ・ハヴィランドとの名コンビで撮り上げた大型活劇で、初期の代表作の一つ。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEPHEN KING'S SLEEPWALKERS", "スリープウォーカーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "モダン・ホラーの旗手S・キングが映画用に書き下ろした脚本を映像化したホラー作品。田舎町に住み着いた、若い処女の生き血を吸って生き延びるヴァンパイア母子が、その正体を知った人間たちと壮絶な死闘を繰り広げる。「ツイン・ピークス」のシェリー役で注目されたM・アミックが、恐怖に怯えるヒロインを熱演。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誰かが私にキスをした", "誰かが私にキスをした", "", "", "http://image.eiga.k-img.com/images/movie/54275/315x210.jpg?4599", 0, "東京のインターナショナルスクールに通うナオミは階段から落ちたショックで記憶を失う。おぼろげに誰かとキスをした覚えがあるものの、それが誰なのか分からぬまま、影のある先輩ユウジ、優しい同級生のミライ、恋人の米国人エースの3人の間で揺れる。第18回東京国際映画祭審査員特別賞を受賞した「カンバセーションズ」のハンス・カノーザ監督が、堀北真希を主演に描くラブストーリー。共演に松山ケンイチ、手越祐也、「ターミネーター4」のアントン・イェルチン。", "2010-03-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージー作戦・くたばれ！無責任", "クレージー作戦・くたばれ！無責任", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "鶴亀製菓では、新しい清涼飲料水ハッスル・コーラを発売すべく社内一の無気力社員・田中太郎に試飲させるや、あっという間に元気100倍！ やがて販売用の新会社が設立され、田中太郎以下７人のスーダラ社員が、あの手この手の販売作戦を展開する。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SCARS OF DRACULA", "血のエクソシズム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "死んだはずのドラキュラがよみがえり、平和な農村は再び恐怖のどん底へと追いやられる。ドラキュラ役者リーが見せる鮮血生々しい迫真の演技と、ラストにおける古城での村人との対決シーンはまさに圧巻。正統派ドラキュラ映画の最後を飾る傑作。", "1970-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シャコタン・ブギ", "シャコタン・ブギ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "車とナンパに明け暮れる若者たちの一晩の青春物語。関西の地方都市を舞台に、様々な男の子、女の子の恋や友情が明るく、楽しく、時に切なく描かれる。「アメリカン・グラフィティ」風の他愛のない話だが、それぞれのひたむきな想いが伝わってくるなかなかの秀作。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Darkest Hour", "ダーケスト・アワー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-05-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARRINGTON", "キャリントン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "39歳という短い生涯のなかで、あふれんばかりの才能を発揮し続けたイギリスの女流画家ドーラ・キャリントンと、同性愛の作家リットン・ストレイチーの愛の生活を描いた異色のラブ・ストーリー。イギリス南岸にある友人の屋敷を訪れたリットンは、金髪の美しい少年に出会う。しかし、その少年は女流画家のキャリントンだった……。英国を代表する演技派女優E・トンプソンが精神と肉体の愛のバランスに苦悩するヒロインを熱演。", "1995-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時の娘", "時の娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "自衛隊を脱走してきた青年と、彼が寄宿することにした旧家の長男で、明日をも知れぬ病床に伏している母に、一目江戸の名残の花火を見せてやりたいと願う男の、それぞれの母を慕う心を描いた作品。鈴木清順が企画した「母に捧げるバラード」が原形となっている。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人斬り", "人斬り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "幕末の刺客・岡田以蔵の半生を、五社英雄監督が、エネルギッシュに活写した力作である。勝新太郎が岡田以蔵を豪快に演じるが、いささかワンパターンの感はぬぐえない。むしろ田中新兵衛役で出演した三島由紀夫の堂々たる剣客ぶりの方が印象に残る。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WINDS OF GOD KAMIKAZE", "THE WINDS OF GOD KAMIKAZE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "俳優の今井雅之が原作・脚本・演出・主演を務め、国内外で高い評価を受けた舞台劇を映画化。アメリカの売れない芸人コンビが、彼らの前世である日本人の姿で太平洋戦争末期の日本にタイムスリップ。神風特攻隊として出撃することになるが……。海外での公開を念頭に置き、日本人キャストが全編英語で演じるという異例の作品。世界初となるグランド・ゼロでのロケや、唯一現存する本物の零戦を使用するなどのこだわりも見せている。", "2006-08-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("剣豪 天狗まつり", "剣豪 天狗まつり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "徳川三代家光治下の寛永15年、天下に散らばる武芸者たちを一堂に会して御前試合を開くと発表。樋口定久に念流を学んだ仏子四郎五郎は、江戸に向かう途中幾度かライバルたちと出会い、立ち合って勝つ。試合の日、四郎五郎は強かったが、結局剣の世界も政治と立身出世につながる汚いものだった。“苦い勝利”を描いた社会派時代劇。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Into Eternity", "100,000年後の安全", "", "", "http://image.eiga.k-img.com/images/movie/56025/315x210.jpg?4085", 75, "フィンランドに世界初の高レベル放射性廃棄物の処分場建設が決定された。固い岩を削り地下400メートルに建設されるその施設は、10万年間の保持が可能だという。しかし、裏を返せば10万年後まで放射性廃棄物が残るということに危惧を抱いたドキュメンタリー作家のマイケル・マドセンは、10万年後の人々にその安全性を伝えることができるのかを検証するため、建設中の調査施設に潜入する。", "2011-04-02", "18,20,14,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CYBORG COPIII", "サイボーグコップ3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "サイボーグ傭兵を生み出すハイテク産業に二人の執行官が立ち向かうSFアクション。TVキャスターのイブリンは、大学生の失踪事件を追ううちに、ハイテクを駆使した人工頭脳研究をしているデルターテク社、シーンの陰謀に気付く。核サイボーグ製造の秘密を知られたシーンは、彼女を捕まえるために、連邦執行官のマックスとセイントを傭う。だが、二人はシーンの悪事に気付き、捕らえられサイボーグとなったイブリンの復讐を果たすために、デルターク社を破壊する。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nochinoi Dozor", "ナイト・ウォッチ NOCHINOI DOZOR", "", "", "http://image.eiga.k-img.com/images/movie/1565/315x210.jpg?3219", 115, "ロシアのダーク・ファンタジー小説3部作を3本に映画化。その第1作の本作は本国で大ヒット。動物に変身する、未来を予知するなど超常能力を持つアザーズ。その対立する2派は停戦協定を結び、夜を見張る光の勢力＝ナイト・ウォッチと昼を見張る闇の勢力＝デイ・ウォッチとして互いを監視している。だが、両者の力の均衡を破る運命の子供が出現。ナイト・ウォッチの一員アントンは、それが行方不明だった自分の息子だと気づく。", "2006-04-01", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アメイジング　グレイス　儚き男たちへの詩", "アメイジング　グレイス　儚き男たちへの詩", "", "", "http://image.eiga.k-img.com/images/movie/55995/315x210.jpg?2961", 121, "小さな田舎町で育った5人の不良少年たちは暴走族に入り青春を過ごすが、仲間の1人の死をきっかけにそれぞれの道を歩んでいく。片桐蓮は地元でヤクザとなり、親友の宮城大成は上京して新宿歌舞伎町の闇に飲まれていく。やがて対立する2人を、大成の母・小百合や幼なじみのシズクは遠くから見守っていたが……。裏社会で生きる男たちの姿を、窪塚俊介主演で描いたバイオレンスドラマ。ヒロイン役の神田沙也加が主題歌「Amazing Grace」も歌う。", "2011-08-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海を渡る祭礼", "海を渡る祭礼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "港町の宿屋・浅野屋。毎年祭りの季節になると、年に一度の書き入れ時とばかりに旅芸人や香具師たちがここにどっとやって来る。ガマの油売りに猿回し、祭文語りやら鼠取りまで。なじみの顔ぶれだが、今年は一人新顔の居合抜きの浪人が皆の注目を集めていた。祭りの前日、境内での地割りが決まった一同を尻目に、突然やって来た馬芸の一団が手荒に縄張りをしたことから雲行きが怪しくなって……。名匠、稲垣浩が「宮本武蔵」四部作の合間に手掛けた傑作時代劇。貧しい庶民たちの連帯を情感豊かにうたい上げる。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HEROES OF TELEMARK", "テレマークの要塞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "第二次大戦中、ナチスドイツの原爆開発を阻止するため、重水素酸化物製造工場を破壊した、ノルウェーのレジスタンスたちの実生活を映画化したもの。ドキュメンタリー・タッチで描かれ、渋めの仕上がりになっている。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BULL DURHAM", "さよならゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "K・コスナーが、マイナー・リーグの捕手に扮したコミカルな人間ドラマ。ラルーシュは、大リーガー・クラスの豪速球を誇りながら、“超”のつくノーコン。いまいちオツムも足りない彼の教育係としてダラム・ブルズに呼ばれたベテラン捕手クラッシュと、彼と同じく、ラルーシュを大リーガーにすべく“公私にわたる”世話役を買って出る野球狂の熟女アニー。奇妙な三角関係へと至る彼らの姿を中心に筋書きのないドラマ、野球に対する熱い思いをハート・ウォーミングなタッチで描いている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LARKS ON A STRING", "つながれたヒバリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「スィート・スィート・ビレッジ」のチェコの名匠メンツェル監督が、1969年に完成させながらも本国で20年間も上映禁止とされていた問題作。1948年の社会主義国家体制の中、スクラップ再処理工場に収容された多種多様の前歴を持つ人々を、辛らつなユーモアを交えて描いていく。", "1969-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("潜水艦１号", "潜水艦１号", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "日本映画としては初めて本格的に潜水艦を扱った作品といわれる。学校で聞いた日本の潜水艦の発達史に刺激を受け、将来は潜水艦長や造艦設計士になって国に尽くそうと誓い合った二人の少年。彼らがそれぞれの道を歩み、やがてその夢が実現するまでを、少年たちの友情を中心に、潜水学校の訓練や潜水艦乗組員の生活を織り込んで描く、一種の戦時国策映画。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THEM！", "放射能X", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "放射能で巨大化したアリが人間を襲う、という1950年代SF映画の代表作の一つ。アリの出す酸に溶かされた変死体が砂漠の町で発見され、警察や昆虫学者が調査隊を結成。見つかった巨大アリの巣を一行は火炎放射器で焼くが、女王アリだけは生きのびて大都会ロサンゼルスへ飛来する。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Orphans", "オーファンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "荒れ果てた一軒家で暮らす2人きりの兄弟。ある日、酔っ払った男が2人の家に迷い込んできた。兄弟は、身なりの立派なその男を誘拐して身代金を得ようとするが、実はその男がギャングの親分だったことから2人の人生に転機が訪れ……。異色の人間ドラマ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACK KNIGHT", "男の城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "イギリスの中世を舞台に、黒騎士の活躍を描いたコスチューム・プレイ。バイキングの襲撃を背景にしながら、アーサー王を亡きものにしようとたくらむ家臣の奸計、黒騎士とバイキングとの戦い、そして恋が波瀾万丈の物語の中に綴られていく。主演の黒騎士を演じるのは、西部劇俳優として名高いA・ラッド。", "1954-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("矢島美容室 THE MOVIE 夢をつかまネバダ", "矢島美容室 THE MOVIE 夢をつかまネバダ", "", "", "http://image.eiga.k-img.com/images/movie/55185/315x210.jpg?4394", 98, "フジテレビのバラエティ番組「とんねるずのみなさんのおかげでした」から生まれた人気ユニット「矢島美容室」の、結成からデビューまでの秘密を描くコメディ。アメリカのネバダ州で家族仲良く美容室を営む矢島家だったが、ある日突然、父の徳次郎が家出をしてしまう。母のマーガレット（木梨憲武）はショックを乗り越え必死に家庭を支えようとするが、オスカー女優を夢見る長女のナオミ（DJ OZMA）とソフトボールに夢中な次女ストロベリー（石橋貴明）も、それぞれの抱える問題に悩んでいた。", "2010-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不灯港", "不灯港", "", "", "http://image.eiga.k-img.com/images/movie/54475/315x210.jpg?3876", 101, "若手監督の登竜門・ぴあフィルムフェスティバル（PFF）で第18回PFFスカラシップを獲得した作品。監督は本作が劇場デビューとなる内藤龍嗣。寂れた港町で父の跡を継ぎ漁師をしている万造は、酒以外の趣味もなく、ひとり寂しく暮らしていた。嫁を探してお見合いパーティに出かけるも、時代錯誤なファッションと寡黙な性格で誰にも相手にされない。そんな万造が、ある日、コケティッシュな女性・美津子に一目ぼれをする。", "2009-07-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　OTHER　FINAL", "アザー・ファイナル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "2002年、FIFAワールドカップ決勝と同日の6月30日に行われたもう一つの決戦、ブータン代表とモントセラト代表のFIFA公式ランキング最下位決定戦を追ったドキュメント。開催に至った経緯と当日までの両国選手の交流を絡めながら、２万5000人もの観客を集めた、熱狂の試合の模様を伝える。韓国とのレベル差は歴然としているが、サッカーに真摯に取り組む両国選手の真剣な眼差しが印象的。", "2002-01-01", "70,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dawn of the Dead", "ドーン・オブ・ザ・デッド", "", "", "http://image.eiga.k-img.com/images/movie/1355/315x210.jpg?3494", 100, "ジョージ・A・ロメロ監督の傑作ホラー、「ゾンビ」（78）をリメイク。突如、動き回る死体が大量発生。アナは数人の生存者ともにショッピングモールに立て籠もるが、周囲は死人たちに取り囲まれていた。監督はCMやクリップで注目を集め映画初挑戦する英国出身のザック・スナイダー。脚本は「スクービー・ドゥー」シリーズのジェームズ・ガン。ヒロインはアトム・エゴヤン監督作品で知られるサラ・ポーリー。", "2004-05-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Open Graves", "ファイナル・デス・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/54915/315x210.jpg?4521", 88, "大学生のジェイソンは、友人たちとスペイン北西部にあるビーチリゾートでバカンスを過ごしていた。ある日ジェイソンは、ふと立ち寄った骨董品店で「マンバ」と呼ばれる不気味なボードゲームを購入する。そのゲームには魔女狩りで迫害された魔女たちの呪いが込められていた。何も知らないジェイソンは、ビーチで出会った女の子や仲間たちとボードゲームを始めてしまい……。主演は「クローバーフィールド／HAKAISHA」のマイク・ボーゲル。", "2009-11-07", "1,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Am Legend", "アイ・アム・レジェンド", "", "", "http://image.eiga.k-img.com/images/movie/53135/315x210.jpg?1953", 100, "これまでにも2度映画化されたリチャード・マシスンの名作SF小説「地球最後の男」を、ウィル・スミス主演で映画化。人類が死に絶えた2012年の地球。ニューヨークでたった1人生き残った科学者ネビルは、愛犬サムを唯一の話し相手に究極の孤独を味わっていた。無線で毎日メッセージを流し続けているが、生存者からの返答はない。それでも諦めずに人類再生の手段を探るネビルだったが……。監督は「コンスタンティン」のフランシス・ローレンス。", "2007-12-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旗本やくざ", "旗本やくざ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "元禄泰平の江戸。幕府の財政を立て直すべく米価の値上げを老中、丹波守がはかる。納得のいかない江戸っ子たちは市中で大騒ぎを起こし始めた。町を鎮めるために旗本を使い警備にあてるがその裏では……。大川橋蔵を主演に、中島貞夫と倉本聰のコンビで描いた人情時代劇。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Twentieth Century", "特急二十世紀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "自称\"ブロードウェイの鬼才\"の舞台演出家ジャフィーは無名の大根役者を新人女優\"リリー・ガーランド\"に仕立て上げ、大成功を収める。だがリリーはジャフィーの干渉と束縛に耐えかね、ハリウッドへと去ってしまう。リリーを失ったジャフィーは半狂乱になるが……。ホークスにとっての全面的なトーキー喜劇映画で、彼独特のスクリューボール・コメディの特徴が既に現れている。", "1934-11-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("唐山大兄／THE CHINESE CONNECTION", "ドラゴン危機一発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "香港カンフー映画の大スター、ブルース・リーの主演第１作。田舎の農村に住むチェン・チャオワンは、大雨の洪水のため農作物に大被害を受け、町の製氷工場で働く決意をする。だがそこには、麻薬にからむ不正が横行していた。正義漢チェンの活躍が爽快。", "1971-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADY PLASTIC", "LADY PLASTIC", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「鉄男II」の特殊メイクスタッフでもあった漫画家、深谷陽の原作を映像化したホラー・ファンタジー。30年前に製作中止となった映画が再び撮影されることに。ところがカメラマンやヒロインが相次いで不可解な事故に遭う。不穏な空気が流れる撮影所に、やがて30年前の亡き主演女優が姿を現し……。恐怖を盛り立てる凝ったCG映像が見どころ。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("村ってさいこう　鬼川村編", "村ってさいこう　鬼川村編", "", "", "http://image.eiga.k-img.com/images/movie/57375/315x210.jpg?3253", 16, "芸能事務所イトーカンパニー所属の俳優たちが出演するショートムービーをネット配信している「イトーチューブ」で公開された作品を映画館で上映する「イトーチューブ Shrot Movie Colleciton」の1本。日本の秘境をめぐるバラエティ番組の撮影クルーが、訪問先の鬼川村でとある民家に突撃取材。すると家の中からサングラスをかけた鬼たちが現れて……。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pirates! Band of Misfits", "ザ・パイレーツ！　バンド・オブ・ミスフィッツ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOOK WHO’S TALKING NOW！", "ワンダフル・ファミリー　ベイビー・トーク3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ヒット・コメディ“ベイビー・トーク”シリーズの第３作。６歳になったマイキー、４歳のジュリーに代わって、ここではペットの犬二匹が言葉を交わす。物語は、兄妹のパパが化粧品会社の美人社長の専属パイロットとなり、ママは子育てに奮闘しながら、夫に仕掛けられた誘惑の罠に立ち向かうというもの。ビデオタイトルは「ベイビー・トーク３　ワンダフル・ファミリー」。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ERRAND BOY", "紐育ウロチョロ族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ディーン・マーティンとの“底抜け”コンビを解消した喜劇王ジェリー・ルイスが、自身のプロダクションで製作した第一作。愚連隊の若者を更正に導こうと考えている警官マイクと出会った、ダメ男シドニー。親しくしてくれるマイクとの交流によって、彼は自分も警官になろうと警察学校に入学する。しかし、13週間の訓練は騒動続きで……。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私をスキーに連れてって", "私をスキーに連れてって", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "『見栄講座』で若者のスノビズムやトレンド意識を笑いの対象として人気を得た、ホイチョイ・プロダクションの馬場康夫が監督にあたった作品で、スキー場を舞台に、若者たちの恋のさやあてが描かれる。が、もちろんただのラブ・コメディには収まらず、モノやテクニックに関するこだわりはホイチョイプロならではの凝りよう。原田知世や三上博史といった人気俳優が個性豊かに好演し、また編集段階でやたらに切りすぎたために生まれてしまったという、妙なテンポの良さがうけて大ヒットした。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SERGEANT PEPPER", "サージェント・ペッパー／ぼくの友だち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "デビュー作「マーサの幸せレシピ」で高い評価を得た女性監督サンドラ・ネットルベックが描く、少年と犬の友情物語。人間になりたくなくて、トラの着ぐるみを着る男の子フェリックスの奇抜な行動と愛らしい仕草が、なんともキュート。ちょっと変わった少年フェリックスは、ある日ペッパーという名の、人と会話ができる犬と出会った。実はペッパーは以前の飼い主から莫大な遺産を相続し、財産を狙う人々から追われていた。", "2006-05-20", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サンダカン八番娼館 望郷", "サンダカン八番娼館 望郷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "第４回大宅壮一ノンフィクション賞を受賞した原作を、社会派の熊井啓が映画化した感動作。東南アジアから売春の出稼ぎに来た“ジャパゆきさん”の方が有名だが、その言葉のもととなった“からゆきさん”の悲しく衝撃的な話を力強く描いている。この作品の３年後に他界した大女優・田中絹代の名演は永遠に忘れられることはないだろう。女性史研究家の三谷圭子は、“からゆきさん”と呼ばれる海外売春婦のことを調べているうちに貧しく孤独に暮らす老婆と出会う。圭子は老婆の家で共同生活をしながら、老婆の語る過去を記録していく。カットバックで回想されるそれは、想像もできないほどの重い事実だった。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Namesake", "その名にちなんで", "", "", "http://image.eiga.k-img.com/images/movie/53125/315x210.jpg?1936", 122, "1977年、アメリカの大学で工学を学ぶアショケは故郷インド・コルカタで美しいアシマと見合い結婚をし、夫婦2人でニューヨークに移住する。やがて誕生した長男に、“未来が良くなるように”と願いを込めて、ある名前を授けるが、成長した息子は名前のことで友人にからかわれ、その名をめぐって苦悩する……。「モンスーン・ウェディング」でベネチア映画祭金獅子賞を受賞したミーラー・ナーイル監督が、インドからアメリカに渡った家族の絆や親子の愛を描くヒューマンドラマ。", "2007-12-22", "1,21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЩИНЕЛЬ／OVERCOAT", "外套", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ゴーゴリの名作を、「鶴は翔んでゆく」に主演した俳優出身のA・バターロフが、ソ連映画として二度目の映画化。外套を買うことが願いだった男が、念願かなってようやく手に入れるが、買った早々に盗まれ、失意のうちに死んでいく……。おなじみの物語を、正統的なリアリズム描写で描いた文芸映画。", "1959-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　PHANTOM　OF　THE　OPERA", "オペラの怪人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "フランスの推理作家G・ルルーの怪奇小説を、「汚名」の名優C・レインズを主演に据えて映画化。硫酸で焼けただれた顔を仮面で隠し、パリのオペラ座の地下に身を潜めた悲運の音楽家が、実の娘である駆け出しの少女歌手を晴れ舞台に立たせようと暗躍する。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLESH FOR FRANKENSTEIN", "悪魔のはらわた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ウォーホルが製作した倒錯的作品。内臓嗜好癖があり、実の妹と結婚しているフランケンシュタイン博士が、新人類創造に意欲を燃やす。男女の人造人間を造り、子供を生ませようとするのだが、男にゲイの頭を使ったために失敗してしまい……。劇場公開時は3Dだった。", "1973-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ice Storm", "アイス・ストーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "1973年、ニューヨーク郊外コネティカット州。一見幸せそうに暮らすカーバー家とフッド家の二つの家族があった。しかし、親同士は複雑な不倫関係にあり、思春期の子供たちも性的関係を持つようになる。不倫された妻の精神的不安、裏切りと誘惑。心が通い合わない家族の孤独と悲しみを、凍てつく嵐の夜が飲み込んでいく……。多彩な登場人物ひとり一人を、印象的なエピソードで描き分けるアン・リー監督の手腕が光る。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ERENDIRA", "エレンディラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "G・G・マルケスの幻想的な中編小説の映画化。強欲な祖母に仕える従順な娘エレンディラは、ある夜不運にも風のため家を全焼。償いに砂漠を巡回する少女娼婦として金を稼ぎ、名を馳せる。ある晩、天使のような青年ユリシスに恋し……。祖母役をI・パパスが怪演。", "1983-01-01", "2,19,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("象のなみだ", "象のなみだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "「お姉ちゃん、弟といく」「ユリ子のアロマ」の吉田浩太監督が、自主制作時代に自身の体験をもとに撮りあげたシュールな人間ドラマ。上野のラブホテルでセックスに熱中する若い男女。いつもより激しく乱れる女に、男は戸惑いを隠しきれない。やがて彼らは、ホテルを出て動物園へと足を運ぶ。まるで子どものように無邪気にはしゃぐ2人。しかし突然、女が押し黙り、自分が妊娠したかもしれないことを男に告げる。", "2010-06-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウォーターボーイズ", "ウォーターボーイズ", "", "", "http://ecx.images-amazon.com/images/I/21HQZ2CN6JL._SL210_.jpg", 91, "“男のシンクロナイズドスイミング”という実在したユニークな題材をベースに、平凡な高校三年生たちの切なくもおかしい夏休みをさわやかに描いた青春映画のヒット作。廃部寸前の男子高校水泳部の顧問に美人新米教師が就いたことから、部員は急増。しかし、彼女が本当に教えたかったのは、競泳でなくシンクロナイズドスイミングだった。先生に言いくるめられて残った部員はたった５人。彼らは、渋々シンクロをするハメに……。「スウィングガールズ」の矢口史靖が、真正面からのコメディ作りを目指して一級のエンターテインメント作品に仕上げた。プールの中で人間ピラミッドを作り上げるクライマックス・シーンでは、達成感に満ちたウォーターボーイたちの姿に思わず胸が熱くなる。", "2001-09-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("情熱のステップ", "情熱のステップ", "", "", "http://image.eiga.k-img.com/images/movie/54855/315x210.jpg?4434", 111, "ダンサーを目指す青年の成長と愛を描いた、韓国・シンガポール・アメリカ合作による青春ドラマ。幼い頃に社交ダンスに魅了されて以来、ダンサーを夢見ている韓国人青年テサン。シンガポールにある有名なダンス学校のオーディションに合格した彼は、元世界チャンピオンであるエミの指導の下で才能を開花させていく。主演は「僕の彼女を紹介します」のチャン・ヒョク、共演に「シャンハイ・ナイト」のシンガポール人女優ファン・ウォン。", "2009-12-05", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Airport 1975", "エアポート'75", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "パニック映画ブームの中で作られた、「エアポート」シリーズ第２作。ワシントンから飛び立ったボーイング747の操縦席に、小型飛行機が衝突。機長をはじめ、操縦士がみな重傷で操縦不能になってしまう……。高速ヘリから事故機に飛び移ろうとする、サスペンスあふれるスタント・シーンに加え、豪華キャストの競演も見もの。「アメリカン・グラフィティ」が機内映画としてかかるのにも注目。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("タクミくんシリーズ Pure ピュア", "タクミくんシリーズ Pure ピュア", "", "", "http://image.eiga.k-img.com/images/movie/55855/315x210.jpg?4203", 79, "ごとうしのぶ原作の人気ボーイズラブ小説「タクミくんシリーズ」の実写映画化第4弾。祠堂学院の入学試験で、試験監督を手伝っていた生徒の三洲新にひと目ぼれしてしまった真行寺兼満。祠堂学院に入学後、三洲への猛烈なアタックを開始する。ついに三洲の恋人となった真行寺だったが、生徒会長を務める多忙な三洲とはすれ違ってばかりいた。主演を務めるのは、前作「美貌のディテイル」でも三洲を演じた馬場良馬。", "2010-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Head in the Clouds", "トリコロールに燃えて", "", "", "http://image.eiga.k-img.com/images/movie/1205/315x210.jpg?4578", 121, "イギリスの貧乏学生ガイは、美貌で名高い上流階級の娘ギルダと一夜を共にする。やがてパリに渡ったギルダに誘われ、ガイはギルダと彼女の友人ミアと3人で暮らすようになるのだが、その頃ヨーロッパには戦争の暗い影が差し込んでいた。シャーリーズ・セロンが「モンスター」後に初主演した大河ドラマ。「キャメロット・ガーデンの少女」のジョン・ダイガン監督、脚本。撮影はアトム・エゴヤン作品の常連、ポール・サロッシー。", "2004-10-30", "1,3,9,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PINOCCHIO", "ピノキオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "主題歌『星に願いを』とともに、ディズニー・アニメ史上に燦然と輝く不朽の名作。あやつり人形“ピノキオ”と彼を取り巻く多彩なキャラクターたちが繰り広げる笑いと涙の大冒険を、華麗な色彩と特撮を駆使して描いていく。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大江戸出世双六", "大江戸出世双六", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "高田浩吉が一心太助に扮し、正月映画らしいにぎやかな顔ぶれが揃った娯楽時代劇。大久保彦左衛門の子分、一心太助は、源兵衛長屋に移り住み魚屋を開業。この長屋にお忍びでやってきた将軍家光は、すっかり町人生活が気に入り、おちかという娘に恋心を抱く。ところがおちかは、大阪夏の陣で逃亡したと言われる旗本の娘だった……。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真赤な恋の物語", "真赤な恋の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "メリメの『カルメン』を翻案したメロドラマ。赴任早々、密輸組織への潜入を命じられた立野三郎刑事は、キャバレー“ハバネラ”へと出向く。そこの支配人・鬼頭は組織のNo.2で、彼の情婦は摩紀。そんな摩紀の魅力に惹かれた立野は、職務を忘れて情欲に溺れていく……。この年、吉田喜重監督との婚約を発表した岡田茉莉子が、脂のノッた女の魅力を見せている。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCANNERS II：THE NEW ORDER", "スキャナーズ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "クローネンバーグがその名前を一躍世界に知らしめた「スキャナーズ」の続編。スキャナー（超能力者）を利用して街を支配しようとたくらむ警察署長フォレスターと、それを阻む正義のスキャナー、デビッドとの壮絶な争いを軸に、悪のスキャナー、ドレクと繰り広げるサイキック・バトルが見もののアクション映画。クローネンバーグのアイデアを最大限に生かすため、カナダ映画界の若手が総結集された。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月形半平太", "月形半平太", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "大正８年に新国劇で上演されて大ヒットした、行友季風の戯曲を翻案した衣笠貞之助監督作品。衣笠にとって大正15年度作品に次ぐ２度目の映画化で、同原作は、他の監督の作品も含め10回以上映画化されている。勤皇派でありながら開国を唱える長州浪士、月形半平太をトップスター・長谷川一夫が演じ、若尾文子、山本富士子らの美女が花を添える。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The King of Comedy", "キング・オブ・コメディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "", "1984-05-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Crimson Wing: Mystery of the Flamingos", "ディズニーネイチャー　フラミンゴに隠された地球の秘密", "", "", "http://image.eiga.k-img.com/images/movie/54445/315x210.jpg?3722", 79, "かつて自然ドキュメンタリー映画でアカデミー賞を受賞したウォルト・ディズニーの意思を継ぎ、2008年に立ち上げられたディズニーの自然ドキュメンタリー専門レーベル“ディズニーネイチャー”の第1弾。アフリカ、タンザニア北部にあるナトロン湖には、毎年雨季になると約10万羽のフラミンゴが飛来する。強い毒性を含む湖で新たな生命を育み、再び過酷な旅へと飛び立つフラミンゴの姿をカメラが追う。日本語吹替えナレーションに宮崎あおい。", "2009-08-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Enigma", "エニグマ", "", "", "http://image.eiga.k-img.com/images/movie/1385/315x210.jpg?4174", 119, "43年、第2次大戦下のイギリス。ドイツの暗号機「エニグマ」の解読に従事する数学者ジェリコ。連合軍戦艦が危険海域に入るまで残された時間はあと4日。しかも、彼の元恋人クレアが行方不明に。彼は暗号と格闘しながら、クレアの行方を追う。原作はロバート・ハリスの「暗号機エニグマへの挑戦」（新潮文庫）。「恋におちたシェイクスピア」のトム・ストッパードが脚色、「007/ワールド・イズ・ノット・イナフ」のマイケル・アプテッドが監督。", "2003-05-17", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若妻", "若妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "日活大将軍時代、脚本家・如月敏とのコンビで新婚もののブームを作り、そのモダンな感覚が高く評価された伊奈精一監督のメロドラマ。夫の理由なき嫉妬によって、愛する一人娘とはなればなれになった若妻の苦しみと母子愛を、確かな演出で描く。松島詩子と岡晴夫が主題歌を歌っている。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大東京誕生 大江戸の鐘", "大東京誕生 大江戸の鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "慶応から明治に変わる日本の歴史の大転換期を背景に、その渦中に巻き込まれていった人々のそれぞれの人間模様を描いた松竹時代劇35周年記念映画。幕府の中心人物として立った小栗上野介と、父に反発するその子又一、上野介の論敵、勝海舟らが幕末の世を激しく生きていく。映画スターに加え、歌舞伎や新劇など様々な分野からスターが勢ぞろいした。監督は「ひばり姫初夢道中」などで知られる大曾根辰保。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE END OF THE AFFAIR", "ことの終わり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "激しく愛し合いながらも別れを決意した男女の悲恋を、「クライング・ゲーム」のN・ジョーダンがミステリー・タッチで描く。戦時下のロンドン。愛のない結婚生活に虚しさを感じていたサラと小説家ベンドリックスは、一目で恋に落ち、人目を忍んで逢引を重ねる。だが、空爆により重傷を負ったべンドリックスが目覚めたとき、サラは突然別れを告げた。それから２年後、サラの夫に彼女が浮気をしているらしいと告白されたべンドリックス。サラの身辺調査を申し出た彼は、２年前の別れの真相を知る。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　ぼくらはヒーロー", "それいけ！アンパンマン　ぼくらはヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "やなせたかし原作の人気アニメーション劇場版第9作「それいけ！アンパンマン　虹のピラミッド」と同時上映の短編。", "1997-07-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LITTLE MERMAID", "リトル・マーメイド　人魚姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "アメリカで大ヒットを飾った、ディズニー長編アニメーション映画の第28作。アンデルセンのおなじみの名作童話をもとに、人間の王子に恋した海の王の末娘アリエルの冒険を、フレッシュに描いている。オリジナル・キャラクターのコメディ・リリーフを巧みに配して、原作の暗いイメージを払拭。さらに海の魔女を悪玉に仕立てて、クライマックスで派手な対決シーンを繰り広げ、悲恋のドラマとなるべきところをハッピーエンドにしてしまうなど、いかにもディズニーらしいと言うべきか、アメリカ人好みの明朗なファンタジーになっていて、軽快なテンポで十分に楽しめることは間違いない。アカデミー最優秀オリジナル作曲賞と主題歌賞にも輝き、ディズニーの第２期黄金時代の曙となった。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("走り屋ZERO I", "走り屋ZERO I", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "峠や高速道路を走り回る走り屋の姿を描いたカー・アクション。ドライビングの基礎がないため天性の才能を活かせない走り屋・カズキだったが、走り屋界最速の称号「ZERO」を持つ祐一に才能を見抜かれ、祐一の手ほどきによりドライビング・テクニックを磨いていく。そんなある日、祐一は最強の刺客・甲斐とのレースバトル中に事故にあってしまう。お笑い芸人の庄司智春（品川庄司）、ヒデ（ペナルティ）らが出演。", "2009-06-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bucket List", "最高の人生の見つけ方", "", "", "http://image.eiga.k-img.com/images/movie/53285/315x210.jpg?2165", 97, "2大オスカー俳優ジャック・ニコルソンとモーガン・フリーマンが初共演を果たした人間ドラマ。実直な自動車整備工のカーター（フリーマン）と豪放な実業家エドワード（ニコルソン）はガンで入院した病室で出会い、ともに余命半年を宣告される。“棺おけに入るまでにやっておきたいこと”を書き出した“バケット（棺おけ）リスト”を作った2人は、リストを実現させるため人生最後の旅に出る……。監督は「スタンド・バイ・ミー」のロブ・ライナー。", "2008-05-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CURLY SUE", "カーリー・スー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ヒット・メーカー、J・ヒューズが手掛けたほのぼのタッチのラブ・コメディ。保護者のビルとともに街々を流れ歩いていた女の子スーが、お金持ちでクールな女流弁護士と出会って巻き起こす騒動の数々をユーモアたっぷりに描いていく。主人公スーを演じたA・ポーターの、小生意気でおチャメな可愛らしさも魅力。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の降る午後", "花の降る午後", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "港町・神戸のシャレたフランス料理店を舞台に、ファッショナブルな衣装をまとって展開するサスペンス。夫の死後、シェフたちの助力で老舗レストランを切り盛りしてきた若い女主人が、麻薬密売に手を染める謎の女の陰謀から店の窮地を救う。喰うか喰われるか女と女の戦いが熾烈。神戸市制100周年を記念し、宮本輝の同名小説を大森監督が、異国情緒満点の街の風光を生かして手堅くまとめた。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎恋やつれ", "男はつらいよ・寅次郎恋やつれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "吉永小百合が「柴又慕情」に続いて登場する。寅さんは夫に蒸発されて苦しむ女性を助けたりで忙しい。そんななか、山陰の津和野で以前、片想いの相手だった歌子と再会。夫に死別した彼女の力になり、偏屈な父親との和解を仲介する。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女のつり橋", "女のつり橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "木村恵吾が自らの脚本を映画化した３話から成るオムニバス映画。マッサージ屋で働く娘の片思いと破局、同じアパートを借りてそれぞれの相手から金を取っていた二号の姿、孤独を感じるストリッパーの心情を描く。女の純情、愚かさ、弱さがそれぞれの話によって浮き彫りにされる。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gli amici del Bar Margherita", "バール・マルゲリータに集う仲間たち", "", "", "http://image.eiga.k-img.com/images/movie/55425/315x210.jpg?1617", 90, "1954年、イタリアのボローニャ。18歳の青年タッデオの家の近くにある「バール・マルゲリータ」には、いつも個性あふれる男たちが集まっては杯を交わしていた。いつか自分もその仲間に加わることを夢見るタッデオは、バールの中心的存在のアルと知り合う。タッデオは彼の専属運転手となり、店の常連たちの陽気な世界を体験していく。イタリア映画祭2010にて上映。", "2010-04-29", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love in the Afternoon", "昼下りの情事", "", "", "http://ecx.images-amazon.com/images/I/51fEn3ncw-L._SL210_.jpg", 134, "　パリを舞台にしたロマンチックコメディ。音楽院でチェロの勉強をしているアリアーヌは、私立探偵である父親の仕事に興味津々。素行調査に登場する億万長者のプレイボーイ、フラナガンの存在が気になってしょうがない。一計を案じ、彼との出会いに成功するが、数々の男を手玉に取った遊び人を装ってしまう。2度目のワイルダー作品となるオードリー・ヘプバーンが、背伸びした純情な娘を好演。フラガナン役は当初、ケイリー・グラントが打診されていた。", "1957-08-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姉ちゃん、ホトホトさまの蠱（こ）を使う", "姉ちゃん、ホトホトさまの蠱（こ）を使う", "", "", "http://image.eiga.k-img.com/images/movie/57045/315x210.jpg?2260", 49, "映画美学校で講師も務める大工原正樹監督が、同校の生徒たちと共同で作り上げた中編映画。亡くなった母の葬儀を終え、かつて暮らしていた町を何気なく訪れた妙子と治の姉弟は、次第に心に巣くう化け物の幻に襲われ始める。「恐怖」「行旅死亡人」の長宗我部陽子と、劇団「東京乾電池」の舞台にも立つ岡部尚が、憎しみの輪廻に陥っていく主人公姉弟を演じる。", "2011-09-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Batman Begins", "バットマン・ビギンズ", "", "", "http://image.eiga.k-img.com/images/movie/1015/315x210.jpg?4642", 140, "DCコミックスの人気キャラ「バットマン」の新作。両親を目の前で殺された大富豪ブルース・ウェインが、いかにしてバットマンとなり、悪と戦うに至るかを描く。監督は「メメント」「インソムニア」のクリストファー・ノーランで、キャストにはクリスチャン・ベール、マイケル・ケイン、リーアム・ニーソン、モーガン・フリーマン、ゲイリー・オールドマン、渡辺謙ら演技派が集結。", "2005-06-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Americano rosso", "アメリカから来た男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "イタリア北部の小さな田舎町。女癖の悪いヴィットリオは、叔父の経営する結婚相談所に勤めていたが、その妻にまで手を出してクビに。ある日、アメリカから叔父を頼って一人の中年男ジョージがやってきた。純潔で若い花嫁を探してほしいという。やがて札束をちらつかせるこの男とヴィットリオの花嫁探しの旅が始まるのだが……。愛すべき人々のだましあい、だまされあいをユーモラスに描く。", "1991-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の又三郎 ガラスのマント", "風の又三郎 ガラスのマント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "宮澤賢治の同名児童文学３回目の映画化。今回は原作にない女の子・かりんを設定し、彼女の目から又三郎少年の不思議さを追っていく。「女囚701号・さそり」以来、撮り上げる作品のすべてに“風”を感じさせる伊藤俊也監督の真骨頂ともいえよう。初映画化の1940年版主題歌『ドードドの歌』を、音楽の冨田勲が効果的に使用している。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("疆屍家族・疆屍先生續集／MR.VAMPIRE II", "霊幻道士２ キョンシーの息子たち！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ユニークな妖怪キョンシーが大暴れするホラー・コメディの第２作。墓荒らしの一味によって息を吹き返した親子３人のキョンシー一家が、はなればなれにされたために起こる騒動を描く。可愛いベビー・キョンシーに御注目。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLUB　BUTTERFLY", "クラブ・バタフライ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "仕事と育児に追われて、最近は倦怠期ですっかりセックスレスになっている夫婦。気分転換の旅行でも仲は盛り上がらない。そんな時、夫の元に夫婦交換＝スワッピングの“クラブ・バタフライ”というサイトから勧誘メールが届く。彼は興味を持ちながらも妻に言い出すのは気がひけていたが……。韓国のタブーを扱った問題作。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拝啓天皇陛下様", "拝啓天皇陛下様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "週刊誌に連載された棟田博の小説を野村芳太郎監督が映画化した２部作の軍隊喜劇。“寅さん”以前の渥美清の代表作といえる。孤児あがりの山田正助にとって軍隊ほど住み心地の良い所はない。仲間はたくさんいるし、メシも食えるし……。上官のイビリなど痛くもカユくもないのだ。ところが戦争が終わりそうだという。冗談じゃない、除隊はいやだ、もっと軍隊においてくれ、と彼は天皇陛下に直訴の手紙を書くのであった。日本映画では軍隊をお笑いにするのが長い間タブーだったが、1955年の「二等兵物語」あたりからそれが破られた。本作もその流れにある、哀歓あふれた新兵訓練喜劇である。DVDは「渥美清メモリアル　渥美清・もうひとつの世界」に収録。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLITIKI　KOUZINA", "タッチ・オブ・スパイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ギリシャで大ヒットを記録した、温もりあふれる人間ドラマ。少年時代、スパイス店を営む祖父から人生について学んだ主人公の原点回帰の旅を、回想を交えつつ笑いと涙で綴る。料理におけるスパイスを人生で起こる出来事に例えた、おじいちゃんの名セリフの数々が印象深い。祖父危篤の知らせを受けたファリスの胸に、子供時代の思い出が甦る。当時の祖父はスパイス店を営み、彼と深く心を通わせていたが……。", "2005-02-05", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("＋1 プラスワン", "＋1 プラスワン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "芸能事務所アプレ主催による俳優ワークショップから生まれたオムニバス映画。映画監督が用意したテキストをもとに、プロアマ問わず参加した俳優たちが3日間かけて演技をつくりあげるという過程を経て完成した5本のショートムービーで構成。監督として、「クロエ」の利重剛、「地下鉄（メトロ）に乗って」の篠原哲雄、「怪談新耳袋　ノブヒロさん」の豊島圭介、「発狂する唇」の佐々木浩久ら、個性あふれる気鋭のクリエイターたちが参加している。", "2007-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギョ", "ギョ", "", "", "http://image.eiga.k-img.com/images/movie/57325/315x210.jpg?2256", 0, "アニメーターが「絵にしてみたい」「動かしてみたい」と感じる企画を、TVシリーズでも劇場用映画でもない短編・中編作品として自由にアニメーション化していく「アニメ文庫」第1弾3作品の1つ。「富江」シリーズで知られるホラー漫画家・伊藤潤二の同名コミックを、「劇場版　空の境界」も手がけた平尾隆之監督がアニメ化した。友人たちと沖縄にリゾートに来た華織は、足の生えた奇妙な魚を発見する。やがてその魚はおびただしい数になって上陸を始め、人々を襲いはじめる。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・居酒屋ゆうれい", "新・居酒屋ゆうれい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「居酒屋ゆうれい」が内容、キャストを一新、前作が幽霊をめぐる人情喜劇だったのに対し、今回は波乱に満ちたラブ・ストーリー仕立てとなった。町外れにある居酒屋かづさ屋。前妻に先立たれた店主の壮太郎は、新しい美人の女房・里子と店を切り盛りしていた。ある日、死んだはずの女房・しず子が幽霊となって現れ、二人の女房が火花を散らす暮らしが始まった。そんな中、しず子と生き写しの薄幸な女・ユキエが現れ、壮太郎は心惹かれていく。一方、里子は訳あって別れた昔の男に迫られ、しず子は彼女に思いを寄せるかづさ屋の客と温かい心の交流をかわしていく。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCARY MOVIE3", "最‘狂’絶叫計画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "“絶叫計画”シリーズの第３作。主人公たちが地球侵略を企む宇宙人に戦いを挑む、という破天荒な物語の中で「８Mile」「ザ・リング」などのパロディーが次々に炸裂する。農場でおかしなミステリーサークルが出現。TVレポーターのシンディは謎を追う。そんなある夜、一週間前に奇妙なビデオを見た親友ブレンダが急死。ビデオとミステリーサークルの関係に着目したシンディが、ビデオを再生すると……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE’S BROTHER", "リトル・イタリーの恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "名脚本家ジャン・サルディが初監督したロマンチックなラブ・ストーリー。写真と文通だけで結婚を決意したイタリア人美女の波乱の恋の行方をユーモアを交えて描く。写真でしか知らない文通相手と結婚しようと、はるばるオーストラリアに旅立ったイタリア人のロゼッタ。ところが港に迎えに来たのは写真とは似ても似つかぬ地味な小男アンジェロ。実は、アンジェロはハンサムな弟ジーノの写真を送っていたのだ……。", "2006-03-25", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Heart Huckabees", "ハッカビーズ", "", "", "http://image.eiga.k-img.com/images/movie/1085/315x210.jpg?1741", 107, "「アメリカの災難」「スリー・キングス」のデビッド・O・ラッセル監督が、ソフィア・コッポラの従兄弟である「天才マックスの世界」のジェイソン・シュワルツマンを主演に描くブラック・コメディ。環境保全団体の支部長アルバートは、森と沼地を守るため、巨大スーパー・ハッカビーズのエリート社員ブラッドによる新店舗建設計画に立ち向かう。ブラッド役はジュード・ロウ。ダスティン・ホフマン、イザベル・ユペールらが豪華共演。", "2005-08-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRST KNIGHT", "トゥルーナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "アーサー王と円卓の騎士たちによって治められていたイングランドの理想郷キャメロット。若く美しいグイネビアは、英雄アーサー王の妃となるためにキャメロットにやってくる途中、王の仇敵マラガント一派に襲われる。危機一髪の彼女を救ったのは、流れ者の若き剣士ランスロット。やがてキャメロットへ向かったランスロットは、王への忠誠を誓い円卓の騎士として迎えられるが、彼と王妃グイネビアは、許されぬ思いをお互いに抱いていた……。S・コネリー、R・ギア共演の品格あるアドベンチャー・ロマン。J・ゴールドスミスによるスコアが素晴らしい。DVDタイトルは「トゥルー・ナイト」。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不死情謎／BULLETS　OF　LOVE", "バレット・オブ・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "瀬戸朝香とレオン・ライが共演した、サスペンス・タッチのハードなラブ・ストーリー。香港警察特殊部隊に勤務していたサムは二年前、自分が追っていた武器麻薬密売事件の担当検事だった恋人アンを殺されて、漁村でひっそりと暮らしていた。ある日彼の前に、アンそっくりな日本人女性ユウが現れる。とまどいながらも惹かれ合う二人だったが……。瀬戸朝香が英語と広東語に挑戦。激しいアクションもこなし、一人二役のヒロインを体当たりで演じている。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAN，WOMAN AND CHILD", "愛の７日間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「ある愛の詩」で知られるシーガルの原作を、本人が脚色。妻と二人の娘とともに、平和で幸福な家庭を築いている大学教授のもとに、10年前１度だけ関係を持った女医との子が突然現れたことで、複雑な波紋が家族に広がっていく……。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tootsie", "トッツィー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ホフマン扮する役者マイケルは才能はあるが仕事がない。そこで女装をしてオーディションを受け、昼メロで女優としてデビューすることに。ところが共演女優に言い寄られ……。男女差別を風刺した傑作コメディ。共演した女優に扮するジェシカ・ラングはアカデミー賞助演女優賞を獲得。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大三元／TRI−STAR", "恋する天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「金玉満堂／決戦！ 炎の料理人」の監督と主演コンビが描いたラブ・コメディ。誠実な青年神父ツォンはある日、懺悔室で不幸な身の上を語る娼婦パイに心を痛める。仲間の借金返済に追われるパイを救おうと、娼婦たちが暮らすアパートに住み始めた彼は、彼女らに希望を与えていく。やがて、パイたちは歌手になる夢を抱き、コンテストに向けてレッスンを開始。ところが、売春を取り仕切るマフィアと警察の抗争に巻き込まれ……。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRINZ IN HO¨LLELAND", "プリンス・イン・ヘル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "統一直後のベルリン・クロイツベルグで、人形使いが語るおとぎ話に操られるように狂っていくゲイの恋人たちの物語。ヘロイン撲滅キャンペーンを行うシュテファンとヨッケルのゲイのカップル。しかし、成果も上がらず虚しさを感じたヨッケルは、地下室での危険なセックスに溺れていき、二人は対立してしまう。そこにバイセクシャルのミーヒャが現れ、ヨッケルはヘロインの魔力にのめり込んでいくのだった……。フリーSEX、ヘロイン、ゲイなど現代アナーキストたちの姿をリアルに描いた問題作。", "1993-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Pie 2", "アメリカン・サマー・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/1675/315x210.jpg?2649", 105, "全米大ヒット作「アメリカン・パイ」の続編。今回は大学1年生になった彼らが、夏休みに故郷にもどり、貸別荘を借りて大騒ぎ。出演は、前作でMTVムービー・アワードのベスト・コメディ演技賞に輝いたジェイソン・ビッグス、クリス・クラインらおなじみの面々に加え「エボリューション」の熱血消防士、ショーン・ウィリアム・スコットも参加。前作の監督ポール・ワイツが製作。監督は「ギリーは首ったけ」に続き本作が2作目の新鋭ジェイムズ・B・ロジャーズ。", "2002-03-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アチャコ青春手帖 第４話", "アチャコ青春手帖 第４話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "人のいいタクシー運転手が、次々と騒動に巻き込まれるという“アチャコ青春手帖”もの第４話「めでたく結婚の巻」。1952年、ラジオ・ドラマ「アチャコ青春手帖」が大ヒット。これにあやかって映画化された１本である。トニー谷のソロバン芸など、芸人たちの多彩な顔ぶれが見もの。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("透視する女", "透視する女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "OLの和美は、自分に向けられた他人の欲望を透視する能力を持っていた。そんなある日、彼女を鋭いメスで切り刻むという、変質者の欲望を透視してしまった和美に危機が迫る。エロスをテーマに５人の監督がVTRで手掛けた“MOVIE STORM”シリーズ第３作のサイコ・スリラー。安原麗子が特殊能力を持つがゆえに脅えるヒロインを熱演している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Garbo Talks", "ガルボトーク／夢のつづきは夢…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　会計士ギルバートの母親エステルは伝説の女優グレダ・ガルボの熱烈なファンだった。そんなエステルの余命が残りわずかであることが明らかに。死ぬ前にガルボ本人に会いたいという母親の願いをかなえるべく、ギルバートは隠遁生活を送る女優を捜索する。彼は女優の卵ジェーンの助けを借りながら、方々を訪ね回るが……。往年の名女優をめぐって展開されるコメディ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Babylon A.D.", "バビロンA.D.", "", "", "http://image.eiga.k-img.com/images/movie/54035/315x210.jpg?2424", 90, "一匹狼の傭兵トーロップは、不思議な予知能力を持つ若い女性オーロラをニューヨークまで“運ぶ”仕事を請け負い、カザフスタンからロシア、アラスカ、そしてアメリカへと横断していく。しかし、オーロラを狙う謎の集団が彼らの行く手を阻み……。「クリムゾン・リバー」「ゴシカ」のマチュー・カソビッツ監督が、ビン・ディーゼルを主演に描く近未来SFアクション。共演にミシェル・ヨー、シャーロット・ランプリングなど豪華キャストが集う。", "2009-05-09", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOWHERE", "ノーウェア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "米インディーズの気鋭G・アラキが手掛けた現代的な青春映画。ロスで暮らす大学生たちの行き詰まった青春を、ユーモアと幻想シーンを織り交ぜて描く。自分の死の瞬間を収めるため、常にビデオカメラを持ち歩いている映画学科の学生ダークは、仲間とともにパーティーへ。ところが、友人が消えたり、謎の怪物を目撃したりと、その夜は奇妙な出来事が続発し……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUT ON A LIMB", "マシュー・ブロデリックの緊急事態", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "証券会社に勤めるビルは、14億ドルの取引を目前にして、女強盗に身ぐるみはがされる。財布の中には大事な取引先の電話番号のメモが。おまけに妹からの連絡で殺人事件までもが絡んできて……。「３人の逃亡者」でニック・ノルティのコメディ演技を引き出したF・ヴェベールの演出によるコミカル・サスペンス。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOCE BLANCHE", "白い婚礼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "14歳で歌手デビューし、フランスのアイドルとなったV・パラディの、女優としての出世作。複雑な家庭環境の中で育った17歳の少女マチルドは、ある日、49歳の高校教師フランソワの優しい心にふれ、恋に落ちる。だが二人の関係を周囲は許さず……。中年男との恋に、たった一つの拠り所を見つけた、孤独な少女の心情をパラディが見事に表現。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハダカの城　西宮冷蔵・水谷洋一", "ハダカの城　西宮冷蔵・水谷洋一", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "02年に日本中を騒がせた雪印食品の牛肉偽装詐欺事件。雪印食品は3カ月後に解体となったが、告発者の水谷洋一が社長を務める兵庫県西宮市の倉庫会社・西宮冷蔵も営業停止に追い込まれた。03年10月、西宮冷蔵の再建を目指す水谷は、告発の経緯を著した本を、大阪・梅田の曾根崎陸橋で販売していた……。そんな水谷の活動を追い、西宮冷蔵再建までの険しい道のりと、営業再開から1年間を記録したドキュメンタリー。", "2007-10-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うまれる", "うまれる", "", "", "http://image.eiga.k-img.com/images/movie/55535/315x210.jpg?3624", 0, "“赤ちゃんは親を選んで生まれてくる”という子どもの胎内記憶をモチーフに、妊娠から出産、そして出産と隣り合わせにある不妊や流産・死産といった命のドラマをめぐるドキュメンタリー。ウェブサイトで応募した約200組のなかから選ばれた4組の家族を追い、生まれてくることの意味や家族のあり方、人とのつながりについて考えていく。私生活で良き父親として知られるタレントのつるの剛士がナレーションを担当。", "2010-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADVENTURES OF THE WILDERNESS FAMILY", "アドベンチャー・ファミリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "製作者ダブスが、実際にロッキーに移り住んだという親友の話からヒントを得た作品。公害や疎外感など、都市生活の悩みから離れ、一家そろって脱・都会を実行し、大自然の中で自給自足を始める家族を心温まるタッチで描いている。主役ともいえる動物の描写が見事。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("藍風箏／THE BLUE KITE", "青い凧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "1950～60年代の中国を舞台に、権力にほんろうされていく人々の姿を、一人の女性とその息子の目を通して描く。鉄頭(ティエトウ)の父と母は、共産党政府の指導の下、簡素な結婚式を挙げ、新しい生活を送り始める。1957年に毛沢東主席が清風運動を起こし、党・官僚への批判を半ば強いられた人々は、それぞれの批判を述べるが、右派のレッテルが張られ、僻地へ追放。鉄頭の父も同様だった。やがて労働キャンプで父は無惨に死に、それに責任を感じた彼の同僚、李が母の面倒を見るようになり、再婚。だが、その李も病のため突然、他界。母は鉄頭の将来を案じ、党幹部の老呉と愛のない結婚に踏み切るが、1966年の文化大革命の開始とともに、老呉も紅衛兵に連れ去られてしまう……。ティエン・チュアンチュアン監督自身の人生を半ば自伝的に映画化した作品で、中国の近現代を包括する壮大な叙事詩となっている。1993年の東京国際映画祭にてグランプリおよび最優秀女優賞を受賞。", "1993-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("篤姫ナンバー1", "篤姫ナンバー1", "", "", "http://image.eiga.k-img.com/images/movie/56765/315x210.jpg?1556", 86, "江戸幕府第13代将軍・徳川家定の正室として知られる篤姫がタイムスリップし、現代の荒波の中で成長していく姿を、元「モーニング娘。」の石川梨華主演で描く。薩摩藩主・島津斉彬の養女で、後に徳川13代将軍・家定の正室＝篤姫となる運命を背負った源篤子は、ひょんなことから時空を超え、平成の世にタイムスリップしてしまう。銀座のホステスに助けられ高級クラブで働くことになった篤子は、そこで現代女性の強さやもろさを学んでいくが……。篤子とともに時代を超えてしまう世話係のタエ役で中澤裕子が共演。", "2012-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GATE OF HEAVENLY PEACE", "天安門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 189, "世界に衝撃を与えた1989年6月4日の“天安門事件”を再検証するドキュメンタリー大作。事件当日だけに焦点をあてるのではなく、学生による政府への抗議運動が高まっていった過程とその政治的背景を、時間を追って丹念に解明していく。調査に６年間を費やした監督のR・ゴードンとC・ヒントンは、250時間に及ぶ映像資料を分析。当時のニュース映像はもちろん、学生運動のリーダーや文化人など多数の関係者へのインタビューも収録されている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Miss March", "お願い！プレイメイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・サラリーマン忠臣蔵", "続・サラリーマン忠臣蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「サラリーマン忠臣蔵」の後編。丸菱銀行頭取・吉良剛之介へのうらみを胸に、大石良雄らは“大石商事”を設立。苦しい財政のなか、妻を実家へ帰してしまうなど、道は険しい。12月14日、吉良との決戦の株主総会がやってきた。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN OLD CALIFORNIA", "西部の顔役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ゴールドラッシュの頃のサクラメント。ボストンから新生活を夢みてやって来たトムは町のボス、ブリットの婚約者レイシーと親しくなる。それに気付いたブリットはトムの開く薬屋をなんとか妨害しようと画策するのだが……。J・ウェインが主演した初期西部劇の１本。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jam Films S", "Jam Films S", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "自由なスタンスでショートフィルムを提供する「Jam Films」シリーズの第３弾は、１作目に参加した岩井俊二や行定勲ら有名監督が、気になるクリエイターを“指名する”というバトンタッチ方式。「凶気の桜」でデビューした薗田賢次やCGアーティストの原田大三郎など、バラエティー豊かな顔ぶれが、独自の切り口でエンタメ精神あふれるショートフィルムを製作。小西真奈美や綾瀬はるか、ラッパーのZEEBRAにミュージシャンのスネオヘアーほか、異色のキャスト編成にも注目。平凡なサラリーマンの今野は、新兵器“スーツ”を着用し、日本の危機を救う大役に抜擢された。その頃、マスコミが駆けつけた彼の部屋にはキャバクラ嬢レイコがいて……。(「スーツ-suit-」)", "2005-01-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　BIG　BOUNCE", "悪女のたわむれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ハードボイルド作家の名作を原作にした、犯罪ラブ・サスペンスの会心作。2004年には「ビッグ・バウンス」のタイトルで本作がリメイクされている。男女のエゴがぶつかり合う新しいタイプの映画として当時のハリウッドで話題になった。主人公のホームレスは工場を経営する男とその秘書の女と知り合い、女と肉体関係を持った。そんなある日、女から盗みをそそのかされる。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TARZAN’S　NEW　YORK　ADVENTURE", "ターザン紐育へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "サーカス団に連れ去られた愛するボーイを取り戻すため、ジャングルからニューヨークに乗り込んだターザンとジェーン。彼らはジェーンの提案で法に訴えるが、敵はすでにボーイの養子手続きを済ませていて……。J・ワイズミュラー主演の“ターザン”シリーズ第６作。カルチャーギャップ・ネタのユーモアを織り交ぜ、摩天楼を駆けめぐるターザンの奮闘を活写している。DVDは「ジョニー・ワイズミュラー ターザン・フィルムズ」に収録。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUYING TIME", "ラナウェイ・タイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "MTV界で活躍していたM・ガブリエが撮り上げた青春アクション映画。軽快な音楽にのせて、アメリカの闇の部分とともに、若者たちの青春群像を描き出していく。麻薬密売のトラブルに巻き込まれた友がギャングに殺され、恋人まで誘拐された不良少年たちが復讐を誓い、生死を懸けた戦いに挑む。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博多っ子純情", "博多っ子純情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "長谷川法世原作の人気マンガの映画化。性に目覚め、いまだ見も知らぬ女性の神秘に興味を抱く男子中学生３人組が、憧れの女性とのデートを夢見たり、不良に囲まれた同級生をカッコ良く助けようと意気がったりする様をコミカルに誇張を込めて描いている。“嗚呼!!花の応援団”シリーズでマンガの映画化には定評のある曾根監督が、素人の男子中学生をいきいきと動かしていて、小便の掛け合いをする場面などは、大らかな楽しさにあふれている。最後に繰り広げられる決闘場面は、「けんかえれじい」を彷彿とさせるハチャメチャさ。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　ヒヤ・ヒヤ・ヒヤリコとばぶ・ばぶ・ばいきんまん", "それいけ！アンパンマン　ヒヤ・ヒヤ・ヒヤリコとばぶ・ばぶ・ばいきんまん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "やなせたかし原作の人気アニメーション劇場版第20作「それいけ！ アンパンマン 妖精リンリンのひみつ」と同時上映の短編。", "2008-07-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Little Ashes", "天才画家ダリ　愛と激情の青春", "", "", "http://ecx.images-amazon.com/images/I/513FppksGjL._SL210_.jpg", 112, "", "2008-01-01", "3,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Nativity Story", "マリア", "", "", "http://image.eiga.k-img.com/images/movie/34287/315x210.jpg?2796", 100, "ナザレに住む少女マリアは、婚約者ヨセフとの結婚を控えたある日、天使ガブリエルから「神の子を身ごもる」とのお告げを受け、妊娠する。一方、ユダヤの王ヘロデは“救い主”誕生の預言を知り、王国を奪われることを恐れて“救い主”になりうる者たちの抹殺を命じる。イエスの母マリアを主人公に、キリスト誕生までの苦難の道のりを描いた物語。マリア役は「クジラの島の少女」（02）でアカデミー賞主演女優賞候補となったケイシャ・キャッスル＝ヒューズ。", "2007-12-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　SIMPLE　WISH", "シンプル・ウィッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "新米魔法使いのマレーは８歳の少女アナベルの願いを叶えることにする。しかし、アナベルの“オーディションに落ちてばかりのパパをブロードウェイのスターに”という簡単な希望も、失敗の連続でうまく叶えられず……。M・ショートがドジな魔法使いを演じて笑いを呼ぶファンタジック・コメディ。監督は「がんばれ！ ベアーズ」などの大御所M・リッチー。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Coogan's Bluff", "マンハッタン無宿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "　アリゾナで殺人を犯したリンガーマンがニューヨークで逮捕された。その男を引き取るため、クーガン保安官補が生まれて初めてニューヨークにやってくる。複雑な警察のルールを無視し、リンガーマンを強引に護送しようとするクーガン。しかし空港でリンガーマンの仲間に襲われ、彼に逃げられてしまう。クーガンは保護監察係ジュリーの協力を得て、リンガーマンを追うことに。クリント・イーストウッドとドン・シーゲルが初めて組んだアクション。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEPTUNE’S DAUGHTER", "水着の女王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "水着会社の女社長と水球チームのキャプテンが恋に落ちる。ところが、ひょんなことから騒動が起こり、混乱が始まる……。全編水着姿に徹して見せるウィリアムズの健康肉体美と、華麗な水中バレエ・シーンの数々が見もの。主題歌『ベイビー、外は寒いよ』もヒットした。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ORFEU NEGRO", "黒いオルフェ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ギリシャ神話で名高いオルフェとユーリディスの愛の物語の舞台を、カーニバルで有名なリオ・デ・ジャネイロに移した異色作。カーニバルを明日にひかえた日、田舎からユーリディスが従妹のセラフィーナを訪ねてくる。彼女は謎の男に追われて逃げてきたのだ。そんな彼女が隣家に住む市電の運転手オルフェと知り合う。オルフェにはミラという婚約者がいたが、カーニバルの熱狂と喧騒の中で、いつしか二人は深く愛し合うようになる。だが、謎の男の不吉な影が……。カーニバルの強烈な色彩感覚と世界的に大ヒットした主題歌『オルフェの歌』や『しあわせ』を背景に、古典的な愛の物語をよみがえらせた大作。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Miracle Woman", "奇蹟の処女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "フランク・キャプラ監督による社会風刺ドラマ。牧師の娘フロレンスが父親のために教会で抗議しているのを見たホーンスビー。彼は弁の立つ彼女を利用して宗教団体をでっち上げ、ひと儲けしようと企んだ。計画は順調に進んだが、ある日、フロレンスの前にひとりの青年が現れる。彼はラジオで放送されたフロレンスの説教を聴いて、自殺を思いとどまったというのだ。2人はいつしか恋に落ちるが、ホーンスビーはそれを快く思わず……。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUNAWAY TRAIN", "暴走機関車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "黒澤明によるオリジナル脚本を映画化したアクション大作。アラスカのストーンヘブン刑務所から野獣のような凶悪犯と、15歳の娘をレイプした罪で服役中の若い囚人が脱獄、吹雪の中の原野を４連の機関車に乗って逃亡した。しかし機関手が死亡、非常停止装置も作動せず、最高時速150キロで巨大な鉄の塊が疾駆する。刑務所長はこの脱獄囚を執拗に追うが……。暴走する機関車と自然と人間の三者が織り成すサスペンスをリアルにダイナミックに描く。大胆なメイクのヴォイトとエキセントリックなロバーツの演技は、アカデミー賞にノミネートされた。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBOWAR", "サイバーロボ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ゲリラ鎮圧やクーデター制圧の目的で組織された６人からなる特殊部隊。ある日彼らにカンボジアに設置した兵器“オメガ・ワン”を回収せよ、との命令が下る。戦争プロの彼らにとって何でもない仕事のように思えたが、“オメガ・ワン”とは軍が極秘開発していた軍事ロボットだった……。「ロボコップ」と「プレデター」をミックスしたSFアクション。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ねらわれた学園", "ねらわれた学園", "", "", "http://ecx.images-amazon.com/images/I/51D1LNQ-kaL._SL210_.jpg", 90, "世界を思うままにしようと“栄光塾”なる組織が学園に送り込んだ、超能力を使う謎の美少女と、自分にも超能力があることを知ったクラス一番の秀才少女の対決を描いた青春学園サスペンス。ラストの対決シーンではアニメーションやオプティカル合成を使用した。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PHANTASM II", "ファンタズム II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ふだんは葬儀屋、裏をかえせば残忍な殺人鬼トールマンが、10年の歳月を経てよみがえった。かつての惨劇のショックがいまだ完治しないマイクは、ある夜突然、トールマンの気配を察知。惨劇の現場に行くと、埋葬されていたはずの友人の死体が消えていた。夢と現実が交錯し、トールマンの恐怖がジワリと迫るファンタジー・ホラー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("民警故事／ON　THE　BEAT", "スケッチ・オブ・Peking", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「北京好日」で脚光を浴びた中国の女流監督ニン・インの第３作。北京で働く警官の日常を通して、変化の波に揺れ動く“今の北京”の表情を描き出す。クオは北京市警察徳勝門分署に７年勤務するベテラン警官。ある日、クオの管轄で狂犬病の野良犬が発見され、署では狂犬撲滅のため“飼い犬禁止令”を出し、責任者にクオは選ばれる。そんな彼の元には、街頭賭博、痴漢など様々な事件が持ち込まれてくる。出演者は本物の警官をはじめ全員素人を起用、ドキュメンタリー・タッチの演出でドラマを綴る。", "1995-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゼロの焦点", "ゼロの焦点", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "松本清張の同名ベストセラーを、のちに名作「砂の器」を生むコンビ、野村芳太郎と橋本忍が映画化。金沢で広告会社に勤める男が死体で発見された。彼の死に疑問を感じた妻は、単身調査に乗り出す。時制が過去・現在と交錯するなかに、人間の性が浮かび上がる。能登半島でのクライマックスが印象に残る。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VARSITY　BLUES", "バーシティ・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "MTVが製作したロック満載の青春映画。スポーツ至上主義に逆らう若者たちの心情を捉え、汗臭さを感じさせない作りに仕上げている。高校のアメフト・チーム、コヨーテズは22回の地区優勝歴を誇る強豪だが、コンディションを無視して勝利を優先する鬼コーチのやり方に、選手たちは反発心を抱いていた。そんな気持ちを代弁したのが補欠選手のモックス。主力選手の故障で出番がまわってきた彼はコーチに逆らい、自分のやり方で勝利を目指す。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マノン", "マノン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "アベ・プレボーの有名小説『マノン・レスコー』を原案として東陽一が演出。劇団の研究生みつこの男性遍歴と、彼女にほんろうされる男たちの物語。デビュー間もない烏丸せつこ、歌手の荒木一郎、「戦場のメリークリスマス」以前のビートたけしといった当時としては異色の配役が面白い。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Charlie Bartlett", "チャーリー・バートレットの男子トイレ相談室", "", "", "http://image.eiga.k-img.com/images/movie/54437/315x210.jpg?3822", 97, "有名私立高校に通う17歳のセレブ高校生チャーリーは、その明晰な頭脳を活かし、学校で偽造運転免許証などを売りさばくなどして退学と転校を繰り返していた。遂に不良が集まる高校に転校させられたチャーリーは、そこでも不良学生のカウンセリングと称した商売を始めるのだが……。「アイアンマン」のロバート・ダウニー・Jr.が、問題多き生徒を抱える高校のアル中校長を好演。", "2009-04-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戀戀風塵／DUST IN THE WIND", "恋恋風塵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "オランダのロッテルダム映画祭が1988年に行ったアンケート“明日を担う世界の映画作家”で第３位に挙げられた、H・シャオシエン監督の長編第７作。鉱山の村で幼いころから兄妹のように育った少年ワンと少女ホン。台北に出た二人は、夏の里帰りを楽しみにしながら、愛すると感じるよりも深く心で結ばれている。やがてワンのところに兵役の知らせが届く。ホンはワンに毎日手紙を書き続けることを約束し、ワンにも自分の宛名を書いた1000通の封筒を託す……。「風櫃〈フンクイ〉の少年」「冬冬の夏休み」「童年往事・時の流れ」に続いて、兵役と青春の終章を描いて４部作にピリオドを打った作品。", "1987-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("連続殺人鬼 冷血　～犯罪史上空前の　 連続23人強姦殺人事件", "連続殺人鬼 冷血　～犯罪史上空前の　 連続23人強姦殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "大下英治のノンフィクション小説を映画化。婦女暴行、強盗殺人、派出所襲撃、銀行強盗というすさまじい犯罪歴で話題となった勝田清孝を、作家刺傷事件、自動車での劇場突入事件など現実にもセンセーションを巻き起こした俳優・中山一也が鮮烈に演じきる。ピンク映画のベテラン、渡辺監督初の一般映画。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Day After Tomorrow", "デイ・アフター・トゥモロー", "", "", "http://image.eiga.k-img.com/images/movie/1047/315x210.jpg?2700", 124, "古代気象学者ジャック・ホールが、自らの研究から地球温暖化による世界規模の大災害を予告。その4カ月後、世界各地で異常気象が発生し、彼の仮説が証明されることに……。「インデペンデンス・デイ」のローランド・エメリッヒ監督によるスペクタクル巨編。ロサンゼルス、ニューヨーク、東京、ロンドンなど、世界の大都市が竜巻や洪水などにより崩壊。その中で生き延びようとする人々の生き様を描く。", "2004-06-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("約三十の嘘", "約三十の嘘", "", "", "http://ecx.images-amazon.com/images/I/51X6V8P4QTL._SL210_.jpg", 100, "豪華寝台特急を舞台に、消えた大金をめぐって詐欺師チーム６人が互いに疑心暗鬼に囚われる。だましのプロたちが繰り広げる裏切りと駆け引き。笑いを交えた犯人探しのスリリングなドラマの中に、それぞれ隠された本音が浮き彫りにされ、最後にはちょっぴり切なく温かい感動が広がる。詐欺師チーム６人が大仕事を成功させる。だが、大金の詰まったスーツケースが紛失。彼らは互いを探り合い、犯人探しを始めるが……。原作者である土田英生が脚本も手がけている。", "2004-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONCE UPON A CRIME", "モンテカルロ殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "モンテカルロの街で、行方不明の愛犬探しに多額の懸賞金を懸けていた富豪のマダムが殺された。容疑者は、彼女の愛犬にかかわっていた５人の男女。彼らには殺意もなければアリバイもない。騒動が大きくなる一方で、事件は混迷を極め……。豪華キャストが顔をそろえたミステリアスなコメディ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRELIGHT", "ファイアーライト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "母親となったS・マルソーの復帰第１作となった愛のドラマ。演出は「永遠の愛に生きて」「ネル」の脚本を手掛けたW・ニコルソンで、これが監督デビュー作となった。19世紀、22歳のエリザベスは父の負債を救うために子供のいないイギリス貴族チャールズのために彼の子供を産む契約をする。二人は３夜をともに過ごし、彼女は子供を身ごもり、出産後生まれた女の子はチャールズのもとに引き取られる。それから７年、エリザベスはチャールズの娘ルイザの家庭教師として雇われる。だが、彼は彼女があの時の女性だとは気付かない。エリザベスは実の娘でもあるルイザに、時には厳しく、時には優しく読み書きや道徳心を教えていくが……。ビデオ、DVDタイトルは「ファイアーライト／情炎の愛」。", "1997-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EKOEKO AZARAK　〈エコエコアザラク〉", "EKOEKO AZARAK　〈エコエコアザラク〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "人気シリーズの第４作。人気のない森の中で凄惨な猟奇殺人事件が起こる。生存者は女子高生の黒井ミサだけだったことから、マスコミでは、意図的な“魔女狩り”が行われる。TVディレクターの前田の、偽りの報道によって魔女扱いされたミサは、次第に追い詰められていく……。吉野公佳、佐伯日菜子に続くニュー・ヒロインには、バラエティー番組などで活躍するアイドル、加藤夏希が抜擢され、新人らしからぬ存在感を見せている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Boys in the Band", "真夜中のパーティー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "　ニューヨーク、マイケルはホモセクシュアルの仲間を集め、ハロルドの誕生パーティを開く。しかしそこにマイケルの友人で、ホモではないアランが飛び入りする。アランはみんなに溶け込もうとするが、パーティは次第に険悪なムードに包まれていく。やがて彼らは愛の告白ゲームを始めるが……。オフ・ブロードウェイのヒット劇を、オリジナルキャストを起用して映画化。差別、孤独、疎外感といった人間の暗部を鋭くえぐり出す異色心理劇。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("永遠の天", "永遠の天", "", "", "http://image.eiga.k-img.com/images/movie/54957/315x210.jpg?4576", 115, "1990年代初頭から2000年代末に至るまでの近代化が押し寄せる中国を舞台に、若者たちの波乱万丈な人生と恋を描いた青春ストーリー。親の愛を知らずに育った若者たちが過酷な境遇に翻弄されながらも懸命に生きようとする姿を、SARS騒動や北京オリンピックといった実際の出来事を絡ませながら描いていく。16歳で作家デビューを果たし、テレビドラマの脚本家としても活躍する期待の若手女流監督リー・ファンファンの長編デビュー作。", "2009-10-19", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Cats of Mirikitani", "ミリキタニの猫", "", "", "http://image.eiga.k-img.com/images/movie/34177/315x210.jpg?1528", 74, "ニューヨークに暮らす80歳の日系人画家ジミー・ミリキタニを追ったドキュメンタリー。カリフォルニアで生まれたミリキタニは、第2次世界大戦中に日系人強制収容所に送られたことから市民権を捨て、それ以来反骨の人生を歩んできた。9.11テロをきっかけに、ニューヨークで路上生活を送りながら絵を描いていた彼と共同生活することになったリンダ・ハッテンドーフ監督が、時代に翻弄されながらも不屈の精神を貫き続けてきた彼の姿を捉える。", "2007-09-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("T.R.Y.〈トライ〉", "T.R.Y.〈トライ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "織田裕二が狡猾な詐欺師を妙演する痛快活劇。20世紀初頭の上海、日本陸軍相手に壮大なペテンを仕掛ける主人公の姿を、緩急に富んだテンポで追いかけていく。ペテンをかけた相手に恨まれ、殺し屋に追われるハメになった伊沢修。革命家グループ、中華黎明会は“命の保証はするから”と彼に協力を要請、日本陸軍から武器を騙し取れ、というのだが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRISON", "プリズン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "1964年、アメリカ西部のクリードモア刑務所で、無実の男が電気イスに送り込まれ処刑された。その後ここは長い間閉鎖されたが、囚人の数が増え始めたため、再び扉が開かれた。だが無実の罪で殺された男の怨霊が、刑務所内に超常現象を巻き起こす。R・ハーリン監督によるオカルト・ホラー。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUFFY", "太陽を盗め", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "太陽の輝く地中海を舞台に、若き日のJ・コバーンが華麗なる現ナマ強奪作戦を繰り広げるアクション映画。100万ポンドの報酬で、イギリス有数の海運業者から、現金を奪うことを引き受けたダフィーだったが、思わぬ罠にはまり大ピンチに……。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Across the Tracks", "傷だらけのランナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ブレイク前のB・ピットが主演した青春映画。友だちのルーと車を乗り回して捕まり、一人感化院に入れらたビリーが、家に帰って来る。母親は温かく迎えてくれるが、兄のジョーは冷たい。陸上部のスターとなったジョーは州のチャンピオンになることを夢見ていたのだ。エネルギーのはけ口を探していたビリーは、ジョーのライバル校のチームに入ることにする。そこへ、ルーが現れるのだった。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎の青春", "男はつらいよ・寅次郎の青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "東京で就職をした泉は、週末になると満男の家に遊びにきて、家族のように過ごしていた。同級生の結婚式に出席すべく、宮崎に出かけた泉は、そこで寅さんと出会った。久々の再会に喜んでいた時、ささいなことから寅さんは足をくじいてしまった。知らせを受けて満男は宮崎に向かうが……。満男の恋物語シリーズ第４作。1993年に他界した笠智衆の“男はつらいよ”シリーズの遺作。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEYOND THE DOOR", "ザ・トレイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ユーゴ大草原を走る機関車に怨霊が乗り移り、その中の人々を次々と惨殺していく恐怖が綴られていく。処女の女子大生が悪魔のいけにえとして追われて……。線路もないのに暴走し続ける機関車のSFXが物足りないのはご愛嬌。", "1989-01-01", "1,34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄拳", "鉄拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "「どついたるねん」で劇場用映画に鮮烈なデビューを飾った、阪本順治監督の第２作。今回もボクサーの再起に懸ける日々を描いている。高知県大正町、ここでボクシングジムのオーナーをしている中本誠次は、少年院上がりの後藤明夫という素材を見つける。明夫は快進撃を続けるが、ある時交通事故で右の拳を潰してしまい……。クライマックスは、まるで香港のカンフー映画のよう。菅原文太が、中本役を楽しんで演じている。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NATIONAL VELVET", "緑園の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "E・B・ノルドの同名小説の最初の映画化。1920年、イギリスの片田舎。馬を愛する少女ベルベットが、馬に詳しい少年の力を借りて、暴れ馬を調教し、障害競馬グランド・ナショナルに出場し、優勝するまでを描く。", "1945-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("烈火青春／NOMADO", "レスリー・チャン／嵐の青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "'03年にこの世を去った香港の映画スター、レスリー・チャン。彼が演技派の評価を獲得するきっかけとなった'82年の主演作。二組のカップルの悲恋を綴ったラブ・ストーリーで、エンド・クレジットではレスリーの美しい歌声を聴くことができる。従姉妹のロマンスを知って、いら立ち気味のルイ。そんなある夜、彼はレストランで恋人と喧嘩別れしてきた女性、トマトと運命的な出会いをする。", "2005-04-02", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("前科・仮釈放", "前科・仮釈放", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "渡哲也主演“前科”もの第１作。主人公が仮釈放でシャバに出てくると、組長は殺され、昔の幹部が組を取りしきっている。兄弟分の情報で、その幹部が敵方の幹部と通じて組長を殺したのを知り、主人公は怒りのドスを抜く。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の顔は履歴書", "男の顔は履歴書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「阿片台地 地獄部隊突撃せよ」「懲役十八年」と続く、監督・加藤泰、主演・安藤昇の、戦中世代３部作の第１作。雨宮病院に交通事故の男が運ばれてきた。男の本名は崔、日本名を柴田という韓国人だった。かつて雨宮は沖縄で柴田と一緒に戦ったことがあった。戦後、復員した雨宮を待っていたのは、雨宮のマーケットを乗っ取ろうと企み、傍若無人に振るまう新興勢力の連中で、その中に柴田もいた。映画は柴田が病院に運びこまれてくる現在、マーケットでの抗争の過去、戦争中の大過去という３つの時間を交錯させて、民族、友情、恋愛、男の意地、誇りを日本の戦後史とダブらせて描いている。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shocker", "ショッカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　殺人犯の姿を予知するジョナサン。犯行が繰り返される中、ついにテレビ修理人のピンカーが逮捕される。しかし電気椅子で処刑されたピンカーの体は電流とともに消滅。なんと彼は電波と化し、あらゆる場所に出現し始める。縦横無尽、神出鬼没の殺人鬼ピンカーを食い止めることはできるのか?　鬼才ウェス・クレイブン監督が「エルム街の悪夢」のフレディに続いて生み出した新・殺人キャラ、ピンカー。その恐怖を描いたホラー。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REPECHAGE", "敗者復活戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「ブラザーフッド」のチャン・ドンゴンと「アウトライブ」のキム・ヒソンが揃って銀幕デビューしたロマンチックなラブ・コメディ。元カレに必死で復讐しようとするヒロインをキム・ヒソンが可愛く演じ、その真っ直ぐさに共感させられる。獣医のミンギュは、見知らぬ美女ウネから自分の恋人がウネの元カレと浮気をしていると知らされる。彼女は一緒に復讐しようと持ちかけるが……。", "2005-06-25", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LOVED ONE", "ラブド・ワン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "イギリスの盗作詩人デニスが、伯父を頼りにハリウッドへやって来た。生活のため動物墓地で働き出した彼の身辺に次々と奇怪なことが起こり始める……。イギリスの俊英リチャードソンがアメリカへ渡って作り上げた上質のブラック・コメディ。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La fleur du mal", "悪の華", "", "", "http://image.eiga.k-img.com/images/movie/56197/315x210.jpg?2012", 104, "2010年に他界した仏ヌーベルバーグの巨匠クロード・シャブロルの日本未公開作品を連続公開する「映画の國名作選II クロード・シャブロル未公開傑作選」にて上映。第2次大戦末期ドイツ占領下時代の悪夢を脈々と受け継いできたブルジョア一家に、ある日、1枚の中傷ビラが投げ込まれる。そのことをきっかけに、一家のナゾめいた血縁関係や退廃的なモラルが徐々に暴かれていく。", "2011-06-04", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彼女と彼女の猫", "彼女と彼女の猫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 5, "", "2001-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("絞死刑", "絞死刑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "1958年に起きた小松川女子高校生殺人事件を題材に、死刑制度存廃問題や在日朝鮮人間題などを追及した異色社会劇。拘置所の片隅の死刑場で、死刑囚・朝鮮人少年Rの絞死刑が執行されるが、Rは死ななかった。ロープにぶら下がったまま、Rは心神喪失状態に陥ってしまう。この状態での刑の再執行は法的に許されないため、Rの記憶を取り戻そうと、死刑執行人たちはRの彼の犯罪や家庭環境を芝居で再現して見せる。執行人たちが四苦八苦する姿をブラック・ユーモア交えて描きつつ、その混沌とする事実と虚構に、事件の真相や日本国家が内包する矛盾、問題点をあらわにしていく。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アルビン号の深海探検3D", "アルビン号の深海探検3D", "", "", "http://image.eiga.k-img.com/images/movie/55027/315x210.jpg?3836", 40, "米マサチューセッツ州にあるウッズホール海洋研究所で運用されている深海探査船アルビン号がとらえた映像を3Dで上映するネイチャー・ドキュメンタリー。最大潜水能力4500メートルを誇るアルビン号が深海へと潜り、神秘的で幻想的な生物や光景を次々と映しだしていく。", "2009-10-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZE´RO DE CONDUITE", "新学期 操行ゼロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "「アタラント号」で知られるJ・ヴィゴの傑作中編。彼自身が８年間過ごした寄宿学校での体験がもとになっているといわれており、寄宿学校と子供たちの想像を絶するような“戦い”が、映画の喜びそのものとなって描き出されている。舞台となっているのは夏休みが終わり、新学期を迎えた寄宿学校。学校へ戻るため、列車に乗った二人の少年がまず映し出される。はしゃぎ回る二人の前では、一人の男が死んだようになっていた。そして学校へ。なんと列車の男は新任のユゲ先生。ところがこの先生、子供たちと一緒になってはしゃいだり、子供たちを連れて町に出ると美人のあとを追っかけて行ったり……。寝室で子供たちが枕に詰められた羽毛を飛び散らせて大騒ぎする有名なシーンなど、子供たちと一緒になって既存の権威に反抗し、楽しんでいるヴィゴが作品越しに垣間見える。フランスでは第二次大戦後の1946年まで、13年間にわたって上映禁止となっていた。", "1933-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALENDER GIRL", "カレンダー・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "青春の思い出にと、高校を卒業したばかりの仲良し３人組がハリウッドに向かって旅に出た。目的は何と、彼らのアイドル、マリリン・モンローとのデート。３人はあの手この手で彼女との接触を試みるが、門前払いとニアミスを繰り返し……。1960年代の青春像を生き生きと描いたノスタルジック・コメディ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("友友友友友", "友友友友友", "", "", "http://image.eiga.k-img.com/images/movie/57957/315x210.jpg?4349", 0, "2012年・第4回沖縄国際映画祭の企画「地域発信型プロジェクト」で製作された作品。台湾を舞台に、日本の有名なフィギュアデザイナーが学生時代を過ごした台湾を訪れたことから巻き起こる騒動を描く。", "2012-01-01", "76,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Diana: Last Days of a Princess", "ダイアナ　プリンセス最期の日々", "", "", "http://image.eiga.k-img.com/images/movie/34247/315x210.jpg?4194", 90, "世界に衝撃を与えたダイアナ元英国皇太子妃の事故死から10年。06年12月にロンドン警視庁がまとめた最終報告書を基に、悲劇が起こるまでの“最期の日々”を再現したドキュメンタリー・ドラマ。目撃者による重要な証言を交えながら、様々な憶測を呼びマスコミを騒がせた事件の真実に迫る。日本では、07年8月に放送されたテレビ特番「プリンセス・ダイアナ　衝撃の事故から10年　愛と葛藤と死の真相」の再現パートに使用され反響を呼んだ。", "2007-10-13", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENTANGLED", "ピアース・ブロスナン ファイナル・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "サスペンス小説家ボアロー＆ナルスジャックの作品を映画化。売れない作家デビッドは、トップモデルのアナベルの行動を絶えず気にしていた。ある時、一枚の写真がデビッドを逆上させる。それは彼女と男友だちが、ある億万長者の古城を背景にして写っているものだ。この時、すでに彼は罠にはめられていたのだった……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレヨンしんちゃん／　伝説を呼ぶ　ブリブリ３分ポッキリ大進撃", "クレヨンしんちゃん／　伝説を呼ぶ　ブリブリ３分ポッキリ大進撃", "", "", "http://ecx.images-amazon.com/images/I/61nrXapNoeL._SL210_.jpg", 88, "もはや説明不要の国民的人気アニメ『クレヨンしんちゃん』の、13作目となる劇場版。今回の舞台は“ミライ”。しかも未来に行って3分で怪獣を倒さないと現実世界が危険にさらされるという怒涛の展開だ。いつものん気なしんちゃんも、今回ばかりは世界の危機を救うために大奮闘。突然現れた“ミライマン”に3分後の未来で怪獣を倒さないと現実に怪獣が現れると言われた野原家は、強制的に3分後の世界へ行くことに……。", "2005-04-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Detachment", "デタッチメント", "", "", "http://image.eiga.k-img.com/images/movie/57357/315x210.jpg?2993", 97, "", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAYERLING", "うたかたの恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "1935年フランスで作られた同名映画(アナトール・リトヴァク監督)の再映画化。19世紀末、ハプスブルグ家のオーストリア皇帝の跡継ぎのルドルフが、不倫の恋人マリーとマイエルリンクで心中するという歴史的事件を素材にしたロマンティックな悲劇。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("塀の中のプレイ・ボール", "塀の中のプレイ・ボール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "安部譲二のベストセラー『塀の中の懲りない面々』の映画化が大ヒットしたのを受けて作られた第２作。前作に続き個性ある囚人たちによって看守たちとの野球試合が画策されるが……。元高校軟式野球で全国大会出場の経験がある草刈正雄が、主人公を五分刈りで熱演。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A NIGHTMARE ON ELM STREET ４：THE DREAM MASTER", "エルム街の悪夢4　　ザ・ドリームマスター 最後の反撃", "", "", "http://ecx.images-amazon.com/images/I/510A7PMQEJL._SL210_.jpg", 93, "長く鋭利な鉄の爪、焼けただれた顔、そしてあの不快な高笑い。「13日の金曜日」のジェイソンと並ぶホラーの人気者となったフレディが、またまた地獄からよみがえり、若者たちを惨殺していく“エルム街の悪夢”シリーズ第４作。今回フレディと対決するのは、超能力を身につけた高校生アリス。フレディは彼女の友人を次々と血祭りにあげていく。ある者はプールと化したベッドで溺死し、ある者はボイラー室で焼き殺される。そしてまたある者は醜いゴキブリへと姿を変えられ殺される。人の夢に棲むフレディから逃れる術はない。アリスは殺されていった友人たちの復讐のため、命を懸けてフレディと対決する。", "1989-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rude Boy", "ルード・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "1977年にアルバム「白い暴動」で衝撃のデビューを果たし、セックス・ピストルズやザ・ダムドらとともにイギリスの初期パンク・ムーブメントを牽引したザ・クラッシュ。その熱狂的なファンでローディとして雇われた青年の目を通して描いたセミ・ドキュメンタリー・フィルム。1978年のライブ映像やスタジオでのリハーサル風景等、本作でしか見ることのできない貴重な映像が満載。ベルリン国際映画祭で審査員特別賞を受賞。", "1980-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOY WHO COULD FLY", "ミリイ 少年は空を飛んだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "13歳のミリイが越してきた家の隣に、エリックという同年の男の子が住んでいた。だが彼は父母を飛行機事故で亡くして以来、一言も言葉を発せなくなっていた。ミリイは彼の心を開こうと必死になる。子供から大人へ成長する少年少女の姿をファンタスティックに描く。SFXをリチャード・エドランドが担当。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("文学賞殺人事件 大いなる助走", "文学賞殺人事件 大いなる助走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "文壇界を完膚なきまでに笑い飛ばした天才・筒井康隆の傑作小説を映画化。ひょんなことから同人誌で小説を書き始めた主人公の青年は、自らの作品を直本賞受賞作にするため、審査員にゴマをすり、金を積み、うしろの童貞を捧げ、恋人を献上する。そして、出た結果は？　小説は、すべての登場人物が狂気をひきずり、その渦へ巻き込んでいくという徹底したものだが、映画では主人公に肩入れし、狂気に巻き込まれてしまった悲劇の人物として描いている。その点が弱点ともいえるが、スタッフたちの優しさともとれる。妹役の松本典子で終わるラストも鈴木監督らしい。筒井氏自身、SF作家役で怪演。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の夢　ある中国残留婦人", "花の夢　ある中国残留婦人", "", "", "http://image.eiga.k-img.com/images/movie/34197/315x210.jpg?1553", 97, "戦前・戦時中に中国に渡り、敗戦による混乱の中で現地に置き去りにされた“中国残留婦人”を巡るドキュメンタリー。東京・江東区のアパートで暮らす81歳の女性・栗原貞子。1944年、当時18歳だった彼女は、希望に胸を膨らませて中国・東北部へと渡った。ところがその直後に日本は敗戦。それから35年もの間、彼女は帰国することができなかった。彼女の証言を中心に、戦争によって運命を狂わされた女性たちの苦難の日々を描き出す。", "2007-09-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WAR WAGON", "戦う幌馬車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "無実の罪で投獄されていたトウが、仮出獄を許され、復讐のために帰ってきた。投獄の間に彼の所有する土地と金鉱を奪ったピアースは復讐をおそれ、流れ者のガンマンであるローマックスにトウを殺すように頼む。しかし、トウとローマックスの間には、ピアースが砂金を運ぶ装甲馬車を襲撃して50万ドルの砂金を奪う計画があったのだった。監督は、職人肌のB・ケネディ。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベトナムのダーちゃん", "ベトナムのダーちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "早乙女勝元の原作をもとに、戦争について問いを投げかけるヒューマン・ドラマ。17年前に書いた本のモデル、ダーちゃんに会うため、作家の早瀬はベトナムを訪れる。彼の心にダーちゃんと語った日々や、目の前で母を殺され、さすらいの旅を続けた彼女の悪夢のような戦争体験が生々しくみがえる……。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAREM HOLIDAY", "ハレム万才", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "主演映画のプレミアショーで中近東を訪れたプレスリー扮するハリウッド・スター。そこで彼は王位はく奪の陰謀に巻き込まれてしまう……。アラビアン・ナイトそこのけの大活躍とロマンが繰り広げられる作品で、主題歌をはじめ、プレスリーのナンバー９曲を収録。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE JAZZ SINGER", "ジャズ・シンガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "トーキー映画第１作として名高いアル・ジョルソン主演の1927年作品のリメイク。ユダヤ教の家庭に生まれた青年がショービジネス界に入り、持ち前の情熱で成功を収める。父親や妻子に向けて熱唱する主人公の姿が、ダイヤモンドのさわやかな好演で伝わる佳編。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Firm", "ザ・ファーム／法律事務所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 155, "ハーバード大学を優秀な成績で卒業したミッチはある法律事務所から内定を受ける。最高の労働条件を提示され、大喜びで就職するミッチ。だが、この事務所には謎の死を遂げた4人の弁護士がいたことが判明。やがてミッチは事務所とシカゴ・マフィアの繋がりを知り……。マフィアのボスを相手に、青年の必死の攻勢を描くスリル満点のサスペンス。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌10", "釣りバカ日誌10", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "通算第11作となった平成の喜劇、“釣りバカ”シリーズの10周年記念作。ある日の会議で社長を辞めると宣言してしまった一之助ことスーさん。慌てふためく周囲をよそに、そのまま運転手の前原と釣りに直行。西伊豆・駿河湾で釣り三昧となるはずだったが、何だか気分が晴れないスーさん。がっくりしたまま万年“ヒラリーマン”のハマちゃん宅を訪れると……。“第２の人生”をスタートしたスーさんを軸に展開する爆笑編。笹野高史扮する運転手・前原が釣りにどっぷりハマるなど、人気のレギュラー陣が大活躍。同時に若いカップルの恋の行方もリリカルに演き出される。そしてクライマックスにはハマちゃんが何と、“高木ブーとニューハロナ”を従え驚愕の一大セッションを。ちなみにラストでハマちゃんが釣り上げる大きな獲物は、スズキ！", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hoodwinked", "リトル・レッド　レシピ泥棒は誰だ!?", "", "", "http://image.eiga.k-img.com/images/movie/34187/315x210.jpg?4070", 81, "ワインスタイン・カンパニーが初めて手掛けた長編アニメーション映画。童話「赤ずきん」の世界をモチーフに、平和な森で起きた事件を複数の容疑者の証言から解き明かしていく様子を、笑いを交えて描く。オリジナルの声の出演はアン・ハサウェイ、グレン・クローズら。日本語吹替え版は上野樹里、加藤浩次らが務める。赤ずきんの少女レッドは、森中のお菓子のレシピを盗んでいる“レシピ泥棒”の仕業とみられる事件に巻き込まれてしまい……。", "2007-10-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO GIRLS AND SAILOR", "姉妹と水兵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "1940年代のMGMのミュージカルの醍醐味が堪能できる一編。ニューヨークのナイトクラブの歌手姉妹が、海軍の慰問ショーに出演、そこで二人は一人の水兵に同時に恋をしてしまう……。アリスンの魅力と、数々のヒットナンバーが楽しめる。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本一の色男", "日本一の色男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "当時人気絶頂のコミック・バンド、クレージー・キャッツの映画シリーズの中で、植木等を主役にした“日本一の～男”シリーズの第１作。女子校の卒業式でツイストを踊り出し、クビになった光等は、化粧品会社のセールスマンとなり、メチャクチャなセールスを展開する。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2 DAYS IN THE VALLEY", "トゥー・デイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ロサンゼルスを舞台に、男女10人の人生が交錯する二日間を描いたユニークな群像劇。冷酷な殺し屋と、彼に裏切られた相棒、前夫を殺害された女子スキー選手。そして、その事件を追う刑事たち、腎臓結石に苦しむ若きアート・ディーラー、その秘書……。彼らが数奇な運命の糸に導かれて出会い、それぞれに人生の転機を迎えていく。演技派俳優たちのにぎやかな共演はもちろん、時にユーモラス、時にスリリングな味わいを与える緩急自在の語り口が魅力。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIDAY THE 13TH PART6: JASON LIVES", "13日の金曜日PART6 ジェイソンは生きていた！", "", "", "http://ecx.images-amazon.com/images/I/51YD4SJVHZL._SL210_.jpg", 87, "トミーは殺人鬼ジェイソンの悪夢から逃れようと、ジェイソンの墓を掘りおこす。ミイラ化した死体が現れた瞬間、目もくらむような雷が死体を直撃。ジェイソンがよみがえり、再びフォレスト・グリーンと名を変えたクリスタル・レイクが殺りくの舞台に。", "1986-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Young Guns II", "ヤングガン2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1988年に作られた「ヤングガン」の続編。1878年、ニューメキシコ南部。ビリー・ザ・キッドは伝説のアウトローになっていて、その首には賞金が懸けられていた。かつての仲間とともに行動する彼を、牧場主チザムとウォーレス知事から保安官に任命されたパット・ギャレットが執拗に追いかけて……。西部時代の終焉とビリーの真実をダブらせ、若い命を散らしたガンマンたちの挽歌をうたい上げる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEON BIBLE", "ネオン・バイブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "夭折のピュリッツァー賞作家J・K・トゥールが16歳の時に書いた小説を、「遠い声、静かな暮し」の映像詩人T・デイヴィスが映画化。1930年代、ジョージア州の少年デビッドの家に、ドサ回りの歌手であるメイ叔母さんが住み込み始める。派手な服を着て奔放に遊び回るメイは、保守的で信仰厚い両親にとっては気が気でない存在だったが、デビッドにとっては良き理解者だった。そんな折、第二次大戦が起こり、デビッドの父親フランクが戦死。母親セイラまで病に倒れてしまい、悲痛な事件がデビッドに降りかかる……。少年とその家族の歩みを、人種差別などの社会的背景を絡めて描く叙情的な人間ドラマ。アメリカ南部の物語らしく「風と共に去りぬ」のテーマやトラディショナル・ソングなどの音楽が全編に散りばめられ、夢幻的なムードの映像世界を彩っている。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A PASSAGE TO INDIA", "インドへの道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 158, "イギリスの巨匠D・リーンによる77歳の遺作。1920年代、英国植民地インドに婚約者を訪ねる若い娘と同行の老婦人。マラバー洞窟での神秘的な体験が思わぬ事件を引き起こし、反英運動が燃え上がる。堂々たるスケール感、異郷での心理、女性のきめ細かい性格描写が見事だ。", "1984-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ils", "THEM ゼム", "", "", "http://image.eiga.k-img.com/images/movie/53267/315x210.jpg?4264", 77, "ハリウッドリメイク版「the EYE【アイ】」の監督に抜擢されたフランスの新鋭ダビッド・モロー＆グザビエ・パリュの長編デビュー作。ルーマニアのフランス人学校で教師として働くクレモンティーヌと夫のリュカは、人里離れた林に佇む屋敷で穏やかな生活を送っていた。ある晩、クレモンティーヌが電話に出ると、受話器の向こうから不気味な金属音が聞こえてくる。やがて、その音と共に現われた複数の何者かが屋敷に侵入し……。", "2008-01-26", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOM THUMB", "親指トム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "身長10cmほどしかないトムが、友だちといっしょに悪人をこらしめるという有名なグリム童話からヒントを得て、パルが製作・監督したファンタジー映画。見事な人形アニメーションはアカデミー賞に輝いた。悪漢役にデビュー当時のセラーズが出演している。", "1958-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Emmanuel's Gift", "エマニュエルの贈りもの", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "", "2007-06-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴走の季節", "暴走の季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "岩城滉一主演の“暴走族”もの第３作。下町の自動車修理工場に働く暴走族が、ふとしたことからブルジョワ娘の、ひと夏のお相手に選ばれる。娘のまわりにたむろするブルジョワ不良グループと、主人公の青年はやがて対立し、決闘するが……。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OTHER", "悪を呼ぶ少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "コネチカット州の田舎に住む双子の兄弟はある日、祖母から一種のテレパシー風の遊びを教えられる。ところが、その頃から街では謎の事故が続出し、次々に人が死んでいく。その背後には双子の一族の恐るべき事実が隠されていた……。静かな語り口の中にじわじわと恐怖がにじみ出てくるスリラー映画の佳作。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK　CHRISTMAS", "暗闇にベルが鳴る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "女子寮の学生を狙う殺人鬼が登場する、ホラー・モードのミステリー・サスペンス。後に「ハロウィン」などのホラー映画に多大な影響を与えた作品。当時の人気女優O・ハッセーの清楚な演技にも注目。女子寮のクリスマス・イブのパーティーの最中にかかってきた一本の怪電話から、血も凍る連続殺人事件が発生し、次々に女学生が姿を消していく。果たして犯人の正体は……？", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPELLBINDER", "デビルズ・バインダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "若き弁護士ジェフが愛した女性は、呪われた運命に囚われていた。二人は悪魔崇拝者たちに追われることに……。男女の逃避行を幻想的な映像で描いたオカルト・サスペンス。ジョン・トラヴォルタの妻、K・プレストンがヒロインを熱演する。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABY ： SECRET OF THE LEGEND", "恐竜伝説ベイビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "有史以前に生存した“動物”たちの生き残り組に遭遇してしまった夫婦の物語。広大なアフリカのジャングル。探検隊チームの一員であるジョージとスーザンは母親を見失った恐竜の赤ちゃんを発見、さっそく親捜しを始めるが、恐竜を生け捕り、全世界に発表しようとする博士と対決するはめに陥る。全編アフリカ・オールロケから得た映像は印象的。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("性夢驚魂／SCARED STIFF", "サイキック・アクション 復讐は夢からはじまる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "害虫駆除会社で働くアランとハーレイは、仕事を離れても親友同士。ある日、アランが事故に遭い、他人の夢の中に入り込むという能力を持ってしまった。ハーレイが殺人事件を目撃したことから、犯人に重傷を負わされた時、アランは彼の夢の中に入り、殺人犯の正体を知る……。監督のリュウ・チャーインは、「少林寺三十六房」に出演した俳優として、またカンフー映画の武術指導者として有名。ハーレイ役のE・ツァンは、「悪漢探偵」などの監督としても知られ、アニタ・ムイは香港の人気美人歌手。だがここでの目玉は、何といっても悪徳刑事に扮しているチョウ・ユンファ。コメディからアクションまで、自由自在の大熱演だ。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆裂都市 Burst City", "爆裂都市 Burst City", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "カスタム・カーによるデス・レースが血をざわめかせる近未来のある町を舞台に、原発建設の強制労働を強いられた町の下層民たちが結託、やがて暴動へと発展していく様を描いたSFバイオレンス。泉谷しげるら個性派俳優陣がエネルギッシュな演技を見せ、カルト的な人気を博した。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAGIC", "マジック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "しがない手品師から全米きっての腹話術のスターになった男が、今は人妻となった初恋の女性と再会。二人の間に愛の炎が燃えあがるが、彼女の夫は嫉妬深くこれを黙認しない……。演技派ホプキンスの怪演が光るサイコ・スリラー。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("佐賀のがばいばあちゃん", "佐賀のがばいばあちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1980年代に一世を風びした漫才コンビ「B＆B」の島田洋七が自身の少年時代をつづった同名ベストセラーを映画化。昭和30年代の広島。父親を亡くし、母親に女手ひとつで育てられていた明広は、佐賀で暮らす祖母のもとに預けられることに。極貧の厳しい状況にあっても、独特の人生哲学で気丈に切りぬけていくがばいばあちゃんとの暮らしの中で、明広はさまざまなことを学んでいく。主演はベテラン女優の吉行和子。", "2006-06-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Geliebte Clara", "クララ・シューマン　愛の協奏曲", "", "", "http://image.eiga.k-img.com/images/movie/54347/315x210.jpg?3999", 109, "これまでキャサリン・ヘプバーンやナスターシャ・キンスキーが演じ、幾度も映画化されてきた才色兼備の音楽家クララ・シューマンの生涯を描いた伝記映画。本作でクララを演じるのは「善き人のためのソナタ」で一躍脚光を浴びたマルティナ・ゲデック。天才作曲家ロベルト・シューマンの妻クララは、ピアニストとしてヨーロッパ各地を周っていたある日、新進作曲家ヨハネス・ブラームスに出会う。若く情熱的なブラームスに心揺さぶられるクララだったが、体調不良に悩むロベルトは自身の後継者としてブラームスを信頼していた。", "2009-07-25", "7,2,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DERNIER MILLIARDAIRE", "最後の億萬長者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "カジノに落ちる金で経済をまかなう小国カジナリオの台所は火の車。そこで女王は一計を案じ、王女と大金持ちのバンコを婚約させる。だが、バンコは暴君となり、ついに反乱が起こり……。当時の世界情勢を背景に巨匠クレールが描く風刺コメディ。", "1934-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Goodbye Again", "さよならをもう一度", "", "", "http://ecx.images-amazon.com/images/I/51Gxxi10tCL._SL210_.jpg", 121, "トラック販売会社の重役ロジェとインテリア・デザイナーのポーラは、ともに自立した大人の恋を楽しんでいた。５年間いい関係を築き、もちろん愛し合ってもいる。ところが、なぜか結婚には踏み切れない。そんな二人の前に現れた青年フィリップが、ポーラに恋をしてしまい……。フランソワーズ・サガンの小説『ブラームスはお好き』をもとに、中年カップルの恋の行方をシャレたタッチで描く大人のラブ・ロマンス。クリスチャン・ディオールを身にまとったバーグマンの美しさが光る。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋に至る病", "恋に至る病", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Obratnyy otschet", "ミッション・イン・モスクワ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "爆弾テロに立ちむかうテロ対策チームの闘いを描いたロシア製アクション。テロ組織を追っていた捜査官が「48時間後にモスクワで爆発が起こる」という言葉を遺して殺害され、最近結成されたばかりのテロ対策スペシャルチームが捜査に当たることに。スピード狂の元特殊作戦隊員や美貌の心理学者、盲目の爆弾処理員ら、個性派ぞろいの5人のメンバーが、48時間というタイムリミットの中で事件の解決に挑む。", "2008-02-02", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WINDOWS", "エミリーの窓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ブリックリンの闇に潜む暴漢がエミリーの部屋にしのび寄る。だが、エミリーの悲鳴は、無数の窓からこぼれる一つの音にすぎなくて……。ニューヨークの撮影にかけては、第一人者といわれるウィリスが自ら監督。絶妙のカメラワークをみせるサスペンス・ドラマ。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍的心／HEART OF DRAGON", "ファースト・ミッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "チェン＆キンポー・コンビの痛快アクション。両親を亡くし、二人きりで育ったキンポーとチェンの兄弟。チョッピリおつむの足りない兄貴は、刑事である弟の足を引っぱることもしばしば。そんなある日、密輸組織が兄貴を誘拐して……。お得意のカンフーに、兄弟愛を加えた痛快作。", "1985-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トワノクオン　第六章　永久の久遠", "トワノクオン　第六章　永久の久遠", "", "", "http://image.eiga.k-img.com/images/movie/57117/315x210.jpg?3759", 45, "「機動戦士ガンダム　第08MS小隊」などで知られ、2010年に他界した飯田馬之介監督が生前に制作を進めていた全6話のオリジナルアニメーションの最終話。飯田監督と旧知の仲だったもりたけし（「ストラトス・フォー」「ヴァンドレッド」）が協力監督として引き継ぎ、「第08MS小隊」でも飯田監督とタッグを組んだアニメーションディレクターの川元利浩らとともに作品を完成させた。弟のトワやテイを取り戻すため、クオンは瞬とともにクーストースの本部に向かう。そんなクオンと瞬の前に、ベスティア（能力者）を追い続けていたサイボーグ部隊「WTOC」が立ちはだかり、能力者とサイボーグたちの最後の戦いが始まる。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒートアイランド", "ヒートアイランド", "", "", "http://image.eiga.k-img.com/images/movie/34257/315x210.jpg?1624", 106, "人気ミステリー作家・垣根涼介の同名小説を、大ヒットテレビドラマ「花より男子」の演出家・片山修＆脚本家・サタケミキオのコンビで映画化。毎週渋谷でファイトパーティーを開催しているストリートギャング“ギルティ”のメンバーの元に、ひょんなことから3000万円もの大金が転がり込む。それは関西ヤクザが経営するカジノからプロ強盗団が奪った金だった。彼らは敵対ヤクザや南米マフィアまでもが入り乱れた現金争奪戦に巻き込まれてしまい……。", "2007-10-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLEEP STALKER", "スリープストーカー　砂男の恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "フリーライターのグリフィンは少年時代に殺人鬼に両親を惨殺されていた。殺人鬼は駆けつけた警察官に逮捕され、すんでのところで助かったグリフィン。ところが長年の刑務所生活を経てガス室送りになったはずの殺人鬼が、砂でできた身体と、想像を絶する力を持ったスリープストーカーとしてよみがえり、再度グリフィンを殺害しようと執拗に追い回し始めた。そしてその奥には、深い理由が隠されていたのだ……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・童貞物語　ホンコンバージンボーイ", "新・童貞物語　ホンコンバージンボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "“童貞物語”シリーズ第６作。今回はオール香港ロケの異色編で、恋あり、冒険あり、アクションありの娯楽作に仕上がっている。カンフー・スターを夢見る健治は、知り合いの修を頼って香港までやって来た。そこで出会った少女シュウェンに一目惚れしたり、カンフーの達人である彼女の祖父に弟子入りし修行に励むが、そこで思わぬ事件に巻き込まれ……。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG BLUE", "摩天楼〈ニューヨーク〉に抱かれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「ドゥームド・ラブ 宿命之恋〈さだめのこい〉」が、日本でもカルト的人気を得た、A・ホーンの長編第２作。独特の映像感覚と反復する台詞まわしで綴った、新感覚のハードボイルド映画。毎日毎日、浮気調査の依頼ばかりで、少々ウンザリ気味のしがない私立探偵。ところがある日、麻薬事件に巻き込まれ、あげくに恋も絡んで、とんでもない事態に……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUEHE HECHO YO PARA MERECER ESTO!!", "グロリアの憂欝　セックスとドラッグと殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "スペインの鬼才P・アルモドヴァル監督の1984年作品。家事、子育てに追われる平凡な主婦グロリアは、夫との冷めきった関係、非行に走る息子たち、お金に振り回される毎日に限界を感じていた。彼女が、最後に選んだものは？", "1984-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シティハンター　百万ドルの陰謀", "シティハンター　百万ドルの陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "世界に誇るジャパニメーションの老舗サンライズが、アニメ製作を担当。好評を得たTVシリーズの映画版第3弾となる都会派アクション・アニメ。都会にうごめく悪と日夜、対決する主人公・冴羽リョウ。女好きだが憎めない彼を戒めつつも、真剣に依頼案件に取り組む槙村香。そんな彼らがコンビを組んで、悪を撃ち抜く。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("腐女子彼女。", "腐女子彼女。", "", "", "http://image.eiga.k-img.com/images/movie/54377/315x210.jpg?1545", 97, "累計1100万アクセスを突破し、書籍化も話題を呼んだ人気ブログを映画化したラブコメディ。平凡な大学生が、アニメやボーイズラブの世界をこよなく愛する“腐女子”の彼女に振り回されながらも愛を育んでいく姿をコミカルに描く。主人公カップルを演じるのは「クローズZERO」シリーズの大東俊介と、「仮面ライダー電王」シリーズの松本若菜。「ちーちゃんは悠久の向こう」の兼重淳監督がメガホンを取る。", "2009-05-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("下郎の首", "下郎の首", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "父の仇を捜す気弱な息子、新太郎とお供の下郎、納平。ふとしたことから納平は敵の侍、須藤と出会い仇討ちを果たす。主従は逆に須藤の息子から敵と狙われるハメになり……。下郎であるために人間らしく扱われない納平の悲哀を、田崎潤が見事に演じきる。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃える男 長島茂雄　栄光の背番号３", "燃える男 長島茂雄　栄光の背番号３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 44, "1974年10月14日、後楽園球場。日本中のすべての人々がそこに注目していた。秋の陽がグラウンドに夕暮れを告げたその時、一人の男がスポットを浴びて立ちつくしている。男の名は長島茂雄。老若男女を問わず日本中のすべての人を魅了したプロ野球・読売巨人軍の３塁手である。その17年間にった選手生活の名場面、さらには知られざる私生活のフィルムを集めたドキュメンタリー作品。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE IS A MANY SPLENDORED THING", "慕情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "イギリス人と中国人の混血スーイン女史の自伝小説の映画化。夫が戦死し、香港の病院で働く中国系の女医が、アメリカの従軍記者と偶然知り合い恋に落ちる。間もなく朝鮮戦争勃発、男は戦地へ飛ぶが……。甘美なラブ・シーンのバックに流れるテーマ曲は出色の出来。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Taxi", "TAXI NY", "", "", "http://image.eiga.k-img.com/images/movie/1687/315x210.jpg?1750", 97, "リュック・ベッソン製作・原案のフランス製コメディ・アクション「TAXi」をハリウッドでリメイク。「シカゴ」の女性看守役でアカデミー賞にノミネートされたクイーン・ラティファが凄腕女性タクシー・ドライバーに扮し、「サタデーナイト・ライブ」出身の若手コメディアン、ジミー・ファロン演じる若手刑事と組んで、色っぽい美女銀行強盗4人組を追う。強盗の首領役でスーパーモデルのジゼル・ブンチェンが共演。", "2005-01-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Joe Kid on a Stingray", "ジョー・キッド・オン・ア・スティングレイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アメリカ発のストリート・カルチャーとして世界中で人気を集め、2008年の北京オリンピックで正式種目となったBMXの歴史をたどったドキュメンタリー。1963年にシュウイン社が発表した自転車「STING-RAY」の誕生から始まったBMXの30年以上にわたる変遷を、歴史を切りひらいたパイオニアたちや伝説のライダーほかBMXの発展に貢献した人々の証言を交えつつ、貴重なアーカイブ写真や映像とともに振りかえる。", "2007-02-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("炎神戦隊ゴーオンジャー BUNBUN！BANBAN！劇場BANG!!", "炎神戦隊ゴーオンジャー BUNBUN！BANBAN！劇場BANG!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "意思を持つ乗り物・炎神とタッグを組んで蛮機族ガイアークに立ちむかう若者たちの戦いを描いたスーパー戦隊シリーズ「炎神戦隊ゴーオンジャー」の劇場版。ガイアーク三大臣によって次元に裂け目ができ、ヒューマンワールドが別の次元とつながってしまった。そこから現われた3人の戦士・炎衆に炎神キャストを奪われたゴーオンジャーたちは、次元の裂け目へと飛びこみ、ナゾの女・魔姫が支配するサムライワールドにたどり着く。", "2008-08-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泣きぼくろ", "泣きぼくろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "安部譲二の同名短編小説を工藤栄一が忠実に映画化。“泣きぼくろのある女”を追って東京から名古屋まで旅をする３人の男たち。どこか世間からはみ出したような彼らの道中を、人間味あふれるタッチで描く。劇中に使われる松田優作の歌が効果的だ。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Reaping", "リーピング", "", "", "http://image.eiga.k-img.com/images/movie/33977/315x210.jpg?1325", 100, "「ミリオンダラー・ベイビー」のヒラリー・スワンクと、大ヒットドラマ「24」ファースト・シーズンを手掛けたスティーブン・ホプキンス監督がタッグを組んだスーパーナチュラル・スリラー。家族の悲劇的な死をきっかけに信仰を捨てた元キリスト教宣教師キャサリン。科学を駆使して不可思議な現象を解明する専門家になった彼女は、旧約聖書の“十の災い”に似た現象が次々と起こっているルイジアナ州の小さな町ヘイブンへと向かうが……。", "2007-05-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハードラックヒーロー", "ハードラックヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "人気アイドルグループ、V６が全員で主演するアクション・ムービー。V６のCDリリースと連動し、一部ずつ露出されるストーリーや、全国一万人限定で一度のみ行われたプレミアム上映も話題になった。八百長の仕組まれたムエタイの試合中、会場に銃声が響き、刑事たちが突入してきた。大混乱の中、ひょんなことから追われる身となった６人の青年たちは様々なアクシデントに見舞われることになる。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRULY，MADLY，DEEPLY", "愛しい人が眠るまで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "亡き恋人ジェイミーの面影を追い続けてばかりいるニーナ。そんな彼女のもとにある夜、死んだはずの彼が幽霊になって現れた。驚きもつかの間、幸せにひたるニーナ。だがジェイミーの思惑は別なところにあった……。ハリウッドでは仇役で鳴らすA・リックマンが、演技派の本領を発揮した地元イギリスでの恋愛ドラマ。自分を忘れさせるために恋人のもとにやって来た幽霊役を切なく演じている。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOWARD THE DUCK", "ハワード・ザ・ダック　暗黒魔王の陰謀", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "宇宙の彼方アヒル惑星からやって来たハワードは、言葉も話せば知性も抜群のアヒル。その彼が地球侵略をたくらむ暗黒魔王を相手に大活躍を演じる。映画のパロディーやギャグも満載。コミックから生まれた超人気キャラクター、ハワードの愛くるしい表情が大ウケのSF冒険コメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIE HARD WITH A VENGEANCE", "ダイ・ハード3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "B・ウィリス扮するマックレーン刑事がテロ事件に巻き込まれる大ヒット・シリーズ第３作。今回の舞台はニューヨーク。デパートを爆破した冷徹なテロリスト、サイモンは、マックレーンに対して黒人街ハーレムで“俺は黒人が大嫌い”という看板を掲げろと命令してくる。命令に背けば、さらなる爆弾テロが行われるのだ。黒人に囲まれながらも何とか危機を逃れたマックレーンだが、やっとの思いで警察署に戻ったところへ次の指令が入る……。第１作のJ・マクティアナンが再びメガホンを執り、ハードなアクションにコミカルな要素も加え、前２作に縛られない作品に仕上げている。J・アイアンズ、S・L・ジャクソンら実力派も期待通り。また、ベテランぞろいのスタッフの中にあって、新鋭J・ヘンスレーの脚本も光る。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIBERTY STANDS STILL", "スナイパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "学校内の発砲事件で娘を失った元CIA局員ジョーの怒りは犯人だけでなく、銃の製造元マクロード社にも向けられた。ジョーは社長夫人リバティを高性能ライフルで狙い、爆弾を仕掛けたホットドッグスタンドに拘束。そして銃問題に関する公開討論を求め、彼女には不法な武器取引に関与する政府高官の名前を暴露するように迫るつもりだった。しかし、リバティの夫ビクターは不正取引の秘密を知る彼女を抹殺するため暗殺部隊を差し向ける……。自らの目的を達成するためリバティの命を守ることになるジョーの戦いを、銃規制問題を盛り込んで描くサスペンス。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED SURF", "TRUE　BLUE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ウエスト・コーストのマリブビーチ。プロのサーファーとして鳴らす一方で、やくざな生活を続ける青年と、その恋人の切ない愛と夢を中心に、男たちの友情、野望が描かれる。ヒロインのD・ファイファーはミシェル・ファイファーの妹。ジェットスキーによるアクションも楽しめる。ナイスガイ、G・クルーニーが主演を務めたファン必見の一作。以前「レッド・サーフ」のタイトルでのビデオ販売有り。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Two-Lane Blacktop", "断絶", "", "", "http://image.eiga.k-img.com/images/movie/46647/315x210.jpg?3818", 102, "シンガーソングライターのジェームズ・テイラーと「ビーチ・ボーイズ」のデニス・ウィルソンの主演で描かれたロードムービー。賭けレースの相手を求めてロサンゼルスを飛び出したザ・ドライバーとザ・メカニックは、車に転がり込んできたザ・ガールを加えた3人で旅を続け、途中のガソリンスタンドで出会った中年男と互いの車を賭けた大陸横断のレースをすることになる。ユニバーサル映画がアメリカン・ニューシネマの野心作として製作するも興行的に惨敗し、メガホンをとったモンテ・ヘルマンが映画製作から遠ざかる結果を残してしまった伝説的な一作。2012年、ヘルマン監督の21年ぶり新作「果てなき路」（11）の公開にあわせニュープリントでリバイバル。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("696 TRAVELING HIGH", "696 TRAVELING HIGH", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "人気ミュージシャン、川村カオリらが主宰するクラブ・イベント“696”の全国ツアーを追ったドキュメンタリー。川村自身がカメラを回し、バックステージで繰り広げられるロックンローラーたちの素顔を映し出す。THEE MICHELL GUN ELEPHANTら仲間のミュージシャンのステージへの乱入、観客の熱狂する姿など、ロックに魅了された人々の熱い思いがダイレクトに伝わってくる。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RAGE OF PARIS", "巴里の評判娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "フランスが誇る「うたかたの戀」の美女D・ダリューが、H・コスター監督と組んだハリウッド映画デビュー作。ニューヨークに住むフランス娘はお金のために金持ちの息子に取り入るが、別の青年実業家に惚れられてしまう。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Evan Almighty", "エバン・オールマイティ", "", "", "http://ecx.images-amazon.com/images/I/510BDwWWH7L._SL210_.jpg", 0, "ジム・キャリー主演の大ヒットコメディ「ブルース・オールマイティ」でスティーブ・カレルが演じたキャラクター、エバンを主人公にしたスピンオフ作品。「世界を変えよう」を公約に掲げて下院議員選挙に出馬し、見事当選を果たしたテレビキャスターのエバン。ところが公約の実現を神に祈った翌日、自分を神と名乗る男が目の前に現われ、箱舟をつくるよう命じる。それ以来、エバンの周囲では不思議な出来事が次々と起こりはじめる。", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Don Is Dead", "ザ・ファミリー マフィア血の抗争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "３つのマフィアが共存する街で、ある日一人のボスが殺された。親分を失い組は解散するが、実子のフランクだけは将来跡目を継ぐという名目で別のボスのもとへ養子入りする。一方、親友のトニーはこれを機会に堅気になろうと決心するが……。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GIRL NEXT DOOR", "隣の少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ブラジルの観光リゾート地を舞台に、14歳の少女と、親子ほど年齢の離れた中年作家とのひと夏の恋を描く禁断の官能メルヘン。219人の中から選ばれたスレンダー少女F・モンテーロの、ブラジル版ロリータぶりが見ものの一つ。ボサノバの権化A・C・ジョビンが音楽を担当している。", "1987-01-01", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOMORROW", "オリビア・ニュートン・ジョンのトゥモロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1970年代のポップス界のプリンセス、オリヴィアが、オーストラリアからイギリスへ渡ってすぐに出演した映画デビュー作。地球の優れたミュージシャンを獲得しようとやって来た宇宙人の活躍を、ロンドンの学園を舞台に描く、SF青春ミュージカル。", "1970-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Marie Antoinette", "マリー・アントワネット", "", "", "http://image.eiga.k-img.com/images/movie/28927/315x210.jpg?3581", 123, "14歳でオーストリアからフランス・ブルボン王家に嫁ぎ、18歳でフランス王妃に即位、そしてフランス革命によって37歳の短い生涯に幕を閉じたマリー・アントワネットの人生を「ヴァージン・スーサイズ」「ロスト・イン・トランスレーション」のソフィア・コッポラ監督が映画化。タイトルロールにはキルステン・ダンスト。その他アーシア・アルジェント、ジェイソン・シュワルツマンらが共演。", "2007-01-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAROLES ET MUSIQUE", "残火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "結婚12年目で二人の娘を持つマーゴ。プロデューサーとして忙しい彼女は家庭生活をおろそかにしがちだった。ある日突然、夫に家出されてしまうが、同じ頃若いミュージシャンが彼女に接近して……。脂の乗りきったドヌー主演の作品。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花物語", "花物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "太平洋戦争末期、食糧難のため多くの家がそれまでの花から野菜へと畑を転作、しかし、心から花を愛する女・ハマだけは花畑をかたくなに守った。そんななか食糧管理法が施行され、ハマの畑にも食物の栽培が義務付けられる。当時の実話に題材をとった田宮虎彦の『花』を原作にした心温まるドラマ。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLASS OF 1984", "処刑教室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "校内暴力のはびこる悪名高い高校に赴任した高校教師ノリス。不良グループに授業妨害を受けたうえ、身重の妻をレイプされて、彼は怒りの反撃にでる。電動ノコギリで生徒の腕を切り、ガソリンで焼き殺すといったウルトラ・バイオレンスが戦慄を呼ぶハードな学園ドラマ。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ВЕСЕЛЫЕ РЕБЯТА", "陽気な連中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ジャズ・オーケストラを起用し新境地に挑んだソ連初のミュージカル・コメディ。音楽好きの牧童青年コーチャが、ひょんなことから高名な指揮者と間違えられ、金持ちの別荘に招かれるが……。映像と音の巧みな組み合わせからなる上質なユーモアが快く心和む、音楽映画の古典的作品。", "1934-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さよなら銀河鉄道999", "さよなら銀河鉄道999", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "1979年に公開されて大ヒットした「銀河鉄道999」の続編。前の旅から２年、人間と機械化人との戦いは今も続いていた。星野鉄郎はメーテルからのメッセージを受け取り、再び999号に乗ることになる。だが鉄郎はラーメタル星で、メーテルが機械帝国の女王になったという信じられない話を聞く。そして鉄郎の旅の行手をはばむ謎の黒騎士の出現。やがて機械帝国の首都・アンドロメダにたどり着いた鉄郎は、そこでメーテルと黒騎士についての恐るべき真実を知ることになる……。マンガ、TVシリーズ、映画を含めた「銀河鉄道999」という物語について、その様々な謎を明らかにした異色作。予想を裏切るドンデン返しの連続が、エキサイティングな面白さを生みだしている。DVDタイトルは「さよなら銀河鉄道999−アンドロメダ終着駅ー」。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・社長えんま帖", "続・社長えんま帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "マルボー化粧品社長の大高長太郎は、ライバル・椿堂に水をあけ、海外市場にも手を拡げる勢い。そんな時、長太郎に親会社の社長就任の命令が下った。社内では未曾有の大人事異動で大わらわ。後任は西条企画宣伝部長を任命した。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛の陽炎", "愛の陽炎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "将来を誓いあった男にはほかに女がおり、裏切られたと思い込んだ娘は祖母から教えられた“呪い釘”を手にし、白装束に身を包んでそれを実行に移す。一途な女心とその裏に潜む強烈な嫉妬を、伊藤麻衣子が映画初出演ながら体当たりで演じる。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("堀部安兵衛", "堀部安兵衛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "高田馬場の仇討ちを冒頭に、吉良家討入りをクライマックスにした珍しい安兵衛もの。浅野家仕官までの、のらりくらりしたペースが面白く、黒川弥太郎の飄々とした味がでている。黒川・花井コンビは当時日活の準エースコンビで、約20本の共演作がある。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ネプチューンinどつきどつかれ", "ネプチューンinどつきどつかれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「岸和田少年愚連隊」などで知られる異色の作家・中場利一の小説を映画化した破天荒な青春映画。定職にも就かず、ケンカに明け暮れるミンチ、ガイラ、リイチの３人。それでもガイラは昔の女、ウミが忘れられずに悩んだり、リイチは銭湯の娘、美香に惚れ込んでカタギになる決意をしたりと、それなりに平穏な日々を過ごしていた。だが、ウミの男と美香の妹のために３人は本物のヤクザに勝負を挑むハメになってしまう……。人気お笑いグループ、ネプチューンが初主演。リイチの男っぽさとナイーブな一面を演じた原田泰造の演技が光る。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAVANA　VIOLENTA", "グレートハンティング２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "人喰いライオンの登場でヒットした残酷ドキュメンタリーの続編。ニューヨークの地下道に潜む謎のワニや、グァテマラ大地震の惨状と略奪、サメの腹から出た人間の死体などグロテスクなシーンが連続。", "1976-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRIVING FORCE", "ドライビング・フォース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "S・ジョーンズが子連れトラック野郎に扮して、無法地帯で牙をむく悪党どものバリケードを突破して任務をまっとうするトラックチェイス・アクション。男の闘いと、父と娘の親子愛、ラブ・ストーリーと、盛りだくさんの趣向だ。アクション・シーンはフィリピン・ロケ。", "1989-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dancing Ninja", "ダンシング・ニンジャ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2010-01-01", "5,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cool Running", "クール・ランニング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　オリンピック出場の夢に破れたジャマイカの陸上選手が、あきらめずに冬季五輪のボブスレーに挑戦する姿を実話をもとに描いた。集まったのはボブスレー経験のない陸上選手とその幼なじみ。4人はカルガリー五輪を目標に練習に励む。氷上での経験不足を克服するため、シンクでイメージトレーニングをするなど笑えるシーンが満載。「ライオン・キング」でアカデミー作曲賞を受賞したハンス・ジマーの音楽も、コメディドラマを盛り立てている。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("震える舌", "震える舌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ごく平凡で幸福な家庭の子供が、破傷風にかかった。病気の苦痛と死の恐怖、看護に追われる家族のパニックに陥る状況などを描いた、芥川賞作家・三木卓の同名小説を映画化。意外にもろい家族の絆に焦点をあてる。子役の若命真裕子の熱演はもちろん、彼女の両親を演じた渡瀬恒彦と十朱幸代の鬼気迫る表情も忘れがたい。「砂の器」の名匠、野村芳太郎が撮り上げた異色のヒューマン・ドラマ。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルッキング・フォー・フミコ：　女たちの自分探し", "ルッキング・フォー・フミコ：　女たちの自分探し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "日本が窮屈で飛び出した監督は、ニューヨークで自立した一人の日本人女性・史子と出会い意気投合するが、数年後に彼女が死亡。その史子の生き方をたどるために日本に戻り、かつて彼女が関わっていたというウーマン・リブ運動の女性たちを訪ねていく……。当時の映像を交え“ウーマン・リブ”とは何かを問いかけたドキュメンタリー。ロンドン、シドニー、ボンベイほか海外の映画祭に参加し、高い評価を得ている。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みのりスクランブル！", "みのりスクランブル！", "", "", "http://image.eiga.k-img.com/images/movie/57327/315x210.jpg?4488", 0, "アニメーターが「絵にしてみたい」「動かしてみたい」と感じる企画を、TVシリーズでも劇場用映画でもない短編・中編作品として自由にアニメーション化していく「アニメ文庫」第1弾3作品の1つ。2007年～08年に「まんがタイムきららフォワード」（芳文社）に連載されたちはや深影のコミックを、「劇場版　空の境界」も手がけた野中卓也監督がアニメ化。父はペンギン研究者だがペンギンが苦手な少女たまきは、苦手を克服するために作られたペンギンのアンドロイド「ペンギノイド」のみのりとさまざまなトラブルを巻き起こしてしまう。", "2011-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA GUERRE EST FINIE", "戦争は終った", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "脚本はスペインの元コミュニスト作家の書き下ろし。少年期に内乱を避けてパリに移住して以来、25年間反フランコ運動を続ける男ディエゴは、ある朝スペインとフランスの国境を越えようとするが……。地下活動家の状況を３日間に凝縮して描いた政治と恋の物語。緻密な演出のなかに自由への憧れが脈打つ。", "1965-01-01", "2,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Step Brothers", "俺たちステップ・ブラザーズ　義兄弟", "", "", "http://image.eiga.k-img.com/images/movie/53617/315x210.jpg?4477", 98, "「俺たち～」シリーズのウィル・フェレルと「シカゴ」のジョン・C・ライリーが義兄弟を演じるコメディ。定職にも就かずに親と同居している30代の独身男のブレナンとデールは、親同士の結婚で義理の兄弟に。大人気ない性格の2人は激しく反発し合うが、ブレナンの嫌味な弟が出現で団結し、2人で弟に立ち向かうことになる。監督はフェレル主演作「タラデガ・ナイト」のアダム・マッケイ、製作は「40歳の童貞男」のジャド・アパトー。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("惑星大戦争", "惑星大戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "特撮映画の老舗・東宝が、久々に手掛けたスペース・オペラ。金星を前線基地とする恒星ヨミの第三惑星人が、“宇宙大魔艦”と呼ばれる巨大な宇宙戦艦で地球侵略を開始した。地球軍は、宇宙防衛艦“轟天”を完成させ、これに立ち向かっていく。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カノジョは嘘を愛しすぎてる", "カノジョは嘘を愛しすぎてる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fragiles", "機械じかけの小児病棟", "", "", "http://image.eiga.k-img.com/images/movie/33557/315x210.jpg?3579", 102, "人気テレビドラマ「アリーmyラブ」のキャリスタ・フロックハート主演のホラー映画。イギリス、ワイト島のマーシー・フォールズ小児病院に派遣されてきた看護婦エイミー。閉鎖間近のこの病院には少女の幽霊が現われるという噂があり、次々と起こる怪奇現象が子供たちを悩ませていた。やがてエイミーは、かつて病院で起こった恐ろしい惨劇を知る。監督は「ダークネス」などの作品で知られるスペインの鬼才ジャウマ・バラゲロ。", "2006-07-15", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くノ一忍法帖 自来也秘抄", "くノ一忍法帖 自来也秘抄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "究極の忍法“くノ一忍法・精水波”を操る４人のくノ一。４人の女と肌を合わせた男は、その後、ほかの女と関係しても満足することはできず、最後は悶絶死するという恐ろしい技だ。服部蛇丸はこの４人のくノ一を使い、藤堂藩の乗っ取りを狙うのだった。山田風太郎作品らしい荒唐無稽の内容をエロティックに描いた異色作であり、プロレスラー藤原喜明の出演も話題を呼んだ。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KID", "ザ・リベンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "C・T・ハウエルがハード・トレーニングでマッチョマンに変身、アウトローを演じるハード・アクション。とある田舎町に降り立った一人の男、人々の目は眼光の鋭いその謎の男に注がれる。やがて一人また一人と残忍なテクニックで殺人が起こる。実は10年前、旅の途中５人の狩猟グループに両親を惨殺された男が、その復讐を開始したのだった。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Musketeer", "ヤング・ブラッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　1625年、フランス。銃士だった父と母を殺されてしまった青年ダルタニアンはその敵討ちを胸に秘め、パリへ向かう。しかし国王の近衛銃士隊長は王権を狙う枢機卿らの陰謀によって投獄され、銃士たちもその任務を解かれていた。そんな中、ダルタニアンはひとりで悪と戦おうとするが……。デュマの古典「三銃士」を再映画化。「ワンス・アポン・ア・タイム・イン・チャイナ」シリーズのシャン・シンシンをアクション監督に迎え、ワイヤー・アクションと西洋剣劇を見事に融合させた。", "2001-01-01", "1,7,58", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("めぐりあい", "めぐりあい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "恩地日出夫監督が手掛けた東宝青春路線の佳作。川崎の工場街に生きる貧しい工員と、女店員の純愛を、ほぼ全編にわたるロケーション撮影で描く。映画初主演の酒井和歌子がみずみずしく、以来彼女は内藤洋子と人気を二分する青春スターとなった。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE FANTOME DE LA LIBERTE", "自由の幻想", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1808年、スペインのドレドでナポレオン軍に抵抗するスペイン人たちが叫ぶ、「自由くたばれ！」−−飛んで現代のパリ。少女が持ち帰った観光絵葉書に興奮する両親、翌朝父親が訪れた医者の看護婦は危篤の父の元へ、不思議なホテルを出た彼女の車に乗り合わせた教授の向う先は……数珠繋ぎの不可解な出来事の果てに動物園の動物たちの向うから叫びが！　ブニュエルが完全に自由な映画を作ると宣言した<不可思議3部作>の最終作にして、シュルレアリスム映画の集大成。", "1977-11-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wrath of the Titans", "タイタンの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/57687/315x210.jpg?3621", 99, "サム・ワーシントン主演でギリシャ神話の英雄ペルセウスの活躍を描いた3Dアクション「タイタンの戦い」（2010）の続編。怪物クラーケンを打ち破ってから10年。神々の長ゼウスと人間の間に生まれたデミゴッド（半神半人）のペルセウスは、人間の漁師として息子のヘレイオスとともに静かな生活を送っていた。一方、人間からの崇拝が薄れたことで神々の力が弱まり、冥界に封じられていた巨神クロノスが目覚め始める。クロノスを鎮めようとしたゼウスは、ハデスと息子アレスの裏切りにより捕らわれてしまう。ペルセウスは愛する息子を守り、捕らわれたゼウスを救うため再び立ち上がり、同じ半神のアゲノールやアンドロメダとともにクロノスが眠る「タイタロスの牢獄」を目指す。監督は前作のルイ・レテリエから「世界侵略：ロサンゼルス決戦」のジョナサン・リーベスマンにバトンタッチ。", "2012-04-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上州鴉", "上州鴉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "山中貞雄の名作「国定忠治」（1935）のリメイク。大戸の関所を破って以来、消息を断ったお尋ね者・星越の瀧蔵は、久しぶりに故郷に帰って来る。彼は、金に困って娘を山形屋藤蔵に売った農民・佐兵衛が、金をもらっての帰り道、藤蔵の子分にその金を巻き上げられたことを知り、藤蔵から金と娘を取り戻す。だがそのため身辺が危なくなり……。冬島の演出は歯切れが悪く、山中版よりかなり劣る。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KYOKO", "KYOKO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ハリウッド・インディペンデント映画の巨匠R・コーマンが製作総指揮にあたり、ハリウッドのスタッフによって作られた村上龍の第５作。トラックの運転手をして貯めたお金で、キョウコはニューヨークへ旅立った。目的は彼女が小さかった頃、キューバのダンスを教えてくれたGIでダンサーのホセに会って“ありがとう”と言い、一緒にダンスを踊ること。しかしホセは簡単には見つからず、キョウコは思わぬ旅をアメリカで経験する。街で出会ったマイノリティーの人々の力も借りて、やっと会えたホセ。しかし彼はエイズの末期症状でキョウコのことも思い出せなかった。ホセの望みを叶えるため、キョウコの旅はマイアミ、さらにその先へと続いていく。高岡早紀がダイナミックなキューバのダンスと全編英語のセリフに挑戦した意欲作。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLASH GORDON: PURPLE DEATH FROM OUTER SPACE", "フラッシュ・ゴードン 恐怖のパープル・デス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "1954年「宇宙征服」というタイトルで日本公開されヒットしたシリーズ最終作で、興奮度、完成度のうえからシリーズ最高作といわれる宇宙冒険活劇。死人の額に紫色の斑点を残す謎の疾病が蔓延。悪の皇帝ミンの策略と知ったフラッシュ・ゴードンは、友人バーリンの助けを借り、ミンとの最後の戦いに臨む。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRIMARY　COLORS", "パーフェクト・カップル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "クリントン米大統領がモデルといわれるベストセラー小説を映画化した政界風刺劇。州知事のスタントンと愛妻スーザンは、大統領選挙に野心満々。しかし、スタントンの女グセの悪さはスキャンダルを生み、陣営を慌てさせる。そんな騒動の連続を、理想主義の新米選挙スタッフの視点から描出。主人公夫婦に扮するJ・トラヴォルタ＆E・トンプソンの妙演はもちろん、選挙スタッフの個性的なキャラもおもしろい。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIMSTONE ＆ TREACLE", "スティング ブリムストン＆トリークル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ベイツ夫妻のもとに、娘パトリシアの友人でテイラーと名乗る青年が訪れた。パトリシアは交通事故で植物人間となり物言えぬ状態だったが、彼を見て怯えた様子をする。テイラーは両親を信用させ、娘のレイプを企むが気付かれ失敗。しかし、ショックでパトリシアの体が動き出した。両親に取り入るテイラーの狙いとは……。スティングの映画初主演作。", "1982-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A SOLDIER’S STORY", "ソルジャー・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "第二次大戦中の1944年、アメリカ南部陸軍基地の黒人軍曹が射殺された。その事件を調査するため、ワシントンから黒人大尉のダベンポートが派遣されてきた……。ミステリー仕立てで、当時のアメリカ社会が内包する苦悩を描き出した社会派ドラマ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エル・オー・ヴィ・愛・N・G", "エル・オー・ヴィ・愛・N・G", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "“たのきん”映画から一人立ちした田原俊彦主演のラブ・サスペンス。田原扮するチンピラが殺人事件の容疑者にされ、その真相を突き止めていく。アダルト路線への脱皮を狙ってか、相手役に多岐川裕美を起用したが、このカップルはなんとも不自然だった。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すんドめ3", "すんドめ3", "", "", "http://image.eiga.k-img.com/images/movie/54187/315x210.jpg?3381", 85, "岡田和人の同名人気コミックを実写映画化した第3弾。シリーズを通し、監督と脚本を宇田川大吾が務める。冴えない童貞の高校生・英男は、転校してきたミステリアスな胡桃に一目ぼれし、2人は微妙な恋愛関係を保っていた。そんなある日、胡桃の異性関係にまつわる陰湿な噂が校内でささやかれ始め……。", "2008-11-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雷電", "雷電", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "赤井英和の主演で贈るギャンブル活劇。刑事を辞め、家族も捨てた男、大石は体内の細胞が発する活動電流によってパチプロとしての才能を開花させる。そんな彼の前に現れた、亡き妹とよく似た少女、彩。麻薬漬けにされた彼女を救い出すため、大石はパチンコでヤクザと決着をつけようとする。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BITTER MOON", "赤い航路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "地中海を航海する豪華客船上で、パリ在住のアメリカ人作家オスカーに声をかけられたイギリス人のナイジェル。話相手になってほしいというオスカーに言われるままに、話を聞くナイジェルだったが、妻ミミとのセックスに関するオスカーの、あまりにエキセントリックな話の中身に嫌悪をもよおす。だが、ナイジェル自身、妻フィオナとの愛を確認するため、この船旅に出たのであった。そして皆が浮かれ騒ぐニュー・イヤーズ・イヴのパーティーで、悲劇が起こる……。R・ポランスキー監督が描く、男女の愛の罪深さと性欲の深淵。", "1992-01-01", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LILIES OF THE FIELD", "野のユリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "S・ポワチエが、黒人としては史上二人目のアカデミー賞を受けた心温まるヒューマン・ドラマ。アリゾナの荒地に修道院を建設しようと、マリア院長以下、東ドイツから亡命してきた５人の尼僧が孤軍奮闘していた。そこへ流れ者の黒人青年ホーマーが現れ、気まぐれ半分に修道院建設を手伝うことに。信心深くガンコなマリア院長と束縛を嫌うホーマーは、ことあるごとにケンカになるが、やがて地域の人々の友情と信頼を得た彼らは、ついに独力で困難な仕事を成し遂げる……。R・ネルソン監督の手腕が発揮された、粋で泣かせるラストシーンは必見。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千機變II　花都大戦／THE　HUADU　CHRONICLES：BLADE　OF　ROSE", "ツインズ・エフェクトII／花都大戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "古代の中国を舞台に、女人帝国の圧政に立ち向かうために旅する青年と仲間の冒険を描いた伝奇活劇。本作で俳優デビューを飾ったジャッキー・チェンの愛息ジェイシーが、秘めた能力に覚醒する主人公を熱演。父ジャッキーや、香港の人気アイドル・ユニット“Twins”との共演も見逃せない。男を奴隷として扱う冷酷な女帝に支配された帝国。旅芸人一座の青年チャーは、この圧政を打倒するため、神剣を求めて旅に出るが……。", "2005-08-27", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST OF THE BLUE DEVILS", "KC〈カンザスシティー〉　ジャズの侍たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "カンザス・ジャズの大御所C・ベイシーをはじめ、J・マクシャンらが1974年に地元で行ったジャム・セッションの模様と、彼らの回顧談を収めたドキュメンタリー。伝説的なバンド“ブルー・デヴィルス”の復活や、チャーリー・パーカーの貴重な映像等、ジャズ・ファン泣かせのシーンが続々登場する。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HYPERSAPIEN PEOPLE FROM ANOTHER STAR", "ハイパーサピエンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "銀河の果てからやって来た異星人二人と一匹の不思議な生物が、地球に降り立った。彼らの目的は何なのか……。SFXスタッフは「スター・ウォーズ」「エイリアン」を作ったメンバー。また、製作総指揮はF・F・コッポラの実妹であり「ロッキー」シリーズの女優T・シャイア。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OTSUYU・怪談牡丹燈籠", "OTSUYU・怪談牡丹燈籠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "武家の娘・露と浪人者の新三郎が出会い、お互いに惹かれ合うが、彼は相手が露の妹・鈴とは知らずに見合いをしてしまう。鈴は彼に好意を寄せるが、姉の気持ちを知って自害。露も後を追った。そしてお盆も過ぎたころから姉妹の亡霊が新三郎の元に毎晩通い始めるようになる。有名な古典怪談を、戦国、江戸、現代と時空を越えた大胆な設定と、CGを駆使した映像で映画化したホラー。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門六番手柄 仁念寺奇談", "右門六番手柄 仁念寺奇談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "時の将軍・家光の日光参拝が発表されるや、その通過路・武州忍の松平伊豆守の城下に奇怪な辻斬り事件が発生、はるか江戸から招かれたむっつり右門は伝六を従え事件解決に乗り出す。原駒子が、櫛巻のお由に扮して大活躍。", "1930-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五条霊戦記 GOJOE", "五条霊戦記 GOJOE", "", "", "http://image.eiga.k-img.com/images/movie/1407/315x210.jpg?1239", 137, "平安時代末期。平家が支配する京の都は荒れ果て、世紀末の様相を呈していた。五条橋では、平家武者が次々と襲われ、人々に“鬼”の仕業と恐れられていたが、その正体は源氏の生き残り、遮那王こと源義経だった。その頃、夢の中で「鬼を退治せよ」との掲示を受けた弁慶は、義経を討つべく立ち上がる。そして五条橋で弁慶が見たものは、希有なる容姿を持ち最強の気を操る美青年の姿だった。", "2000-10-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("7 HOURS TO JUDGMENT", "デッド・リミット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "３人の凶悪犯を証拠不十分で無罪にした裁判官が、被害者の夫から逆うらみされ、妻を誘拐されてしまう。ニューヨークを舞台に、緊張感あふれる駆け引きが展開する。B・ブリッジスが監督と主役を担当している。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOOS", "時を刻んだ鎖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "オランダが生んだ天才画家ゴッホの孫にあたるT・V・ゴッホ監督による官能的なサスペンス・ミステリー。何度も事件を起こしている被告を憎みながらも、腐りきった縁がもとで離れられない孤独な弁護士。彼は一人の女性と知り合い、彼女とのSM的な性の快楽に溺れていく。だが、突然彼女が失踪して……。", "1989-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVEHOTELS ラヴホテルズ", "LOVEHOTELS ラヴホテルズ", "", "", "http://ecx.images-amazon.com/images/I/41DiRhJ7OGL._SL210_.jpg", 104, "日本特有の文化であるラブホテルを舞台にしたオムニバス・ラブストーリー。クリスマスに2人の男性から誘われて両方と約束してしまったスズコ、初恋相手と不倫を続けているサクラ、体だけの関係の相手がいるサチ、そしてラブホテルのオーナーなのにセックスが嫌いな愛という、さまざまな事情を抱えた女性たちの恋の行方を描いていく。主演を務めるのは「NANA」のサエコ、映画やCMで活躍中の原田佳奈ほか。", "2006-06-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MUSIC LOVERS", "恋人たちの曲／悲愴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "作曲家チャイコフスキーの伝記映画だが、この有名な音楽家をマザコンのホモセクシャルな男として描いた異色作。1873年のクリスマスの夜、チャイコフスキーは友人シロフスキーと結ばれる。だが、二人の愛はチャイコフスキーの姉によって引き裂かれ、その苦悩のなかで、チャイコフスキーは数々の美しい曲を生み出す。そして、フォン・メック夫人の援助のもと、彼は新たな作曲活動の日々を送り、美しいニーナと結婚するが、二人の結婚生活を待っていたのは、耐えがたいまでの日々だった。K・ラッセル独特の美的乱調と暴力的なセックス表現が描かれたセンセーショナルな夢の世界。", "1970-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASANSOR", "エレベーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "日本に紹介されるのは珍しい、トルコ製のサイコ・スリラー。過激な報道で有名なキャスター、ジャンが、あるアパートのエレベーター内に閉じこめられた。停止階の目の前のドアから現れたセクシーな美女は彼を挑発。いたぶられ、残酷な仕打ちを受けながらも、ジャンはなす術もない。そして監禁から４日後、女の行動に変化が生じ……。緊迫感あふれる密室劇のなかに、メディア風刺を浮き彫りに。", "1999-01-01", "32", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Heaundea", "TSUNAMI ツナミ", "", "", "http://image.eiga.k-img.com/images/movie/55567/315x210.jpg?3026", 107, "韓国を代表するリゾート地・海雲台（ヘウンデ）を舞台に、大津波の恐怖を壮大なスケールで描くディザスター・ムービー。「オアシス」「シルミド　SILMIDO」のソル・ギョング、テレビドラマ「ファン・ジニ」のハ・ジウォンが主演を務める。2004年、歴史上類を見ない死傷者を出したインドネシアの大津波。インド洋で遠洋漁船に乗っていたマンシクは津波に巻き込まれ、密かに想いを寄せていたヨニの父親を失う。この事故をきっかけにヨニへの気持ちを隠してきたマンシクが、ついにプロポーズを決意する。しかしそのころ、高さ100メートル、時速800キロの超大型津波が迫っていた。", "2010-09-25", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RUSSIANS ARE COMING", "アメリカ上陸作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "アメリカ北西部の沖合を潜行中のソ連の潜水艦が誤って座礁したことから、乗組員８人は近くの島に上陸したが、それを知った島の人々はロシア人が攻めてきたと勘違いし、大騒動になっていく……。N・ジュイソンが、当時の米ソの冷戦状態を徹底した風刺とドタバタで描いたシニカル・コメディの傑作。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Spy Next Door", "ダブル・ミッション", "", "", "http://image.eiga.k-img.com/images/movie/55237/315x210.jpg?3965", 92, "ジャッキー・チェン主演のアクションコメディ。CIAエージェントのボブ・ホーは、自身のキャリアアップを諦め、隣に住むガールフレンド・ジリアンと静かに暮らすことを選ぶ。だが退官早々、ジリアンが所用で家を空けることになり、ボブはジリアンの3人の子供のベビーシッターを務めることになる……。監督は「ベートーベン」「フリントストーン　モダン石器時代」のブライアン・レバント。共演にアンバー・バレッタ、ビリー・レイ・サイラス。", "2010-06-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Autumn in New York", "オータム・イン・ニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/1267/315x210.jpg?2171", 107, "ニューヨーカーに人気の高級レストランを経営するウィルは、次々と恋人を替え独身生活を謳歌する48歳。シャーロットは22歳になったばかりの学生。2人は秋のセントラル・パークで出会った。ウィルは娘のような彼女に翻弄される快感に身を任せ、シャーロットは初めての恋に酔っていた。だが、好奇心が本当の愛に変わった時、ウィルが知った悲しい事実──シャーロットは不治の病に冒され、残されたのは僅か一年という命だったのだ。", "2000-09-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNION STATION", "武装市街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "W・ホールデンとN・オルソンのコンビで描いた犯罪アクション映画。大富豪の盲目の娘が誘拐され、10万ドルの身代金が要求されてきた。保安刑事のカルホーンは、市警察の応援を受けて捜査に乗り出していく。監督はカメラマン出身のR・マテ。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROLL", "トロル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "風変わりなアパートに引っ越して早々、悪の精トロルに乗り移られた妹のウェンディ。兄のハリーだけがその異変に気付き、魔女の力を借りてトロルと戦う決心をするが……。エンパイア・ピクチャーズが手掛けたSFファンタジー。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Unbreakable", "アンブレイカブル", "", "", "http://ecx.images-amazon.com/images/I/51LgM-gxWwL._SL210_.jpg", 107, "フィラデルフィアで起こった悲惨な列車衝突事故。131人の乗員・乗客が死亡したこの事故で、デビッドはただ1人、傷ひとつ負わず奇跡的に死を免れた。「なぜ、俺だけが？」　やがてイライジャと名乗る男が現れ、デビッドこそが不滅の肉体を持つ者“アンブレイカブル”であり、弱き者を守る使命を帯びたヒーローだと告げる。", "2001-02-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUMPELSTILTSKIN", "ランペル　蘇る狂気", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1300年代のヨーロッパに実在したというランペルスティルスキン。それは、世にも恐ろしい顔をした小人で、誘拐した人間の赤ん坊の生気を吸い取り、永遠の生命を得る怪物である。１度はジプシーによって、石の人形に封印されたこの怪物を倒す方法はただ一つ。燃えたぎるわらを浴びせながら“ランペルスティルスキン”と３回唱えることであった。時は変わって1900年代。ロサンゼルスの骨董屋で、ランペルスティルスキンの石の人形を手に入れた未亡人シェリーは、この怪物をよみがえらせてしまう。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふるさと−JAPAN", "ふるさと−JAPAN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "昭和30年代前半の東京の下町を舞台に、童謡を歌い継いでいこうとする女性教師と合唱を通して成長していく子どもたちの絆を描いた感動アニメ。昭和31年の春、深川・木場の小学校に坂本理恵子という名の教師が赴任してくる。彼女は戦死した兄の遺言により、子どもたちに童謡を伝えていくことに強い情熱を持っていた。そんな彼女の指導の下、子どもたちは年末に開催される合唱大会での優勝を目指し練習に取り組むが……。", "2007-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOUENRY TO THE CENTER OF THE EARTH", "地底探検", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "ヴェルヌ原作『地底旅行』の映画化。英国のオリヴァー地質学教授一行は、アイルランドの噴火口から地底探検に出発。未知の地底で大キノコや大蛇に出会いながら、やがて彼らがたどり着いた場所は……。ハリウッドの職人監督らしい、娯楽に徹したSFアドベンチャー。。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAJOMARU", "TAJOMARU", "", "", "http://image.eiga.k-img.com/images/movie/54337/315x210.jpg?3090", 131, "黒澤明監督の名作「羅生門」（50）の原作として知られる芥川龍之介の小説「藪の中」を換骨奪胎し、「羅生門」で三船敏郎が演じたキャラクター「多襄丸」を膨らませたオリジナルストーリーとして製作した小栗旬主演の異色時代劇。室町時代末期を舞台に、1人の愛する女性のため、地位、名誉、肉親、名前を捨てた青年の愛憎劇が描かれる。監督は「RED SHADOW　赤影」の中野裕之。共演に柴本幸、田中圭、松方弘樹、萩原健一。", "2009-09-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北の宿から", "北の宿から", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "田沢湖畔の北の宿を舞台に、帰らない恋人を待つ女、いわくありげな男女の心中、大病院の令嬢と天文学者の恋という、３つの恋の形を描いた作品。1976年のレコード大賞に輝く都はるみの大ヒット曲をもとにした作品で、田村正和、中野良子らが生真面目な若者を演じる。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOUR FRIENDS", "フォー・フレンズ～4つの青春～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "ニューシネマの名匠ペンが「ミズーリ・ブレイク」以来５年ぶりに製作した青春映画。1960年ユーゴからシカゴへやって来た少年と、その親友の男二人、女一人の15年にわたる愛と友情が綴られる。移民の悲しみ、人種偏見、反戦など、1960年代のアメリカ社会が内包する様々な問題を織り込んだ、厚みのあるドラマだ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("センチメンタル・シティ・マラソン", "センチメンタル・シティ・マラソン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "幼い頃、離れ離れになった姉妹、マチネとソワレ。マチネは暗殺者として成長し、言葉を失ったソワレは花屋の店員として人生を送っていた。境遇の違う二人は、互いに互いを探し始める。それは、自分自身を探す旅でもあった……。キャラクターのまったく違うマチネとソワレ、二つの少女像をともさかりえが好演。タイで１ヵ月間のロケを敢行し、無国籍感覚あふれるユニークな青春映画に仕上げた。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ターン", "ターン", "", "", "http://image.eiga.k-img.com/images/movie/1717/315x210.jpg?3528", 111, "27歳の銅版画家、真希は交通事故に遭い、そのときから毎日、事故にあった時刻になるとその前日に戻ってしまうようになる。孤独な生活を送る彼女に、ある日、現実世界で彼女の作品を見たデザイナーの卵、洋平から電話がかかってくる。彼女の状況はなぜ生じたのか、はたして現実に戻れるのか。「愛を乞うひと」の平山秀幸監督が北村薫の同名小説を映画化。撮影は「夢二」の藤澤順一、美術は「学校の怪談」の中澤克巳が担当。", "2001-10-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　戦国BASARA The Last Party", "劇場版　戦国BASARA The Last Party", "", "", "http://image.eiga.k-img.com/images/movie/55797/315x210.jpg?2169", 90, "2005年の第1作からシリーズ累計220万本を売り上げた人気アクションゲームで、TVアニメーション化もされた「戦国BASARA」の劇場版。群雄割拠の戦国時代、豊臣秀吉の治世が終わり、新たな覇権をめぐって戦が起ころうとしていた。奥州・伊達政宗の前には、秀吉の仇討ちに燃える石田三成が姿を現す。一方、己の拳を武器に天下統一を誓う徳川家康も、世を治めるため立ち上がる。それぞれの思いを秘めた武将たちは、やがて決戦の地・関が原に集う。", "2011-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Morte a Venezia", "ベニスに死す", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "ノーベル賞作家トーマス・マンの同名小説を原作に、作曲家グスタフの美少年への心酔と老いの苦しみを描いたルキノ・ビスコンティ晩年の作品。「地獄に堕ちた勇者ども」に続いて撮られた、ドイツ3部作の2作目にあたる。療養のためベネチアにやってきたドイツの老作曲家は、ホテルで少年タジオを見かける。一目で少年の美しさの虜になり、彼の姿を見つけるだけで喜びを感じ始める。全編に流れるのは、グスタフのモデルになったマーラーの「交響曲第3、5番」。", "1971-10-02", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男組", "男組", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "同名の人気劇画を映画化した、過激な学園もの青春アクション。荒廃した私立の名門高校、青雲学園。神竜剛次をリーダーとする凶悪な不良グループに対抗するために、学校側は関東少年刑務所から、父親殺しで服役中の流全次郎を呼び寄せるが……。手錠をした高校生という豪快さが魅力。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刹那", "刹那", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "2010年8月に死去した芸能リポーターの梨元勝が、アイドル・酒井法子の覚せい剤所持・使用事件を追った遺作著書「酒井法子・隠された素顔」をもとに映画化。酒井の取材に奔走する週刊誌の女性記者の姿を通して、社会をむしばむ薬物の恐ろしさを訴える。", "2011-04-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BERSAGLIO MOBILE", "太陽の暗殺者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "怪盗として名を売ったジェイスンは手錠をつけたままアテネ空港から脱出するが、ある組織の罠にはまり、強引に仕事を引き受けさせられる。その仕事とは、刑務所の死体置場から義歯を一本盗んでくるというもの。義歯には東西にまたがる二重スパイの名簿を収めたマイクロフィルムが詰め込まれているのだった。太陽の町アテネを舞台に展開するスパイ・アクション。", "1968-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君はまだ、無名だった。", "君はまだ、無名だった。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "仕事に忙殺される新進作曲家が、純粋に音楽を愛した高校時代を振り返る青春ストーリー。仲間たちとのバンド活動など若さあふれるシーンは瑞々しく、懐かしい気分になる。湘南出身の監督らしく、地元民ならではの湘南が見られる。夢叶って作曲家として活動している和実だが、多忙な日々に違和感を持っていた。そんな中、高校時代の恩師の死をきっかけに、ただただ音楽が好きだった青春時代を思い起こす。", "2006-04-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROOM TO LET", "間借人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 164, "登場人物が、過去に起こった未解決の殺人事件を語り、ラストにどんでん返しが……。ハマー・フィルムならではの本格推理ドラマ。マイケルは昔、自分が記者だった頃の謎めいた事件の話を二人の友人に語り始める。ある病院で発生した火事、そのあとドクター・フェルという男が、マイケルの知人宅に下宿したこと。そしてその男が、下宿の密室で殺されたこと。マイケルは、自分の推理を聞かせ、周りを納得させるが、真相はマイケルが帰ったあと明らかになる……。", "1950-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK COBRA４：DETECTIVE MALONE", "ブラック・コブラ４", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "シカゴ市警のスゴ腕黒人刑事“ブラック・コブラ”ことロバート・マローンが活躍するポリス・アクション・シリーズ第４作。今回、マローンに新しい相棒が登場。凶悪なテロ組織に、新コンビで戦いを挑む。第１作に出演したC・ランドグレンとM・ポーリーが再び共演している。", "1991-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イルカの豆 TWILIGHT FILE VII", "イルカの豆 TWILIGHT FILE VII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "オムニバスシリーズ「TWILIGHT FILE」第7弾の1作。海の近くにある昔ながらの喫茶店「ベルーガ」のマスター・風間亮は、17歳の時にたった1人の家族である母親を亡くして以来、孤独な人生を歩んでいた。そんなある夏の日、突然、店に亮の妹だと主張する女性・小夜が現れ、2人のぎこちない兄妹生活が始まる。主演は大沢樹生、ヒロイン小夜役で人気タレントの夏川純が出演。", "2010-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狂った一頁", "狂った一頁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 59, "大正末期、文壇で新鋭作家の集団として結成された“文芸時代”の同人・横光利一、川端康成、片岡鉄兵ら、いわゆる新感覚派の協力を得て、衣笠貞之助が日本映画史上はじめて監督として独力で製作した作品。純粋映画を狙った画期的な無字幕の無声映画として話題を呼び、当時としては異例だが洋画系で封切られた。狂った妻が入院している精神病院に勤める小使いの目を通して、非日常的な世界を光と影の中に描いた映像は強烈。50年間フィルムは消滅したものと思われていたが、1971年に偶然完全な状態で発見され、監督自らがサウンド版を作りフランスやイギリスで公開、大成功を収めた。", "1926-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鍵", "鍵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "耽美派文学の巨匠・谷崎潤一郎が晩年に発表した同名小説を、最も脂の乗っていた時期の市川崑監督が映画化した作品。初老を迎えた地位ある男とその妻、さらにその娘と恋人の医師の４人の奇妙な関係が官能的に描かれている。カラーの発色を極力抑えた撮影が秀逸。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FOUR FEATHERS", "四枚の羽根", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "英国の作家A・E・W・メイソンの同名小説二度目の映画化。戦争を嫌い、友人三人がエジプト出征していく中、軍隊から除隊したハリー。彼の元には、臆病者の印である羽根が３つ届けられ、ハリーの婚約者エスネも羽根を残して去って行った。やがてハリーは、汚名返上しようとエジプトへと向かうが、そこで失明した友、ジョンを発見する。友情を描く大河ロマン。", "1939-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪の華", "悪の華", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "財閥の娘、多鶴子を誘拐した辰をはじめとする３人組は、犯罪者の逃がし屋をする澄江の指示で軽井沢へ向かう。一方、これを横取りしようとする澄江は愛人の正一とその弟、タカシに跡を追わせるのだが……。悪党２組の身代金をめぐる争いと多鶴子とタカシの愛が展開するアクション作品。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("North by Northwest", "北北西に進路を取れ", "", "", "http://ecx.images-amazon.com/images/I/51opxuWH9aL._SL210_.jpg", 137, "アルフレッド・ヒッチコック監督の集大成とも言われる巻き込まれ型サスペンス映画の名作。ニューヨークで広告会社を経営するロジャーは、偶然同じ場所に居合わせてしまったためにキャプランという男に間違えられ、謎の組織から追われる羽目に。ロジャーは追われながらも真相を探るべくニューヨークからシカゴ、サウスダコタのラシュモア山まで息もつかせぬ逃走劇を展開する。主演にケイリー・グラント。脚本は「成功の甘き香り」の名手アーネスト・レーマン。", "1959-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONTRA EL VIENTO", "アントニオ・バンデラス　インセスト 近親相姦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "A・バンデラスが、スペイン時代に出演した禁断のラブ・ストーリー。核廃棄物施設で働くファンを頼り、妹のアナが家を飛び出す。一年ぶりに再会した兄と妹の間には、懐かしさ以上の熱い感情が流れていた。その感情を抑えるため、それぞれ違う相手と関係を持つが心は満たされない。やがて二人は、お互い激しく求め合い一線を越えてしまう……。", "1990-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銭のとれる男", "銭のとれる男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "佐川次郎は、音楽家を夢見ながら独学でトランペットを吹きつづけていた。そんな次郎の素質を見込んだブルー・フィンガーズのバンド・マスターの岡は彼を一人前のトランペッターに育てていく。カーレースと音楽に青春をかける若者の姿を、彼をとりまく女たちとともに描く。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CaddyshackII", "ボールズ・ボールズ2／成金ゴルフマッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "あるゴルフ場を巡って繰り広げられるドタバタ騒動を描いたコメディ。娘に連れられて高級カントリークラブへやって来た成金親父ジョンは、クラブへの入会を拒否されてしまい大激怒。彼は報復のためにクラブを買収しようと企てる。そこで、クラブの所有権を賭けたゴルフ試合が開催されることになり……。奇妙な殺し屋を演じたダン・エイクロイドが、ラジー賞のワースト助演男優賞を受賞。また本作は同賞のワースト主題歌賞も受賞している。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUTE UNE VIE", "マイ・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 148, "C・ルルーシュ監督の集大成といわれる作品。第一、二次大戦を経て現代に至るまでのヨーロッパが舞台。映画の誕生期からカラーの時代へと進歩する映画界を縦軸に、一族三代にわたる愛のドラマをF・レイの華麗な曲にのせて描く。", "1974-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魚河岸の石松", "魚河岸の石松", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "魚河岸育ちの若い衆、人呼んで石松の木村松吉は、仲間の宴会旅行でふと知り合ったストリッパーのマノン、由美、マミにのぼせ、上海の辰というヤクザと大喧嘩……。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("kamome", "kamome", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "昭和57年８月24日、愛媛県松山市で起こった“ホステス殺人事件”を題材にした実録ドラマ。犯人の女が顔に整形を受け、15年間も逃亡生活を送ったことで、ワイドショーのネタにもなった怪事件の映画化だ。衝動的に殺人を犯し、夫と子供を置き去りにして逃走した主人公、福間和江が、セックスに身を焦がす情念の女として日本各地を転々とし、警察に通報されるまでの軌跡をドキュメンタリー・タッチで描き出す。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獨眼龍政宗", "獨眼龍政宗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "奥州を制した戦国武将・伊達政宗の若き日を描き、指導者としての理想的な人間像を追及した時代劇。“いま戦をするのは民を救わんがため”と熱弁をふるう政宗の姿に戦時中の映画界体制が垣間見えるが、片岡千恵蔵の人間味たっぷりの名演がそれを忘れさせてくれる。原作は石坂洋次郎の『小さな独裁者』。", "1942-01-01", "76", 0, NOW(), NOW());
