INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜のピクニック", "夜のピクニック", "", "", "http://image.eiga.k-img.com/images/movie/31217/315x210.jpg?3481", 96, "第2回本屋大賞に輝いた恩田陸の同名ベストセラーを「ココニイルコト」「ソウル」の長澤雅彦監督が映画化。24時間、夜通しで80kmを歩き続ける高校の伝統行事“歩行祭”に参加した生徒たちの心の成長と恋愛をストレートに描く青春群像劇。主演は宮部みゆき原作の「理由」に出演した多部未華子。共演に嶋田久作、田山涼成、南果歩ら。", "2006-09-30", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUDWIG", "ルートヴィヒ 神々の黄昏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 184, "19歳の若さでバイエルン国王となったルートヴィヒ２世は、作曲家ワグナーに心酔し、巨大な国費を注ぎこんだ。彼が思いを寄せる従姉のオーストリア皇后エリザベートは、妹ソフィーとの婚約を勧め彼も同意するが、エリザベートへの思いが断ち難く婚約は破棄される。1886年、オーストリアとの戦いに敗れ、信頼していたワグナーにも裏切られたルートヴィヒは孤独のどん底へと突き落とされていく……。「地獄に堕ちた勇者ども」「ベニスに死す」に続いて作られた、ヴィスコンティのドイツ３部作を締めくくる作品。19世紀半ば、国王に即位し、40歳で謎の死を遂げた“伝説の狂王”ルートヴィヒ２世の生涯を描いたこの作品には、病に倒れながらも執念で撮り上げたヴィスコンティの気迫が全編にみなぎっている。ルートヴィヒにはヴィスコンティが愛したバーガーが扮し、鬼気迫る演技で狂王になりきった。1989年に復元完全版が公開。DVDは「ルキノ・ヴィスコンティ DVD−BOX２」に完全復元版が収録。", "1972-01-01", "4,7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("尼羅河女兒", "ナイルの娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ホウ・シャオシェンのフィルモグラフィー中、自伝的作品系列から離れた異色のアイドル映画。母を亡くし警官の父とは仕事で遠く離れ、兄妹と三人で暮らす孤独な少女、シャオヤンは日本のコミック『ナイルの娘』に夢中。彼女は兄の経営するレストランで働くアーサンに想いを寄せ、コミックのヒーローと重ね合わせて夢中になるが、ある日アーサンは、ヤクザの情婦と恋に落ちてしまう…。主演は当時の人気歌手、ヤン・リン。", "1987-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("呪怨２", "呪怨２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "サム・ライミ製作で清水崇監督によるハリウッド・リメイク作も製作された、大ヒット・ホラーの第２作。呪われた一軒家を訪れたテレビ番組のスタッフたちが、次々と怪現象に襲われる。隙あらば幽霊が出現するというショック描写に加えて、母性を絡めた悲痛なムードも恐怖をあおる。本作もハリウッドでリメイクされた。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My New Partner", "最高のパートナー ", "", "", "http://image.eiga.k-img.com/images/movie/54127/315x210.jpg?3369", 110, "「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。対照的な親子刑事が、警察内部の巨大悪に立ち向かうハードボイルド刑事アクション。「光州5・18」のアン・ソンギが人情深い風俗事件の万年班長の父親を、「連理の枝」の人気スター、チョ・ハンソンが冷血漢のエリート刑事の息子を好演。8年ぶりに再会した父子が、部下と上司という一触即発の関係を乗り越え、警察内部の闇を暴くためタッグを組むが……。", "2008-10-11", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("純情二重奏", "純情二重奏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "佐々木康＝高峰三枝子＝万城目正トリオの記念すべき第１作。このトリオは戦後も「懐しのブルース」から「情熱のルンバ」まで日本の歌謡映画をリードした。スターを目指す娘二人の話に霧島昇や淡谷のり子らのヒット曲が花を添えている。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　REVENGE　OF　FRANKENSTEIN", "フランケンシュタインの復讐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "イギリスのハマー・プロによるフランケンシュタインもの第２作。処刑寸前のところで難を逃れたフランケンシュタイン男爵が、スタイン博士と名を偽ってまたしても人造人間の制作に着手する。ところが再生した死体は暴走してしまう。追い詰められたフランケンシュタインは、自分の脳を死体に移植するよう助手に命じるのだった。", "1958-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("正門前行", "正門前行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "卒業制作に取り組む美大の青年と、彼の周囲で起きた盗難事件のてん末をコミカルに描く青春ドラマ。美大の卒業制作としてシナリオ作りに悩んでいた神崎の学校で、盗難騒ぎが持ち上がる。金工科の女子学生が制作していた金のブレスレットがなくなり、彼女の恋人の松永が疑われていた。この騒ぎが映画のネタになると考えた神崎は、松永に取材を開始。やがて意外な真相が明らかになる。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ネコと金魚の恋物語", "ネコと金魚の恋物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "ペットをめぐる人間模様を6人の監督が描いた「PETBOX ペットボックス」シリーズの1編。恋人を亡くしたショックから、彼女が飼っていた金魚を飲みこんでしまった雄介。その後、書いた小説が芥川賞を受賞したものの、現在はスーパーの店員として冴えない毎日を送っている。そんな彼の前に、アパートの屋上から突然、ネコと女が降ってきて……。主演は、「リアリズムの宿」の山本浩司と、映画初出演となるモデルの鮎川陽子。監督は「サンクチュアリ」の瀬々敬久。", "2006-12-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Michel Villant", "ミシェル・ヴァイヨン", "", "", "http://image.eiga.k-img.com/images/movie/1537/315x210.jpg?3850", 104, "天才カーレーサー、ミシェル・ヴァイヨンがル・マン耐久レースで宿敵チームと対決。リュック・ベッソンの製作会社の最新作。57年に連載開始の人気同名コミックをベッソンの共同脚本で映画化。監督はホンダ、ルノーなど自動車CFでも注目を集め、「略奪者」で監督デビューしたルイ・パスカル・クブレア。04年の注目作「トロイ」でヘレン役に大抜擢されたディアーヌ・クルージェがヴァイヨンのチームメイト役で出演。", "2003-12-20", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("An Inconvenient Truth", "不都合な真実", "", "", "http://image.eiga.k-img.com/images/movie/1397/315x210.jpg?1749", 96, "アメリカで公開されるや、各都市で大ヒットを記録し、06年度アカデミー賞でも本命視される環境ドキュメンタリー。民主党クリントン政権下で米合衆国副大統領を務めたアル・ゴアによる地球温暖化問題についてのスライド講演を追っている。監督は「24」や「ER緊急救命室」などの人気TVドラマの演出を手がけてきたデイビス・グッゲンハイム。", "2007-01-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画ドラえもん　のび太の新魔界大冒険 7人の魔法使い", "映画ドラえもん　のび太の新魔界大冒険 7人の魔法使い", "", "", "http://image.eiga.k-img.com/images/movie/33877/315x210.jpg?3745", 112, "新たな声優陣を迎えてリニューアルされた「ドラえもん」新シリーズの劇場版第2弾。1984年に公開された「のび太の魔界大冒険」をリメイク。のび太は“もしもボックス”で現実の世界を魔法の世界に変えてしまう。その世界では誰もが魔法を使えるが、のび太は魔法を上手く操ることができない。やがて、ドラえもんたちは地球侵略を企む魔界星が地球に接近していることを知る。「ホワイトアウト」のベストセラー作家・真保裕一が脚本を担当。", "2007-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TARZAN，THE APE MAN", "類猿人ターザン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "アフリカで商売を営むイギリス商人パーカーは、友人のホルトと一緒に象の墓場といわれる秘境の探検に出発するが、たまたまイギリスから帰ってきたパーカーの娘ジェーンも一行に加わる。一行は途中、見知らぬ原住民の一団やカバの大群に襲われるが、その度にターザンの叫び声に救われる。そして、ある夜、野営する一行の前にターザンが姿を現わし、ジェーンをさらっていく。ジェーンは初めターザンを恐れるが、やがて二人の仲は親密になっていく。歴代のターザン役者のなかで最も有名なワイズミューラーのデビュー作。この作品から例の“アーアアー！”というターザンの叫び声が始まった。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KINGS OF THE SUN", "太陽の帝王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "数千年の栄華を誇った中米のマヤ王国。西方部族の突然の来襲で国を失ったマヤ族は、バラーム王子に率いられ北米に漂着。さらに、インディアンとの争いを経て、酋長ブラック・イーグルの協力を得た王子は、西方部族に新たな戦いを挑むという壮大な歴史ドラマ。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JENSEITS DER STILLE", "ビヨンド・サイレンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "1997年の東京国際映画祭でグランプリを受賞した人間ドラマ。少女ララは手話を駆使して、ろうあの両親の“通訳”を務めている。ある日、叔母からクラリネットをもらった彼女は練習に励む。しかし、叔母を嫌っている父は耳の不自由さからくる負い目もあって、ララの趣味を快く思わない。やがて18歳になったララは父の反対を押し切り、家を出て音楽学校に入学するが……。父親との対立と和解、恋人との愛、最愛の母親の死を経験して、大人へと成長するヒロインの心の変化を、みずみずしく描写。感傷に溺れない真っ直ぐな演出が、心地よい感動を呼び起こす。", "1996-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WILD AMERICA", "Born to be ワイルド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "1967年、映画好きの兄弟３人が中古の16ミリカメラを手に旅に出た。大自然をフィルムに収めようとワニやクマの棲息地に足を踏み入れた彼らは、多くの冒険や出会いを経て成長していく。製作者M・ストーファーの実体験に基づく、さわやかな冒険青春映画。エンド・クレジットには、実際にストーファー兄弟が撮影した野生動物の映像が登場。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("台風クラブ", "台風クラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "第１回東京国際映画祭のヤングシネマ部門で大賞を獲得した記念すべき作品。審査員として来日していた英国のプロデューサー、デヴィッド・パットナムやイタリアのベルナルド・ベルトルッチ監督らも興奮させたという折り紙つきの傑作。ディレクターズ・カンパニーのシナリオ募集コンクール準入選の脚本を「ションベン・ライダー」や「翔んだカップル」など少年少女の姿を鮮烈に描いた異才・相米慎二が演出した。東京近郊のある中学校に台風が接近。やり場のないいらだちを感じながら平凡な日々を送っていた中学生たちは、台風の接近とともにその感情をたかぶらせ、風雨にさらされた校舎はいつしか彼らの祭りの解放区に……。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Breakout", "ブレイクアウト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "殺人罪で28年の刑を言い渡されたジェイ。彼の無実を知る妻は、脱獄を望む夫のため、金のためなら何でも請け負う、一匹狼の流れ者を雇う。厳重な警備のなか、ヘリコプターを使って脱走するラストが痛快。実際にあった事件にヒントを得て製作。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続丹下左膳", "続丹下左膳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "妖刀・乾雲、坤竜をめぐる江戸市内の混乱に心を悩ました南町奉行・大岡越前守は、将軍家を動かして饗庭・主水正を出府させる。累の及ぶことを恐れた主水正は、主君懐しさに会いに来た左膳を追い払い、月輪剣団に左膳暗殺を命じるが……。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("錆びた炎", "錆びた炎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "とある総合病院から５歳児の血友病患者が誘拐されたことに端を発する、推理サスペンス。病院のお手伝いが見えない犯人の指示で、地下鉄を乗り継いで身代金を届けるシーンが見どころ。思わぬどんでん返しが待ち受けている。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATHER　AND　DAUGHTER", "岸辺のふたり", "", "", "http://ecx.images-amazon.com/images/I/4115JHNF15L._SL210_.jpg", 8, "2001年のアカデミー賞短編アニメーション賞を受賞した、オランダのアニメ作家M・D・D・ヴィットの監督作品。帰らぬ人となった父の面影を追い続ける娘に訪れた、奇跡の瞬間を見つめる。わずか８分の作品ながら、父と娘の強固な絆を描いた物語が深い感動を呼ぶ好編だ。幼い頃、ボートで旅立ち、そのまま戻らなかった父親を娘は岸辺で待ち続ける。月日が流れ、娘も老女となったある日、奇跡が起こる。", "2008-12-06", "3,70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ATOMIC CAFE´", "アトミック・カフェ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1940～50年代にアメリカで製作された核の宣伝フィルム(兵隊教育映画、当時のニュース、証言、大統領の演説から漫画、リトル・シーザーのヒット曲『アトミックラブ』まで)を集大成し“事実”のみで冷戦下の米国政府核戦略を解明、批判した問題作。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD HOT BALLROOM", "ステップ！ステップ！ステップ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "各国の映画祭で好評を得た、興奮と感動のドキュメンタリー。情操教育の一環で社交ダンスを導入したN.Y.の小学校を舞台に、人種も環境も異なる子供たちが、ダンスを通してさまざまなことを学び、成長していく姿を追う。大人顔負けの情熱にあふれた彼らを応援せずにはいられない。N.Y.の60もの小学校で行われている社交ダンスのレッスン。10週間のコースのあと開催されるコンテストで優勝をかけて、彼らは練習に励む。", "2006-03-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Morocco２ 横浜愚連隊物語", "Morocco２ 横浜愚連隊物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "太平洋戦争後の横浜を舞台にしたアクション活劇の後編にあたる第２作。前作でヤクザとの争いに終止符を打った愚連隊の“モロッコの辰”が仲間に祝福されて藤江と結婚するところから、物語は始まる。戦後の混乱も収束しはじめ、愚連隊が自由に暴れ回る時代も終わろうとしていた。そんな中、ヒロポンをめぐる抗争に巻きこまれた辰は刑務所入り。２年後、出所してみると、かつての愚連隊仲間はヤクザの舎弟となっていた……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CRIMSON PIRATE", "真紅の盗賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "18世紀のカリブ海を荒らしまわった“真紅の盗賊”と呼ばれた一団の頭目が、悪政を敷くスペイン国王軍をやっつける海洋アクション。「怪傑ダルド」に次ぐランカスター・プロの第２作で万人向けの娯楽作品。クリストファー・リーが端役で出演。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仰げば尊し", "仰げば尊し", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "森繁久彌が老教師を淡々とユーモラスに演じた和製“チップス先生”の趣の作品。瀬戸内の小学校教師・浜口丈太郎は、かつての教え子たちの消息を訪ねて上京する。だが、彼らの多くは日々の暮らしに追われる、世知辛い生活を送っていた……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAIDERS OF THE SUN", "未来戦士伝 メタルガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "近未来の荒廃した地球に現れた、一人の戦士を描いたハード・アクション。20XX年、地球は荒れ果て、ほとんどの生物は死に絶えていた。生き残った者の中で、血に飢えた一団が権力を持ち、残り100人足らずの人々は、支配権を取り戻すべく最後の抵抗を続けている。そこに一人の勇敢な戦士が誕生。彼はまだ自分の伝説を知らずにいた……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦 IKUSA", "戦 IKUSA", "", "", "http://ecx.images-amazon.com/images/I/51MhZJLNVEL._SL210_.jpg", 92, "「脱皮ワイフ」などで国内外から注目を集める本田隆一監督が、「ウルトラマンガイア」の高野八誠主演で放つ異色の任侠ドラマ。フリーターの御堂一沙が開発したアイデア商品が、100円ショップを中心に大ヒットする。ところが、その100円ショップは東北7国を支配する「七力会」の企業舎弟だった。やがて、一沙は七力会から呼びだされ……。共演には遠藤憲一、本宮泰風ら任侠映画には欠かせない俳優陣が名を連ねる。", "2005-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎恋愛塾", "男はつらいよ・寅次郎恋愛塾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "今回は寅次郎が恋愛指南役にまわって気の弱い青年を助けるパターン。五島列島へやって来た寅さんは老婆を助けたことから東京在住の孫娘・若菜と知り合う。若菜に思いを寄せる大学生を手助けせんとはり切る寅さんだが……。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナージャの村", "ナージャの村", "", "", "http://image.eiga.k-img.com/images/movie/38267/315x210.jpg?3558", 118, "1986年４月26日に起きたチェルノブイリ原発事故。ベラルーシ共和国は、放射能部質の汚染地帯となりドゥヂチ村からは300世帯の家族が移住した。が、故郷に執着する６家族は生活を続けている。ドキュメンタリーの写真家、本橋成一が、写真集『無限抱擁』を原案に撮り上げた作品。警察官が常駐し、許可証を持たない外部の人間は入ることもできないドゥヂチ村の少女ナージャを中心に、自分たちで収穫した作物を食べ普通に生活をする人々の姿を四季を通じて追っていく。ベラルーシ共和国と日本の合作映画。", "1997-11-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　ブラックノーズと魔法の歌", "それいけ！アンパンマン　ブラックノーズと魔法の歌", "", "", "http://image.eiga.k-img.com/images/movie/55467/315x210.jpg?3918", 50, "人気アニメ「それいけ！アンパンマン」の劇場版シリーズ第22作。闇の女王ブラックノーズに育てられたヒナ鳥の化身・カーナは、女王の命令でアンパンマンワールドへやってきて、人々から幸せを奪う“暗やみの笛”を吹く。しかし、アンパンマンたちとの出会いによって、カーナは友だちと楽しく歌うことを学んでいく。ヒロイン・カーナの声を中谷美紀が担当する。", "2010-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イケメンバンク THE MOVIE", "イケメンバンク THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/54037/315x210.jpg?3835", 83, "2008年にバンダイが発売した貯金箱「イケメンバンク」を原案にしたラブコメディ。売れない漫画家の金底梨太郎は、親友・ゼニー赤羽の路上ライブに現われる少女・百合に恋心を抱く。同じ頃、梨太郎の義理の兄である家内紙幣、百合の高校時代の同級生・財前波流乃、百合の弟的存在である一本気円も、それぞれ百合に想いを寄せており……。主演はイケメン俳優集団D-BOYSの柳浩太郎。「少林老女」の寺内康太郎が監督を務める。", "2009-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アウトレイジ", "アウトレイジ", "", "", "http://image.eiga.k-img.com/images/movie/55127/315x210.jpg?3985", 109, "「その男、凶暴につき」「ソナチネ」「HANA-BI」といった暴力映画で知られる北野武監督が原点回帰し、ヤクザ同士の熾烈な権力闘争を描いた第15作。関東最大の暴力団山王会の若頭・加藤は直参の池元組組長・池元に、池元と付き合いのある村瀬組を締めるよう苦言を呈する。そこで池元は配下の大友組組長・大友に、その役目を任せるが……。大友にビートたけし、加藤に三浦友和のほか、椎名桔平、加瀬亮、國村隼、石橋蓮司、小日向文世、北村総一朗ら豪華キャストが集結。", "2010-06-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fast & Furious", "ワイルド・スピード MAX", "", "", "http://image.eiga.k-img.com/images/movie/54557/315x210.jpg?3539", 107, "人気カー・アクション「ワイルド・スピード」シリーズ第4弾。オリジナルキャストのビン・ディーゼル、ポール・ウォーカー、ミシェル・ロドリゲス、ジョーダナ・ブリュースターが再集結。トレーラー強奪事件の犯人として指名手配されたドミニクは、仲間と共にロサンゼルスから南米へと拠点を移し、輸送車からの強奪を繰り返していた。そんな折、ドミニクのもとに衝撃的な知らせが入り、復讐を誓ったドミニクは危険を覚悟でアメリカへ戻る。", "2009-10-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In the Realms of the Unreal", "非現実の王国で　ヘンリー・ダーガーの謎", "", "", "http://image.eiga.k-img.com/images/movie/53427/315x210.jpg?3146", 82, "本人の死後に初めて作品が発見された孤高のアウトサイダー・アーティスト、ヘンリー・ダーガー。家族も友人も持たず孤独な生活を送っていた彼は、1万5千ページ以上もの小説と数百枚の挿絵からなる壮大な作品「非現実の王国で」を自宅のアパートで密かに完成させていた。アカデミー賞最優秀短編ドキュメンタリー賞を受賞した経験のあるジェシカ・ユー監督が、謎に包まれた彼の生涯と創作の秘密に迫るドキュメンタリー。", "2008-03-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOGETHER", "TOGETHER", "", "", "http://image.eiga.k-img.com/images/movie/54817/315x210.jpg?4374", 70, "交通事故で両親を亡くした姉妹・早紀と亜美は、悲しみを乗り越え明るく暮らしていた。両親を担当した医師の俊介は2人を陰ながら支え続け、いつしか早紀と愛し合うようになる。一方、亜美は親友の紹介で出会った拓哉と意気投合し、交際を始める。そんなある日、亜美の体に異変が起こり始める……。主演は加藤夏希、伊坂俊哉。", "2009-09-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Maleficent", "マレフィセント（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CELUI QUI DOIT MOURIR", "宿命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "“赤狩り”を逃れ、フランス亡命中のJ・ダッシンが「男の争い」のあとに撮った抵抗映画の傑作。４年間にわたるトルコ＝ギリシャ戦争が終わった1922年、トルコ軍統治下のギリシャ人村を舞台に、援助を求めてやって来た難民たちを受け入れ、協力してトルコ軍に立ち向かっていく様を描く。ダッシン監督らしい格調高いリアリズム描写に支えられており、ことに、キリスト受難劇を村人たちが演じ、その役割にまるで沿うかのように物語が進行していくという形式が目を引く。ギリシャ悲劇を想起させるような荘重ともいえるストーリーも印象的。なお、本作が縁でダッシンと女優のM・メルクーリは結ばれ、後の「掟」「日曜はダメよ」などで公私ともにコンビを組むことになる。", "1957-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rocky Balboa", "ロッキー・ザ・ファイナル", "", "", "http://image.eiga.k-img.com/images/movie/1877/315x210.jpg?2290", 103, "シルベスター・スタローンの出世作でもある「ロッキー」のシリーズ完結編となる第6作。アカデミー作品賞に輝いた第1作から30年を経て製作された本作では、スタローン自らが監督・脚本・主演の3役を務め、ロッキーの熱い魂をスクリーンによみがえらせた。ボクシング界を引退し、愛妻エイドリアンを亡くしたことへの喪失感を抱えるロッキーが、再びリングに上がり現役世界王者との戦いに挑む姿をストレートに描く。", "2007-04-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の外側", "風の外側", "", "", "http://image.eiga.k-img.com/images/movie/53157/315x210.jpg?1989", 123, "「少女　an adolecent」「るにん」「長い散歩」に続く奥田瑛二監督の第4作。山口県・下関を舞台に在日韓国人のチンピラと女子高生による恋愛と、2人の間にある見えない壁を描く。坂の上の名門女子高に通う真理子は通学途中のフェリーの中でチンピラのソンムンに出会う。その後、あることがきっかけで真理子がソンムンにボディガードを頼むなど2人の距離は縮まるが、ソンムンは自分の名前を言い出せずにいた……。主演はソンムンにモデル出身の佐々木崇雄、真理子に奥田監督の次女、安藤サクラ。", "2007-12-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRUST", "トラスト・ミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "アメリカン・インディーズ出身のH・ハートリーが、「シンプルメン」の前に手掛けた監督第２作。妊娠して家を追い出された16歳のマリアと、父の抑圧に煮え切らぬ日々を送り、手榴弾を持ち歩く青年マシュー。偶然出会った孤独な二人が信頼関係を築き上げていく様子を、斬新なタッチでユーモラスに描く。", "1991-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乙女物語 あぶないシックスティーン", "乙女物語 あぶないシックスティーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「童貞物語」の女の子版としてスタートした、ちょっぴりエッチな青春コメディの第１作。“ミス・コン”荒らしに青春を費やす女子高生の奮闘を描く。女子高生役は本作が映画デビューとなるタレント・島崎和歌子を中心に、藤木流花やかわいさとみなどのAV女優が脇を固め、花を添えている。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SONNY BOY", "ワイルド・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "モーテルで若い夫婦が殺される。犯人は残された赤ん坊を売り飛ばそうとするが、オカマの妻の願いで育てられることになった。そして拷問にも似た育てられ方によって、野獣のような体力と優しい心を持つ青年に成長する。彼は殺人兵器として利用されながらも、人間的感情に戸惑いを覚え始め……。異常な世界で育った少年の運命を描いた異色の犯罪アクション。オカマをD・キャラダインが怪演。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー×仮面ライダー×仮面ライダー THE MOVIE 超・電王トリロジー EPISODE RED ゼロのスタートウィンクル", "仮面ライダー×仮面ライダー×仮面ライダー THE MOVIE 超・電王トリロジー EPISODE RED ゼロのスタートウィンクル", "", "", "http://image.eiga.k-img.com/images/movie/55377/315x210.jpg?3559", 0, "「劇場版　超・仮面ライダー電王＆ディケイド　NEOジェネレーションズ　鬼ヶ島の戦艦」の続編第1弾。「EPISODE RED　ゼロのスタートウィンクル」「EPISODE BLUE派遣イマジンはNEWトラル」「EPISODE YELLOW お宝 DE エンド・パイレーツ」の3連作を、2週間ごとに連続上映する。新幹線型の時の列車“デンライナー”がコントロール不能となり、良太郎のもとにモモタロスから救助要請が届く。一方、侑斗の指示で、良太郎の姉・愛理には暴漢の魔の手が迫る。", "2010-05-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COUCH TRIP", "カウチ・トリップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "刑務所の精神病棟に病人のふりをして入院していた男が、病院を抜け出してニセの医者になりすまし、ラジオでカウンセリングを担当。珍妙なトークが受けて一躍人気者に……。M・リッチー監督による爆笑コメディ。D・エイクロイドのキャラクターが絶品だ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLORA", "フローラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "世界的に名高いアニメーション作家J・シュヴァンクマイエルの24秒という超短編。手・足・顔以外は野菜でできている人形がベッドに縛り付けられている。次第に野菜が腐り始め、ウジ虫が這い、人形の顔に哀しい表情が浮かぶ。DVDは「ヤン・シュヴァンクマイエル　短編集」に収録。", "1989-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEET ME IN ST.LOUIS", "若草の頃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "セントルイス生まれの女流作家S・ベンソンの小説をミュージカル化。20世紀初頭のセントルイスが舞台。スミス家の次女エスターは隣に越してきたジョンに恋してしまうが、父の転勤でニューヨークに移らねばならなくなる……。ハロウィンやクリスマスなどの季節感をこだわりの美術で表現し、美しい映像世界を演出した本作がV・ミネリ監督の出世作となった。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEVER SAY DIE", "ネバー・セイ・ダイ～地獄のデルタ・フォース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "極め付きの精鋭を集めて組織された特殊部隊“ブラッド・イーグル”の元隊員の壮絶な死闘を描いた、ハード・バイオレンス・ムービー。戦いを離れ、平穏な生活を送っていたブレイクだが、反モラル・カルト教団“エデンの園”から逃げ出して来たブルックという女を助けたことによって、思いがけない事態に巻き込まれていく。単身“エデンの園”に乗り込んだブレイクは、教祖が“ブラッド・イーグル”での仲間、ジェームズであることを知るのだった。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOKYO LOOP", "TOKYO LOOP", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "アニメーション映画誕生100年を記念して製作されたオムニバス・アニメーション。日本のアート・アニメーションの生みの親として知られる久里洋二をはじめ、「頭山」でアカデミー賞にノミネートされた山村浩二、実験映像作家の伊藤高志、本作でアニメーションに初挑戦する漫画家のしりあがり寿やしまおまほら、ベテランから新鋭まで16組の豪華アーティストが参加。「マインド・ゲーム」の山本精一が音楽を手がける。", "2006-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I cento passi", "ペッピーノの百歩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "社会派として知られるM・T・ジョルダーナ監督による、実在した若者の波乱に満ちた短い生涯を捉えたドラマ。1960～1970年代の反マフィア運動の実情を鋭く突いた傑作だ。マフィアのボスの世話になっている父を持つ息子は、父からボスのようになってほしいと期待されていた。しかし、今では反マフィア運動に熱中する青年に成長していた。そんなマフィアとの緊張が続く折、父が何者かに暗殺される。", "2000-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COYOTE UGLY", "コヨーテ・アグリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ニューヨークのクラブ・バーを舞台に、過激なパフォーマンスを繰り広げる若き女性バーテンダーたちの熱い青春を描く痛快作。21歳のヴァイオレットが、ソングライターを目指してニュージャージーの田舎町からニューヨークにやって来る。だが、現実は厳しく、生活費のためにクラブ・バー“コヨーテ・アグリー”で働くことに。そこの女性バーテンダーたちは、カウンターの上で踊ったり客をうまくあしらって挑発するのが仕事。あまりの過激さに戸惑うヴァイオレットだが、刺激的な雰囲気に魅了され、自分の中に眠っていた新たな魅力を開花させていく。ポップスの女王リアン・ライムスがヒロインの歌の吹き替えを担当、出演もしている。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無理心中　日本の夏", "無理心中　日本の夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "豊満な肉体で男を求めて止まないネジ子は、死にたがっているオトコを海岸へ誘った。そこでヤクザ風の男たちがライフル銃を掘り出すのを目撃、ネジ子とオトコは謎の廃屋に監禁されてしまう。そこは異様な雰囲気に包まれ、怪しげな男たちがいた……。現代の狂気が炸裂した暴力、セックス、そして奇妙な恋を通して、人々の心の奥底に潜む残酷な殺意とそれと同居する無関心さに、警告を発する大島渚監督の衝撃作。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラッグストア・ガール", "ドラッグストア・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "人気脚本家、宮藤官九郎が田中麗奈のために書き下ろしたオリジナル脚本を映画化。失恋の痛手から見知らぬ田舎町に到着したヒロインが、同地のドラッグストアの看板娘になり商店街のオヤジ店主たちに恋されてしまうというストーリーで、先の読めないメルヘン・コメディに仕上がっている。濃過ぎるベテラン共演陣を相手にしながらも、田中麗奈が喜劇魂を魅せる。薬科大３年生の恵子は、彼氏の浮気現場を目撃し大ショック。ぼう然としたまま見知らぬ町にやってきた彼女は、ドラッグストアでバイトを始める。一生懸命働く彼女に、商店街の中年男たちが一目惚れしてしまい……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DALI", "ダリ 天才日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "シュールレアリスム運動の一翼を担った偉大なる芸術家、サルバドール・ダリの幼年期からアメリカ亡命後までを描く。ロルカやエリュアール、さらにピカソ、ブニュエル、ブルトン、コクトー、ヘンリー・ミラーなど、ダリと実際に親交のあった周辺人物が実名で登場するほか、主演のR・クインのダリ以上にダリふうな演説シーンが楽しい。", "1990-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("照る日くもる日", "照る日くもる日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 182, "幕末の嵐吹き荒れる嘉永６年、倒幕派と幕府の暗殺団は、日夜抗争を繰り広げていた。倒幕派の急先鋒は白雲堂、暗殺団の首領は加納八郎。この両派の戦いの渦に、青年・細木年尾は巻き込まれていく……。嵐寛寿郎が、ほとんど鞍馬天狗を思わせる白雲堂を熱演。大河内伝次郎の加納との絡みが見どころ。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Deception", "彼が二度愛したS", "", "", "http://image.eiga.k-img.com/images/movie/53697/315x210.jpg?2041", 108, "プロデュースにも参加しているヒュー・ジャックマンとユアン・マクレガーがダブル主演。官能とスリルが交錯する恋愛サスペンス。監督は本作がデビュー作となるマーセル・ランゲネッガー。ニューヨークの孤独な会計士ジョナサンは、ある日弁護士ワイアットに出会い、上流階級専門の秘密クラブを紹介される。そこで美女たちと一夜限りの情事にはまっていくジョナサンだったが……。", "2008-11-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Willie and Phil", "ウィリーとフィル／危険な関係", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　フランソワ・トリュフォーの名作「突然炎のごとく　ジュールとジム」をリメイク。同じ女性を愛したふたりの友人たちの姿を綴るドラマ。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君が元気でやっていてくれると嬉しい", "君が元気でやっていてくれると嬉しい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "夏、ある島でプーという男が家を建てている。そこへ彼からの手紙を携えたラーという女が来て、手伝い始める。日々の辻褄合わせに忙しい社会の人間たちをよそに、昇っては沈む太陽の下で家を建てる二人。彼らに、世の中からはぐれた者特有の鬱屈はない。その屈託のなさが、人間何が幸せかを問いかける。完成が近づいた家は二人に、これからどうするのか、答えを要求するのだが……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デビルマン", "デビルマン", "", "", "http://image.eiga.k-img.com/images/movie/1357/315x210.jpg?2572", 116, "永井豪が72年に開始した名作コミック「デビルマン」を「ビー・バップ・ハイスクール」「ピンチランナー」の那須博之監督が映画化。デビルマン＝不動明とサタン＝飛鳥了はモデル出身で音楽ユニットFLAMEで活動する双生児、伊崎央登、伊崎右典が演じる。高校生、不動明はデーモンのひとりに身体に侵入されるが、精神は人間のままのデビルマンとなる。映画版は原作をアレンジ、デーモン軍団とデビルマンの最終戦争が描かれる。", "2004-10-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Vida Secreta De Las Palabras", "あなたになら言える秘密のこと", "", "", "http://image.eiga.k-img.com/images/movie/1427/315x210.jpg?3889", 114, "「死ぬまでにしたい10のこと」のイザベル・コイシェ監督が再び主演にサラ・ポーリーを迎え、1人の女性の苦悩と再生を描いた人間ドラマ。海の上に浮かぶ油田掘削所を舞台に、過去の辛い思い出により口を閉ざしているハンナと事故で負傷したジョセフとの交流と、それぞれの哀しい過去が語られる。共演にティム・ロビンス、ジュリー・クリスティ、レオノール・ワトリング。05年ゴヤ賞の作品賞・監督賞を受賞。", "2007-02-10", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("香港小教父／LITTLE GODFATHER FROM HONG KONG", "ドラゴン世界を征く 無敵のゴッドファーザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ヨーロッパでの大規模なロケーションを敢行した、香港アクション作品。映画スターが国際的麻薬犯罪に巻き込まれ、次々と送られてくる刺客と死闘を演じる、という物語。ブルース・リー映画からジャッキー・チェン映画への橋渡し的な位置にある。", "1972-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("路上の霊魂", "路上の霊魂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "近代日本における新劇運動の推進者・小山内薫が総指揮を務めた映画草創期の作品。シュミット・ボンの『街の子』とゴーリキーの『夜の宿』を牛原虚彦が自由に翻案した。人間の悩みを主題に、二つの対照的な例が並行して描かれる手法にはD・W・グリフィスの「イントレランス」の影響が見られる。", "1921-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・雪国", "新・雪国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "笹倉明の同名小説を「パイレーツによろしく」の後藤幸一監督が映画化。雪深い新潟県月岡温泉とその周辺を舞台に、財産も地位もなくして死に場所を求める中年男と、人なつこさの中にも暗い過去を忍ばせる若き芸者・萌子との、大人の純愛が描かれていく。さりげなく芸者文化の一端を観ることができるのも興味深い。原作者自身が作詞、加藤登紀子作曲の主題歌を坂本冬美が熱唱。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無常素描", "無常素描", "", "", "http://image.eiga.k-img.com/images/movie/56617/315x210.jpg?2655", 75, "2011年3月11日に発生した東日本大震災から約1カ月後の被災地の様子を見つめたドキュメンタリー。「ただいま　それぞれの居場所」で平成22年度文化庁映画賞「文化記録映画大賞」を受賞した大宮浩一監督が、尼崎の町医者とともに被災地に入ってカメラを回し、日付や地名、人の名も情報は一切付さず、未曽有の大地震と津波の爪跡を素描していく。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アメリカばんざい crazy as usual ", "アメリカばんざい crazy as usual ", "", "", "http://image.eiga.k-img.com/images/movie/53777/315x210.jpg?2597", 120, "アメリカ海兵隊ブートキャンプの若者たちと帰還兵の姿に焦点を当て、戦争を続ける国・アメリカの真実をあぶり出したドキュメンタリー。3カ月間の訓練によって若者たちを心身ともに強靭な兵士へと改造するブートキャンプの様子や、ベトナムやイラクの戦場から帰還した兵士たちの戦争体験と現在を追うと共に、多くの帰還兵がホームレスになっているという現実を通し、戦争が社会に及ぼす深刻な影響を浮き彫りにする。監督は「闇を掘る」の藤本幸久。", "2008-07-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("笑林小子／SHARON　POPEYE", "ビビアン・スーの恋しくて…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "日本でもバラエティ番組で活躍する台湾出身のアイドル、V・スーが、日本デビュー前に出演した青春ラブ・ストーリー。高校生のダアリチョイは校長の孫娘で学園のアイドル、アニーに思いを寄せていたが、彼女には教頭の息子チェサンという恋人がいる。あきらめきれない彼は友達に相談したり、悩み続けるばかり。そんなある日、チェサンの浮気現場を見つけたアニーは腹いせにダアリチョイをデートに誘う。有頂天の彼はアニーとのデートを楽しむが……。", "1993-01-01", "6,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Burlesque", "バーレスク", "", "", "http://image.eiga.k-img.com/images/movie/55277/315x210.jpg?2241", 100, "シェール＆クリスティーナ・アギレラ主演のミュージカル・ドラマ。ロサンゼルスにあるラウンジ「バーレスク」を経営するテス（シェール）は、かつて有名なダンサーだったが、今は引退し後進の指導に当たっていた。そこにアイオワの田舎町から出てきた若い女性アリ（アギレラ）がやってくる。ウェイトレスとしてラウンジで働いていたアリだったがステージで歌声を披露すると、テスに認められ、その才能を開花させていく。監督は俳優出身のスティーブン・アンティン。", "2010-12-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DETECTIVE", "刑事〈デカ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ある殺人事件の捜査を担当することになったリーランド刑事。容疑者に口を割らせ、殺人を自供させたまではよかったが、あとに真犯人が現れ……。家庭生活に悩みを抱え、捜査上での女と深い関係になり、そして仕事に失敗と、一人の刑事の姿をナレーションを多用した中に綴った異色の刑事ドラマ。主人公を演じるF・シナトラの好演が光る。原作者R・ソープは「ダイ・ハード」の原作でも知られる。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dark Side of the Sun", "リック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "B・ピットが初主演を果たした幻の作品。旧ユーゴスラビアで撮影され、編集中に内乱によってフィルムが散逸してしまったが、プロデューサー、A・アランジェロヴィッチの必死の捜索で完成した。日光に当たると死んでしまう難病に冒された少年リックは、革のスーツで体を覆い、孤独な生活を送っていた。治療法を探し、世界中を回っていたある日、彼はついに皮のスーツを脱ぐことを決意する。しかし、それは“３日間の命”を意味していた。死を覚悟した彼は、太陽の下、わずかな時間で生の喜びを体感するのだが……。", "1997-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死霊の罠", "死霊の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ある人物から送られてきた“惨殺ビデオ”の謎を追う女性TVレポーターと、その仲間たちに襲いかかる悪夢のような惨劇を描いた、血しぶきあふれる和製スプラッタ・ムービー。「天使のはらわた・赤い淫画」で認められた池田敏春監督が、本格的なホラー映画に挑む。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIRTY LOVE", "女は娼婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ダンサーを夢見る家出娘が、様々な出来事にぶつかり傷つきながらも、ダンスに情熱を傾ける姿を、エロティックなシーンを絡めながら描いている。主演のV・デミーは、ルイーズ・ブルックス風の容姿で注目されたセクシー女優。監督のJ・ダマトとのコンビ作品はこれが２作目。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Incredibles", "Mr.インクレディブル", "", "", "http://ecx.images-amazon.com/images/I/51xs2q-XLzL._SL210_.jpg", 115, "感動ロボット・アニメ「アイアン・ジャイアント」のブラッド・バード監督が「ファインディング・ニモ」のピクサー社と組んだアニメ。ヒーロー活動が禁止された世界で、元ヒーロー、Mr.インクレディブルだったボブは欲求不満。そんな彼が何者かに誘拐されて、やはり元ヒーローだった妻とヒーロー能力を持つ子供たちが救援に向かう。屈折した悪役シンドロームの声を、ケビン・スミス映画の常連俳優ジェイソン・リーが演じている。", "2004-12-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SILENCERS", "サイレンサー／沈黙部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "D・ハミルトンの人気スパイ小説“マット・ヘルム”シリーズから『沈黙の部隊』を映画化。アメリカ空軍のミサイル実験を利用し、合衆国南西部に死の灰をふらせようとする敵、タン・ツェとの対決はやがて意外な結末を迎える。サイレンサー部隊シリーズの第１作でアクション・お色気・ミステリーと３拍子そろった傑作スパイ・アクション。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きみに届く声", "きみに届く声", "", "", "http://image.eiga.k-img.com/images/movie/53987/315x210.jpg?3163", 96, "人気ダンスユニット「EXILE」のメンバー、眞木大輔が離島の診療所で働く医師を演じるヒューマンドラマ。共演に西岡徳馬、杉本哲太、戸田菜穂ら。東京で自信をなくした医師・北岡は、再出発のため離島の診療所に赴任する。慣れない診療所でも、島民や同僚から徐々に信頼を勝ち取っていく北岡。そんなある日、北岡は浜辺でイルカを眺める少女に出会うが、彼女は重い心臓病を患っていた。", "2008-09-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HRA O JABLKO", "リンゴゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "激動のチェコで製作を続ける女流監督V・ヒティロバが、1976年に撮り上げたシニカルなラブ・コメディ。浮気な産婦人科医と看護婦の恋愛模様を通して、男と女の“リンゴゲーム”の顛末を淡々と綴っていく。冒頭に映し出される様々な種類のリンゴや、リアルな出産シーンなど、女性ならではの感性がいっぱい。主人公の医師を演じているのが、チェコ映画界の名匠イジー・メンツェルだというのも面白い。", "1976-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Doctor, The Tornado & The Kentucky Kid", "Turn8 ラグナセカの青い空", "", "", "http://image.eiga.k-img.com/images/movie/33647/315x210.jpg?1839", 97, "世界最高峰のバイクレースMotoGPに焦点を当てたドキュメンタリー。2005年にアメリカ西海岸のマツダ・ラグナセカ・レースウェイで開催されたレースを舞台に、バレンティーノ・ロッシをはじめとした4人のトップレーサーに密着し、激しい争いの裏側を捉える。監督は、同じくMotoGPを題材にしたドキュメンタリー「Faster」を手掛けたマーク・ニール。俳優のユアン・マクレガーがナレーションを担当している。", "2006-09-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スケバン刑事 風間三姉妹の逆襲", "スケバン刑事 風間三姉妹の逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "同名TVシリーズ第３部より、オリジナルストーリーで映画化。三代目スケバン刑事・風間唯と青少年治安局との闘い。他愛のない物語ではあるが、すっきりとした脚本とテンポのいい演出、そして浅香唯の魅力で気持ちよく見せる。浅香唯の映画初主演作。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もんしぇん", "もんしぇん", "", "", "http://image.eiga.k-img.com/images/movie/33607/315x210.jpg?4700", 94, "天草の地を舞台に、ある女性が出会った不思議な出来事を描くファンタジー・ドラマ。妊娠中の主人公はるは、何かに導かれるようにしてある小さな島にたどり着く。そこには老人たちと、ちいという若い女性が暮らしていた。同じ頃、作一という老人が島にやって来る。彼は、あるショックが原因で年をとることを止めてしまったちいの元夫だった……。「千と千尋の神隠し」でリンの声を担当した玉井夕海が主演・脚本・音楽の3役を務める。", "2006-08-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("heat after dark〈ヒート・アフター・ダーク〉", "heat after dark〈ヒート・アフター・ダーク〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "俳優・渡部篤郎のプロデュースによる異色アクション。拳銃密売人の死体を処分するため、山の中へと潜伏した二人の青年。ところが、この密売人が、実は生きており、組織の手下を呼び寄せて逆襲に転じたことから、二人組の必死のサバイバルが始まる。露骨なバイオレンスを押さえた静かなタッチが、逆に息詰まるような緊張感を醸し出している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　MIGHTY　WIND", "みんなのうた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "カルト・ムービーの「スパイナルタップ」で脚本を手掛けたC・ゲストの擬似ドキュメンタリー風のコメディ。架空のフォーク・グループが30年ぶりに音楽活動を再開するまでの過程が、コミカルなタッチで綴られる。作り物のインタビューや過去のフィルムを織り交ぜ、本物の出来事のように見せる演出は圧巻。1960年代にフォーク・ソングの大ブームを巻き起こした伝説の大物マネージャーがこの世を去った。彼の息子ジョナサンは、父の追悼記念のコンサートの開催を企画。父にゆかりのあったフォーク・グループに話を持ちかけるが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暖春　Warm Spring", "幸せの絆", "", "", "http://image.eiga.k-img.com/images/movie/34077/315x210.jpg?1410", 89, "孤児の少女と彼女を引き取った老人の心の交流を描き、中国全土で感動の嵐を巻き起こした人間ドラマ。中国の山間にある農村に、里親の虐待から逃れてきた7歳の少女・シャオファが行き倒れていた。そこを通りかかった1人の老人が、彼女を助け、そのまま引き取ることに。しかし、老人の息子夫婦はシャオファを追い出そうとする。メガホンを取ったのは、本作が長編デビュー作となる内モンゴル出身の女性監督ウーラン・ターナ。", "2007-07-21", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEENEY TODD", "スウィーニー・トッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "過去に二度映画化され、秀作ミュージカルも生み出したイギリスの伝説の殺人鬼スウィーニー・トッド。彼の狂気の物語を「真夜中のカーボーイ」「マラソンマン」などの名匠J・シュレシンジャーが映画化した異色スリラーだ。18世紀末のロンドン。理髪店の主人スウィーニー・トッドは、温厚な人柄で町でも評判の男。だが、彼にはもう一つの顔があった。裕福そうな客に目を付け、剃刀で喉を切り裂いて殺害し、その肉を愛人の店でミートパイにして売っていたのだ。失踪した宝石商を探しにやって来たアメリカ人のベンは、独自の捜査の結果、彼らに不審を抱くのだが……。殺人を犯すまでのスリルと、退廃と活気に満ちた町の雰囲気が異様なムードを醸し出し、物語の残虐性がブラックなユーモアで味付けされている。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海賊戦隊ゴーカイジャーVS宇宙刑事ギャバン THE MOVIE", "海賊戦隊ゴーカイジャーVS宇宙刑事ギャバン THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/57417/315x210.jpg?1522", 63, "スーパー戦隊シリーズ35作記念作品の「海賊戦隊ゴーカイジャー」の劇場版で、最新作とシリーズ前作のスーパー戦隊がタッグを組む「VS」シリーズでは初となる、スーパー戦隊と特撮ヒーローがコラボレーションした作品。1982年に放映され高視聴率を記録した「宇宙刑事ギャバン」が30年ぶりに復活し、宇宙を舞台に2大ヒーローが戦いを繰り広げる姿を描く。ある日、ゴーカイジャーの前に宇宙最強の刑事と呼ばれるギャバンが出現。海賊行為の汚名を着せてゴーカイジャーを逮捕し、宇宙警察に引き渡してしまう。しかし、その行動の裏には、宇宙警察のトップが悪に乗っ取られているのではと疑うギャバンが、その疑念を晴らすために考えたある作戦があった。", "2012-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DREAM LOVER", "水曜日に抱かれる女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "リーナは優しく、賢く、ファッショナブルでセクシーな美女。若手エリート建築家レイとの結婚も順調で、完璧なまでに幸福に包まれた人生を送っていた。しかし、あることからリーナに疑問を持ったレイは彼女のことを調べ、事実を知ってしまう……。現代社会に潜む男と女の落とし穴を変化に富んだストーリー展開で描写するサスペンス・スリラー。リーナにほんろうされるレイをJ・スペイダーが都会的な演技で魅力的に演じている。「氷の微笑」「蜘蛛女」を彷彿させる冷酷な悪女もの。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPPINぶるうす ザ・ムービー", "SUPPINぶるうす ザ・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "テレビ、映画、舞台と幅広い分野で活躍中の個性派俳優、今井雅之が初めてメガホンを執った痛快コメディ。ひったくり犯とゲイとニューハーフの銀行強盗３人組と、一癖もニ癖もある人質たち。さらには銀行員や刑事までをも巻き込んで、数々の奇想天外なストーリーが展開。今井監督は、そうしたドタバタ喜劇調の設定を利用しながら、男同士の友情や禁断の恋、そして真実の人間愛を、感動的に描いていく。監督兼主演を務めた今井雅之をはじめ、ニューハーフ役に挑戦した新山千春、主人公の相棒役に扮する岡安泰樹など、多彩な顔ぶれの出演陣にも注目だ。セコいひったくりで生計を立てている、幼なじみの鉄男と文佐久。二人は“性転換手術をしてストリッパーになりたい”と願う仲間、お瑞の夢を叶えるため借金を抱えこみ、田舎町にある銀行の強盗を計画するハメに……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER SUSPICION", "疑惑に抱かれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "殺人事件に巻き込まれ、犯人の疑いをかけられた私立探偵と、事件の鍵を握る謎の美女を、演技派俳優二人が演じるエロティック・サスペンス。元警察官のトニーは、今は離婚専門のしがない探偵に落ちぶれていた。妻の協力により偽の浮気現場をでっち上げるのが、彼のやり方だったが、ある時いつものように現場に向かいフラッシュをたいた瞬間、彼の見た光景は、妻と依頼人の射殺死体だった。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Disney's a Christmas Carol", "Disney's クリスマス・キャロル", "", "", "http://image.eiga.k-img.com/images/movie/54447/315x210.jpg?4397", 97, "町の嫌われ者スクルージは、金が全てと考える天涯孤独の男。あるクリスマス・イブの夜、かつてのビジネス・パートナーの亡霊が現れ、その翌日から「過去の亡霊」「現在の亡霊」「未来の亡霊」が一夜ずつスクルージを時間の旅へと連れ出す。ジム・キャリー、ゲイリー・オールドマン、コリン・ファースらが声優＆パフォーマンス・キャプチャーを担当。監督はロバート・ゼメキス。", "2009-11-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SEVENTH VOYAGE OF SINBAD", "シンドバッド七回目の航海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ファンタスティックな特撮映画の第一人者、ハリーハウゼンらが考案した、アニメーションと実写の合成による画期的表現手段“ダイナメーション”方式を使った作品。内容は『アラビアンナイト』の中のシンドバッドの冒険物語。シンドバッドは航海中、数々の事件に出会う。そのなかでも必見の一つは、主人公が一体の骸骨と剣を交えて戦う場面。人間の動きと同時に、生命を吹き込まれたごときのリアルな骸骨の描写は、後々まで語り草になったほど。監督は、フォードの名作「わが谷は緑なりき」の美術でアカデミー賞を獲得したジュラン。彼の監督作品中、最高作の一つと称賛されている。ビデオタイトルは「シンドバッド７回目の冒険」。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("哭泣的女人／CYRWOMAN", "涙女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "“哭〈な〉き女”とは、葬儀で故人のために涙を流す職業のこと。ひょんなことからこの職に就いた女性の姿を少々ブラックな笑いの中に描く。お金のためと割り切って、歌い踊り、大げさに泣いてみせる主人公。そのしたたかでたくましい生き方は痛快だが、やがてそこに孤独や悲しみが浮き彫りにされて切ない味も。そんな複雑な女心を主演のリャオ・チンが熱演している。夫の借金返済に追われるグイ。ある日、彼女の夫がケンカ相手を失明させて逮捕される。被害者から治療費を迫られたグイは、泣きまねをして難を逃れることに成功。その才能を買われて、グイは“哭き女”として働き始める。", "2002-01-01", "10,2,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("森繁のデマカセ紳士", "森繁のデマカセ紳士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "稀代のペテン師・堀川新兵衛は元ストリッパー・浪江とひともうけを企らみ、彼女を女子プロレスラーに仕立て上げ、まんまと10万円を手にする。さらに、堀川は彼女を天才舞踏家として売り込むことに成功。各劇場から出演依頼が殺到するのだが……。「我が名はペテン師」の主要スタッフ、キャストで作られた喜劇。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奴が殺人者だ", "奴が殺人者だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "隅田川に死体が浮かんだ。大利根刑事は、被害者が麻薬密売人の崔と親しかったことを知り、ほかにユーという仲間の名前が浮かびあがる。ユーを探すため、大利根の妹の婚約者・石原は、密売組織にもぐり込むが……。麻薬常習者の殺し屋・ヤッパの竜を演じた天本英世は、この作品がキッカケで、以降東宝娯楽映画の常連となる。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CIRCUS", "サーカス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "「黄金狂時代」と「街の灯」という二大傑作の間で忘れられがちな作品だが、面白さは保証付き。サーカスに紛れ込んだおなじみの浮浪者チャーリーが、曲馬乗りの少女に恋をして、綱渡りをするハメに陥る。チャップリンの綱渡りの至芸。そしてラストの詩情。", "1928-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEEPER", "追跡　ザ・スイーパー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "法で裁けない悪を秘かに倒す、闇の処刑人の戦いを描いたハードアクション。ロサンゼルス警察のマーク・ゴッダードは、幼い頃に家族が皆殺しにされて以来、犯罪者に対して異常な憎悪を持つ男。既に９人の犯罪者を射殺し、内部告発をも受けていた。ある日、法に縛られず悪を始末できる暗殺集団J・Iから誘いを受けたマークは、失った家族への復讐を果たすために仲間に加わり、新しい任務に手を染めていく。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("駅 STATION", "駅 STATION", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "人と人とのめぐり会いを、駅に停車する列車にたとえ、オリンピックのピストル競技選手でもある刑事と３人の女の宿命的別離を描いた、３部からなる作品。脚本家の倉本聰が、「冬の華」に続いてあえて高倉健のために書き下ろしたもので、彼の感情を秘めた“間”の表情が生かされる。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BOUM", "ラ・ブーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "13歳の少女ビックは、ブーム（＝パーティ）で知り合った少年に恋心を抱く。一方結婚15年目にして離婚の危機を迎えた両親もそれぞれ浮気を経験して……。一人一人の恋愛騒動をコミカルに描いたヒット作。S・マルソーのデビュー作である。", "1980-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　ENFANTS　DU　MARAIS", "クリクリのいた夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1930年代フランス。世捨て人のように沼地に暮らす人々の豊かな生活を、一人の少女の瞳を通して回想する人間ドラマ。監督のJ・ベッケルは、牧歌的なひと夏のドラマを美しい映像と細やかなカットで感動的に綴り、父ジャック・ベッケル譲りの詩的リアリズムの世界を展開した。フランスでは、200万人を動員する大ヒットを記録している。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOLKS！", "oh！大迷惑!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "T・セレックがベテラン俳優のD・アメチーと親子を演じる、災難巻き込まれ型コメディ。老人性痴呆症で次々とトラブルを起こす父親を、D・アメチーが好演。シカゴ商品取引所で働くジョンは豪華アパートに住み、愛する家族に囲まれ幸せな毎日を送っていた。ところが、８年も会わずにいた両親を訪ねたところ、父親はボケ始め自分の家を焼いてしまっていた。おまけに仕事でも、あらぬ疑いをかけられ失業、FBIにまで追われるハメになる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さくらな人たち", "さくらな人たち", "", "", "http://ecx.images-amazon.com/images/I/514-8A-e%2BwL._SL210_.jpg", 0, "人気俳優のオダギリジョーが、小学校時代の同級生でもあるお笑いコンビ「次長課長」の河本準一を主演に迎えた長編初監督作。入院中の祖父を見舞う剛史は、祖父のもとに毎日のように届く差出人不明の桜の絵はがきが気になって仕方がない。ある日、ひょんなことからその桜がある場所を知っているというタクシー運転手ジャックと出会った剛史は、ジャックと一緒にその場所へ向かうことにするが……。共演に個性派俳優の河原さぶ。", "2009-04-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dogora Ouvrons les Yeux", "パトリス・ルコントのDOGORA", "", "", "http://image.eiga.k-img.com/images/movie/33617/315x210.jpg?4617", 75, "「髪結いの亭主」「親密すぎるうちあけ話」等の作品で知られるフランスの巨匠パトリス・ルコントによる、映像と音楽のみで構成された異色の感動作。フランスの音楽家エティエンヌ・ペルションの曲“DOGORA”に心を揺さぶられたルコント監督。実弟が暮らすカンボジアを訪れ、この国が持つ圧倒的なパワーに感銘を受けた彼は、長い構想期間を経て2つの衝動を融合させた。映像と音楽のアンサンブルがかつてない感動を呼び起こす。", "2006-08-26", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラストゲーム　最後の早慶戦", "ラストゲーム　最後の早慶戦", "", "", "http://image.eiga.k-img.com/images/movie/53567/315x210.jpg?2920", 96, "太平洋戦争真っ只中の1943年。戦局の悪化に伴って6大学野球は廃止され、学生に対する徴兵猶予も停止となった。そんな中、出陣直前まで野球を続けることを誓った早稲田大学野球部顧問の飛田の元に、慶応義塾塾長の小泉から\"早慶戦\"の申し出が舞い込み……。現代にまで語り継がれる感動の実話を、「大河の一滴」の神山征二郎監督のメガホンで映画化。渡辺大、柄本佑ら注目の若手俳優が、野球に希望を見出す学生たちを瑞々しく演じる。", "2008-08-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("力道山・男の魂", "力道山・男の魂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "日本プロレス界のスーパースター・力道山主演による明朗アクション映画。力道山扮する熱血漢が子供たちを助けて悪人と闘い、得意の空手チョップで悪人をやっつける。プロレスラー出身の映画俳優M・マゾルキが共演し、力道山との迫力あるファイト・シーンを見せる。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("広場／THE SQUARE", "広場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "天安門事件から５年後の1994年春から夏にかけて、中国のシンボルである天安門広場の光景を収めたドキュメンタリー。ナレーションや音楽を排し、広場の治安を守る警察、記念撮影に興じる観光客、先生に引率されて見学にやって来た小学生といった平穏な日常のスケッチによって、現代を生きる中国人の微妙な心情が浮き彫りにされる。中国国内で上映禁止となった問題作「北京バスターズ」のチャン・ユァンと、ドキュメンタリー畑で活動するドゥアン・ジンチョアンの若手コンビが共同で監督した。中国国内では公的には認められていない作品だ。", "1994-01-01", "8,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN EXTREMIS", "ゲッタウェイ 狼たちの逃走", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "銀行強盗の青年とその恋人、さらには犯人と誤認された悪党の奇妙な連帯感と三角関係を描くフィルム・ノワール。主演の３人を追う刑事役に、トリュフォー作品のセット・デザイナーとして知られるJ・P・コユ・スベルコが扮している。O・ロルサック監督の長編劇映画デビュー作。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神の左手　悪魔の右手", "神の左手　悪魔の右手", "", "", "http://image.eiga.k-img.com/images/movie/33367/315x210.jpg?2030", 95, "ホラー漫画家、楳図かずおの伝説的コミックを「デスノート」の金子修介監督が実写映画化。人間の悪意を夢で予知できるという不思議な能力を持つ少年ソウ。ある晩、悪夢にうなされていた彼の首から、大量の血が噴き出した。夢の中に現われたのは、病気の娘のために絵本を描く優しい父親の姿。しかしその内容は、少女たちが次々と惨殺されていく恐ろしいものだった。弟思いの姉イズミは、ソウの願いを聞き、とある街へと向かうが……。", "2006-07-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛇姫道中", "蛇姫道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "下野国鳥山の城主大久保忠成は迷信を信じていた。娘の琴姫は絶世の美女で、岡崎の本多家は琴姫をぜひ嫁にと考え、策略を仕掛ける。しかしこの企てを知った岡部内膳は、人気女形の瀬川菊之助を誘拐し、彼を琴姫に変装させて岡崎のもとへと送った。大スターに囲まれて助演の柳家金語楼と横山エンタツが笑いを誘う、ドキドキのコメディ時代劇。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA REINE MARGOT", "王妃マルゴ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "文豪A・デュマの同名小説をもとに、激動の16世紀フランス・ルーブル宮で愛に燃えた王妃マルゴと、彼女が愛した男たちの運命を描いた豪華絢爛たる歴史ドラマ。I・アジャーニがヒロイン、マルゴを演じている。宗教革命の波に揺れていたフランスは、カトリック（国王派）とプロテスタントが相争い、反目しあっていた。そんな中、母妃カトリーヌは、マルゴを政略結婚の犠牲にする。しかしそれは、宗教統一という和解の名目に隠れた、大虐殺事件を起こすための策略だった。結婚初夜、マルゴは王宮を抜け自由な町に走り出る。そこでプロテスタントの貴族青年ラ・モールに出会い、激しい恋に落ちていく……。「リバー・ランズ・スルー・イット」でアカデミー撮影賞を受賞したP・ルスロが、陰影の魔術により16世紀を見事に再現している。カトリーヌを演じたV・リージがカンヌ映画祭主演女優賞を獲得。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dr.No", "007／ドクター・ノオ", "", "", "http://ecx.images-amazon.com/images/I/51o6ZTaIPCL._SL210_.jpg", 110, "「007」シリーズの記念すべき第1作。フロリダから発射されたロケットの軌道が、何者かの怪電波によって狂わされる。真相を究明すべくボンドはジャマイカに飛び、そこでノオ博士の秘密要塞に潜入する……。エキゾチックな南洋の島を舞台にコネリー＝ボンドが誕生した。1963年初公開時のタイトルは「007は殺しの番号」。", "1963-06-30", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NIGHTMARE BEFORE CHRISTMAS", "ナイトメアー・ビフォア・クリスマス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "「ビートルジュース」「バットマン」など、鋭い感性でファンタスティックな世界を生み出してきたT・バートンが製作・原案・キャラクター設定を担当し、全編を“ファンタメーション”と呼ばれる人形アニメで描いた驚異的な映像が話題の一作。年に一度のお祭り、ハロウィンで人を怖がらせることだけが生きがいのハロウィンタウンの住人たち。その一人ジャックは、ハロウィンの騒ぎにウンザリしていた。彼は墓場をさまよううち、別世界のクリスマスタウンに迷い込む。白い雪と笑い声に包まれたその街に魅了されたジャックは、自分でもクリスマスを主催しようと決心するが……。ミュージカル風の構成が楽しい異色ファンタジー。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ISLAND OF Dr.MOREAU", "ドクター・モローの島", "", "", "http://ecx.images-amazon.com/images/I/51FEc29g%2BvL._SL210_.jpg", 99, "H・G・ウェルズの同名小説の映画化。太平洋を漂流した若者がたどり着いた熱帯の孤島。その島にはドクター・モローと名乗る不気味な科学者がいた。ランカスター、ヨークらの個性派俳優の熱演が見事。1932年に製作された「獣人島」のリメイク。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うる星やつら オンリー・ユー", "うる星やつら オンリー・ユー", "", "", "http://ecx.images-amazon.com/images/I/510TWC3NSHL._SL210_.jpg", 110, "超ナンパ高校生・諸星あたると、宇宙人少女・ラムを中心に巻き起こるドタバタ騒動を描いた人気まんが『うる星やつら』。そのTVアニメ化に続く、劇場版の第１作。物語は幼い時の約束をタテに、エル星の美少女・エルが、あたるとの結婚を宣言。あたるを愛するラムは怒り、ついにラムの星とエル星の、戦争寸前にまで事態は発展する。しかも女好きのあたるがエルになびいたため、ラムは二人の結婚式へと乗り込んでいくが……。あたるとラムのラブ・ストーリーを基調に、この作品が初監督の押井守は、やや悪ノリ気味にギャグを連発。ドラマの所々でイメージが暴発し、収拾のつかなくなる寸前で、かろうじて物語を収めている。このオリジナル・ストーリー路線は以後第４作まで続くことになるが、あらかじめ観客をファンに限定して作られたため、マニアックな傾向も同時に強まっていった。良くも悪しくも、日本のスラップスティック・アニメの、一端を示す作品といえようか。DVDタイトルは「うる星やつら 劇場版 オンリー・ユー（ノーカット版）」。", "1983-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dogtooth", "ドッグトゥース（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("頭文字〈イニシャル〉D／Third Stage", "頭文字〈イニシャル〉D／Third Stage", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "車と峠での走りをこよなく愛する高校三年生の拓海は、自分の将来の道を決めかねながら周囲の走り屋チームとの街道バトルを繰り返していくうち“何か”を発見していく。TVアニメでも人気を博した大ヒット・コミックを原作にした劇場用アニメ。TV版同様、3DCGによるカー・バトルとユーロ・ビートとの融合が面白い。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("半身反義", "半身反義", "", "", "http://image.eiga.k-img.com/images/movie/53637/315x210.jpg?2677", 90, "「東京オリンピック」「日本万国博」など、昭和を象徴する出来事の記録映画に携わってきた演出家・山岸達児。2003年に脳梗塞で倒れ半身不随となった彼に、気鋭の女性映像作家・竹藤佳世がカメラを向けたドキュメンタリー。老いや病という現実に直面しながらも心の自由を保とうとする山岸との対話、さらに関係者による証言やイメージ映像を織り交ぜながら、昭和という時代を駆け抜けたひとりの演出家の過去と現在に迫る。", "2008-07-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BELLE E´QUIPE", "我等の仲間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ペンキ屋のジャン、大工のシャルル、屋根の修理屋のレイモン、スペインの政治犯マリオの４人はそろって失業中である。ある日仲間で買った宝くじが当たり、10万フランが転がり込んだ。そこへ行商に失敗したジャックがやって来る。５人はパリ郊外の壊れかかった家を手直しして一緒に仕事を始めることにする。家の名も“我等の家”。ジャックはマリオの恋人ユーゲットに恋し、二人はカナダへ旅立つが、マリオは警察に追われ、レイモンも屋根から落ちて死ぬ……。夢見る男たちの善意の努力が破綻をきたす物語だが、結末は悲劇版とハッピーエンド版の２種類がある。ロマンスの正式デビュー作。", "1936-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌15／ハマちゃんに明日はない!?", "釣りバカ日誌15／ハマちゃんに明日はない!?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "“スペシャル版”を含めてシリーズ17作目となる本作は、風情あふれる東北・秋田を舞台に、名コンビがまたしても大騒動を繰り広げる。長引く不況の煽りを受け、大規模な人事制度改革に踏み出すことになった鈴木建設。しかし、そんな会社の危機も、そして自身に迫るリストラの影も露知らず、ハマちゃんは秋田への釣り旅行へ出かけるのだが……。“黄金時代の松竹映画の再現”に挑戦した朝原雄三監督は、名匠・小津安二郎の「麦秋」のほか、随所に松竹の名作映画へのオマージュを散りばめ、情緒深い雰囲気を漂わせている。長引く不況の影響で鈴木建設も、ついに人事制度改革に着手することに。経営コンサルタント会社の合田と薫の厳しい説明にビクつく社長のスーさんだったが、その頃、ハマちゃんはリフレッシュ休暇を取り、秋田への釣り旅行に出かけていた。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dazed and Confused", "バッド・チューニング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1970年代のテキサスを舞台に、10代の若者たちのセックス、ドラッグ、ロックンロールな青春の日々を描き、公開時アメリカで大ヒットを記録したコメディ。「恋人までの距離（ディスタンス）」のリチャード・リンクレイター監督の初期作品で、出演者にはミラ・ジョボビッチ、ベン・アフレック、マシュー・マコノヒー、そしてデビューしたばかりのレニー・ゼルウィガー等、今となってはそうそうたる顔ぶれが揃っている。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一江春水向東流／SPRING RIVER FLOWS EAST", "春の河、東へ流る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 190, "1931年の満州事変から1945年の日本敗戦に至るまでの15年間に及ぶ日中戦争期を背景に、上海の紡績工場の女工・素芬、夜間学校教師・忠良との出会い、結婚、そして別れを描いた大作。実写フィルムを効果的に活用し、坑日に生きる当時の中国人の心情をリアルに描写している。", "1947-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌行燈", "歌行燈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "泉鏡花の名作を衣笠貞之助監督が映画化。芸一筋に生きる喜太八は、田舎で権力をふるう謡曲指南宗山の芸を侮辱し、自殺に追い詰める。罪の意識にかられた喜多八は謡曲界を去り、芸妓に身をおとした宗山の娘・お袖に仕舞を教え、窮地を救う……。泉鏡花の幻想的な世界を衣笠監督ならではの様式美で見事に映像化。主演二人のこの世ならぬ美しさも絶品だ。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Piranha 3D", "ピラニア3D", "", "", "http://image.eiga.k-img.com/images/movie/53907/315x210.jpg?2048", 90, "1978年に大ヒットを記録した「ピラニア」を3D映画としてリメイクしたパニック・スリラー。毎年スプリング・ブレイクには、町外から45000人以上の学生がやってくるビクトリア湖。例年は、酔っ払いの学生とそれに対する地元の老人からの苦情に悩まされていた町役場だったが、今年は湖に突如として発生した外来種の人食い魚の対応に迫られることになる……。出演に「ゴシップガール」のジェシカ・ゾア、ビング・レイムス、クリストファー・ロイド、そして「ジョーズ」のリチャード・ドレイファス。監督は「ヒルズ・ハブ・アイズ」「ミラーズ」のアレクサンドル・アジャ。", "2011-08-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENFANTS DE SALAUD", "ならず者の子どもたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "父親の裁判をきっかけに出会った４人の異母兄弟が、家族として結束していく姿をシニカルに描いたコメディ。祖母に育てられた男好きのシルビット44歳、歳の離れた夫との愛は冷めながらも裕福に暮らすソフィー41歳、離婚経験から女性不信の自動車修理工サンドロ39歳、母親がアメリカ人で女優を目指す男性恐怖症のスーザン28歳。父親を知らずに育った４人はある日、65歳になる殺人罪の男の裁判に召喚され、その男が父親で、異母兄弟であることを知る。４人は家族の絆を深めていこうとするが、そのためには様々な決断を強いられるのだった。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STARSHIP　TROOPERS", "スターシップ・トゥルーパーズ", "", "", "http://ecx.images-amazon.com/images/I/41iXQORqEdL._SL210_.jpg", 128, "ハインラインのヒューゴ賞受賞作『宇宙の戦士』を映画化したSF大作。急速に勢力を拡大した巨大昆虫型異星物バグズと人類が、宇宙戦を繰り広げている未来。ハイスクール卒業を目前に控えたジョニーは裕福な両親への反発と、宇宙海軍アカデミーに進む憧れの女の子カルメンのために、自らも地球連邦軍に入隊する。過酷な訓練によって優秀な兵士となった彼だが、訓練中の事故で挫折感を味わい軍を辞める決意をする。その矢先、故郷の壊滅を知り、バグズと戦うために戦場に赴くのだった……。脳天気な青春群像を中核にすえ、戦意高揚映画ばりのスタイルで物語は展開。シニカルきわまりない構成で、高品位SFX技術をひたすら残酷描写に奉仕させるヴァーホーヴェンの演出は、単なる反戦メッセージを凌ぎ、得体の知れないふてぶてしさに満ちている。", "1998-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRESUMED GUILTY", "ジャッジメント 推定有罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "権力機構のずさんな実態に、父子の絆を絡めて描いた社会派の法廷サスペンス。アリバイがありながら、強盗の共犯者として不当に逮捕されてしまった若者ボビー。目撃者の証言をねじ曲げた警察の暴走は、彼を有罪へと追い込んだ。ボビーの父ハロルドは我が子を救うべく、孤立無援の闘いを挑んでいく。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOGS IN SPACE", "ドッグ・イン・スペース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "サムは、仲間たちとコミューンを形成し、勝手気ままな生活を手に入れたロッカー。1978年メルボルンを舞台に、パンクや、青ざめた顔色、失業などがファッションだった若者たちの姿を描く。故M・ハッチェンス、イギー・ポップ、ブライアン・イーノなどの音楽が流れる。", "1986-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Shipping News", "シッピング・ニュース", "", "", "http://image.eiga.k-img.com/images/movie/1647/315x210.jpg?1645", 112, "E・アニー・プルーの小説「港湾ニュース」（集英社）を「ショコラ」「サイダーハウス・ルール」のラッセ・ハルストレム監督が映画化。オスカー2冠のケビン・スペイシーを主役に、彼と心を通わす女性役にノミネート歴2回のジュリアン・ムーア、彼の叔母役にオスカー女優、ジュディ・デンチと主演陣はオスカー常連ぞろい。共演陣も、クオイルの妻役はケイト・ブランシェット、同僚役はリス・エバンスやピート・ポスルスウェイト、雇い主役はスコット・グレンと、演技派が大集合。", "2002-03-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GENTLEMAN’S AGREEMENT", "紳士協定", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "妻に先立たれ、幼い息子と老いた母との暮らしが続く人気ライター、フィル。彼は週刊“スミス”の編集長の招きでカリフォルニアからニューヨークに移り、さっそく反ユダヤ主義の記事を依頼される。この記事の発案者は編集長の姪キャシーで、フィルは彼女の心に動かされる。この厄介な仕事を幼なじみでユダヤ人のデビッドに相談しようかと悩むが、結局フィルは自分自身がユダヤ人になりきることにする。社の幹部との昼食会で、ユダヤ人だと名乗ったため、噂はあっという間に広まった。フィルがユダヤ人と知ると人々は急によそよそしくなった。フィルはキャシーに求婚し、二人はハネムーンに向かうが、ホテルがユダヤ人であることを理由にキャンセルしてきた。しかしフィルの記事が発表されると、実はユダヤ人でなかったフィルに対する世間の見方は変わった。当時反ユダヤ主義批判の映画として「十字砲火」とともに日本で公開されなかった問題作。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER CRYSTAL LAKE", "首謀者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ひどい干ばつに悩まされているカリフォルニアの小さな町には、美しいクリスタル湖という湖があり、町の唯一の水源となっていた。この湖にダムを造れば水不足は解消できるのだが、どうしても湖の所有者スワンが首を縦に振らない。実は、スワンには絶対にダムを造れない理由があったのだ。ある日、父親の不審な死の原因を探っていたダニエルは、スワンこそが父殺害の真犯人であり、クリスタル湖の湖底にある秘密基地で様々な悪事を働いていたことを知る。ダニエルは新聞記者とともに、巨大な陰謀に立ち向かう。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("It's All Gone Pete Tong", "フランキー・ワイルドの素晴らしき世界", "", "", "http://image.eiga.k-img.com/images/movie/33797/315x210.jpg?1457", 92, "ヨーロッパのクラブシーンの中心のひとつ、イビザ島を拠点に活躍するカリスマDJフランキー・ワイルドは、レコーディングのアクシデントで聴力を失ってしまう。友人や家族に見捨てられ、一時は死を決意するフランキーだが、読唇術を教える一人の女性に出会い、再生への道を歩み出す……。監督は、長編2作目となるマイケル・ドース。音楽に、ビョークやプライマル・スクリームのプロデュースなどを手がけたグラハム・マッセイ。", "2006-12-23", "3,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FALL OF THE BERLIN WALL", "ベルリンの壁 その日、何を見たか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "1989年11月、東西ドイツ分断の象徴でもあるベルリンの壁が崩壊。分裂から統合までの半世紀近いドイツの歴史を、その折々の実録フィルムを交えながら綴ったドキュメンタリー。", "1990-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HYSTERIC", "HYSTERIC", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「アナーキー・イン・じゃぱんすけ」で初期の勢いを取り戻した瀬々敬久が、再び得意の実録もの路線に戻って手掛けた犯罪逃亡劇。1994年に起きた“青学大生殺害事件”から材を得て、行き当たりばったりの凶行を繰り返す一組のカップルの末路を追う。“太く短く”生きて死のうとする少年のような男と、彼から逃れようとしながらも引きずられてしまう少女のような女。破滅したくても破滅できない彼と、現実から離脱したくても離脱しきれない彼女の道行きを、独特の停滞感の中に綴る。二人に殺される人のいいサラリーマン役を鶴見辰吾が好演。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ワールドプロレスリング3D 第4弾 1.4東京ドーム 2012", "ワールドプロレスリング3D 第4弾 1.4東京ドーム 2012", "", "", "http://image.eiga.k-img.com/images/movie/57737/315x210.jpg?2810", 90, "新日本プロレス主催の大会を映画館で上映する「ワールドプロレスリング」シリーズの第4弾。2012年1月4日に行われ、4万3000人の観衆を熱狂させた「レッスルキングダムVI in 東京ドーム」の模様を3Dカメラで収録。11回連続防衛という偉業を成し遂げたIWGPヘビー級王者・棚橋弘至の名勝負ほか、熱戦の数々を臨場感たっぷりの映像で映し出す。", "2012-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("American Beauty", "アメリカン・ビューティー", "", "", "http://ecx.images-amazon.com/images/I/51VAVmPj7XL._SL210_.jpg", 117, "郊外の新興住宅地に暮らす夫婦と娘の三人家族。夫婦仲は冷め、娘は親と意思の疎通がない。おかしな青年とゲイ嫌いの父親がいる隣家も同様の家庭だ。だが夫がリストラに合い、娘の友人に性的妄想を抱き、妻は浮気、娘は隣家の青年と駆け落ちを決意し……。コミカルで辛辣な家庭崩壊ドラマ。アカデミー作品賞、監督賞、主演男優賞、脚本賞、撮影賞の5部門を獲得。", "2000-04-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HISTORY OF THE WORLD PART I", "メル・ブルックス 珍説世界史 Part1", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "紀元前3000年からフランス革命まで、約5000年間の歴史を綴る“お笑い世界史”。モーゼからルイ16世まで、一人５役をやってのけるブルックスに加え、スタッフ、キャストともブルックスの常連ばかりと、まさにワンマン・コメディと言える１本。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月光仮面・魔人の爪", "月光仮面・魔人の爪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "東南アジアが舞台。パラダイ王国のシャバナシ殿下が、サタンの爪と名乗る幽鬼の仮面をつけた怪人に射殺され、パラダイ王国５千億の秘宝の在りかを示す地図が奪われる。悪の限りをつくすサタンの爪の前に正義の味方・月光仮面が立ちはだかった。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dirty Heart", "Dirty Heart", "", "", "http://image.eiga.k-img.com/images/movie/54867/315x210.jpg?2209", 0, "ボクシングの東洋フェザー級チャンピオンの斉藤は、任侠組織「地獄興業」に、敵対組織である「極楽興業」を壊滅させるために大金で雇われる。斉藤は「極楽興業」に所属する忍者に両組織の金を強奪しようと持ちかけるが、計画がばれてしまい……。主演にお笑い芸人のパッション屋良、猫ひろし、元キックボクサーの横川康次。監督は「DIVIDE／ディバイド」の辻岡正人。", "2009-11-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIDORI", "MIDORI", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "内田春菊のコミック『物陰に足拍子』を映画化。吉成みどりは兄夫婦との３人暮らし。家ではみどりのことで兄夫婦の言い争いが絶えず、彼女が唯一落ち着けるのは学校の保健室だった。そこで出会い、初体験の相手となったのが下級生のシュン。孤独だったみどりは、シュンと過ごす時間に初めて安らぎをおぼえる。ドライな女子高生が兄嫁との確執、恋、クラスメイトの自殺、カルト集団などにほんろうされながらも自立に向かって歩く姿を描く。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREEBIE AND THE BEAN", "フリービーとビーン大乱戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "サンフランシスコ警察のズッコケ刑事コンビ、フリービーとビーンが、賭博のボスを相手に大活躍するアクション・コメディ。すさまじい撃ち合いとカーチェイス・シーンの迫力は抜群。アーキン＆カーンのコンビが絶妙の持ち味を見せている。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Daisy", "デイジー　アナザー・バージョン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「インファナル・アフェア」のアンドリュー・ラウ監督＆「猟奇的な彼女」のクァク・ジェヨン脚本によるラブストーリー「デイジー」を、チョン・ウソン扮する暗殺者の視点から描いた「アナザー・バージョン」。山間の村に身を潜めていた暗殺者パクウィは、スケッチ旅行にやって来た美しい女性ヘヨンに心を奪われる。それ以来、ヘヨンを陰から見守るようになったパクウィは、正体を明かさぬままヘヨンに毎日デイジーの花を贈り続けるが……。", "2006-06-24", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SQUEEZE", "マンハッタン・ミステリー 消えた黒い箱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "八百長賭博にも手を出しているしがないビデオ・アーティストと、その別れた妻の慰謝料を取り立てにやって来た見習いの女探偵。ひょんなことからコンピュータを悪用して宝くじの当選番号を操作している悪党の陰謀に巻き込まれて……。コメディ・タッチのアクション・ミステリー。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女体桟橋", "女体桟橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "売春組織に立ち向かう刑事の奔走を描いたサスペンス活劇。手配中の公金横領犯を装い、売春地帯に潜入した刑事、吉岡は昔の恋人ルミと再会。彼女は国際的な売春組織のボスの情婦となっていたが、吉岡の行動力に惚れ直して彼に協力。かくして吉岡は組織の検挙作戦に乗り出す。石井輝男監督が当時得意としていたセミドキュメンタリー風の手法に注目。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WISH YOU WERE HERE", "あなたがいたら 少女リンダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1950年代のイギリスの港町。16歳の少女リンダは愛する母を亡くし、愛情に飢えた反抗的な日々を送っていた。彼女は様々な男性経験を重ね、挙げ句の果てに妊娠。しかし、彼女は自分の道をしっかりと選んでいく……。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Roots Time", "ルーツ・タイム", "", "", "http://image.eiga.k-img.com/images/movie/34167/315x210.jpg?1517", 77, "2人のラスタマンが移動式レコードショップでジャマイカを横断するロード・ムービー。カラフルなおんぼろ車でレコードを売っているバブーとジャー・ブルは、ラジオの人気DJファーマー・ルーツがヒッチハイクしているところに遭遇する。具合が悪くなったガールフレンドを病院に連れて行きたいというのだ。西洋医学を信じないバブーとジャー・ブルは、彼らを薬草医ボンゴ・ヒューの元へ連れて行こうとするのだが……。", "2007-09-01", "77,22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬猫", "犬猫", "", "", "http://ecx.images-amazon.com/images/I/51EH2V2NTKL._SL210_.jpg", 94, "PFFアワード2001で企画賞を受賞し、８ミリ作品でありながら劇場公開もされた同名自主映画を井口奈己監督が35ミリでセルフ・リメイク。ひょんなことから一軒家で同居することになった幼なじみの女の子たちを主人公に、彼女たちのつかず離れずの奇妙な関係を綴る。榎本加奈子、藤田陽子のふんわりとした自然体の演技もさることながら、ささやかなスリルとユーモア満載の映像世界を紡ぎ上げた井口監督のきめ細やかな演出が秀逸。不思議な愛おしさがこみ上げてくる１本だ。中国に留学する友人の留守宅を預かることになったヨーコ。ところが彼氏に見切りをつけたスズも、その家に転がり込んでくる。二人は過去に一悶着あった微妙な間柄で……。", "2004-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ポケットモンスター　ダイヤモンド＆パール　ディアルガVSバルキアVSダークライ", "劇場版ポケットモンスター　ダイヤモンド＆パール　ディアルガVSバルキアVSダークライ", "", "", "http://image.eiga.k-img.com/images/movie/34057/315x210.jpg?2447", 95, "“ピカチュウ・ザ・ムービー”が10周年を迎え、新シリーズ「ポケットモンスター　ダイヤモンド＆パール」の劇場版第1弾として登場。ポケモンコンテストに参加するためアラモスタウンにやって来たサトシたちは、そこで出会った美しい女性アリスに街を案内してもらうことに。そんな彼らの前に、幻のポケモン・ダークライが姿を現す。同じ頃、神と呼ばれるポケモン・ディアルガとパルキアが、時空の狭間で激しい闘いを繰り広げていた……。", "2007-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("盗馬賊／HORSE THIET", "盗馬賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "チェン・カイコー、チャン・イーモウらとともに北京電影学院の再開第１期生として学んだティエン・チュアンチュアンが、ドキュメンタリー映画の巨匠、ヨリス・イヴェンスの絶賛を得た前作「狩り場の掟」に続いて再び厳格なドキュメンタリー・タッチを駆使してチベット民族の悲劇を描き上げた傑作。1923年のチベット。遊牧民族のロールブは逼迫した生活に耐えきれず、馬泥棒をする。そして強盗までするようになった彼は、部落から追放され、妻子を連れて流浪の旅に出るが、途中で子供を失ってしまう。罪の意識に目覚めた彼は祈りに明けくれ、ついに二人目の子をもうけるのだが……。出演者は現地のチベット人から選ばれている。第三世界映画祭クリーパオ市大賞受賞。", "1985-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KEN PARK", "KEN PARK", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「KIDS」で衝撃を与えたラリー・クラーク監督と、脚本を担当したハーモニー・コリンが７年ぶりに顔を合わせた作品。舞台はカリフォルニア州郊外の住宅街ヴァイセリア。中流家庭の少年少女とその親たちの日常を通して、ドラッグとセックス、暴力があふれる歪んだアメリカのティーンの闇に切り込んでいく。執拗で生々しい性描写が賛否両論を呼んだ。", "2002-01-01", "1,70,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1492: Conquest of Paradise", "1492・コロンブス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 154, "　1941年、スペイン。船乗りで探検家のクリストファー・コロンブスは地球球体説を証明する航海に出るため、イザベル女王に許可を願い出る。そして翌年、第一回目の旅に出発。やがてコロンブスは新大陸を発見することに。一躍英雄となった彼は大船団を率いて再び航海へ。しかしたどり着いた土地での新都市建設に思わぬ障害が。貴族の反発に加え、コロンブスと反目するモクシカが反乱を起こしたのだ……。コロンブスの数奇な人生を壮大なスケールで描く。アメリカ大陸発見500周年を記念して作られたスペクタクル・ロマン。", "1992-01-01", "3,1,2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWORD　IN　THE　MOON", "清風明月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "一度は固い友情で結ばれながらも、朝廷の武将とテロリストという敵対する立場に分かれるはめになった男たちの宿命的な闘いを描く歴史活劇。CGやワイヤーワークを控えめに、リアリティと臨場感を追求した剣術シーンが見もの。韓国映画らしい男臭さを前面に押し出した熱血ドラマだ。時は朝鮮王朝時代。武官訓練所で腕を磨いたジファンとギュヨプが、政変の勃発で離ればなれに。やがてジファンはテロリストとなり……。", "2005-03-19", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bastardi Senza Gloria", "地獄のバスターズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1944年、第2次世界大戦下のフランスを舞台にした戦争アクション。護送中に敵機から爆撃を受けて脱走した連合軍の兵士たちは、自由を求めて永世中立国スイスへの亡命を図るが、逃走途中でドイツ軍のV2ロケット奪取作戦に参加する……。監督はマカロニウェスタンを数作手掛けているB級アクションの名手エンゾ・カステラーリ。主演はボー・スベンソン。", "1976-01-01", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PATSY", "底抜けいいカモ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ホテルのボーイ、スタンリーは急死した喜劇俳優に代わって、コメディスターに育てられることに。寝耳に水の彼も、楽をして暮らせるといわれて、すっかりその気になった。事務所の手立てで名も知られ、出演依頼も殺到するが、実際にお笑い演技をしてみると、まったくウケず……。喜劇の才人J・ルイスが妙演をみせるコメディ。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("深紅", "深紅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「白い犬とワルツを」の月野木隆が、今は亡き野沢尚の原作にして彼の遺作となった脚本を映画化。運命の悪戯で顔を合わせてしまった殺人事件の被害者と加害者それぞれの娘同士の愛憎渦巻く物語を描く。単なるミステリーで終わらない野沢の洞察力が光る、深層心理を見つめた奥深いドラマだ。小学生の時、家族を惨殺された体験が暗い影を落とす奏子。心に深い闇を抱える彼女は、犯人の娘・未歩の居場所を入手し、接近を図る。", "2005-09-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SENZA　PELLE", "アパッショナート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「愛のめぐり逢い」で注目されたイタリアの若手俳優K・ロッシ・スチュアート主演のラブ・ストーリー。感受性の強い青年が初めて恋を経験し、傷つきながら成長していく姿を繊細なタッチで描く。美しい人妻ジーナの元に、愛の詩を綴ったラブレターが届く。夫が突き止めた差出人は裕福な家庭に生まれた美青年サヴェリオだった。感受性が強すぎるあまり社会に適応できず、親の庇護の下で暮らしている彼をジーナは温かく見守るようになる。ところが、サヴェリオのジーナへの狂おしい愛は次第にエスカレートし、ついに事件を引き起こしてしまう。", "1994-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にっぽん親不孝時代", "にっぽん親不孝時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "自分の家の金庫から金を盗んでも泥棒にはならないという理屈で、邦男は金庫から金を持ち出し５人分の楽器を買ってバンドを結成。そこへフーテンの二人組が加わり、この７人はヤミドル事件に巻き込まれる。監督は「おくさまは18歳・新婚教室」などの山本邦彦で、これが監督デビュー作。ザ・スパイダースの最後の映画作品でもある。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SEX MACHINE", "セックス発電　男女100人絶頂物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "時はすべてのエネルギー源を消費しつくした21世紀のある日。主人公は、新たなエネルギー源を求め、日夜研究に励む科学者ノエル。そんな彼が女性のエクスタシーから生じる奇妙な電力を発見し……。ムチムチの美女が次々と登場するお色気コメディ。", "1978-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("民衆の敵", "民衆の敵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "今井正の戦後第１作となった人間ドラマ。大戦末期、民間の肥料工場が財閥に吸収され、軍需用の化学工場に転換させられる。不自由な足をひきずって工場を守る工場長の金子は、上からの無理な増産命令にも従ってきた。そんな中、徴用工がケガをしてしまう。それを知った金子は社長・花園のもとへに乗り込むが、花園は裏で金子を失脚させようと悪だくみをしていて……。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神秘の法", "神秘の法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真昼の花", "真昼の花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "人気作家、角田光代の同名小説を映画化した人間ドラマ。放浪する兄の行方を追う孤独な妹が旅先での経験を通し、自分自身を再生させていく。人気ボーカリストの小林桂やサックスプレイヤーの梅津和時など、ジャンルを越えて抜擢された異色キャストに注目だ。母と死別し、一人きりになった“私”は家を出た兄を探す旅に出る。沖縄の地に流れ着いた“私”は、そこでさまざまな人々と出会い……。", "2005-10-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Encarnacion", "化身", "", "", "http://image.eiga.k-img.com/images/movie/54057/315x210.jpg?3268", 93, "第5回スペイン・ラテンアメリカ映画祭上映作品。元B級アイドルの中年女性エルニは、世間に忘れ去られることに怯えながら、芸能界の片隅で小さな仕事を淡々とこなしている。そんなある日、田舎に住む姪のアナから15歳の誕生パーティの知らせが届く。美しい女性へと変貌していくアナと、若さと美貌に執着するエルニの交流を通し、女の発見と成長を描く。", "2008-09-12", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINAL JUDGEMENT", "ファイナルジャッジメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "カルト作品を次々と送り出した鬼才R・コーマンが、製作総指揮として名を連ね、相変わらずの斬新なアイデアで迫るエロティックなサスペンス。不良少年から聖職者へ転身した神父が、連続美女殺人の疑いをかけられ、自ら犯人探しに乗り出す。主演は、“チャイルド・プレイ”シリーズのチャッキーの声としても知られるB・ドゥーリフ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAUTY AND THE BEAST", "美女と野獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "アニメ映画史上初めて、アカデミー作品賞にノミネートされ、惜しくも受賞は逃したもののオリジナル作曲賞、主題歌賞を受賞した、ディズニーの長編アニメ第30作。たたみかけるようなドラマ展開で、感動のクライマックスへと一気に盛り上げる。魔女によって醜い獣に変えられた王子の住む城に、発明狂の老人が迷い込む。老人を捜す彼の娘ベルが城にやって来て、父を解放してもらう代わりに自分が囚れの身になる。野獣もベルとともに暮らすうちに少しずつ人間の心を取り戻していく。だが、野獣に許されて町に戻ったベルからそのことを聞いた猟師のガストンが、ベルを自分のものにしようと人々を率いて城を攻撃するのだった……。ティー・ポットや燭台に姿を変えられた人々がディズニーらしいユーモラスな演技を見せる一方で、ガストンと野獣の対決は実写さながらのバイオレンス感覚。新旧ディズニー・アニメのコントラストを一作の中で際立たせた力作だ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マリリンに逢いたい", "マリリンに逢いたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ニュースでも紹介された“恋人に逢うために海を泳いで渡る犬”シロを描いた動物映画。沖縄の阿嘉島から座間味島までの３キロを泳ぎ、愛するメス犬・マリリンに逢いに行くシロの純情を、飼い主で民宿建設を夢見る青年や、カメラマン志望の若い女性など、彼を取り巻く人々のエピソードを交えて映し出す。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bourne Legacy", "ボーン・レガシー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/56727/315x210.jpg?2446", 0, "記憶を失ったスパイ、ジェイソン・ボーンの戦いを描いたロバート・ラドラム原作、マッド・デイモン主演の人気スパイアクション3部作（「ボーン・アイデンティティー」「ボーン・スプレマシー」「ボーン・アルティメイタム」）の世界観を引き継ぎ、ジェイソン・ボーンの戦いの裏側で繰り広げられていた、もう1人のスパイの物語をジェレミー・レナー主演で描く。CIAの極秘計画「トレッドストーン作戦」によって生み出された最強のスパイ、ジェイソン・ボーンが起こした一連の事件は、同計画によって生み出されていたもう1人のスパイ、ケネス・キットソンを巨大な陰謀に巻き込んでいく。監督は前3部作の脚本を手がけたトニー・ギルロイ。ジョアン・アレン、デビッド・ストラザーンらシリーズおなじみのキャストも再登場。", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機関車先生", "機関車先生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "1997年にアニメ映画化もされた伊集院静の同名小説を、「ヴァイブレータ」の廣木隆一が映画化したヒューマン・ドラマ。瀬戸内海に浮かぶ小島を舞台に、口のきけない先生と生徒たちの交流を軸に人々の温かい触れ合いを叙情的に描き出す。これが映画初主演となる坂口憲二が、セリフのない主人公を包容力を感じさせる豊かな演技でさわやかに演じる。瀬戸内海の小島・葉名島にある唯一の小学校に若い教師・吉岡がやって来る。生徒たちは、口のきけない彼を口をきかん先生だから“機関車先生”と呼んで温かく迎えた。吉岡は生徒や島の人々と絆を深めていく。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE，HONOUR＆OBEY", "ロンドン・ドッグス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ロンドンの裏社会を舞台にしたユーモラスなギャング映画。郵便配達の仕事に嫌気のさした若者ジョニーは、親友の紹介でギャングの一員に。だが組織のボスは婚約者の御機嫌とりに執心し、下っぱ連中も仕事が終わればカラオケに繰り出す始末。そんな日常を楽しんでいたジョニーだが、一方で敵対組織にケンカを売ってしまい……。意外にノンキなギャングたちのユーモラスな姿はもちろん、皮肉なエンディングも忘れ難い。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELL HUNTERS", "ヘル・ハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「007／オクトパシー」のボンド・ガール、M・アダムス主演作。ナチの残党と彼らを追い詰めるハンターたちの熾烈な戦いを、南米ロケにより壮大なスケールで描いたアクション巨編。ジャングルの奥地で殺人兵器の開発に励み、ナチ帝国の復興を夢見るホフマン一味に、母親を殺されたアリーの怒りが炸裂する。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAMON JAMON", "ハモン・ハモン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "妊娠から結婚を決意した、娼婦の娘シルヴィアと良家の子息ホセ・ルイス。息子の結婚に反対する母コンチータは、筋骨隆々のたくましい青年にシルヴィアを誘惑するように命ずるが……。二つの世代にまたがる愛憎関係を描いた、情熱的なラブ・ストーリー。シルヴィアの母役をA・ガリエナが好演。", "1992-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京暮色", "東京暮色", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 141, "二人の娘を残したまま、母親が愛人と家出をしてしまった一家の物語で、戦後期の小津のフィルモグラフィーでは例外的に暗く、陰うつな雰囲気に包まれた異色の作品といえるだろう。しっかり者の姉、つまらぬボーイフレンドに妊娠させられ事故死する妹、妻に逃げられながら態度がはっきりしない父親、変転の果てに、ひっそりと麻雀荘を営む母親。小津監督は、それぞれのもの言わぬ肩や背中に生きることの悲哀をずっしりと感じさせ、寂漠の人生模様を甘い感傷に溺れることなく、見事に織り上げてみせた。とりわけ、母親と娘の再度の別れのシークエンスが激しく胸を打つ。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　アンパンマンとハッピーおたんじょう日", "それいけ！アンパンマン　アンパンマンとハッピーおたんじょう日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 30, "やなせたかし原作の人気アニメーション劇場版第7作「それいけ！アンパンマン　ゆうれい船をやっつけろ！」と同時上映の短編。", "1995-07-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄男II BODY HAMMER", "鉄男II BODY HAMMER", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "妻と子供が誘拐されたことがきっかけとなり、自らの肉体が鋼鉄の銃器と化してしまった男の姿を描く。「ヒルコ」で待望のメジャー・デビューを果たした塚本晋也が再び、製作・監督をはじめ一人８役をこなした。その独特のテンションの高さを十分に味わえる作品である。監督の塚本晋也自身が100カ所以上に新しいサウンドを加え、再編集した特別版“スーパー・リミックス・バージョン”も公開され、ビデオのリリースもされた。DVDタイトルは「鉄男II　TETSUOII　THE BODY HAMMER SUPER REMIX VERSION」。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はやぶさ HAYABUSA BACK TO EARTH", "はやぶさ HAYABUSA BACK TO EARTH", "", "", "http://image.eiga.k-img.com/images/movie/56177/315x210.jpg?2770", 44, "2003年5月9日に打ち上げられ、度重なるトラブルに見舞われながらも小惑星イトカワに到達し微粒子を採取。7年を経た10年6月13日、奇跡的に地球に帰還した小惑星探査機「はやぶさ」の軌跡をフルCGで再現したドキュメンタリー。全国のプラネタリウムで上映されたバージョンに最新映像を追加して劇場公開する。", "2011-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帰らざる波止場", "帰らざる波止場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "石原裕次郎と浅丘ルリ子のコンビによるムード・アクションの後期代表作。麻薬組織のために臭い飯を食わされた世界的ジャズ・ピアニスト・津田史郎は、復讐を誓い出所後組織と接触するため横浜へやって来るが、そこで財閥の未亡人・冴子と出会い、恋に落ちてしまう。横浜というバタ臭い土地を背景に、石原裕次郎と浅丘ルリ子が息のあったところを見せる。特にラスト、傷を負った裕次郎とルリ子がタラップの上でひしと抱き合うシーンの甘美さはなかなかのもの。主人公をつけまわす刑事役で珍しく志村喬が出演。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADVENTURES OF DON JUAN", "ドン・ファンの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "活劇スター、E・フリン主演のもの。女性と剣に滅法強い放蕩児ドン・ファンと宿敵ローカとの対決をクライマックスに、活劇の醍醐味をふんだんに盛り込んだ娯楽作。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASTE´RIX　＆　OBE´LIX： MISSION　CLE´OPA＾TRE", "ミッション・クレオパトラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "フランスの国民的な漫画をもとにしたコメディ。女王クレオパトラの命令で90日のうちに宮殿を造ることになった建設家の、魔法の力を借りた奮闘を描く。突拍子もない展開や映画のパロディーなど、大胆なギャグが続出。クレオパトラに扮した「アレックス」のモニカ・ベルッチの自己中心的キャラとセクシーな魅力にも注目！", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNA RAGAZZA PIUTTOSTO COMPLICATA", "痴情の森", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "気が弱く、空想にふけって日々を送るアルベルトは恋人クラウディアと暮らしている。そんな彼がある日、クラウディアと義母の異常に親しげな姿を目にして発作的に義母を殺してしまった。その時から彼の空想の世界は狂気に支配されていく……。", "1969-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2010-2011 ワーグナー「ニーベルングの指環」　第1夜「ワルキューレ」", "METライブビューイング2010-2011 ワーグナー「ニーベルングの指環」　第1夜「ワルキューレ」", "", "", "http://image.eiga.k-img.com/images/movie/56477/315x210.jpg?1780", 320, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を劇場のスクリーンで上映する「METライブビューイング」2010～11年シーズンの12作目。ワーグナーの全4部からなる大作オペラ「ニーベルングの指環」の第2部。主神ボータンが人間に産ませた兄妹ジークムントとジークリンデはお互いを知らずに育ち、禁断の愛に落ちてしまう。ジークムントはジークリンデの夫フンディングと決闘することになり、ボータンの娘ブリュンヒルデはジークムントを勝たせようとするが、自身の思いと神としての立場の間で葛藤（かっとう）するボータンはそれを阻む。", "2011-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　FEMME　EST　L’AVENIR　DE　L’HOMME", "女は男の未来だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「豚が井戸に落ちた日」「気まぐれな唇」などで知られるホン・サンスが、恋愛における男女の相違、本音と嘘を描き出す。過去を引きずる男二人と、彼らを悪気なく翻弄する女。彼らの奇妙な三角関係の行方を追っていく。完成したシナリオを用いず、俳優たちとドラマを作り上げていく独特の撮影方法でリアルな雰囲気が生まれた。大学の美術講師ムノは、先輩で映画監督のホンジュンと再会。彼らは、7年前二人が付き合っていたソナを思い出し、会いに行くが……。", "2005-10-29", "5,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEPHEN KING'S SOMETIMES THEY COME BACK", "ブロス やつらはときどき帰ってくる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "９歳の時に兄が不良グループにからまれて汽車にひかれて死亡するという過酷な体験を持つ、高校教師のジム。以来彼はずっとその悪夢にうなされてきたのだが、ある日彼のクラスに転校生がやって来る。それは兄とともに汽車にひかれて死んだ、不良グループの一人だったのだ……。S・キングの短編小説の映画化。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LESS THAN ZERO", "レス・ザン・ゼロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "20歳のB・E・エリスが書いたベストセラー小説を、M・カニエフスカ監督が映像化した青春もの。華やかなパーティに明け暮れ、コカインとセックスに溺れる上流階級の若者たちの青春が、MTV感覚のファッショナブルな映像の中で描かれていく。東部の大学からロスに帰省したクレイは、事業に失敗し麻薬中毒に陥っている親友のジュリアンと再会。彼は恋人のブレアとともにジュリアンを立ち直らせようとするのだが……。アメリカの豊かさの象徴ビバリーヒルズを舞台に、現代の“ゼロ世代”の若者たちの、華やかだが実体のない青春がホロ苦く胸を打つ。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勝手に死なせて！", "勝手に死なせて！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "南米で客死した夫を巡り、理想的な葬儀を行おうとする妻、それに反対する親戚たち、謎の男女、まったく無関心な娘らが入り乱れ、死体争奪ゲームが展開。「ひき逃げファミリー」の監督＝脚本コンビが再び放つ、ブラックなホームドラマ。伊丹十三の「お葬式」の反対をいく“バチ当たり”コメディ。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トワノクオン　第一章　泡沫の花弁", "トワノクオン　第一章　泡沫の花弁", "", "", "http://image.eiga.k-img.com/images/movie/56127/315x210.jpg?1933", 49, "「機動戦士ガンダム　第08MS小隊」などで知られ2010年に他界した飯田馬之介監督が、生前に制作を進めていたオリジナルアニメーション。全6話を6カ月連続で劇場上映する。飯田監督の没後は、「ストラトス・フォー」「ヴァンドレッド」のもりたけしが“協力監督”として制作現場を引き継いだ。異端の能力者「ベスティア」として生きるクオンは、秘密組織「オールドー」により体を機械に改造された追っ手「クーストース」から逃れるため戦い続ける。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CHUTE DE LA MAISON USHER", "アッシャー家の末裔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "ポーの『アッシャー家の崩壊』を中心に、『楕円の肖像』『リジーア』の３作から想を得た怪奇映画。不気味な燐光を発する暗い沼地の脇に建つアッシャー家。病弱で瞑想的なロドリークと妻マドレーヌがここに暮らしていた。ある夕暮れ、友人ラミが館を訪ねて来た。妻の肖像を描くロドリーク。だが、肖像が完成に近づくにつれ、マドレーヌは生気を失っていき、やがて……。ラストには、館が沼にのみ込まれていくシーンが用意され、ポーの原作がもつ怪奇色が忠実に再現される。と同時に、監督のJ・エプスタンは、全編に漂う妖気を再現するため、スローモーションやアングルの多彩な変化、あるいはシュールレアリスティックなショットの積み重ねなどを最大限に駆使。サイレント映画の美学的な頂点を極める作品となっている。", "1928-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("狼たちの復讐", "狼たちの復讐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "元WBA世界ジュニアバンタム級チャンピオンの鬼塚勝也が映画初出演、復讐のために闘う男を演じたハードボイルド作品。ヤクザのドン・藤村に拾われ、暴力的な仕事に手を染めていた光二は空しさを感じ、組織からの脱退を決意。藤村は最後の仕事として光二にある仕事を与える。光二は自由のために引き受けるが、そこには思わぬ出来事が待ち受けていた。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Motorcycle Diaries", "モーターサイクル・ダイアリーズ", "", "", "http://image.eiga.k-img.com/images/movie/1547/315x210.jpg?2860", 127, "キューバ革命の指導者チェ・ゲバラが、青年時代に親友と2人でオートバイで挑戦した南米大陸横断の旅を、「天国の口、終りの楽園。」の人気俳優ガエル・ガルシア・ベルナルの主演で映画化。親友役は実際にゲバラのはとこにあたるロドリゴ・デ・ラ・セルナ。監督は「セントラル・ステーション」のウォルター・サレス。23歳の喘息持ちの医学生エルネストは、親友と2人で中古バイクで南米を旅してさまざまな人々に出会っていく。", "2004-10-09", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED SONJA", "レッドソニア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "“コナン”シリーズのスタッフによる第３作にして番外編的な１作。悪の女王ゲドレンに村を焼き打ちにされ、姉を殺された女戦士ソニアは剣の修行を積み、カリドーや王子ターンらの助力を得てゲドレンを倒す。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("酔いどれ博士", "酔いどれ博士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "傷害事件を起こしたために外科医の免許を剥奪された医師が、スラム街のドヤに住みついて無免許のまま医院を開業。底辺に生きる人々の外傷から心の傷までも治していく。勝新太郎が柄に合ったアナーキーな男を好演。無免許が警官にばれかけた主人公が、社会の不正に怒りをぶつけるシーンは圧巻だ。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOSHUA TREE", "バニシング・レッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "D・ラングレンが主演した、カーチェイス満載のバイオレンス・ドラマ。高級車の運び屋サンティは、身に覚えのない警官殺しの罪で投獄された。脱獄した彼は、美女リタの乗る4WDを強奪。やがて、警官である彼女はサンティの無実を信じ始め、彼を罠にハメた組織をともに追い詰めていく。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA NAISSANCE DE L’AMOUR", "愛の誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "パリで暮らす俳優のポールはウルリカという若い娘を愛していたが、同時に子供を愛するがゆえに妻の元を離れられない。一方、ポールの親友マルキュスは文章を発表する場に恵まれない作家で、恋人エレーヌとも感情がスレ違い始めていた。そんなある日、ポールとマルキュスは、エレーヌが滞在するローマへの自動車旅行に出る……。“心にカメラを持つ”とも評される映画作家P・ガレルが、凍てつくようなパリの街を背景に撮り上げた内省的な愛の叙事詩。ヌーヴェル・ヴァーグの名カメラマン、R・クタールによるモノクロームの映像に、J・ケイルの美しいピアノの即興音楽がしみじみと鳴り響く。", "1993-01-01", "2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAST MIDNIGHT", "ミッドナイト25時 殺しの訪問者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ソーシャルワーカーという自分の仕事を天職だと思っているローラの前に、15年前に身重の妻を殺して服役、刑期を終えて出所してきたばかりのベンという男が現れる。彼の人柄に触れていくうち、身重の妻を殺すような人物であると信じられなくなったローラは、ベンの過去を独自に調査し始める。偏執狂的な憎まれ役が絶品のハウアー主演のスリラー。無名時代のクエンティン・タランティーノが、ノン・クレジットで脚本のリライトを担当している。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海軍戦記", "海軍戦記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "今までに起こった様々な戦争の歴史を、貴重な映像と解説で検証する戦争ドキュメンタリー。「帝国海軍勝利の記録」に続き、海軍省が監修している。軍装に身を包み出撃を見送る山本五十六長官の姿や、ソロモン海戦、ツラギ夜襲戦などの帝国海軍の貴重な映像を捉えた長編記録映画。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STONE", "マッドストーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "シドニーの公園で起こった政治家の暗殺事件。犯人は証拠隠滅のため、現場の近くにいた暴走族のメンバーを一人ずつ殺していく。刑事のストーンは狙われた暴走族に潜入して捜査するが……。オーストラリア製のワイルドな暴走族アクション。", "1979-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・影の軍団／最終章　服部半蔵最期の戦い", "新・影の軍団／最終章　服部半蔵最期の戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "往年の人気TV番組をスクリーンに甦らせた、「新・影の軍団」シリーズが完結。忍者俳優の代名詞・千葉真一を筆頭に、松方弘樹、菅原文太らラストを飾るに相応しい大御所が一堂に会し、徳川家康暗殺の陰謀を阻止するべく強敵に立ち向かう、“影の軍団”の活躍を華々しく彩っている。藩主の娘にかけられた呪いを解くため、服部半蔵率いる影の軍団が再結集。任務遂行に動き出した彼らだったが、想像を絶する強敵が待ち受けていた。", "2005-02-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Don Giovanni", "ドン・ジョバンニ", "", "", "http://image.eiga.k-img.com/images/movie/55107/315x210.jpg?4802", 0, "", "2010-05-29", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("里見八犬伝", "里見八犬伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "中国の『水滸伝』の構想にならった滝沢馬琴のおなじみ『南総里見八犬伝』に、新解釈を加えた書き下ろし小説をもとに映画化。薬師丸ひろ子７本目の出演作で初の時代劇。特撮に製作費と時間を費やし、「スター・ウォーズ」ばりのSFタッチの作品となっている。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い海峡", "黒い海峡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "自分の窮状を救ってくれた船場のために人を殺し、自首した槙が３年ぶりに横浜に帰ってきた。船場組は横浜一の組にのし上がっていたが、槙の同僚・啓次は突然姿を消し、槙は彼を追って神戸へ飛んだ。そこで槙は、船場がずっと自分をだましていたことを知るのだった……。日活ムードアクションに属する佳作。ロングショットを生かした演出が印象的で、江崎実生監督の名を初めてファンに知らしめた。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地球交響曲　ガイアシンフォニー", "地球交響曲　ガイアシンフォニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "登山家R・メスナー、動物保護活動家D・シェルドリック、植物学者・野澤重雄、歌手エンヤ、ケルト美術研究家・鶴岡真弓、元宇宙飛行士R・シュワイカートの６人が、それぞれの立場から地球と人間の関係について語るドキュメンタリー。公開当時全国各地の活発な自主上映に支えられ、1994年末までに400ヵ所25万人の動員を記録した。監督はかつてロックバンド、キャロルのドキュメンタリー番組をめぐって“NHK馘首事件”に巻き込まれた龍村仁。", "1992-11-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばり捕物帖 かんざし小判", "ひばり捕物帖 かんざし小判", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "美空ひばり十八番の歌謡時代劇。老中の妹でありながらお姫様暮らしを嫌い、十手持ちとなったお七こと妙姫の活躍を、歌あり笑いありで描いていく。江戸美人番付の行列のさなか、そのうちの一人が殺されてかんざしが盗まれた。それは３本一組で松永藩の財宝のありかがわかるというかんざしだったが……。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rikidozan", "力道山", "", "", "http://image.eiga.k-img.com/images/movie/1617/315x210.jpg?2386", 149, "日本のプロレス界の歴史的人物、力道山の半生を「パイラン」「ホワイトクリスマス　恋しくて、逢いたくて」のソン・へソン監督が映画化。力道山は「オアシス」「シルミド／SILMIDO」のソル・ギョングが体重を5カ月で28キロ増やし、日本語を習得して熱演。1945年、貧困を逃れるため相撲取りになることを夢見て単身日本にやってきたシルラク。関脇になったシルラクは横綱を目指すが、そこには目に見えない民族の壁があった。", "2006-03-04", "76,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("姐 極道を愛した女・桐子", "姐 極道を愛した女・桐子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "愛人の勇の服役中に、むりやり二階堂組組長の妻にさせられた桐子。やがて出所した勇との再会に、彼女は立場をわきまえながらも喜びをかみしめる。しかし、それを知った二階堂は彼を抗争の渦中へ追いやった。桐子は勇を救うため、奔走するが……。家田荘子の原作による、もう一つの“極妻”ドラマ。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画ドラえもん　新・のび太と鉄人兵団　はばたけ天使たち", "映画ドラえもん　新・のび太と鉄人兵団　はばたけ天使たち", "", "", "http://image.eiga.k-img.com/images/movie/55687/315x210.jpg?3147", 108, "1986年の公開作「ドラえもん　のび太と鉄人兵団」を現代風にアレンジ。のび太は、北極で見つけた大きなロボットの足とナゾの青い球体を家に持ち帰る。それ以来、庭に次々と落ちてくるようになった部品を組み立て、巨大ロボット・ザンダクロスを完成させたのび太とドラえもんの前に、ロボットの持ち主だという少女リルルが現れる。実はザンダクロスとリルルは、ロボットの星・メカトピアから地球を征服するためにやって来たのだった。", "2011-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新 仁義なき戦い・組長の首", "新 仁義なき戦い・組長の首", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "“新 仁義なき戦い”シリーズ第２作だが、前作とはストーリー的な関連性はない。映画は昭和43年の北九州を舞台に始まる。覚醒剤密輸の利権で肥え太った暴力団・大和田組の派閥抗争・跡目相続をめぐる陰謀を、カー・アクションを盛り込んでスピーディーに描く。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alien: The Director's Cut", "エイリアン　ディレクターズ・カット", "", "", "http://ecx.images-amazon.com/images/I/51WS9CZ06FL._SL210_.jpg", 116, "　宇宙船ノストロモ号に侵入した未知の生命体の恐怖を描くSFホラー。監督リドリー・スコットはこの映画を若い世代に最良の状態で見せたいと考え、フィルムを修復し、サウンドをリミックス、さらに新しいフッテージを追加し再編集し直すことに。ダラス船長とブレットがエイリアンの巣に取り込まれている衝撃的な場面を始め、約6分のカットが追加された。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BAMBA", "ラ★バンバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ロック台頭期の1958年、突如としてミュージック・シーンに姿を現し、わずか８ヵ月の間に『ラ・バンバ』などヒット曲を連発させながら、翌年不慮の事故により17歳で世を去ったリッチー・バレンスの半生を、心温まる家族愛に絡めて描いた作品。ドラマはメキシコ系季節労働者の集まる北カリフォルニアの果実農場から始まる。貧しいながらも明るく生きるリッチーが、南カリフォルニアに移り住んだ後、新しい学園生活の中で理想の恋を見つけ、やがて自分のバンドを結成。デビュー・コンサートで、デルファイ・レコードの敏腕プロデューサーに認められ、人気歌手へと成長していく姿を追う。悲劇的なラストに至る事件経過は実話に忠実。リッチーの歌声とバンドのサウンドは、デヴィッド・ヒダルゴと彼がリードヴォーカルを務めるメキシコ系グループ、ロス・ロボスが吹き替えている。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mine Vaganti", "あしたのパスタはアルデンテ", "", "", "http://image.eiga.k-img.com/images/movie/55947/315x210.jpg?3818", 113, "パスタ会社社長の次男トンマーゾは家族には経営学部と偽って文学部を卒業し、小説を執筆していた。兄で長男のアントニオが新社長に就任するパーティで秘密を打ち明けようとしていたところ、兄がさらに驚きの秘密を明かし、父親は卒倒。トンマーゾは何も言い出せないまま会社の経営を任されてしまい……。「ラスト・ハーレム」「向かいの窓」などで知られるイタリアの名匠フェルザン・オズペテク監督が、家族のきずなをユーモアたっぷりに描く。", "2011-08-27", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOLLOW THE FLEET", "艦隊を追って", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "アステア、ロジャースのコンビで描いたミュージカル・コメディ。海軍の水兵と昔ダンス・パートナーだった踊り子という組み合わせに、もう一組のロマンスが絡む。コメディ・リリーフを加えず、手のこんだ物語にした野心以上に、軍隊をも踊りのめすエンターテインメント精神がすごい。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("網走番外地・南国の対決", "網走番外地・南国の対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "沖縄に舞台を移し、母と子の愛情描写にいい味をみせる。竜神一家の親分の死因に不審を抱いた橘真一は、事故の真相を突きとめるために沖縄へ飛ぶ。サイド・ストーリーながら、三原葉子が母の心情をすっきりと演じて素晴らしい。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAN　DE　REPENTE", "ある日、突然。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ロカルノ映画祭の準グランプリをはじめ、各国映画祭で受賞を果たしたアルゼンチン映画。地味でおデブな下着ショップの販売嬢が、パンキッシュなレズビアンのカップルに拉致される……というブッ飛んだ導入から、ストーリーは予測不可能。刃物のようにエキセントリックな女性たちの行動にほんろうされていると、いつの間にやらスリリングな逃避行は、不思議な優しさを持った群像ドラマに変貌を遂げる。モノクロ映像の淡々とした語り口はいかにも1980年代のアメリカン・インディーズ風だが、スタイルを超えた魅力が心に沁みる余韻を残す。タダモノではない音楽センスの良さにも注目！", "2002-01-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE CORPS DE MON ENNEMI", "追悼のメロディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "町のボスの娘ジルベルトと恋仲となり、その一族に迎えられたフランソワは、父親とボスの反目によりポストを追われ、殺人の濡れ衣を着せられる。７年後、出獄を待ち受けていたジルベルトの元へ戻ると、彼はボスへの復讐を心に誓って……。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　SENTINELLE", "魂を救え！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "「そして僕は恋をする」で日本にも多くのファンを生んだ気鋭の映画作家A・デプレシャンが、中編「二十歳の死」に続いて発表した長編処女作。父の死を契機に、法医学を学ぶためにドイツのボンからパリ行きの列車に乗った青年マチアス・バリエ。何者かによってミイラ化した人間の首をスーツケースに隠された彼は、ミイラの正体を探るうちに迷宮をさまような日々を過ごすことに……。デプレシャン自身が“敬愛するジョン・ル・カレのスパイ小説風”に仕上げたと語る一種のポリティカル・スリラーだが、マチアスを取り巻く人間模様がどんどん拡散していく夢幻的なドラマ展開には、青春サスペンスというべき独特の味わいがある。また後半、マチアスがミイラをいじり回す医学的な描写が執拗に登場するが、デヴィッド・クローネンバーグの映画が好きだというデプレシャンの嗜好を聞けば、ついニヤリとさせられてしまう。「歩哨」（原題の意）のタイトルで上映されたこともある。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("でんきくらげ", "でんきくらげ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "俗悪な生活環境のなか、時には性を武器にして体を張ってたくましく生きる女たちを、感傷を断ち切った、いわば乾いた知性を持って描いてきた増村保造の風俗映画。母の愛人に犯され、ホステスとなったヒロインは惚れた男の言いなりに金持ちと結婚し、その男の死後、結婚を迫る男を捨て、堂々と生きていくのだった。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Flight of the Phoenix", "飛べ！フェニックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "　石油会社の輸送機が砂嵐に遭遇し、砂漠の真っ只中に不時着する。操縦士フランクと航空士ルーは脱出策を練るが、見通しは立たず、犠牲者も相次ぐ。そんな中、航空技師ハインリッヒが壊れた機を新たな単発機に改造し、飛び立つことを提案。生存者たちはフェニックス号と名づけた改造機に全てを託す！　男たちの決死のサバイバルを描くアドベンチャー・ロマン。2004年にリメイク作「フライト・オブ・フェニックス」も製作された。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Regrets", "リグレット（原題）", "", "", "http://image.eiga.k-img.com/images/movie/55347/315x210.jpg?2521", 105, "「おせっかいな天使」「チャーリーとパパの飛行機」のセドリック・カーンが描く大人のラブストーリー。パリに暮らす建築家マチュー・リバンは、母親の突然の入院で、故郷の小さな村に帰る。その道中、マチューは昔の恋人マヤと15年ぶりに再会するが、男と少女と一緒だったマヤはマチューと言葉を交わすことはなかった。しかし、しばらくしてマヤから連絡が入り、家に遊びに来ないかと誘われる……。フランス映画際2010にて上映。", "2009-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Thin Red Line", "シン・レッド・ライン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 171, "　太平洋戦争中の1942年、アメリカ陸軍の部隊がガナルカナル島へ上陸。兵士たちは日本軍と激しい戦闘を続ける一方、複雑な思いを抱えていた。手柄を焦る司令官トール中佐、彼に反発するスタロス大尉、志願して斥候隊に加わるウィット二等兵、勇敢なウェルシュ軍曹、戦地で知った妻の心変わりに困惑するベル二等兵。そんな彼らは大自然の中で繰り広げられる戦闘の果てに何を見たのか？　戦場の狂気、そして名もない兵士たちの苦悩を、20年ぶりに監督復帰したテレンス・マリックが描く。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ボーイズ・オン・ザ・ラン", "ボーイズ・オン・ザ・ラン", "", "", "http://image.eiga.k-img.com/images/movie/54357/315x210.jpg?3711", 114, "花沢健吾の人気コミックを、舞台演出家の三浦大輔が映画化した青春映画。主演はロックバンド「銀杏BOYZ」のボーカル・峯田和伸、共演には松田龍平、YOU、リリー・フランキー、小林薫ら個性派が揃う。玩具メーカーに勤めるサラリーマン・田西は、実家暮らしで彼女いない歴29年の冴えない男。同僚のちはるに密かな恋心を抱いているものの、積極的になれず全く相手にされていない。仕事先で出会ったエリート営業マンの青山の手ほどきで、ちはるとの距離を縮めようとする田西だが……。", "2010-01-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hellraiser: Hellworld", "ヘルレイザー　ヘルワールド", "", "", "http://ecx.images-amazon.com/images/I/515ry7NTTvL._SL210_.jpg", 95, "", "2005-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Big Bang", "THE BIG BANG ザ☆ビッグバン!!", "", "", "http://image.eiga.k-img.com/images/movie/57227/315x210.jpg?1312", 101, "「デスペラード」「マスク・オブ・ゾロ」のアントニオ・バンデラスが主演するノワールアクション。元ボクサーのアントンから、失踪した恋人の捜索を依頼された中年探偵のネッドだったが、事件の関係者は次々と奇怪な死を遂げ、ネッドの身にも危険が迫る。それでも捜査を続けるネッドは、かつてアントンが八百長試合で殺人を犯したこと、その報酬として受け取った3000万ドルのダイヤモンドも行方知れずになっていることを突き止める。", "2011-11-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents うそ　「バーブの点滅と」", "桃まつり presents うそ　「バーブの点滅と」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "", "2010-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROGER CORMAN’S FRANKENSTEIN UNBOUND", "フランケンシュタイン 禁断の時空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "知る人ぞ知るB級ホラーの巨匠、R・コーマン監督が、20年ぶりにメガホンを執った作品。21世紀で、最終兵器の研究をしている男ブカナンが、19世紀のスイス・ジュネーブにタイムスリップ。なんと、フランケンシュタイン博士と出会うという、奇想天外なストーリー。もちろん、おなじみのモンスターも登場。ブカナン役にJ・ハート、フランケンシュタイン博士はR・ジュリアが扮している。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEATHERFACE ： THE TEXAS CHAINSAW MASSACREIII", "悪魔のいけにえ3　レザーフェイス逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "前作で全滅したはずの人食い一家が、再び蘇生した。中でも中心的存在のマッド・キラー“レザーフェイス”は、テクノロジーに取り憑かれた弟が改造してチューンナップされた、切れ味120パーセントの“スーパーチェーンソー”を振りまわして大暴れ。テキサスの奥深くに潜む異常極悪家族のとどまるところを知らない恐怖攻めが堪らない。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALEXSANDER'S RAGTIME BAND", "世紀の楽団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "アカデミー賞候補となった音楽映画。シスコの酒場で出会った、バンド指揮者のロジャーと歌手ステラ。組んで演奏を始めた彼らは次第に有名になっていく。気の強い二人はケンカばかりだったが、実は愛し合っていた。ようやく素直に愛を認め合った彼らだが、そんな折り、ステラに興業契約の話が舞いこみ……。I・バーリンのナンバーが全編を彩る。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Civil Action", "シビル・アクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "アメリカで実際に起きた民事訴訟を題材に描く法廷ドラマ。若くして自分の法律事務所を構えたエリート弁護士のジャンは、マスコミにも顔を出す人気者。そんな彼が、息子の死が環境汚染によるものだと訴える一人の女性からの電話を受け、やむを得ず調査を開始する。やっかいな環境問題には消極的だった彼だが、廃液を垂れ流す工場の実態と、被害者の苦しみを目の当たりにし、全財産を投げ打って不可能な闘いに挑む。監督は「シンドラーのリスト」の脚本で名高いS・ザイリアン。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハイザイ　神さまの言うとおり", "ハイザイ　神さまの言うとおり", "", "", "http://image.eiga.k-img.com/images/movie/57947/315x210.jpg?1664", 0, "2012年・第4回沖縄国際映画祭の長編プログラム「Laugh」部門にて上映。", "2012-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Bigger Splash", "デヴィッド・ホックニー／彼と彼　とても大きな水しぶき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "イギリスが生んだポップ・アートの世界的アーティストであるデビッド・ホックニーと、ゲイである彼を取り巻く恋人や仲間たちの姿を、1971年から73年にかけて追い続けたセミ・ドキュメンタリー・フィルム。愛する恋人との別れや新しい出会い、そして新天地アメリカでの芸術活動など、ホックニーの日常をリアルに描く。さらに彼の作品「ペーパー・プール」を制作する過程を収録した貴重な映像も見ることができる。", "1974-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Invention of Lying", "ウソから始まる恋と仕事の成功術", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Iron Man 2", "アイアンマン2", "", "", "http://image.eiga.k-img.com/images/movie/54257/315x210.jpg?1634", 124, "マーベル・コミックの人気作品をロバート・ダウニー・Jr.主演で映画化した「アイアンマン」の続編。新たなキャストとして、スカーレット・ヨハンソンとミッキー・ロークが参加。巨大軍事企業の経営者であり、天才科学者でもあるトニー・スタークは、前作で自ら開発したすさまじいパワーを発揮するパワード・スーツを装着し、アイアンマンとしてテロ組織と激闘を繰り広げた。その後、スタークはパワード・スーツを軍事利用のため国家に引き渡すよう命じられるが、これを拒否する。一方、スタークを敵視するウィップラッシュがアイアンマンと同等なパワーを持つスーツでモナコGPに現れ……。", "2010-06-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAN’S BEST FRIEND", "未確認生命体 MAX〈マックス〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "J・ラフィアが手掛けたユニークなSFホラー。様々な動物のDNA融合によって生まれたクリーチャー“マックス”は、一見、おとなしいマスティフ犬に見えるが、その正体は高い知能を持つ凶暴な生物兵器。この危険な生命体になつかれた女性TVレポーターの恐怖の体験が描かれる。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CRIMINAL", "ザ・クリミナル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ロンドンを舞台にしたクライム・サスペンス。売れないミュージシャンJは、いきつけのバーで魅力的な女性サラと出会う。首尾よく自分のアパートに彼女を連れ込んだJだが、そこで何者かが侵入、サラは惨殺されてしまう。殺人容疑をかけられ、独自で調査を始めるJ。命を狙われるという危険にさらされながら、彼はついにとんでもない事実にたどりつく。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　くろゆき姫とモテモテばいきんまん", "それいけ！アンパンマン　くろゆき姫とモテモテばいきんまん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 20, "やなせたかし原作の人気アニメーション劇場版第18作「それいけ！アンパンマン　ハピーの大冒険」と同時上映の短編。", "2005-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GITAN", "ル・ジタン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "自ら獄中経験を持つ異色監督ジョヴァンニの生粋フィルム・ノワール。殺人、脱獄、銀行強盗で暗黒街に知られるジプシー男“ル・ジタン”の、社会への徹底した反逆の人生を描く。非情でどこか寂しげな一匹狼をドロンが好演。フランス製の任侠ヤクザ映画といえる。", "1975-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忠僕直助", "忠僕直助", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "稲垣浩、山中貞雄に師事したこともある国木田三郎のデビュー作。赤穂義士の一人、岡島八十右衛門の下僕・直助が、主人が佩刀をバカにされたため、刀鍛治の弟子になり、名刀を売って主人の汚名をそそぐ。後半には、師・山中に影響されたユーモラスなタッチが見える。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLER: A JOURNAL OF MURDER", "KILLER 第一級殺人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1920年代アメリカに実在した第一級殺人犯カール・パンズラムの犯行の全貌を描いた問題作。カンザス州北東部にある連邦刑務所に送られてきた囚人カールは、入所早々冷酷な看守グライサーから容赦ない仕打ちを受けていた。信念と理想に燃える新任看守のヘンリーは、そんなカールに職務を逸脱していると知りながら、１ドル札を差し入れる。一方カールは、ヘンリーの差し入れに対する礼として、自分が犯してきた凶悪な犯行の数々の手記を新聞社に売るようにと申し出るが……。カール役のJ・ウッズが、冷酷無比な殺人鬼であると同時に人間的な部分も持ち合わせた複雑怪奇なキャラクターを見事に演じている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Transylvania", "トランシルヴァニア", "", "", "http://image.eiga.k-img.com/images/movie/34117/315x210.jpg?1606", 102, "「ガッジョ・ディーロ」など自らのルーツである流浪の民ロマをテーマに描き続けてきたトニー・ガトリフ監督が、真実の愛を探してさまよう女性の旅を描き出した人間ドラマ。突然姿を消した恋人を追い、彼の故郷であるトランシルバニアを訪れたジンガリナ。しかし、再会した恋人に彼女への愛情は残っていなかった。失意のうちに再び旅に出た彼女の前に、謎めいた男チャンガロが現われ……。主演は「サラ、いつわりの祈り」のアーシア・アルジェント。", "2007-08-11", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUMA", "ドゥーマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "南アフリカを舞台に少年とチーターの友情と、自然に向かう彼らの過酷な旅を見つめた感動作。動物との交流や冒険を通して成長する少年の内面を、「グース」のキャロル・バラード監督が丁寧に描く。農場で動物に囲まれて育ったというアレキサンダー・ミハルトスの好演も光る。チーターの子供を助け、ドゥーマと名づけて育てる少年キサン。やがて成長したドゥーマを野生に返すため、彼は密林への旅を始めるが……。DVDタイトルは「ぼくのともだち　ドゥーマ」。", "2005-11-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fair Game", "フェア・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/55877/315x210.jpg?3768", 108, "03年3月に開始されたイラク戦争のきっかけとなった大量破壊兵器の存在。米外交官ジョセフ・ウィルソンはその存在そのものを否定するレポートを発表したが、米政府はそれを無視。さらに報復としてウィルソンの妻バレリー・プレイムが現役のCIAのエージェントであることマスコミに暴露する……。イラク戦争開戦をめぐり実際に起こった「プレイム事件」を「ボーン・アイデンティティー」のダグ・リーマン監督が完全映画化した実録サスペンス。出演は、バレリーにナオミ・ワッツ、ウィルソンにショーン・ペン。", "2011-10-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬と私の10の約束", "犬と私の10の約束", "", "", "http://image.eiga.k-img.com/images/movie/53127/315x210.jpg?1940", 117, "犬から人への願い事という形で綴られた短編詩“犬の10戒”をモチーフに、犬と少女との10年間に渡る絆を綴った感動作。函館で暮らす少女・あかりは、ある日迷い込んできたゴールデンレトリーバーの子犬に“ソックス”と名付けて飼うことに。あかりは母から犬を飼うときの“10の約束”を教えてもらい……。成長したあかりを田中麗奈が演じるほか、加瀬亮、豊川悦司ら実力派キャストが揃う。監督は実写版「ゲゲゲの鬼太郎」の本木克英。", "2008-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fourth Kind", "THE 4TH KIND フォース・カインド", "", "", "http://image.eiga.k-img.com/images/movie/53917/315x210.jpg?1609", 99, "多数の住民が行方不明となり、不眠症を訴える人々が増え続けるアラスカ州ノーム。この事態を不審に思ったノーム在住の心理学者タイラー博士は、催眠療法で彼らの不眠の理由を解明しようとしていた。65時間以上に及ぶ記録映像と、再現映像によって構成された異色サスペンス。主演はミラ・ジョボビッチ。", "2009-12-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LADY FROM SHANGHAI", "上海から来た女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "資産家バニスターの美しい妻エルザが暴漢に襲われそうになったところを偶然救ったマイクは、ヨット航海で夫婦と同行することに。航海中にエルザと親密な仲になったマイクは彼女と駆け落ちする資金を作るために、夫婦の弁護士に持ちかけられた奇妙な殺人事件に巻き込まれるが……。ニューヨークの闇夜、サンフランシスコのチャイナタウン、鏡張りの部屋での撃ち合いなど、ウェルズらしい意匠に富んだサスペンス・スリラー。撮影で使われたヨットは往年のアクション俳優エロール・フリン所有の物。DVDは「フィルム・ノワール・コレクション(2)」に収録。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Barravento", "バラベント", "", "", "http://image.eiga.k-img.com/images/movie/56297/315x210.jpg?4602", 79, "ブラジルの新しい映画＝シネマ・ヌーボの旗手として賞賛されたグラウベル・ローシャ監督の没後30年を記念し、日本初公開作を含む代表作を連続上映する「グラウベル・ローシャ・ベスト・セレクション」にて上映。ブラジル北東部バイーア地方の漁村に、村を出て都会で暮らしていた男が戻ってくる。男は村人たちを古い因習から解放しようとするが、それをきっかけにさまざまな波紋が広がっていく。ローシャ監督が1962年に発表した長編第1作。2011年日本初公開。", "2011-06-18", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRAZY IN LOVE", "愛を奏でて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "キャリアウーマンのジョージーは、郊外の広い家に祖母、母、妹一家、そして愛する夫ニックと暮らしてきた。ところが夫のロンドン出張中に、ハンサムなカメラマンのマイクに求愛されたジョージーの心は激しく揺れる。内心の動揺を知ってか知らずか、母たちは彼女にとても優しい。そんな時、夫が不意に帰宅し、彼らは互いの不満を爆発させる……。「ランブリング・ローズ」の女流監督がケーブルTV用に作った感動ドラマで、日本では劇場で公開された。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISSIONE SPECIALE", "エンジェル・ミッション 戦場のショーガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "異国の地で戦いに明け暮れる戦士たちのキャンプに現れた若く美しいダンサーのマリー。だがキャンプは敵の砲撃を受け、ほとんどの人間は死んでしまう。生き残った兵士ジョンは、彼女を守るために悪夢のような逃避行をしていくが……。戦争のさなか、一人の少年が青年へと成長を遂げていく過程を描くアクション・ドラマ。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tuck Everlasting", "エバーラスティング　時をさまようタック", "", "", "http://ecx.images-amazon.com/images/I/51PD37C6EEL._SL210_.jpg", 90, "", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GINGER ET FRED", "ジンジャーとフレッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "“ジンジャーとフレッド”という名で、かつて人気を博した芸人コンビ、アメリアとピッポが、クリスマスのTV番組出演で、30年ぶりに再会する。共演者たちの奇妙きてれつな顔ぶれに戸惑う二人に出番の時がやってくる……。フェリーニがこれまでいろいろな形で表現してきた、TV産業に対する批判的姿勢を、より明確に、かつコミカルに映像化。ラスト、二人に再び訪れる別れのシーンでは、サラリとした演出ながら、互いの深い愛情が、きめ細かく情感たっぷりに盛り込まれている。また、フェリーニ映画を象徴する“巨大なもの”“サーカス的要素”も随所に散りばめられ、まさにフェリーニの集大成といえる作品である。", "1985-01-01", "4,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Breath Less ブレス・レス", "Breath Less ブレス・レス", "", "", "http://ecx.images-amazon.com/images/I/51eW4566HQL._SL210_.jpg", 112, "芥川賞作家・高橋三千綱の短編小説を筒井道隆主演で映画化した大人の青春映画。看守として働きながら、自分が組んだブルースバンドのライブを留置所で行なう警察官の徹。警察の独身寮で共同生活を送る後輩の中野とは、冗談を言いあう良いコンビだった。ある日、念願の刑事に昇進した徹は、連続窃盗事件の被害者・恵美子に事情聴取することに。ところが、被害を必死で否定する恵美子に個人的な興味を抱いてしまい……。", "2006-04-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高速戦隊ターボレンジャー", "高速戦隊ターボレンジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 25, "地球征服を目論む暴魔百族と、妖精シーロンや太宰博士の力を借りてターボレンジャーとなった5人の高校生たちの戦いを描くスーパー戦隊シリーズの劇場版。かつて1人の妖精の命と引きかえに封印された凶悪な暴魔獣ジャシンボーマが、現代に復活した。ジャシンボーマを再び封印するには、妖精族最後の生き残りであるシーロンの命が必要だと知ったターボレンジャーたちは、自分たちの力でジャシンボーマを倒すべく激闘を繰りひろげる。", "1989-03-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DARKEST LIGHT", "ハロー、アゲイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「フル・モンティ」の脚本家サイモン・ボーフォイの長編デビュー作。純心無垢な子供の心が奇跡をもたらす感動ドラマ。イングランドのヨークシャー地方。活発な少女キャサリンは転校生のインド系の少女ウマと意気投合、人種を超えて友情で結ばれる。羊の伝染病が広がり、町に不安が広がるなか、二人は探険に出かけた荒野でまばゆい光を目撃する。キャサリンは、その光が弟の白血病が治る予言だと信じるが……。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADOLPHE", "イザベル・アジャーニの惑い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "コンスタンの同名小説をI・アジャーニが、自ら企画し主演を務めた文芸ラブ・ストーリー。美貌の青年貴族と年上の伯爵夫人との許されぬ恋が、耽美的かつ悲壮感あふれるムードで綴られる。アジャーニの出世作「アデルの恋の物語」を思わせる、愛のためなら自らを破滅させる女性像が強烈。日々の倦怠から逃れるべく、青年貴族のアドルフは一目惚れした年上の伯爵夫人エレノールを誘惑する。エレノールも夢や希望に満ちあふれるアドルフに惹かれるようになる。やがて二人は激しい恋に落ちるのだが……。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Repo Men", "レポゼッション・メン", "", "", "http://image.eiga.k-img.com/images/movie/53177/315x210.jpg?3700", 111, "エリック・ガルシア原作の小説をジュード・ロウ主演で映画化したSFアクションスリラー。高価な人工臓器の開発によって、より寿命が長くなった近未来。人工臓器の支払能力がなくなった人間への取り立て（レポメン）で生計を立てていたレミー（ロウ）は、ある日、取り立てに失敗し、重傷を負ってしまう。傷は癒え、職場復帰を果たすが、以前のように仕事が出来なくなったレミーは、自分が生きてきた社会に疑問を感じ、かつての同僚と戦うことを決意する。共演にフォレスト・ウィテカー、リーブ・シュレイバー。", "2010-07-02", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Scott Pilgrim vs. the World", "スコット・ピルグリム VS. 邪悪な元カレ軍団", "", "", "http://image.eiga.k-img.com/images/movie/55367/315x210.jpg?1501", 112, "ブライアン・リー・オマーリーによるコミック「Scott Pilgrim」を「ショーン・オブ・ザ・デッド」「HOT FUZZ ホットファズ／俺たちスーパーポリスメン！」のエドガー・ライト監督が映画化したアクション・コメディ。カナダに住む売れないバンドのベーシスト、スコット・ピルグリムは、ある日不思議な女性ラモーナに出会い、すぐさま恋に落ちる。だが、ラモーナと付き合うためには、彼女の邪悪な元彼7人と決闘をして、すべてを倒さなければならなかった……。主人公スコットにマイケル・セラ。共演に「ダイ・ハード4.0」のメアリー・エリザベス・ウィンステッド、クリス・エバンス。", "2011-04-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("川のある下町の話", "川のある下町の話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "下町のS大附属病院のインターン・義三は川に落ちた少年を救ったのが縁で、その姉のふさ子と知り合う。両親を亡くした彼女は、パチンコ屋で働く貧しい娘だったが、いつしか義三との間に愛が芽ばえていく……。『婦人画報』に連載されて人気を博した川端康成の原作の恋愛もの。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太と鉄人兵団", "ドラえもん のび太と鉄人兵団", "", "", "http://ecx.images-amazon.com/images/I/61ZniK9ejWL._SL210_.jpg", 97, "北極でロボットの部品を見つけたのび太。あまりに巨大なため、ドラえもんは鏡の中の世界で組み立てることにした。完成後、リルルという少女がロボットの持ち主であるとのび太たちの前に現れる。しかし彼女は、地球征服をしようとするロボットの国メカトピアからやってきたロボットだった。現実世界と鏡の中の世界が交錯しながら展開する不思議アドベンチャーだ。", "1986-03-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガンマー第３号 宇宙大作戦", "ガンマー第３号 宇宙大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "日米合作SF作品。地球に突進する惑星を爆破するために、宇宙ステーション・ガンマ３号の隊員が派遣される。だが、任務を終えて基地に帰ってきた彼らは宇宙生物を持ち帰ってしまった。不死身の宇宙生物は人間を襲いはじめる。「エイリアン」タイプの佳作。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("State Legislature", "州議会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 217, "", "2006-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャンプ！ボーイズ", "ジャンプ！ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/33577/315x210.jpg?3383", 84, "7人の小学生と幼稚園児が体操に打ち込む姿を描き、金馬賞ドキュメンタリー部門最優秀作品賞を受賞した傑作ドキュメンタリー。全国体操大会を目指す厳しい熱血コーチ、リンに泣きながらついて行く7人の姿が感動を呼び台湾で大ヒットを記録した。監督は、リンコーチの実弟で本作が初の長編映画となるリン・ユゥシェン。撮影に1年半をかけて、子供たちの素のコメントを引き出している。", "2006-07-15", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Date with Drew", "デート・ウィズ・ドリュー", "", "", "http://image.eiga.k-img.com/images/movie/33777/315x210.jpg?4758", 90, "ハリウッド女優ドリュー・バリモアとのデートを夢見る平凡な男性が、クイズ番組で獲得した賞金1,100ドルを使ってデート実現を目指すドキュメンタリー。紹介を繰り返すことで徐々に彼女に近づいていくほか、直接デートを申し込もうと「チャーリーズ・エンジェル／フルスロットル」のプレミアパーティに潜入するなど大奮闘。ドリューの元恋人コリー・フェルドマンやジュリア・ロバーツの兄エリック・ロバーツも登場する。", "2006-12-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY GIRL2", "マイ・ガール2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "少女の淡い初恋をみずみずしく描いてヒットを記録した「マイ・ガール」の続編。春休みの自由研究の題材として、13歳になった少女ベーダが選んだのは、亡き母の実像。ロサンゼルスに行き、母の知り合いから意外な事実を聞き出す彼女の心の成長を、セカンド・ラブのエピソードを絡めてさわやかに描き出す。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HIDDEN II", "ヒドゥン2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "カイル・マクラクランが主演した「ヒドゥン」の続編。前作で善玉エイリアンの手によって倒された、凶悪な寄生生物“ラウティス”。15年後ラウティスは蘇生したばかりか、大量の繭を作っていた。その消息を追っていた善玉エイリアンが殺され、彼の同僚がラウティスと戦うため、新たに地球にやって来る。主人公と人間の美女とのラブ・ストーリーを盛り込んで、前作にないヒューマンな味わいを醸し出している。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ATRACTION", "レディ・ドール２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "美しい裸身を惜し気もなく披露し、劇場公開でヒットを飛ばした「レディ・ドール」に続き、F・ゲランがさらに挑発的に過激に迫る１本。今回は、列車の中での体験によって女として開花していく姿を官能的な映像で描く。画面からエロチシズムがあふれんばかりにほとばしる。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLAZING SADDLES", "ブレージングサドル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "法も秩序もない西部のある町。腹黒い知事は、黒人奴隷バートを保安官にして住民の不安を募らせ、彼らを町から追い出すことを企てる。だがバートは監獄の常連ジムと意気投合し、知事が送り込んだ無法者一味に立ち向かうのだった。パイ投げ、ターザンやヒトラーの登場など、どんちゃん騒ぎの大合戦が見ものの爆笑ウエスタン。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人斬り与太・狂犬三兄弟", "人斬り与太・狂犬三兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「現代やくざ・人斬り与太」の深作欣二・菅原文太のコンビによる“現代ヤクザ”シリーズの番外編とも言うべき１本。６年の刑を終えて出所した文太演じる若いヤクザが、自分をけむたがる新興ヤクザ組織にゲリラ作戦で立ち向かうが、ついに惨殺される。狂犬のような若いヤクザを文太が熱演。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地球のステージ　ありがとうの物語", "地球のステージ　ありがとうの物語", "", "", "http://image.eiga.k-img.com/images/movie/54237/315x210.jpg?2042", 0, "NHKのドキュメンタリー番組を制作してきた監督・佐藤威一郎が、NPO法人「地球のステージ」代表理事の桑山紀彦の活動を追ったドキュメンタリー映画。世界各国を駆け巡る精神科医の桑山は、勤務医の傍らで子供たち心のケアや医療活動を行っている。旧ユーゴスラビアの紛争で心に傷を負った少女アリッサから別れ際にもらった手紙を胸に、桑山は9年ぶりにクロアチアへと向かう。", "2009-01-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パッセンジャー 過ぎ去りし日々", "パッセンジャー 過ぎ去りし日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "早くに両親を失い、固い兄妹愛で結ばれているバイク・レーサーの兄とスーパー・アイドルの妹。やがて妹は兄のライバルでもあるバイク・レーサーと愛し合うが、あるコンサートの日、兄の事故死の報が届く。アイドル映画のワクを超えスケール豊かに描く、今は亡き本田美奈子の映画初出演作。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHACKLETON’S　ANTARCTIC　ADVENTURE", "シャクルトン奇跡の生還", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "アイマックス・シアター用に製作されたドキュメンタリー。20世紀初頭、人類初の徒歩による南極大陸横断に挑んだ伝説の探検家アーネスト・シャクルトンの冒険を検証する。南極への到着直前、船が氷山によって動かなくなり、極寒の地で孤立してしまった一行が、遭難から１年９ヵ月後に生還を果たすまでの奇跡的な実話を収録。極限状況におけるシャクルトンの卓越したリーダーシップが、数々の貴重な映像資料を通して伝えられる。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ДАМА С СОБАЧКОЙ／THE LADY WIHT A DOG", "子犬をつれた貴婦人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "チェーホフの短編小説『犬をつれた奥さん』を映画化した文芸ドラマ。19世紀末、クリミヤ半島の避暑地ヤルタ。銀行員グーロフは、犬を連れた貴婦人アンナに心奪われる。互いに家庭を持つ二人だが、ともに過ごすうちに強く惹かれ合っていく。やがて、夫の元へ戻っていったアンナが忘れられず、グーロフは彼女に会いに行き、再び互いの愛を確認し合うのだが……。チェーホフの文章さながらの美しく、洗練された映像で切ない恋を描き出す。", "1959-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The American Way", "アメリカン・ウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "オンボロのB−29爆撃機の中に海賊TVスタジオを構え、全米ネットワークのTV番組を電波ジャックしては暴れ回るベトナム帰りの元兵士８人。彼らが次なる攻撃目標に選んだのは次期大統領候補と目されるタカ派女史。さっそく彼女の当選を阻止すべくキャンペーン番組へことごとく電波介入していくが……。D・ホッパー主演で海賊チャンネルの活躍を描く快作。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ABOMINABLE DR. PHIBES", "怪人ドクター・ファイブス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "外科手術で妻を失った男が、旧約聖書の一節にあるファラオの呪いを使って、次々と妻の担当医たちを殺していく。彼と行動をともにする謎の美女の正体は？　主役を務めるプライスの怪演が出色のマッド・サイエンティスト・ホラー。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jour De Fete", "新のんき大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "フランス中部ののどかな田園地帯。年に1度の夏祭りの手伝いで大忙しの郵便配達員フランソワは、祭り当日にテント映画館で観た「アメリカ式郵便配達」に触発されて一念発起。アメリカ式のスピード郵便配達で村中をかけまわるのだが……。ジャック・タチの長編監督デビュー作「のんき大将脱線の巻」には、当初公開された白黒版の他に、カラー版が存在していた。本作は、後に発見されたそのカラー版を復元したもの。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("薩摩飛脚", "薩摩飛脚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "サイレント時代に、伊藤大輔・山中貞雄両監督によって作られ、さらにトーキー初期に伊藤監督によって再映画化された大佛次郎の原作の３度目の映画化。神谷金三郎は松村四海とともに幕府の密命を受け薩摩藩に潜入するが、松村は谷底へ転落。江戸へ生還した神谷に、疑惑の目が向けられ……。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プーサン", "プーサン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "数々のヒット作を生んできたとはいえ、市川崑の“才能”を一躍世に知らしめたのはこの作品であった。横山泰三の漫画を原作に、現代社会とそこで蠢く庶民の姿を、風刺コメディの形で完成させた逸品である。世渡りがヘタで、下宿先の娘に恋をしても想いを伝えられない主人公のやもめの教師が、俗物政治家が苦境もなんのその、スイスイ世を渡っていくのを目の当たりにして、非情の心を持とうとするが……。現代社会批判を、声高に訴えるのではなく、さりげなく良質のブラック・ユーモアのオブラートに包んでしまうあたり、「億万長者」「満員電車」などにつながっていく市川演出の面目躍如。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あんにょん・サヨナラ", "あんにょん・サヨナラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "日本軍に徴用された父と幼い頃に生き別れ、90年代になってから、中国での父の死や靖国神社に合祀されていることを初めて知った韓国人女性イ・ヒジャ。合祀取り下げを訴える彼女の姿を中心に、日韓の遺族や研究者など、様々な立場に置かれる人々のインタビューを交えて靖国神社問題を浮き彫りにした、日韓共同制作によるドキュメンタリー。釜山国際映画祭でドキュメンタリー部門最優秀賞を受賞するなど、世界中で大きな反響を呼んだ。", "2006-07-08", "76,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空想の森", "空想の森", "", "", "http://image.eiga.k-img.com/images/movie/53717/315x210.jpg?2795", 129, "北海道のほぼ中心に位置する新得町で農業を営む人々を捉えたドキュメンタリー。結婚・出産を機に今後の暮らし方を改めて考え直そうとする若い夫婦と、70年代に京都から移住して自分たちのスタイルを確立した夫婦という2組の家族を中心に、町の人々の何気ない日常にカメラを向け、その中にわきおこる様々な感情を丁寧にすくい取っていく。同町で開催されている映画祭をきっかけに映画制作を始めた田代陽子が7年の歳月をかけて完成させた意欲作。", "2008-07-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Love, First", "私の恋", "", "", "http://image.eiga.k-img.com/images/movie/54117/315x210.jpg?4322", 111, "韓国のイ・ハン監督が、「永遠の片想い」「青春漫画　僕らの恋愛シナリオ」に続いて手がけた恋愛映画3部作の完結編。変わり者の女性チュウォンを愛する地下鉄運転士セジン、思いを寄せる先輩チウを飲みに誘ったソヒョン、コピーライターでシングルファザーのジョンソクに密かな恋心を抱く広告代理店勤務のスジョン、別れた恋人に会うため6年ぶりに帰国したジンマン。5組のカップルが織りなす恋愛模様を、豪華キャスト共演で描く。「韓流シネマ・フェスティバル2008ラブ＆ヒューマン」上映作品。", "2008-10-04", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("七変化狸御殿", "七変化狸御殿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "昭和を代表する歌姫こと美空ひばりが、映画に主演して歌って踊って弾けた一作。本作は、時代劇タッチに加え「狸御殿」ものが中心。これは、狸御殿で繰り広げられるラブ・ストーリーと歌唱パフォーマンスとダンスの融合を目指す娯楽映画。2005年の鈴木清順監督作『オペレッタ狸御殿』では、そんなひばりをオマージュしている。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CROCODILE DUNDEE", "クロコダイル・ダンディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "オーストラリアで空前の大ヒットとなり、全米でも10週間連続興行ベスト１となるなど、時ならぬオーストラリア・ブームを巻き起こした、P・ホーガン主演の爆笑コメディ。オーストラリア訛りまる出しのホーガンの魅力が、得もいわれぬ味を醸し出している。オーストラリアの奥地、ワニが多数生息するジャングルで40年以上暮らし、ワニと戦って勝った男、“クロコダイル・ダンディー”。そんな彼がアメリカはニューヨークを訪れ、見るもの聞くもの驚くことばかりの中で、いかに生きのびるか……。現代の都会人が失ってしまった人間本来の素朴さが画面いっぱいにあふれる好編である。", "1985-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vinicius", "ヴィニシウス　愛とボサノヴァの日々", "", "", "http://image.eiga.k-img.com/images/movie/54427/315x210.jpg?2962", 122, "生涯で400編を超える詩と400曲の歌詞を世界に送り出した、ボサノバ史に残る詩人ビニシウス・ジ・モライス。彼が愛したブラジルのリオデジャネイロを舞台に、現在のブラジルを代表するアーティストたちのインタビューと演奏に加え、ビニシウスと関わった伝説のアーティストたちの貴重な写真や映像を織り交ぜながら語られる音楽ドキュメンタリー。", "2009-04-18", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・縁結び旅行", "喜劇・縁結び旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "忠臣蔵で名高い播州赤穂。その国鉄・赤穂駅に勤める赤垣源太は四十七士の子孫ながらいまだ独身。駅長の娘・みち代をめぐって源太と高利貸しの吉良甲介とがアツい戦いを繰り広げる。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("“キャンパス一一〇番”より 学生野郎と娘たち", "“キャンパス一一〇番”より 学生野郎と娘たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "曽野綾子の小説『キャンパス一一〇番』を「豚と軍艦」「若者たち」「私が棄てた女」の名脚本家・山内久が脚色、中平康が監督した諷刺喜劇。芳土大学に在籍する新劇の演出家を夢見る山本、オートバイを貸して稼ぐ吉野ら男子学生と行動派のノエミ、バーでアルバイトをする晃子ら女子学生の姿を描き出す。中平康が当時の学生気質をユニークなタッチで表現している。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("杣人〈そまうど〉物語", "杣人〈そまうど〉物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "女流監督・河瀬直美が出世作「萌の朱雀」のロケ地である奈良県西吉野村に再びカメラを向けたドキュメンタリー。孫の成長を楽しみにしつつ林業に勤しむ老人や、子供に先立たれた夫婦、別れた恋人の思い出を語る老人など、山で暮らす人々（＝杣人）の日常や人生観を描写。人生の喜怒哀楽を真摯に見つめた、河瀬の実直な視点が光る。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Good Man in Africa", "グッドマン・イン・アフリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "舞台は西アフリカの架空の独立国キンジャンジャ。大統領選挙を控えたこの国では、利権をめぐり土地の政治家やイギリス政府高官たちのキナ臭い思惑が入り乱れていた。若きイギリス人外交官リーフィーは、出世コースからはほど遠いこの任地で何とか一花咲かせようとかけずり回る。そんな世俗にまみれたリーフィーの生き方を大きく変えるきっかけとなったのが、“グッドマン”と呼ばれる偉大な医師マレーとの出会いだった……。B・ベレスフォード監督の笑いと感動のヒューマンドラマ。マレー医師をS・コネリーが好演。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが愛の譜 滝廉太郎物語", "わが愛の譜 滝廉太郎物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "『花』『荒城の月』などの名曲を生み出し、23歳10ヵ月という若さでこの世を去った滝廉太郎の没後90年を記念して作られた伝記映画。東京音楽学校でピアニストを志すが挫折、それでも音楽への情熱を捨てず、ドイツに留学。そんな彼の青春を憧れの女性ピアニスト・中野ユキとの恋模様を軸に描き出していく。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KOREAN CONNECTION", "バイオレンス・コネクション 処刑警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "麻薬担当刑事ファンは、偶然に目撃したひき逃げ事故から、麻薬密売の糸口をつかみ、ついにはその実体をつかむことに成功。警察は大規模な作戦に乗り出すが……。韓国の実写フィルムを挿入し、ドラッグに溺れる女たちや吊し上げ拷問など生々しいシーンが連発するハード・アクション。", "1990-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏の別れ", "夏の別れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "高校卒業後も両親のもとでぶらぶらしている少年が、とある帆船の中で男女の激しいラブ・シーンに遭遇。その女に憧れて彼女を追いまわすが、男女の複雑な過去を知り驚嘆する。短編小説的な味わいで描いた、少年のひと夏のスケッチ。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬笛", "犬笛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "大企業の謀略による殺人の現場を目撃してしまった娘が誘拐され、父親と愛犬の決死の追跡行が始まった。父と娘を結ぶのは、１本の犬笛と愛犬・鉄。父は鉄を連れて北海道から沖縄へ……。スリルとサスペンスの連続の三船プロ創立15周年記念映画。西村寿行のベストセラー作の映画化。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年メリケンサック", "少年メリケンサック", "", "", "http://image.eiga.k-img.com/images/movie/53477/315x210.jpg?2447", 125, "レコード会社で新人発掘を担当する栗田かんなは、ネット上で偶然見つけたイケメンのパンクバンド「少年メリケンサック」に惹かれ、メジャーデビューの話を持ちかける。しかし、ネットの動画は25年前に撮影されたもので、実際の彼らはすでに50歳に近いオヤジばかりだった……。人気脚本家・宮藤官九郎の監督第2作。佐藤浩市、木村祐一、田口トモロヲらが扮する中年パンクロッカーに振り回されるかんな役で、宮崎あおいが初のコメディ映画に主演。", "2009-02-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Grudge", "THE JUON 呪怨", "", "", "http://image.eiga.k-img.com/images/movie/1197/315x210.jpg?2411", 99, "「今まで見た映画の中で最も怖い映画」と絶賛、ハリウッド版を作ることを決めたサム・ライミがオリジナル版と同じ清水崇監督を起用して劇場版「呪怨」を完全リメイク。2週連続全米ナンバーワン獲得、日本人監督作初となる興収1億ドル突破も話題に。東京の大学で福祉を学ぶ留学生カレンは、授業の一環として、米国人ビジネスマンの母親の介護をするため、ある民家を訪れるが……。", "2005-02-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WING COMMANDER", "ウィング・コマンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "全米でカルト的人気を誇り、シリーズ化されたシューティング・ゲームの映画化。地球連邦の移動式艦隊基地が地球外生命体のキルラティに奇襲され、人工知能コンピュータを奪われてしまう。キルラティは連邦の防衛ラインを突破し、地球へと接近していた……。ゲームの作者であるC・ロバーツが監督・脚本を担当したSFアクション。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沈まぬ太陽", "沈まぬ太陽", "", "", "http://image.eiga.k-img.com/images/movie/54297/315x210.jpg?4314", 202, "累計700万部を超える山崎豊子のベストセラー小説を渡辺謙主演で映画化。監督は「ホワイトアウト」の若松節朗。巨大企業・国民航空の労働組合委員長を務める恩地は、職場環境の改善を目指し会社側と戦うが、懲罰人事で海外赴任を命じられてしまう。パキスタン、イラン、ケニアと次々と転勤を強いられた恩地は、10年後に本社復帰を果たすが、帰国後間もなく自社のジャンボ機が御巣鷹山に墜落するという事件に直面する。", "2009-10-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MASQUERADER", "仮面の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "下院議員と瓜二つのジャーナリストが、身代わりを頼まれたことで、思いもしない運命を背負うミステリー・タッチのドラマ。彼は金のために、その役割を引き受けるが、やがて、その仮面がはがされていく。1920～30年代を代表する二枚目役者コールマンが、二人の男を演じわける。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I’VE HEARD THE MERMAIDS SINGING", "私は人魚の歌を聞いた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "傷つきやすく、自分に自信の持てない31歳の“落ちこぼれキャリアウーマン”ポリーは、どこにでもいるような女性。アート・ギャラリーに職を得たポリーは、そこでの出来事を見つめ、自分の心の内側を見つめていく。ハート・ウォーミングな作品。", "1987-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mary and Max", "メアリー＆マックス", "", "", "http://image.eiga.k-img.com/images/movie/54997/315x210.jpg?2081", 94, "短編アニメ「ハーヴィー・クランペット」でオスカーを受賞したアダム・エリオット監督の長編デビュー作となるクレイ・アニメーション。ニューヨークに住むアスペルガー症候群の中年男性マックスと、メルボルンで暮らす孤独な少女メアリー。彼らが20年間に渡る文通を通して強い絆を育んでいく様子を、ブラックユーモアを交えながら描く。声優陣には「リトル・ミス・サンシャイン」のトニ・コレット、「カポーティ」のフィリップ・シーモア・ホフマンら豪華キャストが揃う。", "2011-04-23", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUI E＾TES−VOUZ POLLY MAGGOO？", "ポリー・マグー　お前は誰だ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "最新流行の発信地であるパリ。宇宙時代にふさわしいアルミ素材のデザインを売り出すため多くのモデルが集められる。そのなかでも注目されたポリーは消費文明を探ろうとするTV局のドキュメント番組の主役となる。写真家のW・クラインによる、現代のトレンド社会にも通じる風刺劇。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗戦／RUNNING　OUT　OF　TIME", "暗戦・デッドエンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「過ぎゆく時の中で」や「ワンダーガールズ 東方三侠」など、ヒット作を手掛けてきたJ・トーによるサスペンス。末期ガンで余命数週間と宣告されたチャンはある目的のために完全犯罪を計画。その手始めに、大手金融コンサルタント会社に押し入り、人質を取ってビルの屋上に立てこもる。交渉人として重犯課のホー刑事を指名したチャンは、“これはゲームだ。72時間以内に俺を逮捕しろ”と言い残し、厳重な包囲網から逃げ出してしまう。独自に追跡を始めたホーは、チャンの目的が復讐であることを突き止める……。二人の男の間に芽生える奇妙な友情、そして繰り返される虚々実々の駆け引きを、二転三転する巧妙なストーリー展開の中に映し出す。孤独な犯罪者チャンを演じたA・ラウが香港電影金像奨最優秀主演男優賞を受賞した。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIDAY NIGHT LIGHTS", "プライド／栄光への絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ピュリッツァー賞作家として知られるH.G.ビッシンガーのノンフィクション小説を映画化した感動ドラマ。テキサス州に実在する高校アメフト・チームが優勝を目指して奮闘。その険しい道のりをドキュメンタリー風に描く。情熱的なコーチ役を熱演するのは、個性派ビリー・ボブ・ソーントン。'88年。パーミアン高校のアメフト・チーム“パンサーズ”は、5度目の優勝を目指し、シーズン開幕を前に練習に励んでいたが、さまざまな試練が彼らに降りかかる。", "2005-05-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Simpatico", "背信の行方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "S・シェパードの戯曲を映像化したサスペンス調の人間ドラマ。実業家カーターは、旧友のウェッブからの突然の連絡に動揺。彼らは20年前に競馬の八百長事件にかかわり、さらにカーターはウェッブの恋人と駆け落ちしていた。落ちぶれたウェッブは憎きカーターをゆすろうとするが……。過去の苦悩と悔恨にとらわれる人間の弱さ、もろさを浮き彫りにした作品。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPEEDWAY", "スピードウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "プレスリー26本目の主演作。今回は豊かな収入を貧しい人々に分け与える人のよい慈善家青年を演じている。金銭トラブルでやって来た女会計士との恋愛もさらりと描かれている。『その気でいこう』他６曲が披露される。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TRIP TO BOUNTIFUL", "バウンティフルへの旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "息子夫婦とソリがあわず、嫁とケンカの毎日を過ごしている老婦人の夢は、懐かしの生まれ故郷バウンティフルへ帰ることだった。そしてある日思い立ち、旅に出る計画を立てる……。ペイジはもちろんのこと脇役にいたるまで素晴らしい演技を見せる佳作。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("格闘技世界一 四角いジャングル", "格闘技世界一 四角いジャングル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "『巨人の星』の原作で知られる梶原一騎が製作総指揮を務めた、格闘技ドキュメンタリーシリーズの第１作。“プロレスこそ最強の格闘技”と豪語するアントニオ猪木vsモハメド・アリの試合ほか、数々の名試合が楽しめる。DVDタイトルは「四角いジャングル 格闘技世界一」。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RONIN", "RONIN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "　冷戦終結後のパリに、各国で秘密任務に就いていた元スパイたちが集まる。互いの名前さえ知らない彼ら6人はある女性からブリーフケースを奪う指令を受ける。仕事は成功したが、その後ひとりが裏切り、ケースを持って逃走。一体何が起こったのか?　元スパイたちがプライドをかけた諜報戦を繰り広げるハード・アクション。ジョン・フランケンハイマー監督がベテランの手腕を発揮し、大迫力のカーチェイスや銃撃戦を展開。", "1998-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("26 Years Diary", "あなたを忘れない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "2001年1月、JR新大久保駅のホームから線路に転落した男性を救おうとして命を失った韓国人留学生イ・スヒョンさんの半生を、フィクションを交えながら描いた感動ドラマ。兵役を終えて日本に留学した25歳の青年イ・スヒョンは、東京の路上でストリートミュージシャンの星野ユリと出会う。故郷でバンドを組んでいたスヒョンはユリと意気投合し、音楽を通して親交を深めていく。やがて2人の友情は恋へと発展するが……。ユリ役は沖縄出身のバンド「HIGH and MIGHTY COLOR」のボーカル、マーキーが務めた。", "2007-01-27", "76,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天辺一朶雲", "西瓜", "", "", "http://image.eiga.k-img.com/images/movie/33657/315x210.jpg?4527", 112, "台湾映画界の巨匠ツァイ・ミンリャン監督によるラブ・ストーリー。愛に飢えた女性とAV男優の純愛を、過激な性描写や鮮やかなミュージカルシーン満載で描き、2005年度の台湾興行収入第1位を記録した。深刻な水不足が続く台湾の街。海外から帰国したばかりのシャンチーは、以前路上で腕時計を売っていたシャオカンと再会する。次第に惹かれあう2人。しかしシャオカンには、彼女にどうしても知られたくない秘密があった……。", "2006-09-23", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金田一少年の事件簿", "金田一少年の事件簿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "『週刊少年マガジン』で連載開始以来、爆発的な人気を集めている大ヒット・マンガの劇場版アニメ。孤島のホテルで続発する密室殺人事件のナゾに、名探偵、金田一耕助の孫、一（はじめ）が挑む。絶海の孤島にある劇場兼ホテルのオペラ座館で、戯曲『オペラ座の怪人』が上演されることになる。招待されたはじめと美雪が島に到着してから数日後、４年前の少女の自殺を示唆する何者かからの不吉な手紙が舞い込む。やがて主演女優がシャンデリアの下敷きになって死亡。ナゾが解けないまま、再び殺人を予告する手紙が届くが……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Life as a House", "海辺の家", "", "", "http://image.eiga.k-img.com/images/movie/1497/315x210.jpg?3509", 126, "42歳の建築デザイナーが余命4カ月と宣告され、離婚した妻のもとで暮らし、父に反感を持つ16歳の息子とともに、父の住んでいた家を建て直そうと決意する。ふたりは親子の絆を回復することができるのか。監督は「ロッキー」などの製作を経て「真実の瞬間」で監督デビューしたアーウィン・ウィンクラー。脚本は「恋愛小説家」のマーク・アンドラス。撮影は「ディア・ハンター」などのベテラン、ビルモス・ジグモント。", "2002-07-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUNSET GRILL", "ハードラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "P・ウェラーが私立探偵となって活躍するハード・アクション。舞台はロサンゼルス。元FBIの私立探偵ライダー・ハートの前妻アニータが殺された。ハートは、彼女がメキシコからの不法移民であることに、事件のカギがあると直感する。調査の末、不法移民の斡旋組織が、強制的に臓器売買もしていた事実を知る。そしてアニータの正体とは？", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("R.I.P.REST　IN　PIECES", "R.I.P. ジョー・コールマンの肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「エデンへの道」で観客を震撼させたドキュメンタリー作家、R・ペヨが、前衛アーティスト、J・コールマンの過去と現在を取材した作品。漫画家として出発しながら、次第に過激なパフォーマンス活動にのめり込み、近年は特異な作風の絵画制作に没頭しているコールマン。そのアバンギャルドな思想や冷徹な自己批評が、解き明かされていく。彼のファンであるという映画監督J・ジャームッシュとの対話では、一筋縄ではいかないキャラクターと素顔も垣間見られる。", "1997-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LULU ON THE BRIDGE", "ルル・オン・ザ・ブリッジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「スモーク」などで映画参画にも意欲を見せていた人気作家P・オースターの初単独監督作品。発砲事件に巻き込まれたサックス奏者ビリーは楽器を吹けない体となってしまった。打ちひしがれる彼に希望を与えたのが、光を放つ不思議な“石”。そのパワーに導かれ、ビリーは女優の卵セリアと出会い、恋に落ちる。だが、そんな幸福も長くは続かず……。人生に絶望した男の数奇な体験を描く、大都会の寓話というべき物語が展開。観る者に解釈をゆだねた哀切なラストが、深い味わいを残す。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVEN'S PRISONERS", "ヘブンズ・プリズナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "全米でベストセラーとなったJ・L・バーク原作のハードボイルド小説『天国の囚人』の映画化。ニューオリンズ殺人課の刑事だったデイブ・ロビショーは、警察を辞めアル中から立ち直り、愛する妻アニーとともに平和な日々を送っていた。そんなある日、小型飛行機が二人の目の前で海に墜落する。機内にいたのは胸に刺青のある男の死体と、奇跡的に生き残った幼い少女。数日後、少女を引き取り、わが子のように可愛がって暮らす二人のもとへ麻薬取締局の捜査官がやって来る。刺青の男の存在を否定する捜査官に不信感を抱いたデイブは、アニーが止めるのも聞かず独自で捜査を開始するが……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents すき　「SAI-KAI」", "桃まつり presents すき　「SAI-KAI」", "", "", "http://image.eiga.k-img.com/images/movie/57887/315x210.jpg?1746", 27, "若手女性監督による映画製作・上映集団「桃まつり」が、「好き」をテーマに女性監督たちが撮り上げた短編作品を上映する「桃まつり presents すき」の1本。へたれなヤクザのノブはある日、幼なじみのりょうちゃんと再会。同じく幼なじみでノブの恋人の夏美を交えて再会を祝していたが、3人には意外な運命が待ち受けていた……。監督は、映画美学校出身で今回が初監督の名倉愛。「参のすき」として「さめざめ」（星崎久美子監督）、「LAST SHOW」（佐藤麻衣子監督）と同時上映。", "2012-03-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真・女立喰師列伝", "真・女立喰師列伝", "", "", "http://image.eiga.k-img.com/images/movie/34307/315x210.jpg?4290", 123, "「GHOST IN THE SHELL／攻殻機動隊」の押井守が創り出した架空の職業“立喰師（たちぐいし）”をモチーフに、無銭飲食を生業とする6人の女立喰師のエピソードを実写で描いたオムニバス・ムービー。押井自らが2本の監督を務めるほか、テレビ版「攻殻機動隊／STAND ALONE　COMPLEX」の神山健治ら気鋭のクリエイターが集結。出演は「ウルトラセブン」のアンヌ隊員役で知られるひし美ゆり子ほか。", "2007-11-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOKYO JOE", "東京ジョー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "戦後間もない東京を舞台にした、異色のハリウッド映画。第二次大戦前、東京でレストランを経営していたジョーは、８年ぶりに日本へやって来る。その彼に、かつての妻トリーナの暗い過去を公にしたくなければ、韓国から日本人戦犯を逃がす手助けをしろと、ある男が持ちかけて来る……。ボギーと早川雪洲、二大名優の共演も、どこか奇妙に映る珍品だ。DVDは「フィルム・ノワール・コレクション(1)」に収録。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THING", "遊星よりの物体X", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "北極に墜落した円盤の墜落現場から回収した宇宙人の死体は、北極基地の中でやがて融けだし、基地の人々を襲いはじめる。人類よりも知力、腕力にすぐれ、動物の血を吸い、種子によって繁殖する宇宙人との戦いがはじまる……。製作者のホークスが新人スタッフで作り上げた、SF映画の幕開けを告げる傑作。1982年にリメイクされた。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAMP NOWHERE", "僕達のサマーキャンプ　親の居ぬ間に…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "毎年、親の命令で行かされるキャンプがイヤでしょうがないマットは、失業中のデニスの協力で、まんまと架空のキャンプをでっちあげる。マットは友だちを誘い、親の干渉も、指導員も、ルールもない天国のようなキャンプを謳歌する。しかし、あまりの騒々しさに、街の人々も親たちも不信感を抱き始める。これではマズイと、危機を察知したマットは再び悪知恵を働かせる。デニスを巻き込んで急遽、父兄の参観日を計画するが……。アメリカ映画の定番ともいうべき“キャンプもの”を、お気楽な笑いとともに描いている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポケットモンスター・アドバンスジェネレーション／裂空の訪問者デオキシス", "ポケットモンスター・アドバンスジェネレーション／裂空の訪問者デオキシス", "", "", "http://ecx.images-amazon.com/images/I/51a-CXtp0YL._SL210_.jpg", 101, "日本はもちろん、世界中で大人気を誇る“ポケモン”の新シリーズ、“アドバンスジェネレーション”の劇場版第２作。今回は、フォルムが変わる幻のポケモン、デオキシスが登場。その謎を追って、主人公サトシとピカチュウらがハイテク都市・ラルースを駆ける。スリリングな冒険はもちろん、サトシと心に傷を負った少年の友情のドラマも見逃せない。ピカチュウタイプのなごみ系新キャラ・プラスルとマイナンの愛らしさにも注目。４年前、宇宙から落ちる隕石の爆風とポケモンのバトルに巻き込まれた少年トオイ。サトシとピカチュウたちは、ラルースシティで偶然トオイと知り合うが、彼は過去のバトルのせいでポケモンが苦手になっていた……。", "2004-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TIME AFTER TIME", "タイム・アフター・タイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "H・G・ウェルズの小説『タイムマシン』と、19世紀に実在した切り裂きジャックを大胆に結びつけた独創的なサスペンス映画。７件の殺人を犯し、行方をくらましたジャックは何と、タイムマシンで20世紀へ高飛び。それをウェルズ自身が追いかける……。スリルとユーモアたっぷりのSF娯楽作。アヴォリアッツ国際ファンタスティック映画祭グランプリ受賞。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAP ROOTS", "愛と血の大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "開拓者サムは、ミシシッピ河流域のレバノンに土地を求め、広大な領地を開いた。時あたかも南北戦争直前。孫娘のモーナにはクレイ大尉という許婚がいたが、彼は南軍に参加し結婚は延期された。やがてサムが死に、その遺志を継ごうとするモーナの父ホープは、レバノンをミシシッピー州から独立させることを目論むが、やがて南軍はクレイを長とするレバノン討伐隊を組織し……。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALIFORNIA CONQUEST", "征服者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "まだアメリカが現在のような国になっていなかった19世紀。ロシアに属するか、アメリカと合併するかでふた手に別れ、激しい戦いが繰り広げられていたカリフォルニアを舞台に、様々な人間たちの姿を描いた西部劇。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADIEU L’AMI", "さらば友よ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "アルジェリア戦争帰りの軍医と戦争を商売にする男が、ひょんなことからある広告会社の金庫に債券を戻す仕事を頼まれる。二人の男は債券より金を盗もうと思い立つが、金庫の中には死体が……。男の友情をクールに描き、ブロンソンをスターダムに押し上げた好編。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Every Little Step: The Journey of a Phenomenon", "ブロードウェイ♪ブロードウェイ　コーラスラインにかける夢", "", "", "http://image.eiga.k-img.com/images/movie/53537/315x210.jpg?1698", 93, "トニー賞9部門受賞の大ヒットミュージカル「コーラスライン」のオーディションの舞台裏に迫ったドキュメンタリー。監督は、ミュージカル「ヘアスプレー」でトニー賞を受賞したジェームズ・D・スターン。2006年秋、16年ぶりに伝説のミュージカル「コーラスライン」の再演が決まり、応募者ダンサーは3000人を超えた。わずか19名のポストをかけ、熾烈な戦いを繰り広げるダンサーたちをカメラが追う。", "2008-10-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I WILL，I WILL…FOR NOW", "ダイアン・キートン 可愛い女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "再会した元夫婦が、５年間の契約結婚に踏み切る。同じ失敗を繰り返すまいと思うあまり、窮屈な結婚生活を送るが、セックス・クリニックによって新しい絆を発見していく。スワッピングめいた描写もある、ポルノティックなラブ・コメディ。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛の言霊", "愛の言霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "「仮面ライダーカブト」の徳山秀典＆「轟轟戦隊ボウケンジャー」の齋藤ヤスカ共演によるボーイズラブストーリー。高校時代から仲が良かった大谷晋也と立花都は、卒業後は恋人となって一緒に暮らしていた。そんなある日、2人は高校の同級生・水沢雪子と再会する。高校時代から立花と雪子の仲を疑っていた大谷は、雪子の出現に不安を募らせていくが……。「青いうた　のど自慢　青春編」の金田敬監督がメガホンをとる。", "2007-10-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Szabadsag, Szerelem", "君の涙　ドナウに流れ　ハンガリー1956", "", "", "http://image.eiga.k-img.com/images/movie/53067/315x210.jpg?3511", 120, "1956年の“ハンガリー動乱”を背景に、自由を求めて闘う恋人たちの愛と運命を描いた感動作。ソ連による弾圧が続き、自由を求める声が高まりつつあるハンガリーの首都ブダペスト。オリンピック出場を間近に控えた水球選手カルチは、革命を信じて活動する女学生ビキと出会う。それまで政治には関心を持たずにいたカルチだったが、ソ連軍との衝突で多くの市民が犠牲になるのを目の当たりにし、ビキと共に闘いに身を投じていく。", "2007-11-17", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマンコスモスvs　ウルトラマンジャスティス／　THE FINAL BATTLE", "ウルトラマンコスモスvs　ウルトラマンジャスティス／　THE FINAL BATTLE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "好評を博したウルトラマンコスモス・シリーズの完結編。何とウルトラマン同士のバトルが繰り広げられる。人類待望の宇宙輸送ロケットが完成。ところが突如現れた巨大な円盤によって無残にもロケットは破壊され、地球は危機に陥る。この緊急事態にウルトラマンコスモスが駆けつけるが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Catwoman", "キャットウーマン", "", "", "http://image.eiga.k-img.com/images/movie/1327/315x210.jpg?3132", 104, "「ヴィドック」のフランス人監督ピトフがアメコミ映画化に挑戦、コミックを踏まえつつ、独自のキャットウーマン像を創造。内気な広告デザイナー、ペイシェンスは彼女が勤める化粧品会社の企業秘密を知ったことから抹殺されるが、ネコたちの神秘的な力によってキャットウーマンとして復活。自分の中に眠っていた野生のパワーを発揮して、悪と対決していく。ヒロイン役ハル・ベリーと悪女役シャロン・ストーンの肉弾戦も登場。", "2004-11-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLEEPING　DICTIONARY", "スリーピング・ディクショナリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "植民地時代のインドネシアを舞台に英国人青年と現地の娘の許されぬ恋を描く。TVドラマ『ダーク・エンジェル』でブレイクしたJ・アルバが初主演し、ヒロイン役を熱演する。20世紀初頭、若き英国行政官のジョンは植民地のインドネシアに赴任。そこには現地語を覚えるため村の娘とベッドをともにする慣習があり、彼のもとには村一番の聡明な美女、セリマがやって来る……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　GREAT　WALTZ", "グレート・ワルツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "フランスの名匠ジュリアン・デュヴィヴィエがハリウッドで撮った伝記映画。ワルツの王と呼ばれた音楽家ヨハン・シュトラウスの半生を、豪華キャストと華麗な音楽で彩る。銀行を辞め仲間とともに管弦楽団を結成したシュトラウスは、歌姫カーラに才能を認められ、ウィーンの上流社会で成功を収める。やがて彼は貞淑な女性ボルディと結婚するが、カーラとも愛を育み……。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風兒[足易]踏[足采]", "風が踊る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "台北を離れ、香港に住む恋人のもとへ旅立とうとしている女性カメラマンのシンホイ。ある日、彼女は事故で失明したチンタイという青年に出会い、彼の素朴な人柄に惹かれてゆく。その後チンタイは、角膜の移植手術を受けるため、台北でシンホイと再会する。手術は成功し、視力を取り戻した彼は、初めてシンホイの顔を見ることになるが……。台湾の名匠ホウ・シャオシエンのデビュー2作目にして、爽やかなユーモアが漂う恋愛映画。", "1981-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("窓ノ外ノ世界", "窓ノ外ノ世界", "", "", "http://image.eiga.k-img.com/images/movie/54727/315x210.jpg?4241", 60, "都内に小さなショップを開店したばかりのジュン、シロウ、トネリ。お客がなかなか来ないその店でネットサーフィンに興じていたジュンは、ふと素朴だが美しい風景画の掲載されたページに引き込まれ、絵を描いた少女ミサとメール交換するようになる。ミサの描く街に興味を持ったジュンは、ミサに会いにその街へと出かけると、ミサがALSという有効な治療法のない難病を抱えていることを知る。", "2009-07-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Neighbor Zombie", "隣りのゾンビ The Neighbor Zombie", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "韓国の若手監督4人が手がけた全6話構成のオムニバス。全世界に広がったゾンビウィルスによりソウル全域で感染者が続出。政府は戒厳令を発し、感染者の除去を開始する。しかし、家族や恋人など大切な存在がゾンビとなってしまった人々は、政府から除去される前に感染者をかくまい、共存する道を模索していく。", "2010-10-23", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　BIG　BROADCAST　OF　１９３８", "100万ドル大放送", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "往年のミュージカル・スター、B・ホープのデビュー作。ジャイガンディック号とコロッサル号の大西洋横断競争が始まった。ニューヨークからフランスのシェルブールへ向けて出航したジャイガンティック号の持ち主ベロウズ兄弟は、勝つために様々な策を練るが、なぜか失敗ばかり。賞金で先妻３人への慰謝料を払おうと思っている船内放送のアナウンサー、バズも、故障したラジオ機の修理や、新しい恋人の浮気などでパニックに陥ってしまい……。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAT RACE", "ラットレース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ラスベガスからニューメキシコまで、最初にたどり着いた者に賞金2千万ドル。そんなレースに参加した人々の、何でもアリの無法競争を描いたドタバタ・コメディ。車のみならず飛行機やヘリ、果てはロケットまで繰り出す参加者たち。競争相手の足を引っ張り合っては足止めを食らう彼らの醜い争いに加え、このレースを仕組み誰が勝つかを賭けている富豪たちの悪趣味ぶりにも、ブラックな笑いが満ちている。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CREATURES THE WORLD FORGOT", "原始人100万年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "イギリス怪奇映画の雄ハマー・プロが製作した原始時代ドラマ第３作。まだ言葉を持たなかった石器時代が舞台。穴居族の娘ナーラは、兄弟との主導権争いに巻き込まれるが……。具体的なセリフは無く、原始派肉体美人ナーラのエロチシズムとアクションが入り交じる一風変わった作品。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Firehouse Dog", "ファイアー・ドッグ　消防犬デューイの大冒険", "", "", "http://image.eiga.k-img.com/images/movie/34157/315x210.jpg?1504", 111, "ハリウッドのスター犬が消防犬として新たな人生を歩んでいく姿を描いたハートウォーミング・コメディ。高級住宅地で暮らすセレブ犬レックスは、撮影中に飛行機から落下し迷子になってしまう。レックスは火事に巻き込まれたところを消防隊長コナーに救出され、彼の息子シェーンによってデューイと名付けられる。デューイは自らの運動能力を生かして消防活動を手伝うようになり……。シェーンに扮するのは「ザスーラ」のジョシュ・ハッチャーソン。", "2007-09-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霧の夜の男", "霧の夜の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "「激流に生きる男」(1962)からアクション俳優に転じた、高橋英樹の初期の主演作。家業であるヤクザを嫌って船員になった青江舜次は、父が殺人罪の汚名を着せられていると知り、調査に乗り出す。殺人を裏で操っていたのが、悪徳ボス、伍堂と知った舜次は……。ヤクザの家業を捨ててカタギの生活を送る青年という設定は、のちの高橋英樹のヒット・シリーズ“男の勲章”を思わせる。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざの横顔", "やくざの横顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "４年前、兄の仇を討って父に勘当されていた本堂哲平は、数人の男に襲われたところを銀造に救われた。やりきれない気分の哲平は、ふと知り合ったりつ子を心のよりどころにしている。しかし、りつ子は銀造の情婦で、銀造は哲平を狙う殺し屋であった。内田良平が愛する女の幸せのために、自らの命を犠牲にする殺し屋をムードたっぷりに演じている。脇役にも焦点を当てたヤクザ・アクション。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吼えろ鉄拳", "吼えろ鉄拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "養父の死を機に、18年ぶりにアメリカから日本へ戻ってきた青年・譲次。死の間際にあった養父から、双子の兄と姉がいること教えてもらった彼は、亡き実父の弟・一輝を訪ねる。ところが、一輝は譲次の実父から継いだ事業を隠れ蓑に、裏で麻薬の密輸をする暗黒街のボスだった。その事実を知り、その上、兄と姉まで一輝に殺された譲次は復讐を誓う……。千葉真一のアクション監督により、真田広之の華麗なカラテが全編に炸裂。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜の最前線 女狩り", "夜の最前線 女狩り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "傷害罪で服役後、アングラ・バーで働くことになった元ヤクザの淳。だがバーは脱税の疑いで手入れを受け、つぶれてしまう。淳はソープランドへのホステスの紹介料でもうけた金でバーを買い戻すが、巻き返しの機会をうかがう元経営者らの妨害にあって……。風俗の裏と表、そこに巣くう人間像を描き出す。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAR FROM HOME：THE ADVENTURES OF YELLOW DOG", "イエロードッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "アンガス少年とラブラドール・レトリバー犬のイエローは無二の友だち。いつも一緒の二人は、父親のクルーザーでの航海にも連れだって出かけていった。ところが大波にのまれた船は転覆。父親は救助されたものの、二人は行方不明になってしまう。気付いた時、二人は見覚えのない海辺にいた。生きるために、少年と犬の冒険が始まる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Fury", "フューリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "　元アメリカ諜報員ピーターは誘拐された息子ロビンを追って、シカゴへやってくる。ピーターの同僚だったチルドレスは超能力者だったロビンを諜報活動に利用しようと企んでいたのだ。一方、シカゴで暮らしていた少女ギリアンもまた超能力の持ち主だった。ピーターはギリアンの存在を知り、彼女の協力でロビンを捜し出そうとするが……。超能力に絡む陰謀、そしてそれに立ち向かう者の戦いを描くサスペンス・スリラー。スローモーションなどデ・パルマ監督の映像テクニックが冴え渡る。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("会いたくて 愛の殺人者", "会いたくて 愛の殺人者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "過去の記憶を失った青年の周りで起こる猟奇殺人事件。その裏に秘められた真実とは……？　連続殺人、多重人格、ストーカーといった今日的な題材とともに展開していくサイコ・スリラー。主演は、ロック・グループ、PENICILLINのベーシスト、GISHO。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Don't Know How She Does It", "ケイト・レディが完璧（パーフェクト）な理由（ワケ）", "", "", "http://image.eiga.k-img.com/images/movie/57217/315x210.jpg?2566", 90, "「セックス・アンド・ザ・シティ」のサラ・ジェシカ・パーカー主演で、仕事と家庭の両立に悪戦苦闘する女性の姿を描いたコメディドラマ。原作は、イギリス人作家アリソン・ピアソンの小説「ケイト・レディは負け犬じゃない」。ボストンの投資銀行に勤めるキャリアウーマンのケイト・レディは、幸せな家庭もやりがいのある仕事も、すべてを手にした「勝ち組」。しかし、大きな仕事を任されて家族に会える時間は減っていき、仕事と家庭の両立に悩んでいた。そんなある日、魅力的な上司がケイティの前に現れて……。", "2012-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MAP OF THE WORLD", "マップ・オブ・ザ・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "J・ハミルトンの同名ベストセラー小説の映画化。S・ウィーヴァーとJ・ムーアが初顔合わせした人間ドラマ。田舎町に住む平凡な主婦アリスは、親友テレサの娘を預かっている時に不慮の事故で死なせてしまう。良心の呵責にさいなまれるアリス。さらにでっちあげの幼児虐待の罪を着せられたアリスは、家族とともに追い詰められていく。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雷桜", "雷桜", "", "", "http://image.eiga.k-img.com/images/movie/55167/315x210.jpg?3849", 133, "宇江佐真理の原作を、時代劇初挑戦の岡田将生と蒼井優の主演で映画化。徳川将軍・秀斉の十七男として生まれた清水斉道は、母の愛を知らずに育ち、心を病んでいた。一方、瀬田村の山で生まれ育った野性の娘・雷は、豊かな自然の中で自由奔放に生きていた。静養のため瀬田村に向かった斉道は、落雷で根元から折れてしまった銀杏に桜が芽をつけた奇妙な巨木「雷桜」の下で、雷と運命的な出会いを果たす。監督は「ヴァイブレータ」「やわらかい生活」の廣木隆一。", "2010-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’HUMANITE´", "ユマニテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 148, "ナイーブなファラオン警部補は、同じブロックに住むドミノに好意を寄せている。ある日、少女の強姦殺人事件が起こり彼は捜査に乗り出すが、捜査は難航。ファラオンは、ドミノとその恋人ジョゼフに誘われてデートに同行したり、一人の時間を過ごすうち、彼の周りには様々な痛みを伴う出来事が巻き起こる。そんな折り、事件の犯人が捕まったとの一報が入り……。1999年のカンヌ映画祭で審査員グランプリと最優秀主演男優賞＆女優賞をトリプル受賞し、物議を醸した衝撃作。前作の「ジーザスの日々」同様、人間性という主題に挑み、その苦悶の果ての救済を描く。他人の痛みを我がことのように感じ涙を流し、無言で抱きしめることしかできないファラオン警部の姿は、現代のキリストそのもの。出演俳優は、これまで演技経験のまったくない素人たちだが、監督の期待に見事に応え、難役を演じきった。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("濡れた荒野を走れ", "濡れた荒野を走れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "当時の官権による粛清のなか、約１年がかりで実現した反権力映画の力作。ベトナム救済基金を教会から強奪し、女を輪姦した５人組の正体は警官だった。現場にとってかえした彼らは証拠集めならぬ証拠隠滅を敢行。さらにその犯罪は県警ぐるみで学習的に行われていた。という冒頭のみが往時は喧伝されたが、この映画の大半は精神病院から脱走し行きずりの女子高生と逃避行を続ける元警官と、彼の狂気を疑い自分たちの犯罪が露見することを恐れて県警が送り込んだ二人の警官の話である。この道行きはアナーキーな明るさに富んでおり、澤田演出＝長谷川脚本の真骨頂はこれらの描写にうかがえる。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MECHANIC", "メカニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "腕の立つ殺し屋ビショップは、その完璧さから“メカニック”と呼ばれていた。彼は仕事での犠牲者の息子スティーブに会い、珍しく親近感を覚え、彼を助手として使い始める。スティーブもやがて一人前の殺し屋に成長して……。円熟したブロンソンとヴィンセントの組み合わせによる異色犯罪映画。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT　WAS　NOT　THE　NIGHTINGALE", "卒業試験", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「エマニエル夫人」のS・クリステルが主演するエロティックな青春ドラマ。夏休みで祖母の別荘にやってきた高校生パウリ。彼はそこで、家族の乱脈たる性生活を目の当たりにしショックを受ける。そんな中、パウリは隣に住む美しい女性、アンドレアと知り合う。パウリは彼女に興味を持つが、なかなか行動に出せずにいて……。", "1974-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fantastic Four: Rise of the Silver Surfer", "ファンタスティック・フォー　銀河の危機", "", "", "http://image.eiga.k-img.com/images/movie/1947/315x210.jpg?4718", 92, "超能力を駆使する4人のヒーローチーム“ファンタスティック・フォー”の活躍を描き、全米で初登場ナンバーワンを記録した人気シリーズ第2作。ファンタスティック・フォーのメンバー、リードとスーの結婚の話題でもちきりのアメリカに、ある日、宇宙から謎の存在シルバーサーファーが襲来。過去にシルバーサーファーが現れた惑星は必ず8日以内に滅びていることを知った4人は、早速シルバーサーファーを追うのだが……。", "2007-09-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GABRIEL　＆　ME", "天使にさよなら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "イギリスの小さな造船の町を舞台に、天使になろうとする少年の姿を描く感動ドラマ。「リトル・ダンサー」の脚本家L・ホールが、彼の初めてのラジオドラマを自ら脚色した。失業と病のせいでいら立つ父親と、いさかいばかり起こしている11歳のジミー。彼の心の支えは、かつて父親がジミーを空高く抱き上げてくれた時の幸福な記憶だけだ。やがて、天使になって大空を舞いたいと願い始めるジミーの前に、本物の天使が現れる。", "2001-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("何が彼女をそうさせたか", "何が彼女をそうさせたか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1930年前後に量産された左翼思想を色づけした商業映画、一般に“傾向映画”と呼ばれた作品群のうちの代表的な無声映画の傑作。身寄りのない少女が悲惨な体験を重ねるうち、最後には教会に放火して犯罪者となってしまう……。ラストに“何が彼女をそうさせたか”と字幕が出て、当時、観客に大きな感動を与えたという。写真家でもあった鈴木重吉監督の映像センスも冴え渡っており、前衛映画的なショットの数々が本作の画面構成に目覚ましい効果を上げている。", "1930-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lotta pa Brakmakargatan", "ロッタちゃんと赤いじてんしゃ", "", "", "http://image.eiga.k-img.com/images/movie/1827/315x210.jpg?1373", 76, "ニイマン家はスウェーデンの小さな町ビンメルビーに住むとても楽しい家族。パパとママ、お兄さんのヨネスとお姉さんのミア、そして末っ子のロッタちゃんという5人の家族構成。ある雨の朝、風邪をひいてしまったロッタちゃんは、ヨネスとミアと一緒に買い物に行きたいのにママが許してくれない。でも、我慢できない彼女は、黄色いレインコートを羽織ってとうとう雨の中へ飛び出して行った……。", "2000-06-24", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャタピラー", "キャタピラー", "", "", "http://image.eiga.k-img.com/images/movie/55337/315x210.jpg?3812", 84, "第2次世界大戦中の日本。シゲ子の夫・久蔵にも赤紙が届き、勇ましく戦場へと向かったが、戦争から戻った久蔵の顔は無残にも焼けただれ、四肢を失っていた。村中から奇異の目で見られながらも、多くの勲章を得た久蔵は「生ける軍神」として崇められ、シゲ子は戸惑いながらも久蔵の尽きることのない食欲と性欲を埋めていく。やがて日本に敗戦の影が色濃く迫り、久蔵は自ら戦場で犯した悪行に苦しみ始める。第60回ベルリン国際映画祭で、寺島しのぶが最優秀女優賞を受賞した。", "2010-08-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chapter 27", "チャプター27", "", "", "http://image.eiga.k-img.com/images/movie/53137/315x210.jpg?1956", 85, "ジョン・レノン殺害事件の真相に迫る衝撃ドラマ。「アレキサンダー」のジャレッド・レトが殺害犯マーク・デイビッド・チャップマンに扮し、彼が凶行に及ぶまでの3日間の行動と心理を克明に描き出す。1980年12月、ジョン・レノンの熱狂的なファンである青年チャップマンがニューヨークを訪れる。彼の目的は、レノンを殺害すること。空港からダコタ・ハウスに直行した彼は、そこでレノンが現われるのをひたすら待ち続け……。", "2007-12-15", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌ふ狸御殿", "歌ふ狸御殿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "臨戦体制の日本映画界にあって、すでに1939年「狸御殿」でファンタスティックなオペレッタ風喜劇を撮り上げていた木村監督が、さらに徹底したミュージカルとして完成させた作品。人気歌手総出演で森の中の御殿に歌や踊りが繰り広げられていく。軍部は怒ったが興行はヒット。木村監督はこのあと、応召された。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("従軍慰安婦", "従軍慰安婦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "慰安婦として連行された女性たちの苦しみを赤裸々に映画化、韓国映画史上かつてない問題作として論争を呼んだ作品。1943年、スニが日本軍に連行された先はフィリピン。そこで待っていたのは、従軍慰安婦としての過酷な日々だった。純潔な乙女だったスニの肉体は、昼夜を問わず日本兵に汚され、ボロボロになっていく。ある者は将校に反抗して死刑になり、ある者は脱走に失敗して自ら生命を絶った。スニは同国人のヨウン二等兵に恋心を抱くが、ヨウンは前線に送られ、やがて激化したアメリカ軍の反撃に日本軍は撤退。置き去りにされたスニたちを待ち受けていたのは、砲弾の炸裂するジャングルを逃げまどう、さらなる地獄だった。", "1993-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オッパイ星人", "オッパイ星人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "個性派俳優の遠藤憲一や、人気グラビア・アイドルの森下千里らが出演した中編コメディ。オッパイを神聖な存在として崇める宇宙人たちによる地球侵略をギャグ満載で描く。CG合成で宇宙人の体と合体し、“顔だけ”の出演を果たした俳優たちのコミカルな熱演が、観る者の笑いを誘う。宇宙の彼方で、オッパイを崇め奉るオッパイ星人たち。彼らは神聖なるオッパイを汚す地球人の存在を知り、侵略を開始するが……。", "2005-04-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陽のあたる坂道", "陽のあたる坂道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "かつて、石原裕次郎主演で映画化された石坂洋次郎の原作を、渡哲也主演でリメイク。３時間弱の堂々たる大作だった前作に比べ、安手な作りになっていることは否めない。自分が妾腹だと知った青年が、実の母が異母妹の家庭教師と同じアパートに住んでいることを知る。彼は実の母に近づくが……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAPARAZZI", "パパラッチ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「カンヌ映画祭殺人事件」のA・ベルベリアンの監督第２作。有名人を追い回す“パパラッチ”たちが繰り広げるスクープ合戦の舞台裏を描くブラック・コメディ。ジャーナリストの不倫現場を収めたスクープ写真に映ってしまったフランクが、そのため仕事をさぼっていたことがバレて会社をクビにされてしまう。雑誌社に怒鳴り込むが、なぜかトップ・クラスのパパラッチ、ミシェルと行動をともにすることになり……。イザベル・アジャーニら大物スターが、激写される有名人として出演。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("本日ただいま誕生", "本日ただいま誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "第二次大戦中、戦場で右肩の自由を失い、抑留されていたシベリアの極寒の中で凍傷にかかり両足を切断した大沢雄平は、帰国後仏門に入り、不自由な身体をひきずって日本中を行脚する。原作は曹洞宗大垣法永寺住職、故・小沢道雄師が自らの数奇な運命を著した同名の小説。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("髪がかり", "髪がかり", "", "", "http://image.eiga.k-img.com/images/movie/53667/315x210.jpg?4674", 103, "不思議な散髪屋に出会った人々の物語を描いた山本甲士の連作短編小説「わらの人」（文藝春秋社刊）を、「いかレスラー」「日本以外全部沈没」の河崎実監督が映画化。不幸せな人々を、散髪することで気持ちを新たにさせる不思議な女理容師をベテラン女優の夏木マリが演じる。", "2008-05-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アキハバラ電脳組／2011年の夏休み", "アキハバラ電脳組／2011年の夏休み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "1998年にテレビ放映されたSFアクション・コメディ・アニメの劇場版。地球の危機を救った美少女サイバー・チーム、アキハバラ電脳組は中学２年の夏休みを楽しく過ごしていたが、アキハバラの地面が急上昇するという大異変が起きていた。一方、電脳組のメンバーにも謎の組織の魔の手が伸び……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRY FREEDOM", "遠い夜明け", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 158, "アパルトヘイト政策下の南アフリカ。黒人運動家スティーブン・ビコを批判した記事を書いたことから、新聞社の編集長ドナルド・ウッズはビコと会うことに。ウッズはビコの人間性に魅せられ、現実のアパルトヘイトに目覚めていく。だが当局の弾圧は一段と厳しさを増し、ビコは逮捕され、拷問の末に帰らぬ人となった。ウッズはビコの真の姿と、この国の現状を全世界に伝えるため、一家で国を脱出することを決意。1977年大晦日、ついにその決行の日は来た……。長年人種問題に取り組んだ巨匠アッテンボローの力作で、ウッズが自らの体験を書いた原作の映画化。各国の上映では、様々な論争を巻き起こした。", "1987-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Meet John Doe", "群衆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「或る夜の出来事」のフランク・キャプラ監督、ロバート・リスキン脚本の名コンビが贈る社会風刺ドラマ。解雇されそうになった女性記者アンは、ジョン・ドーという人物が社会抗議のために自殺しようとしているという内容の記事をでっちあげる。大反響を呼んだその記事を利用して発行部数を伸ばすため、オーディションで選ばれた元野球選手の男がドーを演じることに。一躍時の人となった彼は、民衆の偶像として祭り上げられるが……。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOO MUCH SUN", "ストレンジ・ピープル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "L.A.の大富豪の娘ピッツィと息子ソニーは二人とも同性愛者。ところが、“遺産は子供をつくった方に託す”との父の遺言で大慌て。子供ができなければ、莫大な遺産はすべて悪徳神父に渡ってしまう。そんな矢先、ピッツィには16歳の時に生んだフランクという息子がいることが発覚。ソニーはあせるし、神父はフランクを拉致する計画まで立てる始末。おまけに悪事をたくらむフランクの相棒も現れて……。R・ダウニーJr.、R・マッチオ共演の爆笑コメディ。“モンティ・パイソン”出身のE・アイドルが、子供をつくらなければならなくなったゲイの役で登場するのも見もの。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE END OF THE AFFAIR", "情事の終わり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "第二次大戦中のロンドン。激しい恋に落ちた作家と人妻の心理的葛藤を描いた恋愛ドラマの傑作。G・グリーンの小説をE・ドミトリクが監督、きめの細かい心理描写で奥深いドラマに仕立てている。往年の美人女優D・カーが、夫と愛人、そして神との掟の間で揺れ動く人妻を知的に演じている。", "1954-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Quarantine 2: Terminal", "REC レック　ザ・クアランティン2 ターミナルの惨劇", "", "", "http://ecx.images-amazon.com/images/I/51ulVyHt3AL._SL210_.jpg", 0, "", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Texas Chainsaw Massacre: The Begining", "テキサス・チェーンソー　ビギニング", "", "", "http://image.eiga.k-img.com/images/movie/33737/315x210.jpg?2492", 92, "名作ホラー「悪魔のいけにえ」をリメイクして全米興行収入第1位を記録した「テキサス・チェーンソー」の続編。チェーンソーで殺戮を繰り返す凶悪な殺人鬼レザーフェイス誕生の秘密が明かされる。1939年、ある食肉工場で赤ん坊が産み落とされ、すぐにゴミ箱に捨てられた。ゴミを漁っていた女に拾われトーマスと名づけられた赤ん坊はヒューイット家で育てられ、やがて食肉工場で働き始めるが、工場閉鎖にショックを受け工場長を殺害してしまう。", "2006-11-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("薄桜記", "薄桜記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "五味康祐の新聞連載小説を市川雷蔵主演で映画化した正統派時代劇の名作。丹下典膳は留守中最愛の妻が、知心派の５人組に犯されたことを知り苦難の末、５人を捜し出し復讐を果たすが、自らも傷つき妻とともに果てる。この典膳と、勝新太郎扮する中山安兵衛との交友を絡ませ、二人の剣士の明暗を赤穂浪士の討ち入りを背景に映し出した内容になっている。森一生の重厚な演出と本多省三の華麗なカメラワークが、非業の最期を遂げた剣豪・丹下典膳の半生を浮き彫りにする。特にラスト、片手片足に深傷を負い戸板に寝かされながら、多数の敵と必死に闘う丹下典膳のデカダンスな美しさは筆舌につくしがたいものがある。丹下典膳に扮した市川雷蔵が素晴らしい演技をみせ、彼の代表作となった。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スパルタの海", "スパルタの海", "", "", "http://image.eiga.k-img.com/images/movie/56827/315x210.jpg?2848", 105, "家庭内暴力を起こした少年・俊平が、非行少年や不登校児の更生施設として知られる愛知県美浜市の戸塚ヨットスクールに送り込まれる。入校初日から校長の厳しい体罰を受けても反抗を止めない俊平だったが、嫌々ながらもヨットの訓練に参加するようになる。俊平はそこで、壮絶なしごき、突然の仲間の死、卒業していった仲間アッコへの恋など、さまざまな経験を積んでいく。1982年、生徒の相次ぐ死亡事故で校長ら関係者が逮捕される事態に発展した「戸塚ヨットスクール事件」の影響で公開が中止された青春映画。2011年劇場初公開。", "2011-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents 真夜中の宴　「明日のかえり路」", "桃まつり presents 真夜中の宴　「明日のかえり路」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 17, "", "2008-03-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("・ふ・た・り・ぼ・っ・ち・", "・ふ・た・り・ぼ・っ・ち・", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "榎戸耕史が独特のタッチで丸山昇一の脚本を映像化した初監督作。都会に暮らす孤独な男と女のわずか24時間のラブ・ストーリー。大きな事件が起きるわけではなく、すれ違いながらも最後は一つに解けあっていく、若い二人の心の動きを静かに見つめている。キャリアウーマンに憧れるものの、ままならない22歳のOL広子と、ロックンローラーを目指したがモノにならず、今はしがないPR制作マンの29歳の健二。見合いで出会った二人は、会話も好みもかみ合わない。一度は別れたものの、その日のうちに再会し、奇妙な運命の糸は二人を近づけていく。元BARBEE BOYSの近藤敦と古村比呂が等身大の若者像を好演している。結ばれるでもなく別れるでもないラストはこの映画にふさわしい。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Appaloosa", "アパルーサの決闘", "", "", "http://ecx.images-amazon.com/images/I/51yjqHSJbgL._SL210_.jpg", 108, "ハードボイルド作家ロバート・B・パーカーの同名小説を、「ポロック 2人だけのアトリエ」のエド・ハリスが製作・監督・脚本・主演を務めて映画化したウェスタン映画。無法者のブラッグ一味から罪なき人々を守るため、アパルーサの町へやって来た保安官バージル・コールと相棒のエベレット・ヒッチ。しかし、美しい未亡人アリソン・フレンチの登場により、2人の運命は狂わされていく。共演にはビゴ・モーテンセン、レニー・ゼルウィガーら豪華キャストがそろう。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Afterwards", "メッセージ　そして、愛が残る", "", "", "http://image.eiga.k-img.com/images/movie/53927/315x210.jpg?1857", 107, "ギヨーム・ミュッソによるフランスのベストセラー小説を、「スパニッシュ・アパートメント」のロマン・デュリス主演で映画化。共演に「LOST」のエバンジェリン・リリー、ジョン・マルコビッチら。ニューヨーク在住の敏腕弁護士ネイサンは、幼い息子を亡くしたショックから立ち直れず、仕事に没頭する日々をおくっていた。そんな彼の前に人の死を予見できる医師のジョセフが現れ、ネイサンは自分に死期が迫っていることを悟る。ネイサンは別れた妻とのきずなを取り戻そうとニューメキシコに向かうが、そこには予想もしない真実が待っていた。", "2010-09-25", "7,2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Welcome to Collinwood", "ウェルカム・トゥ・コリンウッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "G・クルーニーとS・ソダーバーグが設立した映画制作会社“セクション・エイト”の第１回作品。ユーモラスなストーリーと個性派俳優たちの、とぼけた演技が絶妙だ。成功確実のおいしい強盗計画の情報を仕入れたペロら６人。金庫破りの達人、ジャージーのアドバイスを受け、彼らは意気揚々と計画を実行に移す。しかし、全員がドジの踏みっぱなしで……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泥だらけの純情", "泥だらけの純情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "大使を父に持つ財閥の一人娘と、ふと知り合った若いヤクザが恋に落ち、抗争に巻き込まれていく姿を描くアクション・タッチの純愛メロドラマ。1963年に吉永小百合、浜田光夫主演で映画化された作品のリメイク版で、百恵＝友和コンビ６本目の出演作。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("亀は意外と速く泳ぐ", "亀は意外と速く泳ぐ", "", "", "http://image.eiga.k-img.com/images/movie/1467/315x210.jpg?4070", 90, "「イン・ザ・プール」で映画監督デビュー、シティボーイズの舞台の作・演出で知られる三木聡監督の第2作は、主婦が主人公の脱力系コメディ。夫が海外赴任中の平凡な主婦スズメは、話し相手がペットの亀だけだという毎日に飽きて「スパイ募集」の貼り紙に応募。スパイとして雇われて「平凡な日々を過ごす」という指令に基づいて暮らすようになるが……。「スウィングガールズ」の上野樹里、「花とアリス」の蒼井優が共演。", "2005-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brodeuses", "クレールの刺繍", "", "", "http://image.eiga.k-img.com/images/movie/1337/315x210.jpg?3840", 88, "スーパーのレジ係として働く17歳のクレールは、望まない妊娠をして仕事を辞めるはめになり、刺繍職人のメリキアン夫人のアトリエで働くことに。夫人は最愛の一人息子を事故で亡くしたばかりで悲しみに沈んでいたが、2人は刺繍を通して心を通わせていく。監督が自分の妊娠中に脚本を書き始め、3年の歳月をかけて脚本を執筆。主演は「イブラヒムおじさんとコーランの花たち」で注目を集めたローラ・ネマルク。04年カンヌ国際映画祭で批評家週間グランプリ受賞。", "2005-09-03", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆走兄弟レッツ＆ゴー!!　WGP爆走ミニ四駆大追跡", "爆走兄弟レッツ＆ゴー!!　WGP爆走ミニ四駆大追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "男の子の間にミニ四駆ブームを巻き起こした人気マンガの初の劇場アニメ化。謎の暴走マシン、ガンブラスターXTOを追う烈と豪の星馬兄弟の活躍がスピード感たっぷりに描かれる。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LET’S SPEND THE NIGHT TOGETHER", "ザ・ローリングストーンズ ～レッツ・スペンド・ザ・ナイト・トゥゲザー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1981年のザ・ローリング・ストーンズ全米ツアーから、アリゾナ州テンピとニュージャージー州イースト・ラザフォートセの２ヵ所のコンサートの模様を映し出した記録映画。数万人の大観衆や、風船、花火を使った派手なステージ演出を20台のカメラとヘリコプター空撮を駆使して活写。ミックの魅力全開、ストーンズ・ファン必見もの。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HASARDS　OU　COI¨NCIDENCES", "しあわせ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "名匠C・ルルーシュによる感動的な愛のドラマ。愛する人を失い、生きる希望をなくした女性の癒しと再生の心の軌跡をたどる。夫に裏切られたミリアムは、旅行中に知り合った画家ピエールと恋に落ち、新しい生活を始める。だが、幸せはつかの間、彼女は一人息子とピエールを海難事故で失い、失意の底に突き落とされる。一方、ミリアムら３人が映っているビデオカメラを入手した大学教授は、ミリアムに魅了され、その行方を探し始める……。偶然の重なりで引き合わされた人と人の出会い、そこから生まれる温かい心の触れ合いを、少ないセリフとやわらかな映像で感情豊かに描き出す。", "1998-01-01", "2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JESUS OF NAZARETH", "ナザレのイエス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 195, "名匠F・ゼフィレッリが手掛けた、イエス・キリストの伝記物語。ガラリヤ地方で生まれ、救世主として貧しい裳の病める者に希望を与え、反逆罪で処刑される。聖書でもおなじみのイエス・キリストの物語を、ストレートな演出で描いている。もともとは約６時間のTVシリーズとして製作され、日本では短縮版が劇場公開された。", "1977-01-01", "3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN AMERICAN IN PARIS", "巴里のアメリカ人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "MGMで数々のミュージカルを生み出してきた名プロデューサー、A・フリードがジョージ・ガーシュインの曲をモチーフに製作した傑作。ジェリーは第二次大戦でパリに来て以来、戦後も絵の勉強に精を出している画家。彼の友人でピアニストのクックの夢も大きかった。金持ち娘が絵を買ってくれるのだが、どうも絵より彼の方に興味があるらしい。ところが、一緒に出かけたクラブでジェリーは美しいパリジェンヌに一目惚れしてしまう。10の名曲が踊りや唄のナンバーとして全編に散りばめられ、特にラストの17分に及ぶダンス・シーンは圧巻。ハリウッド・ミュージカルの金字塔と呼べる作品。", "1951-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nuovo cinema paradiso", "ニュー・シネマ・パラダイス／3時間完全オリジナル版", "", "", "http://ecx.images-amazon.com/images/I/51Nu20bEvoL._SL210_.jpg", 175, "　シチリアの田舎町にある、庶民にとって唯一の娯楽場「パラダイス座」を舞台に、父親がいないトトと映写技師アルフレードの長年の友情を綴った名作。初公開版より約50分長くなり、トトの青春時代の恋愛エピソードや、シチリアへ戻った中年時代のその後が詳細に描かれている。パラダイス座では、「どん底」「駅馬車」など1930〜50年代の名作が数々上映され、ノスタルジックな雰囲気やラストシーンは、映画好きにはたまらない。", "1989-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仇討 崇禅寺馬場", "仇討 崇禅寺馬場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1928年、にマキノ雅広が監督した「崇禅寺馬場」のリメイク作品。本多家武術指南の生田伝八郎は、武芸大会でまだ若輩の宗左衛門に負け、お役御免となった。ふとした弾みで宗左衛門を切ってしまった伝八郎は領内から遁走し、沖仲仕の頭・万造のもとに隠れる。万蔵の娘・お勝は伝八郎にほのかな恋心を寄せる。自分を仇と狙う者が現れたことを聞いて覚悟を決める伝八郎だが、お勝は伝八郎の身を案じ、手下の人足を集め始める。マキノ監督が得意としている明るい軽妙なタッチの作品でなく、暗い情感が全編にほとばしっている。心ならずも裏切り者の汚名を着せられ、崇禅寺の決闘でもお勝が助っ人を頼んだことで生き残ってしまい、世間からの糾弾で気がふれてしまう悲運のアンチヒーロー・伝八郎役で、大友柳太朗が今までにない熱演を見せる。千原しのぶも、恋する男を想うがあまり、逆に堕落させてしまうお勝を力まず好演。後半、伝八郎の気がふれてからは怪奇性を帯び、作品に不思議な魅力を与えている。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LEMON POPSICLE", "グローイング・アップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「アメリカン・グラフィティ」方式で、往年のオールディーズをBGMに高校生たちの青春を描いたイスラエル映画。親友ボビーの子を身ごもった美少女ニキに恋しながらも、あえなくフラれてしまう主人公ベンジーの姿が多くの共感を呼び、日本でも大ヒットした。プロデュースが量産コンビ、ゴーラン＆グローバスだけに、続々シリーズ作が製作されることになる。", "1978-01-01", "46,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAN　TADEUSZ", "パン・タデウシュ物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 154, "巨匠A・ワイダが、ポーランドの国民的詩人A・ミツキエヴィチの長編叙事詩『パン・タデウシュ』を映画化。舞台は19世紀前半、ロシア支配下に置かれたリトアニアの農村。この土地で『ロミオとジュリエット』のように長らく対立する二つの家の関係が、それぞれの子供同士が恋に落ちたことをきっかけに大きく変化していく様を描き出す。美しい田園風景、情熱的なセリフ回しで感情をぶつけ合う役者たちのアンサンブルに引き込まれる文芸大作。", "1999-01-01", "42,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WATCHERS", "ウォッチャーズ 第３生命体", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "政府の極秘研究が生んだ二頭の知的生命体が実験室から脱走する。一頭は優れた知能を備えたゴールデン・レトリバー犬“GH3”、そしてもう一頭は、純粋な凶器として20体作られた生物のうち、最も凶暴で、犬に対して激しい敵意を持つ“オックスカム７号”。GH3は高校生の少年トラヴィスに助けられるが、オックスカム７号がすぐそばまで追ってきていた……。奇想天外な設定で描くSFサスペンス。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SILVER CHALICE", "銀の盃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "キリスト最後の晩餐で使われた銀の聖杯にまつわる物語を描いた歴史劇。聖杯の枠を作るよう12使徒の一人、ルカから命じられた銀職人のバジル。枠を完璧なものとすべく彼は、12使徒を一人ずつ訪ね歩き、様々な冒険、ロマンスにめぐり会う。P・ニューマンが銀職人バジルを演じている。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東南角部屋二階の女", "東南角部屋二階の女", "", "", "http://image.eiga.k-img.com/images/movie/53747/315x210.jpg?2839", 104, "父親が莫大な借金を残して死んでしまった野上は、祖父の古アパートの建つ土地を売却することで借金を返済しようと考えていたが、祖父・友次郎は土地を手放すつもりはなかった。突発的に会社を辞めてしまった野上と後輩の三崎、不安定な生活を結婚で解消しようとする涼子、3人は吸い寄せられるように友次郎の古アパートに集まる。監督は本作が劇場デビュー作となる池田千尋、出演は西島秀俊、加瀬亮ら。", "2008-09-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Guardian", "ガーディアン／森は泣いている", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "　夫婦フィルとケイトはロスアンゼルスにある森のはずれの広い家に越してきた。赤ん坊が生まれたばかりの彼らはカミラという若い女性を住み込みのベビーシッターとして雇うことに。しかしカミラには秘密があった。彼女は森の樹木と奇妙な関係を持っていたのだ。やがて怪奇現象が赤ん坊の身を脅かす。フィルとケイトは我が子のために戦いを挑むが……。樹木に宿る悪霊の恐怖を描いたオカルト・スリラー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛と悲しみと", "愛と悲しみと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "西伊豆のミカン山に住む由紀子は東京の医学生・武との縁談が進んでいるが、武は実は由紀子の姉・弥江子が結婚前に心をよせた相手でもあった。夏休み、東京から伊豆に遊びに来た武と弥江子が久しぶりに顔を合わせる。そして禁じられた恋が燃え上がる……。みずみずしいキャストで描く恋愛悲劇の佳作。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新宿酔いどれ番地 人斬り鉄", "新宿酔いどれ番地 人斬り鉄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "中年になってもなかなか組織に受け入れられず、チンピラの仕事ばかりあてがわれる、中年ヤクザの悲哀が胸にしみる小平監督の意欲作。菅原文太はまさにはまり役で、暴力でしか自分を表現できない凶暴な中年ヤクザをのびのびと演じている。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夜ごとの夢", "夜ごとの夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "夫に逃げられたおみつは、幼い子を養うため港町の酒場で身を売っていた。そこへ夫が突然帰ってくるが、喜びもつかのま、子供がケガをしてしまう。治療代に困った揚げ句、夫はある会社の金庫を襲う……。下層階級の人間たちの葛藤を、サスペンス豊かに描き出した成瀬巳喜男監督初期の代表作。小津作品で知られる池田忠雄が脚本を担当。", "1933-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YAJAMAN", "ヤジャマン　踊るマハラジャ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "「ムトゥ／踊るマハラジャ」のラジニカーントとミーナの共演によるマサラ・ムービー。“２”と名打っているが、「ムトゥ…」とのドラマ的な関連はない。村人から尊敬されるヤジャマンは憧れの美女と結婚。しかし、恋敵が卑劣な手段で報復に出た。それを知ったヤジャマンは怒りを爆発させ、敵地に乗り込む。歌と踊りのスペクタクルに加え、涙の悲恋物語と怒りのアクションもあり、娯楽味は盛りだくさん。ビデオタイトルは「ヤジャマン　踊るパラダイス」。", "1993-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOCE EN GALILE´E", "ガリレアの婚礼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "イスラエル軍の占領下にあるパレスチナのガリレアで、村民が伝統的なアラブの結婚式を挙げるまでを描いたM・クレイフィの劇映画デビュー作。平穏なたたずまいを見せるパレスチナの農村。だが、現実は厳しく、イスラエル占領軍が絶えずパトロールし、村民たちの生活は軍の監視下にある。そんなある日、村長ムクタールは息子の結婚式をアラブの慣習通りに挙げたいと思い、イスラエル軍の司令官に許可を願い出た。司令官は軍政のアピールにと結婚式を許可するが、血気にはやる青年たちは、この際に司令官を暗殺しようと企てる……。珍しいアラブの伝統的な結婚式を背景に、パレスチナの現実を浮き彫りにした力作。", "1987-01-01", "37,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミスター・ミセス ミス・ロンリー", "ミスター・ミセス ミス・ロンリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "ある男が、電柱に手錠でくくられた女をひろい、二人の奇妙な同居生活が始まる。その二人と謎の男・三崎が、15億円を手に入れる計画を練るが……。主演の原田美枝子が、いつも鼻をグスグスいわせている女を熱演、自らも製作に乗り出した意欲作。脚本の刹那は、彼女のペンネーム。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖僧", "妖僧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "厳しい山嶽仏教の修業に100人のうちでただ一人耐え抜いた行道は、恐るべき魔力を秘めた法力を獲得した。法力で女帝の病を治し、その信任を厚くした行道は、名を道鏡と改めて宮廷にとどまり、やがて女帝の深い寵愛を受けて法王の位を授けられる……。耽美派で知られる衣笠貞之助監督の異色王朝もの。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("C.I.A. CODENAME ： ALEXA", "地獄のアレクサ 殺人捜査網", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "子持ちの美人テロリスト、アレクサと彼女に恋をしたCIAのマーク、そして一匹狼の刑事ニック。立場の違う３人が手を組んで、世界の核兵器をコントロールできるマイクロ・チップを国際犯罪組織の手から奪還しようとする。L・ラマス、K・キンモント夫婦のおしどり共演によるバイオレンス・アクション。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沖縄10年戦争", "沖縄10年戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "本土復帰を間近に控えた沖縄を舞台に、沖縄進出を企てる本土系暴力団とそれを阻止しようとする地元暴力団の激烈な抗争が火ぶたを切る。現地の事情を理由に沖縄ロケが禁止され、ようやく完成したものの封切りが遅れて、沖縄では上映禁止となった。まさに呪われた実録風ヤクザ映画である。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Till There Was You", "ゴールド・レイダース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "甘いマスクの二枚目俳優M・ハーモン主演の宝探しアドベンチャー映画。兄を訪ねて南太平洋の小島に来たフランクは、すでに兄が殺されていることを知る。犯人探しのため、兄の友人ビブに会うが、彼は何も話したがらなかった。やがてビブの妻アンナから聞いた重大な事実——兄は金塊を積んだまま海に沈んだ米軍機を発見したというのだ。フランクはアンナとともに、兄殺しの犯人と宝を探しに小島の奥地ジャングルへ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GANGSTER NUMBER 1", "ギャングスター・ナンバー1", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "ギャングのボスにのし上がった男の光と影を描く犯罪ドラマ。風刺とユーモア、バイオレンスを散りばめた、「アシッド・ハウス」の俊英ポール・マクギガンによる演出が光る。1960年代、若きギャングが組織のボス、フレディを罠にはめて頂点に。憧れのフレディのやり方を真似て、彼は組織を巨大化させる。30年後、今なお裏社会で幅を利かせる彼の前に、出所したフレディが現われ……。", "2000-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRUNKER ： MY NAME IS BILL W.", "ジェームズ・ウッズのドランカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ニヒルな表情と射るような視線で個性的演技をみせるジェームズ・ウッズ。過去に傷を持ち、エキセントリックな役柄を得意とする彼だが、この作品では1930年代無法と混乱が渦巻くアメリカを、波乱万丈に生き抜く男を演じる。幸福な家庭を持つ一人の証券マンが、1929年の世界恐慌によりどん底につき落とされ、やがては酒びたりの日々を送ることになる。人生の落伍者と化していく彼をつなぎ留めるものは何なのか……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チェーン／連鎖呪殺", "チェーン／連鎖呪殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "“チェーンメール”を基にした都市伝説ホラー第２弾。実際に日本中を駆けめぐったメールをモチーフに、不条理な惨劇を描き出す。携帯電話で怪メールを受け取った友人が次々と怪死する不幸に見舞われた女子高生のサキ。不安に駆られる彼女は、メールの出どころを調べることに。やがて彼女はある人物にぶつかる。", "2006-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Night At The Roxbury", "ロクスベリー・ナイト・フィーバー", "", "", "http://ecx.images-amazon.com/images/I/31AX6A61NPL._SL210_.jpg", 82, "アメリカの人気コメディ番組「サタデー・ナイト・ライブ」から生まれた青春おバカ・コメディ。父親が営むシルクフラワーの店を手伝いながら、夜はディスコに通う落ちこぼれ兄弟ダグとスティーブ。高級ディスコ「ロクスベリー」に憧れる彼らだったが、いつも門前払いされ中に入ることすらできずにいた。そんなある日、ロクスベリーの常連客である人気スター、リチャードに車を追突されたことをきっかけに2人の運命は一変する。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あゝ野麦峠−新緑篇−", "あゝ野麦峠−新緑篇−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "女工たちを酷使した信州・岡谷の製糸工場を舞台に、検番に犯されて彼の女となってしまった女工や、他工場から優秀な女工を引き抜く冷酷な出世志向の青年など、様々な人間模様を綴っている。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORGUE", "モルグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "死体置場（モルグ）で働く青年が巻き込まれる謎めいた事件を描くサイコ・ホラー。法科の学生マーティンはモルグで夜警のアルバイトを始める。ある夜、連続殺人鬼によって皮を剥がされた娼婦の死体が運び込まれた。翌日、動くはずのない死体が移動しているのを発見したのをきっかけに、彼に妄想癖・屍姦マニアの疑いがかかり始める。さらに彼が友だちとの賭けでデートした娼婦のジョイスが死体で発見され、警察もマーティンを容疑者と断定するが……。", "1994-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コント55号とミーコの絶体絶命", "コント55号とミーコの絶体絶命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "湘南市役所の“何でもやる課”に15年も勤める次郎には、父母を失った後面倒を見続けて育て上げた弟、金作がいた。金作も次郎と同じ職場で働いていたがある日、金作は次郎を追い越し同課の係長に昇進。とたんに上司風を吹かして次郎をコキ使う。コント55号のコンビによる最後の映画作品である。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("本流", "本流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "野外活動の育成機関“日本アウトワード・バウンド・スクール”に参加した女性たちに密着するドキュメント。立場も考え方も違う女性たちが、新しい自分を求めて過酷なトレーニングに挑む。“変わりたい”との一心で、見栄もプライドも捨てた彼女たちの一生懸命さこそ、今最も必要とされるものと感じさせる１作。12人の女性が、長野県の冒険教育学校で育成プログラムに参加。小田原から日本海までの人力横断を皮切りに、トレーニングが開始される。", "2004-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEDICATO A UNA STELLA", "ラスト・コンサート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "場末の酒場のピアノ弾きに落ちぶれたピアニストが、ある日、余命いくばくもない少女と出会う。少女の生き抜こうとする意志の強さに胸うたれた男は、いつしか自分を取り戻すが……。美しい音楽をバックに繰り広げられるラブ・ストーリー。", "1976-01-01", "4,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Trotsky", "少年トロツキー", "", "", "http://image.eiga.k-img.com/images/movie/54967/315x210.jpg?4590", 113, "俳優としても活躍するジェイコブ・ティアニーがメガホンを取ったカナダの学園コメディ。自らをロシアの革命家レオン・トロツキーの生まれ変わりだと信じてやまない高校生レオン・ブロンスタイン。父親が経営する工場の従業員にストライキを呼びかけた罰として公立高校に転校させられてしまった彼は、学校の指導方針に異議を唱えて校内に革命を呼びかける。主演は「トロピック・サンダー／史上最低の作戦」のジェイ・バルチェル。", "2009-10-20", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("倩女幽魂／A　CHINESE　GHOST　STORY", "チャイニーズ・ゴースト・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "世界３大奇書の一つといわれる『聊斉志異』の一編をSFXを駆使してまとめ上げた力作。舞台は、いつとも知れぬ古き時代。苦学の旅を続ける若者ニンは、とある古寺で妖艶な美女と出会い恋をする。ところが彼女は、悪の妖怪が人間の生気を吸い取るために利用している操り人形。それを知ったニンは、彼女を救うために一人の道士とともに妖怪に戦いを挑んでいく。コミカルな味も残しながら、格調高い東洋の幻想的エロティシズムとアクション、ホラーなどの要素が混然一体となって幽幻なラブ・ストーリーが繰り広げられる。美人女優J・ウォンもその世界にとけ込んでいる。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロード・オブ・ザ・リング 三部作〈スペシャル・エクステンデッド・エディション〉", "ロード・オブ・ザ・リング 三部作〈スペシャル・エクステンデッド・エディション〉", "", "", "http://ecx.images-amazon.com/images/I/51HZK565WYL._SL210_.jpg", 681, "鬼才ピーター・ジャクソンが３部作で映画化した作品を、DVD発売用に新たに再編集した『エクステンデッド版』。「旅の仲間」「二つの塔」は、最終章「王の帰還」の日本公開にあわせて劇場公開が実現した。「旅の仲間」は約30分、「二つの塔」は約40分、「王の帰還」は約50分の映像を追加。オリジナル版でカットされた旅の仲間たちの隠れたエピソードや後につながる伏線も多数含まれている。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IF　IT　SNOWS　ON　CHRISTMAS", "クリスマスに雪が降れば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "TVドラマ『冬のソナタ』のパク・ヨンハとキム・ヒョンジュの映画デビュー作。“韓流もの”の典型的なツボを押さえた三角関係のラブ・ロマンスが繰り広げられる。彼女の元に、12年前の初恋の相手で今も想い続ける青年が海外から帰国する。だが、彼は有名なヴァイオリニストである婚約者を連れていた……。", "1998-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("禁断の事件簿・ストーカーを愛した女", "禁断の事件簿・ストーカーを愛した女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「完全なる飼育」の原作者として知られる松田美智子がオリジナル脚本を手掛けた作品。大学教授にセクハラを受けている女子大学院生が、彼女を慕うストーカー青年に“救われる”という屈折した愛の物語だ。モラトリアムとコンプレックス、トラウマと引きこもりなど、“社会”と“コミュニケーション”をめぐる様々な問題を衝撃的な展開で見据えている。大学院生の安岡真里子は、日増しにエスカレートする権藤教授のセクハラに苦しんでいた。その事実を知った大学の警備員の赤羽譲は、彼女を助けたい想いに強く駆られる。だが、赤羽は自分の気持ちを上手く伝えられず……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルパン三世・念力珍作戦", "ルパン三世・念力珍作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "孤児院出身で、盗みのテクニックにかけては人後に落ちないルパン三世。だがルパン一族の直系として帝国を再建する気概は毛頭なく……。監督は、クレージー・キャッツの作品を数多く手掛けた坪島孝。実写版の本作も全編ギャグの連続。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE SOURIRE", "オディールの夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "川端康成の『眠れる美女』をモチーフに、死を間近にした男性が最高のエロスと愛を探す冒険を描く。パリ郊外、テニスのインストラクターのオディールは美しく、男はみな彼女に惹かれるが、まだ本当の恋を知らない。心臓病を患う精神病院の院長フランソワは彼女に夢中になるが、オディールは遊園地のストリップショーの呼び込みをするジャンジャンと親しくなり、ストリッパーになってしまうのだった……。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲルマニウムの夜", "ゲルマニウムの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "作家・花村萬月の芥川賞受賞作を「赤目四十八瀧心中未遂」で話題を集めた荒戸源次郎のプロデュースで映画化した衝撃作。育った教会の教護院で暴力に身を任せる青年の神への挑戦とも言える日々を描く。本作で初主演を飾る若手俳優の新井浩文が、荒々しくも透明感のある熱演をみせている。殺人を犯した後、自分が育った教会の教護院に舞い戻ってきた青年・朧は、自己の欲望のおもむくままに冒とくの限りを尽くしはじめる……。", "2005-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAKI183", "TAKI183", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "ヒップホップ文化の象徴でもあるグラフィティを題材に、現代の若者たちの成長や葛藤を描いた青春ドラマ。グラフィティに熱中するトミーら5人の仲間たち。彼らは渋谷のあばら家“スルメ屋敷”に巨大なグラフィティアートを完成させるが、それ以降はそれぞれ別の道へと進んでいった。2年後、スルメ屋敷が取り壊されることを知ったトミーは、そこに新たなグラフィティを描きはじめる。そんなトミーの元に、かつての仲間たちも集まってきて……。", "2006-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("勇み肌千両男", "勇み肌千両男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "“火事と喧嘩は江戸の華”と言われたが、そのさっそうとした勇ましさで江戸の町民を熱狂させた町火消しの誕生にまつわる物語。武家屋敷専門の定火けしに対抗する、町火消しのてん持ち・新吉を、高田浩吉が気持ちよく演じている。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dawn of the Dead", "ゾンビ／ディレクターズカット完全版", "", "", "http://ecx.images-amazon.com/images/I/51uSZpemU3L._SL210_.jpg", 139, "　ゾンビの群れから逃れ、ショッピング・センターに避難した男女4名。だが、やがてそこに略奪団とゾンビが侵入し、血みどろのサバイバル戦が繰り広げられることに……。テンポの速いダリオ・アルジェント監修版に対し、こちらのバージョンは、ヘリコプターの給油場面やバリケードを作る過程など幾つかの場面がより詳細に描かれ、さらに音楽の選曲も異なっている。", "1978-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("激突！殺人拳", "激突！殺人拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "千葉真一がアクロバット・アクションを披露する“東映空手”ものの第１作。彼がこの後出演するカラテ・アクションの原点である。幼くして父を失い、今は殺し屋同然となった空手の達人・剣琢麿が、中近東の石油王の娘の誘拐事件を解決していく。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUDDENLY，LAST SUMMER", "去年の夏 突然に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "T・ウィリアムズの“黒い戯曲”三部作の１本を映画化。州立精神病院の優秀な医師クロウィッツは、新病棟の建設をエサに、地元の有力者ビネブル夫人から姪キャサリンのロボトミー手術を依頼された。夫人の息子セバスチャンが去年の夏、旅行中に突然死亡し、居合わせたキャサリンは、ショックのあまり、早発性痴呆性にかかったというのだ。だがキャサリンの記憶が呼び戻されるにつれ、意外な事実が浮かび上がる。クロウィッツとキャサリンとの愛を軸に描かれるサスペンス映画の秀作。特にキャサリンに扮するE・テイラーの後半の演技は圧巻である。公開当時、全米でセンセーショナルな話題をまいたことでも有名。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("27 Missing Kisses", "シビラの悪戯", "", "", "http://image.eiga.k-img.com/images/movie/1227/315x210.jpg?3147", 96, "ソ連と呼ばれていた時代のグルジア生まれのジョルジャーゼ監督は70年代から活動する53歳のベテラン。10作目となる本作は2000年カンヌ映画祭の監督週間に正式出品、2001年のブリュッセル映画祭では審査員特別賞を受賞した。シビラ役のクヒアニチェは、本作が長編映画初出演だが、ニール・ジョーダン監督の新作「Double Down」への出演が決定。音楽はクストリッツア監督の「アンダーグラウンド」（95）、「アリゾナ・ドリーム」（93）のゴラン・ブレコビチが担当。", "2001-09-15", "7,68", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("婦系図（総集編）", "婦系図（総集編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「昨日消えた男」「男の花道」などコンビ作品が続いていたマキノ雅広演出、長谷川一夫主演の恋愛映画の総集編。約１ヵ月後に続編が封切られている。酒井教授のもとで更生、今は教授とともに新爆薬研究に打ち込む早瀬主税と、幼なじみで今は柳橋の売れっ子のお蔦との悲恋を描いたもの。泉鏡花の有名な原作を、同じく名コンビを組んでいた小国英雄が脚色。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真救世主伝説　北斗の拳ZERO ケンシロウ伝", "真救世主伝説　北斗の拳ZERO ケンシロウ伝", "", "", "http://image.eiga.k-img.com/images/movie/53657/315x210.jpg?1384", 90, "名作アニメ「北斗の拳」を新たな視点で描く「真救世主伝説」5部作の最終章。南斗弧鷲拳の伝承者シンに敗れた北斗神拳の伝承者ケンシロウ。胸に7つの傷を受け、最愛の女性ユリアまでをも奪われてしまった彼が救世主として覚醒するまでの空白の1年間を、映画版オリジナルのストーリーで描き出す。主人公ケンシロウの声を前4作に引き続き阿部寛が務めるほか、アクション俳優の藤岡弘がナレーションを担当する。", "2008-10-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ДЯДЯ ВАНЯ", "ワーニャ伯父さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1880年代のロシア。退官した老教授が、若い妖麗な後妻を連れ、ワーニャ伯父さんのもとへ帰郷する。老教授に失望したワーニャは憎悪と羨望からその後妻を愛するが……。19世紀末の退廃した空気の中で、愛に身を焦がす人々を描くチェーホフの『伯父ワーニャ』の映画化。", "1971-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("同期", "同期", "", "", "http://image.eiga.k-img.com/images/movie/56607/315x210.jpg?3142", 115, "警察小説の名手・今野敏の原作小説を、松田龍平主演で映像化。監督は「サイタマノラッパー」の俊英・入江悠。本庁捜査一課の刑事・宇田川は、公安に引っ張られていった同期の蘇我が理由もなく懲戒免職になったことを不審に思い、蘇我の行方を追う。しかし、警察内部からは圧力がかかり、さらにはある殺人事件の容疑者として蘇我の名前があげられ……。共演に栗山千明、新井浩文、竹中直人。2011年2月にWOWOWのオリジナルドラマ「ドラマW」枠で放送され、同年6月に劇場公開。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("安娜瑪徳蓮娜／ANNA　MAGDALENA", "アンナ・マデリーナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ひょんなことからお調子者のモッヤンを自宅に居候させることになったピアノ調律師ガーフ。彼はマンションの階上に引っ越してきた無愛想な娘マンイーにひそかな恋心を抱くが、ある事件をきっかけにモッヤンと彼女が接近してしまい……。バッハが妻のアンナ・マデリーナに捧げた名曲を効果的に散りばめ、恋愛感情のもどかしさを綴るラブ・ストーリー。香港の３大スターが恋のトライアングルを演じ、L・チャン、A・ユンがゲストで登場するキャストは超豪華。美術監督出身の新人監督ハイ・チョンマンのロマンティックなムード作りが光る反面、後半“別の映画”になってしまう意外な展開は賛否が分かれるところ。", "1998-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rush Hour 3", "ラッシュアワー3", "", "", "http://image.eiga.k-img.com/images/movie/34137/315x210.jpg?2674", 90, "ジャッキー・チェン＆クリス・タッカー主演の人気シリーズ第3弾。謎の中国マフィア“シャイシェン”の存在を突き止めたハン大使が、シンポジウムでそれを公言した瞬間、何者かに銃撃された。犯人を追う香港警察のリー警部は、交通整理中に無線を聞いて駆けつけたロス市警のカーターと再会する。事件の鍵となる人物がフランスにいることを知った2人はパリへと向かうが……。2人を迎え撃つ最強の敵に真田広之が扮する。", "2007-08-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Rolling Stones: Some Girls Live in Texas '78", "ザ・ローリング・ストーンズ　サム・ガールズ・ライヴ・イン・テキサス‘78", "", "", "http://image.eiga.k-img.com/images/movie/57367/315x210.jpg?3841", 100, "世界的ロックバンド「ザ・ローリング・ストーンズ」が1978年にアルバム「女たち」発表にあわせて行った全米ツアーの中から、78年7月18日、テキサス州フォート・ワースで行われたライブパフォーマンスの模様を収録したドキュメンタリー。「レット・イット・ロック」「ジャンピン・ジャック・フラッシュ」「ブラウン・シュガー」「ミス・ユー」など全17曲のパフォーマンスが収められているほか、2011年に初めてDVD化されるにあたり、ミック・ジャガーの最新インタビューも収録。当時の思い出や名曲「ミス・ユー」誕生の経緯などを語っている。", "2011-11-05", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("リラの花忘れじ", "リラの花忘れじ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "妻や息子、そして娘という愛すべき家族を捨て、事業一筋に歩んできた男・韮崎伝右衛門。離婚後、母に育てられた息子の敏政はやがて新聞記者になり、北海道で悪どい開拓を展開する韮崎北海道開拓会社、つまり父の会社を非難するという皮肉なめぐりあわせになって……。家族の愛と憎しみを、重厚な人物描写の中に描ききった人間ドラマ。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEARTS　IN　ATLANTIS", "アトランティスのこころ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "S・キングの同名小説をもとに、孤独な少年と謎めいた老人の心の交流を描く感動のファンタジー。父を亡くし、母の愛も受けられず寂しい思いをしている11歳の少年ボビーの家に、老紳士テッドが下宿することになる。不思議な魅力を持つテッドに心の安らぎを感じるボビーは、彼との絆を育んでいく。だが、何者かに追われるテッドに危険が迫っていた……。穏やかで神秘的な老人を演じるA・ホプキンスが圧倒的な存在感を放つ。幻想的で美しい少年の日の思い出をノスタルジックな映像で綴り、哀切な雰囲気で包み込む。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AZ PRUDE KOCOUR", "猫に裁かれる人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "チェコらしい民話風の作りにして、アイロニカルな視点も持ったマジカル・ファンタジー・ストーリー。チェコのモラビア地方の自然も美しい。ある村に、老魔術師と美女とメガネを掛けた猫という風変わりな一団が現れた。その猫は人を性格別に色彩化できるという。悪人は悪人色、善人は善人色といった具合。主人公の村の先生は美女と恋人同士の色で輝くのだった。", "1963-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FRIGHTENERS", "さまよう魂たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「乙女の祈り」で世界的に注目を集めたニュージーランドの鬼才P・ジャクソンが、ハリウッドに招かれて撮り上げたコミック風のコメディ・ホラー。３人のゴーストと共謀して悪魔祓いの詐欺稼業を続ける男が、霊体となってもなお殺人を続けようとする凶悪犯罪者と戦う。怪作「ブレインデッド」の時のような破天荒なパワーに劣ることなく、場面から場面へ突き抜けていくハイパーなスピード感はジャクソン監督ならでは。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブレイクブレイド　第三章「凶刃ノ痕」", "ブレイクブレイド　第三章「凶刃ノ痕」", "", "", "http://image.eiga.k-img.com/images/movie/55627/315x210.jpg?3163", 50, "吉永裕ノ介の人気ロボット漫画を6部作で映画化した第3弾。「昆虫物語みつばちハッチ　勇気のメロディ」のアミノテツロ監督がシリーズ総監督を務める。魔力を持たない青年ライガットは、親友の国王ホズルを守るためにクリシュナ軍へ正式に入隊する。一方、敵国アテネスのワルキウレス部隊は再びクリシュナへの侵攻を開始。さらに、“戦争の天才”と呼ばれるボルキュス将軍が動き出していた。", "2010-09-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の子ものがたり", "女の子ものがたり", "", "", "http://image.eiga.k-img.com/images/movie/54277/315x210.jpg?3587", 110, "「ぼくんち」「毎日かあさん」などで知られる人気漫画家・西原理恵子の同名原作を、「子猫の涙」の森岡利行監督が映画化。主演は「博士の愛した数式」の深津絵里、その少女時代を、実写版「ちびまる子ちゃん」の森迫永依と、「北の零年」「SAYURI」の大後寿々花が演じる。スランプから抜け出せないダメ漫画家の菜都美は、故郷の愛媛で出会った友だちとのかけがえのない少女時代を回顧し、人生を見つめ直していく。", "2009-08-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TAILOR OF PANAMA", "テイラー・オブ・パナマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "女とギャンブルで身を持ち崩した英国諜報部員アンディはパナマへ飛ばされる。任務はアメリカからパナマ運河を返還されたこの国の情勢の調査。アンディは国家要人も利用する紳士服店の仕立て屋ハリーに目をつけ、半ば脅迫する形で協力を取り付ける。しかし、アンディからせっつかれたハリーは要人たちから聞いた話に尾ひれをつけ、運河の権利をめぐって各国の争奪戦が繰り広げられていると報告。これを聞いたアンディは早速本国に情報を流すが……。スパイ小説の巨匠J・ル・カレが自作を初めて脚色、製作総指揮も担当したサスペンス・ドラマ。追い込まれて次々と嘘の情報を流すハリー、それを利用してひともうけを企むアンディという二人の人間の愚かな思惑が、米・英国家を動かす様を描いていく。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一個和八個／ONE AND EIGHT", "一人と八人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "中国ニューウェーブ・シネマ史上最初に作られた作品として記念すべき傑作。共産党の王指導員はスパイ容疑で牢にぶち込まれる。同じ牢の部屋には８人の犯罪者たちがいて、彼らは一様にこの新参者を煙たがった。ある時、彼ら“１人と８人”は別の地の牢に移ることになり、一緒に平原を旅していく。そこに日本軍が侵攻してきて、彼らを率いる兵士たちは次々と殺されてしまう。反目し合っていた“１人と８人”だが……。「黄色い大地」より先に完成していたものの、抗日戦争期の共産党の査問を描いていたため公開が遅れた作品。脚本に「黄色い大地」のチャン・ツーリァン、撮影が「菊豆」のチャン・イーモウなど、ニューウェーブ初期ならではの充実したスタッフが脇を固めている。", "1984-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天国の本屋～恋火", "天国の本屋～恋火", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "発売当時はほとんど注目されなかったが、地方のある書店から火がついてじわじわと全国に広がり、異例のロング・ベストセラーとなった“天国の本屋”シリーズ。本作は、その第１作と第３作を融合して篠原哲雄監督が映画化した。普遍的な純愛を、地上と天国の交錯というファンタジックな世界観の中で描き、深い感動を呼び起こした。オーケストラを解雇になったピアニストの健太。ヤケ酒で酔いつぶれて眠ってしまい、不思議な場所で目を覚ました。聞けば、なんとそこは天国の本屋だという。その頃地上では、花火大会の復活を計画する香夏子の姿があった。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宿無し犬", "宿無し犬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "鴨井大介は、母の墓参りのため高松にやってくる。ところが、土地のヤクザ・天興組のさしがねで、墓地は、ゴルフ場となっていた……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("辺境幻想", "辺境幻想", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "2008年「すみれ人形」が劇場公開されて独特な世界観が評価された金子雅和監督が、同作から2年の間に撮り上げた短編5作品を一挙上映。場所や境遇、状況など常に「辺境」におかれた人間や事物を描き、「迷鏡譚」「幻水譚」の2つのプログラムで構成される。「迷鏡譚」の「こなごな」は漫画家・早見純の「復活祭」を、「失はれる物語」は小説家・乙一の同名小説が原作。", "2010-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT HAMILTON WOMAN", "美女ありき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "歴史上名高いトラファルガー海戦の英雄ネルソン提督と、イギリス大使夫人ハミルトンとのロマンスを描いた壮大な作品。当時実生活でも熱烈な恋愛関係にあったV・リーとL・オリヴィエの共演が話題になった。", "1940-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEYOND RANGOON", "ラングーンを越えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "アメリカ人女性医師ローラは、強盗に夫と子供を惨殺されて以来、心を閉ざしていた。心配した姉とともに心を癒すためビルマのラングーンに観光旅行に来るが、パスポートをなくし一人残されることになる。ローラはそこで出会ったビルマ人ガイド、アウン・コーとの交流によって、心を開いていく。だが、彼女をかばいアウン・コーが軍に捕まった時、ローラは彼を助け、彼女自身も軍政府から追われる身になるのだった。1988年ビルマで何が起こったかを語る真実のドラマ。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IF　I　SHOULD　FALL　FROM　GRACE−THE　SHANE　MACGOWAN　STORY", "シェイン・ザ・ポーグス／　堕ちた天使の詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "アイリッシュ・トラッドとパンク・ロックを融合させ、1980年代～90年代初頭に人気を博したザ・ポーグスのフロントマン、シェイン・マガウアンをめぐる記録映画。近年のシェインの映像、ザ・ポーグス時代の貴重なライブ、ニック・ケイヴらのインタビューなどで構成されている。1980年代にイギリスの音楽シーンに登場し、世界的な支持を得たザ・ポーグス。彼らの波乱に満ちた歩みと鮮烈な魅力を振り返っていく。", "2005-10-29", "33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まじめにふまじめ　かいけつゾロリ　なぞのお宝大さくせん", "まじめにふまじめ　かいけつゾロリ　なぞのお宝大さくせん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 54, "大人気児童書を原作とする同名テレビアニメの劇場版。「いたずらの王者になる」「自分のお城を建ててきれいなお姫さまをお嫁さんにする」という夢を叶えるため、子分のイシシ＆ノシシと旅を続けるゾロリ。ある日、海賊タイガーに襲われていた少女テイルを助けたゾロリは、彼女にひと目ぼれしてしまう。ゾロリはテイルのペンダントに隠されていた地図のナソを解きあかし、早速お宝さがしを開始するが……。", "2006-03-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プレイボール", "プレイボール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "人気お笑いコンビ“ガレッジセール”初主演のコメディドラマ。誤って殺人事件を起こしマニラに逃げてきたリキは、現地で知り合ったコウジと万引きやアタリ屋をして生計を立てる日々。ある日、一人壁に向かいキャッチボールをする少年にかつての自分を重ねたリキは少年の夢を叶えようと奮闘する。ガレッジセール・ゴリは、NHKテレビ小説『ちゅらさん』で培われた演技力で、主演というプレッシャーを見事にはねのける好演を見せた。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Don't Tell Papa", "非日常的な彼女", "", "", "http://ecx.images-amazon.com/images/I/31RVWEWC6WL._SL210_.jpg", 102, "高校生でシングルファーザーになった青年と息子の絆を描いた韓国発のハートフルコメディ。高校生のチョルスは、クラブで出会った女子高生エランと一夜を共にする。チョルスの子を身ごもったエランは学校のトイレで男の子を出産、そのままチョルスに息子を預けて姿を消してしまう。8年後、男手ひとつで息子チョウォンを育てていたチョルスは、偶然エランと再会を果たし……。「亡国のイージス」のチェ・ミンソが破天荒な母親役を好演。", "2005-12-03", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOIGNE TON GAUCHE", "左側に気をつけろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ボクシング大会を目前に控えてチャンピオンの練習相手がダウン。家の手伝いを放り出してボクシングのまねをして遊んでいた農家の息子ロジェが、間に合わせにリングに上がることになった。彼は入門書を片手に珍ファイトを繰り広げるが……。タチが26歳ごろに主演したR・クレマンの短編。ビデオはタチが自ら監督する「のんき大将」に併録。", "1936-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLASS ACTION", "訴訟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "運転中の車が突然炎上し、生涯を車イスで過ごすことになった男が、自動車会社を訴える。原告側の弁護は父ウォード、被告側の弁護はその娘マギー。実の親子でありながら憎しみ合う二人によって裁判は進んでいく。組織悪への告発とともに、世代間の対立と家族の絆を描くサスペンス・ドラマ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A DANDY IN ASPIC", "殺しのダンディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "イギリス情報局に勤めるエバリンはソ連との二重スパイ。情報局内にソ連のスパイがいることが指摘され、東側への逃亡をはかるが殺される。撮影中にマン監督が死去。主演のL・ハーヴェイの手で完成された作品。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEEPERS　CREEPERS　２", "ヒューマン・キャッチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "巨匠フランシス・フォード・コッポラ監督が製作総指揮を務めたホラー「ジーパーズ・クリーパーズ」の続編。今回は主人公を高校のバスケットボール・チームに変更し、23年に一度、23日間暴れ回るという謎の怪物の恐怖を描き出していく。高校のバスケットチームを乗せたバスがパンクしてハイウェイで立ち往生してしまう。その夜、空から舞い降りてきた怪物が、運転手やコーチを次々とさらっていく。残された少年たちは脱出を試みるが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUN SKY WALKER(S)　“MY GENERATION”", "JUN SKY WALKER(S)　“MY GENERATION”", "", "", "http://image.eiga.k-img.com/images/movie/53957/315x210.jpg?3120", 0, "97年に解散し、今年でメジャーデビュー20周年を迎えたロックバンド「JUN SKY WALKER(S)」が1年限りの再結成のために行ったツアー“MY GENERATION”のライブ映像を劇場にて公開。2008年2月より始まったツアーでは、デビュー当時と変わらずメンバー自らが運転する通称「ジュンスカ号」で全国32カ所のライブハウスを回る。忘れかけていた過去やバンドの感覚を取り戻し、成長していくメンバーをカメラが追った。", "2008-08-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おんな牢秘図", "おんな牢秘図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "「秘録おんな牢」「秘録おんな蔵」「秘録おんな寺」など、特異な環境の中に生きる女たちの欲望と悲哀を描いた大映一連の“秘録”ものの系譜に属する一編。孤島に流刑囚として閉じこめられた女囚13人が、監視役の男５人の欲望のえじきにされていく……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アイ・ラヴ・ピース", "アイ・ラヴ・ピース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "身体に障害を抱えた人々が力強く生きている姿を見据えてきた“アイ・ラヴ”シリーズの第３作目。今回も監督をはじめ、主演ほか前作と同じスタッフが結集した。アフガニスタンのカブールと島根県太田市大森を舞台に、地雷で片足を失くしたアフガニスタンの少女と義肢装具士を目指すろうあ者のヒロインの出会いと心の交流が温かな目線で綴られる。島根県で義肢装具士を目指して働いている花岡いづみは、先輩の久保とともにNGOの一員としてアフガニスタンでの救援活動へ同行する。現地の診療所で活動を始めたいづみは、地雷で片足を失ったパリザットという少女と出会う。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("One Point O", "１.０〈ワン・ポイント・オー〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "進化し続ける最先端技術のナノテクノロジーと、コンピュータ・ウイルスをモチーフとしたサスペンス・スリラー。死を呼ぶウィルスに感染してしまった青年の恐怖を、エッジの効いた映像とダイナミックな演出で活写。絵空事では片付けられない現代的なテーマに惹き込まれる。何者かによって部屋に届けられ続ける謎の箱を開けたサイモンは、肉眼では見えない“ナノマイト・ウイルス”に感染してしまい……。", "2005-06-18", "1,56,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Miami Vice", "マイアミ・バイス", "", "", "http://image.eiga.k-img.com/images/movie/33457/315x210.jpg?4502", 132, "80年代に一世を風靡したTVドラマを、当時製作総指揮を務めていたマイケル・マン監督（「ヒート」「コラテラル」）が巨費を投じてリメイク。TVドラマにあった陽気さは払拭され、近年のマン監督のスタイルそのままに2人の麻薬潜入捜査官（コリン・ファレル、ジェイミー・フォックス）の活躍がマイアミや中南米を舞台にハードかつスタイリッシュに描かれる。ヒロイン、イザベラ役には「SAYURI」に続いてのハリウッド作となるコン・リー。", "2006-09-02", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルーキーズ・ゴト師株式会社　THE MOVIE", "ルーキーズ・ゴト師株式会社　THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "根津甚八主演の人気シリーズ“ゴト師株式会社”の番外編。主人公は、卒業単位と引き換えに、渋々パチンコ・サークルに入部した大学生。買収の危機に瀕したパチンコ店を救うため、大手チェーンに戦いを挑んでいく。パチンコ産業研究部に入った熱志は、パチンコ大学選手権大会に出場。パチンコ・チェーン社長の御曹子、財前が率いるライバル大学の打倒に燃えるが……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トルソ", "トルソ", "", "", "http://image.eiga.k-img.com/images/movie/55457/315x210.jpg?2256", 104, "他人とのかかわりを避けて暮らす独身OLのヒロコは、顔も手も足もない男性型の人形・トルソを心のよりどころにしていた。しかし、自由奔放な妹ミナが家に転がり込んできたことで、平穏だった日々が崩れ始める。「誰も知らない」「歩いても　歩いても」（是枝裕和監督）などの撮影を手がける山崎裕が初メガホンをとり、「愛の予感」の渡辺真起子と「ケンタとジュンとカヨちゃんの国」の安藤サクラが姉妹役を演じる。", "2010-07-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("緋牡丹博徒・二代目襲名", "緋牡丹博徒・二代目襲名", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "火野葦平の原作『女侠一代』をもとに鈴木則文が脚色した４作目。故郷・熊本へ帰ったお竜は、鉄道敷設工事で矢野一家の再興をはかる。宝満一家の悪らつな妨害にも屈せずにこれを完成させ、矢野一家・二代目を正式に襲名する。天津敏が迫力ある悪役ぶりを見せる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KUKUSHKA", "ククーシュカ／ラップランドの妖精", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "2002年のモスクワ映画祭で最優秀監督賞をはじめ、5部門を受賞。大らかな語り口と、不思議な生活感が魅力のロシア映画。第二次大戦末期、敵対する国の兵士二人と、彼らをかくまったラップランドの未亡人が言葉も判らないまま心を通わせていく様を瑞々しく綴る。生と性を謳歌するヒロイン像が新鮮だ。フィンランド最北の地・ラップランド。フィンランド軍のヴェイッコと、ロシア軍のイワンは、瀕死のところを地元の女性アンニに救われる。やがて彼らの奇妙な共同生活が始まるが……。", "2006-03-25", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("電子戦隊デンジマン", "電子戦隊デンジマン", "", "", "http://ecx.images-amazon.com/images/I/21TE6CWY0NL._SL210_.jpg", 45, "地球総ヘドロ化を目論むベーダー一族とデンジマンの闘いを描く特撮テレビシリーズ「電子戦隊デンジマン」の劇場オリジナル版。3000年前にベーダー一族に滅ぼされ地球へと移住したデンジ星の子孫たちが、ヘドラー将軍によって次々と連れ去られた。ヘドラーの目的は、かつてデンジ姫が地球を守るために授けた「虹の石」で、デンジマン全員の力をあわせてもかなわないという恐るべきパワーを持つものだった。", "1980-07-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dante's Peak", "ダンテズ・ピーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "地質調査所の学者ハリーは地震活動の調査のためにアメリカ北西部の田舎町ダンテズ・ピークを訪れる。女性市長レイチェルの案内で山を見て回った彼は数々の異変に気付き仲間たちを呼び寄せ、本格的な研究、調査を重ねていく。その結果、火山の噴火が近いことを確信。報告を受けたレイチェルが市民を集めて事情の説明を始めた矢先、大地震が町を襲う……。総製作費130億円をつぎ込んだ大型パニック映画。火山の大噴火シーンから始まり、火山灰や溶岩の飛来、火砕流、洪水、火事などのあらゆる天災をSFX映像と特撮技術によって再現。その中を走り回るハリーとレイチェルの脱出劇がサスペンスフルに繰り広げられていく。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　OUT−OF−TOWNERS", "アウト・オブ・タウナーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ニール・サイモンが1970年に書いた脚本を基にしたドタバタ・コメディ。会社をリストラされたヘンリーが、再就職の面接のため妻ナンシーとともにニューヨークへやって来る。ところが、どこへ行っても災難続き。強盗に遭い一文なしになってしまったうえ、勘違いからヘンリーがワイセツ罪で逮捕されてしまう……。ベテラン、S・マーティンとG・ホーンの息が合った絶妙のやり取りで笑いを誘う。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TRAIN ROBBERS", "大列車強盗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "夫が列車強盗で得た大金を探し出してほしいという、美しい未亡人の申し出を買って出た男たちは、メキシコ奥地のボロ機関車の中に50万ドルを発見する。しかし思わぬドンデン返しが……。B級西部劇の名手ケネディが得意のユーモア感覚で綴った逸品。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャラウェイ", "キャラウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "「男はソレを我慢できない」の温水洋一、「長州ファイブ」の三浦アキフミら個性派俳優をキャストに迎えたオムニバス・ムービー。2006年にau EZチャンネルで配信された「プチラブ★10」を基にした10話に、未公開作品1話を追加。男女の恋愛、兄弟愛、同性愛といった様々な「愛」の形を、コメディ、ファンタジー、ブラックなどバラエティ豊かな作風で描きだす。メガホンを取るのは、本作が劇場監督デビューとなる日高尚人。", "2007-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("El Pasado", "失われた肌", "", "", "http://image.eiga.k-img.com/images/movie/54467/315x210.jpg?2504", 114, "「蜘蛛女のキス」「黄昏に燃えて」の名匠へクトール・バベンコが、「アモーレス・ペロス」「バベル」のガエル・ガルシア・ベルナルを主演に描くラブストーリー。結婚12年目の夫婦、リミニとソフィアに別れが訪れる。寂しさを埋めるために次々と女性たちと関係を持つリミニであったが、ソフィアの記憶を消すことは出来なかった。一方ソフィアもリミニに対し激しい執着心を抱き、リミニの生活を振り回すようになり……。", "2009-04-11", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PART17 プライド", "難波金融伝 ミナミの帝王　劇場版PART17 プライド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "少年野球のコーチを務める山村は、父親の借金でチームをやめようとしていたエースの秀一郎を引き留めるため、借金を肩代わりすることに。ところが秀一郎の父親が夜逃げしてしまい、彼は多額の借金返済を迫られ途方に暮れる。人気シリーズの劇場版第18作。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("透明人間と蝿男", "透明人間と蝿男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "南方の島で人間を蝿のサイズに変える麻薬を発見した男が、自分に戦犯の罪を着せた者たちに復讐を開始。蝿男になった彼は透明光線を発明した博士を殺し、透明人間になった博士の助手から報復を受ける。「虹男」「透明人間現わる」などの作品の系譜を受け継ぐ、大映の“変身人間”ものの佳作。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ladybird Ladybird", "レディバード・レディバード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "社会福祉の名の下に愛する子どもたちを次々と奪われた母親の悲劇を、実話を基にケン・ローチ監督が映画化。パブの歌手マギーにはつらい過去があった。彼女の留守中に起きた火事で長男が大やけどを負い、社会福祉局によって4人の子ども全員が養子に出されてしまったのだ。やがて彼女はパラグアイ出身のホルヘと再婚し、子どもを授かる。ところが、彼女に母親失格の烙印を押した福祉局によってまたもや子どもが奪われてしまい……。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Scream 3", "スクリーム3", "", "", "http://image.eiga.k-img.com/images/movie/1847/315x210.jpg?4701", 117, "ウェス・クレイブン監督による人気ホラーミステリーシリーズ第3弾。ハリウッドでウッズボロー連続殺人事件のその後を描く映画「スタブ3」の撮影が行われていた。しかし、ある日、同作に出演する女優が何者かに惨殺される事件が発生。それを皮切りに次々と撮影現場で殺人事件が起こる。ネーブ・キャンベル、デビッド・アークエットらシリーズおなじみのキャストが集う。", "2000-04-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mentiras Y Gordas", "セックスとパーティーと嘘", "", "", "http://image.eiga.k-img.com/images/movie/54787/315x210.jpg?4334", 105, "スペインの若者たちの風俗と彼らが抱える心の闇を赤裸々に描き出し、同国で大きな話題を巻き起こした青春ドラマ。キャストにはキューバ出身の若手女優アナ・デ・アルマスをはじめ、マリオ・カサス、マキシ・イグレシアスら、スペインのTV界・映画界で活躍する人気アイドル俳優が集結。「エストレーリャ／星のまわりで」でゴヤ賞を受賞したベテラン脚本家アンヘルス・ゴンサレス・シンデが脚本に参加している。", "2009-09-17", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　３００　SPARTANS", "スパルタ総攻撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "「トロイ」や「キングダム・オブ・ヘブン」などへと通じる、紀元前のハリウッド史劇にして大活劇のスペクタクル巨編。戦争現場であるギリシャでセット設営やオール・ロケを敢行し、ペルシャ戦争を再現した。紀元前5世紀、ペルシャ軍がギリシャへ侵攻した。ギリシャ側は都市国家を集めた連合軍を結成するが出征しないため、スパルタ王が300人で出撃。大苦戦の展開が……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SATURDAY NIGHT FEVER", "サタデー・ナイト・フィーバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "トニーはしがない店員だが、土曜の夜だけはディスコの花形だった。やがて、自分が優勝したダンス・コンテストの舞台裏を知ったり、悪ふざけで気弱な少年を死に追いやってしまったりという事から、自分の生活に目を向ける様になっていく。公開されると同時に、全米の若者の圧倒的な支持を得て、世界中にディスコ・ブームを巻き起こした青春映画の好編。ナイーブさとセクシーさを備えたJ・トラヴォルタは1970年代後半における全女性のアイドルとなり、また全編を彩るヒット曲の数々はビージーズのもので、そのレコードは史上に残る記録的な売り上げを示した。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE 39 STEPS", "三十九階段", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "J・バカンの有名な冒険小説の映画化、というよりヒッチコック監督の「三十九夜」の再映画化。外交官ハネイは、ふとした機会で政府機密機関の女と知り合い、何者かに追われているところをかくまったが、彼の留守中に女は殺された。ハネイは彼女が残した地図と“三十九階段”という謎の言葉を頼りに彼女を殺したスパイ一味と対決すべくスコットランドへ向かう。一方警察はハネイこそ殺人犯と考え追跡を開始する。スパイと警察に追われた彼は、女教師を巻き込み新婚旅行を装って敵地へ乗り込むが……。", "1959-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TESTAMENT", "テスタメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "核の恐怖を捉えた非常に今日的なドラマ。監督はTVのドキュメンタリー番組を数多く手掛けてきた女流のL・リットマン。平和で穏やかなある町に何の前ぶれもなく突然、核の閃光が走ったらどうなるか。“狂気の兵器”によって生み出される絶望と悲劇をじっくりと描いた力作。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キッチン", "キッチン", "", "", "http://ecx.images-amazon.com/images/I/41CATPJG2PL._SL210_.jpg", 106, "祖母を亡くし、天涯孤独の身となったみかげは、生前に祖母の知り合いだったという雄一の誘いで、彼のマンションに住むことになる。そこには、雄一の母・絵里子（実はゲイの父親）も同居しており、女ふたり（？）と青年との奇妙な共同生活が始まるが……。映画初出演のスーパー・モデル、川原亜矢子の透明感がハマっている。橋爪功の好演、松田ケイジの不思議な存在感も印象的。", "1989-10-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　REAL　BLONDE", "リアル・ブロンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「ジョニー・スエード」のT・ディチロ監督が手掛けたユーモラスな恋愛群像劇。売れない俳優ジョーと、同棲中の恋人メアリーの関係は近ごろ停滞ぎみ。それに追い打ちをかけるように、同じく俳優志望の友人ボブが昼メロの主演をモノにしたことからジョーは焦りを覚える。そして、男性の視線に妙に神経質になっているメアリーもイライラを募らせ、護身術の教室に通い始める。一方、ブロンド美女こそ理想の女性と考えているボブはニセ金髪のモデルと恋仲になるが、高い理想に邪魔され、彼女を愛しきれずにいた……。コミカルなドラマのなかに、大人になりきれない男たち、女たちの恋のホンネをサラリと浮かび上がらせる。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVERS", "ラヴァーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "作家性を重視する映画監督の集団“ドグマ95”の第５作として、「グラン・ブルー」のジャン・マルク・バールが監督デビューを果たしたラブ・ストーリー。パリの街角にある小さな本屋。店員と客として出会ったジャンヌとドラガンが一目で恋に落ちる。恋に傷ついていたジャンヌは、情熱的なドラガンに戸惑いながらも心の安らぎを感じ始めていた。そんなある日、ユーゴスラビア出身のドラガンが不法滞在者であることが発覚、３日後に国外退去を言い渡される……。愛の喜びに浸る恋人たちの姿、微妙に揺れ動く表情を、手持ちカメラによる映像でスケッチ風に追っていく。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HUNCHBACK OF NOTRE DAME", "ノートルダムの傴僂男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "V・ユゴー原作『ノートルダム・ド・パリ』の映画化。ルイ11世統治下のパリを舞台に、幼少時より寺院で働く薄幸のせむし男カジモドと、彼に人間的愛情を注ぐジプシー娘との深い絆をスペクタクルの中に描く。カジモドに扮したロートンの哀歓を込めた鬼気迫る名演が感動を呼ぶ一編。DVDタイトルは「ノートルダムのせむし男」。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE VOYOU", "流れ者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "フランス感覚派ラブ・ストーリーの旗手C・ルルーシュ監督が手掛けた珍しいアクション映画。法の裏も表も知り尽くしている元弁護士の男が、ある誘拐事件をたくらみ、着々と実行に移していく。ルルーシュ監督の出世作「男と女」の主演俳優トランティニャンが、C級犯罪者を好演。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新蛇姫様 お島千太郎", "新蛇姫様 お島千太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "直木賞作家・川口松太郎の小説を原作に、美空ひばりが一人二役に挑んだ人情もの時代劇。父を殺した家老の末っ子を討った兄は旅芸人一座の元でかくまわれることに。妹は藩の刺客となったが、密かに姫を陥れようと狙う家老一族に殺されてしまう。それを知った兄は、姫や一座の看板女優らの協力を得て敵討ちへ。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スイートリトルライズ", "スイートリトルライズ", "", "", "http://image.eiga.k-img.com/images/movie/54677/315x210.jpg?2181", 117, "「三月のライオン」「ストロベリーショートケイクス」の矢崎仁司監督が、江國香織原作の同名小説を映画化した恋愛ドラマ。テディベア作家の瑠璃子とIT会社に勤める聡は、結婚3年目の夫婦。一見幸せそうに見える夫婦は、お互いに別の相手と恋に落ちていた。2人は同じ屋根の下に暮らしながら、小さな嘘を重ねていくが……。主演は中谷美紀、大森南朋。", "2010-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TORNADO RUN", "トルネード・ラン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "核兵器カルテルにトップガン・チームが立ち向かうスカイアクション。兵器産業のボーデン社は、各国に小型原爆を売りさばく核兵器カルテル。“トルネード・ラン”と呼ばれる彼らの武器輸送管路を探していたCIAは、ボーデン社の武器格納庫を発見し、トップガン・チーム“ファースト・ストライク”に出撃を命令。だが、作戦は事前に察知され、メンバーは捕虜に。チームにスパイがいるとにらんだ指揮官シャーマンは、信頼のおけるメンバーを集め、捕虜の救出へと向かう。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Clean", "クリーン", "", "", "http://image.eiga.k-img.com/images/movie/54637/315x210.jpg?4110", 111, "「夏時間の庭」の俊英オリビエ・アサイヤスが家族の再生を描いたヒューマン・ドラマ。歌手としての成功を夢見るエミリーは、ある日、ロックスターの夫をドラッグで亡くし、同時にひとり息子の養育権も失ってしまう。息子を取り戻そうと奮闘するエミリーは昔住んでいたパリへと向かうが、望む仕事にも就けず、久々に会えた息子とも衝突してしまう。マギー・チャンが第57回カンヌ国際映画祭で主演女優賞を受賞。", "2009-08-29", "2,3,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("菊池 Kikuchi", "菊池 Kikuchi", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "エキセントリックな漫画家イワモトケンチが映画監督になるために、漫画家を廃業し、自ら資金を集めて手掛けた第一作。洗濯工場に勤める菊池という何の変てつもない平凡な青年の日常を追いながら、ノイズ音響や微妙に色彩処理したフィルムの効果により観る者に新しい映像体験をせまる異色作。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CODE90", "CODE90 愛欲指令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "女スパイのアニエは、組織の指令を受けてハイテク・ビルのオフィスから1枚のフロッピーディスクを盗み出した。だがその瞬間から彼女は謎の男にレイプされ、正体不明のジープに追われる運命となってしまう。１枚のフロッピーが一人の女を恐怖と性の体験に陥れていく、エロティック・サスペンス。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ある日わたしは", "ある日わたしは", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "アクションものを得意とする岡本喜八、初期の快調な明朗青春メロドラマ。現在のボーイフレンドに物足りないゆり子は男っぽい大助に惹かれるが、なぜか両親は交際に反対。実は大助の父とゆり子の母は、昔恋人同士だった。そんな障害を乗り越えて、二人の愛は親たちの理解を得、結婚に実を結ぶ。原作は戦後映画界を席捲した石坂洋次郎。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンのワンワン大将", "エノケンのワンワン大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "エノケン扮する浮浪者が１匹のシェパードの浮浪犬と親友になるが、浮浪犬は実はドイツの名犬で、軍用犬として戦場に旅立って行く。チャップリン風の悲喜劇だが、エノケン＝中川信夫コンビとしては、あまり出来がよくない。クレジットは師匠の山本嘉次郎だが、実は黒澤明が脚本を書いている。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LATE FOR DINNER", "レイト・フォー・ディナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "都市建設を推し進める悪徳不動産屋にだまされ土地を手放すことになったウィリーと義弟で親友のフランク。土地代金を支払いたくない不動産屋の画策により誘拐犯に仕立てあげられ、銃撃戦でケガまで負わされた二人。逃亡先で冷凍保存の研究に打ち込んでいる医者に出会い、そのサンプルにされてしまう。それから29年後の現代、ふとしたことから目を覚ました二人を待っていたものは……。一味違うタイム・トラベル・ストーリー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版NARUTO ナルト　大興奮！みかづき島のアニマル騒動（パニック）だってばよ", "劇場版NARUTO ナルト　大興奮！みかづき島のアニマル騒動（パニック）だってばよ", "", "", "http://image.eiga.k-img.com/images/movie/33427/315x210.jpg?4331", 95, "週刊少年ジャンプ連載の大人気コミックを原作としたテレビアニメシリーズの劇場版第3弾。ナルトと仲間たちに与えられた今回の任務は、世界旅行中の“月の国”の王子を故郷のみかづき島まで無事に連れて帰ること。ワガママ放題の王子の相手や、王子が途中で買い取ってしまったサーカス団の世話に悩まされながらも、ナルトたち一行はなんとかみかづき島にたどり着く。ところが彼らの前に、今度は謎の忍者3人衆が立ちはだかり……。", "2006-08-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大内密探零零發／FORBIDDEN CITY COP", "008（ゼロゼロパー）　皇帝ミッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "香港映画界のコメディ・スター、C・シンチーが監督と脚本も務めたスパイ・コメディ。発明好きのドジなスパイ、“008”に久しぶりに与えられた任務。それは皇帝ご執心の娼婦カムの調査。愛妻家の“008”であったが、彼女の美しさについマイってしまい……。全編にわたって繰り広げられるギャグの数々が爆笑モノ。ドタバタ騒ぎの中にもカンフーアクションや見事な剣術など、スリリングな見せ場も盛り込んだ痛快作だ。主人公の美人妻を演じるのは、「2046」のカリーナ・ラウ。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("家庭教師", "家庭教師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "自らの体験をもとに、家庭教師の仕事を生きることに重ねあわせながら、屈折した青春像をパワフルに描いていく。自主製作作品でありながら、監督、脚本、撮影、主演とすべてを負った渡辺文樹が自ら売り込みにあたり、正式に公開にこぎつけた執念の一作。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("影法師捕物帖", "影法師捕物帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "和製ホラーの第一人者・中川信夫監督による娯楽時代劇。嵐寛寿郎の300本出演記念作品でもある。老中田沼意次の金権政治を正すべく、松平定信の命を受けた“影法師”こと朱桜玲三郎は、天草四郎の埋蔵金を手に入れようとする田沼の陰謀を阻止していく。田沼は報復として、松平の行列襲撃を企てるが……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE ROOM", "ホワイトルーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1991年のアヴォリアッツ国際ファンタスティック映画祭で、黄金のアンテナ賞、高等技術委員会賞を受賞したエロティック・スリラー。憧れの人気歌手“マドレーヌX”が殺されるのを目撃してしまったノーマン。独自の捜査を始めた彼の前に、次々と謎めいた事件が起きる。", "1990-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("同じ月を見ている", "同じ月を見ている", "", "", "http://image.eiga.k-img.com/images/movie/1577/315x210.jpg?3086", 106, "窪塚洋介が2年振りに映画に復帰、「インファナル・アフェア」「頭文字D」で人気の香港俳優エディソン・チャンと共演する話題作。医大で研修中の熊川は、幼なじみのエミの心臓病を治すために励んでいた。そんなある日、彼はもうひとりの幼なじみのドンが刑務所を脱走したことを知る。2人の間には秘められた過去があった。土田世紀のコミックを「バトル・ロワイアルII」の監督が映画化。主題歌は久保田利伸の「君のそばに」。", "2005-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLAY PIGEONS", "ムーンライト・ドライブ", "", "", "http://ecx.images-amazon.com/images/I/21AC28CWCAL._SL210_.jpg", 104, "モンタナの田舎町に住む青年クレイは友人アールの妻アマンダと不倫を重ねていたが、それを知ったアールは彼を殺人犯に仕立てるために自殺してしまう。パニック状態に陥った彼だったがアールの死を転落事故に見せかけて難を逃れる。しかし、この件のせいで、夫の死を悲しむでもなく誘惑してくるアマンダが自分の恋人グロリアを射殺したときも、死体を隠すことになる。そんなある日、クレイはレスターという男と出会い、意気投合するが、彼こそは近隣を騒がす連続殺人鬼だった……。新人M・ヒーリーの脚本を、監督デビューとなるCF界出身のD・ドプキンが映画化したサスペンス。スリリングな展開の中に織り込まれたユーモア、登場人物たちのユニークな個性が物語をより魅力的なものにしている。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXODUS", "栄光への脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 212, "イスラエル建国の物語。1947年のキプロスには、イスラエルに帰国しようとするユダヤ人たちが収容されていた。夫の消息を尋ねてキプロスを訪れた米国女性キティは、地下運動のリーダー、アリと心を通じ“エクソダス（脱出）号”と名付けられた船で、母国に向かうのだった……。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夕やけ雲", "夕やけ雲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "下町の魚屋の家に生まれ育った息子が、家業の魚屋を継ぐのはいやで、船乗りに憧れていたにもかかわらず、結局は父の死によって魚屋の跡継ぎになるまでを描いた作品。前作「野菊の如き君なりき」でデビューした新人・田中晋二が、主役に抜擢された。脚本の楠田芳子は木下惠介の妹で、撮影の楠田浩之の夫人でもある。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAPRICES DE MARIE", "君に愛の月影を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "P・ド・ブロカが、伝統的なフランスの田舎の伸びやかな生活に愛着を寄せ、近代文明をチクリと皮肉った佳作。何事も意のままになるアメリカの大富豪ブロデリックは、５番目の妻に生粋のフランス人マリーを選ぶ。彼女に猛然とアタックしたブロデリックは、ついに婚約を成立させてニューヨークに連れていくが、彼女はホームシックにかかってしまい……。", "1969-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ATLANTIS：THE　LOST　EMPIRE", "アトランティス／失われた帝国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ウォルト・ディズニー生誕100周年を記念して作られたアドベンチャー・アニメ。水没した幻の大陸アトランティスを目指す冒険の数々やスペクタクル・シーンが、ディズニーの伝統的な2Dとデジタルの3Dを融合させた映像で描かれていく。1914年、亡き祖父の遺志を継いでアトランティスの研究に励む若き学者マイロは、ある日、祖父の友人だった富豪に呼び出された。そして、アトランティスの謎を解く鍵を秘めた伝説の書『羊飼いの日誌』を渡され、アトランティスを目指す探検隊への参加を要請される。マイロはローク隊長や爆破のプロ、ヴィニーらと潜水艦で海底へ。やがて、神秘の大陸に到達した彼らは平和に暮らすアトランティス人と遭遇するが、そこでマイロは探検の裏に隠された陰謀を知る……。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIFE IS SWEET", "ライフ・イズ・スウィート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "時にはシニカルでドライな視線で、イギリスの都会生活を映し出すM・リー監督による新感覚ドラマ。一風変わった双子の姉妹をめぐる話が展開、一人は男まさりの性格と容貌のナット、そして自閉症気味で拒食症のニコラ。こんな二人に両親も手を持て余し気味だが……。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOODBYE LOVER", "パトリシア・アークエットのグッバイ・ラバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "ロサンゼルスを舞台に、大金をめぐって繰り広げられる男女の争奪戦を描くオフビート感覚のサスペンス・ドラマ。不動産の美人ブローカー・サンドラは、教会の奉仕活動に励む一方で夫ジェイクの兄ベンと不倫中。やがて、そのことを知ったジェイクが逆上し、ベンが二人のもとに駆け付ける。だが、そこにはある罠が仕掛けられていた……。愛もお金も手に入れるヒロインを、P・アークエットが小悪魔的な魅力で好演。無口な殺し屋役V・ギャロなど、個性派俳優が脇を固める。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAXTER", "バクステール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "人間の生活に異常なまでの興味を示し、行く先々でトラブルを巻き起こすブルテリア犬バクステールと、ヒトラーに傾倒する少年との対決。人間よりも人間らしい犬バクステールを通して、人間の本性をブラックな視線で描いている。1989年アヴォリアッツ映画祭特別賞受賞作。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOPLESS", "TOPLESS", "", "", "http://image.eiga.k-img.com/images/movie/53707/315x210.jpg?4045", 108, "同性愛者のナツコは失恋の寂しさを紛らわせようとナンパに明け暮れていた。一方、ナツコの元恋人トモミは、傷つくことを恐れて男性との結婚という道を選択する。ある日ナツコは、母親を探すため上京したという女子高生カナと知り合う。カナの母親は彼女がまだ幼い頃に同性の恋人と駆け落ちしており……。「しあわせなら手をたたこう」の内田英治監督が、女の子同士の恋をみずみずしいタッチで綴った青春ストーリー。", "2008-06-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Reflections in a Golden Eye", "禁じられた情事の森", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "　ジョージア州の陸軍将校宿舎に暮らすペンダートン少佐夫妻。妻のレオノーラは隣家のラングトン中佐と人目を忍ぶ仲だった。一方、夫のウェルドンはゲイで、厩舎係の若い兵士ウィリアムスに心奪われる。しかしウィリアムスはレオノーラに恋し、彼女の寝姿を覗き見ていた。異常な行動の果て、彼らは恐るべき結末を迎えることに。性倒錯とそれに絡む複雑な人間関係を描いた異色作。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CIEN", "影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "疾走中の電車から一人の男が転落死する。近くで無賃乗車でつかまった青年の話によると、その男はスパイだった。そして検死をした医師も戦争中の体験を思い出す。戦中から戦後にかけてのポーランドの“影”の部分をミステリー仕立てで描いた社会派ドラマ。", "1956-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Killing of Chinese Bookie", "チャイニーズ・ブッキーを殺した男", "", "", "http://image.eiga.k-img.com/images/movie/19677/315x210.jpg?2107", 108, "　コズモはクラブの経営者。店はストリップ・ショーで人気を博し、そこそこ繁盛している。ある時、彼は店のダンサーを引きつれて賭博場へ行くが、そこで高額の借金を作ってしまう。返済を迫るマフィアはコズモの弱みに付け込み、彼に中国人の大物殺害を要求。コズモは何とかその仕事を完了するが……。窮地に陥った男の葛藤を描くフィルム・ノワール調のドラマ。1976年に135分版が完成している。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAMPION", "チャンピオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "「友へ・チング」のクァク・キョンテク監督による、ユ・オソンが主演した人間ドラマ。韓国で今も語り継がれる実在のボクサー、キム・ドゥックの壮絶なリング人生を濃厚に描き出す。幼くして家を飛び出し、ソウルで孤児同然の生活を送ったキム。成長した彼はボクシングと出会い、メキメキと頭角を現す。やがて彼のもとに夢にまでみた世界タイトル戦の話が舞い込む。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Berlin Alexanderplatz", "ベルリン・アレクサンダー広場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 869, "「マリア・ブラウンの結婚」「リリー・マルレーン」などで知られる名匠ファスビンダー監督の集大成的作品。テレビ映画化された本作品は全14話構成で14時間56分の超大作でもある。1920年代末の第一次大戦敗戦の痛手で社会は不安定を極め、失業者は日々増加し犯罪が横行していたベルリンを舞台に、一人の普通の男フランツ・ビーバーコップがたどる受難に満ちた物語。", "1980-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1 chance sur 2", "ハーフ・ア・チャンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "高級外車窃盗常習犯のアリスは、母親の遺品であるカセットテープで2人の父親候補がいることを知る。2人は共にアリスの父親だと言い張るが、そんな中、3人はマフィアの抗争に巻き込まれていくのだった。フランスを代表する2大俳優アラン・ドロンとジャン＝ポール・ベルモンドが競演したパトリス・ルコント監督のアクション・ドラマ。「白い婚礼」で映画デビューを果たしたフランス人歌手バネッサ・パラディがアリスを演じる。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北海の暴れ竜", "北海の暴れ竜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "深作欣二監督・梅宮辰夫主演による痛快アクション映画。北海道のさびれた漁村を舞台に、魚の買いつけをめぐって新旧二つの一家が対立する。深作欣二が1964年に監督した快作「ジャコ萬と鉄」とよく似たストーリーだが、作品のノリは今一つであった。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAITH", "フェイス～愛と青春の輝き～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "軍人の父を持つ少女フェイスは、ロサンゼルスの高校での学園生活をエンジョイしていた。だが、飛行機事故で突然両親を失い、彼女の運命は一転。里親のもとを逃げ出した彼女は、マフィアのボスに救われ、その身をダンスに打ち込んでいく……。ダンスに情熱を傾ける少女の姿に、恋愛や、ギャングの対立まで、欲張りに盛り込んだ青春映画。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAJOR LEAGUEII", "メジャーリーグ2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "奇跡の優勝を遂げたインディアンズは、再び落ちこぼれに戻っていた。タレント気取りでのぼせる“ワイルド・シング”リッキー投手に、平和主義者になった４番打者セラノ。見かねたオーナーは日本から助っ人として“カミカゼ野郎”タカ・タナカを呼ぶが……。とんねるずの石橋貴明が出演し、話題となったコメディ第２作。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHERE　THE　MONEY　IS", "ゲット・ア・チャンス！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "P・ニューマンが元スゴ腕銀行強盗に扮したコメディ・ドラマ。老人ホームで看護の仕事をするキャロルは、毎日さえない日々を送っていた。そんなある日、老人ホームにヘンリーという痴呆症の老人がやってくる。だが、彼は元大泥棒で、実は仮病を使ってホームに入所してきたのだった。キャロルはヘンリーと仲良くなっていくうちに、自分の中で何かが変わっていくのを感じる。平凡な生活から抜け出そうとする彼女は、ヘンリーに銀行強盗の話を持ちかけるのだが……。年老いた男と、夢をなくした平凡な女性が繰り広げる冒険が展開。ニューマンが傑作「明日に向って撃て！」のセルフ・パロディー的な演技を披露し、年齢を感じさせないパワーと色気で魅せる。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRIVATE LIVES OF ELIZABETH AND ESSEX", "女王エリザベス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「何がジェーンに起ったか？」の名女優B・デイヴィスと、剣劇ヒーロー俳優のE・フリン共演。イギリス・エリザベス王朝後期の時代を背景に描かれる史劇悲恋ドラマ。老いぼれた処女のエリザベス女王は家臣のエセックス伯と恋に落ちてしまう。眉毛を剃り白塗りメイクで女優としての美貌を完全に打ち消したベティの迫真の演技に圧倒される。オスカー５部門ノミネートの傑作。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一心太助 男一匹道中記", "一心太助 男一匹道中記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "“男一匹道中記”とあるが、実は太助と恋女房お仲の新婚一周年・二人旅を描くてん末記。旅先で鯛の出荷を牛耳り、大もうけをたくらむヤクザと豪商の陰謀を知った太助が、持ち前の機転と行動力で立ち向かう。錦之助の魅力を最大限に生かした、沢島忠の代表的シリーズの最終作。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛は降る星のかなたに", "愛は降る星のかなたに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "日米間に緊張した空気が流れ始めた昭和16年、ヒトラーの信任厚いゾルゲによる赤色スパイ事件に関与したかどで、元内閣嘱託・坂崎秀美は逮捕される。残された妻・栄子や秘書・くに子は坂崎の真意を世に問うべく、彼の手紙の整理を始めるが……。ゾルゲ事件に連座した尾崎秀美をモデルにしたメロドラマ。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヘヴィメタル・イン・ザ・カントリー", "ヘヴィメタル・イン・ザ・カントリー", "", "", "http://image.eiga.k-img.com/images/movie/53767/315x210.jpg?2871", 74, "平凡でのどかなドイツ南部の村、ドンツドルフと、そこに本拠地を構える世界的に有名なインディペンデント系ヘヴィメタル専門レコード会社「ニュークリア・ブラスト」の実態を描いたドキュメンタリー。村の産業のほとんどがヘヴィメタル関連で、のどかな村の風景に、生活の一部として荒々しいメタルが自然と溶け込み、共存している光景はシュールで斬新。ニュークリア・ブラスト所属アーティストのPVを同時上映。", "2008-09-27", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロマンス", "ロマンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "大学時代の友人同士である小説家志望の市役所開発課職員と不動産屋。彼らの友情と、二人に絡む不安定な女性の奇妙な三角関係を描く。練り上げた脚本に、俳優たちの卓越した演技。とりわけ玉置浩二の大胆気ままな存在感が観る者を圧倒する。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'argent", "ラルジャン　ニュープリント版", "", "", "http://image.eiga.k-img.com/images/movie/56677/315x210.jpg?2030", 85, "1999年に他界したフランス映画界の名匠ロベール・ブレッソンの名作をニュープリント版で上映する「映画の國名作選III ロベール・ブレッソンの芸術」にて上映。ロシアの文豪トルストイの原作をもとに、偶然手にしてしまったニセ札を知らずに使ってしまったことから、一家惨殺事件を引き起こし転落していく人生を歩む青年イボンの姿を描く。ブレッソン監督の遺作。", "2011-06-25", "2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LET’S GET LOST", "レッツ・ゲット・ロスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ファッション・カメラマンとしても有名なB・ウェーバーによる不世出のトランペット奏者、C・ベイカーのドキュメンタリー・フィルム。チェットがホテルの窓から落ちて死ぬ直前のツアーに同行したスタッフが撮った映像は、彼や友人のインタビューを交えながら鮮烈な世界を紡いでいく。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ENDLESS　SUMMER", "終りなき夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "今なおサーファーの支持を得る、サーフィン・ドキュメンタリーの金字塔。サーファーのM・ヒンソンとR・オーガスト、彼らの姿を捉えるカメラマンのB・ブラウンの３人は、これまでに見たことのない波を求めて西アフリカへ。セネガル、ガーナ、ナイジェリア、ケープタウンを経由し、各地で未知の海に挑みながら、彼らはついに南アフリカ東海岸のダーバンで、機械で作ったように正確な、サーフィンにはうってつけの波を発見する。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Go Fast", "ゴー・ファースト　潜入捜査官", "", "", "http://image.eiga.k-img.com/images/movie/54477/315x210.jpg?3300", 89, "麻薬組織に潜入した警察官の実体験をもとに描くサスペンス・アクション。主演は「あるいは裏切りという名の犬」のロシュディ・ゼム。麻薬密売の捜査中に同僚を失った警察官のマレクは、猛訓練の末に潜入捜査官となり、大量の麻薬をスペインから輸送する組織に潜入する任務に就くことに。モロッコからスペインを経てフランスへ、ドラッグを積んだスポーツカーが危険なカー・チェイスを繰り広げる。", "2009-08-29", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("木更津キャッツアイ　ワールドシリーズ", "木更津キャッツアイ　ワールドシリーズ", "", "", "http://image.eiga.k-img.com/images/movie/33717/315x210.jpg?1485", 131, "宮藤官九郎脚本の人気TVドラマの映画化第2弾。前作「日本シリーズ」で描かれたぶっさんの死から3年、4人のキャッツたちは「それを作れば、彼はやってくる」というケビン・コスナー主演のある映画と同じ声に導かれて、チームを再結成する……。監督はTV、前作と同じ金子文紀。", "2006-10-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カンゾー先生", "カンゾー先生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "「うなぎ」でカンヌ映画祭パルム・ドール賞を受賞した今村昌平が、坂口安吾の『肝臓先生』に、『堕落論』『行雲流水』を組み合わせて一遍の物語を創出した人間ドラマ。昭和20年、敗戦間近の瀬戸内の田舎町で開業医をする赤城風雨。何でも肝臓病と見立てることから“カンゾー先生”と呼ばれる彼が、物資の不足する戦時下で、世間的には知られていない肝臓炎を撲滅しようと奔走する。“医は仁術”を地でいくカンゾー先生のバイタリティーとほのぼのとした人間性を、骨太なタッチで活写。同時に、大らかなセックス観を持ちながら相手の男からはしっかり金を取るあっけらかんとした自然児・ソノ子や、医者でありながらモルヒネ中毒となっていく鳥海、破戒坊主の梅本など、カンゾー先生を取り囲む個性的な人間たちの群像劇を映し出していく。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("脱線三銃士", "脱線三銃士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 44, "タクシーの運転手・八波君とペンキ屋の看板描きの由利君、サンドイッチマンの南君のおとぼけ３人組は、それぞれ仕事で大失態を演じ、クビになる。そろいもそろって惚れ込んだ食堂のお咲ちゃんに“強くて男らしくてスマートなのは自衛隊の幹部さんよ”と言われた彼らは自衛隊に入隊するのだが……。脱線トリオの主演による軽快なコメディ。DVDは「新東宝映画傑作選　BOX　FUNNY&ELEGY　編」に収録。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪友", "悪友", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「極道の妻たち」で脚光を浴び、「私を抱いてそしてキスして」で第22回大宅ノンフィクション賞を受賞した気鋭のライター、家田荘子のヤクザ社会の新人を追った原作を映画化した作品。なぜかヤクザの有栖川組に就職した瞳と涼に、やっと大きな仕事が与えられる。瞳は取り立て、涼はスケコマシに励むが抗争に巻き込まれボロボロになってしまう。何とか組から逃れたものの、すでにヤクザとしてしか生きられない二人だった。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POSSESSION", "ポゼッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "長い単身赴任生活を終え、妻アンナの元に帰ってきたマルクを迎えたのは、妙に冷たい態度の彼女だった。やがて妻の不倫相手ハインリッヒと対峙したマルクは、彼からアンナの“３番目の相手”の存在を聞かされ愕然とするが……。「私生活のない女」「狂気の愛」など一人の女性を中心に据え、その愛と狂気の両極性を描いてきたズラウスキー監督が、1980年に発表した作品。全身を震わせ、体液を絞り出すような嘔吐や、自らの妄想が生み出した魔物との性交シーンなど、I・アジャーニの際立った演技が光る。", "1980-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE SIGNE DU LION", "獅子座", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ヌーヴェル・ヴァーグの名匠E・ロメールが1959年に撮った長編第１作。この作品と前後してトリュフォーの「大人は判ってくれない」やゴダールの「勝手にしやがれ」が製作されており、それらの作品と並んでヌーヴェル・ヴァーグの誕生を語る上で欠かせない重要な作品である。獅子座生まれの中年作曲家はそれまで運のない人生を送ってきたが、ある日伯母の遺産が入ることになる。喜んでパーティーを開く彼のもとに、遺産はすべて従兄のものになるという知らせ。金を使い果たし浮浪者同然に街をふらつく彼だが……。会話中心のロメール・タッチはすでにこの作品から見ることができる。DVDは「エリック・ロメール コレクション(1)」に収録。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHOSES　SECRE｀TES", "ひめごと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「白い婚礼」などで知られるフランスの異才ジャン・クロード・ブリソーが、きわどい性行為や官能世界の裏に潜む人間の本心を巧みに抽出。その美貌を最大の武器に男を破滅に追い込もうとする女性二人のたどり行く皮肉な運命を見据える。トラブルで店をクビになったサンドリーヌは、同店のヌードダンサー、ナタリーの家に転がり込む。やがて彼女は“セックスは武器”と言いきるナタリーの計画に乗り、美貌を生かして仕事を得ると、その勤め先の上司を誘惑し始める。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("槌音", "槌音", "", "", "http://image.eiga.k-img.com/images/movie/57387/315x210.jpg?2033", 23, "2011年3月11日の東日本大震災の発生直後に被災地で撮影されたドキュメンタリー。岩手県大槌町出身の映像作家・大久保愉伊が、被災した故郷と家族を撮影した。同じく震災直後の被災地を記録した「大津波のあとで」と同時上映。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUTINY ON THE BOUNTY", "戦艦バウンティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 177, "1787年に南太平洋で実際に起こった、英国の戦艦バウンティ号の叛乱を題材にしたハリウッド・スペクタクル。叛乱事件を中心に、冷酷な艦長ブライと、事件の首謀者クリスチャンの対決がドラマティックに描かれる。ブランドとハワードの迫真の名演が印象深い一編。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Betsy", "ベッツィー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "デトロイトを舞台に、全米自動車業界の内側に渦巻く愛と欲望に満ちた人間模様を描いたもの。ヨーロッパと日本勢の挟み撃ちに遭って苦悶する老自動車王が起死回生を図って夢の新車“ベッツィー”を開発し、人生最後の賭けを開始する。老練オリヴィエがはまり役で好演。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・社長三代記", "続・社長三代記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "前作に続いて三代目のお話。福富電機の二代目社長・浅川啓太郎は女性問題が昂じてアメリカ支店に飛ばされ、三代目にはマジメ一本の大場太平が任じられた。ところがこの大場社長、突然女狂いになってしまったから大騒ぎ。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ストレイン", "ストレイン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "腹違いの兄である大企業グループの総帥、日下俊一郎の罠に落ち、異国の地マレーシアで死刑判決を受けた慎吾。だが、何者かの手によって死刑を免れた彼は５ドルの報酬で人を消す殺し屋へと変貌していく。そんな中、慎吾は若い女シオンと運命的な出会いをするが……。『ビッグコミック』に連載された人気コミックの映画化。同じ血を受けた血族の宿命の戦いを描くアクション。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和残侠伝・人斬り唐獅子", "昭和残侠伝・人斬り唐獅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "皆川一家と東雲一家は玉の井をめぐって争っていた。花田秀次郎は、渡世の義理から皆川の親分を斬る。わだかまりがとけると秀次郎は皆川の二代目の後見人になる。両者の対立は皆川の後押しをする剣一家も巻き込み、剣の親分は銃弾に倒れる。それを知った秀次郎は……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たん子たん吉珍道中", "たん子たん吉珍道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "家康公からの大事な下り品である茶壷を無事江戸に届けなければならない白菊姫は、途中の山道で２匹の仔狸を助ける。仔狸は命の恩人のために茶壷を守ってあげようと決心、たん子たん吉という人間の姿に化けた。そのころ江戸では家老たちが殿に陰謀を持ちかけていた。子役・松島トモ子がその魅力を十二分に発揮する、民放人気番組の映画化。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("undo", "undo", "", "", "http://ecx.images-amazon.com/images/I/2191DW1JTCL._SL210_.jpg", 47, "「Love Letter」「花とアリス」の岩井俊二監督初の劇場公開作品。夫ユキオの仕事が忙しくなるにつれ、モエミは身の回りのものを縛らずにはいられなくなっていく。とうとう彼女は、自分自身を縛ってくれとユキオに頼むのだった。主演は山口智子、豊川悦司。また、モエミを強迫性緊迫症候群と診断するセラピストを田口トモロヲが演じる。", "1994-10-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Toyo's Camera", "東洋宮武が覗いた時代", "", "", "http://image.eiga.k-img.com/images/movie/54197/315x210.jpg?3470", 0, "第2次世界大戦中、アメリカの市民権を持つにも関わらず強制収容所に収容された東洋宮武は、収容所内で手製のカメラを作り、そのカメラで収容所の実態を記録した。東洋の500枚の写真と、東洋と親交があった写真家アンセル・アダムスとエドワード・ウェストンの作品と共に、収容所の真実と日系人の歴史を描くドキュメンタリー。", "2009-04-11", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談夜泣き燈籠", "怪談夜泣き燈籠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "友人にだまされ、生き埋めにされた豪商の息子。まだ死んでいなかった彼は、墓所からはい出て友人をこらしめた。しかし、その後彼は信用していた親方に女を取られた上に殺害される。親方への恨みを持って、今度は本物の幽霊となるが……。「座頭市」第１作で知られる名脚本家、犬塚稔のオリジナル。ラストに発狂する、女の狂気の描写が見もの。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Other Guys", "アザー・ガイズ　俺たち踊るハイパー刑事！", "", "", "http://image.eiga.k-img.com/images/movie/55287/315x210.jpg?3066", 107, "ウィル・フェレル＆マーク・ウォールバーグがニューヨーク市警の刑事に扮したアクション・コメディ。現場では働かずデスクワークばかりのアレン（フェレル）と、過去の失敗を引きずっているテリー（ウォールバーグ）の2人が、NY市警の人気黒人コンビに憧れて、難事件の解決に挑むことになるが……。共演にエバ・メンデス、サミュエル・L・ジャクソン、ドウェイン・ジョンソン。監督は「俺たちニュースキャスター」のアダム・マッケイ。", "2011-08-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Revelation des Pyramides", "ピラミッド 5000年の嘘", "", "", "http://image.eiga.k-img.com/images/movie/57127/315x210.jpg?1502", 106, "古代エジプト文明の象徴として知られ、紀元前2700～2500年代に建造されたと伝えられているクフ王の墓・ギザの大ピラミッドの真実を紐解くドキュメンタリー。37年間にわたる調査と研究を実施し、建設・物理・数学・天文学などあらゆる方面から科学的な検証を行い、ピラミッドに隠された数々の疑問に答えを出していく。", "2012-02-18", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("せんせい", "せんせい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "原爆病をテーマに実在の女教師の一生を綴った感動のドラマ。長崎の小学校へ赴任してきた川口竹子は、明るく生徒たちの人気者になる。しかしある日、急病で倒れ入院し、白血病であることが判明する。彼女は幼かった頃、原爆投下まもない長崎を母に連れられて通ったことがあったのだ。長崎のオール・ロケが美しく、五十嵐の明るい個性が生かされている。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀座化粧", "銀座化粧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "井上友一郎の同名小説を映画評論家の岸松雄が脚色、成瀬巳喜男が監督した女性映画。昔の情人との間にできた息子とともにひっそりと暮らす、しがない銀座のホステスの姿を名女優・田中絹代主演で描く。銀座の裏町の風俗描写や人間関係の処理に成瀬の手腕が冴え、これを機に、成瀬は長いスランプ状態を脱した。ちなみに、助監督に石井輝男がついている。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("武闘派仁義", "武闘派仁義", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "牛次郎原作、那須輝一作画の劇画『掟の紋章』の映画化。時代の波から取り残された武闘派ヤクザが、敵対する組織と抗争し、味方の組織には破門され、四面楚歌のなか、両者のドンと渡り合う。経済的な才覚で隆盛する組織からハミ出した一匹狼的ヤクザの壮絶な闘いぶりが見どころ。渡辺裕之が主人公を怪演。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAT PEOPLE", "キャット・ピープル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "造船デザイナーのオリヴァーは、動物園の黒豹の檻の前で、デザイナー志望の美しい娘イリーナと知り合う。二人はやがて愛し合い、結婚するが、彼女はオリヴァーにキスさえ許そうとしない。実はイリーナは猫族の血筋を引いていた。伝説によれば猫族の女は人間と結ばれると豹に変身し、相手を喰い殺してしまうと言い伝えられており、彼女はそれを恐れていたのだ。オリヴァーはイリーナのことを同僚のアリスに相談する。しかし、そのことで嫉妬したイリーナは黒豹に変身し、二人をつけ狙う。夜のシーンや姿を見せない黒豹など造形的要素によって恐怖感を生み出したホラー映画の古典的名作。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八ツ墓村", "八ツ墓村", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "鳥取と岡山の県境にある不気味な伝説が残る八ツ墓村。この村の鍾乳洞に眠る黄金3000枚をめぐって、次々と恐怖の惨劇が起きていく……。この頃には、千恵蔵の金田一耕助もすっかり板に付いてきた。当時ニュースターの御園裕子と大映から客演した相馬千恵子が彩りを添えている。本作はその後も２度リメイクされた。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH NOON", "真昼の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "西部の小さな町ハードレービルの日曜日。教会で結婚したばかりのシェリフが任期を終え、新妻ケリーと町を出ようとしている時、彼を仇と狙う４人の無法者がお礼参りに来るという知らせが入る。町の人は、勝ち目のないシェリフを助けようとしない。決闘の時は刻々と迫ってきた……。保安官が人の助けを求めるという異色の設定を、ジンネマンが緻密なリアリズム描写で撮り上げた西部劇の傑作。10時40分から、凄絶な１対４の決闘シーンまでの１時間20分が、現実の時間に合わせて構成されていて、その息詰まるような臨場感は出色。J・W・カニンガムの『ブリキの星章』をフォアマンが脚色。主題歌『ハイ・ヌーン』は大ヒットし、クーパーは二度目のオスカーに輝いた。「ハイ・ヌーン」というタイトルでリバイバル上映もされた。", "1952-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Feast", "ザ・フィースト", "", "", "http://image.eiga.k-img.com/images/movie/53507/315x210.jpg?2491", 86, "マット・デイモン、ベン・アフレックらが設立した映画製作会社がプロデュースする新人発掘オーディション番組\"プロジェクト・グリーンライト\"から誕生したモンスター・パニック・ムービー。テキサスの荒野に建つバーに、銃を持った血まみれの男が乱入してくる。男はバーに居合わせた人々に、謎の怪物の群れが近づいて来ていることを警告。すぐにバーの封鎖を指示するが、窓際に立った瞬間、無残にも怪物に引き裂かれてしまう。", "2008-03-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUNK MAIL", "ジャンク・メール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "他人の郵便物を破棄したり勝手に開けたりしてしまう不謹慎な郵便配達夫ロイ。ある日、クリーニング店に勤める難聴の娘リーナに心惹かれたロイは、彼女の家に忍び込んだことをきっかけに、犯罪絡みのややこしいトラブルに巻き込まれていく。ノルウェーの俊悦P・シュレットアウネが、本国のアカデミー賞にあたるアマンダ賞で主要部門を独占した長編デビュー作。主人公のまったく刺激のない日常がふとした偶然から一変していく様を、人を食ったようなユーモアとサスペンスを交えてひょうひょうと描き上げる。オスロの街の錆びついたたたずまいはロマンティックなムードとはかけ離れているが、ほのかな幸福感が伝わるラストシーンには何物にも代え難い詩情が立ちこめている。郵便バッグを幾つもぶら下げた主演俳優R・シャーシュタの奇妙な歩き方も、一度見たら忘れることができない。1997年の東京国際映画祭ヤングシネマ部門に出品されブロンズ賞を獲得した。", "1996-01-01", "36", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美しい十代", "美しい十代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "薄幸の少年、少女の純愛を描いた宮内婦貴子のオリジナル・シナリオを歌謡ドラマに仕上げた一編。主人公はみなしごのミカと、暴力団の幹部になりたくて、その機会を待つ少年・純。二人の出会いから真実の愛に気づくまでを描く。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちびまる子ちゃん　わたしの好きな歌", "ちびまる子ちゃん　わたしの好きな歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "原作者さくらももこのオリジナル・ストーリーに基づくアニメ・シリーズの劇場版第２作。おませな小学生まる子と、画家を目指すお姉さんとの交流を描く。図工の授業で童謡『めんこい仔馬』の絵を描くことになった、まる子は、知り合いになったばかりのお姉さんのアドバイスで絵を描き上げる。そんなある日、お姉さんが恋人にプロポーズされ……。細野晴臣、大瀧詠一など、原作者の愛聴している音楽が全編にフィーチャーされ、ほのぼの感覚を盛り立てる。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("29th STREET", "奇跡が降る街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "生まれた時から知らず知らずのうちにツキに恵まれていた27歳のフランクは、何と、賞金620万ドルの宝くじの最終候補者に選ばれる。一方、フランクの父親はギャンブルに浸り金をスッてばかり。どうやらギャングに１万ドルもの借金をしているらしい。最終候補者になった夜、そのことを知ったフランクは父親のために、ある一大決心をする。クリスマスのニューヨークを舞台に、親子の愛情をほのぼのと描き出すハートウォーミングな作品。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pour Elle", "すべて彼女のために", "", "", "http://image.eiga.k-img.com/images/movie/54917/315x210.jpg?3260", 96, "フランスのパリ。教師のジュリアンと出版社に勤めるリザの夫婦は、息子のオスカルと平凡ながらも幸せに暮らしていた。しかし、上司殺害の容疑でリザが突然逮捕され、日常は一変する。ジュリアンは妻の無実を証明しようと奮闘するが、あっという間に3年の月日が流れ、リザに20年の禁固刑が宣告されてしまう。主演はバンサン・ランドン、ダイアン・クルーガー。監督は本作が劇場デビュー作となる新鋭フレッド・カバイエ。", "2010-02-27", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Do the Right Thing", "ドゥ・ザ・ライト・シング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "　ブルックリン、その年一番の猛暑の日。黒人街にあるピザ屋でいさかいが起こった。ある者が店内に黒人スターの写真が一枚も貼られていないことで憤慨。経営者のイタリア人はそれを相手にしなかったが、この一件がきっかけとなり、やがて事件が。その日暮らしのアルバイター、飲んだくれの哲人、ピザ屋の主人の息子たち、韓国人のカップル、といった面々を巻き込んで、ついには暴動へと発展していく！　監督スパイク・リーの名を世界中に知らしめたパワフルな衝撃作。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学生社長", "学生社長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "アルバイト合資会社を作った３人の大学生の恋と友情の物語を中心に、子供のために更生しようと決意する老スリ師の姿を描く。中野実の原作を川島雄三、柳沢類寿とともに練ったのは助監督だった野村芳太郎。学生会社という現代にも通用する設定に親子の情という浪花節的世界を放りこんでいる。学生社長に扮する鶴田浩二の学生服姿が初々しい。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瀬戸はよいとこ 花嫁観光船", "瀬戸はよいとこ 花嫁観光船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "本四架橋の一つである明石・鳴門ルートの橋建設をめぐる３人の男たちの利権争いと、地元の発展に精を出す強気の女たちとの、悪戦苦闘を描いた喜劇。釣り宿の主人に山城新伍、それを買収してリゾートホテルの建設をたくらむパチンコ屋にフランキー堺が扮して笑わせる。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逃げだした縁談", "逃げだした縁談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "３人の子供を持つ瀬戸口鉄造は、日之丸自転車の販売係長だ。娘の昌代は宣伝課の三上、昭和製菓宣伝課に勤める長男・正一はデパート店員の小島とみ江、城北大生の次男・明夫は女子大生の西条則子と３人の子供たちはそれぞれ好意を持つ相手がいた。そんなある時、鉄造に課長昇進の話がまわってきたが……。松竹大船撮影所が最も得意としたホーム・ドラマの佳作。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大怪獣東京に現わる", "大怪獣東京に現わる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "怪獣が登場しない、何ともユニークな怪獣映画。東京に出現した怪獣が街を破壊して北上。最初は他人事のように思っていた福井県三国町の人々も、このニュースを聞いて大慌て。やがて九州に第二の怪獣が現れたことから、２大怪獣が北陸で激突する可能性が出てきた。三国町民は軍が守ってくれるであろう安全な場所を求めて、原子力発電所へと避難するが……。TVのニュースを通して怪獣の動きを伝えつつ、町民たちのパニックをユーモラスにかつシニカルに描写。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A View from the Bridge", "橋からの眺め", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　ブルックリンの波止場で働くエディは妻ベアトリス、そして妻の姪カテリーヌと暮らすイタリア移民。ある時、妻のいとこにあたるマルコとその弟ロドルフォがイタリアから不法入国してくる。エディは兄弟を家に置いてやり、さらに仕事の世話まですることに。しかしロドルフォとカテリーヌが愛し合う仲になり、事態は一変する。美しいカテリーヌのことを常に気にかけていたエディはふたりの関係を許そうとしなかった。やがて彼らに悲劇が……。アーサー・ミラーの同名戯曲を映画化したドラマ。", "1961-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I LOVE TROUBLE", "アイ・ラブ・トラブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "N・ノルティとJ・ロバーツによるラブ・コメディ。『シカゴ・クロニクル』紙の人気コラムニスト、ピーターは列車脱線事故の取材をすることになる。現場ではライバル紙の新米記者サブリナが精力的に取材をしていた。ピーターはサブリナを子供扱いするが、翌朝、同紙はサブリナの『シカゴ・グローブ』紙に惨敗を喫する……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE　PLACARD", "メルシィ！人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "コンドーム会社に勤める真面目一筋20年のサラリーマン、ピニョンがリストラ対策のため、自分はゲイだとカミングアウト。この嘘に態度をコロコロ変える同僚たち、ゲイ差別批判を恐れてリストラを慌てて取り消す社長など、噂に振り回される人々を面白おかしく皮肉った軽妙なフレンチ・コメディ。真面目に演じるほどトボケた笑いを誘うD・オートゥイユ、マヌケな小心者ぶりがコミカルなG・ドパルデューほかキャスト陣のこなれた演技も魅力的。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STALKING MOON", "レッド・ムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1881年のアリゾナ。インディアン居留区で暴動が起き、鎮圧に向かったサム・バーナーはアパッチの一団を保護した。しかし、その中にいた白人の女が凶悪な酋長サルバヘの子供を連れていたことから、一行は執拗な追跡を受けるはめになる。サスペンスを盛り上げるR・マリガン監督の手腕、簡潔なショットなど、アメリカン・ニューシネマの先駆けともなった異色の西部劇。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蝉しぐれ", "蝉しぐれ", "", "", "http://image.eiga.k-img.com/images/movie/1637/315x210.jpg?4316", 131, "市川染五郎、木村佳乃主演の人情時代劇。舞台は江戸時代の東北の小さな藩。15歳の文四郎は、下級武士の義父・助左衛門を手本に剣術と学問に励んでいたが、父が藩の世継ぎを巡る陰謀に巻き込まれて切腹を命じられ、文四郎の生活は激変する。原作は人気時代小説家、藤沢周平の同名小説。監督は「オルゴール」「英二」の黒土三男。黒土は原作に惚れ込んで15年もの間、映画化を熱望、03年のTV版の脚本を経て、遂に監督として映画化を実現。", "2005-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フジヤマにミサイル", "フジヤマにミサイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "別れた恋人同士だが、やり直す機会を探っているバンドマンとAV女優の胸の内を丹念に描いた恋愛ドラマ。CM界出身の新鋭、冨嶽岡がリアリズムに徹したタッチで、登場人物の心理に鋭く迫る。山口弁を交えた自然体のセリフに加えて、ヒロイン・紺野千春のナチュラルな存在感も印象深い。バンドマンの新太郎は３年前に別れた恋人、冬子を忘れられずにいる。冬子もAV女優の仕事を辞め、彼の思いに応えようとするが……。", "2005-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("20世紀少年　第2章　最後の希望", "20世紀少年　第2章　最後の希望", "", "", "http://image.eiga.k-img.com/images/movie/54027/315x210.jpg?4664", 140, "浦沢直樹の同名人気コミックを全3部作で映画化する第2部。“ともだち”が支配する西暦2015年、“血の大みそか”以降ケンヂは行方不明のままだった。ケンヂの姪カンナは高校生に成長し、オッチョ、ユキジ、ヨシツネ、マルオらは、それぞれの方法で“ともだち”の正体を追っていた。監督は「まぼろしの邪馬台国」の堤幸彦。豊川悦司、常盤貴子、香川照之ほか、多数の豪華キャストが出演。", "2009-01-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ILLEGALLY YOURS", "ロブ・ロウのおかしなおかしな探偵物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "恋にやぶれ、帰郷したリチャードは、ひょんなことから裁判の陪審員を務めることに。ところが、その被告が彼の小学校時代の初恋の人だったから大変。なんとか彼女を救おうとリチャードの悪戦苦闘が始まるが……。R・ロウ主演のロマンティック・コメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヨムトシヌ DEATH COMIC PART2", "ヨムトシヌ DEATH COMIC PART2", "", "", "http://image.eiga.k-img.com/images/movie/54507/315x210.jpg?2372", 84, "呪われたコミックについての都市伝説を描いた「ヨムトシヌ　DEATH　COMIC／PART1」の続編。“赤いコミック”の謎を追究するシズカとリョウコは、ついにコミックを描く謎の少女の存在を明らかにする。しかしそんな2人にも、コミックの恐怖が忍び寄っていた……。", "2009-04-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TARTARS", "黒い砦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "草原の民ダッタン族と、海賊バイキングの戦いを描いたスペクタクル活劇。バイキングの首長オリグとエリックの兄弟は、ある日ダッタン族の砦に向かう。ダッタン族の頭領トグラルはこれを温かく迎えるが、オリグは彼を殺し、娘のサミアを奪って逃走する。トグラルのあとを継いだ弟のバランダイは兄の復讐を誓うが、さらわれたサミアはいつしかエリックを愛するようになって……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE AFFAIR", "めぐり逢い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1939年の「邂逅」、1957年の「めぐり逢い」をリメイクしたラブ・ストーリー。元フットボールのスターで現在スポーツ解説者のマイクは婚約をしていたが、仕事のために乗った飛行機の中で出会ったピアノ教師で生計を立てる無名歌手テリーに惚れてしまう。テリーにもまた実業家の婚約者がいた。しかしマイクもテリーも打算による婚約であり、この運命的な出会いにより、お互いの心は激しく揺れるのだった……。実生活でも夫婦同士のW・ベイティとA・ベニングの息の合ったロマンティックな演技が心に残る。「黄昏」以来、スクリーンから遠ざかっていたK・ヘプバーンが、タヒチに住むマイクの叔母ジニー役で好演。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLOAK AND DAGGER", "外套と短剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「死刑執行人もまた死す」「恐怖省」に続いてF・ラングが三たび、反ナチをテーマに取り上げたスパイ映画。アメリカの物理学者ジェスパー教授は、同じ物理学者でファシストのために原爆の研究に従事させられているポルタ博士を救出するためにイタリアへ潜入した。博士を捜してレジスタンスの女性闘士と転々とするうち、二人は愛し合うようになるが……。この映画が作られたときには、すでに戦争は終わっていて、戦略兵器として原爆が問題になり始めていた。この映画もナチを題材にしながらも、オッペンハイマー博士やフェルミ博士ら当時の著名な学者をモデルにしているのがわかる。原爆にかかわるラストの部分はカットされた。敵はもうドイツではないという時代の雰囲気も、その奥から伝わってくる。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("G-Force", "スパイアニマル・Gフォース", "", "", "http://image.eiga.k-img.com/images/movie/53237/315x210.jpg?2017", 89, "世界征服を企む悪の億万長者の野望を阻止するため、高度の知能を持った動物の捜査官たちが活躍するアドベンチャー。製作にジェリー・ブラッカイマー。邪悪な億万長者レナード・セイバーは、全米のあらゆる家族を対象に、殺人マシーンによる破壊計画を立てていた。高度な機密訓練を受けたモルモット集団「Gフォース」は、人類の危機を救うべく、セイバーとの戦いに挑む。", "2010-03-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("この広い空のどこかに", "この広い空のどこかに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "木下惠介の妹である楠田芳子のオリジナル脚本を松山善三が潤色し、小林正樹が監督した小市民映画。川崎で酒屋を営むある一家を題材に、妹と姑、戦災で足が不自由になった義妹らの確執と和解を描く。いずれも名優ぞろいの出演者たちがそれぞれ好演、貧しくても懸命に生きる庶民の生活がいきいきと描き出される。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TABACCO ROAD", "タバコ・ロード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "J・フォードは西部劇、戦争ものなど多彩な作品を送り出したが、この作品は、彼のもう一つの面で「怒りの葡萄」「わが谷は緑なりき」などの系列に属するもの。戦後アメリカ映画が続々と公開される中、貧しいアメリカを描いているという理由から輸出されず、日本では1988年、製作以来実に47年ぶりに封切られた。1930年代のジョージア州の貧しい不毛の土地で暮らしている一家。老農夫のジーターは汚い小屋で生活している。一家は常にスキッ腹で、訪ねてきた娘が持ってきた“カブラ”を奪いあうといったありさまだった。頭のちょっと弱い長男、自称伝道師といった人間たちの生活を、ある時は冷静に、またある時は温かくユーモアを交えて描く演出は、美しい白黒撮影とあいまって一級品の出来。若い娘エリーに扮する若き日のG・ティアニーが美しい。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chloe", "クロエ", "", "", "http://image.eiga.k-img.com/images/movie/56047/315x210.jpg?1446", 96, "大学教授の夫と息子の3人で幸せな人生を歩んできたキャサリンだったが、ある日、夫の携帯電話に教え子との浮気を思わせるメールを見つけてしまう。疑心暗鬼にかられたキャサリンは、偶然知り合った若く美しい娼婦のクロエに夫を誘惑させ、夫がどんな行動を取るかを報告させようとするが……。「スウィート　ヒアアフター」のアトム・エゴヤン監督が、ジュリアン・ムーア、リーアム・ニーソン、アマンダ・セイフライドを起用して描く愛憎と官能のサスペンス。", "2011-05-28", "10,2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hanging on a Star", "ハンギング・オン・スター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "酒場で少ない客を相手に演奏活動を続けているジェフ・マーティン・バンドのもとに、マネージャー志願のケティが押しかけてきた。彼女の努力が功を奏してバンドは野外フェスティバルへの参加が決定する。D・ラフィン主演の青春音楽映画。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INCOGNITO", "迷宮のレンブラント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "パリ、アムステルダムなどヨーロッパ各地をめまぐるしく移動するサスペンス・ロマン。素晴らしい画才を持ちながら、裏街道を生きる贋作者に落ちぶれたニューヨークの青年ハリー。ある人物からレンブラントの贋作という困難な仕事を依頼された彼は、凄まじい集中力でそれを成し遂げるが、いつしか殺人容疑をかけられ逃亡の身に……。アクション派の職人監督J・バダムが、様々なテクニックや材料を用いる贋作づくりのシーンを緻密なカメラワークで描く。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CYNARA", "シナラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ロンドンの高名な弁護士が妻の旅行中に若い娘と不倫の仲になり、やがて彼は家庭へ戻ろうとするが、それを知った娘が自殺。彼はスキャンダラスな法廷の証言台に立つ身となる。しかし、名声を失い南アフリカへ去ろうとする彼を追ったのは、再出発を願う妻であった。メロドラマながら上質の一編。", "1932-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("京浜抗争史外伝・最後の組長", "京浜抗争史外伝・最後の組長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "元鷹山組組長、鷹山は９年前の抗争で多くの組員を失い、今は堅気に戻っていた。そんな彼のかつての兄弟分、小宮山は東竜会の組長を務めていたが、敵対する武原組と自らの子分の策略によって殺されてしまう。そのころ、再びヤクザの世界に舞い戻っていた鷹山は、いつしか武原組と東竜会の内部抗争に巻き込まれていく……。“日本の首領”シリーズや“極道の妻たち”シリーズの脚本を手掛けた、任侠ものの第一人者、高田宏治が脚本監修を務めた現代ヤクザ映画。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大冒険", "大冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "クレージー・キャッツ結成10周年を記念して、世界に誇る東宝・円谷英二特撮を駆使しながら描く、国際的スケールの犯罪アクション・コメディ。世界各国で偽札が発見された。事件を調査する記者の植松は、警察と偽札団の双方から追われながらも神戸にたどりつく。そこには、組織の潜水艦Uボートが隠されていたのだが……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARENTS", "ペアレンツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「未知との遭遇」や「アルタード・ステーツ」の科学者役で知られる個性派俳優B・バラバンが監督デビューを飾ったブラック・ホラー。勤勉なパパとやさしいママがいる地方の中産階級。古き良きアメリカン・ファミリーといった感じだったが、９歳の少年は両親が人肉を食べていると信じていて……。アヴォリアッツ国際ファンタスティック映画祭批評家賞を受賞した。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARIS, TEXAS", "パリ、テキサス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 146, "テキサス州の町パリを求めて砂漠をさまよう男トラビス。倒れて口もきかない彼を弟がロサンゼルスの自宅に連れ帰ると、そこには四年前に置き去りにした息子がいた。今度は息子と一緒に妻を捜しに、ふたたびテキサスへと旅立つトラビスだったが、そこには思いがけないにがい再会が待っていた……。ミュラーの切り取る流麗な風景にライ・クーダーの哀愁の旋律が哀しい、ロード・ムービーの作家ベンダースの傑作。", "1985-09-07", "2,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHINING THROUGH", "嵐の中で輝いて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "1940年、ナチス・ドイツが台頭し世界情勢の先行きが怪しくなっていた頃、N.Y.で弁護士の秘書をしていたリンダは上司リーランドと恋に落ちていた。彼がアメリカ軍のスパイだと知ったリンダは、その純粋な愛のために危険を顧みずスパイ活動に身を投じていく……。「ルーカスの初恋メモリー」「パンチライン」でその演出力に注目を集めたD・セルツァーが豪華キャストを得て挑んだ戦争ロマン作品。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUMB AND DUMBER", "ジム・キャリーはMr.ダマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "J・キャリーとJ・ダニエルズの大ボケコンビによる、抱腹絶倒のスラップスティック・コメディ。ロード・アイランドでリムジンの運転手をしているロイドは、ある日空港まで送った社交界のレディ、メアリーに一目惚れ。彼女がスーツケースを忘れたことに気付いて、同居人のハリーを道連れに、車でアスペンまで届けることにする。ロイドもハリーも、純真ではあるもののケタ外れの大ボケ同士。メアリーのスーツケースには誘拐された彼女の夫の身代金が入っており、それを狙う凶悪犯に狙われながらも、大ボケぶりが功を奏して二人はアスペンまで行き着くことはできたのだが……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神童", "神童", "", "", "http://image.eiga.k-img.com/images/movie/33937/315x210.jpg?3286", 120, "さそうあきらの同名コミックを原作に、天才ピアニストの少女と音大を目指す青年の心の触れ合いをみずみずしいタッチで描いた青春ドラマ。ピアノの才能に恵まれながらもそれを持て余している13歳のうた。ある日、音大浪人生のワオと出会った彼女は、彼との交流を通して音楽の真の喜びに目覚めていく。主人公うたにテレビドラマ「瑠璃の島」などで注目を集めた成海璃子、ワオに「デスノート」の松山ケンイチが扮する。", "2007-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LUST FOR LIFE", "炎の人ゴッホ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "画家ヴィンセント・ヴァン・ゴッホの生涯を描いた伝記映画。監督は「巴里のアメリカ人」などのミュージカル映画で有名なV・ミネリだが、その独特な色彩感覚を駆使した映像は素晴らしく、物語構成もまた見事である。伝道の道を志しながらも教会に破門されたゴッホは画家としての活動を始め、30歳を過ぎて初めてパリへ。そこで知り合った豪放な画家仲間ゴーギャンと意気統合し、共同生活が始まるが、それぞれの個性の強さから二人の間に亀裂が……。ゴッホ役のK・ダグラスはまさにゴッホそっくりで、「夢」のマーティン・スコセッシのゴッホと比べるのも一興。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("独立少女紅蓮隊", "独立少女紅蓮隊", "", "", "http://ecx.images-amazon.com/images/I/51uF8yN89qL._SL210_.jpg", 72, "気鋭の新人監督たちが名監督に挑む「映画番長」シリーズの第1弾、「ワラ（＾O＾）番長」の1作で、これがデビュー作となる女性監督・安里麻里が手がけた美少女バイオレンスアクション。日本列島南端の島にあるダンススクール「オリオン・ダンサーズ・アカデミー」には、島の独立のために活動する女スパイ養成学校という裏の顔があった。厳しい訓練を経てスパイとなった4人の少女たちは、大都会・東京で与えられた任務を次々とこなしていくが……。", "2004-04-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フレンズ", "フレンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "日本最大のパチンコ・パチスロ攻略集団“梁山泊”の内情に迫った、ネオシネマ・シリーズの第３作。二人の幹部の仲違いから、梁山泊内に内紛が勃発。巨大組織を襲った抗争必至の危機的状況が、苦悩と葛藤、そして絆の物語として描かれていく。いぶし銀の実力派たちによる味わい深い演技はもちろん、大掛かりで派手なカー・アクション・シーンも見どころ。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代やくざ・盃返します", "現代やくざ・盃返します", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "任侠道と組長の命令との板ばさみになる若者を描く。堂本組の関根は親分・堂本から、大阪空港の拡張地や観光利権を握っている大阪箕面の博徒・萩原組組長を暗殺せよと命じられる。関根は堂本組とは兄弟同様の萩原を殺すことに疑問を感じるが……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD　AT　THE　MOON", "蒼い月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "M・S・マスターソンが疑惑に揺れる人妻に扮したラブ・サスペンス。札つきの不良、ミラーと付き合っていたジェニーは、ミラーの弟ジェームズの求婚に動揺。ミラーが去り、ジェームズと結婚したものの、彼女の心は、かつての恋人と夫との間で揺れ動いていた。やがて、ある満月の夜、ジェニーは夫ジェームズの意外な秘密を知ってしまい……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Young Adult", "ヤング≒アダルト", "", "", "http://image.eiga.k-img.com/images/movie/57477/315x210.jpg?2768", 90, "「JUNO ジュノ」の監督ジェイソン・ライトマン＆脚本家ディアブロ・コーディのコンビが、主演にアカデミー賞女優シャーリーズ・セロンを迎え、再タッグを組んだコメディドラマ。児童小説家のメイビスは、夫と離婚後すぐに故郷ミネソタに帰ってくる。そこで、かつての恋人バディに再会し復縁しようとするが、バディにはすでに妻子がいて……。共演は「ウォッチメン」のパトリック・ウィルソン。", "2012-02-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Équateur", "赤道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "自分の可能性を確かめるため、約束された将来を捨ててアフリカへやってきたフランス人青年ティマール。宿泊したホテルで謎の美女と一夜を共にしたティマールは、彼女の虜になってしまう。ミュージシャンとしても知られるセルジュ・ゲンズブールが、ジョルジュ・シムノン原作のサスペンスを、30年代から50年代に時代を移して描く。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アニメ三銃士 アラミスの冒険", "アニメ三銃士 アラミスの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "1988年から89年にかけてNHKで放送され、好評を博した『アニメ三銃士』のオリジナル劇場版。原作はアレクサンドル・デュマの『三銃士』だが、三銃士の一人アラミスを女性に変えた設定がファンの人気を呼び、映画版は彼女を主人公にした話となった。舞台は17世紀のフランス。生きていた悪女・ミレディーの陰謀に挑むアラミスの活躍が、その知られざる過去を交えて描かれている。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NEMA−YE NAZDIK", "クローズ・アップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "失業中のサブジアンはバスで隣り合わせた老婦人に自分は有名な映画監督モフセン・マフマルバフだと嘘をつき、家に招待される。家で寛いでいるサブジアンは、この家でロケをしたいと頼み、息子を出演させるとさらに嘘をつく。こうしてサブジアンは映画好きな一家を偽りの映画製作に巻き込んでいくのだった。実際に起きた話をもとに、その当人たちを起用して作られた作品。映画は、サブジアンが逮捕されるところから始まり、法廷や刑務所内でのサブジアンの様子などドキュメンタリー部分と、事件を再現したドラマの部分とが交差するように進行していく。なお、マフマルバフもイランでは高名な映画監督であり、本人が出演している。", "1990-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLASS OF NUKE’EM HIGH III", "悪魔の毒々ハイスクール３ ダブル・ヒューマノイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "前作をさらにパワーアップさせた“毒々シリーズ”の第３作。主演のB・ブロンスキーが一人３役を演じ、３フィートもある髪形のクレイジーなL・ゲイも引き続き登場。ここは“トロマヴィル”。ヒューマノイド部隊を率いた超エリート軍団が市民を襲う。そうとは知らないアドレイは、ヒューマノイドの安全性を人々に訴えていた。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エコエコアザラク　黒井ミサ　ファースト・エピソード", "エコエコアザラク　黒井ミサ　ファースト・エピソード", "", "", "http://image.eiga.k-img.com/images/movie/57087/315x210.jpg?3967", 60, "ホラーコミック界を代表する古賀新一、伊藤潤二、御茶漬海苔の3人が、各々の代表作を自らのメガホンで実写化した「古潤茶」プロジェクトの第1弾。人気オカルトホラー「エコエコアザラク」の古賀新一が原作・脚本・監督を務め、シリーズの主人公・黒井ミサが黒魔術に目覚めていく過程を描く。主演はドラマ「Q10」、映画「携帯彼氏」の前田希美。", "2011-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パラサイトドールズ", "パラサイトドールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「アニマトリックス」や「キル・ビル」のアニメ・パートを製作したプロダクションI.Gによる、３話構成のSFアニメーション。近未来の東京を舞台に、凶悪犯罪を繰り返す人造人間ブーマを取り締まる特殊任務組織ブランチの活躍を描く。海外の映画祭で大反響を呼んだ、アクション、エロス、SFなど様々な要素が凝縮されたアニメーション世界が刺激的。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("千夜一夜物語", "千夜一夜物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "“大人のための（劇場用）アニメーション”の製作を目指した手塚治虫が、“アニメラマ”と銘打ってそれを実現した第１作。バクダッドの水売り商人・アルディンがたどる数奇な運命を描いたもので、題材となった古典『千夜一夜物語』から“船乗りシンドバッドの冒険”、“アリババと40人の盗賊”などが引用され、自由奔放な物語を構成している。やなせたかしが手掛けたメイン・キャラクターのデザイン、アニメならではの官能シーンの描写、さらに声の主演に青島幸男を据えての、プレスコ方式による絵作りなど、意欲的な試みが多くなされた。なかでも遠藤周作、筒井康隆、大橋巨泉ら著名人に依頼しての“ひと声だけ”の出演がユニーク。アニメ映画作りに懸けた、手塚治虫の意気込みが伝わってくるような冒険ファンタジー。ただ多くの要素を詰めこみすぎて、全体に散漫な印象が残る。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RETRIEVERS", "CIA 殺しの報酬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "CIAの諜報員トムは、CIAの秘密組織“リトリーバーズ”の残虐行為を内部告発する原稿を書いたダニーに同調し、“リトリーバーズ”に生命を狙われながら、原稿を出版しようとする。監督が中国系のせいか、カンフー映画風の味つけもあるアクション映画。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("団栗と椎の実", "団栗と椎の実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 29, "臆病で内気な養子の少年と、その子をたくましく育てようとする父親の心温まるふれあいを描いた短編。子供を撮るのが得意だった清水宏らしく、ここでも子役はすれた演技ではなく、自然で伸び伸びとした表情を見せている。また、移動撮影を使って人物を背景に溶け込ませるところなど小品だが見事。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUNNING", "ランニング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「カッコーの巣の上で」など、製作者としても手腕をふるうM・ダグラスが、製作総指揮と主演を兼ねてその実力を発揮した意欲作。一流のマラソン・ランナーであるにもかかわらず中途半端な人生を歩んできた男が、再起を懸けてオリンピックを目指す。その過程で、男が真の人生を知る姿を描く。", "1979-01-01", "1,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん　のび太とアニマル惑星〈プラネット〉", "ドラえもん　のび太とアニマル惑星〈プラネット〉", "", "", "http://ecx.images-amazon.com/images/I/31MS4QKX1PL._SL210_.jpg", 100, "ある晩、のび太がピンク色のもやを見つけ、そこを通り抜けるとアニマル・プラネットにたどり着いた。高度な文明を築き、平和に暮らす動物たちのその星でチッポと知り合ったのび太たちは、アニマル・プラネットを征服しようとするニムゲ軍に挑んでいく。ネズミにかじられて以来なくなっていたドラえもんの耳が、プラネット内で復活。懐かしの姿に喜ぶドラえもんがかわいらしい。", "1990-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファンキー・モンキー・ティーチャー２　東京進攻大作戦", "ファンキー・モンキー・ティーチャー２　東京進攻大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "『ヤングマガジン』に連載された、もりやまつる原作の人気漫画の映画化第２作。大阪はミナミを根城とし、ヤバイことなら何でも手を出す高校教師・戸沢康平のアナーキーな活躍ぶりを描く。憧れのマドンナには飯島直子、早坂あきよが扮して華を添える。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("祝辞", "祝辞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "某大手会社の万年課長・早乙女良介はもうすぐ定年である。ある日良介は、専務から息子の結婚式で祝辞を述べてくれと頼まれる。口下手の良介はスピーチの本を買ったりビデオを見たり四苦八苦。ようやく結婚式の日が訪れたが……。十分に笑えるウェルメイドなコメディ。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お染久松 そよ風日傘", "お染久松 そよ風日傘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "家に帰れば自分に首ったけの山家屋のドラ息子、清吉が待っているし、兄貴の多三郎は女遊びの金に困って晴着を盗み出すしで憂うつになってしまった油屋お染。占ってもらったところ、お染はまもなく世紀の大ロマンスを展開すると出た。お染は大喜びするが、金策に失敗した兄貴のせいで清吉と結婚させられるはめになり……。美空ひばりのミュージカル時代劇。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Madagascar: Escape 2 Africa", "マダガスカル2", "", "", "http://image.eiga.k-img.com/images/movie/53937/315x210.jpg?1789", 79, "ニューヨーク動物園からマダガスカルに漂着した動物たちの珍騒動を描いたCGアニメの続編。ライオンのアレックス、シマウマのマーティ、キリンのメルマン、カバのグロリアは、ニューヨークに帰ろうとマダガスカルを飛行機で出発するが、アフリカ大陸に不時着。そこで生まれて初めて自分と同じ種の動物たちと触れ合い、しばしの感動を味わうが……。声の出演に、ベン・スティラー、クリス・ロック、ジェイダ・ビンケット・スミスら豪華キャストが揃う。", "2009-03-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EDWARD II", "エドワード II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ゲイに対する偏見に痛烈なメッセージを投げ掛ける作品。シェイクスピアと同時代の劇作家マーロウの戯曲をもとに、王政時代におけるゲイ・バッシングをあらわにしてみせた。D・ジャーマンの演出による想像を絶するエレガントなヴィジュアルは必見。ベルリン映画祭で国際批評家賞を受賞した。", "1991-01-01", "3,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG BAD MAMA", "ビッグ・バッド・ママ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "B級映画の神様R・コーマン製作によるバイオレンス・アクション。愛らしい娘たちを従えて、次々に銀行強盗を働くギャングママの破天荒な生きざまを小気味良い演出で描く。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIRD ON A WIRE", "バード・オン・ワイヤー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "M・ギブソン、G・ホーンという異色の組み合わせで描いたコメディ・タッチのサスペンス・アクション映画。15年前に恋人のリックを飛行機事故で亡くし、今も彼のことを忘れられずにいるマリアンヌは、ある日リックと再会して我が目を疑った。しかし彼は、自分はリックではないと言い張る……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仏陀再誕", "仏陀再誕", "", "", "http://image.eiga.k-img.com/images/movie/54777/315x210.jpg?2106", 110, "宗教法人「幸福の科学」の総裁・大川隆法による著作をベースにしたアニメーション映画。女子高生・天河小夜子は、新聞記者の金本に憧れてジャーナリストになることを夢見ていた。そんなある日、金本は汚職スキャンダルに悩み電車に飛び込み自殺を図る。事件にショックを受けた小夜子は、その日から霊が見えるようになってしまう。監督は「サクラ大戦」の石山タカ明。", "2009-10-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画 日本国憲法", "映画 日本国憲法", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "日本国憲法を世界の視点から見つめた、知的ドキュメンタリー。「老人と海」「チョムスキー9.11」のジャン・ユンカーマン監督が世界の知識人へのインタビューを通し、平和憲法として通る日本国憲法の意義を探る。我が国の憲法の理念を見直すと共に、改憲が叫ばれる時代に一石を投じる作品だ。戦争を放棄し、世界に向け恒久平和を宣言している“日本国憲法”。世界でも稀有な平和憲法についてさまざまな知識人が見解を述べる。", "2005-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AIRPORT'80−THE CONCORDE", "エアポート'80", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "毎回オールスター・キャストが話題を呼ぶ「エアポート」シリーズの第４作。今回はフランスの超音速旅客機コンコルドが登場。武器の不正輸入を行う実業家が、新型ミサイルや戦闘機を駆使して、乗客を乗せたコンコルド機の撃墜を狙った陰謀を描く。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Frankenweenie", "フランケンウィニー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NE RE´VEILLEZ PAS UN FLIC QUI DORT", "アラン・ドロン 私刑警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "パリを舞台に凄絶なポリス・アクション・ドラマが展開する、A・ドロン製作・脚本・主演によるフィルム・ノワール。ある日、たった一晩のうちにパリ中の犯罪組織のボスの半数が惨殺されるという事件が起きる。捜査に乗り出した本部長は、やがて目撃者、密告屋が次々と殺されることから、敵は警察の内部にいることを悟るが……。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Beetle Juice", "ビートルジュース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ティム・バートン監督の長編ホラーコメディ。自動車事故で死んでしまった新婚カップルのアダムとバーバラ。自分たちの家に引っ越してきた家族を追い出そうと奮闘するが、うまくいかない。困った2人は、霊界の用心棒ビートルジュースを呼び出すのだが……。出演はマイケル・キートン、ウィノナ・ライダーほか。音楽は「スパイダーマン」「ミッション：インポッシブル」のダニー・エルフマン。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HUMAN FACTOR", "狼たちの影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "イタリア・ナポリのNATO基地に勤務するコンピュータ技師の妻子が何者かに惨殺される。彼は完成したばかりの戦略コンピュータを使って犯人割り出しにかかるが……。名脇役ケネディが主役を張り、復讐に燃える男を熱演するサスペンス・ドラマ。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伝染歌", "伝染歌", "", "", "http://image.eiga.k-img.com/images/movie/34127/315x210.jpg?1678", 128, "かつて“自殺ソング”としてヨーロッパの人々を震え上がらせた「暗い日曜日」をモチーフに、呪われた歌の恐怖を描いたホラー映画。同級生の香奈が自殺する瞬間を目撃した女子高生・あんずは、香奈が口ずさんでいた不気味な歌が気になり、友人たちと調査を開始する。一方、歌うと死ぬ“伝染歌”の噂を耳にした雑誌編集者の陸は、何かに突き動かされたようにその謎を追い始める。「着信アリ」シリーズの秋元康が企画・原案を手掛ける。", "2007-08-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Talking Head トーキング・ヘッド", "Talking Head トーキング・ヘッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "アニメ作家、押井守による実写映画第３作。納期を１ヵ月後に控えたアニメ作品「トーキング・ヘッド」の監督が失踪。そこで、どんな監督の演出スタイルも、すべてまねることができるという裏演出家が呼ばれる。彼の指揮によって作業は進むが、その間にスタッフが、次々に謎の死を遂げて……。近未来を描いたこれまでの作品と、まったく違ったタイプの押井版ミステリー。DVDは「押井守シネマ・トリロジー／初期実写作品集 」に収録。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒーローインタビュー", "ヒーローインタビュー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "『東京ラブストーリー』や『101回目のプロポーズ』など大ヒットTVドラマを手掛けた野島伸司脚本による、ホロ苦いトレンディー・ラブ・コメディ。経済部の腕利き女性記者・霞はひょんなことからスポーツ部に左遷され、好きでもない野球担当に。そこで彼女は落ちこぼれ選手の轟仁太と出会い、心惹かれていく……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Star Wars: Episode II - Attack of the Clones 3D", "スター・ウォーズ　エピソード2 クローンの攻撃 3D", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("菅井君と家族石 THE MOVIE", "菅井君と家族石 THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/53347/315x210.jpg?3914", 90, "「秘密結社鷹の爪」などで知られる映像制作ブランド\"蛙男商会\"が2004年に公開してカルト的人気を呼んだフラッシュアニメの映画版。島根県で暮らす全員無職のソウル・ファミリー菅井家は、アニメに影響されて\"機械の体が欲しい\"と言い出した兄ちゃんのため、食堂を開いてお金を稼ぐことに。ある日この食堂に、爺ちゃんの幼なじみであるビクターが現われる。実は彼はアメリカの犯罪組織のボスで、日本で8億ドルもの取引を控えており……。", "2008-02-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("憧憬", "憧憬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "若さにまかせた夢多き生活に別れを告げ、一流実業家と婚約、家庭に入る心がまえでいる女性設計技師。そんな彼女の前に、一心に理想を追い求め続ける婚約者の弟が現れる。彼女の心は再び揺らぎ始めるが……。「約束」などスタイリッシュな青春映画を得意とする斎藤耕一監督作品。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MUPPET CHRISTMAS CAROL", "クリスマス・キャロル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "TV『セサミストリート』や『マペットショー』でおなじみのキャラクターを登場させて描く、C・ディケンズの同名小説の映画化。悪辣な商人スクルージが、クリスマス・イヴの晩に三人の幽霊と出会い、心を入れ替えていくというもの。監督はマペットの生みの親で、今は亡きジム・ヘンソンの息子、B・ヘンソン。DVDタイトルは「マペットのクリスマス・キャロル」。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Informant", "アンダーフィールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ジェラルド・シーモアによるベストセラー小説「テロリストの荒野」を、4代目ジェームズ・ボンドとして知られるティモシー・ダルトン出演で映画化。英国軍とIRAの闘争が激化した1980年代の北アイルランド。元テロリストのジンジーは、IRA幹部から判事の暗殺を依頼され決行するが、逃亡中に顔を目撃されたことから逮捕されてしまう。ジンジーは、自身の保釈と家族の保護を条件に裁判で証言することになるのだが……。", "1997-01-01", "33,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Winner", "ザ・ウィナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "欲望が渦巻くカジノの街ラス・ベガスで、負け知らずと噂される男がいた。彼の名はフィリップ。ある日曜日にルーレットで大儲けし、それ以来ずっと勝ち続けているのだ。やがてフィリップの周囲には金目当ての輩たちが次から次へと集まってくる。彼はその中の1人である妖艶な美女ルイーズに恋をしてしまい、借金を負っている彼女のために全財産を賭けた勝負に出るのだが……。イギリスの鬼才アレックス・コックス監督がメガホンを取る。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CELINE ET JULIE VONT EN BATEAU", "セリーヌとジュリーは舟でゆく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 192, "公園のベンチで本を読んでいるジュリーの脇を、セリーヌが風のように走り抜けた。郊外の屋敷の謎を解くため、魔法のキャンディで夢の世界へ。ジュリーはセリーヌなのか、セリーヌがジュリーなのか？　ふたり（ひとり？）のめくるめく冒険譚が展開する……。いたずら娘たちの即興演技をとらえたような1カット1カットが、映画でしか実現し得ない不条理きわまりないお伽噺へとつむがれていく、リベットによる伝説の傑作ファンタジー。", "1978-11-30", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KUNDUN", "クンドゥン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "「最後の誘惑」で人間としてのキリストを見つめた鬼才M・スコセッシが、チベットの高僧ダライ・ラマを題材にとり、聖と俗のかかわりを捉えた意欲作。ブッダの生まれ変わりと認められ、幼くしてチベットの指導者となったダライ・ラマ14世。修業の日々、中国の侵攻がもたらす苦悩、苦渋の決断を経て国境を超えるまでの、その半生を淡々とたどっていく。聖人が人間として成長していく姿を、じっくりとあぶり出したスコセッシのドキュメント・タッチの演出はもちろん、自然の美しさを収めた雄大な映像美も圧倒的。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒駿馬／A　MONGOLIAN　TALE", "草原の愛 −モンゴリアン・テール−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "香港と中国の合作映画だが、モンゴルを舞台にすべてモンゴル人役者の出演でモンゴル語によって演じられている。母を亡くし、父が都会に出るためにバヤンボルグは、草原で羊を飼って暮らしているおばあちゃんに預けられることになる。そこにはソミヤーという女の子も預けられていた。成長したバヤンボルグは、獣医学校と音楽学校で学び終え、ソミヤーと結婚するために草原に帰る。しかし、ソミヤーは遊び人のシャラーの子供を身ごもっていた。失意のバヤンボルグは草原を後にする。13年後、バヤンボルグは都会で人気歌手になっていた。久しぶりに草原に戻ると……。", "1995-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Young Sherlock Holmes", "ヤング・シャーロック／ピラミッドの謎", "", "", "http://ecx.images-amazon.com/images/I/511E6od5QaL._SL210_.jpg", 106, "ハリウッドの大ヒットメイカー、スティーブン・スピルバーグ製作総指揮によるミステリー・アドベンチャー。名探偵シャーロック・ホームズと彼の助手ワトソンが少年時代に出会っていたという設定の下、ロンドンで発生した連続殺人事件の解決に挑む彼らの活躍を、当時最新鋭のCGを駆使して描く。バリー・レビンソン監督がメガホンを取り、後に「ハリー・ポッター」シリーズの監督を手掛けるクリス・コロンバスが脚本を担当している。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新機動戦記ガンダムW Endless Waltz 特別篇", "新機動戦記ガンダムW Endless Waltz 特別篇", "", "", "http://ecx.images-amazon.com/images/I/51RT2TPV5YL._SL210_.jpg", 90, "1995～96年にTV放映された「新機動戦記ガンダムW（ウィング）」の続編で、97年に製作された全3話のOVA「新機動戦記ガンダムW Endless Waltz」を再編集した劇場公開作。A.C.（アフターコロニー）196年、平和への道を歩む世界に対し、ナゾの組織マリーメイア軍が宣戦を布告。ヒイロ・ユイら5人の少年たちは、一度は役目を終えたガンダムに再び乗り、マリーメイア軍に占拠された地球へ向かう。", "1998-08-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZEBRAHEAD", "ゼブラヘッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "人種のるつぼ、アメリカが抱える問題を、とあるハイスクールで起きる“事件”を通して描く、O・ストーン製作の青春ドラマ。ユダヤ系の高校生ザックは、転校生の黒人少女、ニッキーと恋に落ちるが、ニッキーの両親をはじめ、周囲は二人に冷たい反応を示す。やがてニッキーが、彼女に心を寄せる黒人少女、ナッツに射殺されて……。1992年度、サンダンス映画祭においてフィルムメーカーズ・トロフィーを受賞。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("3大テノール　世紀の競演　特別上映版", "3大テノール　世紀の競演　特別上映版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "1990年7月7日、イタリア・ローマにある歴史的建造物・カラカラ浴場で、世界3大テノールと称されるルチアーノ・パバロッティ、プラシド・ドミンゴ、ホセ・カレーラスが奇跡の共演を果たした。FIFAワールドカップ・イタリア大会の前夜祭として開催され、世界54カ国・約8億人もの人々がテレビの前で視聴したといわれるこの伝説的コンサートの模様を、HD映像化してスクリーンで上映する。", "2011-01-15", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アジアの純真", "アジアの純真", "", "", "http://image.eiga.k-img.com/images/movie/56927/315x210.jpg?2484", 108, "2002年、北朝鮮による拉致問題で日本国内に反朝鮮感情が高まるなか、不当な暴力により姉を奪われた孤独な在日朝鮮人の少女と、その殺害現場を見て見ぬふりをしてしまった気弱な日本人高校生の少年が出会い、2人は怒りと屈辱を抱えたまま旅に出る。少女は暴力により復しゅうを果たすが、2人の逃避行はやがて思いもよらぬ事態へと発展していく。「ピストルオペラ」「オペレッタ狸御殿」など鈴木清順監督作でプロデューサーを務めてきた片嶋一貴がメガホンをとり、憎しみの連鎖によって生まれる悲劇を描く。", "2011-10-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES SPE´CIALISTES", "スペシャリスト —カジノ金庫強奪大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "刑務所へ護送中に脱走した二人の囚人が手錠につながれたままカジノの金庫破りを企てる。銃撃戦、カーチェイス、激流下り、そして男同士の友情といったアクション映画のエッセンスを全編に散りばめて描く。フランス映画らしいユーモアも楽しめる娯楽作。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOGAMBO", "モガンボ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 167, "アフリカを舞台に巨匠J・フォードが描いた冒険映画。野獣の捕獲をしている男とショーガール、それに加えて動物学者とその妻の一行が、ジャングルの中へと進んでいく。アフリカの大自然の神秘が満喫、さらに大人の恋の深遠をしっとりと描いた名作。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("或る音楽", "或る音楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "映像作家・音楽家として国際的に活躍するアーティスト、高木正勝を追ったドキュメンタリー。2008年秋に開催されたコンサート「Tai Rei Tei Rio（タイ・レイ・タイ・リオ）」の模様を中心に、高木ら10人の音楽家による圧巻のパフォーマンスとその舞台裏、インタビュー映像などを収録。音楽とは何か？ヒトはなぜ音楽を奏でるのか？という普遍的な問いに真摯（しんし）に向きあう彼らの姿を映しだしていく。", "2009-07-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤城の血煙・国定忠治", "赤城の血煙・国定忠治", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "おなじみの国定忠治を高田浩吉が演じた時代劇。兄弟分からの知らせで甲州路を急ぐ忠治。すでに殺されていた兄弟分の仇を討った忠治は、上州の凶作につけこんで米を買い占めようとする悪徳商人のたくらみを知る。一味にとらわれた娘を救い出した忠治は赤城の山へ向かう。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("レイクサイド マーダーケース", "レイクサイド マーダーケース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "青山真治監督が東野圭吾の小説『レイクサイド』を映画化し、新境地を開拓した異色のミステリー。お受験対策の親子合宿に参加した３組の夫婦が遭遇する想像を絶する恐怖を、大胆不敵な構成と展開で明るみにしていく。大島渚監督の「日本の夜と霧」をも想起させる求心力のある密室劇だ。娘の中学受験に備えて、湖畔の別荘で行われる勉強合宿に参加した別居中の俊介と美菜子。やがて、そこで俊介の愛人の死体が発見される。", "2005-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("功夫厨神", "カンフーシェフ", "", "", "http://image.eiga.k-img.com/images/movie/54417/315x210.jpg?3794", 90, "元モーニング娘。の加護亜衣が、カンフーアクションに挑んだ香港映画。料理と武術の達人ピンイーは、怪我で引退した兄のレストランを継いでいたが、甥ジョーの罠にはまり料理長の座を失ってしまう。村からも追い出されたピンイーは、料理学校を卒業したてのケンと、おてんばなトラブルメーカーのチン（加護）と共に、ジョーとの料理対決に挑む。共演にサモ・ハン、台湾の人気グループF4のバネス・ウーほか。", "2009-04-25", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Christopher Columbus: The Discovery", "コロンブス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "コロンブスによるアメリカ大陸発見500周年を記念して作られたアドベンチャー・ドラマ。「007」シリーズを多数手掛けたJ・グレンが、ドラマティックな物語に仕上げている。同時期に作られたリドリー・スコット監督の「1492 コロンブス」と見比べてみるのも一興。マルコ・ポーロの『東方見聞録』に魅了された男コロンブス。当時、異端とされた大地が球形であるという説を固く信じる彼は、時の異端審問官に糾弾されながらもスペイン女王の協力を得て、苦難の航海を始める……。デヴィッド・リーンに見いだされたG・コラフェースが、コロンブス役を熱演。M・ブランド、T・セレックら豪華キャストが脇を固めている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARKNESS IN TALLINN", "ダークライズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "エストニアの首都タリンを舞台に、金塊強奪をもくろむロシア人ギャングの陰謀に巻き込まれてしまうエストニア人青年の良心を描く。ギャングたちの“暗闇に乗じての強盗”計画を成功させるためには、電気技師の協力が必要だった。発電所に勤めるまじめで素朴な青年トイボは、彼らの甘言に乗せられ訳も分からないまま町全域を停電させてしまう……。", "1993-01-01", "20,1,77,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Case of Itaewon Homicide", "イテウォン殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/57407/315x210.jpg?2627", 99, "TVシリーズ「美男（イケメン）ですね」「メリは外泊中」などでブレイクしたチャン・グンソクが冷徹な殺人容疑者を演じたミステリーサスペンス。韓国で実際に起こった未解決の事件をもとに映画化した。1997年4月3日、さまざまな人種が入り混じる街・梨泰院(イテウォン)のハンバーガーショップのトイレで、韓国人大学生が胸や首など9カ所を刺されて死亡する殺人事件が発生。現場にいたアメリカ国籍の青年2人が有力な容疑者となるが……。2010年「韓流シネマ・フェスティバル2010」で日本初上映。2011年、新世代の韓流スター出演作を上映する「韓流新世代特集　チャン・グンソク☆ソン・ジュンギ」で上映。", "2009-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PUNISHER", "パニッシャー", "", "", "http://ecx.images-amazon.com/images/I/51rrUMdYzoL._SL210_.jpg", 123, "アメコミの『スパイダーマン』の敵役として登場し、人気キャラとなって独立したコミックも作られたパニッシャー。法で裁けぬ悪を絶つこの戦士を主人公にしたのが本作だ。愛する家族を犯罪組織に殺されたことから、闇の処刑人として生きることを選んだ男。特殊能力のないアメコミ・ヒーローながら、強靭な体力と頭脳を武器に悪に挑む。そんな現実的な主人公ゆえにスンナリ感情移入できる。敵の粗暴さを強調した壮絶なバイオレンスも、主人公の戦いの悲壮感を盛り上げるに十分。低温であぶり出された熱いアクションに燃える。FBIの捜査によって息子を死なせたマフィアのボス、セイントが報復として、潜入捜査官フランクの家族を皆殺しに。怒りに燃えるフランクは闇の制裁者パニッシャーと化し、セイント一味を追い詰めていく。", "2004-11-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANNIE GET YOUR GUN", "アニーよ銃をとれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "二枚目スター、フランクに見初められてバファロー・ビルの西部ショー一座に加わり、そこの花形スターとなっていくアニー・オークリーを主人公にした、リチャード・ロジャース＆オスカー・ハマースタイン二世製作のミュージカルを映画化。アニー役ベティ・ハットンのはつらつとした魅力が画面いっぱいにはじけている。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("越前竹人形", "越前竹人形", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "越前の寒村に住む若い竹細工師・喜助は父の囲い者の遊女・玉枝と同情心から所帯を持つことになる。しかし肉体のまじわりのない夫婦生活に欲求不満を感じた玉枝は、かつてのなじみ客と情交してしまい、不倫の子を身ごもるが、堕胎に失敗して死んでしまう。失意の喜助は人形制作をやめる……。悲劇性の色濃い物語を、宮川一夫が暗い情感を湛えたカメラで捉えていて、竹林などの自然の景観が圧倒的に美しい。喜助が遊女に寄せるプラトニックな想いを、人形制作への打ち込みぶりで表現した吉村監督の演出が秀逸。肉体の欲求不満を、さりげない官能性を漂わせて表現した若尾文子の演技も印象に残る。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MOST BEAUTIFUL WOMAN IN TOWN", "町でいちばんの美女 ありきたりな狂気の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "日本でも人気のあるC・ブコウスキー原作の映画化。長年公開されなかったが、ブコウスキー人気にのって1995年に日本でも日の目を見た。酒場で娼婦のキャスと出会ったアル中の詩人チャールズは、彼女の支えにより再び詩を書こうとする。しかし、そのことが逆にキャスから離れさせることになるのだった。はきだめのような生活の中に芽生えた純愛の切なさが伝わってくる。", "1981-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Little Bit of Heaven", "私だけのハッピー・エンディング", "", "", "http://image.eiga.k-img.com/images/movie/56787/315x210.jpg?1458", 107, "ケイト・ハドソン、ガエル・ガルシア・ベルナル主演で、ガンに侵された女性と主治医の恋を描いた恋愛ドラマ。キャリアも順調で楽しい仲間に囲まれ、恋にも積極的な30歳のマーリーだったが、ある日突然、ガンで余命半年を宣告される。残された少ない時間との戦いの中、マリーにとって主治医ジュリアンの存在が大きなものになっていく。死の予感にさいなまれながらも、ユーモアを忘れず気丈に振る舞うマーリーだったが……。ウーピー・ゴールドバーグ、キャシー・ベイツらベテラン女優が共演。", "2011-12-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Il gattopardo", "山猫", "", "", "http://ecx.images-amazon.com/images/I/412QgKOdDQL._SL210_.jpg", 161, "　イタリア統一戦争を背景に、シチリア貴族の没落を描いた歴史絵巻。自身も貴族の家柄であるルキノ・ビスコンティが、1950年代末のベストセラー小説を原作にメガホンをとった。1860年、イタリア統一のために結成された赤シャツ隊への入隊を希望する貴族の青年。彼の叔父である公爵と共に訪れた別荘地で、村長の娘を見初めるが……。バート・ランカスターとクラウディア・カルディナーレの舞踏会シーンは圧巻。日本初公開当時は短縮版だったが、のちに186分完全版も公開された。", "1964-01-18", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A KISS BEFORE DYING", "死の接吻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "野望に燃える男が大富豪一族を殺害、富と名誉を手に入れるため次々と殺人を重ねていき、ついに彼は富豪の娘と結婚という段階にまでこぎつけるが……。魅力的でクールな容姿とは裏腹に残忍な手口で殺人を重ねる主人公を、青春路線を脱却したディロンが好演。意表を突くストーリーのサスペンス・スリラー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr. Nobody", "ミスター・ノーバディ", "", "", "http://image.eiga.k-img.com/images/movie/54047/315x210.jpg?4564", 137, "「トト・ザ・ヒーロー」「八日目」で知られるベルギーのジャコ・バン・ドルマル監督が、人生の選択によって生じるさまざまな可能性を、複数のパラレルワールドで描くファンタジードラマ。2092年、科学技術の進歩により不死が実現した世界で、唯一命に限りのある118歳のニモは、死を目前にして過去を回想する。最初の選択は、9歳だったニモが別れた両親のどちらについていくかで始まった……。主演は「レクイエム・フォー・ドリーム」のジャレッド・レト。", "2011-04-30", "2,7,37,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嗚呼！おんなたち 猥歌〈わいか〉", "嗚呼！おんなたち 猥歌〈わいか〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "20年間売れないロックばかりを歌っている中年ロッカー、ジョージは妻と別居中。平凡な結婚を夢見ているソープランド嬢の佳江と同棲しているが、彼女の収入をアテにしたヒモ同然の生活だ。ある日、一向に煮えきらないジョージに佳江が腹を立て、車の中での口論から事故を起こし入院。病院でジョージは看護婦の羊子を犯してしまう。だが、佳江と羊子に不思議な親近感が芽ばえ、ジョージとの奇妙な関係が始まる。神代辰巳監督がロマンポルノ10周年記念映画として撮った作品。週刊誌の実話記事をヒントに企画された。堕ちていく中年男の開き直りと悲哀を内田裕也が好演。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("都会の空の用心棒", "都会の空の用心棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "ビラまきを主な仕事とするちっぽけな会社のヘリコプターのパイロットが、殺人事件に巻き込まれて大活躍する。「紅の翼」や「天と地を駈ける男」などで高い評価を得た日活特殊撮影陣による航空シーンが最大の見せ場だが、マイトガイ・小林旭の骨身惜しまぬアクション・スターぶりも高く評価される。「拳銃は俺のパスポート」や「夜霧のブルース」など日活アクション史上に残る傑作を監督した野村孝としては、中級のできながら、的確なドラマ運びとスピーディなアクション処理で楽しめる作品に仕上げている。ジェリー藤尾がアイスピックを持った殺し屋役で、特異な個性をみせた。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APRE｀S L’AMOUR", "愛のあとに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "未婚の女流作家ローラと、妻子ある二人の男性とのやるせなくも美しい恋愛ゲーム。重い心理ドラマになりがちなテーマを、女流監督D・キュリスが、私小説風の映像美と女性らしい繊細さで軽やかに綴っている。小説家としても著名なY・シモンによるアコースティックな愛のメロディーも印象的。", "1992-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチンコ　喧嘩上等", "ガチンコ　喧嘩上等", "", "", "http://image.eiga.k-img.com/images/movie/55247/315x210.jpg?3476", 0, "ヤンキーの巣窟・飛鳥西高校では、3年生番長の次郎、2年生番長の厚志、1年生番長の高木がそれぞれの学年を仕切っていたが、学年同士による冷戦状態が続いていた。そこに、ライバル校である飛鳥東高校の元番長・佐伯が転校してくる。次郎は佐伯と激しく衝突し、厚志の兄・利道が率いる走り屋集団“スネイク”を巻き込み、飛鳥西高校史上最大のバトルを繰り広げる。監督は「走り屋ZERO」シリーズの広瀬陽。", "2010-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUT COLD", "クールボーダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アラスカの雪山で暮らす悪ガキ４人はスノーボードに明け暮れる毎日。ところが、山のリゾート開発計画が発覚し、彼らは生まれ育った山を守るために、あの手この手の一大作戦を繰り広げる。青年たちのハチャメチャな活躍を、下ネタ満載の笑いとロマンスを交えて描く痛快青春コメディ。全編に散りばめられた、プロのスノーボーダーたちによるダイナミックなスノーボード・シーンも必見。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪国", "雪国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "川端康成の不朽の名作を、原作に忠実に翻案した豊田四郎作品。主人公の島村に池部良、芸者・駒子に岸恵子を配し、それぞれの持ち味を生かして洗練された作品に仕上げた。養母や師匠など周辺人物の描写に細やかな注意が払われ、物語展開に厚みを出している。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bourne Supremacy", "ボーン・スプレマシー", "", "", "http://image.eiga.k-img.com/images/movie/1307/315x210.jpg?2384", 108, "ロバート・ラドラムのベストセラーを原作に、マット・デイモンが記憶喪失の元スゴ腕CIAエージェント、ジェイソン・ボーンを演じたヒット作「ボーン・アイデンティティー」の続編。前作の2年後、インドで暮らすボーンとマリーのもとに、正体不明の暗殺者が出現。彼の目的は何なのか？　ボーンはわざとCIAに捕縛され、自分を巡る陰謀を捜査していく。監督は72年の北アイルランドで起きた「血の日曜日」事件を描いた社会派ドラマ「ブラディ・サンデー」で02年ベルリン国際映画祭金熊賞を受賞したポール・グリーングラス。", "2005-02-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tender Mercies", "テンダー・マーシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "歌手になった男の挫折と第２の人生を歩むまでの生き様を描いた作品。カントリー・シンガー、マック・スレッジは結婚に失敗。スターの座も追われ、失意のどん底にあった。そんな彼は若き未亡人ローザに雑用係として雇われた。彼女とその息子に励まされ、マックは再び生きる意欲を持ち始める。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOTHIC", "ゴシック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "怪奇小説の古典『フランケンシュタイン』の誕生秘話を、鬼才K・ラッセルが幻想的な映像で綴っていく。詩人バイロンの屋敷を訪ねたロマン派詩人のシェリー、愛人メアリーとその義妹。怪奇趣味の強いバイロンの提案で本物の幽霊を作り出す儀式を行った彼らは、悪夢のような出来事に遭遇していく。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("投資令嬢", "投資令嬢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "“株の権威”を自認する女子大生の恋を描く明朗青春コメディ。株投資のために、山小屋で即席ラーメンを売って大もうけしたチズ子と仲間たち。ところが客を奪われた山のパン屋はカンカン。危ないところを助けてくれた青年にチズ子はすっかり熱を上げるが、彼が何と親友と見合いすることを知り……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大魔神", "大魔神", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "戦国時代、丹波の山奥には恐ろしい魔神が封じ込められていた。城下の領民は謀反を起こした悪家老の圧政に苦しみ、身を隠していた若君は捕らえられ、処刑の刻限が迫る。若君の妹の必死の祈りに、穏やかな顔立ちの武神像は怒れる大魔神に姿を変えた。特撮技術の見事さと、シリアスな時代劇の合体が面白い。第２作・３作のお子様映画指向は、１作目にはまだない。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天上人間", "天上の恋歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "W・カーウァイ監督の「花様年華」でカメラマンを務めた新鋭、ユー・リクウァイの長編劇映画デビュー作。殺伐とする現代の香港を舞台に、すれ違いを繰り返しながら生きる男女の孤独を描く。人気俳優レオン・カーファイが、泣かず飛ばずのAVレンタル店店長を好演。彼はノーギャラで出演し、プロデュースも手掛けている。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tell Them Anything You Want: A Portrait of Maurice Sendak", "みんなのしらないセンダック", "", "", "http://image.eiga.k-img.com/images/movie/57337/315x210.jpg?1332", 41, "スパイク・ジョーンズ監督が手がけた世界的児童文学の映画化作「かいじゅうたちのいるところ」（2009）の原作者、モーリス・センダックについてのドキュメンタリー。ジョーンズと映像作家のランス・バングスの2人がインタビューし、センダックの絵本作家としての苦労や子どもへの愛情など、さまざまな思いが打ち明けられていく。また、ジョーンズ自身が若き日のセンダックを演じる回想シーンも挿入される。ジョーンズ監督の短編「アイム・ヒア」（10）とあわせた「INVITATION from SPIKE JONZE」として11年12月に劇場公開。", "2011-12-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TO DIE FOR", "蒼い牙 ～果てない愛の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "お互いの虜となった吸血族の王子ブラッドと人間の女性ケート。そんな二人の前に、吸血族への復讐に燃える男が出現。強い愛に結ばれていた二人に悲劇が訪れる。現代に生きるヴァンパイア族の男と人間の女が恋に落ちるロマンティック・スリラー。リチャード・C・サラフィアンの息子ディランの監督作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野ゆき山ゆき海べゆき", "野ゆき山ゆき海べゆき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "佐藤春夫の自伝的小説『わんぱく時代』の映画化で、戦争の影が忍び寄る頃の尾道を舞台に、古き良き時代のわんぱく坊主たちが野を駆け、山を登り、海辺を走る元気いっぱいの活躍を描く。大林監督の“尾道”ものの集大成ともいえる作品。カラー版とモノクロ版が作られ、別々に公開された。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヘヴンズストーリー", "ヘヴンズストーリー", "", "", "http://image.eiga.k-img.com/images/movie/55577/315x210.jpg?4410", 278, "鬼才・瀬々敬久監督が、全9章4時間38分の長尺で描く群像ドラマ。家族を惨殺された8歳の少女サトにとって、妻子を殺した犯人を「この手で殺す」と宣言するトモキはずっと英雄だった。そして事件から8年後、サトはトモキに会いにある島へ向かうが……。出演は、少女時代のサトに「掌の小説」の寉岡萌希、トモキに「気球クラブ、その後」「ハッピーエンド」の長谷川朝晴のほか、村上淳、吹越満、嶋田久作、光石研、津田寛治、佐藤浩市、柄本明ら個性派俳優が集結。", "2010-10-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("すてごろ 梶原三兄弟激動昭和史", "すてごろ 梶原三兄弟激動昭和史", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "『巨人の星』などで知られる今は亡き人気劇画作家、梶原一騎と、弟で作家の真樹日佐夫の関係を描いた人間ドラマ。激動の昭和を駆け抜けた、男たちのアツい生き方を浮き彫りにする。昭和20年代、高森家の三兄弟はケンカに明け暮れる日々を過ごしていた。やがて長男、朝樹は漫画の原作で脚光を浴び始める。次男、真土も、そんな兄の影響を受けて文筆業を志し……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMA，CITTA｀ APERTA", "無防備都市", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "イタリアのネオ・レアリスモを代表するロッセリーニの傑作。第二次大戦のローマ解放直後に、６ヵ月にわたって製作が続けられた。レジスタンス運動の指導者、抵抗運動を支持する神父、そして地下運動家の印刷工の悲惨な末路が、容赦なく映し出されていく。とりわけ、印刷工の内縁の妻が連行される夫を追って、走りながら射殺されるシーンは、映画史に残る屈指の名場面。主題、映像ともに骨太な批評意識が感じられる、今後も時代を超えていく１本といえるだろう。", "1945-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents 真夜中の宴　「みかこのブルース」", "桃まつり presents 真夜中の宴　「みかこのブルース」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 17, "", "2008-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("れっしゃだいこうしん　ザ☆ムービー　れっしゃナンバーワン大集合", "れっしゃだいこうしん　ザ☆ムービー　れっしゃナンバーワン大集合", "", "", "http://image.eiga.k-img.com/images/movie/57097/315x210.jpg?1377", 0, "毎回さまざまな列車が登場し、鉄道ファンや子どもたちに人気の「れっしゃだいこうしん　ザ☆ムービー」シリーズ第3作。さまざまなジャンルのナンバーワンに輝く列車を集めた。子どもたちがわかりやすく列車を学ぶためのクイズコーナーも取り入れられている。", "2011-10-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Plane'te Blanche", "ホワイト・プラネット", "", "", "http://image.eiga.k-img.com/images/movie/1747/315x210.jpg?4765", 83, "地球温暖化の影響で今世紀中には消滅の可能性もある北極大陸を舞台に、ホッキョクグマ、イッカク、アザラシ、カリブーなど、それぞれの動物のサバイバルを、季節の移り変わりを通して記録したドキュメンタリー。監督は「沈黙の世界」で知られる海洋学者で映画作家のジャック＝イブ・クストーに師事したティエリー・ラゴベールとティエリー・ピアンタニダ。", "2006-06-24", "2,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EU　TU　ELES", "私の小さな楽園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "シングルマザーが初老の男と結婚し、さらに二人の男と結ばれ共同生活をする。10年間、３人の男性と暮らした実在の女性をモチーフに描いた人間ドラマだ。サトウキビ畑の重労働、貧しい暮らしなど、厳しい現実の中でたくましく生きる彼らの姿に、生命の尊さを感じさせる。ブラジル北東部の田舎町。結婚するため家を出たダルレーニが、３年後、一人息子を連れて帰郷する。やがて彼女は初老の男と結婚。だが、彼女は夫の従兄弟、労働者の青年と次々と恋に落ち、４人は奇妙な共同生活を始める。", "2000-01-01", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("富士山頂", "富士山頂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "標高3774nの富士山頂へ、レーダーを取り付けることに情熱を燃やす、三菱電気技術部員の梅原と、気象庁測器課長・葛木を中心とした男たちの姿を描く超大作。石原プロとしては、1968年の「黒部の太陽」と同様に、大自然の猛威と闘う、建設現場の人間にスポットをあてたもの。勝新太郎が、荷馬車隊の親分役で特別出演。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("画皮", "画皮　あやかしの恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "54,6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SURVIVORS", "ロビン・ウィリアムズの大混戦サバイバル特訓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "突然“クビ”を言い渡されたドナルドと、自分のガソリンスタンドが爆発したサニー。失業して落ち込む二人が入った食堂へ、今度は覆面強盗が押し入り、サニーはその顔を見てしまう。狙われるサニー。だが、偶然現れたドナルドが犯人を運良く捕まえた。警察へ連行した帰り、興奮状態のドナルドはガン・ショップで銃を買い、サバイバル訓練センターヘ入学してしまう。R・ウィリアムズが得意のマシンガン・トークを披露し、コメディのベテラン、W・マッソーがそれを支える。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カスタードプリン", "カスタードプリン", "", "", "http://image.eiga.k-img.com/images/movie/55517/315x210.jpg?4436", 0, "エイベックスが同社の新人俳優やアーティストを起用し製作する青春映画シリーズ「ニュースター・シネマ・コレクション」の第2弾。芸能事務所「オフィス・ブレーデル」に所属するちはるは、理想とかけ離れたキャンペーンガールの仕事にうんざりしながらも、傾いた事務所を何とか建て直そうと必死で仕事をこなしていた。そんなある日、仕事現場に売れっ子プロデューサーがやってくると聞き、事務所の女の子たちはチャンスをつかもうと奔走するが、そこで地球規模のトラブルが発生してしまう。", "2010-06-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヘリウッド（Hellywood）", "ヘリウッド（Hellywood）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "宇宙暦2001年、地球人植物化をたくらむ悪漢ダンスの魔手から多元植物宇宙王子、アップルくんを救うべく、宇宙大王が派遣したビフノビッチと、彼らの戦いに巻き込まれる美少女探偵団３人組の活躍を描く奇想天外SFミュージカル。熱狂的支持派と断固否定派が極端に分かれるカルト的怪作だ。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("88 Minutes", "88ミニッツ", "", "", "http://image.eiga.k-img.com/images/movie/53417/315x210.jpg?2950", 107, "アル・パチーノが異常犯罪分析医に扮し、88分間という制限時間の中で連続殺人鬼を追うサイコ・サスペンス。シアトルで若い女性をターゲットにした連続殺人事件が発生。FBIの異常犯罪分析医ジャック・グラムは、自身が9年前に証言し死刑判決が下された殺人犯フォレスターの手口と今回の事件が酷似していることに気付く。やがてジャックの元に犯人から電話が入り、衝撃の事実が告げられる。監督は「アンカーウーマン」のジョン・アブネット。", "2008-03-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE　VRAIE　JEUNE　FILLE", "本当に若い娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "無防備で未完成な自らの身体で、性の深遠を探ろうとする思春期の少女アリス。ある日、彼女は父の仕事場で働く青年ジムと出会い、強く惹かれていくがどうしても最後の一線を越えることが出来ない。小説家、脚本家、映画監督として“性”というテーマを一貫して追い続けるカトリーヌ・ブレイヤの幻の処女作。過激な内容のため上映禁止となっていたが、「ロマンスX」の成功により、24年の歳月を経て公開された。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARNIVAL OF WOLVES 狼たちのカーニバル", "CARNIVAL OF WOLVES 狼たちのカーニバル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "日本人のサックス奏者、タクシードライバー、ストリッパーのヒモ、神父。何の接点もなかった４人が奇妙な連帯感から意気投合。男たちはカジノ襲撃を企てるが、そこを経営するチャイニーズ・マフィアのボスに命を狙われる……。デビュー作「凶銃ルガーP08」でアクション映画の旗手として注目された渡邉武が、オール・アメリカ・ロケを敢行、「レザボア・ドッグス」の線を狙ったクライム・ストーリー。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MARTHA　MEET　FRANK，DANIEL　＆　LAURENCE", "マーサ・ミーツ・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "キュートなアメリカ人女性と３人のイギリス人男性の恋のさやあてを描くロマンティック・コメディ。人生に嫌気が差したマーサが、99ドルで行ける所まで行こうとロンドンに旅立つ。到着したその日に、音楽業界のダニエル、俳優志望のフランク、おばさん相手のブリッジ教師ローレンスが彼女と出会い一目惚れ。ところが、３人が偶然にも親友だったことから、事態は複雑になっていく。果たして、彼女と恋に落ちるのは誰なのか!? 恋の予感を感じさせる出会いを、公園や美術館、ホテルなどのロンドンの名所を舞台にロマンティックに演出。人生の再出発を望むヒロインと、彼女にほんろうされるおかしくも切ない男の姿をコミカルに綴る。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE OLD MAN AND THE SEA", "老人と海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "文豪E・ヘミングウェイの同名小説を、名匠J・スタージェスが映画化。キューバのとある村で一人暮らしをしている老漁師は、84日間も続いたシケのあと一人で海へ出る。やがて巨大なカジキの一種マーリンがかかり、老漁師は小舟とともに引っ張られていく。３日間の死闘の末、彼はついにマーリンを仕留め、帰途につくが……。主人公を熱演したS・トレーシーがアカデミー主演男優賞にノミネートされた。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE ODESSA", "リトル・オデッサ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "24歳の新鋭J・クレイのヴェネチア映画祭銀獅子賞に輝いた監督デビュー作。近年ロシア系ユダヤ移民が多いことからリトル・オデッサと呼ばれるニューヨーク・ブルックリンの南端ブライトン・ビーチに、一人の男が帰って来る。シャピロというその男は、裏切り者のイラン人を殺すために故郷であるその町に戻って来たのである。家では弟が兄を英雄視してくれたが、母親は脳腫瘍にかかり、父親は自分のことを許してはくれなかった。一方、彼のリトル・オデッサ入りは波紋を投げかけていた。彼に息子を殺されたロシア人のボスがシャピロを殺そうとしていたのだ。兄弟との愛、父親との確執を軸に現代アメリカの抱える家族の問題を描いていく。シャピロにT・ロス、弟にE・ファーロング、母にV・レッドグレーヴ、父にM・シェルという豪華なキャスティングにも注目。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Random Harvest", "心の旅路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "「哀愁」で映画ファンの涙を誘った名匠ルロイの手によるメロドラマ。第一次大戦下、二度も記憶喪失に陥った男と、その男を愛してしまったショーガールとの恋物語。運命の糸にもてあそばれながらも、幸福の鍵を見失わない男女を情感豊かに描いている。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("...More Than 1000 Words", "1000の言葉よりも　報道写真家ジブ・コーレン", "", "", "http://image.eiga.k-img.com/images/movie/53607/315x210.jpg?1331", 78, "95年、自爆テロにより跡形もなく破壊されたバスを捉えた1枚の写真が世界中に衝撃を与えた。\"過去45年間で最も重要な写真200\"に選ばれたこの写真を撮影したのは、イスラエル人としてパレスチナ問題を取り続けている報道写真家ジブ・コーレン。本人へのインタビューや家族との会話などを通して彼の素顔を浮かび上がらせるとともに、危険を顧みずパレスチナの真実を伝えようとする彼の想いと苦悩に迫ったドキュメンタリー。", "2008-06-14", "46", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最も危険な刑事〈でか〉まつり", "最も危険な刑事〈でか〉まつり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "監督“自腹”オムニバス、シリーズ第３作。上映時間10分以内、ギャグ５回以上の刑事ものというお馴染みの規定の下、今回は「空の穴」の熊切和嘉、「ばかのハコ船」の山下敦弘、「殺し屋１」の脚本家・佐藤佐吉、「A2」の森達也、「SCORE2 THE BIG FIGHT」の小沢仁志、特殊雑誌編集者の町山智浩、「地獄甲子園」の山口雄大といった“危険な”面々が腕を競い合う。", "2003-05-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIE｀VRE", "狂熱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 28, "“フォトジェニー理論”を提唱し、映像そのものが作り出す動きやリズムから映画芸術を創造しようと試みた実験映画の祖、L・デリュックによる異色作。港の酒場の女主人が昔の恋人と偶然に再会し、悲劇的な別れを遂げるまでを、カメラで人間心理を記述するような手法で描き、後年の映画作家に多大な影響を与えた。", "1921-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRSTBORN", "家族の絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "離婚した母が、新しい男を家に引き入れたことで起きた波紋を、子供の目を通して描いた野心作。粗野で感情的な男にのめり込む母をなんとか立ち直らせ、また荒れる弟を必死でかばう長男の姿がいじらしく、彼が困難の中で成長していく姿は感動的だ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SETTE VOMINI D’ORO", "黄金の七人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ジュネーブのスイス銀行。その大金庫の奥に眠る７トンの金塊を盗もうとする７人の大泥棒たち。銀行の真向かいのホテルに陣取った“教授”とジョルジアの指揮のもと、残る５人が地下から大金庫に迫る……。イタリア独特の軽いユーモアとサスペンスを組み合わせた傑作。", "1965-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARADISE ALLEY", "パラダイス・アレイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "S・スタローンが自らの体験をもとに脚本を書き、初めてメガホンを執った作品。1940年代のニューヨークのスラム街を舞台に、そこからはい上がるために、イタリア移民の３兄弟が協力し合い、プロレスの世界へ入っていくという人情味あふれるドラマ。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ときめき海岸物語", "ときめき海岸物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "南紀州の新宮を舞台に、プロのサーファーを目指す漁師出身の青年と、土地の少女とのひと夏のメモワールを描いた青春映画。地元の暴走族のエピソードなどに、地方で暮らす若者たちの倦怠感などが漂ってユニークな作品に仕上がっている。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Die Falscher", "ヒトラーの贋札", "", "", "http://image.eiga.k-img.com/images/movie/53257/315x210.jpg?1251", 96, "ナチス・ドイツがイギリス経済を混乱させるために実行した贋札製造計画“ベルンハルト作戦”。贋造を強制させられたユダヤ系技術者の視点から事件の裏側を描いた人間ドラマ。第2次世界大戦中、贋ポンド札を製造するために強制収容所内の秘密工場に集められたユダヤ系技術者たち。拒否すれば命はないが、作戦の成功はナチスの優勢に繋がり、ユダヤ人がさらなる苦しみを受けることになる。彼らは自らの命と信念の間で葛藤し……。", "2008-01-19", "7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逃学戦警／YOUNG　POLICEMEN　IN　LOVE", "危険な天使たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "日本でも人気のアジアン・スター、金城武がカッコよくない（!?）刑事を演じるアクション・コメディ。ひょんなことからある大学の潜入捜査を命じられた刑事コンビ、ガーリックとギミック。しかし、学長は学生をシゴくのが趣味のキレたサディスト。そこに留学してくる令嬢を守るため学生になりすます二人だが、彼らに恨みを持つ麻薬組織の復讐の手も迫っていた……。台湾スターの共演も見逃せない１本。", "1995-01-01", "53,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本の首領 やくざ戦争", "日本の首領 やくざ戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "飯干晃一の原作をオールスター・キャストで映画化した東映久々の大型シリーズ。全国制覇を目指す暴力団中島組の首領・佐倉一誠の苦悩の日々を描く。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　WILD　PAIR", "ポリス・スクワッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "頻発する爆破テロ。刑事ベニーはFBI捜査官ジョーと組んで、悪の組織の壊滅を目指すが、恋人は組織に殺され、ジョーも拉致される。怒りに燃えたベニーは、独り敵地に乗り込む。B・ブリッジスが自ら監督を手掛けたポリス・アクション。B・スミスが渋い刑事役を好演。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("One Life", "ライフ　いのちをつなぐ物語", "", "", "http://image.eiga.k-img.com/images/movie/56377/315x210.jpg?4682", 85, "「ディープ・ブルー」「アース」の英BBC製作によるネイチャードキュメンタリー。撮影日数3000日、総製作費35億円を投じ、地球の全大陸、陸・海・空に住む多種多様の生物がそれぞれの命をつないでいく様子を、最新のカメラシステムを用いて動物と同じ目線で撮影。生態の細部や決定的な瞬間をとらえた。2011年に設立50周年を迎えたWWFが製作協力。日本語吹き替え版は松本幸四郎、松たか子が親子共演でナレーションを担当し、人気バンド「Mr.Children」が主題歌を提供した。", "2011-09-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マンホール", "マンホール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "かつて札幌で劇団『オーパーツ』を主宰し、北海道ローカルのTV番組などでカリスマ的人気を誇る鈴井貴之が初監督した作品。親に内緒で、デートクラブで働く高校生の希と、勤勉実直な交番巡査の小林。二人はある事件がきっかけとなって、札幌の地下にあると噂され、何でも願い事が叶うという“夢のマンホール”を探すことになるのだが……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Top Gun", "トップガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　トップガンとは、アメリカ海軍に所属するトップ1パーセントのエリート・パイロットのこと。その仲間入りを果たしたマーベリックとグースは厳しい訓練を受けることに。女性教官チャーリーとの恋、ライバルのアイスマンとの激突、そして訓練中の事故によるグースの死を経て、成長していくマーベリック。やがて彼は敵ミグ戦闘機との実戦に出撃する。ケニー・ロギンスやベルリンらの曲を用いたリズム感あふれる映像が大ウケし、世界的メガヒットを記録したスカイ・アクション映画。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歯成魚番生／BY HOOK OR BY CROOK", "燃えよデブゴン 正義への招待拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "かつては民衆の英雄であった義賊、一枝花。輝かしい過去を持つ彼であったが、今では自分に怨みを持つ暗殺者の影に怯え、逃亡の毎日を繰り返していた。しかし、一枝花を師と仰ぐサモ・ハン・キンポー扮するデブゴンは、なんとか彼を立ち直らせようとして……。", "1980-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEXAS RANGERS", "テキサス・レンジャーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "これまで数多くの映画やTVの題材になった“テキサス・レンジャーズ”をモチーフにしたアクション西部劇。1870年代のアメリカ西部、両親を殺された若者が仇を討つためにテキサス・レンジャーズに入隊。しかし隊長は復讐心を捨てて正義のために働くよう説き聞かせる……。復讐か正義かというテーマが現代的な重みを持つ。個性的なキャスト陣の中、レイチェル・リー・クックが可憐に花を添えている。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("放課後ミッドナイターズ", "放課後ミッドナイターズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三等高校生", "三等高校生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ともに熱気球づくりに励みながらも、いつも喧嘩ばかりしている高校３年生の透と、数学教師の菊池。その二人のことが好きだという女性が現れて……。ヨッちゃんこと野村義男が、それまでの“たのきん”映画から独立、一本立ちをした青春映画。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLATOON", "プラトーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1967年、ベトナム。名門大学を中退して殺りくの戦場にやって来た一人の志願兵がいた。次々と徴兵されていく同年代の若者の多くが、少数民族や貧しい者たちだったことに対する憤りゆえの志願である。だが、いきなり最前線の戦闘小隊“プラトーン”に配属された彼にとって、現実の戦争は想像をはるかに超えた過酷なものだった。そして小隊内の対立——味方といえども情け容赦ない戦争マニアの隊長と、たとえ戦争でも人間としての自分を忘れない班長とのミゾは深まる一方だ。そんな状況のもとで彼はベトナム人の村での虐殺、掠奪、強姦と、人間の最大の罪悪＝戦争を体験していく……。これまでのベトナム戦争もの「地獄の黙示録」や「ディア・ハンター」などにはないきわめて客観的な語り口は、映画の主人公がとりもなおさず若き日のストーン監督自身だからである。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("たそがれ", "たそがれ", "", "", "http://image.eiga.k-img.com/images/movie/53437/315x210.jpg?4333", 64, "ピンク映画界の鬼才・いまおかしんじ監督が\"老人の性\"というテーマに挑み、高齢者同士の恋を温かい目線で綴った異色作。65歳の左官職人・鮒吉は、病気の妻がありながら女遊びにいそしむ日々を送っていた。中学校の同窓会に出席した彼は、かつて想いを寄せていた和子と再会を果たし、夫を亡くしたという彼女に自分の電話番号を渡す。やがて、妻が亡くなり気を落としていた鮒吉の元に和子から突然連絡が入り……。", "2008-02-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Triple agent", "三重スパイ", "", "", "http://image.eiga.k-img.com/images/movie/57667/315x210.jpg?4485", 115, "2010年に他界したフランスの巨匠エリック・ロメールが03年に発表したサスペンス。1930年代のパリで実際に起こったスパイ事件をもとに、ロシア白軍の元軍人がスパイ活動に従事する姿を当時のニュース映像や夫婦の会話劇を中心に描き出していく。ギリシャ人の妻アルシノエとともにフランスへ亡命してきたロシア帝政軍の将校フョードルは、在仏ロシア軍人協会の事務員として働いていた。やがてスペインで内戦が勃発するとフョードルの出張が多くなり、アルシノエが問いただすとフョードルは諜報活動を行っていることを密かに打ち明ける。やがて、そんなフョードルの行動に疑惑を抱く者が現れ……。", "2012-04-21", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飛鳥へ そしてまだ見ぬ子へ", "飛鳥へ そしてまだ見ぬ子へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "若くしてガンで命を失った青年医師が、自分の死後に残される妻や子、そしてやがて生まれてくる子に宛てて書いた遺稿集の映画化。医療界に波紋を投げかける徳田虎雄医師の徳洲会が出資して製作、原作の井村和清は同会の医師でもあった。感動的な実話。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOVO", "NOVO／ノボ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "５分として記憶のもたない男を軸に一風変わった恋愛物語が進展する異色作。ヒロイン役を新進女優アナ・ムグラリスが好演、2003年のシャネルのミューズに抜擢された魅力が爆発している。極度の記憶障害で５分前のことさえも忘れてしまうグラハム。いつも自分に苛立ち気味の彼は勤務先の派遣社員のイレーヌと付き合いだし、少しずつ変わり始める。次第に病状も良くなった彼は自分に妻と子供がいることを思い出し……。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A WEATHERWOMAN　お天気お姉さん", "A WEATHERWOMAN　お天気お姉さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "『ヤングマガジン』に連載されたお色気コミックを映像化した、奇想天外なコメディ。1995年にオリジナル・ビデオとして発売されていたが、海外の映画祭に出品され話題となったことから、翌年劇場公開された。物語は、天気予報キャスターの座をかけて、女たちが壮絶な争いを繰り広げるというもの。ヒロイン、桂子は代理キャスターとして出演した天気予報番組で、スカートをまくってみせたことから一躍人気者に。タカビーに振る舞う彼女に、本来のキャスターである放送局会長の娘、かおりが反撃を開始した。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アワ・ブリーフ・エタニティ OUR BRIEF ETERNITY", "アワ・ブリーフ・エタニティ OUR BRIEF ETERNITY", "", "", "http://image.eiga.k-img.com/images/movie/54987/315x210.jpg?1354", 105, "「PRISM」の福島拓哉が監督・脚本を手掛けたサイコ・ファンタジー。感染すると大事な人の記憶を無くしてしまう新種のウィルスが蔓延した東京。自らを高等遊民と称して自堕落な生活を送る青年テルは、ある日元恋人のミオと再会するが、彼女は既にウィルスに感染しテルを忘れてしまっていた。ウィルスの拡大により世界中が大混乱に陥る中、記憶の戻らないミオと再び一緒に暮らし始めるテル。ところが、テルもウィルスに感染してしまう。", "2009-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("巫山雲雨", "沈む街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "川べりで信号守をしているマジメ男マイチャン、何者かにレイプされたという未亡人チェンチン、その事件についてマイチャンを取り調べることになった若い警官ウーガン。ふとしたことから交錯していく彼らの人間模様を、中国第６世代のツァン・ミン監督が淡々としたタッチで浮き彫りにするデビュー作。実際に多くの住民が移住を迫られた三峡ダムの工事によって、水没する運命にある田舎町の事件を、不思議な官能性がしたたる映像で語り明かしている。", "1996-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間の條件 第５部・死の脱出　／第６部・曠野の彷徨", "人間の條件 第５部・死の脱出　／第６部・曠野の彷徨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 188, "敗戦後、梶は強制労働所から逃亡、故郷と妻を夢見て放浪の生活を送る。ついに肉体と精神の極限状況にまで追い込まれ、吹き荒れる吹雪の中、力尽きて倒れてしまう……。壮大なスケールで戦争と人間、愛と憎しみを描いたシリーズの完結編。DVDは「人間の條件DVD-BOX 全６巻」に収録。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Precious: based on the novel “Push”by Sapphire", "プレシャス", "", "", "http://image.eiga.k-img.com/images/movie/55097/315x210.jpg?4545", 109, "1987年のニューヨーク・ハーレムで、両親の虐待を受けながら希望のない日々を生きる黒人少女プレシャス。レイン先生に読み書きを習い、つたない文章で自分の心情を綴り始めたプレシャスは、ひたむきに人生の希望を見出していく。サファイアの小説「プッシュ」を、「チョコレート」で製作を務めたリー・ダニエルズが映画化。マライア・キャリー、レニー・クラビッツ、ポーラ・パットンらが出演。2009年のサンダンス映画祭でグランプリ、第82回アカデミー賞で助演女優賞と脚色賞を受賞した。", "2010-04-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG RETURNS", "BIG RETURNS", "", "", "http://image.eiga.k-img.com/images/movie/55757/315x210.jpg?4608", 90, "ロック・ミュージシャンという生き方を選択した男たちにスポットを当てたヒューマン・ドキュメンタリー。出演するのは、1970年代に博多で生まれた“めんたいロック”ムーブメントの中心的存在として知られる柴山俊之や山部善次郎、80年代に人気を誇ったロックバンド「アンジー」の水戸華之介ら博多のミュージシャンたち。過去や時代に振り回されることなく自らのスタイルを貫き通す力強い生きざまを映し出す。", "2010-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TASTE　THE　BLOOD　OF　DRACULA", "ドラキュラ 血の味", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "怪奇スター、C・リー主演によるハマー・プロの“ドラキュラ”シリーズの一編。ドラキュラ伯爵の弟子である没落貴族コートレイ卿が、町の有力者３人との取引によってドラキュラの遺品を手に入れる。やがて復活したドラキュラは、その代わりに命を落としたコートレイのために、有力者たちへの復讐を開始するのだった。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.MOM", "ミスター・マム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ある日突然、一時解雇を言い渡されたエリート技師と広告代理店に就職が決まった妻……。夫と妻の立場の逆転をおもしろおかしく描いたコメディ。監督のドラゴーティはCM出身で「ドラキュラ都へ行く」でデビューし、これが２作目。ソープ・ドラマに熱中したり、「ジョーズ」のパロディーありで笑わせる。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Once Upon a Time in America", "ワンス・アポン・ア・タイム・イン・アメリカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 204, "マカロニ・ウエスタンで知られる巨匠、S・レオーネが10年余の歳月をかけて手掛けた長編大作。ニューヨークに渡った貧しいユダヤ移民の子供たちが自衛のために団結し、ギャング団となり、やがて崩壊する。彼らの友情、愛、裏切り、苦悩が1920～60年代の長きにわたって描かれている。ロバート・デ・ニーロの老け役も話題。DVDは25分長い“完全版”。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dinosaur Jr. - Bug Live at 9:30 Club: In the Hands of the Fans", "実演！バグ　ダイナソーJR", "", "", "http://image.eiga.k-img.com/images/movie/57847/315x210.jpg?2686", 62, "1980年代末から90年代のアメリカン・オルタナティブ・ロックシーンを代表するバンド「ダイナソーJR」が88年に発表した3rdアルバム「バグ」に収録されている全曲を実演。そのライブの模様を、公募で選ばれたファン6人が撮影したライブドキュメンタリー。", "2012-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("凶弾", "凶弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "1970年、瀬戸内海で起きたシージャック事件を題材にしたハード・アクション。犯人役を演じたのは石原慎太郎の息子、石原良純で大型新人と騒がれた。内容の方は犯人がなぜ凶悪な犯罪者になったかという説得力に欠け、緊張感のない作品になっている。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最佳福星／LUCKY　STARS　GO　PLACES", "十福星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「五福星」「大福星」「七福星」と続いてきた“福星”シリーズの第４作。これまで活躍してきたおなじみ“五福星”の面々がケチな強盗で捕まったため、新たにメンバーが選ばれる。この新サギ師たちが、警察の目をかいくぐり大ハッスル。香港映画のお家芸ともいえるアクション・コメディ。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アダン", "アダン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "「地雷を踏んだらサヨウナラ」など伝記ものを得意とする五十嵐匠監督が、孤高の日本画家として知られる田中一村の壮絶な人生を重厚に描き出す。田中一村、50歳。いまだ無名の画家である彼は、画壇を見返すために最愛の姉、喜美子と別れ、奄美大島に旅立つ。一村はそこで少女アダンと出逢い、その幻影を追いかけるのだが……。", "2006-05-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BROWNING VERSION", "明日にむかって…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "イギリスの著名戯曲の映画化。英国の名門私立学園の古典文学の教師アンドリューは退職を間近にして、いかに生徒たちに嫌われているかに気付く。妻のローラは彼の厳格さについていけず、若いアメリカ人教師フランクと不倫関係にある。しかし、アンドリューを尊敬するフランクは、良心の呵責からこの事実を告げる。苦悩とともに自分の人生を見つめ直す３人。そして、一人の生徒のとった行動が彼らに転機をもたらす。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOODFISTIV：GROUND ZERO", "テロリスト・ウエポン 悪魔の最終兵器", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1980年代初頭に、北米中部の地下に設けられた核ミサイル基地を高度に訓練されたテロリスト集団が占領した。世界秩序を根底から否定するリーダーに導かれた彼らの目的は、世界の崩壊。発射ボタンが押され、カウントダウンが開始されるが、軍当局にはなす術もない。そんな中、偶然その場に居合わせた戦略空軍兵士コリガン軍曹が、狂人達の謀略を阻止するために立ち上がった。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nine Lives", "美しい人", "", "", "http://image.eiga.k-img.com/images/movie/1237/315x210.jpg?4004", 114, "「彼女を見ればわかること」のロドリゴ・ガルシア監督が、ロビン・ライト・ペン、ホリー・ハンター、シシー・スペイセク、グレン・クローズら9人の女優を起用して、9つの物語を紡ぎ上げたオムニバス作品。ワンシーン・ワンショットで撮影し、様々な立場に置かれている女性たちの心の揺らぎを繊細なタッチで表現。演劇的な手法で空間を切り取るほか、それぞれのエピソードを絡めるなど、ガルシア監督独自の世界を創り上げている。", "2006-07-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SNUFF", "スナッフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "劇場公開時に、映画の中で実際に殺人が行われたという噂が流れ、大ヒットしたゲテモノ作品。SMポルノ映画の撮影現場で、監督が女優にセックスを強要。これを女が拒むと男は怒り、女を電動ノコギリで刻み始める。女のはらわたがあふれ出るシーンはSFXにしてもすさまじいばかり。", "1976-01-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　ふたりはプリキュア　スプラッシュ☆スター　チクタク危機一髪！", "映画　ふたりはプリキュア　スプラッシュ☆スター　チクタク危機一髪！", "", "", "http://image.eiga.k-img.com/images/movie/33767/315x210.jpg?2614", 50, "女の子たちの間で一大ブームを巻き起こしている人気テレビアニメ「ふたりはプリキュア　スプラッシュ☆スター」の劇場版。光の使者“プリキュア”に変身して悪と戦う中学生、日向咲と美翔舞。ある日突然時間が止まり、咲と舞の前に精霊アワーズとミニッツが現れる。精霊たちに連れられて時計の郷にやって来たふたりは、謎の男サーロインが、時間を止めて世界を征服しようと企んでいることを知る。ゲスト声優として向井亜紀が本人役で登場。", "2006-12-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CUL−DE−SAC", "袋小路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「水の中のナイフ」「リパルジョン／反撥」など、人間の精神的閉鎖状態での異常心理を描くことに定評のあるポランスキー監督が、同じ流れをくむテーマを扱い、さらに評価を得た作品。中年でハゲ上がった頭の特異な風貌の男と、若いが変に物憂げな女との妙な夫婦が新生活を営むため、全財産を投げうって古城に移り住む。海に囲まれ、孤立したこの城に、逃亡中のギャングが侵入したことから、平穏に見えた二人の生活は、ことごとく、覆されるのだった……。ポランスキー監督は“行き詰まり”に遭遇した時の人間の奇妙な行動を、ブラック・コメディ的なタッチで描きながらも深淵に潜む孤独感を的確に表現している。「ポランスキー・スペシャルDVDコレクション」に収録。", "1966-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花腰新娘", "雲南の花嫁", "", "", "http://image.eiga.k-img.com/images/movie/53447/315x210.jpg?2612", 91, "中国・雲南省で暮らすイ族の娘フォンメイは、幼なじみのアーロンと結婚する。しかしイ族には“夫婦は結婚後3年間一緒に暮らしてはならない”というしきたりがあった。愛する彼と離れ離れでいることが耐えられなくなったフォンメイは、アーロンがコーチを務める龍舞隊に入ることを決意し……。「ラッシュアワー3」などの国際派女優チャン・チンチューが自由奔放なヒロインを好演。監督は「雲南の少女／ルオマの初恋」のチアン・チアルイ。", "2008-07-26", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("橋のない川", "橋のない川", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "東陽一が、住井すゑのライフワークを映画化した作品。壮大な原作の流れに沿って、明治から大正時代に生きた被差別部落の人々の姿を淡々と描いている。アンデスのフォルクローレ音楽の第一人者エルネスト・カブールが音楽を担当している。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPERVIXENS", "スーパービクセン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "殺害の容疑をかけられた男の逃亡劇を、豪快なセックス＆バイオレンス満載で描いた、鬼才R・メイヤー監督の1970年代の代表作。カリフォルニア中西部、町のガソリンスタンドで働くクリントと一緒に暮らしていたスーパーエンジェルが、何者かに殺害された。犯人の濡れ衣を着せられた彼は、砂漠地帯へと逃亡。ハンサムでたくましい体のクリントンは、行く先々でグラマラスな美女たちに誘惑されるが、途中スーパーエンジェルに瓜二つのスーパービクセンに出会い恋に落ちていく……。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLEASANTVILLE", "カラー・オブ・ハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "「シービスケット」のG・ロスの初監督作。TVの世界に入り込んでしまった兄妹が巻き起こす騒動を描くハートフル・コメディ。1950年代のホームドラマ『プレザントヴィル』の中に、現代の高校生の兄妹がトリップしてしまう。そこは白黒の古き良き時代。セックスも争い事もない平和な世界は、兄妹によって刺激を受け、次第に色付き始めていく。最新技術によって、モノクロとカラーの人間を違和感なく同居させる画期的な映像を実現。感情の芽生えと同時に、人々が色彩を取り戻していく様が鮮やか。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUBLE IMPACT", "ダブル・インパクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "幼い頃生き別れになった双子の弟アレックスに会うためロスから香港に飛んだ空手インストラクターのチャド。再会した二人は両親の死に香港暗黒街の陰謀が絡んでいることを知り、仇を討つことを誓い合う……。J・C・ヴァン・ダムが双子の二役ばかりか製作、脚本、原案までも手掛けた気迫のアクション巨編。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fried Dragon Fish", "Fried Dragon Fish", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "オペレーターとして探偵事務所にやって来たプー・リンウォンは、高級魚ドラゴン・フィッシュが盗まれたという資料を目にする。その魚のことを調べるうちに、プーは熱帯魚の水槽に囲まれて暮らすナツロウという少年と出会うのだった……。岩井俊二がCHARAの歌『Break These Chain』に触発されてTVドラマとして作った無国籍ハードボイルドで、「スワロウテイル」のオリジナルとも言える作品。1996年に劇場公開された。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("High School", "高校", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市あばれ凧", "座頭市あばれ凧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "甲州路に足を踏み入れた座頭市は、吃安こと武居の安五郎が代官と手を結び、津向の文吉の縄張りを狙っていることを知り、文吉に加勢して吃安一味を叩き斬る。遠藤辰雄が吃安に扮して好演。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TASTE OF HEAVEN", "成功時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "“成功した者だけが自由だ！”を座右の銘とするパンチョク。貧しい幼年時代を送った彼は大手化学調味料会社に入社し、ライバル会社を蹴落とすため奮闘。出世街道を驀進し、ついには理事の地位まで手に入れるが……。イ・チャンホの下で映画を学び、鮮干浣との共同監督作「ソウルのイエス」で注目されたチャン・ソヌの監督作。", "1988-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA BANYERA", "濁った水", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "シトヘス国際ファンタスティック映画祭で批評家賞を受賞した、幻想的映像に彩られたサスペンス。女優のアンドレアは、ある日、弟ヴィクトルが危険な状態にあると聞いて帰国する。聞けばヴィクトルは一年前に丸一日分だけの記憶を失ったというのだ。その原因は何なのか。アンドレアは、死んだオルビード・ビントローという女流彫刻家に関係があることを突き止めるが……。", "1989-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("40POUNDS OF TROUBLE", "40ポンドのトラブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "扶養料も払わず、離婚した妻から逃げ回っているスティーブは、ネバダの賭博場で少女ペニーと美人歌手のクリスと知り合う。ペニーを気に入った彼は、ディズニーランドに行きたいというペニーの望みをかなえてあげたいと思うが、ディズニーランドのあるカリフォルニア州では、妻や弁護士が手ぐすねひいて彼を待っているのだった。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Severn, la voix de nos enfants", "セヴァンの地球のなおし方", "", "", "http://image.eiga.k-img.com/images/movie/55737/315x210.jpg?3771", 120, "1992年、リオデジャネイロで開催された環境サミットで、当時12歳のセバン・スズキは子どもの環境団体代表としてスピーチを行い、未来の子どもたちの生活を守るために自分の発言に責任をもって行動するよう呼びかけて人々の心を動かした。それから18年、現在のセバンの活動と、日本とフランスで子どもたちの未来を救うために「食」を守り続ける人々を追ったドキュメンタリー。監督は「未来の食卓」のジャン＝ポール・ジョー。", "2011-06-25", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ある映画監督の生涯　—溝口健二の記録", "ある映画監督の生涯　—溝口健二の記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "副題が示す通り、“ある映画監督”とは新藤兼人が師と仰いだ溝口健二のこと。新藤は自らのデビュー作で溝口らしき人物を登場させたり、溝口好みの芸者ものの脚本を書いたり、映画を作ったりして溝口にこだわり続けてきた。そのこだわりはこの作品で頂点に達し、生前の溝口を知る人々にインタビューすることによって溝口という人間そのものに近づこうとする試みとして結実した。その結果、監督としての溝口の姿はもちろん、生い立ちと人間形成、女性関係といった私生活の部分にまで踏み込みながら人間・溝口に光を当てることに成功。とりわけ田中絹代が溝口への思いを告白する場面の崇高さは感動的でさえある。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRIKE ME PINK", "当り屋カンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "気の小さな仕立て屋が、一念発起して自分の住む町に遊園地を造ろうとするが、そこにスロットマシンを置くことになったことからギャングが介入してきて、仕立て屋の大奮闘が始まる。彼が密かな思いを寄せるクラブ歌手にマーマンが扮して、ミュージカル仕立ての喜劇を盛り上げる。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("濟公／MAD MONK", "マッド・モンク　魔界ドラゴンファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "香港の喜劇スター、C・シンチーの主演による伝奇時代劇コメディ。天界を追放されそうな降龍は神仙たちと賭けをし、悪党や娼婦たちを悔い改めさせることに。人間界に転生した降龍は相棒の伏虎とともに奮闘するが、そんな彼を、亡者の魂を操る魔者が邪魔し始め……。ナンセンスなギャグを連発するシンチーの怪演はもちろん、名パートナー、ン・マンタとの絶妙のやりとりも見逃せない。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOREVER YOUNG", "フォーエヴァー・ヤング　時を超えた告白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "恋人が事故で植物人間になってしまったことに絶望したダニエルは、人間冷凍装置の実験台となり眠りにつく。50年後の現代、偶然から目覚めたダニエルは、かつての恋人が生きているのを知る。50年の歳月を越えて二人は再会できるのだろうか？　永遠の愛をテーマにした現代の寓話とも言える奇跡の愛の物語。ビデオタイトルは「フォーエヴァーヤング」。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE BALLON ROUGE", "赤い風船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 36, "パリの朝。学校に行く途中、一人の少年が街灯にひっ掛かった赤い風船を助けてやると、赤い風船はまるで遊び友達のように少年の後をどこまでもついていく……。セリフは、たった一言だけ。美しいパリの町並みを背景に、少年と赤い風船の心温まる交歓を詩情豊かに綴った秀作ファンタジー。", "1956-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE OF DARK SHADOWS", "血の唇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "コリンズ家の使用人が、誤って同家の墓地に200年間眠っていたヴァンパイアをよみがえらせてしまう。実はこのヴァンパイア、果たせぬ恋の怨念を抱いたまま眠り続けていたのだが、復活したのを契機に、次々と美しい女性に襲いかかる。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Gift", "ギフト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "　人の運命を読み取る能力を持っていたアニーは、夫の死後、子供を抱えながら占いの仕事で生計を立てていた。彼女は夫ドニーの暴力に悩むバレリーに離婚を勧めるが、それが原因で彼から逆恨みされることに。そんな折、町の名士の娘ジェシカが行方不明になり、アニーは捜査の協力を依頼される。彼女の透視能力によってジェシカの遺体が発見され、その後、ドニーが容疑者として逮捕されるが……。殺人事件の真相、そしてそれに巻き込まれた薄幸のヒロインを描くサイキック・スリラー。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KICKBOXER ４ ： THE AGGRESSOR", "キックボクサー４", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "元キックボクシングのチャンピオン、デヴィッドは宿敵トン・ポーに兄を殺されたうえ、刑務所に入れられ、妻まで奪い去られてしまう。連邦麻薬取締局との取り引きで、トン・ポーを殺すことを条件に出獄したデヴィッドは麻薬王となったトン・ポーのいるメキシコへと向かう。一方、トン・ポーは麻薬の取り引きの場として格闘技大会を開催。復讐に燃えるデヴィッドは、その大会に出場することにする。J・C・ヴァン・ダムが特別出演する「キックボクサー」シリーズ第４作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニッポンの嘘　報道写真家　福島菊次郎90歳", "ニッポンの嘘　報道写真家　福島菊次郎90歳", "", "", "http://image.eiga.k-img.com/images/movie/58127/315x210.jpg?4349", 114, "戦後日本を見つめ続けてきた報道写真家・福島菊次郎の2009年～11年の3年間に密着したドキュメンタリー。敗戦直後の広島で被爆者家族の苦悩を10年間にわたり撮り続けた福島は、それ以降も三里塚闘争、東大安田講堂、70年安保、あさま山荘事件、水俣、祝島など時代を象徴する数々の事件を25万枚以上の写真に収めてきた。やがて保守化する日本社会やメディアと決別した福島は、無人島での自給自足生活を経て、愛犬との静かな暮らしを送っていた。そして、胃がんの切除手術を受け自身の最期を自覚し始めた11年、東日本大震災が発生。再びカメラを手にした福島は、原発事故が起きた福島県へと向かう。", "2012-08-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("疵千両", "疵千両", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "寛永20年、会津藩お家断絶の余波を受け、真剣で争わねばならなくなったかつての親友同士の因縁。つらくも友を倒して顔に深い傷を負った高倉長右衛門は、その後、思いを寄せる女に、今度は義兄の仇として命を狙われる……。大スター長谷川一夫の主演で、長谷川伸の名作『疵高倉』を映画化したもの。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガメラ対大魔獣ジャイガー", "ガメラ対大魔獣ジャイガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "南太平洋のウエスター島の石像が大阪の万博に運ばれた折、石像の魔力によって封じ込められていた大魔獣ジャイガーが甦り、万博会場をバックにガメラと死闘を展開する。話題の大阪万国博を舞台にしているが、マンネリ化は食い止められなかった。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A TICKET TO TOMAHAWK", "彼女は二挺拳銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1876年、コロラド州の鉄道開通をめぐって、商売を奪われることを恐れる駅馬車業者と鉄道を守ろうとする者との闘いを描くウエスタン・コメディ。トマホーク行き開通列車に乗り込んだ乗客および乗員の奮闘ぶりと、男まさりの拳銃の名手を演じるA・バクスターの可憐さが見ものだ。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎 多情剣", "眠狂四郎 多情剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "“女妖剣”で狂四郎に敗れた将軍の娘・菊姫が再び登場。顔にアザを持つ彼女は武蔵野の原野に屋敷を構え、狂四郎への復讐の機会を狙っていた。狂四郎は薄幸の少女・お春を待合から救い出すが、その頃遊女の殺人事件が起き、町に“眠狂四郎これを犯す”の立札が……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イメルダ", "イメルダ", "", "", "http://image.eiga.k-img.com/images/movie/54707/315x210.jpg?4215", 103, "1965年にフィリピン大統領夫人となり、その後20年間もファースト・レディの地位に君臨し続けたイメルダ・マルコスの実像に迫るドキュメンタリー。内気な少女時代、元大統領フェルディナンド・マルコスとの出会い、権力と贅を尽くした日々、演説中の暗殺未遂事件、ハワイ亡命に至るまで、これまで明らかにされなかったイメルダにまつわる数々の逸話が本人によって語られる。", "2009-09-12", "35,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血槍富士", "血槍富士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "巨匠・内田吐夢が「鳥居強右衛門」以来、13年ぶりに作った、戦後第１作。企画はマキノ雅広監督の実弟である名プロデューサー、マキノ満男で、企画協力に伊藤大輔、小津安二郎、清水宏、溝口健二というそうそうたるメンバーが並んでおり、内田監督は彼らの友情に支えられてカムバックを飾った。舞台は江戸時代。酒乱の気がある若様の槍持ちとして東海道を江戸に向かった権八は、途中様々な人間模様に触れ人の情を知る。ところが、若様がつまらないいさかいから殺され、槍で仇討ちをするハメに……。内田監督は封建制の理不尽さを骨太なタッチで描きつつ、ラストに「海ゆかば」をかぶせ、自己の戦争体験を反映させた。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マリア様がみてる", "マリア様がみてる", "", "", "http://image.eiga.k-img.com/images/movie/55447/315x210.jpg?3798", 90, "コミック＆TVアニメ化され人気を博した今野緒雪のライトノベルを、「女の子ものがたり」の波瑠と「NECK」の未来穂香主演で実写映画化。生粋のお嬢さまたちが通う私立リリアン女学園では、上級生が下級生と“スール（姉妹）”の契（ちぎ）りを結ぶ制度が伝統となっていた。平凡な学園生活を送っていた1年生の福沢祐巳（未来）は、生徒たちのあこがれの存在である2年生の小笠原祥子（波瑠）から突然の“姉妹宣言”を受ける。", "2010-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フィールズ・エンジェル", "フィールズ・エンジェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "日本版「チャーリーズ・エンジェル」ともいうべき森下千里主演のセクシー・アクション。大作映画のヒロインの座を巡って殺人事件が発生。３人の美女探偵は芸能界がらみの陰謀を暴くため、撮影が行われる東アジアのリゾート地、韓国・済州島へと飛ぶ。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Return of the Living Dead : Rave to the Grave", "バタリアン5", "", "", "http://image.eiga.k-img.com/images/movie/33707/315x210.jpg?4696", 95, "人気ホラー「バタリアン」シリーズの第5弾。前作で生き延びた主人公たちが通う大学でバタリアン・エキスが配合されたドラッグが流行し、ハロウィンのレイブパーティが恐ろしいゾンビたちで溢れかえる。前作に引き続きエロリー・エルカイェム監督がメガホンを取り、キャストも同じくジョン・キーフ、ピーター・コヨーテ、エイミー＝リン・チャドウィックらが出演。また本作では、シリーズの人気キャラクターであるタールマンも復活する。", "2006-10-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SKIN DEEP", "スキン・ディープ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "スランプ気味の人気作家ザックは無類の“女好き”。浮気の現場を見られて、妻アレックスから離婚宣告される。なんとかヨリを戻そうとするが、女たらしの悪癖は一向に直らず、事態は悪化するばかり……。蛍光塗料を塗ったコンドームが暗闇に飛びかう(？)前代未聞のベッドシーンが大うけの、アダルト向け艶笑コメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犀の角", "犀の角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "インディペンデントで活動を続ける井土紀州が日本映画学校俳優科、映画美学校フィクションコースとコラボレーションして作り上げた一連の作品を公開する「映画一揆　井土紀州2011」にて上映。過去の事件に端を発する排斥運動により各地を転々とする宗教団体と、その教団で修行する少女、うだつの上がらない高校生の少年との出会いからひと時の交流を描く。", "2010-11-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CHAMBER", "チェンバー 凍った絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "ベストセラー作家、ジョン・グリシャムの『処刑室』を映像化。爆破事件を起こして死刑の時を待つ祖父の弁護を担当することになった、気鋭の弁護士の奮闘を描く。人種差別主義者を祖父に持った主人公の苦悩にスポットを当てた作りとなっており、サスペンスよりも人間ドラマとしての色あいが濃い。DVDタイトルは「チェンバー／処刑室」。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／A LAST WORD OF MY WAR BUDDY", "地獄の要塞 コマンド６", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "朝鮮戦争最大の激戦地、598高地で繰り広げられる熾烈な戦いを描いた戦争アクション。国軍と人民軍が一進一退を続ける戦術上の要地、598高地。敵が作った食料保存と弾薬貯蔵の要塞を爆破するために、６名の精鋭兵士からなる特殊部隊が高地に送り込まれる。彼らは決死の覚悟で敵の要塞へ……。", "1987-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀色の髪のアギト", "銀色の髪のアギト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "数々の大ヒット・アニメを手掛ける最先端映像制作スタジオ“GONZO”が、劇場版オリジナル・アニメーションに初挑戦した作品。荒廃した300年後の地球で生きる少年アギトの成長が描かれる。遺伝子操作の失敗で森が人を襲う未来。地中に暮らすアギトは、荒れ果てた世界に戸惑う過去から来た少女トゥーラと出会い、互いに惹かれ合うが……。従来のアニメ技術と最新デジタルCG技術を完璧に融合させた映像の数々が見事だ。", "2006-01-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スクライド　オルタレイション TAO", "スクライド　オルタレイション TAO", "", "", "http://image.eiga.k-img.com/images/movie/57067/315x210.jpg?3105", 98, "「コードギアス　反逆のルルーシュ」「無限のリヴァイアス」などで知られる谷口悟朗監督が手がけ、2001年にTV放送された人気アニメーション「スクライド」の10周年を記念した特別版2部作の前編。新作パートの追加、全編新アフレコ、HDデジタルリマスターなどを施し、TVシリーズを新たに再構築した。物質を分解し、別物（アルター）として再構成する特集能力をもつ「アルター使い」の少年カズマと、対アルター能力者特殊部隊ホーリーの隊員・劉鳳（りゅうほう）が、外界と隔絶されたロストグラウンドを舞台に、互いの信念をぶつけ合いながら戦いを繰り広げる姿を描く。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／A　TALE　OF　TWO　SISTERS", "箪笥〈たんす〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「クワイエット・ファミリー」などの気鋭監督キム・ジウンが、韓国の有名な古典『薔花紅蓮伝』を現代風にアレンジし、あどけない姉妹が味わうこの世ならぬ恐怖を描き出す。舞台となる古めかしい屋敷の細部にまでこだわった美術、姉妹の意外な秘密が明らかになるクライマックスが見どころの耽美系ホラー。ソウル郊外の一軒家に長期入院から帰って来たスミとスヨンの姉妹。継母のウンジュは笑顔で迎えるが、姉妹は彼女に警戒心を抱く。その夜スヨンは何者かの気配を感じ、スミは亡き母の悪夢を見る。そして姉妹の周囲に怪奇現象が……。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RENT", "レント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "トニー賞を総ナメにした伝説のブロードウェイ・ミュージカルが待望の映画化。N.Y.を舞台に、愛や夢に悩み苦しみながらも懸命に生きる若き芸術家たちの姿を、躍動的に描く。バラエティに富んだ楽曲が若者たちの本音を見事に代弁。N.Y.、イーストヴィレッジに集う芸術家たち。家賃の取り立てに始まり、貧困、犯罪、エイズ、ドラッグ、同性愛といった問題を抱え、路頭に迷いながらも夢をあきらめない彼らの行く末は……。", "2006-04-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZELLY ＆ ME", "ゼリーと私", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "交通事故で両親を失い、ヴァージニア郊外に住む祖母に引き取られた娘。だが、祖母の愛情に縛られ、彼女の暮らしは窮屈なものだった。そんな彼女を、フランス人の家庭教師は不びんに思い、祖母のもとから解放しようとするが……。I・ロッセリーニ主演のヒューマン・ドラマ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Breathless", "息もできない", "", "", "http://image.eiga.k-img.com/images/movie/54947/315x210.jpg?3288", 130, "父への激しい怒りと憎しみを抱き、社会の底辺で生きるサンフンはある日、心の傷を隠しながら生きる勝気な女子高生ヨニと出会う。理由もなく強く惹かれ合う2人だったが、徐々に彼らの運命の歯車が狂いだす……。俳優として活躍してきたヤン・イクチュンの初長編監督作品。第10回東京フィルメックスで史上初めて最優秀作品賞と観客賞をダブル受賞した。", "2010-03-20", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FEST DER VO¨LKER−OLYMPIA TEIL I", "民族の祭典（オリンピア第一部）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "1936年のベルリン・オリンピックの模様を綴ったドキュメンタリー。ギリシャの古代オリンピックの遺跡から始まって、ベルリン・オリンピックで初めて試みられた聖火リレー、開会式、陸上競技の模様が収められている。ナチスの党大会の記録映画「意志の勝利」で名をあげたリーフェンシュタールが、選手の表情や心理を克明に描写する。第二部「美の祭典」とともにヴェネチア映画祭作品賞（ムッソリーニ杯）を受賞。", "1938-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUL FOOD", "ソウル・フード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "人種問題などの社会的意識から描かれることが多いアフロ・アメリカンの家族風景を、等身大のホームドラマとして綴った笑いと涙の感動作。弁護士として活躍する長女テリー、美容室を営む次女マキシーン、前科者と一緒になった三女バード。それぞれの結婚生活のトラブルや姉妹同士の葛藤が、アン・ヴォーグ、ボーイズIIメンらのソウル・ナンバーにのせて活写される。フライドチキン、スイートコーン・ブレッドなどの黒人料理＝ソウル・フードがズラッと並べられた食卓の風景も印象的。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長裸道中", "次郎長裸道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "豪快な殺陣と、一瞬見せる迫力ある演技でB級ながら、戦前のチャンバラ・スターとしてならした羅門光三郎。彼が清水次郎長に扮した本編では、ラストの大乱闘に、彼の持ち味がよく現れている。羅門の師匠で、彼が名を取った光岡龍三郎が、法印大五郎役で助演しているのもご愛嬌。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DISTURBED", "沈黙のリベンジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「時計じかけのオレンジ」「カリギュラ」など強烈な印象を残しているM・マクダウェルが、またまた怪演ぶりをみせたサイコ・サスペンス。精神病院の院長であるラッセル医師は、注射針と意識のない女性に快感を覚え、婦長は患者を虫ケラ同然に扱っていた。そこへ10年前この病院で母親を亡くした女性が、その死の真相を暴こうとやって来る。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lola Montes", "歴史は女で作られる", "", "", "http://image.eiga.k-img.com/images/movie/50817/315x210.jpg?3903", 110, "「輪舞」（1950）のマックス・オフュルス監督が、19世紀に実在した美貌の踊り子ローラ・モンテスの恋の遍歴をテクニカラーの華麗な色彩とシネマスコープのスケール感あふれる映像で描いた大作。1956年フランス映画界では最大級の製作費が投じられた作品だったが、スタジオ側が監督の意図とは異なる編集を勝手に施したことで波紋を呼び、興行的にも失敗してしまった“呪われた傑作”としても知られる。19世紀パリ。不遇な少女時代を送ったローラ・モンテスは、サーカス団の踊り子となり、数々の男と浮名を流していく。やがてババリアへたどり着いたローラは、国王ルイ1世を虜にする。2011年、デジタルリマスター版でリバイバル。", "1956-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドキュメント　太陽の牙ダグラム", "ドキュメント　太陽の牙ダグラム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "'81年から3年間放映されて好評を得たTVアニメがスケールアップ。唯一無比の劇場版は、男のロマンあふれるSFロボット・アドベンチャーとなった力作だ。地球とその植民地星デロイアとの戦いを、一人の男の革命に懸ける情熱と共に熱く描き出す。デロイア星は地球へのエネルギーと食料の大きな供給地だったが、地球からは不当な扱いを受けていた。地球の若者がその星の革命軍に入って活躍する。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Funny Games U.S.", "ファニーゲームU.S.A.", "", "", "http://image.eiga.k-img.com/images/movie/53817/315x210.jpg?2357", 111, "「ピアニスト」「隠された記憶」のミヒャエル・ハネケ監督が、自身の傑作「ファニーゲーム」（97）の舞台をアメリカに移してセルフリメイク。主演のナオミ・ワッツが製作総指揮も務め、ティム・ロス、マイケル・ピットら豪華俳優が集結したサディスティック・スリラー。夏の休暇で湖のある別荘にやってきたファーバー一家のもとに、卵を分けてほしいと隣人の青年が突然やってくる。母親のアンは感じのよい青年に卵を分けてやるが、青年は卵を不自然に落とし……。", "2008-12-20", "1,3,2,77,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIL　SE．．", "ディル・セ／心から", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 167, "インド北東部で、反政府活動を取材するアマルは深夜の駅頭で出会ったメグナに恋をする。それは、彼の生涯を大きく左右する運命の出会いだった……。大都市に暮らす青年と、テロリストとして教育された女のラブ・ストーリー。出会ってはいけなかった男と女をめぐる恍惚の音楽絵巻を「エリザベス」のS・カプールがプロデュースしたことで話題となった。", "1998-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古惑仔２之猛龍過江／YOUNG AND DANGEROUSII", "欲望の街　古惑仔II　台湾立志伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "香港のニューウェーブ青春シリーズ“古惑仔（こわくちゃい)”の第２作。クールなチンピラたちの、さらなる闘いがバイオレンスを織り交ぜて描かれる。古惑仔の一人である洪興社のナンは、カジノ経営の大任をめぐって他の組員と対立。その頃、洪興社と台湾の三連会との間にも亀裂が生じはじめていた。ナンの仲間サンカイは三連会にも恩があり、組織間の板挟みに苦悩。そんな中、ナンとサンカイは何者かによって罠にハメられ……。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOUSE　OF　１０００　CORPSES", "マーダー・ライド・ショー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ヘヴィ・ロック界の立役者として知られるR・ゾンビが手掛けた長編デビュー作。トビー・フーパーの「悪魔のいけにえ」をゾンビ監督流にアレンジした本作は、怪奇趣味満載の残酷ショーの嵐。マニア心をくすぐる様々なホラー映画からインスパイアされた映像の数々が展開する。妥協を許さない完璧主義が作り上げる恐怖の世界は圧倒的。ユニークな取材場所を求めてアメリカ中を旅して回る４人の若者たちが、とあるガソリンスタンドに立寄る。そこではピエロの格好をした変人オーナーが、バケモノ博物館を運営しており、４人はオーナーの案内で見学することにするが……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北京雑種／BEIJING BASTERDS", "北京バスターズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "当局による脚本審査など、映画製作が政府の管轄下にある中国で、完全な独立自主製作を果たし、“中国第六世代”の幕明けとなった作品。質素なミュージックバーを経営する青年、公共の場でのコンサート開催を拒否されるロックスター、失業中の作家、詐欺師に全財産を奪われた画家らの日常を描くことによって、中国の若者が持つ青年期特有の憤りや不満、喪失感や挫折感をリアルに描き出している。中国ロックシーンの草分け的存在ツイ・ジェンがロック・スターを演じ、中国初のロック映画という側面も強く打ち出している。中国国内では上映禁止処分になり、チャン・ユエン監督は事実上の国外追放となった。", "1993-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嵐を呼ぶ楽団", "嵐を呼ぶ楽団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ジャズ・バンドに若いエネルギーをぶつける男女の青春サクセス・ストーリー。「嵐を呼ぶ男」で名を上げた井上監督の朗らかでキビキビとした演出が光る。ジャズ界の草分けの遺児、宏志は豊かな才能を持ちながら気性激しく、仕事が続かない。人気ダンサー・ルリ子の伴奏を務めても、即興演奏で目立とうとして彼女に罵倒される。そんな宏志が自分のバンドを持つという夢をドサ廻りを続けつつ花開かせていく。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い天使", "赤い天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "若尾文子が従軍看護婦に扮した人間ドラマ。天津の陸軍病院に配属された西さくらは、毎日、傷病兵の世話に明け暮れる従軍看護婦。彼女はそんな中、軍医の岡部と知りあい、愛し合う。だが、応急看護班を編成し、前線に向かった二人は敵に囲まれ、コレラの流行る部落に取り残されてしまうのだった……。増村ならではの壮絶な愛情描写が忘れがたい佳作。日本よりもヨーロッパで高い評価を得た。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Big Daddy", "ビッグ・ダディ", "", "", "http://image.eiga.k-img.com/images/movie/1867/315x210.jpg?4726", 93, "ソニーはキャリアにもマイホームにも興味がない、宅配フードとスポーツ番組漬けの毎日を過ごすお気楽人生の32歳。いつまでも子供みたいなソニーに、恋人のバネッサもとうとう愛想をつかしてしまう。そんな時、結婚したばかりのソニーの親友、ケビンと昔の恋人との間にできた子供ジュリアンが訪ねてくる。バネッサにしっかりした自分の姿を見せるため、ソニーは出張中のケビンの代わりにジュリアンのパパになろうと思いつく。", "2000-06-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Toy Story 3", "トイ・ストーリー3", "", "", "http://image.eiga.k-img.com/images/movie/53487/315x210.jpg?3416", 103, "おもちゃの世界を舞台にしたピクサーの人気シリーズの第3弾にして初の3D映画。カウボーイ人形のウッディやバズ・ライトイヤーらの持ち主アンディは大学進学が決定し、おもちゃを整理することに。だが母親の手違いなどにより、おもちゃたちは近所の保育園に寄付されてしまう。アンディに捨てられたと思いこみ傷心のおもちゃたちだったが、ウッディはただ一人アンディを信じて保育園からの脱出を試みる。製作総指揮にジョン・ラセター。監督は「モンスターズ・インク」のリー・アンクリッチ。", "2010-07-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人類の戦士", "人類の戦士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "S・ルイスがピュリッツァー賞を辞退した小説を、巨匠J・フォードが映画化したヒューマン・ドラマ。フォードのフィルモグラフィーにおいて、日本では長らく見ることができなかった幻の作品である。細菌学の権威であるアロウスミス博士がゴッドリーブ教授に誘われ、ペスト撲滅のため西インド諸島に渡る。しかし、教授や妻が病魔に倒れてしまい……。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE DAY IN THE LIFE OF IVAN DENISOVICH", "イワン・デニーソヴィチの一日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "スターリンを批判して1973年に国外追放されたソルジェニーツィンの同名小説を、イギリス人スタッフ＆キャストで映画化した異色の人間ドラマ。厳寒のシベリア強制収容所の一日を、夜明けから夜更けまで淡々と描いていく。声高にメッセージを叫ぶことなく、原作に忠実に反体制を訴えた作品。", "1971-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸最後の日", "江戸最後の日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "明治元年、江戸城の無血明け渡しをめぐって主戦論者を向こうにまわし、平和解決を主張する勝安房守の努力を描く。この映画が製作された年は戦時色が濃くなりつつあった時代であり、時代劇も忠君愛国的なものや外国勢力から国を守ったものが盛んに取り上げられた。安房守には阪東妻三郎が扮し、豪放で人情家の勝安房守が苦悩する姿を見事に演じ、それまでの剣劇スターのイメージを払拭した。鳥羽伏見の戦いに敗れた徳川慶喜は、江戸に戻り幕府方の鎮静に明け暮れる。幕府には官軍との一戦を主張する榎本武揚、大鳥圭介などの主戦論者と、高橋伊勢守、勝安房守の穏健派とが対立していた……。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROCKETEER", "ロケッティア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "フライング・レーサーとして大空をかける命知らずのヒコーキ野郎が、ある日オンボロ機の操縦席の下から不思議なロケット噴射装置を発見。背中に固定すると大空を縦横無尽に飛べるその装置は“ロケッティア”と呼ばれ、ナチスも狙いをつけていた……。人気コミックを原作にしたアクション・アドベンチャー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("tokyo skiｎ", "tokyo skiｎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "“どでかいことをする”という夢を見つつ東京に暮らして５年になる中国人・周。画家を目指してニューヨークに渡ったものの果たせず10年ぶりに帰国した一雄。故郷・パキスタンの“何もない”豪邸を飛び出してきたアリ。さらに、きらめく街を漂う“六本木ギャル”たち。夢とは裏腹に非合法な仕事でその日を暮らす周を中心に、様々な形での“異邦人”たちが本当の自分を探してさまよう姿を描いたストリート・ムービー。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON’T PANIC", "異次元の悪夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "斬新なヴィジュアルと壮絶なスペシャル・メイクで悪夢そのものを映像化する異色サイキック・ホラー。内気な青年マイケルは、ふざけて降霊の儀式を行ったことから悪霊に取り憑かれ、異次元の悪魔の呪いに巻き込まれていく。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("噂の女", "噂の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "溝口健二監督が３度目のヴェネチア映画祭受賞作「山椒太夫」の次に撮った現代ものの小品。京都の色街・島原を舞台に、女手一つで廓を経営する母と、母の商売に疑問を持つ娘との葛藤を描く。監督としての活躍を始めた田中絹代が最後に出演した溝口作品。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GE´NIAL，MES PARENTS DIVORCENT！", "わんぱく離婚同盟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "３組のうちひと組の夫婦が離婚する、というフランスの社会事情を背景に、それを苦にすることなく、むしろ自分たちに有利に別れた両親を活用する今どきの子供たちを主人公にした物語。映画初出演ながら実にのびのびと演じる、子供たちの屈託のない演技が微笑ましい。", "1991-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STUCK　ON　YOU", "ふたりにクギづけ", "", "", "http://ecx.images-amazon.com/images/I/51BW9HM9X1L._SL210_.jpg", 119, "ボビー＆ピーター・ファレリー兄弟が、いつものごとくヤバいネタを独特のユーモアで料理したヒューマン・コメディ。腰の部分でつながった結合性双生児の兄弟が、ショービジネス界での成功を夢見てハリウッドに殴り込み。M・デイモンが気の弱い弟を、G・キニアがイケイケの兄貴を快演。“二人で一人”なことを活かして、スポーツ選手として大活躍するなどおバカなギャグが満載だが、本筋はいたってストレートな兄弟愛の物語。笑いと感動が同居する、お得感タップリの必見作だ。身体がつながった結合性双生児のボブとウォルト。東海岸ののどかな島で、仲良くハンバーガー屋を営んでいたが、俳優になりたいウォルトの夢を叶えるためにハリウッドに向う。", "2004-12-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スターフィッシュホテル", "スターフィッシュホテル", "", "", "http://image.eiga.k-img.com/images/movie/33837/315x210.jpg?4809", 98, "「不思議の国のアリス」と日本の古典的な怪談話をモチーフに、夢と現実が交錯する不条理な世界に引きずり込まれていく男の姿を描いたミステリー。ごく普通のサラリーマン有須は、人気作家・黒田のミステリー小説を唯一の楽しみにしていた。そんな彼の周囲で、不可思議な出来事が次々と起こり始め……。監督・脚本は日本在住のイギリス人ジョン・ウィリアムズ。主人公・有須を演じるのは、監督が脚本を書く段階から想定していたという佐藤浩市。", "2007-02-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAMAN〈ママーン〉", "MAMAN〈ママーン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "松平健主演の痛快コメディ。謎のマジシャンと出会ったことから、体を縮められてしまったDJ・ケント。最愛の女性との結婚を間近に、彼は苦境に追い込まれ……。 ある日突然、体が小さくなってしまった男の結婚騒動が、ユーモラスに繰り広げられる。ナンセンスな展開と、家族愛をベースにしたハートウォーミングな味わいが秀逸。", "2006-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パラノーマル・アクティビティ第2章 TOKYO NIGHT", "パラノーマル・アクティビティ第2章 TOKYO NIGHT", "", "", "http://image.eiga.k-img.com/images/movie/55747/315x210.jpg?2791", 0, "低予算ながら全米で大ヒットを記録したホラー「パラノーマル・アクティビティ」の続編を、東京を舞台に映画化。「放送禁止」シリーズの長江俊和監督がメガホンをとり、「ひゃくはち」「BECK」の中村蒼とモデル出身の青山倫子が主演を務める。アメリカ旅行から帰国したばかりの春花は、目覚めると部屋の中のものが勝手に移動していることに気づく。弟・幸一のいたずらだと思い込むが、潔白を証明するため幸一がビデオカメラで撮影した映像により、衝撃的な現象を目の当たりにする。", "2010-11-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Space Cowboys", "スペース・カウボーイ", "", "", "http://image.eiga.k-img.com/images/movie/1177/315x210.jpg?3754", 130, "1958年。フランク・コービン（イーストウッド）率いるアメリカ空軍の“チーム・ダイダロス”の4人は、宇宙飛行のため厳しい訓練に耐えていた。しかし、直前になってそのプロジェクトをNASAが遂行。アメリカ初の宇宙飛行士は一匹のチンパンジーになった。それから約40年。NASAからフランクのもとに、故障したロシア衛星を修理してほしいと連絡が入る。彼はこの任務のためにかつての仲間達を集め始める。", "2000-11-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アンモナイトのささやきを聞いた", "アンモナイトのささやきを聞いた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "北海道を拠点に数多くの短編を発表し、その独特の作風が高い評価を受けている山田勇男の長編第一作。宮沢賢治と妹トシの関係に着想を得、可逆的な時間の中に生きる愛の物語を夢幻的なイメージで描く。デレク・ジャーマンとのコンビで知られる英国の音楽家S・ターナーによる音楽も話題に。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヌードの夜", "ヌードの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "石井隆監督による、劇場映画第３作。監督デビュー作「天使のはらわた・赤い眩暈」と同様、竹中直人を主役に、石井監督の永遠のキャラクターである“名美”と“村木”の物語を綴っていく。結婚相手ができた名美は、ホストクラブの支配人・行方と手を切ろうとする。彼女は、“何でも代行屋”の看板を掲げる紅次郎（実は村木）に、福岡から上京したと偽って、東京案内を頼む。その後、行方を殺害して彼女は姿を消し、紅次郎に死体の始末をさせようとするが……。商社マンからドロップアウトし、何でも屋をやりながら“受け身”の人生を送ってきた村木が、自分の中の男に目覚めていく過程が圧巻。竹中直人の哀愁を漂わせた好演も見もの。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陰陽法王", "ジョイ・ウォンの魔界伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "キン・フー初の大陸ロケ作品。「チャイニーズ・ゴースト・ストーリー」で日本でも一躍人気者となったジョイ・ウォンが妖艶な幽霊を演じる。科挙試験に落ちた放蕩青年が、ある夜さまよう美女と出会う。美しさに惹かれた青年は自宅に招き入れるが、彼女は人の皮をかぶった幽霊であった……。低予算に苦しむなかで当時流行のスピーディーなファンタジー・アクションに仕上げているが、この作品がキン・フーの遺作となった。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギャンブル一家 チト度が過ぎる", "ギャンブル一家 チト度が過ぎる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "複雑な義理関係で結ばれた老若男女６人組。彼らの共通項はいずれ劣らぬギャンブル狂い。一獲千金を夢見て、今日もトラックに乗り全国各地を暴れ回るが……。喜劇の大姐御から関西落語界のスターまで、喜劇人が勢ぞろいして繰り広げる破天荒な娯楽大作。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLTERGEIST II", "ポルターガイスト2", "", "", "http://ecx.images-amazon.com/images/I/51o8hsskHgL._SL210_.jpg", 91, "前作では“愛の絆”を唯一の武器に、邪悪な霊と戦い抜いたフリーリング一家。今回もその末娘キャロルアンの身辺に様々な怪異現象が起こり、再び悪霊との対決が始まる。前作では謎のままだった悪霊の正体もついに解明されるなど、興味津々の新感覚オカルト映画。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　HOUSE　OF　YES", "ストーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ニューヨークの大学生マーティが、感謝祭の休暇でワシントンD.C.の実家に帰ってくる。しかし彼がフィアンセを連れてきたために、この一家に潜む狂気と謎が明かされていく。舞台劇を巧妙に映画化したサスペンス・ミステリー。嵐の夜の悪夢のような出来事を、シュールかつブラックな笑いを盛り込んで描き出す。個性派女優P・ポージーが、元大統領夫人ジャッキー・オナシスを連想させる一家の長女を圧倒的なインパクトで怪演。その少女時代を「シャンプー台のむこうに」のR・リー・クックが演じている。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春大全集", "青春大全集", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "吉永小百合が「風の慕情」に続いて松竹映画に主演した青春映画。結婚適齢期の律子は恋人の保がどれだけ結婚生活のプランを抱いているか不安でならなかった。そんなある日、律子は姉の勧めで教師の阿部とお見合いをしてしまい、次第に心が揺れ動いていくが……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四谷怪談", "四谷怪談", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "夫に裏切られたお岩が、亡霊となって男を呪うという鶴屋南北の名作怪談。お岩のメイクアップなどは現在の技術に劣るものの、赤・白・黒の三色に統一された色彩設計が怪異を倍増させて見せる。海外のホラー映画と違い、情に訴える心理的恐怖映画。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANGIN’ WITH THE HOMEBOYS", "N.Y.キッズグラフィティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ニューヨークのサウスブロンクスを舞台に、「アメリカン・グラフィティ」を思わせる青春ドラマが展開する。本作が初主演のJ・レグイザモが、初々しい演技を見せる。共演者にはD・E・ダグら、同じく気鋭の若手を起用。ナンパにケンカ、そして若さゆえの悩みを織り交ぜながら、さわやかな作品に仕上がっている。1991年度サンダンス・フィルム・フェスティバルで最優秀脚本賞を受賞。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪猫岡崎騒動", "怪猫岡崎騒動", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "入江たか子主演の“化け猫”もの第３作。妾の子であるため家督を継げぬ水野刑部は、兄の伊勢守を毒殺するが側室・萩は雪太郎を生む。刑部は求愛を拒む萩を殺害し雪太郎も殺害しようとするが……。今回は仇役側がやられっぱなしではなく、化け猫対策をいろいろと駆使するのが新趣向である。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("龍門客棧", "残酷ドラゴン・血斗！龍門の宿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "東南アジア全域で記録的大ヒットとなり、武侠アクション映画ブームを巻き起こした記念碑的作品。明朝時代、無実の罪で処刑された大臣の遺児の暗殺をもくろむ特務機関と、遺児を守る大臣の腹心たちの生き残りをかけた凄まじい死闘が、辺境の宿・龍門閣を舞台に繰り広げられる超絶アクション。監督自身が考案したといわれるワイヤーによる飛翔アクションも見所のひとつ。", "1967-01-01", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Munich", "ミュンヘン", "", "", "http://image.eiga.k-img.com/images/movie/1127/315x210.jpg?3323", 164, "1972年9月5日、ミュンヘン・オリンピック開催中に、パレスチナゲリラ、ブラック・セプテンバーがイスラエル選手団を襲撃。この事件後、イスラエル政府は特殊部隊モサドにパレスチナゲリラ11人の暗殺を命じるが、暗殺グループのリーダー、アブナーは報復行動に疑問を抱いて苦悩する。「トロイ」のエリック・バナ、新007役で注目のダニエル・クレイグ、「シャイン」のオスカー俳優ジェフリー・ラッシュらが共演。", "2006-02-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hello Ghost", "ハロー!?ゴースト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "", "2012-06-10", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沙耶のいる透視図", "沙耶のいる透視図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "３人の男女の愛と相克を、無気質な感覚で描いたエキセントリックなラブ・サスペンス。主演の高樹沙耶はこの映画でデビュー。共演には名高達郎と個性派ミュージシャンの土屋昌巳という異色の顔合わせ。カフェバー、ロフトなど当時の東京風俗も楽しめる。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("清水の暴れん坊", "清水の暴れん坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "石原裕次郎と赤木圭一郎の記念すべき初共演作。裕次郎扮する敏腕ラジオ・プロデューサーと、赤木扮する麻薬組織のチンピラの心の交流が、痛快なアクションを交えて描かれる。裕次郎があまりに立派すぎるキャラクターで、二人のギャプも見どころ。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談新耳袋殴り込み！劇場版　沖縄編", "怪談新耳袋殴り込み！劇場版　沖縄編", "", "", "http://image.eiga.k-img.com/images/movie/56487/315x210.jpg?4718", 90, "日本全国から集めた実話怪談集「現代百物語　新耳袋」を原作とした人気TVシリーズ「怪談新耳袋」のスピンオフ企画「怪談新耳袋殴り込み！」の劇場版。原作に収録された心霊スポットを探検する「新耳Gメン」のメンバーが幽霊を挑発し、心霊現象をカメラに収めるドキュメンタリー。「沖縄編」では、太平洋戦争最大の悲劇の地でもある「ひめゆりの塔」や一家殺人事件のあった廃きょなどで幽霊を追う。", "2011-07-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私たちの時代", "私たちの時代", "", "", "http://image.eiga.k-img.com/images/movie/56797/315x210.jpg?3392", 110, "2010年にTV放送され高い評価を得たドキュメンタリーを劇場公開。07年3月25日、石川県奥能登の小さな町・門前町を震度6強の能登半島地震が襲った。被災からわずか2週間後、まだ傷跡の残る門前高校のグラウンドで女子ソフトボール部が練習を再開。悲嘆に暮れる町に女子高生たちの明るい掛け声が響き、それをきっかけに町が再生に向けて動き出す姿を追う。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Starsky & Hutch", "スタスキー＆ハッチ", "", "", "http://ecx.images-amazon.com/images/I/51Th9p36mhL._SL210_.jpg", 100, "1970年代に人気を博したアメリカの同名TVシリーズを、ベン・スティラー＆オーウェン・ウィルソン主演で映画化。真面目すぎる性格が災いして周囲に迷惑をかけてしまう刑事スタスキーは、上司の命令でいい加減な性格の刑事ハッチとコンビを組むことに。とある水死事件の捜査に乗りだした2人は、巨額の麻薬取引に関わる富豪の男リースの存在にたどり着く。監督は「アダルト♂スクール」のトッド・フィリップス。", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽は知らない", "太陽は知らない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 15, "", "2007-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ОСВОВОЖЛЕНИЕ", "ヨーロッパの解放I～III", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 471, "ソ連側から見た第二次大戦の集大成映画。歴史的事実をセミドキュメンタリー・タッチで描いたこの超大作は、1943年の独ソ戦から1945年のベルリン陥落までを、ソ連映画界の総力をあげて再現した。オリジナルは５部作８時間（日本では３部にまとめられて公開)。", "1970-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUADARILLE", "カドリーユ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ポップ・シンガーでもあるフランスのコメディエンヌ、V・ルメルシュが手掛けた初の長編監督作。ベテラン編集者と舞台女優のカップルの前にアメリカの映画スターが現れたことをきっかけに、美人ジャーナリストを巻き込んで男女４人の恋のさや当てが始まる。S・ギトリの戯曲がお気に入りだというルメルシュが、1938年に製作されたギトリ原作の映画をリメイク。全編の大半をホテルの一室に限定した演劇的要素の濃い仕上がりで、パステル・カラーのポップな美術がセンスの良さを感じさせる。流麗な音楽を担当するのは、ルメルシュの私生活のパートナーでもあるB・ブルガラ。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("復讐・消えない傷痕", "復讐・消えない傷痕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「復讐・運命の訪問者」の続編。元刑事の安城は、古紙回収所で地道に働きながら、暴力団吉岡組の組長と親交を温めていた。そして、妻殺しの背後にあったヤミ資金の関係者を次々に殺していく。それを知った若い刑事、西は安城の思いを知りながら止めようとするのだが……。安城と吉岡の友情が物語の中心となっており、長回し撮影がその奇妙な光景を盛り上げている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にせ刑事", "にせ刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "拳銃を紛失したことから刑事をクビになった寅松は、ある日、不良に絡まれた女性を助けるが、乱闘のはずみでその女性、保母の美恵子はケガをする。美恵子の入院中に、彼女の生徒が誘拐され、責任を感じた寅松は、独力で捜査を開始する……。高岩肇のオリジナル脚本を社会派・山本薩夫が監督したコミカル・モードの刑事ドラマ。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("凶気の桜", "凶気の桜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "窪塚洋介が企画段階から参加した過激な青春ドラマ。若きナショナリスト山口は、幼なじみの市川と小菅とともに、ネオ・トージョーなる結社を結成。自分たちが生まれ育った渋谷の街から“ゴミ”を抹殺するため、不良狩りを始める。恐いもの知らずの彼らは、右翼系の暴力団幹部にかわいがられ、ますます暴力的になっていく。だが、その裏には暴力団の陰謀があった……。原田芳雄、江口洋介らのすご味を利かせた熱演の中、窪塚洋介がギラギラとした狂気を見せひと際存在感を放つ。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ARABIAN KNIGHT", "アラビアン・ナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "「千夜一夜物語」ではなく、KNIGHT＝騎士を主人公にしたアニメ。銀の川に囲まれた夢の王国ゴールデン・シティは、居眠り王のもと平和そのものだった。しかし、王国の守り神として信じられている３つの魔法の球“ゴールデン・ボール”が盗まれてしまい大騒ぎとなる。その頃、若き靴職人タックが泥棒に間違えられて牢屋に入れられそうになるところを、ヤムヤム王女に助けられる。タックは王女のためなら何でもしようと決心をする。“ゴールデン・ボール”は、国を支配しようと考えるジグザグ大臣の手に渡っていた。タックは王女のため、国のため、“ゴールデン・ボール”を奪い返す戦いに立つのだった。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Goethe!", "ゲーテの恋　君に捧ぐ「若きウェルテルの悩み」", "", "", "http://image.eiga.k-img.com/images/movie/56117/315x210.jpg?1254", 105, "1774年に発表されて以降、いまなお読み継がれている恋愛小説の名作「若きウェルテルの悩み」の誕生に秘められた、ドイツの文豪ゲーテの恋の物語を描く。自由奔放な若者ゲーテはある日、美しい少女シャルロッテと出会い恋に落ちる。しかし、シャルロッテは父親の命令でゲーテの上司ケストナーと結婚することになり、失望したゲーテは自分の気持ちを伝えるため、ある物語をつづりはじめる。監督は「アイガー北壁」のフィリップ・シュテルツェル。", "2011-10-29", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ART OF LOVE", "恋するパリジェンヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "金持ちの娘と婚約しているポールは、パリで絵の勉強をしている。才能のなさに絶望した彼はアメリカへ帰ると言い出すが、同居人のケーシーが絵の値段をつりあげるため狂言自殺を勧める。ところが彼がセーヌ河に飛び込もうとした寸前、先客が現れ……。アンジー・ディキンソン、エセル・マーマンなど、多彩な共演陣も楽しいラブ・コメディ。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カレーのにおい", "カレーのにおい", "", "", "http://ecx.images-amazon.com/images/I/41ajq7iCxHL._SL210_.jpg", 74, "青春と性をテーマにした「青春Hシリーズ」第6作。カレー好きな男が織り成す日常をシニカルかつ軽やかにつづった和製スクリューボール・コメディ。「ユニットバス・シンドローム」「代行のススメ」の山口智が監督・脚本を兼ねる。カレーライスを異常なまでに愛する男・藤瀬は、複数の女と付き合っていた。しかし彼の目当ては女たちの体ではなく、彼女たちがつくってくれるおいしいカレーライスだった。そんな藤瀬が、カレー嫌いの女に本気で恋をしてしまう。", "2011-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLORY！ GLORY！", "聖女はロック・シンガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "スタッキー牧師の教会は、彼のカリスマ的魅力で地方TV局に番組を持つほど。ところが牧師が病気で倒れ、息子ボビーが跡を継ぐが、おカタイばかりのボビーの説教に信者も寄付金も減るばかり。起死回生に必死のボビーは偶然出会った無名の女性ロッカー、ルースをむりやり教会で歌わせたところ意外やこれが大成功。信仰心ゼロでドラッグ中毒だったルースも聖女に変身、スターの道を歩き出す。教会とロックというミスマッチがウリのほのぼのコメディ。スタッキー牧師役をオールドファンには懐かしい、TV『逃亡者』のジェラード警部ことバリー・モースが演じる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月猫に蜜の弾丸（たま）", "月猫に蜜の弾丸（たま）", "", "", "http://ecx.images-amazon.com/images/I/31QXGB4FFTL._SL210_.jpg", 70, "「盲獣vs一寸法師」などで美術監督を手がけた港博之が初メガホンを取り、ギャングに復讐する化け猫の恐怖をスタイリッシュに描いた奇想天外ホラー。本人と見間違うほど精巧なダミーロボットを作ることのできる人形師・大角の才能に目をつけたギャングの手下クロキンは、組織のナンバー2・追分の女で、自分も惚れているマリのダミーを作らせるが……。日本映画界を代表する監督と新人監督が競作する「ホラー番長」シリーズの1作。", "2004-10-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("草間の半次郎 霧の中の渡り鳥", "草間の半次郎 霧の中の渡り鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ヤクザ者・草間の半次郎は、義理のために人を斬ってある浜へ逃れて来る。そこでは土地の親分が、村娘をさらって他国に売り飛ばしていた。ヤクザを毛嫌いする網元の女主人・おとよが、幼い頃に別れた自分の真実の母と知った彼は、村を救うため大活躍する。大川橋蔵らによる豪華キャストの股旅もの。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stardust", "スターダスト", "", "", "http://image.eiga.k-img.com/images/movie/16637/315x210.jpg?2391", 128, "イングランドのはずれのウォール村。“越えてはならない”壁の向こうに、流れ星が落ちるのを見た青年トリスタンは、恋焦がれる美女ビクトリアへの愛の証に、星を取りにいこうと旅立つが……。イギリスの人気作家ニール・ゲイマンの原作を映画化したファンタジー・アドベンチャー。クレア・デーンズと新鋭チャーリー・コックスを主演に、ミシェル・ファイファー、ロバート・デ・ニーロ、ピーター・オトゥールら名優がクセのあるキャラクターで出演。", "2007-10-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEXAS，ADDIO", "ガンマン無頼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "町の人々の絶大なる信頼を受けていた保安官バートが、ある日突然、町を去る。彼には父を殺したシスコという男を捜し出し、裁判にかけるという目的があったのだ。弟のジムとともに国境を越えメキシコの町へ入ったバートだが、そこでは残虐な支配者シスコが暴力をほしいままにしていた。F・ネロ主演のマカロニ・ウエスタン。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Monster House", "モンスター・ハウス", "", "", "http://image.eiga.k-img.com/images/movie/33817/315x210.jpg?4311", 90, "スティーブン・スピルバーグとロバート・ゼメキスの師弟コンビによるフルCGアニメーション。偏屈な老人ネバークラッカーの家の向かいに住む少年DJは、ある日、ネバークラッカーの家が、敷地に入ったものを何でも飲み込んでしまう生きた家であることを知る。DJは友人2人と連れ立って、その“モンスター・ハウス”の真相を探り始めるが……。", "2007-01-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カーテンコール", "カーテンコール", "", "", "http://image.eiga.k-img.com/images/movie/1347/315x210.jpg?4167", 111, "日本アカデミー賞作品賞を受賞したヒット作「半落ち」や「チルソクの夏」の佐々部清監督が、自身の故郷である下関を舞台に、家族の絆と、戦後のプログラム・ピクチャーへの愛を描く感動作。東京の出版社で働く香織は故郷・福岡のタウン誌に異動になり、ある投稿から昭和30年代から40年代の下関の映画館で活躍していた幕間芸人・安川修平のことを知ってその調査を始める。主演は「スワロウテイル」「花とアリス」の伊藤歩と藤井隆。共演に鶴田真由、藤村志保、夏八木勲など。", "2005-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Duellists", "デュエリスト／決闘者", "", "", "http://ecx.images-amazon.com/images/I/51J7EBXX6BL._SL210_.jpg", 101, "　1800年、フランス。上官からの命令を受け、フェロー中尉のもとに向かったデュベール中尉は決闘を挑まれ、彼に勝ってしまう。その後もフェローは幾度かデュベールの前に現れ、両者は闘うことに。それから数年後、デュベールはフェローが反逆者として処刑されるという話を聞くが……。映像派リドリー・スコットの初監督作品。", "1977-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("骨までしゃぶる", "骨までしゃぶる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "遊廓に閉じこめられ、骨までしゃぶりつくされる運命の遊女が、自由を求めて脱出を図る。加藤泰が撮った初めての“チャンバラのない映画”。彼の映画には欠かすことのできないヒロイン・桜町弘子が、気丈な遊女を体当たりで演じた。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の妻たち・死んで貰います", "極道の妻たち・死んで貰います", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "前作で“極妻”初主演を飾った高島礼子が再び登板した新シリーズ第２作。西日本最大のヤクザ一家傘下系列で若頭を務める拝島組組長、泰次の妻、久仁子は服役中の夫に代わり、組を仕切っていた。だが、総長が急死、若頭補佐の半沢が泰次を出し抜いて新総長を襲名する。夫が出所するまでに反撃に転じようとする久仁子だったが、半沢を背後で操る唐津はその勢いを封じるために獄中の泰次を殺害し……。久仁子と半沢の妻しのぶ、そして久仁子とは旧知の間でありながら半沢の愛人となる飛鳥。惚れた男のために命を懸ける３人の女たちが、時に意地をぶつけ合い、時に心を寄せ合い戦う様を鮮烈に映し出す。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレヨンしんちゃん　ブリブリ王国の秘宝", "クレヨンしんちゃん　ブリブリ王国の秘宝", "", "", "http://ecx.images-amazon.com/images/I/61NOtBDNOpL._SL210_.jpg", 87, "“クレヨンしんちゃん”シリーズの長編劇場版第２作。商店街の福引きで南の楽園、ブリブリ王国行きをつかんだしんのすけ一家。しかしそれは王国の秘宝を狙う秘密結社ホワイトスネーク団の陰謀だった。一団に誘拐されたしんのすけは、ホワイトスネーク団のボス、アナコンダ伯爵と、秘宝によって復活したブリブリ魔人に挑んで行く。", "1994-04-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Kids Are All Right", "キッズ・オールライト", "", "", "http://image.eiga.k-img.com/images/movie/55667/315x210.jpg?1755", 106, "アネット・ベニング、ジュリアン・ムーア、マーク・ラファロら豪華キャスト共演のファミリー群像劇。ニック（ベニング）とジュールス（ムーア）は結婚して、南カリフォルニアで暮らすレズビアンのカップル。彼女たちにはそれぞれ子供がいて、家族4人で暮らしているが、ジュールスの息子で弟のレイザーは、大学進学で家から出てしまう姉のジョニに頼み込み、自分たちの父親（人工授精の精子提供者）を一緒に探しだそうとするが……。共演にミア・ワシコウスカ、ジョシュ・ハッチャーソン。監督は「しあわせの法則」のリサ・チョロデンコ。", "2011-04-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼくとママの黄色い自転車", "ぼくとママの黄色い自転車", "", "", "http://image.eiga.k-img.com/images/movie/54497/315x210.jpg?3911", 0, "新堂冬樹のベストセラー小説「僕の行く道」を、「子ぎつねヘレン」の河野圭太監督が映画化。横浜で父と2人で暮らす小学3年生の大志は、パリに留学していると聞かされていた母が実は瀬戸内海の小豆島にいることを知る。大志は愛犬アンを連れ、母からもらった黄色い自転車に乗って小豆島を目指すが……。主人公・大志を「いま、会いにゆきます」の武井証、その両親を鈴木京香と阿部サダヲがそれぞれ好演。", "2009-08-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINDING GRACELAND", "グレイスランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "最愛の妻の死によって悲しみのドン底に突き落とされた医学生バイロン。メンフィスへと車を走らせる彼は、“エルヴィス・プレスリーだ”と名乗る奇妙な中年男を乗せることに。現代のアメリカ南部を舞台に、死後も人々の心に生き続けるプレスリーの伝説をモチーフとした男同士の友情劇が展開。“リメンバー・ザ・キング！ ”なるキメ台詞を連発するミステリアスな“プレスリー”にH・カイテルが扮し、B・フォンダがマリリン・モンローのもの真似を披露して驚かせる。ちなみにグレイスランドとはプレスリーの家が残る場所で、アメリカ中からファンが訪れる聖地として有名。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("36 HOURS", "36時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "アメリカ空軍のパイロット、ビルは36時間の休暇をもらってイギリスに戻ってくる。しばらく連絡が途絶えていた妻のことが気になっていたが、案の定、家は引っ越したあとだった。やっとのことで妻を捜し当てると同時に謎の男に殴り倒され、妻は殺される。ビルは殺人容疑を背負わされながらも、事件の真相を探る。一日半の間に起こる出来事を早いテンポで進め、意外なラストを迎える推理サスペンス。", "1954-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIONHEART", "ライオンハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "肉体派スター、J・C・ヴァン・ダムが原案、脚本まで手掛けたバイオレンス・アクション。瀕死の重傷を負った弟に会うために軍隊を脱走した傭兵リヨン。アメリカに戻った彼が義妹とその娘を守るために非合法のフィスト・ファイトの世界で戦う様をハードに描いていく。華麗なるファイト・シーンが見もの。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOVE OR MONEY？", "恋のマネー・ゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "友人と二人で不動産事務所を作ったクリスだったが、経営不振に陥り、料金滞納で電気も止められそうな状態。それでも大手スポーツ用品会社の社長に気に入られ、週末テニスに誘われる。そこで紹介された娘のジェニファーと意気投合。ところが、クリスの次の大仕事が環境破壊の恐れがあることを知ったジェニファーと衝突。仕事が優先か、恋をとるか迷うクリスだった。ニューヨークの若者たちの恋や仕事をコミカルに描いたトレンディー・ドラマ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Madigan", "刑事マディガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "　ニューヨークの刑事マディガンと相棒ボナーロは敏腕で知られるコンビ。ある日、ふたりはバーニーという前科者を締め上げようとするが、彼は隙をつき、マディガンの拳銃を奪って逃走を図る。警視総監から3日の猶予をもらったマディガンとボナーロはバーニーを追う。一方、バーニーは奪った銃で殺人を犯し……。事件発生の金曜日から3日間に渡る刑事たち、そしてその妻や上司の姿を描くサスペンス。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("家族ゲーム", "家族ゲーム", "", "", "http://ecx.images-amazon.com/images/I/51e6OL%2B1XkL._SL210_.jpg", 106, "松田優作扮する三流大学の7年生という風変わりな家庭教師が、高校受験生を鍛え上げる様をコミカルに描く。音楽なしの誇張された効果音、テーブルに横一列に並び食事をするという演劇的な画面設計など、新しい表現が評判となった森田演出が冴えるホーム・コメディの傑作。", "1983-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦慄迷宮3D", "戦慄迷宮3D", "", "", "http://image.eiga.k-img.com/images/movie/54567/315x210.jpg?2863", 95, "世界最大のお化け屋敷としてギネスブックに登録された富士急ハイランドにあるアトラクション「戦慄迷宮」を、「呪音」シリーズで知られる清水崇が3D映画化したスリラー。柳楽優弥、勝地涼、前田愛らが出演。幼馴染のケン、モトキ、リン、ミユ、ユキの5人はある遊園地のお化け屋敷に遊びに行くが、その際にユキが行方不明となってしまう。それぞれが青年へと成長した10年後のある日、突然4人の前にユキが現れる。", "2009-10-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IDLE　HANDS", "アイドル・ハンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "美形アイドル俳優D・サワ主演のナンセンスなホラー・コメディ。平和な田舎町に暮らすジャンキー少年の右手が、なぜか意思を持って暴走を開始。彼に殺された悪友コンビはゾンビとなって復活し、大混乱の珍騒動が繰り広げられる。R指定を受けた脳天気なバイオレンス描写もご愛敬の怪作。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Taboo", "TABOO", "", "", "http://image.eiga.k-img.com/images/movie/1067/315x210.jpg?2036", 145, "「君は完璧さ」「カーマは気まぐれ」などのヒット曲で知られ、80年代に一世を風靡したカルチャークラブのフロントマン、ボーイ・ジョージの半生を、ジョージ自らがプロデュースし、大ヒットナンバーやオリジナル楽曲を加えて舞台化した「TABOO]。04年トニー賞4部門にノミネートされるなど、高い評価と人気を得た舞台のライブ映像。", "2005-12-24", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ノサップの銃", "ノサップの銃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "宍戸錠主演の和製ウエスタン。オホーツク海に面するノサップ村では、二つの網元が対立している。錠は最初悪玉側に雇われるが、最後は定石通りに善玉側を助ける。馬ゾリ、ガン・プレイ、そして北海道の広大な空間が、西部劇的なムードを盛り上げている。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I MARRIED A MONSTER FROM OUTER SPACE", "宇宙船の襲来", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "滅亡の危機に瀕したアンドロメダ星人は、子孫を絶やさぬよう、地球人の花嫁を手に入れるため、結婚式を明日に控えたビルとすり替わる。だが妻となるマージは彼の様子に不審を抱き……。ホテルで雷が光った瞬間、奇妙な宇宙人の顔が見えるシーンは恐怖感十分。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どん底", "どん底", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "ゴーリキーの同名戯曲を、黒澤明監督と小国英雄が翻案。陽の当たらない、江戸の場末の棟割長屋に暮らす人々の人生模様をユーモラスに描いた辛口辛口群像劇。黒澤としては珍しく短期間・低予算で仕上げた作品といわれる。入念なリハーサルを繰り返し、本番では複数カメラで一気に撮り上げた。物語のほとんどが長屋の中で展開されていながら、極限の臨場感と緊張感を見るものに与える複数カメラ演出が光る。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("れっしゃだいこうしん　ザ☆ムービー　しんかんせん☆とっきゅう大集合", "れっしゃだいこうしん　ザ☆ムービー　しんかんせん☆とっきゅう大集合", "", "", "http://image.eiga.k-img.com/images/movie/57897/315x210.jpg?1481", 45, "さまざまな列車を紹介し、子どもたちや鉄道ファンに人気の「れっしゃだいこうしん　ザ☆ムービー」シリーズ第4作。2011年にデビューした東北新幹線E5系「はやぶさ」や山陽・九州新幹線N700系7000番台、8000番台「みずほ」「さくら」をはじめとした新幹線と、日本全国を走る特急にスポットを当てる。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("切られ与三郎", "切られ与三郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "市川雷蔵の与三郎、淡路恵子のお富で『与話情浮名横櫛』を映画化。有名な源氏店のゆすり場はむしろさらりと描かれ、与三郎にひたむきな恋心を寄せる義理の妹との顛末に伊藤大輔らしい哀感が漂う。宮川一夫の撮影も魅力。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("首都消失", "首都消失", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "巨大な円盤型の雲が突如首都圏を覆い、外部との連絡が途絶えてしまう。内に閉じ込められ、危険にさらされる２千万人の命を救出すべく、TVのレポーターと科学者たちが謎とパニックに立ち向かう。小松左京の原作をSFXを駆使して映画化した、SFパニック大作。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Thunderbirds", "サンダーバード", "", "", "http://image.eiga.k-img.com/images/movie/1707/315x210.jpg?3244", 95, "65年から英国でTV放映された同名人形アニメ・シリーズを「ブリジット・ジョーンズの日記」「アバウト・ア・ボーイ」などヒット作が続く英国の製作会社ワーキング・タイトル・フィルムズが実写映画化。監督は「新・スタートレック」のライカー副長役を演じ、「ファースト・コンタクト／STAR TREK」「スタートレック／叛乱」を監督したジョナサン・フレイクス。国際救助隊の5人兄弟はオリジナルより若く末弟が14歳という設定。", "2004-08-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLESHEATER: REVENGE OF THE LIVING DEAD", "フレッシュ・イーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ハロウィンの日、町はずれで農夫が切り株を引き抜くと、穴には棺があり、中の死体がよみがえって農夫の肉をかみ切る。付近にいた若者たちも襲われ、犠牲者も続々とゾンビ化していき……。ジョージ・A・ロメロの名作「ナイト・オブ・ザ・リビング・デッド」に照明係として参加していたB・ハインズマンによる、残酷血みどろゾンビ・ホラー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("G.I.Joe: Rise of Cobra", "G.I.ジョー", "", "", "http://image.eiga.k-img.com/images/movie/53207/315x210.jpg?1256", 122, "ハズブロ社販売の同名アクション・フィギュアをもとにしたTVアニメを、「トランスフォーマー」の製作陣と「ハムナプトラ／失われた砂漠の都」「ヴァン・ヘルシング」のスティーブン・ソマーズ監督が実写映画化したSFアクション大作。超ハイテク装備の国際機密部隊「G.I.ジョー」と悪のテロ組織「コブラ」の壮絶なバトルが展開する。出演はチャニング・テイタム、シエナ・ミラー、イ・ビョンホン、デニス・クエイドほか。", "2009-08-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風来忍法帖", "風来忍法帖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "山田風太郎の同名小説を渥美清、佐藤允、佐々十郎の主演トリオで映画化したコメディ時代劇。戦塵の箱根山中、悪源太なり平、陣虚兵衛、なんぼベンケイは、人質になっている忍城城主の孫娘・麻也姫に一目惚れし、風魔と伊賀の両忍者部隊と戦う。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMIL　UND　DIE　DETEKTIVE", "エーミールと探偵たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「点子ちゃんとアントン」の原作者エーリヒ・ケストナーによる有名な児童文学を、現代風にアレンジして映像化。協力し合って難事件に挑む、子供たちの友情や生き生きとした姿が魅力的だ。父親の入院によって知人に預けられることになった12歳の少年エーミールは、ベルリンに向かう列車内で全財産を奪われ大弱り。そんな彼に、現地の少女が救いの手を差し伸べる。彼女は仲間たちとともに、エーミールを陥れた犯人を追跡する。", "2001-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2 Filhos de Francisco", "フランシスコの2人の息子", "", "", "http://image.eiga.k-img.com/images/movie/33887/315x210.jpg?1250", 124, "ブラジルの国民的ミュージシャン、ゼゼ・ヂ・カマルゴ＆ルシアーノをモデルに、貧しい家族を救うために歌い続けトップアーティストにまでのぼりつめた兄弟と彼らを支え続けた父との絆を描いた感動作。妻や子どもたちと、貧しいけれど幸せな日々を送っていたフランシスコ。息子たちをミュージシャンにしようと考えた彼は、長男ミロズマルと次男エミバルに楽器を買い与える。やがて2人は、苦しくなる一方の家計を助けようと路上ライブを始める。", "2007-03-17", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うしろの正面だあれ", "うしろの正面だあれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "故・林家三平夫人の海老名香葉子が、子供時代の体験をもとに綴った、同名児童文学の映画化作品。太平洋戦争開戦前から終戦直後までの東京の下町・本所を舞台に、釣竿作りの中根忠吉一家の庶民生活を、長女・かよ子を中心に描いている。８歳のかよ子がやさしい母やワンパクな兄に囲まれて成長してゆき、もうすぐ小学校卒業という時に迎える学童疎開。その間に東京大空襲によって下町は火に包まれ、かよ子は三男の兄以外の、すべての家族を失ってしまう。当時の世相や懐しい遊びを交えての、丹念な暮らしの描写が、素朴なゆえにいっそうクライマックスの悲劇を際立てている。絵と物語のバランスがほどよくとれた、感動のアニメ。2005年には同原作者の「あした天気にな～れ！」もアニメ映画化。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE SEASONS", "季節の中で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "サンダンス映画祭でグランプリ、観客賞、撮影賞の３冠に輝いたヒューマン・ドラマ。過去を清算しようとする元米兵、詩人の屋敷で蓮積みの仕事を始めた少女、娼婦を愛したシクロの中年運転手、物売りのストリート・キッズ。現代のサイゴンで人生の転機を迎えた彼らの人間模様が綴られる。ベトナム生まれで二歳の時にアメリカに移住したT・ブイ監督のデビュー作で、ベトナム戦争以降初のオール現地ロケを行ったアメリカ映画でもある。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おろち", "おろち", "", "", "http://image.eiga.k-img.com/images/movie/53107/315x210.jpg?1909", 107, "楳図かずおの同名原作を「リング0／バースデイ」「予言」の鶴田法男監督が映画化したダークファンタジー。100年に1度永い眠りにつき、不老不死の体で人間の世界を彷徨う謎の美少女・おろちは、29歳を過ぎると醜い姿に朽ち果て死んでいくという悲劇にとり憑かれた門前家へ家政婦として潜りこむ。現在は美しい姉妹がいる門前家だったが、そこにはさらにもうひとつ大きな秘密が……。美しい顔が崩れていく門前姉妹を木村佳乃、中越典子が熱演。おろち役に谷村美月。", "2008-09-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REVERSAL OF FOETUNE", "運命の逆転", "", "", "http://ecx.images-amazon.com/images/I/51TBVHAD3CL._SL210_.jpg", 112, "億万長者の妻を植物人間にしたかどで告訴されながら、あくまで無罪を主張する貴族フォン・ビューロー。1980年のクリスマスにアメリカで実際に起きた事件をもとに、上流社会の背後にひそむ退廃と荒涼とした人間関係を深くえぐり出すサスペンス・ドラマ。主役のJ・アイアンズの冷徹な存在感も必見の一編。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("運転手之恋／THE　CABBIE", "運転手の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "惚れた婦人警官に、あの手この手の交通違反でアタックするタクシー運転手の大奮闘を描いた恋愛映画。「たそがれ清兵衛」の宮沢りえが、それ以前に出演したコミカルな台湾映画だ。恋愛には関心がないと公言していた台北のタクシー運転手アチュアンが、婦人警官ジンウェンに一目惚れ。アチュアンは彼女に会うために、様々な手段を使って交通違反を繰り返し、両親もその恋を応援するのだが……。", "2000-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bad Lieutenant: Port of Call New Orleans", "バッド・ルーテナント", "", "", "http://image.eiga.k-img.com/images/movie/55017/315x210.jpg?3349", 122, "「アギーレ／神の怒り」「フィッツカラルド」などで知られるドイツの鬼才ベルナー・ヘルツォーク監督が、主演にニコラス・ケイジを迎え、問題作「バッド・ルーテナント／刑事とドラッグとキリスト」 （92）をリメイクしたサスペンスドラマ。ハリケーン・カトリーナ直撃後のニューオーリンズを舞台に、麻薬中毒の悪徳刑事テレンスの日常が描かれる。共演にエバ・メンデス、バル・キルマー、マイケル・シャノン。", "2010-02-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にっぽんのお婆あちゃん", "にっぽんのお婆あちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "高齢化社会問題を当時としては画期的に先取りし、コメディ・タッチでありながらも鋭く描いた問題作。北林谷栄やミヤコ蝶々をはじめとして、日本の映画界を背負ってきた女優陣がスクリーン狭しと登場する。浅草を放浪する二人の老婆。一人は家出し、一人は老人ホームを抜け出してきたが……。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOO　MUCH　FLESH", "SEX:EL〈セックス：イーエル〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「グラン・ブルー」で主人公のダイバーを演じたJ・M・バールが製作・監督・主演の３役を務めた意欲作。若くして大地主のライルは、初体験の相手から性器が大きいと町中に広められたことが原因で、妻とも肉体関係を持つことができない。そんな彼の前に奔放なフランス娘が現れる。性の喜びを取り戻していくライルだが、村の人々からは疎外されていく。コンプレックスという呪縛からの開放、閉鎖的な村と偏見から起こる悲劇を、セックスという行為を通して描く問題作。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ACCUSED", "告発の行方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ピンボール・マシーンの上で、３人の男に次々と強姦される娘サラ。世界中を驚愕させた凄絶なレイプ・シーンを、J・フォスターが迫真の演技で見せ、見事1989年アカデミー主演女優賞を勝ち取った話題作だ。ある夜、場末の酒場で、レイプ事件が発生した。被害者はサラという小柄な若い娘だった。この事件を担当した女性検事補キャサリンは、サラの話に基づき、大学生を含む３人の男をレイプ犯として告訴。だが被告側は、あれは和姦行為だったと主張。事件当夜、サラがかなり酒に酔っていて、しかもマリファナもやっていたことが彼女の立場を不利にした。キャサリンは、弁護側との裁定取引に応じようとするが……。６分に１件の割合で発生し、そのうち４件に１件は複数犯によるというアメリカのレイプ事情に肉迫。スキャンダルを超えて、女性の尊厳と、女同士の深い信頼関係をうたった、全世界の女性たちに贈る愛と勇気のメッセージである。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花よりもなほ", "花よりもなほ", "", "", "http://image.eiga.k-img.com/images/movie/1417/315x210.jpg?3506", 127, "元禄15年、父親の仇討ちのために信州松本から江戸に出てきた若い武士、青木宗右衛門は、実は剣の腕がまるで駄目。貧しいながら人情あふれる長屋で半年暮らすうち、仇討ちしない人生があることを知る。仇役に浅野忠信、ほかに香川照之、加瀬亮、田畑智子、夏川結衣、寺島進、原田芳雄、石橋蓮司ら新旧のクセ者役者が結集。撮影、美術、録音は監督の前作「誰も知らない」のスタッフが再結集。美術には「羅生門」の馬場正男が参加。", "2006-06-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幻想のパリ（幻想のParis）", "幻想のパリ（幻想のParis）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "“刑事コジャック”シリーズの吹き替えなどで知られる森山周一郎。彼の芸能生活40周年を記念して企画され、自らメガホンを執った作品。ファッション・デザイナーを目指し、パリに留学していた麗子。日本人商社マンと恋に落ちた彼女は、愛する男のため女を武器にして彼の商談を手伝うのだったが……。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("One day", "ワン・デイ 23年のラブストーリー", "", "", "http://image.eiga.k-img.com/images/movie/57077/315x210.jpg?4190", 107, "アン・ハサウェイ、ジム・スタージェスの共演で、ロンドンとパリを舞台に23年間に及ぶ男女の愛の軌跡を描いたラブストーリー。監督は「17歳の肖像」のロネ・シェルフィグ。しっかり者のエマと自由奔放で恋多きデクスターは、互いにひかれ合いながらも友人でいることを選び、親友として毎年7月15日を一緒に過ごすと決める。旅行や恋愛相談など友人としての交際を続けながらも、エマは秘かにデクスターを思い続けていたが、あるときデクスターから別の女性と結婚することを告げられ……。", "2012-06-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("兵隊やくざ", "兵隊やくざ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "昭和18年、ソ連国境に近い孫呉の丘にそびえ立つ関東軍兵舎。ここに、元ヤクザの用心棒で札つきの暴れ者と噂される大宮貴三郎が入隊してきた。早々に大宮は浴場で大立ち回り、十数人の砲兵隊をブチのめしてしまう。指導係を任されたインテリ上等兵・有田は頭をかかえるが、規律に縛られない奔放な大宮に奇妙な友情を感じていた……。記念すべきシリーズ第１作は、大宮と有田の出会いと、男としてお互いに惹かれ合っていく二人の姿をテンポよく見せる。シリーズもののキャラクターは１作ごとに成長、変化していく場合が多いが、“兵隊やくざ”は本作ですべてを確立、以後最後までまったく変わらない。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEST DEFENSE", "おかしな関係", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "人気スターのE・マーフィとD・ムーアが共演するものの、本編中、二人が一度も顔を合わせないというユニークな構成。新型のミサイル誘導システムをめぐって繰り広げられる抱腹絶倒のサスペンス・コメディ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トリナクリア PORSCHE959", "トリナクリア PORSCHE959", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "メカニックなポルシェのエンジン音に魅せられて失踪した、幻の音楽家を探して旅する男の物語を、ある映画監督が紆余曲折を経て何とか完成させるまでを描いた作品。西ドイツ、オーストリア、イタリアを舞台に疾走するポルシェ959の雄姿が映える。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・各駅停車", "喜劇・各駅停車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ナポレオンを尊敬し、同僚からナポ源と呼ばれるベテラン機関士・源吉は55歳。助士の咲平は助役から源吉に退職を説得してくれと言われる。頑固者同志の源吉と咲平の頑固くらべが始まった。しめっぽいところがかなり見られる人情喜劇。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("解散式", "解散式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "深作欣二の任侠映画第１作。刑期を終えて出所した沢木は、石油コンビナートの利権をめぐる組の争いに巻き込まれ、ついに堪忍袋の緒が切れて敵地に殴り込んでいく。コンビナートを背景にした、着流しヤクザの対決というコントラストがきわだっている。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("友情", "友情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "歌舞伎界の名門である十八代目中村勘九郎（現・勘三郎）と“寅さん”の渥美清という異色の顔合わせで、女の稼ぎとバイトで暮らす苦学生と流れ者の中年労務者の善意あふれる交流を描いた人情ドラマ。山田洋次のもとで修行した宮崎晃監督の、彼への傾倒ぶりを如実に語る一編。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Green Hornet", "グリーン・ホーネット", "", "", "http://image.eiga.k-img.com/images/movie/53837/315x210.jpg?2781", 0, "新聞社の若き社長ブリット・リードが、緑のスーツとマスク姿の正義のヒーロー“グリーン・ホーネット”となって夜な夜な悪に立ち向かう姿を描いた60年代の人気TVドラマを、「エターナル・サンシャイン」の鬼才ミシェル・ゴンドリー監督が映画化。主演を務めるのは、「40歳の童貞男」などのコメディ俳優セス・ローゲン。ドラマ版で無名時代のブルース・リーが演じた助手カトー役に扮するのは、台湾の人気俳優ジェイ・チョウ。", "2011-01-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大統領のクリスマスツリー", "大統領のクリスマスツリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「RAMPO」に続く奥山和由プロデュースの監督第２作。ニューヨークに降り立った25歳の川端燐（きらり）は、これから始まる新しい生活への期待で胸をいっぱいにしていた。ニューヨークには、末期ガンと闘っている姉のキョウコ、ミュージカル女優を目指して留学中の妹の煌（あかり）が暮らしていた。燐は二人との再会を楽しみにしていたが、二人の間には壁ができ、またキョウコとその夫ハルキとの仲も冷えていた。燐はハルキへの想いを隠していたが、キョウコとハルキとの仲を戻したいと願っていた……。ニューヨークを舞台に描かれる、希望を胸に生きる男女のラブ・ストーリー。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jungfrukallan", "処女の泉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "スウェーデンが生んだ世界的巨匠イングマール・ベルイマンが、敬虔なキリスト教徒の娘に降りかかった悲劇と父親の復讐を通して“神の不在”を描いたドラマ。16世紀スウェーデンの田舎町。豪農のひとり娘カリンは、教会へ向かう途中で3人の羊飼いに出会う。貧しそうな3人に食事を施すカリンだったが、彼らはカリンを強姦した上に殺害してしまう。娘の悲劇を知ったカリンの父テーレは、復讐心から3人を惨殺するが……。2012年、デジタルリマスター版でリバイバル公開。", "1960-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談新耳袋　怪奇", "怪談新耳袋　怪奇", "", "", "http://image.eiga.k-img.com/images/movie/55497/315x210.jpg?4591", 115, "実話怪談集「新耳袋」を実写映画化した劇場版第4作。Jホラー界の新鋭・篠崎誠がメガホンをとり、人気アイドル真野恵里菜が映画初主演。「背負う気あんの？」というひとり言をつぶやく不気味な裸足の女を描く「ツキモノ」と、自分の誕生日に妹を失ったことで幻覚を見続ける女子高生めぐみの苦悩を描いた「ノゾミ」の2編で構成される。主演の真野は、両作品で違ったキャラクターを演じている。", "2010-09-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まあだだよ", "まあだだよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "黒澤明監督が敬愛する随筆家・内田百閒と教え子たちの交流を、ほのぼのとしたタッチで描いた監督83才の遺作となった作品。作家活動に専念するために教師を退職する百閒先生。しかし教え子たちは百閒先生の家を訪れるようになる。先生の家を舞台に、先生と教え子を巡る様々なエピソードが人情味豊かに綴られていく。先生の誕生会での「もういいかい(死んだかい)?」「まあだだよ」との『かくれんぼ』の台詞そのままに、叙情詩のような情景を織りまぜた、黒澤監督得意のヒューマンドラマ。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("巌流島", "巌流島", "", "", "http://ecx.images-amazon.com/images/I/51VC29CESPL._SL210_.jpg", 75, "“巌流島の決闘”で佐々木小次郎を倒したのは宮本武蔵ではなかった!?　荒唐無稽な設定で世紀の対決を描く。武蔵は女好きで乱暴者の野蛮な男。彼はしがない漁師に船を出させ、小次郎が待つ舟島へと向かう。圧倒的なイマジネーションと斬新な映像で、常識を軽々と覆す痛快作。武蔵をワイルドに演じる本木雅弘を始め、脇役陣も個性たっぷりの熱演を披露している。", "2003-10-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VERSO　SERA", "黄昏に瞳やさしく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「かぼちゃ大王」の女性監督F・アルキブジが、M・マストロヤンニの主演で撮り上げた感動の人間ドラマ。孤独な晩年を送っていた75歳の大学教授ブルスキが、ある日疎遠だった息子から４歳の娘パペレを預かってほしいと頼まれる。パペレは情緒不安定な父と自由奔放に愛に生きる母の間で傷ついていた。ブルスキは自分がもう一人いると思い込んでいるパペレに戸惑うが、いつしか彼女に深い愛情を感じていく。そこへ、パペレの母ステラが突然現れ、世代の違う３人の奇妙な共同生活が始まった。", "1990-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("借王〈シャッキング〉THE MOVIE 2000〈ミレニアム〉", "借王〈シャッキング〉THE MOVIE 2000〈ミレニアム〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "それぞれの事情で多額の借金を背負った３人の男女が、巧妙な手口で裏金を稼いではその返済にあてる、痛快シリーズ第７作。今回は義兄をだまして自殺未遂にまで追い込んだ、悪名高きシステム金融・向井グループにターゲットを向けた、安斉ら借王たちの活躍を描く。宍戸錠がグループのボス役で貫禄のゲスト出演。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOUCHING THE VOID", "運命を分けたザイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "イギリスの登山家、ジョー・シンプソンが実体験を綴った著書『死のクレバス アンデス氷壁の遭難』を映画化した人間ドラマ。生と死の選択を迫られた登山家の奇跡の生還が、壮絶な雪山ロケによってリアルに描き出される。想像を遥かに超えた真実の物語に、圧倒されること間違いなしの1本だ。前人未踏のシウラ・グランデ峰の登頂に成功したジョーとサイモン。だがその帰路、ジョーが事故で骨折。彼らは窮地に追い込まれる。", "2003-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暁の追跡", "暁の追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "1947～51年まで新東宝に在籍した市川崑は、数々の通俗メロドラマを作っている。この作品はそれらとは違った野心作で、カメラを交番の中に据え、ドキュメンタリー・タッチで警官の活躍を描いたもの。のちの様式美に至る様々な手法を模索していた時代の佳作。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO ESCAPE", "ノー・エスケイプ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "2022年、近未来のスーパー・テクノロジーによって隔離された囚人島に、上官を殺した海軍大佐ジョンが送られてくる。この島は、脱獄不可能な絶海の孤島で、囚人たちは自然と戦いながら生き延びていかなくてはならなかった。この島の中に極悪非道なアウトサイダーと穏便なインサイダーという二つの勢力があることを知ったジョンは、この対立に巻き込まれてしまう。果たして、ジョンはここから脱出することはできるのだろうか。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Doubt", "ダウト　あるカトリック学校で", "", "", "http://image.eiga.k-img.com/images/movie/54327/315x210.jpg?4116", 105, "1964年、ニューヨーク・ブロンクスにあるカトリック系教会学校の校長シスター・アロイシスは、進歩的で生徒にも人気のあるフリン神父が学校で唯一の黒人生徒と不適切な関係を持っているのではないかと強い疑惑を抱く……。トニー賞とピュリッツァー賞をダブル受賞した舞台劇を劇作家ジョン・パトリック・シャンリィが自ら映画化。メリル・ストリープ、フィリップ・シーモア・ホフマン、「魔法にかけられて」のエイミー・アダムスらが厳格な聖職者たちを熱演。", "2009-03-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長外伝・石松と追分三五郎", "次郎長外伝・石松と追分三五郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "森の石松と追分の三五郎にスポットを当てた松竹の時代劇。森の石松には北上弥太郎、追分の三五郎には日活から移籍した名和宏が扮した。黒駒の勝蔵の息のかかった都鳥兄弟によって石松が殺され、次郎長一家は富士見河原で黒駒一家と対決して、見事に勝蔵を斬り殺す。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANOTHER YOU", "サギ師とウソつき患者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "自称プロのおかしなサギ師と、精神病院を退院したばかりのホラ吹き男。この珍コンビが、行方不明になった億万長者の遺産奪取作戦にまんまと成功。二人は金持ちの気分にしばし酔いしれるが、この二人に輪をかけたサギ師集団が登場して……。プライヤー、ワイルダー・コンビが連発するジョークとギャグの数々が見もののスーパー・コメディ。ビデオ発売時タイトルは「ひと嘘ツイたら億万長者!?」。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COCOON: THE RETURN", "コクーン２　遥かなる地球", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "前作で、不死の身体を手に入れるため地球を離れた老人たちが、置き残した“まゆ”を引き揚げようと戻ってきたアンタレス星人とともに里帰り。家族との久々の再会を楽しむ。だがその間に“まゆ”は学者の手に渡ってしまう。老人たちは大活躍し、ついに“まゆ”を奪回するが……。前作の主要キャストがそっくり残ったシリーズ第２作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EXECUTIONER", "華麗なる暗殺", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "東西両陣営の冷戦下におけるチェコを舞台に繰り広げられるアクション映画。イギリスの諜報部員ジョンはチェコでの作戦に失敗してしまう。内部にスパイがいたのではないかと疑った彼は、数々の罠をくぐりぬけ、裏切者を追い詰めていく……。", "1969-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛しのチィパッパ", "愛しのチィパッパ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "妻子ある男性を愛してしまい、父親と口論して家出。やがて男にも捨てられたOL１年生が、これまでの甘い生活を脱却して徐々に自立していく様を描くホーム・ドラマの佳作。“チィパッパ”というあだ名を持つ女主人公に、高見知佳が微笑ましい魅力で挑戦。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("都市霊伝説　幽子", "都市霊伝説　幽子", "", "", "http://image.eiga.k-img.com/images/movie/56907/315x210.jpg?1457", 70, "「童貞放浪記」「nude」の小沼雄一監督が手がけるホラー「都市霊伝説」シリーズ第2作。10年前、プールで幼い少女が溺死体で発見されるという事件が起こり、いまは廃校となった学校で怪奇現象が続発。ある日、卒業生の野島兼ら10人の同級生が学校に集まり、かつての恩師が自殺したことを知る。10年前の事件を苦に死んだ恩師の残した遺書には「鏡の裏」とだけ記されており、野島らは校内の鏡をしらみつぶしに調べていくが……。", "2011-09-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プラトニック・セックス", "プラトニック・セックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "タレントの飯島愛が、家出からAV出演まで、これまで歩んできた人生を包み隠さずに綴り、多くの若者に支持されたベストセラー私小説の映画化。「デボラがライバル」「ダンボールハウスガール」の女性映画で知られる松浦雅子が、その手腕を見事に発揮した過激で切ないラブ・ストーリー。友人たちにレイプされ、家族にも見放されたあおいの自殺を留まらせたのは、トシという青年からの偶然入った間違いメールだった。家に帰ることもできないあおいは、愛という源氏名を付け、ホステス、援助交際などに身を染めていく。トシを真剣に愛し始めた愛は、自分の仕事を隠しながら同棲を始める。しかし、その時すでに５本500万円で契約したアダルトビデオ出演の仕事が始まっていた……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シナモン the movie", "シナモン the movie", "", "", "http://image.eiga.k-img.com/images/movie/53167/315x210.jpg?2004", 45, "ハローキティに次ぐサンリオの人気キャラクター“シナモロール”の劇場版アニメ。ある日、雲の中から1匹の子犬が生まれる。カフェ・シナモンのアンナさんに拾われた子犬は“シナモン”と名付けられ、一緒に暮らすことに。ところが、イタズラ好きな魔法使い・チャウダーが魔法をかけ間違えてアンナさんを消してしまい……。アンナさんの声を石原さとみ、チャウダーの声をお笑い芸人の陣内智則が担当する。監督は「あらしのよるに」の杉井ギサブロー。", "2007-12-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Embrace your shadow", "雨音にきみを想う", "", "", "http://image.eiga.k-img.com/images/movie/33747/315x210.jpg?4734", 102, "ドラマ「アウトサイダー～闘魚～」でブレイクしたディラン・クォが映画初主演を果たしたラブ・ストーリー。裏社会で生きてきた孤独な青年と難病に苦しむ女性との純愛を描く。介護が必要な兄とその娘と共に暮らすウィンイン。彼女自身も兄と同じ病に冒され、辛い毎日を送っていた。そんなある日、彼女の前にチョッカンという青年が現われる。やがて2人は惹かれあうようになり……。人気アイドル歌手のフィオナ・シッがヒロインを熱演。", "2006-11-18", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT’S SHOWTIME", "イッツ・ショータイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "名犬ラッシー、リン・チン・チン、「ターザン」のチータに、「緑園の天使」の名馬パイ、イルカの代表フリッパーと、動物たちの名演、珍演ばかりを寄せ集めた“ザッツ・エンタテインメント”。もちろん、彼らに食われた(!?)役者たちの懐かしの横顔も楽しめる仕掛けだ。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Seraphine", "セラフィーヌの庭", "", "", "http://image.eiga.k-img.com/images/movie/55207/315x210.jpg?3164", 126, "19世紀から20世紀にかけてフランスに実在した女性画家セラフィーヌ・ルイの生涯を描いた伝記映画。2009年のセザール賞で最多7部門を受賞し、フランス本国で大ヒットを記録した。1912年、パリ郊外のサンリスで貧しく孤独なセラフィーヌは、草木との対話や絵を描くことを心のよりどころにひっそりと暮らしていた。そんなある日、セラフィーヌはピカソをいち早く見出したドイツ人画商のウーデと出会い、援助を受けて個展を開くことを夢見るようになる。しかし第1次世界大戦が始まると、ウーデは敵国の人間となってしまう。", "2010-08-07", "2,37,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GLASS MENAGERIE", "ガラスの動物園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "T・ウィリアムズの同名戯曲の最初の映画化作品。暗い家庭を嫌って海へとこぎ出したトム・ウィングフィールドの回想により映画は進む。セントルイスの寂れた一角に住むウィングフィールド一家。内気で、ガラスの動物を飾ることだけが楽しみの妹のため、トムは友人を家へと連れて来る。しかしこれが失敗に終わり、彼は家を出ることを決意する……。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハナ子さん", "ハナ子さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "雑誌『主婦之友』に連載された杉浦幸雄の漫画をマキノ雅広が演出した“国民歌謡”映画。ハナ子さんと五郎さんの新婚生活を中心に、隣組の人たちとの心の交流を描く。戦時下の作品とあって、結末は五郎さんの出征となる。主題歌「お使いは自転車に乗って」も大ヒット。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HUMANOIDS FROM THE DEEP", "モンスター・パニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ロジャー・コーマンの系列に連なる女流監督ピーターズによる“半魚人”もの。鮭の養殖に使った特殊な薬のために生まれたモンスターと、住民たちとの対決を描く。女性海洋生物学者役のターケルが好演。モンスターが襲うのはもっぱら“女”ばかり、というのがユニーク。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("老人と海　ディレクターズ・カット版", "老人と海　ディレクターズ・カット版", "", "", "http://image.eiga.k-img.com/images/movie/55507/315x210.jpg?2466", 99, "1990年に公開された同名ドキュメンタリーを再編集したディレクターズ・カット版。日本最西端に位置する国境の島、与那国島でサバニと呼ばれる小舟を操り、200キロもの巨大カジキを追っていた今は亡き老漁師・糸数繁氏の暮らしと、彼の思想や信条を、村の人間たちの発言やイベントを通して丁寧に映し出す。監督は「チョムスキー9・11」「映画　日本国憲法」のジャン・ユンカーマン。", "2010-07-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Road to Glory", "栄光への道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ジュディスは交通事故で怪我を負った翌日、父を突然亡くしてしまう。その上、事故の後遺症で視力を失うかもしれないと告げられる。彼女は親友のデビッドから求婚されるが、盲目となる我が身を考え、頑なに断る。だがデビッドは盲目でも愛に変わりはないと必死に説得し……。ハワード・ホークスの監督デビュー作。", "1926-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("氷点", "氷点", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "幼い娘を殺された水口は、そうなったのは妻が浮気をしていたからだと信じ、妻への復讐として犯人の子を養女にした。しかし妻はそのことを知らない。養女は美しく成長する。水口の家庭で繰り広げられる、複雑で異常な愛憎の絡み合いを、山本薩夫は面白く見ごたえのあるものにした。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NUIT DOCILE", "夜のアトリエ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アンドレ・テシネ、ベルトラン・タヴェルニエらと同世代のフランスの映像作家G・ジル。本編は彼の日本での初紹介となった作品で、理知的かつイメージを先行させた独特の映像世界が楽しめる。絵描きのジャンと看護婦のステラ。かつて愛し合っていた二人だが、ジャンの愛は絵の方へと向いていき……。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WATUSI", "ソロモン王の宝庫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "H・R・ハガードの冒険小説の映画化で、1950年に製作されたMGM映画の名作「キング・ソロモン」の続編にあたる。20世紀初頭、ソロモン王の幻の財宝を求め、探検家ハリーはアフリカ大陸へ渡った。凶悪な原住民や猛獣の襲撃をかわし、財宝を手に入れようとするが……。ビデオ発売時タイトルは「キングソロモン 魔境の伝説」。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Natural Born Killers", "ナチュラル・ボーン・キラーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "史上最悪の大量殺人鬼としてアメリカ犯罪史にその名を刻むことになったミッキー＆マロリーの凶行と、彼らを取り巻くメディア社会の狂気を描いた問題作。マロリーとその恋人のミッキーは、マロリーに性的虐待を加えた父と、それを見て見ぬふりをした母を殺し、ルート666をひた走る。ゲームのように殺人を重ねる彼らは、いつしかマスコミによってヒーローに仕立てられていた。二人を捕らえ自分の名を売りたい刑事、独占インタビューをもくろむTVキャスターたちが彼らを追う。Q・タランティーノによる原案をO・ストーンが脚色・監督。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALFIE", "アルフィー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "60年代の同名映画を基に、ハンサムなプレイボーイの恋愛遍歴をユーモラスに描いたラブ・ストーリー。ジュード・ロウが魅惑の主人公アルフィーに扮し、軽やかな存在感を発揮しつつ好演を見せる。アルフィーが語るシニカルだが的を突いた(？)遊び人哲学にも注目。N.Y.で奔放な恋愛遍歴を重ねる青年アルフィー。理想の女性と出会うも手痛い別れを経験する彼に、追い討ちをかけるような事件が……。", "2005-07-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOMMERSBY", "ジャック・サマースビー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "南北戦争で死んだと思われていた男サマースビーが６年ぶりに村に帰って来た。以前は粗野で冷酷だった彼は、優しく品格ある人間に変わっていた。初めて夫に愛を感じた妻ローレルは、この男が本当の夫ではないと疑うようになる……。R・ギア、J・フォスターの二大スター競演による謎めき、やがて涙を誘う愛の物語。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("らくだ銀座", "らくだ銀座", "", "", "http://ecx.images-amazon.com/images/I/61WVWXKX11L._SL210_.jpg", 109, "日本のとある商店街を舞台に、個性ゆたかな住人たちが繰りひろげる珍騒動を描いた人情ドラマ。八百屋の後継ぎ修行中の青年・光は、夜の商店街をらくだが歩く夢を見たことから、商売の神様“らくだ”を呼ぶ祭りの幹事を任されてしまう。時を同じくして、4年前にアメリカへと旅立った光の兄貴分・卓馬が帰国。久々の再会を果たす光と卓馬だったが、商店街で大事件が発生し……。主人公・光役に、「八月の狂騒曲」の伊崎充則。", "2005-02-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fah Talai Jone", "快盗ブラック・タイガー", "", "", "http://image.eiga.k-img.com/images/movie/1297/315x210.jpg?3720", 114, "額に傷を持ち銃とハーモニカを愛する名うての快盗ブラック・タイガー。かつて父を葬った盗賊たちに復讐を誓うため、盗賊の一味に加わった彼だったが、かつて愛した女性ラムプイの婚約者を救ったために、組織から裏切り者として追われてしまう。そしてラムプイの結婚式の当日、政府軍と盗賊たちが待ち受ける中、ブラック・タイガーはやってきたが……。監督はCM界の出身、製作はTV局やサッカー・チームを持つタイのエンターテインメント・グループBEC-TEROの映画部門として欧米でも注目のFILMBANGKOK。", "2002-07-27", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ここにいる", "ここにいる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "元精神病院だった廃屋を舞台に、若い男女たちが狂気に取り込まれていく様を描いたホラー。低予算作品ながらも、観客を確実に恐怖の世界へと誘ってくれる秀作だ。俳優である友人のプロモ写真を撮影しようと、人里離れた山荘にやってきた隼人たち４人。精神不安定な隼人は取り憑かれたように廃墟の写真を撮り始め、いないはずの人物の姿を写してしまう。", "2006-06-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏の祈り", "夏の祈り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DELLAMORTE DELLAMORE", "デモンズ'95", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "19世紀末に活躍した象徴派の画家アーノルト・ベックリンの『死の島』をモチーフに、ヨーロッパ世紀末のオカルト的世界観を見事にビジュアライズしたホラー。フランチェスコは、北イタリアの小さな村の墓地管理人。墓に眠っていた死人が、突然、不可思議な疫病により生き返ったため、毎夜ゾンビとの戦いに明け暮れることになる。「デモンズ」シリーズとは別物。", "1994-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MR.BILLION", "Mr.ビリオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ふとしたことから叔父の莫大な遺産を相続することになった唯一の血縁者グイドは、20日以内にサンフランシスコに来てサインをするよう通達される。一方、遺産を狙う会社重役はグイドにニセ書類にサインさせようと美人探偵を使って妨害に出るが……。T・ヒルがとぼけておかしいアクション・コメディ。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUATORZE JUILLET", "巴里祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ルネ・クレール得意のパリの下町を舞台にした人情喜劇。７月14日の革命記念日（日本で巴里祭と呼ぶようになったのはこの作品から）の一日と、可憐な花売り娘アンナとタクシーの運転手ジャンの恋のいざこざを、情感とユーモアを込めて綴った一編。", "1933-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GARDEN OF EVIL", "悪の花園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "19世紀後半のゴールドラッシュのメキシコ。“悪の花園”と呼ばれるインディアンの聖地の中の金坑を目指す３人の男がいた。金坑内に閉じ込められた夫を助けたいという美女を加えた一行は、危険きわまりない土地に足を踏み入れる……。正義漢G・クーパーとクセ者R・ウィドマークの個性が激突する痛快作。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Skyline", "スカイライン　征服", "", "", "http://image.eiga.k-img.com/images/movie/55857/315x210.jpg?4140", 94, "「タイタニック」「ターミネーター3」ほか多数のSF作品においてビジュアル・エフェクトを手がけ、「AVP2 エイリアンズ VS. プレデター」で長編監督デビューを果たしたコリン＆グレッグのストラウス兄弟の長編第2作。ある夜、ロサンゼルス上空に突如として巨大宇宙船が現れ、人間たちを吸い上げていく。人類は謎の異星人たちに立ち向かうが……。", "2011-06-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Karate Kid", "ベスト・キッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "　高校生ダニエルは好戦的なコブラ会の道場で空手を学ぶジョニーたちから目をつけられ、嫌がらせを受けるハメに。そんなダニエルは日系2世のミヤギと知りあい、彼のもとで働くうち、空手の基礎を身につけていく。やがてダニエルは学生空手大会でジョニーと激突することに……。少年の成長、そして彼と師匠との交流を爽やかに描いた大ヒットシリーズ第1弾。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒカリサス海、ボクノ船", "ヒカリサス海、ボクノ船", "", "", "http://image.eiga.k-img.com/images/movie/53997/315x210.jpg?3179", 0, "松方弘樹と仁科亜季子の実娘・仁科仁美が主演する、人間の切ない葛藤を描いた物語。監督・脚本は「トニー滝谷」「亀は意外と速く泳ぐ」のプロデュースを務めてきた橋本直樹。共演に深水元基、松本まりから。7年前に亡くなった弟のことが忘れられない奈々子は、思い悩む日々を過ごす大学生。そんなある日、彼女の前に見覚えあるピエロが現れ……。", "2008-09-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PATTES BLANCHES", "白い足", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "農園主のケリアデックは大の人間嫌い。そんな彼の財産を狙って、酒場の女オデットはケリアデックを誘惑する。やがてオデットはケリアデックの義弟と組んで彼を毒殺しようとたくらむが……。近年再評価されつつある隠れた巨匠J・グレミヨンの、バロック風とも呼ばれる映像が美しいヒューマニズムあふれる傑作。", "1949-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("РОМЕО И ЖУЛЪЕТТА", "ロメオとジュリエット物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ソ連国立ボリショイ劇場での、バレエ『ロミオとジュリエット』の公演を全幕フィルムに収めた、正統派バレエ映画。同劇場所属のバレエ団及び管弦楽団が総出演している。カンヌ映画祭での受賞にふさわしく、幽玄で優雅な舞台が展開する、バレエ・ファン必見の一本。ビデオ発売時タイトルは「ロミオとジュリエット」。", "1954-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES PE´TROLEUSES", "華麗なる対決", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "仏・伊を代表するセックス・シンボル、B・バルドーとC・カルディナーレがお色気タップリで暴れまわるアクション・コメディ。バルドー率いる女ばかりのフレンチ・ギャングと女傑カルディナーレとその弟たちの一家が、石油の眠る牧場の権利をめぐって大乱闘を繰り広げる。", "1971-01-01", "2,4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STRIP SEARCH", "売春捜査官", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "色と欲の罠を妖しい雰囲気の中に描き出した、エロティックなサスペンス・アクション。敏腕刑事ロビーは謎めいた美女から、義理の娘ビリーを探し出す仕事を請け負った。家出したビリーはストリップや売春に手を出しており、行方不明になっている。危ない橋を渡りながらも、ロビーは何とかビリーを見つけ出すが……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔法のプリンセス　ミンキーモモ　夢の中の輪舞", "魔法のプリンセス　ミンキーモモ　夢の中の輪舞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お墓に泊まろう！", "お墓に泊まろう！", "", "", "http://image.eiga.k-img.com/images/movie/55587/315x210.jpg?2920", 75, "2011年、経営難に陥ったテレビ東京は葬儀屋に買収され、多くの社員が葬儀の手伝いばかりさせられる日々を過ごしていた。そして、入社したばかりのAD（アシスタントディレクター）今井は、バラエティの雄・伊藤プロデューサーが葬儀局に異動を命じられたことに絶望。そんななか、元社長の島田が「世界一くだらない葬儀にしてくれ」という遺書を残して他界する。残された局員は、島田の葬儀を実況中継する葬式バラエティ番組を作ることにするが……。「私の優しくない先輩」で主演を務めたお笑いコンビ「はんにゃ」の金田哲が再び主演に抜擢され、大鶴義丹、松方弘樹らベテラン勢が脇を固める。", "2010-08-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Little Fockers", "ミート・ザ・ペアレンツ3", "", "", "http://ecx.images-amazon.com/images/I/5107En6NTSL._SL210_.jpg", 98, "ロバート・デ・ニーロ＆ベン・スティラー主演の大ヒットコメディ「ミート・ザ・ペアレンツ」シリーズの第3弾。愛妻パムとの間に2人の子供に恵まれ幸せな毎日を送っているグレッグ・フォッカー（スティラー）は、ある日仕事で出会った元看護婦の女性アンディに、ある男性用精力剤のスポークスマンになって欲しいと打診される。そんな折、家でひとりくつろいでいた義父ジャック（デ・ニーロ）が軽い心臓発作を起こし……。監督は前2作を手がけたジェイ・ローチから「ダレン・シャン」のポール・ワイツへバトンタッチ。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪魔の刑事（でか）まつり", "悪魔の刑事（でか）まつり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "人気オムニバスシリーズ「刑事（でか）まつり」の第5弾。新たにホラーの要素が追加された本作では、「恋する幼虫」の井口昇、「呪霊　THE MOVIE」の村上賢司、「発狂する唇」の佐々木浩久、「あんにょんキムチ」の松江哲明ら、各方面で注目を集める気鋭の監督たちが集結。「主人公は刑事」「上映時間は15分以内」「本編中に最低5回はギャグを入れる」という制約のもと、個性あふれる6本の短編作品がそろった。", "2004-09-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TUXEDO", "タキシード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ジャッキー・チェンがスピルバーグ主宰の、ドリームワークスと組んだコメディ活劇。ヒロイン、ジェニファー・ラヴ・ヒューイットとの息の合ったコミカルな掛け合いも見ものだ。格闘もダンスも、着ただけでこなせるようになるハイテク・タキシード。ひょんなことからこのタキシードを身に付けることになった気弱なお抱え運転手ジミーは、諜報員であるご主人様になり代わり、大企業の巨大な陰謀に立ち向かう。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HITTING HOME", "アクシデント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "突然車の前に飛び出して来た少年を轢き殺してしまった男と、事故の一部始終を目撃してしまったその母親。事故はまったくの偶然だった。だが、子供の命を奪われた母親の絶望と怒りは、法律の壁に阻まれて行き場をなくし、やがて犯人への復讐へと姿を変えていく。正義と権力をテーマに、社会的弱者の立場から法の欺瞞を告発した社会派サスペンス。", "1988-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Croods", "ザ・クルッズ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Das Leben Der Anderen", "善き人のためのソナタ", "", "", "http://image.eiga.k-img.com/images/movie/1757/315x210.jpg?2710", 138, "秘密警察による反体制派への監視が行われていた冷戦時代の旧・東ドイツ。秘密警察局員のビースラーは、ある日“反体制派”と目される劇作家ドライマンを監視するように命じられる。ドライマンの家に盗聴器を仕掛けたビースラーだったが、彼の部屋から聞こえてきたピアノ曲“善き人のためのソナタ”に心を奪われてしまう……。監督は、本作でデビューを飾った33歳のフロリアン・ヘンケル・フォン・ドナースマルク。第79回アカデミー賞では外国語映画賞にノミネート。", "2007-02-10", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続番頭はんと丁稚どん", "続番頭はんと丁稚どん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "大阪の薬問屋“七ふく堂”の丁稚の崑松、小松、平松、と小番頭の雁七らが巻き起こす騒動をコミカルに描いた花登筐のTVドラマの映画化。今回は崑松が瓜二つのサーカス団団長の息子と間違われてサーカス団に入れられ、団長に可愛がられる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Toy Story 2 in 3D", "トイ・ストーリー2　3D版", "", "", "http://image.eiga.k-img.com/images/movie/55047/315x210.jpg?4714", 0, "「トイ・ストーリー2」（99）のデジタル3D版。木製カウボーイ人形のウッディとスペースレンジャーのバズは、いつものようにアンディの部屋で仲間と楽しい時間を過ごしていた。そんなある日、バザーの売りに出されたペンギンのおもちゃ・ウィージーを助けに行ったウッディが、何者かにさらわれてしまう。バズたちはウッディの救出作戦を計画するが、ウッディは自分がプレミア人形だと知り戸惑いを隠せずにいた。", "2010-02-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ERIK THE VIKING", "エリック・ザ・バイキング バルハラへの航海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "イギリスの喜劇集団、“モンティ・パイソン”の一人、T・ジョーンズが監督したアドベンチャー・コメディ。巨大な狼が太陽をのみ込んだため、神々が眠り続けている北欧の村。太陽を取り戻す旅へと向かった青年と、その一行との珍道中を描く。関根勤が、奴隷船の番人に扮して怪演している。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEMONWARP", "エイリアン・ゾンビ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "緑萌ゆる森林に囲まれ、娘とともに平和に暮らしていたビルは、ある日突然、不幸のどん底に叩き落とされる。伝説の巨猿“ビッグフット”のような毛むくじゃらの化け物が現れ、娘を連れ去ったのだ。この森を調査しに訪れた若者５人とともに娘を探すビル。しかしモンスターの怪力にかなうはずもなく、彼は殺されてしまう。そんなビルの遺志を継いだ若者ジャックは、森の奥で多数のゾンビと遭遇し……。名バイプレイヤーとして日本にも多くのファンを持つG・ケネディ主演のSFホラー映画。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SISTER ACT2: BACK IN THE HABIT", "天使にラブ・ソングを2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "歌う修道女の陽気な活躍を描いたヒット・コメディ第２作。ショービズ界に戻ったデロリスがシスターたちの要請で聖職に復帰。ボランティア先であるスラムの高校で悪ガキ相手に苦戦しながらも、歌う喜びを教えていく。W・ゴールドバーグは今回も『ダンシング・イン・ザ・ストリート』等のヒット曲を熱唱。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("縁 enishi", "縁 enishi", "", "", "http://image.eiga.k-img.com/images/movie/56357/315x210.jpg?2930", 83, "年間10万人を超える行方不明者の中から、7年間行方が知れなかった者は死亡認定がなされ、記録上はこの世に存在しないこととなる。裏の世界で生き続ける彼らは「死人（シビト）」と呼ばれ、犯罪に手を染める者から、国家を守るために影で戦う者までさまざまだった。バーを営むハヤトとケイジも死人として戦いを続けていたが、ある日、ナゾの女性アカネと知り合ったことから、ある惨劇に巻き込まれていく。谷山紀章、浪川大輔ら人気声優やロックバンド「AYABIE」のメンバーが出演するアクション。", "2011-05-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE HUNDRED MEN AND A GIRL", "オーケストラの少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "当時、名声を欲しいままにしていた天才少女歌手D・ダービン主演の音楽映画。クラシック界から大物指揮者ストコフスキーとフィラデルフィア交響楽団が特別出演したことも大きな話題となった。失業した楽士を父に持つ娘パッシーは、彼をもう一度ちゃんとしたオーケストラで演奏させたいと思っていた。少女は、大胆にも世界的指揮者ストコフスキーに近づき、願いを聞き入れてもらおうと決意。そしてついに彼の指揮で父親とその仲間たちのオーケストラ演奏が実現する。ラスト、喜びの涙で頬を濡らしながら歌う、けなげな少女の姿が心にしみる。観る者をハッピーな気分にしてくれる、善意にあふれた作品。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARD TO BE A GOD", "惑星アルカナル 宇宙からの使者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "近未来の地球では、地球外の惑星に探査船を送り込み、その星の情報を得るとともに、彼らの歴史の進行を妨げることなく社会の発展を手助けしていた。情報員アントンとバーベルも何千光年も離れた惑星に派遣される。だが彼らの着いた星のアルカナル王国は、かつての中世の地球のような血みどろの戦いと悪政がはびこっていた。民衆を救うため、二人は地球の高度な科学力を駆使してクーデターを起こし、神として君臨していく。『ストーカー』で知られる旧ソ連のSF作家、ストルガツキー兄弟の原作を、J・C・カリエールが脚色している。", "1990-01-01", "2,7,12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КУРЬЕР／MESSENGER　BOY", "メッセンジャー・ボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1980年代のソ連映画を代表する若手監督シャフナザーロフが、自ら執筆した同名小説を映画化。無責任で無目的な、ソ連版“新人類”ともいえる17歳の少年イワンの心情を、キメ細かく軽妙に綴った青春コメディ。大学受験に失敗したイワンは、母の勧めで某雑誌社のお使い係＝メッセンジャー・ボーイとして働くことになるが……。モスクワ映画祭特別賞を受賞。", "1986-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おろしや国酔夢譚", "おろしや国酔夢譚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "日本が鎖国をしていた江戸時代、嵐のためロシアに漂着した大黒屋光太夫ら一行。国に帰る手段を探るため、言葉を覚え生活になじんでいく彼らの行く手には想像を絶する冒険が待っていた……。井上靖の原作をもとにマリナ・ブラディなど国際キャストを得て壮大なスケールで描く大河歴史ドラマ。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Johｎ　aｎd　Jaｎe　Doe", "Johｎ　aｎd　Jaｎe　Doe", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "役者業の傍ら、監督としてもその手腕を発揮する坂上忍の「The Junk Food Generation」に続く第３作。一見、何のつながりもないカップル３組の不思議と重なり合う愛の形が描かれる。坂上監督が日本、韓国、サイパンの３ヵ国に及ぶロケを敢行して撮り上げた意欲作だ。近所の誰もがおしどり夫婦と認めるほど、仲の良い中島夫妻。だが妻の友梨子は夫に嘘をつき、毎週末になると年下の男と密会を繰り返していて……。", "2004-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャコ萬と鉄", "ジャコ萬と鉄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "谷口千吉監督の名作「ジャコ万と鉄」（1949）を同じ脚本で深作欣二監督がリメイク。北海道のニシン漁期、片目の無法者ジャコ萬と網元の息子で、弱い漁夫たちを助けようとする鉄との対決を、深作監督はダイナミックに描いている。高倉が片想いの女性に尽くす叙情的な場面も秀逸。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANNA OZ", "アンナ・オズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "夜毎ベニスにいる自分の夢を見る、パリの女性アンナ。夢の中の彼女は、盗品を売買する犯罪者と奇妙な同居生活を送っていた。やがて夢の中のアンナは、現実の彼女を謎めいた事件に巻き込み……。夢の中に存在する、もう一人の自分にほんろうされる女性の体験を描いたサイコ・ミステリー。「愛さずにいられない」のE・ロシャンが演出を手掛け、恐怖以上にロマンを押し出したドラマに仕立てている。", "1996-01-01", "2,26,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("葯", "薬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "魯迅生誕百周年を記念して、その短編小説『薬』を映画化したもの。老栓夫婦の店では朝から常連たちが集まって革命党談議に夢中。夫婦は商売も順調だったが、一人息子の肺病が心配で、役人に賄賂を渡し、万病に効くという人血マントウをくれるよう頼む。その頃革命党員が処刑され、一人息子はその血のしたたるマントウを口にするのだが……。", "1981-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUARDING TESS", "不機嫌な赤いバラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "重要人物を警護するシークレット・サービスのダクは、その仕事に誇りを持っていたが、この３年間の任務は元ファーストレディのテスの警護という張り合いのないもの。過剰な警備のためプライバシーのない生活を強いられているテスは、わがままのし放題。特にタグに対しては使い走り同然の扱いだが、内心では彼に目をかけており、タグも彼女の孤独感を察するようになる。ある日、テスがタグ一人をお供にピクニックへ出かけるが、突然、タグを残して車が発進してしまう……。威厳と傲慢の中に可愛らしさがのぞくテス役のS・マクレーンと、要人警護に強い使命感を持つダクを演ずる演技派N・ケイジが好演。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PLEASURE OF HIS COMPANY", "結婚泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "すでに３回の結婚・離婚歴を持つポゴ・プール。最初の妻との間にできた娘の結婚式のため、20年ぶりにアメリカに帰ってきたポゴだったが、彼と最初の妻、そして娘の思惑が絡んで大騒動に……。アステアのダンス・シーンもたっぷり楽しめるミュージカル・ラブ・コメディ。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新喋血讐雄／THUNDER　COP", "男たちの挽歌 烈火之章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "正義感あふれる新人警官ルンと非合法組織のボス、スーアン。偶然出会った二人が、スーアンの恋人をめぐる葛藤、組織間の抗争に巻き込まれながらついに運命の日を迎える……。二人の友情と闘いを、激しいアクションの中に描いたバイオレンス。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　STORY　OF　LOUIS　PASTEUR", "科学者の道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "炭疽病や狂犬病の治療法を発見し、ジョン・ドヌール勲章を受けたフランスの科学者ルイ・パスツールの伝記ドラマ。炭疽病の病原菌を発見したパスツールは、その後狂犬病の研究に着手。だが、ナポレオン３世の侍医頭シャルボンネは、かねてから彼に疑惑の目を向けていた。シャルボンネの度重なる妨害を受けながらも、パスツールは助手や娘婿の協力を得て、ついに培養液を完成させる。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TORRENT", "イバニエスの激流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "幼友だちとの愛を育みながらも、息子を溺愛する母親のために引き裂かれていくレオノーラとラファエル。やがて失意から故郷の村を去ったレオノーラは、パリで歌姫となるが……。V・B・イバニェスの原作を、G・ガルボの主演で映画化。彼女のアメリカでの最初の作品であり、その大理石のごとき美しさはいたるところに見いだされる。", "1926-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くノ一忍法帖　影ノ月", "くノ一忍法帖　影ノ月", "", "", "http://image.eiga.k-img.com/images/movie/56157/315x210.jpg?1547", 84, "1990年代にビデオシリーズ、劇場版あわせて8本が製作された女忍者（くノ一）たちの活躍を描くセクシー時代劇を、原作者・山田風太郎の没後10年を記念して復活させた。派手好みの尾張藩主・徳川宗春は、質素倹約を掲げる徳川8代将軍・吉宗と対立。吉宗のスキャンダルを暴こうと甲賀の忍者たちを送り込む。一方の吉宗は、スキャンダル発覚の前にかつての愛妾（あいしょう）たちの抹殺をくノ一に命じる。主演は元グラビアアイドルの阿部真里、人気グラビアアイドルの森下悠里ら。", "2011-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lone Ranger", "ローン・レンジャー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕たちは世界を変えることができない。 But, We wanna build a school in Cambodia.", "僕たちは世界を変えることができない。 But, We wanna build a school in Cambodia.", "", "", "http://image.eiga.k-img.com/images/movie/55817/315x210.jpg?2378", 126, "ありきたりな毎日に物足りないものを感じていた医大生のコータは、ふとしたことから手にした海外支援案内のパンフレットに触発され、カンボジアに学校を建てることを決意。人材や資金集めに奔走する。しかし、現地視察で訪れたカンボジアでは厳しい現実を目の当たりにし、帰国後もさらなる困難が待ち受けていた……。2008年に自費出版された現役大学生・葉田甲太の体験記を原作に、NHKドラマ「ゲゲゲの女房」でブレイクした向井理が映画初主演を飾る。", "2011-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ちょうちん", "ちょうちん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ロールスロイスを乗り回し、３人の若衆を率いてはいるものの、組の幹部たちからは小突かれ、町ではカツアゲなどチンケなことをやっている“中堅ヤクザ”村田千秋。彼が、クラブのホステス・新子と恋に落ち、彼女の連れ子のために足を洗おうと努力していた矢先に、ガンに倒れて無念の死を遂げるまでを描いた異色のヤクザ映画である。やはりガンに倒れた金子正次の原作を忠実に映画化、病と闘いながらも図太く生きる千秋役を、陣内孝則が好演している。妹の誕生日に大きなバースデー・ケーキを買って帰ったり、連れ子の父親参観日に顔を出したりと、家庭的なパパの日常を映し出す演出が、ヤクザ映画だけにかえって印象的。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALICE　ET　MARTIN", "溺れゆく女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "フランスの名匠A・テシネが「ランデヴー」以来12年ぶりにJ・ビノシュ主演で撮り上げた狂おしい愛の物語。ある日突然、家を飛び出した青年マルタンが、年上の女性アリスに恋をする。マルタンの情熱に押され、彼の愛を受け入れたアリス。だが、二人の幸せの日々はアリスの妊娠によって崩れていく。情緒不安定になっていくマルタンに心を痛めるアリスは、やがて、彼とその家族に隠された10年前の秘密を告白される。ある家族が悲劇と向き合うまでのサスペンスフルなドラマを軸に、深い絆で結ばれた恋人たちの無償の愛を描き出す。J・ビノシュが芯の強い情熱的なヒロインを熱演。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Does cuckoo sing a song at night?", "カッコーの啼く夜 別離", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "韓国のアカデミー賞といわれる大鐘賞で９部門を制覇した作品。清らかな森林の中で、自然とともに素朴な夫婦生活を営む若い男女。だが、妻が物欲に目覚めた時から、そんな二人の幸福な生活にも崩壊の兆しが……。韓国映画界の鬼才、チョン・ジンウが独自の鋭い視点で、人間の愛と欲望を描いた問題作。", "1980-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴー！ゴー！若大将", "ゴー！ゴー！若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "京南大学陸上部のホープ・田沼雄一は、ガソリンスタンドに勤める澄子に心を奪われる。しかし祇園の芸者・京奴が雄一にベタベタし澄子は面白くない。やがて雄一は大学対抗駅伝に出場し、ライバル西北大に大差をつけられるが、澄子の声援で元気百倍。見事ゴールのテープを切る。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拳銃無頼帖 電光石火の男", "拳銃無頼帖 電光石火の男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ハイライト興業の元幹部・丈二が、３年ぶりにシャバに戻ってくる。足を洗うつもりだったが、町に残した恋人・圭子を忘れられない。ボスの麻島は丈二を消そうと流れ者の殺し屋・五郎を差し向ける。吉永小百合が喫茶店のウェイトレス役で本格的な日活デビューを飾った。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙戦艦ヤマト", "宇宙戦艦ヤマト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "西暦2199年、ガミラス軍の放つ遊星爆弾により、地球は滅亡の危機にさらされていた。そこへイスカンダル星から、地球の放射能を消す除去装置があるとの知らせを受け、地球は戦艦大和を改造した、宇宙戦艦ヤマトを発進させる。波動エンジンを持つヤマトはワープを重ねてイスカンダル星へ向かい、襲い来るガミラスの宇宙艦隊を、絶大な威力の波動砲で打ち破る。だが、数々の激戦を経て到着したイスカンダル星は、敵の星・ガミラスの二連星だった……。1974年に放映されたTVシリーズのダイジェスト版ながら、大ヒットを経てアニメ・ブームの基礎を作った記念碑的作品。それまでのアニメにはなかったスペクタクルSF感覚、そして斬新なメカ・デザインや、主人公の古代進と森雪のスマートな魅力、独特の悲壮感などが当時の若者たちに大受けした。そのビジュアル・イメージは、美術とデザイン設定を担当した、マンガ家・松本零士の力によるところが大きい。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("“認罪”　中国　撫順戦犯管理所の6年", "“認罪”　中国　撫順戦犯管理所の6年", "", "", "http://image.eiga.k-img.com/images/movie/55227/315x210.jpg?3568", 109, "1950年7月、中国の撫順市に元日本兵969人が戦犯容疑者として収容され、彼らは戦時中に犯した罪を自ら認めることを求められる。自らの行為に向き合う元日本兵や、恨みを抑えて寛大に接しようとする中国人職員をカメラが捉え、戦後史の真実に迫るドキュメンタリー。NHKで放送されたドキュメンタリー番組を劇場で公開するプロジェクト「This is REAL／NHK-BSドキュメンタリー・コレクション」にて上映。", "2010-02-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUN IF YOU CAN", "殺人プレイバック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "美女ばかりを狙う殺人鬼“レディ・キラー”は、殺人の一部始終をいつもビデオに撮っていた。女子大生キムの見ていたテレビに偶然その映像が流れ、キムは殺人の目撃者となってしまう。それを知った犯人の魔手がキムに迫る。女流監督によるB級スリラー。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DALLE ARDENNE ALL’INFERNO／DIRTY　HEROES", "アルデンヌの戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "第二次大戦下のアルデンヌの激戦を描いた戦争ドラマ。アムステルダム郊外にあるナチの捕虜収容所から、３人のアメリカ兵が脱走した。親衛隊の執拗な追跡をかわすが、一人は射殺されてしまう。そして偶然に出会ったパルチザンの隊長に助けられた二人は、彼らと行動をともにすることにしたが……。", "1968-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAN’T BUY ME LOVE", "キャント・バイ・ミー・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ビートルズの1964年の大ヒット曲をタイトルにした青春ラブ・コメディ。ハイスクールでサエない“ボケ”グループの一人だったロナルドが、憧れのチア・リーダー、シンディとお金で契約。一ヵ月の期限付きで恋人同士を装ってもらい、人気者になろうとするのだが……。主演二人の演技もなかなかで、さわやかな印象を残すキュートな作品となっている。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bellflower", "ベルフラワー", "", "", "http://image.eiga.k-img.com/images/movie/57757/315x210.jpg?1682", 105, "世界滅亡を夢見る若者たちの友情、愛と憎しみを幻覚的なビジュアルとサウンドを駆使して描く異色の青春映画。働く気もなく恋人もいないウッドローとエイデンは、映画「マッドマックス2」に登場する悪の首領ヒューマンガスをヒーローと崇め、映画に描かれた滅亡後の世界を夢見て、ひたすら戦闘用改造車のチューンアップと搭載する火炎放射器づくりに明け暮れていた。やがてウッドローには恋人ができるが、その恋人に裏切られると、怒りと絶望から狂気へと駆り立てられていく。", "2012-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bordertown", "ボーダータウン　報道されない殺人者", "", "", "http://image.eiga.k-img.com/images/movie/53787/315x210.jpg?2518", 112, "メキシコで起きている実際の連続女性殺害事件をもとに、国家の闇を鋭く批判するサスペンス。主演はプロデューサーも兼任するジェニファー・ロペス。共演に「レジェンド・オブ・ゾロ」のアントニオ・バンデラス、「ディパーテッド」のマーティン・シーンら。メキシコとアメリカの国境の町フアレスで起こっている連続女性殺人事件を追う敏腕記者ローレンは、現地で新聞社を経営する元同僚ディアスを訪ねるが、そこでメキシコの汚職にまみれた情報操作の実態に直面する。", "2008-10-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sound of Music", "サウンド・オブ・ミュージック", "", "", "http://ecx.images-amazon.com/images/I/51mNO-UOEQL._SL210_.jpg", 172, "修道院を抜け出て、草原で高らかにテーマ曲を歌うマリア。映画史に残る、この大ヒットミュージカル映画は、そんな心躍るようなシーンで始まる。1938年オーストリア、後に家族合唱団となるフォン・トラップ大佐一家にまつわる実話を、名コンビ、ロジャース＆ハマースタインがミュージカル化、それを今度はR・ワイズがロケ効果を存分に生かして映像化したものだ。奔放で子供好きな修道女マリアは院長の命令で厳格なトラップ家の家庭教師を務めることになった。一筋縄ではいかぬ７人の子供たちだが、マリアの歌で心が通じ合う。大佐とも恋が芽ばえ、結ばれる二人。折しもナチスの侵略の時、一家は音楽祭を利用して亡命を図り、スイスへと雪割草の咲く山を越えていく……。音楽の素晴らしさはもちろん、軽快なテンポとリズム、そして映像の躍動感は見事の一言。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Waterloo Bridge", "哀愁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "ハリウッドの代表的恋愛映画として、誰からも愛されている作品。空襲下のロンドンを舞台に、西部戦線に向かう青年将校と、バレエの踊り子との美しく悲しい恋を描いている。原作は、何度もピュリッツァー賞に輝いたロバート・E・シャーウッドの舞台劇。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("酒と女と槍", "酒と女と槍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "豊臣時代の末期、槍の名人・富田高定は切腹に失敗し、山村に引きこもって静かに暮らしていた。が、関ケ原の合戦が始まり、侍の血を呼びさまされた高定はまた戦場に赴いていく……。武士道に封建社会の矛盾という、内田吐夢が一貫して描き続けたテーマを扱った力作。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AT THE CIRCUS", "マルクス兄弟　珍サーカス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "マルクス・ブラザースのMGM第３作。今回は倒産しそうになっているサーカス団を救うお話。グルーチョの歌う愉快な『刺青の女リディア』、怪力男や小人を相手にしての３人のナンセンスぶりなどの連続で見せる笑劇作。DVDは「マルクス・ブラザーズ コレクターズ・ボックス」に収録。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼平犯科帳", "鬼平犯科帳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "人気TV時代劇『鬼平犯科帳』をTVと同じ中村吉右衛門主演で映画化。火付盗賊改方長官・長谷川平蔵は、兇賊・蛇の平十を捕らえ、火あぶりにかけるが、平十は“仲間が必ず仇をとる”と不吉な言葉を残して果てた。平蔵の密偵・おまさは、昔なじみで今も心に秘める狐火の勇五郎と再会。その夜、盗賊が薬種問屋・山田屋に押し入り、家人らを惨殺、“狐火”の絵札を残して消えるという事件が起こった。おまさは、一味の頭は勇五郎の異母弟の文吉であることを平蔵に告げ、勇五郎と二人で文吉らのもとに乗り込む。危ないところで盗賊改方に助けられるが、文吉の背後には大坂(昔の大阪)の大盗賊・白子の菊右衛門がおり、江戸の大頭目・荒神のお豊と手を組んで平蔵失脚を謀ろうとしていた……。映画では、平蔵の情の部分とあわせ、“鬼”と呼ばれる凄味が強く描かれていく。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TINA", "TINA ティナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ソウル界の女王として君臨する、ティナ・ターナーの半生を映画化。かつての公私に渡るパートナー、アイクとの激しい愛憎を軸に、成功と転落、そしてカムバックを果たすまでを、『愛の魔力』等のヒット曲にのせて描く。ティナに扮したA・バセットが見せる、熱演とそっくりぶりにご注目。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エクステ", "エクステ", "", "", "http://image.eiga.k-img.com/images/movie/33857/315x210.jpg?3971", 108, "ヘアー・エクステンション（＝エクステ）に乗り移った少女の怨念が引き起こす恐怖を描いた異色ホラー。ある日、横浜港に到着した巨大コンテナから膨大な量の髪の毛に埋もれた少女の遺体が発見される。その直後、美容師の卵・優子が働く美容室の周囲で次々と不可解な出来事が起こり始める。やがて優子の元に、エクステを売り歩く山崎という男が現れ……。主演は「キル・ビル」の栗山千明。また髪フェチ男・山崎を実力派俳優の大杉漣が怪演。", "2007-02-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／ONE　FINE　SPRING　DAY", "春の日は過ぎゆく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "「八月のクリスマス」で注目されたホ・ジノが、男女の心のすれ違いを繊細に描く切ないラブ・ストーリー。恋愛経験も少ない若いサンウと、離婚歴のある年上の女性ウンスが仕事を通じて出会い、ごく自然に恋に落ちる。だが、春が終わりかけた頃、ウンスの心に変化が表れ始め、彼女の冷たい態度にサンウは動揺し傷ついていく……。説明を省いて、二人の恋の行方を、移ろいゆく季節の中に静かに見つめるシンプルな演出。だが、微妙な表情の変化や二人に漂う空気で、言葉では表せない恋の危うさがリアルに浮き彫りにされる。時にもろく、時に傲慢な女性の複雑な心情を、イ・ヨンエが自然体の演技で体現する。", "2001-01-01", "5,76,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒い雪", "黒い雪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "売春宿を経営している母を持つ次郎。彼は売春婦らが平気で黒人兵と関係を持つことに言いようのない挫折感を覚える。やがて彼は黒人兵を射殺、情婦だった叔母も犯して殺してしまう。占領軍に捕まっても自白を拒否する次郎だが……。公開と同時に性描写をめぐって裁判にもなったいわくつきの作品。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("波", "波", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "「タイムレス・メロディ」でぴあフィルム・フェスティバル・スカラシップ作品として劇場デビューを飾った奥原浩志が放つ、独特の空気感流れる青春物語。ホテルのナイトフロントのバイトをしているケンサクは、３年以上も自分の父親かも判然としない老人の世話をしている。毎年、夏の間だけ親戚のガソリンスタンドを手伝いにやって来るミカとは、以前付き合っていた関係だ。しかし、失恋の痛手から逃れるように、この町にやって来たユカと何となく関係を持ってしまったケンサク。何となく均整がとれていた３人の関係だったが、軽薄なノリのタツの登場で彼らのバランスは崩れていく。現金輸送車を襲撃しようとするタツ。彼を止めるためにケンサクは実行寸前のタツの前に姿を現すが……。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SECOND CHORUS", "セカンド・コーラス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ニューイングランドの大学で、トランペッターとして覇を競うダニーとハンクの夢は、有名なジャズメン、アーティ・ショウの楽団に入ること。二人はその売り込みを図る途中、美人秘書のエレンにぞっこんとなる。時に協力し合い、大体は足を引っ張り合いながら、売り込みと恋の両方をもくろむが……。アステア充実期の一作。実名で登場するアーティ・ショウと彼の楽団の演奏も見所の一つで、主題歌はアカデミー賞にノミネートされた。振付はアカデミー受賞歴のあるハーメス・パン。ビデオタイトルは「恋のスイング合戦」。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太の太陽王伝説", "ドラえもん のび太の太陽王伝説", "", "", "http://ecx.images-amazon.com/images/I/31ZMTK5EGML._SL210_.jpg", 92, "藤子・F・不二雄原作のコミックを原作にした劇場版アニメ・シリーズの第21作で、時空を超えたアドベンチャーが展開する。秘密道具“タイムホール”で、古代文明に迷い込んでしまったのび太とドラえもん。そこで二人はのび太にそっくりのな太陽の国・マヤナ国の王子ティオと出会う。魔女レディナの呪いで眠り続ける母を救いたいと願うティオは現代日本で修行したいと二人に頼むのだった。そして、のび太とティオは入れ替わり、それぞれ不思議な生活を始める。果たしてティオは目的を達することができるのだろうか？", "2000-03-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Misfits", "荒馬と女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "　離婚都市と呼ばれるリノに訪れた美しい女性ロザリン。離婚直後の彼女はカウボーイのゲイ、そして自動車修理工のギドと知り合う。ふたりの男はロザリンに心を奪われてしまう。ゲイの友人パースも加わり、一行は砂漠へ野馬狩りに出発する。馬と人間の壮絶な闘いを見たロザリンはそれを止めようとするが……。作家アーサー・ミラーが自身の戯曲を脚色し、新たにモンローのための役を加えた。この撮影終了後まもなく、クラーク・ゲイブルが他界。彼の遺作となった。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチバンV 竜虎炎上", "ガチバンV 竜虎炎上", "", "", "http://ecx.images-amazon.com/images/I/512jKqbFA1L._SL210_.jpg", 67, "", "2009-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ENFANT SECRET", "秘密の子供", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1970年代に先鋭的な実験映画を製作していたP・ガレルが、商業映画に復帰した作品。映画監督ジャン・バチストは、電気ショックで薬物中毒を克服し、恋人エリーと映画を作ろうとする。しかし映画のために母親の死に立ち会えなかったエリーは情緒不安定に陥り、二人の関係は破局へと向かう……。ガレルが元妻であるシンガー、ニコとの生活を題材にした自伝的な物語。もともとニコ自身が主演する予定だったが、二人が破局したためゴダール作品などで知られるA・ヴィアゼムスキーがヒロインを務めた。痛々しい愛の軌跡の中に、どこか幸福な瞬間が垣間見れる美しい恋愛詩である。", "1979-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG BUS", "弾丸特急ジェット・バス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "全長150メートル、重量75トン、動力は原子力という超巨大なバスが主役のアクション映画。バスの行く先々で起こるパニックの数々。フローリー監督は、普通のパニック映画とは異なり、一般映画のパロディー・シーンを随所に盛り込んで工夫をほどこしている。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Scanners", "スキャナーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　浮浪者のべイルは自分がスキャナーと呼ばれる超能力者であることを知らされる。その頃、もうひとりのスキャナー、レボックがコンセック社の会議場で人の頭蓋骨を破裂させるという事件が起こる。自らの能力を使って、世界征服をたくらむレボック。女性スキャナー、キムとともにレボックを追うベイルは、やがて自分とレボックにまつわる秘密を知ることに……。超能力者たちの戦いを描くSFスリラー。のちにシリーズ化されたヒット作。", "1981-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Near Death", "臨死", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 358, "", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("とらばいゆ", "とらばいゆ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「avec mon mari／アベックモンマリ」の大谷健太郎が、前作通りセリフの応酬劇を推進力に二組のカップルの悲喜こもごもを綴ったドラマ。対照的な性格にして女流棋士としてライバル同士の姉妹を主人公に、姉の夫、妹の同棲相手を巻き込んだ四角関係を快活なドラマ・テンポで切り取っていく。勝負にこだわるあまり家事はほったらかしという姉の夫を塚本晋也が好演。愛するゆえに耐える男のやるせなさが、自然体の演技から伝わってくる。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女死刑囚の脱獄", "女死刑囚の脱獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "父親殺しの罪を着せられ、死刑囚となった女・京子。彼女は、結婚を断った明夫が犯人だと恋人・赤尾に教えられ、明夫に復讐をしようと脱獄する。しかし、彼は京子の無罪を固く信じ、彼女の逃亡を手助けしてくれるのだった。一方、警部・宮田が事件を洗い直してみると……。中川信夫のスリラー演出で一気に見せる珍品。ギター１本の音楽も効果的。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トントンギコギコ図工の時間", "トントンギコギコ図工の時間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「こどもの時間」の野中真理子監督が、小学校を舞台に撮り上げたドキュメンタリー。週に一度訪れる図工の時間を心待ちにしている子供たち。そこでは正解などなく、それぞれが自由に自分の思いをカタチにしていく。大人たちの束縛から解き放たれ、自分の作品と真剣に向かい合う子供たちの姿は、大人になるうちに忘れてしまった大切なものを呼び起こさせてくれる。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間革命", "人間革命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 159, "創価学会の誕生をドキュメンタリー的に描いた作品。札幌から上京した戸田は、友人に紹介された小学校校長の牧口の考え方に感銘を受ける。その後、牧口は日蓮正宗に入信し、続けて牧口と戸田は“創価教育学会”を創設する。時は昭和５年であり、時局柄、当局は二人の活動を制約、ついには特高警察に逮捕される。牧口は獄中で仏教に打ち込み、日蓮の教えを理解するまでに至るが、栄養失調のため亡くなってしまう。昭和20年７月に出獄した戸田は、牧口の教えを継ぐべく、努力を続ける。事業も上手いき、仏教の勉強会も会員を増やしていく。そして、会の名前を創価学会と変えるのだった。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAMBADA！ THE FORBIDDEN DANCE", "情熱のランバダ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ブラジルの熱帯雨林にある平和な村が、乱開発によって破壊の危機に。村の王女は渡米し、開発企業の会長に交渉しようとするが門前払い。やがてダンス・コンテストに優勝すればTVに出られると知った彼女はTVに出て訴えようと思いつくが……。“ランバダ”をフィーチャーしたダンス・ラブ・ストーリー。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("You, The Living", "愛おしき隣人", "", "", "http://image.eiga.k-img.com/images/movie/53407/315x210.jpg?2352", 94, "ロックスターとの結婚を夢見る少女、世界で一番ついてない夫婦、誰からも愛されたことのない男、「誰も私を理解してくれない！」と泣き叫びながら歌う女、困窮した家計を静かに嘆く精神科医……北欧の町を舞台に、一生懸命に生きながらも恵まれない人々の日常をユーモラスに描く。監督は前作「散歩する惑星」（2000）でカンヌ国際映画祭審査員特別賞を受賞したスウェーデンのロイ・アンダーソン。", "2008-04-26", "14,2,7,18,36,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Goal II: Living the Dream", "GOAL!2", "", "", "http://image.eiga.k-img.com/images/movie/1897/315x210.jpg?1265", 114, "1人の青年がプロ・サッカー選手として成功していく姿を描いたFIFA公認サッカー・ドラマ3部作の第2弾。前作で英国プレミア・リーグを舞台にサッカー選手になる夢を叶えた主人公が、本作ではスペインの名門チーム、レアル・マドリードに移籍。ヨーロッパ最強のクラブチームを決定するチャンピオンズ・リーグに挑戦する。主人公のチームメイトとしてベッカム、ジダン、ロナウドらレアル・マドリードのスター選手も出演。", "2007-05-26", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Dolce Vita", "甘い生活", "", "", "http://ecx.images-amazon.com/images/I/51oEafEhVcL._SL210_.jpg", 174, "最初、フェリーニが考えていたタイトルは「紀元2000年のバビロン」。20世紀のバビロンさながらのローマの退廃ぶりを余すところなく描いた大作だ。フェリーニ作品としては、後年の「サテリコン」や「カサノバ」の原型ともいえる画期的作品である。スターの顔ぶれの豪華さも特筆に値する。現代のローマ、作家を夢見て地方から出て来たマルチェロは、今ではゴシップ記者。ある夜、大富豪の娘と一夜をともにして帰宅すると、同棲中のエンマが自殺を図っていた。エンマを病院に入れたマルチェロは、空港にハリウッドのグラマー・スター、シルヴィアの取材に行く。天衣無縫なシルヴィアにマルチェロはさんざん引きずり回される。そんな彼の心のよりどころは、友人のスタイナー一家。スタイナーの知的で平和な生活は、マルチェロに作家への志を思い出させるが、スタイナーは子供と一緒に自殺をしてしまう。ショックを受けた彼は、乱痴気パーティーをして夜を明かした後、海岸で怪魚の死骸を見つけるのだった。", "1960-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBIN AND THE SEVEN HOODS", "七人の愚連隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "いわゆるシナトラ一家総出演のアクション・ミュージカル・コメディ。1920年代のギャング隆盛の地シカゴ。ロボと弟分ウィルそれにジョンの３人は、縄張り拡張を狙うギスボーン一味と対立。ギスボーンが殺した男の娘がロボを訪れ、父の仇討ちを依頼するが。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ライファーズ／終身刑を超えて", "ライファーズ／終身刑を超えて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "受刑者が300万人を超え、そのうち13万人あまりのライファーズと呼ばれる終身刑・無期刑受刑者を抱えるアメリカ。そのライファーズたちを積極的に受け入れているカリフォルニア州の刑務所内の更生施設アミティを、映像ジャーナリストの坂上香監督が密着して撮り上げた、渾身のドキュメンタリー。罪の償いやこれからの人生と積極的に向き合う受刑者たち。彼らを通して、暴力や犯罪について改めて考えさせられる。カリフォルニアの更生施設アミティで穏やかに暮らす受刑者たち。薬物依存症の若者、弁護士を目指す少女、カウンセラーとして修行中の者など、かつては凶悪な犯罪を繰り返してきた彼らの現在の心境に迫る。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("落花の舞", "落花の舞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "戦前のチャンバラ・スターの中で、トーキーになって、その転換期をいち早く乗りきったのが市川右太衛門。由比正雪の乱に題材を取った本編でも、舞台出身らしい確かな発声法で貫禄を見せた。ちなみに当時、チャンバラの一方の雄、阪東妻三郎は、まだトーキーの発声と格闘していた。現存するフィルムは、オリジナルから１巻欠けて９分短い。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コント55号 人類の大弱点", "コント55号 人類の大弱点", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "日本の喜劇界に風穴を開けたお笑いの名コンビ、コント55号が主演した、東宝製作のコメディ・シリーズ第2弾。『ブルーライトヨコハマ』がミリオンセラーとなった当時、歌手のいしだあゆみがゲスト出演している。今回は、サギ師に扮する欽ちゃんと刑事に扮する二郎さんのガチンコ対決だ。軽犯罪を手始めに煙草サギ、自動車サギなどのユニークな手口が次々に繰り出されて……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("はつ恋", "はつ恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ロシアの文豪ツルゲーネフの恋愛小説を基に、舞台を日本の70年代に置き換えて撮り上げた青春ラブ・ストーリー。年上の女性と年下の男の子の恋愛ものだが、アイドル映画の定番としてぴったりの題材だ。16歳の高校生の一彦は、隣家の自由気ままな年上の女性に恋をする。彼女もまた一彦に優しく接してくれるので両思いの脈があるかと思ったが、彼女には恋人がいた。片思いの切ないムードが漂う一作。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRAN CASINO", "グラン・カジノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "内戦で荒廃したスペインを離れ、ハリウッド経由でメキシコへ渡ったブニュエル監督の、メキシコ時代の開幕を告げる作品。石油ブームに沸く小さな町を舞台に、ミュージカルあり、恋愛ありの娯楽映画となっている。アルゼンチンからメキシコへとやって来た歌姫のメルセデスは、油田の持ち主である兄が失踪したと聞かされる。最初は使用人のヘラルドを疑っていた彼女だったが、外国のあくどい石油業者が黒幕だと知り、ヘラルドと力を合わせ油田を守るため大奮戦する。メキシコらしいマリアッチや単純なストーリーなど、「アンダルシアの犬」や「糧なき土地」を撮ったあの鬼才とは思えぬ作品ではあるが、プログラムピクチャーとして、完璧にフィルムの上に定着してしまう監督の演出手腕が見事だ。ハワード・ホークスを思わせるカッチリとしたニーショットなど、映画の文法に忠実であろうとするブニュエルの力量が、遺憾なく発揮された作品といえる。", "1946-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("D.O.A.（DEAD ON ARRIVAL）", "D.O.A.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "セックス・ピストルズのUSツアーをメインに、ジャム69、ジェネレーションX、クラッシュらの熱気あふれるライヴの模様をドキュメンタリー・タッチで追った作品。随所に収められたシド・ヴィシャスのインタビュー・シーンも見どころだ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOCUMENTARY of AKB48 Show must go on 少女たちは傷つきながら、夢を見る", "DOCUMENTARY of AKB48 Show must go on 少女たちは傷つきながら、夢を見る", "", "", "http://image.eiga.k-img.com/images/movie/57607/315x210.jpg?3640", 121, "2011年1月に劇場公開された「DOCUMENTARY of AKB48 to be continued 10年後、少女たちは今の自分に何を思うのだろう？」に続く、人気アイドルグループ「AKB48」のメンバーの素顔に迫るドキュメンタリー第2弾。05年12月に劇場公演をスタートさせ、活動6年目を迎えた11年のAKB48は、リリースしたCDシングル5作すべてがミリオンセールスを達成し、初のドームコンサートも3日間で9万人を動員、選抜総選挙やじゃんけん大会といったイベントも大きな話題を呼んだ。連日メディアを賑わせ人気を博したが、その裏側にはプレッシャーに傷つき、孤独と向き合いながらもステージに向かおうとする少女たちの姿があった。11年のAKB48の活動に密着し、グアム、台湾、シンガポールなど世界各地を飛び回る姿や、東日本大震災復興支援のために立ち上げた「誰かのためにプロジェクト」で5月から毎月行われた被災地訪問の様子なども収められている。監督は、AKB48の「言い訳Maybe」「ポニーテールとシュシュ」「上からマリコ」といった人気楽曲のPVも手がける高橋栄樹。", "2012-01-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブホテル・コレクション　甘い記憶", "ラブホテル・コレクション　甘い記憶", "", "", "http://image.eiga.k-img.com/images/movie/54857/315x210.jpg?2278", 53, "1985年の新風営法の施行以来、減少の一途を辿るラブホテルの実態を描いたドキュメンタリー。監督は「夏に生れる」「工場萌えな日々」の奇才・村上賢司。ヨーロッパの古城を思わせる外観、回転ベッド、天井ミラーなどの昔ながらの仕掛けを常備したラブホテルを徹底取材。各界の著名人にもインタビューを慣行し、文化遺産としてのラブホテルのあり方を模索する。", "2009-09-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SISTER，SISTER", "地獄のシスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "演技派二人が共演したサイコ・スリラー。ホテルを経営する姉と暮らすルーシーは精神病院から退院したばかり。ある日、青年マットがホテルに宿泊し、彼女は恋心を覚える。だが、その日から連続殺人事件が起こる。ルーシーは、かつて殺人を犯したことのある姉シャーロットに疑惑の目を向ける。ホテルの人々は次々と殺害され、やがてルーシーとシャーロット、マットだけが生き残り……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハナミズキ", "ハナミズキ", "", "", "http://image.eiga.k-img.com/images/movie/55077/315x210.jpg?1784", 128, "人気アーティスト・一青窈の大ヒット曲「ハナミズキ」をモチーフにしたラブストーリー。主演に新垣結衣、相手役で生田斗真が共演。北海道で母・良子（薬師丸ひろ子）と2人で暮らす紗枝は、東京の大学に進学することを夢見る高校生。若くして亡くなった父（ARATA）が紗枝へのメッセージとして庭に植えたハナミズキに見守られながら受験勉強に励む紗枝は、ある日、漁師の跡取りで水産高校に通う康平に出会い、互いに恋に落ちる。やがて紗枝が大学に合格し、遠距離恋愛が始まるが……。", "2010-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代豪侠傳／EXECUTIONERS", "ワンダーガールズ　東方三侠2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "香港の３大人気女優が共演した近未来アクションの続編。舞台は核戦争後、限られた水を人々が奪い合う時代。浄水装置を発明した天才キムが水の配給の権限を牛耳っていたが、それだけでなく彼は地球上の水を独占し、世界を征服しようとしていた。この陰謀に巻き込まれてしまったトントン、サン、チャットのワンダーガールズたちがキムを倒すために立ち上がる。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不良番長・口から出まかせ", "不良番長・口から出まかせ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "太平洋横断に挑戦した神坂率いるカポネ団は、一路アメリカ大陸を目指すが、流れ着いたのは大阪の近くの寒村。彼らは村の海女を使って釜ヶ崎にヌーディスト・クラブを開設。そこで麻薬密輸に巻き込まれ、関西挺身会・ジャンボ団と対決する。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("４ FOR TEXAS", "テキサスの４人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "銀行から駅馬車で運ばれる大金が強盗一味に襲われる。だが、乗り合わせていた男ジョーがどさくさ紛れに金を横取り、逃走。銀行の用心棒ザックは数日後、町へ現れたジョーに会いに行くのだが、そこで意外な事件が起きて……。ひとクセある男たちの争いと奇妙な友情を描いた、R・アルドリッチ監督によるアクション西部劇。", "1963-01-01", "1", 0, NOW(), NOW());
