INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Trip to Asia: The Quest for Harmony", "ベルリン・フィル　最高のハーモニーを求めて", "", "", "http://image.eiga.k-img.com/images/movie/53967/315x210.jpg?4637", 108, "創立125周年を迎えるベルリン・フィルハーモニー管弦楽団の東アジアを巡るコンサートツアーを追ったドキュメンタリー。監督は「ベルリン・フィルと子どもたち」のトマス・グルベ。カメラは飛行機、ホテル、楽屋などを自由に動き回り、芸術監督兼主席指揮者のサー・サイモン・ラトルが率いる126名の演奏家の誇りや苦悩など、舞台と客席という距離を越えて観客の身近に描く。", "2008-11-15", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hairspray", "ヘアスプレー", "", "", "http://image.eiga.k-img.com/images/movie/34097/315x210.jpg?1433", 117, "ジョン・ウォーターズ監督が88年に発表した青春映画「ヘアスプレー」は、02年にブロードウェイでミュージカル化され、トニー賞8部門を受賞した。そのミュージカルをジョン・トラボルタ、クリストファー・ウォーケンら豪華キャストで映画化。60年代ボルチモア、人気TV番組「コーニー・コリンズ・ショー」に憧れる、ちょっと太めの女子高生トレーシーは、番組レギュラーメンバーのオーディションが開催されると知り、喜び勇んで挑戦するが……。", "2007-10-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr. Popper's Penguins", "ミスター・ポッパーズ・ペンギンズ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2011-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラ・モスラ・キングギドラ／大怪獣総攻撃", "ゴジラ・モスラ・キングギドラ／大怪獣総攻撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "“平成ガメラ”シリーズで怪獣映画の新機軸を打ち出した金子修介が老舗“ゴジラ”を手掛けたシリーズ第25作。物語としては、昭和29年に上陸して以来、日本にはゴジラが現れていない設定となっている。47年ぶりに日本を襲うゴジラと、民間伝説『護国聖獣伝記』にある護国三聖獣ギドラ[魏怒羅]、モスラ[最珠羅]、バラゴン[婆羅護吽]が繰り広げる戦い。そして、かつてゴジラに両親を殺された防衛軍准将、立花のゴジラ打倒に燃やす執念、その娘であるBS局リポーター・由里の記者魂が描かれる。金子修介は「ガメラ」と同様、怪獣と神話を結びつける手法をここでも展開。かつ、ゴジラが太平洋戦争で散っていった人間たちの残留思念の集合体とすることで、“ゴジラ”の物語に新しい解釈を加えている。ちなみにギドラは最終決戦で変態、翼を得てキングギドラと成る。", "2001-12-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALWAYS 三丁目の夕日’64", "ALWAYS 三丁目の夕日’64", "", "", "http://image.eiga.k-img.com/images/movie/55987/315x210.jpg?1309", 144, "西岸良平のコミックを原作に、昭和30年代の東京下町に暮らす人々の悲喜こもごもを描いた人気シリーズ第3作で、シリーズ初の3Dで製作。前作「ALWAYS 続・三丁目の夕日」（2007）から5年後となる昭和39年を舞台に、それぞれ人生の転機を迎える夕日町三丁目の住民たちの姿を描く。戦後19年がたち復興を遂げた日本は高度経済成長の真っただ中で、東京オリンピックの開催もひかえ熱気に沸いていた。夕日町三丁目に暮らす茶川家には間もなく家族が増える予定で、鈴木オートの事業も好調。それぞれに賑やかな日々を送っていたが……。", "2012-01-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODY HEAT", "白いドレスの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "現代によみがえったファム・ファタールの狂おしい妖しさと、これが監督デビューとなる脚本家出身のカスダンの流麗な演出力が、ねっとりと絡み合った、実に官能的な犯罪サスペンスの傑作。“ボディ・ヒート”という原題通りに、観る者まで熱くする官能美がたち込めている。ターナーとハートの汗にまみれ、湯気さえあがりそうな濡れ場。完璧にとり込まれてしまったハートは、悪女ターナーの筋書き通りに犯罪に手を染める。ひねりの効いたラストやロークの爆弾売りなどアクの強いキャラクター、やるせないジャズの調べなどなど、見るべきところが多い。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マネーざんす", "マネーざんす", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "深夜TV番組『つんくタウン』から誕生した“つんくタウンFILM”より発信された、お金をモチーフに６つの短編で綴るオムニバス映画。つんくを製作総指揮に据えて、タレントから新進映画監督まで、バラエティーあふれるメンバーがメガホンを執り、ジャンルも様々で、それぞれが個性豊かな演出を披露している。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INSEMINOID", "悪魔の受胎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "地球を遠く離れた惑星を探索する調査隊。生物はすべて絶滅したはずだったが、ただ１匹生き延びていたエイリアンが、女性調査隊員を次々と妊娠させる。妊婦たちは胎児の命ずるままに、隊員たちを殺していき……。「エイリアン」を意識したエロティックなSFホラー。", "1980-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HILARY AND JACKIE", "ほんとうのジャクリーヌ・デュ・プレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "イギリス音楽界最高のチェリストとして活躍しながらも、多発性硬化症という不治の病にかかり、28歳にして表舞台を去ったジャクリーヌ・デュ・プレ。栄光の陰で極度のストレスに蝕まれていった彼女の生涯を、幼い頃からのライバルで唯一の理解者でもある姉ヒラリーとの関係を軸に綴っていく。「奇跡の海」のエミリー・ワトソンがチェロをマスターして入魂の熱演を披露。精神が不安定になったジャクリーヌがヒラリーの夫とセックスする描写など、彼女の私生活を赤裸々に明かす内容に対し、欧米で抗議運動が起こされた。", "1998-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DX（ディスレクシア）な日々　美んちゃんの場合", "DX（ディスレクシア）な日々　美んちゃんの場合", "", "", "http://image.eiga.k-img.com/images/movie/57457/315x210.jpg?2404", 81, "発達障害・学習障害の一種で、読み書きにつまずきや学習の困難をきたすディスレクシア（Dyslexia＝難読症）を抱えて成人したひとりの女性・砂長美（び）ん（砂長宏子）さんの日常を追ったドキュメンタリー。外見や知覚、聴覚には問題がないため周囲が障害を認識しずらく、発達障害そのものが一般的に知られる以前に子ども時代を送った美んさんは、周囲からバカにされ、勤め先もすぐにクビになるなど偏見や無理解に苦しみながら生きてきた。しかし、数年前に自分がディスレクシアだということを知り、同じ症状で苦しむ仲間がいることを知った美んさんは、障害に負けずに生きようと自立に向けて歩み始める。", "2012-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トワノクオン　第三章　夢幻の連座", "トワノクオン　第三章　夢幻の連座", "", "", "http://image.eiga.k-img.com/images/movie/56397/315x210.jpg?1906", 45, "「機動戦士ガンダム　第08MS小隊」などで知られ、2010年に他界した飯田馬之介監督が生前に制作を進めていた全6話のオリジナルアニメーションを映画館で上映する第3話。能力者「ベスティア」を秘密裏に追うサイボーグ部隊「WTOC」のイプシロンが、ある能力者の少女との接触により忘れ去られていた記憶を呼び覚ます。そのことをきっかけに、イプシロンと対峙するクオンもまた大きく運命を変えていくことになる。", "2011-08-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地頭龍／HARD TO DIE", "リーサル・パンサー２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "犯罪都市・香港を舞台にした火薬、銃撃、スタントシーン満載のアクション映画。香港警察の女刑事ジュリーとFBIの女性潜入捜査官ジェシカ。二人はチャイニーズ・マフィアを追っていた。そのマフィアの親子を狙う日本人女性、殺し屋たちが加わり、壮絶な戦いが展開する。", "1990-01-01", "6,76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スカイハイ 劇場版", "スカイハイ 劇場版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "TVドラマ化もされた高橋ツトム原作の人気漫画を映画化。理不尽な死に遭遇した者たちの無念を晴らす“怨みの門”の門番・イズコにTV版同様、釈由美子が扮し、映画オリジナルのストーリーで描く。「ALIVE」でも高橋ツトムの漫画を斬新に映像化した北村だけに、ここでもよりスケール感を増した映像で、奇想天外な世界を見せつけている。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCANNER COP", "スキャナーズ・ニューエッジ ザカリアス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "デヴィッド・クローネンバーグのオリジナルをベースにしたSFアクション「スキャナーズ」シリーズの第４作。相次ぐ警官の惨殺事件の謎に挑む、スキャニング能力を持つ警官サミュエル。犯人の一人を逮捕し、その記憶を探る彼は、事件の影に潜む新種の洗脳実験の存在と黒幕の意外な正体を知る……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Twilight Saga: Breaking Dawn - Part 2", "トワイライト・サーガ　ブレイキング・ドーン Part2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "人間の少女ベラとバンパイアのエドワードの禁断の恋を描く、ステファニー・メイヤーによる人気小説を映画化したシリーズ最終章2部作の後編。", "2012-12-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　SECRET　AGENT", "シークレット・エージェント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ヒッチコックの「サボタージュ」の原作としても有名な、文豪コンラッドの『密偵』を映画化。1910年代のイギリス、ヴァーロックは英国警察とロシアに通じる二重スパイ。天文台の爆破という任務を命じられたものの、計画は失敗に終わり、彼に協力した知覚障害の義弟が爆死した。亡き弟を溺愛していた妻はヴァーロックを責めるが、そんな彼らに追手が迫る……。サスペンスよりも登場人物の心理に焦点が絞られている。世捨て人のようなアナーキストに扮する、ノンクレジット出演のロビン・ウィリアムズの好演も見もの。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOR PETE’S SAKE", "またまたおかしな大追跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "夫のピートが株で3000ドルの大穴をあけたため、世話女房のヘンリエッタはアブない高利貸しから借金。あげくに売春婦として売り飛ばされて……。軽快なテンポで進行するドタバタ・コメディ。アクと明るさを持つバーブラのコメディエンヌぶりがおかしい。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("港の日本娘", "港の日本娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "「風の中の子供」「蜂の巣の子供たち」などの児童映画で特に知られ、独創的な映画作法を創り出した巨匠、清水宏。サイレント期に出発した彼が、当時の“モダン”なメロドラマを活写した貴重な作品。異国情緒豊かな横浜を舞台に、女学生であるヒロインの流転の半生を即興的な演出で描いていく。", "1933-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mindhunters", "マインドハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "心理分析官を目指しすFBIの訓練生たちが本物の殺人事件に遭遇するサスペンス・スリラー。残酷な罠に追い詰められていく訓練生たちの奔走をレニー・ハーリン監督がスリリングに描き出す。想像を絶するエンディングまで息もつかせぬ緊迫した心理戦が展開する。FBIアカデミーで心理プロファイリングを学ぶ7人の若き訓練生たち。用意された最終試験は、無人島にある特殊訓練施設で連続殺人犯の正体を暴くことだった。だが、本当に殺人事件が発生。彼らは互いに疑心暗鬼になりながら、犯人割り出しに挑む。", "2006-02-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大天狗出現", "大天狗出現", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "開国か鎖国かをめぐって幕政が大いに動揺していた頃の、黒船来航の下田を舞台にした娯楽時代劇。来航してきた外人相手に武器弾薬で暴利をむさぼろうと謀る老中久世大和守とその腹心山名主膳。だが、彼らはことごとく白装束に天狗の面をつけた男、通称“下田の大天狗”に邪魔される。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アラカルト・カンパニー", "アラカルト・カンパニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "花の都パリにやって来たものの、何をして食べていけばいいか分からない若い男女が、国際コンサルタントの中年男とともに“便利屋”を開業する様子を描いた青春映画。在パリ日本人の実情を、リアルに描き出している。原田芳雄がおトボケ中年男を好演。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COP TARGET", "コップ・ターゲット 狼たちの牙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "麻薬組織に殺された外交官の未亡人とその娘プリシラの護衛として南米にやってきたベテラン刑事のウッド。だが婦人が夫の追悼式に出席する矢先、プリシラが誘拐される。治外法権のため思うように捜査ができず、ウッドは米国大使グレンジャーに協力を要請するが、彼こそが麻薬組織の影の実力者だった……。", "1990-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西環的故事／THE STORY OF KENNEDY TOWN", "ケネディタウン・ストーリー　嵐の季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1960年代の香港を背景に、激しいアクションをとり入れたノスタルジックな青春ストーリー。３人の若者、カン、パン、ウェイはともに成長し親友となった仲だった。ある日彼らは、ヤクザと喧嘩になり窮地に陥ったところを、初老の刑事に救われる。その日から３人は、警官を志すが、合格したのはパン一人だった。チンピラを続けるカンとウェイ、順調に出世していくパン。３人の運命は大きく変わっていった。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("センチメンタルヤスコ", "センチメンタルヤスコ", "", "", "http://image.eiga.k-img.com/images/movie/56647/315x210.jpg?2261", 87, "女性ファッション誌「Seventeen」モデル出身の若手女優・岡本あずさが、心に闇を抱えた女性を演じたサスペンス。両親の命日になると自殺未遂を繰り返してきたキャバクラ嬢のヤスコが、何者かに首を絞められ意識不明の重体で病院に搬送される。容疑者としてヤスコの恋人だった7人の男が浮かび上がるが、7人の証言は食い違い、隠されていたヤスコの過去が明らかになっていく。「ベロニカは死ぬことにした」の堀江慶監督が旗揚げした劇団「CORCFLASES」で上演された舞台劇を、堀江監督自ら映画化した。", "2012-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Monkey's Mask", "ポエトリー,セックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「女と女と井戸の中」で注目されたS・ラングが、ミステリアスなドラマに隠された女たちの欲望を浮き彫りにする。性的に過激な内容の詩を残して失踪した女子大生ミッキー。女探偵ジルが、ポエトリー・リーディングに参加していたミッキーの人間関係を探ると、両親も知らなかったもう一つの彼女の顔が明らかになる。一方、ジルは、捜査中に知り合った魅力的な女教授に惹かれ、同性愛に目覚めていく。", "2000-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母べえ", "母べえ", "", "", "http://image.eiga.k-img.com/images/movie/53247/315x210.jpg?2108", 132, "山田洋次監督＆吉永小百合主演による人間ドラマ。黒澤明監督作品のスクリプターとして活躍した野上照代の自伝的小説を原作に、激動の昭和初期をたくましく生き抜こうとする1人の母の姿を通して家族の素晴らしさを描き出す。昭和15年の東京。野上佳代は夫の滋や2人の娘と仲睦まじく暮らしていた。しかし、戦争反対を訴えていた滋が治安維持法で検挙されてしまう。", "2008-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAMILLE", "椿姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "A・デュマ・フィスの名作を名匠キューカーが演出した傑作の１本。パリの社交界の高級娼婦マルグリットと純情青年アルマンの悲恋物語。スウェーデン映画界からハリウッドに渡り大成したガルボの代表作。テイラー、バリモアら当時の大スターの共演がうれしい。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUTOUR DE MINUIT／'Round Midnight", "ラウンド・ミッドナイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "天才ジャズ・ピアニストとうたわれたバド・パウエルと、サックス奏者レスター・ヤングのキャラクターと実話に想を得て、B・タヴェルニエ監督が脚色した作品。1959年。ニューヨークからパリのジャズクラブ“ブルーノート”にサックス奏者デイル・ターナーがやって来た。酒とドラッグに溺れたデイルだが、その演奏は人々の心を感動させずにはおかなかった。ある雨の夜、貧しいがゆえに、クラブの扉の外で彼の演奏に聴き入っていたフランシスは、その演奏に感動し、デイルこそ“神の声”を伝えるアーティストだと確信する。やがてデイルにふと声をかけられたフランシスは彼の支えとなり、酒とドラッグの生活から脱出させようと心血を注ぐが……。忠実に再現された“ブルーノート”と、そこで演奏されるジャズが聴きもの。ハービー・ハンコック、ウェイン・ショーター、フレディ・ハバート、ロン・カーターほか一流アーティストの特別出演、演奏も話題を呼んだ。独特の色調による映像が美しい。", "1986-01-01", "2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WO DIE GRU¨NEN AMEISEN TRA¨UMEN", "緑のアリが夢見るところ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ウランの調査のため、オーストラリアの原野にやって来た地質学者と、聖なる土地を侵されまいと座り込みで抗議する原住民との交流を描く。文明とかけ離れた、神秘に満ちたこの地に、次第に魅了されていく主人公の姿に、監督自身の南半球への思いが投影された力作。", "1984-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY BRILLIANT CAREER", "わが青春の輝き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "オーストラリアの女流作家M・フランクリンによる自伝的な同名小説を映画化。農村の開拓者一家に生まれ育った娘シビラは、芸術家志望だったが貧しさゆえに夢のまた夢。しかし裕福な祖母の家にもらわれたことから状況は一変する。", "1979-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOOLS OF FORTUNE", "フールズ・オブ・フォーチュン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "20世紀初頭にイギリスからの分離、独立をかけて戦われたアイルランドの独立戦争の時代を背景に、平和な一家族が、歴史の悲劇に巻き込まれ崩壊していくさまを描く。トレヴァーの同名ベストセラー小説を、絵画のように美しいアイルランドの情景と、心に染みる音楽で、一大叙事詩に仕上げた。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dopo mezzanotte", "トリノ、24時からの恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/33627/315x210.jpg?2414", 93, "イタリア映画発祥の地トリノを舞台に、3人の男女が織りなすロマンチックなラブ・ストーリー。シネマ・ミュージアムの警備員として働く映画好きの青年マルティーノの元に、ある日勤め先でトラブルを起こしたアマンダという女性が逃げ込んできた。彼女とその恋人アンジェロとの出会いにより、マルティーノの日常は次第に変化していく。本国イタリアでは、もうひとつの「ニュー・シネマ・パラダイス」と絶賛され大ヒットを記録した。", "2006-09-02", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("弁天小僧", "弁天小僧", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "河竹黙阿弥の歌舞伎狂言を題材に、伊藤大輔が監督した娯楽時代劇の佳作。弁天小僧をはじめとする白浪五人男は悪徳旗本を出し抜き、大名や商人から大金を奪い、悪人に操を狙われた町娘の危機を救う。町娘に扮した、青山京子の好演が光る。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ASCENSEUR POUR L’E´CHAFAUD", "死刑台のエレベーター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "N・カレフの推理小説を、当時25歳のマル監督が演出した処女作で、罪を犯す現代人の空虚な心理を斬新な演出で鋭くえぐった傑作。土地開発会社の青年医師ジュリアンは、社長夫人と密通を重ね、ついに邪魔者の社長を殺す完全犯罪を企てる。だが犯行直後、帰り際に乗ったエレベーターが電源解除のため、停止してしまった。ジュリアンのアリバイは壊れたかに見えたが……。都会を映すノワールなモノクロ映像と即興演奏によるジャズ音楽が、情事に溺れた行き場のない男女の姿を見事に浮き彫りにする。ヌーヴェル・ヴァーグの存在を世界に知らしめた記念すべき作品で、ルイ・デリュック賞受賞。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In the Mouth of Madness", "マウス・オブ・マッドネス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　保険調査員のジョンは失踪した作家サター・ケーンの捜索を依頼される。やがてジョンは編集者のリンダとともに、ケーンの小説に出てくる架空の町にたどり着く。不可解な出来事が相次ぐ中、ジョンはケーンを見つけるが、彼の新作小説「マウス・オブ・マッドネス」に絡む恐ろしい企みを知る一方、次第に正気を失っていくことに……。現実と小説の中の世界が交錯する幻想的な怪奇ホラー。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hangover Part II", "ハングオーバー!!　史上最悪の二日酔い、国境を越える", "", "", "http://image.eiga.k-img.com/images/movie/55407/315x210.jpg?4821", 112, "二日酔いで記憶をなくした男たちの騒動を描き世界的大ヒットした「ハングオーバー！　消えた花ムコと史上最悪の二日酔い」（2010）の続編。フィル、スチュ、アラン、ダグの4人は、スチュの結婚式に出席するためにタイ、バンコクへ。再びバチェラーパーティを繰り広げる。翌朝目覚めると部屋には猿がいて、アランは丸刈り、スチュの顔にはタトゥが入っており、またしても想像できない事態を引き起こしていた……。監督のトッド・フィリップス、主演のブラッドリー・クーパーら前作のスタッフ、キャストが再結集。", "2011-07-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWICE DEAD", "亡霊は2度死ぬ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ロサンゼルス郊外の廃墟同然の屋敷を舞台に、善良なケイツ一家とパンク・ギャング、そして屋敷の元オーナー、さらに自意識過剰のテーラーの亡霊が繰り広げる珍騒動。J・チャピンがギャングのリーダーと亡霊の一人二役をこなしたホラー・コメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vincere", "愛の勝利を　ムッソリーニを愛した女", "", "", "http://image.eiga.k-img.com/images/movie/55427/315x210.jpg?3963", 128, "「肉体の悪魔」「夜よ、こんにちは」のマルコ・ベロッキオ監督が、ファシズムの創始者ベニート・ムッソリーニを愛し続けた女性、イーダの半生を描く。熱心な社会主義者だったムッソリーニはいつしかファシストに転向してしまうが、彼に恋をしていたイーダは変わらずムッソリーニを愛し続けた。イーダは全財産をかけてムッソリーニを支援し、身も捧げて長男を授かるが、正妻のいるムッソリーニはイーダを徐々に遠ざけていくのだった。2009年カンヌ国際映画祭のコンペティション部門に出品。日本ではイタリア映画祭2010にて「勝利を」のタイトルで初上映。11年劇場公開。", "2011-05-28", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADVENTURES OF SHARKBOY AND LAVAGIRL IN ３−D", "シャークボーイ＆マグマガール３−D", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "特製メガネを装着して楽しむ立体アドベンチャー映画。ロバート・ロドリゲス監督が「スパイキッズ3-D：ゲームオーバー」に続いて、イマジネーション豊かな世界を3-D映像で見せる。独創的なヒーローや仕掛けに富んだメカ、罠が潜む惑星など、目の前に迫るビジュアルの迫力を体感しよう。気弱な少年の前に現われた空想上のヒーロー、シャークボーイとマグマガール。彼らの住む星の危機を救うため、少年は冒険に旅立つが……。", "2006-02-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カミハテ商店", "カミハテ商店", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ΤΑΚСИーВЛЮЗ／TAXI BLUES", "タクシー・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "脚本家として多くの映画にかかわってきたP・ルンギン監督の処女作。現代のモスクワを舞台に、一人のタクシー運転手が見たままの姿を、映画の中に定着させている。サックス奏者リョーシャたちを乗せ、夜のモスクワを走るタクシー。ところが運賃を踏み倒されてしまい、怒った運転手のシュリコフは、なんとか彼を見つけ出した。リョーシャと取引をし、モスクワのアンダーグラウンド世界に足を踏み入れるようになっていくシュリコフ。ドラッグやアルコールに溺れながらも、やがてアメリカで成功を手にするリョーシャ。だがシュリコフは、相変わらずつまらぬタクシー運転手として夜のモスクワを流している……。映画の世界でもペレストロイカの浸透には目を見張るものがあるが、この作品もまた、ペレストロイカがなければ存在しない映画であっただろう。ソビエトのブラックマーケットに大胆に迫った映像や、現実のモスクワを切り取った迫真の構図などが、センセーショナルな話題を呼んだ。", "1990-01-01", "12,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラストダンス・離婚式", "ラストダンス・離婚式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "離婚を決意した様々なカップルが繰り広げる笑いと涙と愛のドラマ。TVの企画番組“離婚式”のために集まった100組の夫婦を乗せて豪華客船が10日間の船旅に出航した。大物俳優夫婦、正彦とよしみを追って乗り込んできた若手女性記者、香里はスクープを狙うが、番組のディレクター大輔に邪魔されてばかり。いがみ合っていた二人だが、いつしか互いに気になる存在になっていた。香里と大輔、そしてそれぞれに事情を抱えた夫婦は、旅の最後に行われるダンス・パーティーに向けて、次第に心に変化が生じていく。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAR ISLAND", "オーロラ殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "マクリーンの冒険小説『北海の墓場』の映画化。極北のベア島にやって来た国連気象調査隊の一行を襲った、姿なき殺人者。それは、Uボート基地に隠された金塊を狙うナチの残党だった……。氷塊と雪に閉ざされた美しい風景が、サスペンス・ムードを盛り上げる。", "1979-01-01", "10,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名市場", "悪名市場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "清次はある詐欺師によってムショ送りに。朝吉がその詐欺師を追って四国の港町に単身乗り込むと、そこにはニセの朝吉がいた。そしてそのニセモノを操っていたのが例の詐欺師だったのだ。朝吉は仮出所で駈けつけた清次とともに怒りを爆発させる……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハンサム★スーツ", "ハンサム★スーツ", "", "", "http://image.eiga.k-img.com/images/movie/53547/315x210.jpg?2550", 115, "人気放送作家の鈴木おさむが脚本を手がけ、CM・PV出身の英勉が長編映画初監督を務めたコメディ。定食屋を営む心優しいがブサイクな啄郎は、新人アルバイトの寛子に勇気を出して告白するが、振られた挙句に店も辞められてしまう。落ち込む琢郎は、紳士服屋で着るだけでハンサムになれるというスーツを手に入れ……。普段の琢郎をお笑いコンビ「ドランクドラゴン」の塚地武雄が演じ、ハンサムになった琢郎に谷原章介が扮する。", "2008-11-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Feast III: The Happy Finish", "フィースト3 最終決戦", "", "", "http://image.eiga.k-img.com/images/movie/54527/315x210.jpg?2181", 80, "殺戮モンスターと人間との壮絶な闘いを描いた大ヒット・ホラー「ザ・フィースト」の第3弾。モンスターの襲撃によってゴーストタウンと化したテキサスの街。スクールバスで街からの脱出を図るもモンスターに囲まれてしまった生存者たちの前に、モンスターを追い払うことのできる謎の男“預言者”が現れる。彼に導かれ地下の安全地帯へと逃げ込んだ生存者たちだったが、そこには更なる恐怖が待ち受けていた……。", "2009-07-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABATJOUR", "令嬢チャタレイ ～愛の遍歴～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "今回登場するのは、あの“チャタレイ夫人”の娘カミラ。大学の卒業論文を書くために、イタリアの田舎町を訪れたカミラは、年老いた伯爵や親友の婚約者らを性の虜にし、愛の遍歴を重ねていく。文芸の香り漂う官能の世界を、母親に負けない激しい愛欲シーンを盛り込んで描く。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SONG OF THE SOUTH", "南部の唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ディズニー・プロが、アニメと実写の合成によって作り上げたファンタジー映画。近所の子供たちに、民話を話すリーマスおじさんの物語が中心。見ものは出演俳優がアニメの動物たちと一緒に歌い踊る“ライヴ・アクション”シーン。主題曲『ジッパ・ディー・ドゥー・ダー』がオスカー受賞。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OF MICE AND MEN", "二十日鼠と人間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "文豪スタインベックの原作を、真正面から映像化した、感動の物語。ニューヨーク演劇界の俊英G・シニーズが、旧友J・マルコヴィッチと共演するとともに演出を手掛けている。1930年代、不況風で荒れるアメリカの農村地帯を流れ歩くレニーとジョージ。体は大きく力持ちだが子供並みの知能しか持ち合わせていないレニーにとって、頭の回転が早くしっかり者のジョージは、保護者的存在だった。二人はやっとの思いでとある農場に落ち着くが、安息もつかの間、レニーが雇い主の若妻を図らずも絞め殺してしまい……。子供のように純粋な大男と、彼を慈しみ面倒をみる友人の堅固な友情。純粋さゆえの悲劇が涙を誘う。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Devil is a Woman", "スペイン狂想曲", "", "", "http://image.eiga.k-img.com/images/movie/45997/315x210.jpg?4281", 79, "名匠ジョゼフ・フォン・スタンバーグと女優マレーネ・ディートリッヒの名コンビが、ピエール・ルイスの「女と人形」を映画化したラブ・ストーリー。カーニバルに沸くスペインで、アントニオは美しく妖艶な女性コンチャに一目ぼれしてしまう。酒場で出会った男の忠告を無視し、コンチャの虜となってしまったアントニオだったが……。", "2009-09-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mesrine: Part 2 - Public Enemy No.1", "ジャック・メスリーヌ　フランスで社会の敵（パブリック・エネミー）No.1と呼ばれた男 Part.2 ルージュ編", "", "", "http://image.eiga.k-img.com/images/movie/54837/315x210.jpg?2627", 133, "伝説の強盗ジャック・メスリーヌの晩年を描いた2部作の後編。カナダから欧州に舞い戻ったメスリーヌは、再び強盗を働き、投獄される。獄中の手記を自伝として地下出版し、その後すぐに脱獄、強盗と、“社会の敵 No.1”の生活を謳歌していたメスリーヌだったが、彼の悪評を立てたジャーナリストを拉致監禁したことで世間から集中砲火を浴びると、潮時を感じ、恋人シルビアに宛てた遺言を録音する……。共演にリュディビーヌ・サニエ、マチュー・アマルリック。", "2009-11-07", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五人の兄妹", "五人の兄妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "島津保次郎門下生として映画界でキャリアを開始した吉村公三郎の第５作。脚本は同じく島津門下の木下惠介で、これが彼の脚本としては初めて映画化されたものとなった。選挙違反で父親が自殺した長男・健一郎が、残された母や弟妹のために一所懸命働き、やがて弟妹たちも成長するが……。典型的な松竹家庭劇。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血汐笛", "血汐笛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "旗本の娘・由香に将軍家からお墨付きが下される。大納言に呼び出された旗本はそれを拒み、刺客たちに殺されそうになるが、現れた笛吹き天狗によって救われる。一方、由香も大納言の罠にかかり連れ出されるが、こちらはきらら主水によって助けられる。由香の家で顔を合わせた二人は対決しようとするが……。柴田錬三郎の人気新聞小説を映画化した娯楽時代劇。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Suspicion", "断崖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "フランシス・アイリスの小説「犯行以前」を、巨匠アルフレッド・ヒッチコックが映画化した犯罪スリラー。一目惚れでプレイボーイのジョンと結婚した富豪の令嬢リナだったが、結婚後にジョンが一文無しの男だと知り驚く。その後不審な行動が多くなっていくジョンに対し、リナは恐れを抱くようになる……。プレイボーイのジョンにケイリー・グラント。リナにはジョーン・フォンテイン。42年のアカデミー賞ではジョーン・フォンテインが主演女優賞を受賞した。", "1947-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女王蜂の逆襲", "女王蜂の逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "鬼怒川温泉の元湯の利権をめぐって桜組の珠美と無鉄砲の政が活躍する。“女王蜂”シリーズは田口哲監督による第１作「女王蜂」、石井輝男監督による「女王蜂の怒り」と「女王蜂と大学の竜」が作られたが、新東宝の崩壊とともにシリーズも消滅した。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE FILMS", "BLUE FILMS", "", "", "http://image.eiga.k-img.com/images/movie/54657/315x210.jpg?4141", 88, "大槻ケンヂの綴った半自伝的な散文詩「なつみさん」をもとにした「NATSUMISAN」や、実在のガールズ・パンクバンドの“穴奴隷”の成功を描く「穴奴隷伝説」ほか、「LIPS」「ANUS」「しょじょをかる」「スタジオワーク」「粥バーの夏」「強化合宿」の8本の短編で構成される官能的なオムニバス映画。", "2009-06-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GO", "go", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「スウィンガーズ」のD・リーマンによる青春コメディ。家賃を払うために麻薬売買に手を出す少女ロナ、友人と一緒に行ったラスベガスで犯罪組織に追われるハメになる青年サイモン、そして、麻薬取引きのおとり捜査をさせられる昼メロの俳優アダムとザック。次々とトラブルに巻き込まれていく彼らのクリスマス・イヴをオムニバス風の３部構成で綴る。奇妙だけど妙にリアルなシチュエーションやセリフの数々に思わず笑いがこみ上げる。別々の場所で全く違う問題を抱えていた彼らが、やがて一つに絡み合っていくストーリー展開も秀逸。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぴぐれっと", "ぴぐれっと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ハンデを持つ子供の母親たちが、仲間を集めて20年前に始めた地域作業所“ぴぐれっと”の軌跡を追うドキュメンタリー。横浜市でボランティアによって始まった作業所が、家族や地域の人々に支えられながら、広い範囲にわたって様々な活動を展開していく。監督は「奈緒ちゃん」「見えない学校」等の自主映画を手掛けてきた伊勢真一。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OSS 117: Le Caire, nid d'espions", "OSS 117 私を愛したカフェオーレ", "", "", "http://ecx.images-amazon.com/images/I/51U0BpL-EIL._SL210_.jpg", 0, "フランスで1950年代～60年代に製作されて人気を博した「OSS」シリーズを復活させたスパイ・アクション・コメディ。1955年のエジプト。フランスの諜報部員OS117は行方不明になった友人の謎を探るため、スエズ運河の利権をめぐって各国のスパイが暗躍するカイロに潜入する。ところが、イスラム文化への無知が仇となって様々な騒動を巻き起こしてしまい……。コメディ俳優ジャン・デュジャルダンが主人公を好演。2006年・第19回東京国際映画祭で最高賞の東京サクラグランプリを受賞した（映画祭上映タイトル「OSS 117 カイロ、スパイの巣窟」）。", "2006-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きな子　見習い警察犬の物語", "きな子　見習い警察犬の物語", "", "", "http://image.eiga.k-img.com/images/movie/55177/315x210.jpg?3719", 0, "警察犬を目指すラブラドール・レトリーバー犬のきな子と、見習い訓練士の川西智紗さんの実話をもとに映画化。主演は「天然コケッコー」の夏帆、共演に寺脇康文、戸田菜穂、山本裕典、遠藤憲一ほか。警察犬訓練士を目指す杏子は、入所した訓練所で子犬のきな子と出会う。きな子は体が弱く、警察犬になれる見込みがないと所長に言われるが、杏子ときな子はパートナーとなり、警察犬試験に向けて奮闘の日々が始まる。しかし杏子は、試験に何度も失敗するきな子と未熟な自分を責め、きな子との別れを決意する……。", "2010-08-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イン・ザ・プール", "イン・ザ・プール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "『トリビアの泉』などのTV番組に携わり、映画やドラマの演出も手掛ける三木聡監督が、直木賞作家、奥田英朗の同名ベストセラーを映画化。悩める現代人を不思議と癒す傍若無人な精神科医の活躍をユーモアたっぷりに描く。主人公に扮する松尾スズキがはまり役で、その怪演が爆笑を呼ぶ。“強迫神経症”の涼美は、伊良部総合病院の神経科を訪れる。だが、そこの主治医、伊良部は医者とは言い難い滅茶苦茶な人物で……。", "2005-05-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("荒獅子", "荒獅子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "嵐寛寿郎のチャンバラを十分に堪能できる娯楽時代劇。直参旗本の息子、岩瀬新十郎は、継母の生んだ義弟・小太郎との家禄争いからすすんで身を引き、今は無頼の徒となっていた。しかし、奉行となった父の危機と、小太郎の行方不明という事件が重なり、新十郎は、父と弟を救うために立ち上がる。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEMPORADA DE PATOS", "ダック・シーズン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "スタイリッシュでユーモラスなメキシコ産コメディ。ある日曜日に、アパートの一室に集まるハメになった人の少年少女＆ピザ配達のオッサンの、不思議な交流を描く。世界の映画祭で絶賛を浴びたF・エインビッケ監督のセンスが光る。14歳のフラマとモコは親友同士。二人はピザを注文するが、30分以内に届かなかったことを理由に支払いを拒否。キレた配達員のウリセスは、代金をもらうまで動かないと玄関に居座って……。", "2006-05-13", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("轟轟戦隊ボウケンジャー THE MOVIE 最強のプレシャス", "轟轟戦隊ボウケンジャー THE MOVIE 最強のプレシャス", "", "", "http://image.eiga.k-img.com/images/movie/33597/315x210.jpg?2740", 33, "人気テレビシリーズ「轟轟戦隊ボウケンジャー」の劇場版。巨大な岩山とともに出現した謎の美少女ミューズ。ボウケンジャーたちは彼女が放ったメッセージを受け、強大な力を持つ古代の秘宝プレシャスを守るため岩山へと向かう。時を同じくして、世界征服を企む悪の組織ネガティブ・シンジケートや、伝説の未確認生物ハンターでボウケンレッドの父でもある明石虹一も動き出していた……。日本が誇るアクションスター倉田保昭が虹一を演じる。", "2006-08-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRINCE：SIGN O’ THE TIMES", "プリンス／サイン・オブ・ザ・タイムス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "時代の寵児となったプリンスの「…パープル・レイン」「…アンダー・ザ・チェリー・ムーン」に続く劇場用映画第３作だが、前２作がドラマだったのに対しこれは初のコンサート・フィルム。1987年に発表した二枚組同名アルバムに合わせて行われた、ヨーロッパ・ツアーが中心に収められている。プリンス・ファミリーのシーラ・Eが強烈なパーカッショニストぶりを発揮するほか、セクシーな踊りと歌を披露するキャットなど、プリンスのまわりを彩るミュージシャンたちのパフォーマンスも見どころ。またS・イーストンとの共演ナンバーも一曲だけ収録されている。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人生劇場・飛車角と吉良常", "人生劇場・飛車角と吉良常", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1936年に「人生劇場（青春編）」を映画化し、成功させた日活で足場を固めた内田は、以後名作・力作を続けて送り出し、日本を代表する監督の一人になっていくが、今回は原作の第３部にあたる「残侠編」を中心とした映画化。当時、隆盛をきわめていた東映任侠ヤクザ映画の１本として企画されたものを、内田は情感あふれる格調ある作品に仕上げた。ラストの殴り込みのシーンは、東映ヤクザ映画のセオリーだが、脇役に至るまで魅力的な登場人物を配し、出演者の個性を生かした演出で巨匠の本領を見せつけた。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心霊病棟　ささやく死体", "心霊病棟　ささやく死体", "", "", "http://image.eiga.k-img.com/images/movie/56917/315x210.jpg?2809", 62, "「自殺マニュアル」「こわい童謡」ではメガホンをとり、「渋谷怪談」では原作・脚本を務めるなど、ホラー界でマルチに活躍する福谷修監督が病院を舞台に描く心霊ホラー。新人看護師の可奈は院内の霊安室に安置されている遺体が「助けて」とうめく声を聞いてしまう。精神科の主治医・頼子から、遺体は病棟で起きた殺人事件の被害者で、いまだに事件の犯人は見つかっていないと教えられるが、ある日頼子の机の引き出しから白いタオルに包まれた血まみれの電気メスを発見してしまい……。", "2011-09-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フツーの仕事がしたい", "フツーの仕事がしたい", "", "", "http://image.eiga.k-img.com/images/movie/53857/315x210.jpg?2990", 70, "厳しすぎる労働条件に限界を感じて労働組合に助けを求めた男性が“フツーの仕事”を手に入れるまでの闘いを追い、現代社会の闇を浮き彫りにしたドキュメンタリー。1カ月の労働時間は最長552時間、その一方で賃金が引き下げられるという過酷な労働状況に身を置くセメント輸送運転手の皆倉信和は、すがるような気持ちで労働組合に加入する。そんな彼を待ち受けていたのは、組合脱退を求める会社からの執拗な脅迫だった。", "2008-10-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン 11人目のストライカー", "名探偵コナン 11人目のストライカー", "", "", "http://image.eiga.k-img.com/images/movie/57537/315x210.jpg?3688", 110, "青山剛昌原作コミックの人気アニメーション劇場版第16作。ある日、毛利探偵事務所に謎の脅迫電話がかかり、所長の毛利小五郎が見ている目の前で路上の自動車が爆発。犯人が突きつけた暗号を解読しなければ、再び爆発事件が発生するという。一方、大好きなサッカー観戦を楽しんでいた江戸川コナンは、小五郎の娘・蘭から事件の知らせを受け、その場で暗号解読に乗り出すが、そこで恐るべき真実を知ることになる。Jリーグとのコラボレーションにより、現役Jリーガーの三浦和良（横浜FC）、遠藤保仁（ガンバ大阪）、今野泰幸（FC東京）、中村憲剛（川崎フロンターレ）、楢崎正剛（名古屋グランパス）が本人役でゲスト出演する。", "2012-04-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パンダ・コパンダ(1972)", "パンダ・コパンダ(1972)", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 33, "東映動画を離れたあと高畑勲、宮崎駿ら、精鋭スタッフが手掛けた珠玉作。当時のパンダ・ブームを反映したオリジナル・メルヘンで、動物園を脱走した父親パンダと子供パンダが巻き起こす珍騒動を描いている。脳天気が極まって一種不気味な、パパンダ（＝父親パンダ）のキャラクターが傑作。竹林の中の家で、祖母を旅行に送り出し、一人暮すミミ子。幼いけれども元気な彼女は、突然現れたパンダの親子を迎え入れ、お母さん気取りで世話を始める。そのころ動物園から逃げ出した２頭を、人々が探していた……。この続編でほぼ同じスタッフによる「パンダ・コパンダ 雨ふりサーカスの巻」（38分）が、1973年に製作された。こちらもファンタスティックな佳作。DVDは「パンダ・コパンダ　雨ふりサーカスの巻」を併録。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽と星", "太陽と星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "伯母の経営するそば屋で働くために上京して来た剛と武の兄弟。しかし剛はある縁で運輸会社で働くことに。一方武はかおるという足の不自由な少女と知り合い、仲間との賭けから彼女を誘惑する。かおるは運輸会社で働く安田の妹で、面倒見のいい剛はかおるの足を治そうとするのだった。その頃、武の優しい思いやりが賭けのためだったことをかおるは知る。波乱の展開を見せるラブ・ストーリー。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お父さん、元気？", "お父さん、元気？", "", "", "http://image.eiga.k-img.com/images/movie/55037/315x210.jpg?4698", 107, "1999年に発表した「最愛の夏」で第12回東京国際映画祭グランプリをはじめ他3部門に輝いた台湾出身のチャン・ツォーチ監督がメガホンを取り、父親をテーマにした10本のショートフィルムからなるオムニバス作品。ノスタルジックな街並や海辺といった台湾の美しい景観をバックに、様々な父と子が織りなすドラマを穏やかなタッチで綴っていく。第14回釜山国際映画祭、第10回東京フィルメックス上映作品。", "2009-11-24", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奇謀妙計／WINNERS ＆ SINNERS", "五福星／　THE ５ LUCKEY STARS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "香港映画界では名実ともにNo.1といわれるキンポーが、監督・脚本・主演を兼ねたバイタリティーあふれるアクション映画。警官と５人の詐欺師がひょんなことからマフィアと対決するはめに……。警官役をジャッキーがユーモアたっぷりに演じている。“福星”シリーズ第１作で、「大福星」「七福星」「十福星」と続く。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAST TRAIN FROM GUN HILL", "ガンヒルの決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "妻を殺した犯人を追ってガンヒルにやって来たマットは、その犯人が旧友の息子と知って愕然とする……。「OK牧場の決斗」「ゴーストタウンの決闘」と並ぶスタージェスの決闘３部作の一編。２大スターの強烈な個性の激突が見事。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほかいびと　伊那の井月", "ほかいびと　伊那の井月", "", "", "http://image.eiga.k-img.com/images/movie/57797/315x210.jpg?3876", 119, "信州伊那谷を中心に活動した江戸末期の俳人・井上井月（いのうえせいげつ）の生涯をドキュメンタリーとフィクションを交えて描く。信州の伊那谷にふらりとやってきた井月は、幕末から明治にかけておよそ30年、家も家族ももたず放浪生活を続ける。宿を借りた家にはお礼に句を置き、伊那の自然や風土、生活を詠み続けた井月の姿を、残された1800の句や逸話、日記の断片などから描き出していく。井月に扮するのは舞踏家・俳優の田中泯。", "2012-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDERWORLD", "暗黒街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "アル・カポネが暗躍した禁酒法下のシカゴを舞台に、ギャングたちの姿を真正面から描いてフィルム・ノワールの草分けとされる傑作。一匹狼を気どる悪漢ブルは、金庫破りの現場を酔っぱらいのロイスに目撃されたが、なんとなくこの男が気に入って逃がしてやった。ある時、酒場で彼は、このロイスを笑い者にし、親分風を吹かす乱暴者マリガンに盾を突く。あげくにはマリガンが自分の情婦フェザースに手を出すにおよんで、彼は発作的に殺してしまう。死刑を宣告されたブルはすぐさま脱獄して女の家に急ぐが、到着するなり警官隊に包囲されてしまった。ロイスとフェザースがいつしか愛し合っていることに気付いた彼は、二人を逃がすため警察への投降を決意する……。巨匠J・V・スタンバーグ監督の出世作となった無声映画。トーキー前夜の製作ということもあり、サイレント演出の完成度はきわめて高い。とりわけ構図の美しさが特筆に値する。", "1927-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLANKMAN", "ブランクマン フォーエヴァー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "テレビ局で働くケビンには発明好きの弟ダリルがいる。ある日、悪名高きギャングのミネリー一味が市長の事務所を襲撃し、そこで働いていた彼らの祖母が殺されてしまう。子供の頃から“バットマン”に憧れていたダリルは真紅の衣装に身を包んだブランクマンに変身。珍発明の数々を駆使して悪の征伐に乗り出す……。ニューヒーロー・ブランクマンの活躍をパロディ満載に描いたアクション・コメディ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋しくて", "恋しくて", "", "", "http://image.eiga.k-img.com/images/movie/33927/315x210.jpg?1284", 99, "「ホテル・ハイビスカス」の中江裕司監督がBEGINのエッセイ集「さとうきび畑の風に乗って」にヒントを得て描いた青春映画。沖縄・石垣島の高校に入学した加那子は、しばらく顔を合わせていなかった幼なじみの栄順と再会する。ある日、加那子の兄セイリョウが突然バンドを結成すると言い出し、栄順がボーカルを担当することに。最初は渋々歌っていた栄順だったが、加那子の励ましのかいあって次第にバンド活動に熱中していく。", "2007-04-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RENEGADES", "レネゲイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「ヤングガン」で共演した若手スターの顔合わせによる青春アクション。上層部の汚職を知った若手警官と、兄を殺されたインディアンの青年、運命的にめぐり会い、共通の敵に向かって戦いを挑む二人の姿がハードなタッチで描かれていく。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽を盗んだ男", "太陽を盗んだ男", "", "", "http://image.eiga.k-img.com/images/movie/37677/315x210.jpg?3512", 147, "中学校の孤独な物理教師、城戸誠は東海村の原子力発電所からプルトニウムを強奪、アパートの自室で原爆製造に成功する。TVのプロ野球中継を終わりまで見せろと政府を脅迫し、ラジオ番組を通じローリング・ストーンズの日本公演を要求するが不可能で、ついには５億円の要求にエスカレート。国家を敵に回しての孤独な戦いを始めるが……。バスジャック、原発襲撃、カー・チェイス、警察の手に渡った原爆の奪回と息もつかせぬ見せ場が連続し、クライマックスでは屋上で大活劇！ 毒のある、壮大な娯楽アクション映画となっている。沢田研二が孤独でニヒリスティックな青年を好演している。", "1979-10-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENTER THE PHOENIX", "エンター・ザ・フェニックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "犯罪稼業を嫌うマフィアの御曹司と、ギャングに憧れるその友人が巻き込まれる騒動を描いたコメディ。笑いはもちろん、思わず涙するハートフルな展開も魅力的だ。暗黒街を嫌い、家出したマフィアの息子ジョージ。10年後、跡取りを探しにきた父の手下が、マフィアに憧れている彼のルームメイト・サムをジョージとカン違い。ジョージはそれを黙認し、サムに付き添うが……。人気スターがカメオ出演で多数登場。", "2006-06-03", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴力金脈", "暴力金脈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "東映実録ヤクザ路線の１本。いつもの暴力団と異なり、今回は総会屋を物語の中心に据えて、暴力と謀略渦巻く人間ドラマを描く。一匹狼の総会屋・中江は暴力団の若衆と手を組み東京へ進出、企業の内部紛争に紛れ込み、大総会屋と対決する。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("洞", "Hole", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "台湾の映画作家T・ミンリャンの長編第４作。舞台となるのは、西暦2000年まで７日に迫り、ゴキブリ症候群と呼ばれる謎の奇病が蔓延した都市の一角。人気の少ないマンションに住む男の部屋の床に水道工事による穴があき、階下に暮らす女の部屋との空間が通じ合ったことから、二人の奇妙な交流が始まる。全編に豪雨が降りっぱなしという陰鬱な世界観を築きながらも、一つの穴ボコを媒介とした男女のやりとりは場違いなユーモアがたっぷり。往年の香港の人気歌姫グレース・チャンのナンバーをフィーチャーしたミュージカル・シーンが随所に挿入され、この不思議な幸福感が漂うファンタジーを軽快に彩っている。", "1998-01-01", "53,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAREFOOT IN THE PARK", "裸足で散歩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ブロードウェイで大ヒットしたN・サイモンの戯曲の映画化。夢のようなハネムーンを終えて、新婚カップルが落ちついた新居は、エレベーターのないビルの最上階という最悪の物件だった……。夫婦ゲンカの可愛い顛末を、R・レッドフォード、J・フォンダの好演で、シャレたコメディに仕上げている。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和おんな博徒", "昭和おんな博徒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "組織の若頭として将来有望なヤクザだった夫をライバルに殺された女・お藤は、自らも女賭博師となり各地を遍歴、夫を殺した男たちを追って復讐を果たしていく……。“緋牡丹博徒”シリーズでも有名な加藤泰が、“女賭博師”シリーズでスターとなった江波杏子を使って撮り上げたヤクザ版「五瓣の椿」。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・唐獅子株式会社", "新・唐獅子株式会社", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「Let's豪徳寺！」以来、11年ぶりの前田陽一監督作品だが、撮影中に監督は急逝。残った部分はチーフ助監督の長濱英高と、前田の助監督を長く務めた南部英夫が協力監督という形で進め、完成させた。原作は、横山やすし主演作でも知られる小林信彦のユーモア小説。６年の刑期を終え出所した黒田を待っていたのは、様変わりした二階堂組だった。時代の影響で多角経営に乗り出した組はケーブルテレビの経営も手掛けており、黒田は局長に就任し、高視聴率番組を目指して奮闘を開始。そんな折、地域密着番組宛てに“ヤクザ狩り”を予告する電話が入り、組員が次々に血祭りに上げられていくが……。ひょうひょうとしたコメディが一筋縄ではいかないサスペンスへ、ゆっくりと変貌。ボケと狡猾さが同居する黒田のキャラクターが効果的で、赤井英和にハマっている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Island at the Top of the World", "地球の頂上の島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "イアン・キャメロンによる冒険小説「呪われた極北の島」を映像化したディズニー製アドベンチャー映画。20世紀初頭。大富豪ロス卿は、北極圏で行方不明になった息子ドナルドを探そうと、考古学者アイバーソンらと共に飛行船に乗って旅に出た。やがてたどり着いた“世界の頂上の島”は、バイキングの子孫たちが11世紀もの間、世界から隔絶されたまま暮らしている“失われた楽園”だった。上陸した一行はバイキングに捕らえられるが……。", "", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗黒の旅券", "暗黒の旅券", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "鈴木清順のB級アクション・スリラー。トロンボーン奏者、伊吹の新妻がハネムーンの列車内で姿を消した。絶望し、泥酔して家に戻った彼の前に、妻の絞殺体が……。刑事の“君の女房はヘロイン中毒だね”との言葉に、伊吹は誓った。復讐に命を賭けることを……。白木マリと筑波久子の二大グラマー初共演。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MU¨LLER’S BU¨RO", "ミュラー探偵事務所", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "秘書は赤毛、デスクの引き出しにはスコッチのボトルと、気分は、サム・スペードながら、マトモな仕事とはごぶさた続きの探偵ミュラー。しかし、久しぶりの依頼人は“イングリッド・バーグマン”と名乗るブロンド美女。すっかりノリ気になったミュラーだが、新聞にはたった今帰ったばかりの依頼人の死亡記事が載っていた……。ストーリーも主人公もハードボイルド・タッチなのに、登場人物が突然歌い始めたりする珍品コメディ。女がベッドで悶えつつ歌っていたり、ギャングが踊りながら死んだりとヘンな（？）シーンが続出。", "1986-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The First Rains of Spring", "春、一番最初に降る雨", "", "", "http://image.eiga.k-img.com/images/movie/57347/315x210.jpg?2820", 85, "", "2011-01-01", "76,51", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES SOEURS BRONTE¨", "ブロンテ姉妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「嵐が丘」「ジェーン・エア」「アグネス・グレイ」と世界文学史に不朽の名を残したブロンテ３姉妹の生涯。19世紀のイギリス・ヨークシャーの暗く風の吹きすさぶ土地で、生涯のほとんどを過ごした３姉妹の内に秘められた情熱を淡々とした描写の中に描く。19世紀の絵画を思わせる映像が美しい。", "1978-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本暗殺秘録", "日本暗殺秘録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 142, "桜田門外の変の大老、井伊直弼暗殺から二・二六事件まで、江戸～昭和の歴史を血に染めた数々の暗殺事件をドラマ化した異色作。先の二つ以外に取り上げられているのは、大久保利通暗殺事件、ギロチン社事件、血盟団事件など。“仁義なき戦い”シリーズに取り組む以前の笠原和夫が、脚本に参加している。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エコエコアザラク II　BIRTH OF WIZARD", "エコエコアザラク II　BIRTH OF WIZARD", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "人気同名漫画の映画化第２作。主人公・黒井ミサの出生の秘密を、SFXを駆使した映像で明らかにしていく。明治13年、魔術を操る斎呀一族は凶悪な魔女・霧江の大量虐殺により消滅した。そして現代、斎呀一族の村と思われる廃村から霧江のミイラが発見される。彼女は100年の封印から息を吹き返し、強力な魔術パワーを秘めた黒井ミサの肉体を乗っ取ろうと動き始める。霧江はミサの身近な人物に乗り移りミサを襲うが、間一髪のところを斎呀一族の生き残りに助けられる。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE EIGER SANCTION", "アイガー・サンクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "標高3975メートル、アルプスのアイガー北壁。登頂に２度失敗、今は山小屋で絵の教師をするジョナサン・ヘムロックはかつてプロの殺し屋だった。その彼にCIAから極秘依頼が……。監督、主演のイーストウッドがスタントなしの熱演を見せるスパイ・アクション。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トーテム Song for home", "トーテム Song for home", "", "", "http://image.eiga.k-img.com/images/movie/54737/315x210.jpg?3077", 85, "「星影のワルツ」で国際的に高い評価を受けた若木信吾監督が、台湾原住民の若手ミュージシャンたちの活動を記録したドキュメンタリー。台湾には政府から承認されているだけでも14の部族が存在し、古くからの風習を守り続ける一方で、若者たちは仕事を求めて都会へと移り住んでいる。そんな若者たちが台北で結成したバンド「トーテム」にスポットを当て、都会での生活と故郷への想いの間で揺れ動くメンバーたちの姿を映し出す。", "2009-10-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HARD WORD", "トエンティマン・ブラザーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "実力派俳優ガイ・ピアースがオーストラリアで主演したクライム・サスペンス。大胆極まりない完全犯罪を繰り返す３兄弟の奮闘を、スリルとユーモアたっぷりに描く痛快娯楽作だ。刑務所をアリバイ作りに利用する盗みのプロ、ダイル、マル、シャインの３兄弟。彼らを利用する悪徳弁護士フランクの策略で最後の大仕事に挑むハメになった彼らは、競馬場から史上最高額の大金を強奪しようとするが……。", "2002-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("K-Pax", "光の旅人", "", "", "http://image.eiga.k-img.com/images/movie/1487/315x210.jpg?2816", 121, "自分を異星人だと主張するプロートは、詳細に故郷の星を語り、また精神病院の患者たちの閉ざされた心を開いていく。彼は本当に異星人なのか。彼と出会った精神科医は悩む。監督は「バック・ビート」「サイバーネット」のイアン・ソフトリー、脚本は「マイ・フレンド・メモリー」のチャールズ・リービット、撮影は「愛の悪魔／フランシス・ベイコンの歪んだ肖像」のジョン・マシソンが担当。", "2002-04-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ELECTRA GLIDE IN BLUE", "グライド・イン・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "アメリカの片田舎に住む若い警官の、小さな夢とその挫折を描く青春映画。一介の白バイ警官では飽きたらず、殺人課の腕利き刑事になりたがっているジョンと、最高級バイクのエレクトラ・グライドを手に入れたいと願うデイビスの管轄内で、珍しく殺人事件が起きた。ジョンは自らの野心のためにこの機を利用しようとするが……。ロック・バンド、シカゴのプロデューサーだったJ・W・ガルシオが製作、監督を担当している。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FEST DER SCHO¨NHEIT／OLYMPIA TEIL II", "美の祭典（オリンピア第二部）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「民族の祭典」と並んで“オリンピア”２部作を形成する1936年のベルリン・オリンピックの記録映画。オリンピック村のスケッチから始まって、体操、ヨット、フェンシング、ボクシング、近代五種、ホッケー、陸上競技など各競技の高度な技術の競り合いを、華麗なカメラワークで捉える。第１部「民族の祭典」とともにベルリン映画祭作品賞（ムッソリーニ杯）を受賞。", "1938-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE INSPECTOR GENERAL", "ダニー・ケイの検察官閣下", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "D・ケイの作品としては中期に続するもので、ニコライ・ゴーゴリの著名な原作を題材に、ミュージカル仕立てにしたもの。ロシアの田舎町を薬の行商で回っている男ゲオルギーが、ひょんなことから検察官に間違えられて大騒動が巻き起こる。ケイ得意の早口の唄もたっぷりサービス。ギャグもかなり盛り込まれており楽しめる作品となった。市長夫人に扮する名優ランチェスター、恋人役のB・ベイツがお色気を添え、コスターのツボを押さえた演出でテンポ良く進行する。作曲のシルヴィア・ファインはケイの夫人。別題「拝啓、検察官閣下」でもIVCより発売。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画！たまごっち　うちゅーいちハッピーな物語！？ ", "映画！たまごっち　うちゅーいちハッピーな物語！？ ", "", "", "http://image.eiga.k-img.com/images/movie/53877/315x210.jpg?2509", 0, "大ヒットゲーム“たまごっち”の映画化第2弾。たまごっち星のまめっちたちが通うたまごっちスクールに、空飛ぶ移動図書館がやってくる。その図書館では、おとぎっち兄弟が描く“ふしぎな絵本”の中に入り、物語の主人公になれるという。まめっちはそこで感じたハッピーな気持ちをヒントに、街中の幸せを集めて落ち込んでいるひとに届ける“ハピハピっち”を発明するが……。", "2008-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("豪侠", "剣聖たちの挽歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "武門の名家、高家荘で結婚式が挙げられようとしていた。しかし祝いの宴にライバル武門、白家荘の面々が大挙乗りこんできて闘いとなる。花嫁の謀りによって深手を負う高は昔の師のもとに落ち延び復讐を誓うが……。複雑に絡み合う人物像の中でのテーマはやはりウー監督らしい「男の友情」。竹藪の中での闘いなど、キン・フー監督の「侠女」を思わせる演出も。", "", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("都市霊伝説　心霊工場", "都市霊伝説　心霊工場", "", "", "http://image.eiga.k-img.com/images/movie/55307/315x210.jpg?2687", 0, "殺人事件や自殺の名所として知られ、心霊現象の目撃情報も後を絶たない不気味な廃工場に、ホラー映画監督のトヨカワが取材撮影にやってくる。一方、ホラーサークルを率いる恵梨は、大手ホラー出版社から廃工場取材の依頼を受け、肝試しのつもりで取材を引き受ける。副会長の俊郎は、自分に断りなくサークルのメンバーが廃工場へ行ったことを知り、後を追うが、そこで恐ろしい惨劇を目にする。", "2010-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロストガール", "ロストガール", "", "", "http://image.eiga.k-img.com/images/movie/54307/315x210.jpg?3715", 63, "生きることに迷子になってしまった30代女性が再生するまでを描いた人間ドラマ。夫と一緒に小さなレストランを営むシェフの涼子は、店で食中毒事件を起こしたのをきっかけに家に引きこもってしまう。ストレスから摂食障害まで患い、仲の良かった夫とも次第にすれ違うようになった彼女は、大喧嘩の末ついに家を出るが……。キャストには「殯の森」「愛の予感」の渡辺真起子と「リアリズムの宿」の山本浩司ら演技派が揃う。", "2009-02-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG COUNTRY", "大いなる西部", "", "", "http://ecx.images-amazon.com/images/I/51Km1RzZZTL._SL210_.jpg", 167, "巨匠ワイラーが西部の新しい夜明けを描いた格調高い大型西部劇。マッケイは牧場主テリルの娘パットと結婚するため、東部からはるばるテキサスまでやって来る。テリルは、女教師ジュリーが持つ水源地をめぐってヘネシーと対立。一方、テリル家の牧童頭スティーブは、パットを密かに愛していて、マッケイをうとましく思っていた。マッケイはさまざまなトラブルを合理的に解決しようとするのだが……。モロスのテーマ曲にのって荒野を疾走する駅馬車の遠景ショットの幕開けから、最後の壮絶な決闘シーンまで、ワイラーの演出は西部への限りない賛歌にあふれている。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Duel", "激突！", "", "", "http://ecx.images-amazon.com/images/I/41dVzwdhHfL._SL210_.jpg", 90, "「地球最後の男オメガマン」などで知られるSF作家リチャード・マシスンの短編小説を当時25歳のスティーブン・スピルバーグがTV用に監督したサスペンス・アクション。知人から借金を取り立てるためにカリフォルニアのハイウェイを南下していたデビッドは、途中で1台の大型タンクローリーを追い抜く。すると、そのタンクローリーがデビッドに嫌がらせを始める……。日本やヨーロッパでは90分に再編集され劇場公開された。", "1973-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE VOYAGE EN DOUCE", "二人の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "毎日を平凡に送る二人の女たちの、３日間だけの日常からの逸脱（＝旅）を描いてさわやかな印象を残す一編。家庭から、仕事から解放されたヒロインたちの、伸び伸びとした時間が映画の中ではキラキラとした輝きを放っている。監督は「読書する女」など、女性の細やかな心情を描いて演出の冴えを見せるM・ドヴィル。", "1979-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APRIL FOOLS’DAY", "エイプリル・フール／鮮血の記念日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "週末の４月１日、金持ちの家の息子であるマフィに招待されたスキップやキットら９人は、孤島の別荘にやって来た。誰にも邪魔されないところで、その晩は乱痴気騒ぎ。しかし、翌朝からスキップをはじめ７人が次々と姿を消していく……。「13日の金曜日」シリーズのプロデューサー、F・マンキューソJr.が製作、原案を担当したサスペンス・ホラー。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白蘭の歌（総集編）", "白蘭の歌（総集編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "エキゾチックな“満州国美人スター”李香蘭の日本売り出しを狙い、当代きっての二枚目・長谷川一夫を相手役にした恋愛映画。満鉄の日本人青年技師と、声楽を勉強する満州の豪族令嬢の情熱的で悲しい恋を描く。公開するや映画は空前の大ヒット。主題歌は一世を風靡、李香蘭は一躍国際的大スターとなった。だが、彼女は満州国人ではなく実は日本人で、いわば両国間の絆を強めようと政策的に作られた存在だった。けれども、当時真実を明かせるはずもなく、満州国が潰滅した敗戦後にようやく日本名、山口淑子を名乗ることができた。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユリ子のアロマ", "ユリ子のアロマ", "", "", "http://image.eiga.k-img.com/images/movie/55417/315x210.jpg?1605", 74, "デビュー作「お姉ちゃん、弟といく」で、2008年ゆうばり国際ファンタスティック映画祭のオフシアター部門審査員特別賞受賞した吉田浩太監督が、若年性脳梗塞で倒れた後に撮りあげた復帰第1作。主演は「月とチェリー」の江口のりこ、共演に「パンドラの匣」の染谷将太。アロマセラピスト・ユリ子は、サロンオーナーの甥・徹也のすえた汗の匂いにどうしようもなく惹かれていた。一方、指名客のアヤメがアロマセラピーのレッスンをしてほしいとユリ子に迫ってくる。", "2010-05-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初国知所之天皇", "初国知所之天皇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "高校在学中に自主製作した「おかしさに彩られた悲しみのバラード」が各方面で絶賛され、20歳で大島渚の「東京＊争戦後秘話」の脚本を担当するなど、“早熟の天才”と呼ばれた原将人の代表作。もともとは「古事記」を題材にした劇映画として企画されたが、撮影半ばで中断。原は８ミリカメラを手に日本を縦断するロケーションハンティングの旅に出る。北海道から鹿児島までの行脚のなかで、原は映画を作ることこそ神話そのものと思い至る……。1973年に８ミリと16ミリを交互に映写する形態で上映され、ロングランヒットを記録。1975年に再編集された後1994年に２面マルチ版として復活した、まさに伝説の大作。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAT／灼熱", "HEAT／灼熱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "“極道聖戦／ジハード”シリーズの横井健司監督が同名人気コミックを映画化。新宿・歌舞伎町を舞台に、裏社会を自力でのし上がっていくアウトローな主人公が、ヤクザや警察と激しい抗争を繰り広げる。劇画界で圧倒的人気を誇る武論尊・池上遼一による原作の世界観を忠実に映像化したアクションが見もの。歌舞伎町の闇金融を訪れた唐沢辰巳は、無担保で１千万の融資を要求する。社長の伊丹は、型破りな唐沢を見込んで、違法ホストクラブから金を搾取することを提案。やがて、唐沢は裏社会に君臨する実力者となるが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEP ACROSS THE BORDER", "ステップ・アクロス・ザ・ボーダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "クラシックから民族音楽まで、世界のあらゆる音楽から自分のスタイルを創造していくサウンド・クリエイター、F・フリス。彼が世界各地を回り、そこで出会うミュージシャンたちと繰り広げるライヴ・パフォーマンス、そしてそれらの都市、街、人々が持つ表情をモノクロームの映像美の中に描き出した“ボーダーレス時代”を象徴する音楽映画。", "1989-01-01", "26,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("６７歳の風景／　若松孝二は何を見たのか", "６７歳の風景／　若松孝二は何を見たのか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 67, "2004年「完全なる飼育・赤い殺意」で復帰を遂げた若松孝二監督の最新作「17歳の風景…」の制作現場に同行、同監督がいかに映画を形作るかを記録したメイキング・ドキュメント。ときにメイキング映像にも“乱入”してくる若松のバイタリティ、そして不屈の信念がひたひたと感じられる一作だ。67歳の若松孝二が17歳の少年の映画を撮ろうとしたのはなぜか。「17歳の風景…」のロケに同行、極寒の東北でスタッフを率いる人間、若松に迫る。", "2005-08-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JENNY", "ジェニイの家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "M・カルネの長編第１作。パリで評判のナイトクラブを経営する母と、音楽留学先のイギリスから帰って来た娘を主人公に、年下の男を愛人にもつ母、そしてその愛人に恋してしまう娘の、それぞれの葛藤を描いている。脚本はこの後もカルネと次々とコンビ作を発表していくことになるJ・プレベールが、J・コンスタンと共同で担当。脚本の巧みさが際だった作品でもある。", "1936-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大暴れ風来坊", "大暴れ風来坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "四国・松山の瀬川運送は、県から払い下げられる土地の入札資金調達に奔走していた。瀬川は関西の実力者、大田黒に頼むが、彼こそ瀬川の土地を狙う悪漢で、執拗なまでに瀬川の土地を奪おうとする。そんな時、松山に得体の知れない風来坊がやって来る……。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("朝やけ血戦場、鉄火の中", "朝やけ血戦場、鉄火の中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "時は幕末維新の頃。官軍に包囲された城から身重の妻を連れ、逃げ出した孫次郎は、百姓家で官軍偵察隊に捕まり殺されそうになる。ところが、妻の出産で彼と隊の間に一体感が芽生え、百姓家が幕府軍に囲まれた時には、彼も槍をとる。妻に裏切られた過去を持ち孫次郎に嫉妬するものの、最後には赤ん坊のために身を挺する隊士の姿が印象的。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("7TH HEAVEN", "第七天国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "サイレント期のメロドラマのなかでも最上級の名編の一つ。第一次世界大戦前夜のパリ。下水道掃除人の若者シコは、ある日、実姉に路上でムチ打たれている薄幸の娘ディアーヌを助ける。行き場がないという娘を仕方なくぼろアパートに連れ帰った彼は、汚い屋根裏部屋を“７階にある天国”と呼んで娘を笑わせた。二人はやがて恋に落ちて結婚をするが、幸せはつかの間。大戦に召集されたシコの帰りを待つ若妻に、夫の戦死の報が届く……。感傷に流されることなく甘い物語を描き切るボーゼイジ監督の演出はさすが。また“光”が美しく浮き立つ撮影の効果も深い余韻を残す。", "1927-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いつか来た道", "いつか来た道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ウィーン少年合唱団の２度目の来日を記念して作られたコーラス音楽映画にして家族ドラマの快作。幼い弟と妹を抱えたさやは、身障者である弟に生きる自信をつけさせるためバイオリンを習わせる。弟の腕はめきめき上達するが、やがて彼に白血病の診断が下る。そんな時、彼が感動したウィーン少年合唱団の公演が開かれる。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INTO THE WEST", "白馬の伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "製作補佐に個性派俳優G・バーン、脚本には「マイ・レフトフット」の監督J・シェリダンというアイルランド人脈によって生まれた、ファンタスティックな物語。横暴な大人たちに連れ去られた親友同然の白馬を奪い返そうとする、幼い兄弟の冒険が温かい眼差しで描かれる。", "1993-01-01", "33,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刺客列伝", "刺客列伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "西村昭五郎監督によるアクション色の強い任侠映画。将来を誓い合った女郎、おしのの身請け金を作るために、樋口正次郎は松田一家の仕切る石切場の人足として働くことになった。しかし、そこでは監督官らの不正がはびこっており、ついに堪忍袋の緒が切れた正次郎は一家に殴り込みをかける。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABAR，KING　OF　ELEPHANTS", "ババール／ぞうの国の王様〈劇場用特別完全版〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "1931年からフランスで出版され世界中の子供たちに愛され続けている“ぞうのババール”シリーズ。原作絵本の中から『ぞうのババール』『おうさまババール』『ババールのこどもたち』の３冊のストーリーをNHK教育テレビが放送。その映像をノーカット・バージョンとして映画化した。お母さんを亡くして一人ぼっちになったババールは、森を離れ人が大勢住む都会へとやってくる。心優しいおばあさんから生きるために大切なことを学び成長したババールは、象の国を幸せにするために仲間の元へと戻っていく。", "1998-01-01", "10,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HEART IS A LONELY HUNTER", "愛すれど心さびしく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "口も耳も不自由なシンガーは、最愛の友を失い大きな町へと引っ越して来た。下宿先の娘ミックは、初めは打ち解けなかったが、やがて彼と心を通い合わせるようになるのだが……。R・E・ミラー監督の繊細な演出が冴えわたる心に残るラブ・ストーリー。1968年のアカデミー賞でA・アーキンが主演男優賞に、S・ロックが助演女優賞にノミネートされた。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Edward Burtynsky: Manufactured Landscapes", "いま　ここにある風景", "", "", "http://image.eiga.k-img.com/images/movie/53647/315x210.jpg?2693", 87, "産業発展によって変化を強いられた風景の写真を撮り続け、国際的に活躍するカナダ人写真家エドワード・バーティンスキーが、産業発展に伴い劇的に変わっていく中国の景色をカメラに収める姿を追ったドキュメンタリー。映画「長江哀歌」で知られる三峡ダム建設のために壊されていく町並み、世界最速の都市化が進む上海、真っ赤に汚染された河川などを映し出していく。", "2008-07-12", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Emperor of the North （Emperor of the North Pole）", "北国の帝王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "　1930年代の中西部。鉄道に無賃乗車し、あてもなく放浪する男たちがいた。ホーボーと呼ばれた彼らが恐れたのは車掌シャックの汽車。冷酷なシャックはタダ乗りするホーボーに容赦なく襲いかかる。しかしAナンバーワンと呼ばれるホーボーがシャックへの挑戦を宣言。やがてふたりは列車上で激突する！　不況の嵐が吹き荒れる時代を背景に、名匠アルドリッチ監督が男同士の闘いをエネルギッシュに描く。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE MO", "リトル・モー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "10歳の少女モーリンは、才能を認められ、アメリカ屈指のコーチ、ティーチのもとでテニス指導を受けることになる。やがて世界への第１歩であるウィンブルドンの地を踏むことになった彼女だが、その大事な試合でケガをし、出場をめぐってティーチと袂を分かつ。その後引退し、幸せな結婚をしていた彼女の前に、突然ティーチが現れ……。テニスに夢を託した少女リトル・モーの成長記。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／LAST　PRESENT", "ラスト・プレゼント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "主演女優のイ・ヨンエが脚本を読んで顔が腫れ上がるほど涙し、出演を熱望したといわれる切ないラブ・ストーリー。才能はあるのに運に恵まれず、なかなか芽の出ないコメディアン、ヨンギと、彼を明るく気丈に支える健気な妻ジョンヨン。ある日、ヨンギはTVの勝ち抜きお笑い番組で順調に勝ち進み、大きなチャンスを目の前にする。夢中で仕事に取り組む彼だったが、時を同じくして妻が不治の病にかかっていることを隠していると知り……。韓国では100万人を超える観客動員を記録したヒット作。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREAKTHROUGH", "掠奪戦線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "第二次大戦末期、科学者の頭脳掠奪戦が展開する戦争サスペンス。連合軍はドイツ軍の高性能ロケットに苦しめられ、発明者誘拐を企てた。米軍のコマンド部隊は単独でこれに成功するが、計画にはソ連も加わっていたため、独ソ両軍に追われる。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KUMISO—組葬", "KUMISO—組葬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "極道の葬式をユーモラスに切り取った異色のヤクザ映画。急逝した伊崎組組長の葬儀準備委員長に抜擢された幹部の松田。未亡人や幹部間の揉め事をまとめつつ、葬儀の日取りを決めた彼だったが、そんな折、他組織との関係が悪化。一触即発の緊張のなか、松田は命を懸けて組葬を行おうとする……。スゴみに頼らない、人間くさいヤクザ像がユニーク。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STEALING HOME", "君がいた夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "マークが少年の頃いつも一緒にいた、６歳年上の従姉が自殺した。遺書には、遺骨の安置場所をマークに託してほしいと記されていた。一時は野球の天才バッターとして華々しいデビューを飾ったこともあるマークも、今は怠惰な生活を送ってうらぶれていた。そして彼は、従姉にふさわしい場所を探し求めて、傷心の旅に出る。一人の男の人生の節目節目に、必ずそばにいて勇気づけた従姉の死を通して、甘く苦い初恋の記憶をたどる感動作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUMOR HAS IT．．．", "迷い婚／すべての迷える女性たちへ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "結婚を目前にして揺れる女心を描いたロマンチック・コメディ。ジェニファー・アニストンが、現代女性のホンネを代弁するヒロインを愛らしく演じている。ケヴィン・コスナーのダンディな好演も見逃せない。目前に迫った結婚に不安を抱くサラ。そんなある日、両親の結婚に意外な秘話があることを知った彼女は、真相を確かめるために旅に出る。そこでは予期せぬ出会いが彼女を待ち受けていた。", "2006-05-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEARTS AND ARMOUR", "ハーツ・アンド・アーマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "中世を舞台に繰り広げられる、スペクタクル・ファンタジー。ムーア人とキリスト教徒との間に、激しい戦いが繰り広げられていた中世ヨーロッパ。敬虔なキリスト教信者の女戦士、プラダマンテは、ムーア人と恋に落ちるだろうと予言される。そして彼女はふとしたことから捕虜の兄と知り合い……。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮本武蔵・般若坂の決斗", "宮本武蔵・般若坂の決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "武蔵は京都で吉岡道場の門弟を一方的に破って遺恨を残す。また奈良の宝蔵院を訪ね槍の名手・阿巌と木刀で立ち合い、一撃で即死させる。さらに般若坂で、荒法師たちを煽動している牢人たちを斬りまくる。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星を追う子ども", "星を追う子ども", "", "", "http://image.eiga.k-img.com/images/movie/55847/315x210.jpg?1749", 116, "「ほしのこえ」「雲のむこう、約束の場所」など繊細な心理描写と緻密な映像美で知られるアニメーション作家・新海誠が、「秒速5センチメートル」以来5年ぶりに手がけたオリジナル長編作。少女アスナが父の形見の鉱石ラジオで聞いた不思議な音楽をきっかけに、大冒険へと旅立つ姿を描くジュブナイルアニメ。美術監督の丹治匠、音楽の天門ら新海作品を支えるおなじみのスタッフが集う。", "2011-05-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BATMAN FOREVER", "バットマン フォーエヴァー", "", "", "http://ecx.images-amazon.com/images/I/51ppp6%2B4lFL._SL210_.jpg", 119, "監督、キャスト、コスチュームを一新し、新生バットマンの活躍を描くシリーズ第３作。ゴッサムシティの精神病院からバットマンに恨みを持つトゥー・フェイスが脱走した。同じ頃、自分の発明を相手にされなかった科学者ニグマもバットマンに復讐を誓う。そしてリドラーと名を変えゴッサムシティの悪の帝王と化したニグマは、トゥー・フェイスと手を組み、街全体を恐怖の渦に陥れる狂気の数々で復讐を開始するのだった……。M・キートンに替わってバットマンを演じるのはアクション、アドベンチャーに定評のV・キルマー。サイバーパンク型のバットマンスーツに身を包み二代目を好演している。悪役には個性派俳優のJ・キャリーとT・L・ジョーンズ、今回初めて登場したバットマンの相棒ロビンを二枚目俳優のC・オドネルが演じるなど豪華キャストも見逃せない。また、「スター・ウォーズ」のジョン・ダイクストラ、「ターミネーター２」のトーマス・L・フィッシャーによるSFX、リック・ベイカーによる特殊メイクも見もの。", "1995-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL TOPO", "エル・トポ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "1986年の東京国際ファンタスティック映画祭で日本で初めて公開され、カルト・ムービーの原点として注目を集めた。1971年、ニューヨークのミッドナイトシアターで無告知で公開され、人づてで評判が高まり、ミック・ジャガーやアンディ・ウォーホルらも鑑賞したというエピソードも残っている。また、当時アバンギャルドに影響されていたジョン・レノンの指示により、マネージャーのA・クラインが独占配給権を獲得した。物語は前編と後編に分かれ、まず前編は、自分を神と信じる主人公エル・トポが、ある女にそそのかされ、東洋哲学者、自然主義者、砂漠の聖人などに決闘を挑み殺害し、あげくは自らも死を迎える。そして後編、僧侶の姿となって再生したエル・トポは、地底生活者を救うためトンネル掘りに余生を捧げる。ようやく解放された地底生活者だが……。“映画とは、詩であって欲しい”というホドロフスキーの姿勢が観る者に幻覚剤にも似た衝撃を与える作品。", "1967-01-01", "1,19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I am.　アイ・アム", "I am.　アイ・アム", "", "", "http://image.eiga.k-img.com/images/movie/55147/315x210.jpg?1265", 95, "介護ロボットのミキは、その可憐さと能率的な仕事ぶりで医療スタッフや患者たちの人気者だった。しかし、介護ロボットに懐疑的な看護士の羽月は、何かにつけミキに冷たく当たる。そんな中、事故で歩けない身体になってしまった元花形サッカー選手の川尻も、露骨にミキに嫌悪感を抱いていた。「カメレオン」「彼岸島」の水川あさみが初主演を務める。共演に柏原収史、泉谷しげるほか。", "2010-01-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Harry Potter and the Chamber of Secrets", "ハリー・ポッターと秘密の部屋", "", "", "http://image.eiga.k-img.com/images/movie/1077/315x210.jpg?3921", 161, "大ヒットシリーズ第2弾。ホグワーツ魔法学校の2年生の新学期を迎えようとするハリーの前に、屋敷しもべ妖精ドビーがやってきて「学校に戻ってはいけない」と止めるが、ハリーは学校へ。やがて「殺してやる」という正体不明の謎の声が聞こえ、生徒たちが石になってしまうという事件が起こる。監督はじめスタッフのほどんどは前作と同じだが、撮影のロジャー・プラットと編集のピーター・ホネスは本作でシリーズ初参加。", "2002-11-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前競馬", "喜劇・駅前競馬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "藤本義一が初めて脚本を担当。ホルモン料理屋の主人・徳之助、競馬新聞社社長の次郎、風呂屋の主人はいずれも競馬狂いで子どもがいない。ヨシツネヒカリという馬の馬主となった亭主連、さてレースに勝てるか？", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガラスの脳", "ガラスの脳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "手塚治虫の1971年の短編漫画をベースにした、切ないファンタジー。生まれてから17年も昏睡し続けている少女、由美。彼女に想いを寄せる少年、雄一は願いをこめて彼女にキスし続ける。やがて由美は、ついに目覚めるが、彼女にあたえられた時間は、わずか５日間。雄一と由美は、限られた時間のなかで、精一杯愛を貫こうとする。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VERONIKA VOSS", "ベロニカ・フォスのあこがれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1955年のミュンヘン。スポーツ記者の男は、ある日、ベロニカ・フォスという名の女に出会った。かつては大女優だったとつぶやく彼女の謎めいた容姿に興味を抱いた彼は、彼女の身辺を探り始めた……。ファスビンダーの幻惑的映像が、怪しげなムードを盛り上げる異色ラブ・ストーリー。", "1982-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("源氏九郎颯爽記 濡れ髪二刀流", "源氏九郎颯爽記 濡れ髪二刀流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "江戸の刺客・大坪左源太は奉納試合への道中、何者かに襲われて倒れる。そこに現れたのは白づくめの美剣士・源氏九郎だった。九郎は左源太の遺言を聞き、名刀火焔剣を持って奉納試合に向かうが、行く手には幕府隠密の刺客団が待ちかまえていた……。中村錦之助主演、柴田錬三郎原作の人気時代アクション。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROSSINI　ODER　DIE　MO¨RDERISCHE　FRAGE，WER　MIT　WEM　SCHLIEF", "悦楽晩餐会・または誰と寝るかという重要な問題", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ドイツで大ヒットしたブラック・ユーモアあふれる群像劇。製作準備中の新作映画のスタッフたちが通い詰めている高級イタリアンレストラン、ロッシーニ。ガンコな原作者は映画化権の売却を渋り、投資者もソッポを向き、映画監督とプロデューサーは頭を痛めていた。そこに女優志望のブロンド美女が迷い込み、彼らの間に波紋を投げかける。", "1996-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　人情噺文七元結", "シネマ歌舞伎　人情噺文七元結", "", "", "http://image.eiga.k-img.com/images/movie/53947/315x210.jpg?2543", 87, "歌舞伎の舞台公演をスクリーンで上演する「シネマ歌舞伎」の第6弾。日本映画界が誇る巨匠・山田洋次監督が中村勘三郎の要請で初めて歌舞伎の演出を手がけ、三遊亭円朝の創作落語を原作とする名作世話話「人情噺文七元結」に挑む。左官の長兵衛は腕も人もいいが無類の博打好き。借金がかさむばかりの父を見かねた娘のお久は、ついに吉原に身を売る決心をして妓楼を訪れる。事情を知った妓楼の女房・お駒は長兵衛を諭して50両を貸し与えるが……。", "2008-10-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BALLAD IN BLUE", "星空", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "盲目の黒人歌手、R・チャールズが自ら出演し、盲目の少年を励まして、光明を取り戻させるという感動的な作品。ヒット曲もふんだんに歌われ、彼の温かい人柄がそのまま画面からにじみ出る、魅力ある作品。", "1964-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pickle", "フライング・ピクルス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　近年ヒットに恵まれない映画監督のハリー・ストーンは、巨大なピクルスが空を飛ぶという子供向けのB級SF映画を撮ることに。キャリアに傷がつくことを恐れて悩むハリーだったが……。映画業界の内幕を描いたブラック・コメディ。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cop Out", "コップ・アウト　刑事（デカ）した奴ら", "", "", "http://image.eiga.k-img.com/images/movie/54687/315x210.jpg?2396", 107, "ブルース・ウィリス主演のアクション・コメディ。ニューヨーク市警のベテラン刑事ジミー（ウィリス）は、愛娘の結婚式の資金として当てにしていたレアなベースボールカードを盗まれてしまう。相棒のポールとともにカードを探すジミーだったが、事態へ思わぬ方向へと進んでいく……。共演にトレイシー・モーガン、ショーン・ウィリアム・スコット、ケビン・ポラック。監督は「ドグマ」「ジェイ＆サイレント・ボブ　帝国への逆襲」のケビン・スミス。", "2010-09-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("早射ち野郎", "早射ち野郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "宍戸錠主演のおなじみウエスタン調アクション。ダム景気に沸く新開地にやって来たエースのジョー。折しも工事の事務所から現金が奪われる事件が続発していた。流れ者に絡まれたジョーは土地の有力者・三島に助けられるが、その三島は元刑事のジョーの兄を殺した犯人で、一連の事件の首謀者だった。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恐怖学園", "恐怖学園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ほんとにあった呪いのビデオ Ver.X」などのVシネマを多く手掛けてきた山口誠監督が、劇場映画デビューを飾ったホラー。小学生篇、中学生篇、高校生篇の三つのエピソードから構成されるオムニバス形式で、夏休みの人気の少ない学校を舞台に、世にも恐ろしい出来事を体験する少女たちの姿を描く。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女賭博師十番勝負", "女賭博師十番勝負", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "兼松組の女ツボ振り師・大滝銀子は、不敗を誇る留造を破り名実ともに日本一の賭博師となるが、兼松の縄張りを狙う新興ヤクザの中井組は組長を殺し、銀子にも刺客を放つ。当時大映の若手スターとして売り出し中だった峰岸隆之介（現・徹）が共演。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPARROW", "尼僧の恋 マリアの涙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "1854年、シチリアの港町カターニャ。町にコレラが蔓延したため、修道女たちが一時帰郷させられることになった。19歳になった美しいマリアも、12年ぶりに故郷の地を踏むが、同じように帰郷している弁護士を目指す青年ニーノを紹介され、恋に落ちる。やがてマリアは修道院に、ニーノは大学へと戻っていくのだが、どうしてもニーノのことが忘れられないマリアは、ついに修道院を抜け出し……。「ロミオとジュリエット」で純愛を描いた巨匠F・ゼフィレッリが、再び狂おしいまでの純粋な愛の世界を描く。ゼフィレッリ本人に大抜擢され、これが初主演となるA・ベティスが、楚々とした美しさを放っている。ビデオ発売時タイトルは「尼僧の恋」。", "1993-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("結婚行進曲 WEDDING MARCH", "結婚行進曲 WEDDING MARCH", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "小説家志望の青年が仕事中も小説に熱を上げていたためにクビになる。社長のところへ彼の復職を頼みにいった恋人は、社長に気に入られ入社してしまう。社長の奥方は彼女と社長の仲を誤解して……というラブ・コメディ。監督の市川崑はこの年２ヵ月に１本という驚異的なペースで仕事をこなした。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABOVE SUSPICION", "偽証", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "妻と弟の不倫を知った刑事が二人に罠をしかける、TVフィーチャーのサスペンス・ドラマ。サンディエゴ警察の敏腕刑事デンプシー・ケイン。愛する家族と幸せな生活を送っていた彼はある日、捜査中の銃撃戦で下半身不随の重傷を負い、車椅子での生活を余儀なくされる。そんな彼には家族の愛だけが頼りだったが、妻ゲイルと弟ニックの不倫関係を知り、デンプシーは巧妙な殺人計画を練る。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SON　OF　FRANKENSTEIN", "フランケンシュタイン復活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "怪奇俳優として一世を風靡したB・カーロフ、シリーズ最後の主演作品。フランケンシュタイン男爵が創り上げた人造人間が再び大暴れする。医者になった男爵の息子が妻子を連れて帰郷した。父の遺産を継ぐためだったが、故郷の人々は過去の惨劇を思い出し息子を歓迎しなかった。やがて彼は実家の城に眠る怪物を発見し、父の遺志を継ぐために怪物を復活させようとする……。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELLACATS OF THE NAVY", "勝利への潜航", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "第二次大戦下、対島海峡攻略の命を受けて負傷した司令官と看護婦の戦場ラブ・ロマンス。第40代アメリカ大統領レーガンとナンシー夫人が共演した唯一の貴重な作品。モノクロの美しい映像で、会話の響き、シーンのつなぎ、音楽などいかにも古き良きハリウッド映画の趣タップリのほほえましい一編。DVDは「コロムビアトライスター ウォー・ムービー・コレクションBOX(3)」に収録。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEACH RED", "ビーチレッド戦記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1943年の南太平洋の島々。マクドナルド率いる海兵隊がこの島への上陸を敢行、杉山大佐率いる日本軍特攻隊と激しい戦闘を繰り広げる。やがて杉山は自決し……。C・ワイルドが製作・監督・主演を兼ねた戦争もの。ワイルド夫人のG・ウォーレスも出演。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒の奔流", "黒の奔流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "松本清張の小説『種族同盟』を映画化した社会派サスペンス。殺人容疑で法廷に立った旅館の女中と敏腕の弁護士。二人の間にはやがて情交ができるが、男の方がほかの女と結婚すると知り、女は復讐する。岡田茉莉子が女の情の怖さを表現して巧演。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　MAKER", "ザ・メイカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「ベルベット・ゴールドマイン」でブレイクする以前の、J・L・マイヤーズ主演による青春映画。18歳の誕生日を迎えた孤児ジョシュの前に、10年も消息を断っていた兄ウォルターが出現。犯罪の世界で生きてきた彼は弟に、いかなる状況でも主導権をとれる人物（＝メイカー）になることを説く。そんな兄に憎悪と憧れ、相反する複雑な心情を抱えながら、ジョシュはウォルターの犯罪計画に加担していく。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五虎將之決裂／THE TIGERS", "蒼き獣たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "次々と人気俳優を輩出してきたテレビ局TVBから誕生したアイドルグループ“五虎將”の５人が、10年の歳月を経て再び共演した作品。アンディたち香港警察特捜班CIDのメンバーは、レオン警部のもと、次々と凶悪犯罪を解決したが、ある事件で麻薬犯ケンを取り逃がしたことから、警察の汚職調査機関から裏金着服の疑いをかけられる。そして、ケンに親友トニーを殺され、レオンも命を落とす。怒りに燃えるアンディは刑事の立場を捨て、復讐に立ち上がるのだった。香港を舞台に繰り広げられる、愛憎渦巻くポリス・アクション・ストーリー。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWO PEOPLE", "ふたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "モロッコで知り合ったファッションモデルのディアドラと謎めいた男ボナーは、パリへの旅をともにすることになる。お互いの心が惹かれ合うのを感じたボナーは、自分がアメリカへ送還される途中のベトナムからの脱走兵であることを告白する……。別離を運命づけられた恋人たちの短かくも美しい日々を、エキゾチックな風景の中にほろ苦く描き出した佳作。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Glastonbury", "グラストンベリー", "", "", "http://image.eiga.k-img.com/images/movie/34037/315x210.jpg?1370", 138, "FUJI ROCK FESTIVALのモデルになったイギリスの野外フェスティバル“グラストンベリー”。片田舎の農場主が個人的に始めたイベントから、現在では毎年10万人以上の人々が詰め掛ける巨大イベントにまで発展したこのフェスティバルの黄金の歴史を綴ったドキュメンタリー。監督は、セックス・ピストルズのドキュメンタリー「NO FUTURE A SEX PISTOLS FILM」のジュリアン・テンプル。", "2007-06-30", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ocean's Twelve", "オーシャンズ12", "", "", "http://image.eiga.k-img.com/images/movie/1137/315x210.jpg?4514", 125, "大ヒット作「オーシャンズ11」と同じスタッフ＆キャストに、キャサリン・ゼタ＝ジョーンズ、バンサン・カッセルらが加わりさらに豪華共演となった続編。前作で彼らに金庫破りをされたカジノ王が彼らの所在を突きとめて返済を要求。オーシャンたちはヨーロッパでひと仕事を目論むが、オーシャンの右腕ラスティーは、彼らを追うユーロポリスの女性捜査官イザベルとかつて熱愛した仲で今でも未練が。", "2005-01-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・望郷篇", "男はつらいよ・望郷篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "１作、２作とメガホンを執った山田洋次が、久々に復帰。TV版でさくら役だった長山藍子が美しい美容師で登場、かつて世話になったヤクザの親分を訪ねた寅次郎は、今や落ちぶれた親分をみて、義理のすたれた任侠道に失望し、ヤクザ渡世から足を洗おうと決意する。さっそく浦安の豆腐屋に就職し、順調に行くかに見えたが、店の娘に恋して……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("War of the Worlds", "宇宙戦争（2005）", "", "", "http://image.eiga.k-img.com/images/movie/1217/315x210.jpg?2865", 114, "湾岸地帯で働く平凡な労働者レイが、別れた妻との間にもうけた子供たちと面会するその日、突如現れた“何者か”が容赦なく町を破壊していく。レイは子供たちとともに生きるために町を逃げ出すが……。H・G・ウェルズの傑作小説を再映画化。撮影はヤヌス・カミンスキー、音楽はジョン・ウィリアムズなど、おなじみのスピルバーグ組が結集。", "2005-06-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KOLYA", "コーリャ 愛のプラハ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "社会主義体制崩壊直前のチェコ・プラハを舞台にした人間ドラマ。遊び人の初老のチェロ奏者ロウカは、友人の頼みでロシア人女性と偽装結婚。ところが、その女性は姿を消し、５歳の子供コーリャが彼の許に残される。子育ての経験のないロウカは悪戦苦闘しつつ、次第にコーリャと心を通わせるが……。軍の圧力や革命、老人問題を背景にしながら、老人と少年の交流をユーモラスに描出。プラハの四季を追った叙情味が隠し味となり、触れ合いを深める二人の絆が静かな感動を呼び起こす。スヴィエラーク監督は前作「アキュムレーター１」とはガラッと異なり、落ち着いた演出を披露。脚本、主演を兼任した父親ズディニェクのサポートを受けて、味わい深いドラマに仕上げた。東京国際映画祭ではグランプリを受賞。", "1996-01-01", "43,3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Serbian Film", "セルビアン・フィルム", "", "", "http://image.eiga.k-img.com/images/movie/57547/315x210.jpg?2662", 104, "内容のあまりの過激さに世界各国で上映の際に多くのシーンがカットされるなど物議をかもしたセルビア製ゴア・スリラーのノーカット版。元ポルノ男優のミロシュは、怪しげな大作ポルノ映画への出演を依頼され、高額なギャラにひかれて話を引き受ける。ある豪邸につれていかれ、そこに現れたビクミルと名乗る謎の男から「大金持ちのクライアントの嗜好を満たす芸術的なポルノ映画が撮りたい」と諭されたミロシュは、具体的な内容の説明も聞かぬうちに契約書にサインしてしまうが……。", "2012-01-21", "44", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西楚覇王／THE GREAT CONQUEROR’S CONCUBINE", "項羽と劉邦−その愛と興亡−", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "チャン・イーモウが総監修を務めている歴史スペクタクル巨編。紀元前206年の中国、始皇帝が亡くなったあとに各地で反乱が起きていた。そのなかでも傑出していたのが項羽と劉邦だった。項羽は情に厚く、恋人の虞姫を一途に思っていたが、劉邦は好色で酒好きな無頼漢だった。二人は兄弟の盃を交わしていたが、競争相手でもあった。項羽と劉邦は秦王朝に反抗していたが、項羽の功により秦を倒すことに成功、項羽は“西楚覇王”と呼ばれるようになる……。", "1994-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TRIUMPH OF SHERLOCK HOLMES", "シャーロック・ホームズの勝利", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ペンシルバニアの炭鉱夫らの対立から起こった殺人事件を、ディアストーカーにパイプ姿のご存知シャーロック・ホームズ名探偵が冴えた推理で解決していく。イギリス映画界の誇る名優A・ウォントナーが扮するシリーズ第４作。原作は『恐怖の谷』。", "1935-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("その日のまえに", "その日のまえに", "", "", "http://image.eiga.k-img.com/images/movie/53887/315x210.jpg?3037", 0, "重松清の同名原作を、「転校生」「時をかける少女」の大林宣彦が映画化。脚本は「異人たちとの夏」で大林とタッグを組んだ市川森一。「七人のおたく」の南原清隆と「人のセックスを笑うな」永作博美が夫婦役で共演し、筧利夫や柴田理恵ら豪華俳優陣が脇を固める。育ち盛りの2人の息子を抱えるとし子は病に倒れ、余命を宣告される。夫の健大ととし子は、子供の将来を相談したりしながら、“その日”が来るまでを一生懸命生きる。", "2008-11-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nid de Guepes", "スズメバチ", "", "", "http://image.eiga.k-img.com/images/movie/1677/315x210.jpg?1968", 108, "凶悪犯を護送中の特殊警察の装甲車を、彼を脱獄させようとする一味が攻撃、装甲車が逃げ込んだ倉庫には、備品を盗もうとするシロウト窃盗グループが侵入中だった。特殊警察と窃盗グループは協力して敵と戦う。「クリムゾン・リバー」の女性氷河研究者役、ナディア・ファレスが特殊警察の中尉を演じるフランス製アクション。「TAXi」シリーズのサミー・ナセリ、「ピアニスト」のブノワ・マジメルが窃盗グループのメンバー役で共演。", "2002-10-26", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ストーンズ・イン・エグザイル　「メイン・ストリートのならず者」の真実", "ストーンズ・イン・エグザイル　「メイン・ストリートのならず者」の真実", "", "", "http://image.eiga.k-img.com/images/movie/55527/315x210.jpg?1724", 61, "人気ロックバンド「ローリング・ストーンズ」が1972 年に発表した2 枚組アルバム「メイン・ストリートのならず者」の制作現場を収めた音楽ドキュメンタリー。71 年、イギリスの高い所得税から逃れるためにフランスへ移住したメンバーは、カンヌ近くの別荘でレコーディングを始める。貴重な制作風景を軸に現在のメンバーへのインタビューを交え、名作アルバム誕生の秘密を探る。第63回カンヌ映画祭・監督週間で特別上映された。", "2010-07-12", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("見鬼２／THE EYE２", "the EYE２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "幽霊が見える女性の恐怖体験を描き、日本でもヒットした心霊ホラーの第2弾。今回は人気女優スー・チーが主演を務め、妊娠をきっかけに怪現象に襲われるヒロインを熱演。パン・ブラザースによる巧みなショック描写が戦慄を呼び起こす。愛人との別れ話がもつれ、自殺を図ったジョーイ。彼女は病院で手当を受けて一命を取り留めるが、それ以来、幽霊が見えるようになってしまう。しかも自分が妊娠していることを知り……。", "2006-04-08", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FARE", "殺意の瞬間〈とき〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "政治家の愛人であるヴァネッサは、二人の関係を清算しようと迫られ争いとなり、誤って殺してしまう。怖くなった彼女は、CIAのクィーンに電話する。実は彼女はCIAの手先で、指令によって政治家に接近していたのだった。クィーンとの待ち合わせ場所に行った彼女は、待ち受けていたクィーンに証拠隠滅で殺されそうになり、タクシーの運転手ベンの協力を得てカナダまで逃亡しようとするが……。名悪役俳優M・アイアンサイドが活躍する、ポリティカル・サスペンス。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("亀", "亀", "", "", "http://image.eiga.k-img.com/images/movie/54267/315x210.jpg?3573", 118, "「グミ・チョコレート・パイン」「ハッピーフライト」の森岡龍が主演する青春映画。監督は「えび」「くらげ」の池田将。寝静まった夜の街を忙しなく駆け回る新聞配達の男や、鼻血を塞ぎながら受験勉強に打ち込む男など、少し変わった10人が送る淡々とした切実な日常生活を映し出す群像劇。", "2008-12-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LOOK AT ME！", "白夜の好奇心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "舞台は北欧。金持ちの実業家を父に持ち、裕福な学園生活を送る女子大生シャルロットは、ある日長身でハンサムな青年カルロに一目惚れ。だが彼には、シャルロットには言えない夜の仕事の悩みと、彼女の両親との性の秘密を持っていた。肉体グラマー女優カラチが大胆で刺激的な体当たりの演技を見せる。", "1989-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パプリカ", "パプリカ", "", "", "http://image.eiga.k-img.com/images/movie/1587/315x210.jpg?3973", 90, "「時をかける少女」の筒井康隆による同名SF小説を「千年女優」「東京ゴッドファーザーズ」のアニメーション監督今敏が映画化。表の顔は精神医療研究所に勤めるセラピー機器の研究者、裏の顔は“パプリカ”というコードネームを持つセラピスト千葉敦子は他人の夢をスキャンすることが出来るというセラピー機器“DCミニ”を使い、日々患者の迷える心をケアしていた。だがある日、その“DCミニ”が何者かによって盗まれてしまう……。", "2006-11-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD POETS SOCIETY", "いまを生きる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "オーストラリアの鬼才、P・ウィアー監督の心温まる学園ドラマ。舞台は1959年。バーモントのウェルトン・アカデミー。全寮制で高い進学率を誇るこの名門校に、新任の英語教師キーティングが赴任してくる。同校OBでもある彼は、型破りな授業を行い、また人生を楽しむ心を説いて、生徒たちの気持ちを捉えていくが……。教師と生徒の交流、現代の社会では、ある種失われた信頼で結ばれた師弟関係が、ウィアー独特の映像美の中で展開する。迷いや悩み、自分の殻から抜け出すことのできなかった生徒がキーティングを支持するラストは圧巻。R・ウィリアムズも静と動を使い分けた演技を披露している。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHE WORE A YELLOW RIBBON", "黄色いリボン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「アパッチ砦」「リオ・グランデの砦」の中間に位置するJ・フォードの“騎兵隊”シリーズ第２作。退役目前の大尉が活躍する話であるが、フォードは、ストーリー展開よりも、人物描写と西部の雰囲気に重点をおいている。モニュメント・ヴァレーをバックにした騎兵隊の雄大な美しさ、出演者の熱演、フォードが到達した枯淡の境地。主題歌『黄色いリボン』はあまりにも有名。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウィリアム・テロル、タイ・ブレイク", "ウィリアム・テロル、タイ・ブレイク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ボーイフレンドとうまくいっていない弥生は、パトロンにも恋人にも逃げられた姉の妙から呼び出される。だが、そこへ姉の恋人が戻って来た。しかも、二人が抱き合っているところへ、パトロンまでも戻ってきてしまう。だが、その光景を見たパトロンは卒倒。３人は死んだパトロンのポケットをまさぐり、遺体を川へ捨てに行く。夜明け前、なんとか恐しい作業を終えた３人はそれぞれの生活に戻っていく。そして死んだはずのパトロンも……。パトロン役を映画評論家の宇田川幸洋が演じているのに注目。水面のきらめきを捉えた映像の、ゴダール的な美しさが印象的。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BENEATH THE TWELVE MILE REEF", "十二哩の暗礁の下に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "風光明媚なフロリダを舞台にした青春ラブ・ロマンス。当時、できたばかりのシネマスコープの広い画面をフルに生かし、特に海中特撮の美しさが評判を呼んだ。ワグナー、ムーアのフレッシュなコンビぶりもなかなか良い。タコとの戦いがクライマックスで盛り上がる。", "1953-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ユモレスク　逆さまの蝶", "ユモレスク　逆さまの蝶", "", "", "http://image.eiga.k-img.com/images/movie/33727/315x210.jpg?4715", 72, "人気モデルの太田莉菜と、映画や舞台で活躍中の美波が主演を務めるガールズ・ムービー。17歳のエイミーとソニーは、夕方になると“秘密のアジト” に集まり、互いに干渉することなくそれぞれの楽しい時間を過ごしていた。ある日エイミーは、バーで出会った年上の男性“J”に恋をする。大人になることを意識するようになったエイミーとソニーは次第にすれ違いはじめ……。監督・脚本は、テレビドラマや映画で女優としても活躍する猪俣ユキ。", "2006-11-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生きていてよかった", "生きていてよかった", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "広島、長崎への原爆投下から10年にわたる被爆者たちの苦しみを、詳細な現地調査と２ヵ月に及ぶ現地撮影で記録した反核ドキュメンタリー。「死ぬことは苦しい」「生きていることも苦しい」「でも生きていてよかった」の三部から構成されている。1955年に開催された第１回原水爆禁止世界大会で、被爆者救助運動の一環として企画されたもの。助監督の一人として勅使河原宏が参加している。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拳銃は掩のパスポート", "拳銃は掩のパスポート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "藤原審爾の小説『逃亡者』を野村孝監督、宍戸錠主演で映画化した日活アクションの傑作。殺し屋・上村は大田原組に雇われ島津組親分を射殺し、相棒とともに身を隠すが大田原は島津組二代目と手打ちを行ない相棒を血祭りにあげる。大田原の裏切りを知った上村は単身、島津や大田原らが待ち受ける埋立地に向かう。ラストの対決シーンにおける宍戸錠のバネのきいたスピーディーなアクションが見ごたえ十分。宍戸錠はこの年（1967年）には、「殺しの烙印」（鈴木清順監督）、「みな殺しの拳銃」（長谷部安春監督）そして本作品という３本の傑作に出演し、ハードボイルド・スターの第一人者としての貫禄を示した。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポケットモンスター・アドバンスジェネレーション／ミュウと波導の勇者ルカリオ", "ポケットモンスター・アドバンスジェネレーション／ミュウと波導の勇者ルカリオ", "", "", "http://ecx.images-amazon.com/images/I/51DHRSQ8EEL._SL210_.jpg", 103, "ポケモン史上最大のアドベンチャー第３弾。今回は、新ポケモンのルカリオとポケモンの中でもトップクラスの力を持つ“幻のポケモン”ミュウが大活躍。ミュウに連れ去られたピカチュウの運命は？ そしてルカリオの隠された過去とは？ 次々と新しい魅力を見せるポケモンたちの活躍に注目だ。サトシとピカチュウたちは“波導伝説”が伝わる町ロータへ。お城で行われた波導の勇者を決める戦いに優勝したサトシたちだが……。", "2005-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEEP SLEEP", "ミーガン・フォローズin ディープ・スリープ 禁断の殺意", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「赤毛のアン」で一躍有名となったM・フォローズ主演のサイコ・スリラー。４年前、父の射殺事件を目撃したショックで入院していたシェリーが自宅へ戻ってきた。そこでは父の仕事仲間だった男が、父の仕事を引き継ぎ、母とも再婚していた。シェリーは父の死に関する悪夢に悩まされながらも、一枚の写真を手がかりに死因の究明に乗り出す。", "1990-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風櫃來的人", "風櫃（フンクイ）の少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "ホウ・シャオシェン監督が初めて自伝的題材を扱った青春メモワール。台湾の離島ポンフー島にある風櫃に住むアーチン、アーロン、グォズの三青年。彼らはビリヤード場に入り浸り、毎日悪さばかりして暮らしていた。ある日警察ざたを起こし、大都会・高雄へと出ていくが……。ナント三大陸映画祭で大賞を受賞し、ホウ・シャオシエンの名を世界に知らしめた出世作。", "1983-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE INNOCENT", "愛の果てに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "1950年代、分割統治下のベルリンに潜入した若きスパイ、レナードは人妻マリアと恋に落ちる。だが、彼は誤ってマリアの夫を殺害。レナードは彼女を連れて国外逃亡を図るが、そこでマリアが選んだ愛ゆえの選択は……。「カサブランカ」を彷彿させるクライマックスが話題となったドラマティックなラブ・ストーリー。", "1993-01-01", "3,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Back to the Future Part III", "バック・トゥ・ザ・フューチャーPART3", "", "", "http://ecx.images-amazon.com/images/I/51GMsmsqS2L._SL210_.jpg", 119, "空前のヒットを飛ばした人気シリーズの完結編。前作で落雷事故のため19世紀末の大西部開拓時代に飛ばされてしまったドクは、デロリアンが故障してもう現代には戻れないという手紙を、1955年にいるマーティに送った。マーティはやがてドクの墓を見つけるが、墓碑銘からドクが手紙を書いた数日後に背中を撃たれて死んだことが判明する。19世紀のドクを救うため、マーティは1955年のドクの力を借りて1885年の大西部に向かってタイム・トラベルをすることに……。前作までの伏線があまりに簡単に解決してしまう感はあるが、相変わらずのテンポの良さとリアルに描かれた西部の世界が楽しめる。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SINCE YOU’VE BEEN GONE", "ディープ・ハプニング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "TVシリーズ『フレンズ』で人気を博した俳優D・シュワイマーの初監督作品。彼自身が設立に参加したシカゴの劇団“ルッキング・グラス・シアター・カンパニー”のメンバーを総出演させた青春コメディ。高校卒業以来、10年ぶりに同窓会で再会した仲間たちの人生の浮き沈み、ロマンスの再燃などの波乱含みのエピソードが展開する。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRACULA", "魔人ドラキュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "トランシルバニアの古城。ドラキュラ伯爵は500年前に死去したはずだが、いまだに夜になると姿を現わしていた。彼は恐るべき精気をもって棺の中で生息していたのである。若き法律家レンフィールドはドラキュラの魔力にかかり彼の古城に招き寄せられ、命じられるまま棺をイギリス行きの漁船に積んだ。途中洋上で暴風雨にあって難破。彼ら二人はウイスピーの漁村へ漂着する。その日からドラキュラは夜ごとに街に現れ、若い女の生血を吸い求めてさまよい歩く……。ストーカーの原作を1927年に舞台化、映画でもルゴシが同じドラキュラ役で出演し、評判となった。ユニバーサルがこれ以後、怪奇映画を連作することになった画期的な作品。", "1931-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A GOOFY MOVIE", "グーフィーとマックス　ホリデーは最高！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "ディズニー映画の名バイプレイヤー、グーフィーを主役にした長編アニメ。グーフィーの息子のマックスは父親みたいなマヌケになりたくないと思っているが、グーフィーは息子との仲を深めようとマックスの都合も聞かずに、旅行に出かける。デートの予定があったマックスは不機嫌で、親子の仲は最悪に。しかし、旅行の途中に様々なトラブルが起き、それを乗り越える度に親子の絆は深まっていくのだった。ビデオ、DVDタイトルは「グーフィー・ムービー ホリデーは最高!!」。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOUTH OF THE BORDER", "国境のバラード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "中央アメリカの５つの国をまわり、世界共通の言語である音楽を武器に戦い続ける人々の姿を記録したドキュメンタリー。自由で安定した生活を求め歌う人、侵略者から祖国を守るため歌う人などが作り出す、バラエティーに富んだ音楽を通して、切実で力強いメッセージが感じとれる。", "1988-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ESCAPE FROM SOBIBOR", "ルトガー・ハウアー　脱走戦線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 149, "第二次大戦下、ナチスがポーランド東部に設立したソルボー強制収容所では、連日大勢のユダヤ人が強制労働の末に虐殺されていた。そんな絶望的な状況下でも決して明日への希望を捨てないレオンは、ある日、捕虜として送られてきた兵士サーシャに脱走計画を持ちかける。ユダヤ人強制収容所からの大量脱走計画の一部始終を、実話をもとに描いたTVフィーチャーのアクション・ドラマ。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・社長道中記／女親分対決の巻", "続・社長道中記／女親分対決の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "設定は前編と同じく太陽食料。マジメな桑原社員は秘書課長に抜擢され、社長の一人娘の婿にむりやりさせられてしまう寸前だ。恋人の敬子はびっくり。そんな時、ライバルの日の出食糧が浜名湖のウナギを買い占める計画に出てきた。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("集金旅行", "集金旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "異なった目的を持つ二人の男女が繰り広げる珍道中もの。お互いに喧嘩しながらも、次第に恋仲になっていく過程をローカルカラーを巧みに取り入れ、ユーモアを交えながら描き出す。桃井かおりと渡瀬恒彦主演で「神様のくれた赤ん坊」の題名でリメイクされている。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAMILLA", "カミーラ あなたといた夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "年の離れた女性二人の心の交流を描いたロマンティック・コメディ。夫とのマンネリ生活に飽き飽きしていたフレッダは、二人の関係をリフレッシュさせるために、カナダの島で過ごす二週間の休暇を心待ちにしていた。しかし、美しい老婦人カミーラに出会ったことにより、休暇は思わぬ方向に転じていく……。シンガー・ソングライターのフレッダをB・フォンダ、プロのヴァイオリニストとして、華麗でロマンティックな人生を送ってきたカミーラに、J・タンディが扮している。", "1994-01-01", "10,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sinai Field Mission", "シナイ半島監視団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("12 Angry Men", "十二人の怒れる男", "", "", "http://ecx.images-amazon.com/images/I/51xhqCAgFkL._SL210_.jpg", 96, "　ニューヨークの裁判所。18歳の不良少年が実父殺害の容疑で裁かれようとしていた。12人の陪審員たちは評決の投票をするが、ただひとり陪審員8番だけが無罪を主張し、改めて審議が行なわれることに。それでなくても疲れきっていた11人は苛立つが、8番の説得によって次々と無罪に転じていく。はたして審議の行方は？　レジナルド・ローズ脚本のテレビドラマを映画化。シドニー・ルメットがこの作品で映画初監督を飾った。", "1959-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DON Q：SON OF ZORRO", "ドンQ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1919年に作られた大ヒット作「奇傑ゾロ」の続編となる、血沸き肉躍る無声冒険活劇。D・フェアバンクスがゾロにそっくりの“ゾロの息子”に扮し、フランスの将軍の娘を悪者から救うために、命懸けの大立ち回りを繰り広げる……。監督のD・クリスプは「散り行く花」で主人公の娘を死に追いやるサディスティックな父親役など、独特な風貌で知られる個性派俳優でもある。", "1925-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("閉じる日", "閉じる日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "日本を代表する６人の監督が、デジタル・ビデオで自由自在に撮りあげる“ラブシネマ”第３作作品。両親亡き後、二人きりで暮らしてきた作家の名雪と弟の拓海は体を重ね合う奇妙な関係だった。ある日、名雪は長い間二人が閉じ込めてきた、ある過去から逃れようと赤裸々な小説を書き上げる。監督は「ひまわり」の行定勲。多数のアーティストを手掛ける音楽プロデューサーの朝本浩文が音楽を手掛けている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YAABA", "ヤーバ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "アフリカ・ブルキナファソの小さな村には魔女と呼ばれて、村八分にされている老婆サナが住んでいた。ある日、少年ビラは、サナが村の悪ガキたちに石をぶつけられているのを見た。怒ったビラは、サナのために殴りかかった。すっかり仲良しになって、ビラはサナのことをヤーバ（＝おばあさん）と呼ぶ。そんな時、従妹のポコが破傷風にかかり熱にうなされた。知識のない村の人たちは、インチキ祈とう師にだまされ、ヤーバの魔術のせいだと吹き込まれ……。生まれた時に母親が死に、それがもとで魔女と疎まれながらも、誰も恨まず淡々と生きていくヤーバ。人の言うことを鵜呑みにしないで、自分の信じる正義を貫く勇気を持つビラ。どちらも現代社会で忘れられつつある姿を広大なアフリカの土地を背景に映し出す。素人の村人たちを使った自然な演技は、アフリカの日常生活とともに深い感動を呼び起こす。", "1989-01-01", "2,63,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FEARLESS VAMPIRE KILLERS，OR：PARDON ME，BUT YOUR TEETH ARE IN MY NECK", "吸血鬼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ポランスキーによるパロディー吸血鬼映画。冒頭のMGMマークで、ライオンが吸血鬼に変わるなど、凝りに凝っている。吸血鬼を追い続ける老教授と、ポランスキー扮する門弟が、トランシルバニアの片田舎の宿に泊まった。ところが、宿の娘サラが、吸血鬼クロロックス伯爵にさらわれてしまう。彼女を救おうと、二人は伯爵の古城へと向かった……。とにかく、ユダヤ人なので十字架が平気な吸血鬼や、ゲイの吸血鬼まで登場するから面白い。また、吸血鬼の舞踏会に紛れ込んだ二人が、鏡に姿が映って“人間”と呼ばれるシーンは秀逸だ。吸血鬼の弱点を逆手にとった見事なギャグが楽しめる。サラ役のS・テイトも美しい。", "1967-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋の狩人 ラブ・ハンター", "恋の狩人 ラブ・ハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "1972年１月28日の封切り最終日に警視庁から３本の映画が“わいせつ”であると摘発された。その後摘発された１本も加えた４本のうち、最も有名な作品が本作品である。だが、その知名度も山口清一郎が、監督４人中最も戦闘的な姿勢をとったからであり、作品内容については触れられることは少ない。母親の淫蕩な血を受け継いだ今日子の結婚前の奔放な性行動と、有閑マダムになったあとの乱痴気騒ぎを描いており、文学的な味わいもある。豊かな性の世界を展開した佳作だ。裁判は1980年に無罪確定となるが、山口はその間２本しか撮っておらず結審後は沈黙を守っている。なお、脚本は神代辰巳との合作ペンネーム。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("辻が花", "辻が花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "立原正秋による同名小説を、女性映画の名匠・中村登が映画化。人妻の裕子は８歳年下の四郎に想いを寄せられ困惑していたが、行方不明だった夫がフランスの女性と子供を連れて歩く姿を見て、ついに四郎と結ばれる。しかし、彼女は二人の関係を“辻が花”のような幻の恋として胸に秘めようと決意する。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トラック野郎・一番星北へ帰る", "トラック野郎・一番星北へ帰る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "未亡人・静代をめぐって桃次郎はアメリカ帰りのコンボイ野郎と張り合うことになる。クライマックスは山奥の診療所に医療器具を届けるため、トラックをぶっ飛ばす桃次郎と鬼警部のパトカーとのカーチェイス。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("過埠新娘／PAPER　MARRIAGE", "恋はいつも嘘からはじまる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1997年の中国返還を控えた香港の移民ブームを背景に、市民権獲得のため偽装結婚した男と女のラブ・コメディ。元ボクサーのチンはカナダに移住、たくましかった体も今は見る影もなく太ってしまい、おまけに無職。そこへカナダでの永住権を欲しがっている女ジェイドが来た。二人が偽装結婚すれば、チンはお金を、ジェイドは永住権を手に入れられる。そうと知ったチンとジェイドは赤の他人同士の奇妙な生活を始めることになった。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACK WIDOW", "女郎蜘蛛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "深夜人気のない田舎道を走行中のマークは、誰かが道に横たわっているのを発見。車から降りたところを突然その男に殴られる。気を失ったマークは、記憶を失くし、唯一の手がかりはポケットの中の一枚の映画チケットだった。その映画でマークが見たものは、彼自身の葬式だった。映画チケットなど、小道具をうまく使った、サスペンスたっぷりの演出で見ごたえ十分。", "1951-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("茶々　天涯の貴妃", "茶々　天涯の貴妃", "", "", "http://image.eiga.k-img.com/images/movie/34207/315x210.jpg?1772", 0, "井上靖の歴史小説「淀どの日記」を原作に、激動の戦国時代を駆け抜けた女性・茶々の数奇な運命を描いた時代劇。主演は元宝塚の男役トップスター・和央ようか。織田信長の妹・お市の方の娘として生まれた茶々は、父・浅井長政を信長に攻め落とされ、信長の重臣である柴田勝家の元に身を寄せることに。ところがそこへ豊臣秀吉が攻め入り、お市の方は自害に追い込まれる。やがて茶々は、母の仇である秀吉の側室に入ることになり……。", "2007-12-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白鳥麗子でございます！", "白鳥麗子でございます！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "TVドラマにもなった鈴木由美子原作の人気漫画『白鳥麗子でございます！』の映画化。容姿端麗、眉目秀麗、そして大富豪。天下無敵のお嬢様・白鳥麗子はフツーの大学生・秋本哲也と同棲中。それなりに楽しい日々を送っていたものの、友人の婚約をきっかけに、麗子が結婚を意識し始めたところから二人の歯車がかみ合わなくなってくる。折しも、麗子の父親が病に倒れ、一目花嫁姿を見せたいと麗子と哲也は婚約する。しかし、哲也はあまりの急激な展開と結婚準備のゴタゴタで、疲れ果てて……。白鳥麗子をTVと同様、松雪泰子が演じている。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOCK TROOP", "アフガン ネクサス奪還作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "ソ連軍に最新鋭の射撃管制ウェポン＝ネクサスを盗まれたアメリカ軍は極秘奪還作戦を開始する。が、作戦は失敗、隊長はKGBに捕らえられ強制収容所へ。ところがアメリカ軍は彼を見捨ててしまう。アフガン戦士と連帯し脱走した彼は単身ソ連軍に向かう。一触即発の危険地帯アフガニスタンを舞台にしたスリル満点の戦争アクション。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真木栗ノ穴", "真木栗ノ穴", "", "", "http://image.eiga.k-img.com/images/movie/53677/315x210.jpg?2732", 110, "山本亜紀子のホラー小説「穴」を、「体育館ベイビー」「同級生」の深川栄洋監督が映画化。主演は西島秀俊。築40年の安アパートに暮らす作家の真木栗は、官能小説を依頼されるもアイデアが浮かばない。そんなある日、隣の空き部屋をのぞくことが出来る穴を見つけた真木栗は、そこに若い女が引越してくることを妄想し、それをもとに小説を書き始める。やがて、その空き部屋に本当に女が引越しきて、その生活をのぞき見る真木栗は女の虜になっていく……。", "2008-10-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TCHAO PANTIN", "チャオ・パンタン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "過去を隠し、夜にまぎれるようにして生きている中年男ランベール。彼の勤めるガソリンスタンドに、ある夜、ユダヤとアラブの混血児ベンスサンが飛び込んでくる。二人は無関心を装いながらも、次第に父子のように打ち解けるが、その交流は、ランベールの目前でベンスサンが殺害されるという事件で寸断される。麻薬の絡んだ事件の黒幕を追って、元刑事ランベールの復讐劇が始まる……。名美術監督トローネルの手による、街はずれのガード下のたたずまいが、降りしきる雨ににじみ、名手ニュイッテンの渋みの効いたカラー映像が、フィルム・ノワール独特のムードを醸し出す。朝の空気が匂うラストも秀逸。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INNOCENCE", "もういちど", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "老世代が再び“性”に目覚めるという、ともすれば人々が避けがちなテーマを優しく上品に扱った大人のラブ・ストーリー。もう一度女として愛されたい、輝きたいと願い、長年連れ添った夫と別れて50年前の初恋の人のもとへ走る女性クレアを演じたオーストラリアの大女優J・ブレイクが美しくエレガントな輝きを放っている。2000年モントリオール国際映画祭グランプリ、オーストラリア・インディペンデント映画賞作品賞、監督賞、主演女優賞などのほか、イタリア・タオルミナ映画祭批評家賞、フランス・サントロペ映画祭審査員賞などを受賞。", "2000-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ねじりん棒", "ねじりん棒", "", "", "http://ecx.images-amazon.com/images/I/31VWGM9XCQL._SL210_.jpg", 75, "「ヴァイブレータ」の廣木隆一監督が企画した“ラブコレクション”プロジェクトの一作。ブラック・ユーモアあふれる大人のコメディだ。床屋を訪れたタクシー運転手が回想話を始める。自宅の鍵も所持金も不倫相手の家に忘れてきた女の客を乗せてしまったため、彼女の自宅でもひと騒動。さらに、旅行中の夫が金を全部持って家を出ていた……。", "2004-11-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("守ってあげたい！", "守ってあげたい！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "恋人にフラれた腹いせに、自衛隊に入隊した20歳の女性の奮闘を描く青春映画にして女性映画。落ちこぼればかりが集まった婦人自衛官教育隊３班の面々が実習訓練のさなかに、倒壊した一般家屋から住民を救出しなければならない一大事に直面する。おなじみの設定と展開による物語ながら、菅野美穂のメリハリの効いた演技と存在感によって、リアリティーあるエンターテインメント作品に仕上がっている。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版 2010 BIGBANG BIGSHOW 3D", "劇場版 2010 BIGBANG BIGSHOW 3D", "", "", "http://image.eiga.k-img.com/images/movie/55997/315x210.jpg?2322", 0, "韓国出身の男性5人組人気グループ「BIGBANG」が、2010年1月29日に韓国・ソウルオリンピック公園体操競技場で行ったライブ「2010 BIGBANG LIVE CONCERT BIGSHOW」の公演初日の様子を3Dで劇場公開。日本初公開となるバックステージやライブ当日にいたるまでのビハインドストーリーも収録されている。", "2011-02-19", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版アクエリオン", "劇場版アクエリオン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "「マクロス」シリーズの河森正治監督が手がけ、2005年にテレビ放映され好評を呼んだSFロボットアニメ「創聖のアクエリオン」の劇場版。OVA版「創星のアクエリオン」前後編をまとめあげた長編「創星神話篇」と、新作短編「壱発逆転篇」の2本立て。人を狩る天翅族と人類が死闘を繰り広げた神話の時代から1万2000年後。封印されたはずの天翅族が蘇り、再び人類を襲いはじめる。絶滅の危機に追い込まれた人類は、機械天使運用部隊・ディーバを結成して反撃を開始するが……。", "2007-09-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Cave of the Yellow Dog", "天空の草原のナンサ", "", "", "http://image.eiga.k-img.com/images/movie/1557/315x210.jpg?3959", 93, "ビャンバスレン・ダバー監督が、祖母から聞いた昔話とモンゴルで語り継がれる「黄色い犬の伝説」を下敷きに、実際にモンゴル草原に住むある遊牧民の一家の出演で描く。6歳になるナンサは子犬を見つけて家に連れ帰るが、父親はオオカミの仲間かかもしれないからと飼うことを禁ずる。ナンサは父にないしょでこの犬を飼うが、ある日、犬がいなくなってしまう。ナンサの犬ツォーホルは、本年度カンヌ映画祭でパルムドールならぬパルムドッグ賞を受賞。", "2005-12-23", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おしどり喧嘩笠", "おしどり喧嘩笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "鶴田浩二と美空ひばりの主演コンビで送る、歌あり恋ありの明朗歌謡股旅時代劇。いろはの伊四郎は旅の途中、無理難題の縁談を母親から押し付けられて家出したお才と知り合い、互いに追われる者同士として一緒に旅を続けることになる。追いつ追われつの旅の中、やがてお才は伊四郎を憎からず想うようになるのだが……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATHER OF THE BRIDE PARTII", "花嫁のパパ２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "S・マーティン主演の子離れしない父親を襲った大事件“娘の結婚”を、ユーモアとペーソスあふれるタッチで描き感動を呼んだヒット作「花嫁のパパ」の続編。花嫁のパパ、ジョージは愛娘アニーの結婚騒動からやっと落ち着きを取り戻し、ささやかな幸せを味わっていた。ある日、アニーと婿のブライアンが重大ニュースがあるとジョージを訪ねてくる。そのニュースとはなんとアニーの妊娠。自分がおじいちゃんだなんて、と気の若いジョージは大ショック。さらには妻のニーナまで妊娠したというではないか。そこへ娘の結婚式を悪夢にしてくれたあの怪しいコーディネーター、フランクまで登場して来る……。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("密約　外務省機密漏洩事件", "密約　外務省機密漏洩事件", "", "", "http://image.eiga.k-img.com/images/movie/39587/315x210.jpg?4190", 100, "1971年の沖縄返還協定締結時に起こった外務省機密漏洩事件を描いたドラマ。原作は澤地久枝。アメリカが支払うはずの地権者に対する土地原状回復費を、日本政府が肩代わりするという密約のスクープを追っていた新聞記者の石山は、外務省の女性事務官と肉体関係を結び、機密情報を得るが……。1978年にテレビ朝日開局20周年記念番組として製作されたTVドラマが、10年の歳月を経て1988年に劇場公開。また、公開から22年ぶりとなる2010年にリバイバル上映が決定。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うつつ", "うつつ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "平凡なサラリーマンに謎の女が忍び寄り、“あなたの奥さんは私の夫と浮気している。私たちも同じことをしましょう”とささやく。そこから男の精神と運命が流転していく様を綴った心理ミステリー。不倫をきっかけに妻殺しにまでのめりこんでいく主人公。“夢”から醒めた時、“ひょっとしたら妻は浮気などしていなかったのでは？”という疑念が頭をよぎる。鰻など特異なモチーフを配しながら不安感をかきたてる映像が続く中、男の部下のOLを演じる小西真奈美が見せる自然体のユーモアがさりげない印象を残す。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOOD DAY FOR A HANGING", "絞首台の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "タイトル通り、絞首台の下での1対1対決をクライマックスに据えたウエスタン・アクション。元刑務執行官で今は駅馬車業を営む主人公と、強盗団のメンバーで主人公の娘の恋人。この西部劇的キャラクターの対決で、興奮度合いは非常に高い作品だ。ある日、強盗団が銀行を襲撃し大金を奪って逃走。追った主人公が一味のエディを捕捉したが、彼は死刑執行前に脱獄する。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Christine", "クリスティーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　内気な高校生アーニーはスクラップ寸前の自動車、58年型プリマス・フューリーを買い取り、それにクリスティーンと名づける。しかしアーニーはクリスティーンの恐るべき秘密を知らなかった。不良たちによって破壊されたクリスティーンは自力で再生・修復し、復讐を開始。それは意思を持ち、自らの美を汚す者に容赦なく襲いかかる残忍な車だったのだ！　破壊不可能の車が引き起こす怪現象を描いたサスペンス・ホラー。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOCHKA トーチカ", "TOCHKA トーチカ", "", "", "http://image.eiga.k-img.com/images/movie/54767/315x210.jpg?3488", 93, "第2次世界大戦末期に国境防衛を目的に北海道根室市の海岸に建造され、戦後も取り壊されることなく現存している戦争遺跡「トーチカ群」。その闇に引き寄せられるように偶然の出会いを果たした男女が織りなすドラマを、ほぼワンロケーションという空間の中で描いた異色作。主演は「犬猫」の藤田陽子と「ラストサムライ」の菅田俊。監督は北海道出身の新鋭・松村浩行。", "2009-10-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A｀　LA　FOLIE．．．　PAS　DU　TOUT", "愛してる、愛してない…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「アメリ」のヒロイン役で世界中を魅了したフランスの新進女優、オドレイ・トトゥの主演作。恐るべき妄想に囚われてしまった女性、その情熱的な恋の裏に隠された驚きの真実が明かされる。画学生のアンジェリクは現在、既婚者のロイックと熱愛中。恋に一途な彼女は彼の離婚を待ち望む。ところが離婚は進むどころか奥さんの妊娠が発覚。これを知った彼女はよからぬ妄想が広がり、おかしな行動をとり始める。", "2002-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOWN　IN　THE　VALLEY", "ダウン・イン・ザ・バレー", "", "", "http://ecx.images-amazon.com/images/I/51BTPKV2BFL._SL210_.jpg", 112, "E・ノートンが主演のみならず、製作も手がけた意欲作。自称カウボーイという謎の男に、抑圧的な父の下で暮らす姉弟が憧れ、脱出を夢みる……。きわめて現代的なホーム・ドラマでありつつ、西部劇の要素が混在するファンタジーを含んだ内容で、アメリカ郊外の“いま”をじっくり見つめる。17歳の少女トーブはガソリンスタンド店員ハーレンに魅了される。やがて自分はカウボーイだと告白する彼に、トーブは夢中になるが……。", "2005-12-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Slap happy", "Slap happy", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "「真夏のビタミン」「燃えよピンポン」など、大阪を拠点に製作を続ける三原光尋の1996年作品。田舎から上阪し、コンビニエンス・ストアでバイトしている浪人生の日常を、多彩なエピソードの積み重ねで綴っている。ことさらデフォルメを加えることなく見つめられたコンビニ客たちのキャラクターを始め、演出は自然体そのもの。主演は、劇団“南河内万歳一座”の前田晃男。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Let's 豪徳寺！", "Let's 豪徳寺！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "資産は国家予算なみという超ブルジョワジーの豪徳寺家にお手伝いさんとして入った女子大生と、モラルや常識を逸脱したお金持ち人種たちとの間で繰り広げられる珍騒動を描いたドタバタ・コメディ。原作は庄司陽子の同名マンガで、上流社会への皮肉もチラリ。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊太八縞", "伊太八縞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "のちに“怪談監督”の異名をとる中川信夫の14作目の作品。元スリだが今は堅気の江戸っ子伊太八と、幼友だちのお俊の恋愛物語と、二人を取り巻く善人悪人の人間模様を描いた、長谷川伸お得意の人情噺。情感をこまやかに描き込んだ小品佳作である。当初、脚本は藤田潤一によって書かれたが、あまりの不出来に中川自身が１日で書き直した。従ってタイトルには脚色者の名前はない。当時、黒川弥太郎は引く手あまたの売れっ子スターで、中川によれば市川雷蔵以上の二枚目だったという。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rare Exports", "レア・エクスポーツ　囚われのサンタクロース", "", "", "http://image.eiga.k-img.com/images/movie/57037/315x210.jpg?4439", 80, "世界中で親しまれるサンタクロースを題材に、ブラックユーモア満載で描くフィンランド製アドベンチャー。北フィンランドに住む少年ピエタリは、サンタクロースを恐ろしい存在と信じ、トナカイ猟師の厳格な父ウラノと寂しく過ごしていた。そんなある日、国境付近の山に封印されたという“本物のサンタクロース”が、「考古学上の発掘作業」の名目で掘り起こされ、それを機に地元の子どもたちの失踪やトナカイの大量死など事件が頻発。ピエタリは町を守るため事件解決に乗り出す。2010年シッチェス・カタロニア国際映画祭で作品賞、監督賞、撮影賞を受賞。", "2011-10-08", "20", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天長地久／DAYS OF TOMORROW", "アンディ・ラウのスター伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "A・ラウが、禁じられた愛を貫く伝説のスターを演じる。1960年代末に製作された映画“デイズ・オブ・トゥモロー”のリメイクにあたって、製作アシスタントの少女ヤンは、その撮影中に劇的な死を遂げた伝説のスター、ジョンの半生を調べていくうちに30年の時を越えてよみがえる悲恋物語の旅に誘われる。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Portrait of a Lady", "ある貴婦人の肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "「ピアノ・レッスン」に続いてJ・カンピオンが手掛けた愛の物語。H・ジェームズの名著に材を得て、19世紀の貴族社会で自由に生きる女性が本当の恋にたどり着くまでを、繊細に綴っていく。あまたの求婚を拒絶し続けるイザベルは、莫大な遺産を相続。結婚よりも自由な生き方を欲していた彼女だったが、尊敬する貴婦人マダム・マールの紹介で虚無的な芸術家オズモンドと結婚する。だが、その影にはイザベルの知らない策略が秘められていた……。ヒロインの感情を余すことなく捉えた、カンピオンならではの息苦しいまでに濃密な空気が漂う演出が圧倒的。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bobby Deerfield", "ボビー・デアフィールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "F1レーサーのボビーは、不思議な女リリアンと出会い、愛し合うようになる。刹那的で衝動的に生きる彼女に強くひかれていくボビーは、彼女が不治の病に冒されていること知り苦悩する。ヨーロッパを舞台にしたラブロマンス。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE FEMME FRANCAISE", "フランスの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "第二次世界大戦から植民地戦争へと突入していったフランスの混乱期に、自らの運命をゆだねざるを得なかった女の勇気と決断を描く。軍人である夫の度重なる赴任によって、ジャンヌの寂しさは限界に達していた。そんな折彼女は、家主の息子であるドイツ人青年のマチアスと恋に落ちてしまう。激しく官能的な恋に酔いしれるジャンヌは、女として愛し愛されることに生きることの意味を見出していく。しかし、ジャンヌの一途な情熱はいつしか彼女自身をも苦しめることに……。E・ベアールが、愛のみに生きようとする情熱的なヒロインを見事に演じている。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Underneath", "蒼い記憶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "故郷に戻り、警備会社に勤め始めた男が、かつて共に暮らした妻と再会する。今の妻の情夫との三角関係は、いつしか現金輸送車襲撃計画へとすりかわっていく……。過去と現在を交錯させ、人間の深層心理や狂気と正気というテーマをスタイリッシュに描いたスリラー。「裏切りの街角」（1949年）のリメイク。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT LUCKY TOUCH", "ラッキータッチは恋の戦略", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "国際都市ブリュッセルに住むハンサムな武器商人のマイケルは、知人のつてでNATOに新型の銃の売り込みを図っていた。一方、平和主義者の美人ジャーナリストはNATOの戦略記事のスクープを狙っていた。偶然出会ったこの二人の、恋の駆け引きを中心に展開するロマンティックなラブ・コメディ。", "1975-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("つみきのいえ", "つみきのいえ", "", "", "http://image.eiga.k-img.com/images/movie/54487/315x210.jpg?3985", 12, "第81回アカデミー短編アニメーション賞や2008年アヌシー国際アニメーション映画祭グランプリを受賞するなど、高い評価を受けた短編アニメ。監督は「R」「或る旅人の日記」など短編アニメを制作してきた加藤久仁生。海の上に建つ積み木のような家に住んでいるおじいさんは、海面がどんどん上がってくるので、家を上へ上へと建て増し続けていく。そんな不思議な家に住んでいるおじいさんの、家族との思い出の物語。", "2009-03-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本暴力列島　京阪神殺しの軍団", "日本暴力列島　京阪神殺しの軍団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "大阪で起きた暴力団の抗争事件が波及して山陰、北海道、岐阜などに飛び火するさまを描いた実録ヤクザ映画。小林旭以外は“悪役勢ぞろい”といった感のあるキャスティング。それぞれの性格もよく描き分けられていて、典型的パターンだが実録ヤクザ映画の集大成的作品といえる。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIENDS", "フレンズ　～ポールとミッシェル～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "裕福だが家庭の温かさに恵まれない15歳の少年ポールと、14歳の孤児の少女ミッチェルは大人たちから離れ、二人だけの愛の世界を築いていく。そして、二人の間には赤ん坊まで誕生するのだが……。若者たちの感動と共感を呼んだラブ・メルヘン。", "1970-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ISKANDARIAH LEH？", "アレキサンドリアWHY？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "戦争直前の1942年、エジプトの国際的商港アレキサンドリアを舞台に、18歳の演劇少年とその家族、回教徒とクリスチャン、若きテロリストと外国兵らを皮肉と哀歓を込めて描く。エジプト映画の巨匠シャヒーンの自伝的青春映画。", "1978-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FEMALE TROUBLE", "フィメール・トラブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「ピンク・フラミンゴ」に次ぐウォーターズの長編第２作。ディヴァイン扮するドーン・ダベンポートという奇怪な俗悪女の奇怪な半生を描く。不良少女から犯罪者へ、そして驚異のエンターテイナーとなっていく、ディヴァインの軽やかな変身ぶりが見事。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SING", "ロック・イン・ブルックリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「フットルース」の名コンビ、プロデューサーのC・ゼイダンと脚本家のD・ピッチフォードが手掛けた学園ドラマ。ブルックリンの廃校寸前の高校を舞台に、女教師と生徒たちが街最大のイベントでもあるミュージカル発表会“シング”を成功させようと奮闘する様を描く。主題曲が全米で１位を記録した。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("細雪", "細雪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "谷崎潤一郎の長編ベストセラー小説の初の映画化で、花井、轟、山根、高峰が演じる四人姉妹の姿を描いた文芸作品。当時の製作費で3800万円をかけた野心的な大作で、ややストーリーを追いがちながらも、風格ある作品に仕上げた阿部豊の演出が冴える。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GREAT OUTDOORS", "大混乱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ホーム・アローン」のJ・ヒューズが製作総指揮・脚本を担当したドタバタ・コメディ。主演はD・エイクロイド。湖畔でバカンスを楽しみに来た家族が、招かれざる客の珍入に散々な目に遭うというストーリー。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦国自衛隊", "戦国自衛隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "大演習に向かう途中、自衛隊の一個小隊21名が、戦車、ヘリコプター、装甲車、哨戒艇もろとも400年前の戦国時代にタイム・スリップ。２万人の武田信玄軍対近代兵器の凄絶な戦いが展開される。半村良原作による大掛かりなSF大作で、2005年にも新たな角度よりリメイクされた。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMMA", "エマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "1816年に刊行されたJ・オースティンの同名小説を、「ブロードウェイと銃弾」の脚本家D・マクグラスが映像化。イギリス南部のハイベリーで父親と二人で暮らすエマは、大のおせっかい焼き。好きな人に素敵な恋人を見つける恋のキューピットを演じることに、いつも喜びを感じていた。兄のような立場にある古い友人のナイトレイの助言にも、エマは聞く耳を持たない。親友ハリエットに求婚する農家の青年ロバートを退け、代わりに若い牧師のエルトンを押しつけようとする始末。そんなある日、ハリエットがナイトレイに好意を抱いていることを知った彼女は、突然、胸を締めつけられるような切ない気持ちになるのだった……。G・パルトロウが、お転婆でうぬぼれの強い主人公エマを演じている。エマが親友の恋人候補の一人に選ぶ青年役でE・マクレガーも出演。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地球交響曲　ガイアシンフォニー　第六番", "地球交響曲　ガイアシンフォニー　第六番", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "地球をひとつの生命体と捉え、その未来にとって我々人類がどうあるべきかを、様々な分野で活躍する賢人たちへのインタビューを通して考えるオムニバス・ドキュメンタリー「地球交響曲」。第6弾となる本作では、“全ての存在は響きあっている”をテーマに、人類以外の存在が奏でる、耳には聞こえない“音楽”について描く。出演はピアニストのケリー・ヨスト、海洋生物学者のロジャー・ペイン、シタール奏者のラビ・シャンカールら。", "2007-04-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("学校をつくろう", "学校をつくろう", "", "", "http://image.eiga.k-img.com/images/movie/55897/315x210.jpg?1390", 0, "直木賞作家・志茂田景樹の小説「蒼翼の獅子たち」を、三浦貴大主演で映画化。明治維新の動乱期に、専修学校（現専修大学）の初代校長を務めた相馬永胤（ながたね）、日本初の法学博士となった田尻稲次郎、幕臣・旗本出身で米ハーバード大学を卒業した目賀田種太郎、旧桑名藩主・松平定教の随従である駒井重格の4人が、法律と経済を日本語で教える日本初の学校を創設しようと奔走する姿を描く。", "2011-02-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺たちの荒野", "俺たちの荒野", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "青春の夢と挫折、友情と恋愛をみずみずしく描いた青春映画。哲也と純は集団就職で都会に出てきた。哲也はバーの女と同棲しながら、アメリカに行くことを夢見て精力的に働き、純は基地の脇にある空地に自分の城を建てることを夢見ていた。ある日、純は空地で両親のいない由希と出会う。そして３人はこの空地に自分たちの城を建てることを誓い合う。やがて純は由希に愛を感じるが、哲也も由希を愛しはじめていた……。東宝青春映画のパターンを踏襲しながらも、悲劇的な結末を迎えるのは異例といえるだろう。純の遺骨を空地にばらまく由希の姿を捉えたクレーン撮影が胸に突き刺さる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Supernova", "スーパーノヴァ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "宇宙を航行中の医療船“ナイチンゲール229”のクルー６人は、謎のSOSシグナルに導かれ、危険をくぐり抜けながら生命の存在しない星へとたどり着き、そこで唯一の生存者カールを収容する。しかし、彼は人類の想像を絶する９次元エネルギーで形成された物体を宇宙船の中に持ち込んでしまい……。様々な監督が降板を繰り返しながら、ようやく完成にこぎつけたSFアクション。監督にクレジットされているトーマス・リーは、ウォルター・ヒルの別名。編集にフランシス・コッポラがかかわるなど、仕上げをめぐるトラブルも一部ファンに注目された。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ILE　SE　MARIERENT　ET　EURENT　BEAUCOUP　D’ENFANTS", "フレンチなしあわせのみつけ方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "実生活でもパートナーのシャルロット・ゲンズブールとイヴァン・アタルが「ぼくの妻はシャルロット・ゲンズブール」に続いて発表した恋愛映画。前作同様、男女関係をテーマに、結婚してさまざまな問題を抱える夫婦の姿をコミカルに描く。実話かと思ってしまうほどリアルな夫婦像が興味深い。一見幸せそうな夫婦ヴァンサンとガブリエル。しかしヴァンサンには愛人がいた。それを薄々感じ、ガブリエルは深い哀しみを抱くが……。", "2005-04-02", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("「見逃した君たちへ」 AKB48グループ全公演", "「見逃した君たちへ」 AKB48グループ全公演", "", "", "http://image.eiga.k-img.com/images/movie/56537/315x210.jpg?2306", 0, "人気アイドルグループ「AKB48」がTOKYO DOME CITY HALLで2011年5月24日から6月12日まで連続で行うリバイバル公演「見逃した君たちへ」を、全国の映画館へ配信するライブビューイング（6月9日のみ「AKB48 22thシングル選抜総選挙」のため休演）。「AKB48」のチームA、K、Bや研究生、姉妹グループ「SKE48」「NMB48」「SDN48」の各チームが日替わりで公演を行う。", "2011-05-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VAMPIRE’S KISS", "バンパイア・キッス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "人種のるつぼ、ニューヨーク・マンハッタンに夕闇の訪れとともに現れては、弁護士や医師、一流商社マンらを次々と悩殺していく“バットガール”。首筋に噛みつかれた男は彼女なしでは生きていけないほど、ふぬけになってしまうのだが……。“バットマン”人気に対抗して、“バットガール・センセーション”を巻き起こした、超ハイレグ・コスチュームのまぶしいセクシー・コメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらば、わが友 実録大物死刑囚たち", "さらば、わが友 実録大物死刑囚たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "実在の元死刑囚K・Oの体験記をもとに、生々しい死刑囚たちの実態を浮き彫りにした意欲作。1954年、元自衛隊の大沢は、カービン銃を持って、防衛庁に乗り込み、６千万円を強奪。逃亡ののちに逮捕され、余罪も発覚して死刑囚となる。彼は、拘置所で“帝銀事件”の平沢をはじめ、多くの死刑囚と知り合うのだが……。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NIGHT OF THE HUNTER", "狩人の夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "1930～60年代にかけてイギリス、アメリカ映画界で活躍した、名優C・ロートンが監督した唯一の作品。福音伝導師になりすました殺人鬼ハリーは、刑務所の中で知り合った死刑囚が、警察に連行される前に盗んだ大金を子供にあずけていたことを知り、釈放されるとその家族のもとに現れた。ハリーは未亡人をだまし、結婚すると、子供たちをおどして金のありかを突きとめようとする。やがて母も殺され、危機を感じた兄妹は船に乗って川を下り逃げ出したのだが……。不安がみなぎる魔術的な映像でトリュフォーやマルグリット・デュラスをも熱狂させた幻の名作である。", "1955-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花いちもんめ。", "花いちもんめ。", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "アルツハイマー型老年痴呆症、いわゆる老人ボケの問題をホーム・ドラマの中に取り入れた、ショッキングな社会派作品。加速度的に進行するボケと、それをもてあまし始める家族の冷たい対応……。千秋実が迫真の演技で老人役を演じ、各種の演技賞を獲得した。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WALKING TALL", "ウォーキング・トール 怒りの街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "故郷に帰った元プロレスラーのビュフォードが見たものは暴力団がはびこり、腐敗しきった街だった。それを見かねた彼は怒りをこめて立ち上がる……。実在の保安官をモデルに、暴力には暴力で立ち向かう姿をパワフルに描く。“正義”を強調する社会派の人間ドラマ。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA GRANDE E´POQUE", "喜劇の黄金時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "1920年代から戦後近くに至るまでのスラップスティック・コメディの名シーンを集めた、R・クレールの解説によるアンソロジーもの。ハリウッド喜劇生みの親であるマック・セネットの主演作や、ローレル＆ハーディのドタバタシーンなど、珍品・逸品ぞろいの場面でまとめられ、映画ファンにはうれしい１本。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャイブ　海風に吹かれて", "ジャイブ　海風に吹かれて", "", "", "http://image.eiga.k-img.com/images/movie/53897/315x210.jpg?1797", 109, "東京でIT企業を経営する哲郎は、祖父の法事に出席するため北海道・江差へと帰郷する。共同経営者の裏切りを知ってしまった哲郎は、故郷で20年振りに再会した同級生の由紀と飲みに出かけ、青春時代の思い出を懐かしく語り合う。そこでヨット部だった哲郎は、北海道を無寄港で一周するという無謀な挑戦を思いつく。監督は主にピンク映画界で活躍し、“ピンク四天王”として知られるサトウトシキ。", "2009-06-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゆけゆけ二度目の処女", "ゆけゆけ二度目の処女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "映画におけるテロリズムを追求し続けた若松監督の初期作品。冒頭から前半部はピンク映画のノリだが、中盤以降の展開は実験的なラブ・ストーリーへと転じていく野心的な構成だ。マンションの屋上を密室空間として捉えたセンスにも、映画作家としてのこだわりがある。屋上で男たちに暴行された少女に、少年は淡い恋心を抱く。「若松孝二 初期傑作選 DVD-BOX３」に収録。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("續姿三四郎", "續姿三四郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "黒澤明の記念すべき監督デビュー作品「姿三四郎」の続編。前作のラストでライバルを倒し旅に出た三四郎の帰還から、その後の格闘遍歴を描く。出演者も前作とほぼ同じで、三四郎に敗れた源之介を演じた月形龍之介が彼の弟に扮し、兄の復讐に燃える鉄心役で再登場している。クライマックスはその鉄心と三四郎が極寒の雪山で壮絶な死闘を繰り広げる。", "1945-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日曜日は終わらない", "日曜日は終わらない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "'05年に急逝した林由美香が出演したハイビジョン・ドラマ。実父に“捨てられた”青年が義父を殺すまでを描き、カンヌ映画祭に正式出品されるほどの珠玉の青春劇だったが、放映後、劇場公開はされたがソフト化されることはなく、幻の作品となっていた。林は主人公が出会うランパブ嬢を好演し、深い余韻を残す。離婚した母の許に身を寄せる一也。義父と折り合いが悪い彼は、ランパブ嬢の佐知子と出会い、束の間の安らぎを得るが……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONEY TALKS", "ハネムーンはモンテカルロで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "「第三の男」で知られる作家G・グリーンの『負けた者がみな貰う』を映画化。数学者である会計士バートラムは、結婚直前に社長から夢のようなハネムーンをプレゼントされ、新妻キャリーとモンテカルロへ向かう。だが、あとから来てすべての経費を払うはずの社長が来ない。バートラムは、持ち前の数学の才能でカジノに挑戦するが……。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EARTH GIRLS ARE EASY", "ボクの彼女は地球人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "結婚を間近に控えながら婚約者に浮気され、失意のどん底に陥ったヴァレリー。そんな時、偶然彼女の前に宇宙船が落ち、星の王子さまならぬおかしなエイリアンが現れる。やがてそのうちの一人を真剣に愛するようになるヴァレリーだが……。当時、アメリカ映画界のおしどり夫婦と呼ばれたゴールドブラムとデイヴィス共演のSFラブ・コメディ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOYZ’N THE HOOD", "ボーイズ’ン・ザ・フッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "犯罪多発地区として、有名なロサンゼルスのサウスセントラルに住む黒人青年トレは、厳格な父親のしつけのおかげでまじめに働く孝行息子であった。しかし、近所の幼なじみの車に同乗したある日、ギャング同士の撃ち合いに巻き込まれ、警察に逮捕される。あたかもギャングの一員のように扱われたことにショックを受けるトレ。そして親友が射殺されたことを知った彼は、今までの非暴力主義を捨て、銃を手にするのだが……。黒人監督J・シングルトンのデビュー作で、各国で絶賛を受けた話題作。アメリカでは公開初日に各地で発砲事件が起こり問題となった。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女人的故事／WOMAN’S　STORY", "女人故事　女のものがたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "北京電影学院でチャン・イーモウやチェン・カイコーらと同期だった女流監督ポン・シャオリエンの作品。農村へ下放したときの経験をもとに、封建社会で苦悩する３人の女性の姿を描く。長男に嫁いだために義兄弟たちの結婚資金を作る来子、兄の結婚のために交換条件で口の不自由な男と結婚する金香、そして一家の生活費を稼ぐ小鳳。悲しい運命を背負った女性３人は、毛糸玉を売りに行った北京で、初めての都会に興奮するが、田舎者である彼女たちはつらい思いをする。やむを得ず重慶の町で商売をすることになった３人はは、稼いだ金を持って再び村へ帰るのだが……。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Insanitarium", "ゾンビ・ホスピタル", "", "", "http://image.eiga.k-img.com/images/movie/54587/315x210.jpg?4034", 89, "ジャックは自殺未遂した妹リリーに会うため搬送先の病院に行くが、病院側にリリーとの面会を許してもらえなかった。病院の対応を不審に思ったジャックは院内に潜入し、妹を見つけ出して脱出を試みるが、人体実験でゾンビ化した患者たちが次々にジャックとリリーに襲いかかってきて……。主演は人気TVシリーズ「デスパレートな妻たち」に出演したジェシー・メカトーフ。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODY BAGS", "ボディ・バッグス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "J・カーペンターがプロデュースした３話のオムニバス・ホラー。他殺、自殺、事故死で命を失いボディ・バッグ（死体袋）に収められた変死体にまつわる、それぞれの恐るべきエピソードが描かれる。R・コーマンやS・ライミら大御所の出演も、マニアは必見。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陳宝的故事", "チンパオ —陳宝的故事—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "元小学校教諭、黒薮次男が大戦中の中国での体験をもとに執筆した児童文学『少年の目』をベースにした日中合作映画。76歳になる相澤健は戦争中に駐屯していた中国、桂林を訪れ、当時のことを回想していく。相澤のいた隊は食料調達と称して中国農民の家畜や穀物を略奪していた。そして両親を失った幼い兄妹が飼っていた牛も奪ってしまう。だが、その幼い兄妹は家族同然の牛を返してほしいと部隊の後を追ってくる。その懸命さに相澤や上官も心打たれるのだった……。戦争が生んだ悲劇を切なく綴ったヒューマン・ドラマ。", "1999-01-01", "76,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本侠客伝 白刃の盃", "日本侠客伝 白刃の盃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "銚子の博徒・外川一家は堅気の運送業を始め、順調に繁盛していた。一方、元幹部の根占は外川運送の乗っ取りを企らんでいた。東京から流れてきた風来坊の大喜多俊二は、一家の温かい人情にふれる。根占のあまりの無法ぶりに、俊二は死を覚悟で殴り込んだ。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MON−RAK　TRANSISTOR", "わすれな歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "前作「６ixtynin９」で“タイのタランティーノ”と絶賛されたペンエーグ・ラッタナルアーンが描くノスタルジックな愛の物語。天真爛漫な青年ペンと村一番の美しい娘サダウ。めでたく結ばれた二人は、美しい自然に囲まれたタイののどかな村で、慎ましくも幸せに暮らしていた。だがある日、ペンが兵役に駆り出される。身重の体でけなげに愛する夫の帰りを待つサダウ。ところがペンは不運な事件をきっかけに逃亡、放浪して身を隠さざるを得なくなる……。劇中、ペンが歌う歌はスパコン・ギッスワーンがすべて吹き替えなしで挑戦。少女のような純粋さと強い心を併せ持った女性サダウをタイの国民的女優シリヤゴーン・プッカウェートが熱演している。", "2002-01-01", "23", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("必殺！三味線屋・勇次", "必殺！三味線屋・勇次", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "人気時代劇“必殺”シリーズの第７作。前作で絶命した中村主水に代わり、主人公となるのは中条きよし演じる三味線屋の勇次。藤田まことは上方から江戸に流れてきたかつての仕事人の元締、伝兵衛という役どころで出演している。回春効果のある媚薬、回春丸をめぐって陰謀を張り巡らす大店と役人に、すべてを知った勇次とその仲間たちが立ち向かっていく。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("奇跡の山 さよなら、名犬平治", "奇跡の山 さよなら、名犬平治", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "母の自殺によって言葉を失った少女、敦子。大分・九重連山で案内人をする祖父、清三とともに母の死を知る子犬をガイド犬に育て、自らの言葉も呼び戻すまでの物語。実在したガイド犬、平治の話をベースにTV出身の水島総が人間愛を描く。中島みゆきがこの映画のために主題歌を書き下ろしている。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浪人街", "浪人街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "マキノの出世作「浪人街／第１話・美しき獲物」(1928)を潤色した２度目のリメイク版（１度目は1951年の「酔いどれ八萬騎」）。山上伊太郎のオリジナル・シナリオにマキノ自身と、山上と同じく長谷川伸の門下生だった村上元三が手を加えてできあがった。江戸中期の浅草をうろつく浪人たち。そんな一人荒牧源内は女房・お新にスリをさせて放蕩三昧の日々を送っている。一方、赤牛弥五衛門は金で頼まれ、源内の短刀を狙っている。お新が悪党旗本に誘拐され、救出に向けて二人は力を合わせるのだった……。ちなみにマキノは1957年に監督した５本中４本が自作のリメイクだった。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHO KILLED VINCENT CHIN？", "誰がビンセント・チンを殺したか？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "1982年、日本企業の進出で不況にあえぐ“自動車の街”デトロイト。失業中の自動車工イベンスに、日本人と間違われた中国人のビンセント・チンは、口論の末バットで殴り殺される。なぜイベンスは殺すまでにこの中国人を憎んだのか。アメリカに住むアジア系ドキュメンタリスト、チョイが長時間にわたるインタビューやニュースソースを巧みにつなぎ合わせながら問題を浮き彫りにした衝撃作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("釣りバカ日誌", "釣りバカ日誌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "『ビッグコミック・オリジナル』誌連載の人気釣りマンガの映画化。自分の会社の社長の顔も知らない脳天気なサラリーマン浜崎伝助は、生きる価値のすべてを釣りに注ぎ込んでいた。こんなダメ社員のハマちゃんが、ひょんなことから社長の鈴木一之助に釣りを教え込むことになるのだが……。「植村直己物語」「敦煌」など大作の続いた西田敏行が、持ち前のコミカルな演技を披露している。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("短編映画集「kino」", "短編映画集「kino」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "一大ブームを巻き起こした“だんご３兄弟”の生みの親としても知られるCFクリエイター、佐藤雅彦の初監督作品。６つの短編で構成されており、アニメーションの「ポイント」以外は、ルーマニアで撮影された無国籍な味わいのショート・ストーリーである。街頭に本作の巨大ポスターを貼りつけるイントロダクション、電源スウィッチをめぐる一コマ「ホテル・ドミニクの謎」、大人の視点と子供の目線を交錯させる「大人の領域、子供の領域」など、いずれもほのぼのムードで一貫した内容。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANHATTAN MELODRAMA", "男の世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "遊覧船の事故で孤児となったブラッキーとジムは、その事故で妻子を亡くした老人に育てられる。30年後、ブラッキーは暗黒街の顔役に、ジムは地方検事になっており、めったに顔を合わさないものの、友人であることに変わりはなかった。そして、ジムが知事選挙に出馬したとき、彼の腹黒い助手がデマを流してジムを陥れようとする。それを知ったブラッキーはジムには内密にその男を射殺、逮捕されるのだが、何も知らぬジムは地方検事として彼に死刑を求刑する……。若きC・ゲーブルが友情に厚い男を演じた人間ドラマ。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新忍びの者", "新忍びの者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "秀吉暗殺に失敗し、三条河原で釜煎りにされた石川五右衛門は、実は徳川方の服部半蔵の手によりひそかに助け出されていた。天下を狙う家康は、妻子を秀吉に殺された私憤を持つ五右衛門の力が必要だったのだ。淀君に若尾文子、秀吉には東野英治郎と豪華な配役。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伝七捕物帖・人肌千両", "伝七捕物帖・人肌千両", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "土師清二の原作を高田浩吉主演で映画化。十二代将軍徳川家慶の時世、“泥棒令状”という前ふれの矢文を放って暴れる兇賊“疾風”が江戸に現れ、黒門町の伝七が探索にあたる。近衛十四郎が悪役に扮して助演。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美味しんぼ", "美味しんぼ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "『ビッグコミック・スピリッツ』連載の人気同名コミックを映画化。東西新聞文化部の山岡士郎と、陶芸家の海原雄山。ともに美食家同士の因縁の親子が雑誌社の企画で“料理対決”を行う。原作にあるウンチクや、TV版の娯楽性とは一線を画した森崎東の演出は、一種の崇高さすら漂う堂々たるもの。実際の親子、三國連太郎と佐藤浩市の共演がはらむドキュメンタリーのような生々しさもさることながら、終盤における“味”をめぐる映像考察の力強さには圧倒される。クライマックスの樹木希林の名演も見逃せない。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HALLOWEEN５：THE　REVENGE　OF　MICHAEL　MYERS", "ハロウィン5　ブギーマン逆襲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "一人の少女に執ように襲いかかるブギーマンの恐怖をスリリングに描いた、人気シリーズの第５作。残虐な手口で無差別殺人を繰り広げる、ブギーマンのその目的とは……？", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("右門捕物帖 紅蜥蜴", "右門捕物帖 紅蜥蜴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "大友柳太朗扮するむっつり右門の推理が冴える時代捕物帖。江戸の音羽の滝見茶屋“新月”のそばで、いかけ屋権十が殺されていた。犯人は黒いカラスを連れ、紅蜥蜴の刺青を施した男らしい。江戸の町は紅蜥蜴の噂でもちきりになったが、右門はその男を犯人と断定することに疑問を感じ、独自の捜査を始めた。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが青春に悔なし", "わが青春に悔なし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "戦前の京大・滝川事件と戦中のゾルゲ・スパイ事件のふたつをモチーフに、ひとりの女性の反戦活動を描いたドラマ。“永遠の処女”原節子が芯の強いヒロインを好演。大学教授の一人娘・幸枝は学生たちのマドンナだった。野毛と糸川も幸枝に思いを寄せるライバルだが、野毛は反戦運動に身を投じ、糸川は取り締まる側の検事にと正反対の道を選ぶ。幸枝は信念を持って行動する野毛に惹かれ結婚するが……。ファシズムの吹き荒れる時代、自ら信じる道を強く生きる女性の姿を謳い上げたドラマ。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アッと驚く為五郎", "アッと驚く為五郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "関東大震災の日にアッと驚いた母親から生まれ、戦時中もアッと驚く体験を重ねて、過去にあまりにも驚きすぎて、今や決して驚かない男となった“為五郎”を主人公にした爆笑喜劇。1960年代から70年代のTV番組『ゲバゲバ90分』で人気となったハナ肇のキャラクターを映画にそのまま引用。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鋼鉄の巨人／地球滅亡寸前", "鋼鉄の巨人／地球滅亡寸前", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 39, "子供たちはカピア星人の魔城にさらわれるが、スーパー・ジャイアンツにもらった玉を投げるとジャイアンツが出現、子供たちを救い出す。カピア星人は魔術の力で地球上のあらゆる物の運行を止めるが、スーパー・ジャイアンツの活躍で平和が戻る。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUNNY LADY", "ファニー・レディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "実在のブロードウェイ女優ファニー・ブライスの伝記で、1968年にウィリアム・ワイラー監督によって作られた「ファニー・ガール」の続編の形をとっている。前作で苦労の末、大女優に成長したファニーが再婚、私生活に舞台に再び大活躍するという物語。バーブラの熱演はもちろんのこと、歌われる曲の数々も素晴らしい。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HO＾TEL DU PARADIS", "巴里ホテルの人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "パリの裏通りにひっそりとたたずむ“天国のホテル”を舞台に、そこに出入りする人々の人生模様を綴った作品。引退間近の老俳優を中心に、映画監督になることを夢見る貧乏青年や、恋に疲れて男のもとから逃げてきた女性写真家など、様々な人生の悲哀が淡々としたタッチで描かれている。ドキュメンタリストとして活躍してきたプラハ生まれのヤナ・ボコーヴァ監督の長編劇映画第１作。亡命者として青春のほとんどを過ごしたパリに対する、彼女の思い入れが感じられる一編。", "1986-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録・安藤組外伝／餓狼の掟", "実録・安藤組外伝／餓狼の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1988年の「疵」でも描かれた花形敬。33歳で殺されるまで、拳銃や刃物を用いず己の拳のみで闘い、“素手コロ”と呼ばれてヤクザ社会の伝説となった彼の熱い生き様を描く。戦後の混乱が続く昭和20年代、渋谷を中心に縄張りを拡大する安藤組にあって、花形敬は組内外を問わず恐れられる存在となっていく。そして、花形の猛り狂う狼のような暴走は加速を増していくが、組長が逮捕される事件が起き、彼は安藤組をまとめていこうとする…。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TANGO AND CASH", "デッドフォール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "スタローンが、アルマーニのスーツに身を包み、ダンディに登場する刑事アクション。ロス市警内で一、二を争う捜査能力を持ちながら互いにソリのあわないレイとゲイヴ。そんな二人に予期せぬトラブルが降りかかり、彼らは殺人容疑で逮捕されてしまう。裁判の結果は有罪。“悪徳刑事”の汚名をそそぐべく、ライバル同士は燃えるような復讐心と闘争心の中に身を躍らせるが……。男たちの対決と友情をテーマに、凄絶なガンファイトとアクション、ドライなユーモアとロマンスが交差する。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ツィゴイネルワイゼン", "ツィゴイネルワイゼン", "", "", "http://image.eiga.k-img.com/images/movie/37867/315x210.jpg?3992", 144, "「殺しの烙印」（1967）、「悲愁物語」（77）の鈴木清順監督が、内田百間の「サラサーテの盤」などいくつかの短編小説をもとに、夢と幻が交錯するなかで狂気にとりつかれた男女の愛を描いた幻想譚。大学教授の青地と友人の中砂は、旅先の宿で小稲という芸者と出会う。1年後、中砂から結婚の知らせをうけた青地は中砂家を訪れるが、新妻の園は小稲に瓜二つだった……。80年、東京タワーの下に建造されたドーム型の移動式映画館シネマ・プラセットで上映されたことも話題に。日本アカデミー賞の作品賞ほか、ベルリン国際映画祭の審査員特別賞を受賞するなど国内外で高い評価を受けた一作。2012年、“浪漫3部作”を成す「陽炎座」（88）、「夢二」（91）とあわせてニュープリントでリバイバル上映。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEMORIES", "MEMORIES", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "大友克洋がアニメーションによる映像表現の最高峰を目指して製作したオムニバス作品。宇宙空間のゴミ処理業者が、ソプラノ歌手の怨念が作り上げた不思議な浮遊物に誘い込まれる叙情的恐怖譚「彼女の想いで…」。人間を意識不明にするガスを身体から発生させる薬を、ある男が誤って飲んでしまったことから起こる騒動を描く「最臭兵器」。敵めがけて繰り返される砲撃、子供たちの軍事訓練など、重武装した巨大な移動島都市の１日をワンカットでパノラマ式に見せる「大砲の街」の３本からなる。「AKIRA」の作画監督補を務めた森本晃司が「彼女の想いで…」を、「YAWARA！」の演出を務めた岡村天斎が「最臭兵器」を、大友自身が「大砲の街」を監督している。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMINENT DOMAIN", "密告者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ベストセラーとなったノンフィクションに基づく社会派サスペンス。舞台は冷戦下のポーランド。共産党の上層部に登り詰めたヨゼフは、妻子とともに何不自由なく暮らしていた。そんなある日、ヨゼフが出勤してみると、オフィスは何の前触れもなく消滅していた。同僚は彼を避け、しかも自宅からは無数の盗聴器が発見される。やがて見えない魔の手はヨゼフの家族にも迫り……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋の季節", "恋の季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "当時100万枚のセールスを突破した大ヒット曲、ピンキーとキラーズの『恋の季節』をモチーフにした青春歌謡映画。リード・ボーカルの今陽子が主演を務め、メンバーたちも出演した一作だ。また、'71年のTVドラマ『おれは男だ』の名カップル、森田健作と早瀬久美も出演。ピンキーと洋子は卒業試験を終え、社会へ旅立つことに。しかし洋子は二人の男を天秤に掛け、デートに勤しむが……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FAMILY OF THE VICTIM WILL NOT BE WARNED", "シシリアン・マフィア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "マフィアの裏切り者として殺された父親の仇を討つため、機会をうかがい続ける息子。やっとその機会がめぐってきた。息子はまず父親を殺した男を探し出して暗殺に成功、さらにその男のボスに取り入る。こうして彼は自分の勢力を次第に広げていくのだが……。T・サバラスが貫禄たっぷりに演じるアクション。", "1972-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グッドバイ夏のうさぎ", "グッドバイ夏のうさぎ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "劇映画「アイコ十六歳」のオーディションや、撮影風景を納めたドキュメンタリー。いわゆる“メイキングもの”の一種だが、冒頭で田舎の中学生だった富田靖子が、オーディションを経てアイドルへと変身を遂げていくさまをリアルに捉えている。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЧEБУPAШКА", "チェブラーシカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 64, "20年以上前にロシアで製作された、とびきりキュートなパペット・アニメーション。ロシア国内の有名児童文学を、ロシア・アニメの巨匠ユーリ・ノルシュテインの師にあたるカチャーノフ監督が映画化したもの。映画は３つの短編で構成される。オレンジの木箱から現れた茶色い体に大きな耳の小動物。果物屋から“ばったりたおれ屋さん”という意味のチェブラーシカと名づけられるが学問上正体不明、ということで動物園では引き取ってもらえない。そんな彼が友達募集中のワニ、ゲーナと出会い“友だちの家”を作る「こんにちはチェブラーシカ」(1969)、日本でいうボーイスカウトのような課外組織ピオネールの入団をめぐって、チェブラーシカとゲーナが奮闘する「ピオネールに入りたい」(1971)、前述２作にも登場するいじわるばあさんに、海水浴へ行こうとするチェブラーシカとゲーナが邪魔をされ、散々な目に遭う「チェブラーシカと怪盗おばあさん」(1974)。", "1969-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NATIONAL SECURITY", "ナショナル・セキュリティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "お調子者の黒人と堅物の白人という、警備員コンビの奮戦を描いたアクション・コメディ。ユーモアあふれる主人公二人の衝突はもちろん、負け犬の奮起が醸し出す痛快な味わいも魅力的だ。無実の黒人を暴行したと誤解され、服役した元警官ハンク。出所後、彼は警備会社に再就職するが、同僚には、なんと誤解を招いた因縁の男アールが。彼らは嫌々ながらコンビを組むハメになり……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小さな死", "小さな死", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 26, "ゲイの青年が疎遠になっていた父親が危篤であることを姉から知らされ、病院に見舞いにいくが、父との和解することはできなかった。翌日、彼は父親の病室に忍び込み、裸の父親の写真を撮るのだが……。", "2004-05-28", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DARK ANGEL", "ダーク・エンジェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "肉体派スター、D・ラングレンがニューヨーク市警麻薬課刑事に扮して、人間を襲っては麻薬漬けにして、そこから生じる快感ホルモン“エンドルフィン”を採取する殺人エイリアンに挑んでいくSFXアクション。初めて人間的で表情に変化のある役を演じるラングレンに注目したい。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WATTSTAX", "ワッツタックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1972年８月20日、ロサンゼルスのメモリアル・コロシアム。真夏の太陽が照り返すなか、ソウル・ミュージックの名門スタックス・レコードのアーティストが勢ぞろい。、黒人の観衆10万人が見守るなか、７時間にわたって繰り広げられた大野外コンサートの一部始終を収録。ファンには見逃せないライブ・ドキュメントだ。K・ウェストン、ルーファス、A・キング、L・ミルトンなど、そうそうたる顔ぶれが次々に登場する。また、コンサート・シーンに挟んで、ワッツに住む黒人たちの主張や風俗・習慣を紹介。単なるコンサート・フィルムとは一線を画すアメリカ黒人の生の姿を映し出した、社会性の高い作品としても評価されている。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEEP THROAT", "ディープ・スロート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "公開当時全米でセンセーショナルな話題を呼び、物議をかもしたポルノ映画の問題作。その当時の反響を記録したドキュメンタリー「インサイド・ディープ・スロート」が2004年に公開されたことも本作を伝説にしたといえる。1975年の日本公開時には「喉の奥深く」というタイトルで２本立てでの公開だった。タイトル名のセックスのテクニックが、過剰なまでに披露されていく。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　DESTINY　OF　HER　OWN", "娼婦ベロニカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "16世紀後半の水の都ヴェネチアの風俗を、華麗な映像で再現した人間ドラマ。実在の女性ベロニカ・フランコの半生を映画化したもので、青年貴族との身分違いの恋に破れた彼女が高級娼婦となる道を選び、魔女扱いされてしまう波乱の運命をたどっていく。まさに才色兼備のポジティブなヒロインを、C・マコーマックがハツラツと演じている。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雲霧仁左衛門", "雲霧仁左衛門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 160, "10年前にかぶせられた無実の罪のうらみを晴らそうと、武家社会を捨て盗賊となり、豪商ばかりを襲って大金を奪い去る怪盗・雲霧仁左衛門。最後の仕事として尾張屈指の呉服商を狙うが、火付盗賊改めとの死闘が始まった。松竹では４年ぶりに製作された大作時代劇。DVDタイトルは「雲霧仁左衛門　修復デジタルマスター版」。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗戀桃花源／THE PEACH BLOSSOM LAND", "暗戀桃花源", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ここは台北の劇場。ある劇団が『暗戀』というメロドラマの芝居の稽古を始める。するとそこへ、別の劇団が『桃花源』という喜劇の舞台稽古にやって来た。劇場側の手違いから予約が重なってしまったのである。両者は互いに譲らず、交互に劇の断片を演じていく。そんなとき、一人の若い女性が現れて……。世界各地で上演された舞台『暗戀桃花源』を、その劇作家兼演出家であり、台湾演劇界のリーダー的存在でもあるS・ライが自ら映画化。ファンタジーから現実にたどり着く人間たちの物語を描き出している。ウォン・カーウァイ作品でおなじみの撮影・美術コンビが参加。ビデオ発売時タイトルは「楽園のかなたに」。", "1992-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AN AUSTRALIAN IN ROME", "誘惑の香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "大女優となったN・キッドマンが20歳の頃に主演したラブ・ロマンス。兄弟に愛された女の苦悩を演じる。フランスを旅するオーストラリア人女性ジルは魅力的な若者PGと出会い、恋に落ちるが、彼は交通事故で重傷を負ってしまう。それでも病床で彼女にプロポーズするPG。しかし、彼は厳格な貴族の家柄で、ジルは母親に冷たくあしらわれる。そんな彼女の味方になってくれたのはPGの弟ジャンマルコだった。ジルは許されないと知りながら、彼の誘惑に負けてしまい……。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宮 Love in Palace THE MOVIE 第1章", "宮 Love in Palace THE MOVIE 第1章", "", "", "http://image.eiga.k-img.com/images/movie/55617/315x210.jpg?2011", 0, "現代の韓国に王室が存在しているという設定で大ヒットした少女漫画が原作のTVドラマを、劇場版3部作として再編集した第1作。主演のユン・ウネ、チュ・ジフン、John-Hoonら若手人気俳優が多数出演した。立憲君主制をとる韓国の王室では、体調を崩した皇帝の跡継ぎ・シン皇太子の結婚準備が進められていた。政略結婚を避けたいシンは恋人のヒョリンにプロポーズするが、平凡な女子高生チェギュンと結婚させられてしまう。そんな中、シンの従兄弟ユルが留学先のイギリスから帰国してくる。", "2010-07-17", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("W/O", "W/O", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "東京大学駒場寮内で創作活動を行っていた長谷川宏紀が、仲間との２年間を追ったドキュメンタリー。学歴も国籍も様々な自称アーティストたちが、寮に勝手に住みつき、建物が壊されれば別の棟に移り住むという生活を続けながら、“オブスキュア”というギャラリーを運営。研ぎ澄まされた感性がとらえた日常の風景、体験する数々の出来事をデジタルカメラで収めていく。荒々しくもリアルな息遣いが感じられる映像のコラージュは、観る者の心を刺激する。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAMILY PLOT", "ヒッチコックのファミリー・プロット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "女霊媒師と運転手の詐欺コンビ、身代金には必ずダイヤを要求する誘拐の達人カップル。この二組のアベックの遭遇を、軽妙洒脱に語るヒッチコックの監督第53作。残念ながら本作が遺作となった。運転手に扮したB・ダーンの好演が光る。DVDタイトルは「ファミリー・プロット」。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("博徒解散式", "博徒解散式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "８年ぶりにシャバに戻った岩崎組の幹部・黒木は堅気になった組長から、運輸会社の経営をまかされる。しかし、昔の仲間の唐沢も運輸会社を経営しており、黒木の会社を潰して仕事を独占しようと企てる。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Huevos de oro", "ゴールデン・ボールズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「ハモンハモン」に続いてB・ルナが物質主義社会を風刺したコミカルな物語。タワー建設という夢のためなら何でもするという精力的な男ゴンザレス。金のために銀行員の娘と結婚し、成功への階段を上る彼の野心はもはや尽きることがない。彼は次に、妻や愛人と一度にベッドをともにするという大胆な行動に出るが……。", "1993-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAFE SOCIETY", "カフェ・ソサエティ 背徳の群れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1950年代にN.Y.で実際に起きた売春摘発事件の実話に基づく愛憎劇。売春事件を追ってナイトクラブに潜入した若き刑事ジャックは資産家の御曹司ミッキーや、その恋人パットと親しくなった。やがてパットの悲惨な過去が明らかになった時、３人の友好関係はきしみ始める。売春斡旋業を始めたミッキーはパットにも売春を強要。打ちひしがれたパットを、今度はジャックが捜査に利用しようとする。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("御法度", "御法度", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "誰もが知る新撰組の世界を、大島監督がまったく新しい解釈で綴った異色時代劇。厳しい戒律によって結束を固めてきた新撰組に、ある日妖しい美貌の少年が入隊したことから起こる隊士たちの騒動を描いた時代絵巻。匂い立つような男の殺気と色気、愛と闘いをドラマティックに美しく描きあげる。隊士を惑わす主役の美剣士役には、故・松田優作の長男・松田龍平を大抜擢し、豪華キャストが脇を固める。また、ワダエミ手がける衣装の斬新さ、夜間の殺陣シーンの美しさなど見所も多い。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妹", "妹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「赤ちょうちん」に続き、藤田敏八監督＝秋吉久美子主演で送る青春歌謡映画第２作。嫁ぎ先から突然帰ってきてしまった妹とその兄の近親相姦的愛情を描く異色作。引っ越し屋をやっている秋夫のもとに結婚したはずの妹・ねりが、ひょっこり帰って来る。とにかく秋夫は家に置いてやることにするのだが、どうも家出の理由がよく分からない。親戚連中に聞くと、ねりの夫は行方不明らしく、その原因は妹にあるという。秋夫はとにかくねりを家に戻そうとするが、ある日「夫を殺した」と告白される……。白けた表情はしているが、一方で何をするか分からないという秋吉久美子の奔放なイメージは、この作品で定着した。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の国", "風の国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "作家などで活躍するマルチ・プレーヤーー、戸井十月の監督作。蔵王の自然を乱開発から守るために立ち上がった７人の男女。彼らとレジャーランド建設をたくらむ大企業の戦いを痛快に描いたアクション・ロマン。バイク・アクションが見ものの、モトクロス版「七人の侍」ともいえる作品である。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("古墳ギャルのコフィー 12人と怒れる古墳たち", "古墳ギャルのコフィー 12人と怒れる古墳たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「秘密結社　鷹の爪」でおなじみのFLASHアニメクリエイター・FROGMANが、古墳たちが通う学園を舞台に描くナンセンスコメディ「古墳ギャルのコフィー」の劇場版第2作。文化祭の出し物の練習をしていた前方後円墳ギャルの女子高生コフィーは、近所に住む阿武隈さんにうるさいと怒られてしまう。腹を立てたコフィーは阿武隈さんが大切にしているマンドラゴラの盆栽を投げ捨てて壊してしまい、器物損壊罪で起訴され裁判にかけられるが……。「秘密結社 鷹の爪 THE MOVIE II 私を愛した黒烏龍茶」と同時上映。", "2008-05-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Welcome to the Punch", "ウェルカム・トゥ・ザ・パンチ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("薔薇の標的", "薔薇の標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "４年前に麻薬取引を密告され、弟分を殺されたあげく逮捕されてしまった一匹狼の若者が、暴力団組織を相手に繰り広げる復讐戦を描いたハードボイルド映画。舘ひろしの野性的な部分とナイーブな部分をうまく使い分けた。スピードと殺気に満ちた冒頭シーンも見もの。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("だれの椅子？", "だれの椅子？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "石坂洋次郎の原作を吉永小百合と渡哲也のコンビで映画化した青春もの。ある高校を舞台に、英語教師として赴任してきた美しい女教師をめぐって二人の男がしのぎをけずる。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2011-2012 ヘンデル・ヴィヴァルディ・ラモー他「エンチャンテッド・アイランド　魔法の島」", "METライブビューイング2011-2012 ヘンデル・ヴィヴァルディ・ラモー他「エンチャンテッド・アイランド　魔法の島」", "", "", "http://image.eiga.k-img.com/images/movie/57167/315x210.jpg?4233", 215, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を映画館で上映する「METライブビューイング」2011～12年シーズンの7作目。シェイクスピアの「真夏の夜の夢」と「テンペスト」の物語をベースに、ヘンデル、ビバルディ、ラモーらバロック期の作曲家による名曲を抜粋して新たに編曲されたオペラ。絶海の孤島に追放された魔法使いプロスペローは、娘のミランダを王子フェルディナンドと結婚させようと、手下の妖精アリエルに王子の乗った船を難破させるよう命じる。しかし、アリエルは間違えて2組の恋人たちが乗る別の船を難破させてしまい……。", "2012-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOMOKO／もっとも危険な女", "TOMOKO／もっとも危険な女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "直木賞作家、大沢在昌による日本を舞台にしたハードボイルド作品「相続人TOMOKO」の映画化作品。小柳ルミ子が「陽炎２」以来、４年ぶりに映画に出演、元CIAで完璧な容姿と頭脳を併せ持つヒロイン、トモコ役を体当たりで熱演している。アクションシーンをよりリアルに描く為、フィリピンのマニラに一大ロケーションを敢行。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江山美人", "エンプレス　運命の戦い", "", "", "http://image.eiga.k-img.com/images/movie/54407/315x210.jpg?1862", 95, "「セブンソード」のドニー・イェン、「インファナル・アフェア」のケリー・チャン、「花の生涯／梅蘭芳」のレオン・ライらが出演する歴史武侠アクション。「HERO」「チャイニーズ・ゴースト・ストーリー」などのアクション監督として知られるチン・シウトンが監督を務める。中国戦国時代、趙国と対立する燕国の王が討たれ、死に際に伝家の宝刀“飛燕剣”を王女の燕飛児に託す。しかし、王位を狙う王の甥が飛児の暗殺を企み刺客は放つ。", "2009-04-04", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ULTIMATE THRILL", "大滑走！ 死のゲーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "R・バトラー監督によるアップ・テンポなアクション・サスペンス。ある事業家が、若いスキーヤーを美貌の妻の不倫相手と疑い、追い詰めて殺す。それ以来彼は“死のゲーム”に取り憑かれ、雪原に新たな獲物を追い求めるようになる。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FAUST", "ファウスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アニメーション作家として、世界的に名高いJ・シュヴァンクマイエルの実写とアニメで構成された長編映画。ゲーテなど多くの文学者に書かれたファウスト伝説を独自の解釈により、映画化している。男が何かに引き寄せられるようにさびれた芝居小屋に入り込み、楽屋にあったファウストの衣装を着、ファウストを読み上げるうちに、彼は何者かが仕掛けた罠にはまっていく。やがて悪魔メフィストフェレスが現れ、彼は悪魔と契約してしまうのだった。", "1994-01-01", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大江山酒天童子", "大江山酒天童子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "日本三大伝説の一つ“大江山の鬼退治”をベースにした川口松太郎原作の映画化。源頼光に市川雷蔵、四天王の渡辺綱に勝新太郎、坂田金時に本郷功次郎、碓井貞光に島田竜三、卜部季武に林成年が扮している。酒天童子を反権力の象徴とした視点がユニーク。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sleeping Beauty", "スリーピング・ビューティー　禁断の悦び", "", "", "http://image.eiga.k-img.com/images/movie/57107/315x210.jpg?4721", 101, "川端康成の「眠れる美女」を新たな解釈で映画化した官能サスペンス。「エンジェル　ウォーズ」のエミリー・ブラウニングが主演を務め、大胆なヌードも披露した。学費を稼ぐため、カフェの店員や企業の事務などさまざまなアルバイトをしてきた女子大生のルーシーは、雑誌の求人で見つけた秘密クラブで一風変わった仕事を始める。最初は下着姿で男性たちに給仕するというものだったが、仕事内容は次第にエスカレートし、睡眠薬を服用し眠りにつくという謎めいたものになっていく……。", "2011-11-05", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOLE´RANCE", "グルメ・アカデミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "フランス革命の生き残り貴族マルモンの夢は美食学院（グルメ・アカデミー）を創設すること。ある日、彼のもとへ断食をしている修道僧アシュルスが来た。マルモンとアシュルスの奇妙な闘いが始まる。美男俳優R・エヴェレットが禁欲的な修道僧から、後に悪徳の塊のような偽貴族に変貌するさまを巧妙に演じる。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("電車男", "電車男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "書籍、漫画、朗読劇にもなり、話題集中のインターネット発の純愛物語を、数々のTVドラマを手掛けてきた村上正典が映画化。まったく女性に縁のなかったオタク青年が憧れの女性と出会ったことで、恋のアドバイスを求めたのは、ネット掲示板の住人たち。初恋の甘酸っぱさと、ネット上の不思議な連帯感が見る者の感動と共感を呼ぶラブ・ストーリーだ。22歳の地味な青年が、電車で出会った女性に恋をする。そんな彼の恋を、掲示板サイトの仲間たちが叱咤激励しながら見守っていた。青年は温かい励ましを受けて、勇気を与えられるが……。", "2005-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOUNCE", "偶然の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "G・パルトロウとB・アフレック共演による切ないラブ・ストーリー。クリスマス間近のシカゴ。大雪で飛行機が飛ばないため、空港で足止めされていた広告代理店に勤めるバディ。彼は家族のもとへ急ぐ男性にチケットを譲り翌日帰ることにするが、その飛行機が墜落。激しい後悔の念にさいなまれ、遺族を訪ねた彼は、二人の子供を抱えて懸命に生きる未亡人アビーに惹かれて、真実を告げないまま恋に落ちていく。皮肉な運命に導かれて出会った男女の苦悩と葛藤を、二大スターがそれぞれ好演。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二等兵物語・万事要領の巻", "二等兵物語・万事要領の巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "昭和20年の早春、南方派遣の途中で輸送船が沈没。古山と柳の両二等兵は、漂流中を渋川隊の飛行機に助けられる。二人は上官にワイロをつかませ航空基地に潜り込む。古山は航空熱にうかされ、飛行学校を受験するが……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AMANT", "愛人／ラマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "世界的大ベストセラーとなったM・デュラスの自伝的小説をJ・J・アノーが映画化。ヨーロッパのみならず日本でも大ヒットとなった。1920年代仏領インドシナを舞台に、金持ちの中国人青年と小間使いのフランス人少女との間に芽生えた、世間に許されない愛情を静かに、そして熱く描いている。", "1992-01-01", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Collateral", "コラテラル", "", "", "http://image.eiga.k-img.com/images/movie/1037/315x210.jpg?3930", 120, "トム・クルーズがプロの殺し屋ビンセントを演じるサスペンス。彼は一晩で5人の標的を抹殺するため、たまたま乗ったタクシー運転手マックスを脅迫、彼のタクシーで夜のロサンゼルスを廻る。ビンセントの目的を知った運転手マックスは、なんとかビンセントの企てを阻止しようとするのだが。監督は「ヒート」「インサイダー」のマイケル・マン。夜のロサンゼルスの暗い街並みを捕らえるために、ほぼ全編をデジタルで撮影。", "2004-10-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Turtle Diary", "海に帰る日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "書店に勤める孤独な中年男と、盛りを過ぎた女流絵本作家が水族館で知り合う。水槽の中の海亀を自分たちの日常と重ね合わせ、やがて二人はこの亀を海へ解き放とうと計画する。公共の施設からまんまと亀を助け出し、海へ逃がすという行動に全力を費やす男と女の姿が、哀しくも感動的。ピンターの脚本とハンナンのカメラ、それに二人の名優による演技が見どころ。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Bang Bang Club", "バンバン・クラブ　真実の戦場", "", "", "http://image.eiga.k-img.com/images/movie/57587/315x210.jpg?2691", 108, "ピュリッツァー賞を受賞した若き戦場カメラマンの苦悩を描いたノンフィクション小説を、ライアン・フィリップ主演で映画化。1990年代初頭、内戦状態にあるアパルトヘイト末期の南アフリカで、グレッグ、ケビン、ジョアオ、ケンの4人の戦場カメラマンは次々と決定的な瞬間を捕えていく。グレッグは人間が炎に包まれる写真でピュリッツァー賞を受賞し、命知らずな4人は「バンバン・クラブ」と呼ばれて注目を集める。グレッグに続き今度はケビンが、スーダンで飢餓状態の少女とハゲワシを撮影した「ハゲワシと少女」という写真でピュリッツァー賞を受賞するが、その写真は「報道か人命か」という議論を巻き起こす。刺激的な被写体を求め続ける4人は、次第に精神のバランスを崩していき……。ケビン役は「ジョン・カーター」「バトルシップ」のテイラー・キッチュ。", "2012-03-10", "10,67", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Edge of Darkness", "復讐捜査線", "", "", "http://image.eiga.k-img.com/images/movie/55297/315x210.jpg?2286", 116, "英BBCのTVシリーズを、7年ぶりの映画主演となるメル・ギブソン、「007 カジノ・ロワイヤル」のマーティン・キャンベル監督で映画化したアクションドラマ。ボストン警察の殺人課刑事トーマス（ギブソン）の自宅で、彼の一人娘のエマが殺害される。当初はトーマスが本来の殺しの標的だったと思われていたが、実はエマには秘密があることがわかり……。脚本は「ディパーテッド」でオスカーを受賞したウィリアム・モナハン。", "2011-07-30", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Catchfire", "ハートに火をつけて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "偶然にもマフィアの殺人現場を目撃してしまった、現代アートの芸術家アン。組織は彼女を消すため冷酷な殺し屋マイロを送り込むが、マイロはターゲットであるアンに対して奇妙な感情を抱き始めて……。もともとはD・ホッパーが監督を兼ねていたが、製作会社が勝手に編集をしたことに怒ったD・ホッパーがクレジットされることを拒否、裁判にまでなり、アメリカでは上映されなかった、いわくつきの作品（ヨーロッパ、日本では公開された)。D・ホッパーはその後、自ら編集し直してまったく別の作品に作り直している。その作品は「バックトラック」という邦題で日本でも1995年に公開された。なお、“アラン・スミシー”とは監督がトラブルなどでクレジットされない時にアメリカで使われる名前である。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アクション・グラフィティー", "アクション・グラフィティー", "", "", "http://image.eiga.k-img.com/images/movie/33507/315x210.jpg?4530", 98, "アクション俳優を目指す高校生たちの青春ドラマ。武道一家の長女、優は些細なことから高校の空手部を廃部に追い込んでしまう。退学に追い込まれそうだった優だが、優等生のまり子が、条件付きで優を高校退学から救い出す。しかし、その条件とは「アクション俳優クラスに編入すること」だった。主演は「パラサイトイヴ」の大村彩子、長谷部瞳。共演に津田寛治、秋本奈緒美、ケント・ギルバート、エド山口ら。", "2006-07-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUGAR", "シュガー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "「ハスラー・ホワイト」など、ポップでアナーキーな作品で旋風を巻き起こしたカウンター・カルチャーの申し子、ブルース・ラ・ブルースの短編小説を、気鋭の舞台演出家ジョン・パーマーが映画化。ダウンタウンの裏社会を舞台に、若者たちの危うい心情をリアルに描いた刺激的な作品。18歳の誕生日の夜、クリフは男娼のブッチと出会い、強く惹かれる。以来、行動を共にするが、ブッチはドラッグに溺れていて……。", "2005-09-10", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キューティーガール・美少女ボウラー危機一発", "キューティーガール・美少女ボウラー危機一発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "天才的な美少女ボウラーが、敵対するチームに戦いを挑むアイドル系のスポ根ドラマ。賭けボウリングが横行する時代、かつて世界一の実力と言われた黒岩が率いるボウリング・チームに、どこか影のある少女、マイが加入する。黒岩のチームは、町でボウリング・カジノをもくろむ一団と対立関係にあった。同じ頃、スゴ腕女性ボウラーと彼女を追う刑事が町に現れて……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPACEHUNTER: ADVENTURES IN THE FORBIDDEN ZONE", "スペースハンター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "西暦2136年、疫病に侵された禁断の惑星“テラ・イレブン”。ここに迷い込んだ３人の女性旅行者たちを独裁者の魔の手から救出するため、賞金稼ぎが荒っぽい活躍を展開する。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オーロラの下で", "オーロラの下で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "３年の月日をかけ、オール・シベリアロケで作られた日ソ合作。1925年に極寒のアラスカで伝染病の血清を届けた犬ゾリのリーダーのオオカミ犬と人間との友情を描いたもの。舞台をシベリアに置き換えた本編では、オオカミ犬の“ブラン”と、日本人・源蔵の熱い友情が綴られる。極寒の零下50度のなか、演技をした役所広司の熱演が見もの。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONDAY", "MONDAY", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ある朝目覚めた男は、何も覚えていなかった。胸ポケットには、お清めの塩。男の脳裏に、断片的な記憶が蘇ってくる。葬式、喫茶店、BAR、やくざに正義の味方……。酒に酔った男が巻き起こした大騒動を、超高速のストーリー展開で描ききったファンキー・エンターテインメント・ムービー。“初心に返ってやりたいことをやった”という言葉通り、監督第４作にしてSABUワールドの集大成的作品になった。ベルリン映画祭国際批評家連盟賞受賞。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Crazy, Stupid, Love.", "ラブ・アゲイン", "", "", "http://image.eiga.k-img.com/images/movie/56027/315x210.jpg?4167", 118, "スティーブ・カレル、ライアン・ゴズリング主演で描くロマンティックストーリー。40代の生真面目な男カルは、妻エミリーが浮気をし離婚を切り出される。高校時代からつきあっていたエミリー以外の女性とデートもしたことがないカルだったが、ある夜、バーでプレイボーイのジェイコブと知り合い、ジェイコブの助言でファッションや髪型を磨いて新しい人生を歩もうとする。監督は「フィリップ、きみを愛してる！」のグレン・フィカーラとジョン・レクア。共演にジュリアン・ムーア、エマ・ストーンら。", "2011-11-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UPTOWN GIRLS", "アップタウン・ガールズ", "", "", "http://ecx.images-amazon.com/images/I/51rYji0WZIL._SL210_.jpg", 93, "「８ Mile」の人気女優B・マーフィと、天才子役D・ファニングが共演したハートフル・コメディ。親の遺産で暮らすワガママなお嬢様と、健康オタクで口やかましい８歳の少女。相反する二人の間に芽生えた友情を、大都会ニューヨークを舞台に綴る。ロック・スターだった親の遺産でド派手な暮らしを満喫していたモリーは、遺産を管理していた男の横領によってすべてを失ってしまった。途方に暮れる彼女だったが、生活するため仕方なく友達に紹介してもらったベビー・シッターの仕事を始める。", "2004-02-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEFFREY", "ジェフリー！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ゲイで俳優志願のジェフリーは、エイズの猛威を目の当たりにして“セックス断ち”を決意。そんな矢先に理想の男性スティーブと出会い、思わずデートの約束を取り付けるが、彼はHIV感染者だった……。オフ・ブロードウェイで大ヒットした作品の映画化。舞台の演出を手掛けたC・アシュリーが監督を務め、ジョークに覆い隠された深遠なテーマに迫っている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY DARLING CLEMENTINE", "荒野の決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ワイアット・アープは３人の兄弟を率いて牛を追ってカリフォルニアへ向かっていた。途中、アリゾナ州のある町に寄るが、ちょっとしたすきに末弟を殺され牛を盗まれる。荒野で出会ったクラントンと名のる牛買いを犯人とにらんだワイアットは、復讐のため、その町の保安官になる。そして西部に名だたる飲んだくれの賭博師ドク・ホリディの力を得て、クラントン一家に迫る。そんな折、ドクを追って東部からやって来た、美しい娘クレメンタインに一目惚れ。ドクは肺病で自分の命が長くないことを知っていて彼女に帰れとすげない。酒場の女チワワの首飾りが死んだ末弟のもので、クラントン一家の息子にもらったと知ったワイアットは、OK牧場での決闘を申し込むのだった。愛唱歌として世界中で親しまれている歌『いとしのクレメンタイン』が優しく響く、フォードの最高傑作。西部劇の古典であると同時に、メロドラマの古典として後世に語り継がれる一編。", "1946-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビリケン", "ビリケン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "阪本順治の「どついたるねん」「王手」に続く“新世界３部作”の最終章。大阪・新世界に建つ、通天閣に安置されているビリケン像にチンピラやホームレスなど様々な人々が願を懸けにやって来る。その願いを叶えようと、奔走するビリケンの仮想人間だったが、あることがきっかけで信用をなくし、同時にビリケン像は質屋に売り飛ばされ、超能力を失ってしまう。ビリケン像を買い戻すため、ビリケンは肉体労働を始める。その頃、住民立ち退き工作のために新世界にやってきた江影は、少年を人質に通天閣に立てこもるのだが……。“ビリケン”に扮した杉本哲太が好演する、ファンタジー・コメディ。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SON OF THE PINK PANTHER", "ピンク・パンサーの息子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "B・エドワーズが久しぶりに手掛けた“ピンク・パンサー”シリーズ第７作。故ピーター・セラーズに代わって、「ジョニーの事情」でイタリア映画史上最高の業績を残したR・ベニーニが、クルーゾー警部の息子に扮する。ルガッシュ王国のヤスミン王女が誘拐される。捜査を任されたドレフュス警視総監は現地に向かうものの思わぬ事故に遭い、あとを地元の警官が引き継ぐことになる。誰もが心配するほど頼りない警官だがそれもそのはず、彼はクルーゾー警部の隠し子だった。父親の迷惑なキャラクターをすべて受け継いで、王女を求めるジュニアの迷走が始まった。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wrestling Ernest Hemingway", "潮風とベーコンサンドとヘミングウェイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "「ダンス・ウィズ・ミー」のR・ヘインズ監督が手掛けた人間ドラマ。南フロリダを舞台に、対照的な人生を送りながらも、ともに孤独な心を抱える老人二人の出会いと交流を、温かいまなざしで描き出す。老人たちに扮するR・デュヴァルやR・ハリスらベテラン勢に交じって、S・ブロックが快活な魅力を発揮。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Haywire", "ヘイワイヤー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("反逆の旅", "反逆の旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "藤原審爾の原作を渡辺祐介監督、原田芳雄主演で映画化したアクション映画。殺し屋を廃業するために最後の殺しを行った主人公だが、一人の少女を真剣に愛してしまったことで、警察と組織の両方から狙われるハメになる。原田芳雄が殺し屋を好演。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Portraits of Jamaican Music", "ポートレイツ・オブ・ジャマイカン・ミュージック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "レゲエを生みだしたジャマイカ音楽の変遷を体系的にまとめた音楽ドキュメンタリー。R＆Bやカリプソからスカ、ロックステディへと発展していった経緯にはじまり、レゲエから派生したダブや現在の主流であるダンスホールまでを網羅。スカタライツのローランド・アルフォンソ、オリジナル・ウェイラーズのバニー・ウェイラー、2006年に他界したカルチャーのジョゼフ・ヒルら歴史の当事者たちへのインタビューをはじめ、音楽史に残る貴重な映像が満載。", "2007-03-17", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONSPIRACY THEORY", "陰謀のセオリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "「リーサル・ウェポン」の製作、監督、主演トリオによるサスペンス・ドラマ。N.Y.のタクシー運転手ジェリーは、様々な陰謀説を説く、どこか偏執的な男。記憶を失った彼にとって、はっきりと分かっているのは、司法省に勤務する女性弁護士アリスを守るということだけ。やがて彼らに危険が迫り、ジェリーの失われた過去と、アリスの父の死の秘密が次第に明らかになっていく……。“大地震はNASAの仕業”“ノーベル賞受賞者の精子はロックフェラー・センターに冷凍保存されている”などの珍妙な陰謀説を早口でまくしたてるジェリーを、M・ギブソンが怪演。サイコなユーモア感覚が、スリル満点のストーリー展開に絡みついた異色作。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREMLINS", "グレムリン", "", "", "http://ecx.images-amazon.com/images/I/51wsQvgC3ML._SL210_.jpg", 107, "かわいいペットが怪物に変身するSFブラック・コメディ。ビリーが父に贈られた珍獣モグワイには、３つの飼育条件が付いていた。これをビリーが破ったとき、モグワイは狂暴なグレムリンに……。「禁断の惑星」のロボットが現れたり、SFの楽屋落ちネタも楽しい。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("余熱∞太郎", "余熱∞太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "映画人養成機関“ニューシネマワークショップ”で学んだ河合亮三が製作した映像作品２本。「余熱」は、アパートの隣同士で奇妙な別居生活を送ることになった夫婦の物語。「太郎」は、何故か不法投棄されたゴミを拾い続ける女ソノコと、彼女に拾われた男サトシの共同生活を描く。サトシ役は、演劇界出身で、TVドラマでも幅広い活躍を見せる堺雅人。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rough Cut", "ラフ・カット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "　実業家ジャック、その正体は凄腕の宝石泥棒だった。彼はジリアンという美しい泥棒と出会い、心を奪われる。しかしジリアンはロンドン警視庁のウィリス警部が送り込んだオトリだった。引退間近のウィリスはジリアンを利用し、ジャック逮捕を画策していたのだ。やがてジャックは大掛かりな宝石強奪計画を決行するが……。クライマックスに大どんでん返しを用意した犯罪サスペンス。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海峡をつなぐ光　玉虫と少女と日韓歴史ロマン", "海峡をつなぐ光　玉虫と少女と日韓歴史ロマン", "", "", "http://image.eiga.k-img.com/images/movie/56167/315x210.jpg?4207", 65, "在日韓国人の少女・麻衣が自身のルーツを探るなか、日本の教科書で知った法隆寺の国宝「玉虫厨子」のナゾを解くため日韓の職人や研究者たちを訪ね、質問を投げかける。玉虫をキーワードに、日韓1500年の文化交流を紐解くドキュメンタリー。監督は「蘇る玉虫厨子」の乾弘明。", "2011-06-25", "76,5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若い人", "若い人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "私生児として生まれ、ミッション・スクールでも問題児視されていた恵子と、彼女に理解を示す教師・間崎、彼と親密な関係にある女教師・橋本の三者間の微妙な関係を描いた作品。ある日、恵子が妊娠し、その相手が間崎だとの噂が流れ……。豊田四郎が文芸ものに自らの才を発揮するきっかけとなった作品。のちに、市川崑、西河克己、河崎義祐らによって再映画化された。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PEACEMAKER", "ピースメーカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "スティーヴン・スピルバーグらが新しく設立した映画スタジオ、ドリームワークスの第１回作品となるポリティカル・サスペンス・アクション。監督はスピルバーグ製作のTVシリーズ『ER／緊急救命室』で３度のエミー賞を受賞した女流監督M・レダーで、映画初監督とは思えないほどのハイテンションのアクション・シーンを生み出している。物語はロシアの貨物列車から10発の核弾頭が盗まれるところから始まる。消えた核を追って、核兵器密輸対策チームの女性科学者と、アメリカ国防省の特殊情報部大佐が、ロシア、サラエボ、トルコ、ニューヨークと世界中を駆けめぐり、壮大な追跡劇を繰り広げていく。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zidane, un portrait du 21e siecle", "ジダン　神が愛した男", "", "", "http://image.eiga.k-img.com/images/movie/33547/315x210.jpg?3619", 0, "98年のワールドカップで優勝するなど、現代最高のサッカープレイヤーとして知られるジネディーヌ・ジダン。その彼の芸術的なプレーを現代美術の第一人者、ダグラス・ゴードンとフランス人アーティスト、フィリップ・パレーノの2人が共同監督したアーティスティックなスポーツドキュメンタリー。ベッカムやロナウドらレアル・マドリッドのチームメイトとのやりとりも登場する。", "2006-07-15", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どろろ", "どろろ", "", "", "http://image.eiga.k-img.com/images/movie/1367/315x210.jpg?4640", 138, "「黄泉がえり」「カナリア」の塩田明彦監督が手塚治虫原作の同名漫画を妻夫木聡＆柴咲コウ主演で映画化。戦乱の世を憂う武将・醍醐景光は、天下統一する力を得るために生まれたばかりの息子の身体の48カ所を魔物に捧げる。医者の寿海に拾われ、48カ所を補う仮の身体を貰い受けた景光の息子百鬼丸は、魔物を倒すことで自分の体の部位が取り戻せることを知り、魔物退治の旅に出る。", "2007-01-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京河童まつり", "東京河童まつり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "喜劇一筋の斎藤寅次郎監督による、田端義夫主演の歌あり涙ありのコメディ。理髪店に勤める圭一と一郎は、児童ホームの美人寮母に熱を上げ、ホームに遊動円木を寄附しようとして流しの歌手を始める。実は彼女、理髪店主の娘であり、恋人が北海道にいるのであるが……。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("容疑者Xの献身", "容疑者Xの献身", "", "", "http://image.eiga.k-img.com/images/movie/53307/315x210.jpg?3787", 128, "05年に直木賞を受賞した東野圭吾の人気ミステリー「探偵ガリレオ」シリーズ第3作「容疑者Xの献身」をTVドラマ「ガリレオ」のスタッフ・キャストで映画化。顔を潰され指を焼かれるという残忍な殺人事件が発生。内海刑事（柴咲）はいつものように天才物理学者で通称“ガリレオ”の湯川（福山）に助けを求めるが、被害者の元妻の隣人として捜査線上に浮かんだのは、湯川の大学時代の友人で天才数学者の石神（堤）だった……。監督は「県庁の星」の西谷弘。", "2008-10-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犬のおまわりさん　てのひらワンコ3D", "犬のおまわりさん　てのひらワンコ3D", "", "", "http://image.eiga.k-img.com/images/movie/56867/315x210.jpg?4345", 68, "新米警官が子イヌとの触れあいを通して成長していく姿を描いた3D映画。のどかな町の交番に勤務する新米警官の健は、事件もない日々に退屈していた。ある日、迷子のゴールデンレトリーバーの子イヌが届けられるが、遺失物法の改正により交番でイヌを預かることができない。保健所に預けても1週間で殺処分されてしまうことを知った健は、子イヌを自宅のアパートに連れ帰り、大家に内緒で飼い始めるのだが……。主演は「ROOKIES 卒業」「時をかける少女」の中尾明慶。", "2011-08-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MISSISSIPPI MASALA", "ミシシッピー・マサラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「サラーム・ボンベイ！」で一躍脚光を浴びた女流監督M・ナーイルによる純愛ドラマ。ミシシッピー州で出会ったアフリカ育ちのインド女性ミナと黒人青年デメトリアス。彼らの恋が、インド人社会と黒人社会に様々な波紋を広げていく。有色人種間の意識の違いをテーマにした切ない恋物語だ。ヴェネチア映画祭で脚本賞を受賞した。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿虎　A　FIGHTER’S　BLUES", "ファイターズ・ブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "香港のスター、アンディ・ラウの映画出演100本目となった作品。香港の刑務所を出所したタイガーは、昔の恋人を捜して訪れたタイで恋人の死と、彼女が娘を生んでいたことを知る。孤児院で娘に対面するタイガー。しかし娘は突如現れた父親に素直になることができない。そんな二人をとりなしたのは孤児院のシスターで日本女性の澪子。互いに悲しい過去を持つタイガーと澪子はしだいに心を通わせていくが……。クライマックスのファイティング・シーンが涙を誘うヒューマン・ラブ・ストーリー。アジア映画出演２作目の常盤貴子が広東語、タイ語、英語、日本語という４ヵ国語のセリフに挑戦した意欲作でもある。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ランデブー！", "ランデブー！", "", "", "http://image.eiga.k-img.com/images/movie/55217/315x210.jpg?3733", 0, "女優を目指して田舎から上京しためぐるだが、なかなか夢が叶わずに、3年が過ぎようとしていた。そんなある日、オーディションに向かう途中で携帯電話を拾う。矢島と名乗る男からの電話に出ためぐるは、しばらく携帯をあずかるはめになるが、おかげでとんでもない事件に巻き込まれる。「結婚できない男」「特命係長　只野仁」などヒットドラマを手がけてきた脚本家の尾崎将也が、初監督に挑む青春コメディ。主演は音楽グループ「AAA（トリプル・エー）」のボーカル、宇野実彩子。", "2010-05-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暴れん坊兄弟", "暴れん坊兄弟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "泰助と泰三は仲良しの兄弟だが性格はまるで正反対。泰助は初のお国入りを控えた主君松平長門守から、国詰めを命じられる。泰助来たるの報に、若葉城は過剰反応。家老がベタぼめするため、泰助は武芸百般の長ということになってしまったのだ。のんびり屋の泰助は、まんまと覆面の一味のワナにかかって小川に落ち、たちまち評判を下げる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA SYMPHONIE PASTORALE", "田園交響楽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "フランスの文豪ジイドの同名小説の映画化。片田舎に住む牧師は、自分が育てた盲目の孤児ジェルトリュードに恋をする。しかし彼の息子もまた、彼女の清純な美しさに心惹かれて……。許されない愛に苦悩する牧師の姿を端正な演出で綴る悲恋物語の佳作。", "1946-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("We Jam Econo: The Story of the Minutemen", "ミニットメン：ウィ・ジャム・エコノ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "独自のサウンドで1980年代のアンダーグラウンド・シーンに衝撃を与え、その後のロック界に多大な影響を及ぼしたアメリカのハードコア・パンクバンド「ミニットメン」にスポットを当てたドキュメンタリー。貴重なライブ映像やメンバーの現在の姿、そして「ソニック・ユース」「レッド・ホット・チリ・ペッパーズ」「ブラック・フラッグ」、リチャード・ヘルら豪華ミュージシャンたちによる証言を織りまぜながら、ミニットメンの活動の軌跡とその魅力に迫る。", "2008-07-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Black Power Mixtape 1967-1975", "ブラックパワー・ミックステープ　アメリカの光と影", "", "", "http://image.eiga.k-img.com/images/movie/58037/315x210.jpg?3720", 92, "スウェーデンのテレビ局に眠っていた、アメリカ公民権運動の模様を記録したフィルムをもとに、自由と平等を求めて権力と戦ったアフリカ系アメリカ人たちの歴史を紐解くドキュメンタリー。キング牧師、マルコムX、アンジェラ・デイビス、ストークリー・カーマイケルらの貴重な映像に加え、現代アメリカで活躍する著名なアフリカ系アメリカ人たちが、アメリカの現在や過去を語る。", "2012-04-28", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VE´NUS　BEAUTE´（INSTITUT）", "エステサロン／　ヴィーナス・ビューティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "パリの街角にあるエステサロンを舞台に、世代の違う３人のエステティシャンと様々な客の人間模様、それぞれのロマンスを描くラブ・ストーリー。40歳のアンジェルは、若い同僚サマンタやマリーの相談相手を務めるベテラン・エステティシャン。だが、プライベートでは苦い過去を引きずり、慰めを求めて一夜限りの愛を楽しんでいる。そんなある日、青年アントワーヌに一目惚れされ、彼女は戸惑いながらも、彼のまっすぐさに心を動かされていく。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAY GOLD ステイゴールド", "STAY GOLD ステイゴールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "自殺した親友のために、３人の女子中学生たちは永遠の友情を可能にする伝説の雫を求めて、修学旅行を離脱する。それを追う若い男女の教師二人……。野沢尚のオリジナル脚本をもとに、村上修監督は10代、20代、それぞれ異なる感情の揺らめきを並行して描きながら、みずみずしい作品に仕上げている。ちなみに、水原里絵は深津絵里の旧名である。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スターダスト・ストーリー 星砂物語", "スターダスト・ストーリー 星砂物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "南の島・与論島の海と空を背景に、大学のウィンド・サーフィン部に所属するボンボン育ちの社長の息子と、純情で庶民的な島の娘との恋を描いた青春ラブ・ロマンス。全編にわたって加山雄三のヒット曲の数々が流れる、いわば1980年代版“若大将”もので、若大将を４本撮っている福田純が監修にあたっている。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("17才", "17才", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "若手女優・猪俣ユキと三輪明日美の企画を、俳優・木下ほうかの監督で映画化。女子高生たちのはかない友情や、前向きに未来へ進もうとする思いをデジタルカメラですくい取った青春映画だ。漠然と高校生活を送る17歳のアコが、一つ年上のリョウの夢を聞き、彼女に憧れを抱く。やがて新たなバイトを始めたアコの周囲で次々と事件が起こり、彼女自身の心にも変化が生じていく。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　MARTIAN　CHRONICLES", "火星年代記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 300, "SF小説の巨匠ブラッドベリの代表的大作を、５時間に及んで映画化した壮大なスケールの作品。「80日間世界一周」でアカデミー作品賞を獲得したM・アンダーソン監督が、多彩なハリウッド・スターを起用して一大スペース・オペラに仕上げた。2001年に火星人が絶滅し、翌年から人類の多くが火星へ移住し火星の地球化が進んでいた。その頃、地球では最終戦争が勃発していた。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("АШИК-КЕРИБ", "アシク・ケリブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "ロシアの国民的詩人、ミハイル・レールモントフのおとぎ話をもとに、パラジャーノフ監督が描いた美しく、ロマンティックな恋物語。貧しいながらも心優しい吟遊詩人アシク・ケリブは、富豪の娘マグリに結婚を申し込むが、父親から一蹴されてしまう。1000の夜と1000の昼の後に帰ると娘に約束し、放浪の旅に出たアシク・ケリブを、様々な困難が待ち受ける。やがて、マグリがほかの男といやいやながら結婚させられようとしているのを知ったアシク・ケリブは、聖人の白馬に乗って故郷へと急ぐ……。「ざくろの色」とは一転した動きのある映像と物語。そしてアゼルバイジャン語のセリフにグルジア語のナレーションという多重構造もまた、この映画を特異なものとしている。加えて、映画を豊かに彩る貴重な美術品や民族衣装により、第１回ヨーロッパ映画賞の美術部門賞を獲得した。なお、生前親交があったタルコフスキーに捧げられた本作は、最後まで権力に抗し続けたパラジャーノフ監督の遺作でもある。", "1988-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brodre", "ある愛の風景", "", "", "http://image.eiga.k-img.com/images/movie/34267/315x210.jpg?4015", 117, "「アフター・ウェディング」が06年アカデミー賞外国語映画賞にノミネートされ、一躍注目を集めたデンマークの女性監督スサンネ・ビアが「アフター・ウェディング」の前に手掛けた作品で、ハリウッド・リメイクも決まったヒューマン・ドラマ。国連軍のエリート兵士のミカエルは美しい妻サラと2人の娘を残し、戦地へ赴く。同じ頃、ミカエルの弟ヤニックが刑務所から出所し、兄の家に厄介になる。そんな時、戦地からミカエルの訃報が届くが……。", "2007-12-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUST BETWEEN FRIENDS", "女ざかり ホリーとサンディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "理想的な中流家庭を営む主婦ホリーと行動的なキャリアウーマン、サンディ。エアロビクス・ジムで知り合った二人はたちまち無二の親友になる。しかし不幸は起こった。サンディはなんとホリーの夫の浮気相手だったのだ。女同士の温かい友情を描いた心優しいドラマ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONLY THE STRONG SURVIVE", "ソウル・サヴァイヴァー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "1960～70年代に人気を博した、伝説のソウル・シンガーたち。その後、第一線からは遠ざかったものの現在も歌い続けている、そんな彼らの姿を見つめたドキュメンタリー作品。W・ピケットやS・ムーア、本作の撮影後に世を去ったR・トーマスほかの大御所に加えて、シャイ・ライツやシュープリームスのメアリー・ウィルソンらが出演。全盛期以上に深みを増した歌声で、ソウルの真髄を見せつける。浮き沈みの激しい音楽業界を生き延びた者たちのパワーに圧倒される。", "2004-12-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Adam", "恋する宇宙", "", "", "http://ecx.images-amazon.com/images/I/51nfgIRBKBL._SL210_.jpg", 99, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROKEN ARROW", "ブロークン・アロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "盗まれた核弾頭をめぐる男と男の対決をスリリングに描いたノンストップ・アクション。ディーキンス少佐とヘイル大尉が搭乗した戦闘機が、核弾頭を搭載しユタへ向かっていた。だが突然ディーキンスがヘイルを非常脱出装置で機外に放り出し、核弾頭を強奪する。国立公園に落下したヘイルは女性監視員に協力を頼み、消えた核弾頭とディーキンスの行方を追うが……。“香港ノワール”の雄ジョン・ウー監督のハリウッド進出第二弾。戦闘機、ヘリコプター、列車と息もつかせぬ速さで度肝を抜くアクションが展開する痛快作。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NIGHT OF THE LIVING DEAD", "ナイト・オブ・ザ・リビングデッド 死霊創世記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ホラー映画史上に残る、G・A・ロメロの処女作「ナイト・オブ・ザ・リビング・デッド ゾンビの誕生」。22年前のこの衝撃的なホラーをカラーでリメイク。ロメロ自身は製作総指揮に回り、代わって彼とのコンビ作も多い特殊メイク・アーティストのT・サヴィーニが演出を担当。ゾンビに囲まれた一軒の農家に立てこもった７人の男女のサバイバルが展開。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BELLBOY", "底抜けてんやわんや", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "“底抜け”シリーズでおなじみのJ・ルイスが初めて製作、監督に乗り出した作品で、シリーズ中の最高傑作と評する者も多い。マイアミのホテルのボーイが失敗の連続でホテル中を大混乱に陥れていく。という大まかな筋立てはあるものの、実際はほとんどプロットなしの即興演出で撮影されたというだけあって、ルイスの行きあたりばったりの暴れぶりをコラージュ風につないだという感じの内容。喜劇というよりは実験映画に近いその奔放な手法は、ゴダールをはじめ当時の若い映画作家に大きな衝撃を与えたといわれている。他に類を見ない大怪作、超珍品なので、一度は見ておきたいもの。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンの天国と地獄", "エノケンの天国と地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "天国の裁判所。死んだ圭太はそこで自らの過去が記録された映画を証拠として見せられる。生前の圭太はメリケンサーカスの人気者。ある日、路上で佇んでいるユキという少女に憐れみを抱き、彼女を一座の小間使いとして雇ってやる。やがて深く愛し合うようになった二人は、ある事件をきっかけにそろって一座を退団。けれども、なかなか職に恵まれず、映写が終わると彼は地獄行きを命じられる。そして10年の刑期を終え……。監督は島津保次郎門下、後年は記録映画界で活躍する佐藤武。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("F／X ： MURDER　BY　ILLUSION", "F／X 引き裂かれたトリック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "日本で使われるSFXと同様、映画などの特殊効果を表わすF／X。映画の裏方ともいえる、このF／Xのプロを主人公にした作品。世界のトップレベルの技術を持つ、特殊効果の魔術師ロリー・タイラー。その彼が、司法省のある機関から、暗殺の偽装を依頼され、黒い策謀の渦に巻き込まれていく……。主演をオーストラリア出身のB・ブラウンが演じ、小気味よいアクションを見せている。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Insh Allah", "インシャラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "日本でも人気を集めた韓国ドラマ「宮廷女官チャングムの誓い」のイ・ヨンエの映画デビュー作。アメリカに留学していた韓国人女性イ・ヒャンは、旅先のアルジェリアで密輸犯に間違えられ抑留されてしまう。彼女の取調べを任されたのは、北朝鮮の外交員として働きながら革命スパイの養成も請け負っているハン・スンヨプ。ヒャンを不憫（ふびん）に思ったスンヨプは、彼女を連れて国外へ脱出することを決意する。", "2006-11-06", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コワイ女", "コワイ女", "", "", "http://image.eiga.k-img.com/images/movie/33757/315x210.jpg?4743", 107, "雨宮慶太、鈴木卓爾、豊島圭介ら3人の監督が、女性が持つ底知れぬ怖さをテーマに描いたオムニバス・ホラー。結婚を間近に控えたOLが自殺した女の怨霊に狙われる「カタカタ」、工場で働く青年がズタ袋をかぶった不気味な少女に振り回される「鋼−はがね−」、母から娘に受け継がれた呪われた血が悲劇を引き起こす「うけつぐもの」など、ふとしたことから“こわい女”と出会ってしまった人々の恐怖を描く。", "2006-11-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("午前零時の出獄", "午前零時の出獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "理由ありの囚人と、彼を追って特ダネをものにしようとする新聞記者の二人を主人公にした、アクション映画。刑務所で囚人の芝山が狙撃されて負傷した。なぜか出所日になっても刑務所を出ようとしない芝山に興味を持った新聞記者の片桐は、彼の身の安全を保証して出所させる。芝山と行動をともにするうちに、片桐は汚職事件に絡む陰謀を知り……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("いかさま博奕", "いかさま博奕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "清市は借金を返そうと娘のおしのを身売りして金を作り、博奕打ちの銀次郎と勝負する。しかし結果は大負けで、絶望した清市は自殺してしまう。事情を知った銀次郎はおしのを身受けしようと、再度賭場を訪れるが、関西の博徒竜吉のイカサマに敗れてしまった。人情に熱い博奕打ちを鶴田浩二がピッタリのハマリ役で演じる。ギャンブル・シーンが緊迫感に満ちた仕上がりに。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUPER MODEL’S　CATWALK", "SUPER MODEL'S CATWALK", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ファッション業界のみならず、歌に映画と活躍の場が広がるスーパーモデルたちのプライベート・ライフとコレクションの舞台裏を、ミラノ、パリ、ニューヨークと回るスーパーモデルの一人、クリスティ・ターリントンの姿を通して紹介するドキュメンタリー。緊迫したコレクションの舞台裏から恋人とのデート、モデル仲間のナオミ・キャンベルやケイト・モスとの交遊関係、フォトセッションとクリスティの日常に迫り、モデルたちに必要なカリスマ性を描き出す。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悲器", "悲器", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "松竹の“やくざの掟”シリーズで、元暴力団組長・安藤昇をスターに押し上げた湯浅浪男監督のピンク映画。雑草のように生き抜く女の生命力と、エロスと叙情性をテーマに、ピンク女優第１号の香取環、“ピンクの山本冨士子”と騒がれた松井康子、元祖ワキ毛女優内田高子ら総勢10人のピンク・スターが出演。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KOTOKO", "KOTOKO", "", "", "http://image.eiga.k-img.com/images/movie/56957/315x210.jpg?2126", 91, "「鉄男」「六月の蛇」の塚本晋也監督がシンガーソングライターのCoccoを主演に迎え、苦しみもがきながらも愛する息子を育て、懸命に生きるひとりの女性の姿を描き出したドラマ。ひとりで幼い息子の大二郎を育てる琴子は、世界が“ふたつ”に見える現象に悩まされ、歌っているときだけ世界が“ひとつ”になる。神経が過敏になり強迫観念にかられた琴子は、大二郎に近づくものを殴り、蹴り倒して必死に息子を守っていたが、幼児虐待を疑われて大二郎と引き離されてしまう。そんなある日、琴子の歌に魅了されたという小説家の田中が現れるが……。2011年・第68回ベネチア国際映画祭オリゾンティ部門で、同部門の最高賞にあたるオリゾンティ賞を受賞した。", "2012-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mis Uszatek", "おやすみ、クマちゃん", "", "", "http://image.eiga.k-img.com/images/movie/34107/315x210.jpg?1443", 84, "ヨーロッパ諸国で50年に渡って愛され続けている絵本を原作に、愛らしい主人公“クマちゃん”と動物たちが繰り広げるハートウォーミングな物語を綴ったパペット・アニメーション。57年から87年にかけて、ポーランド国営放送と国営人形アニメ・スタジオ“セ・マ・フォル”が共同制作した104話の中から10話を厳選して上映する。原作の絵を手掛けたズビグニエフ・ルィフリツキによる、細部にまで凝った美術も見どころ。", "2007-08-04", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAMES BALDWIN：THE PRICE OF THE TICKET", "ハーレム135丁目 ジェイムズ・ボールドウィン抄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ゲイとして、黒人としての自らのアイデンティティーを追求し、アメリカの黒人文学を単なる“抗議小説”をはるかに超えるレベルに高めた第一人者、J・ボールドウィンのドキュメンタリー。生前に撮影したフィルムを中心に、家族、友人、知人、批評家、同業の作家たちのインタビューを織り込みながら彼の足跡をたどる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE VIRGIN WIFE", "ああ新婚", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "イタリアのエロティック・コメディ。結婚したてのカップルだが、夫はいざというときになかなか役に立たない。妻も派手なネグリジェを着て、ムード音楽を流すのだが、これもダメ。この二人の結婚、さてハッピーエンドとなるか。", "1976-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hi-Lo Country", "ハイロー・カントリー", "", "", "http://image.eiga.k-img.com/images/movie/1797/315x210.jpg?2947", 114, "1930年代後半のニューメキシコ、広大な平野ハイロー・カントリー。ピートは両親を事故で失い、一人で小さな牧場を取り仕切っていた。ある日、ピートは豪放磊落なカウボーイ、ビッグ・ボーイと出会う。ピート、ビッグ・ボーイ、弟のリトル・ボーイと仲間達は協力し、自分達の牧場で友情を育んでいった。天涯孤独のピートはいつしかビッグ・ボーイを家族同様に思うようになっていた。しかし、第二次世界対戦が勃発、2人は別 々の戦場に旅立ってゆくのだった。", "1999-11-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きつね", "きつね", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "きつねが媒介する難病・エキノコックス症にかかった少女と、中年の男との交流を北海道の自然をバックに描いた情緒あふれる映画詩。世代的に離れた男女の愛をうまく捉えた、邦画には珍しい作品だったが、興行的にふるわず幻の映画になっている。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HEAVENLY CREATURES", "乙女の祈り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1954年にニュージーランドで実際に起きた殺人事件の映画化。クライスチャーチの女子高に通う風変わりなポーリーンと、イギリスから転校してきたジュリエット。正反対の環境に育ちながらも驚くほど似た感性を持った二人は、固い友情を育んでいく。空想の世界をも共有する親密ぶりに、周りの大人たちは彼女たちを引き離そうとするが、離れないと誓った二人は恐ろしい計画を実行するのだった……。思春期の少女特有の純粋さが残虐性へと変化していくさまを鮮明に描き切った佳作。", "1994-01-01", "31,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほしのこえ", "ほしのこえ", "", "", "http://ecx.images-amazon.com/images/I/51zWeacU4GL._SL210_.jpg", 25, "中学3年の夏、国連宇宙軍の選抜に選ばれた長峰美加子は、思いを寄せていた同級生の寺尾昇に別れを告げる。美加子は地球を後にし、昇は普通の高校へ進学。地球と宇宙に引き裂かれた2人はメールで連絡を取り続けるが、美加子が地球から離れるに従い、メールの往復にかかる時間は何年も開いていく。クリエイターの新海誠が個人制作したフルデジタルアニメーションで、そのクオリティから高い評価を受けたSF短編アニメ。", "2002-02-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRUM", "ドラム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "白人との混血で生まれた黒人青年ドラムが奴隷として次から次へと売り渡され、やがて解放されて自由の身になるまでの数奇な運命を描く人間ドラマ。「マンディンゴ」のオンストットの小説の映画化で、ボクサー出身のノートンが「マンディンゴ」に次いで主演。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カブキングZ", "カブキングZ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "歌舞伎をモチーフにした扮装でパフォーマンスする異色ロックバンド“カブキロックス”の氏神一番を主演に迎えたバトル・アクション。悪の蜉蝣（かげろう）一族と太古の時代から激闘を繰り広げてきた氏神族。その末裔である正義のヒーロー・カブキングZは、弟分のヤルキングと共に戦いの日々を送っていた。やがて彼らの戦いは、謎の自警団やカブキングZの実弟・悠太郎らも巻き込んだ大戦争へと発展する。監督は「ミートボールマシン」の山本淳一。", "2008-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OSAKAビッグ・リバー・ブルース　平成名探偵・阪田京介２", "OSAKAビッグ・リバー・ブルース　平成名探偵・阪田京介２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "坂田利夫が人情味あふれる名探偵を演じ話題となったドラマの第２作。大阪・ミナミで探偵事務所を営む阪田はある日、刑事時代の先輩・吉村と偶然再会。しかし喜びもつかの間、翌日、吉村の他殺死体が発見される。かつての事故で視力を失った吉村の一人娘・久美の証言をもとに、阪田はその鋭い名推理で事件の真相を暴いていく。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Empire Strikes Back: Special Edition", "スター・ウォーズ　帝国の逆襲　特別篇", "", "", "http://ecx.images-amazon.com/images/I/51C5B2Q7A9L._SL210_.jpg", 124, "「スター・ウォーズ」シリーズの20周年を記念して製作された、第2作「帝国の逆襲」（1980）の特別篇。帝国軍の猛反撃によってピンチに陥った反乱軍の奮闘や、ジェダイ・マスターのヨーダの下で修行を積むことになったルークの成長を描く。特別篇での主な変更点は、肉食獣ワンパの全身を映しだすカットの追加や空中都市クラウド・シティの景観の修正、ダース・ベイダーがクラウド・シティからエグゼクターに戻るシーンの追加など。", "1998-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUDES", "N.Y.バッド・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ロックバンド“デューズ”の３人組は都会生活にいや気がさして旅に出るが、途中、一人が暴走族に殺される。残された二人は美女ガンマンに射撃を習い、復讐戦に挑む、というモダン・ヤング・ウエスタン。女ガンマンのK・M・スチュアートが最高にカッコいい。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PRETTY IN PINK", "プリティ・イン・ピンク／恋人たちの街角", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アンディは成績優秀な女子高生。アルバイトで貧しい家庭を助けたり、彼女に恋心を抱く同級生ダッキーに勉強を教えたりの毎日。だけどアンディが好きなのはブルジョワのブレーン。さてこの三角関係は……。リングウォルドの人気を決定づけた青春映画の佳作。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRASS TARGET", "ブラス・ターゲット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "“パットン将軍は暗殺された”という大胆な仮説をもとに書かれたノーランの小説『アルゴンキン計画』の映画化。第二次大戦の終結した1945年、ヨーロッパでナチから接収した金塊が強奪され、その捜査を命じたパットン将軍に対して暗殺計画が企てられるが……。", "1979-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ISLAND　TALES／有時跳舞", "異邦人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "台湾人監督スタンリー・クワンが日本と台湾の俳優を起用して撮り上げた無国籍風群像ドラマ。2001年を間近に控えたある日、香港近郊の小さな蜉蝣洲〈カゲロウ〉島が、死を引き起こす“ストーン病”の発生源として突如封鎖される。ジャーナリスト、女性写真家、銀行経営者、人気俳優など国籍も立場も違う人々が島に閉じ込められてしまう。彼らは不安の中で一夜を明かし、そして現実を忘れるためにパーティーを開くことにする。", "2000-01-01", "76,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空山靈雨／RAINING IN THE MOUNTAIN", "空山靈雨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "時代は明朝の中頃。三蔵法師の自筆の経典が納められている寺の大僧正が、高齢のためその位を譲ろうと俗界から相談役を招く。ところが、やって来た二人の人間はひそかに寺の宝典を狙っていたのだった。ドラマとアクションがほどよくミックスされた歴史映画。", "1979-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YEELEN", "ひかり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "“人間のなかにある神秘性、人を死に至らせる狂気へと誘う利己主義”が、この映画の重要なところだと語るS・シセ監督。彼は、1940年マリに生まれ、モスクワで映画を学び、現在はパリ在住。映画は、主人公ニャナンコロが子供から大人へと成長する過程での、一種の通過儀礼の旅を物語っていく。彼は、様々な力を思いのままにする力を授かろうとし、彼の父親は、そんな息子を殺そうとする。母親は、息子を救おうと、彼を遠くへ逃がす。ニャナンコロはやがて究極の魔力を身につけ、追ってくる父親に立ち向かう。神話的な要素を持つこの作品は、その昔からマリに伝わる儀式や宗教をベースにしている。", "1987-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SANS TOIT NI LOI", "冬の旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "冬の田舎道に行き倒れて死んだ少女モナ。誰も知ることのない彼女の身に何が起ったのか？　彼女と知り合った何人かの証言からモナの孤独な旅の物語が、少しずつ明らかになっていく……。セザール賞にも輝いたサンドリーヌ・ボネールのあざやかな存在感とともに静かな感動を呼び、各国で絶賛された力強い作品。ビデオ発売時タイトルは「さすらう女」。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋骨・劇場版", "恋骨・劇場版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "石川啄木の歌集『一握の砂』を巡る謎が描かれた学園ミステリー。渋谷や原宿の大型ヴィジョンとインターネットで同時配信された前代未聞の連続ドラマに、未公開シーンを加えた完全版。ヴィジョン版では知らされなかったタイトルの真意と衝撃の結末が明かされる。涼子は転校初日に女子生徒の幽霊を見た。以来、幽霊を見る度に殺人が起こり、調べるうちに石川啄木の歌集に関係していると気づく。", "2005-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤いカラスと幽霊船", "赤いカラスと幽霊船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "バスケットボールの試合でミスをし、意気消沈したタクヤと妹のエミが赤いカラスに導かれて空飛ぶ幽霊船に乗る。床に伏す老船長のために幽霊船の舵を取り、未知の宇宙に乗り出していくというアドベンチャー・ロマン。アニメ界の巨匠・宮崎駿が、幽霊船のデザインを担当している。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAST ACTION HERO", "ラスト・アクション・ヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "アクション映画のヒーロー、ジャック・スレイターに憧れる少年ダニーは、学校をさぼって映画館に通いづめ。ある日、魔法のチケットを手にした彼は、現実の世界からスクリーンの中の世界に吸い込まれてしまう。そこはスレイターが悪人相手に危険な冒険を繰り広げるエキサイティングな世界だった。スレイターと組んで悪人に戦いを挑むダニーだが、魔法のチケットが悪人の手に渡ってしまい……。A・シュワルツェネッガーが自ら製作に乗り出し、アクション映画監督J・マクティアナンを迎えて作り上げた、映画への愛にあふれるファンタスティックなアクション。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BELLE DE JOUR", "昼顔", "", "", "http://ecx.images-amazon.com/images/I/51EAjis77gL._SL210_.jpg", 100, "発表当時センセーショナルな反響を巻き起こしたジョセフ・ケッセルの小説を異才ブニュエルが映画化。貞淑な人妻でありながら、昼は娼婦宿に通う女の肉体と心の矛盾を、現実と幻想が交錯するシュールな映像で描き出す。ドヌーヴの“女”を逸脱した官能美が圧巻。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'enfer", "美しき運命の傷痕", "", "", "http://image.eiga.k-img.com/images/movie/1727/315x210.jpg?2166", 102, "「トリコロール」3部作で知られるクシシュトフ・キェシロフスキ監督の遺稿を「ノーマンズ・ランド」のダニス・タノビッチ監督が映画化。ある出来事によって父を失った3姉妹とその母。「美しすぎて」のキャロル・ブーケが母親役、3姉妹を演じるのは「8人の女たち」のエマニュエル・ベアール、「年下のひと」のカリン・ビアール、「ひとりぼっちの狩人たち」のマリー・ジラン。セバスチャン役は「ザ・ビーチ」のギョーム・カネ。", "2006-04-08", "2,4,37,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Crucible", "クルーシブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "A・ミラーが1950年代の“赤狩り”全盛の風潮に反発して著した同名戯曲を、自身が映画用に脚色し映画化。舞台は1692年、マサチューセッツ州セイラム。折から魔女による悪魔つきが問題となっていた寒村で、深夜に呪術的な踊りに興じる少女たちの姿が目撃される。少女たちのリーダー格であるアビゲイルは、率先して悪魔に取り憑かれたふりをし、農夫ジョンの妻エリザベスを告発した。かつてジョンと不倫関係にあったアビゲイルは、ジョンを取り戻したいがために、エリザベスを憎んでいたのだ。魔女狩りが断行されるなか、捕らわれた妻を追って、ジョンはアビゲイルとの不倫と彼女の悪だくみを訴えるが、逆に召し使いに悪魔の手先だと告発され、逮捕されてしまう。思わぬ展開に慌てたアビゲイルは、獄中のジョンに逃亡を提案するが……。人間としての尊厳と妻への愛を貫き、魔女裁判に真っ向から立ち向かうジョン役のD・D・ルイスの迫真の演技がヒューマンな感動を呼ぶ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Vanishing", "失踪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "旅行先で恋人に失踪された作家志望のジェフ。些細なケンカをしたあとだったため、警察にも見離されてしまう。必死になって消息を追い求めるのだが何の手がかりも得ることなく３年の歳月が過ぎていった。そして、テレビ出演がきっかけとなって、有力な情報を持つ男と出会うが……。「マイセン幻影」がヨーロッパで高い評価を得たG・シュルイツァーが、本国オランダで撮った自作を、ハリウッドで自らの手でリメイクしたサイコ・サスペンス。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最後の晩餐", "最後の晩餐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "人気作家、大石圭の問題作『湘南人肉医』を映画化した衝撃のホラー。人肉の味に取り憑かれてしまった美容整形外科医が、自らの欲望を満たすため、次々と若い女性を手にかけていく。劇中は、ショッキングなシーンの連続。主人公を怪演する加藤雅也を始め、はまったキャスティングも絶妙。人肉に魅せられた美容整形外科医の小鳥田は、女性を次々と殺害し、自らの空腹を満たしていく。だが、それを刑事に嗅ぎつけられ……。", "2005-02-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MA SAISON PRE´FE｀RE´E", "私の好きな季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "キャリアウーマンのエミリーは、温厚な夫と二人の子供とともに暮らしている。優秀な職務能力と美貌を持ち、一見幸福そうに見える彼女だったが、心の奥底にある空虚さを感じずにはいられなかった。一方、独身の弟アントワーヌはエミリーに対し姉弟の関係を越えた愛情を抱いていたが、愛するがゆえ反発してしまい、二人の交友は長い間途絶えたままになっていた。そんな折、母親のベルトが脳卒中で倒れてしまい、姉弟は再会することに……。現代人が失いつつある家族の絆や愛を見つめ直した感動作。C・ドヌーヴの自然な演技が光る。マルチェロ・マストロヤンニとC・ドヌーヴを両親に持つK・マストロヤンニが母親と共演していることにも注目。ビデオタイトルは「背徳のささやき」。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　BIG　SWINDLE", "ビッグ・スウィンドル！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "韓国で実際に起きた未解決事件をヒントにした、粋でスリリングなクライム・ムービー。史上最大の銀行詐欺を計画した、５人のスゴ腕の詐欺師チーム。すべては順調に進められ、みごと成功したかに見えたが、カネは消え、ひとりは遺体で発見される。一体彼らに何が起こったのか？ キャストは韓国ドラマ『パリの恋人』などで人気を博したパク・シニャンなど、芸達者なメンツが勢揃い。本作で監督デビューを飾ったチェ・ドンフンが脚本も手がけており、緻密に組み合わされた構成の妙が冴え渡る。出所したての詐欺師チャンヒョクは、伝説の詐欺師キム先生と組んで銀行から50億ウォンを騙し取る計画を立てる。犯行はみごとに成功するが、なぜかチャンヒョクは遺体で発見され……。", "2005-12-03", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("義胆紅唇／CITY WAR", "チョウ・ユンファ 非情の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "10年前の銃撃戦で負傷し、捕らわれの身となったギャングのテッド。刑務所に服役している間、彼は自分を捕えた刑事ケンへの復讐を誓っていた。そしてテッドと刑事たちとの闘いの幕が切っておとされた……。「男たちの挽歌」シリーズの名コンビが描いたアクション巨編。", "1989-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRISTESSE ET BEAUTE´", "美しさと哀しみと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "20年前に愛と憎しみを抱いて別れた小説家と再会した女彫刻家レア。彼女を師とあおぐ少女プルダンスは、二人の過去を知り、レアのために小説家に復讐することを心に誓う。川端文学を南フランスを舞台に脚色した女性監督フルーリーのデビュー作。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("八月の濡れた砂", "八月の濡れた砂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "若者のやり場のないエネルギーを、独特なタッチで描いた青春映画。時代を“気分”でみせる藤田敏八監督ならではの作品である。夏の朝、町の高校生・清は不良たちに強姦された少女・早苗に出会う。彼女は別荘に遊びに来ていたお嬢さんだった。また、清には健一郎という高校を退学になった元同級生の友人がいた。健一郎は父の死後、バーで働く母と関係を持っている中年男・亀井をはじめ、偽善的で汚い大人たちすべてに憎悪を抱いていた。３人は次第に仲良くなっていく……。突っぱっていながらも、最後の瞬間でシラケてしまう主人公たち。結局彼らの攻撃目標は、自分自身なのかもしれない。大学闘争が後退を余儀なくされた1971年、目標を失った当時の若者たちは、シラケることしかできなかったのだ。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUMMER OF '42", "おもいでの夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1942年の夏。ニューイングランドの沖合いに浮かぶ小さな島。15歳のハーミーは家族とともにこの島にバカンスにやって来た。ハーミーはさっそく、同い歳のオジーとベンジーと友人になったが、思春期の少年たちが興味を持っているのは女の子のこと。彼らは映画館でガール・ハントをしたり、医学書をこっそり読んだりする。だが、ハーミーの憧れは、丘の上の一軒家に住むドロシーという美しい年上の女性。やがてハーミーはドロシーと知り合いとなり、楽しい夏の日々を過ごすが、ある日、彼女の夫の戦死を知らせる電報が届く。思春期におけるセックスへの憧れをホロ苦い思い出として描いた作品。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("栄光と狂気", "栄光と狂気", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "奥山和由プロデューサーが監督の原田眞人以外はアメリカ、カナダのスタッフを使って作った作品。日本人俳優は羽田美智子一人であり、セリフもすべて英語で話されている。無敵のボート選手、ティフとクノップは1980年のモスクワ・オリンピックを目指していたが、アメリカが出場をボイコットしたために、夢は打ち砕かれる。しかし、夢を捨てられない二人は、1984年のロサンゼルス・オリンピックを目指すが、全盛期を過ぎた彼らにとってそれは厳しい道だった。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’UOMO DI PAGLIA", "わらの男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "一人息子の療養のため、妻子を田舎に送り出したアンドレが、ふとしたことから近くのアパートに住む女性と知り合い、いつしか後ろめたい仲になる。妻子が帰って来ることになっても煮えきらない男の態度に疲れた女は……。小市民生活の哀歓を描くジェルミの秀作。", "1957-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花を摘む少女と虫を殺す少女", "花を摘む少女と虫を殺す少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 236, "1991年の「三月のライオン」が海外でも注目されたインディペンデントの映画作家、矢崎仁司が、文化庁の芸術家海外研修員としての訪問先であるイギリスのロンドンで着想を得たラブ・ストーリー。ドイツからロンドンにやってきたバレエ・ダンサー、彼女と英会話学校で出会った日本人青年、そして日本人の若い女優の３人を中心に、男女の愛や孤独をみずみずしい叙情性豊かに紡ぎ上げている。古典バレエの戯曲『ジゼル』をモチーフにしたきわめて感覚的な作品ながらも、異国の地を浮遊する登場人物の心情が切なく伝わってくる。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("完全なる飼育　女理髪師の恋", "完全なる飼育　女理髪師の恋", "", "", "http://ecx.images-amazon.com/images/I/516VR2GVFVL._SL210_.jpg", 103, "監禁される少女と犯人との間に芽生えた屈折した愛情を描く官能ドラマ「完全なる飼育」シリーズの第5弾。自堕落な夫と2人で暮らす理髪店主ハルミ。そんな彼女に想いを寄せる青年ケンジは、ハルミを拉致・監禁する。最初は怯えるハルミだったが、ケンジの献身的な愛に少しずつ心を動かされていき……。監督・脚本は「歩く、人」の小林政広。ヒロイン役にはベテラン女優の荻野目慶子が起用され、過激な濡れ場を披露している。", "2004-12-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALICE IN DEN STÄDTEN", "都会のアリス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "旅行記執筆のためにニューヨークを訪れていたドイツ人フィリップはひょんなことから少女アリスと知り合う。書き置きを残して去った少女の母は約束のアムステルダムに現れず、今度は祖母の家を探して西ドイツへと旅するふたりだったが……。リュディガー・フォーグラー主演のベンダース“ロード・ムービー3部作”の記念すべき第1作。", "1988-11-19", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GARFIELD", "ガーフィールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "世界中の人に愛される猫の“ガーフィールド”が、コメディの名手P・ヒューイット監督の手により、3D−CGの実写版で登場。飼い主に連れられて新しくやって来た子犬のオーディに嫉妬するガーフィールドの姿が、愛嬌たっぷりに描かれる。皮肉屋だけどどこか憎めないガーフィールドのキャラクターの声を担当するのはB・マーレー。", "2004-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("潤の街", "潤の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "「君は裸足の神を見たか」で知られる金秀吉の1981年度の城戸賞受賞作を、新人、金佑宣が映像化した硬派の青春ドラマ。在日朝鮮人の高校生・潤子と日本人青年・雄司の交際を通して日本にはびこる差別と在日朝鮮人たちの哀しみを浮き彫りにしている。数ヵ月の主役捜しの結果選ばれた姜美帆が、はつらつたる好演を見せる。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大江戸千両囃子", "大江戸千両囃子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "美空ひばり、東千代之介、大友柳太朗という東映の三大スターが揃い踏みした時代劇。将軍家拝領の天女の扇が紛失したことから巻き起こる中仙道二十四里の大騒動を、恋と歌、チャンバラ満載で描き上げた痛快娯楽作だ。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DROP SQUAD", "ドロップ・スクワッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "S・リー製作総指揮によるバイオレンス・アクション。白人社会に溶け込んだ黒人を拉致、洗脳する秘密組織ドロップ集団の実態を暴く。マンハッタンの広告会社に勤務する黒人ブルフォードは成功を収めていたが、家族や生まれた町を忘れて白人社会にどっぷりと浸かっていた。それを見かねた妹のレノーラは彼の心に黒人魂を取り戻させるために、ドロップ集団に仕事を依頼する。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RADIOACTIVE DREAMS", "ラジオアクティブ・ドリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "西暦2000年。核戦争で廃墟と化した町の核シェルターから、二人の若者が14年ぶりに地上へと出てきた。しかしそこは、もはや過去の記憶とはほど遠い荒涼たる世界……。未知の世界に放り出された主人公二人の奇妙な体験を描いた、一風変わったSFアドベンチャー作品。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLIPSTREAM", "風の惑星 スリップストリーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「スター・ウォーズ」のM・ハミルが久々にその勇姿を見せたSF活劇。環境汚染のために荒廃した近未来の地球。殺人犯バイロンを護送中の保安官ウィルは、ふとしたことで彼を取り逃してしまう。手製のエアクラフトで追跡を始めたウィル。だが彼の前には様々な敵が立ちはだかり、捜査は困難をきわめていく。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大巨獣ガッパ", "大巨獣ガッパ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "日活初にして唯一の特撮怪獣映画。南海の孤島オベリスク島から日本の探検家によって連れ去られた子ガッパを追って、二匹の親ガッパが東京に上陸する。ガッパは熱光線で東京を破壊するが、子ガッパを取り戻すと３匹で故郷へ帰っていく……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUNGO 日本文学シネマ　グッド・バイ", "BUNGO 日本文学シネマ　グッド・バイ", "", "", "http://ecx.images-amazon.com/images/I/51cIwfVW4fL._SL210_.jpg", 30, "文豪たちの短編を映像化したテレビドラマシリーズ「BUNGO」の1編として、太宰治の未完の絶筆を「地下鉄（メトロ）に乗って」の篠原哲雄監督が映像化。10人の愛人をつくり身動きが取れなくなった田島は、全員と別れて田舎から妻子を呼び穏やかに暮らそうと決心する。そこで田島は、絶世の美女・キヌ子に妻役を演じてもらい、一緒に愛人のもとをまわるという案を思いつくが……。主演はミュージシャンの山崎まさよしと「のだめカンタービレ」の水川あさみ。", "2010-05-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("子連れ狼・地獄へ行くぞ！大五郎", "子連れ狼・地獄へ行くぞ！大五郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「大魔神」などの特撮監督としてならした黒田義之監督による一編。３人の息子をことごとく拝一刀に倒された柳生烈堂は、一人娘の香織に“お手玉の剣”を体得させ一刀を追わせる。黒田監督の特撮技術を生かしたクライマックスの、大雪原の凄絶な死闘が見もの。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Je T'aime Moi Non Plus", "ジュ・テーム…", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ゴミ処理をしながら町を転々とするゲイ・カップルのクラスキーとパドバン。2人は立ち寄ったカフェで、ウェイトレスで働く少年のような女ジョニーと出会う。クラスキーとジョニーはお互いに興味を持つのだが……。監督、脚本、音楽は、本作が監督デビューとなったセルジュ・ゲンズブール。ジョニーを演じたゲーンズブールの妻、ジェーン・バーキンとのデュエット「ジュ・テーム・モワ・ノン・プリュ」も話題となった。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shadow of a Doubt", "疑惑の影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "アルフレッド・ヒッチコック監督による心理スリラー。カリフォルニア州の田舎町サンタローザで暮らすニュートン一家のもとに突然チャーリー叔父さんが訪ねてくる。ニュートン家の長女チャーリーは同じ名前の叔父の来訪を喜んでいたが、やがて2人の探偵が訪ねてくると次第にチャーリー叔父さんに秘密があるのではないかと疑い始める……。チャーリー叔父さんに「第三の男」のジョセフ・コットン。チャーリーに「打撃王」のテレサ・ライト。", "1946-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moonraker", "007／ムーンレイカー", "", "", "http://ecx.images-amazon.com/images/I/51FWCyrulWL._SL210_.jpg", 126, "地球に毒ガスをまいて人類を滅亡に追い込み、選ばれた人間だけで新世界を創造しようとするドラックス。スペースシャトルをめぐる怪事件から悪事を知ったボンドがベニス、アマゾン、そして宇宙を舞台に闘うシリーズ第11作。脇役キールも快調。", "1979-12-31", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("没有太陽的日子／SUNLESS DAYS", "SUNLESS DAYS ～ある香港映画人の天安門～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1989年６月４日に起きた北京の天安門事件。この事件が1997年の返還が予定されている香港の人々にどのような影響を与えたかを、シュウ・ケイが綴ったドキュメンタリー映画。シュウ・ケイ自身の家族や香港、台湾、大陸の友人たちと対話しながら、広い意味での中国人の心情を浮き彫りにしていく。NHKスペシャルで放映され大反響を呼んだものに30分の追加シーンを加えた完全版。1990年ベルリン映画祭O.C.I.C.特別賞受賞。", "1990-01-01", "6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SONS OF STEEL", "スティール・ランナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "英雄ブラック・アリスと、独裁者オシャーナの時空を超えた戦いを描く、近未来SFアクション。東京国際ファンタスティック映画祭参加作品での上映時タイトルは「サンズ・オブ・スティール」。", "1988-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUFFALO'66", "バッファロー'66", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "俳優としても知られるマルチアーティスト、V・ギャロが監督、脚本、音楽、主演の４役をこなした人間ドラマ。刑務所を出て故郷の町バッファローへ帰郷しようとするビリーは“嫁を連れて帰る”と電話で両親に大見栄を切ってしまう。通りすがりの少女レイラを拉致して妻のふりをするよう脅迫。なんとか両親との対面を済ませたビリーは、次に自分を刑務所に送りこんだ裏切り者への復讐に挑む。そんな彼の孤独な素顔を知ったレイラは、そのあとも行動をともにし……。愛を知らない孤独な男の感情を、ギャロは渇いたユーモアとともに描出。自身で主人公ビリーを妙演し、大人になれない男の情けなさをリアルに伝える。あどけない顔に似合わない濃厚なメイクを施したC・リッチの聖母のようなヒロイン像も印象深い。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・負けてたまるか！", "喜劇・負けてたまるか！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "野坂昭如の小説の映画化。流行作詞家に弟子入りした寺川友三は事務員の良子とデキてしまい、良子は押しかけ女房の座にすわる。ラジオ作家と知り合った友三にはそろそろ運が向いてくるが……。CM業界の裏側をネタにしたコメディ。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("彩り河", "彩り河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "無実の罪を苦に自殺した父の復讐に執念を燃やす青年と高級クラブのママが、悪名高い相互銀行の頭取相手に二人だけの闘いを挑んでいく、という社会派ドラマ。「天城越え」で監督デビューした三村晴彦が、再び松本清張の原作に取り組んだ監督第２作。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EN TRES Y DOS", "フルカウント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "野球の黒人名選手ロペスは、その日の試合を最後に引退することになっていた。観衆の絶大な拍手に笑顔で応える彼の脳裏には、過ぎ去った日々が走馬灯のように流れていく。世界屈指の野球国キューバが生んだ、しっとりと心にしみる、情感あふれる野球と人生ドラマ。", "1985-01-01", "49", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Yakuza", "ザ・ヤクザ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "義理と人情で結ばれた日本人の男とアメリカ人の男。２人は誘拐された旧友の娘を救出するため、日本のヤクザ組織に立ち向かう。ミッチャムと高倉の共演でポラック監督が描く異色の仁侠映画。", "1974-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPIDER−MAN STRIKES BACK", "スパイダーマン／プルトニウムを追え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "我らがヒーロー、スパイダーマンが帰ってきた。ピーターの大学から盗み出されたプルトニウムから原子爆弾を作り、ロサンゼルスで遊説中の大統領を街もろとも吹き飛ばそうと企む悪漢を相手に、またまた大活躍。日本未公開のシリーズ第３作。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひまわりと子犬の7日間", "ひまわりと子犬の7日間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-04-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eloge de l'Amour", "愛の世紀", "", "", "http://image.eiga.k-img.com/images/movie/1257/315x210.jpg?3013", 98, "現在をモノクロフィルムで、回想をカラーのデジタル撮影で描く、ゴダール久々の新作。愛について描く新作のため、オーディションをする芸術家エドガー。彼は、ヒロインに相応しい女性に2年前会っていることを思い出す。彼女は、レジスタンスの歴史を調べていて出会った、かつてレジスタンスの闘志だった老夫婦の孫娘だった。老夫婦の回想録の映画化を巡り、ハリウッドのエージェント、国防省の役人らが彼らの周囲に出没する。", "2002-04-13", "2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仲間たち", "仲間たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "東北の田舎から出てきた光弘は、バスの車掌、節子と知り合う。運転手の仕事が不安定なことを知った彼は自分のトラックを持ちたいと思い、今まで以上に働いてお金を貯めるがある日事故を起こし、苦しい入院生活に入る。だが、やけを起こしかけた彼を節子と、同郷の友人が温かく包みこむのだった。さわやかな雰囲気が漂う青春もの。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Another 48 Hrs.", "48時間PART2／帰って来たふたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "　監督と主演コンビが再集結した痛快アクション第2弾。麻薬取り引きの黒幕と目される正体不明のボス、アイスマンを追うサンフランシスコの刑事ジャックは、かつて捜査に協力させた囚人レジーがその鍵を握っていると知り、再び彼を引っぱり出すことに。バイクに乗った謎の集団に狙われながらも捜査を続けるジャックとレジー。やがて警察内部にアイスマンとつながる者がいるとにらむが……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AMOUR EXTRE＾ME", "裏切りの情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "刑を終え、ポルトガルに戻ってきたクラウディア。彼女は同棲相手クロエツと再会するが、暗黒街のボスである彼は仕事に追われてばかり。かまってもらえぬ寂しさから、クラウディアはディスコで知り合ったクロエツの手下ヨルグと関係を持ってしまう……。美人女優L・モランテが主演した、エロティック・サスペンス。", "1991-01-01", "2,71,25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スローなブギにしてくれ", "スローなブギにしてくれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "自由で気の向くままに生きる少女と、その少女に引きずられていく中年男の奇妙な交流を通して、それぞれの世代の関係の成立と崩壊を描いた青春映画の佳作。主人公の少女を強姦する労務者風の男に、作家の高橋三千綱と映画監督の和泉聖治が扮している。後にTVドラマでブレイクする浅野温子が快演。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エンジェル 僕の歌は君の歌", "エンジェル 僕の歌は君の歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「ゴースト／ニューヨークの幻」などの流れを汲むラブ・ファンタジー。偶然出会った天使から、別れた恋人があと１週間の命だと聞かされた青年が、彼女のために精いっぱいのことをしてあげようとする、ピュアな恋愛模様を描く。エルトン・ジョンの『YOUR SONG』が効果的に使われている。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("テイク・イット・イージー MODERN TIME", "テイク・イット・イージー MODERN TIME", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "本物の吉川晃司の成長に合わせて、映画の主人公・民川裕司が男として、大人として成長していく様を描いた「すかんぴんウォーク」「ユー・ガッタ・チャンス」に続く“民川裕司ストーリー”の完結編。ニューヨーク公演に失敗した民川の再生までの道程を描く。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEN NOCI SVATOJANSKE", "真夏の夜の夢／イジィ・トルンカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "シェイクスピアの同名ファンタジー・コメディを、人形アニメの名手トルンカが細密に描き上げた、アニメーションの一大モニュメント。大公と女王の婚礼の日、音楽好きの娘ハーミアは婚約者をさし置いて笛吹きを思い、婚約者の方はハーミアをさし置いて金髪の娘にモーションをかける。恋のさや当てに素人芝居の６人も加わって、それぞれが森の中へ。森の妖精パックのいたずらで、不釣り合いな組み合わせのカップルたちの誕生と相なるが……。雨滴を受ける木の葉の揺れ方から恋する者のはにかむ仕草まで、確かな自然観察、人間観察に裏打ちされたトルンカの詩的造形力は驚嘆に値する。", "1959-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人狼", "人狼", "", "", "http://ecx.images-amazon.com/images/I/511wMiNbw0L._SL210_.jpg", 98, "昭和30年代、架空の街・東京を設定に据え、政府の特殊部隊エリート隊員と、反政府ゲリラの少女との哀しき愛憎を描く、反権力的な社会派SF長編アニメーション映画の力作ドラマ。押井守原作のコミック『犬狼伝説』をもとに、押井自ら脚色。アニメーターとして長年のキャリアを持つ、沖浦啓之が初監督の任にあたっている。首都圏治安警察機構“首都警”に属する伏は、反政府セクト“赤ずきん”を追い詰めるさなか、一瞬のスキで組織の少女の自爆を許してしまった。責任を問われて養成学校に戻された彼は、そこで彼女に瓜二つの姉・圭と出会い、お互いに心惹かれていくのだが……。ベルリン映画祭、アヌシー国際アニメーションフェスティバルなど世界各国の映画祭に出品され、絶賛された。国内でも、第54回毎日映画コンクール・アニメーション映画賞、ゆうばり国際ファンタスティック映画祭2000・南俊子賞を受賞。", "2000-06-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("完全なる飼育・香港情夜", "完全なる飼育・香港情夜", "", "", "http://ecx.images-amazon.com/images/I/51GG97ZRDZL._SL210_.jpg", 96, "現実に起きた少女誘拐監禁事件をもとに書かれた松田美智子著『女子高校生誘拐飼育事件』が原作の「完全なる飼育」シリーズ第３作。香港に修学旅行に来た日本の女子高生・愛は地元のタクシー運転手ボウに誘拐、監禁される。言葉の通じない愛とボウがどのように関係を築いていくのかが見どころの一つ。主演の愛には本作が映画初出演となる伊藤かな。前２作にも出演している竹中直人は愛のクラス担任として登場。", "2002-12-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INNER SANCTUM2", "妄想スキャンダラスな女2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "狂った夫を、正当防衛ながらも死なせてしまったジェニファー。彼女は住み込みの看護婦のおかげで、ようやく当時の記憶から立ち直り、平穏な心を取り戻しつつあった。しかし、義兄夫妻が彼女の家を訪れた夜から、ジェニファーの悪夢は再び彼女を苦しめるようになる。“夫が帰ってきた”と怯えるが、誰も本気にしない。自分の夢が次第に現実のものとなり、彼女はさいなまれる。果たして夫は本当によみがえったのだろうか？", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の咲く家", "花の咲く家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "青年医師と美貌の人妻の純愛を描く文芸メロドラマ。旅先のバリ島で、運命的に出逢った隆三と和子。だが男は父の危篤の報を聞いて急きょ帰国、女は強欲な夫に莫大な額の手切れ金を要求され……。松竹きっての美男美女コンビを得て、プログラムピクチャーの名手が巧みな采配を見せた佳編。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MADAME ROSA", "これからの人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "マダム・ローザが住むベルビルは、ユダヤ人、アラブ人、黒人が隣接して暮らす町。彼女は貧民救済院の子供たちと娼婦の息子モハメッドの面倒を見ている。マダム・ローザとモハメッドの交流の中に戦争の記憶、人種問題が描かれる文芸的な名編。", "1977-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("代号美洲豹", "ハイジャック／台湾海峡緊急指令", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "　台湾海峡で起きたハイジャック事件を題材に、「紅いコーリャン」のチャン・イーモウがサスペンスに挑戦した意欲作。台湾の要人を乗せたソウル行きの飛行機が何者かにハイジャックされる。犯人の要求は、中国への進路変更と台湾で収監されている仲間の釈放だった。中国当局は事件を収束すべく、秘密裏に台湾との協力体制を敷く。撮影当時、中国と台湾の国交はまだデリケートで製作には制約がつきまとった。", "1988-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("王将", "王将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "伊藤大輔監督による北条秀司の有名戯曲の３度目の映画化。天才棋士・坂田三吉に三國連太郎が扮し、重厚でアクの強い演技を見せる。ワイドスクリーンに描かれる貧しい裏長屋の風景が秀逸。村田英雄の歌った同名主題歌（作詞：西条八十、作曲：船村徹）は大ヒットしてスタンダード化した。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("QUAND　JE　VOIS　LE　SOLEIL", "ラ・ピエトラ／愛を踊る女", "", "", "http://ecx.images-amazon.com/images/I/21WPGGWCW7L._SL210_.jpg", 113, "フランス・バレエ界の頂点に立つ人気ダンサー、ピエトラガラの初主演映画。彼女が扮する、死を控えたダンサーの命の燃焼がドラマチックに描かれる。ヒロインの悲壮な決意、彼女と夫、愛娘の絆など感動的な逸話が交錯。全身全霊で役柄を演じ切ったピエトラガラの華麗なダンスは必見だ。バレエ・ダンサーのマルゴは、夫と娘に囲まれ、幸福に暮らしている。そんなある日、病が彼女を襲った。すでに死期が近いことを察したマルゴは、最期まで踊り続けることを決意し……。", "2004-11-06", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Frontier(s)", "フロンティア", "", "", "http://image.eiga.k-img.com/images/movie/53797/315x210.jpg?2912", 108, "リュック・ベッソン率いるヨーロッパコープのサバイバルムービー。「ヒットマン」のザビエ・ジャン監督の長編デビュー作。亡命資金のため仲間と強盗を計画するが失敗に終わった移民の少女ヤスミンは、恋人のアレックスと仲間と落ち合う予定の国境近くの小さな宿へ向かうが、そこには、変わり果てた仲間の姿が。ヤスミンは、奇妙な経営者が仕かけた罠だらけの宿から脱出しようとするが……。", "2008-09-13", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eternal Sunshine of the Spotless Mind", "エターナル・サンシャイン", "", "", "http://image.eiga.k-img.com/images/movie/1057/315x210.jpg?1850", 108, "「ヒューマンネイチュア」のミシェル・ゴンドリー監督が、同作と同じチャーリー・カウフマンによるユニークな脚本を映画化。ジョエルは元恋人クレメンタインが、ラクーナ医院で特定の記憶だけを消去する施術を受けてジョエルの記憶を消したことを知り、自分も彼女の記憶を消そうと同じ施術を受ける。だが、クレメンタインを忘れたくないジョエルの深層意識は施術に反抗、自分の脳内のクレメンタインの記憶を守ろうとする。", "2005-03-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妖艶毒婦伝・お勝兇状旅", "妖艶毒婦伝・お勝兇状旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "宮園純子主演の“妖艶毒婦伝”シリーズ第３作。内容は前２作と同工異曲で特筆すべきことはないが、ただ１ヵ所、会社を驚かせたカットがあった。２階建ての宿屋で登場人物が動き回る様を、ロングの俯瞰で捉えたワン・ショット。この作品は監督・中川信夫のメジャー映画会社での最後の仕事となった。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("国会へ行こう！", "国会へ行こう！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "政治に無関心の大学生、直哉はひょんなことから松平代議士の秘書を始める。倫理観の違いに戸惑いながらも政治の世界に情熱を見出した彼は、松平とともに政治改革法の立案に挑むが……。現実との類似にドキリともさせられる政界コメディ。カリスマ的な代議士、松平に扮した緒形拳のとぼけた演技が笑いを誘う。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("БИТВА　ЗА　МОСКВУ／THE BATTLE FOR MOSCOW", "モスクワ大攻防戦 第２部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 180, "ロシア平原を舞台に、ナチス機甲部隊とソ連軍が激突する戦争スペクタクルの続編。快進撃を続け、ついに首都モスクワに迫ったナチスを、ソ連の精鋭部隊は阻むことができるか？　セミドキュメンタリー・タッチの迫力ある映像で描いた辛口映画。", "1985-01-01", "12,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Queen of The Damned", "クイーン・オブ・ザ・ヴァンパイア", "", "", "http://image.eiga.k-img.com/images/movie/1607/315x210.jpg?2395", 102, "18世紀から生きてきたバンパイア、レスタトは眠りから現代に甦り、人気ロック・スターに。その音楽によって目覚めた古代エジプト女王だった吸血鬼、アカーシャは、共に世界を征服しようとレスタトを誘惑する。アン・ライスのベストセラー連作“バンパイア・クロニクルズ”の「呪われし者の女王」を元に映画化。ヒット映画「インタビュー・ウィズ・ヴァンパイア」ではトム・クルーズが演じた吸血鬼レスタトをスチュアート・タウンゼントが演じる。", "2002-10-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダー剣〈ブレイド〉／MISSING ACE", "仮面ライダー剣〈ブレイド〉／MISSING ACE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 42, "平成版“仮面ライダー”シリーズの劇場版。剣崎たち仮面ライダーの死闘の結果、すべてのアンデッドは封印され、平和な日々を送る剣崎たち。そんなある日、突然アンデッドが蘇り、人々は再び恐怖の世界に陥ってしまう。今回、ライダーシステムを封印し、ブレイドに変身する術を持たない剣崎たちに代わって、３体の“未知のライダー” が登場。また、新たなアンデッドが現れるなど劇場版オリジナルのストーリー展開にも注目。", "2004-09-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キミとボク", "キミとボク", "", "", "http://image.eiga.k-img.com/images/movie/55967/315x210.jpg?4124", 45, "マルチクリエイターのやまがらしげとが自身の体験をもとに製作し、2001年にWEB上で発表したFLASHアニメーションの実写化。ある年の七夕にアメリカンショートヘアの銀王号と出合った漫画家志望の青年は、やがて銀王号と心を通わせ、家族同然の存在になっていく。青年とネコの出合いから別れまでの10年間を描く。主演は「BECK」「大奥」の中村蒼。人気声優で歌手の坂本真綾が銀王号の声と主題歌を担当。", "2011-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FLYII", "ザ・フライ２ 二世誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "遺伝子融合の結果、ハエ男と化してしまった科学者セスと恋人ベロニカの悲劇的な愛を描いたヒット作「ザ・フライ」の続編。ベロニカはセスの子供を宿していた。しかし彼女の腹から出てきたのは、なんとハエの卵だった。その卵の中にいた子供マーチンは、人間とハエの混合遺伝子を受け継いでおり、わずか５年で成人へと成長する。父の発明した物質転移装置を再び作動させるため、研究所に幽閉されていたマーチンはコンピュータ・プログラマー、ベスと恋に落ちる。だが幸せもつかのま、彼の身体は父と同じく次第にハエへと変身していくのだった。彼はそんな呪われた運命を作り出した研究所の人間たちに復讐を開始する。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("湾岸道路", "湾岸道路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "都会に住む若い夫婦の現代的なライフ・スタイルを描いたメロドラマ。互いに干渉しあわず、妻が一晩他の男に買われても何も言わない夫、いきなりフラリとバイクで消えた夫を許す妻、という日常的感覚から遊離した二人が映される。都市の硬質な風景を捉えた川上皓市のカメラが秀逸。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("非行少女", "非行少女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "デビュー作「キューポラのある街」で、一躍名をあげた浦山桐郎が、和泉雅子を主演に起用した監督第２作。金沢へ帰ってきた三郎は、幼なじみの若枝と再会するが、彼女は絶望的な環境のなかで身も心もすさみきっていた。ついに若枝は非行少女の保護機関である北陸学園に入れられるが、三郎は彼女を励ます。浦山桐郎はともすればセンチメンタリズムに陥りやすい題材を、１歩手前で抑制をきかせ、これをシャープな佳作に仕上げた。日活時代は始終吉永小百合の影に隠れた存在だった和泉雅子が、一世一代の好演を見せる。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NAVIGATOR", "海底王キートン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "悪漢の陰謀によって、太平洋をはてしなく漂流している巨大客船ナビゲーター号。無人のはずの船内には、意中の娘とたった二人で取り残されて大喜びのキートンがいた……。設定が設定だけに、キートンにしては珍しく密室を主な舞台にしたサイレント映画の長編。水中服を着たキートンが、魚とチャンバラを見せる場面が楽しい。", "1924-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("未来予想図／ア・イ・シ・テ・ルのサイン", "未来予想図／ア・イ・シ・テ・ルのサイン", "", "", "http://image.eiga.k-img.com/images/movie/34227/315x210.jpg?1588", 115, "DREAMS COME TRUEのヒット曲「未来予想図」をモチーフに、それぞれの夢を追う恋人たちの姿を描いたハートウォーミングなラブストーリー。印刷会社に勤めるさやかは、大学時代からの恋人・慶太に励まされ、以前からの夢だった雑誌編集者に転職する。その矢先、スペインの建築家・ガウディに憧れて建築事務所で働いていた慶太がスペインへ赴任することになり、2人は別々の道を歩き始める……。", "2007-10-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IM KREISE DER LIEBEN", "交際欄の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "男から男へと乗りかえ、金を稼ぎまくる女だけの３世代家族の行状を、ユーモアを交じえて描く異色のドイツ映画。祖母、母と３人で暮らしているマリアは、一家の稼ぎ頭。男を欺すのは毎度のことだが、ある時、その一人がマリアに惚れて家に押しかけてくる。ところが、その男にマリアの母が一目惚れして……。", "1991-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZOO", "ZOO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "若手人気作家・乙一の同名短編集を、新進気鋭のクリエイター5人が映画化したオムニバス。ダークな世界観で貫かれた原作の雰囲気をそのままに、生と死を静かに見つめたドラマが並ぶ。人気子役の神木隆之介や、個性派女優の松田美由紀など、乙一ワールドに集結した多彩なキャストも見もの。一卵性双生児の姉妹カザリとヨーコ。母親はカザリだけを溺愛し、ヨーコには虐待を繰り返すのだが……。(「カザリとヨーコ」より)", "2005-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さくや／妖怪伝", "さくや／妖怪伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "平成“ガメラ”シリーズなどで知られる特殊メイク・造形の第一人者の原口智生が監督、同シリーズの特技監督・樋口真嗣とコンビを組んだSFX時代劇アドベンチャー。江戸時代、悪しき妖怪たちを封じ込めていた霊峰・富士が噴火、妖怪たちが地上に放たれてしまう。公儀妖怪討伐士の家に生まれ育った咲夜は幕府大老の命によって、怪異を引き起こした元凶を探り、妖怪の首領退治に向かうのだった……。カッパから、ラストに登場する松坂慶子扮する土蜘蛛の女王まで、次々と登場する妖怪たちの造形が見事。映画初主演の安藤希の凛々しさも光る。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花と龍", "花と龍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "明治末期の北九州を舞台に、一介の沖仲士が男を上げていく任侠ロマン。1954年の東映版、1962年の日活版に続く３度目の映画化。この山下耕作の作品では、中村錦之助のさわやかさと、佐久間良子の世話女房的な温かさのコンビで魅せる。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冒険王／DR.WAI IN“THE SCRIPTURE WITH NO WORDS”", "冒険王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「チャイニーズ・ゴースト・ストーリー」の活劇派シウトンが、香港のアクション・スター、L・リンチェイ（＝ジェット・リー）と組んで描いたアクション・アドベンチャー。伝説の聖典を探し求めて盗賊やギャング、日本軍と戦う考古学者の冒険をダイナミックに描く。主人公に扮するリンチェイが、お得意のカンフー・アクションの一方で、ユーモラスな妙演を見せる。", "1996-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MIDNIGHT KISS", "キング・オブ・ヴァンパイア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "血液を一滴残らず吸い取られるという残虐な手口の連続殺人事件が発生した。被害者が狙われるのは決まって夜で、そのすべてが女性だという。囮となり犯人と格闘する女刑事のキャリーは、この世のものとは思えない形相に変化していく彼の正体を知る……。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MACBETH", "マクベス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "子供の頃からシェイクスピアに親しんだウェルズが初めて手掛けた、監督・主演のシェイクスピア映画。ウェルズは原作を大幅にカットし、ドラマ展開をスピーディーにした。全編が岩のごつごつした洞窟のようなセットで撮影され、構図には非常に工夫が凝らされている。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NINJA III—THE DOMINATION", "ニンジャ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1980年代アメリカで東洋の神秘がもてはやされるなか、忍者ブームも映画から到来した。日本人俳優のS・コスギが、自ら発案したエキゾチックな忍者装束で活躍する作品がまさに本編で、全米で大ヒットを記録。物語は、現代のアメリカ文明社会に突如忍者が出現し、活躍するという実にシンプルなもの。S・コスギは、ブルース・リーのように己の肉体をもって、それをステータス・シンボルとした。映画の“アイデア”というものを考えさせられる一編。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人造人間ハカイダー", "人造人間ハカイダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "1970年代のTVシリーズ「人造人間キカイダー」「キカイダー01」の人気悪役キカイダーを主役に据えたヒーロー・アクション。超管理社会ジーザスタウンを壊滅させるため、悪の元老院議長グルジェフと公安指揮官ロボットのミカエルに闘いを挑んだハカイダーの活躍を描く。雨宮慶太らしいSFX活劇の合間に、宝生舞が演じる謎の少女カオルをめぐる幻想的なエピソードが挿入されている。グルジェフ役をミュージシャン、本田恭章が怪演。長尺の「ディレクター・カット版」もリリースされている。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛇にピアス", "蛇にピアス", "", "", "http://image.eiga.k-img.com/images/movie/53357/315x210.jpg?1907", 123, "金原ひとみの芥川賞受賞作「蛇にピアス」を、世界に知られる演出家・蜷川幸雄が映画化。蛇のように舌先が割れた“スプリット・タン”を持ち、全身にピアスや刺青をした男アマと、アマの紹介で知り合った彫り師シバの2人の男と関係を持つようになった19歳のルイは、自らの舌にもピアスをあけ、背中に刺青を彫る。それでも満たされない何かを探し求めるルイだったが……。ルイ役は映画初主演の吉高由里子。藤原竜也、唐沢寿明ら豪華俳優が特別出演。", "2008-09-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Obsessed", "オブセッション　歪んだ愛の果て", "", "", "http://image.eiga.k-img.com/images/movie/54617/315x210.jpg?3571", 108, "家族を愛する美しい妻シャロンは、豪華な新居で理想的な生活に満たされていた。会社で昇進した夫デレクの元には、派遣社員リサが新登用される。リサは積極的に仕事こなす一方、優しいデレクに恋愛妄想を募らせていく。過激な偏愛に取り憑かれた彼女はストーカーという名の凶器へと変貌し、やがてシャロンや幼い息子までもがターゲットにされてしまう……。", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Break Out！", "Break Out！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "ネットムービーとして無料配信され、のちに劇場公開された家族ドラマ。娘の縁談をご破算にしようとする父親と、なんとか結婚しようとする娘の奮闘がコミカルに描かれる。70年代にホームドラマの顔として人気を博した主演の石立鉄男が、娘を溺愛する父親をいぶし銀の魅力で熱演。熱海で悠々自適の老後を過ごす阿久根瞬は、結婚を間近に控えた長女リカの婚約者・一郎とその親に挨拶するため、上京するのだが……。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Synecdoche, New York", "脳内ニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/53557/315x210.jpg?2081", 124, "「マルコヴィッチの穴」「エターナル・サンシャイン」の人気脚本家、チャーリー・カウフマンの初監督作。妻と娘に家を出て行かれ、行き詰っていたニューヨークの劇作家ケイデン・コタードは、自身が思い描くニューヨークを実際のニューヨークの中に作り出すという、壮大な芸術プロジェクトを構想するが……。ケイデン役にフィリップ・シーモア・ホフマン、彼を取り巻く女性たちにミシェル・ウィリアムズ、キャサリン・キーナーら豪華女優陣が集う。", "2009-11-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MARSEILLAISE", "ラ・マルセイエーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "1789年のフランス革命時、パリのバスチーユ監獄占領の報を聞いたマルセイユの志願兵約500名は、彼らの進軍歌『ラ・マルセイエーズ』を歌いながらパリに攻め上り、ルイ16世のいる宮殿を占領する……。フランス労働総同盟の協力と一般の募金で製作された巨匠J・ルノワールの作品。", "1938-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DILLINGER AND CAPONE", "デリンジャーVSカポネ　抗争の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "誰もが憎んだ世紀の犯罪者デリンジャーは、今では犯罪から足を洗い、家族とともに新しい人生をスタートさせていた。そうと知らないFBIはシカゴのバイオグラフ・シアターで、デリンジャーの兄をデリンジャー本人と間違えて射殺する。そして、犯罪史上最も不可思議な事件はここから始まった。デリンジャーの秘密のすべてを知っているアル・カポネは、平穏な生活を送っている彼にもう一度だけ、銀行強盗を依頼する。M・シーンとアカデミー賞男優F・M・エイブラハムが、壮絶なガン・アクションを繰り広げる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Abed 二十歳の恋", "Abed 二十歳の恋", "", "", "http://image.eiga.k-img.com/images/movie/56107/315x210.jpg?1618", 100, "“20歳の恋”をテーマに、映像作家・俳優の育成を目的として企画・制作されたオムニバス。カンヌ国際映画祭出品の経歴を持つ木村有理子ら5人の若手監督がメガホンをとり、人気アイドルの真野恵里菜、「仮面ライダーW」の木ノ本嶺浩、「侍戦隊シンケンジャー」の高梨臨らフレッシュな若手俳優が多数出演。人気ロックバンド「GOING UNDER GROUND」の松本素生が映画初出演を飾る。", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔法少女リリカルなのは The Movie 1st", "魔法少女リリカルなのは The Movie 1st", "", "", "http://image.eiga.k-img.com/images/movie/55057/315x210.jpg?3900", 0, "2004年にTV放映され人気を博した「魔法少女リリカルなのは」の劇場版第1弾。TVシリーズ1期・全13話のエピソードを、新しいシナリオと映像で再構築する。ある事件をきっかけに魔法使いとなった平凡な小学3年生・高町なのは、同い年の少女魔導師、フェイト・テスタロッサとともに、異世界の遺産「ジュエルシード」をめぐる戦いに巻き込まれていく。", "2010-01-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bulletproof Monk", "バレット・モンク", "", "", "http://image.eiga.k-img.com/images/movie/1317/315x210.jpg?4061", 103, "チベットの高僧が代々伝えてきた巻物には、世界を地獄にも天国にも変え得る強大な力が秘められていた。この巻物の守護者になれるのは、3つの預言を成就した者のみ。現在の守護者であるチベット僧は、ニューヨークでスリをしている白人青年カーに出会い、彼を後継者だと考えるようになる。初監督に挑むのは、エミネム、ジェニファー・ロペスのクリップで注目の22歳のポール・ハンター。特殊メイクにはグレッグ・キャノムが参加。", "2004-01-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAN FRANCISCO", "桑港〈サンフランシスコ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "1936年のアメリカ映画興業成績No.1に輝いた超大作。出演はC・ゲーブル、S・トレーシーなど当時最高の豪華キャスト。とくにゲーブルは“キング・オブ・ハリウッド”といわれ、最もノリにノッていた時期だ。オペラ歌手を夢見るマリーはサンフランシスコでダンスホールの仕事に就く。その店のオーナーとマリーは恋に落ちた。しかし、そこへオペラの団長が現れ、彼女に契約を申し出る。二者択一を迫られるマリー。折しもサンフランシスコ大地震が起こり、恋もダンスホールも、すべてを呑み込んでいく……。全編を彩るジャズやオペラの名曲の数々、そしてラストのSFXなど、どれをとっても一級の作品だ。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("小島の春", "小島の春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "瀬戸内海・長島の国立ライ病療養所における患者救済の記録を女医・小川正子が綴った体験記をもとに、八木保太郎脚色、豊田四郎監督で映画化した名作。正子は、瀬戸の島々に隠れ住むライ病患者を探して療養所に入るように説得するが、患者たちは皆かたくなに拒否する。しかし彼女の心からの説得に患者たちも次第に心を開いていく。当時、不治の病と言われたライ病の実態を豊田四郎は、美しい風光に女医の短歌を添えた情景ショットを差しはさみつつ、厳しくヒューマニズムの中に描き出した。夏川静江が淡々としたなかに情熱をたぎらせ主人公の女医を好演。中村メイコが島の少女として登場し、懐いた女医を遠くに見ながら幾度もさよならを言う、別れの名シーンを作った。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pledge This!", "プリティ・ライフ　パリス・ヒルトンの学園天国", "", "", "http://ecx.images-amazon.com/images/I/51MIs3dQ%2BfL._SL210_.jpg", 89, "お騒がせセレブとして世界中の注目を浴びるパリス・ヒルトンが映画初主演＆プロデュースに挑んだ、ちょっとエッチな学園青春コメディ。完璧なルックスとゴージャスなファッションで学園中の人気を集めるビクトリアは、仲間たちと一緒に地味な女子グループをイジメて楽しんでいた。ところがある日、地味な女子たちがビクトリアへの反撃を開始。隠し撮りされた恥ずかしいプライベート映像を、ビューティコンテストの会場で流されてしまう。", "2007-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月とキャベツ", "月とキャベツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "第２回さっぽろ映像セミナー入選シナリオである鶴間香の『眠れない夜の終わり』の映画化。森田芳光、中原俊らの助監督を務めていた篠原哲雄の「草の上の仕事」に続く第２作。かつてミュージシャンとしてカリスマ的な人気のあった花火は、今では意欲もなくなり田舎町でキャベツを育てながら隠遁生活を送っていた。そんな彼の前に、ヒバナという少女が現れて……。山崎まさよしが主演、名バラードを作るまでのメイキング・ドラマにもなっている。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CREATURE", "クリーチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "21世紀、人類は資源を求めて、広大な宇宙へと捜索の旅に出た。ところが、土星の衛星タイタンに向かった調査隊が、“20万年前の遺跡を発見”の連絡を最後に行方不明となる。真相究明のため新たな調査隊が出発した……。多様なSFXが恐怖感を盛り上げるSFホラー。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE VIOLENT PEOPLE", "三人のあらくれ者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1866年、南軍の大佐だったコルトは、５年ぶりに牧場に帰る。親友のイノセンシオは喜んで迎えてくれたが、弟のシンチは、牧場の分け前をよこせと兄のコルトに迫っていた。その頃、政府の長官とその部下、山師らが腹を合わせて重税を課し、土地や牧場を強奪しようとしていたが、彼らはコルトの牧場に現れ、両者の間に争いが起こる。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANGER　MANAGEMENT", "N.Y.式ハッピー・セラピー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "気弱で不満もグチも言えない男がなぜか怒り抑制セラピーを受けることに。しかも、彼のセラピストは、患者よりもブチギレることで有名な過激な男だった……。狂気のセラピストにJ・ニコルソン、不運な患者にA・サンドラー。二人が繰り広げるドタバタ・セラピーで笑いを誘い、最後にはさわやかな感動で満たしてくれるハートフル・コメディ。幼い頃からイジメられっ子で気の弱い男デイヴが、飛行機の中での誤解からトラブルを招き、逮捕されてしまう。判事の命令で怒り抑制セラピーを受けるハメになった彼は、非常識でアブないセラピストにすっかり振り回されてしまう。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kilimanjaro", "キリマンジャロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「八月のクリスマス」の脚本家オ・スンウクが初メガホンを取ったノワール・ドラマ。互いを憎みあいながら、刑事と下っ端ヤクザという正反対の人生を歩んできた双子の兄弟ヘシクとヘチョル。ある日、事件の容疑者として追いつめられたヘチョルが、ヘシクの銃を奪って自殺してしまう。ヘチョルの遺骨を抱えて故郷へ戻ったヘシクは、町のヤクザたちに弟と勘違いされて抗争に巻き込まれていく。「手紙」のパク・シニャンが、双子の兄弟を1人2役で熱演。", "2007-03-10", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FROGS", "吸血の群れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "フロリダのある湖沼近くの屋敷に住むジェイソンは、一族を集め誕生日と独立記念日を祝っていた。そのパーティーに、異常繁殖したカエルやトカゲ、ヒル、ヤモリなどが侵入してくる……。あらゆる爬虫類を登場させたパニック・ホラー。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレヨンしんちゃん　暗黒タマタマ大追跡", "クレヨンしんちゃん　暗黒タマタマ大追跡", "", "", "http://ecx.images-amazon.com/images/I/61K4m7Bc9jL._SL210_.jpg", 100, "最強の幼稚園児、野原しんのすけが大騒動を繰り広げるコメディ・アニメの劇場版シリーズ第５作。今回より、彼の妹ひまわりも登場。河原で寝ていたオカマのローズが落とした光る玉をしんのすけがそっと持ち帰り、それをひまわりが飲み込んでしまった。その玉が魔神ジャークを封じ込めた埴輪のカギだったことから、しんのすけは悪霊退治を生業とするタマヨミ族とタマユラ族の抗争に巻き込まれていく。", "1997-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吉原炎上", "吉原炎上", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "女性を描くことに定評のある五社英雄監督が、ことさら美術に力を入れて撮り上げた吉原遊廓の一大絵巻。衣装、メイクに白と赤を基調とした極端な色彩感覚を発揮し、名取裕子やかたせ梨乃などクセのある女優のぶつかりあいを力強い演出でみせる。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ノロイ", "ノロイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "「リング」「呪怨」シリーズのプロデューサー・一瀬隆重が、新鋭監督・白石晃士を起用して完成させた衝撃作。謎の失踪を遂げた怪奇実話作家の残したドキュメンタリー作品にまつわる恐怖を、リアリティーを追求した演出で映像化。謎が謎を呼ぶスリリングな展開に圧倒されるホラーだ。怪奇実話作家・小林雅文の自宅が全焼、妻の焼死体が発見された。行方不明の小林は映像作品『ノロイ』を完成させた直後だった……。", "2005-08-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Son of No One", "陰謀の代償　N.Y.コンフィデンシャル", "", "", "http://image.eiga.k-img.com/images/movie/56507/315x210.jpg?3608", 95, "9・11テロの影響も色濃く残る2002年アメリカ、ニューヨーク。生まれ育ったクイーンズ地区に配属された警官ジョナサンは、少年時代に誤って殺人を犯していた。父親の相棒だった刑事スタンフォードの助けで、その事実は隠されていたが、ジョナサンがクイーンズ地区に戻った途端、何者かにより過去の殺人をほのめかす脅迫状が届き……。チャニング・テイタムが主演、アル・パチーノ、ジュリエット・ビノシュら豪華キャストが共演するクライムサスペンス。", "2011-07-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("斬 KILL", "斬 KILL", "", "", "http://image.eiga.k-img.com/images/movie/54077/315x210.jpg?3485", 82, "「イノセンス」「スカイ・クロラ」の押井守監督が、“チャンバラ”をテーマに総監修を務める実写オムニバス。押井監督も自らメガホンを取り、菊地凛子主演で「ASSAULTGIRL2」を手がける。その他、「バトル・ロワイアルII」の深作健太監督作「こども侍。」、「真・女立喰師列伝」の辻本貴則監督作「キリコ」（水野美紀主演）、本作がデビュー作となる田原実の監督作「妖刀射程」（石垣佑磨主演）の計4編。第21回東京国際映画祭「日本映画・ある視点」部門出品。", "2008-12-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋人岬", "恋人岬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "妹が車で人を轢き、その賠償金のために銀座のホステスになったTVの女性プロデューサー。ある日、客が嫉妬から彼女と間違えて妹に硫酸を浴びせてしまい、妹は失明の危機にさらされる。随所に美しい画面を配した西河克己の演出が見もののメロドラマ。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パーフェクト・ブルー", "パーフェクト・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/55637/315x210.jpg?2346", 0, "人気作家・宮部みゆきの長編デビュー作を加藤ローサ主演で映像化した社会派ミステリー。高校野球のスター選手が全身にガソリンをかけられて焼死するという事件が発生。被害者の弟・進也の捜索を父親から依頼されていた探偵事務所の加代子は、兄殺しの容疑をかけられた進也の無実を証明するべく、愛犬マサとともに調査を開始する。同じころ、巨大企業・三友製薬が、かつて開発した薬品の製薬方法をネタに脅迫を受けていた。", "2010-09-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SAEKO", "SAEKO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "新宿・歌舞伎町を根城にする女探偵、冴子。彼女の仲間は、バーのマスター内藤、そして伝説化しているヤクザの藤原。その藤原が久しぶりに出所し、藤原の弟分は、今は他の組に取られた縄張りを、藤原を盛り立てて取り戻そうとするが……。コメディアン内藤陳とプロレスラー藤原喜明。二人の“地”のキャラクターを生かした、異色ハードボイルド作品。ビデオ発売時タイトルは「冴子・新宿事件屋稼業」。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Legally Blonde 2: Red, White and Blonde", "キューティ・ブロンド　ハッピーMAX", "", "", "http://image.eiga.k-img.com/images/movie/52047/315x210.jpg?2846", 95, "キャラクターはそのままだがスタッフは一新。監督は「Kissingジェシカ」のチャールズ・ハーマン・ワームフェルド、ストーリー原案は「恋は邪魔者」のエバ・アーラートとデニス・ドレイクに本作で映画デビューのケイト・コンデルが担当し、コンデルが脚本を執筆。映画が動物愛護ネタなので、ヒロイン、エルの靴には、動物の皮以外の素材で作られた63足が用意されたが、演じたウィザースプーンが全足、お持ち帰りしたそうだ。", "2003-11-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("素人助役奮闘記", "素人助役奮闘記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "自治省の係長から埼玉県の小村の助役に赴任した公務員のエッセイ風体験記をもとにした作品。異例の人事によって利根川村の助役となった坂本太郎。最初はくさっていたものの次第に村人たちの温かい心に触れて、貴重な人生体験を積んでいく。ユーモアとペーソスを織り交ぜた人間喜劇。ローカル色も豊かな作品。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARD PROMISES", "いつも隣りにいてほしい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "家庭を顧みずに夢を追い続ける前夫と、まじめで誠心誠意愛してくれる再婚相手。そんな二人の間で、クリスは苦労する。彼女はどちらの愛を選ぶのか……。オスカー女優S・スペイセクが、愛の選択に迫られたヒロインを熱演するラブ・コメディ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ふたり", "ふたり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "赤川次郎原作の同名小説をもとに大林宣彦が再び故郷・尾道を舞台に描くファンタジー。美人で頭脳明晰な千津子とドジでノロマな実加の姉妹。ある日、千津子が事故で急死し、そのショックで一家は火が消えたようになってしまう。それでもなんとか姉の死を乗り越えていく実加。そんな彼女の前に死んだはずの千津子が現れ、実加は千津子の力添えで、数々の困難を乗り越えていく。そんなある日、父の浮気が発覚し、実加の心は大きく揺れ動くが……。本作がスクリーン・デビューとなる石田ひかりが様々な困難に遭いながらも少女から女へと成長していくヒロイン役を好演。また、従来の尾道３部作では見られなかった家庭崩壊という極限に陥った少女の心理描写などを取り入れ、まさに“新・尾道３部作”の幕開けにふさわしい傑作となった。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHEENA", "シーナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "1940年代の人気漫画をオール・ケニアロケを敢行して映画化したアドベンチャー・ドラマ。落盤事故でアフリカの奥地に一人残された幼児が、ザンブリ族の女占い師に育てられ、ジャングルの女王シーナに成長する。シーナは部族間の争いに巻き込まれた女占い師を助けに、多くの動物を率いてやって来る。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JULIANA", "夢のバスにのって", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "貧困にあえぐペルーの首都リマ。継父の非常な仕打ちに怒った12歳の少女フリアナは、家を飛び出し、男の子のふりをして、ヤクザ者のドン・ペドロの下で働き始めるが……。実際のストリート・チルドレンたちが出演した社会派の思春期ドラマ。プライドを捨てることなくたくましく生きる子供たちの姿が感動を呼ぶ。", "1988-01-01", "59", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROM NIGHTIII：THE LAST KISS", "プロムナイト3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ハイスクールのプロムの女王コンテストで優勝し、ボーイフレンドの放火によって焼死した美少女の霊が、悪霊となって学校に取り憑き、様々な怪奇事件を起こすホラー作品。事件から30年後のプロムナイト。悪霊となった彼女は昔のボーイフレンドを惨殺し、平凡な学生に取り憑き、邪魔をする者たちを殺していく。", "1989-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Great Northfield Minnesota Raid", "ミネソタ大強盗団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "西部史に名高いジェシー・ジェームスとヤンガー兄弟の物語。連続列車強盗でその名を知られたジェシーとヤンガー兄弟だが西部の無法者の時代はもう終わりつつあった……。P・カウフマンによる、時代にとり残された男たちの悲哀を描いた異色ウエスタン。これが彼の監督第１作である。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CULPEPPER CATTLE CO.", "男の出発〈たびだち〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "カウボーイを夢みる16歳の少年ベンは、テキサスからコロラドまで牛を運ぼうとしているカルペッパーに懇願して仕事を世話してもらった。だが、賊に襲われて大事な拳銃を奪われたり、見張りをしている時に馬泥棒に襲われ馬をごっそり盗まれたりと、彼はカウボーイとしての厳しい現実に直面していく。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン／時計じかけの摩天楼", "名探偵コナン／時計じかけの摩天楼", "", "", "http://ecx.images-amazon.com/images/I/51Z2MQ3KXWL._SL210_.jpg", 95, "『週刊少年サンデー』に連載されたコミックに基づくTVアニメの劇場版。小さな名探偵コナンの名推理を中心に、ミステリアスなドラマが展開する。プラスティック爆弾用の特殊火薬が大量に盗まれた。事件を追うコナンは、正体不明の犯人にほんろうされるばかり。犯人は次々と爆破を予告して町を震撼させる。やがてコナンの身にも危険が迫り……。", "1997-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おくりびと", "おくりびと", "", "", "http://image.eiga.k-img.com/images/movie/53337/315x210.jpg?2240", 130, "遺体を棺に納める“納棺師”という職業を通して、様々な死と向き合い人生をみつめるヒューマンドラマ。監督は「壬生義士伝」の滝田洋二郎、音楽を久石譲が担当。ひょんなことから納棺師の見習いとなった元チェリストの大悟は、妻の美香には冠婚葬祭の仕事とごまかして働いていた。日々とまどいながらも様々な死と出会い成長していく大悟と、それを見守る美香を本木雅弘と広末涼子が好演。第81回アカデミー賞で、日本映画史上初の外国語映画賞を受賞した。", "2008-09-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ST. IVES", "セント・アイブス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "J・リー・トンプソン監督が、ブロンソンを起用して描くサスペンス・アクション。事件記者から探偵小説家へ転向した主人公は、さる富豪がたくらんだ大金運搬の事件に巻き込まれる。ビセット、ハウスマンら脇役の好演が光る。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まごころ", "まごころ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "木下惠介の脚本を得て、前年監督デビューした小林正樹が少年の愛を叙情豊かに綴った名編。受験生の弘は、胸を患い部屋から一歩も出られない少女ふみ子を、窓越しに見つめ恋に落ちる。映画２本目の出演で、純粋な愛に生きる少女に扮した野添ひとみは、この１作で清純派スターの印象を決定づけた。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GARDIEN DE LA NUIT", "夜の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "イヴは夜勤の警備員。しかし一方では車泥棒という顔を持っていた。彼にはオーロールという看護婦の恋人がいるが、すれ違いが多く、二人の間の溝は次第に深まっていった。そんな時、彼女にプレイボーイが近づき、彼をイラつかせる。そしてその不満に比例するかのようにイヴの犯罪はエスカレートしていく。俊英J・P・リモザンが手掛けた、一風変わった犯罪映画。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL COLOSO DE RODAS", "ロード島の要塞", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "紀元前３世紀、ロード王国に建てられた難攻不落の大要塞、太陽神アポロの巨像。現在でも“世界の七不思議”の一つとして数えられているこの巨像をめぐる、ロード島王国フェニキアとギリシャ間の闘争と陰謀を描いた歴史スペクタクル。S・レオーネの単独による監督デビュー作で、「ベン・ハー」の助監督時代の経験が大いに生かされている。", "1961-01-01", "1,4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADVENTURES OF BUCKAROO BANZAI: ACROSS THE 8TH DIMENSION", "バカルー・バンザイの8次元ギャラクシー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "日本人の血が入ったニュー・アメリカン・ヒーロー“バカルー・バンザイ”。彼は有能な医者であり、また自分のバンドを率いて歌う人気ロック・ボーカリストであり、はたまた人々の平和を守るために独自のレスキュー隊を組織する、まるでスーパーマンのような男だった。ある時彼は、新装置の実験によってついに８次元の壁を突き破ることに成功。しかし、捕らえられていたはずの極悪エイリアン・リザルドがひそかに刑務所を脱走し、何とかその新装置を奪いとろうと画策し始めていた。かくして二人の間で凄絶な戦いが繰り広げられるのだが……。奇想天外なエピソードがぎっしり詰まった風変わりなSFアドベンチャー。P・ウェラーは、この作品によってアメリカでカルト的な人気を得た。J・ゴールドブラムが騎兵隊の格好をした医者仲間で登場したり、J・リスゴーが極悪エイリアンでクレイジーぶりを披露したりと、脇役たちの大熱演にも大いに注目。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アイコ十六歳", "アイコ十六歳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「ORANGING1979」「フルーツバスケット」など、女の子の微妙な心の動きを映し出してきた自主映画作家、今関あきよしの35ミリ映画デビュー作。名古屋の高校生・三田アイコの学生生活を今関らしいタッチで切り取っている。富田靖子のみずみずしい演技も魅力。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOP SECRET！", "トップ・シークレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "国際フェスティバル出演のため、東ドイツに招待された米国の人気ロック歌手が、そこで政治的陰謀に巻き込まれ、やっとの思いで国外に脱出するまでをコメディ・タッチで描いた作品。歌手を救うため、諜報員、地下組織のメンバーが大活躍。痛快な娯楽作品。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鶏はふたたび鳴く", "鶏はふたたび鳴く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「愛と死の谷間」に続き、五所平之助が作家の椎名麟三を脚本に据えて再び組んだ作品。不評だった前作と打って変わり庶民の生活をテーマにした佳作となった。恋人に自殺されたふみ子は、その責任が自分にあると思い悩む。が、ともに働く仲間に力づけられ、恋人の事業の失敗を償うため、ふみ子は立ち上がる。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STREETWISE", "子供たちをよろしく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "家出や逃亡のはてに、シアトルにある小さな広場に住みついたティーンエイジャー・グループの生態を、インタビューによる彼らの生の声を交じえながらレポートするドキュメンタリー。17歳のラットは、年長者のジャックとともにぼろホテルを棲み家にし、空軍入隊を夢見ている。14歳のやせた売春婦タイニーの夢はヨットを持つ大金持ちになること。16歳の細身の少年ドゥエインは服役中の父親の期待に応えたいと思っている。盗みや麻薬や売春の誘惑にさらされながら、否応なく大人びていく彼らの街頭暮らしの苦楽に、カメラは愛情を込めて肉迫。撮影中にドゥエインが自殺するというハプニングが深い悲しみを誘う。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("落葉樹", "落葉樹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "新藤兼人の自伝的要素の色濃い作品で、老作家の少年時代の回想を通して、亡き母への追憶と思慕を描く。父親が知人の借金の連帯保証人になったために、山や田畑、屋敷を失うことになった広島のある農家を舞台に、常に泰然自若としている父、20歳の長兄、17歳と15歳の姉、８歳の主人公、そしてそれを見守る母親のひたむきな姿を心優しく描く佳編。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LITTLE SISTER", "アリッサ・ミラノ 言いだせなくて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "A・ミラノがヒロインを演じる青春満喫カレッジ・ライフもの。ロバートは先輩の命令を受け、女装して女子寮に侵入する。ところが、そこで片思い中のダイアナに出会ってしまう。彼は女の子ロバータになりすまし彼女と親友になるのだが、親しくなればなるほど、自分の素性を言いだせなくて……。笑いの味つけが効いた作品。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TELEPHONE", "ウーピー・ゴールドバーグの　ザ・テレフォン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "風変わりな舞台女優バシュティの最愛の友は電話。ビデオ店への抗議やら、コンビニへの無理な配達依頼、友人へ借金の申し込みと、日々、彼女の電話攻撃は続く。ある時、留守番電話に電話料金未払いのメッセージが。遂に作業員が電話機を撤去しにやって来て……。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("寄性獣医・鈴音 GENESIS", "寄性獣医・鈴音 GENESIS", "", "", "http://image.eiga.k-img.com/images/movie/57137/315x210.jpg?1555", 72, "1500万ダウンロードを記録した春輝原作の人気ケータイ配信コミック「寄性獣医・鈴音」を2編で実写映画化した「寄性獣医・鈴音 GENESIS×EVOLUTION」の前編。人体に寄生し宿主を発情させる謎の寄生虫が猛威を奮うなか、寄生虫の専門知識をもつ美女獣医・有薗鈴音は、寄生虫を駆除するため日々奮闘していた。やがて寄生虫増殖の裏に悪の組織・鬼頭財閥の関与をつきとめるが、同時に鈴音が幼いころに失踪した父がかかわっていることを知る。鈴音は真相を確かめるため鬼頭財閥に戦いを挑むが……。", "2011-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忍者武芸帳", "忍者武芸帳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "白土三平原作の長編劇画を、大島渚監督が映画化した意欲作。俳優は一切出演せず、全編を白土三平の原画のみで構成、静止画像をモンタージュしナレーションを被せるという画期的な方法で描かれた実験的作品。父親を殺された結城重太郎の敵討ちと、忍者影丸の暗躍を描いた長編。影に生き影に死ぬ忍びの悲しさ。白土三平の反権力的なアンチヒーロー像に1970年代の学生運動、安保闘争がオーバーラップして見える、壮絶な反体制闘争時代劇画。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トニー谷の忍術武者修業", "トニー谷の忍術武者修業", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 44, "草双紙を読み過ぎた質屋の伜・福太郎は、すっかり忍術の虜。自分も忍術が使えるのだと思い込み、空想のお姫様救出のために旅に出る。そして運良く悪家老に狙われるお姫様と出会い、忍術を使って悪漢たちを懲らしめるのだが、それはすべて彼を慕うお八重が細工したものだった……。トニー谷主演のファンタジック・コメディ。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("キャバレー", "キャバレー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "学生ジャズと訣別し、港町のキャバレーに身を置きながら本物のジャズを求める青年の彷徨を描いた、ハードボイルド・タッチの青春アクション。これが監督・製作の第３作になる角川春樹の起死回生の一発で、薬師丸ひろ子や渡辺典子らも顔を出す。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LOVES OF CARMEN", "カルメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "P・メリメの原作を再現した、サイレント期から数えて15度目の映画化作品。セビリアの竜騎兵ドン・ホセは、タバコ工場で問題を起こした女性カルメンの色香にほんろうされ、逮捕直前に彼女の逃亡を助けるが……。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL PRECIO DE UN HOMBRE", "ガンクレイジー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "さすらいの賞金稼ぎと凶悪犯の死闘が展開するマカロニ・ウエスタン。T・ミリアンが初めてこのジャンルに進出した作品でもある。連邦警察に逮捕されたならず者ゴメスが、幼なじみの女イーデン率いる一味に救われて逃走する。名うての賞金稼ぎのルークは、懸賞金目当てにゴメスの隠れ家を見つけ出す。地元の人々は冷酷非情なルークを恐れてゴメスに同情。しかしルークを監禁したゴメスが、思わぬ行動に出て……。", "1966-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("工業哀歌バレーボーイズ THE MOVIE", "工業哀歌バレーボーイズ THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/54087/315x210.jpg?3203", 82, "村田ひろゆきの同名人気コミックを映画化した、ヤンキーの青春物語。「クローズZERO」のやべきょうすけ、「あずみ」「逆境ナイン」の松本実ら、実年齢30歳以上の俳優がヤンキー高校生を熱演している。朝沼工業高校第2バレー部の3バカトリオの赤木、宮本、谷口は、彼女をつくることにしか興味がなく、いい加減な日々を送っていた。そんなある日、1番モテない谷口が中学の同級生だったのり子から告白され……。", "2008-11-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新選組鬼隊長", "新選組鬼隊長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "元治元年。新選組は池田屋を襲撃し、天下に名を馳せる。だが組織が巨大化するにつれ、庶民から殺りく集団だと恐れられるようになった。隊長の近藤勇は理想とかけ離れた現実に悩むが……。数々のエピソードで剣と涙に彩られた近藤勇の半生を描いた巨編。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHANGING　LANES", "チェンジング・レーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "気鋭の若手弁護士ギャビンが、出勤途中のハイウェイで接触事故を起こす。重要な法廷に急ぐギャビンは、事故相手ギプソンの示談を断り小切手を渡して立ち去る。だが、ギプソンも別居中の妻との裁判に急いでいた。置き去りにされたせいで裁判に遅れ、子供の親権を取られてしまったギプソンは怒りの矛先をギャビンに向け、ある行動に出る……。小さな偶然とすれ違いが、抜き差しならない事態に陥っていく様を、緊迫感たっぷりに活写するサスペンス。焦る主人公二人の感情描写とエピソードを畳み掛けるバランスが絶妙で、無駄がない。たった一日の出来事とは思えない濃密さで、手に汗握るスリルが味わえる快作。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("静かなる決闘", "静かなる決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "戦争中、当時不治の病といわれた性病“梅毒”を真摯に取り上げたヒューマンドラマ。野戦病院で手術中に梅毒に感染した青年軍医藤崎。彼は復員後も恋人の美佐緒を遠ざけ、誰にも打ち明けることなく一生を病と戦いながら、独身のまま生きる決意をする。軍医として生命を救った兵士から不治の病をうつされるという皮肉に絶望しつつも、医師としてひとり静かに人生と闘っていく姿を描いた良心作。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("In America", "イン・アメリカ　三つの小さな願いごと", "", "", "http://image.eiga.k-img.com/images/movie/1447/315x210.jpg?2474", 106, "幼い息子をなくし、悲しみに暮れるジョニーとサラは、2人の娘クリスティとアリエルを連れ、アイルランドからニューヨークへ移り住む。幼い姉妹は、貧しいながらも新天地の生活に楽しみを見出し、同じアパートの住人でアーティストのマテオとの出会いから、家族は再生の道を歩む。「マイ・レフトフット」「父の祈りを」のジム・シェリダンが、実の娘2人と共同で書いた半自伝的物語を映画化。劇中でクリスティとアリエルを演じるサラとエマは、実際の姉妹。", "2003-12-13", "33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jardins en Automne", "ここに幸あり", "", "", "http://image.eiga.k-img.com/images/movie/53097/315x210.jpg?4786", 121, "「素敵な歌と舟はゆく」「月曜日に乾杯！」の名匠オタール・イオセリアーニが“生きていることの尊さと幸せ”をユーモラスに描いた人間賛歌。大臣として多忙な毎日を送っていたバンサンは、ある日突然失脚し、仕事とお金を一気に失ってしまう。妻に愛想をつかされて住む家までなくした彼だったが、優しい女性たちとの出会いや古い仲間とのんびり過ごすことで少しずつ癒されていく。バンサンの母役で名優ミシェル・ピコリが出演。", "2007-12-01", "2,4,24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Brothers Grimm", "ブラザーズ・グリム", "", "", "http://image.eiga.k-img.com/images/movie/1027/315x210.jpg?1266", 117, "「未来世紀ブラジル」「バロン」のテリー・ギリアム監督が、グリム兄弟をモデルに、グリム童話に登場する赤ずきん、ヘンゼルとグレーテル、シンデレラ、ラプンツェルなど童話のモチーフを散りばめて描くファンタジー。グリム兄弟ウィルとジェイコブは、民話の怪物を退治するふりをして謝礼をもらう詐欺稼業をしていたが、ある日、本当の魔女に出会ってしまう。現実家の兄をマット・デイモン、夢想家の弟をヒース・レジャーが好演。", "2005-11-03", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Muse", "ハリウッド・ミューズ", "", "", "http://ecx.images-amazon.com/images/I/518X-hvvUFL._SL210_.jpg", 97, "映画の都ハリウッドを風刺したファンタジー・コメディ。不調の脚本家スティーブンの前に現われた創造の女神ミューズ。ヒット作の発想を得ようと、彼はミューズのご機嫌をとるが、贅沢好きの彼女に次々と無理難題を押しつけられ……。ジェームズ・キャメロン、マーティン・スコセッシらがミューズから発想を得た映画監督として実名で出演。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仮面ライダーV3対デストロン怪人", "仮面ライダーV3対デストロン怪人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 32, "子どもたちの間で大ブームを巻き起こした石ノ森章太郎原作による特撮ヒーロードラマ「仮面ライダーV3」の劇場用オリジナル作品。原子物理学者の沖田が、ウラニウムの数百倍の威力をもつサタンニウムを四国の山奥で発見。これを巡り、悪の秘密結社デストロンが放った怪人たちと仮面ライダーV3が激闘を繰りひろげる。さらに、ピンチに陥ったV3を助けるため、仮面ライダー1号＆2号もオーストラリアから駆けつける。", "1973-07-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大暴れマドロス野郎", "大暴れマドロス野郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "謎の死を遂げた父のガン研究の資料を持って、義母の所を飛び出た五郎。今はタグボートの船員をしている彼に、父の助手だった早瀬と、暗黒街のボス・陳が、研究資料を狙って魔手を伸ばしてきて……。和田浩治主演によるアクション映画。だが、彼が扮する五郎のヒーロー性が薄いため、出来は今一つ。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MALTA STORY", "マルタ島攻防戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "第二次大戦たけなわの頃、地中海の小島マルタを舞台に、この島を死守しようとするイギリス空軍と、航空写真偵察の名手である中尉たちとの死闘が、島の娘と中尉の恋も絡めて描かれる。イギリスが戦争映画に独自のスタイルを築いていた時代の作品の１本。", "1953-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PRICE OF POWER", "復讐のダラス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "G・ジェンマ、F・レイといった、ヨーロッパを代表する一流スターが顔を合わせたサスペンス・アクション。人種差別による偏見の強い街で、大統領暗殺事件が起こった。犯人の濡れ衣を着せられた黒人の友人を救うため、一人の男が真相究明の危険な行動を開始する。", "1969-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA FEMME DE ROSE HILL", "ローズヒルの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "文通結婚でスイスの雪にうもれた村に嫁いできた黒人女性ジュリー。しかし彼女は夫を愛することができず、やがて村の若者と密会をはじめる……。“白い風景の中にいる黒人女性”というイメージから映画を紡ぎ出した、とA・タネールは語っている。その静謐な映像が観る者の心をゆさぶる。", "1989-01-01", "2,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE REPLACEMENTS", "リプレイスメント", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "一度あきらめた夢に挑戦するチャンスをつかんだアメフト選手たちの姿を、笑いを散りばめて描くスポーツ・ドラマ。プロのアメフト・チーム、“ワシントン・センティネルズ”の主力選手たちが、シーズン終盤に入って年棒アップを求めてストに突入。名コーチ、ジミーはやむを得ず代理選手（リプレイスメント）たちをかき集める。学生時代の名クウォーター・バック、ファルコは、熱血ポリスマンや日本人力士、服役中の牧師らひとクセあるメンバーとともに、再び訪れたチャンスで人生の再起を図る。K・リーヴスが体重を10キロも増量、２ヵ月のトレーニングを積んでアメフト選手になりきった。好プレー、珍プレー続出の白熱の試合シーンも迫力満点。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神戸国際ギャング", "神戸国際ギャング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "高倉健と菅原文太の顔合わせによるギャング映画。終戦後の神戸は新興暴力団が横暴をきわめていた。団と大滝はこれに対抗すべく無法者を集めてギャング団を結成。激戦の末ギャング団が勝利を収めるが、ダイヤをめぐって団と大滝が対立する。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXECUTIVE DECISION", "エグゼクティブ・デシジョン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "テロリストがジャンボ機をハイジャックし、陸軍情報部顧問のグラント博士と、陸軍特殊部隊のトラヴィス中佐が招集された。テロリストの要求は組織のリーダーの釈放だったが、グラントは、毒ガスでワシントンを攻撃することが彼らの真の狙いだと指摘する。地上４千万人の命を危険にさらすか、乗客400人を犠牲にするか。政府が判断に苦しんでいる時、トラヴィスが奇想天外な作戦を提案。それは、実験中の空中輸送機を高度8000メートルの上空で、ジャンボ機にドッキングさせ、特殊部隊のメンバーを送り込むというものだった。作戦が実行されることになり、輸送機は無事、ドッキングに成功。メンバーは次々とジャンボ機に乗り移った。ところが、乱気流が発生。トラヴィスはジャンボ機を守るため、輸送機を切り離し、自ら爆発してしまう。果たしてグラントら残されたメンバーは無事に毒ガスを発見することができるのか？　無敵の男、セガールがあっけない最期を遂げたことでも話題を呼んだサスペンス・アクション。緻密に作られた脚本、密室のスリルを盛り上げる撮影、そして新人S・ベアードの演出は快調そのもの。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIDAY", "Friday", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ラップ・ミュージシャン、アイス・キューブが、主演・音楽はもとより、製作総指揮・脚本も手掛けた青春コメディ。失業中のクレイグとスモーキーは、毎日たわいない無駄話に興じている。そんな彼らの住む平和な町に、やっかい者のディーボやマリファナ仲介業者のビッグワームがトラブルを持ち込む。事件に巻き込まれたスモーキーを救うため、拳銃を手に立ち上がろうとするクレイグ。しかし、素手で戦えと父親に論された彼は……。ウエストコースト・ヒップホップや1970年代ソウルの名曲などI・キューブ自身が選曲したサウンドトラックも話題の一つ。ビデオ発売時タイトルは「フライデー」。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Unknown", "アンノウン", "", "", "http://image.eiga.k-img.com/images/movie/56037/315x210.jpg?4167", 113, "「96時間」のリーアム・ニーソン主演によるアクションスリラー。医学博士のマーティン・ハリス（ニーソン）は若い妻とともに学会に出席するためベルリンへと赴くが、宿泊するホテルに向かう途中で交通事故に遭う。病院で一人意識を取り戻したハリスだったが、妻にはその存在すら認識されず、見ず知らずの男が自分になりすましていたため、真相究明に乗り出すが……。監督は「エスター」のジャウム・コレット＝セラ。", "2011-05-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・タイガース ハーイ！ ロンドン", "ザ・タイガース ハーイ！ ロンドン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "超多忙の過密スケジュールに追いまわされるザ・タイガースの５人組の前に、“時間を売る男”という悪魔の化身が現れ、５人は魂を担保にすることを条件につかの間の休息をもらうのだが……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLER INSTINCT", "セクシャル・ファーム 殺しの代理人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "押しの弱さでいつも手柄を同僚にさらわれていた青年検事補ティムのオフィスに、女性研修生デボラが現れる。一目で心奪われた彼はいつになく積極的に彼女に迫り、オフィスで愛し合う。ある晩、泥酔したティムは彼女から叔父であり、事務所の実力者でもあるデューガン検事にレイプされたという話を聞き……。危険な愛の罠に陥る姿を描いたエロティック・サスペンス。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLLEGE", "キートンのカレッジ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "勉強はできるが、運動神経はまるでゼロのロナルドは、ガールフレンドのメアリーを追いかけカレッジに進学、彼女の目をひこうと運動部に入ろうとするが……。様々なスポーツに挑戦するキートンのドタバタぶりが楽しめる無声映画の一編。なお「キートンの船出」で沈没した“ダムファイト号”と同名のボートが出てきて、乗ろうとするとズボっと穴が開いてしまうのがおかしい。ビデオタイトルは「キートンの大学生」。", "1927-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("湘女／A　GIRL　FROM　HUNAN", "蕭蕭〈シャオシャオ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "20世紀はじめの封建的な中国の村を舞台に、少女の半生を叙情的な映像で描いた秀作。12歳のシャオシャオは、農家に嫁として売られ、わずか二歳の幼児の妻となる。彼女の暮らしは、野良仕事に精を出す苛酷な労働の日々。やがて18歳になったとき、彼女は同じ家で働く青年と初めての恋に落ち、その子供を身ごもってしまう……。", "1986-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Rain People", "雨のなかの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "コッポラの初期を代表する傑作ロード・ムービー。結婚して１年がたつのにナタリーは、結婚生活になじむことができず、置き手紙を残して家を出る。車で旅をする彼女は、女友達の家に向かう若者キルギャノンを車に乗せる。陽気な彼に心惹かれたナタリーはその夜、彼をベッドに誘うが、彼はフットボールの試合中のケガがもとで不能だった。二人はキルギャノンの女友達の家に着くが、女友達は彼を受け入れず、ナタリーは彼と旅を続けることにする。はじめは当惑していたナタリーだったが、やがて彼に特別な感情を抱くようになる。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボールZ　極限バトル！！　三大超サイヤ人", "ドラゴンボールZ　極限バトル！！　三大超サイヤ人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 46, "人気声優、草尾毅がシリーズ初登場の第10弾。孫悟空を殺そうと、超人まがいの人造人間を製造するマッド・サイエンティスト、ドクター・ゲロが登場。しかし人造人間に自ら殺されてしまうが、死後に残ったデータでパワーアップ。やがて悟空たちに襲いかかる。DVDは『DRAGON BOX THE MOVIES』に収録。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("集団左遷", "集団左遷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "“平成不況”を象徴した、辛口のサラリーマン映画。業界大手の太陽不動産は、バブル崩壊後、多量の売り残り物件と余剰人員をかかえて経営難に。横山副社長をはじめとする首脳陣は、大々的合理化作戦として、リストラ要員50名を“首都圏特販部”に集めるのだが……。サラリーマンとしての生き残りを懸けて会社と戦う、中年男の奮闘ぶりが見もの。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EAT THE RICH", "金持ちを喰いちぎれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ポスト・モンティ・パイソンとして注目されたイギリスの喜劇集団“コミック・ストリップ”。その彼らのブラックな笑いが本作でも炸裂。たまたま拾った拳銃で強盗をはたらき、マスコミの寵児となった貧乏人アレックスと、超タカ派の内務大臣の奇妙な闘争を描く。ポール・マッカートニーがゲスト出演している。", "1988-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREE WILLY ２", "フリー・ウィリー2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "少年ジェシーとオルカとの友情を描いた「フリー・ウィリー」の続編。ジェシーがウィリーを海に帰してから二年。ジェシーは太平洋北西部沖で、たくましく成長し、妹や弟を引き連れたウィリーと再会する。しかし、再会の喜びもつかの間、原油の流出事故が発生。ウィリーたちは危険な状況に追い込まれる。なんとか彼らを助け出そうとするジェシー。だが事態はさらに悪化する。流出した油に火がついてしまったのだ。ウィリーを救おうとジェシーは火の海へ飛び込むが……。前作同様、海洋写真家ボブ・タルボットの撮影したオルカの写真が、オープニングとエンディングで効果的に使われている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FUNHOUSE", "ファンハウス 惨劇の館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "アメリカのとある小さな田舎町。カーニバルの夜、恋人たちはデートを楽しみに会場にやって来た。彼らは呼びものの一つファンハウス＝恐怖の館に入るが、次々と起こる恐怖の出来事は実は本モノ……!? おばけ屋敷を思い出させるカラクリが楽しくもコワーいスプラッタ・ホラー。特殊メイクはリック・ベイカーが担当。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魂の風景・大野一雄の世界", "魂の風景・大野一雄の世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "1977年の「ラ・アルヘンティーナ頌」以来、世界をまたにかけた公演活動を続ける84歳の舞踏家・大野一雄。精神性の高い独特なスタイルで熱狂的なファンを多く持つ彼の舞踏に接することによってその魅力を追及し、記録したドキュメンタリー。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅場の人間学", "修羅場の人間学", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "昭和28年、ヤクザの格好良さに憧れて極道の世界に足を踏み入れた青年、平吉は失敗を重ねながらも、持ち前の明るさで難局を乗り切っていく。だが、組の殺しの泥を被ったことから彼は恋人を連れて警察から逃げ回るハメになり……。高嶋政伸の軽妙な演技も見ものの、コミカルなヤクザ映画。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Boheme", "ラ・ボエーム（2008）", "", "", "http://image.eiga.k-img.com/images/movie/54147/315x210.jpg?3398", 114, "1896年の初演以来、100年以上に渡って愛され続けるプッチーニの名作オペラ「ラ・ボエーム」を映画化。19世紀初頭のパリ。屋根裏部屋で芸術家仲間たちと気ままな生活を送っていた詩人ロドルフォは、階下に住むお針子ミミと運命的な恋に落ちる。つつましくも幸せな暮らしを始めた2人だったが、貧しさ故に不治の病で衰弱していくミミを救うことができないロドルフォは、彼女と別れる決意をする。数カ月後、2人は思わぬ再会を果たし……。", "2009-02-14", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE UNDERSTUDY：GRAVEYARD SHIFTII", "ドラキュラ 吸血鬼の寝室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "新進女優カミラは、ドラキュラ映画のヒロインに抜擢される。ところがドラキュラ役の俳優が行方不明に。代わって起用された男、ステファンは妖しい魅力を放っていた。それもそのはずステファンは、本物の吸血鬼。カミラはステファンの魔力に魅入られ、次第に妖しい世界へ没入していく……。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EDEN", "EDEN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("味", "味", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "文化大革命により、中国国内では廃れてしまった中国料理の源流といわれる魯菜＝山東料理。その伝承者として、中国山東省より“正宗魯菜伝人”の認定を受けた唯一の日本人女性、佐藤孟江とその夫・浩六の山東料理復興にかける活動を追うドキュメンタリー。山東料理の現状を憂う佐藤夫妻の70歳を超えているとは思えない行動力が大きな感動を呼ぶ。東京で中国料理店、済南賓館を切り盛りする佐藤夫妻。妻の孟江さんは、山東料理の秘伝の料理法を受け継いだ数少ない正宗魯菜伝人である。本場中国で廃れた魯菜＝山東料理の復興を目指すべく、彼女は夫とともに中国へ渡る意思を固めるが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どついたるねん", "どついたるねん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "“浪花のロッキー”と呼ばれた元プロボクサー、赤井英和が主演した本格的ボクシング映画。二度とリングに上がれないほどの傷を負ったボクサーが、復活を懸けてリングに立つまでを描く。赤井のガムシャラなキャラクターを生かした、阪本順治の演出は、爽快感を誘う。原田芳雄も好演。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THIS HAPPY BREED", "幸福なる種族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1919年、西部戦線から帰還したギボンズは妻エセル、男女３人の子供、エセルの母、そして自分の妹たちとともにロンドン郊外の住宅地に引っ越した。偶然にも隣家は戦友のミッチェル家。両家の交際から、ギボンズの次女とミッチェルの次男の間に恋が芽ばえ、やがて二人は結婚へ。しかしギボンズの一人息子リッジ夫妻が事故で死亡する……。イギリス中流家庭の20年間にわたる、悲喜こもごもの人生模様を、日常的視点から淡々と映し出した人間ドラマ。イギリスの名匠D・リーン監督の初期の作品の一つで、お得意の心理描写の巧みさや絶妙なカメラワークが光る秀作。", "1944-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BABY BLOOD", "ベイビー・ブラッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "アヴォリアッツ国際ファンタスティック映画祭で話題となったスプラッタ・ムービー。邪悪な意思を持った謎のクリーチャーが若くセクシーな女の子宮内に侵入し、勝手に胎児となって成長していく。胎児は母に血を要求し、母は路上で男を誘いその血をむさぼる。デヴィッド・クローネンバーグを彷彿させる作風が見どころ。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アフリカの光", "アフリカの光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "さいはての港町を舞台に、マグロ漁船に乗ってアフリカ行きを夢見る主人公の二人の若者と、土地に流れて定着しようとするヤクザたち、風土に縛り付けられて生活する漁師たちの三者をからめて描く、神代辰巳の文芸青春もの。萩原＝田中のコンビが熱演。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北帰行より・渡り鳥北へ帰る", "北帰行より・渡り鳥北へ帰る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "“渡り鳥”滝伸次は弟分を殺した敵（かたき）を求めて函館へやって来て、造船所乗っ取りをたくらむ悪玉と対決する。ライバルに宍戸錠の実弟・郷英治が扮したが、貫禄不足は否めなかった。この作品は事実上シリーズの最終作となった。DVDタイトルは「渡り鳥北へ帰る」。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プライド", "プライド", "", "", "http://image.eiga.k-img.com/images/movie/53587/315x210.jpg?4291", 126, "「有閑倶楽部」などで知られる人気漫画家、一条ゆかりの同名コミックを映画化。監督は「デスノート」シリーズの金子修介、主演に映画初出演の歌手ステファニーと、「デスノート」「愛のむきだし」の満島ゆかり。有名オペラ歌手の父を持ち、美貌と才能にも恵まれ裕福な暮らしを送っていた史緒だったが、父親の会社が突然倒産し路頭に迷ってしまう。一方、貧しい家庭で育った萌もオペラ歌手を夢見てバイトに明け暮れおり、2人はコンクールで激突することになるが……。", "2009-01-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若さま侍捕物帖 深夜の死美人", "若さま侍捕物帖 深夜の死美人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "大工の棟梁・政五郎、彼の娘・あおいと続いた殺人事件。そして骨董屋・金政の娘も魔の手によって殺害され……。事件解明に乗り出した若さまは、政五郎の祖先が東照宮の御宝殿建設に立ち会っていたことと、そこに宝物が隠されていることを知る。スリルとサスペンスに満ちた捕物時代劇。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("好色一代男", "好色一代男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "江戸元禄を背景に世之介の女性遍歴を描いた、井原西鶴の『好色一代男』の映画化。「刺青」「清作の妻」などで、どんな障害にも負けず自分の欲望を貫く女性像を描いた増村保造は、それを男性に置き換え戯作的に描いた。但馬屋の一人息子、世之介は父親の心配をよそに放蕩三昧。怒った父は世之介を江戸の出店に修行に出すが、そこでも世之介はしたい放題で、ついに勘当される。頭を丸めた世之介は、流転の末、私娼のヒモにまで成り下がっていく。やがて父が死に、但馬屋を引き継ぐやいなや金にまかせての女遊びの毎日で、お上に財産を没収されてしまう。世之介は好色丸に乗り、新世界に向けて船出するのだった。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("君も出世ができる", "君も出世ができる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "同じ会社に勤めるハッスル型のフランキー堺とノンビリ型の高島忠夫の出世競争を描くミュージカル・コメディ。ハッスル型とノンビリ型、果たしてどちらに軍配が？　日本版「努力しないで出世する方法」といえる。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("錦衣衛", "処刑剣 14 BLADES", "", "", "http://image.eiga.k-img.com/images/movie/55977/315x210.jpg?4622", 113, "14世紀後半から200年以上続いた明王朝を陰から支えた秘密警察組織「錦衣衛（きんいえい）」。その指揮官・青龍（チンロン）は、何者かの陰謀により部下たちから追われる身となってしまう。青龍は、錦衣衛の頂点に立つ者だけが持つことを許される14本の剣「大明十四刀」を武器に戦う。ドニー・イェン、ビッキー・チャオらが主演のアクション時代劇。監督は「ドラゴン・スクワッド」「三国志」のダニエル・リー。", "2011-05-28", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ОДИНОЧНОЕ ПЛАВАНИЕ", "デタッチド・ミッション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アメリカ軍とソ連軍の大規模な軍事演習が始まろうとしている太平洋上。米・ソの関係を悪化させようとたくらむ、アメリカの軍需産業を操る死の商人クラウンダーがいた。彼の部下は、独断でソ連にミサイルを打ち込もうとする。対するソ連は、その情報をキャッチし精鋭部隊を送った。ソ連版「ランボー」といったアクション大作。", "1987-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЛЕГЕНДА　О　СУРАМСКОЙ　КРЕПОСТИ／THE LEGEND OF THE SURAM FORTRESS", "スラム砦の伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ロッテルダム映画祭において、ヴィム・ヴェンダースやホウ・シャオシエンらと並び、“期待される世界の監督”10人に選ばれたパラジャーノフ監督。1984年に撮られたこの作品は、神話的な彩りに満ちている。トルコ軍の攻撃から祖国を守るために、グルジアの人々は辺境の地にスラム砦を築こうとするが、どうしてもトルコ軍の手に落ちてしまう。そこで予言者に占わせたところ、背が高く目の青い美しい若者を人柱として埋め込むことが必要だというお告げが下る……。一人の勇敢な青年の犠牲によって築かれた砦の伝説を、パラジャーノフ監督は、峻厳な美しさをたたえた映像によって描き上げている。", "1984-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LIMEY", "イギリスから来た男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "T・スタンプとP・フォンダという1960年代のイコン的スターが織り成すミステリー・ロマン。９年間の刑務所暮らしを終えて出所したウィルソンは、娘の事故死を知らせた一通の封書を受け取る。娘の死に疑問を感じた彼は、差出人の住所のロサンゼルスへと旅立つ。事故の背景には、巨大シンジケートによるヘロインの密売が絡んでいた……。「セックスと嘘とビデオテープ」のS・ソダーバーグによる1960年代へのオマージュ的作品で、テレンスが30年前に出演したケン・ローチ監督の長編デビュー作「夜空に星のあるように」を巧みにサンプリングしながら物語を展開させているのが特徴。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("インモラル　凍える死体", "インモラル　凍える死体", "", "", "http://image.eiga.k-img.com/images/movie/53727/315x210.jpg?4294", 87, "警視庁外苑署で、女性警官のみで構成される“性犯罪特別捜査班”が結成された。各課から寄せ集められたメンバーたちは慣れない仕事に戸惑うばかりだったが、ある日同署の管内で婦女暴行殺人事件が発生。捜査本部から全く当てにされず独自に捜査を開始した彼女たちは、少しずつ犯人に迫っていくが……。Vシネマ俳優として活躍する小沢仁志が“OZAWA”名義でメガホンを取った刑事ドラマ。主演は「ワンダフルライフ」の吉野紗香。", "2008-06-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヘルタースケルター", "ヘルタースケルター", "", "", "http://image.eiga.k-img.com/images/movie/57707/315x210.jpg?1709", 0, "第8回手塚治虫文化賞マンガ大賞を受賞した岡崎京子の同名コミックを、写真家・蜷川実花の監督第2作として実写映画化。主演は「クローズド・ノート」（2007）以来5年ぶりの銀幕復帰となる沢尻エリカ。究極の美貌とスタイルを武器に芸能界でトップスターとなったモデルのりりこだったが、その美貌はすべて全身整形で手に入れた作り物だった。そんな誰にも言えない秘密を抱えながらも、人々の羨望の的となり欲望渦巻く世界をひた走るりりこは、やがてある事件を巻き起こし……。", "2012-07-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Great Dictator", "チャップリンの独裁者", "", "", "http://ecx.images-amazon.com/images/I/517bUoLm1kL._SL210_.jpg", 125, "チャーリー・チャップリン初のトーキー作品で最高傑作とも言われる風刺コメディ。第1次大戦末期、トメニア国の兵士として戦線に出ていたユダヤ人の床屋チャーリーは、戦傷によりすべての記憶を失ってしまう。戦後、トメニアは独裁者ヒンケルが支配する国となりユダヤ人迫害を開始。そんな中、激変した状況を知らない床屋のチャーリーが退院し、ゲットーに帰ってくる……。チャップリンが、製作・監督・脚本・主演（2役）を務め、痛烈にヒトラーの独裁政治を批判した。", "1960-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("An Unmarried Woman", "結婚しない女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "　ニューヨークに暮らす夫婦エリカとマーティン。夫マーティンはウォール街の証券マン、エリカは画廊で働き、ふたりの間には15歳の娘パティもいた。ある時、エリカはマーティンから浮気していたことを告げられ、ふたりは離婚することに。その後、エリカは画家のソールと知り合い、愛しあう仲になるが……。ヒロインが離婚を経て、やがて自立するまでの姿を描いた物語。公開当時は女性映画ブームの代表作として注目された。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIU｀ LA TESTA", "夕陽のギャングたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 157, "1913年のメキシコ。陽気な山賊の首領がアイルランドから逃げて来たおたずね者と知り合い、銀行強盗を計画。だが、ひょんなことからメキシコ革命に巻き込まれ、政府軍と戦うはめになる……。「荒野の用心棒」「夕陽のガンマン」を経て、ついに到達したレオーネの新境地。文句なしの傑作。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どぶ鼠作戦", "どぶ鼠作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「地獄の饗宴」に続く岡本喜八監督の西部劇タッチの戦争アクション。日本軍の特命隊が、中国軍でいっぱいの敵陣を強行突破する。快作「独立愚連隊」の延長線上にある作品で、佐藤允以下の岡本喜八一家が総出演。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL BOSQUE ANIMADO", "にぎやかな森", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1943年にスペイン文学の巨匠W・F・フローレスが発表した小説を映画化。スペインのガルシア地方。迷信の多いこの地方にあるセセブレの森に、井戸掘り職人ヘラルドがいた。彼がひそかに想う娘エルメリンダは町へと去り、友人のマルビスは盗賊に変貌。彼の周りでは様々なことが起こる。そのうえ、幽霊騒ぎまでが飛び出して……。寓話的な物語と、のどかな自然描写が魅力的な作品。", "1988-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少年時代", "少年時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "戦局が悪化の一途をたどる昭和19年。東京から富山に縁故疎開することになった小学５年生・進二の田舎体験記。力で相手をねじ伏せるガキ大将、強きになびき弱きをいじめるデブッチョ、策略家の副委員長——少年たちの小社会が軍国・日本の気運を反映して、権力抗争の場になっていくさまを、カメラは厳しく見つめる。そんな時代の流れに逆らう軍国少年の矜持、純情、悲哀がにじみ出てくる演出が見事だ。柏原兵三の小説『長い道』を藤子不二雄Aが『少年時代』として漫画化。これを山田太一がシナリオ化する、という過程を経ている。井上陽水の主題歌もヒットした。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ascenseur pour I'Echafaud", "死刑台のエレベーター　ニュープリント版", "", "", "http://image.eiga.k-img.com/images/movie/55597/315x210.jpg?3705", 92, "ノエル・カレフの推理小説を、製作当時25歳だったルイ・マルが監督した傑作サスペンス。パリの土地開発会社の重役ジュリアンは、その会社の社長夫人フロランスと不倫関係にあった。情事の果て、2人は社長を自殺に見せかけて殺す完全犯罪を計画し、実行に移すが、犯行直後、会社のエレベーターが停電で止まり、ジュリアンが閉じ込められてしまう……。出演はジャンヌ・モロー、モーリス・ロネ、リノ・バンチュラ。音楽にマイルス・デイビス。", "2010-10-09", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("E　TORNATO　SABATA　HAI　CHUISO　UN　ALTRA　VOLTA", "西部決闘史", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「西部悪人伝」「大西部無頼列伝」に続く“サバタ”３部作の最終編で、第１作の主演俳優L・V・クリーフが再び主人公サバタに扮したマカロニ・ウエスタン。サーカス団の早撃ちショーのスターとして西部の町に現れたサバタが、町民の金貨をニセ札にすり替えようとしている有力者の悪事をかぎつけ、彼が差し向けた殺し屋と対決する。", "1972-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラ・エビラ・モスラ　南海の大決闘", "ゴジラ・エビラ・モスラ　南海の大決闘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "南海の孤島を舞台にゴジラとモスラが、世界征服を企てる秘密組織“赤い竹”の原爆工場を破壊し、海の王者エビラと対決する。モスラは最後にゲスト的にチラッと登場するだけで、小美人役もザ・ピーナッツからペア・バンビに代わった。", "1966-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京★ざんすっ", "東京★ざんすっ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "TV番組『つんくタウン』から生まれたオムニバス映画。イギリスの地下鉄をモチーフにした「チューブ・テイルズ」に発想を得て、“東京の乗り物”を題材にした７つの物語が並ぶ。親切すぎるバスのアナウンスにサラリーマンの怒りが爆発する「『優しさ』の国」、元オペラ歌手が迷い込んだ奇妙な世界「東京エスカレーター」、観覧車にまつわる切ないファンタジー「約束」、銭湯の古い体重計のキモチをコミカルに描く「東日暮里五丁目」、都会人の孤独と自由を映像でコラージュしたアーティスティックな作品「～らしい姿」、小学校の運動会、親の因縁から勃発したヤクザの息子と警察官の息子の代理戦争「ランニング・フリー」、老婆をめぐる老人４人の電動車椅子レース「マッハ★85」。タレント、俳優、カメラマンなど個性も感性もバラバラな監督たちだが、それぞれに味があり楽しめる。この成功を受けて、お金を題材にした「マネーざんす」も製作された。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Spirit of Jeet Kune Do", "マルチュク青春通り", "", "", "http://image.eiga.k-img.com/images/movie/1517/315x210.jpg?4778", 116, "TV「天国の階段」などで注目の新世代韓流スター、クォン・サンウが肉体派人気を決定的にした初主演作。78年、軍事政権下のソウル郊外マルチュク通り。男子校から転校してきた高校生ヒョンスは、仲間たちとツルんで他校とケンカしたり、初恋をしたりと青春を満喫。が、やがて教師たちの理不尽な教育が仲間たちを変えていき、ヒョンスはたったひとりで反乱を起こすことを決意する。詩人としても知られる監督ユ・ハの自伝的作品。", "2005-07-16", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("となり町戦争", "となり町戦争", "", "", "http://image.eiga.k-img.com/images/movie/33847/315x210.jpg?4817", 114, "第17回小説すばる新人賞を受賞した三崎亜記の同名小説を、江口洋介、原田知世共演で映画化。ある日突然始まったとなり町との戦争。旅行会社に勤める北原は町の広報誌でその事実を知るが、周囲に変わった様子はなくいつも通りの生活を続けていた。そんな彼の元に、町役場“戦争推進室”の女性・香西から辞令交付の知らせが届く。偵察業務に就くことになった北原は、現実感のないまま偵察を開始するが……。", "2007-02-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bukowski: Born into This", "ブコウスキー：オールドパンク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "20世紀最高の酔いどれ詩人として、カルト的な人気を集めた作家チャールズ・ブコウスキーの生涯を追ったドキュメンタリー。知られざる素顔を垣間見られる貴重な映像が満載。ショーン・ペンやU2のボノ、トム・ウェイツをはじめ、ブコウスキーと関わりの深い面々も登場し、熱い思いを語っている。安酒場に生息するダメ人間の心情を生々しい言葉で綴った天才作家ブコウスキーの生涯を、本人の映像や関係者の証言からたどっていく。", "2005-10-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOTORAMA", "モトラマ　５億ドルを狙え！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "10歳の少年ガスは、両親の不仲に嫌気がさし、盗んだムスタングで旅に出た。彼の目的は、ガソリンスタンドで給油するともらえるモトラマ・カードを集めて、５億ドル獲得することだった……。少年が行く先々で出会う不思議な体験を、乾いた映像で描いたロード・ムービー。監督のB・シルスはニューヨーク映画界の新鋭で、未成年の犯罪刑罰に関するドキュメンタリー「Doing Justice」などを製作・監督している。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("警察官", "警察官", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "老警官に救われた少年・喬助は自分も警察官となって働こうと決意する。喬助が一人前の警官として派出所に勤めだした頃、銀行を襲ったギャングに老警官は射たれてしまう。しかも犯人は中学時代の親友だった……。名匠・内田吐夢が、彼の得意の武士道ものに共通する使命と友情をテーマに描いた佳作。", "1933-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Raising Arizona", "赤ちゃん泥棒", "", "", "http://ecx.images-amazon.com/images/I/51XfD8LvzxL._SL210_.jpg", 94, "「ブラッド・シンプル」のジョエル＆イーサン・コーエン兄弟が、ニコラス・ケイジとホリー・ハンターを主演に迎えて手掛けたアクション・コメディ。元強盗の夫と元警察官の妻。子宝に恵まれず悩んでいた2人は、ある大富豪の家に5つ子が誕生したことをテレビで知り、そのうちの1人を奪って逃走する。赤ちゃん泥棒はひとまず成功したものの、夫の刑務所仲間や赤ちゃんを狙う賞金稼ぎが次々と現れて連鎖的に事件が起こり……。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONTINENTAL DIVIDE", "Oh！ベルーシ絶体絶命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "『シカゴ・サンタイムズ』の特ダネ記者が、ロッキー山中で絶滅寸前のワシの生態調査をするマスコミ嫌いな女性学者の取材を開始。大自然に感動し彼女に魅了された彼は、やがて恋敵に勝って彼女の愛を得る。ほのぼのとしたロマンティック・コメディ。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("To Die For", "誘う女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "アメリカで実際に起こった殺人事件を元に、鬼才ガス・バン・サント監督が手掛けたサスペンスドラマ。小さな地方局でお天気キャスターをつとめるスーザン・マレット。有名ニュースキャスターになるという野望を持つ彼女は、夢を叶えるためには夫の存在が邪魔だと考え、ある計画を思いつく……。目的達成のためなら手段を選ばない魔性の女スーザンをニコール・キッドマンが体当たりで演じ、ゴールデングローブ賞主演女優賞を受賞。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AK", "AK ドキュメント黒澤明", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "黒澤明監督による大作「乱」の撮影風景を収めたドキュメンタリー映画。「乱」の製作過程をつぶさに追い、監督の指示のもと、情熱を傾けて取り組むスタッフや出演者を捉えている。黒澤のイメージがいかにして具現化されるかを浮き彫りにしていく。", "1985-01-01", "76,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Gingerbread Man", "相続人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "　ジョージア州サバナ。敏腕弁護士のリックは、偏執的な父親のストーカー行為に悩まされている女性マロリーと知り合い、この父親ディクソンを病院に強制収容させる法的措置をとる。その後、親密な関係になるリックとマロリー。しかし、ふたりに対する脅迫事件が起こり、ついにはリックの子供たちが誘拐されるという事態に。やがて遺産相続をめぐる陰謀が浮かび上がり……。ベストセラー作家ジョン・グリシャムが映画のために書き下ろしたストーリーを映画化。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPANGLISH", "スパングリッシュ／太陽の国から来たママのこと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "「恋愛小説家」などで知られるアカデミー賞の常連監督ジェームズ・L・ブルックスが贈る、現代の家族像を描いたヒューマン・ドラマ。英語が喋れないメキシコ人のシングルマザーとL.A.の金持ち一家の交流と葛藤を、ユーモアを交えて綴る。コメディ俳優アダム・サンドラーのマジ演技もお見事。娘の未来を案じてメキシコからL.A.に移り住んだフロールは、有名シェフの家にお手伝いとして雇われるが、英語がまったく話せなくて……。", "2006-01-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONSIEUR", "ムッシュー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "パリの高層ビルにオフィスを持つ、自動車販売会社のサラリーマン、ムッシュー。ガールフレンドは浮気中だけど、彼女の両親とはなんとなく仲良く同居。でもそのまま住むわけにもいかないのでちょっとお洒落なアパートへ。そこでもずうずうしい隣人に振り回されるが、やっかいごとからスルリと逃げて、気の合う彼女とデート。フランスの若者たちの“自然体”を、主人公ムッシューの姿にオーバーラップさせたキュートな作品。監督は「浴室」の原作者として知られるJ・P・トゥーサン。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRESA Y CHOCOLATE", "苺とチョコレート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "「公園からの手紙」で知られる、ラテンアメリカを代表する鬼才T・G・アレアが手掛けた人間ドラマ。ゲイであるために祖国を追われる感性豊かな青年と、共産主義に傾倒する大学生。偶然の出会いから、やがて心を通わせていく彼らの友情を描きつつ、同性愛者への偏見という問題をリアルに浮き彫りにする。", "1993-01-01", "49,19,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TIE THAT BINDS", "闇を見つめる瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "幸せなクリフトン夫婦の唯一の悩みは自分たちの子供をもうけることができないことだった。そこで彼らは養子縁組センターに出かけ、６歳の可憐な少女ジェニーに出会う。二人はジェニーを迎えて新生活を始めるが、そこに彼女の実の両親リーンとジョンが現れる。彼らは殺人を平気で犯せるサイコな犯罪者で、娘を取り返すためにクリフトン夫婦を恐怖のどん底に突き落とす……。「ケープ・フィアー」「ウルフ」の脚本を手掛けたW・ストリックが監督デビューを飾ったサイコ・スリラー。D・ハンナが凶暴性を持つ異常な役どころに挑んでいる。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道釜ヶ崎に帰る", "極道釜ヶ崎に帰る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "終戦直後、島村清吉は復員して故郷の大阪・阿倍野に帰って来る。けれども、闇市は中華連盟の姜徳会と輩下の唐沢組が、わがもの顔に闊歩していた。島村は昔の仲間を集めて、次々に縄張りを荒らしていく。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("堤防小手／PICK POCKET", "ピックポケット！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "香港映画界のヒット・メーカー、サモ・ハン・キンポーが主演と監督の二役をこなすコメディ・タッチのカンフー活劇。キムを首領とするスリ一味のコーが、一目惚れした婦人警官のために、ある密輸団を一網打尽にすべく力を貸すはめになるが……。", "1981-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幻の湖", "幻の湖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 164, "滋賀県で愛犬とジョギングすることが生きがいの女性が、殺された犬の復讐を誓う縦糸と、戦国時代の結ばれなかった恋人同士の悲話の横糸が絡み合い、果ては宇宙空間にまで広がっていくスケールの大きい作品。失敗作とされたが、現在では熱烈なカルト支持を得ている。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Man Who Wasn't There", "バーバー", "", "", "http://image.eiga.k-img.com/images/movie/1277/315x210.jpg?3220", 116, "49年、北カリフォルニアの小さな町。義兄の経営する理髪店で働くエドは、店に立ち寄ったセールスマンから聞いた新事業＝ドライ・クリーニングに投資するため、妻の不倫相手に脅迫状を送ることを思いつく。しかし、この行為が、思わぬ事態を招いてしまう。コーエン兄弟の第9作目は、第1作と同ジャンル、田舎町での犯罪サスペンス。彼らにとって初のモノクロ映画でオスカー撮影賞にノミネート、LA批評家協会賞ほかで撮影賞を受賞。", "2002-04-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("妻の日の愛のかたみに", "妻の日の愛のかたみに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "リューマチを患い闘病生活を強いられた池上三重子の同名手記を木下惠介が脚色した純愛映画。見合い結婚で北原家に嫁いだ千枝子は、夫の正之と仲むつまじく暮らしていたが、突然病魔が彼女を襲った。正之は入院する千枝子に、１日に何通もの手紙を書き、健康な日の訪れを信じて待つのだが……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PETITE VOLEUSE", "小さな泥棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "F・トリュフォーが遺したオリジナル脚本をもとに、その弟子格にあたるC・ミレールが監督した“少女版「大人は判ってくれない」”ともいうべき作品。伯父さん夫婦に預けられて暮らしている16歳の少女ジャニーヌは、学校が終わるとハイヒールにワンピースという格好に着替えては街で盗みを働く毎日。しかしある日、洋品店の主人に見つかってしまった。家を飛び出したジャニーヌは住み込みのメイドを始める。知り会った中年男と恋をしたり、つかの間の幸福に浸った彼女だったが……。C・ゲンズブールが多感な少女ジャニーヌを演じている。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wallace & Gromit in The Curse of the Were-Rabbit", "ウォレスとグルミット　野菜畑で大ピンチ！", "", "", "http://image.eiga.k-img.com/images/movie/1737/315x210.jpg?4740", 85, "89年から続く英国製の人気クレイ・アニメシリーズが初めて劇場長編映画に。発明家のウォレスと愛犬グルミットのコンビが大スクリーンで活躍する。町中の人々が楽しみにしている巨大野菜コンテスト開催目前に、ウサギたちが野菜を食べてしまう事件が発生。しかも巨大なウサギが出現するらしい？　声の出演で「コープス・ブライド」のヘレナ・ボナム・カーター、「ハリー・ポッターと炎のゴブレット」のレイフ・ファインズが共演。", "2006-03-18", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMAZON", "孤高の勇者 アマゾン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「ヘルシンキ・ナポリ・オールナイトロング」以来、日本では紹介される機会のなかったカウリスマキ兄弟の兄、ミカの監督作品。交通事故で植物人間となった妻を安楽死させ、娘二人と逃げだしたカリ。一獲千金を夢みてアマゾンに来た彼らは、ベトナム帰還兵のダン、そして村の教師パオラと出会い、夢に向けて歩き始めるのだったが……。", "1991-01-01", "1,20,17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("King Corn", "キング・コーン　世界を作る魔法の一粒", "", "", "http://image.eiga.k-img.com/images/movie/54457/315x210.jpg?3389", 90, "大学生のイアンとカートは食物のことをもっと知ろうと、アイオワ州の農家に移り住み、農業を始めることに。まずはアメリカでもっとも生産量の多いトウモロコシを育てるため農作業に取りかかる2人だったが、遺伝子組み換えされた種子や強力な除草剤を使うことによって、農業初心者でも驚くほど簡単にトウモロコシを育てられるのだった……。食のルーツを探る旅を通じ、飽食の現在を鋭く批評するドキュメンタリー。", "2009-04-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("梁祝／THE LOVERS", "バタフライ・ラヴァーズ／梁祝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "身分の差が激しかった東晋の時代。名家の娘、祝は大学に通うため男装をしていた。その祝が、大学で貧しい青年梁と知り合い、友情を育てていく。ある日、梁は祝が女であることに気付き、それ以来二人は愛し合うようになる。しかし、祝は政略結婚を企てる父親に家に呼び戻され、部屋に閉じ込められてしまう。梁は叩きのめされ、叶わぬ恋と知り病に倒れる。そして、祝の結婚を知った梁は死んでしまうのだった。香港版「ロミオとジュリエット」ともいえる作品。ビデオ発売時タイトルは「永遠〈とわ〉の恋人たち　バタフライ・ラヴァーズ」。", "1994-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ヒロシマという名の少年", "ヒロシマという名の少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "広島に原爆が落とされてから38年後、一人の少年が１日だけの命を授かって誕生する。ヒロシマという名のその少年は母を探しに街へ出てある少女と出会う……。少年を過去、少女を現在に置き換え、原爆で亡くなった少年たちをひと目母親に会わせてあげたいという気持ちから作られた、自主製作の短編映画。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青少年／REBELS OF THE NEON GOD", "青春神話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "急激な高度成長でバブルに浮かれる台北の街。受験間近の予備校生シャオカンは、歓楽街で遊び回るアツーやアピンにほのかな羨望を抱いている。そんな彼がある日、ナジャ王子の生まれ変わりと占われたことから心機一転、夜の街にさまよい出るようになる。ナジャとは、仏教の神、四天王の一尊・毘沙門天の第三王子の名。ツァイ・ミンリャン監督は、反逆心の塊のようなナジャを現代のティーンエイジャーにだぶらせ、行き場のない鬱屈を抱えた彼らの多感で孤独なきらめきをリアルに描き出している。", "1992-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン　戦慄の楽譜（フルスコア）", "名探偵コナン　戦慄の楽譜（フルスコア）", "", "", "http://image.eiga.k-img.com/images/movie/53297/315x210.jpg?2154", 115, "原作コミックも大好調の本格推理アニメ「名探偵コナン」の劇場版第12弾。高名な元ピアニストが経営する音楽アカデミー出身の音楽家ばかりを狙った連続殺人事件が発生。コナンたちは、元ピアニストが新たにつくったコンサートホールのこけら落としに招かれる。何者かに命を狙われた女性歌手を間一髪で救い出すコナンたちだったが、今度はコナンたちが犯人に狙われることに。さらにコンサート本番中にホールで大爆発が起こり……。", "2008-04-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ブリスター！", "ブリスター！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "同棲相手にも愛想をつかされるほどフィギュア・コレクションにハマッている青年の物語と、彼が追う伝説のフィギュアの原作であるアメリカン・コミックの世界、そして近未来の砂漠を舞台に繰り広げられる謎の口ゲンカ。時空の異なるストーリーを交錯させながら展開する、ナイーブな青春映画。本作で映画初主演を果たした主演の伊藤英明が“おたく”のイメージを一新している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACROSS 110TH STREET", "110番街交差点", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "A・クインが、自ら総指揮を担当した社会派の刑事ドラマ。人種的な偏見を隠さない部長刑事マテリと若き黒人エリート警部ポープの対立を軸に、ニューヨークのハーレムで起こった30万ドル強盗殺人事件のてん末を描いている。ジャジーな音楽と過激な暴力描写も印象的。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWORDKILL", "SFソードキル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "仮死状態のまま凍っていた400年前の武士が、現代のロサンゼルスで蘇生した。暴力のはびこる街で、“サムライ”藤岡弘が悪を斬る。“サムライの道は死”など、日本文化が少々誤解されているが、テーマ性は十分。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぼくは勉強ができない", "ぼくは勉強ができない", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "高校２年の時田秀美は勉強はできないけれど、クラスの人気者で、年上の恋人、桃子がいた。秀美は世間の常識や学校という枠に収まりきれない気持ちを持ちながら、“ぼくはぼくである”ということを大事にしていた。ピュアな心を持った高校生の日常を描いた山田詠美の人気小説の映画化。３万人を超えるオーディションで選ばれた鳥羽潤が秀美を好演している。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("2 Become 1", "私の胸の思い出", "", "", "http://image.eiga.k-img.com/images/movie/34237/315x210.jpg?1602", 98, "香港の女性作家シー・シーが自らの体験を綴った小説を基に、乳がんに冒された女性の闘病生活を描いたヒューマンドラマ。「エレクション」のジョニー・トー監督がプロデュースを手掛ける。キャリアウーマンのビンゴは、医師から乳がんの診断を受けショックを受ける。民間療法を試したものの効果が表れず、手術にも踏み切れない彼女は次第に思い詰めるように。そんな時、乳がんを克服したある夫婦との出会いが彼女の考えを変えていく。", "2007-10-06", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COWBOY BEBOP　天国の扉", "COWBOY BEBOP　天国の扉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "TVシリーズとして放映後、60万本を超えるビデオ、DVD、LDの売り上げで絶大な人気を誇る『カウボーイビバップ』の劇場用アニメーション。広大な宇宙間に逃げ散った無法者や犯罪者を捕らえるため“賞金稼ぎ”の制度が導入された近未来。その賞金首を追って惑星間航行船ビパップ号に乗り、時空を超えて４人と一匹の犬が死闘を繰り広げるクールなスペース・アクション。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スワップ・スワップ　伝説のセックスクラブ", "スワップ・スワップ　伝説のセックスクラブ", "", "", "http://image.eiga.k-img.com/images/movie/54897/315x210.jpg?2033", 80, "不況や大停電などで治安の悪化が進んでいた1977年のニューヨークに、誰もが見知らぬ人とのセックスを楽しめるクラブ「プレイトーズ・リトリート」がオープン。それまで富裕層の娯楽だった「スワッピング」が一般市民に浸透するが、いつしかクラブは時代の波に飲まれていく。「プレイトーズ・リトリート」の繁栄と衰退、クラブのオーナーであるラリー・レビンソンの半生に迫るドキュメンタリー。", "2009-10-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE LIFT", "悪魔の密室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "舞台は超近代的な高層ビルに設置されたエレベーターの中。ある激しい雨の夜、そのエレベーターに乗り合わせた４人の男女が、突然呼吸困難に陥り全員死亡してしまう。数日後、さらに恐ろしい事件が起きて……。エレベーターがまるで生物のように勝手に動いて殺人を重ねるという発想が面白く、殺風景で寒々とした風景が、いっそう無気味さを引き立てている。アヴォリアッツ国際ファンタスティック映画祭グランプリを受賞。", "1983-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES UNS ET LES AUTRES", "愛と哀しみのボレロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 184, "ヌレエフ、ピアフ、カラヤン、グレン・ミラーらをモデルに、モスクワ、パリ、ベルリン、ニューヨークの４都市４家族の戦中戦後史を音楽とバレエで描く、ルルーシュの大作メロドラマ。振り付けはモーリス・ベジャール。ラスト17分のG・ドンのボレロは圧巻。", "1981-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LAUREL　CANYON", "しあわせの法則", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "若者たちの揺れ動く恋心をヴィヴィッドに捉えた恋愛劇。主人公は新米医師とその恋人である学者の卵。婚約して医師の実家に身を寄せ、新生活を始めた彼らは、奔放な音楽プロデューサーの母親とのかかわりによって運命を狂わされる。人間同士のぶつかり合いから起こる皮肉な科学反応をユーモラスかつリアルに描写。音楽面での絶妙な選曲センスも見事。地元の精神病院での勤務が決まったサムは、ともに医学部を卒業した婚約者のアレックスと実家に戻った。空き家のはずの家には、音楽プロデューサーの母と、バンドのメンバーがいて……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PASSION DE JEANNE D’ARC", "裁かるゝジャンヌ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "ロベール・ブレッソンも「ジャンヌ・ダルク裁判」の名で映画化した、伝説的な聖女ジャンヌ・ダルクの物語を、C・T・ドライエルが映画化したもの。裁判にかけられた彼女が一生獄中で生きながらえるのを潔しとせず、自ら進んで火刑に処せられていく姿を、崇高なタッチで描いている。誰もが知っている物語ゆえに、物語全体への興味よりも、ドライエルの素晴らしいカット構成技法によって感動がもたらされる作品。主演のM・ファルコネッティの表情も忘れがたい。", "1928-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WISHMASTER", "ウィッシュマスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "W・クレイヴンが製作総指揮を務めたホラー映画。美人宝石鑑定士と、永い眠りから目覚め彼女の周囲の人間を残虐に殺し回る魔物との戦いを描く。「エルム街の悪夢」でフレディを演じていたR・イングランドほか、「キャンディマン」のT・トッド、「13日の金曜日」でジェイソンを演じたK・ホッダーら、ホラー映画の個性派たちの出演シーンもお忘れなく。ビデオ、DVDタイトルは「ウェス・クレイヴンズ／ウィッシュマスター」。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE GRAND ESCOGRIFFE LE GRAND ESCOGRIFFE", "赤ちゃんは紳士がお好き?", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「真夜中の刑事」「恐怖の報酬」などで、渋い魅力を発揮してきたイヴ・モンタンが、なんと赤ちゃんを誘拐する犯人に扮した１本。赤ちゃんに手を焼きながらも愛しはじめ、ハプニングに振りまわされる男の表情をユーモラスに描いたコメディ。67歳でパパになった彼自身を重ねて観るのも面白い。赤ちゃんものの元祖的作品。", "1976-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("横川サスペンス", "横川サスペンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "日本初の国産バスを復元させて話題を集めた広島市西区横川町で制作された異色のサスペンス映画。バスの復元計画で盛りあがる横川町では、男たちが家を留守にしがちになっていた。そんなある日、いつものように学校から帰宅した魚屋の娘・理緒は、自宅の廊下が水びたしで、風呂場に包丁と破れた日記帳が散らばっているのを見つける。さらに、足が悪い母親も姿を消していた。時を同じくして、理緒の恋人・チリも行方不明となり……。", "2005-09-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("股旅", "股旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "この作品の前年、市川崑はTV『木枯し紋次郎』の演出・監修を手掛けているが、本編はある種のヒーローであった“紋次郎”とは違い、どん底にいながら成り上がろうとあえぐチンピラ渡世人３人の青春を描いたもの。とはいえ、片意地はっていても、空腹と貧乏を友とする旅がらすの生活をリアルに描いている点は“紋次郎”に通じるものがあろう。渡世で名を売ろうとする源太、信太、黙太郎は、父を斬り、惚れた女を売り飛ばすなど、およそ人の道からはずれた旅を続け、みじめな死を迎える。主演の萩原、小倉、尾藤の取り合わせが絶妙で、1970年代初頭のシラケ世代の若者像とダブリ、姑息なタイプのアウトローとして異彩を放っている。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROKEDOWN PALACE", "ブロークダウン・パレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "「告発の行方」などの監督J・カプランによる社会派ドラマ。無実の罪で投獄された少女たちの苦悩と闘いを描く。高校生のアリスと親友のダーリーンが、夏休みにタイのバンコクへ旅行に行く。エキゾチックな街の雰囲気と開放感に包まれて浮き足立つ二人は、偶然知り合ったオーストラリア人男性に誘われて香港へ足をのばすことにする。だが、空港でアリスのかばんから麻薬が発見され、二人は身に覚えのない罪で投獄されてしまう……。通訳を介さない一方的な取り調べにも気丈な態度を崩さないアリスをC・デインズが熱演。少女たちを救おうとする弁護士にB・プルマンが扮している。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さらば相棒", "さらば相棒", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "ロック界のスーパースターになる日を夢見て、地方から上京してきた３人。現実は厳しかったが、そんな彼らにもレコーディングのチャンスが訪れる。だが、歓喜する彼らの陰で、悪の顔が見え隠れして……。若いロッカーたちの夢と挫折を、ロックシンガーの宇崎竜童が情感たっぷりに描く青春メロドラマ。", "1982-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("沙羅双樹", "沙羅双樹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「火垂〈ほたる〉」の河瀬直美監督が、再び故郷の奈良を舞台に作り上げた人間ドラマ。悲運に見舞われた一組の家族を通して、現代人が失いつつある真の心の豊かさや生きる歓びを見つめる。伝統ある奈良の町で代々墨職人を受け継ぐ麻生家の長男・圭が神隠しにあったかのように行方不明に。５年後、両親の卓と礼子、弟の俊のもとに圭の訃報が届く。その直後に臨月を据えていた礼子が無事出産。家族は新たな一歩を踏み出す。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最高殊勲夫人", "最高殊勲夫人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "三原家の兄弟と野々村家の姉妹はそれぞれ結婚し、三原商事の営業部長と秘書のニ組のカップルとしてうまくいっていた。両家は、残る三男の三郎と三女の杏子を結ばせようと画策するが、若い二人はこれに反発。だがいつの間にかお互いに惹かれ合って……。女性映画で名高い増村保造監督の青春ドラマの佳作。若尾と川口のみずみずしい存在感が魅力。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人斬り銀次", "人斬り銀次", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "現在と過去を交錯させながら、特攻兵の生き残りである男が歩んだ壮絶な人生を描く。主人公の若き日を演じた竹内力の斬り込みと、50年後に扮した夏八木勲の男気あふれる演技は見もの。終戦後の日本で、愚連隊をなぎ倒し“人斬り銀次”と呼ばれ、恐れられた男がいた。収監から50年後、79歳の銀次は釈放されるが、彼が戦時下のある事件の真相を握ることから命を狙われ……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’APE REGINA", "女王蜂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "若く健康で美しい女レジナを紹介され、彼女と結婚した40代のセールスマン、アルフォンソ。新婚生活は幸せそのものだったが、時がたつにつれ、彼女は子供を欲しがるようになる。ところがいくら頑張ってもレジナはなかなか妊娠しない。そうこうするうちにアルフォンソはレジナの要求が苦痛になり、とうとう病気になって海岸の村に保養にやられるが、レジナはそこにも現れて……。", "1963-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE AMICHE", "女ともだち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "M・アントニオーニが初期に監督した作品。偶然知り合った４人の女が、恋愛・仕事・芸術をめぐって様々な立場からわかり合う。アントニオーニとしては異色の、ストーリー主体の作品。自分本位の生き方が展開されるこの作品の中にも、後に見られるような人間不信の中の愛の冷たさが漂っている。", "1955-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Strada", "道", "", "", "http://ecx.images-amazon.com/images/I/51a5EzOEQCL._SL210_.jpg", 107, "サーカスの芸人の世界に憧れるフェリーニが、大道芸人のわびしい浮草生活を描いた名作中の名作。粗野な大道芸人のザンパノは、ジェルソミーナという少し頭の弱い娘を雇って旅から旅の生活を送る。ジェルソミーナは、ザンパノから家畜のような扱いを受けるが、“キ印”と呼ばれる綱渡りの青年から心の慰めを得る。だが、“キ印”と犬猿の仲のザンパノはふとしたはずみで“キ印”を殺してしまう。それ以来、ジェルソミーナは、ただ泣いているばかりで役に立たなくなり、とうとう彼女は置き去りにされてしまう。数年後、ジェルソミーナの死を知り、夜の浜辺でけだもののように泣き続けるザンパノ。ロータの哀切なメロディーはあまりに有名である。最も感動的なフェリーニ作品として知られ、ヴェネチア映画祭ではサンマルコ銀獅子賞に輝いた。", "1954-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("乾いた花", "乾いた花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "石原慎太郎の同名小説を、篠田正浩監督が池部良主演で映画化したニヒリズム映画の秀作。３年ぶりに出所したヤクザ・村木は、賭場で見事な手さばきをみせる冴子という娘に心惹かれる。しかし彼女には葉という、殺しと麻薬だけに生きている死神のような男が影のようによりそっていた……。この作品は公開までに幾多の問題が生じた。画面からにじみ出るあまりに無機的なニヒリズムから会社側に難解映画とみなされ８ヵ月オクラ（正式公開は1964年３月）になり、続いて映倫が反社会的というレッテルをはり成人映画に指定した。しかし皮肉なことに公開されるや大ヒットを記録。また池部良が初めて着流しスタイルで登場し、新生面を開いた。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Magnolia", "マグノリア", "", "", "http://image.eiga.k-img.com/images/movie/1857/315x210.jpg?3055", 187, "死の床で息絶えんとするテレビの大物プロデューサー、彼が昔捨てた息子、プロデューサーの若い妻、看護人、癌を宣告されたテレビのクイズ番組の司会者、彼を憎む娘、彼女に一目惚れする警官、番組でおなじみの天才少年、かつての天才少年……。ロサンゼルス、マグノリア・ストリート周辺に住む、一見何の繋がりもない12人が、不思議な糸に操られて大きな一つの物語に結び付けられていく。そして……“それ”は、起こる！", "2000-02-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("病葉（わくらば）流れて", "病葉（わくらば）流れて", "", "", "http://ecx.images-amazon.com/images/I/51pSlJGNT3L._SL210_.jpg", 0, "ハードボイルド作家・白川道がギャンブルに溺れる青年の青春をリアルにつづった同名小説を、「心中エレジー」の亀井亨監督が映画化。学園紛争が盛りあがりを見せていた時代。大学生の梨田雅之は同じ学校に通う4浪2留の永田一成と出会い、その虚無的な雰囲気に興味を抱く。永田の影響で博打麻雀の世界に足を踏み入れた雅之は、それまで経験したことのない刺激に心を奪われていく。そんな中、雅之は喫茶店で働くテコと恋に落ち……。", "2008-02-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("F.I.S.T.", "フィスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 143, "1937年オハイオ州。長距離トラックの運転手ジョニーは、会社への待遇改善要求をきっかけにFIST（全米長距離トラック輸送組合）に入り、18年後、ついにユニオン・リーダーの座につく。若者の権力への果敢なる挑戦と名士への黒い野望を、スタローン独特のキャラクターで見せる社会派劇。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハイハイ３人娘", "ハイハイ３人娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ゴージャスな歌謡ショー・ビズ映画。冒頭の３人娘とスリーファンキーズのアカペラの『ハイそれまでよ』も決まってるが、中尾ミエが『６時になると』の歌に入っていくミュージカル感覚が素晴らしい。歌って踊って騒げる楽しいお祭り映画になった。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("秘密", "秘密", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "東野圭吾のベストセラー小説を映画化したファンタジー。満ち足りた家庭生活を送っていた杉田は、スキーバスの事故によって妻に先立たれ、娘も意識不明の重体となり、失意の底に。ところが、目覚めた娘は、なんと妻の人格を宿していた。かくして彼は17歳の愛娘と奇妙な“夫婦生活”を始める……。笑いと涙の要素に加えて、ラストに“秘密”をふくんだユニークな設定の物語が展開。中年女性の心を持つ、あどけないヒロインに扮した広末涼子の妙演も評判となった。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("浮草物語", "浮草物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "池田忠雄＝小津安二郎の名コンビによる人情ものの傑作。ドサ回りの一座の座長・喜八は、昔の女がいる田舎町に興行に行く。女には、喜八の子供がいて、立派に成長し、喜八をおじさんだと思い込まされている。一座の看板女優で、喜八の情婦でもある女は、昔の喜八の女に嫉妬して、妹分の女優に喜八の息子を誘惑するようにしむける。騒動が起こり、息子はおじさんだと思っていた喜八が実の父だと知ってしまう……。坂本武を主人公にしたいわゆる“喜八”ものの一作で、旅一座の哀感と、人々の細やかな心情とを小津安二郎ならではの淡々とした空間に描いた。1959年に小津自身が自ら「浮草」という題名でリメイクしている。", "1934-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長良川ド根性", "長良川ド根性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-12-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("闇の影法師", "闇の影法師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "佐幕派が支配する城下、勤王派に味方し、夜な夜な出没する白覆面。稲垣浩と阪東妻三郎がコンビを組んだ第１作。阪東が昼はしがないタバコ売り、夜は敵をバッタバッタと切りまくる怪傑影法師という神出鬼没の正義の味方を、さっそうと演じている。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅がゆく４ 東京大戦争", "修羅がゆく４ 東京大戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "人気劇画を映画化した極道アクション・シリーズの第４作。新宿、歌舞伎町に場外馬券場の建設が決定し、その利権をめぐって暴力団抗争が勃発。静観していた本郷も思わぬ事故から、この争いに巻き込まれる。一方、関西に拠点を構える旧敵、伊能は、これに乗じて関東進出を図ろうと画策し……。前作まで監督を務めていた和泉聖治に代わり、「餓狼伝」の佐々木正人がメガホンを取っている。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Last tango in Paris", "ラストタンゴ・イン・パリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "　ある冬の日、アパートを探すジャンヌは、空き室で中年のアメリカ人と出会う。たいした会話も交わさぬまま、突然彼に陵辱されるが、その後もたびたび彼のアパートへ足を運んでしまう。名前のないただの男と女として、セックスにふける2人。人生に絶望した男をマーロン・ブランドが熱演。過激な性描写にセンセーションが巻き起こり、ベルナルド・ベルトルッチの名も広く知られるようになった。", "1973-06-23", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("余命", "余命", "", "", "http://image.eiga.k-img.com/images/movie/54067/315x210.jpg?3285", 131, "「海猫」などで知られる谷村志穂の同名小説を、「手紙」の生野慈朗が映画化。主演は「フラガール」「デトロイト・メタル・シティ」の松雪泰子、その夫役に椎名桔平。大学病院で働く38歳の外科医・滴は、結婚10年目にして夫との間に待望の子供を授かる。10年前に患った乳がんで右胸を摘出している滴にとって妊娠は最高に幸せなニュースだったのだが、右胸に異変が表れ、検査を行うと乳がんの再発が明らかになる。", "2009-02-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　勇気の花がひらくとき", "それいけ！アンパンマン　勇気の花がひらくとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "“本当の勇気”をテーマにアンパンマンとヤンチャなキララ姫のスリリングな冒険談。キララ姫の声を雛形あきこが担当している。勇気ある者が手にしたときのみ力を発揮するスタースティックが使えないキララ姫は、ショックのあまり星を脱出、本当の勇気を探す旅に出る。彼女は危ないところを救ってくれたアンパンマンに連れられてアンパンマンワールドに向かうが、そこに最新ロボット・ジャイアントモグリンが現れ……。", "1999-07-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誘拐", "誘拐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "ロス市警帰りの藤一郎は、ベテラン警部の津波浩とコンビを組むことになる。その矢先、誘拐事件が発生。FBI仕込みのプロファイリングで犯人像に迫ろうとする藤。一方、ガンに冒されていることを隠す津波も執念を燃やし、残り少ない命をこの事件に懸けていた。その後、犯人グループは、身代金３億円と受け渡しをテレビ中継することを要求、指定された運搬役は心筋梗塞で倒れてしまう。次に指定された運搬役も力尽きてしまう。その時、「これは公開殺人だ」とつぶやいた津波が３億円を持って山根の代わりに走りだす。一体、犯人の真の狙いは？　渡哲也、永瀬正敏の共演によるヒューマン・サスペンス。城戸賞を受賞した森下直の脚本を、新“ゴジラ”シリーズを手掛けてきた大河原孝夫が映像化。誘拐事件とその裏に隠された企業の巨悪を、二転三転のストーリー展開の中に描き出す。身代金受け渡しのシーンでは、日本映画撮影監督協会の応援によって21台のカメラが同時撮影を行い、史上空前の東京ロケーション撮影が敢行された。結末のサプライズは凄い。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACE VENTURA: PET DETECTIVE", "エース・ベンチュラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "NFLマイアミ・ドルフィンズのマスコットのイルカが誘拐された。オーナーは世界でただ一人の“ペット専門探偵”エース・ベンチュラに捜査を依頼する。エースは独特のヒラメキを駆使して事件に迫るが……。アニメもまっ青の百面相を連発するJ・キャリーの芸達者ぶりに爆笑必至のアクション・コメディ。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血と砂", "血と砂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "三船敏郎と岡本喜八監督コンビによる時代劇「侍」に続く戦記もの。昭和20年の北支戦線を舞台に、三船演じる曹長と13人の少年軍楽隊員と慰安婦の交流、そして少年たちの壮絶な戦いが描かれる。伊藤桂一原作の『悲しき戦記』が物語の骨子となっている。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Near Dark", "ニア・ダーク／月夜の出来事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "吸血鬼の少女に恋をした為に自らも吸血鬼となった青年の苦悩と闘いを描くバンパイア・アクション。ある日路上で出会った少女メイに恋をしたケレブ。実は彼女は吸血鬼で、そうとは知らずにキスをし、噛まれてしまった彼も吸血鬼となる。ケレブは他の吸血鬼たちと行動を共にするようになるが、彼を探しにきた父と妹が仲間たちに襲われそうになり……。メガホンをとるのは、単独では本作がデビュー作となる女性監督キャスリン・ビグロー。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("活着", "活きる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "　海外の映画祭で輝かしい実績を持つチャン・イーモウ監督が、時代に翻弄される家族の姿を描く。脚本は同タイトルの原作も手掛けたユイ・ホア。博打で家財を失ったフークイは、貧しいながらも家族と平穏に暮らす。生業である影絵芝居の巡業中、内戦に巻き込まれやっとの思いで家へ戻ると、母は亡く、娘は口がきけなくなっていた。原作の悲劇的なエンディングとは違い、救いのあるラストで幕を閉じる。カンヌ映画祭審査員大賞、主演男優賞受賞。", "1994-01-01", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OFFICE KILLER", "オフィスキラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "エキセントリックな変装趣味によるセルフ・ポートレートで名高いアーティスト、C・シャーマンの監督デビュー作。主人公は古めかしい出版社で働くさえないOL、ドリーン。ある夜、オフィスで上司を感電死させてしまったことをきっかけに殺人の快楽に目覚め、自宅の地下室で人体パーツのコレクションを始めた彼女の奇行を描き出す。狂気や悪意とは一見無縁に思える中年ヒロインが、連続殺人を犯すという意表を突いた設定。いたずらにアーティスティックな表現に走らず、のっそりしたテンポの映像世界に奇妙なリアリティーを刻み込んだシャーマンの演出が光る。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("O CONVENT", "メフィストの誘い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「アブラハム渓谷」ではフローベールに材を得た巨匠オリヴェイラが、今度はゲーテの『ファウスト』を下敷にした特異な物語に挑戦。シェイクスピアの出生の秘密を追う文学教授が妻を連れて修道院を訪れる。教授は書庫を管理する美女に惹かれるが、嫉妬した妻は自分に気があるらしい管理人を使ってその美女を亡き者にしようとする……。様々な思惑が絡み合う様が官能的。", "1995-01-01", "2,25", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ここから始まる", "ここから始まる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "クレージー・キャッツものを多く手掛けた坪島監督のホームドラマ。小学校の教師・都は四人姉妹の末娘。母親は“結婚こそ女の幸せ”と彼女をせかすが、“結婚なんて人生の墓場”と都は取り合わない。失敗例は姉たちを通してイヤというほど見ているからだ。そこで母親はカンフル剤に、老紳士との再婚を宣言するが……。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRIEVOUS BODILY HARM", "ボディ・クロス 殺しは危険な香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "瀕死のギャングから託された大金のほとんどを我が物とした抜け目のない事件記者が、その抜け目のなさゆえに殺人鬼や金目当ての上司につけ狙われていく恐怖を描くサスペンス・スリラー。C・フリールズをはじめ、オーストラリア映画界の異色俳優たちが総出演している。", "1988-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SANCTUS", "サンクタス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "現代アメリカを代表するレズビアン・アーティスト、B・ハマー監督が、生と死をユーモラスな視点で描いた瞑想的な作品。「アッシャー一家の崩壊」(1929)のジェームズ・シブレイ・ワトソン博士とそのチームが撮影した人体のX線写真を素材に、Xフィルムの骸骨たちが、変容、増殖し、色と光を得ていく様子を描く。なお、この作品はビデオ「ナイトレイト・キス」に収録されている。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢千代日記", "夢千代日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "山陰の温泉町。芸者置屋“はる屋”の夢千代は、病院で自らの死期が迫っていることを知らされる。その帰り、列車の窓から一人の女の投身自殺を目撃。ところが一緒に目撃した男は……。死期迫る薄幸の女の生きる姿を、これが遺作となった浦山桐郎が、見事に演出している。女性映画の傑作。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スキ！", "スキ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「居酒屋ゆうれい」の渡邊孝好が監督した都会のラブ・ストーリー。ドツボの人生を歩んできた女の子、双葉。彼女は占い師から、“13日の金曜日の午後６時６分６秒から数えて100人目の男をつかまえれば幸福になれる”と言われる。それにピッタリはまった男が氷介。目下、社長令嬢との逆玉も決まってウキウキだったのだが、双葉に言い寄られて……。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛と平成の色男", "愛と平成の色男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "昼間はエリート歯科医、夜はジャズ・サックスプレイヤーというプレイボーイの独身男と、彼をめぐる５人の美女の関係を、恋愛マニュアル風に描くラブコメディ。石田純一の本格的初主演作。", "1989-07-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEADLY SINS", "ポイズン・ボディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "敏腕刑事と美人シスターが活躍するエロティック・サスペンス。都会から離れた島にある女子修道院の付属学校。都会の子女が学ぶ神聖な女学校と思われていたその学校の女子学生たちは、実は夜になると、町の男をたらし込み、公園でセックスの狂宴を繰り広げていた。そんなある日、連続殺人事件が発生。女子学生が餌食となる。しかも、殺害テクニックは残酷極まりない。敏腕刑事がやって来て、美人シスターの助けを借りての捜査が開始された。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Spiderwick Chronicles", "スパイダーウィックの謎", "", "", "http://image.eiga.k-img.com/images/movie/53387/315x210.jpg?2323", 96, "ホリー・ブラックのファンタジー小説「スパイダーウィック家の謎」を映画化。「チャーリーとチョコレート工場」「ネバーランド」のフレディ・ハイモアが、1人2人役で双子の主人公を演じる。両親の不仲のため、田舎の森にある古い屋敷に引越してきたジャレッドは、屋根裏部屋で先祖のアーサーが妖精たちの生態を記した本を発見。しかし、邪悪で凶暴な妖精マルガラスが、その本に記されている秘密を狙っていた……。", "2008-04-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("棒たおし！", "棒たおし！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "新人脚本家の登竜門、城戸賞を受賞したシナリオに基づく青春映画。ヒップホップ系グループ、Leadのメンバーが主役格の若者たちに扮し、沸き起こる情熱を生き生きと体現している。退屈な高校生活を過ごす次雄。そんな彼に、体育祭の棒倒しの選手にと、級友の勇が声をかけてきた。最初は全く興味を示さなかった次雄だが、ある事件がきっかけとなり、やる気に火がつき……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST DRAGON", "ラスト・ドラゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ブルース・リーを敬愛し、ニューヨークでカンフー修行を続ける黒人青年が、ビデオ産業制覇をたくらむ組織から、ディスコ・クィーンを救出する、というストーリー。スティーヴィー・ワンダーをはじめ、ビッグ・アーティストのヒット曲が全編にわたって流れる。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAW AND JAKE WADE", "ゴーストタウンの決斗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ニューメキシコの町で保安官になった元悪党のジェイクは、昔の仲間クリントを脱獄させてやる。昔盗んだ大金の場所を聞き出すためクリントはジェイクを連れだすのだが……。ラストのR・テイラーとR・ウィドマークの対決が見もの。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hannah Montana: The Movie", "ハンナ・モンタナ　ザ・ムービー", "", "", "http://image.eiga.k-img.com/images/movie/54577/315x210.jpg?4213", 102, "アメリカの人気TVシリーズ「シークレット・アイドル　ハンナ・モンタナ」の劇場用映画。スーパーアイドルのハンナ・モンタナとして仕事をする一方で、普段は普通の高校生として暮らすマイリーだったが、アイドルでいることと自分自身でいることのバランスを見失い、人生を見つめ直すため、生まれ育ったナッシュビルに帰郷する……。監督は「セレンディピティ」「Shall We Dance？」のピーター・チェルソム。", "2010-02-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLI OCCHIALI D’ORO", "フェラーラ物語「金縁の眼鏡」より", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "現代イタリア文学を代表する作家G・バッサーニの小説『金縁の眼鏡』の映画化。P・ノワレが、ユダヤ人の迫害の恐怖に怯え、破滅を望むかのようにハンサムな青年に溺れていく中年男を見事に演じている。第二次大戦前夜、北部イタリアの都市フェラーラ。街には早くもユダヤ人迫害の脅威が迫っている。そのなかで、50がらみの教養ある医師ファディガーティの同性愛関係は人々の知るところとなり、孤独に陥る。一方大学生ダビデは、恋人ノーラの愛を失いながらも不穏な時代に負けずに毅然と生きる。", "1987-01-01", "4,2,34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRISTANA", "哀しみのトリスターナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ブニュエルが生涯で作った3本のスペイン映画の最後の作品。文豪ガルドスの原作の舞台を今世紀のトレドに移し、主演を熱望したドヌーブが、無垢で隷属するしか知らぬ孤児の少女が愛を自由に操るに至る恐るべき逆転を旋律的な美しさで体現した傑作。みなしごトリスターナは、名付親ドン・ロペの養女としてひきとられた。日々美しくなる娘を溺愛するようになるドン・ロペだったが、彼女は外に愛人を作って村を出てしまう。2年後、重病を患ったトリスターナがトレドに戻った……。", "1971-01-31", "45,2,9,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前女将", "喜劇・駅前女将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "両国駅前の酒屋“吉良屋”の主人・徳之助と柳橋の“孫寿子”の主人・孫作は義理の兄弟で仲が良い。だが徳之助が、バーのマダムにダブルベッドを買ってやったことから騒動がもち上がる。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Spasi I Sokhrani", "ボヴァリー夫人（1989・2009）", "", "", "http://image.eiga.k-img.com/images/movie/54877/315x210.jpg?4466", 128, "フランス文学の最高峰とされるギュスターブ・フローベールの同名原作を、「エルミタージュ幻想」「太陽」の巨匠アレクサンドル・ソクーロフが映画化。フランスの小さな田舎町トスト。厳格な修道院で育ったエマは、夫人という肩書きに憧れ年の離れた町医者のシャルル・ボヴァリーと結婚する。しかし、エマは平凡な夫との単調な田舎生活に退屈を感じ、徐々に生気を失っていく。そんなエマを心配したシャルルは、違う町での開業を決意するが……。", "2009-10-03", "24", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNE PARTIE DE CAMPAGNE", "ピクニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "巨匠ルノワールが、モーパッサンの小説『野あそび』を繊細な筆致で映画化した珠玉の傑作中編。ある日、パリから郊外にピクニックにやって来た家族。男たちがうたた寝している間に、一家の妻と娘はそこで出会った青年たちに誘われ、ボート遊びに興じていく……。突然の雨や風がアクセントを添える川辺の自然美を捉えた絵画的映像の中に、娘と青年の忘れがたい出会いと別れのドラマを描出。当初編集したフィルムは戦時中にドイツ軍に没収されたが、撮影から十年を経た戦後、本作の助監督を務めたジャック・ベッケルがルノワールの脚本にしたがって再編集し完成させた。日本では1977年に公開された。", "1936-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DESCENTE AUX ENFERS", "デサント・オ・ザンファー 地獄に堕ちて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "美しい若妻ローラを連れて、ハイチ島にやって来た作家のアランだったが、ローラの心は彼から離れていくばかり。ローラは心に閉ざした秘密があったのだ。いらつくアランは、ある晩財布を盗もうとした若者を殺してしまう。二人の愛の行方は……。21歳のS・マルソーが、「狂気の愛」に続くこの作品でも南の島ハイチを背景に美しい肢体を披露している。ビデオ、DVDタイトルは「地獄に堕ちて」。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歌謡曲だよ、人生は", "歌謡曲だよ、人生は", "", "", "http://image.eiga.k-img.com/images/movie/1807/315x210.jpg?2483", 130, "", "2007-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMMITTED", "ノンストップ・ガール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "ライブハウスを切り盛りするジョリーンは仕事も結婚生活も順調のはずだったが、結婚式から２年も経たないうちに夫カールが置手紙を残して家出。唖然とするジョリーンだったが、カールから届いた一枚の葉書を頼りに、アメリカ縦断2000マイルの旅に出る。思い込みの激しい女性が、夫を探して突っ走る様をコミカルに描いたロード・ムービー。H・グラハムのキュートな魅力が弾けている。監督は「のら猫の日記」で注目されたリサ・クルーガー。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jay and Silent Bob Strike Back", "ジェイ＆サイレント・ボブ　帝国への逆襲", "", "", "http://image.eiga.k-img.com/images/movie/1097/315x210.jpg?4364", 104, "ケビン・スミス監督作「クラークス」から「ドクマ」まで登場するオタクな2人組、ジェイとサイレント・ボブ（演じるのは監督自身）が主演。ときにはイタすぎる（とくにアフレックの）楽屋落ちネタとパロディが満載。共演陣が豪華で、クリス・ロックや、青春ドラマで人気のジェームズ・バン・ダー・ビークやショーン・ウィリアム・スコット、「スクリーム」のジェイミー・ケネディ、シブイところではジャド・ネルソンらも登場。", "2007-03-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("50/50", "50/50 フィフティ・フィフティ", "", "", "http://image.eiga.k-img.com/images/movie/56967/315x210.jpg?2539", 100, "「（500）日のサマー」のジョセフ・ゴードン＝レビットが主演し、ガンで余命宣告を受けた青年の姿を笑いや涙を交えて描くハートフルドラマ。酒もタバコもやらない普通の青年アダムは27歳でガンを患い、生存率50％と宣告される。同僚や恋人、家族は病気を気づかってどこかよそよそしくなっていくなか、悪友カイルだけはガンをネタにナンパに連れ出すなど、いつも通りに接してくれていた。アダムはなんとかガンを笑い飛ばそうと日々を過ごしていくが、刻々と進む病状に次第に平穏を装えなくなってしまう。カイル役のセス・ローゲンが製作を務め、ガンを克服した親友の脚本家の実体験をもとに映画化した。", "2011-12-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN SAMURAI", "バトル・マスター USAサムライ伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "誇り高きサムライ、タツヤ・ワンガのもとで、剣士として成長した孤児ドルーは、タツヤから家宝の刀を譲り受ける。ところがタツヤの実子、ケンジローはそれを深く恨み、２年後、イスタンブールで、今やジャーナリストとして活躍するドルーをら致。世界中の剣士が集まる地下殺人ゲームへ強引に出場させる。血に飢えた観衆を前に、壮絶のソード・バトルが展開していく……。監督のS・ファーステンバーグは、「ニンジャ 転生の章」を手掛けており、本作でも、日本では決して見られない“ブシドー”アクションを作り上げた。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOTRE DAME DE LA CROISETTE", "カンヌ映画通り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 55, "1981年の５月のカンヌ。街は映画祭に集う人々でにぎわっている。ホテル・カールトンに滞在しているベティもその一人。彼女は映画祭に参加したくてこの街にやって来たが、関係者でもジャーナリストでもないため、映画祭事務局から正式参加を認められない。いくつかの記者会見場にもぐりこみ、毎日、ホテルの部屋から事務局に電話をかけるが、相変わらずナシのつぶてで、部屋に閉じこもる日々が続く。退屈まぎれにベッドの上でのぞくオペラ・グラスには、1950年代の華やかな映画スターたちの姿が次々に映し出される。カンヌ映画祭を舞台にドキュメンタリー・タッチで描いた、不思議な魅力をたたえた異色作。", "1981-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KOUNTERFEIT", "マネークラッシュ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「パッセンジャー57」「シュリンジ」などの悪役で異彩を放っている個性派B・ペインが主役を張ったクライム・サスペンス。たまたま300万ドルのニセ札を手に入れたチンピラとクラブオーナーが、それを100万ドルの現金と交換しようとする。しかし取引の行われる夜、何者かの銃撃を浴びた二人はからくも反撃し、合計400万ドルが入ったスーツケースを持って逃走するが……。主人公への復讐を試みるヒロインに、「ベスト・キッド４」で脚光を浴びたH・スワンクが扮している。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("上海バンスキング", "上海バンスキング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "舞台ミュージカルの大ヒット作を、舞台の演出家、出演者らが自らの手で映画化したオリジナルに近い作品。深作版と違ってすべてセットの中で撮影が行われた。衣装デザインに菊地武夫が参加するなど、各方面からの熱い支援のもとに“手作り”で完成した貴重版。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COME IMPARAI AD AMARE LE DONNE", "イタリア式 愛のテクニック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "19歳になったばかりの青年ロベルトは、なぜかいつも女性にモテモテ。ひょんなことからイタリアの高級避暑地でアルバイトすることになった彼は、やはりそこでも……。本当の恋の味を初めて知った青年の姿を描いた青春ラブ・コメディ。", "1966-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mister Lonely", "ミスター・ロンリー", "", "", "http://image.eiga.k-img.com/images/movie/53277/315x210.jpg?2154", 111, "「ガンモ」のハーモニー・コリン監督が8年ぶりにメガホンを取ったラブストーリー。主演に「天国の口、終りの楽園。」のディエゴ・ルナ、共演に「ギター弾きの恋」のサマンサ・モートンを迎え、他人を演じることでしか生きられない男女の恋と自分探しの旅を描く。マイケル・ジャクソンになりきって暮らしてきた男が、ある日マリリン・モンローとして生きる女に出会う。彼は彼女に導かれてモノマネ芸人たちが集団生活を送るスコットランドを訪れ……。", "2008-02-02", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Game of Death", "ゲーム・オブ・デス", "", "", "http://image.eiga.k-img.com/images/movie/55767/315x210.jpg?4592", 0, "「ブレイド」シリーズのウェズリー・スナイプス主演のアクション・ムービー。莫大な富に目がくらみ、暗殺者集団と化してしまったエリートCIAチーム。マーカスはたったひとり正義を貫こうと、自らの信念と仲間たちへの思いとの間で葛藤（かっとう）しながら孤独な戦いに身を投じる。共演に「エクスペンダブルズ」のゲイリー・ダニエルズ、「デス・プルーフ in グラインドハウス」のゾーイ・ベルら。", "2010-11-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MALINA", "マリーナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "女流作家の“わたし”は、マリーナと呼ばれる男とウィーンで同棲生活を送っていたが、ある日、彼女はイバンという男と恋に落ちてしまう。少女時代、近親相姦関係にあり、残虐に扱われていた父親の暗い思い出と、激しすぎる三角関係の情熱が、彼女を狂気の淵へひきずり込む。ドイツ映画祭で作品賞をはじめ４部門を獲得した、官能と戦慄のサイコホラー。主演のI・ユペールがクールな美貌を生かし、鬼気迫る名演技を見せている。", "1990-01-01", "7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КОМИССАР", "コミッサール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "1967年に製作されながらも上映禁止となり、20年後にペレストロイカによってようやく復元、解禁された曰くつきの問題作。コミッサールとは、軍より派遣された政治指導委員のこと。かつて革命に命を懸けた女兵士はコミッサールとなる。同じ革命軍士官の子を身ごもり動揺するが、やがて出産を決意する。", "1967-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OH！　WHAT　A　LOVELY　WAR", "素晴らしき戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "ヒット舞台劇を映画化し、R・アッテンボローが監督デビューを飾った異色の反戦ミュージカル。1914年、セルビアでの事件をきっかけに、欧州各国が次々と第一次世界大戦へ参戦。戦場で命を落としていく庶民や兵士の運命と、彼らを駆り立てた国家を対比させ、戦争の虚飾を痛烈に暴き出していく。L・オリヴィエを筆頭に、英国が誇る名優が総出演。戦時中の流行歌や軍歌を替え歌にして、各エピソードを鮮やかに盛り立てている。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・スパイダースのバリ島珍道中", "ザ・スパイダースのバリ島珍道中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ワールド・ツアーに忙しいザ・スパイダース。ところがそんな彼らに目をつけた密輸団がいて……。香港、バリ島を舞台にしたアクション・コメディ。監督は、「青い山脈」「伊豆の踊り子」など青春文芸もので著名な西河克己。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIRL　FROM　RIO", "ガール・フロム・リオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「ウェールズの山」で人々を温かい笑いに包んだC・マンガーが、持ち味のユーモア感覚をタップリと交えて描くハートフル・コメディ。妻は自分の上司と浮気の真っ最中。おまけに自分は金庫係に降格されてしまい、自信喪失気味の銀行員のレイモンド。ある日、彼は金庫の金を盗んで南米リオデジャネイロへ向かうが……。", "2000-01-01", "3,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("母千鳥", "母千鳥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "夫に捨てられ、寄席で三味線を弾きながら必死に娘を育ててきた女・比佐子。彼女は幼い愛娘・百合の幸せのために、義母のわがままな望みを聞き入れて泣く泣く娘を渡す。そして10数年後、美しい女性に成長した百合は父の墓前でぬかずくみすぼらしい老婆に出会う……。三益愛子の“母もの”映画の一つ。", "1951-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ハロー張りネズミ", "ハロー張りネズミ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "『課長 島耕作』などで人気の弘兼憲史原作の同名コミックの映画化。“正義は金じゃない！”を信条に、どんな事件でも全力疾走で突っ走る熱血探偵“張りネズミ”こと七瀬五郎の活躍を痛快に描いていく。七瀬役を「おいしい結婚」で映画デビューした唐沢寿明が演じている。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("哀しみのベラドンナ", "哀しみのベラドンナ", "", "", "http://ecx.images-amazon.com/images/I/51C1jHW5zML._SL210_.jpg", 89, "フランスの歴史家ジュール・ミシュレの著書『魔女』を題材にして、イラストレーター・深井国の原画を、そのまま静止画として使った手法が注目を集めた。同時にセル画による動画も併用され、両者の間に違和感は残るものの、その野心的な試みがもたらした衝撃は、早すぎた傑作と呼ぶにふさわしい。映画史的にも貴重な一作といえるだろう。中世フランスの農民の妻ジャンヌが主人公。無慈悲な領主にもてあそばれた彼女は、夫からも裏切られる。奈落の底で彼女がつのらせる神への不信、それがジャンヌのもとへ悪魔を招き寄せる。そして魔女となった彼女は、妖しい魅力で男たちを従えていく。肉体の快楽を、むしろ中世の暗黒からの人間の解放と捉えたミシュレ。その主張のドラマ化は、やや難解と思われたためか、興行的には失敗に終わっている。テーマを抜きにしても独特のエロチシズムをたたえた作品だけに、再評価の時が来ることを期待したい。", "1973-06-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("200 CIGARETTES", "200本のたばこ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1981年12月31日のN.Y.を舞台にしたコミカルな恋愛群像劇。年越しパーティーに集まるはずの若者たちは、景気づけの寄り道に夢中。デートを楽しむ者や、失恋の悲しみを女友達に告白する者、道に迷った者などが、それぞれ恋の駆け引きに熱中する。大晦日の浮かれ気分を伝える若手アクターたちの共演が楽しい。E・コステロやブロンディらの当時のヒット曲も陽気なムードに華を添える。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREAK IN THE CIRCLE", "組織〈やつら〉をたたけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "巡洋艦ボナベンチャーのオーナー、モーガンは、資本家ケラーの依頼で、東ドイツの科学者ワドニックを密出国させることになった。途中、リサというスコットランド・ヤードの女スパイを同乗させ、ハンブルグへ。なんとか、ワドニック奪還に成功する。英国に戻り、ワドニックの高い価値を知ったモーガンは、ケラーに多額の報酬を要求するのだが……。ハマー・フィルムではおなじみの監督V・ゲストが、連続するアクションと次々起こるどんでん返しで綴る冒険活劇。", "1955-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EROTICA", "エロティカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "女性が抱えるセックスの欲望を考察したドキュメンタリー。性を追究する女性アーティスト10人が率直な意見を語る。登場するのは『O嬢の物語』の作者ポーリーヌ・レアージュ、映画監督となった元ポルノ女優アニー・スプリンクル、写真家ベッティナ・ランスら。彼女たちの証言を通して、受け身なだけではない女性のセックス願望を浮き彫りにする。", "1997-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　ひみつのアッコちゃん", "映画　ひみつのアッコちゃん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "赤塚不二男の代表作で、過去3度にわたりTVアニメ化もされた人気少女漫画「ひみつのアッコちゃん」を、綾瀬はるか主演で実写映画化。時代背景を現代に置き換えたオリジナルストーリーが展開する。10歳の小学生・加賀美あつ子が、鏡の精からもらった「魔法のコンパクト」で22歳の女子大生に変身。大好きなメイクやオシャレを満喫していたところ、化粧品会社のエリート社員と出会い、運命的な恋に落ちる。買収問題に揺れる会社を立て直すため新商品開発に取り組む彼のため、アッコも奇想天外なアイデアで危機に立ち向かおうとする。アッコの初恋の相手役に岡田将生。監督は「のだめカンタービレ」の川村泰祐。", "2012-09-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAKE DOWN", "テイクダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "巨大都市ロサンゼルスを舞台に、コカインの密輸ルートをめぐる闘いを描いたギャング・アクション。ビラーチ・ファミリーのボス、アンソニーは誰もが成し得なかったルートの独占をたくらんでいたが、シカゴのステイン・ファミリーが勢力を拡大してきたうえ、元ステイン・ファミリーの一員で仲間と脱獄したジョナサンまでがロスにやって来る。それぞれが思惑を秘め、血で血を洗う三つ巴の権力抗争を繰り広げられる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PIRANHA II ： THE SPAWNING", "殺人魚フライングキラー", "", "", "http://ecx.images-amazon.com/images/I/51IKMWeytaL._SL210_.jpg", 91, "体に二枚の翼、口には鋭い歯を持ち空を飛んで人を襲う戦争用の殺人魚、フライングキラーがカリブの美しい浜辺に現れ、次々と観光客を襲い始める。主人公の海洋生物学者と殺人魚の対決の行方は？　美しい海を背景に恐怖の世界を描く、パニック映画。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クジラの跳躍", "クジラの跳躍", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "絵本を中心に、CD-ROMから漫画まで幅広い表現方法を持つイラストレーター、たむらしげるが手掛けたファンタジー・アニメーション。時間速度が異なるガラスの海、そこではクジラの跳躍は静止したように見え、多くの見物人は失われた波の音や安らぎの星の旋律を思い出していく。その中にいた老人も忘れていたある記憶を呼び起こされる……。イマジネーションを刺激する幻想的な映像が心に残る。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明日の記憶", "明日の記憶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "若年性アルツハイマーを題材にした荻原浩の同名ベストセラー小説を、「トリック」シリーズの堤幸彦監督が映画化した感動作。今年で50歳を迎えるサラリーマンの佐伯雅行は、自分が若年性アルツハイマーに冒されていることを知りがく然とする。徐々に記憶が失われていく厳しい現実に焦りを感じながらも、妻・枝実子に支えられて病気と闘う決意をするが……。「バットマン・ビギンズ」など世界的に活躍する渡辺謙が自らエグゼクティブプロデューサーを務め、主演した。", "2006-05-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATAL　LOVE", "堕ちた女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "HIV感染を知った女の心の変化を描いたドラマ。何不自由なく暮らしていた建築デザイナーのアリソンは、体調を崩し、慢性的な風邪の症状に悩まされるようになった。精密検査を受けると、医師はHIV感染の事実を告げる。彼女にはゆきずりの男との関係があったのだった。それまでの生活を失った彼女は……。主演は子役出身で「ゴダールのリア王」にも出演したM・リングウォルド。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELICOPS", "ヘリコップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "マシンガンの銃撃を跳ね返し、高速で追跡する最新鋭ヘリコプターの活躍が楽しめるスカイ・アクション。麻薬組織の罠にはまり、恋人である同僚を殺された女刑事ジェニファー。復讐を誓った彼女は、警察内の裏切り者を告発。さらに、特殊警察が開発した無敵の機動ヘリに乗り込み、麻薬組織を追ってビルの谷間で猛チェイスを繰り広げる。", "1998-01-01", "1,7,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ファの豆腐", "ファの豆腐", "", "", "http://image.eiga.k-img.com/images/movie/56287/315x210.jpg?3099", 40, "国内外で活躍するクリエイターら10人が次世代のクリエイターを発掘・育成するプロジェクト「コ・フェスタPAO」で選出された、期待の若手監督3人による中編映画を一挙上映する「movie PAO」の1本。父と豆腐屋を営む朝子はある日、幼なじみの男と再会。穏やかで静かな日常を送っていた朝子の心に、微妙な変化が訪れる。主演は「森崎書店の日々」の菊池亜希子。監督は、李相日の「悪人」や西川美和の「ゆれる」で助監督を務めてきた久万真路。", "2011-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シャトー・デ・ローゼス", "シャトー・デ・ローゼス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "映像作家、TVプロデューサー、脚本家など多彩な顔を持つ才人、山崎英樹の監督デビュー作は、無国籍な風情が漂う感動ドラマ。ゲイたちが暮らす館に捨てられた少女が、育ての親の死や恋など、さまざまな経験を通じて成長していく姿を描く。ほぼ全編が英語で、字幕付きという異色の日本映画。ゲイたちが暮らす“シャトー・デ・ローゼス”で育てられた少女モラは、館の長老マリアの死期が近いと知って、ある決意をする。", "2005-05-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あした来る人", "あした来る人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "月丘夢路と新珠三千代が美しさを競う井上靖原作による川島雄三作品。すれ違う男と女の姿を独自のタッチで描く。大貫克平と、八千代の仲は冷えきっていた。ささいなケンカから八千代が実家に帰ったあと、克平は友人とカラコルム山脈征服の計画を実行しようとしていた。助監督はチーフを今村昌平が、セカンドを浦山桐郎が務めている。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Blue Mansion", "青い館", "", "", "http://image.eiga.k-img.com/images/movie/54977/315x210.jpg?4607", 106, "「フォーエバー・フィーバー」のグレン・ゴーイ監督によるコミカルなミステリードラマ。一代にして財を築き上げた大物実業家が突然の死を遂げる。自身の不可解な死に疑問を感じた彼は、幽霊となって自らの葬式に戻ってくる。彼が見守る中、葬式に参列した3人の子供たちや嫉妬深い親族、商売敵ら怪しい人物が次々と警察の捜査線上に浮かび上がり、やがて実業家一族の隠された秘密が明らかになっていく。", "2009-10-18", "54", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("垂乳女 Tarachime", "垂乳女 Tarachime", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "「家族」や「生と死」をテーマに作品を撮り続けてきた河瀬直美監督が、自らの出産経験を通して「生」を見つめなおしたドキュメンタリー。2003年に第1子を妊娠した河瀬監督は、自身の妊娠から出産までを映画にしようと撮影を開始。新しい命の誕生を描く一方で、90歳を越えた育ての母にもスポットを当て、引き継がれていく命のつながりを浮かびあがらせていく。ロカルノ国際映画祭審査員特別賞を受賞。", "2007-06-16", "76,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("belief", "belief", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "実の母親がカルト宗教に入ったことを知った青年が、自らカメラを手に取って撮りあげたセルフ・ドキュメンタリー作品。ある日、世間からカルト視されている教団に母親が入信したと知った映画監督・土居哲真。母親の本当の気持ちを理解したいと願った土居は、母親本人や家族をはじめ、宗教信者、心理学者、弁護士などさまざまな人との対話を繰り返すことで、カルトとは何か、そして家族とは何かを見つめなおしていく。", "2007-07-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A　MAN　CALLED　HERO", "レジェンド・オブ・ヒーロー／　中華英雄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "20世紀初頭、両親を殺した西洋人に復讐したため、アメリカはチャイナタウンへと逃れた青年武術家・華英雄の波乱の生涯を描いたダイナミックなアクション大作。差別など苛酷な試練を乗り越えながら生きる主人公をイーキン・チェンが好演。自由の女神像を解体していくクライマックスの格闘シーンが素晴らしい。", "1999-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VERDES DAG", "怒りの日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "「裁かるゝジャンヌ」(1927)、「吸血鬼」(1931)を撮った後、長らく劇映画製作から離れていたC・T・ドライエルが、1942年のドキュメンタリー映画「母性を助ける」のすぐあとに撮った一編。老司祭代理アブサロンは、自分の息子よりも若い後妻アンヌを迎えている。そんな折、遊学に出ていた息子マッティンが帰郷。アンヌとマッティンは自然に接近し、愛し合うようになる。それを知ったアブサロンは、ショックのあまり死亡。つかの間、解放感を味わうアンヌだったが、魔女との告発を受けてしまう……。サイレント時代の圧倒的な画面の強度は薄れ、平凡な構図を選ぶようになったドライエル監督ではあるが、衝撃的なシーンが散見されることも確か。タイトルの「怒りの日」は、カトリックの葬送ミサからきている。ヴェネチア映画祭審査員特別表彰。なお、オリジナル版は105分という説もある。", "1943-01-01", "18", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("向左走・向右走", "ターンレフト・ターンライト", "", "", "http://ecx.images-amazon.com/images/I/51MZ20CG3TL._SL210_.jpg", 99, "台湾の人気絵本作家J・リャオのイラスト詩集を映画化。アジアのスター、金城武と人気女優ジジ・リョンが孤独を抱えながら、新しい生き方を模索する男女に扮したラブ・ストーリーだ。結ばれる運命にありながら、不運が重なったり第三者の登場ですれ違いを繰り返す二人の恋の行方をロマンティックに綴る。ヴァイオリニストの卵ジョンと、翻訳家イブは同じアパートの隣同士に暮らしている。才能が認められず、真実の恋にも出会っていない彼らは、まだ顔を合わせたことがない。やがて、二人は近所の公園で偶然で出会うのだが……。", "2004-10-30", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Pianiste", "ピアニスト", "", "", "http://image.eiga.k-img.com/images/movie/1597/315x210.jpg?2555", 132, "2001年カンヌ映画祭でグランプリ、最優秀主演女優賞、主演男優賞の3冠を独占。監督ハネケは42年ドイツ生まれ。97年「ファニーゲーム」がカンヌコンペ部門に招待され、00年のジュリエット・ビノシュ主演「コード・アンノウン」は同祭エキュメニック賞受賞、続く本作でカンヌを制覇。主演は、2度目のカンヌ主演女優賞のイザベル・ユペール。エリカは、国立音楽院の厳格なピアノ教授。学生ワルターは彼女に恋して授業を受けるが……。", "2002-02-02", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JILL RIPS", "ジルリップス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "肉体派アクション俳優D・ラングレンの主演によるサイコ・サスペンス。ラングレンが扮するタフな元刑事マットが、残忍な方法で殺された兄のカタキを討つべく単身、捜査に乗り出し、ある容疑者をマークする。しかし、それを嘲笑うかのように犯人は、さらなる猟奇殺人を重ね、その罠はマットをも待ち受けていた……。DVDタイトルは「ジル・リップス　殺戮者」。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("信号ばか", "信号ばか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "下校途中の小学生が事故に遭ったのを偶然目撃したことから、菅田監督が手づくりで取り組んだ自主製作の交通安全劇映画。母親が居眠り運転のトラックにひかれて死亡した小学校６年生の男の子を主役に、彼が悲しみを乗り越え、成長していくまでを描く。出演者も全員がノーギャラで参加した。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("西のペテン師・東のサギ師", "西のペテン師・東のサギ師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "変幻自在、口八丁手八丁の大阪ペテン師界No.1のバラやんと、東京代表のサギ師・犬太郎がコンビを組んで、私腹を肥やす悪どい奴らから、悪銭を次々と巻きあげていく様をコミカルに描いた作品。ギャグあり、ロマンスありとバラエティーに富んだ構成。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Definitely, Maybe", "ラブ・ダイアリーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蘇える金狼", "蘇える金狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "大藪春彦のハードボイルド小説の２度目の映画化。昼はマジメなサラリーマン、夜は復讐のために大企業の乗っ取りをたくらむ大悪党。二つの顔を持つ男が、強靱な肉体と明晰な頭脳を武器に野望へと突き進む。「凶銃ルガーP08」に続いて大藪作品の映像化に挑んだ渡辺武監督が、鬱屈したエネルギーの暴発を鮮烈に描写。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生まれかわった為五郎", "生まれかわった為五郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "TVの人気バラエティー・ショー『ゲバゲバ90分』に登場するキャラクターを主人公とする“為五郎”ものの第５作。人情喜劇で名高い森崎東が監督し、ハナ肇扮する為五郎の巻き起こす大騒動を描く。多彩な周辺人物のキャラクターが魅力的。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天使のはらわた 赤い閃光", "天使のはらわた 赤い閃光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "高校時代に見舞われたレイプという忌しい記憶を断ち切れず、カメラマンの名美は極度の清潔症に陥り、男と体を重ねると暴力的になってしまう。ある日、酔いつぶれて目覚めた彼女のかたわらに男の死体が横たわっていた。果して自分がやったのか、名美は消し忘れビデオを再生するが……。今回は相手役の村木は背後に退き、名美のトラウマ（精神的外傷）に焦点を絞ってサイコ・ミステリーに仕立てたところがミソ。鮮烈なラストシーンに川上麻衣子の新境地がうかがえる。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おれの行く道", "おれの行く道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "当時、“新・御三家”の一人と呼ばれた西城秀樹主演のアイドル青春ドラマ。田中絹代演じる祖母の、数十億の資産をねらって孫たちがご機嫌を取る。秀樹扮する主人公は祖母が無一文になっても、見捨てず愛情をかたむける。プログラム・ピクチャーの雄・山根成之の演出が冴える。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ねじ式", "ねじ式", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "石井輝男が「ゲンセンカン主人」に次いで、『別離』『もっきり屋の少女』『やなぎ屋主人』『ねじ式』という、つげ義春のカルト短編漫画４本を連続劇として映画化。売れない作家の奇妙な妄想世界が、シュールなタッチで描かれる。貸本漫画家のツベは、家賃が払えなくなり内縁の妻・国子と別れて放浪生活を始める。人里離れた居酒屋で働き、客に弄ばれるおかっぱ頭の少女チヨジ、自殺未遂で担ぎ込まれた病院の美人看護婦など様々な女たちとの出会いを経て、彼はさらに夢想の旅にはまっていくのだった……。つげの分身とも言える主人公を、浅野忠信がけだるい雰囲気を漂わせて好演し、独特の“つげワールド”に溶け込んでいる。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Songwriter", "ソングライター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "　ソングライターであり、プロデューサーでもあるドクは、ずる賢い経営者ロッキー・ロデオとの契約が罠だったことに気づく。この契約を白紙にすべく、ドクは古い友人の歌手ブラッキーと、アル中気味のラブソング歌手ギルダを雇うが……。長年の友人同士であり、カントリーミュージック界の頂点に立つ二大歌手が共演する音楽業界ドラマ。ネルソンとクリストファーソンが映画のために曲を書き下ろしている。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビースティ・ボーイズ", "ビースティ・ボーイズ", "", "", "http://image.eiga.k-img.com/images/movie/54847/315x210.jpg?3891", 125, "「6年目も恋愛中」のユン・ゲサンと「チェイサー」のハ・ジョンウがホストに扮する人間ドラマ。ソウルの歓楽街・清淡洞のホストクラブで、店のトップとして華やかな生活を送っていたスンウは、純粋な女性ジウォンと出会い次第に惹かれていく。一方、クラブのオーナーでスンウの姉と同棲中のジェヒョンは、ギャンブルで膨らんだ借金を返済するため新たな金づるの女に手を出すが……。「許されざるもの」で高い評価を得たユン・ジョンビン監督の長編第2作。", "2009-11-29", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラブサイコ　妖赤のホラー", "ラブサイコ　妖赤のホラー", "", "", "http://ecx.images-amazon.com/images/I/51HQTNV40VL._SL210_.jpg", 98, "ホラー映画をネット配信する「角川ホラーシネマ」第3弾となるオムニバスホラー「ラブサイコ」の「妖赤のホラー」編。これまで同シリーズの原案・脚本・音楽を手がけてきた岡嶋寛人が初メガホンを取り、女たちの愛と狂気をテーマに描く。山で道に迷った男の前に2人の女が現われ、それぞれ違う方向へ導こうとする「二人の女」をはじめ、「峠の女」「愛してる…」「青い窓」」の4作品で構成。出演は「ピーナッツ」の佐藤めぐみほか。", "2006-07-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Cave", "地獄の変異", "", "", "http://image.eiga.k-img.com/images/movie/33677/315x210.jpg?3227", 97, "ルーマニアのカルパチア山脈付近で発見された巨大洞窟。地元研究者らはプロのケイブダイバーたちを招き調査を敢行するが、地下洞に潜ったダイバーは次々と何者かに襲われていく。そこには、脅威の再生能力と巨大な翼を持ち、獲物を襲撃する魔物が待ちかまえていたのだ。「ワイルド・スピードX2」のコール・ハウザー、「コヨーテ・アグリー」のパイパー・ペラーボらが出演。クリーチャーデザインをパトリック・タトポロスが担当。", "2006-09-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪傑、洪吉童〈ホン・ギルトン〉", "怪傑、洪吉童〈ホン・ギルトン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "17世紀初めに書かれた『洪吉童伝』を脚色した朝鮮初のアクション娯楽映画。幼い頃、山賊に襲われたところを仙人に救われた洪吉童。彼は仙人のもとで修行し、20年後、超人的な武術家に成長した。やがて、外敵から国を救うことを己の使命と感じ、かつての山賊を従え、侵略者一味に立ち向かう。", "1986-01-01", "48", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Black Windmill", "ドラブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "　イギリス諜報機関の工作員タラントの息子が誘拐された。ドラブルと名乗る犯人は身代金としてダイヤモンドを要求。上司ハーパーは犯人が機密に通じている者とにらみ、タラントを疑う。タラントは息子を救出するため、単独で捜索をすることに。はたして犯人の正体は？　たったひとりで戦うハメになったスパイの苦闘を描くサスペンス・アクション。", "1974-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MYSTERY DATE", "ミステリー・デイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "自分自身にも、女性の扱いにもまったく自信のない若者が、兄がやっとのことでセッティングしてくれたファーストデートの最中にとんでもない事件に遭遇。世にも不思議な災難が降りかかる中、彼らはファンタジックで冒険いっぱいのミステリアスな夜を過ごす。当時、アイドルスターだったE・ホークと新進気鋭の監督が手を組んだ、異色青春ロマンティック・コメディ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新・平家物語", "新・平家物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "吉川英治の歴史小説『新・平家物語』を映画化した３部作の第１作。平安末期、次第に力をつけてきた武士階級。若き平清盛はそんな中で権力の野望を胸に、勢力を伸ばしていく。長回しを基調としたカメラワークはフランス・ヌーヴェル・ヴァーグの人々に絶賛された。なんといっても市川雷蔵の若武者ぶりが素晴らしい。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dust", "ダスト", "", "", "http://image.eiga.k-img.com/images/movie/1377/315x210.jpg?4255", 0, "91年の監督デビュー作「ビフォア・ザ・レイン」でベネチア映画祭金獅子賞受賞のマンチェフスキーが7年かけて完成した第2作。ルーク役のデビッド・ウェンハムは「ロード・オブ・ザ・リング／二つの塔」で、前作で死んだボロミアの弟、ファラミアを演じる注目株。ルークと同じ女性を愛してしまう弟役でジョセフ・ファインズが共演。撮影はケン・ローチ監督作の常連バリー・アクロイド。", "2002-07-13", "3,7,4,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("暗黒街", "暗黒街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "古屋組の親分・古谷恒次郎の寵愛でたちまち組の幹部にのし上がってきた庄司。だが組内部にはそれを快く思わぬ者たちが……。東宝専属時代の鶴田浩二がキレ者のヤクザを演じた内部抗争もの。製作の本木荘二郎と監督の山本嘉次郎は、東宝争議時代に、映画芸術協会設立メンバーとして活動した。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JANICE　BEARD　４５WPM", "ジャニスのOL日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "TV番組のドキュメンタリー作品などを手掛けてきた女流監督C・キルナーが、実体験をもとに執筆した脚本を自ら映画化。派遣OLジャニスが体験する大企業の“女の園”をコミカルに描く。スコットランドの田舎からロンドンに出てきたジャニスは、出世とうつ病に悩む母の治療を夢みてとある企業で派遣社員の仕事に就く。しかし、何かと不便な派遣社員の立場と彼女自身の空想癖も手伝ってなかなか仕事になじめない。やがてジャニスに好意を持つ同僚も現れ始めるが、いつの間にか彼女は企業間の陰謀に巻き込まれてしまう。オフィスの中で繰り広げられる女同士の醜い争いや、恋愛騒動を笑い飛ばしながら、女性たちにエールを送ったコメディだ。ビデオタイトルは「ジャニス・ベアードのOL日記」。", "1999-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEAN CREEK", "さよなら、僕らの夏", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "仲間の死という悲劇によって過酷な体験を強いられる10代の少年少女の繊細な感受性をリアルに描き、カンヌ映画祭などで賞賛を浴びた青春映画。美しい水辺の情景など、青春を象徴するようなロケーションが感情をさらに突き動かす。同級生ジョージのいじめに悩むサムを心配して、兄ロッキーは、友人たちを誘ってジョージへの復讐を計画。泳ぎが苦手なジョージを川に突き落としておどかすだけのつもりだったが……。", "2006-06-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("珍説忠臣蔵", "珍説忠臣蔵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "松の廊下の一件にも懲りず、密輸、米の買占め、人身売買から高利貸と、吉良上野介は悪行三昧の日々。講釈師が街頭で盛んにヤジるがしょせんは庶民の非力さ。だが赤穂浪士たちの地下活動は始まっており、彼らは居酒屋、夜鷹、ソバ売り、按摩、飴売などに身をやつして着々と討ち入りの機会をうかがっていた。喜劇の常連をそろえた傑作時代劇。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRIDLOCK’D", "グリッドロック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1996年に、謎の凶弾に倒れ、帰らぬ人となった人気ラッパー、“２パック”ことトゥパック・シャクール。「ジュース」や「ポエティック・ジャスティス…」などでは俳優としてもイイ味を出していた彼が、死の直前に出演していた心温まるバディ・ムービーが本作。仕事仲間でガールフレンドの女性がドラッグ中毒で昏睡状態に陥ったのをきっかけに、“ヤク断ち”を新年の誓いにしたストレッチとスプーン。真摯で前向きなスプーンと、気ままで後ろ向きなストレッチのコンビは、さっそく福祉局に駆け込み、中毒治療を受けようとする。ところが、保険証を持たない二人に役所は冷たく、たらい回しにされ……。社会風刺のメッセージをさりげなく利かせ、ピリリとした味わいの中に、二人の男たちの関係性を浮き彫りにする。若き日のロバート・デ・ニーロを彷彿とさせるT・ロスの風情も絶品だが、トゥパックの美しい瞳は見た人の記憶に残り続けることだろう。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHAT　NOIR，CHAT　BLANC", "黒猫・白猫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "「パパは、出張中！」や「アンダーグラウンド」で知られるユーゴスラビアの才人E・クストリッツァ。一度は引退を宣言した彼が復活して撮り上げたヒューマン・コメディ。ドナウ川のほとりの町を舞台に、若者ザーレと酒場の娘イダのラブ・ストーリーを軸に、ザーレの父親と新興ヤクザがたくらむ石油列車強奪事件、ザーレの祖父とマフィアのボスの友情など、３世代の物語が入り乱れながら愛と情熱の物語が展開していく。ジプシーで初映画出演となる人が多く、彼らの自由奔放な演技が生き生きとした人物像を作り上げている。", "1998-01-01", "2,7,34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海の勝負師", "海の勝負師", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "２年前、仕事中に仲間の裏切りに遭い、卑怯者の烙印を押された潜水夫のジョー。彼にその時と同じケースの仕事が舞い込んでくる。ジョーはかつて潜水の神様と呼ばれながら酒に溺れる北川とコンビで仕事にあたることに。海に陸に宍戸錠が大暴れする、男同士の友情を絡めたアクション作品。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wolfman", "ウルフマン", "", "", "http://image.eiga.k-img.com/images/movie/53847/315x210.jpg?4435", 102, "1941年製作の古典ホラーをベニチオ・デル・トロ＆アンソニー・ホプキンス主演でリメイク。19世紀末のイギリス。人気俳優のローレンス（デル・トロ）は、兄の行方不明の知らせを受けて、生家のあるブラックムーア村へと帰郷する。だが到着早々、切り裂かれた兄の死体が発見され、犯人捜索に乗り出すローレンスだったが、謎の狼男に襲撃を受け、自らも満月の夜に“ウルフマン”となってしまう……。監督は「ジュラシック・パークIII」のジョー・ジョンストン。", "2010-04-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メノット", "メノット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "フランス語で“手錠”という意味のタイトルを冠した本作は、「ラヴァーズ・キス」「富江」の及川中監督による官能サスペンス。アブノーマルな性に溺れていく名家の姉妹の愛憎劇を、美しい映像と音楽で耽美的に描く。グラビア・アイドルとして名を馳せた藤本綾が大胆な濡れ場を披露している。名家・西徳寺家の姉妹、久美と香織は、毎年別荘で夏を過ごしていた。今年も別荘を訪れた香織は、そこで怪しげな男たちと出会い……。", "2005-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY FAIR LADY", "マイ・フェア・レディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 172, "ショーの戯曲『ピグマリオン』をもとにしたヒット・ミュージカルの映画化。貧しい花売り娘イライザが、言語学者ヒギンズ教授の指導によって、社交界の花形レディに変身するシンデレラ・ストーリー。イライザ役の歌はマーニ・ニクソンが吹替えているものの、ヘプバーンのはつらつとした美しさ、F・ロウの名曲の数々、キューカーの巧みな演出で非の打ちどころのない大傑作になった。", "1964-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Spy", "アイ・スパイ", "", "", "http://image.eiga.k-img.com/images/movie/1457/315x210.jpg?1820", 96, "エディ・マーフィ扮する無敵のボクシング・チャンピオンが、ひょんなことから国家保安局のスペシャル・エージェントに。その相棒は、腕は優秀だがシャイな新人エージェント。演じるのは「シャンハイ・ヌーン」シリーズではジャッキー・チェンの相棒を務めるオーウェン・ウィルソン。この凸凹コンビが大騒動を巻き起こすバディ・コメディ。美女エージェント役で「X-メン」シリーズのファムケ・ヤンセンが共演。監督は「ドクター・ドリトル」でマーフィと組んだベティ・トーマス。", "2003-04-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rachel Getting Married", "レイチェルの結婚", "", "", "http://image.eiga.k-img.com/images/movie/54317/315x210.jpg?3651", 112, "「羊たちの沈黙」のオスカー監督ジョナサン・デミが、ある一家の結婚式前後の数日間をドキュメンタリータッチで描いた群像劇。姉レイチェルの結婚式に出席するため治療施設を退院した麻薬中毒患者のキム（アン・ハサウェイ）。だが、一家の厄介者であるキムが9カ月ぶりに家に帰ってきたことにより、家族内の様々な問題が浮かび上がってくる……。主演のアン・ハサウェイは第81回アカデミー主演女優賞にノミネート。巨匠シドニー・ルメットの娘ジェニー・ルメットが脚本を担当。", "2009-04-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("晴れ姿勢揃い・剣侠五人男", "晴れ姿勢揃い・剣侠五人男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "長崎奉行・大野丹波守のはからいで江戸城に招かれた豪商・唐人屋のたいそうなお大尽ぶりは江戸中で評判となっていた。唐人屋と丹波守の背後に何かあると睨んだ北町奉行・遠山金四郎は、目明かし・閂の為八らにその内情を探らせるが……。高田浩吉大熱演の娯楽時代劇。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチバン SUPERMAX", "ガチバン SUPERMAX", "", "", "http://image.eiga.k-img.com/images/movie/58017/315x210.jpg?3085", 70, "人気ヤンキーアクション「ガチバン」シリーズ第13作。主演は「ガチバンMAX」の窪田正孝。共演に若手俳優集団「D-BOYS」の遠藤雄弥。歌舞伎町から逃れるようにして北関東の田舎町にたどりついた黒永勇人と先輩の義男は、「飛羅睨悪（ピラニア）」という不良グループに遭遇。一触即発の空気が漂うが、飛羅睨悪の初代ヘッド・天野光圀が現れてその場を収める。勇人は光圀に敵意を抱きつつも、35歳でも現役のつっぱりを貫く姿に共感も覚える。そんな時、飛羅睨悪の現ヘッド・佐竹の恋人が別のヤンキー軍団にさらわれてしまう事件が起こり……。", "2012-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Swiri", "シュリ", "", "", "http://image.eiga.k-img.com/images/movie/1787/315x210.jpg?1883", 124, "1998年。韓国情報機関“OP”の情報部室長ユ・ジュンウォンは、相棒のイ・ジャンギルと共に、武器密売人イム・ボンジュからある情報を入手するために落ち合う約束をするが、ボンジュは何者かに狙撃され即死する。現場の状況からジュンウォンは、北朝鮮の凄腕女性工作員イ・バンヒが事件に関係していると直感し、捜査を進めるうちに、バンヒがボンジュと組んで液体爆弾CTXを手に入れようとしていたことを突き止める。", "2000-01-22", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天保遊侠伝 代官所破り", "天保遊侠伝 代官所破り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "多彩な娯楽要素を散りばめた時代劇エンターテインメント作品。親友を妹と結婚させる約束を果たすために、父の落胤を探す旅に出た名門松平家の次男坊・源之助は、山賊の根城で出会った信姫が、まさしく彼の妹であると聞く。身分を隠して信姫のお守を続ける彼は、奉行と結託して暴利をむさぼる米問屋と対決することになる。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・ゴールデン・カップス／ワンモアタイム", "ザ・ゴールデン・カップス／ワンモアタイム", "", "", "http://ecx.images-amazon.com/images/I/511APT42G1L._SL210_.jpg", 130, "日本最初の本格的R&Bバンドとして1960年代の音楽シーンをリードしながらも、メンバーチェンジを繰り返し、劇的に去った伝説のグループ、ザ・ゴールデン・カップス。彼らの現在を１年間密着取材し、復活ライブの映像を採録した音楽ドキュメンタリーの傑作。北野武、忌野清志郎、矢野顕子、CHARほか、彼らを敬愛する44人の証言も貴重だ。1960年代後半、横浜・本牧で誕生した日本初のR&Bバンド、ザ・ゴールデン・カップスが一世を風靡していた。解散から31年、復活ライブが行われる。", "2004-11-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMERICAN　STRAYS", "アメリカン・バイオレンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "銀行強盗やマフィア、ストリートギャング、連続殺人犯が、運命に導かれるように砂漠の寂れたドライブインに結集。何のつながりもない彼らが顔をそろえた時、ぶつかり合った欲望は銃弾飛び交う壮絶な戦いを呼び起こし……。スネに傷を持つ犯罪者たちのバトルロイヤルを、ダイナミックに捉えたバイオレンス・アクション。ラスト20分の豪快な銃撃戦が見どころ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Same Time, Next Year", "セイム・タイム、ネクスト・イヤー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "一年に一度だけ会うという不倫カップルの、実に26年におよぶ交際を描いた異色のラブ・ロマンス。もとは男女二人だけが登場する舞台劇であったが、R・マリガンが見事に映画化。洒落た大人同士の会話、M・ハムリッシュによるムーディーな音楽と見どころは多い。A・アルダの怪演にも注目したいところ。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋すがた狐御殿", "恋すがた狐御殿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "原作は菊五郎劇団によって上演された北條秀司の『狐と笛吹き』。足利時代末期、愛妻に先立たれた笛師・春方は、森の中で可愛い少女に出会った。実はこの少女はかつて春方が助けた子狐の化身だった……というファンタジーに、ひばりの歌を随所に散りばめた中川信夫監督の娯楽編。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道拳", "極道拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "元ボクサーでありながら極道となった男の孤独、哀愁、若きボクサーとの友情を描いた同名小説の映画化。元ボクサーで極道の成瀬は、ケンカに負けて這いつくばっていた少年、真田に出会い、ボクシングを教え始める。成瀬は上達の早い真田に、いつしか自分の果たせなかったチャンピオンの夢を託すようになっていた。そんな中、成瀬の仕切る三途会と敵対する黄燐会の抗争が激しくなり……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("School for Scoundrels", "恋愛ルーキーズ", "", "", "http://ecx.images-amazon.com/images/I/519dtPEhNiL._SL210_.jpg", 101, "「ハングオーバー！　消えた花ムコと史上最悪の二日酔い」のトッド・フィリップス監督が、ビリー・ボブ・ソーントンと「バス男」のジョン・ヘダーを主演に描くラブコメディ。気弱な駐車違反取締官のロジャーは、同じアパートに住む女性アマンダに恋をしていたが、まともに会話すらできない自分に歯がゆさを感じていた。そんなロジャーは、ドクターPと呼ばれるカリスマ恋愛教師の秘密の特訓を受け、恋愛下手を克服しようとするが、ドクターPとアマンダのデート現場を目撃してしまい……。", "2006-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAUNTED HONEYMOON", "呪われたハネムーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "俳優G・ワイルダーが監督・脚本・主演の３役をこなしたホラー・コメディ。1930年代後半、人気ラジオ・スターのアボットが婚約者とともに訪れた大叔母の家で殺人事件に巻き込まれ……。レトロ感覚あふれる映像が、独特な雰囲気を盛り上げる。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WILD GEESE", "ワイルド・ギース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "元アメリカ陸軍大尉フォークナーは、大金と引き換えに某国大統領の奪回を頼まれ、かつての戦友である戦争のプロたちとともにアフリカへ乗り込む……。数多くの西部劇を手掛け、ジョン・フォードの直系といわれるマクラグレン監督の巧みな戦争アクション。", "1978-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マブイの旅", "マブイの旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "沖縄を舞台に描かれる、人生に疲れた一人の男と若いカップルの切ない恋愛物語。東京でリストラされ、妻子に逃げられた中年男の福間。自暴自棄になって沖縄にやってきた彼は、酒と女に溺れる毎日を送っていた。そんなある日、娼婦の文海に出会った福間は、彼女のひたむきな純粋さに心惹かれるが……。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ナオミ", "ナオミ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1949年の京マチ子、1960年の叶順子、1967年の安田道代に続いて、水原ゆう紀が主演した谷崎潤一郎原作『痴人の愛』の４度目の映画化。原作を大胆に現代風にアレンジし、ファッショナブルに味つけした異色作で、思いきった官能ドラマに仕上げている。ビデオ、DVDタイトルは「谷崎潤一郎・原作『痴人の愛』より　ナオミ」。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("重装警察／HIT TEAM", "重装警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "香港警察の重装武器専門の情報課特殊部隊Team−E、通称“ヒット・チーム”の活躍を描くアクション。警察署内のラグビー仲間で、固い友情で結ばれている警察官のドンたちは、ある計画をくわだてる。潜入捜査中に正体を暴かれて半身不随の重傷を負った上に戒免職処分になった仲間ホーの治療費を稼ぐため、彼らは地下組織の金庫を襲撃。顔を見られたためやむなく殺人まで犯してしまう。この事件の捜査を担当することになったヒット・チームのリーダー、ツォンはホーの一件を知り、犯人がドンたちではないかとにらむ。一方、地下組織も犯人を追及しており、やがて三つ巴の戦いが始まる……。", "2001-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不壊の白珠", "不壊の白珠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "菊池寛原作によるメロドラマを、八雲恵美子、及川道子、高田稔といった当時の松竹オールスターで映画化した作品。成田は俊枝に淡い恋心を抱いていたが、その妹・玲子を知り、玲子と結婚してしまう。傷心の俊枝は会社の上司・片山に惹かれていく。けれども、新婚の成田と玲子の愛には早くもヒビが入り……。", "1929-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人も歩けば", "人も歩けば", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "様々なジャンルの映画を残した川島雄三が手掛けたナンセンス・コメディ。主人公の青年が、なじみの質屋に出入りするうちに、婿に見込まれ……。「幕末太陽傅」には及ばないが、フランキー堺の演技と、あれよあれよと進んでいく物語のテンポがいい。藤木悠扮する私立探偵・金田一小五郎が笑わせる。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shall we Dance？", "Shall we Dance？", "", "", "http://image.eiga.k-img.com/images/movie/1167/315x210.jpg?2477", 106, "周防正行監督、役所広司、草刈民代主演の大ヒット作「Shall we ダンス？」を、ハリウッドでリメイク。主演にリチャード・ギア、ジェニファー・ロペスを配し、監督は「セレンディピティ」で知られる英国出身のピーター・チェルソムが担当。弁護士事務所で働くクラークは妻と娘との平穏な生活を送っていたが、通勤電車から見えるダンス教室の窓辺にたたずむ美女の姿が気に掛かる。ダンス教室を訪れたクラークは、次第にダンスに夢中になっていく。", "2005-04-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("弁天通りの人々", "弁天通りの人々", "", "", "http://ecx.images-amazon.com/images/I/512CjaQzPxL._SL210_.jpg", 94, "浅野財閥を一代で築きあげた実業家・浅野総一郎の青年時代を、激動の明治時代を背景に描いた歴史ロマン。明治4年。故郷の富山県氷見で事業をおこすも失敗した24歳の総一郎は上京を決意する。開港景気に沸く横浜へたどり着いた総一郎は、故郷の先輩である小倉屋のアドバイスを受けて竹皮商をはじめることに。事業が軌道に乗りはじめたころ、総一郎は貸蒲団屋の女中サクと互いに惹かれあうようになり……。主演は「破天荒力」の水元秀二郎。", "2009-05-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REBEL", "レベル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "S・スタローン演じるテロリスト、ジェリーは恋人を捨ててまで組織のために困難な作戦を遂行するが……。孤独と焦燥の中で都会をさまよう男を描いた、スタローン主演作。", "1973-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("華鬼　三部作　華鬼×神無編", "華鬼　三部作　華鬼×神無編", "", "", "http://image.eiga.k-img.com/images/movie/54717/315x210.jpg?4325", 0, "WEBサイトで連載開始し、後に書籍化され累計25万部を突破したファンタジー小説の映画化。主演は「大奥　浮絵悲恋」「ビートロック☆ラブ」の荒木宏文、「炎神戦隊ゴーオンジャー」「天地人」などで活躍の幅を広げる逢沢りな。16歳の誕生日、「鬼の花嫁」の刻印を持つ少女・朝霧神無は、「鬼の頭」である木籐華鬼のもとへ強制的に嫁がされる。嫁ぎ先の鬼ヶ里にある、鬼とその花嫁たちが生活する学園へ通うことになった神無だったが、夫の華鬼は神無に対して密かな殺意を抱いていた。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きけ、わだつみの声", "きけ、わだつみの声", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "1950年に公開された同名映画を、戦後50年を記念して再映画化。1943年10月21日、東京六大学のラグビー選手として競いあってきた若者たちは、ボールを追っていた神宮の杜で、学徒出陣の盛大な壮行会のなかにいた。戦争という大きな渦に巻き込まれ、彼らの運命は大きく変わっていく。勝村と相原は戦況の厳しさを増すフィリピンで物資不足に苦しみながら抗戦を続け、芥川は特攻隊員として訓練に励む。そして一人徴兵を拒否する鶴谷は、日本で孤独な逃亡を続けていた。早坂暁の脚本は、学徒出陣兵の遺稿手記をもとにした原作に、現代からタイムスリップした大学生を加えることで新しい視点を添えている。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alle Anderen", "恋愛社会学のススメ", "", "", "http://image.eiga.k-img.com/images/movie/56807/315x210.jpg?3545", 119, "カンヌ、ベルリン、ベネチアの世界3大映画祭の受賞作を中心に、各地の映画祭で話題になりながらも日本未公開だった作品を一挙上映する「三大映画祭週間2011」にて上映。避暑地にやってきたクリスとギッティは一見すると熱愛中のカップルだったが、さらに幸せそうでうまくいっている別のカップルに出会ったことで、2人の関係が微妙に崩れていく。2009年・第59回ベルリン国際映画祭で2つの銀熊賞（女優賞・審査員賞）を受賞した。", "2011-08-13", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLIC OU VOYOU FLIC OU VOYOU", "警部", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "汚職捜査官と売春婦の死体が発見され、この二人の関係を調べていくうちに巨大な組織の存在が浮かび上がる。パリ警察の敏腕警視コマンドーは組織を壊滅するために南フランスの暗黒街に潜入、捜査を開始する。そして彼流の荒っぽいやり方で、組織を叩きつぶしていくのだが……。ビデオ発売時タイトルは「ジャン・ポール・ベルモンドの警視コマンドー」。", "1978-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stand by Me", "スタンド・バイ・ミー", "", "", "http://ecx.images-amazon.com/images/I/5106GCOgMuL._SL210_.jpg", 84, "スティーブン・キングの短編小説「死体」をロブ・ライナー監督が映画化したノスタルジックな青春ドラマ。オレゴン州の小さな田舎町キャッスルロック。それぞれに家庭の問題を抱える4人の少年たちが、町から30キロばかり離れたところに列車の轢死体が放置されているという噂を聞き、死体探しの旅に出る。出演はリバー・フェニックス、ウィル・ウィートン、コリー・フェルドマン、ジェリー・オコンネル。", "1987-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Voice of a Murderer", "あいつの声", "", "", "http://image.eiga.k-img.com/images/movie/56937/315x210.jpg?3199", 122, "1991年ソウルで起こった児童誘拐事件を題材にしたサスペンス。監督は「ユア・マイ・サンシャイン」のパク・ジンピョ、主演は「シルミド SILMID」のソル・ギョング。最愛のひとり息子を誘拐された夫婦は、犯人から身代金1億ウォンを要求される。警察の捜査網をかいくぐり、決して正体をつかませない犯人の手がかりは、電話から聴こえてくる声のみ。犯人との44日間におよぶ悪夢の日々が、やがて夫婦間の亀裂を生んでいく……。姿を見せない誘拐犯を「デュエリスト」「M」のカン・ドンウォンが不気味に演じる。韓国映画の名作や最新作を一挙上映する「K-MOVIEセレクション2011」にて上映。", "2011-09-03", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POLICE", "ソフィー・マルソーの刑事物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "刑事マルジャンと謎の女ノリアの危険な恋を描くサスペンス映画。ある事件で逮捕されたノリアを尋問するうち、マルジャンは彼女に心惹かれていく。やがて弁護士ランベールの力を借りて釈放されたノリアだが、今度はギャングの金を盗み出し暗黒街から追われるはめに。ところが、ひょんなことからマルジャンまで事件に巻き込まれてしまい……。M・ピアラ監督の佳作だが、日本未公開に終わった。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LONDON KILLS ME", "ロンドン・キルズ・ミー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ドラッグの売人をしているロンドンのストリート・キッズ、クリントは、自分の生活にいや気がさし、カタギの仕事に就くことにする。まずウェイターになろうとするが、そのためには身だしなみが必要。キレイな靴を手に入れようと彼は必死になるが……。社会問題を扱いながらもどこかトボケた味わいのある青春映画。", "1991-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DANCE OR DIE", "デッド・オン・ダンシング／ラスベガス死の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "麻薬中毒の振付師ジェイソンは、フラッシュダンスばりの新しい踊りを目指していた。彼が成功しかけた時、彼のルームメイトが殺されるという事件が起きた。夜のラスベガスを舞台に繰り広げられる、ショッキングなサスペンス・ストーリー。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("なるしまフレンド　俺っち自転車道", "なるしまフレンド　俺っち自転車道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "73歳にして現役でロードバイクに乗りつづける鳴嶋英雄と、彼が会長を務めるロードバイク愛好会「なるしまフレンド」の活動を捉えたドキュメンタリー。鳴嶋会長が幼少時の自転車との思い出を語るインタビュー、なるしまフレンドの初心者向けコース「Eeeラン」の練習風景、2008年11月に実施された九州サイクルツアーと同年12月に修善寺で開催されたレースの模様などを収録。監督は「能楽師」「浪漫者たち」の田中千世子。", "2009-10-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES ESPIONS", "ザ・スパイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "視察旅行でライプチヒを訪れたアメリカの物理学者ジェームズ・パワー教授は、着くなりロシアの宇宙船実験を記録したマイクロフィルムを入手するよう依頼される。だが、そのフィルムを持つロシアの物理学者が殺害されて……。M・クリフトの遺作となったスパイもの。撮影はゴダールとのコンビで名高いR・クタール。", "1966-01-01", "2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEN THE WIND BLOWS", "風が吹くとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "イギリスの片田舎で平穏な日々を送る老夫婦が、ある日、数時間後に核爆弾が落ちてくることを知る。さっそく政府のパンフレット通りに準備を始めるが……。素朴で純真な人々が、それゆえ巻き込まれていく戦争の恐ろしさを淡々と描いたショッキングなアニメ。原作は人気作家ブリックスの絵本。日本語版では、大島渚が監督し、森繁久彌と加藤治子が声を担当した。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("客途秋恨／SONG OF THE EXILE", "客途秋恨〈きゃくとしゅうこん〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "中国人の祖父母に溺愛されて育ち、英国留学を経て香港に帰ってくるが、日本人である母との関係を悩み、そして憎むヒロイン、ヒューエン。そんな母娘の関係を軸に、死亡した中国人の父、新中国建設の夢を捨てられず大陸に帰っていく祖父母を、自身も日本人のハーフである香港の女性監督A・ホイが描いた力作。", "1990-01-01", "6,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MIDSUMMER NIGHT’S DREAM", "夏の夜の夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "イギリスの伝統ある劇団“ロイヤル・シェイクスピア・カンパニー”が製作したファンタジー。シェイクスピア劇をベースに、イマジネーション豊かな幻想世界が展開していく。眠りについた少年が夢の中で、超現実的な出来事に遭遇。妖精の森や、王と女王の確執が続く宮殿、オモチャの芝居小屋へと、少年の幻想の旅は続く……。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HISTOIRE(S) DU CINEMA", "映画史", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 270, "ゴダール監督が、88年の着手以来10年をかけて独断の視点で綴った映画の歴史／物語。映画のみならず、文学、思想、絵画、音楽といったあらゆる分野からの引用を縦横に散りばめ、「すべての歴史」、「ただ一つの歴史」、「映画だけが」、「命がけの美」、「絶対の貨幣」、「新たな波」、「宇宙のコントロール」、「徴（しるし）は至る所に」という全８章にまとめた大作で、世界に先駆けて日本で劇場公開が実現した。", "2000-05-13", "", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO　MUSIC,NO　LIFE", "NO　MUSIC,NO　LIFE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "10回にわたりネット配信された短篇ドラマの劇場公開版。東京・渋谷のタワーレコードで働く26歳の青年、井本要。音楽プロデューサーになることを夢み、日夜デモテープを製作していた。そんなある日、新進の音楽ディレクター、五月女京子が渋谷店に来店。要はためらいながらも、彼女にデモテープを渡すことに成功する……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("打鐘", "打鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "成長していくヒーロー、ライバルとの戦いと、スポ根マンガを映画にしたような作品。新人競輪選手の立花ワタルは、先行１本でトップを狙っていた。同期生の清原は、トレーナー・佐々木頼子の指導で着実に実績を上げていったが、ワタルは最下位のB2班入り。そんな時、ワタルは頼子の父である文造を、レース中に負傷させてしまう。この事故をきっかけに彼は奮起して……。小松監督のシャープなカット割りにも注目。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES YEUX SANS VISAGE", "顔のない眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "顔にヤケドを負った自分の娘に、ほかの娘の皮膚を移植しようとして殺人を犯す外科医を描いたホラー映画。恐怖感を雰囲気の中から出そうとするフランジュ監督は、映像の詩的なムードを強調して、これを芸術的なホラーに昇華している。", "1960-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("だいじょうぶマイ・フレンド", "だいじょうぶマイ・フレンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "救世主として長いことアメリカにいた宇宙人が、宇宙に帰ろうとしたが飛行能力を失っていて東京に落下。プールにいた３人組は彼が宇宙へ帰るのを助けようとするが……。日本映画では難しいとされるファンタスティックなコメディに挑んだ村上龍の野心作。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURPHY’S WAR", "マーフィの戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "燃えたぎる男の執念を描き尽くした、イエーツ監督の傑作。第二次大戦末期、ドイツが降伏してもなお、南の島でただ一人生き残ったマーフィは、水上飛行機で、クレーンで、敵のはずれ魚雷で、重なる失敗にもめげずUボート撃沈をたくらむ……。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本残侠伝", "日本残侠伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "東映任侠映画の名匠・マキノ雅広が、日活に招かれ高橋英樹主演で監督した任侠映画。大正半ばの浅草。長屋を潰してデパートを建てようとたくらむ悪玉ヤクザの不埒なふるまいに、江戸常組の小頭・中山秀次郎の怒りの刃がうなりをあげる。マキノ雅広は日活でも東映調のパターンを崩さず正統的な任侠映画の佳作に仕上げた。日活任侠映画を一人で背負っていた感のある高橋英樹が、マキノ雅広監督一流の演技指導を受け、堂々たる任侠スターぶりを披露。また、マキノ雅広が名づけ親となり、太田雅子がこの作品から梶芽衣子と芸名を変えた。彼女もこの作品以降見違えるほどの成長をみせる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メイク・アップ", "メイク・アップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ストリッパーと知恵遅れの少年の純愛を描く文芸賞受賞小説の映画化。にっかつの俊英、中原俊が初めて手掛けた一般作だったが、完成後２年間オクラ入りになっていた。ヒロインの心情を象徴するかのように、曇・雨・夜のシーンが多く、寂寥感が漂っている。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILL　THE　LOVE", "彼らだけの世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "韓国を代表する二枚目アクター、イ・ビョンホンが主演した、ワイルド＆ハードなラブ・ストーリー。ビョンホンは孤高のヒットマンをクールに演じている。主人公の青年がある秘密のモノを携えて、アメリカから韓国に帰って来た。そんな彼を待っていたのは、セクシーなダンサー。やがて二人は、愛の深みにはまり込んでいくが……。", "1996-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALFIE", "アルフィー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "次から次へと女を替え、転々と職を変える男を主人公としたB・ノートンの舞台劇の映画化。ロンドンやブロードウェイで大ヒットし、舞台と同じくM・ケインが主役を演じて彼を一挙にスターダムにのしあげた作品。ロンドンのイーストエンドの汚いアパートに住むアルフィーは、身なりだけは素晴らしくスキがなかった。片思い中のギルダにはフラれたが、結核になっても女医や看護婦といい仲となる。退院後は金持ち女に養われ、料理のうまい女の子と同棲、しかしいずれも彼の下から去っていく。そんな時、彼の最初の女が現れた。", "1966-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("口裂け女0 ビギニング", "口裂け女0 ビギニング", "", "", "http://image.eiga.k-img.com/images/movie/54227/315x210.jpg?2971", 88, "アイドルグループ「アイドリング!!!」の遠藤舞と「深海獣雷牙」の折山みゆが初主演するホラー。井上弥生と井上美里は仲の良い美人姉妹だが、美里は幼いころに顔に負った火傷のためにコンプレックスを抱えていた。ある日、森の中を歩いていた美里があやまって転落し、偶然、骸骨死体を発見してしまう。翌朝、森の中で刃物にメッタ刺しにされた夫婦の惨殺死体が発見され、行方の分からない美里には疑いの目がかかり……。", "2008-12-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE DOULOS", "いぬ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "“いぬ”とは警察に仲間を密告する者のことで、ギャングの世界ではそれは死をもって制裁される卑劣な行為を意味する言葉。刑務所帰りのモーリスは、出所するやすぐに裏切り者を射殺する。彼は仲間のシリアンを誘って金庫破りの計画に手をつける。が、その計画は警察に漏れてしまった。シリアンが“いぬ”だという噂が流れる。果たして、本当の“いぬ”は誰なのか……。ゴダールが敬愛してやまないフランスのフィルム・ノワールの巨匠J・P・メルヴィルがクールでダンディーに撮り上げた強烈な作品で、J・P・ベルモンドの魅力も十分に引き出された傑作。", "1963-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("宇宙兄弟", "宇宙兄弟", "", "", "http://image.eiga.k-img.com/images/movie/56147/315x210.jpg?1963", 128, "日本人初の宇宙飛行士という夢を追いかける兄弟の姿を描いた小山宙哉の人気コミックを、小栗旬＆岡田将生の主演で実写映画化。幼い頃から2人で一緒に宇宙飛行士になることを夢見てきた兄ムッタと弟ヒビト。2025年、ヒビトはその約束どおりに宇宙飛行士となり、月面長期滞在クルーの一員として世間の期待と注目の的だった。一方、挫折して無職のムッタは弟との落差に落ち込んでいたが、ある日、JAXAから宇宙飛行士選抜試験の選考通過を知らせる手紙が届く。ムッタは夢に向かって再び歩みはじめるが、そんな時、月面でヒビトの乗った探査機が消息を絶ち……。", "2012-05-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE BRUIT ET DE FUREUR", "かごの中の子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "パリ郊外にある労働者の町バニョレ。そこにやって来た、カゴのカナリヤが友だちの少年ブルーノと、大人も恐れる不良少年ジャン・ロジェとの、奇妙なかかわり合いを描く。監督のJ・C・ブリソーは、元教師だった経験を生かし、少年の世界の残酷さと、落ちこぼれの子供に対する社会の冷酷さを描いている。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RUY BLAS", "ルイ・ブラス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "J・コクトーがユゴーの戯曲を脚色し、当時では破格の１億フランをかけて映画化した悲劇的運命の物語である。17世紀のスペインの王宮を舞台に、女王に追放された男の策略にのせられて側近となった平民ルイ・ブラスの愛と波乱の半生が綴られていく。女王役のD・ダリューの美しさが目を弾く。", "1947-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUES BROTHERS 2000", "ブルース・ブラザース2000", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "「ブルース・ブラザース」の18年ぶりの続編。黒ずくめの服にレイバンでキメたアウトロー、ジェイクとエルウッドことブルース・ブラザース。あれから18年—刑期を終えて出所したエルウッドは相棒ジェイクの死を知る。だが、彼はかつての仲間を呼び戻し再びバンドを組むことを決意する……。ファンの熱狂的な支持を得る前作を手掛けたランディス監督が、久々に気をはいた続編らしからぬ(？)パワフルな快作コメディ。“宙を舞う”パトカーの連続大クラッシュにもア然とさせられるが、何といっても豪華なミュージシャンたちの競演、絶妙な演技と歌がカッコいい。B.B.キングやクラプトンらがこの映画のために結成したスーパーユニット“ルイジアナ・ゲーター・ボーイズ”のライヴ・シーンは必見だ。そして本編のラストに“神のみぞ知る心の謎”とばかりにシャウトするJBが再び登場、あっと驚く“舞台裏”が爆笑もの。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の谷のナウシカ", "風の谷のナウシカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "アニメ雑誌「アニメージュ」誌上に連載されていた宮崎駿の同名漫画の映画化。宮崎監督自身が、監督、脚本を務めた。高度な産業文明を破壊させた「火の七日間」呼ばれる大戦争から1000年。人類は、巨大な虫や、毒の森・腐海に脅かされながら生きていた。辺境の小国「風の谷」の族長の娘、ナウシカは、人間同士の争いに巻き込まれていく。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もうひとつの原宿物語", "もうひとつの原宿物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "TVドラマを中心に活躍する、シナリオ・ライター、石田芳子の監督デビュー作。原宿で生まれ育った美想、珠美、有里は、中学卒業以来、久々に再会。ダンス・グループ“BC”に熱をあげる３人は、その中にかつての憧れの人、“優さん”を発見する。“ホコ天”ならぬ“ロジ天”でパフォーマンスを繰り広げる若者たちを描いた青春映画。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEPT　FROM　THE　SEA", "輝きの海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "海洋文学の巨匠J・コンラッドの短編『エイミー・フォスター』をもとにした感動のラブ・ストーリー。イギリス南西部の小さな村コーンウォールに、難破船が打ち上げられる。ただ一人生き残ったウクライナ人の青年ヤンコは、言葉が通じないため村人から迫害される。そんな彼を優しく介抱したのが、私生児として生まれた孤独な女性エイミーだった。急速に惹かれ合っていく二人は、周囲の非難にも負けず深い愛で結ばれ結婚。やがて子供にも恵まれ、ささやかな幸せを手に入れた二人に過酷な試練が襲いかかる……。無口だが熱い情熱を秘めたヒロインをR・ワイズが、純粋に一人の女性を愛し続ける青年をV・ペレーズがそれぞれ好演。激しい愛の物語の心象風景として映し出される、悲しくも美しい海の景色が圧倒的な印象を残す。", "1997-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFRICA EXPRESS", "アフリカ特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「新・黄金の７人／７X７」「ザ・ビッグマン」など、アクションに切れ味の良さを見せるM・ルーポがおなじみG・ジェンマと組んで発表した痛快アクション。アフリカで雑貨屋を営む冒険野郎が密猟団の首領と対決。そこに美人スパイが加わり……。", "1975-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("想い出を売る店", "想い出を売る店", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "“想い出を売る店”の老主人を狂言回しに、北海を望むノルマンディ地方の小さな村で繰り広げられる愛の物語を描いたメルヘン。スタッフはほとんどが日本人だが、ロケ地や出演者はすべて外国、セリフは日本語に吹き替えるという異色の日本映画。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOING MY WAY", "我が道を往く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "“アメリカの声”といわれた名シンガー、クロスビーがミュージカル以外の映画に出演し、初めてオスカーを手にした名作。ニューヨークの下町にある小さな教会。建物はボロボロ、環境も最低でフィッツギボン老牧師も半ばサジを投げていた。そこに現れたのが若い副牧師オマリー。彼は独特のユーモアと機転で街のいざこざを解決し、人生に望みを失った老牧師を励ましながら、教会焼失という不幸にもめげず、得意の歌を生かして再建に乗り出していく。幸福を運ぶ風来坊といった役柄を、クロスビーが飄々とした持ち味で好演。彼の唄う歌のように心温かいユーモアにあふれた作品。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RAINMAKER", "雨を降らす男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "干ばつが続き、作物も枯れ果てて困惑する農村一家に、前金で100ドル払えば雨を降らせてやると豪語する男がやって来る。そのあざやかな熱弁に、一家の父は金を払うことにするが……。1954年にブロードウェイで上演されたナッシュの舞台劇を、彼自らが脚本を書いて映画化した、夢と愛情に満ちた物語。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POUSSIE｀RE D’ANGE", "キリング・タイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "フランスのフィルム・ノワールの流れをくむニューウェーヴの秀作。監督E・ニエルマンはこれが長編２作目だが、斬新な感覚で魅せてくれる。犯罪捜査課の刑事シモンは、妻が愛人の元へ去ってから、苛立った混乱の日々を送っている。ある日、捜査の現場で出会った18歳の娘ビオレッタ。シモンはちょっと風変わりな彼女に惹かれていく。しかし、捜査が進むにつれ、彼女が殺人事件にかかわっていることがわかってくる……。主演のB・ジロドーは、「ヘカテ」「パッション・ダモーレ」などでおなじみ。F・バスティアンの透明感ある存在感も魅力的。", "1987-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("柳生月影抄", "柳生月影抄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "大目付＝指南役・柳生但馬守を狙う悪女の陰謀を、十兵衛と４人兄弟が力を合わせて打ち破る。十兵衛の弟の又十郎や左門が色仕掛けにだまされ、柳生家を窮地に陥らせる、といった但馬暗殺の様々な趣向を楽しめる。権謀術数入り乱れる波乱に富んだストーリー展開が話題となった。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAZZ　SEEN", "Jazz seen／カメラが聴いたジャズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ジャズを愛する名カメラマン、ウィリアム・クラクストンの半生を追ったドキュメンタリー。1952年にスタートして一躍世間の話題をさらった、「パシフィック・ジャズ」レーベルのアルバム・ジャケット。数々の大物アーティストをカメラに収めてきたクラクストンの知られざる横顔を、写真家ヘルムート・ニュートンや映画監督のジョン・フランケンハイマー、俳優のデニス・ホッパーほか各界の著名人のインタビューにより浮き彫りにする。", "2001-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STREET OF CROCODILES", "ストリート・オブ・クロコダイル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "ある荒れはてた博物館。そこに置かれた奇妙な機械に一人の男が近づき、一滴の唾を垂らす。するとその唾は、静止していた機械を動かし、中にネジと歯車でできた“クロコダイル通り”を現出させる。この映画はそこに展開される幻想的な出来事を綴った人形アニメーションの異色短編映画だ。からくり仕かけの世界、ギクシャクした動き。人形の奇怪な顔立ち、いつの間にか変わる背景。クエイはアメリカ人だが、チェコのアニメーションに惹かれ、チェコ・アニメ独特の悪夢のような世界をマスターして、発展させた。原作は、ポーランドの作家ブルーノ・シュルツの短編『大鰐通り』。クエイ作品には他に「レオシュ・ヤナーチェク」「ヤン・シュヴァンクマイヤーの部屋」「ギルガメッシュ／小さなほうき」「失われた解剖模型のリハーサル」などの短編がある。", "1986-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の花道", "女の花道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "芸一筋に生きた踊りの名手に、美空ひばりが扮した時代劇。安政6年。出雲大社の勧進坐子として全国を旅するおきみの踊りは各地で大評判。地唄舞に弟子入りし、厳しい修行の末に名声を得るが、歌舞伎の振付師に惹かれたことから、おきみは舞を捨ててしまうのだった……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海岸線／COAST　GUARD", "コースト・ガード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「ブラザーフッド」のチャン・ドンゴンと韓国映画界の異端児キム・ギドク監督が強力タッグを組んだ、スリリングな心理サスペンスの傑作。監督自らが海兵隊に所属していた経験をベースに、朝鮮半島の現実を浮き彫りにする。南北の境界線近くの海岸を担当する海兵隊員カン上等兵が、ある夜、濃い闇の中で北の工作員らしき人影を発見。すぐに射殺したが、それは酒に酔った民間人だった……。", "2002-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROP　JOLIES　POUR　E＾TRE　HONNE＾TES　OU　４　SOURIS　POUR　UN　HOLD−UP", "マドモアゼルa Go Go", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "フレンチ・カップル、ゲンズブール＆バーキンの６度目の共演作。バーキン扮するのは、共同生活を送る４人組の女の子の一人。望遠鏡を覗いていた彼女たちは、向かいの空家に強盗コンビ（片方がゲンズブール）が潜伏していることを知り、彼らから金を横取りしようと奔走。これに強盗コンビによる絵画強盗計画の行方が絡み、コミカルかつスリリングなドラマが展開する。1974年の日本初公開時タイトルは「女の望遠鏡」。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("山下少年物語", "山下少年物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "柔道界の第一人者・山下泰裕の生いたちから高校時代までを描いた、ヒューマン・ドラマ。“甘やかしてはならん”という厳しい道徳教育をする祖父や、“彼をこれ以上強くすることはできない”と転校を勧める柔道のコーチなどが、人間味豊かに描かれている。", "1985-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("決闘般若坂", "決闘般若坂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "伊藤＝片岡コンビの宮本武蔵・第２作。前作「二刀流開眼」で武蔵に弟を斬られた宍戸梅軒が、鎖鎌の技を駆使して武蔵に復讐を挑む。梅軒に扮する月形龍之介が好演、単なる悪漢ではない魅力的な敵役像を創りあげ、そのイメージは黒澤明の処女長編「姿三四郎」にも連なるといわれる。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Presumed Innocent", "推定無罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 127, "首席検事補ラスティが、同僚の検事補キャロライン殺害事件を担当することになる。実はキャロラインはラスティの不倫相手だった。捜査を進めていくうちに、浮かび上がる犯人像はラスティを指し……。スコット・トゥローの大ベストセラーを映画化。ハリソン・フォード演じるラスティの苦闘を緊張感たっぷりに描く推理サスペンス。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが恋せし乙女", "わが恋せし乙女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "木下の「大曽根家の朝」が戦後初のキネマ旬報ベストワン作品に輝いた同じ年、同５位としてランクされた作品。ある農家に育てられた兄妹が実の兄妹ではないことを知らされ兄は動揺するが、妹の幸福な結婚を壊してはならぬと自ら身を引く、という木下的ロマンチシズム色濃い物語。DVDは「木下惠介　DVD-BOX 第１集」に収録。", "1946-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五瓣の椿", "五瓣の椿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 163, "松竹が、というより日本が生んだ大作メロドラマの１本。人の良い父親が病気なのをいいことに、不義を重ねていた母親を憎む娘が、父の死後、身を挺して相手の男たちに近付き、次々と殺害しては、父が愛した椿の花を残して去る。岩下の熱演と川又の美しいカメラ・ワークが印象的。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男の紋章・花と長脇差", "男の紋章・花と長脇差", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "大島竜次は、県の役人の依頼で弾丸道路建設のために住民説得に協力する。しかし、悪徳ヤクザ一家が工事を請け負いながら、裏で自ら土地を買い占め、住民たちを操って暴利を得ようとしていることを知り、単身ヤクザ一家に乗り込む。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("満月のくちづけ", "満月のくちづけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "恋のおまじないを行う少女たちが、ふとしたことで失敗してしまい、恋の精霊が悪霊と化して少女たちを襲い始めるオカルト・ホラー。三宅裕司がDJを担当していたラジオ番組“ヤング・パラダイス”を母体に製作された若々しい作品だ。1989年ローマ国際ファンタスティック映画祭で監督賞と主演女優賞を受賞している。なお、高原里絵は深津絵里の別名。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DJEMBEFOLA", "ジャンベフォラ～聖なる帰郷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "キング・オブ・マンディンカと呼ばれる偉大なパーカッショニスト（ジャンベフォラとは伝統的打楽器ジャンベを自由に操られる者のことを言う)、ママディ・ケイタが、20数年ぶりに故郷へと帰る姿を追ったドキュメンタリー映画。素朴な村人たちとの再会、そしてかつての仲間たちと繰り広げる躍動感あふれるセッションに圧倒される。", "1991-01-01", "2,77,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BEACH BOYS ： AN AMERICAN BAND", "ザ・ビーチ・ボーイズ／アン・アメリカン・バンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "息の長い活動を続けるベテラン・ロックバンド、“ザ・ビーチ・ボーイズ”の映像素材を編集した音楽ドキュメント。『サーフィンUSA』『カリフォルニア・ガールズ』など、おなじみのヒット・ソングをはじめ収録曲は50曲以上。マニアックなファンをも唸らせる、音質の良い貴重なライブ映像を中心に構成されている。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("きょうのできごと／a　day　oｎ　the　plaｎet", "きょうのできごと／a　day　oｎ　the　plaｎet", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "新鋭小説家、柴崎友香の同名小説を、「世界の中心で、愛をさけぶ」の行定勲監督が映画化した話題作。どこにでもいるような若者たちが、たった一日の間に経験する出来事を通じて、日常にある豊かさとは何かが綴られる。また、田中麗奈、妻夫木聡、伊藤歩、柏原収史、池脇千鶴ほかの人気若手俳優が集結。彼らの等身大の演技も見ものだ。映画監督志望の中沢と恋人の真紀、中沢の幼なじみのけいとの３人は、京都の大学院に進学する正道の引っ越しを祝うため、車で向かっていた。正道の家には大学の仲間たちも駆けつけて、賑やかなパーティーが始まろうとしていた。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eden", "厨房で逢いましょう", "", "", "http://image.eiga.k-img.com/images/movie/34147/315x210.jpg?1491", 98, "06年のロッテルダム映画祭で観客賞を獲得した大人のラブストーリー。南ドイツで小さなレストランを営むグレゴアは、他人とのコミュニケーションは苦手だが料理の腕は超一流。ある日彼は、カフェで働く平凡な主婦エデンと出会い、密かに思いを寄せ始める。彼女を喜ばすために次々と美味しい料理を生み出していくグレゴアだったが……。5つ星ホテルの料理長が手掛けた魅惑的な創作料理“エロチック・キュイジーヌ”の数々が物語を彩る。", "2007-08-25", "7,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CONTACT", "コンタクト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 150, "NASAの惑星探査計画にも参加した有名な宇宙科学者であり、作家でもあるC・セーガンのベストセラー小説をもとにしたSF大作。「フォレスト・ガンプ／一期一会」のオスカー受賞監督R・ゼメキスが、同作品のスタッフを結集して、一大スペクタクルに仕上げた。天文学者のエリーがある日、不思議な電波信号を受信。恒星ベガからのそのメッセージの解読に取り組む彼女は、それが宇宙間移動装置（ポッド）の設計図であることを発見する。しかし、乗員はたった一人。幼い頃から地球外知的生命体の存在を信じてきたエリーは、宗教学者パーマーに見守られる中、命を懸けて乗組員に志願するが……。宇宙の神秘と、己の存在理由を求める宗教的観念を巧みに絡ませた壮大なる物語が展開。信念を貫く女性天文学者に扮するJ・フォスターが迫真の演技を披露。精巧に作られた移動装置の斬新なデザインや、SFXを駆使した宇宙空間の迫力映像も圧巻だ。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("娘・妻・母", "娘・妻・母", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "井手俊郎と松山善三のオリジナル脚本を、成瀬巳喜男が演出。山の手の中産階級の一家が、金銭面の問題から家族の間に亀裂を生んでいく様を、オールスターで描く。劇中で主人公たちが撮る８ミリで、映画のトリックが暴かれるシーンが興味深い。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("銀嶺の果て", "銀嶺の果て", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "監督の谷口千吉と三船敏郎のデビュー作。３人組の強盗が、警察の手を逃れて北アルプスの山小屋に隠れ、そこに住む老人と娘との間に交流が生まれていく。冬のアルプスでロケーションを行い、スリルとアクションあふれる娯楽作になっている。脚本は、谷口と黒澤明が20日間で書き上げた。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("関東破門状", "関東破門状", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "伯父を殺して破門の身となった寺田二郎が頼った、古き良き美徳をもったヤクザ一家。そこにふりかかる大組織の暴力。二郎の怒りはついに爆発する……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The A-Team", "特攻野郎Aチーム THE MOVIE", "", "", "http://image.eiga.k-img.com/images/movie/55187/315x210.jpg?2136", 118, "80年代に人気を博した米TVドラマ「特攻野郎Aチーム」を、リドリー＆トニー・スコット兄弟のプロデュースで映画化。無実の罪を着せられた4人の破天荒集団“Aチーム”が、汚名を晴らすために奇抜な作戦で事件の黒幕に迫る。リーダーで天才戦略家のハンニバルにリーアム・ニーソン、調達屋のフェイスにブラッドリー・クーパー、用心棒バラカスに元世界UFCライトヘビー級王者のクイントン・“ランペイジ”・ジャクソン、敏腕パイロットに「第9地区」のシャルト・コプリーが扮する。", "2010-08-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILENT LOVE", "サイレント・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ラクエル・ウェルチを母にもつ、T・ウェルチが主演したエロティックな恋愛映画。美術講師アレッサンドラは街にフィアンセを残して仕事のため、ある伯爵邸を訪れる。そこで出会った青年ジュリオに魅力を感じた彼女は、彼に愛の手ほどきをする決心をする。", "1992-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラえもん のび太の宇宙開拓史", "ドラえもん のび太の宇宙開拓史", "", "", "http://ecx.images-amazon.com/images/I/31GR6MBF73L._SL210_.jpg", 94, "時空のもつれにより宇宙の星、コーヤコーヤ星とのび太の部屋がつながってしまう。鉱石に恵まれた開発中のこの星で、ロップルと出会い友達になったのび太たち。そんな折、ガルタイト鉱業がコーヤコーヤ星の鉱石を狙い、星の爆破を企んでいることが分かる。重力差の中、惑星で野球をしようとするのび太たちの姿が微笑ましい。", "1981-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAPRICE", "おしゃれスパイ危機連発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "フェルミナ社の産業スパイ、パトリシアは、自社の製品の製法を商売敵のメイ・フォーチュン社に売ろうとして捕まってしまった。失業した彼女を今度はライバルのメイ・フォーチュン社があらためてスパイとして雇うが、彼女はさっそくメイ・フォーチュン社の秘密を探りだしにかかって……。気まぐれな女スパイをD・デイがユーモラスに演じる秀作コメディ。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("趙先生", "趙先生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「天菩薩」や「上海ルージュ」などで撮影監督を務めたリュウ・ユエが初メガホンを執った軽妙な人間ドラマ。妻、愛人ら３人の女の間をひょうひょうと行き来する中年教師の生き様を描く。中国映画には珍しく“不倫”が否定的に扱われておらず、大らかなタッチでお気楽男ののほほん人生を見つめている。", "1998-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAN OF THE HOUSE", "ママのフィアンセ　結婚に異議あり！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "母親と二人で暮らしている少年ベンは、降ってわいたような母親の再婚話にびっくり。相手は連邦検事のジャック。試しに３人で暮らすことになるが、なんとかジャックを追い出したいベンは、彼を父子参加の“インディアン・プログラム”に誘い込む。訳のわからない参加者と仕事への影響もあり、ジャックはすぐに脱会、ベンは傷ついたフリをする。しかし、ベンの悪だくみを知ったジャックは、脱会を撤回し、ベンの気持ちを取り戻そうとする。C・チェイスが珍しく、ヒューマンな味わいの演技を披露。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女優須磨子の恋", "女優須磨子の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "夫と離婚して新劇運動に飛び込んだ須磨子は、自由恋愛を説く学者・島村抱月と出会う。新劇界の大スターとなっていく須磨子と新劇運動のリーダーとして活躍する抱月との恋は、やがて悲しい結末を迎えることになるのだった……。溝口＝田中の名コンビで描く女性の自立をテーマとした一編。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dead Babies", "デッドベイビーズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「ドッグヴィル」の個性派俳優ポール・ベタニー主演作。快楽を求める超個性的な若者10人の狂騒が、歯止めの効かないブラック・ユーモアとバイオレンス満載で展開する。ロンドン郊外の屋敷で怠惰な共同生活を送る男女６人。ヒッピー風のアメリカ人らを交えて特製ドラッグを服用した彼らは、乱痴気騒ぎを満喫する。しかし、イギリス中を騒がすシリアルキラー“殺人論者”が屋敷に紛れ込み……。", "2000-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　蜘蛛の拍子舞", "シネマ歌舞伎　蜘蛛の拍子舞", "", "", "http://image.eiga.k-img.com/images/movie/55387/315x210.jpg?1722", 68, "歌舞伎の舞台公演をスクリーンでデジタル上映する「シネマ歌舞伎」シリーズ第11弾で、源頼光とその家臣の四天王たちによる土蜘蛛退治の舞踏劇。出演に坂東玉三郎、尾上松緑、坂東三津五郎ほか。「身替座禅」と2本立て上映。", "2010-05-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUN IN ACAPULCO", "アカプルコの海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "E・プレスリーのヒット曲を11曲フィーチャーした娯楽映画。ヨットの水夫ながら歌のうまさを買われ、ホテルで歌うようになったマイク。メキシコに亡命中の貴族の娘と恋に落ちるが、様々な邪魔が入り……。甘いムードのなかに描かれる恋と歌唱シーン。U・アンドレスの姿態も見ものの一編。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピンポン", "ピンポン", "", "", "http://image.eiga.k-img.com/images/movie/1147/315x210.jpg?4653", 114, "松本大洋の同名人気コミックを、ジェームズ・キャメロンのデジタルドメインで「タイタニック」のVFXに参加した曽利文彦監督が映画化。脚本は「GO」の宮藤官九郎。小学校のガキ大将ペコは、得意の卓球をいじめられっ子スマイルに教える。が、高校入学初の全国大会で、スマイルは勝ち進むが、ペコは幼なじみのアクマに負けて大ショック。優勝は名門校の主将、ドラゴンに。彼らそれぞれの1年間の後、次の大会がやってくる。", "2002-07-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青銅の基督", "青銅の基督", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "「本日休診」「現代人」などで知られる渋谷実監督が意欲的に取り組んだ時代劇の大作。幕府のキリシタン弾圧が激しさを増すなか、踏絵代わりのキリスト像を作らされる若い鋳物師とキリシタンの娘との恋や、幕府の政策に協力する転びバテレンの姿を中心に描く。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sherlock Holmes: A Game of Shadows", "シャーロック・ホームズ　シャドウゲーム", "", "", "http://image.eiga.k-img.com/images/movie/57187/315x210.jpg?3535", 130, "アーサー・コナン・ドイルの生み出した名探偵シャーロック・ホームズを主人公に、ガイ・リッチー監督、ロバート・ダウニー・Jr.主演で描くアクション「シャーロック・ホームズ」（2009）の続編。ヨーロッパ各地で連続爆破事件が発生し、ホームズは天才数学者で作家のモリアーティ教授こそが黒幕だと推理する。事件に関与していると見たアイリーンが姿を消し、「手を引かなければワトソンの命も狙う」とモリアーティから脅迫されたホームズは、事件解決の鍵となる女性シムを探しにフランスへ向かうが……。前作に続きジュード・ロウ、レイチェル・マクアダムスが共演するほか、シム役で「ミレニアム　ドラゴン・タトゥーの女」のノオミ・ラパスが参加。", "2012-03-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NIGHT OF THE GENERALS", "将軍たちの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "第二次世界大戦下のワルシャワを舞台に、ナチ将校の犯した猟奇的犯罪を暴いた異色のスリラー。女性が次々と変死する殺人事件を追うナチ警察。やがて捜査線上にタンツ将軍が浮かび上がる……。オトゥールの鬼気迫る演技はスゴ味たっぷり。DVDは「コロムビアトライスター ウォー・ムービー・コレクションBOX(1)」に収録。", "1966-01-01", "3,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("馬鹿っちょ出船", "馬鹿っちょ出船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "「アンコ椿は恋の花」と同様、都はるみ、竹脇無我、香山美子のトリオによる青春歌謡映画。船乗りの兄とマリンガールの妹は、政略結婚に追い込まれた自分たちの幼なじみの窮地を救うが、なぜか兄の姿は寂しげで……。都はるみの1965年の同名ヒット曲がベース。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CE´RE´MONIE D’AMOUR", "愛の化身", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "売春で生活している女優の卵ミリアムは、地下鉄でボードレールを読むのを唯一の楽しみとしていた。ある日、そこで出会った中年写真家を部屋に誘い込み、愛の儀式を行う……。エロティックな内容の前衛小説で知られるマンディアルグの原作をボロズウィックが映画化。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("波の塔", "波の塔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "松本清張の同名小説の映画化。東京地検の新任検事は美しい人妻と不倫を重ねているが、政治ブローカーである彼女の夫の汚職事件の捜査を担当することになってしまう。富士山麓の樹海を背景に、若き津川雅彦がメロドラマ・ミステリーの主人公を好演。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジ・ゴ・ロ アーバンナイト・ストーリー", "ジ・ゴ・ロ アーバンナイト・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "とあるホストクラブを舞台に、ひょんなことでこの世界に入った予備校生が、欲望の渦巻く大人の世界で失敗と苦節を経て、人間として成長していく姿をコミカルに描いたサクセス・ストーリー。「ウェルター」で注目された沢向要士が主人公のホストボーイ役を好演。監督は独特のシティ派感覚に定評の廣木隆一。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京戰争戦後秘話", "東京戰争戦後秘話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "映画製作に没頭する戦後派の若者を主人公に、来たるべき時代の志向性を模索した作品。脚本に起用した原（「20世紀ノスタルジア」監督）は当時まだ10代の映画作家。1969年の羽田闘争（東京戰争）の敗北でひとつの時代が終わったとする大島監督が、若い世代とのコラボレートを試みたストリート感覚溢れる異色作。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やさぐれ刑事", "やさぐれ刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "狙っていた暴力団幹部に妻を寝取られ、コールガールに売りとばされた刑事が、警察官としての職務も規律もなげうって復讐魔と化して日本中を追跡する。原田芳雄のワイルドな魅力が十分に発揮されたバイオレンス・アクション。従来の日本映画にはない迫力。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("賭侠／GOD OF GAMBLERSII", "ゴッド・ギャンブラー２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "チョウ・ユンファ主演の第１作に引き続き、ゴッド・ギャンブラーの弟子“ナイト・ギャンブラー”が代わって活躍する香港ノワール。トランプでの華麗なテクニックを見せるギャンブル・シーンとアクションが見もの。また、「燃えよドラゴン」などのパロディー・シーンでも笑わせるなど、盛りだくさんの香港娯楽映画。主演は香港で人気絶頂のチャウ・シンチー。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRINGING DOWN THE HOUSE", "女神が家〈ウチ〉にやってきた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "妻子に捨てられた仕事一筋のカタブツ弁護士が、チャットで出会った脱獄囚女性との触れ合いを通じて人生を取り戻していくハートウォーミング・コメディ。“花嫁のパパ”シリーズのスティーヴ・マーティンと「シカゴ」のクイーン・ラティファの掛け合いが爆笑を誘う。バツイチ弁護士ピーターの唯一の楽しみは、出会い系サイトでのチャット。そこで美人弁護士シャーリーンに接触、自宅でのディナーへ招待することに成功した。ところが現れたのは巨漢で無造作な女。しかも彼女は脱獄囚だった。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アオグラ AOGRA", "アオグラ AOGRA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "川上健一の自伝的小説「四月になれば彼女は」を、本作が劇場用長編デビューとなる小林要監督が映画化した青春群像劇。1968年の青森県南部。高校を卒業して地元工務店への就職を翌日に控えた沢木は、親友の伝法寺の駆け落ちを手伝うことに。ところが、2人は偶然にも駆け落ち相手の父・熊夫とばったり遭遇してしまう。その上、熊夫が体格の良い沢木を強引に相撲取りにスカウトしはじめ……。主演は、「精霊流し」の内田朝陽。", "2006-10-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラヴァーズ・キス", "ラヴァーズ・キス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "吉田秋生の同名漫画を映画化。一組のカップルと、彼らに想いを寄せる４人の姿を、登場人物の視点を錯綜させながら全４章形式で紡ぐ。宮崎あおい、市川実日子らの競演が見もの。鎌倉の高校生、里伽子と朋章が付き合い始めた。里伽子のことが好きな親友、美樹と、美樹に惹かれる里伽子の妹、依里子は心中穏やかではない。一方、朋章に横恋慕する者たちもいた。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京のえくぼ", "東京のえくぼ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "数々の戦争大作や“社長”シリーズで知られる松林宗恵監督のデビュー作。敗戦から劇的な復興を遂げた東京を舞台に、“都会のえくぼ”というべきユーモラスな人々の人間模様を綴る。上原謙、高峰秀子ら当時の人気スターが顔をそろえた風刺喜劇だ。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PANTHER", "パンサー　黒豹の銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "1960年代後半、オークランドの黒人居住地域で強まる警官の圧力に対して、自らの権利と自衛のために結成された黒人極左組織“ブラックパンサー”党の真の姿を描いた実録ドラマ。地域の自衛に取り組んでいたブラックパンサーは、リーダーのヒューイ・ニュートンが警官殺しの汚名を着せられたうえに、黒人居住地区が麻薬漬けにされるという陰謀に巻き込まれる。ベトナム帰りの党員ジャッジは、FBIが仕組んだ計画だと知り……。伝説の黒人映画監督として知られるメルヴィンとその息子であるマリオが組んだ衝撃作。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOXER JOE", "BOXER JOE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "阪本順治にとって３度目のボクシング映画。赤井英和、大和武士に続いて、今回は辰吉丈一郎を取り上げる。網膜剥離という困難を乗り越えてのハワイでの復帰第１戦から、薬師寺保栄との世界タイトルマッチまでの約１年半をカメラは追う。また、サブストーリーとしてからむのが、辰吉の周りにいかにもいそうな応援団たちの人情ドラマ。大阪のお好み焼き屋の主人・ガンさんとその一家を中心に、おかしくも温かな応援団ぶりで世紀の一戦までを盛り上げる。ドキュメントとドラマが交錯する様は、スポーツ批評とも言うべき高度な達成を果たしており、阪本順治の新たな出発点を見せつけた。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お嫁においで", "お嫁においで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "弾厚作（加山のペンネーム）の同名のヒット曲をもとに松山善三が脚本を担当、怪獣映画の大御所・本多猪四郎が演出にあたる青春映画。父の経営する造船会社に働く加山雄三と、タクシーの運転手・黒沢年男の恋の獲得作戦。加山の妹役の内藤洋子が兄に加勢し、ついに加山はプロポーズするが……。ヒット曲『夜空を仰いで』も挿入される楽しい一編。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実は熟したり", "実は熟したり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "源氏鶏太原作によるサラリーマン喜劇。若尾文子扮する主人公のしのぶは23歳になるOL。彼女には、見合いの話が次から次へと舞い込み、言い寄る男も多い。だが、彼女はちょっと頼りないデザイナーの五郎が気になっている。その五郎にも女の噂が絶えない。そんな時、誠実な好青年・堀田がしのぶの前に現れる……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bandhobi", "ソウルのバングラデシュ人", "", "", "http://image.eiga.k-img.com/images/movie/56767/315x210.jpg?2158", 107, "韓国の女子高生と移住労働者の青年の交流を描いたドラマ。自立心は強いがそれゆえに孤独な女子高生ミンソは、英語塾に通うためにアルバイトを始めるが、なかなかお金が貯まらない。そんなある日、バングラデシュ出身の青年カリムが落とした財布を拾ったミンソは知らぬ振りを決め込むが、カリムに見つかってしまい……。2011年「真！韓国映画祭」にて上映。", "2011-05-28", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SABU／さぶ", "SABU／さぶ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "江戸の下町。経師屋に住み込む栄二とさぶは、幼いころから兄弟以上の友情と信頼で支えあって来た。ある日、栄二が盗っ人の汚名を着せられた上、石川島の人足寄場に送られてしまった。さぶは必死に栄二の行方を探しあてる。しかし、身も心もすさんでしまい“おれはもう復讐しか考えていねぇ”と言う栄二に追い返され……。山本周五郎の不朽の名作を「着信アリ」の三池崇史が映像化した青春友情物語。藤原竜也、妻夫木聡が若々しい演技を見せる。名古屋テレビ開局40周年記念として製作されたテレビドラマ作品に未放送カット分を大幅に加え、完全版として劇場公開した。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEN ZAN", "情無用の戦士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "舞台は北朝鮮。近隣諸国で続発する女性誘拐事件の真相が、同国で“不老不死”の薬を研究しているラーソン博士に関係していることを知ったアメリカ特殊部隊隊長マネットが、仲間とともに救出作戦を敢行する。ヨーロッパではなかなかの人気を誇り、「炎の戦士・ストライカー」のF・ザガリーノが、今回も得意のアクションを披露。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("グラマ島の誘惑", "グラマ島の誘惑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "川島雄三初のカラー・ワイド作品。飯沢匡の原作は「アナタハン」のパロディー。南海の孤島に漂流した皇族たち。だが、のんびりした生活はちっとも変わらない。彼らと軍人のエゴイズムを対比させながら、かなり痛烈な天皇制批判も盛り込んだ珍作。要再評価。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAKE　IT　BIG　ONCE　RUN　JUST　RUN", "ひとまず走れ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "韓国の人気ドラマ『天国の階段』が日本でも大ブレイクしたクォン・サンウと、『夏の香り』のソン・スンホン。韓国若手俳優のトップを走る二人が夢の競演を果たした、ファンにはたまらない一作。甘いマスクに鍛え上げた肉体美が魅力の彼らが、エネルギッシュな青春ドラマを等身大で披露。いつもつるんでいる3人組を乗せた車の上に、何かが落下した。なんとそれは、血まみれの男と米ドル紙幣の詰まった巨大な布袋だった……。", "2005-03-19", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Aventures d'Arsene Lupin", "怪盗ルパン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "日本でもおなじみの怪盗アルセーヌ・ルパンの活躍を描いたモーリス・ルブランの小説を、「現金に手を出すな」のジャック・ベッケル監督が映画化。20世紀初頭のフランス。上院議員の豪邸で開かれていたパーティの途中、怪盗アルセーヌ・ルパンにより数枚の名画が盗まれる。その数日後、ルパンは宝石店を狙い、またしても成功をおさめる。共犯と疑われて職を失ったレオンティヌは、ルパンらしき人物を捕らえ警察に引き渡すが……。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("For Eternal Hearts", "永遠の魂", "", "", "http://image.eiga.k-img.com/images/movie/53497/315x210.jpg?2477", 103, "パク・チョンヒ独裁政権下の韓国を舞台に、3人の男女が織りなす切ない恋をミステリアスに綴ったファンタジック・ラブストーリー。大学生のスヨンは校内で知り合った女子学生ピッピに恋心を抱くが、ピッピは彼の目の前で投身自殺してしまう。それを機にスヨンの周囲で不思議な出来事が続発。やがて別の女性との運命的な恋に導かれていく。主人公スヨンに扮するのは、韓流ドラマ「ごめん、愛してる」でブレイクしたチョン・ギョンホ。", "2008-04-05", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL GRANDE RETORNO／DJANGO STRIKES AGAIN", "ジャンゴ 灼熱の戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "1960年代に隆盛を誇ったイタリア製西部劇、いわゆる“マカロニ・ウエスタン”を正統的に継承した作品。名うてのガンマンとして無法者どもを一掃したにもかかわらず、今は修道士として静かな暮らしを送るジャンゴが独裁者の暴虐を見かねて立ち上がり、闘いを挑む姿を描く。派手なアクション・シーンが見もの。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雨のアムステルダム", "雨のアムステルダム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "小さな商社のヨーロッパ駐在員と、大商社がヨーロッパ上流階級の有力者に人身御供として提供した女の愛を、国際問題を絡ませて描いたサスペンス・ドラマ。アムステルダムを中心としたオランダ各地の美しい風景をバックに、物語は展開する。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Safe", "セイフ（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-10-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Chain Reaction", "チェーン・リアクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "　バークレイ博士とその研究スタッフは石油に代わる新エネルギーの発生装置開発に成功。しかし博士が何者かに殺され、研究所も爆破されるという事件が起こる。疑いの目は、スタッフの一員であるエンジニアのエディに向けられることに。身に覚えのない証拠も見つかり窮地に立たされるエディ。警官殺害の容疑まで着せられた彼は物理学者のリリーとともに逃亡を図るが。巨大な陰謀の黒幕は？　キアヌが危険なアクションスタントに挑んだ大迫力のサスペンス巨編。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUT CAST", "ヘルダイム 悪魔の聖徳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "身のまわりの人間たちから虐げられ続けてきた青年が、悪魔に魂を売り渡し、自分をバカにしてきた人間に復讐をするサイキック・ホラー。「デビルスピーク」や「キャリー」を彷彿させるストーリーだ。主人公は、悪魔と“決して命を創造しない”ことを条件に契約を交わす。彼のすさまじい復讐が開始された。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・男の顔は人生よ", "喜劇・男の顔は人生よ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "石原裕次郎主演「帰らざる波止場」「夜霧よ今夜も有難う」など、全盛期の日活アクションを支えた主要監督の一人、江崎実生による、三波伸介主演のコメディ。1971年、ポルノ路線転換を期に日活を離れた江崎の、最後の日活作品となった。元ヤクザ、セールスマンのペテン師、馬鹿正直者の３人が、金のために右往左往する姿を描いていてる。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN WHO KNEW TOO LITTLE", "知らなすぎた男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "個性派B・マーレーが、『サタデー・ナイト・ライブ』時代から培ってきた独特の喜劇センスをフルに発揮したスパイ・コメディ。彼が演じるのは、弟の家を訪ねてロンドンにやって来たビデオショップ店員ウォレス。そこで弟からもらった誕生日プレゼントの参加型演劇ゲームを体験することになった彼が、本当のスパイ戦に巻き込まれながらも、ひょうひょうと危機一髪のトラブルを切り抜けていく彼の姿に大爆笑だ。命の危険が迫っているとはつゆ知らず、群がる殺し屋を震え上がらせるマーレーの徹底したオトボケぶりが不気味ですらある珍作。", "1997-01-01", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PIT AND THE PENDULUM", "恐怖の振子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ポーの小説『早すぎた埋葬』と『穴と振子』を組み合わせたサスペンス・ホラー。姉の死因を確かめるためイギリスからスペインに渡ったフランシス。真相を探っていく彼は、やがて姉の夫がその犯人ではないかとにらむのだが……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビバ！カッペ", "ビバ！カッペ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "デザイナーを夢見て上京した花子は、なかなか希望の仕事に就くことができず、故郷の水戸に出戻ってくる。そこで風変わりな農業家の青年・太郎に出会った花子は、太郎の家が営む「恩田農場」で働くことになる。花子は農業を通じてたくさんの人々に出会い、ゆっくりと自分の人生を見つめ直していく。主演は元マジシャンの西尾舞生、共演に遠藤憲一、渡辺裕之、螢雪次朗、榊原郁恵、愛川欽也らベテランが揃う。", "2010-04-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほうこう", "ほうこう", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "顔師のかたわら、長年に渡って独創的な人形を作り続け、折にふれて各地で公演している現代の“傀儡師〈かいらいし〉”奥山恵介の世界を、彼とその人形たちが演じるレパートリーの中から代表的な演目を選び出して映像化。さらに彼のもとに集まる様々な人々をエピソードを交えながら描いたセミ・ドキュメンタリー。ビデオ発売時タイトルは「魂〈たま〉遊び　ほうこう」。", "1986-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海賊戦隊ゴーカイジャー THE MOVIE 空飛ぶ幽霊船", "海賊戦隊ゴーカイジャー THE MOVIE 空飛ぶ幽霊船", "", "", "http://image.eiga.k-img.com/images/movie/56237/315x210.jpg?4462", 30, "スーパー戦隊シリーズ35作目「海賊戦隊ゴーカイジャー」の劇場版。どんな夢でもひとつだけかなえることができるという幻の宝「ゴッドアイ」を積んだナゾの幽霊船が出現し、マーベラスらは宝探しに奔走する。しかし、そんな彼らの前に、生きる者の生体エネルギーを集めている幽霊船の船長ロスダークが現れ、マーベラスらを閉ざされた空間に陥れてしまう。", "2011-08-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOI TRE", "モーツァルト 青春の日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "1770年、14歳のモーツァルトがボローニャ近郊を訪れた時のエピソードを映画化。当地の伯爵の息子ジュゼッペとの友情、そして美しき令嬢アントニア・レダに抱いた恋心を情感豊かに綴った青春ドラマだ。監督は、自らの故郷であるボローニャで活動を続けてきたベテラン監督、P・アバティ。", "1984-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNFAITHFULLY YOURS", "殺したいほど愛されて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "プレストン・スタージェス監督作「殺人幻想曲」をリメイクした作品。音楽一筋の世界的な指揮者が、年若い美人女優を妻にする。しかし、若くて美しい妻を持つ身の悲しさか、いつしか嫉妬と妄想に包まれて、妻殺しを計画し……。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES GENS NORMAUX N’ONT RIEN D’EXCEPTIONNEL", "おせっかいな天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "“自分探し”の旅をテーマに、平凡な毎日を送る女性が新しい自分と出会う姿を描く。マルチーヌは３年間付き合っていた恋人フランソワにふられ、ある日街で見かけた彼と罵りあいのけんかになり、逆上してショーウィンドーに頭を打ちつけ一時的な記憶喪失になってしまう。連れていかれた精神病院で彼女は、自分と向かい合っている患者たちと時間を共有し、彼らの手助けをするうちに新しい自分と恋を発見していく……。L・フェレイラ・バルボッザ監督は、将来が期待される新人監督に贈られるシリル・コラール賞とジョルジュ・サドゥール賞を受賞した。", "1993-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Tooth Fairy", "妖精ファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ザ・ロック改めドウェイン・ジョンソン主演のコメディ。反則ばかりするプロアイスホッケー選手のデレク（ジョンソン）は、ある日、夢のない発言をして子供を失望させてしまう。そこで、デレクは妖精たちのボスから、1週間妖精として働くよう命じられる……。共演にアシュレイ・ジャッド、ジュリー・アンドリュース、ビリー・クリスタル。監督は「コニー & カーラ」「サンタクローズ」シリーズのマイケル・レムベック。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しびれくらげ", "しびれくらげ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「でんきくらげ」につづく増村＝渥美マリのコンビで放つ第２作。ファッション・モデルが肉体を武器にして大企業をゆすり、巨万の富をせしめるに至る。性の解放と女性の自立を結びつけた増村保造独特の美学。渥美のグラマラスな肢体が挑発的。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Apartment 1303", "1303号室", "", "", "http://image.eiga.k-img.com/images/movie/34277/315x210.jpg?4164", 94, "ホラー作家・大石圭の小説を、「富江」シリーズの及川中監督が映画化。とある高級マンションの1303号室で恋人と同棲することになった沙弥香は、引越当日に部屋の窓から飛び降りて命を落としてしまう。彼女の死に疑問を抱いた姉の真利子は事件現場となった部屋を訪れるが、隣室で暮らしている不気味な少女から“1303号室の女性は全員死ぬ”という話を聞かされ……。ヒロイン・真利子に扮するのは「夕凪の街　桜の国」の中越典子。", "2007-10-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lucky You", "ラッキー・ユー", "", "", "http://image.eiga.k-img.com/images/movie/1937/315x210.jpg?4146", 124, "「L.A.コンフィデンシャル」「イン・ハー・シューズ」のカーティス・ハンソン監督による人間ドラマ。ラスベガスを舞台にプロのポーカープレーヤー、ハックとその父親でポーカーの世界では伝説となっているL.C.との闘いと、歌手になる夢を抱いてラスベガスにやってきた女性ビリーとハックの恋愛が描かれる。脚本は「インサイダー」「フォレスト・ガンプ／一期一会」のエリック・ロス。", "2007-06-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The River Wild", "激流", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "　夫との離婚を考えていたゲイルは、息子ロークの誕生日に故郷でのリバー・ツーリングを計画。夫のトムも加わり、ゲイルのガイドで川下りが始まる。一方、彼らと同行した青年ウェイドがやがて本性を現していく。ウェイドは強盗と殺人を犯した逃亡犯だったのだ。ウェイド一味は一家を人質にして危険な急流を下ろうとするが……。家族に襲いかかる恐怖、そしてそのサバイバルを描いたアクション・アドベンチャー。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A LAWLESS STREET", "捨身の一撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "R・スコットが製作にも関わり主演したウエスタン活劇。西部劇の魅力の一つである拳銃アクションが緊張感いっぱいに展開する。コロラドのある町が舞台。町に一人しかいない保安官は名ガンファイターで、ならず者を次々に撃ち倒していた。ある日、町にシカゴの芸人一団がやって来るが、その中に保安官の別居中の妻がいた。妻を巡って、保安官と劇場の支配人が対立することに。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A LOW DOWN DIRTY SHAME", "ダーティ・シェイム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "過去に同僚５人が殉職した捜査で、相手のギャング、メンドーザとの関係を疑われて警察を辞職した私立探偵シェイム。そんな彼のところに元同僚で現在は麻薬取締局に勤務するロスミラーが訪ねて来た。メンドーザ逮捕のために愛人のアンジェラを探して欲しいというのだ。アンジェラは、シェイムの元恋人。ほどなくアンジェラは見つかるが、実はこれには大きなウラがあった。エミー賞に輝くコメディアン、K・I・ウェイアンズが監督・脚本・主演を務めている。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Phorpa", "ザ・カップ　夢のアンテナ", "", "", "http://image.eiga.k-img.com/images/movie/1697/315x210.jpg?2245", 93, "ヒマラヤ山麓で暮らす少年僧ウゲンと仲間達は、サッカーのワールドカップに夢中で近頃修行にも身が入らない。夜中に僧院を抜け出し、民家までテレビ中継を見に行ったのがばれて大目玉をくらった彼らは、考えあぐねた末、先生にサッカーを見せて欲しいと直談判する。そして「決勝戦だけ」との約束で僧院でテレビを見る事が許された。しかし僧院にはテレビがない。急いでアンテナとテレビを借りて来なくては！　決勝戦の放映まで、残された時間はあと半日と迫っていた。", "2001-01-27", "62,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FERRY TO HONG KONG", "香港定期船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "香港とマカオを往復するフェリー・ボートを舞台に、オーストリアから追放された白人の流れ者マークをめぐって、同じ無法者たちと女がからむ冒険アクション編。ウェルズがいや味たっぷりの気取り屋に扮して、ユルゲンスと対決する。", "1959-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("堂本剛　平安神宮公演2011 限定特別上映　平安結祈 heianyuki", "堂本剛　平安神宮公演2011 限定特別上映　平安結祈 heianyuki", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「KinKi Kids」の堂本剛が2011年9月に京都・平安神宮で行ったソロライブ「堂本剛　平安神宮公演2011」を映画監督の堤幸彦が映像化したライブドキュメンタリー。", "2012-02-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL　CRIMEN　DEL　PADRE　AMARO", "アマロ神父の罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "世界各国で上映禁止運動が起きる一方で、第75回アカデミー賞では外国語映画賞の候補に。教会の戒律を破り信者の女性と関係した新人神父の苦悩と愛の行方が赤裸々に綴られる。「アモーレス・ペロス」で鮮烈なデビューを飾った新進俳優、ガエル・ガルシア・ベルナルが神父役を熱演する。将来を期待される若き神父のアマロは経験を積むため、小さな町の教会へ。そこで彼は純真な女性信者のアメリアと出会い、教会の規律を破って肉体関係を結んでしまう。やがて彼女の妊娠が判明、彼は事実を隠ぺいしようとするが……。", "2002-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXIT", "イグジット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "リュック・ベッソンがプロデュースを買って出たサイコ・スリラー。被害者が舌を切り落とされるという猟奇連続殺人事件の容疑者として逮捕されたスタン。精神科医に通っていた彼にとって、それは身に覚えのないことだった。６年後、彼は証拠不十分で釈放されるが、時を同じくして再び殺人事件が続発。彼は、自分が犯人かもしれないという妄想に悩まされ……。幻覚と現実が交錯する映像世界はシュールでショッキング。", "2000-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Flandres", "フランドル", "", "", "http://image.eiga.k-img.com/images/movie/33947/315x210.jpg?1302", 91, "人間のあらゆる罪を受け止めるかのように男たちと体を重ねる少女の姿を描き、カンヌ国際映画祭で審査員グランプリを受賞した衝撃作。監督は「ユマニテ」でも同賞に輝いたブリュノ・デュモン。フランドル地方で暮らす少女バルブは、複数の男たちと関係を持つ。彼女に強い想いを抱くデメステルもその内の1人だった。やがて他の男たちと戦場に赴いた彼は、そこであらゆる罪を犯していく。それに呼応するかのように、バルブの精神は異常を来たし始める。", "2007-04-28", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女囚さそり・701号怨み節", "女囚さそり・701号怨み節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "刑務所を脱走したナミは、児玉警部に逮捕されるが、護送中再び脱走しヌード劇場の照明係に救われる。彼は元学生運動家で、児玉をうらんでいた……。監督は伊藤俊也から長谷部安春にバトンタッチした。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNTIL SEPTEMBER", "９月まで抱きしめて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "アメリカからギリシャに向かう途中で、パリで立ち往生してしまった若い女性モー。彼女は友人のアパートで出会った妻子あるフランス人男性と恋に落ちていく。「針の眼」「スター・ウォーズ／ジェダイの復讐」などで知られ、1989年に他界した職人監督R・マーカンドが撮り上げた、夏のひとときに燃え上がる大人の恋物語。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お琴と佐助", "お琴と佐助", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "谷崎潤一郎の小説『春琴抄』初の映画化。明治初期・大阪を舞台にした、商家の盲目の娘と、彼女に献身的に仕える丁稚の愛の物語。当時最高の美男美女・高田浩吉＝田中絹代を使って記録的な大ヒット。リメイクは「春琴物語」(1954)、「お琴と佐助」(1961)、「讃歌」(1972)、「春琴抄」(1976）と数多い。", "1935-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GO−BETWEEN", "恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "20世紀初め、イングランド東部ノーフォークの美しい田園地帯を舞台に、階級を超えた男女の愛をその女性に憧れた少年の回想で描く。数十年後、思い出の地に立つ紳士の、いつまでも心に残る思春期の心象風景を、ロージー監督が驚くほど鋭く切り取って見せる。", "1971-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The New Daughter", "ネスト", "", "", "http://image.eiga.k-img.com/images/movie/55927/315x210.jpg?2012", 108, "「ダンス・ウィズ・ウルブズ」「ボディガード」のケビン・コスナー初となるホラー主演作。ジョン・コナリーの短編小説を、「REC／レック」の脚本家ルイス・ベルデホが初監督を務めて映像化。共演は「パンズ・ラビリンス」のイバナ・バケロ、「チェンジリング」の子役ガトリン・グリフィスほか。妻と離婚した小説家のジョンは、思春期の娘ルイーサと7歳になる息子サムと片田舎の一軒家に越してくるが、裏には古墳のような塚があった。怪しげな音が毎夜響くなか、やがてルイーサは家を抜け出して塚に向かい、泥だらけになって帰ってくるようになる。", "2011-01-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("APUR SANSAR", "大樹のうた", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "インドの名匠レイが人生をかけて作った、畢生の大長編人間ドラマ３部作の完結編。身寄りを失ったオプーの安アパートでの作家修行、ひょんなことで降りかかった結婚と早産による妻の突然の死などが、悠々の大地のリズムで描き出される。放浪するオプーが父性に目覚めるラストが感動的。", "1959-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Otto e Mezzo", "8 1/2　完全修復ニュープリント版", "", "", "http://image.eiga.k-img.com/images/movie/53757/315x210.jpg?2233", 138, "映画史にその名を残すイタリアの巨匠フェデリコ・フェリーニの代表作として知られる自伝的作品「8 1/2」のオリジナルネガを完全修復して蘇らせたニュープリント版。一流映画監督のグイドは、新作の構想に行き詰まってしまいクランクインを2週間も先延ばしにしていた。療養のため温泉地を訪れるグイドだったが、女性たちとの関係や仕事上の知人たちとの現実に悩まされ続けるうちに、様々な夢や幻が彼の前に現われるようになり……。", "2008-07-12", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE AMIGOS！", "サボテン・ブラザース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "米国の人気TV番組『サタデー・ナイト・ライブ』の同窓生チェイスとマーティンが、おなじみのランディス監督と組んだ爆笑西部劇。映画スターの“サボテン・ブラザース”がメキシコの寒村に用心棒として雇われ、ハチャメチャなやり方で賊を退治する爆笑コメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEW GUY", "ニュー・ガイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「メリーに首ったけ」の脚本家が監督したギャグとパロディー満載の学園コメディ。地味な少年が人気者になるべく、偉大(？)な師匠のもとで修行に励む。学校一さえないディズは学園生活を一転させようと、クールな男になる決意をする。偶然知り合った犯罪者ルーサーから処世術を学び、名前を変え、バラ色の学園生活を求めて転校するが……。主人公のズレたセンスが笑いを誘う。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Last Stand", "ラスト・スタンド（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-03-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Going the Distance", "遠距離恋愛　彼女の決断", "", "", "http://image.eiga.k-img.com/images/movie/55397/315x210.jpg?4734", 109, "ドリュー・バリモアとジャスティン・ロングが共演するラブストーリー。「アメリカン・ティーン」の女流監督ナネット・バーンスタインがメガホンをとる。ニューヨークで暮らすギャレットは、バーで出会ったサンフランシスコ出身のエリンと意気投合し、一夜をともにする。数週間後、エリンは地元へ帰ることになるが、一緒に過ごすうちに互いの存在が必要不可欠となってしまった2人は、遠距離恋愛を続ける決意をする。", "2010-10-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A Beautiful Mind", "ビューティフル・マインド", "", "", "http://image.eiga.k-img.com/images/movie/1017/315x210.jpg?1290", 136, "天才数学者ナッシュは、周囲に変人扱いされながら研究に没頭、やがて精神状態に異常をきたすようになり、病との闘いが始まる。ゲーム理論の基礎を築いたノーベル賞数学者ジョン・ナッシュの伝記小説を、オスカー俳優ラッセル・クロウを主演に「アポロ13」のロン・ハワード監督が映画化。", "2007-03-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AU BONHEUR DES DAMES", "貴婦人たちお幸せに", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "19世紀末のパリ。新しくできた百貨店が女性たちの心を捉えていた。経営者のムレ氏は業務拡張を図ろうとしていたが、百貨店に客を取られた地元商店はたまらない。ラシャ生地商のボーデュもそんな一人だ。ある日、ボーデュの姪が田舎からやって来て、百貨店で働き始めた。かいがいしく働く彼女に、ムレ氏は心惹かれるのだが、パトロンのデフォルジュ夫人にやっかまれてしまう……。いかにも19世紀的な豪華絢爛たる装置である百貨店を舞台に、当時の風俗を再現してみせ、それをアイロニカルに料理してみせたA・カイヤット。ボーデュに扮したM・シモンとムレ氏を演じるA・プレジャンの共演が見もの。", "1943-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旗本喧嘩鷹", "旗本喧嘩鷹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "上様のお声掛かりで大の公儀嫌いである旗本・宝大吉の登用が図られる。それは若年寄の地位欲しさに、大吉と恋仲である文を大老家に嫁入りさせた駿河守の入れ知恵だった。仕官は断ったが、ひそかに米問屋越前屋と時の勘定奉行備前守との不正な結びつきを探る大吉は、事件の黒幕が駿河守であることを知る。スリリングかつストレートに話が進行していく、勧善懲悪系時代劇。「東海道四谷怪談」などで知られる巨匠、中川信夫がメガホンを執っている。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("聊斎三集之 燈草和尚／EROTIC GHOST STORYIII", "真説　エロティック・ゴースト・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "香港エンターテインメントの拠点、ゴールデン・ハーベスト社が手掛けたお色気伝奇ロマン。剛腕の美男子が超能力を持つ和尚に導かれ、異次元の魔界へ。美女イーモンを助け出すため、和尚とともに女妖怪や色情ゴーストと渡り合う。“ウルトラマン”シリーズで鳴らした佐川和夫のSFX効果を得て、繰り出される剣術・忍術・魔術の数々。香港・韓国・台湾・日本など国境をまたぐセクシー女優の競演も話題だ。DVD発売時タイトルは「真説　エロティック・ゴースト・ストーリー３」。", "1992-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KID CO.", "キッド カンパニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "自由の国アメリカならではの、奇想天外なサクセス・ストーリー。11歳の天才少年が、妹や二人の姉と会社を設立し、大もうけに成功。これをねたむ大人たちは、納税問題を理由に少年たちを法廷へと引っぱり出すが……。原題は、本作のモデルである実在の会社名。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("他人の顔", "他人の顔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "勅使河原宏の前作「砂の女」と同様に安部公房の原作・脚本による作品。顔に大火傷を負った男は妻が自分を拒んだこともあり医者にまったく他人の顔を作ってくれと頼む。他人の顔をつけた男は妻を誘惑し結ばれるが、妻はそれが夫だと知っていた。安部公房得意の特異な状況を設定した不条理劇。DVDは「勅使河原宏の世界　DVDコレクション 」に収録。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("終わりよければすべてよし", "終わりよければすべてよし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "", "2007-06-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOME SWEET HOME", "ホーム・スイート・ホーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "作曲家ジョン・ハワード・ペインの生涯を、彼の作曲した歌をエピソードとしてオムニバス風に挟みながら描く伝記映画。世界を旅するペインの帰りを家でじっと待つ恋人役には、後の大女優L・ギッシュ。初の長編映画出演を初々しく演じ、不幸に絶え続ける女というイメージを確立した記念すべき作品。", "1914-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("冬の華", "冬の華", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "TVドラマ『北の国から』などで知られる脚本家、倉本聰が挑む東映ヤクザ映画の世界。倉本は抗争のきっかけをカラオケや名画をめぐるセコい争いに求めたり、チャイコフスキーの引用、話の骨子を“足ながおじさん”にしたりと目先の新しさで独自のヤクザ映画を目指す。結果、日常的平和にまみれたヤクザが現出した。出所した関東東竜会幹部・加納は、自ら殺した兄貴分の娘を陰から応援していたが、ついに会う時がやってくる。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("サラリーマンどんと節・ 気楽な稼業と来たもんだ", "サラリーマンどんと節・ 気楽な稼業と来たもんだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "支社長以下とんとやる気のない三日月物産信州信濃支社の気楽な社員たち。そこへ業績不振に腹を立てた社長の御曹子が乗り込んで来るとの報が入り、社員たちは大あわて……。「スーダラ節・わかっちゃいるけどやめられねえ」に続く植木等のヒット・ソングもの。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃太郎２ 大顕神威", "新桃太郎２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「新桃太郎」の続編。成長して、人々から親しまれている桃太郎。だがある日、育ての親であるおばあさんを殺され、自らも大ケガをする。そこで憎い敵・鬼ヶ城の大長老を退治するため、桃太郎は再び出かける。超能力パワーとカンフー・アクションも痛快。", "1988-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレージーの殴り込み清水港", "クレージーの殴り込み清水港", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "C調な追分三五郎は、森の石松のピンチを救い、恩に着せまくる。一方鮫島一家は次郎長を狙って虎視眈々。やがて次郎長一家がフグに当たって全滅したとの知らせを受けた鮫島一家は、殴り込みをかける。しかし知らせは次郎長のトリックであった。作品、興行ともに精彩なく、シリーズはこれで打ち止めとなる。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("国境のない鳥", "国境のない鳥", "", "", "http://image.eiga.k-img.com/images/movie/55007/315x210.jpg?4654", 52, "絶滅の危機に瀕した渡り鳥を守るため、国境を越えて手を結ぶアジア各国の研究者たちの姿を捉えたドキュメンタリー。アジアの湿地に生息する絶滅危惧種の渡り鳥・クロツラヘラサギは、台湾、朝鮮半島、中国、日本と広範囲に渡って移動を続け、北朝鮮と韓国との軍事境界線付近にある非武装地帯で繁殖を行う。彼らの移動を追いながら、いまだその習性に不明な点の多い彼らを救おうと保護研究活動を続ける人々を映し出していく。", "2009-10-19", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANTASY MISSION FORCE", "ジャッキー・チェン ドラゴン特攻隊", "", "", "http://ecx.images-amazon.com/images/I/518qnIsxPjL._SL210_.jpg", 90, "第二次世界大戦下、連合軍の将校が、軍費50万ドルとともに行方不明となる。さっそくドン中尉を中心とした救出部隊が結成される。“ビリー・ザ・キッド”、“パピヨン”、“ホモの兄弟”など、個性的だがすご腕の奴らが集められ、“ドラゴン特攻隊”として将軍救出に向かう。だが途中、恐るべきアマゾネス軍団が彼らを襲いドン中尉を連れ去り、ほかのメンバーも捕らえられてしまう。そこに颯爽と現れたジャッキーは、“ドラゴン特攻隊”を助け、彼らと行動をともにする。ナチスに日本軍、カンフー、アマゾネスから幽霊騒動、お色気、ギャグ、スリルとサスペンス。舞台設定も時代考証もおかまいなし、徹底的に面白さのみを追求したエンターテインメント映画。ジャッキー・チェンをはじめジミー・ウォン、ブリジット・リンらが競演。小気味よいテンポとめくるめくストーリー展開が真骨頂。", "1982-01-01", "6,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("素晴らしい悪女", "素晴らしい悪女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "金だけを信じる女・ミカは、多くの愛人と肉体関係を結んでいる。彼女のアパートの隣に住む一流大学の学生・葉介は、そんな彼女の自由な生き方に憧憬を抱く。ある日、ミカは密輸宝石を手に入れるチャンスをつかみ、葉介が相棒を買って出るが……。石原慎太郎の小説『明日に船出を』を恩地日出夫が映画化。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Max Payne", "マックス・ペイン", "", "", "http://image.eiga.k-img.com/images/movie/53217/315x210.jpg?4205", 100, "「ディパーテッド」のマーク・ウォールバーグが主演するハードボイルド・アクション。共演に「007／慰めの報酬」のオルガ・キュリレンコほか。監督は「エネミー・ライン」「オーメン」のジョン・ムーア。妻子を殺され、いまだ犯人が捕まらないニューヨーク市警の刑事マックス・ペインは、単独で捜査を続けていた。そんなある時、ドラッグ中毒の女性ナターシャが殺害される事件が発生し、マックスは妻子の事件と共通点があることを発見するが……。", "2009-04-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("機動戦士ZガンダムIII A New Translation 星の鼓動は愛", "機動戦士ZガンダムIII A New Translation 星の鼓動は愛", "", "", "http://ecx.images-amazon.com/images/I/31TWM17F35L._SL210_.jpg", 99, "「機動戦士ガンダム」の続編として1985～86年に放映された「機動戦士Zガンダム」を、TV放映20周年を記念し劇場版として再構成した3部作の第3部。エゥーゴとティターンズの戦いに、復興を目指すジオン軍残党アクシズが加わり、戦局は三つどもえの消耗戦に突入。Zガンダムに乗り戦場を駆けるカミーユ・ビダンは、そのなかで本当に倒すべき敵を見つけ出す。", "2006-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MADE IN U.S.A.", "メイド・イン・U.S.A.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "フランス秘密警察とギャング組織のつながりが暴露された“ベン・バルカ事件”をもとに、架空の都市で謎の死を遂げた愛人の死を調べる女性記者の活躍を描いた作品。ゴダール映画としては「気狂いピエロ」と並んで“娯楽性”が高い。暴力と殺人、政治的陰謀の渦巻く街にさまよい込んだ主人公アンナ・カリーナを取り巻くのはドナルド・ダック、リチャード・ウィドマーク、ドリス・溝口などいずれも映画人の名前を持った怪人物ばかり。全編に散りばめられた引用の数々もゴダールならではのもので、映画への偏愛と遊びの精神に満ちた傑作。ミック・ジャガーの恋人であったM・フェイスフルが歌う場面も有名。", "1966-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDER WORLD", "アンダーワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "都市の地下深く広がる“アンダーワールド”。そこはドラッグにより醜く変身したミュータントの世界。彼らはドラッグの解毒剤を求め地上にやって来る。彼らの希望の光はニコルという美しい少女。彼女だけが醜く変身しないのだ。そしてミュータントは彼女の誘拐に成功する。地上を支配するボスはニコル奪還、ミュータント壊滅のため、一人の男ベインをアンダーワールドへ送り込む。ベインの迷宮めぐりが始まる……。ホラー・ファンタジーのベストセラー作家C・バーカーの初脚本作品。彼はこのあと、「ヘルレイザー」で監督業に乗り出した。", "1985-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PERMANENT RECORD", "パーマネント・レコード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "作曲のできないことを苦に自殺した親友のため、クリスは理事長の反対も聞かず卒業公演で彼の曲を披露しようとするが……。元クラッシュのJ・ストラマーが音楽を担当。J・D・サウザーが主題歌を歌い、ルー・リードが特別出演と、ロック・ファンには見逃せないラインナップで描くドラマ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("カニバル・カンフー／燃えよ！食人拳", "カニバル・カンフー／燃えよ！食人拳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "中国政府の密使が長年追う盗賊逮捕のために向かった離れ小島の寒村。しかし密使は、そこでおぞましきものを目撃する。そこは訪問者を惨殺しては食料にする恐るべき“食人族”の村だった……。中国の奥地に住むという食人族とカンフーの達人との死闘を描く。“ツイ・ハーク幻のカルト作品”としてマニアの間で語り継がれてきた、過激なホラー・アクション。", "1980-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志", "次郎長三国志", "", "", "http://image.eiga.k-img.com/images/movie/53377/315x210.jpg?3721", 126, "叔父である監督・マキノ雅弘の「次郎長三国志」シリーズのメガホンを「寝ずの番」のマキノ雅彦が引継いだ、豪華キャスト競演の本格エンターテインメント時代劇。結婚したばかりの妻・お蝶を置いて、世渡り修行へ出かけた駆け出し博徒の次郎長（中井）は、道中に様々な仲間に出会い、やがて東海道中にその名を轟かせるようになる。だが、甲州で一大勢力を誇る黒駒の勝蔵（佐藤）、石松の極悪人の三馬政らが現れて……。", "2008-09-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("フランキー・ブーチャンのあゝ軍艦旗", "フランキー・ブーチャンのあゝ軍艦旗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "「フランキーの牛乳屋」のコンビ、フランキー堺と市村俊幸に、新人・大泉滉が加わって繰り広げる珍無類な水兵物語。口の早い江戸っ子で要領のよいフランキーと、田舎者でいつも損な役まわりの市村の対照が面白く、春原監督得意の爆笑編となった。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野獣都市", "野獣都市", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "工学部の学生・有間は、銃砲店でアルバイト中に銃と運転の腕前を見込まれて、実業家・石浜の運転手兼用心棒となる。石浜は戦争中に麻薬で荒稼ぎした過去があり、昔の仲間やヤクザに脅迫されていた……。ゴミ埋立地を舞台にした決闘シーンや暗闇でのクライマックスなど、シャープな演出が光るハードボイルド活劇。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ソウル・ミュージック・ラバーズ・オンリー", "ソウル・ミュージック・ラバーズ・オンリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "直木賞受賞作で８話の短編から成る山田詠美の同名小説から４つの恋を抜粋、一つの物語にして設定を現代の日本に置きかえた青春恋愛映画。クラスメイトに惚れながら、紹介されたヤングミセスと深い仲になる高校生・良介は出口のない青春を謳歌する。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　銀魂　新訳紅桜篇", "劇場版　銀魂　新訳紅桜篇", "", "", "http://image.eiga.k-img.com/images/movie/55067/315x210.jpg?4662", 0, "2003年に「週刊少年ジャンプ」で連載開始された、空知英秋によるギャグ漫画を映画化。架空の江戸時代末期、黒船の代わりに襲来しした異星人・天人（あまんと）が、地球人と数十年にわたる攘夷戦争を繰り広げた結果、江戸の町には天人の文明が定着する。天人の出した廃刀令によって刀を失った銀髪の侍・坂田銀時は、何でも屋「万事屋銀ちゃん」を営み、町で起こるさまざまなトラブルを解決していく。", "2010-04-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITY OF ANGELS", "シティ・オブ・エンジェル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "演技派N・ケイジと、ロマンティック・コメディ映画の女王M・ライアンが共演、日本でもスマッシュ・ヒットを飛ばしたハートフルなラブ・ファンタジー。天使セスは死に逝く者の魂を天国に導く仕事をこなしていたがある日、女医のマギーと出会う。いつしか彼女に恋したセスだったが、彼には永遠の命が授けられている代わりに、触覚、味覚、臭覚などがなかった。つまり、どんな思いを募らせても彼女に触れることはできないのだ。耐え切れなくなった彼はついに永遠の命を捨て、人間となってマギーを愛することにするのだが……。合理主義者で割り切った恋愛をしてきたマギーがピュアな恋愛に心ときめかす様を、M・ライアンが可憐に演じている。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OPERATION PETTICOAT", "ペティコート作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "潜水艦を舞台にした戦争コメディ。潜水艦“海の虎”号は、南の小島で看護婦５人を救出。ところが艦内はお色気騒動の連続となってしまう。色事師役のカーティスの名演技をはじめ、爆笑シーン満載。特に、ペンキ不足でピンク色に塗られた“海の虎”号が堂々浮上する場面は抱腹絶倒のきわみ。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逆転裁判", "逆転裁判", "", "", "http://image.eiga.k-img.com/images/movie/56587/315x210.jpg?3191", 134, "2001年の発売以来、シリーズ累計410万本を売り上げているカプコンの人気法廷バトルアドベンチャーゲームを、「クローズZERO」「十三人の刺客」の三池崇史監督が実写映画化。犯罪の増加に伴い3日以内に被告人の有罪・無罪を決定する「序審法廷」システムが採用された日本。弁護士と検事が限られた時間内で裁判と捜査を同時に行い、なによりもスピードが最重要視される法廷を舞台に、新人弁護士の成歩堂龍一が、持ち前の正義感と依頼人を信じる心を武器にライバルの天才若手検事と戦いを繰り広げる姿を描く。主人公の成歩堂役に成宮寛貴、ライバルの検事・御剣役に斎藤工。", "2012-02-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜びも悲しみも幾歳月", "喜びも悲しみも幾歳月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 151, "北は北海道・納沙布岬から、南は五島列島・女島まで全国15ヵ所をカバーする、日本縦断ロケを敢行した燈台守夫婦の25年にわたる年代記で、一種のロード・ムービーである。また同名の主題歌は大ヒットとなり今でも懐メロとして歌われている。上海事変の勃発した1932年、新婚早々の夫婦が観音崎燈台に赴任した。日本が国際連盟を脱退した翌年には、北海道石狩燈台へ転任となり、そこで長女と長男が生まれ、1937年には女島へ転任。ついに日米開戦を告げる前年、佐渡の弾崎燈台に移る、という風に“激動の昭和史”を副軸に置く感動編である。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バンパイアハンターD", "バンパイアハンターD", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "1983年に発表された人気シリーズ小説『吸血鬼ハンター』の第３作『D−妖殺行』をアニメ化。吸血鬼と人間の間に生まれたダーク・ヒーロー“D”の活躍を描くゴシック・ロマン。令嬢がバンパイアに誘拐されたとの知らせを受けたDは、犯人を追ってある村を訪れる。だが村人は皆、犯人によってバンパイアにされていた。村を抜け、辺境の地に赴いたDは罠にはまり……。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PART XIII リストラの代償", "難波金融伝 ミナミの帝王　劇場版PART XIII リストラの代償", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "大阪・ミナミの高利貸し、萬田銀次郎の活躍を描く人気シリーズ劇場版第14作。会社をリストラされ、探偵稼業をしながら暮らす宇田川は、銀次郎からの借金を重ねていた。しかし、会社から退職金を受け取っていなかったことに気づき……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GIRLS LOVE", "GIRLS LOVE", "", "", "http://image.eiga.k-img.com/images/movie/54247/315x210.jpg?3871", 82, "「ニュースター・シネマ・コレクション」として、エイベックスが同社の新人を起用し製作した青春映画の第3弾。ピアニストになることを母から期待されている高校生ヨーコは、自分の本当の夢を言い出せず苦しんでいた。そんなある日、ヨーコは陸上部の少女ナツオに出会い、2人は互いの夢を語り合ううちに友情を越えた感情で結ばれていく……。「蒼き狼／地果て海尽きるまで」の下宮里穂子、雑誌「セブンティーン」モデルの大石参月が主演。", "2009-02-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ТАЙНОЕ ПУЕШЕСТВИЕ ЗМИРА", "ブハラ大公の秘密の旅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ウズベキスタンの新鋭F・ダブレトシンの監督デビュー作で、800万人を動員した映画。1911年、第一次ロシア革命の敗北と十月革命の間の時期。羊飼いの娘ネタイは、ブハラ大公に見初められるが、彼女は思い通りにならない。大公はネタイに未練を残しつつ政権打倒の援助を得るため、ロシア皇帝のもとへ隠密の旅に出るが……。", "1986-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AGE OF CONSENT", "としごろ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "中年の芸術家は、とある島で創作活動を続けていたが、ふと知り合った美しい少女をモデルにする。芸術家は、次第に少女の美しさに惹かれ、二人の奇妙な生活は続いていくが……。風物詩的な作品で、汚れのない青々とした海や島の大自然の描写が見もの。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ペンギン夫婦の作りかた（仮題）", "ペンギン夫婦の作りかた（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/57987/315x210.jpg?1956", 0, "", "2012-10-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・猪突猛進せよ!!", "喜劇・猪突猛進せよ!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "当時流行のウーマン・リブに押されつつもガンバる男たちの珍騒動。CMディレクターの兄はズッコケ三枚目、かつらのセールスマンをしている弟はハンサムな二枚目。好対照な二人がCMモデルとの結婚をめぐっててんやわんや……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゲキ×シネ「SHIROH」", "ゲキ×シネ「SHIROH」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 197, "「劇団☆新感線」による演劇を映画館で上映する「ゲキ×シネ」第3作。天草四郎の史実をモチーフに、「劇団☆新感線」が初の本格ロックミュージカルに挑んだ。3代将軍・徳川家光の時代。九州の天草・島原では、迫害に耐えながらキリシタン信仰を続ける農民たちが、救世主・天の御子の出現を待ち望んでいた。その頃、島原には幕府への反乱を起こす決意をした益田四郎時貞、そして天草には歌で人の心を操ることのできるシローという、2人の“SHIROH”がおり……。", "2005-08-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Somebody Killed Her Husband", "シャレード'79", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "舞台を前作のパリからニューヨークに移し変えて翻案したロマンティック・ミステリー。オモチャ売り場で働くジェリーと一児の母ジェニーとの不倫に、保険会社の要職に就くジェニーの夫の殺人事件が絡んでいく。健康肉体派フォーセットのデビュー作。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("吾輩は猫である", "吾輩は猫である", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "夏目漱石の同名小説の再映画化（最初の映画化は1936年・山本嘉次郎監督）。原作の通り、猫から見た人間世界を映し取り、明治時代の知識人たちの憂うつと倦怠を描く。猫の飼い主の苦沙弥（くしゃみ）先生には仲代達矢が扮しているが、明治という時代に生きる気骨は感じられない。市川崑の演出もサラリとヒョウヒョウとした仕上がり。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOOK OF DAYS", "ブック・オブ・デイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "作曲家、ダンサー、振付師、映像作家など様々なフィールドで活躍するM・モンクによる、静謐な映像作品。中世の村に生きる、予知能力を持った一人のユダヤ少女が垣間見る未来社会の姿を描いていく。カラーとモノクロを未来と中世の各シークエンスで使い分けることによって、映像に重層的な広がりが与えられる。そして、彼女自身の手になる音楽が作品世界に不思議な静寂を付け加えると同時に、身体の動きと呼応し、あたかも、さらなる次元へと映像を進化させていくかのようだ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES　MAUVAISES　FREQUENTATIONS", "わるい仲間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 42, "J・L・ゴダールやF・トリュフォーに才能を認められ、夭折の天才と呼ばれたJ・ユスターシュの監督デビュー作。カフェのピンボールで遊び、女性をナンパするのが唯一の楽しみである悪友同士のダニエルとジャクソン。ある日彼らは一人の女性をナンパし、ダンスホールに連れ出すことに成功。ところが、彼女は二人を無視して別の男と踊ってばかり。怒った彼らは、彼女の財布を盗んで逃走するが……。VHS、DVDは「ジャン・ユスターシュコレクション２」に収録。", "1963-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("イグナシオ 純粋的衝動殺人者", "イグナシオ 純粋的衝動殺人者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "芥川賞作家・花村萬月の『聖殺人者イグナシオ』の映画化。新しいバイオレンス・キャラクターを創造した会心作。教護院の聖堂で洗礼を受け、イグナシオという洗礼名を受けた一夫は、教護院を抜け出し、新宿歌舞伎町のクラブで働き始める。しかし、イグナシオはそこで、ゲイのマネージャーに迫られ、殺してしまう。それを目撃したヤクザの組長・大谷は、イグナシオの凶暴性を見抜き、妹の茜に面倒を見させることにする。茜とイグナシオは次第に惹かれ合うようになるのだったが……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAX，MON AMOUR", "マックス、モン・アムール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「愛のコリーダ」以来、活動の拠点を海外においている大島渚が、スタッフ・キャスト全員を外国人で製作した問題作。共同脚本はルイス・ブニュエルの映画の脚本などで有名なJ・C・カリエールで、撮影はゴダールの作品の多くを担当しヌーヴェル・ヴァーグの一端を担ったR・クタール。物語はチンパンジーと愛し合ってしまった人妻とその家族の生活を、実話をもとにして構成したもの。人間と獣の間に芽ばえた愛情という、異常ともいえるテーマを決してゲテモノ趣味ではなく、大島渚が長年追求してきた性と愛の問題体系の中に鋭く描き出す。室内シーンを主として独特な映像世界を展開。", "1986-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CUT", "CUT／カット！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ホラー映画「熱い血」の撮影中に、本当の殺人事件が発生。その12年後、映画学科の学生たちが「熱い血」を完成させようとするが、再び殺人鬼が現れて彼らを抹殺していく。巨大ハサミを操る“スカーマン”なるキャラクターを登場させたサスペンス・ホラーだが、恐怖度はイマイチ。元青春アイドルのM・リングウォルドがポッチャリした体型で、ハリウッド女優役をコミカルに演じている。", "2000-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I Don't Want to Sleep Alone", "黒い眼のオペラ", "", "", "http://image.eiga.k-img.com/images/movie/33897/315x210.jpg?1260", 118, "「楽日」「西瓜」のツァイ・ミンリャン監督が、初めて祖国マレーシアを舞台に描いた愛の物語。賭け事によるトラブルから瀕死の重傷を負ったシャオカンは、行き倒れる寸前で労働者の青年ラワンに救われる。シャオカンの世話をするうち、ラワンは次第に彼に惹かれていく。一方、食堂で働く女性シャンチーは、経営者の寝たきりの息子を看病している。ある日、シャオカンとシャンチーは運命的な出会いを果たすが……。", "2007-03-24", "53,2,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エンプティー・ブルー", "エンプティー・ブルー", "", "", "http://image.eiga.k-img.com/images/movie/54367/315x210.jpg?3725", 130, "主要スタッフわずか3人だけで、5年の歳月をかけて製作した異色のドラマ。他人とのコミュニケーションが苦手なタカシは、どこまでも続く階段を上り続けるという夢を毎晩のように見ていたが、ある日の夢の中で1人の少女と出会う。やがて少女との夢の時間に安らぎと救いを見出したタカシは、ひたすら眠りに没頭するようになるが……。", "2009-05-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA VISIONE DEL SABBA", "サバス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "殺人容疑で逮捕された美貌の女性マッダレーナ、彼女は自らを400年間生き続ける魔女と称し、精神鑑定を受けさせられる。担当医の若いデビッドは彼女の魔力に惑わされるかのように、次第に幻想や悪夢にとらわれていく。「ベティ・ブルー／愛と激情の日々」でデビューしたB・ダルが、再び小悪魔的魅力を発揮。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Break Heat・バクの胃袋をひらけ", "Break Heat・バクの胃袋をひらけ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "L.A.のマフィアから持ち去られた 100万ドル。これを手に入れようとするヤクザ・友和興業は犯人メグミの妹分リョウを誘拐。リョウの恋人ショーンは彼女を取り戻すため、組を裏切った辰巳と手を組み、メグミと金を捜し回る……。ベテラン、清水あきひろとアメリカのポルノ男優S・マイケル、人気AV女優、瞳リョウの異色の顔合わせで贈るハードボイルド。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWORD　AND　THE　DRAGON", "キング・ドラゴンの逆襲／　魔竜大戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "伝説の英雄“イリア・ムウロメツ”の物語を、巨費と４年の歳月を費やして映像化したスペクタクル・ファンタジー。恋人を魔王の軍隊によってさらわれたイリアは、神の保護を受け、恋人を取り戻す旅に出る。やがて恋人はイリアの息子を生むが、その子は魔王の子として育てられる。", "1959-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("／THE　UNINVITED", "４人の食卓", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "日本でも大ヒットした「猟奇的な彼女」で一躍韓国トップ女優となったJ・ジヒョンの２年ぶりの主演作。新人女性監督イ・スヨンがメガホンを取った心理サスペンス・ホラーだ。ジヒョンはキュートなイメージから一転、複雑な心理に揺れる影のある女性を体当たりで怪演する。結婚を控えたジョンウォン。４人がけの食卓を購入した日、フッと見るとそこには地下鉄で毒殺されたはずの二人の少女が座っていた。それ以来、恐ろしい幻覚は繰り返された。そんな中、暗い過去を持つ謎めいた女性と出会い……。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAPLO´ GYERMEKEIMNEK", "日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "1947年、亡命先のモスクワから16歳の少女ユーリはブダペストに帰ってきた。モスクワで両親を亡くした彼女は、新聞社勤めから刑務所長に転身した厳格なマグダという女性に引き取られるのだが……。メーサーロシュ監督の自伝的要素の強い作品。", "1982-01-01", "30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("春だドリフだ全員集合!!", "春だドリフだ全員集合!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "売れない落語家・いかりや亭長楽は、“小柳ルミ子を連れてくる”と彼女の大ファンの県会議員から30万円を受け取り上京するが、交渉は失敗し友人の今川焼屋の２階を借りる。長楽は隣に住むヤクザ者の妹・文子に惚れてしまうが……。この作品から“男はつらいよ”シリーズの併映作品となる。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本大侠客", "日本大侠客", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "九州の侠客議員として知られる吉田磯吉の長男・敬太郎が書いた自伝をもとに、マキノ雅広監督が鶴田浩二主演で映画化した任侠映画。明治中期の北九州の港町・若松を舞台に、若き日の吉田磯吉が沖仲仕の側に立ち、賃金の上前をはねる悪徳ヤクザ一家に敢然と闘いを挑む。藤純子が、主人公のピンチを救う鉄火芸者・お竜に扮して画面をさらう。この役は後年、藤純子の当たり役となった“緋牡丹博徒”シリーズの女侠客・緋牡丹のお竜の原型となった。1970年の「玄海遊侠伝・破れかぶれ」では勝新太郎が吉田磯吉に扮し、鶴田浩二とは対照的な主人公を熱演した。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギャングスタ", "ギャングスタ", "", "", "http://image.eiga.k-img.com/images/movie/55787/315x210.jpg?2352", 87, "人気作家・新堂冬樹によるヤンキー小説を、「草食系男子。」の崎本大海主演で映画化。ヤンキーたちあこがれの高校・明王工業では、校内を制覇した者に与えられる称号“ギャングスタ”を狙う不良たちがケンカに明け暮れる毎日をおくっていた。気合いを入れて登校したケンカの弱い新入生・川谷銀二（崎本）は、女好きのチャラ男・白石力（久保田悠来）と出会う。見かけと違ってケンカの強い力に惚れこんだ銀二は、彼をギャングスタの座に就かせるべく奮闘する。", "2011-02-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ADIOS GRINGO", "続・さすらいの一匹狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "G・ジェンマの魅力が余すところなく収められたマカロニ・ウエスタン。牛泥棒の汚名を晴らすために旅に出たガンマンの活躍を描く。0.6秒の早射ちや、アクロバット射ちなど、お得意のガンプレイやアクションをふんだんに盛り込んだ娯楽作品。", "1965-01-01", "4,9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Invictus", "インビクタス　負けざる者たち", "", "", "http://image.eiga.k-img.com/images/movie/54387/315x210.jpg?2391", 134, "南アフリカ共和国のネルソン・マンデラ大統領と同国代表ラグビーチームの白人キャプテンがワールドカップ制覇へ向け奮闘する姿を、クリント・イーストウッド監督が描いた人間ドラマ。1994年、南アフリカ初の黒人大統領となったマンデラは、アパルトヘイトによる人種差別や経済格差をなくし、国をまとめるためには、95年に自国で開催されるラグビーワールドカップでの優勝が必要と感じ、代表チームのキャプテン、ピナールとの接触を図る……。主演はモーガン・フリーマンとマット・デイモン。", "2010-02-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Too Late Blues", "よみがえるブルース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　ジャズ・バンドでピアニストを務めているジョンは女性シンガーのジェスと愛し合う仲に。しかしそれがきっかけでエージェントのベニーと口論になってしまう。やがてバンド仲間とも喧嘩し、ジェスとも別れることに。その後、ジョンはジェスと再会を果たすが……。ミュージシャンの成功と挫折を描くドラマ。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROLLING THUNDER", "ローリング・サンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "８年にも及ぶ北ベトナムでの捕虜生活から故郷に帰ったチャールズとジョニー。だが、彼らの心は深く閉ざされてしまっていた。しかし、ある日、チャールズはならず者に妻子を殺され、自分も右手をつぶされた。彼の中に、血みどろの復讐心が芽ばえてくる……。ハードボイルドの佳作「組織」に続くJ・フリンの衝撃作。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まっぴら社員遊侠伝", "まっぴら社員遊侠伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「悪党社員遊侠伝」「昭和元禄ハレンチ節」に続く“松竹ハレンチ喜劇”ものの第３作。下着のトップメーカーの名うての４人のハレンチ社員たちは、社の重役陣が全員二号を囲っていることを知り、本妻と二号たちを利用して金もうけをたくらむ。ピカレスク的な味わいを持つ作品。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドロップ", "ドロップ", "", "", "http://image.eiga.k-img.com/images/movie/53737/315x210.jpg?2824", 122, "人気お笑いコンビ・品川庄司の品川祐が、「品川ヒロシ」名義で青春時代の実体験を綴ったベストセラー小説を、自らの監督・脚本で映画化。不良に憧れて私立のお坊ちゃん中学校から公立狛江北中学校に転校した信濃川ヒロシは、転校初日からアイドル顔のカリスマ不良・達也にあっさりブチのめされるが、口のうまい性格が気に入られ、不良グループに仲間入りする。成宮寛貴と水嶋ヒロがダブル主演、上地雄輔、中越典子らが脇を固める。", "2009-03-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こちら葛飾区亀有公園前派出所　THE MOVIE２／UFO襲来！トルネード大作戦!!", "こちら葛飾区亀有公園前派出所　THE MOVIE２／UFO襲来！トルネード大作戦!!", "", "", "http://ecx.images-amazon.com/images/I/31AK4T1603L._SL210_.jpg", 109, "秋本治原作の人気漫画の劇場版第２作。破天荒な警察官の両さんこと、両津勘吉巡査長が巻き起こす珍騒動を、溢れんばかりのギャグの洪水で描く。今回の両さんは、お馴染みの美女とともに東京とハワイを舞台に大活躍。平和な下町・亀有に突如現れたミステリーサークルやUFO目撃情報に巻き込まれながら、ある謎に迫っていく。亀有にUFO目撃騒動が勃発。さらにミステリーサークルも現れ、町は大騒ぎ。一方、お馴染みの派出所メンバーは、両津を残して、慰安旅行でハワイへ。あきらめの悪い両津は悪知恵を駆使し、ハワイ行きを講じる。", "2003-12-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MUSIC BOX", "ミュージック・ボックス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "社会派監督C・ガヴラスによる、法廷サスペンス。女性弁護士アン・タルボットは、ハンガリー移民の父ラズロットが、50年前のユダヤ人虐殺犯の容疑をかけられた事件を担当する。真相を調べるうちに明かされる知られざる父の一面。彼女は、父の無実を証明するため、彼の祖国ハンガリーへ飛ぶが……。娘と弁護士という立ち場で揺れるアンをJ・ラングが熱演。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Agents Secrets", "スパイ・バウンド", "", "", "http://image.eiga.k-img.com/images/movie/1667/315x210.jpg?3944", 110, "85年にフランスで起きた実際の事件、虹の戦士号爆破事件をモチーフにしたスパイ・アクション。フランス情報機関のエージェント、リザとジョルジュは、武器密輸船の爆破指令を受け、夫婦を装って作戦の遂行に向かう。リザは今回の任務を最後に仕事を辞めたいと考えていたが、そんな彼女にある罠が仕掛けられる。主演は実生活でも夫婦のモニカ・ベルッチとバンサン・カッセル。カルティエ、ルイ・ヴィトンなどが衣装協力。", "2005-01-29", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("組織暴力・兄弟盃", "組織暴力・兄弟盃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "復員した木島と特攻くずれの大場は、荒廃した銀座で縄張りを広げるが、木島は殺人でムショ送りとなる。出所後再び大場と組むものの、右翼の大物のワナにハメられてしまう。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・女の警察", "続・女の警察", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "あるときは夜の蝶のスカウト、あるときは“女の警察”と呼ばれる篝正秋は、自分の店のホステスを引き抜こうとしたクラブ“アロン”を訪れた。この店のマダム久代は、かつての正秋の恋人の姉であった……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nostalgia de la luz", "光、ノスタルジア", "", "", "http://image.eiga.k-img.com/images/movie/57277/315x210.jpg?1722", 90, "世界中から天文学者が集まるチリのアタカマ砂漠と、そこにとどめられたさまざまな“記憶”に焦点を当てたドキュメンタリー。チリ北部、太平洋とアンデス山脈の間を走り、標高3000メートルに位置するアタカマ砂漠は、湿気や大地の揺らぎが少なく天文観測に適しており、世界中から天文学者が集まる場所として知られている。一方で、古代人のミイラや探検者、採掘鉱夫たちの亡がらがいまも手付かずに残っている。生命の起源を求めて天を仰ぐ学者たちのかたわらで、行方不明の肉親の遺骨を探して地を掘り返す人々がいるアタカマ砂漠の姿を、壮大な宇宙の映像とともにとらえた。", "2012-12-31", "2,7,77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Swamp Thing", "怪人スワンプ・シング／影のヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "　政府の研究所で働くアレック・ホランド博士は優秀な頭脳を持つ男。秘密の研究を進めていたところ、悪人の襲撃による事故がきっかけで、彼は植物状の皮膚を持つ緑色の怪人スワンプシングとなり、悪人たちと戦うことに……。DCコミックスのモンスターヒーローを実写映画化。89年には続編も製作された。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE THIEF OF BAGDAD", "バグダッドの盗賊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "アラビアンナイトの一挿話を大プロデューサー、コルダが製作したファンタジー。魔法のカーペット、魔法のランプから出てくる巨人、おもちゃの空飛ぶ馬などが次々と登場。戦時中の製作にもかかわらず巨費を投じた特撮が素晴らしい効果を上げている。1940年代ファンタジー映画の代表的な一編。", "1940-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Road", "ザ・ロード", "", "", "http://image.eiga.k-img.com/images/movie/55487/315x210.jpg?3430", 112, "「ノーカントリー」の原作者として知られる現代米文学の巨匠コーマック・マッカーシーによるピュリッツアー賞受賞の同名ベストセラー小説を、ビゴ・モーテンセン主演で映画化したロードムービー。ほとんどの生物が死に絶え、文明が崩壊した近未来のアメリカを舞台に、父親と幼い一人息子が漂流する姿を描く。監督は「プロポジション　血の誓約」のジョン・ヒルコート。共演にシャーリーズ・セロン、ロバート・デュバル、ガイ・ピアース。", "2010-06-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATAL　DECEPTION：MRS．LEE　HARVEY　OSWALD", "暗殺調書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "あのケネディ暗殺事件を新しい角度から見据えた社会派サスペンスのTVムービー。1963年11月22日、ケネディ大統領が暗殺。実行犯と見なされたオズワルドが逮捕され、彼は射殺されてしまう。15年後、学者のリプトンは新たに公表された当時の写真から、実際の狙撃犯はオズワルド以外にいるという結論を導き出す。そして、彼は二人の娘と暮らすオズワルドの妻マリーナに接触し、彼女の記憶の断片を組み合わさせて謎を解き明かそうとする。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stone", "ストーン", "", "", "http://image.eiga.k-img.com/images/movie/55727/315x210.jpg?3346", 105, "ロバート・デ・ニーロ、エドワード・ノートン、ミラ・ジョボビッチ共演によるクライム・サスペンス。定年間近の真面目な仮釈放管理官ジャック（デ・ニーロ）と、最後に担当することになった放火殺人犯ストーン（ノートン）。仮釈放を切望するストーンが、自身の魅力的な妻ルセッタにジャックを誘惑させて操ろうとしたことから、運命が狂いはじめる。メガホンをとるのは「夫以外の選択肢」のジョン・カーラン監督。", "2010-10-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOOLIGANS", "フーリガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "過激な行動を伴うサッカー応援で知られるフーリガンの世界に魅せらた青年の苦悩と成長を追った青春ドラマ。暴力的な世界に身を委ねつつ、再生を図ろうとする主人公の姿が感動的。ハーバード大学を退学処分になったジャーナリスト志望のマットは、姉のいるロンドンへ行く。そこで熱狂的なフーリガンのピートと出会ったマットは、一緒にサッカー観戦へ行くが……。", "2006-06-17", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TROOP BEVERLY HILLS", "ガールスカウト ビバリーヒルズ版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "リッチな街ビバリーヒルズを舞台に、何不自由なく暮らす百万長者の奥様が、ガールスカウト・ビバリーヒルズ隊の隊長に応募したことから巻き起こる珍騒動を描いたスラップスティック・コメディ。奥様が身にまとう金持ちファッション、ビバリーヒルズの高級邸宅街、ロールスロイスなどの名車も続々登場という、見ているだけで豪華な気分にさせてくれるシアワセな作品。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("疆屍叔叔／MR.VAMPIRE IV", "霊幻道士・完結編／最後の霊戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "シリーズ４作目で、４年続いた「霊幻道士」の完結編。お隣り同士ながら、仲の悪い霊幻道士とおとぼけ和尚。しかし、道士の弟子は和尚の弟子に恋している。そんな４人のところへ強力なキョンシーが現れたことから道士と和尚は一致団結、奇想天外な魔術合戦を繰り広げていく。", "1988-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("毎日が夏休み", "毎日が夏休み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "登校拒否の娘と出社拒否の義父がヨロズ代行業を始める。娘の成績優秀と夫のエリートぶりが自慢の種だったママは、ついにキレてクラブのホステスに……。家族崩壊の危機というのに、それを冷めて眺めている大人のような子供の目、あるいは子供のような大人の目がある。その距離感、軽み。事態が悪化するごとに、映画がきびきびと弾み出す。新興住宅地の淡い空気感を見事につかんで、金子監督はライト・コメディの資質を全開させた。シラーッとしながら大人を大目に見てやっている感じや、自分が思いがけなく必要とされていることのわくわく気分を演じた、13歳のヒロイン・スギナ役を佐伯日菜子が好演、佐野と風吹が佐伯を飄々とした味で支えている。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Let's Rock Again!", "レッツ・ロック・アゲイン！", "", "", "http://image.eiga.k-img.com/images/movie/1157/315x210.jpg?3989", 90, "ロンドン・パンクの代表バンドのひとつ、クラッシュのメンバーで、2年前に急逝したジョー・ストラマーの最期をとらえたドキュメント。監督は「レポマン」「ストレート・トゥ・ヘル」で彼と共演したディック・ルード。", "2004-11-06", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DEVIL AT ４ O’CLOCK", "４時の悪魔", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "火山の噴火を背景に様々な人々の脱出行を描いたスリリングなドラマ。３人の凶悪犯を乗せた水上機が、太平洋の小さな島タルアに着陸した。島から追い出されたドナウン神父が飛行機に乗り込もうとする直前、島の火山が噴火。山の病院に残された子供たちを救うため、神父と囚人たちは山へと向かうのだが……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ひばりの陽気な天使", "ひばりの陽気な天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "美空ひばりの歌を存分に楽しめる、ミュージカル・タッチの明朗人間ドラマ。牧師の娘で、大のスイング・ジャズ・ファンのゆかり。彼女はある日、貧しい兄弟と出会い、自分と同じ、母なし子だとわかって同情する。ゆかりは彼らのために、豪華な誕生日のパーティを開いてやろうとするが……。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ENSAYO DE UN CRIMEN", "アルチバルド・デラクルスの犯罪的人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "ブニュエルのメキシコ時代の作品で、アルモドバル監督の「ライブ・フレッシュ」に引用されるなど、最も愛すべき傑作として語られてきた。日本では40年の時を超えてついに公開された。少年アルチバルドは美しい家庭教師からオルゴールを鳴らせば思い通りに人を殺せると教えられる。その彼女は言葉通りにオルゴールの音の中で死んでしまった。大人になったアルチバルドは、革命騒ぎで失われていたオルゴールと再会。その日から、彼の周りで美女たちが次々に死んでいく……。", "1996-11-23", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZENA S KRAJOLIKOM", "遺言 女のいる風景", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "美しい映像で、閉鎖的な社会での芸術家の受難を描いた田園叙情詩。田園の絵を描くことを趣味とする森林監督官が、ボスニア地方のある村に赴任してくる。しかし、村の主婦たちの裸像も描いていることが知れ、男たちに袋だたきにされる。やがて村の女と結婚するが、その妻の裸像も描いたために非難され、家を出た彼は非業の死を遂げるのだった。", "1976-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("8ミリメートル", "8ミリメートル", "", "", "http://image.eiga.k-img.com/images/movie/55267/315x210.jpg?1425", 0, "人気デュオ「スキマスイッチ」が2009年に発表したバラード3部作「Aアングル」「Bアングル」「8ミリメートル」をモチーフに映像化したラブストーリー。ケータイ音楽ドラマ「DOR＠MO」で配信され、後に劇場公開される。春夫はあやまってアパートから転落し、命を落としてしまう。春男の元恋人である品子の元にも不幸の知らせが入るが、品子はなぜか春男の顔を思い出せずにいた……。", "2010-02-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUENA VISTA SOCIAL CLUB", "ブエナ・ビスタ・ソシアル・クラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "ベンダース監督が「エンド・オブ・バイオレンス」でふたたび組んだライ・クーダーに同行、彼がプロデュースしたグラミー賞受賞アルバム「ブエナ・ビスタ・ソシアル・クラブ」に参加したミュージシャン達とのその後を追ったドキュメンタリー。熱い日差しのキューバで再集結してのレコーディング、アムステルダムそしてニューヨークのカーネギーホールでの栄光のステージが世界中の感動を呼び、各地でロングランを記録して空前のキューバ・ブームを巻き起こした大ヒット作。", "2000-01-15", "7,1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("6號出口", "ウエスト・ゲートNo.6", "", "", "http://image.eiga.k-img.com/images/movie/54017/315x210.jpg?3949", 98, "台湾シネマ・コレクション2008上映作品。台北の若者の町、西門町で、気ままに生きる青年2人とどこか影のある少女2人が織り成す青春劇。韓国人の父親に結婚を強いられているフィオナは、その反発から仲間と夜遊びするようになる。一方、失恋で自暴自棄になったビビアンが失踪すると、街では女子高校生連続失踪事件が発覚し……。台湾のTVドラマで人気のエディ・ポンとイーサン・ルァンが出演。", "2008-08-23", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Eros", "愛の神、エロス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「情事」「欲望」のミケランジェロ・アントニオーニ、「花様年華」「ブエノスアイレス」のウォン・カーウァイ、「セックスと嘘とビデオテープ」のスティーブン・ソダーバーグが、愛とエロティシズムをテーマにそれぞれの作品を撮り上げたオムニバス。カーウァイ監督の「若き仕立屋の恋」（トニー・レオン、コン・リー主演）、ソダーバーグ監督の「ペンローズの悩み」（ロバート・ダウニー・Jr.、アラン・アーキン主演）、アントニオーニ監督の「危険な道筋」（クリストファー・ブッフホルツ主演）の3編で構成される。", "2005-04-16", "1,4,2,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("りゃんこの彌太郎", "りゃんこの彌太郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "子母沢寛の時代小説を原作に、マキノ雅広監督が描いた娯楽時代劇。絹織物の産地・桐生と足利に織り娘を周旋する、川股の奉公人宿に現れたりゃんこの彌太郎は、両者の織り娘争奪騒ぎに巻き込まれていく。足利に織り娘を移らせまいと図る桐生側の悪どいやり方に彌太郎の男気がやがて爆発する。", "1955-01-01", "76", 0, NOW(), NOW());
