INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Capitalism: A Love Story", "キャピタリズム　マネーは踊る", "", "", "http://image.eiga.k-img.com/images/movie/54747/315x210.jpg?1749", 127, "「ロジャー＆ミー」「ボーリング・フォー・コロンバイン」「華氏911」「シッコ」など常にコンテンポラリーな問題に光を当ててきたドキュメンタリー作家マイケル・ムーアの最新作。今回はサブプライム問題やリーマンショックといった経済問題に焦点を当て、ウォール街に棲む強欲資本主義経済の主役たちにアポ無し取材を敢行。米政府と金融界の驚くべき癒着関係をあぶり出していく。", "2009-12-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RICOCHET", "リコシェ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 59, "大ヒットアルバム『レッツ・ダンス』発表直後に行われたシリアス・ムーンライト・ツアーより、香港、シンガポール、バンコクにおけるデヴィッド・ボウイの素顔を映したプライベート・タイムを、ドキュメンタリー・タッチで描く。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BODIES，REST ＆ MOTION", "恋愛の法則", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "空虚感から住居を転々とするニック、彼との生活に不安を抱える同棲相手のベス、ニックへの思いを断ち切れない昔の恋人キャロル、そしてベスに恋をした純朴なシド。４人の若者が織り成す恋模様をユーモラスに描いた、大人の青春映画。主演のB・フォンダの父ピーターが、バイカー役で特別出演。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガラスのヒール　レースクイーンの女神たち The Movie", "ガラスのヒール　レースクイーンの女神たち The Movie", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "グラビアアイドルの浜田翔子が映画初主演を果たしたサスペンスドラマ。京都の漁村で生まれ育った唯は、太りぎみの体型にコンプレックスを抱き、引きこもりとなってしまう。そんな自分を変えようと東京・秋葉原へと旅に出た唯は、そこでスカウトされてレースクイーンを目指すことに。努力の末、美しく変身し人気レースクイーンとして活躍するようになる唯だったが、周囲でレースクイーンたちが次々と失踪する。", "2006-10-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Notorious Bettie Page", "ベティ・ペイジ", "", "", "http://image.eiga.k-img.com/images/movie/53147/315x210.jpg?1973", 91, "50年代に過激なボンテージ・ファッションと天真爛漫な笑顔で世界中の男性を魅了した伝説のピンナップ・ガール、ベティ・ペイジの知られざる半生を、「アメリカン・サイコ」の女性監督メアリー・ハロンが映画化。故郷ナッシュビルで大学進学と結婚に失敗したベティは、新たな人生を求めニューヨークへ。彼女はカメラマンの男性と知り合ったことからモデルとして活動するようになり、大胆な衣装や魅惑的なポーズで一躍人気者となる。", "2007-12-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シロタ家の20世紀", "シロタ家の20世紀", "", "", "http://image.eiga.k-img.com/images/movie/53867/315x210.jpg?3005", 93, "日本国憲法に“男女平等”の条文を書いたベアテ・シロタ・ゴードンのドキュメンタリー「ベアテの贈りもの」を手がけた藤原智子監督が、同作のパリ上映で出会ったベアテの親族による話や資料を基に作りあげたドキュメンタリー。ヨーロッパでピアニストとして成功し、日本でもピアニスト育成に励んだベアテの父レオをはじめ、ノルマンディ上陸作戦やアウシュビッツなど、迫害と戦争の20世紀を駆け抜けたシロタ一族の激動の歴史を辿る。", "2008-09-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アカシアの道", "アカシアの道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "編集者として多忙な日々を送る美和子の元に、長らく会っていない母親、かな子の様子がおかしいという知らせが。かな子はアルツハイマーにかかっており、極度に物忘れが激しくなっていた。やむなく美和子は仕事を在宅でこなせるものに切り替え、母親との同居生活を始めるのだが……。介護問題をモチーフにした迫真の人間ドラマ。理不尽なまでに娘につらくあたる母親と、地獄のような少女時代のトラウマを引きずる娘との葛藤が、シンプルながらも、緊迫感が張りつめた映像の中に展開していく。松岡錠司のシャープな演出、主演女優二人の鬼気迫る演技合戦が圧巻だ。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大学の若大将", "大学の若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "加山雄三主演による“若大将”シリーズの第１作。京南大学水泳部の田沼雄一は、すきやき屋の老舗・田能久の若大将で、父・久太郎と祖母・りきとの３人暮らし。青大将と呼ばれる石山製菓のドラ息子・石山新次郎は、美人OL中里澄子に首ったけだが、彼女は雄一を愛している。雄一は縁談話で澄子との仲が険悪になるが、結局、誤解が解けて元気になり、水泳の大学対抗戦で、見事優勝する。歌もこなし、スポーツも万能という上原謙の息子・加山雄三が、周囲の期待に応えて見事にスターぶりを発揮した。以降、青春ものでは異例のロング・シリーズとして連作されることになる。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD WARRIORS", "ブラッド・ウォリアー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "元海兵隊員のウェスは戦友のキースのもとを訪れるが、妹のカレンから彼の死を知る。ウェスはカレンの面倒を見ることに。ある日、彼女がキースに恨みを持つ男たちに拉致された。救出に向かったウェスが見たものは、カレンを助け出す謎の特殊部隊。その指揮を執っていたのは、死んだはずのキースだった。その日から二人の壮絶な死闘が始まる。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALL ABOUT EVE", "イヴの総て", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "演劇界の大女優に憧れる田舎娘イヴは秘書になるきっかけをつかんでからは、批評家に取り入り、恩人の女優をあざむいてまで舞台に立った。満場拍手の中で夢にまで見たスターの座を得たのだが、彼女はやがて以前の自分とまったく同じような演劇少女が近づいてくるのを知る……。スターの座の内幕を大胆かつ、見ごたえたっぷりに描いた人間ドラマ。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・てなもんや三度笠", "続・てなもんや三度笠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "あんかけの時次郎と小坊主・珍念は“日米通商条約調印”でごったがえす下田の港へやって来る。ここでは町を二分する東屋一家と西屋一家がメイン・ストリートを隔てて冷たいにらみ合いを続けていた。時次郎はハッタリをきかして東西両一家に潜り込むが……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SEA OF SAND", "熱砂の海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "第二次大戦下の北アフリカ戦線。ドイツ軍の燃料庫破壊の任務を遂行するため、400マイルもの灼熱の砂漠を横断する英国の偵察隊。だがその途中、敵にトラックを攻撃され、部隊は手痛い損傷を受ける。砂漠に生き残った英軍兵士は６名。果たして彼らの運命は……。", "1959-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIMONADOVY JOE", "レモネード・ジョー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "「アデラーニック・カーター／プラハの対決」や「カルパテ城の謎」など、コミカルな異色作を発表しているチェコスロバキアの鬼才、O・リプスキー監督のパロディー西部劇。レモネードをこよなく愛する西部のヒーロー、ジョーが、アルコール擁護派の悪党ガンマンたちと、攻防戦を繰り広げるというもの。DVDタイトルは「レモネード・ジョー　或いは、ホース・オペラ」。", "1964-01-01", "28", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤い靴とろくでなし", "赤い靴とろくでなし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "“ろくでなし”シリーズの第２作。ろくでなしコンビのジョーと圭介は、南国のある港町で赤い靴の女・ユリと知り合う。そして彼女からある会社乗っ取りの計画を持ちかけられた彼らは即、実行。まんまと成功して数億円の現ナマをせしめるのだが……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十三人の刺客（2010）", "十三人の刺客（2010）", "", "", "http://image.eiga.k-img.com/images/movie/54797/315x210.jpg?2864", 141, "片岡千恵蔵主演、工藤栄一監督による集団抗争時代劇の傑作を役所広司主演、三池崇史監督でリメイク。江戸時代末期、罪なき民衆に不条理な殺戮を繰り返していた明石藩主・松平斉韶の暴政を訴えるため明石藩江戸家老・間宮が切腹自害する。この事件を受け、幕府内では極秘裏に斉韶暗殺が画策され、御目付役・島田新左衛門（役所）がその命を受ける。新左衛門は早速刺客集めにとりかかるが、彼の前に斉韶の腹心・鬼頭半兵衛が立ちはだかる。斉韶に稲垣吾郎、鬼頭に市村正親のほか、山田孝之、伊勢谷友介ら豪華俳優陣が集結。", "2010-09-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Awake", "アウェイク", "", "", "http://image.eiga.k-img.com/images/movie/53117/315x210.jpg?3153", 85, "富豪の青年クレイトンは何不自由なく暮らしてきたが、心臓に病を抱え移植手術が必要だった。ある日、友人の医師ジャックの根回しでドナーを得たクレイトンは、愛する秘書サムに求婚し、満ち足りた気分のまま手術室へ運ばれる。しかし、全身麻酔から意識が目覚めてしまう“術中覚せい”が起こり、手術の痛みを味わうとともに、驚くべき事実を知ってしまう。ヘイデン・クリステンセン、ジェシカ・アルバが主演する医療サスペンス。", "2011-05-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二階の他人", "二階の他人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "山田洋次の監督昇進第１作。若いサラリーマン夫婦が借金をして、２階建ての家を新築するが、借金返済のため２階を間貸しする。ところが下宿人は下宿代を踏み倒したり、横領犯だったりで騒動が巻き起こる。庶民派コメディだが、この後の活躍を予感させるような山田エッセンスが散りばめられた傑作。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KAMPUCHEA…THE UNTOLD STORY", "殺戮の戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ベトナム戦争終結後も内戦や大国介入など緊張の絶えないインドシナ半島。1978年、カンボジアもベトナム軍の侵攻によって大きな戦火に包まれている。映画は、そんな1978年当時のカンボジアの姿を、一人の少年の目を通して描いたもの。両親をベトナム軍に惨殺され、幼い妹とともに、生まれ育った小さな村から安全なタイへ向かうソンバット少年。戦争の惨状の数々を目のあたりにした彼が、やがて愛国心に目覚め、祖国カンボジア解放のために激烈な戦場に身を投じていく物語。", "1986-01-01", "1,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MONTH OF SUNDAYS", "ミスター・サンデー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "老人ホームで暮らすワーパーの楽しみは、日曜日の娘夫婦の訪問と、親友エイロットとの交流だった。だが妻の死後、娘や孫は彼から遠ざかり、さらにエイロットは健忘症になっていた。親友を失いたくないワーパーに、娘が再会を求め、同居の話を持ち出した。アメリカの良心を描いた心温まる作品。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THAT NIGHT", "恋に焦がれて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "1960年代の封建的な風潮の中で苦悩する若者たちの恋模様を描いた青春ドラマ。父親を亡くして悲しみに暮れる17歳のシェリルは、ボーリング場で働く不良のリックに慰められ、激しい恋に落ちた。しかし、周囲の大人たちは彼らの恋を理解しようとせず、やがてシェリルの妊娠から、その仲を引き裂こうとする。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("終わらない青", "終わらない青", "", "", "http://image.eiga.k-img.com/images/movie/56057/315x210.jpg?1973", 66, "父親から性的虐待を受けている高校生の楓は、うつの母親にも助けてもらえず、心のバランスを保つために日常的にリストカットをしていた。そんなある日、妊娠していることに気づいた楓は出産を決意するが、父親の虐待は日々エスカレートしていき……。20代前半の女性7人に1人が経験しているといわれる自傷行為を題材に、映像作家の緒方貴臣が自主製作で撮り上げた。", "2011-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UTZ", "マイセン幻影", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "『ウィダの総督』で知られるB・チャトウィンの遺作となった小説を映像化。マイセン磁器の魅力に憑かれ、収集のために東奔西走する男のコレクター人生を、物静かなタッチで綴っていく。A・M・スタールが、マイセンの美しさに永遠の真実を見いだす主人公、ウッツ男爵役を好演。", "1992-01-01", "3,4,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("牛の鈴音", "牛の鈴音", "", "", "http://image.eiga.k-img.com/images/movie/54907/315x210.jpg?4508", 78, "79歳の農夫・チェ爺さんは、通常15年ほどの寿命しかない牛と30年もともに仕事をしている。周りの農家が耕作機械を使うなかで、チェ爺さんは頑固に牛と畑を耕し続ける。ある日、獣医から牛の寿命が迫っていることを告げられたチェ爺さんは、献身的に牛の面倒を見続け、遂には自分の体調まで崩してしまう。イ・チュンニョル監督が3年の歳月をかけて撮影したドキュメンタリーで、韓国で300万人の動員を記録した。", "2009-12-19", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　ホタルノヒカリ", "映画　ホタルノヒカリ", "", "", "http://image.eiga.k-img.com/images/movie/56997/315x210.jpg?2662", 110, "ひうらさとるの同名漫画を原作に、綾瀬はるか主演で人気を博したドラマ「ホタルノヒカリ」を映画化。舞台はドラマ版第2弾「ホタルノヒカリ2」から2年後。主人公の“干物女”雨宮蛍が、高野部長とのハネムーンでイタリアへ、初めての海外旅行にでかける。ローマで同じく干物女の冴木莉央と出会った蛍は、莉央とその弟・優が巻き起こす騒動に巻き込まれ、消息を絶ってしまった高野部長を追いかけてローマの街を奔走する。主演の綾瀬ほか、藤木直人、板谷由夏、安田顕らドラマ版キャストが続投。ローマの干物女・莉央を松雪泰子、優を手越祐也が演じる。", "2012-06-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("紅顔の密使", "紅顔の密使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "加藤泰監督が、大川橋蔵を主演に据えて描く時代劇。時は平安、陸奥の反乱を鎮圧するため、朝廷から派遣された密使・歌磨が、策をめぐらして反乱軍をせん滅する。広野で展開する合戦シーンは、外国の戦争スペクタクルを思わせるほどダイナミック。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("課長 島耕作", "課長 島耕作", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "『週刊コミック・モーニング』誌に９年間連載された弘兼憲史の同名の人気漫画を根岸吉太郎が映画化。大手電機メーカー宣伝課の若き課長、島耕作は、仕事人間だが出世志向はなく、常にマイペースで生きている。そんな彼の日常を、彼を取り巻く女性たちとの恋模様も絡めて軽快に綴る。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MACABRE", "首だけの情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "イタリアン・ホラーの俊英L・バーヴァのグロテスクなサイコ・サスペンス。死んだ愛人の首を愛撫する母、そして弟を殺して人喰いに走る娘。実際に起きた猟奇事件をもとに、恐るべきストーリーが展開していく。原題の“マカーブレ”（＝ぞっとする)、という表現がまさにピッタリのすさまじい映画。", "1980-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喧嘩道", "喧嘩道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "『平凡パンチ』で連載していた劇画の映画化。当時は『嗚呼！花の応援団』とともに不良高校生ものの代表格だった。年上の女と同棲している高校生番長が、子分を従えて繰り広げるケンカの毎日。「ビー・バップ・ハイスクール」あたりと比べてみるのも一興。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("METライブビューイング2010-2011 ムソルグスキー「ボリス・ゴドゥノフ」", "METライブビューイング2010-2011 ムソルグスキー「ボリス・ゴドゥノフ」", "", "", "http://image.eiga.k-img.com/images/movie/56467/315x210.jpg?1641", 252, "米ニューヨーク・メトロポリンタン歌劇場（MET）で行われる最新のオペラ公演を劇場のスクリーンで上映する「METライブビューイング」2010～11年シーズンの2作目。子どものいない皇帝フョードル1世が没し、まだ幼い弟のドミトリーも変死。ドミトリー暗殺の疑いの暗殺もある摂政のボリス・ゴドゥノフが皇帝の座に就く。しかし、ボリスにまつわる真相を聞いた青年グレゴリーが野望を抱き、死んだドミトリーになりすまし反乱軍を組織する。ムソルグスキーの代表作としても知られるロシア・オペラの金字塔。", "2010-11-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢", "夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "黒澤明が、自分の見た夢をもとに撮りあげた全8話で構成されるオムニバス作品。黒澤を師と仰ぐスティーヴン・スピルバーグとジョージ・ルーカスが製作に協力し、ワーナー・ブラザースが配給を担当、まさに世界のクロサワならではのスケールの大きな作品。様々な夢に不安と希望を織り交ぜ、文明社会への批判と人間の自然とのかかわりの大切さを説いたこの作品は、黒澤明が一貫して追及してきたヒューマニズムの結晶といえる。ルーカスのILM社よる特撮の他、ハイビジョン・システムでの合成を導入。幻想的な雰囲気の映像と豪華なキャストで話題となった。", "", "76,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pak-Poe 朴保", "Pak-Poe 朴保", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "韓国人の父と日本人の母の間に生まれ、反戦や環境問題、多民族共生などをテーマに歌い続ける在日2世のシンガーソングライター、パクポーの活動を追ったドキュメンタリー。「未来世紀ニシナリ」など人権や差別を扱ったドキュメンタリー作品で高く評価されてきた田中幸夫監督が、10年以上の歳月をかけて取材を敢行。世界各地でのライブの模様やCDの制作現場などを捉え、孤高のアーティストの生きざまに迫る。", "2009-07-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Planet of the Apes", "PLANET OF THE APES 猿の惑星", "", "", "http://image.eiga.k-img.com/images/movie/1007/315x210.jpg?3167", 114, "2029年。ある惑星に不時着した宇宙飛行士のレオ。熱く燃え上がったポッドからどうにか脱出した彼を待っていたのは、逃げまどう人間たちと、その人間を捕獲しようとする猿たちの姿だった。この惑星は知性を持った猿が支配する“猿の惑星”だったのだ。", "2001-07-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("全身と小指", "全身と小指", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "近親相姦という衝撃的なテーマを題材に、男女の恋愛感のすれ違いや純愛を真摯に描く。海辺の田舎町の瑞々しい夏の景色が狂おしいほどの愛を幻想的に見せていて、不思議な余韻を残す。妹と肉体関係を持ってしまった過去に悩む純は、記憶障害のふりをして都会で無気力な生活を送っていた。カウンセラーの勧めで妹・久美と再会した純だったが、封印した想いが再燃し……。", "2006-07-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAWKS", "この命尽きるまで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "不治の病を宣告された二人の男が、病院を脱走しアムステルダムへと旅立つ。死への恐怖におびえながらも、人生を楽しもうとするその姿は時には笑いをも誘う。独特のブラックでシニカルな味を持つ、コミカルなロード・ムービー。第４代ジェームズ・ボンドのT・ダルトンとA・エドワーズが二人を演じる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT STARTED IN NAPLES", "ナポリ湾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "イタリアの大女優ローレンと、当時のアメリカを代表するゲーブルが豪華共演を果たしたラブ・コメディの快作。イタリアのネオ・リアリスモの巨匠監督デ・シーカが、女に惑う弁護士役で出演した。イタリアの美しい風景も必見の仕上がり。アメリカの弁護士が死んだ兄の財産整理のためにイタリアへ。そこで、兄の遺児と彼を育てる兄の妻の姉に出会うが、姉は子供を放任していた。", "1960-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の遊侠伝", "花の遊侠伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "歌舞伎や講談の人気者、河内山宗俊、金子市之丞、片岡直次郎、暗闇の丑松が活躍する明朗時代劇。宗俊は国元に帰るという侍に、シビンを名器“夜の雨”だと偽って売りつけ、これがあとで大騒動に。直次郎は、出世したと故郷の母に嘘をつき、上京した母の前で、皆の協力によって目付に化ける。と、４人のエピソードをコミカルに散りばめた、楽しい作品に仕上がっている。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SIBERIA", "アムス→シベリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ドラッグや性に寛容的な自由の町、オランダのアムステルダムを舞台に描くポップなラブ・ストーリー。二人組の青年とミステリアスな旅の女の子、それぞれの思惑が絡み合う恋模様を軽快に綴る。クールなヒューホと、お人好しのゴーフは、外国から来た女の子をナンパし、一夜の恋とお金を奪う青年コンビ。彼らはいつものように二人組の女の子に声をかけるが、ゴーフがシベリアから来たという美少女ララに恋をしてしまったことから、事態は一転。ララが二人の部屋に転がり込んで、３人はだましだまされの三角関係に陥っていく。", "1998-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("腑抜けども、悲しみの愛を見せろ", "腑抜けども、悲しみの愛を見せろ", "", "", "http://image.eiga.k-img.com/images/movie/34047/315x210.jpg?1378", 112, "「劇団、本谷有希子」の主催者で作家としても活躍する本谷有希子の大ヒット戯曲を、佐藤江梨子主演で映画化。女優を目指して上京したものの全く芽が出ない自意識過剰の勘違い女・澄伽、かつて澄伽の秘密を漫画に描き暴露してしまったために彼女から壮絶ないびりを受ける妹・清深、姉妹の間で板ばさみになってしまう兄・宍道、お人好しの兄嫁・待子。澄伽の帰省をきっかけに巻き起こる一触即発の人間模様を、ブラック・ユーモアたっぷりに描く。", "2007-07-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROMANCING THE STONE", "ロマンシング・ストーン 秘宝の谷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "姉が誘拐されたことを知った女流作家ジョーンは、救出のため単身南米コロンビアへ。彼女を待ち受けていたのは……。巨大なエメラルドをめぐって繰り広げられる大冒険活劇。スリルに富んだテンポの良い演出でヒットし、1985年には続編も製作された。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ACES HIGH", "スカイエース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "第一次世界大戦中の英国空軍を舞台に、新たに赴任してきた一人の新兵の眼を通して軍隊の虚像と実像を描いたドラマ。死の戦闘を前に酒に溺れる上官、ノイローゼで飛行機を操作できぬ兵士たちの苦悩が、ゴールド監督の手堅い演出で語られる。", "1976-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA CITE´ DES ENFANTS PERDUS", "ロスト・チルドレン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "果てしない闇夜に包まれた港町。夜な夜な子供たちを拉致し夢を盗む“夢泥棒”のマッドサイエンティストから、幼い弟を取り戻そうとする心優しき怪力男ワン、そして彼に淡い恋心を寄せる美少女ミエット。彼女はクローンやシャム双生児、一つ目教団らと対決し、暗黒の海に浮かぶ敵地へと乗り込む……。近未来を舞台に、不器用な怪力男と孤独なストーリートキッズとのラブ・ストーリーを美しくも不思議な映像で描く。衣装をJ・P・ゴルチエが担当。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星砂の島のちいさな天使　マーメイドスマイル", "星砂の島のちいさな天使　マーメイドスマイル", "", "", "http://image.eiga.k-img.com/images/movie/55317/315x210.jpg?2780", 90, "沖縄県にある人口300人の小さな島・竹富島。そこに暮らす光一と瞬一の兄弟はある日、砂浜に倒れていた不思議な少女・美海を見つける。美海はなりゆきから光一の実家が営む民宿でアルバイトを始めることになり、たちまち島の人気者となる。しかし、瞬一に思いを寄せるユカは、“ニライカナイの人魚伝説”から美海の正体を人魚だと言い張り、それを聞いた光一は美海を“ミス人魚”として島の広告塔に利用しようと画策する。D−BOYSの牧田哲也、三上真史、飯田里穂らが出演。", "2010-06-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神勇双炮續集／ROSA", "ユン・ピョウin ポリス・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "職務に熱心なあまり、つい大暴れしてしまう刑事コンビが、ケンカを繰り返しながら暗黒街のボスを相手に大活躍。香港映画界のヒーロー、Y・ピョウとシンガー・ソングライターのR・ローがコンビを組んだポリス・アクション。しゃれた会話やテンポの良いギャグなど、さながら“香港版「あぶない刑事」”。公開時のタイトルは「ユン・ピョウ in ポリス・ストーリー」だが、ジャッキー・チェン主演「ポリス・ストーリー」シリーズとは関係ない。ビデオ発売時タイトルは「香港極楽コップス 俺たちに明日はある!?」。", "1986-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美男（イケメン）ですね　劇場編集版", "美男（イケメン）ですね　劇場編集版", "", "", "http://image.eiga.k-img.com/images/movie/56277/315x210.jpg?4231", 124, "人気俳優チャン・グンソクの主演で大ヒットしたドラマ「美男（イケメン）ですね」（2009）を劇場用に再編集して公開。シスターになるため修道院で修行していたミニョはある日、双子の兄ミナムが加入する予定だったバンド「A.N.JELL」のマネージャーから、ミナムの代役を頼まれる。加入直前で都合がつかなくなってしまった兄が、母を探すためにバンドに入るつもりだったことを知り、ミニョは代役をつとめる決意をするが……。", "2011-05-06", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jackass Number Two", "jackass number two", "", "", "http://image.eiga.k-img.com/images/movie/33807/315x210.jpg?3705", 92, "超過激パフォーマンスでおなじみのMTV発人気番組「jackass」の映画版第2弾。ジョニー・ノックスビルらメンバーたちが、尻に焼印を押されたりサメ釣りのエサになったりと、前作を上回る危険でお下劣なチャレンジを次々と繰り広げる。前作の成功により製作費も大幅に上がり、大掛かりなセットやインドロケの敢行などスケールアップ。映画監督のジョン・ウォーターズや俳優のルーク・ウィルソンら豪華ゲストも出演。", "2007-01-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blood Work", "ブラッド・ワーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "", "2002-12-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUTJESKA", "風雪の太陽", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "全ヨーロッパを制圧し、ソ連にさえも進攻をかけていた1931年のナチス・ドイツ。しかし、その中でただ一ヵ国だけ、自由圏を守っていた国があった。最後まで自由を守るために戦ったユーゴスラビア人民解放軍とナチス・ドイツの戦いを、解放軍を率いる主人公の目を通して描いた戦争映画。モスクワ映画祭で審査員特別賞を受賞。", "1973-01-01", "34", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三百六十五夜・総集編", "三百六十五夜・総集編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "小島政二郎の雑誌連載小説を市川崑が演出した恋愛ドラマ。主人公・川北小六は、縁談を嫌って大阪の実家を飛び出し東京へやって来たが、下宿した邸の娘・照子と恋仲になる。しかし、照子の純潔を疑い、小六は大阪へ帰ってしまうという前編（東京編）と、すったもんだのあげく小六と照子がようやく結ばれる後編（大阪編）からなる。", "1948-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mad Dog Morgan", "デニス・ホッパーのマッド・ドック・モーガン 賞金首", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1860年代、ゴールドラッシュに沸くオーストラリア。親友を殺した警官を憎み、強盗や殺人を繰り返すアウトロー、モーガン。だが、彼は原住民アボリジニと行動をともにする義賊ガンマンでもあった。オーストラリア版西部劇ともいうべき味わいが楽しめる。", "1976-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FLAME OF NEW ORLEANS", "焔の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "19世紀末のニューオリンズに、ヨーロッパ貴族の末裔だという妖しい美女が流れて来る。色香に迷った銀行家と若い船長を両天秤にかけ、彼女は悠々とした生活を続けるが……。フランス映画の名匠、R・クレールの渡米第１作となった純愛コメディで、ほんの“肩ならし”といった感じの演出が逆に素晴らしい。", "1941-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Dark Half", "ダーク・ハーフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "　純文学作家のサド・ボーモントには、もうひとつの顔があった。それは、人気バイオレンス作家のジョージ・スターク。しかしその正体が暴かれた時、サドはジョージ名義での執筆をやめる決意をし、彼を葬ってしまう。やがてサドの周囲で残忍な殺人事件が次々と起こり、彼がその容疑者と目されるが……。ロメロ監督がスティーブン・キングの原作小説を得て作り上げたサイコスリラー。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Sisterhood of the Traveling Pants", "旅するジーンズと16歳の夏", "", "", "http://image.eiga.k-img.com/images/movie/1657/315x210.jpg?3343", 118, "赤ん坊の頃から16歳になる今までずっと仲良しの少女4人組が、生まれて初めて別々に過ごす夏休み。ティビーはスーパーでバイト、リーナは祖父母の済むギリシャへ、カルメンは別居している父の元へ、ブリジットはメキシコのサッカーキャンプへ。4人は忘れられない出来事を体験していく。「ヒー・セッド、シー・セッド／彼の言い分、彼女の言い分」のケン・クワピス監督が全米ベストセラー小説「トラベリング・パンツ」を映画化。", "2005-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Renegade Justice", "沈黙の報復", "", "", "http://image.eiga.k-img.com/images/movie/53077/315x210.jpg?1496", 92, "スティーブン・セガールが息子を殺されて復讐の鬼となる父親に扮したアクション。「沈黙の脱獄」「沈黙の傭兵」のドン・E・ファンルロイ監督がメガホンを取る。潜入捜査官の息子を何者かに殺害されたサイモン。犯人への復讐を誓った彼は、ギャングが牛耳る危険な地域にひとり足を踏み入れるが……。“絶叫計画”シリーズなどのコメディ俳優エディ・グリフィンが冷酷なギャングのリーダーを演じている。", "2007-11-24", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STAR TREK: GENERATIONS", "ジェネレーションズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「スター・トレック」のキャラクター、設定をリニューアルした新シリーズ。最新型のUSSエンタープライズ号“1701−D”の冒険を描く。旧地球歴2371年、ピカード艦長をはじめとする７人が宇宙の秩序と平和を守るため航海に出発した。同艦はエル・オーリアン星からの救済信号をキャッチし、カーク提督の協力のもと救助活動を開始する。だが、この惑星のドクター・ソランが永遠の命が約束される星へ行くために恒星を爆破消滅させようとしており……。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH SCHOOL HIGH", "ハイスクール・ハイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "“裸の銃〈ガン〉を持つ男”シリーズのD・ザッカー製作、脚本による学園コメディ。生徒にナメられている落ちこぼれ教師クラークは理想に燃えてスラム街にある最悪の高校に赴任するが……。生徒による教師の誘拐が横行するほど荒廃した高校で、何とか生徒にやる気を起こさせようとするクラークの奮闘を、ザッカーらしくギャグ満載で描いていく。主人公を演じたサタデーナイトライブ出身のJ・ロビッツのとぼけた演技が笑いを誘う。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太平洋ひとりぼっち", "太平洋ひとりぼっち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1962年の夏、弱冠22歳の一人の青年が、小型ヨットを駆って94日間の太平洋横断を成功させた。その青年・堀江謙一の、日記風の同名の手記を原作にして、市川崑が演出した青春冒険映画。無風状態の大阪湾内を１日半も迷走した“マーメイド号”は一転してシケで大荒れの海原で悪戦苦闘する。やっと太平洋に出たと思えば今度は台風。やがて水は腐り、食料も不足気味になり、体力も消耗し尽くす。しかし目指すサンフランシスコは目前に迫っていた。一人の青年が壮挙を成し遂げる様子を、スリルとユーモアで描く感動的作品。遠くに金門橋を見つけた時の狂わんばかりの歓喜が、観る者に深い感動を呼び起こさずにはおかない。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("徳川いれずみ師 責め地獄", "徳川いれずみ師 責め地獄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "石井輝男監督が贈る“異常性愛路線”シリーズの第６弾。凄まじい拷問がさまざまな所で行われたり、信じられない刑罰が執行されていた江戸時代を背景に、江戸風俗のさらに奥のアンダー・ワールドを描き出す。いれずみ女ばかりの売春宿にヒロインは奉公したが、そこには彼女の体に惚れる女主人がいた。そして、将軍が見物するいれずみ彫り大会にモデルとして参加させられてしまう。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("長江7号", "ミラクル7号", "", "", "http://image.eiga.k-img.com/images/movie/53197/315x210.jpg?2042", 87, "「少林サッカー」「カンフーハッスル」のチャウ・シンチー監督・主演作。謎の地球外生命体との交流を通して、貧しい父子がより深い絆で結ばれる様子を、シンチー流の笑いを交えながら描く。学はないが実直な労働者のティーは、ある日、ゴミ捨て場で緑色のゴムボールを拾う。すると、そのボールが突然、謎の生命体に変化。ティーの息子ディッキーは、その生命体を“ミラクル7号”と名づけ、可愛がるのだが……。", "2008-06-28", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　SHE−CREATURE", "怪物の女性／海獣の霊を呼ぶ女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "1950～60年代に怪奇＆SF映画を量産したAIPの初期作品の一つ。“リーインカーネーション”をモチーフに、太古からよみがえった半魚人の恐怖を描いたモンスター映画だ。海辺のロッジで新婚カップルが殺害され、現場には海草と奇妙な足跡が残されていた。精神科医テッドは、助手の美女を相手に怪しげな催眠実験を行う男をマークするが……。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("凍河", "凍河", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "五木寛之の同名小説をドラマティックに映画化。精神病院を舞台に、赴任してきた青年医師と、完治しているのに退院しようとしない女性患者との愛を中心に、戦時中、アル中患者がこの病院の院長と一緒に細菌戦略部隊にいたこと、病院経営の行きづまりなどが描かれる。テーマ曲は荒井（現・松任谷）由実の軽快なポップス。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("СОРОК　ПЕРВЫЙ", "女狙撃兵マリュートカ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "現在では多数製作されている女戦士が戦い、恋もするという、女性ヒーローもののルーツを感じさせるソ連製活劇。'17年の赤軍と白軍に分かれて内乱が勃発していたソ連の時代を背景にした、孤島の男女の物語は後の諸作にも影響を与えたといえる。赤軍の女狙撃兵が白軍中尉を狙撃。軽傷を負った中尉は捕虜となり、女が船で護送する。だが、船は難破し、二人は孤島へ。", "1956-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("壁あつき部屋", "壁あつき部屋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "巣鴨拘置所に服役中のB・C級戦犯の手記を芥川賞作家・安部公房が脚色し、小林正樹が監督した新鋭プロ第１回作品。無実でありながら戦犯として投獄された戦争犠牲者を通して、戦争の意味を問いかける力作である。1953年10月に完成していたが、対米感情への配慮から配給の松竹が公開を見合わせ、日の目を見たのは1956年のことであった。岸恵子が初の汚れ役に挑み、話題を呼んだ。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無宿人別帳", "無宿人別帳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "松本清張の同名小説を映画化した異色時代劇。徳川時代の無宿人とは、罪を犯して人別帳からはずされた人々だ。刑期を終わっても彼らは人なみの生活を容易に許されず、幕府は江戸の治安を守る形で無宿人狩りを行なって佐渡の金山に送り込み、牛馬のように酷使するのだった……。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版ヤッターマン　新ヤッターメカ大集合！オモチャの国で大決戦だコロン！", "劇場版ヤッターマン　新ヤッターメカ大集合！オモチャの国で大決戦だコロン！", "", "", "http://image.eiga.k-img.com/images/movie/54627/315x210.jpg?3498", 0, "1970年代に人気を集め、2008年に新シリーズとして始まったテレビアニメ「ヤッターマン」の劇場版。おもちゃの国・トイトイ王国で、寂しがりやの王子プラモンらと一緒に楽しい夏休みを過ごしていたヤッターマン。ところが王国では、地球の破壊を企む大臣パ・ズールが、ドロンボー一味をも巻き込んだ陰謀を企んでおり……。テレビ版でおなじみのヤッターメカが大集合するほか、初登場の新メカも加わり大迫力のバトルが繰り広げられる。", "2009-08-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GROSS MISCONDUCT", "過失", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "夜はクラブでサックスを吹く大学教師のジャスティンは、女学生たちの憧れの的。彼はある日、教え子のジェニファーから熱烈な愛の告白を受け、誘われるままに一夜をともにしてしまう。ところが妄想癖のあるジェニファーが、警察に“私は強姦された”と証言したことからジャスティンはレイプ魔として逮捕されるハメに……。G・ミラー監督による実話をもとにした法廷サスペンス。", "1993-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEN　TRUMPETS　FADE", "プライベート・ソルジャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "ノルマンディ上陸作戦をも超える悲劇の戦い“ヒュルトゲンの戦い”を、「戦争の犬たち」「ハンバーガー・ヒル」のJ・アーヴィンが描く戦争ドラマ。1944年11月。ベルギーとドイツの国境で、アメリカ軍とドイツ軍の攻防戦が激化。生き残るために仲間をも犠牲にする非情に徹した軍曹や、恐怖のあまり精神に異常を来たす中尉、一人逃亡する途中で捕虜にされてしまう新兵など、極限下での兵士たちの心情を克明に描き出す。アメリカ軍、ロシア軍、ハンガリー軍の全面協力と大規模なエキストラによって、リアルで緊張感あふれる戦闘シーンが実現した。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　空の境界　第七章　殺人考察（後）", "劇場版　空の境界　第七章　殺人考察（後）", "", "", "http://image.eiga.k-img.com/images/movie/54217/315x210.jpg?3098", 0, "奈須きのこの同名小説を原作に、万物の死を見ることのできる能力「直死の魔眼」を持つ少女・両儀式の苦悩と戦いを描いた人気アニメ劇場版「空の境界」7部作の最終章。かつて連続殺人事件が世間を騒がせた際に自らを人殺しと称した両儀式と、そんな彼女の無実を信じ続ける黒桐幹也。1999年、連続殺人事件の再発と時を同じくして、式が幹也の前から忽然と姿を消した。幹也は式の無実を証明するため捜査を開始するが……。", "2009-08-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOODBYE，COLUMBUS", "さよならコロンバス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "図書館員のニールはニューヨーク郊外のプールで美しい娘ブレンダと知り合う。二人はデートを重ねるが、裕福なユダヤ人の両親に育てられ、わがままに育ったブレンダと、ナイーブなニールとの間には、次第にずれが生じてくる。夏が終わり、ブレンダはボストンの大学へ戻っていった。そして秋、久しぶりに再会した二人だったが、口論の末、ニールはブレンダを置き去りにしていく。階級の違いと、典型的ユダヤ人一家を描いたL・ピアース監督の青春映画の佳作。ユダヤ系の作家P・ロスのベストセラー小説の映画化で、アソシエーションの歌う主題歌もヒットした。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KING OF NEW YORK", "キング・オブ・ニューヨーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "N.Y.のブラック・マフィアたちのカリスマ的リーダー、フランクと、彼をつぶそうとする対立組織や警察との抗争を描いたマフィア映画。リーダー、フランクにC・ウォーケンが扮し、野望に燃えるクールな男を巧演している。ピート・ハミルをはじめ、アリアーヌ、フレディ・ジャクソンらの特別出演も見もの。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MERRY WIDOW", "メリィ・ウィドウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "原作のオペレッタをミュージカル・タッチで映画化した、ラブ・ロマンスの隠れた傑作。ルビッチ監督がドイツからハリウッドにやって来て開拓した都会派コメディの一作で、ドイツ製'31年の「会議は踊る」への返答を感じさせる楽しい作品だ。女はヨーロッパ某大国の大富豪未亡人、男はパリの国家公務員のモテる伯爵。こんな二人の恋のシーソーゲームが陽気に展開する。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mother Teresa", "マザー・テレサ　メモリアル　母なることの由来", "", "", "http://image.eiga.k-img.com/images/movie/28517/315x210.jpg?2377", 83, "ノーベル平和賞を受賞した修道女マザー・テレサの活動の軌跡を追った86年製作のドキュメンタリー。疫病と飢饉に苦しむカルカッタや戦禍のレバノンなどを訪れた5年間の記録を映し出すとともに、彼女の人生を振り返り、世界中の尊敬を集めるようになるまでの経過を描いていく。ナレーションを担当するのは名優リチャード・アッテンボロー。マザー・テレサ没後10周年を記念し、デジタル復刻版で上映される。", "2007-09-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コータローまかりとおる！", "コータローまかりとおる！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "腰まで届く長髪で、学園狭しと暴れまわる異端児・新堂功太郎を主人公に、彼の髪を切ろうとする生徒会と、彼の存在そのものをつぶしにかかる学園の裏組織を敵に回してのてんやわんやの大騒動を描いた学園コメディ。JACのメンバーが総出演でアクションを展開する。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若き姿", "若き姿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "日本により植民地にされた朝鮮に、国策で設立された“朝鮮映画株式会社”の第１回作品。日本と朝鮮の同化を進めるのが設立の目的だが、スタッフやメイン・キャストは日本人によって占められた完全な国策映画である。内容は、戦時下の朝鮮で中学生たちの軍事教練を指導する北村少佐を中心に、中学生や教師たちの日本への忠誠ぶりを描いたもの。豊田四郎としては２年ぶりの監督作だ。", "1943-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ラーメン侍", "ラーメン侍", "", "", "http://image.eiga.k-img.com/images/movie/56847/315x210.jpg?4754", 115, "とんこつラーメン発祥の地といわれる福岡県久留米市を舞台に、とんこつラーメンと屋台にまつわる人間ドラマを人情味たっぷりに描く。東京のデザイン事務所に勤めていた光は父が他界し、故郷・九州で家業のラーメン店を継ぐことになる。ラーメン作りに自信がもてず従業員とも衝突する日々が続く光だったが、母の嘉子から父の屋台やラーメンに込めた思いを聞かされ、記憶を頼りに父の味を再現しようと試行錯誤を始める。人気ラーメン店「大砲ラーメン」のオーナーで、ラーメンフェスタの発案者でもある香月均が地元誌に連載していたコラムをもとに映画化。", "2012-04-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道の妻たちII", "極道の妻たちII", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "うだつのあがらぬ組長に代わって組を仕切る妻の遊紀と、ヤクザとの間に生まれた子をモデルをしながら懸命に育てる麻美という二人の女性の姿を通して、極道の世界に生きる妻（おんな）たちの悲哀を描く。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IMITATION OF LIFE", "悲しみは空の彼方に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "未亡人ローラとその娘スージーはコニー・アイランドの謝肉祭で、白人の夫に捨てられた黒人女性アニーとその娘サラ・ジェーンに知り合う。お互いの身の上に同情しあい共感をおぼえた彼女たちは、ローラの家で一緒に暮らすことになる。アニーの献身的な働きぶりで家庭は明るくなり、ローラもチャンスをつかんで女優としての名声を得た。運命は好転するかに見えたが、サラ・ジェーンは母が黒人であることに苦悩し、ローラもまた女優稼業の空虚な生活に溺れていく……。ハリウッド・メロドラマの名匠D・サークの代表作の一つで、登場人物の心の動きを細やかに表現していくその演出が観る者の胸を打つ。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霊狐／FOX LEGEND", "ジョイ・ウォンの幽女伝説", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "美しい乙女に化けた狐、雪姫と人間との愛を、カンフー・アクションもふんだんに取り入れて描いた作品。「チャイニーズ・ゴースト・ストーリー」など一連のロマンティックなオカルト・ムービーものの一つ。ウー・マとJ・ウォンのコンビもおなじみで、息の合った演技には安心感がある。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRST　MEN　IN　THE　MOON", "H.G.ウェルズのSF月世界探検", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "H・G・ウェルズの同名SF名作小説の映画化。ストップ・モーション・アニメの巨匠レイ・ハリーハウゼンが、パナヴィジョン方式で撮影した唯一のシネマスコープ作品でもある。初めて月へ赴いた人類。しかしそこには地底都市があり、“シレナイト”と呼ばれる月の住人や巨大な月牛“ムーンカーフ”などが住む驚異の世界であった……。", "1964-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星空のマリオネット", "星空のマリオネット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "自主製作映画出身の橋浦方人の本格的監督デビュー作。暴走族のリーダー、ヒデオとシンナー中毒の少年ヒロシ、奔放に生きる少女アケミの３人の奇妙な生活。ヒロシの死、アケミの妊娠などがヒデオの生き方を変えていく。不思議な浮遊感が漂う異色の青春映画。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZACHARIAH", "ウェスタン・ロック／ザカライヤ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "西部劇の世界に現代的なロック・バンドが出現する異色作。ルービンシュタイン扮する主人公ザカライヤの冒険に満ちた放浪の旅が物語の軸。そのバックにはビートの効いたロック・サウンドが……。現代から突然19世紀にタイム・トリップしたような錯覚を覚える、奇妙な味わいの一編。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Big Fat Greek Wedding", "マイ・ビッグ・ファット・ウェディング", "", "", "http://image.eiga.k-img.com/images/movie/1527/315x210.jpg?2940", 96, "製作費500万ドルの低予算映画にして全米収益2億ドルを達成した話題作。原型は主演のニア・バルダロスが書いた自伝的一人芝居。その舞台を気に入った女優リタ・ウィルソンが夫トム・ハンクスとともに製作を担当して映画化。両親と暮らす30歳のトゥーラは人生を変えようと学校に通い大変身。そして非ギリシャ系男性イアンと恋に落ちるが、結婚相手にはギリシャ系しか認めない両親や親戚たちは大騒ぎ。トゥーラの結婚はどうなる？", "2003-07-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ベルナのしっぽ", "ベルナのしっぽ", "", "", "http://image.eiga.k-img.com/images/movie/33667/315x210.jpg?4414", 102, "自身の体験を基にした郡司ななえの同名ベストセラーを原作に、目の不自由な女性と盲導犬との絆を描いた感動作。病気により視力を失ってしまったしずくは、出産・育児という夢を叶えるため、盲導犬ベルナをパートナーとして迎え入れる。昭和50年代半ば、盲導犬に対する人々の理解はまだまだ低かった。様々な困難を乗り越えながら、しずくとベルナは次第に絆を深めていく。主人公しずくに扮するのは、本作で映画初主演を果たした白石美帆。", "2006-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAUTIFUL PEOPLE", "ビューティフル・ピープル ゆかいな仲間", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "アフリカ、ナミブ砂漠を舞台に、大自然の中に生きる動物たちの生態を、コミカルに捉えたドキュメンタリー。好物のヤモリを食べるため、砂の中から尻尾を出してまずアリをおびき寄せ、そのアリを食べにきたヤモリを捕まえる毒ヘビ。下半身が弱く、あまり速く走れないため、自分たちより足の遅い子供の動物を狙うハイエナ。酸性の液を発射して外敵から身を守るカブト虫など、珍しい動物たちがスクリーンいっぱいに登場する。", "1974-01-01", "67", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CALIFORNIA SUITE", "カリフォルニア・スイート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ビバリーヒルズホテルに宿泊した４組の夫婦の姿を“グランド・ホテル”の形式で描いたコメディ。オスカー・ノミネート女優とゲイの亭主、離婚カップルに浮気騒動、狂言回しはシカゴのドジ医者夫婦二組のエピソードが、時にシリアスに時にコミカルに綴られていく。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録三億円事件・時効成立", "実録三億円事件・時効成立", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "昭和43年12月10日に発生した“３億円事件”を題材に架空の犯人像を想定して犯行の一部始終が再現される。時効が間近に迫り急きょ作られた一種のキワもの映画だが、石井輝男のツボを押さえた演出で一気に見せきる。警部役の金子信雄が巧演。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Waste: The Nuclear Nightmare", "放射性廃棄物　終わらない悪夢", "", "", "http://image.eiga.k-img.com/images/movie/57247/315x210.jpg?3519", 92, "原子力発電所から生み出される使用済み核燃料などの放射性廃棄物の行方を追ったドキュメンタリー。アメリカ原発開発のためのプルトニウム製造工場では、閉鎖された現在も放射性廃棄物が土壌に溶け出して汚染を広げている。また、ロシアで50年以上前に起こった核施設の爆発事故の現場近くの村では、今なお住民の健康被害が続いていた。原発を稼動させている各国や、日本の福島原発の汚染水処理にも関与する世界最大の原子力企業アレバ社が行っている核再処理の実態などにも迫っていく。", "2011-10-22", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("マッハ'78", "マッハ'78", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "スポ根漫画の原作者として有名な梶原一騎が製作したリアル・スタント・アクション。その凄まじい車を使ったスタントの連続に、気持ちがスカッとするはず。千秋はアメリカでアクション映画でのカー・スタントを体験して刺激され、翌年日本チームに参加して日米スタント・マッチに挑戦した。危険なアクションの連続だが、激戦を経て日米レーサーたちの心は熱く結ばれる。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRACULA 2000", "ドラキュリア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "ロンドンの博物館に保管されていた棺桶から、不死身の怪人ドラキュリアが復活。血の継承者を求めてアメリカに渡った彼を、宿敵ヘルシング教授とその甥サイモンが追っていく。W・クレイヴンが製作総指揮を務め、テンポのよいストーリー展開と趣向を凝らしたSFXでまとめたアクション・ホラー。吸血鬼という古典的モンスターを現代に登場させ、そのルーツを大胆な解釈で描いているのもミソ。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("U2 3D", "U23D", "", "", "http://image.eiga.k-img.com/images/movie/54287/315x210.jpg?3602", 85, "アイルランド出身の世界的ロックバンドU2が05年から06年にかけて行ったワールドツアー“VERTIGO”の南米公演をデジタル3D映像で再現したライブドキュメンタリー。「U2／魂の叫び」（88）とは異なり、インタビューや舞台裏映像は一切無く、臨場感たっぷりのライブ映像のみで構成されている。監督は、長年U2のライブのビジュアルデザインを手掛けてきたキャサリン・オーウェンズと、「隣人は静かに笑う」「プロフェシー」のマーク・ペリントン。", "2009-03-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vidas Secas", "乾いた人生", "", "", "http://image.eiga.k-img.com/images/movie/55867/315x210.jpg?1676", 105, "ブラジルの作家グラシリアーノ・ラモスの小説を原作に、ブラジル東北部で生きる農民たちの過酷な現実をドキュメンタリータッチで描いた作品。干ばつで難民生活を余儀なくされたファビアーノ一家。ようやく見つけた農場で新たな生活をはじめるが、権力者によるさく取から逃れられず……。カンヌ映画祭で高く評価され、ブラジルの映画運動「シネマ・ノーボ」とネルソン・ペレイラ・ドス・サントス監督の名を一躍世に知らしめた。", "2010-12-04", "17", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HENRY ＆ JUNE", "ヘンリー＆ジューン　私が愛した男と女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "性の冒険家といわれる作家ヘンリー・ミラーと運命的な出会いをした、人妻のアナイス・ニン。そしてその二人から愛されるミステリアスなミラーの妻ジューン。1986年にアナイス自身によって発表された原作をもとに、文学では語られていたが映像では誰も手をつけなかった文学世界の謎の愛に、カウフマンが渾心の力をふりしぼって挑んだ話題作。ビデオ発売時タイトルは「私が愛した男と女 ヘンリー＆ジューン」。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しあわせの一番星", "しあわせの一番星", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "明るいお手伝いさん、16歳の美世子の奮闘ぶりをさわやかに綴った青春映画。ガンコ一徹な主人をとり成して、彼の娘をおでん屋の好青年と結婚させ、自分もまたクリーニング屋の若者と結ばれる。当時・新進の山根成之監督のてらいのない実直な演出に好感が持てる。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もし高校野球の女子マネージャーがドラッカーの『マネジメント』を読んだら", "もし高校野球の女子マネージャーがドラッカーの『マネジメント』を読んだら", "", "", "http://image.eiga.k-img.com/images/movie/55907/315x210.jpg?2481", 125, "2009年12月に発売され、発行部数236万部を超えるベストセラーとなった岩崎夏海の同名小説を、人気アイドルグループ「AKB48」前田敦子の初主演で映画化。公立高校の弱小野球部でマネージャーを務める川島みなみが、勘違いして購入した経営学者ピーター・ドラッカーの著書「マネジメント」を参考に野球部を改革し、甲子園出場という夢を実現するために奔走する姿を描く。監督は「うた魂♪」の田中誠。共演に瀬戸康史、峯岸みなみ（AKB48）、池松壮亮、川口春奈、大泉洋ら。", "2011-06-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GREEN TEA", "緑茶", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "孤独な男女の恋の駆け引きを描くスタイリッシュなラブ・ストーリー。ヴィッキー・チャオが内気な学生と奔放なピアニストというニ役を魅力的に演じている。緑茶で相手との相性が分かると信じる内気な性格のファンと誰とでも関係してしまう奔放な性格のランラン。顔はそっくりな二人と知り合い、心が揺れるミンリャンだったが……。", "2006-04-15", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しとやかな獣", "しとやかな獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "鬼才・川島の映画のなかでも、実に痛快な代表作の１本だ。舞台は２部屋しかない公団住宅。ここに、金のためには世間の道徳観念など屁とも思わない一家が住んでいる。海軍中佐であった父親の指導のもと、息子は芸能プロに勤め、サギまがいの悪徳手口で荒らし回り、娘は流行作家の妾となり、絞り取ることに余念がない。母親はそんな親子を温かく見守っている。そこに息子が横領金を貢いでいた女事務員が登場し……。悪に徹し切った登場人物たちのセリフが実に小気味良く、それにガッチリ応えた役者たちの味も格別。歌舞伎を思わせる音楽の流し方といい、斬新なブラック・ユーモア家庭劇。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("実録ヒットマン・北海の虎 望郷", "実録ヒットマン・北海の虎 望郷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "名脇役として活躍してきた俳優の誠直也が、デビュー30年目にして映画初主演に挑戦した意欲作。かつて“北海の虎”として暗躍した元ヤクザの男が、愛する家族を殺されたことで再び銃を手に取って、復讐へと立ち上がる任侠アクション映画。主人公の親友役に人気プロレスラーの武藤敬司が扮し、その鍛え上げられた体躯を活かした凄まじい力演も見逃せない。その昔“北海の虎”の異名をとったヤクザの田畑浩介は、刑期を終えて故郷・新潟へ戻り、愛する家族と堅気の生活を始めた。しかし、かつて敵対していた大阪侠勇会が勢力を拡大。その標的は、引退した田畑へも向けられていた。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FELLINI，WIZARD OF ROME", "魔術師フェリーニ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 49, "イタリアが生んだ巨匠F・フェリーニの姿を追ったドキュメンタリー。デビュー作から「甘い生活」、そして「カサノバ」までの作品のシーンを挿入し、スタッフや出演者たちへのインタビューを交えて、偉大なる天才の横顔を浮き彫りにしていく。「カサノバ」撮影中のフェリーニが語る、貴重な映画観に注目。", "1976-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE PROFESSIONALS", "プロフェッショナル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "監督のC・コッポラは、巨匠フランシス・F・コッポラの甥っ子にあたり、出演しているN・ケイジは彼の兄弟、そしてT・シャイアは彼の伯母と、コッポラ・ファミリーで固められた作品。天才詐欺師のジョー・ドナンはいつものように“仕事”に取り組んでいた。しかし誤って自分の銃弾で父親を射殺してしまう。失意のジョーは、死の間際に父が残した言葉の謎を解くべく、伯父のルーに会いに行く。サンタモニカでルーに会ったジョーは、ルーがあまりにも父親に似ていることに驚く。一方ルーはすっかりジョーを気に入り、かねてより計画していた“200万ドル”の大仕事にジョーを誘い込む。だが、凄腕ハスラーのモーガンが絡んできて、事態は思わぬ方向へと展開していくのだった。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KEEP ON ROCKIN'　〈キープ・オン・ロッキン〉", "KEEP ON ROCKIN'　〈キープ・オン・ロッキン〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "「ナースのお仕事／ザ・ムービー」の両沢和幸による青春ドラマ。ロック・バンドのヴォーカル青年が、栄光と挫折を味わい、故郷で新たな人生を歩み始めるまでを丁寧に描き出す。自信過剰で傲慢だった主人公が、自分自身と向き合うまでの心の変化を、ロック・バンド“to be continued”の元ヴォーカリスト、岡田浩暉が熱演。主題歌と挿入歌２曲の作曲も手掛けている。ロック・バンドのヴォーカル・英二は、レコード会社から契約を切られ気分転換に故郷に帰る。入院中の父、仕事と看病に追われる母、地道に生活している旧友たち。そんな彼らの姿に心動かされ、英二の中で新しい音楽が生まれる。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Samaritan Girl", "サマリア", "", "", "http://image.eiga.k-img.com/images/movie/1627/315x210.jpg?1453", 95, "「春夏秋冬そして春」のキム・ギドク監督が援助交際をモチーフに10代の少女2人の心の交流を描き、04年ベルリン映画祭銀熊賞を受賞。女子高生ヨジンは、親友チェヨンが援助交際をするのに抵抗を感じながらも見張り役になっていた。が、ある日、警官の取締りを逃れようとしたチェヨンが窓から飛び降りて死亡。ショックを受けたヨジンは、罪をあがなうために、チェヨンの援助交際相手たちを訪ねてお金を返して回ろうとする。", "2005-03-26", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("次郎長三国志・旅がらす次郎長一家", "次郎長三国志・旅がらす次郎長一家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "勘助を叩き斬って兇状旅にでた次郎長一家だが、お蝶が病気になり、かつて急場を救った力士・八尾ヶ岳を訪ねる。八尾ヶ岳は力士をやめ保下田の久六と名を改め、尾張で売り出しの親分になっていたが、実は代官と通じていた。それを知った次郎長は小松村の七五郎の家に落ち着く。そこでお蝶は皆に見守られて死ぬ……。", "1953-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しゃべれども　しゃべれども", "しゃべれども　しゃべれども", "", "", "http://image.eiga.k-img.com/images/movie/33987/315x210.jpg?1333", 109, "佐藤多佳子の同名小説を、TOKIOの国分太一主演で映画化。うだつのあがらない落語家・今昔亭三つ葉は、ひょんなことから話し方教室を開くことに。そんな彼のもとに集まったのは、無口な美人、関西弁の勝気な少年、毒舌の元プロ野球選手という変わり者の3人。しかし彼らは顔をあわせるとケンカばかりでなかなか落語を覚えてくれない。さらに三つ葉は思いを寄せていた女性にまで振られてしまい……。監督は「愛を乞うひと」の平山秀幸。", "2007-05-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CROW: CITY OF ANGELS", "ザ・クロウ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "ブランドン・リー主演の1994年作「クロウ−飛翔伝説−」に続き、J・オバーの人気コミックを映画化したダーク・ファンタジー。近未来のロサンゼルスで、ストリート・ギャングに殺された青年アッシュがカラスの霊力を得て復活。そんな彼と街を牛耳るボス、ユダとの死闘が、前作の主人公エリックを知る少女サラの存在を絡めて描かれる。前作と比べてかなりアクション色は薄れたものの、ザ・キュアーのビデオ・クリップなどで知られるT・ポープが監督にあたり、イエローがかった色彩の映像で耽美的なムードを打ち出している。白塗りのメイクで登場するフランスの二枚目スター、V・ペレーズが、この手のヒーロー映画に主演しているのも珍しい。ビデオ、DVDタイトルは「クロウ２」。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EL ESCOTE", "真夜中の魔性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "自らの異常セックス体験をマイクに向かって告白する。これが深夜放送のパーソナリティー、マルタ、23歳のドライなライフスタイル。それは、走る列車のトイレの中であったり、またはベランダの手すりにつかまったまま……。次々に展開される衝撃的な官能シーン。リアルなセックス描写はスペイン映画ならでは。", "1987-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("江戸へ百七十里", "江戸へ百七十里", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "津山十万石藩主小森家の落としダネである長谷部兵庫は、お家との絶縁条件として大金を手に入れ、フラリと旅に出る。その道中、松平福姫と知り会った兵庫は、彼女が翌日兵庫そっくりの小森家嫡男とお見合いさせられることを知るが……。市川雷蔵が一人二役で剣の冴えをみせる明朗時代劇。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("高校生番長", "高校生番長", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "全日制高校に通う美穂は、定時制クラスの健太に激励の手紙を書くが、健太からは返事として陰毛が送られてくる。美穂に好意を寄せていた弘人と番長の勇治は、この仕打ちに怒り、定時制クラスに殴り込むが……。不安定な思春期の感情を暴力とセックスで描出した青春映画。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mr. Magorium's Wonder Emporium", "マゴリアムおじさんの不思議なおもちゃ屋", "", "", "http://image.eiga.k-img.com/images/movie/53087/315x210.jpg?1878", 93, "ダスティン・ホフマンとナタリー・ポートマンが共演したハートウォーミングなファンタジードラマ。魔法によって命を吹き込まれたおもちゃが所狭しと動き回る不思議なおもちゃ屋。243歳を迎えて引退を決意したオーナーのマゴリアムおじさんは、支配人を務める女性モリーに店を譲ると宣言する。ところが、マゴリアムおじさんのことが大好きなおもちゃたちが大反乱を起こし……。「主人公は僕だった」の脚本家ザック・ヘルムの監督デビュー作。", "2008-02-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Coming Home", "帰郷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "　1968年、ロスアンゼルス。サリーは海兵隊大尉である夫ボブをベトナムに送り出した後、基地の付属病院でボランティアを始める。そんな彼女が出会ったのは、ベトナムでの戦闘で下半身不随となったかつてのクラスメート、ルークだった。やがてサリーとルークは愛し合う仲に。ルークが反戦運動に身を投ずる一方、帰還したボブは妻の不貞を知り……。3人の男女が織り成すラブ・ストーリー。ベトナム戦争の後遺症を告発する反戦映画の代表作としても知られる。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("裸の重役", "裸の重役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "中央商事の重役・日高四郎は“仕事の鬼”と呼ばれているが、バーのマダムには“日本一さびしい男”と言われる、妻のいないやもめ暮らし。22歳の娘を社内の優秀な男と結婚させようとしている父親の姿を浮き彫りにした、哀歓あふれる物語となっている。サラリーマンに捧げる人間ドラマ。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHINA SEAS", "支那海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "香港からシンガポールへ出帆した定期船キンルン号。船には船長のガスケルと“支那人形”と呼ばれる情婦が乗っていたが、もう一人、ガスケルとかつての恋人シビルも乗船していた。人妻だったシビルから身を引いてアジアへと逃れてきた彼をはるばる追ってきた彼女にガスケルは冷たく接するが、一方“支那人形”はシビルに嫉妬し……。名優C・ゲーブル主演の冒険メロドラマ。", "1935-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("星ノくん・夢ノくん", "星ノくん・夢ノくん", "", "", "http://ecx.images-amazon.com/images/I/51kdOvHT91L._SL210_.jpg", 68, "", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("霊気迫人", "チョウ・ユンファの 悪霊退治／デビル・バスターズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "カナダから香港へやってきたアンジーが入居したアパートは、夜な夜な机が勝手に動いたり不可思議な現象が起きる部屋だった。アンジーからの通報を受けて調査に訪れたバレンチノ刑事が以前の住人を調べるうちに、リサという女性が引き起こした衝撃的な事件が浮かび上がる……。チョウ・ユンファが初めて刑事役に扮した都会派ファンタジー・コメディ。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IF　YOU　WERE　ME", "もし、あなたなら～６つの視線", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "韓国の実力派監督6人が独自の観点から“人権”というテーマを掘り下げたオムニバス映画。「子猫をお願い」のチョン・ジェウン、「オールド・ボーイ」のパク・チャヌクらの俊英とベテランが世代の垣根を超えて集結。コメディ調やドキュメント風など多様な魅力に富んだ短編集になっている。高校3年生のソンギョンは、就職のために容姿に磨きをかけるよう教師に促され、二重まぶたの整形手術を受ける決意をするが……。(「彼女の重さ」)", "2005-01-15", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("偶然にも最悪な少年", "偶然にも最悪な少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "CFディレクターのグ・スーヨンが自らの原作で初監督に挑戦した鮮烈な青春映画。いじめられっ子の在日韓国人少年が、亡き姉のために韓国初訪問を企て旅に出る様をリリカルに描く。キレやすい体質のいじめられっ子・ヒデノリは在日コリアン。自殺した姉・ナナコに祖国を見せてやろうと思い立った彼は、遊び仲間のタローと由美を巻き込んで、博多、小倉、下関を旅していく。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER LATZTE MANN", "最後の人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ベルリンの豪華ホテルのドアマンをしている男が、寄る年波に勝てず、支配人から地下のトイレの掃除人への格下げを申し渡される。しかし、それに耐え切れない彼は、金モールのついたドアマンの制服を盗み出し、隣近所の人々には、依然栄誉あるドアマンであるように見せていたが……。ドイツからアメリカに渡ったF・W・ムルナウ監督がドイツ時代に撮った作品。光と影を大胆に使った表現主義的な映像、さらに現在の基準からすれば大仰なE・ヤニングスの演技が悲劇を指向する物語とあいまって、重厚な後味を残す作品となっている。また、アメリカでの公開を想定し、百万長者の遺産を相続してめでたしめでたしという、とってつけたようなハッピーエンドが付け加えられた。", "1924-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Twilight Saga: New Moon", "ニュームーン　トワイライト・サーガ", "", "", "http://image.eiga.k-img.com/images/movie/54667/315x210.jpg?4592", 131, "バンパイアと人間の禁断の恋を描いたステファニー・メイヤーのベストセラー小説を映画化した「トワイライト／初恋」の続編。主演はクリステン・スチュワート＆ロバート・パティンソン。新たにバンパイア役でダコタ・ファニングが参加し、監督は「ライラの冒険」のクリス・ワイツに交代。ベラの18歳の誕生日パーティで起きた事件をきっかけに、エドワードはベラに別れを告げる。狼族のジェイコブは傷心のベラに寄り添うが、ベラの血を求めるバンパイアの危険が迫っていた。", "2009-11-28", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嘘つきみーくんと壊れたまーちゃん", "嘘つきみーくんと壊れたまーちゃん", "", "", "http://image.eiga.k-img.com/images/movie/55777/315x210.jpg?3780", 110, "作家・入間人間のデビュー作を、「パラダイス・キス」の大政絢と「パンドラの匣」の染谷将太主演で映画化。新鋭・瀬田なつき監督がメガホンをとる。10年前に起きた誘拐監禁事件の被害者、みーくん（染谷）とまーちゃん（大政）は、不穏な連続殺人事件が起きている街で再会を果たす。そんなある日、殺人事件を捜査している刑事が2人を疑って接触してくる。", "2011-01-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAU GESTE", "ボー・ジェスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "外人部隊に身を投じた３兄弟の姿を描いた、P・C・レンの小説をもとにした二度目の映画化。家宝の“青い水”を盗んだという書き置きを残してアフリカの外人部隊に旅立ったジェスト３兄弟のボー。残されたジョンとディグビーもそのあとを追うが、戦闘に傷ついていく。そして、ジョンだけが家に帰り着くことができた……。", "1939-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("にくめ、ハレルヤ！", "にくめ、ハレルヤ！", "", "", "http://image.eiga.k-img.com/images/movie/55437/315x210.jpg?2569", 76, "阪神大震災で両親を亡くした裕人は、祖母とともに叔父の家に引き取られて育つ。震災から10年が経ち、徐々に平穏な日々を取り戻しつつある裕人は、認知症が進行してきた祖母から震災時にガレキの下にサキという妹がいたことを知らされ、大きなショックを受ける。そんな折、裕人は街で自分と似た境遇の少女・沙樹に出会い、妹のサキでないかと疑う。新鋭・板倉善之監督の劇場長編デビュー作。", "2010-06-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA ROMANA", "ローマの女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "貧しさのために男から男へと渡り歩く淫らな生活を送っていた女が、理想的な男にめぐり合い結ばれるのだが、その男が政治活動家だったために、再び苦渋の道を歩むことになる。“人間の罪悪と美徳はともに自然に生じ、環境に支配される”というモラヴィアの同名ベストセラーを自らが脚色したメロドラマ。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("静かなり暁の戦場", "静かなり暁の戦場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "第二次大戦下、マレー半島でのインド人捕虜将校と日本人将校との交流を描いた戦争叙事詩。インド兵捕虜の尋問にあたることになった国井中尉。彼は暴力を使わず、魂の触れ合う話をしようとする。日本軍不信の念がやっとほぐれてきたインド兵。やがて彼らは、日本軍はインド独立の友軍だと思い始める。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GLAMOUR", "グラマー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ファッション・モデルの女の子が、自らデザインした作品によるショーを開くまでを、ハンサムな弁護士との恋愛模様を交えながら描くサクセス・ストーリー。主役のガブリエラは実際にモデルの経験があり、またダニエル・エシュテルやA・タルラッツィら有名デザイナーたちが実名で出演。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("なま夏", "なま夏", "", "", "http://ecx.images-amazon.com/images/I/511EY6ZVWAL._SL210_.jpg", 57, "女子高生に恋してしまった冴えない中年男の奮闘を描いた、ちょっとエッチなラブコメディ。恋愛経験のない中年男・益雄は、通勤電車で一緒になる女子高生・杏子に恋心を抱く。しかし、そんな益雄のピュアな思いも杏子にとっては迷惑なだけ。思いきって告白してみるも、見事にフラれてしまい……。ヒロイン役に人気AVアイドルの蒼井そら。長編デビュー作「机のなかみ」で注目を集めた吉田恵輔監督が2005年に手がけた中編作品。", "2005-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHUKA", "砦のガンベルト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "過去に自分の過ちから連隊を全滅へと追いやってしまった騎兵隊の隊長と、さすらいのガンマンを中心に、インディアンと騎兵隊との壮絶な戦いを綴った西部劇。勧善懲悪タイプの西部劇とは違って、主人公たちの人間としての弱さ、強さに焦点を当てた人間ドラマとして描かれている。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バサラ人間", "バサラ人間", "", "", "http://image.eiga.k-img.com/images/movie/54207/315x210.jpg?4290", 77, "長尾みのるがフーテン、ヒッピー、アングラ文化などを描いた「イラストーリー　バサラ人間」を映画化。気鋭ファッションデザイナーのアナニス・オナニス氏が提唱する「バサラ」というライフスタイルが若者から圧倒意的な支持を得て、オナニス氏プロデュースのアイドルグループ「バサラ天女」はメディアを圧巻していた。そんなオナニスブームの中、オナニスのムック本を手に、田舎から上京してきた少女モモコの姿があった。", "2009-03-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("假装没感覚／SHANGHAI　WOMEN", "上海家族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "女性ならではの感情の変化を細やかに描出する手腕に定評のある女性監督P・シャオレンが、祖母、母、娘という三世代の女性の生き方を切り取ってみせた人間ドラマ。庶民的な暮らしをする上海のとある家族をモデルに、離婚に踏み切って家を飛び出す母、それに振り回される娘、離婚に批判的な祖母の、それぞれの価値観の違いが丁寧に描かれた佳作だ。15歳の娘、阿霞（アーシャ）の母は、家庭を省みない父と離婚した。母は、阿霞を連れて祖母のいる実家に戻るが、そこにはもはや居場所はなかった。祖母も離婚に関しては不満がある。そんな中、母に再婚話が持ち上がり……。", "2002-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎ハイビスカスの花", "男はつらいよ・寅次郎ハイビスカスの花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "旅回り歌手・リリーから“沖縄で入院中”との便りが届き、寅さんは急遽沖縄へ。看病した甲斐もあって快方に向かい、二人は夫婦気取りの生活を始めるが……。「…寅次郎忘れな草」「…寅次郎相合傘」に続いて浅丘ルリ子が３度目のマドンナ役を演じる。寅さんの恋愛～結婚についての本音がよく表れた好編。山田洋次自身、“最も気に入っているものの一つ”と言っており、1997年には撮り下ろしシーンを加えた［特別篇］が公開された。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅の群れ", "修羅の群れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "湘南一帯を縄張りにした堀井一家の若い衆となった稲原龍二は、徐々に頭角を現し、やがて横浜から東海道にかけて勢力を広めていく。任侠ヤクザ映画の大プロデューサー故・俊藤浩滋が企画者となり、かつて手掛けた大下英治の同名原作をリメイク。前作同様、主人公に松方弘樹が扮し、昭和の時代を駆け抜けた修羅たちの生きざまをダイナミックに描いている。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lost＆Found", "Lost＆Found", "", "", "http://image.eiga.k-img.com/images/movie/55327/315x210.jpg?1496", 75, "東北のローカル線終着駅にある「落し物あずかり所」を舞台に繰り広げられる群像ドラマ。初老の寡黙な駅員・富樫は、新しく就任した若い駅員の萩野とソリが合わず、微妙な関係が続いていた。やがて荻野は意味もなく駅にやってくるサラリーマンに冷たく当たるようになり、それが悲劇的な事件へと発展してしまう。監督は新鋭・三宅伸行、主演は菅田俊。", "2010-02-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("れっしゃだいこうしん　ザ☆ムービー　しんかんせん大集合", "れっしゃだいこうしん　ザ☆ムービー　しんかんせん大集合", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "2011年3月の東北新幹線新型車両「はやぶさ」運行開始や九州新幹線全線開業を記念し、新幹線を中心とした特急列車を解説するアニメーション。子どもたちが新幹線をわかりやすく学べるために、クイズなども取り入れられている。", "2011-02-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA NUIT PORTE JARRETELLES", "ガーターベルトの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "夜遊びが大好きな現代娘ジュザベルは、ある晩年下の男の子アリエルに出会う。うぶで不器用なアリエルを夜の街に連れ出したジュザベルは、恋の手ほどきを始めるのだった。「エリザとエリック」に先立つV・テヴネの監督第１作。おしゃれでどこか不思議な映像感覚が魅力。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SCARED STIFF", "死霊の棲む館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "まさにタイトル通り、悪霊が取り憑いた家を舞台にしたホラー映画。アフリカから連れてこられた黒人奴隷の呪いによってモンスターにされてしまった奴隷商人が現代によみがえり、次々と人間たちに襲いかかる。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Comme un chef", "ザ・シェフ（英題）", "", "", "http://image.eiga.k-img.com/images/movie/58007/315x210.jpg?2907", 0, "", "2012-12-31", "2,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MELODY OF PASSION", "キャミソール・ナイト／官能と悦楽の不夜城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "女子大生ベティは、その美貌で男心をほしいままにしていた。ある日、彼女は遺産相続により城を一つ手に入れる。刺激を求めて城に住むことにしたベティの前に、いとこのセルバンデスが現れた。彼は城を秘密クラブの乱交パーティーに使っていたのだった。アメリカ映画のそれとは違った、ヨーロッパの淫びなムード漂う官能作。", "1990-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Le Voyage du Ballon Rouge", "ホウ・シャオシェンのレッド・バルーン", "", "", "http://image.eiga.k-img.com/images/movie/53687/315x210.jpg?2748", 113, "台湾の名匠ホウ・シャオシェン監督が、パリのオルセー美術館開館20周年事業として立ち上げられた映画製作プロジェクトの第1弾として手掛けた人間ドラマ。56年のアルベール・ラモリス監督作「赤い風船」にオマージュを捧げ、パリで暮らす母子と、その家にベビーシッターとして訪れた中国人留学生の心の機微を、パリの美しい情景とともに温かいタッチで綴っていく。主演は「イングリッシュ・ペイシェント」のオスカー女優ジュリエット・ビノシュ。", "2008-07-26", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犯人に告ぐ", "犯人に告ぐ", "", "", "http://image.eiga.k-img.com/images/movie/34297/315x210.jpg?1669", 117, "雫井脩介によるベストセラー小説を豊川悦司主演で映画化したサスペンス。川崎で連続児童殺害事件が発生。犯人はテレビに脅迫状を送りつけて世間を騒然とさせるが、3件目の犯行後に忽然と姿を消してしまう。そこで警察は、捜査責任者をテレビに出演させる“劇場型捜査”を断行。6年前の誘拐事件で捜査に失敗した刑事・巻島がその役を任される。カメラの前で、挑発するかのように犯人に呼びかける巻島だったが……。", "2007-11-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ライトノベルの楽しい書き方", "ライトノベルの楽しい書き方", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "本田透の同名ライトノベルを映画化した学園ラブコメディ。冴えない男子高校生の与八雲は、人気ライトノベル作家・姫宮美桜の正体が、クラスメイトで学園最強の美少女・流鏑馬剣であることを知る。剣は新作のラブコメ小説を執筆中だったが、実際の恋愛経験がないことが原因でスランプに陥っていた。八雲は出版社に勤める従妹・心夏に命令され、剣と擬似恋愛をすることになる。「月と嘘と殺人」の佐藤永典が主演を務める。", "2010-12-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("金髪の草原", "金髪の草原", "", "", "http://image.eiga.k-img.com/images/movie/1477/315x210.jpg?3308", 96, "心臓病を患う80歳の日暮里は、ある朝、自分を病に冒される前の20歳の青年だと思い込んだまま目を覚ます。そして、彼の元へやってきた新任ヘルパーの少女、なりすを学生の頃想いを寄せていたマドンナだと思い込む。「なんて素敵な夢なんだ」となりすに夢中になる日暮里。一軒の古い屋敷の中で、現実を夢と思い込む老人と少女の奇妙な生活が始まった。だが、日暮里は徐々に現実と妄想とのずれに気付いていく……。", "2000-09-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛の言霊　世界の果てまで", "愛の言霊　世界の果てまで", "", "", "http://image.eiga.k-img.com/images/movie/55537/315x210.jpg?3784", 89, "紺野けい子のボーイズラブ・コミックを映画化した「愛の言霊」の続編。金田敬監督が続投し、13代目「いいとも青年隊」の植野堀まことと「手のひらの幸せ」の河合龍之介が主演を務める。広告代理店のIT部門で働く裕文（植野堀）は、エリート上司の慎司（河合）に密かに恋心を寄せていた。そんなある日、裕文は慎司が幼なじみの佐和子の元恋人であることを知り動揺する。", "2010-08-07", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lolita", "ロリータ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 152, "　中年男ハンバートは、未亡人シャーロットと”ロリータ”と呼ばれる早熟な娘ドロレスと知り合う。母親に想いを打ち明けられたハンバートだが彼の欲望は娘ロリータに注がれ、彼女の側にいたい一心から、未亡人と偽りの結婚生活をスタートさせる。ロリータ役は当時14歳のスー・リオン。1954年に書き上げられたウラジール・ナボコフの原作は、ベストセラー小説でありスキャンダラスな内容から物議を醸した作品でもある。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トットチャンネル", "トットチャンネル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「恋する女たち」に続く大森一樹＝斉藤由貴コンビの第２作。昭和28年、TV放送が開始されたころ、NHK放送局に入って明日のスターを夢見る若者たちの青春群像を、コメディ・タッチで少々ほろ苦く描いた佳編。同時代の映画の記憶も散りばめられているなど、まさに大森調。", "1987-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("無鉄砲大将", "無鉄砲大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "鈴木清順＝和田浩治コンビ作。ローラースケートの指導員で女にモテモテの英治が、姉のように慕う雪代のバーに巣食う悪い大人たちに正義の鉄拳をふるう。ラスト、プラットホームに立ちつくす主人公の虚無的な表情が、不評とともに一部の共感を得た。和田浩治が豪快な空手で愚連隊を相手に大暴れする。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KT", "KT", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "1973年に起こった韓国大統領候補、金大中の拉致・監禁事件をめぐる人間模様を、阪本順治監督が映画化。韓国で独裁体制を敷く朴大統領の政敵、金大中が日本に逃亡。韓国大使館は暗殺目的で、その行方を必死で追い続けていた。元自衛隊員の富田は、韓国と日本の双方からの依頼を受けて、金の居所を調査。やがて国際情勢の変化によって日本は、この件から手を引くが、後戻りのできなくなった富田は暗殺計画に深く関わっていく……。暗殺者や金の協力者など、事件に関わった様々な立場の人々を、骨太なタッチで描いている。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("地獄の饗宴", "地獄の饗宴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "中村真一郎の推理小説『黒い終点』をもとに岡本喜八がムードたっぷりに作った無国籍風アクション映画。ポン引きの戸部修はふとしたことから、東光住宅の１億５千万円横領事件にかかわり、殺し屋に命を狙われる。敵のアジトを突き止めた戸部は、復讐に乗り出した。団令子の黒を基調としたファッションも見もの。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛と希望の街", "愛と希望の街", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 62, "ある小都市の駅前。靴磨きの女たちに混じって一人の少年が鳩を売っていた。そこへ会社役員の令嬢京子が通りかかり、その鳩を七百円で買う。少年はお金が要るから鳩を売るのだと言う。京子は同情するが、実は鳩の帰巣本能を利用した巧みな金儲けだった……。「鳩を売る少年」のタイトルで書かれた大島オリジナルのシナリオで、若干27歳で監督に抜擢された大島渚監督の記念すべき長編デビュー作。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRAZY-IZM クレイジズム", "CRAZY-IZM クレイジズム", "", "", "http://image.eiga.k-img.com/images/movie/56087/315x210.jpg?2959", 80, "多額の借金を抱える上野は、悪友の洋平から老人のタンス預金強奪計画を持ちかけられ、計画に加わる。洋平の彼女や友人らを巻き込み、強奪に成功した上野たちは、とある密室にこもるが、大金の配分をめぐって仲間割れが生じる。それぞれの思惑が交錯し、密室は次第に修羅場と化していく。江戸川乱歩原作の映画「失恋殺人」がモントリオール世界映画祭で上映された窪田将治監督が、密室を舞台に人間のエゴや欲望を描くオリジナルサスペンス。", "2011-04-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私、違っているかしら", "私、違っているかしら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "森村桂の同名の原作を倉本聰らが脚色した青春ストーリー。就職活動を続ける大学４年の桂は、片親というだけで書類選考ではねられてばかり。そんな時、亡父の友人の紹介で婦人週刊誌の編集部員として働き始める。けれども、ある遭難事件の取材で、遺族の抗議にあい落胆。会社も飛び出してしまう……。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IRRECONCILABLE DIFFERENCES", "ペーパー・ファミリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "９歳の少女ケイシーの一家は“家族”とは名ばかり。両親は常にいさかいを起こし、家族らしい団らんなど、ここ数年持ったことがない。誰にもかまってもらえない彼女の怒りは頂点に達し、とうとう両親を告訴した。「E.T.」の名子役D・バリモアを主役に、ユーモラスだが、シニカルな警告を含んだファミリー・ドラマだ。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("やくざ残酷秘録・片腕切断！", "やくざ残酷秘録・片腕切断！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 65, "元暴力団組長にして、作家、俳優、映画プロデューサーの安藤昇が企画・構成し、かつての古巣であるヤクザ社会の実態を描いたドキュメンタリー映画。登場人物はすべて本物のヤクザで、隠しカメラと隠しマイクを駆使して、賭場の情景から出所シーン、親分衆の会合、ノミ行為、指ツメ、女性の刺青、テキヤの仁義、ショバ割りなどが克明に描かれる。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BANDLERO！", "バンドレロ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "アクション派のA・V・マクラグレン監督が描いた、南北戦争後の混乱した西部を背景にしたアウトローたちへのバラード。自由と富を求めて西部へと流れて行った兄弟たちの明日なき日々を、得意のアクション・シーンを駆使して描き出す。銀行強盗、保安官らとの銃撃戦、そして恋など、たっぷり娯楽要素も加味された痛快な一編だ。", "1968-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PANE　E　TULIPANI", "ベニスで恋して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "本国で圧倒的に支持されたラブ・ストーリー。家族旅行中の主婦ロザルバはひょんなことからツアーバスに置き去りにされる。彼女はヒッチハイクで家に戻ることにするが、着いたのはベニスだった。帰る金もなくなった彼女は、食事をしたトラットリアの主人、フェルナンドに頼んで一晩泊めてもらうことにするのだが、そのまま彼の家に居候することになる。次第に彼女は心優しい彼に心惹かれて……。", "2000-01-01", "4,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADY BEWARE", "愛は危険な香り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "デパートのショーウインドーなどのディスプレイ・デザイナー、カチア。そんな彼女に、異常な性癖を持った一人の男の視線が止まり……。初めてヌードに挑戦したD・レインの熱演ぶりが見もの。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("All the Invisible Children", "それでも生きる子供たちへ", "", "", "http://image.eiga.k-img.com/images/movie/34007/315x210.jpg?1349", 130, "「マルコムX」のスパイク・リー（アメリカ）、「ライフ・イズ・ミラクル」のエミール・クストリッツァ（セルビア・モンテネグロ）、「シティ・オブ・ゴッド」のカティア・ルンド（ブラジル）ら世界各国の7組の監督たちが、それぞれの国の子供たちが置かれている過酷な状況を描いたオムニバス・ドラマ。ストリートチルドレン、少年兵、HIVなど、深刻な問題を突きつけられながらも逞しく生きていく子供たちの姿をリアルに映し出す。", "2007-06-09", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DREAM TEAM", "ドリーム・チーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "４人の現代病患者に外出療法を試みることにしたワイツマン医師。ところが道中ワイツマンは、ある事件に巻き込まれ、重傷を負い病院に運ばれてしまう。待ちくたびれた患者たちはニューヨークの町に散らばって行く……。芸達者たちのコンビネーションが楽しめる、ほのぼのドラマ。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Marcelino pan y vino", "汚れなき悪戯", "", "", "http://ecx.images-amazon.com/images/I/51lLUhdKpxL._SL210_.jpg", 91, "小さな村の修道院に拾われた捨て子マルセリーノは、僧たちに可愛がられていたが、いたずらが好きで皆を困らせていた。ある日、納屋に忍びこんだマルセリーノは、十字架のキリスト像を見つける……。子供の無垢な心が呼び起こす奇跡を描く感動作。", "1955-01-01", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUITE　HABANA", "永遠のハバナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "キューバの首都ハバナに住む、ごく普通の市民12人の姿を記録したドキュメンタリー。この街で生まれ育った名匠フェルナンド・ペレスが、ささやかな夢と希望を胸に生きる人々の日常を描出。その等身大の目線が地元ハバナの人々の共感を呼び起こし、劇場が連日満員になったという1本だ。障害を持つ子供、街角でピーナッツを売る老女、ダンサーを夢みる青年……。ハバナの街と共に生きる男女12人の人生をスケッチする。", "2005-03-12", "49,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悲情城市", "悲情城市", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 159, "台湾現代史において、最も激動的な1945年の日本敗戦から1949年の国民党政府の樹立までの4年間を、林家の長老・阿祿とその息子たちの姿を通して描いた一大叙事詩。台湾ニューウェーブの雄、ホウ・シャオシエン監督は本作でベネチア映画祭金獅子賞を受賞、その評価を決定づけた傑作。主演は香港のトップスター、トニー・レオン。彼は台湾語を話せないために聾唖という設定になったという逸話もある。", "1989-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("今日もわれ大空にあり", "今日もわれ大空にあり", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "航空自衛隊浜松基地。F103Jに乗るパイロットを育てるため、新任隊長がやってくる。彼が、若手４人を猛特訓して一流のパイロットにするまでを描く。“日本一”や“無責任”など、一連の東宝喜劇を手掛けた古沢憲吾による珍しい航空もの青春映画。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRAVE HEARTS 海猿", "BRAVE HEARTS 海猿", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "伊藤英明主演で海上保安庁の若き潜水士たちの活躍を描く人気シリーズ第4作。巨大天然ガスプラント「レガリア」の爆発事故から2年、仙崎と吉岡は救難のスペシャリスト集団「特殊救難隊」所属となり、過酷な任務をこなしながら充実した日々を送っていた。そんなある日、吉岡の恋人でキャビンアテンダントの美香が搭乗するジャンボジェット機のエンジンが炎上し、飛行困難になる事故が発生。ジャンボ機は東京湾へ着水するが、海面に浮かんでいられる時間はわずか20分間。仙崎や吉岡らは、その間に乗客乗員346人を助け出さなければならず……。", "2012-07-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA PELICULA DEL REY", "王様の映画", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "新進気鋭の映画監督ダビは、19世紀の南米を舞台に、インディオの王として君臨しようとした伝説的フランス人の話を映画にしようと動きだした。オーディションをやめ、街を歩いてほとんど素人ばかりのキャスティングをする。ロケ隊は荒野へ。ところが、スポンサーが失踪し……。W・ヴェンダースの「ことの次第」に似た設定の、“映画作りについての映画”。その後、ダビが強引に撮影を続け、自らを伝説の王に重ねて狂気へと進んでいくあたりから、この作品自体の個性が出てくる。特筆すべきは、その映像感覚で、炎やロングショットの効果的な使い方など、ヨーロッパの大監督の作品に比べても、見劣りしない。", "1986-01-01", "22", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄金花　秘すれば花　死すれば蝶", "黄金花　秘すれば花　死すれば蝶", "", "", "http://image.eiga.k-img.com/images/movie/53977/315x210.jpg?4559", 79, "日本映画界を代表する美術監督として数々の名匠たちの作品を支えてきた木村威夫が91歳にして手掛けた長編監督第2作。孤独な老人たちが身を寄せる老人ホーム「浴陽荘」を舞台に、自らの人生を邂逅しながら夢とも現実ともつかぬ不思議な日々を送る個性豊かな入居者たちの姿を、幻想的な映像美で描き出す。主人公の植物学者を原田芳雄が演じるほか、松坂慶子、川津祐介、松原智恵子ら名優たちが顔を揃える。", "2009-11-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わたしを深く埋めて", "わたしを深く埋めて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "旅行から帰ってきた弁護士のアパートの部屋で謎の女が変死。女は自動車事故で他界した百万長者の遺産相続の重大な証人だった。やがて弁護士の幼なじみも殺され、相続がらみと見られた事件は意外な方向に展開する。H・Q・マスルの原作をもとにしたスリラーもの。若尾文子が悪女ぶりを披露。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("love machine", "love machine", "", "", "http://image.eiga.k-img.com/images/movie/58047/315x210.jpg?3816", 27, "トラッシュ・カルチャーの専門誌「TRASH-UP!!」と3人の映画監督がコラボレーションした上映プログラム「コラボ・モンスターズ!!」の1作。憑かれたように理想の女性を追い求める佐伯は、親友の妻・陽子にも手を出す。周囲はそんな佐伯の行動にあきれるが、ある日、陽子が目の前で交通事故に遭い死亡したことから佐伯の運命は狂い始め……。監督は「making of LOVE」「アベックパンチ」の古澤健。", "2012-05-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Great Balls of Fire!", "グレート・ボールズ・オブ・ファイヤー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "1950年代アメリカで若者たちを中心に爆発的な人気を集め、「火の玉ロック」等の大ヒットを飛ばしたロックンローラー、ジェリー・リー・ルイスの波乱に満ちた人生を、「ブレスレス」のジム・マクブライド監督が映画化。ロックンロールと愛する妻マイラに人生を捧げた破天荒な男ジェリーをデニス・クエイドが熱演。また13歳という若さで彼の妻になったマイラを「ビートル・ジュース」で脚光を浴びたウィノナ・ライダーが演じる。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("薫衣草", "ラベンダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「アンナ・マデリーナ」に続く金城武とケリー・チャンの共演作。悲しみに暮れる女性と天使のファンタスティックな恋物語が展開する。恋人を失った悲しみが拭えないアテナの家に天使が落下。びっくりする彼女だが、負傷している彼にしばらくの同居を認める。世話になることになった天使は、彼女の悲しみを知り励まそうとするが……。大きな翼を携えた金城の天使姿に注目。", "2000-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BACK TO BATAAN", "バターンを奪回せよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "第二次世界大戦で“バターン死の行進”を目撃したマンドン大佐。あまりの悲惨な光景を目のあたりにした彼は、ゲリラ部隊を率いて決死の闘いへと突入していく。バターン奪回までの激しい攻防をリアルな人間ドラマとして描き、製作後22年目にして初めて、日本で公開された幻の名作。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ABSOLUTE　GIGANTEN", "ギガンティック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "ドイツの新人監督S・スキッパーがハンブルグを舞台に撮り上げた青春映画。この街で気ままに生きる悪友トリオの一人フロイドが、保護観察処分を解かれたことを機に、貨物船の船員として旅立つというかねてからの夢を実現しようとする。かくして最後の一夜をともに過ごすことになった３人組が、翌朝までドライブやテーブルサッカーなどに興じる姿を、エネルギッシュかつ切なく描き出す。", "1999-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SO FINE", "恋のジーンズ大作戦　巨人の女に手を出すな", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "若い大学教授が父親の会社を手伝うことになり、その会社と恋をめぐる騒動を描いたエロティック・コメディ。浮気の現場がバレて、あわててはいて逃げたジーンズは女もの。ヒップの部分がビリリと裂ける……。この尻出しジーンズが新ファッションとして大流行というお気楽編。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHEN EIGHT BELLS TOLL", "八点鐘が鳴るとき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「ナバロンの要塞」の原作者として知られるA・マクリーンが、自らの同名小説を脚本化した海洋活劇。大量の金塊を積んだ船がアイルランド沖で行方不明となり、英国海軍情報部員フィリップは捜査に乗り出す。やがて彼の周りに怪しい人物が出没し、彼を襲撃し始め……。", "1971-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("曼陀羅／MANDALA", "曼陀羅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "人間的な苦悩を抱えつつ、仏教の真理を求めて韓国全土をさすらう若き修行僧と破戒僧の精神的な葛藤を描く。発表当時衝撃的ベストセラーとなったソンドンの小説を、韓国映画の代表的監督グォンテクが映画化。韓国の四季折々の自然の風景や仏教の美が、格調高い、見事な映像の中に収められている。", "1981-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TEACHER’S PET", "先生のお気に入り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "新聞社社会部の部長ジムは、裸一貫でたたき上げたタフな新聞記者。経験以外の学問などというものはまったく信用しない。その彼が編集局長に呼ばれて、市立大学のジャーナリズム講座での講演を命じられる。当初は断ったものの担任教授は美しいブロンド美人。やがて彼は学生になりすまして教授のお気に入りの生徒になる。C・ゲーブルが持ち前のキャラクターを生かしたラブ・コメディ。", "1958-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Scream", "スクリーム", "", "", "http://ecx.images-amazon.com/images/I/41PZbxaWc7L._SL210_.jpg", 111, "高校生ケイシーとその恋人がマスクをかぶった者によって惨殺されるという事件が起こる。やがて犯人は、ケイシーのクラスメイトで、1年前に母親を殺されていたシドニーに迫る。町には外出禁止令が敷かれ、学生たちは大はしゃぎするが、そんな中を殺人鬼が徘徊。はたしてその正体は？　新鋭脚本家ケビン・ウィリアムソンの手がけた意表をつく物語が話題を呼び、大ヒットを記録したホラーシリーズ第1弾。", "1997-08-23", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラVSビオランテ", "ゴジラVSビオランテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "G細胞を研究していた世界的権威、白神博士の娘、英理加が殺害された。博士はバイオテクノロジーによってゴジラの細胞とバラの細胞、そして英理加の細胞を掛け合わせて超怪獣・ビオランテを生み出す。三原山から５年ぶりに復活したゴジラが、このビオランテを相手に戦う。一般公募ストーリー5025本の中から選ばれた小林晋一郎の作品を大森一樹が脚本化し、監督した。", "1989-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HISTOIRE D’O NUME´RO ２", "O嬢の物語 第二章", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "出版社を買収しようとする大富豪のペンブロウクの野望を阻むために、出版社の常務がマダム・Oを雇い、彼をセックスの虜にし、スキャンダルを起こそうとする。1975年度作「O嬢の物語」の続編だが、キャラクターを借りただけで前作との関連性はない。", "1984-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Milk", "ミルク", "", "", "http://image.eiga.k-img.com/images/movie/53227/315x210.jpg?2080", 128, "同性愛者であることを公表した上で、米国史上初めて公職に就いた政治家ハービー・ミルクの半生を描いた伝記ドラマ。1970年代のサンフランシスコ。生来の人柄でゲイやヒッピーたちに慕われる同性愛者のミルクは、マイノリティに対する権利と機会の平等を求め、世間の差別や偏見と戦いながら市制執行委員会の選挙に立候補する。第81回アカデミー賞では、主演男優賞（ショーン・ペン）、オリジナル脚本賞を受賞。監督は「エレファント」のガス・バン・サント。", "2009-04-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JOHNNY MNEMONIC", "JM", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "全世界が巨大コンピュータ・ネットワークにより、国境を越えて結ばれている2021年、人類はあらゆる電子機器から放射される電磁波による環境汚染が原因で“NAS”と呼ばれる奇病に冒されていた。ジョニーは、脳に移植されたシリコンチップに直接情報を記憶し、情報の密輸をしている“運び屋”。仕事から足を洗うべく、チップを摘出するための資金を調達するため受けた最後の仕事とは、北京からニューアーク・シティまである情報を運ぶことだった。ジョニーは情報を受けとったその日から、人類の存亡をかけた壮絶な戦いに巻き込まれていく。テクノロジーの飛躍的進歩により誕生した超・情報化社会を舞台に繰り広げられるサイバー・パンク・ムービー。監督のロンゴはN.Y.アート界の才人。ビートたけしが、未来都市ニューアークを支配する日本やくざのボス、タカハシを熱演。ヴィジュアル・コンサルタントとしてシド・ミードが参加している。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("書の道", "書の道", "", "", "http://image.eiga.k-img.com/images/movie/54927/315x210.jpg?1386", 93, "ボクシングの夢に挫折した駿一は、目標も定まらないまま大学へ進学する。そこで書道に出会った駿一は、書道部の仲間たちと全国大学書道大会に向けて特訓の日々を送っていた。一方、駿一の幼なじみの健太郎は、ボクシングのプロテスト合格を目指しトレーニングを続けていた。主演はD-BOYSの柳下大、共演に古原靖久、古川雄大、三浦涼介、河合龍之介ら若手人気俳優が揃う。", "2009-12-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("信さん　炭坑町のセレナーデ", "信さん　炭坑町のセレナーデ", "", "", "http://image.eiga.k-img.com/images/movie/54177/315x210.jpg?2127", 108, "「愛を乞うひと」「OUT」の平山秀幸監督が、同郷の辻内智貴による小説を映画化。主演は小雪、共演に池松壮亮、石田卓也、大竹しのぶ、岸部一徳らが顔を揃えた。昭和38年、美智代は故郷である福岡の炭坑町に、小学生のひとり息子・守とともに帰ってくる。ある日、信一という少年が、町になじめずいじめられていた守を助けてくれる。親を早くに亡くした信一は町の厄介者だったが、美智代は息子を守ってくれた信一に優しく接し、やがて信一は美智代に淡い恋心のようなものを抱くようになる。", "2010-11-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風立ちぬ", "風立ちぬ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "太平洋戦争さなかの1942年、軽井沢の別荘で病気療養中の少女と、誠実な青年が出会って愛し合い、結婚を誓う。周囲も温かく見守っていたが、少女の病気は確実に彼女の身体をむしばんでいった。堀辰雄の原作に当時空前の人気コンビ・百恵＝友和が挑んだ、純愛に満ちた泣けるラブ・ストーリー。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("しらんぷり", "しらんぷり", "", "", "http://image.eiga.k-img.com/images/movie/57977/315x210.jpg?3632", 25, "アニメーションの制作環境の問題解決や振興を図ることを目的とした文化庁の若手アニメーター育成プロジェクト「アニメミライ」2011年度制作の4作品のうちの1本。小学6年生のボクは、いじめっ子のヤラガセら4人組がドンチャンをいじめていることを知りながらも、巻き込まれるのが怖くて知らんぷりをしてしまう。ヤラガセたちの行動はエスカレートしていくが、ある日、ドンチャンが思わぬ行動に出て……。いじめ問題を取り上げ、1998年度課題図書にもなった同名絵本を、白組がアニメーション化。絵本風の手描きのタッチが取り入れられている。", "2012-03-24", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JAWS THE REVENGE", "ジョーズ1987 復讐篇", "", "", "http://ecx.images-amazon.com/images/I/51TXR9V23PL._SL210_.jpg", 90, "人気シリーズの４作目。鮫に殺された息子の仇を討つべく、その母親と兄が決死の戦いを挑んでいくというもので、名優M・ケインが母親の友人役で出演。ビデオ発売時タイトル、DVDタイトルは「ジョーズ４　復讐篇」。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("shoes", "shoes", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "バレエダンサーを目指す女性が、暴走しはじめた自らの夢に飲みこまれていく姿を描いたダーク・ファンタジー。アルバイトをしながらバレエダンサーの養成所に通う20歳のナオは、愛するバレエをずっと踊り続けたいと願う一方で、自分の才能に自信が持てずにいた。そんなある日、バレエ団の入団テストを受けた彼女は、帰り道で1足の美しい靴を見つけ……。メガホンをとるのは、CMディレクター出身の山本英行。", "2006-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOB ROBERTS", "ボブ・ロバーツ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "億万長者の歌手ボブ・ロバーツが上院議員選に立候補した。聡明で行動力のあるボブは支持者を増やしてゆき、古株の現職議員と一騎打ちとなった選挙戦は激しくなるばかり。ライバルのスキャンダルが暴かれたと思えばボブの黒い噂も流れ、戦いは泥試合の様相をみせる。ところがある日、ボブは何者かの凶弾に倒れた。彼を撃ったのは誰か？　ボブは復帰できるのだろうか？　ボブ・ロバーツという人物の架空のドキュメンタリーという形をとり、テレビニュース、プロモーションMTV、インタビューなどが重層的に展開する風刺劇。人気俳優T・ロビンスがその才能を遺憾なく発揮した監督第１作だ。", "1992-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PSYCHO III", "サイコ３／怨霊の囁き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ヒッチコックの代表的スリラー「サイコ」の主人公、ノーマンのその後を描いた作品。今回の特徴は、主演のパーキンスが監督としてデビューし、演出にあたったこと。主人公ノーマンのすべてを知りつくしたパーキンスの手で、再びあの忌まわしい惨劇がベイツ・モーテルを舞台に引き起こされる。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Anne of Green Gables:The Sequel", "続・赤毛のアン　アンの青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "　大ヒットした「赤毛のアン」から2年後に公開された続編。孤児院から引き取られて5年、マシュウ亡き後マリラと暮らす18歳のアンは、周りが大人へと変化していく様子に寂しさを感じていた。かつてはいがみ合っていたギルバートからの告白、親友ダイアナの結婚……。自然豊かなグリーン・ゲイブルズから都会のキングスポートへ舞台を変え、恋や教師生活を通して成長していくアンの姿が綴られる。", "1988-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Ninja Assassin", "ニンジャ・アサシン", "", "", "http://image.eiga.k-img.com/images/movie/54537/315x210.jpg?3964", 99, "「マトリックス」シリーズのスタッフが製作、韓流スターのRain（ピ）が主演する忍者アクション映画。暗殺集団「小角（おづぬ）の一族」に育てられた殺し屋の雷蔵は、ともに修行を積んだ仲間を一族に容赦なく処刑されたことで、抜け忍となり復讐を誓う。一方、欧州警察組織のユーロポールの科学捜査官ミカは、暗殺者の闇ネットワークを捜査しているうち、「小角の一族」に自身の命を狙われてしまう。", "2010-03-06", "1,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("オーバーヒート　輝きの先に", "オーバーヒート　輝きの先に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2007-10-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魁!!男塾 劇場版", "魁!!男塾 劇場版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "「北斗の拳」「ドラゴンボール」と同じく、1980年代の『週刊少年ジャンプ』に連載された人気コミックスを原作にした劇場版格闘アニメ。暴れて退学させられた不良男子学生ばかりを集める私塾が舞台。鬼の塾長の下、その暴れっぷりを磨き、さまざまな戦いを続けて格闘家として成長する主人公の姿を描く。本作では、日本とアメリカの選手軍団が凄まじい死闘を繰り広げる。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ДОЛГИЕ ПРОВОДЫ", "長い見送り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ウクライナ、オデッサ劇映画製作所の女性監督、K・ムラートワが1971年に撮り、実に16年間にもわたって上映禁止となっていた作品。16歳の息子サーシャと、彼だけが生きがいの母親エフゲーニヤ。母親の愛情に息がつまりそうになる息子と、彼なしでは生きていけない母親との葛藤を物語の主軸にして生々しく描きながらも、躍動感ある映像表現が圧倒的にさわやかな印象を与えている。O・カラバイチュークによる音楽も味わい深い。", "1971-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歩く、人", "歩く、人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "「海賊版＝BOOTLEG FILM」「殺し」の小林政広監督が16年前に書いたシナリオをベテラン俳優、緒形拳が発掘、自らが出演を希望して映画化に至った作品。愛妻をガンで亡くし、次男と二人暮らしをしながら酒屋を営む本間信雄66歳。信雄は、２日後に控えた妻の三回忌をきっかけに、12年前に家出した長男との絆を取り戻そうと考えていた。雪深い北海道増毛町を舞台に、孤独を抱えた頑固親父と息子、彼らを見守る女性たちの生き様を真正面から見据えた家族ドラマ。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE YOUNG WARRIORS", "重戦車総攻撃", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "第二次世界大戦下のヨーロッパ戦線。次々と戦場に送られてくる新兵たち。未熟な彼らを指揮するクーリー軍曹は絶体絶命の危機を乗り越え、部下たちを一人前の兵士へと育てあげるのだった。監督は、TV映画『コンバット』を数多く手掛けたJ・ペイザー。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPUN", "スパン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "スウェーデン出身のミュージック・クリップ作家、J・アカーランドの映画監督デビュー作。ドラッグを手に入れたい一心で伝説的なドラッグ製造人のお抱え運転手となった青年が、次々とおかしなトラブルに巻き込まれていく。ダメ人間＆変人ぞろいの個性的なキャラクター、ストリート感覚あふれるファッションや音楽に、作り手のこだわりが感じられる青春ドラマ。ストリッパーのニッキーと知り合ったロスは、ニッキーの恋人でスゴ腕の麻薬調合屋コックに紹介される。コックからドラッグを譲ってもらったロスは、パシリ＆運転手として働かされるハメに。一方、警察はドラッグ密売人の内偵中で……。", "2002-01-01", "1,14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鞍馬天狗（活弁トーキー版）", "鞍馬天狗（活弁トーキー版）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "嵐寛寿郎がマキノプロから独立し、自分の嵐寛寿郎プロで主演した第１回作品。杉作と桂小五郎によって大阪城内の水牢から救われた鞍馬天狗は、剣鬼・佐々木唯三郎や好敵手・近藤勇らと死闘を展開する。", "1928-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("三たびの海峡", "三たびの海峡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "第二次大戦末期から現代までの日本と朝鮮半島を舞台に、最初は強制されて、その後は自分の意思で、両国の間に横たわる海峡を三たび渡った韓国人・河時根（ハー・シグン）の憎しみと愛に彩られた半生を描いた大河ロマン。「月光の夏」「ひめゆりの塔」と、ヒューマニズムあふれる作風が高く評価される神山征二郎が本作でも難しいテーマを見事に演出。キャストは総じて好演だが、とりわけ主人公の妻に扮した南野陽子が、時代の波にほんろうされる女の哀しみを絶妙に表現した。原作は吉川英治文学新人賞を受賞した同名小説。日本映画としては初めての韓国ロケを行った作品。韓国で試写が予定されたが、韓国政府の反対により、中止された。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BIG TOWN", "ビッグタウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "M・ディロンとD・レインが、それぞれギャンブラーとストリッパーに扮して話題となった青春映画。1950年代のシカゴを舞台に、田舎出身の若手のすご腕ギャンブラーが、賭博場で出会った魅力的なストリッパーと危険な恋に落ちていくさまをスリリングに描いている。バックに流れるボビー・ダーリンやレイ・チャールズらのヒット・ナンバーの数々も効果的。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("続・社長紳士録", "続・社長紳士録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "大正製袋の小泉社長は新潟の“北越瓦斯”が化学肥料を大量生産するという情報を入手。部下を連れて新潟へ入ったはよかったが、美人芸者・菊千代とお楽しみになってしまう。やがて、それが妻・貞子の耳に入ってパニックとなる。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鼠胆龍威／HIGH RISK", "ハイリスク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ドクターと呼ばれるテロリスト集団に、爆弾で妻子を殺された特殊部隊の中尉リーは、妻子を救えなかったショックから軍を去る。事件の二年後、映画スターのボディガード兼スタントマンをしているリーの前に再び悪夢がよみがえった。ロシアの秘宝を展示したホテルを、ドクターの一味が占拠したのだ。たまたまその場に居合わせたリーは、いち早く警察に通報。ホテルはたちまち機動隊に包囲される。緊迫した膠着状態の中、リーは人質救済のためにホテルに潜入する。果たしてリーは人質を救い、妻子の仇を討つことができるのか？　香港アクションの旗手B・ウォンと、L・リンチェイ(現・ジェット・リー)がタッグを組んだ意欲作。", "1995-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("みやび 三島由紀夫", "みやび 三島由紀夫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "没後35年を迎えてもなお論争の的になっている作家、三島由紀夫について11人の今を生きる人々が語り明かす。狂言師、野村万之丞は“三島は仮面的な人間”と語り、演出家、坂手洋二は“彼の死は演劇的ではない”と批評する。多彩な領域からの発言が、三島の現代性をまざまざと見せつける。能を愛した作家・三島由紀夫。能楽師、関根祥人が『清経』を舞うなか、三島と直接のかかわりのなかった人々による独特の三島論が展開される。", "2005-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CAPTAIN NEMO AND THE UNDERWATER CITY", "ネモ船長と海底都市", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "19世紀半ば、ニューヨークから帰航中の英国船が難破、何人かの船客がネモ船長の潜水艦ノーチラス号に救助され海底都市に連れていかれるが……。戦後、短編児童劇映画を多く発表しているヒル監督が、得意のジャンルで本領を発揮した海洋SFアクション。", "1969-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猿飛佐助II／闇の軍団", "猿飛佐助II／闇の軍団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "松方弘樹が闇の仕事人、猿飛佐助に扮する忍者アクションの第2作。戦国時代を背景に、豊臣側につく佐助率いる“闇の軍団”が、千葉真一扮する柳生十兵衛ら徳川の送り込む刺客と妖刀をめぐり死闘を繰り広げる。松方、千葉の両ベテラン及び、若手俳優たちが見せるド派手なアクションが見どころ。元和元年、真田幸村の命を受けた猿飛佐助は、手にしたものが天下を取るとされる妖刀を探す。だが、その刀を敵方の柳生十兵衛も狙っていた。", "2005-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COP GAME", "サイゴン野獣刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "陥落直前のサイゴンで殺人事件が発生し、捜査を命じられたCID（陸軍犯罪捜査分遣隊）の警官とベトナム人警官。だが、捜査が進むうち事件の関係者が次々と殺され、その裏にKGBの顔が見え隠れする。ベトナム戦争下の混乱の町を舞台にした、複雑な人間関係のなかに展開するポリス・アクション。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("拝啓総理大臣様", "拝啓総理大臣様", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「拝啓天皇陛下様」に始まる野村芳太郎監督と渥美清のコンビによる“拝啓”３部作の最終作。昔の相棒が時事漫才“拝啓総理大臣様”で人気を呼んでいるのを横目で見ながら、鶴川角丸は幼なじみの混血児と組んでドサ回りを続ける。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Noise", "NOISE", "", "", "http://image.eiga.k-img.com/images/movie/54607/315x210.jpg?4062", 120, "コンサート、朗読、実験映画など多彩なプログラムで構成されるフランスのアート・ロック・フェスティバルを記録した音楽ドキュメンタリー。「夏時間の庭」「クリーン」などで知られるオリビエ・アサイヤスが監督を務める。2005年6月、ニューヨークの音楽シーンを牽引する“ソニック・ユース”が2人2組に分かれて出演した斬新なライブ映像を収録。", "2009-07-11", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天地無用！ in LOVE２　遥かなる想い", "天地無用！ in LOVE２　遥かなる想い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "オリジナルビデオからスタートし、TV、映画に進出した人気アニメ・シリーズの完結編。時空をかける女難の高校生、天地の“最後の決断”がユーモラスに、そしてせつなく描かれる。宇宙海賊の美女たちの心配をよそに、突然、姿を消した天地。気がつけば彼は、ハルナという女性と夫婦同然の暮らしをしていた。やがてハルナの正体や天地の意外な過去が明らかになるが……。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　PAINTED　DEVIL", "彩られし女性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "伝染病を研究する夫とともに、香港にやってきたカトリン。しかし、夫は研究一筋で家を空けることも多かった。やがて、見知らぬ土地で一人寂しさを募らせていたカトリンは、英国人クラブで偶然知り合った外交官と恋に落ちてしまう。しかし、そのことが夫の耳に入ってしまい……。グレタ・ガルボの美しさも忘れがたいメロドラマ。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BIG GIRLS DON'T CRY", "ビタースウィート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "17歳の少女たちの危うい内面を現実味たっぷりに描き出す青春ドラマ。ヒロインを等身大で演じきったドイツ期待の新星女優アンナ・マリア・ミューエとカロリーネ・ヘルフルトに注目が集まった。典型的な中流家庭のカティと裕福な家に生まれたステフィ。家庭環境は違えど大親友の二人は、ある日、ステフィの父の浮気現場を目撃してしまう。これに深いショックを受けたステフィの心は屈折、浮気相手の女性の娘に復讐を誓う。", "2002-01-01", "7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Princess Diaries", "プリティ・プリンセス", "", "", "http://ecx.images-amazon.com/images/I/41AHC7HV0XL._SL210_.jpg", 115, "ドジでさえない女子高生が、突然、ある王国のプリンセスで唯一の王位継承者であることがわかる。戸惑いながらもプリンセスになるための教育を受けることになるのだが……。内気な女の子の成長を描いたロマンチック・コメディ。主演のアン・ハサウェイは新人。", "2002-01-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fedora", "悲愁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "　ビリー・ワイルダーが29年前に製作した「サンセット大通り」同様、ハリウッドの内幕をテーマにしたシリアスドラマ。映画製作者バリーは、引退した名女優フェドーラをカムバックさせようと彼女の住むギリシャヘ向かう。初めは面会すらできなかったが、熱心な説得により出演の承諾を得る。バリー役は、「サンセット大通り」で若き脚本家役を演じたウィリアム・ホールデン。ヘンリー・フォンダがアカデミー会長“ヘンリー・フォンダ”役でカメオ出演している。", "1980-02-23", "1,2,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("折り梅", "折り梅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "デビュー「ユキエ」で好評を得た松井久子監督が、突然の試練を乗り越えていく家族の絆を描いた第２作。物語の舞台である愛知県豊明市を中心に市民運動が起こり、映画製作が後押しされた経緯のある人間ドラマだ。平凡な４人家族の主婦・巴の家に、夫の母親・政子が同居することに。まもなく政子はアルツハイマー症だと診断され、彼女の世話とパート、家事をかけ持ちする巴は疲労で寝込んでしまう。しかしこれまでの人生を振り返る政子の言葉に耳を傾けた巴は、彼女のあるがままを受け止めようとする。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RHYTHM　IS　IT！", "ベルリン・フィルと子どもたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ベルリン・フィルハーモニー管弦楽団の芸術監督サー・サイモン・ラトルが発足させたダンス・プロジェクトの模様を収録したドキュメンタリー。総勢250名の子供たちが踊りを学び、ベルリン・フィル演奏のバレエ曲で踊るまでの過程を見つめる。このプロジェクトに集められた子供たちは、問題児ばかりで始めは無気力そのもの。そんな彼らがダンスと音楽を通して自分自身を変え、最後に披露する踊りは言葉にならないほど素晴らしい。音楽の持つ可能性をあらためて感じさせる１本だ。", "2004-12-04", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超星艦隊セイザーX／　戦え！星の戦士たち", "超星艦隊セイザーX／　戦え！星の戦士たち", "", "", "http://ecx.images-amazon.com/images/I/61PRZYP5ARL._SL210_.jpg", 70, "キッズ世代の人気を集めたヒーロー・ドラマの劇場版。平和を愛するヒーロー戦士、セイザーXが、全宇宙の支配を目論む宇宙海賊相手に戦いを挑む。「超星神グランセイザー」「幻星神ジャスティライザー」に続く“超星神”シリーズの第3弾に当たる今回は、グランセイザー、ジャスティライザーなど、過去のヒーローも揃い踏み。得意技を武器に各戦士が大活躍を見せる。また、宇宙最強最悪の生命体、ボスキートが登場。セイザーXとボスキートの壮絶を極める大バトルはファン必見だ。西暦2500年、邪悪な宇宙海賊が全宇宙を支配するパワーを持つ“コスモカプセル”を強奪、宇宙は危機に。もとの平和を取り戻すため、超星艦隊セイザーXの戦士たちが立ち上がる。", "2005-12-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMPANY BUSINESS", "ロシアン・ルーレット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "CIAを引退したサムは、米ソのスパイ交換任務を遂行するため、投獄中のKGB工作員グリシェンコを連れてベルリンへ向かう。だが、この取引には国家間の陰謀が絡んでいた。驚くべき事実を知った二人を、CIAとKGBが協力して抹殺しようとする。共産主義崩壊につれて起こり得る様々な可能性を推測したサスペンス・スリラー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミートボールマシン", "ミートボールマシン", "", "", "http://image.eiga.k-img.com/images/movie/29087/315x210.jpg?2609", 90, "謎の生命体に寄生され恐ろしい怪物と化した人間たちが繰り広げる死闘を、スプラッター描写満載で描くホラー・アクション。互いに惹かれ合いながらも、怪物ネクロボーグとなり非情な運命の渦に巻き込まれていく男女を「世界の中心で、愛を叫ぶ」の高橋一生と「ガールフレンド」の河井青葉が熱演。監督は「魁!!クロマティ高校 THE★MOVIE」「地獄甲子園」の鬼才・山口雄大と、自主製作のオリジナル版でもメガホンを取った山本淳一。", "2006-09-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES ANNE´ES SANDWICHES", "サンドイッチの年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "第二次大戦後の1947年。両親をナチス収容所に連行された少年ヴィクトールは、身を寄せた家を飛び出し、パリの古物商に住み込みで働くことになる。老主人マックスは同じユダヤ人。そんな中、良家の息子フェリックスとも友だちになり、人生の貴重な日々を過ごす。少年の心を細やかに描いた佳作。", "1988-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SESSOMATTO", "セッソ・マット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "庶民派コメディの旗手、ディーノ・リージ監督によるセックス・コメディ。9組の妖艶無恥な男女が繰り広げる性のドラマを、お洒落なサウンドに乗せて華麗に描出。若妻、娼婦、未亡人や看護婦を演じ分ける、ラウラ・アントネッリの妖艶な色気にノックアウトされる。豪邸で優雅な生活を送る実業家マダム。早起きが苦手なのか、朝の8時に目を覚まさない彼女には秘密が……。(「奥様８時です」)", "2005-08-20", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神經刀與飛天猫", "フライング・バトル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "レオン・カーファイ、グロリア・イップら、香港と台湾のスターが競演するアクション。“大飛刀”と異名をとる武術家ホン・チャンは、甥のホン・ランと組んで賞金稼ぎとして活躍。一方、二人の美人姉妹は名うての女流武術家であるが、それぞれホン・チャン、ホン・ランに片思いの身。何かと仕事の邪魔をしてはホン・チャンを困らせている。そんななか大盗賊が出現。彼らは街を救うため、妖術や剣術を駆使して死闘を繰り広げる。", "1993-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LADY CAROLINE LAMB", "レディ・カロライン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "19世紀のイギリス貴族社会を舞台に、天才詩人バイロンに恋した人妻の悲劇を描く時代劇。「アラビアのロレンス」など一連のデヴィッド・リーン作品の脚本を手掛けたボルトの監督第１作。豪華キャストが見どころ。", "1973-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うつし世の静寂（しじま）に", "うつし世の静寂（しじま）に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "多摩丘陵の広がる川崎市北部を舞台に、古来の風習を守り続ける人々の姿を通して人と風土との関係を見つめるドキュメンタリー。人々が円座して数珠を繰り回す「念仏講」、地蔵を背負って家々を巡る「巡り地蔵」、土俵の上を舞う「初山獅子舞」など、近代化のなかにあっても土地の神々や祖先に祈りを奉げ続ける住民たちにスポットを当てる。2008年度文化庁映画賞に輝いた「オオカミの護符」の由井英監督がメガホンをとる。", "2010-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BORN LOSERS", "地獄の天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "カリフォルニアの山岳地帯に位置する田舎町を舞台に、ダニー・カーモディをリーダーとする暴走族グループに、一人敢然と立ち向かう青年ビリー・ジャックの姿を描いたアクション映画。己の正義を信じて戦う男ビリーの運命は……。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TORSO", "影なき淫獣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "猟奇性とエロティシズムを押し出したイタリアン・ホラー。とある学生街で、女子学生だけを狙った連続殺人事件が起こる。その手口は、死体の手や足、乳房を切断するという残虐なものだった。犯人の手がかりは、現場に残されていたスカーフのみ。そんな中、女子大生のジェーンは友人とともに別荘に遊びに行くが、そこに殺人鬼の魔の手が迫る。", "1973-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガンバとカワウソの冒険", "ガンバとカワウソの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "'75年に連続TVアニメとして放映され、多くの視聴者を感動させた動物アニメの劇場版。児童文学の傑作を原作に、ガンバという名のドブネズミが仲間を励まし勇気付けていく、ディズニー・タッチの作品だ。今回はカワウソがガンバに助けを求めてくる。行方不明になったカワウソを探してほしいとの依頼だった。ガンバと仲間のネズミたちは、彼を探す旅に出るが……。", "1991-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("教祖誕生", "教祖誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "映画監督としても異彩を放つ才人、ビートたけしのベストセラーをベースにした社会風刺喜劇。布教活動をしている新興宗教の一行に興味本位で同行することになった青年が、やがて新たな教祖に祀り上げられ、次第に教祖としての自覚を持ち始めていく。たけし作品の助監督を務めた天間敏広の初演出作。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セカンド・ラブ", "セカンド・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "一見幸福そうな夫婦が、互いに隠しごとをしていたことから相手を詮索するようになり、不快ないたずら電話や、見知らぬ男の死体発見に至って亀裂はさらに深まっていく。現代風俗を新しい感性でスケッチする東陽一監督らしい作品。大原麗子が好演。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROJECT SHADOWCHASER2", "シャドー・チェイサー2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "無敵のアンドロイドと人間の対決を描いたSFアクションのシリーズ第２作。核兵器研究所が人間を憎むアンドロイドとテロリストたちに占拠され、マイクロチップを持って逃げた研究所主任フランクの息子ローリーが人質にとられた。だが、研究所の外では軍の命令で機動隊が出動し、研究所破壊を進行。それを知ったフランクは息子を救うため、決死の戦いに挑む。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RYAN", "RYAN［ライアン］", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 14, "カナダ出身のアニメーション作家、ライアン・ラーキンの栄光と挫折を描いたドキュメンタリー・アニメーション。60年代に実験アニメのパイオニアとして、また「歩く」（1968）でアカデミー短編アニメ映画賞にノミネートされるなど不動の地位を築いた伝説のアニメーター、ライアン。彼の栄光と挫折の軌跡を斬新な構成で描き、第77回アカデミー短編アニメ映画賞に輝いた傑作だ。モントリオール出身の紳士、そして今やトロントのホームレス、ライアン・ラーキン。その栄光と挫折について彼は静かに語り始める。", "2004-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les anges du peche", "罪の天使たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「抵抗（レジスタンス）　死刑囚の手記より」「ラルジャン」などで知られ、ヌーベルバーグにも影響をもたらしたフランスのロベール・ブレッソン監督のデビュー作。女子修道院を舞台に、あつい信仰心と使命感に満ちたアンヌ＝マリーと不幸な犯罪に手を染めた若い娘テレーズとの対峙（たいじ）を通して、修道女たちの葛藤（かっとう）や憎しみ、友愛を描く。", "2010-02-20", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初笑い びっくり武士道", "初笑い びっくり武士道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "山本周五郎のユーモア時代小説のほぼ忠実な映画化。臆病な旗本が命じられた上意討ち。相手を追って旅の空、世渡りにたけた旅人や美しい女将と出会いながら、自らの臆病さを武器にする奇抜な作戦を思いつく。アメリカ映画のようにカラリと楽しい人情喜劇。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Perfect Host", "パーフェクト・ホスト　悪夢の晩餐会", "", "", "http://image.eiga.k-img.com/images/movie/56257/315x210.jpg?4821", 93, "警察に追われる銀行強盗の青年ジョンは、気ままな独身生活を送る中年紳士ウォーウィックの豪邸に転がり込み、どんな客人でも温かくもてなす完ぺきなホストであることが信条のウォーウィックは、ジョンを招き入れる。ジョンはふとしたことから強盗であることがばれ、包丁を振りかざしてその場を支配しようとするが、ウォーウィックの異常な本性が次第にあらわになっていき……。オーストラリア出身の新鋭ニック・トムニー監督が、自作短編「Host」を長編化したデビュー作。", "2011-05-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DRDINATY DECENT CRIMINAL", "私が愛したギャングスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "黒いヘルメットに覆面、ダブリンの街を颯爽とバイクで銀行強盗に向かう伝説的強盗のマイケル・リンチ。彼は銀行強盗を繰り返す強盗団のカリスマ的なボスだが、家に戻ると優しいマイホームパパに変身してしまう……。実在したダブリンの伝説的強盗マーティン・カーヒルをモデルにした愉快なエンターテイメント・ムービー。ルパン三世さながらの強盗をK・スペイシーが妙演している。", "1999-01-01", "3,33", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAFFERTY AND THE GOLD DUST TWINS", "ブルージーンズ・ジャーニー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "孤独でさえない中年男が二人の不良娘を連れて、オンボロ車でロサンゼルスからニューヨークへあてのない旅に出る。この道中を通じ３人に奇妙な連帯意識が芽ばえ、強い絆で結ばれる。男が不良娘の一人を養女として迎え入れるまでを情感を込めて描いた人間ドラマ。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女飼い", "女飼い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ヒットしたオリジナル・ビデオ作品の映画化。丸忠物産の秋島は会社創設以来の不良社員だったが、それは仮の姿。彼は、闇に葬り去られる事件の調査をする仕事人だったのだ。ある銀行の財務部長、落合がチンピラに殺された事件は、一件落着していたが、その裏には巨大な陰謀があり、秋島が調査に乗り出す。彼は事件の鍵を握る５人の女たちから、話を聞き出し、事件の背後には、銀行と暴力団と通産省がつながっていることを知るのだった。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Star Wars", "スター・ウォーズ", "", "", "http://ecx.images-amazon.com/images/I/510aww6Ba2L._SL210_.jpg", 121, "遥か彼方の銀河系を舞台に繰りひろげられる善と悪の壮大な戦いを描いた「スター・ウォーズ」シリーズの第1作。帝国の支配下にある銀河系で、反乱軍が帝国の宇宙要塞デス・スターの設計図を奪取した。ダース・ベイダー率いる帝国軍の攻撃を受けた反乱軍の指導者レイア姫は、ドロイドのR2-D2に救援メッセージを託す。やがて砂の惑星タトゥイーンにたどり着いたR2-D2とその相棒C-3POは、農場で暮らす青年ルーク・スカイウォーカーに出会い……。後に本作の前章となる新シリーズ（「エピソード1～3」）が生まれ、本作は全6部作の第4部にあたる「スター・ウォーズ　エピソード4 新たなる希望」とされた。", "1978-06-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EMPIRE RECORDS", "エンパイア・レコード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "深夜12時まで開いているエンパイア・レコードの売りは個性的な店員たち。画家志望のAJは愛しのコリーに告白しようと決意し、店長のジョーに宣言する。しかし、ジョーは店の売り上げ9000ドルとともに消えたルーカスのことで頭がいっぱいだった。そしてコリーの方も、今日プロモーションにやって来る元アイドルのレックスに処女を捧げようと決意していた。そんなところへ、オーナーのミッチが店にやって来る。彼は、店を大手チェーン店に売り払おうと考えていたのだった。そして、そのことを知った店員たちは……。L・タイラーの姿もまぶしい青春映画。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CUTE", "CUTE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アルバイトをしながら成功を夢見る新人モデルの女の子が、厳しいレッスンや人々との出会いを経てパリコレのランウェイに立つまでを、さわやかに綴る青春サクセス・ストーリー。映画初出演でヒロインのミキに扮した未希は実際にショーなどで活躍するモデルであり、撮影中に受けたパリコレのオーディションに合格。その現実のショーが映画のクライマックス用に収録された。また、奥田瑛二が主人公を勇気づける大物カメラマン役で助演している。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HAUNTED MANSION", "ホーンテッドマンション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ディズニーランドの人気アトラクション“ホーンテッドマンション”のベールに包まれていた秘密が明かされるホラー・ファンタジー。屋敷に住み続ける999人のゴーストや建物の様式美を完全に具現化した驚きの映像マジックが見もの。仕事人間の不動産エージェント、ジム・エヴァースは、せっかくの家族旅行の最中に売出し中の物件に立ち寄ることに。人生最大のビジネス・チャンスになると確信したジムは、嫌がる家族を無理矢理納得させ、屋敷に入るのだが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PAINT YOUR WAGON", "ペンチャー・ワゴン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 161, "ブロードウェイで上演され好評を博した同名ミュージカルの映画化。ゴールドラッシュに沸く西部の町。一獲千金を夢見てやって来たベンは気は短くてケンカっ早く、大酒飲みだが憎めない男。そんな彼の相棒は二枚目のパードナー。二人は仲良く金を探していたのだが、女性のいない毎日は味気ない。そこで二人の妻を持つ男から美人を800ドルで譲り受けた。そして三角関係のもつれから街は大騒ぎに。ダイナミックなミュージカル西部劇。", "1969-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HUD", "ハッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "P・ニューマンが、空虚な心を抱える大牧場主の息子に扮したウエスタン。かつて無謀運転で実の兄を殺してしまったハッドが、故郷のテキサスに帰って来る。だが父ホーマーはわだかまりを捨て切れず、二人の間には深い溝ができていた……。", "1962-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガンドレス", "ガンドレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "西暦2100年、電脳有機国際都市ベイサイドシティとして生まれ変わった横浜。国際犯罪の温床となりつつあるこの街を舞台に、超法規的な５人のバウンサーレディの活躍を、スタイリッシュなアニメ映像で活写するSFアクション。美しい戦闘服を着こなす個性豊かなキャラと、斬新なメカ・デザインが見もの。「攻殻機動隊／GHOST IN THE SHELL」の士郎正宗が設定協力で参加している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かすかな光へ", "かすかな光へ", "", "", "http://image.eiga.k-img.com/images/movie/56747/315x210.jpg?1234", 84, "1918年生まれの93歳で現在も現役の教育研究者として精力的に活動している大田堯のドキュメンタリー。過酷な戦争体験や、敗戦直後にさまざまな職業の住民参加の中で取り組んだ「民衆の学校」づくりとその挫折、高度経済成長期に国を被告とした家永教科書裁判などを経て、大田がつかんだ「教育」の姿に迫る。", "2011-07-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mon Oncle", "ぼくの伯父さん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "ジャック・タチの代表的キャラクター、ユロ氏が日本で初めて紹介された作品。超モダンな邸宅に住む社長夫妻の息子ジェラールは、下町のアパートで気ままに暮らすユロ伯父さんと遊ぶのが大好き。ところが夫妻は定職につかないユロ氏を気に入らず、お嫁さんを押しつけようとパーティを開催したり、自分の経営するプラスチック工場で雇ったりと躍起になる。アカデミー賞最優秀外国語映画賞、カンヌ国際映画祭審査員特別賞を受賞。", "1958-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH WISH３", "スーパー・マグナム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「狼よさらば」「ロサンゼルス」に続くブロンソン、ウィナーのコンビ作“デスウィッシュ”シリーズ第３弾。今作では、戦友を殺したN.Y.のストリート・パンク・ギャングをけ散らす。温厚だった住民もともにストリート・ギャングに立ち向かうあたりは、かつての西部劇調。ハードなバイオレンスもの。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("親分を倒せ", "親分を倒せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "マッギヴァーンの原作を、石井輝男監督と高倉健のコンビで映画化したギャング映画。金の誘惑に負け、ギャングに落ちぶれた元刑事の主人公が、殺人現場を目撃した弟を殺され、ギャング組織に復讐の牙をむく。まだチンピラの臭いを残した高倉健が熱演。", "1963-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LOST PATROL", "肉弾鬼中隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "J・フォードが初めて戦争ドラマに挑戦した異色作。第一次大戦中のメソポタミアを舞台に、砂漠に迷い込んだイギリス軍中隊と、それを襲うアラブ狙撃兵の壮絶な戦いを描く。過酷な条件のもとで発狂していく兵士や、冷血な殺りくを繰り返すアラブ兵などの人間描写があざやか。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あかね空", "あかね空", "", "", "http://image.eiga.k-img.com/images/movie/33907/315x210.jpg?1268", 120, "人情時代小説の名手、山本一力の直木賞受賞作を内野聖陽＆中谷美紀主演で映画化した感動作。京で豆腐作りの修行を積んだ永吉と江戸っ子のおふみ。江戸で出会ったふたりはすぐに惹かれ合い、深川の長屋に小さな豆腐店を開いた。永吉が作る京の豆腐は江戸の人々にはなかなか受け入れられなかったが、気丈なおふみの努力のかいあって店は次第に繁盛しはじめる。彼らはやがて3人の子どもにも恵まれ、幸せな日々を送っていたが……。", "2007-03-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("轟天龍虎會", "チャイナホワイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "アムステルダムのカジノを舞台に、麻薬取引を巡る中国人ギャングとマフィアの抗争を描く実録アクション。チャイナタウンのゴッドファーザーが、対抗組織のマフィアのボスによって殺された。跡目を任された甥のボビーらは、中国人組織を結集して復讐を決意する。一方麻薬捜査のため、カジノに潜入していた国際警察の女性捜査官アンは、窮地をボビーに助けられ、次第に思いを寄せていく。", "1990-09-30", "1,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HOWLING", "ハウリング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "TVの女性キャスターが殺人事件の犯人に呼び出される。彼女は、そこでモンスターの影を見て激しいショックを受け、田舎に休養に出かけるが、そこは狼人間たちの巣窟だった……。“狼男もの”の新解釈による現代版で、ロブ・ボーディンの変身メイクは一見の価値あり。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("９.１１−８.１５　日本心中", "９.１１−８.１５　日本心中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 145, "9.11以後、混迷を極める世界において、私たちの向かう先は？　異端の美術作家・大浦信行が、舞踏や現代アートを織り交ぜて、観るものにその問いかけを投げかける革新的なドキュメンタリー。世界を放浪する出演者たちの思想が、鮮烈な映像と共に胸に迫る。美術批評家・針生一郎、そして、連合赤軍リーダー・重信房子を母に持つ重信メイ。二人はそれぞれの思想に導かれるように世界を旅していく。やがて、二人は朝鮮半島に向かうが……。", "2005-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LIAR’S MOON", "初恋物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "1949年のテキサス。ジャックとジニーは愛し合うようになるが、なぜかジャックの母親とジニーの父親は、二人が交際することに大反対。実はそこには隠された秘密があって……。ディロンが主演した青春ラブ・ストーリー。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bill and Ted's Bogus Journey", "ビルとテッドの地獄旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "ロックスターを夢見る2人のおバカな高校生が繰り広げる時間旅行を描いたコメディ「ビルとテッドの大冒険」の続編で、前作に引き続きキアヌ・リーブスとアレックス・ウィンターが主演をつとめる。未来の悪の軍団の親分デ・ノモロスは、将来ロックスターとして世界を平和に導く運命にあるビルとテッドを抹殺しようと、2人にそっくりなサイボーグを送り込む。あっさり殺されてしまった2人は、ゴーストとなって地獄へ堕ちるが……。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MEN DON’T LEAVE", "メン・ドント・リーブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "家族を襲った不幸や苦難に屈することなく力強く生き抜く女性を、オスカー女優J・ラングが演じる感動作。「ミザリー」で偏執的な愛を注ぐ狂気の女を怪演して、やはりオスカーを獲得したK・ベイツも出演している。", "1989-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paranormal Activity 4", "パラノーマル・アクティビティ4（仮題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-11-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死びとの恋わずらい", "死びとの恋わずらい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "恐怖コミック作家、伊藤潤二の原作に基づくスリラー。故郷の町に引っ越してきた少女、みどりは夜毎、悪夢に悩まされる。そんなある日、“辻占（つじうら）”と呼ばれる恋占いをした同級生が変死を遂げた。事件の背後には、みどりの幼なじみで同じクラスの美少年、龍一の存在が……。幻想的な映像と、龍一役に扮する松田龍平の神秘的な存在感が印象的。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A CHORUS OF DISAPPROVAL", "浮気なシナリオ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "イギリス演劇界を代表し、“イングランドのニール・サイモン”といわれるA・エイクボーンの戯曲の映画化。J・アイアンズ、A・ホプキンスといった、ひとクセもふたクセもある性格俳優のコメディ競演も見もの。海辺の小さな町に赴任してきた男やもめ、ガイは淋しさをまぎらわすため、地方劇団の『三文オペラ』に参加する。芝居を演出する弁護士ダフィッドは、いつも怒鳴ってばかりのワンマン男。そんななかで、ダフィッドの妻や美人の人妻たちに誘惑されるガイは、芝居でも主役に抜てきされてしまう。", "1990-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BALKAN RUNNER", "バルカンランナー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "巨大な麻薬組織に罠にはめられ、特殊部隊の麻薬捜査官が殺害された。未亡人となったその妻マーナは、亡き夫の後を継いで部隊に志願。やがて、彼女は国際捜査官マークの助けを借りて、夫を殺した女スパイへの復讐へと乗り出す……。世界の火薬庫と呼ばれるバルカン半島を舞台にしたハード・アクション。", "1994-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("相棒　劇場版II 警視庁占拠！特命係の一番長い夜", "相棒　劇場版II 警視庁占拠！特命係の一番長い夜", "", "", "http://image.eiga.k-img.com/images/movie/55547/315x210.jpg?4217", 119, "2000年のテレビ初放送から10周年を迎えた大ヒット刑事ドラマ「相棒」の劇場版第2弾。主役コンビの水谷豊と及川光博のほか、ゲスト陣には小西真奈美、小澤征悦らがそろう。警視庁本部内で、警視総監や副総監ら幹部12人を人質とした立てこもり事件が発生。犯人からの要求がないまま時間だけが過ぎていくなか、いち早く事件に気づいた尊と右京は、鑑識の米沢らの協力を得てある奇策に出る。", "2010-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOEL〈ノエル〉", "NOEL〈ノエル〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "これまでCMディレクターやプロデューサーとして活躍してきた梨木友徳の初監督作。人気バンド、“La'cryma Christi”のTAKAら、バラエティーに富んだキャストが注目だ。沖縄で暗殺ビジネスを営む会社“楽園”に、かおるという老婦から依頼が舞い込む。その暗殺を任されたみかさは早速、仕事に取り掛かる。ところが暗殺直前、かおるが自ら手を下したいと言い出す。みかさは困惑するが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Toys", "トイズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "「グッドモーニング，ベトナム」の名コンビ、バリー・レビンソン監督と名優ロビン・ウィリアムズが再びタッグを組んで贈る、戦争批判を盛り込んだファンタジック・コメディ。巨大オモチャ会社を経営する兄が急死したことにより、新社長に就任した軍国主義者の将軍。オモチャを兵器化しようと企む将軍の野望を阻止するべく、前社長の息子で平和主義者のレスリーが立ち上がる。こうしてオモチャ対オモチャの大戦争が始まった……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ENNUI", "倦怠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "フランスの気鋭C・カーンが、A・モラヴィアの同名小説を映画化した凶暴な恋愛劇。妻と別れ、知らず知らずのうちにストレスが溜まった哲学教授が、年老いた画家を腹上死させた少女と肉体関係を持つ。やがて彼女を支配したい欲求に駆られた教授が、ストーカーまがいの暴走を繰り広げる姿を描き出していく。C・ベルリング扮する主人公の壊れっぷりもさることながら、彼に対してひたすらドライな態度をとり続け、セックスにだけ情熱を燃やすヒロインのキャラクターが鮮烈。", "1998-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DUPLEX", "おまけつき新婚生活", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "超売れっ子スターのベン・スティラーとドリュー・バリモアが新婚夫婦役で共演。死ぬまで追い出すことのできない老婆が階上に住む2世帯住宅を購入したカップルが、異常にタフで悪知恵の働くその老婆に翻弄される姿を描く。81歳の老女優アイリーン・エッセルの怪演も必見の爆笑作だ。ブルックリンの古いメゾネット住宅を買った夫婦アレックスとナンシー。しかし2階に住むコネリー夫人の騒音や苦情に悩まされ……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CANNONBALL RUN II", "キャノンボール2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "スタントマン出身のニーダム監督が香港資本で作った続編。アメリカ横断レース“キャノンボール”のハチャメチャぶりを綴ったカー・アクション・コメディだ。レイノルズ、ジャッキーらオールスター総出演に加え、豪華な車も続々登場するサービス精神旺盛な娯楽作。", "1983-01-01", "1,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Teaching Mrs. Tingle", "鬼教師ミセス・ティングル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「スクリーム」で注目された脚本家K・ウィリアムソンが監督デビューを果たしたブラックな味わいの青春コメディ。優等生のリー・アンはハーバード大学進学のために奨学金を獲得することに必死。ところが、彼女を目の敵にする鬼教師ティングルがそれを妨害しようとする。さらに、誤解から答案用紙の窃盗犯と疑われたリー・アンは大ピンチに。友人とともに誤解を解こうとティングル先生の自宅を訪れるが、トラブルが重なり、やむを得ず彼女を監禁することになり……。非情な女教師と高校生たちの命懸けの対決を、壮絶な格闘シーンと独特の笑いで描く。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Lost in Translation", "ロスト・イン・トランスレーション", "", "", "http://image.eiga.k-img.com/images/movie/1507/315x210.jpg?3930", 102, "CM撮影のために東京にやってきたハリウッドの中年男優。カメラマンの夫に同行して東京にやってきてホテルの部屋で過ごす若い妻。見知らぬ異国の街で出会った、年齢も性別も違う2人が夜の都市をさまよう。「ヴァージン・スーサイズ」のソフィア・コッポラが監督と脚本を担当して、アカデミー賞オリジナル脚本賞を受賞。撮影は、スパイク・ジョーンズ監督作やビンセント・ギャロ監督の「バッファロー'66」のランス・アコード。", "2004-04-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA SIRE｀NE DU MISSISSIPPI", "暗くなるまでこの恋を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "見合い写真の相手と待ち合わせたルイは、写真とは似ても似つかぬ美人が現れたのに驚き、すぐに結婚を決意する。心を許してしまったルイだったが、ある日、女はルイの預金を引き出して姿を消してしまった……。トリュフォーがヒッチコック風の華麗な映像美を展開するラブロマンス・ミステリー。原作はアイリッシュの小説『暗闇のワルツ』。", "1969-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les mariés de l'an deux", "コニャックの男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　「シラノ・ド・ベルジュラック」のヒットで知られるジャン＝ポール・ラプノーの監督第2作。18世紀末フランス革命の最中、風来坊のニコラが富豪の娘と結婚することに。しかし、前妻との離婚が成立していないことがばれ……。主演のジャン＝ポール・ベルモンドは、ヌーベルバーグの代表作「勝手にしやがれ」で国民的スターの地位を築いた、フランス屈指の人気スター。", "1970-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("雪夫人絵図", "雪夫人絵図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "旧華族の家に生まれた雪が、養子に迎えた夫の暴虐放蕩に身も心も踏みにじられ、ひそかに愛する青年を心に想いながら湖に身を投じて果てる。小原譲治の光と影を抑えた軟調の撮影が艶麗な画面を作る。特に夜の寝室の場面は息をのむ美しさ。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("J・MOVIE・WARS", "J・MOVIE・WARS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 171, "1992年から1993年まで、“WOWOW”で放送された〈J・MOVIE・WARS〉。これは、毎月一人の監督が、10分ほどの短編を４本。計約40分弱の映画を作っていくシリーズで、６人の監督による６作品が生まれた。それを劇場公開したのが、この作品。放送された中から中原俊監督を除いた５編からなり、それぞれ監督の個性が出た、バラエティーに富んだ作品群となっている。「TOKYO BLOOD」は、４つのエピソードで東京に生きる人々を見つめた石井聰亙作品。「月はどっちに出ている」は、のちに同名の映画も作られた崔洋一作品。「来たことのある初めての道」は、男女の出会いから別れまでを描く山川直人作品。劇場公開にあたって、音楽を排除し、物語の流れに沿って編集をし直している。「ワイルドサイド」は、玉置浩二の好演が光る、長崎俊一作品。「殺し屋アミ」は、プロの殺し屋、少女アミの日常を追った榎戸耕史作品。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("チーちゃんごめんね", "チーちゃんごめんね", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "1960～70年代にかけて文化放送の人気DJとして活躍しながら、ガンで1975年に世を去った成田敦子の遺稿集をもとに、“女と仕事”というテーマに取り組んだ作品。タイトルは作者が一人娘にあてた最後のメッセージからつけられている。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("父と子", "父と子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "父の過去に秘密があることを知り、その鬱屈した心から教師を傷つけ、心を閉ざしてしまった息子。その息子の心をほぐそうと、郷里である東北へ二人旅に出ようという父親。やがて父の秘密が明らかになり……。被差別社会に触れた硬質の社会派作品。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BASIL", "バジル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "推理小説の元祖W・コリンズの同名小説を「クローゼット・ランド」のR・バラドワジが映画化。19世紀、ヴィクトリア王朝時代のイギリス。名家の次男に生まれた青年バジルは、生地問屋で働くマニヨンと知り合い、深い友情で結ばれる。やがて、マニヨンの雇主の娘ジュリアと恋に落ちたバジルは、家族に極秘で結婚。ところが、マニヨンとジュリアが愛人関係にあったこと、さらにマニヨンと自分の家族に隠された衝撃的な過去を知るのだった。主人公をほんろうするミステリアスな青年に扮するC・スレーターが、共同製作に名を連ねている。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("讃歌", "讃歌", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "５度映画化された谷崎潤一郎の名作『春琴抄』の４度目の作品。琴三弦の名手、盲目のお琴と使用人の佐助の道なき愛の秘めごとを、奪い奪われる情念の葛藤劇として描いている。異色作ながら、谷崎の豊麗なコクはすっかり削げてしまった。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Name of the Rose", "薔薇の名前", "", "", "http://ecx.images-amazon.com/images/I/51zsp0ib2PL._SL210_.jpg", 132, "イタリアの哲学者ウンベルト・エーコ執筆による同名小説を、ショーン・コネリー主演、ジャン＝ジャック・アノー監督で映画化した歴史ミステリー。1327年、北イタリアのベネディクト修道院を訪れた修道士ウィリアム（コネリー）と見習いのアドソが、修道院で起こった連続殺人の謎に迫る。共演にF・マーリー・エイブラハム、クリスチャン・スレーター、ロン・パールマン、ミシェル・ロンスデール。", "1987-12-31", "2,7,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("84 CHARING CROSS ROAD", "チャーリング・クロス街84番地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ニューヨークに住む売れっ子女流作家ヘレーヌは、珍しい本を収集するのが趣味。ある日新聞広告で、ロンドンにある古書店が絶版書や稀書を扱っていることを知り、注文の手紙を出す。しばらくしてヘレーヌのもとへ、古書店の店主フランクの丁寧な返信とともに探していた本が送られてきた。これがきっかけとなって、二人はお互いに書簡を交わすようになる。ヘレーヌは幾度となくロンドン行きを決意するが、実現しないまま、20年の歳月が過ぎていった。そんなある日、ヘレーヌはフランクの死を知らされる……。イギリスの食糧不足の時代を経て、若くはない二人の男女が往復書簡で温めてきたプラトニックな愛を描いた佳作。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クレーマー case2", "クレーマー case2", "", "", "http://image.eiga.k-img.com/images/movie/53627/315x210.jpg?4432", 70, "過剰なクレーマーによる恐怖を描いたサスペンス。主演は人気グラビアアイドルで女優としても活躍する小野真弓。製菓会社のお客様相談室でクレーム処理を担当する夏美（小野真弓）は、女手ひとつで5歳になる息子を育てている。ある日、「流産したのはお宅のドリンクを飲んだせい」と女性からクレームが入り、夏美には思い当たる節があるものの、会社からの圧力でマニュアル通りの対応をし、電話を切る。その翌日、その女性が本社ビルから飛び降り自殺した……。", "2008-04-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Private Lives of Pippa Lee", "50歳の恋愛白書", "", "", "http://image.eiga.k-img.com/images/movie/54597/315x210.jpg?4231", 98, "戯曲家アーサー・ミラーの娘であるレベッカ・ミラーが、原作・脚本・監督を務める中年女性のラブストーリー。主演はロビン・ライト・ペン、アラン・アーキン、キアヌ・リーブス、モニカ・ベルッチ、ジュリアン・ムーア、ウィノナ・ライダーら豪華共演陣が脇を固める。ベストセラー小説家の夫を持つ50歳のピッパ・リーは、誰もが憧れる理想の妻だったが、現実は反抗的な娘と浮気性の夫を抱える鬱屈とした日々を送っていた。そんなある日、ピッパは15歳年下のクリスと出会い、恋に落ちる。", "2010-02-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Things We Lost in the Fire", "悲しみが乾くまで", "", "", "http://image.eiga.k-img.com/images/movie/53367/315x210.jpg?4102", 119, "「ある愛の風景」「アフター・ウェディング」で注目されたデンマークの女性監督スサンネ・ビアのハリウッドデビュー作。愛妻の夫を突然失ってしまったオードリーは、夫の親友でヘロイン中毒から立ち直ろうと苦しんでいるジェリーに、寂しさを紛らわせるための一時的な共同生活を提案する。そして、やがて2人は互いを支えあう存在になっていくのだが……。主演は共にオスカー俳優のハル・べリーとベニチオ・デル・トロ。", "2008-03-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLER FORCE", "ダイヤモンドの犬たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "マイク・ブラッドリーは、ダイヤモンド国際シンジケートの保安部長ハリー・ウェブの部下であった。しかしダイヤモンドの採掘現場に５人の傭兵が侵入したことを知るや、マイクは彼らに合流する。裏切りに気付いたウェブは執拗な追撃を開始する……。", "1975-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("甘い汗", "甘い汗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "水木洋子のオリジナル脚本を豊田四郎が監督した下町の風俗ドラマ。従来の豊田作品には見られない、バイタリティーあふれる現代の女を描き、新境地を開いた。水商売を転々とする中年のヒロインを京マチ子が好演、岡崎宏三のカメラがその存在感を熱っぽく描写。佐田啓二最後の出演作。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Ward", "ザ・ウォード　監禁病棟", "", "", "http://image.eiga.k-img.com/images/movie/56387/315x210.jpg?3594", 89, "鬼才ジョン・カーペンターが「ゴースト・オブ・マーズ」（2001）以来、約10年ぶりに長編劇場映画のメガホンをとったサイコホラー。20歳の少女クリステンは、身に覚えのない放火の罪で精神病院に収容されてしまう。病棟には精神状態に問題を抱えた同年代の少女ばかりが隔離されており、クリステンは彼女らとは違うと自負していたが、担当医との面接で自分の名前以外の一切の記憶を失っていることに気づく。さらにその夜、廊下を歩く奇妙な女性を目撃したことから事態はさらに混迷していき……。", "2011-09-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHECKERED FLAG", "チェッカード・フラッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "「007／ユア・アイズ・オンリー」(1981)から５作連続で「007」シリーズを手掛けたJ・グレン監督のカー・アクション。スティーヴ・マックィーンの「栄光のル・マン」、トム・クルーズの「デイズ・オブ・サンダー」に見られるような迫力あるカー・アクションと並行して、レーサーたちの素顔を描いていく。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　ROOKIE", "オールド・ルーキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "35歳で大リーグのマウンドに上がった高校教師と、その家族の奇跡のドラマを実話に基づいて映画化。夢を追い続ける主人公の熱意と、彼を支えた人々の献身的な愛情が感動を呼び起こす。肩の故障で大リーグ投手の夢を断念し、高校教師となったジム。35歳になったある日、プロテストを受けることになった彼は、自分でも信じられないほどの速球を投げる。これで再び夢に火が点いたジムだったが、妻や父は彼の転身に反対し……。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOOD DIE YOUNG", "善人は若死する", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "“007”シリーズで名を馳せたL・ギルバートが初期に監督した犯罪映画。若者の犯罪をドキュメンタリー・タッチで表現した“破壊”サスペンスの秀作。偶然知り合った若者４人が突発的に起こした強盗事件。そして彼らは悲劇的結末へと疾走する。", "1954-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Stoned", "ブライアン・ジョーンズ　ストーンズから消えた男", "", "", "http://image.eiga.k-img.com/images/movie/33587/315x210.jpg?2679", 103, "ローリング・ストーンズのオリジナル・メンバーで、27歳という若さでこの世を去ったブライアン・ジョーンズ。ストーンズのリーダーとして60年代ミュージックシーンのトップにまで登りつめながら、アルコールとドラッグに溺れ転落していった彼の人生の明暗と、謎に包まれた死の真相に迫る。「インタビュー・ウィズ・ヴァンパイア」「プルートで朝食を」などニール・ジョーダン監督のプロデュースを手掛けてきたスティーブン・ウーリーが監督に初挑戦。", "2006-08-05", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BROKEN ARROW", "折れた矢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "アメリカ映画で初めてインディアンの立場から白人を描いたといわれている記念すべきウエスタン。インディアンの少年を助けたことから大曾長コチーズと知り合い、アパッチ族の娘と結婚した白人の悲劇を描いていく。“折れた矢”とは、争いを放棄しようと願うインディアンの祈りの象徴である。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エレクトロニックガール", "エレクトロニックガール", "", "", "http://image.eiga.k-img.com/images/movie/54807/315x210.jpg?4362", 80, "温暖化により荒廃してしまった世界でわずかに生き延びた人類は、ドームに住む裕福な特権階級と、外の世界に住む一般市民に分かれ、激減した人口を補完するためのロボット工学の発展で機械化が進んでいた。ある日、人型アンドロイドのナナは開発中の事故により自我を持ち、処分されることを恐れて研究所を脱出する。主演はグラビアアイドルの小泉麻耶。", "2009-09-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLUE HAWAII", "ブルー・ハワイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "E・プレスリーが大暴れするプログラム・ピクチャーの快作。監督は「G.I.ブルース」でプレスリーとコンビを組んだN・タウログ。二年間の兵役を終え帰省したチャドは、家業を継いでほしいという母親の願いも聞かず観光会社に就職した。だが、最初の仕事に失敗した彼は、すぐさま会社をクビになってしまい……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大喘気／OUT OF BREATH", "失われた青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "イエ・ターインの監督デビュー作。金もうけのため解放都市・広東へとやって来た丁建。そこで彼は闇の女王として鳴らす女性ブローカー、李白玲と親しくなり、金もうけの技術を身につけていく。次第に自信を持ち始めた丁建だが……。原作は人気若手小説家、王朔の『橡皮人(ゴム人)』。“ゴム人”とは何にぶつかっても平気な人物のこと。", "1988-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("集団殺人クラブ／GROWING", "集団殺人クラブ／GROWING", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "女子高生が餌食になるスプラッタ・ホラーのシリーズ第３作。グラビア・アイドル、小向美奈子が主演し、名プレーヤー、遠藤憲一が彼女を脅かす殺人鬼タケゾウを熱演する。イマドキの女子高生３人組が、悪夢をきっかけに恐怖のどん底へ。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WRONG TURN", "クライモリ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "「エイリアン」「ターミネーター２」などで４度アカデミー賞を受賞した特殊効果の第一人者S・ウィンストンがプロデュースを務めた衝撃のホラー。地元の人間は絶対に近付こうとしない森の中に足を踏み入れてしまった若者たちを、次々と悲劇が襲う。不気味な森の中で起こる恐怖をダークな映像と、身の毛もよだつスリリングなショック描写で描き出す。木々がうっそうと生い茂る暗い森の中、若者たちの車同士が衝突し故障。携帯電話もつながらず立ち往生した彼らは、助けを求めて不気味な森の中をさまようが、一人また一人と何者かに襲われ惨殺されてしまう……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PROOF　OF　LIFE", "プルーフ・オブ・ライフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "南米の国テカラに赴任し、ダム建設に携わっていた米国人技師ピーターが反政府ゲリラに誘拐される。会社の協力も得られず、途方に暮れる妻アリスを助けてくれたのは、国際的な人質事件を専門に扱うプロの交渉人テリー。だが、高い身代金を要求してくるゲリラたちとの交渉は難航を極め……。敵の腹を探りながら続けられる緊迫感あふれる交渉劇と、次第に心惹かれ合いながらも許されない愛と知って耐えるアリスとテリーの姿を綴ったサスペンス。最後に展開する救出アクションもなかなかの見ごたえ。この映画の共演をきっかけに始まったM・ライアンとR・クロウの恋のスキャンダルも話題になった。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GASLIGHT", "ガス燈", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "19世紀末のロンドン。有名な女性歌手である叔母を殺されたポーラは、叔母の遺産を相続して、夫グレゴリーと叔母の屋敷で新婚生活を始めるが、次第に身辺に奇妙なことが起こる。犯人がポーラを発狂寸前にまで追い詰めていく心理的サスペンスが見もの。", "1944-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE STRANGER'S RETURN", "南風", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "1930年代アメリカ南部の農場地帯で、都会生まれの若い娘が体験する苦難の物語。85歳になる優しい祖父、隣家に住まう教養豊かな好青年に守られながら、財産目当てに彼女を追い払おうとする叔母や土地の因習に悩む女主人公の姿をすがすがしいタッチで描く。“古き良きハリウッド”を代表する良心作。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大兵小将", "ラスト・ソルジャー", "", "", "http://image.eiga.k-img.com/images/movie/55717/315x210.jpg?2775", 95, "ジャッキー・チェンが構想20年をかけ、原案・主演・製作・武術指導を手がけるアクション。共演に「ラスト・コーション」のワン・リーホンら。紀元前227年の中国が舞台。全滅した戦場をずる賢く生き延びた梁の兵士（チェン）は、負傷した敵国・衛の若き将軍（リーホン）を捕虜にする。兵士は祖国に将軍を生きたまま連れ帰ろうとするが、山賊や野生の熊、将軍の暗殺を企む衛の捜索隊などが兵士の行く手をはばむ。", "2010-11-13", "8,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Star Wars: Episode III - Revenge of the Sith", "スター・ウォーズ　エピソード3 シスの復讐", "", "", "http://image.eiga.k-img.com/images/movie/1187/315x210.jpg?4569", 141, "大ヒットSFシリーズ「スター・ウォーズ」新3部作の最終章。ジェダイの騎士アナキン・スカイウォーカーが暗黒卿ダース・ベイダーとなる過程をはじめ、1977年製作の旧シリーズ第1作「エピソード4 新たなる希望」につながる全ての謎が明かされる。クローン戦争の勃発から3年。パルパティーン最高議長がドロイド軍の人質となり、オビ＝ワンとアナキンが救出に向かう。任務を遂行して帰還したアナキンは、妻パドメが妊娠していることを知るが……。", "2005-07-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幕末太陽傳　デジタル修復版", "幕末太陽傳　デジタル修復版", "", "", "http://image.eiga.k-img.com/images/movie/57197/315x210.jpg?3582", 110, "川島雄三監督が1957年に発表した名作喜劇を、2012年の日活創立100周年記念事業の一環として最新のデジタル技術を駆使して映像修復した。幕末の文久2年、品川の遊郭で飲めや歌えやの大騒ぎを繰り広げた佐平次は、一文の金も持ち合わせておらず、遊んだ分を居残りで働くことに。しかし、お調子者の佐平次は自らの困難をものともせず、遊郭に立ち寄った高杉晋作らとも交友し、巻き起こる騒動を次々と片付けていく。物語のベースは古典落語の「居残り佐平次」。今村昌平が助監督を務めている。", "2011-12-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ORDEAL BY INNOCENCE", "ドーバー海峡殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "A・クリスティが自作の中で最も好きだったという『無実はさいなむ』の映画化。大富豪の女主人が殺害され、息子が“母親殺し”の罪で処刑される。彼の無実を証明する素人探偵キャリガリー教授が活躍する本格派ミステリー。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　DANCER", "ダンサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "L・ベッソンがプロデュースを担当した青春ドラマ。口の不自由なインディアは週末ごとにクラブに通い、スゴ腕のDJの挑戦を受けてフロアで踊る人気ダンサー。ブロードウェイを夢見てオーデションを受けた彼女だったが、障害ゆえに落選してしまう。そんな彼女を助けようと、若い科学者が画期的な音声装置の開発に乗り出し……。ヒロイン、M・フライアのダンス・パフォーマンスは圧倒的。", "1999-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rain Man", "レインマン", "", "", "http://ecx.images-amazon.com/images/I/51bhZPBSRIL._SL210_.jpg", 134, "高級外車ディーラーのチャーリーは、絶縁状態にあった父の訃報を聞き、遺産目当てに帰省する。ところが、遺産の300万ドルは全て匿名の人物に相続されることとなっていた。その人物が、今まで存在すら知らなかった自閉症の兄レイモンドであると知った彼は、兄を病院から連れ出してロスへと向かうが……。アカデミー賞主要4部門（作品・監督・脚本・主演男優賞）他、多数の映画賞に輝いた、バリー・レビンソン監督による感動作。", "1989-12-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FOUR SEASONS", "四季", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "タレントにして演出家の才人、アルダの注目作。社会的に安定した職業を持つ３組６人の中年夫婦の仲間たち。ある時、仲間の一組が離婚するのを機に、それぞれの胸中に去来する嫉妬や不安、そして人生の機微が、季節の移り変わりとともにあざやかに表現されている。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GYPSY EMMA", "不倫への招待２ ジプシー・エマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "スペインを舞台に、大金持ちの夫を持つ官能的な若い人妻が、野性的な男にその肉体をもてあそばれる姿をギリギリの官能映像で描いた、韓国エロスの衝撃作。元フランス航空のスチュワーデス、エマ。10歳以上年の離れた夫は、セックスにうずく若いエマの肉体を満足させられなかった。ある夜、街のカーニバルで、エマは名うてのプレイボーイ、パブロと出会う。", "1990-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿賀に生きる", "阿賀に生きる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "豊かな水量を誇り、日本海へと注ぐ新潟県、阿賀野川。有機水銀の垂れ流しによって水俣病をもたらしたことでも知られるこの川の、流域に住む老人たちの姿を捉えたドキュメンタリー。80歳になってなお、田に出ようとする老夫婦や、元舟大工で頑固者の老人、そして、未だ公害病の後遺症と闘い続ける人々。彼らの生き生きとした表情を、温かい視点で追っていく。映画のスタッフたちは４年間、村で共同生活を送り、田んぼ仕事を手伝ったりしながら、老人たちとの生活に溶け込んでいった。その成果は顕著に本作の中に表れ、撮る側と撮られる側の壁を越え、作り手の愛情を自然に映し出す作品に仕上がった。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THOMAS　EST　AMOUREUX", "トマ＠トマ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ちょっと不思議で驚きいっぱいの近未来を斬新なスタイルで描いたヴァーチャル・ロマンティック・ムービー。広場恐怖症のトマ・トマの生活はコンピューター任せで、他人とコミュニケーションを取ることが出来ない。そんな生活を心配した精神分析医はトマ・トマを娼婦派遣会社に無理やり登録させる。彼は、エヴァとメロディという女性に次第に興味を抱くが……。ピンぼけショット、劇中に６分間ものアニメ場面を挿入するなど、視覚を刺激する工夫がいっぱい。", "2000-01-01", "37,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blade Runner", "ブレードランナー", "", "", "http://ecx.images-amazon.com/images/I/514ufpUl8KL._SL210_.jpg", 116, "　2019年、惑星移住が可能になった未来。レプリカントと呼ばれる人造人間が謀反を起こし、地球に侵入。レプリカント専門の捜査官“ブレードランナー”のデッカードは追跡を開始する。一方、彼は製造元のタイレル社でレイチェルというレプリカントに会い、心を通わせていくが……。熱心なファンによって支持され、カルト化したSFハードボイルド・アクション。監督リドリー・スコットの映像センスは絶賛され、その人気を不動のものとした。92年に再編集された「ディレクターズカット／ブレードランナー　最終版」が公開された。", "1982-07-10", "1,6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEASTMASTERII ： THROUGH THE PORTAL OF TIME", "ミラクルマスターII L.A.時空大戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "あらゆる動物たちとコンタクトを取り、見事な剣さばきで太古の魔界に巣くう悪に敢然と立ち向かう正義のヒーロー、ミラクルマスター。前作から９年、新たな悪の出現により、再び動物たちと立ち上がった彼は、悪を追って現代のL.A.にワープする。「コナン・ザ・グレート」のヒロイック・ファンタジー、「クロコダイル・ダンディー」の主人公が受けるカルチャー・ショックなど、エンターテイメントの要素を存分に含んだ作品。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スラバヤ殿下", "スラバヤ殿下", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "名優・森繁久彌の芸達者ぶりが光る和製ナンセンス・コメディの傑作。長曾我部兄弟の弟・永二は、天才物理学者である兄・久太郎に成りすまして外国のスパイから大金をだまし取ろうと画策。しかしそれがバレて窮地に陥った永二は、怪しい扮装をして、日本に漂流した南洋某国の王子“スラバヤ殿下”を名乗り一躍、マスコミの寵児となってしまう。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les Triplettes de Belleville", "ベルヴィル・ランデブー", "", "", "http://image.eiga.k-img.com/images/movie/1287/315x210.jpg?1383", 80, "03年カンヌ国際映画祭特別招待、アカデミー賞で長編アニメーション映画賞、歌曲賞の2部門ノミネート、NYとLAの批評家賞で最優秀アニメ賞を受賞して注目を集めたフランス製アニメ。これが長編デビューの監督シルヴァン・ショメは63年フランス生まれ。コミック作家を経てロンドンに渡ってアニメーターに。ツールド・フランス出場中にギャングにさらわれた内気な孫を救うため、お祖母ちゃんが老犬と老婆3姉妹と協力して大活躍。", "2004-12-18", "2,37,10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MONDO CANE", "世界残酷物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "牛の頭を一刀で切り落とすグルカ族、お祭りで数百頭の豚をたたき殺してはむさぼり食う未開人、放射能で方向感覚を失い灼熱の山で死に絶える海亀……。地球上に残る未開地の驚くべき風習、そして文明社会が生み出した歪みを、地球全土を旅して捉えた生々しい長編記録映画。主題曲『モア』もヒット。", "1962-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA MESSA E｀ FINITA", "ジュリオの当惑〈とまどい〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "新米神父として自信と理想に燃え、故郷ローマに帰ってきた青年ジュリオ。しかし正義感にあふれた彼に次々と降りかかる災難。努力してもすべては悪い方へ向かうばかりで深い挫折感にさいなまれるが……。“イタリアのウディ・アレン”と呼ばれた、モレッティ監督の意欲作。", "1985-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BREAKING UP", "ターニング・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "すれ違いを繰り返し、不安を抱える恋人たちの姿をN.Y.を背景に描くラブ・ストーリー。結婚に解決策を見い出だそうとする男女の複雑な心情が、リアルな会話とともに鮮やかに映し出される。カメラマンのスティーブと教師のモニカは出会いから二年半になる恋人同士。最近はケンカが絶えないが、離れられないでいる。そんなある日、スティーブが結婚を決意。モニカは突然の申し出に戸惑いながらもこれを受け入れ、二人はついに結婚式を迎えるが……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NELLY ET MR.ARNAUD", "とまどい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "「愛を弾く女」でコンビを組んだC・ソーテ監督とE・ベアールによる大人のラブ・ストーリー。親子ほども歳の違う初老の紳士と若い女性の愛を繊細なタッチで綴る。25歳のネリーは失業中の夫に代わって家計を支え、働きづめの毎日を送っていた。ある日、ネリーはカフェで偶然知り合った初老のアルノーから金銭援助を申し込まれたうえに、彼の自叙伝執筆のタイピストとして働くことを提案される。夫に見切りをつけた彼女はアルノーの下で働き始め、仕事を進めるうちに二人は互いに魅せられていく。だが、若くて魅力的なアルノーの担当編集者ヴァンサンが出現したことにより、二人の関係は微妙に変化していく……。コメディ俳優のM・セローが情熱につき動かされる男のとまどいを見事に演じ、セザール賞の主演男優賞を受賞した。", "1995-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新諸国物語 紅孔雀　完結篇 廃墟の秘宝", "新諸国物語 紅孔雀　完結篇 廃墟の秘宝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "当時の子供たちを熱狂させた「新諸国物語　紅孔雀」シリーズの最終編。東千代之介扮する主要登場人物・浮寝丸をめぐる謎が解き明かされるとともに、されこうべ党と白鳥党の最後の闘いが繰り広げられる。DVDは「新諸国物語　紅孔雀(2)」に収録。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STORIA DI PIERA", "ピエラ 愛の遍歴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "女優エスポジティと女流作家ダーチャ・マライーニが共同で書いたエスポジティの自伝的小説の映画化。30年間にわたって娘のピエラの目を通して描かれた母親エウジェニアの生き方。エウジェニアは、普通の母親とはまったく異なった奇妙な女だ。彼女は、母親であり妻でありながら女として生き、夫を愛していながらも自由奔放に生きる女だ。そんな妻を深く愛し続ける夫のロレンツォ。そして、母親の生き方をうらやましく思いながらも反発するピエラ。映画は、浜辺でピエラとエウジェニアが裸で抱き合って終わる。フェレーリの詩情あふれる愛の物語。娘のピエラが、老いぼれた父親に裸を見せるシーンも印象的。", "1983-01-01", "4,2,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The World Is Not Enough", "007 ワールド・イズ・ノット・イナフ", "", "", "http://ecx.images-amazon.com/images/I/51oVw9SFoPL._SL210_.jpg", 128, "", "2000-02-05", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hustle and Flow", "ハッスル＆フロウ", "", "", "http://image.eiga.k-img.com/images/movie/1437/315x210.jpg?2204", 116, "アカデミー賞をはじめ、数々の映画祭で絶賛された音楽映画の傑作。メンフィスの貧しい地域で、ポン引きとしてしがない日々を送っているDジェイ。ビッグなラッパーになる夢をあきらめていたはずだったが、同郷のヒップホップ・スターのスキニ—・ブラックが凱旋公演を行いに帰ってくるという知らせを聞き、旧友や売春婦らと共に再びラッパーへの夢を追いかけることになる……。監督はメンフィス出身のクレイグ・ブリュワー。", "2006-08-12", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Australia", "オーストラリア", "", "", "http://image.eiga.k-img.com/images/movie/53827/315x210.jpg?2946", 165, "「ムーラン・ルージュ」のバズ・ラーマン監督が、第2次世界大戦目前の雄大なオーストラリアを舞台に描くアドベンチャー・ロマンス。主演にニコール・キッドマンとヒュー・ジャックマン。ロンドンからオーストラリアへ渡った英国貴婦人のサラは、自身の運命を変える野生的なカウボーイとアボリジニの少年に出会う。ほどなくして夫を亡くしてしまったサラは、相続した牧場と家畜を守るため、彼らの力を借りて家畜を引き連れオーストリア横断の旅に出る。", "2009-02-28", "1,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Abendland", "眠れぬ夜の仕事図鑑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "", "2012-06-30", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHORT TIME", "天国に行けないパパ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "定年を間近に控えた老刑事が、ある日、医者にあと２週間の命と宣告される。一人息子を深く愛する老刑事は、自分が殉職すれば多額な保険金がおり、息子のこれからの生活費がまかなえると知って、わざわざ犯罪多発地区の危険な仕事に志願するが……。G・チャンピオンによる心温まるコメディ。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SHOWDOWN IN LITTLE TOKYO", "リトルトウキョー殺人課", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "肉体派アクション俳優D・ラングレンと、ブルース・リーの息子ブランドンが共演した作品。ジャパニーズ・マフィアに支配されたL.A.リトルトウキョーは、凶悪な犯罪都市になりつつあった。刑事ケナーは、新しいパートナーのジョニーとともに、ヤクザ顔負けの強引な捜査で、組織壊滅を狙う。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジュノー", "ジュノー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "原爆投下直後の広島で救援活動に尽力したスイス人医師マルセル・ジュノーの人生をアニメ映画化。中学校の修学旅行で広島平和記念公園に立ち寄った美依と優子は、公園の片隅でジュノー博士の顕彰碑を発見する。すると突然、2人の意識は70年前にタイムスリップ。そこで2人は、広島の惨状に驚愕したジュノー博士がGHQを動かして現地に15トンの医療物資を届け、自身も治療活動に奔走する姿を目の当たりにする。", "2010-09-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("華麗なる一族", "華麗なる一族", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 211, "大富豪の銀行家一族を中心に、富と権力をめぐる人間たちの野望や愛憎を描く社会派超大作。山崎豊子の同名小説をもとに山本薩夫監督が重厚な人間ドラマに仕上げた。３時間半の長さを感じさせない、娯楽性十分の作品。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　MANXMAN", "マンクスマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ケインの有名な文芸小説を原作にヒッチコックが撮り上げた、監督作品として最後となるサイレント映画。イメージ映像をフィーチャーしながら、男女の三角関係の行方を終始緊張したテンションを保ちながら描き出す。二人の男と一人の女は幼なじみ。そのうち一人が彼女に求愛したが、貧乏を理由に彼女の父親に拒否されてしまう。彼は出稼ぎに旅立つが、やがて訃報が届く。", "1929-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("神様のくれた赤ん坊", "神様のくれた赤ん坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "昭和32年に作られた「集金旅行」のリメイク作品。同棲中の晋作と小夜子のもとに一人の少年が連れてこられる。少年の母親は晋作の昔の彼女で、男と逃げてしまったらしい。子供の父かもしれない晋作は押しつけられるように引き取る。父親候補は他にも４人いた。そこで二人は、子供の養育費をせしめるため、その４人に会いに行くことにする。リメイクといっても、時代に合ったアレンジが施され、古さは全く感じられない。ファースト・シーンとラスト・シーンが見事に呼応する名セリフ、子供に心を動かされていくカップルの心理描写の妙など、職人芸ともいえる演出が光る。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Great Waldo Pepper", "華麗なるヒコーキ野郎", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "1920年代のアメリカを舞台に、飛行機に魅せられた男たちの生きざまを描く。第1次世界大戦で戦闘機のパイロットとして活躍し、その後も曲芸飛行で田舎を飛びまわっていたウォルド・ペッパー。彼はハリウッドにスタントマンとして招かれるが、そこで出会ったのは、元ドイツ空軍の英雄ケスラーだった・・・・・・。「明日に向かって撃て！」「スティング」に続き、ジョージ・ロイ・ヒル監督がロバート・レッドフォードと組んだ痛快作。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極悪坊主 念仏三段斬り", "極悪坊主 念仏三段斬り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "“極悪坊主”ものの第４作。数年ぶりに故郷の直方に戻った真海は、そこが竜王一家と権田原一家の抗争に巻き込まれていることを知る。幼友だちの松五郎は、権田原一家にワラジを脱ぎ、抗争を止めようとする真海と敵対することになってしまう。二つの一家は邪魔な真海をはさんで手を組み、彼に襲いかかるが……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAKED TANGO", "ネイキッド・タンゴ 剥き出しの愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "アルゼンチンへ向かう船の上で、海に身を投げた女、アルバに代わって、彼女の人生を歩むことにしたステファニー。アルゼンチンで彼女を待っていたのは、妖しげなタンゴ・バーだった……。タンゴが単なる舞踏ではなく“悪魔の踊り”と呼ばれた時代を背景に、L・シュレーダーが描いた愛と官能の世界。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MCLINTOCK！", "マクリントック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "ミスター・アメリカン・ヒーローのJ・ウェインが、コミカルな演技に挑戦した西部劇コメディの快作。TVドラマ『ローハイド』シリーズで名声を得たマクラグレン監督の、娯楽に徹したソフトな演出ぶりも爽快な仕上がり。牧場を経営する主人公の元に、オハラが演じる別れた妻が久しぶりに帰ってくる。目的は二人の娘を引き取ること。彼は元妻を何とか懐柔しようと奮闘する。", "1963-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あなたの命", "あなたの命", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "芸能プロ同士の抗争を、アクションを交えて描いた作品。マネージャーの不始末を肩代わりして、刑務所に服役していた、オリオンプロ社長の火野。彼は、出所後すっかり傾いたプロダクションを再建するため執念を燃やす。しかし、ライバルの新々プロ社長、大崎が妨害して……。大崎の用心棒のくせに、火野と意気投合する今川を、宍戸錠が好演。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("トップをねらえ2！　劇場版", "トップをねらえ2！　劇場版", "", "", "http://ecx.images-amazon.com/images/I/51maj6kLiUL._SL210_.jpg", 95, "庵野秀明が手がけた「トップをねらえ！」（1988）の続編として2004年に発表された全6話のOVAを再編集して劇場公開。前作「トップをねらえ！」の劇場版とあわせて上映された。人類と宇宙怪獣の戦いが続くある日、少女ノノはあこがれの宇宙パイロット、ラルクと出会う。ラルクを「お姉さま」と慕い、宇宙パイロットに志願するノノだったが……。監督は「ふしぎの海のナディア」「新世紀エヴァンゲリオン」などにも参加した鶴巻和哉。", "2006-10-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIRE AND ICE", "ファイヤー&アイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1970年代、アニメ界の異端児としてデビューしたR・バクシが、人類誕生直後の氷河期を背景に描いたファンタジー・アドベンチャーの大作。類人猿群を率いる邪悪な“氷の魔王ネクロン”と、“善の王ジャロル”を統治者とする人類の戦いが、壮大なスケールで描かれる。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK MOON RISING", "ブラック・ライダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "FBIの密命を受け、ラッキー・ダラー社に潜入し、脱税の証拠を盗み出したプロの泥棒屋クイント。彼は追っ手から逃れるため、証拠のテープをハイテク・カー、ブラックムーンに隠す。だがブラックムーンが何者かによって盗まれてしまった。クイントはテープを取り戻せるのか。FBIが与えた猶予は72時間だけだ……。J・カーペンター原案によるハイテク・バイオレンス・アクション。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("伊豆の踊子", "伊豆の踊子", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "川端康成の名作の３度目の映画化。女流文学の第一人者・田中澄江が脚色し、「手さぐりの青春」の川頭義郎が演出にあたった。３人目の踊り子はドイツ人とのハーフ・鰐淵晴子。エキゾチックな魅力で売り出した彼女に伊豆の踊り子は不似合いの感じがするが、まげ姿は美しい。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HAMMERHEAD", "ハマーヘッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "核防衛の秘密をめぐる、国際的な犯罪者“ハマーヘッド”とプロ・スパイ、フッドの闘い。そこに謎の美女が絡み、ハードボイルドな豪華スパイ・アクションが展開される。主演のフッド役には、往年の大ヒットTV『ベン・ケーシー』のV・エドワーズ。舞台となったポルトガルの風景も美しい。", "1968-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN WHO FELL TO EARTH", "地球に落ちて来た男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "メキシコの湖に一人の宇宙人が不時着する。世界的な特許をもとに大会社を設立した彼は、滅びつつある故郷の星にいる家族を救うため帰郷しようとするが、アメリカ政府は彼を実験用に監禁した……。特撮を思い切って省いたN・ローグの演出が冴える異色SF。", "1976-01-01", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二人日和", "二人日和", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "四季折々で様々な表情を見せる京都を舞台に、夫婦の絆を描く大人のラブ・ストーリー。45年間連れ添った夫と妻の慎ましい愛の日々が綴られる。共に俳優として40年以上のキャリアを持つベテラン、栗塚旭と藤村志保が円熟の演技を披露。その名演と人の生命を真摯に見つめた物語が印象深い。妻の千恵が体の自由が利かなくなる難病ALSであると知った黒由。看病に専念することを決めた彼はある日、妻の古い日記を見つける。", "2005-11-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GALLIVANT", "GALLIVANT〈ガリバント〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "イギリスの海岸沿いを行く旅を捉えたドキュメンタリー。A・コッティング監督が85歳の祖母と、染色体異常による障害を持つ７歳の愛娘を連れて、グレートブリテン島の沿岸を渡り歩く。そのさまを13週間に渡って撮影。行く先々で出会う、ごく普通ながら風変わりな人々との交流が描かれる。ユーモアと詩情にあふれた作りが魅力。", "1996-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウルトラマンG 怪獣撃滅作戦", "ウルトラマンG 怪獣撃滅作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ウルトラマン生誕25周年を記念して作られたオリジナル実写版の劇場用。キャストは外国人、製作はオーストラリアで行われ、これまでと違った“ウルトラマン・ワールド”が展開する。国際軍事組織UMAの隊員シンドー（実はウルトラマン）と怪獣が戦うというもの。", "1990-12-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("涙", "涙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "厳しい現実のなかで引き裂かれていく初恋と新しい生活への意志を描いて、喜びと哀しみが胸をいき交う青春編。日本楽器の女工、志津子と同僚の事務員、雄二は互いに愛し合い結婚を願う。しかし、志津子の家族の過去の汚点を雄二の肉親が見とがめて、二人の間は遠のくばかり。そんな時、志津子に見合い話が……。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MY　BOSS，MY　HERO／頭師父一体", "マイ・ボス マイ・ヒーロー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "高校を中退したヤクザのボスが再び高校生になりすまし、私腹をこやす校長はじめ学校の無法ぶりに敢然と戦いを挑んでいく。破天荒にしてユニークきわまりない学園アクション映画。韓国では370万人の観客が劇場に足を運んだメガ・ヒット作品だ。今作で一気にスターダムに駆け上がったチョン・ジュノの出世作で、学歴差別や腐敗しきった権力の問題など、韓国社会の今を鋭く風刺する。", "2001-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("僕たちのアフタースクール", "僕たちのアフタースクール", "", "", "http://image.eiga.k-img.com/images/movie/57047/315x210.jpg?3584", 94, "人気男性K-POPグループ「超新星」が主演する学園青春劇。交換留学で韓国から日本の高校にやってきたユナク、ソンジュ、ソンモ、ジヒョク、グァンス、ゴニルの6人は、経営するクラブが閑古鳥で悩む幼なじみのジュノを助けるため、クラブの仕事を手伝うことに。しかし働きはじめた次の日に借金取りが押し寄せ、ジュノは失踪。6人はクラブで偶然鉢合わせた学級委員の真希と力をあわせ、店の再建に乗り出す。共演に「嘘つきみーくんと壊れたまーちゃん」「パラダイス・キス」の大政絢。", "2011-11-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ANGE", "天使", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "７つのシークエンスに分かれ、悪夢のように繰り返す幻想的映像と弦楽中心の魅惑的な音楽が、観る者を迷宮へと誘う。1982年のカンヌ映画祭批評家週間で上映された、難解だが不思議な魅力を持つ実験映画。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モナリザお京", "モナリザお京", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "セクシー女優、渥美マリが美しい女スリにふんした犯罪活劇。リスボンで盗まれた時価数億円のブローチ“海の星”が日本にあることを知り、スゴ腕の女スリ、お京はそれを狙って暗躍。彼女の逮捕に執念を燃やす老刑事、ライバルの女スリの罠をかいくぐりながら、彼女は、謎めいた探偵とともに“海の星”の行方を追い……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ONE SHOT ONE KILL 兵士になるということ", "ONE SHOT ONE KILL 兵士になるということ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「アメリカ　戦争する国の人びと」で戦争を体験したアメリカの若者たちの“その後”を描いた藤本幸久監督が、アメリカ海兵隊における新兵訓練の実態に迫ったドキュメンタリー。毎週500人から700人の若者たちを受け入れるサウスカロライナ州パリスアイランドの海兵隊ブートキャンプ（新兵訓練所）で取材を敢行し、入隊したばかりの普通の若者たちが、12週間の過酷な訓練を経て、戦場で人を殺すことのできる兵士に育てあげられていく過程を追う。", "2010-06-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GARCON！", "ギャルソン！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "パリのとあるレストラン。ギャルソンたちの日常風景を描きながら、人生の悲喜こもごもを綴った小粋な映画。チーフ・ウェイターのアレックスは、事業に失敗し、妻と別れて20年、引退後は海辺に遊園地を作る夢をかなえるため、せっせと働いているのだ。食事時の店は猛烈に忙しく、愛想よく客をさばき、同僚とシェフのけんかをなだめ、閉店後は仲間と一杯。ガールフレンドにも不自由しない。ふと本気になった若い恋人は、結局去って行ったが、気楽なやもめ暮らしは時に寂しくとも快適だ。酸いも甘いもかみ分けた陽気な初老の男をY・モンタンが絶妙に演じている。フランスの香りが漂う一編。", "1983-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京大学物語", "東京大学物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "人気漫画家・江川達也の代表作として知られる同名コミックを、江川自身が初メガホンを取って実写映画化した青春ラブコメディ。函館向陽高校に通う水野遥は、学校一の秀才・村上直樹から愛の告白を受ける。直樹との交際をスタートさせた遥は、直樹と一緒に東京大学に入るべく猛勉強を開始。直樹の指導によって順調に成績を伸ばしていくが……。グラビアアイドルの三津谷葉子と「シムソンズ」の田中圭が主人公カップルを好演。", "2006-02-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VIOLETS ARE BLUE", "すみれは、ブルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "やり手の女性フォト・ジャーナリストと、小さな新聞社の経営者。かつて愛し合っていた二人が、15年ぶりに再会し、青春の恋の思い出をたどり始める……。オスカー女優スペイセクと、演技派K・クラインが共演した大人のラブ・ストーリー。監督はスペイセクの実生活の夫、J・フィスク。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SLOGAN", "スローガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "S・ゲンズブールとJ・バーキンが愛し合うきっかけとなったことで知られる作品。ベニスのフェスティバルでグランプリを獲ったCM監督が、そこで出会ったエヴリーヌという女性に恋をする。パリに戻った彼は、妻に浮気ではなく真剣な恋をしていることを気付かれてしまう。しかし、エヴリーヌは彼を捨て、イタリア人のプレイボーイの元へと去ってしまう……。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA LECON PARTICULIE｀RE", "個人教授", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "18歳の高校生オリヴィエはスキー場で年上の美しい女性と知りあい夢中になるが、彼女はレーサーのフォンタナを愛していた……。年上の女性との恋をみずみずしいタッチで描くベルレーのデビュー作。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GOOD EARTH", "大地", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "アメリカの代表的女流作家P・バックの原作の映画化で、中国ロケを敢行した大作。1930年代のアメリカ映画の力と水準の高さをまざまざと感じさせる作品である。貧農の百姓ワンは奴隷奉公の娘オランを嫁にして苦しいながらも畑を耕していく。二人は苦労して働き、その甲斐あって田畑は増え、子供も生まれた。だが大地は干ばつ、飢餓、イナゴの大群といった災害を生み、一家は苦難と闘う。やがて月日が流れ、成長した息子の婚礼の日、長年夫に付き添い苦楽をともにした妻、オランが死ぬ……。S・フランクリンは、欧米人が東洋人を演じるという困難な問題も、巧みなメイクアップと、俳優たちの優れた演技によって解決。正攻法の演出で、原作のダイジェスト版に終わらせることなく、見事に感動的で壮大なドラマに仕上げた。オランを演じたL・ライナーがアカデミー賞主演女優賞を受賞。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青空どろぼう", "青空どろぼう", "", "", "http://image.eiga.k-img.com/images/movie/56327/315x210.jpg?2691", 94, "「平成ジレンマ」「光と影」の東海テレビ制作による、日本4大公害の1つ「四日市ぜん息」の現在を追ったドキュメンタリー。1973年、四日市の裁判をきっかけに公害病認定制度が法律に明記され、2010年11月現在、四日市市には公害認定患者が459人いる。しかし88年に制度が廃止されたことで新たな公害認定は行われず、患者数は年々減少しているが、制度廃止後に気管支ぜん息と診断された人もいた。裁判の原告の1人で今も四日市市に暮らす78歳の野田之一さんと、公害発生当初から患者たちを追ってきた公害記録人・澤井余志郎さんは、「公害はまだ終わっていない」と話し、町の青空が誰によって奪われたのかを問い続ける。", "2011-06-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東のエデン　劇場版II Paradise Lost", "東のエデン　劇場版II Paradise Lost", "", "", "http://image.eiga.k-img.com/images/movie/54697/315x210.jpg?4357", 92, "2009年4月～6月に放送された人気TVアニメの完結編で、「劇場版I」から直接続く2部作の第2部。日本を救うという義務を課せられた12人の救世主（セレソン）の1人として、滝沢朗は「この国の王様になる」ことを決意。ニューヨークから日本へ戻ってくると、それを機に残りのセレソンたちも、それぞれの思惑で最後の活動に出る。そして滝沢と行動を共にする森美咲は、滝沢の過去にまつわる秘密を知り……。", "2010-03-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("竹久夢二物語 恋する", "竹久夢二物語 恋する", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "大正ロマンチシズムの代表画家・竹久夢二の、恋と芸術に生きた半生を描く。竹久夢二はすでに20代前半にして、多くのファンを持つ流行画家で、モデルのたまきと同棲していた。大逆事件に巻き込まれた彼は、釈放後、美大生・彦乃に惹かれていく。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("河童のクゥと夏休み", "河童のクゥと夏休み", "", "", "http://image.eiga.k-img.com/images/movie/34087/315x210.jpg?2706", 138, "劇場版「クレヨンしんちゃん」シリーズの「嵐を呼ぶモーレツ！オトナ帝国の逆襲」「嵐を呼ぶアッパレ！戦国大合戦」で子供だけでなく大人たちも感動させ、高い評価を受けた原恵一監督によるアニメ。木暮正夫の児童文学を原作に、不思議な力を秘めた河童のクゥと少年の友情を描く。小学生の康一は、学校からの帰り道に大きな石を拾う。持ち帰って洗ってみると、それはなんと河童の子供だった。康一は河童にクゥと名付け、一緒に暮らし始めるが……。", "2007-07-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nights in Rodanthe", "最後の初恋", "", "", "http://image.eiga.k-img.com/images/movie/53527/315x210.jpg?2521", 97, "「きみに読む物語」の原作者として知られる人気作家ニコラス・スパークスの小説を、「運命の女」のリチャード・ギア＆ダイアン・レイン再共演で映画化した大人のラブストーリー。夫や娘との関係に疲れ果ててしまったエイドリアンは、友人が営むホテルの留守を預かるため、ノースカロライナの海辺の町ローダンテを訪れる。時期はずれのホテルの客は、高名な外科医ポールただ1人。それぞれ悩みを抱える2人は徐々に惹かれあっていき……。", "2008-09-27", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Under Siege", "沈黙の戦艦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "　核兵器を搭載した巨大戦艦ミズーリが軍人たちによってジャックされる。この危機に、艦内勤務のコックが立ち上がった。名前はライバック。その正体は優れた戦闘能力を秘めた精鋭部隊の兵士だった。彼は、慰問で乗り込んでいた娘ジョーダンを守りながら、戦艦を奪還すべく孤軍奮闘することに。海洋版「ダイ・ハード」と呼ばれ、大ヒットしたサスペンス・アクション。ライバックはセガールの当たり役となり、続編も製作された。", "1992-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("座頭市血煙り街道", "座頭市血煙り街道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "座頭市は旅篭で病死した母親から、夫・庄吉のもとに息子を連れていくことを頼まれた。悪代官に監禁されていた庄吉を助けた市は、公儀隠密の赤塚多十郎と対決する……。剣豪スターの近衛十四郎が、堂々たる風格をみせる。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("お日柄もよくご愁傷さま", "お日柄もよくご愁傷さま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "父の急死、長女の出産など、一家を嵐のように襲う冠婚葬祭を通して家族の意味を問いかけるハートフル・コメディ。生まれて初めての仲人役を明日に控えた和夫をはじめ、臨月の長女・玲子は旦那の浮気で里帰りしたり、次女の瞳は恋人との旅行の準備と、田中家は慌ただしさのさなかだった。そして翌朝、出がけの和夫と妻・佳菜子の元に、父・源三郎急死の知らせが入る。玲子と瞳にあとをまかせ結婚式に向かったが、気もそぞろで式はハプニングの連続。あたふた帰宅すれば、玲子と夫・貴之が夫婦ゲンカの真っさなか。しかし何とか仮通夜、通夜と進むが、今度は本葬で娘が産気づいてしまう。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLIGHT OF THE NAVIGATOR", "ナビゲイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "誤って谷底へ転落した少年。彼が目を覚ますと８年の歳月が経過していた。その頃NASAではUFOを捕獲、調査するうちに少年との関係が判明する。CGを駆使して描いたUFOの華麗な飛行シーンが印象的。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("陸軍中野学校・開戦前夜", "陸軍中野学校・開戦前夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "シリーズ最終作は、第二次世界大戦の舞台に突入。戦争決定がなされた御前会議の内容が敵国のスパイ連合・P機関にもれる。中野学校はこのP機関を突きとめるために、御前会議出席メンバーを調査する。一匹狼だった椎名がリーダー的役割で集団行動をとるようになる。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SR サイタマノラッパー　ロードサイドの逃亡者", "SR サイタマノラッパー　ロードサイドの逃亡者", "", "", "http://image.eiga.k-img.com/images/movie/57627/315x210.jpg?4366", 110, "レコード屋もライブハウスもない田舎町を舞台に、ラッパーを目指す若者たちの奮闘を描いてロングランヒットを記録した青春映画「SR サイタマノラッパー」のシリーズ第3作。仲間と別れ東京に出たマイティは、「極悪鳥」というヒップホップグループに入れてもらおうと努めるが、苦渋を味わった挙句に怒りを爆発させてメンバーの1人に大怪我を負わせてしまう。栃木へ逃亡したマイティは、盗難車の転売など違法行為で商売するグループの一員として働き始め、そのグループが開いた詐欺まがいの音楽イベントでかつての仲間のイックとトムと再会するが……。", "2012-04-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO CONTESTII", "フル・ブラスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "コンピュータ制御された美術館を舞台に、テロ一味とアクション女優の攻防を描いたサスペンス・アクション。美術商を装って美術館を占拠したテロリスト、デインは彫像に隠された中性子爆弾を館内にセット。偶然、美術館を訪れ、一味の目を逃れた女優シャロンはテロリストと戦いながら、密室化した館からの脱出を図るが……。悪役ヘンリクセンの不敵な怪演も見どころ。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Insidious", "インシディアス", "", "", "http://image.eiga.k-img.com/images/movie/56217/315x210.jpg?2118", 103, "「ソウ」シリーズのジェームズ・ワンが監督、リー・ワネルが脚本を手がけ、「パラノーマル・アクティビティ」のオーレン・ペリが製作を担当したホラー。ルネとジョシュの夫婦は、とある古い一軒家に引越す。しかし不審な出来事が相次ぎ、息子のダルトンがハシゴから転落して昏睡状態に陥ってしまう。不吉に思ったルネとジョシュはすぐに新しい家に引越すが、一家にはすでに見えない何かがとりついていた……。主演は「28週後...」のローズ・バーンと「リトル・チルドレン」のパトリック・ウィルソン。", "2011-08-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUNDOWN", "サンダウン　ボクたち、二度と血は吸いません", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "故郷を離れ、アメリカ西部の田舎町に移民した吸血鬼たち。村を統治する吸血鬼の伯爵は、ペレストロイカ路線で人間との共存を提案、さっそく人工血液の製造にのり出すが、天然の血を吸いたい反体制の吸血鬼たちが決起して……。キバではなく、マシンガンを武器に吸血鬼が暴れ回る、パロディー満載の異色作。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("スイートハート・チョコレート", "スイートハート・チョコレート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-04-30", "76,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新網走番外地・嵐を呼ぶ知床岬", "新網走番外地・嵐を呼ぶ知床岬", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "サラブレッドを生産する牧場が舞台に。ダービー馬の生産を目指す加納牧場で働く末広勝治は、ダービーの有力馬を狙う熊谷牧場の狡猾なやり口に激怒し、殴り込む。西部劇タッチを狙った作品だが、マンネリの感はぬぐえなかった。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("難波金融伝 ミナミの帝王　劇場版PART V 甘い罠", "難波金融伝 ミナミの帝王　劇場版PART V 甘い罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "借り逃げは決して許さない高利貸し・萬田銀次郎の姿を描いたシリーズ第５作。銀次郎から金を借りたゲイバーのママ・花江は競買屋に騙され財産を失う。そのショックから自殺未遂までも起こす。花江を騙した相手は、銀次郎から多額の借金を重ね、返済を焦げ付かせている志垣だった。彼を追っていくと、その陰には悪質な不動産と競買屋の姿が浮かび上がり……。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Exorcist: The Beginning", "エクソシスト・ビギニング", "", "", "http://image.eiga.k-img.com/images/movie/1387/315x210.jpg?2291", 114, "「エクソシスト」シリーズの第4作は、73年製作のシリーズ第1作「エクソシスト」の前を描く物語。この作品に登場したメリン神父を主人公に、彼の若き日の悪魔との初めての遭遇を描く。第2次世界大戦末期、ナチスの残虐行為を目撃したメリン神父は信仰を見失って放浪の旅に出て、アフリカの教会遺跡発掘現場で不可思議な現象に見舞われる。撮影は名手ビットリオ・ストラーロ、音楽は元イエスのトレバー・ラビン。", "2004-10-16", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("男はつらいよ・寅次郎相合傘", "男はつらいよ・寅次郎相合傘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "第11作「寅次郎忘れな草」に続いて浅丘ルリ子が相手役。東北の旅先でヘンな蒸発男と道連れになった寅さんは、かつて思いを寄せたキャバレー歌手のリリーとばったり再会。彼女は妻の座に納まらずにまたドサ回りをしていたのだった。何度かケンカと仲直りをくりかえす二人は結局東京へ戻り、“とらや”の面々は二人の結婚を画策するが、やはり堅気にはなれない運命だった。浅丘はキネマ旬報主演女優賞に輝いた。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JUST A LITTLE INCOVENIENCE", "勇気ある友情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "子供の頃、スキー友だちだったフランクとケニー。ベトナム戦争で片腕片足を失い、心をすさませたケニーに、フランクは再びスキーを教えて生きる意味を見いださせる……。身障者の自立と恋を描いた感動のドラマ。L・メジャースが主演したTV映画だが、日本では劇場公開された。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SKYSCRAPER", "デンジャーゾーン　摩天楼からの脱出", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "ハイテク高層ビルを占拠したテロリストに、一人の女性が立ち向かうサスペンス・アクション。ビルの屋上から屋上へ人の送迎をするヘリサービスのパイロット・チャーリー。ある日彼女は、不審な二人組の男を街中の高層ハイテクビルへと輸送するが、実は彼らは軍のトップシークレットを強奪するためにビルへと向かったテロリストだったのだ。そのことを知ったチャリーは、彼らに単身反撃を開始する。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Erin Brockovich", "エリン・ブロコビッチ", "", "", "http://image.eiga.k-img.com/images/movie/1837/315x210.jpg?3756", 131, "第73回アカデミー賞でジュリア・ロバーツが主演女優賞を受賞。作品賞、監督賞ほか5部門にノミネートされたスティーブン・ソダーバーグの監督作。カリフォルニアの小さな町に住むエリンは、幼い子供を3人も抱えているのに仕事もお金もなく、しまいには車の衝突事故に遭い怪我までする始末。雇った弁護士エドは和解金を取れず、借金はかさむ一方だ。窮地に追い込まれたエリンは、エドの弁護士事務所に押しかけて居座り、エドはしぶしぶ彼女を雇い入れる。不動産関係のファイル整理を任された彼女は、ある書類に不審を抱いて調査を始め、大企業が引き起こした周辺地域の水質汚染に気付く。", "2000-05-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Friends with Benefits", "ステイ・フレンズ", "", "", "http://image.eiga.k-img.com/images/movie/56067/315x210.jpg?4607", 109, "「ソーシャル・ネットワーク」のジャスティン・ティンバーレイクと「ブラック・スワン」のミラ・クニス主演で男女の友情と恋愛の本音をつづるラブストーリー。ニューヨークでヘッドハンターをしているジェイミーは、ロサンゼルスのアートディレクター、ディランを転職させるためニューヨークに招く。多くの恋愛に失敗してきたことで恋愛そのものを楽しめなくなっていた2人は、互いに価値観が似ていることから意気投合。そのまま恋愛感情なしのセックス・フレンドになるが、次第に2人の関係に微妙な変化が表れ……。", "2011-10-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IP5", "IP5 愛を探す旅人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "「ベティ・ブルー」のジャン＝ジャック・ベネックス監督が描く、3人の男のロードムービー。一目ぼれした女を追って旅に出た青年トニーと彼を慕う少年ジョッキーは、不思議な力を持つ老人レオンと出会う。彼もまた、昔の恋人を探していた。レオンを演じたのは、俳優、シャンソン歌手として知られるイブ・モンタン。本作が彼の遺作となった。音楽は、「ベティ・ブルー」「愛人／ラマン」のガブリエル・ヤーレ。", "", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Machine Gun Preacher", "マシンガン・プリーチャー", "", "", "http://image.eiga.k-img.com/images/movie/57467/315x210.jpg?2642", 129, "「チョコレート」「君のためなら千回でも」のマーク・フォースター監督が、アフリカの紛争地で少年兵を救う活動に従事する実在のアメリカ人活動家サム・チルダースの姿を描いたドラマ。麻薬とアルコールに溺れる生活を送っていたサムは、ある事件をきっかけに改心し、アフリカ各地で内戦に巻き込まれている子どもたちのために教会と孤児院を建設することを決意。LRA（神の抵抗軍）の執拗な攻撃を受け、やむなく銃をとったサムは、その姿から「マシンガン・プリーチャー（銃をもった天使）」と呼ばれるようになる。主演はジェラルド・バトラー。共演にミシェル・モナハン、マイケル・シャノン。", "2012-02-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SURVIVAL", "ザ・サバイバル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "「ホワイトファング」などの原作者J・ロンドンの名作を映画化。カリフォルニアの邸宅で飼われていた犬のバックは泥棒に連れ去られ、北極でソリ犬にされてしまう。訓練の中で、主人との主従関係を学び、信頼できる人間を見分けることができるようになったバックは、仲間の犬たちの中でリーダー格になっていく。やがて極寒の地での過酷な旅に疲れ果てたバックは、一匹狼の男ジョンに救われる。やっと信頼できる主人にめぐり合い、彼と深い絆で結ばれていくバックだが、次第に野生の本能に目覚めていき……。容赦なく襲いかかる自然の猛威を、犬の視点から描くサバイバル・アドベンチャー。ナレーションをリチャード・ドレイファスが担当。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA DE´ROBADE", "夜よ、さようなら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "平凡な娘が惚れた男のために娼婦となり、足を洗うまでをドキュメンタリー・タッチで描く。コルドリエの自伝小説を、TVで記録映画を撮っていたデュヴァルが、冷めた目で映像化。淡々と主人公を演じたミュウ・ミュウはセザール賞を拒否。素肌に着た皮ジャンがセクシー。", "1979-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES ROSEAUX SAUVAGES", "野性の葦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "A・テシネ監督の青春時代の経験をもとに、政治と性に目覚める思春期の若者たちの姿を描いた青春群像劇。1962年初夏、アルジェリア独立戦争末期のフランス南西部の田舎町が舞台。高校卒業を間近に控え寄宿舎生活を送るフランソワは、近くの町まで列車で映画を見にいく熱烈な映画ファン。マイテという恋人がいるが、ある夜、イタリア系アルジェリア人のセルジュと関係を持ってしまう。一方セルジュは、マイテに思いを寄せていた。そんな中、フランソワとセルジュのクラスに、アルジェリアから右翼植民主義者OASを信奉しているアンリが転校してきて……。同性愛を交えながらも、澄みきった後味を残す語り口はテシネならでは。1995年度セザール賞で作品賞、監督賞、脚本賞、新人女優賞の主要４部門を獲得し、“テシネの最高傑作”と評された。", "1994-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("南京の基督", "南京の基督", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "文豪・芥川龍之介の同名小説を映画化した香港・日本合作作品。1920年代の南京を舞台に、妻子ある日本人小説家とキリストの存在を信じる中国人娼婦の悲恋を描く。小説家の岡川は、年老いた父の借金返済のために遊廓に身を任せた金花と出会う。二人は一緒に暮らし始めるが、岡川に妻子がいることを知った金花は姿を消し、岡川も日本へ帰国する。しかし行くあてのない金花は遊廓へと戻り、不治の病に冒されてしまう……。キリストを信じ続けるヒロインを富田靖子が熱演し、1995年東京国際映画祭の最優秀女優賞を受賞した。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILVER BLAZE", "銀星号事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "1931年の「フェータル・アワー」以来、本作が第５作目になるA・ウォントナー主演による“シャーロック・ホームズ”シリーズ。コナン・ドイルの傑作短編から競走馬消失事件の謎を映像化。名馬・銀星号が消え去り、馬丁が殺された。影に潜むのはモリアーティ教授なのか……。", "1937-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女心と秋の空　ふしだらな子猫", "女心と秋の空　ふしだらな子猫", "", "", "http://image.eiga.k-img.com/images/movie/55677/315x210.jpg?4473", 70, "6人の監督が新しい性愛の可能性を探る特集「Love＆Eros CINEMA COLLECTION」の1作。瀬々敬久監督やいまおかしんじ監督に従事した伊藤一平監督が本作で劇場長編デビューを飾り、お笑い芸人のヒロシが主演を務める。ある雨の日、リストラされた代作は、男に捨てられ怪我をした女性・春菜を自宅で介抱してやる。同じ日に拾った捨て猫と春菜を重ね合わせた代作は、春菜との再会を夢見るようになる。", "2010-10-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　GAME　OF　THEIR　LIVES...", "奇蹟のイレブン／1966年W杯北朝鮮 vs イタリア戦の真実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "サッカー・ワールドカップ史上最大のサプライズとも語り継がれる、'66年イギリス大会での北朝鮮代表の快進撃をめぐるドキュメンタリー。強豪イタリアを破り、アジア初のベスト8に進出した当時の選手を取材。北朝鮮での撮影を実現するなどサッカー・ファンならずとも興味深い1本だ。'66年のワールドカップで強豪イタリアを破った、北朝鮮代表選手をダニエル・ゴードンが取材。その知られざる事実に迫っていく。", "2005-05-28", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("McHALE’S　NAVY", "ホット・ネイビー／カリブ海イケイケ大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "1960年代アメリカのTVシリーズ『マクヘイル・ネイビー』を曲者ぞろいのキャストで映画化したアクション・コメディ。海軍の兵士に密輸品を売りさばくお気楽な元将校マクヘイルの前に、かつてのライバルであるテロリスト、ブラディコフ一味が出現。両軍のズッコケ・バトルの幕が切って落とされる。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STOP！ OR MY MOM WILL SHOOT", "刑事ジョー ママにお手あげ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "ロス市警の刑事ジョーのもとにニューヨークからママがやってきた。いまだにジョーを子供扱いのママは頼みもしないのに世話ばかりやきたがり、あげくにはジョーの捜査に顔を出して激しい銃撃戦まで……。スタローンのコメディ演技もおかしいが、なんといっても見どころはE・ゲティの悪のりママぶり。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("斬殺せよ 切なきもの、それは愛", "斬殺せよ 切なきもの、それは愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "二・二六事件を背景に据え、正義感に燃える若き将校と貧しさのため娼婦になった幼なじみの女とのはかない恋を描いた作品。監督の須藤久にとっては、これが17年間温めた企画。若い二人の愛を見守る侠客に若山富三郎、土地の組長役にビートたけしらを配し、「人生劇場」ばりのドラマが展開する。1993年に新聞社で自決を遂げた野村秋介が企画・監修を担当した。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Vengeance　復仇", "冷たい雨に撃て、約束の銃弾を", "", "", "http://image.eiga.k-img.com/images/movie/55137/315x210.jpg?1249", 108, "「ザ・ミッション／非情の掟」「エグザイル／絆」のジョニー・トー監督が、フランスの国民的スター、ジョニー・アリディを主演に描くフィルム・ノワール。マカオに暮らす愛娘の家族を何者かに殺された初老の男・コステロ（アリディ）は、重体の娘から犯人の手がかりを聞き出し、復讐を誓う。コステロは偶然出会った殺し屋3人組に、犯人を探し出し、暗殺するよう依頼するが……。共演に、トー作品の常連アンソニー・ウォン、サイモン・ヤム、ラム・シュ。", "2010-05-15", "6,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOB LE FLAMBEUR", "賭博師ボブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ヌーヴェル・ヴァーグの先駆者といわれるメルヴィル初期の作品で、モンマルトル界隈に棲息する暗黒街の人々の人間模様をメルヴィル特有のダンディズムを漂わせ、シニカルに描いた秀編。夜のとばりがおりるころ、老賭博師ボブは、モンマルトルの賭博場に顔を出すのが日課だった。ある時カジノの金庫が夜間になると手薄になることを知ったボブは彼を慕う若者パウロとその恋人、それに昔の仲間を巻き込んで、その金を強奪する計画を立てる。ところが決行の当夜、ルーレットのツキがまわり勝負にのめり込んでしまう。一方仲間は警官と撃ち合いとなり、パウロは息をひきとる。警察に連行されるボブの手の中には、今までにないほどの大金が残されはしたが……。いっさいの努力が無になるという結末に、「男の争い」や「筋金〈ヤキ〉を入れろ」で犯罪ものを大ヒットさせたブルトンの脚色がさえる。美術にメルヴィル自らがあたり、黒白の画面が実に美しい仕上がりを見せているのも、特筆すべきだ。", "1955-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BRIGADE ANTI−GANGS", "凶悪犯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "パリ警視庁の特捜班10人と、凶悪なギャングとの闘いを描く、フィルム・ノワール。ブルトン原作の刑事アクション・ドラマを、一流のスタッフ・キャストで作り上げたムードたっぷりの一編。", "1966-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BASTIEN，BASTIENNE", "バスティアンとバスティエンヌ ～湖畔にて～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "モーツァルト作曲のオペラをモチーフとして、戦時下のフランスの片田舎での出来事を少年の目を通してみずみずしく描く作品。女ばかりの屋敷に住む３人の少年は、毎日響いてくる砲声や、親たちのいさかいに心を痛めながらも、『バスティアンとバスティエンヌ』のオペラを上演しようと努力する。", "1979-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HANGING　UP", "電話で抱きしめて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "「めぐり逢えたら」「ユー・ガット・メール」でM・ライアンと組んだ監督N・エフロンが、妹デリアの小説を彼女とともに脚色、D・キートンが監督を務めた家族劇。夫と息子の世話をしながら仕事もこなすイヴ。彼女には女性誌の編集長をしている姉ジョージアと、昼メロの女優をしている妹マディがいたが、離れて暮らしているために老人性痴呆症の父ルウの面倒はイヴに任せっぱなし。仕事のトラブルや毎日病院から電話してくる父のことで彼女はパンク寸前だった……。壊れそうで壊れない３姉妹の絆、そして父への愛情を温かい視点で綴っていく。父を演じた名優W・マッソーの遺作ともなった。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("忠裂圖／THE　VALIANT　ONES", "忠裂圖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "アジアの巨匠キン・フーが絶頂期の頃に手掛けた大活劇。明の時代。倭冦討伐のために武術家７人が集められる。メンバーのウーと妻は敵地への潜入を図るが、倭冦のボスは二人を試すためにイー将軍らの暗殺を命じる。二人は暗殺を成功させたように見せかけて、ボスをおびき寄せ、倭冦との闘いに挑む。サモ・ハン・キンポーやユン・ピョウらが、キン・フーならではの様式美あふれる壮大なアクションを繰り広げる。", "1975-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FRIENDS", "友達", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "1968年に発表された安部公房の戯曲を原作に、日本とスウェーデンの共同製作で５年をかけて完成された作品。有能な弁護士助手のジョンの住む部屋に、ある日なんの前ぶれもなく６人連れの見知らぬ家族が訪れる。ずうずうしい好意を押しつける彼らを、ジョンはどうしても追い払うことができず逆に追い詰められて……。", "1989-01-01", "14,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("旅の重さ", "旅の重さ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "男出入りの多い母親や、学校生活がイヤになった16歳の少女が、家を飛び出し四国遍路の旅に出る。痴漢にあったり、旅芸人一座に加わって、レズビアンを初めて体験したりの道中の末、少女は中年の魚の行商人と夫婦生活を始めてしまう。旅の出来事がスケッチ風に淡々と綴られていて、母親に送る手紙のように、少女のモノローグが時おり流れる構成になっている。吉田拓郎の『今日までそして明日から』が流れる中、まだ初々しい高橋洋子が、山あいの田園を歩く姿が美しい撮影で捉えられていて、新鮮でみずみずしい感動を与えてくれる。秋吉久美子がチョイ役で出演。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("恋する彼女、西へ。", "恋する彼女、西へ。", "", "", "http://image.eiga.k-img.com/images/movie/53327/315x210.jpg?2225", 102, "広島を舞台に、60年前からタイムスリップしてきた青年と現代女性との激しい愛を描いたSFラブストーリー。NHK連続テレビ小説「さくら」で注目を集めた田渕久美子が脚本を手掛ける。主演は「半落ち」の鶴田真由と「樹の海」の池内博之。出張で広島を訪れたキャリアウーマンの響子は、街中で白い海軍服を着た青年に声を掛けられる。青年と話すうちに、響子は彼が昭和20年8月3日からタイムスリップしてきたことを知り……。", "2008-02-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WORLD OF SUZIE WONG", "スージー・ウォンの世界", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "「慕情」とともに香港を舞台としたラブ・ロマンスの１本。新境地を求めて香港へとやって来た画家のロバートは、スージー・ウォンという娘をモデルとして雇う。次第に二人は惹かれ合うが、実はスージーは子持ちの売春婦で、ロバートに絵の勉強を続けさせるため、“商売”に出ようとするのだが……。", "1960-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOONSTRUCK", "月の輝く夜に", "", "", "http://ecx.images-amazon.com/images/I/512lAFx9D9L._SL210_.jpg", 102, "ニューヨークのダウンタウン。夫と死別した独身のロレッタに、幼友だちのジョニーがプロポーズ。彼女はOKしたが、母親の危篤でジョニーは故郷のシシリアに帰った。自分たちの結婚式への出席依頼のため、ジョニーの弟ロニーに会ったロレッタは、不運な自分を嘆くロニーを優しく慰める。満月の夜、フィアンセがいながら、その弟と一夜をともにしてしまったロレッタ。人の心を狂わすという満月の夜に起こったイタリア人一家のあれこれをユーモラスに描いた傑作。「シルクウッド」「マスク」のシェールが、この役でオスカーに輝いた。ディーン・マーティンが歌う『ザッツ・アモーレ』が印象的。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOW−UP", "欲望", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "カメラマンを主人公としたJ・コルタザールの小説にインスピレーションを受け、ロンドンでロケした作品。原題は、写真の引き伸ばしを意味する。売れっ子のカメラマン、トーマスは、深夜の公園でアベックを盗み撮りする。それに気付いた女の方は、トーマスのスタジオにやって来て体を代償にネガを取り戻そうとする。トーマスは、女に別のネガを渡し、アベックのネガを現像し、引き伸ばしてみると、草の上に何かが写っている。夜、公園に行ってみると、そこには男の死体があった。そして彼の留守中、スタジオから例のネガが消え、公園からは死体が消えていた。ラストでは若者たちが、目に見えない球でテニスをしている。アントニオーニが新境地を示した意欲作。", "1966-01-01", "3,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("なつかしい風来坊", "なつかしい風来坊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "衛生局の防疫課課長・早乙女は、家庭でも妻や娘に相手にされないサエない中年。ある日通勤電車で出会った土方の源さんの粗野なパワーに惹かれ、二人の間に友情が芽ばえる。源さんをハナ肇が、早乙女を有島が好演。山田喜劇の頂点の一つ。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("らぶれたあ", "らぶれたあ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 40, "鈴木清順監督が初めて手掛けた叙情派純愛ミステリー。クラブの歌手兼支配人の男はクラブでピアノを弾いている女に想いを寄せている。だが、女には、身体を壊し遠い山小屋で療養中の恋人がいた。二人の愛の証しは、交わし合うラブレターだ。時が経ち、恋人の手紙が次第に間遠くなっていく。片想いを続ける男は、恋人の安否を気づかう女に山小屋行きを助言する。女の帰還を願いつつ——。さて、彼女が山小屋で見たものは？　大ドンデン返しが待ち受ける、清順異色の作品。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NUAGES　LETTRES　A　MON　FILS", "雲／息子への手紙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "フランスの女性映像作家、M・ヘンセルによる“雲”のドキュメンタリー。雲に魅せられた彼女が、様々な表情を見せる雲の様子をカメラに収めている。南アフリカの喜望峰やアイルランドの火山などで撮影された映像は、“雲のある風景”というより“雲そのもの”を捉えたもの。雲が発生する瞬間など、気象学者も驚く貴重な映像が飛び出し、そこに彼女が息子へ宛てた手紙の朗読が加わることで、詩的で叙情豊かな作品に仕上がっている。また、その朗読の仏語版をC・ドヌーヴ、英語版をC・ランプリングが担当。二人の優しい語り口の朗読も味わい深い。", "2001-01-01", "37,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HORROR　EXPRESS", "ホラー・エクスプレス／　ゾンビ特急“地獄”行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "ゾンビやミイラに加え、宇宙から来たエイリアンまで登場するサービス満点のホラー未公開作。20世紀初頭のシベリア横断鉄道の車内が主な舞台となる。満州でミイラが続々と発見され、列車で運搬するのだが、そんなミイラたちが車内で次々に蘇生し、乗客を襲い始めた。恐怖は連鎖型に広がっていき、連続殺人事件が発生。さらに恐るべき宇宙人までもが出現して……。", "1973-01-01", "9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時雨の記", "時雨の記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "吉永小百合と渡哲也が日活時代の「愛と死の記録」以来、実に29年ぶりの共演を果たしたラブ・ストーリー。大手建設会社の重役・壬生が、若き日に見初めた華道教授・多江と偶然再会し、妻子がいる身にも関わらず熱い恋の炎を燃やす。壬生の強引とも思えるアプローチによって慎ましい逢い引きを重ねる二人だったが、壬生が心臓病の発作に襲われてしまい……。激しい切迫感に駆られるように一方的に求愛する男と、それを戸惑いながらもひたむきに受け入れる女を、主役二人が圧倒的な吸引力をもって体現。また二大スターの熱演に応えた澤井信一郎監督が、鎌倉・京都といった古都の美しい日本情緒を盛り込み、不倫ドラマらしからぬ清々しさに満ちた純愛劇に仕上げている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白い犬とワルツを", "白い犬とワルツを", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "同名ベストセラー小説を映画化した感動作。40年連れ添った妻に先立たれた英助は、深い悲しみに暮れていた。そんな彼の前に白い犬が現れる。どこからともなく現れ、そばに寄り添ってくれる犬に安らぎを感じ始める英助。犬は妻の生まれ変わりなのかもしれない。そう思った彼は、妻との約束を果たすために、犬を連れて長い旅に出る……。最愛の人を失った老人の孤独を静かに漂わせる仲代達矢を始め、演技派陣が脇を固める。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bulletproof", "ダーティ・ボーイズ", "", "", "http://ecx.images-amazon.com/images/I/51NM%2BNGlKmL._SL210_.jpg", 84, "「ハードロック・ハイジャック」のアダム・サンドラー＆「ラスト・ボーイスカウト」のデイモン・ウェイアンズ共演によるアクションコメディ。麻薬密売組織の大物コルトンを追う潜入捜査官ジャックは、コルトンの手下アーチーに接触し、厚い信頼を得る。しかしコルトン逮捕の直前になって正体を知られてしまい、撃たれて重傷を負う。やがて復帰したジャックは、逮捕されて検察側の証人となったアーチーの護衛を任されるが……。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("遥かなる走路", "遥かなる走路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "義兄の会社経営のもとで自動車の研究を続け、シボレーを解体して模倣品を作ることに始まる、現在のトヨタ自動車を築き上げた豊田喜一郎の一代記。トラックの需要が増えて自動車生産が軌道に乗った戦時中の話など興味深いが、美談調の宣伝映画でもある。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("藪の中", "藪の中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "黒澤明監督「羅生門」の原作となった、芥川龍之介の短編に新解釈を加えて映画化。時は平安。若い武士・金沢武弘と美しい妻・真砂は旅の途中、薮の中で多襄丸という盗賊に襲われる。武弘は妻を手込めにされたうえ、彼女の申し出により殺害されてしまう。その間に真砂は逃げ、多襄丸は検非違使につかまり打ち首に。だが、この事件に疑問を抱いた検非違使の森川中正は、事件のあった薮の中へと入っていく。彼はそこで美しい巫女と出会い、幻想と怪異な世界へと引きずり込まれていく。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("憲兵と幽霊", "憲兵と幽霊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "若き憲兵少尉は金と色の欲からスパイとなり、その罪を同僚に着せて銃殺。彼の母を自殺に追い込み、妻をも犯す。しかし、少尉のもとに現れた新任の憲兵は、殺した男に瓜二つの弟であった。忌まわしい過去を思い出し、苦悩する少尉は次第に追い詰められ……。猟奇色も豊かな戦争スパイ秘話。冷酷な憲兵少尉の役を天知茂が熱演している。「憲兵銃殺」というタイトルでビデオが出ていたこともある。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOR WHOM THE BELL TOLLS", "誰が為に鐘は鳴る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "文豪ヘミングウェイの代表作の映画化。スペイン戦争で右翼フランコ軍と闘う大学教授ジョーダンと、市長の娘マリアの激しい恋と、悲劇的な別離がドラマティックに描かれる。クーパーとバーグマンの組み合わせが絶妙。３年の年月を費やして製作された力作だ。", "1943-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("それいけ！アンパンマン　つみき城のひみつ", "それいけ！アンパンマン　つみき城のひみつ", "", "", "http://ecx.images-amazon.com/images/I/21RB64V3DZL._SL210_.jpg", 60, "やなせたかしの絵本を原作とする名作アニメ「それいけ！アンパンマン」の劇場版第4作。隣り合わせなのに仲の悪い「つみき島」と「おりがみ島」。それぞれの島で暮らすブロック王子とオリガ姫は、2つの島の関係に心を痛めながらも、互いに愛しあっていた。そんなある日、ばいきんまんが2つの島を手に入れようと攻め込んでくる。ブロック王子とアンパンマンたちは、ばいきんまんにさらわれたオリガ姫を助けようとするが……。", "1992-03-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("御存じ快傑黒頭巾 マグナの瞳", "御存じ快傑黒頭巾 マグナの瞳", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "慶応３年、幕府は長崎奉行を交代し、外国の新式武器購入に本腰を入れた。倒幕派の益満休之助も、オランダ人バーレから武器を買おうとするが、バーレは見返りに、長崎のどこかに隠された青色ダイヤ“マグナの瞳”を要求してきて……。世界一のダイヤをめぐり、幕府と倒幕派の争いが展開される。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎　野田版　研辰の討たれ", "シネマ歌舞伎　野田版　研辰の討たれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "2005年5月、18代目中村勘三郎襲名披露狂言として歌舞伎座にて上演された野田秀樹演出による大ヒット舞台を映像化。近江の国、粟津城内の剣術道場。研屋あがりのにわか侍・辰次は、根っからの町人根性が抜けず、周囲の武士たちからいつも仲間はずれにされていた。ある日、家老に散々に打ち据えられて恥をかかされた辰次は、家老への仕返しを企てる。ところが、辰次の仕掛けにはまった家老が死んでしまい……。", "2008-01-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("死にゆく妻との旅路", "死にゆく妻との旅路", "", "", "http://image.eiga.k-img.com/images/movie/54827/315x210.jpg?4203", 113, "1999年12月、夫婦でワゴン車に乗って旅をしている最中に末期がんだった妻が車内で亡くなり、保護者遺棄致死事件として報道された実話を、三浦友和と石田ゆり子主演で映画化。当事者の夫・清水久典さんによる同名手記をもとに、死と向き合いながら9カ月間にわたって旅を続けた夫婦の深い愛を描きだす。三億円強奪事件を題材にした映画「初恋」の塙幸成監督がメガホンをとる。", "2011-02-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last Movie", "ラストムービー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「イージー・ライダー」の大ヒットで、D・ホッパーがユニバーサルから100万ドルの製作資金を得て作り上げた作品。ペルーの映画ロケ地で撮影が終わった。ランチキ騒ぎを繰り返す映画の撮影隊にいや気がさしたスタントマンが、終了後もこの土地に残る。だが、アメリカ人夫妻と出会ったことから、酒とドラッグに溺れるようになって、どこかで見たような幻覚の光景に引きずりまわされる。現実と幻想、過去と現在を交錯させる特異なスタイルに、ユニバーサルとホッパーはこの映画の扱いをめぐって衝突し、ニューヨーク、ロサンゼルスで２週間、サンフランシスコで３日間しか上映されず、ホッパーはその後長らくアルコール中毒になったという、いわくつきの作品。", "1971-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("pierce〈ピアス〉 LOVE＆HATE", "pierce〈ピアス〉 LOVE＆HATE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "「blue」の安藤尋が、1997年に製作した作品。モデルのマリオや村上淳、町田康らのエキセントリックな存在感に注目。見知らぬ男とホテルに入っては、セックスの前に男を殺してしまう奈穂。そんな彼女に影のように寄り添う学は、奈穂の胸にピアスをする。無気力に生きる二人の前に、フリーライターの中谷という中年男が現れ……。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Joy Division", "JOY DIVISION ジョイ・ディヴィジョン", "", "", "http://image.eiga.k-img.com/images/movie/53577/315x210.jpg?1456", 93, "70年代末のUKロック・シーンに新たな風を吹き込み、全盛期を迎えながらボーカリストのイアン・カーティスが自殺を遂げた悲劇のバンド、ジョイ・ディビジョン。イアン亡き後に結成した新バンド\"ニュー・オーダー\"で大成功を収めた元メンバーや、伝説のレコード・レーベル\"ファクトリー\"創始者のトニー・ウィルソンら関係者の証言を通し、イアン・カーティスとジョイ・ディビジョンの真実に迫ったドキュメンタリー。", "2008-05-17", "3,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪名縄張荒らし", "悪名縄張荒らし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "第１作「悪名」と第２作「続・悪名」のストーリーを再構成。増村保造監督以下、旧大映関係者の心意気が画面にみなぎり、一級の娯楽映画に仕上がった。田宮二郎の当たり役・モートルの貞を北大路欣也が熱演。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("絶唱", "絶唱", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "大江賢次の原作を小林旭と浅丘ルリ子のフレッシュコンビで映画化した純愛メロドラマの古典。戦前の山陰地方。封建的な大地主の息子・順吉は、山番の娘・小雪と恋仲になるが、周囲の猛烈な反対の末に駆け落ちする。二人はつかの間の幸福を得るが、戦争が始まり順吉は召集される。戦争が終り順吉が復員してきた日、小雪は息を引き取る。戦前派の滝沢英輔監督が、キメ細かな演出で観客の涙腺をゆるませ、小林旭と浅丘ルリ子の二人も初々しい演技で好感が持てる。特に小林旭は、“マイトガイ”として売り出す以前の青春スターとして地味に活躍していた時期の作品だけに、興味深い。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ERIK", "戦場の牙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "政情不安な中米のとある国。そこの軍事政府に雇われた復員軍人エリック。彼は美人テロリストのリリアナと時ならぬ恋に落ちる。エリックの命の恩人となる青年ミゲルとの友情やロマンスを絡めながら、戦う男の生きざまを描くアクションもの。ジェームズ・ディーンのそっくりさんとして話題となったS・マクハーティの主演作。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VON RYAN’S EXPRESS", "脱走特急", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "D・ウエストハイマーの小説『フォン・ライアン特急』を、ベテランのM・ロブスン監督が映画化。第二次大戦末、イタリア中部からオーストリアに移送されることになった米軍捕虜たちは、輸送列車の乗っ取りに成功するが、ドイツ軍は彼らを執拗に追いかけ、ついにスイス国境で一大銃撃戦となる。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画　プリキュアオールスターズDX3 未来にとどけ！世界をつなぐ★虹色の花", "映画　プリキュアオールスターズDX3 未来にとどけ！世界をつなぐ★虹色の花", "", "", "http://image.eiga.k-img.com/images/movie/55957/315x210.jpg?2945", 70, "2004年の放送開始以来、女の子たちから絶大な人気を集め続ける「プリキュア」シリーズの劇場版第10作。巨大ショッピングモールへやって来た北条響と南野奏は、はぐれてしまった妖精ハミィを探すうちに、つぼみたちのファッションショーに乱入してしまう。すると、妖精たちが空から降ってきて、「おもちゃの国」の建物や「デザート王国」の巨大スイーツが次々と出現。さらに、これまでプリキュアたちが戦ってきた強敵たちまでがよみがえってしまう。", "2011-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ノンちゃん雲に乗る", "ノンちゃん雲に乗る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "天才少女ヴァイオリニストとうたわれた鰐淵晴子の本格的デビュー作。木に登って枝が折れ、池に落ちたノンちゃんが雲の上の白ヒゲのおじさんに出会うというマジカル・ファンタジー。童話作家のベストセラーが原作で、当時の人気女優・原節子も出演。雲の上の世界を映像化したアニメ合成などの特撮も独創的。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("真実一路", "真実一路", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "山本有三の同名小説の、二度目の映画化。かつて、愛人との間にできた子供を腹に抱えて、守川義平と形ばかりの結婚をしたむつ子。今は家出をし、別の愛人と暮らしている彼女は、18歳になった娘のしず子と再会する。愛について真実を貫いているつもりのむつ子だが、しず子はその生き方をふしだらと思うのだった。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("こま撮りえいが　こまねこ", "こま撮りえいが　こまねこ", "", "", "http://image.eiga.k-img.com/images/movie/33787/315x210.jpg?1920", 60, "ネコが主人公のコマ撮りアニメ。コマ撮りアニメは、人形をひとコマ（ひとコマは24分の1秒）ずつ動かし、照明・セット・カメラポジションを変えながら撮影するため、1日わずか数秒分の映像しか撮影できない。元々は、NHKのキャラクター「どーもくん」のスタッフによって東京都写真美術館での展示用に企画・制作された短編アニメだったが、今回は5つのストーリーからなるオムニバスとして劇場映画化された。", "2006-12-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLAY BACK", "プレイバック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ナイーブなジャンヌと奔放なジョアンヌは姉妹のように育った親友同士。バンド活動をしている彼女たちは、音楽の才能に恵まれたジャンヌが歌を歌い、それをジョアンヌがステージ上で口パクで演じて人気を高めていく。だが成功するほど、二人の間の溝は深くなり……。性格の異なる女性同士の友情を描いた青春映画。フランスの人気シンガーソングライター、M・ロスがジャンヌ役に扮するとともに、劇中の曲も手掛けている。", "1997-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("怪談鏡ヶ淵", "怪談鏡ヶ淵", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "円朝落語をもとにした、新東宝のおどろおどろしい怪談映画。古着を扱う大店・江島屋の身代を狙い、邪魔者を次々と手にかけていく番頭とその妾。策略にはまった善良な夫婦養子の安次郎とお菊が窮地に立たされた時、河から無数の亡者たちが現れて……。新東宝のお嬢様女優・北沢典子の初々しさは必見。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青春の門 自立篇", "青春の門 自立篇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 162, "前作『筑豊篇』に続く第２作で、スタッフは前作と同じ。昭和29年、信介は故郷の筑豊をあとに上京し早稲田大学に入学した。やがて、信介は石井講師の家に住み込み、ボクシングのコーチを受けながら、アルバイトに励んだ。演劇部の緒方は、貧乏生活のしのぎ方から新宿２丁目の遊び方までを信介に手ほどきする。新宿２丁目の娼婦・カオルは暗い過去のある女で、ある日、石井と衝動的に心中をはかる。やがて、信介の幼なじみの織絵が上京し、妊娠を告げた。浦山は信介の青春と彼の出会う人々を通して、自身の青春を追慕するかのごとく、ひたむきな青春像を共感をこめて描いた。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("私は貝になりたい", "私は貝になりたい", "", "", "http://image.eiga.k-img.com/images/movie/1917/315x210.jpg?4037", 139, "橋本忍が監督・脚本、主演・フランキー堺で映画化された1959年の同名作品を、SMAPの中居正広を主演にリメイク。共演に仲間由紀恵、西村雅彦、伊武雅刀ほか。高知の漁村で理髪店を営んでいた豊松は、召集令状により本部防衛の部隊に配属され、過酷な戦争を経験する。終戦後、家族との平穏な日々も束の間、豊松のもとに軍警察が現れ、従軍中の戦犯として逮捕されてしまう。必死に無実を訴える豊松だったが……。", "2008-11-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風ものがたり 食と農と環境", "風ものがたり 食と農と環境", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 58, "食と農と環境をテーマに、21世紀に向けて本当に安全で豊かな暮らしとは何かを問うドキュメンタリー映画。福井県で無農薬、減農薬栽培に取り組む夫婦、生ゴミの減量とリサイクルに取り組む市民グループなどの活動ぶりを紹介する。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BETTER OFF DEAD", "やぶれかぶれ一発勝負!!", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "何をやってもドジばかりで、あげくの果てに恋人ベイジャーにもふられてしまったレーン。そんな彼の家の隣に、キュートな女の子モニークが引っ越して来て……。誰もが１度は経験する初恋の甘酸っぱい思い出を綴ったエッチ度満点のコメディ。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("もしもしちょいと林昌さん わたしゃアナタにホーレン草　　嘉手刈林昌 唄と語り", "もしもしちょいと林昌さん わたしゃアナタにホーレン草　　嘉手刈林昌 唄と語り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 57, "70歳を過ぎてなお、現役の沖縄民謡歌手として活動を続け、島唄の神様ともいわれる嘉手刈林昌のコンサートの様子やインタビューなどで構成されたドキュメンタリー。監督は、「パラダイスビュー」「ウンタマギルー」と沖縄映画を撮り続ける高嶺剛。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOING ALL THE WAY", "インディアナポリスの夏　青春の傷跡", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "MTV界で活躍したM・ペリントンの初監督作。1950年代のインディアナポリスを舞台に、漠然とした不安を抱える若者たちの姿を描く青春ドラマ。帰還兵のソニーとガナーは高校の同級生。それまでつき合いのなかった二人だが、内向的なソニーとスポーツマンのガナーは、互いに自分にはない魅力を認め合い友情で結ばれる。やがてガナーは知的な美女にほんろうされ、ソニーはなかなかうまくいかない恋に傷ついていく……。恋や将来、家族間の問題に苦悩する若者たちの心の動きを、ときにコミカルにときにシリアスに繊細なタッチで描出。混沌とした1950年代を背景に、青春の光と影をスタイリッシュに映し出す。ビデオ、DVDタイトルは「インディアナポリスの夏」。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA DONNA DEL FIUME", "河の女", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "名うての女たらしの男と、彼に弄ばれた一人の女の姿を描いた恋愛ドラマ。捨てられ、男に対する復讐を誓いながらも男の子を産み、やがて明らかになる男の真実の愛を受け入れる女を、S・ローレンが熱演。なお物語の原作となるストーリーを、イタリアの現代文学を代表する作家A・モラヴィアが、E・フライアーノと共同で執筆している。", "1955-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE SANDS", "ホワイト・サンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "W・デフォーとM・ロークの、魅力的な顔合わせで描いたサスペンス。殺されたFBI捜査官に代わっておとり捜査を命じられた保安官のレイは、武器商人のレノックスに接近し、大金が動く国際武器売買に巻き込まれる。レイはFBIに報告するが、頼れるはずのFBIが逆に彼を罠にはめていたことを知ってしまう……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わたしたちがうたうとき", "わたしたちがうたうとき", "", "", "http://image.eiga.k-img.com/images/movie/57437/315x210.jpg?2748", 15, "無名の若手監督たちが作り上げた15分の短編10作品を特集上映する「NO NAME FILMS」の1本。外見も歌声も似たさやかと輝夏は、放課後の音楽室で合唱して気持ちを通じ合わせる。そんなある日、さやかは精神科医の父親が受け持つ患者が漂わせる停滞感と社会性のなさに強い印象を受けるが、その患者は輝夏の父親で……。監督は、映画評論家としても活動し、「犬を撃つ」（2000）がカンヌ国際映画祭シネフォンダシオン部門に出品された木村有理子。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KILLER TOMATOES STRIKE BACK", "キラートマト 決戦は金曜日", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "野菜兵器キラートマトの発明者、ガングリーン博士は、TV番組を使って“人類洗脳作戦”を開始、秘かに世界征服をたくらんでいた。かたや、町中では残虐なトマト事件が多発。人間たちは、サンドイッチの具にされ、食い殺されようとしていた……。「13日の金曜日」「エイリアン」「インディ・ジョーンズ」などあらゆるSF、ホラー、CMまでもパロディー化した“キラートマト”シリーズの第３作。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Strong Man", "当たりっ子ハリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "白塗りの無表情なキャラクターで人気者となり、チャップリン、キートン、ロイドと共に「四大喜劇王」とまで称されたハリー・ラングトンが主演をつとめるコメディ。監督は後に巨匠となるフランク・キャプラ。第1次世界大戦中、ベルギーの兵士ポールの元に、メリーというアメリカ人女性から手紙が届く。それまで女性から手紙をもらったことのなかった彼はメリーのことを好きになってしまい、終戦後、アメリカで彼女を探すのだが……。", "1926-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Inside", "INSIDE インサイド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "アパルトヘイト政策下の南アフリカ共和国。理想に燃える若き大学教授マーティは政治犯として拘留される。尋問官クルーガーは拷問だけでなく、仲間や恋人の裏切りをほのめかすなど卑劣な手を使って揺さぶりをかけ、屈強だったマーティの精神は崩壊していく。それから10年。クルーガーは黒人尋問官によって過去の非人道的行為を問い出されるが、あくまでも正当性を申し立てる。しかし、その尋問官は彼を追い詰めるための切り札を持っていた……。巨匠A・ペンが真摯な視線で撮り上げた社会派ドラマ。過去と現在を交錯させながら、それぞれの時代の尋問官対容疑者の対決を濃密なタッチで描いていく。新旧の個性派俳優たちによる演技合戦も見もの。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CELLULAR", "セルラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "アクション演出に定評のあるデヴィッド・R・エリス監督が、アカデミー賞女優キム・ベイシンガーを主演に撮り上げたサスペンス・スリラー。誘拐された女性からのSOSを偶然、携帯電話で受信した青年が彼女を助け出すために奔走。決死の救出劇が、スピード感あふれる演出でスリリングに描かれる。高校教師のジェシカは、自宅に侵入してきた男たちに拉致される。監禁された部屋には、壊れた電話が一台置いてあるだけだった……。", "2005-02-26", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA LOI", "掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "社会派リアリストとして出発し、1950年に政治的理由でヨーロッパに移ったダッシン監督の「日曜はダメよ」の前作。コルシカ島の賭博“掟遊び”を、町のボス、奔放な娘マリエッタ、彼女に気がある男などの人間関係とダブらせた佳作。", "1959-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jodaeiye Nader az Simin", "別離", "", "", "http://image.eiga.k-img.com/images/movie/57557/315x210.jpg?3640", 123, "2011年・第61回ベルリン国際映画祭で最高賞の金熊賞と男優、女優陣のアンサンブルに対して銀熊賞（男優賞・女優賞）が授与され、ベルリン映画祭史上初の作品および男優、女優賞の3冠を達成したイラン映画。第84回米アカデミー賞でもイラン映画として初の外国語映画賞を受賞したほか、脚本賞にもノミネートされた。離婚の危機を迎えた夫婦を軸に、両親をつなぎとめようとする娘や、彼らの問題に巻き込まれてしまうもうひとつの家族の物語が絡み合い、複雑な人間心理を描き出していく。監督は「彼女が消えた浜辺」（09）でもベルリン国際映画祭の銀熊賞（監督賞）を受賞したイランの新鋭アスガー・ファルハディ。", "2012-04-07", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A｀ NOUS DEUX", "夢追い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "強姦事件以来、悪の道に入ったコールガールのフランソワーズ。生まれついての泥棒シモン。警察に追われる身の二人が偶然出会い“ボニーとクライド”ばりの逃避行が始まる。パリ、コートダジュール、ケベック、ニューヨークと追われながらも夢を追う男女の恋愛ドラマ。", "1979-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOOD MORNING BABILONIA", "グッドモーニング・バビロン！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "イタリアのロマネスク建築修復家兄弟が家業の資金稼ぎのためにアメリカに渡った。誕生期のハリウッドでグリフィス監督「イントレランス」の栄華の都バビロンのセットに参加することになったふたりは、映画の虜になり、女優を妻に娶る。しかし時は第一次大戦下、ふたりの青春の日々が終わろうとしていた……。シャンテ シネ1のオープニングを飾ったタビアーニ兄弟の傑作。", "1987-10-09", "4,2,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CARO DIARIO", "親愛なる日記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "自作自演の作品ではミケーレの名前で登場していたモレッティが、分身ミケーレと別れ、初めて映画監督本人として主演したコメディ。モレッティ自身が日記に語りかけながら物語は始まり、日記に記された３つのエピソードが軽妙に語られる。〈第１章：ベスパに乗って〉は、自分の好きなローマの町の風景を楽しみ、憧れのジェニファー・ビールスとの出会い、〈第２章：島めぐり〉はテレビ中毒にかかった友人と、静かな仕事場を求めてシチリア周辺の島めぐり、〈第３章：医者めぐり〉では誤診がもとで様々な医者を回り、あげくの果てにはガンと宣告された実話に基づく悲喜劇が綴られている。", "1993-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JONAS QUI AURA 25ANS EN L’AN 2000", "ジョナスは2000年に25才になる", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "1960年代に多感な青春を過ごしてきた８人の男女。1968年５月革命の闘士で挫折したマックス。タントラにのみ興味を示す女秘書のマドレーヌ。失業中の印刷工マチューと、夫の代わりに電気工場で働くマチルドの夫婦。野菜栽培に精を出し、自然のなかで生活するマルセルとマルグリットの夫婦。風変わりな考え方を持つ高校教師のマルコ。フランスに住み、スイスのスーパーで働くマリー。彼らは今、30代を迎え、未来を想う。マチルドの産まれたばかりの息子ジョナスが25歳となる2000年、世界はどうなっているのだろうか。８人の男女の出会いと生活をスケッチ風に描きながら、現代の生き方を問うタネール監督の代表作。", "1976-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WANT TO STAY ALIVE", "レプスキー最後の挑戦 コートダジュール殺人事件", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "シリーズ完結編。元刑事で、今は南仏ニース駐在の保険会社調査員レプスキー。かつての恋人で、今は人気バイオリニストになっているリサとの再会を祝し、ひさびさのデートを楽しもうとする。だが、彼女はその晩ベッドの上で全裸で絞殺された。怒り爆発のレプスキーは友人の警部とともに捜査を始める。", "1989-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TWO JAKES", "黄昏のチャイナタウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "「チャイナタウン」（1974）の脚本＝主演コンビ、タウンとニコルソンが描いた16年ぶりの続編。前作から10年後、私立探偵のジェイクのもとに、とある男が妻の素行調査の依頼にやってくる。調査を進めるうち、ジェイクの胸に10年前の悲しい思い出がよみがえるのだった。ミステリアスなストーリーの中に感動ドラマを秘めたニコルソンの力作。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GEORGE’S ISLAND", "キャプテン・キッドの宝島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "250年前、海賊キャプテン・キッドがジョージ島に埋めたといわれる膨大な財宝をめぐる大活劇。ジョージ少年はおじいさんと二人暮らし。キャプテン・キッドの伝説を信じている二人は、ある日身勝手な連中に引き離されそうになり、船で町を逃れる。途中遭難し、漂着した先は、伝説の島ジョージ島。そこで二人が見たのは、宝の山と死んだはずの海賊たちだった。", "1990-01-01", "10,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FINEST HOUR", "シールズ 栄光の戦士たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "エリートの中のエリートだけがメンバーになれる、海軍のトップガン“SEALS”。ともにSEALS入りを目指す対照的な二人の男たちが、26週間におよぶ厳しい水中での戦闘訓練、女性関係、そして戦場と様々な場所で激しくぶつかりあい、助け合いながら男の友情や絆を築き上げていく。人間性を前面に描き出したアクション・アドベンチャー映画。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒薔薇昇天", "黒薔薇昇天", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "メイキング・フィルムのアイデアをポルノ撮影に取り入れた快作で、名作を数多く撮り上げた神代辰巳監督作品の中でも異彩を放つロマンポルノ。ヒロイン演じる谷ナオミの演技が開放的で明るく、心地良い。大阪に住む十三はブルーフィルムの監督兼販売人。主演女優が降板し撮影が頓挫したが、新たな女優を盗聴テープの声で発掘。さっそく強引なスカウトを始める。岸田森の怪優ぶりも要チェック。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HOUSE OF WAX", "肉の蝋人形", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "後に「カサブランカ」などの名作を世に送り出すM・カーティス監督が、C・ベルデンの戯曲を映画化。蝋人形館の彫刻家が、欲望に目が眩んだ共同経営者の放火で大ヤケドを負う。数年後、マスクで顔を覆った彫刻家の復讐が始まる。人間を蝋漬けにするという猟奇的な物語と、冷たいムードを生み出すセットが見どころ。", "1933-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COWBOY AND THE LADY", "牧童と貴婦人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "上院議員の一人娘オテンバのメリーが、フロリダの別荘で、地方まわりのカウボーイと知り合い、恋に落ちるというロマンティックなラブ・ストーリー。政界の有力者で次期大統領の野心を持つ父は、大切な時期に娘がカウボーイと結婚することによるスキャンダルを恐れて大慌て。なんとか結婚をやめさせようとするが……。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Case 39", "ケース39", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "レネー・ゼルウィガー主演のミステリー・ホラー。ソーシャル・ワーカーのエミリー（ゼルウィガー）は、ドメスティック・バイオレンスに苦しむ10歳の少女リリスを助け、自宅で一緒に住み始める。だが、2人の周囲では次々と不可解な事件が発生。リリスに取り憑いた悪霊が事件の原因だと判明するが……。共演にイアン・マクシェーン、ブラッドリー・クーパー。監督は「パンドラム」のクリスチャン・アルバート。", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("情人結", "初恋の想い出", "", "", "http://image.eiga.k-img.com/images/movie/53822/315x210.jpg?2940", 112, "「山の郵便配達」のフォ・ジェンチイ監督が描く、1980年代の中国を舞台にしたラブストーリー。主演は「少林サッカー」「レッドクリフ」のビッキー・チャオと「セブンソード」のルー・イー。幼馴染のチー・ランとホウ・ジアは互いに惹かれあうようになるが、ホウ・ジアの母親が夫の自殺はチー・ランの父親のせいだと交際を反対される。しかし家族間の確執があっても惹かれあう2人は、「ロミオとジュリエット」に自分たちの境遇を重ねて……。", "2008-10-04", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VELAZQUEZ’S LITTLE MUSEUM", "ラララ・ヒューマン・ステップス　inベラスケスの小さな美術館", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 50, "カナダの気鋭のダンスグループ、ラララ・ヒューマン・ステップスの作品「ベラスケスの小さな美術館」を収めた中編映画。スペインの画家ベラスケスの絵が飾られた空想の美術館を舞台に、バレエとアクロバットの要素を兼ね備えたダンスが展開されていく。官能的かつ破壊的ともいえる大胆な肉体の躍動には圧倒される。", "1994-01-01", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HARRY AND THE HENDERSONS", "ハリーとヘンダスン一家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "ヘンダスン一家が、キャンプの帰りに車でハネてしまったのは、なんと伝説の生物“ビッグフット”だった！　家に連れ帰った“彼”と一家の騒動を、ヒューマニティー豊かに描いたファミリー・ピクチャー。オスカー受賞のリック・ベイカーのメイクが見もの。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("水に棲む花", "水に棲む花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "人気漫画家・篠原千絵の作品を実写映画化。バスの転落事故に遭い、たった一人だけ生き残った六花。しかしそれ以来、水なしでは生きられない身体になってしまう。さらに、背中には花びらの模様をした不思議なアザができ始め……。前田亜季が不思議な因縁に巻き込まれていく女子高生をミステリアスに好演。", "2006-05-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OVER THE TOP", "オーバー・ザ・トップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "妻子を残し家を飛び出したコンボイ・トラックの運転手。妻の病気を契機に10年ぶりに息子と再会するが、二人の間には心の溝ができていた……。父として男として、主人公がすべてを懸けたアームレスリング・シーンが圧巻。力強い父をスタローンが演じ、親子の絆をうたい上げた一編。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("芙蓉鎮／HIBISCUSS TOWN", "芙蓉鎮", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "現代の中国映画界を代表する巨匠シェ・チンが、クー・ホア原作の『古華』を映画化した現代史ドラマの傑作。鎮というのは町にあたり、この映画の表題「芙蓉鎮」は、この作品の舞台となる町のこと。そこで米豆腐を売るヒロイン、ユィインをめぐる、1963～79年までの文化大革命をはさんだ16年間の生活の移り変わりを描き出す。ユィインは夫と一緒に努力し、米豆腐を売る店を構えるまでになる。しかし、その努力は、資本主義的ブルジョワジーという烙印で簡単に踏みにじられ、町では反社会主義分子の厳しい摘発が行われる。文化大革命の嵐の中、彼女は店を没収され、夫を失う。絶望の日々を送るユィイン。インテリでありながら“ウスノロ”と呼ばれ右派の烙印を押されるチン。状況の変化を静かに見つめるクー。日和見主義のお調子者ワン、中国現代の歴史とその変転、そして状況にほんろうされながら生きる人々の姿が見事に描かれた作品。百花奨の最優秀作品賞ほか３つの賞を受賞。", "1987-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("High School Musical", "ハイスクール・ミュージカル", "", "", "http://ecx.images-amazon.com/images/I/61Kihjvq2jL._SL210_.jpg", 98, "世界中に熱狂的なファンを生んだディズニー・チャンネル発の大ヒットテレビ映画。イースト高校バスケ部の人気者トロイと、数学が得意な優等生ガブリエラ。大みそかのパーティでなりゆきからデュエットすることになった2人は、一緒に歌う楽しさを知り、互いに好意を抱く。そして新学期、偶然にもイースト高校にガブリエラが転校してくる。再会を果たした2人は、学校主催のミュージカルのオーディションに一緒に参加することを決めるが……。", "2006-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("まぼろし", "まぼろし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "25年の結婚生活をともにしてきた、50代の夫婦マリーとジャン。子供はいないが幸せな2人は、今年も毎年夏にバカンスに訪れている南仏の別荘にやってくる。しかし、人気のない海辺でマリーが眠っている少しの間に、海に入ったはずのジャンが行方知れずになってしまう。警察の捜査も虚しく手がかりはつかめず、パリで日常生活に戻ったマリーだが、やがてジャンの幻が見えるようになり……。フランソワ・オゾン監督が愛する人を失った女性の深い喪失感を描いたドラマで、“死についての3部作”の第1章。", "2002-09-14", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛より愛へ", "愛より愛へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "助監督時代の大庭秀雄が書き上げたオリジナル・シナリオを島津保次郎が監督した、都会派小品メロドラマ。良家出身の無名作家の青年が酒場の女給との結婚を親に反対されるが、青年の妹の努力でなんとか結婚が認められるまでを描く。妹役は、当時売り出し中の高峰三枝子。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("昭和残侠伝", "昭和残侠伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "敗戦直後、浅草の露店商街。新誠会は神津組の縄張りを狙っていた。復員した寺島清次は神津組の跡目を継ぎ、新しいマーケットの建設に力を注ぐ。新誠会は妨害を繰り返し、ついにマーケットに放火する。清次は客人の風間重吉とともに新誠会に殴り込む。健さんの他のシリーズ「日本侠客伝」「網走番外地」と違って最初からシリーズ化をもくろんでいたため、新旧ヤクザの対立や高倉＝池部の道行きなど基本パターンはすでに出そろっている。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OFF BEAT", "オフビート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "警察と市民との親睦をかねて行われるミュージカル公演を軸に展開する物語。ニューヨーク市立図書館に勤める気のいい青年が、ひょんなことから警察官に扮して、ミュージカルの練習に参加するハメになる。そこで、美人警官と恋に落ちたり、銀行強盗に巻き込まれて大騒動になったりと、にぎやかなラブ・コメディが繰り広げられていく。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四十七人の刺客", "四十七人の刺客", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "元禄14年（1701年）の春。江戸城で播州赤穂藩主・浅野内匠頭が勅使響応指南役・吉良上野介と刃傷沙汰を起こし、浅野は即日切腹、藩は取り潰しと処断された。しかし吉良は無罪。この一方的な処遇の裏には、時の宰相・柳沢吉保と上杉家江戸家老・色部又四郎の策謀があった……。「十三人の刺客」などの脚本家・池上金男が、別名で発表した時代小説の映画化で、おなじみ「忠臣蔵」の物語に新解釈を施し、一種の情報・経済戦争として扱っている点が異色。吉良邸討ち入りを胸に誓った大石内蔵助が、まず塩相場を操って資金を得るくだりや、意図的な流言で“吉良憎し”の世論を高めるなど、これまで“涙”や“義理”でごまかしていた部分を合理的な解釈で埋めている。高倉健が挑む４度目の時代劇で、市川崑との初顔合わせになる。日本アカデミー賞では色部を演じた中井貴一が助演男優賞に輝いたほか、美術・録音・編集の計４部門で受賞。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おかえり、はやぶさ", "おかえり、はやぶさ", "", "", "http://image.eiga.k-img.com/images/movie/56072/315x210.jpg?2722", 114, "世界で初めて地球から3億キロ離れた小惑星イトカワの微粒子を採取して地球へ帰還した無人小惑星探査機はやぶさと、そのプロジェクトに携わった人々のドラマを全編3Dで映画化。2003年5月9日に打ち上げられたはやぶさは、燃料漏れや通信途絶、4基のメインエンジンの停止などさまざまなトラブルに見舞われながらもイトカワに到達。4年間35億キロの予定だった航行は7年60億キロに及び、10年6月13日、奇跡的に地球へと帰還してきた。18年間に及んだプロジェクトを、計画に携わった人々の親子の絆や再生を交えながら描く。監督は「ゲゲゲの鬼太郎」「鴨川ホルモー」の本木克英。JAXAエンジニア助手を演じる藤原竜也ほか、杏、前田旺志郎（まえだまえだ）、三浦友和らが出演。", "2012-03-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEAD PRESIDENTS", "ダーク・ストリート　仮面の下の憎しみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "海兵隊に志願し、ベトナムへ派遣されたアンソニー。しかし、復員した彼を待っていたのは、あまりにも厳しい現実だった。先に除隊していたスキップはヤク中になっており、せっかく見つけた仕事場も潰れてしまう。生活苦に陥った彼は、以前の恩人カービーやスキップらと、銀行強盗を企てる。ところがスキップのミスで、計画は失敗、逮捕され裁判にかけられる。アンソニーの弁護士は、彼をベトナムの英雄として弁護するが……。ベトナム帰還兵の苦悩と転落をテーマに、「ポケットいっぱいの涙」のヒューズ兄弟が現代アメリカの深層をえぐり出す問題作。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Whirlpool", "アリ地獄のような街", "", "", "http://image.eiga.k-img.com/images/movie/55332/315x210.jpg?1505", 79, "バングラデシュの田舎の農村で暮らす少年ラジュは、わずかなお金を握りしめ、大都会ダッカへと向かう。仕事を探していたラジュは、路上で暮らすストリートチルドレンにイアシンという男を紹介され、自分でも気づかぬうちにあらゆる違法な商売に手を染めていく……。バングラデシュでストリートチルドレンを支援するNGO「エクマットラ」の代表シュボシシュ・ロイが監督を務め、実際のストリートチルドレンたちを役者に起用している。", "2010-04-10", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("КАТОК И СКРИПКА", "ローラーとバイオリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 45, "1986年に他界した、ソ連が生んだ映像詩人、A・タルコフスキー。彼が、ソ連邦国立映画高等学院監督科の卒業製作として監督したこの作品は、ニューヨーク国際学生映画コンクールで第１位に輝き、以後タルコフスキーが国際的に注目されるキッカケになった。バイオリンを習わされている夢想家の少年サーシャは、ある日、いじめにあっているところを、整地作業をしてローラーを引いていた青年セルゲイに助けられる。二人は仲良くなり、サーシャはセルゲイから労働の意味を、セルゲイはサーシャによって音楽の素晴らしさを知った。だが、サーシャの母は、セルゲイとのつきあいを快く思っていなくて……。アルベール・ラモリスの「赤い風船」に想を得て、少年と大人との友情を、詩情豊かにうたい上げた、タルコフスキー初期の秀作。", "1960-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("決戦天の橋立", "決戦天の橋立", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "時代劇の大スター、アラカン・大河内・月形の顔合わせ。数十名の山賊を軽々と退治するほど強い岩見重太郎が、父と弟を暗殺された仇討ちを決意。後藤又兵衛らの助けを借りて天の橋立二百五十番斬りの幕が切って落され……。クライマックスの立ち回りが見事。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢十夜　海賊版", "夢十夜　海賊版", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "文豪・夏目漱石の短編小説「夢十夜」を市川崑、清水崇、西川美和ら日本映画界を代表する10組の監督が映像化した「ユメ十夜」に対抗し、無名の若手監督たちが同作の映像化に取り組んだオムニバス映画。東京藝術大学映像研究科で学ぶ船曳真珠、2008年に「シャーリーの好色人生」で注目を集めた佐藤央、歌舞伎町でダンサーとして働きながら映像制作を続ける天野邪子ら、個性あふれる10人の監督たちが独自の解釈で漱石の世界に挑む。", "2007-03-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Charade", "シャレード", "", "", "http://ecx.images-amazon.com/images/I/51Fu8R3HM4L._SL210_.jpg", 113, "「雨に唄えば」（1953）の巨匠スタンリー・ドーネン監督が、オードリー・ヘプバーンを主演に描くコミカルなミステリー。レジーナは友人とのスキー旅行の途中で離婚を決意するが、帰宅すると夫は死んでいた。さらに、夫が戦時中に3人の男と軍資金を横領したあげく、仲間をだましていた事実が発覚する。3人から脅迫を受けるはめになったレジーナは、旅行で知り合ったピーターに助けを求めるが……。", "1963-12-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NOTTE D’ESTATE CON PROFILO GRECO，OCCHI A MANDORLA E ODORE DI BASILICO", "流されて２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "「流されて…」の女流監督ウェルトミューラーが、再び根源的な男女の愛の形をコミカルなタッチの中で痛烈に描く。上流階級から金を巻き上げるテロリストのボスを女実業家が逆に誘拐。孤島に閉じ込め身代金を奪おうとするが、男の荒々しいセックスに魅せられて……。主役メラートの豪華なヴァレンチノの衣装にも注目。", "1987-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Exam", "エグザム", "", "", "http://image.eiga.k-img.com/images/movie/55502/315x210.jpg?2097", 101, "合格すれば死ぬまで年俸1億円を保証するという大企業の最終就職試験に残った8人の男女が繰り広げる、壮絶な頭脳戦とサバイバルを描く。8人は武装した警備員が監視する密室で、白紙の問題用紙と向き合っていた。80分という制限時間のなか、受験者たちは手を組んだりだましあったりして、必死に試験の問題を見つけようとするが……。イギリスの新鋭脚本家スチュアート・ヘイゼルダインの長編監督デビュー作。", "2010-07-17", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Brothers", "マイ・ブラザー", "", "", "http://image.eiga.k-img.com/images/movie/53172/315x210.jpg?4458", 105, "スサンネ・ビア監督によるデンマーク映画「ある愛の風景」（04）を、「マイ・レフトフット」「父の祈りを」の名匠ジム・シェリダンがリメイクした人間ドラマ。アフガニスタンへ出兵し戦死した兄のサムの代わりに、一家のはみ出し者だった弟のトミーが、未亡人となったサムの妻グレースと残された2人の娘のいる家庭に入る。トミーとグレースは親密な関係を築いていくが、米海軍からサム生還の知らせが入り……。出演はトビー・マグワイア、ジェイク・ギレンホール、ナタリー・ポートマン、サム・シェパード。脚本は「トロイ」「25時」のデビッド・ベニオフ。", "2010-06-04", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("時をかける少女（1983）", "時をかける少女（1983）", "", "", "http://ecx.images-amazon.com/images/I/51w7SN4OtLL._SL210_.jpg", 104, "筒井康隆の名作SF小説を、「転校生」の大林宣彦監督が映画化した青春ファンタジー。高校生の芳山和子は、学校の実験室で白い煙とともに立ちのぼったラベンダーの香りをかいだ瞬間、意識を失い倒れてしまう。それ以来、時間を移動してしまうような不思議な現象に悩まされるようになった和子は、同級生の深町一夫に相談するが……。本作が映画初主演となる原田知世がフレッシュな魅力でヒロインを好演し、大ブレイクを果たした。", "1983-07-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FOREVER MINE", "人妻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「タクシードライバー」や「救命士」など、マーティン・スコセッシ作品の脚本家として知られるP・シュレーダー。「白い刻印」では監督としても高い評価を受けた彼が、長年温めていた企画を映画化したラブ・サスペンス。マイアミの高級ビーチで学費を稼ぐために働いているアランは、ある日、一人の美しい女性に一目で心を奪われる。だが、彼女は人妻だった。許されない恋とは知りながらも、アランはエラへの思いを募らせていく……。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("家族輪舞曲", "家族輪舞曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "発売当時、学生作家だった椎名桜子が自身の体験をもとに書いた同名のデビュー作を自ら脚本化、監督し、話題作となった作品。父親が次々と新しい恋人を作り、それが代わるがわる家族の一員となっていく特異な家庭に育つ二人の姉妹。今までにない家族構成のなか、彼女たちは大きく揺れ動きながらも新しい生活の風景をつむぎだしていく。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("免田栄 獄中の生", "免田栄 獄中の生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "34年６ヵ月もの独房生活を強いられた無実の元死刑囚・免田栄氏に、獄中で彼自身が綴ったぼう大な手紙を再び見せ、当時の気持ちの揺れや覚悟、放免後の現在の心境をともどもに照らし出す。免田氏の真正面にカメラを据えたシンプルなインタビュー形式が、えん罪がもたらした一人の人間の気迫と無念を鋭く捉える。“何のために出てきたのか……”と言う彼の問いかけが重い。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FIREPOWER", "ファイヤー・パワー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "近未来のロサンゼルス、無法地帯で暴れ回る“ヘルライダー”と呼ばれるギャングたちと、二人のスーパーポリスが戦うSFアクションのテレフィーチャー。スティーヴ・マックィーンの息子で、父の跡を継ぎアクション俳優となったC・マックィーンが主演。幼い頃から習っていた空手の腕前を披露する。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LA　VERDAD　DE　LA　LUCHA", "ミル・マスカラス／愛と宿命のルチャ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "メキシカン・プロレスこと“ルチャ・リブレ”の世界を描く熱気あふれるドラマ。1960年代に非業の死を遂げた人気タッグ・コンビの息子たちが成長し、運命のめぐり合わせによってリングで相まみえるというドラマティックなストーリー。日本でもおなじみのミル・マスカラス、ドス・カラスらルチャの英雄レスラーが主演を務めている。", "1988-01-01", "19", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("祐天吉松", "祐天吉松", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 68, "巾着切りの祐天吉松は、豪商加賀屋の一人娘・お縫の純情にほだされて、堅気の経師職に戻り、加賀屋に婿入りする。だが、昔の仲間の金五郎が脅迫を始め、吉松は家に迷惑がかかるのを怖れて旅に出る。金五郎は、それを良いことに、加賀屋に火を放つ……。八尋不二のオリジナル脚本を映画化した森一生の監督昇進第２作。", "1937-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YANKEE DOODLE DANDY", "ヤンキー・ドゥードゥル・ダンディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 126, "ボードビリアンとして初期のブロードウェイで活躍したジョージ・M・コーハンの伝記映画。主人公に扮したキャグニーはギャング役で一世を風靡した役者だが、実際は歌って踊れることをこの作品で実証した。ダンスナンバーで踏むあざやかで軽快なタップは素晴らしい。", "1942-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Les saveurs du Palais", "オート・キュイジーヌ（英題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2013-12-31", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES AILES DU PAPILLON", "蝶の翅", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 52, "スイスの監督ロッドの“残酷物語”３部作完結編。奇怪な雰囲気漂う古屋敷の中。赤ん坊のような面持ちの巨漢と陰気な顔をした監視人、そして二人の老婆……。これは、フロイト的オイディプス物語の異形ともいえる寓話的作品だ。独特の耽美的な映像が圧巻。", "1982-01-01", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KARAKTER", "キャラクター 孤独な人の肖像", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 125, "F・ボルデバイクのオランダの古典的小説『キャラクター』と『ドレイブルハーブンとカタドローフ』をもとに描く重厚な人間ドラマ。父と息子の長年に渡る葛藤、それぞれが抱える孤独や不安を、陰影に富んだ映像で描いた作品。1920年代のロッテルダム。殺人罪で逮捕された青年ヤコブが、自分の過去を振り返る。冷たい母と二人暮らしのヤコブは幼い頃、父親がいないことでいじめられていた。そんなある日、母に声をかけた男ドレイブルハーブンが父親であることを直感で知る。強欲な金貸しの彼は、ある時は手を差し延べ、ある時は債権者としてヤコブを突き放す。父の行動が理解できない苦悩の中で、ヤコブは働きながら弁護士を目指して勉強に励み、やがて彼が弁護士資格を取得した日、ある事件が起きる……。ビデオタイトルは「キャラクター」。", "1996-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("KAMERADSCHAFT", "炭坑", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "戦争直後のフランスとドイツの国境の町。お互いに憎悪の感情が残っている中で炭坑事故が発生した。救出に向かった３人のドイツ坑夫はフランス人の二人の坑夫と地下に閉じ込められてしまう……。坑内からの脱出劇とその中で国を超えた人間の絆が生まれていく様を力強いタッチで描いたスペクタクル。", "1931-01-01", "7,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("砂の上の植物群", "砂の上の植物群", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "吉行淳之介の同名小説を中平康が映画化した風俗もの。化粧品セールスマンが、マリンタワーで会った美少女に姉を犯して欲しいと頼まれるが、その姉はマゾヒストで、二人は次第に倒錯的な関係にのめり込んでいく。日活きってのテクニシャン中平康が、映像化の難しい原作をソツなくこなした。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cool World", "クール・ワールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "人気漫画家ジャックは、自分の描いた漫画『クール・ワールド』へ引き込まれてしまう。そこにはジャックによって描かれたキャラクターたちが生きていた。その中の一人、美女ホリーは、人間と寝て漫画から生身の人間になろうとしていた。もし彼女が人間になってしまったら、人間界とクール・ワールドの空間バランスが崩れるのだ！　K・ベイシンガーが、アニメーション・キャラクターのホリーに劣らず、セクシーな人間のホリーを悩ましく演じる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("New York, I Love You", "ニューヨーク、アイラブユー", "", "", "http://image.eiga.k-img.com/images/movie/54202/315x210.jpg?3332", 103, "ファティ・アキン、チアン・ウェン、ブレット・ラトナー、岩井俊二、ナタリー・ポートマンら世界中から集った個性的な11人の監督が、ニューヨークを舞台にさまざまな愛の物語を紡ぐ群像オムニバス。ソーホー、ハーレム、セントラル・パーク、グリニッジ・ビレッジなどニューヨークの各名所で撮影が行われた。ヘイデン・クリステンセン、オーランド・ブルーム、クリスティーナ・リッチ、シャイア・ラブーフ、イーサン・ホーク、マギー・Q、アンディ・ガルシアらハリウッドの豪華キャストが揃う。", "2010-02-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("２つの名前を持つ男", "２つの名前を持つ男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "韓国映画の発展に尽力したカメラマン・金学成、日本名・金井成一の足跡を辿るドキュメンタリー。戦前、金井成一という名で日本に渡り、撮影技師となった金学成。現在の韓国映画界の重鎮たちを育て上げた彼の映画人生を、友人であった名カメラマン・岡崎宏三らの証言と、貴重な映像を交えて振り返る。", "2006-05-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道VSまむし", "極道VSまむし", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "“極道”と“まむしの兄弟”の両人気シリーズをドッキングさせたコミック・アクションだが、第10作に続き、またしても不発。シャバに出た極道・島村清吉とゴロ政と不死身の勝は、いがみ合いながらも最後は共通の敵に、一致協力して殴り込む。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Underground", "アンダーグラウンド　デジタルリマスター版", "", "", "http://image.eiga.k-img.com/images/movie/56942/315x210.jpg?4572", 171, "1995年・第45回カンヌ国際映画祭でパルムドールを受賞したエミール・クストリッツァの名作をデジタルリマスターで上映。1941年、ナチスドイツがユーゴスラビアに侵攻。ベオグラードに住む武器商人のマルコは祖父の屋敷の地下に避難民たちを匿い、そこで武器を作らせて生活する。やがて戦争は終結するがマルコは避難民たちにそのことを知らせず、人々の地下生活は50年もの間続いていく。", "2011-09-24", "2,7,30", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("1THE FOUR MUSKETEERS", "四銃士", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "「三銃士」に続く後編で、２作同時に撮影された。前半で３人の銃士に加えられたダルタニアンは、陰謀に巻き込まれ、処刑されそうになるコンスタンスを助けようと必死の活躍。悲惨な結末ながら、ドタバタ精神を失わないオフビート・タッチが小気味良い。", "1975-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUBLE　TROUBLE", "ダブル・トラブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "E・プレスリー主演の日本未公開作品。ミステリーとコメディを融合した珠玉のエンターテインメントだ。ベルギーへ向かう船中、自分を追っかけて来る17歳の少女に手を焼く人気歌手。そんな時に、歌手と少女の命を狙う事件が続き、謎めいた二人組がさらなるトラブルを呼んでくる。アッと驚く事件の犯人が、最後にその正体を現す！", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SANDS OF KALAHARI", "カラハリ砂漠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "アフリカのある空港でヨハネスブルグ行きの便を待っていた３人の男は、その便がないとわかると小型機をチャーターする。ところがその小型機が殺人鬼にハイジャックされ、カラハリ砂漠に不時着。運良く助かった彼らは数日砂漠をさまよい、なんとか水と食料を手に入れるが、そこで殺人事件が起こり……。窮地に立たされた人間の歪みを巧みに描くサバイバル・ムービー。", "1966-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("STATE SECRET", "絶壁の彼方に", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "世界的権威の外科医マーロウ博士は、東欧の独裁国に招かれて、公開手術を行った。しかし、その患者が独裁者本人であり、しかも彼が手術後に急死してしまったことから、博士は秘密警察から命を狙われ、国内を逃げ回るはめに陥る。政治的寓意を多分に含んだイギリス伝統の冒険スリラー映画。", "1950-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("維新の曲", "維新の曲", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "大映創立第１回超大作。新旧のスターを集結、幕末の英雄群像を薩長連合誕生・大政奉還を中心に描く。坂本龍馬に阪妻、桂小五郎に右太衛門、西郷隆盛に千恵蔵、将軍慶喜に嵐寛を配している。市井の娘・あきが、尊敬する理想家・吉田実磨を殺した新選組の沖田に情をかける場面などに、明治維新の思想的勝利が描かれている。これは太平洋戦争中であった当時の日本の事情のせいであるが、逆に一部からその思想の単純さと古さを指摘され、映画自体の持つ大味さを露呈する結果となった。とはいえ、大スターの初顔合わせの魅力は大きい。実質的な主演は阪妻と右太衛門でほかはゲスト格だが、その豪華さは楽しめる。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("福本耕平かく走りき", "福本耕平かく走りき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "日本映画学校の農業実習の体験をもとに執筆した脚本が第16回城戸賞を受賞、自らがメガホンを執るに至った久保田傑のデビュー作。福島県の農村を舞台に、肉親の死や進路の問題に直面しながらも、自分自身の生き方を模索する若者たちの群像を、みずみずしいタッチで描く。日本映画に久々に現れた正統派の作風が高い評価を受けた。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ミュージカル黒執事 The Most Beautiful DEATH in The World 千の魂と堕ちた死神", "ミュージカル黒執事 The Most Beautiful DEATH in The World 千の魂と堕ちた死神", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "19世紀イギリスを舞台に、名門貴族の少年に仕える執事の活躍を描いた人気コミック「黒執事」の舞台版第2弾を映像化。「女王の番犬」として裏の仕事を請け負っているファントムハイブ家の若き当主シエルと、呪われた契約によってシエルに執事として仕える悪魔セバスチャン。ある日、2人は女王の命令を受け、ロンドンで頻発している無差別殺人事件の捜査に乗りだす。セバスチャン役に「時をかける少女」の松下優也。", "2010-09-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シックス・エンジェルズ", "シックス・エンジェルズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "近未来の民間警備会社“ガード・オブ・ローズ”に勤務する美女たち。彼女らは実は男性の不当な暴力からすべての女性を護ることを任務とした護身エキスパートだった。そんな彼女らが、突如米軍と巨大地下刑務所からの攻撃を受けたことから地球規模の争いに巻き込まれていく。秋本康がプロデュース、メカデザイナーとして著名な小林誠が総監督を務めたSFアクション・アニメーション映画。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Alla vi barn i Bullerbyn", "やかまし村の子どもたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「長靴下のピッピ」「ロッタちゃん」シリーズでおなじみの、スウェーデンが誇る人気児童文学作家アストリッド・リンドグレーンの代表作を、作者自身の手による脚本で映画化。監督は、同じくスウェーデンの名匠ラッセ・ハルストレム。家が3軒しかないやかまし村で暮らす6人の子どもたちは、まるで兄弟のようにいつも一緒。白夜の国スウェーデンの美しい自然と暖かい大人たちに囲まれて、彼らが過ごす短い夏休みをいきいきと描く。", "1986-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("あげまん", "あげまん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "話題作りには定評のある伊丹十三が仕掛けた監督第５作。当時流行語ともなった“あげまん”とは、男にツキをもたらす女のこと。捨て子だったナヨコは芸者の置屋にあずけられ、やがて僧侶の旦那を持つが、その旦那の位はめきめきと高くなり、ある日知り合った銀行員も、どんどん出世していく。周囲の男の運を開いていく“あげまん”ナヨコであったが、彼女自身の人生は決して幸福ばかりではなかった……。宮本信子、津川雅彦、大滝秀治といった伊丹映画の常連を配してユーモアと人情を絡めて描くヒット作。宝田明、島田正吾といった脇役も実にいい味を出している。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("放浪／SWEET　DEGENERATION", "台北ソリチュード", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "伝統的な倫理観と急速な近代化が混在する、現代の台北を舞台にした人間ドラマ。結婚生活の希望をなくした女性アフェンと、兵役を終えて怠惰な日々を過ごす、その弟チュンシェン。彼らには姉と弟の関係を越えた暗い過去があった。やがてアフェンは落とした携帯電話が縁で、奔放な娼婦と知り合う。だが、偶然にもチュンシェンは、この娼婦と恋に落ちていた……。リアルな語り口のドラマから、心の重荷を抱えた人々の精神的な救済が浮き彫りに。", "1997-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RENDEZ−VOUS", "ランデヴー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "不動産会社に勤めるポールは、部屋を探す女優志願の娘ニーナと知り合い心惹かれる。だが、ニーナはポールの友人で役者のカンタンに恋をしてしまう。三角関係に悩むポールだが、ある日、カンタンが交通事故で死ぬ。彼の死の真相には実は裏があった……。A・テシネの斬新な映像処理が冴える、J・ビノシュの出世作。", "1985-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Birdman of Alcatraz", "終身犯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 147, "　終身刑を宣告され、刑務所に送られた殺人犯ロバート・ストラウドは迷い込んだ小鳥を独房内に持ち込み、育てることに。やがて鳥の観察と研究に夢中になり、ついには雑誌に論文を発表。その後、愛鳥家の女性ステラがロバートを訪ね、ふたりは心を通わせていく。そんな折、刑務所内での鳥の飼育が禁止されることに……。鳥類学の権威となった囚人の実話を映画化。バート・ランカスターの演技も高く評価された。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Meet Bill", "幸せのセラピー", "", "", "http://image.eiga.k-img.com/images/movie/54552/315x210.jpg?3983", 97, "大手銀行頭取の娘と逆玉の輿で結婚したものの、引っ込み思案なビルは、身内や同僚から見下され、肩身の狭い日々を過ごしていた。そんなある日、妻の浮気が発覚したことから、更なる汚名をかぶったビルは一念発起。かねてよりの夢だったドーナツ・チェーン店のビジネスに乗り出し、妻を取り戻そうと奮闘するが……。主演は「ダークナイト」のアーロン・エッカート、共演に「ファンタスティック・フォー」シリーズのジェシカ・アルバほか。", "2009-06-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ホワイト・ラブ", "ホワイト・ラブ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "原案を公募して、製作された、百恵・友和コンビの第10作記念作品。それぞれに陰の部分を持つ若い男女が反発しながらも愛し合っていく姿を、スペイン・ロケを敢行して描いた青春メロドラマ。10作目にして初めて、山口百恵が等身大の現代の女性を演じる。", "1979-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鯉名の銀平", "鯉名の銀平", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "衣笠貞之助監督作品をはじめ何度も映画化された大衆小説の巨匠長谷川伸の股旅もの。元ヤクザの銀平は惚れた娘・お市が親友・卯之吉を好いていると知った時、二人をヤクザから救って旅立つ。そして４年後、二人が再び窮地に陥った時に帰って来た……。市川雷蔵が、情に厚いヤクザ崩れの旅ガラスを好演している。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("純愛物語", "純愛物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "不良仲間から足を洗おうとしてリンチを受けそうになっていたミツ子は、ふとしたことから貫太郎に助けられる。二人は組んでスリをして稼ごうとたくらむが、警察に捕まり、貫太郎は鑑別所へ……。新人の中原ひとみを主役に起用し、みずみずしいタッチで描いて大ヒットした青春ドラマ。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白・THE WHITE", "白・THE WHITE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "AV監督、平野勝之の「由美香」「流れ者図鑑」に続く北海道自転車三部作の完結編。今度はたった一人、平野自身が35ミリフィルムを抱え、生まれ故郷、静岡県浜松市から極寒の北海道へ自転車で縦断する。途中、盲腸で緊急入院するなどのアクシデントに見舞われながらの過酷な旅。自転車や道路が凍て付くなかで、一人涙を流すこともある平野の熱意もさることながら、次第に荒涼としてくる雪原の壮大な風景の神秘的な映像に息を飲む。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NO MERCY", "ノー・マーシイ／非情の愛", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "相棒を惨殺されたロサンゼルスの刑事エディは、事件の鍵を握る謎の女を追って、ニューオリンズにやって来る。ところが彼女は、麻薬組織を牛耳る男の情婦。彼女を無理矢理連行しようとしたエディだったが、ボスの手下どもに追われ、二人は手錠につながれたまま泥沼を逃げるハメに。なんとか警察までたどり着くものの、地元警察は非協力的で、証拠不十分として彼女を釈放してしまうのだが……。初の刑事役をギアが体当たりで熱演。シャープで透明感のある映像と、凄絶なバイオレンス・シーンのコントラストも絶妙。映画ならではの演出のうま味が堪能できる異色娯楽作。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FINDING FORRESTER", "小説家を見つけたら", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 136, "プロバスケット選手を目指す高校生ジャマールは、仲間にそそのかされて遊び場の近くのアパートに住む老人の部屋に忍び込む。しかし、ほんのいたずら心だったこの出来事は、ジャマールの隠された文才を引き出す運命の出会いとなる。部屋にひきこもって暮らす偏屈な元作家と、彼に偶然出会った16歳の黒人少年との人種も年齢も超えた魂の触れ合いを描く感動の人間ドラマ。製作も兼ねたS・コネリーの圧倒的な存在感と、新人とは思えない生き生きとしたR・ブラウンの演技が秀逸。マイルス・デイヴィスのジャズにのって映し出されるニューヨークの下町ブロンクスや観光名所マディソン・スクエア・ガーデンなどの風景がみずみずしい。脚本のM・リッチは元ニュースキャスター。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COMMANDOS", "地獄の戦場 コマンドス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "第二次大戦下における北アフリカの砂漠を舞台にした戦争アクション映画。伊、独軍の重要拠点となっているオアシスの奪取を命じられたコマンド部隊が、飢えや渇きに耐えながら任務を遂行するさまを、ドラマティックに描いている。", "1969-01-01", "7,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POSITIVE I.D.", "見知らぬ女〈ひと〉でなく", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "レイプのショックから立ち直ることのできない人妻が、二重人格となり、家族を捨て、犯人を追い詰めていく姿を描く。独立プロ、低予算映画ながら、最後まで目の離せない良質のサスペンス・スリラーに仕上がっている。1987年度USフィルムフェスティバル参加作品。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("少林小子／KIDS　FROM　SHAOLIN", "少林寺２", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "本物の拳法家、武術家たちを総動員して少林拳の神髄を披露し、成功を収めた第１作の続編。“少林寺”と“武闘派”の双方の流派を受け継ぐファミリーが、一致協力して凶悪な盗賊団を打倒する。その中に様々な武術格闘技が展開される爽快な作品。", "1983-01-01", "6,8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("眠狂四郎 悪女狩り", "眠狂四郎 悪女狩り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "江戸城大奥の醜い権力争い、そして仲間を救うため大奥総取締のいうがままに狂四郎になりすまし、幕臣を切り捨てる隠れキリシタン。“人肌蜘蛛”の直後に入院した雷蔵の手術を経ての復帰作だが、病気のため殺陣は少ない。この年の７月に雷蔵は永眠し、シリーズは本作で終わる。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLACK COBRA", "ラスト・エマニュエル 異国の情事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "香港のエキゾチックなナイトクラブで、大蛇を裸身に絡め踊る謎の女エヴァ。彼女は大富豪ジュディスに倒錯した性の悦びを教えられるが、やがて美女ジュリーと女同士の情事に溺れるようなった。嫉妬に狂ったジュディスは毒蛇を使い……。エロティックなドラマを数多く手掛けるJ・ダマトによる官能作。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白雪先生と子供たち", "白雪先生と子供たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "現場教師の原案をもとに、原節子がヒューマニストの女教師に扮して人間教育のあり方を問う作品。東京都の小学校教師の雨宮加代子は、校庭の池の鯉が最近しきりに盗まれ、子供たちの間で野蛮な言葉や賭けごとが増えていることに頭を悩ませている。子供たちを煽動しているのは、浮浪者の常治だった……。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RED SHADOW 赤影", "RED SHADOW 赤影", "", "", "http://image.eiga.k-img.com/images/movie/1612/315x210.jpg?4658", 114, "TV化もされた横山光輝の漫画「仮面の忍者　赤影」を原作に「SF サムライ・フィクション」の中野裕之監督が忍者映画に挑戦。戦国大名、東郷に仕える忍者集団・影の一族の赤影、青影、飛鳥は、隣国の新兵器偵察へ。そこで隣国の忍者集団とのバトルが始まる。「マトリックス」のスティディカム担当、ロバート・アガニスが同役で参加。ピエール瀧、舞の海秀平、スティーブ・エトウ、篠原凉子、藤井フミヤ、アリーナ・カバエワらが出演。", "2001-08-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ある子守の詩", "ある子守の詩", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "「五木の子守歌」のメロディーにのせ、郷愁そそる九州の自然を背景に、一人の薄幸な少女子守の悲しみを綴る一編の映画詩。昭和初期、マサ子は貧乏のため小学校を６年でやめ、年季奉公に出されて子守となる。せっかくもらった金を弟と妹のアメと祖母のまんじゅうで使ってしまうシーンが、忘れがたい印象を残す長編児童劇映画。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Devil's Advocate", "ディアボロス　悪魔の扉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "A・パチーノとK・リーヴスの共演によるスリラー。悪魔の罠に落ちた、若き弁護士の姿をスリリングに描く。野心家の弁護士ケビンは才腕を買われ、法曹界の大物ミルトンの法律事務所へ転職。ミルトンは破格の報酬を与えて、彼に難事件を担当させる。だが、ケビンの周囲では不審な出来事が続発し、彼の妻は次第に狂気に憑かれていった。やがてケビンは母から、ミルトンの秘密を聞かされ……。ケビンに扮するリーヴスは追い詰められた者の焦燥感をリアルに体現。一方のパチーノは邪悪なミルトンを弁舌もなめらかに喜々として怪演し、演技派の存在感を見せつける。ビデオタイトルは「ディアボロス」。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SP 野望篇", "SP 野望篇", "", "", "http://image.eiga.k-img.com/images/movie/54892/315x210.jpg?1304", 97, "フジテレビ系で人気を博したTVドラマ「SP」の劇場版2部作の前編。直木賞作家の金城一紀が原案と脚本を担当し、「エイリアン2」「ターミネーター2」でVFXを手がけたロバート・スコタックがスーパバイザーとして参加。主演に「V6」の岡田准一、共演に堤真一、真木よう子、香川照之ほか。警視庁警護課第4係に所属する井上薫は、危険を察知する能力“シンクロ”や、一瞬で物事を映像として記憶する“フォトグラフィック・メモリー”などの特殊能力を持つ男。井上や同僚のSPらは、国家を揺るがす大規模テロの脅威に追い込まれながらも、陰謀の解明に奔走する。", "2010-10-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL GIOVAVE TOSCANINI", "トスカニーニ～愛と情熱の日々～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "20世紀初頭には天才指揮者として栄光の道を歩んでいた世界的な指揮者アルトゥーロ・トスカニーニの青春の日々を描いた作品。長年の夢だったミラノのスカラ座の採用試験を放棄したトスカニーニが、チェロ奏者として新天地リオに渡り、ひょんなことから18歳で指揮者としてデビューするまでを描く。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺の空だぜ！若大将", "俺の空だぜ！若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "東海建設の計画部員・田沼雄一は、会社側の土地買収の強引なやり口に耐えられず辞表を叩きつける。一方、伊豆の離れ島で節子に言い寄ろうとした青大将はマムシに噛まれ昏倒。雄一は血清を積んだ小型飛行機で伊豆へ急行する。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("住民が選択した町の福祉", "住民が選択した町の福祉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "「痴呆性老人の世界」「安心して老いるために」と老人問題に関する記録映画を撮り続けてきた羽田澄子が地域社会の福祉問題に目を向けたドキュメンタリー。秋田県の鷹巣町で“福祉のまちづくり”を掲げた若い町長が、住民に呼びかけて作ったワーキンググループの活動に支えられ、町の福祉を充実させていく様をとらえていく。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("天守物語", "天守物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "これまで何度も舞台で演じてきた得意の演目を取り上げた、坂東玉三郎の監督第３作。1994年の舞台版とほぼ同様のキャストと構成で、泉鏡花の幻想的な世界に迫っている。“白鷺城”と呼ばれる城の天守に魔物たちと暮らしている主人・富姫。下界を見下ろしながら優雅な毎日を送る彼女の許に、逃げた鷹を追って若侍、図書之介が迷い込んできた。彼の真摯な態度に好感を持った富姫は、二度三度と来訪を受けるうちに人間界に帰したくなくなり……。映画ならではのシークエンスを随所に織り込みながら、この世のものとは思えぬ恋模様を端整に映像化。グロテスクな描写にすら高貴さが漂い、まるでダニエル・シュミットの映画のような特異な時間感覚で全編をコーディネートしていく。玉三郎が「外科室」「夢の女」で試みてきた手法の数々が見事に結実した１本である。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLIC STORY", "フリック・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "フランス史上まれに見る凶悪犯と、彼を追う国家警察きっての敏腕刑事ボルニッシュとの息詰まるような戦いを、冷静な目で描いた実録小説の映画化。いつもは善良な役ばかりのトランティニャンが凶悪犯に扮し、冷徹ですご味のある辛口の演技を見せる。", "1975-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビー・バップ・ハイスクール 高校与太郎音頭", "ビー・バップ・ハイスクール 高校与太郎音頭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "今回はヒロシが警察にパクられ、トオルが孤軍奮闘するという異色作。二流のツッパリ北高・維新軍団につけ狙われたトオルが、彼らをいかにさばくかが見もの。今回から“五中の鬼姫”翔子役は、五十嵐いづみから立花理佐にバトンタッチ。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本一の裏切り男", "日本一の裏切り男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "早坂暁・佐々木守の名シナリオライターがコンビを組み、シリーズ６作目にして監督も須川栄三に代わった。特攻隊員として玉砕したはずの日の本太郎は日本を裏切って生き残り、戦後の荒廃から高度成長の時代へと、しぶとく裏切りを続けながらのし上がる。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("嗚呼!! 花の応援団", "嗚呼!! 花の応援団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "1976年に大ヒットし、その後シリーズ化された“嗚呼!! 花の応援団”の20年ぶりの再映画化。南河内大学応援団で、恒例のOB総見が行われることになるが、大事な団旗に穴があいていることがわかる。あわてて団員たちは修復費を稼ぐためにアルバイトを始めるのだが、そこはヤクザが経営していてアルバイト代も払ってくれないうえに、脅かされる始末。しかし、そこへ青田赤道が現れ、ヤクザを叩きのめすのだった……。薬痴寺先輩役に前シリーズ同様、なぎら健壱が出演しているほか、宮下順子も出演。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TCHIN　TCHIN", "アフタヌーンティーはベッドで", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "パリを舞台に、妻に逃げられた典型的イタリア男と、夫に去られたこれまた典型的英国婦人の二人が織り成すロマンティック・コメディ。イタリアの名優M・マストロヤンニとミュージカル・スター、J・アンドリュースの共演はまさに適役。イタリアファッション界の雄、ジャンニ・ヴェルサーチがすべての衣装を担当しているのも見逃せない。", "1991-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("修羅城秘聞 前・後編", "修羅城秘聞 前・後編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 192, "山手樹一郎の原作『桃太郎侍』を衣笠貞之助が前編、後編に分けて映画化。丸亀十万石藩主の子でありながら、双子の弟として生まれたために街に捨てられた桃太郎が、毒を盛られた兄の身代わりを頼まれて、我が父を悪から守るべく旅に出る。何も知らない伊賀一党は彼の後を追うが……。", "1952-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・駅前開運", "喜劇・駅前開運", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "かつてヤミ屋をやっていた孫作や次郎らは今では赤羽の商店街で店を営んでいる。街の真ん中にゴミ処理場ができるとあって共同で反対運動を始めたが、ことは簡単に運ばない。前作に続いて豊田四郎が監督を務めた。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Skate or Die", "スケート・オア・ダイ", "", "", "http://image.eiga.k-img.com/images/movie/54052/315x210.jpg?3838", 0, "", "2008-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Crowd Roars", "群集の喚呼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "カー・レーサーのジョーは大会で優勝し、故郷に凱旋した。ジョーを崇拝する弟のエディもレーサーを目指しており、ジョーはその熱意に負けて弟を本格的に育てることにする。常に危険と隣合せのジョーの身を案じていた愛人リーは、ジョーの目を覚ますため、友人のアンにエディを誘惑させるが……。大好きな自動車を題材に、使ってみたかったジェームズ・キャグニーを主演に迎え、ホークスが大いに撮影を楽しんだという一篇。", "1932-09-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hoax", "ザ・ホークス　ハワード・ヒューズを売った男", "", "", "http://image.eiga.k-img.com/images/movie/56002/315x210.jpg?1892", 116, "1971年ニューヨーク。売れない作家のクリフォード・アービングは、隠とん生活を送っていた大富豪ハワード・ヒューズの偽りの伝記を執筆し、大手出版社に売り込む。何度かのピンチを巧みな話術でかわしていったアービングだが、ウソにまみれたことで次第に精神に異常をきたしはじめ……。アービング自身による回顧録「ザ・ホークス　世界を騙した世紀の詐欺事件」を、ラッセ・ハルストレム監督、リチャード・ギア主演で映画化。", "2011-04-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("District 9", "第9地区", "", "", "http://image.eiga.k-img.com/images/movie/53212/315x210.jpg?1827", 111, "南アフリカ出身の新鋭ニール・ブロムカンプ監督が、05年製作の自作短編「Alive in Joburg」を長編として作り直したSFアクションドラマ。1982年、南アフリカ上空に突如UFOが飛来。政府は不気味な容姿をした異星人を難民として受け入れるが、やがて彼らの特別居住区「第9地区」はスラムと化す。2010年、難民のさらなる人口増加を懸念した超国家機関MNUは難民を「第10地区」に移動させる計画を立てる。製作は「ロード・オブ・ザ・リング」のピーター・ジャクソン。", "2010-04-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BARCELONA", "バルセロナの恋人たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "スペイン・バルセロナを舞台に、アメリカ人青年の恋を描いた青春ドラマ。冷戦が終わりを告げようとしていたバルセロナで、アメリカ企業のセールスマンとして働くテッドのもとに、いとこのフレッドがやって来る。ある日、二人は、モンセラートとマルタという女性と知り合い、４人はデートを重ねていく。そんな中、反米感情はピークに達し、モンセラートの同棲相手でジャーナリストのラモンの書いた根拠のない中傷記事により、フレッドはテロリストに狙われるはめになる。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不良少年", "不良少年", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "恵まれた家庭に生まれた17歳の青年が、人生に息苦しさを感じて家出。勤めたピンク・サロンのバックである暴力団の拳銃を盗み、警察官を射殺した時から、彼の転落が始まる。金田賢一がナイーブでありながら危険を秘めた青年を好演した青春映画。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パーマネント・ブルー 真夏の恋", "パーマネント・ブルー 真夏の恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「さらば夏の光よ」に続く山根成之監督、秋吉久美子主演の純愛ドラマ。脚本は、ジェームス三木と石森史郎。舞台は、瀬戸内海に面した四国の港町。そこに住む受験生は、ある日、砂浜で倒れていた女を発見、家に連れて帰り看護する。が、やがて彼女が警察に追われている過激派の女だとわかり……。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POUR　RIRE！", "男と女と男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "敏腕弁護士の妻アリスと専業主夫のニコラ。ある日、ニコラは彼女が浮気しているのではと疑いを抱いて尾行を続け、ついにスポーツ・カメラマンのガスパールという愛人がいることを突き止めた。そこでニコラは正体を偽ってガスパールに接近し、二人の仲を裂こうとする……。トリュフォー作品でおなじみのJ・P・レオー主演。常軌を逸したニコラの行動と愛の行方をブラック・ユーモアたっぷりに描く。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("One Voice", "ワンヴォイス　ハワイの心を歌にのせて", "", "", "http://image.eiga.k-img.com/images/movie/56742/315x210.jpg?4229", 86, "ハワイ、オアフ島で120年の歴史を持つ「カメハメハ・スクール」の生徒たちが、伝統の「ハワイアン・スクール・ソング・コンテスト」に挑む1年間を追ったドキュメンタリー。ハワイアンの血を引く子どもたちが通う同校では、毎年3月に合唱コンクールが開かれ、9～12年生の約2000人の生徒がハワイ語で歌う課題曲を競い合う。学年ごとにリーダーに選ばれた生徒たちとその家族を中心に、指導にあたる教師やミュージシャンたちのインタビューを交えながら、練習に励む1年間を追う。", "2011-08-06", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("風の絨毯", "風の絨毯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "母親の死を受け入れられない日本人少女と、純朴なイラン人少年の心と文化の交流を見つめた感動作。舞台となるイランの世界遺産、イスファハンと歴史ある飛騨高山の美しい街並みも印象深い。飛騨高山の町で暮らす少女、さくらは母親を失い悲しみに暮れていた。これを見た父親の誠は娘を元気づけるには、妻がデザインした高山に伝わる山車の幕となるペルシャ絨毯の完成を見届けさせるしかないと、イランへ娘を連れていく。", "2002-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PLATOON LEADER", "地獄からの生還 プラトーン・リーダー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "死体の山で埋まっていくベトナム戦争最前線。その小隊に送り込まれた米軍の若き少尉ジェフの目を通して、人間性を喪失させる戦争の悲惨さ、過酷さを描いたドキュメンタリー・タッチの問題作。「プラトーン」や「ハンバーガー・ヒル」の流れをくむ、ベトナム戦争“真相追及”映画。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獣の剣", "獣の剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「三匹の侍」でTV界から映画界に殴り込みをかけた五社英雄監督のアクション時代劇。家老の口車に乗せられて城代家老を斬り、さすらいの旅を続けるニヒルな浪人と、藩命を受けて天領の山中で砂金を盗掘している下級武士を主人公に、壮烈なチャンバラが迫力十分に描かれる。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SPIES LIKE US", "スパイ・ライク・アス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "ソ連の軍事基地に潜入した二人のアメリカ人スパイが巻き起こすドタバタ・コメディ。片や国務省、片や国防省の選り抜きの落ちこぼれ、エメットとオースティンは、捨て石として中央アジアのミサイル基地に送り出される。軍機構の愚かさを笑う薬味が効いている。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MILLIONAIRESS", "求むハズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "イギリスの劇作家バーナード・ショウの舞台劇『女百万長者』を映画化したロマンティック・コメディ。富豪の娘エピファニアが、インドの医師カビールと出会い、無欲で誠実な彼の人柄に引かれていく。ところが、カビールはエピファニアの魅力の虜になることを恐れて、彼女に対して消極的。互いに好意を抱きながらもすれ違ってばかりの男女の恋模様を、ソフィア・ローレンとピーター・セラーズの共演で描く。", "1960-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORT D’UN POURRI", "チェイサー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "フランス政財界の内幕を暴いたラフ・ヴァレのベストセラー小説の映画化。親友が不可解な死を遂げたことで、政界にたちこめる黒い霧の陰謀に巻き込まれていく実業家をドロンが熱演。自らも実業界に進出したドロンの華麗な半生を思わせる興味深い作品。", "1978-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AFGHAN　ALPHABET", "アフガン・アルファベット", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 46, "タリバン政権下のアフガニスタンに生きる人々を描いた「カンダハール」で世界的注目を浴びたイラン映画界の巨匠マフマルバフ監督。彼が、９・11N.Y.テロ事件の後、「カンダハール」のロケ地であるイラン国内のアフガン難民キャンプを訪れ、そこの子供たちの教育の実態をキャメラに収めたドキュメンタリー。彼がACEM(アフガン子供教育運動)というNGOを主催し、アフガン国内にまで教育の輪を広げていくきっかけとなった作品でもある。", "2002-01-01", "15", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PICASSO TRIGGER", "ピカソ・トリガー 殺しのコード・ネーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "この世で最も残虐な魚＝ピカソ・トリガーをコードネームに持つ国際犯罪組織の首領サラザールが、何者かの凶弾に倒れた。しかしそれは、連邦捜査エージェントに仕掛けられた巧妙な罠であった。パリ、ダラス、そしてハワイで壮絶なバトルが今始まろうとしている。プレイメイト７人が勢ぞろいの、エロティック・アクション。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Nervous Ticks", "ノンストップ・クレイジー 大迷惑な人々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "不倫相手と駆け落ち旅行を企てた男が、出発までの90分に様々な災難に遭う姿をリアルタイムで描いたドタバタ・コメディ。飛行場の手荷物係ヨークは、不倫中の人妻ナンシーとの愛を成就させようとリオへの旅行を計画する。ところが飛行場では上司や同僚につかまり、お客の荷物探しに回される始末。果てはその荷物を狙う奴らがヨークを追いかけてくる。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("麗人", "麗人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "温順清楚な女学生・鞆子は、信頼していた男友だち・浅野に処女を奪われ妊娠してしまう。鞆子は浅野と結婚することにしか解決を見出せないが、浅野にはすでに婚約者が……。この時期の島津ならではのメロドラマ大作で、原作者・佐藤紅緑の弟、サトウ・ハチローが挿入歌の歌詞を担当している。", "1930-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("がんばれ！若大将", "がんばれ！若大将", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "草刈正雄が加山雄三、大矢茂に続いて３代目“若大将”を襲名。とんかつ屋の老舗・梅長の一人息子で京南大学のアメリカン・フットボール部員の田沼雄一は、ライバル・青大将をしりぞけ、恋人・鮎子を獲得。対抗戦でも大活躍し京南大を優勝に導く。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AMICO，　STAMMI　LONTANO　ALMENO　UN　PALMO", "新・さすらいの用心棒／ベン＆チャーリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "イタリア製の西部劇、マカロニ・ウエスタンの一作。中でも日本での人気が高かったG・ジェンマ主演作。長身の二枚目俳優が華麗なフットワークでガン・ファイトを見せる。ギャンブルで生計を立てている二人組の男たちだが、負けが込んでしまった挙げ句、銀行強盗をはたらく。騎馬隊に追われながら果てしない逃亡劇を繰り広げる。", "1972-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ノーライフキング", "ノーライフキング", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "プロデューサー集団“アルゴ・プロジェクト”製作映画の第２作で、CM界出身の、市川準の監督第３作。ファミコン・ブームやパソコン通信によって、独自のネットワークを持ち、終末感を背負いながらも生き続けなくてはならない、現代の子供たちの思いを描いている。大ヒットしたファミコンソフト“ライフキングの伝説”、その中の“ライフキング４”には、呪いがかかっていて、最後まで解けなくては死んでしまうという噂が広がる。子供たちが、次々にソフトを手にすることをやめるなか、一人、まことだけがソフトに挑戦する……。セリフを抑え、無言の中でどこからともなく襲ってくる重圧に耐えるまこと。希望を見出せない中で踏み止まり、ファミコンという虚構ではなく、自分が生きる街とリアルに出会い直面した時、彼の中の意識が変わる。子供たちに託した今の大人たちへの、市川監督のメッセージとも言える作品である。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Twelve Monkeys", "12モンキーズ", "", "", "http://ecx.images-amazon.com/images/I/51KMH22ZTML._SL210_.jpg", 130, "謎の細菌に侵された近未来から、原因究明のために1996年にタイムトラベルした男の活躍を描いたSFサスペンス。人類の99パーセントが謎のウイルスによって滅亡した近未来。その原因を探るために科学者グループは、ウイルスが蔓延し始めた1996年に囚人のコールを送り込む。原因究明のカギとなる言葉“12モンキーズ”を教えられタイムスリップしたコールは、数回の失敗の末、1996年に到着。以前出会ったレイリー博士に協力を求め調査を進めていったコールは、精神病院に収容された時、彼の世話役だった患者ジェフリーが事件のカギを握る人物であることを突き止める。B・ピットがジェフリー役の怪演でゴールデン・グローブ助演男優賞を受賞した。", "1996-07-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZAZIE", "ZAZIE", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "1981年に撮った８ミリ「教訓 I」でPFFに入選した利重剛。その彼が初めて作った35ミリ映画で、“アミューズ・10ムービーズ”の第２作。元ロック・ミュージシャンの“ザジ”は、伝説的な存在になっている。彼は東京のベイエリアに住み、就職した会社も辞め、もう一度自分を見つめ直そうとしていた。彼の周りには、ミュージシャンとしてザジが復活することを望む友人、羨望するファン、彼を愛す女性などが集まって来る。ザジを中心とした青春群像の行く末は……。ザジに扮した“横道坊主”のボーカル、中村義人が好演。利重監督はビデオ・カメラや手持ちカメラなど、自主映画で培った手法をふんだんに使い、今を生きる若者の心の痛みを活写している。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VISKNINGAR OCH ROP", "叫びとささやき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "巨匠ベルイマン監督が、ストリンドベルイの戯曲を思わせる息詰まるような物語を、強烈な色彩によって描いた秀作。19世紀末の、ある大邸宅を舞台に、病に苦しむ娘とその家族を中心に、人間の本質に迫るドラマが展開する。", "1972-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE REWARD", "メキシコで死ね", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "冒険家スコットは、ひょんなことからお尋ね者フランクの隠れ家を見つける。村に借金のある彼は、警察署長のカーバジャルに賞金を山分けにするなら隠れ家に案内しようと申し出た。同意したカーバジャルは巡査部長のロペスらを従えフランクのもとへ向かい、逮捕する。だがロペスも、彼が賞金付きのお尋ね者と知って、賞金の山分けを主張。そして彼らは欲望のために自滅していく……。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("血とダイヤモンド", "血とダイヤモンド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "３億円を超えるダイヤの原石を４人組のギャングが強奪した。その一人、小柴は脇腹に銃弾を受け、一味は医者を探しながら逃走するハメに。ダイヤを以前から狙っていた別のギャング団や、保険会社からの報酬が目当ての悪徳私立探偵も追っ手に加わる。おまけに、小柴がダイヤを独占しようと企んで……。登場人物、みな悪漢という乾いたアクション。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MILANO TREMA: LA POLIZIA VOULE GIUSTIZA", "イタリアン・コネクション", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "護送中の凶悪犯を射殺した責任を問われ、刑事をやめた男が一匹狼になって犯罪組織と闘う。マカロニ・アクションらしく、なかなか派手に見せる一方、法と正義の矛盾を突いたり、警察の腐敗を内部告発しているところも魅力になっている。", "1973-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UN MALEDETTO IMBROGLIO", "刑事", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "ローマの古いアパートに強盗が入り、警部イングラバロが捜査。女中アッスンタとその婚約者が怪しまれるなか、隣室の婦人が惨殺される。犯人を探す警部の目を通してローマの生活が細かく描写される。ジェルミ監督らしい小市民劇だ。", "1959-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　忍風戦隊ハリケンジャー　シュシュッと THE MOVIE", "劇場版　忍風戦隊ハリケンジャー　シュシュッと THE MOVIE", "", "", "http://ecx.images-amazon.com/images/I/21BM7EQADVL._SL210_.jpg", 30, "子どもたちから絶大な人気を集めるスーパー戦隊シリーズ「忍風戦隊ハリケンジャー」の劇場版。アストラム星のプリンセス、ライーナを追って地球に降りたった凶悪宇宙忍猿ヒザールとブリザール。なんとか彼らから逃げだしたライーナは、奔放な振る舞いでハリケンジャーたちを翻弄する。ついに捕まってしまったライーナを救うため、ゴウライジャーやシュリケンジャーと共に敵に立ちむかうハリケンジャーだったが……。", "2002-08-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OTTO E MEZZO", "フェリーニの８1/2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "上流社会の退廃を抽象的に描いて評判になった「甘い生活」に続き、フェリーニ監督が発表した自伝的大作。映画監督グイドは、ある日自分の体が空中を落ちていく夢を見る。現実の日常の中で、精神的にも肉体的にも疲れきっていた彼は、療養と称して温泉に行くが、そこでも生活や仕事から逃れきれず、温泉で余生を過ごす老人たちの中に自分がいるという幻覚を見始める。別れる勇気がないだけの妻にも、肉体だけしか愛せない愛人にもウンザリしている彼にとって、唯一の救いは、心の中に現れる娼婦のような聖女クラウディアだった。確かな現実をつかみきれずにあがくグイドは、少年の頃の思い出に浸るうち、長く忘れていたあるものを思い出す……。夢と幻想と現実を平然と同居させながら、ありのままの自分の姿を映画に託した作品。ラスト、映画の仲間たちと踊り続ける主人公が去ったあと、一人残って笛を吹く少年グイドの姿が感動的。モスクワ映画祭でグランプリを受賞した。", "1963-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海女の戦慄", "海女の戦慄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "新東宝が、時のグラマー女優と称された前田通子ら女優陣の魅力を売り物にした“海女”シリーズの一編。ミス海女に選ばれて上京したチエとユキが行方不明になり、まもなくユキの溺死体が発見された。チエの姉ヨシは、風来坊・俊介の力を借りてチエの行方を追うが、やがてダイヤモンドをめぐる陰謀が一行の行く手を阻む……。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VOYAGE OF THE DAMNED", "さすらいの航海", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 182, "1935年５月、外国移住を望む900人余りのユダヤ難民を乗せたドイツ客船がキューバに向けて出航。だが、ナチスの仕組んだ計画のためキューバにもアメリカにも乗り入れを拒まれ、大西洋を逆行するはめに……。ドキュメンタリー・タッチの感動作。", "1976-01-01", "3,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rogue", "マンイーター", "", "", "http://image.eiga.k-img.com/images/movie/57772/315x210.jpg?2737", 92, "「アバター」（2009）のサム・ワーシントンと「アリス・イン・ワンダーランド」（10）のミア・ワシコウスカの共演で、06年に製作されたパニックアドベンチャー。大自然に囲まれたオーストラリアの世界遺産・カカドゥ国立公園で、観光客を乗せたリバークルーズ船が水中から現れた巨大な何かに攻撃され沈没。川に浮かぶ小島にたどり着いた乗客たちは対岸を目指して脱出を図るが、水中の巨大な影が再びうごめき……。ワーシントン、ワシコウスカのほか、巨大生物と対峙する旅行ジャーナリスト役でマイケル・バルタン、現地ガイド役でラダ・ミッチェルらが共演。", "2012-04-14", "1,11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRANCE", "トランス・愛の晩餐", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "ロック界のスーパースター“R”に夢中になった少女。運良く彼に処女を捧げることができたが、急に冷たくされて彼を殺してしまう。彼女はRを永遠に所有しようとその死体を食べるのだった……。異常なファン心理を描き、“究極の愛”をうたった怪作。ビデオ発売時タイトルは「マーダー・ケース・ファイル ドイツ人肉食事件」。", "1982-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sketches of Frank Gehry", "スケッチ・オブ・フランク・ゲーリー", "", "", "http://image.eiga.k-img.com/images/movie/1932/315x210.jpg?1850", 84, "", "2007-06-02", "7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALGIERS", "カスバの恋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "アルジェリアのカスバに逃げ込んだ犯罪者ペペが、情婦の密告で破滅の道に……。名作「望郷」をアメリカでリメイクした作品。オリジナルの原題“ペペ・ル・モコ”のモコとは、もともとアルジェリア語でアルジェリア人のことを指していたのだが、このリメイク版の原題はそのものズバリである。", "1938-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大怪獣ガメラ", "大怪獣ガメラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "東宝に対抗して大映が手掛けた怪獣映画“ガメラ”シリーズの第１作。原爆実験のショックで、北極の地下で冬眠していたガメラが目覚め、日本に上陸して大暴れするが、最後はロケットで火星に飛ばされる。大映独特の斬新な特撮シーンが見もの。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bruiser", "URAMI 怨み", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ホラー作家の第一人者、ジョージ・A・ロメロ8年ぶりの新作。キャストには「ロック、ストック＆トゥー・スモーキング・バレルズ」のジェイソン・フレミング、「ダンサー・イン・ザ・ダーク」のピーター・ストーメアといった実力派を起用した。", "2001-10-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ドラゴンボールZ　超戦士撃破！！ 勝つのはオレだ", "ドラゴンボールZ　超戦士撃破！！ 勝つのはオレだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 47, "“'94夏東映アニメフェア”の一本として公開された、アドベンチャー・アクションのシリーズ第14弾。初期シリーズを引き継いだミラクルな激闘は、本作でも健在だ。ジャガー男爵は屋敷で科学者を雇い、バイオ戦士を製造していた。そこへ孫悟天たちが訪れると、かつての強敵ブロリーが最強戦士として蘇っていた。悟天たちはチームワークを発揮して、ブロリーに挑むが……。DVDは『DRAGON BOX THE MOVIES』に収録。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おくさまは18歳・新婚教室", "おくさまは18歳・新婚教室", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "原作は『週刊マーガレット』に連載された本村三四子の漫画『おくさまは18歳』で、それをもとにしたTVの同名連続ドラマが人気を得たために、映画化された作品である。主演はTV版と同じ岡崎友紀と石立鉄男で、岡崎としては初の主演作品。同じ高校の先生と女生徒は実は夫婦だったが、そのことは秘密にしなくてはならなかった、というユーモア青春映画。助監督は長谷川和彦。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blind", "視覚障害", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Shutter Island", "シャッターアイランド", "", "", "http://image.eiga.k-img.com/images/movie/53192/315x210.jpg?3413", 138, "「ミスティック・リバー」のデニス・ルヘイン原作の同名小説をマーティン・スコセッシ監督＆レオナルド・ディカプリオ主演で映画化。1954年、失踪した女性患者の謎を探るためにボストン沖の孤島に建つ犯罪者用精神病院を訪れた米連邦保安官テディ・ダニエルズ（ディカプリオ）に次々と不可解な出来事が起こる。脚色は「アレキサンダー」「ナイト・ウォッチ／NOCHINOI DOZOR」のレータ・カログリディス。", "2010-04-09", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Play", "プレイ", "", "", "http://image.eiga.k-img.com/images/movie/57362/315x210.jpg?4109", 118, "2006年から08年にかけてスウェーデンのヨーテボリで12歳から14歳の少年グループが起こしたかつあげ事件を映画化した社会派ドラマ。白人の子どもたちを相手にかつあげ行為を繰り返す黒人の少年たちの姿をリアリズムに徹して描き、人が抱くステレオタイプな恐怖心や偏見をえぐりだしていく。2011年・第24回東京国際映画祭で最優秀監督賞を受賞した。", "2011-01-01", "14,18,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Broken Flowers", "ブロークン・フラワーズ", "", "", "http://image.eiga.k-img.com/images/movie/1312/315x210.jpg?3053", 106, "「ストレンジャー・ザン・パラダイス」「デッドマン」のジム・ジャームッシュ監督、6年ぶりの新作長編。ビル・マーレイ演じる中年男性の元に、ある日届いた手紙には「あなたの子供がもうすぐ19歳になる」と書かれていた。彼は20年前の交際相手を訪ねて回るが……。05年のカンヌ国際映画祭で審査員特別グランプリを受賞し、ジャームッシュ復活を印象づけた。", "2006-04-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さや侍", "さや侍", "", "", "http://image.eiga.k-img.com/images/movie/55932/315x210.jpg?1408", 103, "お笑いコンビ「ダウンタウン」の松本人志が、自身初の時代劇に挑む監督作第3作。刀を捨てた侍・野見勘十郎と、反発する娘たえの流浪の旅、脱藩した罪で捕らわれた勘十郎が、変わり者の殿様にくだされた、成功すれば無罪放免という“30日の業”に挑む姿を描くオリジナルストーリー。かつて松本が司会を務めたTV番組に出演したことのあるバーテンダーで、俳優経験皆無の素人・野見隆明を主演に抜てきした。", "2011-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE RESCUERS DOWN UNDER", "ビアンカの大冒険　ゴールデン・イーグルを救え！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 77, "ディズニー・アニメ「ビアンカの大冒険」の続編。ネズミたちの国際緊急救助協会にオーストラリアから、密猟者マックリーチが数少ない大ワシ、マラフーテを狙っているというSOSが入る。正義感の強いネズミのビアンカとバーナードは現地に急行。だが、マックリーチ一味は手強く、ビアンカたちは苦戦を強いられる。果たしてビアンカは大ワシを助けることができるのか？", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("うたって　てあそび　アンパンマンともりのたから ", "うたって　てあそび　アンパンマンともりのたから ", "", "", "http://image.eiga.k-img.com/images/movie/56882/315x210.jpg?1788", 20, "やなせたかし原作の人気アニメーション劇場版第23作「それいけ！アンパンマン　すくえ！ココリンと奇跡の星」と同時上映の短編。", "2011-07-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NAVAJO　JOE", "さすらいのガンマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "悪党ダンカンと無法者集団が、インディアンの集落を襲うという事件を引き起こす。指名手配されたダンカンは保安官を殺害し、前科者の医師と組んで大金を積んだ列車を襲撃。一方、インディアンの若者ジョーは殺された一族の恨みを晴らすため、ダンカンの手下を抹殺していく。スターになる前の若きB・レイノルズがインディアンの青年に扮し、主人公の壮絶な復讐を熱演するマカロニ・ウエスタン。", "1966-01-01", "4,9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アリーテ姫", "アリーテ姫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "「魔女の宅急便」で演出補を務めた片渕須直が、劇場用長編アニメ監督デビューを飾ったファンタジー。城の高い塔のてっぺんの小部屋に、閉じこもった生活を強いられているアリーテ姫。彼女の意志とは関係なく、城内では婿選びが連日繰り広げられていた。やがて、魔法使いの生き残りと称する男が現れ、重臣たちは彼を姫の婿に決めてしまう。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hand That Rocks the Cradle", "ゆりかごを揺らす手", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "　優しい夫、そして娘とともに暮らすクレアは2人目の子供を身ごもる。しかし彼女が通っていた産科医のわいせつ行為が明るみに。これが騒ぎとなり、やがて産科医は自殺。彼の妻で妊娠中だったペートンはショックで流産したあげく、二度と子供を埋めない体に。その後、ペートンは自らの正体を隠したまま、バーテル家で子守として働き始める。復讐に燃える彼女の罠はバーテル家を崩壊へ導いていく……。幸せを奪われた女の壮絶な復讐、そしてその恐怖に立ち向かう母親を描くスリラー。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE PACHA", "パリ大捜査網", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "同僚の死に疑いを持った警察署長は謎を追っていくうちに、とある組織にぶつかる。この警察署長の大活躍によって、次々と謎が解き明かされて、やがて犯人は逮捕されるのだった。名優ギャバンが徹底したヒーローを演じるアクション映画。", "1968-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dream Lover", "ドリーム・ラバー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "フルート奏者のキャシーは、アパートに侵入してきた変質者を刺し殺して以来、侵入者の夢ばかり見て、不眠症になってしまう。キャシーは治療を受けたハンセン博士の実験に協力することになるが……。アイドル・スター、マクニコル主演によるサイコ・サスペンス。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("愛と誠", "愛と誠", "", "", "http://ecx.images-amazon.com/images/I/61aHaLPxqzL._SL210_.jpg", 89, "大ヒット・マンガ『愛と誠』の実写版。主人公・大賀誠を当時人気の歌手・西城秀樹が演じ興行的にも成功した。野性的な誠と、可憐なブルジョア娘・早乙女愛との刹那的な純愛を描いている。女優・早乙女愛はこの作品のヒロイン役でデビュー。本編のあとシリーズ化され、「続・愛と誠」「愛と誠・完結編」が作られている。DVDは「愛と誠 DVD-BOX」に収録。", "1974-07-13", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("危し！伊達六十二万石", "危し！伊達六十二万石", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "乱行を続ける奥州仙台六十二万石の城主伊達綱宗の跡目を狙い、分家と江戸家老らが入り乱れて展開するいわゆる“伊達騒動”を取り上げた時代劇。不行跡によって隠居を命じられた綱宗に代わって、幼少の亀千代が家督を相続する。だが江戸家老・原田甲斐が分家・伊達兵部の子を当主に迎えるべく画策する……。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("おのぼり物語", "おのぼり物語", "", "", "http://image.eiga.k-img.com/images/movie/55452/315x210.jpg?2395", 114, "カラスヤサトシの自伝的4コマ漫画「おのぼり物語」を実写映画化。ミュージカル界で活躍する井上芳雄が映画初主演を務め、八嶋智人、哀川翔らが脇を固める。売れない漫画家・カラスヤサトシこと片桐聰は、ある日ふと思い立って大阪から上京する。個性的な人々が集う古びたアパート「松風荘」で新しい生活を始めた聰だが、連載していた唯一の雑誌が突然休刊になってしまい、経済的に追い詰められてしまう。そんなとき、カメラマンを目指す高校の同級生・由美子と再会するが、彼女も夢と現実の間で葛藤（かっとう）していた。", "2010-07-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL GIARDINO DELL’EDEN", "エデンの園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "金持ちの娘アレッサンドラと貧しい家の育ちのミケーレは互いに愛し合っていたが、身分の差が原因でアレッサンドラの家族から交際を禁じられる。やがて二人の夢は悲劇的な結末を迎える。増村保造監督の日・伊合作映画。", "1980-01-01", "76,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ЛЕБЕДИНОЕ　ОЗЕЛО", "白鳥の湖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "バレエの中で最もポピュラーなチャイコフスキーの『白鳥の湖』を映画化した作品。ソビエトが世界に誇ったバレリーナ、M・プリセツカヤが踊る“白鳥”を、ボリショイ劇場やスタジオで撮影し、劇場観覧さながらの感動を再現している。", "1959-01-01", "12", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IN THE BLEAK MIDWINTER", "世にも憂鬱なハムレットたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "貧乏役者のジョーは、教会を救うという憂うつな使命のためクリスマスに『ハムレット』を上演することを決意する。オーディションで集まった役者たちもそれぞれに憂うつを抱えていて、彼らの憂うつが、演じるキャラクターにオーバーラップし使命遂行を困難にしていく。ちょうど『ハムレット』と同じように……。果たして彼らは憂うつを克服することができるのか？「ヘンリー五世」を始め数々のシェイクスピア作品の監督や主演を手掛け高い評価を得てきたK・ブラナーが、そのシェイクスピアへの愛を形を変えて見せた秀作コミカル・ドラマ。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Zyciorys", "ある党員の履歴書", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 47, "「トリコロール」三部作で知られるポーランドの巨匠クシシュトフ・キエシロフスキー監督初期の作品。共産党員の男は、彼の党除名をめぐる委員会に出席。男は必死に除名不服を唱えるのだが……。実在した委員会や元共産党員を出演させ、当時の共産主義体制下にあったポーランドを再現。多くのドキュメンタリーを手がけてきたキエシロフスキー監督による、半ドキュメンタリー。", "", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LAST REMAKE OF BEAU GESTE", "Oh！外人部隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "1926年にコールマン主演でスクリーンに登場して以来、何度も映画化されている「ボー・ジェスト」のリメイク版。アフリカの砂漠を舞台に、フランス外人部隊と原住民との闘いを描いたP・C・レンの冒険小説をもとにフェルドマンがパロディー仕掛けで作り上げた珍説「ボー・ジェスト」。1939年版のG・クーパーが合成場面で登場したりと、にぎやかさ満載。", "1977-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("北京的西瓜", "北京的西瓜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 135, "船橋市郊外のとある八百屋を舞台に、中国人留学生のためにすべての生活を捧げた八百屋夫婦と留学生たちとの交流を描いていく。撮影中に天安門事件が発生し撮影予定変更を強いられたが、それを乗り越えて完成された。劇中の37秒の空白場面は天安門事件の起きた日の1989年６月４日の数字の和からきている。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花笠若衆", "花笠若衆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "双生児を忌む風習から故郷・但馬の扇山を追われ、江戸の侠客のもとで育てられた男姿の吉三（実は扇山五万三千石の主・牧野内膳正の息女雪姫）が、お家乗っ取りを企てる一味により巧みに罠にはめられていく牧野家を救うため、故郷に舞い戻る。美空ひばりが一人二役で、見事なタンカを切った娯楽時代劇。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("INNOCENT BLOOD", "イノセント・ブラッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "A・パリローがキュートなヴァンパイアに扮したコミカル・ホラー。女吸血鬼のマリーは、マフィアのマルチェリの血を賞味するが、彼を殺し損ねてしまった。マルチェリがヴァンパイアになる前に手を打とうとする彼女は、ハンサムな刑事ジョーとともに彼を捜し出そうとするが……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Wiz", "ウィズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 134, "　クリスマスの夜、突然発生した雪の竜巻に飲み込まれ、ドロシーは魔法の国に迷い込む。故郷に帰るためには、エメラルド・シティに住むという魔法使いウィズに会わなくてはならない。ドロシーは道中で出会ったカカシ、ブリキ男、ライオンと一緒に不思議な旅をすることに……。映画「オズの魔法使い」をベースに、1974年にブロードウェイで上演され７つのトニー賞を獲得した同名ミュージカルを映画化。オール黒人キャストで繰り広げられる踊りと熱唱が感動を呼ぶ。", "1978-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BOYS IN THE ISLAND", "ボーイズ・イン・ジ・アイランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "“ロック・アラウンド・ザ・クロック”が流れる、1955年のオーストラリアに生きる青年４人の姿を描く青春映画。タスマニアに住むフランク、ルイ、ジェイク、シェーンの４人は、いつも退屈な島から出たがっていた。シェーン以外の３人は、憧れのメルボルンへ渡り、謎の美女キーバと出会う。彼女に操られるまま、彼らは犯罪に手を染めていくが……。", "1989-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("No Way Home", "孤独の絆", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "兄弟の葛藤をテーマにした辛口の人間ドラマ。兄の罪をかぶって服役していた、知能障害の青年ジョーイが出所。兄のトミーは、身の置き場のないジョーイを温かく迎え、最初は反発していたトミーの妻も次第にジョーイと打ち解けていく。だが、いまだに悪事に手を出しているトミーは、再び弟を麻薬密売事件へと巻き込み……。頭は弱いが誠実さを失わないジョーイを、演技派T・ロスが好演。ビデオタイトルは「ノー・ウェイ・ホーム／孤独の絆」。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("涙の季節", "涙の季節", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "何をやっても失敗ばかりのTV局報道部員。ある日、上司に怒られた彼は、やけになって家の古いテレビを蹴飛ばす。するとその画面に、死んだはずの祖父が現れ、翌日の出来事を話し始めた。それからというもの、彼はスクープの連続で、にわかに脚光を浴び……。当時アイドル的人気のピンキーとキラーズの同名ヒット曲にインスパイアーされたラブ・ファンタジー。同グループの今陽子も出演した。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORGAN−A SUITABLE CASE FOR TREATMENT", "モーガン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "花や動物、そして子供を愛する心優しい男モーガン。だが彼は、危険を感じると狂暴な男に変身してしまう欠点を持っていた……。D・メーサーの戯曲『モーガン —彼向きの治療法』の映画化。", "1966-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("YOU CAN’T WIN ’EM ALL", "アドベンチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "オスマントルコ崩壊前夜、黄金と美女をめぐって展開される二人の男のアクション・アドベンチャー。エーゲ海を航行中に知り合ったアダムとジョッシュは、スミルナの港へ赴く。ジョッシュは宝石を狙うが、アダムの真の目的は、父親の持ち船である“イスラムの星”号を、オスマン帝国から取り戻すことにあった。だが船を見つけ、いざ出港という時になって、彼らは市民反乱軍に逮捕される。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEKYLL ＆ HYDE", "ファントム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "スティーヴンソンの『ジキル博士とハイド氏』は恐怖映画の原点として何度か映画化されているが、本作は、サイコ・スリラー的要素に加え、視覚的恐怖が見もの。一瞬にして肉体が変身していく特殊メイキャップ、恐ろしくグロテスクなクリーチャーと化したハイドなど、SFXの技術を駆使した作品となっている。ジキル役には善と悪、両方の顔を表現できる名優M・ケイン。", "1989-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("肉体の門", "肉体の門", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "田村泰次郎のベストセラー小説の、鈴木清順監督、野川由美子主演による２度目の映画化。敗戦直後の東京を舞台にたくましく生き残るボルネオ・マヤら５人の娼婦が、野性味あふれる復員兵の伊吹新太郎をめぐって欲望と嫉妬に狂う。鈴木清順が野川由美子主演で監督した３部作「春婦伝」「河内カルメン」の第１作。野川由美子は主人公の娼婦ボルネオ・マヤに扮して全裸のからみや、娼婦たちのすさまじいリンチ・シーンを文字通り体当たりで熱演した。清順と名コンビの木村威夫による様式的な美術も効果を上げた。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("蛍 II 赤い傷痕", "蛍 II 赤い傷痕", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "横浜を舞台にした青春ヤクザ映画のシリーズ第２作。カタギになろうとチケットショップで働いていた謙次は、店に換金に来た中国語を話す麻子と出会う。次第に麻子に心惹かれていく謙次と幼なじみの秀夫だが、麻子はチャイナマフィア“リュウマン”のボスに仕える女だったのだ……。ヤクザから足を洗おうとしながらも、友情と惚れた女を救うために犯罪組織との戦いに挑む主人公を的場浩司が好演。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MURDER BY DEATH", "名探偵登場", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "大富豪トゥエイン邸に招かれた世界の名探偵たちに仕掛けられたワナとは？ 謎の殺人事件をめぐって、我らの名探偵諸氏があの手この手で大活躍。喜劇作家サイモンが初めて探偵ものに挑戦した書き下ろし脚本で、随所に織り込まれた有名探偵小説のパロディが、気が利いていて楽しい。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRAPEZE", "空中ぶらんこ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "B・ランカスターが製作にも参加した、サーカス団を舞台にしたラブ・ロマンス。ヨーロッパ最大のサーカス団にやってきた若いブランコ芸人ディーノは、かつての花形芸人マイクとアクロバット芸人ローラとトリオを組んで成功を収める。ディーノはローラに惚れていたが、彼女はマイクに気があって……。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本橋", "日本橋", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "大正初期の日本橋を舞台に、新旧二派の芸者と、周辺の男たちの色模様を描いた鏡花文学を、溝口に続いて市川が映画化。しかし、文芸作品を、いい意味での通俗性とともに、独特の軽妙洒脱な文体の中に映像化する市川節は、少々大胆さに欠けた。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HOSTILE　ENVIRONMENT", "ネイビー・ストーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "世界中の海が有毒廃棄物に汚染され、水が貴重な資源となっている近未来。女リーダー、ミナが率いる支配階級は水の浄化システムを独占、浄化装置を搭載した巨大な戦艦を基地として世界を支配していた。彼女の独裁態勢を打破するため、反乱軍は特殊コマンド技術を身につけたマイクに浄化システムの奪取と戦艦爆破を指令する……。巨大戦艦の中で繰り広げられる壮絶な戦いを描いた軍事アクション。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("帆綱は唄う 海の純情", "帆綱は唄う 海の純情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 48, "のちの鈴木“清順”による、１作目に続く歌謡映画。元歌は春日八郎の『海の純情』。捕鯨船の砲手助手の八郎はいい男。芸者や同僚の恋人にまでモテまくる。引退間近の老砲手兼艦長の花道を飾ろうと奮闘努力し、最後にはその娘と結ばれる。主人公名は春海八郎で、春日人気におんぶした企画。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PONG", "桑の葉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "1920年代、日本軍の支配下にあった韓国の小村を舞台に、そこに生きる女のしたたかさをコミカルに、そしてエロチシズムを織り交ぜて描く。アンヒョプは村一番の美人だが、夫は賭博好きで家計は火の車。彼女は、村の男たちと関係を持ち、その貢ぎ物によって家計を支えようとするが……。イ・ミスクの楚々としたヌードも見どころ。", "1985-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("N.Y. Undercover Cop", "N.Y. Undercover Cop", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "東映Vシネマのアメリカ・シリーズ“Vアメリカ”の一編を劇場公開したもの。ニューヨークのロウワー・イーストサイドに、潜入捜査した日本人、村上年男（通称トシ）を主人公に、彼が、拳銃密売組織のボス、ミスター・Cと戦う様を描く。トシと友情で結ばれるギャングのボス、ホーク役でスティーヴ・マックィーンの息子、チャドが出演。", "1993-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE COMANCHEROS", "コマンチェロ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "「カサブランカ」でアカデミー作品賞、監督賞を受賞したM・カーティスによる西部劇。インディアンの暴虐が頻繁に起こるテキサスの開拓村。テキサス警備隊の鬼大尉カッターは、その原因が不良白人の強盗団コマンチェロにあるとにらむ。彼らはコマンチ族に武器を密売して私腹を肥やしているのだ。ひょんなことから流れ者のリグレットという男と知り合ったカッターは二人で協力してコマンチェロの巣窟へ乗り込むが……。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ATOMIK　CIRCUS", "エイリアン VS ヴァネッサ・パラディ", "", "", "http://ecx.images-amazon.com/images/I/51C1JTD2ZBL._SL210_.jpg", 91, "フランスの歌姫ヴァネッサ・パラディが、5年ぶりに銀幕復帰を果たしたSFアクション。人間たちを襲う残虐なエイリアンの恐怖を、ハリウッド大作に匹敵する迫力映像で活写する。劇中で突然、ヴァネッサが歌い出すなど大胆不敵な邦題に引けを取らない、奇想天外な展開に圧倒される怪作だ。とある田舎町の歌手コンチャは、突然街にやってきたジェームスと出会い、恋に落ちる。そんなとき不穏な気配が街全体を覆いはじめ……。", "2005-10-08", "2,7,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Aeon Flux", "イーオン・フラックス", "", "", "http://image.eiga.k-img.com/images/movie/1242/315x210.jpg?4597", 93, "90年代にMTVで放送された人気アニメを「モンスター」のアカデミー女優シャーリーズ・セロン主演で映画化。西暦2415年、外界から壁で隔離された都市ブレーニャには病気も飢えもなかったが、独裁政治により思想の自由は失われていた。反政府組織モニカンの一員、イーオン・フラックスは、組織の命令で独裁者トレバーの命を狙う。だが、この都市には恐るべき秘密が隠されていた。監督は「ガール・ファイト」のカリン・クサマ。", "2006-03-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("犯す！", "犯す！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "図書館に勤める純真な娘が、ある日、自宅マンションのエレベーターの中で強姦され、性に開眼。しかし強姦されなければ燃えられない彼女は、その強姦魔を捜し出し、これまでにない快感を味わうというアクション映画タッチで描かれたロマンポルノ。強姦魔を演じるのは当時、強姦役でならした蟹江敬三。", "1976-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("埋もれ木", "埋もれ木", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「眠る男」以来となる小栗康平監督の通算第5作。独創的なスタイルで物語る意表を突いたファンタジー。未来への視点と過去へのまなざしがない混ぜになって、不思議な読後感をもたらす。女子高生が仲間と紡ぐリレー形式の物語と、町で暮らす大人たちの自分史。双方は同時進行し、やがて合流することになる。友達と短い物語を作り、リレーして遊ぶことを思いついた高校生のまち。町にらくだがやってきたところから、意外な方向へ物語は膨らんでいき……。", "2005-06-25", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MAD探偵 7人の容疑者", "MAD探偵 7人の容疑者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「エレクション」「エグザイル　絆」のジョニー・トー監督が手がけ、2007年ベネチア国際映画祭のコンペティション部門に出品されたサスペンス。共同監督に「マッスルモンク」「フルタイム・キラー」などでもトーとタッグを組んだワイ・カーファイ。自らを殺人現場と同じ状況に置くことで犯人をつきとめる刑事バンは、やがてその奇行の数々により辞職に追い込まれる。その5年後、新人刑事のホーがバンのもとを訪れ、1年半前から行方不明のウォン刑事の拳銃が使われた殺人事件の捜査協力を依頼する。", "2011-02-19", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ほんの５g", "ほんの５g", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "富田靖子の快活な魅力を生かした、コミカルな青春ドラマ。明日香は就職戦線真っ只中、採用が決まらずイライラしていたある日、フラフラと立ち寄ったパチンコ屋で、いきなり“777”の大フィーバーを出してしまう。その日からパチンコを通して明日香の単調な生活に変化が表われ始めた。就職の面接官を演じた大地康雄のイヤミたっぷりな演技が光る。タイトルの“５g”とはパチンコ玉の１個の重さのこと。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("５０　FIRST　DATES", "50回目のファースト・キス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "アダム・サンドラー＆ドリュー・バリモアという「ウェディング・シンガー」コンビで贈るラブ・コメディ。ヒロインは前日の出来事を忘れてしまう記憶障害の持ち主。彼女にひと目ぼれをした男が、連日ゼロからのアタックを繰り返す。荒唐無稽なようだが、ラストでは熱い感動が観る者の胸をしめつける。プレイボーイのヘンリーはルーシーという娘に出会ってひと目ぼれ。ところが翌日に訪ねて行くと、彼女はヘンリーのことを忘れていて……。", "2005-06-18", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SILKWOOD", "シルクウッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "プルトニウム工場に働く平凡な女性、カレン・シルクウッド。工場の安全性に疑いを持った彼女は、熱心な組合活動員に変貌。やがて、その身辺で不審なことが起きはじめる……。現実に起きたシルクウッド事件をベースに、ニコルズ監督が取り組んだ社会派問題作。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A BOUT DE SOUFFLE", "勝手にしやがれ", "", "", "http://ecx.images-amazon.com/images/I/51UQO2ROWrL._SL210_.jpg", 89, "自動車泥棒のミシェルと、新聞売りのアメリカ留学生パトリシアが再会する。ある日ミシェルの放った銃弾が白バイの警官に当たってしまった。愛、逃亡、密告、そして死……。即興演出、手持ちカメラでの街頭撮影、説明排除のジャンピング・カット編集−−今では不思議でもない手法の数々が当時の劇映画のタブーを打ち破り、世界の映画を一変させた、ヌーベル・バーグ誕生の記念碑となったゴダール監督の傑作。", "1960-03-26", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美麗時光", "きらめきの季節／美麗時光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "隣家に住む従兄弟同士で親友でもある青年アウェイとアジェ。平穏な日々を送っていた二人だったが、ある日、暴力団組織が経営する飲み屋のツケの取り立て係を任され、ピストルを与えられたことから運命の歯車が狂い出す。万国に通ずる青春時代特有の無鉄砲さときらめく瞬間を描き出したのは、「最愛の夏」で世界的評価を受けたチャン・ツォーチ。2002年ヴェネチア国際映画祭コンペティション部門出品作品。", "2001-01-01", "53,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWILIGHT ZONE：THE MOVIE", "トワイライトゾーン 超次元の体験", "", "", "http://ecx.images-amazon.com/images/I/51iQDQWYoTL._SL210_.jpg", 103, "アメリカで1959～64年に放送された人気TVシリーズ『ミステリー・ゾーン』の映画化。少年時代に番組に熱中したスピルバーグとランディスがプロデューサーに回り、ダンテ、ミラーの２監督を誘って４話から成るオムニバス・ファンタジー映画として仕上げた。第１話はランディスが担当。人種差別の激しい白人がユダヤ人、アジア人、黒人に間違えられて、あらゆる時代で己の卑小さを思い知らされる。スピルバーグが担当する第２話では、魔法のカンによって老人ホームの住人たちが今一度若さを取り戻して老いを考える。何でも願いが叶う超能力少年を描く第３話は、ダンテらしい毒気とコミック感覚が楽しい。クライマックスの第４話では、飛行機恐怖症の男性の恐ろしい高空での体験をミラーがダイナミックに描いた。それぞれの監督たちの個性が鮮やかに反映された佳作。TV時代の音楽を担当していたJ・ゴールドスミスが再び招かれ、映画４本分の色彩豊かな音楽を披露したことでも注目を集めた。また、第１話の撮影中にV・モローがヘリコプターの墜落事故で死亡し、裁判沙汰になったことでも有名。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OUTRAGEOUS FORTUNE", "うるさい女たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "大部屋女優二人が、知らずに同じ男に恋をした。ところが、そいつは二重スパイ。彼女たちは、いつしか事件に巻き込まれ……。コメディエンヌ、ミドラーとロングが繰り広げる、ダイナミックでフィジカルな、スリルと笑いのアクション・コメディ。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BEAUMARCHAIS　L’INSOLENT", "ボーマルシェ フィガロの誕生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "戯曲『セビリャの理髪師』『フィガロの結婚』の原作者ボーマルシェの波乱の半生を描く伝記映画。パリの時計職人の息子として生まれたボーマルシェは、知性と教養を身につけ、処世術にも長けていた。貴族の地位を金で手に入れ、裁判官を務め、スパイとしても活躍。常識にとらわれることなく、色恋沙汰にも事欠かない時代の風雲児として名を馳せる。その一方で、作家活動に励んでいた彼は、『セビリャの理髪師』の公演失敗で挫折感を味わう。執筆から遠ざかっていた彼だが、弟子のギュタンや愛妻マリ・テレーズの支えで、新作『フィガロの結婚』を書き上げるのだった……。", "1996-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("泉", "泉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "岸田国士の新聞小説を基に、自尊心とは何かを問いかける問題作。小林正樹の完全主義者ぶりがのぞき始めた作品で、心理的な描写が優れている。浅間山麓の水源地問題をめぐって都会人と農民が対立する様子を背景に、破局していく不思議な男女の愛を浮かび上がらせていく。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOON CHILD", "MOON CHILD", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "Gacktが企画・脚本を手掛け、相手役のHYDEを自ら口説き、主演した話題作。無国籍な近未来空間を舞台に、ヴァンパイアとストリート・ギャングの間に芽生える友情を越えた絆を、壮大なスケールで描く。2024年、アジアの経済特区マレッパ。10年前、永遠の命を持つ吸血鬼ケイと出会ったショウは彼とコンビを組み、裏社会で暗躍していた。ところが、マフィア組織を襲撃した事件をきっかけに、ケイは姿を消してしまう。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TERROR VISION", "テラービジョン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "スタン・バッターマンは先端の衛星技術によるマルチ・チャンネルTVを見るため、裏庭にパラボラ・アンテナを取り付けた。さっそく人気のモンスター番組にチャンネルをあわせると、画面から奇妙な怪物が出てきて……。", "1986-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POINT OF NO RETURN", "アサシン 暗・殺・者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "死刑囚マギーは死刑を免れるため、政府の暗殺者となることを引き受ける。女ながら抜群の才能を持ったマギーは様々な殺しのテクニックを身につけていき……。フランス映画「ニキータ」のアメリカ版リメイクだが、ストーリーのみならず、見せ場のカット割りまでそっくりなのはご愛嬌。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Marmaduke", "サーフィン　ドッグ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "大型犬マーマデュークのコミカルな生活を描き、全世界20カ国で600以上の新聞に掲載されたブラッド・アンダーソン原作の同名人気漫画を実写映画化したファミリーコメディ。マーマデュークの飼い主であるウィンスロウ一家が米カンザス州からカリフォルニアのオレンジ・カウンティに引っ越すことになり、マーマデュークもウィンスロウ家の子供たち同様カリフォルニアで新しい仲間たちと新生活を送ることになる。マーマデュークの声にオーウェン・ウィルソン。監督は「シャンハイ・ヌーン」のトム・デイ。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The House Bunny", "キューティ・バニー", "", "", "http://image.eiga.k-img.com/images/movie/54632/315x210.jpg?3353", 97, "プレイボーイ誌で表紙を飾ることを夢見るセクシーモデルのシェリーは、27歳という年齢を理由に契約を切られてしまう。途方に暮れていた彼女は、学生寮の寮母という仕事を見つけるが、寮は今にも潰れそうな経営状況。シェリーは寮を救うため、まずは冴えない寮生の女子学生たちを磨こうと奮闘し……。主演は「最‘新’絶叫計画 」のアンナ・ファリス。プロバスケットボールNBAのスター選手、シャキール・オニールが本人役で出演。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ザ・ドリフターズの極楽はどこだ", "ザ・ドリフターズの極楽はどこだ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "東京の下町、黒木長作は妻に先立たれ、一作と花子を男手一つで育ててきた。長作は小さな会社で退職金だけを楽しみに生きている。ところが、一作がバンド結成資金にそれを前借りしてしまう。従来の作品とは趣を異にし、人情もの的な一編。", "1974-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SOMEBODY UP THERE LIKES ME", "傷だらけの栄光", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "ニューヨーク裏町の貧しく悲惨な暮らしから、世界ミドル級チャンピオンになったボクサー、ロッキー・グラジアーノの伝記。P・ニューマンの名演、ルッテンバーグの白黒画面、ワイズ監督のテンポのいい演出と、どれをとっても一級品。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’ULTIMA NEVE DI RPIMAVERA", "メリーゴーランド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "多忙な弁護士の父と、白血病で余命いくばくもない息子との交流の物語。死を予感した息子が、父と一緒に夜明けの遊園地でメリーゴーランドに乗り、父親に抱かれながら静かに死んでいく。難病に苦しむ少年とその姿を見て悩む父親の姿がドラマティックに描かれた感動作。", "1974-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEFILADA", "金日成のパレード　～東欧の見た“赤い王朝”～", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1988年９月に行われた、朝鮮民主主義人民共和国の、建国40周年を祝う100万人の大パレード。その模様をカメラに収めた、ドキュメンタリーの異色作。そのマスゲームの美しさ、統制のとれたパレードのすごさ。そしてそれを上まわる、全体主義的な国情の現実が、観る者に伝わってくる。", "1989-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若親分", "若親分", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "日露戦争の戦勝気分に酔っていた頃、南条組の親分・辰五郎が殺される。海軍少尉の軍装のまま葬儀に参加した辰五郎の一子・武が二代目を襲名。父の仇である伊蔵一家に殴り込む。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CLOUDBURST", "復讐の雨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "大戦中レジスタンスの任務に就いていたグラハムは、今は暗号解読の仕事をし、愛妻キャロルと、じき生まれてくるベビーを待って幸福であった。しかし突然の不幸が彼を襲う。身重のキャロルが目の前で、暴走車にひき殺されたのだった。逃走した犯人ミッキーとローナの二人に復讐を誓うグラハム。まずはミッキーを追い詰め、その殺人現場に謎の暗号を残していく……。復讐に燃える男の苦悩が生々しい映画。前半のグラハムの輝く表情が、復讐後には一気に疲れ果て、老けこんでしまうのが印象的である。", "1951-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劔岳　撮影の記　標高3000メートル、激闘の873日", "劔岳　撮影の記　標高3000メートル、激闘の873日", "", "", "http://image.eiga.k-img.com/images/movie/54942/315x210.jpg?1819", 0, "日本映画界を代表するカメラマン・木村大作が初監督に挑戦した「劔岳　点の記」の製作現場に密着したドキュメンタリー。監督・撮影は大澤嘉工。クランクイン前から木村に密着し、200日を越える標高3000メートル級の立山連峰で行われた撮影に同行し、2年間にも及ぶ過酷な製作環境に迫る。", "2009-11-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("一本の手", "一本の手", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "「名もなく貧しく美しく」などで知られる松山善三監督が手掛けた舞台ミュージカル『ご親切は半分に…』を基に映画化した感動作。有料老人ホームで巻き起こるさまざまな人間模様をさわやかに描き出すと同時に、“老いていく”意味を老若男女に問いかける。東京の有料老人ホーム“あかつき”で、多忙な毎日を送る新任の看護福祉士ミツ子。ケースワーカーの健太らとともに、今日も一筋縄ではいかない老人たちに振り回されている。そんな折、元中学校の校長・辻村が入所して来て……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本女侠伝・血斗乱れ花", "日本女侠伝・血斗乱れ花", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "明治の中頃、大阪船場の一人娘・ていは、婿養子を迎え呉服商を仕切っていた。一方、夫の藤吉は石炭にとりつかれていた。ていは藤吉を説得しようと北九州へ飛ぶが、彼は炭層を掘り当てた直後、落盤事故で死亡。ていは、夫の志を継ぐ決意を固める。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OSCAR", "オスカー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "禁酒法下のアメリカで、警察やライバル・ファミリーも一目置くほどの、肩で風切る顔役だったアンジェロ・プロボローネ。だが父の遺言によって人生は一転、彼は失われた家名を取り戻すため、極道から足を洗おうとするのだった。しかし、にわか仕立ての“悪名クリーン作戦”はうまくいくはずがなく……。アクション派スタローンが、初の古典的笑劇の世界に挑んだコメディ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANALIFE〈アナライフ〉", "ANALIFE〈アナライフ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "国内外の映画祭で数々の受賞歴を持つ映像作家、合田健二監督による異色作。“穴”をキーワードに、独自の方法によって相手に気づかれずにレイプを続ける男など、変質的な行為に走る男女3人の姿が描かれる。現代人が抱える心の闇と欲望を、独特の映像効果で表現する合田監督の視点が斬新だ。尻に傷を負った男女3人が肛門科を訪れ、待合室で顔を合わせる。ごく普通の人間に見える彼らの、想像もしない日常が明かされていく。", "2005-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女の坂", "女の坂", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "沢野久雄の小説『五条坂』『愛する権利』の２編を新藤兼人が脚色、吉村公三郎が監督した女性映画。京銘菓の老舗“鍵村”を継いだ現代娘・明恵が、のれんを守ることに若い情熱を燃やし、次第に京おんなとして成長していく姿を描く。美しい京都の街並に映える、岡田茉莉子のはつらつとした存在感が魅力。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fireproof", "ファイアー・ストーム", "", "", "http://image.eiga.k-img.com/images/movie/54692/315x210.jpg?4192", 118, "どんな困難な状況でも人命救助を諦めないケイレブは、仲間から信頼を寄せられる勇敢な消防士。しかし、妻キャサリンとの結婚生活はうまくいかず、離婚の危機を迎えていた。そんな2人の状況を見かねたケイレブの父親は、離婚を40日延ばすことを提案し、その間に夫婦のよりを戻そうと必死にケイレブにアドバイスを続けるのだが……。主演は米人気TVシリーズ「愉快なシーバー家」で長男マイクを演じてブレイクしたカーク・キャメロン。", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IT’S ALL TRUE ： BASED ON AN UNFINISHED FILM BY ORSON WELLES", "オーソン・ウェルズ　イッツ・オール・トゥルー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "O・ウェルズの未完の作品で新たに発見されたフィルムと、製作にかかわった人たちへのインタビューにより構成された作品。1942年にO・ウェルズは南米を舞台にドキュメンタリー・タッチのオムニバスを撮影していたが、ブラジルでは独裁政権と衝突し、メキシコでは第２話の撮影が中断したまま、出演者の一人が事故死する。結局、製作は中止され、フィルムは行方不明になる。そして1985年にフィルムが発見され、第３話「筏の四人」がほとんど残っていたが、第１話はほとんど見つからず、第２話は未完のままだった。「筏の四人」は事故死した漁師の仲間が自分たちの窮状を訴えるために筏に乗ってリオデジャネイロまで航海する話である。この「筏の四人」をもとに、残されたその他のフィルムの断片、関係者へのインタビューを加えて再構成している。O・ウェルズらしい緊張感にあふれた映像が見られるほか、彼の映画の作り方を知ることができる。", "1942-01-01", "1,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RYAN’S DAUGHTER", "ライアンの娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 203, "20世紀はじめのアイルランドの寒村を舞台に描く、イギリス軍守備隊指揮官と、ここに住む居酒屋の娘ロージーとの不倫の恋の物語。人間の愚かさや聡明さを大自然の豊かな表情のなかに、重厚なタッチで描く70ミリの大作。", "1970-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("REPULSION", "リパルジョン／反撥", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "キャロルは姉のヘレンと二人暮らし。姉の恋人が毎晩現れては一緒に夜を過ごすので、感じやすい年ごろの彼女はセックスに憧れる気持ちが高まる一方で、嫌悪感も抱くようになる。夢の中のシーン、幻覚に悩まされるシュールな映像が秀逸なサイコ・スリラー。「ポランスキー・スペシャルDVDコレクション」に収録。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("俺は死なないぜ", "俺は死なないぜ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 79, "小気味良いアクションで人気を博した、和田浩二主演のサスペンス。日雇いバンドマンの洋二は、父が急死したことを知って、その原因を探るため、熱海へと向かう。父の死には、数々の疑問が残されていた。それを調査する洋二を何者かがつけ狙いだした。彼は父の死因の裏に、事件の匂いを嗅ぎとるが……。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE TENDER", "myベスト・フレンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "シカゴからロサンゼルスへ、悪の手を逃れて逃避行を続ける父と娘。その父娘を追って彼らに窮地を救われた満身創痍のドーベルマンが、長い長い道程をただひたすら走り続ける。やっとのことで父娘のもとについたドーベルマンを抱いたとき、父は組織との対決を決意する。失意と絶望の中から、愛と勇気に目覚めていく主人公をトラヴォルタが熱演。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Little　Birds／　イラク・戦火の家族たち", "Little　Birds／　イラク・戦火の家族たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "『ニュースステーション』などの報道番組でイラク戦争のリポートを送り続けたビデオジャーナリスト、綿井健陽が自らの取材による映像をまとめて作り上げたドキュメント。戦火に巻き込まれたイラク市民の声を伝える。一般メディアでは伝え切れなかった戦争の真実が見える貴重な一作。'03年3月、アメリカによるイラク侵攻が開始された。これにより平穏な日常から一転、戦火に巻き込まれたイラク市民の姿を映し出す。", "2005-04-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桜の国", "桜の国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "1938年作品「母と子」で評価を確立した渋谷実が、戦時下に国策を反映させつつも彼らしく仕上げた悲恋ドラマ。満州で住民を宣撫する仕事にあたっていた三郎は、北京で新子という女と知り合い、愛し合うようになる。しかし彼は大陸での仕事に専念し、新子も別の男と結婚するのだった。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Fame", "Fame フェーム", "", "", "http://ecx.images-amazon.com/images/I/51U2tfa9WwL._SL210_.jpg", 107, "", "2009-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海賊八幡船", "海賊八幡船", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "戦国乱世のころ、船首に八幡大菩薩の旗印を揚げ、交易、通商に雄飛した八幡船。貿易港堺船問屋の息子、鹿門は、その船の男たちから、鹿門の父親が八幡船の船主を裏切り、船を奪った張本人だと聞かされる。そのころ海上にニセ八幡船団が出没する。純和製海洋スペクタクル。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JULIE", "影なき恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "未亡人のジェリーは夫への思いを断ち切ってライルとの結婚を決めた。だが、病的に嫉妬深いライルに耐えきれず、新婚早々逃げ出した。すぐに捕まってしまった彼女だったが、前の夫を殺害したのはライルだったと確信するようになる。しかし、彼の狂気はますます激しくなり……。L・ジュールダンが、この嫉妬深い夫を好演したサイコ・スリラー。ラスト、妻が乗った飛行機を乗っ取ろうとするライルとパイロットとの格闘が、迫真のサスペンスを生む。", "1956-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH MACHINE", "デスマシーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "「エイリアン２」等でSFXアーティストとして活躍してきたS・ノリントンの監督デビュー作。巨大兵器企業チャンク社では、兵士を殺人マシーンにする開発を進めていたがことごとく失敗。重役のケイルは、会社の意見を無視して開発を進める主任研究員のダンテを解任させようとするが、別の重役リドリーから止めるように忠告される。そしてその夜、リドリーの惨殺死体が見つかり……。近未来のロサンゼルスを舞台に、殺人ロボットと人間の死闘を描いたSFアクション。", "1994-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("デッドボール", "デッドボール", "", "", "http://image.eiga.k-img.com/images/movie/56432/315x210.jpg?4499", 99, "「地獄甲子園」（2002）の監督・山口雄大、主演・坂口拓のコンビが再びタッグを組み、ルール無用の非道野球バトルを描くスプラッターコメディ。数々の凶悪犯罪で逮捕された野球ジュウベイは矯正施設に収容され、そこで野球部への入部を要請される。幼少期のある出来事がきっかけで二度と野球はしないと誓っていたジュウベイだが、生き別れた弟ムサシの居場所を教えるという条件をのみ、入部を決意。爆弾、地雷、改造金属バットなど、なんでもありの極悪非道な野球バトルに挑む。", "2011-07-23", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("クルテク　もぐらくんと森の仲間たち", "クルテク　もぐらくんと森の仲間たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "1957年にチェコで誕生して以来、国や世代を超えて愛され続けているアニメ「クルテク」を日本で初めて劇場公開。緑色に輝く星のような石を見つけたクルテクが空に戻そうとする「もぐらくんとみどりのほし」、クルテクが映画に出演して大スターになってしまう「もぐらくんはえいがスター」など7話を厳選して上映。もぐらの男の子クルテクと森の仲間たちが繰り広げる楽しい冒険が、どこか懐かしさを感じさせる素朴なタッチで描かれる。", "2002-12-21", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("残雪", "残雪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 44, "遠距離恋愛をテーマに、微妙に変化していく二人を静かに描いた作品。東京の出版社に勤務する八坂周子は多忙ななかでも、長野支社に転勤した恋人、秋山貴広への週一度の電話を欠かさない。しかしいつ頃からか、彼の様子に変化が現れる。直接訪ねようと電車に飛び乗り、まだ雪の残る道をたどってきた周子を迎えたのは、憔悴しきった貴広だった。それから１年。あの日以来、彼と会わなくなった周子の心にはわだかまりが残っている。元気のない彼女をカメラマンの響研一が気遣う。しばしば無言の留守番電話が入るようになったある夜、受話器を取ってみると相手は貴広。周子は、会社を辞めて東京で新しい職に就いていた彼と再会するが……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京の嘘", "東京の嘘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 75, "映像（Cinema）と音楽（Music）のコラボレーションを目指す「CineMusica（シネムジカ）」シリーズ第2弾。小説家の島田雅彦が映画初主演に挑んだミステリアスなドラマ。都内の大学で教授を務める精神分析学者・瀬尾朔太郎。ある日、講義を終えたばかりの瀬尾の前に、日頃から弄んでいる教え子ちひろの妹・ゆかりが現われる。瀬尾は誘惑するようなゆかりの視線に戸惑いながらも、次第に吸い寄せられていき……。ゆかり役に、ドラマ「3年B組金八先生」などの若手注目女優・岩田さゆり。", "2007-03-17", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("藝州かやぶき紀行", "藝州かやぶき紀行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "日本人が太古の昔から暮らしてきた“かやぶき民家”を題材にしたドキュメンタリー。古民家建築をめぐる映像記録作業に長年にわたって携わってきたドキュメンタリー作家・青原さとしが自身の故郷・広島にカメラを向け、屋根ふきかえ作業の工程や職人・居住者へのインタビューを通して藝州かやぶき民家の魅力を探る。さらに、明治から昭和30年頃に山口県、関西、北九州などで“藝州屋根屋”と呼ばれて隆盛を極めた出稼ぎ文化の実態にも迫っていく。", "2008-05-31", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ビリン・闘いの村　パレスチナの非暴力抵抗", "ビリン・闘いの村　パレスチナの非暴力抵抗", "", "", "http://image.eiga.k-img.com/images/movie/53682/315x210.jpg?4121", 61, "パレスチナ暫定自治区・ヨルダン川西岸に位置するビリン村では、イスラエル政府が作った分離フェンスによって水さえも十分に得られないという状況が続いている。フェンスを境に経済格差が広がる中、村の人々はイスラエルに対して非暴力を掲げたデモ活動を週に1度実施している。パレスチナ人のみならずイスラエル人や外国人活動家らも参加するこのデモを追い、分離フェンスがもたらす数々の問題を浮き彫りにしたドキュメンタリー。", "2008-08-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Welcome Home Roscoe Jenkins", "ミート・ザ・ジェンキンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FANTASIA", "ファンタジア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "ストコフスキー指揮、フィラデルフィア交響楽団演奏のチャイコフスキーの『くるみ割り人形』やストラビンスキーの『春の祭典』などクラシックの名曲にのせて７つのエピソードが展開するミュージカル・アニメ。デュカの「魔法使いの弟子」の章では見習い魔法使いのミッキーマウスが悪ノリ魔法を披露、「春の祭典」は地球誕生から恐竜たちの世界へ壮大なパノラマが繰り広げられる。圧巻はムソルグスキーの『禿山の一夜』からシューベルトの『アベ・マリア』へとつなぐ最後のエピソードで魔法が飛び交う呪われた夜が明け、聖なる朝が訪れるまでを描いた、ダイナミックで、このうえなく美しい映像はディズニー・アニメの真骨頂。60年以上も昔の作品というのが嘘のようだ。", "1940-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GOBLIN III", "ガバリン３", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "１作目に主演したW・カットが再び出演。カット扮するロジャーは、前半で事故死。その妻と娘が、彼の愛した古屋敷で、様々な怪奇現象に遭遇していく。ピザマン、スネークマン、ゴキブリマンなど、ユニークな新ガバリンたちが登場。心温まる(？)ラストというおまけもついたファンタスティック・ホラー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("桃まつり presents kiss!　「月夜のバニー」", "桃まつり presents kiss!　「月夜のバニー」", "", "", "http://image.eiga.k-img.com/images/movie/57912/315x210.jpg?2406", 30, "", "2009-03-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("卍舞２ 妖艶三女濡れ絵巻", "卍舞２ 妖艶三女濡れ絵巻", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "エロティックな過激度をパワーアップしたお色気とアクションの娯楽絵巻。第１作で仇討ちを果たしたお蝶は、今は男装の麗人・女剣客として街から街へと渡り歩くお尋ね者。ヤクザに追われる女を助けたのを縁に、居酒屋の女将おゆう、その隣家の武家の妻・志保と親しくなり、お蝶もしばしの安息を楽しんでいた。ところが名主・庄左衛門からの借金に苦しむ志保の夫は、お蝶に懸賞金がかかっているのを聞きつけ襲いかかる。一方、庄左衛門はひそかにおゆうと志保の柔肌を狙い、志保はその肉欲の餌食となる。義憤に駆られたお蝶は、名主の家に切り込むが……。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版　仮面ライダー電王＆キバ　クライマックス刑事", "劇場版　仮面ライダー電王＆キバ　クライマックス刑事", "", "", "http://image.eiga.k-img.com/images/movie/53582/315x210.jpg?1388", 110, "2008年1月まで放送され人気を博した「仮面ライダー電王」の劇場版第2弾。悪の組織“ネガタロス軍団”結成を企むはぐれイマジンのネガタロス一味が、デンライナーのオーナーから電王のパスを盗み出した。地球がイマジンの世界になってしまうのを防ぐべく捜査を開始した良太郎とモモタロスは、警視庁から派遣された刑事らと協力してネガタロスたちのアジトを突き止めるが……。電王の後を引き継いだ“仮面ライダーキバ”も登場。", "2008-04-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FUZZ", "複数犯罪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "マクベインのミステリー・シリーズ“87分署”の中の代表作の映画化。ボストン警察87分署に、謎の男から５千ドルの要求と公園局長射殺の脅迫電話が入る。刑事たちはさっそく捜査を開始するが、犯人はそれをあざ笑うかのように射殺を実行。続いて今度は“副市長、値は５万ドル”という電話が入って……。なお、脚本のE・ハンターは原作者の別名。", "1972-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花井さちこの華麗な生涯", "花井さちこの華麗な生涯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "海外の映画祭やPFF2005でも紹介された異色のピンク映画。社会風刺をベースにしながらも、シュールなコメディとして疾走する奇妙な味わいは、さながら“和製マカヴェイエフ”の趣。額に穴が開いたことから知性が芽生えたイメクラ嬢が、ミサイル発射装置＝“大統領の指”を手に入れたから、さあ大変。地球を股にかける(？)キテレツな展開が待ち受ける。イメクラ嬢、さちこはスパイ絡みの発砲事件に巻き込まれ、額に流れ弾を食らう。なぜか一命を取り留めるが、額に穴ができたことから、猛烈な知識欲が生まれていくのだった……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("最強 最後のカラテ", "最強 最後のカラテ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "「地上最強のカラテ」「同・PART2」に続く、格闘ドキュメンタリー・シリーズの第３作。1979年に開催された、第２回空手世界選手権大会の模様を描く。“熊殺し”の異名をとるウィリー・ウィリアムスの参戦で、日本勢危うしの声が聞かれるなか、過酷なトレーニングに励み、大会に臨んだ日本の極真空手の猛者たちの姿をとらえていく。前２作で製作を務めた梶原一騎が監督にあたった。", "1980-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("情無用の罠", "情無用の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ある工事現場で橋本という名前の女が殺される。女が持っていた玩具の指紋から二宮三郎が逮捕されたが、彼にはアリバイがあった。三郎は、自分を罠に落とした者を探し始めるが……。田中邦衛はこの映画の殺し屋役で注目され、以後東宝の名脇役になっていく。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Arthur et la Vengeance de Maltazard", "アーサーと魔王マルタザールの逆襲", "", "", "http://image.eiga.k-img.com/images/movie/55162/315x210.jpg?1647", 0, "リュック・ベッソン監督による実写とアニメを融合したファンタジー「アーサーとミニモイの不思議な国」の続編。前作に引き続き、主演はフレディ・ハイモア。かつて体長2ミリのミニモイ族に変身して「ミニモイの国」の危機を救ったアーサーのもとに、「助けて！」と書かれた米粒が届く。王女セレニアの危機を知ったアーサーは、宿敵・魔王マルタザールを倒すため、再び「ミニモイの国」へと向かう。", "2010-04-29", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Cocalero", "コカレロ", "", "", "http://image.eiga.k-img.com/images/movie/54062/315x210.jpg?3276", 94, "第5回スペイン・ラテンアメリカ映画祭上映作品。南米最貧国ボリビアのコカノキを巡り、社会格差や人種差別を浮き彫りにするドキュメンタリー。先住民にとってコカインの原料となるコカの葉は重要な民間薬だったが、90年代後半から始まったアメリカによるコカノキ栽培撲滅計画により、貧困層のコカ農民たちは収入源を失う。スペインからの独立以降も白人が政権を握ってきたボリビアで、アイマラ族のエボ・モラレスは反米色を打ち出し、初の先住民系大統領を目指す。", "2008-09-15", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FATSO", "愛と食欲の日々", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "超太っちょのドミニクは、幼なじみの従兄弟が太り過ぎのため心臓発作で死んだことを知り、自分の過食症に打ち勝ってダイエットすることを心に誓うが……。ダイエット・ブームとグルメ・ブームを皮肉った異色コメディ。「奇跡の人」のサリヴァン先生役でアカデミー主演女優賞を獲得したA・バンクロフトが初の監督、脚本に挑戦している。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pistol Whipped", "弾突 DANTOTSU", "", "", "http://image.eiga.k-img.com/images/movie/53722/315x210.jpg?3564", 0, "沈黙シリーズで知られるスティーブン・セガールが主演する犯罪アクション。酒とギャンブルに溺れ、警官としての職も失ってしまったマット。彼は謎の男に借金を肩代わりしてもらう代償に、法では裁けない犯罪者たちの制裁を請け負うが……。", "2008-09-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Benchwarmers", "がんばれ！ベンチウォーマーズ", "", "", "http://ecx.images-amazon.com/images/I/51Bgm0%2ByFdL._SL210_.jpg", 85, "ひょんなことからリトルリーグに参戦することになったダメ男3人組の奮闘を描いたスポーツコメディ。子どもの頃からいじめられっ子だった青年ガスは、グラウンドで野球少年たちにいじめられていた少年を助けたことをきっかけに、友人のリッチーやクラークと3人でリトルリーグの大会に出場することに。順調に勝ち進んでいくが、やがてガスの意外な過去が明らかになり……。主演は、「アニマルマン」のロブ・シュナイダー。", "2006-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ころがし涼太　激突！モンスターバス", "ころがし涼太　激突！モンスターバス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "元暴走族のヘッドで、喧嘩には強いが女にはめっぽう弱い、型破りのバス運転手“ころがし涼太”こと高倉涼太の活躍を描いた痛快アクション。一目惚れした美女を守るため、正体不明の敵に立ち向かうというシンプルな物語を全編アクションで見せる。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Lightship", "ライトシップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "沖合に停泊している灯台船ライトシップが３人の凶悪犯に乗っ取られた。犯人たちは船を動かして逃げようとするが、夜間の移動を禁じられている船長は決して動かせないと言い続ける。極限状況の中での人間ドラマをスリリングに描き出した作品。", "1985-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("映画ドラえもん　のび太の人魚大海戦", "映画ドラえもん　のび太の人魚大海戦", "", "", "http://image.eiga.k-img.com/images/movie/54762/315x210.jpg?1284", 0, "30周年を迎えたシリーズ「映画ドラえもん」の30作品目。「のび太の新魔界大冒険」の真保裕一が脚本を担当。のび太とドラえもんがひみつ道具「架空水」で町を海の底にして遊んでいると、5000年前に地球へやってきた人魚族の王女・ソフィアが迷い込んでくる。のび太たちはソフィアと一緒に海の世界へ冒険に出かけるが、突然巨大ウツボに襲われ、しずかちゃんが何者かにさらわれてしまう。", "2010-03-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Joe's Bed-Stuy Barbershop: We Cut Heads", "ジョーズ・バーバー・ショップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "　ザックはブルックリンにある床屋の店主。もともと店はジョーのものだったが、彼はそこで行なわれていた賭博の金を持ち逃げしようとして殺されてしまった。店の客が少なかったため、ザックはやめていた賭博行為を再開することに。しかし彼もまたその金に手をつけてしまい……。スパイク・リー監督がニューヨーク大学映画学科の卒業作品として製作。アフリカ系アメリカ人の生活を、ユーモアを交えながらドライなタッチで描く。", "1983-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kill Switch", "雷神 RAIJIN", "", "", "http://image.eiga.k-img.com/images/movie/54282/315x210.jpg?4645", 96, "人気アクション俳優スティーブン・セガールが、主演のみならず脚本・製作総指揮も手掛けた刑事アクション。アメリカ史上最も多くの殺人事件を解決した敏腕刑事ジェイコブ・キングは、少年時代に双子の弟を目の前で殺害されたトラウマにいまだに悩まされていた。そんなある日、胸に爆破装置を埋め込まれた娼婦が発見されたのを皮切りに、猟奇事件が次々と発生。ジェイコブは過去のつらい記憶と闘いながら犯人を追い詰めていくが……。", "2009-02-14", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEXT KARATE KID", "ベスト・キッド4", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "東洋の鉄人ミヤギが新しい愛弟子とともにスクリーンに再登場する。ミヤギの命の恩人であり今は亡き戦友の孫娘のジュリーは、ハイスクール内にはびこる悪との確執に心を痛めていた。そんなある日、ジュリーが思わず見せたカラテ・ウーマンとしての非凡な才能を鉄人ミヤギは見逃さなかった……。ヒロイン役のH・スワンクの素晴らしい運動神経としなやかなボディに注目。", "1994-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Battleship", "バトルシップ", "", "", "http://image.eiga.k-img.com/images/movie/56682/315x210.jpg?2366", 130, "「キングダム　見えざる敵」「ハンコック」のピーター・バーグ監督が手がけ、エイリアンの侵略部隊と各国の連合艦隊が洋上で激闘を繰り広げる姿を描くアクション大作。ハワイ沖の太平洋上で大規模な軍事演習を行っていた、アメリカを中心とした世界各国の護衛艦隊の前に、突如として正体不明のエイリアンの母船が出現。地球側の呼びかけ応じることなく、侵略を始める。演習に参加していた米海軍の新人将校アレックスや、アレックスがライバル心を抱いている海上自衛隊の指揮官ナガタらは、弱点もわからない謎の侵略者と相対することになる。主演は「ウルヴァリン：X-MEN ZERO」の新鋭テイラー・キッチュ。ナガタを演じる浅野忠信は、「マイティ・ソー」に続くハリウッド映画出演。", "2012-04-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAXIDI STA KITHIRA", "シテール島への船出", "", "", "http://ecx.images-amazon.com/images/I/41J8Z8CVQGL._SL210_.jpg", 140, "ひとりの映画監督が父の映画を撮ることになるが、父役がなかなか決まらない。そこへひとりの花売り老人が通りかかり、思わず後を追う監督。「エゴイメ（私だよ）」、劇中映画と父の人生が交錯していく……。家族を捨ててソ連へ亡命した父が年老いた姿で帰ってきた。かつての同志たちからも政府からも拒絶されて雨の桟橋にひとり打ち捨てられる老父。歩み寄る妻を一緒に乗せて小さな桟橋は暗い海をどこまでも流れて行くのだった。政治への望みを失った時代を語ったアンゲロプロス監督の叙情傑作。", "1986-02-08", "27", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ぬるぬる燗燗", "ぬるぬる燗燗", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 71, "物部の経営する居酒屋は繁盛していたが、町にやってきたジプシー居酒屋の“夢殿”に客を取られてしまう。人気の秘密を偵察した物部は、夢殿が若い妻のレオタードに酒を流し込み、その汗と分泌液を混ぜることで微妙な味を作り出していることを知る。さっそく、物部は自分の娘にレオタードを着せて真似してみるが、夢殿の燗酒の味には負けていた。そして、物部は夢殿の若妻が実は処女で、そのことが酒の味を良くしていることを知るのだが……。異才、西山洋一がTVドラマをもとに描いた、風変わりなピンク映画。物部を藤田敏八、夢殿を渡辺護という映画監督が扮している。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I.K.U.", "I.K.U.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "未来のセックス産業をスケッチしたサイバー・ポルノ。20XX年、ハイテク企業ゲノム社は性的刺激をあたえるチップ開発のため、サンプルデータを収集することに。その任務に当たるセクシーなレプリカント７人が奔走を繰り広げる。デジタルビデオで撮影した画面を合成し、シュールな映像世界を創造。「ブレードランナー」へのオマージュというべき描写も見どころ。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒暗之光", "最愛の夏", "", "", "http://image.eiga.k-img.com/images/movie/1352/315x210.jpg?4517", 102, "17歳の夏休み、カンイは地元の小さな港町基隆（キールン）に帰って来た。目の見えない両親、知的障害を持つ弟、年老いた祖父。家族みんなが支え合い、小さな幸せを見つけながら生きているのだった。そんな折、カンイは父親の反対をよそに、孤独な青年アピンに恋をする。よそ者である彼を気に入らないグゥ警察所長の思惑で、ヤクザ同士の争いに巻き込まれたアピンは、その若い命を落としてしまう。", "2000-09-30", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MO’MONEY", "モー・マネー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「ラスト・ボーイスカウト」でB・ウィリス相手に好演した黒人スター、D・ウェイアンズ主演のコメディ。美女目当てにクレジット会社に就職した詐欺師のジョニーは、キャッシュカードを盗んだことから、腹黒い上司の企てる陰謀に巻き込まれてしまう……。ジャネット・ジャクソンらのノリの良いブラック・ミュージックが全編を彩る。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東方禿鷹／EASTERN CONDORS", "イースタン・コンドル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "1976年、ベトナム撤退を終えたアメリカだったが、破壊したはずの地下要塞が設備もそのまま残っているという情報を得る。正規の軍隊を出動できないアメリカは、大統領の密命により、ベトナム系や中国系の東洋人による特殊部隊を送り込んだ。全編フィリピン・ロケ。カンフーを取り入れたアクションが盛りだくさんの戦争映画。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("机のなかみ", "机のなかみ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "お調子者の家庭教師と教え子の女子高生が織りなす、風変わりで予測不能なラブコメディ。大学受験を控える高校生・望の家庭教師を引きうけたフリーターの馬場。同せい中の彼女がいるにもかかわらず、望の可憐さにすっかり心を奪われてしまった馬場は、あの手この手で彼女の気を引こうとする。一方、馬場のアタックをかわしながら猛勉強を続ける望には、ある秘密があった……。メガホンを取るのは、本作が長編デビューとなる吉田恵輔。", "2007-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SUSUZ　YAZ", "野性のもだえ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "タバコ栽培が村民の糧となり、それに必要な水と女性の働き手が大事にされるトルコの村。農夫オスマンは自分の所有地から湧き出る水をせき止め、独占しようとするが、弟ハサンに制止される。やがてオスマンは水をめぐるトラブルから村人を殺し、ハサンにその罪を着せ、さらに弟の内縁の妻を奪った。特赦で釈放されたハサンは名誉のために兄と対決する。貧しい農村を舞台に、兄弟の対立を重厚に描いた人間ドラマの力作。", "1964-01-01", "32", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MORNING DEPARTURE", "暁の出航", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "対潜レーダーの実験と訓練のため沖へ出た潜水艦が、戦時中に投下された機雷に触れて爆発、海底に沈没する。乗組員は12名だが救命胴衣は８個。艦長を含めて艦内に取り残された４名の死との戦いが始まる。イギリス映画らしい重厚なタッチに人道的感動が盛り上がる。", "1949-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("探偵物語", "探偵物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "大学受験のため芸能活動を一時休業していた薬師丸ひろ子の復帰第１作。アメリカ出発を１週間後に控えた女子大生が殺人事件に巻き込まれ、監視役の私立探偵とともに事件の捜査に乗り出す。赤川次郎が薬師丸のために書いたオリジナル・ストーリー。", "1983-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("亀虫", "亀虫", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 61, "2002年の水戸短編映像祭でグランプリを受賞した「VICUNAS」の冨永昌敬監督の最新作。本作は“亀虫”をキーワードに５篇のエピソードである「亀虫の兄弟」「亀虫の嫁」「亀虫の妹」「亀虫の性」「台なし物語」が連なる短編シリーズで、東京都内を東西に横切る目白通りを舞台に、とある“兄弟”“嫁”“妹”の、それぞれの物語が繰り広げられる。夫婦喧嘩の後、家を飛び出した、かー君。妻への復讐を胸に戻るが、かー君が見たのは、妻ではなく、幼なじみのたかおだった。突然の再会でたかおの目的を怪しむ、かー君。やがて、たかおは亀虫に変身し、衝撃の事実を告白する。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ガチャポン", "ガチャポン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "雑誌ライターから映画監督になった異色の経歴を持つ内田英治が、オリジナルの脚本から手掛けた劇場用映画デビュー作。「バトル・ロワイアルII／鎮魂歌〈レクイエム〉」の忍成修吾が主演を務めた青春群像劇。複雑な感情でつながった若者たちが、挫折と失敗を繰り返しながら自らをつかんでいく姿が描かれる。音楽プロデュースは、現在の日本のラップ・シーンをリードする、DJ GREEN PEEACEが担当。大学生のケンは、DJになる夢を漠然と見つつも、結局は毎日をなんとなく過ごしていた。一方、ケンの同級生テツオはクラブ仲間の悪友ジャンボ、マコトらと一緒に、麻薬の売人であるリョウの仕事に手を出し始める……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("生命の冠", "生命の冠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 51, "蟹缶詰製作所を経営する恒太郎と鉄次郎の兄弟は、例年にない不漁に悩まされ、取引先との契約も危ぶまれている。口論しながらも打開策に力を合わせる二人だが……。戦前当時の検閲に、労使問題を十分掘り下げられなかったとはいえ、ある種ドキュメンタリー的な力強さも感じさせる一編。", "1936-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MAN FROM UTAH", "ユタから来た男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 56, "製作より22年の時を経て'56年に日本公開された時には、45分の短縮版だったという西部劇の古典的作品。“ミスター西部劇”のJ・ウェインが主役を張って熱演する。19世紀末のネバダ州が舞台。山に潜伏するアウトローたちが開拓者たちを襲撃してくる。そこで、射撃の達人に扮したウェインが登場して悪の一味と戦う。西部劇の見どころとしての銃撃戦演出が巧みな仕上がり。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BAD LIEUTENANT", "バッド・ルーテナント 刑事とドラッグとキリスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ニューヨーク・インディーズ出身の映画作家として知られるA・フェラーラが、彼の最もお気に入りの俳優であるH・カイテルを主人公に、アルコールとドラッグと汚職にまみれた警官の姿を描き、その中に聖なるものを見ようとした野心作。朝、目覚めてはコカインをキメ、夜は愛人宅でアルコール漬けという毎日を送る警部補のLT。ある日、尼僧に対するレイプ事件が起こる。犯人を無条件に許そうとする尼僧に、LTはイラつく。“イエスはあなたのために死んだのです”と語る尼僧に、LTは泣き崩れ、己の弱さと罪を自覚するのだが……。むき出しの欲望とカソリック的な道徳、その相剋の中で葛藤する男を演じたH・カイテルの迫真の演技が感動的。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COL CUORE IN GOLA", "危険な恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "ロンドンに住む俳優のベルナールが、ある日仕事仲間の家を訪れると、そこには男の惨殺死体があった。そして死体の傍には一人の女が。無実を言いはる彼女の不思議な魅力に、ベルナールはいつしか惹かれていく……。サイケ調の実験的映像が印象的なサスペンス。", "1968-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TC2000", "未来警察TC2000", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "マーシャルアーツのチャンピオン、B・ブランクスとB・フィリップス主演のSFアクション。荒廃した近未来都市を舞台に、殉職のあと、殺人マシーンと化した元同僚の女性と戦うハメになってしまった刑事の活躍を描く。おなじみ格闘技アクションにSFXを加味。敵役に「燃えよドラゴン」のB・ヤングが登場するのが懐しい。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MRS. WINTERBOURNE", "くちづけはタンゴの後で", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "ミステリー作家、C・ウールリッチの『死者との結婚』をライトなタッチで映画化。身重のまま男に捨てられた田舎娘が、乗り合わせた列車で横転事故に遭遇。一命をとりとめると、亡くなった富豪の新婚夫妻の生き残りと間違われてしまい……。ハートウォーミングなシンデレラ・ストーリー。ウソをつききれないヒロインを「シリアル・ママ」のリッキー・レイクが演じている。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒蜥蜴", "黒蜥蜴", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "江戸川乱歩の小説を三島由紀夫が戯曲化。これはその映画化で、京マチ子扮する怪盗・黒蜥蜴と、大木実の明智小五郎が対決する。そこに恋愛感情を絡めたうえ、井上梅次の中途半端なミュージカルまがいの演出が顔をのぞかせ、サスペンスとしてはまとまりがない。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夏音 Caonne", "夏音 Caonne", "", "", "http://image.eiga.k-img.com/images/movie/33582/315x210.jpg?4587", 0, "ミュージシャンやデザイナーとして活躍するIZAMが監督デビューを果たした青春ドラマ。映画製作に奮闘する高校生たちの姿をさわやかに描く。17歳の讃は、ある日押入れで8ミリフィルムを発見し、そこに写っていた美しい少女に心を奪われる。翌日、讃のクラスに転入してきた夏音は、その少女にそっくりだった。讃は夏音や友人らと共に、文化祭に向けて映画製作にとりかかる。主演は、映画「ウォーターボーイズ」の三浦アキフミ。", "2006-07-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL GIARDINO DEI FINZI−CONTINI", "悲しみの青春", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "V・デ・シーカ晩年の作品。第二次大戦間近の北イタリア・フェラーラを舞台に、その町の大富豪フィンツィ・コンティーニ家の娘、ミコルと彼女の周囲の若者たちのはかない青春を描く。", "1971-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL　GILO　DEL　MONDO　DEGLI　INNAMORATI　DI　PEYNET", "ペイネ・愛の世界旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "フランスを代表する世界的イラストレーター、R・ペイネ。彼が４年の歳月をかけて1974年に完成させたハートフルなアニメーション。おかっぱ頭の青年バレンチノとかわいらしい恋人のバレンチナが、“本当の愛”を探す時空を超えた世界一周の旅をする姿を描く。ベツレヘムのイエス・キリストの誕生に立会い、ギリシャからオランダ、ドイツなどで様々な体験をする第１部、愛の飛行機“エア・ラブ”に乗ってイギリスから日本、アメリカなどを回りフランスのパリへ到着する第２部から成る。全編を通して語られる「戦争反対、恋愛賛成」というペイネのストレートなメッセージが、あたたかい感動を呼ぶ。「ニュー・シネマ・パラダイス」の名匠E・モリコーネによる優しくロマンティックなテーマ曲も、作品のテイストにマッチしていて味わい深い。", "1974-01-01", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版 機動戦艦ナデシコ", "劇場版 機動戦艦ナデシコ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "宇宙戦艦、美少女、ロボットという多彩な要素を現代的なセンスとコメディ・タッチで描いたTV版SFアニメの続編。この劇場版の舞台はTV版から３年後の西暦2021年。宇宙の存在すら揺るがしかねない古代火星の遺跡を入手した“火星の後継者”と名乗るグループが、地球連合の転覆を謀る。機動戦艦ナデシコのスポンサーであるネルガル重工は、敵から遺跡を奪取するため、新ナデシコ艦長である16歳の少女ホシノ・ルリをリーダーに特別部隊を結成。彼女たちは“火星の後継者”との戦いに臨む……。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE NEST", "ザ・ネスト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "孤島の研究所“インテック”では、かつて神をも恐れぬ研究が進められていた。闇に葬られたはずのバイオテクノロジーの落とし子・殺人ゴキブリが、島の住民を襲い始める。ロジャー・コーマン夫人、J・コーマンがプロデュースを担当、若手クリエイターたちが描いたゴキブリ・モンスター・ホラー。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Licence to Kill", "007／消されたライセンス", "", "", "http://ecx.images-amazon.com/images/I/51IEQvZup8L._SL210_.jpg", 133, "ボンドは友人フェリックスの結婚式に出席するためフロリダを訪れる。ところが、ボンドらが逮捕した麻薬王がフェリックスの妻を殺して脱走する。たとえ殺しのライセンスを失おうとも、親友の仇を討つのが先決と、英国情報機関本部Mの指令を無視して、南米某国の麻薬王に戦いを挑むボンド。おなじみ超ロング・シリーズの16作目。４代目ボンド役者ダルトンの生身のアクション・シーンと2タイプのボンドガール、ロウエルとソトが見もの。", "1989-09-30", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("10億円稼ぐ", "10億円稼ぐ", "", "", "http://image.eiga.k-img.com/images/movie/55642/315x210.jpg?2233", 0, "数々の人気バラエティ番組を手がけてきた演出家のテリー伊藤が映画監督に初挑戦し、自らキャラクタービジネスで10億円を稼ごうと奮闘する過程を描いたドキュメンタリー。自身のバイクを売るなどして300万円の資金を作ったテリーは、エイベックス所属のモデルやアーティストたちをスタッフに起用してプロジェクトを開始。オリジナルキャラクターの開発から国内外での営業まで、その一部始終をカメラに収める。", "2010-11-20", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間交差点〈ヒューマンスクランブル〉雨", "人間交差点〈ヒューマンスクランブル〉雨", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 70, "「ザ・中学教師」で気を吐いた俊英、平山秀幸が、「お引越し」の女流脚本家、奥寺佐渡子のシナリオを得て映像化した作品。女子更生施設に入れられた不良少女のあけみと、彼女に体当たりで接する若い教官、洋平。反抗と対立を経て二人がやがて築き上げる信頼の絆を、じっくりと見つめていく。", "1992-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLD SLEEP", "COLD SLEEP", "", "", "http://image.eiga.k-img.com/images/movie/55942/315x210.jpg?2234", 0, "世界的に注目を集めるバイオリニストの川井郁子と、ロシアバレエ界を代表するスターダンサー、ファルフ・ルジマトフが、2010年10月に東京・新国立劇場で行ったコラボレーション公演を映像化。地球が死滅する直前、種の保存のためコールド・スリープに入った“女”。長い眠りから目覚めた彼女の前に、1人の屈強な“男”が現われる。2人は互いにひかれあい、激しい恋に落ちていく。", "2011-03-05", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Against All Odds", "カリブの熱い夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "かつてはプロ・フットボールの花形選手ながら、今では失業中の男が、金のためにある女を追跡する仕事を引き受ける。その女とは上流社会の空虚な生活から逃げ去ったロス社交界の花。カリブ海でめぐり会った二人はたちまち恋に落ちるが……。西海岸に根をはる悪の権力機構を背景に、危険な大人たちの愛と欲望をミステリアスに描いた作品。フィル・コリンズによる主題歌『見つめてほしい』が大ヒットした。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OTESANEK", "オテサーネク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "子供のいない夫婦が手に入れた木の切り株が、人間の子供のように食べ物を欲しがり、ムクムクと成長。オテサーネクと名付けられたその怪物は、ついには夫婦が暮らすアパートの住民まで食べてしまい……。鬼才J・シュヴァンクマイエルがチェコの民話を現代に翻案して実写映画化。底なしの食欲を誇るオテサーネクのグロテスクな動きをアニメの技法で表現する一方、母性愛に突き動かされてエゴイスティックな行動に走る妻らの人物描写も強烈そのもの。シュヴァンクマイエルならではのシュールな寓話に仕上がっている。", "2000-01-01", "43", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("COLD HEART", "コールド・ハート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ダイヤを強奪し、逃亡を続けていたカップル、ネッドとジュード。だが、ジュードはネッドをおとりにしてまんまと消え去ってしまう。警察に捕まったネッドだったが、運よく脱走。彼女とダイヤを追いかける。一方、ジュードは三流ハスラー、Tを護衛につけ、ネッドを殺すように命じていた……。男と女の愛と復讐をハードに描いたサスペンス。監督はO・ストーンの「Uターン」で脚本を執筆したJ・リドリー。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE POPE MUST DIE", "法王さまご用心", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ローマ法王がある日、急死。あわてた枢機卿たちは、最新コンピュータを駆使して新法王を選び出した。ところが大ボケ司祭が名前を読み違えたから大変。とんでもなくファンキーな田舎司教がローマにやって来て……。イタリアでは当然上映禁止になり、アメリカでも物議を醸した、冗談のキツイ、イギリス製コメディ。", "1990-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ロード８８／出会い路〈みち〉、四国へ", "ロード８８／出会い路〈みち〉、四国へ", "", "", "http://ecx.images-amazon.com/images/I/21955NRBHZL._SL210_.jpg", 110, "初の四国４県オールロケで撮影された、癒やしと感動のロード・ムービー。白血病を患う一人の女子高生が自らの宿命を乗り越え、打開の道を切り開こうと、四国八十八ヵ所をめぐるお遍路の旅に果敢に挑戦する。そんな彼女が道中で出会う仲間たちもまた、それぞれに悩みや心の傷を抱えながら、旅の先にある“何か”を求めて、自身と必死に対峙しているのだった。いかなる困難の前にあっても、満ち満ちる希望を抱かせながらたくましく前進する彼女たち。その生きる力が起こした奇跡。人間の強さを、改めて実感させられる一篇だ。四国八十八ヵ所巡りをする女子高生の明日香は、白血病に冒されていた。だが、病気の苦労も見せず元気に旅をする彼女の姿は、悩みを抱えて旅を続ける人々を次第に惹き付けていく。", "2004-11-06", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TWIN　FALLS　IDAHO", "ツイン・フォールズ・アイダホ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "結合性双生児として生まれ育った兄弟の葛藤を温かく、切なく描いた好編。実の双子であるポーリッシュ兄弟が監督と主演を務め、斬新な愛のドラマを展開させている。生き別れた母親を探して、アイダホの町にやって来た双子の兄弟フランシスとブレイクは、孤独な娼婦ペニーと知り合う。特異な容姿の彼らを最初は敬遠していたペニーだが、しだいにその優しさにふれ、ブレイクと惹かれ合うようになる。一方、フランシスは病を患っており、ブレイクが生き延びるためには分離手術が必要だった……。双子の揺るぎない絆や、それに接するヒロインの愛の深さがジワジワと胸にしみ込んで、深い感動を与えてくれる。", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アナザー・ウェイ D機関情報", "アナザー・ウェイ D機関情報", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "西村京太郎のスパイ小説『D機関情報』を映画化。第二次大戦中、特殊爆弾買い付けのため、ヨーロッパへ派遣された海軍中佐・関谷直人の活躍と、彼を取り巻く戦略組織の陰謀を描いた大作。役所広司、初の主演映画でもある。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Factotum", "酔いどれ詩人になるまえに", "", "", "http://image.eiga.k-img.com/images/movie/34132/315x210.jpg?1473", 94, "カルト的人気を誇る作家チャールズ・ブコウスキーの自伝的小説を「クラッシュ」（05）のマット・ディロン主演で映画化。自称“詩人”のヘンリー・チナスキーは、詩や小説を出版社に送り続けるが相手にされず、その日暮らしの生活を送っていた。バーで出会った女性ジャンと暮らし始めても、酒とセックスに溺れる堕落した毎日。そんな彼にも、“言葉”というたったひと筋の光があった……。監督は「キッチン・ストーリー」のベント・ハーメル。", "2007-08-18", "1,36", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花の特攻隊 あゝ戦友よ", "花の特攻隊 あゝ戦友よ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "太平洋戦争末期、戦火の中に消えた特攻隊の悲劇を描く。若き杉良太郎の主演作で、彼が演じる特攻隊員の死をめぐって、その母との絆、悲恋など、悲壮な青春を映し出した一編である。杉にとっては日活入り15本目の作品で、これを最後にフリーとなり、TV時代劇のスターとしての道を歩んでいく。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("至尊無上／CASINO RAIDERS", "カジノ・レイダース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "腕の立つギャンブラー・コンビのサムとクラブは、アメリカで日本人ヤクザのイカサマを摘発し、香港に戻る。だがヤクザの報復に遭い、クラブは左手を負傷。荒んだ生活をするクラブと対照的に、サムは実業家への道を歩む。クラブはヤクザに最後の挑戦を仕掛け、見事に大金をせしめるが……。ギャンブラーたちの愛と友情を描く“香港ノワール”の一編。", "1990-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Red Heat", "シュワルツェネッガー／レッドブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "　モスクワ警察の刑事ダンコーは麻薬密売組織のボス、ロスタビリがアメリカ・シカゴで逮捕されたという報を聞き、その身柄引取りのため渡米する。しかし護送中にロスタビリの仲間が急襲。ロスタビリに逃げられたダンコーは責任を取るため、シカゴに残ることに。現地の刑事リジックと組み、ダンコーはロスタビリを追うが……。ソ連からやってきた凄腕の刑事が異国の大都会で問答無用の大暴れ。無骨なシュワと皮肉屋のはみ出し刑事を演ずるジム・ベルーシの掛け合いも楽しめる。", "1988-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Sotto falso nome", "そして、デブノーの森へ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "秘密を抱える小説家が謎に包まれた美女に翻弄されていく姿を描いた官能ミステリー。匿名ベストセラー作家のダニエルは、義理の息子の結婚式に向かう途中、美しい女性ミラと出会い一夜を共にする。翌日、彼はミラが義理の息子の花嫁であることを知る。2人はその後も許されぬ情事を重ねていくが……。主演はシャネルのミューズに抜擢されたアナ・ムグラリスと「あるいは裏切りという名の犬」のダニエル・オートゥイユ。", "2007-06-30", "2,4,26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE ROI DU COEUR", "まぼろしの市街戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "第一次大戦中のパリ北方の村。ドイツ軍の仕掛けた時限爆弾を撤去する任務を負った伝令兵プランピックが、その村にやって来ると、村人たちは皆避難していて、残っているのは動物と精神病院の狂人ばかりだった……。ドタバタ喜劇に才能を発揮するP・ド・ブロカ監督作品。", "1967-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POPCORN", "ポップコーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "映画学科に通うマギーたちのグループが、ホラー映画のオールナイトショーを開くことになった。さっそく業者がフィルムを持ってくるが、その中に奇妙な短編が交じっていた。それは、マギーが、ある夜見た悪夢とまったく同じ内容だったのだ……。ハロウィンをポップな感覚で撮ったホラー。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wの悲劇", "Wの悲劇", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "三田静香は劇団“海（かい）”の研究生。人間的な成長がなければ芝居ができないと、役者として尊敬する五代淳にそのきっかけを作ってもらおうと身体を与えてしまう、積極的な一面をもつ女の子。恋人からのプロポーズも断り芝居に打ち込む日々が続いていたが、看板女優・羽鳥翔のスキャンダルの身代わりになったことで転機到来。静香は『Wの悲劇』のヒロインの座を手に入れ、幕が上がろうとする瞬間を迎えるが……。夏樹静子の同名小説を下敷きにしながら、その部分を劇中劇として、舞台裏の出来事にはさみ込んだ“バックステージもの”としての構成が素晴らしい。澤井監督も薬師丸ひろ子を大人の女優として脱皮させようと、マキノ雅広監督ゆずりのきめの細かな演出を施し、薬師丸の大人の女の魅力を引き出すのに成功している。なお、先輩女優のスキャンダルを新人が身代わりになってかぶるくだりは、アーウィン・ショウの短編『愁いを含んで、ほのかに甘く』からアイデアを借りていることがのちに確認されている。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Forbidden Quest", "恋の罠", "", "", "http://image.eiga.k-img.com/images/movie/53432/315x210.jpg?2389", 139, "朝鮮半島最後の王朝・李朝の宮廷で繰り広げられるスキャンダラスな恋の駆け引きを描いた歴史ドラマ。官吏として働く実直な青年ユンソは、ある日偶然目にした官能小説に強い興味を抱く。やがて官能小説家としての才能を開花させた彼は、自分に好意を寄せる王妃との禁断の情事を匿名で執筆するが……。2つの顔を併せ持つ青年ユンソを演じるのは「シュリ」のハン・ソッキュ。「スキャンダル」の脚本家キム・デウが初監督を務める。", "2008-04-05", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BECK", "BECK", "", "", "http://image.eiga.k-img.com/images/movie/54662/315x210.jpg?4160", 144, "ハロルド作石による人気コミックを、「20世紀少年」の堤幸彦監督が映画化。キャストには水嶋ヒロ、佐藤健、桐谷健太、中村蒼、向井理、忽那汐里ら若手人気俳優が揃う。平凡な男子高生コユキ（佐藤）は、ニューヨーク帰りの天才ギタリスト・竜介（水嶋）に偶然出会う。竜介は、ラッパーの千葉とベースの平を誘い、そこにコユキとドラムのサク（中村）を加えてバンド「BECK」を結成する。初めは戸惑うコユキだったが、バンド活動を重ねるうち、天性のギターの才能を開花させていく。", "2010-09-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JACK FROST", "ジャック・フロスト／パパは雪だるま", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 101, "雪だるまとなって愛する家族のもとに帰ってきた男の冒険を描くハートウォーミング・ファンタジー。妻子のもとに帰る途中で事故に遭い、世を去ったロック・ヴォーカリスト、ジャック。一年後、息子のチャーリーが作った雪だるまに、彼の魂が入り込んだ。チャーリーは予想もしない父との再会を喜ぶが、周囲の大人たちは少年の異変に不安を抱き……。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鉄火場慕情", "鉄火場慕情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "昭和初期の霞ヶ浦。土地の水運業を握る伊勢徳一家と、東京から乗り込んできた舛川組が激しく対立していた。伊勢徳の親分は、後継を養女・節子と代替の並木に継がそうとしたが、実の子に義理立てした節子は芸者となってしまう……。任侠ムードもあり、ラブ・ストーリーも入った人間ドラマ。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十日間の人生", "十日間の人生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "船長の父とその息子を主役に、父子の信頼と愛を描いた作品。父の船が入港の日、海に身投げした男が収容される。父は最初得意気だったものの、息子が自殺の現場を見ていたにもかかわらず、新しい服を汚したくなかったため救助に飛び込まなかったことを知ってしまう。そして、一挙に息子への不信感を募らせる。一方、息子も自分の非を気に留め始めて……。井上正夫の10数年ぶりのスクリーン復帰作。", "1941-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("深海獣雷牙", "深海獣雷牙", "", "", "http://ecx.images-amazon.com/images/I/513j8uaxFBL._SL210_.jpg", 82, "落語家の林家しん平が「深海獣レイゴー」に続いて放つ特撮怪獣映画の第2弾。深海獣レイゴーと戦艦大和の戦いから60年以上が経った現代。日本近海に謎の巨大生物が出現し、東京湾へと迫る。これを迎え撃つべく、政府から要請を受けた台東防衛隊がミサイル攻撃を開始。凄まじい戦闘によって浅草の町は炎に包まれ……。林家監督作には欠かせない俳優である螢雪次郎が主演を務めるほか、折山みゆら人気アイドルも多数出演。", "2009-10-03", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("どろんこ天国", "どろんこ天国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "「この世の花」10部作で知られる穂積利昌監督の人情もの。当時反響を呼んだ小学１年生の作文『私のお母ちゃん』をもとに、道化師の母を持った少年が、周囲の眼から恥ずかしさを覚え、それを乗り越えて母親への愛に目覚めていく様を描いた。", "1958-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("渡世無頼", "渡世無頼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "人気漫画の映画化。商社マンの良二は結婚式のさなかに、花嫁・美奈子と義父の清三郎を爆殺される。清三郎は関東叶一家の総長で、ヤクザの抗争に巻き込まれたのだった。良二は商社を辞め叶組を継ぎ、大学の同期で海外の外国人部隊の傭兵として活躍していた円野を雇い、対立する侠英連合に復讐するのだった……。愛憎入り乱れた人間模様をバイオレンスたっぷりに描いたハード・アクション。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Husbands", "ハズバンズ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 140, "　それぞれ仕事もあり、家庭も持っている3人の中年男、ハリー、ガス、そしてアーチーは親友の死という事件に直面する。ショックを受けた彼らはさんざんバカ騒ぎをするが、悲しみは癒えない。やがて3人はロンドンへ行き、そこで女性を誘惑する。しかし事はうまく運ばない……。そんな騒動の果てに、再び生きる気力を取り戻していく男たちの物語。幾度か編集し直され、サンフランシスコ映画祭で上映されたのは、153分版だった。", "1970-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白い牙", "白い牙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "六甲山の中腹にある洋館を舞台に、それぞれが愛人を持ち、お互いの絆を失っていく夫婦の姿を通して、人間同士の信頼が失われていく様を描いた井上靖の同名小説の映画化。佐分利信、牧紀子といった俳優陣の冷徹な演技もすご味を加え、反メロドラマとして強い印象を残す五所平之助後期の異色作。", "1960-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黒の試走車", "黒の試走車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "スポーツカーの新車売り出しをめぐってしのぎをけずる、二つの自動車メーカーをバックに、花形産業の舞台裏で暗躍する産業スパイたちの愛と憎しみを鋭く描き出した異色作。スパイ合戦のスリリングな展開と、花形産業にメスを入れた社会性がヒットの要因。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FAR COUNTRY", "遠い国", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "アラスカへ牛の群れを連れて旅をしてきたジェフは、目的の町の横暴な顔役ギャノンと衝突。町の人々を食いものにしているギャノンを倒すべく、ジェフは一人で立ち向かっていく……。西部劇ではあるが舞台はアラスカ。寒々と凍てついた大自然を背景に、どこか陰影を帯びた物語が語られていく異色作。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALSINO Y EL CONDOR", "アルシノとコンドル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "ラテンアメリカ映画人の全面協力のもとに作られた、チリ出身のM・リティン監督によるニカラグア初の長編劇映画。解放戦争真っ最中のニカラグアの小村。祖母と二人暮らしのアルシノは、いつも自力で空を飛ぶことを夢見る少年だった。ある日、彼と遊び友達の少女ルシアは、偶然軍事ヘリコプター“コンドル”号に乗る機会を得る。ゲリラ抑圧は大量虐殺にまで発展、民衆の怒りはますます広がっていった。そんなある日の嵐の去った明け方、アルシノは風の音に誘われるようにひときわ高い木に登り、大手を広げて空へ飛んだ!! それ以来、彼はせむしになってしまう……。少年の夢と現実、大人への成長を通して、現ニカラグア民衆の自由への戦いを高らかに謳歌。飾り気のないみずみずしい映像感覚が新鮮な感動を呼ぶ。1982年度アカデミー賞で外国語映画賞にノミネートされ、モスクワ映画祭では金賞を受賞した。", "1982-01-01", "77", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HIGH　VOLTAGE", "ハイボルテージ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "若き犯罪グループが、カリフォルニアの小さな銀行を襲撃。ところが彼らが奪ったのはマフィアの隠し金で、追いすがる組織との大乱戦が始まる。「ビッグ・ヒット」のA・サバトJr.が主演した威勢のいいバイオレンス・アクション。アクロバティックな銃撃戦や珍妙なジョークを矢継ぎ早に繰り出し、クライマックスまで一気に見せる。ブルース・リーの娘シャノンが主人公と恋に落ちるヒロインを熱演。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Father", "マイ・ファーザー", "", "", "http://image.eiga.k-img.com/images/movie/54852/315x210.jpg?2623", 109, "韓国のドキュメンタリー番組で取り上げられ話題を呼んだ感動の実話を基に、幼少時に養子に出された青年と生みの両親との再会や苦悩を描いた人間ドラマ。5歳の時に韓国からアメリカへと養子に出されたジェイムズ・パーカー。時が経ち立派な青年へと成長した彼は、両親に会いたいという積年の想いを叶えるため在韓米軍に志願して韓国へ渡る。主演は「私の名前はキム・サムスン」などの韓国系アメリカ人俳優ダニエル・ヘニー。", "2009-12-12", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Il ferroviere", "鉄道員", "", "", "http://ecx.images-amazon.com/images/I/51C-cIXYQ6L._SL210_.jpg", 115, "イタリアの社会派ピエトロ・ジェルミ監督の代表作。鉄道員として働く初老の父親アンドレアとその家族の姿を、幼い末っ子サンドロの視点から描くホームドラマ。第2次世界大戦後のイタリア。ある日、アンドレアが運転する列車に若者が投身自殺を図る。しかもそれが原因で衝突事故を起こしかけ、左遷されてしまう。一方、家庭では長女ジュリアが流産、続けて長男マルチェロが家出をする。アンドレアは酒に溺れるようになり……。製作はフェリーニの「道」を手掛けたカルロ・ポンティ。", "1958-12-31", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MOONLIGHT　EXPRESS", "もういちど逢いたくて・星月童話", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "L・チャンと常盤貴子の共演が話題となったロマンティック・ラブ・ストーリー。婚約者を事故で亡くした瞳は結婚後に住むはずだった香港を訪れ、彼とよく似た男ガーポウと出会う。そして翌日、ケガをしたガーポウが瞳のアパートに転がり込んでくる。麻薬捜査官の彼は潜入捜査中に銃で撃たれたのだ。必死に看病する瞳は回復した彼に今までのいきさつを話し、一日だけ恋人の代わりにデートしてほしいと頼む……。", "1999-01-01", "6,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE 4D MAN", "4Dマン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "自分の腕があらゆる物体を突き通してしまうことを偶然発見した科学者が、それを突き詰めていくうちに邪悪な行動を身につけ、それとともに自らの急激な衰えに気付いて愕然とし……。一風変わったパートカラーSF。", "1959-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Jack the Giant Killer", "ジャック・ザ・ジャイアント・キラー（原題）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "", "2012-08-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Future X-Cops", "未来警察 Future X-cops", "", "", "http://image.eiga.k-img.com/images/movie/58072/315x210.jpg?1438", 100, "アンディ・ラウ主演でワイヤーアクションとVFXを駆使して描く近未来SFアクション。西暦2080年、太陽エネルギー運用システムを開発したマー博士により、世界のエネルギー事情は改善されたが、大きな損失を受けた石油企業が博士の暗殺を計画。政府が極秘裏に開発したタイムスリップ技術を盗み、子ども時代の博士を殺そうと60年前の世界にサイボーグを送り込む。この情報をつかんだ当局もエリート警官のジーハオをサイボーグに改造し、過去の世界に向かわせる。ジーハオ役のアンディ・ラウほか、ファン・ビンビン、バービー・スーらアジアの豪華キャストが共演。", "2012-05-19", "6,53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DER TODES KING", "死の王", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "ドイツ国内では上映が禁止され、ネガの廃棄処分となったが、監督自身が極秘にネガを持ち出してニュープリントを作成した作品。月曜日、一人の男が睡眠薬を飲んだまま風呂に入って自殺する。徐々に腐っていく男の肉体の映像にはさまれて、男の遺書を受け取った様々な人々の自殺や殺人が描かれていく。「ネクロマンティック」に続くJ・ブットゲライトの問題作。", "1989-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ニューキッズオンザゲリラ", "ニューキッズオンザゲリラ", "", "", "http://image.eiga.k-img.com/images/movie/57432/315x210.jpg?3595", 15, "無名の若手監督たちが作り上げた15分の短編10作品を特集上映する「NO NAME FILMS」の1本。昼間はタクシードライバーで夜は男娼として働くゲイのステラは、ある日、女の死体を抱えたヤクザ風の男、我妻に出会う。我妻もステラと同じく寂しさにまみれた男で……。監督は、「白昼のイカロス」（2009）が第32回ぴあフィルムフェスティバルで審査委員特別賞を受賞した阿部綾織と高橋那月の女性コンビ。", "2011-11-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("待ち伏せ", "待ち伏せ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "冬、寒風吹きすさぶ峠の茶屋に、得体の知れない５人の男女が現れる。それぞれの素性が明らかにされるにつれて、老中・水野越前の外様大名つぶしの陰謀が浮かび上がってくる。サスペンスたっぷりのアクション巨編。戦後映画界を代表する大スターが揃って出演。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パルチザン前史", "パルチザン前史", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1968年の京大全共闘運動における、大学助手・滝田修と彼を囲む学生たちの言動をアクチュアルに記録した長編ドキュメンタリー。一人の理想家肌のノンセクト・ラジカルの闘争、アジ活動から家庭での表情までを、観客的なニュース報道ではあり得ない、撮られる側との信頼関係に基づく至近距離で捉えて、異彩を放つ。小川紳介、田村正毅など小川プロのスタッフが撮影に協力している。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Pursuit of Happyness", "幸せのちから", "", "", "http://image.eiga.k-img.com/images/movie/33852/315x210.jpg?2077", 117, "医療機器事業の失敗によりホームレスとなったが、超難関の株トレーダー養成コースを経て一流証券会社に就職し、アメリカンドリームを成し遂げた実在の人物（クリス・ガードナー）の半生と彼を支えた息子との心の交流を描く人間ドラマ。息子役には主演ウィル・スミスの息子ジェイデン・クリストファー・サイア・スミスが抜擢。監督はイタリア映画界で活躍し、本作でハリウッドデビューを飾ったガブリエレ・ムッチーノ。", "2007-01-27", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("セラフィムの夜", "セラフィムの夜", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "“愛と暴力”をテーマとした作家活動を続ける花村萬月の同名小説を映画化。遺伝子の先天性異常により両性具有であることを知った女性の愛と苦悩を描く。偶然に自分が両性具有だと知ってしまった涼子は、彼女に偏愛を抱く美大の後輩、大島博之のアトリエで、両性具有の天使像の絵を見せられ、犯されてしまう。自我も肉体も汚された涼子は博之を刺すが、とどめをさしたのは彼の異母兄弟の山本だった。右翼で韓国人の母を持つ山本もまた、涼子と同じく自らのアイデンティティーの崩壊に悩んでいた。そんな二人は、韓国・釜山へと旅立つが……。", "1996-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("エノケンの拳闘狂一代記", "エノケンの拳闘狂一代記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "多作、早撮りで有名な渡辺邦男の、当時しばしばコンビを組んでいたエノケンものの１本。実の父が子供に真実を語ることができないために起こる騒動を描いた父性愛喜劇だが、エノケンの軽やかな身のこなしが生かされず、表情のみに頼った演技になっていると批評された。", "1949-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("女渡世人", "女渡世人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "藤純子主演の“女渡世人”ものの第１作。生き別れの母を探し求めながら渡世修行の旅を続ける女渡世人・妻恋いお駒は子連れの流れ者に淡い恋心を抱くが……。“緋牡丹博徒”シリーズに比べて、女性の悲しさや肉親愛が強調された作り。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DR.BUTCHER，M.D.", "人間解剖島／ドクター・ブッチャー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "死体臓器摘出事件を追って、ニューヨークから南米の島へやって来たローリー女医とチャンドラー博士。温厚そうなブッチャー博士が彼らを迎えたが、彼こそゾンビ製造の野望を持つ黒幕。内臓食い、皮はぎ、舌切りなど、イタリア的残酷描写に心血が注がれている。", "1981-01-01", "1,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TOP LINE", "アステカ・アドベンチャー 宇宙の秘宝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "離婚して以来、今いちノリの悪いジャーナリストのアンジェロは、特ダネを追って南米コロンビアへ向かった。偶然にも、そこで古代アステカ文明時代のナイフを手に入れた彼は、財宝とスクープの一挙両得を目指して、宝探しに乗り出す。ところが、行動を起こした彼の周りの人々が次々と変死し始めて……。イタリア産のB級冒険活劇。", "1988-01-01", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ポストマン", "ポストマン", "", "", "http://image.eiga.k-img.com/images/movie/53382/315x210.jpg?2315", 111, "美しい自然が広がる千葉県房総町を舞台に、手紙がつなぐ人と人との心の絆を綴ったハートフルドラマ。手紙をいち早く確実に届けることを誇りとしている郵便配達員・龍兵。2年前に妻を亡くしてからは中学生の娘と小学生の息子を男手ひとつで育ててきたが、進学を巡り娘との間に深い溝ができてしまう。そんな中、龍兵は配達中に老人が自宅で倒れているのを発見。そのポケットの中には1通の手紙が入っていた。長嶋一茂が主演と製作総指揮を務める。", "2008-03-22", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L’AMOUR AVEC DES SI…", "行きずりの二人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "シトロエンでドライブウェイをひた走る男。途中、カフェで会った女を乗せ、二日間をともにするが……。淡い愛のドラマの中に痛烈な社会風刺を織り込んだサスペンス。", "1962-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かまち", "かまち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "表現欲のかたまりともいうべき生き様が伝説化している山田かまちの半生を、アウトサイダーの悲哀を描き続ける望月六郎監督が映画化。過剰な自信と現実の挫折にもがき苦しみながら、詩や絵、音楽に表現衝動をぶつけて17歳で感電死した彼の姿を、現在を生きる若者たちの苦悩と迷走を交えつつ見つめる。主演は、ダンスユニット“Lead”の谷内伸也。70年代半ば、中学校に通う山田かまちは、自己表現したい気持ちを、絵や詩、音楽に昇華させて毎日を過ごしていた。“１日が24時間じゃ足りない”ほどにやりたいことがたくさんあった彼にも、高校受験という厳しい現実が待っていた。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DEATH MASK", "ザ・ハロウィン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "1979年製作のジョン・カーペンター監督作「ハロウィン」から、すっかりホラー色の強くなってしまったハロウィンの夜。この映画もまた、ハロウィンに起こる惨劇を血なまぐさい映像でみせる、おなじみのミステリー・ホラー映画。殺人鬼によってのどを切り裂かれた女、スコップが頭に突き刺さり胸をえぐられた美少女などのスプラッタ・シーンが、ある一家の秘密と絡めて描かれる。", "1987-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("逃亡列車", "逃亡列車", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "終戦直後の満州＝朝鮮国境沿いの部隊に赴任してきた裕次郎扮する少尉が、終戦後に抗日ゲリラと戦いながら、避難民を輸送する鉄道を守る。最後の避難列車が爆破され、古い機関車を修理して決死の脱出行を試みる裕次郎の手に汗握るアクションが見どころ。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GRANDVIEW U.S.A.", "グランドビューU.S.A.", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "C・トーマス・ハウエル主演で描いた青春映画。高校卒業を控えたティムは、カーレース場のオーナーである美しい女性マイクに出会い、恋に落ちる。一方、不動産業を営むティムの父はレース場買収を画策、親子が激しく対立する。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("花城／THE LAST AFFAIR", "終わらない愛を探して", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "友人の結婚式のため、パリを訪れた人妻が、情熱的な青年と出会い、やがて二人は恋に落ちる……。パリを舞台に、留学生の青年と人妻が激しく愛し合う道ならぬ恋を描くラブ・ストーリー。T・ホウ監督が、異国情緒あふれるロマンティックな作品に仕上げている。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("РУССКИЙ　КОВЧЕГ", "エルミタージュ幻想", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "ロシアが生んだ天才映像作家、アレクサンドル・ソクーロフがワンカットで撮り上げた前代未聞の作品。彼自身の語りとフランス外交官キュスティーヌ伯爵をモデルにした男が案内役を務め、エカテリーナ大帝やニコライ２世が登場した300年にわたるロシアの近世、近代史をたどる。ロシアが世界に誇るエルミタージュ美術館でロケを敢行。美しい館内の光景も必見だ。", "2002-01-01", "24,7,76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CHILDREN OF THE NIGHT", "バンパイア・タウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "タイトル通り、吸血鬼（しかもゾンビ!?）だらけになってしまった街で、教え子の少女を救おうと奮闘する青年教師。モンスターの大群の中に、まともな人間は二人だけという、おなじみB級スプラッタ・ホラーの“王道”をいく作品だ。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BUNSHINSABA", "コックリさん", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "子供時代の恐怖体験として誰もが覚えがあるであろう“コックリさん”を題材にした韓国ホラー。田舎の村に転校してきた女子高生の恨みがコックリさんを呼び、村に恐るべき災いをもたらす。「ボイス」のビョンギ監督による恐怖描写はもちろんだが、大勢登場する美少女たちの競演も見もの。閉鎖的な村の女子高に転校したユジンは、同級生のイジメに苦しみ、“コックリさん”で呪いをかける。すると校内で次々と怪事件が……。", "2005-04-23", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("I　AM　DAVID", "アイ・アム・デビッド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "世界的ベストセラーとなったアン・ホルムの同名小説を映画化。自由を勝ち取るために収容所を抜け出した12歳の少年の、長く過酷な旅をスリリングに描く。さまざまな経験と出会いを通して、勇気と希望、信頼の大切さを学んでいく少年。その微妙な心の変化を新星ベン・ティバーが見事に演じきった。第ニ次大戦直後、共産主義が支配するブルガリア。強制収容所を脱走した12歳のデビッドは、母との再会を夢みて、遠く離れたデンマークへと向かう。", "2005-02-19", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Charlie: The Life and Art of Charles Chaplin", "チャーリー・チャップリン　ライフ・アンド・アート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 131, "時代を超えて世界中の人々から愛され続ける喜劇王チャーリー・チャップリンをテーマにしたドキュメンタリー。ウッディ・アレン、マーティン・スコセッシ、ジョニー・デップ、ロバート・ダウニー・Jr.らチャップリンに影響を受けた映画人へのインタビューや遺族による証言、さらに未公開映像などの貴重な資料を織り交ぜながら、チャップリンの輝かしい経歴のみならず、知られざる私生活にまでスポットを当てていく。", "2007-12-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FORGOTTEN　SILVER", "コリン・マッケンジー／　もうひとりのグリフィス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "「ロード・オブ・ザ・リング」３部作のP・ジャクソン監督率いる撮影クルーが、ニュージーランド南島の険しいジャングルに埋もれた巨大な映画用セットを発掘する。それは20世紀初頭、映画に人生を捧げ、歴史超大作「サロメ」の完成に執念に燃やしたコリン・マッケンジー監督が建設したものだった。現場から見つかったフィルムに映っていたものとは……。ニュージーランドに映画の父グリフィスに匹敵する歴史的な大人物がいた、という架空の大発見を映像化した異色作。事実とフィクションの垣根を大胆に飛び越え、観る者に驚くべき映画の夢を体験させてくれる。リバイバル時のタイトルは「光と闇の伝説／コリン・マッケンジー」。", "1996-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("黄飛鴻", "ワンス・アポン・ア・タイム・イン・チャイナ　天地黎明", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "19世紀半ばに実在した、希代の英雄・黄飛鴻（ウォン・フェイフォン）。この香港映画史上最も有名なキャラクターを、ツイ・ハーク＆リー・リンチェイという夢のコンビが、まったく新たに映像化した作品。現代アクション映画に押されていた香港映画界に、新たに“古装片”ブームを巻き起こした。舞台は英米列強が幅を利かせる動乱の時代の中国。英軍の陰謀を叩こうとする黄の前に、最強最大の武道家が立ちはだかる……。ツイ・ハーク得意のワイアー・アクションが冴える功夫シーンが最大の見どころ。許婚の若い叔母役のロザマンド・クワンが花を添える。", "1991-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("極道修業 決着〈おとしまえ〉", "極道修業 決着〈おとしまえ〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "日本映画「眠る男」にも出演した韓国を代表する名優アン・ソンギと「トゥー・カップス」で彼と共演したパク・チュンフン、それに哀川翔らの日本人俳優がからんだ、オール日本ロケによる日韓合作の極道映画。それぞれの事情で韓国にいられなくなり、日本にやって来たソンチョルとヘグは暴力団の張本組の世話になる。韓国組織への復帰を夢見るソンチョルだったが、ヒットマンとして生きるしかない自分の運命に虚無感を覚えていく。一方、ソンチョルに憧れて極道の世界に染まっていくヘグはいつしか彼とは違う生き方を選び、張本組の組長を暗殺して敵の組に寝返ることを決意する。二人の間に深い溝が生まれたとき、決着をつけるのは銃弾しかなかった。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TAKE OFF 国家代表", "国家代表!?", "", "", "http://image.eiga.k-img.com/images/movie/55662/315x210.jpg?3332", 146, "韓国初のスキージャンプチームの実話を元に映画化し、国内で860万人を動員する大ヒットを記録したコメディ映画。冬季オリンピック誘致のため、韓国では急きょスキージャンプの代表チームが結成されることに。ところが集まったのはジャンプ未経験の落ちこぼればかりで……。「カンナさん大成功です！」のキム・ヨンファが監督を務め、キャストには「チェイサー」のハ・ジョンウ、ドラマ「コーヒープリンス1号店」のキム・ドンウクら人気俳優がそろう。", "2010-10-23", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SATURN3", "スペース・サタン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "「スター・ウォーズ」の美術監督、J・バリーの原案によるSFサスペンス。西暦2000年、太陽系惑星を支配していた人類のもとへ、何者かが意思と感情を持つ不気味なサイボーグ・ロボットを送り込み、戦いを挑む。土星の衛星にある研究所の所員も決死の覚悟で臨むが……。精密に作られたロボットの暗躍が印象的な一編。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("誘惑者", "誘惑者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 109, "精神科医・外村のもとにある日、一人の女が訪ねてくる。同居人のことで悩んでいるという彼女に興味を持った彼は、彼女に接近。やがて彼女が完璧な二重人格者だとわかるのだが……。長崎俊一が日本人初の1986年のサンダンス・インスティテュート（ロバート・レッドフォード主宰の映画塾）での経験を経て完成させたサスペンス・ラブ・ストーリー。第３回東京国際映画祭ヤングシネマ部門では、さくらシルバー賞を受賞した。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　CENTER　OF　THE　WORLD", "赤い部屋の恋人", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "「スモーク」が高い評価を集めたウェイン・ワン監督と、作家のポール・オースターが再び顔を合わせた、愛と官能の物語。偽りの愛の関係を結んだ男女に芽生えた複雑な感情を見つめる。コンピューター・エンジニアとして成功を収めたリチャードは、すべてが順風満帆。しかし、彼は愛する人を得られず空しさを感じていた。そんな折、彼はストリッパーのフローレンスと出会い、彼女と報酬付きでラスベガス旅行に向かう。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("劇場版メタルファイト　ベイブレードVS太陽　灼熱の侵略者ソルブレイズ", "劇場版メタルファイト　ベイブレードVS太陽　灼熱の侵略者ソルブレイズ", "", "", "http://image.eiga.k-img.com/images/movie/55172/315x210.jpg?2920", 0, "「コロコロコミック」で人気を博す「メタルファイト ベイブレード」の劇場版。惑星アドニスが遠い宇宙から地球に飛来し、地上ではさまざまな異常気象が起こり始めていた。一方、大好きなベイブレード大会に没頭する銀河たちの前に、古代より伝わるベイブレード“ソルブレイズ”を持った謎の少年ヘリオスが立ちはだかる。そして2人が激しい戦いを繰り広げている最中、さらなる危機が世界を襲う……。", "2010-08-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("かぞくのくに", "かぞくのくに", "", "", "http://image.eiga.k-img.com/images/movie/57222/315x210.jpg?2059", 100, "ドキュメンタリー「愛しきソナ」で知られる在日コリアン2世のヤン・ヨンヒ監督が、自らの体験を題材に、国家の分断によって離れ離れになった家族が傷つきながらもたくましく生きていく姿を描いたドラマ。北朝鮮の「帰国事業」により日本と北朝鮮に別れて暮らしていた兄ソンホと妹リエ。病気療養のためソンホが25年ぶりに日本へ戻り、2人は再会を果たす。異なる環境で育った2人がともに暮らすことで露呈する価値観の違いや、それでも変わらない家族の絆を描き出していく。妹リエに安藤サクラ、兄ソンホに井浦新（ARATA）。", "2012-08-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("現代仁侠道兄弟分", "現代仁侠道兄弟分", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "新宿から赤線の灯が消え、引退を宣言した竜崎組組長。だがそれは隠れみので、彼は新しく設立した会社の社長に収まり、裏から組を操っていこうとしていたのだった。義理と人情を重んじる幹部の五十嵐はそうとは知らずに賭場の手入れの際、体を張って客を逃がし、務めを終えて出て来ると状況は一変していて……。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GET SHORTY", "ゲット・ショーティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "マイアミで取り立て屋をしているチリは大の映画好き。映画俳優が着ていたものと同じ革のコートをギャングのレイに持っていかれ、怒ったチリは彼を殴り倒してコートを取り返す。プライドを傷つけられたレイはチリに復讐を試みるが、何度も失敗。そのうちチリの組織が自分の組織の傘下に入ったため、チリに自分に従うよう命令する。最初に言いつけられたのは、クリーニング屋の借金の取り立て。飛行機事故で死んだと思われていた店主の行方を捜しているうちに、チリは映画プロデューサーのハリーに会う。意気投合した二人の間で、店主の話を映画化する話題で盛り上がるが……。犯罪小説で有名な作家E・レナードが書き上げたコメディを映画化。シャレた味わいの小粋な作品に仕上がっている。", "1995-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("代紋・男で死にたい", "代紋・男で死にたい", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "日活任侠映画の雄・高橋英樹主演による、“代紋”ものの１本。関東大震災で両親を失った兄弟は梧井組に引き取られるが、陸軍物資納入の利権を狙う本間組に兄を殺され、弟・鉄五郎が仇討ちのために殴り込む……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Journey to the Center of the Earth 3D", "センター・オブ・ジ・アース", "", "", "http://image.eiga.k-img.com/images/movie/53482/315x210.jpg?4610", 92, "東京ディズニーシーのアトラクションでも有名な冒険SF文学の巨匠ジュール・ベルヌの「地底旅行」を、数々のハリウッド大作の特殊効果を手掛けてきたエリック・ブレビグ監督が全編フルデジタル3Dで映画化。兄の遺志を継いで地球内部のマントルを貫く裂け目を探す地質学者トレバー（ブレンダン・フレイザー）はアイスランドの火山を調査中に山麓の洞窟にあった深い竪穴に落ちてしまう。するとそこには太古の植物や動物が生息する驚異の世界が広がっていた……。", "2008-10-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRAUMA", "トラウマ 鮮血の叫び", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "精神病院を脱走し、自殺しようとしていた拒食症の美少女オーラを助けたデビッドは、彼女に異様な何かを感じる。彼女が家に戻ったその夜、母親が主催する降霊会が開かれた。そこで彼女は、母親の身体に乗り移った霊によって、両親が首を切断されるという残虐な現場を目撃してしまう。恐怖にうちのめされる彼女が頼れるのはデビッドしかいなかった。二人で犯人捜しを始めるが、謎の首切り殺人鬼は第２、第３の犯行を重ねていく……。「サスペリア」で日本中を恐怖の渦に巻いたD・アルジェントらしい作品。事件のカギを握る美少女オーラ役に、監督の愛娘A・アルジェントを起用している。特殊メイクは、「ゾンビ」のトム・サヴィーニ。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("百合祭", "百合祭", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "老年女性の性と愛を描き、高い評価を得た同名小説を映画化。洋館アパートに住む７人の老女が、ある日引っ越してきたお洒落でダンディなおじいさんを巡ってドタバタの恋愛劇を展開。“性”の可能性を取り戻し、希望を見いだしていく彼女たちの姿は、高齢化社会への新たな価値観を与えてくれる。７人の老女が暮らす洋館アパートに、ダンディーな老人・三好さんが越してくる。自分たちを立派なレディーとして扱ってくれる彼に一同は魅了されてしまう。", "2004-12-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("呪いの館・血を吸う眼", "呪いの館・血を吸う眼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 82, "少女時代の恐ろしい幻想に悩まされる秋子は、妹の夏子と二人で富士見湖畔に住んでいた。レストハウスのボーイ・久作は何かと二人に親切にしてくれたが、久作のもとに大きな柩が届けられた夜から秋子の幻想は現実のものとなっていく……。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mother Teresa: The Legacy", "マザー・テレサ　メモリアル　母なるひとの言葉", "", "", "http://image.eiga.k-img.com/images/movie/34192/315x210.jpg?1548", 55, "86年に製作された「マザー・テレサ　母なるひとの言葉」のスタッフが再結集したドキュメンタリー。97年に87歳でこの世を去ったマザー・テレサ。インド政府は彼女の功績を称え、その葬儀を異例の国葬として執り行った。盛大な葬儀の模様や、生前のマザー・テレサ本人が語る貴重なインタビュー映像を収録するほか、彼女を看取った側近の修道女たちによって、死の迫ったマザー・テレサが遺した言葉が明かされる。", "2007-09-15", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("支那事変海軍作戦記録", "支那事変海軍作戦記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "昭和12年、盧溝橋事件をはじめとする排日勢力の拡大に対抗して、太沽攻撃で第一弾を放った日本海軍。蘆溝橋事件から武漢攻略までに海軍が行った作戦行動の記録映像。上海租界、居留民の避難や大山大尉の葬儀の様子などの映像資料も満載。", "1939-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Switch", "アラフォー女子のベイビー・プラン", "", "", "http://ecx.images-amazon.com/images/I/51MBg1EwjsL._SL210_.jpg", 0, "ジェニファー・アニストン、ジェイソン・ベイトマン共演のコメディドラマ。ニューヨークで働くウォーリー（ベイトマン）は長年の親友キャシー（アニストン）が人工授精で子供を産むと聞き、ショックを受ける。キャシーの人工授精を控えたある日、病院で開かれたパーティーで酔っ払ったウォーリーは、トイレでドナーの精液サンプルをこぼしてしまい、急場しのぎで自分の精液とすり替えてしまう。それから7年後、ニューヨークに帰ってきたキャシーに再会したウォーリーは、彼女の子供が自分の子供だと確信する……。監督は「俺たちフィギュアスケーター」のジョシュ・ゴードンとウィル・スペック。", "2010-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新書・忍びの者", "新書・忍びの者", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "シリーズ最終作となったこの作品は霞小次郎という忍者が主人公。父を惨殺した３人の忍者への復讐を、武田信玄と徳川家康の三方ヶ原の戦いを背景に描く。第１作での怪演が話題となった伊藤雄之助が再び登場、作品に厚みが増した。妖術使いまで現れる娯楽作。", "1966-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悪魔の手毬唄", "悪魔の手毬唄", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 144, "文明社会から隔離された鬼首（おにこべ）村。この村に代々伝わる手毬唄通りに殺人事件が起こる。どうやら謎はこの村の二大勢力、由良家と仁礼家の確執にあるらしいが、金田一の捜査が進まぬうちに第２、第３の犠牲者が……。今回は犯人探しの面白さよりも、多分に情緒に訴えるストーリーと絵作りが強調されている。中年刑事・磯川のヒロイン・リカに対するほのかな恋心、色を抑えたシックな映像、さらには名作「モロッコ」の部分使用など、監督のセンスがキラリと光る傑作。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("戦後秘話 宝石略奪", "戦後秘話 宝石略奪", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "闇屋の大原を助けたことから彼の相棒となった山田慎一。大原は政界の黒幕、岡村に政治献金を流して、製鋼会社の社長におさまる。ひょんなことから時価50億のダイヤ“大蒙古”を手に入れた岡村は、彼の理想とする大アジア主義のために使おうと、“大蒙古”をシンガポールに届けるよう、山田に頼むのだが……。戦後の動乱を背景に描く、色と欲に目がくらんだ人々の、ドロドロとした人間ドラマ。", "1970-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャンケン娘", "ジャンケン娘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "当時人気抜群の３人娘（美空・雪村・江利）が唄い踊る楽しい作品。東京の女子高生ひばりとチエミが修学旅行で訪れた京都で舞妓のいづみと知り合い、３人は名前しかわからないハンサム青年を捜し回る、という他愛のないお話。", "1955-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Samurai Avenger: The Blind Wolf", "サムライアベンジャー　復讐剣　盲狼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "米TVシリーズ「HEROES　ヒーローズ」「アグリー・ベティ」に出演する光武蔵人が、監督・主演を務めるバイオレンス・アクション。ある家族が砂漠のオアシスでピクニックを楽しんでいると、犯罪組織のボス、ネイサン・フレッシャーが現れる。夫は言われるがまま両目をつぶして命ごいするが、目の前で妻と娘を惨殺され、自身も瀕死に追いやられる。8年後、男はフレッシャーへの復しゅうを誓い、盲目の剣士“盲狼”として砂漠へ戻る。フレッシャーは7人の刺客を雇い、 “盲狼”を待ち受ける。", "2010-08-21", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("アブバとヤーバ", "アブバとヤーバ", "", "", "http://image.eiga.k-img.com/images/movie/54652/315x210.jpg?2874", 96, "日本とスーダンの老人たちの人生を、「親と子」「戦争」「肉親の死」といった共通テーマで切り取った異色ドキュメンタリー。スーダンの子供たちに自身の戦災体験を重ねる77歳の日本人女性と、彼女の文通相手である11歳のスーダン人少年、内戦やエイズで多くの子供たちを失ったスーダン南部の町で残された孫の世話をする老夫婦、千葉県の農業地帯でひとり暮らしをする85歳の男性といった4組の日常を追う。", "2009-07-04", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("告白的女優論", "告白的女優論", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "岡田茉莉子、浅丘ルリ子、有馬稲子という３人の女優それぞれを主人公とするエピソードで構成。撮影開始を前に、失語症、自殺癖、性的恐怖など、様々なトラブルが彼女たちを襲う。吉田監督は、すべてが虚構の産物である女優という存在を鋭く描き出す。DVDは「吉田喜重全集 [68-73] 性と政治の季節」に収録。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ゴジラVSデストロイア", "ゴジラVSデストロイア", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "シリーズ最高配収を記録した「ゴジラVSモスラ」のスタッフが集結した最終作。第１作で登場したオキシジェンデストロイヤー（水中酸素破壊剤）が、41年の歳月をかけて生み出した新怪獣デストロイアとゴジラが東京で壮絶な闘いを展開する。太平洋上の大戸島を巨大生物が襲撃。古生物学者の山根博士は、娘の恵美子とその恋人・尾形とともに調査に向かい巨大生物を発見し、ゴジラと命名した。国民が怯える中、ゴジラは東京に上陸、首都を完全に破壊し東京湾へと去っていく。ゴジラを抹殺できるのはオキシジェンデストロイヤーしかないと悟った恵美子は、芹沢博士との約束を破り尾形にこの秘密を打ち明けるのだが……。", "1995-12-09", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Submarine", "サブマリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "アメリカ海軍に所属するジャックとボブは大親友。いつも行動を共にし固い絆で結ばれていた2人だったが、ボブがジャックの妻と一夜を共にしてしまったことから仲違いする。ある日、ボブの乗った潜水艦が事故を起こし海底深く沈んでしまう。彼を救うことができるのは自分しかいないと知ったジャックは……。監督をつとめるのは、後に「或る夜の出来事」「素晴らしき哉、人生！」などの作品で成功をおさめたフランク・キャプラ。", "1928-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BOYS", "DOWN UNDER BOYS", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 86, "実話に基づいた舞台の映画化作品。シドニー郊外のある寂れた町。傷害事件で投獄されていたブレッドが仮出所で故郷に戻ってきた。彼の不在の間、家族の関係は大きく変化していた。彼はかつての支配力を取り戻すと、弟二人を引き連れて、夜の町へと繰り出す。その途中で、彼らはバス停で一人の女性を見つける。果たして、彼らのとった行動とは？　盗撮のような画面が緊張をより盛り上げている。", "1998-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE CARS THAT ATE PARIS", "キラーカーズ —パリを食べた車—", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "オーストラリアの片田舎で交通事故を起こして入院した主人公、だがそれは仕組まれたものだった。村人たち全員が獲物を狙うように通りすがりの車に事故を起こさせていたのだ。P・ウィアー監督、オーストラリア時代のデビュー作。", "1974-01-01", "11", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MIRACLE OF THE BELLS", "奇蹟の鐘", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 120, "1946年にベストセラーとなった同名小説の映画化。スターを目指しドサ回りを続ける女優オルガは、ハリウッドの腕利き宣伝マン、ビルに認められ映画に主演することになるが、映画完成とともに彼女もその生涯を終え……。人間愛を謳いあげる感動作。", "1948-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("野盗風の中を走る", "野盗風の中を走る", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 111, "戦国時代初期。東の国を暴れ回る野盗一味がいた。独眼の太郎を首領とする彼らは、豪族を襲撃して得た金を持って八重山の郷に向かう。領主による悪税、労役によってすたれた村の様子に、彼らは村人を救うため立ち上がる。時代劇の雄、稲垣浩によるダイナミックな演出も見もの。", "1961-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("人間蒸発", "人間蒸発", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "今村昌平が初めて撮った劇場用ドキュメンタリー。佳江さんは俳優の露口茂の手を借りて、行方不明の婚約者を追う。露口の役割は、今日でいうレポーターとは性格を異にしていて、作者＝今村昌平の分身かつ佳江さんのパートナーとしての役割を担っている。婚約者の足跡を追ううちに、浮かび上がってくる新事実と佳江さんのリアクションにカメラは肉迫。取材される女性が取材者の露口に私的な興味を抱き始めて、事態が思わぬ方向にねじ曲がっていくさまなど、“カメラは事実に対して中立を貫く”といった優等生的発想からは、決して捉えられない興趣がある。ドキュメンタリーの可能性と落とし穴を明確に示し、公開当時反響を呼んだ。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("そうかもしれない", "そうかもしれない", "", "", "http://image.eiga.k-img.com/images/movie/33672/315x210.jpg?4664", 106, "芸術選奨文部大臣賞を受賞した私小説作家・耕治人による原作を、映画初主演となる落語家・桂春團治と歌手の雪村いづみ共演で映画化。文筆家の高山治と彼の妻ヨシ子は、平穏な毎日を過ごすごく普通の老夫婦。しかし、ある日突然、ヨシ子が認知症になってしまう。治は少しずつ自分を見失っていく妻を見守ることしかできないでいた。やがて、彼もガンであることが発覚し、2人は特別養護老人ホームと病院で別々に暮らすことになる。", "2006-09-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE AGONY AND THE ECSTASY", "華麗なる激情", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 139, "I・ストーンによるミケランジェロの伝記小説『苦悩と恍惚』を、C・リードが映画化したもの。1508年、法王からシスチネ礼拝堂のフレスコ画を依頼されたミケランジェロはなかなか筆が進まず悩む。いったんはローマから姿を消すが、やがて激しい情熱が彼をつき動かし始める。", "1965-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Gibier d'Elevage", "飼育", "", "", "http://image.eiga.k-img.com/images/movie/57342/315x210.jpg?3532", 110, "", "2011-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("幽霊男", "幽霊男", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "70年代の金田一耕助ブームは第２次横溝正史ブームといえた。それより以前の50年代に横溝第一次ブームがあり、映画化作品が頻繁に作られていた。そんな中で、現在では埋もれてしまっていて発掘された名作が本作である。幽霊男と自称する男がモデルクラブの女と会った夜、女は幽霊男の部屋で裸体で殺された。袴ではなく背広姿の金田一探偵は、このモデル連続殺人を追う。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("四つの恋の物語", "四つの恋の物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 112, "題名の示す通り、豊田四郎・成瀬巳喜男・山本嘉次郎・衣笠貞之助という４人の監督によるオムニバス作品。女学生と高校生の淡い恋心を描く「初恋」（豊田）、愛する男の心を察してわざと愛想づかしを言い、男のもとを去っていく女を描く「別れも愉し」（成瀬）、三枚目役者と踊り子の他愛ない恋を描く「恋はやさしく」（山本）、空中ブランコ乗りの命賭けの恋を描く「恋のサーカス」（衣笠）の４話からなる。", "1947-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ROBOCOP3", "ロボコップ3", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "近未来のデトロイト。超未来都市デルタ・シティを建設するための悪徳地上げが横行し、市民は生活もままならない。その悪に対決すべく、ロボコップが天才コンピュータ少女ニコと組んで立ち上がった！ しかし敵も最新技術による強力ロボットで応戦する……。無敵のメカヒーローが活躍するシリーズ第３作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE LONG WALK HOME", "ロング・ウォーク・ホーム", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "W・ゴールドバーグとS・スペイセク、二人のオスカー女優が共演した社会派の人間ドラマ。1955年、アメリカ南部の町で黒人のメイド、オデッサは公民権運動に参加し、通勤バスをボイコット。これに同情した雇い主の白人主婦ミリアムは彼女を車で送り迎えするが、これが周囲に思わぬ波紋を呼ぶ……。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE HUMAN SHIELD", "アイアン・シールド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "かつてイラク軍の試験教官だったダグは、今ではCIAの特殊部隊員をしていた。彼のもとへ兄の消息不明の情報が入ってくる。湾岸戦争前の混乱の中、イラク軍に拉致されたらしい。ダグは兄救出のため、ヨルダンへ向かった。「アメリカン忍者」で甘いマスクとシャープなアクションをみせた、M・ダディコフがダグに扮し、派手なアクションを披露する。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("名探偵コナン／天国へのカウントダウン", "名探偵コナン／天国へのカウントダウン", "", "", "http://ecx.images-amazon.com/images/I/61-S0uYKA6L._SL210_.jpg", 100, "人気アニメ・シリーズ第５作。完成間近の高層ビル、ツインタワーを案内されることになった名探偵コナンは、そこで殺人事件に遭遇。彼はビル建設に絡む何者かの陰謀を感じ始める。さらに、工藤新一に毒薬を飲ませコナンの姿に変えた黒ずくめの組織ジンとウォッカも出現。不穏な空気が広がる中でついに迎えたビルのオープニング・パーティーの日、コナンの想像を絶する事件が起こる。", "2001-04-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE FOUNTAINHEAD", "摩天楼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "天才建築技師と呼ばれるハワードは、その妥協のない頑固な姿勢ゆえに仕事を失う。友人のピーターは平凡な技師ながら世渡りのうまさで出世する。ピーターはハワードの才能を利用しようと協力を申し出るが……。K・ヴィダーがその緻密な演出力のすべてを懸けて撮り上げた骨太の人間ドラマの傑作。", "1949-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE ADVENTURES OF TOM SAWYER", "トム・ソーヤの冒険", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "トウェインの名作小説の映画化。「少年の町」など、ヒューマンな作品で子供たちの世界を綴るタウログ監督が、悪童トムと友だちハックルベリー・フィンの活躍を手なれた演出で表現している。", "1937-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BeRLiN", "BeRLiN", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "“ベルリンの壁”のかけらをペンダントにしているホテトル嬢、キョーコ。行方不明になった彼女の消息を、ドキュメンタリーのクルーやデートクラブの客だったサラリーマン、そして同棲していた男性らが追っていく。回想をカラー、現在をモノクロに分け、取材場面はビデオ映像といった創意に富む演出で、彼女にとりつかれた男たちの思い出を叙情的に映し出している。念願の企画を実現したのは、「エレファントソング」の利重剛。", "1995-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CITY HEAT", "シティヒート", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "禁酒法下のカンザスシティ。スゴ腕だがクールなスピア刑事が、冷戦中の元同僚で女にモテモテの有能な私立探偵マイクと手を組み、暗黒街壊滅に乗り出した……。ハードなアクションと、イーストウッドとレイノルズの大物二人の掛け合いが面白い娯楽作品。", "1984-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Midnight Express", "ミッドナイト・エクスプレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "トルコ旅行中に麻薬不法所持の罪で禁固4年の実刑をくらい、現地の刑務所に投獄されたアメリカ人青年ビリー。暴力が横行する地獄のような毎日をひたすら耐えていたが、出所目前にトルコとアメリカの関係が悪化、彼は取引材料として利用され、刑期が30年に延長されてしまう。ついにビリー脱獄を決意するが……。アラン・パーカー監督による社会派ドラマの傑作。実話を元にオリバー・ストーンが脚色、アカデミー賞を受賞している。", "1978-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("仁義なき戦い", "仁義なき戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "“仁義なき戦い”シリーズの第１作で、シリーズ化は撮影直前に決定された。このため最初、菅原文太は本作で殺される坂井役に決まりかけていたが、急きょ広能昌三役へと変更になった。深作はそれまでも実録タッチのヤクザものを何本か作り、暴力の世界に生きる若者の激烈な青春像を戦後史を絡めて描いてきたが、本シリーズはその集大成といえるだろう。敗戦直後の広島県・呉市。戦争から復員してきた広能はヤクザのいざこざに手を貸して殺人を犯し、刑務所に服役する。広能はそこで土居組の若杉と知り合い、盃を交わす。山守に保釈金を出してもらって出所した広能は、やがて山守組の組員となる。山守は次第に勢力を伸ばし、土居組との間で抗争事件が起こる。広能は土居を殺し、再び刑務所に戻る。その間、山守組では組員の坂井が勢力を伸ばし、内部抗争が激化していた。荒々しい手持ちカメラで撮られた暴力描写とともに、テーマ音楽もドラマを盛り立てている。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAINBOW DRIVE", "スーパーコップ90", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "ハリウッド警察署殺人課課長マイク・ギャラガーは、敏腕刑事だが女にはだらしがない。ある日、愛人との情事を終えた帰りに残虐な殺人事件に遭遇し。その事件を追ううちに警察内部の巨大な陰謀を知る……。「ロボコップ」のP・ウェラーが、クールな刑事を熱演するハードボイルド・アクション。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("青年の樹", "青年の樹", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "石原慎太郎の原作をもとにした青春ドラマの２度目の映画化。前回は主人公に石原裕次郎が扮したが、今回は三浦友和が演じる。坂木武馬は東大に入学し、友人たちを取りまく複雑な環境に、正義感と行動力でかかわっていく。青年の熱い心を恋愛・スポーツ・友情を通して描く。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("バースデー・ウェディング／　母が教えてくれた　　いちばん大切なこと", "バースデー・ウェディング／　母が教えてくれた　　いちばん大切なこと", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "『天体観測』『東京湾景』など人気TVドラマを多数手がけてきた田澤直樹監督が、家族愛を中心とする普遍的な感情を追求した感動作。人生で最もドラマチックな瞬間を迎える結婚式を題材に、愛することの素晴らしさを描出。TVドラマ『白い巨塔』で注目を集めた木村多江が、薄幸な母親役を好演。結婚式を翌日に控えた千晴は、1本のビデオテープを見つける。そこには亡き母と家族で過ごした思い出の日々が収められていた……。", "2005-06-11", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Pippi in Taka-Tuka-Land", "ピッピの宝島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "スウェーデンのアストリッド・リンドグレーンによる名作児童文学を映画化。おさげとそばかすがトレードマークの女の子ピッピが海賊の島を舞台に繰り広げる大冒険を描く。ある日海岸で、お父さんのSOSを知らせる手紙入りのビンを発見したピッピ。海賊に捕まってしまったお父さんを助けるため、彼女は仲良しのトミーやアニカと一緒に気球に乗って旅に出る。数々の困難を乗り越え、ピッピたちはなんとか海賊の島にたどり着くが……。", "1970-01-01", "14,45", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LE JOUEUR DE VIOLON", "無伴奏「シャコンヌ」", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "自らの芸術を追求するためにキャリアを捨て、メトロの地下道で演奏を続けるバイオリニストのアルトマン。そんな彼の音色に魅せられ、薄幸で孤独なメトロの女性職員リディアは次第に心惹かれていく……。バイオリンを通して交歓する二人の淡く切ない愛を、彼が真の芸術の境地を見いだし、愛の神髄にふれるまでのエピソードを併せて描いた力作。実際の演奏は、天才バイオリニスト、ギドン・クレーメルが担当。ラスト15分間に流れるバッハの『シャコンヌ』の演奏シーンは圧巻だ。", "1994-01-01", "2,37,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("AUSTERLITZ", "ナポレオン・アウステルリッツの戦い", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "1927年に３面マルチなどの手法を駆使した「ナポレオン」を発表したガンス監督が30年余を経て、再びナポレオンを描いた劇場未公開作品。三帝会戦といわれるアウステルリッツの戦いを中心にしたスケールの大きなストーリー展開はもちろん、豪華キャストも魅力。", "1959-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("空中庭園", "空中庭園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "角田光代の同名小説を基に、家族の絆の崩壊と再生を描いた人間ドラマ。小泉今日子が「風花」以来４年ぶりに主演を務め、絶望を深めていく母親役を迫真の演技で好演する。原作とはひと味違うエンディングや、ほのかな温もりを演出するUAが歌う主題歌『この坂道の途中で』も印象的。“隠し事をしない”というルールの下で暮らす京橋家の家族4人。しかし彼らが抱えるそれぞれの秘密が、やがてボロボロと露見し始める。", "2005-10-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Hot Touch", "泥棒貴族II", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "耽美的な作風で知られるR・ヴァディム監督が珍しく都会的で小粋なタッチに仕上げたロマンティック・コメディ。ある時は美術鑑定家、またある時は贋作名人と変幻自在に自分を操る主人公を軸に、優雅で知的な犯罪をおしゃれに描いている。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("美麗在唱歌／MURMUR　OF　YOUTH", "青春のつぶやき", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "台北のマンションで、様々な問題を抱えた中流階級の家族とともに暮らすメイリ。そして町外れの古めかしい家で、労働階級の両親に育てられたもう一人のメイリ。まったく異なる人生を過ごしながらも同じ名前を持ち、なぜか感覚的に通じ合う彼女たちの出会いと友情を見つめる青春映画。「浮草人生」のL・チェンシン監督のみずみずしい感性がきらめく作品で、主演の二人は、1997年の東京国際映画祭で主演女優賞をダブル受賞した。", "1996-01-01", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANNA CHRISTIE", "アンナ・クリスティ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "スウェーデン出身で、その神秘的な魅力によりサイレント末期から1930年代にかけて映画界に君臨した女優、G・ガルボのトーキー主演第１作。娼婦の生活に疲れたアンナは、休養をとるため父の所へ向かう。ある日、アンナは父と一緒に海で溺れかけていた水夫マットを助ける。純朴でたくましいマットとアンナは激しい恋に落ちるが、アンナが自分の過去を打ち明けるとマットは激怒し、彼女のもとを去ってしまう。しかし、やがてすべてを許したマットは彼女のもとへ戻り、二人で新たな人生を送ることを誓うのだった。ハスキー・ヴォイスの第一声は、“ウイスキーをちょうだい、ジンジャーエールで割って”だった。", "1930-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海盗り 下北半島・浜関根", "海盗り 下北半島・浜関根", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "原子力船“むつ”の新母港の建設予定地となった下北半島・関根浜に住む人々の建設反対運動を通して、日本の現状を描いていくドキュメンタリー。権力と札束の前に屈していく人々の姿を、カメラはあくまでも冷静に捉え続けていく。一人芝居を続けながら運動に加わった松橋勇蔵が映画の企画者である。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("艶恋師　北海道　放浪編", "艶恋師　北海道　放浪編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 0, "「夜王」「女帝」の倉科遼原作による人気コミックを基に、性に悩む女たちを救う“竿師”の活躍を描いた官能ドラマの第2弾。“幻の女”を求めて北海道へやって来た菊之介は、実日牧場のひとり娘・真澄と出会う。名馬を育成する実日牧場は、小さな牧場を相次いで買収している千極ファームから狙わていた。真澄は幼なじみでもある千極の息子・直人にレイプされた過去から、性に対して潔癖になっており……。", "2008-01-26", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("メンゲキ！", "メンゲキ！", "", "", "http://image.eiga.k-img.com/images/movie/57562/315x210.jpg?1278", 99, "こってりラーメンで人気の中華そば専門店「天下一品」を展開する株式会社天一食品グループが、同社の創業40周年を記念して企画した劇場映画。30歳が目前に迫った売れない役者の本郷は、恋人の美世と結婚するためまともな職を探すか、このまま夢を追い続けて役者を続けるか悩んでいた。そんなある日、美世の両親に紹介された本郷は売れない役者とは言い出せず、テレビの取材もくる人気ラーメン店の店長だと嘘をついてしまう。美世の両親がテレビの取材日にあわせて来店すると言い出したため困り果てた本郷だったが、高校の元演劇部の仲間たちが本郷を助けるためラーメン屋で演劇をする集団「チームメンゲキ！」を結成。本郷が店長役を演じることで、苦境を乗り越えようとする。 主演は「劇団EXILE」所属の青柳翔。お笑いコンビ「スピードワゴン」の小沢一敬が脚本を担当した。", "2012-01-28", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THREE DAYS TO A KILL", "スリーディ トゥ キル 任務遂行せよ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "二人のプロフェッショナルが、誘拐された大使を救出するため、南米コロンビアの麻薬コネクションに戦いを挑むアクション映画。オールド・ファンには懐しいTVシリーズ『ライフルマン』の主役でもあり、映画での渋い演技が光っていた脇役、C・コナーズの遺作。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Texas Killing Fields", "キリング・フィールズ　失踪地帯", "", "", "http://image.eiga.k-img.com/images/movie/57592/315x210.jpg?2622", 105, "マイケル・マン（「ヒート」「コラテラル」）の実娘アミ・カナーン・マンの劇場長編監督デビュー作で、米テキサス郊外に実在する犯罪多発地帯で少女を救うため奮闘する刑事たちの姿を描くクライムサスペンス。刑事マイクと相棒のブライアンは、連続して発生した少女の失踪と殺人事件の捜査にあたっていたが、その矢先にブライアンが気にかけていた心に傷を抱えた少女アンも失踪してしまう。アンが事件に巻き込まれたと直感した2人は、犯罪が多発することで「キリング・フィールド（殺人地帯）」と呼ばれる危険地帯へと向かう。2人の刑事に「アバター」のサム・ワーシントンと「ウォッチメン」のジェフリー・ディーン・モーガン。少女アンに「キック・アス」「モールス」のクロエ・モレッツ。マイケル・マンが製作を務め、2011年・第68回ベネチア国際映画祭のコンペティション部門にも出品された。", "2012-04-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("悲愁物語", "悲愁物語", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "異才・鈴木清順が、“わけのわからない映画を撮る監督はいらない”と日活を追い出されて以来、実に10年振りにスクリーンに復活した作品。しかし、一般の客のみならず、鈴木清順復活を待ち望んでいたファンの頭をもかしげさせるような、いわゆる“失敗作”の烙印を押されてしまい、興行的にも惨胆たるものであった。CFのモデルとしてデビューし、成功の道を駆け上がっていく女子プロゴルファーの栄光と破滅を描いた、故・梶原一騎原作による物語。ガラスを使ったり、原色を多く用いたセットには、10年の隔たりを感じさせない清順美学が貫かれている。", "1977-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("IL SOLE ANCHE DI NOTTE", "太陽は夜も輝く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "トルストイの原作の舞台を南イタリアに移し、一人の男の苦悩の日々を描いたタビアーニ兄弟監督作品。優秀な士官学生セルジュは、王の副官になるにあたり身分の高い娘と結婚することになる。しかし王と娘の不義理な関係を知り、世俗を離れて隠者の道へと旅立つ。欲望への抗いは厳しく、真実への道は遠い……。", "1990-12-31", "4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("御用牙・かみそり半蔵地獄責め", "御用牙・かみそり半蔵地獄責め", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "かみそり半蔵は、捕らえた盗人から、寺や神社ではやっている“子おろし”に関係する殺人事件をかぎつける。女神主を得意の拷問にかけて、事件の裏に潜む尼寺の腐りきった実態を突き止めていく……。名匠・増村が１作目に負けぬ迫力で描いた佳作。", "1973-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ギャング対Gメン", "ギャング対Gメン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "暴力組織を内偵中のGメンが殺された。警察では組織のシッポをつかむために、元ヤクザ組織の幹部で拳銃の名手・東島を大将格に、７人の無頼漢を組織に潜入させる。", "1962-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TYCOON", "タイクーン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 129, "J・ウェインが土木鉄道技師に扮し、南米アンデス山中での大工事に従事する、スペクタクル・ヒューマンドラマ。主人公のジョイは、危険な工事を主張する雇い主とことごとく対立するが、やがて雇い主の娘と結ばれ、数々の苦難に遭いながらも自分の主張する工事を完成させていく。", "1947-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("An Angel at My Table", "エンジェル・アット・マイ・テーブル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 158, "ニュージーランド映画界の女流監督、J・カンピオンの長編第２作。ニュージーランドを代表する女流作家J・フレイムの自伝３部作を原作に、彼女がたどった数奇な運命を描く。子供の頃、その髪型から“鳥の巣”と呼ばれていた赤毛のジャネット。シャイな反面、感性に秀でた彼女は、やがて文学少女として頭角を現わしていく。その後、彼女は教師を志すが、内向的な性格が災いし断念。自殺未遂を図り、精神病院へ送られて、長い闘病生活が始まる。その間も彼女は小説を書き続け、社会的に認められるようになるが……。決して美人ではないが、自分の生き方に正直なジャネットを、K・フォックスが熱演。ヴェネチア映画祭では映画批評家賞など10部門で受賞し、ベルリン映画祭でもフォーラム部門最高人気賞に輝いた。", "1990-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A WOMAN OF PARIS", "巴里の女性", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 81, "大正13年に公開されて以来、劇場で一度もリバイバルされていない幻の名作。フランスの田舎町、美しい娘と貧しい青年画家が恋に落ち、駆け落ちを決意するが、約束の時間になっても青年は現れなかった。数年後、パリで偶然再会した二人は……。珍しくチャップリン主演ではない、メロウな悲恋物語。", "1923-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Wag the Dog", "ウワサの真相　ワグ・ザ・ドッグ", "", "", "http://ecx.images-amazon.com/images/I/51LhAgmeIOL._SL210_.jpg", 97, "「スリーパーズ」に続き、バリー・レビンソン監督がロバート・デ・ニーロとダスティン・ホフマンの2大スターを迎えて放つブラック・コメディ。次期大統領選挙を目前にして発覚した、現大統領のセックス・スキャンダル。大統領直属の“もみ消し屋”ブリーンは、大衆の目をこのスキャンダルからそらすため、ある計画を思いつく。それは、ハリウッドの大物プロデューサーを雇い、架空の戦争をでっちあげるというものだった……。", "1997-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジャム・セッション／菊次郎の夏〈公式海賊版〉", "ジャム・セッション／菊次郎の夏〈公式海賊版〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "「犬と歩けば／チロリとタムラ」の篠崎誠が、北野武の「菊次郎の夏」の製作現場をデジカムで追いかけたメイキング・ドキュメント。とはいえ、映画ライター時代から北野映画に深い理解を示していた篠崎だけに、通常の映画舞台裏ものとは一線を画した内容。ナレーションも音楽も不使用。テロップも最小限に抑えられており、映画撮影の流れを追うのではなく、北野映画の現場に観客を放り込むような映像がストイックに連なっている。監督・北野武の“素顔”もさることながら、美術スタッフがアイデアを結晶化させていく場面などにも見ごたえあり。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La Cité du Pétrole", "石油プラットフォーム", "", "", "http://image.eiga.k-img.com/images/movie/55012/315x210.jpg?4662", 52, "第2次世界大戦直後、カスピ海底に大量の油田を発見したスターリン政権下のソ連によって構築された巨大な石油プラットフォーム「オイル・ロックス」を題材にしたドキュメンタリー。監督はスイスのマルク・ボルフェンスバーガー。原油価格の下落や原油の枯渇といった深刻な問題に直面しながらも、60年以上に渡って操業を続けている巨大都市とそこで生きる人々の姿を、完成当時のニュース映像を交えながら映し出していく。", "2009-10-18", "26", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Unforgiven", "許されざる者", "", "", "http://ecx.images-amazon.com/images/I/51l0uw6yofL._SL210_.jpg", 131, "クリント・イーストウッドが、師匠であるセルジオ・レオーネ監督とドン・シーゲル監督に捧げた異色西部劇。1870年代の米ワイオミング。かつては無法者として悪名を轟かせたビル・マニーだったが、今は若い妻に先立たれ、2人の子供とともに貧しい農夫として静かに暮らしていた。そこに若いガンマン、キッドが立ち寄り、賞金稼ぎの話を持ちかける……。共演にジーン・ハックマン、モーガン・フリーマン、リチャード・ハリス。92年度のアカデミー賞では作品、監督を含む4部門を受賞した。", "1993-04-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("明治大帝御一代記", "明治大帝御一代記", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 187, "嵐寛寿郎主演の一連の“明治天皇”映画を１本に再編集、追加撮影して製作された一代記。冒頭、大蔵貢の写真付製作意図のあと、明治天皇の誕生、幕末の争乱から西南の役、日清・日露戦争、そして逝去、乃木将軍夫妻の殉死までが、絵巻物のように編集されている。エキストラの数には驚嘆させられる。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWEDENHIELMS", "スウェーデンイェルム家", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "科学者で発明家のロルフは、贅沢三昧の子供たち３人と家政婦の５人暮らし。はた目には裕福そうにみえる暮らしぶりも、その実は一文無し。今まで生活を続けられたことすら不思議なほどである。そんなある日、息子のボーが借金をしていたことが発覚する。スウェーデン映画が初めて国際的に評価を得たとされている作品。I・バーグマンが脇役で、ボーの婚約者として顔をみせている。", "1935-01-01", "14", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Blade Runner: The Final Cut", "ブレードランナー　ファイナル・カット", "", "", "http://image.eiga.k-img.com/images/movie/53062/315x210.jpg?4389", 117, "日本でも未だに根強いファンを持つSF映画の金字塔「ブレードランナー」。初公開から25年を迎えた本作に、リドリー・スコット監督自らが再編集とデジタル修正を施して蘇らせたファイナル・カット版。酸性雨で荒廃した2019年のロサンゼルス。人間にそっくりな外見を持つ人造人間“レプリカント”たちが植民地惑星から逃亡してきた。レプリカント専門の捜査官“ブレードランナー”のデッカードが追跡を開始するが……。", "2007-11-17", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("くぐつ", "くぐつ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "人形遣いの鬼才、水田外史の生前の舞台と、芸に対する飽くなき情熱を映したドキュメンタリー。戦後まもなく独立した水田は、ガイ氏即興人形劇場を設立。玉人形を使った独自の芸風を確立し、国際的な評価を得る。だが、人気絶頂時に突然休業。世界を放浪した後、山村にこもり、『ごんぎつね』『耳なし芳一』などの名作を生み、生涯、理想の人形劇を追い求めた。", "2001-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("My Super Ex-girlfriend", "Gガール　破壊的な彼女", "", "", "http://image.eiga.k-img.com/images/movie/33842/315x210.jpg?4813", 98, "超音速で空を飛び、セクシーボディで銃弾を跳ね返すスーパー・ヒロイン“Gガール”と普通の男性との恋を描くセクシー・ラブコメディ。圧倒的パワーで悪を退治する正義の味方でありながら、私生活では恋の悩みに一喜一憂する等身大の主人公を「キル・ビル」のユマ・サーマンが熱演。嫉妬心から暴走するGガールに振り回される気弱な会社員マットに「キューティ・ブロンド」シリーズのルーク・ウィルソンが扮する。", "2007-02-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("燃えつきた地図", "燃えつきた地図", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 118, "「おとし穴」「砂の女」「他人の顔」に続く安部公房＝勅使河原宏コンビの４作目。蒸発した人間を追っていくうちに、不条理な人間関係に巻き込まれ、迷い込んでいく男を勝新太郎が熱演。映画化絶対不可能の作を取り上げた。製作は勅使河原プロから勝プロに代わったが、前３作ほどのボルテージの高さはない。", "1968-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルパン三世　カリオストロの城", "ルパン三世　カリオストロの城", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "宮崎駿監督にとって初の劇場監督作品となる劇場用「ルパン三世」シリーズ第2弾。盗み出した大金が偽札と気づいたルパンと次元は、偽のゴート札の秘密を探るため、カリオストロ公国へやって来た。そして謎の男たちに追われていた少女クラリスを助けるのだが……。クラリスの声を演じるのは、宮崎監督の「風の谷のナウシカ」でもヒロイン、ナウシカを担当した島本須美。", "", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("過客", "カラテ愚連隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "革命直後のある町を舞台に、無法の限りを尽くす盗賊一味と、正義感溢れる流れ者の男の戦いをバイオレンス色濃く描いたカンフー・アクション。過激な内容のため上映中止になり、75年に改題されて再上映された。王凱怡の共同監督作品で、ウー監督の実質上の監督デビュー作。当時売り出中のジャッキー・チェンが“陳元龍”の名で武術指導を担当、ウー・監督自身も出演している。", "1973-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rio Lobo", "リオ・ロボ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "南北戦争の末期、北軍のマクナリー大佐が護衛する金塊輸送列車が南軍のゲリラに襲われた。マクナリーは北軍に裏切者がいると見込んで捜索するが、彼も南軍に捕えられる。敵陣で出会ったコルドナは旧知の仲であった。戦後、マクナリーは裏切者を探してコルドナらと再会、悪党に占拠された町リオ・ロボに潜入する……。「リオ・ブラボー」「エル・ドラド」と並び、3部作ともいうべきホークスの傑作西部劇であり、これが彼の遺作となった。", "1971-02-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　LAST　CASTLE", "ラスト・キャッスル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 132, "かつて数々の武勲に輝きつつも部下を死なせた責任から軍刑務所に服役させられているアーウィン中将。やがて彼は非人間的な刑務所の管理体制に憤り、敢然と立ち向かっていく……。誇りと尊厳を蘇らせて、たった一人で“歪んだ正義”に闘いを挑む男の姿をスリリングに描いたロッド・ルーリー監督の佳作。刑務所ものならではの、個性派名優が勢ぞろいのキャスティング。巨匠J・ゴールドスミスのパワフルな音楽も、孤独な男の心意気を見事に奏で上げている。", "2001-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LES PARENTS TERRIBLES", "恐るべき親たち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "1939年初演のコクトーの同名の戯曲をコクトー自らが映画化した室内劇。没落したブルジョワの中年夫婦、イヴォンヌとジョルジュ。ある日、イヴォンヌは溺愛する一人息子のミシェルにマドレーヌという恋人がいることを知って逆上する。一方、父親のジョルジュは、息子の恋人の名を聞いて愕然とする。マドレーヌは、ジョルジュの愛人だったのである。無邪気そのもののミシェル役のJ・マレー、母親役のY・ド・ブレーが好演。登場人物は５人、全編室内シーンという演劇的な設定にもかかわらずコクトーの映画テクニックはあざやか。", "1948-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("喜劇・怪談旅行", "喜劇・怪談旅行", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "信平は港町の太地駅の駅長として赴任して来る。その信平の新居は幽霊が出るというほどの古びたあばら家だった。乗客係の庄作はその家から隣の尼寺の風呂場がのぞけるとそそのかす。信平はそんな趣味はないと笑いとばすが、誘惑に負けて湯殿をのぞき見る……。亡妻の幽霊が出没するファンタスティックな喜劇映画。", "1972-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("モル", "モル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 76, "PFFアワード2001でグランプリとブリリアント（日活）賞を受賞したビデオ作品。監督タナダユキが自ら主演、生理の日には必ず自殺者と目が合い高熱を出すという“奇病”にかかった売れないモデルの述懐を、明快に紡ぎ出す。風吹く屋上で、今まさに自殺しようとしている男を前にすべてが語られるスタイルなど、随所に小劇場的な仕掛けが充満している。自作自演によって、さえない状況を笑い飛ばすあたりは、女性作家によるエッセイやコミックも思わせる。主人公が繰り出すブロークンな関西弁が、ぬるま湯を拒否するかのようなパワフルな印象。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GUESS WHO’S COMING TO DINNER", "招かれざる客", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "サンフランシスコの中流白人家庭の娘が、旅先で結婚を誓い合った男をディナーに招く。家族は期待するが、その男は黒人だった。彼女の父親は新聞社を経営、人種差別反対を強く訴えてきたのだが……。アメリカ社会に根強く残る黒人問題を正攻法で描いた良心作。S・トレーシーの遺作。", "1967-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Dolls（ドールズ）", "Dolls（ドールズ）", "", "", "http://image.eiga.k-img.com/images/movie/1362/315x210.jpg?3232", 113, "北野武監督第10作。近松門左衛門の人形浄瑠璃「冥途の飛脚」の舞台で幕を開け、その舞台で死への旅に出る男女の人形が、観客を3つの愛の物語へと誘っていく。監督は「日本の四季」を描くことをも構想、撮影は北野組の常連で、近年では「バトル・ロワイアル」「GO」を手がけた柳島克巳が担当。音楽も北野組の久石譲。衣装はヨウジヤマモト。往年の美人女優、松原智恵子、吉沢京子の久々の映画出演も話題。", "2002-10-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SANS SOLEIL", "サン・ソレイユ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "世界を旅するカメラマンからの手紙を読む女性の声（日本語版は池田理代子）に導びかれて始まる世界の映像。とくに日本とアフリカという両極にある世界の映像が、混沌とした記憶として時間と空間を駆けめぐる。記録でも日記でもない斬新な映像作品だ。", "1982-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SWING TIME", "有頂天時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "F・アステアとG・ロジャースの名コンビが恋に踊りに大活躍する傑作ミュージカル・コメディ。ニューヨークにやって来たショーダンサーのラッキー。ひょんなことからダンス教師のペニィと知り合うが、ラッキーは素人のふりをして彼女に弟子入りしたことから思わぬ珍騒動が巻き起こる。アカデミー主題歌賞に輝いた名曲『今宵の君は』をはじめ、全編にわたって“スイング”を貫く構成が圧巻。監督は後に「シェーン」や「ジャイアンツ」を手掛けるG・スティーヴンス。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BENEATH THE PLANET OF THE APES", "続・猿の惑星", "", "", "http://ecx.images-amazon.com/images/I/51O0wlMzGdL._SL210_.jpg", 95, "「猿の惑星」の続編。禁断地帯に入ったテーラーたちは、地下に埋もれた廃墟のN.Y.を支配するミュータントと出会う。彼らは、放射能による突然変異でテレパシー能力を持ち、コバルト爆弾を“神”と崇めていた。猿、人間、ミュータントの三つ巴の戦いが始まる。", "1970-08-31", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネ響「マエストロ6」　クラウディオ・アバド　ルツェルン祝祭管弦楽団", "シネ響「マエストロ6」　クラウディオ・アバド　ルツェルン祝祭管弦楽団", "", "", "http://image.eiga.k-img.com/images/movie/56532/315x210.jpg?2849", 100, "映画館でクラシック音楽のコンサートを上映する「シネ響」の2作目。ガンを克服し“生きる伝説”とも言われる名指揮者クラウディオ・アバドが世界の名プレイヤーを集めて創設したルツェルン祝祭管弦楽団の2008年コンサートの模様を収録。美貌のピアニスト、エレーヌ・グリモーをフィーチャーしたラフマニノフの「ピアノ協奏曲第2番」や、チャイコフスキーの「テンペスト」などが演奏される。", "2010-09-18", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("シネマ歌舞伎特別篇　牡丹亭", "シネマ歌舞伎特別篇　牡丹亭", "", "", "http://image.eiga.k-img.com/images/movie/54572/315x210.jpg?2858", 142, "600年という長い伝統を持つ中国の昆劇「牡丹亭還魂記」を、歌舞伎役者の板東玉三郎が中国語の台詞と歌で挑んだ中国公演。その舞台を記録した映像と、昆劇のメッカ・蘇州での玉三郎の素顔に迫ったドキュメンタリーの2部構成。南安太守の令嬢・杜麗娘は、夢の中で出会った柳夢梅という青年と一瞬で恋に落ちる。2人は幸せな時間を共にするが、気がつくとそれらは全て夢の中の出来事で……。", "2009-05-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("五月みどりのかまきり夫人の告白", "五月みどりのかまきり夫人の告白", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 66, "ロマン・ポルノ的な展開で魅了するエロティック作品。和製「エマニエル夫人」の趣も。夫の浮気が発覚してから次々に男たちと交歓を結び、次々と彼らの人生を破滅させていく魔性の女を、五月みどりが実名のヒロインで大胆にして奔放に演じている。隣の家の主人との行為の後、彼は家を売って愛人宅を買ったが、“かまきり夫人”はつれなく彼のもとを去っていく。さらに４人の男たちが次々と彼女との情事に溺れ……。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("POZEGNANIE JESIENI", "秋への別れ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "アバンギャルドの先駆者といわれるポーランドのマルチアーティスト、S・I・ビトキェヴィッチが1929年に発表した小説を映画化。革命時の不穏なムードを背景に、バイ・セクシュアルの作家アタナジーが、純心な妻と妖艶な愛人、その愛人の夫である公爵や男友達らと繰り広げる複雑な愛憎模様が綴られていく。原作の持つ退廃的なムードを巧みに映像化した前衛的作品。", "1990-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Mystery Men", "ミステリー・メン", "", "", "http://ecx.images-amazon.com/images/I/31D8K4Z8CJL._SL210_.jpg", 121, "", "1999-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ルパン三世", "ルパン三世", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "原作はアメコミ調のタッチで人気を得た、モンキー・パンチのヤング・コミック。TVシリーズでも旧作(1971)、新作（1977)、PART III（1984）とアニメ化されたが、なかで最もアダルト色の強い旧シリーズのスタッフを中心に作られた、劇場用アニメ化の第１作。世界を舞台に、奔放なイメージで繰り広げられるノンストップ・アクションは痛快の一語で、アニメによる日本製“007”といった趣がある。出現した巨大な脳を、ルパンが破壊するクライマックスが圧巻。ルパンは、愛する謎の女・峰不二子に頼まれ、“賢者の石”を探していた。だが不二子は、マモーなる謎の男に操られていたのだ。ルパンも彼に捕らわれ、カリブ海の孤島へと運ばれる。実はマモーは１万年も生き続け、歴史に介入してきたクローン人間で、偉人たちのコレクションにルパンも加えようというのだ。ルパンの仲間の五右衛門と次元、さらにルパンを追う銭形警部も島に乗り込み、激しい戦いが始まった。DVDタイトルは「ルパン三世　ルパンVS複製人間〈クローン〉」。", "1978-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JABBERWOCKY", "ジャバーウォッキー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "中世のブルーノ王国に、狂暴な怪獣ジャバーウォッキーが出没する。被害に困り果てた王は、退治した男を王女と結婚させて跡継ぎにするとおふれを出した。そこへ来あわせたのがデニスだった。王女と王国を手に入れるまでのデニスの奮闘をアドベンチャー・ゲーム感覚で描いた“モンティ・パイソン”ファミリーによる作品。", "1978-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飢える魂（正・続編）", "飢える魂（正・続編）", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 176, "社会的地位のある実業家の若妻とプレイボーイの青年実業家、二人の子供を抱えて生きる未亡人と病気の妻を持つ男。そんな二組の愛の行く末を、全国各地に転々と舞台を移して描くメロドラマ。正・続編同時に撮影されたが、別々に封切られた。", "1956-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Scarface", "暗黒街の顔役", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "N.Y.の南地区を仕切るギャングのボスが殺害される。犯行は、新興ギャング・ジョニー・ロボに寝返った用心棒トニー・カモンテによるものだった。トニーはその後、ロボの片腕となり、銃と暴力の力で勢力を伸ばしていくが…。戦前のホークス映画の中でもっとも有名であり、ギャング映画の古典としての評価が定着した作品。", "1933-03-13", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Obsession", "愛のメモリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "　1959年、幸せの絶頂にあったマイケルに突然悲劇が訪れる。最愛の妻子が何者かに誘拐され、その後、ふたりが事故死するという最悪の結末に。それから16年後。妻の面影を忘れることができなかったマイケルの前に、彼女とそっくりの女性サンドラが現れる。ふたりは結婚を約束するが、やがてサンドラが誘拐されるという事件が……。妻を愛し続けた男のあまりにも皮肉な運命を描くロマンティック・サスペンス。", "1976-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("第36個故事", "台北カフェ・ストーリー", "", "", "http://image.eiga.k-img.com/images/movie/57752/315x210.jpg?3319", 81, "ホウ・シャオシェン製作総指揮の下、新鋭シアオ・ヤーチュアン監督が手がけたラブストーリー。台北のカフェを舞台に、訪れる客たちと特別なものを交換することで物語が展開していく様子を描く。ドゥアルとチャンアルの美人姉妹が開いた台北のカフェでは、客がさまざまな物を持ち込み、次の持ち主のもとへと引き継がれる物々交換が話題に。そんなある日、世界35都市で集めたという35個の石けんを何か特別なものと交換したいという男が店を訪れる。姉のドゥアルは、男が語るそれぞれの石けんにまつわるストーリーに魅せられていく。歌手の中孝介が、大ヒット作「海角七号」に続き2度目の台湾映画出演を果たし、歌声も披露している。", "2012-04-14", "53", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OEIL POUR OEIL", "眼には眼を", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 113, "誤診の末、手遅れになったため妻を亡くした男の医者に対する復讐劇を、緊迫感あふれる映像で描いたドラマ。シリアの砂漠地帯を背景に、男と医者との追討をスリリングに紡ぎ出しながらも、人間対人間の闘いが異常とも思える緊張の中で綴られていく。「裁きは終りぬ」などで人間の内面を独自の視点でえぐり出した弁護士出身の映画作家、A・カイヤットらしい描写が随所に見られる。ラストシーン近く、不毛の砂漠での、男と医者、二人の生命を賭した相手の本心の探り合いが強烈な印象を残す。", "1957-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Rock & Rule", "ROCK＆RULE ロックン・ルール", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "米ソ冷戦時代の1983年に製作され、世界でもごく限られた映画館でのみ公開された幻のロック・ミュージカル・アニメ。世界規模の核戦争の結果、人類の多くが死に絶えた未来世界。世紀末の退廃と享楽に慣れきった生活の中で、悪魔をよみがえらせようとするロックスターの野望と、それを阻止しようと奮闘する売れないミュージシャンの戦いを描く。「チープ・トリック」のロイ・サンダー、「ブロンディ」のデボラ・ハリー、ルー・リード、イギー・ポップら豪華ミュージシャンたちが声優や歌で参加している。", "2010-12-03", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Setup", "セットアップ", "", "", "http://image.eiga.k-img.com/images/movie/57662/315x210.jpg?4271", 84, "人気ラッパーの50セント、ブルース・ウィリス、ライアン・フィリップの主演で描くクライムドラマ。犯罪都市デトロイトで兄弟同然に助け合って生きてきたサニー、ビンセント、デイブの3人は、500万ドルのダイヤ強奪計画を成功させるが、ビンセントが裏切りダイヤを持ち逃げしてしまう。その過程でデイブは死に、サニーは重傷を負う。サニーは復讐と裏切りの真実を暴くためビンセントを追う。", "2012-03-10", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Afriques: Comment ca va avec la douleur?", "アフリカ、痛みはいかがですか？", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 165, "ピュリッツァー賞を受賞したフランス出身の世界的カメラマン、レイモン・ドゥパルドンが、アフリカ縦断の旅を通して1990年代前半のアフリカを捉えたドキュメンタリー。アフリカ大陸最南端の喜望峰からはじまり、激しい内戦が繰りひろげられたルワンダや荒廃した風景のひろがるソマリアを経て、穏やかな地中海に面したエジプトの都市アレクサンドリアにたどり着くまでの長い旅の中で、アフリカが日常的に抱えている“痛み”を浮き彫りにしていく。", "2010-06-12", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZIPPERFACE", "ジッパー・フェイス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "鋲とレザーでできたマスクをかぶり、大ナタをふるって首をはねるホラーヒーローが美女を襲う。捜査にあたった婦人警官リサは、恋人マイケルに向けられた疑惑を晴らすため、自らおとりになるのだが……。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GHOSTS　OF　THE　ABYSS", "ジェームズ・キャメロンのタイタニックの秘密", "", "", "http://ecx.images-amazon.com/images/I/51AEZC8177L._SL210_.jpg", 45, "「タイタニック」のジェームズ・キャメロン監督が、実際のタイタニック号の撮影に挑戦。潜水艦に乗り込み、北大西洋沖の海底約3650メートルに眠るタイタニック号を最先端システムのカメラで捉えた。3Dで立体的に映し出される船体の映像は意外にも原型をとどめ、当時の面影を偲ばせる。", "2002-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CUBE ZERO", "CUBE ZERO", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "立方体の部屋が連なる謎の迷宮を舞台にしたスリラー「CUBE」シリーズの第3作。トラップを避けて出口を探る人々のサバイバルは、疑心暗鬼が渦を巻くキャラクター間の不信も手伝い、今回も緊迫感満点だ。これまで秘密だったキューブの仕組みや、トラップによる凄惨な惨殺描写にも注目。キューブの管理人ウィンが監視カメラで目に留めた若い女性。彼女がキューブに誤って入れられたと考えたウィンは救出に向かうが……。", "2005-11-19", "10", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Famille ファミーユ　フランスパンと私 TWILIGHT FILE V", "Famille ファミーユ　フランスパンと私 TWILIGHT FILE V", "", "", "http://image.eiga.k-img.com/images/movie/54152/315x210.jpg?3845", 0, "オムニバスシリーズ「TWILIGHT FILE」第5弾の1作。妻に先立たれ、娘を手放した過去を持つ風間は屋敷から金品を盗み出す泥棒を生業としている中年男。ある日、裕福な家庭で暮らす風間の娘・小夜は自分が養子であること知り、家を飛び出す。ショックから自殺を図ろうとする小夜の前に偶然現れたのは、風間だった。主演は「蒲田行進曲」「モスラ」の萩原流行、共演に「まぼろしの邪馬台国」の風間トオルほか。", "2008-11-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("SITUATION HOPELESS−BUT NOT SERIOUS", "戦場はどこだ！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "第二次世界大戦末期。敵国ドイツ上空で撃墜された米軍パイロットのラッキーとハンクは、ある静かな村に逃げ込んだ。そこで彼らはハンクという男と知り合い、地下室に匿ってもらうことになる。ところがこのハンクは奇人として村では有名な人物だった。終戦も知らされず、６年後に初めて地下室を出た二人は……。日本の“横井庄一さん事件”を彷彿とさせる、A・ギネス主演の異色ドラマ。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Holes", "穴 HOLES", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 117, "　一族にかけられた呪いのせいで、不運続きの少年スタンリー。ある日、彼は靴泥棒の犯人に仕立て上げられ、青少年犯罪者の矯正キャンプに送られてしまう。そこで待っていたのは、広大な砂漠での穴掘り作業。それを監督する冷酷な女所長と部下たちは何かをたくらんでいるらしいが。はたしてスタンリーの運命は？　絶大な支持を集める児童文学を映画化。奇想天外でファンタジック、そしてユーモラスな物語。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE END OF VIOLENCE", "エンド・オブ・バイオレンス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "バイオレンス映画のプロデューサー、マイクはトラブル続きの仕事に忙殺されて妻にも愛想をつかされている。そんなある日、何者かに誘拐され殺されかけたマイクを、実験中の都市監視カメラが捉えていた……。「パリ、テキサス」以来ふたたびロサンゼルスを舞台に、巨大な暴力に翻弄される人間の姿に迫ろうとしたベンダース監督の意欲作。", "1998-03-31", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("８時間の恐怖", "８時間の恐怖", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "鈴木清順としては珍しい本格サスペンス。台風のため列車が立往生。臨時バスに乗り込んだ15人の乗客は、２人組のギャングによって恐怖の８時間を過ごすはめに……。といっても、やはり清順。これを喜劇映画にしてしまい、会社は激怒。再編集のあと、公開された。バスという密室空間の生かし方が巧み。", "1957-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Week End", "ウイークエンド", "", "", "http://image.eiga.k-img.com/images/movie/1742/315x210.jpg?2571", 104, "都会に住む夫婦が、ある週末、夫の父の住む田舎に向けて車で出かける。が、道は同じように田舎に向かう車で渋滞していて、さまざまな不条理な出来事が起こっていく。59年「勝手にしやがれ」でデビューしたゴダールは、60年代に「軽蔑」「気狂いピエロ」などを監督した後、一時長編劇映画から離れるが、その直前作が本作。この後彼は、政治闘争的映画製作を実践する集団ジガ・ベルドフを結成、匿名集団での映画製作を始めた。", "2002-04-27", "2,4", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京湾炎上", "東京湾炎上", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "原油を満載した20万トンのタンカーが、東京湾に帰り着く直前にゲリラによってシージャックされ、東京湾は爆破の危機にさらされる。田中光二のベストセラー小説を映画化、現実味も帯びた設定で見せるパニック映画。特撮もふんだんに駆使されている。", "1975-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DE JURK", "ドレス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "人から人の手へと渡り、人間の欲望を喚起する不思議なドレス。制作者や服飾メーカーの重役の運命を狂わせ、最初に購入した老婆は発情死。さらに、この服をまとった若い女性や女子高生はストーカーに付け回され、最後にはホームレスの女性の手に渡るが……。一着の洋服をめぐるビザールな人間模様を描いた一編。ブラック・ユーモアと大胆な展開が好評を呼び、ヨーロッパではルイス・ブニュエルの作品と比較された。", "1996-01-01", "70", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE DELIVERY", "ザ・デリバリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "借金苦の青年ガイとアルフレッドが、2500万ドル相当のドラッグをアムステルダムからバルセロナまで運ぶ仕事を請け負う。ところが、車で出発した二人の行く手には、空から降ってきた謎のセクシー美女、さらに殺し屋やインターポールの捜査官らが出現するのだった！　ヨーロッパ各国を股にかけたロード・ムービー。作りそのものは相当粗っぽいが、風変わりなキャラクター描写、パワフルなアクション、唐突なユーモアなどを矢継ぎ早に連打する威勢のよさは買える痛快作だ。", "2000-01-01", "70,37", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CINDERELLA", "シンデレラ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "『シンデレラ』の物語をディズニーがアニメ技術の限りを尽くして作り上げた傑作。人の動きをまず実写して、その画面をもとにアニメーション画面を作成するという方法で撮影されたために、そのなめらかなキャラクターの動きや美しい映像は、本当にこれが50年以上も昔のアニメーション作品なのかと、目を疑ってしまうほどの出来栄えだ。", "1950-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'INCORRIGIBLE", "ベルモンドの怪盗二十面相", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "20の名前と20の愛人を持ち、パリ中に波乱を巻き起こす変幻自在の大怪盗の活躍を描いたアクション・コメディ。刑務所を出所した怪盗ヴィクトールの元に、女性保護監察官のマリーがやって来る。彼女のあまりの美しさに惚れ込んでしまったヴィクトールは、その日からマリー攻略作戦を開始するのだが……。", "1975-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("限りある日を愛に生きて", "限りある日を愛に生きて", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "医師からあと10年の命と宣告された少女が、愛する男とともに限りある日々を精一杯生きようとする泣ける青春ドラマ。高校を卒業して２年後に心臓病を患った紀子は、受験生の草薙と知り合う。やがて紀子の手術は奇跡的に成功し、大学に合格した草薙と４年後に結婚しようと約束したのだが……。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UNDERWORLD，USA", "殺人地帯USA", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "幼いころ、やくざな生活を送っていた父を目の前で殺された少年は、犯人の顔を心に刻みつけて、ギャングに復讐を誓う。やがて成人した彼は暗黒街の一員となり、父を殺した３人のギャングを追い詰めていく……。S・フラーが独特の荒々しく暴力的な演出で、暗黒街の影と悲哀を描き出した骨太のアクション。", "1961-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("MANHATTAN MURDER MYSTERY", "マンハッタン殺人ミステリー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "ミア・ファローとのスキャンダラスな破局のあと、W・アレンがかつての恋人D・キートンと再度コンビを組んだミステリアス・コメディ。マンションの隣室に住む老夫人の死に、疑問を抱く主婦キャロル。というのも、隣人のハウス氏は妻が心臓発作で亡くなったにもかかわらず、妙に明るい。完全犯罪という疑惑に駆られたキャロルは気弱な夫や友人を巻き込んで、探偵ごっこに乗り出すが……。「マンハッタン」から14年を経ての本格的な共演となるアレンとキートンだが、息はピッタリ。アレンの演出にユーモラスな掛け合いに加え、ヒッチコックを思わせるスリリングな描写も見られ、肩の力が抜けた娯楽作となっている。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("若さま侍捕物帳・謎の能面屋敷", "若さま侍捕物帳・謎の能面屋敷", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "“若さま侍捕物帳”新東宝ものの第１作。江戸では能面をつけた強盗殺人犯が横行し、追跡するといつも能役者・間崎廣光の住む“能面屋敷”の辺りで姿を見失う。それを知った堀田左馬介こと若さま侍が重い腰をあげる。黒川弥太郎の若さま侍も適役で、中川信夫の演出でひねりのきいた面白さがある作品となった。のちに監督として名を上げる井上梅次が脚本を担当。", "1950-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("東京フレンズ The Movie", "東京フレンズ The Movie", "", "", "http://image.eiga.k-img.com/images/movie/33422/315x210.jpg?2296", 115, "大塚愛が初主演を果たし話題となったDVDドラマを映画化。東京に憧れて上京してきた玲は、アルバイト先の居酒屋「夢の蔵」で、それぞれの夢を追う個性的な仲間たちと出会う。やがてバンドのボーカルに誘われた彼女は、音楽という夢に向かって走り出す。「東京ラブストーリー」「ロングバケーション」など多くの人気テレビドラマを手掛けた永山耕三監督が、恋や友情に悩みながら少しずつ成長していく若者たちの姿を爽やかに描く。", "2006-08-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("快傑黒頭巾 爆発編", "快傑黒頭巾 爆発編", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 60, "大友柳太朗が一人９役を演じ、大阪城落城の時に残された秘宝の謎をめぐって活躍するシリーズ中の傑作。この秘宝の地図を持つ外国人ゴーラム博士は、秘宝発見を条件に、幕府に液体火薬を渡すという密約をする。これを聞きつけた黒頭巾は、地図を奪取。その地図は、黒頭巾の父・山鹿土行の手に渡るのだが……。黒頭巾に襲いかかる危機また危機の手に汗握る展開が見どころ。", "1959-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("猛龍過江／THE WAY OF THE DRAGON", "最後のブルース・リー　ドラゴンへの道", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "リーが名製作者チョウと組み、一人５役をこなした自作自演の作品。イタリアの中華レストランで働く、タン・ロンは、その店の買収を迫るヤクザと闘うはめに陥るが……。ダブル・ヌンチャクを使うリーの爽快なアクションが見もの。", "1973-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WALK， DON'T RUN！", "歩け走るな！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "'64年の東京オリンピックを背景にしたハリウッド映画。本作まではヒッチコック監督のサスペンス映画での役柄が似合っていたC・グラントが、新境地と言えるコミカルな演技を披露したラブ・コメディだ。東京にビジネスでやって来た英国の実業家だが、ホテルが取れず英国大使館の世話で外国人女性のアパートの隣室を借りることに。そこへ、五輪の競歩競争に参加する外国人青年も同居して……。", "1966-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Teamamerica: World Police", "チーム★アメリカ　ワールドポリス", "", "", "http://image.eiga.k-img.com/images/movie/1192/315x210.jpg?4765", 98, "世界の平和と秩序を守る国際組織“チーム・アメリカ”の面々は、ブロードウェイで活躍する俳優、ゲイリーをスカウトし、彼の演技力で潜入捜査をさせるが……。「サウスパーク／無修正版」のトレイ・パーカーとマット・ストーンが送り出す過激なパペットアニメ。約1メートルの人形にあわせて、セットを作って撮影した。「マトリックス」「スパイダーマン2」のビル・ポープが撮影監督を務めるなど、スタッフも豪華。", "2005-07-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("さよならジュピター", "さよならジュピター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 130, "西暦2125年、生活圏拡張のため木星を第２の太陽にすることが決定、自然保護を訴える団体と国家との対立が生まれる。SF作家・小松左京が自らの原作を脚本化、総監督を務めて製作にもかかわったSFロマン。CGも駆使した特撮シーンが見もの。", "1984-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("夢の祭り", "夢の祭り", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "著書『紙ヒコーキ通信』で映画への思い入れを知られる直木賞作家・長部日出雄が、長年温めてきた企画で監督デビュー。昭和初期の津軽の山村で、津軽三味線にとりつかれた若者が、三味線競争でライバルに敗れ、修行の旅に出る。旅の途中、様々な人々との出会いのなかで自分を見つめ直し、１年後、再び彼はライバルと対決する。", "1989-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("No Reservations", "幸せのレシピ", "", "", "http://image.eiga.k-img.com/images/movie/34112/315x210.jpg?1450", 104, "ドイツ映画「マーサの幸せレシピ」をキャサリン・ゼタ＝ジョーンズ主演でリメイクしたロマンティック・コメディ。「シャイン」のスコット・ヒックス監督がメガホンを取る。マンハッタンにある高級レストランの料理長を務めるケイトは、突然の事故で姉を亡くし、9歳の姪ゾーイを引き取ることに。一方、職場には新進気鋭の料理人ニックが副料理長として加入する。完璧主義者のケイトは陽気な性格のニックに反感を募らせていくが……。", "2007-09-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE WATERBOY", "ウォーターボーイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "ハリウッドの人気コメディアン、A・サンドラー主演作で、1998年度の全米興行収入５位という大ヒットを飛ばしたコメディ。猛烈なママと二人暮らしのマザコン気味の小心男ボビーは名門大学のアメフト・チームのウォーターボーイ(給水係)をクビになり、ダメ大学チームに雇われる。だが、ここでもイジメられた彼はついにキレ、花形選手を吹っ飛ばしてしまう。ボビーのタックルに才能を見いだした監督は彼を選手として起用、連戦連勝を重ねていく……。今まで自分をイジメた人間を思い出し、その怒りでタックルを繰り返すボビーの暴れっぷりに爆笑。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE BLACK SHIELD OF FALWORTH", "フォルウォスの黒楯", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "15世紀初頭。農夫の出だが貴族のもとで厳しい訓練を積み、やがて王の前で御前試合をするまでになったマイルズ。国王は彼に騎士の称号を授けることにするが、そのとき彼が手にしていた黒楯から、彼がかつて反逆罪で一族刑罰となったはずのフォルワース侯の遺児であることが判明して……。ユニバーサル社の記念すべきシネマスコープ第１回作品。", "1954-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Hollow Man", "インビジブル", "", "", "http://image.eiga.k-img.com/images/movie/1092/315x210.jpg?4347", 112, "天才的だが傲慢な科学者セバスチャンは、政府から最高機密プロジェクトを任されていた。目標は人間を透明にすること。彼はすでに透明化する血清を発明、動物を透明にするところまでこぎつけていた。問題はいかに元に戻すかだ。そんなある日、セバスチャンはついに復元する方法を突き止める。そして、透明化しているゴリラで実験を試みるのだが……。", "2000-10-14", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("獵場札撒／ON THE HUNTING GROUND", "狩り場の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 83, "「青い凧」のティエン・チュアンチュアンが内モンゴル深くに入って撮り上げた、事実上のデビュー作（長編第１作は児童映画「九月」)。13世紀にジンギスカンが定めた“掟”を今も守りながら、“狩り”を続ける蒙古民族。そこで起きた“掟破り”のドラマを通して、この民族の情熱や躍動感、土着性をドキュメンタリー・タッチで表現していく。ストーリー性を極力排した徹底的な客観描写や流れるようなカメラワークが秀逸。", "1985-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("EXTREME JUSTICE", "必殺処刑コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "ロサンゼルス・タイムズにスクープされた衝撃的な事実をもとに描かれる、社会派のバイオレンス・アクション。ロス市警の内部に実在する極秘の超法規部隊“SIS”。法で裁けぬ極悪人を抹殺するこの部署に配属された若きはみだし刑事の活躍と、任務とモラルの狭間に立たされた苦悩をリアルに描いていく。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BATTLETRUCK", "バトルトラック", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 93, "戦争が終わり、世界的に石油資源が枯渇し、無法状態に陥った近未来を舞台に、凶悪なバイオレンス軍団に一人戦いを挑む若者の姿を描いたアクション映画。巨大な戦闘用装甲トラックを操り、悪行のかぎりを尽くす一味に対してバイク一台で対決する彼の運命は？", "1982-01-01", "31", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Moonwalker", "ムーンウォーカー", "", "", "http://image.eiga.k-img.com/images/movie/49992/315x210.jpg?4091", 93, "マイケル・ジャクソンの初主演作。世界中の子供たちをドラッグ中毒にして地球征服をたくらむ悪の組織を倒すため、マイケルが巨大ロボットに変身する。特殊メイクのリック・ベイカー率いる最高のSFX技術陣による驚異の映像と、J・レノンの息子・ショーンが本作で映画デビューしたのことも話題となった。", "2009-11-07", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("歓呼の町", "歓呼の町", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 73, "1943年「花咲く港」でデビューした木下惠介監督の第３作。戦時下、人々が一人、また一人と疎開して消えてしまった町で、家を出たままの夫を待ち続ける母・きよとその息子・慎吾を中心に、なおも町に残ろうとする数少ない家族たちの人生模様を描いていく。ある日きよの夫が戻ってきて、一家に再び幸せが訪れたかに見えたが……。現存フィルムは冒頭が欠けている。DVDは「木下惠介　DVD-BOX 第１集」に収録。", "1944-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FLED", "F.L.E.D", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "名作「手錠のまゝの脱獄」の現代版リメイク。脱獄した囚人コンビの逃亡劇をユーモアたっぷりのセリフとアクションを交えて描く。ハッキングの罪で服役していた白人青年ドッジは、野外作業中に黒人の囚人パイパーといがみ合ったため、彼と手錠でつながれてしまう。その矢先、暴動が発生し、二人は騒ぎに紛れて逃亡。しかし、この騒ぎはドッジが偶然入手した、マフィアの犯罪証拠となるフロッピーを奪還するための何者かのワナだった。", "1996-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("N43°", "N43°", "", "", "http://image.eiga.k-img.com/images/movie/54392/315x210.jpg?3666", 121, "北海学園大学演劇研究会出身の戸次重幸、森崎博之、安田顕、音尾琢真、大泉洋で結成される演劇ユニット「TEAM NACS」の5人が、各々脚本と監督を担当して制作したショートムービーのオムニバス。「北海道」を共通のキーワードに、ジャンルもキャストも様々な5つの物語が展開する。「TEAM NACS」のほか、夏八木勲やお笑い芸人の安田大サーカスらも出演。", "2009-02-21", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE GARDEN", "ザ・ガーデン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 85, "イギリスの鬼才、D・ジャーマンが二年間にわたって、自らの心象風景と自宅からの眺めを切り取って作った作品。イギリスの海岸沿いにある小さな町ダンジェネス。ジャーマンの家はここにあり、その裏には原子力発電所がある。彼はここで、自分の“庭”を撮ろうとするが……。", "1990-01-01", "3,76,7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Trapped", "コール", "", "", "http://ecx.images-amazon.com/images/I/51DSXRC69AL._SL210_.jpg", 106, "グレッグ・アイルズのベストセラー小説『24時間』を映画化した本格サスペンス。「アイ・アム・サム」でショーン・ペンの娘役が絶賛された天才子役ダコタ・ファニングの迫真の演技に注目だ。幸福な日々を送っていた麻酔医ウィル夫妻の６歳の娘アビーが誘拐された。犯人たちは家族を別々に監禁し、30分ごとの電話連絡をしなければアビーを殺すというが、彼女には一度発作を起こすと死に至るぜん息の持病があった。", "2003-12-20", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELL BOATS", "地獄の艦隊", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "第二次世界大戦下、ドイツ軍に包囲されたマルタ島を死守するため、シシリーのドイツ軍要塞へ体当たりの攻撃を仕掛けるイギリス海軍兵士たちの活躍を描く。紺碧の海を背景にした熾烈な戦闘シーンが見もの。", "1970-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大阪極道戦争 しのいだれ", "大阪極道戦争 しのいだれ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 97, "暴力団新法、バブル崩壊と厳しい状況にある現代のヤクザ組織。ここでは、大阪でも弱小の吉留組２代目・吉川一平を主人公に、カッコ悪くも日々のシノギを続けるヤクザの姿を描いている。ヒモのような生活を続けながらも、どこか憎めない吉川を役所広司が好演。吉川と友情で結ばれる、不動産会社の若社長・島本を演じた阿部寛との対照的なキャラクターのコンビネーションも見もの。", "1994-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ZAZIE DANS LE ME´TRO", "地下鉄のザジ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "処女作「死刑台のエレベーター」、第２作「恋人たち」と一作ごとにテーマを変え、斬新な映像を作り上げていたマルの第３作。フランス・アカデミー会員、クノーの小説の映画化。田舎からパリに遊びに来た、10歳の少女ザジの36時間にわたるパリ見物の物語。ザジがいちばん楽しみにしていたのは、街の地下鉄に乗ることだった。ところがあいにく地下鉄はストライキのさなか。彼女は出迎えに来てくれたガブリエル叔父さんと一緒にパリ見物をする。翌日、ザジは一人で勝手に街へ出た。口ヒゲの怪しい男がザジを追いかけ回したり、エッフェル塔で垣間見る大人たちのアバンチュールの数々……。ザジはパリ見物で実に多くのものを見て経験したように思った。マル監督はザジの36時間の出来事を、スラップスティック・コメディの手法で活写し、子供の目が捉えたパリの街を鮮やかに映像化する。モダンジャズの手法によるF・カルピの口笛のテーマ曲も印象的。", "1960-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("不機嫌な果実", "不機嫌な果実", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "話題を呼んだ林真理子の同名ベストセラーの映画化。結婚６年目を迎えた麻也子は夫とのセックスにも満たされず、“私は損をしているのかも”という思いに囚われていた。そして、過去の恋人である野村修と会い、情事を重ねるが、彼が他の女とも関係を持っていることを知って決別する。そんなある日、麻也子は勤務する製薬会社の会長の甥、通彦と出会う。クラシック音楽に精通し、イタリア留学を目指している繊細な彼に麻也子はどんどん惹かれていく……。脚本は「失楽園」の筒井ともみ。", "1997-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Faculty", "パラサイト", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 104, "「スクリーム」のK・ウィリアムソン脚本、「フロム・ダスク・ティル・ドーン」のR・ロドリゲス監督がボディスナッチ（人体乗っ取り）ものに挑んだSFサスペンス。郊外の高校に通うケイシーはグランドで奇妙なサナギのようなものを発見。それは水槽に入れると突然変異を起こし、二つに分裂した。そのころから周囲の人間の奇怪な行動に気付き始めたケイシーは人間が何物かに寄生されているのではと考え、それを６人の仲間に話す。最初はバカにしていた彼らだったが、成長したサナギに乗っ取られた教師の姿を目撃したことから“それ”と戦うことになる……。学校という限られた空間の中で展開する恐怖をロドリゲス監督がスピーディーな演出で捉えていく。", "1998-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("VALMONT", "恋の掟", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 137, "ロジェ・ヴァディム、スティーヴン・フリアーズの手によって、これまで二度映画化されているラクロの“禁断の書”『危険な関係』。本作はその３度目の映画化で、演出には鬼才M・フォアマンがあたっている。18世紀、斜陽のフランス貴族社会。若き未亡人メルトゥイユ侯爵夫人は、名うてのプレイボーイ、ヴァルモン子爵と共謀し、自分を裏切った男への復讐を企てる。男の婚約者セシルの処女をヴァルモンに奪わせる腹づもりだ。だが、ヴァルモンは貞淑な人妻トゥーヴェル夫人に夢中で、一方のセシルも音楽教師との叶わぬ恋に身を焦がし……。登場人物の深みのある心情描写が、いかにもフォアマンらしい。", "1989-01-01", "2,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("国定忠治", "国定忠治", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 114, "第二次大戦後、製作を再開した日活の第１回公開作。それを記念し、辰巳柳太郎、島田正吾ら、新国劇の面々が総出演。辰巳が舞台でも得意の国定忠治に扮して、豪快な殺陣を披露している。「足摺岬」「七人の侍」と、この年、その清楚な美貌の頂点にあった特別出演の津島恵子が、ひときわ光る一編。", "1954-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ANTHONY ADVERSE", "風雲児アドヴァース", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 138, "親を知らずに育った男の波乱の生を描く人間ドラマ。実の父親は決闘によって殺され、母は出産後に死亡。孤児アンソニーは尼僧に育てられ、やがて商会に奉公に出る。その主人は、実はアンソニーの実の祖父であった。祖父の手引きで紳商となったアンソニーは、その後も数奇な人生をたどり……。ドラマティックでスリリングな展開が好評を呼び、アカデミー賞４部門を制した。", "1936-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JEUX　D’ENFANTS", "世界でいちばん不運で幸せな私", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 94, "子供時代にある“ゲーム”に興じ、大人になってもお互いを笑い、傷つけ合う男女の奇妙な関係を綴る恋愛映画。ひたすらエスカレートする二人の駆け引きを描いた奇想天外な物語と、ファンタジックな詩情あふれる映像世界を紡ぎ上げたのは、フランスの新人監督Y・サミュエル。ヒロインに扮する「TAXi」シリーズのM・コティヤールの小悪魔的な魅力も注目。幼なじみのジュリアンとソフィーは、子供の頃から様々な“ゲーム”を相手に仕掛けてきた。鉄則は、相手の出した条件には絶対に従う、ということ。最初は無邪気な遊びだったが、次第に常識を逸脱した倒錯の世界へと突入し……。", "2003-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Never Back Down", "ネバー・バックダウン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "", "2008-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("BLOOD RING", "バーニングファイター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "元WAKミドル級チャンピオンのD・A・コックと、かつて前田日明と名勝負を繰り広げた、元全米プロカラテ・チャンピオンD・N・ニールセンの二大格闘家が共演する、アクション映画。クライマックスに二人が対決する試合方法は、“有刺鉄線デスマッチ”。本物の格闘シーンがたっぷりと楽しめる。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("UKオペラ＠シネマ　フンパーディンク：ヘンゼルとグレーテル", "UKオペラ＠シネマ　フンパーディンク：ヘンゼルとグレーテル", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "イギリス郊外のオペラ・ハウスで5月から8月にかけて開催される格式高い夏の祭典・グラインドボーン音楽祭で、2008年に上演されたフンパーディンクの傑作オペラ「ヘンゼルとグレーテル」をデジタルシネマ化。道に迷った兄妹が不思議な世界に足を踏みいれていく様子が、フランスの気鋭ロラン・ペリによるユニークで刺激的な演出によって描かれていく。指揮を務めるのは、世界的に活躍する日本人若手指揮者・大野和士。", "2009-01-03", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("非女子図鑑", "非女子図鑑", "", "", "http://image.eiga.k-img.com/images/movie/54342/315x210.jpg?3689", 105, "従来の“女子”の定義から外れた“非女子”をテーマにした短編オムニバス。お笑い芸人の鳥居みゆき、片桐はいり、仲里依紗らが各作品の主演を務め、監督を「呪怨」の清水崇や「地獄甲子園」の山口雄大らが務める。エデンの園の非女子“イブ”に始まり、現代の6人の非女子が自らの欲望に忠実に生きる様を描く。", "2009-05-30", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("白百合クラブ 東京へ行く", "白百合クラブ 東京へ行く", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 90, "「ホテル・ハイビスカス」の中江裕司監督が自主製作したドキュメンタリー。戦争で傷ついた人々の心を癒すのを目的に、終戦直後に結成されたバンド“白百合クラブ”の活動を追う。終戦後56年たった今も不動のメンバーで唄い続ける彼らの石垣島白保での生活と2002年10月に行われた東京でのライブを主に収録。その独特の歌唱法によるユニークなライブは必見だ。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DIARY OF HITMAN", "錆びついた銃弾", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "殺し屋デッカーは、闇の仲介者コーニッグからジジックという依頼人を紹介される。ジジックは、麻薬中毒の妻ジェインと生まれたばかりの我が子の死を望んでいた。女と子供を殺すという仕事に嫌悪を感じながら、破格の報酬を提示されたデッカーは、ジェインの住むアパートへ向かうのだったが……。デッカー役を「バード」や「クライング・ゲーム」で高い評価を得たF・ウィテカーが演じるハードボイルド・サスペンス。S・ストーンとJ・ベルーシもゲスト出演している。", "1991-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ALONE IN THE DARK", "アローン・イン・ザ・ダーク", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "アクション・ホラーの雄、ウーヴェ・ボル監督が、「バイオハザード」シリーズの原点とされる3Dゲームを実写映画化。邪悪な悪魔の蘇生をめぐる、超常現象調査員の奔走を描く。主演C・スレーターのキレのある演技も見逃せない豪快作。超常現象を調査するエドワードは、古代アビカニ族の遺物を偶然手に入れるが、それを狙う何者かに襲われる。その後、彼と同じ孤児院で育った19人全員が失踪する奇怪な事件が発生する。", "2006-05-20", "10,7,1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("立喰師列伝", "立喰師列伝", "", "", "http://ecx.images-amazon.com/images/I/512R0X7M89L._SL210_.jpg", 106, "「攻殻機動隊」などで世界的に注目を集めてきた押井守監督が、これまで多くの作品で繰りかえし描いてきた“立ち食いのプロ＝立喰師”をモチーフに撮りあげたコメディ作品。ただ一食のために己の全てを賭けて飲食店主に挑む立喰師たちの奮闘を、デジタル写真を3DCGアニメ化した新感覚の映像技法“スーパーライブメーション”を駆使して描きだす。キャスト・スタッフには、日本を代表するトップクリエイターたちが集結。", "2006-04-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("FREE", "FREE", "", "", "http://image.eiga.k-img.com/images/movie/57372/315x210.jpg?4240", 20, "芸能事務所イトーカンパニー所属の俳優たちが出演するショートムービーをネット配信している「イトーチューブ」で公開された作品を映画館で上映する「イトーチューブ Shrot Movie Colleciton」の1本。2009年に劇場公開されたドキュメンタリー「バカは2回海を渡る」の劇中にも登場した俳優・弓削智久の監督作。広大なアメリカを旅する2人の男の姿が描かれる。同じくイトーカンパニーに所属する「RIZE」の金子ノブアキが音楽を担当。", "2011-11-12", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE MASQUE OF THE RED DEATH", "赤死病の仮面", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "コッポラやジョー・ダンテをはじめとして優秀な若い才能を世に放っていることで知られる、アメリカB級映画界の首領コーマン。これは彼自身が演出した最高傑作とされる１本だ。劇場では、第２回東京国際ファンタスティック映画祭で上映されたのみなのが不思議なくらいである。中世のイタリア。黒死病が蔓延した村では、悪魔を崇拝する領主プロスペロが城内に閉じこもり、いつ果てるともない狂乱の宴をもっていた。そこに現れた赤い頭巾の男。頭巾の下はプロスペロと同じ顔の死神だった。めくるめく色彩が目に見えぬ恐怖を醸し出す。", "1964-01-01", "1,3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("熱情の翼", "熱情の翼", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 72, "新興キネマからのちに東映に移籍し、“魚河岸の石松”などのヒット・シリーズで知られる小石栄一が大陸出征の帰国後に発表した航空機製造をめぐる軍事スパイ映画。美術を新藤兼人が担当している。なお、当時の新藤は溝口健二の下で「愛怨峡」「元録忠臣蔵」などの美術助手を務めていた。", "1940-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("阿片戦争", "阿片戦争", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "戦時中の時局に迎合して企画されたもので、イギリスの中国への不当な干渉・阿片戦争を題材にしている。しかし単なる国策映画に止まらず、D・W・グリフィス監督の「嵐の孤児」に想を得た、日本映画史のなかでも傑出のスペクタル史劇となっている。物語の舞台は1839年の中国で、阿片を流し侵略を計るイギリス側と、阿片から人民を守ろうとする清の役人・林則徐との虚々実々をめぐる戦いと、戦乱に巻き込まれて離れ離れになる悲劇の姉妹の物語を並行的に描く。美しい原節子と高峰秀子の姉妹に涙すると同時に、俯瞰で捉えられたミュージカル場面の素晴らしさにも心踊らされるバラエティーに富んだ作品。", "1942-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A計劃：續集／PROJECT AII", "プロジェクトA２ 史上最大の標的", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 105, "大ヒット作、「プロジェクトA」の続編。20世紀初頭の香港を舞台に、水上警察、陸上警察が清朝に抵抗する革命軍、暗黒街のボスに敢然と立ち向かっていくさまを、ド迫力のアクション・シーンを満載して、スリリングに描き上げたエンターテインメント大作だ。", "1987-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("La posesion de Emma Evans", "エクソシズム", "", "", "http://image.eiga.k-img.com/images/movie/56362/315x210.jpg?4345", 100, "かつて悪魔払いに失敗し少女を死なせてしまった神父クリスは、汚名返上のために悪魔が実在することを証明しようと考えていた。そんな折、15歳の姪エマが白目をむいて倒れ、幻覚や幻聴に悩まされるといった異変が生じる。エマに悪魔がとりついたと主張するクリスはエクソシズム（悪魔払い）を行い、その一部始終をビデオに録画していたのだが……。「REC レック」のプロデューサー、フリオ・フェルナンデスが手がけたサスペンスホラー。", "2011-06-25", "9", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("中国武術／THIS IS KUNG FU", "燃えよカンフー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 78, "'82年のカンフー映画「少林寺」でデビュー、今やハリウッドで大活躍しているジェット・リーが、本名リー・リンチェイの名で出演した中国武術のドキュメンタリー・フィルム。拳法だけでなく刀術、槍術、棒術などを含む少林拳をはじめ、ジャッキー・チェンでもお馴染みの蛇拳、酔拳、太極拳ほかが紹介される。クライマックスでは中国全国武術大会の熱気あふれる模様が伝えられる。", "1983-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("日本侠客伝", "日本侠客伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "舞台は深川木場。木場政組と新興の沖山運送との間には衝突が絶えなかった。木場政は小頭・辰巳の長吉を中心に巻き返しを図るが、悪らつな沖山は政治家や警察を抱き込み、木場政を潰しにかかる。見かねた木場政の客分・清治は単身沖山に乗り込むが……。", "1964-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE SUMMERTIME KILLER", "サマータイムキラー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 103, "マフィアに父を殺された青年がマフィアたちを殺し、一人残った大物を追ってポルトガルへ。しかし歯がたたないことを知った青年はフランスにいる大物の娘を誘拐する……。S・アバーバネルの短編小説の映画化。", "1973-01-01", "4,9,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("超劇場版ケロロ軍曹　誕生！究極ケロロ　奇跡の時空島であります!!", "超劇場版ケロロ軍曹　誕生！究極ケロロ　奇跡の時空島であります!!", "", "", "http://image.eiga.k-img.com/images/movie/54702/315x210.jpg?1653", 0, "吉崎観音による人気コミック「ケロロ軍曹」の劇場版第5弾。地球侵略をもくろむケロロは、日向家に居候するカエル型宇宙人。ある日、冬樹のもとにケロロそっくりな奇妙な石像が届き、一同はその謎を解くため、絶海の孤島・イースター島へと向かう。そこでケロロたちは愉快な精霊・マナや、不思議な双子イオとラナに出会う。主題歌を平原綾香が担当。「超電影版SDガンダム／三国伝　Brave Battle Warriors」と2本立て同時上映。", "2010-02-27", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ウィニング・パス", "ウィニング・パス", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "アジアでは初となる世界車椅子バスケットボール選手権大会“ゴールドカップ”開催地の福岡県北九州市を舞台に、「チンパオ」の中田新一監督が、車椅子バスケに情熱を注ぐ一人の若者を描いた青春群像ドラマ。交通事故で下半身不随になってしまった高校生が、周囲の人々に支えられつつ、生きる希望を取り戻していくまでの姿をさわやかなタッチで綴る。北九州市の高校生、小林健太は、バイク事故が原因で下半身不随となり、車椅子の生活を余儀なくされてしまう。現実を受け入れられず、自暴自棄の生活を送る健太だが、リハビリ中に車椅子バスケの試合を目の当たりにし……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("心の中", "心の中", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 89, "「ターチ・トリップ」などで知られ、現代美術界からも注目を集める実験映画作家、大木裕之が、従来のホモセクシャルな詩的映像表現をさらに推し進めたイマジネイティブな作品。海辺で“心中”を演じる二人の青年。彼らが波打ち際に身を横たえると、やがて空には夕焼けが広がり、潮が満ちる。水没しかけた二人が立ち上がり、そこに大木自身のプライベート・フィルムともいうべき様々な映像群が折り重なっていく。ドラマとドキュメンタリーの共鳴によって、潮の満ち引きにも似た心象風景が展開。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CENTER OF THE WEB", "標的の罠", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "カーチェイスや銃撃戦が盛りだくさんの、巻き込まれ型サスペンス・アクション。突然、何者かに間違われた主人公、ジョン・フィリップス。激しいカーチェイスと狙撃の末、殺人容疑で役人の前に連行されたジョンは、釈放の条件として“間違えられた男”に成りすますことを言い渡される。ジョンの殺人犯としての逃亡が始まる。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("コトリタチノ楽園", "コトリタチノ楽園", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 53, "ペットをめぐる人間模様を題材に6人の監督が競作した「PETBOX ペットボックス」シリーズの1作。両親に捨てられてしまった幼い兄妹・エイチとココロは、捨てネコのようなナゾの少女・アサキと出会う。兄妹はアサキとともに“移動動物園”をしながら旅を続けるが……。アサキ役に、「2番目の彼女」「ストロベリーショートケイクス」の前田綾花。黒沢清、塩田明彦ら気鋭監督たちの下で助監督を務めてきた毛利安孝の監督デビュー作。", "2006-12-14", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("月光の囁き", "月光の囁き", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 100, "喜国雅彦の同名漫画を、これが監督デビュー作となる塩田明彦が映画化。ごく平凡なカップルとして出発した高校生二人。ところが、少年が少女に対してひそかなフェチ趣味を抱き続け、それが発覚したことから、彼らは坂道を転げ落ちるように、禁断の絆を結んでいく。異様な状況に抗いながらもサディストとしての資質に目覚めていく女と、マゾヒストとしての自分を開花すればするほど純化していく男。ありきたりなゲームとしてのSM関係ではなく、いわば互いを育み成長させていく“喪失”と“発見”の旅模様。方言による効果的なセリフ使いもあり、生々しいモチーフを一種のファンタジーとして着地させることに成功している。", "1999-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("童貞放浪記", "童貞放浪記", "", "", "http://image.eiga.k-img.com/images/movie/54162/315x210.jpg?3420", 98, "ベストセラー作家・小谷野敦の自伝的小説をもとに、30歳の童貞男の恋と苦悩をコミカルに描いた人間ドラマ。東大出身の大学講師・金井淳は、30歳にして童貞というコンプレックスを抱えていた。ある日、彼は出張先で大学院の後輩・北島萌と再会する。連絡を取り合ううちに萌に恋心を抱くようになった淳は、ついに童貞からの卒業を決心するが……。「リアリズムの宿」などの個性派俳優・山本浩司が、恋愛下手な男の悲哀と純情をリアルに好演。", "2009-08-08", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("TRZECIA CZESC NOCY", "夜の第三部分", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 107, "アバンギャルドで不条理な世界を描く、異才ズラウスキーのデビュー作。第二次大戦中のポーランド。人里離れた屋敷に住むミハウは、留守中にナチスの騎兵の襲撃を受け、妻子を失ってしまう。街に戻り、抵抗運動に参加したミハウは、作戦中に身元が発覚し追われる身に。逃亡しているうちに、ひょんなことから彼の身代わりとなって逮捕された男の妊婦妻マルタは、驚くほどミハウの妻に似ていた。突然産気づいたマルタは、ミハウの助けもあって無事出産するが、ケガと疲労で気絶したミハウを残し、赤ん坊とともに消えてしまう。ミハウはやっとの思いで二人をとある養老院で見つけるが、なんとそこは自分の家。彼は自分の人生をもう一度やり直していることに気づくのだった……。", "1971-01-01", "42", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Roadie", "ローディー", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "　電気機械に詳しいテキサスの青年トラビスがその才能のお陰でコンサートの裏方“ローディー”として雇われる。やがて彼はグルーピーの娘ローラに恋し、アリス・クーパーに会いたいという彼女の夢をかなえる手助けをすることに……。音楽業界を舞台にしたコメディ。アリス・クーパー、ブロンディ、ロイ・オービソンら人気アーティストが出演。", "1980-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ISLAND IN THE SUN", "日のあたる島", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 119, "イギリス領西インド諸島の架空の島サンタ・マルタに生活する人々の人種的悩み、複雑な恋愛観などを描いた、イギリスの小説家A・ウォーの同名小説の映画化。住民の９割が黒人系というこの島で、ある日実権を握る総督のレセプションがあり、そこで様々なロマンスが生まれる。", "1957-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("瞼の母", "瞼の母", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 84, "長谷川伸の同名戯曲の３回目の映画化。番場の忠太郎が幼くして別れた母を求めてさすらう股旅ものである。松竹スターの林長二郎が東宝へ移り、本名の長谷川一夫に戻ってからの第２作にあたる。当時、彼は移籍が原因で切られた頬の傷をいかに隠すか、苦労して撮影に臨んでいたという。", "1938-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Paranormal Activity", "パラノーマル・アクティビティ", "", "", "http://image.eiga.k-img.com/images/movie/54952/315x210.jpg?2651", 86, "新鋭オーレン・ペリ監督がビデオカメラを使用し、わずか1万5000ドルという超低予算で製作したホラー。2009年10月に全米公開され大ヒット、5週目にして全米ナンバーワンまで上りつめた。米サンディエゴで暮らす若いカップル、ミカとケイティは夜中に家の中で不審な現象が起こっていることに気づく。ミカがその原因を探るべくビデオカメラを購入し、寝室に設置すると、ビデオには衝撃の映像が映っていた……。", "2010-01-30", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Original Kings of Comedy", "キング・オブ・コメディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "　アメリカで大人気のスタンダップ・コメディアン4人によるツアーの模様を収録。ラッパーやスポーツ・パーソナリティに扮した彼らが世相やヒット映画までメッタ斬り。ホットな芸人たちの過激な話芸を詰め込んだ爆笑ライブ・フィルム。", "2000-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("鬼畜大宴会", "鬼畜大宴会", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 106, "PFF1997で準グランプリに輝き、スキャンダラスな話題を呼んだ問題作。1970年代の学生運動グループの内紛劇を、学生監督とは思えない確かな構成力で見せた力作だが、後半エスカレートする残酷描写の数々が、生理的拒否感も伴った賛否両論を引き起こす結果となった。カリスマ的リーダーの獄中死に揺らぎ、崩壊していくある集団の末路。政治的な思惑やホラー映画的な感触を越えて、友情と裏切りのドラマをしたたかに見つめる監督の視線はどこまでもクールだ。16ミリ作品だが、そのまま一般劇場で公開されるという、PFF史上初の快挙を成し遂げている。", "1998-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("大日本スリ集団", "大日本スリ集団", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "藤本義一原作・脚本によるペーソスあふれるコメディ・タッチの人情劇。関西に平平平平（ひらだいらへっぺい）というスリの名人とスリを最も嫌う刑事船越がいた。二人は実は戦友であり、追いかけ合いながらもどこか心が通じていた。が、ひょんなことからいがみ合うことになった二人は暴力団の企みに巻き込まれていく……。", "1969-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("のだめカンタービレ　最終楽章　前編", "のだめカンタービレ　最終楽章　前編", "", "", "http://image.eiga.k-img.com/images/movie/54302/315x210.jpg?1311", 121, "エリート音大生の恋愛をコミカルに描いた二ノ宮知子の人気コミックをTVドラマ化した「のだめカンタービレ」の劇場版2部作の前編。主演に玉木宏と上野樹里、共演に瑛太、水川あさみ、小出恵介ら若手人気俳優が揃う。プラティニ国際音楽コンクールで優勝した千秋（玉木）は、「ルー・マルレ・オーケストラ」の常任指揮者を務めることになる。しかし、非協力的でやる気の感じられないオーケストラに失望した千秋は、野田恵ことのだめ（上野）に定期公演の「ボレロ」のチェレスタの演奏を依頼するが……。", "2009-12-19", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("M:I-2", "M:I-2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 124, "トム・クルーズ主演のスパイアクション「ミッション：インポッシブル」の続編。休暇中、ロック・クライミングを楽しむイーサン・ハントの元に、またしても指令が届いた。今度のミッションは細菌兵器を狙う元同僚の陰謀を阻止すること。そして、そのためにまずやり手の女泥棒ナイア・ホールに接触せよ、というものだった……。監督に「フェイス／オフ」のジョン・ウーを迎え、2丁拳銃などジョン・ウー流のアクションが満載。", "2000-07-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WHITE LIGHT", "デッドライン・コップ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "死後の世界で出会った女性とのロマンスや臨死体験、さらには主人公の刑事と悪役の対決など、刑事アクションと心理サスペンスに時空を超えた恋愛を盛り込んだ映画。ショーン・グレイグ刑事はおとり捜査で悪の一味ラモンたちを仕留めるが、殺し屋に撃たれ生死の間をさまよう。そこで彼は見ず知らずの女性と愛し合う自分を見る。", "1990-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Veronica Guerin", "ヴェロニカ・ゲリン", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "実在したアイルランドの女性ジャーナリストの生きざまを「フォーン・ブース」のジョエル・シュマッカーが映画化。夫と子供たちと平穏な日々を送る新聞記者ヴェロニカ。だが彼女は、街の子供たちが麻薬犯罪の犠牲になっていることに憤って取材をし、ある麻薬組織のボスに辿り着く。が、彼女の報道を阻止しようと組織が脅迫してくる。「ロード・オブ・ザ・リング」「ミッシング」に続き、「アヴィエイター」が控えるケイト・ブランシェットが熱演。", "2004-05-29", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("刺青一代", "刺青一代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "独特の様式美が爆発、ファンを熱狂させた鈴木清順監督の秀作。日活B級アクションとして作られたありきたりの任侠映画のようでありながら、ラストに至って突如、歌舞伎にも似た舞台的＝作劇的な展開を見せる。その破天荒なイメージは清順美学の最も顕著な一例として特筆されるものである。昭和の初め、ヤクザの鉄太郎は渡世の掟に従い人を刺した。堅気の弟との別れ際、追手とのいざこざで弟も人を殺してしまう。兄弟はそろって満州へ逃げのびようと、とある港町へ。しかし金をだまし取られ足止めを食う。旅費を稼ぐため人夫としてまじめに働く二人だったが、追っ手に執拗に追われ、またその町のヤクザの争いにも巻き込まれて、兄をかばうように弟は切り殺されてしまう。守るものを失った鉄太郎は怒りを胸に一人、組の屋敷へ殴り込む。血に染まる空、轟く雷鳴、次々に開かれる襖の色・色・色……。背中一面に鮮やかな刺青を見せながら、激しい立ち回りの末、鉄太郎はついに復讐を遂げるのだった。", "1965-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("わが心の歌舞伎座", "わが心の歌舞伎座", "", "", "http://image.eiga.k-img.com/images/movie/55732/315x210.jpg?3868", 167, "2010年4月、これまで数々の名舞台を生み出してきた東京・銀座の歌舞伎座が、建て替えのため59年の歴史に幕を下ろした。2009年1月の「古式顔寄せ手打ち式」から2010年4月30日の閉場式までの16カ月間にわたる歌舞伎座さよなら公演に密着し、歌舞伎の真髄に迫るドキュメンタリー。豪華な顔ぶれの名優たちが歌舞伎座への思いを語るほか、初公開となる稽古（けいこ）風景や舞台裏の様子などを映し出していく。", "2011-01-15", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("初恋", "初恋", "", "", "http://image.eiga.k-img.com/images/movie/1422/315x210.jpg?4523", 114, "1968年、東京・府中で実際に起こった「3億円事件」の犯人が18歳の女子高生だったらという大胆な仮定をもとに、その女子高生の初恋の思い出を描いた宮崎あおい主演の青春ドラマ。監督は、林海象などの助監督を務めた後に「tokyo skin」（96）を発表し、それ以来の長編となる塙幸成。共演に小出恵介、宮崎将、小嶺麗奈、藤村俊二ら。", "2006-06-10", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Last Exorcism", "ラスト・エクソシズム", "", "", "http://image.eiga.k-img.com/images/movie/55712/315x210.jpg?2698", 88, "「ホステル」のイーライ・ロス製作によるモキュメンタリー・ホラー。ルイジアナ州バトン・ルージュに住む福音派の牧師マーカスは、悪魔払いの真実を暴く告白ドキュメンタリーへの出演を許諾し、撮影クルーとともに同州の牧場を訪問。ネルという少女の悪魔払いを行うことになる。マーカスはこれを最後の悪魔払いにするつもりだったが、撮影中に異変が起こり、ネルが周囲の人間を襲い始める。監督はドイツ出身のダニエル・スタム。", "2011-10-08", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("A MONTH BY THE LAKE", "湖畔のひと月", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 92, "1930年代のイタリア北部・コモ湖を舞台に、独身のまま人生の熟年期を迎えた女流カメラマンの恋の行方を描いた恋愛映画。ミス・ベントリーは、50代の独身カメラマン。父とともに16年間、ひと夏をコモ湖で過ごしてきた彼女は、父亡きあと初めて一人で訪れた湖畔のバルビアネッロ荘で、“やさしさと誠実”を語る素敵な耳の持ち主、中年実業家のウィルショー少佐と出会う。彼こそが自分の生涯をともにする男性だと感じたベントリーは彼の気を引こうと策略をめぐらし、少佐も次第に彼女に惹かれていく。だが、そんな二人の前に美しい子守のミス・ボーモントが現れる。", "1995-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("太陽山／MOUNTAINS OF THE SUN", "太陽山", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "国民党兵士として台湾に渡った夫を福建の山村で待ち続ける女と、何かと彼女を助けてきた薬草採りの男。孤独な二人が生活をともにして30年の月日が流れた頃、音信不通だった夫が台湾から戻って来た……。「晩鐘」（1987）などで知られるウー・ツーニュウが、中国・台湾海峡両岸問題に取り組んだ意欲作。", "1992-01-01", "8", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("THE　STORY　OF　THE　WEEPING　CAMEL", "らくだの涙", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "撮影当時、ミュンヘン映画学校に通う学生だったB・ダバーとL・ファロルニが、卒業制作として発表し、世界各国の映画祭で大反響を呼んだドキュメンタリー。モンゴルのコビ砂漠で暮らす遊牧民族の一家の営みと、家族同然の存在である、母子らくだの愛情をカメラが見つめる。不思議なモンゴル音楽の力で絆を取り戻す親子らくだの姿が感動的。モンゴルの大地で、一頭のらくだが子らくだを出産した。しかし、母らくだは難産の苦しみから、子育てを拒否する。困った遊牧民一家は、町から音楽家を連れてきて、音楽療法を試みることにする。", "2003-01-01", "7", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("RAJU BAN GAYA GENTLEMAN", "ラジュー出世する", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 159, "映画製作本数世界一を誇るインドで、大半を占める娯楽映画の１本。笑いと涙、ロマンス、アクションというあらゆる要素を詰め込んで、一人の青年の出世物語が展開する。ダージリンの大学を卒業したラジューが、ひと旗揚げようとボンベイにやって来る。ひょんなことから知り合ったOLレヌと恋に落ちた彼は、彼女の勤める会社に就職。めきめきと頭角を現すのだが、賄賂事件に巻き込まれるなど次々とトラブルに見舞われる。レヌにも愛想を尽かされ、ラジューは自暴自棄になるのだが……。長尺も感じさせないスピーディーでパワフルな展開。人々が歌って踊る突然のミュージカル・シーンなど、大胆な構成が痛快だ。", "1992-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("爆走デコトラ烈伝", "爆走デコトラ烈伝", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 80, "デコトラ運転手の新藤と舎弟格の自動車修理工・竜也が、土地開発にからむ悪徳組織と対決するトラック・アクション。ある日、新藤は竜也がひそかに思いを寄せるめぐみが営む定職屋が、悪徳不動産業者に立ち退きを迫られていることを知る。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("飼育の部屋・終のすみか", "飼育の部屋・終のすみか", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 98, "監禁事件の裏側を凝視する「飼育の部屋」の続編。前作同様、小沢和義が閉ざされた空間で女性と心を通い合わせようとする孤独な男の狂気を体現。静かだが、鬼気迫る演技で引き込む。社会人一年生の真琴は、毎日利用している駅の清掃員に監禁される。場所は清掃員詰め所。ホームまで30メートルの場所で二人の暮らしが始まる。男はボイスレターで対話を試み、用意されたラジカセに向かって話し始める真琴だが……。", "2003-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ジュブナイル", "ジュブナイル", "", "", "http://ecx.images-amazon.com/images/I/410ZB6QH68L._SL210_.jpg", 105, "VFXマンの山崎貴が監督デビューを果たしたSFファンタジー。2000年の夏。少年少女４人が山の中で小さなロボットを発見。メカに強いユースケがロボットをテトラと名付けて修理を始める。テトラは人間の言葉を話し、高度な知能を持っていた。マニアックな天才物理学者・神崎に協力を求め、彼らは交流を深めていくが、ある日、テトラが行方不明になってしまう。そんな中、人間の体を乗っ取る異星人が出現。地球侵略を企てる彼らと少年たちの闘いが始まる。複雑な動きをするロボットや、巨大ロボット同士の戦闘など、最新技術を駆使した緻密でダイナミックな映像が圧巻。テトラをめぐる友情と少年の初恋のエピソードが巧みに盛り込まれ、切ないドラマで感動を誘う。", "2000-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("郷愁", "郷愁", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 115, "昭和28年の高知・四万十川流域を舞台に、一人の平凡な青年が、料理屋の女主人と暮らしている父親への反発や友人の死、そして唯一の心の支えだった姉の家出といった様々な出来事のなかで、大人へと成長していく姿を描く。中島丈博初監督にして、西川弘志初主演作。", "1988-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("新座頭市・破れ！唐人剣", "新座頭市・破れ！唐人剣", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "座頭市と香港のアクション・スター、ジミー・ウォングとの対決をメインにした、安田公義監督によるシリーズ第22作。ふとしたことから唐人剣士の王と知り合った座頭市は、懸賞金欲しさに王を狙う藤兵衛一家と闘うが、市を裏切り者と誤解した王を図らずも斬ってしまう。", "1971-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("ピューと吹く！ジャガー　いま、吹きにゆきます", "ピューと吹く！ジャガー　いま、吹きにゆきます", "", "", "http://image.eiga.k-img.com/images/movie/54072/315x210.jpg?4318", 0, "うすた京介の大人気ギャグ漫画「ピューと吹くジャガー！」（週間少年ジャンプ連載）を、「秘密結社　鷹の爪」シリーズのFROGMANが映画化したアニメ。声優に「ゆれる」の真木ようこ、「アキレスと亀」の伊武雅刀、板東英二ら。ギタリストを目指しているのに成り行きでジャガー講師のふえ科に在籍するピヨ彦が、自宅のトイレのドアを開けるとそこには別世界が広がっていた……。", "2009-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Benji:Off the Leash!!", "ラブいぬベンジー　はじめての冒険", "", "", "http://image.eiga.k-img.com/images/movie/33732/315x210.jpg?3999", 97, "1974年にアメリカで初公開されて大人気を博し、その後日本でもブームを巻き起こしたファミリー映画「ベンジー」のシリーズ最新作。ぬいぐるみのようにフワフワした毛並みと垂れ耳がトレードマークの愛くるしい犬ベンジーが、悪徳ブリーダーに捕らえられて病気になってしまった母犬を救うために大冒険を繰り広げる。シリーズ全作を手掛けてきたジョー・キャンプ監督が本作でもメガホンを取り、製作・脚本も務める。", "2006-11-11", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Green Mile", "グリーンマイル", "", "", "http://image.eiga.k-img.com/images/movie/1852/315x210.jpg?3354", 188, "大恐慌時代の1935年、ポールは刑務所の看守主任を務めていた。グリーンマイルと呼ばれる通路を通って電気椅子に向かう受刑者たちに安らかな死を迎えさせてやることが、彼らの仕事だった。ある年、この刑務所に身長2メートルを越す黒人の大男、コーフィが送られてくる。双子の少女を殺害した罪で死刑囚となった男だ。ところがこの男は、ある日不思議な力でポールが患っていた病気を治してしまう……。", "2000-03-25", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("DOUBLE　BLIND", "ダブル・ブラインド", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 74, "写真とテキストを並列させ、鑑賞者のイマジネーションを喚起させる独特の手法で知られるコンセプチャル・アーティスト、S・カル。本作は彼女が監督・主演したビデオ作品で、日本では劇場公開された。パリで暮らすカルとニューヨークで生活するG・シェパードが三度のすれ違いの末、1992年に再会。二人は映画作りを前提に、ニューヨークからサンフランシスコまでの自動車旅行を計画した。道中、それぞれはビデオに向き合って、互いへの思いを告白する。やがて二人の関係性は変化していくかに見えたが……。きわめてプライベートな題材ながら、どこかフィクショナルな様式も漂う異色の構成。辛らつさとユーモアを併せ持つカルらしい作品になっている。", "1992-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("海でのはなし。", "海でのはなし。", "", "", "http://ecx.images-amazon.com/images/I/41MU3yQyAiL._SL210_.jpg", 71, "CMディレクターの大宮エリーが映画監督に初挑戦し、人気バンド「スピッツ」の曲をモチーフに、不器用な男女が織りなす恋を綴ったラブストーリー。ごく平凡な家庭で、両親に愛されながら育った楓。ところがある日、実は母親が父親の愛人で、自分は私生児だったことを知る。深く傷ついた楓は博士の元を訪れるが、これまで人とのかかわりを持たずに生きてきた博士は彼女の思いを受け止めきれず……。主演は、NHK連続テレビ小説「純情きらり」でも共演した宮崎あおいと西島秀俊。", "2006-12-16", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("blue", "blue", "", "", "http://image.eiga.k-img.com/images/movie/1302/315x210.jpg?3775", 116, "魚喃キリコの同名コミックの映画化は、TV「つげ義春ワールド／義男の青春」（98）、映画「dead BEAT」（98）などの安藤尋監督が5年間温めてきた念願の企画。「とらばいゆ」の市川実日子、TV「ちゅらさん」の小西真奈美はじめ、今宿麻美、仲村綾乃ら注目の若手女優が共演。高校3年生のカヤ子。そろそろ進路を決めなければならない時期だが、彼女の目は、いつもクラスメートの遠藤を追ってしまう。", "2003-03-29", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("パンツの穴　ムケそでムケないイチゴたち", "パンツの穴　ムケそでムケないイチゴたち", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 88, "若松孝二監督が製作を兼ねた４話オムニバス形式。隣の女子大生に憧れる中学生を描く第１話、悪ガキトリオとAV女優が知り合う第２話、片想いの男女がスレ違いに走り続ける第３話、そして若松監督の第４話は、森沢なつ子のパンティを旗印に、権力への反抗を示す快作だ。", "1990-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("NANA", "女優ナナ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 87, "舞台は1870年頃のパリ。街の娼婦からミュージカル・レビューの大スターになった女が男性遍歴の末に初めて本当の愛を知るが……。自然主義文学の大御所E・ゾラの有名小説の映画化。第２のグレタ・ガルボを狙ってソ連の女優ステンを抜てきしたゴールドウィンの文芸大作。", "1934-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Surrogates", "サロゲート", "", "", "http://image.eiga.k-img.com/images/movie/54512/315x210.jpg?2638", 89, "ブルース・ウィリス主演のSFサスペンス。遠隔操作可能なロボット・サロゲートが人間の役割を代行する未来。人は自宅から1歩も出ることなく、自分の理想の姿をしたサロゲートの行動をバーチャルで体験していた。だがある日、安全だと思われていたバーチャル世界で何者かにサロゲートが破壊され、そこに接続していた人間までもが死亡してしまう。監督は「U-571」「ターミネーター3」のジョナサン・モストウ。", "2010-01-22", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("プッシーキャット大作戦", "プッシーキャット大作戦", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 43, "ラス・メイヤー監督のカルト・ムービー「ファスタープシィキャット！キル！キル！」にインスパイアされたセクシー・アクション。東北の田舎を疾走するゴーゴーガール３人組が、欲望のおもむくままに大暴走を繰り広げる。ヒロインたちがまくし立てる東北弁のセリフや、1960～70年代カルチャーに傾倒する本田隆一監督ならではのレトロな味つけがユニークだ。東北弁の巨乳ガール、ハリー、サリー、マリーの３人は、田舎道を軽トラックで爆走中。ところが警官に取り締まられてしまい、マリーが警官を誘惑。しかし色仕掛けは失敗し、荷台に積んでいた男の死体を発見されてしまう……。", "2004-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Bolt", "ボルト", "", "", "http://image.eiga.k-img.com/images/movie/54192/315x210.jpg?3464", 96, "ハリウッドのスター犬の冒険を描いた3DCGアニメーション。人気子役のペニーに引き取られ、撮影スタジオで育ったボルトは、“スーパーパワーでペニーを守る”というTVの設定を現実だと信じ込み、毎日必死でペニーを守る日々を過ごしていた。ある日、TVの中でペニーが悪役にさらわれ、ボルトはペニーを救出するためスタジオを飛び出すが……。ボルトの声をジョン・トラボルタ、ペニーの声をマイリー・サイラスが務める。", "2009-08-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("WAYNE’S　WORLD２", "ウェインズ・ワールド2", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 95, "夢の中に伝説のカリスマ、ジム・モリソンが登場!? 彼に“コンサートを開け……”という啓示を受けたウェインが仲間たちとともに大騒動を繰り広げていく。“エクセレント”なロックバンド、エアロスミスのライヴをクライマックスに、矢継ぎ早に放たれるクダらないギャグの数々。意外な大物俳優を登場させたりと、“シュイーン”とはじける未公開の爆笑コメディ・シリーズ第２作。", "1993-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Buddy Buddy", "新・おかしな二人／バディ・バディ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "　2002年に死去したビリー・ワイルダーの遺作となったドタバタ喜劇。監督お気に入りの俳優コンビ、ジャック・レモンとウォルター・マッソーが3度目のタッグを組み、死にたい男と殺したい男の奇妙な関係を演じている。妻に去られた自殺願望の会社員ビクターと、仕事の命を受けた殺し屋がホテルで出会う。妻を連れて帰ることだけしか頭にないビクターは、図らずも殺し屋の仕事の邪魔ばかりしてしまう。", "1981-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("JACQUELINE SUSAN’S ONCE IS NOT ENOUGH", "いくたびか美しく燃え", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 121, "映画界の大物プロデューサーと、幼くして母親を失い男手一つで育てられたその娘。二人の愛情を中心に華やかな上流社交界を描いたメロドラマ。女流作家J・スーザンのベストセラー小説の映画化。", "1975-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("CRONACE DI UNA MORTE ANNUNCIATA", "予告された殺人の記録", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 110, "ノーベル賞作家G・G・マルケスの同名の小説を豪華キャストで映画化した名匠F・ロージの力作。故郷に帰って来たベドヤは、25年前、親友サンティアゴが殺された事件の真相を調査し出す。一人の異邦人バヤルドが町にやって来てアンヘラを見初め、結婚を決意する。だが、盛大な婚礼の夜、処女でなかった花嫁は実家に戻される。アンヘラの貞操を奪った男がサンティアゴであるということを聞き出したアンヘラの双子の兄弟は、殺害を予告する。ギリシャ悲劇を思わせる運命的な殺人事件をロージは堂々たる、格調高い演出で描き出す。映像、音楽ともに秀逸。", "1987-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("魔界転生", "魔界転生", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 122, "島原の乱を起こすも、松平伊豆守信綱率いる幕府軍に敗れたキリシタン・天草四郎時貞が、魔界からよみがえり、呪文によって魔界衆を呼び出して幕府転覆の成就をはかる、という山田風太郎原作の伝奇ロマン。人形作家・辻村ジュサブローによる衣装と、デカダンな妖気を発散する沢田研二の金色の目が印象的。", "1981-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("GHARWALI BAHARWALI", "歌う色男、 愛・ラブ・パラダイス！", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 133, "「ムトゥ　踊るマハラジャ」などを生み出した南インド映画とは一味違う北インド映画＝ボリウッド(Bombay＋Hollywood)の痛快作。愛する妻がいながら旅先で出会った少女と結婚するはめになってしまったアルン。愛の板挟みに悩む彼が奮闘を繰り広げる様を、都会派センスいっぱいの歌と踊り、カラフルな衣装によって描き出す。", "1998-01-01", "21", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("The Heartbreak Kid", "ライラにお手あげ", "", "", "http://ecx.images-amazon.com/images/I/51kZWyBOkhL._SL210_.jpg", 116, "「メリーに首ったけ」のピーター＆ボビー・ファレリー兄弟監督と主演のベン・スティラーが再タッグを組み、1972年製作の「ふたり自身」をリメイクしたラブコメディ。街でひと目ぼれした女性ライラと結婚することになった40歳のエディ。ところが、新婚旅行でライラの衝撃的な本性を知り、早くも結婚を後悔しはじめる。そんな中、エディは同じホテルに宿泊するミランダと恋に落ち……。共演は、「M:i:III」のミシェル・モナハンと「幸せになるための27のドレス」のマリン・アッカーマン。", "2007-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("二重スパイ", "二重スパイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 123, "朝鮮半島の南北分断を背景にしたスパイ・サスペンス。韓国を代表する名優ハン・ソッキュが北朝鮮のエリート・スパイに扮し、皮肉な運命にほんろうされる男の悲哀をエモーショナルに演じる。1980年、北朝鮮のスパイ、ビョンホが韓国に偽装亡命する。韓国側の信頼を得たビョンホは、女性スパイ、スミに接触して恋人同士を装う。しかし二人は思わぬ窮地に追い込まれていく。", "2003-01-01", "5", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("LICENSE TO KILL", "殺しの免許証〈ライセンス〉", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 96, "イギリス秘密諜報部のジェームズ・ボンドの同僚チャールズ・バインが世界的科学者の護衛をする。スパイ映画ブームに乗った“007”の類似品。主演のバイン役にはTV出身のアダムスが扮し、スーパーヒーローぶりを披露。続編も製作された。", "1965-01-01", "3", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("十年愛", "十年愛", "", "", "http://image.eiga.k-img.com/images/movie/54142/315x210.jpg?3390", 75, "TVドラマ「夜王」「女帝」の原作者、倉科遼の携帯配信用漫画の映画化。主演はグラビアアイドルの桜井まり。高校3年の夏、サッカー部主将の高木は優勝をかけた試合のPKをはずしたことで、友達も恋人も失ってしまう。サッカー部マネージャーの麻里は変わらずに高木のことを愛していたが、高木は高校を出て歌舞伎町でホストとして働き始める。高木を追って歌舞伎町へ向かった麻里だったが……。", "2008-11-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("殺人狂時代", "殺人狂時代", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 69, "都筑道夫の『なめくじに聞いてみろ』を鬼才・岡本喜八が映画化。日本映画としては異色な、グロテスクな殺しのサスペンス・コメディとなっている。人口調節のため無駄な人間を殺すことを目的とした団体“大日本人口調節審議会”は、大学講師の信治に次々と殺し屋を差し向ける。信治の背中の傷に隠された、ヒトラーのダイヤモンドを狙っているのだ。信治は女性記者の啓子とコソ泥のビルの協力を得て、殺し屋を倒していく……。元ナチスの将校、義眼に毒針を仕込んだ女など登場人物が一癖も二癖もあり、次の展開が読めない面白さがある。また、役名も“オバQ”“アトム”“ソラン”など人を食った名前がつけられている。", "1967-01-01", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("L'empire des loups", "エンパイア・オブ・ザ・ウルフ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 128, "「クリムゾン・リバー」の原作者ジャン＝クリストフ・グランジェのミステリー小説「狼の帝国」を、ジャン・レノ主演で映画化。パリのトルコ人街で、不法入国のトルコ人女性ばかりを狙った連続殺人事件が発生。パリ警察が迷宮入りを宣言したこの事件の再捜査に乗りだした若き刑事ポールは、裏社会に顔のきく潜入捜査官シフェールに協力を依頼する。やがて彼らの捜査線上に、記憶を失った女・アンナの存在が浮かびあがり……。", "2005-12-10", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("Kiss Me Goodbye", "キスミー グッバイ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "若くして夫に先立たれた未亡人ケイは考古学者ルパートとの再婚間際に、前夫ジョリーの幽霊（彼女にしか見えない）に惑わされることに……。R・マリガン監督が、ウイットとユーモアたっぷりに描くラブ・コメディ。", "1982-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("赤んぼ少女", "赤んぼ少女", "", "", "http://image.eiga.k-img.com/images/movie/53752/315x210.jpg?2848", 100, "楳図かずお原作のコミック「赤んぼ少女」を、「地獄甲子園」の山口雄大が映画化。主演は「ぼくたちと駐在さんの700日戦争」の水沢奈子。孤児院で育った葉子は、生家である南条家に引き取られることになった。豪邸で慣れない両親との生活を送る葉子は、ある日「タマミ」という醜い姿の女の子に襲われる。タマミは赤ん坊のまま成長できない南条家のもう一人の娘だった……。", "2008-08-02", "76", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PARTY　MONSTER", "パーティ☆モンスター", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 99, "1980～90年代のN.Y.クラブ・シーンに君臨した敏腕オーガナイザーの実話を映画化。クラブのスターに憧れ、次々とユニークなイベントを企画して成功をつかむも、ドラッグに溺れ、ついには殺人を犯す。成功の光と影を見つめる、辛口のドラマが展開していく。目立つことを重視した派手なファッションや、９年ぶりの映画出演となるM・カルキンの好演も見もの。田舎町のいじめられっ子だったマイケル・アリグは、N.Y.のディスコで働き始める。マイケルはしだいに華やかな世界に溶け込み、自らパーティーをプロデュース。ナイトライフのカリスマとして世間の注目を浴びるようになるが……。", "2003-01-01", "1", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("OLTRE IL BE｀NE E IL MALE／AU−DELA｀ DU BIEN ET DU MAL", "ルー・サロメ 善悪の彼岸", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 116, "19世紀後半、当時の精神、文化史に多大な影響を与えた女傑ルー・サロメと、二人の哲学者ニーチェ、パウル・レーの愛と妄執の物語。イタリアの女流監督カヴァーニが、自由を渇望し、精神の解放を求める３人の赤裸々な生きざまを鮮烈に描いた異色作。", "1977-01-01", "4,2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("HELLE´", "花のようなエレ", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 102, "南アルプスの麓のオート・サボワを舞台に、帰省した17歳の少年ファブリスと、ろうあの美少女エレとの神話的な恋を描いたラブ・ストーリー。美しい南アルプスの景観が全編に神話的彩りを加える。R・ヴァディムにしては珍しいタッチの作品だが、人間の愛憎の深淵へと迫る演出はさすが。", "1972-01-01", "2", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("快餐車／WHEELS ON MEALS", "スパルタンX", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 108, "大ヒット作「プロジェクトA」のトリオが出演した、超一級アクションの娯楽大作。バルセロナを舞台に、巨大な組織“X”に挑むカンフー青年とその友人たちの活躍を描いている。", "1984-01-01", "6", 0, NOW(), NOW());
INSERT INTO movies (name_of_original, name_of_japan, name_of_japan_kana, name_of_english, image, show_time, outline, open_date, category, negative, created_at, updated_at) VALUES ("PANE，AMORE E FANTASIA", "パンと恋と夢", "", "", "http://image.eiga.k-img.com/images/movie/noimg/315x210.png?4466", 91, "山奥の平和な村に新任の警察署長アントニオがやって来た。彼は“山猫”と呼ばれる村の娘に恋心を抱き始める。しかし彼女の方は内気な巡査ステルティに気があって……。V・デ・シーカが脚本・主演を務めたコメディ。G・ロロブリジーダが共演。", "1953-01-01", "4", 0, NOW(), NOW());
